INSERT INTO `nahjs` VALUES (1, 1, 1, 'آغاز آفرینش آسمان و زمین و آدم و...', 'خطبه ۱-آغاز آفرینش آسمان و... \nعجز انسان از شناخت ذات خدا\nسپاس خداوندی را که سخنوران از ستودن او عاجزند، و حسابگران از شمارش نعمت‌های او ناتوان، و تلاشگران از ادای حق او درمانده‌اند. خدایی که افکار ژرف اندیش، ذات او را درک نمی‌کنند و دست غواصان دریای علوم به او نخواهد رسید. پروردگاری که برای صفات او حد و مرزی وجود ندارد، و تعریف کاملی نمی‌توان یافت و برای خدا وقتی معین، و سر آمدی مشخص نمی‌توان تعیین کرد. مخلوقات را با قدرت خود آفرید، و با رحمت خود بادها را به حرکت در آورد و به وسیله کوه‌ها اضطراب و لرزش زمین را به آرامش تبدیل کرد. \n۲- دین و شناخت خدا\n سر آغاز دین، خداشناسی است، و کمال شناخت خدا، باور داشتن، او، و کمال باور داشتن خدا، شهادت به یگانگی اوست و کمال توحید (شهادت بر یگانگی خدا) اخلاص، و کمال اخلاص، خدا را از صفات مخلوقات جدا کردن است، زیرا هر صفتی نشان می‌دهد که غیر از موصوف، و هر موصوفی گواهی می‌دهد که غیر از صفت است، پس کسی که خدا را با صفت مخلوقات تعریف کند او را به چیزی نزدیک کرده، و با نزدیک کردن خدا به چیزی، دو خدا مطرح شده و با طرح شدن دو خدا، اجزایی برای او تصور نموده و با تصور اجزا برای خدا، او را نشناخته است. و کسی که خدا را نشناسد به سوی او اشاره می‌کند و هر کس به سوی خدا اشاره کند، او را محدود کرده، به شمارش آورده. و آن کس که بگوید «خدا در چیست» او را در چیز دیگری پنداشته است، و کسی که بپرسد «خدا بر روی چه چیزی قرار دارد» به تحقیق جایی را خالی از او در نظر گرفته است، در صورتی که خدا همواره بوده، و از چیزی به وجود نیامده است. با همه چیز هست، نه اینکه همنشین آنان باشد، و با همه چیز فرق دارد نه اینکه از آنان جدا و بیگانه باشد. انجام دهنده همه کارهاست، بدون حرکت و ابزار و وسیله، بیناست حتی در آن هنگام که پدیده‌ای وجود نداشت، یگانه و تنهاست، زیرا کسی نبوده تا با او انس گیرد، و یا از فقدانش وحشت کند. \n۳- راه‌های خدا شناسی \nاول- آفرینش جهان \nخلقت را آغاز کرد، و موجودات را بیافرید، بدون نیاز به فکر و اندیشه‌ای، یا استفاده از تجربه‌ای، بی آن که حرکتی ایجاد کند، و یا تصمیمی مضطرب در او راه داشته باشد. برای پدید آمدن موجودات، وقت مناسبی قرار داد، و موجودات گوناگون را هماهنگ کرد، و در هر کدام، غریزه خاص خودش را قرار داد، و غرائز را همراه آنان گردانید. خدا پیش از آن که موجودات را بیافریند، از تمام جزئیات و جوانب آن‌ها آگاهی داشت، و حدود و پایان آن‌ها را می‌دانست، و از اسرار درون و بیرون پدیده‌ها، آشنا بود. سپس خدای سبحان طبقات فضا را شکافت، و اطراف آن را باز کرد، و هوای به آسمان و زمین راه یافته را آفرید، و در آن آبی روان ساخت، آبی که امواج متلاطم آن شکننده بود، که یکی بر دیگری می‌نشست، آب را بر بادی طوفانی و شکننده نهاد، و باد را به باز گرداندن آن فرمان داد، و به نگهداری آب مسلط ساخت، و حد و مرز آن را به خوبی تعیین فرمود. فضا را در زیر تند باد و آب را بر بالای آن در حرکت بود. سپس خدای سبحان طوفانی بر انگیخت که آب را متلاطم ساخت و امواج آب را پی در پی در هم کوبید. طوفان به شدت وزید، و از نقطه‌ای دور دوباره آغاز شد. سپس به طوفان امر کرد تا امواج دریاها را به هر سو روان کند و بر هم کوبد و با همان شدت که در فضا وزیدن داشت، بر امواج آب‌ها حمله‌ور گردد از اول آن بر می‌داشت و به آخرش می‌ریخت، و آب‌های ساکن را به امواج سرکش بر گرداند. تا آنجا که آب‌ها روی هم قرار گرفتند، و چون قله‌های بلند کوه‌ها بالا آمدند. امواج تند کف‌های بر آمده از آب‌ها را در هوای باز، و فضای گسترده بالا برد، که از آن هفت آسمان را پدید آورد. آسمان پایین را چون موج مهار شده، و آسمان‌های بالا را مانند سقفی استوار و بلند قرار داد، بی آن که نیازمند به ستونی باشد یا میخ‌هایی که آن‌ها را استوار کند. آنگاه فضای آسمان پایین را به وسیله نور ستارگان درخشنده زینت بخشید و در آن چراغی روشنایی بخش (خورشید)، و ماهی درخشان، به حرکت در آورد که همواره در مدار فلکی گردنده و بر قرار، و سقفی متحرک، و صفحه‌ای بی قرار، به گردش خود ادامه دهند. \nدوم- شگفتی خلقت فرشتگان: \nسپس آسمان‌های بالا را از هم گشود، و از فرشتگان گوناگون پر نمود. گروهی از فرشتگان همواره در سجده‌اند و رکوع ندارند و گروهی در رکوعند و یارای ایستادن ندارند و گروهی در صف‌هایی ایستاده‌اند که پراکنده نمی‌شوند و گروهی همواره تسبیح گویند و خسته نمی‌شوند و هیچ گاه خواب به چشمشان راه نمی‌یابد، و عقل‌های آنان دچار اشتباه نمی‌گردد، بدن‌های آنان دچار سستی نشده، و آنان دچار بی خبری برخاسته از فراموشی نمی‌شوند. برخی از فرشتگان، امینان وحی الهی، و زبان گویای وحی برای پیامبران می‌باشند، که پیوسته برای رساندن حکم و فرمان خدا در رفت و آمدند. جمعی از فرشتگان حافظان بندگان، و جمعی دیگر دربانان بهشت خداوندند. بعضی از آن‌ها پاهایشان در طبقات پایین زمین قرار داشته، و گردن‌هاشان از آسمان فراتر، و ارکان وجودشان از اطراف جهان گذشته، عرش الهی بر دوش‌هایشان استوار است، برابر عرش خدا دیدگان به زیر افکنده، و در زیر آن، بالها را به خود پیچیده‌اند. میان این دسته از فرشتگان با آن‌ها که در مراتب پایین‌تری قرار دارند، حجاب عزت، و پرده‌های قدرت، فاصله انداخته است. هرگز خدا را با وهم و خیال، در شکل و صورتی نمی‌پندارند، و صفات پدیده‌ها را بر او روا نمی‌دارند، هرگز خدا را در جایی محدود نمی‌سازند، و نه با همانند آوردن، به او اشاره می‌کنند. \nسوم- شگفتی آفرینش آدم علیه السلام و ویژگی‌های انسان کامل. \nسپس خداوند بزرگ، خاکی از قسمت‌های گوناگون زمین، از قسمت‌های سخت و نرم، شور و شیرین، گرد آورد، آب بر آن افزود تا گلی خالص و آماده شد، و با افزودن رطوبت، چسبناک گردید، که از آن، اندامی شایسته، و عضوهایی جدا و به یکدیگر پیوسته آفرید. آن را خشکانید تا محکم شد. خشکاندن را ادامه داد تا سخت شد تا زمانی معین و سر انجامی مشخص، اندام انسان کامل گردید. آنگاه از روحی که آفرید در آن دمید تا به صورت انسانی زنده در آمد، دارای نیروی اندیشه، که وی را به تلاش اندازد و دارای افکاری که در دیگر موجودات، تصرف نماید. به انسان اعضاء و جوارحی بخشید، که در خدمت او باشند، و ابزاری عطا فرمود، که آن‌ها را در زندگی به کار گیرد. قدرت تشخیص به او داد تا حق و باطل را بشناسد، و حواس چشایی، و بویایی، و وسیله تشخیص رنگ‌ها، و أجناس مختلف در اختیار او قرار داد. انسان را مخلوطی از رنگ‌های گوناگون، و چیزهای همانند و سازگار، و نیروهای متضاد، و مزاج‌های گوناگون، گرمی، سردی، تری، خشکی، قرار داد. سپس از فرشتگان خواست تا آن چه در عهده دارند انجام دهند، و عهدی را که پذیرفته‌اند وفا کنند، اینگونه که بر آدم سجده کنند، و او را بزرگ بشمارند، و فرمود: «بر آدم سجده کنید پس فرشتگان همه سجده کردند جز شیطان» غرور و خود بزرگ بینی او را گرفت و شقاوت و بدی بر او غلبه کرد، و به آفرینش خود از آتش افتخار نمود، و آفرینش انسان از خاک را پست شمرد. خداوند برای سزاوار بودن شیطان به خشم الهی، و برای کامل شدن آزمایش، و تحقق وعده‌ها، به او مهلت داد و فرمود: «تا روز رستاخیز مهلت داده شدی». \nچهارم- آدم علیه السلام و داستان بهشت \nسپس خداوند آدم را در خانه‌ای مسکن داد که زندگی در آن گوارا بود. جایگاه او را امن و امان بخشید و او را از شیطان و دشمنی او ترساند. پس شیطان او را فریب داد، بدان علت که از زندگی آدم در بهشت و هم نشینی او با نیکان حسادت ورزید. پس آدم علیه السلام یقین را به تردید، و عزم استوار را به گفته‌های ناپایدار شیطان فروخت و شادی خود را به ترس تبدیل کرد، که فریب خوردن برای او پشیمانی آورد آنگاه خدای سبحان در توبه را بر روی آدم گشود و کلمه رحمت، بر زبان او جاری ساخت و به او وعده بازگشت به بهشت را داد. آنگاه آدم را به زمین، خانه آزمایش‌ها و مشکلات، فرود آورد، تا ازدواج کند، و فرزندانی پدید آورد، و خدای سبحان از فرزندان او پیامبرانی برگزید. \n۴- فلسفه بعثت پیامبران (نبوت عامه) \nخدا پیمان وحی را از پیامبران گرفت تا امانت رسالت را به مردم برسانند، آنگاه که در عصر جاهلیت‌ها بیشتر مردم، پیمان خدا را نادیده انگاشتند و حق پروردگار را نشناختند و برابر او به خدایان دروغین روی آوردند، و شیطان مردم را از معرفت خدا باز داشت و از پرستش او جدا کرد، خداوند پیامبران خود را مبعوث فرمود، و هر چندگاه، متناسب با خواسته‌های انسان‌ها، رسولان خود را پی در پی اعزام کرد تا وفاداری به پیمان فطرت را از آنان باز جویند و نعمت‌های فراموش شده را به یاد آورند و با ابلاغ احکام الهی، حجت خدا را بر آن‌ها تمام نمایند و توانمندی‌های پنهان شده عقل‌ها را آشکار سازند و نشانه‌های قدرت خدا را معرفی کنند، مانند: سقف بلند پایه آسمان‌ها بر فراز انسان‌ها، گاهواره گسترده زمین در زیر پای آن‌ها، و وسائل و عوامل حیات و زندگی، و راه‌های مرگ و مردن، و مشکلات و رنج‌های پیر کننده، و حوادث پی در پی، که همواره بر سر راه آدمیان است. خداوند هرگز انسان‌ها را بدون پیامبر، یا کتابی آسمانی، یا برهانی قاطع، یا راهی استوار، رها نساخته است، پیامبرانی که با اندک بودن یاران، و فراوانی انکار کنندگان، هرگز در انجام وظیفه خود کوتاهی نکردند. بعضی از پیامبران، بشارت ظهور پیامبر آینده را دادند و برخی دیگر را پیامبران گذشته معرفی کردند. بدین گونه قرن‌ها پدید آمد، و روزگاران سپری شد، پدران رفتند و فرزندان جای آن‌ها را گرفتند. \n۵- فلسفه بعثت پیامبر خاتم صلی الله علیه و آله و سلم \nتا این که خدای سبحان، برای وفای به وعده خود، و کامل گردانیدن دوران نبوت، حضرت محمد (که درود خدا بر او باد) را مبعوث کرد، پیامبری که از همه پیامبران پیمان پذیرش نبوت او را گرفته بود، نشانه‌های او شهرت داشت، و تولدش بر همه مبارک بود. در روزگاری که مردم روی زمین دارای مذاهب پراکنده، خواسته‌های گوناگون، و روش‌های متفاوت بودند: عده‌ای خدا را به پدیده‌ها تشبیه کرده و گروهی نام‌های ارزشمند خدا را انکار و به بت‌ها نسبت می‌دادند، و برخی به غیر خدا اشاره می‌کردند. پس خدای سبحان، مردم را به وسیله محمد صلی الله علیه و آله و سلم از گمراهی نجات داد و هدایت کرد، و از جهالت رهایی بخشید. سپس دیدار خود را برای پیامبر صلی الله علیه و آله و سلم بر گزید، و آنچه نزد خود داشت برای او پسندید و او را با کوچ دادن از دنیا گرامی داشت، و از گرفتاری‌ها و مشکلات رهایی بخشید و کریمانه قبض روح کرد. \n۶- ضرورت امامت پس از پیامبران الهی \nرسول گرامی اسلام، در میان شما مردم جانشینانی برگزید که تمام پیامبران گذشته برای امت‌های خود برگزیدند، زیرا آن‌ها هرگز انسان‌ها را سرگردان رها نکردند و بدون معرفی راهی روشن و نشانه‌های استوار، از میان مردم نرفتند. \n۷- ویژگی‌های قرآن و احکام اسلام \nکتاب پروردگار میان شماست، که بیان کننده حلال و حرام، واجب و مستحب، ناسخ و منسوخ، مباح و ممنوع، خاص و عام، پندها و مثل‌ها، مطلق و مقید، محکم و متشابه می‌باشد، عبارات مجمل خود را تفسیر، و نکات پیچیده خود را روشن می‌کند، از واجباتی که پیمان شناسایی آن را گرفت و مستحباتی که آگاهی از آن‌ها لازم نیست. قسمتی از احکام دینی در قرآن واجب شمرده شد که ناسخ آن در سنت پیامبر صلی الله علیه و آله و سلم آمده، و بعضی از آن، در سنت پیامبر صلی الله علیه و آله و سلم واجب شده که در کتاب خدا ترک آن مجاز بوده است، بعضی از واجبات، وقت محدودی داشته که در آینده از بین رفته است. محرمات الهی از هم جدا می‌باشند، برخی از آن‌ها، گناهان بزرگ است که وعده آتش دارد و بعضی کوچک که وعده بخشش داده است، و برخی از اعمال که اندکش مقبول و در انجام بیشتر آن آزادند. \n۸- فلسفه و راه آورد حج \nخدا حج را خانه محترم خود را بر شما واجب کرد، همان خانه‌ای که آن را قبله‌گاه انسان‌ها قرار داده که چونان تشنگان به سوی آن روی می‌آورند، و همانند کبوتران به آن پناه می‌برند. خدای سبحان، کعبه را مظهر تواضع بندگان برابر عظمت خویش و نشانه اعتراف آنان به بزرگی و قدرت خود قرار داد و در میان انسان‌ها، شنوندگانی را برگزید که دعوت او را برای حج، اجابت کنند و سخن او را تصدیق نمایند و پای بر جایگاه پیامبران الهی نهند. همانند فرشتگانی که بر گرد عرش الهی طواف می‌کنند، و سودهای فراوان در این عبادتگاه و محل تجارت زائران به دست آورند و به سوی وعده‌گاه آمرزش الهی بشتابند. خدای سبحان، کعبه را برای اسلام، نشانه گویا، و برای پناهندگان، خانه امن و امان قرار داد، ادای حق آن را واجب کرد و حج بیت الله را واجب شمرد و بر همه شما انسانها مقرر داشت که به زیارت آن بروید، و فرمود: آن کس که توان رفتن به خانه خدا را دارد، حج بر او واجب است و آن کس که انکار کند، خداوند از همه جهانیان بی نیاز است.', 'و من خطبة له (علیه السلام) يريد الشيطان أو يكنّي به عن قوم‏:\r\nأَلَا وَ إِنَّ الشَّيْطَانَ قَدْ جَمَعَ حِزْبَهُ وَ اسْتَجْلَبَ خَيْلَهُ وَ رَجِلَهُ وَ إِنَّ مَعِي لَبَصِيرَتِي مَا لَبَّسْتُ عَلَى نَفْسِي وَ لَا لُبِّسَ عَلَيَّ. وَ ايْمُ اللَّهِ لَأُفْرِطَنَّ لَهُمْ حَوْضاً أَنَا مَاتِحُهُ، لَا يَصْدُرُونَ عَنْهُ وَ لَا يَعُودُونَ إِلَيْهِ‏.', NULL, 'آگاهى امام (عليه السّلام) براى مقابله با اصحاب جمل:\r\nآگاه باشيد كه شيطان حزب خود را جمع كرده، و سواره و پياده هاى لشكر خود را فراخوانده است. امّا من آگاهى لازم به امور را دارم، نه حق را پوشيده داشتم، و نه حق بر من پوشيده ماند. سوگند به خدا، گردابى براى آنان به وجود آورم كه جز من كسى نتواند آن را چاره سازد، آنها كه در آن غرق شوند، هرگز نتوانند بيرون آيند، و آنان كه بگريزند، خيال بازگشت نكنند.', NULL, 'https://hadith.net/en/post/51245/sermon-1-praise-is-due-to-allah-whose-worth-cannot-be-descr/', NULL, NULL);
INSERT INTO `nahjs` VALUES (2, 1, 10, 'حزب شیطان', 'خطبه ۱۰-حزب شیطان \nآگاهی امام برای مقابله با اصحاب جمل \nآگاه باشید که شیطان حزب خود را جمع کرده، و سواره و پیاده‌های لشگر خود را فرا خوانده است، اما من آگاهی لازم به امور دارم، نه حق را پوشیده داشتم و نه حق بر من پوشیده ماند سوگند به خدا، گردابی، برای آنان بوجود آورم که جز من کسی نتواند آن را چاره سازد، آن‌ها که در آن غرق شوند، هرگز نتوانند بیرون آیند، و آن‌ها که گریختند، خیال بازگشتن نکنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (3, 1, 100, 'درباره پیامبر و خاندان او', 'خطبه ۱۰۰-درباره پیامبر و خاندان او\n(نوشته‌اند که این سخنرانی را در جمعه سوم ایام خلافت خود در سال ۳۵ هجری در روز جمعه ایراد فرمود) \nشناخت خدا \nستایش خداوندی را سزاست که احسان فراوانش بر آفریده‌ها گسترده، و دست کرم او را برای بخشش گشوده است، او را بر همه کارهایش می‌ستاییم، و برای نگهداری حق الهی از او یاری می‌طلبیم، و گواهی می‌دهیم جز او خدایی نیست \nویژگیهای پیامبر (ص) \nو گواهی می‌دهیم که محمد (ص) بنده و فرستاده اوست. او را فرستاد تا فرمان وی را آشکار و نام خدا را بر زبان راند. پس با امانت، رسالت خویش را به انجام رساند، و با راستی و درستی به راه خود رفت، و پرچم حق را در میان ما به یادگار گذاشت، هر کس از آن پیشی گیرد از دین خارج و آن کس که از آن عقب ماند هلاک گردد و هر کس همراهش باشد رستگار شود راهنمای این پرچم، با درنگ و آرامش سخن گوید، و دیر بپا خیزد، و آنگاه که برخاست سخت و چالاک، به پیش رود، پس چون در اطاعت او درآمدید، و او را بزرگ داشتید، مرگ فرا رسید و او را از میان شما برد، پس از او چندان که خدا خواهد زندگانی می‌گذرانید تا آنکه خدا شخصی را برانگیزاند که شما را متحد سازد، و پراکندگی شما را جبران نماید. مردم! به چیزی که نیامده دل نبندید، و از آنکه درگذشت مایوس نباشید، که آن پشت کرده اگر یکی از پاهایش بلغزد، و دیگری برقرار باشد شاید هر دو پا به جای خود برگشته و استوار ماند. \nتدوام امامت تا ظهور امام زمان (ع) \nآگاه باشید، مثل آل محمد (ص) چونان ستارگان آسمان است، اگر ستاره ای غروب کند، ستاره دیگری طلوع خواهد کرد (تا ظهور صاحب الزمان (ع)). گویا می‌بینم در پرتو خاندان پیامبر (ع) نعمتهای خدا بر شما تمام شده و شما به آنچه آرزو دارید رسیده اید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (4, 1, 101, 'مشتمل بر خبر از حوادث ناگوار', 'خطبه ۱۰۱-خبر از حوادث ناگوار \nستایش و اندرز \nستایش خداوندی را که اول هر نخستین است و آخر هر گونه آخری، چون پیش از او چیزی نیست باید که ابتدایی نداشته باشد، و چون پس از او چیزی نیست پس پایانی نخواهد داشت، و گواهی می‌دهم که جز او خدایی نیست، و خدا یکی است، آن گواهی که با درون و برون قلب و زبان، هماهنگ باشد. \nای مردم! دشمنی و مخالفت با من شما را تا مرز گناه نراند، و نافرمانی از من شما را به پیروی از هوای نفس نکشاند، و به هنگام شنیدن سخن من، به گوشه چشم، یکدیگر را ننگرید. خبر از حوادث خونین آینده سوگند به آنکس که دانه را شکافت، و جانداران را آفرید، آنچه به شما خبر می‌دهم از پیامبر امین (ص) است نه گوینده دروغ گفت و نه شنونده ناآگاه بود گویا می‌بینم شخص سخت گمراهی را که از شام فریاد زند و بتازد و پرچمهای خود را در اطراف کوفه بپراکند، و چون دهان گشاید، و سرکشی کند، و جای پایش بر زمین محکم گردد، فتنه فرزند خویش را به دندان گیرد، و آتش جنگ شعله ور شود، روزها با چهره عبوس و گرفته ظاهر شوند و شب و روز با رنج و اندوه بگذرند. و آنگاه که کشتزار او به بار نشست، و میوه او آبدار شد، و چون شتر مست خروشید، و چون برق درخشید، پرچمهای سپاه فتنه از هر سو به اهتزاز درآید، و چونان شب تار و دریای متلاطم به مردم روی آورند. از آن بیشتر، چه طوفانهای سختی که شهر کوفه را بشکافد، و چه تندبادهایی که بر آن وزیدن گیرد، و به زودی دستجات مختلف به جان یکدیگر یورش آوردند، آن‌ها که بر سر پا ایستاده‌اند درو شوند، و آن‌ها که بر زمین افتادن \nد لگدمال گردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (5, 1, 102, 'در زمینه سختیها', 'خطبه ۱۰۲-در زمینه سختیها \nوصف روز قیامت \nقیامت روزی است که خداوند همه انسانهای گذشته و آینده را برای رسیدگی دقیق حساب و رسیدن به پاداش اعمال، گرد آورد، همه فروتنانه به پاخیزند، عرق از سر و رویشان و کنار دهانشان جاری است، و زمین زیر پایشان لرزان است، نیکوحالترین آنان کسی است که جای گذاشتن دو پایش را پیدا کند یا جایی برای آسوده ماندن بیابد. \nخبر از آینده خونین بصره و رزم مجاهدانی پیروز فتنه‌هایی چون تاریکی شب که هیچ نیرویی نمی تواند برابر آنان بایستد، و هیچ کس نتواند پرچمهای آن را پایین کشد، به سوی شما می‌آیند چونان شتری که مهارشده، و جهاز بر پشت آن نهاده، و ساربان آن را کشانده و به سرعت می‌راند فتنه جویان کسانی هستند که ضربات آن‌ها شدید و غارتگری آنان اندک است. مردمی با آنان جهاد می‌کنند که در چشم متکبران خوار، و در روی زمین گمنام، و در آسمانها معروفند، در این هنگام، وای بر تو ای بصره! از سپاهی که نشانه خشم و انتقام الهی است، بی گرد و غبار و صدایی به تو حمله خواهند کرد، و چه زود ساکنانت به مرگ سرخ و گرسنگی غبارآلود دچار می‌گردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (6, 1, 103, 'در تشویق به زهد', 'خطبه ۱۰۳-در تشویق به زهد \nروش برخورد با دنیا \nای مردم! به دنیا چونان زاهدان رویگردان از آن، بنگرید، به خدا سوگند! دنیا به زودی ساکنان خود را از میان می‌برد، و رفاه زدگان ایمن را به درد می‌آورد، آنچه از دست رفت و پشت کرده هیچگاه برنمی گردد، و آینده به روشنی معلوم نیست تا در انتظارش باشند. شادی و سرور دنیا با غم و اندوه آمیخته، و توانایی انسان به ضعف و سستی می‌گراید، زیباییها و شگفتیهای دنیا شما را مغرور نسازد، زیرا زمان کوتاهی دوام ندارد. \nخدا بیامرزد کسی را که به درستی فکر کند، و پند گیرد، و آگاهی یابد، و بینا شود، پس به زودی خواهید دانست که آنچه از دنیا وجود داشت از آن چیزی نمانده، و آنچه از آخرت است جاویدان خواهد ماند، هر چیز که به شمارش آید پایان پذیرد، و هر چه انتظارش می‌کشیدید خواهد آمد، و آن چه آمدنی است نزدیک باشد \nارزش عالم و بی ارزشی جاهل دانا کسی است که قدر خود را بشناسد، و در نادانی انسان این بس که ارزش خویش نداند. دشمن رویترین افراد نزد خدا کسی که خدا او را به حال خود واگذاشته تا از راه راست منحرف گردد، و بدون راهنما برود، اگر به محصولات دنیا دعوت شود تا مرز جان تلاش کند اما چون به آخرت و نعمتهای گوناگونش دعوت شود، سستی ورزد، گویا آنچه برای آن کار می‌کند بر او واجب و آنچه نسبت به آن کوتاهی و تنبلی می‌کند، لزومی ندارد. \nسخنی از آینده و این روزگاری است که جز مومن بی نام و نشان از آن رهایی نیابد، در میان مردم است او را نشناسند، و در میان جمعیت که نباشد کسی سراغ او را نگیرد، آن‌ها چراغهای هدایت، و نشانه‌های رستگاریند، نه فتنه انگیزند و اهل فساد، و نه سخنان دیگران و زشتی این و آن را به مردم رسانند، خدا درهای رحمت را به روی آنان باز کرده و سختی عذاب خویش را از آنان گرفته است. ای مردم! به زودی زمانی بر شما خواهد رسید که اسلام چونان ظرف واژگون شده، آنچه در آن بود ریخته می‌شود \nای مردم! خداوند به شما ظلم نخواهد کرد و از این جهت تامین داده است اما هرگز شما را ایمن نساخت که آزمایش نفرماید، چه این سخن از آن ذات برتر است که فرمود: در جریان نوح پیامبر نشانه‌هائی است و ما مردم را می‌آزماییم (مومنون، آیه ۳۰ ((هدف امام از جمله (کل مومن نوبه) یعنی گمنام و ناشناخته می‌باشند و جمله (المساییح) که جمع مسایح می‌باشد کسی است که در میان مردم و غیبت کردن و گناه و فساد سرگرم است و کلمه (المذاییع) که جمع مذیاع است کسی است که چون حرف زشتی را بشنود رواج می‌دهد و (البذر) جمع بذور کسی است که بسیار نادان و بیهوده گو می‌باشد).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (7, 1, 104, 'درباره پیامبر و فضیلت خویش', 'خطبه ۱۰۴-پیامبر و فضیلت خویش \nره آورد بعثت پیامبر (ص) \nپس از ستایش پروردگار، \nهمانا خداوند سبحان، حضرت محمد (ص) را مبعوث فرمود، در روزگارانی که عرب کتابی نخوانده و ادعای وحی و پیامبری نداشت پیامبر اسلام (ص) با یارانش به مبارزه با مخالفان پرداخت تا آنان را به سرمنزل نجات کشاند، و پیش از آنکه مرگشان فرا رسد آنان را به رستگاری رساند، با خستگان مدارا کرد، و شکسته حالان را زیر بال گرفت تا همه را به راه راست هدایت کرد، جز آنان که راه گمراهی پیمودند، و در آن‌ها خیری نبود همه را نجات داد، و در جایگاه مناسب رستگاری، استقرارشان بخشید، تا آنکه آسیاب زندگی آنان به چرخش درآمد، و نیزه شان تیز شد. به خدا سوگند! من در دنباله آن سپاه بودم، تا باطل شکست خورد و عقب نشست، و همه رهبری اسلام را فرمانبردار شدند، در این راه هرگز ناتوان نشدم، و نترسیدم، و خیانت نکردم، و سستی در من راه نیافت به خدا سوگند! درون باطل را می‌شکافم تا حق را از پهلویش بیرون کشم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (8, 1, 105, 'در ذکر صفات پیامبر و تهدید بنی‌امیه', 'خطبه ۱۰۵-صفات پیامبر \nویژگیهای پیامبر (ص) \nتا اینکه خدا محمد (ص) را برانگیخت، گواهی دهنده، بشارت دهنده، هشداردهنده. پیامبری که بهترین آفریدگان در خردسالی، و در سن پیری نجیبترین و بزرگوارترین مردم بود، اخلاقش از همه پاکان پاکتر و باران کرمش از هر چیزی بادوامتر بود. هشدار از آینده تلخ فرزندان امیه شما ای فرزندان امیه! زمانی از لذت و شیرینی دنیا بهره مند شدید، و از پستان آن نوشیدید که افسارش رها، و بند جهازش محکم نبود (در حکومت عثمان)، تا آنجا که حرام دنیا نزد گروهی چونان درخت سدر بی خار، آسان و حلال آن دشوار و غیر ممکن شد به خدا سوگند دنیایی که در دست شماست چونان سایه ای است گسترده که زود به سرآمد خود نزدیک می‌شود. امروز زمین برای شما خالی و گسترده، و دستهایتان برای انجام هر رفتار نادرستی باز، و دستهای رهبران واقعی بسته است، شمشیر شما بر آنان مسلط و شمشیر آنان از شما باز گرفته شده است. آگاه باشید! هر خونی، خونخواهی دارد، و هر حقی را جستجوگری است، انتقام گیرنده خونهای ما، چونان حاکمی است که برای خود داوری کند، و او خداوندی است که از گرفتن کسی ناتوان نگردد، و کسی از پنجه عدالت او نمی تواند بگریزد، ای فرزندان امیه! سوگند \nبه خدا! زود باشد که این خلافت و دولت را در دست دیگران و در خانه دشمنان خود بنگرید، \nآگاه باشید بیناترین چشمها آنکه در دل نیکیها نفوذ کند، و شنواترین گوشها آنکه پندها و تذکرات سودمند را در خود جای دهد. اندرزهای جاودانه، و توجه دادن مردم به اهل بیت (ع) ای مردم! چراغ دل را از نور گفتار گوینده با عمل روشن سازید، ظرفهای جان را از آب زلال چشمه‌هایی که از آلودگیها پاک است پر نمایید ای بندگان خدا! به نادانیهای خود تکیه نکنید، و تسلیم هوای نفس خویش نباشید که چنین کسی بر لبه پرتگاه قرار دارد، و بار سنگین هلاکت و فساد را بر دوش می‌کشد، و از جایی به جای دیگر می‌برد، تا آنچه را که ناچسب است بچسباند، و آنچه را که دور می‌نماید نزدیک جلوه دهد. خدا را، خدا را، مبادا شکایت نزد کسی برید که نمی تواند آن را برطرف سازد، و توان گره گشایی از کارتان ندارد، همانا بر امام واجب نیست جز آنچه را که خدا امر فرماید، و آن، کوتاهی نکردن در پند و نصیحت، تلاش در خیرخواهی، زنده نگهداشتن سنت پیامبر (ص)، جاری ساختن حدود الهی بر مجرمان، رساندن سهمهای بیت المال به طبقات مردم، است. پس در فراگیری علم و دانش پیش از آنکه درختش بخشکد تلاش کنید، و پیش از آنکه به خود مشغول گردید از معدن علوم (اهل بیت (ع)) دانش استخراج کنید، \nمردم را از حرام و منکرات باز دارید، و خود هم مرتکب نشوید، زیرا دستور داده شدید که ابتدا خود محرمات را ترک و سپس مردم را باز دارید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (9, 1, 106, 'وصف پیامبر و بیان دلاوری', 'خطبه ۱۰۶-وصف پیامبر و بیان دلاوری \nره آورد اسلام \nستایش خداوندی را سزاست که راه اسلام را گشود، و راه نوشیدن آب زلالش را بر تشنگان آسان فرمود، ستونهای اسلام را در برابر ستیزه جویان استوار نمود، و آن را پناهگاه امنی برای پناه برندگان، و مایه آرامش برای واردشوندگان قرار داد اسلام، حجت و برهان برای گویندگان، و گواه روشن برای دفاع کنندگان، و نور هدایتگر برای روشنی خواهان، و مایه آرامش برای واردشوندگان قرار داد اسلام، حجت و برهان برای گویندگان، و گواه روشن برای دفاع کنندگان، و نور هدایتگر برای روشنی خواهان، و مایه فهمیدن برای خردمندان، و عقل و درک برای تدبیرکنندگان، و نشانه گویا برای جویندگان حق، و روشن بینی برای صاحبان عزم و اراده، پندپذیری برای عبرت گیرندگان، عامل نجات و رستگاری برای تصدیق کنندگان، و آرامش دهنده تکیه کنندگان، راحت و آسایش توکل کنندگان، و سپری نگهدارنده برای استقامت دارندگان است. اسلام روشن ترین راهها است، جاده‌هایش درخشان، نشانه‌های آن در بلندترین جایگاه، چراغهایش پرفروغ و سوزان، می‌دان مسابقه آن پاکیزه برای پاکان، سرانجام مسابقه‌های آن روشن و بی پایان، مسابقه دهندگان آن پیشی گیرنده و چابک سوارانند، برنامه این مسابقه، ت \nصدیق کردن به حق، راهنمایان آن، اعمال صالح، پایان آن، مرگ، می‌دان مسابقه، دنیا، مرکز گردآمدن مسابقه دهندگان، قیامت، و جایزه آن بهشت است. \nدعا برای پیامبر (ص) تا آنکه خداوند با دست پیامبر (ص) شعله ای از نور برای طالبان آن برافروخت، و بر سر راه گمشدگان چراغی پرفروغ قرار داد. خداوندا! پیامبر (ص) امین و مورد اطمینان و گواه روز قیامت است، و نعمتی است که برانگیخته و رحمتی است که به حق فرستاده ای، خداوندا! بهره فراوانی از عدل خود به او اختصاص ده، و از احسان و کرم خود فراوان به او ببخش، خدایا! بنای دین او را از آنچه دیگران برآورده اند، عالی تر قرار ده. او را بر سر خان کرمت گرامیتر دار، و بر شرافت مقام او در نزد خود بیفزا، و وسیله تقرب خویش را به او عنایت فرما، و بلندی مقام و فضیلت او را بی مانند گردان، و ما را از یاران او محشور فرما، چنانکه نه زیانکار باشیم و نه پشیمان، نه دور از راه حق باشیم و نه شکننده پیمان، نه گمراه باشیم و نه گمراه کننده بندگان، نه فریب هوای نفس خوریم و نه وسوسه شیطان. \nره آورد بعثت پیامبر (ص) مردم! از سر نعمت بعثت پیامبر (ص) و لطف خداوند بزرگ به مقامی رسیده اید که حتی کنیزان شما را گرامی می‌دارند، و به همسایگان شما محبت می‌کنند، کسانی برای شما احترام قائلند که شما از آن‌ها برتری نداشته و بر آن‌ها حقی ندارید، کسانی از شما می‌ترسند که نه ترس از حکومت شما دارند و نه شما بر آن‌ها حکومتی دارید. علل سقوط و سیر ارتجاعی امت با آن همه بزرگواری و کرامت، هم اکنون می‌نگرید که قوانین و پیمانهای الهی شکسته شده اما خشم نمی گیرید، در حالی که اگر پیمان پدرانتان نقض می‌شد ناراحت می‌شدید، شما مردمی بودید که دستورات الهی ابتدا به دست شما می‌رسید و از شما به دیگران ابلاغ می‌شد و آثار آن باز به شما برمی گشت. اما امروز جایگاه خود را به ستمگران واگذاردید، و زمام امور خود را به دست بیگانگان سپردید، امور الهی را به آنان تسلیم کردید، آن‌هایی که به شبهات عمل می‌کنند، و در شهوات غوطه ورند (بنی امیه) به خدا سوگند! اگر دشمنان شما را در زیر ستارگان آسمان بپراکنند، باز خداوند شما را برای انتقام گرفتن از ستمگران گرد می‌آورد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (10, 1, 107, 'در یکی از ایام صفین', 'خطبه ۱۰۷-در یکی از ایام صفین \nوصف نبرد یاران در صفین \nاز جای کنده شدن و فرار شما را از صفها دیدم، فرومایگان گمنام، و بیابان نشینانی از شام، شما را پس می‌راندند، در حالی که شما از بزرگان و سرشناسان عرب و از سران شرف می‌باشید، برازندگی چشمگیری دارید و قله‌های سرفراز و بلند قامتید، سرانجام سوزش سینه ام با مقاومت و حملات دلاورانه شما، تسکین یافت، که دیدم شامیان را هزیمت دادید و صفهای آنان را درهم شکستید، و آنان را از لشگرگاه خود راندید، آنگونه که آن‌ها شما را کنار زدند، می‌دیدم با نیزه‌ها آنان را کوفتید و با تیرها، آن‌ها را هدف قرار دادید، که فراریان و کشتگان دشمن روی هم ریختند، و بر دوش هم سوار می‌شدند، چونان شتران تشنه ای که از آبشخورشان برانند و به هر سو گریزان باشند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (11, 1, 108, 'خبر دادن از حادثه‌های بزرگ', 'خطبه ۱۰۸-حادثه‌های بزرگ \nخداشناسی \nستایش خدای را سزاست که با آفرینش مخلوقات، بر انسانها تجلی کرد، و با برهان و دلیل خود را بر قلبهایشان آشکار نمود، مخلوقات را بدون نیاز به فکر و اندیشه آفرید، که فکر و اندیشه مخصوص کسانی است که دلی درون سینه داشته باشند، و او چنین نیست که علم خداوندی ژرفای پرده‌های غیب را شکافته، و به افکار و عقاید پنهان احاطه دارد. \nوصف پیامبر اسلام (ص) پیامبر (ص) را از درخت تنومند پیامبران، از سرچشمه نور هدایت، از جایگاه بلند بی همانند، از سرزمین بطحا، از چراغهای برافروخته در تاریکیها، و از سرچشمه‌های حکمت برگزید. \nپیامبر (ص) طبیبی است که برای درمان بیماران سیار است، مرهمهای شفابخش او آماده، و ابزار داغ کردن زخمها را گداخته، برای شفای قلبهای کور، و گوشهای ناشنوا، و زبانهای لال، آماده است و با داروی خود در پی یافتن بیماران فراموش شده و سرگردان است. علل انحراف فرزندان امیه بنی امیه با نور حکمت، جان و دل خود را روشن نساخته، و با شعله‌های فروزان دانش، قلب خود را نورانی نکرده اند، چونان چهارپایان صحرایی و سنگهای سخت و نفوذناپذیرند \nبه تحقیق رازهای درون برای صاحبان آگاهی آشکار، و راه حق برای گمراهان نیز روشن است، و رستاخیز نقاب از چهره برانداخت، و نشانه‌های خود را برای زیرکان و آنان که طالب حقند نمایاند. نکوهش کوفیان مردم کوفه! چرا شما را پیکرهای بی روح، و روحهای بدون جسد می‌نگرم؟ چرا شما را عبادت کنندگانی بدون صلاحیت، و بازرگانانی بدون سود و تجارت، و بیدارانی خفته، و حاضرانی غایب از صحنه، بینندگانی نابینا، شنوندگانی کر، و سخن گویانی لال، مشاهده می‌کنم؟ \nپرچم گمراهی در جای خود برافراشته شده، و طرفداران آن پراکنده شده اند، شما را با پیمانه خود می‌سنجند و سرکوب می‌کنند، پرچمدارشان (معاویه)، از ملت اسلام خارج و بر راه گمراهی ایستاده است. خبر از کشتار و فساد بنی امیه پس آن روز که بر شما دست یابند جز تعداد کمی از شما باقی نگذارند، چونان باقیمانده غذایی اندک در ته دیگ، یا دانه‌های غذای چسبیده در اطراف ظرف، مانند پوستهای چرمی شما را به هم پیچانده می‌فشارند، و همانند خرمن شما را به شدت می‌کوبند، و چونان پرنده ای که دانه‌های درشت را از لاغر جدا کند، این گمراهان، مومنان را از میان شما جدا ساخته نابود می‌کنند. هشدار و سفارش به اطاعت از اهل بیت (ع) با توجه به این همه خطرات، روشهای گمراه کننده شما را به کجا می‌کشاند؟ تاریکیها و ظلمتها، تا کی شما را متحیر می‌سازد؟ دروغ پردازیها تا چه زمانی شما را می‌فریبد؟ از کجا دشمن در شما نفوذ کرده به اینجا آورده و به کجا باز می‌گرداند؟ آگاه باشید! که هر سرآمدی را پرونده ای، و هر غیبتی را بازگشت دوباره ایست مردم! به سخن عالم خداشناس خود گوش فرا دهید، دلهای خود را در پیشگاه او حاضر کنید، و با فریادهای او بیدار شوید، رهبر \nجامعه باید با مردم به راستی سخن گوید، و پراکندگی مردم را به وحدت تبدیل، و اندیشه خود را برای پذیرفتن حق آماده گرداند، پیشوای شما چنان واقعیتها را برای شما شکافت چونان شکافتن مهره‌های ظریف، و حقیقت را از باطل چون شیره درختی که از بدنه آن خارج شود، بیرون کشید. خبر از مسخ ارزشها در حکومت بنی امیه پس در آن هنگام که امویان بر شما تسلط یابند، باطل بر جای خود استوار شود، و جهل و نادانی بر مرکبها سوار، و طاغوت زمان عظمت یافته، و دعوت کنندگان به حق اندک و بی مشتری خواهند شد، روزگار چونان درنده خطرناکی حمله ورشده، و باطل پس از مدتها سکوت، نعره می‌کشد، مردم در شکستن قوانین خدا دست در دست هم می‌گذارند، و در جدا شدن از دین متحد می‌گردند، و در دروغ پردازی با هم دوست و در راستگویی دشمن یکدیگرند و چون چنین روزگاری می‌رسد، فرزند با پدر دشمنی ورزد، و باران خنک کننده، گرمی و سوزش آورد، پست فطرتان همه جا را پر می‌کنند، نیکان و بزرگواران کمیاب می‌شوند، مردم آن روزگار چون گرگان، و پادشاهان چون درندگان، تهیدستان طعمه آنان، و مستمندان چونان مردگان خواهند بود، راستی از میانشان رخت برمی بندد، و دروغ فراوان می‌شود، با زبان تظاهر به د \nوستی دارند اما در دل دشمن هستند، به گناه افتخار می‌کنند، و از پاکدامنی به شگفت می‌آیند، و اسلام را چون پوستینی واژگونه می‌پوشند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (12, 1, 109, 'توانایی خداوند', 'خطبه ۱۰۹-توانایی خداوند \nوصف قدرت پروردگار \nهمه چیز برابر خدا خشوع کننده است، و همه چیز با یاری او بر جای مانده اند، خدا بی نیازکننده هر نیازمند، و عزتبخش هر خوار و ذلیل، نیروی هر ناتوان، و پناهگاه هر مصیبت زده است، هر کس سخن گوید می‌شنود، و هر که ساکت باشد اسرار درونش را می‌داند، روزی زندگان بر اوست و هر که بمیرد به سوی او باز می‌گردد. خدایا! چشمها تو را ندیده تا از تو خبر دهند، پیش از توصیف کنندگان از موجودات، بوده ای، آفرینش برای ترس از وحشت تنهایی نبود، و برای سودجویی آن‌ها را نیافریدی، هیچ کس از قدرت تو نتواند بگریزد، و هر کس را تو بگیری از قدرت تو نتواند خارج گردد، گناهکاران از عظمت تو نکاهند، و اطاعت کنندگان بر قدرت تو نیفزایند، آن کس که از قضای تو به خشم آید نتواند فرمانت را برگرداند، و هر کس که به فرمان تو پشت کند از تو بی نیاز نگردد. هر سری نزد تو آشکار و هر پنهانی نزد تو هویداست، تو خدای همیشه ای و بی پایان، و تو پایان هر چیزی، که گریزی از آن نیست، وعده گاه همه، محضر توست، و رهایی از تو جز به تو ممکن نیست، و زمام هر جنبنده ای به دست تو است، و به سوی تو بازگشت هر آفریده ای است. پاک و منزهی ای خدا! چه قدر بزرگ و \nوالاست قدر و عظمت تو، و چه بزرگ است آنچه را که از خلقت تو می‌نگرم!! و چه کوچک است هر بزرگی در برابر قدرت تو، و چه باعظمت است آنچه را که از ملکوت تو مشاهده می‌کنم، و چه ناچیز است برابر آنچه که بر ما نهان است از سلطنت تو، و چه فراگیر است در این جهان نعمتهای تو، و چه کوچک است نعمتهای فراوان دنیا در برابر نعمتهای آخرت. \nوصف فرشتگان شگفت آور است آفرینش فرشتگان تو که گروهی از آن‌ها را در آسمانها سکونت دادی و از زمین بالا برده ای، آن‌ها از همه آفریدگان نسبت به تو آگاه ترند، و بیشتر از همه نسبت به تو بیمناکند، و به تو نزدیکترند فرشتگانی که در پشت پدران قرار نگرفته و در رحم مادران پرورش نیافته اند، و از آبی پست خلق نشده اند، و ناراحتی و مشکلات زندگی آنان را پراکنده نساخت، آن‌ها با مقام و مرتبتی که دارند، و از ارزشی که در نزد تو برخوردارند، و آنگونه که تو را دوست دارند، فراوان تو را اطاعت می‌کنند که اندک غفلتی در فرمان تو ندارند اگر آنچه بر آنان پوشیده است بدانند، همه کارهای خود را کوچک و ناچیز می‌شمارند، و بر خویش ایراد می‌گیرند، و می‌دانند آن گونه که باید، تو را عبادت نکرده اند، و آنچنان که سزاوار توست فرمانبردار نبودند. \nنعمتهای خداوند و سوء استفاده‌ها خدایا ستایش تو را سزاست، که آفریننده و معبودی، و بندگان را به درستی آزمایش کردی، خانه آخرت را آفریدی و سفره رنگارنگ نعمتها را گستراندی، و در آن انواع نوشیدنی، خوردنی، همسران، میهمانداران، قصرها، نهرهای روان، میوه‌ها و کشتزاران، قرار دادی. سپس پیامبری را فرستادی تا انسانها را به آن خانه و نعمتها دعوت کند، افسوس که مردم نه آن دعوت کننده را اجابت کردند، و نه به آنچه تو ترغیبشان کردی رغبت نشان دادند، و نه به آنچه تو تشویقشان کردی مشتاق شدند، بر لاشه مرداری روی آوردند که با خوردن آن رسوا شدند، و در دوستی آن همداستان گردیدند. خطر عشق و وابستگیهای دروغین هر کس به چیزی عشق ناروا ورزد، نابینایش می‌کند، و قلبش را بیمار کرده، با چشمی بیمار می‌نگرد، و با گوشی بیمار می‌شنود، خواهشهای نفس پرده عقلش را دریده، دوستی دنیا دلش را میرانده است، شیفته بی اختیار دنیا و برده آن است و برده کسانی است که چیزی از دنیا در دست دارند، دنیا به هر طرف برگردد او نیز برمی گردد، و هر چه هشدارش دهند از خدا نمی ترسد، از هیچ پنددهنده ای شنوایی ندارد، با اینکه گرفتار آمدگان دنیا را می‌نگرد که راه پس و \nپیش ندارند و در چنگال مرگ اسیرند می‌بینید که آن‌ها بلاهایی را که انتظار آن را نداشتند بر سرشان فرود آمد و دنیایی را که جاویدان می‌پنداشتند از آن‌ها جداشده و به آنچه در آخرت وعده داده شده بودند خواهند رسید، آنچه بر آنان فرود آمد وصف ناشدنی است. \nوصف چگونگی مرگ و مردن سختی جان کندن و حسرت از دست دادن دنیا به دنیاپرستان هجوم آورد، بدنها در سختی جان کندن سست شده و رنگ باختند، مرگ آرام آرام همه اندامشان را فرا گرفته، زبان را از سخن گفتن باز می‌دارد، و او در میان خانواده اش افتاده با چشم خود می‌بیند و با گوش می‌شنود و با عقل درست می‌اندیشد که عمرش را در پی چه کارهایی تباه کرده؟ و روزگارش را چگونه سپری نمود؟ به یاد ثروتهایی که جمع کرده می‌افتد، همان ثروتهایی که در جمع آوری آن‌ها چشم بر هم گذاشته و از حلال و حرام و شبهه ناک گرد آورده و اکنون گناه جمع آوری آن همه بر دوش اوست که هنگام جدایی با آن‌ها فرا رسید، و برای وارثان باقیمانده است تا از آن بهره مند گردند، و روزگار خود گذرانند، راحتی و خوشی آن برای دیگری و کیفر آن بر دوش اوست، و او در گرو این اموال است که دست خود را از پشیمانی می‌گزد. به خاطر واقعیتهایی که هنگام مرگ مشاهده کرده است. در این حالت از آنچه که در زندگی دنیا به آن علاقمند بود بی اعتناشده آرزو می‌کند، ای کاش آن کسی که در گذشته بر ثروت او رشک می‌برد، این اموال را جمع کرده بود، اما مرگ همچنان بر اعضا بدن او چیره می‌شود، تا آنکه گوش او \nمانند زبانش از کار می‌افتد، پس در میان خانواده اش افتاده نه می‌تواند با زبان سخن بگوید و نه با گوش بشنود، پیوسته به صورت آنان نگاه می‌کند، و حرکات زبانشان را می‌نگرد اما صدای کلمات آنان را نمی شنود، سپس چنگال مرگ تمام وجودش را فرا می‌گیرد، و چشم او نیز مانند گوشش از کار می‌افتد، و روح از بدن او خارج می‌شود، و چون مرداری در بین خانواده خویش بر زمین می‌ماند که از نشستن در کنار او وحشت دارند، و از او دور می‌شوند. نه سوگواران را یاری می‌کند و نه خواننده ای را پاسخ می‌دهد، سپس او را به سوی منزلگاهش در درون زمین می‌برند، و به دست عملش می‌سپارند و برای همیشه از دیدارش چشم می‌پوشند. \nوصف رستاخیز و زنده شدن دوباره تا آن زمان که پرونده این جهان بسته شود، و خواست الهی فرا رسد، و آخر آفریدگان به آغاز آن بپیوندد، و فرمان خدا در آفرینش دوباره دررسد. آنگاه آسمان را به حرکت آورد و از هم بشکافد، و زمین را به شدت بلرزاند، و تکان سخت دهد، که کوهها از جا کنده شده و در برابر هیبت و جلال پروردگاری بر یکدیگر کوبیده و متلاشی شده و با خاک یکسان گردد سپس هر کس را که در زمین به خاک سپرده شده، درآورد، و پس از فرسودگی تازه شان گرداند، و پس از پراکنده شدن، همه را گردآورد، سپس برای حسابرسی و روشن شدن اعمال از هم جدا سازد، آن‌ها را به دو دسته تقسیم فرماید، به گروهی نعمتها دهد و از گروه دیگر انتقام گیرد، اما فرمانبرداران را در جوار رحمت خود جای دهد و در بهشت جاویدان قرار دهد، خانه ای که مسکن گزیدگان آن هرگز کوچ نکنند و حالات زندگی آنان تغییر نپذیرد، در آنجا دچار ترس و وحشت نشوند، و بیماریها در آن‌ها نفوذ نخواهد کرد، خطراتی دامنگیرشان نمی شود، و سفری در پیش ندارند تا از منزلی به منزل دیگر کوچ کنند. و اما گنهکاران را در بدترین منزلگاه درآورد، و دست و پای آن‌ها را با غل و زنجیر به گردنشان درآویزد، چنانکه س \nرهایشان به پاها نزدیک گردد، جامه‌های آتشین بر بدنشان پوشاند، و در عذابی که حرارت آتش آن بسیار شدید و بر روی آن‌ها بسته، و صدای شعله‌ها هراس انگیز است قرار دهد. جایگاهی که هرگز از آن خارج نگردند، و برای اسیران آن غرامتی نپذیرند، و زنجیرهای آن گسسته نمی شود، مدتی برای عذاب آن تعیین نشده تا پایان پذیرد و نه سرآمدی تا فرا رسد. \nپارسایی پیامبر (ص) پیامبر (ص) دنیا را کوچک شمرد و در چشم دیگران آن را ناچیز جلوه داد، آن را خوار می‌شمرد و در نزد دیگران خوار و بی مقدار معرفی فرمود. و می‌دانست که خداوند برای احترام به ارزش او دنیا را از او دور ساخت و آن را برای ناچیز بودنش به دیگران بخشید، پیامبر (ص) از جان و دل به دنیا پشت کرد، و یاد آن را در دلش میراند، دوست می‌داشت که زینتهای دنیا از چشم او دور نگهداشته شود، تا از آن لباس فاخری تهیه نسازد، یا اقامت در آن را آرزوها نکند، و برای تبلیغ احکامی که قطع کننده عذرهاست تلاش کرد، و امت اسلامی را با هشدارهای لازم نصیحت کرد، و با بشارتها مردم را به سوی بهشت فرا خواند، و از آتش جهنم پرهیز داد. \nویژگیهای اهل بیت (ع) ما از درخت سرسبز رسالتیم، و از جایگاه رسالت و محل آمد و شد فرشتگان برخاستیم، ما معدنهای دانش و چشمه سارهای حکمت الهی هستیم، یاران و دوستان ما در انتظار رحمت پروردگارند و دشمنان و کینه توزان ما در انتظار کیفر و لعنت خداوند به سر می‌برند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (13, 1, 11, 'خطاب به محمد حنفیه', 'خطبه ۱۱-خطاب به محمد حنفیه \nآموزش نظامی \nاگر کوهها از جای کنده شوند تو ثابت و استوار باش، دندانها را بر هم بفشار، کاسه سرت را به خدا عاریت ده، پای بر زمین میخکوب کن، به صفوف پایانی لشگر دشمن بنگر، از فراوانی دشمن چشم پوش، و بدان که پیروزی از سوی خدای سبحان است.', 'و من كلام له (علیه السلام) لابنه محمد ابن الحنفية لمّا أعطاه الراية يوم الجمل: تَزُولُ الْجِبَالُ وَ لَا تَزُلْ، عَضَّ عَلَى نَاجِذِكَ، أَعِرِ اللَّهَ جُمْجُمَتَكَ، تِدْ فِي الْأَرْضِ قَدَمَكَ، ارْمِ بِبَصَرِكَ أَقْصَى الْقَوْمِ وَ غُضَّ بَصَرَكَ، وَ اعْلَمْ أَنَّ النَّصْرَ مِنْ عِنْدِ اللَّهِ سُبْحَانَهُ.', NULL, '(در جنگ جمل سال 36 هجرى روز پنجشنبه 15 جمادى الآخر، هنگام دادن پرچم به دست فرزندش محمد حنفيّه فرمود):آموزش نظامى:\r\nاگر كوه ها از جاى كنده شوند تو ثابت و استوار باش، دندان ها را برهم بفشار، كاسه سرت را به خدا عاريت ده، پاى بر زمين ميخكوب كن، به صفوف پايانى لشكر دشمن بنگر، از فراوانى دشمن چشم بپوش، و بدان كه پيروزى از سوى خداى سبحان است.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (14, 1, 110, 'اندرز به یاران', 'خطبه ۱۱۰-اندرز به یاران \nره آورد برخی از مبانی اعتقادی \nهمانا بهترین چیزی که انسانها می‌توانند با آن به خدای سبحان نزدیک شوند، ایمان به خدا و ایمان به پیامبر (ص) و جهاد در راه خداست، که جهاد قله بلند اسلام، و یکتا دانستن خدا، بر اساس فطرت انسانی است، برپا داشتن نماز آیین ملت اسلام، و پرداختن زکات تکلیف واجب الهی، و روزه ماه رمضان سپری برابر عذاب الهی است، و حج و عمره، نابودکننده فقر و شستشودهنده گناهان است. و صله رحم مایه فزونی مال و طول عمر، و صدقه‌های پنهانی نابودکننده گناهان، و صدقه آشکارا، مرگهای ناگهانی و زشت را باز می‌دارد، و نیکوکاری، از ذلت و خواری نگه می‌دارد. \nبه یاد خدا باشید که نیکوترین ذکر است، و آنچه پرهیزگاران را وعده دادند آرزو کنید که وعده خدا راست ترین وعده‌هاست، از راه و رسم پیامبرتان پیروی کنید که بهترین راهنمای هدایت است، رفتارتان را با روش پیامبر (ص) تطبیق دهید که هدایت کننده ترین روشهاست. ارزش قرآن و قرآن را بیاموزید، که بهترین گفتار است، و آن را نیک بفهمید که بهار دلهاست، از نور آن شفا و بهبودی خواهید که شفای سینه‌های بیمار است، و قرآن را نیکو تلاوت کنید که سودبخش ترین داستانهاست، زیرا عالمی که به غیر علم خود عمل کند، چونان جاهل سرگردانی است که از بیماری نادانی شفا نخواهد گرفت، بلکه حجت بر او قویتر و حسرت و اندوه بر او ثابت و در پیشگاه خدا سزاوار سرزنش است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (15, 1, 111, 'در نکوهش دنیا', 'خطبه ۱۱۱-در نکوهش دنیا \nهشدار از دنیاپرستی \nپس از ستایش پروردگار، \nهمانا من شما را از دنیای حرام می‌ترسانم، زیرا در کام شیرین و در دیده انسان سبز و رنگارنگ است، در شهوات و خواهشهای نفسانی پوشیده شده، و با نعمتهای زودگذر دوستی می‌ورزد، با متاع اندک زیبا جلوه می‌کند، و در لباس آرزوها خود را نشان می‌دهد، و با زینت غرور خود را می‌آراید، شادی آن دوام ندارد، و کسی از اندوه آن ایمن نیست شناخت ماهیت دنیا دنیای حرام بسیار فریبنده و بسیار زیان رساننده است، دگرگون شونده و ناپایدار، فناپذیر و مرگبار، و کشنده ای تبهکار است، و آ نگاه که به دست آرزومندان افتاد و با خواهشهای آنان دمساز شد می‌نگرند که جز سرابی بیش نیست که خدای سبحان فرمود: (زندگی چون آبی ماند که از آسمان فرو فرستادیم و به وسیله گیاهان فراوان رویید سپس خشک شده، باد آن‌ها را پراکنده نمود و خدا بر همه چیز قادر و تواناست). کسی که از دنیا شادمانی ندید جز آنکه پس از آن با اشک و آه روبرو شد، هنوز با خوشیهای دنیا روبرو نشده است که با ناراحتیها و پشت کردن آن مبتلا می‌گردد، شبنمی از رفاه و خوشی دنیا بر کسی فرود نیامده جز آنکه سیل بلاها همه چیز را از بیخ و بن می‌کنند، هرگاه صبحگاها \nن بیاری کسی برخیزد، شامگاهان خود را به ناشناسی می‌زند، اگر از یک طرف شیرین و گوارا باشد از طرف دیگر تلخ و ناگوار است. کسی از فراوانی نعمتهای دنیا کام نگرفت جز آنکه مشکلات و سختیها دامنگیر او شد، شبی را در آغوش امن دنیا به سر نبرده جز آنکه صبحگاهان بالهای ترس و وحشت بر سر او کوبید، بسیار فریبنده است و آنچه در دنیاست نیز فریبندگی دارد، فانی و زودگذر است، و هر کس در آن زندگی می‌کند فنا می‌پذیرد روش برخورد با دنیا در زاد و توشه آن جز تقوا خبری نیست، کسی که به قدر کفایت از آن بردارد در آرامش به سر می‌برد، و آن کس که در پی به دست آوردن متاع بیشتری از دنیا باشد وسائل نابودی خود را فراهم کرده، و به زودی از دست می‌رود، بسا افرادی که به دنیا اعتماد کردند، ناگهان مزه تلخ مصیبت را بدانها چشاند و بسا صاحب اطمینانی که به خاک و خونش کشید، چه انسانهای باعظمتی را که خوار و کوچک ساخت، و بسا فخرفروشانی را که به خاک ذلت افکند حکومت دنیا ناپایدار، عیش و زندگانی آن تیره و تار، گوارای آن شور، و شیرینی آن تلخ، غذای آن زهر، و اسباب و وسائل آن پوشیده است، زنده آن در معرض مردن، و تندرست آن گرفتار بیماری است، حکومت آن بر باد رفته، و \nعزیزان آن شکست خورده متاع آن نکبت آلود و پناه آورنده آن غارت زده خواهد بود. \nعبرت از گذشتگان آیا شما در جای گذشتگان خود به سر نمی برید؟ که عمرشان از شما طولانی تر و آثارشان با دوامتر، و آرزویشان درازتر، و افرادشان بیشتر، و لشگریانشان انبوه تر بودند؟ دنیا را چگونه پرستیدند؟ و آن را چگونه بر خود گزیدند؟ و سپس از آن رخت بربستند و رفتند بی توشه ای که آنان را برای رسیدن به منزلگاه کفایت کند، و بی مرکبی که آنان را به منزلشان رساند. آیا شنیده اید که دنیا خود را فدای آنان کرده باشد؟ یا به گونه ای یاریشان داده با با آنان به نیکی به سر برده باشد؟ نه هرگز!! بلکه سختی و مشکلات دنیا چنان به آن‌ها رسید که پوست و گوشتشان را درید، با سختیها آنان را سست و با مصیبتها ذلیل و خوارشان نمود، و بینی آنان را به خاک مالید و لگدمال کرد، و گردش روزگار را بر ضد آن‌ها برانگیخت، شما دیدید! دنیا آن کس را که برابر آن فروتنی کرد، و آن را برگزید، و بر همه چیز مقدم داشت، که گویا جاودانه می‌ماند، نشناخت و روی خوش نشان نداد تا آنکه از دنیا رفت، آیا جز گرسنگی توشه ای به آن‌ها سپرد؟ آیا جز در سختی فرودشان نیاورد؟ و آیا روشنی دنیا جز تاریکی، و سرانجامش جز پشیمانی بود؟ پرهیز از دنیای حرام آیا شما چنین دنیایی را ب \nر همه چیز مقدم می‌دارید؟ و بدان اطمینان می‌کنید؟ یا در آرزوی آن به سر می‌برید؟ پس دنیا بد خانه ای است برای کسی که خوشبین باشد، و یا از خطرات آن نترسد، پس بدانید! - و می‌دانید- که آن را ترک می‌کنید و از آن رخت برمی بندید، و پند گیرید از آن‌ها که گفتند: (چه کسی از ما نیرومندتر است؟) سپس آنان را به گورهایشان سپردند بی آنکه سواره کارانشان خوانند، و در قبرها فرود آوردند بی آنکه همسایگان نامیده شوند، از سطح زمین، قبرها، و از خاک کفنها، و از استخوانهای پوسیده همسایگانی پدید آمدند، که هیچ خواننده ای را پاسخ نمی دهند، و هیچ ستمی را باز نمی دارند، و نه به نوحه گری توجهی دارند، نه از باران خوشحال و نه از قحطسالی نومید می‌گردند، گرد هم قرار دارند و تنهایند، همسایه یکدیگرند اما از هم دورند، فاصله ای با هم ندارند ولی هیچگاه به دیدار یکدیگر نمی روند، نزدیکان از هم دورند، بردبارانی هستند که کینه‌ها از دل آنان رفته، بی خبرانی که حسد در دلشان فرومرده است، نه از زیان آن‌ها ترسی، و نه به دفاع آن‌ها امیدی وجود دارد، درون زمین را به جای سطح آن برای ماندن انتخاب کردند و خانه‌های تنگ و تاریک را به جای خانه‌های وسیع برگزیدند، بجای زندگی \nبا خویشاوندان، غربت را، و به جای نور، ظلمت را را برگزیدند، به زمین بازگشتند چونان که در آغاز آن را پابرهنه و عریان ترک گفتند، و با اعمال خود به سوی زندگی جاویدان و خانه همیشگی کوچ کردند، آنان که خدای سبحان فرمود: (چنانکه آفرینش را آغاز کردیم، آن را باز می‌گردانیم، و عده ای برماست و همانا این کار را انجام خواهیم داد).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (16, 1, 112, 'درباره ملک‌الموت', 'خطبه ۱۱۲-درباره ملک الموت \nعجز انسان از درک فرشتگان، \n(فرشته مرگ) \nآیا هنگامی که فرشته مرگ به خانه ای درآمد، از آمدن او آگاه می‌شوی؟ آیا هنگامی که یکی را قبض روح می‌کند او را می‌بینی؟ بچه را چگونه در شکم مادر می‌میراند؟ آیا از راه اندام مادر وارد می‌شود؟ یا روح به اجازه خداوند به سوی او می‌آید؟ یا همراه کودک در شکم مادر به سر می‌برد؟ آن کس که آفریده ای چون خود را نتواند توصیف کند. چگونه می‌تواند خدای خویش را توصیف نماید؟.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (17, 1, 113, 'در نکوهش دنیا', 'خطبه ۱۱۳-در نکوهش دنیا \nپرهیز دادن از دنیاپرستی \nشما را از دنیاپرستی می‌ترسانم، زیرا منزلگاهی برای کوچ کردن، نه منزلی برای همیشه ماندن است، دنیا خود را با غرور زینت داده و با زینت و زیبایی می‌فریبد، خانه ای که نزد خداوند بی مقدار است زیرا که حلال آن با حرام، و خوبی آن با بدی، و زندگی در آن با مرگ، و شیرینی آن با تلخیها درآمیخته است، خداوند آن را برای دوستانش انتخاب نکرد. و در بخشیدن آن به دشمنانش دریغ نفرمود خیر دنیا اندک و شر آن آماده و فراهم آمده اش پراکنده، و ملک آن غارت شده، و آبادانی آن رو به ویرانی نهاده است، چه ارزشی دارد خانه ای که پایه‌های آن در حال فرو ریختن و عمر آن چون زاد و توشه پایان می‌پذیرد؟ و چه لذتی دارد زندگانی که چونان مدت سفر به آخر می‌رسد؟ روش برخورد با دنیا مردم! آنچه را خدا واجب کرده جزو خواسته‌های خود بدانید، و در پرداختن حقوق الهی از او یاری طلبید، و پیش از آنکه مرگ شما را بخواند گوش به دعوت او دهید، \nهمانا زاهدان اگر چه خندان باشند، قلبشان می‌گرید، و اگر چه شادمان باشند اندوه آنان شدید است، و اگر چه برای نعمتهای فراوان مورد غبطه دیگران قرار گیرند اما با نفس خود در دشمنی بسیاری قرار دارند. نکوهش از غفلت زدگان یاد مرگ از دلهای شما رفته، و آرزوهای فریبنده جای آن را گرفته است، و دنیا بیش از آخرت شما را تصاحب کرده، و متاع زودرس دنیا بیش از متاع جاویدان آخرت در شما نفوذ کرده است، و دنیازدگی قیامت را از یادتان برده است. همانا شما برادران دینی یکدیگرید، چیزی جز درون پلید، و نیت زشت، شما را از هم جدا نساخته است، نه یکدیگر را یاری می‌دهید، نه خیرخواه یکدیگرید، و نه چیزی به یکدیگر می‌بخشید، و نه به یکدیگر دوستی می‌کنید. شما را چه شده است؟ که با به دست آوردن متاعی اندک از دنیا شادمان می‌گردید! و از متاع بسیار آخرت که از دست می‌دهید اندوهناک نمی شوید! اما با از دست دادن چیزی اندک از دنیا مضطرب شده که آثار پریشانی در چهره‌ها آشکار است و بی تابی می‌کنید، گویا این دنیا محل زندگی جاودان شما و وسائل آن برای همیشه از آن شماست، چیزی شما را از عیبجویی برادر دینی باز نمی دارد جز آنکه می‌ترسید مانند آن عیب را در ش \nما به رختان کشند، در بی اعتنایی به آخرت و دوستی دنیا یکدل شده اید، و هر یک از شما دین را تنها بر سر زبان می‌آورید، و چنان از این کار خشنودید همانند کارگری که کارش را به پایان رسانده و خشنودی مولای خود را فراهم کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (18, 1, 114, 'در اندرز به مردم', 'خطبه ۱۱۴-در اندرز به مردم \nارزش ستایش و شهادت به یگانگی خدا \nستایش خداوندی را سزاست که حمد و ستایش را به نعمتها، و نعمتها را به شکرگزاری پیوند داد، خدای را بر نعمتهایش آن گونه ستایش می‌کنیم که بر بلاهایش، و برای به راه آوردن نفس سرکش که در برابر اوامر الهی سستی می‌کند، و در ارتکاب زشتیها که نهی فرمود شتاب دارد، از خدا یاری می‌خواهیم، و از گناهانی که علم خدا به آن‌ها احاطه دارد و کتابش آن‌ها را برشمرده و ثبت کرده، طلب آمرزش می‌کنیم، علم خداوندی که کمتر نارسایی نداشته، و کتابی که چیزی را وانگذاشته است، به خدا ایمان داریم، ایمان کسی که غیبها را به چشم خود دیده، و بر آنچه وعده داده‌اند آگاه است، ایمانی که اخلاص آن شرک را زدوده، و یقین آن شک را نابود کرده است. و گواهی می‌دهیم که خدایی نیست جز خدای یکتا، نه شریکی دارد و نه همتایی، و گواهی می‌دهیم که حضرت محمد (ص) بنده و فرستاده اوست (که درود خدا بر او و خاندانش باد) این دو گواهی (شهادتین) گفتار را بالا می‌برند، و کردار و عمل را به پیشگاه خدا می‌رسانند، ترازویی که این دو گواهی را در آن نهند سبک نباشد، اگر بردارند با چیز دیگری سنگین نخواهد شد. \nسفارش به تقوی ای بندگان خدا! شما را به تقوای الهی سفارش می‌کنم، که زاد و توشه سفر قیامت است، تقوا توشه ای که به منزل رساند، پناهگاهی که ایمن می‌گرداند، بهترین خواننده آن را به گوش مردم خوانده، و بهترین شنونده آن را فرا گرفت، پیامبری که بهتر از هر کس سخن را به گوش مردم رساند. پس خواننده، دعوت خود را به خوبی شنواند، و شنونده خود را رستگار گرداند. ای بندگان خدا! همانا تقوای الهی دوستان خدا را از انجام محرمات باز می‌دارد، و قلبهایشان را پر از ترس خدا می‌سازد تا آنکه شبهای آنان با بی خوابی، و روزهایشان با تحمل تشنگی، و روزه داری، سپری می‌گردد، که آسایش آخرت را با رنج دنیا و سیراب شدن آنجا را با تحمل تشنگی دنیا به دست آوردند، اجل و مرگ را نزدیک دیده و در اعمال نیکو شتاب کرده اند، آرزوهای دنیایی را دروغ خواندند و مرگ را به درستی نگریستند. شناخت دنیا آری همانا دنیا خانه نابود شدن، رنج بردن از دگرگونیها، و عبرت گرفتن است، و از نشانه نابودی آنکه روزگار کمان خود را به زه کرده، تیرش به خطا نمی رود، و زخمش بهبودی ندارد، زنده را با تیر مرگ هدف قرار می‌دهد، و تندرست را با بیماری از پا درمی آورد، و نجات یافته \nرا به هلاکت می‌کشاند دنیا خورنده ایست که سیری ندارد، و نوشنده ای است که سیراب نمی شود، و نشانه رنج دنیا آنکه آدمی جمع آوری می‌کند آنچه را که نمی خورد، و می‌سازد بنایی که خود در آن مسکن نمی کند، پس به سوی پروردگار خود می‌رود نه مالی برداشته و نه خانه ای به همراه برده است. و نشانه دگرگونی دنیا آنکه، کسی که دیروز مردم به او ترحم می‌کردند امروز حسرت او می‌خوردند و آن کس را که حسرت او می‌خوردند امروز به او ترحم می‌کنند، و این نیست مگر برای نعمتهایی که به سرعت دگرگون می‌شود، و بلاهایی که ناگهان نازل می‌گردد، و نشانه عبرت انگیز بودن دنیا، آنکه، آدمی پس از تلاش و انتظار تا می‌رود به آرزوهایش برسد، ناگهان مرگ او فرا رسیده، امیدش را قطع می‌کند، نه به آرزو رسیده، و نه آنچه را آرزو داشته باقی می‌ماند. سبحان الله! شادی دنیا چقدر فریبنده و سیراب شدن از آن، چه تشنگی زاست؟ و سایه آنچه سوزان است؟ نه زمان آمده را می‌شود رد کرد و نه گذشته را می‌توان باز گرداند، پس منزه و پاک است خداوند، چقدر زنده به مرده نزدیک است برای پیوستن به آن؟ و چه دور است مرده از زنده که از آنان جدا گشتند؟ \nارزیابی دنیا و آخرت هیچ چیز بدتر از شر و بدی نیست جز کیفر و عذاب آن، و هیچ چیز نیکوتر از خیر و نیکی وجود ندارد جز پاداش آن، همه چیز دنیا شنیدن آن بزرگتر از دیدن است، و هر چیز از آخرت دیدن آن بزرگتر از شنیدن است، پس کفایت می‌کند شما را شنیدن از دیدن، و خبر دادن از پنهانیهای آخرت. آگاه باشید، هرگاه از دنیای شما کاهش یابد و به آخرت افزوده گردد بهتر از آن است که از پاداش آخرت شما کاسته و بر دنیای شما افزایش یابد، چه بسا کاهش یافته‌هایی که سودآور است، و افزایش داشته‌هایی که زیان آور بود، همانا به آنچه فرمان داده شدید گسترده تر از چیزی است که شما را از آن باز داشتند، و آنچه بر شما حلال است، بیش از چیزی است که بر شما حرام کرده اند، پس آنچه را اندک است برای آنچه بسیار است ترک کنید، و آنچه را بر شما تنگ گرفته‌اند به خاطر آنچه که شما را در گشایش قرار دادند انجام ندهید. خداوند روزی شما را ضمانت کرده و شما را به کار و تلاش امر فرمود، پس نباید روزی تضمین شده را بر آنچه که واجب شده مقدم دارید، با اینکه به خدا سوگند آنچنان نادانی و شک و یقین به هم آمیخته است که گویا روزی تضمین شده بر شما واجب است، و آنچه را که \nواجب کرده اند، برداشتند. پس در اعمال نیکو شتاب کنید، و از فرا رسیدن مرگ ناگهانی بترسید، زیرا آنچه از روزی که از دست رفته، امید بازگشت آن وجود دارد، اما عمر گذشته را نمی شود باز گرداند، آنچه امروز از بهره دنیا کم شده را می‌توان فردا به دست آورد اما آنچه دیروز از عمر گذشته، امید به بازگشت آن نیست، به آینده امیدوار و از گذشته ناامید باشید، (از خدا بترسید و جز بر مسلمانی نمیرید).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (19, 1, 115, 'در طلب باران', 'خطبه ۱۱۵-در طلب باران \nدعا برای طلب باران \nخداوندا! کوه‌های ما از بی آبی شکاف خورده، و زمین ما غبارآلود، و دامهای ما در آغلهای خود سرگردانند، و چون زن بچه مرده فریاد می‌کشند، و از رفت و آمد بی حاصل به سوی چراگاهها و آبشخورها، خسته شده اند، بار خدایا! بر ناله گوسفندان و فریاد و آه شتران ماده رحمت آور، خدایا به سرگردانی آن‌ها در راهها و ناله‌هاشان در خوابگاهها رحمت آور. بار خدایا! هنگامی به سوی تو بیرون آمدیم که خشکسالی پیاپی هجوم آورده، و ابرهای پرباران از ما پشت کرده و خشک و نامهربان بدون بارش قطره ای باران گذشتند خدایا! تو امید هر بیچاره و حل کننده مشکلات هر طلب کننده می‌باشی، خدایا تو را می‌خوانیم، در این هنگام که همه ناامید شدند، و ابر رحمت بر ما نمی بارد، و حیوانات ما نابود گردیدند، ما را به کردار ما عذاب نکنی، و به گناهان ما کیفر ندهی. خدایا! رحمت خود را با ابر پرباران، و بهار پرآب، و گیاهان خوش منظر شاداب بر ما نازل فرما، بارانی درشت قطره بر ما فرو فرست که مردگان را زنده و آنچه از دست ما رفته به ما باز گرداند. خدایا! ما را با بارانی سیراب کن که زنده کننده، سیراب سازنده، فرا گیرد و به همه جا رونده، پاکیزه و بابرکت، \nگوارا و پرنعمت، گیاه آن بسیار، شاخه‌های آن به بار نشسته، برگهایش تازه و آبدار، تا با چنان بارانی بنده ناتوان را توان بخشی، و شهرهای مرده ات را زنده سازی. خدایا! بارانی ده که بسیار ببارد تا زمینهای بلند ما پرگیاه شود، و در زمینهای پست روان گردد، و نعمتهای فراوان در اطراف ما گسترش یابد، تا با آن میوه‌های ما بسیار، گله‌های ما زنده و فراوان، و سرزمینهای دورتر از ما نیز بهره مند گردند، و روستاهای ما از آن نیرومند شوند، اینها همه از برکات گسترده و بخششهای فراوان تو باشد که بر سرزمینهای فقرزده و حیوانات وحشی ما نازل می‌گردد. خداوندا! بارانی ده! دانه درشت که پیاپی برای سیراب شدن گیاهان ما ببارد، چنانکه قطرات آن یکدیگر را برانند، و دانه‌های آن به شدت بر هم کوبیده شوند، نه رعد و برقی بی باران، و ابری بی ثمر، و کوچک و پراکنده، و نه دانه‌های ریز باران همراه با بادهای سرد. خدایا بارانی پرآب فرو فرست که قحطی زدگان به نعمتهای فراوان رسند، و آثار خشکسالی از میان برود که همانا، تویی خداوندی که پس از ناامید شدن مردم باران را فرو می‌فرستی، و رحمت خود را همه جا گسترش می‌دهی و تویی سرپرست نظام آفرینش که به ستودن سزاواری.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (20, 1, 116, 'در اندرز به یاران', 'خطبه ۱۱۶-در اندرز به یاران \nویژگیهای پیامبر اسلام (ع) \nخداوند پیامبر (ص) را فرستاد تا دعوت کننده به حق، و گواه اعمال خلق باشد، پیامبر (ص) بدون سستی و کوتاهی، رسالت پروردگارش را رسانید، و در راه خدا با دشمنانش بدون عذرتراشی جنگید، پیامبر (ص) پیشوای پرهیزکاران، و روشنی بخش چشم هدایت شدگان است. (و از همین خطبه است) \nاندرز یاران مردم اگر شما همانند من از آنچه بر شما پنهان است باخبر بودید، از خانه‌ها کوچ می‌کردید، در بیابانها سرگردان می‌شدید، و بر کردارتان اشک می‌ریختید، و چونان زنان مصیبت دیده بر سر و سینه می‌زدید، سرمایه خود را بدون نگهبان و جانشین رها می‌کردید، و هر کدام از شما تنها بکار خود می‌پرداختید، و به دیگری توجهی نداشتید. افسوس، آنچه را به شما تذکر دادند فراموش کردید، و از آنچه شما را ترساندند، ایمن گشتید، گویا عقل از سرتان پریده، و کارهای شما آشفته شده است. تعریف یارانی که به شهادت رسیدند به خدا سوگند! دوست داشتم که خدا میان من و شما جدایی اندازد، و مرا به کسی که نسبت به من سزاوارترست ملحق فرماید به خدا سوگند! آنان مردمی بودند، نیک اندیش، ترجیح دهنده بردباری، گویندگان حق، و ترک کنندگان ستم، پیش از ما به راه راست قدم گذاشته، و شتابان رفتند، در به دست آوردن زندگی جاویدان آخرت، و کرامت گوارا، پیروز گردیدند. خبر از خونریزی و شکمبارگی حجاج بن یوسف ثقفی. آگاه باشید! به خدا سوگند پسرکی از طایفه ثقیف (حجاج بن یوسف) بر شما مسلط می‌گردد که هوسباز و گردنکش و ستمگر است، سبزه زارهای شما را (اموال و داراییه \nا) می‌چرد و چربی شما را آب می‌کند، ابووذحه: بس کن. (وذحه، نوعی سوسک است، چون ماجرایی با حجاج دارد، او را (ابووذحه) خطاب فرمود، که اینجا جای آوردن آن نیست (می گویند وذحه، سوسک مخصوصی است حجاج را گزید که بدنش ورم کرد و مرد).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (21, 1, 117, 'موعظه یاران', 'خطبه ۱۱۷-موعظه یاران \nسرزنش انسانهای خودپرست. \nنه اموال خود را در راه خدایی که آن مالها را روزی شما کرد می‌بخشید، نه جانها را در راه خدای جان آفرین به خطر می‌افکنید، دوست دارید مردم برای خدا شما را گرامی دارند اما خودتان مردم را در راه خدا گرامی نمی شمارید، از فرود آمدن در خانه‌های گذشتگان عبرت گیرید، و از جدایی با نزدیکترین برادران و دوستان پند پذیرید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (22, 1, 118, 'ستودن یاران خود', 'خطبه ۱۱۸-ستودن یاران خود \nستودن یاران نیکوکار. \nشما یاران حق و برادران دینی من باشید، در روز جنگ چون سپر محافظ دورکننده ضربتها، و در خلوتها محرم اسرار منید، با کمک شما پشت کنندگان به حق را می‌کوبم و به راه می‌آورم، و فرمانبرداری استقبال کنندگان را امیدوارم، پس مرا، با خیرخواهی خالصانه، و سالم از هر گونه شک و تردید، یاری کنید، به خدا سوگند! من به مردم از خودشان سزاوارترم. (پس از جنگ صفین و نهروان، مردم را برای سرکوبی معاویه فرا خواند، سکوت کردند، فرمود شما را چه شده، آیا لال هستید؟ گروهی گفتند ای امیرالمومنین، اگر تو حرکت کنی با تو حرکت می‌کنیم).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (23, 1, 119, 'در تحریض مردم به جهاد', 'خطبه ۱۱۹-تحریض مردم به جهاد \nعلل نکوهش کوفیان. \nشما را چه می‌شود؟ هرگز ره رستگاری نپویید! و به راه عدل هدایت نگردید! آیا در چنین شرائطی سزاوارست که من از شهر خارج شوم؟ هم اکنون باید مردی از شما که من از شجاعت و دلاوری او راضی و به اطمینان داشته باشم، به سوی دشمن کوچ کند. مسوولیتهای رهبری و برای من سزاوار نیست که لشگر و شهر و بیت المال و جمع آوری خراج و قضاوت بین مسلمانان، و گرفتن حقوق درخواست کنندگان را رها سازم، آنگاه با دسته ای بیرون روم، و به دنبال دسته ای به راه افتم، و چونان تیر نتراشیده در جعبه ای خالی به این سو و آن سو سرگردان شوم. من چونان سنگ آسیاب، باید بر محور خود استوار بمانم، و همه امور کشور، پیرامون من و به وسیله من به گردش درآید، اگر من از محور خود دور شوم مدار آن بلرزد و سنگ زیرین آن فرو ریزد، به حق خدا سوگند که این پیشنهاد بدی است به خدا سوگند! اگر امیدواری به شهادت در راه خدا را نداشتم، پای در رکاب کرده از میان شما می‌رفتم، و شما را نمی طلبیدم چندان که باد شمال و جنوب می‌وزد زیرا شما بسیار طعنه زن، عیبجو، رویگردان از حق، و پرمکر و حیله اید. مادام که افکار شما پراکنده است فراوانی تعداد شما سودی ندارد، من شم \nا را به راه روشنی بردم که جز هلاک خواهان، هلاک نگردند، آن کس که استقامت کرد به سوی بهشت شتافت و آن کس که لغزید در آتش سرنگون شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (24, 1, 12, 'پس از پیروزی بر اصحاب جمل', 'خطبه ۱۲- پس از پیروزی بر اصحاب جمل \n(پس از پیروزی در جنگ بصره در سال ۳۶ هجری یکی از یاران امام علیه السلام گفت: دوست داشتم برادرم با ما بود و می‌دید که چگونه خدا تو را بر دشمنانت پیروز کرد) \nامام (ع) پرسید: آیا فکر و دل برادرت با ما بود؟ گفت: آری امام (ع) فرمود: پس او هم در این جنگ با ما بود، بلکه با ما در این نبرد شریکند آن‌هایی که حضور ندارند، در صلب پدران و رحم مادران می‌باشند، ولی با ما هم عقیده و آرمانند، به زودی متولد می‌شوند، و دین و ایمان به وسیله آنان تقویت می‌گردد.', 'و من كلام له (علیه السلام) لما أظفره الله بأصحاب الجمل:\r\nوَ قَدْ قَالَ لَهُ بَعْضُ أَصْحَابِهِ وَدِدْتُ أَنَّ أَخِي فُلَاناً كَانَ شَاهِدَنَا لِيَرَى مَا نَصَرَكَ اللَّهُ بِهِ عَلَى أَعْدَائِكَ فَقَالَ لَهُ (علیه السلام) أَ هَوَى أَخِيكَ مَعَنَا؟ فَقَالَ نَعَمْ. قَالَ فَقَدْ شَهِدَنَا، وَ لَقَدْ شَهِدَنَا فِي عَسْكَرِنَا هَذَا أَقْوَامٌ فِي أَصْلَابِ الرِّجَالِ وَ أَرْحَامِ النِّسَاءِ سَيَرْعَفُ بِهِمُ الزَّمَانُ وَ يَقْوَى بِهِمُ الْإِيمَان.', NULL, 'شركت آيندگان در پاداش گذشتگان (نقش نيّت در پاداش اعمال):\r\n(پس از پيروزى در جنگ بصره در سال 36 هجرى) يكى از ياران امام عليه السّلام گفت: دوست داشتم برادرم با ما بود و مى ديد كه چگونه خدا تو را بر دشمنانت پيروز كرد.امام عليه السّلام پرسيد: آيا فكر و دل برادرت با ما بود؟ گفت: آرى. \r\nامام عليه السّلام فرمود: پس او هم در اين جنگ با ما بود، بلكه با ما در اين نبرد شريكند آنهايى كه حضور ندارند، در صلب پدران و رحم مادران مى باشند، ولى با ما هم عقيده و آرمانند، به زودى متوّلد مى شوند، و دين و ايمان به وسيله آنان تقويت مى گردد.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (25, 1, 120, 'بیان فضیلتهای خود و اهل‌بیت', 'خطبه ۱۲۰-بیان فضیلتهای خود \nیادآوری ویژگیهای اهل بیت (ع) و اندرز یاران \nبه خدا سوگند! تبلیغ رسالتها، وفای به پیمانها، و تفسیر اوامر و هشدارهای الهی به من آموزش داده شد، درهای دانش و روشنایی امور انسانها نزد ما اهل بیت پیامبر (ص) است، آگاه باشید که قوانین دین یکی و راه‌های آن آسان و راست است، کسی که از آن برود به قافله و سرمنزل رسد، و غنیمت برد، و هر کس که از آن راه نرود گمراه شده پشیمان گردد، \nمردم! برای آن روز که زاد و توشه ذخیره می‌کنند، و اسرار آدمیان فاش می‌گردد، عمل کنید، کسی که از خرد خویش بهره مند نگردد برای پند گرفتن از عقل و فکر دیگران عاجزتر است، که آن غائب برای کمک کردن از عقل حاضر او ناتوان تر است، از آتشی بپرهیزید که حرارتش شدید، و عمق آن ناپیدا، و زیور آن غل و زنجیر، آشامیدنی آن زردآب و چرک جوشان است، آگاه باشید! نام نیکی که خدا برای کسی میان مردم قرار دهد، بهتر از مالی است که برای دیگران باقی می‌گذارد که او را ستایش نمی کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (26, 1, 121, 'خطاب به کسی که در حکمیت بر او عیب گرفته بود', 'خطبه ۱۲۱-درحکمیت \n(پس از پذیرش (حکمیت) صفین، یکی از یاران گفت: ما را از حکمیت نهی فرمودی، سپس پذیرفتی و داور تعیین کردی، ما نمی دانیم کدام یک از این دو کار درست است؟ امام دست بر روی دست کوبید و با تاسف فرمود)\n علل شکست کوفیان و پذیرش (حکمیت) \nاین سزای کسی است که بیعت با امام خود را ترک گوید، و پیمان بشکند. به خدا سوگند! هنگامی که شما را به جنگ با معاویه فرا خواندم، خوشایندتان نبود، ولی خداوند خیر شما را در آن قرار داده بود، اگر مقاومت می‌کردید، شما را راهنمائی می‌کردم و اگر به انحراف می‌رفتید شما را به راه راست برمی گرداندم، اگر سر باز می‌زدید، دوباره شما را برای مبارزه آماده می‌کردم، در آن صورت وضعیتی مطمئن داشتیم، اما دریغ، با کدام نیرو بجنگم؟ و به چه کسی اطمینان کنم؟ شگفتا، می‌خواهم به وسیله شما بیماریها را درمان کنم ولی شما درد بی درمان من شده اید، کسی را می‌مانم که خار در پایش رفته و با خار دیگری می‌خواهد آن را بیرون کشد، در حالی که می‌داند خار در تن او بیشتر شکند و بر جای ماند، خدایا! پزشکان این درد مرگبار به جان آمده، و آبرسان این شوره زار ناتوان شده است. وصف یاران شهیدی که وفادار بودند کجا هستند مردمی که \nبه اسلام دعوت شده و پذیرفتند، قرآن تلاوت کردند و معانی آیات را شناختند، به سوی جهاد برانگیخته شده چونان شتری که به سوی بچه خود روی آورد شیفته جهاد گردیدند، شمشیرها از نیام برآوردند، و گرداگرد زمین را گروه گروه، صف به صف، احاطه نمودند، بعضی شهید، و برخی نجات یافتند. هیچگاه از زنده ماندن کسی در می‌دان جنگ شادمان نبودند، و در مرگ شهیدان نیازی به تسلیت نداشتند، از گریه‌های طولانی از ترس خدا، چشمهایشان ناراحت، و از روزه داری فراوان، شکمهایشان لاغر و به پشت چسبیده بود، لبهایشان از فراوانی دعا خشک، و رنگهای صورت از شب زنده داریها زرد، و بر چهره‌هایشان غبار خشوع و فروتنی نشسته بود، آنان برادران من هستند که رفته اند، و بر ماست که تشنه ملاقاتشان باشیم، و از اندوه فراقشان انگشت حسرت به دندان گیریم. هشدار از فریبکاری شیطان همانا شیطان، راه‌های خود را به شما آسان جلوه می‌دهد، که گره‌های محکم دین شما را یکی پس از دیگری بگشاید، و بجای وحدت و هماهنگی، بر پراکندگی شما بیفزاید، از وسوسه و زمزمه و فریبکاری شیطان روی گردانید، و نصیحت آن کس را که خیرخواه شماست گوش کنید، و به جان و دل بپذیرید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (27, 1, 122, 'خطاب به خوارج', 'خطبه ۱۲۲-خطاب به خوارج \n(پس از پافشاری خوارج در شورشگری، امام (ع) به قرارگاهشان رفت و فرمود:) \nآیا همه شما در جنگ صفین بودید؟ گفتند: بعضی بودیم و برخی حضور نداشتیم فرمود: به دو گروه تقسیم شوید، تا متناسب با هر کدام سخن گویم. دو دسته شدند، امام ندا درداد که: ساکت باشید، به حرفهایم گوش فرا دهید و با جان و دل به سوی من توجه کنید، و هر کس را برای گواهی سوگند دادم با علم گواهی دهد آنگاه سخنان طولانی مطرح فرمود که: (برخی از آن خطبه این است) سیاست استعماری قرآن بر سر نیزه کردن آنگاه که شامیان در گرماگرم جنگ، و در لحظه‌های پیروزی ما، با حیله و نیرنگ، و مکر و فریبکاری قرآن‌ها را بر سر نیزه بلند کردند شماها نگفتید که: (شامیان، برادران ما و هم آیین ما هستند؟ از ما می‌خواهند از خطای آنان بگذاریم. و راضی به حاکمیت کتاب خدا شده اند، نظر ما این است که حرفشان را قبول کنیم و از آنان دست برداریم؟)، اما من به شما گفتم که: این توطئه ظاهرش ایمان و باطن آن دشمنی و کینه توزی است، آغاز آن رحمت و پایان آن پشیمانی است، پس در همین حال به مبارزه ادامه دهید، و از راهی که در پیش گرفته اید منحرف نشوید، و در جنگ دندان بر دندان فشارید، و به هیچ ندایی گ \nوش ندهید، زیرا اگر پاسخ داده شوند گمراه کننده اند، و اگر رها گردند خوار و ذلیل شوند، که همواره چنین بود، اما دریغ! شماها را دیدم که به خواسته‌های شامیان گردن نهادید، و حکمیت را پذیرفتید، سوگند بخدا! اگر از آن سر باز می‌زدم مسوول پیامدهای آن نبودم، و خد گناه آن را در پرونده من نمی افزود بخدا سوگند! اگر هم حکمیت را می‌پذیرفتم به این کار سزاوار پیروی بودم زیرا قرآن با من است، از آن هنگام که یار قرآن گشتم از آن جدا نشدم. وصف یاران جهادگر پیامبر (ص) ما با پیامبر (ص) بودیم، و همانا جنگ و کشتار گرداگرد پدران، فرزندان، برادران و خویشاوندان ما دور می‌زد، اما از وارد شدن هر مصیبت و شدتی جز بر ایمان خود نمی افزودیم، و بیشتر در پیمودن راه حق، و تسلیم بودن برابر اوامر الهی، و شکیبایی بر درد جراحتهای سوزان، مصمم می‌شدیم. هدف مبارزه با شامیان. اما امروز با پیدایش زنگارها در دین، کژیها و نفوذ شبهه‌ها در افکار، تفسیر و تاویل دروغین در دین، با برادران مسلمان خود به جنگ خونین کشانده شدیم، پس هرگاه احساس کنیم چیزی باعث وحدت ماست و به وسیله آن با یکدیگر نزدیک می‌شویم، و شکافها را پر و باقیمانده پیوندها را محکم می‌کنیم، به آن \nتمایل نشان می‌دهیم، آن را گرفته و دیگر راه را ترک می‌گوییم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (28, 1, 123, 'هنگام نبرد صفین به یارانش فرمود', 'خطبه ۱۲۳-هنگام نبرد صفین \nآموزش روانی در جنگ. \nهر کدام از شما در صحنه نبرد با دشمن، در خود شجاعت و دلاوری احساس کرد، برادرش را سست و ترسو یافت، به شکرانه این برتری باید از او دفاع کند، آنگونه که از خود دفاع می‌کند، زیرا اگر خدا خواست او را چون شما دلاور و شجاع گرداند، همانا مرگ به سرعت در جستجوی شماست، آن‌ها که در نبرد مقاومت دارند، و آن‌ها که فرار می‌کنند، هیچ کدام را از چنگال مرگ رهایی نیست و همانا گرامیترین مرگها کشته شدن در راه خداست، سوگند به آن کس که جان پسر ابوطالب در دست اوست، هزار ضربت شمشیر بر من آسانتر از مرگ در بستر استراحت، در مخالفت با خداست. هشدار از کوتاهی در نبرد گویی شما را در برخی از حمله‌ها، در حال فرار، ناله کنان چون گله ای از سوسمار می‌نگرم که نه حقی را بازپس می‌گیرید، و نه ستمی را باز می‌دارید، اینک این شما و این راه گشوده، نجات برای کسی است که خود را به می‌دان افکنده به مبارزه ادامه دهد، و هلاکت از آن کسی است که سستی ورزد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (29, 1, 124, 'تعلیم یاران در کار جنگ', 'خطبه ۱۲۴-تعلیم یاران در کار جنگ \nآموزش نظامی (تاکتیکهای رزم انفرادی) \nزره پوشیده‌ها را در پیشاپیش لشگر قرار دهید، و آن‌ها که کلاه خود ندارند، در پشت سر قرار گیرند، دندانها را در نبرد روی هم بفشارید، که تاثیر ضربت شمشیر را بر سر کمتر می‌کند، در برابر نیزه‌های دشمن، پیچ و خم به خود دهید که نیزه‌ها را می‌لغزاند و کمتر به هدف اصابت می‌کند، چشمها را فرو خوابانید، که بر دلیری شما می‌افزاید، و دل را آرام می‌کند، صداها را آهسته و خاموش سازید که سستی را می‌زداید. پرچم لشگر را بالا دارید و پیرامون آن را خالی مگذارید و جز به دست دلاوران و مدافعان سرسخت خود نسپارید، زیرا آنان که در حوادث سخت ایستادگی می‌کنند، از پرچمهای خود بهتر پاسداری می‌نمایند و آن را در دل لشگر نگاه می‌دارند، و از هر سو، از پیش و پس و اطراف، مراقب آن می‌باشند، نه از آن عقب می‌مانند که تسلیم دشمن کنند و نه از آن پیشی می‌گیرند که تنها رهایش سازند. هر کس باید برابر حریف خود بایستد و کار او را بسازد و به یاری برادر خود نیز بشتابد، و مبارزه با حریف خود را به برادر مسلمان خود وامگذارد که او در برابر دو حریف، قرار گیرد، حریف خود، و حریف برادرش آموزش معنوی سربازان بخدا سوگند! ا \nگر از شمشیر دنیا فرار کنید از شمشیر آخرت سالم نمی مانید، شما بزرگان عرب و شرافتمندان برجسته اید، در فرار از جنگ، خشم و غضب الهی و ذلت همیشگی و ننگ جاویدان قرار دارد، فرارکننده بر عمر خود نمی افزاید. و بین خود و روز مرگش مانعی نخواهد کرد، کیست که شتابان و با نشاط با جهاد خویش به سوی خدا حرکت کند؟ چونان تشنه کامی که به سوی آب می‌دود؟ بهشت در سایه نیزه‌های دلاوران است، امروز در هنگامه نبرد آنچه در دلها و سر زبانهاست آشکار می‌شود، به خدا سوگند! که من به دیدار شامیان در می‌دان نبرد شیفته ترم تا آنان به بازگشت به خانه‌هاشان! که انتظار می‌کشند. بار خدایا! اگر شامیان از حق روی گرداندند جمعشان را پراکنده، و در میانشان اختلاف و تفرقه بیفکن، و آنان را برای خطاکاریشان به هلاکت رسان. ضرورت جنگ بی امان برای شکست شامیان. همانا شامیان، بدون ضربت نیزه‌های پیاپی هرگز از جای خود خارج نشوند، ضرباتی که بدنهایشان را سوراخ نماید، چنانکه وزش باد از این سو فرو شده بدان سو درآید، ضربتی که کاسه سر را بپراکند، و استخوانهای بدن را خرد، و بازوها و پاها را قطع و به اطراف پخش کند، آنان دست برنمی دارند تا آنگاه که دسته‌های لشگر پیاپی بر آن \nان حمله کنند و آن‌ها را تیرباران نمایند، و سواران ما هجوم آورند، و صفهایشان را در هم شکنند، و لشگرهای عظیم، پشت سر لشگرهای انبوه، آن‌ها را تا شهرهایشان عقب برانند، و تا اسبها سرزمینشان را که روی در روی یکدیگر قرار دارد، و اطراف چراگاهایشان، و راه‌های آنان را، زیر سم بکوبند. (در پاسخ خوارج که ماجرای حکمیت را نمی پذیرفتند فرمود).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (30, 1, 125, 'در رابطه با خوارج وقتی حکمیت را انکار کردند', 'خطبه ۱۲۵-در رابطه با خوارج \nعلل پذیرش حکمیت در صفین. \nما افراد را داور قرار ندادیم، تنها قرآن را به حکمیت (داوری) انتخاب کردیم (که آن‌ها بر سر نیزه کرده و داوری آن را می‌خواستند) این قرآن، خطی نوشته شده که میان دو جلد پنهان است، زبان ندارد تا سخن گوید، و نیازمند به کسی است که آن را ترجمه کند، و همانا انسانها می‌توانند از آن سخن گویند، و هنگامی که شامیان ما را دعوت کردند تا قرآن را میان خویش داور گردانیم، ما گروهی نبودیم که به کتاب خدای سبحان پشت کنیم، در حالی که خدای بزرگ فرمود: (اگر در چیزی خصومت کردید آن را به خدا و رسول باز گردانید) باز گرداندن آن به خدا این است که سنت او را انتخاب کنیم، و باز گرداندن به پیامبر (ص) این است که سنت او را انتخاب کنیم، پس اگر از روی راستی به کتاب خدا داوری شود، ما از دیگر مردمان به آن سزاوارتریم، و اگر در برابر سنت پیامبر (ص) تسلیم باشند ما بدان اولی و برتریم، اما سخن شما که چرا میان خود و آنان برای حکمیت (داوری) مدت تعیین کردی؟ من این کار را کردم تا نادان خطای خود را بشناسد، و دانا بر عقیده خود استوار بماند، و اینکه شاید در این مدت آشتی و صلح، خدا کار امت را اصلاح کند و راه تحقیق و شناخت حق ب \nاز باشد، تا در جستجوی حق شتاب نورزند، و تسلیم اولین فکر گمراه کننده نگردند. \nسرزنش کوفیان و خوارج گمراه همانا برترین مردم در پیشگاه خدا کسی است که عمل به حق در نزد او دوست داشتنی تر از باطل باشد، هر چند از قدر او بکاهد و به او زیان رساند، و باطل به او سود رساند و بر قدر او بیفزاید، مردم! چرا حیران و سرگردانید؟ و از کجا به اینجا آورده شدید؟ آماده شوید برای حرکت به سوی شامیانی که از حق روی گرداندند و آن را نمی بینند، و به ستمگری روی آورده حاضر به پذیرفتن عدالت نیستند، از کتاب خدا فاصله گرفتند، و از راه راست منحرف گشتند. افسوس ای کوفیان! شما وسیله ای نیستید که بشود به آن اعتماد کرد، و نه یاوران عزیزی که بتوان به دام آن‌ها چنگ زد، شما بد نیروهایی در افروختن آتش جنگ هستید، نفرین بر شما. چقدر از دست شما ناراحتی کشیدم، یک روز آشکارا با آواز بلند شما را به جنگ می‌خوانم و روز دیگر آهسته در گوش شما زمزمه دارم، نه آزادمردان راستگویی به هنگام فرا خواندن و نه برادران مطمئنی هستید که رازدار باشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (31, 1, 126, 'درباره تقسیم بیت‌المال', 'خطبه ۱۲۶-درباره تقسیم بیت المال \nعدالت اقتصادی امام (ع) \nآیا به من دستور می‌دهید برای پیروزی خود، از جور و ستم درباره امت اسلامی که بر آن‌ها ولایت دارم، استفاده کنم؟ به خدا سوگند! تا عمر دارم، و شب و روز برقرار است، و ستارگان از پی هم طلوع و غروب می‌کنند، هرگز چنین کاری نخواهم کرد! اگر این اموال از خودم بود به گونه ای مساوی در میان مردم تقسیم می‌کردم تا چه رسد که جزو اموال خداست، آگاه باشید! بخشیدن مال به آن‌ها که استحقاق ندارند، زیاده روی و اسراف است، ممکن است در دنیا ارزش دهنده آن را بالا برد اما در آخرت پست خواهد کرد، در میان مردم ممکن است گرامیش بدارند اما در پیشگاه خدا خوار و ذلیل است. هیچ کس مالش در راهی که خدا اجازه نفرمود مصرف نکرد و به غیر اهل آن نپرداخت جز آنکه خدا او را از سپاس آنان محروم فرمود، و دوستی آن‌ها را متوجه دیگری ساخت، پس اگر روزی بلغزد و محتاج کمک آنان گردد، بدترین رفیق و سرزنش کننده ترین دوست خواهند بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (32, 1, 127, 'در خطاب به خوارج', 'خطبه ۱۲۷-در خطاب به خوارج \n(خوارج عقیده داشتند: کسی که گناه کبیره انجام دهد کافر و از اسلام خارج شده است، مگر توبه کند و دوباره مسلمان گردد) \nافشاء گمراهی خوارج \nپس اگر چنین می‌پندارید که من خطا کرده و گمراه شدم، پس چرا همه امت محمد (ص) را به گمراهی من گمراه می‌دانید؟ و خطای مرا بحساب آنان می‌گذراید؟ و آنان را برای خطای من کافر می‌شمارید؟ شمشیرها را بر گردن نهاده، کورکورانه فرود می‌آورید و گناهکار و بیگناه را به هم مخلوط کرده همه را یکی می‌پندارید؟ در حالی که شما می‌دانید، همانا رسول خدا (ص) زناکاری را که همسر داشت سنگسار کرد، سپس بر او نماز گزارد، و میراثش را به خانواده اش سپرد و قاتل را کشت و میراث او را به خانواده اش باز گرداند، دست دزد را برید و زنا کاری را که همسر نداشت تازیانه زد، و سهم آنان را از غنائم می‌داد تا با زنان مسلمان ازدواج کنند، پس پیامبر (ص) آن‌ها را برای گناهانشان کیفر می‌داد، و حدود الهی را بر آنان جاری می‌ساخت، اما سهم اسلامی آن‌ها را از بین نمی برد، و نام آن‌ها را از دفتر مسلمین خارج نمی ساخت (پس با انجام گناهان کبیره کافر نشدند) شما خوارج، بدترین مردم و آلت دست شیطان، و عامل گمراهی این و آن می‌باشید. پ \nرهیز از افراط و تفریط نسبت به امام علی (ع) به زودی دو گروه نسبت به من هلاک می‌گردند، دوستی که افراط کند و به غیر حق کشانده شود، و دشمنی که در کینه توزی با من زیاده وری کرده به راه باطل درآید، بهترین مردم نسبت به من گروه میانه رو هستند، از آن‌ها جدا نشوید، همواره با بزرگترین جمعیتها باشید که دست خدا با جماعت است، از پراکندگی بپرهیزید، که انسان تنها بهره شیطان است آنگونه که گوسفند تنها طعمه گرگ خواهد بود، آگاه باشید هر کس که مردم را به تفرقه و جدایی دعوت کند او را بکشید هر چند که زیر عمامه من باشد. علل پذیرش (حکمیت) اگر به آن دو نفر (ابوموسی و عمروعاص) رای به داوری داده شد، تنها برای این بود که آنچه را قرآن زنده کرد زنده سازند، و آنچه را قرآن مرده خوانده، بمیرانند، زنده کردن قرآن این است که دست وحدت به هم دهند و به آن عمل نمایند، و میراندن، از بین بردن پراکندگی و جدایی است، پس اگر قرآن ما را به سوی آنان بکشاند آنان را پیروی می‌کنیم، و اگر آنان را به سوی ما سوق داد باید اطاعت کنند، پدر مباد شما را! من شری براه نیانداخته، و شما را نسبت به سرنوشت شما نفریفته، و چیزی را بر شما مشتبه نساخته ام، همانا رای مردم شما \nبر این قرار گرفت که دو نفر را برای داوری انتخاب کنند، ما هم از آن‌ها پیمان گرفتیم که از قرآن تجاوز نکنند، اما افسوس که آن‌ها عقل خویش را از دست دادند، حق را ترک کردند در حالی که آن را بخوبی می‌دیدند، چون ستمگری با هواپرستی آن‌ها سازگار بود با ستم همراه شدند، ما پیش از داوری ظالمانه شان با آن‌ها شرط کردیم که به عدالت داوری کنند و بر اساس حق حکم نمایند، اما به آن پایبند نماندند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (33, 1, 128, 'از پیشامدها و فتنه‌های بصره خبر می‌دهد', 'خطبه ۱۲۸-فتنه‌های بصره \nپیشگویی امام (ع) نسبت به حوادث مهم شهر بصره \nای احنف! گویا من او را می‌بینم که با لشگری بدون غبار و سر و صدا، و بدون حرکات لگامها، و شیهه اسبان، به راه افتاده، زمین را زیر قدمهای خود چون گام شترمرغان درمی نوردند، پس وای بر کوچه‌های آباد و خانه‌های زینت شده بصره که بالهایی چونان بال کرکسان و ناودانهایی چون خرطومهای پیلان دارد. وای بر اهل بصره که بر کشتگان آنان نمی گریند، و از گمشدگانشان کسی جستجو نمی کند، من دنیا را برو، بر زمین کوبیده و چهره اش را به خاک مالیدم، و بیش از آنچه ارزش دارد، بهایش نداده ام، و با دیده ای که سزاوارست به آن نگریسته ام. \nوصف ترکهای مغول. گویا آنان را می‌بینم با رخساری چونان سپرهای چکش خورده، لباسهایی از دیباج و حریر پوشیده، که اسبهای اصیل را یدک می‌کشند و آنچنان کشتار و خونریزی دارند که مجروحان از روی بدن کشتگان حرکت می‌کنند و فراریان از اسیرشدگان کمترند. (یکی از اصحاب گفت: ای امیرمومنان تو را علم غیب دادند؟ امام (ع) خندید و به آن مرد که از طایفه بنی کلب بود فرمود) جایگاه علم غیب. ای برادر کلبی! این اخباری که اطلاع می‌دهم غیب نیست، علمی است که از دارنده علم غیب (پیامبر (ص)) آموخته ام، همانا علم غیب علم قیامت است، و آنچه خدا در گفته خود آورده که: (علم قیامت در نزد خداست، خدا باران را نازل کرده و آنچه در شکم مادران است می‌داند، و کسی نمی داند که فردا چه خواهد کرد؟ و در کدام سرزمین خواهد مرد؟) پس خداوند سبحان! از آنچه در رحم مادران است، از پسر یا دختر، از زشت یا زیبا، سخاوتمند یا بخیل، سعادتمند یا شقی آگاه است، و آن کسی که آتشگیره آتش جهنم است یا در بهشت همسایه و دوست پیامبر (ص) است، از همه اینها آگاهی دارد، این است آن علم غیبی (ذاتی) که غیر از خدا کسی نمی داند. جز اینها، علومی است که خداوند به پیامبرش تعلیم داده \n(علم غیب اکتسابی) و او به من آموخته است، پیامبر (ص) برای من دعا کرد که خدا این دسته از علوم و اخبار را در سینه ام جای دهد و اعضا و جوارح بدن من از آن پر گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (34, 1, 129, 'درباره پیمانه‌ها', 'خطبه ۱۲۹-درباره پیمانه‌ها \nوصف جامعه مسخ شده مسلمین. \nبندگان خدا! شما و آنچه از این دنیا آرزومندید، مهمانانی هستید که مدتی معین برای شما قرار داده شد، بدهکارانی هستید که مهلت کوتاهی در پرداخت آن دارید، و اعمال شما همگی حفظ می‌شود، چه بسیارند تلاشگرانی که به جایی نرسیدند، و زحمتکشانی که زیان دیدند. در روزگاری هستید که خوبی در آن پشت کرده و می‌گذرد، و بدی رویگردان شده، پیش می‌تازد، و طمع شیطان در هلاکت مردم بیشتر می‌گردد، هم اکنون روزگاری است که ساز و برگ شیطان تقویت شده، نیرنگ و فریبش همگانی گشته، و به دست آوردن شکار برای او آسان است، به هر سو می‌خواهی نگاه کن! آیا جز فقیری می‌بینی که با فقر دست و پنجه نرم می‌کند؟ یا ثروتمندی که نعمت خدا را کفران کرده و با بخل ورزیدن در ادا حقوق الهی، ثروت فراوانی گرد آورده است؟ یا سرکشی که گوش او از شنیدن پند و اندرزها کر است؟ عبرت گرفتن از دنیا کجایند خوبان و صالحان شما؟ کجایند آزادمردان و سخاوتمندان شما؟ کجایند پرهیزگاران در کسب و کار؟ کجایند پاکیزگان در راه و رسم مسلمانی؟ آیا جز این است که همگی رخت بستند و رفتند؟ و از این جهان پست و گذران و تیره کننده عیش و شادمانی گذشتند؟ مگر نه این ا \nست که شما وارث آن‌ها بر جای آنان تکیه زدید، و در میان چیزهایی بی ارزش قرار دارید که لبهای انسان به نکوهش آن می‌جنبد؟ تا قدر آن‌ها را کوچک شمرده و برای همیشه یادشان فراموش گردد. پس در این نگرانیها باید گفت: (انا لله و انا الیه راجعون). فساد آشکار شد، نه کسی باقیمانده که کار زشت را دگرگون کند، و یا از نافرمانی و معصیت باز دارد، شما با چنین وضعی می‌خواهید در خانه قدس الهی و جوار رحمت پروردگاری قرار گیرید؟ و عزیزترین دوستانش باشید؟ هرگز! خدا را نسبت به بهشت جاویدانش نمی توان فریفت، و جز با عبادت، رضایت او را نمی توان به دست آورد، نفرین بر آنان که امر به معروف می‌کنند و خود ترک می‌نمایند، و نهی از منکر دارند و خود مرتکب آن می‌شوند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (35, 1, 13, 'سرزنش مردم بصره', 'خطبه ۱۳-سرزنش مردم بصره \nعوامل سقوط جامعه (روانشناسی اجتماعی مردم بصره)\nشما سپاه یک زن بودید، و پیروان حیوان (شتر عایشه) تا شتر صدا می‌کرد می‌جنگیدید، و تا دست و پای آن قطع گردید فرار کردید، اخلاق شما پست، و پیمان شما از هم گسسته، دین شما دورویی، و آب آشامیدنی شما شور و ناگوار است، کسی که میان شما زندگی کند به کیفر گناهش گرفتار می‌شود، و آن کس که از شما دوری گزیند مشمول آمرزش پروردگار می‌گردد، گویا مسجد شما را می‌بینم که چون سینه کشتی غرق شده است، که عذاب خدا از بالا و پایین او را احاطه می‌کند، و سرنشینان آن همه غرق می‌شوند. و در روایتی است: سوگند به خدا، سرزمین شما را آب غرق می‌کند، گویا مسجد شما را می‌نگرم که چون سینه کشتی یا چونان شترمرغی که بر سینه خوابیده باشد بر روی آب مانده است. و در روایت دیگر: مانند سینه مرغ روی آب دریا. و در روایت دیگری آمده: خاک شهر شما بد بوترین خاکها است، از همه جا به آب نزدیکتر و از آسمان دورتر، و نه دهم شر و فساد در شهر شما نهفته است، کسی که در شهر شما باشد گرفتار گناه، و آنکه بیرون رود در پناه عفو خداست گویی شهر شما را می‌نگرم که غرق شده، و آب آن را فرا گرفته، چیزی از آن دیده نمی شود، مگر جاهای بلند مسجد، مانند سینه مرغ بر روی امواج آب دریا!', 'و من كلام له (علیه السلام) في ذم أهل البصرة بعد وقعة الجمل:كُنْتُمْ جُنْدَ الْمَرْأَةِ وَ أَتْبَاعَ الْبَهِيمَةِ، رَغَا فَأَجَبْتُمْ وَ عُقِرَ فَهَرَبْتُمْ. أَخْلَاقُكُمْ دِقَاقٌ وَ عَهْدُكُمْ شِقَاقٌ وَ دِينُكُمْ نِفَاقٌ وَ مَاؤُكُمْ زُعَاقٌ وَ الْمُقِيمُ بَيْنَ أَظْهُرِكُمْ مُرْتَهَنٌ بِذَنْبِهِ وَ الشَّاخِصُ عَنْكُمْ مُتَدَارَكٌ بِرَحْمَةٍ مِنْ رَبِّهِ. كَأَنِّي بِمَسْجِدِكُمْ كَجُؤْجُؤِ سَفِينَةٍ قَدْ بَعَثَ اللَّهُ عَلَيْهَا الْعَذَابَ مِنْ فَوْقِهَا وَ مِنْ تَحْتِهَا وَ غَرِقَ مَنْ فِي ضِمْنِهَا.وَ فِي رِوَايَةٍ وَ ايْمُ اللَّهِ لَتَغْرَقَنَّ بَلْدَتُكُمْ حَتَّى كَأَنِّي أَنْظُرُ إِلَى مَسْجِدِهَا كَجُؤْجُؤِ سَفِينَةٍ أَوْ نَعَامَةٍ جَاثِمَةٍ؛ وَ فِي رِوَايَةٍ كَجُؤْجُؤِ طَيْرٍ فِي لُجَّةِ بَحْرٍ.\r\nوَ فِي رِوَايَةٍ أُخْرَى: بِلَادُكُمْ أَنْتَنُ بِلَادِ اللَّهِ تُرْبَةً أَقْرَبُهَا مِنَ الْمَاءِ وَ أَبْعَدُهَا مِنَ السَّمَاءِ وَ بِهَا تِسْعَةُ أَعْشَارِ الشَّرِّ الْمُحْتَبَسُ فِيهَا بِذَنْبِهِ وَ الْخَارِجُ بِعَفْوِ اللَّهِ كَأَنِّي أَنْظُرُ إِلَى قَرْيَتِكُمْ هَذِهِ قَدْ طَبَّقَهَا الْمَاءُ حَتَّى مَا يُرَى مِنْهَا إِلَّا شُرَفُ الْمَسْجِدِ كَأَنَّهُ جُؤْجُؤُ طَيْرٍ فِي لُجَّةِ بَحْرٍ.', NULL, '(روز جمعه سال 36 هجرى پس از پايان جنگ جمل در مسجد جامع شهر در نكوهش مردم بصره فرمود): عوامل سقوط جامعه (روانشناسى اجتماعى مردم بصره):\r\nشما سپاه يك زن، و پيروان حيوان (شتر عايشه) بوديد، تا شتر صدا مى كرد مى جنگيديد، و تا دست و پاى آن قطع گرديد فرار كرديد. اخلاق شما پست، و پيمان شما از هم گسسته، دين شما دو رويى، و آب آشاميدنى شما شور و ناگوار است.\r\nكسى كه ميان شما زندگى كند به كيفر گناهش گرفتار مى شود، و آن كس كه از شما دورى گزيند مشمول آمرزش پروردگار مى گردد. گويا مسجد شما را مى بينم كه چون سينه كشتى غرق شده است، كه عذاب خدا از بالا و پايين او را احاطه مى كند، و سرنشينان آن، همه غرق مى شوند. و در روايتى است: سوگند به خدا، سرزمين شما را آب غرق مى كند، گويا مسجد شما را مى نگرم كه چون سينه كشتى يا چونان شتر مرغى كه بر سينه خوابيده باشد بر روى آب مانده است. و در روايت ديگر: مانند سينه مرغ روى آب دريا.\r\nو در روايت ديگرى آمده: خاك شهر شما بد بوترين خاكهاست، از همه جا به آب نزديك تر و از آسمان دورتر، و نُه دهم شرّ و فساد در شهر شما نهفته است، كسى كه در شهر شما باشد گرفتار گناه، و آن كه بيرون رود در پناه عفو خداست. گويى شهر شما را مى نگرم كه غرق شده، و آب آن را فرا گرفته. چيزى از آن ديده نمى شود، مگر جاهاى بلند مسجد، مانند سينه مرغ بر روى امواج آب دريا.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (36, 1, 130, 'سخنی با ابوذر', 'خطبه ۱۳۰-سخنی با ابوذر \nخداگرایی در مبارزه با ستمگران. \nای اباذر! همانا تو برای خدا به خشم آمدی، پس امید به کسی داشته باش که به خاطر او غضبناک شدی، این مردم برای دنیای خود از تو ترسیدند، و تو بر دین خویش از آنان ترسیدی، پس دنیا را که به خاطر آن از تو ترسیدند به خودشان واگذار، و با دین خود که برای آن ترسیدی از این مردم بگریز، این دنیاپرستان چه محتاجند به آنکه تو آنان را ترساندی، و چه بی نیازی از آنچه آنان تو را منع کردند، و به زودی خواهی یافت که چه کسی فردا سود می‌برد؟ و چه کسی بر او بیشتر حسد می‌ورزند؟ اگر آسمان و زمین درهای خود را بر روی بنده ای ببندند و او از خدا بترسد، خداوند راه نجاتی از میان آن دو برای او خواهد گشود، آرامش خود را تنها در حق جستجو کن، و جز باطل چیزی تو را به وحشت نیاندازد، اگر تو دنیای این مردم را می‌پذیرفتی، تو را دوست داشتند و اگر سهمی از آن برمی گرفتی دست از تو برمی داشتند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (37, 1, 131, 'در فلسفه قبول حکومت و توصیف امام حق', 'خطبه ۱۳۱-فلسفه قبول حکومت \nنکوهش کوفیان \nای مردم رنگارنگ، و دلهای پریشان و پراکنده، که بدنهایشان حاضر و عقلهایشان از آن‌ها غایب و دور است، من شما را به سوی حق می‌کشانم، اما چونان گوسفندانی که از غرش شیر فرار کنند می‌گریزید، هیهات که با شما بتوانم تاریکی را از چهره عدالت بزدایم، و کجی‌ها را که در حق راه یافته راست نمایم. \nفلسفه حکومت اسلامی خدایا تو می‌دانی که جنگ و درگیری ما برای به دست آوردن قدرت و حکومت، و دنیا و ثروت نبود، بلکه می‌خواستیم نشانه‌های حق و دین تو را در جایگاه خویش باز گردانیم، و در سرزمینهای تو اصلاح را ظاهر کنیم، تا بندگان ستمدیده ات در امن و امان زندگی کنند، و قوانین و مقررات فراموش شده تو بار دیگر اجرا گردد. شرائط رهبر اسلامی خدایا من نخستین کسی هستم که به تو روی آورد، و دعوت تو را شنید و اجابت کرد، در نماز، کسی از من جز رسول خدا (ص) پیشی نگرفت، \nهمانا شما دانستید که سزاوار نیست بخیل بر ناموس و جان و غنیمتها و احکام مسلمین، ولایت و رهبری یابد، و امامت مسلمین را عهده دار شود، تا در اموال آن‌ها حریص گردد. و نادان نیز لیاقت رهبری ندارد تا با نادانی خود مسلمانان را به گمراهی کشاند، و ستمکار نیز نمی تواند رهبر مردم باشد، که با ستم حق مردم را غصب و عطاهای آنان را قطع کند، و نه کسی که در تقسیم بیت المال عدالت ندارد زیرا در اموال و ثروت آنان حیف و میل داشته و گروهی را بر گروهی مقدم می‌دارد، و رشوه خوار در قضاوت نمی تواند امام باشد زیرا که برای داوری با رشوه گرفتن حقوق مردم را پایمال، و حق را به صاحبان آن نرساند، و آن کس که سنت پیامبر (ص) را ضایع می‌کند لیاقت رهبری ندارد زیرا که امت اسلامی را به هلاکت می‌کشاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (38, 1, 132, 'در پارسایی در دنیا', 'خطبه ۱۳۲-در پارسایی در دنیا \nستایش پروردگار \nخدا را ستایش می‌کنیم بر آنچه گرفته، و بخشیده، بر نعمتهایی که عطا کرد و آزمایشهایی که انجام داد، خداوندی که بر هر چیز پنهانی آگاه و در باطن هر چیزی حضور دارد، به آنچه در سینه‌هاست آگاه و بر آنچه دیده‌ها دزدانه می‌نگرد داناست، و گواهی می‌دهم که خدا یکی و جز او خدایی نیست، و گواهی می‌دهم که حضرت محمد (ص) برگزیده و فرستاده اوست، آن گواهی که با او درون و بیرون، قلب و زبان، هماهنگ باشد. \nیاد مرگ. به خدا سوگند! اینکه می‌گویم بازی نیست، جدی و حقیقت است، دروغ نیست، و آن چیزی جز مرگ نیست، که بانگ دعوت کننده اش رسا، و به سرعت همه را میراند، پس انبوه زندگان، و طرفداران، تو را فریب ندهند، همانا گذشتگان را دیدی که ثروتها اندوختند و از فقر و بیچارگی وحشت داشتند، و با آرزوهای طولانی فکر می‌کردند در امانند، و مرگ را دور می‌پنداشتند، دیدی چگونه مرگ بر سرشان فرود آمد؟ و آنان را از وطنشان بیرون راند؟ و از خانه امن کوچشان داد؟ که به چوبه تابوت نشستند، و مردم آن را دست به دست می‌کردند؟ و بر دوش گرفته و با سرانگشت خویش نگاه می‌داشتند؟ آیا ندیدید آنان را که آرزوهای دور و دراز داشتند، و کاخهای استوار می‌ساختند، و مالهای فراوان می‌اندوختند؟ چگونه خانه‌هایشان گورستان شد؟ و اموال جمع آوری شده شان تباه و پراکنده و از آن وارثان گردید؟ و زنان آن‌ها با دیگران ازدواج کردند؟ نه می‌توانند چیزی به حسنات بیفزایند و نه از گناه توبه کنند! تقوی و روش برخورد با دنیا کسی که جامه تقوا بر قلبش بپوشاند، کارهای نیکوی او آشکار شود، و در کارش پیروز گردد، پس در به دست آوردن بهره‌های تقوا فرصت غنیمت شمارید، و برای رسیدن \nبه بهشت جاویدان رفتاری متناسب با آن انجام دهید، زیرا دنیا برای زندگی همیشگی شما آفریده نشد، گذرگاهی است تا در آن زاد و توشه آخرت بردارید، پس با شتاب، آماده کوچ کردن باشید و مرکبهای راهوار برای حرکت مهیا دارید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (39, 1, 133, 'در ذکر عظمت پروردگار', 'خطبه ۱۳۳-ذکر عظمت پروردگار \nعظمت پروردگار \nدنیا و آخرت، خدا را فرمانبردار است، و سررشته هر دو به دست او است، آسمانها و زمینها کلیدهای خویش را به او سپردند، و درختان شاداب و سرسبز، صبحگاهان و شامگاهان در برابر خدا سجده می‌کنند، و از شاخه‌های درختان نور سرخ رنگی شعله ورشده، به فرمان او میوه‌های رسیده را به انسانها تقدیم می‌دارند. \nویژگیهای قرآن. کتاب خدا قرآن در میان شما سخنگویی است که هیچگاه زبانش از حق گویی کند و خسته نشده، و همواره گویاست، خانه ای است که ستونهای آن هرگز فرو نمی ریزد، و صاحب عزتی است که یارانش هرگز شکست ندارند. \nویژگیهای پیامبر (ص) خدا پیامبر (ص) را پس از یک دوران طولانی که دیگر پیامبران نبودند، فرستاد، و زمانی که میان طرفدارن مذاهب گوناگون نزاع درگرفته و راه اختلاف می‌پیمودند، پس او را در پی پیامبران فرستاد، و وحی را با فرستادن پیامبر (ص) ختم فرمود، پس پیامبر (ص) با تمام مخالفانی که به حق پشت کردند، و از آن منحرف گشتند به مبارزه پرداخت. \nروش برخورد با دنیا و همانا دنیا نهایت دیدگاه کوردلان است، که آن سوی دنیا را نمی نگرند، اما انسان آگاه، نگاهش از دنیا عبور کرده از پس آن سرای جاویدان آخرت را می‌بیند، پس انسان آگاه به دنیا دل نمی بندد و انسان کوردل تمام توجه اش دنیاست، بینا از دنیا زاد و توشه برگیرد و نابینا برای دنیا توشه فراهم می‌کند. \nاندرزهای جاودانه آگاه باشید، در دنیا چیزی نیست مگر آنکه صاحبش به زودی از آن سیر و از داشتن آن دلگیر می‌شود، جز ادامه زندگی، زیرا در مرگ آسایشی نمی نگرند، حیات و زندگی چونان حکمت و دانش است که حیات دل مرده، و بینایی چشمهای نابیناست و مایه شنوایی برای گوشهای کر، آبی گوارا برای تشنگان می‌باشد که همه در آن سالم و بی نیازند. ویژگیهای قرآن این قرآن است که با آن می‌توانید راه حق را بنگرید، و با آن سخن گویید، و به وسیله آن بشنوید، بعضی از قرآن از بعضی دیگر سخن می‌گوید، و برخی بر برخی دیگر گواهی می‌دهد، در شناساندن خدا اختلافی نداشته، و کسی را که همراهش شد از خدا جدا نمی سازد. \nعلل سقوط مردم مردم! گویا به خیانت و کینه ورزی اتفاق دارید، و در رفتار ریاکارانه، گیاهان روییده از سرگین را می‌مانید، در دوستی با آرزوها به وحدت رسیدید، و در جمع آوری ثروت به دشمنی پرداختید شیطان شما را در سرگردانی افکنده و غرور شما را به هلاکت می‌کشاند برای خود و شما از خدا یاری می‌طلبم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (40, 1, 134, 'راهنمائی عمر در جنگ با رومیان', 'خطبه ۱۳۴-راهنمائی عمر در جنگ \nمشاوره نظامی \nخداوند به پیروان این دین وعده داد که اسلام را سربلند و نقاط ضعف مسلمین را جبران کند، خدایی که مسلمانان را به هنگام کمی نفرات یاری کرد، و آنگاه که نمی توانستند از خود دفاع کنند، از آن‌ها دفاع کرد، اکنون زنده است و هرگز نمی میرد. هرگاه خود به جنگ دشمن روی و با آنان روبرو گردی و آسیبی بینی، مسلمانان تا دورترین شهرهای خود، دیگر پناهگاهی ندارند و پس از تو کسی نیست تا بدان روآورند. مرد دلیری را به سوی آنان روانه کن، و جنگ آزمودگان و خیرخواهان را همراه او کوچ ده، اگر خدا پیروزی داد چنان است که تو دوست داری، و اگر کار دیگری مطرح شد، تو پناه مردمان و مرجع خواهی بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (41, 1, 135, 'نکوهش مغیره', 'خطبه ۱۳۵-نکوهش مغیره \nشناخت دشمنان امام (ع) \nای فرزند لعنت شده دم بریده، و درخت بی ریشه و شاخ و برگ، تو مرا کفایت می‌کنی؟ به خدا سوگند! کسی را که تو یاور باشی خدایش نیرومند نگرداند، و آن کس را که تو دستگیری، برجای نمی ماند، از نزد ما بیرون شو، خدا خیر را از تو دور سازد، پس هر چه خواهی تلاش کن خداوند تو را باقی نگذراد، اگر از آنچه می‌توانی انجام ندهی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (42, 1, 136, 'در مسئله بیعت', 'خطبه ۱۳۶-در مسئله بیعت \nبیعت بی همانند \nبیعت شما مردم! با من بی مطالعه و ناگهانی نبود، و کار من و شما یکسان نیست، من شما را برای خدا می‌خواهم و شما مرا برای خود می‌خواهید. ای مردم برای اصلاح خودتان مرا یاری کنید، به خدا سوگند! که داد ستمدیده را از ظالم ستمگر بستانم، و مهار ستمگر را بگیرم و به آبشخور حق وارد سازم گرچه تمایل نداشته باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (43, 1, 137, 'درباره طلحه و زبیر', 'خطبه ۱۳۷-درباره طلحه و زبیر \nشناسایی طلحه و زبیر \nبخدا سوگند! طلحه و زبیر و پیروانشان، نه منکری در کارهای من سراغ دارند که برابر آن بایستند، و نه میان من و خودشان راه انصاف پیمودند، آن‌ها حقی را می‌طلبند که خود ترک کرده اند، و انتقام خونی را می‌خواهند که خود ریخته اند، اگر من در ریختن این خون شریکشان بودم آن‌ها نیز از آن سهمی دارند، و اگر خودشان تنها این خون را ریخته اند، باید از خود انتقام بگیرند. اولین مرحله عدالت آنکه خود را محکوم کنند، همانا آگاهی حقیقت بینی، با من همراه است، نه حق را از خود پوشیده داشته ام و نه بر من پوشیده بود، همانا ناکثین (اصحاب جمل) گروهی سرکش و ستمگرند، خشم و کینه، و زهر عقرب، و شبهاتی چون شب ظلمانی در دلهایشان وجود دارد، در حالی که حقیقت پدیدار و باطل ریشه کن شده، و زبانش از حرکت بر ضد حق فرومانده است. بخدا سوگند! حوضی بر ایشان پر از آب نمایم که تنها خود بتوانم آبش را بیرون کشم به گونه ای که از آب آن سیراب برنگردند، و پس از آن از هیچ گودالی آب ننوشند (یعنی نقشه ای برای آنان طرح کنم که راه فرار نداشته باشند) \nوصف بیعت بی همانند شما مردم! برای بیعت کردن به سوی من یورش آوردید، چونان مادران تازه زاییده که به طرف بچه‌های خود می‌شتابند. و پیاپی فریاد کشیدید: بیعت! بیعت! من دستان خویش فروبستم، اما شما به اصرار آن را گشودید، من از دست دراز کردن، سر باز زدم، و شما دستم را کشیدید. شکوه از طلحه و زبیر خدایا! طلحه و زبیر پیوند مرا گسستند، بر من ستم کرده و بیعت مرا شکستند، و مردم را برای جنگ با من شوراندند، خدایا آنچه را بستند تو بگشا، و آنچه را محکم رشته‌اند پایدار مفرما، آرزوهایی که برای آن تلاش می‌کنند بر باد ده، من پیش از جنگ از آن‌ها خواستم تا باز گردند، و تا هنگام آغاز نبرد انتظارشان را می‌کشیدم لکن آن‌ها به نعمت پشت پا زدند و بر سینه عافیت دست رد گذاردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (44, 1, 138, 'در اشارت به حوادث بزرگ', 'خطبه ۱۳۸-اشارت به حوادث بزرگ \nخبر از ظهور و سیستم حکومتی حضرت مهدی (ع) \nاو (حضرت مهدی (عج)) خواسته‌ها را تابع هدایت وحی می‌کند، هنگامی که مردم هدایت را تابع هوسهای خویش قرار می‌دهند، در حالی که به نام تفسیر نظریه‌های گوناگون خود را بر قرآن تحمیل می‌کنند، او نظریه‌ها و اندیشه‌ها را تابع قرآن می‌سازد، (و از همین خطبه است) \nدر آینده آتش جنگ میان شما افروخته می‌گردد، و چنگ و دندان نشان می‌دهد، با پستانهایی پرشیر، که مکیدن آن شیرین، اما پایانی تلخ و زهرآگین دارد به سوی شما می‌آید، آگاه باشید! فردایی که شما را از آن هیچ شناختی نیست، زمامداری حاکمیت پیدا می‌کند که غیر از خاندان حکومتهای امروزی است (حضرت مهدی (عجل الله تعالی فرجه الشریف)) عمال و کارگزاران حکومتها را بر اعمال بدشان کیفر خواهد داد، زمین میوه‌های دل خود (معادن طلا و نقره) را برای او بیرون می‌ریزد، و کلیدهایش را به او می‌سپارد، او روش عادلانه در حکومت حق را به شما می‌نمایاند، و کتاب خدا و سنت پیامبر (ص) را که تا آن روز متروک ماندند، زنده می‌کند، (و از همین خطبه است). \nخبر از تهاجم خونین عبدالملک مروان به کوفه گویی او را می‌بینم که از شام فریاد برمی آورد، و با پرچمهایش پیرامون کوفه را پر می‌کند، و چونان شتر خشمگین به کوفه یورش می‌آورد، زمین را با سرهای بریده فرش می‌کند، دهانش گشاده، گامهایش را سخت و سنگین بر زمین می‌کوبد، تاخت و تاز او بی امان و پایدار و هجومش سخت و دشوار است. بخدا سوگند! شما را در اطراف زمین می‌پراکند، آنگونه که اندکی از شما باقی خواهد ماند چونان باقیمانده سرمه در اطراف چشم، و این وضع خونبار تداوم یابد تا آنکه عقل از دست رفته عرب بازآید، پس باید بر سنت پیامبر (ص) باقی مانید که برپاست، و بر آثار رسالت تکیه نمایید، که آشکار است، به عهد نزدیکی که بسته اید وفادار مانید که یادگار پیامبر بر آن تکیه دارد، و بدانید این شیطان است که راه‌هایش را هموار می‌نماید تا قدم بر جای قدمهایش نهید و راهش را پی گیرید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (45, 1, 139, 'به هنگام شوری', 'خطبه ۱۳۹-به هنگام شوری \n(در سال ۲۳ هجری وقتی در شورای شش نفره تنها با تأیید داماد عثمان «عبد الرحمن» به عثمان رأی دادند و حقائق مسلم را نادیده گرفتند، فرمود) \nویژگی‌های امام علیه السلام (و هشدار از حوادث خونین آینده) \nمردم! هیچ کس پیش از من در پذیرش دعوت حق شتاب نداشت، و چون من کسی در صله رحم، و بخشش فراوان تلاش نکرد، پس به سخن من گوش فرا دهید، و منطق مرا دریابید، که در آینده ای نه چندان دور برای تصاحب خلافت شمشیرها کشیده شده، و عهد و پیمانها شکسته خواهد شد، تا آنکه بعضی از شما پیشوای گمراهان و پیرو جاهلان خواهید شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (46, 1, 14, 'در نکوهش مردم بصره', 'خطبه ۱۴-در نکوهش مردم بصره \nنقش عوامل محیط در انسان سرزمین\nشما به آب نزدیک و از آسمان دور است، عقلهای شما سست و افکار شما سفیهانه است پس شما نشانه ای برای تیرانداز، و لقمه ای برای خورنده، و صیدی برای صیاد می‌باشید.', 'و من كلام له (علیه السلام) في مثل ذلك: أَرْضُكُمْ قَرِيبَةٌ مِنَ الْمَاءِ بَعِيدَةٌ مِنَ السَّمَاءِ خَفَّتْ عُقُولُكُمْ وَ سَفِهَتْ حُلُومُكُمْ فَأَنْتُمْ غَرَضٌ لِنَابِلٍ وَ أُكْلَةٌ لِآكِلٍ وَ فَرِيسَةٌ لِصَائِلٍ.\r\n\r\n', NULL, '(قبل از آغاز جنگ جمل در نكوهش شهر بصره و مردم آن ديار مانند خطبه پيشين فرمود):\r\n\r\nنقش عوامل محيط در انسان:\r\nسرزمين شما به آب نزديك و از آسمان دور است، عقل‏هاى شما سست و افكار شما سفيهانه است. پس شما نشانه ‏اى براى تيرانداز، و لقمه‏ اى براى خورنده، و صيدى براى صيّاد مى ‏باشيد.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (47, 1, 140, 'در نهی از غیبت مردم', 'خطبه ۱۴۰-در نهی از غیبت مردم \nپرهیز دادن از غیبت و بدگویی به کسانی که گناه ندارند، و از سلامت دین برخوردارند، \nرواست که به گناهکاران ترحم کنند، و شکر این نعمت گذارند، که شکرگزاری آنان را از عیبجویی دیگران باز دارد، چرا و چگونه آن عیبجو عیب برادر خویش گوید؟ و او را به بلایی که گرفتار است سرزنش می‌کند؟ آیا به خاطر ندارد که خدا چگونه او را بخشید و گناهان او را پرده پوشی فرمود؟ چگونه دیگری را بر گناهی سرزنش می‌کند که همانند آن را مرتکب شده! یا گناه دیگری انجام داده که از آن بزرگتر است؟ به خدا سوگند! اگر خدا را در گناهان بزرگ عصیان نکرده و تنها گناه کوچک انجام داد، اما جرات او بر عیبجویی از مردم، خود گناه بزرگتری است. ای بنده خدا، در گفتن کسی شتاب مکن، شاید خدایش بخشیده باشد، و بر گناهان کوچک خود ایمن مباش، شاید برای آن‌ها کیفر داده شوی، پس هر کدام از شما که به عیب کسی آگاه است، به خاطر آنچه که از عیب خود می‌داند باید از عیبجویی دیگران خودداری کند، و شکرگزاری از عیوبی که پاک است او را مشغول دارد از اینکه دیگران را بیازارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (48, 1, 141, 'درباره نهی از غیبت', 'خطبه ۱۴۱-درباره نهی از غیبت \nپرهیز از شنیدن غیبت \nای مردم! آن کس که از برادرش اطمینان و استقامت در دین، و درستی راه و رسم را سراغ دارد، باید به گفته مردم درباره او گوش ندهد، آگاه باشید! گاهی تیرانداز، تیر افکند و تیرها به خطا می‌رود، سخن نیز چنین است، درباره کسی چیزی می‌گویند که واقعیت ندارد و گفتار باطل تباه شدنی است، و خدا شنوا و گواه است، بدانید که میان حق و باطل جز چهار انگشت فاصله نیست. شناخت حق و باطل (پرسیدند، معنای آن چیست؟ امام (ع) انگشتان خود را میان چشم و گوش گذاشت و فرمود) باطل آن است که بگویی شنیدم، و حق آن است که بگویی دیدم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (49, 1, 142, 'درباره نیکی به نااهل', 'خطبه ۱۴۲-درباره نیکی به نااهل \nشناخت جایگاه بخشش و احسان \nبرای کسی که نابجا و به ناکسان نیکی کند بهره ای جز ستایش فرومایگان، تعریف سرکشان و اشرار، و سخنان جاهلان بدگفتار ندارد، و اینها نیز تا هنگامی که به آن‌ها بخشش می‌کند ادامه دارد، دست سخاوتمندی ندارد آن کس که از بخشش در راه خدا بخل می‌ورزد! آن کس که خدا او را مالی بخشید، پس به خویشاوندان خود بخشش نماید، و سفره مهمانی خوب بگستراند، و اسیر آزاد کند، و رنجدیده را بنوازد، و مستمند را بهره مند کند، و قرض وامدار را بپردازد، و برای درک ثواب الهی در برابر پرداخت حقوق دیگران، و مشکلاتی که در این راه به او می‌رسد شکیبا باشد، زیرا به دست آوردن صفات یادشده، موجب شرافت و بزرگی دنیا و درک فضائل سرای آخرت است (ان شاء الله.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (50, 1, 143, 'در طلب باران', 'خطبه ۱۴۳-در طلب باران \nنظام آفرینش برای انسان \nآگاه باشید، زمینی که شما را بر پشت خود می‌برد، و آسمانی که بر شما سایه می‌گستراند، فرمانبردار پروردگارند، و برکت آن دو به شما نه از روی دلسوزی یا برای نزدیک شدن به شما، و نه به امید خیری است که از شما دارند، بلکه آن دو، مامور رساندن منافع شما می‌باشند، و اوامر خدا را اطاعت کردند، به آن‌ها دستور داده شد که برای مصالح شما قیام کنند و چنین کردند. فلسفه آزمایشها خداوند بندگان خود را که گناهکارند، با کمبود میوه‌ها، و جلوگیری از نزول برکات، و بستن در گنجهای خیرات، آزمایش می‌کند، برای آنکه توبه کننده ای باز گردد، و گناهکار، دل از معصیت بکند، و پندگیرنده، پند گیرد، و باز دارنده، راه نافرمانی را بر بندگان خدا ببندد، و همانا خدا استغفار و آمرزش خواستن را وسیله دائمی فرو ریختن روزی، و موجب رحمت آفریدگان قرار داد و فرمود: (از پروردگار خود آمرزش بخواهید، که آمرزنده است، برکات خود را از آسمان بر شما فرو می‌بارد، و با بخشش اموال فراوان و فرزندان، شما را یاری می‌دهد، و باغستانها و نهرهای پرآب در اختیار شما می‌گذارد) پس رحمت خدا بر آن کس که به استقبال توبه رود، و از گناهان خود پوزش طلبد، و \nپیش از آنکه مرگ او فرا رسد، اصلاح گردد. نیایش طلب باران بار خداوندا! ما از خانه‌ها، و زیر چادرها پس از شنیدن ناله حیوانات تشنه، و گریه دلخراش کودکان گرسنه، به سوی تو بیرون آمدیم، و رحمت تو را مشتاق، و فضل و نعمت تو را امیدواریم، و از غذاب و انتقام تو ترسناکیم. بار خداوندا! بارانت را بر ما ببار، و ما را مایوس برمگردان! و با خشکسالی و قحطی ما را نابود مفرما، و با اعمال زشتی که بی خردان ما انجام داده‌اند ما را به عذاب خویش مبتلا مگردان، ای مهربانترین مهربانها! بار خداوندا! به سوی تو آمدیم از چیزهایی شکایت کنیم که بر تو پنهان نیست و این هنگامی است که سختیهای طاقت فرسا ما را بیچاره کرده و خشکسالی و قحطی ما را به ستوه آورده، و پیشامدهای سخت ما را ناتوان ساخته، و فتنه‌های دشوار کارد به استخوان ما رسانده است، بار خداوندا! از تو می‌خواهیم ما را نومید برمگردانی، و با اندوه و نگرانی به خانه‌هایمان باز نفرستی، و گناهانمان را به رخمان نکشی، و اعمال زشت ما را قیاس کیفر ما قرار ندهی، خداوندا! باران رحمت خود را بر ما ببار، و برکت خویش را بر ما بگستران، و روزی و رحمتت را به ما برسان، و ما را از بارانی سیراب فرما که سودمند \nو سیراب کننده و رویاننده گیاهان باشد، که آنچه خشک شده دوباره برویاند، و آنچه مرده است زنده گرداند، بارانی که بسیار پرمنفعت، رویاننده گیاهان فراوان، که تپه‌ها و کوهها را سیراب، و در دره‌ها و رودخانه‌ها، چونان سیل جاری شود، درختان را پربرگ نماید. نرخ گرانی را پایین آورد، همانا تو بر هر چیز که خواهی توانایی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (51, 1, 144, 'فضیلت خاندان پیامبر', 'خطبه ۱۴۴-فضیلت خاندان پیامبر \nفلسفه بعثت پیامبران \nخداوند، پیامبران را برانگیخت و وحی را به آنان اختصاص داد، و پیامبران را حجت خود بر بندگان قرار داد، تا استدلالی یا جای عذری برای کسی باقی نماند، پس پیامبران انسانها را، با زبان راستگویی به راه حق فرا خواندند. آگاه باشید که خداوند از درون بندگان پرده برمی گیرد، نه آنکه بر اسرار پوشیده آنان آگاه نیست، و بر آنچه در سینه‌ها نهفته دارند بی خبر است، بلکه خواست آنان را آزمایش کند، تا کدام یک، اعمال نیکو انجام می‌دهد، و پاداش برابر نیکوکاری و کیفر مکافات درخور بدیها شد. ویژگیهای امامان دوازده گانه کجا هستند آنان که پنداشتند دانایان علم قرآنند نه ما؟ که این ادعا را بر اساس دروغ و ستمکاری بر ضد ما روا داشتند، خدا ما اهل بیت پیامبر (ص) را بالا آورده و آنان را پست و خوار نمود، به ما عطا فرمود و آن‌ها را محروم ساخت، ما را در حریم نعمتهای خویش داخل و آنان را خارج کرد، که راه هدایت را با راهنمایی ما می‌پویند، و روشنی دلهای کور را از ما می‌جویند، همانا امامان دوازده گانه همه از قریش بوده که درخت آن را در خاندان بنی‌هاشم کاشته اند، مقام ولایت و امامت درخور دیگران نیست، و دیگر مدعیان زمامداری، \nشایستگی آن را ندارند. \nشناساندن گمراهان و خبر از ستمکاری عبدالملک مروان دنیای زودگذر را برگزیدند، و آخرت جاویدان را رها کردند، چشمه زلال را گذاشتند و از آب تیره و ناگوار نوشیدند، گویا فاسق آن‌ها را می‌نگرم که با منکر و زشتیها یار است (عبدالملک مروان) و با آن انس گرفته و همنشین می‌گردد تا آنکه موی سرش در گناهان سفید گشته و خلق و خوی او رنگ گناه و منکر گیرد، در چنین حالی، کف بر لب به مردم یورش آورد، چونان موج خروشانی که از غرق کردن هر چیز باکش نباشد، یا شعله ای که تر و خشک را بسوزاند و همه چیز را خاکستر کند. اندرزهای جاودانه کجایند، عقلهای روشنی خواه از چراغ هدایت؟ و کجایند چشمهای دوخته شده بر نشانه‌های پرهیزگاری؟ کجایند دلهای به خدا پیش کش شده و در اطاعت خدا پیمان بسته؟ افسوس که دنیاپرستان بر متاع پست دنیا هجوم آوردند، و برای به دست آوردن حرام یورش آورده یکدیگر را پس زدند، نشانه بهشت و جهنم برای آنان برافراشته، اما از بهشت رویگردان و با کردار زشت خود به طرف آتش روی آوردند، پروردگارشان آنان را فرا خواند اما پشت کرده، فرار نمودند، و شیطان آنان را دعوت کرد، پذیرفته به سوی او شتابان حرکت کرده اند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (52, 1, 145, 'در فنای دنیا و نکوهش بدعت', 'خطبه ۱۴۵-در فنای دنیا \nدنیاشناسی \nای مردم! شما در این دنیا هدف تیرهای مرگ هستید، که در هر جرعه ای اندوهی گلوگیر، و در هر لقمه ای استخوان شکسته ای قرار دارد، در دنیا به نعمتی نمی رسید جز با از دست دادن نعمتی دیگر، و روزی از عمر سالخورده ای نمی گذرد مگر به ویرانی یک روز از مهلتی که دارد، و بر خوردنی او چیزی افزوده نمی شود مگر به نابود شدن روزی تعیین شده، و اثری از او زنده نمی شود مگر به نابودی اثر دیگر، و چیزی برای او تازه و نو نمی شود مگر به کهنه شدن چیز دیگر، و چیز جدید از او نمی روید، مگر به درو شدن چیزی دیگر، ریشه‌هایی رفتند که ما شاخه‌های آن می‌باشیم، چگونه شاخه‌ها بدون ریشه‌ها برقرار می‌مانند؟ \nنکوهش از بدعتها هیچ بدعتی در دین ایجاد نمی شود مگر آنکه سنتی ترک گردد، پس از بدعتها بپرهیزید، و با راه راست و جاده آشکار حق باشید، نیکوترین کارها سنتی است که سالیانی بر آن گذشته و درستی آن ثابت شده باشد، و بدترین کارها آنچه که تازه پیداشده و آینده آن روشن نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (53, 1, 146, 'راهنمائی عمر در جنگ با ایرانیان', 'خطبه ۱۴۶-راهنمائی عمر \nعلل پیروزی اسلام و مسلمین \nپیروزی و شکست اسلام، به فراوانی و کمی طرفداران آن نبود، اسلام دین خداست که آن را پیروز ساخت، و سپاه اوست که آن را آماده و یاری فرمود و رسید تا آنجا که باید برسد، در هر جا که لازم بود طلوع کرد، و ما بر وعده پروردگار خود امیدواریم که او به وعده خود وفا می‌کند، و سپاه خود را یاری خواهد کرد. جایگاه رهبر چونان ریسمانی محکم است که مهره‌ها را متحد ساخته به هم پیوند می‌دهد، اگر این رشته از هم بگسلد، مهره‌ها پراکنده و هر کدام به سویی خواهند افتاد و سپس هرگز جمع آوری نخواهند شد. عرب امروز گرچه از نظر تعداد اندک است اما با نعمت اسلام فراوانند، و با اتحاد و هماهنگی عزیز و قدرتمندند، چونان محور آسیاب، جامعه را به گردش درآور، و با کمک مردم جنگ را اداره کن. زیرا اگر تو از این سرزمین بیرون شوی، مخالفان عرب از هر سو تو را رها کرده و پیمان می‌شکنند، چنانکه حفظ مرزهای داخل که پشت سر می‌گذاری مهمتر از آن باشد که در پیش روی خواهی داشت. واقع بینی در مشاوره نظامی همانا، عجم اگر تو را در نبرد بنگرند، گویند این ریشه عرب است اگر آن را بریدید آسوده می‌گردید، و همین سبب فشار و تهاجمات پیاپی آنان \nمی شود و طمع ایشان در تو بیشتر گردد، اینکه گفتی آنان براه افتادند تا با مسلمانان پیکار کنند، ناخشنودی خدا از تو بیشتر و خدا در دگرگون ساختن آنچه که دوست ندارند تواناتر است. اما آنچه از فراوانی دشمن گفتی، ما در جنگهای گذشته با فراوانی سرباز نمی جنگیدیم، بلکه با یاری و کمک خدا مبارزه می‌کردیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (54, 1, 147, 'در هدف از بعثت', 'خطبه ۱۴۷-در هدف از بعثت \nفلسفه بعثت پیامبر (ص) \nخداوند حضرت محمد (ص) را به حق برانگیخت تا بندگان خود را از پرستش دروغین بتها رهایی بخشیده و به پرستش خود راهنمایی کند، و آنان را از پیروی شیطان نجات داده به اطاعت خود کشاند، با قرآنی که معنی آن را آشکار نمود و اساسش را استوار فرمود، تا بندگان عظمت و بزرگی خدا را بدانند که نمی دانستند، و به پروردگار اعتراف کنند پس از انکارهای طولانی، و او را به درستی بشناسند پس از آنکه نسبت به خدا آشنایی نداشتند. پس خدای سبحان در کتاب خود بی آنکه او را بنگرند خود را به بندگان شناساند، و قدرت خود را به همه نمایاند، و از قهر خود ترساند، و اینکه چگونه با کیفرها ملتی را که باید نابود کند از میان برداشت و آنان را چگونه با داس انتقام درو کرد. \nخبر از آینده تاسف بار اسلام و مسلمین همانا پس از من روزگاری بر شما فرا خواهد رسید که چیزی پنهانتر از حق، و آشکارتر از باطل، و فراوانتر از دروغ به خدا و پیامبرش، نباشد، و نزد مردم آن زمان، کالایی زیانمندتر از قرآن نیست اگر آن را درست بخوانند و تفسیر کنند، و متاعی پرسودتر از قرآن یافت نمی شود آنگاه که آن را تحریف کنند و معانی دلخواه خود را رواج دهند. در شهرها چیزی ناشناخته تر از معروف، و شناخته تر از منکر نیست، حاملان قرآن، را واگذاشته و حافظان قرآن، آن را فراموش می‌کنند، پس در آن روز قرآن و پیروانش از میان مردم رانده و مهجور می‌گردند، و هر دو غریبانه در یک راه ناشناخته سرگردانند، و پناهگاهی میان مردم ندارند، پس قرآن و پیروانش در میان مردمند اما گویا حضور ندارند، با مردمند ولی از آن‌ها بریده اند، زیرا گمراهی و هدایت هرگز هماهنگ نشوند گرچه کنار یکدیگر قرار گیرند. مردم در آن روز، در جدایی و تفرقه همداستان، و در اتحاد و یگانگی، پراکنده اند، گویی آنان پیشوای قرآن بوده و قرآن پیشوای آنان نیست، پس از قرآن جز نامی نزدشان باقی نماند و آنان جز خطی از قرآن نشناسند، و از این پیش چه کیفرها که بر نیکوکاران روا \nنداشتند، و سخن راست آنان را بر خدا دروغ پنداشتند، و کار نیکشان را پاداش بد دادند. و همانا آنان که پیش از شما زندگی می‌کردند، به خاطر آرزوهای دراز، و پنهان بودن زمان اجلها، نابود گردیدند، تا ناگهان مرگ وعده داده شده بر سرشان فرود آمد، مرگی که عذرها را نپذیرد، و درهای باز توبه را ببندد، و حوادث سخت و مجازاتهای پس از مرگ را به همراه آورد. \nپندهای حکیمانه ای مردم! هر کس از خدا خیرخواهی طلبد، توفیق یابد، و آن کس که سخنان خدا را راهنمای خود قرار دهد به راست ترین راه، هدایت شده است، پس همانا همسایه خدا در امان، و دشمن خدا ترسان است، آن کس که عظمت خدا را می‌شناسد سزاوار نیست خود را بزرگ جلوه دهد، پس بلندی قدر کسانی که بزرگی پروردگار را می‌دانند در این است که برابر او فروتنی کنند، و سلامت آنان که می‌دانند قدرت خدا چه اندازه می‌باشد در این است که برابر فرمانش تسلیم باشند، پس، از حق نگریزید چونان گریز انسان تندرست از فرد (گر) گرفته، یا انسان سالم از بیمار، و بدانید که هیچگاه حق را نخواهید شناخت جز آن که ترک کننده آن را بشناسید. هرگز به پیمان قرآن وفادار نخواهید بود مگر آنکه پیمان شکنان را بشناسید، و هرگز به قرآن چنگ نمی زنید مگر آن که رهاکننده آن را شناسایی کنید. پس رستگاری را از اهل آن جستجو کنید، که اهل بیت پیامبر (ص) رمز حیات دانش، و راز مرگ جهل و نادانی هستند، آنان که حکمتشان شما را از دانش آنان و سکوتشان از منطق آنان، و ظاهرشان از باطنشان، اطلاع می‌دهد، نه با دین خدا مخالفتی دارند، و نه در آن اختلاف می‌کنند، دین در میان آنان گواهی صاد \nق، و ساکتی سخنگوست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (55, 1, 148, 'درباره اهل بصره', 'خطبه ۱۴۸-درباره اهل بصره \nروانشناسی طلحه و زبیر در شورش بصره \nهر کدام از طلحه و زبیر، امیدوار است که حکومت را به دست آورد، و دیده به آن دوخته و رفیق خود را به حساب نمی آورد، آن دو، نه رشته ای الهی را چنگ زدند و نه با وسیله ای به خدا روی آوردند، هر کدام بار کینه رفیق خود را بر دوش می‌کشد، که به زودی پرده از روی آن کنار خواهد رفت. به خدا سوگند! اگر به آنچه می‌خواهند برسند، این جان آن را می‌گیرد و آن، این را از پای درمی آورد، هم اکنون گروهی سرکش و نافرمان بپا خاسته‌اند (ناکثین) پس خداجویان حسابگر کجایند؟ سنت پیامبر (ع) برایشان بیان گردید و اخبار امروز را به آنان گفته اند، در حالی که برای هر گمراهی علتی و برای هر عهدشکنی بهانه ای وجود دارد. سوگند بخدا! من آن کس نیستم که صدای گریه و بر سر و سینه زدن برای مرده و ندای فرشته مرگ را بشنود و عبرت نگیرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (56, 1, 149, 'پیش از وفاتش', 'خطبه ۱۴۹-پیش از وفاتش \nیاد مرگ \nای مردم! هر کس از مرگ فرار کند به هنگام فرار آن را خواهد دید، اجل سرآمد زندگی، و فرار از مرگ رسیدن به آن است، چه روزگارانی که در پی گشودن راز نهفته اش بودم اما خواست خداوند جز پنهان ماندن آن نبود، هیهات! علمی پنهان است. وصیتهای امام علی (ع) اما وصیت من نسبت به خدا، آنکه چیزی را شریک خدا قرار ندهید، و نسبت به پیامبر (ص) این است که، سنت و شریعت او را ضایع نکنید، این دو ستون محکم را برپا دارید و این دو چراغ را فروزان نگهدارید و تا آن زمان که از حق منحرف نشدید، سرزنشی نخواهید داشت، که برای هر کس به اندازه توانایی او وظیفه ای تعیین گردیده، و نسبت به افراد جاهل و نادان تخفیف داده شده است زیرا که: پروردگار رحیم، و دین استوار، و پیشوا آگاه است، من دیروز رهبر شما بودم و امروز مایه پند و عبرت شما هستم، و فردا از شما جدا خواهم شد، خدا شما و مرا بیامرزد، اگر از این ضربت و در این لغزشگاه نجات یابم، که حرفی نیست، اما اگر گامها بلغزد و از این جهان بروم، ما نیز چون دیگران در سایه شاخساران، مسیر وزش باد و باران، و زیر سایه ابرهای متراکم آسمان پراکنده می‌شویم، و آثارمان در روی زمین نابود خواهد شد. من از \nهمسایگان شما بودم، که چند روزی در کنار شما زیستم، و به زودی از من جز جسدی بی روح و ساکن پس از آن همه تلاش، و خاموش پس از آن همه گفتار، باقی نخواهد ماند، پس باید سکوت من، و بی حرکتی دست و پا و چشمها و اندام من، مایه پند و اندرز شما گردد، که از هر منطق رسایی و از هر سخن موثری عبرت انگیزتر است، وداع و خداحافظی من با شما چونان جدایی کسی است که آماده ملاقات پروردگار است، فردا ارزش ایام زندگی مرا خواهید دید، و راز درونم را خواهید دانست، پس از آن که جای مرا خالی دیدید و دیگری بر جای من نشست، مرا خواهید شناخت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (57, 1, 15, 'در رابطه با برگرداندن املاک بیت‌المال', 'خطبه ۱۵-در برگرداندن بیت المال \nسیاست اقتصادی امام (ع)\nبه خدا سوگند، بیت المال تاراج شده را هر کجا که بیابم به صاحبان اصلی آن باز می‌گردانم، گر چه با آن ازدواج کرده، یا کنیزانی خریده باشند، زیرا در عدالت گشایش برای عموم است، و آن کس که عدالت بر او گران آید، تحمل ستم برای او سخت تر است.', 'و من كلام له (علیه السلام) فيما ردّه على المسلمين من قطائع عثمان: وَ اللَّهِ لَوْ وَجَدْتُهُ قَدْ تُزُوِّجَ بِهِ النِّسَاءُ وَ مُلِكَ بِهِ الْإِمَاءُ لَرَدَدْتُهُ. فَإنَّ فِي الْعَدْلِ سَعَةً وَ مَنْ ضَاقَ عَلَيْهِ الْعَدْلُ فَالْجَوْرُ عَلَيْهِ أَضْيَقُ.', NULL, '(در باره اموال فراوان بيت المال كه عثمان به بعضى از خويشاوندان خود بخشيده بود. ابن عباس مى گويد: روز دوّم خلافت در سال 35 هجرى اين سخنرانى را ايراد فرمود). سياست اقتصادى امام عليه السّلام:\r\nبه خدا سوگند، بيت المال تاراج شده را هر كجا كه بيابم به صاحبان اصلى آن باز مى گردانم، گر چه با آن ازدواج كرده، يا كنيزانى خريده باشند، زيرا در عدالت گشايش براى عموم است، و آن كس كه عدالت بر او گران آيد، تحمّل ستم براى او سخت تر است.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (58, 1, 150, 'در اشارت به حوادث بزرگ', 'خطبه ۱۵۰-اشارت به حوادث بزرگ \nآینده بشریت و ظهور حضرت مهدی (عج) \nبه راه‌های چپ و راست رفتند، و راه ضلالت و گمراهی پیمودند، و راه روشن هدایت را گذاشتند، پس درباره آنچه که باید باشد شتاب نکنید، و آنچه را که در آینده باید بیاید دیر مشمارید، چه بسا کسی برای رسیدن چیزی شتاب می‌کند، وقتی به آن رسید دوست دارد، که ای کاش آن را نمی دید، و چه نزدیک است امروز ما به فردایی که سپیده آن خواهد دمید، ای مردم! اینک ما در آستانه تحقق وعده‌های داده شده، و نزدیکی طلوع آن چیزهایی که بر شما پوشیده و ابهام آمیز است، قرار داریم. ره آورد حکومت حضرت مهدی (عج) بدانید آن کس از ما (حضرت مهدی (عج)) که فتنه‌های آینده را دریابد، با چراغی روشنگر در آن گام می‌نهد، و بر همان سیره و روش پیامبر (ص) و امامان (ع) رفتار می‌کند تا گره‌ها را بگشاید، بردگان و ملتهای اسیر را آزاد سازد، جمعیتهای گمراه و ستمگر را پراکنده و حق جویان پراکنده را جمع آوری می‌کند، حضرت مهدی (عج) سالهای طولانی در پنهانی از مردم به سر می‌برد آنچنان که اثرشناسان، اثر قدمش را نمی شناسند، گرچه در یافتن اثر و نشانه‌ها تلاش فراوان کنند. سپس گروهی برای درهم کوبیدن فتنه‌ها آماده می‌گردند، و چونان شمشی \nرها صیقل می‌خورند، دیده‌هاشان با قرآن روشنایی گیرد، و در گوشهاشان تفسیر طنین افکند، و در صحبگاهان و شامگاهان جامهای حکمت سر می‌کشند. \nسرنوشت امت اسلامی پس از پیامبر (ص) روزگار آنان به طول انجامید تا رسوایی آن‌ها به نهایت رسید، و خود را سزاوار بلای زمانه گرداندند، و چون پایان مدت آن‌ها نزدیک شد، گروهی در فتنه‌ها آسودند و گروهی دست به حمله و پیکار با فسادگران زدند و با شکیبایی که داشتند بر خدا منت ننهادند، و جان دادن در راه خدا را بزرگ نشمردند، تا آنجا که اراده الهی به پایان دوران جاهلیت موافق شد، شمشیرها در راه خدا کشیدند، و بینشهای خود را بر شمشیر نشاندند، و طاعت پروردگار خود را پذیرفتند، و فرمان پنددهنده خود را شنیدند، در پیروزی و سربلندی زیستند. تا آنکه خدا، پیامبرش را نزد خود برد، (افسوس) که گروهی به گذشته جاهلی خود برگشتند، و با پیمودن راه‌های گوناگون به گمراهی رسیدند، و به دوستان منحرف خود پیوستند و از دوستی با مومنان بریدند که به آن امرشده بودند، و بنیان اسلامی را تغییر داده در جای دیگر بنا نهادند، آنان کانون هر خطا و گناه، و هر فتنه جو را درگاه و (پناهگاه) شدند، که سرانجام در سرگردانی فرو رفته، و در غفلت و مستی به روش و آیین فرعونیان درآمدند، یا از همه بریده و دل به دنیا بسته، یا پیوند خود را با دین گسستند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (59, 1, 151, 'خبر دادن از فتنه‌های آینده', 'خطبه ۱۵۱-فتنه‌های آینده \nارزش شهادتین \nخدای را می‌ستایم، و در راندن شیطان، و دور ساختن و نجات پیدا کردن از دامها و فریبهای آن، از خدا یاری می‌طلبم، و گواهی می‌دهم که جز خدای یگانه معبودی نیست، و شهادت می‌دهم، که حضرت محمد (ص) بنده و فرستاده برگزیده و انتخاب شده او است، که در فضل و برتری، همتایی ندارد و هرگز فقدان او جبران نگردد، شهرهایی به وجود او روشن گشت، پس از آنکه گمراهی وحشتناکی همه جا را فرا گرفته بود، و جهل و نادانی بر اندیشه‌ها غالب و قساوت و سنگدلی بر دلها مسلط بود، و مردم حرام را حلال می‌شمردند، و دانشمندان را تحقیر می‌کردند، و جدای از دین الهی زندگی کرده و در حال کفر و بی دینی جان می‌سپردند. \nنکوهش مردم گمراه شما ای عربها، هدف تیرهای بلا هستید که نزدیک است، از مستیهای نعمت بپرهیزید، و از سختیهای عذاب بترسید و بگریزید، و در فتنه‌های درهم پیچیده، به هنگام پیدایش نوزاد فتنه‌ها و آشکار شدن باطن آن‌ها، و برقرار شدن قطب و مدار آسیای آن، با آگاهی قدم بردارید. فتنه‌هایی که به رهگذرهای ناپیدا درآید، و به زشتی و رسوایی گراید، آغازش چون دوران جوانی خوش و دلربا، و آثارش چون آثار باقیمانده بر سنگ خارا زشت و دیرپاست، که ستمکاران آن را با عهدی که با یکدیگر دارند، به ارث می‌برند، نخستین آنان پیشوای آخرین و آخرین گمراهان اقتداکننده به اولین می‌باشند، آنان در به دست آوردن دنیای پست بر هم سبقت می‌گیرند، و چونان سگهای گرسنه، این مردار را از دست یکدیگر می‌ربایند، طولی نمی کشد که پیرو از رهبر، و رهبر از پیرو، بیزاری می‌جویند، و با بغض و کینه از هم جدا می‌شوند، و به هنگام ملاقات، همدیگر را نفرین می‌کنند. خبر از آینده خونین عرب سپس فتنه ای سر برآورد که سخت لرزاننده، درهم کوبنده و نابودکننده است، و قلبهایی پس از استواری می‌لغزند، و مردانی پس از درستی و سلامت، گمراه می‌گردند، و افکار و اندیشه‌ها به هنگام هجوم \nاین فتنه‌ها پراکنده و عقاید پس از آشکار شدنشان به شک و تردید دچار می‌گردد، آن کس که به مقابله با فتنه‌ها برخیزد کمرش را می‌شکند، و کسی که در فرو نشاندن آن تلاش می‌کند، او را درهم می‌کوبد، در این میان فتنه جویان چونان گورخران، یکدیگر را گاز می‌گیرند، و رشته‌های سعادت و آیین محکم شده شان لرزان می‌گردد، و حقیقت امر پنهان می‌ماند، حکمت و دانش کاهش می‌یابد، ستمگران به سخن می‌آیند، و بیابان نشینها را درهم می‌کوبند، و با سینه مرکبهای ستم، آن‌ها را خرد می‌نمایند، تکروان در غبار آن فتنه‌ها نابود گردند، و سواران باقدرت در آن به هلاکت می‌رسند، فتنه‌ها با تلخی خواسته‌ها وارد می‌شود، و خونهای تازه را می‌دوشد، نشانه‌های دین را خراب، و یقین را از بین می‌برد، فتنه‌هایی که افراد زیرک از آن بگریزند، و افراد پلید در تدبیر آن بکوشند، آن فتنه‌ها پررعد و برق و پرزحمت است. در آن پیوندهای خویشاوندی قطع می‌گردد و از اسلام جدا می‌گردند، فتنه‌ها چنان ویرانگرند که تندرستها بیمار، و مسکن گزیدگان کوچ می‌کنند، \nدر آن میان کشته ای است که خونش به رایگان ریخته، و افراد ترسویی که طالب امانند، با سوگندها آنان را فریب می‌دهند، و با تظاهر به ایمان آن‌ها را گمراه می‌کنند. رهنمودها در مقابله با فتنه‌ها پس سعی کنید شما پرچم فتنه‌ها و نشانه‌های بدعتها نباشید، و آنچه را که پیوند امت اسلامی بدان استوار است، و پایه‌های طاعت بر آن پایدار بر خود لازم شمارید، و بر خدا، ستمدیده وارد شوید نه ستمگر، از درافتادن درون دامهای شیطان، و قرار گرفتن در وادی دشمنیها بپرهیزید، و لقمه‌های حرام به شکم خود راه ندهید، شما در برابر دیدگان خداوندی قرار دارید که گناهان را حرام کرد و راه اطاعت و بندگی را آسان فرمود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (60, 1, 152, 'ستایش خدا، پیشوایان دین', 'خطبه ۱۵۲-ستایش خدا \nخداشناسی (شناخت صفات خدا) \nستایش خداوندی را که با آفرینش بندگان بر هستی خود راهنمایی فرمود، و آفرینش پدیده‌های نو، بر ازلی بودن او گواه است، و شباهت داشتن مخلوقات به یکدیگر دلیل است که او همتایی ندارد، حواس بشری ذات او را درک نمی کند، و پوششها او را پنهان نمی سازد، زیرا سازنده و ساخته شده، فرا گیرنده و فرا گرفته، پروردگار و پرورده، یکسان نیستند! یگانه است نه از روی عدد، آفریننده است نه با حرکت و تحمل رنج، شنواست بدون وسیله شنوایی، و بیناست بی آنکه چشم گشاید و برهم نهد، در همه جا حاضر است نه آن که با چیزی تماس گیرد، و از همه چیز جداست، نه اینکه فاصله بین او و موجودات باشد، آشکار است نه با مشاهده چشم، پنهان است نه به خاطر کوچکی و ظرافت، از موجودات جداست که بر آن‌ها چیره و تواناست، و هر چیزی جز او برابرش خاضع و بازگشت آن به سوی خداست. آنکه خدا را وصف کرد، محدودش نمود و آنکه محدودش کند، او را به شمار آورده، و آنکه خدا را به شمار آورد، ازلیت او را باطل کرده است، و کسی که بگوید: خدا چگونه است؟ او را توصیف کرده، و هر که بگوید: خدا کجاست؟ مکان برای او قائل شده است، خدا عالم بود آنگاه که معلومی وجود نداشت، \nپرورنده بود آنگاه که پدیده ای نبود و توانا بود آنگاه که توانایی نبود. \nعظمت امامان دوازده گانه و قرآن همانا طلوع کننده ای آشکار شد، و درخشنده ای درخشید و آشکارشونده ای آشکار گردید، و آنکه از جاده حق منحرف شد به راه راست بازگشت، خداوند گروهی را به گروهی تبدیل، و روزی را برابر روزی قرار داد، و ما چونان مانده در خشکسالان که در انتظار بارانند، انتظار چنین روزی را می‌کشیدیم. همانا! امامان دین، از طرف خدا، تدبیرکنندگان امور مردم، و کارگزاران آگاه بندگانند، کسی به بهشت نمی رود جز آنکه آنان را شناخته، و آنان او را بشناسند، و کسی در جهنم سرنگون نگردد جز آنکه منکر آنان باشد و امامان دین هم وی را نپذیرند، \nهمانا خدای متعال شما را به اسلام اختصاص داد و برای اسلام برگزید، زیرا اسلام نامی از سلامت است، و فراهم کننده کرامت جامعه می‌باشد، راه روشن آن را خدا برگزید، و حجتهای آن را روشن گردانید. ویژگیهای قرآن قرآن را فرو فرستاد که ظاهرش علم، و باطنش حکمت است، نوآوریهای آن پایان نگیرد و شگفتیهایش تمام نمی شود، در قرآن برکات و خیرات چونان سرزمینهای پرگیاه در اول بهاران، فراوان است، و چراغهای روشنی بخش تاریکیها فراوان دارد، که در نیکیها جز با کلیدهای قرآن باز نشود، و تاریکیها را جز با چراغهای آن روشنایی نمی توان بخشید، مرزهایش محفوظ، و چراگاه‌هایش را خود نگهبان است، هر درمان خواهی را درمان، و هر بی نیازی طلبی را کافی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (61, 1, 153, 'صفات گمراهان', 'خطبه ۱۵۳-صفات گمراهان\nوصف گمراهان و غفلت زدگان \nچند روز از طرف خدا به گمراه، مهلت داده شده، و او با غافلان و بی خبران در راه هلاکت قدم می‌نهد، و تمام روزها را با گناهکاران سپری می‌کند، بی آنکه از راهی برود تا به حق رسد و یا پیشوایی برگزیند که راهنمای او باشد. \nو تا آن زمان که خداوند کیفر گناهان را می‌نماید، و آنان را از پرده غفلت بیرون می‌آورد به استقبال چیزی می‌روند که بدان پشت کرده و پشت می‌کنند بدانچه روی آورده بودند، پس نه از آنچه آرزو می‌کردند و بدان رسیدند سودی بردند، و نه از آنچه حاجت خویش بدان روا کردند بهره ای به دست آوردند، من، شما و خود را از اینگونه غفلت زدگی می‌ترسانم. درمان غفلت زدگیها هر کس باید از کار خویش بهره گیرد، و انسان بینا کسی است که به درستی شنید و اندیشه نمود، پس به درستی نگریست و آگاه شد، و از عبرتها پند گرفت، سپس راه روشنی را پیمود، و از افتادن در پرتگاهها، و گم شدن در کوره راهها، دوری کرد، و کوشید تا عدالت را پاس دارد و برای گمراهان جای اعتراض باقی نگذارد، که در حق سختگیری کند، یا در سخن حق تحریف روا دارد، یا در گفتن سخن راست بترسد. پس بهوش باش ای شنونده! و از خواب غفلت بیدار شو، و از شتاب خود کم کن، و در آنچه از زبان پیامبر (ص) بر تو رسیده اندیشه کن، که ناچار به انجام آن می‌باشی و راه فراری وجود ندارد، و با کسی که رهنمودهای پیامبر (ص) را بکار نمی بندد و به جانب دیگری تمایل دارد مخالفت کن و او را با آنچه برای خود پسندیده ر \nها کن، فخرفروشی را واگذار، و از مرکب تکبر فرود آی. به یاد قبر باش که گذرگاه تو به سوی عالم آخرت است، که همانگونه به دیگران پاداش دادی به تو پاداش می‌دهند، و آنگونه که کاشتی، درو می‌کنی، آنچه امروز، پیش می‌فرستی، فردا بر آن وارد می‌شوی، پس برای خود در سرای آخرت جایی آماده کن، و چیزی پیش فرست ای شنونده! هشدار، هشدار، ای غفلت زده! بکوش، بکوش. (هیچ کس جز خدای آگاه تو را باخبر نمی سازد) \nصفات ناپسند نابودکننده از واجبات قطعی خدا در قرآن حکیم که بر آن پاداش و کیفر می‌دهد و به خاطر آن خشنودشده یا خشم می‌گیرد، این است که اگر انسان یکی از صفات ناپسند را داشته و بدون توبه خدا را ملاقات کند، هر چند خود را به زحمت اندازد، یا عملش را خالص گرداند فایده ای نخواهد داشت. و آن صفات ناپسند اینکه: شریکی برای خداوند در عبادات واجب برگزیند، یا خشم خویش را با کشتن بی گناهی فرو نشاند، یا بر بیگناهی به خاطر کاری که دیگری انجام داده عیبجویی کند، یا به خاطر حاجتی که به مردم دارد بدعتی در دین خدا بگذارد، یا با مردم به دو چهره ملاقات کند، و یا در میان آنان با دو زبان سخن بگوید، در آنچه گفتم نیک بیندیش که هر مثال را همانند فراوان است. \nروانشناسی همانا! تمام حیوانات در تلاش پر کردن شکمند، و درندگان در پی تجاوز کردن به دیگران، و زنان بی ایمان تمام همتشان آرایش زندگی و فسادانگیزی در آن است، اما مومنان فروتنند، همانا مومنان مهربانند، همانا مومنان از آینده ترسانند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (62, 1, 154, 'در فضائل اهل‌بیت', 'خطبه ۱۵۴-در فضائل اهل بیت \nضرورت پیروی از امامان دوازده گانه \nعاقل با چشم دل سرانجام کار را می‌نگرد، و پستی و بلندی آن را تشخیص می‌دهد، دعوت کننده حق، (پیامبر (ص)) دعوت خویش را به پایان رسانید، و رهبر امت به سرپرستی قیام نمود، پس دعوت کننده حق را پاسخ دهید و از رهبرتان اطاعت کنید. گروهی در دریای فتنه‌ها فرو رفته، بدعت را پذیرفته، و سنتهای پسندیده را واگذاردند، مومنان کناره گیری کرده و گمراهان و دروغگویان به سخن آمدند. مردم! ما اهل بیت پیامبر (ص) چونان پیراهن تن او، و یاران راستین او، و خزانه داران علوم و معارف وحی، و درهای ورود به آن معارف، می‌باشیم، که جز از در، هیچ کس به خانه‌ها وارد نخواهد شد، و هر کس از غیر در وارد شود، دزد نامیده می‌شود، \nمردم! درباره اهل بیت پیامبر (ص) آیات کریمه قرآن نازل شد، آنان گنجینه‌های علوم خداوند رحمانند، اگر سخن گویند، راست گویند، و اگر سکوت کنند بر آنان پیشی نجویند. شرایط امامت و رهبری پس باید امام و راهنمای مردم به مردم راست بگوید، و راه خرد پیماید و از فرزندان آخرت باشد که از آنجا آمده و بدانجا خواهد رفت، پس آن که با چشم دل بنگرد، و با دیده درون کار کند، آغاز کارش آن است که بیندیشد آیا عمل او به سود او باشد یا زیان؟ اگر به سود است ادامه دهد، و اگر زیانبار است توقف کند، زیرا عمل کننده بدون آگاهی چون رونده ای است که بیراهه می‌رود، پس هر چه شتاب کند از هدفش دورتر می‌ماند، و عمل کننده از روی آگاهی، چون رونده ای بر راه راست است، پس بیننده باید به درستی بنگرد آیا رونده راه است یا واپس گرا؟ \nروانشناسی انسانی (تاثیر جسم و روح در یکدیگر) و بدان که هر ظاهری باطنی متناسب با خود دارد، آنچه ظاهرش پاکیزه، باطن آن نیز پاک و پاکیزه است، و آنچه ظاهرش پلید، باطن آن نیز پلید است، و پیامبر راستگو (ص) فرمود: همانا خداوند بنده ای را دوست اما کردار او را دشمن می‌دارد، و کردار بنده ای را دوست اما شخص را ناخوش می‌دارد. آگاه باش هر عملی رویش دارد، و هر روینده ای از آب بی نیاز نیست، و آبها نیز گوناگون می‌باشند، پس هر درختی که آبیاریش به اندازه و نیکو باشد شاخ و برگش نیکو و میوه اش شیرین است، و آنچه آبیاریش پاکیزه نباشد درختش عیب دار و میوه اش تلخ است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (63, 1, 155, 'در آفرینش خفاش', 'خطبه ۱۵۵-در آفرینش خفاش \nوصف پروردگار \nستایش خداوندی را سزاست که تمامی صفتها از بیان حقیقت ذاتش درمانده، و بزرگی او عقلها را طرد کرده است، چنانکه راهی برای رسیدن به نهایت ملکوتش نیابد، او خدای حق و آشکار، سزاوارتر و آشکارتر از آن است که دیده‌ها می‌نگرد، عقلها نمی توانند برای او حدی تعیین نمایند، تا همانندی داشته باشد، و اندیشه‌ها و اوهام نمی توانند برای او اندازه ای مشخص کنند تا در شکل و صورتی پنداشته شود، پدیده‌ها را بی آنکه نمونه ای موجود باشد یا با مشاوری مشورت نماید، و یا از قدرتی کمک و مدد بگیرد آفرید، پس با فرمان او خلقت آن به کمال رسید، و اطاعت پروردگار را پذیرفت و پاسخ مثبت داد و به خدمت شتافت، گردن به فرمان او نهاد و سرپیچی نکرد. \nشگفتیهای خفاش از زیباییهای صنعت پروردگاری، و شگفتیهای آفرینش او، همان اسرار پیچیده حکمت در آفریدن خفاشان است، روشنی روز که همه چیز را می‌گشاید چشمانشان را می‌بندد، و تاریکی شب که هر چیز را به خواب فرو می‌برد، چشمان آن‌ها را باز می‌کند، چگونه چشمان خفاش کم بین است که نتواند از نور آفتاب درخشنده روشنی گیرد؟ نوری که با آن راه‌های زندگی خود را بیابد، و در پرتو آشکار خورشید خود را به جاهایی رساند که می‌خواهد، و روشنی آفتاب خفاش را از رفتن در تراکم نورهای تابنده اش باز می‌دارد، و در خلوتگاه‌های تاریک پنهان می‌سازد، که از حرکت در نور درخشان ناتوان است، پس خفاش در روز پلکها را بر سیاهی دیده‌ها اندازد، و شب را چونان چراغی برمی گزیند، که در پرتو تاریکی آن روزی خود را جستجو، و سیاهی شب دیده‌های او را نمی بندد، و به خاطر تاریکی زیاد، از حرکت و تلاش باز نمی ماند پس آنگاه که خورشید پرده از رخ بیفکند، و سپیده صبحگاهان بدمد، و لانه تنگ سوسمارها از روشنی آن روشن گردد، شب پره، پلکها برهم نهد، و بر آنچه در تاریکی شب به دست آورده قناعت کند. پس پاک و منزه است خدایی که شب را برای خفاشان چونان روز روشن و مایه به دست آوردن \nروزی قرار دارد، و روز را چونان شب تار مایه آرامش و استراحت آن‌ها انتخاب فرمود، و بالهایی از گوشت برای پرواز آن‌ها آفرید، تا به هنگام نیاز به پرواز از آن استفاده کنند، این بالها، چونان لاله‌های گوشند بی پر و رگهای اصلی، اما جای رگها و نشانه‌های آن را به خوبی مشاهده خواهی کرد. برای شب پره‌ها دو بال قرار داد، نه آنقدر نازک که درهم بشکند، و نه چندان محکم که سنگینی کند، پرواز می‌کنند در حالی که فرزندانشان به آن‌ها چسبیده و به مادر پناه برده اند، اگر فرود آیند با مادر فرود می‌آیند، و اگر بالا روند با مادر اوج می‌گیرند، از مادرانشان جدا نمی شوند تا آن هنگام که اندام جوجه نیرومند و بالها قدرت پرواز کردن پیدا کند، و بداند که راه زندگی کردن کدام است؟ و مصالح خویش را بشناسد. پس پاک و منزه است پدیدآورنده هر چیزی که بدون هیچ الگویی باقیمانده از دیگری، همه چیز را آفرید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (64, 1, 156, 'خطاب به مردم بصره و اخبار از حادثه‌های آینده', 'خطبه ۱۵۶-خطاب به مردم بصره \nضرورت اطاعت از رهبری، (و نکوهش از نافرمانی عایشه) \nمردم بصره! در پیدایش فتنه‌ها هر کس که می‌تواند خود را به اطاعت پروردگار عزیز و برتر، مشغول دارد چنان کند، اگر از من پیروی کنید، به خواست خدا شما را به راه بهشت خواهم برد، هر چند سخت و دشوار و پر از تلخیها باشد. اما عایشه! پس افکار و خیالات زنانه بر او چیره شد، و کینه‌ها در سینه اش چون کوره آهنگری شعله ور گردید، اگر از او می‌خواستند آنچه را که بر ضد من انجام داد نسبت به دیگری روا دارد سر باز می‌زد، به هر حال احترام نخست او برقرار است و حسابرسی اعمال او با خدای بزرگ است. \nره آورد ایمان ایمان روشنترین راهها و نورانی ترین چراغهاست، با ایمان به اعمال صالح می‌توان راه برد، و با اعمال نیکو به ایمان می‌توان دسترسی پیدا کرد، با ایمان، علم و دانش آبادان است، و با آگاهی لازم، انسان از مرگ می‌هراسد، و با مرگ دنیا پایان می‌پذیرد، و با دنیا، توشه آخرت فراهم می‌شود، و با قیامت بهشت نزدیک می‌شود، و جهنم برای بدکاران آشکار می‌گردد، و مردم جز قیامت قرارگاهی ندارند، و شتابان به سوی می‌دان مسابقه می‌روند تا به منزلگاه آخرین رسند. \nیادآوری برخی از ارزشهای اخلاقی و ویژگیهای قرآن گویا می‌نگرم، همه از قبرها خارج شده به سوی منزلگاه‌های آخرین در حرکتند، هر خانه ای در بهشت به شخصی تعلق دارد که نه دیگری را می‌پذیرند و نه از آنجا به جای دیگری انتقال می‌یابند. همانا (امر به معروف) و (نهی از منکر) دو صفت از اوصاف پروردگارند که نه اجل را نزدیک می‌کنند و نه روزی را کاهش می‌دهند. بر شما باد عمل کردن به قرآن، که ریسمان محکم الهی، و نور آشکار و درمانی سودمند است، که تشنگی را فرو نشاند، نگهدارنده کسی است که به آن تمسک جوید و نجات دهنده آن کس است که به آن چنگ آویزد، کجی ندارد تا راست شود، و گرایش به باطل ندارد تا از آن باز گردانده شود، و تکرار و شنیدن پیاپی آیات کهنه اش نمی سازد، و گوش از شنیدن آن خسته نمی شود. کسی که با قرآن سخن بگوید راست گفته و هر کس بدان عمل کند پیشتاز است \nدر این جا مردی بلند شد و گفت: ای امیرالمومنین ما را از فتنه آگاه کن، آیا نسبت به فتنه، از پیامبر خدا (ص) سوالی نفرموده ای؟ پاسخ داد: (خبر از فتنه‌ها و شهادت خویش) آنگاه که خداوند آیه ۱ و ۲ سوره عنکبوت را نازل کرد که: (آیا مردم خیال می‌کنند چونکه گفتند ایمان آوردیم، بدون آزمایش رها می‌شوند؟) دانستم که تا پیامبر (ص) در میان ماست آزمایش نمی گردیم. پرسیدم ای رسول خدا! این فتنه و آزمایش کدام است که خدا شما را بدان آگاهی داده است؟ فرمود: ای علی! پس از من امت اسلامی به فتنه و آزمون دچار می‌گردند. گفتم ای رسول خدا مگر جز این است که در روز (احد) که گروهی از مسلمانان به شهادت رسیدند. و شهادت نصیب من نشد و سخت بر من گران آمد، تو به من فرمودی، ای علی! مژده باد تو را که شهادت در پی تو خواهد آمد. پیامبر (ص) به من فرمود: (همانا این بشارت تحقق می‌پذیرد، در آن هنگام صبر تو چگونه است؟) گفتم ای رسول خدا (ص) چنین موردی جای صبر و شکیبایی نیست بلکه جای مژده شنیدن و شکرگزاری است. و پیامبر خدا (ص) فرمود (ای علی! همانا این مردم به زودی با اموالشان دچار فتنه و آزمایش می‌شوند، و در دینداری بر خدا منت می‌گذارند، با این ح \nال انتظار رحمت او را دارند، و از قدرت و خشم خدا، خود را ایمن می‌پندارند، حرام خدا را با شبهات دروغین، و هوسهای غفلت زا، حلال می‌کنند، شراب را به بهانه اینکه (آب انگور) است و رشوه را که (هدیه) است و ربا را که (نوعی معامله) است حلال می‌شمارند). گفتم ای رسول خدا: در آن زمان مردم را در چه پایه ای بدانم؟ آیا در پایه ارتداد؟ یا فتنه و آزمایش؟ پاسخ فرمود: (در پایه ای از فتنه و آزمایش).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (65, 1, 157, 'سفارش به پرهیزکاری', 'خطبه ۱۵۷-سفارش به پرهیزکاری \nعبرت از گذشتگان \nستایش خداوندی را سزاست که حمد را کلید یاد خویش، و سبب فزونی فضل و رحمت خود، و راهنمای نعمتها و عظمتش قرار داده است. بندگان خدا! روزگار بر آیندگان چنان می‌گذرد که بر گذشتگان گذشت، آنچه گذشت باز نمی گردد، و آنچه هست جاویدان نخواهد ماند، پایان کارش با آغاز آن یکی است، ماجراها و رویدادهای آن همانند یکدیگرند، و نشانه‌های آن آشکار است. گویا پایان زندگی و قیامت، شما را فرا می‌خواند، چونان خواندن ساربان، شتربچه را، پس آن کس که جز به حساب نفس خویش پردازد، خود را در تاریکیها سرگردان، و در هلاکت افکنده است، شیطانها مهارش را گرفته و به سرکشی و طغیان می‌کشانند، و رفتار زشت او را در دیده اش زیبا می‌نمایانند، پس بدانید که بهشت پایان راه پیشتازان و آتش جهنم سرانجام کسانی است که سستی می‌ورزند. \nضرورت تقوی و خودسازی ای بندگان خدا! بدانید که تقوا، دژی محکم و شکست ناپذیر است، اما هرزگی و گناه، خانه ای در حال فرو ریختن و خوارکننده است که از ساکنان خود دفاع نخواهد کرد، و کسی که بدان پناه برد در امان نیست، آگاه باشید با پرهیزکاری، ریشه‌های گناهان را می‌توان برید، و با یقین می‌توان به برترین جایگاه معنوی، دسترسی پیدا کرد. ای بندگان خدا را، خدا را، در حق نفس خویش که از همه چیز نزد شما گرامی تر و دوست داشتنی تر است، پروا کنید، همانا خدا، راه حق را برای شما آشکار کرده و جاده‌های آن را روشن نگاه داشت، پس یا شقاوت دامنگیر یا رستگاری جاویدان در انتظار شماست. پس در این دنیای نابود شدنی برای زندگی جاویدان آخرت، توشه برگیرید، که شما را به زاد و توشه راهنمایی کردند، و به کوچ کردن از دنیا فرمان دادند، و شما را برای پیمودن راه قیامت برانگیختند، همانا شما چونان کاروانی هستید که در جایی به انتظار مانده و نمی دانند در چه زمانی آنان را فرمان حرکت می‌دهند. \nآگاه باشید! با دنیا چه می‌کند کسی که برای آخرت آفریده شده است؟ و با اموال دنیا چه کار دارد آن کس که به زودی همه اموال او را مرگ می‌رباید و تنها کیفر حسابرسی آن بر عهده انسان باقی خواهد ماند، بندگان خدا! خیری را که خدا وعده داد رهاکردنی نمی باشد، و شری را که از آن نهی فرمود دوست داشتنی نیست. بندگان خدا! از روزی بترسید که اعمال و رفتار انسان وارسی می‌شود، که روزی که پر از تشویش و اضطراب است و کودکان در آن روز پیر می‌گردند. ای بندگان خدا! بدانید که از شما نگاهبانانی بر شما گماشته اند، و دیدبانهایی از پیکرتان برگزیده و حافظان راستگویی که اعمال شما را حفظ می‌کنند و شماره نفسهای شما را می‌شمارند، نه تاریکی شب شما را از آنان می‌پوشاند، و نه دری محکم شما را از آن‌ها پنهان می‌سازد. یاد تنهایی قبر فردا به امروز نزدیک است، و امروز با آنچه در آن است می‌گذرد، و فردا می‌آید و بدان می‌رسد، گویی هر یک از شما در دل زمین به خانه مخصوص خود رسیده و در گودالی که کنده‌اند آرمیده اید، وه! که چه خانه تنهایی، و چه منزل وحشتناکی، و چه سیه چال غربتی، گویی هم اکنون بر صور اسرافیل دمیدند، و قیامت فرا رسیده، و برای قضاوت و ح \nسابرسی قیامت بیرون شده اید، پندارهای باطل دور گردیده، بهانه‌ها از میان برخاسته، و حقیقتها برای شما آشکارشده و شما را به آنجا که لازم بود کشانده اند، پس از عبرتها پند گیرید، و از دگرگونی روزگار عبرت پذیرید، و از هشداردهندگان بهره مند گردید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (66, 1, 158, 'پیامبر و قرآن', 'خطبه ۱۵۸-پیامبر و قرآن \nارزش پیامبر (ص) و قرآن \nخداوند پیامبر (ص) را هنگامی فرستاد که پیامبران حضور نداشتند، و امتها در خواب غفلت بودند، و رشته‌های دوستی و انسانیت از هم گسسته بود، پس پیامبر (ص) به میان خلق آمد در حالی که کتابهای پیامبران پیشین را تصدیق کرد، و با نوری هدایتگر انسانها شد که همه باید از آن اطاعت نمایند و آن، نور قرآن کریم است. از قرآن بخواهید تا سخن گوید، که هرگز سخن نمی گوید، اما من شما را از معارف آن خبر می‌دهم، بدانید که در قرآن علم آینده، و حدیث روزگاران گذشته است، شفادهنده دردهای شما، و سامان دهنده امور فردی و اجتماعی شما است. \nخبر از آینده دردناک بنی امیه پس از تسلط فرزندان امیه، خانه ای در شهر یا خیمه ای در بیابان باقی نمی ماند جز آنکه ستمگران بنی امیه، اندوه و غم را بدانجا کشانند، و بلا و کینه توزی را در همه جا مطرح نمایند، پس در آن روز برای مردم نه عذرخواهی در آسمان و نه یاوری در زمین باقی خواهد ماند، زیرا نااهلان را به زمامداری برگزیدید، و زمامداری را به جایگاه دروغینی قرار دادید. اما به زودی خداوند از ستمگران بنی امیه انتقام می‌گیرد، خوردنی را به خوردنی، و نوشیدنی را به نوشیدنی، خوردنی تلختر از گیاه (علقم) و نوشیدنی تلخ و جانگدازتر از شیره درخت (صبر) از درون ترس و وحشت، و از بیرون، شمشیر را بر آن‌ها مسلط خواهد کرد که آنان مرکبهای عصیان و نافرمانی و شتران بارکش گناهانند، من پیاپی سوگند می‌خورم که پس از من بنی امیه خلافت را چونان خلط سینه بیرون می‌اندازند، و پس از آن دیگر، تا شب و روز از پی هم در گردش است مزه حکومت را بار دیگر نخواهید چشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (67, 1, 159, 'درباره خوشرفتاری خود با مردم', 'خطبه ۱۵۹-خوشرفتاری خود با مردم \nوصف کشورداری خویش با شما به نیکویی زندگی کردم، و به قدر توان از هر سو نگهبانی شما دادم، و از بندهای بردگی و ذلت شما را نجات داده، و از حلقه‌های ستم رهایی بخشیدم، تا سپاسگزاری فراوان من برابر نیکی اندک شما باشد، و چشم پوشی از زشتیهای بسیار شما که به چشم دیدم و با بدن لمس نمودم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (68, 1, 16, 'به هنگام بیعت در مدینه', 'خطبه ۱۶-به هنگام بیعت در مدینه \nاعلام سیاستهای حکومتی\nآنچه می‌گویم به عهده می‌گیرم، و خود به آن پای بندم، کسی که عبرتها برای او آشکار شود، و از عذاب آن پند گیرد، تقوی و خویشتن داری او را از سقوط در شبهات نگه می‌دارد، آگاه باشید، تیره روزیها و آزمایشها، همانند زمان بعثت پیامبر (ص) بار دیگر به شما روی آورد. سوگند به خدایی که پیامبر (ص) را به حق مبعوث کرد، سخت آزمایش می‌شوید، چون دانه ای که در غربال ریزند، یا غذایی که در دیگ گذارند، به هم خواهید ریخت، زیر و رو خواهید شد، تا آنکه پایین به بالا، و بالا به پایین رود، آنان که سابقه ای در اسلام داشتند، و تاکنون منزوی بودند، بر سر کار می‌آیند، و آن‌ها که به ناحق، پیشی گرفتند، عقب زده خواهند شد فضائل اخلاقی امام (ع) به خدا سوگند، کلمه ای از حق را نپوشاندم، هیچگاه دروغی نگفته ام، از روز نخست، به این مقام خلافت، و چنین روزی خبر داده شدم، آگاه باشید همانا گناهان چون مرکبهای بدرفتارند، که سواران خود (گناهکاران) را عنان رهاشده در آتش دوزخ می‌اندازند، اما تقوی، چونان مرکبهای فرمانبرداری هستند که سواران خود را، عنان بر دست، وارد بهشت جاویدان می‌کنند. حق و باطل همیشه در پیکارند، و برای هر کدام \nطرفدارانی است، اگر باطل پیروز شود، جای شگفتی نیست، از دیرباز چنین بود، و اگر طرفداران حق اندکند، چه بسا روزی فراوان گردند، و پیروز شوند، اما کمتر اتفاق می‌افتد که چیز رفته باز گردد. (کلمات امام (ع) پیرامون حق و باطل، از سخنان نیکویی است که کلام کسی از سخن سرایان به آن نخواهد رسید، و بیش از آنچه که ما در شگفت شویم، شگفتی، برابر آن فرومانده است، در این کلمات امام (ع) ریزه کاریهایی از فصاحت است که نه زبان قدرت شرح آن را دارد، و نه انسانی می‌تواند از دره‌های عمیق آن بگذرد، این اعتراف مرا کسانی که در فصاحت پیشگامند و با سابقه، درک می‌کنند. \nسرگردانی مردم، و ضرورت تقوا آن کس که بهشت و دوزخ را پیش روی خود دارد، در تلاش است برخی از مردم به سرعت به سوی حق پیش می‌روند، که اهل نجاتند، و بعضی به کندی می‌روند و امیدوارند، و دیگری کوتاهی می‌کند و در آتش جهنم گرفتار است، چپ و راست گمراهی، و راه میانه، جاده مستقیم الهی است که قرآن و آثار نبوت، آن را سفارش می‌کند، و گذرگاه سنت پیامبر (ص) است، و سرانجام بازگشت همه بدان سو است. ادعاکننده باطل نابود شد، و دروغگو زیان کرد، هر کس با حق درافتاد هلاک گردید، نادانی انسان همین بس که قدر خویش نشناسد. آنچه بر اساس تقوی پایه گذاری شود، نابود نگردد، کشتزاری که با تقوی آبیاری شود، تشنگی ندارد. مردم! به خانه‌های خود روی آورید، مسائل میان خود را اصلاح کنید، توبه و بازگشت پس از زشتیها میسر است، جز پروردگار خود، دیگری را ستایش نکنید، و جز خویشتن خویش دیگری را سرزنش ننمایید.', 'و من كلام له (علیه السلام) لما بُويِعَ في المدينة و فيها يخبر الناس بعلمه بما تئول إليه أحوالهم و فيها يقسمهم إلى أقسام‏:\r\n(پس از بيعت مردم مدينه با امام عليه السّلام اين اوّلين سخنرانى آن حضرت در سال 35 هجرى است):\r\nذِمَّتِي بِمَا أَقُولُ رَهِينَةٌ وَ أَنَا بِهِ زَعِيمٌ إِنَّ مَنْ صَرَّحَتْ لَهُ الْعِبَرُ عَمَّا بَيْنَ يَدَيْهِ مِنَ الْمَثُلَاتِ حَجَزَتْهُ التَّقْوَى عَنْ تَقَحُّمِ الشُّبُهَاتِ. أَلَا وَ إِنَّ بَلِيَّتَكُمْ قَدْ عَادَتْ كَهَيْئَتِهَا يَوْمَ بَعَثَ اللَّهُ نَبِيَّهُ (صلی الله علیه وآله) وَ الَّذِي بَعَثَهُ بِالْحَقِّ لَتُبَلْبَلُنَّ بَلْبَلَةً وَ لَتُغَرْبَلُنَّ غَرْبَلَةً وَ لَتُسَاطُنَّ سَوْطَ الْقِدْرِ حَتَّى يَعُودَ أَسْفَلُكُمْ أَعْلَاكُمْ وَ أَعْلَاكُمْ أَسْفَلَكُمْ وَ لَيَسْبِقَنَّ سَابِقُونَ كَانُوا قَصَّرُوا وَ لَيُقَصِّرَنَّ سَبَّاقُونَ كَانُوا سَبَقُوا.وَ اللَّهِ مَا كَتَمْتُ وَشْمَةً وَ لَا كَذَبْتُ كِذْبَةً وَ لَقَدْ نُبِّئْتُ بِهَذَا الْمَقَامِ وَ هَذَا الْيَوْمِ. أَلَا وَ إِنَّ الْخَطَايَا خَيْلٌ شُمُسٌ حُمِلَ عَلَيْهَا أَهْلُهَا وَ خُلِعَتْ لُجُمُهَا فَتَقَحَّمَتْ بِهِمْ فِي النَّارِ، أَلَا وَ إِنَّ التَّقْوَى مَطَايَا ذُلُلٌ حُمِلَ عَلَيْهَا أَهْلُهَا وَ أُعْطُوا أَزِمَّتَهَا فَأَوْرَدَتْهُمُ الْجَنَّةَ. حَقٌّ وَ بَاطِلٌ وَ لِكُلٍّ أَهْلٌ، فَلَئِنْ أَمِرَ الْبَاطِلُ لَقَدِيماً فَعَلَ وَ لَئِنْ قَلَّ الْحَقُّ [لَرُبَّمَا] فَلَرُبَّمَا وَ لَعَلَّ وَ لَقَلَّمَا أَدْبَرَ شَيْ‏ءٌ فَأَقْبَلَ‏.و من هذه الخطبة و فيها يقسم الناس إلى ثلاثة أصناف‏:\r\nشُغِلَ مَنِ الْجَنَّةُ وَ النَّارُ أَمَامَهُ، سَاعٍ سَرِيعٌ نَجَا وَ طَالِبٌ بَطِي‏ءٌ رَجَا وَ مُقَصِّرٌ فِي النَّارِ هَوَى. الْيَمِينُ وَ الشِّمَالُ مَضَلَّةٌ وَ الطَّرِيقُ الْوُسْطَى هِيَ الْجَادَّةُ، عَلَيْهَا بَاقِي الْكِتَابِ وَ آثَارُ النُّبُوَّةِ وَ مِنْهَا مَنْفَذُ السُّنَّةِ وَ إِلَيْهَا مَصِيرُ الْعَاقِبَةِ. هَلَكَ مَنِ ادَّعَى وَ خابَ مَنِ افْتَرى‏. مَنْ أَبْدَى صَفْحَتَهُ لِلْحَقِّ هَلَكَ [عِنْدَ جَهَلَةِ النَّاسِ‏] وَ كَفَى بِالْمَرْءِ جَهْلًا أَلَّا يَعْرِفَ قَدْرَهُ. لَا يَهْلِكُ عَلَى التَّقْوَى سِنْخُ أَصْلٍ وَ لَا يَظْمَأُ عَلَيْهَا زَرْعُ قَوْمٍ. فَاسْتَتِرُوا فِي بُيُوتِكُمْ وَ أَصْلِحُوا ذاتَ بَيْنِكُمْ وَ التَّوْبَةُ مِنْ وَرَائِكُمْ . وَ لَا يَحْمَدْ حَامِدٌ إِلَّا رَبَّهُ وَ لَا يَلُمْ لَائِمٌ إِلَّا نَفْسَهُ‏.', NULL, '1. اعلام سياست هاى حكومتى:\r\nآن چه مى گويم به عهده مى گيرم، و خود به آن پاى بندم. كسى كه عبرت ها براى او آشكار شود، و از عذاب آن پند گيرد، تقوا و خويشتن دارى او را از سقوط در شبهات نگه مى دارد. \r\nآگاه باشيد، تيره روزى ها و آزمايش ها، همانند زمان بعثت پيامبر صلّى اللّه عليه و آله و سلّم بار ديگر به شما روى آورد.\r\n(پس از بيعت مردم مدينه با امام عليه السّلام اين اوّلين سخنرانى آن حضرت در سال 35 هجرى است):\r\nسوگند به خدايى كه پيامبر صلّى اللّه عليه و آله و سلّم را به حق مبعوث كرد، سخت آزمايش مى شويد، چون دانه اى كه در غربال ريزند، يا غذايى كه در ديگ گذارند به هم خواهيد ريخت، زير و رو خواهيد شد، تا آن كه پايين به بالا، و بالا به پايين رود، آنان كه سابقه اى در اسلام داشتند، و تاكنون منزوى بودند، بر سر كار مى آيند، و آنها كه به ناحق، پيشى گرفتند، عقب زده خواهند شد. 2. فضائل اخلاقى امام عليه السّلام:\r\nبه خدا سوگند، كلمه اى از حق را نپوشاندم، هيچ گاه دروغى نگفته ام، از روز نخست، به اين مقام خلافت و چنين روزى خبر داده شدم.\r\nآگاه باشيد همانا گناهان چون مركب هاى بد رفتارند كه سواران خود (گناهكاران) را عنان رها شده در آتش دوزخ مى اندازند. اما تقوا، چونان مركب هاى فرمانبردارى هستند كه سواران خود را، عنان بر دست، وارد بهشت جاويدان مى كنند.\r\nحقّ و باطل هميشه در پيكارند، و براى هر كدام طرفدارانى است، اگر باطل پيروز شود، جاى شگفتى نيست، از دير باز چنين بوده، و اگر طرفداران حق اندكند، چه بسا روزى فراوان گردند و پيروز شوند، امّا كمتر اتّفاق مى افتد كه چيز رفته باز گردد.\r\n(سید رضی مى گويد: كلمات امام عليه السّلام پيرامون حق و باطل، از سخنان نيكويى است كه كلام كسى از سخن سرايان به آن نخواهد رسيد، و بيش از آن چه كه ما در شگفت شويم، شگفتى، برابر آن فرو مانده است، در اين كلمات امام عليه السّلام ريزه كاريهايى از فصاحت است كه نه زبان قدرت شرح آن را دارد، و نه انسانى مى تواند از درّه هاى عميق آن بگذرد، اين اعتراف مرا كسانى كه در فصاحت پيشگامند و با سابقه، درك مى كنند.)3. سرگردانى مردم، و ضرورت تقوا:\r\nآن كس كه بهشت و دوزخ را پيش روى خود دارد، در تلاش است. برخى از مردم به سرعت به سوى حق پيش مى روند، كه اهل نجاتند، و بعضى به كندى مى روند و اميدوارند، و ديگرى كوتاهى مى كند و در آتش جهنّم گرفتار است. \r\nچپ و راست گمراهى، و راه ميانه، جادّه مستقيم الهى است كه قرآن و آثار نبوّت، آن را سفارش مى كند، و گذرگاه سنّت پيامبر صلّى اللّه عليه و آله و سلّم است و سرانجام، بازگشت همه بدان سو مى باشد. \r\nادّعا كننده باطل نابود شد، و دروغگو زيان كرد، هر كس با حق در افتاد هلاك گرديد. نادانى انسان همين بس كه قدر خويش نشناسد.\r\nآن چه بر أساس تقوا پايه گذارى شود، نابود نگردد. كشتزارى كه با تقوا آبيارى شود، تشنگى ندارد. \r\nمردم به خانه هاى خود روى آوريد، مسائل ميان خود را اصلاح كنيد، توبه و بازگشت پس از زشتى ها ميسّر است. جز پروردگار خود، ديگرى را ستايش نكنيد و جز خويشتن خويش ديگرى را سرزنش ننماييد.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (69, 1, 160, 'در بیان عظمت پرورگار', 'خطبه ۱۶۰-در بیان عظمت پروردگار \nخداشناسی \nفرمان خدا قضای حتمی و حکمت، و خشنودی او مایه امنیت و رحمت است، از روی علم، حکم می‌کند و با حلم و بردباری می‌بخشاید. خدایا! سپاس تو راست بر آنچه می‌گیری، و عطا می‌فرمایی، و شفا می‌دهی یا مبتلا می‌سازی، سپاس که تو را رضایت بخش ترین، محبوبترین، و ممتازترین باشد، سپاسی که آفریدگانت را سرشار سازد، و تا آنجا که تو بخواهی تداوم یابد، سپاسی که از تو پوشیده نباشد، و از رسیدن به پیشگاهت باز نماند، سپاسی که شمارش آن پایان نپذیرد، و تداوم آن از بین نرود. خدایا! حقیقت بزرگی تو را نمی دانیم، جز آنکه می‌دانیم تو زنده ای و احتیاج به غیر نداری، خواب سنگین یا سبک تو را نمی رباید، هیچ اندیشه ای به تو نرسد و هیچ دیده ای تو را ننگرد، اما دیده‌ها را تو می‌نگری، و اعمال انسانها را شماره فرمایی، و قدمها و موی پیشانیها (زمام امور همه) به دست تو است، خدایا آنچه را که از آفرینش تو می‌نگریم، و از قدرت تو به شگفت می‌آییم و بدان بزرگی قدرت تو را می‌ستاییم، بسی ناچیزتر است در برابر آنچه که از ما پنهان، و چشمهای ما از دیدن آن‌ها ناتوان، و عقلهای ما از درک آن‌ها عاجز است، و پرده‌های غیب میان ما و آن‌ها گسترده می‌باشد. را \nه‌های خداشناسی پس آن کس که دل را از همه چیز تهی سازد، و فکرش را بکار گیرد، تا بداند که: چگونه عرش قدرت خود را برقرار ساخته ای؟ و پدیده را چگونه آفریده ای؟ و چگونه آسمانها و کرات فضایی را در هوا آویخته ای؟ و زمین را چگونه بر روی امواج آب گسترده ای؟ نگاهش حسرت زده، و عقلش مات و سرگردان، و شنواییش آشفته، و اندیشه اش حیران می‌ماند. \nوصف امیدواری به خدا به گمان خود ادعا دارد که به خدا امیدوار است، به خدای بزرگ سوگند که دروغ می‌گوید، چه می‌شود او را که امیدواری در کردارش پیدا نیست؟ پس هرکس به خدا امیدوار باشد باید، امید او در کردارش آشکار شود، هر امیدواری جز امید به خدای تعالی ناخالص است، و هر ترسی جز ترس از خدا نادرست است. گروهی در کارهای بزرگ به خدا امید بسته و در کارهای کوچک به بندگان خدا روی می‌آورند، پس حق بنده را ادا می‌کند و حق خدا را بر زمین می‌گذارد، چرا در حق خدای متعال کوتاهی می‌شود؟ و کمتر از حق بندگان رعایت می‌گردد؟ آیا می‌ترسی در امیدی که به خدا داری دروغگو باشی؟ یا او را درخور امید بستن نمی پنداری؟ امیدوار دروغین اگر از بنده خدا ترسناک باشد، حق او را چنان رعایت کند که حق پروردگار خود را آنگونه رعایت نمی کند، پس ترس خود را از بندگان آماده، و ترس از خداوند را وعده ای انجام نشدنی می‌شمارد، و اینگونه است کسی که دنیا در دیده اش بزرگ جلوه کند، و ارزش و اعتبار دنیا در دلش فراوان گردد، که دنیا را بر خدا مقدم شمارد، و جز دنیا به چیز دیگری نپردازد و بنده دنیا گردد. \nبرای تو کافی است که راه و رسم زندگی پیامبر اسلام (ص) را اطاعت نمایی، تا راهنمای خوبی برای تو در شناخت بدیها و عیبهای دنیا و رسواییها و زشتی آن باشد، چه اینکه دنیا از هر سو بر پیامبر (ص) باز داشته و برای غیر او گسترانده شد، از پستان دنیا شیر نخورد، و از زیورهای آن فاصله گرفت. اگر می‌خواهی دومی را، موسی (ع) و زندگی او را تعریف کنم، آنجا که می‌گوید (پروردگار، هر چه به من از نیکی عطا کنی نیازمندم) به خدا سوگند! موسی (ع) جز قرص نانی که گرسنگی را برطرف سازد چیز دیگری نخواست، زیرا موسی (ع) از سبزیجات زمین می‌خورد، تا آنجا که بر اثر لاغری و آب شدن گوشت بدن، سبزی گیاه از پشت پرده شکم او آشکاربود. و اگر می‌خواهی سومی را، حضرت داوود (ع) صاحب نی‌های نوازنده، و خواننده بهشتیان را الگوی خویش سازی، که با هنر دستان خود از لیف خرما زنبیل می‌بافت، و از همنشینان خود می‌پرسید چه کسی از شما این زنبیل را می‌فروشد؟ و با بهای آن به خوردن نان جوی قناعت می‌کرد. و اگر خواهی از عیسی بن مریم (ع) بگویم، که سنگ را بالش خود قرار می‌داد، لباس پشمی خشن به تن می‌کرد، و نان خشک می‌خورد، نان خورش او گرسنگی، و چراغش در شب ماه، و پناه \nگاه زمستان او شرق و غرب زمین بود، میوه و گل او سبزیجاتی بود که زمین برای چهارپایان می‌رویاند، زنی نداشت که او را فریفته خود سازد، فرزندی نداشت تا او را غمگین سازد، مالی نداشت تا او را سرگرم کند، و آز و طمعی نداشت تا او را خوار و ذلیل نماید، مرکب سواری او دو پایش، و خدمتگزار وی، دستهایش بود. \nراه و رسم زندگی پیامبر اسلام (ص) پس به پیامبر پاکیزه و پاکت اقتدا کن، که راه و رسم او الگویی است برای الگوطلبان، و مایه فخر و بزرگی است برای کسی که خواهان بزرگواری باشد، و محبوبترین بنده نزد خدا کسی است که از پیامبرش پیروی کند، و گام بر جایگاه قدم او نهد، پیامبر (ص) از دنیا چندان نخورد که دهان را پر کند، و به دنیا با گوشه چشم نگریست، دو پهلویش از تمام مردم فرو رفته تر، و شکمش از همه خالیتر بود، دنیا را به او نشان دادند اما نپذیرفت، و چون دانست خدا چیزی را دشمن می‌دارد آن را دشمن داشت، و چیزی را که خدا خوار شمرده، آن را خوار انگاشت، و چیزی را که خدا کوچک شمرده کوچک و ناچیز می‌دانست. اگر در ما نباشد جز آنکه آنچه خدا و پیامبرش دشمن می‌دارند، دوست بداریم، یا آنچه را خدا و پیامبرش کوچک شمارند، بزرگ بداریم، برای نشان دادن دشمنی ما با خدا، و سرپیچی از فرمانهای او کافی بود. و همانا پیامبر (که درود خدا بر او باد) بر روی زمین می‌نشست و غذا می‌خورد، و چون برده ساده می‌نشست، و با دست خود کفش خود را وصله می‌زد، و جامه خود را با دست خود می‌دوخت، و بر الاغ برهنه می‌نشست، و دیگری را پشت سر خویش سوار می‌کرد، پرده \nای بر در خانه او آویخته بود که نقش و تصویرها در آن بود، به یکی از همسرانش فرمود، این پرده را از برابر چشمان من دور کن که هرگاه نگاهم به آن می‌افتد به یاد دنیا و زینتهای آن می‌افتم، پیامبر (ص) با دل از دنیا روی گرداند، و یادش را از جان خود ریشه کن نمود، و همواره دوست می‌داشت تا جاذبه‌های دنیا از دیدگانش پنهان ماند، و از آن لباس زیبایی تهیه نکند و آن را قرارگاه دائمی خود نداند، و امید ماندن در دنیا نداشته باشد، پس یاد دنیا را از جان خویش بیرون کرد، و دل از دنیا برکند، و چشم از دنیا پوشاند، و چنین است کسی که چیزی را دشمن دارد، خوش ندارد به آن بنگرد، یا نام آن نزد او بر زبان آورده شود. \nدر زندگانی رسول خدا (ص) برای تو نشانه‌هایی است که تو را به زشتیها و عیبهای دنیا راهنمایی کند، زیرا پیامبر (ص) با نزدیکان خود گرسنه به سر می‌برد، و با آنکه مقام و منزلت بزرگی داشت، زینتهای دنیا از دیده او دور ماند، پس تفکر کننده ای باید با عقل خویش به درستی اندیشه کند که: آیا خدا محمد (ص) را به داشتن این صفتها اکرام فرمود یا او را خوار نمود؟ اگر بگوید: خوار نمود، دروغ گفته و بهتانی بزرگ زده است، و اگر بگوید: او را اکرام نمود. پس بداند. خدا کسی را خوار شمرد که دنیا را برای او گستراند و از نزدیکترین مردم به خودش دور نگهداشت. پس پیروی کننده باید از پیامبر (ص) پیروی کند، و به دنبال او راه رود، و قدم بر جای قدم او بگذارد، وگرنه از هلاکت ایمن نمی باشد، که همانا خداوند، محمد (ص) را نشانه قیامت، و مژده دهنده بهشت، و ترساننده از کیفر جهنم قرار داد، او با شکمی گرسنه از دنیا رفت و با سلامت جسم و جان وارد آخرت شد، سنگی بر سنگی نگذاشت تا جهان را ترک گفت، و دعوت پروردگارش را پذیرفت وه! چه بزرگ است منتی که خدا با بعثت پیامبر (ص) بر ما نهاده، و چنین نعمت بزرگی به ما عطا فرمود، رهبر پیشتازی که باید او را پیروی کنی \nم و پیشوایی که باید راه او را تداوم بخشیم. بخدا سوگند! آنقدر این پیراهن پشمین را وصله زدم که از پینه کننده آن شرمسارم، یکی به من گفت: آیا آن را دور نمی افکنی؟ گفتم: از من دور شو، صبحگاهان رهروان شب ستایش می‌شوند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (70, 1, 161, 'در بیان صفات پیامبر و خاندان او', 'خطبه ۱۶۱-در بیان صفات پیامبر \nوصف پیامبر (ص) و اهل بیت (ع) \nخداوند پیامبرش را با نوری درخشان، و برهانی آشکار، و راهی روشن، و کتابی هدایتگر برانگیخت. خانواده او نیکوترین خانواده، و درخت وجودش از بهترین درختان است، که شاخه‌های آن راست و میوه‌های آن سر به زیر و در دسترس همگان است، زادگاه او مکه، و هجرت او به مدینه پاک و پاکیزه است، که در آنجا نام او بلند و دعوتش به همه جا رسید، خدا او را با برهانی کامل و کافی (قرآن) و پندهای شفابخش، و دعوتی جبران کننده فرستاد، با فرستادن پیامبر (ص) شریعتهای ناشناخته را شناساند، و ریشه بدعتهای راه یافته در ادیان آسمانی را قطع کرد، و احکام و مقررات الهی را بیان فرمود. پس هر کس جز اسلام دینی را انتخاب کند به یقین شقاوت او ثابت، و پیوند او با خدا قطع، و سقوطش سهمگین خواهد بود، و سرانجامش رنج و اندوهی بی پایان و شکنجه ای پردرد می‌باشد. بر خدا توکل می‌کنم، توکلی که بازگشت به سوی او باشد، و از او راه می‌جویم، راهی که به بهشت رسد و به آنجا که خواست اوست بیانجامد. \nسفارش به تقوا و عبرت از گذشتگان بندگان خدا! شما را به ترسیدن از خدا و فرمانبرداری او سفارش می‌کنم که نجات فردا و مایه رهایی جاویدان است، خدا آنگونه که سزاوار بود شما را ترسانید، و چنانکه شایسته بود امیدوارتان کرد، و دنیا و بی اعتباری آن و نابودشدنی بودن، و دگرگونی آن را برای شما تعریف کرد. پس از آنچه در دنیا شما را به شگفتی وامی دارد روی گردانید، زیرا که مدت کوتاهی در آن اقامت دارید، دنیا به خشم خدا نزدیکترین و از خشنودی خدا دورترین جایگاه است. پس ای بندگان خدا، از غم و اندوه و سرگرمیهای دنیا چشم برگیرید زیرا شما به جدایی و دگرگونیهای امور دنیایی یقین دارید، و چونان دوست مهربانی که نصیحتگر و کوشا برای نجات رفیقش تلاش کند، خویشتن را از دنیا دور نگهدارید، و از آنچه بر گذشتگان شما رفت عبرت گیرید، که چگونه، بند بند اعضای بدنشان از هم گسست، چشم و گوششان نابود شد، شرف و شکوهشان از خاطره‌ها محو گردید، و همه ناز و نعمتها و رفاه‌ها و خوشیها پایان گرفت، که نزدیکی فرزندان به دوری و از دست دادنشان، و همدمی همسران به جدایی تبدیل شد، دیگر نه به هم می‌نازند، و نه فرزندانی می‌آورند، و نه یکدیگر را دیدار می‌کنند، \nو نه در کنار هم زندگی می‌کنند. پس ای بندگان خدا! بپرهیزید، پرهیز کسی که به نفس خود چیره، و بر شهوت خود پیروز، و با عقل خود به درستی می‌نگرد، زیرا که جریان انسان آشکار، پرچم برافراشته، جاده هموار، و راه، روشن و راست است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (71, 1, 162, 'چرا خلافت را از او گرفتند؟', 'خطبه ۱۶۲-چرا خلافت را از او گرفتند؟ \n(در سال ۳۷ هجری در صفین، شخصی از طایفه بنی اسد پرسید: چگونه شما را از مقام امامت که سزاوارتر از همه بودید کنار زدند فرمود:) \nعلل و عوامل غصب امامت \nای برادر بنی اسدی! تو مردی پریشان و مضطربی که نابجا پرسش می‌کنی، لیکن تو را حق خویشاوندی است، و حقی که در پرسیدن داری و بی گمان طالب دانستی، پس بدان که: ظلم و خودکامگی که نسبت به خلافت بر ما تحمیل شد، در حالی که ما را نسب برتر و پیوند خویشاوندی با رسول خدا (ص) استوارتر بود، جز خودخواهی و انحصارطلبی چیز دیگری نبود که: گروهی بخیلانه به کرسی خلافت چسبیدند، و گروهی سخاوتمندانه از آن دست کشیدند، داور خداست، و بازگشت همه ما به روز قیامت است. (در اینجا شعر امرءالقیس را خواند که:) (واگذار داستان تاراج آن غارتگران را، و به یاد آور داستان شگفت دزدیدن شترهای سواری را) شکوه از ستمهای معاویه بیا و داستان پسران ابوسفیان را به یادآور، که روزگار مرا به خنده آورد از آن پس که مرا گریاند، سوگند بخدا! که جای شگفتی نیست، کار از بس عجیب است که شگفتی را می‌زداید، و کجی و انحراف می‌افزاید، مردم کوشیدند نور خدا را در داخل چراغ آن خاموش سازند، جوشش زلال حقیقت را از سرچشمه آن ببندند، چرا که میان من و خود، آب را وباآلود کردند، اگر محنت آزمایش از ما و این مردم برداشته شود، آنان را به راهی می‌برم که س \nراسر حق است و اگر به گونه دیگری انجامید (با حسرت خوردن بر آن‌ها جان خویش را مگذار، که خداوند بر آنچه می‌کنند آگاه است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (72, 1, 163, 'در توحید الهی', 'خطبه ۱۶۳-در توحید الهی \nخداشناسی \nسپاس خداوندی را سزاست که آفریننده بندگان، و گستراننده زمین، و جاری کننده آب در زمینهای پست، و رویاننده گیاه در کوهها و تپه‌های بلند می‌باشد، نه اول او را آغازی و نه ازلی بودن او را پایانی است، آغاز هر چیزی و جاویدان است، و پایدار و ماندگار بدون مدت و زمان است، پیشانی بندگان برابر عظمت او به خاک افتاده، و لبها در اعتراف به یگانگی او در حرکتند. به هنگام آفرینش، برای هر پدیده ای حد و مرزی قرار داد، تا برای وجود بی نهایت او همانندی نباشد، گمانها خدا را به اندازه‌ها و حرکتها و اندامها و آلتها نمی توانند اندازه گیری نمایند. نمی توان گفت: خدا از کی بود؟ و تا کی خواهد بود؟ وجود آشکاری است که نمی توان پرسید: از چیست؟ و حقیقت پنهانی است که نمی توان پرسید؟ در کجاست؟ نه جسم است که او را نهایتی باشد، و نه پوشیده ای که چیزی او را در بر گرفته باشد، به موجودات آنقدر نزدیک نیست که به آن‌ها چسبیده، و آنقدر دور نیست که جدا و بریده باشد. بر خداوند، خیره نگریستن بندگان، و بازگشتن لفظی به زبان، نزدیک شدن به تپه ای، گام برداشتن در تاریکی شب، راه رفتن در مهتاب که نور می‌افشاند، و درخشش خورشیدی که پس ماه طلوع می‌\nکند، و با طلوع و غروبش، و آمدن شب و روز، چرخ زمان می‌گردد و تاریخ ورق می‌خورد، همه و همه پنهان نیست خدا پیش از هر نهایت و مدت، و فراتر از هرگونه حساب و شمارش است، خدا والاتر از آنچه می‌باشد که عقلهای عاجز تشبیه کنندگان تصور می‌کند، از صفات پدیده‌ها و اندازه‌ها و قطرها که برای موجودات مادی پندارند، و جایگاه‌هایی که برای آن در نظر می‌گیرند، زیرا حد و مرز و اندازه، شایسته پدیده‌هاست و به غیر خدا تعلق دارد. وصف آفرینش پدیده‌ها را از موادی ازلی و ابدی نیافرید، بلکه آن‌ها را از نیستی به هستی آورد، و برای هر پدیده ای حد و مرزی تعیین فرمود، و آن‌ها را به نیکوترین صورت زیبا، صورتگری نمود، چیزی از فرمان او سرپیچی نمی کند، و خدا از اطاعت چیزی سود نمی برد، علم او به مردگانی که رفتند. چونان آگاهی او به زندگانی است که هستند، و علم او به آسمانهای بالا چونان علم او به زمینهای زیرین است. \nشگفتی آفرینش انسان ای انسان! ای آفریده راست قامت، ای پدیده نگاهداری شده در تاریکیهای رحمهای مادران، قرار داده شده در پرده‌های تو در تو، آغاز آفرینش تو از گل و لای بود، و سپس در جایگاه آرامی نهاده شدی تا زمانی مشخص و سرآمدی تعیین شده و آنگاه که در شکم مادرت حرکت می‌کردی، نه دعوتی را می‌توانستی پاسخ گویی، و نه صدایی را می‌شنیدی. سپس از قرارگاهت بیرون کردند و به خانه ای آوردند که آن را ندیده بودی و راه‌های سودش را نمی دانستی، پس چه کسی تو را در مکیدن شیر، از پستان مادر هدایت کرد؟ و به هنگام نیاز جایگاه‌های طلب کردن و خواستن را به تو شناساند؟ هرگز، آن کس که در توصیف پدیده ای با شکل و اندازه و ابزار مشخص درمانده باشد، بدون تردید از وصف پروردگارش ناتوانتر، و از شناخت خدا با حد و مرز پدیده‌ها دورتر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (73, 1, 164, 'اندرز او به عثمان', 'خطبه ۱۶۴-اندرز او به عثمان \nهشدار دادن به عثمان \nهمانا مردم پشت سر من هستند، مرا میان تو و خودشان میانجی قرار داده اند، به خدا نمی دانم با تو چه بگویم؟ چیزی را نمی دانم که تو ندانی، تو را به چیزی راهنمایی نمی کنم که نشناسی، تو می‌دانی آنچه ما می‌دانیم، ما به چیزی پیشی نگرفته ایم که تو را آگاه سازیم، و چیزی را در پنهانی نیافته ایم که آن را به تو ابلاغ کنیم. دیدی چنانکه ما دیدیم، شنیدی چنانکه ما شنیدیم، با رسول خدا (ص) بودی چنانکه ما بودیم، پس ابوقحافه (ابابکر) و پسر خطاب، در عمل به حق، از تو بهتر نبودند، تو به رسول خدا (ص) در خویشاوندی از آن دو نزدیکتری، و داماد او شدی که آنان نشدند. پس خدا را خدا را پروا کن، سوگند به خدا! تو کور نیستی تا بینایت کنند، نادان نیستی تا تو را تعلیم دهند، راهها روشن است، و نشانه‌های دین برپاست، پس بدان که برترین بندگان خدا در پیشگاه او رهبر عادل است که خود هدایت شده و دیگران را هدایت می‌کند، سنت شناخته شده را برپا دارد، و بدعت ناشناخته را بمیراند، سنتها روشن و نشانه‌هایش آشکار است، بدعتها آشکار و نشانه‌های دین برپاست، پس بدان که برترین بندگان خدا در پیشگاه او رهبر عادل است که خود هدایت شده و دیگرا \nن را هدایت می‌کند، سنت شناخته شده را برپا دارد، و بدعتها آشکار و نشانه‌های آن برپاست. و بدترین مردم نزد خدا رهبر ستمگری که خود گمراه و مایه گمراهی دیگران است، که سنت پذیرفته را بمیراند، و بدعت ترک شده را زنده گرداند. من از پیامبر خدا (ص) شنیدم که گفت: (قیامت رهبر ستمگر را بیاورند که نه یاوری دارد و نه کسی از او پوزش خواهی می‌کند، پس او را در آتش جهنم افکنند، و در آن چنان می‌چرخد که سنگ آسیاب، تا به قعر دوزخ رسیده به زنجیر کشیده شود) من تو را به خدا سوگند می‌دهم که امام کشته شده این امت مباشی، چرا که پیش از این گفته می‌شد. (در میان این امت، امامی به قتل خواهد رسید که در کشتار تا روز قیامت گشوده خواهد شد و کارهای امت اسلامی با آن مشتبه شود، و فتنه و فساد در میانشان گسترش یابد، تا آنجا که حق را از باطل نمی شناسند، و به سختی در آن فتنه‌ها غوطه ور می‌گردند.) برای مروان چونان حیوان به غارت گرفته مباش که تو را هر جا خواست براند، آن هم پس از سالیانی که از عمر تو گذشته و تجربه ای که به دست آوردی. (عثمان گفت: با مردم صحبت کن که مرا مهلت دهند تا از عهده ستمی که به آنان رفته برآیم) امام (ع) فرمود: آنچه در مدینه است به مه \nلت نیاز ندارد، و آنچه مربوط به بیرون مدینه باشد تا رسیدن فرمانت مهلت دارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (74, 1, 165, 'آفرینش طاووس', 'خطبه ۱۶۵-آفرینش طاووس \nشگفتی آفرینش انواع پرندگان \nخداوند پدیده‌های شگفتی از جانداران حرکت کننده، و بی جان، برخی ساکن و آرام، بعضی حرکت کننده و بی قرار آفریده است، و شواهد و نمونه‌هایی از لطافت صنعتگری و قدرت عظیم خویش بپا داشت چندان که تمام اندیشه‌ها را به اعتراف واداشته، و سر به فرمان او نهادند، و در گوشهای ما بانگ براهین یکتایی او پیچیده است، آنگونه که پرندگان گوناگون بیافرید، و آنان را در شکافهای زمین، و رخنه دره‌ها و فراز کوهها مسکن داد، با بالهای متفاوت و شکل و هیاتهای گوناگون، که زمام آن‌ها به دست اوست، پرندگانی که با بالهای خود در لا به لای جو گسترده و فضای پهناور پرواز می‌کنند، آن‌ها را از دیار نیستی در شکل و ظاهری شگفت آور بیافرید، و استخوانهاشان را از درون در مفصلهای پوشیده از گوشت به هم پیوند داد. برخی از پرندگان را که جثه سنگین داشتند از بالا رفتن و پروازهای بلند و دور باز داشت، آنگونه که آرام و سنگین در نزدیکی زمین بال می‌زنند، پرندگان را با لطافت قدرتش و دقت صنعتش، در رنگهای گوناگون با زیبایی خاصی رنگ آمیزی کرد، گروهی از آن‌ها را تنها با یک رنگ بیاراست که رنگ دیگری در آن راه ندارد، دسته ای دیگر را در رنگ م \nخالف آن فرو برد، جز اطراف گردنشان که چونان طوقی آویخته، مخالف رنگ اندامشان است. \nشگفتیهای آفرینش طاووس و از شگفت انگیزترین پرندگان در آفرینش طاووس است، که آن را در استوارترین شکل موزون بیافرید، و رنگهای پر و بالش را به نیکوترین رنگها بیاراست با بالهای زیبا که پرهای آن به روی یکدیگر انباشته و دم کشیده اش که چون به سوی ماده پیش می‌رود آن را چونان چتری گشوده و بر سر خود سایبان می‌سازد، گویا بادبان کشتی است که ناخدا آن را برافراشته است. طاووس به رنگهای زیبای خود می‌نازد، و خوشحال و خرامان دم زیبایش را به این سو و آن سو می‌چرخاند، و سوی ماده می‌تازد، چون خروس می‌پرد، و چون حیوان نر مست شهوت با جفت خویش می‌آمیزد، این حقیقت را از روی مشاهده می‌گویم، نه چون کسی که بر اساس نقل ضعیفی سخن بگوید، اگر کسی خیال کند. (باردار شدن طاووس به وسیله قطرات اشکی است که در اطراف چشم نر حلقه زده و طاووس ماده آن را می‌نوشد آنگاه بدون آمیزش با همین اشکها تخمگذاری می‌کند) افسانه بی اساس است ولی شگفت تر از آن نیست که می‌گویند: (زاغ نر طعمه به منقار ماده می‌گذارد که همین عامل باردار شدن زاغ است.) گویا! نی‌های پر طاووس چونان شاخه‌هایی است که از نقره ساخته، و گردیهای شگفت انگیز آفتاب گونه که به پرهای اوست ا \nز زر ناب و پاره‌های زبرجد بافته شده است، اگر رنگهای پرهای طاووس را به روییدنیهای زمین تشبیه کنی، خواهی گفت: دسته گلی است که از شکوفه‌های رنگارنگ گلهای بهاری فراهم آمده است و اگر آن را با پارچه‌های پوشیدنی همانند سازی پس چون پارچه‌های زیبای پرنقش و نگار یا پرده‌های رنگارنگ یمن است، و اگر آن را با زیورآلات مقایسه کنی چون نگینهای رنگارنگی است که در نواری از نقره با جواهرات زینت شده است. روانشناسی حیوانی طاووس طاووس، چون به خود بالنده مغرور راه می‌رود، دم و بالهای زیبایش را برانداز می‌کند، پس با توجه به زیبایی جامه و رنگهای گوناگون پر و بالش قهقهه سرمی دهد، اما چون نگاهش به پاهای او می‌افتد، بانگی برآورد که گویا گریان است، فریاد می‌زند گویا که دادخواه است، و گواه صادق دردی است که در درون دارد، زیرا پاهای طاووس چونان ساق خروس دو رگه (هندی و پارسی) باریک و زشت و در یک سو ساق پایش ناخنکی مخفی روییده است. شگفتی رنگ آمیزی پرهای طاووس برفراز گردن طاووس به جای یال، کاکلی سبزرنگ و پر نقش و نگار روییده، و برآمدگی گردنش چونان آفتابه ای نفیس و نگارین است، و از گلوگاه تا روی شکمش به زیبایی وسمه یمانی (برگ گیاهی با رنگ سب \nز بسیار سیر) رنگ آمیزی شده، یا چون پارچه حریر براق یا آیینه ای شفاف که پرده بر روی آن افکندند، بر اطراف گردنش گویا چادری سیاه افکنده که چون رنگ آن شاداب و بسیار می‌باشد، پنداری با رنگ سبز تندی درهم آمیخته است که در کنار شکاف گوش جلوه خاصی دارد، کمتر رنگی می‌توان یافت که طاووس از آن در اندامش نداشته باشد، یا با شفافیت و صیقل فراوان و زرق و برق جامه اش آن را جلای برتری نداده باشد. طاووس چونان شکوفه‌های پراکنده ای است که باران بهار و گرمای آفتاب را در پرورش آن نقش چندانی نیست، و شگفت آور آنکه هر چند گاهی از پوشش پرهای زیبا بیرون می‌آید، و تن عریان می‌کند، پرهای او پیاپی فرو می‌ریزند و از نو می‌رویند، پرهای طاووس چونان برگ خزان رسیده می‌ریزند و دوباره رشد می‌کنند و به هم می‌پیوندند، تا دیگر بار شکل و رنگ زیبای گذشته خود را باز می‌یابد، بی آنکه میان پرهای نو و ریخته شده تفاوتی وجود داشته باشید یا رنگی جابجا بروید. اگر در تماشای یکی از پرهای طاووس دقت کنی، لحظه ای به سرخی گل و لحظه ای دیگر به سبزی زبرجد و گاه به زردی زرناب جلوه می‌کند عجز انسان از درک حقائق موجود در پدیده‌ها راستی، هوشهای ژرف اندیش و عقلهای پرتل \nاش چگونه این همه از حقائق موجود در پدیده‌ها را می‌توانند درک کنند؟ و چگونه گفتار توصیف گران، به نظم کشیدن این همه زیبایی را بیان توانند نمود؟ و در درک کمترین اندام طاووس، گمانها از شناخت درمانده و زبانها از ستودن آن در کام مانده اند. پس ستایش خداوندی را سزاست که عقلها را از توصیف پدیده ای که برابر دیدگان جلوه گرند ناتوان ساخت، پدیده محدودی که او را با ترکیب پیکری پرنقش و نگار، با رنگها و مرزهای مشخص می‌شناسد، باز هم از تعریف فشرده اش زبانها عاجز و از توصیف واقعی آن درمانده اند. (پس چگونه خدا را می‌توانند درک کنند؟) شگفتی آفرینش جانداران کوچک پاک و برتر است خدایی که در اندام مورچه، و مگس ریز، پاها پدید آورد، و جانداران بزرگتر از آن‌ها را نیز آفرید، از ماهیان دریا، و پیلان عظیم الجثه و بر خود لازم شمرد، که هیچ کالبد جانداری را وانگذارد و به درستی اداره اش نماید، جز آنکه میعادگاهش مرگ و پایان راهش نیستی باشد. \nوصف ویژگیهای بهشت اگر با چشم دل به آنچه از بهشت برای تو ستودند بنگری، از آنچه در دنیاست دل می‌کنی، هر چند شگفتی آور و زیبا باشد، و از خواهشهای نفسانی و خوشیهای زندگانی و منظره‌های آراسته و زیبای آن کناره می‌گیری، و اگر فکرت را به درختان بهشتی مشغول داری که شاخه‌هایشان همواره به هم می‌خورند، و ریشه‌های آن در توده‌های مشک پنهان، و در ساحل جویباران بهشت قرار گرفته آبیاری می‌گردند، و خوشه‌هایی از لولو آبدار به شاخه‌های کوچک و بزرگ درختان آویخته، و میوه‌های گوناگونی که از درون غلافها و پوششها سر بیرون کرده، سرگردان و حیرت زده می‌گردی. شاخه‌های پرمیوه بهشت که بدون زحمتی خم شده در دسترس قرار گیرند، و چیننده آن هرگاه که خواهد برچیند، مهمانداران بهشت، گرد ساکنان آن و پیرامون کاخهایشان در گردشند و آنان را با عسلهای پاکیزه و شرابهای گوارا پذیرایی کنند، آن‌ها که همواره از کرامت الهی بهره مندند تا آنگاه که در سرای ثابت خویش فرود آیند و از نقل و انتقال سفرها آسوده گردند. ای شنونده اگر دل خود را به منظره‌های زیبایی که در بهشت به آن می‌رسی مشغول داری، روح تو با اشتیاق فراوان به آن سامان پرواز خواهد کرد، و از این \nمجلس من با شتاب به همسایگی اهل قبور خواهی شتافت. خداوند با لطف خود من و شما را از کسانی قرار دهد که با دل و جان برای رسیدن به جایگان نیکان تلاش می‌کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (75, 1, 166, 'در تحریض به الفت با یکدیگر', 'خطبه ۱۶۶-تحریض به الفت با یکدیگر \nاحترام متقابل اجتماعی \nباید خردسالان شما از بزرگان شما پیروی کنند، و بزرگسالان شما نسبت به خردسالان مهربان باشند، و چونان ستم پیشگان جاهلیت نباشید که نه از دین آگاهی داشتند و نه در خدا اندیشه می‌کردند، همانند تخم افعی در لانه پرندگان نباشید که شکستن آن گناه و نگهداشتن آن شر و زیانبار است. (به جای جوجه، ماری از آن بیرون می‌آید) \nآینده بنی امیه مسلمانان، پس از وحدت و برادری به جدایی و تفرقه رسیدند، و از ریشه و اصل خویش پراکنده شدند، تنها گروهی شاخه درخت توحید را گرفتند، و به هر طرف که روی آورد. همسو شدند، اما خداوند مسلمانان را به زودی برای بدترین روزی که بنی امیه در پیش دارند جمع خواهد کرد، آن چنانکه قطعات پراکنده ابرها را در فصل پاییز جمع می‌کند، خدا میان مسلمانان الفت ایجاد می‌کند، و به صورت ابرهای فشرده درمی آورد، آنگاه درهای پیروزی برویشان می‌گشاید، که مانند سیلی خروشان از جایگاه خود بیرون می‌ریزند، (چونان (سیل عرم) که دو باغستان شهر (سبا) را درهم کوبید، و در برابر آن سیل هیچ بلندی و تپه ای برجای نماند، نه کوه‌های بلند و محکم، و نه برآمدگیهای بزرگ، توانستند برابر آن مقاوت کنند) خداوند بنی امیه را مانند آب در درون دره‌ها و رودخانه‌ها پراکنده و پنهان می‌کند، سپس چون چشمه سارها بر روی زمین جاری می‌سازد، تا حق برخی از مردم را از بعضی دیگر بستاند، و گروهی را توانایی بخشیده در خانه‌های دیگران سکونت دهد. بخدا سوگند! بنی امیه پس از پیروزی و سلطه گری، همه آنچه را که به دست آوردند از کفشان می‌رود، چنانکه چربی بر روی آتش آب شود. \nعلل پیروزی و شکست ملتها ای مردم! اگر دست از یاری حق برنمی داشتید، و در خوار ساختن باطل سستی نمی کردید، هیچگاه آنان که به پایه شما نیستند در نابودی شما طمع نمی کردند، و هیچ قدرتمندی بر شما پیروز نمی گشت، اما چونان امت بنی اسرائیل در حیرت و سرگردانی فرو رفتید. بجانم سوگند! سرگردانی شما پس از من بیشتر خواهد شد چرا که به حق پشت کردید، و با نزدیکان پیامبر (ص) بریده به بیگانه‌ها نزدیک شدید، آگاه باشید، اگر از امام خود پیروی می‌کردید، شما را به راهی هدایت می‌کرد که رسول خدا (ص) رفته بود، و از اندوه بیراهه رفتن در امان بودید، و بار سنگینی مشکلات را از دوش خود برمی داشتید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (76, 1, 167, 'در ابتدای حکومتش', 'خطبه ۱۶۷-در ابتدای حکومتش \nویژگیهای قرآن \nهمانا خداوند بزرگ کتابی هدایتگر فرستاد، و نیکی و بدی خیر و شر را آشکارا در آن بیان فرمود، پس راه نیکی در پیش گیرید که هدایت شوید، و از شر و بدی پرهیز کنید تا در راه راست قرار گیرید. واجبات! واجبات! در انجام واجبات کوتاهی نکنید تا شما را به بهشت رساند، همانا خداوند چیزهایی را حرام کرده که ناشناخته نیست، و چیزهایی را حلال کرده که از عیب خالی است، و در این میان حرمت مسلمانان را بر هر حرمتی برتری بخشید و حفظ حقوق مسلمانان را به وسیله اخلاص و توحید استوار کرد. ویژگیهای مسلمانی: پس مسلمان کسی است که مسلمانان از زبان و دست او آزاری نبینند، مگر آنجا که حق باشد، و آزار مسلمانان روا نیست جز در آنچه که واجب باشد. به سوی مرگ که همگانی است، و فرد فرد شما را از آن گریزی نیست بشتابید، همانا مردم در پیش روی شما می‌روند، و قیامت از پشت سر، شما را می‌خواند. سبکبار شوید تا به قافله برسید که پیش رفتگان در انتظار بازماندگانند، از خدا بترسید، و تقوا پیشه کنید زیرا شما در پیشگاه خداوند، مسئول بندگان خدا، و شهرها، و خانه‌ها و حیوانات هستید خدا را اطاعت کنید و از فرمان خدا سر باز مدارید، اگر خیری دیدید ب \nرگزینید، و اگر شر و بدی دیدید از آن دوری کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (77, 1, 168, 'پس از بیعت با حضرت', 'خطبه ۱۶۸-پس از بیعت با حضرت \nواقع بینی در مبارزه با ناکثین \nای برادران! از آنچه شما می‌دانید بی اطلاع نیستم، اما قدرت اجرای آن را چگونه به دست آورم؟ آنان با ساز و برگ و نیرو به راه افتادند، بر ما تسلط دارند و ما بر آن‌ها قدرتی نداریم، هم اکنون بردگان شما با آن‌ها می‌جوشند، و بادیه نشینان اطراف شما به آن‌ها پیوسته اند، آن‌ها در میان شما زندگی می‌کنند، و هر مشکلی را که بخواهند بر شما می‌بارند، آیا برای خواسته‌های خود توانایید؟ مشکلات جنگ داخلی کاری که پیش آمده از جاهلیت است، شورشیان یار و یاور دارند، اگر برای کیفر دادنشان حرکتی آغاز شود، مردم به چند دسته تقسیم می‌شوند، گروهی خواسته‌های شما را دارند، و عده ای بر خلاف شما فکر می‌کنند، و گروهی نه این را می‌پسندند و نه آن را، پس صبر کنید تا مردم آرام شوند، و دلهای مضطرب در جای خود قرار گیرد، و حقوق از دست رفته با مدارا گرفته شود. اکنون مرا آسوده گذارید، و در انتظار فرمان من باشید، کاری نکنید که قدرت ما را تضعیف کند، اقتدار امت ما را متزلزل سازد و سستی و زبونی به بار آورد، این جریان سیاسی را تا می‌توانم مهار می‌کنم، اما اگر راه چاره ای نیابم با آنان می‌جنگم (که سرانجام درمان، داغ کردن است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (78, 1, 169, 'هنگام حرکت به بصره', 'خطبه ۱۶۹-هنگام حرکت به بصره \nضرورت اطاعت از رهبری \nهمانا خداوند پیامبری راهنما را با کتابی گویا، و دستوری استوار برانگیخت، هلاک نشود جز کسی که تبهکار است و بدانید که بدعتها به رنگ حق درآمده و هلاک کننده اند، مگر خداوند ما را از آن‌ها حفظ فرماید، و همانا حکومت الهی حافظ امور شماست، بنابراین زمام امور خود را بی آنکه نفاق ورزید یا کراهتی داشته باشید به دست امام خود سپارید. بخدا سوگند! اگر در پیروی از حکومت و امام، اخلاص نداشته باشید، خدا دولت اسلام را از شما خواهد گرفت که هرگز به شما باز نخواهد گردانید و در دست دیگران قرار خواهد داد. \nافشا توطئه ناکثین همانا ناکثین عهدشکن به جهت نارضایتی از حکومت من به یکدیگر پیوستند، و من تا آنجا که برای جامعه احساس خطر نکنم صبر خواهم کرد، زیرا آنان اگر برای اجرای مقاصدشان فرصت پیدا کنند، نظام جامعه اسلامی متزلزل می‌شود، آن‌ها از روی حسادت بر کسی که خداوند حکومت را به او بخشیده است به طلب دنیا برخاسته اند. می‌خواهند کار را به گذشته باز گردانند. حقی که شما به گردن ما دارید، عمل کردن به کتاب خدا (قرآن) و سنت پیامبر (ص) و قیام به حق و برپا داشتن سنت اوست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (79, 1, 17, 'داوران ناشایست', 'خطبه ۱۷-داوران ناشایست \nشناخت بدترین انسانها\nدشمن ترین آفریده‌ها، نزد خدا دو نفرند، مردی که خدا او را به حال خود گذاشته، و از راه راست دور افتاده است، دل او شیفته بدعت، و مردم را گمراه کرده، به فتنه انگیزی می‌کشاند، و راه رستگاری گذشتگان را گم کرده، و طرفداران خود و آیندگان را گمراه ساخته است، بار گناه دیگران را بر دوش کشیده، و گرفتار زشتیهای خود نیز می‌باشد. و مردی که مجهولاتی به هم بافته، و در میان انسانهای نادان امت، جایگاهی پیدا کرده است. در تاریکیهای فتنه فرو رفته، و از مشاهده صلح و صفا کور است، آدم نماها او را عالم نامیدند که نیست، چیزی را بسیار جمع آوری می‌کند که اندک آن به از بسیار است، تا آنکه از آب گندیده سیراب شود، و دانش و اطلاعات بیهوده فراهم آورد. روانشناسی مدعیان دروغین قضاوت در میان مردم با نام قاضی به داوری می‌نشیند، و حل مشکلات دیگری را به عهده می‌گیرد، پس اگر مشکلی پیش آید، با حرفهای پوچ و توخالی، و رای و نظر دروغین، آماده رفع آن می‌شود. سپس اظهارات پوچ خود را باور می‌کند، عنکبوتی را می‌ماند که در شبهات و بافته‌های تار خود چسبیده، نمی داند که درست حکم کرده یا برخطاست؟ اگر بر صواب باشد می‌ترسد که خط \nا کرده، و اگر بر خطاست، امید دارد که رای او درست باشد. نادانی است که راه جهالت می‌پوید، کوری است که در تاریکی گمشده خود را می‌جوید، از روی علم و یقین سخن نمی گوید، روایات را بدون آگاهی نقل می‌کند، چون تندبادی که گیاهان خشک را بر باد دهد، روایات را زیر و رو می‌کند، که بی حاصل است. به خدا سوگند نه راه صدور حکم مشکلات را می‌داند، و نه برای منصب قضاوت اهلیت دارد، آنچه را که نپذیرد علم به حساب نمی آورد، و جز راه و رسم خویش، مذهبی را حق نمی داند، اگر حکمی را نداند آن را می‌پوشاند تا نادانی او آشکار نشود، خون بی گناهان از حکم ظالمانه او در جوشش، و فریاد میراث بر باد رفتگان بلند است. به خدا شکایت می‌کنم از مردمی که در جهالت زندگی می‌کنند، و با گمراهی می‌میرند، در میان آن‌ها، کالایی خوارتر از قرآن نیست، اگر آن را آنگونه که باید بخوانند، و متاعی سودآورتر، گرانبهاتر از قرآن نیست، اگر آن را تحریف کنند، و در نزد آنان، چیزی زشت تر از معروف، و نیکوتر از منکر نیست.', 'و من كلام له (علیه السلام) في صفة من يتصدى للحكم بين الأمة و ليس لذلك بأهل و فيها: أبغض الخلائق إلى اللّه صنفان:\r\nالصنف الأول: إنَّ أَبْغَضَ الْخَلَائِقِ إِلَى اللَّهِ [تَعَالَى] رَجُلَانِ رَجُلٌ وَكَلَهُ اللَّهُ إِلَى نَفْسِهِ فَهُوَ جَائِرٌ عَنْ قَصْدِ السَّبِيلِ مَشْغُوفٌ بِكَلَامِ بِدْعَةٍ وَ دُعَاءِ ضَلَالَةٍ فَهُوَ فِتْنَةٌ لِمَنِ افْتَتَنَ بِهِ ضَالٌّ عَنْ هَدْيِ مَنْ كَانَ قَبْلَهُ مُضِلٌّ لِمَنِ اقْتَدَى بِهِ فِي حَيَاتِهِ وَ بَعْدَ وَفَاتِهِ حَمَّالٌ خَطَايَا غَيْرِهِ رَهْنٌ بِخَطِيئَتِهِ. الصنف الثاني: وَ رَجُلٌ قَمَشَ جَهْلًا مُوضِعٌ فِي جُهَّالِ الْأُمَّةِ عَادٍ فِي أَغْبَاشِ الْفِتْنَةِ عَمٍ بِمَا فِي عَقْدِ الْهُدْنَةِ قَدْ سَمَّاهُ أَشْبَاهُ النَّاسِ عَالِماً وَ لَيْسَ بِهِ بَكَّرَ فَاسْتَكْثَرَ مِنْ جَمْعٍ مَا قَلَّ مِنْهُ خَيْرٌ مِمَّا كَثُرَ حَتَّى إِذَا ارْتَوَى مِنْ مَاءٍ آجِنٍ وَ اكْتَثَرَ مِنْ غَيْرِ طَائِلٍ.جَلَسَ بَيْنَ النَّاسِ قَاضِياً ضَامِناً لِتَخْلِيصِ مَا الْتَبَسَ عَلَى غَيْرِهِ فَإِنْ نَزَلَتْ بِهِ إِحْدَى الْمُبْهَمَاتِ هَيَّأَ لَهَا حَشْواً رَثًّا مِنْ رَأْيِهِ ثُمَّ قَطَعَ بِهِ فَهُوَ مِنْ لَبْسِ الشُّبُهَاتِ فِي مِثْلِ نَسْجِ الْعَنْكَبُوتِ، لَا يَدْرِي أَصَابَ أَمْ أَخْطَأَ فَإِنْ أَصَابَ خَافَ أَنْ يَكُونَ قَدْ أَخْطَأَ وَ إِنْ أَخْطَأَ رَجَا أَنْ يَكُونَ قَدْ أَصَابَ جَاهِلٌ خَبَّاطُ جَهَالاتٍ عَاشٍ رَكَّابُ عَشَوَاتٍ لَمْ يَعَضَّ عَلَى الْعِلْمِ بِضِرْسٍ قَاطِعٍ يَذْرُو الرِّوَايَاتِ ذَرْوَ الرِّيحِ الْهَشِيمَ لَا مَلِيٌّ. وَ اللَّهِ إِصْدَارِ مَا وَرَدَ عَلَيْهِ وَ لَا [هُوَ] أَهْلٌ لِمَا قُرِّظَ بِهِ لَا يَحْسَبُ الْعِلْمَ فِي شَيْءٍ مِمَّا أَنْكَرَهُ وَ لَا يَرَى أَنَّ مِنْ وَرَاءِ مَا بَلَغَ مَذْهَباً لِغَيْرِهِ وَ إِنْ أَظْلَمَ عَلَيْهِ أَمْرٌ اكْتَتَمَ بِهِ لِمَا يَعْلَمُ مِنْ جَهْلِ نَفْسِهِ تَصْرُخُ مِنْ جَوْرِ قَضَائِهِ الدِّمَاءُ وَ تَعَجُّ مِنْهُ الْمَوَارِيثُ.إِلَى اللَّهِ أَشْكُو مِنْ مَعْشَرٍ يَعِيشُونَ جُهَّالًا وَ يَمُوتُونَ ضُلَّالًا لَيْسَ فِيهِمْ سِلْعَةٌ أَبْوَرُ مِنَ الْكِتَابِ إِذَا تُلِيَ حَقَّ تِلَاوَتِهِ وَ لَا سِلْعَةٌ أَنْفَقُ بَيْعاً وَ لَا أَغْلَى ثَمَناً مِنَ الْكِتَابِ إِذَا حُرِّفَ عَنْ مَوَاضِعِهِ وَ لَا عِنْدَهُمْ أَنْكَرُ مِنَ الْمَعْرُوفِ وَ لَا أَعْرَفُ مِنَ الْمُنْكَر.', NULL, '(در اين خطبه مدّعيان قضاوت را مى شناساند كه عهده دار قضاوت هستند و شايسته آن نيستند):\r\n1. شناخت بدترين انسانها:\r\nدشمن ترين آفريده ها نزد خدا دو نفرند: مردى كه خدا او را به حال خود گذاشته، و از راه راست دور افتاده است، دل او شيفته بدعت است و مردم را گمراه كرده، به فتنه انگيزى مى كشاند و راه رستگارى گذشتگان را گم كرده و طرفداران خود و آيندگان را گمراه ساخته است. بار گناه ديگران را بر دوش كشيده و گرفتار زشتى هاى خود نيز مى باشد. \r\nو مردى كه مجهولاتى به هم بافته، و در ميان انسان هاى نادان امّت، جايگاهى پيدا كرده است، در تاريكى هاى فتنه فرو رفته، و از مشاهده صلح و صفا كور است.\r\nآدم نماها او را عالم ناميدند كه نيست، چيزى را بسيار جمع آورى مى كند كه اندك آن به از بسيار است، تا آن كه از آب گنديده سيراب شود، و دانش و اطّلاعات بيهوده فراهم آورد.2. روانشناسى مدّعيان دروغين قضاوت:\r\nدر ميان مردم، با نام قاضى به داورى مى نشيند، و حلّ مشكلات ديگرى را به عهده مى گيرد، پس اگر مشكلى پيش آيد، با حرف هاى پوچ و تو خالى و رأى و نظر دروغين، آماده رفع آن مى شود.\r\nسپس اظهارات پوچ خود را باور مى كند، عنكبوتى را مى ماند كه در شبهات و بافته هاى تار خود چسبيده، نمى داند كه درست حكم كرده يا بر خطاست. اگر بر صواب باشد مى ترسد كه خطا كرده، و اگر بر خطاست، اميد دارد كه رأى او درست باشد.\r\nنادانى است، كه راه جهالت مى پويد، كورى است كه در تاريكى گمشده خود را مى جويد، از روى علم و يقين سخن نمى گويد، روايات را بدون آگاهى نقل مى كند، چون تند بادى كه گياهان خشك را بر باد دهد، روايات را زير و رو مى كند، كه بى حاصل است. \r\nبه خدا سوگند، نه راه صدور حكم مشكلات را مى داند، و نه براى منصب قضاوت أهليّت دارد آنچه را كه نپذيرد علم به حساب نمى آورد، و جز راه و رسم خويش، مذهبى را حق نمى داند، اگر حكمى را نداند آن را مى پوشاند تا نادانى او آشكار نشود، خون بى گناهان از حكم ظالمانه او در جوشش و فرياد ميراث بر باد رفتگان بلند است. به خدا شكايت مى كنم از مردمى كه در جهالت زندگى مى كنند و با گمراهى مى ميرند، در ميان آنها، كالايى خوارتر از قرآن نيست اگر آن را آنگونه كه بايد بخوانند، و متاعى سود آورتر و گرانبهاتر از قرآن نيست اگر آن را تحريف كنند، و در نزد آنان، چيزى زشت تر از معروف، و نيكوتر از منكر نمى باشد.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (80, 1, 170, 'چون به بصره نزدیک شد', 'خطبه ۱۷۰-چون به بصره نزدیک شد \n(در آستانه جنگ بصره، گروهی از اعراب، شخصی را جهت آگاهی از حقیقت و دانستن علل مبارزه امام (ع) با ناکثین به نمایندگی نزد حضرت علی (ع) فرستادند، امام به گونه ای با آن شخص صحبت فرمود که حقیقت را دریافت، آنگاه به او فرمود، بیعت کن، کلیب جرمی گفت، من نماینده گروهی هستم و قبل از مراجعه به آنان به هیچ کاری اقدام نمی کنم امام فرمود) \nروش هدایت کردن \nاگر آن‌ها تو را می‌فرستادند که محل ریزش باران را بیابی، سپس به سوی آنان باز می‌گشتی و از گیاه و سبزه و آب خبر می‌دادی، اگر مخالفت می‌کردند و به سرزمینهای خشک و بی آب روی می‌آوردند تو چه می‌کردی؟ (گفت: آن‌ها را رها می‌کردم و به سوی آب و گیاه می‌رفتم، امام فرمود) پس دستت را برای بیعت کردن بگشای. (مرد گفت: سوگند به خدا به هنگام روشن شدن حق توانایی مخالفت نداشتم و با امام (ع) بیعت کردم).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (81, 1, 171, 'در آغاز نبرد صفین فرمود', 'خطبه ۱۷۱-در آغاز نبرد صفین فرمود \nنیایش در آستانه جنگ \nای خدای آسمان برافراشته، و فضای نگاه داشته، که آن را زمینه پیدایش شب و روز، و جریان گردش ماه و خورشید، و مسیر آمد و شد ستارگان سیار قرار داده ای، و جایگاه گروهی از فرشتگانت ساخته ای که از عبادت تو خسته نمی گردند. ای پروردگار این زمین، که آن را جایگاه سکونت انسانها، و مکان رفت و آمد حشرات و چارپایان، و پدیده‌های دیدنی و نادیدنی غیر قابل شمارش قرار داده ای، و ای پروردگار کوه‌های بلند و پابرجا، که آن را برای زمین چونان میخهای محکم، و برای مخلوقات تکیه گاهی مطمئن ساخته ای. اگر بر دشمن پیروزمان ساختی، ما را از تجاوز برکنار دار، و بر راه حق استوار فرما، و چنانچه آن‌ها را بر ما پیروز گرداندی شهادت نصیب ما فرموده و از شرک و فساد و فتنه‌ها، ما را نگهدار! روش بسیج نیروها در جنگ کجایند آزادمردانی که به حمایت مردم خویش برخیزند؟ کجایند غیور مردانی که به هنگام نزول بلا و مشکلات مبارزه می‌کنند؟ ‌هان مردم!! ننگ و عار پشت سر شما، و بهشت در پیش روی شماست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (82, 1, 172, 'درباره خلافت خود، نکوهش اصحاب جمل', 'خطبه ۱۷۲-درباره خلافت خود \n(مطالب این خطبه، بخشی از نوشته‌های افشاگرانه‌ای است که در سال ۴۰ هجری برای روشن شدن واقعیت‌های تاریخ اسلام به دستور امام نوشته شد)\nستایش خداوندی را سزاست که نه آسمانی مانع آگاهی او از آسمان دیگر، و نه زمینی مانع او از زمین دیگر می‌شود. \nگفتگو در روز شورا \nشخصی در روز شورا به من گفت: (ای فرزند ابوطالب نسبت به خلافت حریص می‌باشی) در پاسخ او گفتم، بخدا سوگند! شما با اینکه از پیامبر اسلام دورترید، حریصتر می‌باشید، اما من شایسته و نزدیکتر به پیامبر اسلامم، همانا من تنها حق خود را مطالبه می‌کنم که شما بین من و آن حائل شدید، و دست رد بر سینه ام زدید. پس چون در جمع حاضران، با برهان قاطع او را مغلوب کردم، درمانده و سرگردان شد و نمی دانست در پاسخم چه بگوید؟ \nشکوه از قریش \nبار خدایا! از قریش و از تمامی آن‌ها که یاریشان کردند به پیشگاه تو شکایت می‌کنم، زیرا قریش پیوند خویشاوندی مرا قطع کردند، و مقام منزلت بزرگ مرا کوچک شمردند، و در غصب حق من، با یکدیگر هم داستان شدند، سپس گفتند: برخی از حق را باید گرفت و برخی را باید رها کرد. (یعنی خلافت حقی است که باید رهایش کنی) \nشکوه از ناکثین \nطلحه و زبیر و یارانشان بر من خروج کردند، و ناموس رسول خدا (ص) را به همراه خود می‌کشیدند چونان کنیزی را که به بازار برده فروشان می‌کشانند، به بصره روی آوردند، در حالی که همسران خود را پشت پرده نگهداشتند. پرده نشین حرم پیامبر (ص) را در برابر دیدگان خود و دیگران قرار دادند. لشکری را گرد آوردند که همه آن‌ها به اطاعت من گردن نهاده، و بدون اکراه، و با رضایت کامل با من بیعت کرده بودند، پس از ورود به بصره، به فرماندار من و خزانه داران بیت المال مسلمین و به مردم بصره، حمله کردند، گروهی از آنان را شکنجه و گروه دیگر را با حیله کشتند. بخدا سوگند! اگر جز به یک نفر دست نمی یافتند و او را عمدا بدون گناه می‌کشتند کشتار همه آن‌ها برای من حلال بود، زیرا همگان حضور داشتند و انکار نکردند، و از مظلوم با دست و زبان دفاع ننمودند، چه رسد به اینکه ناکثین به تعداد لشکریان خود از مردم بیدفاع بصره قتل عام کردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (83, 1, 173, 'شایسته خلافت', 'خطبه ۱۷۳-شایسته خلافت \nپیامبر اسلام (ص) امین وحی پروردگار، و خاتم پیامبران، و بشارت دهنده رحمت، و بیم دهنده کیفر الهی است. \nویژگیهای رهبر اسلامی \nای مردم! سزاوارترین اشخاص به خلافت، آنکه در تحقق حکومت نیرومندتر، و در آگاهی از فرمان خدا داناتر باشد، تا اگر آشوبگری به فتنه گری برخیزد، به حق باز گردانده شود، و اگر سر باز زد با او مبارزه شود. بجانم سوگند! اگر شرط انتخاب رهبر، حضور تمامی مردم باشد هرگز راهی برای تحقق آن وجود نخواهد داشت، بلکه آگاهان دارای صلاحیت و رای، و اهل حل و عقد (خبرگان ملت) رهبر و خلیفه را انتخاب می‌کنند، که عمل آن‌ها نسبت به دیگر مسلمانان نافذ است، آنگاه نه حاضران بیعت کننده، حق تجدید نظر دارند و نه آنان که در انتخابات حضور نداشتند حق انتخابی دیگر را خواهند داشت آگاه باشید! من با دو کس پیکار می‌کنم، کسی چیزی را ادعا کند که از آن او نباشد، و آن کس که از ادای حق سر باز زند. \nای بندگان خدا! شما را به تقوی و ترس از عذاب خدا سفارش می‌کنم، زیرا تقوای الهی بهترین سفارش مومنان، و بهترین پایان نامه کار در پیشگاه خداست، مردم! هم اکنون آتش جنگ بین شما و اهل قبله شعله ورشده است، و این پرچم مبارزه را جز افراد آگاه و بااستقامت و عالم به جایگاه حق بدوش نمی کشند بنابراین آنچه فرمان دادند انجام دهید، و از آنچه نهی کردند توقف کنید، و در هیچ کاری تا روشن نشود شتاب نکنید، زیرا در آنچه شما اکراه دارید توان تغییراتی داریم. \nشناخت دنیا \nآگاه باشید، همانا این دنیا که آرزوی آن را می‌کنید و بدان روی می‌آورید، و شما را گاهی به خشم می‌آورد و زمانی خشنود می‌سازد، خانه ماندگار شما نیست، و منزلی نیست که برای آن آفریده و به آن دعوت شدید، آگاه باشید نه دنیا برای شما جاودانه و نه شما در آن جاودانه خواهید ماند. دنیا گرچه از جهتی شما را می‌فریبد ولی از جهت دیگر شما را از بدیهایش می‌ترساند، پس برای هشدارهایش از آنچه مغرورتان می‌کند چشم پوشید، و به خاطر ترساندنش از طمع ورزی در آن بازایستید، به خانه ای که دعوت شدید سبقت گیرید، و دل از دنیا برگیرید، و چونان کنیزکان برای آنچه که از دنیا از دست می‌دهید گریه نکنید، و با صبر و استقامت بر اطاعت پروردگار، و حفظ و نگهداری فرامین کتاب خدا، نعمتهای پروردگار را نسبت به خویش کامل کنید. آگاه باشید، آنچه برای حفظ دین از دست می‌دهید زیانی به شما نخواهد رساند. آگاه باشید، آنچه را با تباه ساختن دین به دست می‌آورید سودی به حالتان نخواهد داشت خداوند دلهای ما و شما را به سوی حق متوجه سازد و صبر و استقامت عطا فرماید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (84, 1, 174, 'درباره طلحه', 'خطبه ۱۷۴-درباره طلحه \nافشاء ادعاهای دروغین طلحه \nتا بوده ام مرا از جنگ نترسانده، و از ضربت شمشیر نهراسانده اند، من به وعده پیروزی که پروردگارم داده است استوارم. بخدا سوگند! طلحه بن عبیدالله، برای خونخواهی عثمان شورش نکرد، جز اینکه می‌ترسید خون عثمان از او مطالبه شود، زیرا او خود متهم به قتل عثمان است، که در میان مردم از او حریصتر بر قتل عثمان یافت نمی شد، برای اینکه مردم را دچار شک و تردید کند، دست به اینگونه ادعاهای دروغین زد، سوگند بخدا! لازم بود طلحه، نسبت به عثمان یکی از سه راه حل را انجام می‌داد که نداد. اگر پسر عفان ستمکار بود چنانکه طلحه می‌اندیشید، سزاوار بود با قاتلان عثمان همکاری می‌کرد، و از یاران عثمان دوری می‌گزید، و یا اگر عثمان مظلوم بود می‌بایست از کشته شدن او جلوگیری می‌کرد، و نسبت به کارهای عثمان عذرهای موجه و عموم پسندی را طرح کند (تا خشم مردم فرو نشیند) و اگر نسبت به امور عثمان شک و تردید داشت خوب بود که از مردم خشمگین کناره می‌گرفت و به انزوا پناه برده و مردم را با عثمان وامی گذاشت. اما او هیچکدام از سه راه حل را انجام نداد، و به کاری دست زد که دلیل روشنی برای انجام آن نداشت، و عذرهایی آورد که مردم \nپسند نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (85, 1, 175, 'موعظه یاران', 'خطبه ۱۷۵-موعظه یاران \nنکوهش غافلان \nای بی خبرانی که آنی مورد غفلت نیستید، و ای ترک کنندگان فرامین الهی که از تمامی کارهایتان بازخواست می‌شوید، شما را چه شده است که از خدای خود رویگردان و به غیر او گرایش دارید؟ چونان چارپایانی مانید که چوپان آن‌ها را در بیابانی وباخیز، و آبهایی بیماری زا رها کرده است. گوسفندان پروار را می‌مانید که برای کارد قصاب آماده اند. ولی خودشان نمی دانند! چه آنکه هرگاه به گوسفندان با مقداری علف نیکی کنند یک روز خود را یک عمر پندارند، و زندگی را در سیر شدن شکمها می‌نگرند. علوم بی پایان امام (ع) سوگند بخدا! اگر بخواهم می‌توانم هر کدام شما را آغاز و پایان کارش، و از تمام شئون زندگی، آگاه سازم، اما از آن می‌ترسم که با اینگونه خبرها نسبت به رسول خدا (ص) کافر شوید. آگاه باشید! که من این اسرار گرانبها را به یاران رازدار و مورد اطمینان خود می‌سپارم، سوگند بخدایی که محمد (ص) را به حق برانگیخت، و او را برگزید، جز به راستی سخن نگویم، پیامبر اسلام (ص) همه اطلاعات را به من سپرده است، و از محل هلاکت آن کس که هلاک می‌شود، و جای نجات کس که نجات می‌یابد، و پایان این حکومت، همه را به من خبر داده و مرا آگاه‌انده است \n. هیچ حادثه ای بر من نگذشت جز آنکه در گوشم نجوا کرد، و مرا مطلع ساخت. ویژگیهای امام علی (ع) ای مردم! سوگند بخدا! من شما را به هیچ طاعتی وادار نمی کنم مگر آنکه پیش از آن خود عمل کرده ام، و از معصیتی شما را باز نمی دارم جز آنکه پیش از آن ترک گفته ام.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (86, 1, 176, 'در پند گرفتن از سخن خداوند', 'خطبه ۱۷۶-پند گرفتن از سخن خدا \nضرورت اطاعت از دستورات الهی \nمردم! از آنچه خداوند بیان داشته بهره گیرید، و از پند و اندرزهای خدا پند پذیرید، و نصیحتهای او را قبول کنید، زیرا خداوند با دلیلهای روشن راه عذر را به روی شما بسته، و حجت را بر شما تمام کرده است. و اعمالی که دوست دارد بیان فرموده، و از آنچه کراهت دارد معرفی کرد، تا از خوبیها پیروی و از بدیها دوری گزینید، همانا رسول خدا (ص) همواره می‌فرمود: (گرداگرد بهشت را دشواریها (مکاره) و گرداگرد آتش جهنم را هوسها و شهوات گرفته است.) آگاه باشید! چیزی از طاعت خدا نیست جز آن که با کراهت انجام می‌گیرد و چیزی از معصیت خدا نیست جز اینکه با میل و رغبت عمل می‌شود، پس رحمت خداوند بر کسی که شهوت خود را مغلوب و هوای نفس را سرکوب کند، زیرا کار مشکل، باز داشتن نفس از شهوت بوده که پیوسته خواهان نافرمانی و معصیت است. بندگان خدا! بدانید که انسان باایمان، شب را به روز، و روز را به شب نمی رساند جز آنکه نفس خویش را متهم می‌داند، همواره نفس را سرزنش می‌کند، و گناهکارش می‌شمارد، پس در دنیا چونان پیشینیان صالح خود باشید، که در پیش روی شما درگذشتند و همانند مسافران، خیمه خویش را از جا درآورند و به راه خو \nد رفتند. ویژگیهای قرآن آگاه باشید! همانا این قرآن پنددهنده ای است که نمی فریبد، و هدایت کننده ای است که گمراه نمی سازد، و سخنگویی که هرگز دروغ نمی گوید. کسی با قرآن همنشین نشد مگر آنکه بر او افزود یا از او کاست، افزودن در هدایت و کاهش از کوردلی و گمراهی. آگاه باشید کسی با داشتن قرآن، نیازی ندارد، و بدون قرآن بی نیاز نخواهد بود، پس درمان خود را از قرآن بخواهید، و در سختیها از قرآن یاری بطلبید، که در قرآن درمان بزرگترین بیماریها یعنی کفر و نفاق و سرکشی و گمراهی است، پس به وسیله قرآن خواسته‌های خود را از خدا بخواهید، و با دوستی قرآن بخدا روی آورید، و به وسیله قرآن از خلق خدا چیزی نخواهید، زیرا وسیله ای برای تقرب بندگان بخدا، بهتر از قرآن وجود ندارد. آگاه باشید، که شفاعت قرآن پذیرفته شده، و سخنش تصدیق می‌گردد، آن کس که در قیامت، قرآن شفاعتش کند بخشوده می‌شود، و آن کس که قرآن از او شکایت کند محکوم است، در روز قیامت ندادهنده ای بانگ می‌زند که: (آگاه باشید امروز هر کس گرفتار بذری است که کاشته و عملی است که انجام داده، جز اعمال منطبق با قرآن.) پس شما در شمار عمل کنندگان به قرآن باشید، از قرآن پیروی کنید، با قرآن خد \nا را بشناسید، و خویشتن را با قرآن اندرز دهید، و رای و نظر خود را برابر قرآن متهم کنید، و خواسته‌های خود را با قرآن نادرست بشمارید. \nتشویق به اعمال نیکو عمل صالح! عمل صالح! سپس آینده نگری! آینده نگری! و استقامت! استقامت! آنگاه، بردباری! بردباری! و پرهیزکاری! پرهیزکاری! برای هر کدام از شما عاقبت و پایان مهلتی تعیین شده، با نیکوکاری بدانجا برسید، و همانا پرچم هدایتی برای شما برافراشتند، با آن هدایت شوید، و برای اسلام نیز هدف و نتیجه ای است به آن دسترسی پیدا کنید، و با انجام واجبات، حقوق الهی را ادا کنید، که وظائف شما را آشکارا بیان کرده و من گواه اعمال شما بوده و در روز قیامت از شما دفاع می‌کنم و به سود شما گواهی می‌دهم. \nآگاه باشید! آنچه از پیش مقررشده بود، به وقوع پیوست، و خواسته‌های گذشته الهی انجام شد، و همانا من با تکیه به وعده‌های الهی و براهین روشن او سخن می‌گویم که فرمود: (کسانی که گفتند پروردگار ما خداست، سپس استقامت ورزیدند، فرشتگان بر آن‌ها فرود می‌آیند و می‌گویند، نترسید و محزون نباشید که بشارت باد بر شما بهشتی که به شما وعده داده اند.) و همانا شما گفتید: که پروردگار ما خداست، پس در عمل به دستورات قرآن خدا، و در ادامه راهی که فرمان داد، و بر روش درست پرستش بندگان او، استقامت داشته باشید. و پایدار مانید، و از دستورات خدا سرپیچی نکنید، و در آن بدعتگذار مباشید، و از آن منحرف نگردید، زیرا خارج شوندگان از دستورات الهی در روز قیامت از رحمت خدا دورند. ضرورت کنترل زبان سپس مواظب باشید که اخلاق نیکو را در هم نشکنید و به رفتار ناپسند مبدل نسازید، زبان و دل را هماهنگ کنید، مرد باید زبانش را حفظ کند، زیرا همانا این زبان سرکش، صاحب خود را به هلاکت می‌اندازد، بخدا سوگند! پرهیزگاری را ندیده ام که تقوی برای او سودمند باشد مگر آنکه زبان خویش را حفظ کرده بود، و همانا زبان مومن در پس قلب او، و قلب منافق از پس زبان اوست. \nزیرا مومن هرگاه بخواهد سخنی گوید، نخست می‌اندیشد، اگر نیک بود اظهار می‌دارد، و چنانچه ناپسند است پنهانش می‌کند، در صورتیکه منافق آنچه بر زبانش آمد می‌گوید، و نمی داند چه به سود او، و چه حرفی بر ضرر اوست؟ و پیامبر اسلام (ص) فرمود: (ایمان بنده ای استوار نگردد تا دل او استوار شود، و دل استوار نشود تا زبان استوار گردد) پس هر کس از شما بتواند خدا را در حالی ملاقات کند که دستش از خون و اموال مسلمانان پاک، و زبانش از عرض و آبروی مردم سالم ماند باید چنین کند. \nپرهیز از بدعتها ای بندگان خدا! آگاه باشید مومن کسی است که حلال خدا را هم اکنون حلال، و حرام خدا را هم اکنون حرام بشمارد و آنچه را مردم با بدعتها تغییر دادند چیزی از حرام را حلال نمی کند، زیرا حلال همان است که خدا حلال کرده و حرام همان چیزی است که خدا حرام شمرده است. پس شما در امور و حوادث روزگار تجربه آموختید و از تاریخ گذشتگان پند گرفتید، مثلها برای شما زده اند، و به امری آشکار دعوت شده اید، جز ناشنوایان کسی ادعای نشنیدن حق را ندارد، و جز کوران و کوردلان کسی ادعای ندیدن واقعیتها نمی کند، آن کس که از آزمایشها و تجربه‌های خدادادی سودی نبرد از هیچ پند و اندرزی سود نخواهد برد و کوته فکری دامنگیر او خواهد شد تا آنجا که بد را خوب، و خوب را بد می‌نگرد، و همانا مردم دو دسته اند، گروهی پیرو شریعت و دین، و برخی بدعت گذارند که از طرف خدا دلیلی از سنت پیامبر، و نوری از براهین حق ندارند. ویژگیهای قرآن همانا خداوند سبحان! کسی را به چیزی چون قرآن پند نداده است، که قرآن ریسمان استوار خدا، و وسیله ایمنی بخش است، در قرآن بهار دل، و چشمه‌های دانش است، برای قلب جلائی جز قرآن نتوان یافت، بخصوص در جامعه ای که بیدار \nدلان در گذشته و غافلان و تغافل کنندگان حضور دارند. پس هرجا کار نیکی دیدید یاری کنید، و هرگاه چیز بد و ناروایی مشاهده کردید دوری گزینید، زیرا پیامبر اسلام (ص) همواره می‌فرمود: (ای فرزند آدم، کار نیک را انجام ده، و کار بد را واگذار، اگر چنین کنی در راه راست الهی قرار خواهی داشت.) \nاقسام ظلم و ستم آگاه باشید که ظلم بر سه قسم است، ظلمی که نابخشودنی است، و ظلمی که بدون مجازات نمی ماند، و ظلمی که بخشودنی و جبران شدنی است. اما ظلمی که نابخشودنی است، شرک به خدای سبحان است که فرمود: (خداوند هیچگاه از شرک به خود درنمی گذرد.) و اما ظلمی که بخشودنی است، ستمی که بنده با گناهان بر خویشتن روا داشته است، و ظلمی که بدون مجازات نیست، ستمگری بعضی از بندگان بر بعض دیگر است که قصاص در آنجا سخت است، مجروح کردن با کارد، یا تازیانه زدن نیست بلکه اینها در برابرش کوچک است، پس مبادا در دین دورویی ورزید، که همبستگی و وحدت در راه حق گرچه (کراهت داشته باشید) از پراکندگی در راه باطل (گرچه مورد علاقه شما باشد) بهتر است. زیرا خداوند سبحان نه به گذشتگان و نه آیندگان چیزی را با تفرقه عطا نفرموده است. ضرورت خودسازی ای مردم! خوشا به حال کسی که عیب شناسی نفس، او را از عیبجویی دیگران باز دارد، و خوشا به حال کسی که به خانه و خانواده خود پردازد، و غذای حلال خود را بخورد، و به اطاعت پروردگار مشغول باشد، و بر خطاهای خویش بگرید، همواره به خویشتن خویش مشغول و مردم از او در امان باشند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (87, 1, 177, 'درباره حکمین', 'خطبه ۱۷۷-درباره حکمین \nنکوهش از خیانت حکمین \nرای جمعیت شما در صفین یکی شد که دو مرد را به داوری برگزینند (ابوموسی اشعری، عمروعاص) و از آن دو پیمان گرفتیم که در برابر قرآن تسلیم باشند، و از آن تجاوز نکنند، زبان آن دو با قرآن و قلبهایشان پیرو کتاب خدا باشد، اما آن‌ها از قرآن رویگردان شدند، حق را آشکار می‌دیدند و ترک گفتند که جور و ستم، خواسته دلشان، و کجی و انحراف در روش فکریشان بود. در صورتیکه پیش از صدور رای زشت و حکم جائرانه، با آن‌ها شرط کرده بودیم که به عدل حکم نموده و به حق عمل کنند، ما به حقانیت خود ایمان داریم در حالی که آن دو از راه حق بیرون رفتند و حکمی بر خلاف حکم خدا صادر کردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (88, 1, 178, 'در صفات خداوند عزوجل', 'خطبه ۱۷۸-در صفات خداوند \nخداشناسی هیچ کاری خدا را از کار دیگر باز نمی دارد، و گذشت زمان در او دگرگونی ایجاد نمی کند، و مکانی او را در بر نمی گیرد، هیچ زبانی قدرت وصف او را ندارد، و چیزی از خدا مخفی و پنهان نیست، نه تعداد قطرات فراوان آبها، و نه ستارگان انبوه آسمان، و نه ذرات خاک همراه با گردبادها در هوا، و نه حرکات مورچگان بر سنگهای سخت، و نه استراحتگاه مورچگان ریز در شبهای تار. خدا از مکان ریزش برگ درختان، و حرکات مخفیانه چشمها آگاه است. و شهادت می‌دهم که جز الله، خدایی نیست، همتایی نداشته و شک و تردیدی در او راه ندارد، دین او را انکار نمی کنم، و به آفریدگاری او اعتقاد دارم، گواهی کسی که نیت او راست، درون او پاک، یقین او خالص، و میزان عمل او گرانسنگ است. و شهادت می‌دهم که محمد (ص) بنده و فرستاده و برگزیده او از میان انسانهاست پیامبر (ص) برای تشریح حقائق آیین الهی انتخاب، و به ارزشهای ویژه اخلاقی گرامی داشته شد. او را برای رساندن رسالتهای کریمانه اش برگزید، نشانه‌های هدایت به وسیله او آشکار، و تاریکیهای جهل و گمراهی با نور هدایت او از میان رفت. \nروش برخورد با دنیا ای مردم! دنیا آرزومندان و خواهان خود را فریب می‌دهد، برای شیفتگان خود ارزشی قائل نیست، و آن کس را که بر دنیا پیروز شود مغلوب گرداند. بخدا سوگند! هرگز ملتی از ناز و نعمت زندگی گرفته نشدند مگر به کیفر گناهانی که انجام داده اند، زیرا خداوند به بندگان خود ستم روا نمی دارد، اگر مردم به هنگام نزول بلاها، و گرفته شدن نعمتها، با درستی نیت در پیشگاه خدا زاری کنند، و با قلبهای پر از محبت از خداوند درخواست عفو نمایند، آنچه از دستشان رفته باز خواهد گشت، و هرگونه فسادی خواهد شد. من بر شما ترسناکم که در جهالت و غرور فرو رفته باشید، چه اینکه در گذشته به سویی کشیده شدید که قابل ستایش نبود. اما اگر در زندگانی خود اصلاحاتی پدید آورید، سعادتمند خواهید شد، وظیفه من جز تلاش و کوشش در اصلاح امور شما نیست، اگر می‌خواستم، بی مهریهای شما را بازگو می‌کردم، خدا آنچه را گذشت ببخشاید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (89, 1, 179, 'در پاسخ به ذعلب یمانی', 'خطبه ۱۷۹-پاسخ به ذعلب یمانی \nخداشناسی \nدیده‌ها هرگز او را آشکار نمی بینند، اما دلها با ایمان درست او را درمی یابند، خدا به همه چیز نزدیک است نه آنکه به اشیاء چسبیده باشد، و از همه چیز دور است نه آنکه از آن‌ها بیگانه باشد، گوینده است نه با اندیشه و فکر، اراده کننده است نه از روی آرزو و خواهش، سازنده است نه با دست و پا، لطافت دارد نه آنکه پوشیده و مخفی باشد، بزرگ است نه با ستمکاری، بیناست نه با حواس ظاهری، مهربان است نه با نازکدلی، سرها و چهره‌ها در برابر عظمت او به خاک افتاده، و دلها از ترس او بی قرارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (90, 1, 18, 'نکوهش اختلاف رای عالمان', 'خطبه ۱۸-نکوهش اختلاف عالمان \nدعوایی نسبت به یکی از احکام اجتماعی نزد عالمی می‌برند که با رای خود حکمی صادر می‌کند، پس همان دعوی را نزد دیگری می‌برند که او درست برخلاف رای اولی، حکم می‌دهد، سپس همه قضات نزد رییس خود که آنان را به قضاوت منصوب کرد، جمع می‌گردند، او رای همه را بر حق می‌شمارد!! مبانی وحدت امت اسلامی در صورتیکه خدایشان یکی، پیغمبرشان یکی، و کتابشان یکی است، آیا خدای سبحان، آن‌ها را به اختلاف فرمود؟ که اطاعت کردند؟ یا آن‌ها را از اختلاف پرهیز داد و معصیت خدا نمودند؟ آیا خدای سبحان، دین ناقصی فرستاد و در تکمیل آن از آن‌ها استمداد کرده است؟ آیا آن‌ها شرکاء خدایند که هر چه می‌خواهند در احکام دین بگویند، و خدا رضایت دهد؟ آیا خدای سبحان، دین کاملی فرستاد پس پیامبر (ص) در ابلاغ آن کوتاهی ورزید؟ در حالی که خدای سبحان می‌فرماید: (ما در قرآن چیزی را فروگذار نکردیم) و فرمود: (در قرآن بیان هر چیزی است) و یادآور شدیم که: بعض قرآن گواه بعض دیگر است و اختلافی در آن نیست. پس خدای سبحان فرمود: (اگر قرآن از طرف غیر خدا نازل می‌شد اختلافات زیادی در آن می‌یافتند) همانا قرآن دارای ظاهری زیبا، و باطنی ژرف و ناپیداست، مطالب شگفت آور آن تمام نمی شود، و اسرار نهفته آن پایان نمی پذیرد، و در تاریکیها بدون قرآن بر طرف نخواهد شد.', 'و من كلام له (علیه السلام) في ذمّ اختلاف العلماء في الفُتيا و فيه يذمّ أهل الرأي و يكل أمر الحكم في أمور الدين للقرآن‏:\r\nذمّ أهل الرأي‏:\r\nتَرِدُ عَلَى أَحَدِهِمُ الْقَضِيَّةُ فِي حُكْمٍ مِنَ الْأَحْكَامِ فَيَحْكُمُ فِيهَا بِرَأْيِهِ ثُمَّ تَرِدُ تِلْكَ الْقَضِيَّةُ بِعَيْنِهَا عَلَى غَيْرِهِ فَيَحْكُمُ فِيهَا بِخِلَافِ قَوْلِهِ ثُمَّ يَجْتَمِعُ الْقُضَاةُ بِذَلِكَ عِنْدَ الْإِمَامِ الَّذِي اسْتَقْضَاهُمْ فَيُصَوِّبُ آرَاءَهُمْ جَمِيعاً وَ إِلَهُهُمْ وَاحِدٌ وَ نَبِيُّهُمْ وَاحِدٌ وَ كِتَابُهُمْ وَاحِدٌ أَ فَأَمَرَهُمُ اللَّهُ [تَعَالَى‏] سُبْحَانَهُ بِالاخْتِلَافِ فَأَطَاعُوهُ أَمْ نَهَاهُمْ عَنْهُ فَعَصَوْهُ‏.الحكم للقرآن‏:\r\nأَمْ أَنْزَلَ اللَّهُ سُبْحَانَهُ دِيناً نَاقِصاً فَاسْتَعَانَ بِهِمْ عَلَى إِتْمَامِهِ أَمْ كَانُوا شُرَكَاءَ لَهُ فَلَهُمْ أَنْ يَقُولُوا وَ عَلَيْهِ أَنْ يَرْضَى، أَمْ أَنْزَلَ اللَّهُ سُبْحَانَهُ دِيناً تَامّاً فَقَصَّرَ الرَّسُولُ (صلی الله علیه وآله) عَنْ تَبْلِيغِهِ وَ أَدَائِهِ؟ وَ اللَّهُ سُبْحَانَهُ يَقُولُ «ما فَرَّطْنا فِي الْكِتابِ مِنْ شَيْءٍ» وَ فِيهِ تِبْيَانٌ لِكُلِّ شَيْءٍ وَ ذَكَرَ أَنَّ الْكِتَابَ يُصَدِّقُ بَعْضُهُ بَعْضاً وَ أَنَّهُ لَا اخْتِلَافَ فِيهِ، فَقَالَ سُبْحَانَهُ «وَ لَوْ كانَ مِنْ عِنْدِ غَيْرِ اللَّهِ لَوَجَدُوا فِيهِ اخْتِلافاً كَثِيراً». وَ إِنَّ الْقُرْآنَ ظَاهِرُهُ أَنِيقٌ وَ بَاطِنُهُ عَمِيقٌ لَا تَفْنَى عَجَائِبُهُ وَ لَا تَنْقَضِي غَرَائِبُهُ وَ لَا تُكْشَفُ الظُّلُمَاتُ إِلَّا بِهِ.', NULL, '(نكوهش از اختلاف رأى عالمان در احكام قطعى اسلام):\r\n1. نكوهش اهل رأى (خودمحورى در قضاوت):\r\nدعوايى نسبت به يكى از احكام اجتماعى نزد عالمى مى برند كه با رأى خود حكمى صادر مى كند. پس همان دعوا را نزد ديگرى مى برند كه او درست بر خلاف رأى اوّلى حكم مى دهد.\r\nسپس همه قضات نزد رييس خود كه آنان را به قضاوت منصوب كرده، جمع مى گردند. او رأى همه را بر حق مى شمارد. در صورتى كه خدايشان يكى، پيغمبرشان يكى، و كتابشان يكى است.\r\nآيا خداى سبحان، آنها را به اختلاف، امر فرمود، كه اطاعت كردند يا آنها را از اختلاف پرهيز داد و معصيت خدا نمودند. 2. مبانى وحدت امّت اسلامى:\r\nآيا خداى سبحان، دين ناقصی فرستاد و در تكميل آن از آنها استمداد كرده است؟ آيا آنها شركاء خدايند كه هر چه مى خواهند در احكام دين بگويند و خدا رضايت دهد.\r\nآيا خداى سبحان، دين كاملى فرستاد، پس پيامبر صلّى اللّه عليه و آله و سلّم در ابلاغ آن كوتاهى ورزيد؟\r\nخداى سبحان مى فرمايد: «ما در قرآن چيزى را فروگذار نكرديم». و فرمود: «در قرآن بيان هر چيزى است». و ياد آور شده كه: بعض قرآن گواه بعض ديگر است و اختلافى در آن نيست. پس خداى سبحان فرمود: «اگر قرآن از طرف غير خدا نازل مى شد اختلافات زيادى در آن مى يافتند».\r\nهمانا قرآن داراى ظاهرى زيبا و باطنى ژرف و ناپيداست، مطالب شگفت آور آن تمام نمى شود، و اسرار نهفته آن پايان نمى پذيرد و تاريكى ها بدون قرآن بر طرف نخواهد شد.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (91, 1, 180, 'در نکوهش یارانش', 'خطبه ۱۸۰-در نکوهش یارانش \nنکوهش کوفیان \nخدا را بر آنچه که خواسته و هر کار که مقدر فرمود ستایش می‌کنم، و او را بر این گرفتار شدنم به شما کوفیان می‌ستایم، ای مردمی که هرگاه فرمان دادم اطاعت نکردید، و هر زمان شما را دعوت کردم پاسخ ندادید، هرگاه شما را مهلت می‌دهم در بیهودگی فرو می‌روید، و در هنگامه جنگ سست و ناتوانید، اگر مردم اطراف امام خود جمع شوند طعنه زده، و اگر شما را برای حل مشکلی بخوانند سر باز می‌زنید. پدر مباد دشمنان شما را! برای پیروزی منتظر چه چیزی هستید؟ چرا برای گرفتن حق خود جهاد نمی کنید؟ آیا در انتظار مرگ یا ذلت هستید؟ بخدا سوگند! اگر مرگ من فرا رسد که حتما خواهد رسید، بین من و شما جدایی خواهد افتاد، در حالی که من از همنشینی با شما ناراحت، و حضورتان برای من بی فایده بود. علل سقوط و انحطاط فکری کوفیان خدا خیرتان دهد، آیا دینی نیست که شما را گردآورد؟ آیا غیرتی نیست که شما را برای جنگ با دشمن بسیج کند؟ شگفت آور نیست که معاویه انسانهای جفاکار پست را می‌خواند و آن‌ها بدون انتظار کمک و بخششی از او پیروی می‌کنند!! و من شما را برای یاری حق می‌خوانم، در حالی که شما بازماندگان اسلام، و یادگار مسلمانان پیشین می‌باشید، با \nکمک و عطایا شما را دعوت می‌کنم ولی از اطراف من پراکنده می‌شوید، و به تفرقه و اختلاف روی می‌آورید. نه از دستورات من راضی می‌شوید، و نه شما را به خشم می‌آورد که بر ضد من اجتماع کنید، اکنون دوست داشتنی چیزی که آرزو می‌کنم، مرگ است. کتاب خدا را به شما آموختم، و راه و رسم استدلال را به شما آموزش دادم، و آنچه را که نمی شناختید به شما شناساندم، و دانشی را که به کامتان سازگار نبود جرعه جرعه به شما نوشاندم. ای کاش نابینا می‌دید! و خفته بیدار می‌شد! سوگند بخدا چه نادان مردمی که رهبر آنان معاویه، و آموزگارشان پسر نابغه (عمروعاص) باشد!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (92, 1, 181, 'پیوستگان به خوارج', 'خطبه ۱۸۱-پیوستگان به خوارج \nنکوهش فریب خوردگان از خوارج \nاز رحمت خدا دور باشند چونان قوم ثمود، آگاه باشید، اگر نیزه‌ها به سوی آنان راست شود، شمشیرها بر سرشان فرود آید، از گذشته خود پشیمان خواهند شد، امروز شیطان آن‌ها را به تفرقه دعوت کرد، و فردا از آن‌ها بیزاری می‌جوید، و از آن‌ها کنار خواهد کشید. همین ننگ آنان را کافی است که از هدایت گریختند و در گمراهی و کوری فرو رفتند، راه حق را بستند، و در حیرت و سرگردانی ماندند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (93, 1, 182, 'در توحید الهی و یادی از یاران شهید خود در صفین', 'خطبه ۱۸۲-توحید الهی \n(از نوف بکالی نقل شد که امیر مومنان علی (ع) در شهر کوفه بر روی تخته سنگی که جعده بن هیبره برای او نصب کرد ایستاد و این سخنرانی را ایراد کرد، در حالی که پیراهنی خشن از پشم بر تن داشت و شمشیری با بندی از لیف خرما به گردن آویخته و بر پاهای مبارکش، کفشی از لیف خرما داشت و اثر سجده در پیشانی او پیدا بود). \nستایش پروردگار سبحان \nستایش خداوندی را سزاست که سرانجام خلقت، و پایان کارها به او باز می‌گردد، خدا را بر احسان بزرگش، و برهان آشکار، و فراوانی فضل و آنچه بدان بر ما منت نهاده است می‌ستایم، ستایشی که حق او را اداء کند، و شکر شایسته او را بجا آورد، به ثواب الهی ما را نزدیک گرداند و موجب فراوانی نیکی و احسان او گردد. از خدا یاری می‌طلبیم، یاری خواستن کسی که به فضل او امیدوار، و به بخشش او آرزومند، و به دفع زیانش مطمئن، و به قدرت او معترف، و به گفتار و کردار پروردگار اعتقاد دارد. به او ایمان می‌آوریم، ایمان کسی که با یقین به او امیدوار، و با اعتقاد خالص به او توجه دارد، و با ایمانی پاک در برابرش کرنش می‌کند، و با اخلاص به یگانگی او اعتقاد دارد، و با ستایش فراوان خدا را بزرگ می‌شمارد، و با رغبت و تلاش به \nاو پناهنده می‌شود. راه‌های خداشناسی خدا از کسی متولد نشد تا در عزت و توانایی دارای شریک باشد، و فرزندی ندارد تا وارث او باشد، وقت و زمان از او پیشی نگرفت، و زیادی و نقصان در او راه ندارد، خدا با نشانه‌های تدبیر استوار، و خواسته‌های حکیمانه در خلق نظام احسن، در برابر غفلتها، آشکارا جلوه کرده است. از نشانه‌های آفرینش او، خلقت آسمانهای پابرجا بدون ستون و تکیه گاه است، آسمانها را به اطاعت خویش دعوت و آن‌ها بدون درنگ اجابت کردند، اگر اقرار آسمانها به پروردگاری او، و اعترافشان در اطاعت و فرمانبرداری از او نبود، هرگز آسمانها را محل عرش خویش و جایگاه فرشتگان، و بالا رفتن سخنان پاک و اعمال نیک و صالح بندگانش قرار نمی داد. ستارگان را نشانه‌های هدایتگر بیابان ماندگان سرگردان قرار داد تا به وسیله آن‌ها راهنمایی شوند، ستارگانی که پرده‌های تاریک شب مانع نورافشانی آن‌ها نمی گردد، و نمی تواند از نورافشانی و تلالو ماه در دل آسمان جلوگیری کند، پس پاک است خدایی که پوشیده نیست بر او سیاهی تیره و تار، بر روی ناهمواریهای زمین، و قله‌های کوتاه و بلند کوهها، و نه غرش رعد در کرانه آسمان، و نه درخشش برق در لابلای ابرها، و نه وزش بادهای \nتند و طوفان، و نه ریزش برگها بر اثر بارش باران، و نه محل سقوط قطرات باران، و نه مسیر کشیده شدن دانه‌ها به وسیله مورچگان، و نه غذاهای کوچک نادیدنی پشه‌ها، و نه آنچه که در شکم حیوانات ماده در حال رشد است (خدا به همه آن‌ها آگاه است) \nخداشناسی حمد و سپاس خداوندی را سزاست که همواره وجود داشت، پیش از آنکه کرسی یا عرش، آسمان یا زمین، جن یا انس، پدید آیند، خداوندی که ذات او را فکرها و عقلهای ژرف اندیش نتوانند بشناسند، و با نیروی اندیشه اندازه ای برای او نتوانند تصور کنند، هیچ سوال کننده ای او را به خود مشغول نسازد، و فراوانی عطا و بخشش از دارایی او نکاهد، برای دیدن به چشم مادی نیاز ندارد، و در مکانی محدود نمی شود. همسر و همتایی ندارد، و با تمرین و تجربه نمی آفریند، و با حواس درک نشود، و با مردم مقایسه نگردد، خدایی که بدون اعضاء و جوارح و زبان و کام با حضرت موسی (ع) سخن گفت و آیات بزرگش را به او شناساند. ای کسی که برای توصیف کردن پروردگارت به زحمت افتاده ای، اگر راست می‌گویی جبرئیل و می‌کاییل و لشکرهای فرشتگان مقرب را وصف کن، که در بارگاه قدس الهی سر فرود آورده اند، و عقلهایشان در درک خدا، سرگردان و درمانده است، تو چیزی رامی توانی با صفات آن درک کنی که دارای شکل و اعضا و جوارح باشد، دارایی عمر محدود و اجل معین باشد. پس جز الله خدایی نیست که هر تاریکی را به نور خود روشن کرد، و هر چه را جز به نور او روشن بود به تاریکی کشاند. \nسفارش به تقوی و پندپذیری از تاریخ ای بندگان خدا! شما را به پرهیزکاری و ترس از خدایی سفارش می‌کنم که شما را جامه‌ها پوشانید، و وسائل زندگی شما را فراهم کرد، اگر راهی برای زندگی جاودانه وجود می‌داشت، یا از مرگ گریزی بود، حتما سلیمان بن داوود (ع) چنین می‌کرد، او که خداوند حکومت بر جن و انس را همراه با نبوت و مقام بلند قرب و منزلت، در اختیارش قرار داد اما آنگاه که پیمانه عمرش لبریز و روزی او تمام شد، تیرهای مرگ از کمانهای نیستی بر او باریدن گرفت، و خانه و دیار از او خالی گشت، خانه‌های او بی صاحب ماند، و دیگران آن‌ها را به ارث بردند. مردم! برای شما در تاریخ گذشته درسهای عبرت فراوان وجود دارد، کجایند عمالقه و فرزندانشان؟ (پادشاهان عرب در یمن و حجاز) کجایند فرعونها و فرزندانشان؟ کجایند مردم شهر رس (درخت پرستانی که طولانی حکومت کردند) آن‌ها که پیامبران خدا را کشتند، و چراغ نورانی سنت آن‌ها را خاموش کردند، و راه و رسم ستمگران و جباران را زنده ساختند. کجایند آن‌ها که با لشکرهای انبوه حرکت کردند؟ و هزاران تن را شکست دادند، سپاهیان فراوانی گرد آوردند، و شهرها ساختند؟ \nوصف حضرت مهدی (عجل الله تعالی فرجه الشریف) زره دانش بر تن دارد، و با تمامی آداب، و با توجه و معرفت کامل آن را فرا گرفته، حکمت گمشده اوست که همواره در جستجوی آن می‌باشد، و نیاز اوست که در به دست آوردنش می‌پرسد. در آن هنگام که اسلام غروب می‌کند و چونان شتری در راه مانده بر زمین می‌ماند، و سینه به زمین می‌چسباند او پنهان خواهد شد (دوران غیبت صغری و کبری) او باقیمانده حجتهای الهی است، و جانشینان پیامبران. \nپند و اندرز یاران ای مردم! من پند و اندرزهایی که پیامبران در میان امتهای خود داشتند در میان شما نشر دادم، و وظائفی را که امامان پیامبران گذشته در میان مردم خود بانجام رساندند، تحقق بخشیدم، با تازیانه شما را ادب کردم نپذیرفتید، به راه راست نرفتید، و با هشدارهای فراوان شما را خواندم ولی جمع نشدید. شما را بخدا! آیا منتظرید رهبری جز من با شما همراهی کند؟ و راه حق را به شما نشان دهد؟ \nآگاه باشید! آنچه از دنیا روی آورده بود پشت کرد و آنچه پشت کرده بود روی آورد و بندگان نیکوکار خدا آماه کوچ کردن شدند، و دنیای اندک و فانی را با آخرت جاویدان تعویض نمودند. یاد یاران شهید آری! آن دسته از برادرانی که در جنگ صفین خونشان ریخت، هیچ زیانی نکرده‌اند گرچه امروز نیستند تا خوراکشان غم و غصه و نوشیدنی آن‌ها خونابه دل باشد، بخدا سوگند! آن‌ها خدا را ملاقات کردند، که پاداش آن‌ها را داد و پس از دوران ترس آن‌ها را در سرای امن خود جایگزین فرمود کجا هستند برادران من؟ که بر راه حق رفتند، و با حق درگذشتند کجاست عمار؟ و کجاست پسر تیهان؟ (مالک بن تیهان انصاری) و کجاست ذوالشهادتین؟ (خزیمه بن ثابت که پیامبر شهادت او را دو شهادت دانست) و کجایند همانند آنان از برادرانشان که پیمان جانبازی بستند، و سرهایشان را برای ستمگران فرستادند؟ (پس دست به ریش مبارک گرفت و زمانی طولانی گریست و فرمود) دریغا! از برادرانم که قرآن را خواندند، و بر اساس آن قضاوت کردند، در واجبات الهی اندیشه نموده و آن‌ها را برپا داشتند، سنتهای الهی را زنده و بدعتها را نابود کردند، دعوت جهاد را پذیرته و به رهبر خود اطمینان داشته و از او پیروی نمو \nدند. جهاد! جهاد! بندگان خدا! من امروز لشکر آماده می‌کنم، کسی که می‌خواهد به سوی خدا رود همراه ما خارج شود. (نوف گفت: برای حسین (ع) ده هزار سپاه، و برای قیس بن سعد ده هزار سپاه، و برای ابوایوب ده هزار سپاه قرار داد، و برای دیگر فرماندهان نیز سپاهی معین کرد، و آماده بازگشت به صفین بود که قبل از جمعه ابن ملجم ملعون به امام ضربت زد، و لشکریان بخانه‌ها بازگشتند، و ما چون گوسفندانی بودیم که شبان خود را از دست داده که گرگها از هر سو برای آنان دهان گشوده بودند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (94, 1, 183, 'آفریدگار توانا، قرآن گویا', 'خطبه ۱۸۳-آفریدگار توانا \nخداشناسی \nستایش خداوندی را سزاست که شناخته شد بی آنکه دیده شود، و آفرید بی آنکه دچار رنج و زحمتی گردد، با قدرت خود پدیده‌ها را آفرید، و با عزت خود گردنکشان را بنده خویش ساخت، و با بخشش خود بر همه بزرگان برتری یافت، او خدایی است که دنیارا مسکن مخلوقات برگزید، و پیامبران خود را به هدایت جن و انس فرستاد، تا دنیا را آنگونه که هست بشناسانند، و از زیانهای دنیا برحذر دارند، و با مطرح کردن مثلها، عیوب دنیاپرستی را نشان دهند، و آنچه را که مایه عبرت است تذکر دهند، از تندرستیها و بیماریها، از حلال و حرام، و آنچه را که خداوند برای بندگان اطاعت کننده و نافرمان، از بهشت و جهنم آماده کرده، که وسیله کرامت یا سقوط و پستی است بازگو نمایند. خدا را ستایش می‌کنم چنانکه خود از بندگان خواسته است، و برای هر چیزی اندازه ای قرار داده و برای هر اندازه ای مدتی معین کرده، و هر مدتی را حسابی مقرر داشته است. \nویژگیهای قرآن قرآن فرماندهی بازدارنده، و ساکتی گویا، و حجت خدا بر مخلوقات است، خداوند پیمان عمل کردن به قرآن را از بندگان گرفته، و آنان را در گرو دستوراتش قرار داده است، نورانیت قرآن را تمام، و دین خود را به وسیله آن کامل فرمود، و پیامبرش را هنگامی از جهان برد که از تبلیغ احکام قرآن فراغت کامل فرمود، و پیامبرش را هنگامی از جهان برد که از تبلیغ احکام قرآن فراغت یافته بود، پس خدا را آنگونه بزرگ بشمارید که خود بیان داشته است. خداوند چیزی از دینش را پنهان نکرده، و آنچه مورد رضایت یا خشم او بود وانگذاشته، جز آنکه نشانه ای آشکار، و آیتی استوار برای آن قرار داده است، که به سوی آن دعوت یا پرهیز داده شوند، پس خشنودی و خشم خدا در گذشته و حال یکسان است. \nبدانید همانا خداوند از شما خشنود نمی شود بکاری که بر گذشتگان خشم گرفته، و خشم نمی گیرد بکاری که بر گذشتگان خشنود بود، شما در راهی آشکار قدم برمی دارید، و سخن گذشتگان را تکرار می‌کنید، خداوند نیازمندیهای دنیای شما را کفایت کرده و به شکرگزاری وادارتان ساخت، و یاد خویش را بر زبانهای شما لازم شمرد. سفارش به پرهیزگاری خدا شما را به پرهیزگاری سفارش کرد، و آن را نهایت خشنودی خود، و خواستش از بندگان قرار داده است، پس بترسید از خدایی که در پیشگاه او حاضرید، و اختیار شما در دست اوست، و همه حالات و حرکات شما را زیر نظر دارد. اگر چیزی را پنهان کنید می‌داند، و اگر آشکار کردید ثبت می‌کند، برای ثبت اعمال، فرشتگان بزرگواری را گمارده که نه حقی را فراموش، و نه باطلی را ثبت می‌کنند. (آگاه باشید! آن کس که تقوا پیشه کند و از خدا بترسد، از فتنه‌ها نجات می‌یابد) و بانور هدایت از تاریکیها می‌گریزد، و به بهشت و آنچه که دوست دارد جاودانه دسترسی پیدا می‌کند، خدا او را در منزل کرامت خویش مسکن می‌دهد، خانه ای که مخصوص خداست، سقف آن عرش پروردگاری، و روشنایی آن از جمال الهی، و زائرانش فرشتگان، و دوستان و هم نشینانش پیامبران \nالهی، می‌باشند. پس به سوی قیامت بشتابید، و پیش از آنکه مرگ آن‌ها را در کام خود می‌کشد، و در توبه بسته می‌شود، شما امروز بجای کسانی زندگی می‌کنید که قبل از شما بودند و ناگهان رفتند و پس از مرگ تقاضای بازگشت به دنیا کردند. ضرورت یاد قیامت و عذاب الهی مردم! شما چونان مسافران در راهید، که در این دنیا فرمان کوچ داده شدید، که دنیا خانه اصلی شما نیست و به جمع آوری زاد و توشه فرمان داده شدید. آگاه باشید! این پوست نازک تن، طاقت آتش دوزخ را ندارد پس به خود رحم کنید، شما مصیبتهای دنیارا آزمودید، آیا ناراحتی یکی از افراد خود را بر اثر خاری که در بدنش فرو رفته، یا در زمین خوردن پایش مجروح شده، یا ریگهای داغ بیابان او را رنج داده، دیده اید که تحمل آن مشکل است؟ پس چگونه می‌شود تحمل کرد که در میان دو طبقه آتش، در کنار سنگهای گداخته، همنشین شیطان باشید؟ آیا می‌دانید وقتی مالک دوزخ بر آتش غضب کند، شعله‌ها بر روی هم می‌غلطند و یکدیگر را می‌کوبند؟ و آنگاه که بر آتش بانگ زند میان درهای جهنم به هر طرف زبانه می‌کشد؟ \nای پیر سالخورده! که پیری وجودت را گرفته است، چگونه خواهی بود آنگاه که طوقهای آتش به گردنها انداخته شود، و غل و زنجیرهای آتشین به دست و گردن افتد؟ چنانکه گوشت دستها را بخورد؟ روش استفاده از دنیا پس خدا را! خدا را! ای جمعیت انسانها پروا کنید! حال که تندرستید نه بیمار، و در حال گشایش هستید نه تنگ دست، در آزادی خویش پیش از آن که درهای امید بسته شود بکوشید، در دل شبها با شب زنده داری، و پرهیز از شکمبارگی به اطاعت برخیزید، با اموال خود انفاق کنید، از جسم خود بگیرید و بر جان خود بیفزایید، و در بخشش بخل نورزید که خدای سبحان فرمود: (اگر خدا را یاری کنید، شما را پیروز می‌گرداند و قدمهای شما را استوار می‌دارد) و فرمود: (کیست که به خدا قرض نیکو دهد؟ تا خداوند چند برابر عطا فرماید، و برای او پاداش بی عیب و نقصی قرار دهد) درخواست یاری از شما به جهت ناتوانی نیست، و قرض گرفتن از شما برای کمبود نمی باشد، در حالی از شما یاری خواسته که: (لشکرهای آسمان و زمین در اختیار اوست و خدا نیرومند و حکیم است) و در حالی طلب وام از شما دارد که گنجهای آسمان و زمین به او تعلق دارد و خدا بی نیاز و حمید است، بلکه خواسته است شما ر \nا بیازماید که کدام یک از شما نیکوکارترید، پس به اعمال نیکو مبادرت کنید، تا با همسایگان خدا در سرای او باشید که هم نشینان آن‌ها پیامبران، و زیارت کنندگانشان فرشتگانند، و چنان گرامی داشته می‌شوند که صدای آهسته آتش را نشنوند، و به بدنهایشان هیچگونه رنج و ناراحتی نرسد، (این بخشش خداست به هر کس بخواهد می‌دهد و خدا صاحب بخشش بزرگ است)، من آنچه را می‌شنوید می‌گویم، و خداوند را به یاری خود و شما می‌خوانم که او کفایت کننده و بهترین وکیل است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (95, 1, 184, 'خطاب به برج بن مسهر', 'خطبه ۱۸۴-خطاب به برج بن مسهر \nافشای منافق \nخاموش باش! خدا رویت را زشت گرداند، ای دندان پیشین افتاده بخدا سوگند! آنگاه که حق آشکار شد تو ناتوان بودی، و آواز تو آهسته بود تا آن که باطل بانگ برآورد، چونان شاخ بز سر برآوردی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (96, 1, 185, 'در ستایش پیامبر', 'خطبه ۱۸۵-در ستایش پیامبر \nخداشناسی \nستایش خداوندی را سزاست که حواس او را درک نکنند، و مکانها او را دربرنگیرند، دیدگان او را ننگرند، و پوششها او را پنهان نسازند، با حدوث آفرینش ازلی بودن خود را ثابت کرد، و با پیدایش انواع پدیده‌ها وجود خود را اثبات فرمود، و با همانند داشتن مخلوقات ثابت شد که خدا همانندی ندارد. خدا در وعده‌های خود راستگو، و برتر از آن است که بر بندگان ستم روا دارد، میان مخلوقات به عدل و داد رفتار کند، و در اجرای احکام عادلانه فرمان دهد، حادث بودن اشیا، گواه بر ازلیت اوست، و ناتوانی موجود، دلیل قدرت بی مانند او، و نابودی پدیده‌ها، گواه دائمی بودن اوست. خدا یکی است نه با شمارش، همیشگی است نه با محاسبه زمان، برپاست نه با نگهدارنده ای، اندیشه‌ها او را می‌شناسند نه با درک حواس، نشانه‌های خلقت به او گواهی می‌دهند نه به حضور مادی، فکرها و اندیشه‌ها بر ذات او احاطه ندارند، که با آثار عظمت خود بر آن‌ها تجلی کرده است، و نشان داد که او را نمی توانند تصور کنند، و داوری این ناتوانی را بر عهده فکرها و اندیشه‌ها نهاد. بزرگی نیست دارای درازا و پهنا و ژرفا، که از جسم بزرگی برخوردار باشد، و باعظمتی نیست که کالبدش بی نهایت بزرگ و ستبر باشد، بلکه بزرگی خدا در مقام رتبت، و عظمت او در قدرت و حکومت اوست. ویژگیهای پیامبر اسلام (ص) گواهی می‌دهم که محمد (ص) بنده و فرستاده خدا، و پیامبر برگزیده، و امانتدار پسندیده اوست (درود خدا بر او و عترت او باد) خدا او را با حجت‌های الزام کننده، و پیروزی آشکار، و راه روشن فرستاد، پس رسالت خود را آشکار رساند و مردم را به راه راست واداشت، و به همگان نشان داد، و نشانه‌های هدایت را برافراشت، و چراغهای روشن را بر سر راه آدمیان گرفت، رشته‌های اسلام را استوار کرد، و دستگیره‌های ایمان را محکم و پایدار نمود. راه‌های خداشناسی اگر مردم در عظمت قدرت خدا، و بزرگی نعمت‌های او می‌اندیشیدند، به راه راست بازمی گشتند، و از آتش سوزان می‌ترسیدند، اما دلها بیمار، و چشمها معیوب است. آیا به مخلوقات کوچک خدا نمی نگرند؟ که چگونه آفرینش آن را استحکام بخشید؟ و ترکیب اندام آن را برقرار، و گوش و چشم برای آن پدید آورد، و استخوان و پوست متناسب خلق کرد؟ به مورچه و کوچکی جثه آن بنگرید، که چگونه لطافت خلقت او با چشم و اندیشه انسان درک نمی شود، نگاه کنید! چگونه روی زمین راه می‌رود، و برای به دست آوردن روزی خود تلاش می‌کند؟ دانه‌ها را به لانه خود منتقل می‌سازد، و در جایگاه مخصوص نگه می‌دارد، در فصل گرما برای زمستان تلاش کرده، و به هنگام درون رفتن، بیرون آمدن را فراموش نمی کند. روزی مورچه تضمین گردیده، و غذاهای متناسب با طبعش آفریده شده است، خداوند منان از او غفلت نمی کند، و پروردگار پاداش دهنده محرومش نمی سازد، گرچه در دل سنگی سخت و صاف یا در میان صخره ای خشک باشد، اگر در مجاری خوراک و قسمتهای بالا و پایین دستگاه گوارش و آنچه در درون شکم او از غضروفهای آویخته به دنده تا شکم، و آنچه در سر اوست از چشم و گوش، اندیشه نمایی، از آفرینش مورچه دچار شگفتی شده و از وصف او به زحمت خواهی افتاد، پس بزرگ است خدایی که مورچه را بر روی دست و پایش برپا داشت، و پیکره وجودش را با استحکام نگاه داشت در آفرینش آن هیچ قدرتی او را یاری نداد و هیچ آفریننده ای کمکش نکرد. اگر اندیشه ات را بکار گیری تا به راز آفرینش پی برده باشی، دلائل روشن به تو خواهند گفت که آفریننده مورچه کوچک همان آفریدگار درخت بزرگ خرماست، به جهت دقتی که جدا جدا در آفرینش هر چیزی بکار رفته، و اختلافات و تفاوتهای پیچیده ای که در خلقت هر پدیده حیاتی نهفته است، همه موجودات سنگین و سبک، بزرگ و کوچک، نیرومند و ضعیف، در اصول حیات و هستی یکسانند، و خلقت آسمان و هوا و بادها و آب یکی است. پس اندیشه کن در آفتاب و ماه، و درخت و گیاه، و آب و سنگ، و اختلاف شب و روز، و جوشش دریاها، و فراوانی کوهها، و بلندای قله‌ها، و گوناگونی لغت‌ها، و تفاوت زبانها، که نشانه‌های روشن پروردگارند. پس وای بر آن کس که تقدیرکننده را نپذیرد، و تدبیرکننده را انکار کند. و اختلافات صورتهایشان را سازنده ای نیست، بر آنچه ادعا می‌کنند حجت و دلیلی ندارند، و بر آنچه در سر می‌پرورانند تحقیقی نمی کنند. آیا ممکن است ساختمانی بدون سازنده، یا جنایتی بدون جنایتکار باشد؟ شگفتی آفرینش ملخ و اگر خواهی در شگفتی ملخ سخن گو، که خدا برای او دو چشم سرخ، دو حدقه چونان ماه تابان آفرید، و به او گوش پنهان، و دهانی متناسب اندامش بخشیده است، دارای حواس نیرومند، و دو دندان پیشین که گیاهان را می‌چیند، و دو پای داس مانند که اشیاء را برمی دارد، کشاورزان برای زراعت از آن‌ها می‌ترسند و قدرت دفع آن‌ها را ندارند گر چه همه متحد شوند. ملخها نیرومندانه وارد کشتزار می‌شوند و آنچه میل دارند می‌خورند، در حالی که تمام اندامشان به اندازه یک انگشت باریک نیست! نشانه‌های خدا در طبیعت پس بزرگ است خداوندی که تمام موجودات آسمان و زمین، خواه و ناخواه او را سجده می‌کنند، و در برابر او با خضوع چهره بر خاک می‌سایند، و رشته اطاعت او را در تندرستی و ناتوانی به گردن می‌نهند، و از روی ترس و بیم زمام اختیار خود را به او می‌سپارند. پرندگان رام فرمان اویند، از تعداد پرها و نفسهای پرندگان آگاه است، برخی را پرنده آبی و گروهی را پرنده خشکی آفرید، و روزی آن‌ها را مقدر فرمود، و اقسام گوناگون آن‌ها را می‌داند، این کلاغ است و آن عقاب، این شترمرغ است و آن کبوتر، هر پرنده ای را به نام خاصی فراخواند، و روزیش را فراهم کرد. خدایی که ابرهای سنگین را ایجاد، و بارانهای پی درپی را فرستاد، و سهم باران هر جایی را معین فرمود، زمینهای خشک را آبیاری کرد، و گیاهان را پس از خشکسالی رویاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (97, 1, 186, 'در توحید', 'خطبه ۱۸۶-در توحید \nشناساندن صحیح خداوند سبحان \nکسی که کیفیتی برای خدا قائل شد یگانگی او را انکار کرده، و آن کس که همانندی برای او قرار داد به حقیقت خدا نرسیده است، کسی که خدا را به چیزی تشبیه کرد به مقصد نرسید، آن کس که به او اشاره کند یا در وهم آورد، خدا را بی نیاز ندانسته است، هر چه که ذاتش شناخته باشد آفریده است، و آنچه در هستی به دیگری متکی باشد دارای آفریننده است، سازنده ای غیرمحتاج به ابزار، اندازه گیرنده ای بی نیاز نه به فکر و اندیشه، بی نیاز بدون یاری دیگران است، با زمانها همراه نبوده، و از ابزار و وسائل کمک نگرفته است، هستی او برتر از زمان است، و وجود او بر نیستی مقدم است، و ازلیت او را آغازی نیست. با پدید آوردن حواس روشن می‌شود که حواسی ندارد، و با آفرینش اشیا متضاد، ثابت می‌شود که دارای ضدی نیست، و با هماهنگ کردن اشیا دانسته شد که همانندی ندارد، خدایی که روشنی را با تاریکی، آشکار را با نهان، خشکی را با تری، گرمی را با سردی ضد هم قرار داد، و عناصر متضاد را با هم ترکیب و هماهنگ کرد، و بین موجودات ضد هم، وحدت ایجاد نمود، آن‌ها را که با هم دور بودند نزدیک کرد، و بین آن‌ها که با هم نزدیک بودند فاصله انداخت. خدایی که حدی ندارد، و با شماره محاسبه نمی گردد، که همانا ابزار و آلات دلیل محدود بودن خویشند و به همانند خود اشاره می‌شوند، اینکه می‌گوییم موجودات از فلان زمان پدید آمده‌اند پس قدیم نمی توانند باشند و حادثند، و اینکه می‌گوییم حتما پدید آمدند، ازلی بودن آن‌ها رد می‌شود، و اینکه می‌گوییم اگر چنین بودند کامل می‌شدند، پس در تمام جهات کامل نیستند، خدا با خلق پدیده‌ها در برابر عقلها جلوه کرد، و از مشاهده چشم‌ها برتر و والاتر است، و حرکت و سکون در او راه ندارد، زیرا او خود حرکت و سکون را آفرید، چگونه ممکن است آنچه را که خود آفرید در او اثر بگذارد؟ یا خود از پدیده‌های خویش اثر پذیرد؟ اگر چنین شود، ذاتش چون دیگر پدیده‌ها، تغییر می‌کند، و اصل وجودش تجزیه می‌پذیرد، و دیگری ازلی نمی تواند باشد، هنگامی که آغازی برای او تصور شود پس سرآمدی نیز خواهد داشت، و این آغاز و انجام، دلیل روشن نقص، و نقصان و ضعف دلیل مخلوق بودن، و نیاز به خالقی دیگر داشتن است، پس نمی تواند آفریدگار همه هستی باشد، و از صفات پروردگار که (هیچ چیز در او موثر نیست، و نابودی و تغییر و پنهان شدن در او راه ندارد) خارج می‌شود. والاتر از صفات پدیده‌ها خدا فرزندی ندارد تا فرزند دیگری باشد، و زاده نشده تا محدود به حدودی گردد، و برتر است از آنکه پسرانی داشته باشد، و منزه است که با زنانی ازدواج کند، اندیشه‌ها به او نمی رسند تا اندازه ای برای خدا تصور نماید، حواس از احساس کردن او عاجز، و دست‌ها از لمس کردن او ناتوان است و تغییر و دگرگونی در او راه ندارد، و گذشت زمان تاثیری در او نمی گذارد، گذران روز و شب او را سالخورده نسازد، و روشنایی و تاریکی در او اثر ندارد. خدا با هیچ یک از اجزاء و جوارح و اعضا و اندام، و نه با عرضی از اعراض، و نه با دگرگونیها و تجزیه، توصیف نمی شود، برای او اندازه و نهایتی وجود ندارد، و نیستی و سرآمدی نخواهد داشت، چیزی او را در خود نمی گنجاند که بالا و پایینش ببرد، و نه چیزی او را حمل می‌کند که کج یا راست نگهدارد، نه در درون اشیا قرار دارد و نه بیرون آن، حرف می‌زند نه با زبان و کام و دهان، می‌شنود نه با سوراخهای گوش و آلت شنوایی، سخن می‌گوید نه با بکار گرفتن الفاظ در بیان، حفظ می‌کند نه با رنج به خاطر سپردن، می‌خواهد نه با بکارگیری اندیشه، دوست دارد و خشنود می‌شود نه از راه دلسوزی، دشمن می‌دارد و به خشم می‌آید نه با صوتی که در گوشها نشیند، و نه فریادی که شنیده شود بلکه سخن خدای سبحان همان کاری است که ایجاد می‌کند. شناخت قدرت پروردگار پیش از او چیزی وجود نداشته وگرنه خدای دیگری می‌بود، نمی شود گفت (خدا نبود و پدید آمد) که در آن صورت صفات پدیده‌ها را پیدا می‌کند و نمی شود گفت (بین خدا و پدیده‌ها جدایی است) و (خدا بر پدیده‌ها برتری دارد) زیرا سازنده و ساخته شده همانند تصور شوند، و خالق و پدیدآمده با یکدیگر تشبیه گردند، مخلوقات را بدون استفاده از طرح و الگوی دیگران آفرید، و در آفرینش پدیده‌ها از هیچ کسی یاری نگرفت، زمین را آفرید و آن را برپا نگهداشت بدون آنکه مشغولش سازد، و در حرکت و بی قراری، حرکت آن را نظم و اعتدال بخشید و بدون ستونی آن را بپا داشت، و بدون استوانه‌ها حفظ کرد، میخ‌های زمین را محکم، و کوه‌های آن را استوار، و چشمه‌هایش را جاری، و دره‌ها را ایجاد فرمود، آنچه بنا کرده به سستی نگرایید. و آنچه را توانا کرد ناتوان نشد. خدا با بزرگی و قدرت بر آفریده‌ها حاکم است، و با علم و آگاهی از باطن و درونشان باخبر است، و با جلال و عزت خود از همه برتر و بالاتر است، چیزی از فرمان او سرپیچی نمی کند، و چیزی قدرت مخالفت با او را ندارد که بر او پیشی گیرد، و به سرمایه داری نیاز ندارد تا او را روزی دهد، همه در برابر او فروتنند، و در برابر عظمت او ذلیل و خوارند، از قدرت و حکومت او به سوی دیگری نمی توان گریخت که از سود و زیانش در امان ماند، همتایی ندارد تا با او برابری کند، و او را همانندی نیست که شبیه او باشد، اوست نابودکننده پدیده‌ها پس از آفرینش، که گویا موجودی نبود. معاد و آفرینش دوباره پدیده‌ها نابودی جهان پس از پدید آمدن، شگفت آورتر از آفرینش آغازین آن نیست، چگونه محال است در صورتیکه اگر همه جانداران جهان، از پرندگان و چهارپایان، آنچه در آغل است و آنچه در بیابان سرگرم چراست، از تمام نژادها و جنس‌ها، درس نخوانده و زیرک انسانها، گرد هم آیند تا پشه ای را بیافرینند، توان پدید آوردن آن را ندارند، و راه پیدایش آن را نمی شناسند، که عقلهایشان سرگردان و در شناخت آن حیران می‌ماند، و نیروی آن‌ها سست و به پایان می‌رسد، و رانده و درمانده باز می‌گرداند، آنگاه اعتراف به شکست می‌کنند، و اقرار دارند که نمی توانند پشه ای بیافرینند و از نابود ساختنش ناتوانند و همانا پس از نابودی جهان تنها خدای سبحان باقی می‌ماند، تنهای تنها که چیزی با او نیست، آنگونه که قبل از آفرینش جهان چیزی با او نبود، بی وقت و زمان، نه روزگاری و نه مکان، در آن هنگام مهلت‌ها به سرآید، سالها و ساعتها سپری شود و چیزی جز خدای یگانه قهار باقی نمی ماند که بازگشت همه چیز به سوی اوست. پدیده‌ها، چنانکه در آغاز آفریده شدن قدرتی نداشتند، به هنگام نابودی نیز قدرت امتناعی ندارند، زیرا اگر می‌توانستند پایدارتر می‌ماندند، آفرینش چیزی برای خدا رنج آور نبوده و در آفرینش موجودات دچار فرسودگی و ناتوانی نشده است، موجودات را برای استحکام حکومتش نیافریده، و برای ترس از کمبود و نقصان پدید نیاورده است، آفرینش مخلوقات نه برای پرهیز از دشمنی بود که به او هجوم آورد، و نه برای ازدیاد دوران حکومت، و نه برای پیروز شدن بر شریک و معارض، و نه برای رفع تنهایی بوده است. سپس همه موجودات را نابود می‌کند، نه برای خستگی از اداره آن‌ها، و نه برای آسایش و استراحت، و نه به خاطر رنج و سنگینی که برای او داشتند، و نه برای طولانی شدن ملال آور زندگیشان، بلکه خداوند با لطف خود موجودات را اداره می‌فرماید، و با فرمان خود همه را برپا می‌دارد، و با قدرت خود همه را استوار می‌کند. سپس بدون آنکه نیازی داشته باشد بار دیگر همه را باز می‌گرداند، نه برای اینکه از آن‌ها کمکی بگیرد، و نه برای رها شدن از تنهایی با آن‌ها مانوس شود، و نه آنکه تجربه ای به دست آورد، و نه برای آنکه از فقر و نیاز به توانگری رسد، و یا از ذلت و خواری به عزت و قدرت راه یابد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (98, 1, 187, 'در بیان پیشامدها', 'خطبه ۱۸۷-در بیان پیشامدها \nخبر از حوادث آینده \nآگاه باشید! آنان که پدر و مادرم فدایشان باد، از کسانی هستند که در آسمانها معروفند، و در زمین گمنام. ‌هان ای مردم! در آینده پشت کردن روزگار خوشی را انتظار کشید، و قطع شدن پیوندها، و روی کار آمدن خردسالان، و این روزگاری است که ضربات شمشیر بر مومن آسان تر از یافتن مال حلال است، روزگاری که پاداش گیرنده از دهنده بیشتر است. و آن روزگاری که بی نوشیدن شراب مست می‌شوید، با فراوانی نعمت‌ها، سوگند می‌خورید بدون اجبار، دروغ می‌گویید نه از روی ناچاری. و آن روزگاری است که بلاها شما را گاز گیرد و مجروح سازد، چونان زخم شدن دوش شتران از پالان، آه! آن رنج و اندوه چقدر طولانی، و امید گشایش چقدر دور است! ضرورت اطاعت از رهبری ای مردم! مهار بار سنگین گناهان را رها کنید، و امام خود را تنها مگذارید، که در آینده خود را سرزنش می‌کنید خود را در آتش فتنه ای که پیشاپیش افروخته اید نیفکنید، راه خود گیرید، و از راهی که به سوی فتنه‌ها کشانده می‌شود دوری کنید، بجانم سوگند که مومن در شعله آن فتنه‌ها نابود شود. اما نامسلمان در امان باشد. همانا من در میان شما چونان چراغ درخشنده در تاریکی هستم، که هر کس به آن رو می‌آورد از نورش بهره مند می‌گردد، ای مردم سخنان مرا بشنوید، و به خوبی حفظ کنید، گوش دل خود را باز کنید تا گفته‌های مرا بفهمید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (99, 1, 188, 'در سفارش به تقوا', 'خطبه ۱۸۸-در سفارش به تقوا \nسفارش به پرهیزکاری \nای مردم شما را به پرهیزکاری، و شکر فراوان در برابر نعمتهای، و عطاهای الهی، و احسانی که به شما رسیده سفارش می‌کنم، چه نعمتهایی که به شما اختصاص داده، و رحمتهایی که برای شما فراهم فرمود، عیبهای خود را آشکار کردید و او پوشاند، خود را در معرض کیفر او قرار دادید و او به شما مهلت داد. ارزش یاد مرگ مردم! شما را به یادآوری مرگ، سفارش می‌کنم، از مرگ کمتر غفلت کنید، چگونه طمع می‌ورزید که به شما مهلت نمی دهد، مرگ گذشتگان برای عبرت شما کافی است، آن‌ها را به گورشان حمل کردند، بی آنکه بر مرکبی سوار باشند، آنان را در قبر فرود آوردند بی آنکه خود فرود آیند، چنان از یاد رفتند گویا از آبادکنندگان دنیا نبودند و آخرت همواره خانه اشان بود، آن چه را وطن خود می‌دانستند از آن رمیدند، و از آنجا که می‌رمیدند، آرام گرفتند، و چیزهائی که با آن‌ها مشغول بودند جدا شدند، آنجا را که سرانجامشان بود ضایع کردند. اکنون نه قدرت دارند از اعمال زشت خود دوری کنند، و نه می‌توانند عمل نیکی بر نیکیهای خود بیفزایند، به دنیایی انس گرفتند که مغرورشان کرد، چون به آن اطمینان داشتند سرانجام مغلوبشان نمود. ضرورت شتاب در نیکوکاریها خدا شما را رحمت کند، پس بشتابید به سوی آباد کردن خانه‌هایی که شما را به آبادانی آن فرمان دادند، و تشویقتان کرده، به سوی آن دعوت نموده اند، و با صبر و استقامت نعمتهای خدا را بر خود تمام گردانید، و از عصیان و نافرمانی کناره گیرید، که فردا به امروز نزدیک است. وه! چگونه ساعتها در روز، و روزها در ماه، و ماهها در سال، و سالها در عمر آدمی شتابان می‌گذرد؟.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (100, 1, 189, 'ایمان', 'خطبه ۱۸۹-ایمان \nاقسام ایمان \nایمان بر دو قسم است، یکی ایمانی که در دلها ثابت و برقرار، و دیگری در میان دلها و سینه‌ها ناپایدار است، تا سرآمدی که تعیین شده است. پس اگر از کسی بیزارید، او را به حال خود گذارید تا مرگ او فرا رسد، پس در آن هنگام وقت بیزاری جستن است. شناخت هجرت و مهاجر واقعی و هجرت، بر جایگاه ارزشی نخستین خود قرار دارد، خدا را به ایمان اهل زمین نیازی نیست، چه ایمان خود را پنهان دارند یا آشکار کنند. و نام مهاجر را بر کسی نمی توان گذاشت جز آن کس که حجت خدا بر روی زمین را بشناسد، هر کس حجت خدا را شناخت، و به امامت او اقرار کرد مهاجر است. و نام مستضعف در دین، بر کسی که حجت بر او تمام شد، و گوشش آن را شنید، و قلبش آن را دریافت، صدق نمی کند (و معذور نیست) مشکل فهم برخی از احادیث عترت (ع) همانا کار ما (ولایت ما) اهل بیت پیامبر (ص) سخت و تحمل آن دشوار است، که جز مومن دیندار که خدا او را آزموده و ایمانش در دل استوار بوده، قدرت پذیرش و حمل آن را ندارد، و حدیث ما را جز سینه‌های امانت پذیر، و عقلهای بردبار فرا نگیرد. آگاهی ژرف امام (ع) ای مردم پیش از آنکه مرا نیابید، آنچه می‌خواهید از من بپرسید، که من راه‌های آسمان را بهتر از راه‌های زمین می‌شناسم، بپرسید قبل از آن که فتنه‌ها چونان شتری بی صاحب حرکت کند، و مهار خود را پایمال نماید، و مردم را بکوبد و بیازارد، و عقلها را سرگردان کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (101, 1, 19, 'به اشعث بن قیس', 'خطبه ۱۹-به اشعث بن قیس \nسوابق تاریخی نکوهیده اشعث بن قیس \nچه کسی تو را آگاه‌اند که چه چیزی به سود، یا زیان من است؟ لعنت خدا و لعنت لعنت کنندگان، بر تو باد ای متکبر متکبر زاده، منافق پسر کافر، سوگند به خدا، تو یکبار در زمان کفر و بار دیگر در حکومت اسلام، اسیر شدی، و مال و خویشاوندی تو، هر دو بار نتوانست به فریادت برسد، آن کس که خویشان خود را به دم شمشیر سپارد، و مرگ و نابودی را به سوی آن‌ها کشاند، سزاوار است که بستگان او بر وی خشم گیرند و بیگانگان به او اطمینان نداشته باشند. (منظور امام (ع) این است که اشعث ابن قیس یک بار وقتی که کافر بود اسیر شد و بار دیگر آنگاه که مسلمان شد و شمشیرها را به سوی قبیله اش راهنمایی کرد، مربوط به جریانی است که اشعث قبیله خود را فریب داد تا خالد بن ولید، آن‌ها را غافلگیر کند و از دم شمشیر بگذراند که پس از آن خیانت او را با لقب (عرف النار) چیزی که آتش را بپوشاند، می‌نامیدند و این لقبی بود که به نیرنگباز می‌دادند).', 'و من كلام له (علیه السلام) قاله للأشعث بن قيس و هو على منبر الكوفة يخطب، فمضى في بعض كلامه شي‏ء اعترضه الأشعث فيه، فقال: يا أميرالمؤمنين، هذه عليك لا لَكَ، فخفض (علیه السلام) إليه بصره ثم قال:مَا يُدْرِيكَ مَا عَلَيَّ مِمَّا لِي، عَلَيْكَ لَعْنَةُ اللَّهِ وَ لَعْنَةُ اللَّاعِنِينَ، حَائِكٌ ابْنُ حَائِكٍ، مُنَافِقٌ ابْنُ كَافِرٍ. وَ اللَّهِ لَقَدْ أَسَرَكَ الْكُفْرُ مَرَّةً وَ الْإِسْلَامُ‏ أُخْرَى، فَمَا فَدَاكَ مِنْ وَاحِدَةٍ مِنْهُمَا مَالُكَ وَ لَا حَسَبُكَ وَ إِنَّ امْرَأً دَلَّ عَلَى قَوْمِهِ السَّيْفَ وَ سَاقَ إِلَيْهِمُ الْحَتْفَ لَحَرِيٌّ أَنْ يَمْقُتَهُ الْأَقْرَبُ وَ لَا يَأْمَنَهُ الْأَبْعَدُ.(قال السيد الشريف: يريد (علیه السلام) أنه أسِر في الكفر مرة و في الإسلام مرة. و أما قوله (علیه السلام) دل على قومه السيف فأراد به حديثا كان للأشعث مع خالد بن الوليد باليمامة غرّ فيه قومه و مكر بهم حتى أوقع بهم خالد و كان قومه بعد ذلك يسمّونه عرف النار و هو اسم للغادر عندهم‏).', NULL, '(امام در مسجد كوفه در سال 38 هجرى سخنرانى مى كرد، اشعث بن قيس به يكى از مطالب آن اعتراض كرد و گفت اين سخن به زيان شما، نه به سود تو است، امام نگاه خود را به او دوخت و فرمود):\r\n\r\nسوابق تاريخى نكوهيده أشعث بن قيس:\r\nچه كسى تو را آگاهاند كه چه چيزى به سود، يا زيان من است. لعنت خدا و لعنت لعنت كنندگان، بر تو باد اى متكبّر متكبّر زاده، منافق پسر كافر.\r\nسوگند به خدا، تو يك بار در زمان كفر و بار ديگر در حكومت اسلام، اسير شدى، و مال و خويشاوندى تو، هر دو بار نتوانست به فريادت برسد.\r\nآن كس كه خويشان خود را به دم شمشير سپارد، و مرگ و نابودى را به سوى آنها كشاند، سزاوار است كه بستگان او بر وى خشم گيرند و بيگانگان به او اطمينان نداشته باشند. \r\n(سید رضی مى گويد: منظور امام عليه السّلام اين است كه اشعث ابن قيس يك بار وقتى كه كافر بود اسير شد و بار ديگر آنگاه كه مسلمان شد و شمشيرها را به سوى قبيله اش راهنمايى كرد، مربوط به جريانى است كه اشعث قبيله خود را فريب داد تا خالد بن وليد، آنها را غافلگير كند و از دم شمشير بگذراند كه پس از آن خيانت او را با لقب «عرف النّار» (چيزى كه آتش را بپوشاند)، مى ناميدند و اين لقبى بود كه به نيرنگ باز مى دادند).', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (102, 1, 190, 'سفارش به ترس از خدا', 'خطبه ۱۹۰-سفارش به ترس از خدا \nضرورت شکرگزاری \nخدا را برابر نعمتهایش شکرگزارم، و بر انجام حقوق الهی از او یاوری می‌طلبم، پروردگاری که سپاهش نیرومند، و مرتبت او بزرگ است. و گواهی می‌دهم که محمد بنده و پیامبر اوست، انسانها را به اطاعت خدا دعوت کرد، و با دشمنان خدا در راه دین او پیکار کرد و مغلوبشان نمود، هرگز همداستانی دشمنان که او را دروغگو خواندند، او را از دعوت حق باز نداشت، و تلاش آنان برای خاموش کردن نور رسالت به نتیجه ای نرسید. سفارش به پرهیزگاری و یاد مرگ پس به تقوا و ترس از خدا، روی آورید، که رشته آن استوار، و دستگیره آن محکم، قله بلند آن پناهگاهی مطمئن می‌باشد، قبل از فرا رسیدن مرگ، خود را برای پیش آمدهای آن آماده سازید، پیش از آنکه مرگ شما را دریابد آنچه لازمه ملاقات است فراهم آورید، زیرا مرگ پایان زندگی است و هدف نهایی قیامت است، مرگ برای خردمندان پند و اندرز، و برای جاهلان وسیله عبرت آموزی است. پیش از فرا رسیدن مرگ، از تنگی قبرها، و شدت غم و اندوه، و ترس از قیامت، و درهم ریختن استخوانها، و کر شدن گوشها، و تاریکی لحد، و وحشت از آینده، و غم و اندوه فراوان در تنگنای گور، و پوشانده شدن آن با سنگ و خاک، چه می‌دانید؟ \nپس ای بندگان! خدا را! خدا را! پروا کنید، که دنیا با قانونمندی خاصی می‌گذرد، شما با قیامت به رشته ای اتصال دارید، گویا نشانه‌های قیامت، آشکار می‌شود، و شما را در راه خود متوقف نموده، با زلزله‌هایش سررسیده است، سنگینی بار آن را بر دوش شما نهاده، و رشته پیوند مردم با دنیا را قطع کرده، همه را از آغوش گرم دنیا خارج ساخته است. گویی دنیا یک روز بود و گذشت، یا ماهی بود و سپری شد، تازه‌های دنیا کهنه شده، و فربه‌هایش لاغر گردیدند، در جایگاهی تنگ، در میان مشکلاتی بزرگ، و آتشی پرشراره که صدای زبانه‌هایش وحشتزا، شعله‌هایش بلند، غرشش پرهیجان، پرنور و گدازنده، خاموشی شعله‌هایش غیرممکن، شعله‌هایش در فوران. تهدیدهایش هراس انگیز، ژرفایش ناپیدا، پیرامونش تاریک و سیاه، دیگهایش در جوشش، و اوضاعش سخت وحشتناک است. آینده پرهیزکاران و در آن میان (پرهیزکاران را گروه، گروه، به سوی بهشت رهنمون می‌شوند) آنان از کیفر و عذاب در امانند، و از سرزنش‌ها آسوده، و از آتش دورند، در خانه‌های امن الهی، از جایگاه خود خشنودند، آنان در دنیا رفتارشان پاک، دیدگانشان گریان، شبهایشان با خشوع و استغفار چونان روز، و روزشان از ترس گناه چونان \nشب می‌ماند، پس خداوند بهشت را منزلگه نهایی آنان قرار داد، و پاداش ایشان را نیکو پرداخت، که سزاوار آن نعمتها بودند، و لایق ملکی جاودانه و نعمتهایی پایدار شدند. ای بندگان خدا، مراقب چیزی باشید که رستگاران با پاس داشتن آن سعادتمند شدند، و تبهکاران با ضایع کردن آن به خسران و زیان رسیدند، پیش از آنکه مرگ شما فرا رسد با اعمال نیکو آماده باشید، زیرا در گرو کارهایی هستید که انجام داده اید، و پاداش داده می‌شوید به کارهایی که از پیش مرتکب شدید. ناگهان مرگ وحشتناک سر می‌رسد، که دیگر بازگشتی نیست، و از لغزشها نمی توان پوزش خواست، خداوند ما و شما را در راه خود و پیامبرش استوار سازد، و از گناهان ما و شما به فضل رحمتش درگذرد. \nآموزش نظامی برجای خود محکم بایستید، در برابر بلاها و مشکلات استقامت کنید، شمشیرها و دستها را در هوای زبانهای خویش به کار مگیرید، و آنچه که خداوند شتاب در آن را لازم ندانسته شتاب نکنید، زیرا هرکس از شما که در بستر خویش با شناخت خدا و پیامبر (ص) و اهل بیت پیامبر (ع) بمیرد، شهید از دنیا رفته و پاداش او بر خداست، و ثواب اعمال نیکویی که قصد انجام آن را داشته خواهد برد، و نیت او ثواب شمشیر کشیدن را دارد، همانا هر چیزی را وقت مشخص و سرآمدی معین است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (103, 1, 191, 'در حمد خدا و لزوم تقوا', 'خطبه ۱۹۱-در حمد خدا و لزوم تقوا \nشناخت پروردگار \nسپاس خداوندی را سزاست که سپاس او در خلق آشکار، و سپاهش پیروز و بزرگی او والا و بیکرانه است خدا را برای نعمتهای پی در پی و بخشش‌های بزرگش ستایش می‌کنم، خدایی که حلمش بزرگ و عفوکننده است، در فرمانش عادل، و از گذشته و آینده باخبر است، با علم خود جهان هستی را پدید آورده، و با فرمان خود موجودات را آفریده است، بی آنکه از کسی پیروی کند، و یا بیاموزد، و یا از طرح حکیم دیگری استفاده نماید، در آفرینش پدیده‌ها، دچار اشتباهی نشده، و نه با حضور و مشورت گروهی آفریده است. گواهی می‌دهم که محمد (ص) بنده و فرستاده اوست، به هنگامی او را مبعوث فرمود که مردم در گرداب جهالت فرو رفته، و در حیرت و سرگردانی به سر می‌بردند، هلاکت آنان را مهار کرده به سوی خود می‌کشید، و گمراهی بر جان و دلشان قفل زده بود. ره آورد پرهیزکاری ای بندگان خدا! شما را به پرهیزکاری سفارش می‌کنم، که حق خداوند بر شماست، و نیز موجب حق شما بر پروردگار است، از خدا برای پرهیزکاری یاری بطلبید، و برای انجام دستورات خدا از تقوا یاری جویید، زیرا تقوا، امروز سپر بلا، و فردا راه رسیدن به بهشت است، راه تقوا روشن، و رونده آن بهره مند، و امانتدا رش خدا، که حافظان آن خواهد بود، تقوا همواره خود را بر امت‌های گذشته و آینده عرضه می‌کند، زیرا فردای قیامت، همه به آن نیازمندند. آنگاه که در قیامت آفریده‌ها را گرد می‌آورد، و درباره همه نعمت‌ها می‌پرسد، پس چه اندکند آنان که تقوا را برگزیدند، و بار آن را بدرستی بر دوش کشیدند، آری پرهیزکاران تعدادشان اندک است، و شایسته ستایش خداوند سبحان که فرمود: (بندگان سپاس گزار من اندکند.) پس گوش جان را به ندای تقوا بسپارید، و برای به دست آوردن آن تلاش کنید، تقوا را بجای آن چه از دست رفته به دست آوردید و عوض هر کار مخالفی که مرتکب شده اید انتخاب کنید، با تقوا خواب خود را به بیداری، و روزتان را با آن سپری کنید، دلهای خود را با تقوا زنده کنید، و گناهان خود را با آن شستشو دهید، بیماریهای روان و جان خود را با تقوا درمان، و خود را آماده سفر آخرت گردانید، از تباه کنندگان تقوا عبرت گیرید و خود عبرت پرهیزکاران نشوید. آگاه باشید! تقوا را حفظ کنید و خویشتن را با تقوا حفظ نمایید. پرهیز از دنیای حرام برابر دنیا خویشتن دار و برابر آخرت دلباخته باشید، آن کس را که تقوا بلندمرتبت نمود خوار نشمارید، و آن را که دنیا عزیزش کرد گرامی ندارید، برق درخشنده دنیا شما را خیره نکند، و سخن ستاینده دنیا را نشنوید، به دعوت کننده دنیا پاسخ ندهید، و از تابش دنیا روشنایی نخواهید، و فریفته کالاهای گرانقدر دنیا نگردید. همانا برق دنیای حرام بی فروغ است، و سخنش دروغ، و اموالش به غارت رفته، و کالاهای آن در شرف تاراج است. آگاه باشید! دنیای حرام چونان عشوه گر هرزه ای است که تسلیم نشود، و مرکب سرکشی است که فرمان نبرد، دروغگویی خیانتکار، ناسپاس حق نشناس، دشمنی حیله گر، پشت کننده ای سرگردان، حالاتش متزلزل، عزتش خواری، جدش بازی و شوخی، و بلندی آن سقوط است، خانه جنگ و غارتگری، تبهکاری و هلاکت، و سرمنزل ناآرامی است، جایگاه دیدار کردنها و جداییهاست، راه‌های آن حیرت زا، گریزگاه‌هایش ناپیدا، و خواستهایش نومیدکننده و زیانبار است، پناهگاه‌های دنیا انسان را تسلیم مرگ می‌کند، و از خانه‌های خود بیرون می‌راند، و چاره اندیشی‌ها ناتوان کننده است. اقسام دنیاپرستان نجات یافته ای مجروح، یا مجروحی پاره پاره تن، دسته ای سر از تن جدا، و دسته ای دیگر در خون خود تپیده، گروهی انگشت به دندان، و جمعی از حسرت و اندوه دست بر دست می‌مالند، برخی سر بر روی دستها نهاده به فکر فرو رفته اند، عده ای بر اشتباهات گذشته افسوس می‌خورند و خویشتن را محکوم می‌کنند، و عده ای دیگر از عزم و تصمیم‌ها دست برداشته، که راه فرار و هر نوع حیله گری بسته شده، و دنیا آن‌ها را غافلگیر کرده است، زیرا کار از کار گذشته، و عمر گرانبها هدر رفته است. هیهات! هیهات! آنچه از دست رفت گذشت، و آنچه سپری شد رفت، و جهان چنانکه می‌خواست به پایان رسید. (نه آسمان بر آن‌ها گریست و نه زمین، و هرگز دیگر به آن‌ها مهلتی داده نشد.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (104, 1, 192, 'خطبه قاصعه', 'خطبه ۱۹۲-خطبه قاصعه \n(این خطبه را «قاصعه» یعنی تحقیر کننده نامیدند که در آن ارزشهای جاهلی را کوچک و خوار شمرد و از طولانی‌ترین سخنرانی‌های امام است که در سال ۴۰ هجری در حالیکه سوار بر اسب بود ایراد فرمود) \n۱- والایی پروردگار \nستایش خداوندی را سزاست که لباس عزت و بزرگی پوشید، و آن دو را برای خود انتخاب، و از دیگر پدیده‌ها باز داشت. آن دو را مرز میان خود و دیگران قرار داد، و آن دو را برای بزرگی و عظمت خویش برگزید، و لعنت کرد آن کس را که در آرزوی عزت و بزرگی با خدا به ستیزه برخیزد، از این رو فرشتگان مقرب خود را آزمود، و فروتنان را از گردنکشان جدا فرمود. با آن که از آنچه در دل‌هاست، و از اسرار نهان آگاه است، به فرشتگان فرمود. «من بشری را از گل و خاک می‌آفرینم، آنگاه که آفرینش او به اتمام رسید، و روح در او دمیدم، برای او سجده کنید، فرشتگان همه سجده کردند مگر ابلیس» که حسادت او را فرا گرفت. \n۲- نکوهش تکبر و خودپسندی شیطان \nشیطان بر آدم علیه السلام به جهت خلقت او از خاک، فخر فروخت، و با تکیه به اصل خود که از آتش است دچار تعصب و غرور شد. پس شیطان دشمن خدا و پیشوای متعصب‌ها و سر سلسله متکبران است، که اساس عصبیت را بنا نهاد، و بر لباس کبریایی و عظمت با خدا در افتاد، لباس بزرگی را بر تن پوشید و پوشش تواضع و فروتنی را از تن در آورد. آیا نمی‌نگرید که خدا به خاطر خود بزرگ بینی، او را کوچک ساخت و به جهت بلند پروازی او را پست و خوار گردانید پس او را در دنیا طرد شده، و آتش جهنم را در قیامت برای او مهیا فرمود \n۳- آزمایش‌ها درمان تکبر \nخداوند اگر اراده می‌کرد، آدم علیه السلام را از نوری که چشم‌ها را خیره کند، و زیباییش عقلها را مبهوت سازد، و عطر و پاکیزگی‌اش حس بویایی را تسخیر کند می‌آفرید، که اگر چنین می‌کرد، گردن‌ها در برابر آدم فروتنی می‌کردند، و آزمایش فرشتگان برای سجده آدم علیه السلام آسان بود، اما خداوند مخلوقات خود را با اموری که آگاهی ندارند آزمایش می‌کند، تا بد و خوب تمیز داده شود، و تکبر و خودپسندی را از آن‌ها بزداید، و خود بزرگ بینی را از آنان دور کند. پس، از آنچه خداوند نسبت به ابلیس انجام داد عبرت گیرید، زیرا اعمال فراوان و کوشش‌های مداوم او را با تکبر از بین برد. او شش هزار سال عبادت کرد که مشخص می‌باشد از سال‌های دنیا یا آخرت است، اما با ساعتی تکبر همه را نابود کرد، چگونه ممکن است پس از ابلیس، فرد دیگری همان اشتباه را تکرار کند و سالم بماند نه، هرگز خداوند هیچ گاه انسانی را برای عملی وارد بهشت نمی‌کند که برای همان عمل فرشته‌ای را محروم سازد. فرمان خدا در آسمان و زمین یکسان است «»، و بین خدا و خلق، دوستی خاصی وجود ندارد که به خاطر آن، حرامی را که بر جهانیان ابلاغ فرموده حلال بدارد. \n۴- هشدار از دشمنی‌های شیطان \nای بندگان خدا از دشمن خدا پرهیز کنید، مبادا شما را به بیماری خود مبتلا سازد، و با ندای خود شما را به حرکت در آورد، و با لشکرهای پیاده و سواره خود بر شما بتازد به جانم سوگند، شیطان تیر خطرناکی برای شکار شما بر چله کمان گذارده، و تا حد توان کشیده، و از نزدیک‌ترین مکان شما را هدف قرار داده است، و خطاب به خدا گفته: «پروردگارا به سبب آن که مرا دور کردی، دنیا را در چشمهایشان جلوه می‌دهم، و همه را گمراه خواهم کرد» اما تیری در تاریکی‌ها، و سنگی بدون نشانه روی رها ساخت، گرچه فرزندان خودپسندی، و برادران تعصب و خود خواهی، و سواران مرکب جهالت و خود پرستی، او را تصدیق کردند. افراد سرکش شما تسلیم شیطان شدند، و طمع ورزی او را در شما کارگر افتاد، و این حقیقت بر همه آشکار گردید، و حکومت شیطان بر شما استوار شد، و با لشکر خویش به شما یورش برد، و شما را به ذلت سقوط کشانید، و شما را به مرز کشتار و خونریزی کشاند، و شما را با فروکردن نیزه در چشم‌ها، بریدن گلوها، کوبیدن مغزها پایمال کرد، تا شما را به سوی آتشی بکشاند که از پیش مهیا گردید. پس شیطان بزرگ‌ترین مانع برای دینداری، و زیانبارترین و آتش افروزترین فرد برای دنیای شماست شیطان از کسانی که دشمن سر سخت شما هستند و برای در هم شکستنشان کمر بسته‌اید خطرناک‌تر است. مردم آتش خشم خود را بر ضد شیطان به کار گیرید، و ارتباط خود را با او قطع کنید. به خدا سوگند، شیطان بر اصل و ریشه شما فخر فروخت، و بر حسب و نسب شما طعنه زد و عیب گرفت، و با سپاهیان سواره خود به شما هجوم آورد، و با لشکر پیاده راه شما را بست، که هر کجا شما را بیابند شکار می‌کنند، و دست و پای شما را قطع می‌کنند، نه می‌توانید با حیله و نقشه آن‌ها را بپراکنید، و نه با سوگندها قادرید از سر راهتان دور کنید. زیرا کمین‌گاه شیطان ذلت آور، تنگ و تاریک، مرگ آور، و جولانگاه بلا و سختی‌هاست، پس شراره‌های تعصب و کینه‌های جاهلی را در قلب خود خاموش سازید، که تکبر و خود پرستی در دل مسلمان از آفت‌های شیطان، غرورها، و کشش‌ها و وسوسه‌های اوست. تاج تواضع و فروتنی را بر سر نهید، و تکبر و خود پسندی را زیر پا بگذارید، و حلقه‌های زنجیر خود بزرگ بینی را از گردن باز کنید، و تواضع و فروتنی را سنگر میان خود و شیطان و لشکریانش قرار دهید، زیرا شیطان از هر گروهی لشکریان و یارانی سواره و پیاده دارد. و شما همانا قابیل «» نباشید که بر برادرش تکبر کرد، خدا او را برتری نداد، خویشتن را بزرگ می‌پنداشت، و حسادت او را به دشمنی واداشت، تعصب آتش کینه در دلش شعله‌ور کرد، و شیطان باد کبر و غرور در دماغش دمید، و سر انجام پشیمان شد، و خداوند گناه قاتلان را تا روز قیامت بر گردن او نهاد. \n۵- پرهیز از تکبر و اخلاق جاهلی \nآگاه باشید در سرکشی و ستم زیاده روی کردید، و در زمین در دشمنی با خداوند فساد به راه انداختید، و آشکارا با بندگان خدا به نبرد پرداختید. خدا را خدا را از تکبر و خودپسندی، و از تفاخر جاهلی بر حذر باشید، که جایگاه بغض و کینه و رشد وسوسه‌های شیطانی است، که ملتهای گذشته، و امتهای پیشین را فریب داده است، تا آنجا که در تاریکی‌های جهالت فرو رفتند، و در پرتگاه هلاکت سقوط کردند، و به آسانی به همان جایی که شیطان می‌خواست کشانده شدند. کبر و خود پسندی چیزی است که قلب‌های متکبران را همانند کرده تا قرن‌ها به تضاد و خونریزی گذراندند، و سینه‌ها از کینه‌ها تنگی گرفت. \n۶- پرهیز از سران متکبر و خود پسند \nآگاه باشید زنهار زنهار از پیروی و فرمانبرداری سران و بزرگانتان، آنان که به اصل و حسب خود می‌نازند، و خود را بالاتر از آنچه که هستند می‌پندارند، و کارهای نادرست را به خدا نسبت می‌دهند، و نعمت‌های گسترده خدا را انکار می‌کنند، تا با خواسته‌های پروردگاری مبارزه کنند، و نعمت‌های او را نادیده انگارند. آنان شالوده تعصب جاهلی، و ستون‌های فتنه، و شمشیرهای تفاخر جاهلیت هستند. پس، از خدا پروا کنید، و با نعمت‌های خدادادی درگیر نشوید، و به فضل و بخشش او حسادت نورزید، و از فرومایگان اطاعت نکنید، آنان که تیرگی‌شان را با صفای خود نوشیدید، و بیماری‌شان را با سلامت خود درهم آمیخته‌اید، و باطل آنان را با حق خویش مخلوط کرده‌اید، در حالی که آنان ریشه همه فسق‌ها و انحرافات و همراه انواع گناهانند. شیطان آن‌ها را برای گمراه کردن مردم، مرکب‌های رام قرار داد، و از آنان لشکری برای هجوم به مردم ساخت، و برای دزدیدن عقل‌های شما آنان را سخنگوی خود برگزید، که شما را هدف تیرهای خویش، و پایمال قدم‌های خود، و دستاویز وسوسه‌های خود گردانید. \n۷- ضرورت عبرت از گذشتگان \nمردم از آنچه که بر ملت‌های متکبر گذشته، از کیفرها و عقوبت‌ها و سختگیری‌ها، و ذلت و خواری فرود آمده عبرت گیرید، و از قبرها و خاکی که بر آنچه ره نهادند، و زمین‌هایی که با پهلوها بر آن افتادند پند پذیرید، و از آثار زشتی که کبر و غرور در دل‌ها می‌گذارد به خدا پناه ببرید، همانگونه که از حوادث سخت به او پناه می‌برید اگر خدا تکبرورزیدن را اجازه می‌فرمود، حتما به بندگان مخصوص خود از پیامبران و امامان علیهم السلام اجازه می‌داد، در صورتی که خدای سبحان تکبر و خودپسندی را نسبت به آنان ناپسند، و تواضع و فروتنی را برای آنان پسندید، که چهره بر زمین می‌گذارند و صورت‌ها بر خاک می‌مالند، و در برابر مؤمنان فروتنی می‌کنند، و خود از قشر مستضعف جامعه می‌باشند که خدا آن‌ها را با گرسنگی آزمود، و به سختی و بلا گرفتارشان کرد، و با ترس و بیم امتحانشان فرمود، و با مشکلات فراوان، خالصشان گردانید. پس مال و فرزند را دلیل خشنودی یا خشم خدا ندانید، که نشانه ناآگاهی به موارد آزمایش، و امتحان در بی‌نیازی و قدرت است، زیرا خداوند سبحان فرمود: «آیا گمان می‌کنند مال و فرزندانی که به آن‌ها عطا کردیم، به سرعت نیکی‌ها را برای آنان فراهم می‌کنیم نه آنان آگاهی ندارند» \n۸- فلسفه آزمایش‌ها \nپس همانا خداوند سبحان بندگان متکبر را با دوستان خود که در چشم آن‌ها ناتوانند می‌آزماید، وقتی که موسی بن عمران و برادرش‌هارون علیهم السلام بر فرعون وارد شدند، و جامه‌های پشمین بتن، و چوب دستی در دست داشتند، و با فرعون شرط کردند که اگر تسلیم پروردگار شود، حکومت و ملکش جاودانه بماند و عزتش برقرار باشد، فرعون گفت: «آیا از این دو نفر تعجب نمی‌کنید که دوام عزت و جاودانگی حکومتم را به خواسته‌های خود ربط می‌دهند در حالی که در فقر و بیچارگی به سر می‌برند اگر چنین است چرا دستبندهای طلا به همراه ندارند» این سخن را فرعون برای بزرگ شمردن طلا و تحقیر پوشش لباسی از پشم گفت، در حالی که اگر خدای سبحان اراده می‌فرمود، به هنگام بعثت پیامبران، درهای گنج‌ها، و معدن‌های جواهرات، و باغات سرسبز را به روی پیامبران می‌گشود، و پرندگان آسمان و حیوانات وحشی زمین را همراه آنان به حرکت در می‌آورد. اما اگر این کار را می‌کرد، آزمایش از میان می‌رفت، و پاداش و عذاب بی‌اثر می‌شد، و بشارت‌ها و هشدارهای الهی بی‌فایده می‌بود، و بر مؤمنان اجر و پاداش امتحان شدگان واجب نمی‌شد، و ایمان آورندگان ثواب نیکوکاران را نمی‌یافتند، و واژه‌ها، معانی خود را از دست می‌داد. در صورتی که خداوند پیامبران را با عزم و اراده قوی، گرچه با ظاهری ساده و فقیر مبعوث کرد، با قناعتی که دل‌ها و چشم‌ها را پر سازد، هر چند فقر و نداری ظاهری آنان چشم و گوش‌ها را خیره سازد. اگر پیامبران الهی، دارای چنان قدرتی بودند که مخالفت با آنان امکان نمی‌داشت، و توانایی و عزتی می‌داشتند که هرگز مغلوب نمی‌شدند، و سلطنت و حکومتی می‌داشتند که همه چشم‌ها به سوی آنان بود، از راه‌های دور بار سفر به سوی آنان می‌بستند، اعتبار و ارزششان در میان مردم اندک بود، و متکبران در برابرشان سر فرود می‌آوردند، و تظاهر به ایمان می‌کردند، از روی ترس یا علاقه‌ای که به مادیات داشتند. در آن صورت نیت‌های خالص یافت نمی‌شد، و اهداف غیر الهی در ایمانشان راه می‌یافت، و با انگیزه‌های گوناگون به سوی نیکی‌ها می‌شتافتند. اما خدای سبحان اراده فرمود که پیروی از پیامبران، و تصدیق کتب آسمانی، و فروتنی در عبادت، و تسلیم در برابر فرمان خدا، و اطاعت محض فرمانبرداری، با نیت خالص تنها برای خدا صورت پذیرد، و اهداف غیر خدایی در آن راه نیابد، که هر مقدار آزمایش و مشکلات بزرگتر باشد ثواب و پاداش نیز بزرگتر خواهد بود. \n۹- فلسفه حج \nآیا مشاهده نمی‌کنید که همانا خداوند سبحان، انسان‌های پیشین از آدم علیه السلام تا آیندگان این جهان را با سنگ‌هایی در مکه آزمایش کرد که نه زیان می‌رسانند، و نه نفعی دارند، نه می‌بینند، و نه می‌شنوند این سنگ‌ها را خانه محترم خود قرار داده و آن را عامل پایداری مردم گردانید. سپس کعبه را در سنگلاخ‌ترین مکان‌ها، بی‌گیاه‌ترین زمین‌ها، و کم فاصله‌ترین دره‌ها، در میان کوه‌های خشن، سنگریزه‌های فراوان، و چشمه‌های کم آب، و آبادی‌های از هم دور قرار داد، که نه شتر، نه اسب و گاو و گوسفند، هیچ کدام در آن سرزمین آسایش ندارند. سپس آدم علیه السلام و فرزندانش را فرمان داد که به سوی کعبه برگردند، و آن را مرکز اجتماع و سر منزل مقصود و بار اندازشان گردانند، تا مردم با عشق قلب‌ها، به سرعت از میان فلات و دشت‌های دور، و از درون شهرها، روستاها، دره‌های عمیق، و جزایر از هم پراکنده دریاها به مکه روی آورند، شانه‌های خود را بجنبانند، و گرداگرد کعبه لا اله الا الله بر زبان جاری سازند، و در اطراف خانه طواف کنند، و با موهای آشفته، و بدن‌های پر گرد و غبار در حرکت باشند. لباس‌های خود را که نشانه شخصیت هر فرد است در آورند، و با اصلاح نکردن موهای سر، قیافه خود را تغییر دهند، که آزمونی بزرگ، و امتحانی سخت، و آزمایشی آشکار است برای پاکسازی و خالص شدن، که خداوند آن را سبب رحمت و رسیدن به بهشت قرار داد. اگر خداوند خانه محترمش، و مکان‌های انجام مراسم حج را، در میان باغ‌ها و نهرها، و سرزمین‌های سبز و هموار، و پردرخت و میوه، مناطقی آباد و دارای خانه‌ها و کاخ‌های بسیار، و آبادی‌های به هم پیوسته، در میان گندمزارها و باغات خرم و پر از گل و گیاه، دارای مناظری زیبا و پر آب، در وسط باغستانی شادی آفرین، و جاده‌های آباد قرار می‌داد، به همان اندازه که آزمایش ساده بود، پاداش نیز سبک‌تر می‌شد. اگر پایه‌ها و بنیان کعبه، و سنگ‌هایی که در ساختمان آن به کار رفته از زمرد سبز، و یاقوت سرخ، و دارای نور و روشنایی بود، دل‌ها دیرتر به شک و تردید می‌رسیدند، و تلاش شیطان بر قلب‌ها کمتر اثر می‌گذاشت، و وسوسه‌های پنهانی او در مردم کارگر نبود. در صورتی که خداوند بندگان خود را با انواع سختی‌ها می‌آزماید، و با مشکلات زیاد به عبادت می‌خواند، و به اقسام گرفتاری‌ها مبتلا می‌سازد، تا کبر و خود پسندی را از دل‌هایشان خارج کند، و به جای آن فروتنی آورد، و درهای فضل و رحمتش را به روی‌شان بگشاید، و وسائل عفو و بخشش را به آسانی در اختیارشان گذارد. \n۱۰- پرهیز از ستمکاری \nپس، خدا را خدا را از تعجیل در عقوبت، و کیفر سرکشی و ستم بر حذر باشید، و از آینده دردناک ظلم، و سر انجام زشت تکبر و خود پسندی که کمین گاه ابلیس است، و جایگاه حیله و نیرنگ اوست، بترسید، حیله و نیرنگی که با دلهای انسان‌ها، چون زهر کشنده می‌آمیزد، و هرگز بی‌اثر نخواهد بود، و کسی از هلاکتش جان سالم نخواهد برد: نه دانشمند به خاطر دانشش، و نه فقیر به جهت لباس کهنه‌اش، در امان می‌باشد. \n۱۱- فلسفه عبادات اسلامی \nخداوند بندگانش را، با نماز و زکات و تلاش در روزه‌داری، حفظ کرده است، تا اعضا و جوارحشان آرام، و دیدگانشان خاشع، و جان و روانشان فروتن، و دل‌هایشان متواضع باشد، کبر و خودپسندی از آنان رخت بربندد، چرا که در سجده، بهترین جای صورت را به خاک مالیدن، فروتنی آورد، و گذاردن اعضاء پر ارزش بدن بر زمین، اظهار کوچکی کردن است. و روزه گرفتن، و چسبیدن شکم به پشت، عامل فروتنی است، و پرداخت زکات، برای مصرف شدن میوه‌جات زمین و غیر آن، در جهت نیازمندیهای فقرا و مستمندان است. به آثار عبادات بنگرید که چگونه شاخه‌های درخت تکبر را در هم می‌شکند و از روییدن کبر و خودپرستی جلوگیری می‌کند \n۱۲- تعصب ورزیدن زشت و زیبا \nمن در اعمال و رفتار جهانیان نظر دوختم، هیچ کس را نیافتم که بدون علت در باره چیزی تعصب ورزد، جز با دلیلی که با آن ناآگاهان را بفریبد، و یا برهانی آورد که در عقل سفیهان نفوذ کند، جز شما زیرا در باره چیزی تعصب می‌ورزید که نه علتی دارد و نه سببی، ولی شیطان به خاطر اصل خلقت خود بر آدم علیه السلام تعصب ورزید، و آفرینش او را مورد سرزنش قرار داد و گفت «مرا از آتش و تو را از گل ساخته‌اند»، و سرمایه‌داران فساد زده امت‌ها، برای داشتن نعمت‌های فراوان تعصب ورزیدند و گفتند: «ما صاحبان فرزندان و اموال فراوانیم و هرگز عذاب نخواهیم شد» «» پس اگر در تعصب ورزیدن ناچارید، برای اخلاق پسندیده، افعال نیکو، و کارهای خوب تعصب داشته باشید، همان افعال و کرداری که انسان‌های با شخصیت، و شجاعان خاندان عرب، و سران قبائل در آن‌ها از یکدیگر پیشی می‌گرفتند، یعنی اخلاق پسندیده، بردباری «» به هنگام خشم فراوان، و کردار و رفتار زیبا و درست، و خصلت‌های نیکو پس تعصب ورزید در حمایت کردن از پناهندگان، و همسایگان، وفاداری به عهد و پیمان، اطاعت کردن از نیکی‌ها، سرپیچی از تکبر و خود پسندی‌ها، تلاش در جود و بخشش، خود داری از ستمکاری، بزرگ شمردن خونریزی، انصاف داشتن با مردم، فروخوردن خشم، پرهیز از فساد در زمین، تا رستگار شوید. \n۱۳- علل پیروزی و شکست ملت‌ها \nاز کیفرهایی که بر اثر کردار بد و کارهای ناپسند بر امت‌های پیشین فرود آمد خود را حفظ کنید، و حالات گذشتگان را در خوبی‌ها و سختی‌ها به یاد آورید، و بترسید که همانند آن‌ها باشید پس آنگاه که در زندگی گذشتگان مطالعه و اندیشه می‌کنید، عهده دار چیزی باشید که عامل عزت آنان بود، و دشمنان را از سر راهشان برداشت، و سلامت و عافیت زندگی آنان را فراهم کرد، و نعمت‌های فراوان را در اختیارشان گذاشت، و کرامت و شخصیت به آنان بخشید، که از تفرقه و جدایی اجتناب کردند، و بر وحدت و همدلی همت گماشتند، و یکدیگر را به وحدت واداشته به آن سفارش کردند. و از کارهایی که پشت آن‌ها را شکست، و قدرت آن‌ها را در هم کوبید، چون کینه توزی با یکدیگر، پرکردن دلها از بخل و حسد، به یکدیگر پشت کردن و از هم بریدن، و دست از یاری هم کشیدن، بپرهیزید، و در احوالات مؤمنان پیشین اندیشه کنید، که چگونه در حال آزمایش‌ و امتحان به سر بردند، آیا بیش از همه مشکلات بر دوش آن‌ها نبود و آیا بیش از همه مردم در سختی و زحمت نبودند و آیا از همه مردم جهان بیشتر در تنگنا قرار نداشتند فرعون‌های زمان، آن‌ها را به بردگی کشاندند، و همواره بدترین شکنجه‌ها را بر آنان وارد کردند، و انواع تلخی‌ها را به کامشان ریختند، که این دوران ذلت و هلاکت و مغلوب بودن، تداوم یافت نه راهی وجود داشت که سرپیچی کنند، و نه چاره‌ای که از خود دفاع نمایند، تا آن که خداوند، تلاش و استقامت و بردباری در برابر ناملایمات آن‌ها را، در راه دوستی خود، و قدرت تحمل ناراحتی‌ها را برای ترس از خویش، مشاهده فرمود. آنان را از تنگناهای بلا و سختی‌ها نجات داد، و ذلت آنان را به عزت و بزرگواری، و ترس آن‌ها را به امنیت تبدیل فرمود، و آن‌ها را حاکم و زمامدار و پیشوای انسانها قرار داد، و آن قدر کرامت و بزرگی از طرف خدا به آن‌ها رسید که خیال آن را نیز در سر نمی‌پروراندند. پس اندیشه کنید که چگونه بودند آنگاه که: وحدت اجتماعی داشتند، خواسته‌های آنان یکی، قلب‌های آنان یکسان، و دست‌های آنان مدد کار یکدیگر، شمشیرها یاری کننده، نگاه‌ها به یک سو دوخته، و اراده‌ها واحد و همسو بود آیا در آن حال مالک و سرپرست سراسر زمین نبودند و رهبر و پیشوای همه دنیا نشدند پس به پایان کار آن‌ها نیز بنگرید در آن هنگام که به تفرقه و پراکندگی روی آوردند، و مهربانی و دوستی آنان از بین رفت، و سخن‌ها و دل‌هایشان گوناگون شد، از هم جدا شدند، به حزب‌ها و گروه‌ها پیوستند، خداوند لباس کرامت خود را از تنشان بیرون آورد، و نعمت‌های فراوان شیرین را از آن‌ها گرفت، و داستان آن‌ها در میان شما عبرت انگیز باقی ماند. از حالات زندگی فرزندان اسماعیل پیامبر، و فرزندان اسحاق پیامبر، فرزندان اسراییل «یعقوب» (که درود بر آنان باد) عبرت گیرید، راستی چقدر حالات ملتها با هم یکسان، و در صفات و رفتارشان با یکدیگر همانند است در احوالات آن‌ها روزگاری که از هم جدا و پراکنده بودند اندیشه کنید، زمانی که پادشاهان کسری و قیصر «» بر آنان حکومت می‌کردند «»، و آن‌ها را از سرزمین‌های آباد، از کناره‌های دجله و فرات، «» و از محیطهای سر سبز و خرم دور کردند، و به صحراهای کم گیاه، و بی‌آب و علف، محل وزش بادها، و سرزمین‌هایی که زندگی در آنجاها مشکل بود تبعید کردند، آنان را در مکان‌های نامناسب، مسکین و فقیر، همنشین شتران ساختند، «» خانه‌هاشان پست‌ترین خانه ملت‌ها، و سرزمین زندگیشان خشک‌ترین بیابان‌ها بود، نه دعوت حقی وجود داشت که به آن روی آورند و پناهنده شوند، و نه سایه محبتی وجود داشت که در عزت آن زندگی کنند. حالات آنان دگرگون، و قدرت آنان پراکنده، و جمعیت انبوهشان متفرق بود. در بلایی سخت، و در جهالتی فراگیر فرو رفته بودند، دختران را زنده به گور، و بت‌ها را پرستش می‌کردند، و قطع رابطه با خویشاوندان، و غارتگری‌های پیاپی در میانشان رواج یافته بود. \n۱۴- ره‌آورد بعثت پیامبر اسلام صلی الله علیه و آله و سلم \nحال به نعمت‌های بزرگ الهی که به هنگامه بعثت پیامبر اسلام صلی الله علیه و آله و سلم بر آنان فروریخت بنگرید، که چگونه اطاعت آنان را با دین خود پیوند داد. و با دعوتش آن‌ها را به وحدت رساند چگونه نعمت‌های الهی بالهای کرامت خود را بر آنان گستراند، و جویبارهای آسایش و رفاه بر ایشان روان ساخت و تمام برکات آیین حق، آن‌ها را در بر گرفت در میان نعمت‌ها غرق گشتند، و در خرمی زندگانی شادمان شدند، امور اجتماعی آنان در سایه قدرت حکومت اسلام استوار شد، و در پرتو عزتی پایدار آرام گرفتند، و به حکومتی پایدار رسیدند. آنگاه آنان حاکم و زمامدار جهان شدند، و سلاطین روی زمین گردیدند، و فرمانروای کسانی شدند که در گذشته حاکم بودند، و قوانین الهی را بر کسانی اجراء می‌کردند که مجریان احکام بودند، و در گذشته کسی قدرت در هم شکستن نیروی آنان را نداشت، و هیچ کس خیال مبارزه با آنان را در سر نمی‌پروراند. \n۱۵- علل نکوهش و سقوط کوفیان \nآگاه باشید که شما هم اکنون دست از رشته اطاعت کشیدید، و با زنده کردن ارزش‌های جاهلیت، دژ محکم الهی را در هم شکستید، در حالی که خداوند بر این امت اسلامی بر «وحدت و برادری» منت گذارده بود، که در سایه آن زندگی کنند، نعمتی بود که هیچ ارزشی نمی‌توان همانند آن تصور کرد، زیرا از هر ارزشی گران قدرتر، و از هر کرامتی والاتر بود. بدانید که پس از هجرت، دوباره چونان اعراب بادیه نشین شده‌اید، و پس از وحدت و برادری به احزاب گوناگون تبدیل گشته‌اید، از اسلام تنها نام آن، و از ایمان جز نشانی را نمی‌شناسید شعار می‌دهید: آتش آری، ننگ هرگز گویا می‌خواهید اسلام را واژگون، و پرده حرمتش را پاره کنید و پیمانی را که خدا برای حفظ حرمت مسلمین در زمین، و عامل امنیت و آرامش مردم قرار داد بشکنید همانا اگر شما به غیر اسلام پناه برید، کافران با شما نبرد خواهند کرد. آنگاه نه جبرئیل و نه می‌کائیل، نه مهاجر و نه انصار، وجود ندارند که شما را یاری دهند، و چاره‌ای جز نبرد با شمشیر ندارید تا خدا در میان شما حکم نماید. مردم از مثل‌های قرآن در باره کسانی که عذاب و کیفر شدند، و روزهای سخت آنان، و آسیب‌های شدیدی که دیدند آگاهید، پس وعده عذاب خدا را دور مپندارید، و به عذر اینکه آگاهی ندارید خود را گرفتار نسازید، و انتقام خدا را سبک، و خود را از کیفر الهی ایمن مپندارید، زیرا که خدای سبحان، مردم روزگاران گذشته را از رحمت خود دور نساخت مگر برای ترک امر به معروف، و نهی از منکر. پس خدا، بی‌خردان را برای نافرمانی، و خردمندان را برای ترک باز داشتن دیگران از گناه، لعنت کرد. آگاه باشید شما رشته پیوند با اسلام را قطع، و اجرای حدود الهی را تعطیل، و احکام اسلام را به فراموشی سپرده‌اید \n۱۶- قاطعیت امام در نبرد با منحرفان \nآگاه باشید خداوند مرا به جنگ با سرکشان تجاوز کار، پیمان شکنان و فساد کنندگان در زمین فرمان داد: با ناکثان پیمان شکن جنگیدم، و با قاسطین تجاوز کار جهاد کردم، و مارقین خارج شده از دین را خوار و زبون ساختم، و رهبر خوارج (شیطان ردهه) «» بانگ صاعقه‌ای قلبش را به تپش آورد و سینه‌اش را لرزاند و کارش را ساخت. حال تنها اندکی از سرکشان و ستمگران باقی ماندند، که اگر خداوند مرا باقی گذارد با حمله دیگری نابودشان خواهم کرد، و حکومت حق را در سراسر کشور اسلامی پایدار خواهم کرد، جز مناطق پراکنده و دور دست. \n۱۷- سوابق درخشان شجاعت و فضائل امام علیه السلام \nمن در خردسالی، بزرگان عرب را به خاک افکندم، و شجاعان دو قبیله معروف «ربیعه» و «مضر» را در هم شکستم شما موقعیت مرا نسبت به رسول خدا صلی الله علیه و آله و سلم در خویشاوندی نزدیک، در مقام و منزلت ویژه می‌دانید، پیامبر مرا در اتاق خویش می‌نشاند، در حالی که کودک بودم مرا در آغوش خود می‌گرفت، و در بستر مخصوص خود می‌خوابانید، بدنش را به بدن من می‌چسباند، و بوی پاکیزه خود را به من می‌بویاند، و گاهی غذایی را لقمه لقمه در دهانم می‌گذارد، هرگز دروغی در گفتار من، و اشتباهی در کردارم نیافت. از همان لحظه‌ای که پیامبر صلی الله علیه و آله و سلم را از شیر گرفتند، خداوند بزرگ‌ترین فرشته (جبرئیل) خود را مأمور تربیت پیامبر صلی الله علیه و آله و سلم کرد تا شب و روز، او را به راه‌های بزرگواری و راستی و اخلاق نیکو راهنمایی کند، و من همواره با پیامبر بودم چونان فرزند که همواره با مادر است، «» پیامبر صلی الله علیه و آله و سلم هر روز نشانه تازه‌ای از اخلاق نیکو را برایم آشکار می‌فرمود، و به من فرمان می‌داد که به او اقتداء نمایم. پیامبر صلی الله علیه و آله و سلم چند ماه از سال را در غار حراء «» می‌گذراند، تنها من او را مشاهده می‌کردم، و کسی جز من او را نمی‌دید، در آن روزها، در هیچ خانه اسلام راه نیافت جز خانه رسول خدا صلی الله علیه و آله و سلم که خدیجه هم در آن بود و من سومین آنان بودم. من نور وحی و رسالت را می‌دیدم، و بوی نبوت را می‌بوییدم من هنگامی که وحی بر پیامبر صلی الله علیه و آله و سلم فرود می‌آمد، ناله شیطان را شنیدم، گفتم ای رسول خدا، این ناله کیست گفت: شیطان است که از پرستش خویش مأیوس گردید و فرمود: «علی تو آنچه را من می‌شنوم، می‌شنوی، و آنچه را که من می‌بینم، می‌بینی، جز اینکه تو پیامبر نیستی، بلکه وزیر من بوده و به راه خیر می‌روی» \n۱۸- خیره سری و دشمنی سران قریش \nمن با پیامبر صلی الله علیه و آله و سلم بودم آنگاه که سران قریش نزد او آمدند و گفتند: «ای محمد صلی الله علیه و آله و سلم تو ادعای بزرگی کردی، که هیچیک از پدران و خاندانت نکردند، ما از تو معجزه‌ای می‌خواهیم، اگر پاسخ مثبت داده، انجام دهی، می‌دانیم که تو پیامبر و فرستاده خدایی، و اگر از انجام آن سرباز زنی، خواهیم دانست که ساحر و دروغگویی» پس پیامبر صلی الله علیه و آله و سلم فرمود «شما چه می‌خواهید» گفتند: «این درخت را بخوان تا از ریشه کنده شود و در پیش تو بایستد» پیامبر صلی الله علیه و آله و سلم فرمود: خداوند بر همه چیز تواناست. حال اگر خداوند این کار را بکند آیا ایمان می‌آورید و به حق شهادت می‌دهید گفتند: آری، پیامبر صلی الله علیه و آله و سلم فرمود: من بزودی نشانتان می‌دهم آنچه را که درخواست کردید، و همانا بهتر از هر کس می‌دانم که شما به خیر و نیکی باز نخواهید گشت، زیرا در میان شما کسی است که کشته شده و در چاه «بدر» دفن خواهد شد، «» و کسی است «» که جنگ احزاب را تدارک خواهد کرد. سپس به درخت اشاره کرد و فرمود: «ای درخت اگر به خدا و روز قیامت ایمان داری، و می‌دانی من پیامبر خدایم، از زمین با ریشه‌هایت در آی، و به فرمان خدا در پیش روی من قرار گیر» سوگند به پیامبری که خدا او را به حق مبعوث کرد، درخت با ریشه‌هایش از زمین کنده شده، و پیش آمد که با صدای شدید چونان به هم خوردن بال پرندگان، یا به هم خوردن شاخه‌های درختان، جلو آمد و در پیش روی پیامبر صلی الله علیه و آله و سلم ایستاد که برخی از شاخه‌های بلند خود را بر روی پیامبر صلی الله علیه و آله و سلم و بعضی دیگر را روی من انداخت و من در طرف راست پیامبر صلی الله علیه و آله و سلم ایستاده بودم، وقتی سران قریش این منظره را مشاهده کردند، با کبر و غرور گفتند: «به درخت فرمان ده، نصفش جلوتر آید، و نصف دیگر در جای خود بماند» پیامبر صلی الله علیه و آله و سلم فرمان داد. نیمی از درخت با وضعی شگفت‌آور و صدایی سخت به پیامبر صلی الله علیه و آله و سلم نزدیک شد گویا می‌خواست دور آن حضرت بپیچد، اما سران قریش از روی کفر و سرکشی گفتند: «فرمان ده این نصف باز گردد و به نیم دیگر ملحق شود، و به صورت اول در آید» پیامبر صلی الله علیه و آله و سلم دستور داد و چنان شد. من گفتم: لا اله الا الله، ای رسول خدا من نخستین کسی هستم که به تو ایمان آوردم، و نخستین فردی هستم اقرار می‌کنم که درخت با فرمان خدا برای تصدیق نبوت، و بزرگداشت دعوت رسالت، آنچه را خواستی انجام داد. اما سران قریش همگی گفتند: «او ساحری است دروغگو، که سحری شگفت آور دارد، و سخت با مهارت است». و خطاب به پیامبر صلی الله علیه و آله و سلم گفتند: «آیا نبوت تو را کسی جز امثال علی علیه السلام باور می‌کند» \n۱۹- الگوهای کامل ایمان \nو همانا من از کسانی هستم که در راه خدا از هیچ سرزنشی نمی‌ترسند، کسانی که سیمای آن‌ها سیمای صدیقان، و سخنانشان، سخنان نیکان است، شب زنده داران و روشنی بخشان روزند، به دامن قرآن پناه برده سنت‌های خدا و رسولش را زنده می‌کنند، نه تکبر و خود پسندی دارند، و نه بر کسی برتری می‌جویند، نه خیانتکارند و نه در زمین فساد می‌کنند، قلب‌هایشان در بهشت، و پیکرهایشان سرگرم اعمال پسندیده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (105, 1, 193, 'به همام درباره پرهیزکاران', 'خطبه ۱۹۳-به همام درباره پرهیزکاران \n(یکی از یاران پرهیزکار امام (ع) به نام همام گفت: ای امیرمومنان پرهیزکاران را برای من آن چنان توصیف کن گویا آنان را با چشم می‌نگرم. امام (ع) در پاسخ او درنگی کرد و فرمود: (ای همام! از خدا بترس و نیکوکار باش که خداوند با پرهیزکاران و نیکوکاران است) اما همام دست بردار نبود و اصرار ورزید، تا آنکه امام (ع) تصمیم گرفت صفات پرهیزکاران را بیان فرماید. پس خدا را سپاس و ثنا گفت، و بر پیامبرش درود فرستاد، و فرمود) \nسیمای پرهیزکاران \n(پس از ستایش پروردگار!) \nهمانا خداوند سبحان پدیده‌ها را در حالی آفرید که از اطاعتشان بی نیاز، و از نافرمانی آنان در امان بود، زیرا نه معصیت گناهکاران به خدا زیانی می‌رسانند و نه اطاعت مومنان برای او سودی دارد، روزی بندگان را تقسیم، و هر کدام را در جایگاه خویش قرار داد، اما پرهیزکاران! در دنیا دارای فضیلت‌های برترند، سخنانشان راست، پوشش آنان میانه روی، و راه رفتنشان با تواضع و فروتنی است، چشمان خود را بر آنچه خدا حرام کرده می‌پوشانند، و گوشهای خود را وقف دانش سودمند کرده اند، و در روزگار سختی و گشایش حالشان یکسان است، و اگر نبود مرگی که خدا بر آنان مقدر فرمود، روح آنان حتی به اندازه \nبرهم زدن چشم، در بدنها قرار نمی گرفت، از شوق دیدار بهشت، و از ترس عذاب جهنم، خدا در جانشان بزرگ و دیگران کوچک مقدارند. بهشت برای آنان چنان است که گویی آن را دیده و در نعمتهای آن بسر می‌برند، و جهنم را چنان باور دارند که گویی آن را دیده و در عذابش گرفتارند دلهای پرهیزکاران اندوهگین، و مردم از آزارشان در امان، تنهایشان فربه نبوده، و درخواستهایشان اندک، و عفیف و پاکدامنند. در روزگار کوتاه دنیا صبر کرده تا آسایش جاودانه قیامت را به دست آورند، تجارتی پرسود که پروردگارشان فراهم فرمود، دنیا می‌خواست آن‌ها را بفریبد اما عزم دنیا نکردند، می‌خواست آن‌ها را اسیر خود گرداند که با فدا کردن جان، خود را آزاد ساختند. شب پرهیزکاران پرهیزکاران در شب بر پا ایستاده مشغول نمازند، قرآن را جزء جزء و با تفکر و اندیشه می‌خوانند، با قرآن جان خود را محزون و داروی درد خود را می‌یابند، وقتی به آیه ای برسند که تشویقی در آن است، با شوق و طمع بهشت به آن روی آورند، و با جان پرشوق در آن خیره شوند، و گمان می‌برند که نعمتهای بهشت در برابر دیدگانشان قرار داد و هرگاه به آیه ای می‌رسند که ترس از خدا در آن باشد، گوش دل به آن می‌سپارند، و گویا صدای \nبرهم خوردن شعله‌های آتش، در گوششان طنین افکن است، پس قامت به شکل رکوع خم کرده، پیشانی و دست و پا بر خاک مالیده، و از خدا آزادی خود از آتش جهنم را می‌طلبند. روز پرهیزکاران پرهیزکاران در روز، دانشمندانی بردبار، و نیکوکارانی باتقوا هستند که ترس الهی آنان را چونان تیر تراشیده لاغر کرده است، کسی که به آن‌ها می‌نگرد می‌پندارد که بیمارند اما آنان را بیماری نیست، و می‌گوید، مردم در اشتباهند! در صورتیکه آشفتگی ظاهرشان از امری بزرگ است. از اعمال اندک خود خشنود نیستند، و اعمال زیاد خود را بسیار نمی شمارند، نفس خود را متهم می‌کنند، و از کردار خود ترسناکند، هرگاه یکی از آنان را بستایند، از آنچه در تعریف او گفته شد در هراس افتاده می‌گوید: (من خود را از دیگران بهتر می‌شناسم و خدای من، مرا بهتر از من می‌شناسد، بار خدایا، مرا بر آنچه می‌گویند محاکمه نفرما، و بهتر از آن قرارم ده که می‌گویند، و گناهانی که نمی دانند بیامرز.) \nنشانه‌های پرهیزکاران و از نشانه‌های یکی از پرهیزکاران این است که او را اینگونه می‌بینی، در دینداری نیرومند، نرمخو و دوراندیش، دارای ایمانی پر از یقین، حریص در کسب دانش، و با داشتن علم بردبار، و در توانگری میانه رو، در عبادت فروتن، و آراسته در تهیدستی، در سختی‌ها بردبار، در جستجوی کسب حلال، در راه هدایت شادمان، پرهیزکننده از طمع ورزی، می‌باشد، اعمال نیکو انجام می‌دهد و ترسان است، روز را به شب می‌رساند با سپاسگزاری، و شب را به روز می‌آورد با یاد خدا، شب می‌خوابد اما ترسان، و برمی خیزد شادمان، ترس برای اینکه دچار غفلت نشود، و شادمانی برای فضل و رحمتی که به او رسیده است. اگر نفس او در آنچه دشوار است فرمان نبرد، از آنچه دوست دارد محرومش می‌کند، روشنی چشم پرهیزکار در چیزی قرار دارد که جاودانه است، و آنچه را ترک می‌کند که پایدار نیست، بردباری را با علم، و سخن را با عمل، درمی آمیزد پرهیزگار را می‌بینی که: آرزویش نزدیک، لغزشهایش اندک، قلبش فروتن، نفسش قانع، خوراکش کم، کارش آسان، دینش حفظشده، شهوتش مرده، خشمش فرو خورده است. مردم به خیرش امیدوار، و از آزارش در امانند، اگر در بیخبران باشد نامش در گروه یادآو \nران خدا ثبت می‌گردد، و اگر در یادآوران باشد نامش در گروه بیخبران نوشته نمی شود، ستمکار خود را عفو می‌کند، به آنکه محرومش ساخته می‌بخشد، آن کس که با او بریده می‌پیوندد، از سخن زشت دور، و گفتارش نرم، بدیهای او پنهان و کار نیکش آشکار است. نیکی‌های او به همه رسیده، و آزار او به کسی نمی رسد. در سختی‌ها آرام، و در ناگواریها بردبار و در خوشیها سپاسگزار است، به آنکه دشمن دارد ستم نکند، و نسبت به آنکه دوست دارد به گناه آلوده نشود، پیش از آن که بر ضد او گواهی دهند به حق اعتراف می‌کند، و آنچه را به او سپرده‌اند ضایع نمی سازد، و آنچه را به او تذکر دادند فراموش نمی کند. مردم را با لقبهای زشت نمی خواند، همسایگان را آزار نمی رساند، در مصیبتهای دیگران شاد نمی شود، و در کار ناروا دخالت نمی کند، و از محدوده حق خارج نمی شود، اگر خاموش است سکوت او اندوهگینش نمی کند، و اگر بخندد آواز خنده او بلند نمی شود، و اگر به او ستمی روا دارند صبر می‌کند تا خدا انتقام او را بگیرد. نفس او از دستش در زحمت، ولی مردم در آسایشند، برای قیامت خود را به زحمت می‌افکند، ولی مردم را به رفاه و آسایش می‌رساند، دوری او از برخی مردم، از روی زهد و پارسایی، \nو نزدیک شدنش با بعضی دیگر از روی مهربانی و نرمی است، دوری او از تکبر و خودپسندی، و نزدیکی او از روی حیله و نیرنگ نیست، (سخن امام که به اینجا رسید، ناگهان همام ناله ای زد و جان داد. امام (ع) فرمود:) سوگند بخدا من از این پیش آمد بر همام می‌ترسیدم، سپس گفت: آیا پندهای رسا با آنان که پذیرنده آنند چنین می‌کند؟ شخصی رسید و گفت: چرا با تو چنین نکرد؟ امام (ع) پاسخ داد: وای بر تو، هر اجلی وقت معینی دارد که از آن پیش نیافتد و سبب مشخصی دارد که از آن تجاوز نکند، آرام باش و دیگر چنین سخنانی مگو، که شیطان آن را بر زبانت رانده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (106, 1, 194, 'در وصف منافقان', 'خطبه ۱۹۴-در وصف منافقان \nخدا را بر توفیقی که به اطاعتش داده، و ما را از نافرمانی بازداشته، ستایش می‌کنیم، و از او می‌خواهیم که نعمتش را کامل، و دست ما را به ریسمان محکمش متصل گرداند. مشکلات رسالت و شهادت می‌دهیم که محمد (ص) بنده و فرستاده اوست که در راه رضایت حق در کام هرگونه سختی و ناراحتی فرو رفت، و جام مشکلات و ناگواریها را سرکشید، روزگاری خویشاوندان او به دورویی و دشمنی پرداختند، و بیگانگان در کینه توزی و دشمنی با او متحد شدند، اعراب برای نبرد با پیامبر عنان گسیخته، و با تازیانه بر مرکبها نواخته و از هر سو گرد می‌آمدند، و از دورترین سرزمین، و فراموش شده ترین نقطه‌ها، دشمنی خود را بر پیامبر (ص) فرود آوردند. \nسیمای منافقان ای بندگان خدا! شما را به ترس از خدا سفارش می‌کنم، و شما را از منافقان می‌ترسانم، زیرا آن‌ها گمراه و گمراه کننده اند، خطاکار و به خطاکاری تشویق می‌کنند، به رنگهای گوناگون ظاهر می‌شوند از ترفندهای گوناگون استفاده می‌کنند، برای شکستن شما از هر پناهگاهی سود می‌برند، و در هر کمینگاهی به شکار شما می‌نشینند، قلبهایشان بیمار، و ظاهرشان آراسته است، در پنهانی راه می‌روند، و از بیراهه‌ها حرکت می‌کنند، وصفشان دارو، و گفتارشان درمان اما کردارشان دردی است بی درمان، بر رفاه و آسایش مردم حسد می‌ورزند، و بر بلا و گرفتاری مردم می‌افزایند، و امیدواران را ناامید می‌کنند، آن‌ها در هر راهی کشته ای، و در هر دلی راهی، و بر هر اندوهی اشکها می‌ریزند، مدح و ستایش را به یکدیگر قرض می‌دهند، و انتظار پاداش می‌کشند، اگر چیزی را بخواهند اصرار می‌کننند و اگر ملامت شوند، پرده دری می‌کنند، و اگر داوری کنند اسراف می‌ورزند. آن‌ها برابر هر حقی باطلی، و برابر هر دلیلی شبهه ای، و برای هر زنده ای قاتلی، و برای هر دری کلیدی، و برای هر شبی چراغی تهیه کرده اند، با اظهار یاس می‌خواهند به مطامع خویش برسند، و بازار خود را گرم ساز \nند، و کالای خود را بفروشند، سخن می‌گویند اما به اشتباه و تردید می‌اندازند، توصیف می‌کنند اما فریب می‌دهند، در آغاز، راه را آسان و سپس در تنگناها به بن بست می‌کشانند، آن‌ها یاوران شیطان و زبانه‌های آتش جهنم می‌باشند. (آنان پیروان شیطانند، و بدانید که پیروان شیطان زیانکارانند.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (107, 1, 195, 'در ستایش خدا و پیامبر و پند و اندرز', 'خطبه ۱۹۵-در ستایش خدا و پیامبر \nنشانه‌های آشکار الهی \nستایش خداوندی را سزاست که نشانه‌های قدرت و بزرگی و عظمت خود را چنان آشکار کرد که دیده‌ها را از شگفتی قدرتش به حیرت آورده، و اندیشه‌های بلند را از شناخت ماهیت صفاتش باز داشته است، و گواهی می‌دهم که جز خدای یکتا خدایی نیست، شهادتی برخواسته از ایمان و یقین و اخلاص و اقرار درست. و گواهی می‌دهم که محمد (ص) بنده و فرستاده اوست، خدا پیامبرش را به هنگامی فرستاد که نشانه‌های هدایت از یاد رفته، و راه‌های دین ویران شده بود، او حق را آشکار، و مردم را نصیحت فرمود، همه را به رستگاری هدایت، و به میانه روی فرمان داد (درود خدا بر او و خاندانش باد.) \nخداشناسی و بدانید ای بندگان خدا! که خداوند شما را بیهوده نیافرید، و بی سرپرست رها نکرد، از میزان نعمتش بر شما آگاه، و اندازه نیکیهای خود بر شما را می‌داند، از خدا درخواست پیروزی و رستگاری کنید، از او بخواهید و عطای او را درخواست کنید، که میان او و شما پرده و مانعی نیست، و دری بروی شما بسته نمی گردد. خدا در همه جا، و در هر لحظه، و هر زمان، با انسان و پریان است، عطای فراوان از دارایی او نمی کاهد، و بخشیدن در گنج او کاستی نیاورد، و درخواست کنندگان، سرمایه او را به پایان نرسانند، و عطاشدگان، سرمایه او را پایان نمی دهند و کسی مانع احسان به دیگری نخواهد بود، و آوازی او را از آواز دیگر باز ندارد، و بخشش او مانع گرفتن نعمت دیگری نیست، و خشم گرفتن او مانع رحمت نمی باشد، و رحمتش او را از عذاب غافل نمی سازد، پنهان بودنش مانع آشکار بودنش نیست، و آشکار شدنش او را از پنهان ماندن باز نمی دارد. نزدیک و دور است، بلندمرتبه و نزدیک است، آشکار پنهان، و پنهان آشکار است، جزادهنده همگان است و خود جزا داده نمی شود، پدیده‌ها را با فکر و اندیشه نیافریده، و از آنان برای خستگی و زحمات کمکی نخواسته است. \nیاد آخرت ای بندگان خدا! شما را به پرهیزکاری سفارش می‌کنم، که مهار شما در عبادت، مایه استواری شماست، پس به رشته‌های تقوا چنگ آویزید، و به حقیقت‌های آن پناه آورید، تا شما را به سرمنزل آرامش، و جایگاه‌های وسیع، و پناهگاه‌های محکم و منزلگاه‌های پرعزت برساند، در روزی که چشمها خیره می‌شود، و همه جا در نظر انسان تاریک و گله‌های شتر و مال و اموال فراوان فراموش می‌گردد. زیرا در صور اسرافیل می‌دمند، که قلبها از کار می‌افتد، زبانها باز می‌ایستد، کوه‌های بلند و سنگهای محکم فرو می‌ریزد، و قسمتهای سخت آن نرم چون سرابی می‌ماند، کوهستانها با زمین هموار می‌گردد چنان که نه پستی و نه بلندی موجود است، پس در آن هنگام نه شفاعت کننده ای است که شفاعت کند و نه دوستی که نفع رساند و نه پوزش خواستن سودی دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (108, 1, 196, 'در بعثت پیامبر و تحقیر دنیا', 'خطبه ۱۹۶-بعثت پیامبر و تحقیر دنیا \nهشدار از غفلت زدگی خداوند هنگامی پیامبر (ص) را مبعوث فرمود که نه نشانه ای از دین الهی برپا، و نه چراغ هدایتی روشن، و نه راه حقی آشکار بود. ای بندگان خدا! شما را به ترس از خدا سفارش می‌کنم، و از دنیاپرستی شما را می‌ترسانم، زیرا دنیا خانه ای ناپایدار و جایگاه سختی و مشکلات است. ساکنان دنیا در حال کوچ کردن، و اقامت گزیدگانش به جدایی محکومند، مردم را چنان می‌تکاند که باد سخت کشتی را در دل دریاها می‌لرزاند، برخی از آنان در دل آب مرده، و برخی دیگر بر روی امواج جان سالم بدر برده، و بادها با وزیدن آن‌ها را به این سو و آن سو می‌کشاند، و هر جا که خواهد می‌برد، پس آن را که در آب می‌میرد نمی توان گرفت، و آن را که رهاشده، به سوی مرگ می‌رود. ای بندگان خدا، هم اکنون عمل کنید، که زبانها آزاد، و بدنها سالم، و اعضا و جوارح آماده اند، و راه بازگشت فراهم، و فرصت زیاد است، پیش از آنکه وقت از دست برود، و مرگ فرا رسد، پس فرود آمدن مرگ را حتمی بشمارید، و در انتظار آمدنش بسر نبرید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (109, 1, 197, 'در ذکر فضائل خویش', 'خطبه ۱۹۷-در ذکر فضائل خویش \nفضائل امیرالمومنین \nاصحاب و یاران حضرت محمد (ص) که حافظان اسرار او می‌باشند، می‌دانند که من حتی برای یک لحظه هم مخالف فرمان خدا و رسول اوجنبودم. بلکه با جان خود پیامبر (ص) را یاری کردم، در جاهائی که شجاعان قدمهایشان می‌لرزید، و فرار می‌کردند، آن دلیری و مردانگی را خدا به من عطا فرمود. در سوگ پیامبر رسول خدا (ص) در حالی که سرش بر روی سینه ام بود قبض روح گردید، و جان او در کف من روان شد، آن را بر چهره خویش کشیدم، متصدی غسل پیامبر (ص) من بودم، و فرشتگان مرا یاری می‌کردند، گویا در و دیوار خانه فریاد می‌زد. گروهی از فرشتگان فرود می‌آمدند و گروهی دیگر به آسمان پرواز می‌کردند، گوش من از صدای آهسته آنان که بر آنحضرت نماز می‌خواندند، پر بود، تا آنگاه که او را در حجره اش دفن کردیم. چه کسی با آنحضرت در زندگی و لحظات مرگ از من سزاوارتر است؟ پس مردم با دل بینا حرکت کنید، و نیت خویش را در جهاد با دشمن راست بدارید، سوگند بخدائی که جز او خدائی نیست، من بر جاده حق می‌روم، و دشمنان من بر پرتگاه باطلند، می‌گویم آنچه را می‌شنوید، و برای خود و شما از خدا طلب آمرزش دارم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (110, 1, 198, 'در سفارش به تقوا و وصف اسلام و پیامبر', 'خطبه ۱۹۸-سفارش به تقوا \nعلم الهی \nخدا می‌داند نعره حیوانات وحشی را در کوهها و بیابانها، و گناه و معصیت بندگان را در خلوتگاهها، و آمد و رفت ماهیان را در دریاهای ژرف، و به هم خوردن آبها را بر اثر وزش بادهای سخت. و گواهی می‌دهم که حضرت محمد (ص) برگزیده خدا، سفیر وحی، و رسول رحمت اوست. \nارزش پرهیزگاری پس از ستایش پروردگار، همانا من شما را به ترس از خدا سفارش می‌کنم، خدایی که آفرینش شما را آغاز کرد، و به سوی او باز می‌گردید، خدایی که خواسته‌های شما را برآورد، و رغبت و آرزوی شماست، راه راست شما به او پایان می‌پذیرد، و به هنگام ترس و وحشت، او پناهگاه شماست. همانا تقوا و ترس از خدا، داروی بیماریهای دل‌ها، روشنایی قلبها، و درمان دردهای بدنها، مرهم زخم جانها، پاک کننده پلیدیهای ارواح، و روشنایی بخش تاریکی چشمها، و امنیت در ناآرامیها، و روشن کننده تاریکیهای شماست. پس اطاعت خدا را پوشش جان قرار دهید، نه پوشش ظاهری، و با جان فرمانبردار باشید نه با تن، تا با اعضاء و جوارح بدنتان درهم آمیزد. و بر همه امورتان حاکم گردانید، اطاعت خدا را راه ورود به آب حیات، شفیع گرفتن خواسته‌ها، پناهگاه روز اضطراب، چراغ روشنگر قبرها، آرامش وحشت‌های طولانی دوران برزخ، و راه نجات لحظات سخت زندگی، قرار دهید زیرا اطاعت خدا، وسیله نگهدارنده از حوادث هلاک کننده، و جایگاه‌های وحشتناک که انتظار آن را می‌کشید، و حرارت آتشهای برافروخته می‌باشد. پس کسی که تقوا را انتخاب کند، سختی‌ها از او دور گردند، تلخی‌ها شیرین، فش \nار مشکلات و ناراحتیها برطرف خواهد شد، و مشکلات پیاپی و خسته کننده، آسان، و مجد و بزرگی از دست رفته چون قطرات باران بر او فرو می‌بارند، رحمت باز داشته حق باز میگردد، و نعمتهای الهی پس از فرو نشستن به جوشش می‌آیند، و برکات تقلیل یافته فزونی گیرند. پس از خدایی بترسید که با پند دادن شما را سود فراوان بخشیده، و با رسالت پیامبرش شما را نیکو اندرز داده، و با نعمتهایش بر شما منت گذاشته است، خود را برای پرستش خدا فروتن دارید، و با انجام وظائف الهی، حق فرمانبرداری را به جا آورید. \nویژگیهای اسلام همانا این اسلام، دین خداوندی است که آن را برای خود برگزید، و با دیده عنایت پروراند، و بهترین آفریدگان خود را مخصوص ابلاغ آن قرار داد، پایه‌های اسلام را بر محبت خویش استوار کرد، و ادیان و مذاهب گذشته را با عزت آن، خوار نمود، و با سربلند کردن آن دیگر ملت‌ها را بی مقدار کرد، و با محترم داشتن آن دشمنان را خوار گردانید، و با یاری کردن آن دشمنان سرسخت را شکست داد، و با نیرومند ساختن آن ارکان گمراهی را درهم کوبید، و تشنگان را از چشمه زلال آن سیراب کرد، و آبگیره‌های اسلام را پرآب نمود. خداوند اسلام را بگونه ای استحکام بخشید که پیوندهایش نگسلد، و حلقه‌هایش از هم جدا نشود، و ستونهایش خراب نگردد، در پایه‌هایش زوال راه نیاید، درخت وجودش از ریشه کنده نشود، زمانش پایان نگیرد، قوانینش کهنگی نپذیرد، شاخه‌هایش قطع نگردد، راه‌هایش تنگ و خراب نشود، و پیمودن راهش دشوار نباشد، تیرگی در روشنایی آن داخل نشود، و راه راست آن کجی نیابد، ستونهایش خم نشود، و گذرگاهش بدون دشواری قابل پیمودن باشد، در چراغ اسلام خاموشی، و در شیرینی آن تلخی راه نیابد. اسلام ستونهای استواری است که خداوند آن را در دل حق برقرار کرد، \nاساس و پایه آن را ثابت فرمود، اسلام چشمه ساری است که آب آن در فوران، چراغی است که شعله‌های آن فروزان، و نشانه همیشه استواری است که روندگان راه حق با آن هدایت شوند، پرچمی است که برای راهنمایی پویندگان راه خدا نصب گردیده، و آبشخوری است که واردشوندگان آن سیراب می‌شوند. خداوند نهایت خشنودی خود را در اسلام قرار داده، و بزرگ ترین ستونهای دینش، و بلندترین قله اطاعت او در اسلام جای گرفته است، اسلام در پیشگاه خداوند، دارای ستونهایی مطمئن، بنایی بلند، راهنمایی همیشه روشن، شعله ای روشنی بخش، برهانی نیرومند، و نشانه ای بلندپایه است، که درافتادن با آن ممکن نیست، پس اسلام را بزرگ بشمارید، از آن پیروی کنید، حق آن را ادا نمایید، و در جایگاه شایسته خویش قرار دهید. \nبعثت پیامبر (ص) و سختی‌های جاهلیت سپس خداوند سبحان حضرت محمد (ص) را هنگامی مبعوث فرمود که دنیا به پایان خود نزدیک، نشانه‌های آخرت نزدیک و رونق آن به تاریکی گرایید، و اهل خود را بپا داشته، جای آن ناهموار، آماده نیستی و نابودی، زمانش در شرف پایان، و نشانه‌های نابودی آن نزدیک، موجودات در آستانه مرگ، حلقه زندگی آن شکسته، و اسباب حیات درهم ریخته، پرچمهای دنیا پوسیده، و پرده‌هایش دریده. و عمرها به کوتاهی رسیده بود، در این هنگام خداوند پیامبر (ص) را ابلاغ کننده رسالت، افتخارآفرین امت، چونان باران بهاری برای تشنگان حقیقت آن روزگاران، مایه سربلندی مسلمانان، و عزت و شرافت یارانش قرار داد. ارزش و ویژگیهای قرآن سپس قرآن را بر او نازل فرمود. نوری است که خاموشی ندارد، چراغی است که درخشندگی آن زوال نپذیرد، دریایی است که ژرفای آن درک نشود، راهی است که رونده آن گمراه نگردد، شعله ای است که نور آن تاریک نشود، جداکننده حق و باطلی است که درخشش برهانش خاموش نگردد، بنایی است که ستونهای آن خراب نشود، شفادهنده ای است که بیماریهای وحشت انگیز را بزداید، قدرتی است که یاورانش شکست ندارند، و حقی است که یاری کنندگانش مغلوب \nنشوند. قرآن، معدن ایمان و اصل آن است، چشمه‌های دانش و دریاهای علوم است، سرچشمه‌های عدالت، و نهرهای جاری عدل است، پایه‌های اسلام و ستونهای محکم آن است، نهرهای جاری زلال حقیقت، و سرزمینهای آن است، دریایی است که تشنگان آن، آبش را تمام نتوانند کشید، و چشمه‌هایی است که آبش کمی ندارد، محل برداشت آبی است که هر چه از آن برگیرند کاهش نمی یابد، منزلهایی است که مسافران راه آن را فراموش نخواهند کرد، و نشانه‌هایی است که روندگان از آن غفلت نمی کنند، کوهسار زیبایی است که از آن نمی گذرند. خدا قرآن را فرو نشاننده عطش علمی دانشمندان، و باران بهاری برای قلب فقیهان، و راه گسترده و وسیع برای صالحان قرار داده است، قرآن دارویی است که با آن بیماری وجود ندارد، نوری است که با آن تاریکی یافت نمی شود، ریسمانی است که رشته‌های آن محکم، پناهگاهی است که قله آن بلند، و توان و قدرتی است برای آنکه قرآن را برگزیند، محل امنی است برای هر کس که آنجا وارد شود، راهنمایی است برای آنکه از او پیروی کند، وسیله انجام وظیفه است برای آنکه قرآن را راه و رسم خود قرار دهد، برهانی است بر آن کس که با آن سخن بگوید، عامل پیروزی است برای آن کس که با آن استدلال کند \n، نجات دهنده است برای آن کس که حافظ آن باشد و به آن عمل کند، و راهبر آنکه آن را بکار گیرد، و نشانه هدایت است برای آن کس که در او بنگرد، سپر نگهدارنده است برای آن کس که با آن خود را بپوشاند، و دانش کسی است که آن را به خاطر بسپارد، و حدیث کسی است که از آن روایت کند، و فرمان کسی است که با آن قضاوت کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (111, 1, 199, 'در سفارش به یاران خود', 'خطبه ۱۹۹-در سفارش به یاران خود \nره آورد نماز \nمردم! نماز را برعهده گیرید، و آن راحفظ کنید، زیاد نماز بخوانید، و با نماز خود را به خدا نزدیک کنید، (نماز فریضه واجبی است که در وقتهای خاص بر مومنان واجب گردیده است)، آیا به پاسخ دوزخیان گوش فرانمی دهید، آن هنگام که از آن‌ها پرسیدند: چه چیز شما را به دوزخ کشانده است؟ گفتند: (ما از نمازگزاران نبودیم.) همانا نماز! گناهان را چونان برگهای پاییز فرو می‌ریزد، و غل و زنجیر گناهان را از گردنها می‌گشاید، پیامبر اسلام (ص) نماز را به چشمه آب گرمی که بر در سرای مردی جریان داشته باشد، تشبیه کرد، اگر روزی پنج بار خود را در آن شستشو دهد، هرگز چرک و آلودگی در بدن او نماند. همانا کسانی از مومنان حق نماز را شناختند که زیور دنیا از نماز بازشان ندارد، و روشنایی چشمشان یعنی اموال و فرزندان مانع نمازشان نشود. خدای سبحان می‌فرماید: (مردانی هستند که تجارت و خرید و فروش، آنان را از یاد خدا، و برپا داشتن نماز، و پرداخت زکات باز نمی دارد) رسول خدا (ص) پس از بشارت به بهشت، خود را در نماز خواندن به زحمت می‌انداخت، زیرا خداوند به او فرمود: (خانواده خویش را به نماز فرمان ده و بر انجام آن شکیبا باش.) پس پیامبر (ص) \nپی در پی خانواده خود را به نماز فرمان می‌داد، و خود نیز در انجام نماز شکیبا بود. ارزش و ره آورد زکات همانا پرداخت زکات با نماز، عامل نزدیک شدن مسلمانان با خداست، پس آن کس که زکات را با رضایت خاطر بپردازد. کفاره گناهان او می‌شود، بازدارنده و نگهدارنده انسان از آتش جهنم است، پس نباید به آنچه پرداخته با نظر حسرت نگاه کند، و برای پرداخت زکات افسوس خورد، زیرا آن کس که زکات را از روی رغبت نپردازد، و انتظار بهتر از آنچه را پرداخته داشته باشد، به سنت پیامبر (ص) نادان است، و پاداش او اندک، و عمل او تباه و همیشه پشیمان خواهد بود. مسوولیت ادای امانت یکی دیگر از وظائف الهی، ادای امانت است، آن کس که امانتها را نپردازد زیانکار است، امانت الهی را بر آسمانهای برافراشته، و زمینهای گسترده، و کوه‌های بپا داشته، عرضه کردند، که از آن‌ها بلندتر، بزرگتر، وسیع تر یافت نمی شود، اما نپذیرفتند اگر بنا بود که چیزی به خاطر طول و عرض و توانمندی و سربلندی از پذیرفتن امانت سر باز زند آنان بودند، اما از کیفر الهی ترسیدند، و از عواقب تحمل امانت آگاهی داشتند، که ناتوانتر از آن‌ها آگاهی نداشت، و آن انسان است، که خدا فرمود: (همانا انسان ستمکار \nنادان است.) دانش الهی همانا بر خداوند سبحان پنهان نیست آنچه را که بندگان در شب و روز انجام می‌دهند، که دقیقا بر اعمال آن‌ها آگاه است، و با علم خویش بر آن‌ها احاطه دارد، اعضا شما مردم گواه او، و اندام شما سپاهیان او، و روان و جانتان جاسوسان او، و خلوتهای شما بر او آشکار است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (112, 1, 2, 'پس از بازگشت از صفین', 'خطبه ۲-پس از بازگشت از صفین \nستایش پروردگار\nستایش می‌کنم خداوند را، برای تکمیل نعمتهای او، و تسلیم بودن برابر بزرگی او، و ایمن ماندن از نافرمانی او، و در رفع نیازها از او یاری می‌طلبم، زیرا آن کس را که خدا هدایت کند، هرگز گمراه نگردد، و آن را که خدا دشمن دارد، هرگز نجات نیابد، و هر آن کس را که خداوند بی نیاز گرداند، نیازمند نخواهد شد، پس ستایش خداوند از همه چیز گرانسنگ تر، و برترین گنجی است که ارزش ذخیره شدن دارد، \nو گواهی می‌دهم که جز خدای یکتای بی شریک، معبودی نیست، شهادتی که اخلاص آن آزموده، و پاکی و خلوص آن را باور داریم، و تا زنده ایم بر این باور استواریم، و آن را برای صحنه‌های هولناک روز قیامت ذخیره می‌کنیم، زیرا شهادت به یگانگی خدا، نشانه استواری ایمان، بازکننده درهای احسان، مایه خشنودی خدای رحمان، و دورکننده شیطان است. ویژگیهای پیامبر اسلام (ص) و شهادت می‌دهم که محمد (ص) بنده خدا و فرستاده اوست، خداوند او را با دینی آشکار، و نشانه ای پایدار، و قرآنی نوشته شده و استوار، و نوری درخشان، و چراغی تابان، و فرمانی آشکارکننده، فرستاد تا شک و تردیدها را نابود سازد و با دلائل روشن استدلال کند، و با آیات الهی مردم را پرهیز دهد، و از کیفرهای الهی بترساند. شناخت عصر جاهلیت: خدا پیامبر اسلام را زمانی فرستاد، که مردم در فتنه‌ها گرفتارشده، رشته‌های دین پاره شده، و ستونهای ایمان و یقین ناپایدار بود، در اصول دین اختلاف داشته، و امور مردم پراکنده بود، راه رهایی دشوار، و پناهگاهی وجود نداشت، چراغ هدایت بی نور، و کوردلی همگان را فرا گرفته بود، خدای رحمان معصیت می‌شد، و شیطان یاری می‌گردید، ایمان بدون یاور مانده، و ستو \nنهای آن ویران گردیده، و نشانه‌های آن انکارشده، راه‌های آن ویران، و جاده‌های آن کهنه و فراموش گردید، مردم جاهلی، شیطان را اطاعت می‌کردند، و به راه‌های او می‌رفتند، و در آبشخور شیطان سیراب می‌شدند، با دست مردم جاهلیت، نشانه‌های شیطان، آشکار، و پرچم او برافراشته گردید، فتنه‌ها، مردم را لگدمال کرده، و با سمهای محکم خود نابودشان کرده، و پابرجا ایستاده بود، اما مردم حیران و سرگردان، بی خبر و فریب خورده، در کنار بهترین خانه (کعبه) و بدترین همسایگان (بت پرستان) زندگی می‌کردند، خواب آن‌ها بیداری، و سرمه چشم آن‌ها اشک بود، در سرزمینی که دانشمند آن لب فرو بسته، و جاهل گرامی بود. \nویژگیهای اهل بیت (ع) عترت پیامبر (ص) (اهل بیت (ع)) جایگاه اسرار خداوندی، و پناهگاه فرمان الهی، و مخزن علم خدا، و مرجع احکام اسلامی و نگهبان کتابهای آسمانی، و کوه‌های همیشه استوار دین خدایند، \nخدا به وسیله اهل بیت (ع) پشت خمیده دین را راست نمود، و لرزش و اضطراب آن را از میان برداشت، برابر فاسدانی که تخم گناه افشاندند، و با آب غرور و فریب آبیاری کردند، و محصول آن را که جز عذاب و بدبختی نبود برداشتند، کسی را با خاندان رسالت (عترت پیامبر (ع)) نمی شود مقایسه کرد. و آنان که پرورده نعمت هدایت اهل بیت پیامبرند با آنان برابر نخواهند بود. عترت پیامبر (ص) اساس دین، و ستونهای استوار یقین می‌باشند، شتاب کننده، باید به آنان باز گردد، و عقب مانده، باید به آنان بپیوندد، زیرا ویژگیهای حق ولایت به آن‌ها اختصاص دارد، و وصیت پیامبر (ص) نسبت به خلافت مسلمین، و میراث رسالت، به آن‌ها تعلق دارد، هم اکنون (که خلافت را به من سپردید) حق به اهل آن بازگشت، و دوباره به جایگاهی که از آن دور مانده بود، باز گردانده شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (113, 1, 20, 'در منع از غفلت', 'خطبه ۲۰-در منع از غفلت \nعلل پنهان بودن اسرار پس از مرگ \nآنچه را که مردگان دیدند اگر شما می‌دیدید، ناشکیبا بودید، و می‌ترسیدید، و می‌شنیدید و فرمان می‌بردید، ولی آنچه آن‌ها مشاهده کردند بر شما پوشیده است، و نزدیک است که پرده‌ها فرو افتد. گر چه حقیقت را به شما نیز نشان دادند، اگر بدرستی بنگرید، و ندای حق را به شما شنواندند. اگر به خوبی بشنوید، و به راه راست هدایتتان کردند. اگر هدایت بپذیرید، راست می‌گویم، مطالب عبرت آموز اندرزدهنده را آشکارا دیدید، و از حرام الهی نهی شدید، و پس از فرشتگان آسمانی، هیچ کس جز انسان، فرمان خداوند را ابلاغ نمی کند.', 'من كلام له (علیه السلام) و فيه ينفر من الغفلة و ينبّه إلى الفرار للّه:\r\nفَإِنَّكُمْ لَوْ قَدْ عَايَنْتُمْ مَا قَدْ عَايَنَ مَنْ مَاتَ مِنْكُمْ لَجَزِعْتُمْ وَ وَهِلْتُمْ وَ سَمِعْتُمْ وَ أَطَعْتُمْ وَ لَكِنْ مَحْجُوبٌ عَنْكُمْ مَا قَدْ عَايَنُوا وَ قَرِيبٌ مَا يُطْرَحُ الْحِجَابُ وَ لَقَدْ بُصِّرْتُمْ إِنْ أَبْصَرْتُمْ وَ أُسْمِعْتُمْ إِنْ سَمِعْتُمْ وَ هُدِيتُمْ إِنِ اهْتَدَيْتُمْ وَ بِحَقٍّ أَقُولُ لَكُمْ لَقَدْ جَاهَرَتْكُمُ الْعِبَرُ وَ زُجِرْتُمْ بِمَا فِيهِ مُزْدَجَرٌ وَ مَا يُبَلِّغُ عَنِ اللَّهِ بَعْدَ رُسُلِ السَّمَاءِ إِلَّا الْبَشَرُ.', NULL, 'علل پنهان بودن اسرار پس از مرگ:\r\nآنچه را كه مردگان ديدند اگر شما مى ديديد، ناشكيبا بوديد و مى ترسيديد، و مى شنيديد و فرمان مى برديد. ولى آنچه آنها مشاهده كردند بر شما پوشيده است، و نزديك است كه پرده ها فروافتد. اگر چه حقيقت را به شما نيز نشان دادند، اگر بدرستى بنگريد؛ و نداى حق را به شما شنواندند، اگر خوب بشنويد؛ و به راه راست هدايتتان كردند، اگر هدايت بپذيريد.\r\nراست مى گويم، مطالب عبرت آموز اندرز دهنده را آشكارا ديديد، و از حرام الهى نهى شديد، و پس از فرشتگان آسمانى، هيچ كس جز انسان، فرمان خداوند را ابلاغ نمى كند.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (114, 1, 200, 'درباره معاویه', 'خطبه ۲۰۰-درباره معاویه \nسیاست دروغین معاویه \nسوگند بخدا! معاویه از من سیاستمدارتر نیست، اما معاویه حیله گر و جنایتکار است، اگر نیرنگ ناپسند نبود من زیرکترین افراد بودم، ولی هر نیرنگی گناه، و هر گناهی نوعی کفر و انکار است، روز رستاخیز در دست هر حیله گری پرچمی است که با آن شناخته می‌شود. بخدا سوگند! من با فریبکاری غافلگیر نمی شوم، و با سختگیری ناتوان نخواهم شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (115, 1, 201, 'تشویق به پیمودن راه راست', 'خطبه ۲۰۱-پیمودن راه راست \nراه روشن حق \nای مردم در راه راست، از کمی روندگان نهراسید، زیرا اکثریت مردم بر گرد سفره ای جمع شدند که سیری آن کوتاه، و گرسنگی آن طولانی است. ای مردم، همه افراد جامعه در خشنودی و خشم شریک می‌باشند، چنانکه شتر ماده ثمود را یک نفر دست و پا برید، اما عذاب آن تمام قوم ثمود را گرفت، زیرا همگی آن را پسندیدند، خداوند سبحان می‌فرماید: (ماده شتر را پی کردند و سرانجام پشیمان شدند.) سرزمین آنان چونان آهن گداخته ای که در زمین نرم فرو رود، فریادی زد و فرو ریخت، ای مردم آن کس که از راه آشکار برود به آب می‌رسد، و هر کس از راه راست منحرف شود سرگردان می‌ماند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (116, 1, 202, 'آه و ناله هنگام به خاک سپردن فاطمه زهراء', 'خطبه ۲۰۲-هنگام به خاکسپاری فاطمه \nشکوه‌ها از ستمکاری امت \nسلام بر تو ای رسول خدا (ص)، سلامی از طرف من دخترت که هم اکنون در جوارت فرود آمده و شتابان به شما رسیده است، ای پیامبر خدا، صبر و بردباری من با از دست دادن فاطمه (ع) کم شده، و توان خویشتنداری ندارم اما برای من که سختی جدایی تو را دیده، و سنگینی مصیبت تو را کشیدم، شکیبایی ممکن است، این من بودم. که با دست خود تو را در میان قبر نهادم، و هنگام رحلت جان گرامی تو میان سینه و گردنم پرواز کرد پس همه ما از خداییم و به خدا باز می‌گردیم. پس امانتی که به من سپرده بودی برگردانده شد، و به صاحبش رسید، از این پس اندوه من جاودانه، و شبهایم، شب زنده داری است، تا آن روز که خدا خانه زندگی تو را برای من برگزیند. به زودی دخترت تو را آگاه خواهد ساخت که امت تو چگونه در ستمکاری بر او اجتماع کردند، از فاطمه (ع) بپرس، و احوال اندوهناک ما را از خبر گیر، که هنوز روزگاری سپری نشده، و یاد تو فراموش نگشته است. سلام من به هر دوی شما، سلام وداع کننده ای که از روی خشنودی یا خسته دلی سلامی نمی کند، اگر از خدمت تو باز می‌گردم از روی خستگی نیست، و اگر در کنار قبرت می‌نشینم از بدگمانی بدانچه خدا صابران را وعده \nداده نمی باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (117, 1, 203, 'تشویق یاران خود به پرداختن به آخرت', 'خطبه ۲۰۳-پرداختن به آخرت \nآخرت گرایی \nای مردم! دنیا سرای گذرا! و آخرت خانه جاویدان است، پس از گذرگاه خویش برای سرمنزل جاودانه توشه برگیرید، و پرده‌های خود را در نزد کسی که بر اسرار شما آگاه است پاره نکنید، پیش از آنکه بدنهای شما از دنیا خارج گردد، دلهایتان را خارج کنید، شما را در دنیا آزموده اند، و برای غیر دنیا آفریده اند. کسی بمیرد، مردم می‌گویند: (چه باقی گذاشت؟)، اما فرشتگان می‌گویند: (چه پیش فرستاد؟) خدا پدرانتان را بیامرزد، مقداری از ثروت خود را جلوتر بفرستید تا در نزد خدا باقی ماند، و همه را برای وارثان مگذارید که پاسخگویی آن بر شما واجب است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (118, 1, 204, 'اندرز به یاران', 'خطبه ۲۰۴-اندرز به یاران \nآمادگی برای سفر آخرت \nآماده حرکت شوید، خدا شما را بیامرزد، که بانگ کوچ را سردادند، وابستگی به زندگی دنیارا کم کنید، و با زاد و توشه نیکو به سوی آخرت باز گردید، که پیشاپیش شما گردنه ای سخت و دشوار، و منزلگاه‌هایی ترسناک وجود دارد، که باید در آنجاها فرود آیید، و توقف کنید، آگاه باشید، که فاصله نگاه‌های مرگ بر شما کوتاه، و گویا چنگالهایش را در جان شما فرو برده است، کارهای دشوار دنیا مرگ را از یادتان برده، و بلاهای طاقت فرسا آن را از شما پنهان داشته است، پس پیوندهای خود را با دنیا قطع کنید، و از پرهیزکاری کمک بگیرید. (قسمتی از این سخن در خطبه‌های پیش با کمی تفاوت نقل شد).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (119, 1, 205, 'در خطاب به طلحه و زبیر', 'خطبه ۲۰۵-خطاب به طلحه و زبیر \n(طلحه و زبیر پس از بیعت با امام (ع) اعتراض کردند که چرا در امور کشور با آنان مشورت نکرده و از آن‌ها کمک نگرفته است فرمود:) \nبرخورد قاطعانه با سران ناکثین (طلحه و زبیر) \nبه اندک چیزی خشمناک شدید، و خوبیهای فراوان را از یاد بردید، ممکن است به من خبر دهید که کدام حقی را از شما باز داشته ام؟ یا کدام سهم را برای خود برداشته ام؟ و بر شما ستم کردم؟ و کدام شکایت حقی پیش من آورده شده که ضعف نشان دادم؟ و کدام فرمان الهی را آگاه نبوده و راه آن را به اشتباه پیموده ام؟ بخدا سوگند! من به خلافت رغبتی نداشته، و به ولایت بر شما علاقه ای نشان نمی دادم، و این شما بودید که مرا به آن دعوت کردید، و آن را بر من تحمیل نمودید، روزی که خلافت به من رسید در قرآن نظر دوختم، هر دستوری که داده، و هر فرمانی که فرموده پیروی کردم، به راه و رسم پیامبر (ص) اقتدا کردم، پس هیچ نیازی به حکم و رای شما و دیگران ندارم، هنوز چیزی پیش نیامده که حکم آن را ندانم، و نیاز به مشورت شما. و دیگر برادران مسلمان داشته باشم، اگر چنین بود از شما و دیگران رویگردان نبودم. و اما اعتراض شما که چرا با همه به تساوی رفتار کردم، این روشی نبود که به رای خود، \nو یا با خواسته دل خود انجام داده باشم، بلکه من و شما این گونه رفتار را از دستورالعملهای پیامبر اسلام (ص) آموختیم، که چه حکمی آورد؟ و چگونه آن را اجرا فرمود؟ پس در تقسیمی که خدا به آن فرمان داد به شما نیازی نداشتم. سوگند بخدا! نه شما، و نه دیگران را بر من حقی نیست که زبان به اعتراض گشایند، خداوند قلبهای شما و ما را به سوی حق هدایت فرماید، و شکیبایی و استقامت را به ما و شما الهام کند. (سپس فرمود:) خدا رحمت کند آن کس را که حقی را بنگرد و یاری کند، یا ستمی مشاهده کرده آن را نابود سازد، که خداوند حق را یاری می‌کند تا به صاحبش برگرداند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (120, 1, 206, 'منع از دشنام شامیان', 'خطبه ۲۰۶-منع از دشنام شامیان \nاخلاق در جنگ \nمن خوش ندارم که شما دشنام دهنده باشید، اما اگر کردارشان را تعریف و حالات آنان را بازگو می‌کردید به سخن راست نزدیک تر، و عذرپذیرتر بودید، خوب بود بجای دشنام آنان می‌گفتید: خدایا! خون ما و آن‌ها را حفظ کن، بین ما و آنان اصلاح فرما، و آنان را از گمراهی به راه راست هدایت کن، تا آنان که جاهلند، حق را بشناسند، و آنان که با حق می‌ستیزند پشیمان شده به حق باز گردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (121, 1, 207, 'بازداشتن امام حسن از رفتن به می‌دان', 'خطبه ۲۰۷-بازداشتن امام حسن از... \nضرورت حفظ امامت \nاین جوان را نگهدارید تا پشت مرا نشکند، که دریغم آید مرگ، حسن و حسین (ع) را دریابد، نکند با مرگ آن‌ها نسل رسول خدا (ص) از بین برود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (122, 1, 208, 'درباره حکمیت', 'خطبه ۲۰۸-درباره حکمیت \nنکوهش از نافرمانی کوفیان \nای مردم، همواره کار من با شما به دلخواه من بود تا آنکه جنگ شما را ناتوان کرد، به خدا اگر جنگ کسانی را از شما گرفت، و جمعی را گذاشت، برای دشمنانتان نیز کوبنده تر بود، من دیروز فرمانده و امیر شما بودم ولی امروز فرمانم می‌دهند، دیروز بازدارنده بودم که امروز مرا باز می‌دارند، شما زنده ماندن را دوست دارید، و من نمی توانم شما را به راهی که دوست ندارید اجبار کنم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (123, 1, 209, 'در خانه علاء حارثی', 'خطبه ۲۰۹-در خانه علاء حارثی \nروش استفاده از دنیا \nبا این خانه وسیع در دنیا چه می‌کنی؟ در حالی که در آخرت به آن نیازمندتری آری اگر بخواهی می‌توانی با همین خانه به آخرت برسی! در این خانه وسیع مهمانان را پذیرایی کنی، به خویشاوندان با نیکوکاری بپیوندی، و حقوقی که بر گردن تو است به صاحبان حق برسانی، پس آنگاه تو با همین خانه وسیع به آخرت نیز می‌توانی پرداخت. برخورد با تفکر ترک دنیا ای دشمنک جان خویش، شیطان سرگردانت کرده، آیا تو به زن و فرزندانت رحم نمی کنی؟ تو می‌پنداری که خداوند نعمتهای پاکیزه ای را حلال کرده اما دوست ندارد تو از آن‌ها استفاده کنی؟ تو در برابر خدا کوچکتر از آنی که اینگونه با تو رفتار کند. وای بر تو، من همانند تو نیستم، خداوند بر پیشوایان حق واجب کرده که خود را با مردم ناتوان همسو کنند، تا فقر و نداری، تنگدست را به هیجان نیاورد، و به طغیان نکشاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (124, 1, 21, 'در توجه به قیامت', 'خطبه ۲۱-در توجه به قیامت \nراه رستگاری قیامت پیش روی شما و مرگ در پشت سر، شما را می‌راند، سبکبار شوید تا برسید، همانا آنان که رفتند در انتظار رسیدن شمایند. (این سخن امام (ع) پس از سخن خدا و پیامبر (ص) با هر سخنی سنجیده شود بر آن برتری دارد و از آن پیشی می‌گیرد و از جمله (سبکبار شوید تا برسید) کلامی کوتاهتر و پر معنی تر از آن شنیده نشده چه کلمه ژرف و بلندی؟ چه جمله پر معنی و حکمت آمیزی است؟ که تشنگی را با آب حکمت می‌زداید ما عظمت و شرافت این جمله را در کتاب خود به نام (الخصائص) بیان کرده ایم).', 'و من خطبة له (علیه السلام) و هي كلمة جامعة للعِظة و الحكمة:\r\nفَإِنَّ الْغَايَةَ أَمَامَكُمْ وَ إِنَّ وَرَاءَكُمُ السَّاعَةَ تَحْدُوكُمْ؛ تَخَفَّفُوا تَلْحَقُوا، فَإِنَّمَا يُنْتَظَرُ بِأَوَّلِكُمْ آخِرُكُمْ.\r\n[قال السيد الشريف: أقول إن هذا الكلام لو وُزِن بعد كلام الله سبحانه و بعد كلام رسول الله (صلی الله علیه وآله) بكل كلام لَمالَ به راجحا و برز عليه سابقا. فأما قوله (علیه السلام) «تخفّفوا تلحقوا» فما سمع كلام أقل منه مسموعا و لا أكثر منه محصولا و ما أبعد غورها من كلمة و أنقع نطفتها من حكمة و قد نبّهنا في كتاب الخصائص على عظم قدرها و شرف جوهرها].', NULL, '(اين خطبه بخشى از خطبه 167 است كه در سال 35 هجرى در آغاز خلافت ايراد فرمود): \r\nراه رستگارى:\r\nقيامت پيش روى شما و مرگ در پشت سر، شما را مى راند. سبكبار شويد تا برسيد. همانا آنان كه رفتند در انتظار رسيدن شمايند. \r\n(سید رضی مى گويد: اين سخن امام عليه السّلام پس از سخن خدا و پيامبر صلّى اللّه عليه و آله و سلّم با هر سخنى سنجيده شود بر آن برترى دارد و از آن پيشى مى گيرد و از جمله «سبكبار شويد تا برسيد» كلامى كوتاهتر و پر معنى تر از آن شنيده نشده، چه كلمه ژرف و بلندى چه جمله پر معنى و حكمت آميزى است كه تشنگى را با آب حكمت مى زدايد. ما عظمت و شرافت اين جمله را در كتاب خود به نام «الخصائص» بيان كرده ايم).', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (125, 1, 210, 'در باب حدیثهای مجعول', 'خطبه ۲۱۰-در باب حدیثهای مجعول \nاقسام احادیث رواج یافته \nاحادیثی که در دسترس مردم قرار دارد، هم حق است هم باطل، هم راست هم دروغ، هم ناسخ هم منسوخ، هم عام هم خاص، هم محکم هم متشابه، هم احادیثی که به درستی ضبط گردید و هم احادیثی که با ظن و گمان روایت شد. در روزگار پیامبر (ص) آنقدر دروغ به آن حضرت نسبت داده شد که ایستاد و خطابه ایراد کرد و فرمود: (هر کس از روی عمد به من دروغ نسبت دهد جایگاه او پر از آتش است.) اقسام راویان حدیث افرادی که حدیث نقل می‌کنند چهار دسته اند، که پنجمی ندارد. اول- منافقان نفوذی نخست منافقی که تظاهر به ایمان می‌کند، و نقاب اسلام بر چهره دارد، نه از گناه می‌ترسد و نه از آن دوری می‌جوید و از روی عمد دروغ به پیامبر (ص) نسبت می‌دهد. اگر مردم می‌دانستند که او منافق دروغگو است، از او نمی پذیرفتند و گفتار دروغین او را تصدیق نمی کردند، اما با ناآگاهی می‌گویند و از اصحاب پیامبر است، رسول خدا را دیده و از او حدیث شنیده، و از او گرفته است، پس حدیث دروغین او را قبول می‌کنند، در صورتیکه خدا تو را از منافقین آنگونه که لازم بود آگاه‌اند، و وصف آنان را برای تو بیان داشت، آنان پس از پیامبر (ص) باقی ماندند و به پیشوایان گ \nمراهی و دعوت کنندگان به آتش با دروغ و تهمت نزدیک شده پس به آنان ولایت و حکومت بخشیدند، و بر گردن مردم سوار گردیدند، و به وسیله آنان به دنیا رسیدند، همانا مردم هم با سلاطین و دنیا هستند، مگر آن کس که خدا او را حفظ کند، این یکی از آن چهار گروه. دوم- اشتباه کار دوم کسی که از پیامبر (ص) چیزی را به اشتباه شنیده، و سخن آنحضرت را درست حفظ نکرده است، و با توهم چیزی را گرفته، اما از روی عمد دروغ نمی گوید، آنچه در اختیار دارد روایت کرده و به آن عمل می‌کند و می‌گوید، من از پیامبر (ص) آن را شنیده ام، اگر مسلمانان بدانند که او اشتباه کرده، و غیر واقعی پنداشته، از او نمی پذیرفتند، خودش هم اگر آگاهی می‌یافت که اشتباه کرده، آن را رها می‌کرد. سوم- ناآگاهانی که حدیث شناس نیستند. و سومی شخصی که شنیده پیامبر (ص) به چیزی امر فرمود، سپس از آن نهی کرد و او نمی داند، یا شنید که چیزی را نهی کرد، سپس به آن امر فرمود و او آگاهی ندارد، پس نسخ شده را حفظ کرد ولی ناسخ را نمی داند، اگر بداند که حدیث او منسوخ است ترکش می‌کند، و اگر مسلمانان نیز می‌دانستند روایت او نسخ شده آن را ترک می‌کردند. چهارم- حافظان راست گفتار دسته چهارم، آنکه ن \nه به خدا دروغ می‌بندد و نه به پیامبرش دروغ نسبت می‌دهد، دروغ را از ترس خدا، و حرمت نگهداشتن از رسول خدا (ص) دشمن دارد، در آن چه از پیامبر (ص) شنیده اشتباه نکرده، بلکه آن را با تمام جوانبش حفظ کرده است. و آن چنانکه شنیده بدون کم و کاست نقل می‌کند، پس او ناسخ را حفظ و به آن عمل کرد، و منسوخ را حفظ و از آن دوری جست، خاص و عام، محکم و متشابه، را شناخته، هر کدام را در جای خویش قرار داده است. اقسام اصحاب رسول خدا (ص) گاهی سخنی از رسول خدا (ص) دارای دو معنا بود، سخنی عام، و سخنی خاص، کسی آن را می‌شنید که مقصود خدا و پیامبرش را از کلام نمی فهمید، پس به معنای دلخواه خود تفسیر می‌کرد، و بدون آنکه معنای واقعی آن را بداند، که برای چه هدفی صادر شد، و چرا چنین گفته شد، حفظ و نقل می‌کرد. همه یاران پیامبر (ص) چنان نبودند که از او چیزی بپرسند و معنای واقعی آن را درخواست کنند تا آنجا که عده ای دوست داشتند، عربی بیابانی یا سوال کننده ای از آن حضرت چیزی بپرسد و آن‌ها پاسخ آن را بشنوند، اما من هر چه از خاطرم می‌گذشت می‌پرسیدم، و حفظ می‌کردم، پس این است علل اختلاف روایاتی که در میان مردم وجود دارد، و علل اختلاف روایات در نقل حدی \nث.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (126, 1, 211, 'در قدرت خداوند و خلقت زمین', 'خطبه ۲۱۱-در قدرت خداوند \nشگفتی آفرینش پدیده‌ها \nاز نشانه‌های توانایی و عظمت خدا، و شگفتی ظرافتهای صنعت او آن است که از آب دریای موج زننده، و امواج فراوان شکننده، خشکی آفرید، و به طبقاتی تقسیم کرد، سپس طبقه‌ها را از هم گشود و هفت آسمان را آفرید، که به فرمان او برقرار ماندند، و در اندازه‌های معین استوار شدند. و زمین را آفرید که دریایی سبز رنگ و روان آن را بر دوش می‌کشد، زمین در برابر فرمان خدا فروتن، و در برابر شکوه پروردگاری تسلیم است، و آب روان از ترس او ایستاد، و سپس صخره‌ها، تپه‌ها، و کوه‌های بزرگ را آفرید، آن‌ها را در جایگاه خود ثابت نگاه داشت، و در قرارگاهشان استقرار بخشید، پس کوهها در هوا و ریشه‌های آن در آب رسوخ کرد، کوهها از جاهای پست و هموار سر بیرون کشیده و کم کم ارتفاع یافتند، و ریشه آن در دل زمین ریشه دوانید، قله‌ها سر به سوی آسمان برافراشت، و نوک آن‌ها را طولانی ساخت، تا تکیه گاه زمین، و میخهای نگهدارنده آن باشد، سپس زمین با حرکات شدیدی که داشت آرام گرفت، تا ساکنان خود را نلرزاند، و آنچه بر پشت زمین است سقوط نکند، یا از جای خویش منتقل نگردد. پس پاک و منزه است خدایی که زمین را در میان آنهمه از امواج ناآرام، نگهدا \nشت، و پس از رطوبت آن را خشک ساخت، و آن را جایگاه زندگی مخلوقات خود گردانید، و چون بستری برایشان بگستراند، بر روی دریایی عظیم و ایستاده ای که روان نیست و تنها بادهای تند آن را بر هم می‌زند، و ابرهای پرباران آن را می‌جنباند. (و توجه به این شگفتی‌ها درس عبرتی است برای کسی که بترسد.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (127, 1, 212, 'در نکوهش اصحاب', 'خطبه ۲۱۲-در نکوهش اصحاب \nبسیج مردم برای جنگ با شامیان \nخدایا! هر کدام از بندگان تو، که سخن عادلانه دور از ستمکاری، و اصلاح کننده دور از فسادانگیزی ما را نسبت به دین و دنیا شنید، و پس از شنیدن سر باز زد، و از یاری کردنت باز ایستاد، و در گرامی داشتن دین تو درنگ کرد، ما تو را بر ضد او به گواهی می‌طلبیم، ای خدایی که از بزرگ ترین گواهانی و تمام موجوداتی که در آسمانها و زمین سکونت دادی همه را بر ضد او به گواهی دعوت می‌کنیم با اینکه تو از یاری او بی نیازی، و او را به کیفر گناهانش گرفتار خواهی کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (128, 1, 213, 'آفریدگار بی‌همتا', 'خطبه ۲۱۳-آفریدگار بی همتا \nخداشناسی \nسپاس خداوندی را سزاست که از شباهت داشتن به پدیده‌ها برتر است، و از تعریف وصف کنندگان والاتر، و با تدبیر شگفتی آورش بر همه بینندگان آشکار، و با بزرگی عزتش بر همه فکرهای اندیشمندان پنهان است. داناست نه آن که آگاهی او از جایی گرفته شده یا در حال فزونی باشد، و یا از کسی فراگیرد، اداره کننده سراسر نظام آفرینش است بی آنکه نیازی به فکر کردن یا اندیشه درونی باشد، خدایی که تاریکیها او را پنهان نسازد، و از نورها روشنی نگیرد، شب او را نپوشاند، و روز بر او نمی گذرد، نه بینایی او با دیدگان است، و نه علم او با اطلاعات و اخبار (قسمتی از این سخنرانی پیرامون پیامبر (ص) است) \nویژگیهای پیامبر (ص) خدا پیامبر (ص) را با روشنایی اسلام فرستاد، و در گزینش، او را بر همه مقدم داشت، با بعثت او شکافها را پر، و سلطه گران پیروز را درهم شکست، و سختی‌ها را آسان، و ناهمواریها را هموار تا آنکه گمراهی را از چپ و راست تار و مار کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (129, 1, 214, 'در وصف پیامبر و عالمان، و اندرز مردم', 'خطبه ۲۱۴-در وصف پیامبر و عالمان \nپیامبرشناسی \nگواهی می‌دهم که خدا عدل است و دادگر، و دادرسی جداکننده حق و باطل، و گواهی می‌دهم که محمد (ص) بنده و فرستاده او سرور مخلوقات است، هرگاه آفریدگان را به دو دسته کرد او را بهترین آن‌ها قرار داد، در خاندان او نه زناکار است و نه مردم بدکار. آگاه باشید! خداوند برای خوبیها مردمی، و برای حق ستونهای استواری، و برای اطاعت نگهدارنده ای قرار داد، و در هر گامی که در اطاعت برمی دارید یاوری از طرف خدای سبحان وجود دارد که زبانها به نیروی آن‌ها سخن می‌گویند، و دلها با کمک آن‌ها استوارند، برای یاری طلبان یاور، و برای شفاخواهان شفادهنده اند. \nارزش دانشمندان الهی بدانید! بندگانی که نگاهدار علم خداوندند، و آن را حفظ می‌کنند، و چشمه‌های علم الهی را جوشان می‌سازند، با دوستی او با یکدیگر پیوند دارند، و با دوستی یکدیگر را دیدار می‌کنند، جام محبت او را به همدیگر می‌نوشانند، و از آبشخور علم او سیراب می‌گردند، شک و تردید در آن‌ها راه نمی یابد، و از یکدیگر بدگویی نمی کنند، سرشت و اخلاقشان با این ویژگیها شکل گرفته است، و بر این اساس تمام دوستیها و پیوندهایشان استوار است. آنان، چونان بذرهای پاکیزه ای هستند که در میان مردم گزینش شده، آن‌ها را برای کاشتن انتخاب و دیگران را رها می‌کنند، با آزمایشهای مکرر امتیاز یافتند، و با پاک کردنهای پی در پی وارسته شدند. پندهای جاودانه پس آدمی باید اندرزها را بپذیرد، و بیش از رسیدن رستاخیز پرهیزکار باشد، و به کوتاهی روزگارش اندیشه کند، و به ماندن کوتاه در دنیا نظر دوزد تا آن را به منزلگاهی بهتر مبدل سازد، پس برای جایی که او را می‌برند، و برای شناسایی سرای دیگر تلاش کند، خوشا بحال کسی که قلبی سالم دارد، خدای هدایتگر را اطاعت می‌کند، از شیطان گمراه کننده دوری می‌گزیند، با راهنمایی مردان الهی با آگاهی به راه سلامت \nرسیده، و به اطاعت هدایتگرش بپردازد، و به راه رستگاری پیش از آنکه درها بسته شود، و وسائلش قطع گردد بشتابد، در توبه را بگشاید، و گناهان را از بین ببرد. پس به راه راست ایستاده، و به راه حق هدایت شده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (130, 1, 215, 'نیایش', 'خطبه ۲۱۵-نیایش \nستایش و نیایش \nستایش خداوندی را سزاست که شبم را به صبح آورد بی آنکه مرده یا بیمار باشم، نه دردی بر رگهای تنم باقی، و نه به کیفر بدترین کردارم گرفتار کرد، نه بی فرزند و خاندان، و نه از دین رویگردان، و نه منکر پروردگارم، نه ایمانم دگرگون، و نه عقلم آشفته، و نه به عذاب امتهای گذشته گرفتارم. در حالی صبح کردم که بنده ای بی اختیار و بر نفس خود ستمکارم. خدایا تو راست که مرا محکوم فرمایی در حالی که عذری ندارم، و توان فراهم آوردن چیزی جز آنچه که تو می‌بخشایی ندارم، و قدرت حفظ خویش ندارم جز آن که تو مرا حفظ فرمایی. خدایا به تو پناه می‌برم از آنکه در سایه بی نیازی تو، تهیدست باشم، یا در پرتو روشنایی هدایت تو گمراه گردم، یا در پناه قدرت تو بر من ستم روا دارند، یا خوار و ذلیل باشم در حالی که کار در دست تو باشد. خدایا ما به تو پناه می‌بریم از آنکه از فرموده تو بیرون شویم، یا از دین تو خارج گردیم، یا هواهای نفسانی پیاپی بر ما فرود آید، که از هدایت ارزانی شده از جانب تو سر باز زنیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (131, 1, 216, 'خطبه‌ای که در صفین خواند', 'خطبه ۲۱۶-خطبه ای در صفین \nحقوق اجتماعی \n(پس از ستایش پروردگار!) \nخداوند سبحان، برای من، بر شما به جهت سرپرستی حکومت، حقی قرار داده، و برای شما همانند حق من، حقی تعیین فرموده است، پس حق گسترده تر از آن است که توصیفش کنند، ولی به هنگام عمل تنگنایی بی مانند دارد. حق اگر به سود کسی اجراء شود، ناگزیر به زیان او نیز روزی به کار رود، و چون به زیان کسی اجراء شود روزی به سود او جریان خواهد داشت. اگر بنا باشد حق به سود کسی اجراء شود و زیانی نداشته باشد، این مخصوص خدای سبحان است نه دیگر آفریده‌ها، به خاطر قدرت الهی بر بندگان، و عدالت او بر تمام موجوداتی که فرمانش بر آن‌ها جاری است، لکن خداوند حق خود را بر بندگان، اطاعت خویش قرار داده، و پاداش آن را دوچندان کرده است، از روی بخشندگی، و گشایشی که خواسته به بندگان عطا فرماید. حقوق متقابل رهبری و مردم پس خدای سبحان! برخی از حقوق خود را برای بعضی از مردم واجب کرد، و آن حقوق را در برابر هم گذاشت، که برخی از حقوق برخی دیگر را واجب گرداند، و حقی بر کسی واجب نمی شود مگر همانند آن را انجام دهد. و در میان حقوق الهی بزرگترین حق، حق رهبر بر مردم، و حق مردم بر رهبر است، حق واجبی که خدای سبحان، بر هر \nدو گروه لازم شمرد، و آن را عامل پایداری پیوند ملت و رهبر، و عزت دین قرار داد. پس رعیت اصلاح نمی شود جز آنکه زمامداران اصلاح گردند، و زمامداران اصلاح نمی شوند جز با درستکاری رعیت. و آنگاه که مردم حق رهبری را ادا کنند، و زمامدار حق مردم را بپردازد، حق در آن جامعه عزت یابد، و راه‌های دین پایدار، و نشانه‌های عدالت برقرار، و سنت پیامبر (ص) پایدار گردد، پس روزگار اصلاح شود، و مردم در تداوم حکومت امیدوار، و دشمن در آرزوهایش مایوس می‌شود. اما اگر مردم بر حکومت چیره شوند، یا زمامدار بر رعیت ستم کند، وحدت کلمه از بین می‌رود، نشانه‌های ستم آشکار، و نیرنگبازی در دین فراوان می‌گردد. و راه گسترده سنت پیامبر (ص) متروک، هواپرستی فراوان، احکام دین تعطیل، و بیماریهای دل فراوان گردد، مردم از اینکه حق بزرگی فراموش می‌شود، یا باطل خطرناکی در جامعه رواج می‌یابد، احساس نگرانی نمی کنند، پس در آن زمان نیکان خوار، و بدان قدرتمند می‌شوند، و کیفر الهی بر بندگان بزرگ و دردناک خواهد بود. پس بر شماست که یکدیگر را نصیحت کنید، و نیکو همکاری نمایید. درست است که هیچ کس نمی تواند حق اطاعت خداوندی را چنانکه باید بگذارد، هر چند در به دست آوردن رض \nای خدا حریص باشد، و در کار بندگی تلاش فراوان نماید، لکن باید به مقدار توان، حقوق الهی را رعایت کند که یکی از واجبات الهی، یکدیگر را به اندازه توان نصیحت کردن، و برپا داشتن حق، و یاری دادن به یکدیگر است. هیچ کس هر چند قدر او در حق بزرگ، و ارزش او در دین بیشتر باشد، بی نیاز نیست که او را در انجام حق یاری رسانند، و هیچ کس گرچه مردم او را خوار شمارند، و در دیده‌ها بی ارزش باشد، کوچکتر از آن نیست که کسی را در انجام حق یاری کند یا دیگری به یاری او برخیزد. (پس یکی از یاران بپا خواست و با سخنی طولانی امام را ستود، حرف شنوایی و اطاعت از امام را اعلام داشت آنگاه امام فرمود:) \nکسی که عظمت خدا در جانش بزرگ، و منزلت او در قلبش والاست، سزاوار است که هر چه جز خدا را کوچک شمارد، و از او سزاوارتر کسی که نعمتهای خدا را فراوان در اختیار دارد، و بر خان احسان خدا نشسته است، زیرا نعمت خدا بر کسی بسیار نگردد جز آنکه حقوق الهی بر او فراوان باشد. روابط سالم و متقابل رهبر و مردم مردم! از پست ترین حالات زمامداران در نزد صالحان این است که گمان برند آن‌ها دوستدار ستایش باشند، و کشورداری آنان بر کبر و خودپسندی استوار باشد، و خوش ندارم، در خاطر شما بگذرد که من ستایش را دوست دارم، و خواهان شنیدن آن می‌باشم، سپاس خدا را که چنین نبودم، و اگر ستایش را دوست می‌داشتم، آن را رها می‌کردم به خاطر فروتنی در پیشگاه خدای سبحان، و بزرگی و بزرگواری که تنها خدا سزاوار آن است. گاهی مردم، ستودن افرادی را برای کار و تلاش روا می‌دانند. اما من از شما می‌خواهم که مرا با سخنان زیبای خود مستایید، تا نفس خود را به وظائفی که نسبت به خدا و شما دارم خارج سازم، و حقوقی که مانده است بپردازم، و واجباتی که بر عهده من است و باید انجام گیرد اداء کنم، پس با من چنانکه با پادشاهان سرکش سخن می‌گویند، حرف نزنید، و چنانکه از \nآدمهای خشمگین کناره می‌گیرند دوری نجویید، و با ظاهرسازی با من رفتار نکنید، و گمان مبرید اگر حقی به من پیشنهاد دهید بر من گران آید، یا در پی بزرگ نشان دادن خویشم. زیرا کسی که شنیدن حق، یا عرضه شدن عدالت بر او مشکل باشد، عمل کردن به آن دشوارتر خواهد بود. پس، از گفتن حق، یا مشورت در عدالت خودداری نکنید، زیرا خود را برتر از آنکه اشتباه کنم و از آن ایمن باشم نمی دانم، مگر آنکه خداوند مرا حفظ فرماید، پس همانا من و شما بندگان و مملوک پروردگاریم که جز او پروردگاری نیست، او مالک ما، و ما را بر نفس خود اختیاری نیست، ما را در آنچه بودیم خارج و بدانچه صلاح ما بود درآورد، به جای گمراهی هدایت، و بجای کوری بینایی به ما عطا فرمود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (132, 1, 217, 'گله از قریش', 'خطبه ۲۱۷-گله از قریش \nشکوه از قریش \nخدایا من بر ضد قریش، و یارانشان از تو کمک می‌خواهم، که پیوند خویشاوندی مرا بریدند، و کار مرا دگرگون کردند، و همگی برای مبارزه با من در حقی که از همه آنان سزاوارترم، متحد گردیدند. و گفتند: (حق را اگر توانی بگیر، و یا تو را از حق محروم دارند، پس یا با غم و اندوه صبر کن، یا با حسرت بمیر.) به اطرافم نگریستم دیدم که نه یاوری دارم، و نه کسی از من دفاع و حمایت می‌کند، جز خانواده ام که مایل نبودم جانشان به خطر افتد، پس خار در چشم فرو رفته، دیده برهم نهادم، و با گلوی استخوان در آن گیر کرده، جام تلخ را جرعه جرعه نوشیدم، و در فرو خوردن خشم در امری که تلخ تر از گیاه حنظل، و دردناک تر از فرو رفتن تیزی شمشیر در دل بود شکیبایی کردم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (133, 1, 218, 'در مورد روندگان به بصره برای جنک با او', 'خطبه ۲۱۸- کسانی برای جنگ با حضرت به بصره رفتند \nافشای خیانت ناکثان \nبر کار گذاران و خزانه داران بیت المال مسلمانان که در فرمان من بودند، و بر مردم شهری که تمامی آن‌ها بر اطاعت من، و وفاداری در بیعت با من وحدت داشتند، هجوم آوردند، آنان را از هم پراکندند، و به زبان من در میانشان اختلاف افکندند، و بر شیعیان من تاختند، گروهی را با نیرنگ کشتند، و گروهی دست بر شمشیر فشرده با دشمن جنگیدند تا صادقانه خدا را ملاقات کردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (134, 1, 219, 'هنگام گذشتن از کشته شدگان جمل', 'خطبه ۲۱۹-عبور از کشته شدگان جمل \n(در می‌دان نبرد جمل در سال ۳۶ هجری وقتی به جنازه طلحه و عبد الرحمن بن عتاب رسید فرمود) \nتاسف بر کشتگان جمل \nابومحمد (طلحه) در این مکان، غریب مانده است، بخدا سوگند خوش نداشتم قریش را زیر تابش ستارگان کشته و افتاده بینم، به خونخواهی بر فرزندان (عبدمناف) دست یافتم، ولی سرکردگان (بنی جمح) از دستم گریختند، آنان برای کاری که در شان آن‌ها نبود سر برافراشتند، و پیش از رسیدن به آن سرکوب شدند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (135, 1, 22, 'در نکوهش بیعت شکنان', 'خطبه ۲۲-در نکوهش بیعت شکنان \nامام (ع) و شناساندن ناکثین (اصحاب جمل) \nآگاه باشید، که همانا شیطان حزب و یارانش را بسیج کرده، و سپاه خود را از هر سو فراهم آورده است، تا بار دیگر ستم را به جای خود نشاند، و باطل به جایگاه خویش پایدار شود. سوگند به خدا ناکثین هیچ گناهی از من سراغ ندارند، و انصاف را بین من و خودشان رعایت نکردند، آن‌ها حقی را می‌طلبند که خود ترک کردند، و انتقام خونی را می‌خواهند که خود ریختند، اگر شریک آن‌ها بودم، پس آن‌ها نیز در این خونریزی سهم دارند، و اگر تنها خودشان خون عثمان را ریختند پس کیفر مخصوص آن‌هاست، مهمترین دلیل آن‌ها به زیان خودشان است، می‌خواهند از پستان مادری شیر بدوشند که خشکیده، بدعتی را زنده می‌کنند که مدتهاست مرده، وه چه دعوت کننده ای!؟ و چه اجابت کنندگانی؟! همانا من به کتاب خدا و فرمانش درباره ناکثین خشنودم. اما اگر از آن سر باز زدند با شمشیر تیز پاسخ آن‌ها را خواهم داد، که برای درمان باطل و یاری دادن حق کافی است. شگفتا! از من خواستند به می‌دان نبرد آیم و برابر نیزه‌های آنان قرار گیرم و ضربتهای شمشیر آن‌ها را تحمل کنم، گریه کنندگان بر آن‌ها بگریند، تاکنون کسی مرا از جنگ نترسانده، و از ضربت شمشیر نهراسانده است، من به پروردگار خویش یقین داشته و در دین خود شک و تردیدی ندارم.', 'و من خطبة له (علیه السلام) حين بلغه خبر الناكثين ببيعته و فيها يذمّ عملهم و يلزمهم دَم عثمان و يتهددهم بالحرب:ذمّ الناكثين:\r\nأَلَا وَ إِنَّ الشَّيْطَانَ قَدْ ذَمَّرَ حِزْبَهُ وَ اسْتَجْلَبَ جَلَبَهُ لِيَعُودَ الْجَوْرُ إِلَى أَوْطَانِهِ وَ يَرْجِعَ الْبَاطِلُ إِلَى نِصَابِهِ وَ اللَّهِ مَا أَنْكَرُوا عَلَيَّ مُنْكَراً وَ لَا جَعَلُوا بَيْنِي وَ بَيْنَهُمْ نَصِفاً.دَم عثمان:\r\nوَ إِنَّهُمْ لَيَطْلُبُونَ حَقّاً هُمْ تَرَكُوهُ وَ دَماً هُمْ سَفَكُوهُ، [فَإِنْ] فَلَئِنْ كُنْتُ شَرِيكَهُمْ فِيهِ فَإِنَّ لَهُمْ لَنَصِيبَهُمْ مِنْهُ وَ [إِنْ] لَئِنْ كَانُوا وَلُوهُ دُونِي فَمَا التَّبِعَةُ إِلَّا عِنْدَهُمْ وَ إِنَّ أَعْظَمَ حُجَّتِهِمْ لَعَلَى أَنْفُسِهِمْ. يَرْتَضِعُونَ أُمّاً قَدْ فَطَمَتْ وَ يُحْيُونَ بِدْعَةً قَدْ أُمِيتَتْ، يَا خَيْبَةَ الدَّاعِي مَنْ دَعَا وَ إِلَامَ أُجِيبَ وَ إِنِّي لَرَاضٍ بِحُجَّةِ اللَّهِ عَلَيْهِمْ وَ عِلْمِهِ فِيهِمْ.التهديد بالحرب:\r\nفَإِنْ أَبَوْا أَعْطَيْتُهُمْ حَدَّ السَّيْفِ وَ كَفَى بِهِ شَافِياً مِنَ الْبَاطِلِ وَ نَاصِراً لِلْحَقِّ وَ مِنَ الْعَجَبِ بَعْثُهُمْ [بَعْثَتُهُمْ] إِلَيَّ أَنْ أَبْرُزَ لِلطِّعَانِ وَ أَنْ أَصْبِرَ لِلْجِلَادِ هَبِلَتْهُمُ الْهَبُولُ لَقَدْ كُنْتُ وَ مَا أُهَدَّدُ بِالْحَرْبِ وَ لَا أُرْهَبُ بِالضَّرْبِ وَ إِنِّي لَعَلَى يَقِينٍ مِنْ رَبِّي وَ غَيْرِ شُبْهَةٍ مِنْ دِينِي.', NULL, '(در سال 36 هجرى پس از بازگشت فرستادگان امام عليه السّلام از جانب طلحه و زبير، در سرزمين «ذى قار» اين سخنرانى را ايراد كرد).\r\nامام (عليه السّلام) و شناساندن ناكثين (اصحاب جمل):\r\nآگاه باشيد، كه همانا شيطان حزب و يارانش را بسيج كرده و سپاه خود را از هر سو فراهم آورده است، تا بار ديگر ستم را به جاى خود نشاند و باطل به جايگاه خويش پايدار شود.\r\nسوگند به خدا ناكثين هيچ گناهى از من سراغ ندارند، و انصاف را بين من و خودشان رعايت نكردند.آنها حقّى را مى طلبند كه خود ترك كردند، و انتقام خونى را مى خواهند كه خود ريختند.\r\nاگر شريك آنها بودم، پس آنها نيز در اين خونريزى سهم دارند، و اگر تنها خودشان خون عثمان را ريختند پس كيفر مخصوص آنهاست مهم ترين دليل آنها به زيان خودشان است.\r\nمى خواهند از پستان مادرى شير بدوشند كه خشكيده، بدعتى را زنده مى كنند كه مدّت ها است مرده، و چه دعوت كننده اى و چه اجابت كنندگانى. همانا من به كتاب خدا و فرمانش در باره ناكثين خشنودم.امّا اگر از آن سرباز زدند با شمشير تيز پاسخ آنها را خواهم داد، كه براى درمان باطل و يارى دادن حق كافى است.\r\nشگفتا از من خواستند به ميدان نبرد آيم و برابر نيزه هاى آنان قرار گيرم و ضربت هاى شمشير آنها را تحمّل كنم.\r\nگريه كنندگان بر آنها بگريند، تا كنون كسى مرا از جنگ نترسانده و از ضربت شمشير نهراسانده است، من به پروردگار خويش يقين داشته و در دين خود شك و ترديدى ندارم.', NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (136, 1, 220, 'در وصف سالکان', 'خطبه ۲۲۰-در وصف سالکان \nپوینده راه خدا \nعقلش را زنده، و نفس خویش را کشته است، تا آنکه جسمش لاغر، و خشونت اخلاقش به نرمی گرایید، برقی پرنور برای او درخشید، و راه را برای او درخشید، و راه را برای او روشن کرد، و در راه راست او را کشاند، و از دری به در دیگر برد تا به در سلامت، و سرای جاودانه رساند، که دو پای او در قرارگاه امن با آرامش تن، استوار شد، پاداش آن بود که دل را درست بکار گرفت، و پروردگار خویش را راضی کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (137, 1, 221, 'در ترغیب یارانش به جهاد', 'خطبه ۲۲۱-تلاوت الهیکم التکاثر \n(پس از خواندن آیه ۱ سوره تکاثر: افزون طلبی شما را به خود مشغول داشته تا آنجا که به دیدار قبرها رفتید. فرمود) \nهشدار از غفلتزدگی‌ها \nشگفتا چه مقصد بسیار دوری، و چه زیارت کنندگان بیخبری! و چه کار دشوار و مرگباری! پنداشتند که جای مردگان خالی است، آن‌ها که سخت مایه عبرتند، و از دور با یاد گذشتگان، فخر می‌فروشند، آیا به گورهای پدران خویش می‌نازند؟ و یا به تعداد فراوانی که در کام مرگ فرو رفته اند؟ آیا خواهان بازگشت اجسادی هستند که پوسیده شد؟ و حرکاتشان به سکون تبدیل گشت؟ آن‌ها مایه عبرت باشند سزاوارتر است تا تفاخر! اگر با مشاهده وضع آنان به فروتنی روی آورند عاقلانه تر است تا آنان را وسیله فخرفروشی قرار دهند. اما بدنها با دیده‌های کم سو نگریستند، و با کوته بینی در امواج نادانی فرو رفته اند، اگر حال آنان را از ورای خانه‌های ویران، و سرزمینهای خالی از زندگان می‌پرسیدند، هرآینه می‌گفتند: (آنان با گمراهی در زمین فرو خفتند، و شما ناآگاهانه دنباله روی آنان شدید.) بر روی کاسه‌های سر آن‌ها راه می‌روید. و بر روی جسدهایشان زراعت می‌کنید؟ و آنچه بجا گذاشته‌اند می‌خورید، و در خانه‌های ویران آن‌ها مسکن گرفتید، و روزگاری که میان آن‌ها و شماست بر شما زاری می‌کند، آن‌ها پیش از شما بکام مرگ فرو رفتند، و زودتر از شما به آبشخورتان رسیدند. شرح حالات رفتگان آن‌ها دارای عزت پایدار، و درجات والای افتخار، پادشاهان حاکم، یا رعیت سرفراز بودند که سرانجام راه به ژرفای برزخ پیمودند، و زمین آن‌ها را در خود گرفت، و از گوشت بدنهای آنان خورد، و از خون آنان نوشید. پس در شکاف گورها بی جان و بدون رشد پنهان مانده اند. نه از دگرگونیها اندوهناک، و نه از زلزله‌ها ترسناک، و نه از فریادهای سخت هراسناکند، غائب شدگانی که کسی انتظار آنان را نمی کشد، و حاضرانی که حضور نمی یابند، اجتماعی داشتند و پراکنده شدند، با یکدیگر الفتی داشتند و جدا گردیدند. اگر یادشان فراموش شد، یا دیارشان ساکت شد، برای درازی زمان و دوری مکان نیست، بلکه کاسه مرگ نوشیدند، گویا بودند و گنگ شدند، شنوا بودند و کر گشتند، و حرکاتشان به سکون تبدیل شد، چنان آرمیدند که گویا بیهوش بر خاک افتاده و در خواب فرو رفته اند. \nهمسایگانی که با یکدیگر انس نمی گیرند، دوستانی که به دیدار یکدیگر نمی روند، پیوندهای شناسایی در میانشان پوسیده، و اسباب برادری قطع گردیده است، با اینکه در یک جا گرد آمدند تنهایند، رفیقان یکدیگرند و از هم دورند، نه برای شب صبحگاهی می‌شناسند، و نه برای روز شامگاهی، شب، یا روزی که به سفر مرگ رفته‌اند برای آن‌ها جاویدان است. خطرات آن جهان را وحشتناک تر از آنچه می‌ترسیدند یافتند، و نشانه‌های آن را بزرگ تر از آن چه می‌پنداشتند مشاهده کردند، و برای رسیدن به بهشت یا جهنم تا قرارگاه اصلی شان مهلت داده شدند، و جهانی از بیم و امید برایشان فراهم آمد، اگر می‌خواستند آنچه را که دیدند توصیف کنند، زبانشان عاجز می‌شد. حال اگر آثارشان نابود شد، و اخبارشان فراموش، اما چشمهای عبرت بین، آن‌ها را می‌نگرد، و گوش جان اخبارشان را می‌شنود، که با زبان دیگری با ما حرف می‌زنند و می‌گویند: پیام مردگان! چهره‌های زیبا پژمرده، بدنهای نازپرورده پوسیده شد، و بر اندام خود لباس کهنگی پوشانده ایم، و تنگی قبر ما را در فشار گرفته، وحشت و ترس را از یکدیگر به ارث می‌بریم. خانه‌های خاموش قبر بر ما فرو ریخته، و زیباییهای اندام ما را نابود، \nو نشانه‌های چهره‌های ما را دگرگون کرده است، اقامت ما در این خانه‌های وحشتزا طولانی شده، نه از مشکلات رهایی یافته، و نه از تنگی قبر گشایشی فراهم شد. مردم! اگر آن‌ها را در اندیشه خود بیاورید، یا پرده‌ها کنار رود، مردگان را در حالتی می‌نگرید که حشرات گوشهایشان را خورده، چشمهایشان به جای سرمه پر از خاک گردیده، و زبانهایی که با سرعت و فصاحت سخن می‌گفتند پاره پاره شده، قلبها در سینه‌ها پس از بیداری به خاموشی گرایید، و در تمام اعضای بدن پوسیدگی تازه ای آشکار شد، و آن‌ها را زشت گردانید، و راه آفت زدگی بر اجسادشان گشوده شد، همه تسلیم شده، نه دستی برای دفاع، و نه قلبی برای زاری دارند. و آنان را می‌بینی که دلهای خسته از اندوه، و چشمهای پرشده از خاشاک دارند، در حالات اندوهناک آن‌ها دگرگونی ایجاد نمی شود و سختی‌های آنان برطرف نمی گردد. \nعبرت از گذشتگان آه! زمین چقدر اجساد عزیزان خوش سیما را که با غذاهای لذیذ و رنگین زندگی کردند، و در آغوش نعمتها پرورانده شدند بکام خویش فرو برد، آنان که می‌خواستند با شادی غمها را از دل برون کنند، و به هنگام مصیبت با سرگرمیها، صفای عیش خود را بر هم نزنند، دنیا به آن‌ها و آن‌ها به دنیا می‌خندیدند، و در سایه خوشگذرانی غفلت زا، بی خبر بودند که روزگار با خارهای مصیبت زا آن‌ها را درهم کوبید و گذشت روزگار توانایی شان را گرفت، مرگ از نزدیک به آن‌ها نظر دوخت، و غم و اندوهی که انتظارش را نداشتند آنان را فرا گرفت، و غصه‌های پنهانی که خیال آن را نمی کردند در جانشان راه یافت، در حالی که با سلامتی انس داشتند انواع بیماریها در پیکرشان، پدید آمد، و هراسناک به اطبا، که آموزش دادند گرمی را با سردی، و سردی را با گرمی درمان کنند روی آوردند که بی نتیجه بود زیرا، داروی سردی، گرمی را علاج نکرد، و آنچه برای گرمی بکار بردند، سردی را بیشتر ساخت، و ترکیبات و اخلاط مزاج را به اعتدال نیاورد، جز آنکه آن بیماری را فزونی داد، تا آنجا که درمان کنند، خسته، و پرستار سرگردان، و خانواده از ادامه بیماریها سست و ناتوان شدند، و از پاسخ پر \nسش کنندگان درماندند، و درباره همان خبر حزن آوری که از او پنهان می‌داشتند در حضورش به گفتگو پرداختند. سختی‌های لحظه مرگ یکی می‌گفت تا لحظه مرگ بیمار است، دیگری در آرزوی شفا یافتن بود، و سومی خاندانش را به شکیبایی در مرگش دعوت می‌کرد، و گذشتگان رابه یاد می‌آورد، در آنحال که در آستانه مرگ، و ترک دنیا، و جدایی با دوستان بود که ناگهان اندوهی سخت به او روی آورد، فهم و درکش را گرفت، زبانش به خشکی گرایید، چه مطالب مهمی را می‌بایست بگوید که زبانش از گفتن آن‌ها باز ماند، و چه سخنان دردناکی را می‌شنید و خود را به کری می‌زد، از شخص بزرگی که احترامش را نگه می‌داشت، یا فرد کوچکی که به او ترحم می‌کرد، همانا مرگ سختی‌هایی دارد که هراس انگیز و وصف ناشدنی است، و برتر از آن است که عقلهای اهل دنیا آن را درک کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (138, 1, 222, 'تلاوت رجال لا تلهیهم...', 'خطبه ۲۲۲-تلاوت رجال لا تلهیهم... \nارزش یاد خدا \nهمانا خدای سبحان و بزرگ، یاد خود را روشنی بخش دلها قرار داد، تا گوش پس از ناشنوایی بشنود، و چشم پس از کم نوری بنگرد، و انسان پس از دشمنی رام گردد، خداوند که نعمتهای او گرانقدر است، در دورانهای مختلف روزگار، و در دوران فترت (تا آمدن پیامبری پس از پیامبر دیگر) بندگانی داشته که با آنان در گوش جانشان زمزمه می‌کرد، و در درون عقلشان با آنان سخن می‌گفت. که چراغ هدایت را با نور بیداری در گوشها و دیده‌ها و دلها برافروختند، روزهای خدایی را به یاد می‌آوردند و مردم را از جلال و بزرگی خدا می‌ترساندند، آنان نشانه‌های روشن خدا در بیابانهایند، آن را که راه میانه در پیش گرفت می‌ستودند، و به رستگاری بشارت می‌دادند، و آن را که به جانب چپ یا راست کشانده می‌شد، روش او را زشت می‌شمردند، و از نابودی هشدار می‌دادند، همچنان چراغ تاریکیها، و راهنمای پرتگاهها بودند. همانا! یاد خدا را مردمی است که آن را بجای دنیا برگزیدند، که هیچ تجارتی یا خرید و فروش، آن‌ها را از یاد خدا باز نمی دارد، با یاد خدا روزگار می‌گذرانند، و غافلان را با هشدارهای خود، از کیفرهای الهی می‌ترسانند. به عدالت فرمان می‌دهند، و خود عدالت گست \nرند، از بدیها نهی می‌کنند و خود از آن‌ها پرهیز دارند. با اینکه در دنیا زندگی می‌کنند گویا آن را رها کرده به آخرت پیوسته اند، سرای دیگر را مشاهده کرده، گویا از مسائل پنهان برزخیان و مدت طولانی اقامتشان آگاهی دارند، و گویا قیامت وعده‌های خود را برای آنان تحقق بخشیده است، آنان پرده‌ها را برای مردم دنیا برداشته اند، می‌بینند آنچه را که مردم نمی نگرند، و می‌شنوند آنچه را که مردم نمی شنوند، اگر اهل ذکر را در اندیشه خود آوری، و مقامات ستوده آنان، و مجالس آشکارشان را بنگری، می‌بینی که آنان نامه‌های اعمال خود را گشوده، و برای حسابرسی آماده‌اند که همه را جبران کردند. اعمال کوچک و بزرگی که به آنان فرمان داده شدند و کوتاهی کردند، یا اعمالی که از آن نهی شده و مرتکب گردیدند، بار سنگین گناهان خویش را بر دوش نهاده، و در برداشتن آن ناتوان شدند، گریه در گلویشان شکسته، و با ناله و فریاد می‌گریند و با یکدیگر گفتگو دارند، در پیشگاه پروردگار خویش به پشیمانی اعتراف دارند. آن‌ها نشانه‌های هدایت، و چراغهای روشنگر تاریکیها می‌باشند، فرشتگان آنان را در میان گرفته، و آرامش بر آن‌ها می‌بارند، درهای آسمان برویشان گشوده، و مقام ارزشمندی برای \nآنان آماده کرده اند، مقامی که خداوند با نظر رحمت به آن می‌نگرد، و از تلاش آن‌ها راضی، و منزلت آن‌هارا می‌ستاید، دست به دعا برمی دارند، و آمرزش الهی می‌طلبند، در گرو نیازمندی فضل خدا، و اسیران بزرگی اویند، غم و اندوه طولانی دلهایشان را مجروح، و گریه‌های پیاپی چشمهایشان را آزرده است، دست آنان به طرف تمام درهای امیدواری خدا دراز است، از کسی درخواست می‌کنند که بخشش او را کاستی، و درخواست کنندگان او را نومیدی نیست. پس اکنون به خاطر خودت، حساب خویش را بررسی کن زیرا دیگران حسابرسی غیر از تو دارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (139, 1, 223, 'تلاوت یا ایها الانسان...', 'خطبه ۲۲۳-تلاوت یا ایها الانسان... \nهشدار از غرور زدگیها \nبرهان گناهکار، نادرست ترین برهان‌ها است، و عذرش از توجیه هر فریب خورده ای بی اساس تر، و خوشحالی او از عدم آگاهی است. ای انسان! چه چیز تو را بر گناه جرات داده؟ و در برابر پروردگار مغرور ساخته؟ و بر نابودی خود علاقمند کرده است؟ آیا بیماری تو را درمان نیست؟ و خوابزدگی تو بیداری ندارد؟ چرا آنگونه که به دیگران رحم می‌کنی، به خود رحم نمی کنی؟ چه بسا کسی را در آفتاب سوزان می‌بینی بر او سایه می‌افکنی یا بیماری را بنگری که سخت ناتوان است، از روی دلسوزی بر او اشک می‌ریزی، چه چیز تو را بر بیماری خود بی تفاوت کرده؟ و بر مصیبتهای خود شکیبا و از گریه بر حال خویشتن باز داشته است؟ در حالی که هیچ چیز برای تو عزیزتر از جانت نیست! چگونه ترس از فرود آمدن بلا، شب هنگام تو را بیدار نکرده است؟ که در گناه غوطه ور، و در پنجه قهر الهی مبتلا شده ای؟ چگونه بودن پس سستی دل را با استقامت درمان کن، و خوابزدگی چشمانت را با بیداری برطرف کن، و اطاعت خدا را بپذیر، و با یاد خدا انس گیر، و یاد آر که تو از خدا روی گردانی و در همان لحظه او روی به تو دارد، و تو را به عفو خویش می‌خواند، و با کرم خویش می‌پوشاند! در حالی که تو از خدا بریده و به غیر خدا توجه داری! پس چه نیرومند و بزرگوار است خدا! و چه ناتوان و بی مقداری تو، که بر عصیان او جرات داری، در حالی که تو را در پرتو نعمت خود قرار داده، و در گستره رحمت او آرمیده ای، نه بخشش خود را از تو گرفته، و نه پرده اسرار تو را دریده است. بلکه چشم بر هم زدنی، بی احساس خدا زنده نبودی، یا در نعمت‌های او غرق بودی، یا گناهان تو پرده پوشی شد، و یا بلا و مصیبتی را از تو دور ساخته است، پس چه فکر می‌کنی! اگر او را اطاعت کنی؟ بخدا سوگند! اگر این رفتار میان دو نفر که در توانایی و قدرت برابر بودند وجود داشت تو نخستین کسی بودی که خود را محکوم می‌کردی، بر زشتی اخلاق و نادرستی کردار. دنیاشناسی به حق می‌گویم که دنیا تو را فریفته است، که تو خود فریفته دنیایی، دنیا عبرت‌ها را برای تو آشکار کرده، و تو را به تساوی دعوت کرد، دنیا با دردهایی که در جسم تو می‌گذارد، و با کاهشی که در توانایی تو ایجاد می‌کند، راستگوتر از آن است که به تو دروغ بگوید، و یا مغرورت سازد. چه بسا نصیحت کننده ای از سوی دنیا را متهم نمودی، و راستگویی را دروغگو پنداشتی، اگر دنیا را از روی شهرهای ویران شده، و خانه‌های درهم فرو ریخته بشناسی، آن را یادآوری دلسوز، و اندرزدهنده ای گویا می‌یابی که چونان دوستی مهربان از تباهی و نابودی تو نگران است. دنیا چه خوب خانه ای است برای آن کس که آنرا جاودانه نپندارد، و خوب محلی است برای آن کس که آن را وطن خویش انتخاب نکند، سعادتمندان به وسیله دنیا در قیامت کسانی هستند که امروز از آن می‌گریزند. انسان و رستاخیز آنگاه که زمین سخت بلرزد، و نشانه‌های هولناک قیامت تحقق پذیرد، و پیروان هر دینی به آن ملحق شوند، و هر پرستش کننده به معبود خود، و هر اطاعت کننده ای به فرمانده خود رسد، نه چشمی بر خلاف عدالت و برابری در هوا گشوده، و نه قدمی برخلاف حق، آهسته در زمین نهاده می‌شود، در آن روز چه دلیلهایی که باطل می‌گردد، و عذرهایی که پذیرفته نمی شود! پس در جستجوی عذری باشید که پذیرفته شود، و دلیلی بجویید که استوار باشد، و از دنیای فانی برای آخرت جاویدان توشه بردار، و برای سفر آخرت وسائل لازم را آماده کن، و چشم به برق نجات بدوز، و بار سفر بربند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (140, 1, 224, 'پارسائی علی و سرگذشت عقیل', 'خطبه ۲۲۴-پارسائی علی \nپرهیز از ستمکاری \nسوگند بخدا! اگر بر روی خارهای سعدان به سر ببرم، و یا با غل و زنجیر به این سو یا آن سو کشیده شوم، خوش تر دارم تا خدا و پیامبرش را در روز قیامت، در حالی ملاقات کنم که به بعضی از بندگان ستم، و چیزی از اموال را غصب کرده باشم، چگونه بر کسی ستم کنم برای نفس خویش، که به سوی کهنگی و پوسیده شدن پیش می‌رود، و در خاک، زمان طولانی اقامت می‌کند. پرهیز از امتیازخواهی بخدا سوگند، برادرم عقیل را دیدم که به شدت تهیدست شده و از من درخواست داشت تا یک من از گندمهای بیت المال را به او به خشم (ببخشم)، کودکانش را دیدم که از گرسنگی دارای موهای ژولیده، و رنگشان تیره شده بود گویا با نیل رنگ شده بودند. پی در پی مرا دیدار و درخواست خود را تکرار می‌کرد، چون گفته‌های او را گوش فرادادم پنداشت که دین خود را به او واگذار می‌کنم، به دلخواه او رفتار و از راه و رسم عادلانه خود دست برمی دارم، روزی آهنی را در آتش گداخته به جسمش نزدیک کردم تا او را بیازمایم، پس چونان بیمار از درد فریاد زد ونزدیک بود از حرارت آن بسوزد. به او گفتم، ای عقیل: گریه کنندگان بر تو بگریند، از حرارت آهنی می‌نالی که انسانی به بازیچه آن را گرم ساخته است؟ اما مرا به آتش دوزخی می‌خوانی که خدای جبارش با خشم خود آن را گداخته است، تو از حرارت ناچیز می‌نالی و من از حرارت آتش الهی ننالم؟ و از این حادثه شگفت آورتر اینکه شب هنگام کسی به دیدار ما آمد و ظرفی سرپوشیده پر از حلوا داشت، معجونی در آن ظرف بود که از آن تنفر داشتم، گویا آن را با آب دهان مار سمی، یا قی کرده آن مخلوط کردند، به او گفتم: هدیه است؟ یا زکات یا صدقه؟ که این دو بر ما اهل بیت پیامبر (ص) حرام است. گفت: نه، زکات است نه صدقه، بلکه هدیه است. گفتم: زنان بچه مرده بر تو بگریند، آیا از راه دین وارد شدی که مرا بفریبی؟ یا عقلت آشفته شده یا جن زده شدی؟ یا هذیان می‌گویی؟ بخدا سوگند! اگر هفت اقلیم را با آنچه در زیر آسمانهاست به من دهند تا خدا را نافرمانی کنم که پوست جوای را از مورچه ای ناروا بگیرم، چنین نخواهم کرد، و همانا این دنیای آلوده شما نزد من از برگ جویده شده دهان ملخ پست تر است علی را با نعمتهای فناپذیر، و لذتهای ناپایدار چه کار؟!! بخدا پناه می‌بریم از خفتن عقل، و زشتی لغزشها، و از او یاری می‌جوییم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (141, 1, 225, 'نیایش به خدا', 'خطبه ۲۲۵-نیایش به خدا \nیاری خواستن از خدا در مشکلات اقتصادی \nخدایا آبرویم را با بی نیازی نگهدار، و با تنگدستی شخصیت مرا لکه دار مفرما، که از روزی خواران تو روزی خواهم، و از آدمهای بدکردار عفو و بخشش طلبم، مرا در ستودن آن کس که به من عطایی فرمود موفق فرما، و در نکوهش آن کس که از من دریغ داشت آزمایش گردم، در صورتیکه در پشت پرده، اختیار هر بخشش و دریغی در دست تو است و تو بر همه چیز توانایی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (142, 1, 226, 'نکوهش دنیا', 'خطبه ۲۲۶-نکوهش دنیا \nدنیاشناسی \nدنیا خانه ای است پوشیده از بلاها، در حیله و نیرنگ شناخته شده، نه حالات آن پایدار، و نه مردم آن از سلامت برخوردارند، دارای تحولات گوناگون، و دورانهای رنگارنگ، زندگی در آن نکوهیده و امنیت در آن نابود است، اهل دنیا همواره هدف تیرهای بلا هستند که با تیرهایش آن‌ها را می‌کوبد، و با مرگ آن‌ها را نابود می‌کند. عبرت گرفتن از دنیا ای بندگان خدا! بدانید، شما و آنان که در این دنیا زندگی می‌کنید، بر همان راهی می‌روید که گذشتگان پیمودند، آنان زندگیشان از شما درازتر، خانه‌هایشان آبادتر، و آثارشان از شما بیشتر بود، که ناگهان صدایشان خاموش، و بادهایشان ساکت، و اجسادشان پوسیده، و سرزمینشان خالی، و آثارشان ناپدید شد. قصرهای بلند و محکم، و بساط عیش و بالشهای نرم را به سنگ‌ها و آجرها، و قبرهای به هم چسبیده تبدیل کردند، گورهایی که بنای آن بر خرابی، و با خاک ساخته شده است، گورها به هم نزدیک اما ساکنان آن‌ها از هم دور و غریبند در وادی وحشتناک به ظاهر آرام اما گرفتار قرار دارند، نه در جایی که وطن گرفتند انس می‌گیرند، و نه با همسایگان ارتباطی دارند، در صورتیکه با یکدیگر نزدیک، و در کنار هم جای دارند. چگونه یکدیگر را دیدار کنند در حالی که فرسودگی آن‌ها را درهم کوبیده، و سنگ و خاک آنان را در کام خود فرو برده است. شما هم راهی را خواهید رفت که آنان رفته اند، و در گرو خانه‌هایی قرار خواهید گرفت که آن‌ها قرار دارند، و گورها شما را به امانت خواهند پذیرفت، پس چگونه خواهید بود که عمر شما بسر آید؟ و مردگان از قبرها برخیزند؟ (در آن هنگام که هر کس به اعمال از پیش فرستاده آزمایش می‌شود، و به سوی خدا که مولا و سرپرست آن‌هاست باز می‌گردد، و هر دروغی را که می‌بافتند برای آنان سودی نخواهد داشت.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (143, 1, 227, 'دعائی از آن حضرت', 'خطبه ۲۲۷-دعائی از آن حضرت \nیکی از دعاهای امام (ع) \nخدایا! تو با دوستانت انس گیرنده تر، و برطرف کننده نیازهای توکل کنندگانی، بر اسرار پنهانشان آگاه، و از آنچه در دل دارند آشنایی، و از دیدگاه‌های آنان باخبر، و رازشان نزد تو آشکار، و دلهایشان در حسرت دیدار تو داغدار است، اگر تنهایی و غربت به وحشتشان اندازد یاد تو آرامشان می‌کند، اگر مصیبتها بر آنان فرود آید، به تو پناه می‌برند، و روی به درگاه تو دارند، زیرا می‌دانند که سررشته کارها به دست توست، و همه کارها از خواست تو نشات می‌گیرد. خدایا! اگر برای خواستن درمانده شوم، یا راه پرسیدن را ندانم، تو مرا به اصلاح کارم راهنمایی فرما، و جانم را به آن چه مایه رستگاری من است هدایت کن، که چنین کاری از راهنماییهای تو به دور، و از کفایتهای تو ناشناخته نیست. خدایا، مرا با بخشش خود بپذیر، و با عدل خویش رفتار مکن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (144, 1, 228, 'درباره یکی از حاکمان', 'خطبه ۲۲۸-درباره یکی از حاکمان \nویژگیهای سلمان فارسی \nخدا او را در آنچه آزمودش پاداش خیر دهد، که کجیها را راست، و بیماریها را درمان کرد، سنت پیامبر (ص) را بپا داشت، و فتنه‌ها را پشت سر گذاشت، با دامن پاک، و عیبی اندک، درگذشت، به نیکی‌های دنیا رسیده و از بدیهای آن رهایی یافت، وظائف خود نسبت به پروردگارش را انجام داد، و چنانکه باید از کیفر الهی می‌ترسید، خود رفت و مردم را پراکنده برجای گذاشت، که نه گمراه، راه خویش شناخت، و نه هدایت شده به یقین رسید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (145, 1, 229, 'در توصیف بیعت مردم با آن جناب بر خلافت', 'خطبه ۲۲۹-در توصیف بیعت مردم \nویژگیهای بیعت مردم با امام \nدست مرا برای بیعت می‌گشودید و من می‌بستم، شما آن را به سوی خود می‌کشیدید و من آن را می‌گرفتم، سپس چونان شتران تشنه که به طرف آبشخور هجوم می‌آوردند بر من هجوم آوردید، تا آنکه بند کفشم پاره شد، و عبا از دوشم افتاد، و افراد ناتوان پایمال گردیدند، آن چنان مردم در بیعت با من خشنود بودند که خردسالان شادمان، و پیران برای بیعت کردن لرزان به راه افتادند، بیماران بر دوش خویشان سوار، و دختران جوان بی نقاب به صحنه آمدند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (146, 1, 23, 'در باب بینوایان', 'خطبه ۲۳-در باب بینوایان \nتناسب نعمتها با استعدادهای گوناگون \n(پس از ستایش پروردگار،) \nبدانید که تقدیرهای الهی چون قطرات باران از آسمان به سوی انسانها فرود می‌آید، و بهره هر کسی، کم یا زیاد به او می‌رسد، پس اگر یکی از شما برای برادر خود، برتری در مال و همسر و نیروی بدنی مشاهده کند، مبادا فریب خورد و حسادت کند، زیرا مسلمان (تا زمانی که دست به عمل پستی نزده که از آشکار شدنش شرمنده باشد و مورد سرزنش مردم پست قرار گیرد) به مسابقه دهنده ای می‌ماند که دوست دارد در همان آغاز مسابقه پیروز گردد تا سودی به دست آورد و ضرری متوجه او نگردد. همچنین مسلمانی که از خیانت پاک است انتظار دارد یکی از دو خوبی نصیب او گردد، یا دعوت حق را لبیک گفته عمر او پایان پذیرد. (که آنچه در نزد خداست برای او بهتر است) و یا خداوند روزی فراوان به او دهد و صاحب همسر و فرزند و ثروت گردد، و همچنان دین و شخصیت خود را نگاهدارد. همانا ثروت و فرزندان، محصول دنیا و فانی شدنی، و عمل صالح زراعت آخرت است، گرچه گاهی خداوند. هر دوی آن را به ملتهایی خواهد بخشید. از خدا در آنچه اعلام خطر کرده است برحذر باشید، از خدا آنگونه بترسید که نیازی به عذرخواهی نداشته باشید، عمل نیک انجام دهید بدون آنکه به ریا و خودنمایی مبتلا شوید، زیرا هر کس، کاری برای غیر خدا انجام دهد، خدا او را به همان غیر واگذارد. از خدا، درجات شهیدان، و زندگی سعادتمندان، و همنشینی با پیامبران را درخواست می‌کنیم. ضرورت تعاون با خویشاوندان ای مردم، انسان هر مقدار که ثروتمند باشد، باز از خویشاوندان خود بی نیاز نیست که از او با زبان و دست دفاع کنند، خویشاوندان انسان، بزرگترین گروهی هستند که از او حمایت می‌کنند، و اضطراب و ناراحتی او را می‌زدایند، و در هنگام مصیبتها نسبت به او، پرعاطفه ترین مردم می‌باشند، نام نیکی که خدا از شخصی در میان مردم رواج دهد بهتر از میراثی است که دیگری بردارد. (قسمت دیگری از همین خطبه): آگاه باشید، مبادا از بستگان تهیدست خود رو برگردانید، و از آنان چیزی را دریغ دارید، که نگاه داشتن مال دنیا زیادی نیاورد و از بین رفتنش کمبودی ایجاد نکند، آن کس که دست دهنده خود را از بستگانش بازدارد، تنها یک دست را از آن‌ها گرفته اما دستهای فراوانی را از خویش دور کرده است، و کسی که پر و بال محبت را بگستراند، دوستی خویشاوندانش تداوم خواهد داشت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (147, 1, 230, 'درباره تقوا و کوشش در عمل', 'خطبه ۲۳۰-درباره تقوا \nپرهیزکاری و عمل \nهمانا ترس از خدا کلید هر در بسته، و ذخیره رستاخیز، و مایه آزادگی از هرگونه بردگی، و عامل نجات از هرگونه هلاکت است، در پرتو پرهیزکاری، تلاشگران پیروز، پرواکنندگان از گناه رستگار، و به هر آرزویی می‌توان رسید. مردم! عمل کنید که عمل نیکو به سوی خدا بالا می‌رود، و توبه سودمند است، و دعا به اجابت می‌رسد، و آرامش برقرار، و قلمهای فرشتگان در جریان است، به اعمال نیکو بشتابید پیش از آنکه عمرتان پایان پذیرد، یا بیماری مانع شود، و یا تیر مرگ شما را هدف قرار دهد. ضرورت یاد مرگ زیرا مرگ نابودکننده لذتها، تیره کننده خواهشهای نفسانی، و دورکننده اهداف شماست، مرگ دیدارکننده ای ناخشنود، هماوردی شکست ناپذیر و کینه توزی است که بازخواست نمی شود، دامهای خود را هم اکنون بر دست و پای شما آویخته، و سختی‌هایش شما را فرا گرفته، و تیرهای خود را به سوی شما پرتاب کرده است. قهرش بزرگ، و دشمنی او پیاپی و تیرش خطا نمی کند. چه زود است که سایه‌های مرگ، و شدت دردهای آن، و تیرگیهای لحظه جان کندن، و بیهوشی سکرات مرگ، و ناراحتی و خارج شدن روح از بدن، و تاریکی چشم پوشیدن از دنیا، و تلخی خاطره‌ها، شما را فرا گیرد. پس \nممکن است ناگهان مرگ بر شما هجوم آورد، و گفتگوهایتان را خاموش، و جمعیت شما را پراکنده، و نشانه‌های شما را نابود، و خانه‌های شما را خالی، و میراث خواران شما را برانگیزد تا ارث شما را تقسیم کنند، آنان یا دوستان نزدیکند که به هنگام مرگ نفعی نمی رسانند، یا نزدیکان غمزده ای که نمی توانند جلوی مرگ را بگیرند، یا سرزنش کنندگانی که گریه و زاری نمی کنند. سفارش به نیکوکاری بر شما باد به تلاش و کوشش، آمادگی و آماده شدن، و جمع آوری زاد و توشه آخرت در دوران زندگی دنیا، دنیا شما را مغرور نسازد، چنانکه گذشتگان شما، و امتهای پیشین را در قرون سپری شده مغرور ساخت، آنان که دنیا را دوشیدند، به غفلت زدگی در دنیا گرفتار آمدند، فرصتها را از دست دادند، و تازه‌های آن را فرسوده ساختند، سرانجام خانه‌هایشان گورستان، و سرمایه‌هایشان ارث این و آن گردید، آن را که نزدیکشان رود نمی شناسند، و به گریه کنندگان خود توجهی ندارند، و نه دعوتی را پاسخ می‌گویند. مردم! از دنیای حرام بپرهیزید، که حیله گر و فریبنده و نیرنگباز است، بخشنده ای بازپس گیرنده، و پوشنده ای برهنه کننده است، آسایش دنیا بی دوام، و سختی‌هایش بی پایان، و بلاهایش دائمی است. \nدنیا و زاهدان زاهدان گروهی از مردم دنیایند که دنیاپرست نمی باشند، پس در دنیا زندگی می‌کنند اما آلودگی دنیاپرستان را ندارند، در دنیا با آگاهی و بصیرت عمل می‌کنند، و در ترک زشتی‌ها از همه پیشی می‌گیرند، بدنهایشان بگونه ای در تلاش و حرکت است که گویا میان مردم آخرتند، اهل دنیا را می‌نگرند که مرگ بدنها را بزرگ می‌شمارند، اما آن‌ها مرگ دلهای زندگان را بزرگتر می‌دانند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (148, 1, 231, 'خطبه‌ای که در ذوقار خواند', 'خطبه ۲۳۱-خطبه ای در ذوقار \nویژگیهای پیامبر (ص) \nپیامبر اسلام (ص) آنچه را که به او فرموده شد آشکار کرد، و پیامهای پروردگارش را رساند، او شکافهای اجتماعی را به وحدت اصلاح، و فاصله‌ها را پیوستگی بخشید، میان خویشاوندان یگانگی برقرار کرد پس از آنکه آتش دشمنی‌ها و کینه‌های برافروخته در دلها راه یافته بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (149, 1, 232, 'با عبدالله بن زمعه', 'خطبه ۲۳۲-با عبدالله بن زمعه \nاحتیاط در بیت المال \nاین اموال که می‌بینی نه مال من و نه از آن توست، غنیمتی گردآمده از مسلمانان است که با شمشیرهای خود به دست آوردند، اگر تو در جهاد همراهشان بودی، سهمی چونان سهم آنان داشتی، وگرنه دسترنج آنان خوراک دیگران نخواهد بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (150, 1, 233, 'در باب زیانهای زبان', 'خطبه ۲۳۳-در باب زیانهای زبان \nفصاحت و بلاغت اهل بیت (ع) \nآگاه باشید، همانا زبان پاره ای از وجود انسان است، اگر آمادگی نباشد سخن نمی گوید، و به هنگام آمادگی، گفتار او را مهلت نمی دهد، همانا ما امیران سخن می‌باشیم، درخت سخن در ما ریشه دوانده، و شاخه‌های آن بر ما سایه افکنده است. علل سقوط جامعه انسانی خدا شما را رحمت کند، بدانید که همانا شما در روزگاری هستید که گوینده حق اندک، و زبان از راستگویی عاجز، و حق طلبان بی ارزشند، مردم گرفتار گناه، و به سازشکاری هم داستانند، جوانشان بداخلاق، و پیرانشان گناهکار، و عالمشان دورو، و نزدیکشان سودجویند، نه خردسالانشان بزرگان را حرمت می‌نهند و نه توانگرانشان دست مستمندان را می‌گیرند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (151, 1, 234, 'چرا مردم در سرشت مختلفند؟', 'خطبه ۲۳۴-چرا مردم مختلفند؟ \nعلل تفاوتها میان انسانها \nعلت تفاوتهای میان مردم، گوناگونی سرشت آنان است، زیرا آدمیان در آغاز، ترکیبی از خاک شور و شیرین، سخت و نرم، می‌باشند، پس آنان به میزان نزدیک بودن خاکشان با هم نزدیک، و به اندازه دوری آن از هم دور و متفاوتند یکی زیباروی و کم خرد، دیگری بلندقامت و کم همت، یکی زشتروی و نیکوکار، دیگری کوتاه قامت و خوش فکر، یکی پاک سرشت و بداخلاق، دیگری خوش قلب و آشفته عقل، و آن دیگر سخنوری دل آگاه است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (152, 1, 235, 'هنگام غسل دادن و کفن کردن رسول خدا', 'خطبه ۲۳۵-\nدر سوگ پیامبر (ص) \n(به هنگام غسل دادن پیامبر صلی الله علیه و آله و سلم فرمود) \nپدر و مادرم فدای تو ای رسول خدا! با مرگ تو رشته ای برید که در مرگ دیگران نبرید، با مرگ تو رشته پیامبری، و فرود آمدن پیام و اخبار آسمانی گسست، مصیبت تو، دیگر مصیبت دیدگان را به شکیبایی واداشت، و همه را در مصیبت تو یکسان عزادار کرد، اگر به شکیبایی امر نمی کردی، و از بی تابی نهی نمی فرمودی، آنقدر اشک می‌ریختم تا اشکها تمام شود، و این درد جانکاه همیشه در من می‌ماند، و اندوهم جاودانه می‌شد، که همه اینها در مصیبت تو ناچیز است. چه باید کرد که زندگی را دوباره نمی توان باز گرداند، و مرگ را نمی شود مانع شد، پدر و مادرم فدای تو! ما را در پیشگاه پروردگارت یاد کن، و در خاطرات نگهدار.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (153, 1, 236, 'در حوادث بعد از هجرت', 'خطبه ۲۳۶-در حوادث بعد از هجرت \nیاد مشکلات هجرت \nخود را در راهی قرار دادم که پیامبر (ص) رفته بود، و همه جا از او پرسیدم تا به سرزمین (عرج) رسیدم. (این جملات در یک سخن طولانی آمده است، جمله (فاطا ذکره) در همه جا از او می‌پرسیدم. یکی از سخنانی است که در اوج فصاحت قرار دارد، یعنی خبر حرکت پیامبر (ص) از ابتداء حرکت تا پایان به من می‌رسید، که امام این معنا را با کنایه آورده است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (154, 1, 237, 'در کار خیر شتاب کنید', 'خطبه ۲۳۷-در کار خیر شتاب کنید \nسفارش به نیکوکاری \nپس عمل نیکو انجام دهید، حال که زنده و برقرارید، پرونده‌ها گشوده، راه توبه آماده، و خدا گریزان را فرا می‌خواند، و بدکاران امید بازگشت دارند، عمل کنید پیش از آنکه چراغ عمل خاموش، و فرصت پایان یافته، و اجل فرا رسیده، و در توبه بسته، و فرشتگان به آسمان پرواز کنند. پس هر کسی با تلاش خود برای خود، از روزگار زندگانی برای ایام پس از مرگ، از دنیای فناپذیر برای جهان پایدار، و از گذرگاه دنیا برای زندگی جاودانه آخرت، توشه برگیرد، انسان باید از خدا بترسد، زیرا تا لحظه مرگ فرصت داده شده، و مهلت عمل نیکو دارد، انسان باید نفس را مهار زند، و آن را در اختیار گرفته از طغیان و گناهان باز دارد، و زمام آن را به سوی اطاعت پروردگار بکشاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (155, 1, 238, 'درباره حکمین', 'خطبه ۲۳۸-درباره حکمین \nوصف شامیان \nشامیان درشت خویانی پست، بردگانی فرومایه‌اند که از هر گوشه ای گرد آمده، و از گروه‌های مختلفی ترکیب یافته اند، مردمی که سزاوار بودند احکام دین را بیاموزند، و تربیت شوند، و دانش فرا گیرند، و کارآزموده شوند، و سرپرست داشته باشند، و دستشان گیرند، و آن‌ها را به کار مفید وادارند. آنان نه از مهاجرانند و نه از انصار، و نه آنان که خانه و زندگی خود را برای مهاجران آماده کرده، و از جان و دل ایمان آوردند. نکوهش از انتخاب حکم آگاه باشید که شامیان در انتخاب حکم، نزدیک ترین فردی را که دوست داشتند برگزیدند، و شما فردی را که از همه به ناخشنودی نزدیک تر بود انتخاب کردید، همانا سر و کار شما با عبدالله پسر قیس است که می‌گفت: (جنگ فتنه است بند کمانها را ببرید و شمشیرها را در نیام کشید.) اگر راست می‌گفت پس چرا بدون اجبار در جنگ شرکت کرد؟ و اگر دروغ می‌گفت پس متهم است. برای داوری، عبدالله ابن عباس را رو در روی عمروعاص قرار دهید، و از فرصت مناسب استفاده کنید، و مرزهای دوردست کشور اسلامی را در دست خود نگهدارید، آیا نمی بینید که شهرهای شما می‌دان نبردشده؟ و خانه‌های شما هدف تیرهای دشمنان قرار گرفته است؟.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (156, 1, 239, 'در ذکر آل محمد', 'خطبه ۲۳۹-در ذکر آل محمد \nفضائل اهل بیت پیامبر (ص) \nخاندان پیامبر (ص) مایه حیات و زنده بودن دانش، و نابودی جهل می‌باشند، بردباریشان شما را از دانش آن‌ها خبر می‌دهد، و ظاهرشان از صفای باطن، و سکوتشان از حکمتهای گفتارشان باخبر می‌سازد، هرگز با حق مخالفت نمی کنند، و در آن اختلاف ندارند. آن‌ها ستونهای استوار اسلام، و پناهگاه مردم می‌باشند، حق به وسیله آن‌ها به جایگاه خویش باز گردید، و باطل از جای خویش رانده و نابود، و زبان باطل از ریشه کنده شد، اهل بیت پیامبر (ص) دین را چنانکه سزاوار بود، دانستند و آموختند و بدان عمل کردند، نه تنها شنیدند و نقل نمودند، زیرا راویان دانش بسیار، اما حفظ کنندگان و عمل کنندگان به آن اندکند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (157, 1, 24, 'برانگیختن مردم به پیکار', 'خطبه ۲۴-برانگیختن مردم به پیکار \nضرورت آمادگی برای جهاد \nسوگند به جان خودم، در مبارزه با مخالفان حق، و آنان که در گمراهی و فساد غوطه ورند، یک لحظه مدارا و سستی نمی کنم. پس ای بندگان خدا! از خدا بترسید، و از خدا، به سوی خدا فرار کنید، و از راهی که برای شما گشوده بروید، و وظائف و مقرراتی که برای شما تعیین کرده بپا دارید، اگر چنین باشید، علی (ع) ضامن پیروزی شما در آینده می‌باشد گرچه هم اکنون به دست نیاورید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (158, 1, 240, 'سخنی با عبدالله بن عباس', 'خطبه ۲۴۰-سخنی با عبدالله بن عباس \nنکوهش از موضع گیری‌ها ناروای عثمان \nای پسر عباس! عثمان جز این نمی خواهد که مرا سرگردان نگهدارد، گاهی بروم، و زمانی برگردم، یک بار پیغام فرستاد از مدینه خارج شوم، دوباره خبر داد که باز گردم، هم اکنون تو را فرستاده که از شهر خارج شوم بخدا سوگند! آنقدر از او دفاع کردم که ترسیدم گناهکار باشم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (159, 1, 241, 'در ترغیب یارانش به جهاد', 'خطبه ۲۴۱-در ترغیب یارانش به جهاد \nتشویق برای جهاد \nخدا شکرگزاری را بر عهده شما نهاده، و امر حکومت را در دست شما گذارده. و فرصت مناسب در اختیارتان قرار داده است تا برای جایزه بهشت با هم ستیز کنید، پس کمربندها را محکم ببندید، و دامن همت بر کمر زنید، که به دست آوردن ارزشهای والا با خوشگذرانی میسر نیست، چه بسا خوابهای شب که تصمیم‌های روز را از بین برده، و تاریکیهای فراموشی که همتهای بلند را نابود کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (160, 1, 25, 'رنجش از یاران سست رای', 'خطبه ۲۵-رنجش از یاران سست \nعلل شکست ملتها (علل شکست کوفیان و پیروزی شامیان) \nاکنون جز شهر کوفه در دست من باقی نمانده است، که آن را بگشایم یا ببندم ای کوفه اگر فقط تو مرا باشی، آن هم برابر این همه مصیبتها و طوفانها!! چهره ات زشت باد. آنگاه به گفته شاعر مثال آورد: به جان پدرت سوگند ای عمرو که سهم اندکی از ظرف و پیمانه داشتم. \nسپس ادامه داد: به من خبر رسیده که بسر بن ارطاه بر یمن تسلط یافت، سوگند به خدا می‌دانستم که مردم شام به زودی بر شما غلبه خواهند کرد. زیرا آن‌ها در یاری کردن باطل خود، وحدت دارند، و شما در دفاع از حق متفرقید، شما امام خود را در حق نافرمانی کرده و آن‌هاامام خود را در باطل فرمانبردارند. آن‌ها نسبت به رهبر خود امانتدار و شما خیانتکارید، آن‌ها در شهرهای خود به اصلاح و آبادانی مشغولند و شما به فساد و خرابی (آنقدر فرومایه اید) اگر من کاسه چوبی آب را به یکی از شماها امانت دهم می‌ترسم که بند آن را بدزدید نفرین به امت خیانتکار خدایا، من این مردم را با پند و تذکرهای مداوم خسته کردم و آن‌ها نیز مرا خسته نمودند، آن‌ها از من به ستوه آمده، و من از آنان به ستوه آمده، دل شکسته ام، به جای آنان افرادی بهتر به من مرحمت فرما، و به جای من بدتر از من بر آن‌ها مسلط کن. خدایا، دلهای آنان را، آنچنان که نمک در آب حل می‌شود، آب کن. به خدا سوگند، دوست داشتم، به جای شما کوفیان، هزار سوار از بنی فراس بن غنم می‌داشتم که: (اگر آنان را می‌خواندی، سوارانی از ایشان نزد تو می‌آمدند مبارز و تازنده چون ابر تابستانی).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (161, 1, 26, 'اعراب پیش از بعثت', 'خطبه ۲۶-اعراب پیش از بعثت \nشناخت فرهنگ جاهلیت \nخداوند، پیامبر اسلام، حضرت محمد (ص) را هشداردهنده جهانیان مبعوث فرمود، تا امین و پاسدار وحی الهی باشد، آنگاه که شما ملت عرب، بدترین دین را داشته، و در بدترین خانه زندگی می‌کردید، میان غارها، سنگهای خشن و مارهای سمی خطرناک فاقد شنوایی، بسر می‌بردید، آبهای آلوده می‌نوشیدید. و غذاهای ناگوار می‌خوردید، خون یکدیگر را به ناحق می‌ریختید، و پیوند خویشاوندی را می‌بریدید، بتها میان شما پرستش می‌شد، و مفاسد و گناهان، شما را فرا گرفته بود. \nمظلومیت و تنهایی علی (ع) پس از وفات پیامبر (ص) و بی وفایی یاران به اطراف خود نگاه کرده یاوری جز اهل بیت خود ندیدم، (که اگر یاری کنند، کشته خواهند شد و به مرگ آنان رضایت ندادم) چشم پر از خار و خاشاک را ناچار فرو بستم، و با گلویی که استخوان شکسته در آن گیر کرده بود جام تلخ حوادث را نوشیدم، و خشم خویش فرو خوردم، و بر نوشیدن جام تلخ تر از گیاه حنظل، شکیبایی نمودم. (قسمت دیگری از همین خطبه:) \nمعرفی عمروعاص و ضرورت آمادگی نظامی عمروعاص با معاویه بیعت نکرد. مگر آنکه شرط کرد تا برای بیعت درهم و دینار فراوان بگیرد، که در این معامله شوم، دست فروشنده هرگز به پیروزی نرسد و سرمایه خریدار به رسوایی کشانده شود. ای مردم کوفه، آماده پیکار شوید، و ساز و برگ جنگ فراهم آورید، زیرا که آتش جنگ زبانه کشیده و شعله‌های آن بالا گرفته است، صبر و استقامت را شعار خویش سازید که پیروزی می‌آورد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (162, 1, 27, 'در فضیلت جهاد', 'خطبه ۲۷-در فضیلت جهاد \nارزش جهاد در راه خدا \n(پس از ستایش پروردگار،) \nجهاد در راه خدا، دری از درهای بهشت است، که خدا آن را به روی دوستان مخصوص خود گشوده است، جهاد، لباس تقوا، و زره محکم، و سپر مطمئن خداوند است، کسی که جهاد را ناخوشایند دانسته و ترک کند، خدا لباس ذلت و خواری بر او می‌پوشاند، و دچار بلا و مصیبت می‌شود، و کوچک و ذلیل می‌گردد، دل او در پرده گمراهی مانده، و حق از او روی می‌گرداند، به جهت ترک جهاد، به خواری محکوم و از عدالت محروم است \nدعوت به مبارزه و نکوهش از نافرمانی کوفیان آگاه باشید! من شب و روز، پنهان و آشکار، شما را به مبارزه با شامیان، دعوت کردم و گفتم پیش از آن که، آن‌ها با شما بجنگند با آنان نبرد کنید، به خدا سوگند، هر ملتی که درون خانه خود مورد هجوم قرار گیرد، ذلیل خواهد شد، اما شما سستی بخرج دادید، و خواری و ذلت پذیرفتید، تا آنجا که دشمن پی در پی به شما حمله کرد و سرزمینهای شما را تصرف نمود، و اینک، فرمانده معاویه، (مرد غامدی) با لشگرش وارد شهر انبارشده و فرماندار من، (حسان بن حسان بکری) را کشته و سربازان شما را از مواضع مرزی بیرون رانده است. به من خبر رسید که مردی از لشگر شام به خانه زن مسلمان و غیر مسلمان که در پناه حکومت اسلام بود وارد شد، و خلخال و دستبند و گردنبند و گوشواره‌های آن‌ها را به غارت برد، در حالی که هیچ وسیله ای برای دفاع، جز گریه و التماس کردن، نداشتند، لشگریان شام با غنیمت فراوان رفتند بدون اینکه حتی یک نفر آنان، زخمی بردارد، و یا قطره خونی از او ریخته شود، اگر برای این حادثه تلخ، مسلمانی از روی تاسف بمیرد، ملامت نخواهد شد، و از نظر من سزاوار است. \nشگفتا، شگفتا!! به خدا سوگند، این واقعیت قلب انسان را می‌میراند و دچار غم و اندوه می‌کند که شامیان در باطل خود وحدت دارند، و شما در حق خود متفرقید. زشت باد روی شما و از اندوه رهایی نیابید که آماج تیر بلا شدید. به شما حمله می‌کنند، شما حمله نمی کنید؟ با شما می‌جنگند، شما نمی جنگید؟ اینگونه معصیت خدا می‌شود و شما رضایت می‌دهید؟ وقتی در تابستان فرمان حرکت به سوی دشمن می‌دهم، می‌گویید هوا گرم است مهلت ده تا سوز گرما بگذرد، و آنگاه که در زمستان فرمان جنگ می‌دهم، می‌گویید هوا خیلی سرد است بگذار سرما برود، همه این بهانه‌ها برای فرار از سرما و گرما بود؟ وقتی شما از گرما و سرما فرار می‌کنید، به خدا سوگند که از شمشیر بیشتر گریزانید. مظلومیت امام (ع) و علل شکست کوفیان ای مرد نمایان نامرد! ای کودک صفتان بی خرد، که عقلهای شما به عروسان حجله آرای، شباهت دارد، چقدر دوست داشتم که شما را هرگز نمی دیدم و هرگز نمی شناختم، شناسایی شما سوگند به خدا که جز پشیمانی حاصلی نداشت، و اندوهی غم بار سرانجام آن شد خدا شما را بکشد که دل من از دست شما پرخون، و سینه ام از خشم شما مالامال است، کاسه‌های غم و اندوه را، جرعه جرعه به \nمن نوشاندید، و با نافرمانی و ذلت پذیری، رای و تدبیر مرا تباه کردید، تا آنجا که قریش در حق من گفت: (بی تردید پسر ابیطالب مردی دلیر است ولی دانش نظامی ندارد) خدا پدرانشان را مزد دهد، آیا یکی از آن‌ها تجربه‌های جنگی سخت و دشوار مرا دارد؟ یا در پیکار توانست از من پیشی گیرد؟ هنوز بیست ساله نشده، که در می‌دان نبرد حاضر بودم، هم اکنون که از شصت سال گذشته ام. اما دریغ، آن کس که فرمانش را اجراء نکنند، رایی نخواهد داشت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (163, 1, 28, 'اندرز و هشدار', 'خطبه ۲۸-اندرز و هشدار \nدنیاشناسی \n(پس از حمد و ستایش الهی،) \nهمانا دنیا روی گردانده، و وداع خویش را اعلام داشته است، و آخرت به ما روی آورده، و پیشروان لشگرش نمایان شد. آگاه باشید! امروز، روز تمرین و آمادگی، و فردا روز مسابقه است، پاداش برندگان، بهشت، و کیفر عقب ماندگان آتش است آیا کسی هست که پیش از مرگ، از اشتباهات خود، توبه کند؟ آیا کسی هست که قبل از فرا رسیدن روز دشوار قیامت، اعمال نیکی انجام دهد؟ آگاه باشید! هم اکنون در روزگار آرزوهایید، که مرگ را در پی دارد، پس هر کس در ایام آرزوها، پیش از فرا رسیدن مرگ، عمل نیکو انجام دهد، بهره مند خواهد شد، و مرگ او را زیانی نمی رساند، و آن کس که در روزهای آرزوها، پیش از فرا رسیدن مرگ کوتاهی کند، زیانکار و مرگ او زیانبار است، همانگونه که به هنگام ترس و ناراحتی برای خدا عمل می‌کنید، در روزگار خوشی و کامیابی نیز عمل کنید. آگاه باشید! هرگز چیزی مانند بهشت ندیدم که خواستاران آن در خواب غفلت باشند، و نه مانند آتش جهنم، که فراریان آن چنین در خواب فرورفته باشند. آگاه باشید! آن کس را که حق، منفعت نرساند، باطل به او زیان خواهد رساند، و آن کس که هدایت، راهنماییش نکند، گمراهی او را به هلاکت افک \nند آگاه باشید! به کوچ کردن فرمان یافته و برای جمع آوری توشه آخرت راهنمایی شدید، همانا، وحشتناکترین چیزی که بر شما می‌ترسم، هواپرستی، و آرزوهای دراز است، پس از این دنیا توشه برگیرید تا فردا خود را با آن حفظ نمایید. (اگر سخنی بتواند مردم را به آخرت گرایی و زهد و تقوا بکشاند همین سخن امام است که می‌تواند انسان را از آرزوها جدا کرده و نور امید را در دلها زنده سازد و انسان را نسبت به زشتیها بیزار کند از جمله‌های شگفت این خطبه آن است که فرمود امروز روز تمرین و فردا روز مسابقه، و جایزه برندگان بهشت است و کیفر عقب ماندگان آتش جهنم خواهد بود در این خطبه معانی ارزشمند در قالب مثلهای گویا و تشبیهات صحیح جاگرفته است. امام بین دو لفظ (السبقه) و (الغایه) به خاطر اختلاف معنا فاصله انداخته است زیرا سبقت گرفتن در مسابقاتی است مورد علاقه انسان است و این از صفات بهشت است و در راهی که به آتش جهنم می‌انجامد بکار گرفته نمی شود از این رو فرمود: السبقه الجنه اما واژه الغایه به معنای پایان در هر مسابقه استعمال می‌شود همانگونه که در قرآن خدا به کافران فرماید از دنیا بهره گیرد که پایان شما به سوی آتش است در این خطبه دقت کنید که معنایی \nژرف و عمقی دست نایافتنی دارد.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (164, 1, 29, 'در نکوهش اهل کوفه', 'خطبه ۲۹-در نکوهش اهل کوفه \nعلل شکست کوفیان \nای مردم کوفه! بدنهای شما در کنار هم، اما افکار و خواسته‌های شما پراکنده است، سخنان ادعایی شما، سنگهای سخت را می‌شکند، ولی رفتار سست شما دشمنان را امیدوار می‌سازد، در خانه‌هایتان که نشستید، ادعاها و شعارهای تند سر می‌دهید، اما در روز نبرد، می‌گویید ای جنگ، از ما دور شو، و فرار می‌کنید. آن کس که از شما یاری خواهد، ذلیل و خوار است، و قلب رهاکننده شما آسایش ندارد، بهانه‌های نابخردانه می‌آورید، چون بدهکاران خواهان مهلت، از من مهلت می‌طلبید و برای مبارزه سستی می‌کنید، بدانید که افراد ضعیف و ناتوان هرگز نمی توانند ظلم و ستم را دور کنند، و حق جز با تلاش و کوشش به دست نمی آید، شما که از خانه خود دفاع نمی کنید چگونه از خانه دیگران دفاع می‌نمایید؟ و با کدام امام پس از من به مبارزه خواهید رفت؟ به خدا سوگند فریب خورده آن کس که به گفتار شما مغرور شود، کسی که به امید شما به سوی پیروزی رود، با کندترین پیکان به می‌دان آمده است، و کسی که بخواهد دشمن شما را با شما هدف قرار دهد، با تیری شکسته، تیراندازی کرده است به خدا سوگند! صبح کردم در حالی که گفتار شما را باور ندارم، و به یاری شما امیدوار نیستم، و \nدشمنان را به وسیله شما تهدید نمی کنم. راستی شما را چه می‌شود؟ دارویتان چیست؟ و روش درمانتان کدام است؟ مردم شام نیز همانند شمایند؟ آیا سزاوارست شعار دهید و عمل نکنید؟ و فراموشکاری بدون پرهیزگاری داشته، به غیر خدا امیدوار باشید؟.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (165, 1, 3, 'شقشقیه', 'خطبه ۳-شقشقیه \n۱- شکوه از ابا بکر و غصب خلافت \nآگاه باشید به خدا سوگند ابا بکر، جامه خلافت را بر تن کرد، در حالی که می‌دانست جایگاه من نسبت به حکومت اسلامی، چون محور آسیاب است به آسیاب که دور آن حرکت می‌کند. او می‌دانست که سیل علوم از دامن کوهسار من جاری است، و مرغان دور پرواز اندیشه‌ها به بلندای ارزش من نتوانند پرواز کرد. پس من ردای خلافت رها کرده و دامن جمع نموده از آن کناره گیری کردم و در این اندیشه بودم که آیا با دست تنها برای گرفتن حق خود به پاخیزم یا در این محیط خفقان‌زا و تاریکی که به وجود آوردند، صبر پیشه سازم که پیران را فرسوده، جوانان را پیر، و مردان با ایمان را تا قیامت و ملاقات پروردگار اندوهگین نگه می‌دارد پس از ارزیابی درست، صبر و بردباری را خردمندانه‌تر دیدم. پس صبر کردم در حالی که گویا خار در چشم و استخوان در گلوی من مانده بود. و با دیدگان خود می‌نگریستم که میراث مرا به غارت می‌برند. \n۲- بازی ابا بکر با خلافت \nتا اینکه خلیفه اول، به راه خود رفت و خلافت را به پسر خطاب سپرد. سپس امام مثلی را با شعری از أعشی عنوان کرد: مرا با برادر جابر، «حیان» چه شباهتی است (من همه روز را در گرمای سوزان کار کردم و او راحت و آسوده در خانه بود.) شگفتا ابا بکر که در حیات خود از مردم می‌خواست عذرش را بپذیرند، چگونه در هنگام مرگ، خلافت را به عقد دیگری در آورد. هر دو از شتر خلافت سخت‌ دوشیدند و از حاصل آن بهره‌مند گردیدند. \n۳- شکوه از عمرو ماجرای خلافت: \nسرانجام اولی حکومت را به راهی در آورد، و به دست کسی (عمر) سپرد که مجموعه‌ای از خشونت، سختگیری، اشتباه و پوزش طلبی بود زمامدار مانند کسی که بر شتری سرکش سوار است، اگر عنان محکم کشد، پرده‌های بینی حیوان پاره می‌شود، و اگر آزادش گذارد، در پرتگاه سقوط می‌کند. سوگند به خدا مردم در حکومت دومی، در ناراحتی و رنج مهمی گرفتار آمده بودند، و دچار دو رویی‌ها و اعتراض‌ها شدند، و من در این مدت طولانی محنت‌زا، و عذاب آور، چاره‌ای جز شکیبایی نداشتم، تا آن که روزگار عمر هم سپری شد. \n۴- شکوه از شورای عمر: \nسپس عمر خلافت را در گروهی از قرار داد که پنداشت من همسنگ آنان می‌باشم پناه بر خدا از این شورا در کدام زمان در برابر شخص اولشان در خلافت مورد تردید بودم، تا امروز با اعضای شورا برابر شوم که هم اکنون مرا همانند آن‌ها پندارند و در صف آن‌ها قرارم دهند ناچار باز هم کوتاه آمدم، و با آنان هماهنگ گردیدم. یکی از آن‌ها با کینه‌ای که از من داشت روی بر تافت، و دیگری دامادش را بر حقیقت برتری داد و آن دو نفر دیگر که زشت است آوردن نامشان. \n۵- شکوه از خلافت عثمان: \nتا آن که سومی به خلافت رسید، دو پهلویش از پرخوری باد کرده، همواره بین آشپزخانه و دستشویی سرگردان بود، و خویشاوندان پدری او از بنی امیه به پاخاستند و همراه او بیت المال را خوردند و بر باد دادند، چون شتر گرسنه‌ای که بجان گیاه بهاری بیفتد، عثمان آنقدر اسراف کرد که ریسمان بافته او باز شد و أعمال او مردم را برانگیخت، و شکم بارگی او نابودش ساخت. \n۶- بیعت عمومی مردم با امیر المؤمنین علیه السلام: \nروز بیعت، فراوانی مردم چون یال‌های پر پشت گفتار بود، از هر طرف مرا احاطه کردند، تا آن که نزدیک بود حسن و حسین علیه السلام لگد مال گردند، و ردای من از دو طرف پاره شد. مردم چون گله‌های انبوه گوسفند مرا در میان گرفتند. اما آنگاه که به پاخاستم و حکومت را به دست گرفتم، جمعی پیمان شکستند و گروهی از اطاعت من سرباز زده و از دین خارج شدند، و برخی از اطاعات حق سر بر تافتند، گویا نشنیده بودند سخن خدای سبحان را که می‌فرماید: «سرای آخرت را برای کسانی برگزیدیم که خواهان سرکشی و فساد در زمین نباشند و آینده از آن پرهیزکاران است» آری به خدا آن را خوب شنیده و حفظ کرده بودند، اما دنیا در دیده آن‌ها زیبا نمود، و زیور آن چشم‌هایشان را خیره کرد. \n۷- مسؤولیت‌های اجتماعی \nسوگند به خدایی که دانه را شکافت و جان را آفرید، اگر حضور فراوان بیعت کنندگان نبود، و یاران حجت را بر من تمام نمی‌کردند، و اگر خداوند از علماء عهد و پیمان نگرفته بود که برابر شکم بارگی ستمگران، و گرسنگی مظلومان، سکوت نکنند، مهار شتر خلافت را بر کوهان آن انداخته، رهایش می‌ساختم، و آخر خلافت را به کاسه اول آن سیراب می‌کردم، آنگاه می‌دیدید که دنیای شما نزد من از آب بینی بزغاله‌ای بی ارزش‌تر است. \nگفتند: در اینجا مردی از أهالی عراق بلند شد و نامه‌ای به دست امام علیه السلام داد و امام علیه السلام آن را مطالعه می‌فرمود، گفته شد، مسایلی در آن بود که می‌بایست جواب می‌داد. وقتی خواندن نامه به پایان رسید، ابن عباس گفت یا امیر المؤمنین چه خوب بود سخن را از همان جا که قطع شد آغاز می‌کردید امام علیه السلام فرمود: هرگز ای پسر عباس، شعله‌ای از آتش دل بود، زبانه کشید و فرو نشست، ابن عباس می‌گوید، به خدا سوگند بر هیچ گفتاری مانند قطع شدن سخن امام علیه السلام این گونه اندوهناک نشدم، که امام نتوانست تا آنجا که دوست دارد به سخن ادامه دهد. \nمی‌گویم: (معنای سخن امام علیه السلام که فرمود، \nکراکب الصعبة، \nاین است که اگر سوار کار مهار شتر سرکش را سخت بکشد، و مرکب چموشی نافرمانی کند، بینی او پاره می‌شود، و اگر مهارش را رها کند، چموشی کرده در پرتگاه سقوط قرار می‌گیرد و صاحبش قدرت کنترل او را ندارد. می‌گویند «اشنق الناقه» یعنی بوسیله مهار، سر شتر را بالا بکشد و «شنقها» نیز می‌گویند که ابن سکیت در کتاب اصلاح المنطق گفته است. اینکه فرمود \n«اشنق لها» \nو نفرمود «اشنقها» برای آنکه این کلمه را مقابل \n«اسلس لها» \nقرار داد، گویی فرموده باشد که اگر سر او را بالا کشد. یعنی آن را واگذارد تا سر خود را بالا نگاه دارد).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (166, 1, 30, 'درباره قتل عثمان', 'خطبه ۳۰-درباره قتل عثمان \nپاسخ به شایعات دشمن \nاگر به کشتن او (عثمان) فرمان داده بودم، قاتل بودم، و اگر از آن باز می‌داشتم از یاوران او به شمار می‌آمدم، با این همه، کسی که او را یاری کرد، نمی تواند بگوید، از کسانی که دست از یاریش برداشتند بهترم، و کسانی که دست از یاریش برداشتند نمی توانند بگویند. یاورانش از ما بهترند، من جریان عثمان را برایتان خلاصه می‌کنم، عثمان استبداد و خودکامگی پیشه کرد، و شما بی تابی کردید و از حد گذراندید، و خدا در خودکامگی و ستمکاری، و در بی تابی و تندروی، حکمی دارد که تحقق خواهد یافت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (167, 1, 31, 'دستوری به ابن‌عباس', 'خطبه ۳۱-دستوری به ابن عباس \nروانشناسی طلحه و زبیر \nبا طلحه، دیدار مکن، زیرا در برخورد با طلحه، او را چون گاو وحشی یابی که شاخش را تابیده و آماده نبرد است، سوار بر مرکب سرکش می‌شود و می‌گوید، رام است، بلکه با زبیر! دیدار کن که نرمتر است، به او بگو، پسردایی تو می‌گوید. در حجاز مرا شناختی، و در عراق مرا نمی شناسی؟! چه شد که از پیمان خود باز گشتی؟! (جمله کوتاه (فما عدا مما بدا) برای نخستین بار از امام علی (ع) شنیده شد و پیش از امام از کسی نقل نگردید).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (168, 1, 32, 'روزگار و مردمان', 'خطبه ۳۲-روزگار و مردمان \nسیر ارتجاعی امت اسلامی \nای مردم، در روزگاری کینه توز، و پر از ناسپاسی و کفران نعمتها، صبح کرده ایم، که نیکوکار، بدکار به شمار می‌آید، و ستمگر بر تجاوز و سرکشی خود می‌افزاید، نه از آنچه می‌دانیم بهره می‌گیریم، و نه از آنچه نمی دانیم، می‌پرسیم، و نه از هیچ حادثه مهمی تا به ما فرود نیاید، می‌ترسیم. اقسام مردم (روانشناسی اجتماعی مسلمین، پس از پیامبر (ص)) در این روزگاران، مردم چهار گروهند، گروهی اگر دست به فساد نمی زنند، برای این است که، روحشان ناتوان، و شمشیرشان کند، و امکانات مالی، در اختیار ندارند، گروه دیگر، آنان که شمشیر کشیده، و شر و فسادشان را آشکار کرده اند. لشگرهای پیاده و سواره خود را گرد آورده، و خود آماده کشتار دیگرانند، دین را برای به دست آوردن مال دنیا تباه کردند که یا رئیس و فرمانده گروهی شوند، یا به منبری فرا رفته، خطبه بخوانند، چه بد تجارتی، که دنیا را بهای جان خود بدانی، و با آنچه که در نزد خداست معاوضه نمایی. گروهی دیگر، با اعمال آخرت، دنیا می‌طلبند، و با اعمال دنیا در پی کسب مقامهای معنوی آخرت نیستند، خود را کوچک و متواضع جلوه می‌دهند، گامها را ریاکارانه کوتاه برمی دارند، دامن خود \nرا جمع کرده، خود را همانند مومنان واقعی می‌آرایند، و پوششی الهی را وسیله نفاق و دورویی و دنیاطلبی خود قرار می‌دهند. و برخی دیگر، با پستی و ذلت و فقدان امکانات، از به دست آوردن قدرت محروم مانده اند، که خود را به زیور قناعت آراسته، و لباس زاهدان را پوشیده اند. اینان هرگز، در هیچ زمانی از شب و روز، از زاهدان راستین نبوده اند. وصف پاکان در جامعه مسخ شده در این میان گروه اندکی باقی مانده‌اند که یاد قیامت، چشمهایشان را بر همه چیز فرو بسته، و ترس رستاخیز، اشکهایشان را جاری ساخته است، برخی از آن‌ها از جامعه رانده شده، و تنها زندگی می‌کنند، و برخی دیگر ترسان و سرکوب شده یا لب فرو بسته و سکوت اختیار کرده اند، بعضی مخلصانه همچنان مردم را به سوی خدا دعوت می‌کنند، و بعضی دیگر گریان و دردناکند که تقیه و خویشتن داری، آنان را از چشم مردم انداخته است، و ناتوانی وجودشان را فرا گرفته گویا در دریای نمک فرو رفته اند، دهنهایشان بسته، و قلبهایشان مجروح است، آنقدر نصیحت کردند که خسته شدند، از بس سرکوب شدند، ناتوانند و چندان که کشته دادند، انگشت شمارند. روش برخورد با دنیا ای مردم باید دنیای حرام در چشمانتان از پر کاه خشکیده، و تفا \nله‌های قیچی شده دامداران، بی ارزشتر باشد، از پیشینیان خود پند گیرید، پیش از آنکه آیندگان از شما پند گیرند، این دنیای فاسد نکوهش شده را رها کنید، زیرا مشتاقان شیفته تر از شما را رها کرد. (برخی از افراد ناآگاه این خطبه را به معاویه نسبت دادند و در صورتی که بی تردید از سخنان امیرالمومنین (ع) است طلا کجا، خاک کجا؟ آب گوارا و شیرین کجا، آب شور کجا؟ دلیل ما سخن جاهز است که در ادبیات عرب مهارت تمام داشت و با آگاهی سخن می‌گفت او می‌گوید این خطبه به سخنان امام علی (ع) و روش مردم شناسی او نزدیک است تنها علی است که مردم در حالات گوناگون می‌شناسد و معرفی می‌کند تاکنون در کجا دیده اید که معاویه در یکی از سخنانش راه زهد و تقوا پیشه کند و راه رسم بندگان خدا را انتخاب نماید؟!).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (169, 1, 33, 'آنگاه که به جنگ اهل بصره می‌رفت، فرمود', 'خطبه ۳۳-در راه جنگ اهل بصره \n(به هنگام عزیمت به شهر بصره، جهت جنگ با ناکثین فرمود)\n(ابن عباس می‌گوید در (سرزمین ذی قار) خدمت امام رفتم که داشت کفش خود را پینه می‌زد، تا مرا دید، فرمود: قیمت این کفش چقدر است؟ گفتم بهایی ندارد. فرمود: به خدا سوگند، همین کفش بی ارزش نزد من از حکومت بر شما محبوب تر است مگر اینکه حقی را با آن بپا دارم، یا باطلی را دفع نمایم. آنگاه از خیمه بیرون آمد و برای مردم خطبه خواند)\n آثار بعثت پیامبر اسلام \nهمانا خداوند هنگامی محمد (ص) را مبعوث فرمود که هیچ کس از عرب، کتاب آسمانی نداشت، و ادعای پیامبری نمی کرد، پیامبر (ص) مردم جاهلی را به جایگاه کرامت انسانی پیش برد و به رستگاری رساند، که سرنیزه‌هایشان کندی نپذیرفت و پیروز شدند و جامعه آنان استحکام گرفت. \nویژگیهای نظامی و اخلاقی امام علی (ع)\n به خدا سوگند! من از پیشتازان لشگر اسلام بودم تا آنجا که صفوف کفر و شرک تار و مار شد، هرگز ناتوان نشدم، و نترسیدم، هم اکنون نیز همان راه را می‌روم، پرده باطل را می‌شکافم تا حق را از پهلوی آن بیرون آورم.\n شکوه از فتنه گری قریش \nمرا با قریش چه کار، به خدا سوگند، آن روز که کافر بودند با آن‌ها جنگیدم، و هم اکنون که فریب خورد \nه اند، با آن‌ها مبارزه می‌کنم، دیروز با آن‌ها زندگی می‌کردم و امروز نیز گرفتار آن‌ها می‌باشم. به خدا سوگند! قریش از ما انتقام نمی گیرد جز به آن علت که خداوند ما را از میان آنان برگزیده و گرامی داشت، ما هم آنان را در زندگی خود پذیرفتیم، پس چنان بودند که شاعر گفته است. (به جان خودم سوگند، هر صبح از شیر صاف نوشیدی. و سرشیر و خرمای بی هسته خوردی. ما این مقام و عظمت را به تو بخشیدیم در حالی که بلند مرتبت نبودی و در اطراف تو با سواران خود تا صبح نگهبانی دادیم و تو را حفظ کردیم).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (170, 1, 34, 'پیکار با مردم شام', 'خطبه ۳۴-پیکار با مردم شام \nنکوهش از سستی و نافرمانی کوفیان \nنفرین بر شما کوفیان! که از فراوانی سرزنش شما خسته شدم، آیا بجای زندگی جاویدان قیامت به زندگی زودگذر دنیا رضایت دادید؟ و بجای عزت و سربلندی، بدبختی و ذلت را انتخاب کردید؟ شما را به جهاد با دشمنتان دعوت می‌کنم، چشمتان از ترس در کاسه می‌گردد، گویا ترس از مرگ عقل شما را ربوده و چون انسانهای مست از خود بیگانه شده، حیران و سرگردانید، گویا عقلهای خود را از دست داده و درک نمی کنید، من دیگر هیچگاه به شما اطمینان ندارم، و شما را پشتوانه خود نمی پندارم، شما یاران شرافتمندی نیستید که کسی به سوی شما دست دراز کند. به شتران بی ساربان می‌مانید که هرگاه از یک طرف جمع آوری گردید، از سوی دیگر پراکنده می‌شوید. علل عقب ماندگی مردم کوفه به خدا سوگند! شما بد وسیله ای برای افروختن آتش جنگ هستید شما را فریب می‌دهند اما فریب دادن نمی دانید، سرزمین شما را پیاپی می‌گیرند و شما پروا ندارید، چشم دشمن برای حمله شما خواب ندارد ولی شما در غفلت بسر می‌برید، به خدا سوگند! شکست برای کسانی است که دست از یاری یکدیگر می‌کشند، سوگند به خدا! اگر جنگ سخت درگیر شود و حرارت و سوزش مرگ شما را در بر گیرد، از \nاطراف فرزند ابوطالب، همانند جدا شدن سر از تن، جدا و پراکنده می‌شوید. به خدا سوگند! کسی که دشمن را بر جان خویش مسلط گرداند که گوشتش را بخورد، و استخوانش را بشکند، و پوستش را جدا سازد، عجز و ناتوانی او بسیار بزرگ و قلب او بسیار کوچک و ناتوان است. تو اگر می‌خواهی اینگونه باش، اما من، به خدا سوگند از پای ننشینم و قبل از آنکه دشمن فرصت یابد با شمشیر آب دیده چنان ضربه ای بر پیکر او وارد سازم که ریزه‌های استخوان سرش را بپراکند، و بازوها و قدمهایش جدا گردد و از آن پس خدا هر چه خواهد انجام دهد. \nحقوق متقابل مردم و رهبری ای مردم، مرا بر شما و شما را بر من حقی واجب شده است، حق شما بر من، آنکه از خیرخواهی شما دریغ نورزم و بیت المال را میان شما عادلانه تقسیم کنم، و شما را آموزش دهم تا بی سواد و نادان نباشید، و شما را تربیت کنم تا راه و رسم زندگی را بدانید، و اما حق من بر شما این است که با من وفادار باشید، و در آشکار و نهان خیرخواهی کنید، هرگاه شما را فرا خواندم اجابت نمایید و فرمان دادم اطاعت کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (171, 1, 35, 'بعد از حکمیت', 'خطبه ۳۵-بعد از حکمیت \nضرورت ستایش پروردگار \nخدا را سپاس! هر چند که روزگار دشواریهای فراوان و حوادثی بزرگ پدید آورد، و شهادت می‌دهم جز خدای یگانه و بی مانند خدایی نباشد و جز او معبودی نیست، و گواهی می‌دهم محمد (ص) بنده و فرستاده اوست. علل شکست کوفیان پس از حمد و ستایش خدا! بدانید که نافرمانی از دستور نصیحت کننده مهربان دانا و باتجربه، مایه حسرت و سرگردانی و سرانجامش پشیمانی است، من رای و فرمان خود را نسبت به حکمیت به شما گفتم، و نظر خالص خود را در اختیار شما گذاردم. (ای کاش که از قصیر پسر سعد اطاعت می‌شد) ولی شما همانند مخالفانی ستمکار، و پیمان شکنانی نافرمان، از پذیرش آن سر باز زدید، تا آنجا که نصیحت کننده در پند دادن به تردید افتاد، و از پند دادن خودداری کرد، داستان من و شما چنان است که برادر هوازنی سروده است: (در سرزمین منعرج، دستور لازم را دادم اما نپذیرفتند، که فردا سزای سرکشی خود را چشیدند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (172, 1, 36, 'در بیم دادن نهروانیان', 'خطبه ۳۶-در بیم دادن نهروانیان \nتلاش در هدایت دشمن \nشما را از این می‌ترسانم! مبادا صبح کنید در حالی که جنازه‌های شما در اطراف رود نهروان و زمینهای پست و بلند آن افتاده باشد، بدون آن که برهان روشنی از پروردگار، و حجت و دلیل قاطعی داشته باشید، از خانه‌ها آواره گشته و به دام قضا گرفتار شدید. من شما را از این حکمیت نهی کردم ولی با سرسختی مخالفت نمودید، تا به دلخواه شما کشانده شدم. شما ای سبکسران، و بیخردان، ای ناکسان و بی پدران، من که این فاجعه را ببار نیاوردم و هرگز زیان شما را نخواستم (زیرا امام همواره با حکمیت مخالف بود).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (173, 1, 37, 'ذکر فضائل خود', 'خطبه ۳۷-ذکر فضائل خود \nویژگیها و فضائل امام علی (ع) \nآنگاه که همه از ترس سست شده، کنار کشیدند، من قیام کردم، و آن هنگام که همه خود را پنهان کردند من آشکارا به می‌دان آمدم، و آن زمان که همه لب فرو بستند، من سخن گفتم، و آن وقت که همه باز ایستادند من با راهنمایی نور خدا به راه افتادم، در مقام حرف و شعار صدایم از همه آهسته تر بود اما در عمل برتر و پیشتاز بودم، زمام امور را به دست گرفتم، و جلوتر از همه پرواز کردم، و پاداش سبقت در فضیلتها را بردم. همانند کوهی که تندبادها آن را به حرکت درنمی آورد، و طوفانها آن را از جای برنمی کند، کسی نمی توانست عیبی در من بیابد، و سخن چینی جای عیبجویی در من نمی یافت. خوارترین افراد نزد من عزیز است تا حق او را باز گردانم، و نیرومندها در نظر من پست و ناتوانند تا حق را از آن‌ها باز ستانم. علت سکوت و کناره گیری از خلافت در برابر خواسته‌های خدا راضی، و تسلیم فرمان او هستم، آیا می‌پندارید من به رسول خدا (ص) دروغی روا دارم؟ به خدا سوگند! من نخستین کسی هستم که او را تصدیق کردم، و هرگز اول کسی نخواهم بود که او را تکذیب کنم. در کار خود اندیشیدم دیدم پیش از بیعت، پیمان اطاعت و پیروی از سفارش رسول خدا (ص \n) را برعهده دارم، که از من برای دیگری پیمان گرفت (پیامبر (ص) فرمود اگر در امر حکومت کار به جدال و خونریزی کشانده شود، سکوت کن).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (174, 1, 38, 'معنی شبهه', 'خطبه ۳۸-معنی شبهه \nضرورت شناخت شبهات \nشبهه را برای این شبهه نامیدند که به حق شباهت دارد. اما دوستان خدا نور هدایت کننده آن‌ها در شبهات، یقین است. و راهنمای آنان مسیر هدایت الهی است، اما دشمنان خدا، دعوت کننده آنان در شبهات گمراهی است، و راهنمای آنان کوری است، آن کس که از مرگ بترسد نجات نمی یابد، و آن کس که مرگ را دوست دارد برای همیشه در دنیا نخواهد ماند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (175, 1, 39, 'نکوهش یاران و دعوت به جهاد', 'خطبه ۳۹-نکوهش یاران \nنکوهش کوفیان \nگرفتار کسانی شدم که چون امر می‌کنم فرمان نمی برند، و چون آن‌ها را فرا می‌خوانم اجابت نمی کنند. ای مردم بی اصل و ریشه، در یاری پروردگارتان برای چه در انتظارید؟ آیا دینی ندارید که شما را گرد آورد؟ و یا غیرتی که شما را به خشم وادارد؟ علل شکست و نابودی کوفیان در میان شما بپا خاسته فریاد می‌کشم، و عاجزانه از شما یاری می‌خواهم، اما به سخنان من گوش نمی سپارید، و فرمان مرا اطاعت نمی کنید، تا آن که پیامدهای ناگوار آشکار شد، نه با شما می‌توان انتقام خونی را گرفت، و نه با کمک شما می‌توان به هدف رسید. شما را به یاری برادرانتان می‌خوانم، مانند شتری که از درد بنالد، ناله و فریاد سرمی دهید، و یا همانند حیوانی که پشت آن زخم باشد، حرکتی نمی کنید، تنها گروه اندکی به سوی من آمدند که آن‌ها نیز ناتوان و مضطرب بودند، گویا آن‌ها را به سوی مرگ می‌کشانند، و مرگ را با چشمانشان می‌نگرند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (176, 1, 4, 'اندرز به مردم و رهانیدن از گمراهی', 'خطبه ۴-اندرز به مردم \nویژگیهای اهل بیت (ع)\n شما مردم به وسیله ما، از تاریکیهای جهالت نجات یافته و هدایت شدید، و به کمک ما، به اوج ترقی رسیدید، صبح سعادت شما با نور ما درخشید، کر است گوشی که بانگ بلند پندها را نشنود، و آن کس را که فریاد بلند، کر کند، آوای نرم حقیقت چگونه در او اثر خواهد کرد؟ قلبی که از ترس خدا لرزان است، همواره پایدار و با اطمینان باد \nمن همواره منتظر سرانجام حیله گری شما مردم بصره بودم، و نشانه‌های فریب خوردگی را در شما می‌نگریستم، تظاهر به دینداری شما پرده ای میان ما کشید ولی من با صفای باطن درون شما را می‌خواندم. من برای واداشتن شما به راه‌های حق، که در میان جاده‌های گمراه کننده بود بپا خاستم در حالی که سرگردان بودید، و راهنمایی نداشتید، به دنبال رهبر و راهنما بودید اما او را نمی یافتید، امروز زبان بسته را به سخن می‌آورم، دور باد رای کسی که با من مخالفت کند، از روزی که حق به من نشان داده شد، هرگز در آن شک و تردید نکردم، کناره گیری من چونان حضرت موسی (ع) برابر ساحران است که بر خویش بیمناک نبود، ترس او برای این بود که مبادا جاهلان پیروز گردند و دولت گمراهان حاکم گردد، امروز ما و شما بر سر دوراهی حق و باطل قرار داریم، آن کس که به وجود آب اطمینان دارد تشنه نمی ماند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (177, 1, 40, 'در پاسخ شعار خوارج', 'خطبه ۴۰-در پاسخ شعار خوارج \nضرورت حکومت \nسخن حقی است، که از آن اراده باطل شده! آری درست است، فرمانی جز فرمان خدا نیست، ولی اینها می‌گویند زمامداری جز برای خدا نیست، در حالی که مردم به زمامداری نیک یا بد، نیازمندند، تا مومنان در سایه حکومت، بکار خود مشغول و کافران هم بهره مند شوند، و مردم در استقرار حکومت، زندگی کنند، به وسیله حکومت بیت المال جمع آوری می‌گردد و به کمک آن با دشمنان می‌توان مبارزه کرد، جاده‌ها امن و امان، و حق ضعیفان از نیرومندان گرفته می‌شود، نیکوکاران در رفاه و از دست بدکاران، در امان می‌باشند. (در روایت دیگری آمده). منتظر حکم خدا درباره شما هستم. و نیز فرمود: اما در حکومت پاکان، پرهیزکار به خوبی انجام وظیفه می‌کند ولی در حکومت بدکاران، ناپاک از آن بهره مند می‌شود تا مدتش سرآید و مرگ فرا رسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (178, 1, 41, 'وفاداری و نهی از منکر', 'خطبه ۴۱-وفاداری و نهی از منکر \nپرهیز از حیله و نیرنگ \nای مردم! وفا همراه راستی است، که سپری محکمتر و نگهدارنده تر از آن سراغ ندارم، آن کس که از بازگشت خود به قیامت آگاه باشد خیانت و نیرنگ ندارد. اما امروز در محیط و زمانه ای زندگی می‌کنیم که بیشتر مردم حیله و نیرنگ را، زیرکی می‌پندارند، و افراد جاهل آنان را اهل تدبیر می‌خوانند. چگونه فکر می‌کنند؟ خدا بکشد آن‌ها را؟ چه بسا شخصی تمام پیش آمدهای آینده را می‌داند، و راه‌های مکر و حیله را می‌شناسد ولی امر و نهی پروردگار مانع اوست، و با اینکه قدرت انجام آن را دارد آن را به روشنی رها می‌سازد، اما آن کس که از گناه و مخالفت با دین پروا ندارد از فرصتها برای نیرنگ بازی، استفاده می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (179, 1, 42, 'پرهیز از هوسرانی و دنیاپرستی', 'خطبه ۴۲-پرهیز از هوسرانی \nپرهیز از آرزوهای طولانی و هواپرستی \nای مردم! همانا بر شما از دو چیز می‌ترسم، هواپرستی و آرزوهای طولانی. اما پیروی از خواهش نفس، انسان را از حق باز می‌دارد، و آرزوهای طولانی، آخرت را از یاد می‌برد. آگاه باشید! دنیا به سرعت پشت کرده و از آن جز باقیمانده اندکی از ظرف آبی که آن را خالی کرده باشند، نمانده است. به هوش باشید که آخرت به سوی ما می‌آید، دنیا و آخرت، هر یک فرزندانی دارند. بکوشید از فرزندان آخرت باشید، نه دنیا، زیرا در روز قیامت، هر فرزندی به پدر و مادر خویش باز می‌گردد. امروز هنگام عمل است نه حساب رسی، و فردا روز حساب رسی است، نه عمل. ((حذا) به معنای شتابان و (جذا) به معنای بریده از نیک و بد، که برخی نقل کردند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (180, 1, 43, 'علت درنگ در جنگ با شامیان', 'خطبه ۴۳-علت درنگ در جنگ \nواقع نگری در برخورد با دشمن \nمهیا شدن من برای جنگ با شامیان، در حالیکه (جریر) را به رسالت به طرف آنان فرستاده ام، بستن راه صلح و باز داشتن شامیان از راه خیر است، اگر آن را انتخاب کنند. من مدت اقامت (جریر) را در شام معین کردم، که اگر تاخیر کند یا فریبش دادند و یا از اطاعت من سر باز زده است. عقیده من اینکه صبر نموده با آن‌ها مدارا کنید، گر چه مانع آن نیستم که خود را برای پیکار آماده سازید. ضرورت جهاد با شامیان من بارها جنگ با معاویه را بررسی کرده ام، و پشت و روی آن را سنجیده، دیدم راهی جز پیکار، یا کافر شدن نسبت به آنچه پیامبر (ص) آورده باقی نمانده است، زیرا در گذشته کسی بر مردم حکومت می‌کرد که اعمال او حوادثی آفرید و باعث گفتگو و سر و صداهای فراوان شد، مردم آنگاه اعتراض کردند و تغییرش دادند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (181, 1, 44, 'سرزنش مصقله پسر هبیره', 'خطبه ۴۴-سرزنش مصقله پسر هبیره \nتاسف از فرار مصقله \nخدا روی مصقله را زشت گرداند، کار بزرگواران را انجام داد، اما خود چونان بردگان فرار کرد، هنوز ثناخوان به مداحی او برنخاسته بود که او را ساکت کرد، هنوز سخن ستایشگر او به پایان نرسیده بود که آن‌ها را به زحمت انداخت. اما اگر مردانه ایستاده بود همان مقدار که داشت از او می‌پذیرفتیم و تا هنگام قدرت و توانایی به او مهلت می‌دادیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (182, 1, 45, 'گذرگاه دنیا', 'خطبه ۴۵-گذرگاه دنیا \nضرورت ستایش پروردگار \nستایش خداوندی را سزاست که کسی از رحمت او مایوس نگردد، و از نعمتهای فراوان او بیرون نتوان رفت، خداوندی که از آمرزش او هیچ گنهکاری ناامید نگردد، و از پرستش او نباید سرپیچی کرد، خدایی که رحمتش قطع نمی گردد و نعمتهای او پایان نمی پذیرد. روش برخورد با دنیا دنیا خانه آرزوهایی است که زود نابود می‌شوند، و کوچ کردن از وطن حتمی است، دنیا شیرین و خوش منظر است که به سرعت به سوی خواهانش می‌رود، و بیننده را می‌فریبد، سعی کنید با بهترین زاد و توشه از آن کوچ کنید و بیش از کفاف و نیاز خود از آن نخواهید و بیشتر از آنچه نیاز دارید طلب نکنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (183, 1, 46, 'در هنگامی که به قصد شام سفر می‌کرد', 'خطبه ۴۶-در راه شام \nدعای سفر \nخدایا! از سختی سفر، و اندوه بازگشتن، و روبرو شدن با مناظر ناگوار در خانواده و مال و فرزند، به تو پناه می‌برم. پروردگارا! تو در سفر همراه ما و در وطن نسبت به بازماندگان ما سرپرست و نگهبانی، و جمع میان این دو را هیچ کس جز تو نتواند کرد، زیرا آن کس که سرپرست بازماندگان است نمی توانند همراه مسافر باشد و آنکه همراه و همسفر است سرپرست بازماندگان انسان نمی تواند باشد (چند جمله اول، از رسول خدا (ص) است که علی (ع) آن را با عباراتی رساتر به اتمام رساند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (184, 1, 47, 'درباره کوفه', 'خطبه ۴۷-درباره کوفه \nخبر از آینده کوفه \nای کوفه! تو را می‌نگرم گویا چنان چرمهای بازار عکاظ کشیده می‌شوی، زیر پای حوادث لگدکوب می‌گردی، و حوادث فراوان تو را در بر می‌گیرد، من به خوبی می‌دانم، ستمگری نسبت به تو قصد بد نمی کند مگر آنکه خداوند او را به بلایی گرفتار سازد یا قاتلی بر او مسلط گرداند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (185, 1, 48, 'هنگام لشکرکشی به شام', 'خطبه ۴۸-هنگام لشکرکشی به شام \nضرورت آمادگی رزمی \nستایش خداوند را سزاست، هر لحظه که شب فرا رسد، و پرده تاریکی فرو افتد. ستایش مخصوص پروردگاری است هر زمان که ستاره ای طلوع و غروب کند. ستایش خداوندی را سزاست که نعمتهای او پایان نمی پذیرد، و بخششهای او را جبران نتوان کرد. پس از ستایش پروردگار، پیشتازان لشگرم را از جلو فرستادم، و دستور دادم در کنار فرات توقف کنند، تا فرمان من به آن‌ها برسد. زیرا تصمیم گرفتم از آب فرات بگذرم و به سوی جمعیتی از شما که در اطراف دجله مسکن گزیده‌اند رهسپار گردم و آن‌ها را همراه شما بسیج نمایم، و از آن‌ها برای کمک و تقویت شما یاری بطلبم (منظور امام از (ملطاط) آنجایی است که دستور توقف داد، (کنار فرات) که به کنار فرات یا دریا (ملطاط) هم می‌گویند. و امام (ع) از کلمه (نطفه) آب فرات اراده کرده که شگفت آور است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (186, 1, 49, 'صفات خداوندی', 'خطبه ۴۹-صفات خداوندی \nخداشناسی \nستایش خداوندی را سزاست که از اسرار نهانها آگاه است، و نشانه‌های آشکاری در سراسر هستی بر وجود او شهادت می‌دهند، هرگز برابر چشم بینندگان ظاهر نمی گردد، نه چشم کسی که او را ندیده می‌تواند انکارش کند، و نه قلبی که او را شناخت می‌تواند مشاهده اش نماید، در والایی و برتری از همه پیشی گرفته، پس از او برتر چیزی نیست، و آنچنان به مخلوقات نزدیک است که از او نزدیکتر چیزی نمی تواند باشد. مرتبه بلند او را از پدیده‌هایش دور نساخته، و نزدیکی او با پدیده‌ها، او را مساوی چیزی قرار نداده است. عقلها را بر حقیقت ذات خود آگاه نساخته، اما از معرفت و شناسایی خود باز نداشته است. پس اوست که همه نشانه‌های هستی بر وجود او گواهی می‌دهند و دلهای منکران را بر اقرار به وجودش واداشته است، خدایی که برتر از گفتار تشبیه کنندگان و پندار منکران است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (187, 1, 5, 'پس از رحلت رسول خدا', 'خطبه ۵-پس از رحلت رسول خدا \nراه‌های پرهیز از فتنه‌ها \nای مردم، امواج فتنه‌ها را با کشتیهای نجات، درهم بشکنید، و از راه اختلاف و پراکندگی بپرهیزید. و تاجهای فخر و برتری جویی را بر زمین نهید، رستگار شد آن کس که با یاران بپا خواست، یا کناره گیری نمود و مردم را آسوده گذاشت، اینگونه زمامداری، چون آبی بدمزه، و لقمه ای گلوگیر است، و آن کس که میوه را کال و نارس چیند، مانند کشاورزی است که در زمین دیگری بکارد. فلسفه سکوت در شرائطی قرار دارم که اگر سخن بگویم، می‌گویند بر حکومت حریص است، و اگر خاموش باشم، می‌گویند: از مرگ ترسید!! هرگز! من و ترس از مرگ؟! پس از آن همه جنگها و حوادث ناگوار؟! سوگند به خدا، انس و علاقه فرزند ابیطالب به مرگ در راه خدا، از علاقه طفل به پستان مادر بیشتر است، اینکه سکوت برگزیدم، از علوم و حوادث پنهانی، آگاهی دارم که اگر باز گویم مضطرب می‌گردید، چون لرزیدن ریسمان در چاه‌های عمیق!!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (188, 1, 50, 'در بیان فتنه', 'خطبه ۵۰-در بیان فتنه \nعلل پیدایش فتنه‌ها \nهمواره آغاز پدید آمدن فتنه‌ها، هواپرستی، و بدعت گذاری در احکام آسمانی است، نوآوریهایی که قرآن با آن مخالف است، و گروهی (با دو انحراف یاد شده) بر گروه دیگر سلطه و ولایت یابند، که برخلاف دین خداست. پس اگر باطل با حق مخلوط نمی شد، بر طالبان حق پوشیده نمی ماند، و اگر حق از باطل جدا و خالص می‌گشت زبان دشمنان قطع می‌گردید. اما قسمتی از حق و قسمتی از باطل را می‌گیرند و به هم می‌آمیزند، آنجاست که شیطان بر دوستان خود چیره می‌گردد، و تنها آنان که مشمول لطف و رحمت پروردگارند نجات خواهند یافت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (189, 1, 51, 'در هنگامی که یاران معاویه بر فرات غلبه یافتند', 'خطبه ۵۱-یاران معاویه و غلبه بر فرات \nفرمان خط شکستن و آزاد کردن آب فرات \nشامیان با بستن آب شما را به پیکار دعوت کردند. اکنون بر سر دوراهی قرار دارید، یا به ذلت و خواری بر جای خود بنشینید، و یا شمشیرها را از خون آن‌ها سیراب سازید تا از آب سیراب شوید، پس بدانید که مرگ در زندگی توام با شکست، و زندگی جاویدان در مرگ پیروزمندانه شماست. آگاه باشید! معاویه گروهی از گمراهان را همراه آورده و حقیقت را از آنان می‌پوشاند، تا کورکورانه گلوهاشان را آماج تیر و شمشیر کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (190, 1, 52, 'در نکوهش دنیا', 'خطبه ۵۲-در نکوهش دنیا \nتعریف دنیا \nآگاه باشید، گویا دنیا پایان یافته، و وداع خویش را اعلام داشته است، خوبیهایش ناشناخته مانده به سرعت پشت کرده می‌گذرد، ساکنان خود را به سوی نابود شدن می‌کشاند، و همسایگانش را به سوی مرگ می‌راند. آنچه از دنیا شیرین بود تلخ شده، و آنچه صاف و زلال بود تیرگی پذیرفت، و بیش از ته مانده ظرف آب ریخته شده از آن باقی نمانده است، یا جرعه ای آب که با آن عطش تشنگان دنیا فرو نخواهد نشست. ای بندگان خدا! از سرایی کوچ کنید که سرانجام آن نابودی است، مبادا آرزوها بر شما چیره گردد، مپندارید که عمر طولانی خواهید داشت. والایی نعمتهای قیامت به خدا سوگند! اگر مانند شتران بچه مرده ناله سردهید، و چونان کبوتران نوحه سرایی کنید، و مانند راهبان زاری نمایید، و برای نزدیک شدن به حق، و دسترسی به درجات معنوی، و آمرزش گناهانی که ثبت شده و ماموران حق آن را نگه می‌دارند، دست از اموال و فرزندان بکشید سزاوار است زیرا: برابر پاداشی که برایتان انتظار دارم، و عذابی که از آن بر شما می‌ترسم، اندک است به خدا سوگند! اگر دلهای شما از ترس آب شود، و از چشمهایتان با شدت شوق به خدا، یا ترس از او، خون جاری گردد، و اگر تا پایان دنیا زند \nه باشید و تا آنجا که می‌توانید در اطاعت از فرمان حق بکوشید، در برابر نعمتهای بزرگ پروردگار به خصوص نعمت ایمان، ناچیز است. \nوصف قربانی کمال قربانی در این است که گوش و چشم آن سالم باشد، هرگاه گوش و چشم سالم بود، قربانی کامل و تمام است، گرچه شاخش شکسته باشد و با پای لنگ به قربانگاه آید (منظور امام از کلمه (منسک) در اینجا (قربانگاه) است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (191, 1, 53, 'وصف قربانی', 'خطبه ۵۳- وصف قربانی \nکمال قربانی در این است که گوش و چشم آن سالم باشد، هرگاه گوش و چشم سالم بود، قربانی کامل و تمام است، گرچه شاخش شکسته باشد و با پای لنگ به قربانگاه آید (منظور امام از کلمه (منسک) در اینجا (قربانگاه) است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (192, 1, 54, 'در مساله بیعت', 'خطبه ۰۵۴-در مساله بیعت \nوصف روز بیعت \nمردم همانند شتران تشنه ای که به آب نزدیک شده، و ساربان رهاشان کرده، و عقال (پای بند) از آن‌ها گرفته، بر من هجوم آوردند و به یکدیگر پهلو می‌زدند، فشار می‌آوردند، چنان که گمان کردم مرا خواهند کشت، یا بعضی به وسیله بعض دیگر می‌میرند و پایمال می‌گردند. پس از بیعت عمومی مردم، مسئله جنگ با معاویه را ارزیابی کردم، همه جهات آن را سنجیدم تا آن که مانع خواب من شد، دیدم چاره ای جز یکی از این دو راه ندارم. یا با آنان مبارزه کنم، و یا آنچه را که محمد (ص) آورده، انکار نمایم، پس به این نتیجه رسیدم که، تن به جنگ دادن آسانتر از تن به کیفر پروردگار دادن است، و از دست دادن دنیا آسانتر از رها کردن آخرت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (193, 1, 55, 'درباره تاخیر جنگ با شامیان', 'خطبه ۵۵-درباره تاخیر جنگ \nتوضیحاتی پیرامون جنگ صفین \nاینکه می‌گویید، خویشتن داری از ترس مرگ است، به خدا سوگند! باکی ندارم که من به سوی مرگ روم یا مرگ به سوی من آید، و اگر تصور می‌کنید در جنگ با شامیان تردید دارم، به خدا سوگند! هر روزی که جنگ را به تاخیر می‌اندازم برای آن است که آرزو دارم عده ای از آن‌ها به ما ملحق شوند و هدایت گردند. و در لابلای تاریکیها، نور مرا نگریسته به سوی من بشتابند، که این برای من از کشتار آنان در راه گمراهی بهتر است، گرچه در این صورت نیز به جرم گناهانشان گرفتار می‌گردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (194, 1, 56, 'در وصف اصحاب رسول الله صلی الله علیه و آله', 'خطبه ۵۶-در وصف اصحاب رسول \nیاد مبارزات دوران پیامبر (ص) در صفین \nدر رکاب پیامبر خدا (ص) بودیم و با پدران و فرزندان و برادران و عموهای خود جنگ می‌کردیم، که این مبارزه بر ایمان و تسلیم ما می‌افزود، و ما را در جاده وسیع حق و صبر و بردباری برابر ناگواریها و جهاد و کوشش برابر دشمن، ثابت قدم می‌ساخت. گاهی یک نفر از ما و دیگری از دشمنان ما، مانند دو پهلوان نبرد می‌کردند، و هر کدام می‌خواست کار دیگری را بسازد و جام مرگ را به دیگری بنوشاند، گاهی ما بر دشمن پیروز می‌شدیم و زمانی دشمن بر ما غلبه می‌کرد. پس آنگاه که خدا، راستی و اخلاص ما را دید، خواری و ذلت را بر دشمنان ما نازل و پیروزی را به ما عنایت فرمود، تا آنجا که اسلام استحکام یافته فراگیر شد و در سرزمینهای پهناوری نفوذ کرد، به جانم سوگند! اگر ما در مبارزه مثل شما بودیم هرگز پایه ای برای دین استوار نمی ماند، و شاخه ای از درخت ایمان سبز نمی گردید، به خدا سوگند! شما هم اکنون از سینه شتر خون می‌دوشید و سرانجامی جز پشیمانی ندارید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (195, 1, 57, 'به یاران خود', 'خطبه ۵۷-به یاران خود \nخبر از سلطه ستمگری بی باک \nآگاه باشید! پس از من مردی با گلوی گشاده و شکمی بزرگ بر شما مسلط خواهد شد، که هر چه بیابد می‌خورد، و تلاش می‌کند آنچه ندارد به دست آورد، او را بکشید! ولی هرگز نمی توانید او را بکشید. آگاه باشید! به زودی معاویه شما را به بیزاری و بدگویی من وادار می‌کند، بدگویی را به هنگام اجبار دشمن اجازه می‌دهم که مایه بلندی درجات من و نجات شماست، اما هرگز در دل از من بیزاری نجویید که من بر فطرت توحید تولد یافته ام و در ایمان و هجرت از همه پیش قدم بوده ام.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (196, 1, 58, 'با خوارج', 'خطبه ۵۸-با خوارج \nخبر از آینده شوم خوارج \nسنگ حوادث و بلا بر شما ببارد، چنانکه اثری از شما باقی نگذارد. آیا پس از ایمان من به خدا، و جهاد کردن من در رکاب رسول خدا (ص) به کفر خویش گواهی دهم؟ اگر چنین کنم گمراه شده و از هدایت شدگان نخواهم بود. پس به بدترین جایگاه رهسپار شوید، و به راه گذشتگان باز گردید، آگاه باشید! به زودی پس از من، به خواری و ذلت گرفتار می‌شوید و شمشیر برنده بر شما مسلط می‌گردد، و به استبدادی دچار خواهید شد که برای دیگر ستمگران راه و رسم حکومت قرار خواهد گرفت. کلمه (آثر) در نقل دیگری (آبر) آمده، به معنای اصلاح کننده، و آثر یعنی خبردهنده، یعنی خبردهنده ای از شما باقی نمی ماند یا کسی باقی نمی ماند که درختان خرمای شما را اصلاح کند (یابرالنخل).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (197, 1, 59, 'درباره خوارج', 'خطبه ۵۹-درباره خوارج \n(به هنگام حرکت برای جنگ با خوارج شخصی گفت، خوارج از پل نهروان عبور کردند امام فرمود) \nخبر از قتلگاه خوارج \nقتلگاه خوارج این سوی نهر است، به خدا سوگند از آن‌ها جز ده نفر باقی نمی ماند، و از شما نیز ده نفر کشته نخواهد شد. (منظور امام از (نطفه) آب نهر است که از فصیحترین کنایه در رابطه با آب هر چند زیاد و فراوان باشد).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (198, 1, 6, 'آماده نبرد', 'خطبه ۶-آماده نبرد \nآگاهی و مظلومیت امام (ع) \nبه خدا سوگند! از آگاهی لازمی برخوردارم و هرگز غافلگیر نمی شوم، که دشمنان ناگهان مرا محاصره کنند و با نیرنگ دستگیرم نمایند، من همواره با یاری انسان حق طلب، بر سر آن می‌کوبم که از حق روی گردان است، و با یاری فرمانبر مطیع، نافرمان اهل تردید را در هم می‌کوبم، تا آن روز که دوران زندگانی من به سر آید پس، سوگند به خدا، من همواره از حق خویش محروم ماندم، و از هنگام وفات پیامبر (ص) تا امروز حق مرا از من باز داشتند و به دیگری اختصاص دادند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (199, 1, 60, 'خبر دادن از پایان کار خوارج', 'خطبه ۶۰-خبر دادن از پایان کار خوارج \nنه، سوگند به خدا هرگز! آن‌ها نطفه‌هایی در پشت پدران و رحم مادران وجود خواهند داشت، هرگاه که شاخی از آنان سر برآورد قطع می‌گردد تا اینکه آخرینشان به راهزنی و دزدی تن درمی دهند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (200, 1, 61, 'در باب خوارج', 'خطبه ۶۱-در باب خوارج \nنهی از کشتار خوارج \nبعد از من با خوارج نبرد نکنید، زیرا کسی که در جستجوی حق بوده و خطا کرد مانند کسی نیست که طالب باطل بوده و آن را یافته است. (منظور امام از گروه دوم معاویه و یاران او هستند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (201, 1, 62, 'هنگامی که او را از کشته شدن ناگهانی هشدار دادند', 'خطبه ۶۲-هشدار به کشته شدن \nموضعگیری امام برابر تهدید به قتل و ترور \nپروردگار برای من پوششی استوار قرار داد که مرا حفظ نماید، هنگامی که عمرم به سرآید، از من دورشده و مرا تسلیم مرگ می‌کند، که در آن روز نه تیر خطا می‌رود و نه زخم بهبود می‌یابد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (202, 1, 63, 'نکوهش دنیا', 'خطبه ۶۳-نکوهش دنیا \nروش برخورد با دنیا \nآگاه باشید! دنیا خانه ای است که کسی ایمنی ندارد مگر در آن به جمع آوری توشه آخرت پردازد، و از کارهای دنیایی کسی نجات نمی یابد. مردم به وسیله دنیا آزمایش می‌شوند، پس هر چیزی از دنیا را برای دنیا به دست آورند از کفشان بیرون می‌رود، و بر آن محاسبه خواهند شد، و آنچه را در دنیا برای آخرت تهیه کردند به آن خواهند رسید، و با آن خواهند ماند، دنیا در نظر خردمندان چونان سایه ای است که هنوز گسترش نیافته، کوتاه می‌گردد، و هنوز فزونی نیافته کاهش می‌یابد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (203, 1, 64, 'تشویق به عمل صالح', 'خطبه ۶۴-تشویق به عمل صالح \nشتافتن به سوی اعمال پسندیده \nای بندگان خدا! از خدا بپرهیزید. و با اعمال نیکو به استقبال اجل بروید، با چیزها فانی شدنی دنیا آنچه که جاویدان می‌ماند خریداری کنید. از دنیا کوچ کنید که برای کوچ دادنتان تلاش می‌کنند، آماده مرگ باشید که بر شما سایه افکنده است، چون مردمی باشید که بر آن‌ها بانگ زدند و بیدار شدند، و دانستند دنیا خانه جاویدان نیست و آن را با آخرت مبادله کردند. خدای سبحان شما را بیهوده نیافرید، و بحال خود وانگذاشت، میان شما تا بهشت یا دوزخ، فاصله اندکی جز رسیدن مرگ نیست. زندگی کوتاهی که گذشتن لحظه‌ها از آن می‌کاهد، و مرگ آن را نابود می‌کند، سزاوار است که کوتاه مدت باشد، زندگی که شب و روز آن را به پیش می‌راند بزودی پایان خواهد گرفت، مسافری که سعادت یا شقاوت همراه می‌برد باید بهترین توشه را با خود بردارد، از این خانه دنیا زاد و توشه بردارید که فردای رستاخیز نگهبانتان باشد. بنده خدا باید از پروردگار خود بپرهیزد، خود را پند دهد و توبه را پیش فرستد، و بر شهوات غلبه کند، زیرا زمان مرگ پنهان، و آرزوها فریبنده اند، و شیطان، همواره با اوست و گناهان را زینت و جلوه می‌دهد تا بر او تسلط یابد، انسان را در انتظار توبه نگه می‌دارد که آن را تاخیر اندازد، و تا زمان فرا رسیدن مرگ از آن غفلت نماید. وای بر غفلت زده ای که عمرش بر ضد او گواهی دهد، و روزگار او را به شقاوت و پستی کشاند، از خدا می‌خواهیم که ما و شما را برابر نعمتها مغرور نسازد، و هیچ چیزی ما را از اطاعت پروردگار باز ندارد، که پس از فرا رسیدن مرگ دچار پشیمانی و اندوه نگرداند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (204, 1, 65, 'در علم الهی', 'خطبه ۶۵-در علم الهی \n(در سال ۳۸ هجری پس از پایان جنگ صفین در مسجد کوفه این سخنرانی را ایراد کرد) \nخدا شناسی «شناخت صفات خدا» \nستایش خداوندی را سزاست که هیچ صفتی بر صفت دیگرش پیشی نگرفته تا بتوان گفت: پیش از آنکه آخر باشد اول است و قبل از آنکه باطن باشد ظاهر است. هر واحد و تنهایی جز او، اندک است، هر عزیزی جز او ذلیل، و هر نیرومندی جز او ضعیف و ناتوان است، هر مالکی جز او بنده، و هر عالمی جز او دانش آموز است، هر قدرتمندی جز او، گاهی توانا و زمانی ناتوان است، هر شنونده ای جز خدا در شنیدن صداهای ضعیف کر و برابر صداهای قوی، ناتوان است و آوازهای دور را نمی شنود. هر بیننده ای جز خدا، از مشاهده رنگهای ناپیدا و اجسام بسیار کوچک ناتوان است، هر ظاهری غیر از او پنهان، و هر پنهانی جز او آشکار است. مخلوقات را برای تقویت فرمانروایی، و یا برای ترس از آینده، یا یاری گرفتن در مبارزه با همتای خود، و یا برای فخر و مباهات شریکان، و یا ستیزه جویی مخالفان نیافریده است، بلکه همه، آفریده‌های او هستند و در سایه پرورش او، بندگانی فروتن و فرمانبردارند. خدا در چیزی قرار نگرفته تا بتوان گفت در آن جاست، و دور از پدیده‌ها نیست تا بتوان گفت از آن‌ها جداست. آفرینش موجودات او را در آغاز ناتوان نساخته، و از تدبیر پدیده‌های آفریده شده باز نمانده است، نه به خا \nطر آنچه آفریده قدرتش پایان گرفته و نه در آنچه فرمان داد و مقدر ساخت دچار تردید شد. بلکه فرمانش استوار، و علم او مستحکم، و کارش بی تزلزل است، خدایی که به هنگام بلا و سختی به او امیدوار، و در نعمتها از او بیمناکند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (205, 1, 66, 'در آداب جنگ', 'خطبه ۶۶-در آداب جنگ \n(در یکی از روزهای آغازین جنگ صفین در سال ۳۷ هجری برای لشکریان خود ایراد فرمود) \nآموزش تاکتیک‌های نظامی \nای گروه مسلمانان، لباس زیرین را ترس خدا، و لباس رویین را آرامش و خونسردی قرار دهید، دندانها را برهم بفشارید تا مقاومت شما برابر ضربات شمشیر دشمن بیشتر گردد، زره نبرد را کامل کنید، پیش از آنکه شمشیر را از غلاف بیرون کشید چند بار تکان دهید، با گوشه چشم به دشمن بنگرید و ضربت را از چپ و راست فرود آورید، و با تیزی شمشیر بزنید، و با گام برداشتن به پیش، شمشیر را به دشمن برسانید، و بدانید که در پیش روی خدا و پسر عموی پیغمبر (ص) قرار دارید. پی در پی حمله کنید و از فرار شرم دارید، زیرا فرار در جنگ، لکه ننگی برای نسلهای آینده و مایه آتش روز قیامت است، از شهادت خرسند باشید و به آسانی از آن استقبال کنید. به آن گروه فراوان اطراف خیمه پر زرق و برق و طناب در هم افکنده (فرماندهی معاویه) به سختی حمله کنید، و به قلب آن‌ها هجوم برید که شیطان در کنار آن پنهان شده، دستی برای حمله در پیش، و پایی برای فرار آماده دارد. مقاومت کنید تا ستون حق بر شما آشکار گردد. شما برترید، خدا با شماست، و از پاداش اعمالتان نمی کاهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (206, 1, 67, 'در معنی انصار', 'خطبه ۶۷-در معنی انصار \n(وقتی در ۲۸ صفر سال ۱۱ هجری ماجرای سقیفه را به امام رساندند پرسید: انصار چه گفتند پاسخ شنید که انصار گفتند: زمامداری از ما، و رهبری از شما مهاجرین انتخاب گردد. پس امام علیه السلام فرمود) \nرد استدلال انصار و قریش نسبت به امامت در سقیفه\nچرا با آن‌ها به این آیه قرآن استدلال نکردید که پیامبر (ص) درباره انصار سفارش فرمود: با نیکان آن‌ها به نیکی رفتار کنید و از بدکاران آن‌ها درگذرید! پرسیدند چگونه این حدیث انصار را از زمامداری دور می‌کند؟ پاسخ داد: اگر زمامداری و حکومت در آنان بود، سفارش کردن درباره آن‌ها معنایی نداشت (سپس پرسید، قریش در سقیفه چه گفتند؟ جواب دادند: قریش می‌گفتند ما از درخت رسالتیم، امام (ع) فرمود) به درخت رسالت استدلال کردند!! اما میوه اش را ضایع ساختند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (207, 1, 68, 'پس از شهادت محمد بن ابی‌بکر', 'خطبه ۶۸-شهادت محمد بن ابی بکر \n(پس از آنکه محمد بن ابی بکر در مصر در سال ۳۸ هجری شهید شد فرمود:) \nستودن‌هاشم بن عتبه برای فرمانداری مصر پس از شهادت محمد بن ابی بکر \nمی خواستم‌هاشم بن عتبه را فرماندار مصر کنم، اگر او را انتخاب می‌کردم می‌دان را برای دشمنان خالی نمی گذارد، و به عمروعاص و لشگریانش فرصت نمی داد، نه اینکه بخواهم محمد بن ابی بکر را نکوهش کنم، چه او مورد علاقه و محبت من بوده و در دامنم پرورش یافته بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (208, 1, 69, 'سرزنش یاران', 'خطبه ۶۹-سرزنش یاران \nعلل نکوهش کوفیان \nچه مقدار با شما کوفیان مدارا کنم؟ چونان مدارا کردن با شتران نو باری که از سنگینی بار، پشتشان زخم شده است، و مانند وصله زدن جامعه فرسوده ای که هرگاه از جانبی آن را بدوزند، از سوی دیگر پاره می‌گردد؟ هرگاه دسته ای از مهاجمان شام به شما یورش آورند، هر کدام از شما به خانه رفته، درب خانه را می‌بندید، و چون سوسمار در سوراخ خود می‌خزید، و چون کفتار در لانه می‌آرمید. سوگند به خدا! ذلیل است آن کس که شما یاری دهندگان او باشید، کسی که با شما تیراندازی کند گویا تیری بدون پیکان رها ساخته است. به خدا سوگند! شما در خانه‌ها فراوان، و زیر پرچمهای می‌دان نبرد اندکید، و من می‌دانم که چگونه باید شما را اصلاح کرد و کجیهای شما را راست کرد، اما اصلاح شما را با فاسد کردن روح خویش جایز نمی دانم، خدا بر پیشانی شما داغ ذلت بگذارد، و بهره شما را اندک شمارد، شما آنگونه که باطل را می‌شناسید از حق آگاهی ندارید، و در نابودی باطل تلاش نمی کنید آنسان که در نابودی حق کوشش دارید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (209, 1, 7, 'نکوهش دشمنان', 'خطبه ۷-نکوهش دشمنان \nشناخت پیروان شیطان \nمنحرفان شیطان را، معیار کار خود گرفتند، و شیطان نیز آن‌ها را دام خود قرار داد، و در دلهای آنان تخم گذارد، و جوجه‌های خود را در دامانشان پرورش داد، پس با چشمهای آنان می‌نگریست، و با زبانهای آنان، سخن می‌گفت: پس با یاری آن‌ها بر مرکب گمراهی سوار شد، و کردارهای زشت را در نظرشان زیبا جلوه داد، مانند رفتار کسی که نشان می‌داد در حکومت شیطان شریک است، و با زبان شیطان، باطل می‌گوید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (210, 1, 70, 'در سحرگاه روزی که ضربتش زدند، فرمود', 'خطبه ۷۰-پس از ضربت خوردن \nشکوه‌ها با پیامبر (ص) \nهمان گونه که نشسته بودم، خواب چشمانم را ربود، رسول خدا (ص) را دیدم، پس گفتم ای رسول خدا! از امت تو چه تلخیها دیدم و از لجبازی و دشمنی آن‌ها چه کشیدم؟ پیامبر (ص) فرمود: نفرینشان کن. گفتم: خدا بهتر از آنان را به من بدهد، و بجای من شخص بدی را بر آن‌ها مسلط گرداند. (کلمه (اود) یعنی کجی و انحراف و (لدد) یعنی دشمنی و خصومت، و این از فصیحترین کلمات است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (211, 1, 71, 'در نکوهش مردم عراق', 'خطبه ۷۱-در نکوهش مردم عراق \nعلل نکوهش اهل عراق \n(پس از ستایش پروردگار!)\nای مردم عراق! همانا شما به زن بارداری می‌مانید که در آخرین روزهای بارداری جنین خود را سقط کند، و سرپرستش بمیرد، و زمانی طولانی بی شوهر ماند، و میراث او را خویشاوندان دور غارت کنند. آگاه باشید! من با اختیار خود به سوی شما نیامدم بلکه به طرف دیار شما کشانده شدم، به من خبر دادند که می‌گویید علی دروغ می‌گوید!! خدا شما را بکشد، به چه کسی دروغ روا داشته ام؟ آیا به خدا دروغ روا داشتم؟ در حالی که من نخستین کسی هستم که به خدا ایمان آوردم، یا بر پیامبرش؟ من اول کسی بودم که او را تصدیق کردم! نه به خدا هرگز!! آنچه گفتم واقعیتی است که شما از دانستن آن دورید، و شایستگی درک آن را ندارید، مادرتان در سوگ شما زاری کند وای، وای، سردهد. پیمانه علم را بر شما رایگان بخشیدم، اگر ظرفیت داشته باشید. و به زودی خبر آن را خواهید فهمید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (212, 1, 72, 'درود بر پیامبر', 'خطبه ۷۲-درود بر پیامبر \nویژگیهای پیامبر (ص) \nبار خدایا! ای گستراننده هر گسترده، و ای نگهدارنده آسمانها، و ای آفریننده دلها بر فطرتهای خویش، دلهای رستگار و دلهای شقاوت زده. گرامی ترین درودها و افزون ترین برکات خود را بر محمد (ص) بنده و فرستاده ات اختصاص ده، که خاتم پیامبران گذشته است، و گشاینده درهای بسته و آشکارکننده حق با برهان است. دفع کننده لشگرهای باطل، و درهم کوبنده شوکت گمراهان است، آنگونه که بار سنگین رسالت را بر دوش کشید، و به فرمانت قیام کرد، و به سرعت در راه خشنودی تو گام برداشت، حتی یک قدم به عقب برنگشت، و اراده او سست نشد، و در پذیرش و گرفتن وحی، نیرومند بود، حافظ و نگهبان عهد و پیمان تو بود، و در اجرای فرمانت تلاش کرد تا آنجا که نور حق را آشکار، و راه را برای جاهلان روشن ساخت، و دلهایی که در فتنه و گناه فرو رفته بودند هدایت شدند. پرچمهای حق را برافراشت احکام نورانی را برپا کرد، پس او پیامبر امین، و مورد اعتماد، و گنجینه دار علم نهان تو، و شاهد روز رستاخیز، و برانگیخته تو برای بیان حقائق، و فرستاده تو به سوی مردم است. دعا برای پیامبر (ص) پروردگارا! برای پیامبر (ص) در سایه لطف خود جای با وسعتی بگشای، و از فض \nل و کرامت پاداش او را فراوان گردان. خداوندا! کاخ آیین او را از هر بنایی برتر، و مقام او را در پیشگاه خود گرامی دار، نورش را کامل گردان، و پاداش رسالت او را پذیرش گواهی و شفاعت و قبول گفتار او قرار ده، زیرا که دارای منطقی عادلانه، و راه جداکننده حق از باطل بود. بارخدایا! بین ما و پیغمبرت در نعمتهای جاویدان، و زندگانی خوش، و آرزوهای برآورده، و خواسته‌های به انجام رسیده، در کمال آرامش، و در نهایت اطمینان، همراه با مواهب و هدایای باارزش، جمع گردان!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (213, 1, 73, 'درباره مروان', 'خطبه ۷۳-درباره مروان \nخبر غیبی از حکومت چهار فرمانروای فاسد، از پسران مروان\nمگر پس از کشته شدن عثمان با من بیعت نکرد؟ مرا به بیعت او نیازی نیست! دست او دست یهودی است، اگر با دست خود بیعت کند، در نهان بیعت را می‌شکند، آگاه باشید، او حکومت کوتاه مدتی خواهد داشت، مانند فرصت کوتاه سگی که با زبان بینی خود را پاک کند. او پدر چهار فرمانرواست (قوچهای چهارگانه) و امت اسلام از دست او و پسرانش روز خونینی خواهند داشت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (214, 1, 74, 'هنگام بیعت کردن اعضاء شورا با عثمان', 'خطبه ۷۴-هنگام بیعت شورا با عثمان \nویژگیهای امام علی (ع) از زبان خود \nهمانا می‌دانید که سزاوارتر از دیگران به خلافت من هستم. سوگند به خدا! به آنچه انجام داده اید گردن می‌نهم، تا هنگامی که اوضاع مسلمین روبراه باشد، و از هم نپاشد، و جز من به دیگری ستم نشود، و پاداش این گذشت و سکوت و فضیلت را از خدا انتظار دارم، و از آنهمه زر و زیوری که بدنبال آن حرکت می‌کنید، پرهیز می‌کنم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (215, 1, 75, 'پاسخ به اتهامی ناروا', 'خطبه ۷۵-پاسخ به اتهامی ناروا \n(وقتی شنید که بنی امیه با تهمتهای فراوان آنحضرت را در خون عثمان شریک می‌پندارند، فرمود) \nدفاعیات امام برابر تهمتها \nآیا شناختی که بنی امیه از روحیات من دارند آنان را از عیبجوئی بر من باز نمی دارد؟ و آیا سوابق مبارزات من نادانان را بر سر جای خود نمی نشاند؟ که به من تهمت نزنند؟ آنچه خدا آنان را بدان پند داد از بیان من رساتر است. من مارقین (از دین خارج شدگان) را با حجت و برهان مغلوب می‌کنم و دشمن ناکثین (پیمان شکنان) و تردید دارندگان در اسلام می‌باشم، شبهات را باید در پرتو کتاب خدا قرآن، شناخت و بندگان خدا به آنچه در دل دارند پاداش داده می‌شوند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (216, 1, 76, 'اندرز', 'خطبه ۷۶-اندرز \nصفات بنده پرهیزکار \nخدا رحمت کند کسی را که چون سخن حکیمانه بشنود، خوب فرا گیرد، و چون هدایت شود بپذیرد، دست به دامن هدایت کننده زند و نجات یابد، مراقب خویش در برابر پروردگار باشد، از گناهان خود بترسد، خالصانه گام بردارد، عمل نیکو انجام دهد، ذخیره ای برای آخرت فراهم آورد، و از گناه بپرهیزد، همواره اغراض دنیایی را از سر دور کند، و درجات آخرت به دست آورد. با خواسته‌های دل مبارزه کند، آرزوهای دروغین را طرد و استقامت را مرکب نجات خود قرار دهد. و تقوی را زاد و توشه روز مردن گرداند، در راه روشن هدایت قدم بگذارد، و از راه روشن هدایت فاصله نگیرد. چند روز زندگی دنیا را غنیمت شمارد و بیش از آنکه، مرگ او فرا رسد خود را آماده سازد، و از اعمال نیکو، توشه آخرت برگیرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (217, 1, 77, 'نکوهش رفتار بنی‌امیه با آن حضرت', 'خطبه ۷۷-نکوهش رفتار بنی امیه \nهشدار به غاصبان بنی امیه \nبنی امیه، از میراث پیامبر (ص) جز اندک چیزی، به من نمی پردازند، سوگند به خدا اگر زنده ماندم، بنی امیه را از حکومت دور می‌کنم چونان قصابی که شکمبه خاک آلوده را دور می‌افکند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (218, 1, 78, 'نیایش', 'خطبه ۷۸-نیایش \nنیایش امام \nخدایا! بر من ببخش آنچه را از من بدان داناتری، و اگر بار دیگر به آن بازگردم. تو نیز به بخشایش بازگرد. خدایا! آنچه از اعمال نیکو که تصمیم گرفتم و انجام ندادم ببخش. خدایا! ببخش آنچه را که با زبان به تو نزدیک شدم ولی با قلب آن را ترک کردم. خدایا! ببخش نگاه‌های اشارت آمیز و سخنان بی فایده، و خواسته‌های بی مورد دل، و لغزشهای زبان.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (219, 1, 79, 'پاسخ اخترشناس', 'خطبه ۷۹-پاسخ اخترشناس \nپرهیز از توجه به غیر خدا \nگمان می‌کنی تو از آن ساعتی آگاهی که اگر کسی حرکت کند زیان نخواهد دید؟ و می‌ترسانی از ساعتی که اگر کسی حرکت کند ضرری دامنگیر او خواهد شد؟ کسی که گفتار تو را تصدیق کند، قرآن را تکذیب کرده است، و از یاری طلبیدن خدا در رسیدن به هدفهای دوست داشتنی، و محفوظ ماندن از ناگواریها، بی نیاز شده است. گویا می‌خواهی بجای خداوند، تو را ستایش کنند! چون به گمان خود مردم را به ساعتی آشنا کردی که منافعشان را به دست می‌آورند و از ضرر و زیان درامان می‌مانند. پرهیز دادن مردم از ستاره شناسی ای مردم، از فرا گرفتن علم ستاره شناسی برای پیشگویی‌های دروغین، بپرهیزید، جز آن مقدار از علم نجوم که در دریانوردی و صحرانوردی به آن نیاز دارید، چه اینکه ستاره شناسی شما را به غیبگویی و غیبگویی به جادوگری می‌کشاند، و ستاره شناس چون غیبگو، و غیبگو چون جادوگر و جادوگر چون کافر و کافر در آتش جهنم است. با نام خدا حرکت کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (220, 1, 8, 'درباره زبیر و بیعت او', 'خطبه ۸-درباره زبیر و بیعت او \nپیمان شکنی زبیر \nزبیر، می‌پندارد با دست بیعت کرد نه با دل، پس به بیعت با من اقرار کرده ولی مدعی انکار بیعت با قلب است، بر او لازم است بر این ادعا دلیل روشنی بیاورد، یا به بیعت گذشته باز گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (221, 1, 80, 'نکوهش زنان', 'خطبه ۸۰-نکوهش زنان \nبیان تفاوتهای زنان و مردان \nای مردم! همانا زنان در مقایسه با مردان، در ایمان، و بهره وری از اموال، و عقل متفاوتند، اما تفاوت ایمان بانوان، برکنار بودن از نماز و روزه در ایام عادت حیض آنان است، و اما تفاوت عقلشان با مردان بدان جهت که شهادت دو زن برابر شهادت یک مرد است، و علت تفاوت در بهره وری از اموال، آنکه ارث بانوان نصف ارث مردان است. مدیریت خانوادگی پس، از زنان بد، بپرهیزید و مراقب نیکانشان باشید، در خواسته‌های نیکو، همواره فرمانبردارشان نباشید تا در انجام منکرات طمع ورزند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (222, 1, 81, 'وارستگی و پارسائی', 'خطبه ۸۱-وارستگی و پارسائی \nتعریف زهد و پارسایی \nای مردم، زهد یعنی کوتاه کردن آرزو، و شکرگزاری برابر نعمتها، و پرهیز در برابر محرمات، پس اگر نتوانستید همه این صفات را فراهم سازید، تلاش کنید که حرام بر صبر شما غلبه نکند، و در برابر نعمتها، شکر یادتان نرود، چه اینکه خداوند با دلایل روشن و آشکار، عذرها را قطع، و با کتابهای آسمانی روشنگر، بهانه‌ها را از بین برده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (223, 1, 82, 'در نکوهش دنیا', 'خطبه ۸۲-در نکوهش دنیا \nدنیاشناسی \nچگونه خانه دنیا را توصیف کنم که ابتدای آن سختی و مشقت، و پایان آن نابودی است، در حلال دنیا حساب، و در حرام آن عذاب است، کسی که ثروتمند گردد فریب می‌خورد، و آن کس که نیازمند باشد اندوهناک است، و تلاش کننده دنیا به آن نرسد، و به رهاکننده آن، روی آورد، کسی که با چشم بصیرت به آن بنگرد او را آگاهی بخشد و آن کس که چشم به دنبال دنیا دوزد کوردلش می‌کند. (از شگفتیهای بی نظیر کلام امام (ع) این است که فرمود: (و اگر به دنیا بنگرد آگاهی یابد) و (اگر چشم به دنبال دنیا دوزد کوردل شود) درود و سلام خدا بر علی (ع) با این فصاحت و بلاغت اعجاز گونه!!).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (224, 1, 83, 'خطبه غراء', 'خطبه ۸۳-خطبه غراء \n(از خطبه‌های شگفت آور امام علیه السلام که به آن خطبه «غرا» گویند.) \nشناخت صفات الهی \nستایش خداوندی را سزاست، که به قدرت، والا و برتر، و با عطا و بخشش نعمت‌ها به پدیده‌ها نزدیک است. اوست بخشنده تمام نعمت‌ها، و دفع کننده تمام بلاها و گرفتاری‌ها. او را می‌ستایم در برابر مهربانی‌ها و نعمت‌های فراگیرش. به او ایمان می‌آورم چون مبدأ هستی و آغاز کننده خلقت آشکار است. از او هدایت می‌طلبم چون راهنمای نزدیک است، و از او یاری می‌طلبم که توانا و پیروز است، و به او توکل می‌کنم چون تنها یاور و کفایت کننده است. و گواهی می‌دهم که محمد صلی الله علیه و آله و سلم بنده و فرستاده اوست. او را فرستاده تا فرمان‌های خدا را اجرا کند و بر مردم حجت را تمام کرده، آن‌ها را در برابر اعمال ناروا بترساند. \n۲- سفارش به پرهیزکاری \nسفارش می‌کنم شما بندگان خدا را به تقوای الهی، که برای بیداری شما مثل‌های پند آموز آورده، و سر آمد زندگانی شما را معین فرمود، و لباس‌های رنگارنگ بر شما پوشانده، و زندگی پر وسعت به شما بخشیده، و با حسابگری دقیق خود، بر شما مسلط است. در برابر کارهای نیکو، به شما پاداش می‌دهد، و با نعمت‌های گسترده و بخشش‌های بی‌حساب، شما را گرامی داشته است، و با اعزام پیامبران و دستورات روشن، از مخالفت با فرمانش شما را بر حذر داشته است. تعداد شما را می‌داند، و چند روزی جهت آزمایش و عبرت برای شما مقرر داشته،\n که در این دنیا آزمایش می‌گردید، و برابر اعمال خود محاسبه می‌شوید. \n۳- دنیا شناسی \nآب دنیای حرام همواره تیره، و گل آلود است. منظره‌ای دل فریب و سر انجامی خطرناک دارد. فریبنده و زیباست اما دوامی ندارد. نوری است در حال غروب کردن، سایه‌ای است نابود شدنی، ستونی است در حال خراب شدن، آن هنگام که نفرت دارندگان به آن دل بستند و بیگانگان به آن اطمینان کردند، چونان اسب چموش پاها را بلند کرده، سوار را بر زمین می‌کوبد، و با دام‌های خود آن‌ها را گرفتار می‌کند، و تیرهای خود را به سوی آنان، پرتاب می‌نماید، طناب مرگ به گردن انسان می‌افکند، به سوی گور تنگ و جایگاه وحشتناک می‌کشاند تا در قبر، محل زندگی خویش، بهشت یا دوزخ را بنگرد، و پاداش اعمال خود را مشاهده کند. و همچنان آیندگان به دنبال رفتگان خود گام می‌نهند، نه مرگ از نابودی انسان دست می‌کشد و نه مردم از گناه فاصله می‌گیرند که تا پایان زندگی و سر منزل فنا و نیستی آزادانه به پیش می‌تازند. \n۴- وصف رستاخیز \nتا آنجا که امور زندگانی پیاپی بگذرد، و روزگاران سپری شود، و رستاخیز بر پا گردد، در آن زمان، انسانها را از شکاف گورها، و لانه‌های پرندگان، و خانه درندگان، و می‌دان‌های جنگ، بیرون می‌آورد که با شتاب به سوی فرمان پروردگار می‌روند، و به صورت دسته‌هایی خاموش، وصف‌های آرام و ایستاده حاضر می‌شوند، چشم بیننده خدا آن‌ها را می‌نگرد، و صدای فرشتگان به گوش آن‌ها می‌رسد. لباس نیاز و فروتنی پوشیده درهای حیله و فریب بسته شده و آرزوها قطع گردیده است. دل‌ها آرام، صداها آهسته، عرق از گونه‌ها چنان جاری است که امکان حرف زدن نمی‌باشد، اضطراب و وحشت همه را فرا گرفته، بانگی رعد آسا و گوش خراش، همه را لرزانده، به سوی پیشگاه عدالت، برای دریافت کیفر و پاداش می‌کشاند. \n۵- وصف احوال بندگان خدا \nبندگانی که با دست قدرتمند خدا آفریده شدند، و بی اراده خویش پدید آمده، پرورش یافتند، سپس در گهواره گور آرمیده متلاشی می‌گردند.\n و روزی به تنهایی سر از قبر بر می‌آورند، و برای گرفتن پاداش به دقت حساب رسی می‌گردند، در این چند روزه دنیا مهلت داده شدند تا در راه صحیح قدم بر دارند، راه نجات نشان داده شده تا رضایت خدا را بجویند، تاریکی‌های شک و تردید از آن‌ها برداشته شد، و آن‌ها را آزاد گذاشته‌اند تا برای مسابقه در نیکوکاری‌ها، خود را آماده سازند، تا فکر و اندیشه خود را به کار گیرند و در شناخت نور الهی در زندگانی دنیا تلاش کنند. \n۶- مثل‌های پند آموز (سمبل‌های تقوی) \nوه چه مثال‌های بجا، و پندهای رسایی وجود دارد اگر در دل‌های پاک بنشیند، و در گوش‌های شنوا جای گیرد، و با اندیشه‌های مصمم و عقل‌های با تدبیر بر خورد کند. پس، از خدا چونان کسی پروا کنید که سخن حق را شنید و فروتنی کرد، گناه کرد و اعتراف کرد، ترسید و به اعمال نیکو پرداخت، پرهیز کرد و پیش تاخت، یقین پیدا کرد و نیکوکار شد، پند داده شد و آن را به گوش جان خرید، او را ترساندند و نافرمانی نکرد، به او اخطار شد و به خدا روی آورد، پاسخ مثبت داد و نیایش و زاری کرد، بازگشت و توبه کرد، در پی راهنمایان الهی رفت و پیروی کرد، راه نشانش دادند و شناخت، شتابان به سوی حق حرکت کرده و از نافرمانی‌ها گریخت، سود طاعت را ذخیره کرد، و باطن را پاکیزه نگاه داشت، آخرت را آبادان و زاد و توشه برای روز حرکت، هنگام حاجت و جایگاه نیازمندی، آماده ساخت، و آن را برای اقامتگاه خویش، پیشاپیش فرستاد. ای بندگان خدا برای هماهنگی با اهداف آفرینش خود، از خدا پروا کنید، و آن چنان که شما را پرهیز داد از مخالفت و نافرمانی خدا بترسید، تا استحقاق وعده‌های خدا را پیدا کنید، و از بیم روز قیامت بر کنار باشید. \n۷- راه‌های پند پذیری (راه‌های شناخت) \nخدا گوش‌هایی برای پند گرفتن از شنیدنی‌ها، و چشم‌هایی برای کنار زدن تاریکی‌ها، به شما بخشیده است، و هر عضوی از بدن را اجزاء متناسب و هماهنگ عطا فرموده تا در ترکیب ظاهری صورت‌ها و دوران عمر با هم سازگار باشند، با بدن‌هایی که منافع خود را تأمین می‌کنند، و قلب‌هایی که روزی را به سراسر بدن با فشار می‌رسانند، و از نعمت‌های شکوهمند خدا برخوردارند، و در برابر نعمت‌ها شکر گزارند، و از سلامت خدادادی بهره مندند. مدت زندگی هر یک از شماها را مقدر فرمود، و از شما پوشیده داشت، و از آثار گذشتگان عبرت‌های پند آموز برای شما ذخیره کرد، لذت‌هایی که از دنیا چشیدند، و خوشی‌ها و زندگی راحتی که پیش از مرگ داشتند، سر انجام دست مرگ گریبان آن‌ها را گرفت و میان آن‌ها و آرزوهایشان جدایی افکند: آن‌ها که در روز سلامت چیزی برای خود ذخیره نکردند، و در روزگاران خوش زندگی عبرت نگرفتند. آیا خوشی‌های جوانی را جز ناتوانی پیری در انتظار است و آیا سلامت و تندرستی را جز حوادث بلا و بیماری در راه است و آیا آنان که زنده‌اند جز فنا و نیستی را انتظار دارند با اینکه هنگام جدایی و تپش دل‌ها نزدیک است که سوزش درد را چشیده، و شربت غصه را نوشیده، و فریاد یاری خواستن برداشته، و از فرزندان و خویشاوندان خود، در خواست کمک کرده است. آیا خویشاوندان می‌توانند مرگ را از او دفع کنند و آیا گریه و زاری آن‌ها نفعی برای او دارد \n۸- عبرت از مرگ \nاو را در سرزمین مردگان می‌گذارند، و در تنگنای قبر تنها خواهد ماند. حشرات درون زمین، پوستش را می‌شکافند، و خشت و خاک گور بدن او را می‌پوساند، تند بادهای سخت آثار او را نابود می‌کند، و گذشت شب و روز، نشانه‌های او را از میان بر می‌دارد، بدن‌ها پس از آن همه طراوت متلاشی می‌گردند، و استخوان‌ها بعد از آن همه سختی و مقاومت، پوسیده می‌شوند. و ارواح در گرو سنگینی بار گناهانند، و در آنجاست که به اسرار پنهان یقین می‌کنند، اما نه بر اعمال درستشان چیزی اضافه می‌شود و نه از اعمال زشت می‌توانند توبه کنند. آیا شما فرزندان و پدران و خویشاوندان همان مردم نیستید که بر جای پای آن‌ها قدم گذاشته‌اید و از راهی که رفتند می‌روید و روش آن‌ها را دنبال می‌کنید اما افسوس که دلها سخت شده، پند نمی‌پذیرد، و از رشد و کمال باز مانده، و راهی که نباید برود می‌رود، گویا آن‌ها هدف پندها و اندرزها نیستند و نجات و رستگاری را در به دست آوردن دنیا می‌دانند. بدانید که باید از صراط عبور کنید، گذرگاهی که عبور کردن از آن خطرناک است، با لغزش‌های پرت کننده، و پرتگاه‌های وحشت زا، و ترس‌های پیاپی. \n۹- معرفی الگوی پرهیزگاری \nاز خدا چون خردمندان بترسید که دل را به تفکر مشغول داشته، و ترس از خدا بدنش را فرا گرفته، و شب زنده داری خواب از چشم او ربوده، و به امید ثواب، گرمی روز را با تشنگی گذارنده، با پارسایی شهوات را کشته، و نام خدا زبانش را همواره به حرکت در آورده. ترس از خدا را برای ایمن ماندن در قیامت پیش فرستاده، از تمام راه‌های جز راه حق چشم پوشیده، و بهترین راهی که انسان را به حق می‌رساند می‌پیماید. چیزی او را مغرور نساخته، و مشکلات و شبهات او را نابینا نمی‌سازد، مژده بهشت، و زندگی کردن در آسایش و نعمت سرای جاویدان و ایمن‌ترین روزها، او را خشنود ساخته است. با بهترین روش از گذرگاه دنیا عبور کرده، توشه آخرت را پیش فرستاده، و از ترس قیامت در انجام اعمال صالح پیش قدم شده است، ایام زندگی را با شتاب در اطاعت پروردگار گذرانده، و در فراهم آوردن خشنودی خدا با رغبت تلاش کرده، از زشتی‌ها فرار کرده، امروز رعایت زندگی فردا کرده، و هم اکنون آینده خود را دیده است. پس بهشت برای پاداش نیکوکاران سزاوار و جهنم برای کیفر بدکاران مناسب است، و خدا برای انتقام گرفتن از ستمگران کفایت می‌کند، و قرآن برای حجت آوردن و دشمنی کردن، کافی است. \n۱۰- هشدار از دشمنی شیطان \nسفارش می‌کنم شما را به پروا داشتن از خدا، خدایی که با ترساندن‌های مکرر، راه عذر را بر شما بست، و با دلیل و برهان روشن، حجت را تمام کرد، و شما را پرهیز داد از دشمنی شیطانی که پنهان در سینه‌ها راه می‌یابد، و آهسته در گوش‌ها راز می‌گوید، گمراه و پست است، وعده‌های دروغین داده، در آرزوی آن‌ها به انتظار می‌گذارد، زشتی‌های گناهان را زینت می‌دهد، گناهان بزرگ را کوچک می‌شمارد، و آرام آرام دوستان خود را فریب داده، راه رستگاری را بر روی در بند شدگانش می‌بندد، و در روز قیامت آنچه را که زینت داده انکار می‌کند، و آنچه را که آسان کرده، بزرگ می‌شمارد، و از آنچه که پیروان خود را ایمن داشته بود سخت می‌ترساند. \n۱۱- شگفتی‌های آفرینش انسان \nمگر انسان، همان نطفه و خون نیم بند نیست که خدا او را در تاریکی‌های رحم و خلاف‌های تو در تو، پدید آورد تا به صورت چنین در آمد، سپس کودکی شیرخوار شد، بزرگتر و بزرگتر شده تا نوجوانی رسیده گردید، سپس او را دلی فراگیر، و زبانی گویا، و چشمی بینا عطا فرمود تا عبرت‌ها را درک کند، و از بدی‌ها بپرهیزد، و آنگاه که جوانی در حد کمال رسید، و بر پای خویش استوار ماند، گردن کشی آغاز کرد، و روی از خدا گرداند، و در بیراهه گام نهاد، در هوا پرستی غرق شد، و برای به دست آوردن لذت‌های دنیا تلاش فراوان کرد، و سر مست شادمانی دنیا شد، هرگز نمی‌پندارد مصیبتی پیش آید و بر اساس تقوی فروتنی ندارد، ناگهان سرمست و مغرور در این آزمایش چند روزه، مرگ او را می‌رباید، او را که در دل بد بختی‌ها، اندکی زندگی کرده، و آنچه را که از دست داده عوضی به دست نیاورده است، و آنچه از واجبات را که ترک کرد، قضایش را بجا نیاورد، که درد مرگ او را فراگرفت، روزها در حیرت و سرگردانی، و شب‌ها با بیداری و نگرانی می‌گذراند.\n۱۲- عبرت از مرگ \nهر روز به سختی درد می‌کشد، و هر شب رنج و بیماری به سراغش می‌رود، در میان برادری غم خوار، و پدری مهربان و ناله کننده‌ای بی طاقت و بر سینه کوبنده‌ای گریان افتاده است. اما او در حالت بیهوشی و سکرات مرگ، و غم و اندوه بسیار، و ناله دردناک، و درد جان کندن، با انتظاری رنج آور، دست به گریبان است. پس از مرگ او را مأیوس وار در کفن پیچانده، در حالی که تسلیم و آرام است، بر می‌دارند، و بر تابوت می‌گذارند. خسته و لاغر به سفر آخرت می‌رود، که فرزندان و برادران او را به دوش کشیده تا سر منزل غربت، آنجا که دیگر او را نمی‌بینند، و آنجا که جایگاه وحشت است، پیش می‌برند. اما هنگامی که تشییع کنندگان بروند و مصیبت زندگان باز گردند، در گودال قبر نشانده، برای پرستش حیرت آور، و امتحان لغزش‌زا، زمزمه غم آلود دارد. و بزرگ‌ترین بلای آنجا، فرود آمدن در آتش سوزان دوزخ و بر افروختگی شعله‌ها و نعره‌های آتش است، که نه یک لحظه آرام گیرد تا استراحت کند، و نه آرامشی وجود دارد که از درد او بکاهد، و نه قدرتی که مانع کیفر او شود، نه مرگی که او را از این همه ناراحتی بره‌اند، و نه خوابی که اندوهش را بر طرف سازد، در میان انواع مرگ‌ها و ساعت‌ها مجازات گوناگون گرفتار است. به خدا پناه می‌بریم. \n۱۳- پند آموزی از گذشتگان \nای بندگان خدا کجا هستند آنان که سالیان طولانی در نعمت‌های خدا عمر گذراندند تعلیمشان دادند و دریافتند، مهلتشان دادند و بیهوده روزگار گذراندند از آفات و بلاها دورشان داشتند اما فراموش کردند، زمانی طولانی آن‌ها را مهلت دادند، نعمت‌های فراوان بخشیدند، از عذاب دردناک پرهیزشان دادند، و وعده‌هایی بزرگ از بهشت جاویدان به آن‌ها دادند. ای مردم از گناهانی که شما را به هلاکت افکند، از عیب‌هایی که خشم خدا را در پی دارد، بپرهیزید. دارندگان چشمهای بینا، و گوش‌های شنوا، و سلامت و کالای دنیا آیا گریزگاهی هست یا رهایی و جای امنی، پناهگاهی و جای فراری هست آیا باز گشتی برای جبران وجود دارد نه چنین است پس کی باز می‌گردید به کدام سو می‌روید و به چه چیز مغرور می‌شوید همانا بهره هر کدام از شما زمین به اندازه طول و عرض قامت شماست، آنگونه که خاک آلوده بر آن خفته باشد. ای بندگان خدا هم اکنون به اعمال نیکو پردازید، تا ریسمان‌های مرگ بر گلوی شما سخت نشده، و روح شما برای کسب کمالات آزاد است، و بدن‌ها راحت، و در حالتی قرار دارید که می‌توانید مشکلات یکدیگر را حل کنید. هنوز مهلت دارید، و جای تصمیم و توبه و باز گشت از گناه باقی مانده است. عمل کنید پیش از آن که در شدت تنگنای وحشت و ترس و نابودی قرار گیرید، پیش از آن که مرگ در انتظار مانده، فرا رسد، و دست قدرتمند خدای توانا شما را برگیرد. \n(وقتی که امام این خطبه را ایراد فرمود، بدنها به لرزه در آمد، اشکها سرازیر و دل‌ها ترسان شد، که جمعی آن را غراء نامیدند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (225, 1, 84, 'درباره عمرو بن عاص', 'خطبه ۸۴-درباره عمرو بن عاص \nروانشناسی عمروعاص \nشگفتا از عمروعاص پسر نابغه! میان مردم شام گفت که من اهل شوخی و خوشگذرانی بوده، و عمر بیهوده می‌گذرانم!! حرفی از روی باطل گفت و گناه در میان شامیان انتشار داد مردم آگاه باشید! بدترین گفتار دروغ است، عمروعاص سخن می‌گوید، پس دروغ می‌بندد، وعده می‌دهد و خلاف آن مرتکب می‌شود، درخواست می‌کند و اصرار می‌ورزد، اما اگر چیزی از او بخواهند، بخل می‌ورزد، به پیمان خیانت می‌کند، و پیوند خویشاوندی را قطع می‌نماید، پیش از آغاز نبرد در هیاهو و امر و نهی بی مانند است تا آنجا که دستها به سوی قبضه شمشیرها نرود. اما در آغاز نبرد، و برهنه شدن شمشیرها، بزرگترین نیرنگ او این است که عورت خویش را آشکار کرده، فرار نماید. آگاه باشید! به خدا سوگند که یاد مرگ مرا از شوخی و کارهای بیهوده باز می‌دارد، ولی عمروعاص را فراموشی آخرت از سخن حق باز داشته است، با معاویه بیعت نکرد مگر بدان شرط که به او پاداش دهد، و در برابر ترک دین خویش، رشوه ای تسلیم او کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (226, 1, 85, 'در توحید و موعظه', 'خطبه ۸۵-در توحید و موعظه \nخداشناسی\nو گواهی می‌دهم که خدایی نیست جز خدای یکتا، آغاز اوست که پیش از او چیزی نیست، و پایان همه اوست که بی نهایت است، پندارها برای او صفتی نمی توانند فراهم آورند، و عقلها از درک کیفیت او درمانده اند، نه جزئی برای او می‌توان تصور کرد و نه تبعیض پذیر است و نه چشمها و قلبها می‌توانند او را به درستی فرا گیرند. \nضرورت پندپذیری ای بندگان خدا! از عبرتهای سودمند پند پذیرید، و از آیات روشنگر عبرت آموزید. و از آنچه با بیان رسا شما را ترسانده، بپرهیزید، و از یادآوریها و اندرزها سود ببرید، آن چنان که گویا چنگال مرگ در پیکر شما فرورفته، و رشته آرزوها و دلبستگیها قطع گردیده و سختیهای مرگ و آغاز حرکت به سوی قیامت به شما هجوم آورده است. آن روز که (همراه هر کسی گواه و سوق دهنده ای است) سوق دهنده که تا صحنه رستاخیز او را می‌کشاند، و شاهدی که بر اعمال او گواهی می‌دهد. (و قسمتی از این خطبه) \nوصف بهشت در بهشت، درجاتی از یکدیگر برتر، و جایگاه گوناگون و متفاوت وجود دارد که نعمتهایش پایان ندارد، و ساکنان آن هرگز خارج نگردند، ساکنان بهشت جاوید، هرگز پیر و فرسوده نگردند، و گرفتار شدائد و سختیها نخواهند شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (227, 1, 86, 'صفات پروردگار- پرهیزکاری', 'خطبه ۸۶-صفات پرهیزکاری \nعلم الهی خدا به تمام اسرار نهان آگاه و از باطن همه باخبر است، به همه چیز احاطه دارد و بر همه چیز غالب و پیروز، و بر همه چیز تواناست. پندهای ارزشمند هر کس از شما در روزگارانی که مهلت دارد به اعمال نیکو بپردازد پیش از آنکه مرگ فرا رسد، و در ایام فراغت پاک باشد، پیش از آنکه گرفتار شود، و در ایام رهایی نیکوکار باشد، پیش از آنکه مرگ گلوگاه او را بفشارد، پس برای خود و جایی که می‌رود آماده باشد، و در این دنیا که محل کوچ کردن است برای منزلگاه ابدی، توشه ای بردارد. \nپس ای مردم! خدا را! خدا را! پروا کنید، برای حفظ قرآن، که از شما خواسته، و حقوقی که نزد شما سپرده است، پس همانا خدای سبحان! شما را بیهوده نیافرید، و به حال خود وانگذاشت، و در گمراهی و کوری رها نساخته است، کردارتان را بیان فرمود، و از اعمال شما باخبر است و سرآمد زندگی شما را مشخص کرد و (کتابی بر شما نازل کرد که روشنگر همه چیز است) پیامبرش را مدتی در میان شما قرار داد تا برای او و شما، دین را به اکمال رساند، و آنچه در قرآن نازل شد و مایه رضای الهی است تحقق بخشد. و با زبان پیامبرش، کارهای خوشایند و ناخوشایند، بایدها و نبایدها را ابلاغ کرد، و اوامر و نواهی را آموزش داد، و راه عذر را بر شما بست و حجت را تمام کرد. پیش از کیفر، شما را تهدید نمود، از عذابهای سختی که در پیشروی دارید شما را ترساند، پس بازمانده ایام خویش را دریابید، و صبر و بردباری در برابر نارواییها پیشه کنید، چرا که عمر باقیمانده، برابر روزهای زیادی که به غفلت گذراندید و روی گردان از پندها بودید، بسیار کم است. به خود بیش از اندازه آزادی ندهید، که شما را به ستمگری می‌کشاند، و با نفس سازشکاری و سستی روا مدارید که ناگهان در درون گناه سقوط می‌کنید.\nیادآوری ارزش‌های اخلاقی \nای بندگان خدا! آن کس که نسبت به خود خیرخواهی او بیشتر است، در برابر خدا، از همه کس فرمانبردارتر است، و آن کس که خویشتن را بیشتر می‌فریبد، نزد خدا گناهکارترین انسانها است، زیانکار واقعی کسی که خود را بفریبد. و آن کس مورد غبطه است و بر او رشک می‌برند که دین او سالم باشد. سعادتمند آن کس که از زندگی دیگران عبرت آموزد، و شقاوتمند آنکه فریب هوا و هوسها را بخورد. آگاه باشید! ریاکاری و تظاهر، هر چند اندک باشد شرک است، و همنشینی با هواپرستان ایمان را به دست فراموشی می‌سپارد، و شیطان را حاضر می‌کند، از دروغ برکنار باشید که با ایمان فاصله دارد، راستگو در راه نجات و بزرگواری است، اما دروغگو بر لب پرتگاه هلاکت و خواری است، حسد نورزید که حسد ایمان را چونان آتشی که هیزم را خاکستر کند، نابود می‌سازد، با یکدیگر دشمنی و کینه توزی نداشته باشید که نابودکننده هر چیزی است، بدانید که آرزوهای دور و دراز عقل را غافل و یاد خدا را به فراموشی می‌سپارد، آرزوهای ناروا را دروغ انگارید که آرزوها فریبنده و صاحبش فریب خورده می‌باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (228, 1, 87, 'موعظه یاران، وصف بنده محبوب خدا', 'خطبه ۸۷-موعظه یاران \n(برخی از شارحان گفتند که این خطبه در شهر کوفه ایراد شد) \nمعرفی بهترین بنده خدا (الگوی انسان کامل) \nای بندگان خدا! همانا بهترین و محبوبترین بنده نزد خدا، بنده ای که خدا او را در پیکار با نفس یاری داده است، آن کس که جامه زیرین او اندوه، و لباس رویین او ترس از خداست، چراغ هدایت در قلبش روشن شده و وسائل لازم برای روزی او فراهم آمد، دوریها و دشواریها را بر خود نزدیک و آسان ساخته است. حقایق دنیا را با چشم دل نگریسته، همواره به یاد خدا بوده و اعمال نیکو، فراوان انجام داده است، از چشمه گوارای حق سیراب گشته، سرچشمه ای که به آسانی به آن رسید و از آن نوشیده سیراب گردید. از راه هموار و راست قدم برداشته، پیراهن شهوات را از تن بیرون کرد، و جز یک غم، از تمام غمها خود را می‌ره‌اند، و از صف کوردلان و مشارکت با هواپرستان خارج شده، کلید بازکننده درهای هدایت شد و قفل درهای گمراهی و خواری گردید، راه هدایت را با روشندلی دید، و از همان راه رفت، و نشانه‌های آن را شناخت و از امواج سرکش شهوات گذشت. به استوارترین دستاویزها و محکمترین طنابها چنگ انداخت، چنان به یقین و حقیقت رسید که گویی نور خورشید بر او تابید، در برابر خداوند خود را بگونه ای تسلیم کرد که هر فرمان او را انجام می‌دهد و هر فرعی را به اصلش باز می‌گرداند، چراغ \nتاریکیها، و روشنی بخش تیرگیها، کلید درهای بسته و برطرف کننده دشواریها، و راهنمای گمراهان در بیابانهای سرگردانی است. سخن می‌گوید، خوب می‌فهماند، سکوت کرده به سلامت می‌گذرد، برای خدا اعمال خویش را خالص کرده آنچنان که خدا پذیرفته است، از گنجینه‌های آیین خدا و ارکان زمین است. خود را به عدالت واداشته و آغاز عدالت او آنکه هوای نفس را از دل بیرون رانده است، حق را می‌شناساند و به آن عمل می‌کند، کار خیری نیست مگر که به آن قیام می‌کند، و در هیچ جا گمان خیری نبرده جز آنکه به سوی آن شتافت. اختیار خود را به قرآن سپرده، و قرآن را راهبر و پیشوای خود قرار داده است، هر جا که قرآن بار اندازد فرود آید، و هر جا که قرآن جای گیرد مسکن گزیند. \nوصف زشت ترین انسان (عالم نمایان) \nو دیگری که او را دانشمند نامند اما از دانش بی بهره است، یک دسته از نادانیها را از جمعی نادان فرا گرفته، و مطالب گمراه کننده از گمراهان آموخته، و به هم بافته، و دامهایی از طنابهای غرور و گفته‌های دروغین بر سر راه مردم افکنده، قرآن را بر امیال و خواسته‌های خود تطبیق می‌دهد، و حق را به هوسهای خود تفسیر می‌کند، مردم را از گناهان بزرگ ایمن می‌سازد، و جرائم بزرگ را سبک جلوه می‌دهد، ادعا می‌کند از ارتکاب شبهات پرهیز دارد اما در آن‌ها غوطه می‌خورد. می‌گوید: از بدعتها دورم، ولی در آن‌ها غرق شده است، چهره ظاهر او چهره انسان، و قلبش قلب حیوان درنده است، راه هدایت را نمی شناسد که از آن سو برود، و راه خطا و باطل را نمی داند که از آن بپرهیرد، پس مرده ای است در میان زندگان. \nشناساندن عترت پیامبر (ص) و امامان راستین (ع) \nمردم! کجا می‌روید؟ چرا از حق منحرف می‌شوید؟ پرچمهای حق برپاست و نشانه‌های آن آشکار است، با اینکه چراغهای هدایت روشنگر راهند، چون گمراهان به کجا می‌روید؟ چرا سرگردانید؟ در حالی که عترت پیامبر شما در میان شماست، آن‌ها زمامداران حق و یقینند، پیشوایان دین، و زبانهای راس \nتی و راستگویانند، پس باید در بهترین منازل قرآن جایشان دهید و همانند تشنگان که به سوی آب شتابانند، به سویشان هجوم آورید. ای مردم این حقیقت را از خاتم پیامبران بیاموزید که فرمود: هر که از ما می‌میرد، در حقیقت نمرده است و چیزی از ما کهنه نمی شود. پس آنچه نمی دانید، نگویید، زیرا بسیاری از حقایق در اموری است که ناآگاهانه انکار می‌کنید. \nویژگیهای امام علی (ع) \nمردم! عذرخواهی کنید از کسی که دلیلی بر ضد او ندارید، و آنکس من می‌باشم، مگر من در میان شما بر اساس (ثقل اکبر) که قرآن است عمل نکردم؟ و (ثقل اصغر) عترت پیامبر (ص) را در میان شما باقی نگذارم؟ مگر من پرچم ایمان را در بین شما استوار نساختم؟ و از حدود و مرز حلال و حرام آگاهیتان ندادم؟ مگر پیراهن عافیت را با عدل خود به اندام شما نپوشاندم؟ و نیکیها را با اعمال و گفتار خود در میان شما رواج ندادم؟ و ملکات اخلاق انسانی را به شما نشان ندادم؟ پس وهم و گمان خود را در آنجا که چشم دل ژرفای آن را مشاهده نمی کند، و فکرتان توانایی تاختن در آن راه را ندارد، بکار نگیرید. برخی از همین خطبه است: \nاخبار غیبی نسبت به آینده بنی امیه \nتا آنکه برخی از شما گمان می‌برند که دنیا به کام بنی امیه شد، و همه خوبیها را افزون به آن‌ها سپرده و آن‌ها را از سرچشمه خود سیراب کرده، و تازیانه و شمشیرشان از سر این امت کنار نخواهد رفت، کسانی که چنین می‌اندیشند در اشتباهند، زیرا سهم بنی امیه تنها جرعه ای از زندگی لذت بخش است که مدتی آن را می‌مکند سپس همه آنچه را که نوشیدند بیرون می‌ریزند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (229, 1, 88, 'در بیان هلاکت مردم', 'خطبه ۸۸-در بیان هلاکت مردم \n(به نقل برخی از شارحان، این خطبه در سال ۳۶ هجری پس از قتل عثمان در مدینه ایراد شد)\nعوامل هلاکت انسانها \n(پس از ستایش پروردگار!) \nخدا هرگز جباران دنیا را در هم نشکسته مگر پس از آنکه مهلتهای لازم و نعمتهای فراوان بخشید، و هرگز استخوان شکسته ملتی را بازسازی نفرمود مگر پس از آزمایشها و تحمل مشکلات، مردم! در سختیهایی که با آن روبرو هستید و مشکلاتی که پشت سر گذاردید، درسهای عبرت فراوان وجود دارد، نه هر که صاحب قلبی است خردمند است، و نه هر دارنده گوشی شنواست، و نه هر دارنده چشمی بیناست. در شگفتم، چرا در شگفت نباشم؟! از خطای گروه‌های پراکنده با دلایل مختلف که هر یک در مذهب خود دارند! نه گام بر جای گام پیامبر (ص) می‌نهند، و نه از رفتار جانشین پیغمبر پیروی می‌کنند، نه به غیب ایمان می‌آورند و نه خود را از عیب برکنار می‌دارند، به شبهات عمل می‌کنند و در گرداب شهوات غوطه ورند، نیکی در نظرشان همان است که می‌پندارد، و زشتیها همان است که آن‌ها منکرند. در حل مشکلات به خود پناه می‌برند، و در مبهمات تنها به رای خویش تکیه می‌کنند، گویا هر کدام، امام و راهبر خویش می‌باشند که به دستگیره‌های مطمئن و اسباب محکمی که خود باور دارند چنگ می‌زنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (230, 1, 89, 'در حال مردم پیش از بعثت و پس از آن', 'خطبه ۸۹-مردم پیش از بعثت \n(این خطبه طبق نقل برخی از شارحان در شهر کوفه ایراد شد) \nوصف روزگاران بعثت پیامبر (ص) \nخدا پیامبر اسلام را هنگامی مبعوث فرمود که از زمان بعثت پیامبران پیشین مدتها گذشته، و ملتها در خواب عمیقی فرو خفته بودند، فتنه و فساد جهان را فرا گرفته و اعمال زشت رواج یافته بود، آتش جنگ همه جا زبانه می‌کشید و دنیا بی نور، پر از مکر و فریب گشته بود، برگهای درخت زندگی به زردی گراییده و از میوه آن خبری نبود، آب حیات فرو خشکید و نشانه‌های هدایت کهنه و ویران شده بود، پرچمهای هلاکت و گمراهی آشکار و دنیا با قیافه زشتی به مردم می‌نگریست، و با چهره ای عبوس و غم آلود با اهل دنیا روبرو می‌گشت. میوه درخت دنیا در جاهلیت فتنه، و خوارکش مردار، و در درونش وحشت و اضطراب، و بر بیرون شمشیرهای ستم حکومت داشت.\nعبرت آموزی از روزگار جاهلیت \nای بندگان خدا! عبرت گیرید، و همواره به یاد زندگانی پدران و براداران خود در جاهلیت باشید، که از این جهان رفتند و در گرو اعمال خود بوده و برابر آن محاسبه می‌گردند، به جان خودم سوگند! پیمان برای زندگی و مرگ و نجات از مجازات الهی بین شما و آن‌ها بسته نشده است، و هنوز روزگار زیادی نگذشته، و از آن روزگارانی که در پشت پدران خود بودید زیاد دور نیست. به خدا سوگند \n! پیامبر اسلام (ص) چیزی به آن‌ها گوشزد نکرد جز آن که من همان را با شما می‌گویم، شنوایی امروز شما از شنوایی آن‌ها کمتر نیست، همان چشمها و قلبهایی که به پدرانتان دادند به شما نیز بخشیدند. به خدا سوگند، شما پس از آن‌ها مطلبی را ندیده اید که آن‌ها نمی شناختند، و شما به چیزی اختصاص داده نشدید که آن‌ها محروم باشند. راستی حوادثی به شما روی آورده مانند شتری که مهار کردنش مشکل است، و میانبندش سست و سواری بر آن دشوار است. مبادا آنچه مردم دنیا را فریفت شما را بفریبد! که دنیا سایه ای است گسترده و کوتاه، تا سرانجامی روشن و معین.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (231, 1, 9, 'درباره پیمان شکنان', 'خطبه ۹-درباره پیمان شکنان \nشناخت طلحه و زبیر (و اصحاب جمل) \nچون رعد خروشیدند، و چونان برق درخشیدند، اما کاری از پیش نبردند و سر انجام سست گردیدند، ولی ما اینگونه نیستیم، تا عمل نکنیم رعد و برقی نداریم، و تا نباریم سیل جاری نمی سازیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (232, 1, 90, 'در بیان صفات خداوندی', 'خطبه ۹۰-در بیان صفات خداوندی \nخداشناسی \nسپاس خداوندی را، بی آنکه دیده شود شناخته شده، و بی آنکه اندیشه ای به کار گیرد آفریننده است، خدایی که همیشه بوده و تا ابد خواهد بود، آنجا که نه از آسمان دارای برجهای زیبا خبری بود، و نه از پرده‌های فرو افتاده اثری به چشم می‌خورد، نه شبی تاریک و نه دریایی آرام، نه کوهی با راه‌های گشوده، نه دره ای پر پیچ و خم، نه زمین گسترده، و نه آفریده‌های پراکنده وجود داشت. خدا پدیدآورنده پدیده‌ها و وارث همگان است، خدای آنان و روزی دهنده ایشان است، آفتاب و ماه به رضایت او می‌گردند که هر تازه ای را کهنه، و هر دوری را نزدیک می‌گردانند. خدا، روزی مخلوقات را تقسیم کرد، و کردار و رفتارشان را برشمرد، از نفسها که می‌زنند، و نگاه‌های دزدیده که دارند، و رازهایی که در سینه‌ها پنهان کردند. و جایگاه پدیده‌ها را در شکم مادران پشت پدران تا روز تولد و سرآمد زندگی و مرگ، همه را می‌داند. اوست خدایی که با همه وسعتی که رحمتش دارد بر دشمنان سخت است و با سختگیری که دارد رحمتش همه دوستان را فرا گرفته است، هر کس که با او به مبارزه برخیزد بر او غلبه می‌کند، و هر کس دشمنی ورزد هلاکش می‌سازد، هر کس با او کینه و دشمنی ورزد تیره روز \nش کند، و بر دشمنانش پیروز است، هر کس به او توکل نماید او را کفایت کند، و هر کس از او بخواهد، می‌پردازد، و هر کس برای خدا به محتاجان قرض دهد وامش را بپردازد، و هر که او را سپاس گوید، پاداش نیکو دهد.\nاندرزهای حکیمانه \nبندگان خدا، خود را بسنجید قبل از آنکه مورد سنجش قرار گیرید، پیش از آنکه حسابتان را برسند حساب خود را برسید، و پیش از آنکه راه گلو گرفته شود نفس راحت بکشید، و پیش از آنکه با زور شما را به اطاعت وادارند، فرمانبردار باشید، بدانید همانا آن کس که خود را یاری نکند و پنددهنده و هشداردهنده خویش نباشد، دیگری هشداردهنده و پنددهنده او نخواهد بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (233, 1, 91, 'خطبه اشباح', 'خطبه ۹۱-خطبه اشباح \n(مسعدة بن صدقه از امام صادق علیه السلام نقل کرد، روزی در مسجد کوفه شخصی به امام علی علیه السلام گفت خدا را آنگونه توصیف کن که گویا با چشم سر او را دیده‌ایم، امام به خطابه برخاست، مسجد پر از مردم شد، در حالی که خشمناک بود و رنگ صورت امام تغییر کرده بود فرمود) \n۱- خدا شناسی \nستایش خدایی را سزاست که نبخشیدن بر مال او نیفزاید، و بخشش او را فقیر نسازد، زیرا هر بخشنده‌ای جز او، اموالش کاهش یابد، و جز او هر کس از بخشش دست کشد مورد نکوهش قرار گیرد. اوست بخشنده انواع نعمت‌ها و بهره‌های فزاینده و تقسیم کننده روزی پدیده‌ها، مخلوقات همه جیره خوار سفره اویند، که روزی همه را تضمین، و اندازه‌اش را تعیین فرمود. به مشتاقان خویش و خواستاران آنچه در نزد اوست راه روشن را نشان داد، سخاوت او را در آنجا که از او بخواهند، با آنجا که از او درخواست نکنند، بیشتر نیست. خدا اولی است که آغاز ندارد، تا پیش از او چیزی بوده باشد، و آخری است که پایان ندارد تا چیزی پس از او وجود داشته باشد. مردمک چشم‌ها را از مشاهده خود باز داشته است. زمان بر او نمی‌گذرد تا دچار دگرگونی گردد، و در مکانی قرار ندارد تا پندار جابجایی نسبت به او روا باشد. اگر آنچه از درون معادن کوه‌ها بیرون می‌آید، و یا آنچه از لبان پر از خنده صدف‌های دریا خارج می‌شود، از نقره‌های خالص، و طلاهای ناب، درهای غلطان، و مرجان‌های دست چین، همه را ببخشد، در سخاوت او کمتر اثری نخواهد گذاشت، و گستردگی نعمت‌هایش را پایان نخواهد داد، در پیش او آنقدر از نعمت‌ها وجود دارد که هر چه انسان‌ها درخواست کنند تمامی نپذیرد، چون او بخشنده‌ای است که درخواست نیازمندان چشمه جود او را نمی‌خشکاند، و اصرار و درخواست‌های پیاپی او را به بخل ورزیدن نمی‌کشاند. \n۲- صفات خدا در قرآن \nای پرسش کننده، درست بنگر، آنچه را که قرآن از صفات خدا بیان می‌دارد، به آن اعتماد کن، و از نور هدایتش بهره گیر، و آنچه را که شیطان تو را به دانستن آن وامی‌دارد، و کتاب خدا آن را بر تو واجب نکرده، و در سنت پیامبر صلی الله علیه و آله و سلم و امامان هدایتگر علیه السلام نیامده، رها کن و علم آن را به خدا واگذار، که این نهایت حق پروردگار بر تو است. بدان، آن‌ها که در علم دین استوارند، خدا آن‌ها را از فرو رفتن در آنچه که بر آن‌ها پوشیده است و تفسیر آن را نمی‌دانند، و از فرو رفتن در اسرار نهان بی نیاز ساخته است،\n و آنان را از این رو که به عجز و ناتوانی خود در برابر غیب و آنچه که تفسیر آن را نمی‌دانند اعتراف می‌کنند، ستایش فرمود، و ترک ژرف نگری آنان در آنچه که خدا بر آنان واجب نساخته را راسخ بودن در علم شناسانده است. پس به همین مقدار بسنده کن و خدا را با میزان عقل خود ارزیابی مکن، تا از تباه شدگان نباشی. اوست خدای توانایی که اگر وهم و خیال انسانها بخواهد برای درک اندازه قدرتش تلاش کند، و افکار بلند و دور از وسوسه‌های دانشمندان، بخواهد ژرفای غیب ملکوتش را در نوردد، و قلب‌های سراسر عشق مشتاقان، برای درک کیفیت صفات او کوشش نماید، و عقل‌ها با تلاش وصف ناپذیر از راه‌های بسیار ظریف و باریک بخواهند ذات او را درک کنند، دست قدرت بر سینه همه نواخته بازگرداند، در حالی که در تاریکی‌های غیب برای رهایی خود به خدای سبحان پناه می‌برند و با نا امیدی و اعتراف به عجز از معرفت ذات خدا، باز می‌گردند، که با فکر و عقل نارسای بشری نمی‌توان او را درک کرد، و اندازه جلال و عزت او در قلب اندیشمندان راه نمی‌یابد. خدایی که پدیده‌ها را از هیچ آفرید، نمونه‌ای در آفرینش نداشت تا از آن استفاده کند، و یا نقشه‌ای از آفریننده‌ای پیش از خود، که از آن در آفریدن موجودات بهره گیرد، و نمونه‌های فراوان از ملکوت قدرت خویش، و شگفتی‌های آثار رحمت خود، که همه با زبان گویا به وجود پروردگار گواهی می‌دهند را به ما نشان داده که بی اختیار ما را به شناخت پروردگار می‌خوانند. در آنچه آفریده آثار صنعت و نشانه‌های حکمت او پدیدار است، که هر یک از پدیده‌ها حجت و برهانی بر وجود او می‌باشند، گرچه برخی مخلوقات، به ظاهر ساکت‌اند، ولی بر تدبیر خداوندی گویا، و نشانه‌های روشنی بر قدرت و حکمت اویند خداوندا گواهی می‌دهم، آن کس که تو را به اعضای گوناگون پدیده‌ها و مفاصل به هم پیوسته‌ که به فرمان حکیمانه تو در لابلای عضلات پدید آمده، تشبیه می‌کند، هرگز در ژرفای ضمیر خود تو را نشناخته، و قلب او با یقین انس نگرفته است، و نمی‌داند که هرگز برای تو همانندی نیست. و گویا بیزاری پیروان گمراه از رهبران فاسد خود را نشنیده‌اند که می‌گویند: «به خدا سوگند ما در گمراهی آشکار بودیم. که شما را با خدای جهانیان مساوی پنداشتیم» دروغ گفتند مشرکان که تو را با بت‌های خود همانند پنداشتند، و با وهم و خیال خود گفتند پیکری چون بتهای ما دارد، و با پندار نادرست تو را تجزیه کرده، و با اعضای گوناگون مخلوقات تشبیه کردند. خدایا گواهی می‌دهم آنان که تو را با چیزی از آفریده‌های تو مساوی شمارند از تو روی بر تافته‌اند و آن که از تو روی گردان شود بر اساس آیات محکم قرآن، و گواهی براهین روشن تو، کافر است. تو همان خدای نامحدودی هستی که در اندیشه‌ها نگنجی تا چگونگی ذات تو را درک کنند، و در خیال و وهم نیایی تا تو را محدود و دارای حالات گوناگون پندارند. \n۳- وصف پروردگار در آفرینش موجودات گوناگون \nآنچه را آفرید با اندازه گیری دقیقی استوار کرد، و با لطف و مهربانی نظمشان داد، و به خوبی تدبیر کرد. هر پدیده را برای همان جهت که آفریده شد به حرکت در آورد، چنانکه نه از حد و مرز خویش تجاوز نماید و نه در رسیدن به مراحل رشد خود کوتاهی کند، و این حرکت حساب شده را بدون دشواری به سامان رساند تا بر اساس اراده او زندگی کند. پس چگونه ممکن است سرپیچی کند در حالی که همه موجودات از اراده خدا سرچشمه می‌گیرند، خدایی که پدید آورنده موجودات گوناگون است، بدون احتیاج به اندیشه و فکری که به آن روی آورد، یا غریزه‌ای که در درون پنهان داشته باشد، و بدون تجربه از حوادث گذشته، و بدون شریکی که در ایجاد امور شگفت انگیز یاریش کند، موجودات را آفرید، پس آفرینش کامل گشت و به عبادت و اطاعت او پرداختند، دعوت او را پذیرفتند و در برابر فرمان الهی سستی و درنگ نکردند و در اجرای فرمان الهی توقف نپذیرفتند. پس کجی‌های هر چیزی را راست، و مرزهای هر یک را روشن ساخت، و با قدرت خداوندی بین اشیاء متضاد هماهنگی ایجاد کرد، و وسایل ارتباط آنان را فراهم ساخت، و موجودات را از نظر حدود، اندازه، و غرائز، و شکل‌ها، و قالب‌ها، و هیأتهای گوناگون، تقسیم و استوار فرمود، و با حکمت و تدبیر خویش هر یکی را به سرشتی که خود خواست در آورد. \n۴- چگونگی آفرینش آسمان‌ها \nفضای باز و پستی و بلندی و فاصله‌های وسیع آسمان‌ها را بدون اینکه بر چیزی تکیه کند، نظام بخشید و شکاف‌های آن را به هم آورد، و هر یک را با آنچه که تناسب داشت و جفت بود پیوند داد، و دشواری فرود آمدن و برخاستن را بر فرشتگانی که فرمان او را به خلق رسانند یا اعمال بندگان را بالا برند آسان کرد. در حالی که آسمان را به صورت دود و بخار بود به آن فرمان داد، پس رابطه‌های آن را بر قرار ساخت، سپس آن‌ها را از هم جدا کرد و بین آن‌ها فاصله انداخت، و بر هر راهی و شکافی از آسمان، نگهبانی از شهاب‌های روشن گماشت، و با دست قدرت آن‌ها را از حرکت ناموزون در فضا نگهداشت، و دستور فرمود تا برابر فرمانش تسلیم باشند. و آفتاب را نشانه روشنی بخش روز، و ماه را، با نوری کم رنگ برای تاریکی شب‌ها قرار داد، و آن دو را در مسیر حرکت خویش به حرکت در آورد، و حرکت آن دو را دقیق اندازه گیری کرد تا در درجات تعیین شده حرکت کنند که بین شب و روز تفاوت باشد، و قابل تشخیص شود، و با رفت و آمد آن‌ها شماره سالها، و اندازه گیری زمان ممکن باشد. پس در فضای هر آسمان فلک آن را آفرید، و زینتی از گوهرهای تابنده و ستارگان درخشنده بیاراست، و آنان را که خواستند اسرار آسمان‌ها را دزدانه دریابند، با شهاب‌های درخشنده سوزان تیر باران کرد، و تمامی ستارگان از ثابت و استوار، و گردنده و بی قرار، فرود آینده و بالا رونده، و نگران کننده و شادی آفرین را، تسلیم اوامر خود فرمود. \n۵- ویژگی‌های فرشتگان \nسپس، خداوند سبحان برای سکونت بخشیدن در آسمان‌ها، و آباد ساختن بالاترین قسمت از ملکوت خویش، فرشتگانی شگفت آفرید، و تمام شکاف‌ها و راه‌های گشاده آسمان‌ها را با فرشتگان پر کرد، و فاصله جو آسمان را از آن‌ها گستراند، که هم اکنون صدای تسبیح آن‌ها فضای آسمان‌ها را پر کرده: در بارگاه قدس، درون پرده‌های حجاب و صحنه‌های مجد و عظمت پروردگار، طنین انداز است. در ما ورای آن‌ها زلزله‌هایی است که گوش‌ها را کر می‌کند و شعاع‌های خیره کننده نور، که چشم‌ها را از دیدن باز می‌دارد، و ناچار خیره بر جای خویش می‌ماند. خدا فرشتگان را در صورت‌های مختلف و اندازه‌های گوناگون آفرید، و بال و پرهایی برای آن‌ها قرار داد، آن‌ها که همواره در تسبیح جلال و عزت پروردگار به سر می‌برند، و چیزی از شگفتی‌های آفرینش پدیده‌ها را به خود نسبت نمی‌دهند و در آنچه از آفرینش پدیده‌ها که خاص خداست، ادعایی ندارند:\n«بلکه بندگانی بزرگوارند، که در سخن گفتن از او پیشی نمی‌گیرند و به فرمان الهی عمل می‌کنند» خدا فرشتگان را امین وحی خود قرار داد، و برای رساندن پیمان امر و نهی خود به پیامبران، از آن‌ها استفاده کرد، و روانه زمین کرده، آن‌ها را از تردید شبهات مصونیت بخشید، که هیچ کدام از فرشتگان از راه رضای حق منحرف نمی‌گردند. آن‌ها را از یاری خویش بهرمند ساخت، و دل‌هایشان را در پوششی از تواضع و فروتنی و خشوع و آرامش در آورد، درهای آسمان را بر رویشان گشود تا خدا را به بزرگی بستایند، و برای آن‌ها نشانه‌های روشن قرار داد تا به توحید او بال گشایند. سنگینی‌های گناهان هرگز آن‌ها را در انجام وظیفه دل سرد نساخت، و گذشت شب و روز آن‌ها را به سوی مرگ سوق نداد، تیرهای شک و تردید خلل در ایمانشان ایجاد نکرد، و شک و گمان در پایگاه یقین آن‌ها راه نیافت، و آتش کینه در دل‌هایشان شعله‌ور نگردید، حیرت و سرگردانی آن‌ها را از ایمانی که دارند و آنچه از هیبت و جلال خداوندی که در دل نهادند جدا نساخت و وسوسه‌ها در آن‌ها راه نیافته، تا شک و تردید بر آن‌ها تسلط یابد. \n۶- اقسام فرشتگان\n گروهی از فرشتگان در آفرینش ابرهای پر آب، و در آفرینش کوه‌های عظیم و سر بلند، و خلقت ظلمت و تاریکی‌ها نقش دارند، و گروهی دیگر، قدم‌هایشان تا ژرفای زمین پایین رفته، و چونان پرچمهای سفیدی دل فضا را شکافته‌اند، و در زیر آن بادهایی است که به نرمی حرکت کرده و در مرزهای مشخصی نگاهش می‌دارد. \n۷- صفات والای فرشتگان \nاشتغال به عبادت پروردگار، فرشتگان را از دیگر کارها باز داشته، و حقیقت ایمان میان آن‌ها و معرفت حق، پیوند لازم ایجاد کرد، نعمت یقین آن‌ها را شیدای حق گردانید که به غیر خدا هیچ علاقه‌ای ندارند. شیرینی معرفت خدا را چشیده و از جام محبت پروردگار سیراب شدند، ترس و خوف الهی در ژرفای جان فرشتگان راه یافته، و از فراوانی عبادت قامتشان خمیده و شوق و رغبت فراوان، از زاری و گریه‌شان نکاسته است. مقام والای فرشتگان، از خشوع و فروتنی آنان کم نکرد، و غرور و خود بینی دامنگیرشان نگردید، تا اعمال نیکوی گذشته را شماره کنند، و سهمی از بزرگی و بزرگواری برای خود تصور نمایند. گذشت زمان آنان را از انجام وظائف پیاپی نرنجانده و از شوق و رغبتشان نکاسته تا از پروردگار خویش نا امید گردند. از مناجات‌های طولانی، خسته نشده، و اشتغال به غیر خدا آن‌ها را تحت تسلط خود در نیاورده است، و از فریاد استغاثه و زاری آن‌ها فروکش نکرده و در مقام عبادت و نیایش دوش به دوش هم همواره ایستاده‌اند، راحت طلبی آن‌ها را به کوتاهی در انجام دستوراتش وادار نساخته، و کودنی و غفلت و فراموشی بر تلاش و کوشش و عزم راسخ فرشتگان راه نمی‌یابد، و فریب‌های شهوت، همت‌های بلندشان را تیرباران نمی‌کند. فرشتگان، ایمان به خدای صاحب عرش را ذخیره روز بی نوایی خود قرار داده و آن هنگام که خلق به غیر خدا روی می‌آورد آن‌ها تنها متوجه پروردگار خویشند، هیچ گاه عبادت خدا را پایان نمی‌دهند، و شوق و علاقه خود را از انجام اوامر الهی و اطاعت پروردگار سست نمی‌کنند، آنچه آنان را شیفته اطاعت خدا کرده بذر محبت است که در دل می‌پرورانند، و هیچ گاه دل از بیم و امید او بر نمی‌دارند. عوامل ترس آن‌ها را از مسؤولیت باز نمی‌دارد تا در انجام وظیفه سستی ورزند، طمع‌ها به آنان شبیخون نزده تا تلاش دنیا را بر کار آخرت مقدم دارند، اعمال گذشته خود را بزرگ نمی‌شمارند، و اگر بزرگ بشمارند امیدوارند، و امید فراوان نمی‌گذارد تا از پروردگار ترسی در دل داشته باشند. \n۸- پاک بودن فرشتگان از رذایل اخلاقی \nفرشتگان در باره پروردگار خویش به جهت وسوسه‌های شیطانی اختلاف نکرده‌اند، و برخوردهای بد با هم نداشته و راه جدایی نگیرند. کینه‌ها و حسادت‌ها در دلشان راه نداشته و عوامل شک و تردید و خواهش‌های نفسانی، آن‌ها را از هم جدا نساخته، و افکار گوناگون آنان را به تفرقه نکشانده است. فرشتگان بندگان ایمانند، و طوق بندگی به گردن افکنده و هیچ گاه با شک و تردید و سستی، آن را بر زمین نمی‌گذارند. در تمام آسمان‌ها جای پوستینی خالی نمی‌توان یافت مگر آن که فرشته‌ای به سجده افتاده، یا در کار و تلاش است. اطاعت فراوان آن‌ها بر یقین و معرفتشان نسبت به پروردگار می‌افزاید، و عزت خداوند عظمت او را در قلبشان بیشتر می‌نماید. \n۹- چگونگی آفرینش زمین \nزمین را به موج‌های پر خروش، و دریاهای مواج فرو پوشاند، موج‌هایی که بالای آن‌ها به هم می‌خورد و در تلاطمی سخت، هر یک دیگری را واپس می‌زد، چونان شتران نر مست، فریاد کنان و کف بر لب، به هر سوی روان بودند. پس، قسمت‌های سرکش آب از سنگینی زمین فرو نشست و هیجان آن‌ها بر اثر تماس با سینه زمین آرام گرفت. زیرا زمین با پشت بر آن می‌غلطید و آن همه سر و صدای امواج ساکن و آرام شده، چون اسب افسار شده رام گردید.\nخشکی‌های زمین در دل امواج، گسترد، و آب را از کبر و غرور و سرکشی و خروش باز داشت، و از شدت حرکتش کاسته شد، و بعد از آن همه حرکت‌های تند ساکت شد، و پس از آن همه خروش و سرکشی متکبرانه به جای خویش ایستاد. پس هنگامی که هیجان آب در اطراف زمین فرو نشست، و کوه‌های سخت و مرتفع را بر دوش خود حمل کرد، چشمه‌های آب از فراز کوه‌ها بیرون آورد و آب‌ها را در شکاف بیابان‌ها و زمین‌های هموار روان کرد، و حرکت زمین را با صخره‌های عظیم و قله کوه‌های بلند نظم داد، و زمین به جهت نفوذ کوه‌ها در سطح آن، فرو رفتن ریشه کوه‌ها در شکاف‌های آن و سوار شدن بر پشت دشت‌ها و صحراها، از لرزش و اضطراب باز ایستاد. \n۱۰- نقش پدیده‌های جوی در زمین \nو بین زمین و جو فاصله افکند، و وزش بادها را برای ساکنان آن آماده ساخت، تمام نیازمندیها و وسائل زندگی را برای اهل زمین استخراج و مهیا فرمود، آنگاه هیچ بلندی از بلندی‌های زمین را که آب چشمه‌ها و نهرها به آن راه ندارد وانگذاشت، بلکه ابرهایی را آفرید تا قسمت‌های مرده آن احیا شود، و گیاهان رنگارنگ برویند. قطعات بزرگ و پراکنده ابرها را به هم پیوست تا سخت به حرکت در آمدند، و با به هم خوردن ابرها، برق‌ها درخشیدن گرفت، و از درخشندگی ابرهای سفید کوه پیکر، و متراکم چیزی کاسته نشد.\n ابرها را پی در پی فرستاد تا زمین را احاطه کردند، و بادها شیر باران را از ابرها دوشیدند، و به شدت به زمین فرو ریختند، ابرها پایین آمده سینه بر زمین ساییدند، و آنچه بر پشت داشتند فرو ریختند که در بخش‌های بی گیاه زمین انواع گیاهان روییدن گرفت، و در دامن کوه‌ها، سبزه‌ها پدید آمد. \n۱۱- زیبایی‌های زمین \nپس زمین به وسیله باغ‌های زیبا، همگان را به سرور و شادی دعوت کرده، با لباس نازک گل برگ‌ها که بر خود پوشید، هر بیننده‌ای را به شگفتی واداشت. و با زینت و زیوری که از گلو بند گل‌های گوناگون، فخر کنان خود را آراست، هر بیننده‌ای را به وجد آورد، که فرآورده‌های نباتی را، توشه و غذای انسان، و روزی حیوانات قرار داد. در گوشه و کنار آن دره‌های عمیق آفرید، و راه‌ها و نشانه‌ها برای آنان که بخواهند از جاده‌های وسیع آن عبور کنند، تعیین کرد. \n۱۲- داستان زندگی آغازین آدم علیه السلام و اعزام پیامبران علیهم السلام \nهنگامی که خدا زمین را آماده زندگی انسان ساخت و فرمان خود را صادر فرمود، آدم علیه السلام را از میان مخلوقاتش برگزید، و او را نخستین و برترین مخلوق خود در زمین قرار داد. ابتدا آدم را در بهشت جای داده و خوراکی‌های گوارا به او بخشید و از آنچه که او را منع کرد پرهیز داد و آگاهش ساخت که اقدام بر آن، نافرمانی بوده و مقام و ارزش او را به خطر خواهد افکند. اما آدم علیه السلام به آنچه نهی شد، اقدام کرد و علم خداوند در باره او تحقق یافت، تا آن که پس از توبه، او را از بهشت به سوی زمین فرستاد، تا با نسل خود زمین را آباد کند، و بدین وسیله حجت را بر بندگان تمام کرد، و پس از وفات آدم علیه السلام زمین را از حجت خالی نگذاشت و میان فرزندان آدم علیه السلام و خود، پیوند شناسایی برقرار فرمود، و قرن به قرن، حجت‌ها و دلیل‌ها را بر زبان پیامبران برگزیده آسمانی و حاملان رسالت خویش جاری ساخت،\n تا اینکه سلسله انبیاء با پیامبر اسلام، حضرت محمد صلی الله علیه و آله و سلم به اتمام رسید و بیان احکام و انذار و بشارت الهی به سر منزل نهایی راه یافت. \n۱۳- آفرینش امکانات زندگی \nروزی انسان‌ها را اندازه گیری و مقدر فرمود، گاهی کم و زمانی زیاد، و به تنگی و وسعت به گونه‌ای عادلانه تقسیم کرد تا هر کس را که بخواهد با تنگی روزی یا وسعت آن بیازماید، و با شکر و صبر، غنی و فقیر را مورد آزمایش قرار دهد. پس روزی گسترده را با فقر و بیچارگی در آمیخت، و تندرستی را با حوادث دردناک پیوند داد، دوران شادی و سرور را با غصه و اندوه نزدیک ساخت، اجل و سر آمد زندگی را مشخص کرد، آن را گاهی طولانی و زمانی کوتاه قرار داد، مقدم یا مؤخر داشت، و برای مرگ، اسباب و وسائلی فراهم ساخت، و با مرگ، رشته‌های زندگی را در هم پیچید و پیوندهای خویشاوندی را از هم گسست تا آزمایش گردند. \n۱۴- تعریف علم خداوند \nخداوند از اسرار پنهانی مردم و از نجوای آنان که آهسته سخن می‌گویند و از آنچه که در فکرها بواسطه گمان خطور می‌کند، و تصمیم‌هایی که به یقین می‌پیوندد، و از نگاه‌های رمزی چشم که از لابلای پلک‌ها خارج می‌گردد، آگاه است. خدا از آنچه در مخفی گاه‌های دل‌ها قرار دارد، و از اموری که پشت پرده غیب پنهان است، و آنچه را که پرده‌های گوش مخفیانه می‌شنود، و از اندرون لانه‌های تابستانی مورچگان، و خانه‌های زمستانی حشرات، از آهنگ اندوهبار زنان غم دیده و صدای آهسته قدم‌ها، آگاهی دارد. خدای سبحان از جایگاه پرورش میوه در درون پرده‌های شکوفه‌ها، و از مخفی‌گاه غارهای حیوانات وحشی در دل کوه‌ها، و اعماق دره‌ها، از نهانگاه پشه‌ها بین ساقه‌ها و پوست درختان، از محل پیوستگی برگ‌ها به شاخسارها، و از جایگاه نطفه‌ها در پشت پدران، آگاه است. خدا از آنچه پرده ابر را به وجود می‌آورد و به هم می‌پیوندند، و از قطرات بارانی که از ابرهای متراکم می‌بارند، و از آنچه که گرد بادها از روی زمین برمی‌دارند، و باران‌ها با سیلاب آن را فرو می‌نشانند و نابود می‌کنند، از ریشه گیاهان زمین که میان انبوه شن و ماسه پنهان شده است، از لانه پرندگانی که در قله بلند کوه‌ها جای گرفته، و از نغمه‌های مرغان در آشیانه‌های تاریک، از لؤلؤهایی که در دل صدف‌ها پنهان است، و امواج دریاهایی که آن‌ها را در دامن خویش پروراندند آگاهی دارد. خدا از آنچه که تاریکی شب آن را فرا گرفته، و یا نور خورشید بر آن تافته، و آنچه تاریکی‌ها و امواج نور، پیاپی آن را در بر می‌گیرد، از اثر هر قدمی، از احساس هر حرکتی، و آهنگ هر سخنی، و جنبش هر لبی، و مکان هر موجود زنده‌ای، و وزن هر ذره‌ای، و ناله هر صاحب اندوهی اطلاع دارد. خدا هر آنچه از میوه شاخسار درختان و برگ‌هایی که روی زمین ریخته و از قرارگاه نطفه و بسته شدن خون و جنین که به شکل پاره‌ای گوشت است، و پرورش دهنده انسان و نطفه آگاهی دارد، و برای این همه آگاهی، هیچ گونه زحمت و دشواری برای او وجود ندارد و برای نگهداری این همه از مخلوقات رنگارنگ که پدید آورده دچار نگرانی نمی‌شود، و در تدبیر امور مخلوقات، سستی و ملالی در او راه نمی‌یابد، بلکه علم پروردگار در آن‌ها نفوذ یافته و همه آن‌ها را شماره کرده است، و عدالتش همه را در بر گرفته و با کوتاهی کردن مخلوقات در ستایش او، باز فضل و کرمش تداوم یافته است. \n۱۵- نیایش امیر المؤمنین علیه السلام \nخدایا تویی سزاوار ستایش‌های نیکو، و بسیار و بی شمار تو را ستودن اگر تو را آرزو کنند پس بهترین آرزویی، و اگر به تو امید بندند، بهترین امیدی. خدایا درهای نعمت بر من گشودی که زبان به مدح غیر تو نگشایم. و بر این نعمت‌ها غیر از تو را ستایش نکنم، و زبان را در مدح نومید کنندگان، و آنان که مورد اعتماد نیستند باز نکنم.\n خداوندا هر ثناگویی از سوی ستایش شده پاداشی دارد، به تو امید به ستم که مرا به سوی ذخائر رحمت و گنج‌های آمرزش آشنا کنی. خدایا این بنده توست که تو را یگانه می‌خواند، و توحید و یگانگی تو را سزاست، و جز تو کسی را سزاوار این ستایش‌ها نمی‌داند. خدایا مرا به درگاه تو نیازی است که جز فضل تو جبران نکند، و آن نیازمندی را جز عطا و بخشش تو به توانگری مبدل نگرداند، پس در این مقام رضای خود را به ما عطا فرما، و دست نیاز ما را از دامن غیر خود کوتاه گردان که «تو بر هر چیزی توانایی».', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (234, 1, 92, 'پس از کشته شدن عثمان و بیعت با او', 'خطبه ۹۲-پس از کشته شدن عثمان \nعلل کناره گیری از خلافت \nمرا بگذارید و دیگری را به دست آرید، زیرا ما به استقبال حوادث و اموری می‌رویم که رنگارنگ و فتنه آمیز است، و چهره‌های گوناگون دارد و دلها بر این بیعت ثابت و عقلها بر این پیمان استوار نمی ماند، چهره افق حقیقت را (در دوران خلافت سه خلیفه) ابرهای تیره فساد گرفته، و راه مستقیم حق ناشناخته ماند. آگاه باشید! اگر دعوت شما را بپذیرم بر اساس آنچه که می‌دانم با شما رفتار می‌کنم، و به گفتار این و آن، و سرزنش سرزنش کنندگان گوش فرا نمی دهم، اگر مرا رها کنید چون یکی از شماها هستم، که شاید شنواتر، و مطیع تر از شما نسبت به رئیس حکومت باشم، در حالی که من وزیر و مشاورتان باشم بهتر است که امیر و رهبر شما گردم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (235, 1, 93, 'خبر از فتنه', 'خطبه ۹۳-خبر از فتنه \nویژگیهای علمی و سیاسی امام علی (ع) \nپس از حمد و ستایش پروردگار، ای مردم! من بودم که چشم فتنه را کندم، و جز من هیچ کس جرات چنین کاری را نداشت، آنگاه که امواج سیاهیها بالا گرفت و به آخرین درجه شدت خود رسید، پس از من بپرسید پیش از آنکه مرا نیابید سوگند به خدایی که جانم در دست اوست، نمی پرسید از چیزی که میان شما تا روز قیامت می‌گذرد، و نه از گروهی که صد نفر را هدایت یا گمراه می‌سازد، جز آنکه شما را آگاه می‌سازم و پاسخ می‌دهم. و از آنکه مردم را بدان می‌خواند، و از آنکه رهبریشان می‌کند، و آنکه آنان را می‌راند، و آنجا که فرود می‌آیند، و آنجا که بار گشایند، و آنکه از آن‌ها کشته شود، و آنکه بمیرد، خبر می‌دهم، آن روز که مرا از دست دادید، و نگرانیها و مشکلات بر شما باریدن گرفت، بسیاری پرسش کنندگان به حیرت فرو رفته می‌گویند، سرانجام چه خواهد شد؟ که گروه بسیاری از مسوولان از پاسخ دادن فرو مانند خبر از فتنه‌های آینده این حوادث هنگامی رخ نشان می‌دهد که جنگ در میان شما طولانی شود، و دنیا چنان بر شما تنگ گردد که ایام بلا را طولانی پندارید، تا روزی که خداوند پرچم فتح و پیروزی را برای باقیمانده نیکان شما به اهتزاز \nدرآورد (زمان ظهور حضرت حجت عجل الله تعالی فرجه الشریف) \nفتنه‌ها آنگاه که روی آورند با حق شباهت دارند، و چون پشت کنند حقیقت چنانکه هست، نشان داده می‌شود، فتنه‌ها چون می‌آیند شناخته نمی شوند، و چون می‌گذرند، مورد شناسایی قرار می‌گیرند، فتنه‌ها چون گردبادها می‌چرخند، از همه جا عبور می‌کنند، در بعضی از شهرها حادثه می‌آفرینند و از برخی شهرها می‌گذرند. خبر از فتنه‌های بنی امیه آگاه باشید همانا ترسناکترین فتنه‌ها در نظر من، فتنه بنی امیه بر شما است، فتنه ای کور و ظلمانی که سلطه اش همه جا را فرا گیرد و بلای آن دامنگیر نیکوکاران است، هر کس آن فتنه‌ها را بشناسد نگرانی و سختی آن دامنگیرش گردد، و هر کس که فتنه‌ها را نشانسد، حادثه ای برای او رخ نخواهد داد. به خدا سوگند! بنی امیه بعد از من برای شما زمامداران بدی خواهند بود، آنان چونان شتر سرکشی که دست به زمین کوبد و لگد زند و با دهان گاز گیرد و از دوشیدن شیر امتناع ورزد، با شما چنین برخوردی دارند، و از شما کسی باقی نگذارند، جز آن کس که برای آن‌ها سودمند باشد یا آزاری بدانها نرساند، و بلای فرزندان امیه، بر شما طولانی خواهد ماند، چندان که یاری خواستن شما از ایشان چون یاری خواستن بنده از مولای خویش، یا تسلیم شده از پ \nیشوای خود می‌ماند. فتنه‌های بنی امیه پیاپی با چهره ای زشت و ترس آور، و ظلمتی با تاریکی عصر جاهلیت، بر شما فرود می‌آید، نه نور هدایتی در آن پیدا، و نه پرچم نجاتی در آن روزگاران به چشم می‌خورد. سرانجام تلخ و دردناک بنی امیه ما اهل بیت پیامبر (ع) از آن فتنه‌ها در امانیم و مردم را بدان نمی خوانیم، سپس خدا فتنه‌های بنی امیه را براندازد، و از شما جدا سازد مانند جدا شدن پوست از گوشت، که با دست قصابی انجام پذیرد، خدا با دست افرادی، خواری و ذلت را به فرزندان امیه می‌چشاند که به سختی آن‌ها را می‌رانند، و جام تلخ بلا و ناراحتی و مصیبت را در کامشان می‌ریزند، و جز شمشیر چیزی به آن‌ها نخواهند داد، و جز لباس ترس بر آن‌ها نپوشانند، در آن هنگام، قریش دوست دارد آنچه در دنیاست بدهد تا یک بار مرا بنگرد، گرچه لحظه کوتاهی به اندازه کشتن شتری باشد، تا با اصرار چیزی را بپذیرم که امروز پاره ای از آن را می‌خواهم نمی دهند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (236, 1, 94, 'در فضل رسول اکرم و پند و اندرز', 'خطبه ۹۴-در فضل رسول اکرم \nوصف خدای سبحان \nبرتر و بزرگ است خدایی که اندیشه‌های ژرف، حقیقت ذات او را درک نتوانند کرد، و گمان زیرکها آن را نمی یابد، آغازی است که نهایتی ندارد تا به آخر رسد، و پایانی ندارد تا ایام او سپری شود. \nوصف پیامبران آسمانی پیامبران را در بهترین جایگاه به ودیعت گذارد، و در بهترین مکانها استقرارشان داد، از صلب کریمانه پدران به رحم پاک مادران منتقل فرمود، که هرگاه یکی از آنان درگذشت، دیگری برای پیش بر دین خدا بپا خواست. وصف پیامبر اسلام و اهل بیت (ع) تا اینکه کرامت اعزام نبوت از طرف خدای سبحان به حضرت محمد (ص) رسید، نهاد اصلی وجود او را از بهترین معادن استخراج کرد، و نهال وجود او را در اصیل ترین و عزیزترین سرزمینها کاشت و آبیاری کرد، او را از همان درختی که دیگر پیامبران و امنیان خود را از آن آفرید به وجود آورد، که عترت او بهترین عترتها، خاندانش بهترین خاندانها، و درخت وجودش از بهترین درختان است، در حرم امن خدا رویید، و در آغوش خانواده کریمی بزرگ شد، شاخه‌های بلند آن سر به آسمان کشیده که دست کسی به میوه آن نمی رسید. پس، پیامبر (ص) پیشوای پرهیزکاران، و وسیله بینایی هدایت خواهان است، چراغی با نور درخشان، و ستاره ای فروزان، و شعله ای با برقهای خیره کننده و تابان است، راه و رسم او با اعتدال و روش زندگی او صحیح و پایدار، و سخنانش روشنگر حق و باطل، و حکم او عادلانه است. خدا او را زمانی مبعوث فرمود که با \nزمان پیامبران گذشته فاصله طولانی داشت و مردم از نیکوکاری فاصله گرفته، و امتها، به جهل و نادانی گرفتارشده بودند. \nنصیحت به مردم خدا شما را بیامرزد، اعمال نیکو بر اساس نشانه‌های روشن انجام دهید، زیرا که راه، روشن است، و شما را به خانه امن و امان دعوت می‌کند، هم اکنون در دنیایی زندگی می‌کنید که می‌توانید رضایت خدا را به دست آورید، با مهلت و آسایش خاطری که دارید، اکنون نامه عمل سرگشاده، و قلم فرشتگان نویسنده در حرکت است، بدنها سالم و زبانها گویاست، توبه مورد قبول و اعمال نیکو را می‌پذیرند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (237, 1, 95, 'وصف پیامبر', 'خطبه ۹۵-وصف پیامبر \nدوران جاهلیت و نعمت بعثت \nخدا پیامبر اسلام را به هنگامی مبعوث فرمود که مردم در حیرت و سرگردانی بودند، در فتنه‌ها بسر می‌بردند، هوی و هوس بر آن‌ها چیره شده، و خود بزرگ بینی و تکبر به لغزشهای فراوانشان کشانده بود، و نادانیهای جاهلیت پست و خوارشان کرده، و در امور زندگی حیران و سرگردان بودند، و بلای جهل و نادانی دامنگیرشان بود پس پیامبر (ص) در نصیحت و خیرخواهی نهایت تلاش را کرد، و آنان را به راه راست راهنمایی، و از راه حکمت و موعظه نیکو، مردم را به خدا دعوت فرمود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (238, 1, 96, 'وصف خدا و رسول', 'خطبه ۹۶-وصف خدا و رسول \nخداشناسی \nسپاس خدا را که اول است، و چیزی پیش از او وجود نداشت، و آخر است و پس از او موجودی نخواهد بود، چنان آشکار است که فراتر از او چیزی نیست، و چنان مخفی و پنهان است که مخفی تر از او یافت نمی شود \nویژگیهای پیامبر (ص) قرارگاه پیامبر (ص) بهترین قرارگاه و محل پرورش و خاندان او شریفترین پایگاه است، در معدن بزرگواری و گاهواره سلامت رشد کرد، دلهای نیکوکاران شیفته او گشته، توجه دیده‌ها به سوی اوست. خدا به برکت وجود او کینه‌ها را دفن کرد و آتش دشمنیها را خاموش نمود، با او میان دلها الفت و مهربانی ایجاد، و نزدیکانی را از هم دور ساخت. انسانهای خوار و ذلیل و محروم در پرتو او عزت یافتند، و عزیزانی خودسر ذلیل شدند، گفتار او روشنگر، و سکوت او زبانی دیگر بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (239, 1, 97, 'در باب اصحابش و اصحاب پیامبر', 'خطبه ۹۷-در باب اصحابش \nعلل نکوهش و شکست کوفیان \nاگر خداوند، ستمگر را چند روزی مهلت دهد، از بازپرسی و عذاب او غفلت نمی کند، و او بر سر راه، در کمینگاه ستمگران است، و گلوی آن‌ها را در دست گرفته از فرو رفتن آب دریغ دارد. آگاه باشید! به خدایی که جانم در دست اوست، شامیان بر شما پیروز خواهند شد، نه از آن رو که از شما به حق سزاوارترند بلکه در راه باطلی که زمامدارشان می‌رود شتابان فرمانبردارند، و شما در گرفتن حق من سستی می‌ورزید، و هر آینه، ملتهای جهان صبح می‌کنند در حالی که از ستم زمامدارانشان در ترس و وحشتند، من صبح می‌کنم در حالی که از ستمگری پیروان خود وحشت دارم شما را برای جهاد با دشمن برانگیختم، اما کوچ نکردید، حق را بگوش شما خواندم ولی نشنیدید، و در آشکار و نهان شما را دعوت کردم، اجابت ننمودید، پند و اندرزتان دادم، قبول نکردید آیا حاضران غائب می‌باشید؟ و یا بردگانی در شکل مالکان؟! فرمان خدا را بر شما می‌خوانم از آن فرار می‌کنید، و با اندرزهای رسا و گویا شما را پند می‌دهم از آن پراکنده می‌شوید، شما را به مبارزه با سرکشان ترغیب می‌کنم، هنوز سخنانم به آخر نرسیده، چون مردم سبا، متفرق شده، به جلسات خود باز می‌گردید، و در لباس \nپند و اندرز، یکدیگر را فریب می‌دهید تا اثر تذکرات مرا از بین ببرید، صبحگاهان کجیهای شما را راست می‌کنم، شامگاهان به حالت اول برمی گردید، چونان کمان سختی که نه کسی قدرت راست کردن آن را دارد و نه خودش قابلیت راست شدن را خواهد داشت ای مردمی که بدنهای شما حاضر و عقلهای شما پنهان و افکار و آراء شما گوناگون است و زمامداران شما دچار مشکلات شمایند، رهبر شما از خدا اطاعت می‌کند، شما با او مخالفت می‌کنید، اما رهبر شامیان خدای را معصیت می‌کند، از او فرمانبردارند. به خدا سوگند دوست دارم معاویه شما را با نفرات خود مانند مبادله درهم و دینار با من سودا کند، ده نفر از شما را بگیرد و یک نفر از آن‌ها را به من بدهد! روانشناسی اجتماعی مردم کوفه ای اهل کوفه! گرفتار شما شده ام که سه چیز دارید و دو چیز ندارید، کرهایی با گوشهای شنوا، گنگهایی با زبان گویا، کورانی با چشمهای بینا، نه در روز جنگ از آزادگانید، و نه به هنگام بلا و سختی برادران یکرنگ می‌باشید تهیدست مانید! ای مردم شما چونان شتران دورمانده از ساربان می‌باشید، که اگر از سویی جمع آوری شوند از دیگر سو، پراکنده می‌گردند، به خدا سوگند می‌بینم که اگر جنگ سخت شود و آتش آن شعله \nگیرد و گرمی آن سوزان، پسر ابوطالب را رها می‌کنید و مانند جدا شدن زن حامله پس از زایمان از فرزندش، هر یک به سویی می‌گریزید و من در پی آن نشانه‌ها روانم که پروردگارم مرا رهنمون شد، و آن راه را می‌روم که رسول خدا (ص) گشود، و همانا من به راه روشن حق گام به گام ره می‌سپارم. مردم به اهل بیت پیامبرتان بنگرید، از آن سو که گام برمی دارند بروید، قدم جای قدمشان بگذارید، آن‌ها شما را هرگز از راه هدایت بیرون نمی برند، و به پستی و هلاکت باز نمی گردانند، اگر سکوت کردند سکوت کنید، و اگر قیام کردند قیام کنید، از آن‌ها پیشی نگیرید که گمراه می‌شوید، و از آنان عقب نمانید که نابود می‌گردید. \nوصف اصحاب پیامبر (ع) من اصحاب محمد (ص) را دیدم، اما هیچکدام از شما را همانند آنان نمی نگرم، آن‌ها صبح می‌کردند در حالیکه موهای ژولیده و چهره‌های غبارآلوده داشتند، شب را تا صبح در حال سجده و قیام به عبادت می‌گذراندند، و پیشانی و گونه‌های صورت را در پیشگاه خدا بر خاک می‌ساییدند، با یاد معاد چنان ناآرام بودند گویا بر روی آتش ایستاده اند، بر پیشانی آن‌ها از سجده‌های طولانی پینه بسته بود (چون پینه زانوهای بزها) اگر نام خدا برده می‌شد چنان می‌گریستند که گریبانهای آنان تر می‌شد. و چون درخت در روز تندباد می‌لرزیدند، از کیفری که از آن بیم داشتند، یا برای پاداشی که به آن امیدوار بودند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (240, 1, 98, 'در ستم بنی‌امیه', 'خطبه ۹۸-در ستم بنی امیه \nخبر از ستمگری و فساد بنی امیه \nسوگند به خدا! بنی امیه چنان به ستمگری و حکومت ادامه دهند که حرامی باقی نماند جز آنکه حلال شمارند، و پیمانی نمی ماند جز آنکه همه را بشکنند، و هیچ خیمه و خانه ای وجود ندارد جز آنکه ستمگاری آنان در آنجا راه یابد، و ظلم و فسادشان مردم را از خانه‌ها کوچ دهد، تا آنکه در حکومتشان دو دسته بگریند. دسته ای برای دین خود که آن را از دست داده اند، و دسته ای برای دنیای خود که به آن نرسیده اند، و یاری خواستن یکی از دیگری، چون یاری خواستن برده از ارباب خویش است که در حضور ارباب اطاعت دارد و در غیبت او بدگویی می‌کند. در حکومت بنی امیه هر کس به خدا امیدوارتر باشد بیش از همه رنج و مصیبت ببیند، پس اگر خداوند عافیت و سلامتی بخشید، قدرشناس باشید و اگر به بلا و گرفتاری مبتلا گشتید، شکیبا باشید که سرانجام، پیروزی با پرهیزکاران است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (241, 1, 99, 'در گریز از دنیا', 'خطبه ۹۹-در گریز از دنیا \n(زید بن وهب، از اصحاب امام است، نقل می‌کند که این خطبه در روز جمعه در شهر کوفه ایراد شد)\nخدا را بر نعمتهایی که عطا فرمود ستایش می‌کنیم و در کارهای خود از او یاری می‌جوییم، از او سلامت در دین را خواهانیم آنگونه که تندرستی بدن را از او درخواست داریم \nپرهیز از دنیاپرستی \nای بندگان خدا، شما را به ترک دنیایی سفارش می‌کنم که شما را رها می‌سازد، گرچه شما جدایی از آن را دوست ندارید، دنیایی که بدنهای شما را کهنه و فرسوده می‌کند با اینکه دوست دارید همواره تازه و پاکیزه بمانید. شما و دنیا به مسافرانی مانید که تا گام در آن نهاده، احساس دارند به پایان راه رسیدند، و تا قصد رسیدن به نشانی کرده اند، گویا بدان دست یافتند، در حالی که تا رسیدن به هدف نهایی هنوز فاصله‌های زیادی است. چگونه می‌تواند به مقصد رسد کسی که روز معینی در پیش دارد؟ و از آن تجاوز نخواهد کرد! مرگ به سرعت او را می‌راند، و عوامل مختلف او را برخلاف خواسته خود از دنیا جدا می‌سازند. \nروش برخورد با دنیا \nپس در عزت و ناز دنیا بر یکدیگر پیشی نگیرید، و فریب زینتها و نعمتها را نخورید و مغرور نشوید، و از رنج و سختی آن ننالید، و ناشکیبا نباشید، زیرا عزت و افتخارات دنیا پایان می‌پذیرد، و زینت و نعمتهایش نابود می‌گردد، و رنج و سختی آن تمام می‌شود، و هر مدتی و مهلتی در آن به پایان می‌رسد، و هر موجود زنده ای به سوی مرگ می‌رود آیا نشانه‌هایی از زندگی گذشتگان که برجا مانده شما را از دنیاپرستی باز نمی دارد؟ \nاگر خردمندید!! آیا در زندگانی پدران شما آگاهی و عبرت آموزی نیست؟ مگر نمی بینید که گذشتگان شما باز نمی گردند؟ و فرزندان شما باقی نمی مانند؟ مگر مردم دنیا را نمی نگرید که در گذشت شب و روز حالات گوناگونی دارند؟ یکی می‌میرد و بر او می‌گریند، و دیگری باقیمانده به او تسلیت می‌گویند، یکی دیگر بر بستر بیماری افتاده دیگری به عیادت او می‌آید، و دیگری در حال جان کندن است، و دنیاطلبی در جستجوی دنیاست که مرگ او را درمی یابد، و غفلت زده ای که مرگ او را فراموش نکرده است، و آیندگان نیز راه گذشتگان را می‌پویند. به هوش باشید، مرگ را که نابودکننده لذتها و شکننده شهوتها و قطع کننده آرزوهاست، به هنگام تصمیم بر کارهای زشت، به یاد آورید، و برای انجام واجبات، و شکر در برابر نعمتها و احسان بی شمار الهی، از خدا یاری خواهید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (242, 2, 1, 'به مردم کوفه', 'نامه ۱- به مردم کوفه \nافشای سران ناکثین \nاز بنده خدا، علی امیر مومنان، به مردم کوفه، که در میان انصار پایه ای ارزشمند، و در عرب مقامی والا دارند. پس از ستایش پروردگار! همانا شما را از کار عثمان چنان آگاهی دهم که شنیدن آن چونان دیدن باشد، مردم برعثمان عیب گرفتند، و من تنها کسی از مهاجران بودم که او را برای جلب رضایت مردم واداشته، و کمتر به سرزنش او زبان گشودم، اما طلحه و زبیر، آسان ترین کارشان آن بود که بر او بتازند، و او را برنجانند، و ناتوانش سازند، عایشه نیز ناگهان بر او خشم گرفت، عده ای به تنگ آمده او را کشتند، آنگاه مردم بدون اکراه و اجبار، بلکه به اطاعت و اختیار، با من بیعت کردند. آگاه باشید! مدینه مردم را یکپارچه بیرون رانده، و مردم نیز او را برای سرکوبی آشوب فاصله گرفتند، دیگ آشوب به جوش آمده، و فتنه‌ها بر پایه‌های خود ایستاد، پس به سوی فرمانده خود بشتابید، و در جهاد با دشمن بر یکدیگر پیشی گیرید، به خواست خدای عزیز و بزرگ.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (243, 2, 10, 'به معاویه', 'نامه ۱۰-به معاویه \nافشای چهره معاویه \nچه خواهی کرد، آنگاه که جامه‌های رنگین تو کنار رود؟ که به زیباییهای دنیا زینت شده بود، دنیا تو را با خوشیهای خود فریب داده، و به دعوت آن پاسخ داده ای، فرمانت داد و اطاعت کردی، همانا به زودی تو را وارد می‌دان خطرناکی می‌کند که هیچ سپر نگهدارنده ای نجاتت نمی دهد. ای معاویه از این کار دست بکش، و آماده حساب باش، و آماده حوادثی باش که به سراغ تو می‌آید، به گمراهان فرومایه، گوش مسپار، اگر چنین نکنی به تو اعلام می‌دارم که در غفلت زدگی قرار گرفته ای، همانا تو نازپرورده ای می‌باشی که شیطان بر تو حکومت می‌کند، و با تو به آرزوهایش می‌رسد، و چون روح و خون در سراسر وجودت جریان دارد. معاویه! از چه زمانی شما زمامداران امت و فرماندهان ملت بودید؟ نه سابقه درخشانی در دین، و نه شرافت والایی در خانواده دارید، پناه به خدا می‌برم از گرفتار شدن به دشمنی‌های ریشه دار، تو را می‌ترسانم از ینکه به دنبال آرزوها تلاش کنی، و آشکار و نهانت یکسان نباشد. \nپاسخ به تهدید نظامی \nمعاویه! مرا به جنگ خوانده ای، اگر راست می‌گویی مردم را بگذار و به جنگ من بیا، و دو لشکر را از کشتار بازدار، تا بدانی پرده تاریک بر دل کدام یک از ما کشیده، و دیده چه کس پوشیده است؟ من ابوالحسن، کشنده جد و دایی و برادر تو در روز نبرد بدر، می‌باشم که سر آنان را شکافتم، همان شمشیر با من است، و با همان قلب با دشمنام ملاقات می‌کنم، نه بدعتی در دین گذاشته، و نه پیامبر جدیدی برگزیده ام، من بر همان راه راست الهی قرار دارم که شما با اختیار رها کرده، و با اکراه پذیرفته بودید. \nپاسخ به خونخواهی دروغین معاویه \nخیال کردی به خونخواهی عثمان آمده ای؟ در حالی که می‌دانی خون او به دست چه کسانی ریخته شده، اگر راست می‌گویی از آن‌ها مطالبه کن، همانا من تو را در جنگ می‌نگرم که چونان شتران زیر بار سنگین مانده، فریاد و ناله سرمی دهی، و می‌بینم که لشکریانت با بی صبری از ضربات پیاپی شمشیرها، و بلاهای سخت، و بر خاک افتادن مداوم تنها، مرا به کتاب خدا می‌خوانند در حالی که لشکریان تو، کافرند و در انکار، از بیعت کنندگانند و پیمان شکن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (244, 2, 11, 'به گروهی از سپاهیان که به سرکوبی دشمن فرستاد', 'نامه ۱۱-به گروهی از سپاهیان \nآموزش نظامی به لشکریان \nهرگاه به دشمن رسیدید، یا او به شما رسید، لشکرگاه خویش را بر فراز بلندیها، یا دامنه کوهها، یا بین رودخانه‌ها قرار دهید، تا پناهگاه شما، و مانع هجوم دشمن باشد، جنگ را از یک سو یا دو سو آغاز کنید، و در بالای قله‌ها، و فراز تپه‌ها، دیده بانهایی بگمارید، مبادا دشمن از جایی که می‌ترسید یا از سویی که بیم ندارید، ناگهان بر شما یورش آورد، و بدانید که پیشاهنگان سپاه دیدبان لشگریانند، و دیدبانان طلایه داران سپاهند، از پراکندگی بپرهیزید، هر جا فرود می‌آیید، با هم فرود بیایید، و هرگاه کوچ می‌کنید همه با هم کوچ کنید، و چون تاریک شب شما را پوشاند، نیزه داران را پیرامون لشکر بگمارید، و نخوابید مگر اندک، چونان آب در دهان چرخاندن و بیرون ریختن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (245, 2, 12, 'به معقل بن قیس الریاحی', 'نامه ۱۲-به معقل بن قیس الریاحی \nاحتیاطهای نظامی نسبت به سربازان پیشتاز \nاز خدایی بترس که ناچار او را ملاقات خواهی کرد، و سرانجامی جز حاضر شدن در پیشگاه او را نداری، جز با کسی که با تو پیکار کند، پیکار نکن، در خنکی صبح و عصر سپاه را حرکت ده، در هوای گرم لشکر را استراحت ده، و در پیمودن راه شتاب مکن، در آغاز شب حرکت نکن زیرا خداوند شب را وسیله آرامش قرار داده، و آن را برای اقامت کردن، نه کوچ نمودن، تعیین فرموده است پس آسوده باش، و مرکبها را آسوده بگذار، آنگاه که سحر آمد و سپیده صبحگاهان آشکار شد، در پناه برکت پروردگار حرکت کن. پس هر جا دشمن را مشاهده کردی در میان لشکرت بایست، نه چنان به دشمن نزدیک شو که چونان جنگ افروزان باشی، و نه آنقدر دور باش که پندارند از نبرد می‌هراسی، تا فرمان من به تو رسد، مبادا کینه آنان شما را به جنگ وادارد، پیش از آنکه آنان را به راه هدایت فراخوانید، و درهای عذر را بر آنان ببندید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (246, 2, 13, 'به دو نفر از امیران لشگر', 'نامه ۱۳-به دو نفر از امیران لشگر \nرعایت سلسله مراتب فرماندهی \nمن، مالک اشتر پسر حارث را بر شما و سپاهیانی که تحت امر شما هستند، فرماندهی دادم. گفته او را بشنوید، و از فرمان او اطاعت کنید، او را چونان زره و سپر نگهبان خود برگزینید، زیرا که مالک، نه سستی به خرج داده و نه دچار لغزش می‌شود، نه در آنجایی که شتاب لازم است کندی دارد، و نه آن جا که کندی پسندیده است شتاب می‌گیرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (247, 2, 14, 'به سپاهیانش', 'نامه ۱۴-به سپاهیانش \nرعایت اصول انسانی در جنگ با دشمن \nجنگ را آغاز نکنید تا آن‌ها شروع کنند، زیرا سپاس خداوندی را که حجت با شماست، و آغازگر جنگ نبودن، تا آنکه دشمن بجنگ روی آورد، حجت دیگر بر حقانیت شما خواهد بود، اگر به اذن خدا شکست خوردند و گریختند، آن کس را که پشت کرده مکشید، و آن را که دفاع نمی تواند آسیب نرسانید، و مجروحان را نکشید، زنان را با آزار دادن تحریک نکنید هرچند آبروی شما را بریزند، یا امیران شما را دشنام دهند، که آنان در نیروی بدنی و روانی و اندیشه کم توانند، در روزگاری که زنان مشرک بودند مامور بودیم دست از آزارشان برداریم، و در جاهلیت اگر مردی با سنگ یا چوب دستی به زنی حمله می‌کرد، او و فرزندانش را سرزنش می‌کردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (248, 2, 15, 'راز و نیاز با خدا هنگام روبرو شدن با دشمن', 'نامه ۱۵-راز و نیاز با خدا \nنیایش در جنگ \nخدایا! قلبها به سوی تو روانه شده، و گردنها به درگاه تو کشیده، و دیده‌ها به آستان تو دوخته، و گامها در راه تو نهاده، و بدن‌ها در خدمت تو لاغرشده است، خدایا دشمنی‌های پنهان آشکار، و دیگهای کینه در جوش است، خدایا به تو شکایت می‌کنیم از اینکه پیامبر (ص) در میان ما نیست، و دشمنان ما فراوان، و خواسته‌های ما پراکنده است. (پروردگارا! بین ما و دشمنانمان به حق داوری فرما که تو از بهترین داورانی).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (249, 2, 16, 'به یارانش وقت جنگ', 'نامه ۱۶-به یارانش وقت جنگ \nآموزش تاکتیکهای نظامی \nاز عقب نشینی‌هایی که مقدمه هجوم دیگر است، و از ایستادنی که حمله در پی دارد نگرانتان نسازد، حق شمشیرها را اداء کنید، و پشت دشمن را به خاک بمالید، و برای فرو کردن نیزه‌ها، و محکم ترین ضربه‌های شمشیر، خود را آماده کنید، صدای خود را در سینه‌ها نگهدارید. که در زدودن سستی نقش بسزایی دارد. به خدایی که دانه را شکافت، و پدیده‌ها را آفرید، آن‌ها اسلام را نپذیرفتند، بلکه به ظاهر تسلیم شدند، و کفر خود را پنهان داشتند، آنگاه که یاورانی یافتند آن را آشکار ساختند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (250, 2, 17, 'در پاسخ نامه معاویه', 'نامه ۱۷-در پاسخ نامه معاویه \nافشای چهره بنی امیه و فضائل اهل بیت (ع) \nمعاویه! اینکه خواستی شام را به تو واگذارم، همانا من چیزی را که دیروز از تو باز داشتم، امروز به تو نخواهم بخشید، و اما سخن تو که جنگ، عرب را جز اندکی، بکام خویش فرو برده است، آگاه باش، آن کس که بر حق بود، جایگاهش بهشت، و آنکه بر راه باطل بود در آتش است. اما اینکه ادعای تساوی در جنگ و نفرات جهادگر کرده ای، بدان، که رشد تو در شک به درجه کمال من در یقین نرسیده است، و اهل شام بر دنیا حریص تر از اهل عراق به آخرت نیستند. \nفضائل عترت پیامبر (ص) \nو اینکه ادعا کردی ما همه فرزندان (عبدمناف) هستیم، آری چنین است، اما جد شما (امیه) چونان جد ما (هاشم) و (حرب) همانند (عبدالمطلب) و (ابوسفیان) مانند (ابوطالب) نخواهند بود، هرگز ارزش مهاجران چون اسیران آزادشده نیست، و حلال زاده همانند حرام زاده نمی باشد، و آنکه بر حق است با آنکه بر باطل است را نمی توان مقایسه کرد، و مومن چون مفسد نخواهد بود، و چه زشتند آنان که پدران گذشته خود را در ورود به آتش پیروی کنند. از همه که بگذریم، فضیلت نبوت در اختیار ماست که با آن عزیزان را ذلیل، و خوارشدگان را بزرگ کردیم، و آنگاه که خداوند امت عرب \nرا فوج فوج به دین اسلام درآورد، و این امت در برابر دین یا از روی اختیار یا اجبار تسلیم شد، شما خاندان ابوسفیان، یا برای دنیا و یا از روی ترس در دین اسلام وارد شدید، و این هنگامی بود که نخستین اسلام آورندگان بر همه پیشی گرفتند، و مهاجران نخستین ارزش خود را باز یافتند، پس ای معاویه شیطان را از خویش بهره مند، و او را بر جان خویش راه مده. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (251, 2, 18, 'به عبدالله بن عباس، فرماندار بصره', 'نامه ۱۸-به عبدالله بن عباس\nروش برخورد با مردم \nبدان، که بصره امروز جایگاه شیطان، و کشتزار فتنه‌هاست، با مردم آن به نیکی رفتار کن، و گره وحشت زا از دلهای آنان بگشای، بدرفتاری تو را با قبیله (بنی تمیم) و خشونت با آن‌ها را به من گزارش دادند، همانا (بنی تمیم) مردانی نیرومندند که هرگاه دلاوری از آن‌ها غروب کرد، سلحشور دیگری جای آن درخشید، و در نبرد، در جاهلیت و اسلام، کسی از آن‌ها پیشی نگرفت، و همانا آن‌ها با ما پیوند خویشاوندی، و قرابت و نزدیکی دارند، که صله رحم و پیوند با آنان پاداش، و گسستن پیوند با آنان کیفر الهی دارد، پس مدارا کن ای ابوالعباس! امید است آنچه از دست و زبان تو از خوب یا بد، جاری می‌شود، خدا تو را بیامرزد، چرا که من و تو در اینگونه از رفتارها شریکیم. سعی کن تا خوش بینی من نسبت به شما استوار باشد، و نظرم دگرگون نشود، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (252, 2, 19, 'به یکی از فرماندهان', 'نامه ۱۹-به یکی از فرماندهان \nهشدار از بدرفتاری \nبا مردم پس از نام خدا و درود. همانا دهقانان مرکز فرمانداریت، از خشونت و قساوت و تحقیر کردن مردم و سنگدلی تو شکایت کردند، من درباره آن‌ها اندیشیدم، نه آنان را شایسته نزدیک شدن یافتم، زیرا که مشرکند. و نه سزاوار قساوت و سنگدلی و بدرفتاری هستند زیرا که با ما هم پیمانند، پس در رفتار با آنان، نرمی و درشتی را به هم آمیز. رفتاری توام با شدت و نرمش داشته باش، اعتدال و میانه روی را در نزدیک کردن یا دور نمودن، رعایت کن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (253, 2, 2, 'قدردانی از اهل کوفه بعد از فتح بصره', 'نامه ۲- قدردانی از اهل کوفه \nتشکر از مجاهدان از جنگ برگشته \nخداوند شما مردم کوفه را از سوی اهل بیت پیامبر (ص) پاداش نیکو دهد، بهترین پاداشی که به بندگان فرمانبردار، و سپاسگزاران نعمتش عطا می‌فرماید، زیرا شما دعوت ما را شنیدید و اطاعت کردید، به جنگ فرا خوانده شدید و بسیج گردیدید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (254, 2, 20, 'به زیاد بن ابیه', 'نامه ۲۰-به زیاد بن ابیه \nهشدار از خیانت به بیت المال \nهمانا من، براستی به خدا سوگند می‌خورم، اگر به من گزارش کنند که در بیت المال خیانت کردی، کم یا زیاد، چنان بر تو سخت گیرم که کم بهره شده، و در هزینه عیال، درمانی، و خوار و سرگردان شوی. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (255, 2, 21, 'باز هم به زیاد بن ابیه', 'نامه ۲۱-باز هم به زیاد بن ابیه \nسفارش به میانه روی \nای زیاد، از اسراف بپرهیز، و میانه روی را برگزین، از امروز به فکر فردا باش، و از اموال دنیا به اندازه کفاف خویش نگهدار، و زیادی را برای روز نیازمندیت در آخرت پیش فرست. آیا امید داری خداوند پاداش فروتنان را به تو بدهد در حالی که از متکبران باشی؟ و آیا طمع دارای ثواب انفاق کنندگان را دریابی در حالی که در ناز و نعمت قرار داری؟ و تهیدستان و بیوه زنان را از آن نعمتها محروم می‌کنی؟ همانا انسان به آنچه پیش فرستاده، و نزد خدا ذخیره ساخته، پاداش داده خواهد شد با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (256, 2, 22, 'به ابن‌عباس', 'نامه ۲۲-به ابن عباس \nسفارش به آخرت گرایی \nپس از یاد خدا و درود! \nهمانا انسان گاهی خشنود می‌شود به چیزی که هرگز از دستش نمی رفت، و ناراحت می‌شود برای از دست دادن چیزی که هرگز به آن نمی رسید. ابن عباس! خوشحالی تو از چیزی باشد که در آخرت برای تو مفید است، و اندوه تو برای از دست دادن چیزی از آخرت باشد، آنچه از دنیا به دست می‌آوری تو را خوشنود نسازد، و آنچه در دنیا از دست می‌دهی زاری کنان تاسف مخور، همت خویش را به دنیای پس از مرگ واگذار.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (257, 2, 23, 'پس از آنکه ابن‌ملجم وی را ضربت زد', 'نامه ۲۳-پس از آنکه ضربت خورد \nپندهای جاودانه \nسفارش من برای شما آنکه به خدا شرک نورزید، و سنت محمد (ص) را تباه نکنید، این دو ستون دین را برپا دارید، و این دو چراغ را روشن نگهدارید، آنگاه سزاوار هیچ سرزنشی نباشید. من دیروز همراهتان بودم و امروز مایه عبرت شما می‌باشم، و فردا از شما جدا می‌گردم، اگر ماندم خود اختیار خون خویش را دارم، و اگر بمیرم، مرگ وعده گاه من است، اگر عفو کنم، برای من نزدیک شدن به خدا، و برای شما نیکی و حسنه است، پس عفو کنید. (آیا دوست ندارید خدا شما را بیامرزد؟) به خدا سوگند! مرگ ناگهان به من روی نیاورده که از آن خشنود نباشم، و نشانه‌های آن را زشت بدانم، بلکه من چونان جوینده آب در شب که ناگهان آن را بیابد، یا کسی که گمشده خود را پیدا کند، از مرگ خرسندم که: (و آنچه نزد خداست برای نیکان بهتر است.) (شبیه این کلمات در خطبه‌ها گذشت که جهت برخی مطالب تازه آن را آوردیم.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (258, 2, 24, 'وصیت درباره دارایی خود', 'نامه ۲۴-وصیت درباره دارایی خود \nوصیت اقتصادی نسبت به اموال شخصی \nاین دستوری است که بنده خدا علی بن ابیطالب، امیر مومنان نسبت به اموال شخصی خود برای خشنودی خدا، داده است، تا خداوند با آن به بهشتش درآورد، و آسوده اش گرداند. (قسمتی از این نامه است) همانا سرپرستی این اموال بر عهده فرزندم حسن بن علی است، آنگونه که رواست از آن مصرف نماید، و از آن انفاق کند، اگر برای حسن حادثه ای رخ داد و حسین زنده بود، سرپرستی آن را پس از برادرش به عهده گیرد، و کار او را تداوم بخشد. پسران فاطمه از این اموال به همان مقدار سهم دارند که دیگر پسران علی خواهند داشت، من سرپرستی اموالم را به پسران فاطمه واگذارم، تا خشنودی خدا، و نزدیک شدن به رسول الله (ص) و بزرگداشت حرمت او، و احترام پیوند خویشاوندی پیامبر (ص) را فراهم آورم. \nضرورت حفظ اموال \nو با کسی که این اموال در دست اوست شرط می‌کنم که اصل مال را حفظ نموده تنها از میوه و درآمدش بخورند و انفاق کنند، و هرگز نهالهای درخت خرما را نفروشند، تا همه این سرزمین یکپارچه زیر درختان خرما بگونه ای قرار گیرد که راه یافتن در آن دشوار باشد. و زنان غیر عقدی من که با آن‌ها بودم و صاحب فرزند یا حامله می‌باشند، پس از تولد فرزند، فرزند خود را گیرد که او بهره او باشد، و اگر فرزندانش بمیرد، مادر آزاد است، کنیز بودن از او برداشته، و آزادی خویش را باز یابد.\n/ (ودیه) به معنی نهال خرما، و جمع آن (ودی) بر وزن (علی) می‌باشد، و جمله امام نسبت به درختان (حتی تشکل ارضها غراسا) از فصیح ترین سخن است یعنی زمین پردرخت شود که چیزی جز درختان به چشم نیایند) /.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (259, 2, 25, 'یادداشت به مامور جمع‌آوری مالیات', 'نامه ۲۵-به مامور جمع آوری مالیات \nاخلاق اجتماعی کارگزاران اقتصادی \nبا ترس از خدایی که یکتاست و همتایی ندارد، حرکت کن، در سر راه هیچ مسلمانی را نترسان، یا با زور از زمین او نگذر، و افزون تر از حقوق الهی از او مگیر، هرگاه به آبادی رسیدی، در کنار آب فرود آی، و وارد خانه کسی مشو، سپس با آرامش و وقار به سوی آنان حرکت کن، تا در میانشان قرارگیری، به آن‌ها سلام کن، و در سلام و تعارف و مهربانی کوتاهی نکن. سپس می‌گویی: (ای بندگان خدا، مرا ولی خدا و جانشین او به سوی شما فرستاده، تا حق خدا را که در اموال شماست تحویل گیرم، آیا در اموال شما حقی است که به نماینده او بپردازید؟) اگر کسی گفت: نه، دیگر به او مراجعه نکن، و اگر کسی پاسخ داد: آری، همراهش برو، بدون آنکه او را بترسانی، یا تهدید کنی، یا به کار مشکلی وادار سازی، هر چه از طلا و نقره به تو رساند بردار، و اگر دارای گوسفند یا شتر بود، بدون اجازه اش داخل مشو، که اکثر اموال از آن اوست. آنگاه که داخل شدی مانند اشخاص سلطه گر، و سختگیر رفتار نکن، حیوانی را رم مده، و هراسان مکن، و دامدار را مرنجان، حیوانات را به دو دسته تقسیم کن و صاحبش را اجازه ده که خود انتخاب کند، پس از انتخاب اعتراض نکن، سپس باقیمانده را به دو دسته تقسیم کن و صاحبش را اجازه ده که خود انتخاب کند و بر انتخاب او خرده مگیر، به همین گونه رفتار کن تا باقیمانده، حق خداوند باشد. اگر دامدار از این تقسیم و انتخاب پشیمان است، و از تو درخواست گزینش دوباره دارد همراهی کن، پس حیوانات را درهم کن، و به دو دسته تقسیم نما همانند آغاز کار، تا حق خدا را از آن برگیری و در تحویل گرفتن حیوانات، حیوان پیر و دست و پا شکسته، بیمار و معیوب را به عنوان زکات نپذیر، و به فردی که اطمینان نداری، و نسبت به اموال مسلمین دلسوز نیست، مسپار. تا آن را به پیشوای مسلمین رسانده، در میان آن‌ها تقسیم گردد.\nحمایت از حقوق حیوانات \nدر رساندن حیوانات آن را به دست چوپانی که خیرخواه و مهربان، امین و حافظ، که نه سختگیر باشد و نه ستمکار، نه تند براند و نه حیوانات را خسته کند، بسپار، سپس آنچه از بیت المال جمع آوری شد برای ما بفرست، تا در نیازهایی که خدا اجازه فرمود مصرف کنیم، هرگاه حیوانات را به دست فردی امین سپردی، به او سفارش کن تا: (بین شتر و نوزادش جدایی نیفکند، و شیر آن را ندوشد تا به بچه اش زیانی وارد نشود، در سوار شدن بر شتران عدالت را رعایت کند، و مراعات حال شتر خسته یا زخمی را که سواری دادن برای او سخت است بنماید، آن‌ها را در سر راه به درون آب ببرید، و از جاده‌هائی که دو طرف آن علفزار است به جاده بی علف نکشاند و هر چندگاه شتران را مهلت دهد تا استراحت کنند و هرگاه به آب و علفزار رسید، فرصت دهد تا علف بخورند و آب بنوشند.) تا آنگاه که به اذن خدا بر ما وارد شوند، فربه و سرحال، نه خسته و کوفته، که آن‌ها را بر اساس رهنمود قرآن، و سنت پیامبر (ص) تقسیم نماییم، عمل به دستورات یادشده مایه بزرگی پاداش و هدایت تو خواهد شد. انشاءالله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (260, 2, 26, 'به یکی از ماموران گردآوری زکات', 'نامه ۲۶-به یکی از ماموران زکات \nاخلاق کارگزاران مالیاتی \nاو را به ترس از خدا در اسرار پنهانی، و اعمال مخفی سفارش می‌کنم، آنجا که هیچ گواهی غیر از او، و نماینده ای جز خدا نیست، و سفارش می‌کنم که مبادا در ظاهر خدا را اطاعت، و در خلوت نافرمانی کند، و اینکه آشکار و پنهانش، و گفتار و کردارش در تضاد نباشد، امانت الهی را اداء، و عبادت را خالصانه انجام دهد. و به او سفارش می‌کنم با مردم تندخو نباشد، و به آن‌ها دروغ نگوید، و با مردم به جهت اینکه بر آن‌ها حکومت دارد بی اعتنایی نکند، چه اینکه مردم برادران دینی، و یاری دهندگان در استخراج حقوق الهی اند. بدان! برای تو در این زکاتی که جمع می‌کنی سهمی معین، و حقی روشن است، و شریکانی از مستمندان و ضعیفان داری، همانگونه که ما حق تو را می‌دهیم، تو هم باید نسبت به حقوق آنان وفادار باشی، اگر چنین نکنی در روز رستاخیز بیش از همه دشمن داری، و وای بر کسی که در پیشگاه خدا، فقرا و مساکین، و درخواست کنندگان و آنان که از حقشان محرومند، و بدهکاران و ورشکستگان و در راه ماندگان، دشمن او باشند و از او شکایت کنند. \nامانتداری \nکسی که امانت الهی را خوار شمارد، و دست به خیانت آلوده کند، و خود و دین خود را پاک نسازد، درهای خواری را در دنیا به روی خود گشوده، و در قیامت خوارتر و رسواتر خواهد بود، و همانا! بزرگترین خیانت! خیانت به ملت، و رسواترین دغلکاری، دغلبازی با امامان است، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (261, 2, 27, 'به محمد بن ابوبکر', 'نامه ۲۷-به محمد بن ابوبکر \nاخلاق اجتماعی \nبا مردم فروتن باش، نرمخو و مهربان باش، گشاده رو و خندان باش، در نگاه‌هایت، و در نیم نگاه و خیره شدن به مردم به تساوی رفتار کن، تا بزرگان در ستمکاری تو طمع نکنند، و ناتوانها در عدالت تو مایوس نگردند، زیرا خداوند از شما بندگان درباره اعمال کوچک و بزرگ، آشکار و پنهان پرسش می‌کند، اگر کیفر دهد شما استحقاق بیش از آن را دارید، و اگر ببخشد از بزرگواری اوست. \nاعتدال گرایی زاهدان \nآگاه باشید، ای بندگان خدا، پرهیزکاران از دنیای زودگذر به سلامت گذشتند و آخرت جاودانه را گرفتند، با مردم دنیا در دنیاشان شریک گشتند، اما مردم دنیا در آخرت آن‌ها شرکت نکردند، پرهیزکاران در بهترین خانه‌های دنیا سکونت کردند، و بهترین خوراکهای دنیا را خوردند، و همان لذتهایی را چشیدند که دنیاداران چشیده بودند، و از دنیا بهره گرفتند آنگونه که سرکشان و متکبران دنیا بهره مند بودند. سپس از این جهان با زاد و توشه فراوان، و تجارتی پرسود، به سوی آخرت شتافتند، لذت پارسایی در ترک حرام دنیا را چشیدند، و یقین داشتند در روز قیامت از همسایگان خدایند، جایگاهی که هر چه درخواست کنند، داده می‌شود، و هر گونه لذتی در اختیارشان قرار دارد. ضرورت یاد مرگ \nای بندگان خدا! از مرگ و نزدیک بودنش بترسید، و آمادگیهای لازم را برای مرگ فراهم کنید، که مرگ جریانی بزرگ و مشکلی سنگین به همراه خواهد آورد، یا خیری که پس از آن شری وجود نخواهد داشت، و یا شری که هرگز نیکی با آن نخواهد بود، پس چه کسی از عمل کننده برای بهشت، به بهشت نزدیکتر؟ و چه کسی از عمل کننده برای آتش، به آتش نزدیکتر است؟ شما همه شکار آماده مرگ می‌باشید، اگر توقف کنید شما را می‌گیرد، و اگر فرار کنید به شما می‌رسد، مرگ از سایه شما به شما نزدیک تر است، نشانه مرگ بر پیشانی شما زده شد، دنیا پشت سر شما در حال درهم پیچیده شدن است، پس بترسید از آتشی که ژرفای آن زیاد، و حرارتش شدید، و عذابش نو به نو وارد می‌شود، در جایگاهی که رحمت در آن وجود ندارد، و سخن کسی را نمی شنوند، و ناراحتیها در آن پایان ندارد، اگر می‌توانید که ترس از خدا را فراوان، و خوشبینی خود را به خدا نیکو گردانید، چنین کنید، هر دو را جمع کنید، زیرا بنده خدا خوش بینی او به پروردگار باید به اندازه ترسیدن او باشد، و آن کس که به خدا خوش بین است باید بیشتر از همه از کیفر الهی بترسد. \nاخلاق مدیران اجرایی \nای محمد بن ابی بکر! بدان، که من تو را سرپرست بزرگترین لشکرم یعنی لشکر مصر، قرار دادم، بر تو سزاوار است که با خواسته‌های دل مخالفت کنی، و از دین خود دفاع نمایی، هر چند ساعتی از عمر تو باقی نمانده باشد، خدا را در راضی نگهداشتن مردم به خشم نیاور، زیرا خشنودی خدا جایگزین هر چیزی بوده اما هیچ چیز جایگزین خشنودی خدا نمی شود، نماز را در وقت خودش بجای آر، نه اینکه در بیکاری زودتر از وقتش بخوانی، و به هنگام درگیری و کار آن را تاخیر بیاندازی، و بدان که تمام کردار خوبت در گرو نماز است. \n(قسمتی از نامه) امام هدایتگر، و زمامدار گمراهی هیچگاه مساوی نخواهند بود، چنانکه دوستان پیامبر (ص) و دشمنانش برابر نیستند، پیامبر اسلام (ص) به من فرمود: (بر امت اسلام، نه از مومن و نه از مشرک هراسی ندارم، زیرا مومن را ایمانش باز داشته، و مشرک را خداوند به جهت شرک او نابود می‌سازد، من بر شما از مرد منافقی می‌ترسم که درونی دوچهره، و زبانی عالمانه دارد، گفتارش دلپسند و رفتارش ناپسند است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (262, 2, 28, 'در پاسخ معاویه', 'نامه ۲۸-در پاسخ معاویه \nافشای ادعاهای دروغین معاویه پس از یاد خدا و درود! نامه شما رسید، که در آن نوشتید، خداوند محمد (ص) را برای دینش برگزید، و با یارانش او را تایید کرد، راستی روزگار چه چیزهای شگفتی از تو بر ما آشکار کرده است! تو می‌خواهی ما را از آن چه خداوند به ما عنایت فرمود، آگاه کنی؟ و از نعمت وجود پیامبر باخبرمان سازی؟ داستان تو کسی را ماند که خرما به سرزمین پرخرمای (هجر) برد. یا استاد خود را به مسابقه دعوت کند، و پنداشتی که برترین انسانها در اسلام فلان کس، و فلان شخص است؟ چیزی را آورده ای که اگر اثبات شود هیچ ارتباطی به تو ندارد، و اگر دروغ هم باشد به تو مربوط نمی شود، تو را با انسانهای برتر و غیر برتر، سیاستمدار و غیر سیاستمدار چه کار است؟ اسیران آزاده و فرزندشان را چه رسد به امتیازات میان مهاجران نخستین، و ترتیب درجات، و شناسایی منزلت و مقام آنان! هرگز! خود را در چیزی قرار می‌دهی که از آن بیگانه ای، حال کار بدینجا کشید که محکوم حاکم باشد؟ ای مرد چرا بر سر جایت نمی نشینی؟ و کوتاهی کردنهایت را بیاد نمی آوری؟ و به منزلت عقب مانده ات باز نمی گردی؟ برتری ضعیفان، و پیروزی پیروزمندان در اسلام با تو چه ارتباطی دارد؟ تو همواره در بیابان گمراهی سرگردان، و از راه راست روی گردانی. \nفضائل بنی‌هاشم \nآیا نمی بینی؟ (آنچه می‌گویم برای آگاه‌اندن تو نیست، بلکه برای یادآوری نعمتهای خدا می‌گویم)، جمعی از مهاجر و انصار در راه خدا به شهادت رسیدند؟ و هر کدام دارای فضیلتی بودند؟ اما آنگاه که شهید ما (حمزه) شربت شهادت نوشید، او را سیدالشهداء خواندند، و پیامبر (ص) در نماز بر پیکر او بجای پنج تکبیر، هفتاد تکبیر گفت، آیا نمی بینی؟ گروهی که دستشان در جهاد قطع شد، و هر کدام فضیلتی داشتند، اما چون بر یکی از ما ضربتی وارد شد و دستش قطع گردید، طیارش خواندند؟ که با دو بال در آسمان بهشت پرواز می‌کند! و اگر خدا نهی نمی فرمود که مرد خود را بستاید، فضائل فراوانی را برمی شمردم، که دلهای آگاه مومنان آن را شناخته، و گوشهای شنوندگان با آن آشناست. \nفضائل بنی‌هاشم و رسوایی بنی امیه \nمعاویه! دست از این ادعاها بردار، که تیرت به خطا رفته است، همانا ما، دست پرورده و ساخته پروردگار خویشیم، و مردم تربیت شدگان و پرورده‌های مایند، اینکه با شما طرح خویشاوندی ریختم. ما از طائفه شما همسر گرفتیم، و شما از طایفه ما همسر انتخاب کردید، و برابر با شما رفتار کردیم، عزت گذشته، و فضیلت پیشین را از ما باز نمی دارد، شما چگونه با ما برابرید که پیامبر (ص) از ماست، و دروغگوی رسوا از شما، حمزه شیر خدا (اسدالله) از ماست، و ابوسفیان، (اسدالاحلاف) از شما، دو سید جوانان اهل بهشت از ما، و کودکان در آتش افکنده شده از شما، و بهترین زنان جهان از ما، و زن هیزم کش دوزخیان از شما، از ما این همه فضیلتها و از شما آن همه رسواییهاست. اسلام ما را همه شنیده، و شرافت ما را همه دیده اند، و کتاب خدا برای ما فراهم آورد آنچه را به ما نرسید ه که خدای سبحان فرمود: (خویشاوندان، بعضی سزاوارترند بر بعض دیگر در کتاب خدا.) و خدای سبحان فرمود: (شایسته ترین مردم به ابراهیم کسانی هستند که از او پیروی دارند، و این پیامبر و آنان که ایمان آوردند و خدا ولی مومنان است.) پس ما یک بار به خاطر خویشاوندی با پیامبر (ص) و بار دیگر به خاطر اطاعت از خدا، به خلافت سزاوارتریم، و آنگاه که مهاجرین در روز سقیفه با انصار گفتگو و اختلاف داشتند، تنها با ذکر خویشاوندی با پیامبر (ص) بر آنان پیروز گردیدند، اگر این دلیل برتری است پس حق با ماست نه با شما، و اگر دلیل دیگری داشتند ادعای انصار بجای خود باقی است، معاویه تو پنداری که بر تمام خلفا حسد ورزیده ام؟ و بر همه آن‌ها شورانده ام؟ اگر چنین شده باشد جنایتی بر تو نرفته که از تو عذرخواهی کنم. و آن شکوه‌هایی است که ننگ آن دامنگیر تو نیست. \nمظلومیت امام علیه السلام \nو گفته‌ای که مرا چونان شتر مهار کرده به سوی بیعت می‌کشاندند. سوگند به خدا! خواستی نکوهش کنی، اما ستودی، خواستی رسوا سازی که خود را رسوا کرده ای، مسلمان را چه باک که مظلوم واقع شود، مادام که در دین خود تردید نداشته، و در یقین خود شک نکند، این دلیل را آورده ام حتی برای غیر تو که پند گیرند، و آن را کوتاه آوردم به مقداری که از خاطرم گذشت. \nسپس کار مرا با عثمان بیاد آوردی، تو باید پاسخ دهی که از خویشاوندان او می‌باشی، راستی کدام یک از ما دشمنی اش با عثمان بیشتر بود؟ و راه را برای کشندگانش فراهم آورد؟ آن کس که به او یاری رساند، و از او خواست بجایش بنشیند، و به کار مردم رسد؟ یا آنکه از او یاری خواست و دریغ کرد؟ و به انتظار نشست تا مرگش فرارسد؟ نه، هرگز، به خدا سوگند. (خداوند بازدارندگان از جنگ را در میان شما می‌شناسد، و آنان را که برادران خود را به سوی خویش می‌خوانند، و جز لحظه‌های کوتاهی در نبرد حاضر نمی شوند.) من ادعا ندارم که درمورد بدعتهای عثمان، بر او عیب نمی گرفتم، نکوهش می‌کردم و از آن عذرخواه نیستم، اگر گناه من ارشاد و هدایت اوست، بسیارند کسانی که ملامت شوند و بی گناهند. و بسیارند ناصحانی که در پند و اندرز دادن مورد تهمت قرار گیرند. (من قصدی جز اصلاح تا نهایت توانایی خود ندارم، و موفقیت من تنها به لطف خداست، و توفیقات را جز از خدا نمی خواهم، بر او توکل می‌کنم و به سوی او باز می‌گردم.) \nپاسخ به تهدید نظامی \nدر نامه ات نوشته ای که نزد تو برای من و یاران من چیزی جز شمشیر نیست! در اوج گریه انسان را به خنده وامی داری! فرزندان عبدالمطلب را در کجا دیدی که پشت به دشمن کنند؟ و از شمشیر بهراسند؟ پس (کمی صبر کن که هماورد تو به می‌دان آید.) آن را که می‌جویی به زودی تو را پیدا خواهد کرد، و آنچه را که از آن می‌گریزی در نزدیکی خود خواهی یافت، و من در میان سپاهی بزرگ، از مهاجران و انصار و تابعان، به سرعت به سوی تو خواهم آمد، لشکریانی که جمعشان به هم فشرده، و به هنگام حرکت، غبار آسمان را تیره و تار می‌کنند، کسانی که لباس شهادت بر تن، و ملاقات دوست داشتنی آنان ملاقات با پروردگار است، همراه آنان فرزندانی از دلاوران بدر، و شمشیرهای‌هاشمیان که خوب می‌دانی لبه تیز آن بر پیکر برادر و دایی و جد و خاندانت چه کرد، می‌آیند (و آن عذاب از ستمگران چندان دور نیست.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (263, 2, 29, 'به مردم بصره', 'نامه ۲۹-به مردم بصره \nهشدار به مردم بصره \nشما از پیمان شکستن، و دشمنی آشکارا با من آگاهید، با این همه جرم شما را عفو کردم، و شمشیر از فراریان برداشتم، و استقبال کنندگان را پذیرفتم، و از گناه شما چشم پوشیدم، اگر هم اکنون کارهای ناروا، و اندیشه‌های نابخردانه، شما را به مخالفت و دشمنی با من بکشاند، سپاه من آماده، و پا در رکابند. و اگر مرا به حرکت دوباره مجبور کنید، حمله ای بر شما روا دارم که جنگ جمل در برابر آن بسیار کوچک باشد، با اینکه به ارزشهای فرمانبردارانتان آگاهم، و حق نصیحت کنندگان شما را می‌شناسم، و هرگز برای شخص متهمی، به انسان نیکوکاری تجاوز روا نمی دارم، و هرگز پیمان وفاداران را نخواهم شکست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (264, 2, 3, 'به شریح قاضی', 'نامه ۳- به شریح قاضی \nبرخورد قاطعانه باخیانت کارگزاران \nبه من خبر دادند که خانه ای به هشتاد دینار خریده ای، و سندی برای آن نوشته ای، و گواهانی آن را امضا کرده اند. (شریح گفت: آری ای امیر مومنان امام (ع) نگاه خشم آلودی به او کرد و فرمود) ای شریح! به زودی کسی به سراغت می‌آید که به نوشته ات نگاه نمی کند، و از گواهانت نمی پرسد، تا تو را از آن خانه بیرون کرده و تنها به قبر بسپارد. ای شریح! اندیشه کن که آن خانه را با مال دیگران یا با پول حرام نخریده باشی، که آنگاه خانه دنیا و آخرت را از دست داده ای. اما اگر هنگام خرید خانه، نزد من آمده بودی، برای تو سندی می‌نوشتم که دیگر برای خرید آن به درهمی یا بیشتر، رغبت نمی کردی و آن سند را چنین می‌نوشتم: \nهشدار از بی اعتباری دنیای حرام \nاین خانه ای است که بنده ای خوارشده، و مرده ای آماده کوچ کردن، آن را خریده، خانه ای از سرای غرور، که در محله نابودشوندگان، و کوچه هلاک شدگان قرار دارد، این خانه به چهار جهت منتهی می‌گردد. یک سوی آن به آفتها و بلاها، سوی دوم آن به مصیبتها، و سوی سوم به هوا و هوسهای سست کننده، و سوی چهارم آن به شیطان گمراه کننده ختم می‌شود، و در خانه به روی شیطان گشوده است. این خانه را فریب خورده آزمند، از کسی که خود به زودی از جهان رخت برمی بندد، به مبلغی که او را از عزت و قناعت خارج و به خواری و دنیاپرستی کشانده، خریداری نموده است. هرگونه نقصی در این معامله باشد، بر عهده پروردگاری است که اجساد پادشاهان را پوسانده، و جان جباران را گرفته، و سلطنت فرعونها چون (کسری) و (قیصر) و (تبع) و (حمیر) را نابود کرده است. \nعبرت از گذشتگان \nو آنان که مال فراوان گرد آوردند، و بر آن افزودند، و آنان که قصرها ساختند، و محکم کاری کردند، طلاکاری نمودند، و زینت دادند، فراوان اندوختند، و نگهداری کردند، و به گمان خود برای فرزندان خود گذاشتند، اما همگی آنان به پای حسابرسی الهی، و جایگاه پاداش و کیفر رانده می‌شوند، آنگاه که فرمان داوری و قضاوت نهایی صادر شود (پس تبهکاران زیان خواهند دید.) به این واقعیتها عقل گواهی می‌دهد هرگاه که از اسارت هوای نفس نجات یافته، و از دنیاپرستی به سلامت بگذرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (265, 2, 30, 'به معاویه', 'نامه ۳۰-به معاویه \nپند و هشدار به معاویه \nنسبت به آنچه در اختیار داری از خدا بترس، و در حقوق الهی که بر تو واجب است اندیشه کن، و به شناخت چیزی همت کن که در ناآگاهی آن معذور نخواهی بود، همانا اطاعت خدا، نشانه‌های آشکار، و راه‌های روشن. و راهی میانه و همیشه گشوده، و پایانی دلپسند دارد که زیرکان به آن راه یابند، و فاسدان از آن به انحراف روند، کسی که از دین سر باز زند، از حق رویگردان شده، و در وادی حیرت سرگردان خواهد شد، که خدا نعمت خود را از او گرفته، و بلاهایش را بر او نازل می‌کند، معاویه اینک به خود آی، و به خود بپرداز! زیرا خداوند راه و سرانجام امور تو را روشن کرده است. اما تو همچنان به سوی زیانکاری، و جایگاه کفرورزی، حرکت می‌کنی، خواسته‌های دل تو را به بدیها کشانده، و در پرتگاه گمراهی قرار داده است، و تو را در هلاکت انداخته، و راه‌های نجات را بر روی تو بسته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (266, 2, 31, 'به حضرت مجتبی', 'نامه ۳۱-به حضرت مجتبی \n۱- انسان و حوادث روزگار \nاز پدری فانی، اعتراف دارنده به گذشت زمان، زندگی را پشت سر نهاده- که در سپری شدن دنیا چاره‌ای ندارد- مسکن گزیده در جایگاه گذشتگان، و کوچ کننده فردا، به فرزندی آزمند چیزی که به دست نمی‌آید، رونده راهی که به نیستی ختم می‌شود، در دنیا هدف بیماری‌ها، در گرو روزگار، و در تیررس مصائب، گرفتار دنیا، سودا کننده دنیای فریب کار، وام دار نابودی‌ها، اسیر مرگ، هم سوگند رنجها، هم نشین اندوه‌ها، آماج بلاها، به خاک در افتاده خواهش‌ها، و جانشین گذشتگان است.\n پس از ستایش پروردگار،\n همانا گذشت عمر، و چیرگی روزگار، و روی آوردن آخرت، مرا از یاد غیر خودم باز داشته و تمام توجه مرا به آخرت کشانده است، که به خویشتن فکر می‌کنم و از غیر خودم روی گردان شدم، که نظرم را از دیگران گرفت، و از پیروی خواهشها باز گرداند، و حقیقت کار مرا نمایاند، و مرا به راهی کشاند که شوخی بر نمی‌دارد، و به حقیقتی رساند که دروغی در آن راه ندارد. و تو را دیدم که پاره تن من، بلکه همه جان منی، آنگونه که اگر آسیبی به تو رسد به من رسیده است، و اگر مرگ به سراغ تو آید، زندگی مرا گرفته است، پس کار تو را کار خود شمردم، و نامه‌ای برای تو نوشتم، تا تو را در سختی‌های زندگی رهنمون باشد. حال من زنده باشم یا نباشم. \n۲- مراحل خودسازی \nپسرم همانا تو را به ترس از خدا سفارش می‌کنم که پیوسته در فرمان او باشی، و دلت را با یاد خدا زنده کنی، و به ریسمان او چنگ زنی، چه وسیله‌ای مطمئن‌تر از رابطه تو با خداست اگر سر رشته آن را در دست گیری. دلت را با اندرز نیکو زنده کن، هوای نفس را با بی اعتنایی به حرام بمیران، جان را با یقین نیرومند کن، و با نور حکمت روشنائی بخش، و با یاد مرگ آرام کن، به نابودی از او اعتراف گیر، و با بررسی تحولات ناگوار دنیا به او آگاهی بخش، و از دگرگونی روزگار، و زشتی‌های گردش شب و روز او را بترسان، تاریخ گذشتگان را بر او بنما، و آنچه که بر سر پیشینیان آمده است به یادش آور. در دیار و آثار ویران رفتگان گردش کن، و بیندیش که آن‌ها چه کردند از کجا کوچ کرده، و در کجا فرود آمدند از جمع دوستان جدا شده و به دیار غربت سفر کردند، گویا زمانی نمی‌گذرد که تو هم یکی از آنانی پس جایگاه آینده را آباد کن، آخرت را به دنیا مفروش، و آنچه نمی‌دانی مگو، و آنچه بر تو لازم نیست بر زبان نیاور، و در جاده‌ای که از گمراهی آن می‌ترسی قدم مگذار، زیرا خودداری به هنگام سرگردانی و گمراهی، بهتر از سقوط در تباهی‌هاست. \n۳- اخلاق اجتماعی \nبه نیکی‌ها امر کن و خود نیکوکار باش، و با دست و زبان بدیها را انکار کن، و بکوش تا از بدکاران دور باشی، و در راه خدا آنگونه که شایسته است تلاش کن، و هرگز سرزنش ملامتگران تو را از تلاش در راه خدا باز ندارد. برای حق در مشکلات و سختی‌ها شنا کن، شناخت خود را در دین به کمال رسان، خود را برای استقامت برابر مشکلات عادت ده، که شکیبایی در راه حق عادتی پسندیده است، در تمام کارها خود را به خدا واگذار، که به پناهگاه مطمئن و نیرومندی رسیده‌ای، در دعا با اخلاص پروردگارت را بخوان، که بخشیدن و محروم کردن به دست اوست، و فراوان از خدا درخواست خیر و نیکی داشته باش. وصیت مرا بدرستی دریاب، و به سادگی از آن نگذر، زیرا بهترین سخن آن است که سودمند باشد، بدان علمی که سودمند نباشد، فایده‌ای نخواهد داشت، و دانشی که سزاوار یاد گیری نیست سودی ندارد. \n۴- شتاب در تربیت فرزند \nپسرم هنگامی که دیدم سالیانی از من گذشت، و توانایی رو به کاستی رفت، به نوشتن وصیت برای تو شتاب کردم، و ارزش‌های اخلاقی را برای تو بر شمردم. پیش از آن که أجل فرا رسد، و رازهای درونم را به تو منتقل نکرده باشم، و در نظرم کاهشی پدید آید چنانکه در جسمم پدید آمد، و پیش از آن که خواهشها و دگرگونی‌های دنیا به تو هجوم آورند، و پذیرش و اطاعت مشکل گردد، زیرا قلب نوجوان چونان زمین کاشته نشده، آماده پذیرش هر بذری است که در آن پاشیده شود. پس در تربیت تو شتاب کردم، پیش از آن که دل تو سخت شود، و عقل تو به چیز دیگری مشغول گردد، تا به استقبال کارهایی بروی که صاحبان تجربه، زحمت آزمون آن را کشیده‌اند، و تو را از تلاش و یافتن بی‌نیاز ساخته‌اند، و آنچه از تجربیات آن‌ها نصیب ما شد، به تو هم رسیده، و برخی از تجربیاتی که بر ما پنهان مانده بود برای شما روشن گردد. پسرم درست است که من به اندازه پیشینیان عمر نکرده‌ام، اما در کردار آن‌ها نظر افکندم، و در اخبارشان اندیشیدم، و در آثارشان سیر کردم تا آنجا که گویا یکی از آنان شده‌ام، بلکه با مطالعه تاریخ آنان، گویا از اول تا پایان عمرشان با آنان بوده‌ام، پس قسمت‌های روشن و شیرین زندگی آنان را از دوران تیرگی شناختم، و زندگانی سودمند آنان را با دوران زیانبارش شناسایی کردم، سپس از هر چیزی مهم و ارزشمند آن را، و از هر حادثه‌ای، زیبا و شیرین آن را برای تو برگزیدم، و ناشناخته‌های آنان را دور کردم، پس آنگونه که پدری مهربان نیکی‌ها را برای فرزندش می‌پسندد، من نیز بر آن شدم تو را با خوبی‌ها تربیت کنم، زیرا در آغاز زندگی قرار داری، تازه به روزگار روی آورده‌ای، نیتی سالم و روحی با صفا داری. \n۵- روش تربیت فرزند \nپس در آغاز تربیت، تصمیم گرفتم تا کتاب خدای توانا و بزرگ را همراه با تفسیر آیات، به تو بیاموزم، و شریعت اسلام و احکام آن از حلال و حرام، به تو تعلیم دهم و به چیز دیگری نپردازم. اما از آن ترسیدم که مبادا رأی و هوایی که مردم را دچار اختلاف کرد، و کار را بر آنان شبهه ناک ساخت، به تو نیز هجوم آورد، گر چه آگاه کردن تو را نسبت به این امور خوش نداشتم، اما آگاه شدن و استوار ماندنت را ترجیح دادم، تا تسلیم هلاکت‌های اجتماعی نگردی، و امیدوارم خداوند تو را در رستگاری پیروز گرداند، و به راه راست هدایت فرماید، بنا بر این وصیت خود را اینگونه تنظیم کرده‌ام. پسرم بدان آنچه بیشتر از به کار گیری وصیتم دوست دارم ترس از خدا، و انجام واجبات، و پیمودن راهی است که پدرانت، و صالحان خاندانت پیموده‌اند. زیرا آنان آنگونه که تو در امور خویشتن نظر می‌کنی در امور خویش نظر داشتند. همانگونه که تو درباره خویشتن می‌اندیشی، نسبت به خودشان می‌اندیشیدند، و تلاش آنان در این بود که آنچه را شناختند انتخاب کنند، و بر آنچه تکلیف ندارند روی گردانند، و اگر نفس تو از پذیرفتن سرباز زند و خواهد چنانکه آنان دانستند بداند، پس تلاش کن تا درخواست‌های تو از روی درک و آگاهی باشد، نه آن که به شبهات روی آوری و از دشمنی‌ها کمک گیری. و قبل از پیمودن راه پاکان، از خداوند یاری بجوی، و در راه او با اشتیاق عمل کن تا پیروز شوی، و از هر کاری که تو را به شک و تردید اندازد، یا تسلیم گمراهی کند بپرهیز. و چون یقین کردی دلت روشن و فروتن شد، و اندیشه‌ات گرد آمد و کامل گردید، و اراده‌ات به یک چیز متمرکز گشت، پس اندیشه کن در آنچه که برای تو تفسیر می‌کنم، اگر در این راه آنچه را دوست می‌داری فراهم نشد، و آسودگی فکر و اندیشه نیافتنی، بدان که راهی را که ایمن نیستی می‌پیمایی، و در تاریکی ره می‌سپاری، زیرا طالب دین نه اشتباه می‌کند، و نه در تردید و سرگردانی است، که در چنین حالتی خود داری بهتر است. \n۶- ضرورت توجه به معنویات \nپسرم در وصیت من درست بیندیش، بدان که در اختیار دارنده مرگ همان است که زندگی در دست او، و پدید آورنده موجودات است، همو می‌میراند، و نابود کننده همان است که دوباره زنده می‌کند، و آن که بیمار می‌کند شفا نیز می‌دهد، بدان که دنیا جاودانه نیست، و آنگونه که خدا خواسته است برقرار است، از عطا کردن نعمت‌ها، و انواع آزمایش‌ها، و پاداش دادن در معاد، و یا آنچه را که او خواسته است و تو نمی‌دانی. اگر در باره جهان، و تحولات روزگار مشکلی برای تو پدید آمد آن را به عدم آگاهی ارتباط ده، زیرا تو ابتدا با ناآگاهی متولد شدی و سپس علوم را فرا گرفتی، و چه بسیار است آنچه را که نمی‌دانی و خدا می‌داند، که اندیشه‌ات سرگردان، و بینش تو در آن راه ندارد، سپس آن‌ها را می‌شناسی. پس به قدرتی پناه بر که تو را آفریده، روزی داده، و اعتدال در اندام تو آورده است، بندگی تو فقط برای او باشد، و تنها اشتیاق او را داشته باش، و تنها از او بترس. بدان پسرم هیچ کس چون رسول خدا صلی الله علیه و آله و سلم از خدا آگاهی نداده است، رهبری او را پذیرا باش، و برای رستگاری، راهنمایی او را بپذیر، همانا من از هیچ اندرزی برای تو کوتاهی نکردم، و تو هر قدر کوشش کنی، و به اصلاح خویش بیندیشی، همانند پدرت نمی‌توانی باشی. پسرم اگر خدا شریکی داشت، پیامبران او نیز به سوی تو می‌آمدند، و آثار قدرتش را می‌دیدی، و کردار و صفاتش را می‌شناختی، اما خدا، خدایی است یگانه، همانگونه که خود توصیف کرد، هیچ کس در مملکت داری او نزاعی ندارد، نابود شدنی نیست، و همواره بوده است، اول هر چیزی است که آغاز ندارد، و آخر هر چیزی که پایان نخواهد داشت، برتر از آن است که قدرت پروردگاری او را فکر و اندیشه درک کند. حال که این حقیقت را دریافتی، در عمل بکوش آن چنانکه همانند تو سزاوار است بکوشد، که منزلت آن اندک، و توانایی‌اش ضعیف، و ناتوانی‌اش بسیار، و اطاعت خدا را مشتاق، و از عذابش ترسان، و از خشم او گریزان است، زیرا خدا تو را جز به نیکوکاری فرمان نداده، و جز از زشتی‌ها نهی نفرموده است. \n۷- ضرورت آخرت گرایی \nای پسرم من تو را از دنیا و تحولات گوناگونش، و نابودی و دست به دست گردیدنش آگاه کردم، و از آخرت و آنچه برای انسان‌ها در آنجا فراهم است اطلاع دادم، و برای هر دو مثال‌ها زدم، تا پند پذیری، و راه و رسم زندگی بیاموزی، همانا داستان آن کس که دنیا را آزمود، چونان مسافرانی است که در سر منزلی بی آب و علف و دشوار اقامت دارند و قصد کوچ کردن به سرزمینی را دارند که در آنجا آسایش و رفاه فراهم است. پس مشکلات راه را تحمل می‌کنند، و جدایی دوستان را می‌پذیرند، و سختی سفر، و ناگواری غذا را با جان و دل قبول می‌کنند، تا به جایگاه وسیع، و منزلگاه أمن، با آرامش قدم بگذارند، و از تمام سختی‌های طول سفر احساس ناراحتی ندارند، و هزینه‌های مصرف شده را غرامت نمی‌شمارند، و هیچ چیز برای آنان دوست داشتنی نیست جز آن که به منزل أمن، و محل آرامش برسند. اما داستان دنیا پرستان همانند گروهی است که از جایگاهی پر از نعمت‌ها می‌خواهند به سرزمین خشک و بی آب و علف کوچ کنند، پس در نظر آنان چیزی ناراحت کننده‌تر از این نیست که از جایگاه خود جدا می‌شوند، و ناراحتی‌ها را باید تحمل کنند. \n۸- معیارهای روابط اجتماعی \nای پسرم نفس خود را میزان میان خود و دیگران قرار ده، پس آنچه را که برای خود دوست داری برای دیگران نیز دوست بدار، و آنچه را که برای خود نمی‌پسندی، برای دیگران مپسند، ستم روا مدار، آنگونه که دوست نداری به تو ستم شود، نیکوکار باش، آنگونه که دوست داری به تو نیکی کنند، و آنچه را که برای دیگران زشت می‌داری برای خود نیز زشت بشمار، و چیزی را برای مردم رضایت بده که برای خود می‌پسندی، آنچه نمی‌دانی نگو، گر چه آنچه را می‌دانی اندک است، آنچه را دوست نداری به تو نسبت دهند، در باره دیگران مگو، بدان که خود بزرگ بینی و غرور، مخالف راستی، و آفت عقل است، نهایت کوشش را در زندگی داشته باش، و در فکر ذخیره سازی برای دیگران مباش، آنگاه که به راه راست هدایت شدی، در برابر پروردگارت از هر فروتنی خاضع‌تر باش. \n۹- تلاش در جمع آوری زاد و توشه \nبدان راهی پر مشقت و بس طولانی در پیش روی داری، و در این راه بدون کوشش بایسته، و تلاش فراوان، و اندازه گیری زاد و توشه، و سبک کردن بار گناه، موفق نخواهی بود، بیش از تحمل خود بار مسئولیت‌ها بر دوش منه، که سنگینی آن برای تو عذاب آور است. اگر مستمندی را دیدی که توشه‌ات را تا قیامت می‌برد، و فردا که به آن نیاز داری به تو باز می‌گرداند، کمک او را غنیمت بشمار، و زاد و توشه را بر دوش او بگذار، و اگر قدرت مالی داری بیشتر انفاق کن، و همراه او بفرست، زیرا ممکن است روزی در رستاخیز در جستجوی چنین فردی باشی و او را نیابی. به هنگام بی‌نیازی، اگر کسی از تو وام خواهد، غنیمت بشمار، تا در روز سختی و تنگدستی به تو باز گرداند، بدان که در پیش روی تو، گردنه‌های صعب العبوری وجود دارد، که حال سبکباران به مراتب بهتر از سنگین باران است، و آن که کند رود حالش بدتر از شتاب گیرنده می‌باشد، و سرانجام حرکت، بهشت و یا دوزخ خواهد بود، پس برای خویش قبل از رسیدن به آخرت وسائلی مهیا ساز، و جایگاه خود را پیش از آمدنت آماده کن، زیرا پس از مرگ، عذری پذیرفته نمی‌شود، و راه باز گشتی وجود ندارد. \n۱۰- نشانه‌های رحمت الهی \nبدان، خدایی که گنج‌های آسمان و زمین در دست اوست، به تو اجازه درخواست داده، و اجابت آن را به عهده گرفته است. تو را فرمان داده که از او بخواهی تا عطا کند، درخواست رحمت کنی تا ببخشاید، و خداوند بین تو و خودش کسی را قرار نداده تا حجاب و فاصله ایجاد کند، و تو را مجبور نساخته که به شفیع و واسطه‌ای پناه ببری، و در صورت ارتکاب گناه در توبه را مسدود نکرده است، در کیفر تو شتاب نداشته، و در توبه و بازگشت، بر تو عیب نگرفته است، در آنجا که رسوایی سزاوار توست، رسوا نساخته، و برای بازگشت به خویش شرائط سنگینی مطرح نکرده است، در گناهان تو را به محاکمه نکشیده، و از رحمت خویش نا امیدت نکرده، بلکه بازگشت تو را از گناهان نیکی شمرده است. هر گناه تو را یکی، و هر نیکی تو را ده به حساب آورده، و راه بازگشت و توبه را به روی تو گشوده است. هر گاه او را بخوانی، ندایت را می‌شنود، و چون با او راز دل گویی راز تو را می‌داند، پس حاجت خود را با او بگوی، و آنچه در دل داری نزد او باز گوی، غم و اندوه خود را در پیشگاه او مطرح کن، تا غم‌های تو را بر طرف کند و در مشکلات تو را یاری رساند. \n۱۱- شرائط اجابت دعا \nو از گنجینه‌های رحمت او چیزهایی را درخواست کن که جز او کسی نمی‌تواند عطا کند، مانند عمر بیشتر، تندرستی بدن، و گشایش در روزی. سپس خداوند کلیدهای گنجینه‌های خود را در دست تو قرار داده که به تو اجازه دعا کردن داد، پس هر گاه اراده کردی می‌توانی با دعا، درهای نعمت خدا را بگشایی، تا باران رحمت الهی بر تو ببارد. هرگز از تأخیر اجابت دعا نا امید مباش، زیرا بخشش الهی باندازه نیت است، گاه، در اجابت دعا تأخیر می‌شود تا پاداش درخواست کننده بیشتر و جزای آرزومند کامل‌تر شود، گاهی درخواست می‌کنی اما پاسخ داده نمی‌شود، زیرا بهتر از آنچه خواستی به زودی یا در وقت مشخص، به تو خواهد بخشید، یا به جهت اعطاء بهتر از آنچه خواستی، دعا به اجابت نمی‌رسد، زیرا چه بسا خواسته‌هایی داری که اگر داده شود مایه هلاکت دین تو خواهد بود، پس خواسته‌های تو به گونه‌ای باشد که جمال و زیبایی تو را تأمین، و رنج و سختی را از تو دور کند، پس نه مال دنیا برای تو پایدار، و نه تو برای مال دنیا باقی خواهی ماند. \n۱۲- ضرورت یاد مرگ \nپسرم، بدان تو برای آخرت آفریده شدی، نه دنیا، برای رفتن از دنیا، نه پایدار ماندن در آن، برای مرگ، نه زندگی جاودانه در دنیا، که هر لحظه ممکن است از دنیا کوچ کنی، و به آخرت در آیی. و تو شکار مرگی هستی که فرار کننده آن نجاتی ندارد، و هر که را بجوید به آن می‌رسد، و سرانجام او را می‌گیرد. پس، از مرگ بترس نکند زمانی سراغ تو را گیرد که در حال گناه یا در انتظار توبه کردن باشی و مرگ مهلت ندهد و بین تو و توبه فاصله اندازد، که در این حال خود را تباه کرده‌ای. پسرم فراوان بیاد مرگ باش، و به یاد آنچه که به سوی آن می‌روی، و پس از مرگ در آن قرار می‌گیری. تا هنگام ملاقات با مرگ از هر نظر آماده باش، نیروی خود را افزون، و کمر همت را بسته نگهدار که ناگهان نیاید و تو را مغلوب سازد. مبادا دلبستگی فراوان دنیا پرستان، و تهاجم حریصانه آنان به دنیا، تو را مغرور کند، چرا که خداوند تو را از حالات دنیا آگاه کرده، و دنیا نیز از وضع خود تو را خبر داده، و از زشتی‌های روزگار پرده برداشته است. \n۱۳- شناخت دنیا پرستان \nهمانا دنیا پرستان چونان سگ‌های درنده، عو عو کنان، برای دریدن صید در شتابند، برخی به برخی دیگر هجوم آورند، و نیرومندشان، ناتوان را می‌خورد، و بزرگ‌ترها کوچک‌ترها را. و یا چونان شترانی هستند که برخی از آن‌ها پای بسته، و برخی دیگر در بیابان رها شده، که راه گم کرده و در جاده‌های نامعلومی در حرکتند، و در وادی پر از آفت‌ها، و در شنزاری که حرکت با کندی صورت می‌گیرد گرفتارند، نه چوپانی دارند که به کارشان برسد، و نه چراننده‌ای که به چراگاهشان ببرد. دنیا آن‌ها را به راه کوری کشاند. و دیدگانشان را از چراغ هدایت بپوشاند، در بیراهه سرگردان، و در نعمت‌ها غرق شده‌اند، که نعمت‌ها را پروردگار خود قرار دادند. هم دنیا آن‌ها را به بازی گرفته، و هم آن‌ها با دنیا به بازی پرداخته، و آخرت را فراموش کرده‌اند. اندکی مهلت ده، بزودی تاریکی بر طرف می‌شود، گویا مسافران به منزل رسیده‌اند، و آن کس که شتاب کند به کاروان خواهد رسید. پسرم بدان آن کس که مرکبش شب و روز آماده است همواره در حرکت خواهد بود، هر چند خود را ساکن پندارد، و همواره راه می‌پیماید هر چند در جای خود ایستاده و راحت باشد. \n۱۴- ضرورت واقع نگری در زندگی (ارزش‌های گوناگون اخلاقی) \nبه یقین بدان که تو به همه آرزوهای خود نخواهی رسید، و تا زمان مرگ بیشتر زندگی نخواهی کرد، و بر راه کسی می‌روی که پیش از تو می‌رفت، پس در به دست آوردن دنیا آرام باش، و در مصرف آنچه به دست آوردی نیکو عمل کن، زیرا چه بسا تلاش بی اندازه برای دنیا که به تاراج رفتن اموال کشانده شد. پس هر تلاشگری به روزی دلخواه نخواهد رسید، و هر مدارا کننده‌ای محروم نخواهد شد. نفس خود را از هر گونه پستی باز دار، هر چند تو را به اهدافت رساند، زیرا نمی‌توانی به اندازه آبرویی که از دست می‌دهی بهایی به دست آوری. برده دیگری مباش «»، که خدا تو را آزاد آفرید، آن نیک که جز با شر به دست نیاید نیکی نیست، و آن راحتی که با سختی‌های فراوان به دست آید، آسایش نخواهد بود. بپرهیز از آن که مرکب طمع ورزی تو را به سوی هلاکت به پیش راند، و اگر توانستی که بین تو و خدا صاحب نعمتی قرار نگیرد، چنین باش، زیرا تو، روزی خود را دریافت می‌کنی، و سهم خود بر می‌داری، و مقدار اندکی که از طرف خدای سبحان به دست می‌آوری، بزرگ و گرامی‌تر از (مال) فراوانی است که از دست بندگان دریافت می‌داری، گرچه همه از طرف خداست. آنچه با سکوت از دست می‌دهی آسانتر از آن است که با سخن از دست برود، چرا که نگهداری آنچه در مشک است با محکم بستن دهانه آن امکان پذیر است، و نگهداری آنچه که در دست داری، پیش من بهتر است از آن که چیزی از دیگران بخواهی، و تلخی نا امیدی بهتر از درخواست کردن از مردم است. شغل همراه با پاکدامنی، بهتر از ثروت فراوانی است که با گناهان به دست آید، مرد برای پنهان نگاه داشتن اسرار خویش سزاوارتر است، چه بسا تلاش کننده‌ای که به زیان خود می‌کوشد، هر کس پر حرفی کند یاوه می‌گوید، و آن کس که بیندیشد آگاهی یابد، با نیکان نزدیک شو و از آنان باش، و با بدان دور شو و از آنان دوری کن. بدترین غذاها، لقمه حرام، و بدترین ستم‌ها، ستمکاری به ناتوان است. جایی که مدارا کردن درشتی به حساب آید به جای مدارا درشتی کن، چه بسا که دارو بر درد افزاید، و بیماری، درمان باشد، و چه بسا آن کس که اهل اندرز نیست، اندرز دهد، و نصیحت کننده دغل کار باشد. هرگز بر آرزوها تکیه نکن که سرمایه احمقان است، و حفظ عقل، پند گرفتن از تجربه‌هاست، و بهترین تجربه آن که تو را پند آموزد. پیش از آن که فرصت از دست برود، و اندوه ببار آورد، از فرصت‌ها استفاده کن. هر تلاشگری به خواسته‌های خود نرسد، و هر پنهان شده‌ای باز نمی‌گردد. از نمونه‌های تباهی، نابود کردن زاد و توشه آخرت است. هر کاری پایانی دارد، و به زودی آنچه برای تو مقدر گردیده خواهد رسید. هر بازرگانی خویش را به مخاطره افکند. چه بسا اندکی که از فراوانی بهتر است نه در یاری دادن انسان پست چیزی وجود دارد و نه در دوستی با دوست متهم، حال که روزگار در اختیار تو است آسان گیر، و برای آن که بیشتر به دست آوری خطر نکن. از سوار شدن بر مرکب ستیزه جویی بپرهیز. \n۱۵- حقوق دوستان \nچون برادرت از تو جدا گردد، تو پیوند دوستی را بر قرار کن، اگر روی برگرداند تو مهربانی کن، و چون بخل ورزد تو بخشنده باش، هنگامی که دوری می‌گزیند تو نزدیک شو، و چون سخت می‌گیرد تو آسان گیر، و به هنگام گناهش عذر او بپذیر، چنان که گویا بنده او می‌باشی، و او صاحب نعمت تو می‌باشد. مبادا دستورات یاد شده را با غیر دوستانت انجام دهی، یا با انسان‌هایی که سزاوار آن نیستند بجا آوری، دشمن دوست خود را دوست مگیر تا با دوست دشمنی نکنی. در پند دادن دوست بکوش، خوب باشد یا بد، و خشم را فرو خور که من جرعه‌ای شیرین‌تر از آن ننوشیدم، و پایانی گواراتر از آن ندیده‌ام. با آن کس که با تو درشتی کرده، نرم باش که امید است به زودی در برابر تو نرم شود، با دشمن خود با بخشش رفتار کن، زیرا سرانجام شیرین دو پیروزی است (انتقام گرفتن یا بخشیدن) اگر خواستی از برادرت جدا شوی، جایی برای دوستی باقی گذار، تا اگر روزی خواست به سوی تو باز گردد بتواند، کسی که به تو گمان نیک برد او را تصدیق کن، و هرگز حق برادرت را به اعتماد دوستی که با او داری ضایع نکن، زیرا آن کس که حقش را ضایع می‌کنی با تو برادر نخواهد بود، و افراد خانواده‌ات بد بخت‌ترین مردم نسبت به تو نباشند، و به کسی که به تو علاقه‌ای ندارد دل مبند، مبادا برادرت برای قطع پیوند دوستی، دلیلی محکم‌تر از برقراری پیوند با تو داشته باشد، و یا در بدی کردن، بهانه‌ای قوی‌تر از نیکی کردن تو بیاورد، ستمکاری کسی که بر تو ستم می‌کند در دیده‌ات بزرگ جلوه نکند، چه او به زیان خود، و سود تو کوشش دارد، و سزای آن کس که تو را شاد می‌کند بدی کردن نیست. \n۱۶- ارزشهای اخلاقی \nپسرم بدان که روزی دو قسم است، یکی آن که تو آن را می‌جویی، و دیگر آن که او تو را می‌جوید، و اگر تو به سوی آن نروی، خود به سوی تو خواهد آمد، چه زشت است فروتنی به هنگام نیاز، و ستمکاری به هنگام بی‌نیازی همانا سهم تو از دنیا آن اندازه خواهد بود که با آن سرای آخرت را اصلاح کنی، اگر برای چیزی که از دست دادی ناراحت می‌شوی، پس برای هر چیزی که به دست تو نرسیده نیز نگران باش. با آنچه در گذشته دیده یا شنیده‌ای، برای آنچه که هنوز نیامده، استدلال کن، زیرا تحولات و امور زندگی همانند یکدیگرند، از کسانی مباش که اندرز سودشان ندهد، مگر با آزردن فراوان، زیرا عاقل با اندرز و آداب پند گیرد، و حیوانات با زدن. غم و اندوه را با نیروی صبر و نیکویی یقین از خود دور ساز. کسی که میانه روی را ترک کند از راه حق منحرف می‌گردد، یار و همنشین، چونان خویشاوند است. دوست آن است که در نهان آیین دوستی را رعایت کند. هوا پرستی همانند کوری است. چه بسا دور که از نزدیک نزدیک‌تر، و چه بسا نزدیک که از دور دورتر است، انسان تنها، کسی است که دوستی ندارد، کسی که از حق تجاوز کند، زندگی بر او تنگ می‌گردد، هر کس قدر و منزلت خویش بداند حرمتش باقی است، استوارترین وسیله‌ای که می‌توانی به آن چنگ زنی، رشته‌ای است که بین تو و خدای تو قرار دارد. کسی که به کار تو اهتمام نمی‌ورزد دشمن توست. گاهی نا امیدی، خود رسیدن به هدف است، آنجا که طمع ورزی هلاکت باشد. چنان نیست که هر عیبی آشکار، و هر فرصتی دست یافتنی باشد، چه بسا که بینا به خطا می‌رود و کور به مقصد رسد. بدی‌ها را به تأخیر انداز، زیرا هر وقت بخواهی می‌توانی انجام دهی. بریدن با جاهل، پیوستن به عاقل است، کسی که از نیرنگ‌بازی روزگار ایمن باشد به او خیانت خواهد کرد، و کسی که روزگار فانی را بزرگ بشمارد، او را خوار خواهد کرد. چنین نیست که هر تیر اندازی به هدف بزند، هر گاه اندیشه سلطان تغییر کند، زمانه دگرگون شود. پیش از حرکت، از همسفر بپرس، و پیش از خریدن منزل همسایه را بشناس. از سخنان بی ارزش و خنده آور بپرهیز، گر چه آن را از دیگری نقل کرده باشی. \n۱۷- جایگاه زن و فرهنگ پرهیز \nدر امور سیاسی کشور از مشورت با زنان بپرهیز، که رأی آنان زود سست می‌شود، و تصمیم آنان ناپایدار است. در پرده حجاب نگاهشان دار، تا نامحرمان را ننگرند، زیرا که سخت‌گیری در پوشش، عامل سلامت و استواری آنان است. بیرون رفتن زنان بدتر از آن نیست که افراد غیر صالح را در میانشان آوری، و اگر بتوانی به گونه‌ای زندگی کنی که غیر تو را نشناسند چنین کن. کاری که برتر از توانایی زن است به او وامگذار، که زن گل بهاری است، نه پهلوانی سخت کوش، مبادا در گرامی داشتن زن زیاده روی کنی که او را به طمع ورزی کشانده برای دیگران به ناروا شفاعت کند. بپرهیز از غیرت نشان دادن بیجا که درستکار را به بیمار دلی، و پاکدامن را به بدگمانی رساند. کار هر کدام از خدمتکارانت را معین کن که او را در برابر آن کار مسئول بدانی، که تقسیم درست کار سبب می‌شود کارها را به یکدیگر وا نگذارند، و در خدمت سستی نکنند. \nخویشاوندانت را گرامی دار، زیرا آن‌ها پر و بال تو می‌باشند، که با آن پرواز می‌کنی، و ریشه تو هستند که به آن‌ها باز می‌گردی، و دست نیرومند تو می‌باشند که با آن حمله می‌کنی. دین و دنیای تو را به خدا می‌سپارم، و بهترین خواسته الهی را در آینده و هم اکنون، در دنیا و آخرت، برای تو می‌خواهم، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (267, 2, 32, 'به معاویه', 'نامه ۳۲-به معاویه \nافشای سیاست استحماری معاویه \nای معاویه! گروهی بسیار از مردم را به هلاکت کشاندی، و با گمراهی خود فریبشان دادی، و در موج سرکش دریای جهالت خود غرقشان کردی، که تاریکیها آنان را فرا گرفت، و در امواج انواع شبهات غوطه ور گردیدند، که از راه حق به بیراه افتادند، و به دوران جاهلیت گذشتگانشان روی آوردند، و به ویژگیهای جاهلی خاندانشان نازیدند، جز اندکی از آگاهان که مسیر خود را تغییر دادند، پس از آنکه تو را شناختند از تو جدا شدند، و از یاری کردن تو به سوی خدا گریختند، زیرا تو آنان را به کار دشوار واداشتی، و از راه راست منحرفشان ساختی. ای معاویه! در کارهای خود از خدا بترس، و اختیارت را از کف شیطان درآور، که دنیا از تو بریده و آخرت به تو نزدیک شده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (268, 2, 33, 'به قثم بن عباس', 'نامه ۳۳-به قثم بن عباس \nهشدار از تبلیغات دروغین یاران معاویه در مراسم حج \nپس از یاد خدا و درود، \nهمانا مامور اطلاعاتی من در شام به من اطلاع داده که گروهی از مردم شام برای مراسم حج به مکه می‌آیند، مردمی کوردل، گوشهایشان در شنیدن حق ناشنوا، و دیده‌هایشان نابینا، که حق را از راه باطل می‌جویند، و بنده را در نافرمانی از خدا، فرمان می‌برند، دین خود را به دنیا می‌فروشند، و دنیا را به بهای سرای جاودانه نیکان و پرهیزکاران می‌خرند، در حالی که در نیکی‌ها، انجام دهنده آن پاداش گیرد، و در بدیها جز بدکار کیفر نشود. پس در اداره امور خود هشیارانه و سرسختانه استوار باش، نصیحت دهنده ای عاقل، پیرو حکومت، و فرمانبردار امام خود باش، مبادا کاری انجام دهی که به عذرخواهی روی آوری، نه به هنگام نعمتها شادمان و نه هنگام مشکلات سست باشی. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (269, 2, 34, 'به محمد بن ابی‌بکر', 'نامه ۳۴-به محمد بن ابی بکر \nروش دلجویی از فرماندار معزول \nپس از یاد خدا و درود، به من خبر داده‌اند که از فرستادن اشتر به سوی محل فرمانداریت، ناراحت شده ای، این کار را انجام ندادم که تو در تلاش خود کند شدی، یا انتظار کوشش بیشتری از تو دارم، اگر تو را از فرمانداری مصر عزل کردم، فرماندار جایی قرار دادم که اداره آنجا بر تو آسان تر، و حکومت تو در آن سامان خوش تر است. همانا مردی را فرماندار مصر قرار دادم، که نسبت به ما خیرخواه، و به دشمنان ما سخت گیر و درهم کوبنده بود، خدا او را رحمت کند، که ایام زندگی خود را کامل و مرگ خود را ملاقات کرد، در حالی که ما از او خشنود بودیم و خداوند خشنودی خود را نصیب او گرداند، و پاداش او را چند برابر عطا کند. پس برای مقابله با دشمن سپاه را بیرون بیاور، و با آگاهی لازم به سوی دشمن حرکت کن، و با کسی که با تو در جنگ است آماده پیکار باش. مردم را به راه پروردگارت بخوان، و از خدا فراوان یاری خواه که تو را در مشکلات کفایت می‌کند، و در سختیهایی که بر تو فرود می‌آید یاریت می‌دهد. ان شاء الله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (270, 2, 35, 'به عبدالله بن عباس', 'نامه ۳۵-به عبدالله بن عباس \nعلل سقوط مصر \nپس از یاد خدا و درود! \nهمانا مصر سقوط کرد، و فرماندارش محمد بن ابی بکر (که خدا او را رحمت کند) شهید گردید، در پیشگاه خداوند، او را فرزندی خیرخواه، و کارگزاری کوشا، و شمشیری برنده، و ستونی باز دارنده می‌شماریم، همواره مردم را برای پیوستن به او برانگیختم، و فرمان دادم تا قبل از این حوادث ناگوار به یاریش بشتابند، مردم را نهان و آشکار، از آغاز تا انجام فرا خواندم، عده ای با ناخوشایندی آمدند، و برخی به دروغ بهانه آوردند، و بعضی خوار و ذلیل بر جای ماندند. از خدا می‌خواهم به زودی مرا از این مردم نجات دهد. به خدا سوگند اگر در پیکار با دشمن، آرزوی من شهادت نبود، و خود را برای مرگ آماده نکرده بودم، دوست می‌داشتم حتی یک روز با این مردم نباشم، و هرگز دیدارشان نکنم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (271, 2, 36, 'به عقیل', 'نامه ۳۶-به عقیل \nآمادگی رزمی امام (ع) \nلشکری انبوه از مسلمانان را به سوی بسر بن ارطاه (که به یمن یورش برد) فرستادم، هنگامی که این خبر به او رسید، دامن برچید و فرار کرد، و پشیمان بازگشت، اما در سر راه به او رسیدند و این به هنگام غروب آفتاب بود، لحظه ای نبرد کردند، گویا ساعتی بیش نبود، که بی رمق با دشواری جان خویش از می‌دان نبرد بیرون برد. برادر! قریش را بگذار تا در گمراهی بتازند، و در جدایی سرگردان باشند، و با سرکشی و دشمنی زندگی کنند، همانا آنان در جنگ با من متحد شدند آنگونه که پیش از من در نبرد با رسول خدا (ص) هماهنگ بودند، خدا قریش را به کیفر زشتی‌هایشان عذاب کند، آن‌ها پیوند خویشاوندی مرا بریدند، و حکومت فرزند مادرم (پیامبر (ص)) را از من ربودند. \nاعلام مواضع قاطعانه در جنگ \nاما آنچه را که از تداوم جنگ پرسیدی، و رای مرا خواستی بدانی، همانا رای من پیکار با پیمان شکنان است تا آنگاه که خدا را ملاقات کنم، نه فراوانی مردم مرا توانمند می‌کند، و نه پراکندگی آنان مرا هراسناک می‌سازد، هرگز گمان نکنی مرا توانمند می‌کند، و نه پراکندگی آنان مرا هراسناک می‌سازد، هرگز گمان نکنی که فرزند پدرت، اگر مردم او را رها کنند، خود را زار و فروتن خواهد داشت، و یا در برابر ستم سست می‌شود، و یا مهار اختیار خود را به دست هرکسی می‌سپارد، و یا از دستور هر کسی اطاعت می‌کند، بلکه تصمیم من آنگونه است که آن شاعر قبیله بنی سلیم سروده: (اگر از من بپرسی چگونه ای؟ همانا من در برابر مشکلات روزگار شکیبا هستم. بر من دشوار است که مرا با چهره ای اندوهناک بنگرند، تا دشمن سرزنش کند و دوست ناراحت شود.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (272, 2, 37, 'به معاویه', 'نامه ۳۷-به معاویه \nافشای ادعای دروغین معاویه \nپس خدای را سپاس! معاویه تو چه سخت به هوسهای بدعتزا، و سرگردانی پایدار، وابسته ای؟ حقیقتها را تباه کرده، و پیمانها را شکسته ای، پیمانهایی که خواسته خدا و حجت خدا بر بندگان او بود. اما پرگویی تو نسبت به عثمان و کشندگان او را جواب آن است که: تو عثمان را هنگامی یاری دادی که انتظار پیروزی او را داشتی، و آنگاه که یاری تو به سود او بود او را خوار گذاشتی، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (273, 2, 38, 'به مردم مصر', 'نامه ۳۸-به مردم مصر \nویژگیهای بی مانند مالک اشتر \nاز بنده خدا، علی امیر مومنان، به مردمی که برای خدا به خشم آمدند، آن هنگام که دیگران خدا را در زمین نافرمانی کردند، و حق او را نابود نمودند، پس ستم، خیمه خود را بر سر نیک و بد، مسافر و حاضر، و بر همگان، برافراشت، نه معروفی ماند که در پناه آن آرامش یابند، و نه کسی از زشتیها نهی می‌کرد پس از ستایش پروردگار! من بنده ای از بندگان خدا را به سوی شما فرستادم، که در روزهای وحشت، نمی خوابد، و در لحظه‌های ترس از دشمن روی نمی گرداند، بر بدکاران از شعله‌های آتش تندتر است، او مالک پسر حارث مذحجی است، آنجا که با حق است، سخن او بشنوید، و از او اطاعت کنید، او شمشیری از شمشیرهای خداست، که نه تیزی آن کند می‌شود، و نه ضربت آن بی اثر است، اگر شما را فرمان کوچ کردن داد، کوچ کنید، و اگر گفت بایستید، بایستید، که او در پیش روی و عقب نشینی و حمله، بدون فرمان من اقدام نمی کند مردم مصر! من شما را بر خود برگزیدم که او را برای شما فرستادم، زیرا او را خیرخواه شما دیدم، و سرسختی او را در برابر دشمنانتان پسندیدم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (274, 2, 39, 'به عمروعاص', 'نامه ۳۹-به عمروعاص \nافشای بردگی عمروعاص \nتو دین خود را پیرو کسی قرار دادی که گمراهیش آشکار است، پرده اش دریده، و افراد بزرگوار در همنشینی با او لکه دار، و در معاشرت با او به سبک مغزی متهم می‌گردند، تو در پی او می‌روی، و چونان سگی گرسنه بدنبال پس مانده شکار شیر هستی، به بخشش او نظر دوختی که قسمتهای اضافی شکارش را به سوی تو افکند، پس دنیا و آخرت خود را تباه کردی، در حالی که اگر به حق می‌پیوستی به خواسته‌های خود می‌رسیدی. اگر خدا مرا بر تو و پسر ابوسفیان مسلط گرداند، سزای زشتیهای شما را خواهم داد، اما اگر قدرت آن را نیافتم و باقی ماندید آنچه در پیش روی دارید برای شما بدتر است. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (275, 2, 4, 'به یکی از فرماندهان سپاهش', 'نامه ۴- به یکی از فرماندهانش \nروش گزینش نیروهای عمل کننده \nاگر دشمنان اسلام به سایه اطاعت باز گردند پس همان است که دوست داریم، و اگر کارشان به جدایی و نافرمانی کشید با کمک فرمانبرداران با مخالفان نبرد کن، و از آنان که فرمان می‌برند برای سرکوب آن‌ها که از یاری تو سر باز می‌زنند مدد گیر، زیرا آن کس که از جنگ کراهت دارد بهتر است که شرکت نداشته باشد، و شرکت نکردنش از یاری دادن اجباری بهتر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (276, 2, 40, 'به یکی از کارگزاران خود', 'نامه ۴۰-به یکی از کارگزاران خود \nنکوهش یک کارگزار \nپس از یاد خدا و درود! \nاز تو خبری رسیده است که اگر چنان کرده باشی، پروردگار خود را به خشم آورده، و امام خود را نافرمانی کردی، و در امانت خود خیانت کردی. به من خبر رسیده که کشت زمینها را برداشته، و آن چه را که می‌توانستی گرفته، و آنچه در اختیار داشتی به خیانت خورده ای، پس هر چه زودتر حساب اموال را برای من بفرست و بدان که حسابرسی خداوند از حسابرسی مردم سخت تر است. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (277, 2, 41, 'به یکی از کارگزارانش', 'نامه ۴۱-به یکی از کارگزارانش \nعلل نکوهش یک کارگزار خیانتکار \nپس از یاد خدا و درود! \nهمانا من تو را در امانت خود شرکت دادم، و همراز خود گرفتم، و هیچ یک از افراد خاندانم برای یاری و مددکاری، و امانت داری، چون تو مورد اعتمادم نبود، آن هنگام که دیدی روزگار بر پسر عمویت سخت گرفته، و دشمن به او هجوم آورده، و امانت مسلمانان تباه گردیده. و امت اختیار از دست داده، و پراکنده شدند، پیمان خود را با پسر عمویت دگرگون ساختی، و همراه با دیگرانی که از او جدا شدند فاصله گرفتی، تو هماهنگ با دیگران دست از یاریش کشیدی، و با دیگر خیانت کنندگان خیانت کردی، نه پسر عمویت را یاری کردی، و نه امانتها را رساندی. گویا تو در راه خدا جهاد نکردی! و برهان روشنی از پروردگارت نداری، و گویا برای تجاوز به دنیای این مردم نیرنگ می‌زدی، و هدف تو آن بود که آن‌ها را بفریبی! و غنائم و ثروتهای آنان را در اختیار گیری، پس آنگاه که فرصت خیانت یافتی شتابان حمله ور شدی، و با تمام توان، اموال بیت المال را که سهم بیوه زنان و یتیمان بود، چونان گرگ گرسنه ای که گوسفند زخمی یا استخوان شکسته ای را می‌رباید، به یغما بردی، و آن‌ها را به سوی حجاز با خاطری آسوده، روانه کردی، بی آنکه در این کار احساس گناهی داشته باشی. \nنکوهش از سوءاستفاده در بیت المال \nدشمنت بی پدر باد، گویا میراث پدر و مادرت را به خانه می‌بری! سبحان الله!! آیا به معاد ایمان نداری؟ و از حسابرسی دقیق قیامت نمی ترسی؟ ای کسی که در نزد ما از خردمندان بشمار می‌آمدی، چگونه نوشیدن و خوردن را بر خود گوارا نمودی در حالی که می‌دانی حرام می‌خوری! و حرام می‌نوشی! چگونه با اموال یتیمان و مستمندان و مومنان و مجاهدان راه خدا، کنیزان می‌خری و با زنان ازدواج می‌کنی؟ که خدا این اموال را به آنان واگذاشته، و این شهرها را به دست ایشان امن فرموده است! \nبرخورد قاطع با خیانتکار \nپس از خدا بترس، و اموال آنان را باز گردان، و اگر چنین نکنی و خدا مرا فرصت دهد تا بر تو دست یابم، تو را کیفر خواهم نمود، که نزد خدا عذرخواه من باشد، و با شمشیری تو را می‌زنم که به هر کس زدم وارد دوزخ گردید. سوگند به خدا! اگر حسن و حسین چنان می‌کردند که تو انجام دادی، از من روی خوش نمی دیدند و به آرزو نمی رسیدند تا آنکه حق را از آنان باز پس ستانم، و باطلی را که به ستم پدید آمده نابود سازم، به پروردگار جهانیان سوگند، آنچه که تو از اموال مسلمانان به ناحق بردی، بر من حلال بود، خشنود نبودم که آن را میراث بازماندگانم قرار دهم، پس دست نگهدار و اندیشه نما، فکر کن که به پایان زندگی رسیده ای، و در زیر خاکها پنهان شده، و اعمال تو را بر تو عرضه داشتند، آنجا که ستمکار با حسرت فریاد می‌زند، و تباه کننده عمر و فرصتها، آرزوی بازگشت دارد اما راه فرار و چاره مسدود است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (278, 2, 42, 'به عمر بن ابی‌سلمه مخزومی', 'نامه ۴۲-به عمر بن ابی سلمه \nروش دلجویی در عزل و نصبها \nپس از یاد خدا و درود! \nهمانا من نعمان ابن عجلان زرقی، را به فرمانداری بحرین نصب کردم، و بی آنکه سرزنشی و نکوهشی برای تو وجود داشته باشد تو را از فرمانداری آن سامان گرفتم، که تاکنون زمامداری را به نیکی انجام دادی، و امانت را پرداختی، پس به سوی ما حرکت کن، که تصمیم دارم به سوی ستمگران شام حرکت کنم، دوست دارم در این جنگ با من باشی، زیرا تو از دلاورانی هستی که در جنگ با دشمن، و برپا داشتن ستون دین از آنان یاری می‌طلبم. ان شاء الله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (279, 2, 43, 'به مصقله بن هبیره', 'نامه ۴۳-به مصقله بن هبیره \nسخت گیری در مصرف بیت المال \nگزارشی از تو به من دادند که اگر چنان کرده باشی، خدای خود را به خشم آوردی، و امام خویش را نافرمانی کردی، خبر رسید که تو غنیمت مسلمانان را که نیزه‌ها و اسبهاشان گرد آورده، و با ریخته شدن خونهایشان به دست آمده، به اعرابی که خویشاوندان تواند، و تو را برگزیدند، می‌بخشی. به خدایی که دانه را شکافت، و پدیده‌ها را آفرید، اگر این گزارش درست باشد، در نزد من خوارشده، و منزلت تو سبک گردیده است، پس حق پروردگارت را سبک مشمار، و دنیای خود را با نابودی دین آباد نکن، که زیانکارترین انسانی، آگاه باش، حق مسلمانانی که نزد من یا پیش تو هستند در تقسیم بیت المال مساوی است، همه باید به نزد من آیند و سهم خود را از من گیرند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (280, 2, 44, 'به زیاد بن ابیه', 'نامه ۴۴-به زیاد بن ابیه \nافشای توطئه معاویه نسبت به زیاد \nاطلاع یافتم که معاویه برای تو نامه ای نوشته تا عقل تو را بلغزد، و اراده تو را سست کند، از او بترس که شیطان است، و از پیش رو، و پشت سر، و از راست و چپ به سوی انسان می‌آید تا در حال فراموشی، او را تسلیم خود سازد، و شعور و درکش را برباید، آری ابوسفیان در زمان عمر بن خطاب ادعایی بدون اندیشه و با وسوسه شیطان کرد که نه نسبی را درست می‌کند، و نه کسی با آن سزاوار ارث است، ادعاکننده چونان شتری بیگانه است که در جمع شتران یک گله واردشده تا از آبشخور آب آنان بنوشد که او را از خود ندانند و از جمع خود دور کنند. یا چونان ظرفی که بر پالان مرکبی آویزان پیوسته از این سو بدان سو لرزان باشد. (وقتی زیاد نامه را خواند گفت به پروردگار کعبه سوگند که امام (ع) به آنچه در دل من می‌گذشت گواهی داد تا آنکه معاویه او را به همکاری دعوت کرد. (واغل) حیوانی است که برای نوشیدن آب هجوم می‌آورد اما از شمار گله نیست و همواره دیگر شتران او را به عقب می‌رانند، و (نوط مذبذب) ظرفی است که به مرکب می‌آویزند، که همیشه به این سو و آن سو می‌جهد، و در حال حرکت لرزان است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (281, 2, 45, 'به عثمان بن حنیف انصاری', 'نامه ۴۵-به عثمان بن حنیف \nضرورت ساده زیستی کارگزاران \nپس از یاد خدا و درود! ای پسر حنیف، به من گزارش دادند که مردی از سرمایه داران بصره، تو را به مهمانی خویش فرا خواند و تو به سرعت به سوی آن شتافتی، خوردنیهای رنگارنگ برای تو آوردند، و کاسه‌هایی پر از غذا پی در پی جلوی تو می‌نهادند، گمان نمی کردم مهمانی مردمی را بپذیری که نیازمندانشان با ستم محروم شده، و ثروتمندانشان بر سر سفره دعوت شده اند، اندیشه کن در کجایی؟ و بر سر کدام سفره می‌خوری؟ پس آن غذایی که حلال و حرام بودنش را نمی دانی دور بیافکن، و آنچه را به پاکیزگی و حلال بودنش یقین داری مصرف کن. \nامام الگوی ساده زیستی \nآگاه باش! هر پیروی را امامی است که از او پیروی می‌کند، و از نور دانشش روشنی می‌گیرد، آگاه باش! امام شما از دنیای خود به دو جامه فرسوده، و دو قرص نان رضایت داده است، بدانید که شما توانایی چنین کاری را ندارید اما با پرهیزکاری و تلاش فراوان و پاکدامنی و راستی، مرا یاری دهید. پس سوگند به خدا! من از دنیای شما طلا و نقره ای نیاندوخته، و از غنیمتهای آن چیزی ذخیره نکرده ام، بر دو جامه کهنه ام جامه ای نیفزودم، و از زمین دنیا حتی یک وجب در اختیار نگرفتم و دنیای شما در چشم من از دانه تلخ درخت بلوط ناچیزتر است. \nآری از آنچه آسمان بر آن سایه افکنده، فدک در دست ما بود که مردمی بر آن بخل ورزیده، و مردمی دیگر سخاوتمندانه از آن چشم پوشیدند، و بهترین داور خداست، مرا با فدک و غیر فدک چه کار است؟ در صورتیکه جایگاه فردای آدمی گور است. که در تاریکی آن، آثار انسان نابود و اخبارش پنهان می‌گردد، گودالی که هر چه بر وسعت آن بیفزایند، و دستهای گورکن فراخش نماید، سنگ و کلوخ آن را پر کرده، و خاک انباشته رخنه‌هایش را مسدود کند. من نفس خود را با پرهیزکاری می‌پرورانم، تا در روز قیامت که هراسناکترین روزهاست در امان، و در لغزشگاه‌های آن ثابت قدم باشد. \nمن اگر می‌خواستم، می‌توانستم از عسل پاک، و از مغز گندم، و بافته‌های ابریشم، برای خود غذا و لباس فراهم آورم، اما هیهات که هوای نفس بر من چیره گردد، و حرص و طمع مرا وادارد که طعامهای لذیذ برگزینم، در حالی که در (حجاز) یا (یمامه) کسی باشد که به قرص نانی نرسد، و یا هرگز شکمی سیر نخورد، یا من سیر بخوابم و پیرامونم شکمهایی که از گرسنگی به پشت چسبیده، و جگرهای سوخته وجود داشته باشد، یا چنان باشم که شاعر گفت: \n(این درد تو را بس، که شب را با شکم سیر بخوابی.) \n(و در اطراف تو شکمهایی گرسنه و به پشت چسبیده باشد.) \nآیا به همین رضایت دهم که مرا امیرالمومنین (ع) خوانند؟ و در تلخی‌های روزگار با مردم شریک نباشم؟ و در سختی‌های زندگی الگوی آنان نگردم؟ آفریده نشده ام که غذاهای لذیذ و پاکیزه مرا سرگرم سازد، چونان حیوان پرواری که تمام همت او علف، و یا چون حیوان رهاشده که شغلش چریدن و پر کردن شکم بوده، و از آینده خود بی خبر است. آیا مرا بیهوده آفریدند؟ آیا مرا به بازی گرفته اند؟ آیا ریسمان گمراهی در دست گیرم؟ و یا در راه سرگردانی قدم بگذارم؟ گویا می‌شنوم که شخصی از شما می‌گوید: (اگر غذای فرزند ابیطالب همین است، پس سستی او را فرا گرفته و از نبرد با هماوردان و شجاعان بازمانده است.) آگاه باشید! درختان بیابانی، چوبشان سخت تر، اما درختان کناره جویبار را پوست نازک تر است، درختان بیابانی که با باران سیراب می‌شوند آتش چوبشان شعله ورتر و پردوام تر است. من و رسول خدا (ص) چونان روشنایی یک چراغیم، یا چون آرنج به یک بازو پیوسته، به خدا سوگند! اگر اعراب در نبرد با من پشت به پشت یکدیگر بدهند، از آن روی برنتابم، و اگر فرصت داشته باشم به پیکار همه می‌شتابم، و تلاش می‌کنم که زمین را از این شخص (معاویه) مسخ شده، و این جسم کج اندیش، پاک سازم تا سنگ و شن از میان دانه‌ها جدا گردد. (قسمتی از آخر نامه) ای دنیا از من دور شو، مهارت را بر پشت تو نهاده، و از چنگالهای تو رهایی یافتم، و از دامهای تو نجات یافته، و از لغزشگاه‌هایت دوری گزیده ام، کجایند بزرگانی که به بازیچه‌های خود فریبشان داده ای؟ کجایند امتهایی که با زور و زیورت آن‌ها را فریفتی؟ که اکنون در گورها گرفتارند! و درون لحدها پنهان شده اند. \nامام و دنیای دنیاپرستان \nای دنیا به خدا سوگند! اگر شخصی دیدنی بودی، و قالب حس کردنی داشتی، حدود خدا را بر تو جاری می‌کردم، به جهت بندگانی که آن‌ها را با آرزوهایت فریب دادی، و ملتهایی که آن‌ها را به هلاکت افکندی، و قدرتمندانی که آن‌ها را تسلیم نابودی کردی، و هدف انواع بلاها قرار دادی که دیگر راه پس و پیش ندارند، اما هیهات! کسی که در لغزشگاه تو قدم گذارد سقوط کرد، و آن کس که بر امواج تو سوار شد غرق گردید، کسی که از دامهای تو رست پیروز شد، آن کس که از تو به سلامت گذشت نگران نیست که جایگاهش تنگ است، زیرا دنیا در پیش او چونان روزی است که گذشت. از برابر دیدگانم دور شو، سوگند به خدا، رام تو نگردم که خوارم سازی، و مهارم را به دست تو ندهم که هر کجا خواهی مرا بکشانی، به خدا سوگند، سوگندی که تنها اراده خدا در آن است، چنان نفس خود را به ریاضت وادارم که به یک قرص نان، هرگاه بیابم، و به نمک به جای نان خورش قناعت کند، و آنقدر از چشم‌ها اشک ریزم که چونان چشمه ای خشک درآید، و اشک چشم پایان پذیرد. آیا سزاوار است که چرندگان فراوان بخورند و راحت بخوابند، و گله گوسفندان پس از چرا کردن به آغل رو کنند، و علی نیز از زاد و \nتوشه خود بخورد و استراحت کند؟ چشمش روشن باد! که پس از سالیان دراز، چهار پایان رهاشده، و گله‌های گوسفندان را الگو قرار دهد!! خوشا به حال آن کس که مسوولیتهای واجب را در پیشگاه خدا به انجام رسانده، و در راه خدا هرگونه سختی و تلخی را به جان خریده، و به شب زنده داری پرداخته است، و اگر خواب بر او چیره شود بر روی زمین خوابیده، و کف دست را بالین خود قرار می‌دهد، در گروهی که ترس از معاد خواب را از چشمانشان ربوده، و پهلو از بسترها گرفته، و لبهایشان به یاد پروردگار در حرکت و با استغفار طولانی گناهان را زدوده اند. (آنان حزب خداوندند، و همانا حزب خدا رستگار است.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (282, 2, 46, 'به یکی از فرماندهان خود', 'نامه ۴۶-به یکی از فرماندهان خود \nمسوولیت فرمانداری و اخلاق اجتماعی \nپس از یاد خدا و درود! \nهمانا از تو کسانی هستی که در یاری دین از آن‌ها کمک می‌گیرم، و سرکشی و غرور گناهکاران را درهم می‌کوبم، و مرزهای کشور اسلامی را که در تهدید دشمن قرار دارند حفظ می‌کنم، پس در مشکلات از خدا یاری جوی، و درشتخویی را با اندک نرمی بیامیز، در آنجا که مدارا کردن بهتر است مدارا کن، و در جایی که جز با درشتی کار انجام نگیرد، درشتی کن، پر و بالت را برابر رعیت بگستران، با مردم گشاده روی و فروتن باش، و در نگاه و اشاره چشم، در سلام کردن و اشاره نمودن با همگان یکسان باش، تا زورمندان در ستم تو طمع نکنند، و ناتوانان از عدالت تو مایوس نگردند. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (283, 2, 47, 'وصیت به حسن و حسین علیهما السلام', 'نامه ۴۷-وصیت به حسن و حسین \nپندهای جاودانه \nشما را به ترس از خدا سفارش می‌کنم به دنیا روی نیاورید، گرچه به سراغ شما آید، و بر آنچه از دنیا از دست می‌دهید اندوهناک مباشید، حق را بگویید، و برای پاداش الهی عمل کنید. دشمن ستمگر و یاور ستمدیده باشید. شما را، و تمام فرزندان و خاندانم را، و کسانی را که این وصیت به آن‌ها می‌رسد، به ترس از خدا، و نظم در امور زندگی، و ایجاد صلح و آشتی در میانتان سفارش می‌کنم، زیرا من از جد شما پیامبر (ص) شنیدم که می‌گفت: (اصلاح دادن بین مردم از نماز و روزه یکسان برتر است.) \nخدا را! خدا را! درباره یتیمان، نکند آنان گاهی سیر و گاه گرسنه بمانند، و حقوقشان ضایع گردد. خدا را! خدا را! درباره همسایگان، حقوقشان را رعایت کنید که وصیت پیامبر (ص) شماست، همواره به خوش رفتاری با همسایگان سفارش می‌کرد تا آنجا که گمان بردیم برای آنان ارثی معین خواهد کرد. \nخدا را! خدا را! درباره قرآن، مبادا دیگران در عمل کردن به دستوراتش از شما پیشی گیرند. \nخدا را! خدا را! درباره نماز همانا که ستون دین شماست. \nخدا را! خدا را! درباره خانه خدا، تا هستید آن را خالی مگذارید، زیرا اگر کعبه خالی شود مهلت داده نمی شوید. \nخدا را! خدا را! درباره جهاد با اموال و جانها و زبانهای خویش در راه خدا. بر شما باد به پیوستن با یکدیگر، و بخشش همدیگر، مبادا از هم روی گردانید، و پیوند دوستی را از بین ببرید، امر به معروف و نهی از منکر را ترک کنید که بدان شما بر شما مسلط می‌گرداند، آنگاه هر چه خدا را بخوانید جواب ندهد. (سپس فرمود). \nسفارش به رعایت مقررات عدالت در قصاص \nای فرزندان عبدالمطلب: مبادا پس از من دست به خون مسلمین فرو برید و بگویید: امیر مومنان کشته شد، بدانید جز کشنده من کسی دیگر نباید کشته شود. درست بنگرید! اگر من از ضربت او مردم، او را تنها یک ضربت بزنید، و دست و پا و دیگر اعضای او را مبرید، من از رسول خدا (ص) شنیدم که فرمود: (بپرهیزید از بریدن اعضای مرده، هر چند سگ دیوانه باشد.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (284, 2, 48, 'به معاویه', 'نامه ۴۸-به معاویه \nاندرز دادن دشمن \nهمانا ستمگری و دروغ پردازی، انسان را در دین و دنیا رسوا می‌کند، و عیب او را نزد عیب جویان آشکار می‌سازد، و تو می‌دانی آنچه که از دست رفت باز نمی گردد، گروهی باطل طلبیدند، و خواستند با تفسیر دروغین، حکم خدا را دگرگون سازند، و خدا آنان را دروغگو خواند، معاویه، از روزی بترس که صاحبان کارهای پسندیده خوشحالند، و تاسف می‌خورند که چرا عملشان اندک است، آن روز کسانی که مهار خویش در دست شیطان دادند سخت پشیمانند. تو ما را به داوری قرآن خواندی، در حالی که خود اهل قرآن نیستی، و ما هم پاسخ مثبت به تو ندادیم، بلکه داوری قرآن را گردن نهادیم. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (285, 2, 49, 'به معاویه', 'نامه ۴۹-به معاویه \nهشدار به معاویه از دنیاپرستی \nپس از یاد خدا و درود! \nهمانا دنیا انسان را به خود سرگرم و از دیگر چیزها باز می‌دارد، دنیاپرستان چیزی از دنیا به دست نمی آورند جز آن که دری از حرص برویشان گشوده، و آتش عشق آنان تندتر می‌گردد، کسی که به دنیای حرام برسد آنچه به دست آورده راضی نیست، و در فکر آن است که به دست نیاورده، اما سرانجام آن، جداشدن از فراهم آورده‌ها، و به هم ریختن بافته شده‌هاست، اگر از آنچه گذشته عبرت گیری، آنچه را که باقی مانده توانی حفظ کرد. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (286, 2, 5, 'به اشعث بن قیس', 'نامه ۵- به اشعث بن قیس \nهشدار از استفاده ناروای بیت المال \nهمانا پست فرمانداری برای تو وسیله آب و نان نخواهد بود، بلکه امانتی است در گردن تو، باید از فرمانده و امامت اطاعت کنی، تو حق نداری نسبت به رعیت استبداد ورزی، و بدون دستور به کار مهمی اقدام نمایی، در دست تو اموالی از ثروتهای خدای بزرگ و عزیز است، و تو خزانه دار آنی تا به من بسپاری، امیدوارم برای تو بدترین زمامدار نباشم، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (287, 2, 50, 'به امیران سپاه خود', 'نامه ۵۰-به امیران سپاه خود \nپرهیز از غرورزدگی در نعمتها \nاز بنده خدا، علی بن ابیطالب، امیر مومنان، به نیروهای مسلح و مرزداران کشور پس از یاد خدا و درود! همانا بر زمامدار واجب است که اگر اموالی به دست آورد، یا نعمتی مخصوص او شد، دچار دگرگونی نشود، و با آن اموال و نعمتها، بیشتر به بندگان خدا نزدیک، و به برادرانش مهربانی روا دارد. \nمسوولیتهای رهبری و نظامیان \nآگاه باشید حق شما بر من آن است که جز اسرار جنگی هیچ رازی را از شما پنهان ندارم، و کاری را جز حکم شرع، بدون مشورت، با شما انجام ندهم، و در پرداخت حق شما کوتاهی نکرده، و در وقت تعیین شده آن بپردازم، و با همه شما بگونه ای مساوی رفتار کنم. پس وقتی من مسوولیتهای یادشده را انجام دهم، بر خداست که نعمتهای خود را بر شما ارزانی دارد، و اطاعت من بر شما لازم است، و نباید از فرمان من سرپیچی کنید، و در انجام آن چه صلاح است سستی ورزید، و در سختیها برای رسیدن به حق تلاش کنید، حال اگر شما پایداری نکنید، خوارترین افراد نزد من انسان کج رفتار است، که او را به سختی کیفر خواهم داد، و هیچ راه فراری نخواهد داشت، پس دستورالعملهای ضروری را از فرماندهانتان دریافت داشته و از فرماندهان خود در آنچه که خدا امور شما را اصلاح می‌کند، اطاعت کنید، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (288, 2, 51, 'به ماموران مالیات', 'نامه ۵۱-به ماموران مالیات \nاخلاق اجتماعی کارگزاران اقتصادی \nاز بنده خدا علی امیرمومنان به کارگزاران جمع آوری مالیات. پس از یاد خدا و درود، همانا کسی از روز قیامت نترسد، زاد و توشه ای از پیش نخواهد فرستاد. بدانید، مسوولیتی را که به عهده گرفته اید اندک اما پاداش آن فراوان است، اگر برای آنچه که خدا نهی کرد (مانند ستمکاری و دشمنی)، کیفری نبود، برای رسیدن به پاداش در ترک آن نیز عذری وجود نداشت، در روابط خود با مردم انصاف داشته باشید، و در برآوردن نیازهایشان شکیبا باشید همانا شما خزانه داران مردم، و نمایندگان ملت، و سفیران پیشوایان هستید، هرگز کسی را از نیازمندی او باز ندارید، و از خواسته‌های مشروعی محروم نسازید، و برای گرفتن مالیات از مردم، لباسهای تابستانی یا زمستانی، و مرکب سواری، و برده کاری او را نفروشید، و برای گرفتن درهمی، کسی را با تازیانه نزنید، و دست اندازی به مال کسی (نمازگزار باشد، یا غیر مسلمانی که در پناه اسلام است)، نکنید، جز اسب یا اسلحه ای که برای تجاوز به مسلمانها بکار گرفته می‌شود، زیرا برای مسلمان جایز نیست آن‌ها را در اختیار دشمنان اسلام بگذارد، تا نیرومندتر از سپاه اسلام گردند. از پند دادن به نفس خویش هیچ گونه کوتاهی نداشته، و از خوش رفتاری با سپاهیان، و کمک به رعایا، و تقویت دین خدا، غفلت نکنید، و از آنچه در راه خدا بر شما واجب است انجام دهید، همانا خدای سبحان از ما و شما خواسته است که در شکرگزاری کوشا بوده، و با تمام قدرت او را یاری کنیم، (و نیروئی جز قدرت خدائی نیست.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (289, 2, 52, 'به فرمانداران شهرها، درباره نماز', 'نامه ۵۲-به فرمانداران شهرها \nوقتهای نماز پنجگانه \nپس از یاد خدا و درود! \nنماز ظهر را با مردم وقتی بخوانید که آفتاب به طرف مغرب رفته، سایه آن به اندازه دیوار خوابگاه بز گردد، و نماز عصر را با مردم هنگامی بخوانید که خورشید سفید و جلوه دارد، در پاره ای از روز که تا غروب می‌شود دو فرسخ راه پیمود. و نماز مغرب را با مردم زمانی بخوانید که روزه دار افطار، و حاجی از عرفات به سوی منی کوچ می‌کند. و نماز عشاء را با مردم وقتی بخوانید که شفق پنهان می‌گردد تا یک سوم از شب بگذرد و نماز صبح را با مردم هنگامی بخوانید که شخص چهره همراه خویش را بشناسد، و در نماز جماعت در حد ناتوان آنان نماز بگذارید، و فتنه گر مباشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (290, 2, 53, 'به مالک‌اشتر نخعی', 'نامه ۵۳-به مالک اشتر نخعی \n(نامه به مالک اشتر، در سال ۳۸ هجری هنگامی که او را به فرمانداری مصر برگزید، آن هنگام که اوضاع محمد بن ابی بکر متزلزل شد، و از طولانی‌ترین نامه‌هاست که زیبایی‌های تمام نامه‌ها را دارد). \nبه نام خداوند بخشنده و مهربان، این فرمان بنده خدا علی امیر مؤمنان، به مالک اشتر پسر حارث است، در عهدی که با او دارد، هنگامی که او را به فرمانداری مصر بر می‌گزیند تا خراج آن دیار را جمع آورد، و با دشمنانش نبرد کند، کار مردم را اصلاح، و شهرهای مصر را آباد سازد. \n۱- ضرورت خود سازی \nاو را به ترس از خدا فرمان می‌دهد، و اینکه اطاعت خدا را بر دیگر کارها مقدم دارد، و آنچه در کتاب خدا آمده، از واجبات و سنت‌ها را پیروی کند، دستوراتی که جز با پیروی آن رستگار نخواهد شد، و جز با نشناختن و ضایع کردن آن جنایتکار نخواهد گردید. به او فرمان می‌دهد که خدا را با دل و دست و زبان یاری کند، زیرا خداوند پیروزی کسی را تضمین کند که او را یاری دهد، و بزرگ دارد آن کس را که او را بزرگ شمارد. و به او فرمان می‌دهد تا نفس خود را از پیروی آرزوها باز دارد، و به هنگام سرکشی رامش کند، که: «همانا نفس همواره به بدی وا می‌دارد جز آن که خدا رحمت آورد» پس ای مالک بدان من تو را به سوی شهرهایی فرستادم که پیش از تو دولت‌های عادل یا ستمگری بر آن حکم راندند، و مردم در کارهای تو چنان می‌نگرند که تو در کارهای حاکمان پیش از خود می‌نگری، و در باره تو آن می‌گویند که تو نسبت به زمامداران گذشته می‌گویی، و همانا نیکوکاران را به نام نیکی توان شناخت که خدا از آنان بر زبان بندگانش جاری ساخت. پس نیکوترین اندوخته تو باید اعمال صالح و درست باشد، هوای نفس را در اختیار گیر، و از آنچه حلال نیست خویشتن داری کن، زیرا بخل ورزیدن به نفس خویش، آن است که در آنچه دوست دارد، یا برای او ناخوشایند است، راه انصاف پیمایی. \n۲- اخلاق رهبری (روش برخورد با مردم) \nمهربانی با مردم را پوشش دل خویش قرار ده، و با همه دوست و مهربان باش. مبادا هرگز، چونان حیوان شکاری باشی که خوردن آنان را غنیمت دانی، زیرا مردم دو دسته‌اند، دسته‌ای برادر دینی تو، و دسته دیگر همانند تو در آفرینش می‌باشند. «» اگر گناهی از آنان سر می‌زند یا علت‌هایی بر آنان عارض می‌شود، یا خواسته و ناخواسته، اشتباهی مرتکب می‌گردند، آنان را ببخشای و بر آنان آسان گیر، آن گونه که دوست داری خدا تو را ببخشاید و بر تو آسان گیرد. همانا تو از آنان برتر، و امام تو از تو برتر، و خدا بر آن کس که تو را فرمانداری مصر داد والاتر است، که انجام امور مردم مصر را به تو واگذارده، و آنان را وسیله آزمودن تو قرار داده است. هرگز با خدا مستیز، که تو را از کیفر او نجاتی نیست، و از بخشش و رحمت او بی‌نیاز نخواهی بود. بر بخشش دیگران پشیمان مباش، و از کیفر کردن شادی مکن، و از خشمی که توانی از آن رها گردی شتاب نداشته باش. به مردم نگو، به من فرمان دادند و من نیز فرمان می‌دهم، پس باید اطاعت شود، «» که این گونه خود بزرگ بینی دل را فاسد، و دین را پژمرده، و موجب زوال نعمت‌هاست. و اگر با مقام و قدرتی که داری، دچار تکبر یا خود بزرگ بینی شدی به بزرگی حکومت پروردگار که برتر از تو است بنگر، که تو را از آن سرکشی نجات می‌دهد، و تند روی تو را فرو می‌نشاند، و عقل و اندیشه‌ات را به جایگاه اصلی باز می‌گرداند. \n۳- پرهیز از غرور و خود پسندی \nبپرهیز که خود را در بزرگی همانند خداوند پنداری، و در شکوه خداوندی همانند او دانی، زیرا خداوند هر سرکشی را خوار می‌سازد، و هر خود پسندی را بی‌ارزش می‌کند. با خدا و با مردم، و با خویشاوندان نزدیک، و با افرادی از رعیت خود که آنان را دوست داری، انصاف را رعایت کن، که اگر چنین نکنی ستم روا داشتی، و کسی که به بندگان خدا ستم روا دارد خدا به جای بندگانش دشمن او خواهد بود، و آن را که خدا دشمن شود، دلیل او را نپذیرد، که با خدا سر جنگ دارد، تا آنگاه که باز گردد، یا توبه کند، و چیزی چون ستمکاری نعمت خدا را دگرگون نمی‌کند، و کیفر او را نزدیک نمی‌سازد، که خدا دعای ستمدیدگان را می‌شنود و در کمین ستمکاران است. \n۴- مردم گرایی، حق گرایی \nدوست داشتنی‌ترین چیزها در نزد تو، در حق میانه‌ترین، و در عدل فراگیرترین، و در جلب خشنودی مردم گسترده‌ترین باشد، که همانا خشم عمومی مردم، خشنودی خواص (نزدیکان) را از بین می‌برد، اما خشم خواص را خشنودی همگان بی‌أثر می‌کند. خواص جامعه، همواره بار سنگینی را بر حکومت تحمیل می‌کنند زیرا در روزگار سختی یاریشان کمتر، و در اجرای عدالت از همه ناراضی‌تر، و در خواسته‌هایشان پافشارتر، و در عطا و بخشش‌ها کم سپاس‌تر، و به هنگام منع خواسته‌ها دیر عذر پذیرتر، و در برابر مشکلات کم استقامت‌تر می‌باشند. در صورتی که ستون‌های استوار دین، و اجتماعات پرشور مسلمین، و نیروهای ذخیره دفاعی، عموم مردم می‌باشند، پس به آن‌ها گرایش داشته و اشتیاق تو با آنان باشد. \n۵- ضرورت راز داری \nاز رعیت، آنان را که عیب جوترند از خود دور کن «»، زیرا مردم عیوبی دارند که رهبر امت در پنهان داشتن آن از همه سزاوارتر است، پس مبادا آنچه بر تو پنهان است آشکار گردانی، و آنچه که هویداست بپوشانی، که داوری در آنچه از تو پنهان است با خدای جهان می‌باشد، پس چندان که می‌توانی زشتی‌ها را بپوشان، تا آن را که دوست داری بر رعیت پوشیده ماند خدا بر تو بپوشاند، گره هر کینه‌ای را در مردم بگشای، و رشته هر نوع دشمنی را قطع کن، و از آنچه که در نظر روشن نیست کناره گیر. در تصدیق سخن چین شتاب مکن، زیرا سخن چین گرچه در لباس اندرز دهنده ظاهر می‌شود اما خیانتکار است. \n۶- جایگاه صحیح مشورت \nبخیل را در مشورت کردن دخالت نده، که تو را از نیکوکاری باز می‌دارد، و از تنگدستی می‌ترساند. ترسو را در مشورت کردن دخالت نده، که در انجام کارها روحیه تو را سست می‌کند. حریص را در مشورت کردن دخالت نده، که حرص را با ستمکاری در نظرت زینت می‌دهد. همانا بخل و ترس و حرص، غرائز گوناگونی هستند که ریشه آن‌ها بدگمانی به خدای بزرگ است. بدترین وزیران تو، کسی است که پیش از تو وزیر بدکاران بوده، و در گناهان آنان شرکت داشته، پس مبادا چنین افرادی محرم راز تو باشند، زیرا که آنان یاوران گناهکاران، و یاری دهندگان ستمکارانند. تو باید جانشینانی بهتر از آنان داشته باشی که قدرت فکری امثال آن‌ها را داشته، اما گناهان و کردار زشت آن‌ها را نداشته باشند: کسانی که ستمکاری را بر ستمی یاری نکرده، و گناه کاری را در گناهی کمک نرسانده باشند. هزینه این گونه از افراد بر تو سبک‌تر، و یاریشان بهتر، و مهربانیشان بیشتر، و دوستی آنان با غیر تو کمتر است. آنان را از خواص، و دوستان نزدیک، و راز داران خود قرار ده، سپس از میان آنان افرادی را که در حق گویی از همه صریح‌ترند، و در آنچه را که خدا برای دوستانش نمی‌پسندد تو را مدد کار نباشند، انتخاب کن، چه خوشایند تو باشد یا ناخوشایند. \n۷- اصول روابط اجتماعی رهبران \nتا می‌توانی با پرهیزکاران و راستگویان بپیوند، و آنان را چنان پرورش ده که تو را فراوان نستایند، و تو را برای اعمال زشتی که انجام نداده‌ای تشویق نکنند، که ستایش بی‌اندازه، خود پسندی می‌آورد، و انسان را به سرکشی وا می‌دارد. هرگز نیکو کار و بدکار در نظرت یکسان نباشند، زیرا نیکوکاران در نیکوکاری بی‌رغبت، و بدکاران در بد کاری تشویق می‌گردند، پس هر کدام از آنان را بر أساس کردارشان پاداش ده. بدان ای مالک هیچ وسیله‌ای برای جلب اعتماد والی به رعیت بهتر از نیکوکاری به مردم، و تخفیف مالیات، و عدم اجبار مردم به کاری که دوست ندارند، نمی‌باشد، پس در این راه آنقدر بکوش تا به وفاداری رعیت، خوشبین شوی، که این خوشبینی رنج طولانی مشکلات را از تو بر می‌دارد، پس به آنان که بیشتر احسان کردی بیشتر خوشبین باش، و به آنان که بد رفتاری کردی بد گمان‌تر باش. و آداب پسندیده‌ای را که بزرگان این امت به آن عمل کردند، و ملت اسلام با آن پیوند خورده، و رعیت با آن اصلاح شدند، بر هم مزن، و آدابی که به سنت‌های خوب گذشته زیان وارد می‌کند «»، پدید نیاور، که پاداش برای آورنده سنت، و کیفر آن برای تو باشد که آن‌ها را در هم شکستی. با دانشمندان، فراوان گفتگو کن، و با حکیمان فراوان بحث کن، که مایه آبادانی و اصلاح شهرها، و برقراری نظم و قانونی است که در گذشته نیز وجود داشت. \n۸- شناخت اقشار گوناگون اجتماعی \nای مالک بدان مردم از گروه‌های گوناگونی می‌باشند که اصلاح هر یک جز با دیگری امکان ندارد، و هیچ یک از گروه‌ها از گروه دیگر بی‌نیاز نیست. از آن قشرها، لشکریان خدا، و نویسندگان عمومی و خصوصی، قضات دادگستر، کارگزاران عدل و نظم اجتماعی، جزیه دهندگان، پرداخت کنندگان مالیات، تجار و بازرگانان، صاحبان صنعت و پیشه‌وران، و نیز طبقه پایین جامعه، یعنی نیازمندان و مستمندان می‌باشند، که برای هر یک خداوند سهمی مقرر داشته، و مقدار واجب آن را در قرآن یا سنت پیامبر صلی الله علیه و آله و سلم تعیین کرده که پیمانی از طرف خداست و نگهداری آن بر ما لازم است. پس سپاهیان به فرمان خدا، پناهگاه استوار رعیت، و زینت و وقار زمامداران، شکوه دین، و راه‌های تحقق امنیت کشورند. امور مردم جز با سپاهیان استوار نگردد، و پایداری سپاهیان جز به خراج و مالیات رعیت انجام نمی‌شود که با آن برای جهاد با دشمن تقویت گردند، و برای اصلاح امور خویش به آن تکیه کنند، و نیازمندی‌های خود را برطرف سازند. «» سپس سپاهیان و مردم، جز با گروه سوم نمی‌توانند پایدار باشند، و آن قضات، و کارگزاران دولت، و نویسندگان حکومتند، که قراردادها و معاملات را استوار می‌کنند، و آنچه به سود مسلمانان است فراهم می‌آورند، و در کارهای عمومی و خصوصی مورد اعتمادند. و گروه‌های یاد شده بدون بازرگانان، و صاحبان صنایع نمی‌توانند دوام بیاورند، زیرا آنان وسائل زندگی را فراهم می‌آورند، و در بازارها عرضه می‌کنند، و بسیاری از وسایل زندگی را با دست می‌سازند که از توان دیگران خارج است. قشر دیگر، طبقه پایین از نیازمندان و مستمندانند که باید به آن‌ها بخشش و یاری کرد. برای تمام اقشار گوناگون یاد شده، در پیشگاه خدا گشایشی است، و همه آنان به مقداری که امورشان اصلاح شود بر زمامدار، حقی مشخص دارند، و زمامدار از انجام آنچه خدا بر او واجب کرده است نمی‌تواند موفق باشد جز آن که تلاش فراوان نماید، و از خدا یاری بطلبد، و خود را برای انجام حق آماده سازد، و در همه کارها، آسان باشد یا دشوار، شکیبایی ورزد. \nاول سیمای نظامیان \nبرای فرماندهی سپاه کسی را برگزین که خیرخواهی او برای خدا و پیامبر صلی الله علیه و آله و سلم و امام تو بیشتر، و دامن او پاک‌تر، شکیبایی او برتر باشد، از کسانی که دیر به خشم آید، و عذر پذیرتر باشد، و بر ناتوان رحمت آورد، و با قدرتمندان، با قدرت برخورد کند، درشتی او را به تجاوز نکشاند، و ناتوانی او را از حرکت باز ندارد. سپس در نظامیان با خانواده‌های ریشه‌دار، دارای شخصیت حساب شده، خاندانی پارسا، دارای سوابقی نیکو و درخشان، که دلاور و سلحشور و بخشنده و بلند نظرند، روابط نزدیک بر قرار کن، آنان همه بزرگواری را در خود جمع کرده، و نیکی‌ها را در خود گرد آورده‌اند. پس در کارهای آنان به گونه‌ای بیندیش که پدری مهربان در باره فرزندش می‌اندیشد، و مبادا آنچه را که آنان را بدان نیرومند می‌کنی در نظرت بزرگ جلوه کند، و نیکوکاری تو نسبت به آنان- هر چند اندک باشد- خوار مپندار، زیرا نیکی، آنان را به خیرخواهی تو خواند، و گمانشان را نسبت به تو نیکو گرداند، و رسیدگی به امور کوچک آنان را به خاطر رسیدگی به کارهای بزرگشان وامگذار، زیرا از نیکی اندک تو سود می‌برند، و به نیکی‌های بزرگ تو بی‌نیاز نیستند. برگزیده‌ترین فرماندهان سپاه تو، کسی باشد که از همه بیشتر به سربازان کمک رساند، و از امکانات مالی خود بیشتر در اختیارشان گذارد، به اندازه‌ای که خانواده‌هایشان در پشت جبهه، و خودشان در آسایش کامل باشند، تا در نبرد با دشمن، سربازان اسلام تنها به یک چیز بیندیشند. همانا مهربانی تو نسبت به سربازان، دل‌هایشان را به تو می‌کشاند، و همانا برترین روشنی چشم زمامداران، برقراری عدل در شهرها و آشکار شدن محبت مردم نسبت به رهبر است، که محبت دلهای رعیت جز با پاکی قلب‌ها پدید نمی‌آید، و خیرخواهی آنان زمانی است که با رغبت و شوق پیرامون رهبر را گرفته، و حکومت بار سنگینی را بر دوش رعیت نگذاشته باشد، و طولانی شدن مدت زمامداری بر ملت ناگوار نباشد. پس آرزوهای سپاهیان را بر آور، و همواره از آنان ستایش کن، و کارهای مهمی که انجام داده‌اند بر شمار، زیرا یادآوری کارهای ارزشمند آنان، شجاعان را بر می‌انگیزاند، و ترسوها را به تلاش وامی‌دارد، انشاء الله. و در یک ارزشیابی دقیق، رنج و زحمات هر یک از آنان را شناسایی کن، و هرگز تلاش و رنج کسی را به حساب دیگری نگذاشته، و ارزش خدمت او را ناچیز مشمار، تا شرافت و بزرگی کسی موجب نگردد که کار کوچکش را بزرگ بشماری، یا گمنامی کسی باعث شود که کار بزرگ او را ناچیز بدانی. مشکلاتی که در احکام نظامیان برای تو پدید می‌آید، و اموری که برای تو شبهه ناکند، به خدا، و رسول خدا صلی الله علیه و آله و سلم باز گردان، زیرا خدا برای مردمی که علاقه داشته هدایتشان کند فرموده است: «ای کسانی که ایمان آوردید، از خدا و رسول و امامانی که از شما هستند اطاعت کنید، و اگر در چیزی نزاع دارید، آن را به خدا و رسولش باز گردانید» پس باز گرداندن چیزی به خدا، یعنی عمل کردن به قرآن، و باز گرداندن به پیامبر صلی الله علیه و آله و سلم یعنی عمل کردن به سنت او که وحدت بخش است، نه عامل پراکندگی. \nدوم سیمای قضات و داوران \nسپس از میان مردم، برترین فرد نزد خود را برای قضاوت انتخاب کن، کسانی که مراجعه فراوان، آن‌ها را به ستوه نیاورد، و برخورد مخالفان با یکدیگر او را خشمناک نسازد، در اشتباهاتش پافشاری نکند، و بازگشت به حق پس از آگاهی برای او دشوار نباشد، طمع را از دل ریشه کن کند، و در شناخت مطالب با تحقیقی اندک رضایت ندهد، و در شبهات از همه با احتیاطتر عمل کند، و در یافتن دلیل اصرار او از همه بیشتر باشد، و در مراجعه پیاپی شاکیان خسته نشود، در کشف امور از همه شکیباتر، و پس از آشکار شدن حقیقت، در فصل خصومت از همه برنده‌تر باشد، کسی که ستایش فراوان او را فریب ندهد، و چرب زبانی او را منحرف نسازد و چنین کسانی بسیار اندکند. پس از انتخاب قاضی، هر چه بیشتر در قضاوت‌های او بیندیش، و آنقدر به او ببخش که نیازهای او بر طرف گردد، و به مردم نیازمند نباشد، و از نظر مقام و منزلت آنقدر او را گرامی دار که نزدیکان تو، به نفوذ در او طمع نکنند، تا از توطئه آنان در نزد تو در أمان باشد. در دستوراتی که دادم نیک بنگر که همانا این دین در دست بدکاران اسیر گشته بود، که با نام دین به هوا پرستی پرداخته، و دنیای خود را به دست می‌آوردند. \nسوم سیمای کارگزاران دولتی \nسپس در امور کارمندانت بیندیش، و پس از آزمایش به کارشان بگمار، و با میل شخصی، و بدون مشورت با دیگران آنان را به کارهای مختلف وادار نکن، زیرا نوعی ستمگری و خیانت است. کارگزاران دولتی را از میان مردمی با تجربه و با حیا، از خاندان‌های پاکیزه و با تقوی، که در مسلمانی سابقه درخشانی دارند انتخاب کن، زیرا اخلاق آنان گرامی‌تر، و آبرویشان محفوظتر، و طمع و روزی‌شان کمتر، و آینده نگری آنان بیشتر است. سپس روزی فراوان بر آنان ارزانی دار، که با گرفتن حقوق کافی در اصلاح خود بیشتر می‌کوشند، و با بی‌نیازی، دست به اموال بیت المال نمی‌زنند، و اتمام حجتی است بر آنان اگر فرمانت را نپذیرند یا در امانت تو خیانت کنند. سپس رفتار کارگزاران را بررسی کن، و جاسوسانی راستگو، و وفا پیشه بر آنان بگمار، که مراقبت و بازرسی پنهانی تو از کار آنان، سبب امانت داری، و مهربانی با رعیت خواهد بود. و از همکاران نزدیکت سخت مراقبت کن، و اگر یکی از آنان دست به خیانت زد، و گزارش جاسوسان تو هم آن خیانت را تأیید کرد، به همین مقدار گواهی قناعت کرده او را با تازیانه کیفر کن، و آنچه از اموال که در اختیار دارد از او باز پس گیر، سپس او را خوار دار، و خیانتکار بشمار، و طوق بد نامی به گردنش بیفکن. \nچهارم سیمای مالیات دهندگان \nمالیات و بیت المال را به گونه‌ای وارسی کن که صلاح مالیات دهندگان باشد، زیرا بهبودی مالیات و مالیات دهندگان، عامل اصلاح امور دیگر اقشار جامعه می‌باشد، و تا امور مالیات دهندگان اصلاح نشود کار دیگران نیز سامان نخواهد گرفت زیرا همه مردم نان خور مالیات و مالیات دهندگانند. باید تلاش تو در آبادانی زمین بیشتر از جمع آوری خراج باشد که خراج جز با آبادانی فراهم نمی‌گردد، و آن کس که بخواهد خراج را بدون آبادانی مزارع به دست آورد، شهرها را خراب، و بندگان خدا را نابود، و حکومتش جز اندک مدتی دوام نیاورد. پس اگر مردم شکایت کردند، از سنگینی مالیات، یا آفت زدگی، یا خشک شدن آب چشمه‌ها، یا کمی باران، یا خراب شدن زمین در سیلاب‌ها، یا خشکسالی، در گرفتن مالیات به میزانی تخفیف ده تا امورشان سامان گیرد، و هرگز تخفیف دادن در خراج تو را نگران نسازد زیرا آن، اندوخته‌ای است که در آبادانی شهرهای تو، و آراستن ولایت‌های تو نقش دارد، و رعیت تو را می‌ستایند، و تو از گسترش عدالت میان مردم خشنود خواهی شد، و به افزایش قوت آنان تکیه خواهی کرد، بدانچه در نزدشان اندوختی و به آنان بخشیدی، و با گسترش عدالت در بین مردم، و مهربانی با رعیت، به آنان اطمینان خواهی داشت، آنگاه اگر در آینده کاری پیش آید و به عهده‌شان بگذاری، با شادمانی خواهند پذیرفت، زیرا عمران و آبادی، قدرت تحمل مردم را زیاد می‌کند. همانا ویرانی زمین به جهت تنگدستی کشاورزان است، و تنگدستی کشاورزان، به جهت غارت اموال از طرف زمامدارانی است که به آینده حکومتشان اعتماد ندارند، و از تاریخ گذشتگان عبرت نمی‌گیرند. \nپنجم سیمای نویسندگان و منشیان \nسپس در امور نویسندگان و منشیان به درستی بیندیش، و کارهایت را به بهترین آنان واگذار، و نامه‌های محرمانه، که در بر دارنده سیاست‌ها و اسرار تو است، از میان نویسندگان به کسی اختصاص ده که صالح‌تر از دیگران باشد، کسی که گرامی داشتن، او را به سرکشی و تجاوز نکشاند تا در حضور دیگران با تو مخالفت کند، و در رساندن نامه کار گزارانت به تو، یا رساندن پاسخ‌های تو به آنان کوتاهی نکند، و در آنچه برای تو می‌ستاند یا از طرف تو به آنان تحویل می‌دهد، فراموش کار نباشد. و در تنظیم هیچ قراردادی سستی نورزد، و در برهم زدن قراردادی که به زیان توست کوتاهی نکند، و منزلت و قدر خویش را بشناسد، همانا آن که از شناخت قدر خویش عاجز باشد، در شناخت قدر دیگران جاهل‌تر است. مبادا در گزینش نویسندگان و منشیان، بر تیز هوشی و اطمینان شخصی و خوش باوری خود تکیه نمایی، زیرا افراد زیرک با ظاهر سازی و خوش خدمتی، نظر زمامداران را به خود جلب می‌نمایند، که در پس این ظاهر سازی‌ها، نه خیرخواهی وجود دارد، و نه از امانت داری نشانی یافت می‌شود لکن آن‌ها را با خدماتی که برای زمامداران شایسته و پیشین انجام داده‌اند بیازمای، به کاتبان و نویسندگانی اعتماد داشته باش که در میان مردم آثاری نیکو گذاشته، و به امانت داری از همه مشهورترند، که چنین انتخاب درستی نشان دهنده خیرخواهی تو برای خدا، و مردمی است که حاکم آنانی. برای هر یک از کارهایت سرپرستی برگزین که بزرگی کار بر او چیرگی نیابد، و فراوانی کار او را در مانده نسازد، و بدان که هر گاه در کار نویسندگان و منشیان تو کمبودی وجود داشته باشد که تو بی‌خبر باشی خطرات آن دامنگیر تو خواهد بود. \nششم سیمای بازرگانان و صاحبان صنایع \nسپس سفارش مرا به بازرگانان و صاحبان صنایع بپذیر، و آن‌ها را به نیکوکاری سفارش کن، بازرگانانی که در شهر ساکنند، یا آنان که همواره در سیر و کوچ کردن می‌باشند، و بازرگانانی که با نیروی جسمانی کار می‌کنند، چرا که آنان منابع اصلی منفعت، و پدید آورندگان وسایل زندگی و آسایش، و آوردندگان وسایل زندگی از نقاط دور دست و دشوار می‌باشند، از بیابان‌ها و دریاها، و دشت‌ها و کوهستان‌ها، جاهای سختی که مردم در آن اجتماع نمی‌کنند، یا برای رفتن به آنجاها شجاعت ندارند. بازرگانان مردمی آرامند، و از ستیزه جویی آنان ترسی وجود نخواهد داشت، مردمی آشتی طلبند که فتنه انگیزی ندارند. در کار آن‌ها بیندیش چه در شهری باشند که تو به سر می‌بری، یا در شهرهای دیگر، با توجه به آنچه که تذکر دادم. این را هم بدان که در میان بازرگانان، کسانی هم هستند که تنگ نظر و بد معامله و بخیل و احتکار کننده‌اند، که تنها با زورگویی به سود خود می‌اندیشند. و کالا را به هر قیمتی که می‌خواهند می‌فروشند، که این سود جویی و گران فروشی برای همه افراد جامعه زیانبار، و عیب بزرگی بر زمامدار است. پس، از احتکار کالا جلوگیری کن، که رسول خدا صلی الله علیه و آله و سلم از آن جلوگیری می‌کرد، باید خرید و فروش در جامعه اسلامی، به سادگی و با موازین عدالت انجام گیرد، با نرخ‌هایی که بر فروشنده و خریدار زیانی نرساند، کسی که پس از منع تو احتکار کند، او را کیفر ده تا عبرت دیگران شود، اما در کیفر او اسراف نکن. \nهفتم سیمای محرومان و مستضعفان \nسپس خدا را خدا را در خصوص طبقات پایین و محروم جامعه، که هیچ چاره‌ای ندارند، >و عبارتند< از زمین گیران، نیازمندان، گرفتاران، دردمندان. همانا در این طبقه محروم گروهی خویشتن داری کرده، و گروهی به گدایی دست نیاز بر می‌دارند، پس برای خدا پاسدار حقی باش که خداوند برای این طبقه معین فرموده است: بخشی از بیت المال، و بخشی از غله‌های زمین‌های غنیمتی اسلام را در هر شهری به طبقات پایین اختصاص ده، زیرا برای دورترین مسلمانان همانند نزدیک‌ترین آنان سهمی مساوی وجود دارد و تو مسئول رعایت آن می‌باشی. مبادا سر مستی حکومت تو را از رسیدگی به آنان باز دارد، که هرگز انجام کارهای فراوان و مهم عذری برای ترک مسئولیت‌های کوچک‌تر نخواهد بود. همواره در فکر مشکلات آنان باش، و از آنان روی بر مگردان، به ویژه امور کسانی را از آنان بیشتر رسیدگی کن که از کوچکی به چشم نمی‌آیند و دیگران آنان را کوچک می‌شمارند و کمتر به تو دسترسی دارند. برای این گروه، از افراد مورد اطمینان خود که خدا ترس و فروتنند فردی را انتخاب کن، تا پیرامونشان تحقیق و مسائل آنان را به تو گزارش کنند. سپس در رفع مشکلاتشان به گونه‌ای عمل کن که در پیشگاه خدا عذری داشته باشی، زیرا این گروه در میان رعیت بیشتر از دیگران به عدالت نیازمندند، و حق آنان را به گونه‌ای بپرداز که در نزد خدا معذور باشی، از یتیمان خردسال، و پیران سالخورده که راه چاره‌ای ندارند. و دست نیاز بر نمی‌دارند، پیوسته دلجویی کن که مسئولیتی سنگین بر دوش زمامداران است، اگر چه حق، تمامش سنگین است اما خدا آن را بر مردمی آسان می‌کند که آخرت می‌طلبند، نفس را به شکیبایی وا می‌دارند، و به وعده‌های پروردگار اطمینان دارند. پس بخشی از وقت خود را به کسانی اختصاص ده که به تو نیاز دارند، تا شخصا به امور آنان رسیدگی کنی، و در مجلس عمومی با آنان بنشین و در برابر خدایی که تو را آفریده فروتن باش، و سربازان و یاران و نگهبانان خود را از سر راهشان دور کن تا سخنگوی آنان بدون اضطراب در سخن گفتن با تو گفتگو کند، من از رسول خدا صلی الله علیه و آله و سلم بارها شنیدم که می‌فرمود: «ملتی که حق ناتوانان را از زورمندان، بی اضطراب و بهانه‌ای باز نستاند، رستگار نخواهد شد» پس درشتی و سخنان ناهموار آنان را بر خود هموار کن، و تنگ خویی و خود بزرگ بینی را از خود دور ساز تا خدا درهای رحمت خود را به روی تو بگشاید، و تو را پاداش اطاعت ببخشاید، آنچه به مردم می‌بخشی بر تو گوارا باشد، و اگر چیزی را از کسی باز می‌داری با مهربانی و پوزش خواهی همراه باشد. \n۹- اخلاق اختصاصی رهبری \nبخشی از کارها به گونه‌ای است که خود باید انجام دهی، مانند پاسخ دادن به کارگزاران دولتی، در آنجا که منشیان تو از پاسخ دادن به آن‌ها درمانده‌اند، و دیگر، بر آوردن نیاز مردم در همان روزی که به تو عرضه می‌دارند، و یارانت در رفع نیاز آنان ناتوانند، کار هر روز را در همان روز انجام ده، زیرا هر روزی، کاری مخصوص به خود دارد. نیکوترین وقت‌ها و بهترین ساعات شب و روزت را برای خود و خدای خود انتخاب کن، اگر چه همه وقت برای خداست، اگر نیت درست و رعیت در آسایش قرار داشته باشد. از کارهایی که به خدا اختصاص دارد و باید با اخلاص انجام دهی، انجام واجباتی است که ویژه پروردگار است، پس در بخشی از شب و روز، وجود خود را به پرستش خدا اختصاص ده، و آنچه تو را به خدا نزدیک می‌کند بی‌عیب و نقصانی انجام ده، اگر چه دچار خستگی جسم شوی. هنگامی که نماز به جماعت می‌خوانی، نه با طولانی کردن نماز، مردم را بپراکن و نه آن که آن را تباه سازی، زیرا در میان مردم، بیمار یا صاحب حاجتی وجود دارد. آنگاه که پیامبر صلی الله علیه و آله و سلم مرا به یمن می‌فرستاد از او پرسیدم، با مردم چگونه نماز بخوانم فرمود: «در حد توان ناتوانان نماز بگذار و بر مؤمنان مهربان باش» هیچ گاه خود را فراوان از مردم پنهان مدار، که پنهان بودن رهبران، نمونه‌ای از تنگ خویی و کم اطلاعی در امور جامعه می‌باشد. نهان شدن از رعیت، زمامداران را از دانستن آنچه بر آنان پوشیده است باز می‌دارد، پس کار بزرگ، اندک، و کار اندک بزرگ جلوه می‌کند، زیبا زشت، و زشت زیبا می‌نماید، و باطل به لباس حق در آید. همانا زمامدار، آنچه را که مردم از او پوشیده دارند نمی‌داند، و حق را نیز نشانه‌ای نباشد تا با آن راست از دروغ شناخته شود، و تو به هر حال یکی از آن دو نفر می‌باشی: یا خود را برای جانبازی در راه حق آماده کرده‌ای که در این حال، نسبت به حق واجبی که باید بپردازی یا کار نیکی که باید انجام دهی ترسی نداری، پس چرا خود را پنهان می‌داری و یا مردی بخیل و تنگ نظری، که در این صورت نیز مردم چون تو را بنگرند مأیوس شده از درخواست کردن باز مانند. با اینکه بسیاری از نیازمندی‌های مردم رنجی برای تو نخواهد داشت، که شکایت از ستم دارند یا خواستار عدالتند، یا در خرید و فروش خواهان انصافند. \n۱۰- اخلاق رهبری با خویشاوندان \nهمانا زمامداران را خواص و نزدیکانی است که خود خواه و چپاولگرند، و در معاملات انصاف ندارند، ریشه ستمکاریشان را با بریدن اسباب آن بخشکان، و به هیچ کدام از اطرافیان و خویشاوندانت زمین را واگذار مکن «»، و به گونه‌ای با آنان رفتار کن که قرار دادی به سودشان منعقد نگردد که به مردم زیان رساند، مانند آبیاری مزارع، یا زراعت مشترک، که هزینه‌های آن را بر دیگران تحمیل کنند، در آن صورت سودش برای آنان، و عیب و ننگش در دنیا و آخرت برای تو خواهد ماند. حق را به صاحب حق، هر کس که باشد، نزدیک یا دور، بپرداز، و در این کار شکیبا باش، و این شکیبایی را به حساب خدا بگذار، گر چه اجرای حق مشکلاتی برای نزدیکانت فراهم آورد، تحمل سنگینی آن را به یاد قیامت بر خود هموار ساز. و هر گاه رعیت بر تو بد گمان گردد، عذر خویش را آشکارا با آنان در میان بگذار، و با این کار از بدگمانی نجاتشان ده، که این کار ریاضتی برای خود سازی تو، و مهربانی کردن نسبت به رعیت است، و این پوزش خواهی تو آنان را به حق وامی‌دارد. \n۱۱- روش برخورد با دشمن \nهرگز پیشنهاد صلح از طرف دشمن را که خشنودی خدا در آن است رد مکن، که آسایش رزمندگان، و آرامش فکری تو، و امنیت کشور در صلح تأمین می‌گردد. لکن زنهار زنهار از دشمن خود پس از آشتی کردن، زیرا گاهی دشمن نزدیک می‌شود تا غافلگیر کند، پس دور اندیش باش، و خوشبینی خود را متهم کن. حال اگر پیمانی بین تو و دشمن منعقد گردید، یا در پناه خود او را امان دادی، به عهد خویش وفا دار باش، و بر آنچه بر عهده گرفتی امانت دار باش، و جان خود را سپر پیمان خود گردان، زیرا هیچ یک از واجبات الهی همانند وفای به عهد نیست. که همه مردم جهان با تمام اختلافاتی که در افکار و تمایلات دارند، در آن اتفاق نظر داشته باشند. تا آنجا که مشرکین زمان جاهلیت به عهد و پیمانی که با مسلمانان داشتند وفادار بودند، زیرا که آینده ناگوار پیمان شکنی را آزمودند. پس هرگز پیمان شکن مباش، و در عهد خود خیانت مکن، و دشمن را فریب مده، زیرا کسی جز نادان بدکار، بر خدا گستاخی روا نمی‌دارد، خداوند عهد و پیمانی که با نام او شکل می‌گیرد با رحمت خود مایه آسایش بندگان، و پناهگاه امنی برای پناه آورندگان قرار داده است، تا همگان به حریم أمن آن روی بیاورند. پس فساد، خیانت، فریب، در عهد و پیمان راه ندارد. مبادا قراردادی را امضاء کنی که در آن برای دغلکاری و فریب راه‌هایی وجود دارد، و پس از محکم کاری و دقت در قرار داد نامه، دست از بهانه جویی بردار، مبادا مشکلات پیمانی که بر عهده‌ات قرار گرفته، و خدا آن را بر گردنت نهاده، تو را به پیمان شکنی وا دارد، زیرا شکیبایی تو در مشکلات پیمان‌ها که امید پیروزی در آینده را به همراه دارد، بهتر از پیمان شکنی است که از کیفر آن می‌ترسی، و در دنیا و آخرت نمی‌توانی پاسخ گوی پیمان شکنی باشی. \n۱۲- هشدارها \nاول- هشدار از خون ناحق \nاز خونریزی بپرهیز، و از خون ناحق پروا کن، که هیچ چیز همانند خون ناحق کیفر الهی را نزدیک مجازات را بزرگ نمی‌کند، و نابودی نعمت‌ها را سرعت نمی‌بخشد و زوال حکومت را نزدیک نمی‌گرداند، و روز قیامت خدای سبحان قبل از رسیدگی اعمال بندگان، نسبت به خون‌های ناحق ریخته شده داوری خواهد کرد، پس با ریختن خونی حرام، حکومت خود را تقویت مکن. زیرا خون ناحق، پایه‌های حکومت را سست، و پست می‌کند و بنیاد آن را بر کنده به دیگری منتقل سازد، و تو، نه در نزد من، و نه در پیشگاه خداوند، عذری در خون ناحق نخواهی داشت چرا که کیفر آن قصاص است و از آن گریزی نیست. اگر به خطا خون کسی ریختی، یا تازیانه یا شمشیر، یا دستت دچار تند روی شد، - که گاه مشتی سبب کشتن کسی می‌گردد، چه رسد به بیش از آن- مبادا غرور قدرت تو را از پرداخت خونبها به بازماندگان مقتول باز دارد. \nدوم- هشدار از خود پسندی \nمبادا هرگز دچار خود پسندی گردی و به خوبی‌های خود اطمینان کنی، و ستایش را دوست داشته باشی، که اینها همه از بهترین فرصت‌های شیطان برای هجوم آوردن به توست، و کردار نیک، نیکوکاران را نابود سازد. \nسوم- هشدار از منت گذاری \nمبادا هرگز با خدمت‌هایی که انجام دادی بر مردم منت گذاری، یا آنچه را انجام داده‌ای بزرگ بشماری، یا مردم را وعده‌ای داده، سپس خلف وعده نمایی منت نهادن، پاداش نیکوکاری را از بین می‌برد، و کاری را بزرگ شمردن، نور حق را خاموش گرداند، و خلاف وعده عمل کردن، خشم خدا و مردم را بر می‌انگیزاند که خدای بزرگ فرمود: «دشمنی بزرگ نزد خدا آن که بگویید و عمل نکنید» \nچهارم- هشدار از شتابزدگی \nمبادا هرگز در کاری که وقت آن فرا نرسیده شتاب کنی، یا کاری که وقت آن رسیده سستی ورزی، و یا در چیزی که (حقیقت آن) روشن نیست ستیزه جویی نمایی و یا در کارهای واضح و آشکار کوتاهی کنی تلاش کن تا هر کاری را در جای خود، و در زمان مخصوص به خود، انجام دهی. \nپنجم- هشدار از امتیاز خواهی \nمبادا هرگز در آنچه که با مردم مساوی هستی امتیازی خواهی از اموری که بر همه روشن است، غفلت کنی، زیرا به هر حال نسبت به آن در برابر مردم مسئولی، و به زودی پرده از کارها یک سو رود، و انتقام ستمدیده را از تو باز می‌گیرند. باد غرورت، جوشش خشمت، تجاوز دستت، تندی زبانت را در اختیار خود گیر، و با پرهیز از شتابزدگی، و فروخوردن خشم، خود را آرامش ده تا خشم فرو نشیند و اختیار نفس در دست تو باشد. و تو بر نفس مسلط نخواهی شد مگر با یاد فراوان قیامت، و بازگشت به سوی خدا. آنچه بر تو لازم است آن که حکومت‌های دادگستر پیشین، سنت‌های با ارزش گذشتگان، روش‌های پسندیده رفتگان، و آثار پیامبر صلی الله علیه و آله و سلم و واجباتی که در کتاب خداست، را همواره به یاد آوری، و به آنچه ما عمل کرده‌ایم پیروی کنی، و برای پیروی از فرامین این عهد نامه‌ای که برای تو نوشته‌ام، و با آن حجت را بر تو تمام کرده‌ام، تلاش کن، زیرا اگر نفس سرکشی کرد و بر تو چیره شد عذری نزد من نداشته باشی. از خداوند بزرگ، با رحمت گسترده، و قدرت برترش در انجام تمام خواسته‌ها، درخواست می‌کنیم که به آنچه موجب خشنودی اوست ما و تو را موفق فرماید، که نزد او و خلق او، دارای عذری روشن باشیم، برخوردار از ستایش بندگان، یادگار نیک در شهرها، رسیدن به همه نعمت‌ها، و کرامت‌ها بوده، و اینکه پایان عمر من و تو را به شهادت و رستگاری ختم فرماید، که همانا ما به سوی او باز می‌گردیم. با درود به پیامبر اسلام صلی الله علیه و آله و سلم و اهل بیت پاکیزه و پاک او، درودی فراوان و پیوسته. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (291, 2, 54, 'به طلحه و زبیر', 'نامه ۵۴-به طلحه و زبیر \nپاسخ به ادعاهای سران جمل \nپس از یاد خدا و درود! \nشما می‌دانید گرچه پنهان می‌دارید. که من برای حکومت در پی مردم نرفته، آنان به سوی من آمدند، و من قول بیعت نداده تا آنکه آنان با من بیعت کردند، و شما دو نفر از کسانی بودید که مرا خواستید و بیعت کردید. همانا بیعت عموم مردم با من نه از ترس قدرتی مسلط بود، و نه برای به دست آوردن متاع دنیا، اگر شما دو نفر از روی میل و انتخاب بیعت کردید تا دیرنشده باز گردید، و در پیشگاه خدا توبه کنید، و اگر در دل با اکراه بیعت کردید خود دانید، زیرا این شما بودید که مرا در حکومت بر خویش راه دادید، اطاعت از من را ظاهر، و نافرمانی را پنهان داشتید. به جان خودم سوگند! شما از سایر مهاجران سزاوارتر به پنهان داشتن عقیده و پنهان کاری نیستید، اگر در آغاز بیعت کنار می‌رفتید آسان تر بود که بیعت کنید و سپس به بهانه سر باز زنید. شما پنداشته اید که من کشنده عثمان می‌باشم، بیایید تا مردم مدینه بین من و شما داوری کنند، آنان که نه به طرفداری من برخاستند نه شما، سپس هر کدام به اندازه جرمی که در آن حادثه داشته، مسوولیت آنرا پذیرا باشد. ای دو پیرمرد، از آن چه در اندیشه دارید باز گردید، هم اکنون بزرگترین مسئله شما عار است، پیش از آنکه عار و آتش خشم پروردگار دامنگیرتان گردد. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (292, 2, 55, 'به معاویه', 'نامه ۵۵-به معاویه \nاندرز دادن به دشمن \nپس از یاد خدا و درود! \nهمانا خداوند سبحان دنیا را برای آخرت قرار داده، و مردم را در دنیا به آزمایش گذاشت، تا روشن شود کدام یک نیکوکارتر است، ما را برای دنیا نیافریده اند، و تنها برای دنیا به تلاش فرمان داده نشدیم، به دنیا آمدیم تا در آن آزمایش گردیم. و همانا خداوند مرا به تو، و تو را با من آزموده، و یکی از ما را بر دیگری حجت قرار داد، تو با تفسیر دروغین قرآن به دنیا روی آوردی، و چیزی از من درخواست می‌کنی که دست و زبانم هرگز به آن نیالود (قتل عثمان) تو و مردم شام، آن دروغ را ساختید و به من تهمت زدید تا آگاهان شما مردم ناآگاه را، و ایستادگان شما زمینگیرشدگان را بر ضد من تحریک می‌کنند. معاویه! از خدا بترس! و با شیطانی که مهار تو را می‌کشد، درآویز، و به سوی آخرت که راه من و تو است باز گرد، و بترس از خدا که بزودی با بلایی کوبنده ریشه ات را برکند، و نسل تو را قطع نماید. همانا برای تو به خدا سوگند می‌خورم، سوگندی که بر آن وفا دارم، اگر روزگار من و تو را در یکجا گرد آورد، هم چنان بر سر راه تو خواهم ماند. (تا خدا میان ما داوری کند و او بهترین داوران است.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (293, 2, 56, 'به شریح بن‌هانی', 'نامه ۵۶-به شریح بن‌هانی \nاخلاق فرماندهی \nدر هر صبح و شام از خدا بترس، و از فریبکاری دنیا بر نفس خویش بیمناک باش، و هیچگاه از دنیا ایمن مباش، بدان اگر برای چیزهایی که دوست می‌داری، یا آنچه را که خوشایند تو نیست، خود را باز نداری، هوسها تو را به زیانهای فراوانی خواهند کشید، سپس نفس خود را باز دار و از آن نگهبانی کن، و به هنگام خشم، بر نفس خویش شکننده و حاکم باش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (294, 2, 57, 'به مردم کوفه', 'نامه ۵۷-به مردم کوفه \nروش بسیج کردن مردم برای جهاد \nپس از یاد خدا و درود! \nمن از جایگاه خود مدینه بیرون آمدم، یا ستمکارم یا ستم دیده، یا سرکشی کردم یا از فرمانم سر باز زدند، همانا من خدا را به یاد کسی می‌آورم که این نامه به دست او رسد، تا به سوی من کوچ کند، اگر مرا نیکوکار یافت یاری کند، و اگر گناهکار بودم مرا به حق باز گرداند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (295, 2, 58, 'به مردم شهرها درباره جنگ صفین', 'نامه ۵۸-درباره جنگ صفین \nافشای حوادث جنگ صفین \nآغاز کار چنین بود ما با مردم شام دیدار کردیم، که در ظاهر پروردگار ما یکی، و پیامبر ما یکی، و دعوت ما در اسلام یکی بود، و در ایمان به خدا و تصدیق کردن پیامبرش، هیچ کدام از ما بر دیگری برتری نداشتیم، و با هم وحدت داشتیم جز در خون عثمان که ما از آن برکناریم، پس به آنان گفتیم: بیایید با خاموش ساختن آتش جنگ، و آرام کردن مردم، به چاره جویی و درمان بپردازیم، تا کار مسلمانان استوار شود، و به وحدت برسند، و ما برای اجرای عدالت نیرومند شویم، اما شامیان پاسخ دادند: (چاره ای جز جنگ نداریم.) پس سر باز زدند، و جنگ درگرفت، و تداوم یافت، و آتش آن زبانه کشید. پس آنگاه که دندان جنگ در ما و آنان فرو رفت، و چنگال آن سخت کارگر افتاد، به دعوت ما گردن نهادند، و بر آنچه آنان را خواندیم، پاسخ دادند. ما هم به درخواست آنان پاسخ دادیم، و آنچه را خواستند زود پذیرفتیم، تا حجت را بر آنان تمام کنیم، و راه عذرخواهی را ببندیم. آنگاه آنکه بر پیمان خود استوار ماند، از هلاکت نجات یافت، و آن کس که در لجاجت خود پا فشرد، خدا پرده ناآگاهی بر جان او کشید، و بلای تیره روزی گرد سرش گردانید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (296, 2, 59, 'به اسود بن قطبه', 'نامه ۵۹-به اسود بن قطبه \nمسوولیتهای فرماندهی \nپس از یاد خدا و درود. \nاگر رای و اندیشه زمامدار دچار دگرگونی شود، او را از اجرای عدالت بسیار باز می‌دارد، پس کار مردم در آنچه حق است نزد تو یکسان باشد. زیرا در ستمکاری بهایی برای عدالت یافت نمی شود، از آنچه که همانند آن را بر دیگران نمی پسندی پرهیز کن، و نفس خود را در آنچه خداوند بر تو واجب کرده است، وادار، در حالی که امیدوار به پاداش الهی بوده و از کیفر او هراسناک باشی. و بدان که دنیا سرای آزمایش است، و دنیاپرست ساعتی در آن نمی آساید جز آنکه در روز قیامت از آن افسوس می‌خورد، و هرگز چیزی تو را از حق بی نیاز نمی گرداند. و از جمله حقی که بر توست آن که، نفس خویش را نگهبان باشی، و به اندازه توان در امور رعیت تلاش کنی، زیرا آنچه در این راه نصیب تو می‌شود، برتر از آن است که از نیروی بدنی خود از دست می‌دهی، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (297, 2, 6, 'به معاویه', 'نامه ۶- به معاویه \nعلل مشروعیت حکومت امام (ع) \nهمانا کسانی با من بیعت کرده‌اند که با ابابکر و عمر و عثمان، با همان شرایط بیعت نمودند، پس آنکه در بیعت حضور داشت نمی تواند خلیفه ای دیگر برگزیند، و آنکه غایب است نمی تواند بیعت مردم را نپذیرد، و همانا شورای مسلمین از آن مهاجرین و انصار است، پس اگر بر امامت کسی گرد آمدند، و او را امام خود خواندند، خشنودی خدا هم در آن است. حال اگر کسی کار آنان را نکوهش کند یا بدعتی پدید آورد، او را به جایگاه بیعت قانونی باز می‌گردانند، اگر سر باز زد با او پیکار می‌کنند، زیرا که به راه مسلمانان درنیامده، خدا هم او را در گمراهیش وامی گذارد. بجانم سوگند! ای معاویه اگر دور از هوای نفس، به دیده عقل بنگری، خواهی دید که من نسبت به خون عثمان پاک ترین افرادم، و می‌دانی که من از آن دور بوده ام، جز اینکه از راه خیانت مرا متهم کنی، و حق آشکاری را بپوشانی، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (298, 2, 60, 'به فرماندارانی که ارتش از قلمرو آنان می‌گذرد', 'نامه ۶۰-به فرماندارانی که ارتش... \nجبران خسارتها در مانورهای نظامی \nاز بنده خدا! علی امیر مومنان به گردآوران مالیات و فرمانداران شهرهایی که لشکریان از سرزمین آنان می‌گذرند. پس از یاد خدا و درود! همانا من سپاهیانی فرستادم که به خواست خدا بر شما خواهند گذشت، و آنچه خدا بر آنان واجب کرده به ایشان سفارش کردم، و بر آزار نرساندن به دیگران، و پرهیز از هرگونه شرارتی تاکید کرده ام، و من نزد شما و پیمانی که با شما دارم از آزار رساندن سپاهیان به مردم بیزارم، مگر آنکه گرسنگی سربازی را ناچار گرداند، و برای رفع گرسنگی چاره ای جز آن نداشته باشد، پس کسی را که دست به ستمکاری زند کیفر کنید، و دست افراد سبک مغز خود را از زیان رساندن به لشکریان، و زحمت دادن آن‌ها جز در آنچه استثنا کردم باز دارید. من پشت سر سپاه در حرکتم، شکایتهای خود را به من رسانید، و در اموری که لشکریان بر شما چیره شده‌اند که قدرت دفع آن را جز با کمک خدا و من ندارید، به من مراجعه کنید، که با کمک خداوند آن را برطرف خواهم کرد. ان شاء الله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (299, 2, 61, 'به کمیل بن زیاد', 'نامه ۶۱-به کمیل بن زیاد \nنکوهش از فرمانده شکست خورده \nپس از یاد خدا و درود! \nسستی انسان در انجام کارهایی که بر عهده اوست، و پافشاری در کاری که از مسولیت او خارج است، نشانه ناتوانی آشکار، و اندیشه ویرانگر است. اقدام تو به تاراج مردم (قرقیسا) در مقابل رها کردن پاسداری از مرزهایی که تو را بر آن گمارده بودیم و کسی در آنجا نیست تا آنجا را حفظ کند، و سپاه دشمن را از آن مرزها دور سازد، اندیشه ای باطل است. تو در آنجا پلی شده ای که دشمنان تو از آن بگذرند و بر دوستانت تهاجم آورند، نه قدرتی داری که با تو نبرد کنند، و نه هیبتی داری از تو بترسند و بگریزند، نه مرزی را می‌توانی حفظ کنی، و نه شوکت دشمن را می‌توانی درهم بشکنی، نه نیازهای مردم دیارت را کفایت می‌کنی، و نه امام خود را راضی نگه می‌داری.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (300, 2, 62, 'به مردم مصر', 'نامه ۶۲-به مردم مصر \nمظلومیت امام در خلافت \nپس از یاد خدا و درود! \nخداوند سبحان محمد (ص) را فرستاد تا بیم دهنده جهانیان، و گواه پیامبران پیش از خود باشد، آنگاه که پیامبر (ص) به سوی خدا رفت، مسلمانان پس از وی در کار حکومت با یکدیگر درگیر شدند، سوگند به خدا نه در فکرم می‌گذشت، و نه در خاطرم می‌آمد که عرب خلافت را پس از رسول خدا (ص) از اهل بیت او بگرداند، یا مرا پس از وی از عهده دار شدن حکومت باز دارند، تنها چیزی که نگرانم کرد شتافتن مردم به سوی فلان شخص بود که با او بیعت کردند. من دست باز کشیدم، تا آنجا که دیدم گروهی از اسلام باز گشته، می‌خواهند دین محمد (ص) را نابود سازند، پس ترسیدم که اگر اسلام و طرفدارانش را یاری نکنم، رخنه ای در آن بینم یا شاهد نابودی آن باشم، که مصیبت آن بر من سخت تر از رها کردن حکومت بر شماست، که کالای چند روزه دنیاست، به زودی ایام آن می‌گذرد چنانکه سراب ناپدید شود، یا چونان پاره‌های ابر که زود پراکنده می‌گردد. پس در میان آن آشوب و غوغا بپا خواستم تا آنکه باطل از میان رفت، و دین استقرار یافته، آرام شد. \n(بخشی از همین نامه است:) \nشجاعت و دشمن شناسی امام (ع) \nبه خدا سوگند! اگر تنها با دشمنان روبرو شوم، در حالی که آنان تمام روی زمین را پر کرده باشند، نه باکی داشته، و نه می‌هراسم، من گمراهی آنان و هدایت خود را که بر آن استوارم، آگاهم، و از طرف پروردگارم به یقین رسیده ام، و همانا من برای ملاقات پروردگار مشتاق، و به پاداش او امیدوارم. لکن از این اندوهناکم که بیخردان، و تبهکاران این امت حکومت را به دست آورند، آنگاه مال خدا را دست به دست بگردانند، و بندگان او را به بردگی کشند، با نیکوکاران در جنگ، و با فاسقان همراه باشند، زیرا از آنان کسی در میان شماست که شراب نوشید و حد بر او جاری شد، و کسی که اسلام را نپذیرفت اما به ناحق بخششهایی به او عطا گردید. اگر اینگونه حوادث نبود شما را برنمی انگیختم، و سرزنشتان نمی کردم، و شما را به گردآوری تشویق نمی نمودم، و آنگاه که سر باز می‌زدید رهاتان می‌کردم. آیا نمی بینید که مرزهای شما را تصرف کردند؟ و شهرها را گشودند؟ و دستاوردهای شما را غارت کردند؟ و در میان شهرهای شما آتش جنگ را برافروختند؟ برای جهاد با دشمنان کوچ کنید. خدا شما را رحمت کند، در خانه‌های خود نمانید، که به ستم گرفتار، و به خواری دچار خواهید شد، و بهره زندگی شما از همه پست تر خواهد بود، و همانا برادر جنگ، بیداری و هوشیاری است هر آن کس که به خواب رود، دشمن او نخواهد خوابید. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (301, 2, 63, 'به ابوموسی اشعری', 'نامه ۶۳-به ابوموسی اشعری \nبازداشتن ابوموسی از فتنه انگیزی \nاز بنده خدا علی امیر مومنان به عبدالله بن قیس (ابوموسی اشعری)\n پس از ستایش پروردگار! \nسخنی از تو به من رسید که هم به سود، و هم به زیان تو است، چون فرستاده من پیش تو آید، دامن همت کمر زن، کمرت را برای جنگ محکم ببند، و از سوراخ بیرون آی، و مردم را برای جنگ بسیج کن، اگر حق را در من دیدی بپذیر، و اگر دودل ماندی کناره گیر، به خدا سوگند هر جا که باشی تو را بیاورند و بحال خویش رها نکنند، تا گوشت و استخوان و تر و خشکت درهم ریزد، و در کنار زدنت از حکومت شتاب کنند، چنانکه از پیش روی خود همانگونه بترسی که از پشت سرت هراسناکی. حوادث جاری کشور آنچنان آسان نیست که تو فکر می‌کنی، بلکه حادثه بسیار بزرگی است که باید بر مرکبش سوار شد، و سختی‌های آن را هموار کرد، و پیمودن راه‌های سخت و کوهستانی آن را آسان نمود، پس فکرت را بکار گیر، و مالک کار خویش باش، و سهم و بهره ات را بردار، اگر همراهی با ما را خوش نداری کناره گیر، بی آنکه مورد ستایش قرارگیری یا رستگار شوی، که سزاوار است تو در خواب باشی و دیگران مسوولیتهای تو را برآورند، و از تو نپرسند که کجا هستی؟ و به کجا رفته ای؟ به خدا سوگند! این راه حق است و به دست مرد حق انجام می‌گیرد، و باکی ندارم که خداشناسان چه می‌کنند؟ با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (302, 2, 64, 'به معاویه', 'نامه ۶۴-به معاویه \nپاسخ تهدیدات نظامی معاویه \nپس از یاد خدا! \nچنانکه یادآور شدی، ما و شما دوست بودیم و هم خویشاوند، اما دیروز میان ما و شما بدان جهت جدایی افتاد که ما ایمان آوردیم و شما کافر شدید، و امروز ما در اسلام استوار ماندیم و شما آزمایش گردیدید، اسلام آوردگان شما با ناخشنودی، آن هم زمانی به اسلام روی آوردند که بزرگان عرب تسلیم رسول خدا (ص) شدند، و در گروه او قرار گرفتند. در نامه ات نوشتی که طلحه و زبیر را کشته، و عایشه را تبعید کرده ام، و در کوفه و بصره منزل گزیدم، این امور ربطی به تو ندارد، و لازم نیست از تو عذر بخواهم. و نوشتی که با گروهی از مهاجران و انصار به نبرد من می‌آیی، هجرت از روزی که برادرت (یزید) در فتح مکه اسیر شد پایان یافت، پس اگر در ملاقات با من شتاب داری، دست نگهدار، زیرا اگر من به دیدار تو بیایم سزاوارتر است، که خدا مرا به سوی تو فرستاده تا از تو انتقام گیرم، و اگر تو با من دیدار کنی چنان است که شاعر اسدی گفت: (تندباد تابستانی سخت می‌وزد و آن‌ها را با سنگ ریزه‌ها، و در میان غبار و تخته سنگها، درهم می‌کوبد.) \nیادآوری سوابق نظامی در پیروزیها \nو در نزد من همان شمشیری است که در جنگ بدر بر پیکر جد و دایی و برادرت زدم. به خدا سوگند! می‌دانم تو مردی بی خرد و دل تاریک هستی، بهتر است درباره تو گفته شود از نردبانی بالا رفته ای که تو را به پرتگاه خطرناکی کشانده، و سودی برای تو نداشته، زیانبار است، زیرا تو غیر گمشده خود را می‌جویی، و غیر گله خود را می‌چرانی. منصبی را می‌خواهی که سزاوار آن نبوده، و در شان تو نیست، چقدر گفتار تو با کردارت فاصله دارد؟ چقدر به عموها و دایی‌هایت شباهت داری، شقاوت و آرزوی باطل آن‌ها را به انکار نبوت محمد (ص) واداشت، و چنانکه می‌دانی در گورهای خود غلطیدند، نه برابر مرگ توانستند دفاع کنند، و نه آنگونه که سزاوار بود از حریمی حمایت، و نه در برابر زخم شمشیرها خود را حفظ کردند، که شمشیرها در می‌دان جنگ فراوان، و سستی در برابر آن شایسته نیست. تو درباره کشندگان عثمان فراوان حرف زدی، ابتدا چون دیگر مسلمانان با من بیعت کن، سپس درباره آنان از من داوری بطلب، که شما و مسلمانان را به پذیرفتن دستورات قرآن وا دارم، اما آنچه را که تو می‌خواهی، چنان است که به هنگام گرفتن کودک از شیر، او را بفریبند، سلام بر آنان که سزاوار سلامند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (303, 2, 65, 'به معاویه', 'نامه ۶۵-به معاویه \nافشای علل گمراهی معاویه \nپس از یاد خدا!\nمعاویه! وقت آن رسیده است که از حقائق آشکار پند گیری، تو با ادعاهای باطل همان راه پدرانت را می‌پیمایی، خود را در دروغ و فریب افکندی، و خود را به آنچه برتر از شان تواست نسبت می‌دهی، و به چیزی دست دراز می‌کنی که از تو باز داشته اند، و به تو نخواهد رسید، این همه را برای فرار کردن از حق، و انکار آنچه را که از گوشت و خون تو لازم تر است، انجام می‌دهی، حقائقی که گوش تو آن‌ها را شنیده و از آن‌ها آگاهی داری، آیا پس از روشن شدن راه حق، جز گمراهی آشکار چیز دیگری یافت خواهد شد؟ و آیا پس از بیان حق، جز اشتباه کاری وجود خواهد داشت؟ از شبهه و حق پوشی بپرهیز، فتنه‌ها دیر زمانی است که پرده‌های سیاه خود را گسترانده، و دیده‌هایی را کور کرده است. \nپاسخ به ادعاهای دروغین معاویه \nنامه ای از تو به دستم رسید که در سخن پردازی از هر جهت آراسته، اما از صلح و دوستی نشانه ای نداشت، و آکنده از افسانه‌هایی بود که هیچ نشانی از دانش و بردباری در آن بچشم نمی خورد، در نوشتن این نامه کسی را مانی که پای در گل فرو رفته، و در بیغوله‌ها سرگردان است، مقامی را می‌طلبی که از قدر و ارزش تو والاتر است، و هیچ عقابی را توان پرواز بر فراز آن نیست و چون ستاره دوردست (عیوق) از تو دور است. پناه بر خدا که پس از من ولایت مسلمانان را بر عهده گیری، و سود و زیان آن را بپذیری، یا برای تو با یکی از مسلمانان پیمانی یا قراردادی را امضا کنم. از هم اکنون خود را دریاب، و چاره ای بیندیش، که اگر کوتاهی کنی، و برای درهم کوبیدنت بندگان خدا برخیزند، درهای نجات بروی تو بسته خواهد شد، و آنچه را که امروز از تو می‌پذیرند فردا نخواهند پذیرفت، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (304, 2, 66, 'به عبدالله بن عباس', 'نامه ۶۶-به عبدالله بن عباس \nضرورت واقع بینی \nپس از یاد خدا و درود! \nهمانا انسان از به دست آوردن چیزی خشنود می‌شود که هرگز آن را از دست نخواهد داد، و برای چیزی اندوهناک است که هرگز به دست نخواهد آورد، پس بهترین چیز در نزد تو در دنیا، رسیدن به لذتها، یا انتقام گرفتن نباشد، بلکه باید خاموش کردن باطل، یا زنده کردن حق باشد، تنها به توشه ای که از پیش فرستادی خشنود باش، و بر آنچه بیهوده می‌گذاری حسرت خور، و همت و تلاش خود را برای پس از مرگ قرار ده.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (305, 2, 67, 'به قثم بن عباس فرماندار مکه', 'نامه ۶۷-به قثم بن عباس \nرسیدگی به امور حاجیان در مراسم حج \nپس از یاد خدا و درود! \nبرای مردم حج را به پای دار، و روزهای خدا را به یادشان آور، در بامداد و شامگاه در یک مجلس عمومی با مردم بنشین، آنان که پرسشهای دینی دارند با فتواها آشنایشان بگردان، و ناآگاه را آموزش ده، و با دانشمندان به گفتگو بپرداز، جز زبانت چیز دیگری پیام رسانت با مردم، و جز چهره ات دربانی وجود نداشته باشد، و هیچ نیازمندی را از دیدار خود محروم مگردان، زیرا اگر در آغاز از درگاه تو رانده شود، دیگر تو را نستاید گرچه در پایان حاجت او برآورده شود. در مصرف بیت المالی که در دست تو جمع شده است اندیشه کن، و آن را به عیالمندان و گرسنگان پیرامونت ببخش، و به مستمندان و نیازمندانی که سخت به کمک مالی تو احتیاج دارند برسان، و مازاد را نزد ما بفرست، تا در میان مردم نیازمندی که در این سامان هستند تقسیم گردد. \nسفارش به رفع نیازهای حجاج \nبه مردم مکه فرمان ده تا از هیچ زائری در ایام حج در مکه اجرت مسکن نگیرند، که خدای سبحان فرمود: (عاکف و بادی در مکه یکسانند.) عاکف، یعنی اهل مکه، و بادی، یعنی زائرانی که از دیگر شهرها به حج می‌آیند، خدا ما و شما را به آنچه دوست دارد توفیق عنایت فرماید، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (306, 2, 68, 'به سلمان فارسی', 'نامه ۶۸-به سلمان فارسی \nروش برخورد با دنیا \nپس از یاد خدا و درود! \nدنیای حرام چونان مار است، که پوستی نرم، و زهری کشنده دارد، پس از جاذبه‌های فریبنده آن روی گردان، زیرا زمان کوتاهی در آن خواهی ماند، و اندوه آن را از سر بیرون کن، زیرا که یقین به جدایی آن، و دگرگونی حالات آن داری، و آنگاه که به دنیا خو گرفته ای بیشتر بترس، زیرا که دنیاپرست تا به خوشگذرانی در دنیا اطمینان کرد او را به تلخ کامی کشاند، و هرگاه که به دنیا انس گرفت و آسوده شد، ناگاه به وحشت دچار می‌گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (307, 2, 69, 'به حارث همدانی', 'نامه ۶۹-به حارث همدانی \nاخلاق کارگزاران حکومتی \nبه ریسمان قرآن چنگ زن، و از آن نصیحت پذیر، حلالش را حلال، و حرامش را حرام بشمار، و حقی را که در زندگی گذشتگان بود تصدیق کن، و از حوادث گذشته تاریخ برای آینده عبرت گیر، که حوادث روزگار با یکدیگر همانند بوده، و پایان دنیا به آغازش می‌پیوندد، و همه آن رفتنی است. نام خدا را بزرگ دار، و جز به حق بر زبان نیاور، مرگ و جهان پس از مرگ را فراوان به یاد آور، هرگز آرزوی مرگ مکن جز آنکه بدانی از نجات یافتگانی، از کاری که تو را خشنود، و عموم مسلمانان را ناخوشایند است بپرهیز، از هر کار پنهانی که در آشکار شدنش شرم داری پرهیز کن، از هر کاری که از کننده آن پرسش کنند، نپذیرد یا عذرخواهی کند، دوری کن، آبروی خود را آماج تیر گفتار دیگران قرار نده، و هر چه شنیدی بازگو مکن، که نشانه دروغگویی است و هر خبری را دروغ مپندار، که نشانه نادانی است. خشم را فرونشان، و به هنگام قدرت ببخش، و به هنگام خشم فروتن باش، و در حکومت مدارا کن تا آینده خوبی داشته باشی، نعمتهایی که خدا به تو بخشیده نیکو دار، و نعمتهایی که در اختیار داری تباه مکن، و چنان باش که خدا آثار نعمتهای خود را در تو آشکارا بنگرد. \nاوصاف مومنان \nو بدان! بهترین مومنان، آنکه جان و خاندان و مال خود را در راه خدا پیشاپیش تقدیم کند، چه آن را که پیش فرستی برای تو اندوخته گردد، و آنچه را که باقی گذاری سودش به دیگران می‌رسد. از دوستی با بیخردان، و خلافکاران بپرهیز، زیرا هر کس را از آنکه دوست اوست می‌شناسند، و در شهرهای بزرگ سکونت کن زیرا مرکز اجتماع مسلمانان است، و از جاهایی که مردم آن از یاد خدا غافلند، و به یکدیگر ستم روا می‌دارند، و بر اطاعت از خدا به یکدیگر کمک نمی کنند، بپرهیز. در چیزی اندیشه کن که یاریت دهد، از نشستن در گذرگاه‌های عمومی، و بازار پرهیز کن که جای حاضر شدن شیطان، و برانگیخته شدن فتنه‌هاست، و به افراد پایین تر از خود توجه داشته باش، که راه شکرگزاری تو در برتری است. در روز جمعه پیش از نماز مسافرت مکن، جز برای جهاد در راه خدا، و یا کاری که از انجام آن ناچاری، در همه کارهایت خدا را اطاعت کن، که اطاعت خدا از همه چیز برتر است. \nروش به کارگیری نفس در خوبیها \nنفس خود را در واداشتن به عبادت فریب ده، و با آن مدارا کن، و به زور و اکراه بر چیزی مجبورش نساز، و در وقت فراغت و نشاط بکارش گیر، جز در آنچه که بر تو واجب است، و باید آن را در وقت خاص خودش بجا آوری، بپرهیز از آنکه مرگ تو فرا رسد در حالی که از پروردگارت گریزان باشی، و در دنیاپرستی غرق باشی، از همنشینی با فاسقان بپرهیز که شر به شر می‌پیوندد، خدا را گرامی دار، و دوستان خدا را دوست شمار، و از خشم بپرهیز که لشکر بزرگ شیطان است. با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (308, 2, 7, 'به معاویه', 'نامه ۷- به معاویه \nافشای چهره نفاق معاویه و مشروعیت بیعت \nپس از نام خدا و درود! \nنامه پندآمیز تو به دستم رسید که دارای جملات به هم پیوسته، و زینت داده شده که با گمراهی خود آن را آراسته، و با بداندیشی خاص امضاء کرده بودی، نامه مردی که نه خود آگاهی لازم دارد تا رهنمونش باشد، و نه رهبری دارد که هدایتش کند، تنها دعوت هوسهای خویش را پاسخ گفته، و گمراهی عنان او را گرفته و او اطاعت می‌کند، که سخن بی ربط می‌گوید و در گمراهی سرگردان است. \n(از همین نامه است) همانا بیعت برای امام یک بار بیش نیست، و تجدید نظر در آن میسر نخواهد بود، و کسی اختیار از سرگرفتن آن را ندارد، آن کس که از این بیعت عمومی سر باز زند، طعنه زن و عیبجو خوانده می‌شود، و آن کس که نسبت به آن دودل باشد منافق است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (309, 2, 70, 'به سهل بن حنیف', 'نامه ۷۰-به سهل بن حنیف \nروش برخورد با پدیده فرار \nپس از یاد خدا و درود! \nبه من خبر رسیده که گروهی از مردم مدینه به سوی معاویه گریختند، مبادا برای از دست دادن آنان، و قطع شدن کمک و یاریشان افسوس بخوری! که این فرار برای گمراهیشان، و نجات تو از رنج آنان کافی است، آنان از حق و هدایت گریختند، و به سوی کوردلی و جهالت شتافتند. آنان دنیاپرستانی هستند که به آن روی آوردند، و شتابان در پی آن گرفتند، عدالت را شناختند و دیدند و شنیدند و به خاطر سپردند، و دانستند که همه مردم در نزد ما، در حق یکسانند، پس به سوی انحصارطلبی گریختند دور باشند از رحمت حق، و لعنت بر آنان باد. سوگند به خدا! آنان از ستم نگریختند، و به عدالت نپیوستند، همانا آرزومندیم تا در این جریان، خدا سختی‌ها را بر ما آسان، و مشکلات را هموار فرماید. ان شاء الله، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (310, 2, 71, 'به منذر بن الجارود', 'نامه ۷۱-به منذر بن الجارود \nسرزنش از خیانت اقتصادی \nپس از یاد خدا و درود! \nهمانا، شایستگی پدرت مرا نسبت به تو خوشبین، و گمان کردم همانند پدرت می‌باشی، و راه او را می‌روی، ناگهان به من خبر دادند، که در هواپرستی چیزی فرو گذار نکرده، و توشه ای برای آخرت خود باقی نگذاشته ای، دنیای خود را با تباه کردن آخرت آبادان می‌کنی، و برای پیوستن با خویشاوندانت از دین خدا بریدی، اگر آنچه به من گزارش رسیده، درست باشد، شتر خانه ات، و بند کفش تو از تو باارزش تر است، و کسی که همانند تو باشد، نه لیاقت پاسداری از مرزهای کشور را دارد، و نه می‌تواند کاری را به انجام رساند، یا ارزش او بالا رود، یا شریک در امانت باشد. یا از خیانتی دور ماند پس چون این نامه به دست تو رسد، نزد من بیا. ان شاء الله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (311, 2, 72, 'به عبدالله بن عباس', 'نامه ۷۲-به عبدالله بن عباس \nانسان و مقدرات الهی \nپس از یاد خدا و درود! \nتو از اجل خود پیشی نخواهی گرفت، و آنچه که روزی تو نیست به تو نخواهد رسید، و بدان که روزگار دو روز است، روزی به سود، و روزی به زیان تو می‌باشد، و همانا دنیا خانه دگرگونیهاست، و آنچه که به سود تو است هر چند ناتوان باشی خود را به تو خواهد رساند، و آنچه که به زیان تو است هر چند توانا باشی دفع آن نخواهی کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (312, 2, 73, 'به معاویه', 'نامه ۷۳-به معاویه \nافشای سیمای دروغین معاویه \nپس از یاد خدا! \nمن با پاسخ‌های پیاپی به نامه‌هایت، و شنیدن مطالب نوشته‌هایت، رای خود را سست، و زیرکی خود را به خطا نسبت می‌دهم، و همانا تو که مدام خواسته‌هایی از من داری و نامه‌های فراوان می‌نویسی، به کسی مانی که خواب سنگینی فرو رفته، و خوابهای دروغینش او را تکذیب می‌کند، یا چون سرگردانی که ایستادن طولانی بر او دشوار می‌باشد، و نمی داند، آیا آینده به سود او یا به زیانش خواهد بود؟ گرچه تو آن کس نیستی اما به تو شباهت دارد. به خدا سوگند! اگر پرهیز از خونریزی در مهلت تعیین شده نبود، ضربه کوبنده ای دریافت می‌کردی که استخوان را خرد، و گوشت را بریزاند، معاویه! بدان، که شیطان تو را نمی گذارد تا به نیکوترین کارت بپردازی، و اندرزی که به سود تو است بشنوی. درود بر آنان که سزاوار درودند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (313, 2, 74, 'پیمان میان ربیعه و یمن', 'نامه ۷۴-پیمان میان ربیعه و یمن \nمبانی صلح و سازش مسلمین \nاین پیمان نامه ای است که مردم (یمن) و (ربیعه) آن را پذیرفته اند، چه آنان که در شهر حضور دارند یا آنان که در بیابان زندگی می‌کنند، آنان پیرو قرآنند، و به کتاب خدا دعوت می‌کنند، و به انجام دستورات آن فرمان می‌دهند، و هر کس که آنان را به کتاب خدا بخواند پاسخ می‌دهند، نه برابر آن مزدی خواهند، و نه به جای آن چیز دیگری بپذیرند، و در برابر کسی که خلاف این پیمان خواهد، یا آن را واگذارد، ایستادگی خواهند کرد، بعضی بعض دیگر را یاری می‌دهند، همه متحد بوده و به خاطر سرزنش سرزنش کننده ای، یا خشم خشم گیرنده ای، یا خوار کردن بعضی، یا دشنام دادن قومی، این پیمان را نمی شکنند. بر این پیمان، حاضران و آن‌ها که غایبند، دانایان و ناآگاهان، بردباران و جاهلان، همه استوارند، و عهد و پیمان الهی نیز بر آنان واجب گردیده است که (همانا از پیمان خدا پرسش خواهد شد) و علی بن ابیطالب آن را نوشت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (314, 2, 75, 'به معاویه', 'نامه ۷۵-به معاویه \nفرمان اطاعت به معاویه \nاز بنده خدا علی امیرمومنان، به معاویه بن ابی سفیان.\n پس از یاد خداو درود! \nمی دانی که من درباره شما معذور، و از آنچه در مدینه گذشت رویگردانم، تا شد آنچه که باید می‌شد، و بازداشتن آن ممکن نبود، داستان طولانی و سخن فراوان است. و گذشته‌ها گذشت، و آینده روی کرده است، تو و همراهانت بیعت کنید، و با گروهی از یارانت نزد من بیا، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (315, 2, 76, 'به عبدالله بن عباس', 'نامه ۷۶-به عبدالله بن عباس \nاخلاق فرماندهی با مردم \nبه هنگام دیدار، و در مجالس رسمی، و در مقام داوری، گشاده رو باش، و از خشم بپرهیز، که سبک مغزی، به تحریک شیطان است، و بدان! آن چه تو را به خدا نزدیک می‌سازد، از آتش جهنم دور می‌کند، و آنچه تو را از خدا دور می‌سازد، به آتش جهنم نزدیک می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (316, 2, 77, 'به عبدالله بن عباس', 'نامه ۷۷-به عبدالله بن عباس \nروش مناظره با دشمن مسلمان \nبه قرآن با خوارج به جدل مپرداز، زیرا قرآن دارای دیدگاه کلی بوده، و تفسیرهای گوناگونی دارد، تو چیزی می‌گویی، و آن‌ها چیز دیگر، لکن با سنت پیامبر (ص) با آنان به بحث و گفتگو بپرداز، که در برابر آن راهی جز پذیرش ندارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (317, 2, 78, 'به ابوموسی اشعری', 'نامه ۷۸-به ابوموسی اشعری \nعلل سقوط جامعه \nهمانا بسیاری از مردم دگرگون شدند، و از سعادت و رستگاری بی بهره ماندند، به دنیاپرستی روی آورده، و از روی هوای نفس سخن گفتند، کردار اهل عراق مرا به شگفتی واداشته است، که مردمی خودپسند در چیزی گرد آمدند، می‌خواستم زخم درون آن‌ها را مدارا کنم، پیش از آنکه غیر قابل علاج گردد. \nتلاش امام در تحقق وحدت \nپس بدان در امت اسلام، هیچ کس همانند من وجود ندارد که به وحدت امت محمد (ص) از من حریص تر، و انس او از من بیشتر باشد، من در این کار پاداش نیک و سرانجام شایسته را از خدا می‌طلبم، و به آنچه پیمان بستم وفا دارم، هر چند تو دگرگون شده، و همانند روزی که از من جدا شدی نباشی. همانا بدبخت کسی است که از عقل و تجربه ای که نصیب او شده است محروم ماند، و من از آن کس که به باطل سخن گوید یا کاری را که خدا اصلاح کرده برهم زند، بیزارم. آنچه را نمی دانی واگذار، زیرا مردان بدکردار، با سخنانی نادرست به سوی تو خواهند شتافت، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (318, 2, 79, 'به سرداران سپاه', 'نامه ۷۹-به سرداران سپاه \nعلل نابودی ملتها \nپس از یاد خدا و درود! \nهمانا ملتهای پیش از شما به هلاکت رسیدند، بدان جهت که حق مردم را نپرداختند، پس دنیا را با رشوه دادن به دست آوردند، و به راه باطلشان بردند و آنان اطاعت کردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (319, 2, 8, 'به جریر بن عبدالله البجلی', 'نامه ۸-به جریر بن عبدالله البجلی \nوادار ساختن معاویه به بیعت \nپس از نام خدا و درود! \nهنگامی که نامه ام به دستت رسید، معاویه را به یکسره کردن کار وادار، و با او برخوردی قاطع داشته باش، سپس او را آزاد بگذار در پذیرفتن جنگی که مردم را از خانه بیرون می‌ریزد، یا تسلیم شدنی خوارکننده، پس اگر جنگ را برگزید، امان نامه او را بر زمین کوب، و اگر صلح خواست از او بیعت بگیر، با درود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (320, 2, 9, 'به معاویه', 'نامه ۹-به معاویه \nافشای دشمنی‌های قریش و استقامت پیامبر (ص) \nخویشاوندان ما از قریش می‌خواستند پیامبرمان (ص) را بکشند، و ریشه ما را برکنند، و در این راه اندیشه‌ها از سرگذراندند، و هر چه خواستند نسبت به ما انجام دادند، و زندگی خوش را از ما سلب کردند، و با ترس و وحشت به هم آمیختند، و ما را به پیمودن کوه‌های صعب العبور مجبور کردند، و برای ما آتش جنگ افروختند، اما خدا خواست که ما پاسدار دین او باشیم، و شر آنان را از حریم دین باز داریم، مومن ما در این راه خواستار پاداش بود، و کافر ما از خویشاوندان خود دفاع می‌کرد، دیگر افراد قریش که ایمان می‌آوردند و از تبار ما نبودند، یا به وسیله هم پیمانهایشان و یا با نیروی قوم و قبیله شان حمایت شده و در امان بودند، هرگاه آتش جنگ زبانه می‌کشید، و دشمنان هجوم می‌آوردند پیامبر اسلام (ص) اهل بیت خود را پیش می‌فرستاد تا به وسیله آن‌ها، اصحابش را از سوزش شمشیرها و نیزه‌ها حفظ فرماید، چنانکه عبیده بن حارث در جنگ بدر، و حمزه در احد، و جعفر در موته، شهید گردیدند. کسانی هم بودند که اگر می‌خواستم نامشان را می‌آوردم، آنان که دوست داشتند چون شهیدان اسلام، شهید گردند، اما مقدر چنین بود که زنده بمانند، و مرگشان به تاخیر افتاد، شگفتا از روزگار! که مرا همسنگ کسی قرار داده که چون من پیش قدم نبوده، و مانند من سابقه در اسلام و هجرت نداشته است، کسی را سراغ ندارم چنین ادعایی کند، مگر ادعاکننده ای که نه من او را می‌شناسم و نه فکر می‌کنم خدا، او را بشناسد، در هر حال خدا را سپاسگزارم. \nافشای ادعای دروغین معاویه در خونخواهی عثمان \nاینکه از من خواستی تا قاتلان عثمان را به تو واگذارم، پیرامون آن فکر کردم و دیدم که توان سپردن آن‌ها را به تو یا غیر تو ندارم، سوگند بجان خودم! اگر دست از گمراهی و تفرقه برنداری، به زودی آن‌ها را خواهی یافت که تو را می‌جویند، بی آنکه تو را فرصت دهند تا در خشکی و دریا و کوه و صحرا زحمت پیدا کردنشان را بر خود هموار کنی. و اگر در جستجوی آنان برآیی بدان که شادمان نخواهی شد، و زیارتشان تو را خوشحال نخواهد کرد، و درود بر اهل آن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (321, 4, 1, 'اشاره به ظهور امام زمان', 'روایتی از امام علیه السلام: «اشاره به ظهور امام زمان علیه السلام» \r\nچون آنگونه شود، پیشوای دین قیام کند، پس مسلمانان پیرامون او چونان ابر پاییزی گرد آیند. \r\n«یعسوب» یعنی بزرگ مسلمانان، و «قزع» یعنی ابرهای پاییزی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (322, 4, 2, 'سخنوری', 'روایتی دیگر از امام علیه السلام: «سخنوری» \r\nاین سخنران، زبردست ماهری است. \r\n«شحشح» یعنی مهارت دارد، به کسی که خوب حرف می‌زند یا خوب راه می‌رود گویند، ولی در موارد دیگر «شحشح» یعنی فردی بخیل.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (323, 4, 3, 'پرهیز از دشمنی کردن', 'روایتی دیگر از امام علیه السلام: «پرهیز از دشمنی کردن» \r\nدشمنی، رنج‌ها و سختی‌هایی هلاک کننده دارد. \r\n«قحم» یعنی مهلکه‌ها، زیرا دشمنی آنان را به هلاکت می‌رساند، و به معنی سختی‌ها نیز آمده که می‌گویند «قحمة الاعراب»، یعنی روزگار سختی و گرسنگی عرب‌ها به گونه‌ای که اموالشان تمام می‌شود، و معنی «تقحم» همین است که می‌گویند خشکسالی روستاییان را به سرزمین‌های سبز و آباد کشانده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (324, 4, 4, 'سرپرستی زنان', 'روایتی دیگر از امام علیه السلام: «سرپرستی زنان» \r\nچون زنان بالغ شوند، خویشاوندان پدری برای سرپرستی آنان سزاوارترند. \r\nمنظور از «نص» آخرین درجه هر چیز است، مانند «نص» در سیر، که به معنی آخرین مرحله توانایی مرکب است، هنگامی که می‌گوییم، «نصصت الرجل عن الآمر» آنقدر سؤال از کسی بشود که آنچه می‌داند بیان کند، بنا بر این «نص الحقاق» بمعنی رسیدن به مرحله بلوغ است که پایان دوره کودکی است، این جمله از فصیح‌ترین کنایات و شگفت آورترین آن‌ها است، منظور امام این است، هنگامی که زنان باین مرحله برسند «عصبه»: مردان خویشاوند پدری که محرم آنان هستند، مانند برادر، و عمو، به حمایت آن‌ها سزاوارتر از مادرند، و هم چنین در انتخاب همسر برای آن‌ها، و منظور از حقاق مخالفت و درگیری مادر، با عصبه، در مورد این زن است، به طوری که هر کدام به دیگری می‌گوید: من از تو احق هستم، گفته می‌شود: «حاققته حقاقا» بمعنی رشد عقلی است، یعنی به مرحله‌ای برسد که حقوق و احکام در باره او اجرا شود، اما آن کس که نص الحقائق نقل کرده منظورش از حقایق، جمع «حقیقت» است. این بود معنایی که «ابو عبید قاسم بن سلام» برای این جمله کرده است، اما نظر من این است که منظور از «نص الحقاق» این است که زن به مرحله‌ای برسد که جائز باشد تزویج کند، و اختیاردار حقوق خود شود، این در حقیقت تشبیه به «حقاق» در شتر است چرا که «حقاق» جمع حقه و «حق» است به معنی شتری که سه سالش تمام و آماده بهره‌برداری است. «حقائق» نیز جمع حقه است، بنا بر این هر دو تعبیر به یک معنی باز می‌گردد، هر چند معنی دوم به روش عرب شبیه‌تر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (325, 4, 5, 'تأثیر ایمان در روح', 'روایتی دیگر از امام علیه السلام: «تأثیر ایمان در روح» \r\nایمان نقطه‌ای نورانی در قلب پدید آورد که هر چه ایمان رشد کند آن نیز فزونی یابد. \r\nلمظة نقطه سیاه یا سفید است، می‌گویند فرس المظ، یعنی اسبی که در لب او نقطه سپیدی باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (326, 4, 6, 'ضرورت پرداخت زکات', 'روایتی دیگر از امام علیه السلام: «ضرورت پرداخت زکات» \r\nهر گاه انسان طلبی دارد که نمی‌داند وصول می‌شود یا نه، پس از دریافت آن واجب است زکات آن را برای سالی که گذشته، بپردازد. \r\nظمی‌گویم: بنا بر این «دین ظنون» آن است که طلب کار نمی‌داند آیا می‌تواند از بدهکار وصول کند یا نه گویا طلب کار در حال ظن و گمان است، گاهی امید دارد که بتواند آن را بستاند، و گاهی امید دارد که بتواند آن را بستاند، و گاهی نه، این از فصیح‌ترین سخنان است، همچنین هر کاری که طالب آن هستی و نمی‌دانی در چه موضعی نسبت به آن خواهی بود، آن را «ظنون» گویند. و گفته اعشی شاعر عرب از همین باب است، آنجا که می‌گوید: «چاهی که معلوم نیست آب دارد یا نه، و از محلی که باران گیر باشد دور است، نمی‌شود آن را همچون فرات، که پر از آب است، و کشتی و شناگر ماهر را از پا در می‌آورد، قرار داد» «جد» چاه قدیمی بیابانی را گویند، و ظنون آن است که معلوم نیست آب دارد یا نه.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (327, 4, 7, 'اخلاق نظامی', 'روایتی دیگر از امام علیه السلام: وقتی لشکری را در راه جنگ مشایعت می‌کرد فرمود: «اخلاق نظامی» \r\nتا می‌توانید از زنان دوری کنید. \r\nمی گویم: معنی این سخن آن که از یاد زنان و توجه دل به آن‌ها در هنگام جنگ، اعراض کنید، و از نزدیکی با آنان امتناع ورزید، چه اینکه این کار بازوان حمیت را سست، و در تصمیم شما خلل ایجاد می‌نماید، و از حرکت سریع، و کوشش در جنگ باز می‌دارد، هر کس که از چیزی امتناع ورزد گفته می‌شود «عذب عنه» و «عازب» و «عذوب» به معنی کسی است که از خوردن و آشامیدن امتناع می‌ورزد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (328, 4, 8, 'امید به پیروزی', 'روایتی دیگر از امام علیه السلام: «امید به پیروزی» \r\nمسلمان چونان تیراندازی ماهری است که انتظار دارد، در همان نخستین تیراندازی پیروز گردد. \r\nمی‌گویم: یاسرون، کسانی هستند که با تیرها بر سر شتری مسابقه می‌دهند، و فالج، یعنی چیره دست پیروز، می‌گویند. قد فلج علیهم و فلجهم. یعنی بر آنان پیروز شدند، و آنان را مغلوب کردند، و راجز «» می‌گوید: چیره دستی را دیدم که پیروز شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (329, 4, 9, 'جهاد پیامبر', 'روایتی دیگر از امام علیه السلام: «جهاد پیامبر صلی الله علیه و آله و سلم» \r\nهر گاه آتش جنگ شعله می‌کشید، ما به رسول خدا صلی الله علیه و آله و سلم پناه می‌بردیم، که در آن لحظه کسی از ما همانند پیامبر صلی الله علیه و آله و سلم به دشمن نزدیک‌تر نبود. \r\nوقتی ترس از دشمن بزرگ می‌نمود، و جنگ به گونه‌ای می‌شد که گویا جنگجویان را می‌خواهد در کام خود فرو برد، مسلمانان به پیامبر پناهنده می‌شدند، تا رسول خدا شخصا به نبرد پردازد، و خداوند به وسیله او نصرت و پیروزی را بر آنان نازل فرماید، و در سایه آن حضرت ایمن گردند، \r\n\r\nاما جمله «اذا احمر البأس»: \r\nکنایه از شدت کارزار است. در این باره سخنان متعددی گفته شده که بهترین آن‌ها اینکه امام داغی جنگ را به شعله‌های سوزان آتش تشبیه کرده است، و از چیزهایی که این نظر را تقویت می‌کند، سخن پیامبر در جنگ حنین است، هنگامی که نبرد سخت شد و شمشیر زدن مردم را در جنگ «هوازن» مشاهده کرد فرمود: \r\n«الآن حمی الوطیس» \r\nاکنون تنور جنگ داغ شد، \r\n«وطیس» تنور آتش است، بنا بر این رسول خدا صلی الله علیه و آله و سلم داغی و گرمی جنگ را به افروختگی و شدت شعله‌وری آتش تشبیه فرموده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (330, 5, 1, 'مقدمه سید رضی', 'ستایش خدا را سزاست که حمد را بهای نعمت‌ها و پناهگاه از بلاها، و وسیله رسیدن به نعمت‌ها و بهشت جاویدان، و موجب افزایش احسان و کرمش قرار داد. درود بر پیامبر رحمت، و پیشوای رهبران الهی و چراغ روشنی بخش امت، که ریشه‌های وجودش بزرگواری و درخت اصل و نسبش پر برگ و پر ثمر بوده است. و درود بر اهل بیتش که چراغ‌های روشنی بخش تاریکی‌ها، و وسیله نجات امت‌ها، و نشانه‌های روشن دین و مرکز ثقل فضیلت و برتری می‌باشند. درود بر همه آنان باد درودی که با فضل و بزرگواری آن‌ها برابری کند و پاداش اعمال آن‌ها قرار گیرد پاداشی که مناسب با پاکیزگی اصل و فرع ایشان است. درود بر آنان باد تا زمانی که سفیده صبح گریبان شب را می‌شکافد و ستارگان طلوع و غروب می‌کنند. \r\n\r\nچگونگی پیدایش کتاب نهج البلاغه: \r\nدر آغاز جوانی و طراوت زندگی، دست به تألیف کتابی در «خصائص و ویژگی‌های ائمه علیهم السلام» زدم که مشتمل بر خبرهای جالب و سخنان برجسته آن‌ها بود. انگیزه این عمل را در آغاز آن کتاب یاد آور شدم و آن را آغاز سخن قرار داده‌ام. پس از گرد آوردن خصائص امیر مؤمنان علیه السلام مشکلات و حوادث روزگار مرا از اتمام بقیه کتاب، باز داشت. من آن کتاب را مبوب نموده و به فصل‌های مختلفی تقسیم کرده بودم در پایان‌ آن، فصلی که متضمن سخنان جالب امام علیه السلام از سخنان کوتاه در مواعظ، حکم، امثال و آداب- «نه خطبه‌های طولانی و نه نامه‌های گسترده» - آورده شد. برخی از دوستان آن را جالب و شگفت انگیز، از جنبه‌های گوناگون دانستند، و از من خواستند کتابی تألیف کنم که سخنان برگزیده امیر مؤمنان علیه السلام در جمیع فنون و بخشهای مختلف، از خطبه‌ها، نامه‌ها، مواعظ و ادب در آن گرد آید، زیرا می‌دانستند این کتاب متضمن شگفتی‌های بلاغت و نمونه‌های ارزنده فصاحت و جواهر سخنان عرب و نکات درخشان از سخنان دینی و دنیوی خواهد بود که در هیچ کتابی جمع آوری نشده و در هیچ نوشته‌ای تمام جوانب آن گرد آوری نگردیده است زیرا امیر مؤمنان منشأ فصاحت و منبع بلاغت و پدید آورنده آن است، مکنونات بلاغت بوسیله او آشکار گردیده، و قوانین و اصول آن از او گرفته شده است، تمام خطباء و سخنرانان به او اقتداء نموده و همه واعظان بلیغ از سخن او استمداد جسته‌اند. با این همه، او همیشه پیشرو است آن‌ها دنباله رو، او مقدم است آن‌ها مؤخر، زیرا سخن او کلامی است که آثار علم الهی، و بوی عطر سخن پیامبر را به همراه دارد. از این رو خواسته دوستان را اجابت کردم و این کار را شروع کرده، و در حالی که یقین داشتم سود و نفع معنوی آن بسیار است، و به زودی همه جا را تحت سیطره خود قرار خواهد داد و أجر آن ذخیره آخرت خواهد بود. منظورم این بود که علاوه بر فضائل بی شمار دیگر، بزرگی قدر، و شخصیت امیر مؤمنان را از این نظر روشن سازم که: امام علی علیه السلام تنها فردی است که از میان تمام گذشتگانی که سخنی از آن‌ها به جا مانده، به آخرین مرحله فصاحت و بلاغت رسیده و گفتار او اقیانوسی است بیکرانه، که سخن هیچ بلیغی به پایه آن نخواهد رسید. خواستم در افتخار کردن به امام علیه السلام به قول شاعر معروف «فرزدق» متمثل شوم (که در مورد افتخار به پدران خود به شخصی به نام «جریر» خطاب می‌کند): \r\nاولئک آبائی فجئنی بمثلهم\r\nاذا جمعتنا یا جریر المجامع‌\r\n(ای جریر اینها پدران و نیاکان منند اگر می‌توانی در آن هنگام که در مجمعی گرد آمدیم همانند آن‌ها را برای خود بر شمار.) \r\n\r\nسخنان آن حضرت بر مدار سه اصل می‌چرخد: \r\nاول- خطبه‌ها و اوامر \r\nدوم- نامه‌ها و رسائل \r\nسوم- کلمات حکمت آمیز و مواعظ \r\n\r\nتصمیم گرفتم ابتداء خطبه‌ها، پس از آن نامه‌ها، و سپس کلمات حکمت آمیز جالب آن حضرت را جمع کنم. سپس به هر کدام از آن‌ها بابی را اختصاص دادم و صفحاتی ویژه آن گردانیدم تا مقدمه‌ای باشد برای به دست آوردن آنچه که به آن دسترسی ندارم (و ممکن است) در آینده به آن برسم. و هر گاه سخنی از آن حضرت در مورد بحث و مناظره، یا پاسخ سؤال، و یا منظور دیگری بود که به آن دست یافتم ولی جزء هیچ یک از این سه بخش نبود، آن را در مناسب‌ترین و نزدیک‌ترین بخش قرار دادم. اگر در میان آنچه برگزیده‌ام فصول غیر منظم و سخنان غیر مرتبی آمده، به خاطر آن است که من نکته‌ها و جملات درخشان آن حضرت را جمع می‌کنم و منظورم حفظ تمام پیوندها و ارتباطات کلامی نیست. از شگفتی‌های سخنان امام علیه السلام که او خود در این زمینه تنها است و شریک و همتایی در آن‌ها ندارد، این است که: سخن آن حضرت را که در باره زهد و مواعظ است هر گاه کسی تأمل کند و خود را از این جهت بیگانه دارد که این سخن شخصی عظیم القدر و نافذ الامر است که همه در برابر او سر فرود می‌آورند شک و تردید نخواهد کرد که گوینده این سخن کسی است که جز در وادی زهد و پارسایی قدم نگذاشته و هیچ اشتغالی بجز عبادت نداشته است. \r\nو یقین می‌کند که این سخن از کسی است که در گوشه خانه‌ای تنها و دور از اجتماع یا در یکی از غارها قرار گرفته که جز صدای خودش را نمی‌شنود و غیر خویش دیگری را نمی‌بیند، و همواره مشغول عبادت می‌باشد. و هیچ گاه نمی‌تواند باور کند که این سخن کسی است که به هنگام جنگ در دریای لشکر دشمن فرو می‌رفت و پهلوانان و جنگجویان نیرومند را بر زمین می‌افکند. و او با این حال یکی از زهاد و یکی از افراد صالح و پاک و نمونه ای است که نظیر ندارد. این است فضائل عجیب و شگفتی‌زا و ویژگی لطیف او که جمع میان اضداد کرده است. بسیار می‌شد که من در این باره با برادران مذاکره می‌کردم و شگفتی آن‌ها را از این ویژگی خاص امام علیه السلام بر می‌انگیختم و راستی این‌ خود جای عبرت و شایسته اندیشه و فکر است. اگر در اثناء سخنان انتخاب شده لفظ مردد و یا معنی مکرری آمده است، عذر من در این باره این است که در روایات مربوط به سخنان امام علیه السلام اختلاف است: گاهی سخنی را در روایتی یافتم و همان طور که بوده آن را نقل کرده‌ام. سپس روایت دیگری در همان موضوع به دستم رسیده که با روایت قبل یکسان نبوده، اما به خاطر مطالب بیشتری که داشته، و یا به خاطر لفظ جالب‌تری که در آن به کار رفته، لازم بود آن را نیز بیاورم. این نیز ممکن است بر اثر سهو و نسیان نه از روی عمد دوباره آورده شده است. با این حال ادعا نمی‌کنم که من به همه جوانب سخنان امام علیه السلام به طوری احاطه پیدا کرده‌ام که هیچ کدام از سخنان او از دستم نرفته است، بلکه بعید نمی‌دانم که آنچه نیافته‌ام بیش از آن باشد که یافته‌ام، و آنچه در اختیارم قرار گرفته کمتر از آنچه به دستم نیامده باشد. اما وظیفه من غیر از تلاش و کوشش و سعی فراوان برای یافتن این گم شده نیست، و از خدا می‌خواهم در این امر مرا راهنمایی کند. بعد از تمام شدن کتاب چنین دیدم که نامش را «نهج البلاغه» بگذارم زیرا این کتاب درهای بلاغت را به روی بیننده می‌گشاید، و خواسته‌هایش را به او نزدیک می‌سازد. این کتاب هم مورد نیاز دانشمند و عالم، و هم دانشجو و متعلم است، و هم خواسته شخص بلیغ و زاهد در آن یافت می‌شود. در بین کلمات امام علیه السلام سخنان شگفت انگیزی در مورد توحید، عدل و تنزیه خداوند از شباهت به خلق می‌بینم که تشنگان را سیراب کرده، و پرده‌ها را از روی تاریکی شبهات بر می‌گیرد. از خداوند بزرگ توفیق و نگهداری از لغزش را خواستارم، و نیز می‌خواهم که به من در این راه مقاومت و یاری بخشد و از خطای فکر پیش از خطای زبان، و از خطای سخن، پیش از لغزش قدم، به او پناه می‌برم. او مرا کفایت می‌کند و بهترین حافظ و یاور است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (331, 5, 2, 'سرآغاز', 'بسم الله الرحمن الرحیم \nبه نام آن که علی علیه السلام را به جهانیان عطا فرمود \nاز سال ۴۰۰ هجری که سید رضی قدس سره با فکری نورانی و با اهدافی والا، به جمع آوری برخی از آثار علمی حضرت امیر المؤمنین علی علیه السلام همت گماشت و نهج البلاغه را تدوین کرد که عطر وجودی آن جان و جهان را شاداب نمود، تاکنون، ترجمه‌های گوناگونی به زبان فارسی تحقق پذیرفته و هر یک از مترجمان با اهداف و انگیزه‌های متفاوتی به اقیانوس همیشه مواج علوم علوی نگریسته‌اند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (332, 5, 3, 'انگیزه‌های ترجمه', 'با شناسایی و جمع‌آوری ۳۰ ترجمه کامل از نهج البلاغه، از قرن پنجم (به تصحیح عزیز الله جوینی) تا روزگار نورانی انقلاب اسلامی ایران، و بررسی اهداف و شیوه‌ها و قابلیت‌های نهفته در آن و با توجه به ضرورت مطرح کردن مفاهیم نهج البلاغه از نظر کاربردی در مجامع فرهنگی، به این نتیجه رسیدیم که هنوز هم به ترجمه‌های دیگری نیاز است، زیرا: یکی از جاذبه‌های ادبی قلم زده، و دیگری در مرز و حدود الفاظ و عبارات مانده برخی با شیوه‌های ترجمه آزاد، دچار تندرویهایی شده‌اند و برخی دیگر تفسیر و ترجمه را به هم آمیخته‌اند بعضی در ترجمه تحت اللفظی موفق، اما در پیام رسانی کوتاهی کرده‌اند و بعضی دیگر در پیام رسانی موفق، اما از حدود الفاظ و عبارات فاصله گرفته‌اند و مخاطب ترجمه‌ها نیز از نظر کاربردی متفاوتند: برخی از ترجمه‌ها به قشر خاصی از جامعه تعلق دارد (تنها محققان و ادیبان و اهل نظر می‌توانند از آن بهره‌مند گردند)، برخی دیگر به گونه‌ای سامان یافته‌اند که برای نسل معاصر قابل استفاده نیستند و کاربرد عمومی ندارند. \nو با توجه به واقعیت‌های یاد شده، و با درک نیازهای نسل معاصر در کلاس‌های گوناگون علوم انسانی و معارف و نهج البلاغه، بر آن شدیم تا ترجمه‌ای گویا و متناسب با اهداف کاربردی از نهج البلاغه تحقق پذیرد که: \n\nالف- قابل فهم و درک برای عموم افراد جامعه باشد \nب- به گروه خاصی از جامعه تعلق نداشته باشد، \nج- اصل پیام رسانی دقیقا رعایت گردد، \nد- از هر گونه رمز و اشاره و کنایه و کلی گویی اجتناب شود، \nه- مخاطب‌های امام علیه السلام آشکارا معرفی گردند، \nو- توضیحات ضروری در متن یا پاورقی آورده شود، \nز- پیام ضرب المثل‌ها کشف، و به نسل معاصر شناسانده شود، \nح- مفاهیم نهج البلاغه در قالب هزاران عناوین زیبا و گویا ارائه شود، تا همه اقشار جامعه بتوانند از آن بهرمند گردند. \n\nبا چنین آرزوها و اهداف ارزشمندی در آستانه نهج البلاغه زانو زدیم و از نور ولایت علوی مدد گرفتیم، که همه از برکات کلاس‌های آموزشی و عنایات حضرت مولی الموحدین امیر المؤمنین علیه السلام است. \nآری: \nبه ذره گر نظر لطف بو تراب کند\nبه آسمان رود و کار آفتاب کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (333, 5, 4, 'نگاهی به ترجمه‌ها', 'برای شناخت صحیح ترجمه‌ها و توان کاربردی آن، باید از سطوح مختلف و گوناگون مفاهیم نهج البلاغه آگاهی داشت، باید بدانیم که: ژرفای اقیانوس مواج علوم علوی چگونه است و معارف بلند و وحی گونه نهج البلاغه دارای چه ویژگی‌ها و تقسیماتی است آنگاه به سراغ ترجمه‌ها برویم، و توان کار بردی آن‌ها را مورد ارزیابی قرار دهیم. \nمعارف عمیق و ارزشمند نهج البلاغه را می‌توان در سطوح مختلف عمومی، تخصصی و فوق تخصصی به ارزیابی گذاشت.\n\nالف- مفاهیم عمومی:\nمفاهیمی مانند مباحث تاریخی، اخلاق فردی، خود سازی، اخلاق عمومی، تذکرات عمومی، گفتگوهای عادی با مردم، با فرزندان و با کارگزاران حکومتی مفاهیمی عمومی‌اند. \nنسبت به این دسته از معارف نهج البلاغه، مشکل خاصی وجود ندارد و هر گونه ترجمه‌ای مفید است: ترجمه آزاد یا مقید به متن، یا ترجمه‌ای ادبی و... و با الفاظ و عباراتی قدیمی یا نوین. \n\nب- مفاهیم و معارف تخصصی:\nبرخی دیگر از مباحث نهج البلاغه، فنی و تخصصی است و به علوم و فنون و رشته‌های تخصصی بسیاری از معارف و علوم انسانی و غیر انسانی تعلق دارد، از این رو زبان خاص خودش را می‌طلبد، و الفاظ و عبارات و شیوه‌های تحقیق مربوط به همان رشته‌ها را لازم دارد، مانند: - مباحث کلامی، فلسفی، اعتقادی- مباحث اقتصادی- مباحث سیاسی و مدیریتی- مباحث نظامی، جنگ و صلح، آموزش نظامی- مباحث بهداشتی، طب و درمان- مباحث روانشناسی، روانکاوی، روانشناسی بالینی (درمانی) - مباحث جامعه شناسی و دیگر مباحث ارزشمندی که به ده‌ها رشته علمی مصطلح امروز ارتباط پیدا می‌کند. طبیعی است که تنها با ترجمه الفاظ و عبارات نهج البلاغه نمی‌توان مفاهیم این دسته از مباحث تخصصی را به خوانندگان منتقل کرد. باید صاحب نظران هر یک از رشته‌های یاد شده از دیدگاه تخصصی خویش به ارزیابی و کنکاش بپردازند تا حق مطلب اداء شده و ترجمه و تفسیر صحیحی تحقق پذیرد. \nبا توجه به این واقعیت است که می‌گوییم: برای نهج البلاغه، ترجمه‌ها و تفاسیری گوناگون ضرورت دارد تا مفاهیم گران سنگ آن در تمام ابعاد، کشف و شناسایی گردد، زیرا پژوهشگری که تخصص او در رشته اقتصاد است و مباحث‌ اقتصادی نهج البلاغه را خوب می‌فهمد و با دیگر دیدگاه‌های اقتصادی جهان معاصر تطبیق می‌دهد، قطعا در مباحث روانشناسی نهج البلاغه ناتوان است و نمی‌تواند مفاهیم عمیق روانکاوی و روان درمانی این کتاب را کشف و شناسایی و در قالب عبارات زیبایی ارائه دهد. \nاز این رو، شروح و ترجمه‌های که هم اکنون در جهان اسلام موجود است این مشکل را دارند که در تمام ابعاد نهفته در نهج البلاغه، کارایی ندارند، زیرا هر کدام بر أساس شناخت و توانمندی خویش قلم زده‌اند.\nباید علوم و فنون نهفته شده در نهج البلاغه کشف و شناسایی گردد، سپس متخصصان هر یک از رشته‌ها، جذب و سازماندهی شوند و در یک حرکت پژوهشی دسته جمعی حساب شده، ترجمه نهج البلاغه با جامعیت بایسته‌ای سامان پذیرد که: فقیه جامع الشرائطی، مباحث فقهی نهج البلاغه را عمیقا کشف و شناسایی کند و روانشناسی، مباحث روانشناسی و روانکاوی نهج البلاغه را استخراج نماید و اقتصاد دانی، مباحث اقتصادی نهج البلاغه را در قالب‌های زیبایی طرح کند و هر متخصص صاحب نظری در یک هماهنگی لازم با دیگر متخصصان، نکته‌ها و ظرایف نهفته در رشته اختصاصی خویش را به خوبی بشناسد و هنرمندانه بشناساند. \nآنگاه می‌توان با خاطری آسوده بگوییم: کار ترجمه و تفسیر نهج البلاغه پایان یافت. \n\nج- مباحث فوق تخصصی:\nدسته‌ای دیگر از مباحث نهج البلاغه فوق تخصصی است و تنها با داشتن تخصص‌های مصطلح و معمولی در رشته‌های علمی، نمی‌توان در ژرفای مفاهیم آن شنا کرد بلکه ابزار و علوم و فنون خاص خودش را می‌طلبد تا بدرستی شرح و تفسیر گردند. \nدر این قسمت نیز ترجمه‌ها و برخی از شروح موجود نهج البلاغه نمی‌توانند به همه نیازهای پژوهشگران پاسخ مثبت دهند، مانند: \nالف- شناخت متشابهات نهج البلاغه، \nب- شناخت مباحث عمیق فقهی و حل تضادهای ظاهری،\nج- شناخت ضرب المثلها و استخراج پیامهای اصلی آن، \nد- روش رفع تعارض ظاهری بین حدیث و قرآن، \nه‍ - رفع تعارض ظاهری نهج البلاغه با مبانی اعتقادی. \nبا کمال تأسف باید اعتراف کنیم که تمام ترجمه‌های موجود و اکثر شروح نهج البلاغه، نسبت به این قسمت از مباحث فنی آسان گذشته‌اند. \nنه تنها پیام اصلی ضرب المثل‌ها را نیاورند بلکه تنها با ترجمه ظاهری عبارات، مشکلاتی نیز به وجود آورده‌اند.\nعام و خاص، مطلق و مقید، درست ترجمه نشده و اهداف آموزشی امام علیه السلام نا شناخته مانده، زیرا معلوم نشده چرا: یک جا دنیا را نکوهش کرده و در جای دیگر، آن را می‌ستاید در خطبه‌ای تعقیب فراریان مطرح است و در نامه‌ای دیگر ترک تعقیب یک جا دستور ریشه کن شدن مهاجمان و در چند خطبه دیگر دستور مدارا کردن با زخمی‌ها و فراریان مطرح است در خطبه‌ای حکومت را نکوهش و در خطبه‌ای دیگر، ستایش می‌کند و نسبت به بانوان مطالبی به ظاهر متضاد وجود دارد دنیا گرایی و ترک دنیا به هم آمیخته شده دخالت در مسائل سیاسی را با انزوا گرایی در کنار هم قرار داده است کار و تولید، با ترک مال دنیا در کنار هم قرار داده شده در نتیجه، خوانندگان ترجمه‌های موجود، دچار نوعی سرگردانی و اضطراب می‌شوند که سرانجام اهداف آموزشی امام علی علیه السلام کدام است و کدام یک را باید باور داشت این دسته از مباحث فوق تخصصی باید با روش‌های دقیق اجتهاد، و اصول صحیح استنباط، هماهنگ با مبانی اعتقادی شیعه و در چهار چوب علم «تعادل و تراجیح» که حضرت امیر المؤمنین علیه السلام و دیگر معصومین علیهم السلام به ما آموخته‌اند بررسی شود، تا نتایج مطلوب به دست آید که خود فرمودند و به ما آموختند:\n- تضاد و تعارض در آیات قرآن وجود ندارد. \n- تضاد و تعارض بین احادیث و قرآن وجود ندارد.\n- تضاد و تعارض بین احادیث معصومین علیهم السلام وجود ندارد.\nو در خطبه ۱۸۹ نهج البلاغه تذکر دادند که این گونه پژوهش‌ها از عهده هر کسی بر نیاید:\nإن أمرنا صعب مستصعب، لا یحمله الا عبد مؤمن امتحن الله قلبه للإیمان و لا یعی حدیثنا إلا صدور أمینة و احلام رزینة \nشناخت مسائل ما، کاری است بس دشوار، که جز بنده مؤمنی که خداوند قلبش را با ایمان آزموده، آن را نپذیرد، و احادیث ما را جز سینه‌های امانت دار و عقل‌های سالم نمی‌توانند نگاهبان باشند. \nاین دسته از مباحث را باید با فراگیری علوم مقدماتی حوزه‌های علمیه و آگاهی از اصول استنباط و شیوه‌های اجتهاد، به ارزیابی گذاشت و در ترجمه، حاصل تحقیقات عمیق و گسترده را گنجاند، تا خواننده ترجمه نهج البلاغه به آسانی پیام امام علیه السلام را دریافت کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (334, 5, 5, 'آرزوها', 'از آغاز تحصیلات حوزوی (سالهای ۴۳- ۵۰) که ترجمه‌های موجود را مطالعه می‌کردم و کاستی‌های مفهومی را می‌یافتم، نگران و در فکر چاره اندیشی بودم که یکی از نوشته‌های جرج جرداق مسیحی، (استاد ادبیات عرب در لبنان) را خواندم که اعتراف کرده بود: «جاذبه‌های کلمات امام علی علیه السلام شوری در من ایجاد کرد که ۲۰۰ بار نهج البلاغه را مطالعه کردم». \nچنان بر خود لرزیدم و به تعصب و غرور اعتقادی من ضربه وارد شد که تا مدتی حالت عادی نداشتم، و بر خود نفرین کردم که چرا یک مسیحی، ۲۰۰ بار نهج البلاغه را می‌خواند اما من که خود را از شیعیان امام علی علیه السلام می‌شمارم و ادعای محبت و ولایت او را دارم به راستی چند بار نهج البلاغه را خوانده‌ام و با مفاهیم ارزشمند آن به چه میزان آشنایی دارم ما که در خانواده شیعه، از پدر و مادری شیعه، و از خاندان شیعه و در کشور شیعیان قرار داریم، چرا باید با نهج البلاغه بیگانه باشیم‌. از روزی که در جلسات نویسندگی شهید مفتح (که روزهای جمعه در منزل ایشان در کوچه ممتاز قم برگزار می‌شد) شرکت کردم، موضوع انتخابی من «حکومت اسلامی» شد. \nشهید مفتح فرمود: یکی از منافع غنی که نسبت به حکومت اسلامی مباحث ارزشمند و گران سنگی دارد، نهج البلاغه است. این کتاب را مطالعه کنید و مباحث مربوط به حکومت را استخراج و سازماندهی نمایید. \nورود من به آستانه نهج البلاغه با رهنمودهای آن استاد والا مقام که پس از انقلاب اسلامی به درجه شهادت ممتاز شد، شکل گرفت. \nبه سراغ نهج البلاغه رفتم، اما: فهرست جامعی نداشت، معجم المفهرس کاملی نداشت، و در ابعاد گوناگون این کتاب از نظر پژوهشی کار نشده بود. \nقدم اول را با طرح تدوین (معجم المفهرس نهج البلاغه) آغاز کردیم و آرزوهای ما نسبت به جهانی و همگانی شدن نهج البلاغه، تا هم اکنون با تحقیق و تدوین و انتشار بیش از ۲۰ أثر تحقیقاتی تداوم یافت. آنگاه آرزوها و ایده ایده‌آلهای ما نسبت به ترجمه نهج البلاغه، که حاصل تلاش ۵ سال کار مداوم و تجربیات کلاس‌های آموزشی از رمضان تا رمضان است‌با ویژگیهای زیر (به منوی قبل مراجعه کنید) سامان یافت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (335, 5, 6, 'ویژگی‌های این ترجمه', '۱- عنوان دادن به مطالب:\nبرای استفاده عموم مراجعه کنندگان به سخنان علوی، سعی کردیم تا مفاهیم و مباحث نهج البلاغه را در قالب ۲۷۳۰ عنوان کلی و زیبا تقدیم داریم، زیرا عنوان زدن صحیح مطالب برای عموم افراد میسر نیست، و برخی در تطبیق مفاهیم کلی با مباحث نهج البلاغه دچار مشکل می‌شوند. \nعرضه مباحث نهج البلاغه در قالب عناوینی زیبا و گویا، مراجعه کنندگان را در یافتن مطالب دلخواه یاری می‌دهد و کار مطالعه و استخراج مفاهیم ارزشمند نهفته‌ در خطبه‌ها، نامه‌ها و حکمت‌ها را آسان می‌سازد. \n\n۲- نامگذاری خطبه‌ها، نامه‌ها، حکمت‌ها:\nیکی دیگر از ویژگی‌های این ترجمه، نامگذاری خطبه‌ها، نامه‌ها و حکمت‌هاست. با این روش در یک نگاه کلی محورهای أصلی مباحث نهج البلاغه به خوبی شناسانده می‌شود و مطالعه کنندگان را دریافتن موضوعات انتخابی در نهج البلاغه کمک می‌کند. \nموضوعاتی که در نامگذاری کلی خطبه‌ها، نامه‌ها و حکمت‌ها مطرح شده‌اند دارای حدود ۱۵۰۰ عنوان در ۷۴ رشته تخصصی است، که در آن سمت‌گیری و اهداف کلی مطالب نهج البلاغه، مشخص شده است. \n\n۳- اشاراتی به علوم و فنون:\nهرگز نخواسته‌ایم تا نظریه‌های علمی را بر نهج البلاغه تحمیل، یا نهج البلاغه را توجیهی برای دیدگاه‌ها و نظریه‌های علمی مطرح جهان معاصر قرار دهیم. \nهدف ما این بود که هر جا کاوش‌های علمی، و مرزهای اندیشه بشری توانسته پرده‌ای از روی واقعیتی بردارد و به افق همیشه نورانی نهج البلاغه نزدیک شود، آن نمونه‌ها را در پاورقی ترجمه آورده‌ایم، نمونه‌هایی که امروز در جهان علم با ابزارها و روش‌های پیشرفته شناسایی گردید، حال آن که در ۱۴ قرن قبل در کلمات نورانی امیر بیان حضرت علی علیه السلام آمده است. \nبه نمونه‌هایی توجه فرمایید: امام علیه السلام در خطبه‌های ۱۳ و ۱۴ در نکوهش مردم بصره، عوامل زیست محیطی را در فریب خوردگی مردم مطرح می‌فرماید که امروز در رشته اکولوژی به این گونه از واقعیت‌ها دست یافته‌اند. یا در روانشناسی و روانکاوی و انسان شناسی و تربیت کودک، دستوراتی دارند که در جهان معاصر در رشته‌های تخصصی علوم به اثبات رسیده است. \n\n۴- ویژگی عمومی بودن ترجمه:\nهدف مهم ما در این ترجمه، رعایت اصل سادگی، و عمومی بودن آن بود که همه اقشار و سطوح متفاوت فکری موجود در جامعه، بدون مراجعه به کتب تفسیری و لغت و تاریخ، بتوانند از آن استفاده کنند. \n\n۵- رعایت اصل «پیام رسانی» در ضرب المثل‌ها:\nدر فرهنگ همه ملت‌ها، ضرب المثل‌هایی وجود دارد. و در ضرب المثل‌ها پیام‌ها و اهدافی نهفته است. \nاگر در ترجمه‌ها «اصل پیام رسانی» رعایت نشود، ضرب المثل‌های اقوام و ملل برای یکدیگر قابل فهم نخواهد بود، و چون در ترجمه‌های موجود نهج البلاغه، غالبا اصل پیام رسانی رعایت نشده، و با ترجمه ظاهری عبارات، اهداف واقعی گوینده قابل دسترسی نیست، خواننده فارسی زبان حق دارد بپرسد: - من «کفتار نیستم» یعنی چه و کفتار چیست \n(و الله لا اکون کالضبع، خطبه ۵) \n- «چون بچه شتر دنبال شتر بودم» چه مفهومی دارد \n(کاتباع الفصیل أثر أمه، خطبه ۱۹۲) \n- «زن عقرب است» یعنی چه چرا باید به زنان اهانت شود \n(المرأة عقرب) \nاگر بدانند که این ضرب المثل‌ها برای رساندن مفهوم خاصی در ملت‌ها پدید آمدند، و پیام خاصی را در شرائط حاکم بر همان ملت و همان فرهنگ به همراه دارند. \nو اگر در ترجمه ضرب المثل‌ها، اصل پیام رسانی رعایت گردد، بسیاری از اعتراضات مطرح نخواهد شد. \nپس از تحقیق و بررسی در ضرب المثل‌های نهج البلاغه، و شناخت کاربردی آن در روزگاران گذشته به این نتیجه می‌رسیم که امام علیه السلام در خطبه ۵ می‌خواهد بفرماید که: «من از مسائل سیاسی کشور غافل نیستم.» و در خطبه ۱۹۲ این مفهوم را می‌خواهد منتقل کند که: «من چونان فرزندی که از مادر جدا نمی‌شود، همواره با پیامبر بودم.» و حکمت ۶۱، یک ضرب المثل است و این پیام را دارد که: «نیش زن برای شوهرش شیرین است» \n\n۶- تفسیر صحیح متشابهات:\nواژه‌های دنیا، حکومت، لذت، عشق، مال و برخی دیگر از کلمات و عبارات، در جاهای گوناگون، متفاوت و در ظاهر متضاد وصف و مطرح شده‌اند: در یک جا عام و فراگیر و در جایی خاص و محدود است. \nاگر دنیا در برخی از خطبه‌ها نکوهش شد، دنیای عام و دنیای خارج و نظام هستی نیست، بلکه علل و عواملی مطرح است، زیرا در موارد دیگر دنیا را می‌ستاید. \nاگر یک جا حکومت را نکوهش می‌کند، در جای دیگری آن را یک ضرورت می‌داند، اگر یک جا نسبت به محبت دنیا هشدار می‌دهد، در جای دیگری محبت دنیا را امری طبیعی می‌داند. \nو ده‌ها و صدها نمونه که احتیاج به پژوهش‌های عمیق دارد. \nدر این گونه از موارد نباید به ترجمه ظاهری عبارات رضایت داد، زیرا برای خوانندگان، هدایتگر نخواهد بود. گاهی باید تفسیر و ترجمه را به هم آمیخت، تا اهداف اصلی و مبانی اعتقادی امام علیه السلام، روشن و بدون ابهام در دسترس عموم قرار گیرد. در ترجمه‌های موجود. \n«النساء نواقص العقول» \nرا همه چنین ترجمه کرده‌اند: «عقل زنان ناقص است.» و \n«المرأة شر کلها» \nرا همه ترجمه کرده‌اند که: «زن همه‌اش شر است.» در صورتی که در قرآن کریم و نهج البلاغه، نظام هستی، نظام أحسن معرفی شده، و امام علیه السلام خود عدل الهی را به جهانیان شناسانده است. \nو می‌دانیم که خدا شر نیافریده، و پدیده‌های پروردگاری شر نیستند. \nپس قرآن و سنت مسلمه و فلسفه و کلام و مبانی اعتقادی با ظاهر عبارات یاد شده در تضاد است، و در ترجمه‌های ظاهری الفاظ و عبارات نیز، تضادها بر طرف نمی‌گردد، که همواره اعتراض‌های خوانندگان را به همراه خواهد داشت. \nاگر پس از بررسی‌های لازم به این نتیجه برسیم که: امام در خطبه ۸۰ در مقام بیان تفاوت‌های موجود میان زنان و مردان است، و «نواقص» در اینجا به معنای «تفاوت» است. \nو در حکمت ۲۳۸، واژه شر را به معنای بد و بدی‌ها استعمال نکرده، بلکه خواسته تا بفهماند که، ازدواج و تشکیل زندگی مسئولیت‌ها و مشکلاتی دارد، همان ضرب المثل فارسی که می‌گویند: «زن و بچه دردسرند و بی دردسر هم نمی‌توان زندگی کرد». آنگاه برداشت‌های منفی و اعتراض‌های بیجا مطرح نخواهد شد.\n\n۷- آوردن نکات تاریخی \n\n۸- آوردن اسامی اشخاص و أماکن:\nنکات تاریخی، و اسامی اشخاص و اماکن را بدان جهت آوردیم تا خوانندگان با داشتن همین ترجمه از مراجعه به دیگر کتاب‌های تاریخی بی نیاز باشند. \n\n۹- آوردن شأن نزول خطبه‌ها:\nبا تلاش فراوان، آن مقدار از حوادث و تحولات و زمینه‌هایی را که در پدید آمدن خطبه‌ها، نامه‌ها و حکمت‌ها نقش داشتند، به عنوان «شأن نزول» در آغاز خطبه‌های آوردیم تا خوانندگان با حال و هوای صدور مطالب نهج البلاغه آشنا شوند، و بدانند که نهج البلاغه کجایی است زیرا توجه به شأن نزول نهج البلاغه تأثیر تعیین کننده‌ای در فهم مطالب و مفاهیم آن خواهد داشت.\n\n۱۰- فهرست موضوعی:\nیکی دیگر از آرزوهای ارزشمند، تهیه و تدوین «فهرست موضوعی نهج البلاغه» بود که همواره ذهن ما را به خود مشغول می‌داشت زیرا ترجمه‌های موجود بدون فهرست موضوعی منتشر شده بودند. \nاستخراج عناوین کلی و جزئی و سازماندهی عناوین، و تطبیق علوم و فنون با مفاهیم نهج البلاغه، و خلق و ابداع فهرست‌های زیبا و قالب‌های گویا برای شناخت مباحث آن، سالیان طولانی ما و جمعی از پژوهشگران مؤسسه را به تلاش واداشت: ابتدا کتاب «فرهنگ موضوعات کلی نهج البلاغه» را با حدود چهار هزار عنوان در ۷۴ رشته تخصصی تدوین و منتشر ساختیم. \nسپس در مدت ۷ سال «فرهنگ معارف نهج البلاغه» را با همکاری جمعی از محققان مؤسسه، در پانزده هزار عنوان کلی و جزئی فراهم آوردیم. \nو در یک اقدام گسترده و فراگیر در تدوین «دائرة المعارف بزرگ نهج البلاغه» کار استخراج فهرست‌ها و قالب‌های مفهومی را به صد هزار عنوان رساندیم، که در فهرست موضوعی این ترجمه، تنها بخشی از آن‌ها را آورده‌ایم. \n\n۱۱- آوردن توضیحات ضروری در متن و پاورقی:\nچون هدف ما در این ترجمه، بهره برداری عموم اقشار جامعه بود، که در سفر و حضر بتوانند از نهج البلاغه استفاده کنند، و در همه جا منابع تحقیق و کتب تاریخی یافت نمی‌شود و همه جا نمی‌توان از شروح گوناگون نهج البلاغه استفاده کرد بر آن شدم تا توضیحات ضروری نسبت به مفاهیم، رویدادها، تحولات سیاسی، تاریخی را در متن یا در پاورقی بیاوریم که عموم اقشار جامعه بتوانند از مباحث ارزشمند آن استفاده کنند. \n\n۱۲- فهرست مطالب کتاب:\nیکی دیگر از ویژگی‌های این ترجمه، همراه داشتن فهرست مطالب در اول کتاب است، که سمت گیری کلی خطبه‌ها و نامه‌ها و حکمت‌ها در آن مشروحا بیان شده است، و در مفاهیم نهج البلاغه را در دو سه هزار عنوان زیبا و گویا می‌توان یافت و مورد ارزیابی قرار داد، این ویژگی در بسیاری از ترجمه‌ها وجود ندارد. \n\n۱۳- آوردن اختلاف نسخه‌ها (در متن عربی):\nمتن عربی نهج البلاغه با ۱۵ نسخه قدیمی از اواسط قرن چهارم (۱۵) سال پس از وفات سید رضی) تاکنون، مقابله و تصحیح شد، و اختلافات مهم و عبارات گوناگونی که در معانی جملات تأثیر داشت در داخل علامت () آورده شد تا محققان و شارحان در مراجعه به این نسخه بتوانند جنبه‌های گوناگون یک واژه یا یک جمله را دریابند. \n\n۱۴- شماره گذاری متن عربی (یک خط در میان):\nدر سال‌های (۱۳۵۳- ۱۳۵۴) که به فکر تدوین معجم المفهرس نهج البلاغه افتادیم اولین مسئله‌ای که در راستای کار ما قرار داشت این بود که متن نهج البلاغه یک خط در میان شماره گذاری شود و همه آدرس‌ها بر أساس شماره‌های متن سازماندهی گردد تا یافتن مطالب دلخواه در خطبه‌ها و نامه‌ها و حکمت‌های طولانی به آسانی میسر باشد و مراجعه کننده ناچار نباشد برای یافتن یک واژه یک یا چند صحفه را جستجو کند. \nو چون پس از انتشار معجم المفهرس در ایران و دیگر کشورهای اسلامی، شماره‌های ثبت شده، و در صدها هزار نسخه موجود است، در این ترجمه نیز همان شماره‌ها همراه متن عربی ثبت شد تا برای عموم محققان اسلامی قابل استفاده باشد. \n\n۱۵- آوردن موضوعات کلی خطبه‌ها، نامه‌ها، حکمت‌ها:\nگر چه در کتاب (فرهنگ موضوعات کلی نهج البلاغه) موضوعات عام و کلی‌ خطبه‌ها و نامه‌ها و حکمت‌های نهج البلاغه را مشروحا آورده‌ایم اما در این نسخه در متن عربی، زیر شماره‌های خطبه‌ها و نامه‌ها و حکمت‌ها، عناوین کلی و فهرست‌های عام هر یک را ثبت کرده‌ایم که گویای مفاهیم نهفته در آن می‌باشد و مراجعه کننده در اولین بر خورد با واژه (سیاسی، اجتماعی، اخلاقی) همراه خطبه یا نامه یا حکمت، سمت‌گیری آن را درک می‌کند و می‌فهمد که این خطبه اخلاقی یا آن نامه سیاسی است که در پیام رسانی و ارزیابی آغازین مباحث نهج البلاغه نقش بسزائی دارد، این ویژگی در هیچ کدام از نسخه‌های موجود بچشم نمی‌خورد. \n\n۱۶- ترجمه توضیحات سید رضی با عنوان (می‌گویم):\nدر بسیاری از ترجمه‌های نهج البلاغه و شرح و تفسیر آن، توضیحات ارزشمند سید رضی ترجمه نشده است و برخی از آوردن آن نیز صرف نظر کرده‌اند در صورتی که توضیحات سید رضی از نظر لغوی و أدبی آثار بسیار تعیین کننده‌ای در فهم مطالب نهج البلاغه دارد. \nدر این نسخه تمامی توضیحات سید رضی هر کدام در جایگاه خاص خودش با تغییر قلم‌ها به گونه‌ای زیبا آورده شد. \n\n۱۷- مشخص کردن مرجع ضمائر:\nدر این ترجمه، تلاش کرده‌ایم تا مرجع ضمائر (هو، هم، انهم، انها) را دقیقا مشخص کرده و در ترجمه بیاوریم که قابل استفاده عموم طبقات جامعه باشد و مطالعه کننده این نسخه جایگاه اصلی ضمائر را بشناسد. \n\n۱۸- مشخص کردن مخاطب‌های امام علیه السلام در نهج البلاغه:\nیکی دیگر از ویژگی‌های این ترجمه که برای عموم مراجعه کنندگان قابل استفاده می‌باشد و کاربرد عمومی دارد، مشخص کردن مخاطب‌های امام علیه السلام در نهج البلاغه است که امام علی علیه السلام با چه کسی یا افرادی سخن می‌گوید شناخت مخاطب‌های امام علیه السلام ما را در ارزیابی مفاهیم نهج البلاغه کمک می‌کند. \n\n۱۹- تفسیر صحیح عام، خاص، مطلق، مقید:\nیکی از مشکلات پژوهشی و نظری عموم افراد جامعه ما، مشکل حل متشابهات، و بررسی تطبیقی عام و خاص و مطلق و مقید در احادیث و روایات اسلامی است. \nو چون همه مترجمان نهج البلاغه شیوه ترجمه تحت اللفظی را برگزیدند، مراجعه کنندگان به ترجمه‌ها دچار نوعی سرگردانی در فهم نهج البلاغه می‌شوند که در یک خطبه دنیا نکوهش و در خطبه دیگری ستایش شده است چرا و می‌پرسند آیا این اختلاف و تناقص را می‌شود حل کرد اگر مترجمان در مباحث عام و خاص و مطلق و مقید، رعایت مفاهیم را می‌کردند و عام تخصیص زده را به گونه‌ای مطلق و فراگیر ترجمه نمی‌کردند و مطلق را با قید آن می‌آوردند، این همه حیرت و سرگردانی وجود نداشت. \nما سعی کرده‌ایم این اصل را رعایت کنیم، اگر در جائی دنیا نکوهش می‌شود با بررسی‌های مداوم علت آن را آورده‌ایم که دنیای حرام یا شبهه‌ناک است، و اگر در جای دیگر دنیا ستایش می‌شود توضیح داده‌ایم که دنیای حلال است. \n\n۲۰- تبیین صحیح اهداف امام علیه السلام در طرح ارزشها:\nبسیاری از مترجمان شیوه ترجمه تحت اللفظی را برگزیدند و معتقدند که: (ما باید تعهد خود را در ترجمه پاسدار باشیم) البته این خود مکتبی و راه و روشی است محترم و ارزشمند اما از نظر کار بردی و پیام رسانی مشکلات فراوانی را به همراه دارد، با دگرگونی فرهنگ‌ها و تمدن‌ها، و دگرگونی واژه‌ها در پیام رسانی‌ها، نسل امروز معانی و مفاهیم نهفته در برخی از عبارات قرآن و احادیث را نمی‌تواند درک کند، در صورتی که ما به امام علی علیه السلام تعهد داده‌ایم تا پیام او را به نسل امروز برسانیم و در این پیام رسانی باید از انواع سبک‌ها و شیوه‌ها استفاده کنیم و گاهی نیز ناچاریم مقید به ظاهر الفاظ و عبارات نباشیم. \nپس برای تبیین صحیح اهداف امام علیه السلام در طرح ارزشها ناچاریم از شیوه ترجمه تحت اللفظی اندکی فاصله گرفته و با تفسیر و ارزیابی صحیح عبارات و کلمات، پیام‌ حضرت مولی علیه السلام را به نسل امروز برسانیم. \n\n۲۱- آوردن آدرس آیات قرآن:\nاینکه گفته‌اند: «نهج البلاغه فوق کلام خلق است» حرفی است ارزشمند و دوست داشتنی، اما در دنباله آن قضاوت فرمودند که: «دون کلام خالق است» این سخن را نشاید که پذیرفت. زیرا نهج البلاغه در تفسیر و تحلیل کلام خداوندی است، نور است و در امتداد نور الهی می‌درخشد، دون، پستی، پایینی، و هر واژه دیگری که سمت و سوی نزول و فرود و سقوط داشته باشد نسبت به نهج البلاغه مردود است، مطرود است باید گفت که: «نهج البلاغه فوق کلام خلق و در پرتو کلام خالق است» که حدیث تفسیر آیات الهی است، واسطه فیض است، در همان اوج و والائی وحی الهی جای دارد، از این رو آدرس تمام آیات قرآن نهفته در کلام حضرت مولی علیه السلام را در پاورقی متن عربی آورده‌ایم تا اگر کسی خواست به قرآن مراجعه کند میسر باشد. \n\n۲۲- آوردن برخی از اشعار ضروری:\nاین هدف بسیار ارزشمند هنری، آموزشی را در طول ۱۲ سال لباس عمل پوشانده و ۱۵۰ دیوان شعر شاعران فارسی زبان را بر سر سفره نورانی نهج البلاغه نشاندیم که فرهنگ شعر شاعران در نهج البلاغه در دو جلد انتشار یافته است تا اساتید مجرب در تدریس مفاهیم حدیث از جاذبه هنری اشعار استفاده کنند. در این ترجمه نیز هر جا که بسیار ضروری تشخیص داده‌ایم در پاورقی برخی از اشعار زیبا و شیرین را آورده‌ایم. \n\n۲۳- آوردن مدارک احادیث موجود در کتاب:\nبا عنایات الهی و توجهات خاصه حضرت امیر المؤمنین علیه السلام اسناد و مدارک خطبه‌ها و نامه‌ها و حکمت‌ها را در کتاب مستقلی با نام (اسناد و مدارک نهج البلاغه) که جلد یازدهم (مجموعه آشنائی با نهج البلاغه) می‌باشد مشروحا آورده‌ایم، اما در این نسخه به مدارک و منابعی که حضرت سید رضی قدس سره آورده است‌ رضایت دادیم. \n\n۲۴- آوردن آمار و ارقام ضروری:\nآمار و ارقام، ما را در ارزیابی و نتیجه‌گیری کمک می‌کنند، و در تحلیل تاریخ نقش بسزائی خواهند داشت، در این نسخه هر کجا که لازم بود، از آوردن ارقام و آمار خود داری نورزیده و با استفاده از منابع تاریخی آمار ضروری را ثبت کرده‌ایم. \n\n۲۵- بررسی تطبیقی مکاتب (در پاورقی):\nدر طول سالهای ۱۳۵۳ تاکنون، که در مراکز گوناگون آموزشی، در دبیرستانها و مراکز تربیت معلم و دانشگاهها، به تدریس نهج البلاغه و معارف اسلام اشتغال داشته و داریم، با این حقیقت روشن آشنا شدیم که در تمام محورهای خط فکری نسل جوان، نهج البلاغه رهنمودهای تعیین کننده دارد، در هجوم ایسم‌ها و ایست‌ها، بهترین پناهگاه است که قدرت دفاعی لازم به نسل ما می‌دهد تا ارزشهای اسلامی خود را پاسدار باشند، و تمامی مکاتب وارداتی را بتوانند عالمانه نقد کنند. \nاز این رو در پاورقی این نسخه سعی کرده‌ایم اشاراتی به مکاتب مصطلح روز داشته باشیم تا خوانندگان این ترجمه رهنمودی در نقد و ارزیابی مکاتب و بررسی تطبیقی آن داشته باشند، و برای تحقیقات گسترده‌تر به کتب و منابع خاص خودش مراجعه کنند. \n\nامیدواریم این ترجمه، با ویژگی‌های یاد شده، خشنودی حضرت مولی الموحدین امیر المؤمنین علیه السلام را- هر چند اندک- فراهم کرده باشد، که اهل و معدن و اصل کرامت است و با بزرگواری خویش، دست روسیاهان بر خاک مانده را خواهد گرفت. \nان شاء الله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (336, 3, 1, 'روش برخورد با فتنه‌ها', 'به هنگام بروز فتنه چون اشتر دو ساله باش که نه پشتى استوار دارد که بر آن سوار توان شد و نه پستانى شیرده، که از آن شیر توان دوشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (337, 3, 2, 'شناخت ضد ارزش‌ها', 'آنکه طمع را شعار خود سازد خود را خوار ساخته است و هر که پریشان حالى خویش با دیگران در میان نهد تن به ذلت داده است و کسى که زبانش بر او فرمان راند بی‌ارج شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (338, 3, 3, 'ضد ارزش‌ها', 'بخل، ننگ است و ترس، نقص است و تنگدستى، زبان زیرک را از بیان حجتش لال می‌کند و بینوا در شهر خود غریب است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (339, 3, 4, 'ارزش‌هاى اخلاقى و ضد ارزش‌ها', 'ناتوانى، آفت است و شکیبایى، دلیرى است و زهد، توانگرى است و پارسایى، چونان سپر است. بهترین همنشین، خشنودى از خواست خداوند است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (340, 3, 5, 'شناخت ارزش‌هاى اخلاقى', 'دانش، میراثى گرانمایه است و آداب، زیورهایى نو به نو و اندیشه، آیینه‌ای شفاف است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (341, 3, 6, 'ارزش راز دارى و خوشرویى', 'سینه عاقل صندوقچه راز اوست و خوشرویى دام دوستى است و بردبارى، گور عیوب است. نیز روایت شده که آن حضرت در بیان این معنى فرموده است: آشتى‌جویى، سرپوش عیبهاست. هر که از خود خشنود بود، خشم گیرندگانش بسیارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (342, 3, 7, 'ایثار اقتصادى و آخرت گرایى', 'صدقه دارویى شفا‌بخش است و اعمال بندگان در این جهان، در آن جهان مقابل چشم آن‌هاست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (343, 3, 8, 'شگفتى‌هاى تن آدمى', 'از این انسان در شگفت شوید، به قطعه‌ای پیه می‌بیند و به پاره گوشتى سخن می‌گوید و به تکه استخوانى می‌شنود و از شکافى نفس می‌کشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (344, 3, 9, 'ره آورد اقبال و ادبار دنیا', 'وقتى که دنیا به قومى روى آورد، خوبیهاى دیگران را به آن‌ها عاریت دهد و چون پشت کند، خوبیهایشان را از ایشان بستاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (345, 3, 10, 'روش زندگى با مردم', 'با مردم به گونه‌ای بیامیزید، که اگر بمیرید، بر شما بگریندو اگر بمانید به دیدارتان مشتاق باشند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (346, 3, 11, 'روش برخورد با دشمن', 'چون بر دشمن ظفر یافتى، عفو و گذشت را شکرانه پیروزیت قرار ده.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (347, 3, 12, 'آیین دوست یابى', 'ناتوانترین مردم کسی است که در یافتن دوست ناتوان باشد وناتوانتر از او کسی است که دوستان خود را از دست بدهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (348, 3, 13, 'روش استفاده از نعمت‌ها', 'چون نشانه‌های نعمت خدا آشکار گردید با کم سپاسى آن‌ها را از خود دور نسازید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (349, 3, 14, 'روش برخورد با خویشاوندان', 'کسى را که نزدیکانش واگذارند، بیگانه او را پذیرا می‌گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (350, 3, 15, 'روش برخورد با فریب خورده گان', 'هر فریب‌خورده‌ای درخور عتاب نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (351, 3, 16, 'شناخت جایگاه جبر و اختیار', 'کارها دستخوش تقدیرند، آن سان، که گاه تدبیر سبب مرگ شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (352, 3, 17, 'ضرورت رنگ کردن موها', 'از آن حضرت در باره این سخن پیامبر (ص) پرسیدند که فرموده بود: رنگ سفید موى را تغییر دهید و خود را همانند یهودان مسازید على (ع) فرمود: این سخن را رسول الله (ص) زمانى فرمود که مسلمانان اندک بودند، اما اکنون که دایره اسلام فراخ گردیده و دین استقرار یافته، هرکس به اختیار خود است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (353, 3, 18, 'ره آورد شوم فرار از جنگ', 'در باره کسانى که از پیکار در کنار او، کناره گرفته بودند، حق را واگذاشتند اما باطل را یارى نکردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (354, 3, 19, 'ره آورد شوم هواپرستى', 'هر که عنان مرکب آرزو را رها کند و در پى آن بشتابد مرگش بلغزاند و بر زمین زند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (355, 3, 20, 'روش برخورد با جوانمردان', 'از لغزش‌هاى جوانمردان درگذرید، که هیچیک از آنان نلغزد، مگر آنکه دست لطف خداوند، او را بلند‌مرتبه می‌سازد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (356, 3, 21, 'ارزش‌ها و ضد ارزش‌ها', 'ترس قرین نومیدى است و شرم، با محرومیت همراه است و فرصت‌ها می‌گذرد، چونان که ابرها می‌گذرند. فرصت‌هاى نیکو را غنیمت شمرید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (357, 3, 22, 'روش گرفتن حق', 'ما را حقى است که اگر آن را به ما دادند، بگیریم و اگر ندادند، بر ترک شتر سوار می‌شویم، هر چند، سیر در شب باشد و به درازا کشد. \r\nسید رضى گوید: این سخن، سخنى لطیف و فصیح است. یعنى اگر حق ما را ندهند، ما در زمره خوارشدگان باشیم زیرا در ردیف پشت سر راکب کسی می‌نشیند که برده، اسیرو یا کسی است که در مرتبه اینان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (358, 3, 23, 'ضرورت عمل گرایى', 'آن کس که کردارش او را به تاخیر اندازد نسب او نمی‌تواند آن عقب ماندگی را جبران کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (359, 3, 24, 'روش یارى کردن مردم', 'از کفاره‌های گناهان بزرگ یاری ستم‌دیدگان و برطرف کردن اندوه غم‌دیدگان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (360, 3, 25, 'ترس از خدا در فزونى نعمت‌ها', 'اى پسر آدم، اگر دیدى که خداى سبحان نعمت‌های خود را پى درپى به تو ارزانى می‌دارد و تو نافرمانیش می‌کنى، از کیفر او بترس.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (361, 3, 26, 'نقش روحیات در تن آدمى', 'هیچکس چیزى را در دل نهان ننمود، جز که در سخنان بی‌اندیشه اش آشکار گشت یا در صفحات چهره اش، هویدا شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (362, 3, 27, 'روش درمان دردها', 'با درد خود بسازتا جایی که با تو همراهی می‌کند. (از پای نینداخته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (363, 3, 28, 'برترین پارسایى', 'برترین زهد، پنهان کردن آن است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (364, 3, 29, 'ضرورت یاد مرگ', 'هنگامى که تو زندگى را پشت سر مى‌گذارى و مرگ به تو روى مى‌آورد، پس دیدار با مرگ چه زود خواهد بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (365, 3, 30, 'پرهیز از غفلت زدگى', 'هشدار! هشدار! به خدا سوگند، خداوند چنان پرده پوشى کرده است که پندارى تو را بخشیده است!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (366, 3, 31, 'شناخت پایه‌هاى ایمان', 'فرمود: ایمان بر چهار پایه استوار است صبر، یقین، عدل و جهاد اما صبر بر چهار شاخه است: شوق و ترس و زهد و انتظار پس آن که مشتاق بهشت باشد دامن از شهوات برمی چیند و هرکس از آتش دوزخ بهراسد از گناهان بپرهیزد، و آن کس که در دنیا زهد ورزد مصیبت‌ها را خوار می‌دارد و کسی که در انتظار مرگ باشد در کارهای نیک شتاب می‌کند\r\nو یقین بر چهار شاخه است: بینش هوشمندانه، تفسیر حکیمانه، پند گرفتن از گذشت زمان معرفت و شناخت سنت پیشینیان پس آن که زیرکانه دید حکمت بر وی آشکار گردید، و آن را که حکمت آشکار گردید عبرت آموخت، و آن که عبرت آموخت گویی که در میان گذشتگان ایستاده است\r\nو عدل بر چهار شاخه است. به فهمی ژرف اندیش و دانشی عمیق و به حقیقت رسیده و نیکو داوری کردن و در بردباری استوار بودن هر کس که فهمید ژرفای علم را در می‌یابد و آنکه به ژرفای علم رسید از چشمه زلال شریعت نوشید و آن که بردبار باشد در امور خود زیاده‌روی نمی‌کند و در میان مردم ستوده زندگی می‌کند\r\nو جهاد نیز بر چهار شاخه است بر امر به معروف و نهی از منکر راستگویی در هر حال و دشمن داشتن فاسقان پس آن کس که امر به معروف می‌کند پشت مومنان را محکم می‌کند و آن که از منکر باز می‌دارد بینی کافران را در خاک می‌مالد و آن که در پیکار با دشمنان پایدار بود، حقی را که بر گردن دارد ادا نمود و آن که فاسقان را دشمن دارد و برای خدا به خشم آید، خدا به خاطر او خشم آورد و روز رستاخیز وی را خشنود نماید \r\nو کفر بر چهار ستون پایدار است: پی وهم رفتن و خصومت کردن و انحراف از حق و دشمنی ورزیدن پس آن که پی وهم گرفت به حق بازنگشت، و آن که از نادانی فراوان، خصومت ورزید نابینایش از دیدن حق استمرار می‌یابد و آن که از راه حق به دیگر منحرف گردید، نیکی در نزد او \"بد\" می‌شود و بدی در نزد او \"نیک\" می‌شود و مست گمراهی گردید، و آن که دشمنی ورزید راهها برایش دشوار شد و کارش بر او پیچیده می‌شود \r\nو شک بر چهار شاخه استدر گفتار جدال نمودن و ترسیدن و دودل بودن، و تسلیم حادثه‌های روزگار گردیدن پس آن که جدال را عادت خود کرد، از تاریکی شبهات بیرون نخواهد آمد و آن کس که از هر چیزی ترسید پیوسته عقب نشینی می‌کند و آن که در تردید ماند زیر پای شیاطین کوبیده خواهد شد و آن که به تباهی دنیا و آخرت گردن نهاد هر دو جهانش نابود می‌شود. \r\nسید رضی می‌گوید: و پس از این سخن گفتاری بود که از بیم درازی و برون شدن از روش کاری که در این کتاب مقصود است نیاوردیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (367, 3, 32, 'ارزش و والایى انجام دهنده کار خیر', 'نیکوکار از کار نیک بهتر است و بد کردار از کار بد بدتر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (368, 3, 33, 'اعتدال در بخشش و حسابرسى', 'بخشنده باش اما زیاده‌روی نکن در زندگی حساب‌گر باش اما سخت‌گیر مباش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (369, 3, 34, 'راه بی‌نیازى', 'شریفترین بی‌نیازی ترک آرزوهاست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (370, 3, 35, 'ضرورت موقعیت شناسى', 'هر که بی محابا به مردمان آن گوید که خوش ندارند درباره‌اش آن گویند که ندانند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (371, 3, 36, 'آرزوهاى طولانى و بزهکارى', 'هر که دامنه آرزوهایش گسترده شود، کردارش ناروا گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (372, 3, 37, 'ضرورت ترک آداب جاهلى', 'و چون دهقانان انبار هنگام رفتن امام به شام او را دیدند، برای وی از اسب‌هاشان پیاده شدند و پیشاپیشش دویدند. فرمود: این چه کار بود که کردید؟ گفتند: عادتی است که داریم و بدان امیران خود را بزرگ می‌شماریم على (ع) فرمود: به خدا که امیران شما از این کار سودی نبردند و شما در دنیایتان خود را بدان به رنج می‌افکنید و در آخرتتان بدبخت می‌گردید و چه زیانبار است رنجی که کیفر در پی آن است و چه سودمند است آسایشی که با آن از آتش امان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (373, 3, 38, 'ارزش‌ها و آداب معاشرت با مردم', 'پسرم! چهار چیز از من بیاد دار، و چهار دیگر به خاطر سپار که تا بدان‌ها عمل کنی زیان نبینی گرانمایه ترین بی‌نیازی خرد است، و بزرگترین فقر بی‌خردی است و ترسناکترین تنهایی خودپسندی است و گرامی‌ترین ارزش خانوادگی، اخلاق نیکوست. پسرم! از دوستی نادان بپرهیز چه او خواهد که تو را سود رساند لیکن دچار زیانت گرداند و از دوستی بخیل بپرهیز، که از تو دریغ دارد، آنچه را سخت بدان نیازمندی و از دوستی بدکار بپرهیز که به اندک بهایت بفروشد و از دوستی دروغگو بپرهیز که او سراب را ماند، دور را به تو نزدیک و نزدیک را به تو دور نمایاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (374, 3, 39, 'جایگاه واجبات و مستحبات', 'عمل مستحب انسان را به خدا نزدیک نمی‌گرداند اگر به واجبات زیان رساند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (375, 3, 40, 'راه شناخت عاقل', 'زبان عاقل در پشت قلب اوست، و قلب نادان پشت زبان او قرار دارد. \r\nسید رضی می‌گوید: و این از معنی‌های شگفت و پرارزش است و مقصود امام (ع) این است که: خردمند زبان خود رها نکند مگر بعد ازمشورت و فکر و سنجش ولى احمق حرف‌ها و سخنانى که از زبانش می‌پرد قبل ازمراجعه به فکر و دقت و اندیشه است پس چنان است که گویی زبان خردمند پیرو قلب اوست و قلب نادان پیرو زبان او.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (376, 3, 41, 'راه شناخت احمق', 'قلب احمق در دهان اوست و زبان عاقل در قلب او و معنی هر دو یکی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (377, 3, 42, 'بیمارى و پاک شدن گناهان', 'امام علی (ع) به یکى از یارانش هنگامى که بیمار شده بود فرمود: خداوند این درد و ناراحتى تو را باعث از بین رفتن گناهانت قرار داده است زیرا بیمارى پاداشى ندارد و لکن گناهان را از میان میبرد و آن‌ها را همچون برک درختان می‌ریزد اجر و پاداش تنها در گفتار به زبان، و کردار با دست‌ها و قدم‌ها است و خداوند سبحان وارد می‌کند به خاطر نیت راست و درون پاک به بهشت هر کس از بندگانش را که بخواهد. \r\nسید رضی می‌گوید\r\nمن می‌گویم آنچه امام علیه السلام فرموده درست است بیمارى اجر و پاداش ندارد چرا که بیمارى از قبیل چیزهائى است که استحقاق عوض دارد نه پاداش زیرا عوض در برابر رفتار خداوند بزرگ است نسبت به بنده خود در ناملایمات زندگی و بیماری‌ها و همانند آن و اما اجر و ثواب در برابر کارى است که بنده انجام می‌دهد بنابر این بین این دو فرق است که امام علیه السلام با آن علم نافذ و نظریه رساى خود، آنرا بیان فرموده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (378, 3, 43, 'الگوهاى انسانى', 'خدا بیامرزد خباب پسر أرت را به رغبت اسلام آورد و از روی فرمانبرداری هجرت کرد و به زندگى ساده قناعت نمود و از خدا راضی بود و مجاهد زندگی نمود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (379, 3, 44, 'ارزش آخرت گرایى', 'خوشا به حال کسی که بیاد معاد باشد و براى روز حساب عمل کند و به‌مقدار کفایت قناعت نماید و از خدا راضى باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (380, 3, 45, 'راه شناخت مؤمن و منافق', 'اگر با این شمشیرم بر بینى مؤمن بزنم که مرا دشمن بدارد دشمن نخواهد داشت و اگر تمام دنیا را در گلوى منافق بریزم که مرا دوست بدارد، دوست نخواهد داشت واین، از آنروست، که حکم خداى بر زبان پیامبر امى (صلى الله علیه و آله) گذشت. که فرمود: «اى على! هیچ مؤمنى ترا دشمن نمیدارد و هیچ منافقى تو را دوست نخواهد داشت»!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (381, 3, 46, 'ارزش پشیمانى و زشتى غرور زدگی', 'گناهى که اندوهگینت سازد در نزد خدا بهتر است از کار نیکى که به خودپسندیت وادارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (382, 3, 47, 'شناخت ارزش‌ها', 'ارزش هر کس به اندازه همت اوست و صدق او به قدرجوانمردى اوست و دلیری او به میزان ننگی - که از بدنامی - دارد و پاکدامنی هر کس به اندازه غیرت او است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (383, 3, 48, 'راز دارى و پیروزى', 'پیروزی به دور اندیشی است و دوراندیشی در به کارگیری صحیح اندیشه و اندیشه صحیح به نگاه داشتن اسرار است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (384, 3, 49, 'شناخت بزرگوار و پست فطرت', 'بپرهیزید از حمله بزرگوار چون گرسنه شوند و از ناکس چون سیر گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (385, 3, 50, 'راه جذب دلها', 'دل‌هاى مردم رمنده است هر که آن را به خود انس داد به او روی نهند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (386, 3, 51, 'قدرت و عیب پوشى', 'عیب تو پوشیده است تا بخت مساعد توست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (387, 3, 52, 'روش برخورد با شکست خوردگان', 'سزاوارترین مردم به بخشودن تواناترین آن‌ها به مجازات است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (388, 3, 53, 'شناخت جایگاه سخاوت و ایثارگری', 'سخاوت آن است که خود آغازگر آن باشی اما آن چه در برابر تقاضا بخشند یا از شرم است و یا از بیم سخن زشت شنیدن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (389, 3, 54, 'ارزش‌هاى اخلاقى', 'هیچ بی‌نیازی چون عقل نیست و هیچ فقری چون جهل نیست و هیچ میراثی چون ادب نیست و هیچ پشتیبانی چون مشاوره نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (390, 3, 55, 'اقسام بردبارى', 'صبر دوگونه است صبر بر آنچه دوست نمی‌داری صبر بر آنچه دوست داری.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (391, 3, 56, 'تهیدستى و تنهایى', 'ثروتمندی در غربت، چون در وطن بودن است و فقر در وطن، غربت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (392, 3, 57, 'ارزش قناعت و خود کفایى', 'قناعت، ثروتی است که پایان نمی‌پذیرد. \r\nسید رضی می‌گوید: \r\nاین کلام از پیغمبر نیز نقل شده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (393, 3, 58, 'توانگرى و شهوت‌ها', 'ثروت اصل و مایه شهوت‌هاست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (394, 3, 59, 'ارزش تذکر دادن اشتباهات', 'آن که تو را از گزندى ترساند چون کسى است که تو را مژده رساند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (395, 3, 60, 'ضرورت کنترل زبان', 'زبان درنده ای است چون رهایش کنی می‌گزد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (396, 3, 61, 'شیرینی آزار زن', 'زن کژدمی است گزیدنش شیرین است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (397, 3, 62, 'روش پاسخ دادن به ستایش‌ها و نیکی‌ها', 'چون تو را سلام کردند به نیکوتر از آن پاسخ گویید چون به تو احسانی کنند فزون‌تر از آن پاداش ده با این وجود برتری از آن آغاز‌کننده نیکی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (398, 3, 63, 'ارزش شفاعت', 'شفاعت کننده بال و پر طلب کننده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (399, 3, 64, 'غفلت دنیا پرستان', 'مردم دنیا همچون مسافران (در کشتی) ‌اند آنان را می‌برند و آنان در خوابند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (400, 3, 65, 'ترک دوستان و تنهایى', 'از دست دادن دوستان غربت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (401, 3, 66, 'روش خواستن', 'از دست رفتن حآجت بهتر از آن است که از نااهل طلب کنى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (402, 3, 67, 'ارزش ایثار اقتصادى', 'از بخشش اندک شرمنده مباش زیرا محروم ساختن از آنهم کم‌تر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (403, 3, 68, 'ره آورد عفت و شکرگذارى', 'پاکدامنی زینت فقر است و سپاسگزاری زینت بی‌نیازی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (404, 3, 69, 'حفظ روح امیدوارى', 'اگر به آنچه می‌خواستى نرسیدی از آنچه هستی نگران مباش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (405, 3, 70, 'روانشناسى جاهل', 'جاهل را نمی‌بینی جز در افراط یا تفریط.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (406, 3, 71, 'نشانه کمال عقل', 'چون عقل به کمال رسد سخن اندک گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (407, 3, 72, 'رابطه دنیا و انسان', 'روزگار بدن‌ها را فرسوده سازد و آرزوها را تازه نماید، و مرگ را نزدیک آرد و خواسته‌ها را دور و دراز می‌سازد کسی که به آن برسد خسته می‌شود کسی که به آن نرسد رنج می‌برد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (408, 3, 73, 'ضرورت خودسازى رهبران و مدیران', 'کسی که خود را رهبر مردم قرارداد پیش از تعلیم دیگرى باید به ادب کردن خویش پردازد و باید که ادب کردن دیگران به کردار باشد پیش از آن که به گفتار تربیت کند و آن که خود را تعلیم دهد و ادب اندوزد شایسته تر به تعظیم است از آن که دیگرى را تعلیم دهد و ادب بیاموزد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (409, 3, 74, 'ضرورت یاد مرگ', 'نفس زدن انسان گام‌های او بسوی مرگ است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (410, 3, 75, 'توجه به فنا پذیرى دنیا', 'هر چه شمردنى است به پایان رسد و هر چه چشم به راه آن هستى، فرا می‌رسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (411, 3, 76, 'تحلیل رویدادها', 'چون امور مشابه هم بودند آخری را به اولی می‌توان مقایسه و ارزیابی کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (412, 3, 77, 'امام و ترک دنیاى حرام', 'در خبر آمده است که چون ضرار بن ضمرة الضبابى بر معاویه داخل شد معاویه از او در باره امیر المؤمنین (ع) پرسید گفت: گواهم که او را در حالى دیدم که شب پرده‌هاى خود را افکنده بود و او در محراب خویش بر پا ایستاده محاسن را به دست گرفته چون مار گزیده به خود مى‌پیچید همچون انسان محزون و غمگین گریه می‌کرد و می‌گفت: \r\nاى دنیا! اى دنیا! از من دور شو آیا برای من خودنمایی می‌کنی یا شیفته من شده‌ای؟ مباد که تو در دل من جاى گیرى هرگز! دیگری را بفریب که مرا در تو هیچ نیازی نیست من تو را سه بار طلاق داده ام و بازگشتی در آن نیست دوران زندگانى ات کوتاه است و اهمیتت اندک، و آرزویت حقیر است. آه از توشه اندک و درازى راه و دورى سفر و سختى مقصد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (413, 3, 78, 'جبر و اختیار', 'و از سخنان آن حضرت است، چون مردی از اهل شام از او پرسید آیا رفتن ما به شام، قضا و قدر خداوند است؟ امام با کلمات طولانی پاسخ او را داد که گزیده‌اش چنین است\r\nواى بر تو! شاید قضاء لازم و قدر حتم را گمان کرده‌اى اگر چنین بود پاداش و کیفر باطل بود بشارت و تهدید هم ساقط می‌شد خداوند بندگان خود را از آنجا که اختیار داشتند فرمان داده است ونهى کرده تا بترسند احکام آسانی را واجب کرد و چیز دشواری را تکلیف نفرمود در برابرعمل اندک، پاداش‌هاى بسیار داده است و از روی ناتوانی مغلوب معصیت کاران نشده است و خداوند به اکراه اطاعت کرده نمی‌شود پیامبران را به بازی نفرستاد و کتاب را براى بندگان بیهوده فرو نفرستاده است و نه آفرینش آسمان‌ها و زمین و آنچه میان این دو است به باطل نبوده است این گمان کسانى است که کافر شدند واى بر آنان که کافر شدند از آتش دوزخ.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (414, 3, 79, 'ارزش حکمت و بی‌لیاقتى منافق', 'حکمت را هر جا باشد فرا گیرید که حکمت گاه در سینه منافق باشد پس بی‌تابی کند تا بیرون آید و با همسان‌های خود آرام گیرد در سینه مومن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (415, 3, 80, 'مؤمن و ارزش حکمت', 'حکمت گمشده مومن است حکمت را فرا گیر هر چند از منافقان باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (416, 3, 81, 'میزان ارزش انسان‌ها', 'ارزش هر انسان به اندازه‌ی آنی است که خوب می‌داند آن ارزى که مى‌ورزى\r\nسید رضی می‌گوید: و این کلمه‌اى است که قیمتی بر آن نتوان تعیین کرد و حکمتى همسنگ آن نمى‌توان یافت و هیچ کلمه‌اى را همتاى آن نتوان نهاد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (417, 3, 82, 'ارزش‌هاى والاى انسانى', 'شما را به پنج چیز سفارش مى‌کنم اگر برای رسیدن به آن‌ها سوار شتران شوید و راه سفر طولانی را در پیش گیرید (این سفر) شایستگی آن را دارد کسی از شما جز به پروردگار خود امید نبندد و جز از گناه خود نترسد و اگر چیزى که از او پرسند که نداند از گفتن «نمى دانم» شرمنده نباشد و از آموختن چیزى که نمی‌داند، ننگ نداشته باشد بر شما باد به شکیبایى زیرا شکیبایى، ایمان را چون سر نسبت به بدن است در بدنى که سر نباشد، خیرى نیست. همچنین است در ایمانى که با شکیبایى توام نباشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (418, 3, 83, 'روش برخورد با چاپلوسان', 'امام به مردى که در ستایش او افراط می‌کرد فرمود و او در نزد امام متهم (امام ارزیابی مثبتی نسبت به او نداشت و او را متهم به نفاق می‌دانست) بود من کمتر از آن هستم که می‌گوئى و برتر از آنم که در دل داری.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (419, 3, 84, 'مردم پس از جنگها', 'گریخته‌ی از دم شمشیر و جنگ، عمری بیشتر دارد و فرزندان بیشمارتر.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (420, 3, 85, 'پرهیز از ادعاهاى علمى', 'کسی که کلمه‌ی \"نمی‌دانم\" را رها کند به هلاکت و نابودی می‌رسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (421, 3, 86, 'برترى تجربه پیران از قدرتمندی جوانان', 'تدبیر پیر را دوست تر مى‌دارم از استقامت جوان (در می‌دان جنگ و در روایتى است: از حاضر و آماده بودن جوان براى کارزار	.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (422, 3, 87, 'ارزش استغفار', 'در شگفتم از کسى که از رحمت خداوند نومید می‌شود و حال آنکه، توان آمرزش خواستنش هست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (423, 3, 88, 'دو عامل ایمنى مسلمین', 'امام باقر علیه السلام از جدش امیر مؤمنان (ع) چنین نقل می‌کند که فرمود در روى زمین دو امان و وسیله نجات از عذاب الهى بود که یکى از آن‌ها برداشته شد آن یکی را بگیرید و بدان چنگ زنید اما امانى که برداشته شد رسول خدا (ص) بود و اما امانى که مانده است آمرزش خواستن است خداى تعالى می‌فرماید و خدا آنان را عذاب نمى‌کند حالى که تو در میان آنانى و خدا عذابشان نمى‌کند درحالیکه آمرزش مى‌خواهند. (سوره انفال، ۳۳): \r\nسید رضی می‌گوید این یکی از بهترین راه‌های بیرون آوردن لطایف معنی و ظرافت سخن از آیات قرآن است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (424, 3, 89, 'راه اصلاح دنیا و آخرت', 'کسى که میان خود و خدا را اصلاح کند خداوند میان او و مردم را اصلاح خواهد نمود و کسی که امر آخرتش را اصلاح کند خدا امر دنیایش را آباد خواهد کرد و کسی که از درون جانش واعظى داشته باشد خداوندحافظى براى او قرار خواهد داد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (425, 3, 90, 'شناخت عالم آگاه', 'فقیه کامل کسى است که مردم را از آمرزش خدا مایوس نسازد و از مهربانى او نومیدشان نکند و از عذاب ناگهانى وى ایمنشان ندارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (426, 3, 91, 'راه درمان روان', 'این قلب‌ها (دل‌ها) ملول می‌شوند همان گونه که تن‌ها ملول می‌شوند پس براى شادابی آن‌ها سخنان نغز و حکمت آمیز بجویید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (427, 3, 92, 'والاترین دانش', 'بى ارزشترین علم، علمى است که بر سر زبان باشد و برترین علم آن است که بر اعضا و ارکان آدمى آشکار شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (428, 3, 93, 'فلسفه آزمایشها', 'کسى از شما نگوید خداوندا به تو پناه می‌برم از امتحان شدن زیراهیچکس نیست مگر آنکه، به نحوى به امتحان شدن گرفتار است ولى اگر کسى خواهد که به خدا پناه جوید از او بخواهد که از امتحانات گمراه‌کننده اش پناه دهد زیرا خداى سبحان فرماید «بدانید که اموال و اولاد شما مایه امتحان شما هستند» (سوره انفال، ۲۸): \r\nمعنى آن است که خداوند بندگانش را به اموال و اولاد می‌آزماید تا معلوم دارد که چه کسى به روزى او ناخشنود است و چه کسی خرسند است هر چند، خداوند به آن‌ها از خود آن‌ها آگاه‌تر است ولى براى آن است که کارهاى مستحق ثواب را از کارهایى که درخورعقاب است معلوم دارد زیرا برخى فرزند پسر را دوست دارندو دختران را ناپسند مى‌شمارند و بعضى افزایش مال را می‌پسندند و از کاهش آن ناخشنودند\r\nسید رضی می‌گوید و این از تفسیرهاى شگفت است که از او شنیده شده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (429, 3, 94, 'شناخت نیکى‌ها و خوبیها', 'هنگامى که از او پرسیدند که خیر چیست؟ چنین فرمود خیر آن نیست که مالت فراوان یا فرزندانت بسیار باشند بلکه خیر آن است که دانشت فراوان گرددو بردباریت افزون شود و به پرستش پروردگار، بر مردمان سرافرازى کنى پس اگر کارى نیک کردى خدا را سپاس گویى و اگر گناه ورزیدى از او آمرزش جویى در این جهان خیرى نیست، مگر براى دو کس کسى که گناهى می‌کند و به توبه جبرانش می‌نماید و کسى که براى کارهاى خیر می‌شتابد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (430, 3, 95, 'تقوى و ارزش أعمال', 'عملى که با تقوا توام باشد، اندک نیست پس چگونه عملى که به درگاه خداوند مقبول است، اندک باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (431, 3, 96, 'ارزش علم و بندگى', 'نزدیک‌ترین مردم به پیامبران داناترین آنان است بدانچه آورده‌اند سپس برخواند همانا نزدیک‌ترین مردم به ابراهیم آنانند که پیرو او گردیدند و این پیامبر و کسانى که ایمان آوردند (سوره آل عمران، ۶۸): سپس فرمود: دوست محمد (ص) کسى است که خدا را اطاعت کند هر چند به نسب از او دور باشد و دشمن محمد (ص) کسى است که خدا را نافرمانى کند هر چند خویشاوند نزدیک محمد (ص) باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (432, 3, 97, 'ارزش یقین', 'وقتى شنید که یکى از اهالی حروریه (از خوارج) نماز شب می‌گزارد و قرآن می‌خواند چنین فرمود: خوابى که با یقین همراه باشد بهتر است از نماز گزاردن با شک.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (433, 3, 98, 'ضرورت عمل کردن به روایات', 'هرگاه خبرى شنیدید آن را نیک بفهمید و در آن بیندیشید نه اینکه بشنوید و نقل کنید زیرا نقل‌کنندگان علم بسیارند ولى رعایت کنندگان آن اندک اند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (434, 3, 99, 'تفسیر اِنا لِله و اِنا الیه راجعون', 'امام علیه السلام شنید مردى را که می‌گوید ما از آن خداییم و به سوی او باز می‌گردیم (انا لله و انا الیه راجعون) فرمود: گفته ما (ما از آن خداییم) اقرار ماست به بندگى و گفته ما (که به سوى او باز می‌گردیم) اقرار ما است به نابودی خویش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (435, 3, 100, 'روش مناجات کردن', 'امام علیه السلام در برابر گروهى که او را ستودند فرمود بار خدایا تو مرا از خودم بهتر مى‌شناسى و من خود را از آنان بیشتر مى‌شناسم خدایا ما را بهتر از آن کن که مى‌پندارند و بیامرز از ما آنچه را که نمى‌دانند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (436, 3, 101, 'روش بر طرف کردن نیازهاى مردم', 'برآوردن نیازهای مردم جز به یکی از سه طریق راست نمی‌گردد کوچک شمردن آن، تا خود بزرگ نماید و پنهان داشتن آن تا خود آشکار شود و شتاب در برآوردن آن، تا گوارا شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (437, 3, 102, 'آینده و مسخ ارزش‌ها', 'روزگاری بر مردم خواهد آمد که جز سخن‌چین را ارج ننهند و جز بدکار را خوش طبع نخوانند و جز با انصاف را ناتوان ندانند در آن زمان کمک به نیازمندان، خسارت و پیوند با خویشاوندان، منت گذاری و عبادت وسیله برتری جویی بر مردم است در چنین هنگام کار حکمرانى با مشورت زنان بُوَد و فرماندهی از آن خردسالان خواهد بود و کارها به دست خواجگان اداره می‌گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (438, 3, 103, 'روش برخورد با دنیا', 'امام علیه السلام را دیدند با جامه‌ای کهنه و پینه زده در این باب از او پرسیدند، فرمود\r\nاین جامه دل راخاشع و نفس را خوار می‌سازد و مؤمنان بدان اقتدا کنند دنیا و آخرت دو دشمن ناهمگون‌اند و دو راهند مخالف هم آن که دنیا را دوست داشت و مهر آن را در دل کاشت آخرت را دشمن می‌دارد و با آن دشمنی خواهد کرد دنیا و آخرت به مثابه مشرق و مغرب‌اند وکسى که میان آن دو سیر می‌کند وکسى که میان آن دو سیر می‌کند هرگاه به یکى نزدیک شود از دیگرى دور می‌گردد و یا چون دو زن هستند در نکاح یک شوى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (439, 3, 104, 'وصف زاهدان و ارزش سحرخیزی', 'و از نوف بکالى روایت است که شبى امیرالمومنین (ع) را دیدم از بستر خود برون آمده نگاهى به ستارگان انداخت و فرمود: اى نوف، خواب هستى یا بیدار؟ گفتم: بیدارم اى نوف خوشا بحال زاهدان در دنیاو مشتاقان در آخرت آنان قومی هستند که زمین را فرش خود قرار دادند و خاک آنرا بستر و آب آن را (نوشیدنی) گوارای خود می‌پندارند قرآن را به جانشان بسته دارند و دعا را ورد زبان پس آنگاه دنیا را بر روش مسیح دور ریختند اى نوف، داود (ع) در چنین ساعتى از شب، برخاست و گفت و گفت این ساعتى است که بنده‌اى در آن دعا نکند جز که از او پذیرفته شود مگر باجگیر یا کسى که کارهاى مردم را به حکومت گزارش می‌کند یا پلیس و یا صاحب عرطبه «طنبور» باشد ویا صاحب کوبه «طبل\r\nسید رضی می‌گویدبعضى گویند عرطبه طبل است و کوبه طنبور.».', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (440, 3, 105, 'احترام گذاشتن به مرزها و حدود احکام الهی', 'همانا خداوند فرائضى بر شما واجب کرده است آن‌ها را تباه نکنید و برایتان حدودى معین کرده از آن حدود تجاوز مکنید شما را از چیزهایى نهى کرده است حرمت آن را مشکنید و نسبت به چیزهایی سکوت کرده است و آن‌ها را از روى فراموشى رها نکرده است پس خود را درباره آن‌ها به رنج میفکنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (441, 3, 106, 'ره آورد شوم دین گریزى', 'مردم، هیچیک از امور دینى را ترک نکنند براى بهبود امور دنیوى مگر اینکه خداوند زیانبارتر از آن را بروى آن‌ها خواهد گشود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (442, 3, 107, 'علل سقوط عالمان بی‌عمل', 'بسا دانشمند که نادانى وى او را از پا در آورد در حالیکه دانش او همراهش باشد اما سودی به حال او نداشته باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (443, 3, 108, 'شگفتى‌هاى روح آدمى', 'به رگهاى دل این آدمى پاره گوشتی آویزان است و آن شگفت‌ترین چیزی است که در او است و آن قلب است که در آن مایه‌هایى از حکمت است و چیزهایی متفاوت با آن، در آن وجود دارد پس اگر در دل امیدى پدید آید طمع آن را خوار گرداند و اگر طمع بر آن هجوم آرد، حرص آن را تباه سازد و اگر نومیدى بر آن چیره شود تاسف خوردن آن را بکشد و اگر خشم بر او مسلط شود کینه توزیش شدت گیرد اگر به خشنودی دست یابد خویشتن داری را از یاد ببرد و اگر ترس به ناگاه او را فرا گیرد بیم از جان او را مشغول می‌داردو اگر گشایشى در کارش پدید آید درغفلت و بی‌خبرى فرو می‌رود و اگر مالى به دست آرد توانگرى وى را به سرکشى وادارد و اگر مصیبتى به او رسد بی‌تابى رسوایش گرداند و اگر به فقر گرفتار شود به بلا دچار شود و اگر گرسنگى با او در آویزد ناتوانى از پایش در اندازد اگر در سیرى افراط کند پرى شکم آزارش دهد پس هر گونه کُندروی برای او زیانبار استو هر گونه تندروی برای او فسادآفرین است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (444, 3, 109, 'ارزش والاى اهل بیت پیامبر (ص)', 'ما تکیه گاه میانه‌ایم عقب افتاده‌گان به ما می‌رسند و پیش‌تاختگان به ما باز می‌گردند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (445, 3, 110, 'شرائط تحقق اوامر الهى', 'فرمان خدای سبحان را برپا ندارد جز آن که (در اجرای حق) سازش نمی‌کند و در برابر دیگران زبونی نمی‌کند و از پى مطامع خود نمی‌رود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (446, 3, 111, 'عشق به امام على (ع) و طاقت نداشتن تحمل آن', 'سهل پسر حنیف انصارى پس از بازگشت از صفین در کوفه مرد و امام او را از هر کس بیشتر دوست مى‌داشت فرمود اگر کوهى مرا دوست بدارد درهم فرو می‌ریزد. 	\r\nسید رضی می‌گوید معنى این سخن این است که رنج و محنت بر او سخت می‌گیرد و مصیبت‌ها به سوى او بشتابند و این جفا نرود مگر با پرهیزگاران و نیکان و برگزیدگان و این همانند حکمت ۱۱۲ است که از امام (ع) نقل شده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (447, 3, 112, 'مشکلات شیعه بودن', 'هر کس ما اهل بیت را دوست بدارد باید خود را آماده پوشیدن بالاپوش فقر کن. \r\nسید رضی می‌گوید: \r\nو گاه این سخن را به معنى دیگرى تاویل می‌کنند که اینجا جاى ذکر آن نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (448, 3, 113, 'ارزش‌هاى والاى اخلاقى', 'هیچ ثروتى سودمندتر از عقل نیست و هیچ تنهایى وحشت انگیزتر از خودخواهی نیست و هیچ عقلی همچون عاقبت اندیشی نیست و هیچ بزرگواری چون تقوی نیست هیچ هم نشینى چون خوشخویى نیست و هیچ میراثى چون ادب نیست و هیچ رهبرى چون توفیق نیست و هیچ تجارتى چون عمل صالح نیست و هیچ سودی همانند پاداش نیک نیست و هیچ پارسایى همانند پرهیز از شبهات نیست هیچ زهدى همانند دوری از حرام نیست هیچ علمی چون تفکر نیست هیچ عبادتی همچون انجام واجبات نیست و هیچ ایمانی چون حیا وصبر نیست و هیچ اصالتی چون تواضع نیست و هیچ شرفی همانند دانش نیست و هیچ عزتی چون بردباری نیست و هیچ پشتیبانی مطمئن‌تر از مشورت نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (449, 3, 114, 'خوشبینى و بد بینى', 'چون صلاح و شایستگی بر زمانه و مردمش مستولى شود و کسى به دیگرى گمان بد برد که از او گناهى ظاهر نشده است به اوستم کرده است وچون بدکارى و فساد بر زمانه و مردمش مستولى شود و کسى به دیگرى گمان نیک برد فریب خورده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (450, 3, 115, 'توجه به پایان پذیرى دنیا', 'کسى به آن حضرت عرض کرد چگونه‌ای یا امیر مومنان چگونه خواهد بود حال آن که در بقایش ناپایدار است و در تندرستى‌اش بیمار می‌شود و از آنجا که در امان است مرگ به سراغ او می‌آید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (451, 3, 116, 'انسان و انواع آزمایش‌ها', 'بسا کسی که با احسان و نعمت خداوندى کم کم به عذاب او نزدیک می‌شود و با پرده پوشی بر گناهش فریب می‌خورد و از اینکه، مردم او را به نیکى یاد می‌کنند، فریفته گردد و خدا هیچکس را به چیزى نیازمود همچون مهلتی که بدو عطا فرمود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (452, 3, 117, 'پرهیز از افراط و تفریط در دوستی امام', 'به سبب من دو کس به هلاکت رسیدند یکى، آنکه در دوستى من غلو می‌کند و دیگر کسی که در دشمنی با من حد انصاف نگه نمی‌دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (453, 3, 118, 'استفاده از فرصت‌ها', 'از دست دادن فرصت، غم انگیز است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (454, 3, 119, 'ضرورت شناخت دنیا', 'دنیا همچون مار است چون بر آن دست کشند، نرم آید ولى در درون آن زهر کشنده است فریب خورده ی نادان بدان میل می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (455, 3, 120, 'روانشناسى قبائل قریش', 'از امام (ع) درباره قریش پرسیدند فرمود اما خاندان مخزوم، گل خوشبوى قریش‌اند دوست داریم که با مردانشان هم‌سخن شویم و با زنانشان ازدواج کنیم اما خاندان عبدشمس، آنان مردان دوراندیشند و در حمایت آنچه پشت سر خود دارند [یعنى مال و خاندان] استوارترندو اما ما (بنی‌هاشم) در آنچه در دستمان داریم بخشنده‌تریم و به وقت بذل جان دادن، سخاوتمندتریم فرزندان عبد شمس به شمار بیش‌اند ومکارتر و بدکردارترند ولى ما (بنی‌هاشم) فصیحتریم و مردم را نیکخواه‌تریم و خوشروتریم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (456, 3, 121, 'ارزیابی عمل‌ها', 'میان دو عمل چقدر فاصله دور است عملى که لذتش می‌رود و عواقب بد آن می‌ماند و عملى که زحمتش می‌رود و اجر و پاداش آن باقى می‌ماند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (457, 3, 122, 'عبرت از مرگ یاران', 'امام در پى جنازه‌ای مى‌رفت شنید مردى مى‌خندد فرمود: گویا مرگ را در دنیا بر غیر ما نوشته‌اند و گوئى حق بر غیر ما واجب گشته است و گویى این مردگان مسافرانی هستند که به زودى نزد ما باز مى‌گردند در حالیکه آنان را در گورهاشان جاى مى‌دهیم و میراثشان را مى‌خوریم گویی که ما پس از آنان جاودان خواهیم بود آنگاه هر اندرز دهنده‌ای را چه مرد و چه زن فراموش می‌کنیم و خود را نشانه تیرهای بلا و آفات قرار می‌دهیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (458, 3, 123, 'الگوهاى کامل انسانیت', 'خوش بحال کسی که خود را خوار ببیند و کسب او حلال و پاک باشد و باطنش نیکو است و اخلاقش نیکو است افزونی مال خود را انفاق کند و سرکشی زبانش را مهار نمایدو شر خود را از مردم باز می‌دارد سنت پیامبر برای او کافی است و به بدعت نسبتش ندهند\r\nسید رضی می‌گوید بعضى از این سخن و سخن پیش از آن را به پیامبر (ص) نسبت داده اند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (459, 3, 124, 'روانشناسى زن و مرد', 'غیرت زن (در برابر شوهرش) کفر است و غیرت مرد (در برابر زنش) از ایمان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (460, 3, 125, 'امام و شناساندن اسلام', 'اسلام را چنان وصف کنم که کس پیش از من نکرده است اسلام تسلیم است و تسلیم همان یقین است و یقین، تصدیق است و تصدیق همان اقرار است و اقرار، ادای وظیفه است و ادای وظیفه همان عمل کردن است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (461, 3, 126, 'شگفتى ضد ارزش‌ها', 'از بخیل در شگفتم به سوی فقری می‌شتابد که از آن گریزان است و سرمایه ای از دستش مى‌رود که در طلب آن بود چون فقیران زیست می‌کند و در آخرت چون توانگران از او حساب می‌کشند و از متکبر در شگفتم که دیروز نطفه بود و فردا مردار گندیده خواهد بود و در شگفتم از کسى که در خدا شک می‌کند و آفریدگان خدا را می‌بیند و در شگفتم از کسى که مرگ را فراموش کرده است و او مردگان را می‌بیند و از کسى در شگفتم که زنده شدن آن جهان را نمى‌پذیرد در حالیکه زنده شدن بار نخستین را مى‌بیند و در شگفتم از آن که به آبادانى دنیای فانی مى‌پردازدو خانه جاودانه (آخرت) را رها می‌سازد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (462, 3, 127, 'نکوهش از سستى در عمل', 'کسى که در عمل کوتاهى کند به اندوه دچار گردد و خداوند را، به کسى که خدا را در مال و جانش نصیبى نباشد، نیازى نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (463, 3, 128, 'تأثیر عوامل زیست محیطى در سلامت', 'در آغاز سرما از آن پرهیز کنید و در آخر به آن رو کنید زیرا سرما با بدن‌ها همان کند که با درختان کند آغازش مى‌سوزاند و پایانش برگ مى‌رویاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (464, 3, 129, 'شناخت عظمت پروردگار', 'بزرگی خالق هستی نزد تو (چشمت مخلوق را در چشمت خوار و کوچک می‌کند.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (465, 3, 130, 'توجه به فنا پذیرى دنیا', 'امام چون از صفین باز مى‌گشت به گورستان برون کوفه نگریست و فرمود اى ساکنان خانه‌هاى وحشت‌آور و سرزمین‌های بی‌آب و گیاه و قبرهای تاریک ای ساکنان خاک، ای ساکنان غربت ای بی‌کسان، ای تنها خفتگان شما در رفتن بر ما پیشى گرفته اید و ما از پى شما می‌آییم اما خانه‌ها (تان)، غیر شما در آن ساکن شدند و اما همسران، دیگران با آنان ازدواج کردند و اما اموال، همه آن‌ها تقسیم شد این خبری است که نزد ماست خبرى که نزد شماست چیست؟ سپس به یاران خود نگریست و فرمود\r\nاگر آنان را رخصت مى‌دادند که سخن گویند شما را خبر مى‌دادند که بهترین توشه‌ها تقوی است. (سوره بقره، آیه ۱۹۷).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (466, 3, 131, 'توبیخ نکوهش کننده دنیا', 'و شنید مردى دنیا را نکوهش مى‌کند فرمود: \r\nای که نکوهش دنیا می‌کنی و حال آنکه فریفته نیرنگ‌هاى او هستى و به باطل‌های آن دلباخته‌ای فریفته دنیایى و سرزنشش مى‌نمایى؟ تو در باره او مرتکب جرم شدی، یا دنیاست که در باره تو مجرم است؟ کی دنیا تو را سرگردان کرد؟ و چه زمانی تو را فریب داد؟ آیا جایگاهی که پدرانت به خواب مرگ رفته‌اند و پوسیده‌اند تو را فریفت یا به خوابگاه مادرانت در زیر خاک؟ چقدر با دو دست خود تیمارشان کردی؟ و چقدر آن دردمندان را پرستارى کردى برایشان شفا طلبیدى و از اطباء وصف (درد و درمان) آن‌ها را می‌خواستی در آن بامدادان، که دارویت آنان را بهبودى نداد و گریه‌ات برای آنان سودی نبخشید احدی از آنان را دلسوزی تو فایده ای نبخشید و آنچه خواهانش بودى به تو نرسید با نیروی خود نتوانستی مرگ را از آنان دور کنی دنیا، براى تو از او نمونه‌ای ساخت که مرگ او، مرگ تو را ماند دنیا خانه‌ی راستی است برای کسی که اهل راستی باشد و خانه عافیت است برای کسی که آن را دریابد و خانه ثروت است برای کسی که از آن توشه و زاد برگیرد و خانه پند و موعظه است برای کسی که پند گیرد سجده‌گاه دوستان و عاشقان خداست (دنیا) مصلای فرشتگان خداست محل نزول وحى خداست و محل تجارت دوستان خداست در آن آمرزش خدا را به دست آوردند و در آن دنیا بهشت را سود بردند چه کسی آن را نکوهش می‌کند در حالی که دنیا جداییش را اعلام کرده است و فراقش را فریاد زده است و اعلان مرگ خود وساکنانش را کرده است با بلای خود از بلا آخرت براى آنان نمونه ساخت و با شادمانى‌اش آنان را به شوق شادمانى دیگر انداخت شب با تندرستى گذشت و بامداد با مصیبتى جانگداز برگشت تا تشویق کند و بترساند، و بیم دهد و بپرهیزاند در صبحگاه ندامت و پشیمانی، کسانی دنیا را نکوهش می‌کنند و دیگران در روز قیامت آن را ستایش می‌کنند زیرا دنیا به یادشان آورد وآنان نیز به یاد آورند با آنان سخن گفت، سخنش را راست شمردندو (دنیا) پندشان داد و آن‌ها پذیرا شدند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (467, 3, 132, 'ضرورت یاد مرگ', 'خدا را فرشته‌اى است که هر روز فریاد برمی‌آورد بزایید براى مردن و گرد آورید برای نابود شدن و بسازید براى ویران شدن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (468, 3, 133, 'اقسام مردم و دنیا', 'دنیا محل گذر است نه محل استقرار و ماندن و مردم در آن دو گونه‌اند یکى آن که خود را فروخت و خویش را به تباهى انداخت و دیگرى که خود را خرید و آزاد ساخت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (469, 3, 134, 'حقوق دوستان', 'دوست، دوست نیست مگردوست خود را در سه جایگاه نگهبان باشد در رنج و بلا، آن هنگام که حضور ندارد و هنگامی که از دنیا برود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (470, 3, 135, 'چهار ارزش برتر', 'کسى را که چهار چیز دادند از چهار چیزش باز نمی‌دارند: آن که دعایش دهند از اجابت محرومش نمی‌کنند آن را که توبه دهند از قبول محرومش نمی‌کنند آن که استغفارش دهند از مغفرت محرومش نمی‌کنند آن که اجازه شکرش دهند از افزونی محرومش نمی‌کنند. \r\nسید رضی می‌گوید گواه این در کتاب خداى عز و جل است، در باب دعا گوید «مرا بخوانید تا شما را اجابت کنم» سوره بقره - آیه ۶۰:: و در باب استغفار گوید «هر که کار ناپسندى کند یا به خود ستم روا دارد آنگاه از خدا آمرزش خواهد، خدا را آمرزنده و مهربان خواهد یافت» سوره نساء - آیه ۱۱۰،: و در باب شکر گوید «اگر مراسپاس گویید بر نعمت شما می‌افزایم» سوره ابراهیم آیه ۷: و در باب توبه گوید «جز این نیست که توبه از آن کسانى است که به نادانى مرتکب کارى زشت می‌شوند و زود توبه می‌کنند خدا توبه اینان را می‌پذیرد و خدا دانا و حکیم است.» سوره نساء - آیه ۱۷.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (471, 3, 136, 'فلسفه احکام الهى', 'نماز موجب نزدیکی هر متقی است و حج، جهاد هر ضعیفى است و براى هر چیز زکاتى است و زکات بدن، روزه است و جهاد زن، شوهردارى شایسته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (472, 3, 137, 'صدقه راه نزول روزى', 'رزق و روزی را با صدقه فرو آورید!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (473, 3, 138, 'نقش یا دانش الهى در انفاق', 'آن که پاداش الهی را باور دارد در بخشش سخاوتمند است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (474, 3, 139, 'تناسب امداد الهى با نیازها', 'یارى خدا می‌رسد به اندازه نیاز (بار زندگی).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (475, 3, 140, 'قناعت و بی‌نیازى', 'فقیر نمی‌شود آنکه میانه روی گزیند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (476, 3, 141, 'راه آسایش', 'کم بودن نان خور یکى از دو آسایش است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (477, 3, 142, 'دوستى‌ها و خردمندى', 'دوستی کردن با مردم نیمی از خرد است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (478, 3, 143, 'غمها و پیرى زودرس', 'اندوه خوردن، نیمى از پیرشدن است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (479, 3, 144, 'تناسب بردبارى با مصیبت‌ها', 'مصیبت به اندازه صبر انسان فرود می‌آید و کسى که دستش را (به عنوان بی تابى و ناشکرى) به هنگام مصیبت بر زانو زند پاداشش از میان می‌رود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (480, 3, 145, 'عبادتهاى بی‌حاصل', 'بسا روزه داری که از روزه خود جز گرسنگى و تشنگى بهره نبرد و چه بسا، (به نماز) ایستاده‌ای که از نمازش جز بیدارى و رنج نصیبى نبرد خوشا خواب زیرکان، و افطارشان!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (481, 3, 146, 'ارزش دعا، صدقه و زکات دادن', 'ایمانتان را با صدقه نگهدارید و اموال خود را با زکات حفظ کنید و امواج بلاء را با دعا برگردانید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (482, 3, 147, 'سفارشات اخلاقی و اعتقادی به کمیل', 'و از سخنان آن حضرت است به کمیل پسر زیاد نخعى کمیل پسر زیاد گفت: امیرالمومنین على بن ابى طالب (ع) دست مرا گرفت و مرا به سوی قبرستان کوفه برد چون به صحرا رسید آهى دراز کشید و گفت ای کمیل! این دل‌ها ظروفند بهترین آن ظروف، جادارترین آن‌هاست پس آنچه می‌گویم را خوب حفاظت کن مردم سه دسته‌اند: دانشمند الهی و آموزنده‌ای که در راه راست گام برمى دارد و پشه‌هایی اسیر باد که بدنبال هر صدایی می‌روند با هر بادی به آن سو میل می‌کنند به نور دانش روشنی نیافته‌اند و به رکن استوارى پناه نجسته اند: \r\nاى کمیل، علم بهتر از مال است دانش تو را حراست می‌کند و تو باید از مال حراست کنی و مال با هزینه کردن نقصان می‌یابد و علم با انفاق کردن، افزایش می‌یابد آنچه به مال پرورده شود با نابودی مال از بین می‌رود ای کمیل پسر زیاد! دانش، روشی است که باید به آن گردن نهاد بوسیله آن انسان در دوران حیات اطاعت فرمان خدا می‌کند و پس از مرگش نام نیک از او بر جاى می‌ماند علم حکومت می‌کند و بر مال حکومت می‌کنند \r\nای کمیل: ثروت اندوزان مرده‌اند اگر چه زنده‌اند ولى دانشمندان تا دنیا برقرار است زنده‌اند (با این که) اجسادشان نابود شده‌اند تصویرشان در دل‌ها زنده است آری! حقا که در اینجا دانش فراوانی است (و با دست خود اشاره به سینه‌اش کرد اگر فراگیرانى براى آن مى‌یافتم آرى! یافتم آن را که نیکو در می‌یافت، لیکن امین نمى‌نمود زیرا دین را وسیله رسیدن به دنیا ساخته بود و با نعمت خدا بر بندگان خدا برترى مى‌جست و به حجت علم بر دوستان خدا بزرگى مى‌فروخت یا کسى که مطیع و تسلیم حاملان حق است ولى در شناخت رمز و راز علم بصیرتش نیست شک در قلبش از نخستین شبهه که بر او عرضه می‌شود، شعله ور می‌گردد پس نه آن‌ها و نه اینها، سزاوار آموختن دانش‌های فراوان من نمی باشند یا کسى که سخت در پى لذت است در برابر شهوت، عنان گسیخته است یا شیفته جمع کردن و مالى را بر مال نهادن است هیچیک از این دو گروه پاسدارى دین را نتواند کرد و بیشتر به چهارپایان چرنده شباهت دارند آرى این چنین علم با مرگ حاملانش می‌میرد آرى، زمین هیچگاه از حجت قائم خداوندى خالى نمی‌ماند یا ترسان و پنهان تا حجت‌ها و نشانه ‌هاى روشن دین خدا از میان نرود ولى آن‌ها چند نفرند و کجا هستند؟ به خدا سوگند آن‌ها تعدادشان کم و مقامشان نزد خدا بسیار است خداوند بواسطه آن‌ها حجت‌ها و دلائلش را حفظ می‌کند تا به افرادى که نظیر آن‌ها هستند بسپارند و بذر آن را در قلب افرادى شبیه خود بیفشانند آنان که دانش، نور حقیقت بینی را بر آنان تابانده است و روح یقین را دریافته اندوآسان (نرم) یافتند آنچه را که مترفین دشوار یافتند آن‌ها به آنچه جاهلان از آن وحشت دارند انس دارند و با تن‌ها، همنشین دنیایند و جان‌هاشان به جهان بالا پیوند خورده است آنان جانشینان خداوند در روى زمین هستند و به دین خداوند، دعوت کنندگانند آه، آه که چه آرزومند دیدار آنانم کمیل اگر خواهى باز گرد.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (483, 3, 148, 'نقش سخن در شناخت انسان', 'آدمى در زیر زبان خود پنهان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (484, 3, 149, 'ضرورت خودشناسى', 'آن کس که قدر خود را نشناخت تباه شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (485, 3, 150, 'ضد ارزش‌ها و هشدارها', 'در پاسخ مردى که از او خواسته بود اندرزش دهد، چنین فرمود از کسانى مباش که بدون عمل، امید سعادت آخرت را دارند و توبه را باآرزوهاى دراز تاخیر می‌اندازند در دنیا سخن زاهدانه می‌گوید و عمل دل دادگان به دنیا را انجام می‌دهد اگر از دنیا بهره اش دهند، سیر نگردد و اگر از دنیا بازش دارند، قناعت نکند در سپاس آنچه به او داده‌اند ناتوان است و از ۱ آنچه مانده است، افزون می‌طلبد دیگران را از کار بد نهى می‌کند، اما خود نهى نمی‌پذیرد و بدانچه خود نمی‌کند فرمان مى‌دهدبه شایستگان علاقه دارد ولی مانند آنان عمل نمی‌کند و با گنهکاران دشمنى می‌ورزد و خود یکى از آنان است از مرگ نفرت مى‌دارد، چون گناهانش بسیار است ولى در انجام دادن کارهایى که سبب بیزارى او از مرگ شده، پاى می‌فشرد چون بیمار شود از کرده خود پشیمان می‌شودو اگر تندرستی یابد احساس امنیت می‌کند و به لهو می‌پردازد چون عافیت یابد مغرور می‌شود و چون بیمار گردد نومید می‌شود اگر بلائى به او رسد به زارى خدا را خواند و اگر آسایشی به او رسد در حال غرور از خدا روی بگرداند در آنچه درباره آن به گمان است، هواى نفس خویش را به فرمان است و درباره آنچه یقین دارد در چیرگى بر نفس ناتوان است بر دیگران به کمتر از گناه خود می‌ترسد و براى خویش بیشتر از آنچه عمل کرده امید دارد اگر بی‌نیاز شود مغرور و سرمست می‌گردد و اگر فقیر شود نومید و سست می‌شود چون کاری کند، در آن کوتاهی می‌کند و چون چیزی خواهد، اصرار می‌کند چون در برابر شهوت قرار گیرد گناه را بر می‌گزیند و توبه را عقب می‌اندازد و اگر محنتى به او برسد از شرایط امت (مسلمان) بودن بیرون می‌رود از عبرت‌ها می‌گوید و خود عبرت نمی‌گیرد در پند دادن مبالغه می‌کند اما خود پند نمی‌پذیرد که او در سخن راهنماست و در عمل از همه کمتر است برای دنیای زودگذر تلاش و رقابت دارد اما برای آخرت جاویدان آسان می‌گذرد سود را زیان، و زیان را سود می‌پندارد از مرک می‌ترسد ولى فرصت‌ها را از دست می‌دهد گناه دیگران را بزرگ می‌شمارد در حالیکه بزرگ‌تر از آن را از خود ناچیز به حساب می‌آورد طاعت و عبادت خود را بزرگ می‌شمارد که مانندش را از دیگران ناچیز به حساب می‌آورد بر مردم طعن می‌زند و خویشتن را می‌ستاید لهو و لعب با ثروتمندان نزد او از ذکر خدا با فقیران محبوب‌‌تر است به زیان دیگران و سود خود داورى کند ولى به سود دیگران و زیان خود داورى نمی‌کند دیگران را ارشاد و خود را گمراه می‌نماید مردم او را اطاعت می‌کنند و او خداوند را عصیان می‌کند حق خود را کامل می‌ستاند اما حق دیگران را به کمال نمی‌دهد در غیر خدا از مردم می‌ترسد و از خدا در باره مردم نمی‌ترسد.. \r\nسید رضی می‌گوید: \r\nو اگر در این کتاب جز این گفتار نبود کافی بود براى اندرز بجا و حکمت رسا و بینایى بیننده و عبرت بینای اندیشه کننده.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (486, 3, 151, 'ضرورت آینده نگری', 'هر انسانی عاقبتی دارد شیرین و یا تلخ است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (487, 3, 152, 'توجه به فنا پذیرى', 'هرنعمت و خوشی روی‌کننده را برگشتنی است و آنچه برگشت، گویی که نبوده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (488, 3, 153, 'صبر و پیروزى', 'انسان شکیبا، پیروزی را از دست نمی‌دهد و اگر چه زمان طولانی بر او بگذرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (489, 3, 154, 'اهمیت نیت‌ها', 'خشنود ازاعمال مردمی، گویى خود در داخل کارهاى آنان بوده است و هر که در باطلى پا نهاد، دو گناه بر گردن وى افتاد گناه آن کار که مرتکب شده و یکى گناه خشنودى او به آن کار.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (490, 3, 155, 'ضرورت پایبندى به عهد و پیمان', 'عهد و پیمان را پاس دارید به خصوص با وفاداران.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (491, 3, 156, 'ضرورت خدا شناسى و اطاعت', 'خدای را اطاعت کنید که در نشناختن پروردگار عذری ندارید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (492, 3, 157, 'فراهم بودن راه‌هاى هدایت', 'حقیقت به شما نشان داده شده است اگر چشم بینا داشته باشید و هدایت کرده‌اند اگر هدایت جویید حق را به شما شنوانده‌اند اگر بشنوید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (493, 3, 158, 'روش برخورد با دوستان بد', 'برادرت را با نیکی به او سرزنش کن شر او را با بخشش به او بازگردان.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (494, 3, 159, 'پرهیز از مواضع اتهام', 'آنکه خود را در مواضع تهمت قرار می‌دهد نباید آن را که به او سوءظن پیدا می‌کند سرزنش نماید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (495, 3, 160, 'قدرت و زورگویى', 'هرکس قدرت به دست آورد مستبد می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (496, 3, 161, 'ارزش مشورت و پرهیز از خودمحوری', 'کسى که استبداد راى داشته باشد هلاک می‌شود و کسی که با مردم مشورت کند در عقل آن‌ها شریک می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (497, 3, 162, 'ضرورت راز دارى', 'هر کس راز خویش پنهان دارد اختیار آن همواره به دست او باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (498, 3, 163, 'فقر و نابودى', 'فقر، بزرگ‌ترین مرگ است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (499, 3, 164, 'روش بر خورد با متجاوزان', 'رعایت حق کسی که او حقش را ادا نمی‌کند نوعی بردگی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (500, 3, 165, 'پرهیز از نا فرمانى خدا', 'هیچ اطاعتی از مخلوق، در نافرمانی پروردگار جایز نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (501, 3, 166, 'پرهیز از تجاوز به حقوق دیگران', 'انسان را سرزنش نکنند که چرا حق خود را دیر درخواست نمود بلکه سرزنش در آنجاست که آنچه حقش نیست بگیرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (502, 3, 167, 'خود پسندى و محرومیت‌ها', 'خودپسندی مانع رشد و افزونی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (503, 3, 168, 'توجه به فنا پذیرى دنیا', 'مرگ نزدیک است و زمان ماندن در دنیا اندک است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (504, 3, 169, 'آینده نگرى', 'صبح برای آنکه دو چشم بینا دارد، طلوع کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (505, 3, 170, 'ضرورت ترک گناه', 'ترک گناه آسان‌تر از طلب توبه است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (506, 3, 171, 'ره آورد شوم حرام خوارى', 'بسا یک لقمه، که از خوردن لقمه‌ها مانع آید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (507, 3, 172, 'نادانى و دشمنى‌ها', 'مردم دشمن چیزى هستند که نمی‌دانند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (508, 3, 173, 'ارزش مشورت‌ها', 'آن که با افکار و آراء گوناگون روبرو شود مواقع خطا را می‌شناسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (509, 3, 174, 'ارزش خشم در راه خدا', 'هر که سر نیزه خشم خود را بخاطر خدا تیز کند در نابود کردن سخت ترین باطل‌ها توانا بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (510, 3, 175, 'راه درمان ترس', 'هنگامیکه از چیزى زیاد می‌ترسیدی خود را در آن بیفکن زیرا گاهی ترسیدن از چیزی، از خود آن سخت‌تر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (511, 3, 176, 'ابزار ریاست', 'اسباب بزرگی، بردباری و تحمل سختی‌ها است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (512, 3, 177, 'روش برخورد با بدان', 'بدکار را ادب کن با پاداش دادن به نیکوکار.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (513, 3, 178, 'روش نابود کردن بدى‌ها', 'بدی را از سینه دیگران ریشه‌کن کن با بیرون کردن آن از سینه خود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (514, 3, 179, 'لجاجت و سستى اراده', 'ستیزه‌جویی، تدبیر را باطل می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (515, 3, 180, 'طمع ورزى و بردگى', 'طمع ورزی، بردگی جاوید است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (516, 3, 181, 'ارزش دوراندیشی و پرهیز از کوته فکری', 'میوه‌ کوتاهی در کار، پشیمانی است میوه‌ی حزم و احتیاط، سلامت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (517, 3, 182, 'شناخت جایگاه سخن و سکوت', 'سکوت از گفتار حکمت آمیز سودی ندارد همانگونه که، سخن گفتن از روى نادانى را فایده‌ای نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (518, 3, 183, 'باطل گرایى و اختلاف', 'دو دعوت با هم متفاوت (و یا متضاد) نمی‌شود جز آنکه یکی باطل باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (519, 3, 184, 'ویژگى‌هاى اعتقادى امام علیه السلام', 'در حق شک نکردم، از آن وقت که آن را به من نشان دادند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (520, 3, 185, 'ویژگى‌هاى اعتقادى امام علیه السلام', 'دروغ نگفتم و به من دروغ گفته نشده است گمراه نشدم و کسی به واسطه من گمراه نشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (521, 3, 186, 'آینده سخت ستمکاران', 'آغازگر ستم، فردا (در قیامت) دستش را به دندان می‌گزد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (522, 3, 187, 'ضرورت یاد قیامت', 'کوچ، نزدیک است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (523, 3, 188, 'ضرورت حق گرایى', 'آن که رو در روی حق ایستاد، نابود شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (524, 3, 189, 'ارزش صبر و خطر بی‌تابى', 'آن کس را که شکیبایی نجات ندهد بی‌تابى او را ازپاى درمى آورد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (525, 3, 190, 'معیار امامت', 'شگفتا آیا معیار خلافت، صحابی پیامبر بودن است؟ اما صحابی بودن و خویشاوندی ملاک نیست؟ \r\nسید رضی می‌گوید\r\n از آن حضرت شعرى در همین زمینه نقل شده است (خطاب به خلیفه اول اگر تو از طریق شورى مالک امور مردم شدى چگونه شورایى بود که صاحبان راى و مشورت در آن حاضر نبودند و اگر از طریق قرابت با پیامبر صلى الله علیه و آله و سلم در برابر مخالفانت استدلال کردى دیگران از تو به پیامبر صلى الله علیه و آله و سلم نزدیکترند!):!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (526, 3, 191, 'مشکلات دنیا', 'انسان در این دنیا همانند هدفى است که تیرهاى مرگ به سوى آن روان است و غارت شده‌ای است که بلاها به سویش می‌شتابند هر جرعه آبش، گلوگیر شود و هر لقمه اش در حلق بماند انسان به نعمتی نمی‌رسد مگر، نعمت دیگری را از او می‌گیرند روزی از عمر او به او روی نمی‌آورد مگر آنکه روز دیگری از عمرش کم می‌شود پس ما یاران مرگیم و جان‌های ما نشانه مرگ است پس از کجا می‌توانیم امید بقاء داشته باشیم در حالیکه این شب و روز بنایى را بالا نبردند مگر آنکه، بشتاب ویرانش کنند و هرجمع را پریشان سازند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (527, 3, 192, 'پرهیز از زراندوزى', 'اى فرزند آدم هر چه بیش از روزى هر روزه ات کسب می‌کنى خزانه دار دیگرى هستى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (528, 3, 193, 'راه به کار گرفتن قلب', 'دل‌ها را میلی است گاه به چیزی روی می‌کنند و گاه روی بر می‌تابند پس از ناحیه میل و اقبال آنان داخل شوید که دل اگر به چیزی مجبور شد کور می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (529, 3, 194, 'ضرورت پرهیز از خشم و انتقام', 'کى خشم خود را فرو نشانم؟ چون خشمناک شوم آیا هنگامی که از انتقام ناتوانم و به من گفته می‌شود: اگر صبر کنی بهتر استیا وقتی که قدرت برکیفر دارم و به من گفته می‌شود اگر می‌بخشیدی بهتر بود؟!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (530, 3, 195, 'پرهیز از بخل ورزى', 'امام علیه السلام از کنار مزبله عبور می‌کرد فرمود این همان است که بخیلان به آن بخل ورزیدند و در روایت دیگر آمده است که امام فرمود این همان چیزى است که دیروز شما بر سر تصاحب آن با هم رقابت داشتید!:!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (531, 3, 196, 'عبرت آموزى از إتلاف اموال', 'از مال تو آنچه از دستت رفته، از دستت نرفته است اگر سبب عبرت تو بوده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (532, 3, 197, 'روش درمان روح', 'این دل‌ها خسته می‌شوند همان گونه که تن‌ها خسته می‌شوند برای نشاط دل‌ها لطائف حکمت را بجویید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (533, 3, 198, 'ضرورت حکومت', 'و چون گفته خوارج را شنید که حکومت جز از آن خدا نیست، فرمود سخن حقی است که از آن باطل می‌جویند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (534, 3, 199, 'نکوهش اوباش', 'و در وصف جمع اوباش فرمود آنان مردمی هستند که چون گرد آیند، غلبه کنند و چون پراکنده گردند شناخته نشوند و گفته‌اند امام فرمود آنان کسانی هستند که چون اجتماع کنند، آسیب رسانند و چون متفرق شوند سود می‌رسانند گفتند: ما زیان اجتماعشان را فهمیدیم سود پراکنده شدنشان چیست؟ صاحبان مشاغل به شغلشان برمی‌گردند و مردم از تلاش آن‌ها سود می‌برند مانند بازگشت بنا به ساختن ساختمان و بافنده به کارگاه بافندگی و نانوا به نانوایی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (535, 3, 200, 'نکوهش انسان‌هاى شرور', 'جنایتکارى را نزد او آوردند و گروهى اوباش با وى بودند، فرمود مبارک مباد چهره‌هایی که جز در کنار زشتی دیده نمی‌شوند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (536, 3, 201, 'امدادهاى الهى و حفظ انسان', 'با هر انسانى دو فرشته است که نگهبان اویند پس چون نقدیر الهی (مرگ) فرا رسد میان او و آن (مرگ) را باز می‌گذارند و همانا اجل (فرصت زندگی انسان تا مرگ)، سپری نفوذ ناپذیر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (537, 3, 202, 'واقع نگرى در مسائل سیاسى', 'و طلحه و زبیر بدو گفتند با تو بیعت مى‌کنیم به شرط آنکه ما را در خلافت شریک کنى \r\nامام فرمود: نه، شما شریکان در قدرت و یاری دادن هستید و دو یاور من در ناتوانی و سختی‌ها و مشکلات می‌باشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (538, 3, 203, 'یاد مرگ و پرهیزکارى', 'ای مردم از خدایی پرهیز کنید که اگر بگویید می‌شنود و اگر نهان داشتید می‌داند و بر مرگی آماده شوید، که اگر از آن فرار کنید شما را در می‌یابد و اگر ایستادید شما را می‌گیرد و اگر او را فراموش کنید، از شما یاد می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (539, 3, 204, 'بى توجهى به سپاسگذارى مردم', 'ناسپاسی مردم تو را از کار نیک باز ندارد زیرا کسانی هستند که تو را سپاس می‌گویند ولی از نعمت تو بهره مند نشده‌اند و تو از سپاس سپاسگزار بیش‌تر بدست می‌آوری از آنچه ناسپاسان تباه کرده‌اند وخداوند نیکوکاران را دوست می‌دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (540, 3, 205, 'گنجایش نامحدود ظرف علم', 'هر ظرفى از آنچه در آن می‌نهند پر شود مگر ظرف دانش که هر چه در آن نهند فراخی می‌یابد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (541, 3, 206, 'ره آورد حلم و بردبارى', 'نخستین پاداشى که بردبار از بردباریش می‌گیرد این است، که مردم در برابر نادان حمایتش می‌کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (542, 3, 207, 'ارزش همانند شدن با انسان فروتن', 'اگر بردبار نیستى خود را به بردبارى وادار که اندک اتفاق می‌افتد که انسان خود را شبیه مردمی کند و از جمله آنان نشود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (543, 3, 208, 'مراحل خودسازى', 'آنکه خود را محاسبه کند سود برده است و آن که از (نفس) خود غفلت کند زیان دیده است آن که (از خدا) ترسید در امان ماند و آن که عبرت گرفت بینا گردید و آن که بینا شد فهمید و آن که فهمید دانا گردید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (544, 3, 209, 'خبر از ظهور حضرت مهدى علیه السلام', 'دنیا به ما روی می‌کند پس از سرکشی آن چونان ماده شتر بدخویى که به بچه خود مهربان شود سپس آن حضرت این آیه را تلاوت کرد: \r\n«می‌خواهیم بر مستضعفین زمین منت گذاریم و آن‌ها را پیشوایان زمین و وارثان آن قرار دهیم» (سوره قصص، آیه ۵).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (545, 3, 210, 'تقوی و متقی', 'از خدا بپرهیزید پرهیز کسی که دامن همت به کمر زده و خود را از علایق جدا نموده است و کوشش و جدیت کرده است و خود را به چالاکی آراسته است و هراسان در اطاعت خدا تلاش کرده است و به انتهای مسیر نگاه می‌کند به مقصدى که به سوى آن روان است و به آنجا که جایگاه بازگشت اوست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (546, 3, 211, 'ارزش‌های اخلاقی', 'سخاوت حافظ آبرو است و شکیبایی دهان‌بند بی‌خردان است و بخشیدن زکات پیروزی است و دورى و فراموشى کیفر پیمان‌شکنان است مشورت کردن، خود هدایت (یافتن) است هر که تنها به راى و تدبیر خویش بسنده کند، خود را به مخاطره افکنده است و صبر با حوادث می‌جنگد و بی‌تابی، زمان را در نابودی انسان یاری می‌دهد و شریف‌ترین بی‌نیازی ترک آرزوهاست و بسا عقل که اسیر فرمانروایی هوس است حفظ تجربه نشان پیروزی است و دوستى ورزیدن، خویشاوندی با مردم را فراهم می‌آورد بر مردم رنجیده و به ستوه آمده اطمینان مکن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (547, 3, 212, 'خودپسندى آفت عقل', 'خود پسندی آدمى یکى از حسودان خرد اوست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (548, 3, 213, 'تحمل مشکلات و خشنودى', 'بر آزار و درد باید تحمل نمود وگرنه هرگز خرسند نخواهى بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (549, 3, 214, 'نرمخویى و کامیابى', 'کسى که ساقه درخت وجودش نرم است شاخه‌هایش فراوان است. (کنایه از این است که افراد متواضع و با محبت دوستان فراوان دارند).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (550, 3, 215, 'اختلاف آفت اندیشه', 'جدال، تدبیر را ویران سازد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (551, 3, 216, 'قدرت و تجاوزکارى', 'کسی که به نوایی رسید تجاوزکار شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (552, 3, 217, 'دگرگونى روزگار و شناخت انسان‌ها', 'در دگرگونی اوضاع جوهر انسانها شناخته می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (553, 3, 218, 'حسادت آفت دوستى', 'حسد دوست از آفات دوستی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (554, 3, 219, 'آرزوها آفت اندیشه', 'بیشترین قربانگاهای اندیشه‌ها زیر برق‌های (شمشیر) طمع است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (555, 3, 220, 'عدالت در قضاوت', 'از عدالت نیست که با اعتماد بر گمان قضاوت کنی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (556, 3, 221, 'آینده دردناک ستمکاران', 'بدترین توشه براى آخرت ستم بر بندگان خداست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (557, 3, 222, 'بى توجهى به بدى بدکاران', 'از بهترین اعمال فرد بزرگوار فراموش کردن آن چیزی (کارهای نیک خود) است که می‌داند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (558, 3, 223, 'حیاء و عیب پوشى', 'کسی که جامه‌ی حیا بپوشد مردم عیب او را نمی‌بینند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (559, 3, 224, 'برخى از ارزش‌هاى اخلاقى', 'با بسیاری سکوت، وقار انسان بیشتر می‌شود و انصاف مایه فزونى دوستان می‌شود با بذل و بخشش قدر انسان بالا می‌رود و نعمت با فروتنی کامل می‌شود و با تحمل سختی‌ها، سروری انسان واجب می‌شود با روش عادلانه، دشمن درهم می‌شکند با بردباری در برابرنادان، یاران انسان علیه او افزون می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (560, 3, 225, 'حسادت و بیمارى', 'از غفلت حسودان نسبت به سلامتی جسمشان در‌شگفتم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (561, 3, 226, 'طمع ورزى و خوارى', 'طمعکار، در زنجیر ذلت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (562, 3, 227, 'ارکان ایمان', 'از امام علیه السلام درباره ایمان سؤال شد فرمود ایمان شناخت با قلب است واقرار به زبان است و عمل به اندام‌ها است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (563, 3, 228, 'ارزش‌ها و ضد ارزش‌ها', 'آنکه به خاطر دنیا اندوهناک باشد از قضاء الهی خشمناک است هر که از مصیبتى که به او نازل شده شکایت کند از پروردگارش شکوه کرده است و هر که نزد توانگرى رود و به سبب مالش برابر او فروتنى کند دو سوم ایمانش را از دست داده است وهر که قرآن خواند و بمیرد و به جهنم برود او کسی است که آیات خدا (قرآن و محتوی آن را) را استهزا می‌کرده است و آنکه دلش برای دوستی دنیا تپید به دلش ۳ چیز از دنیا می‌چسبد غمی که او را رها نمی‌کند و حرصى که او را وانگذارد و آرزویی که آن را درنیابد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (564, 3, 229, 'ارزش قناعت و خوش خلقى', 'آدمی را قناعت برای دولتمندی کافی است و نعمت با حسن خلق کافی است و از امام علیه السلام در تفسیر این آیه سؤال شد او را حیات طیب و پاکیزه خواهیم بخشید (سوره نحل آیه ۹۷ فرمود: (مراد از حیات طیبه) همان قناعت است).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (565, 3, 230, 'راه به دست آوردن روزى', 'با کسى که روزى روى بدو آورده شریک شوید که او سزاوارترین فرد برای ثروتمندی و شایسته‌ترین برای روزی کردن شانس است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (566, 3, 231, 'تعریف عدل و إحسان', 'امام علیه السلام در باره فرموده خدا گفت: \r\nخداوند به عدل و احسان فرمان می‌دهد (سوره نحل آیه ۹۰) عدل همان انصاف، و احسان، همان بخشش به سایر مردم است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (567, 3, 232, 'ره آورد إنفاق', 'کسى که با دست کوتاه ببخشد با دست بلند باو بخشیده می‌شود\r\nسید رضی می‌گوید\r\nمعنى سخن این است که: آنچه انسان از اموال خود در راه خیر و نیکى انفاق می‌کند هر چند کم باشد خداوند جزا و پاداش اورا بسیار می‌دهد و منظور از «دو دست» در اینجا دو نعمت است که امام علیه السلام بین نعمت از ناحیه انسان با نعمت پروردگار را با کوتاهى و بلندى فرق گذاشته است نعمت و بخشش از ناحیه بنده را کوتاه و آنکه از ناحیه خداوند را بلند قرارداده است زیرا نعمت خدا همیشگى و چند برابر نعمت مخلوق است زیرا که نعمت خداوند اصل و اساس تمام نعمت‌ها است بنا بر این تمام نعمت‌ها به نعمت‌هاى خدا باز می‌گردد و از آن سر چشمه می‌گیرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (568, 3, 233, 'پرهیز از آغازگرى در مبارزه', 'امام علیه السلام به فرزندش حسن چنین فرمود کسى را به مبارزه دعوت نکن اما اگر به مبارزه دعوت شدی آن را اجابت کن زیرا آنکه یه مبارزه دعوت می‌کند تجاوزکار است و تجاوزکار زمین می‌خورد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (569, 3, 234, 'تفاوت اخلاقى مردان و زنان', 'برخی از بهترین صفات زنان بدترین صفات مردان است غرور، ترس و بخل اگر زن مغرور باشد بیگانه را به حریم خود راه نمی‌دهد و اگر زن بخیل باشد مال خود را حفظ می‌کندو مال همسرش را و اگر زن، ترسو باشداز هر چیزى که به آبروى او صدمه بزند فاصله می‌گیرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (570, 3, 235, 'نشانه خردمندى', 'به امام علیه السلام گفته شد: «عاقل» را برایمان توصیف کن فرمود: خردمند آن بود که هر چیزى را سر جاى خود می‌گزارد گفتند: جاهل را براى ما وصف کن فرمود: وصف کردم. \r\nسید رضی می‌گوید: \r\nیعنى جاهل کسى است که هر چیز را به جاى خود نمی‌نهد پس گویى وصف نکردن او وصف کردن اوست چه اعمال او خلاف اعمال عاقل است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (571, 3, 236, 'زشتى دنیاى حرام', 'به خدا سوگند، که دنیاى شما در چشم من خوارتر است از پاره استخوان خوکى که در دست شخصى جذامى باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (572, 3, 237, 'اقسام عبادت', 'گروهی خدا را عبادت می‌کنند از طمع (به بهشت پس آن عبادت بازرگانان است گروهی خدا را عبادت می‌کنند از ترس (عذاب پس آن عبادت بردگان است گروهی خدا را عبادت می‌کنند سپاسگزارانه پس آن عبادت آزادگان است.)).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (573, 3, 238, 'مشکلات تشکیل خانواده', 'زن و زندگی، همه‌اش زحمت و دردسر است و زحمت بارترآنکه از آن گریزی نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (574, 3, 239, 'زشتى سستى و سخن چینى', 'هر که در کارها سستى پیشه کند، حقوق خویش تباه کرده است و آن که سخن‌چین را پیروى کند، دوست خود را از دست بدهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (575, 3, 240, 'غصب و ویرانى', 'سنگ غصبی در بنای خانه مایه ویران شدن آن است\r\nسید رضی می‌گوید: \r\nاین سخن از پیامبر (ص) هم نقل شده است و دراینکه سخن این دو بزرگوار شبیه هم میباشد هیچ جاى شگفتى نیست زیرا مانند آبى هستند که از یک چاه کشیده شده‌اندو در دو دلو ریخته شده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (576, 3, 241, 'آینده دردناک ستمکاران', 'روز ستمدیده علیه ستمکار، سخت‌تر است از روز ستمکار علیه ستم کشیده.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (577, 3, 242, 'ارزش ترس از خداوند', 'از خدا بترس هر چند ترسى اندک باشد و میان خود و خدا پرده‌ای بیاویز هر چند نازک باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (578, 3, 243, 'روش صحیح پاسخگویى', 'هر گاه پاسخ‌ها همانند و زیاد شد پاسخ صحیح پوشیده و پنهان گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (579, 3, 244, 'مسؤولیت نعمت‌هاى الهى', 'خداوند در هر نعمتى حقى دارد هر که آن را ادا کند، برنعمتش بیفزاید و هر که در اداى آن کوتاهى کندنعمت را در خطر زوال قرار می‌دهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (580, 3, 245, 'کاستى قدرت و کنترل شهوت', 'هر گاه توانایی انسان افزایش یابد خواسته انسان کم می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (581, 3, 246, 'هشدار از پشت کردن نعمت‌ها', 'از فرار نعمت‌ها بپرهیزید که هر گریزنده‌ای بر نمی‌گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (582, 3, 247, 'ره آورد سخاوت', 'کرم و جوانمردى از خویشاوندی پرعاطفه‌تر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (583, 3, 248, 'تقویت خوشبینى‌ها', 'هر که به تو گمان خیر دارد چنان کن که گمانش صادق آید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (584, 3, 249, 'بهترین عمل‌ها', 'برترین کردار (آدمی) آنی است که خود را به انجام دادن آن، وادارسازى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (585, 3, 250, 'خداشناسى در حوادث روزگار', 'خدای سبحان را شناختم از در هم شکستن اراده‌ها و گشوده شدن گره‌های دشوارو در هم شکسته شدن تصمیم‌ها.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (586, 3, 251, 'تلخى‌هاى و شیرینى‌هاى دنیا و آخرت', 'تلخى دنیا شیرینى آخرت است و شیرینى دنیا تلخى آخرت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (587, 3, 252, 'فلسفه احکام الهى', 'خداوند «ایمان» را به خاطر تطهیر از شرک واجب کرد و نماز را برای پاک بودن از کبر و زکات را موجب فزونی روزی و روزه را براى آزمودن اخلاص مردم و حج را سبب نزدیک شدن بندگان به دین و جهاد را برای عزت اسلام و امر به معروف را برای اصلاح امور مردم و نهی از منکر را برای راندن و دفع سفیهان و صله رحم را براى افزون شدن شمار خویشاوندان وقصاص را براى ممانعت از خونریزى و اجراى حدود را براى بزرگ نشان دادن زشتى اعمال حرام و ترک میخوارگی را برای حمایت از عقل و اجتناب از دزدى را براى رعایت عفت مردم و ترک زنا را برای سلامت نسل آدمی و ترک لواط را بخاطر افزایش نسل و گواهی دادن‌ها را برای بدست آوردن حقوق انکار شده و ترک دروغ را تا راستگویی حرمت یابد و صلح‌جویی را برای امنیت از خطرات و امانت (امامت) را تا نظام امت پایدار باشد و اطاعت بردن را براى بزرگداشت امامت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (588, 3, 253, 'روش سوگند دادن ستمکار', 'امام (ع) کرارا میفرمود: هر گاه خواستید ظالمى را سوگند دهید اینگونه سوگند دهید که او از حول و قوت خدا بیزار است چه او اگر به دروغ چنین سوگندى خورد در کیفرش شتاب شود و اگر سوگند خورد به خدایى که جز او خدایى نیست در عقوبتش شتاب نشود چرا که او خدا را به یگانگى یاد کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (589, 3, 254, 'تلاش در انفاقهاى اقتصادى', 'ای فرزند آدم وصى خود در مال خویش باش امروز به گونه‌ای عمل کن که دوست داری پس از مرگت عمل کنند.!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (590, 3, 255, 'تندخویى و جنون', 'تندخویى، نوعى دیوانگى است زیرا تندخو از کرده خود پشیمان می‌شود اگر پشیمان نشد، پس دیوانگی او پایدار است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (591, 3, 256, 'حسادت و بیمارى', 'سلامت بدن، از کمی حسد است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (592, 3, 257, 'راه شاد کردن دیگران', 'به «کمیل بن زیاد نخعى» فرمودخاندانت را فرمان ده که روزها در کسب فضائل و مکارم بکوشند و شب‌ها در رفع نیاز مردمى که خوابند تلاش کنند پس سوگند به کسى که تمام صداها را می‌شنود هیچکس دلی را شاد نکند مگر آنکه خداوند از آن شادمانی، برایش لطفی می‌آفریند چون مصیبتی بدو رسد آن لطف چون آبى به سوى او سرازیر شود تا تلخی مصیبت را از او دور گرداند چنانکه شتر غریبه را از چراگاه دور سازند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (593, 3, 258, 'صدقه و توانگرى', 'آن گاه که فقیر و نیازمند شدید با صدقه دادن، با خدا تجارت کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (594, 3, 259, 'شناخت جایگاه وفادارى', 'وفادارى با بی‌وفایان، بی‌وفایى با خداست و بی‌وفایى با بی‌وفایان، وفادارى با خداست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (595, 3, 260, 'پرهیز از مهلت دادن‌هاى خدا', 'بسا کسی که با احسانی (که خدا به او کرده است) فریب خورد و بسا کسى که بخاطر خطاپوشى خداوند مغرور می‌شود و بسا کسی که خداوند نام نیکش را بر زبان‌ها اندازد و فریفته می‌شود خداوند هیچکس را به آزمایشى همچون مهلت‌دادن نیازموده است. \r\nسید رضی می‌گوید: \r\nاین سخن امام درکلمات گذشته آمده بود و در اینجا با اضافاتی نیکو و سودمند تکرار شد. \r\nدر این فصل گزیده‌ای از سخنان امام (ع) را می‌آوریم که در آن‌ها الفاظ غریب آمده و نیاز به تفسیر دارد در حدیثى از آن حضرت (ع) آمده است: \r\nچون چنین شود پیشوای دین با پیروان خود به راه افتد و مردم بر او گرد آیند همانگونه که ابرپاییزی گرد می‌آید. \r\nسید رضی می‌گوید: \r\n. «یعسوب» به معنى سرور و مهتر قوم است که کارهاى مردم به دست اوست و «قزع» به معنى پاره‌هاى ابر است که در آن باران نباشد\r\nدر حدیثى از آن حضرت (ع) آمده است این سخنران ماهری است. \r\nسید رضی می‌گوید: \r\nمراد از «شحشح» کسى است که در اداى خطبه و ادامه دادن آن مهارت دارد و هر که در سخن و رفتار چالاک باشد او را «شحشح» گویند در غیر این موضوع به معنى بخیل و ممسک است\r\nدر حدیثى از آن حضرت (ع) آمده است: دشمنى، رنج‌ها و سختی‌هایی هلاک کننده دارد. \r\nسید رضی می‌گوید: \r\nمنظور امام از «قحم» مهلکه‌ها است زیرا دشمنى آنان را به هلاکت می‌رساند و از این باب است که گفته می‌شود: «قحمة الاعراب آن سالهاى قحطى است که ایشان را فرا گیرد و اموالشان را نابود گرداند و معنى «تقحم» نیز همین است. البته در مورد معنى این جمله معنی دیگرى نیز گفته شده و آن این است که خشکى صحرا ناچارشان سازد تا به شهرها رو آورند و یا به کلامی دیگر خشکسالى روستائیان را از سرزمین خود به مناطق سبز و آباد می‌کشد.» \r\nدر حدیثى از آن حضرت (ع) آمده است چون زنان به بلوغ (نص الحقاق) رسیدند خویشاوندان پدرى برای سرپرستی سزاوارترند. \r\nسید رضی می‌گوید: \r\n«نص الحقائق» هم روایت شده است و نص نهایت هر چیز است و رسیدن به پایان آن چون «نص در رفتن» و آن منتهاى سرعتى است که چهار پا تواند رفت. و مى‌گویى «نصصت الرجل عن الامر» منظور اینست که آنقدر سؤال از کسى بشود که آنچه در آن زمینه می‌داند بیان کند بنابر این «نص الحقائق» بمعنى رسیدن به مرحله بلوغ است که همان پایان دوره کودکى استو آن هنگامى است که کودک پاى به دوران جوانى می‌نهدو این از فصیح‌ترین و غریب‌ترین کنایات از این مرحله استمنظور امام این است: هنگامى که زنان بدین مرحله رسیدند خویشاوندان پدرى از خویشاوندان مادرى به آنان نزدیک‌ترند اگر محرم باشند چون برادران و عموها و به شوى دادن او اگر بخواهند و حقاق ستیز مادر است با خویشاوندان پدرى بر سر دخترکه هر یک از آن دو به دیگرى می‌گوید که من سزاوارتر به او هستمگویند از این معنى است حاققته حقاقا، مانند جادلته جدالا و گفته‌اند: نص الحقاق رسیدن عقل است به کمال و آن بالغ شدن استچه قصد امام از آوردن این کلمه هنگامى است که در آن حقوق و احکام واجب می‌شودو آنان که «نص الحقایق» گفته اند، از حقایق جمع حقیقت را فهمیده‌انداین معنى را ابوعبید آورده استاما نظرمن این است که منظور از «نص الحقاق» این است که زن به مرحله‌ای برسدکه در آن سن، ازدواجش جایز باشد و بتواند درحقوق خود تصرف کند این در حقیقت تشبیه به «حقاق» در شتر است چرا که «حقاق» جمع «حق» است و آن شترى است که سه سالگى را به پایان رسانیده و به چهارمین سال در آمده است و در این هنگام به حدى رسیده که می‌توان بر آن سوار شد و تند راند و حقائق نیز جمع حقه است پس هر دو روایت به یک معنى باز مى‌گرددو این معنى به روش عرب شبیه‌تر است تا معنى که آورده شد. \r\nدر حدیثى از آن حضرت (ع) آمده است: ایمان به صورت نقطه سفید در دل ظاهر می‌شود و هراندازه که ایمان افزایش یابد، نقطه سفید فراخ‌تر می‌گردد. \r\nسید رضی می‌گوید: و لمظه همانند نقطه سیاه است یا مانند آن از سپیدى و از همین باب گفته شده است: «فرس المظ» (اسب المظ)، که به اسبى گفته می‌شود که بر لبش نقطه سفیدى وجود داشته باشد. \r\nدر حدیثى از آن حضرت (ع) آمده است: هرگاه کسى طلبی دارد که نمی‌داند وصول می‌شود یا نه باید هنگامى که آن را می‌ستاند زکاتش را بدهد. \r\nسید رضی می‌گوید: بنابر این «دین ظنون» آن است که طلبکار نمی‌داند آیا می‌تواند از بدهکار وصول کند یا نه گاه امیدوار است به گرفتن آن و گاه ناامید از ستاندنش این از فصیح‌ترین سخنان است همچنین هر کارى که طالب آن هستى و نمی‌دانى در چه موضعى نسبت به آن خواهى بود، آن را «ظنون» گویند \r\nو گفته اعشى شاعر عرب از همین باب است آنجا که می‌گوید: چاهى که معلوم نیست آب دارد یا نه و از محلى که باران‌گیر باشد دوراست نمی‌شود آن را همچون فرات، که پر از آب است و کشتى و شناگر ماهر را از پا در می‌آورد، قرار داد «جد» چاه قدیمى بیابانى را گویند و ظنون آن است که معلوم نیست آب دارد یا نه. \r\nو در حدیث آن حضرت است، چون سپاهى را مشایعت کرد که به جنگشان مى‌فرستاد فرمود: از زنان تا می‌توانید دوری کنید. \r\nسید رضی می‌گوید: \r\nیعنى به هنگام نبرد از یاد زنان و دل بستن به آن‌ها دورى کنید و با آنان همبستر مشوید زیرا این کار بازوى حمیت را سست کندو گره عزیمت‌ها را بگسلد و مانع تعقیب دشمن شود و جنگجو را از فرو رفتن در می‌دان نبرد روى گردان سازد وهر چه انسان را از کارى باز می‌دارد، گویند، «اعذب عنه و «العاذب» و «العذوب» کسى که از خوردن و آشامیدن روى بر تافته است\r\nدر حدیثى از آن حضرت (ع) آمده است: همانند قمارباز پیروز منتظر نخستین پیروزى از تیرهاى قمار خود است. \r\nسید رضی می‌گوید: \r\nمنظور از «یاسرون» کسانى هستند که به وسیله پیکان‌هاى تیر بر سر شترى بخت‌آزمائى می‌کنند و «فالج» کسى است که پیروزشده استگفته می‌شود «فلج علیهم و فلجهم» یعنى بر آن‌ها پیروز شد\r\nو راجز گوید: چون دیدم که پیروز، پیروز شده است. \r\nدر حدیثى از آن حضرت (ع) آمده است: چون (تنور) جنگ داغ می‌شد به رسول الله پناه می‌بردیم کسی از ما، به دشمن از او نزدیک‌تر نبود. \r\nسید رضی می‌گوید: معنى این سخن این است که: وقتى ترس از دشمن بزرگ می‌نمودو جنگ به گونه‌ای می‌شد که گویا جنگجویان را می‌خواهد در کام خود فرو برد مسلمانان به پیامبر پناهنده می‌شدند تا رسول خدا شخصا به نبرد پردازد و خداوند بوسیله او نصرت و پیروزى را بر آنان نازل فرماید و در سایه آنحضرت ایمن گردنداما جمله «اذا احمر الباس کنایه از شدت کارزار است در این باره سخنان متعددى گفته شده که بهترین آن‌ها این است که امام داغى جنگ را به شعله‌هاى سوزان آتش تشبیه کرده است و آنچه این معنى را تقویت می‌کند این است که رسول‌الله (صلى الله علیه و آله) در روز حنین، در جنگ با هوازن چون جلادت مردان را دید فرمود اکنون تنور جنگ داغ شد همانگونه که ابرپاییزی گرد می‌آید\r\nاین بخش (کلمات پیچیده محتاج به تفسیر) پایان پذیرفت و ما به روش و سبک خود در این باره باز می‌گردیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (596, 3, 261, 'مظلومیت امام على علیه السلام', 'چون خبر غارت بردن یاران معاویه را بر انبار شنید خود پیاده به راه افتاد تا به نخیله (منزلگاهى در نزدیکى کوفه) رسید مردم در نخیله بدو پیوستند و گفتند اى امیر مومنان ما کار آنان را کفایت مى‌کنیم امام فرمود: به خدا! مرا از خودتان بی‌نیاز نتوانید کرد چگونه می‌توانید مرا از دیگران بی‌نیاز کنید اگر رعایاى پیش از من از ستم فرمانروایان خود شکایت می‌کردند من امروز از ستم رعیتم شکایت دارم گویا من پیروم و آن‌ها زمامدار یا من فرمانبر و محکوم و آن‌ها فرمانروایانند چون امام این نکته در یک ضمن سخنرانی طولانی فرمود دو مرد از یاران وى نزد او آمدند، یکى از آن دو گفت من جز خود و برادرم را در اختیار ندارم اى امیر مومنان فرمان ده تا انجام دهیم امام فرمود: شما کجا و آنچه من مى‌خواهم کجا؟!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (597, 3, 262, 'مشکل حق ناشناسى', 'و گفته‌اند حارث بن حوت نزد امام آمد و گفت: چنین پندارى که من اصحاب جمل را گمراه مى‌دانم؟ چنین نیست امام در پاسخ فرمود: اى حارث تو به پایین نگریسته‌ای و به بالا ننگریسته‌ای از این رو حیران وسرگردان مانده‌ای تو حق را نشناخته‌ای تا بدانى اهل حق چه کسانی هستند و نیز باطل را هم نشناخته‌ای تا بدانى آن‌ها که راه باطل می‌سپرند، کدام‌اند حارث گفت من با سعید بن مالک و عبدالله پسر عمر کناره مى‌گیرم امام فرمود: «سعید» و «عبد الله بن عمر» نه حق را یارى کردند و نه باطل را خوار ساختند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (598, 3, 263, 'مشکل هم نشینى با قدرتمندان', 'همنشین سلطان چون کسى است که بر شیر سوار شده دیگران به مقام او غبطه می‌خورند و او خود بهتر از هر کس می‌داند که در کجا نشسته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (599, 3, 264, 'نیکى به بازماندگان دیگران', 'به بازماندگان دیگران نیکى کنید تا حرمت بازماندگان شما را نگاه دارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (600, 3, 265, 'گفتار حکیمان و درمان', 'سخن حکیمان اگر درست باشد داروست و اگر نادرست باشد، درد است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (601, 3, 266, 'روش صحیح پاسخ دادن', 'و مردى از او خواست تا ایمان را به وى بشناساند امام فرمود: فردا نزد من آى تا با تو بگویم ومردم هم بشنوند تا اگر سخن مرا فراموش کردى دیگرى برایت حفظ و نگهدارى کند زیراسخن چون شکار رمنده است یکى آن را می‌گیرد، دیگرى از دستش می‌دهد. \r\nسید رضی می‌گوید\r\nو پاسخ امام را از این پیش آوردیم و آن سخن اوست که ایمان بر چهار شعبه است. (حکمت ۳۱.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (602, 3, 267, 'پرهیز از حرص ورزى در کسب روزی', 'اى پسر آدم اندوه روز نیامده‌ات را بر امروزت میفزا زیرا فردا، اگر از عمر تو باشد خداوند در آن روز، روزیت را خواهد داد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (603, 3, 268, 'اعتدال در دوستى‌ها و دشمنى‌ها', 'دوستت را چندان دوست مدار! بسا که روزى دشمنت شود و دشمنت را چندان دشمن مدار بسا که روزى دوستت گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (604, 3, 269, 'روش برخورد با دنیا', 'مردم در دنیا دوگونه عمل می‌کنند یکى در دنیا، براى دنیا کار می‌کند دنیا او را را از آخرت مشغول داشته است و می‌ترسد که بازماندگانش گرفتار فقر شوند ولى (از فقر معنوى اخروى) خویش را در امان می‌داند چنین کسى عمر خود را براى سود دیگران نابود می‌کند و آن (گروه) دیگر در دنیا براى پس از دنیا کارمى کند پس بی‌آنکه کار کند بهره وى از دنیا پیش او می‌آید پس هر دو بهره (دنیا و آخرت) را با هم می‌برد و مالک هر دو سرا می‌شود و در درگاه خداوند آبرومند خواهد بود از خدا چیزی نمی‌خواهد که او را از آن منع کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (605, 3, 270, 'ضرورت حفظ اموال کعبه', 'و گفته‌اند که در روزگار خلافت عمر بن خطاب از زیور کعبه و فراوانى آن نزد وى سخن رفت گروهى گفتند اگر آن را به فروش رسانى و به بهایش سپاه مسلمانان را آماده گردانى ثوابش بیشتر است کعبه را چه نیاز به زیور است؟ عمر تصمیم چنین کاری گرفت و از امیرالمومنین پرسیدامام فرمود: هنگامی که قرآن بر پیامبر (ص) نازل شد دارایی‌ها بر چهار نوع بود یکی دارایی (فرد) مسلمان است که پس از آن‌ها به میان وارثانشان، به گونه‌ای که مقرر شده، تقسیم می‌شود «فیى ء» یا غنائم جنگی که آن را بر مستحقان آن تقسیم کرد و سوم، خمس که خداوند تقسیم آن را طریقه‌ای معین کرده است و چهارم صدقات که خدا آن را در مصرف‌هاى معین قرار داده است و در آن هنگام زیورهاى «کعبه» بر آن بود و خداوند آنرا بر همان حال گذاشت نه اینکه از روى فراموشى آن را کنار گذاشته باشدو برای او، مکان آن زیور پنهان نمانده بود بنابر این تو هم آنرا بر همان حال که خدا و پیامبرش آنرا قرار داده‌اند باقى گذار «عمر» به او گفت: «اگر تو نبودى رسوا می‌شدیم و زیورهاى کعبه را بحال خود واگذاشت.».', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (606, 3, 271, 'روش صحیح قضاوت', 'و روایت شده است که دو مرد را نزد او آوردند که از مال خدا دزدى کرده بودند یکى از آن دو بنده بود از بیت المال مسلمانان و دیگرى در ملک مردمان امام علیه السلام فرمود: اما آن یکى از مال خداست و حدى بر او نیست چه مال خدا برخى از مال خدا را خورده است و اما دیگرى بر او حد جارى است سپس دست او را برید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (607, 3, 272, 'ضرورت استقامت در برداشتن کجی‌ها', 'اگر از این فتنه‌ها و لغزش‌گاه‌ها با قدرت بگذرم چیزهایی را تغییر خواهم داد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (608, 3, 273, 'ضرورت توکل به خداوند', 'بدانید و یقین بدانید که خداوند برای بنده‌اش قرار نداده است اگر چه او بسیار چاره‌جو سخت‌کوش و در طرح و نقشه نیرومند باشد جز آنچه در علم الهی وعده فرموده است و ناتوانى بنده و کمى چاره جوئى وى موجب نمی‌شود، که آنچه برایش مقرر گشته باو نرسد و آن کس که این حقیقت داند و آن را به کار بندد از همه مردم آسوده‌تر بوده و سود بیشتر خواهد برد وکس که این اندیشه را رها کند و در آن شک دارد از همه مردم گرفتارتر و زیانکارتر است چه بسا صاحب نعمتى که فریب نعمت خود خورد و اندک اندک راه عذابى الهى پوید و بسا مبتلایى که خداوند بیازمایدش تا نعمتى دهد پس ای بهره مند، در شکر خدای خویش بیفزای و از شتاب بیجا دست بردار و بیش از آنچه تو را روزى است انتظار نداشته باش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (609, 3, 274, 'ضرورت عمل گرایى', 'علمتان را به جهل بدل نکنید و یقینتان را به شک اگر دانستید پس به دانشتان عمل کنید و اگر به یقین رسیدید اقدام کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (610, 3, 275, 'ضد ارزش‌هاى اخلاقى', 'طمع کشانده انسان به هلاکت است و نجات نمی‌دهد ضمانت کننده‌ای است که وفا نمی‌کند بسا نوشنده‌ی آبی که پیش از سیراب شدن، آب گلوگیرش شود هر مقدار ارزش چیزى که براى بدست آوردنش رقابت می‌کنند بیشتر باشد بهمان قدر مصیبت از دست دادنش زیادتر است و آرزوها چشم بصیرت را نابینا می‌کند و بخت و اقبال بدنبال کسی است که بدنبال آن نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (611, 3, 276, 'پرهیز از دورویى‌ها', 'خدایا! به تو پناه مى‌برم از اینکه ظاهرم را در چشم‌ها نیکو جلوه دهى و درونم در آنچه که از تو پنهان می‌دارم، زشت باشد و در نزد مردم از روى ریا خودنمایى کنم در کارهایى که تو از من بدان آگاه‌ترى و خود را در برابر مردم نیکو جلوه دهم و اعمال بدم را نزد تو آرم تا به بندگانت تقرب جویم و از خشنودى تو دور گردم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (612, 3, 277, 'سوگند امام علیه السلام', 'نه، سوگند به خدایی که با قدرت او شب تاریک را به سر بردیم که روز سپیدی در پی داشت چنین و جنان نبوده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (613, 3, 278, 'ارزش تداوم عمل', 'کار اندکی که دوامش می‌دهی امیدوارانه‌تر از کار بسیاری است که از آن خسته شوی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (614, 3, 279, 'شناخت جایگاه واجبات و مستحبات', 'آنگاه که نوافل (مستحبات) به واجبات آسیب می‌رساند آن را ترک گوئید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (615, 3, 280, 'یاد قیامت و آمادگى', 'کسی که دوری سفر (آخرت) را به یاد می‌آورد آمادگی (لازم را) در خود ایجاد می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (616, 3, 281, 'برترى عقل از مشاهده چشم', 'اندیشیدن، همانند دیدن با چشم‌ها نیست بسا که چشم‌ها به صاحبش دروغ می‌گویند و حالی که عقل به کسى که از او نصیحت خواهد، خیانت نکند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (617, 3, 282, 'غرور، آفت پند پذیرى', 'میان شما و موعظه پرده ای از غفلت (آویزان) است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (618, 3, 283, 'علل سقوط جامعه', 'جاهلان شما پر تلاش و دانشمندان شما تن پرور و و کوتاهی ورزند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (619, 3, 284, 'دانش نابود کننده عذرها', 'دانش (راه را) بسته است بر عذر بهانه جویان.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (620, 3, 285, 'ضرورت استفاده از فرصت‌ها', 'آن‌ها که زمانشان پایان گرفته خواهان مهلتند و آن‌ها که مهلت دارندتعلل می‌ورزند و کوتاهى می‌کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (621, 3, 286, 'سرانجام خوشى‌ها', 'مردم به چیزى نگفتند: خوش به حالش مگر آنکه روزگار برای او روز بدی را (در مشت خود) در نهان داشته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (622, 3, 287, 'مشکل درک قضا و قدر', 'از امام درباره (قضا و) قدر سؤال شد امام فرمود: راهی تاریک است در آن گام ننهید و دریایی عمیق است در آن وارد نشویدو رمزی از رمزهای خداوند است خود را به زحمت نیاندازید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (623, 3, 288, 'جهل و خوارى', 'هرگاه خداوند بنده‌ای را خوار خواهد او را از آموختن علم باز دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (624, 3, 289, 'الگوى کامل انسانیت', 'در گذشته برادری دینی داشتم آنچه او را در چشم من بزرگ می‌نمود کوچک دیدن دنیا در چشم او بود شکم بر او سلطه‌ای نداشت آنچه را که نمی‌یافت آرزو نمی‌کرد و چون می‌یافت زیاده‌روی نمی‌کرد اکثر اوقات زندگیش ساکت بود و چون به سخن در می‌آمد بر سخنوران برتری داشت و عطش پرسش‌کنندگان را فرو می‌نشاند به ظاهر ضعیف و مستضعف می‌نمود ولی به وقت جد و جهاد او شیر بیشه بود و یا افعی دره حجتی (به میان) نمی‌آورد مگر آنکه داور (نهایی) بود و کسى را که عذرى داشت سرزنش نمى‌نمود تا آنکه عذر او را می‌شنید از درد خود شکوه نمی‌کرد مگر پس از بهبودی از آن آنچه را مى‌کرد مى‌گفت و اگر نمی‌کرد، نمی‌گفت و اگر در سخن گفتن بر او پیشی می‌گرفتند در سکوت شکست نمی‌خورد همواره بر شنیدن بیشتر از گفتن حریص بود و گاهى که او را دو کار پیش مى‌آمد می‌اندیشید که کدام به هوای نفس نزدیک‌تر است با آن به مخالفت بر می‌خاست پس بر شما باد چنین خصلت‌ها آن‌ها را فرا گیرید و بر سر آن با یکدیگر رقابت نمایید اگر نتوانستید به همه آن‌ها برسید بدانید بدست آوردن برخی از آن ارزش‌های اخلاقی بهتر از ترک کردن همه است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (625, 3, 290, 'مسؤولیت نعمت‌ها', 'اگر هم خداوند از نافرمانى خود بیم نمى‌داد واجب بود بشکرانه نعمت‌هایش نافرمانى نشود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (626, 3, 291, 'روش تسلیت گفتن', 'امام در تسلیت به «اشعث ابن قیس» در مرک فرزندش چنین فرمود: ای اشعث! اگر بر پسرت اندوهگینى به خاطر پیوند خویشاوندی سزاواری و اگر شکیبایی کنی هر مصیبت را نزد خدا پاداشى است اى اشعث! اگر شکیبا باشی تقدیر الهی بر تو جاری می‌شود و تو پاداش می‌یابی و اگر بی‌تابی نمایی تقدیر الهی بر تو جاری می‌شود و وزر و گناه بر توخواهد بود ای اشعث پسرت تو را شاد می‌ساخت و او براى تو مایه‌ی بلا و آزمایش بود و مرگ او تو را اندوهگین کرد در حالى که براى تو پاداش و رحمت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (627, 3, 292, 'عزاى پیامبر علیه السلام و بی تابی‌ها', 'و بر مزار رسول خدا (ص)، هنگام به خاک سپردن او گفت: همانا شکیبایی زیباست جز درغم از دست دادن تو و همانا بی‌تابی زشت است مگر در فقدان تو و مصیبت فقدان تو بزرگ است و هر مصیبتى پیش از تو و بعد از تو در برابر آن کوچک و حقیر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (628, 3, 293, 'دوستى با احمق هرگز', 'با احمق همنشین مباش که کردار خویش در چشم تو بیاراید و دوست می‌دارد که چون او باشی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (629, 3, 294, 'فاصله میان شرق و غرب', 'و کسى از وى مسافت میان مشرق و مغرب را پرسید فرمود: مسیر یک روزه خورشید است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (630, 3, 295, 'شناخت دوستان و دشمنان', 'دوستان تو سه کس‌اند و دشمنان تو نیز، سه کس اما دوستان تو: دوست تو و دوست دوست تو و دشمن دشمن توست و دشمنانت: دشمن تو و دشمن دوست تو و دوست دشمن توست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (631, 3, 296, 'پرهیز از دشمنى‌ها', 'و امام مردى را دید که چنان بر ضد دشمن خویش مى‌کوشید که به خود زیان مى‌رسانید بدو فرمود: تو همانند کسی هستی که به خود نیزه فرو برد تا کسى را که پشت سرش است را بکشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (632, 3, 297, 'ضرورت عبرت گرفتن', 'فرصت‌ها چقدر فراوانند و عبرت گرفتن‌ها چقدر اندک!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (633, 3, 298, 'اعتدال در دشمنى‌ها', 'آن که در خصومت مبالغه کند گناه کرده است و کسی که در آن کوتاهی کند ستم کرده است و چون کسی خصومت ورزد، تقوای خود را نگاه نمی‌تواند داشت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (634, 3, 299, 'نماز در نگرانى‌ها', 'مرا اندوهگین نمی‌دارد گناهی که پس از آن مهلت دو رکعت نماز گزاردن داشته باشم و از خدا عافیت طلبم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (635, 3, 300, 'شگفتى روزى بندگان', 'از امام پرسیدند: خداوند چگونه با وجود کثرت انسان‌ها به حسابشان رسیدگی می‌کند؟ امام فرمود: همانگونه که با وجود کثرتشان روزیشان می‌دهد پرسیدند: چگونه حسابشان را مى‌رسد و او را نمى‌بینند؟ امام فرمود: همانگونه که روزیشان می‌دهد و او را نمی‌بینند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (636, 3, 301, 'نشانه عقل', 'فرستاده تو میزان خرد تو را می‌رساند و نامه تو گویاترین سخنگوى تو است!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (637, 3, 302, 'نیاز انسان به دعا', 'دردمندی که بلا در جانش تاخته است به دعا نیازمندتر نیست از تندرستی که از بلا ایمنى ندارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (638, 3, 303, 'ارزش دنیا دوستى', 'مردم فرزندان دنیا هستند و انسان را برای عشق به مادرش نمی‌توان سرزنش کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (639, 3, 304, 'ضرورت پاسخ دادن به درخواستها', 'انسان بینوا پیک خداوند است کسی که از یاری او دریغ کند از خدا دریغ کرده است و آن کس که به او بخشش کند به خدا بخشیده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (640, 3, 305, 'غیرتمندى و ترک زنا', 'غیرتمند، هرگز زنا نمی‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (641, 3, 306, 'اجل، نگهبان انسان', 'اجل، برای حفاظت انسان کافی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (642, 3, 307, 'مشکل مال غارت شده', 'انسان بر غم مرگ فرزند می‌خوابد اما بر از دست دادن مال نمی‌خوابد. \r\nسید رضی می‌گوید\r\nمعنى سخن بالا این است که انسان بر کشته شدن فرزندان بردباری می‌کند اما در غارت و ربودن اموالش بردبار نیست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (643, 3, 308, 'ره آورد دوستى پدران', 'دوستی میان پدران سبب خویشاوندی فرزندن است و خویشاوندى به دوستى نیازمندتر است ازدوستى به خویشاوندى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (644, 3, 309, 'ارزش گمان مؤمن', 'از گمان‌هاى مردم با ایمان بپرهیزید که خدای متعال حق را بر زبان آنان نهاده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (645, 3, 310, 'ضرورت اطمینان به وعده‌هاى الهی', 'ایمان بنده‌ ای صادق و راست نمی‌گردد مگر آنکه اعتماد او بدانچه در دست خداست بیش از اعتماد وى بدانچه در دست خود اوست باشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (646, 3, 311, 'نفرین امام علیه السلام', 'هنگامى که امام وارد بصره شد «انس بن مالک» را خواست تا نزد «طلحه» و «زبیر» برود و آنچه را که از پیامبر (ص) درباره آن‌ها شنیده به آن‌ها یادآورى کند انس از رساندن پیام سر باز زد و چون بازگشت گفت: آن سخن فراموش کردم\r\nحضرت به او فرمود: اگر دروغ می‌گویى خدایت به برص (سپیدى درخشان) گرفتار گرداند که عمامه آن را نپوشاند. \r\nسید رضی می‌گوید\r\nیعنى بیمارى برص چیزى نگذشت که لکه‌هاى سفید برص، در چهره انس آشکار گشت از آن پس هیچکس او را بی‌نقاب ندید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (647, 3, 312, 'روانشناسى عبادات', 'برای دل‌ها روی آوردن و نشاط و ملالی است پس آنگاه که نشاط دارند آن را به انجام مستحبات وا دارید و آنگاه که ملالت دست داد به انجام واجبات قناعت کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (648, 3, 313, 'جامعیت قرآن', 'در قرآن خبر پیشینیان شما است و خبر بعد از شما است و نیز دستور زندگی شما در قرآن است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (649, 3, 314, 'روش برخورد با متجاوز', 'سنگ را از همان جا که آمده باز گردانید چرا که شر و بدى را جز با بدى نمی‌توان رفع نمود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (650, 3, 315, 'روش نویسندگى', 'و به نویسنده خود عبیدالله بن ابى رافع فرمود\r\n در دواتت لیفه انداز نوک قلمت را بلند گیر و میان سطرها فاصله بگذار فاصله بین حروف را کم کن اینها براى زیبایى خط شایسته‌تر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (651, 3, 316, 'پیشوایى مؤمنان و تبهکاران', 'من پیشوا (یعسوب) مومنان هستم و مال پیشوای تبهکاران. \r\nسید رضی می‌گوید: \r\nمعنى این سخن این است که مؤمنان از من پیروی می‌کنند و بدکاران پیرو مال می‌باشند همانگونه که زنبوران عسل از رئیس خود اطاعت می‌کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (652, 3, 317, 'اختلاف مسلمین و انحراف یهودیان', 'بعضی از یهودیان به امام گفتند: پیامبر خود را دفن نکرده، درباره‌اش خلاف ورزیدید امام در پاسخ فرمود: ما بعد از او اختلاف کردیم نه (در زمان) او اما شما هنوز پاهایتان از آب دریا خشک نشده بود که به پیامبرتان گفتید «براى ما هم خدایى بساز همانطور که آن‌ها (مصریان) را خدایانى است موسی (به شما) گفت: شما حقا! قومی نادان هستید.».', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (653, 3, 318, 'قاطعیت در مبارزات', 'امام را گفتند: چگونه بر حریفان پیروز گشتى؟ فرمود: من با هیچکس روبرو نشدم جز اینکه مرا بر ضد خودش کمک کرد. \r\nسید رضی می‌گوید: \r\nو اشارت بدین می‌فرماید که بیم من در دل او مى‌افتاد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (654, 3, 319, 'ره آورد شوم تهیدستى', 'و به پسر خود محمد بن حنیفه فرمود: پسرم! از فقر بر تو می‌ترسم از آن به خدا پناه ببر زیرا فقر سبب نقص در دین است عقل را سرگردان می‌کند و سبب خشم انسان می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (655, 3, 320, 'روش صحیح پرسیدن', 'امام به کسی که از آن حضرت مسئله پیچیده‌ای را پرسید، فرمود: براى فهمیدن بپرس و نه برای آزار دادن که نادان آموزنده همانند دانشمند است و دانشمند بی‌انصاف همچون نادان بهانه‌جو است!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (656, 3, 321, 'قاطعیت رهبرى در مشورت', 'و به عبدالله پسر عباس درباره نظرى که داده بود و امام موافق آن نبود فرمود: بر تو است که نظر مشورتی به من بگویی و من در آن می‌نگرم اگر نپذیرفتم باید از من اطاعت کنی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (657, 3, 322, 'ضرورت تقویت روحیه مردم پس از جنگ', 'روایت شده که چون امام وارد کوفه شد در حالی که از صفین باز می‌گشت بر شبامیان گذشت و صدای گریه زنان را بر کشتگان صفین شنید حرب پسر شرحبیل شبامى به سوى حضرتش آمد که از سران قبیله خود (شبامیان) بود آیا زنان شما بر شما چیره شده‌اند آن گونه که من می‌شنوم؟ آیا آن‌ها را از این ناله‌ها باز نمیدارید؟ حرب پیاده به راه افتاد و امام سواره بود، او را فرمود: بازگرد که پیاده رفتن (رئیس قبیله ای) چون تو با چون من (که رئیس دولت هستم) موجب فریفته شدن والى است و برای مومن ذلت و خواری است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (658, 3, 323, 'علل انحراف خوارج', 'امام روز جنگ نهروان هنگامى که از کنار کشتگان خوارج، می‌گذشت فرمود: بدا بحال شما! آن کس که شما را فریب داد به شما ضرر زد او را گفتند اى امیر مومنان! که آنان را فریفت؟ فرمود: شیطان گمراه کننده و نفس فرمان دهنده بر بدى آنان را با آرزوها فریب داد راه گناه را بر آن‌ها گسترد و به آنان وعده پیروزی داد و آن‌ها را در آتش جهنم درافکند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (659, 3, 324, 'خداترسی در خلوتگاه‌ها', 'از نافرمانى خدا در خلوت‌ها بپرهیزید زیرا آنکه شاهد اعمال است، همو داورى کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (660, 3, 325, 'اندوه عزاى محمد بن ابى بکر', 'زمانی‌که خبر شهادت محمد ابن ابى بکر بامام رسید فرمود: اندوه ما در کشته شدن او، به قدر شادمانى شامیان است جز آنکه آن‌ها دشمنى را از دست دادند و ما دوستی از دست دادیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (661, 3, 326, 'مهلت پذیرش توبه', 'عمری که خداوند از فرزند آدم پوزش را می‌پذیرد شصت سال است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (662, 3, 327, 'پیروزیهاى دروغین', 'پیروز نشده است آنکه با گناه به پیروزی رسیده است و پیروز با زشتی (و ستم) شکست خورده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (663, 3, 328, 'وظیفه سرمایه داران', 'خداى سبحان روزى درویشان را در اموال توانگران واجب گردانیده است فقیری گرسنه نماند مگر بواسطه اینکه، ثروتمندان حق آنان را نداده‌اند و خداى تعالى توانگران را، بدین سبب، باز خواست خواهد کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (664, 3, 329, 'بى نیازى از عذر خواهى', 'بی‌نیازی از عذرخواهی بهتر از راستگویی (و عذرخواهی) است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (665, 3, 330, 'مسؤولیت نعمت‌ها', 'کمترین حق خدا بر شما این است که با کمک نعمت‌های الهی داده شده به شما گناه نکنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (666, 3, 331, 'ارزش اطاعت و بندگی', 'خداى سبحان طاعت را غنیمت زیرکان قرار داد، هنگامى که ناتوانان در انجام دادن آن کوتاهی می‌کنند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (667, 3, 332, 'مسؤولیت رهبرى', 'سلطان حاکم خداوند بر روی زمین است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (668, 3, 333, 'روانشناسى مؤمن', 'علی (ع) در توصیف مومن فرمود: شادی مومن در چهره او است و اندوهش در دل او است سینه‌اش از هر چیزی فراخ تر و نفس او از هر چیزی خوارتر است برتری جویی را خوش نمی‌دارد و از آوازه‌ی بلند گریزان است غم طولانی دارد و همت او بلند است سکوتش بسیار، و تمام وقت او مشغول است شکرگزار و شکیبا است در تفکر فرو رفته است در اظهار نیاز، سخت بخیل است نرم‌خو است و سرشتی نرم دارد جانش از صخره سخت‌تر است و او (در مقابل دوستان و مومنین) از برده خوارتر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (669, 3, 334, 'یاد مرگ و آرزوها', 'اگر بنده اجل و پایان کار خود را می‌دید و سرنوشتش را با آرزو (ی شیطانی) و فریب آن دشمنی می‌ورزید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (670, 3, 335, 'آفات اموال', 'هر انسانى در اموالش دو شریک دارد وارث و حوادث.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (671, 3, 336, 'مسؤولیت وعده دادن', 'کسى که از او چیزى خواسته اند، آزاد است تا زمانى که وعده دهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (672, 3, 337, 'ضرورت عمل گرایى', 'دعوت کننده بی عمل چون تیرانداز بدون کمان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (673, 3, 338, 'اقسام علم', 'دانش دو گونه است فطری و اکتسابی (شنیدنی دانش اکتسابی اگر هماهنگ با دانش فطرى نباشد سودمند نخواهد بود.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (674, 3, 339, 'قدرت و حاکمیت اندیشه', 'استواری رای با کسی است که قدرت و دارایی دارد با روی آوردن آن می‌آید و با رفتن آن می‌رود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (675, 3, 340, 'ارزش پاکدامنى و شکرگزارى', 'خودداری از سئوال زینت فقر است و شکر (نعمت) زینت ثروتمندی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (676, 3, 341, 'روز دردناک ظالم', 'روز اجرای عدل بر ستمگر شدیدتر است از روز ستم بر مظلوم و ستمدیده.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (677, 3, 342, 'راه بی‌نیازى', 'بزرگترین ثروت (بی‌نیازی) نومیدی است از آنچه در دست مردم است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (678, 3, 343, 'شناخت مردم و ضد ارزشها', 'سخنان محفوظند (و می‌مانند) و باطن آدمیان در آزمایش است و هر کس گروگان دستاورد خویش است (سوره مدثر، ۳۸) و مردم روی در نقصان دارند (بلاها در تن و روانشان) داخل می‌شوند مگر خداوند در امانشان دارد پرسشگرشان مردم آزار و پاسخگویشان در تکلف است نزدیک است برترین آن‌ها از نظر فکر و اندیشه بخاطر خشنودی و یا خشمی از راى خود باز گردد و آن که از همه استوارتر است از نیم نگاهى ناراحت شودیا کلمه‌ای وى را دگرگون کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (679, 3, 344, 'ضرورت توجه به فنا پذیرى دنیا', 'اى مردم از خدا بترسید! بسا آرزومندی که به آرزویش نمی‌رسد و بسا سازندگانی که در آنچه ساخته‌اند سکونت نمی‌کنند و بسا گردآورنده‌ای که زود آن را رها خواهد کرد و شاید آنرا از طریق باطل گرد آورده باشد یا حق دیگران را بازداشته است یا از حرام به دست آورده باشد که گناهش بر گردن اوست پس با بار گناه (به آخرت) باز می‌گردد و در (پیشگاه) خداوند با پشیمانی و دریغ وارد می‌شود «هم دنیا را از دست داده و هم آخرت را و این است زیان آشکار.» (سوره حج، آیه ۱۱).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (680, 3, 345, 'یکى از راههاى پاک ماندن', 'دست نیالودن به گناه، نوعی عصمت است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (681, 3, 346, 'مشکلات درخواست کردن', 'آبرویت جامد است درخواست آن را ذوب می‌کند پس بنگر آن را نزد چه کسی ذوبش می‌کنی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (682, 3, 347, 'جایگاه ستایش', 'ستایش بیش از استحقاق، تملق است و کمتر از استحقاق، از ناتوانی زبان و یا حسد است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (683, 3, 348, 'سخت ترین گناه', 'بدترین گناهان همان است که صاحب گناه، آن را خوار دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (684, 3, 349, 'الگوى انسان کامل', 'آن که در عیب خویش بنگرد از عیب دیگری فرو ماند و آن که از روزی خدا خشنود گردد بر آنچه از دست رفت محزون نمی‌گردد و آن که شمشیر ستم برکشد با همان شمشیر کشته می‌شود و آن که در کارها خود را به مشقت اندازد، هلاک شود و آن که خود را در گرداب‌های خطرناک افکند، غرق می‌گردد و آن که در مکان‌های ناشایست وارد شد، متهم شود و آن که فراوان سخن گوید، فراوان خطا کند و آن که خطای او فراوان گردد، حیای او اندک می‌گردد و آن که حیای او اندک گردد، پرهیزکاری او اندک می‌گردد و آن که پرهیزکاریش اندک گردد، دلش می‌میرد و آن که دلش بمیرد، داخل آتش جهنم می‌شود و آن که در عیب‌های مردم نگرد و آن را زشت شمارد پس آنگاه خود آن‌ها را مرتکب شود، او همان خود احمق است قناعت ثروتی است که پایان نمی‌پذیرد و آن که فراوان یاد مرگ کند به اندکی از دنیا خشنود می‌شود آن که بداند که گفتار او نیز از اعمال او به حساب می‌آید سخنش اندک شود، مگر در آنچه با او ارتباط دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (685, 3, 350, 'روانشناسى مردان ستمکار', 'ستمگر در میان مردم سه نشانه دارد: به مافوق با نافرمانی ستم می‌کند به مادون خود با چیرگی و آزار رسانی (ستم می‌کند) و ستمکاران را یاری و پشتیبانی می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (686, 3, 351, 'امیدوارى در سختى‌ها', 'چون سختى به نهایت رسد، گشایش پدید آید و آنجا که حلقه‌هاى بلا تنگ می‌ردد، راحتى و آسایش فرا می‌رسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (687, 3, 352, 'اعتدال در پرداخت به امور خانواده', 'امام به یکى از یارانش فرمود: اکثر اوقات زندگی (و هم و غم خود) را به همسر وفرزندان اختصاص مده اگر همسر و فرزندانت دوستان خدا بودند خدا دوستان خود را تباه نمی‌کند و اگر دشمنان خدایند چرا هم و غم خود را صرف دشمنان خدا می‌کنی؟.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (688, 3, 353, 'بزرگترین عیب', 'بزرگ‌ترین عیب آن است که چیزى را که در خود داری، بر دیگران عیب شماری.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (689, 3, 354, 'روش تبریک گفتن در تولد فرزند', 'در حضور امام کسى به دیگرى با این عبارت تولد نوزادی را تبریک گفت: قدم این نوزاد قهرمان و یکه سوار مبارک باد امام فرمود: چنین مگوی بلکه بگو: خدای بخشنده را سپاسگزار باش و نوزاد بخشیده بر تو مبارک امید که بزرگ شود و به کمال قدرت برسد و از نیکوکاریش بهره مند گردی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (690, 3, 355, 'پرهیز از تجمل گرایى', 'و مردى از کارگزاران او کاخى شکوهمند برافراخت امام فرمود: سکه‌های نقره سرهای خود را بیرون کردند همانا ساختمان از ثروت تو می‌گوید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (691, 3, 356, 'قدرت خداوند در روزى رسانى', 'از امام پرسیدند: اگر در خانه شخصى به روی او بسته شود و در آن رها کرده شود روزی او از کجا می‌رسد؟ امام فرمود: از آنجا که مرگش می‌آید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (692, 3, 357, 'روش تسلیت گفتن', 'امام به بازماندگان فرد تازه درگذشته ای چنین تسلیت گفت: این امر (مردن) با شما آغاز نشده است و با شما هم پایان نمی‌گیرد این دوست شما به سفر می‌رفته است او را در یکی از سفرهای او بدانید اگر نزد شما برگشت که برگشته است و گرنه، شما به نزد او خواهید رفت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (693, 3, 358, 'مسؤولیت نعمت‌ها', 'اى مردم! باید خداوند شما را به هنگام نعمت، ترسان ببیند همانگونه که شما را از کیفر خود ترسان می‌بیند زیرا کسى که خداوند دراموالش گشایش داده است و گشایش را چون دامى پنهان نبیند، خود را از کارى بیمناک در امان پنداشته است و آن که تنگدست شد و آن را آزمایش به حساب نیاورد پاداشى را که امید آن می‌رفت، تباه کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (694, 3, 359, 'راه خودسازى', 'ای اسیران آرزو آرزوها کوتاه کنید زیرا کسى را که به دنیا گرویده است، نترساند جز صدای برخاسته از دندان‌های حوادث روزگار ای مردم! کار تربیت خود را، خود بر اختیار بگیرید و نفس خود را از عادت‌هاى زشتى که به آن خو گرفته است، برگردانید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (695, 3, 360, 'ضرورت پرهیز از بدگمانى', 'به سخنى که از دهان کسى بیرون می‌آید، گمان بد مبر چندان که برای آن برداشت نیکو می‌یابی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (696, 3, 361, 'روش خواستن از خدا', 'هر گاه از خداوند سبحان حاجتى داری پس حاجت خود را با درود بر پیامبر آغاز کن درور خدا بر او و خاندان او باد سپس حاجت خود را بخواه که خدا بزرگوارتر از آن است که از دو حاجت، یکى را برآورد و دیگرى را باز دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (697, 3, 362, 'ضرورت پرهیز از جدال و درگیرى', 'هر کس آبروی خود دوست دارد مجادله را - باید که - رها کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (698, 3, 363, 'نشانه بی‌خردى', 'از حماقت است شتاب نمودن در کاری پیش از توانایی و درنگ کردن پس از فراهم آمدن فرصت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (699, 3, 364, 'جایگاه پرسیدن', 'از آنچه هنوز رخ نداده، نپرس که آنچه رخ داده براى مشغول ساختن تو کافى است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (700, 3, 365, 'ارزش‌هاى اخلاقى', 'اندیشه آینه‌اى تابناک است و عبرت از حوادث، هشداردهنده‌ای خیرخواه است و تو را در ادب کردن نفس همان بس که از آنچه انجام دادنش را برای دیگران نمی‌پسندی دوری کنی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (701, 3, 366, 'هماهنگى علم و عمل', 'علم با عمل همراه و رفیق است پس آن که دانست، باید به آن عمل کند و علم به عمل فرا می‌خواند اگر (عمل به علم) پاسخ داد، می‌ماند و گرنه کوچ می‌کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (702, 3, 367, 'روش برخورد با دنیا', 'اى مردم! کالاى دنیا همچون گیاهان خشکیده وباخیز است پس از چنین چراگاهى دورى گزینید دل کندن از آن لذت بخش‌تر از دل بستن به آن است و به قدر ضرورت از دنیا برداشتن بهتر از جمع‌آوری ثروت فراوان است آن کس که از دنیا بسیار برداشت، محکوم به فقر و نیازمندى است آن که از دنیا بی‌نیازی جست راحتی برای او رقم خورده است و آن که او را زینت دنیا فریفت دچار کوردلی می‌گردد و آن که عشق دنیا را بدل گرفت، درونش پر از اندوه می‌شود و آن غم و اندوه‌ها در خانه دلش رقصان می‌گردد اندوهی که او را مشغول می‌دارد و او را محزون می‌کند تا آن گاه که گلویش بگیرد و در گوشه ای بمیرد و رگ‌های حیاتش قطع گردد نابود کردن او برای خدا آسان است و افکندنش در گور بر دوستانش سهل باشد. انسان مؤمن بدنیا با چشم عبرت می‌نگرد و از دنیا به اندازه ضرورت برمی‌دارد و با خشم و کینه، سخن دنیا را می‌شنود اگر بگویند کسی ثروتمند شد، دیری نگذرد که گویند تهیدست گردید و اگر به بودنش شادمان شوند با مرگش اندوهگین گردند این حال دنیاى آنان است و هنوز «روزى که در آن متحیر و سرگردان خواهند بود» فرا نرسیده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (703, 3, 368, 'فلسفه کیفر و پاداش', 'خداوند سبحان پاداش را بر اطاعت خود نهاده است و کیفر را برابر معصیت مقرر داشته است تا بندگان خود را از عذابش بره‌اند و آن‌ها را به بهشت خویش روانه دارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (704, 3, 369, 'خبر از مسخ ارزش‌ها', 'بر مردم روزگارى خواهد آمد که از قرآن جز نشانی نماند واز اسلام جز نامی نماند مساجدشان در آن روزگار، از نظر ساختمان آباد است و از هدایت ویران است مسجدیان و مسجدسازان بدترین مردم روی زمین هستند فتنه از آنان برمی‌خیزد و خطاها در آن‌ها لانه می‌کند هر آن که از گناه دوری کند بدان بازش گردانند و هر کس که از آن دوری گزید و عقب ماند او را به بسوی آن کشانند\r\nخداوند متعال می‌گوید\r\nبه خودم سوگند، بر آنان فتنه‌ای بگمارم که انسان شکیبا در حیرت فرو ماند و خداوند چنین کرده است از خداوند می‌خواهیم که از لغزش و غفلت ما درگذرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (705, 3, 370, 'هدفدارى انسان و ضرورت تقوا', 'گویند کمتر اتفاق می‌افتاد که امام بر منبر نشیند و پیش از اداى سخن، مردم را چنین اندرزى ندهد: مردم! از خدا بترسید انسان عبث آفریده نشده است تا مشغول بازی شود و رها نشده است تا خود را سرگرم کارهای بی ارزش کند و دنیایی را که خود را برای او آراسته است جایگزین آخرتی نشود که آن را زشت می‌انگارند و آن مغروری که در دنیا به پیروزی دست یافته است بلند همت تر از آن کسى نیست که به کمترین سهم از آخرت دست پیدا کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (706, 3, 371, 'ارزش‌هاى والاى اخلاقى', 'هیچ شرافتی برتر از اسلام نیست و هیچ عزتی، بزرگوارتر از تقوی نیست و هیچ دژی، نیکوتر از پارسایی نیست هیچ شفیعی، پیروزمندتر از توبه نیست و هیچ گنجی، بی نیازکننده‌تر از قناعت نیست و هیچ ثروتی، برای از بین بردن تهیدستی بهتر نیست از رضایت داشتن به روزی آن کس که به مقدار نیاز اکتفا کند به آسایش و راحتى دست یافته است و در می‌دان فراغ و وسیع آرامش قرار گرفته است و دنیا پرستی کلید دشواری‌ها است و نشستن بر مرکب رنج و گرفتاری است و حرص و کبر و حسد، انگیزه‌های آدمی برای ورود در گناهان هستند و آزار دیگران، مجموعه همه‌ی بدیهاست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (707, 3, 372, 'عوامل استحکام دین و دنیا', 'امام به جابر بن عبد الله انصارى فرمود: ای جابر، دین و دنیا بر چهار چیز استوار است عالمی که دانش خود را به کار می‌گیرد و جاهلی که از آموختن دانش عقب نمی‌نشیند و بخشنده‌ای که در کار نیک بخل نورزد و فقیری که آخرت خود به دنیا نمی‌فروشد اگر عالم، دانش خود را ضایع گذارد جاهل از آموزش باز ایستد و هر گاه ثروتمند در کار خیر بخل ورزد فقیر آخرتش را به دنیا می‌فروشد ای جابر، آن که نعمت‌های خدا بر او افزون باشد نیازهای مردم به او افزون می‌گردد هرکس که در نعمتى که خدا به او ارزانى داشته به گونه‌ای که بر او واجب است عمل کند خداوند به نعمت‌های او دوام و بقا می‌بخشد و اگر کسی حقوق واجب الهی در نعمت‌ها را نپردازد خداوند آن را به زوال و نابودی می‌کشاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (708, 3, 373, 'مراحل امر به معروف و نهى از منکر', 'ابن جریر طبرى در تاریخ خود آورده که عبد الرحمن بن ابى لیلى فقیه همان کسى که براى مبارزه با حجاج به کمک ابن اشعث بر‌خاست در سخنانى که براى تشویق مردم بر جهاد ایراد کرد چنین گفته: من از على شنیدم که خداوند درجاتش را در میان صالحان بالا ببرد و ثواب شهیدان و صدیقان را به او عنایت کند -در روزى که با شامیان روبرو شدیم که می‌فرمود: ای مومنان، هر کس ظلم و یا ستمی را ببیند و یا شاهد دعوت به منکری باشد پس آن را در دلش انکار کند به سلامت جسته است و از محاسبه به دور مانده است و آن کس که با زبان خود باطل را انکار کرد، اجر برده است و از اولی بهتر است و آنکه انکار خود را با شمشیر اعلام دارد تا در جامعه کلام خدا در اوج نشیند و کلام ظالمان پست ترین گردد این اوست که راه هدایت را یافته است و بر جاده‌ی هدایت ایستاده است و در دل خود چراغ یقین را روشن کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (709, 3, 374, 'مراحل امر به معروف و نهى از منکر', 'در سخن دیگرى از آنحضرت که در همین معنى است چنین آمده: در میان آنان، کسی است که منکر را ناپسند می‌شمارد با دست، زبان و قلبش چنین کسی کامل کننده‌ی خصلت‌های نیک است و از آنان کسی است که منکر را با زبان و دل خود انکار می‌کند اما دست به کاری نمی‌برد چنین کس دو خصلت از خصلت‌هاى نیک را گرفته و دیگری را تباه ساخته است و از آنان کسی است که منکر را با قلب انکار می‌کنند و با دست و زبان خویش اقدامی ندارند او همان است که دو خصلت را که شریف تر است تباه نموده، و یک خصلت را گرفته است و بعضی دیگرنه با زبان و نه با قلب و نه با دست با منکرات مبارزه نمی‌کنند چنین کس مرده‌ای میان زندگان است تمام اعمال نیکو و حتی جهاد در راه خدا برابر امر به معروف و نهی از منکر همچون قطره‌ای در مقابل یک دریاى پهناور است امر بمعروف و نهى از منکر نه مرک کسى را نزدیک می‌کنند و نه از روزى کسى می‌کاهند و اما از همه اینها مهم‌تر سخنى است که براى دفاع از عدالت در برابر پیشواى ستمگرى گفته می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (710, 3, 375, 'مراحل جهاد', 'ابو جحیفه می‌گوید: از امیر مؤمنان علیه السلام شنیدم می‌فرمود: اولین مرحله از جهاد که از آن باز می‌مانید جهاد با دستتان است سپس جهاد با زبان، و آنگاه جهاد با قلب‌هایتان و آن که با دل خود به معروف اعتراف نکند و منکر را انکار ننماید، انسان وارونه‌ای (مقلوبی) است بالای تن او (سرش) در پایین قرار گرفته است و پایین او در قسمت بالا نشسته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (711, 3, 376, 'سر انجام حق و باطل', 'حق سنگین است اما گوارا و باطل سبک است و ناگوار.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (712, 3, 377, 'خوف و رجاء', 'از عذاب خدا ایمن مباش بر بهترین افراد این امت زیرا که خداوند می‌فرماید: «از مجازات الهى هیچکس جز زیانکاران احساس امنیت نمی‌کنند» (سوره اعراف، آیه ۹۰) و بر بدترین این امت از رحمت خداوند مایوس مباش چرا که خداوند می‌فرماید: «از رحمت خداوند جز کافران مایوس نمی شوند» (سوره یوسف، آیه ۸۸.).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (713, 3, 378, 'آثار شوم بخل ورزى', 'بخل، جامع تمامی عیب‌ها است و آن مهاری است که انسان را بسوى هر بدى می‌کشاند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (714, 3, 379, 'اقسام روزى و پرهیز از حرص زدن', 'اى فرزند آدم! رزق دو نوع است روزیی که تو می‌طلبی و روزیی که او تو را می‌جوبد که اگر به دنبال آن نروى به سراغ تو میاید پس غم یک سال (آینده‌ات) را بر غم امروزت بار مکن غم همان روز، همان روز را بس است اگر سال آینده در شمار عمر تو باشد خداى بزرگ در هر روز که می‌آید، آنچه قسمت تو ساخته به تو عطا خواهد کرد و اگر آن سال از عمر تو نباشد پس چرا باید اندوه چیزى را بخورى که از آن تو نیست؟ و بدان که کسی بر روزی مقدر تو سبقت نمی‌تواند گرفت و کسی در ربودن آن بر تو چیره نخواهد شد و آن چه که بر تو مقدر شده است هرگز تاخیر نخواهد کرد. \r\nسید رضی می‌گوید: \r\nاین سخن در همین باب در گذشته (تحت شماره ۲۶۷) آمد ولى چون در اینجا واضح تر و مشروح‌تر بود آن را بر اساس روشى که در آغاز کتاب تذکر دادیم آوردیم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (715, 3, 380, 'ضرورت یاد مرگ', 'بسا کسی که در آغاز روز بود و به شامگاهش نرسید بسا کسی که در آغاز شب بر او غبطه می‌خوردند اما در پایان شب عزاداران به سوکش نشسته بودند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (716, 3, 381, 'ضرورت راز دارى و کنترل زبان', 'سخن در اسارت توست تا آن را بر زبان نیاورده‌ای چون آن را به زبان آوردی تو در اسارت آن می‌روی پس زبانت را همانطور که طلا و نقره ات را در خزانه می‌کنی حفظ کن زیرا چه بسا سخنی که نعمتی را از آدمی بستاند و مصیبتی را بر او فرود آرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (717, 3, 382, 'ارزش سکوت', 'آنچه را نمی‌دانی نگو بلکه همه آنچه می‌دانی را نگو زیرا خداوند بر اعضای بدنت وظایفی را واجب گردانیده است که روز قیامت با آن بر تو حجت می‌آورد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (718, 3, 383, 'تلاش در اطاعت و بندگى', 'بترس که خداوند تو را به هنگام گناهان ببیند و در طاعت خویش نیابد که از زیان کاران می‌گردی و چون قوی گشتی در طاعت خدا قوی باش و اگر ضعیف گشتی در نافرمانی خداوند ضعیف باش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (719, 3, 384, 'راه چگونه زیستن', 'تکیه و اعتماد به دنیا با آنچه از آن می‌بینی از نادانی است و کوتاهی در اعمال نیک اگر به پاداش آن اطمینان یافتی، زیان‌کاری است و اطمینان به کسی پیش از آزمودن، از عجز و ناتوانی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (720, 3, 385, 'دنیا شناسى', 'از خواری دنیا نزد خداوند همین بس که جز در دنیا، معصیت خدا کرده نمی‌شود و جز با رها کردن دنیا به پاداش الهی نتوان رسید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (721, 3, 386, 'ضرورت استقامت', 'هر کسی چیزی خواهد به آن و یا پاره ای از آن می‌رسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (722, 3, 387, 'شناخت خوبى‌ها و بدى‌ها', 'خیری که پس از آن آتش باشد، خیر نیست و شری که پس از آن بهشت باشد، شر نیست هر نعمتی در برابر بهشت حقیر و کوچک است و هر بلا و رنجى در برابر جهنم عافیت و تندرستى است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (723, 3, 388, 'روش شناخت سلامت', 'بدانید که از جمله بلاها فقر است و دردناک‌تر از فقر بیماری بدن است و دردناک‌تراز بیماری بدن بیماری دل است بدانید که از نعمت‌های الهی، ثروت است و بهتر از ثروت، سلامت بدن است و بهتر از سلامت بدن، تقوای دل است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (724, 3, 389, 'ضرورت عمل گرایى', 'آن که عملش او را عقب اندازد بزرگی خاندانش، او را به پیش نخواهد رساند و در روایت دیگری است: آن که ارزش‌های خویش را از دست بدهد بزرگی خاندانش، او را سودی نبخشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (725, 3, 390, 'برنامه ریزى صحیح در زندگى', 'مومن باید شبانه‌روز خود را به سه قسمت تقسیم کند زمانی را با خدای خود مناجات می‌کند و زمانی را برای تامین هزینه زندگیش به کار گیرد و زمانی را برای بهره‌گیری از لذت‌های حلال و دلنشین اختصاص دهد و بر عاقل شایسته نیست که جز در ۳ مورد بیرون رود در ترمیم معاش برداشتن گامی برای معاد یا لذتی که حرام نباشد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (726, 3, 391, 'ضرورت ترک حرام و غفلت زدگى', 'در دنیا پرهیزگارى پیشه کن تا خدا نیز دیده تو را به عیب‌هاى آن بینا سازد در غفلت مباش که از تو غافل نیستند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (727, 3, 392, 'راه شناخت انسان‌ها', 'سخن بگویید تا شناخته شوید که انسان زیر زبان خود پنهان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (728, 3, 393, 'روش برخورد با دنیا', 'آنچه از دنیا نزد تو آید بگیر و از آنچه به تو پشت کند روى برگردان و اگر چنین نکردی، اندازه نگه دار.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (729, 3, 394, 'ارزش سخن', 'بسا سخنی که از حمله‌ای نافذتر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (730, 3, 395, 'ارزش قناعت', 'هر چه بدان بسنده توان کرد، همان کافى است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (731, 3, 396, 'راه خوب زیستن', 'مرگ، آری و تن دادن به پستی، هرگز به کم ساختن، آری و دست نیاز به سوی مردم، هرگز آن را که نصیب به آسانى به دست نیاید با کوشش نیز به آن دست نیابد دنیا دو روز است: روزی به سود تو و روزی به ضرر تو اگر به سود تو بود تکبر نورز و اگر به زیان تو بود شکیبایی کن.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (732, 3, 397, 'عطر خوب', 'نیکو بویی است مشک حملش سبک و عطرش بسیار خوشبو!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (733, 3, 398, 'ترک غرور و یاد مرگ', 'فخر فروشی را کنار بگذار تکبر را رها کن به یاد مرگ باش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (734, 3, 399, 'حقوق متقابل پدر و فرزند', 'فرزند بر پدر خود حقی دارد و پدر بر فرزند حقی دارد حق پدر بر فرزند آن است که او را در هر چیزی اطاعت کند مگر در معصیت خداوند سبحان و حق فرزند بر پدر آن است که نام نیکش نهد و ادبش نیکو آموزد و قرآنش تعلیم نماید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (735, 3, 400, 'شناخت واقعیت‌ها و خرافات', 'چشم زخم، راست است و افسون راست است و جادوگری وجود دارد و فال نیک درست است و فال بد زدن راست نیست و سرایت (بعضی از) بیماریها از یکی به دیگری صحیح نیست بوی خوش سبب شادی است و عسل درمان کننده و نشاط‌آور است و سوارکاری و نگاه کردن به سبزه نشاط آور است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (736, 3, 401, 'ضرورت هماهنگى با مردم', 'هماهنگی با اخلاق مردم ایمن ماندن از دشمنی و کینه‌های آنان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (737, 3, 402, 'ضرورت موقعیت شناسى', 'امام به یکى از افراد که در حضورش سخن نامناسب و کوچکى گفت فرمود: پر در نیاورده پرواز کردی و به خردسالی آواز بزرگان سردادی. \r\nسید رضی می‌گوید: \r\nمنظور از «شکیر» در اینجا نخستین پرهائى است که بر بال پرنده می‌روید پیش از آنکه قوى و محکم شود و «سقب» شتر نابالغ است، و شتر معمولا پیش از رسیدن به بلوغ، آن فریاد مخصوص را سر نمی‌دهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (738, 3, 403, 'پرهیز از کارهاى گوناگون', 'کسى که به کارهاى مختلف بپردازد نقشه‌ها و پیش بینی‌هایش به جائى نمی‌رسد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (739, 3, 404, 'تفسیر لا حول و لا قوة الا بالله', 'وقتی از معنی عبارت زیر از امام پرسیدند، فرمود: لاَ حَوْلَ وَلاَ قُوَةَ إِلاَ باللهِ ما با وجود خدا مالک چیزی نیستیم و مالک نمی‌شویم، مگر آنچه او ما را مالک آن گردانیده است پس هرگاه به ما چیزى عطا کند که او از ما سزاوارتر به آن باشد وظیفه ای بر عهده ما گذاشته است و هرگاه آن را از ما گرفت تکلیف خود را ازما برداشته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (740, 3, 405, 'جاهلان متظاهر به دین', 'امام به عمار پسر یاسر فرمود چون گفتگوى او را با مغیره پسر شعبه شنید عمار! رهایش کن زیرا او از دین چیزی نگرفته، مگر همان که او را به دنیا نزدیک می‌کند و به عمد برای خود شبهه می‌آفریند تا شبهات را بهانه لغزش‌ها و خلافهایش قرار دهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (741, 3, 406, 'اخلاق تهیدستان و توانگران', 'چه نیکوست فروتنی اغنیاء برای فقرا برای بدست آوردن پاداش الهی و از آن نیکوتر گردن‌کشی فقرا در برابر اغنیاء است به سبب توکلى که بر خداى دارند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (742, 3, 407, 'ارزش عقل', 'خداوند عقل را به انسانی نداد مگر اینکه، روزى سبب نجات او گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (743, 3, 408, 'سر انجام حق ستیزى', 'هرکه با حق درافتاد نابود شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (744, 3, 409, 'هماهنگى قلب و چشم', 'قلب، کتاب دیده است. (هر چه دیده بیند در دل نقش شود).', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (745, 3, 410, 'ارزش پرهیزگارى', 'تقوی در راس همه ارزش‌های اخلاقی است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (746, 3, 411, 'احترام به استاد', 'تیزی زبانت را بکار نگیر با کسی که تو را سخن گفتن َآموخته است همچنین بلاغت سخنت را بر ضد کسى که فن سخنورى به تو آموخته صرف مکن!', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (747, 3, 412, 'راه خود سازى', 'برای ادب کردن خود همین کافی است که از آنچه بر دیگران نمی‌پسندی پرهیز کنی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (748, 3, 413, 'راه تحمل مصیبت‌ها', 'هر کس صبوری ورزد آنگونه که آزادگان صبوری می‌ورزند وگر نه مانند جاهلان ناله کند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (749, 3, 414, 'راه تحمل مصیبت‌ها', 'در روایت دیگری است که به اشعث بن قیس در تسلیت فرزندش فرمود: یا همچون مردان بزرگوار شکیبائى کن وگرنه چون چهارپایان بی تابی خواهی کرد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (750, 3, 415, 'دنیا شناسى', 'امام در توصیف دنیا فرموده است: مى‌فریبد و زیان مى‌رساند و مى‌گذرد خداوند متعال آن را پاداشی برای دوستان خود نپسندید و نه حتی جایگاه کیفر دشمنانش قرار نداد مردم دنیا همچون کاروانى هستند که هنوز رحل اقامت نینداخته‌اند قافله‌سالار فریاد می‌زند کوچ کنید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (751, 3, 416, 'روش برخورد با دنیا', 'به فرزندش امام حسن (ع) فرمود: چیزى از دنیا بعد از خود باقى مگذار که تو آن را برای دو کس باقی می‌گذاری یا کسی است که آن را در اطاعت خداوند صرف می‌کند پس به چیزی خوشبخت می‌شود که تو بدبخت شدی و یا کسی است که آن را در معصیت خداوند بکار می‌گیرد پس به چیزی که تو گردآورده بودی بدبخت خواهد شد پس یار او در معصیت خدا بوده‌ای هیچ کدام از این دو، لیاقت ندارند که تو آن‌ها را برخود مقدم دارى. \r\nسید رضی می‌گوید: \r\nاین سخن به گونه دیگرى نیز نقل شده و آن این است که: اما بعد! آنچه از دنیا در دست توست پیش از تو از آن دیگری بود و بعد از تو به دیگران خواهد رسید و جز این نیست که تو ثروت را برای یکی از دو تن جمع می‌کنی یکى آنکه مال تو را در راه طاعت خدا به کار برد پس او به چیزى که تو بدان بدبخت شده اى، خوشبخت می‌گردد یا کسى است که آن را در راه معصیت خدا مصرف می‌کند پس به سبب مالى که براى او گرد آورده اى، بدبخت می‌شود هیچیک از این دو، سزاوار آن نیستند که او را بر خود برگزینى یا بار او را بر دوش کشى پس براى آن‌ها که رفته اند، امید آمرزش داشته باش و براى آنان که مانده اند، امید روزى خداوند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (752, 3, 417, 'شرائط توبه و استغفار', 'امام به کسى که در محضر او استغفرالله گفت، فرمود: مادرت در عزایت بنشیند آیا می‌دانى استغفار یعنى چه؟ استغفار مرتیه‌ی علیین (بلند مرتبگان) است و آن نامی است که دارای شش معنا است نخستین آن معانی: پشیمانی است بر آنچه گذشت دوم: تصمیم به عدم بازگشت به آن گناه سوم: حقوق مردم را به آنان ادا کنی تا خدای خود را در حالی دیدار کنی که چیزی بر عهده تو نباشد چهارم: و چهارم آنکه به هر فریضه‌ای که خداوند بر تو واجب گردانیده و تو آن را ضایع گذاشته‌ای رو کنی پس حق آن را ادا کنی پنجم: روی به گوشت‌هایی که به حرام بر تنت روییده است کنی آن‌ها را با اندوه بر گناه آب کنى تا پوستت به استخوانت بچسبد و گوشت تازه به جای آن بروید ششم: رنج طاعت را به جسم بچشانی همانطور که شیرینی گناه را به او چشاندی پس آنگاه بگویی: استغفرالله.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (753, 3, 418, 'ره آورد حلم', 'بردباری برای انسان همچون قوم و عشیره است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (754, 3, 419, 'مشکلات انسان', 'بیچاره فرزند آدم! اجلش از او پوشیده است بیماریش از او پنهان است کردارش نزد خدا محفوظ است پشه ای او را آزار می‌دهد جرعه ای آب گلوگیرش شده او را می‌کشد و عرق کردنی او را بدبو سازد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (755, 3, 420, 'راه درمان شهوت، و عفو اهانت کننده', 'نقل شده آنحضرت در میان اصحابش نشسته بود زن صاحب جمالى از آنجا عبور کرد پس یاران چشم در او (چون تیری) رها کردند امام فرمود: همانا چشمان این مردان سخت در طلب است و این مایه تحریک و هیجان است هرگاه یکى از شما چشمش به زنى افتد، که او را خوش آید با زن خود همبستر شود که او نیز زنى است همانند آن زن دیگر. یکى از خوارج گفت: خدا این کافر را بکشد چقدر دانا و فقیه است مردم برخاستند تا آن مرد را بکشند امام فرمود: آرام بگیرید جواب دشنام، دشنام است و یا گذشت از گناه.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (756, 3, 421, 'رهآورد عقل', 'آن مقدار از عقلت تو را کفایت می‌کند، که برایت واضح سازد راه گمراهیت را ازراه رستگاریت.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (757, 3, 422, 'ارزش نیکوکارى', 'کار نیک را به جاى آرید و چیزى از آن را حقیر مشمارید که کوچک آن بزرگ است و اندک آن بسیار و کسی از شما نگوید: دیگری در انجام کار نیک از من شایسته‌تر است که به خدا سوگند، همان گونه می‌شود چه نیک و بد را مردمانی است. که هرگاه هر یک از آن دو را واگذارید دیگران بجاى شما انجام می‌دهند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (758, 3, 423, 'ره آورد خودسازى', 'هر کس باطن خود را اصلاح نماید خدا ظاهر او را نیکو گرداند هر کس برای دینش کار کند خداوند کار دنیایش را کفایت می‌کند و هر که رابطه خود را با خدا نیکو گرداند خداوند، رابطه او را با مردم نیکو گرداند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (759, 3, 424, 'ارزش عقل و بردبارى', 'بردباری پرده‌پوش (عیوب آدمی) است و خرد شمشیری بُرنده است پس عیوب اخلاقیت را با بردباری بپوشان و با (شمشیر) عقلت هوای نفست را بکش.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (760, 3, 425, 'مسؤولیت توانگران', 'خداوند را بندگانی است که نعمت‌های خاصی به آنان بخشیده است تا دیگر بندگان از آن بهره گیرند پس خدا آن‌ها را در دستشان ثابت می‌دارد مادام که از این نعمت‌ها بذل می‌کنند و هر گاه از بخشش بازایستند نعمت‌ها را از آن‌ها می‌گیرد و به دست دیگران می‌دهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (761, 3, 426, 'دو چیز ناپایدار', 'شایسته نیست که بنده خدا به دو خصلت اطمینان کند سلامت و ثروت چه بسا هنگامی که او را تندرست می‌بینی بناگاه بیمار شود و هنگامى که او را توانگر می‌بینى، بناگاه، فقیر گردد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (762, 3, 427, 'جایگاه شکوه کردن', 'آن که نیاز خود را با مومنی در میان نهد گویی که با خدا در میان نهاده است و آن که نیاز خود را به کافری برد گویی که از خداوند شکایت کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (763, 3, 428, 'عید واقعی', 'امام در یکى از عیدها فرمود: این عید کسى است که خدا روزه‌اش را پذیرفته است و نماز او را سپاس گفته است و هر روزی که در آن خدا معصیت نشود پس آن روز عید است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (764, 3, 429, 'اندوه سرمایه داران', 'بزرگترین حسرت‌ها در روز قیامت حسرت شخصی است که مالی را از راه حرام گرد آورده است و آن را شخصی به ارث برد و در راه اطاعت خدای سبحان بخشش کرد پس او (وارث) داخل بهشت شود و آن نخستین داخل دوزخ شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (765, 3, 430, 'اندوه زر اندوزى', 'همانا زیانکارترین مردم در تجارت و نومیدترین‌شان در کوشش شان فردی است که تن خود را در طلب مال پیر و کهنه کند و اما تقدیر با خواست او هماهنگ نباشد پس با حسرت تمام از دنیا رود و با بار گناهانش به سرای دیگر درآید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (766, 3, 431, 'اقسام روزى', 'روزی دو گونه است آن که جوینده تو است و آن که تو آن را می‌جویی پس هر کس دنیا را می‌طلبد، مرگ نیز او را می‌طلبد تا او را از دنیا بیرون کند آن که آخرت را می‌جوید، مال دنیا او را می‌جوید تا روزی او را به تمام به او رساند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (767, 3, 432, 'ویژگى‌هاى دوستان خدا', 'دوستان خدا کسانی هستند که در باطن دنیا نظر می‌کنند آن گاه که مردم در ظاهر آن نگاه می‌کنند آنان به آینده آن مشعول می‌شوند آن گاه که مردم به امور زودگذر دنیا می‌پردازند پس آن چه از دنیا را (هواهای نفسانی) که می‌ترسند آنان را بکشد، آن را می‌کشند و آن چه که آنان را به زودی ترک می‌کند، آن را ترک می‌کنند آنچه را دیگران بسیار می‌بینند کم می‌شمارند و رسیدن به آن را همچون از دست دادن آن می‌دانند با آن چه مردم در آشتی هستند، دشمنی می‌ورزند و با آن چه مردم در دشمنی هستند، آشتی هستند به واسطه‌ی آنان کتاب خدا شناخته می‌شود آنان به کتاب خدا آگاه هستند کتاب به وسیله آنان پابرجاست و آنان با تکیه بر کتاب بر می‌خیزند بیش از آنکه بدان امید بسته اند، نمی‌بینند و ازچیزى، جز آنچه باید از آن بترسند، نمی‌ترسند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (768, 3, 433, 'ضرورت توجه به فنا پذیرى لذت‌ها', 'به یاد دارید که لذتها تمام شدنى است و پایان ناگوار آن بر جاى ماندنى است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (769, 3, 434, 'آزمودن انسان‌ها', 'مردم را بیازمای، تا دشمن گردی\r\nسید رضی می‌گوید: \r\nبعضى این عبارت را به رسول الله (ع) نسبت داده‌اند ولى از دلایلى که نسبتش را به على (ع) قوت می‌بخشد مطلبى است که ثعلب از ابن الاعرابى روایت کرده که می‌گوید: مامون می‌گفت که اگر على نگفته بود «اخبر تقله» من می‌گفتم «اقله تخبر» با آن‌هادشمنى کن تا امتحانشان کرده باشى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (770, 3, 435, 'ارزش‌هاى والاى اخلاقى', 'برای خدا (شایسته) نیست که برای بنده ای در شکرگزاری را باز کند و در فزونی نعمت را بربندد در دعا را بر بنده باز کند و در اجابت را ببندد و در توبه را باز گزارد و در آمرزش را بر او ببندد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (771, 3, 436, 'راه شناخت بزرگواران', 'شایسته‌ترین مردم به بزرگواری کسی است که بزرگواران را به او شناسند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (772, 3, 437, 'شناخت عدل و بخشش', 'از امام پرسیدند از این دو کدام برترند: عدالت، یا بخشش؟ امام فرمود: عدالت هر چیز را در جاى خود قرار می‌دهد و بخشش آن را از جاى خود بیرون مى‌ برد و عدل تدبیرکننده ای، بسود همگان است و بخشش گروه خاصی را شامل می‌شود بنا بر این عدالت شریف‌تر و برتر است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (773, 3, 438, 'جهل و دشمنى', 'مردم، دشمن آن چیزی هستند که نمی‌دانند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (774, 3, 439, 'تعریف زهد و پارسایى', 'خداى تعالى می‌فرماید: «تا بر آنچه از دستتان می‌رود اندوهگین نباشید و بدانچه به دستتان می‌آید شادمانى نکنید» سوره حدید - ۲۳ و کسی که بر گذشته افسوس نخوردو بر آینده شادمان نباشد زهد را از هر دو سو در اختیار گرفته است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (775, 3, 440, 'نقش خوابدیدن‌ها در زندگى', 'خواب دیدن‌ها چه بسا تصمیم‌هاى روز را نقش بر آب کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (776, 3, 441, 'تجربه کارى و شناخت مدیران', 'مناصب، می‌دان مسابقه و آزمایش افراد است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (777, 3, 442, 'راه انتخاب شهرها براى زندگى', 'هیچ شهرى براى تو شایسته‌تر از شهرى دیگر نیست بهترین شهر همان است که تو را پذیرا باشد و در آن آسوده باشی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (778, 3, 443, 'ویژگى‌هاى مالک اشتر', 'هنگامى که خبر شهادت «مالک اشتر» رحمه الله بامام رسید فرمود: مالک! چه مالکی؟ به خدا قسم اگر کوه بود کوهی بی‌نظیر بود و اگر سنگ بود، سنگی سخت و محکم بود هیچ رونده‌ای به اوج قله او نمی‌رسید و هیچ پرنده‌ای بر فراز آن پرواز نمی‌کرد. \r\nسید رضی می‌گوید: \r\n«فند» کوه‌هاى منفرد و جدا از دیگر کوه‌ها را گویند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (779, 3, 444, 'ارزش تداوم کار', 'اندکی که پیوسته باشد به از فراوان که ملال افزاید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (780, 3, 445, 'انسان شناسى', 'اگر در کسی خصلتی شگفت دیدید همانند آن را نیز انتظار کشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (781, 3, 446, 'ضرورت پرداخت دیون مردم', 'امام در سخنى که بین او و غالب ابن صعصعة پدر «فرزدق» رد و بدل شده چنین فرمود: شتران فراوانت چه شد؟ گفت: ای امیر مومنان پرداخت حقوق (خدا و مردم) آن را پراکنده کرد امام فرمود: آن پسندیده‌ترین راه‌ها بود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (782, 3, 447, 'ضرورت فقه در تجارت', 'آن کس که بدون دانستن فقه به تجارت پردازد به رباخواری آلوده شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (783, 3, 448, 'راه برخورد با مصیبت‌ها', 'هر کس مصیبت‌هاى کوچک را بزرگ شمارد خداوند او را به مصیبت‌هاى بزرک مبتلا میسازد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (784, 3, 449, 'راه مبارزه با هوا پرستى', 'آن کس که خود را عزیز دارد هوا و هوس را خوار شمارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (785, 3, 450, 'پرهیز از شوخى کردن', 'کسی شوخی بی‌جا نکند مگر آنکه مقدارى از عقل خود را با آن از دست می‌دهد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (786, 3, 451, 'روش برخورد با مردم', 'دوری از کسی که خواهان تو است نشانه کمبود بهره تو است و گرایش تو به کسی که از تو دوری می‌گزیند سبب خواری و ذلت توست.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (787, 3, 452, 'معیار شناخت فقر و غنا', 'دارایی و فقر آن گاه آشکار می‌شود که اعمال ما بر خدا عرضه شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (788, 3, 453, 'علل انحراف زبیر', 'زبیر همواره از ما اهل بیت بود تا اینکه فرزند شومش عبدالله به جوانی رسید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (789, 3, 454, 'راه غرور زدایى', 'فرزند آدم را با فخرفروشی چه کار؟ در آغاز نطفه و در آخر مردار است نه می‌تواند به خود روزى دهد و نه مرک را از خود براند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (790, 3, 455, 'راه شناخت بزرگترین شاعر', 'از امام پرسیدند: برترین شعراى عرب کیست؟ فرمود: شاعران در یک می‌دان اسب نتاخته‌اند تا آنان را غایتی بود و خط پایان شناخته شود و اگر ناگزیر از انتخاب باید بود.. پس.. پادشاه سرگردان (یا گمراه) برترین است. \r\nسید رضی می‌گوید: \r\n. منظور امرء القیس است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (791, 3, 456, 'بهاى جان آدمى', 'آیا آزاد مردى پیدا نمی‌شود که این ته مانده دنیا را به اهلش واگذارد؟ بدانید جان شما بهائى جز بهشت ندارد به کمتر از آن نفروشید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (792, 3, 457, 'تشنگان مال و علم', 'دو گرسنه‌اند که هرگز سیری ندارند جوینده علم و جوینده دنیا.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (793, 3, 458, 'نشانه‌هاى ایمان', 'نشان ایمان این است که راست را برگزینى، هر چند به زیان تو بود بردروغى که سود تو در آن است و گفتارت از علمت افزون نیاید و از خداى بترس هنگامى که سخن از دیگرى می‌گویى.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (794, 3, 459, 'شناخت جایگاه جبر و اختیار', 'تقدیر الهی بر نقشه و حسابگری ما پیروز می‌گردد به گونه‌ای که گاهی آفت و تباهی در تدبیر است. \r\nسید رضی می‌گوید: \r\nاین معنى پیش از این در حکمتی ۱۶ با الفاظى دیگر آمده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (795, 3, 460, 'ارزش صبر و تحمل', 'بردباری و درنگ همزادند آن دو را بلندی همت می‌زاید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (796, 3, 461, 'غیبت نشانه ناتوانى', 'غیبت، نهایت کوشش آدم ناتوان است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (797, 3, 462, 'پرهیز از غرور زدگى در ستایش', 'بسا کسانی که از ستودن دیگران فریفته و گمراه شده اند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (798, 3, 463, 'دنیا براى آخرت است', 'دنیا برای هدف دیگری (جهان دیگر) آفریده شده است وبرای خود خلق نشده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (799, 3, 464, 'سرانجام دردناک بنى امیه', 'بنی امیه را مهلتی است که در آن می‌تازند چون بینشان اختلاف پیدا شود اگر کفتارها به جنگ‌شان برخیزند مغلوبشان سازند. \r\nسید رضی می‌گوید: \r\n«مرود» بر وزن «مفعل» از مصدر «ارواد» است و آن به معنى مهلت دادن و فرصت دادن است و این از فصیح‌ترین و غریب‌ترین سخنان اوست گویا امام (ع) مهلتى را که آن‌ها براى حکومت دارند تشبیه به می‌دان مسابقه‌ای کرده است که در آن براى رسیدن به هدف، اسب می‌تازند که چون به پایان رسند نظم آن‌ها در هم شکسته می‌شود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (800, 3, 465, 'ارزش‌هاى والاى انصار', 'امام در مدح و ستایش انصار فرموده است: آنان به خدا، اسلام را پروراندند آن سان، که کره اسب از شیر باز گرفته را پرورش دهند با دست‌های بخشنده و زبان‌هاى برنده و گویا.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (801, 3, 466, 'دقت در مشاهدات', 'چشم، نشستنگاه را چون رشته سربند است. \r\nسید رضی می‌گوید: \r\nاین از استعارات شگفت انگیز است امام نشستنگاه را به مشک، تشبیه کرده و چشم را به بندى که سر مشک را بوسیله آن می‌بندند که هرگاه بند را رها کنند مشک نمی‌تواند آنچه در آن است نگهدارد. مشهور این است که این سخن از پیامبر صلى الله علیه و آله و سلم است ولى عده‌ای آنرا از امیر مؤمنان نقل کرده‌اند این را «مبرد» در کتاب «مقتضب» در باب اللفظ بالحروف آورده است و ما در کتاب «مجازات آثار النبویه» در باره این استعاره سخن گفته ایم.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (802, 3, 467, 'وصف یکى از رهبران الهى', 'امام در ضمن یکى از سخنانش فرمود: بر آنان فرمانروایی حاکم شد که کارها را بر پا داشت و استقامت ورزید تا دین استوار گردید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (803, 3, 468, 'مسؤولیت سرمایه دارى', 'مردمان را روزگاری بس دشوار فرا می‌رسد که ثروتمند در آن روز اموال خود را سخت نگهدارد در حالى که چنین دستورى به آن‌ها داده نشده است خداوند سبحان میفرماید: \"بخشش میان خود را فراموش مکنید\" سوره بقره -آیه ۲۳۷ در آن روزگاران، اشرار بلندپایه شوند و نیکان خوار گردند و مردم بینوا هستى خود را از روى اضطرار به قیمت کم می‌فروشند در حالى که پیامبر صلى الله علیه و آله و سلم از چنین معامله‌ای نهى کرده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (804, 3, 469, 'پرهیز از افراط و تفریط نسبت به امام', 'دو کس در باره‌ی من هلاک می‌شوند دوستدار افراطی و دروغ‌پردازی که به راستی سخن نگوید. \r\nسید رضی می‌گوید: \r\nاین عبارت همانند سخن دیگرى است که از امام نقل شده است: دو کس در مورد من هلاک گردند دوست غلو کننده و دشمن بهتان زننده.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (805, 3, 470, 'تعریف توحید و عدل', 'از امام درباره توحید و عدل سؤال کردند فرمود: توحید آن است که در باره ی او دچار توهم نشوی و عدل آن است که او را به آنچه سزاوار نیست متهم نکنی.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (806, 3, 471, 'شناخت جایگاه سخن و سکوت', 'در سکوت از سخن حکیمانه سودی نیست چنانکه در گفتار جاهلانه سودی وجود ندارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (807, 3, 472, 'دعاى باران', 'امام در دعائى که براى درخواست باران نموده، چنین فرموده است: خداوندا! ما را با ابرهای رام و آرام سیراب کن نه با ابرهای سرکش و ناآرام. \r\nسید رضی می‌گوید: \r\nاین از کلمات فصیح و شگفت انگیز است زیرا امام تشبیه کرده است ابرهاى پر سر و صدا را که توام با رعد و برق و صاعقه و طوفان است به شتران سر کش که بارهاى خود را می‌افکنند و سواران خویش را بزمین می‌کوبند ولى ابرهاى خالى از این هیاهو را به شتران رام تشبیه فرموده استکه براحتى می‌توان شیر آن‌ها را دوشید و بر آن‌ها سوار شد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (808, 3, 473, 'ارزش رنگ کردن و آرایش', 'به آن حضرت عرض شد: ای امیرالمومنین، چه می‌شد اگر موی خود رنگ می‌کردی؟ امام فرمود: رنگ کردن مو، زینت و آرایش است ولی ما عزاداریم. \r\nسید رضی می‌گوید: \r\nمنظور امام وفات پیامبر اکرم صلى الله علیه و آله و سلم بوده است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (809, 3, 474, 'ارزش عفت و پاکدامنى', 'پاداش مجاهد شهید در راه خدا بیشتر از کسى نیست که بر گناهش توانایى هست، ولى پاکدامنى می‌ورزد نزدیک است که فرد پاکدامن در زمره فرشتگان در آید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (810, 3, 475, 'ارزش قناعت', 'قناعت، ثروتی است که پایان نمی‌یابد. \r\nسید رضی می‌گوید: \r\nبعضى این سخن را از پیامبر صلى الله علیه و آله و سلم نقل کرده اند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (811, 3, 476, 'راه کشور دارى', 'امام این سخن را به زیاد بن ابیه فرمود هنگامیکه او را جانشین «عبد الله بن عباس» در منطقه فارس قرار داد در ضمن یک سخن طولانی او را از افزایش خراج و مالیات نهی کرد عدل را در کار گیر و از زورگویی و ستم دوری گزین که زورگویی مردم را از سرزمین‌شان کوچ می‌دهد و ستم شمشیر را در فرا می‌خواند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (812, 3, 477, 'بزرگترین گناه', 'سخت‌ترین گناهان آن است که گناهکار آن را سبک شمارد.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (813, 3, 478, 'مسؤولیت آگاهان', 'خداوند از جاهلان پیمان نگرفت تا بیاموزند تا اینکه از دانایان پیمان گرفت که جاهلان را بیاموزند.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (814, 3, 479, 'بدترین دوست', 'بدترین دوستان کسانی هستند که برای آنان در زحمت می‌افتی. \r\nسید رضی می‌گوید: \r\nتکلف و تکلیف همواره با مشقت توام است بنابر این دوست پر‌توقع که انسان را به تکلف وامیدارد مایه شر است پس او شر الاخوان محسوب میشود.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (815, 3, 480, 'آفت دوستى', 'هنگامى که مؤمن برادر خود را به خشم می‌آورد او را از خود رانده است. \r\nسید رضی می‌گوید: \r\nحشم» و «احشم» یعنى او را به غضب در آورد و بعضى گفته‌اند یعنى او راشرمنده ساخت و «احتشم» بمعنى طلب چنین حالتى است و مسلما چنین کارى مقدمه جدائى است.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `nahjs` VALUES (816, 5, 7, 'درباره برنامه', 'عنوان: نهج البلاغه فارسی\r\nگردآورنده: سید رضی\r\nمترجم: محمد دشتی\r\nسازنده برنامه: گراکد\r\n\r\nشامل سخنان گهربار حضرت علی (ع) \r\n\r\nبا فرستادن یک صلوات بر محمد و آل محمد جهت تعجیل در فرج آقا صاحب الزمان، می‌توانید از برنامه به صورت رایگان استفاده نمایید.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
