INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, '«بسم الله الرحمن الرحيم»'),
(2, 1, 2, '«الحمد لله» جملة خبرية قصد بها الثناء على الله بمضمونها من أنه تعالى: مالك لجميع الحمد من الخلق أو مستحق لأن يحمدوه، والله علم على المعبود بحق «ربِّ العالمين» أي مالك جميع الخلق من الإنس والجن والملائكة والدواب وغيرهم، وكل منها يُطلق عليه عالم، يقال عالم الإنس وعالم الجن إلى غير ذلك، وغلب في جمعه بالياء والنون أولي العلم على غيرهم، وهو من العلامة لأنه علامة على موجده.'),
(3, 1, 3, '«الرحمن الرحيم» أي ذي الرحمة وهي إرادة الخير لأهله.'),
(4, 1, 4, 'أي الجزاء وهو يوم القيامة، وخص بالذكر لأنه لا ملك ظاهرًا فيه لأحد إلا الله تعالى بدليل «لمن الملك اليوم؟ لله» ومن قرأ مالك فمعناه الأمر كله في يوم القيامة أو هو موصوف بذلك دائمًا «كغافر الذنب» فصح وقوعه صفة لمعرفة.'),
(5, 1, 5, '«إيَّاك نعبد وإياك نستعين» أي نخصك بالعبادة من توحيد وغيره ونطلب المعونة على العبادة وغيرها.'),
(6, 1, 6, '«اهدنا الصراط المستقيم» أي أرشدنا إليه، ويبدَل منه:'),
(7, 1, 7, '«صراط الَّذين أنعمت عليهم» بالهداية ويبدل من الذين بصلته «غير المغضوب عليهم» وهم اليهود «ولا» وغير «الضالِّين» وهم النصارى ونكتة البدل إفادة أن المهتدين ليسوا يهوداً ولا نصارى. والله أعلم بالصواب، وإليه المرجع والمآب، وصلى الله على سيدنا محمد وعلى اَله وصحبه وسلم تسليما كثيراً دائما أبداً، وحسبنا الله ونعم الوكيل، ولا حول ولا قوة إلا بالله العلي العظيم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, '«الم» الله أعلم بمراده بذلك.'),
(9, 2, 2, '«ذلك» أي هذا «الكتاب» الذي يقرؤه محمد «لا ريب» لا شك «فيه» أنه من عند الله وجملة النفي خبر مبتدؤه ذلك والإشارة به للتعظيم «هدىً» خبر ثان أي هاد «للمتقين» الصائرين إلى التقوى بامتثال الأوامر واجتناب النواهي لاتقائهم بذلك النار.'),
(10, 2, 3, '«الذين يؤمنون» يصدِّقون «بالغيب» بما غاب عنهم من البعث والجنة والنار «ويقيمون الصلاة» أي يأتون بها بحقوقها «ومما رزقناهم» أعطيناهم «ينفقون» في طاعة الله.'),
(11, 2, 4, '«والذين يؤمنون بما أنزل إليك» أي القراَن «وما أنزل من قبلك» أي التوراة والإنجيل وغيرهما «وبالآخرة هم يوقنون» يعلمون.'),
(12, 2, 5, '«أولئك» الموصوفون بما ذكر «على هدىّ من ربِّهم وأولئك هم المفلحون» الفائزون بالجنة الناجون من النار.'),
(13, 2, 6, '«إن الذين كفروا» كأبي جهل وأبي لهب ونحوهما «سواء عليهم أأنذرتهم» بتحقيق الهمزتين وإبدال الثانية ألفاً وتسهيلها وإدخال ألف بين المسهلة والأخرى وتركه «أم لم تنذرهم لا يؤمنون» لعلم الله منهم ذلك فلا تطمع في إيمانهم، والإنذار إعلام مع تخويف.'),
(14, 2, 7, '«ختم الله على قلوبهم» طبع عليها واستوثق فلا يدخلها خير «وعلى سمعهم» أي مواضعه فلا ينتفعون بما يسمعونه من الحق «وعلى أبصارهم غشاوة» غطاء فلا يبصرون الحق «ولهم عذاب عظيم» قوي دائم.'),
(15, 2, 8, 'ونزل في المنافقين: «ومن الناس من يقول آمنا بالله وباليوم الآخر» أي يوم القيامة لأنه آخر الأيام «وما هم بمؤمنين» روعي فيه معنى من، وفي ضمير يقول لفظها.'),
(16, 2, 9, '«يخادعون الله والذين آمنوا» بإظهار خلاف ما أبطنوه من الكفر ليدفعوا عنهم أحكامه الدنيوية «وما يخدعون إلا أنفسهم» لأن وبال خداعهم راجع إليهم فيفتضحون في الدنيا بإطلاع الله نبيه على ما أبطنوه ويعاقبون في الآخرة «وما يشعرون» يعلمون أن خداعهم لأنفسهم، والمخادعة هنا من واحد كعاقبت اللص وذكر الله فيها تحسين، وفي قراءة وما يخدعون.'),
(17, 2, 10, '«في قلوبهم مرض» شك ونفاق فهو يمرض قلوبهم أي يضعفها «فزادهم الله مرضاً» بما أنزله من القرآن لكفرهم به «ولهم عذاب أليم» مؤلم «بما كانوا يُكذّبوِن» بالتشديد أي: نبي الله، وبالتخفيف أي قولهم آمنا.'),
(18, 2, 11, '«وإذا قيل لهم» أي لهؤلاء «لا تفسدوا في الأرض» بالكفر والتعويق عن الإيمان. «قالوا إنما نحن مصلحون» وليس ما نحن فيه بفساد. قال الله تعالى رداً عليهم.'),
(19, 2, 12, '«ألا» للتنبيه «إنهم هم المفسدون ولكن لا يشعرون» بذلك.'),
(20, 2, 13, '«وإذا قيل لهم آمنوا كما آمن الناس» أصحاب النبي «قالوا أنؤمن كما آمن السفهاء» الجهال أي لا نفعل كفعلهم. قال تعالى ردا َعليهم: «ألا إنهم هم السفهاء ولكن لا يعلمون» ذلك.'),
(21, 2, 14, '«وإذا لقوا» أصله لقيوا حذفت الضمة للاستثقال ثم الياء لالتقائها ساكنة مع الواو «الذين آمنوا قالوا آمنا وإذا خلوا» منهم ورجعوا «إلى شياطينهم» رؤسائهم «قالوا إنا معكم» في الدين «إِنَّما نحن مستهزئون» بهم بإظهار الإيمان.'),
(22, 2, 15, '«الله يستهزئ بهم» يجازيهم باستهزائهم «ويمدهم» يُمهلهم «في طغيانهم» بتجاوزهم الحد بالكفر «يعمهون» يترددون تحيراً حال.'),
(23, 2, 16, '«أولئك الذين اشتروا الضلالة بالهدى» أي استبدلوها به «فما ربحت تجارتهم» أي ما ربحوا فيها بل خسروا لمصيرهم إلي النار المؤبدة عليهم «وما كانوا مهتدين» فيما فعلوا.'),
(24, 2, 17, '«مثلهم» صفتهم في نفاقهم «كمثل الذي استوقد» أوقد «ناراً» في ظلمة «فلما أضاءت» أنارت «ما حوله» فأبصر واستدفأ وأمن مما يخافه «ذهب الله بنورهم» أطفأه وجُمع الضمير مراعاة لمعنى الذي «وتركهم في ظلمات لا يبصرون» ما حولهم متحيرين عن الطريق خائفين فكذلك هؤلاء أمِنوا بإظهار كلمة الإيمان فإذا ماتوا جاءهم الخوف والعذاب.'),
(25, 2, 18, 'هم «صمٌّ» عن الحق فلا يسمعونه سماع قبول «بكم» خرس عن الخير فلا يقولونه «عميٌ» عن طريق الهدى فلا يرونه «فهم لا يرجعون» عن الضلالة.'),
(26, 2, 19, '«أو» مثلهم «كصيِّب» أي كأصحاب مطر وأصله صيوب من صاب يصوب أي ينزل «من السماء» السحاب «فيه» أي السحاب «ظلمات» متكاثفة «ورعد» هو الملك الموكَّل به وقيل صوته «وبرق» لمعان صوته الذي يزجره به «يجعلون» أي أصحاب الصيِّب «أصابعهم» أي أناملها «في آذانهم من» أجل «الصواعق» شدة صوت الرعد لئلا يسمعوها «حذر» خوف «الموت» من سماعها. كذلك هؤلاء: إذا نزل القرآن وفيه ذكر الكفر المشبه بالظلمات والوعيد عليه المشبه بالرعد والحجج البينة المشبهة بالبرق، يسدون آذانهم لئلا يسمعوه فيميلوا إلى الإيمان وترك دينهم وهو عندهم موت «والله محيط بالكافرين» علماً وقدرة فلا يفوتونه.'),
(27, 2, 20, '«يكاد» يقرب «البرق يخطف أبصارهم» بأخذها بسرعة «كلما أضاء لهم مشوا فيه» أي في ضوئه «وإذا أظلم عليهم قاموا» وقفوا، تمثيل لإزعاج ما في القرآن من الحجج قلوبهم وتصديقهم لما سمعوا فيه مما يحبون ووقوفهم عما يكرهون. «ولو شاء الله لذهب بسمعهم» بمعنى أسماعهم «وأبصارهم» الظاهرة كما ذهب بالباطنة «إن الله على كل شيء» شاءه «قدير» ومنه إذهاب ما ذكر.'),
(28, 2, 21, '«يا أيُّها الناس» أي أهل مكة «اعبدوا» وحِّدوا «ربَّكم الذي خلقكم» أنشأكم ولم تكونوا شيئاً «و» خلق «الذين من قبلكم لعلكم تتقون» بعبادته عقابَه، ولعل: في الأصل للترجي، وفي كلامه تعالى للتحقيق.'),
(29, 2, 22, '«الذي جعل» خلق «لكم الأرض فراشا» حال بساطا يفترش لا غاية في الصلابة أو الليونة فلا يمكن الاستقرار عليها «والسماء بناءً» سقفاً «وأنزل من السماء ماءً فأخرج به من» أنواع «الثمرات رزقاً لكم» تأكلونه وتعلفون دوابكم «فلا تجعلوا لله أنداداً» شركاء في العبادة «وأنتم تعلمون» أنه الخالق ولا تخلقون، ولا يكون إلهاً إلا من يخلق.'),
(30, 2, 23, '«وإن كنتم في ريب» شك «مما نزلنا على عبدنا» محمد من القرآن أنه من عند الله «فأتوا بسورة من مثله» أي المنزل ومن للبيان أي هي مثله في البلاغة وحسن النظم والإخبار عن الغيب. والسورة قطعة لها أول وآخر أقلها ثلاث آيات «وادعوا شهداءكم" آلهتكم التي تعبدونها» من دون الله» أي غيره لتعينكم «إن كنتم صادقين» في أن محمدا قاله من عند نفسه فافعلوا ذلك فإنكم عربيون فصحاء مثله ولما عجزوا عن ذلك قال تعالى:'),
(31, 2, 24, '«فإن لم تفعلوا» ما ذكر لعجزكم «ولن تفعلوا» ذلك أبداً لظهور إعجازه- اعتراض «فاتقوا» بالإيمان بالله وانه ليس من كلام البشر «النارَ التي وقودها الناس» الكفار «والحجارة» كأصنامهم منها، يعني أنها مفرطة الحرارة تتقد بما ذكر، لا كنار الدنيا تتقد بالحطب ونحوه «أعدَّت» هُيئت «للكافرين» يعذَّبون بها، جملة مستأنفة أو حال لازمة.'),
(32, 2, 25, '«وَبَشِّر» أخبر «الذين آمنوا» صَّدقوا بالله «وعملوا الصالحات» من الفروض والنوافل «أن» أي بأن «لهم جناتِ» حدائق ذات أشجار ومساكن «تجري من تحتها» أي تحت أشجارها وقصورها «الأنهار» أي المياه فيها، والنهر الموضع الذي يجري فيه الماء لأن الماء ينهره أي يحفره وإسناد الجري إليه مجاز «كلما رزقوا منها» أطعموا من تلك الجنات. «من ثمرة رزقاً قالوا هذا الذي» أي مثل ما «رزقنا من قبل» أي قبله في الجنة لتشابه ثمارها بقرينة «وأُتوا به» أي جيئوا بالرزق «متشابهاً» يشبه بعضه بعضا لونا ويختلف طعما «ولهم فيها أزواج» من الحور وغيرها «مطهَّرة» من الحيض وكل قذر «وهم فيها خالدون» ماكثون أبداً لا يفنون ولا يخرجون. ونزل رداً لقول اليهود لما ضرب الله المثل بالذباب في قوله: (وإن يسلبهم الذباب شيئاً) والعنكبوت في قوله (كمثل العنكبوت) ما أراد الله بذكر هذه الأشياء؟ الخسيسة فأنزل الله.'),
(33, 2, 26, '«إن الله لا يستحيي أن يضرب» يجعل «مثلا» مفعول أول «ما» نكرة موصوفة بما بعدها مفعول ثان أيَّ مثل كان أو زائدة لتأكيد الخسة فما بعدها المفعول الثاني «بعوضة» مفرد البعوض وهو صغار البق «فما فوقها» أي أكبر منها أي لا يترك بيانه لما فيه من الحكم «فأما الذين آمنوا فيعلمون أنه» أي المثل «الحق» الثابت الواقع موقعه «من ربِّهم وأما الذين كفروا فيقولون ماذا أراد الله بهذا مثلاً» تمييز أي بهذا المثل، وما استفهام إنكار مبتدأ، وذا بمعنى الذي بصلته خبره أي: أيّ فائدة فيه قال الله تعالى في جوابهم «يضل به» أي بهذا المثل «كثيراً» عن الحق لكفرهم به «ويهدي به كثيراً» من المؤمنين لتصديقهم به «وما يضل به إلا الفاسقين» الخارجين عن طاعته.'),
(34, 2, 27, '«الذين» نعت «ينقضون عهد الله» ما عهده إليهم في الكتب من الإيمان بمحمد * «من بعد ميثاقه» توكيده عليهم «ويقطعون ما أمر الله به أن يوُصل» من الإيمان بالنبي والرحم وغير ذلك وأن بدل من ضمير به «ويفسدون في الأرض» بالمعاصي والتعويق عن الإيمان «أولئك» الموصوفون بما ذكر «هم الخاسرون» لمصيرهم إلى النار المؤبدة عليهم.'),
(35, 2, 28, '«كيف تكفرون» يا أهل مكة «بالله و» قد «كنتم أمواتاً» نطفاً في الأصلاب «فأحياكم» في الأرحام والدنيا بنفخ الروح فيكم، والاستفهام للتعجيب من كفرهم مع قيام البرهان أو للتوبيْخ «ثم يميتكم» عند انتهاء آجالكم «ثم يحييكم» بالبعث «ثم إليه ترجعون» تردون بعد البعث فيجازيكم بأعمالكم. وقال دليلا على البعث لما أنكروه.'),
(36, 2, 29, '«هو الذي خلق لكم ما في الأرض» أي الأرض وما فيها «جميعاً» لتنتفعوا به وتعتبروا «ثم استوى» بعد خلق الأرض أي قصد «إلى السماء فسواهن» الضمير يرجع إلى السماء لأنها في معنى الجمع الآيلة إليه: أي صيَّرها كما في آية أخرى (فقضاهن) «سبع سماوات وهو بكل شيء عليم» مجملا ومفصلا أفلا تعتبرون أن القادر على خلق ذلك ابتداءً وهو أعظم منكم قادر على إعادتكم.'),
(37, 2, 30, '«و» اذكر يا محمد «إذ قال ربُّك للملائكة إني جاعل في الأرض خليفة» يخلفني في تنفيذ أحكامي فيها وهو آدم «قالوا أتجعل فيها من يفسد فيها» بالمعاصي «ويسفك الدماء» يريقها بالقتل كما فعل بنو الجان وكانوا فيها فلما أفسدوا أرسل الله عليهم الملائكة فطردوهم إلى الجزائر والجبال «ونحن نسبِّح» متلبسين «بحمدك» أي نقول سبحان الله وبحمده «ونقِّدس لك» ننزهك عمالا يليق بك فاللام زائدة والجملة حال أي فنحن أحق بالاستخلاف «قال» تعالى «إني أعلم ما لا تعلمون» من المصلحة في استخلاف آدم، وأن ذريته فيهم المطيع والعاصي فيظهر العدل بينهم فقالوا لن يخلق ربنا خلقاً أكرم عليه منا ولا أعلم لسبقنا له ورؤيتنا ما لم يره فخلق الله تعالى آدم من أديم الأرض أي وجهها، بأن قبض منها قبضة من جميع ألوانها وعجنت بالمياه المختلفة وسوَّاهُ ونفخ فيه الروح فصار حيواناً حسَّاساً بعد أن كان جماداً.'),
(38, 2, 31, '«وعلَّم آدم الأسماء» أي أسماء المسميات «كلها» بأن ألقى في قلبه علمها «ثم عرضهم» أي المسميات وفيه تغليب العقلاء «على الملائكة فقال» لهم تبكيتاً «أنبئوني» أخبروني «بأسماء هؤلاء» المسميات «إن كنتم صادقين» في أني لا أخلق أعلم منكم أو أنكم أحق بالخلافة، وجواب الشرط دل عليه ما قبله.'),
(39, 2, 32, '«قالوا سبحانك» تنزيهاً لك عن الاعتراض عليك «لا علم لنا إلا ما علمَّتنا» إياه «إنَّك أنت» تأكيد للكاف «العليم الحكيم» الذي لا يخرج شيء عن علمه وحكمته.'),
(40, 2, 33, '«قال» تعالى «يا آدم أنبئهم» أي الملائكة «بأسمائهم» المسميات فسمى كل شيء باسمه وذكر حكمته التي خلق لها «فلما أنبأهم بأسمائهم قال» تعالى لهم موبخاً «ألم أقل لكم إنَّي أعلم غيب السماوات والأرض» ما غاب فيهما «وأعلم ما تبدون» ما تظهرون من قولكم أتجعل فيها الخ «وما كنتم تكتمون» تسرون من قولكم لن يخلق الله أكرم عليه منا ولا أعلم.'),
(41, 2, 34, '«و» اذكر «إذ قلنا للملائكة اسجدوا لآدم» سجود تحية بالانحناء «فسجدوا إلا إبليس» هو أبو الجن كان بين الملائكة «أبى» امتنع من السجود «واستكبر» تكبَّر عنه وقال: أنا خير منه «وكان من الكافرين» في علم الله.'),
(42, 2, 35, '«وقلنا يا آدم اسكن أنت» تأكيد للضمير المستتر ليعطف عليه «وزوجك» حواء بالمد وكان خلقها من ضلعه الأيسر «الجنة وكلا منها» أكلاً «رغداً» واسعا لاحجر فيه «حيث شئتما ولا تقربا هذه الشجرة» بالأكل منها وهى الحنطة أو الكرم أو غيرهما «فتكونا» فتصيرا «من الظالمين» العاصين.'),
(43, 2, 36, '«فأزلَّهما الشيطان» إبليس أذهبهما، وفي قراءة ـ فأزالهما ـ نحَّاهما «عنها» أي الجنة بأن قال لهما: هل أدلُّكما على شجرة الخلد وقاسمهما بالله إنه لهما لمن الناصحين فأكلا منها «فأخرجهما مما كانا فيه» من النعيم «وقلنا اهبطوا» إلى الأرض أي أنتما بما اشتملتما عليه من ذريتكما «بعضكم» بعض الذرية «لبعض عدوُّ» من ظلم بعضكم بعضاً «ولكم في الأرض مستقرُّ» موضع قرار «ومتاع» ما تتمتعون به من نباتها «إلى حين» وقت انقضاء آجالكم.'),
(44, 2, 37, '«فتلقى آدمُ من ربِّه كلماتٍ» ألهمه إياها وفي قراءة بنصب آدم ورفع كلمات، أي جاءه وهى (ربَّنا ظلمنا أنفسا) الآية فدعا بها «فتاب عليه» قبل توبته «إنه هو التواب» على عباده «الرحيم» بهم.'),
(45, 2, 38, '«قلنا اهبطوا منها» من الجنة «جميعاً» كرره ليعطف عليه «فإما» فيه إدغام نون إن الشرطية في ما الزائدة «يأتينكم مني هدىً» كتاب ورسول «فمن تبع هداي» فآمن بي وعمل بطاعتي «فلا خوف عليهم ولا هم يحزنون» في الآخرة بأن يدخلوا الجنة.'),
(46, 2, 39, '«والذين كفروا وكذبوا بآياتنا» كتبنا «أولئك أصحاب النار هم فيها خالدون» ماكثون أبداً لا يفنون ولا يخرجون.'),
(47, 2, 40, '«يا بنى إسرائيل» أولاد يعقوب «اذكروا نعمتي التي أنعمت عليكم» أي على آبائكم من الإنجاء من فرعون وفلق البحر وتظليل الغمام وغير ذلك بأن تشكروها بطاعتي «وأوفوا بعهدي» الذي عهدته إليكم من الإيمان بمحمد «أوف بعهدكم» الذي عهدت إليكم من الثواب عليه بدخول الجنة «وإياي فارهبون» خافونِ في ترك الوفاء به دون غيري.'),
(48, 2, 41, '«وآمنوا بما أنزلت» من القرآن «مصدِّقاً لما معكم» من التوراة بموافقته له في التوحيد والنبوة «ولا تكونوا أوَّل كافر به» من أهل الكتاب لأنَّ خلفكم تبع لكم فإثمهم عليكم «ولا تشتروا» تستبدلوا «بآياتي» التي في كتابكم من نعت محمد «ثمناً قليلا» عوضاً يسيرا من الدنيا أي لا تكتموها خوف فوات ما تأخذونه من سفلتكم «وإياي فاتقون» خافون في ذلك دون غيري.'),
(49, 2, 42, '«ولا تلبسوا» تخلطوا «الحق» الذي أنزلت عليكم «بالباطل» الذي تفترونه «و» لا «تكتموا الحق» نعت محمد «وأنتم تعلمون» أنه حق.'),
(50, 2, 43, '«وأقيموا الصلاة وآتوا الزكاة واركعوا مع الراكعين» صلوا مع المصلين محمد وأصحابه، ونزل في علمائهم وكانوا يقولون لأقربائهم المسلمين اثبتوا على دين محمد فإنه الحق.'),
(51, 2, 44, '«أتأمرون الناس بالبر» بالإيمان بمحمد «وتنسون أنفسكم» تتركونها فلا تأمرونها به «وأنتم تتلون الكتاب» التوراة وفيها الوعيد على مخالفة القول العمل «أفلا تعقلون» سوء فعلكم فترجعون، فجملة النسيان محل الاستفهام الإنكاري.'),
(52, 2, 45, '«واستعينوا» اطلبوا المعونة على أموركم «بالصبر» الحبس للنفس على ما تكره «والصلاة» أفردها بالذكر تعظيما لشأنها وفي الحديث (كان صلى الله عليه وسلم إذا حزبه أمر بادر إلى الصلاة) وقيل الخطاب لليهود لما عاقهم عن الإيمان الشره وحب الرياسة فأمروا بالصبر وهو الصوم لأنه يكسر الشهوة والصلاة لأنها تورث الخشوع وتنفي الكبر «وإنها» أي الصلاة «لكبيرة» ثقيلة «إلا على الخاشعين» الساكنين إلى الطاعة.'),
(53, 2, 46, '«الَّذين يظنون» يوقنون «أنهم ملاقو ربِّهم» بالبعث «وأنهم إليه راجعون» في الآخرة فيجازيهم.'),
(54, 2, 47, '«يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم» بالشكر عليها بطاعتي «وأني فضَّلتكم» أي آباءكم «على العالمين» عالمي زمانهم.'),
(55, 2, 48, '«واتقوا» خافوا «يوما لا تجزي» فيه «نفس عن نفسٍ شيئاً» وهو يوم القيامة «ولا تُقبل» بالتاء والياء «منها شفاعة» أي ليس لها شفاعة فتقبل (فما لنا من شافعين) «ولا يؤخذ منها عدل» فداء «ولا هم ينصرون» يمنعون من عذاب الله.'),
(56, 2, 49, '«و» اذكروا «إذ نجيناكم» أي آباءكم، والخطاب به وبما بعده للموجودين في زمن نبينا بما أنعم الله على آبائهم تذكيراً لهم بنعمة الله تعالى ليؤمنوا «من آل فرعون يسومونكم» يذيقونكم «سوء العذاب» أشده والجملة حال من ضمير نجيناكم «يُذبّحون» بيان لما قبله «أبناءكم» المولودين «ويستحيون» يستبقون «نساءكم» لقول بعض الكهنة له إن مولوداً يولد في بني إسرائيل يكون سبباً لذهاب ملكك «وفي ذلكم» العذاب أو الإنجاء «بلاء» ابتلاء أو إنعام «من ربكم عظيمْ».'),
(57, 2, 50, '«و» اذكروا «إذا فرقنا» فلقنا «بكم» بسببكم «البحر» حتى دخلتموه هاربين من عدوكم «فأنجيناكم» من الغرق «وأغرقنا آل فرعون» قومه معه «وأنتم تنظرون» إلى انطباق البحر عليهم'),
(58, 2, 51, '«وإذا واعدنا» بألف ودونها «موسى أربعين ليلة» نعطيه عند انقضائها التوراة لتعلموا بها «ثم اتخذتم العجل» الذي صاغه لكم السامري إلهاً «من بعده» أي بعد ذهابه إلى ميعادنا «وأنتم ظالمون» باتخاذه لوضعكم العبادة في غير محلها.'),
(59, 2, 52, '«ثم عفونا عنكم» محونا ذنوبكم «من بعد ذلك» الاتخاذ «لعلّكم تشكرون» نعمتنا عليكم'),
(60, 2, 53, '«وإذ آتينا موسى الكتاب» التوراة «والفرقان» عطف تفسير، أي الفارق بين الحق والباطل والحلال والحرام «لعلكم تهتدون» به من الضلال.'),
(61, 2, 54, '«وإذ قال موسى لقومه» الذين عبدوا العجل «يا قوم إنكم ظلمتم أنفسكم باتخاذكم العجل» إلهاً «فتوبوا إلى بارئكم» خالقكم من عبادته «فاقتلوا أنفسكم» أي ليقتل البريءُ منكم المجرم «ذلكم» القتل «خير لكم عند بارئكم» فوفقكم لفعل ذلك وأرسل عليكم سحابة سوداء لئلا يبصر بعضكم بعضا فيرحمه حتى قتل منكم نحو سبعين ألفا «فتاب عليكم» قبل توبتكم «أنه هو التواب الرحيم».'),
(62, 2, 55, '«وإذ قلتم» وقد خرجتم مع موسى لتعتذروا إلى الله من عبادة العجل وسمعتم كلامه «يا موسى لن نؤمن لك حتى نرى الله جهرة» عيانا «فأخذتكم الصاعقة» الصيحة فمتم «وأنتم تنظرون» ما حل بكم.'),
(63, 2, 56, '«ثم بعثناكم» أحييناكم «من بعد موتكم لعلكم تشكرون» نعمتنا بذلك.'),
(64, 2, 57, '«وظلَّلنا عليكم الغمام» سترناكم بالسحاب الرقيق من حر الشمس في التيه «وأنزلنا عليكم» فيه «المن والسلوى» هما الترنجبين والطير السماني بتخفيف الميم والقصر، وقلنا: «كلوا من طيبات ما رزقناكم» ولا تدَّخروا، فكفروا النعمة وادخروا فقطع عنهم «وما ظلمونا» بذلك «ولكن كانوا أنفسهم يظلمون» لأن وباله عليهم.'),
(65, 2, 58, '«وإذ قلنا» لهم بعد خروجهم من التيه «ادخلوا هذه القرية» بيت المقدس أو أريحا «فكلوا منها حيث شئتم رغدا» واسعا لاَ حَجْرَ فيه «وادخلوا الباب» أي بابها «سجداً» منحنين «وقولوا» مسألتنا «حطة» أي أن تحط عنا خطايانا «نغفر» وفي قراءة بالياء والتاء مبنياً للمفعول فيهما «لكم خطاياكم وسنزيد المحسنين» بالطاعة ثواباً.'),
(66, 2, 59, '«فبدل الذين ظلموا» منهم «قولا غير الذي قيل لهم» فقالوا: حبة في شعرة ودخلوا يزحفون على أستاههم «فأنزلنا على الذين ظلموا» فيه وضع الظاهر موضع المضمر مبالغة في تقبيح شأنهم «رجزاً» عذاباً طاعوناً «من السماء بما كانوا يفسقون» بسب فسقهم أي خروجهم عن الطاعة فهلك منهم في ساعة سبعون ألفاً أو أقل.'),
(67, 2, 60, '«و» اذكر «إذ استسقى موسى» أي طلب السقيا «لقومه» وقد عطشوا في التيه «فقلنا اضرب بعصاك الحجر» وهو الذي فر بثوبه خفيف مربع كرأس الرجل رخام أو كذان فضربه «فانفجرت» انشقت وسالت «منه اثنتا عشرة عيناً» بعدد الأسباط «قد علم كل أناس» سبط منهم «مشربهم» موضع شربهم فلا يشركهم فيه غيرهم وقلنا لهم «كلوا واشربوا من رزق الله ولا تعثوا في الأرض مفسدين» حال مؤكدة لعاملها من عثى بكسر المثلثة أفسد.'),
(68, 2, 61, '«وإذا قلتم يا موسى لن نصبر على طعام» أي نوع منه «واحد» وهو المن والسلوى «فادع لنا ربَّك يُخرج لنا» شيئاً «مما تنبت الأرض من» للبيان «بقلها وقثائها وفومها» حنطتها «وعدسها وبصلها قال» لهم موسى «أتستبدلون الذي هو أدنى» أخس «بالذي هو خير» أشرف أي أتأخذونه بدله، والهمزة للإنكار فأبوا أن يرجعوا فدعا الله تعالى فقال تعالى «اهبطوا» انزلوا «مصراً» من الأمصار «فإن لكم» فيه «ما سألتم» من النبات «وضُربت» جعلت «عليهم الذلة» الذل والهوان «والمسكنة» أي أثر الفقر من السكون والخزي فهي لازمة لهم، وإن كانوا أغنياء لزوم الدرهم المضروب لسكته «وباءُوا» رجعوا «بغضب من الله ذلك» أي الضرب والغضب «بأنهم» أي بسبب أنهم «كانوا يكفرون بآيات الله ويقتلون النبيين» كزكريا ويحيى «بغير الحق» أي ظلماً «ذلك بما عصوا وكانوا يعتدون» يتجاوزون الحد في المعاصي وكرره للتأكيد.'),
(69, 2, 62, '«إن الذين آمنوا» بالأنبياء من قبل «والذين هادوا» هم اليهود «والنصارى والصابئين» طائفة من اليهود أو النصارى «من آمن» منهم «بالله واليوم الآخر» في زمن نبينا «وعمل صالحاً» بشريعته «فلهم أجرهم» أي ثواب أعمالهم «عند ربهم ولا خوف عليهم ولا هم يحزنون» رُوعي في ضمير آمن وعمل لفظ من وفيما بعده معناها.'),
(70, 2, 63, '«و» اذكر «إذ أخذنا ميثاقكم» عهدكم بالعمل بما في التوراة «و» قد «رفعنا فوقكم الطور» الجبل اقتلعناه من أصله عليكم لما أبيتم قبولها وقلنا «خذوا ما آتيناكم بقوة» بجد واجتهاد «واذكروا ما فيه» بالعمل به «لعلكم تتقون» النار أو المعاصي.'),
(71, 2, 64, '«ثم توليتم» أعرضتم «من بعد ذلك» الميثاق عن الطاعة «فلولا فضل الله عليكم ورحمته» لكم بالتوبة أو تأخير العذاب «لكنتم من الخاسرين» الهالكين.'),
(72, 2, 65, '«ولقد» لام قسم «علمتم» عرفتم «الذين اعتدوا» تجاوزوا الحد «منكم في السبت» بصيد السمك وقد نهيناهم عنه وهم أهل آيلة «فقلنا لهم كونوا قردة خاسئين» مبعدين فكانوا وهلكوا بعد ثلاثة أيام.'),
(73, 2, 66, '«فجعلناها» أي تلك العقوبة «نكالاً» عبرة مانعة من ارتكاب مثل ما عملوا «لما بين يديها وما خلفها» أي للأمم التي في زمانها وبعدها «وموعظة للمتقين» الله وخصوا بالذكر لأنهم المنتفعون بخلاف غيرهم.'),
(74, 2, 67, '«و» اذكر «إذ قال موسى لقومه» وقد قُتل لهم قتيل لا يُدرى قاتله وسألوه أن يدعو الله أن يبينه لهم فدعاه «إن الله يأمركم أن تذبحوا بقرة قالوا أتتخذنا هزواً» مهزوءاً بنا حيث تجيبنا بمثل ذلك «قال أعوذ» أمتنع «بالله أن أكون من الجاهلين» المستهزئين.'),
(75, 2, 68, 'فلما علموا أنه عزم «قالوا ادع لنا ربك يبيّن لنا ما هي» أي ما سنها «قال» موسى «إنه» أي الله «يقول إنها بقرة لا فارضٌ» مسنة «ولا بكرٌ» صغيرة «عوانٌ» نصف «بين ذلك» المذكور من السنين «فافعلوا ما تؤمرون» به من ذبحها.'),
(76, 2, 69, '«قالوا ادع لنا ربك يبين لنا ما لونها قال إنه يقول إنها بقرة صفراء فاقع لونها» شديد الصفرة، «تسر الناظرين» إليها بحسنها أي تعجبهم.'),
(77, 2, 70, '«قالوا ادع لنا ربك يبين لنا ما هي» أسائمة أم عاملة «إن البقر» أي جنسه المنعوت بما ذكر «تشابه علينا» لكثرته فلم نهتد إلى المقصودة «وإنا إن شاء الله لمهتدون» إليها وفي الحديث (لو لم يستثنوا لما بينت لهم لآخر الأبد).'),
(78, 2, 71, '«قال إنه يقول إنها بقرة لا ذلول» غير مذللة بالعمل «تثير الأرض» تقلبها للزراعة والجملة صفة ذلول داخلة في النهي «ولا تسقي الحرث» الأرض المهيأة للزراعة «مسلمة» من العيوب وآثار العمل «لا شية» لون «فيها» غير لونها «قالوا الآن جئت بالحق» نطقت بالبيان التام فطلبوها فوجدوها عند الفتى البار بأمه فاشتروها بملء مسكها ذهبا «فذبحوها وما كادوا يفعلون» لغلاء ثمنها وفي الحديث: (لو ذبحوا أي بقرة كانت لأجزأتهم ولكن شددوا على أنفسهم فشدد الله عليهم).'),
(79, 2, 72, '«وإذا قتلتم نفساً فأدّارأتم» فيه إدغام التاء في الأصل في الدال أي تخاصمتم وتدافعتم «فيها والله مخرج» مظهر «ما كنتم تكتمون» من أمرها وهذا اعتراض وهو أول القصة.'),
(80, 2, 73, '«فقلنا اضربوه» أي القتيل «ببعضها» فضرب بلسانها أو عَجْب ذنبها فحييَ وقال: قتلني فلان وفلان لاِبْنيْ عمه ومات فحرما الميراث وقتلا، قال تعالى: «كذلك» الإحياء «يحيي الله الموتى ويريكم آياته» دلائل قدرته «لعلكم تعقلون» تتدبرون فتعلمون أن القادر على إحياء نفس واحدة قادر على إحياء نفوس كثيرة فتؤمنون.'),
(81, 2, 74, '«ثم قست قلوبكم» أيها اليهود صلبت عن قبول الحق «من بعد ذلك» المذكور من إحياء القتيل وما قبله من الآيات «فهي كالحجارة» في القسوة «أو أشد قسوة» منها «وإن من الحجارة لما يتفجَّر منه الأنهار وإن منها لما يشقق» فيه إدغام التاء في الأصل في الشين «فيخرج منه الماء وإن منها لما يهبط» ينزل من علو إلى أسفل «من خشية الله» وقلوبكم لا تتأثر ولا تلين ولا تخشع «وما الله بغافل عما تعلمون» وإنما يؤخركم لوقتكم وفي قراءة بالتحتانية وفيه التفات عن الخطاب.'),
(82, 2, 75, '«أفتطعمون» أيها المؤمنون «أن يؤمنوا لكم» أي اليهود لكم. «وقد كان فريق» طائفة «منهم» أحبارهم «يسمعون كلام الله» في التوراة «ثم يحرّفونه» يغيرونه «من بعد ما عقلوه» فهموه «وهم يعلمون» أنهم مفترون والهمزة للإنكار أي لا تطمعوا فلهم سابقة بالكفر.'),
(83, 2, 76, '«وإذا لقوا» أي منافقوا اليهود «الذين آمنوا قالوا آمنا» بأن محمداً نبي وهو المبشر به في كتابنا «وإذا خلا» رجع «بعضهم إلى بعض قالوا» أي رؤساؤهم الذين لم ينافقوا لمن نافق «أتحدثونهم» أي المؤمنين «بما فتح الله عليكم» أي عرَّفكم في التوراة من نعت محمد * «ليحاجوكم» ليخاصموكم واللامُ للصيرورة «به عند ربكم» في الآخرة ويقيموا عليكم الحجة في ترك اتِّباعه مع علمكم بصدقه «أفلا تعقلون» أنهم يحاجونكم إذا حدثتموهم فتنتهوا.'),
(84, 2, 77, 'قال تعالى «أو لا يعلمون» الاستفهام للتقرير والواو الداخلة عليها للعطف «أن الله يعلم ما يسرون وما يعلنون» ما يخفون وما يظهرون من ذلك وغيره فيرعَوُوا عن ذلك.'),
(85, 2, 78, '«ومنهم» أي اليهود «أميون» عوام «لا يعلمون الكتاب» التوراة «إلا» لكن «أمانيَّ» أكاذيب تلقَّوْها من رؤسائهم فاعتمدوها «وإن» ما «هم» في جحد نبوة النبي وغيره مما يختلقونه «‌إلا يظنون» ظناً ولا علم لهم.'),
(86, 2, 79, '«فويل» شدة عذاب «للذين يكتبون الكتاب بأيديهم» أي مختلقاً من عندهم «ثم يقولون هذا من عند الله ليشتروا به ثمناً قليلا» من الدنيا وهم اليهود غيَّروا صفة النبي في التوراة وآية الرجم وغيرها وكتبوها على خلاف ما أنزل «فويل لهم مما كتبت أيديهم» من المختلق «وويل لهم مما يكسبون» من الرشا جمع رشوة.'),
(87, 2, 80, '«وقالوا» لما وعدهم النبيُّ النار «لن تمسَّنا» تصيبنا «النار إلا أياماً معدودة» قليلة أربعين يوماً مدة عبادة آبائهم العجل ثم تزول «قل» لهم يا محمد «أتخذتم» حذفت منه همزة الوصل استغناءً بهمزة الاستفهام «عند الله عهداً» ميثاقاً منه بذلك «فلن يُخلف الله عهده» به؟ لا «أم» بل «تقولون على الله ما لا تعلمون».'),
(88, 2, 81, '«بلى» تمسكم وتخلدون فيها «من كسب سيئة» شركاً «وأحاطت به خطيئته» بالإفراد والجمع أي استولت عليه وأحدقت به من كل جانب بأن مات مشركاً «فأولئك أصحاب النار هم فيها خالدون» روعي فيه معنى من.'),
(89, 2, 82, '«والذين آمنوا وعملوا الصالحات أولئك أصحاب الجنة هم فيها خالدون».'),
(90, 2, 83, '«و» اذكر «إذ أخذنا ميثاق بني إسرائيل» في التوراة وقلنا «لا تعبدون» بالتاء والياء «إلا الله» خبر بمعنى النهي، وقرئ: لا تعبدوا «و» أحسنوا «بالوالدين إحساناً» براً «وذي القربى» القرابة عطف على الوالدين «واليتامى والمساكين وقولوا للناس» قولا «حسناً» من الأمر بالمعروف والنهي عن المنكر والصدق في شأن محمد والرفق بهم، وفي قراءة بضم الحاء وسكون السين مصدر وصف به مبالغة «وأقيموا الصلاة وآتوا الزكاة» فقبلتم ذلك «ثم تولَّيتم» أعرضتم عن الوفاء به، فيه التفات عن الغيبة والمراد آباؤهم «إلا قليلا منكم وأنتم معرضون» عنه كآبائكم.'),
(91, 2, 84, '«وإذ أخذنا ميثاقكم» وقلنا «لا تسفكون دماءكم» تريقونها بقتل بعضكم بعضاً «ولا تخرجون أنفسكم من دياركم» لا يخرج بعضكم بعضا من داره «ثم أقررتم» قبلتم ذلك الميثاق «وأنتم تشهدون» على أنفسكم.'),
(92, 2, 85, '«ثم أنتم» يا «هؤلاء تقتلون أنفسكم» بقتل بعضكم بعضا «وتخرجون فريقا منكم من ديارهم تَظَّاهَرُونَ» فيه إدغام التاء في الأصل في الظاء، وفي قراءة بالتخفيف على حذفها تتعاونون «عليهم بالإثم» بالمعصية «والعدوان» الظلم «وإن يأتوكم أسارى» وفي قراءة أسرى «تَفْدُوهُمْ» وفي قراءة «تُفَادُوهُمْ» تنقذوهم من الأسر بالمال أو غيره وهو مما عهد إليهم «وهو» أي الشأن «محرَّم عليكم إخراجهم» متصل بقوله وتخرجون والجملة بينهما اعتراض: أي كما حرم ترك الفداء، وكانت قريظةُ حالفوا الأوسَ، والنضيرُ الخزرجَ فكان كل فريق يقاتل مع حلفائه ويخرب ديارهم ويخرجهم فإذا أسروا فدوهم، وكانوا إذا سئلوا لم تقاتلونهم وتفدونهم؟ قالوا أمرنا بالفداء فيقال فَلِمَ تقاتلونهم؟ فيقولون حياء أن تستذل حلفاؤنا. قال تعالى: «أفتؤمنون ببعض الكتاب» وهو الفداء «وتكفرون ببعض» وهو ترك القتل والإخراج والمظاهرة «فما جزاء من يفعل ذلك منكم إلا خزيٌ» هوان وذلّ «في الحياة الدنيا» وقد خزوا بقتل قريظة ونفي النضير إلى الشام وضرب الجزية «ويوم القيامة يردون إلى أشد العذاب وما الله بغافلِ عما يعملون» بالياء والتاء.'),
(93, 2, 86, '«أولئك الذين اشتروا الحياة الدنيا بالآخرة» بأن آثروها عليها «فلا يخفَّف عنهم العذاب ولا هم يُنصرون» يمنعون منه.'),
(94, 2, 87, '«ولقد آتينا موسى الكتاب» التوراة «وقفيَّنا من بعده بالرسل» أي اتبعناهم رسولا في إثر رسول «وآتينا عيسى ابن مريم البينات» المعجزات كإحياء الموتى وإبراء الأكمه والأبرص «وأيدناه» قويناه «بروح القدس» من إضافة الموصوف إلى الصفة أي الروح المقدسة جبريل لطهارته يسير معه حيث سار فلم تستقيموا «أفكلما جاءكم رسول بما لا تهوى» تحب «أنفسُكم» من الحق «استكبرتم» تكبرتم عن إتباعه جواب كلما وهو محل الاستفهام، والمراد به التوبيخ «ففريقا» منهم «كذبتم» كعيسى «وفريقاً تقتلون» المضارع لحكاية الحال الماضية: أي قتلتم كزكريا ويحيى.'),
(95, 2, 88, '«وقالوا» للنبي استهزاء «قلوبنا غُلْفٌ» جمع أغلف أي مغشاة بأغطية فلا تعي ما تقول قال تعالى: «بل» للإضراب «لعنهم الله» أبعدهم من رحمته وخذلهم عن القبول «بكفرهم» وليس عدم قبولهم لخلل في قلوبهم «فقليلا ما يؤمنون» ما زائدة لتأكيد القلة أي: إيمانهم قليل جداً.'),
(96, 2, 89, '«ولما جاءهم كتاب من عند الله مصدق لما معهم» من التوراة: هو القرآن «وكانوا من قبل» قبل مجيئه «يستفتحون» يستنصرون «على الذين كفروا» يقولون اللهم انصرنا عليهم بالنبي المبعوث آخر الزمان «فلما جاءهم ما عرفوا» من الحق وهو بعثة النبي «كفروا به» جحداً وخوفاً على الرياسة وجوابُ لما الأولى دل عليه جواب الثانية «فلعنة الله على الكافرين».'),
(97, 2, 90, '«بئسما اشتروا» باعوا «به أنفسهم» أي حظها من الثواب، وما: نكرة بمعنى شيئاً تمييز لفاعل بئس والمخصوص بالذم «أن يكفروا» أي كفرهم «بما أنزل الله» من القرآن «بغياً» مفعول له ليكفروا: أي حسداً على «أن ينزل الله» بالتخفيف والتشديد «من فضله» الوحي «على من يشاء» للرسالة «من عباده فباءوا» رجعوا «بغضب» من الله بكفرهم بما أنزل والتنكيرُ للتعظيم «على غضب» استحقوه من قبل بتضييع التوراة والكفر بعيسى «وللكافرين عذاب مُهين» ذو إهانة.'),
(98, 2, 91, '«وإذا قيل لهم آمِنوا بما أنزل الله» القرآن وغيره «قالوا نؤمن بما أنزل علينا» أي التوراة قال تعالى: «ويكفرون» الواو للحال «بما وراءه» سواه أو بعده من القرآن «وهو الحق» حال «مصدقاً» حال ثانية مؤكدة «لما معهم قل» لهم «فلم تقتلون» أي قتلتم «أنبياء الله من قبل إن كنتم مؤمنين» بالتوراة وقد نهيتم فيها عن قتلهم والخطاب للموجودين في زمن نبينا بما فعل آباؤهم لرضاهم به.'),
(99, 2, 92, '«ولقد جاءكم موسى بالبينات» بالمعجزات كالعصا واليد وفلق البحر «ثم اتخذتم العجل» إلَهاً «من بعده» من بعد ذهابه إلى الميقات، «وأنتم ظالمون» باتخاذه.'),
(100, 2, 93, '«وإذ أخذنا ميثاقكم» على العمل بما في التوراة «و» قد «رفعنا فوقكم الطور» الجبل حين امتنعتم من قبولها ليسقط عليكم وقلنا «خذوا ما آتيناكم بقوة» بجد واجتهاد «واسمعوا» ما تؤمرون به سماع قبول «قالوا سمعنا» قولك «وعصينا» أمرك «وأشربوا في قلوبهم العجل» أي خالط حبه قلوبهم كما يخالط الشراب «بكفرهم، قل» لهم «بئسما» شيئا «يأمركم به إيمانكم» بالتوراة عبادة العجل «إن كنتم مؤمنين» بها كما زعمتم. المعنى لستم بمؤمنين لأن الإيمان لا يأمر بعبادة العجل، والمراد آباؤهم: أي فكذلك أنتم لستم بمؤمنين بالتوراة وقد كذَّبتم محمداً والإيمان بها لا يأمر بتكذيبه.'),
(101, 2, 94, '«قل» لهم «إن كانت لكم الدار الآخرة» أي الجنة «عند الله خالصة» خاصة «من دون الناس» كما زعمتم «فتمنوا الموت إن كنتم صادقين» تعلق بتمنوا الشرطان على أن الأول قيد في الثاني أي إن صدقتم في زعمكم أنها لكم ومن كانت له يؤثرها والموصل إليها الموت فتمنوه.'),
(102, 2, 95, '«ولن يتمنَّوه أبداً بما قدمت أيديهم» من كفرهم بالنبي المستلزم لكذبهم «والله عليم بالظالمين» الكافرين فيجازيهم.'),
(103, 2, 96, '«ولتجدنهم» لام قسم «أحرص الناس على حياة و» أحرص «من الذين أشركوا» المنكرين للبعث عليها لعلمهم بأن مصيرهم النار دون المشركين لإنكارهم له «يودُّ» يتمنى «أحدهم لو يعمر ألف سنة» لو مصدرية بمعنى أن وهي بصلتها في تأويل مصدر مفعول يود «وما هو» أي أحدهم «بمزحزحه» مبعده «من العذاب» النار «أن يعمَّر» فاعل مزحزحه أي تعميره «والله بصير بما يعملون» بالياء والتاء فيجازيهم وسأل ابن صوريا النبي أو عمر عمن يأتي بالوحي من الملائكة فقال جبريل فقال هو عدونا يأتي بالعذاب ولو كان ميكائيل لآمنا لأنه يأتي بالخصب والسلم فنزل:'),
(104, 2, 97, '«قل» لهم «من كان عدوّاً لجبريل» فليمت غيظاً «فإنه نزَّله» أي القرآن «على قلبك بإذن» بأمر «الله مصدقاً لما بين يديه» قلبه من الكتب «وهدىً» من الضلالة «وبشرى» بالجنة «للمؤمنين».'),
(105, 2, 98, '«من كان عدواً لله وملائكته ورسله وجبريل» بكسر الجيم وفتحها بلا همز وبه بياء ودونها «وميكال» عطف على الملائكة من عطف الخاص على العام وفي قراءة ميكائيل بهمزة وياء وفي أخرى بلا ياء «فإن الله عدوٌ للكافرين» وأوقعه موقع لهم بياناً لحالهم.'),
(106, 2, 99, '«ولقد أنزلنا إليك» يا محمد «آيات بينات» أي واضحات حال، رد لقول ابن صوريا للنبي ما جئتنا بشيء «وما يكفر بها إلا الفاسقون» كفروا بها.'),
(107, 2, 100, '«أو كلما عاهدوا» الله «عهداً» على الإيمان بالنبي إن خرج، أو النبيَّ أن لا يعاونوا عليه المشركين «نبذه» طرحه «فريق منهم» بنقضه، جواب كلما وهو محل الاستفهام الإنكاري «بل» للانتقال «أكثرهم لا يؤمنون».'),
(108, 2, 101, '«ولما جاءهم رسول من عند الله» محمد «مصدق لما معهم نبذ فريق من الذين أوتوا الكتاب كتاب الله» أي التوراة «وراء ظهورهم» أي لم يعملوا بما فيها من الإيمان بالرسول وغيره «كأنهم لا يعلمون» ما فيها من أنه نبي حق أو أنها كتاب الله.'),
(109, 2, 102, '«واتبعوا» عطف على نبذ «ما تتلوا» أي تلت «الشياطين على» عهد «ملك سليمان» من السحر وكانت دفنته تحت كرسيه لما نزع ملكه أو كانت تسترق السمع وتضم إليه أكاذيب وتلقيه إلى الكهنة فيدونونه وفشا ذلك وشاع أن الجن تعلم الغيب فجمع سليمان الكتب ودفنها فلما مات دلت الشياطين عليها الناس فاستخرجوها فوجدوا فيها السحر فقالوا إنما ملككم بهذا فتعلموه فرفضوا كتب أنبيائهم قال تعالى تبرئه لسليمان ورداً على اليهود في قولهم انظروا إلى محمد يذكر سليمان في الأنبياء وما كان إلا ساحراً: «وما كفر سليمان» أي لم يعمل السحر لأنه كفر «ولكن» بالتشديد والتخفيف «الشياطين كفروا يعلمون الناس السحر» الجملة حال من ضمير كفروا «و» يعلمونهم «ما أنزل على الملكين» أي ألهماه من السحر وقرئ بكسر اللام الكائنين «ببابل» بلد في سواد العراق «هاروت وماروت» بدل أو عطف بيان للملكين قال ابن عباس هما ساحران كانا يعلمان السحر وقيل ملكان أنزلا لتعليمه ابتلاء من الله للناس «وما يعلمان من» زائدة «أحد حتى يقولا» له نصحاً «إنما نحن فتنة» بلية من الله إلى الناس ليمتحنهم بتعليمه فمن تعلمه كفر ومن تركه فهو مؤمن «فلا تكفر» بتعلمه فإن أبى إلا التعليم علماه «فيتعلمون منهما ما يفرقون به بين المرء وزوجه» بأن يبغض كلا إلى الآخر «وما هم» أي السحرة «بضارين به» بالسحر «من» زائدة «أحد إلا بإذن الله» بإرادته «ويتعلمون ما يضرهم» في الآخرة «ولا ينفعهم» وهو السحر «ولقد» لام قسم «علموا» أي اليهود «لمن» لام ابتداء معلقة لما قبلها ومن موصلة «اشتراه» اختاره أو استبدله بكتاب الله «ماله في الآخرة من خلاق» نصيب في الجنة «ولبئس ما» شيئاً «شروا» باعوا «به أنفسهم» أي الشارين: أي حظها من الآخرة إن تعلموه حيث أوجب لهم النار «لو كانوا يعلمون» حقيقة ما يصيرون إليه من العذاب ما تعلَّموه.'),
(110, 2, 103, '«ولو أنهم» أي اليهود «آمنوا» بالنبي والقرآن «واتقوا» عقاب الله بترك معاصيه كالسحر، وجوابُ لو محذوف: أي لأثيبوا دل عليه «لمثوبة» ثواب وهو مبتدأ واللام فيه للقسم «من عند الله خير» خبره مما شروا به أنفسهم «لو كانوا يعلمون» أنه خير لما آثروه عليه.'),
(111, 2, 104, '«يا أيها الذين أمنوا لا تقولوا» للنبي «راعنا» أمر من المراعاة وكانوا يقولون له ذلك وهي بلغة اليهود سب من الرعونة فسُرُّوا بذلك وخاطبوا بها النبي فنُهى المؤمنون عنها «وقولوا» بدلها «انظرنا» أي انظر إلينا «واسمعوا» ما تؤمرون به سماع قبول «وللكافرين عذاب أليم» مؤلم هو النار.'),
(112, 2, 105, '«ما يود الذين كفروا من أهل الكتاب ولا المشركين» من العرب عطف على أهل الكتاب ومن للبيان «أن يُنَزَّلَ عليكم من» زائدة «خير» وحي «من ربكم» حسداً لكم «والله يختص برحمته» نبوته «من يشاء والله ذو الفضل العظيم»'),
(113, 2, 106, 'ولما طعن الكفار في النسخ وقالوا إن محمداً يأمر أصحابه اليوم بأمر وينهى عنه غداً نزل: «ما» شرطية «ننسخ من أية» أي ننزل حكمها: إما مع لفظها أو لا وفي قراءة بضم النون من أنسخ: أي نأمرك أو جبريل بنسخها «أو ننسها» نؤخرها، فلا ننزل حكمها ونرفع تلاوتها أو نؤخرها في اللوح المحفوظ وفي قراءة بلا همز من النسيان أي ننسها، أي نمحها من قلبك وجواب الشرط «نأت بخير منها» أنفع للعباد في السهولة أو كثرة الأجر «أو مثلها» في التكليف والثواب «ألم تعلم أن الله على كل شئ قدير» ومنه النسخ والتبديل، والاستفهام للتقرير.'),
(114, 2, 107, '«ألم تعلم أن الله له ملك السموات والأرض» يفعل ما يشاء «وما لكم من دون الله» أي غيره «من» زائدة «وليٌ» يحفظكم «ولا نصير» يمنع عنكم عذابه إن أتاكم، ونزل لما سأله أهل مكة أن يوسعها ويجعل الصفا ذهباً.'),
(115, 2, 108, '«أم» بل «تريدون أن تسألوا رسولكم كما سئل موسى» أي سأله قومه «من قبل» من قولهم: أرنا الله جهرة وغير ذلك «ومن يتبدل الكفر بالإيمان» أي يأخذ بدله بترك النظر في الآيات البينات واقتراح غيرها «فقد ضل سواء السبيل» أخطأ الطريق الحق والسواءُ في الأصل الوسط.'),
(116, 2, 109, '«ودَّ كثير من أهل الكتاب لو» مصدرية «يردونكم من بعد إيمانكم كفارا حسدا» مفعول له كائنا «من عند أنفسهم» أي حملتهم عليه أنفسهم الخبيثة «من بعد ما تبين لهم» في التوراة «الحق» في شأن النبي «فاعفوا» عنهم أي اتركوهم «واصفحوا» أعرضوا فلا تجازوهم «حتى يأتي الله بأمره» فيهم من القتال «إن الله على كل شئ قدير».'),
(117, 2, 110, '«وأقيموا الصلاة وآتوا الزكاة وما تقدموا لأنفسكم من خير» طاعة كصلة وصدقة «تجدوه» أي ثوابه «عند الله إن الله بما تعملون بصير» فيجازيكم به.'),
(118, 2, 111, '«وقالوا لن يدخل الجنة إلا من كان هوداً» جمع هائد «أو نصارى» قال ذلك يهود المدينة ونصارى نجران لما تناظروا بين يدي النبي أي قال اليهود لن يدخلها إلا اليهود وقال النصارى لن يدخلها إلا النصارى «تلك» القولة «أمانيهم» شهواتهم الباطلة «قل» لهم «هاتوا برهانكم» حجتكم على ذلك «إن كنتم صادقين» فيه.'),
(119, 2, 112, '«بلى» يدخل الجنة غيرهم «من أسلم وجهه لله» أي انقاد لأمره. وخص الوجه لأنه أشرف الأعضاء فغيره أولى «وهو محسن» موحد «فله أجره عند ربِّه» أي ثواب عمله الجنة «ولا خوف عليهم ولا هم يحزنون» في الآخرة.'),
(120, 2, 113, '«وقالت اليهود ليست النصارى على شئ» معتد به وكفرت بعيسى «وقالت النصارى ليست اليهود على شئ» معتد به وكفرت بموسى «وهم» أي الفريقان «يتلون الكتاب» المنزل عليهم، وفي كتاب اليهود تصديق عيسى، وفي كتاب النصارى تصديق موسى والجملة حال «كذلك» كما قال هؤلاء «قال الذين لا يعلمون» أي المشركون من العرب وغيرهم «مثل قولهم» بيان لمعنى ذلك: أي قالوا لكل ذي دين ليسوا على شئ «فالله يحكم بينهم يوم القيامة فيما كانوا فيه يختلفون» من أمر الدين فيدخل المحقُّ الجنة والمبطل النار.'),
(121, 2, 114, '«ومن أظلم» أي لا أحد أظلم «ممن منع مساجد الله أن يذكر فيها اسمه» بالصلاة والتسبيح «وسعى في خرابها» بالهدم أو التعطيل، نزلت إخباراً عن الروم الذين خربوا بيت المقدس أو في المشركين لما صدوا النبي عام الحديبية عن البيت «أولئك ما كان لهم أن يدخلوها إلا خائفين» خبر بمعنى الأمر أي أخيفوهم بالجهاد فلا يدخلها أحد آمناً. «لهم في الدنيا خزي» هوان بالقتل والسبي والجزية «ولهم في الآخرة عذاب عظيم» هو النار.'),
(122, 2, 115, 'ونزل لما طعن اليهود في نسخ القبلة أو في صلاة النافلة على الراحلة في السفر حيثما توجهت «ولله المشرق والمغرب» أي الأرض كلها لأنهما ناحيتها «فأينما تولوا» وجوهكم في الصلاة بأمره «فثمَّ» هناك «وجه الله» قبلته التي رضيها «إن الله واسع» يسع فضله كل شئ «عليم» بتدبير خلقه.'),
(123, 2, 116, '«وقالوا» بواو وبدونها اليهود والنصارى ومن زعم أن الملائكة بنات الله «اتخذ الله ولداً» قال تعالى «سبحانه» تنزيها له عنه «بل له ما في السماوات والأرض» ملكا وخلقا وعبيدا والملكية تنافي الولادة وعبر بما تغليبا لما لا يعقل «كل له قانتون» مطيعون كل بما يراد منه وفيه تغليب العاقل.'),
(124, 2, 117, '«بديع السماوات والأرض» موجدهم لا على مثال سبق «وإذا قضى» أراد «أمرا» أي إيجاده «فإنما يقول له كن فيكون» أي فهو يكون وفى قراءة بالنصب جوابا للأمر.'),
(125, 2, 118, '«وقال الذين لا يعلمون» أي كفار مكة للنبي «لولا» هلا «يكلمنا الله» بأنك رسوله «أو تأتينا آية» مما اقترحناه على صدقك «كذلك» كما قال هؤلاء «قال الذين من قبلهم» من كفار الأمم الماضية لأنبيائهم «مثل قولهم» من التعنت وطلب الآيات «تشابهت قلوبهم» في الكفر والعناد، فيه تسلية للنبي «قد بينا الآيات لقوم يوقنون» يعلمون أنها آيات فيؤمنون فاقتراحُ آية معها تعنُّت.'),
(126, 2, 119, '«إنا أرسلناك» يا محمد «بالحق» بالهدى «بشيرا» من أجاب إليه بالجنة «ونذيرا» من لم يجب إليه بالنار «ولا تُسأل عن أصحاب الجحيم» النار، أي الكفار ما لهم لم يؤمنوا إنما عليك البلاغ، وفي قراءة بجزم تسأل نهيا.'),
(127, 2, 120, '«ولن ترضى عنك اليهود ولا النصارى حتى تتبع ملَّتهم» دينهم «قل إن هدى الله» أي الإسلام «هو الهدى» وما عداه ضلال «ولئن» لام قسم «اتبعت أهواءهم» التي يدعونك إليها فرضاً «بعد الذي جاءك من العلم» الوحي من الله «مالك من الله من ولي» يحفظك «ولا نصير» يمنعك منه.'),
(128, 2, 121, '«الذين آتيناهم الكتاب» مبتدأً «يتلونه حق تلاوته» أي يقرؤونه كما أنزل والجملة حال وحق نصب على المصدر والخبر «أولئك يؤمنون به» نزلت في جماعة قدموا من الحبشة وأسلموا «ومن يكفر به» أي بالكتاب المؤتى بأن يحرفه «فأولئك هم الخاسرون» لمصيرهم إلى النار المؤبدة عليهم.'),
(129, 2, 122, '«يابني إسرائيل اذكروا نعمتي التي أنعمت عليكم و أني فضَّلتكم على العالمين» تقدم مثله.'),
(130, 2, 123, '«واتقوا» خافوا «يوما لا تجزي» تغني «نفس عن نفس» فيه «شيئا ولا يُقبل منها عدل» فداء «ولا تنفعها شفاعة ولا هم يُنصرون» يمنعون من عذاب الله.'),
(131, 2, 124, '«و» اذكر «إذا ابتلى» اختبر «إبراهيم» وفي قراءة إبراهام. «ربُّه بكلمات» بأوامر ونواه كلَّفه بها، قيل هي مناسك الحج، وقيل المضمضة والاستنشاق والسواك وقص الشارب وفرق الرأس وقلم الأظافر ونتف الإبط وحلق العانة والختان والاستنجاء «فأتمهن» أداهن تامات «قال» تعالى له «إني جاعلك للناس إماما» قدوة في الدين «قال ومن ذرِّيتي» أولادي اجعل أئمة «قال لا ينال عهدي» بالإمامة «الظالمين» الكافرين منهم دل على أنه ينال غير الظالم.'),
(132, 2, 125, '«وإذ جعلنا البيت» الكعبة «مثابة للناس» مرجعا يثبون إليه من كل جانب «وأمنا» مأمنا لهم من الظلم والإغارات الواقعة في غيره، كان الرجل يلقى قاتل أبيه فيه فلا يهيجه «واتخذوا» أيها الناس «من مقام إبراهيم» هو الحجر الذي قام عليه عند بناء البيت «مصلى» مكان صلاة بأن تصلوا خلفه ركعتي الطواف، وفي قراءة بفتح الخاء خبر «وعهدنا إلى إبراهيم وإسماعيل» أمرناهما «أن» أي بأن «طهِّرا بيتي» من الأوثان «للطائفين والعاكفين» المقيمين فيه «والركع السجود» جمع راكع وساجد المصلين.'),
(133, 2, 126, '«وإذا قال إبراهيم رب اجعل هذا» المكان «بلدا آمنا» ذا أمن وقد أجاب الله دعاءه فجعله حرما لا يسفك فيه دم إنسان ولا يظلم فيه أحد ولا يصاد صيده ولا يختلي خلاه «وارزق أهله من الثمرات» وقد فعل بنقل الطائف من الشام إليه وكان أقفر لا زرع فيه ولا ماء «من آمن منهم بالله واليوم الآخر» بدل من أهله وخصهم بالدعاء لهم موافقة لقوله لا ينال عهدي الظالمين «قال» تعالى «و» ارزق «من كفر فَأُمَتِّعُهُ» بالتشديد والتخفيف في الدنيا بالرزق «قليلا» مدة حياته «ثم أضطره» ألجئه في الآخرة «إلى عذاب النار» فلا يجد عنها محيص «وبئس المصير» المرجع هي.'),
(134, 2, 127, '«و» اذكر «إذ يرفع إبراهيم القواعد» الأسس أو الجدر «من البيت» يبنيه متعلق بيرفع «وإسماعيل» عطف على إبراهيم يقولان «ربنا تقبل منا» بناءنا «إنك أنت السميع» للقول «العليم» بالفعل.'),
(135, 2, 128, '«ربنا واجعلنا مسلمَيْن» منقادين «لك و» اجعل «من ذرِّيتنا» أولادنا «أمة» جماعة «مسلمة لك» ومن للتبعيض وأتى به لتقدم قوله لا ينال عهدي الظالمين «وأرنا» علِّمنا «مناسكنا» شرائع عبادتنا أو حجنا «وتب علينا إنك أنت التواب الرحيم» سألاه التوبة مع عصمتهما تواضعا وتعليما لذريتهما.'),
(136, 2, 129, '«ربنا وابعث فيهم» أي أهل البيت «رسولا منهم» من أنفسهم وقد أجاب الله دعاءه بمحمد «يتلو عليهم آياتك» القرآن «ويعلمهم الكتاب» القرآن «والحكمة» أي ما فيه من الأحكام «ويزكيهم» يطهرهم من الشرك «إنك أنت العزيز» الغالب «الحكيم» في صنعه.'),
(137, 2, 130, '«ومن» أي لا «يرغب عن ملة إبراهيم» فيتركها «إلا من سفه نفسه» جهل أنها مخلوقة لله يجب عليها عبادته أو استخف بها وامتهنها «ولقد اصطفيناه» اخترناه «في الدنيا» بالرسالة والخلة «وإنه في الآخرة لمن الصالحين» الذين لهم الدرجات العلى.'),
(138, 2, 131, 'واذكر «إذ قال له ربه أسلمْ» انقد لله وأخلص له دينك «قال أسلمت لرب العالمين».'),
(139, 2, 132, '«ووصَّى» وفي قراءة أوصى «بها» بالملة «إبراهيم بنيه ويعقوب» بنيه قال: «يا بني إن الله اصطفى لكم الدين» دين الإسلام «فلا تموتنَّ إلا وأنتم مسلمون» نهى عن ترك الإسلام وأمر بالثبات عليه إلى مصادفة الموت.'),
(140, 2, 133, 'ولما قال اليهود للنبي ألست تعلم أن يعقوب يوم مات أوصى بنيه باليهودية نزل: «أم كنتم شهداء» حضورا «إذ حضر يعقوب الموتُ إذ» بدل من إذ قبله «قال لبنيه ما تعبدون من بعدي» بعد موتي «قالوا نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق» جد إسماعيل من الآباء تغليب ولأن العم بمنزلة الأب «إلها واحدا» بدل من إلهك «ونحن له مسلمون» وأم بمعنى همزة الإنكار أي لم تحضروه وقت موته فكيف تنسبون إليه ما لا يليق به.'),
(141, 2, 134, '«تلك» مبتدأ والإشارة إلى إبراهيم ويعقوب وبينهما وأُنِّث لتأنيث خبره «أمة قد خلت» سلفت «لها ما كسبت» من العمل أي جزاؤه استئناف «ولكم» الخطاب لليهود «ما كسبتم ولا تُسألون عما كانوا يعلمون» كما لا يسألون عن عملكم والجملة تأكيد لما قبلها.'),
(142, 2, 135, '«وقالوا كونوا هودا أو نصارى تهتدوا» أو للتفضيل وقائل الأول يهود المدينة والثاني نصارى نجران «قل» لهم «بل» نتبع «ملة إبراهيم حنيفا» حال من إبراهيم مائلا عن الأديان كلها إلى الدين القيّم «وما كان من المشركين».'),
(143, 2, 136, '«قولوا» خطاب للمؤمنين «آمنا بالله وما أنزل إلينا» من القرآن «وما أنزل إلى إبراهيم» من الصحف العشر «وإسماعيل وإسحاق ويعقوب والأسباط» أولاده «وما أوتي موسى» من التوراة «وعيسى» من الإنجيل «وما أوتي النبيُّون من ربهم» من الكتب والآيات «لا نُفرِّق بين أحد منهم» فنؤمن ببعض ونكفر ببعض كاليهود والنصارى «ونحن له مسلمون».'),
(144, 2, 137, '«فإن آمنوا» أي اليهود والنصارى «بمثل» زائدة «ما آمنتم به فقد اهتدوا وإن تولوا» عن الإيمان به «فإنما هم في شقاق» خلاف معكم «فسيكفيكهم الله» يا محمد شقاقهم «وهو السميع» لأقوالهم «العليم» بأحوالهم وقد كفاه إياهم بقتل قريظة، ونفي النضير وضرب الجزية عليهم.'),
(145, 2, 138, '«صِبْغَةَ الله» مصدر مؤكد لآمنا ونصبه بفعل مقدر، أي صبغنا الله والمراد بها دينه الذي فطر الناس عليه لظهور أثره على صاحبه كالصبغ في الثوب «ومن» أي لا أحد «أحسن من الله صبغة» تمييز «ونحن له عابدون» قال اليهود للمسلمين نحن أهل الكتاب الأول وقبلتنا أقدم ولم تكن الأنبياء من العرب ولو كان محمد نبيا لكان منا فنزل.'),
(146, 2, 139, '«قل» لهم «أتحاجوننا» تخاصموننا «في الله» أن اصطفى نبيا من العرب «وهو ربنا وربكم» فله أن يصطفي من عباده ما يشاء «ولنا أعمالنا» نجازى بها «ولكم أعمالكم» تجازون بها فلا يبعد أن يكون في أعمالنا ما نستحق به الإكرام «ونحن له مخلصون» الدين والعمل دونكم فنحن أولى بالاصطفاء، والهمزة للإنكار والجمل الثلاث أحوال.'),
(147, 2, 140, '«أم» بل «تقولون» بالتاء والياء «إنَّ إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط كانوا هودا أو نصارى قل» لهم «أأنتم أعلم أم الله» أي الله أعلم وقد برأ منهما إبراهيم بقوله (ما كان إبراهيم يهوديا ولا نصرانيا) والمذكورون معه تبع له «ومن أظم ممن كتم» أخفى عن الناس «شهادة عنده» كائنة «من الله» أي لا أحد أظلم منه وهم اليهود كتموا شهادة الله في التوراة إبراهيم بالحنيفية «وما الله بغافل عما تعملون» تهديد لهم.'),
(148, 2, 141, '«تلك أمة قد خلت لها ما كسبت ولكم ما كسبتم ولا تُسألون عما كانوا يعملون» تقدم مثله.'),
(149, 2, 142, '«سيقول السفهاء» الجهال «من الناس» اليهود والمشركين «ما ولاّهم» أي شيء، صرف النبي والمؤمنين «عن قبلتهم التي كانوا عليها» على استقبالها في الصلاة وهي بيت المقدس، والإتيان بالسين الدالة على الاستقبال من الإخبار بالغيب «قل لله المشرق والمغرب» أي الجهات كلها فيأمر بالتوجه إلى أي جهة شاء لا اعتراض عليه «يهدي من يشاء» هدايته «إلى صراط» طريق «مستقيم» دين الإسلام أي ومنهم أنتم دل على هذا.'),
(150, 2, 143, '«وكذلك» كما هديناكم إليه «جعلناكم» يا أمة محمد «أمة وسطا» خيارا عدولا «لتكونوا شهداء على الناس» يوم القيامة أنَّ رسلهم بلَّغتهم «ويكون الرسول عليكم شهيدا» أنه بلغكم «وما جعلنا» صيرنا «القبلة» لك الآن الجهة «التي كنت عليها» أولا وهي الكعبة وكان يصلى إليها فلما هاجر أمر باستقبال بيت المقدس تألُّفا لليهود فصلى إليه ستة أو سبعة عشر شهرا ثم حول «إلا لنعلم» علم ظهور «من يتبع الرسول» فيصدقه «ممن ينقلب على عقبيه» أي يرجع إلى الكفر شكا في الدين وظنا أن النبي في حيرة من أمره وقد ارتد لذلك جماعة «وإن» مخففة من الثقيلة واسمها محذوف أي وإنها «كانت» أي التولية إليها «لكبيرة» شاقة على الناس «إلا على الذين هدى الله» منهم «وما كان الله ليضيع إيمانكم» أي صلاتكم إلى بيت المقدس بل يثيبكم عليه لأن سبب نزولها السؤال عمن مات قبل التحويل «إن الله بالناس» المؤمنين «لرؤوف رحيم» في عدم إضاعة أعمالهم، والرأفةُ شدة الرحمة وقدَّم الأبلغ للفاصلة.'),
(151, 2, 144, '«قد» للتحقيق «نرى تقلُّب» تصرف «وجهك في» جهة «السماء» متطلعا إلى الوحي ومتشوقا للأمر باستقبال الكعبة وكان يود ذلك لأنها قبلة إبراهيم ولأنه دعى إلى إسلام العرب «فلنولينك» نحولنك «قبلة ترضاها» تحبها «فولِّ وجهك» استقبل في الصلاة «شطر» نحو «المسجد الحرام» أي الكعبة «وحيثما كنتم» خطاب للأمة «فولُّوا وجوهكم» في الصلاة «شطره وإن الذين أوتوا الكتاب ليعلمون أنه» أي التولي إلى الكعبة «الحق» الثابت «من ربهم» لما في كتبهم من نعت النبي من أنه يتحول إليها «وما الله بغافل عما تعملون» بالتاء أيها المؤمنون من امتثال أمره وبالياء أي اليهود من إنكار أمر القبلة.'),
(152, 2, 145, '«ولإن» لام قسم «أتيت الذين أوتوا الكتاب بكل آية» على صدقك في أمر القبلة «ما تبعوا» أي لا يتبعون «قبلتك» عناداً «وما أنت بتابع قبلتهم» قطع لطمعه في إسلامهم وطمعهم في عوده إليها «وما بعضهم بتابع قبلة بعض» أي اليهود قبلة النصارى وبالعكس «ولإن اتبعت أهواءهم» التي يدعونك إليها «من بعد ما جاءك من العلم» الوحي «إنك إذا» إن تبعتهم فرضا «لمن الظالمين».'),
(153, 2, 146, '«الذين آتيناهم الكتاب يعرفونه» أي محمداً «كما يعرفون أبناءهم» بنعته في كتبهم قال ابن سلام: لقد عرفته حين رأيته كما أعرف ابني ومعرفتي لمحمد أشد «وإن فريقا منهم ليكتمون الحق» نعته «وهم يعلمون» هذا الذي أنت عليه.'),
(154, 2, 147, '«الحق» كائنا «من ربك فلا تكوننَّ من الممترين» الشاكين فيه أي من هذا النوع فهو أبلغ من أن لا تمتر.'),
(155, 2, 148, '«ولكل» من الأمم «وجهة» قبلة «هو موليها» وجهه في صلاته وفي قراءة مُوَلاَّهَا «فاستبقوا الخيرات» بادروا إلى الطاعات وقبولها «أينما تكونوا يأت بكم الله جميعا» يجعلكم يوم القيامة فيجازيكم بأعمالكم «إن الله على كل شيء قدير».'),
(156, 2, 149, '«ومن حيث خرجت» لسفر «فولِّ وجهك شطر المسجد الحرام وإنه للحق من ربك وما الله بغافل عما تعلمون» بالياء والتاء تقدم مثله وكرره لبيان تساوي حكم السفر وغيره.'),
(157, 2, 150, '«ومن حيث خرجت فولِّ وجهك شطر المسجد الحرام وحيثما كنتم فولوا وجوهكم شطره» كرره للتأكيد «لئلا يكون للناس» اليهود أو المشركين «عليكم حجة» أي مجادلة في التولي إلى غيره أي لتنتفي مجادلتهم لكم من قول اليهود يجحد ديننا ويتبع قبلتنا وقول المشركين يدعي ملة إبراهيم ويخالف قبلته «إلا الذين ظلموا منهم» بالعناد فإنهم يقولون ما تحول إليها إلا ميلا إلى دين آبائه والاستثناء متصل والمعنى: لا يكون لأحد عليكم كلام إلا كلام هؤلاء «فلا تخشوهم» تخافوا جدالهم في التولي إليها «واخشوني» بامتثال أمري «ولأتم» عطف علي لئلا يكون «نعمتي عليكم» بالهداية إلى معالم دينكم «ولعلكم تهتدون» إلى الحق.'),
(158, 2, 151, '«كما أرسلنا» متعلق بأتم أي إتماما كإتمامها بإرسالنا «فيكم رسولا منكم» محمد «يتلو عليكم آياتنا» القرآن «ويزكيكم» يطهركم من الشرك «ويعلمكم الكتاب» القرآن «والحكمة» ما فيه من الأحكام «ويعلمكم ما لم تكونوا تعلمون».'),
(159, 2, 152, '«فاذكروني» بالصلاة والتسبيح ونحوه «أذكركم» قيل معناه أجازيكم وفي الحديث عن الله (من ذكرني في نفسه ذكرته في نفسي ومن ذكرني في ملأ ذكرته في ملأ خير من ملئه) «واشكروا لي» نعمتي بالطاعة «ولا تكفرون» بالمعصية.'),
(160, 2, 153, '«يا أيها الذين آمنوا استعينوا» على الآخرة «بالصبر» على الطاعة والبلاء «والصلاة» خصها بالذكر لتكررها وعظمها «إن الله مع الصابرين» بالعون.'),
(161, 2, 154, '«ولا تقولوا لمن يقتل في سبيل الله» هم «أموات بل» هم «أحياء» أرواحهم في حواصل طيور خضر تسرح في الجنة حيث شاءت لحديث بذلك «ولكن لا تشعرون» تعلمون ما هم فيه.'),
(162, 2, 155, '«ولنبلونكم بشيء من الخوف» للعدو «والجوع» القحط «ونقص من الأموال» بالهلاك «والأنفس» بالقتل والموت والأمراض «والثمرات» بالحوائج أي لنختبرنكم فننظر أتصبرون أم لا «وبشر الصابرين» على البلاء بالجنة.'),
(163, 2, 156, 'هم «الذين إذا أصابتهم مصيبة» بلاء «قالوا إنا لله» ملكا وعبيدا يفعل بنا ما يشاء «وإنا إليه راجعون» في الآخرة فيجازينا وفي الحديث (من استرجع عند المصيبة أجره الله فيها وأخلف الله عليه خيرا) وفيه أن مصباح النبي صلى الله عليه وسلم طفئ فاسترجع فقالت عائشة: إنما هذا مصباح فقال: (كل ما أساء المؤمن فهو مصيبة) رواه أبو داود في مراسيله.'),
(164, 2, 157, '«أولئك عليهم صلوات» مغفرة «من ربهم ورحمة» نعمة «وأولئك هم المهتدون» إلى الصواب.'),
(165, 2, 158, '(إن الصفا والمروة) جبلان بمكة (من شعائر الله) أعلام دينه جمع شعيرة (فمن حج البيت أو اعتمر) أي تلبس بالحج أو العمرة وأصلهما القصد والزيارة (فلا جناح عليه) إثم عليه (أن يطوف) فيه إدغام التاء في الأصل في الطاء (بهما) بأن يسعى بينهما سبعا، نزلت لما كره المسلمون ذلك لأن أهل الجاهلية كانوا يطوفون بهما وعليهما صنمان يمسحونهما، وعن ابن عباس أن السعي غير فرض لما أفاده رفع الإثم من التخيير وقال الشافعي وغيره ركن، وبين صلى الله عليه وسلم فرضيته بقوله "" إن الله كتب عليكم السعي "" رواه البيهقي وغيره "" وقال ابدءوا بما بدأ الله به "" يعني الصفا رواه مسلم (ومن تطوع) وفي قراءة بالتحتية وتشديد الطاء مجزوما وفيه إدغام التاء فيها (خيرا) أي بخير أي عمل ما لم يجب عليه من طواف وغيره (فإن الله شاكر) لعمله بالإثابة عليه (عليم) به.'),
(166, 2, 159, 'ونزل في اليهود: «إن الذين يكتمون» الناس «ما أنزلنا من البينات والهدى» كآية الرجم ونعت محمد «من بعد ما بيَّناه للناس في الكتاب» التوراة «أولئك يلعنهم الله» يبعدهم من رحمته «ويلعنهم اللاعنون» الملائكة والمؤمنون أو كل شيء بالدعاء عليهم باللعنة.'),
(167, 2, 160, '«إلا الذين تابوا» رجعوا عن ذلك «وأصلحوا» عملهم «وبيَّنوا» ما كتموا «فأولئك أتوب عليهم» أقبل توبتهم «وأنا التواب الرحيم» بالمؤمنين.'),
(168, 2, 161, '«إن الذين كفروا وماتوا وهم كفار» حال «أولئك عليهم لعنة الله والملائكة والناس أجمعين» أي هم مستحقون ذلك في الدنيا والآخرة. والناس قيل: عام. وقيل: المؤمنون.'),
(169, 2, 162, '«خالدين فيها» أي اللعنة والنار المدلول بها عليها «لا يخفف عنهم العذاب» طرفة عين «ولا هم ينظرون» يمهلون لتوبة أو معذرة.'),
(170, 2, 163, 'ونزل لما قالوا صف لنا ربك: «وإلهكم» المستحق للعبادة منكم «إِلهٌ واحد» لا نظير له في ذاته ولا في صفاته «لا إلهَ إلا هو» هو «الرحمن الرحيم» وطلبوا آيه على ذلك فنزل.'),
(171, 2, 164, 'إن في خلق السماوات والأرض» وما فيهما من العجائب «واختلاف الليل والنهار» بالذهاب والمجيء والزيادة والنقصان «والفلك» السفن «التي تجري في البحر» ولا ترسب موقرة «بما ينفع الناس» من التجارات والحمل «وما أنزل الله من السماء من ماء» مطر «فأحيا به الأرض» بالنبات «بعد موتها» يبسها «وبث» فرق ونشربه «فيها من كل دابة» لأنهم ينمون بالخصب الكائن عنه «وتصريف الرياح» تقليبها جنوبا وشمالا حارة وباردة «والسحاب» الغيم «المسخَّر» المذلَّل بأمر الله تعالى يسير إلى حيث شاء الله «بين السماء والأرض» بلا علاقة «لآيات» دالاّت على وحدانيته تعالى «لقوم يعقلون» يتدبرون.'),
(172, 2, 165, '«ومن الناس من يتخذ من دون الله» أي غيره «أندادا» أصناما «يحبونهم» بالتعظيم والخضوع «كحب الله» أي كحبهم له «والذين آمنوا أشد حبا لله» من حبهم للأنداد لأنهم لا يعدلون عنه بحال ما، والكفار يعدلون في الشدة إلى الله «ولو يرى» تبصر يا محمد «الذين ظلموا» باتخاذ الأنداد «إذ يرون» بالبناء للفاعل والمفعول به يبصرون «العذاب» لرأيت أمرا عظيما وإذا بمعني إذا «أن» أي لأن «القوة» القدرة والغلبة «لله جميعا» حال «وأن الله شديد العذاب» وفي قراءة ترى والفاعل ضمير السامع، وقيل الذين ظلموا وهي بمعني يعلم وأن وما بعدها سدت مسد المفعولين وجواب لو محذوف والمعنى لو علموا في الدنيا شدة عذاب الله وأن القدرة لله وحده وقت معاينتهم له وهو يوم القيامة لما اتخذوا من دونه أندادا.'),
(173, 2, 166, '«إذ» بدل من إذ قبله «تبرَّأ الذين اتُبعوا» أي الرؤساء «من الذين اتَّبعوا» أي أنكروا إضلالهم «و» قد «رأوا العذاب وتقطعت» عطف تبرأ «بهم» عنهم «الأسباب» الوصل التي كانت بينهم في الدنيا من الأرحام والمودة.'),
(174, 2, 167, '«وقال الذين اتَّبعوا لو أن لنا كرَّة» رجعة إلى الدنيا «فنتبرَّأ منهم» أي المتبوعين «كما تبرءوا منا» اليوم ولو للتمني ونتبرأ جوابه «كذلك» أي كما أراهم شدة عذابه وتبرأ بعضهم من بعض «يريهم الله أعمالهم» السيئة «حسرات» حال ندامات «عليهم وما هم بخارجين من النار» بعد دخولها.'),
(175, 2, 168, 'ونزل فيمن حرَّم السوائب ونحوها: «يا أيها الناس كلوا مما في الأرض حلالا» حال «طيبا» صفة مؤكدة أي مستلذاً «ولا تتبعوا خطوات» طرق «الشيطان» أي تزيينه «إنه لكم عدو مبين» بَيِّنُ العداوة.'),
(176, 2, 169, '«إنما يأمركم بالسوء» الإثم «والفحشاء» القبيح شرعا «وأن تقولوا على الله ما لا تعلمون» من تحريم ما لم يحرم وغيره.'),
(177, 2, 170, '«وإذا قيل لهم» أي الكفار «اتبعوا ما أنزل الله» من التوحيد وتحليل الطيبات «قالوا» لا «بل نتبع ما ألفينا» وجدنا «عليه آباءنا» من عبادة الأصنام وتحريم السوائب والبحائر قال تعالى: «أ» يتبعونهم «وَلو كان آباؤهم لا يعقلون شيئا» من أمر الدين «ولا يهتدون» إلى الحق والهمزةُ للإنكار.'),
(178, 2, 171, '«ومثل» صفة «الذين كفروا» ومن يدعوهم إلى الهدي «كمثل الذي ينعق» يصوت «بما لا يسمع إلا دعاءً ونداءً» أي صوتا ولا يفهم معناه أي في سماع الموعظة وعدم تدبرها كالبهائم تسمع صوت راعيها ولا تفهمه، هم «صمٌ بكمٌ عميٌ فهم لا يعقلون» الموعظة.'),
(179, 2, 172, '«يا أيها الذين آمنوا كلوا من طيِّبات» حلالات «ما رزقناكم واشكروا لله» على ما أحل لكم «إن كنتم إياه تعبدون».'),
(180, 2, 173, '«إنما حرم عليكم الميتة» أي أكلها إذا الكلام فيه وكذا ما بعدها وهي ما لم يذك شرعا، وألحق بها بالسنة ما أبين من حيِّ وخُص منها السمك والجراد «والدم» أي المسفوح كما في الأنعام «ولحم الخنزير» خص اللحم لأنه معظم المقصود وغيره تبع له «وما أهل به لغير الله» أي ذبح على اسم غيره والإهلال رفع الصوت وكانوا يرفعونه عند الذبح لآلهتهم «فمن اضطر» أي ألجأته الضرورة إلى أكل شيء مما ذكر فأكله «غير باغ» خارج على المسلمين «ولا عاد» متعد عليهم بقطع الطريق «فلا إثم عليه» في أكله «إن الله غفور» لأوليائه «رحيم» بأهل طاعته حيث وسع لهم في ذلك وخرج الباغي والعادي ويلحق بهما كل عاص بسفره كالآبق والماكس فلا يحل لهم أكل شيء من ذلك ما لم يتوبوا وعليه الشافعي.'),
(181, 2, 174, '«إن الذين يكتمون ما أنزل الله من الكتاب» المشتمل على نعت محمد صلى الله عليه وسلم وهم اليهود «ويشترون به ثمنا قليلا» من الدنيا يأخذونه بدله من سفلتهم فلا يظهرونه خوف فوته عليهم «أولئك ما يأكلون في بطونهم إلا النار» لأنها مآلهم «ولا يكلمهم الله يوم القيامة» غضبا عليهم «ولا يزكيهم» يطهرهم من دنس الذنوب «ولهم عذاب اليم» مؤلم هو النار.'),
(182, 2, 175, '«أولئك الذين اشتروا الضلالة بالهدى» آخذوها بدله في الدنيا «والعذاب بالمغفرة» المعدة لهم في الآخرة لو لم يكتموا «فما أصبرهم على النار» أي ما أشد صبرهم وهو تعجب للمؤمنين من ارتكابهم موجباتها من غير مبالاة وإلا فأيُّ صبر لهم.'),
(183, 2, 176, '«ذلك» الذي ذكر من أكلهم النار وما بعده «بأن» بسبب أن «الله نزَّل الكتاب بالحق» متعلق بنزل فاختلفوا فيه حيث آمنوا ببعضه وكفروا ببعضه بكتمه «وإن الذين اختلفوا في الكتاب» بذلك وهم اليهود وقيل المشركون في القرآن حيث قال بعضهم شعر وبعضهم سحر وبعضهم كهانة «لفي شقاق» خلاف «بعيد» عن الحق.'),
(184, 2, 177, '«ليس البر أن تولوا وجوهكم» في الصلاة «قبل المشرق والمغرب» نزل ردا على اليهود والنصارى حيث زعموا ذلك «ولكن البرَّ» أي ذا البر وقرئ بفتح الباء أي البار «من آمن بالله واليوم الآخر والملائكة والكتاب» أي الكتب «والنبيين و آتى المال على» مع «حبه» له «ذوي القربى» القرابة «واليتامى والمساكين وابن السبيل» المسافر «والسائلين» الطالبين «وفي» فك «الرقاب» المكاتبين والأسرى «وأقام الصلاة وآتى الزكاة» المفروضة وما قبله في التطوع «والموفون بعهدهم إذا عاهدوا» الله أو الناس «والصابرين» نصب على المدح «في البأساء» شدة الفقر «والضراء» المرض «وحين البأس» وقت شدة القتال في سبيل الله «أولئك» الموصوفون بما ذكر «الذين صدقوا» في إيمانهم أو ادعاء البر «وأولئك هم المتقون» الله.'),
(185, 2, 178, '«يا أيها الذين منوا كُتب» فرض «عليكم القصاص» المماثلة «في القتلى» وصفا وفعلا «الحر» ولا يقتل بالعبد «والعبد بالعبد والأنثى بالأنثى» وبيَّنت السنة أن الذكر يقتل بها وأنه تعتبر المماثلة في الدين فلا يقتل مسلم ولو عبدا بكافر ولو حرا «فمن عفي له» من القاتلين «من» دم «أخيه» المقتول «شيء» بأن ترك القصاص منه، وتنكيرُ شيء يفيد سقوط القصاص بالعفو عن بعضه ومن بعض الورثة وفي ذكر أخيه تعطُّف داع إلى العفو وإيذان بأن القتل لا يقطع أخوة الإيمان ومن مبتدأ شرطية أو موصولة والخبر «فاتِّباع» أي فعل العافي إتباع للقاتل «بالمعروف» بأن يطالبه بالدية بلا عنف، وترتيب الإتباع على العفو يفيد أن الواجب أحدهما وهو أحد قولي الشافعي والثاني الواجب القصاص والدية بدل عنه فلو عفا ولم يسمها فلا شيء ورجح «و» على القاتل «أداء» الدية «إليه» أي العافي وهو الوارث «بإحسان» بلا مطل ولا بخس «ذلك» الحكم المذكور من جواز القصاص والعفو عنه على الدية «تخفيف» تسهيل «من ربكم» عليكم «ورحمة» بكم حيث وسَّع في ذلك ولم يحتم واحدا منهما كما حتم على اليهود القصاص وعلى النصارى الدية «فمن اعتدى» ظلم القاتل بأن قتله «بعد ذلك» أي العفو «فله عذاب أليم» مؤلم في الآخرة بالنار أو في الدنيا بالقتل.'),
(186, 2, 179, '«ولكم في القصاص حياة» أي بقاء عظيم «يا أولي الألباب» ذوي العقول لأن القاتل إذا علم أنه يُقتل ارتدع فأحيا نفسه ومن أراد قتله فشرع «لعلكم تتقون» القتل مخافة القود.'),
(187, 2, 180, '«كتب» فرض «عليكم إذا حضر أحدكم الموت» أي أسبابه «إن ترك خيرا» مالا «الوصية» مرفوع بكتب ومتعلق بإذا إن كانت ظرفية ودال على جوابها إن كانت شرطية وجواب إن أي فليوص «للوالدين والأقربين بالمعروف» بالعدل بأن لا يزيد على الثلث ولا يفضل الغني «حقا» مصدر مؤكد لمضمون الجملة قبله «على المتقين» الله وهذا منسوخ بآية الميراث وبحديث: (لا وصية لوارث رواه الترمذي).'),
(188, 2, 181, '«فمن بدَّله» أي الإيصاء من شاهد ووصي «بعد ما سمعه» علمه «فإنما إثمه» أي الإيصاء المبدل «على الذين يبدلونه» فيه إقامة الظاهر مقام المضمر «إن الله سميع» لقول الموصي «عليم» بفعل الوصي فمجاز عليه.'),
(189, 2, 182, '«فمن خاف من موص» مخففا ومثقلا «جنفا» ميلا عن الحق خطأ «أو إثما» بأن تعمَّد ذلك بالزيادة على الثلث أو تخصيص غني مثلا «فأصلح بينهم» بين الموصي والموصى له بالأمر بالعدل «فلا إثم عليه» في ذلك «إن الله غفور رحيم».'),
(190, 2, 183, '«يا أيها الذين أمنوا كُتب» فرض «عليكم الصيام كما كتب على الذين من قبلكم» من الأمم «لعلكم تتقون» المعاصي فإنه يكسر الشهوة التي هي مبدؤها.'),
(191, 2, 184, '«أيَّاما» نصب بالصيام أو يصوموا مقدرا «معدودات» أي قلائل أو مؤقتات بعدد معلوم وهي رمضان كما سيأتي وقلَّله تسهيلا على المكلفين «فمن كان منكم» حين شهوده «مريضا أو على سفر» أي مسافرا سفر القصر وأجهده الصوم في الحالين فأفطر «فعدَّة» فعليه عدة ما أفطر «من أيام أخر» يصومها بدله «وعلى الذين» لا «يطيقونه» لكبر أو مرض لا يرجى برؤه «فدية» هي «طعام مسكين» أي قدر ما يأكله في يومه وهو مدٌ من غالب قوت البلد لكل يوم، وفي قراءة بإضافة فدية وهي للبيان وقيل لا غير مقدرة وكانوا مخيرين في صدر الإسلام بين الصوم والفدية ثم نسخ بتعيين الصوم بقوله من شهد منكم الشهر فليصمه، قال ابن عباس: إلا الحامل والمرضع إذا أفطرتا خوفا على الولد فإنها باقية بلا نسخ في حقهما «فمن تطوع خيرا» بالزيادة على القدر المذكور في الفدية «فهو» أي التطوع «خير له، وأن تصوموا» مبتدأ خبره «خير لكم» من الإفطار والفدية «إن كنتم تعلمون» أنه خير لكم فافعلوه.'),
(192, 2, 185, 'تلك الأيام «شهر رمضان الذي أنزل فيه القرآن» من اللوح المحفوظ إلى السماء الدنيا في ليلة القدر منه «هدى» حال هاديا من الضلالة «للناس وبينات» آيات واضحات «من الهدى» بما يهدي إلى الحق من الأحكام «و» من «الفرقان» مما يفرق بين الحق والباطل «فمن شهد» حضر «منكم الشهر فليصمه ومن كان مريضا أو على سفر فعدة من أيام أخر» تقدم مثله وكرر لئلا يتوهم نسخه بتعميم من شهد «يريد الله بكم اليسر ولا يريد بكم العسر» ولذا أباح لكم الفطر في المرض والسفر ولكون ذلك في معنى العلة أيضا للأمر بالصوم عطف عليه «ولتكملوا» بالتخفيف والتشديد «العدة» أي عدة صوم رمضان «ولتكبروا الله» عند إكمالها «على ما هداكم» أرشدكم لمعالم دينه «ولعلكم تشكرون» الله على ذلك.'),
(193, 2, 186, 'وسأل جماعة النبي محمد صلى الله عليه وسلم أقريب ربُّنا فنناجيه أم بعيد فنناديه: فنزل «وإذا سألك عبادي عني فإني قريب» منهم بعلمي فأخبرهم بذلك «أجيب دعوة الداع إذا دعان» بإنالته ما سأل «فليستجيبوا لي» دعائي بالطاعة «وليؤمنوا» يداوموا على الإيمان «بي لعلهم يرشدون» يهتدون.'),
(194, 2, 187, '«أُحلَّ لكم ليلة الصيام الرفث» بمعنى الإفضاء «إلى نسائكم» بالجماع، نزل نسخا لما كان في صدر الإسلام على تحريمه وتحريم الأكل والشرب بعد العشاء «هن لباس لكم وأنتم لباس لهن» كناية عن تعانقهما أو احتياج كل منهما إلى صاحبه «علم الله أنكم كنتم تختانون» تخونون «أنفسكم» بالجماع ليلة الصيام وقع ذلك لعمر وغيره واعتذروا إلى النبي صلى الله عليه وسلم «فتاب عليكم» قبل توبتكم «وعفا عنكم فالآن» إذ أحل لكم «باشروهن» جامعوهن «وابتغوا» اطلبوا «ما كتب الله لكم» أي أباحه من الجماع أو قدره من الولد «وكلوا واشربوا» الليل كله «حتى يتبيَّن» يظهر «لكم الخيط الأبيض من الخيط الأسود من الفجر» أي الصادق بيان للخيط الأبيض وبيان الأسود محذوف أي من الليل شبه ما يبدو من البياض وما يمتد معه من الغبش بخيطين أبيض وأسود في الامتداد «ثم أتُّموا الصيام» من الفجر «إلى الليل» أي إلى دخوله بغروب الشمس «ولا تباشروهن» أي نساءكم «وأنتم عاكفون» مقيمون بنية الاعتكاف «في المساجد» متعلق بعاكفون نهيٌ لمن كان يخرج وهو معتكف فيجامع امرأته ويعود «تلك» الأحكام المذكورة «حدود الله» حدَّها لعباده ليقفوا عندها «فلا تقربوها» أبلغ من لا تعتدوها المعبر به في آية أخرى «كذلك» كما بيَّن لكم ما ذكر «يُبيِّن الله آياته للناس لعلهم يتقون» محارمه.'),
(195, 2, 188, '«ولا تأكلوا أموالكم بينكم» أي يأكل بعضكم مال بعض «بالباطل» الحرام شرعا كالسرقة والغصب «و» لا «تُدلوا» تلقوا «بها» أي بحكومتها أو بالأموال رشوة «إلى الحكام لتأكلوا» بالتحاكم «فريقا» طائفة «من أموال الناس» متلبسين «بالإثم وأنتم تعلمون» أنكم مبطلون.'),
(196, 2, 189, '«يسألونك» يا محمد «عن الأهلة» جمع هلال لمَ تبدو دقيقة ثم تزيد حتى تمتلئ نورا ثم تعود كما بدت ولا تكون على حالة واحدة كالشمس «قل» لهم «هي مواقيت» جمع ميقات «للناس» يعلمون بها أوقات زرعهم ومتاجرهم وعُدد نسائهم وصيامهم وإفطارهم «والحج» عطف على الناس أي يعلم بها وقته فلو استمرت على حالة لم يعرف ذلك «وليس البر بأن تأتوا البيوت من ظهورها» في الإحرام كغيره بأن تنقبوا فيها نقبا تدخلون منه وتخرجون وتتركوا الباب وكانوا يفعلون ذلك ويزعمونه برّا «ولكنَّ البرَّ» أي ذا البِر «من اتقى» الله بترك مخالفته «وأتوا البيوت من أبوابها» في الإحرام «واتقوا الله لعلكم تفلحون» تفوزون.'),
(197, 2, 190, 'ولما صُدَّ محمد صلى الله عليه وسلم عن البيت عام الحديبية وصالح الكفار على أن يعود العام القابل ويُخلوا له مكة ثلاثة أيام وتجهز لعمرة القضاء وخافوا أن لا تفي قريش ويقاتلوهم وكره المسلمون قتالهم في الحرم والإحرام والشهر الحرام نزل «وقاتلوا في سبيل الله» أي لإعلاء دينه «الذين يقاتلونكم» الكفار «ولا تعتدوا» عليهم بالابتداء بالقتال «إن الله لا يحب المعتدين» المتجاوزين ما حد لهم وهذا منسوخ بآية براءة أو بقوله.'),
(198, 2, 191, '«واقتلوهم حيث ثقفتموهم» وجدتموهم «وأخرجوهم من حيث أخرجوكم» أي من مكة وقد فعل بهم ذلك عام الفتح «والفتنة» الشرك منهم «أشد» أعظم «من القتل» لهم في الحرم أو الإحرام والذي استعظمتموه «ولا تقاتلوهم عند المسجد الحرام» أي في الحرم «حتى يقاتلوكم فيه فإن قاتلوكم» فيه «فاقتلوهم» فيه، وفي قراءة بلا ألف في الأفعال الثلاثة «كذلك» القتل والإخراج «جزاء الكافرين».'),
(199, 2, 192, '«فإن انتهوا» عن الكفر وأسلموا «فإن الله غفور» لهم «رحيم» بهم.'),
(200, 2, 193, '«وقاتلوهم حتى لا تكون» توجد «فتنة» شرك «ويكون الدين» العبادة «لله» وحده لا يعبد سواه «فإن انتهوا» عن الشرك فلا تعتدوا عليهم دل على هذا «فلا عدوان» اعتداء بقتل أو غيره «إلا على الظالمين» ومن انتهى فليس بظالم فلا عدوان عليه.'),
(201, 2, 194, '«الشهر الحرام» المحرم مقابل «بالشهر الحرام» فكلما قاتلوكم فيه فاقتلوهم في مثله رد لاستعظام المسلمين ذلك «والحرمات» جمع حرمة ما يجب احترامه «قصاص» أي يقتص بمثلها إذا انتهكت «فمن اعتدى عليكم» بالقتال في الحرم أو الإحرام أو الشهر الحرام «فاعتدوا عليه بمثل ما اعتدى عليكم» سمى مقابلته اعتداء لشبهها بالمقابل به في الصورة «واتقوا الله» في الانتصار وترك الاعتداء «واعلموا أن الله مع المتقين» بالعون والنصر.'),
(202, 2, 195, '«وأنفقوا في سبيل الله» طاعته بالجهاد وغيره «ولا تلقوا بأيديكم» أي أنفسكم والباء زائدة «إلى التهلكة» الهلاك بالإمساك عن النفقة في الجهاد أو تركه لأنه يقوي العدو عليكم «وأحسنوا» بالنفقة وغيرها «إن الله يحب المحسنين» أي يثيبهم.'),
(203, 2, 196, '«وأتموا الحج والعمرة لله» أدُّوهما بحقوقهما «فإن أُحصرتم» مُنعتم عن إتمامها بعدوِّ «فما استيسر» تيسَّر «من الهدي» عليكم وهو شاة «ولا تحلقوا رؤوسكم» أي لا تتحللوا «حتى يبلغ الهدي» المذكور «محله» حيث يحل ذبحه وهو مكان الإحصار عند الشافعي فيذبح فيه بنية التحلل ويفرَّق على مساكينه ويحلق وبه يحصل التحلل «فمن كان منكم مريضا أو به أذى من رأسه» كقمل وصداع فحلق في الإحرام «ففدية» عليه «من صيام» ثلاثة أيام «أو صدقة» بثلاثة أصوع من غالب قوت البلد على ستة مساكين «أونسك» أي ذبح شاة وأو للتخيير وألحق به من حلق لغير عذر أنه أولى بالكفارة وكذا من استمتع بغير الحلق كالطيب واللبس والدهن لعذر أو غيره «فإذا أمنتم» العدو بأن ذهب أو لم يكن «فمن تمتع» استمتع «بالعمرة» أي بسبب فراغه منها بمحظورات الإحرام «إلى الحج» أي إلى الإحرام به بأن يكون أحرم بها في أشهره «فما استيسر» تيسر «من الهدي» عليه وهو شاة يذبحها بعد الإحرام به والأفضل يوم النحر «فمن لم يجد» الهدي لفقده أو فقد ثمنه «فصيامُ» أي فعليه صيام «ثلاثة أيام في الحج» أي في حال الإحرام به فيجب حينئذ أن يُحْرمَ قبل السابع من ذي الحجة والأفضل قبل السادس لكراهة صوم يوم عرفة ولا يجوز صومها أيام التشريق على أصح قولي الشافعي «وسبعة إذا رجعتم» إلى وطنكم مكة أو غيرها وقيل إذا فرغتم من أعمال الحج وفيه التفات عن الغيبة «تلك عشرة كاملة» جملة تأكيد لما قبلها «ذلك» الحكم المذكور من وجوب الهدي أو الصيام على من تمتع «لمن لم يكن أهله حاضري المسجد الحرام» بأن لم يكونوا على دون مرحلتين من الحرم عند الشافعي فإن كان فلا دم عليه ولا صيام وإن تمتع فعليه ذلك وهو أحد وجهين عند الشافعي والثاني لا والأهل كناية عن النفس وألحق بالتمتع فيما ذكر بالسنة القارن وهو من أحرم بالعمرة والحج معا أو يدخل الحج عليها قبل الطواف «واتقوا الله» فيما يأمركم به وينهاكم عنه «واعلموا أن الله شديد العقاب» لمن خالفه.'),
(204, 2, 197, '«الحج» وقته «أشهر معلومات» شوال وذو القعدة وعشر ليال من ذي الحجة وقيل كله «فمن فرض» على نفسه «فيهن الحج» بالإحرام به «فلا رفث» جماع فيه «ولا فسوق» معاص «ولا جدالَ» خصام «في الحج» وفي قراءة بفتح الأولين والمراد في الثلاثة النهي «وما تفعلوا من خير» كصدقة «يعلمه الله» فيجازيكم به، ونزل في أهل اليمن وكانوا يحجون بلا زاد فيكون كلاًّ على الناس: «وتزودوا» ما يبلغكم لسفركم «فإن خير الزاد التقوى» ما يُتَّقى به سؤال الناس وغيره «واتقون يا أولي الألباب» ذوي العقول.'),
(205, 2, 198, '«ليس عليكم جناح» في «أن تبتغوا» تطلبوا «فضلا» رزقا «من ربكم» بالتجارة في الحج نزل ردا لكراهيتهم ذلك «فإذا أفضتم» دفعتم «من عرفات» بعد الوقوف بها «فاذكروا الله» بعد المبيت بمزدلفة بالتلبية والتهليل والدعاء «عند المشعر الحرام» هو جبل في آخر المزدلفة يقال له قزح وفي الحديث أنه صلى الله عليه وسلم وقف به يذكر الله ويدعو حتى أسفر جدا رواه مسلم «واذكروه كما هداكم» لمعالم دينه ومناسك حجه والكاف للتعليل «وإن» مخففة «كنتم من قبله» قبل هداه «لمن الضالين».'),
(206, 2, 199, '«ثم أفيضوا» يا قريش «من حيث أفاض الناس» أي من عرفة بأن تقفوا بها معهم وكانوا يقفون بالمزدلفه ترفعا عن الوقوف معهم وثم للترتيب في الذكر «واستغفروا الله» من ذنوبكم إن الله غفور» للمؤمنين «رحيم» بهم.'),
(207, 2, 200, '«فإذا قضيتم» أدَّيتم «مناسككم» عبادات حجكم بأن رميتم جمرة العقبة وطفتم واستقررتم بمنى «فاذكروا الله» بالتكبير والثناء «كذكركم آباءكم» كما كنتم تذكرونهم عند فراغ حجكم بالمفاخرة «أو أشد ذكرا» من ذكركم إياهم ونصب أشد على الحال من ذكر المنصوب باذكروا إذ لو تأخر عنه لكان صفة له «فمن الناس من يقول ربنا آتنا» نصيبا «في الدنيا» فيؤتاه فيها «وماله في الآخرة من خلاق» نصيب.'),
(208, 2, 201, '«ومنهم من يقول ربنا آتنا في الدنيا حسنة» نعمة «وفي الآخرة حسنة» هي الجنة «وقنا عذاب النار» بعدم دخولها وهذا بيان لما كان عليه المشركون ولحال المؤمنين والقصد به الحث على طلب خير الدارين كما وعد بالثواب عليه بقوله.'),
(209, 2, 202, '«أولئك لهم نصيب» ثواب «مـ» من أجل «ما كسبوا» عملوا من الحج والدعاء «والله سريع الحساب» يحاسب الخلق كلهم في قدر نصف نهار من أيام الدنيا لحديث بذلك.'),
(210, 2, 203, '«واذكروا الله» بالتكبير عند رمي الجمرات «في أيام معدودات» أي أيام التشريق الثلاثة «فمن تعجل» أي استعجل بالنفر من منى «في يومين» أي في ثاني أيام التشريق بعد رمي جماره «فلا إثم عليه» بالتعجيل «ومن تأخر» بها حتى بات ليلة الثالث ورمى جماره «فلا إثم عليه» بذلك أي هم مخيَّرون في ذلك ونفي الإثم «لمن اتقى» الله في حجه لأنه الحاج في الحقيقة «واتقوا الله واعلموا أنكم إليه تحشرون» في الآخرة فيجازيكم بأعمالكم.'),
(211, 2, 204, '«ومن الناس من يعجبك قوله في الحياة الدنيا» ولا يعجبك في الآخرة مخالفته لاعتقاده «ويُشهد الله على ما في قلبه» أنه موافق لقوله «وهو ألدُّ الخصام» شديد الخصومة لك ولأتباعك لعداوته لك وهو الأخنس بن شريق كان منافقاً حلو الكلام للنبي صلى الله عليه وسلم يحلف أنه مؤمن به ومحب له فيدنى مجلسه فأكذبه الله في ذلك ومر بزرع وَحُمُر لبعض المسلمين فأحرقه وعقرها ليلا كما قال تعالى.'),
(212, 2, 205, '«وإذا تولى» انصرف عنك «سعى» مشى «في الأرض ليفسد فيها ويهلك الحرث والنسل» من جملة الفساد «والله لا يحب الفساد» أي لا يرضى به.'),
(213, 2, 206, '«وإذا قيل له اتق الله» في فعلك «أخذته العزة» حملته الأنفة والحمية على العمل «بالإثم» الذي أُمر باتقائه «فحسبه» كافيه «جهنم ولبئس المهاد» الفراش هي.'),
(214, 2, 207, '«ومن الناس من يشري» يبيع «نفسه» أي يبذلها في طاعة الله «ابتغاء» طلب «مرضات الله» رضاه، وهو صهيب لما آذاه المشركون هاجر إلى المدينة وترك لهم ماله «والله رءووف بالعباد» حيث أرشدهم لما فيه رضاه.'),
(215, 2, 208, 'ونزل في عبد الله بن سلام وأصحابه لما عظموا السبت وكرهوا الإبل بعد الإسلام «يا أيها الذين آمنوا ادخلوا في السَّلم» بفتح السين وكسرها الإسلام «كافة» حال من السلم أي في جميع شرائعه «ولا تتبعوا خطوات» طرق «الشيطان» أي تزيينه بالتفريق «إنه لكم عدو مبين» بيِّن العداوة.'),
(216, 2, 209, '«فإن زللتم» ملتم عن الدخول في جميعه «من بعد ما جاءتكم البينات» الحجج الظاهرة على أنه حق «فاعلموا أن الله عزيز» لا يعجزه شيء عن انتقامه منكم «حكيم» في صنعه.'),
(217, 2, 210, '«هل» ما «ينظرون» ينتظر التاركون الدخول فيه «إلا أن يأتيهم الله» أي أمره كقوله أو يأتي أمر ربك أي عذابه «في ظلل» جمع ظلة «من الغمام» السحاب «والملائكةُ وقضي الأمر» تم أمر هلاكهم «وإلى الله ترجع الأمور» بالبناء للمفعول والفاعل في الآخرة فيجازي كلا بعمله.'),
(218, 2, 211, '«سل» يا محمد «بني إسرائيل» تبكيتا «كم آتيناهم» كم استفهامية معلقة سل عن المفعول الثاني وهي ثاني مفعول آتينا ومميزها «من آية بينة» ظاهرة كفلق البحر وإنزال المن والسلوى فبدَّلوها كفرا «ومن يبدِّل نعمه الله» أي ما أنعم به عليه من الآيات لأنها سبب الهداية «من بعد ما جاءته» كفرا «فإن الله شديد العقاب» له.'),
(219, 2, 212, '«زُيِّن للَّذين كفروا» من أهل مكة «الحياة الدنيا» بالتمويه فأحبوها «و» هم «يسخرون من الذين آمنوا» لفقرهم كبلال وعمَّار وصهيب أي يستهزءون بهم ويتعالوْن عليهم بالمال «والذين اتقوا» الشرك وهم هؤلاء «فوقهم يوم القيامة والله يرزق من يشاء بغير حساب» أي رزقا واسعا في الآخرة أو الدنيا بأن يملك المسخور منهم أموال الساخرين ورقابهم.'),
(220, 2, 213, '«كان الناس أمة واحدة» على الإيمان فاختلفوا بأن آمن بعض وكفر بعض «فبعث الله النبيين» إليهم «مبشِّرين» من آمن بالجنة «ومنذرين» من كفر بالنار «وأنزل معهم الكتاب» بمعنى الكتب «بالحق» متعلق بأنزل «ليحكم» به «بين الناس فيما اختلفوا فيه» من الدين «وما اختلف فيه» أي الدين «إلا الذين أوتوه» أي الكتاب فآمن بعض وكفر بعض «من بعد ما جاءتهم البينات» الحجج الظاهرة على التوحيد ومن متعلقة باختلاف وهي وما بعدها مقدم على الاستثناء في المعنى «بغيا» من الكافرين «بينهم فهدى الله الذين آمنوا لما اختلفوا فيه من» للبيان «الحق بإذنه» بإرادته «والله يهدي من يشاء» هدايته «إلى صراط مستقيم» طريق الحق.'),
(221, 2, 214, 'ونزل في جهد أصاب المسلمين «أم» بل أ «حسبتم أن تدخلوا الجنة ولَّما» لم «يأتكم مثل» شبه ما أتى «الذين خلوا من قبلكم» من المؤمنين من المحن فتصبروا كما صبروا «مسَّتهم» جملة مستأنفة مبينة ما قبلها «البأساء» شدة الفقر «والضراء» المرض «وزُلزلوا» أزعجوا بأنواع البلاء «حتى يقول» بالنصب والرفع أي قال «الرسول والذين آمنوا معه» استبطاء للنصر لتناهي الشدة عليهم «متى» يأتي «نصر الله» الذي وعدنا فأجيبوا من قِبَل الله «ألا إن نصر الله قريب» إتيانه.'),
(222, 2, 215, '«يسألونك» يا محمد «ماذا ينفقون» أي الذي ينفقونه والسائل عمرو بن الجموح وكان شيخاً ذا مال فسأل النبي صلى الله عليه وسلم عما ينفق وعلى من ينفق «قل» لهم «ما أنفقتم من خير» بيان لما شامل للقليل والكثير وفيه بيان المنفق الذي هو أحد شقي السؤال وأجاب عن المصرف الذي هو الشق الآخر بقوله: «فللوالدين والأقربين واليتامى والمساكين وابن السبيل» أي هم أولى به «وما تفعلوا من خير» إنفاق أو غيره «فإن الله به عليم» فمجاز عليه.'),
(223, 2, 216, '«كُتِب» فرض «عليكم القتال» للكفار «وهو كُرْهٌ» مكروه «لكم» طبعا لمشقته «وعسى أن تكرهوا شيئا وهو خير لكم وعسى أن تحبوا شيئا وهو شر لكم» لميل النفس إلى الشهوات الموجبة لهلاكها ونفورها عن التكليفات الموجبة لسعادتها فلعل لكم في القتال وإن كرهتموه خيرا لأن فيه إما الظفر والغنيمة أو الشهادة والأجر وفي تركه وإن أحببتموه شرا لأن فيه الذل والفقر وحرمان الأجر «والله يعلم» ما هو خير لكم «وأنتم لا تعلمون» ذلك فبادروا إلى ما يأمركم به.'),
(224, 2, 217, 'وأرسل النبي صلى الله عليه وسلم أول سراياه وعليها عبد الله بن جحش فقاتلوا المشركين وقتلوا ابن الحضرمي آخر يوم من جمادى الآخرة والتبس عليهم برجب فعيرهم الكفار باستحلاله فنزل: «يسألونك عن الشهر الحرام» المحرم «قتال فيه» بدل اشتمال «قل» لهم «قتال فيه كبير» عظيم وزرا مبتدأ وخبر «وصد» مبتدأ منع للناس «عن سبيل الله» دينه «وكفر به» بالله «و» صد عن «المسجد الحرام» أي مكة «وإخراج أهله منه» وهم النبي صلى الله عليه وسلم والمؤمنون وخبر المبتدأ «أكبر» أعظم وزرا «عند الله» من القتال فيه «والفتنة» الشرك منكم «أكبر من القتل» لكم فيه «ولا يزالون» أي الكفار «يقاتلونكم» أيها المؤمنون «حتى» كي «يردوكم» إلى الكفر «إن استطاعوا ومن يرتدِد منكم عن دينه فيمت وهو كافر فأولئك حبطت» بطلت «أعمالهم» الصالحة «في الدنيا والآخرة» فلا اعتداد بها ولا ثواب عليها والتقيد بالموت عليه يفيد أنه لو رجع إلى الإسلام لم يبطل عمله فيثاب عليه ولا يعيده كالحج مثلا وعليه الشافعي «وأولئك أصحاب النار هم فيها خالدون».'),
(225, 2, 218, 'ولما ظن السرية أنهم إن سلموا من الإثم فلا يحصل لهم أجر نزل «إن الذين آمنوا والذين هاجروا» فارقوا أوطانهم «وجاهدوا في سبيل الله» لإعلاء دينه «أولئك يرجون رَحْمَتَ الله» ثوابه «والله غفور» للمؤمنين «رحيم» بهم.'),
(226, 2, 219, '«يسألونك عن الخمر والميسر» القمار ما حكمهما «قل» لهم «فيهما» أي في تعاطيهما «إثم كبير» عظيم وفي قراءة بالمثلثة لما يحصل بسببهما من المخاصمة والمشاتمة وقول الفحش «ومنافع للناس» باللذة والفرح في الخمر وإصابة المال بلا كد في الميسر «وإثمهما» أي ما ينشأ عنهما من المفاسد «أكبر» أعظم «من نفعهما» ولما نزلت شربها قوم وامتنع عنها آخرون إلى أن حرمتها آية المائدة «ويسألونك ماذا ينفقون» أي ما قدره «قل» أنفقوا «العفو» أي الفاضل عن الحاجة ولا تنفقوا ما تحتاجون إليه وتضيعوا أنفسكم وفي قراءة بالرفع بتقدير هو «كذلك» أي كما بين لكم ما ذكر «يبين الله لكم الآيات لعلكم تتفكرون».'),
(227, 2, 220, '«في» أمر «الدنيا والآخرة» فتأخذون بالأصلح لكم فيهما «ويسألونك عن اليتامى» وما يلقونه من الحرج في شأنهم فإن واكلوهم يأثموا وإن عزلوا ما لهم من أموالهم وصنعوا لهم طعاما وحدهم فحرج «قل إصلاح لهم» في أموالهم بتنميتها ومداخلتكم «خير» من ترك ذلك «وإن تخالطوهم» أي تخلطوا نفقتكم بنفقتهم «فإخوانكم» أي فهم إخوانكم في الدين ومن شأن الأخ أن يخالط أخاه أي فلكم ذلك «والله يعلم المفسد» لأموالهم بمخالطته «من المصلح» بها فيجازي كلا منهما «ولو شاء الله لأعنتكم» لضيق عليكم بتحريم المخالطة «أن الله عزيز» غالب على أمره «حكيم» في صنعه.'),
(228, 2, 221, '«ولا تنكحوا» تتزوجوا أيها المسلمون «المشركات» أي الكافرات «حتى يؤمنّ ولأمة مؤمنة خير من مشركة» حرة لأن سبب نزولها العيب على من تزوج أمة وترغيبه في نكاح حرة مشركة «ولو أعجبتكم» لجمالها ومالها وهذا مخصوص بغير الكتابيات بآية (والمحصنات من الذين أوتوا الكتاب) «ولا تُنكحوا» تُزوجوا «المشركين» أي الكفار المؤمنات «حتى يؤمنوا ولعبد مؤمن خير من مشرك ولو أعجبكم» لماله وجماله «أولئك» أي أهل الشرك «يدعون إلى النار» بدعائهم إلى العمل الموجب لها فلا تليق مناكحهم «والله يدعو» على لسان رسله «إلى الجنة والمغفرة» أي العمل الموجب لهما «بإذنه» بإرادته فتجب إجابته بتزويج أوليائه «ويبن آياته للناس لعلهم يتذكرون» يتعظون.'),
(229, 2, 222, '«ويسألونك عن المحيض» أي الحيض أو مكانه ماذا يفعل بالنساء فيه «قل هو أذى» قذر أو محله «فاعتزلوا النساء» اتركوا وطأهن «في المحيض» أي وقته أو مكانه «ولا تقربوهن» بالجماع «حتى يَطْهُرن» بسكون الطاء وتشديدها والهاء وفيه إدغام التاء في الأصل في الطاء أي يغتسلن بعد انقطاعه «فإذا تطهرن فأتوهن» بالجماع «من حيث أمركم الله» بتجنبه في الحيض وهو القبل ولا تعدوه إلى غيره «إن الله يحب» يثيب ويكرم «التوابين» من الذنوب «ويحب المتطهرين» من الأقذار.'),
(230, 2, 223, '«نساؤكم حرث لكم» أي محل زرعكم الولد «فأتوا حرثكم» أي محله وهو القبل «أنَّى» كيف «شئتم» من قيام وقعود واضطجاع وإقبال وإدبار، نزل ردا لقول اليهود: من أتى امرأته في قبلها أي من جهة دبرها جاء الولد أحول «وقدموا لأنفسكم» العمل الصالح كالتسمية عند الجماع «واتقوا الله» في أمره ونهيه «واعلموا أنكم ملاقوه» بالبعث فيجازيكم بأعمالكم «وبشر المؤمنين» الذين اتقوه بالجنة.'),
(231, 2, 224, '«ولا تجعلوا الله» أي الحلف به «عرضة» علة مانعة «لأيمانكم» أي نصيبا لها بأن تكثروا الحلف به «أن» لا «تبروا وتتقوا» فتكره اليمين على ذلك ويسن فيه الحنث ويكفِّر بخلافها على فعل. البر ونحوه فهي طاعة «وتصلحوا بين الناس» المعنى لا تمتنعوا من فعل ما ذكر من البر ونحوه إذا حلفتم عليه بل ائتوه وكفِّروا لأن سبب نزولها الامتناع من ذلك «والله سميع» لأقوالكم «عليم» بأحوالكم.'),
(232, 2, 225, '«لا يؤاخذكم الله باللغو» الكائن «في أيمانكم» وهو ما يسبق إليه اللسان من غير قصد الحلف نحو ألا والله، وبلى و الله فلا إثم فيه ولا كفارة «ولكن يؤاخذكم بما كسبت قلوبكم» أي قصدته من الأيمان إذا حنثتم «والله غفور» لما كان من اللغو «حليم» بتأخير العقوبة عن مستحقها.'),
(233, 2, 226, '«للذين يؤلون من نسائهم» أي يحلفون أن لا يجامعوهن «تربص» انتظار «أربعة أشهر فإن فاءُوا» رجعوا فيها أو بعدها عن اليمين إلى الوطء «فإن الله غفور» لهم ما أتوه من ضرر المرأة بالحلف «رحيم» بهم.'),
(234, 2, 227, '«وإن عزموا الطلاق» أي عليه بأن لا يفيئوا فليوقعوه «فإن الله سميع» لقولهم «عليم» بعزمهم المعنى ليس لهم بعد تربص ما ذكر إلا الفيئة أو الطلاق.'),
(235, 2, 228, '«والمطلقات يتربصن» أي لينتظرن «بأنفسهن» عن النكاح «ثلاثة قروءٍ» تمضي من حين الطلاق، جمع قرء بفتح القاف وهو الطهر أو الحيض قولان وهذا في المدخول بهن أما غيرهن فلا عدة عليهن لقوله: «فما لكم عليهن من عدة» وفي غير الآيسة والصغيرة فعدتهن ثلاثة أشهر والحوامل فعدتهن أن يضعن حملهن كما في سورة الطلاق والإماء فعدتهن قَرءان بالسُّنة «ولا يحل لهن أن يكتمن ما خلق الله في أرحامهن» من الولد والحيض «إن كنّ يؤمن بالله واليوم الآخر وبعولتهن» أزواجهن «أحق بردهن» بمراجعتهن ولو أبين «في ذلك» أي في زمن التربص «إن أرادوا إصلاحا» بينهما لإضرار المرأة وهو تحريض على قصده لا شرط لجواز الرجعة وهذا في الطلاق الرجعي وأحق لا تفضيل فيه إذ لا حق لغيرهم في نكاحهن في العدة «ولهن» على الأزواج «مثل الذي» لهم «عليهن» من الحقوق «بالمعروف» شرعا من حسن العشرة وترك الضرار ونحو ذلك «وللرجال عليهن درجة» فضيلة في الحق من وجوب طاعتهن لهم لما ساقوه من المهر والانفاق «والله عزيز» في ملكه «حكيم» فيما دبره لخلقه.'),
(236, 2, 229, '«الطلاق» أي التطليق الذي يراجع بعده «مرتان» أي اثنتان «فإمساك» أي فعليكم إمساكهن بعده بأن تراجعوهن «بمعروف» من غير ضرار «أو تسريح» أي إرسالهن «بإحسان ولا يحل لكم» أيها الأزواج «أن تأخذوا مما آتيتموهن» من المهور «شيئا» إذا طلقتموهن «إلا أن يخافا» أي الزوجان «أ» ن «لا يقيما حدود الله» أي لا يأتيا بما حده لهما من الحقوق وفي قراءة يخافا بالبناء للمفعول فأن لا يقيما بدل اشتمال من الضمير فيه وقرئ بالفوقانية في الفعلين «فإن خفتم أ» ن «لا يقيما حدود الله فلا جناح عليهما» «فيما افتدت به» نفسها من المال ليطلقها أي لا حرج على الزوج في أخذه ولا الزوجة في بذله «تلك» الأحكام المذكورة «حدود الله فلا تعتدوها ومن يتعدَّ حدود الله فأولئك هم الظالمون».'),
(237, 2, 230, '«فإن طلقها» الزوج بعد الثنتين «فلا تحل له من بعد» بعد الطلقة الثالثة «حتى تنكح» تتزوج «زوجا غيره» ويطأها كما في الحديث رواه الشيخان «فإن طلقها» أي الزوج الثاني «فلا جناح عليهما» أي الزوجة والزوج الأول «أن يتراجعا» إلى النكاح بعد انقضاء العدة «إن ظنا أن يقيما حدود الله وتلك» المذكورات «حدود الله يُبَيِّنها لقوم يعلمون» يتدبرون.'),
(238, 2, 231, '«وإذا طلقتم النساء فبلغن أجلهن» قاربن انقضاء عدتهن «فأمسكوهن» بأن تراجعوهن «بمعروف» من غير ضرر «أو سرحوهن بمعروف» اتركوهن حتى تنقضي عدتهن «ولا تمسكوهن» بالرجعة «ضرارا» مفعول لأجله «لتعتدوا» عليهن بالإلجاء إلى الافتداء والتطليق وتطويل الحبس «ومن يفعل ذلك فقد ظلم نفسه» بتعريضها إلى عذاب الله «ولا تتخذوا آيات الله هزوا» مهزوءا بها بمخالفتها «واذكروا نعمت الله عليكم» بالإسلام «وما أنزل عليكم من الكتاب» القرآن «والحكمة» ما فيه من الأحكام «يعظكم به» بأن تشكروها بالعمل به «واتقوا الله واعلموا أن الله بكل شيء عليم» ولا يخفى عليه شيء.'),
(239, 2, 232, '«وإذا طلقتم النساء فبلغن أجلهن» انقضت عدتهن «فلا تعضلوهن» خطاب للأولياء أي تمنعوهن من «أن ينكحن أزواجهن» المطلقين لهن لأن سبب نزولها أن أخت معقل بن يسار طلقها زوجها فأراد أن يراجعها فمنعها معقل بن يسار كما رواه الحاكم «إذا تراضوا» أي الأزواج والنساء «بينهم بالمعروف» شرعا «ذلك» النهي عن العضل «يوعظ به من كان منكم يؤمن بالله واليوم الآخر» لأنه المنتفع به «ذلكم» أي ترك العضل «أزكى» خير «لكم وأطهر» لكم ولهم لما يُخشى على الزوجين من الريبة بسبب العلاقة بينهما «والله يعلم» ما فيه المصلحة «وأنتم لا تعلمون» ذلك فاتَّبعوا أوامره.'),
(240, 2, 233, '«والوالدات يرضعن» أي ليرضعن «أولادهن حولين» عامين «كاملين» صفة مؤكدة، ذلك «لمن أراد أن يتم الرضاعة» ولا زيادة عليه «وعلى المولود له» أي الأب «رزقهن» إطعام الوالدات «وكسوتهن» على الإرضاع إذا كن مطلقات «بالمعروف» بقدر طاقته «لا تُكلَّفُ نفس إلا وسعها» طاقتها «لا تضار والدة بولدها» أي بسببه بأن تكره على إرضاعه إذا امتنعت «ولا» يضار «مولود له بولده» أي بسببه بأن يكلف فوق طاقته وإضافة الولد إلى كل منهما في الموضعين للاستعطاف «وعلى الوارث» أي وارث الأب وهو الصبي أي على وليه في ماله «مثل ذلك» الذي على الأب للوالدة من الرزق و الكسوة «فأن أرادا» أي الوالدان «فصالا» فطاما له قبل الحولين صادرا «عن تراض» اتفاق «منهما وتشاور» بينهما لتظهر مصلحة الصبي فيه «فلا جناح عليهما» في ذلك «وإن أردتم» خطاب للآباء «أن تسترضعوا أولادكم» مراضع غير الوالدات «فلا جناح عليكم» فيه «إذا سلّمتم» إليهن «ما آتيتم» أي أردتم إيتاءه لهن من الأجرة «بالمعروف» بالجميل كطيب النفس «واتقوا الله واعلموا أن الله بما تعملون بصير» لا يخفى عليه شيء منه.'),
(241, 2, 234, '«والذين يتوفون» يموتون «منكم ويذرون» يتركون «أزواجا يتربصن» أي ليتربصن «بأنفسهن» بعدهم عن النكاح «أربعة أشهر وعشرا» من الليالي وهذا في غير الحوامل أما الحوامل فعدتهن أن يضعن حملهن بآية الطلاق والأمة على النصف من ذلك بالسنة «فإذا بلغن أجلهن» انقضت مدة تربصهن «فلا جناح عليكم» أيها الاولياء «فيما فعلن في أنفسهن» من التزين والتعرض للخطاب «بالمعروف» شرعا «والله بما تعملون خبير» عالم بباطنه كظاهره.'),
(242, 2, 235, '«ولا جُناح عليكم فيما عَرَّضتم» لوحتم «به من خطبة النساء» المتوفى عنهن أزواجهن في العدة كقول الإنسان: مثلا إنك لجميلة ومن يجد مثلك ورب راغب فيك «أو أكننتم» أضمرتم «في أنفسكم» من قصد نكاحهن «علم الله أنكم ستذكرونهن» بالخطبة ولا تصبرون عنهن فأباح لكم التعريض «ولكن لا تواعدوهن سرّاً» أي نكاحاً «إلا» لكن «أن تقولوا قولاً معروفا» أي ما عرف لكم شرعا من التعريض فلكم ذلك «ولا تعزموا عقدة النكاح» أي على عقده «حتى يبلغ الكتاب» أي المكتوب من العدة «أجله» بأن ينتهي «واعلموا أن الله يعلم ما في أنفسكم» من العزم وغيره «فاحذروه» أن يعاقبكم إذا عزمتم «واعلموا أن الله غفور» لمن يحذره «حليم» بتأخير العقوبة عن مستحقها.'),
(243, 2, 236, '«لا جُناح عليكم إن طلقتم النساء ما لم تمسوهن» وفي قراءة «تُماسُّوهُنَّ» أي تجامعوهن «أو» لم «تفرضوا لهن فريضة» مهرا وما مصدرية ظرفية أي لا تبعة عليكم في الطلاق زمن عدم المسيس والفرض بإثم ولا مهر فطلقوهن «ومتعوهن» أعطوهن ما يتمنعن به «على الموسع» الغني منكم «قدره وعلى المقتر» الضيِّق الرزق «قدره» يفيد أنه لا نظر إلى قدر الزوجة «متاعا» تمتيعا «بالمعروف» شرعا صفة متاعا «حقا» صفة ثانية أو مصدر مؤكد «على المحسنين» المطيعين.'),
(244, 2, 237, '«وإن طلقتموهن من قبل أن تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم» يجب لهن ويرجع لكم النصف «إلا» لكن «أن يعفون» أي الزوجات فيتركنه «أو يعفو الذي بيده عقدة النكاح» وهو الزوج فيترك لها الكل، وعن ابن عباس: الولي إذا كانت محجورة فلا حرج في ذلك «وأن تعفوا» مبتدأ خبره «أقرب للتقوى ولا تنسوا الفضل بينكم» أي أن يتفضل بعضكم على بعض «إن الله بما تعملون بصير» فيجازيكم به.'),
(245, 2, 238, '«حافظوا على الصلوات» الخمس بأدائها في أوقاتها «والصلاة الوسطى» هي العصر أو الصبح أو الظهر أو غيرها أقوال وأفردها بالذكر لفضلها «وقوموا لله» في الصلاة «قانتين» قيل مطيعين لقوله صلى الله عليه وسلم: كل قنوت في القرآن فهو طاعة، رواه أحمد وغيره، وقيل ساكتين لحديث زيد بن أرقم: كنا نتكلم في الصلاة حتى نزلت فأمرنا بالسكوت ونُهينا عن الكلام رواه الشيخان.'),
(246, 2, 239, '«فإن خفتم» من عدو أو سيل أو سبع «فرجالا» جمع راجل أي مشاة صلوا «أو ركبانا» جمع راكب أي كيف أمكن مستقبلي القبلة أو غيرها ويومئ بالركوع والسجود «فإذا أمنتم» من الخوف «فاذكروا الله» أي صلّوا «كما علمكم ما لم تكونوا تعلمون» قبل تعليمه من فرائضها وحقوقها والكاف بمعنى مثل وما مصدرية أو موصولة.'),
(247, 2, 240, '«والذين يُتوفون منكم ويذرون أزواجا» فليوصوا «وصيةً» وفي قراءة بالرفع أي عليهم «لأزواجهم» وليعطوهن «متاعا» ما يتمتعن به من النفقة والكسوة «إلى» تمام «الحول غير إخراج» حال أي غير مخرجات من مسكنهن «فإن خرجن» بأنفسهن «فلا جناح عليكم» يا أولياء الميت «في ما فعلن في أنفسهن من معروف» شرعا كالتزين وترك الإحداد وقطع النفقة عنها «والله عزيز» في ملكه «حكيم» في صنعه والوصية المذكورة منسوخة بآية الميراث وتربص الحول بآية أربعة أشهر وعشرا السابقة المتأخرة في النزول والسكنى ثابتة لها عند الشافعي رحمه الله.'),
(248, 2, 241, '«وللمطلّقات متاع» يعطينه «بالمعروف» بقدر الإمكان «حقّا» نصب بفعله المقدر «على المتقين» الله تعالى كرره ليعم الممسوسة أيضا إذ الآية السابقة في غيرها.'),
(249, 2, 242, '«كذلك» كما يبين لكم ما ذكر «يبين الله لكم آياته لعلكم تعقلون» تتدبرون.'),
(250, 2, 243, '«ألم تر» استفهام تعجيب وتشويق إلى استماع ما بعده أي ينته علمك «إلى الذين خرجوا من ديارهم وهم ألوف» أربعة أو ثمانية أو عشرة أو ثلاثون أو أربعون أو سبعون ألفا «حذر الموت» مفعول له وهم قوم من بني إسرائيل وقع الطاعون ببلادهم ففروا «فقال لهم الله موتوا» فماتوا «ثم أحياهم» بعد ثمانية أيام أو أكثر بدعاء نبيهم حزقيل بكسر المهملة والقاف وسكون الزاي فعاشوا دهرا عليهم أثر الموت لا يلبسون ثوبا إلا عاد كالكفن واستمرت في أسباطهم «إن الله لذو فضل على الناس» ومنه إحياء هؤلاء «ولكن أكثر الناس» وهم الكفار «لا يشكرون» والقصد من ذكر خبر هؤلاء تشجيع المؤمنين على القتال ولذا عطف عليه.'),
(251, 2, 244, '«وقاتلوا في سبيل الله» أي لإعلاء دينه «واعلموا أن الله سميع» لأقوالكم «عليم» بأحوالكم فمجازيكم.'),
(252, 2, 245, '«من ذا الذي يقرض الله» بإنفاق ماله في سبيل الله «قرضا حسنا» بأن ينفقه لله عز وجل عن طيب قلب «فيضاعفه» وفي قراءة فيضعفه بالتشديد «له أضعافا كثيرة» من عشر إلى أكثر من سبعمائة كما سيأتي «والله يقبض» يمسك الرزق عمن يشاء ابتلاء «ويبسط» يوسعه لمن يشاء امتحانا «وإليه ترجعون» في الآخرة بالبعث فيجازيكم بأعمالكم.'),
(253, 2, 246, '«ألم تر إلى الملأ» الجماعة «من بني إسرائيل من بعد» موت «موسى» أي إلى قصتهم وخبرهم «إذ قالوا لنبي لهم» هو شمويل «ابعث» أقم «لنا ملكا نقاتل» معه «في سبيل الله» تنتظم به كلمتنا ونرجع إليه «قال» النبي لهم «هل عسَيتم» بالفتح والكسر «إن كتب عليكم القتال أ» ن «لا تقاتلوا» خبر عسى والاستفهام لتقرير التوقع بها «قالوا وما لنا أ» ن «لا نقاتل في سبيل الله وقد أُخرجنا من ديارنا وأبنائنا» بسبيهم وقتلهم وقد فعل بهم ذلك قوم جالوت أي لا مانع لنا منه مع وجود مقتضيه قال تعالى: «فلما كُتب عليهم القتال تولوا» عنه وجبنوا «إلا قليلا منهم» وهم الذين عبروا النهر مع طالوت كما سيأتي «والله عليم بالظالمين» فمجازيهم وسأل النبي إرسال ملك فأجابه إلى إرسال طالوت.'),
(254, 2, 247, '«وقال لهم نبيهم إن الله قد بعث لكم طالوت ملكا قالوا أنَى» كيف «يكون له الملك علينا ونحن أحق بالملك منه» لأنه ليس من سبط المملكة ولا النبوة وكان دباغا أو راعيا «ولم يؤت سعة من المال» يستعين بها على إقامة الملك «قال» النبي لهم «إن الله اصطفاه» اختاره للملك «عليكم وزاده بسطة» سعة «في العلم والجسم» وكان أعلم بني إسرائيل يومئذ وأجملهم وأتمهم خلقا «والله يؤتي ملكه من يشاء» إيتاءه لا اعتراض عليه «والله واسع» فضله «عليم» بمن هو أهل له.'),
(255, 2, 248, '«وقال لهم نبيهم» لما طلبوا منه آية على ملكه «إن آية ملكه أن يأتيكم التابوت» الصندوق كان فيه صور الأنبياء أنزله الله على آدم واستمر إليهم فغلبهم العمالقة عليه وأخذوه وكانوا يستفتحون به على عدوهم ويقدمونه في القتال ويسكنون إليه كما قال تعالى «فيه سكينة» طمأنينة لقلوبكم «من ربكم وبقية مما ترك آل موسى وآل هارون» أي تركاه هما، وهي نعلا موسى وعصاه وعمامة هارون وقفيز من المن الذي كان ينزل عليهم ورضاض من الألواح «تحمله الملائكة» حال من فاعل يأتيكم «إن في ذلك لآية لكم» على ملكه «إن كنتم مؤمنين» فحملته الملائكة بين السماء والأرض وهم ينظرون إليه حتى وضعته عند طالوت فأقروا بملكه وتسارعوا إلى الجهاد فاختار من شبابهم سبعين ألفاً.'),
(256, 2, 249, '«فلمَّا فصل» خرج «طالوت بالجنود» من بيت المقدس وكان الحر شديدا وطلبوا منه الماء «قال إن الله مبتليكم» مختبركم «بنهر» ليظهر المطيع منكم والعاصي وهو بين الأردن وفلسطين «فمن شرب منه» أي من مائه «فليس مني» أي من أتباعي «ومن لم يطعمه» يذقه «فإنه مني إلا من اغترف غرفة» بالفتح والضم «بيده» فاكتفى بها ولم يزد عليها فإنه مني «فشربوا منه» لما وافوه بكثرة «إلا قليلا منهم» فاقتصروا على الغرفة روي أنها كفتهم لشربهم ودوابهم وكانوا ثلاثمائة وبضعة عشر رجلا «فلما جاوزه هو والذين آمنوا معه» وهم الذين اقتصروا على الغرفة «قالوا» أي الذين شربوا «لا طاقة» قوة «لنا اليوم بجالوت وجنوده» أي بقتالهم وجبنوا ولم يجاوزوه «قال الذين يظنون» يوقنون «أنهم ملاقوا الله» بالبعث وهم الذين جاوزوه «كم» خبرية بمعني كثير «من فئة» جماعة «قليلة غلبت فئة كثيرة بإذن الله» بإرادته «والله مع الصابرين» بالعون والنصر.'),
(257, 2, 250, '«ولما برزوا لجالوت وجنوده» أي ظهروا لقتالهم وتصافّوا «قالوا ربنا أفرغ» أصبب «علينا صبرا وثبت أقدامنا» بتقوية قلوبنا على الجهاد «وانصرنا على القوم الكافرين».'),
(258, 2, 251, '«فهزموهم» كسروهم «بإذن الله» بإرادته «وقتل داود» وكان في عسكر طالوت «جالوت وآتاه» أي داود «الله الملك» في بني إسرائيل «والحكمة» النبوة بعد موت شمويل وطالوت ولم يجتمعا لأحد قبله «وعلّمه مما يشاء» كصنعة الدروع ومنطق الطير «ولولا دفع الله الناس بعضهم» بدل بعض من الناس «ببعض لفسدت الأرض» بغلبة المشركين وقتل المسلمين وتخريب المساجد «ولكنّ الله ذو فضل على العالمين» فدفع بعضهم ببعض.'),
(259, 2, 252, '«تلك» هذه الآيات «آيات الله نتلوها» نقصها «عليك» يا محمد «بالحق» بالصدق «وإنك لمن المرسلين» التأكيد بأن وغيرها ردٌ لقول الكفار له لست مرسلا.'),
(260, 2, 253, '«تلك» مبتدأ «الرسل» نعت أو عطف بيان والخبر «فضلنا بعضهم على بعض» بتخصيصه بمنقبة ليست لغيره «منهم من كلّم الله» كموسى «ورفع بعضهم» أي محمد صلى الله عليه وسلم «درجات» على غيره بعموم الدعوة وختم النبوة وتفضيل أمته على سائر الأمم والمعجزات المتكاثرة والخصائص العديدة «وآتينا عيسى بن مريم البينات وأيدناه» قويناه «بروح القدس» جبريل يسير معه حيث سار «ولو شاء الله» هدى الناس جميعا «ما اقتتل الذين من بعدهم» بعد الرسل أي أممهم «من بعد ما جاءتهم البينات» لاختلافهم وتضليل بعضهم بعضا «ولكن اختلفوا» لمشيئته ذلك «فمنهم من آمن» ثبت على إيمانه «ومنهم من كفر» كالنصارى بعد المسيح «ولو شاء الله ما اقتتلوا» تأكيد «ولكن الله يفعل ما يريد» من توفيق من شاء وخذلان من شاء.'),
(261, 2, 254, '«يا أيها الذين آمنوا أنفقوا مما رزقناكم» زكاته «من قبل أن يأتي يوم لا بيْعٌ» فداء «فيه ولا خُلَّة» صداقة تنفع «ولا شَفَاعَةَ» بغير إذنه وهو يوم القيامة وفي قراءة برفع الثلاثة «والكافرون» بالله أو بما فرض عليهم «هم الظالمون» لوضعهم أمر الله في غير محله.'),
(262, 2, 255, '«الله لا إله» أي لا معبود بحق في الوجود «إلا هو الحيُّ» الدائم بالبقاء «القيوم» المبالغ في القيام بتدبير خلقه «لاتأخذه سنة» نعاس «ولا نوم له ما في السماوات وما في الأرض» ملكا وخلقا وعبيدا «من ذا الذي» أي لا أحد «يشفع عنده إلا بإذنه» له فيها «يعلم ما بين أيديهم» أي الخلق «وما خلفهم» أي من أمر الدنيا والآخرة «ولا يحيطون بشيء من علمه» أي لا يعلمون شيئا من معلوماته «إلا بما شاء» أن يعلمهم به منها بأخبار الرسل «وسع كرسيه السماوات والأرض» قيل أحاط علمه بهما وقيل ملكه وقيل الكرسي نفسه مشتمل عليهما لعظمته، لحديث: ما السماوات السبع في الكرسى إلا كدارهم سبعة ألقيت في ترس «ولا يئوده» يثقله «حفظهما» أي السماوات والأرض «وهو العلي» فوق خلقه بالقهر «العظيم» الكبير.'),
(263, 2, 256, '«لا إكراه في الدين» على الدخول فيه «قد تبين الرشد من الغي» أي ظهر بالآيات البينات أن الايمان رشد والكفر غي نزلت فيمن كان له من الأنصار اولاد أراد أن يكرههم علي الإسلام «فمن يكفر بالطاغوت» الشيطان أو الأصنام وهو يطلق على المفرد والجمع «ويؤمن بالله فقد استمسك» تمسك «بالعروة الوثقى» بالعقد المحكم «لا انفصام» انقطاع «لها والله سميع» لما يقال «عليم» بما يفعل.'),
(264, 2, 257, '«الله ولي» ناصر «الذين آمنوا يخرجهم من الظلمات» الكفر «إلى النور» الإيمان «والذين كفروا أولياؤهم الطاغوت يخرجونهم من النور إلى الظلمات» ذكر الإخراج إما في مقابلة قوله يخرجهم من الظلمات أو في كل من آمن بالنبي قبل بعثته من اليهود ثم كفر به «أولئك أصحاب النار هم فيها خالدون».'),
(265, 2, 258, '«ألم ترَ إلى الذي حَاًجَّ» جادل «إبراهيم في ربّه» ل «أن آتاه الله الملك» أي حمله بطره بنعمة الله على ذلك وهو نمرود «إذ» بدل من حاج «قال إبراهيم» لما قال له من ربُّك الذي تدعونا إليه: «ربي الذي يحيي ويميت» أي يخلق الحياة والموت في الأجساد «قال» هو «أنا أحيي وأميت» بالقتل والعفو عنه ودعا برجلين فقتل أحدهما وترك الآخر فلما رآه غبيا «قال إبراهيم» منتقلا إلى حجة أوضح منها «فإن الله يأتي بالشمس من المشرق فأت بها» أنت «من المغرب فَبُهت الذي كفر» تحيَّر ودُهش «والله لا يهدي القوم الظالمين» بالكفر إلى محجَّة الاحتجاج.'),
(266, 2, 259, '«أو» رأيت «كالذي» الكاف زائدة «مرَّ على قرية» هي بيت المقدس راكبا على حمار ومعه سلة تين وقدح عصير وهوعزيز «وهي خاوية» ساقطة «على عروشها» سقوطها لما خرَّبها بختنصر «قال أنَّى» كيف «يحيي هذه الله بعد موتها» استعظاما لقدرته تعالى «فأماته الله» وألبثه «مائه عام ثم بعثه» أحياء ليريه كيفية ذلك «قال» تعالى له «كم لبثت» مكثت هنا «قال لبثت يوما أو بعض يوم» لأنه نام أول النهار فقبض وأحيي عند الغروب فظن أنه يوم النوم «قال بل لبثت مائة عام فانظر إلى طعامك» التين «وشرابك» العصير «لم يتسنَّه» لم يتغير مع طول الزمان، والهاء قيل أصل من سانهت وقيل للسكت من سانيت وفي قراءة بحذفها «وانظر إلى حمارك» كيف هو فرآه ميتا وعظامه بيض تلوح فعلْنا ذلك لتعلم «ولنجعلك آية» على البعث «للناس وانظر إلى العظام» من حمارك «كيف ننشزها» نحييها بضم النون وقرئ بفتحها من أنشز ونشز - لغتان - وفي قراءة بضمها والزاي- نحركها ونرفعها- «ثم نكسوها لحما» فنظر إليه وقد تركبت وكسيت لحما ونفخ فيه الروح ونهق «فلما تبيَّن له» ذلك بالمشاهدة «قال أعلم» علم مشاهدة «أن الله على كل شيء قدير» وفي قراءة إعْلَمْ أمر من الله له.'),
(267, 2, 260, '«و» اذكر «إذ قال إبراهيم ربَّ أرني كيف تحيي الموتى قال» تعالى له «أولم تؤمن» بقدرتي على الإحياء سأله مع علمه بإيمانه بذلك ليجيبه بما سأل فيعلم السامعون غرضه «قال بلى» آمنت «ولكن» سألتك «ليطمئن» يسكن «قلبي» بالمعاينة المضمومة إلى الاستدلال «قال فخذ أربعة من الطير فّصُرْهُنَّ إليك» بكسر الصاد وضمها أملهن إليك وقطعهن واخلط لحمهن وريشهن «ثم اجعل على كل جبل» من جبال أرضك «منهن جزءاً ثم ادعهن» إليك «يأتينك سعيا» سريعا «واعلم أن الله عزيز» لا يعجزه شيء «حكيم» في صنعه فأخذ طاووسا ونسرا وغرابا وديكا وفعل بهن ما ذكر وأمسك رءوسهن عنده ودعاهن فتطايرت الأجزاء إلى بعضها حتى تكاملت ثم أقبلت إلى رءوسها.'),
(268, 2, 261, '«مثل» صفة نفقات «الذين ينفقون أموالهم في سبيل لله» أي طاعته «كمَثل حبة أنبتت سبع سنابل في كل سنبلة مائة حبة» فكذلك نفقاتهم تضاعفت لسبعمائة ضعف «والله يضاعف» أكثر من ذلك «لمن يشاء والله واسع» فضله «عليم» بمن يستحق المضاعفة.'),
(269, 2, 262, '«الذين ينفقون أموالهم في سبيل الله ثم لا يُتبِعون ما أنفقوا مَنًّا» على المنفق عليه بقولهم مثلا: قد أحسنت إليه وجبرت حاله «ولا أذًى» له بذكر ذلك إلى من لا يجب وقوفه عليه ونحو «لهم أجرهم» ثواب إنفاقهم «عند ربهم ولا خوف عليهم ولا هم يحزنون» في الآخرة.'),
(270, 2, 263, '«قول معروف» كلام حسن ورد على السائل جميل «ومغفرة» له في إلحاحه «خير من صدقة يتبعها أذًى» بالمن وتعيير له بالسؤال «والله غني» عن صدقة العباد «حليم» بتأخير العقوبة عن المانّ والمؤذي.'),
(271, 2, 264, '«يأيها الذين آمنوا لا تبطلوا صدقاتكم» أي أجورها «بالمن والأذى» إبطالا «كالذي» أي كإبطال نفقة الذي «ينفق ماله رئاء الناس» مرائيا لهم «ولا يؤمن بالله واليوم الآخر» هو المنافق «فمثله كمثل صفوان» حجر أملس «عليه تراب فأصابه وابل» مطر شديد «فتركه صلدا» صلبا أملس لا شيء عليه «لا يقدرون» استئناف لبيان مثل المنافق المنفق رئاء الناس وجمع الضمير باعتبار معنى الذي «على شيء مما كسبوا» عملوا أي لا يجدون له ثوابا في الآخرة كما لا يوجد على الصفوان شئ من التراب الذي كان عليه لإذهاب المطر له «والله لا يهدى القوم الكافرين».'),
(272, 2, 265, '«ومثل» نفقات «الذين ينفقون أموالهم ابتغاء» طلب «مرضات الله وتثبيتا من أنفسهم» أي تحقيقا للثواب عليه بخلاف المنافقين الذين لا يرجونه لإنكارهم له ومَن ابتدائية «كمثل جنة» بستان «بِرُبْوَةٍ» بضم الراء وفتحتها مكان مرتفع مستو «أصابها وابل فآتت» أعطت «أكلَها» بضم الكاف وسكونها ثمرها «ضعفين» مثلَيْ ما يثمر غيرها «فإن لم يصبها وابل فطلٌ» مطر خفيف يصيبها ويكفيها لارتفاعها، المعنى: تثمر وتزكو كثر المطر أم قل فكذلك نفقات من ذكر تزكو عند الله كثرت أم قلت «والله بما تعلمون بصير» فيجازيكم به.'),
(273, 2, 266, '«أيَوَدُّ» أيحب «أحدكم أن تكون له جنة» بستان «من نخيل وأعناب تجري من تحتها الأنهار له فيها» ثمر «من كل الثمرات و» قد «أصابه الكبر» فضعف من الكبر عن الكسب «وله ذُرِّيةٌ ضعفاء» أولاد صغار لا يقدرون عليه «فأصابها إعصار» ريح شديدة «فيه نار فاحترقت» ففقدها أحوج ما كان إليها وبقي هو وأولاده عجزة متحيرين لا حيلة لهم وهذا تمثيل لنفقة المرائي والمانّ في ذهابها وعدم نفعها أحوج ما يكون إليها في الآخرة والاستفهام بمعنى النفي، وعن ابن عباس هو الرجل عمل بالطاعات ثم بعث له الشيطان فعمل بالمعاصي حتى أحرق أعماله «كذلك» كما بين ما ذكر «يبين الله لكم الآيات لعلكم تتفكرون» فتعتبرون.'),
(274, 2, 267, '«يا أيها الذين آمنوا أنفقوا» أي زكوا «من طيبات» جياد «ما كسبتم» من المال «ومـ» ـن طيبات «ما أخرجنا لكم من الأرض» من الحبوب والثمار «ولا تيمموا» تقصدوا «الخبيث» الرديء «منه» أي من المذكور «تنفقونـ» ـه في الزكاة حال من ضمير تيمموا «ولستم بآخذيه» أي الخبيث لو أعطيتموه في حقوقكم «إلا أن تغمضوا فيه» بالتساهل وغض البصر فكيف تؤدون منه حق الله «واعلموا أن الله غني» عن نفقاتكم «حميد» محمود على كل حال.'),
(275, 2, 268, '«الشيطان يعدكم الفقر» يخوفكم به إن تصدقتم فتمسكوا «ويأمركم بالفحشاء» البخل ومنع الزكاة «والله يعدكم» على الإنفاق «مغفرة منه» لذنوبكم «وفضلا» رزقا خلفا منه «والله واسع» فضله «عليم» بالمنفق.'),
(276, 2, 269, '«يؤتي الحكمة» أي العلم النافع المؤدي إلى العمل «من يشاء ومن يُؤت الحكمة فقد أوتي خيرا كثيرا» لمصيره إلى السعادة الأبدية «وما يذَّكَّر» فيه إدغام التاء في الأصل في الذال يتعظ «إلا أولوا الألباب» أصحاب العقول.'),
(277, 2, 270, '«وما أنفقتم من نفقة» أديتم من زكاة أو صدقة «أو نذرتم من نذر» فوفيتم به «فإن الله يعلمه» فيجازيكم عليه «وما للظالمين» بمنع الزكاة والنذر أو بوضع الإنفاق في غير محله من معاصي الله «من أنصار» مانعين لهم من عذابه.'),
(278, 2, 271, '«إن تبدوا» تظهروا «الصدقات» أي النوافل «فَنعمَّا هي» أي نعم شيئاً إبداؤها «وإن تخفوها» تسروها «وتؤتوها الفقراء فهو خير لكم» من إبدائها وإيتائها الأغنياء أما صدقة الفرض فالأفضل إظهارها ليقتدي به ولئلا يتهم، وإيتاؤها الفقراء متعين «ويكَفِّر» بالياء والنون مجزوما بالعطف على محل فهو ومرفوعا على الاستئناف «عنكم من» بعض «سيئاتكم والله بما تعملون خبير» عالم بباطنه كظاهره لا يخفى عليه شيء منه.'),
(279, 2, 272, 'ولما منع صلى الله عليه وسلم من التصدق على المشركين ليسلموا نزل: «ليس عليك هداهم» أي الناس إلى الدخول في الإسلام إنما عليك البلاغ «ولكن الله يهدي من يشاء» هدايته إلى الدخول فيه «وما تنفقوا من خير» مال «فلأنفسكم» لأن ثوابه لها «وما تنفقون إلا ابتغاء وجه الله» أي ثوابه لاغيره من أعراض الدنيا خبر بمعنى النهي «وما تنفقوا من خير يوفَّ إليكم» جزاؤه «وأنتم لا تُظلمون» تنقصون منه شيئاً والجملتان تأكيد للأولى.'),
(280, 2, 273, '«للفقراء» خبر مبتدأ محذوف أي الصدقات «الذين أحصروا في سبيل الله» أي حبسوا أنفسهم على الجهاد، نزلت في أهل الصُّفَّةِ وهم أربعمائة من المهاجرين أرصدوا لتعلم القرآن والخروج مع السرايا «لا يستطيعون ضربا» سفرا «في الأرض» للتجارة والمعاش لشغلهم عنه بالجهاد «يحسبهم الجاهل» بحالهم «أغنياء من التعفف» أي لتعففهم عن السؤال وتركه «تعرفهم» يا مخاطب «بسيماهم» علامتهم من التواضع وأثر الجهد «لا يسألون الناس» شيئا فيلحفون «إلحافا» أي لا سؤال لهم أصلا فلا يقع منهم إلحاف وهو الإلحاح «وما تنفقوا من خير فإن الله به عليم» فمجاز عليه.'),
(281, 2, 274, '«الذين ينفقون أموالهم بالليل والنهار سرا وعلانية فلهم أجرهم عند ربَّهم ولا خوف عليهم ولا هم يحزنون».'),
(282, 2, 275, '«الذين يأكلون الربا» أي ياخذونه وهو الزيادة في المعاملة بالنقود والمطعومات في القدر أو الأجل «لا يقومون» من قبورهم «إلا» قياما «كما يقوم الذي يتخبطه» يصرعه «الشيطان من المس» الجنون، متعلق بيقومون «ذلك» الذي نزل بهم «بأنهم» بسبب أنهم «قالوا إنما البيع مثل الربا» في الجواز وهذا من عكس التشبيه مبالغة فقال تعالى ردا عليهم: «وأحل الله البيع وحرم الربا فمن جاءه» بلغه «موعظة» وعظ «من ربِّه فانتهى» عن أكله «فله ما سلف» قبل النهي أي لا يسترد منه «وأمره» في العفو عنه «إلى الله ومن عاد» إلى أكله مشبها له بالبيع في الحلِّ «فأولئك أصحاب النار هم فيها خالدون».'),
(283, 2, 276, '«يمحق الله الربا» ينقصه ويذهب بركته «ويربي الصدقات» يزيدها وينميها ويضاعف ثوابها «والله لا يحب كل كفَّار» بتحليل الربا «أثيم» فاجر بأكله أي يعاقبه.'),
(284, 2, 277, '«إن الذين آمنوا وعملوا الصالحات وأقاموا الصلاة وآتوا الزكاة لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون».'),
(285, 2, 278, '«يا أيها الذين آمنوا اتقوا الله وذروا» اتركوا «ما بقي من الربا إن كنتم مؤمنين» صادقين في إيمانكم فإن من شأن المؤمن امتثال أمر الله تعالى، نزلت لما طالب بعض الصحابة بعد النهي بربا كان لهم من قبل.'),
(286, 2, 279, '«فإن لم تفعلوا» ما أمرتم به «فأذنوا» اعلمَوا «بحرب من الله ورسوله» لكم فيه تهديد شديد لهم ولما نزلت قالوا لا بد لنا بحربه «وإن تبتم» رجعتم عنه «فلكم رءوس» أصول «أموالكم لاتَظلمون» بزيادة «ولا تُظلمون» بنقص.'),
(287, 2, 280, '(وإن كان) وقع غريم (ذو عسرة فنظرة) له أي عليكم تأخيره (إلى ميسرة) بفتح السين وضمها أي وقت يسر (وأن تصدقوا) بالتشديد على إدغام التاء في الأصل في الصاد وبالتخفيف على حذفها أي تتصدقوا على المعسر بالإبراء (خير لكم إن كنتم تعلمون) أنه خير فافعلوه وفي الحديث "" من أنظر معسرا أو وضع عنه أظله الله في ظله يوم لا ظل إلا ظله "" رواه مسلم.'),
(288, 2, 281, '«واتقوا يوما تُرجعون» بالبناء للمفعول تردون وللفاعل تصيرون «فيه إلى الله» هو يوم القيامة «ثم توفَّى» فيه «كل نفس» جزاء «ما كسبت» عملت من خير وشر «وهم لا يُظلمون» بنقص حسنة أو زيادة سيْئة.'),
(289, 2, 282, '«يا أيها الذين آمنوا إذا تداينتم» تعاملتم «بدين» كسلم وقرض «إلى أجل مسمى» معلوم «فاكتبوه» استيثاقا ودفعا للنزاع «وليكتب» كتاب الدين «بينكم كاتب بالعدل» بالحق في كتابته لا يزيد في المال والأجل ولا ينقص «ولا يأب» يمتنع «كاتب» من «أن يكتب» إذا دُعي إليها «كما علَّمه الله» أي فضله بالكتابة فلا يبخل بها والكاف متعلقة بيأب «فليكتب» تأكيد «وليملل» على الكاتب «الذي عليه الحق» الدين لأنه المشهود عليه فيقر ليعلم ما عليه «وليتق الله ربه» في إملائه «ولا يبخس» ينقص «منه» أي الحق «شيئا فإن كان الذي عليه الحق سفيها» مبذرا «أو ضعيفا» عن الإملاء لصغر أو كبر «أو لا يستطيع أن يُملَّ هو» لخرس أو جهل باللغة أو نحو ذلك «فَلْيُمْلِلْ وليُّه» متولي أمره من والد ووصي وقيِّم ومترجم «بالعدل واستشهدوا» أشهدوا على الدَّين «شهيدين» شاهدين «من رجالكم» أي بالغي المسلمين الأحرار «فإن لم يكونا» أي الشهيدان «رجلين فرجل وامرأتان» يشهدون «ممن ترضون من الشهداء» لدينه وعدالته وتعدد النساء لأجل «أن تضل» تنسى «إحداهما» الشهادة لنقص عقلهن وضبطهن «فَتُذكِّرَ» بالتخفيف والتشديد «إحداهما» الذاكرة «الأخرى» الناسية وجملة الإذكار محل العلة أي لتذكر أن ضلت ودخلت على الضلال لأنه سببه وفي قراءة بكسر أن شرطية ورفع تذكر استئناف جوابه «ولا يأب الشهداء إذا ما» زائدة «دُعوا» إلى تحمل الشهادة وأدائها «ولا تسأموا» تملوا من «أن تكتبوه» أي ما شهدتم عليه من الحق لكثرة وقوع ذلك «صغيرا» كان «أو كبيرا» قليلا أو كثيرا «إلى أجله» وقت حلوله حال من الهاء في تكتبوه «ذلكم» أي الكتب «أقسط» أعدل «عند الله وأقوم للشهادة» أي أعون على إقامتها لأنه يذكرها «وأدنى» اقرب إلى «أ» ن «لا ترتابوا» تشكوا في قدر الحق والأجل «إلا أن تكون» تقع «تجارةٌ حاضرةٌ» وفي قراءة بالنصب فتكون ناقصة واسمها ضمير التجارة «تديرونها بينكم» أي تقبضونها ولا أجل فيها «فليس عليكم جُناح» في «ألا تكتبوها» والمراد بها المتجر فيه «وأشهدوا إذا تبايعتم» عليه فأنه أدفع للاختلاف وهذا وما قبله أمر ندب «ولا يُضارّ كاتب ولا شهيد» صاحب الحق ومن عليه بتحريف أو امتناع من الشهادة أو الكتابة ولا يضرهما صاحب الحق بتكليفها ما لا يليق في الكتابة والشهادة «وإن تفعلوا» ما نُهيتم عنه «فإنه فسوق» خروج عن الطاعة لاحق «بكم واتقوا الله» في أمره ونهيه «ويعلمكم الله» مصالح أموركم حال مقدرة أو مستأنف «والله بكل شيء عليم».'),
(290, 2, 283, '«وإن كنتم على سفر» أي مسافرين وتداينتم «ولم تجدوا كاتبا فَرِهاَنٌ» وفي قراءة فَرهُنٌ جمع رهن «مقبوضة» تستوثقون بها وبينت السنة جواز الرهن في الحضر ووجود الكاتب فالتقيد بما ذكر لأن التوثيق فيه أشد وأفاد قوله مقبوضة اشتراط القبض في الرهن والاكتفاء به من المرتهن ووكيله «فإن أمن بعضكم بعضا» أي الدائن المدين على حقه فلم يرتهن «فليؤد الذي أؤتمن» أي المدين «أمانته» دينه «وليتق الله ربَّه» في أدائه «ولا تكتموا الشهادة» إذا دُعيتم لإقامتها «ومن يكتمها فإنه آثم قلبه» خص بالذكر لأنه محل الشهادة ولأنه إذا أثم تبعه غيره فيعاقب عليه معاقبة الآثمين «والله بما تعلمون عليم» لا يخفَى عليه شيء منه.'),
(291, 2, 284, '«لله ما في السماوات وما في الأرض وإن تبدوا» تظهروا «ما في أنفسكم» من السوء والعزم عليه «أو تخفوه» تسروه «يحاسبكم» يخبركم «به الله» يوم القيامة «فيغفرْ لمن يشاء» المغفرة له «ويعذبْ من يشاء» تعذيبه والفعلان بالجزم عطف على جواب الشرط والرفع أي فهو «والله على كل شيء قدير» ومنه محاسبتكم وجزاؤكم.'),
(292, 2, 285, '«آمن» صدق «الرسول» محمد صلى الله عليه وسلم «بما أنزل إليه من ربه» من القرآن «والمؤمنون» عطف عليه «كل» تنوين عوض من المضاف إليه «آمن بالله وملائكته وكتبه» بالجمع والإفراد «ورسله» يقولون «لا نفرق بين أحد من رسله» فنؤمن ببعض ونكفر ببعض كما فعل اليهود والنصارى «وقالوا سمعنا» أي ما أمرنا به سماع قبول «وأطعنا» نسألك «غفرانك ربنا وإليك المصير» المرجع بالبعث، ولما نزلت الآية التي قبلها شكا المؤمنون من الوسوسة وشق عليهم المحاسبة بها فنزل.'),
(293, 2, 286, '(لا يكلف الله نفساً إلا وسعها) أي ما تسعه قدرتها (لها ما كسبت) من الخير أي ثوابه (وعليها ما اكتسبت) من الشر أي وزره ولا يؤاخذ أحد بذنب أحد ولا بما لم يكسبه مما وسوست به نفسه، قولوا (ربنا لا تؤاخذنا) بالعقاب (إن نسينا أو أخطأنا) تركنا الصواب لا عن عمد كما أخذت به من قبلنا وقد رفع الله ذلك عن هذه الأمة كما ورد في الحديث فسؤاله اعتراف بنعمة الله (ربنا ولا تحمل علينا إصراً) أمرا يثقل علينا حمله (كما حملته على الذين من قبلنا) أي بني إسرائيل من قتل النفس في التوبة وإخراج ربع المال في الزكاة وقرض موضع النجاسة (ربنا ولا تحملنا ما لا طاقة) قوة (لنا به) من التكاليف والبلاء (واعف عنا) امح ذنوبنا (واغفر لنا وارحمنا) في الرحمة زيادة على المغفرة (أنت مولانا) سيدنا ومتولي أمورنا (فانصرنا على القوم الكافرين) بإقامة الحجة والغلبة في قتالهم فإن من شأن المولى أن ينصر مواليه على الأعداء، وفي الحديث "" لما نزلت هذه الآية فقرأها صلى الله عليه وسلم قيل له عقب كل كلمة قد فعلت "".');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, '«آلم» الله اعلم بمراده بذلك.'),
(295, 3, 2, '«الله لا إله إلا هو الحي القيوم».'),
(296, 3, 3, '«نزَّل عليك» يا محمد «الكتاب» القرآن ملتبسا «بالحق» بالصدق في أخباره «مصدقا لما بين يديه» قبله من الكتب «وأنزل التوراة والإنجيل من قبل» أي قبل تنزيله «هدّى» حال بمعني هادين من الضلالة «للناس» ممن تبعهما وعبر فيهما بأنزل وفي القرآن ينزل المقتضي للتكرير لأنهما أنزلا دفعة واحدة بخلافة «وأنزل الفرقان» بمعني الكتب الفارقة بين الحق والباطل وذكره بعد ذكر الثلاثة ليعم ما عداها.'),
(297, 3, 4, '«إن الذين كفروا بآيات الله» القرآن وغيره «لهم عذاب شديد والله عزيز» غالب على أمره فلا يمنعه شيء من إنجاز وعده ووعيده «ذو انتقام» عقوبة شديدة ممن عصاه لا يقدر على مثلها أحد'),
(298, 3, 5, '«إن الله لا يخفى عليه شيء» كائن «في الأرض ولا في السماء» لعلمه بما يقع في العالم من كلَّي وجزئي وخصمهما بالذكر لأن الحس لا يتجاوزهما.'),
(299, 3, 6, '«هو الذي يصوّركم في الأرحام كيف يشاء» من ذكورة وأنوثة وبياض وسواد وغير ذلك «لا إله إلا هو العزيز» في ملكه «الحكيم» في صنعه.'),
(300, 3, 7, '(هو الذي أنزل عليك الكتاب منه آيات محكمات) واضحات الدلالة (هن أم الكتاب) أصله المعتمد عليه في الأحكام (و أخر متشابهات) لا تفهم معانيها كأوائل السور وجعله كله محكما في قوله "" أحكمت آياته "" بمعنى أنه ليس فيه عيب، ومتشابهًا في قوله (كتابا متشابهًا) بمعنى أنه يشبه بعضه بعضا في الحسن والصدق (فأما الذين في قلوبهم زيغ) ميل عن الحق (فيتبعون ما تشابه منه ابتغاء) طلب (الفتنة) لجهالهم بوقوعهم في الشبهات واللبس (وابتغاء تأويله) تفسيره (وما يعلم تأويله) تفسيره (إلا الله) وحده (والراسخون) الثابتون المتمكنون (في العلم) مبتدأ خبره (يقولون آمنا به) أي بالمتشابه أنه من عند الله ولا نعلم معناه (كل) من المحكم والمتشابه (من عند ربنا وما يذكر) بادغام التاء في الأصل في الذال أي يتعظ (إلا أولوا الألباب) أصحاب العقول ويقولون أيضا إذا رأوا من يتبعه.'),
(301, 3, 8, '«ربنا لا تُزغ قلوبنا» تملها عن الحق بابتغاء تأويله الذي لا يليق بنا كما أزغت قلوب أولئك «بعد إذ هديتنا» أرشدتنا إليه «وهب لنا من لَدنك» من عندك «رحمة» تثبيتا «إنك أنت الوهاب».'),
(302, 3, 9, 'يا «ربنا إنك جامع الناس» تجمعهم «ليوم» أي في يوم «لا ريب» لا شك «فيه» هو يوم القيامة فتجازيهم بأعمالهم كما وعدت بذلك «إن الله لا يخلف الميعاد» موعده بالبعث فيه التفات عن الخطاب ويحتمل أن يكون من كلامه تعالى والغرض من الدعاء بذلك بيان أن همهم أمر الآخرة ولذلك سألوا الثبات على الهداية لينالوا ثوابها روى الشيخان عن عائشة رضي الله عنها قالت: (تلا رسول الله صلى الله عليه وسلم هذه الآية هو الذي أنزل عليك الكتاب منه آيات محكمات إلى آخرها وقال: فإذا رأيت الذين يتبعون ما تشابه منه فأولئك الذين سمى الله فاحذروهم) وروى الطبراني في الكبير عن أبي موسى الأشعري أنه سمع النبي صلى الله عليه وسلم يقول: (ما أخاف على أمتي إلا ثلاث خلال وذكر منها أن يفتح لهم الكتاب فيأخذه المؤمن يبتغي تأويله وليس يعلم تأويله إلا الله والراسخون في العلم يقولون آمنا به كل من عند ربنا وما يذكر إلا أولو الألباب) الحديث.'),
(303, 3, 10, '«إن الذين كفروا لن تُغنيَ» تدفع «عنهم أموالهم ولا أولادهم من الله» أي عذابه «شيئا وأولئك هم وقود النار» بفتح الواو ما توقد به.'),
(304, 3, 11, 'دأبُهم «كدأب» كعادة «آل فرعون والذين من قبلهم» من الأمم كعاد وثمود «كذبوا بآياتنا فأخذهم الله» أهلكم «بذنوبهم» والجملة مفسرة لما قبلها «والله شديد العقاب» ونزل لما أمر النبي صلى الله عليه وسلم اليهودَ بالإسلام بعد مرجعه من بدر فقالوا لا يغرنك أن قتلت نفراً من قريش أغمارا لا يعرفون القتال.'),
(305, 3, 12, '«قل» يا محمد «للذين كفروا» من اليهود «ستُغلبون» بالتاء والياء في الدنيا بالقتل والأسر وضرب الجزية وقد وقع ذلك «وتُحشرون» بالوجهين في الآخرة «إلى جهنم» فتدخلونها «وبئس المهاد» الفراش هي.'),
(306, 3, 13, '«قد كان لكم آية» عبرة وذكر الفعل للفصل «في فئتين» فرقتين «التقتا» يوم بدر للقتال «فئة تقاتل في سبيل الله» أي طاعته، وهم النبي وأصحابه وكانوا ثلاثمائة وثلاثة عشر رجلا معهم فرسَان وست أذرع وثمانية سيوف وأكثرهم رجالة «وأخرى كافرة يرونهم» أي الكفار «مثليهم» أي المسلمين أي أكثر منهم وكانوا نحو ألف «رأي العين» أي رؤية ظاهرة معاينة وقد نصرهم الله مع قلتهم «والله يؤيد» يقِّوي «بنصره من يشاء» نصره «إن في ذلك» المذكور «لعبرة لأولي الأبصار» لذوى البصائر أفلا تعتبرون بذلك فتؤمنون.'),
(307, 3, 14, '«زُيَّن للناس حبُّ الشهوات» ما تشتهيه النفس وتدعوا إليه زينها الله ابتلاءً أو الشيطانُ «من النساء والبنين والقناطير» الأموال الكثيرة «المقنطرة» المجمعة «من الذهب والفضة والخيل المسومة» الحسان «والأنعام» أي الإبل والبقر والغنم «والحرث» الزرع «ذلك» المذكور «متاع الحياة الدنيا» يتمتع به فيها ثم يفنى «والله عنده حسن المآب» المرجع وهو الجنة فينبغي الرغبة فيه دون غيره.'),
(308, 3, 15, '«قل» يا محمد لقومك «أؤنبِّئكم» أخبركم «بخير من ذلكم» المذكور من الشهوات استفهام تقرير «للذين اتقوا» الشرك «عند ربهم» خبر مبتدأُه «جنات تجري من تحتها الأنهار خالدين» أي مقدرين الخلود «فيها» إذا دخلوها «وأزواج مطهرة» من الحيض وغيره مما يستقذر «ورضوان» بكسر أوله وضمه لغتان أي رضا كثير «من الله والله بصير» عالم «بالعباد» فيجازي كلاً منهم بعمله.'),
(309, 3, 16, '«الذين» نعت أو بدل من الذين قبله «يقولون» يا «ربَّنا إننا آمنا» صدَّقنا بك وبرسولك «فاغفر لنا ذنوبنا وقنا عذاب النار».'),
(310, 3, 17, '«الصابرين» على الطاعة وعن المعصية نعت «والصادقين» في الإيمان «والقانتين» المطيعين لله «والمنفقين» المتصدقين «والمستغفرين» الله بأن يقولوا اللهم اغفر لنا «بالأسحار» أواخر الليل خُصت بالذكر لأنها وقت الغفلة ولذة النوم.'),
(311, 3, 18, '«شهد الله» بيَّن لخلقه بالدلائل والآيات «أنه لا إله» أي لا معبود في الوجود بحق «إلا هو و» شهد بذلك «الملائكةُ» بالإقرار «وأولوا العلم» من الأنبياء والمؤمنين بالاعتقاد واللفظ «قائماً» بتدبير مصنوعاته ونصبه على الحال والعامل فيها معنى الجملة أي تفرد «بالقسط» بالعدل «لا إله إلا هو» كرره تأكيدا «العزيز» في ملكه «الحكيم» في صنعه.'),
(312, 3, 19, '«إن الدين» المرضي «عند الله» هو «الإسلام» أي الشرع المبعوث به الرسل المبنى على التوحيد وفي قراءة بفتح أن بدل من أنه الخ بدل اشتمال «وما اختلف الذين أوتوا الكتاب» اليهود والنصارى في الدين بأن وحَّد بعضٌ وكفر بعضٌ «إلا من بعد ما جاءهم العلم» بالتوحيد «بغيا» من الكافرين «بينهم ومن يكفر بآيات الله» «فإن الله سريع الحساب» أي المجازاة له.'),
(313, 3, 20, '«فإن حاجوك» خاصمك الكفار يا محمد في الدين «فقل» لهم «أسلمت وجهي لله» أنقدت له أنا «ومن اتبعن» وخص الوجه بالذكر لشرفه فغيره أولى «وقل للذين أوتوا الكتاب» اليهود والنصارى و «الأميين» مشركي العرب «أأسلمتم» أي أسلموا «فإن أسلموا فقد اهتدوا» من الضلال «وإن تولوا» عن الإسلام «فإنما عليك البلاغ» أي التبليغ للرسالة «والله بصير بالعباد» فيجازيهم بأعمالهم وهذا قبل الأمر بالقتال'),
(314, 3, 21, '«إن الذين يكفرون بآيات الله ويقتلون» وفي قراءة يقاتلون «النبيين بغير حق ويقتلون الذين يأمرون بالقسط» بالعدل «من الناس» وهم اليهود رُوى أنهم قتلوا ثلاثة وأربعين نبياً فنهاهم مائة وسبعون من عبَّادهم فقتلوهم من يومهم «فبشِّرهم» أعلمهم «بعذاب اليم» مؤلم وذكر البشارة تهكم بهم ودخلت الفاء في خبر إن لشبه اسمها الموصول بالشرط.'),
(315, 3, 22, '«أولئك الذين حبطت» بطلت «أعمالهم» ما عملوا من خير كصدقة وصلة رحم «في الدنيا والآخرة» فلا اعتداد بها لعدم شرطها «وما لهم من ناصرين» مانعين من العذاب.'),
(316, 3, 23, '«ألم تر» تنظر «إلى الذين أوتوا نصيبا» حظاً «من الكتاب» التوراة «يُدْعَوْنَ» حال «إلى كتاب الله ليحكم بينهم ثم يتولى فريق منهم وهم معرضون» عن قبول حكمه نزلت في اليهود زنى منهم اثنان فتحاكموا إلى النبي صلى الله عليه وسلم فحكم عليهما بالرجم فغضبوا فجيء بالتوراة فوجدا فيها فرجما.'),
(317, 3, 24, '«ذلك» التولي والإعراض «بأنهم قالوا» أي بسبب قولهم «لن تمسنا النار إلا أياما معدودات» أربعين يوما مدة عبادة آبائهم العجل ثم تزول عنهم «وغرَّهم في دينهم» متعلق بقوله «ما كانوا يفترون» من قولهم ذلك.'),
(318, 3, 25, '«فكيف» حالهم «إذا جمعناهم ليوم» أي في يوم «لا ريب» لا شك «فيه» هو يوم القيامة «ووفِّيت كل نفس» من أهل الكتاب وغيرهم جزاء «ما كسبت» عملت من خير وشر «وهم» أي الناس «لا يُظلمون» بنقص حسنة أو زيادة سيئة.'),
(319, 3, 26, 'ونزلت لما وعد صلى الله عليه وسلم أمته مُلك فارس والروم فقال المنافقون هيهات: «قل اللهم» يا الله «مالك الملك تؤتي» تعطي «الملك من تشاء» من خلقك «وتنزع الملك ممن تشاء وتعز من تشاء» بإيتائه «وتذل من تشاء» بنزعه منه «بيدك» بقدرتك «الخير» أي والشر «إنك على كل شيء قدير».'),
(320, 3, 27, '«تولج» تدخل «الليل في النهار وتولج النهار» تدخله «في الليل» فيزيد كل منهما مما نقص من الآخر «وتخرج الحيَّ من الميت» كالإنسان والطائر من النطفة والبيضة «وتخرج الميت» كالنطفة والبيضة «من الحي وترزق من تشاء بغير حساب» أي رزقا واسعا.'),
(321, 3, 28, '«لا يتخذ المؤمنون الكافرين أولياء» يوالونهم «من دون» أي غير «المؤمنين ومن يفعل ذلك» أي يواليهم «فليس من» دين «الله في شيء إلا أن تتقوا منهم تقاة» مصدر تقيته أي تخافوا مخافة فلكم موالاتهم باللسان دون القلب وهذا قبل عزَّة الإسلام ويجري فيمن هو في بلد ليس قويا فيها «ويحذركم» يخوفكم «الله نفسه» أن يغضب عليكم إن واليتموهم «وإلى الله المصير» المرجع فيجازيكم.'),
(322, 3, 29, '«قل» لهم «إن تخفوا ما في صدوركم» قلوبكم من موالاتهم «أو تبدوه» تظهروه «يعلمْه الله و» هو «يعلم ما في السماوات وما في الأرض والله على كل شيء قدير» ومنه تعذيب من والاهم.'),
(323, 3, 30, 'اذكر «يوم تجد كل نفس ما عملتـ» ـهُ «من خير محضرا وما عملتـ» ـهُ «من سوءٍ» مبتدأ خبره «تود لو أن بينها وبينه أمدا بعيدا» غاية في نهاية البعد فلا يصل إليها «ويحذركم الله نفسه» كرر للتأكيد «والله رؤوف بالعباد».'),
(324, 3, 31, 'ونزل لما قالوا ما نعبد الأصنام إلا حبّا لله ليقربونا إليه «قل» لهم يا محمد «إن كنتم تحبون الله فاتبعوني يحببكم الله» بمعنى يثيبكم «ويغفر لكم ذنوبكم والله غفور» لمن اتبعني ما سلف من قبل ذلك «رحيم» به.'),
(325, 3, 32, '«قل» لهم «أطيعوا الله والرسول» فيما يأمركم به من التوحيد «فإن تولَّوا» أعرضوا عن الطاعة «فان الله لا يحب الكافرين» فيه إقامة الظاهر مقام المضمر أي لا يحبهم بمعني أنه يعاقبهم.'),
(326, 3, 33, '«إن الله اصطفى» اختار «آدم ونوحا وآل إبراهيم وآل عمران» بمعني أنفسهما «على العالمين» يجعل الأنبياء من نسلهم.'),
(327, 3, 34, '«ذرِّية بعضها من» ولد «بعض» منهم «والله سميع عليم».'),
(328, 3, 35, 'اذكر «إذ قالت امرأة عمران» حنة لما أسنت واشتاقت للولد فدعت الله وأحست بالحمل يا «رب إني نذرت» أن أجعل «لك ما في بطني محرَّرا» عتيقا خالصا من شواغل الدنيا لخدمة بيتك المقدس «فتقبَّل مني إنك أنت السميع» للدعاء «العليم» بالنيات، وهلك عمران وهي حامل.'),
(329, 3, 36, '(فلما وضعتها) ولدتها جارية وكانت ترجو أن يكون غلامًا إذ لم يكن يحرر إلا الغلمان (قالت) معتذرة يا (رب إني وضعتها أنثى والله أعلم) أي عالم (بما وضعت) جملة اعتراض من كلامه تعالى وفي قراءة بضم التاء (وليس الذكر) الذي طلبت (كالأنثى) التي وهبت لأنه يقصد للخدمة وهي لا تصلح لضعفها وعورتها وما يعتريها من الحيض ونحوه (وإني سميتها مريم وإني أعيذها بك وذريتها) أولادها (من الشيطان الرجيم) المطرود في الحديث "" ما من مولود يولد إلا مسه الشيطان حين يولد فيستهل صارخًا إلا مريم وابنها "" رواه الشيخان.'),
(330, 3, 37, '«فتقبلها ربها» أي قبل مريم من أمها «بقبول حسن وأنبتها نباتا حسنا» أنشأها بخلق حسن فكانت تنبت في اليوم كما ينبت المولود في العام وآتت بها أمها الأحبار سدنة بيت المقدس فقالت: دونكم هذه النذيرة فتنافسوا فيها أنها بنت إمامهم فقال زكريا أنا أحق بها لأن خالتها عندي فقالوا لا حتى نقترع فانطلقوا وهم تسعة وعشرون إلى نهر الأردن وألقوا أقلامهم على أن من ثبت قلمه في الماء وصعد فهو أولى بها فثبت قلم زكريا فأخذها وبنى لها غرفة في المسجد بسلم لا يصعد إليها غيره وكان يأتيها بأكلها وشربها ودهنها فيجد عندها فاكهة الصيف في الشتاء وفاكهة الشتاء في الصيف كما قال تعالى «وَكَفَلَهَا زَكَريَّاُ» ضمها إليه وفي قراءة بالتشديد ونصب زكريا ممدودا ومقصورا والفاعل الله «كلما دخل عليها زكريا المحراب» الغرفة وهي أشرف المجالس «وجد عندها رزقا قال يا مريم أنَّى» من أين «لك هذا قالت» وهي صغيرة «هو من عند الله» يأتيني به من الجنة «إن الله يرزق من يشاء بغير حساب» رزقا واسعا بلا تبعة.'),
(331, 3, 38, '«هنالك» أي لما رأى زكريَّا ذلك وعلم أن القادر على الإتيان بالشيء في غير حينه قادر على الإتيان بالولد على الكبر وكان أهل بيته انقرضوا «دعا زَكَريَّاُ ربَّه» لما دخل المحراب للصلاة جوف الليل «قال ربِّ هب لي من لدنك» من عندك «ذرية طيبة» ولدا صالحا «إنك سميع» مجيب «الدعاء».'),
(332, 3, 39, '«فنادته الملائكة» أي جبريل «وهو قائم يصلي في المحراب» أي المسجد «أنَّ» أي بأن وفي قراءة بالكسر بتقدير القول «الله يُبشِّرك» مثقلا ومخففا «بيحيى مصدِّقاً بكلمة» كائنة «من الله» أي بعيسى أنه روح الله وسُمي كلمة لأنه خلق بكلمة كن «وسيِّدا» متبوعا «وحصورا» ممنوعا من النساء «ونبيا من الصالحين» رُوي أنه لم يعمل خطيئة ولم يهم بها.'),
(333, 3, 40, '«قال ربِّ أنَّى» كيف «يكون لي غلام» ولد «وقد بلغني الكبر» أي بلغت نهاية السن مائة وعشرين سنة «وامرأتي عاقر» بلغت ثمانية وتسعين سنة «قال» الأمر «كذلك» من خلق الله غلاما منكما «الله يفعل ما يشاء» لا يعجزه منه شيء ولإظهار هذه القدرة العظيمة ألهمه السؤال ليجاب بها ولما تاقت نفسه إلى سرعة المبشَر به.'),
(334, 3, 41, '«قال رب اجعل لي آية» أي علامة على حمل امرأتي «قال آيتك» علية «أ» ن «لا تكلم الناس» أي تمتنع من كلامهم بخلاف ذكر الله تعالى «ثلاثة أيام» أي بلياليها «إلا رمزا» إشارة «واذكر ربَّك كثيرا وسبِّح» صلِّ «بالعشي والإبكار» أواخر النهار وأوائله.'),
(335, 3, 42, '«و» اذكر «إذ قالت الملائكة» أي جبريل «يا مريم إن الله اصطفاك» اختارك «وطهرك» من مسيس الرجال «واصطفاك على نساء العالمين» أي أهل زمانك.'),
(336, 3, 43, '«يا مريم اقنتي لربك» أطيعيه «واسجدي واركعي مع الراكعين» أي صلِّي مع المصلِّين.'),
(337, 3, 44, '«ذلك» المذكور من أمر زكريا ومريم «من أنباء الغيب» أخبار ما غاب عنك «نوحيه إليك» يا محمد «وما كنت لديهم إذ يُلْقُون أقلامهم» في الماء يقترعون ليطهر لهم «أيهم يكْفُلُ» يربي «مريم وما كنت لديهم إذ يختصون» في كفالتها فتعرف ذلك فتخبر به وإنما عرفته من جهة الوحي.'),
(338, 3, 45, 'اذكر «إذ قالت الملائكة» أي جبريل «يا مريم أن الله يبشرك بكلمة منه» أي ولد «اسمه المسيح عيسى بن مريم» خاطبها بنسبته إليها تنبيها على أنها تلده بلا أب إذ عادة الرجال نسبتهم إلى آبائهم «وجيها» ذا جاه «في الدنيا» بالنبوة «والآخرة» بالشفاعة والدرجات العُلا «ومن المقرَّبين» عند الله.'),
(339, 3, 46, '«ويكلِّم الناس في المهد» أي طفلا قبل وقت الكلام «وكهلا ومن الصالحين».'),
(340, 3, 47, '«قالت ربِّ أنَّى» كيف «يكون لي ولد ولم يمسني بشر» بتزوج ولا غيره «قال» الأمر «كذلك» من خلق ولد منك بلا أب «الله يخلق ما يشاء إذا قضي أمرا» أراد خلقه «فإنما يقول له كن فيكون» أي فهو يكون.'),
(341, 3, 48, '«وَنُعَلِّمُهُ» بالنون والياء «الكتاب» الخط «والحكمة والتوراة والإنجيل».'),
(342, 3, 49, '«و» يجعله «رسولا إلى بنى إسرائيل» في الصبا أو بعد البلوغ فنفخ جبريل في جيب درعها فحملت، وكان من أمرها ما ذكر في سورة مريم فلما بعثه الله إلى بني إسرائيل قال لهم إني رسول الله إليكم «إني» أي بأني «قد جئتكم بآية» علامة على صدقي «من ربكم» هي «أنِّي» وفي قراءة بالكسر استئنافا «أخلق» أصوِّر «لكم من الطين كهيئة الطير» مثل صورته فالكاف اسم مفعول «فأنفخ فيه» الضمير للكاف «فيكون طيرا» وفي قراءة طائرا «بإذن الله» بإرادته فخلق لهم الخفاش لأنه أكمل الطير خلقا فكان يطير وهم ينظرونه فإذا غاب عن أعينهم سقط ميتا «وأبرئ» أشفي «الأكمه» الذي وُلد أعمى «والأبرص» وخصا بالذكر لأنهما ذا إعياء وكان بعثه في زمن الطب فأبرأ في يوم خمسين ألفا بالدعاء بشرط الإيمان «وأحيي الموتى بإذن الله» كرره لنفي توهم الألوهية فيه فأحيا عازر صديقا له وابن العجوز وابنة العاشر فعاشوا وولد لهم، وسام بن نوح ومات في الحال «وأنبئكم بما تأكلون وما تدَّخرون» تخبئون «في بيوتكم» مما لم أعانيه فكان يخبر الشخص بما أكل وبما يأكل وبما يأكل بعد «إن في ذلك» المذكور «لآية لكم إن كنتم مؤمنين».'),
(343, 3, 50, '«و» جئتكم «مصدقاً لما بين يدي» قبلي «من التوراة ولأحلَّ لكم بعض الذي حرم عليكم» فيها فأحل لهم من السمك والطير مالا صيصة له وقيل أحل الجميع فبعض بمعنى كل «وجئتكم بآية من ربكم» كرره تأكيدا وليبنى عليه «فاتقوا الله وأطيعون» فيما آمركم به توحيد الله وطاعته.'),
(344, 3, 51, '«إن الله ربي وربكم فاعبدوه هذا» الذي آمركم به «صراط» طريق «مستقيم» فكذبوه ولم يؤمنوا به.'),
(345, 3, 52, '«فلمّا أحس» علم «عيسى منهم الكفر» وأرادوا قتله «قال مَنْ أنصاري» أعواني ذاهبا «إلى الله» لأنصر دينه «قال الحواريون نحن أنصار الله» أعوان دينه وهم أصفياء عيسى أول من آمن به وكانوا اثني عشر رجلا من الحور وهو البياض الخالص وقيل كانوا قصارين يحورون الثياب أي يبيضونها «آمنّا» صدقنا «بالله واشهد» يا عيسى «بأنّا مسلمون».'),
(346, 3, 53, '«ربنا آمنا بما أنزلت» من الإنجيل «واتَّبعنا الرسول» عيسى «فاكتبنا مع الشاهدين» لك بالوحدانية ولرسولك بالصدق.'),
(347, 3, 54, 'قال تعالى: «ومكروا» أي كفار بني إسرائيل بعيسى إذ وكلوا به من يقتله غيلة «ومكر الله» بهم بأن ألقى شبه عيسى على من قصد قتله فقتلوه ورفُع عيسى إلى السماء «والله خير الماكرين» أعلمهم به.'),
(348, 3, 55, 'اذكر «إذ قال الله يا عيسى إني متوفيك» قابضك «ورافعك إليَّ» من الدنيا من غير موت «ومطهرك» مبعدك «من الذين كفروا وجاعل الذين اتبعوك» صدقوا بنبوتك من المسلمين والنصارى «فوق الذين كفروا» بك وهم اليهود يعلونهم بالحجة والسيف «إلى يوم القيامة ثم إليَّ مرجعكم فأحكم بينكم فيما كنتم فيه تختلفون» من أمر الدين.'),
(349, 3, 56, '«فأما الذين كفروا فأعذبهم عذابا شديدا في الدنيا» بالقتل والسبي والجزية «والآخرة» بالنار «وما لهم من ناصرين» مانعين منه.'),
(350, 3, 57, '(وأما الذين أمنوا وعملوا الصالحات فيوفيهم) بالياء والنون (أجورهم والله لا يحب الظالمين) أي يعاقبهم، روي أن الله تعالى أرسل إليه سحابة فرفعته فتعلقت به أمه وبكت فقال لها إن القيامة تجمعنا وكان ذلك ليلة القدر ببيت المقدس وله ثلاث وثلاثون سنة وعاشت أمه بعده ست سنين وروى الشيخان حديث "" أنه ينزل قرب الساعة ويحكم بشريعة نبينا ويقتل الدجال والخنزير ويكسر الصليب ويضع الجزية "" وفي حديث مسلم أنه يمكث سبع سنين وفي حديث عن أبي دواد الطيالسي أربعين سنة ويتوفى ويصلى عليه فيحتمل أن المراد مجموع لبثه في الأرض قبل الرفع وبعده.'),
(351, 3, 58, '«ذلك» المذكور من أمر عيسى «نتلوه» نقصه «عليك» يا محمد «من الآيات» حال من الهاء في نتلوه وعامله ما في ذلك من معنى الإشارة «والذكر الحكيم» المحكم أي القرآن.'),
(352, 3, 59, '«إن مثل عيسى» شأنه الغريب «عند الله كمثل آدم» كشأنه في خلقه من غير أب وهو من تشبيه الغريب بالأغرب ليكون أقطع للخصم وأوقع في النفس «خلقه» أي آدم أي قالبه «من تراب ثم قال له كن» بشرا «فيكون» أي فكان وكذلك عيسى قال له كن من غير أب فكان.'),
(353, 3, 60, '«الحق من ربك» خبر مبتدأ محذوف أي أمر عيسى «فلا تكن من الممترين» الشاكين.'),
(354, 3, 61, '«فمن حاجَّك» جادلك من النصارى «فيه من بعد ما جاءك من العلم» بأمره «فقل» لهم «تعالوا ندع أبناءنا وأبناءكم ونساءنا ونساءكم وأنفسنا وأنفسكم» فنجمعهم «ثم نبتهل» نتضرع في الدعاء «فنجعل لعنة الله على الكاذبين» بأن نقول: اللهم العن الكاذب في شأن عيسى وقد دعا النبي صلى الله عليه وسلم وفد نجران لذلك لما حاجُّوه به فقالوا: حتى ننظر في أمرنا ثم نأتيك فقال ذو رأيهم: لقد عرفتم نبوته وأنه ما بأهل قوم نبيا إلا هلكوا فودعوا الرجل وانصرفوا فأتوا الرسول صلى الله عليه وسلم وقد خرج معه الحسن والحسين وفاطمة وعلي وقال لهم: إذا دعوت فأمِّنوا فأبوا أن يلاعنوا وصالحوه على الجزية رواه أبو نُعَيْم، وعن ابن عباس: قال: لو خرج الذين يباهلون لرجعوا لا يجدون مالا ولا أهلا، ورُوي: لو خرجوا لاحترقوا.'),
(355, 3, 62, '«إن هذا» المذكور «لهو القصص» الخبر «الحق» الذي لاشك فيه «وما من» زائدة «إله إلا الله وإن الله لهو العزيز» في ملكه «الحكيم» في صنعه.'),
(356, 3, 63, '«فإن تولَّوا» أعرضوا عن الإيمان «فإن الله عليم بالمفسدين» فيجازيهم وفيه وضع الظاهر موضع المضمر.'),
(357, 3, 64, '«قل يا أهل الكتاب» اليهود والنصارى «تعالوا إلى كلمة سواءٍ» مصدر بمعنى مستو أمرها «بيننا وبينكم» هي «أ» ن «لا نعبد إلا الله ولا نشرك به شيئا ولا يتخذ بعضنا أربابا من دون الله» كما اتخذتم الأحبار والرهبان «فإن تولَّوْا» أعرضوا عن التوحيد «فقولوا» أنتم لهم «اشهدوا بأنا مسلمون» موحدون.'),
(358, 3, 65, 'ونزل لما قال اليهود: إبراهيم يهودي ونحن على دينه، وقالت النصارى كذلك: «يا أهل الكتاب لِمَ تُحَاجُّونَ» تخاصمون «في إبراهيم» بزعمكم أنه على دينكم «وما أنزلت التوراة والإنجيل إلا من بعده» بزمن طويل وبعد نزولها حدثت اليهودية والنصرانية «أفلا تعقلون» بطلان قولكم.'),
(359, 3, 66, '«ها» للتنبيه «أنتم» مبتدأ «هؤلاء» والخبرُ «حاججتم فيما لكم به علم» من أمر موسى وعيسى وزعمكم أنكم على دينهما «فلمَا تُحاجُّون فيما ليس لكم به علم» من شأن إبراهيم «والله يعلم» شأنه «وأنتم لا تعلمون» قال تعالى تبرئة لإبراهيم.'),
(360, 3, 67, '«ما كان إبراهيم يهوديا ولا نصرانيا ولكن كان حنيفا» مائلا عن الأديان كلها إلى الدين القيِّم «مسلما» موحدا «وما كان من المشركين».'),
(361, 3, 68, '«إنَّ أولى الناس» أحقهم «بإبراهيم للَّذِينَ اتبعوه» في زمانه «وهذا النبي» محمد لموافقته له في أكثر شرعه «والذين آمنوا» من أمته فهم الذين ينبغي أن يقولوا نحن على دينه لا أنتم «والله ولي المؤمنين» ناصرهم وحافظهم.'),
(362, 3, 69, 'ونزل لما دعا اليهود معاذا وحذيفة وعمارا إلى دينهم: «ودَّت طائفة من أهل الكتاب لو يضلونكم وما يضلون إلا أنفسهم» لأن إثم إضلالهم عليهم والمؤمنون لا يطيعونهم فيه «وما يشعرون» بذلك.'),
(363, 3, 70, '«يا أهل الكتاب لِمَ تكفرون بآيات الله» القرآن المشتمل على نعت محمد صلى الله عليه وسلم «وأنتم تشهدون» تعلمون أنه الحق.'),
(364, 3, 71, '«يا أهل الكتاب لِمَ تلبسون» تخلطون «الحق بالباطل» بالتحريف والتزوير «وتكتمون الحق» أي نعت النبي «وأنتم تعلمون» أنه الحق.'),
(365, 3, 72, '«وقالت طائفة من أهل الكتاب» اليهود لبعضهم «آمنوا بالذي أنزل على الذين آمنوا» أي القرآن «وجه النهار» أوله «واكفروا» به «آخره لعلهم» أي المؤمنين «يرجعون» عن دينهم إذ يقولون ما رجع هؤلاء عنه بعد دخولهم فيه وهم أولو علم إلا لعلمهم بطلانه.'),
(366, 3, 73, 'وقالوا أيضاً «ولا تؤمنوا» تصدَّقوا «إلا لمن تبع» وافق «دينكم» قال تعالى: «قل» لهم يا محمد «إن الهدى هدى الله» الذي هو الإسلام وما عداه ضلال، والجملةُ اعتراض «أن» أي بأن «يؤتى أحدّ مثل ما أوتيتم» من الكتاب والحكمة والفصائل وأن مفعول تؤمنوا، والمستثنى منه أحد قدم عليه المستثنى المعنى: لا تقروا بأن أحدا يؤتى ذلك إلا لمن اتبع دينكم «أو» بأن «يحاجوكم» أي المؤمنون يغلبوكم «عند ربكم» يوم القيامة لأنكم أصح دينا، وفي قراءة: أأن بهمزة التوبيخ أي إيتاء أحد مثله تقرون به قال تعالى «قل إن الفضل بيد الله يؤتيه من يشاء» فمن أين لكم أنه لا يؤتى أحد مثل ما أوتيتم «والله واسع» كثير الفضل «عليم» بمن هو أهله.'),
(367, 3, 74, '«يختص برحمته من يشاء والله ذو الفضل العظيم».'),
(368, 3, 75, '«ومن أهل الكتاب من إن تأمنه بقنطار» أي بمال كثير «يؤدَّه إليك» لأمانته كعبد الله بن سلام أودعه رجل ألفا ومائتي أوقية ذهبا فأعادها إليه «ومنهم من إن تأمنه بدينار لا يؤده إليك» لخيانته «إلا ما دمت عليه قائما» لا تفارقه فمتى فارقته أنكره ككعب بن الأشرف استودعه قرشي دينارا فجحده «ذلك» أي ترك الأداء «بأنهم قالوا» بسبب قولهم «ليس علينا في الأميين» أي العرب «سبيل» أي إثم لاستحلالهم ظلم من خالف دينهم ونسبوه إليه تعالى، قال تعالى «ويقولون على الله الكذب» في نسبة ذلك إليه «وهم يعلمون» أنهم كاذبون.'),
(369, 3, 76, '«بلى» عليهم فيهم سبيل «من أوفى بعهده» الذي عاهد الله عليه أو بعهد الله إليه من أداء الأمانة وغيره «واتقى» الله بترك المعاصي وعمل الطاعات «فإن الله يحب المتقين» فيه وضع الظاهر موضع المضمر أي يحبهم بمعنى يثيبهم.'),
(370, 3, 77, 'ونزل في اليهود لما بدلوا نعت النبي صلى الله عليه وسلم وعهد الله إليهم في التوراة وفيمن حلف كاذبا في دعوى أو في بيع سلعة: «إن الذين يشترون» يستبدلون «بعهد الله» إليهم في الإيمان بالنبي وأداء الأمانة «وأيمانهم» حلفهم به تعالى كاذبين «ثمنا قليلا» من الدنيا «أولئك لا خَلاق» نصيب «لهم في الآخرة ولا يكلمهم الله» غضبا عليهم «ولا ينظر إليهم» يرحمهم «يوم القيامة ولا يزكِّيهم» يطهرهم «ولهم عذاب أليم» مؤلم.'),
(371, 3, 78, '«وإنَّ منهم» أي أهل الكتاب «لفريقا» طائفة ككعب بن الأشرف «يلوون ألسنتهم بالكتاب» أي يعطفونها بقراءته عن المنزل إلى ما حرفوه من نعت النبي صلى الله عليه وسلم ونحو «لتحسبوه» أي المحرف «من الكتاب» الذي أنزله الله «وما هو من الكتاب ويقولون هو من عند الله وما هو من عند الله ويقولون على الله الكذب وهم يعلمون» أنهم كاذبون.'),
(372, 3, 79, 'ونزل لما قال نصارى نجران إن عيسى أمرهم أن يتخذوه ربا ولما طلب بعض المسلمين السجود له صلى الله عليه وسلم «ما كان» ينبغي «لبشر أن يؤتيه الله الكتاب والحكم» أي الفهم للشريعة «والنبوة ثم يقول للناس كونوا عبادا لي من دون الله ولكن» يقول «كونوا ربانيين» علماء عاملين منسوبين إلى الرب بزيادة ألف ونون تفخيما «بما كنتم تَعْلمُونَ» بالتخفيف والتشديد «الكتاب وبما كنتم تدرسون» أي بسبب ذلك فإن فائدته أن تعملوا.'),
(373, 3, 80, '«وَلا يأمُرُكُمْ» بالرفع استئنافا أي الله والنصب عطفا على يقول أي البشر «أن تتخذوا الملائكة والنبيين أربابا» كما اتخذت الصابئة الملائكة واليهود عُزيرا والنصارى عيسى «أيأمُرُكم بالكفر بعد إذ أنتم مسلمون» لا ينبغي له هذا.'),
(374, 3, 81, '«و» اذكر «إذا» حين «أخذ الله ميثاق النبيين» عهدهم «لما» بفتح اللام للابتداء وتوكيد معنى القسم الذي في أخذ الميثاق وكسرها متعلقة بأخذ وما موصولة على الوجهين أي للذي «آتيتكم» إياه، وفي قراءة آتيناكم «من كتاب وحكمة ثم جاءكم رسول مصدق لما معكم» من الكتاب والحكمة وهو محمد صلى الله عليه وسلم «لتؤمنن به ولتنصرنه» جواب القسم إن أدركتموه وأممهم تبع لهم في ذلك «قال» تعالى لهم «أأقررتم» بذلك «وأخذتم» قبلتم «على ذلكم إصري» عهدي «قالوا أقررنا قال فاشهدوا» على أنفسكم وأتباعكم بذلك «وأنا معكم من الشاهدين» عليكم وعليهم.'),
(375, 3, 82, '«فمن تولَّى» أعرض «بعد ذلك» الميثاق «فأولئك هم الفاسقون».'),
(376, 3, 83, '«أفغير دين الله يبغون» بالياء والتاء أي المتولون «وله أسلم» إنقاذ «من في السماوات والأرض طوعا» بلا إباء «وكرها» بالسيف بمعاينة ما يلجأ إليه «وإليه يُرْجَعُون» بالتاء والياء والهمزة في أول الآية للإنكار.'),
(377, 3, 84, '«قل» لهم يا محمد «آمنا بالله وما أنزل علينا وما أنزل على إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط» أولاده «وما أوتي موسى وعيسى والنبيون من ربهم لا نفرق بين أحد منهم» بالتصديق والتكذيب «ونحن له مسلمون» مخلصون في العبادة ونزل فيمن ارتد ولحق بالكفار.'),
(378, 3, 85, '«ومن يبتغ غير الإسلام دينا فلن يقبل منه وهو في الآخرة من الخاسرين» لمصيره إلى النار المؤبدة علية.'),
(379, 3, 86, '«كيف» أي لا «يهدي الله قوما كفروا بعد إيمانهم وشهدوا» أي شهادتهم «أن الرسول حق و» قد «جاءهم البينات» الحجج الظاهرات على صدق النبي «والله لا يهدي القوم الظالمين» أي الكافرين.'),
(380, 3, 87, '«أولئك جزاؤهم أنَّ عليهم لعنة الله والملائكة والناس أجمعين».'),
(381, 3, 88, '«خالدين فيها» أي اللعنة أو النار المدلول بها عليها «لا يخفف عنهم العذاب ولا هم يُنظرون» يمهلون.'),
(382, 3, 89, '«إلا الذين تابوا من بعد ذلك وأصلحوا» عملهم «فإن الله غفور» لهم «رحيم» بهم.'),
(383, 3, 90, 'ونزل في اليهود «إن الذين كفروا» بعيسى «بعد إيمانهم» بموسى «ثم ازدادوا كفرا» بمحمد «لن تُقبل توبتهم» إذا غرغروا أو ماتوا كفّارا «وأولئك هم الضالُّون».'),
(384, 3, 91, '«إن الذين كفروا وماتوا وهم كفار فلن يقبل من أحدهم ملء الأرض» مقدار ما يملؤها «ذهبا ولو افتدى به» أدخل الفاء في خبر إن لشبه بالشرط وإيذانا بتسبب عدم القبول عن الموت على الكفر «أولئك لهم عذاب أليم» مؤلم «وما لهم من ناصرين» مانعين منه.'),
(385, 3, 92, '«لن تنالوا البرَّ» أي ثوابه وهو الجنَّةُ «حتى تنفقوا» تَصَّدَّقُوا «مما تحبون» من أموالكم «وما تنفقوا من شيء فإن الله به عليم» فيجازي عليه.'),
(386, 3, 93, 'ونزل لما قال اليهود إنك تزعم أنك على ملة إبراهيم وكان لا يأكل لحوم الإبل وألبانها: «كل الطعام كان حِلا» حلالا «لبني إسرائيل إلا ما حرم إسرائيل» يعقوب «على نفسه» وهو الإبل لما حصل له عرق النَّسا بالفتح والقصر فنذر إن شفي لا يأكلها فحرم عليه «من قبل أن تُنَزَّل التوراة» وذلك بعد إبراهيم ولم تكن على عهده حراما كما زعموا «قل» لهم «فأتوا بالتوراة فاتلوها» ليتبين صدق قولكم «إن كنتم صادقين» فيه فبهتوا ولم يأتوا بها قال تعالى.'),
(387, 3, 94, '«فمن افترى على الله الكذب من بعد ذلك» أي ظهور الحجة بأن التحريم إنما كان من جهة يعقوب لا على عهد إبراهيم «فأولئك هم الظالمون» المتجاوزون الحق إلى الباطل.'),
(388, 3, 95, '«قُل صدق الله» في هذا كجميع ما أخبر به «فاتبعوا ملة إبراهيم» التي أنا عليها «حنيفا» مائلا عن كل دين إلى الإسلام «وما كان من المشركين» به.'),
(389, 3, 96, 'ونزل لما قالوا قبلتنا قبل قبلتكم (إن أول بيت وضع) متعبدا (للناس) في الأرض (للذي ببكة) بالباء لغة في مكة سميت بذلك لأنها تبك أعناق الجبابرة أي تدقها، بناه الملائكة قبل خلق آدم ووضع بعده الأقصى وبينهما أربعون سنة كما في حديث الصحيحين وفي حديث "" أنه أول ما ظهر على وجه الماء عند خلق السماوات والأرض زبدة بيضاء فدحيت الأرض من تحته "" (مباركا) حال من الذي أي ذا بركة (وهدى للعالمين) لأنه قبلتهم.'),
(390, 3, 97, '«فيه آيات بينات» منها «مقام إبراهيم» أي الحجر الذي قام عليه عند بناء البيت فأثر قدماه فيه وبقي إلى الآن مع تطاول الزمان وتداول الأيدي عليه ومنها تضعيف الحسنات فيه وأن الطير لا يعلوه «ومن دخله كان آمنا» لا يتعرض إليه بقتل أو ظلم أو غير ذلك «ولله على الناس حجُّ البيت» واجب بكسر الحاء وفتحها لغتان في مصدر حج بمعنى قصد ويبدل من الناس «من استطاع إليه سبيلا» طرقاً فسره بالزاد والراحلة رواه الحاكم وغيره «ومن كفر» بالله أو بما فرضه من الحج «فإن الله غني عن العالمين» الإنس والجن والملائكة وعن عبادتهم.'),
(391, 3, 98, '«قل يا أهل الكتاب لم تكفرون بآيات الله» القرآن «والله شهيد على ما تعملون» فيجازيكم عليه.'),
(392, 3, 99, '«قل يا أهل الكتاب لِمَ تصدون» تصرفون «عن سبيل الله» أي دينه «من آمن» بتكذيبكم النبي وكتم نعته «تبغونها» أي تطلبون السبيل «عوجا» مصدر بمعنى معوجة أي مائلة عن الحق «وأنتم شهداء» عالمون بأن الدين المرضي القيم هو دين الإسلام كما في كتابكم «وما الله بغافل عما تعملون» من الكفر والتكذيب وإنما يؤخركم إلى وقتكم ليجازيكم.'),
(393, 3, 100, 'ونزل لما مر بعض اليهود على الأوس والخزرج وغاظهم تألفهم فذكروهم بما كان بينهم في الجاهلية من الفتن فتشاجروا وكادوا يقتتلون: «يا أيها الذين آمنوا إن تطيعوا فريقا من الذين أوتوا الكتاب يردوكم بعد إيمانكم كافرين».'),
(394, 3, 101, '«وكيف تكفرون» استفهام تعجب وتوبيخ «وأنتم تتلى عليكم آيات الله وفيكم رسوله ومن يعتصم» يتمسك «بالله فقد هدي إلى صراطِ مستقيم».'),
(395, 3, 102, '(يا أيها الذين آمنوا اتقوا الله حق تقاته) بأن يطاع فلا يعصى ويشكر فلا يكفر ويذكر فلا ينسى فقالوا يا رسول الله ومن يقوى على هذا فنسخ بقوله تعالى "" فاتقوا الله ما استطتعم "" (ولا تموتن إلا وأنتم مسلمون) موحدون.'),
(396, 3, 103, '«واعتصموا» تمسكوا «بحبل الله» أي دينه «جميعا ولا تفرقوا» بعد الإسلام «واذكروا نعمة الله» إنعامه «عليكم» يا معشر الأوس والخزرج «إذ كنتم» قبل الإسلام «أعداء فألَّف» جمع «بين قلوبكم» بالإسلام «فأصبحتم» فصرتم «بنعمته إخوانا» في الدين والولاية «وكنتم على شفا» طرف «حفرة من النار» ليس بينكم وبين الوقوع فيها إلا أن تموتوا كفارا «فأنقذكم منها» بالإيمان «كذلك» كما بيَّن لكم ما ذكر «يبين الله لكم آياته لعلكم تهتدون».'),
(397, 3, 104, '«ولتكن منكم أمة يدعون إلى الخير» الإسلام «ويأمرون بالمعروف وينهون عن المنكر وأولئك» الداعون الآمرون الناهون «هم المفلحون» الفائزون ومن للتبغيض لأن ما ذكر فرض كفاية لا يلزم كل الأمة ولا يليق بكل أحد كالجاهل وقيل زائدة أي لتكونوا أمه.'),
(398, 3, 105, '«ولا تكونوا كالذين تفرقوا» عن دينهم «واختلفوا» فيه «من بعد ما جاءهم البينات» وهم اليهود والنصارى «وأولئك لهم عذاب عظيم».'),
(399, 3, 106, '«يوم تبيض وجوه وتسودُّ وجوه» أي يوم القيامة «فأما الذين اسودَّت وجوههم» وهم الكافرون فيلقون في النار ويقال لهم توبيخا «أكفرتم بعد إيمانكم» يوم أخذ الميثاق «فذوقوا العذاب بما كنتم تكفرون».'),
(400, 3, 107, '«وأما الذين ابيضَّت وجوههم» وهم المؤمنون «ففي رحمة الله» أي جنته «هم فيها خالدون».'),
(401, 3, 108, '«تلك» أي هذه الآيات «آيات الله نتلوها عليك» يا محمد «بالحق وما الله يريد ظلما للعالمين» بأن يأخذهم بغير جرم.'),
(402, 3, 109, '«ولله ما في السماوات وما في الأرض» ملكا وخلقا وعبيدا «وإلى الله تُرجع» تصير «الأمور».'),
(403, 3, 110, '«كنتم» يا أمة محمد في علم الله تعالى «خير أمة أخرجت» أظهرت «للناس تأمرون بالمعروف وتنهون عن المنكر وتؤمنون بالله ولو آمن أهل الكتاب لكان» الإيمان «خيرا لهم منهم المؤمنون» كعبد الله بن سلام رضي الله عنه وأصحابه «وأكثرهم الفاسقون» الكافرون.'),
(404, 3, 111, '«لن يضروكم» أي اليهود يا معشر المسلمين بشيء «إلا أذًى» باللسان من سبِّ ووعيد «وإن يقاتلوكم يولوكم الأدبار» منهزمين «ثم لا ينصرون» عليكم بل لكم النصر عليهم.'),
(405, 3, 112, '«ضربت عليهم الذلة أين ما ثُقفوا» حيثما وجدوا فلا عز لهم ولا اعتصام «إلا» كائنين «بحبل من الله وحبل من الناس» المؤمنين وهو عهدهم إليهم بالأمان على أداء الجزية أي لا عصمة لهم غير ذلك «وباءُوا» رجعوا «بغضب من الله وضربت عليهم المسكنة ذلك بأنهم» أي بسبب أنهم «كانوا يكفرون بآيات الله ويقتلون الأنبياء بغير حق ذلك» تأكيد «بما عصوا» أمر الله «وكانوا يعتدون» يتجاوزون الحلال إلى الحرام.'),
(406, 3, 113, '«ليسوا» أي أهل الكتاب «سواءً» مستوين «من أهل الكتاب أمة قائمة» مستقيمة ثابتة على الحق كعبد الله بن سلام رضي الله عنه وأصحابه «يتلون آيات الله آناء الليل» أي في ساعاته «وهم يسجدون» يصلُّون، حال.'),
(407, 3, 114, '«يؤمنون بالله واليوم الآخر ويأمرون بالمعروف وينهون عن المنكر ويسارعون في الخيرات وأولئك» الموصوفون بما ذكر الله «من الصالحين» ومنهم من ليسوا كذلك وليسوا من الصالحين.'),
(408, 3, 115, '«وما تفعلوا» بالتاء أيها الأمة والياء أي الأمة القائمة «من خير فلن يُكفروه» بالوجهين أي تعدموا ثوابه بل تجازون عليه «والله عليم بالمتقين».'),
(409, 3, 116, '«إن الذين كفروا لن تغني» تدفع «عنهم أموالهم ولا أولادهم من الله» أي من عذابه «شيئا» وخصها بالذكر لأن الإنسان يدفع عن نفسه تارة بفداء المال وتارة بالاستعانة بالأولاد «وأولئك أصحاب النار هم فيها خالدون».'),
(410, 3, 117, '«مثل» صفة «ما ينفقون» أي الكفار «في هذه الحياة الدنيا» في عداوة النبي أو صدقة ونحوها «كمثل ريح فيها صِرّ» حر أو برد شديد «أصابت حرث» زرع «قوم ظلموا أنفسهم» بالكفر والمعصية «فأهلكته» فلم ينتفعوا به فكذلك نفقاتهم ذاهبة لا ينتفعون بها «وما ظلمهم الله» بضياع نفقاتهم «ولكن أنفسهم يظلمون» بالكفر الموجب لضياعها.'),
(411, 3, 118, '«يا أيها الذين آمنوا لا تتخذوا بطانة» أصفياء تطلعونهم على سرِّكم «من دونكم» أي غيركم من اليهود والنصارى والمنافقين «لا يألونكم خبالا» نصب بنزع الخافض أي لا يقصرون لكم في الفساد «ودُّوا» تمنَّوا «ما عنتم» أي عنتكم وهو شدة الضرر «قد بدت» ظهرت «البغضاء» العداوة لكم «من أفواههم» بالوقيعة فيكم وإطلاع المشركين على سركم «وما تخفي صدورهم» من العداوة «أكبر قد بينا لكم الآيات» على عدواتهم «إن كنتم تعقلون» ذلك فلا توالوهم.'),
(412, 3, 119, '«ها» للتنبيه «أنتم» يا «أولاء» المؤمنون «تحبونهم» لقرابتهم منكم وصداقتهم «ولا يحبونكم» لمخالفتهم لكم في الدين «وتؤمنون بالكتاب كله» أي بالكتب كلها ولا يؤمنون بكتابكم «وإذا لقوكم قالوا آمنا وإذا خلوا عضوا عليكم الأنامل» أطراف الأصابع «من الغيظ» شدة الغضب لما يرون من ائتلافكم ويعبر عن شدة الغضب بِعَضِّ الأنامل مجازا وإن لم يكن ثم عض «قل موتوا بغيظم» أي ابقوا عليه إلى الموت فلن تروا ما يسركم «إن الله عليم بذات الصدور» بما في القلوب ومنه ما يضمره هؤلاء.'),
(413, 3, 120, '«إن تَمْسَسْكُمْ» تصبكم «حسنة» نعمة كنصر وغنيمة «تسؤهم» تحزنهم» «وإن تصبكم سيئة» كهزيمة وجدب «يفرحوا بها» وجملة الشرط متصلة بالشرط قبل وما بينهما اعتراض والمعنى أنهم متناهون في عداوتكم فلم توالوهم فاجتنبوهم «وإن تصبروا» على أذاهم «وتتقوا» الله في موالاتهم وغيرها «لا يضركمْ» بكسر الضاد وسكون الراء وضمها وتشديدها «كيدهم شيئا إن الله بما يعلمون» بالياء والتاء «محيط» عالم فيجازيهم به.'),
(414, 3, 121, '«و» اذكر يا محمد «إذ غدوت من أهلك» من المدينة «تبوئ» تنزل «المؤمنين مقاعد» مراكز يقفون فيها «للقتال والله سميع» لأقوالكم «عليم» بأحوالكم وهو يوم أحد خرج النبي صلى الله عليه وسلم بألف أو إلا خمسين رجلا والمشركون ثلاثة آلاف ونزل بالشعب يوم السبت سابع شوال سنة ثلاثٍ من الهجرة وجعل ظهره وعسكره إلى أحد وسوى صفوفهم وأجلس جيشا من الرماة وأمَّر عليهم عبد الله ابن جبير بسفح الجبل وقال: انضحوا عنا بالنبل لا يأتونا من ورائنا ولا تبرحوا غُلبنا أو نُصرنا.'),
(415, 3, 122, '«إذا» بدل من إذ قبله «همت طائفتان منكم» بنو سلمة وبنو حارثة جناحا العسكر «أن تفشلا» تجبنا عن القتال وترجعا لما رجع عبد الله بن أبيّ المنافقُ وأصحابه وقال: عَلام نقتلُ أنفسنا وأولادنا وقال لأبي جابر السمي القائل له أنشدكم الله في نبيكم وأنفسكم لو نعلم قتالا لا تبعناكم فثبتهما الله ولم ينصرفا «والله وليهما» ناصرهما «وعلى الله فليتوكل المؤمنون» ليثقوا به دون غيره.'),
(416, 3, 123, 'ونزل لما هزموا تذكيرا لهم بنعمة الله «ولقد نصركم الله ببدر» موضع بين مكة والمدينة «وأنتم أذلة» بقلة العدد والسلاح «فاتقوا الله لعلكم تشكرون» نعمه.'),
(417, 3, 124, '«إذ» ظرف لنصركم «تقول للمؤمنين» توعدهم تطمينا «ألن يكفيكم أن يمدّكم» يعينكم «ربكم بثلاثة آلافِ من الملائكة مُنْزَلينَ» بالتخفيف والتشديد.'),
(418, 3, 125, '«بلى» يكفيكم ذلك وفي الأنفال بألف لأنه أمدهم أولا بها ثم صارت ثلاثة ثم صارت خمسة كما قال تعالى «إن تصبروا» على لقاء العدو «وتتقوا» الله في المخالفة «ويأتوكم» أي المشركون «من فورهم» وقتهم «هذا يمددكم ربكم بخمسة آلاف من الملائكة مُسَوَّمينَ» بكسر الواو وفتحها أي معلمين وقد صبروا وأنجز الله وعده بأن قاتلت معهم الملائكة على خيل بلق عليهم عمائم صفر أو بيض أرسلوها بين أكتافهم.'),
(419, 3, 126, '«وما جعله الله» أي الإمداد «إلا بشرى لكم» بالنصر «ولتطمئن» تسكن «قلوبكم به» فلا تجزع من كثرة العدو وقلَّتكم «وما النصر إلا من عند الله العزيز الحكيم» يؤتيه من يشاء وليس بكثرة الجند.'),
(420, 3, 127, '«ليقطع» متعلق ينصركم أي ليُهلك «طرفا من الذين كفروا» بالقتل والأسر «أو يكبتهم» يذلهم بالهزيمة «فينقلبوا» يرجعوا «خائبين» لم ينالوا ما راموه.'),
(421, 3, 128, 'ونزلت لما كسرت رباعيته صلى الله عليه وسلم وشج وجهه يوم أحد وقال: "" كيف يفلح قوم خضبوا وجه نبيهم بالدم "" (ليس لك من الأمر شيء) بل الأمر لله فاصبر (أو) بمعنى إلى أن (يتوب عليهم) بالإسلام (أو يعذبهم فإنهم ظالمون) بالكفر.'),
(422, 3, 129, '«ولله ما في السماوات وما في الأرض» ملكا وخلقا وعبيدا «يغفر لمن يشاء» المغفرة له «ويعذب من يشاء» تعذيبه «والله غفور» لأوليائه «رحيم» بأهل طاعته.'),
(423, 3, 130, '«يا أيها الذين آمنوا لا تأكلوا الربا أضعافا مضاعفة» بألف ودونها بأن تزيدوا في المال عند حلول الأجل وتؤخروا الطلب «واتقوا الله» بتركه «لعلكم تفلحون» تفوزون.'),
(424, 3, 131, '«واتقوا النار التي أعدت للكافرين» أن تعذَّبوا بها.'),
(425, 3, 132, '«وأطيعوا الله والرسول لعلكم ترحمون».'),
(426, 3, 133, '«وسارعوا» بواو ودونها «إلى مغفرة من ربكم وجنة عرضها السماوات والأرض» أي كعرضهما لو وصلت إحداهما للأخرى، والعرضُ السعة «أعدت للمتقين» الله بعمل الطاعات وترك المعاصي.'),
(427, 3, 134, '«الذين ينفقون» في طاعة الله «في السراء والضراء» اليُسر والعسر «والكاظمين الغيظ» الكافين عن إمضائه مع القدرة «والعافين عن الناس» ممن ظلمهم أي التاركين عقوبتهم «والله يحب المحسنين» بهذه الأفعال، أي يثبهم.'),
(428, 3, 135, '«والذين إذا فعلوا فاحشة» ذنبا قبيحا كالزنا «أو ظلموا أنفسهم» بدونه كالقُبلة «ذكروا الله» أي وعيده «فاستغفروا لذنوبهم ومن» أي لا «يغفر الذنوب إلا الله ولم يصروا» يداوموا «على ما فعلوا» بل أقلعوا عنه «وهم يعلمون» أن الذي أتوه معصية.'),
(429, 3, 136, '«أولئك جزاؤهم مغفرة من ربهم وجنات تجري من تحتها الأنهار خالدين فيها» حال مقدرة، أي مقدرين الخلود فيها إذا دخلوها «ونعم أجر العاملين» بالطاعة هذا الأجر.'),
(430, 3, 137, 'ونزل في هزيمة أحد «قد خلَت» مضت «من قبلكم سُنَن» طرائق في الكفار بإمهالهم ثم أخذهم «فسيروا» أيها المؤمنون «في الأرض فانظروا كيف كان عاقبة المكذبين» الرسل أي آخر أمرهم من الهلاك فلا تحزنوا لغلبتهم فإنا أمهلناهم لوقتهم.'),
(431, 3, 138, '«هذا» القرآن «بيانٌ للناس» كلهم «وهدى» من الضلال «وموعظة للمتقين» منهم.'),
(432, 3, 139, '«ولا تهنوا» تضعفوا عن قتال الكفار «ولا تحزنوا» على ما أصابكم بأحد «وأنتم الأعلون» بالغلبة عليهم «إن كنتم مؤمنين» حقا وجوابه دل عليه مجموع ما قبله.'),
(433, 3, 140, '«إن يمسَسْكم» يصبكم بأحد «قرح» بفتح القاف وضمها جهد من جرح ونحو «فقد مسَّ القومَ» الكفار «قرحٌ مثله» ببدر «وتلك الأيام نداولها» نصرِّفها «بين الناس» يوماً لفرقة ويوماً لأخرى ليتعظوا «وليعلم الله» علم ظهور «الذين آمنوا» أخلصوا في إيمانهم من غيرهم «ويتخذ منكم شهداء» يكرمهم بالشهادة «والله لا يحب الظالمين» الكافرين أي يعاقبهم وما ينعم به عليهم استدراج.'),
(434, 3, 141, '«وليمحِّص الله الذين آمنوا» يطهرهم من الذنوب بما يصيبهم «ويمحق» يهلك «الكافرين».'),
(435, 3, 142, '«أم» بل «حسبتم أن تدخلوا الجنة ولَما» لم «يعلم الله الذين جاهدوا منكم» علم ظهور «ويعلم الصابرين» في الشدائد.'),
(436, 3, 143, '«ولقد كنتم تمنون» فيه حذف إحدى التاءين في الأصل «الموت من قبل أن تلقوه» حيث قلتم ليت لنا يوما كيوم بدر لننال ما نال شهداؤه «فقد رأيتموه» أي سببه الحرب «وأنتم تنظرون» أي بصراء تتأملون الحال كيف هي فلم انهزمتم؟ ونزل في هزيمتهم لما أشيع أن النبي قتل وقال لهم المنافقون إن كان قتل فارجعوا إلى دينكم.'),
(437, 3, 144, '«وما محمد إلا رسول قد خلت من قبله الرسل أفإن مات أو قُتل» كغيره «انقلبتم على أعقابكم» رجعتم إلى الكفر والجملة الأخيرة محل الاستفهام الإنكاري أي ما كان معبودا فترجعوا «ومن ينقلب على عقبيه فلن يضرَّ اللهَ شيئا» وإنما يضر نفسه «وسيجزى الله الشاكرين» نعمه بالثبات.'),
(438, 3, 145, '«وما كان لنفس أن تموت إلا بإذن الله» بقضائه «كتابا» مصدر أي: كتب الله ذلك «مؤجَّلا» مؤقتا لا يتقدم ولا يتأخر فلم انهزمتم والهزيمة لا تدفع الموت والثبات لا يقطع الحياة «ومن يُرد» بعمله «ثواب الدنيا» أي جزاءه منها «نؤته منها» ما قسم له ولا حظَّ له في الآخرة «ومن يرد ثواب الآخرة نؤته منها» أي من ثوابها «وسنجري الشاكرين».'),
(439, 3, 146, '«وكأيِّن» كم «من نبي قاتِلَ» وفي قراءة قَُتل والفاعل ضميره «معه» خبر مبتدؤه «ربِّيون كثير» جموعٌ كثيرة «فما وهَنوا» جبنوا «لما أصابهم في سبيل الله» من الجراح وقتل أنبيائهم وأصحابهم «وما ضعفوا» عن الجهاد «وما استكانوا» خضعوا لعدوهم كما فعلتم حين قبل قُتل النبي «والله يحب الصابرين» على البلاء أي يثيبهم.'),
(440, 3, 147, '«وما كان قولَهم» عند قتل نبيهم مع ثباتهم وصبرهم «إلا أن قالوا ربنا اغفر لنا ذنوبنا وإسرافنا» تجاوزنا الحد «في أمرنا» إيذانا بأن ما أصابهم لسوء فعلهم وهضما لأنفسهم «وثبت أقدامنا» بالقوة على الجهاد «وانصرنا على القوم الكافرين».'),
(441, 3, 148, '«فآتاهم الله ثواب الدنيا» النصر والغنيمة «وحسن ثواب الآخرة» أي الجنة: التفضل فوق الاستحقاق «والله يحب المحسنين».'),
(442, 3, 149, '«يا أيها الذين آمنوا إن تطيعوا الذين كفروا» فيما يأمرونكم به «يردوكم على أعقابكم» إلى الكفر «فتنقلبوا خاسرين».'),
(443, 3, 150, '«بل الله مولاكم» ناصركم «وهو خير الناصرين» فأطيعوه دونهم.'),
(444, 3, 151, '«سنلقى في قلوب الذين كفروا الرعب» بسكون العين وضمها الخوف، وقد عزموا بعد ارتحالهم من أُحد على العود واستئصال المسلمين فرعبوا ولم يرجعوا «بما أشركوا» بسبب إشراكهم «بالله ما لم ينزل به سلطانا» حجة على عبادته وهو الأصنام «ومأواهم النار وبئس مثوى» مأوى «الظالمين» الكافرين هي.'),
(445, 3, 152, '«ولقد صدقكم الله وعده» إياكم بالنصر «إذ تحسونهم» تقتلونهم «بإذنه» بإرادته «حتى إذا فَشِلْتُمْ» جبنتم عن القتال «وتنازعتم» اختلفتم «في الأمر» أي أمر النبي صلى الله عليه وسلم بالمقام في سفح الجبل للرمي فقال بعضكم: نذهب فقد نُصر أصحابنا وبعضكم: لا نخالف أمر النبي صلى الله عليه وسلم «وعصَيتم» أمره فتركتم المركز لطلب الغنيمة «من بعد ما أراكم» اللهُ «ما تحبون» من النصر وجواب إذا دل عليه ما قبله أي منعكم نصره «منكم من يريد الدنيا» فترك المركز للغنيمة «ومنكم من يريد الآخرة» فثبت به حتى قتل كعبد الله بن جبير و أصحابه «ثم صرفكم» عطف على جواب إذا المقدر ردَّكم للهزيمة «عنهم» أي الكفار «ليبتليكم» ليمنحنكم فيظهر المخلص من غيره «ولقد عفا عنكم» ما ارتكبتموه «والله ذو فضل على المؤمنين» بالعفو.'),
(446, 3, 153, 'اذكروا «إذ تصعدون» تبعدون في الأرض هاربين «ولا تلوون» تعرجون «على أحد والرسولُ يدعوكم في أخراكم» أي من ورائكم يقول إليَّ عباد الله «فأثابكم» فجازاكم «غمّاً» بالهزيمة «بغمٍّ» بسبب غمِّكم للرسول بالمخالفة وقيل الباء بمعنى على، أي مضاعفا على غم فوت الغنيمة «لكيلا» متعلق بعفا أو بأثابكم فلا زائدة «تحزنوا على ما فاتكم» من الغنيمة «ولا ما أصابكم» من القتل والهزيمة «والله خبير بما تعملون».'),
(447, 3, 154, '«ثم أنزل عليكم من بعد الغم أمنة» أمنا «نعاسا» بدل «يغشى» بالياء والتاء «طائفة منكم» وهم المؤمنون فكانوا يميدون تحت الحجف وتسقط السيوف منهم «وطائفة قد أهمتهم أنفسهم» أي حملتهم على الهم فلا رغبة لهم إلا نجاتها دون النبي وأصحابه فلم يناموا وهم المنافقون «يظنون بالله» ظنا «غير» الظن «الحق ظَنَّ» أي كظن «الجاهلية» حيث اعتقدوا أن النبي قتل أولا ينصر «يقولون هل» ما «لنا من الأمر» أي النصر الذي وُعدناه «من شيء قل» لهم «إن الأمر كله» بالنصب توكيدا والرفع مبتدأ وخبره «لله» أي القضاء له بفعل ما يشاء «يخفون في أنفسهم ما لا يبدون» يظهرون «لك يقولون» بيان لما قبله «لو كان لنا من الأمر شيء ما قتلنا ههنا» أي لو كان الاختبار إلينا لم نخرج فلم نقتل لكن أخرجنا كرها «قل» لهم «لو كنتم في بيوتكم» وفيكم من كتب الله عليه القتل «لبرز» خرج «الذين كتب» قضي «عليهم القتل» منكم «إلى مضاجعهم» مصارعهم فيقتلوا ولم ينجهم وقعودهم لأن قضاءه تعالى كائن لا محالة «و» فعل ما فعل بأخذ «ليبتلي» يختبر «الله ما في صدوركم» قلوبكم من الإخلاص والنفاق «وليمحص» يميز «ما في قلوبكم والله عليم بذات الصدور» بما في القلوب لا يخفى عليه شيء وإنما يبتلي ليظهر للناس.'),
(448, 3, 155, '«إن الذين تَوَلَّوْا منكم» عن القتال «يوم التقى الجمعان» جمع المسلمين وجمع الكفار بأُحُد وهم المسلمون إلا اثنيْ عشر رجلا «إنما استزلَّهم» أزلهم «الشيطان» بوسوسته «ببعض ما كسبوا» من الذنوب وهو مخالفة أمر النبي «ولقد عفا اللهُ عنهم إن الله غفور» للمؤمنين «حليم» لا يعجل على العصاة.'),
(449, 3, 156, '«يا أيها الذين آمنوا لا تكونوا كالذين كفروا» أي المنافقين «وقالوا لإخوانهم» أي في شأنهم «إذا ضربوا» سافروا «في الأرض» فماتوا «أو كانوا غُزٌى» جمع غاز فقتلوا «لو كانوا عندنا ما ماتوا وما قتلوا» أي لا تقولوا كقولهم «ليجعل الله ذلك» القول في عاقبة أمرهم «حسرة في قلوبهم والله يحيي ويميت» فلا يمنع عن الموت قعود «والله بما تعملون» بالتاء والياء «بصير» فيجازيكم به.'),
(450, 3, 157, '«ولئن» لام قسم «قُتِلْتُمْ في سبيل الله» أي الجهاد «أو مُتُّمْ» بضم الميم وكسرها من مات يموت أي أتاكم الموت فيه «لمغفرة» كائنة «من الله» لذنوبكم «ورحمة» منه لكم على ذلك واللام ومدخولها جواب القسم وهو في موضوع الفعل مبتدأ خبره «خير مما تجمعون» من الدنيا بالتاء والياء.'),
(451, 3, 158, '«ولئن» لام قسم «مُتُّمْ» بالوجهين «أو قُتلتم» في الجهاد وغيره «لإلى الله» لا إلى غيره «تُحشرون» في الآخرة فيجازيكم.'),
(452, 3, 159, '«فبما رحمة من الله لِنْتَ» يا محمد «لهم» أي سهلت أخلاقك إذ خالفوك «ولو كنت فظا» سيء الخُلُق «غليظ القلب» جافيا فأغلظت لهم «لا نفضُّوا» تفرقوا «من حولك فاعف» تجاوز «عنهم» ما أتوه «واستغفر لهم» ذنوبهم حتى أغفر لهم «وشاورهم» استخرج آراءهم «في الأمر» أي شأنك من الحرب وغيرة تطيبيا لقلوبهم ولستن بك وكان صلى الله عليه وسلم كثير المشاورة لهم. (فإذا عزمت) على إمضاء ما تريد بعد المشاورة «فَتوكَّلْ على الله» ثق به لا بالمشاورة «إن الله يحب المتوكلين» عليه.'),
(453, 3, 160, '«إن ينصركم الله» يُعنكم على عدوكم كيوم بدر «فلا غالب لكم وإن يخذلكم» يترك نصركم كيوم أُحد «فمن ذا الذي ينصركم من بعده» أي بعد خذلانه أي لا ناصر لكم «وعلى الله» لا غيره «فليتوكل» ليثق «المؤمنون».'),
(454, 3, 161, 'ونزلت لما فقدت قطيفة حمراء يوم أحد فقال بعض الناس: لعل النبي أخذها: «وما كان» ما ينبغي «لنبي أن يّغُلَّ» يخون في الغنيمة فلا تظنوا به ذلك، وفي قراءة بالبناء للمفعول أن ينسب إلى الغلول «ومن يغلُل يأت بما غلَّ يوم القيامة» حاملا له على عنقه «ثم تُوفَّى كل نفس» الغال وغيره جزاء «ما كسبت» عملت «وهم لا يُظلمون» شيئا.'),
(455, 3, 162, '«أفمن اتبع رضوان الله» فأطاع ولم يغل «كمن باء» رجع «بسخط من الله» لمعصيته وغلوله «ومأواه جهنم وبئس المصير» المرجع هي.'),
(456, 3, 163, '«هم درجات» أي أصحاب درجات «عند الله» أي مختلفوا المنازل فلمن اتبع رضوانه الثواب ولمن باء بسخطه العقاب «والله بصير بما يعملون» فيجازيهم به.'),
(457, 3, 164, '«لقد منَّ الله على المؤمنين إذ بعث فيهم رسولا من أنفسهم» أي عربيا مثلهم ليفهموا عنه ويشرُفوا به لا ملكا ولا عجميا «يتلو عليهم آياته» القرآن «ويُزكِّيهمْ» يطهرهم من الذنوب «ويعلمهم الكتاب» القرآن «والحكمة» السنة «وإن» مخففة أي إنهم «كانوا من قبلُ» أي قبل بعثه «لفى ضلال مبين» بيِّن.'),
(458, 3, 165, '«أولّما أصابتكم مصيبة» بأحد بقتل سبعين منكم «قد أصبتم مثليها» ببدر بقتل سبعين وأسر سبعين منهم «قلتم» متعجبين «أنَّى» من أين لنا «هذا» الخذلان ونحن مسلمون ورسولُ الله فينا والجملةُ الأخيرة محل للاستفهام الإنكارى «قل» لهم «هو من عند أنفسكم» لأنكم تركتم المركز فخُذلتم «إن الله على كل شيء قديرٌ» ومنه النصر وحده وقد جازاكم بخلافكم.'),
(459, 3, 166, '«وما أصابكم يوم التقى الجمعان» بأحد «فبإذن الله» بإرادته «وليعلم» علم ظهور «المؤمنين» حقا.'),
(460, 3, 167, '«وليعلم الذين نافقوا و» الذين «قيل لهم» لما انصرفوا عن القتال وهم عبد الله بن أبيّ وأصحابه «تعالوا قاتلوا في سبيل الله» أعداءه «أو ادفعوا» عنا القوم بتكثير سوادكم إن لم تقاتلوا «قالوا لو نَعْلَمُ» نحسن «قتالا لاتبعناكم» قال تعالى تكذيبا لهم: «هم للكفر يومئذ أقرب منهم للإيمان» بما أظهروا من خذلانهم للمؤمنين وكانوا قبل أقرب إلى الإيمان من حيث الظاهر «يقولون بأفواهم ما ليس في قلوبهم» ولو علموا قتالا لم يتبعوكم «والله أعلم بما يكتمون» من النفاق.'),
(461, 3, 168, '«الذين» بدل من الذين قبله أو نعت «قالوا لإخوانهم» في الدين «و» قد «قعدوا» عن الجهاد «لو أطاعونا» أي شهداء أحد أو إخواننا في القعود «ماقتلوا قل» لهم «فادرَءُوا» ادفعوا «عن أنفسكم الموت إن كنتم صادقين» في أن القعود ينجي منه ونزل في الشهداء.'),
(462, 3, 169, '«ولا تحسبن الذين قتلوا» بالتخفيف والتشديد «في سبيل الله» أي لأجل دينه «أمواتا بل» هم «أحياءٌ عند ربهم» أرواحهم في حواصل طيور خضر تسرح في الجنة حيث شاءت كما ورد في الحديث «يرزقون» يأكلون من ثمار الجنة.'),
(463, 3, 170, '«فرحين» حال من ضمير يُرزقون «بما آتاهم الله من فضله و» هم «يستبشرون» يفرحون «بالذين لم يلحقوا بهم من خلفهم» من إخوانهم المؤمنين ويبدل من الذين «أ» نْ أي بأن «لا خوف عليهم» أي الذين لم يلحقوا بهم «ولا هم يحزنون» في الآخرة المعنى يفرحون بأمنهم وفرحهم.'),
(464, 3, 171, '«يستبشرون بنعمة» ثوابٍ «من الله وفضل» زيادة عليه «وأنَّ» بالفتح عطفاً على نعمة وبالكسر استئنافا «الله لا يضيع أجر المؤمنين» بل يأجرهم.'),
(465, 3, 172, '«الذين» مبتدأ «استجابوا لله والرسول» دعاءه لهم بالخروج للقتال لما أراد أبو سفيان وأصحابه العود تواعدوا مع النبي صلى الله عليه وسلم سوق بدر العام المقبل من يوم أحد «من بعد ما أصابهم القرح» بأحد وخبرُ المبتدأ «للذين أحسنوا منهم» بطاعته «واتقوا» مخالفته «أجر عظيم» هو الجنة.'),
(466, 3, 173, '«الذين» بدل من الذين قبله أو نعت «قال لهم الناس» أي نعيم بن مسعود الأشجعي «إن الناس» أبا سفيان وأصحابه «قد جمعوا لكم» الجموع ليستأصلوكم «فاخشوهم» ولا تأتوهم «فزادهم» ذلك القول «إيمانا» تصديقا بالله ويقينا «وقالوا حسبنا الله» كافينا أمرهم «ونعم الوكيل» المفوَّض إليه الأمر هو، وخرجوا مع النبي صلى الله عليه وسلم فوافوا سوق بدر وألقى الله الرعب في قلب أبي سفيان وأصحابه فلم يأتوا وكان معهم تجارات فباعوا وربحوا قال الله تعالى.'),
(467, 3, 174, '«فانقلبوا» رجعوا من بدر «بنعمة من الله وفضل» بسلامة وربح «لم يَمْسَسهُمْ سوء» من قتل أو حرج «واتبعوا رضوان الله» بطاعته وطاعة رسوله في الخروج «والله ذو فضل عظيم» على أهل طاعته.'),
(468, 3, 175, '«إنما ذلكم» أي القائل لكم إن الناس إلخ «الشيطان يخوِّفُ» ـكم «أولياءه» الكفار «فلا تخافوهم وخافون» في ترك أمري «إن كنتم مؤمنين» حقَّا.'),
(469, 3, 176, '«ولا يُحْزِنْكَ» بضم الياء وكسر الزاي وبفتحها وضم الزاي من أحزنه «الذين يسارعون في الكفر» يقعون فيه سريعا بنصرته وهم أهل مكة أو المنافقون أي لا تهتم لكفرهم «إنَّهمُ لن يضروا الله شيئا» بفعلهم وإنما يضرون أنفسهم «يريد الله ألا يجعل لهم حظّا» نصيبا «في الآخرة» أي الجنة فلذلك خذلهم الله «ولهم عذاب عظيم» في النار.'),
(470, 3, 177, '«إن الذين اشتروا الكفر بالإيمان» أي أخذوه بدله «لن يضروا الله» بكفرهم «شيئا ولهم عذاب أليم» مؤلم.'),
(471, 3, 178, '«ولا يحسبنَّ» بالياء والتاء «الذين كفروا أنما نملي» أي إملاءنا «لهم» بتطويل الأعمار وتأخيرهم «خير لأنفسهم» وأن ومعمولاها سدت مسد المفعولين في قراءة التحتانية ومسد الثاني في الأخرى «إنما نملي» نمهل «لهم ليزدادوا إثما» بكثرة المعاصي «ولهم عذاب مهين» ذو إهانة في الآخرة.'),
(472, 3, 179, '«ما كان الله ليذر» ليترك «المؤمنين على ما أنتم» أيها الناس «عليه» من اختلاط المخلص بغيره «حتى يَمِيزَ» بالتخفيف والتشديد يفصل «الخبيث» المنافق «من الطيب» المؤمن بالتكاليف الشاقة المبينة لذلك ففعل ذلك يوم أحد «وما كان الله ليطلعكم على الغيب» فتعرفوا المنافق من غيره قبل التمييز «ولكنَّ الله يجتبي» يختار «من رسله من يشاء» فيطلعه على غيبه كما أطلع النبي صلى الله عليه وسلم على حال المنافقين «فآمنوا بالله ورسله وإن تؤمنوا وتتقوا» النفاق «فلكم أجر عظيم».'),
(473, 3, 180, '«ولايحسبن» بالياء والتاء «الذين يبخلون بما آتاهم الله من فضله» أي بزكاته «هو» أي بخلهم «خيرا لهم» مفعول ثان والضمير للفصل والأول بخلهم مقدرا قبل الوصول وقبل الضمير على التحتانية «بل هو شر لهم سيطوَّقون ما بخلوا به» أي بزكاته من المال «يوم القيامة» بأن يجعل حية في عنقه تنهشه كما ورد في الحديث «ولله ميراث السماوات والأرض» يرثهما بعد فناء أهلهما «والله بما تعلمون» بالتاء والياء «خبير» فيجازيكم به.'),
(474, 3, 181, '«لقد سمع الله قول الذين قالوا إن الله فقير ونحن أغنياء» وهم اليهود قالوه لما نزل (من ذا الذي يقرض الله قرضا حسنا) وقالوا لو كان غنيا ما استقرضناه «سنكتب» نأمر بكتب «ما قالوا» في صحائف أعمالهم ليُجازَوا عليه وفي قراءة بالياء مبينا للمفعول «و» نكتب «قتلَهم» بالنصب والرفع «الأنبياء بغير حق ونقول» بالنون والياء أي الله لهم في الآخرة على لسان الملائكة «ذوقوا عذاب الحريق» النار ويقال لهم إذا القوا فيها.'),
(475, 3, 182, '«ذلك» العذاب «بما قدَّمت أيديكم» عبَّر بها عن الإنسان لأن أكثر الأفعال تزاول بها «وأن الله ليس بظلام» أي بذي ظلم «للعبيد» فيعذبهم بغير ذنب.'),
(476, 3, 183, '«الَّذين» نعت للذين قبله «قالوا» لمحمد «إن الله» قد «عهد إلينا» في التوراة «ألا نؤمن لرسول» نصدقه «حتى يأتينا بقربانٍ تأكله النار» فلا نؤمن لك حتى تأتينا به وهو ما يتقرب به إلى الله من نعم وغيرها فإن قُبل جاءت نار بيضاء من السماء فأحرقته وإلا بقي مكانه وعُهد إلى بني إسرائيل ذلك إلا في المسيح ومحمد قال تعالى «قل» لهم توبيخا «قد جاءكم رسلٌ من قبلي بالبينات» بالمعجزات «وبالذي قلتم» كزكريا ويحيى فقتلتموهم والخطابُ لمن في زمن نبينا محمد صلى الله عليه وسلم وإن كان الفعل لأجدادهم لرضاهم به «فلم قتلتموهم إن كنتم صادقين» في أنكم تؤمنون عند الإتيان به.'),
(477, 3, 184, '«فإن كذَّبوك فقد كُذب رسل من قبلك جاءُوا بالبينات» المعجزات «والزبُر» كصحف إبراهيم «والكتاب» وفي قراءة بإثبات الباء فيهما «المنير» الواضح هو التوراة والإنجيل فاصبر كما صبروا.'),
(478, 3, 185, '«كل نفس ذائقة الموت وإنما توفَّون أجوركم» جزاء أعمالكم «يوم القيامة فمن زُحزح» بعد «عن النار وأدخل الجنة فقد فاز» نال غاية مطلوبه «وما الحياة الدنيا» أي العيش فيها «إلا متاع الغرور» الباطل يتمتع به قليلا ثم يفنى.'),
(479, 3, 186, '«لَتُبْلَوُنَّ» حذف منه نون الرفع لتوالي النونات والواو ضمير الجمع لالتقاء الساكنين، لتختبرن «في أموالكم» بالفرائض فيها والحوائج «وأنفسكم» بالعبادات والبلاء «وَلَتَسْمَعُنَّ من الذين أوتوا الكتاب من قبلكم» اليهود والنصارى «ومن الذين أشركوا» من العرب «أذى كثيرا» من السب والطعن والتشبيب بنسائكم «وإن تصبروا» على ذلك «وتتقوا» الله بالفرائض «فإن ذلك من عزم الأمور» أي: من معزوماتها التي يعزم عليها لوجوبها.'),
(480, 3, 187, '«و» اذكر «إذ أخذ الله ميثاق الذين أوتوا الكتاب» أي العهد عليهم في التوراة «لَيُبَيِّنَنَّهُ» أي الكتاب «للناس ولا يكتمونه» أي الكتاب بالياء والتاء في الفعلين «فنبذوه» طرحوا الميثاق «وراء ظهورهم» فلم يعملوا به «واشتروا به» أخذوا بدله «ثمنا قليلا» من الدنيا من سفلتهم برياستهم في العلم فكتموه خوف فوته عليهم «فبئس ما يشترون» شراؤهم هذا.'),
(481, 3, 188, '«لا تحسبن» بالتاء والياء «الذين يفرحون بما أتوا» فعلوا من إضلال الناس «ويحبون أن يُحمدوا بما لم يفعلوا» من التمسك بالحق وهم على ضلال «فلا تحسبنهم» في الوجهين تأكيد «بمفازة» بمكان ينجون فيه «من العذاب» في الآخرة بل هم في مكان يعذَّبون فيه وهو جهنم «ولهم عذاب أليم» مؤلم فيها، ومفعولا يحسب الاولى دل عليهما مفعولا الثانية على قراءة التحتانية وعلى الفوقانية حذف الثاني فقط.'),
(482, 3, 189, '«ولله ملك السماوات والأرض» خزائن المطر والرزق والنبات وغيرها «والله على كل شيء قدير» ومنه تعذيب الكافرين وإنجاء المؤمنين.'),
(483, 3, 190, '«إن في خلق السماوات والأرض» وما فيهما من العجائب «واختلاف الليل والنهار» بالمجيء والذهاب والزيادة والنقصان «لآيات» دلالات على قدرته تعالى «لأولي الألباب» لذوي العقول.'),
(484, 3, 191, '«الذين» نعت لما قبله أو بدل «يذكرون الله قياما وقعودا وعلى جنوبهم» مضطجعين أي في كل حال، وعن ابن عباس يصلون كذلك حسب الطاقة «ويتفكرون في خلق السماوات والأرض» ليستدلوا به على قدرة صانعهما يقولون «ربنا ما خلقت هذا» الخلق الذي نراه «باطلا» حال، عبثا بل دليلا على كمال قدرتك «سبحانك» تنزيها لك عن العبث «فقِنا عذاب النار».'),
(485, 3, 192, '«ربَّنا إنك من تدخل النار» للخلود فيها «فقد أخزيته» أهنته «وما للظالمين» الكافرين، فيه وضع الظاهر موضع المضمر إشعارا بتخصيص الخزي بهم «من» زائدة «أنصار» يمنعونهم من عذاب الله تعالى.'),
(486, 3, 193, '«ربَّنا إننا سمعنا مناديا ينادي» يدعو الناس «للإيمان» أي إليه وهو محمد أو القرآن «أن» أي بأن «آمنوا بربكم فآمنا» به «ربنا فاغفر لنا ذنوبنا وكفِّر» حط «عنا سيئاتنا» فلا تظهرها بالعقاب عليها «وتوفَّنا» اقبض أرواحنا «مع» في جملة «الأبرار» الأنبياء الصالحين.'),
(487, 3, 194, '«ربَّنا وآتنا» أعطنا «ما وعدتنا» به «على» ألسنه «رسلك» من الرحمة والفضل وسؤالهم ذلك وإن كان وعده تعالى لا يخلف سؤال أن يجعلهم من مستحقيه لأنهم لم يتيقنوا استحقاقهم له وتكرير ربَّنا مبالغة في التضرع «ولا تُخزنا يوم القيامة إنك لا تُخلف الميعاد» الوعد بالبعث والجزاء.'),
(488, 3, 195, '«فاستجاب لهم ربهم» دعاءهم «أنِّي» أي بأني «لا أضيع عمل عامل منكم من ذكر أو أنثى بعضكم» كائن «من بعض» أي الذكور من الإناث وبالعكس والجملة مؤكدة لما قبلها أي هم سواء في المجازاة بالأعمال وترك تضييعها، نزلت لما قالت أم سلمة يا رسول الله إني لا أسمع ذكر النساء في الهجرة بشيء «فالذين هاجروا» من مكة إلى المدينة «وأخرجوا من ديارهم وأوذوا في سبيلي» ديني «وقاتلوا» الكفار «وَقُتِلُوا» بالتخفيف والتشديد وفي قراءة بتقديمه «لأكفِّرنَّ عنهم سيئاتهم» أسترها بالمغفرة «ولأدخلنهم جنات تجري من تحتها الأنهار ثوابا» مصدر من معنى لأكفرن مؤكد له «من عند الله» فيه التفات عن التكلم «والله عنده حسن الثواب» الجزاء.'),
(489, 3, 196, 'ونزل لما قال المسلمون: أعداء الله فيما نري من الخير ونحن في الجهد: «لا يغرنك تقلُّب الذين كفروا» تصرُّفهم «في البلاد» بالتجارة والكسب.'),
(490, 3, 197, 'هو «متاع قليل» يتمتعون به يسيرا في الدنيا ويفنى «ثم مأواهم جهنم وبئس المهاد» الفراش هي.'),
(491, 3, 198, '«لكن الذين اتقوا ربَّهم لهم جنات تجري من تحتها الأنهار خالدين» أي مقدرين بالخلود «فيها نُزُلا» وهو ما يعد للضيف ونصبه على الحال من جَنَّاتٍ والعامل فيها معنى الظرف «من عند الله وما عند الله» من الثواب «خير للأبرار» من متاع الدنيا.'),
(492, 3, 199, '«وإن من أهل الكتاب لمن يؤمن بالله» كعبد الله بن سلام وأصحابه والنجاشي «وما أنزل إليكم» أي القرآن «وما أنزل إليهم» أي التوراة والإنجيل «خاشعين» حال من ضمير يؤمن مراعى فيه معنى من أي: متواضعين «لله لا يشترون بآيات الله» التي عندهم في التوراة والإنجيل من بعث النبي صلى الله عليه وسلم «ثمنا قليلا» من الدنيا بأن يكتموها خوفا على الرياسة كفعل غيرهم من اليهود «أولئك لهم أجرهم» ثواب أعمالهم «عند ربهم» يُؤْتَوْنَهُ مرتين كما في القصص «إن الله سريع الحساب» يحاسب الخلق في قدر نصف نهار من أيام الدنيا.'),
(493, 3, 200, '«يا أيها الذين آمنوا اصبروا» على الطاعات والمصائب وعن المعاصي «وصابروا» الكُفَّار فلا يكونوا أشد صبرا منكم «ورابطوا» أقيموا على الجهاد «واتقوا الله» في جميع أحوالكم «لعلَّكم تفلحون» تفوزون بالجنة وتنجون من النار.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, '«يا أيها الناس» أي أهل مكة «اتقوا ربكم» أي عقابه بأن تطيعوه «الذي خلقكم من نفس واحدة» آدم «وخلق منها زوجها» حواء بالمد من ضلع من أضلاعه اليسرى «وبث» فرق ونشر «منهما» من آدم وحواء «رجالا كثيرا ونساء» كثيرة «واتقوا الله الذي تَسّاءلون» فيه إدغام التاء في الأصل في السين، وفي قراءة بالتخفيف بحذفها أي تتساءلون «به» فيما بينكم حيث يقول بعضكم لبعض أسألك بالله وأنشدك بالله «و» اتقوا «الأرحام» أن تقطعوها، وفي قراءة بالجر عطفا على الضمير في به وكانوا يتناشدون بالرحم «إنَّ الله كان عليكم رقيبا» حافظا لأعمالكم فيجازيكم بها، أي لم يزل متصفا بذلك.'),
(495, 4, 2, 'ونزل في يتيم طلب من وليه ماله فمنعه: «وآتوا اليتامى» الصغار الذين لا أب لهم «أموالهم» إذا بلغوا «ولا تتبدلوا الخبيث» الحرام «بالطيب» الحلال أي تأخذوه بدله كما تفعلون من أخذ الجيد من مال اليتيم وجعل الرديء من مالكم مكانه «ولا تأكلوا أموالهم» مضمومة «إلى أموالكم إنه» أي أكلها «كان حوبا» ذنبا «كبيرا» عظيما ولما نزلت تحرجوا من ولاية اليتامى وكان فيهم من تحته العشر أو الثمان من الأزواج فلا يعدل بينهن فنزل.'),
(496, 4, 3, '«وإن خفتم أ» ن «لا تُقسطوا» تعدلوا «في اليتامى» فتحرجتم من أمرهم فخافوا أيضا أن لا تعدلوا بين النساء إذا نكحتموهن «فانكحوا» تزوجوا «ما» بمعنى من «طاب لكم من النساء مثنى وثلاث ورباع» أي اثنتين وثلاثا وأربعا ولا تزيدوا على ذلك «فإن خفتم أ» ن «لا تعدلوا» فيهن بالنفقة والقسم؟ «فواحدةّ» انكحوها «أو» اقتصروا على «ما ملكت أيمانكم» من الإماء إذ ليس لهن من الحقوق ما للزوجات «ذلك» أي نكاح الأربع فقط أو الواحدة أو التسرَّي «أدنى» أقرب إلى «ألا تعولوا» تجوروا.'),
(497, 4, 4, '«وآتوا» أعطوا «النساء صدقاتهن» جمع صدقة مهورهن «نِحلة» مصدر عطية عن طيب نفس «فإن طبن لكم عن شيء منه نفسا» تمييز محول عن الفاعل، أي طابت أنفسهن لكم عن شيء من الصداق فوهبنه لكم «فكلوه هنيئا» طيبا «مريئا» محمود العاقبة لا ضرر فيه عليكم في الآخرة نزلت ردا على كره ذلك.'),
(498, 4, 5, '«ولا تؤتوا» أيها الأولياء «السفهاء» المبذِّرين من الرجال والنساء والصبيان «أموالكم» أي أموالهم التي في أيديكم «التي جعل الله لكم قياما» مصدر قام أي تقوم بمعاشكم وصلاح أولادكم فيضعوها في غير وجهها وفي قراءة قيَما جمع قيمة ما تقوم به الأمتعة «وارزقوهم فيها» أي اطعموهم منها «واكسوهم وقولوا لهم قولا معروفا» عدوهم عدة جميلة بإعطائهم أموالهم إذا رشدوا.'),
(499, 4, 6, '«وابتلوا» اختبروا «اليتامى» قبل البلوغ في دينهم وتصرفهم في أحوالهم «حتى إذا بلغوا النكاح» أي صاروا أهلا له بالاحتلام أو السن وهو استكمال خمس عشرة سنة عند الشافعي «فإن آنستم» أبصرتم «منهم رشدا» صلاحا في دينهم ومالهم «فادفعوا إليهم أموالهم ولا تأكلوها» أيها الأولياء «إسرافا» بغير حق حال «وبدارا» أي مبادرين إلى إنفاقها مخافة «أن يكبروا» رشداء فيلزمكم تسليمها إليهم «ومن كان» من الأولياء «غنيا فليستعفف» أي يعف عن مال اليتيم ويمتنع من أكله «ومن كان فقيرا فليأكل» منه «بالمعروف» بقدر أجرة عمله «فإذا دفعتم إليهم» أي إلى اليتامى «أموالهم فأشهدوا عليهم» أنهم تسلموها وبرئتم لئلا يقع اختلاف فترجعوا إلى البينة وهذا أمر إرشاد «وكفى بالله» الياء زائدة «حسيبا» حافظا لأعمال خلقه ومحاسبهم.'),
(500, 4, 7, 'ونزل ردا لما كان عليه في الجاهلية من عدم توريث النساء والصغار: «للرجال» الأولاد والأقرباء «نصيب» حظٌ «مما ترك الوالدان والأقربون» المتوفون «وللنساء نصيب مما ترك الوالدان والأقربون مما قلَّ منه» أي المال «أو كثر» جعله الله «نصيبا مفروضا» مقطوعا بتسليمه إليهم.'),
(501, 4, 8, '«وإذا حضر القسمة» للميراث «أولوا القربى» ذوو القرابة ممن لا يرث «واليتامى والمساكين فارزقوهم منه» شيئا قبل القسمة «وقولوا» أيها الأولياء «لهم» إذا كان الورثة صغارا «قولا معروفا» جميلا بأن تعتذروا إليهم أنكم لا تملكونه وأنه للصغار وهذا قيل إنه منسوخ وقيل لا ولكن تهاون الناس في تركه وعليه فهو ندب وعن ابن عباس واجب.'),
(502, 4, 9, '«ولْيخش» أي ليخف على اليتامى «الذين لو تركوا» أي قاربوا أن يتركوا «من خلفهم» أي بعد موتهم «ذرية ضعافا» أولاد صغارا «خافوا عليهم» الضياع «فليتقوا الله» في أمر اليتامى وليأتوا إليهم ما يحبون أن يفعل بذريتهم من بعدهم «ولْيقولوا» للميت «قولا سديدا» صوابا بأن يأمروه أن يتصدق بدون ثلثه ويدع الباقي لورثته ولا يتركهم عالة.'),
(503, 4, 10, '«إن الذين يأكلون أموال اليتامى ظلما» بغير حق «إنما يأكلون في بطونهم» أي ملأها «نارا» لأنه يؤول إليها «وَسَيَصْلَوْنَ» بالبناء للفاعل والمفعول يدخلون «سعيرا» نارا شديدة يحترقون فيها.'),
(504, 4, 11, '«يوصيكم» يأمركم «الله في» شأن «أولادكم» بما يذكر «للذكر» منهم «مثل حظ» نصيب «الأنثيين» إذا اجتمعنا معه فله نصف المال ولهما النصف فإن كان معه واحدة فلها الثلث وله الثلثان وإن انفرد جاز المال «فإن كنَّ» أي الأولاد «نساءً» فقط «فوق اثنتين فلهن ثلثا ما ترك» الميت وكذا الاثنتان لأنه للأختين بقوله «فلهما الثلثان مما ترك» فهما أولى به ولأن البنت تستحق الثلث مع الذكر فمع الأنثى أولى (وفوق) قيل صلة وقيل لدفع توهم زيادة النصيب بزيادة العدد لما فهم استحقاق البنتين الثلثين من جعل الثلث للواحدة مع الذكر «وإن كانت» المولودة «واحدة» وفي قراءة بالرفع فكان تامة «فلها النصف ولأبويه» أي الميت ويبدل منهما «لكل واحد منهما السدس مما ترك إن كان له ولد» ذكر أو أنثى ونكتة البدل إفادة أنهما لا يشتركان فيه وألحق بالولد ولد الابن وبالأب الجد «فإن لم يكن له ولد وورثه أبواه» فقط أو مع زوج «فلأمه» بضم الهمزة وكسرها فرارا من الانتقال من ضمة إلى كسرة لثقله في الموضعين «الثلث» أي ثلث المال أو ما ينبغي بعد الزوج والباقي للأب «فإن كان له إخوة» أي اثنان فصاعدا ذكورا أو إناثا «فلأمه السدس» والباقي للأب ولا شيء للأخوة وإرث من ذكر ما ذُكر «من بعد» تنفيذ «وصية يوصي» بالبناء للفاعل والمفعول «بها أو» قضاء «دين» عليه وتقديم الوصية على الدين وإن كانت مؤخرة عنه في الوفاء للاهتمام بها «آباؤكم وأبناؤكم» مبتدأ خبره «لا تدرون أيهم أقرب لكم نفعا» في الدنيا والآخرة فظان أن ابنه أنفع له فيعطيه الميراث فيكون الأب أنفع وبالعكس وإنما العالم بذلك هو الله ففرض لكم الميراث «فريضة من الله إن الله كان عليما» بخلقه «حكيما» فيما دبَّره لهم: أي لم يزل متصفا بذلك.'),
(505, 4, 12, '«ولكم نصف ما ترك أزواجكم إن لم يكن لهن ولد» منكم أو من غيركم «فإن كان لهن ولد فلكم الربع مما تركن من بعد وصية يوصين بها أو دين» وألحق بالولد في ذلك ولد الابن بالإجماع «ولهن» أي الزوجات تعددن أو لا «الربع مما تركتم إن لم يكن لكم ولد فإن كان لكم ولد» منهن أو من غيرهن «فلهن الثمن مما تركتم من بعد وصية توصون بها أو دين» وولد الابن في ذلك كالولد إجماعا «وإن كان رجل يورث» صفة والخبر «كلالة» أي لا والد له ولا ولد «أو امرأة» تورث كلالة «وله» أي للمورث كلالة «أخ أو أخت» أي من أم وقرأ به ابن مسعود وغيره «فلكل واحد منهما السدس» مما ترك «فإن كانوا» أي الإخوة والأخوات من الأم «أكثر من ذلك» أي من واحد «فهم شركاء في الثلث» يستوي فيه ذكرهم وأنثاهم «من بعد وصية يوصي بها أو دين غير مُضارّ» حال من ضمير يوصي أي غير مدخل الضرر على الورثة بأن يوصي بأكثر من الثلث «وصيةً» مصدر مؤكد ليوصيكم «من الله والله عليم» بما دبره لخلقه من الفرائض «حليم» بتأخير العقوبة عمن خالفه، وخصت السنة توريث من ذكر بمن ليس فيه مانع من قتل أو اختلاف دين أو رقٌ.'),
(506, 4, 13, '«تلك» الأحكام المذكورة من أمر اليتامى وما بعده «حدود الله» شرائعه التي حدَّها لعباده ليعملوا بها ولا يتعدوها «ومن يطع الله ورسوله» فيما حكم به «يدخله» بالياء والنون التفاتا «جنات تجري من تحتها الأنهار خالدين فيها وذلك الفوز العظيم».'),
(507, 4, 14, '«ومن يعص الله ورسوله ويتعد حدوده يدخله» بالوجهين «نارا خالدا فيها وله» فيها «عذاب مهين» ذو إهانة روعي في الضمائر في الآيتين لفظ من وفي خالدين معناها.'),
(508, 4, 15, '(واللاتي يأتين الفاحشة) الزنا (من نسائكم فاستشهدوا عليهن أربعة منكم) أي من رجالكم المسلمين (فإن شهدوا) عليهن بها (فأمسكوهن) احبسوهن (في البيوت) وامنعوهن من مخالطة الناس (حتى يتوفاهن الموت) أي ملائكته (أو) إلى أن (يجعل الله لهن سبيلا) طريقا إلى الخروج منها أمروا بذلك أول الإسلام ثم جعل لهن سبيلا بجلد البكر مائة وتغريبها عاما ورجم المحصنة، وفي الحديث لما بين الحد قال "" خذوا عني خذوا عني قد جعل الله لهن سبيلا "" رواه مسلم.'),
(509, 4, 16, '«وَاًلَّلذّانِ» بتخفيف النون وتشديدها «يأتيانها» أي الفاحشة الزنا أو اللواط «منكم» أي الرجال «فآذوهما» بالسب والضرب بالنعال «فإن تابا» منها «وأصلحا» العمل «فأعرضوا عنهما» ولا تؤذوهما «إن الله كان توَّابا» على من تاب «رحيما» به وهذا منسوخ بالحد إن أريد بها الزنا وكذا إن أريد بها اللواط عن الشافعي لكن المفعول به لا يرجم عنده وإن كان محصنا بل يجلد ويغرب وإرادةُ اللواط أظهر بدليل تثنية الضمير والأول أراد الزاني والزانية ويرده تبيينهما بمن المتصلة بضمير الرجال واشتراكهما في الأذى والتوبة والإعراض وهو مخصوص بالرجال لما تقدم في النساء من الحبس.'),
(510, 4, 17, '«إنما التوبة على الله» أي التي كتب على نفسه قبولها بفضله «للذين يعملون السوء» المعصية «بجهالة» حال أي جاهلين إذا عصوا ربهم «ثم يتوبون من» زمن «قريب» قبل أن يغرغروا «فأولئك يتوب الله عليهم» يقبل توبتهم «وكان الله عليما» بخلقه «حكيما» في صنعه بهم.'),
(511, 4, 18, '«وليست التوبة للذين يعملون السيئات» الذنوب «حتى إذا حضر أحدهم الموتُ» وأخذ في النزع «قال» عند مشاهدة ما هو فيه «إنِّي تبت الآن» فلا ينفعه ذلك ولا يقبل منه «ولا الذين يموتون وهم كفار» إذا تابوا في الآخرة عند معاينة العذاب لا تقبل منهم «أولئك أعتدنا» أعددنا «لهم عذابا أليما» مؤلما.'),
(512, 4, 19, '«يا أيُّها الذين آمنوا لا يحلُّ لكم أن ترثوا النساء» أي ذاتهن «كرها» بالفتح والضم لغتان أي مكرهيهن على ذلك كانوا في الجاهلية يرثون نساء أقربائهم فإن شاءوا تزوجوهن بلا صداق أو زوَّجوهن وأخذوا صداقهن أو عضلوهن حتى يفتدين بما ورثنه أو يمتن فيرثوهن فنُهوا عن ذلك «ولا» أن «تعضلوهن» أي تمنعوا أزواجكم عن نكاح غيركم بإمساكهن ولا رغبة لكم فيهن ضرارا «لتذهبوا ببعض ما آتيتموهن» من المهر «إلا أن يأتين بفاحشة مبيَّنة» بفتح الياء وكسرها أي بينت أو هي بينة أي زنا أو نشوز فلكم أن تضاروهن حتى يفتدين منكم ويختلعن «وعاشروهن بالمعروف» أي بالإجمال في القول والنفقة والمبيت «فإن كرهتموهن» فاصبروا «فعسى أن تكرهوا شيئا ويجعل الله فيه خيرا كثيرا» ولعله يجعل فيهن ذلك بأن يرزقكم منهن ولدا صالحا.'),
(513, 4, 20, '«وإن أردتم استبدال زوج مكان زوج» أي أخذها بدلها بأن طلقتموها «و» قد «آتيتم إحداهن» أي الزوجات «قنطارا» مالا كثيرا صداقا «فلا تأخذوا منه شيئا أتأخُذُونهُ بهتانا» ظلما «وإثما مبينا» بينا ونصبهما على الحال، والاستفهامُ للتوبيخ وللإنكار في قوله.'),
(514, 4, 21, '«وكيف تأخذونه» أي بأي وجه «وقد أفضى» وصل «بعضكم إلى بعض» بالجماع المقرر للمهر «وأخذن منكم ميثاقا» عهدا «غليظا» شديدا وهو ما أمر الله به من إمساكهن بمعروف أو تسريحهن بإحسان.'),
(515, 4, 22, '«ولا تنكحوا ما» بمعنى من «نكح آباؤكم من النساء إلا» لكن «ما قد سلف» من فعلكم ذلك فانه معفوّ عنه «إنه» أي نكاحهن «كان فاحشة» قبيحا «ومقتا» سببا للمقت من الله وهو أشد البغض «وساء» بئس «سبيلا» طريقا ذلك.'),
(516, 4, 23, '(حرمت عليكم أمهاتكم) أن تنكحوهن وشملت الجدات من قبل الأب أو الأم (وبناتكم) وشملت بنات الأولاد وإن سفلن (وأخواتكم) من جهة الأب أو الأم (وعماتكم) أي أخوات آبائكم وأجدادكم (وخالاتكم) أي أخوات أمهاتكم وجداتكم (وبنات الأخ وبنات الأخت) ويدخل فيهن أولادهم (وأمهاتكم اللاتي أرضعنكم) قبل استكمال الحولين خمس رضعات كما بينه الحديث (وأخواتكم من الرضاعة) ويلحق بذلك بالسنة البنات منها وهن من أرضعتهم موطوأته والعمات والخالات وبنات الأخ وبنات الأخت منها لحديث: "" يحرم من الرضاع ما يحرم من النسب "" رواه البخاري ومسلم (وأمهات نسائكم وربائبكم) جمع ربيبة وهي بنت الزوجة من غيره (اللاتي في حجوركم) تربونهن. صفة موافقة للغالب فلا مفهوم لها (من نسائكم اللاتي دخلتم بهن) أي جامعتموهن (فإن لم تكونوا دخلتم بهن فلا جناح عليكم) في نكاح بناتهن إذا فارقتموهن (وحلائل) أزواج (أبنائكم الذين من أصلابكم) بخلاف من تبنيتموهم فلكم نكاح حلائلهم (وأن تجمعوا بين الأختين) من نسب أو رضاع بالنكاح ويلحق بهما بالسنة الجمع بينها وبين عمتها أو خالتها ويجوز نكاح كل واحدة على الانفراد وملكهما معا ويطأ واحدة (إلا) لكن (ما قد سلف) في الجاهلية من نكاحهم بعض ما ذكر فلا جناح عليكم فيه (إن الله كان غفورا) لما سلف منكم قبل النهي (رحيما) بكم في ذلك.'),
(517, 4, 24, '«و» حرمت عليكم «المحصَنات» أي ذوات الأزواج «من النساء» أن تنكحوهن قبل مفارقة أزواجهن حرائر مسلمات كن أو لا «إلا ما ملكت أيمانكم» من الإماء بالسبي فلكم وطؤهن وإن كان لهن أزواج في دار الحرب بعد الاستبراء «كتاب الله» نصب على المصدر أي كتب ذلك «عليكم وَأحلَّ» بالبناء للفاعل والمفعول «لكم ما وراء ذلكم» أي سوى ما حرم عليكم من النساء «أن تبتغوا» تطلبوا النساء «بأموالكم» بصداق أو ثمن «محصنين» متزوجين «غير مسافحين» زانين «فما» فمن «استمتعتم» تمتعتم «به منهن» ممن تزوجتم بالوطء «فآتوهن أجورهن» مهورهن التي فرضتم لهن «فريضة ولا جناح عليكم فيما تراضيتم» أنتم وهن «به من بعد الفريضة» من حطها أو بعضها أو زيادة عليها «إن الله كان عليما» بخلقه «حكيما» فيما دبره لهم.'),
(518, 4, 25, '(ومن لم يستطع منكم طولا) أي غنى لـ (أن ينكح المحصنات) الحرائر (المؤمنات) هو جري على الغالب فلا مفهوم له (فمن ما ملكت أيمانكم) ينكح (من فتياتكم المؤمنات والله أعلم بإيمانكم) فاكتفوا بظاهره وكِلوا السرائر إليه فإنه العالم بتفضيلها ورب أمة تفضل حرة فيه وهذا تأنيس بنكاح الإماء (بعضكم من بعض) أي أنتم وهن سواء في الدين فلا تستنكفوا من نكاحهن (فانكحوهن بإذن أهلهن) مواليهن (وآتوهن) أعطوهن. (أجورهن) مهورهن (بالمعروف) من غير مطل ونقص (محصنات) عفائف حال (غير مسافحات) زانيات جهرا (ولا متخذات أخذان) أخلاء يزنون بهن سرا (فإذا أحصن) زوجن وفي قراءة بالبناء للفاعل تزوجن (فإن أتين بفاحشة) زنا (فعليهن نصف ما على المحصنات) الحرائر الأبكار إذا زنين (من العذاب) الحد فيجلدن خمسين ويغربن نصف سنة ويقاس عليهن العبيد ولم يجعل الإحصان شرطا لوجوب الحد لإفادة أنه لا رجم عليهن أصلا (ذلك) أي نكاح المملوكات عند عدم الطول (لمن خشي) خاف (العنت) الزنا وأصله المشقة سمي به الزنا لأنه سببها بالحد في الدنيا والعقوبة في الآخرة (منكم) بخلاف من لا يخافه من الأحرار فلا يحل له نكاحها وكذا من استطاع طول حرة وعليه الشافعي وخرج بقوله "" من فتياتكم المؤمنات "" الكافرات: فلا يحل له نكاحها ولو عدل وخاف (وأن تصبروا) عن نكاح المملوكات (خير لكم) لئلا يصير الولد رقيقا (والله غفور رحيم) بالتوسعة في ذلك.'),
(519, 4, 26, '«يريد الله ليبين لكم» شرائع دينكم ومصالح أمركم «ويهديكم سنن» طرائق «الذين من قبلكم» من الأنبياء في التحليل والتحريم فتتَّبعوهم «وينوب عليكم» يرجع بكم عن معصيته التي كنتم عليها إلى طاعته «والله عليم» بكم «حكيم» فيما دبره لكم.'),
(520, 4, 27, '«والله يريد أن يتوب عليكم» كرره ليبنى عليه «ويريد الذين يتبعون الشهوات» اليهود والنصارى أو المجوس أو الزناة «أن تميلوا ميلا عظيما» تعدلوا عن الحق بارتكاب ما حُرم عليكم فتكونوا مثلهم.'),
(521, 4, 28, '«يريد الله أن يخفف عنكم» يسهل عليكم أحكام الشرع «وخلق الإنسان ضعيفا» لا يصبر عن النساء والشهوات.'),
(522, 4, 29, '«يا أيها الذين آمنوا لا تأكلوا أموالكم بينكم بالباطل» بالحرام في الشرع كالربا والغصب «إلا» لكن «أن تكون» تقع «تجارة» وفي قراءة بالنصب أن تكون الأموال أموال تجارة صادرة «عن تراضى منكم» وطيب نفس فلكم أن تأكلوها «ولا تقتلوا أنفسكم» بارتكاب ما يؤدي إلى هلاكها أيّا كان في الدنيا أو الآخرة بقرينة «إن الله كان بكم رحيما» في منعه لكم من ذلك.'),
(523, 4, 30, '«ومن يفعل ذلك» أي ما نُهي عنه «عُدوانا» تجاوزا للحلال حال «وظلما» تأكيد «فسوف نصليه» ندخله «نارا» يحترق فيها «وكان ذلك على الله يسيرا» هيِّنا.'),
(524, 4, 31, '«إن تجتنبوا كبائر ما تنهون عنه» وهي ما ورد عليها وعيد كالقتال والزنا والسرقة وعن ابن عباس هي إلى السبعمائة أقرب «نكفِّر عنكم سيِّئاتكم» الصغائر بالطاعات «وندخلكم مُدخلا» بضم الميم وفتحها أي إدخالا أو موضعا «كريما» هو الجنة.'),
(525, 4, 32, '«ولا تتمنَّوا ما فضَّل الله به بعضكم على بعض» من جهة الدنيا أو الدين لئلا يؤدى إلى التحاسد والتباغض «للرجال نصيب» ثواب «مما اكتسبوا» بسبب ما عملوا من الجهاد وغيره «وللنساء نصيب مما اكتسبن» من طاعة أزواجهن وحفظ فروجهن نزلت لما قالت أم سلمة: ليتنا كنا رجالا فجاهدنا وكان لنا مثل أجر الرجال «واسألوا» بهمزة ودونها «الله من فضله» ما احتجتم إليه يعطكم «إن الله كان بكل شيء عليما» ومنه محل الفضل وسؤالكم.'),
(526, 4, 33, '«ولكلٌ» عن الرجال والنساء «جعلنا موالي» عصبة يعطون «مما ترك الوالدان والأقربون» لهم من المال «والذين عاقدت» بألف ودونها «أيمانكم» جمع يمين بمعنى القسم أو اليد أي الخلفاء الذين عاهدتموهم في الجاهلية على النصرة والإرث «فآتوهم» الآن «نصيبهم» حظوظهم من الميراث وهو السدس «إن الله على كل شيء شهيدا» مطلعا ومنه حالكم وهذا منسوخ بقوله (وأولوا الأرحام بعضهم أول ببعض).'),
(527, 4, 34, '«الرِّجال قوَّامون» مسلطون «على النساء» يؤدبونهن ويأخذون على أيديهن «بما فضَّل الله بعضهم على بعض» أي بتفضيله لهم عليهن بالعلم والعقل والولاية وغير ذلك «وبما أنفقوا» عليهن «من أموالهم فالصالحات» منهن «قانتات» مطيعات لأزواجهن «حافظات للغيب» أي لفروجهن وغيرها في غيبة أزواجهن «بما حفظ» لهن «اللهُ» حيث أوصى عليهن الأزواج «والَّتي تخافون نشوزهن» عصيانهن لكم بأن ظهرت أمارته «فعظوهن» فخوِّفوهن الله «واهجروهن في المضاجع» اعتزلوا إلى فراش آخر إن أظهرن النشوز «واضربوهن» ضربا غير مبرح إن لم يرجعن بالهجران «فإن أطعنكم» فيما يراد منهن «فلا تبغوا» تطلبوا «عليهن سبيلا» طريقا إلى ضربهن ظلما «إن الله كان عليا كبيرا» فاحذروه أن يعاقبكم إن ظلمتموهن.'),
(528, 4, 35, '«وإن خفتم» علمتم «شقاق» خلاف «بينهما» بين الزوجين والإضافة للاتساع أي شقاقا بينهما «فابعثوا» إليهما برضاهما «حكما» رجلا عدلا «من أهله» أقاربه «وحكما من أهلها» ويوكل الزوج حكمه في طلاق وقبول عوض عليه وتوكل هي حكمها في الاختلاع فيجتهدان ويأمران الظالم بالرجوع أو يُفَرِّقَان إن رأياه، قال تعالى: «إن يريدا» أي الحكمان «إصلاحا يوفِّق الله بينهما» بين الزوجين أي يقدرهما على ما هو الطاعة من إصلاح أو فراق «إن الله كان عليما» بكل شيء «خبيرا» بالبواطن كالظواهر.'),
(529, 4, 36, '«واعبدوا الله» وحِّدوه «ولا تُشركوا به شيئا و» أحسنوا «بالوالدين إحسانا» برّا ولين جانب «وبذي القربى» القرابة «واليتامى والمساكين والجار ذي القربى» القريب منك في الجوار أو النسب «والجار الجُنُب» البعيد عنك في الجوار أو النسب «والصاحب بالجنب» الرفيق في سفر أو صناعة وقيل الزوجة «وابن السبيل» المنقطع في سفره «وما ملكت أيمانكم» من الأرقاء «إن الله لا يحب من كان مختالا» متكبرا «فخورا» على الناس بما أوتي.'),
(530, 4, 37, '«الذين» مبتدأ «يبخلون» بما يجب عليهم «ويأمرون الناس بالبخل» به «ويكتمون ما آتاهم الله من فضله» من العلم والمال وهم اليهود وخبر المبتدأ لهم وعيد شديد «وأعتدنا للكافرين» بذلك وبغيره «عذابا مهينا» ذا إهانة.'),
(531, 4, 38, '«والذين» عطف على الذين قبله «ينفقون أموالهم رئاء الناس» مرائين لهم «ولا يؤمنون بالله ولا باليوم الآخر» كالمنافقين وأهل مكة «ومن يكن الشيطان له قرينا» صاحبا يعمل بأمره كهؤلاء «فساء» بئس «قرينا» هو.'),
(532, 4, 39, '«وماذا عليهم لو آمنوا بالله واليوم الآخر وأنفقوا مما رزقهم الله» أي أيّ ضرر عليهم في ذلك والاستفهام للإنكار ولو مصدرية أي لا ضرر فيه وإنما الضرر فيما هم عليه «وكان الله بهم عليما» فيجازيهم بما عملوا.'),
(533, 4, 40, '«إن الله لا يظلم» أحدا «مثقال» وزن «ذرَّة» أصغر نملة بأن ينقصها من حسناته أو يزيدها في سيئاته «وإن تك» الذرة «حسنة» من مؤمن وفي قراءة بالرفع فكان تامة «يضاعفها» من عشر إلى أكثر من سبعمائة وفي قراءة يضعفها بالتشديد «ويؤت من لدنه» من عنده مع المضاعفة «أجرا عظيما» لا يقدِّره أحد.'),
(534, 4, 41, '«فكيف» حال الكفـار «إذا جئنا من كل أمة بشهيد» يشهد عليها بعملها وهو نبيها «وجئنا بك» يا محمد «على هؤلاء شهيدا».'),
(535, 4, 42, '«يومئذ» يوم المجيء «يود الذين كفروا وعصوا الرَّسول لو» أي أن «تُسَوَّى» بالبناء للمفعول والفاعل مع حذف إحدى التاءين في الأصل ومع إدغامها في السين أي تتسوى «بهم الأرض» بأن يكونوا ترابا مثلها لعظم هوله كما في آية أخرى (ويقول الكافر يا ليتنى كنت ترابا) «ولا يكتمون الله حديثا» عما عملوه وفي وقت آخر يكتمونه ويقولون (والله ربِّنا ما كنا مشركين).'),
(536, 4, 43, '«يا أيها الذين آمنوا لا تقربوا الصلاة» أي لا تصلوا «وأنتم سكارى» من الشراب لأن سبب نزولها صلاة جماعة في حال سكر «حتى تعلموا ما تقولون» بأن تصْحوا «ولا جُنُبا» بإيلاج أو إنزال ونصبه على الحال وهو يطلق على المفرد وغيره «إلا عابري» مجتازي «سبيل» طريق أي مسافرين «حتى تغتسلوا» فلكم أن تصلوا واستثناء المسافر لأن له حكما آخر سيأتي وقيل المراد النهي عن قربان مواضع الصلاة أي المساجد إلا عبورها من غير مكث «وإن كنتم مرضى» مرضا يضره الماء «أو على سفر» أي مسافرين وأنتم جنب أو محدثون «أو جاء أحد منكم من الغائط» هو المكان المعَدُّ لقضاء الحاجة أي أحدث «أو لامستم النساء» وفي قراءة بلا ألف وكلاهما بمعنى اللمس هو الجَسُّ باليد قاله ابن عمر وعليه الشافعى وألحق به الجس بباقي البشرة وعن ابن عباس هو الجماع «فلم تجدوا ماءً» تتطهرون به للصلاة بعد الطلب والتفتيش وهو راجع إلى ما عدا المرضى «فتيمموا» اقصدوا بعد دخول الوقت «صعيدا طيبا» ترابا طاهرا فاضربوا به ضربتين «فامسحوا بوجوهكم وأيديكم» مع المرفقين منه ومسح يتعدى بنفسه وبالحرف «إن الله كان عفوا غفورا».'),
(537, 4, 44, '«ألم تر إلى الذين أوتوا نصيبا» حظا «من الكتاب» وهم اليهود «يشترون الضلالة» بالهدى «ويريدون أن تضلوا السبيل» تخطئوا الطريق الحق لتكونوا مثلهم.'),
(538, 4, 45, '«والله أعلم بأعدائكم» منكم فيخبركم بهم لتجتنبوهم «وكفى بالله وليا» حافظا لكم منهم «وكفى بالله نصيرا» مانعا لكم من كيدهم.'),
(539, 4, 46, '«من الذين هادوا» قوم «يحِّرفون» يغيرون «الكلم» الذي أنزل الله في التوراة من نعت محمد صلى الله عليه وسلم «عن مواضعه» التي وضع عليها «ويقولون» للنبي صلى الله عليه وسلم إذا أمرهم بشيء «سمعنا» قولك «وعصينا» أمرك «واسمع غير مُسمع» حال بمعنى الدعاء أي لا سمعت «و» يقولون له «راعنا» وقد نهى عن خطابه وهي كلمة سب بلغتهم «ليٌا» تحريفا «بألسنتهم وطعنا» قدحا «في الدين» الإسلام «ولو أنهم قالوا سمعنا وأطعنا» بذل وعصينا «واسمع» فقط «وانظرنا» انظر إلينا بدل راعنا «لكان خيرا لهم» مما قالوه «وأقوم» أعدل منه «ولكن لعنهم الله» أبعدهم عن رحمته «بكفرهم فلا يؤمنون إلا قليلا» منهم كعبد الله بن سلام وأصحابه.'),
(540, 4, 47, '«يا أيها الذين أوتوا الكتاب آمنوا بما نزّلنا» من القرآن «مصدّقا لما معكم» من التوراة «من قبل أن نطمس وجوها» نمحو ما فيها من العين والأنف والحاجب «فنردها على أدبارها» فنجعلها كالأقفاء لوحا واحدا «أو نلعنهم» نمسخهم قردة «كما لعنَّا» مسخنا «أصحاب السبت» منهم «وكان أمر الله» قضاؤه «مفعولا» ولما نزلت أسلم عبد الله بن سلام فقيل كان وعيدا بشرط فلما أسلم به ببعضهم رفع وقيل يكون طمس ومسح قبل قيام الساعة.'),
(541, 4, 48, '«إن الله لا يغفر أن يُشرك» أي الإشراك «به ويغفر ما دون» سوى «ذلك» من الذنوب «لمن يشاء» المغفرة له بأن يدخله الجنة بلا عذاب ومن شاء عذّبه من المؤمنين بذنوبه ثم يدخله الجنة «ومن يشرك بالله فقد افترى إثما» ذنبا «عظيما» كبيرا.'),
(542, 4, 49, '«ألم تر إلى الذين يزكُون أنفسهم» وهم اليهود حيث قالوا نحن أبناء الله وأحباؤه أي ليس الأمر بتزكيتهم أنفسهم «بل الله يزكّي» يطهر «من يشاء» بالإيمان «ولا يُظلمون» ينقصون من أعمالهم «فتيلا» قدر قشرة النواة.'),
(543, 4, 50, '«اُنظر» متعجبا «كيف يفترون على الله الكذب» بذلك «وكفى به إثما مبينا» بيِّنا.'),
(544, 4, 51, 'ونزل في كعب بن الأشرف ونحوه من علماء اليهود لما قدموا مكة وشاهدوا قتلى بدر وحرضوا المشركين على الأخذ بثأرهم ومحاربة النبي صلى الله عليه وسلم «ألم تر إلى الذين أوتوا نصيبا من الكتاب يؤمنون بالجبت والطاغوت» صنمان لقريش «ويقولون للذين كفروا» أبي سفيان وأصاحبه حين قالوا لهم: نحن أهدى سبيلا ونحن ولاة البيت نسقي الحاج ونقري الضيف ونفك العاني ونفعل... أم محمد وقد خالف دين آبائه وقطع الرحم وفارق الحرم «هؤلاء» أي أنتم «أهدى من الذين آمنوا سبيلا» أقوم طريقا.'),
(545, 4, 52, '«أولئك الذين لعنهم الله ومن يلعنـ» ـه «الله فلن تجد له نصيرا» مانعا من عذابه.'),
(546, 4, 53, '«أم» بل «لهم نصيب من الملك» أي ليس لهم شيء منه ولو كان «فإذا لا يؤتون الناس نقيرا» أي شيئا تافها قدر النقرة في ظهر النواة لفرط بخلهم.'),
(547, 4, 54, '«أم» بل «يحسدون الناس» أي النبي صلى الله عليه وسلم «على ما آتاهم الله من فضله» من النبوة وكثرة النساء، أي يتمنون زواله عنه ويقولون لو كان نبيا لاشتغل عن النساء «فقد آتينا آل إبراهيم» جده كموسى وداود وسليمان «الكتاب والحكمة» والنبوة «وآتيناهم ملكا عظيما» فكان لداود تسع وتسعون امرأة ولسليمان ألف ما بين حُرَّةِ وسرية.'),
(548, 4, 55, '«فمنهم من آمن به» بمحمد صلى الله عليه وسلم «ومنهم من صدَّ» أعرض «عنه» فلم يؤمن «وكفي بجهنم سعيرا» عذابا لمن لا يؤمن.'),
(549, 4, 56, '«إن الذين كفروا بآياتنا سوف نصليهم» ندخلهم «نارا» يحترقون فيها «كلما نضجت» احترقت «جلودهم بدَّلناهم جلودا غيرها» بأن تعاد إلى حالها الأول غير محترقة «ليذوقوا العذاب» ليقاسوا شدته «إن الله كان عزيزا» لا يعجزه شيء «حكيما» في خلقه.'),
(550, 4, 57, '«والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا لهم فيها أزواج مطهرة» من الحيض وكل قذر «وندخلهم ظلا ظليلا» دائما لا تنسخه شمس وهو ظل الجنة,'),
(551, 4, 58, '«إن الله يأمركم أن تؤدوا الأمانات» أي ما اؤتمن عليه من الحقوق «إلى أهلها» نزلت لما أخذ عليّ رضي الله عنه مفتاح الكعبة من عثمان بن طلحة الحجبي سادنها قسرا لما قدم النبي صلى الله عليه وسلم مكة عام الفتح ومنعه وقال لو علمت أنه رسول الله لم أمنعه فأمر رسول الله صلى الله عليه وسلم برده إليه وقال هاك خالدة تالدة فعجب من ذلك فقرأ له عليُّ الآية، فأسلم وأعطاه عند موته لأخيه شيبة فبقي في ولده والآيةُ وإن وردت على سبب خاص فعمومها معتبر بقرينة الجمع «وإذا حكمتم بين الناس» يأمركم «أن تحكموا بالعدل إن الله نعمَّا» فيه إدغام ميم نعم في ما النكرة الموصولة أي نعم شيئا «يعظكم به» تأدية الأمانة والحكم بالعدل «إن الله كان سميعا» لما يقال «بصيرا» بما يفعل.'),
(552, 4, 59, '«يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي» وأصحاب «الأمر» أي الولاة «منكم» إذا أمروكم بطاعة الله ورسوله «فإن تنازعتم» اختلفتم «في شيء فردوه إلى الله» أي إلى كتابه «والرسول» مدة حياته وبعده إلى سنته أي اكشفوا عليه منهما «إن كنتم تؤمنون بالله واليوم الآخر ذلك» أي الرد إليهما «خير» لكم من التنازع والقول بالرأي «وأحسن تأويلاً» مآلاً.'),
(553, 4, 60, 'ونزل لما اختصم يهودي ومنافق فدعا المنافق إلى كعب بن الأشرف ليحكم بينهما ودعا اليهودي إلى النبي صلى الله عليه وسلم فأتياه فقضى لليهودي فلم يرض المنافق وأتيا عمر فذكر اليهودي ذلك فقال للمنافق أكذلك فقال نعم فقتله «ألم ترَ إلى الذين يزعمون أنهم آمنوا بما أنزل إليك وما أُنزل من قبلك يريدون أن يتحاكموا إلى الطاغوت» الكثير الطغيان وهو كعب بن الأشرف «وقد أمروا أن يكفروا به» ولا يوالوه «ويريد الشيطان أن يضلَّهم ضلالا بعيدا» عن الحق.'),
(554, 4, 61, '«وإذا قيل لهم تعالوا إلى ما أنزل الله» في القرآن من الحكم «وإلى الرسول» ليحكم بينكم «رأيت المنافقين يصدون» يُعرضون «عنك» إلى غيرك «صدودا».'),
(555, 4, 62, '«فكيف» يصنعون «إذا أصابتهم مصيبة» عقوبة «بما قدّمت أيديهم» من الكفر والمعاصي أي أيقدرون على الإعراض والفرار منها؟ لا «ثم جَاءُوك» معطوف على يصدون «يحلفون بالله إن» ما «أردنا» بالمحاكمة إلى غيرك «إلا إحسانا» صلحا «وتوفيقا» تأليفا بين الخصمين بالتقريب في الحكم دون الحمل على مر الحق.'),
(556, 4, 63, '«أولئك الذين يعلم الله ما في قلوبهم» من النفاق وكذبهم في عذرهم «فأعرض عنهم» بالصفح «وعِظهم» خوِّفهم الله «وقل لهم في» شأن «أنفسهم قولا بليغا» مؤثرا فيهم أي أزجرهم ليرجعوا عن كفرهم.'),
(557, 4, 64, '«وما أرسلنا من رسول إلا ليُطاع» فيما يأمر به ويحكم «بإذن الله» بأمره لا ليعصى ويخالف «ولو أنهم إذ ظلموا أنفسهم» بتحاكمهم إلى الطاغوت «جاءُوك» تائبين «فاستغفروا الله واستغفر لهم الرسول» فيه التفات عن الخطاب تفخيما لشأنه «لوجدوا الله توَابا» عليهم «رحيما» بهم.'),
(558, 4, 65, '«فلا وربِّك» لا زائدة «لا يؤمنون حتى يحكِّموك فيما شجر» اختلط «بينهم ثم لا يجدوا في أنفسهم حرجا» ضيقا أو شكٌا «مما قضيت» به «ويسلِّموا» ينقادوا لحكمك «تسليما» من غير معارضة.'),
(559, 4, 66, '«ولو أنا كتبنا عليهم أن» مفسرة «اقتلوا أنفسكم أو اخرجوا من دياركم» كما كتبنا على بني إسرائيل «ما فعلوه» أي المكتوب عليهم «إلا قليل» بالرفع على البدل والنصب على الاستثناء «منهم ولو أنهم فعلوا ما يوعَظون به» من طاعة الرسول صلى الله عليه وسلم «لكان خيرا لهم وأشد تثبيتا» تحقيقا لإيمانهم.'),
(560, 4, 67, '«وإذا» أي لو ثبتوا «لآتيناهم من لدَّنا» من عندنا «أجرا عظيما» هو الجنة.'),
(561, 4, 68, '«ولهديناهم صراطا مستقيما» قال بعض الصحابة للنبي صلى الله عليه وسلم كيف نراك في الجنة وأنت في الدرجات العلى ونحن أسفل منك فنزل.'),
(562, 4, 69, '«ومن يطع الله والرسول» فيما أمر به «فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين» أفاضل أصحاب الأنبياء لمبالغتهم في الصدق والتصديق «والشهداء» القتلى في سبيل الله «والصالحين» غير من ذكر «وحسُن أولئك رفيقا» رفقاء في الجنة بأن يستمتع فيها برؤيتهم وزيارتهم والحضور معهم وإن كان مقرهم في الدرجات العالية بالنسبة إلى غيرهم.'),
(563, 4, 70, '«ذلك» أي كونهم مع ذكر مبتدأ خبره «الفضل من الله» تفضل به عليهم لا أنهم نالوه بطاعتهم «وكفى بالله عليما» بثواب الآخرة أي فثقوا بما أخبركم به (ولا ينبئك مثل خبير).'),
(564, 4, 71, '«يا أيها الذين آمنوا خذوا حذركم» من عدوكم أي احترزوا منه وتيقظوا له «فانفروا» انهضوا إلى قتاله «ثُبَاتٍ» متفرقين سرية بعد أخرى «أو انفروا جميعا» مجتمعين.'),
(565, 4, 72, '«وإنَّ منكم لمن ليبطئنَّ» ليتأخرن عن القتال كعبد الله بن أبيّ المنافق وأصحابه وجعله منهم من حيث الظاهر واللام في الفعل للقسم «فإن أصابتكم مصيبة» كقتل وهزيمة «قال قد أنعم الله علىَّ إذ لم أكن معهم شهيدا» حاضرا فأصاب.'),
(566, 4, 73, '«ولئن» لام قسم «أصابكم فضل من الله» كفتح وغنيمة «لَيَقُولَنَّ» نادما «كأن» مخففة واسمها محذوف أي كأنه «لم يكن» بالياء والتاء «بينكم وبينه مودة» معرفة وصداقة وهذا راجع إلى قوله (قد أنعم الله علىَّ)، اعترض به بين القول ومقوله وهو «يا» للتنبيه «ليتني كنت معهم فأفوزَ فوزا عظيما» آخذ حظا وافرا من الغنيمة قال تعالى.'),
(567, 4, 74, '«فليقاتل في سبيل الله» لإعلاء دينه «الذين يشرون» يبيعون «الحياة الدنيا بالآخرة ومن يقاتل في سبيل الله فيُقتل» يستشهد «أو يَغلب» يظفر بعدوه «فسوف نؤتيه أجرا عظيما» ثوابا جزيلا.'),
(568, 4, 75, '«وما لكم لا تقاتلون» إستفهام توبيخ، أي لا مانع لكم من القتال «في سبيل الله و» في تخليص «المستضعفين من الرجال والنساء والولدان» الذين حبسهم الكفار عن الهجرة وآذوهم، قال ابن عباس رضي الله عنهما: كنت أنا وأمي منهم «الذين يقولون» داعين يا «ربنا أخرجنا من هذه القرية» مكة «الظالم أهلُها» بالكفر «واجعل لنا من لدنك» من عندك «وليّاً» يتولى أمورنا «واجعل لنا من لدنك نصيرا» يمنعنا منهم وقد استجاب الله دعاءهم فيَسَّر لبعضهم الخروج وبقي بعضهم إلى أن فتحت مكة ووَلَّى صلى الله عليه وسلم عتاب بن أسيد فأنصف مظلومهم من ظالمهم.'),
(569, 4, 76, '«الذين آمنوا يقاتلون في سبيل الله والذين كفروا يقاتلون في سبيل الطاغوت» الشيطان «فقاتلوا أولياء الشيطان» أنصار دينه تغلبوهم لقوتكم بالله «إن كيد الشيطان» بالمؤمنين «كان ضعيفا» واهيا لا يقاوم كيد الله بالكافرين'),
(570, 4, 77, '«ألم تر إلى الذين قيل لهم كفُّوا أيديكم» عن قتال الكفار لما طلبوه بمكة لأذى الكفار لهم وهم جماعة من الصحابة «وأقيموا الصلاة وآتوا الزكاة فلما كُتب» فرض «عليهم القتال إذا فريق منهم يخشون» يخافون «الناس» الكفار، أي عذابهم بالقتل «كَخَشْيَتـ» ـهم عذاب «الله أو أشدَّ خشية» من خشيتهم له ونصب أشد على الحال وجواب لما دل عليه إذا وما بعدها أي فاجأتهم الخشية «وقالوا» جزعا من الموت «ربَنا لم كتبت علينا القتال لولا» هلاّ «أخَّرتنا إلى أجل قريب قل» لهم «متاعُ الدنيا» ما يتمتع به فيها أو الاستمتاع بها «قليل» آيل إلى الفناء «والآخرة» أي الجنة «خير لمن اتقى» عقاب الله بترك معصيته «ولا تُظلمون» بالتاء والياء تنقصون من أعمالكم «فتيلا» قدر قشرة النواة فجاهدوا.'),
(571, 4, 78, '«أينما تكونوا يدرككُّم الموت ولو كنتم في بروج» حصون «مشيدة» مرتفعة فلا تخشوا القتال خوف الموت «وإن تصبهم» أي اليهود «حسنة» خصب وسعة «يقولوا هذه من عند الله وإن تصبهم سيئة» جدب وبلاء كما لهم عند قدوم النبي صلى الله عليه وسلم المدينة «يقولوا هذه من عندك» يا محمد أي بشؤمك «قل» لهم «كل» من الحسنة والسيئة «من عند الله» من قبله «فمال هؤلاء القوم لا يكادون يفقهون» أي لا يقاربون أن يفهموا «حديثا» يُلقى إليهم وما استفهام تعجب من فرط جهلهم ونفي مقاربة الفعل أشد من نفيه.'),
(572, 4, 79, '(ما أصابك) أيها الإنسان (من حسنة) خير (فمن الله) أتتك فضلا منه (وما أصابك من سيئة) بليَّة (فمن نفسك) أتتك حيث ارتكبت ما يستوجبها من الذنوب (وأرسلناك) يا محمد (للناس رسولا) حال مؤكدة (وكفى بالله شهيدا) على رسالتك.'),
(573, 4, 80, '«من يطع الرسول فقد أطاع الله ومن تولى أعرض عن طاعتك فلا يهمنَّك «فما أرسلناك عليهم حفيظا» حافظا لأعمالهم بل نذيرا وإلينا أمرهم فنجازيهم وهذا قبل الأمر بالقتال.'),
(574, 4, 81, '«ويقولون» أي المنافقون إذا جاءوك أمرنا «طاعةٌ» لك «فإذا برزوا» خرجوا «من عندك بيَّت طائفة منهم» بإدغام التاء في الطاء وتركه أي أضمرت «غير الذي تقول» لك في حضورك من الطاعة أي عصيانك «والله يكتب» يأمر بكتب «ما يبيِّتون» في صحائفهم ليجازوا عليه «فأعرض عنهم» بالصفح «وتوكل على الله» ثق به فانه كافيك «وكفى بالله وكيلا» مفوضا إليه.'),
(575, 4, 82, '«أفلا يتدبرون» يتأملون «القرآن» وما فيه من المعاني البديعة «ولو كان من عند غير الله لوجدوا فيه اختلافا كثيرا» تناقضا في معانيه وتباينا في نظمه.'),
(576, 4, 83, '«وإذا جاءهم أمر» عن سرايا النبي صلى الله عليه وسلم ما حصل لهم «من الأمن» بالنصر «أو الخوف» الهزيمة «أذاعوا به» أفشَوه نزل في جماعة من المنافقين أو في ضعفاء المؤمنين كانوا يفعلون ذلك فتضعف قلوب المؤمنين ويتأذى النبي «ولو ردوه» أي الخبر «إلى الرسول وإلى أُولي الأمر منهم» أي الرأي من أكابر الصحابة أي لو سكتوا عنه حتى يخبروا به «لعَلِمَه» هل هو مما ينبغي أن يذاع أو لا «الذين يستنبطونه» يتبعونه ويطلبون علمه وهم المذيعون «منهم» من الرسول وأولي الأمر «ولولا فضل الله عليكم» بالإسلام «ورحمته» لكم بالقرآن «لاتبعتم الشيطان» فيما يأمركم به من الفواحش «إلا قليلا».'),
(577, 4, 84, '(فقاتل) يا محمد (في سبيل الله لا تكلف إلا نفسك) فلا تهتم بتخلفهم عنك المعنى قاتل ولو وحدك فإنك موعود بالنصر (وحرض المؤمنين) حثهم على القتال ورغبهم فيه (عسى الله أن يكف بأس) حرب (الذين كفروا والله أشد بأسا) منهم (وأشد تنكيلا) تعذبيا منهم فقال رسول الله صلى الله عليه وسلم: "" والذي نفسي بيده لأخرجن ولو وحدي "" فخرج بسبعين راكبا إلى بدر الصغرى فكف الله بأس الكفار بإلقاء الرعب في قلوبهم ومنع أبي سفيان عن الخروج كما تقدم في آل عمران.'),
(578, 4, 85, '«من يشفع» بين الناس «شفاعة حسنة» موافقة للشرع «يكن له نصيب» من الأجر «منها» بسببها «ومن يشفع شفاعة سيئة» مخالفة له «يكن له كفل» نصيب من الوزر «منها» بسببها «وكان الله على كل شيء مقيتا» مقتدرا فيجازي كلَّ أحد بما عمل.'),
(579, 4, 86, '«وإذا حُيِّيتم بتحية» كأن قيل لكم سلام عليكم «فحيُّوا» المحيِّي «بأحسن منها» بأن تقولوا له عليك السلام ورحمة الله وبركاته «أوردُّوها» بأن تقولوا له كما قال أي الواجب أحدهما والأول أفضل «إن الله كان على كل شيء حسيبا» محاسبا فيجازي عليه ومنه ردُّ السلام وخصّت السنة الكافر والمبتدع والفاسق والمسلِّم على قاضي الحاجة ومن في الحمام والآكل فلا يجب الرد عليهم بل يكره في غير الأخير ويقال للكافر وعليك.'),
(580, 4, 87, '«الله لا إله إلا هو» والله «ليجمعنكم» من قبوركم «إلى» في «يوم القيامة لا ريب» لا شك «فيه ومن» أي لا أحد «أصدق من الله حديثا» قولا.'),
(581, 4, 88, 'ولما رجع ناس من أُحد اختلف الناس فيهم، فقال فريق اقتلهم، وقال فريق: لا، فنزل: «فما لكم» ما شأنكم صرتم «في المنافقين فئتين» «والله أركسهم» ردهم «بما كسبوا» من الكفر والمعاصي «أتريدون أن تهدوا من أضلًـ» ـه «الله» أي تعدوهم من جملة المهتدين، والاستفهام في الموضعين للإنكار «ومن يضللـ» ـه «اللهُ فلن تجد له سبيلا» طريقا إلى الهدى.'),
(582, 4, 89, '«ودُّوا» تمنوا «لو تكفرون كما كفروا فتكونون» أنتم وهم «سواء» في الكفر «فلا تتخذوا منهم أولياء» توالونهم وإن أظهروا الإيمان «حتى يهاجروا في سبيل الله» هجرة صحيحة تحقق إيمانهم «فإن تَوَلَّوْا» وأقاموا على ما هم عليه «فخذوهم» بالأسر «واقتلوهم حيث وجدتموهم ولا تتخذوا منهم وليا» توالونه «ولا نصيرا» تنتصرون به على عدوكم.'),
(583, 4, 90, '«إلا الذين يصلون» يلجئون «إلى قوم بينكم وبينهم ميثاق» عهد بالأمان لهم ولمن وصل إليهم كما عاهد النبي صلى الله عليه وسلم هلال بن عويمر الأسلمي «أو» الذين «جاَءُوكم» وقد «حَصِرَتْ» ضاقت «صدورهم» عن «أن يقاتلوكم» مع قومهم «أو يقاتلوا قومهم» معكم أي ممسكين عن قتالكم وقتالهم فلا تتعرضوا إليهم بأخذ ولا قتل وهذا ما بعده منسوخ بآية السيف «ولو شاء الله» تسليطهم عليكم «لسلطهم عليكم» بأن يقّوي قلوبهم «فلقاتلوكم» ولكنه لم يشأه فألقى في قلوبهم الرعب «فإن اعتزلوكم فلم يقاتلوكم وألقوا إليكم السَّلَمَ» الصلح أي انقادوا «فما جعل الله لكم عليهم سبيلا» طريقا بالأخذ والقتال.'),
(584, 4, 91, '«ستجدون آخرين يريدون أن يأمنوكم» بإظهار الإيمان عندكم «ويأمنوا قومهم» بالكفر إذا رجعوا إليهم وهم أسد وغطفان «كلما رُدُّوا إلى الفتنة» دعوا إلى الشرك «أركسوا فيها» وقعوا أشد وقوع «فإن لم يعتزلوكم» بترك قتالكم «و» لم «يلقوا إليكم السَّلم و» لم «يكفوا أيديهم» عنكم «فخذوهم» بالأسر «واقتلوهم حيث ثقفتموهم» وجدتموهم «وأولئكم جعلنا لكم عليهم سلطانا مبينا» برهانا بينا ظاهرً على قتلهم وسبيهم لغدرهم.'),
(585, 4, 92, '«وما كان لمؤمن أن يقتل مؤمنا» أي ما ينبغي أن يصدر منه قتل له «إلا خطئا» مخطئا في قتله من غير قصد «ومن قتل مؤمنا خطأ» بأن قصد رمي غيره كصيد أو شجرة فأصابه أو ضربه بما لا يقتل غالبا «فتحرير» عتق «رقبة» نسمة «مؤمنة» عليه «ودية مسلمة» مؤداة «إلى أهله» أي ورثة المقتول «إلا أن يصَّدقوا» يتصدقوا عليه بها بأن يعفوا عنها وبينت السنة أنها مائة من الإبل عشرون بنت مخاض وكذا بنات لبون وبنو لبون، وحقاق وجذاع وأنها على عاقلة القاتل وهم عصبته إلا الأصل والفرع موزعة عليهم على ثلاث سنين على الغني منهم نصف دينار والمتوسط ربع كل سنة فان لم يفوا فمن بيت المال فإن تعذر فعلى الجاني «فإن كان» المقتول «من قوم عدوٌ» حرب «لكم وهو مؤمن فتحرير رقبة مؤمنة» على قاتله كفارة ولا دية تسلم إلى أهله لحرابتهم «وإن كان» المقتول «من قوم بينكم وبينهم ميثاق» عهد كأهل الذمة «فدية» له «مسلَّمة إلى أهله» وهي ثلث دية المؤمن إن كان يهوديا أو نصرانيا وثلثا عشرها إن كان مجوسيا «وتحرير رقبة مؤمنة» على قاتله «فمن لم يجد» الرقبة بأن فقدها وما يحصلها به «فصيام شهرين متتابعين» عليه كفارة ولم يذكر الله تعالى الانتقال إلى الطعام كالظهار وبه أخذ الشافعي في أصح قوليه «توبة من اللهِ» مصدر منصوب بفعله المقدر «وكان الله عليما» بخلقه «حكيما» فيما دبره لهم.'),
(586, 4, 93, '«ومن يقتل مؤمنا متعمِّدا» بأن يقصد قتله بما يقتل غالبا عالما بإيمانه «فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه» أبعده من رحمته «وأعد له عذابا عظيما» في النار وهذا مؤوَّل بمن يستحله أو بأن هذا جزاؤه إن جُوزي ولا يدفع في خلف الوعيد لقوله (ويغفر ما دون ذلك لمن يشاء) وعن ابن عباس أنها على ظاهرها وأنها ناسخة لغيرها من آيات المغفرة وبينت آية البقرة أن قاتل العمد يقتل به وأن عليه الدية إن عفي عنه وسبق قدرها وبينت السنة أن بين العمد والخطأ قتلا يسمى شبه العمد وهو أن يقتله بما لا يقتل غالبا فلا قصاص فيه بل دية كالعمد في الصفة والخطأ في التأجيل والحمل وهو والعمد أولى بالكفارة من الخطأ.'),
(587, 4, 94, 'ونزل لما مر نفر من الصحابة برجل من بني سليم وهو يسوق غنما فسلم عليهم فقالوا ما سلم علينا إلا تقية فقتلوه واستاقوا غنمه «يا أيها الذين آمنوا إذا ضربتم» سافرتم للجهاد «في سبيل الله فتبينوا» وفي قراءة فتثبتوا في الموضعين «ولا تقولوا لمن ألقى إليكم السلام» بألف أو دونها أي التحية أو الانقياد بكلمة الشهادة التي هي أمارة على الإسلام «لست مؤمنا» وإنما قلت هذا تقيه لنفسك ومالك فقتلوه «تبتغون» تطلبون لذلك «عرَضَ الحياة الدنيا» متاعها من الغنيمة «فعند الله مغانم كثيرة» تغنيكم عن قتل مثله لماله «كذلك كنتم من قبل» تعصم دماؤكم وأموالكم بمجرد قولكم الشهادة «فمنَّ الله عليكم» بالاشتهار وبالإيمان والاستقامة «فتبينوا» أن تقتلوا مؤمناً وافعلوا بالداخل في الإسلام كما فُعل بكم «إن الله كان بما تعملون خبيرا» فيجازيكم به.'),
(588, 4, 95, '«لا يستوي القاعدون من المؤمنين» عن الجهاد «غير أولي الضرر» بالرفع صفة والنصب استثناء من زمانة أو عمى ونحوه «والمجاهدون في سبيل الله بأموالهم وأنفسهم فضَّل الله المجاهدين بأموالهم وأنفسهم على القاعدين» لضرر «درجة» فضيلة لاستوائهما في النية وزيادة المجاهدين بالمباشرة «وكلاٌ» من الفريقين «وعد الله الحسنى» الجنة «وفضَّل الله المجاهدين على القاعدين» لغير ضرر «أجرا عظيما» ويبدل منه.'),
(589, 4, 96, '«درجات منه» منازل بعضها فوق بعض من الكرامة «ومغفرة ورحمة» منصوبان بفعلهما المقدر «وكان الله غفورا» لأوليائه «رحيما» بأهل طاعته. ونزل في جماعة أسلموا ولم يهاجروا فقتلوا يوم بدر مع الكفار.'),
(590, 4, 97, '«إن الذين توفاهم الملائكة ظالمي أنفسهم» بالمقام مع الكفار وترك الهجرة «قالوا» لهم موبخين «فيم كنتم» أي في شيء كنتم في أمر دينكم «قالوا» معتذرين «كنا مستضعَفين» عاجزين عن إقامة الدين «في الأرض» أرض مكة «قالوا» لهم توبيخا «ألم تكن أرض الله واسعة فتهاجروا فيها» من أرض الكفر إلى بلد آخر كما فعل غيركم، قال الله تعالى «فأولئك مأواهم جهنم وساءت مصيرا» هي.'),
(591, 4, 98, '«إلا المستضعفين من الرجال والنساء والولدان» الذين «لا يستطيعون حيلة» لا قوة لهم على الهجرة ولا نفقة «ولا يهتدون سبيلا» طريقا إلى أرض الهجرة.'),
(592, 4, 99, '«فأولئك عسى اللهُ أن يعفو عنهم وكان الله عفوَّا غفورا».'),
(593, 4, 100, '«ومن يهاجر في سبيل الله يجد في الأرض مراغما» مهاجرا «كثيرا وسعة» في الرزق «ومن يخرج من بيته مهاجرا إلى الله ورسوله ثم يدركه الموت» في الطريق كما وقع لجندع بن ضمرة الليثي «فقد وقع» ثبت «أجره على الله وكان الله غفورا رحيما».'),
(594, 4, 101, '«وإذا ضربتم» سافرتم «في الأرض فليس عليكم جُناح» في «أن تَقصروا من الصلاة» بأن تردُّوها من أربع إلى اثنتين «إن خفتم أن يفتنكم» أي ينالكم بمكروه «الذين كفروا» بيان للواقع إذ ذاك فلا مفهوم له وبينت السنة أن المراد بالسفر الطويل وهو أربع برد وهي مرحلتان ويؤخذ من قوله تعالى: (فليس عيكم جُناح) أنه رخصة لا واجب وعليه الشافعي «إن الكافرين كانوا لكم عدوا مبينا» بيّني العداوة.'),
(595, 4, 102, '«وإذا كنت» يا محمد حاضرا «فيهم» وأنتم تخافون العدو «فأقمت لهم الصلاة» وهذا جري على عادة القرآن في الخطاب «فلتقم طائفة منهم معك» وتتأخر طائفة «وليأخذوا» أي الطائفة التي قامت معك «أٍسلحتهم» معهم «فإذا سجدوا» أي صلوا «فليكونوا» أي الطائفة الأخرى «من ورائكم» يحرسون إلى أن تقضوا الصلاة وتذهب هذه الطائفة تحرس «ولتأت طائفة أخرى لم يصلوا فليصلوا معك وليأخذوا حذرهم وأسلحتهم» معهم إلى أن تقضوا الصلاة وقد فعل النبي صلى الله عليه وسلم كذلك ببطن نخل رواه الشيخان «ودَّ الذين كفروا لو تغفلون» إذا قمتم إلى الصلاة «عن أسلحتكم وأمتعتكم فيميلون عليكم ميلة واحدة» بأن يحملوا عليكم فيأخذوكم وهذا علة الأمر بأخذ السلاح «ولا جُناح عليكم إن كان بكم أذى من مطر أو كنتم مرضى أن تضعوا أسلحتكم» فلا تحملوها وهذا يقيد إيجاب حملها عند عدم العذر وهو أحد قولين للشافعي والثاني أنه سنة ورجح «وخذوا حذركم» من العدو أي احترزوا منه ما استطعتم «إن الله أعد للكافرين عذابا مهينا» ذا إهانة.'),
(596, 4, 103, '«فإذا قضيتم الصلاة» فرغتم منها «فاذكروا الله» بالتهليل والتسبيح «قياما وقعودا وعلى جنوبكم» مضطجعين أي في كل حال «فإذا اطمأننتم» أمنتم «فأقيموا الصلاة» أدُّوها بحقوقها «إن الصلاة كانت على المؤمنين كتابا» مكتوبا مفروضا «موْقوتا» أي مقدرا وقتها فلا تؤخر عنه، ونزل لما بعث اصلى الله عليه وسلم طائفة في طلب أبي سفيان وأصحابه لما رجعوا من أحد فشكوا الجراحات.'),
(597, 4, 104, '«ولا تهنوا» تضعفوا «في ابتغاء» طلب «القوم» الكفار لتقاتلوهم «إن تكونوا تألمون» تجدون ألم الجراح «فإنهم يألمون كما تألمون» أي مثلكم ولا يجبنون على قتالكم «وترجون» أنتم «من الله» من النصر والثواب عليه «مالا يرجون» هم فأنتم تزيدون عليهم بذلك فينبغي أن تكونوا أرغب منهم فيه «وكان الله عليما» بكل شيء «حكيما» في صنعه.'),
(598, 4, 105, 'وسرق طعمة بن ابيرق درعا وخبأها عند يهودي فوجدت عنده فرماه طعمة بها وحلف أنه ما سرقها فسأل قومه النبي صلى الله عليه وسلم أن يجادل عنه ويبرئه فنزل «إنا أنزلنا إليك الكتاب» القرآن «بالحق» متعلق بأنزل «لتحكم بين الناس بما أراك» أعلمك «الله» فيه «ولا تكن للخائنين» كطعمة «خصيما» مخاصما عنهم.'),
(599, 4, 106, '«واستغفر الله» مما هممت به «إن الله كان غفورا رحيما».'),
(600, 4, 107, '«ولا تجادل عن الذين يختانون أنفسهم» يخونونها بالمعاصي لأن وبال خيانتهم عليهم «إن الله لا يحب من كان خوانا» كثير الخيانة «أثيما» أي يعاقبه.'),
(601, 4, 108, '«يستخفون» أي طعمة وقومه حياءً «من الناس ولا يستخفون من الله وهو معهم» بعلمه «إذ يبيِّتون» يضمرون «ما لا يرضى من القول» من عزمهم على الحلف على نفي السرقة ورمي اليهودي بها «وكان الله بما يعملون محيطا» علما.'),
(602, 4, 109, '«ها أنتم» يا «هؤلاء» خطاب لقوم طعمة «جادلتم» خاصمتم «عنهم» أي عن طعمة وذويه وقرئ عنه «في الحياة الدنيا فمن يجادل الله عنهم يوم القيامة» إذ عذبهم «أم من يكون عليهم وكيلا» يتولى أمرهم ويذبُّ عنهم أي لا أحد يفعل ذلك.'),
(603, 4, 110, '«ومن يعمل سوءا» ذنبا يسوء به غيره كرمي طعمة اليهودي «أو يظلم نفسه» يعمل ذنبا قاصرا عليه «ثم يستغفر الله» منه أي يتب «يجد الله غفورا» له «رحيما» به.'),
(604, 4, 111, '«ومن يكسب إثما» ذنبا «فإنما يكسبه على نفسه» لأن وباله عليها ولا يضر غيره «وكان الله عليما حكيما» في صنعه.'),
(605, 4, 112, '«ومن يكسب خطيئة» ذنبا صغيرا «أو إثما» ذنبا كبيرا «ثم يرم به بريئا» منه «فقد احتمل» تحمل «بهتانا» برميه «وإثما مبينا» بينا بكسبه.'),
(606, 4, 113, '«ولولا فضل الله عليك» يا محمد «ورحمته» بالعصمة «لهمَّت» أضمرت «طائفة منهم» من قوم طعمة «أن يضلوك» عن القضاء بالحق بتلبيسهم عليك «وما يضلون إلا أنفسهم وما يضرونك من» زائدة «شيء» لأن وبال إضلالهم عليهم «وأنزل الله عليك الكتاب» القرآن «والحكمة» ما فيه من الأحكام «وعلَّمك ما لم تكن تعلم» من الأحكام والغيب «وكان فضل الله عليك» بذلك وغيره «عظيما».'),
(607, 4, 114, '«لا خير في كثير من نجواهم» أي الناس أي يتناجون فيه ويتحدثون «إلا» نجوى «من أمر بصدقة أو معروف» عمل بر «أو إصلاح بين الناس ومن يفعل ذلك» المذكور «ابتغاء» طلب «مرضات الله» لا غيره من أمور الدنيا «فسوف نؤتيه» بالنون والياء أي الله «أجرا عظيما».'),
(608, 4, 115, '«ومن يُشاقق» يخالف «الرسول» فيما جاء به من الحق «من بعد ما تبين له الهدى» ظهر له الحق بالمعجزات «ويتَّبع» طريقا «غير سبيل المؤمنين» أي طريقهم الذي هم عليه من الدين بأن يكفر «نولِّه ما تولَّى» نجعله واليا لما تولاه من الضلال بأن نخلي بينه وبينه في الدنيا «ونصله» ندخله في الآخرة «جهنم» فيحترق فيها «وساءت مصيرا» مرجعا هي.'),
(609, 4, 116, '«إن الله لا يغفر أن يُشرك به ويغفر مادون ذلك لمن يشاء ومن يشرك بالله فقد ضل ضلالا بعيدا» عن الحق.'),
(610, 4, 117, '«إن» ما «يدعون» يعبد المشركون «من دونه» أي الله، أي غيره «إلا إناثا» أصناما مؤنثة كاللات والعزى ومناة «وإن» ما «يدعون» يعبدون بعبادتها «إلا شيطانا مريدا» خارجا عن الطاعة لطاعتهم له فيها وهو إبليس.'),
(611, 4, 118, '«لعنه الله» أبعده عن رحمته «وقال» أي الشيطان «لأتخذنَّ» لأجعلن لي «من عبادك نصيبا» حظّا «مفروضا» مقطوعا أدعوهم إلى طاعتي.'),
(612, 4, 119, '«ولأضلنهم» عن الحق بالوسوسة «ولأمنينهم» ألقي في قلوبهم طول الحياة وأن لا بعث ولا حساب «ولآمرنهم فليبتِّكُنَّ» يقطعن «آذان الأنعام» وقد فعل ذلك بالبحائر «ولآمرنهم فليغيرن خلق الله» دينه بالكفر وإحلال ما حرم الله وتحريم ما أحل «ومن يتخذ الشيطان وليا» يتولاه يطيعه «من دون الله» أي غيره «فقد خسر خسرانا مبينا» بينا لمصيره إلى النار المؤبدة عليه.'),
(613, 4, 120, '«يعدهم» طول العمر «ويمنيهم» نيل الآمال في الدنيا وألا بعث ولا جزاء «وما يعدهم الشيطان» بذلك «إلا غرورا» باطلا.'),
(614, 4, 121, '«أولئك مأواهم جهنم ولا يجدون عنها محيصا» معدلا.'),
(615, 4, 122, '«والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا وعد الله حقا» أي وعدهم الله ذلك وحقه حقا «ومن» أي لا أحد «أصدق من الله قيلا» أي قولا.'),
(616, 4, 123, 'ونزل لما افتخر المسلمون وأهل الكتاب «ليس» الأمر منوطا «بأمانيكم ولا أماني أهل الكتاب» بل بالعمل الصالح «من يعمل سوءًا يُجز به» إما في الآخرة أو في الدنيا بالبلاء والمحن كما ورد في الحديث «ولا يجد له من دون الله» أي غيره «وليا» يحفظه «ولا نصيرا» يمنعه منه.'),
(617, 4, 124, '«ومن يعمل» شيئا «من الصالحات من ذكر أو أنثى وهو مؤمن فأولئك يدخلون» بالبناء للمفعول والفاعل «الجنة ولا يظلمون نقيرا» قدر نقرة النواة.'),
(618, 4, 125, '«ومن» أي لا أحد «أحسن دينا ممن أسلم وجهه» أي انقاد وأخلص عمله «لله وهو محسن» موحد «واتبع ملة إبراهيم» الموافقة لملة الإسلام «حنيفا» حال أي مائلا عن الأديان كلها إلى الدين القيم «واتخذ الله إبراهيم خليلا» صفيا خالص المحبة له.'),
(619, 4, 126, '«ولله ما في السماوات وما في الأرض» ملكا وخلقا وعبيدا «وكان الله بكل شيء محيطا» علما وقدرة أي لم يزل متصفا بذلك.'),
(620, 4, 127, '«ويستفتونك» يطلبون منك الفتوي «في» شأن «النساء» وميراثهن «قل» لهم «الله يفتيكم فيهن وما يتلى عليكم في الكتاب» القرآن من آية الميراث ويفتيكم أيضا «في يتامى النساء اللاتي لا تؤتونهن ما كتب» فرض «لهن» من الميراث «وترغبون» أيها الأولياء عن «أن تنكحوهن» لدمامتهن وتعضلوهن أن يتزوجن طمعا في ميراثهن أي يفتيكم أن لا تفعلوا ذلك «و» في «المستضعفين» الصغار «ومن الولدان» أن تعطوهم حقوقهم «و» يأمركم «أن تقوموا لليتامى بالقسط» بالعدل في الميراث والمهر «وما تفعلوا من خير فإن الله كان به عليما» فيجازيكم به.'),
(621, 4, 128, '«وإن امرأة» مرفوع بفعل يفسره «خافت» توقعت «من بعلها» زوجها «نشوزا» ترفعا عليها بترك مضاجتعها والتقصير في نفقتها لبغضها وطموح عينه إلى أجمل منها «أو إعراضا» عنها بوجهه «فلا جُناح عليهما أن يَصَّالَحَا» فيه إدغام التاء في الأصل في الصاد وفي قراءة يصلحا من أصلح «بينهما صلحا» في القسم والنفقة بأن تترك له شيئا طلبا لبقاء الصحبة فإن رضيت بذلك والإ فعلى الزوج أن يوفيها حقها أو يفارقها «والصلح خير» من الفرقة والنشوز والإعراض قال تعالى في بيان ما جبل عليه الإنسان «وأحضرت الأنفس الشح» شدة البخل أي جبلت عليه فكأنها حاضرته لا تغيب عنه، المعنى أن المرأة لا تكاد تسمح بنصيبها من زوجها والرجل لا يكاد يسمح عليها بنفسه إذا أحب غيرها «وإن تحسنوا» عشرة النساء «وتتقوا» الجور عليهن «فإن الله كان بما تعملون خبيرا» فيجازيكم به.'),
(622, 4, 129, '«ولن تستطيعوا أن تعدلوا» تسووا «بين النساء» في المحبة «ولو حرصتم» على ذلك «فلا تميلوا كل الميل» إلى التي تحبونها في القسم والنفقة «فتذروها» أي تتركوا الممال عنها «كالمعلَّقة» التي لا هي أيم ولا هي ذات بعل «وإن تصلحوا» بالعدل بالقسم «وتتقوا» الجور «فإن الله كان غفورا» لما في قلبكم من الميل «رحيما» بكم في ذلك.'),
(623, 4, 130, '«وإن يتفرقا» أي الزوجان بالطلاق «يُغن الله كلا» عن صاحبه «من سعته» أي فضله بأن يرزقها زوجا غيره ويرزقه غيرها «وكان الله واسعا» لخلقه في الفضل «حكيما» فيما دبره لهم.'),
(624, 4, 131, '«ولله ما في السماوات وما في الأرض ولقد وصينا الذين أوتوا الكتاب» بمعني الكتب «من قبلكم» أي اليهود والنصارى «وإياكم» يا أهل القرآن «أن» أي بأن «اتقوا الله» خافوا عقابه بأن تطيعوه «و» قلنا لهم ولكم «إن تكفروا» بما وُصيتم به «فإن لله ما في السماوات وما في الأرض» خلقا وملكا وعبيدا فلا يضره كفركم «وكان الله غنيا» عن خلقه وعبادتهم «حميدا» محمودا في صنعه بهم.'),
(625, 4, 132, '«ولله ما في السماوات وما في الأرض» كرره تأكيدا لتقرير موجب التقوى «وكفى بالله وكيلا» شهيدا بأن ما فيهما له.'),
(626, 4, 133, '«إن يشأ يذهبكم» يا «أيها الناس ويأت بآخرين» بدلكم «وكان الله على ذلك قديرا».'),
(627, 4, 134, '«من كان يريد» بعمله «ثواب الدنيا» «فعند الله ثواب الدنيا والاخرة» لمن أراده لا عند غيره فلم يطلب أحدكم الأخس وهلا طلب الأعلى بإخلاصه له حيث كان مطلبه لا يوجد إلا عنده «وكان الله سميعا بصيرا».'),
(628, 4, 135, '«يا أيها الذين آمنوا كونوا قوامين» قائمين «بالقسط» بالعدل «شهداء» بالحق «لله ولو» كانت الشهادة «على أنفسكم» فاشهدوا عليها بأن تقروا بالحق ولا تكتموه «أو» على «الوالدين والأقربين إن يكن» المشهود عليه «غنيّا أو فقيرا فالله أولى بهما» منكم وأعلم بمصالحهما «فلا تتَّبعوا الهوى» في شهادتكم بأن تحابوا الغني لرضاه أو الفقير رحمةّ له لـ «أن» لا «تعدلوا» تميلوا عن الحق «وإن تلووا» تحرفوا الشهادة وفي قراءة بحذف الواو الأولى تخفيفا «أو تعرضوا» عن أدائها «فإن الله كان بما تعملون خبيرا» فيجازيكم به.'),
(629, 4, 136, '«يا أيها الذين آمَنوا آمِنوا» داوموا على الإيمان «بالله ورسوله والكتاب الذي نزَّل على رسوله» محمد صلى الله عليه وسلم وهو القرآن «والكتاب الذي أنزل من قبل» على الرسل بمعنى الكتب، وفي قراءة بالبناء للفاعل في الفعلين «ومن يكفر بالله وملائكته وكتبه ورسله واليوم الآخر فقد ضل ضلالا بعيدا» عن الحق.'),
(630, 4, 137, '«إن الذين آمنوا» بموسى وهم اليهود «ثم كفروا» بعبادتهم العجل «ثم آمنوا» بعده «ثم كفروا» بعيسي «ثم ازدادوا كفرا» بمحمد «لم يكن الله ليغفر لهم» ما أقاموا عليه «ولا ليهديهم سبيلا» طريقا إلى الحق.'),
(631, 4, 138, '«بشِّر» أخبر يا محمد «المنافقين بأن لهم عذابا أليماً» مؤلما هو عذاب النار.'),
(632, 4, 139, '«الذين» بدل أو نعت للمنافقين «يتخذون الكافرين أولياء من دون المؤمنين» لما يتوهمون فيهم من القوة «أيبتغون» يطلبون «عندهم العزة» استفهام إنكار أي لا يجدونها عندهم «فإن العزة لله جميعا» في الدنيا والآخرة ولا ينالها إلا أولياؤه.'),
(633, 4, 140, '«وقد نزَّل» بالبناء للفاعل والمفعول «عليكم في الكتاب» القرآن في سورة الأنعام «أن» مخففة واسمها محذوف، أي أنه «إذا سمعتم آيات الله» القرآن «يُكفر بها ويستهزأ بها فلا تقعدوا معهم» أي الكافرين والمستهزئين «حتى يخوضوا في حديث غيره إنكم إذا» إن قعدتم معهم «مثلهم» في الإثم «إن الله جامع المنافقين والكافرين في جهنم جميعا» كما اجتمعوا في الدنيا على الكفر والاستهزاء.'),
(634, 4, 141, '«الذين» بدل من الذين قبله «يتربصون» ينتظرون «بكم» الدوائر «فإن كان لكم فتح» ظفر وغنيمة «من الله قالوا» لكم «ألم نكن معكم» في الدين والجهاد فأعطونا من الغنيمة «وإن كان للكافرين نصيب» من الظفر عليكم «قالوا» لهم «ألم نستحوذ» نستول «عليكم» ونقدر على أخذكم وقتلكم فأبقينا عليكم «و» ألم «نمنعكم من المؤمنين» أن يظفروا بكم بتخذيلهم ومراسلتكم بأخبارهم فلنا عليكم المنة قال تعالى: «فالله يحكم بينكم» وبينهم «يوم القيامة» بأن يدخلكم الجنة ويدخلهم النار «ولن يجعل الله للكافرين على المؤمنين سبيلا» طريقا بالإستئصال.'),
(635, 4, 142, '«إن المنافقين يخادعون الله» بإظهارهم خلاف ما أبطنوا من الكفر ليدفعوا عنهم أحكامه الدنيوية «وهو خادعهم» مجازيهم على خداعهم فيفتضحون في الدنيا بإطلاع الله نبيه على ما أبطنوه ويعاقبون في الآخرة «وإذا قاموا إلى الصلاة» مع المؤمنين «قاموا كسالى» متثاقلين «يراءون الناس» بصلاتهم «ولا يذكرون الله» يصلون «إلا قليلا» رياء.'),
(636, 4, 143, '«مذبذبين» مترددين «بين ذلك» الكفر والإيمان «لا» منسوبين «إلى هؤلاء» أي الكفار «ولا إلى هؤلاء» أي المؤمنين «ومن يضللـ» ـه «الله فلن تجد له سبيلا» طريقا إلى الهدى.'),
(637, 4, 144, '«يا أيها الذين آمنوا لا تتخذوا الكافرين أولياء من دون المؤمنين أتريدون أن تجعلوا لله عليكم» بموالاتهم «سلطانا مبينا» برهانا بينا على نفاقكم.'),
(638, 4, 145, '«إن المنافقين في الدرك» المكان «الأسفل من النار» وهو قعرها «ولن تجد لهم نصيرا» مانعا من العذاب.'),
(639, 4, 146, '«إلا الذين تابوا» من النفاق «وأصلحوا» عملهم «واعتصموا» وثقوا «بالله وأخلصوا دينهم لله» من الرياء «فأولئك مع المؤمنين» فيما يؤتونه «وسوف يؤت الله المؤمنين أجرا عظيما» في الآخرة وهو الجنة.'),
(640, 4, 147, '«ما يفعل الله بعذابكم إن شكرتم» نعمه «وآمنتم» به والاستفهام بمعنى النفي أي لا يعذبكم «وكان الله شاكرا» لأعمال المؤمنين بالإثابة «عليما» بخلقه.'),
(641, 4, 148, '«لا يحب الله الجهر بالسوء من القول» من أحد أي يعاقبه عليه «إلا من ظُلم» فلا يؤاخذه بالجهر به بأن يخبر عن ظلم ظالمه ويدعو عليه «وكان الله سميعا» لما يقال «عليما» بما يفعل.'),
(642, 4, 149, '«إن تبدوا» تظهروا «خيرا» من أعمال البر «أو تخفوه» تعملوه سرا «أو تعفوا عن سوء» ظلم «فإن الله كان عفّوا قديرا».'),
(643, 4, 150, '«إن الذين يكفرون بالله ورسله ويريدون أن يفرقوا بين الله ورسله» بأن يؤمنوا به دونهم «ويقولن نؤمن ببعض» من الرسل «ونكفر ببعض» منهم «ويريدون أن يتخذوا بين ذلك» الكفر والإيمان «سبيلا» طريقا يذهبون إليه.'),
(644, 4, 151, '«أولئك هم الكافرون حقا» مصدر مؤكد لمضمون الجملة قبله «وأعتدنا للكافرين عذابا مهينا» ذا إهانة وهو عذاب النار.'),
(645, 4, 152, '«والذين آمنوا بالله ورسله» كلهم «ولم يفرقوا بين أحد منهم أولئك سوف يؤتيهم» بالياء والنون «أجورهم» ثواب أعمالهم «وكان الله غفورا» لأوليائه «رحيما» بأهل طاعته.'),
(646, 4, 153, '«يسألك» يا محمد «أهل الكتاب» اليهود «أن تنزِّل عليهم كتابا من السماء» جملة كما أنزل على موسى تعنتا فإن استكبرت ذلك «فقد سألوا» أي آباؤهم «موسى أكبر» أعظم «من ذلك فقالوا أرنا الله جهرة» عيانا «فأخذتهم الصاعقة» الموت عقابا لهم «بظلمهم» حيث تعتنوا في السؤال «ثم اتخذوا العجل» إلها «من بعد ما جاءتهم البينات» المعجزات على وحدانية الله «فعفونا عن ذلك» ولم نستأصلهم «وآتينا موسى سلطانا مبينا» تسلطا بينا ظاهرا عليهم حيث أمرهم بقتل أنفسهم توبة فأطاعوه.'),
(647, 4, 154, '«ورفعنا فوقهم الطور» الجبل «بميثاقهم» بسبب أخذ الميثاق عليهم ليخافوا فقبلوه «وقلنا لهم» وهو مُظلِّ عليهم «ادخلوا الباب» باب القرية «سجدا» سجود انحناء «وقلنا لهم لا تعدوا» وفي قراءة بفتح العين وتشديد الدال وفيه إدغام التاء في الأصل في الدال أي لا تعدوا «في السبت» باصطياد الحيتان فيه «وأخذنا منهم ميثاقا غليظا» على ذلك فنقضوه.'),
(648, 4, 155, '«فبما نقضهم» ما زائدة والباء للسببية متعلقة بمحذوف، أي لعناهم بسبب نقضهم «ميثاقهم وكفرهم بآيات الله وقتلهم الأنبياء بغير حق وقولهم» للنبي صلى الله عليه وسلم «قلوبنا غلف» لا تعي كلامك «بل طبع» ختم «الله عليها بكفرهم» فلا تعي وعظا «فلا يؤمنون إلا قليلا» منهم كعبد الله بن سلام وأصحابه.'),
(649, 4, 156, '«وبكفرهم» ثانيا بعيسى وكرر الباء للفصل بينه وبين ما عطف عليه «وقولهم على مريم بهتانا عظيما» حيث رموها بالزنا.'),
(650, 4, 157, '«وقولهم» مفتخرين «إنا قتلنا المسيح عيسى بن مريم رسول الله» في زعمهم، أي بمجموع ذلك عذبناهم قال تعالى تكذيبا لهم في قتله «وما قتلوه وما صلبوه ولكن شبه لهم» المقتول والمصلوب وهو صاحبهم بعيسى، أي ألقى الله عليه شبهه فظنوه إياه «وإن الذين اختلفوا فيه» أي في عيسى «لفي شك منه» من قتله حيث قال بعضهم لما رأوا المقتول الوجه وجه عيسى والجسد ليس بجسده فليس به، وقال آخرون: بل هو هو «ما لهم به» بقتله «من علم إلا اتباع الظن» استثناء منقطع، أي لكن يتبعون فيه الظن الذي تخيلوه «وما قتلوه يقينا» حال مؤكدة تنفي القتل.'),
(651, 4, 158, '«بل رفعه الله إليه وكان الله عزيزا» في ملكه «حكيما» في صنعه.'),
(652, 4, 159, '«وإنْ» ما «من أهل الكتاب» أحَد «إلا ليؤمنن به» بعيسى «قبل موته» أي الكتابي حين يعاين ملائكة الموت فلا ينفعه إيمانه أو قبل موت عيسى لما ينزل قرب الساعة كما ورد في حديث «ويوم القيامة يكون» عيسى «شهيدا» بما فعلوه لما بعث إليهم.'),
(653, 4, 160, '«فبظلم» أي فبسبب ظلم «من الذين هادوا» هم اليهود «حرمنا عليهم طيبات أحلت لهم» من التي في قوله تعالى: (حرمنا كل ذي ظفر) الآية «وبصدهم» الناس «عن سبيل الله» دينه صدا «كثيرا».'),
(654, 4, 161, '«وأخذهم الربا وقد نُهوا عنه» في التوراة «وأكلهم أموال الناس بالباطل» بالرشا في الحكم «وأعتدنا للكافرين منهم عذابا أليما» مؤلما.'),
(655, 4, 162, '«لكن الراسخون» الثابتون «في العلم منهم» كعبد الله بن سلام «والمؤمنون» المهاجرون والأنصار «يؤمنون بما أنزل إليك وما انزل من قبلك» من الكتب «والمقيمين الصلاة» نصب على المدح وقرئ بالرفع «والمؤتون الزكاة والمؤمنون بالله واليوم الآخر أولئك سنؤتيهم» بالنون والياء «أجرا عظيما» هو الجنة.'),
(656, 4, 163, '«إنا أوحينا إليك كما أوحينا إلى نوح والنبيين من بعده و» كما «أوحينا إلى إبراهيم وإسماعيل وإسحاق» ابنيه «ويعقوب» ابن إسحاق «والأسباط» أولاده «وعيسى وأيوب ويونس وهارون وسليمان وآتينا» أباه «داود زَبورا» بالفتح اسم للكتاب المؤتى والضم مصدر بمعنى مزبورا أي مكتوبا.'),
(657, 4, 164, '«و» أرسلنا «رسلا قد قصصناهم عليك من قبل ورسلا لم نقصصهم عليك» روي أنه تعالى بعث ثمانية آلاف نبي أربعة آلاف من إسرائيل وأربعة آلاف من سائر الناس قاله الشيخ في سورة غافر «وكلَّم اللهُ موسى» بلا واسطة «تكليما».'),
(658, 4, 165, '«رسلا» بدل من رسلا قبله «مبشرين» بالثواب من آمن «ومنذرين» بالعقاب من كفر أرسلناهم «لئلا يكون للناس على الله حجة» تقال «بعد» إرسال «الرسل» إليهم فيقولوا: ربنا لولا أرسلت إلينا رسولا فنتبع آياتك ونكون من المؤمنين فبعثناهم لقطع عذرهم «وكان الله عزيزا» في ملكْه «حكيما» في صنعه.'),
(659, 4, 166, 'ونزل لما سئل اليهود عن نبوته صلى الله عليه وسلم فأنكروه «لكن الله يشهد» يبين نبوتك «بما أنزل إليك» من القرآن المعجز «أنزله» ملتبسا «بعلمه» أي عالما به أو فيه علمه «والملائكة يشهدون» لك أيضا «وكفى بالله شهيدا» على ذلك.'),
(660, 4, 167, '«إن الذين كفروا» بالله «وصدوا» الناس «عن سبيل الله» دين الإسلام بكتمهم نعت محمد صلى الله عليه وسلم وهم اليهود «قد ضلوا ضلالا بعيدا» عن الحق.'),
(661, 4, 168, '«إن الذين كفروا» بالله «وظلموا» نبيه بكتمان نعته «لم يكن الله ليغفر لهم ولا ليهديهم طريقا» من الطرق.'),
(662, 4, 169, '«إلا طريق جهنم» أي الطريق المؤدي إليها «خالدين» مقدرين الخلود «فيها» إذا دخلوها «أبدا وكان ذلك على الله يسيرا» هينا.'),
(663, 4, 170, '«يا أيها الناس» أي أهل مكة «قد جاءكم الرسول» محمد صلى الله عليه وسلم «بالحق من ربكم فآمنوا» به واقصدوا «خيرا لكم» مما أنتم فيه «وإن تكفروا» به «فإن لله ما في السماوات والأرض» ملكا وخلقا وعبيدا فلا يضره كفركم «وكان الله عليما» بخلقه «حكيما» في صنعه بهم.'),
(664, 4, 171, '«يا أهل الكتاب» الإنجيل «لا تغلوا» تتجاوزوا الحد «في دينكم ولا تقولوا على الله إلا» القول «الحق» من تنزيهه عن الشريك والولد «إنما المسيح عيسى بن مريم رسول الله وكلمته ألقاها» أرسلها الله «إلى مريم وروح» أي ذو روح «منه» أضيف إليه تعالى تشريفا له وليس كما زعمتم ابن الله أو إلها معه أو ثالث ثلاثة لأن ذا الروح مركب والإله منزه عن التركيب وعن نسبة المركب إليه «فآمنوا بالله ورسله ولا تقولوا» الآلهة «ثلاثة» الله وعيسى وأمه «انتهوا» عن ذلك وأتوا «خيرا لكم» منه وهو التوحيد «إنما الله إله واحد سبحانه» تنزيها له عن «أن يكون له ولد له ما في السماوات وما في الأرض» خلقا وملكا وعبيدا، والملكية تنافي النبوة «وكفى بالله وكيلا» شهيدا على ذلك.'),
(665, 4, 172, '«لن يستنكف» يتكبر ويأنف «المسيح» الذي زعمتم أنه إله من «أن يكون عبدا لله ولا الملائكة المقربون» عند الله لا يستنكفون أن يكونوا عبيدا، وهذا من أحسن الاستطراد ذكر للرد على من زعم أنها آلهة أو بنات الله كما رد قبله على النصارى الزاعمين ذلك المقصود خطابهم «ومن يستنكف عن عبادته ويستكبر فسيحشرهم إليه جميعا» في الآخرة.'),
(666, 4, 173, '«فأما الذين آمنوا وعملوا الصالحات فيوفيهم أجورهم» ثواب أعمالهم «ويزيدهم من فضله» ما لا عين رأت ولا أذن سمعت ولا خطر على قلب بشر «وأما الذين استنكفوا واستكبروا» عن عبادته «فيعذبهم عذابا أليما» مؤلما هو عذاب النار «ولا يجدون لهم من دون الله» أي غيره «وليا» يدفعه عنهم «ولا نصيرا» يمنعهم منه.'),
(667, 4, 174, '«يا أيها الناس قد جاءكم برهان» حجة «من ربكم» عليكم وهو النبي صلى الله عليه وسلم «وأنزلنا إليكم نورت مبينا» بينا وهو القرآن.'),
(668, 4, 175, '«فأما الذين آمنوا بالله واعتصموا به فسيدخلهم في رحمة منه وفضل ويهديهم إليه صراطا» طريقا «مستقيما» هو دين الإسلام.'),
(669, 4, 176, '«يستفتونك» في الكلالة «قل الله يفتيكم في الكلالة إن امرؤ» مرفوع بفعل يفسره «هلك» مات «ليس له ولد» أي ولا والد وهو الكلالة «وله أختٌ» من أبوين أو أب «فلها نصف ما ترك وهو» أي الأخ كذلك «يرثها» جميع ما تركت «إن لم يكن لها ولد» فإن كان لها ولد ذكر فلا شيء له أو أنثى فله ما فضل من نصيبها ولو كانت الأخت أو الأخ من أم ففرضه السدس كما تقدم أول السورة «فإن كانتا» أي الأختان «اثنتين» أي فصاعدا لأنها نزلت في جابر وقد مات عن أخوات «فلهما الثلثان مما ترك» الأخ «وإن كانوا» أي الورثة «إخوة رجالا ونساء فللذكر» منهم «مثل حظ الأنثيين يبين الله لكم» شرائع دينكم لـ «أن» لا «تضلوا والله بكل شيء عليم» ومنه الميراث روى الشيخان عن البراء أنها آخر آيه نزلت أي من الفرائض.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, '«يا أيها الذين آمنوا أوفوا بالعقود» العهود المؤكدة التي بينكم وبين الله والناس «أحلت لكم بهيمة الأنعام» الإبل والبقر والغنم أكلاً بعد الذبح «إلا ما يتلى عليكم» تحريمه في (حُرمت عليكم الميتة) الآية فالاستثناء منقطع ويجوز أن يكون متصلا والتحريم لما عرض من الموت ونحوه «غيرَ محلي الصيد وأنتم حرم» أي محرمون ونصب غير على الحال من ضمير لكم «إن الله يحكم ما يريد» من التحليل وغيره لا اعترض عليه.'),
(671, 5, 2, '«يا أيها الذين آمنوا لا تحلوا شعائر الله» جمع شعيرة أي معالم دينه بالصيد في الإحرام «ولا الشهر الحرام» بالقتال فيه «ولا الهدْي» ما أهدى إلى الحرم من النِّعم بالتعرض له «ولا القلائد» جمع قلادة وهي ما كان يقلد به من شجر الحرم ليأمن أي فلا تتعرضوا لها ولا لأصحابها «ولا» تحلوا «آمِّين» قاصدين «البيت الحرام» بأن تقاتلوهم «يبتغون فضلا» رزقا «من ربهم» بالتجارة «ورضوانا» منه بقصده بزعمهم الفاسد وهذا منسوخ بآية براءة «وإذا حللتم» من الإحرام «فاصطادوا» أمر إباحة «ولا يجرمنَّكم» يكسبنكم «شنَآن» بفتح النون وسكونها بغض (قوم) لأجل «أن صدوكم عن المسجد الحرام أن تعتدوا» عليهم بالقتل وغيره «وتعاونوا على البرِّ» بفعل ما أمرتم به «والتقوى» بترك ما نهيتم عنه «ولا تعاونوا» فيه حذف إحدى التاءين في الأصل «على الإثم» المعاصي «والعدوان» التعدي في حدود الله «واتقوا الله» خافوا عقابه بأن تطيعوه «إن الله شديد العقاب» لمن خالفه.'),
(672, 5, 3, '«حرِّمت عليكم الميتة» أي أكلها «والدم» أي المسفوح كما في الأنعام «ولحم الخنزير وما أهل لغير الله به» بأن ذُبح على اسم غيره «والمنخنقة» الميتة خنقا «والموقوذة» المقتولة ضربا «والمتردية» الساقطة من علو إلى أسفل فماتت «والنطيحة» المقتولة بنطح أخرى لها «وما أكل السبع» منه «إلا ما ذكيتم» أي أدركتم فيه الروح من هذه الأشياء فذبحتموه «وما ذُبح على» اسم «النصب» جمع نصاب وهي الأصنام «وأن تستقسموا» تطلبوا القسم والحكم «بالأزلام» جمع زلم بفتح الزاي وضمها مع فتح اللام قدح _بكسر القاف_ صغير لا ريش له ولا نصل وكانت سبعة عند سادن الكعبة عليها أعلام وكانوا يحكمونها فإن أمرتهم ائتمروا وإن نهتهم انتهوا «ذلكم فسق» خروج عن الطاعة. ونزل يوم عرفة عام حجة الوداع «اليوم يئس الذين كفروا من دينكم» أن ترتدوا عنه بعد طمعهم في ذلك لما رأوا من قوته «فلا تخشوهم واخشون اليوم أكملتُ لكم دينكم» أحكامه وفرائضه فلم ينزل بعدها حلال ولا حرام «وأتممت عليكم نعمتي» بإكماله وقيل بدخول مكة آمنين «ورضيت» أي اخترت «لكم الإسلام دينا فمن اضطر في مخمصة» مجاعة إلى أكل شيء مما حرم عليه فأكله «غير متجانف» مائل «لإثم» معصية «فإن الله غفور» له ما أكل «رحيم» به في إباحته له بخلاف المائل لإثم أي المتلبس به كقاطع الطريق والباغي مثلا فلا يحل له الأكل.'),
(673, 5, 4, '«يسئلونك» يا محمد «ماذا أُحل لهم» من الطعام «قل أحل لكم الطيَّبات» المستلذات «و» صيد «ما علَّمتم من الجوارح» الكواسب من الكلاب والسباع والطير «مكلِّبين» حال من كلَّبت الكلب بالتشديد أي أرسلته على الصيد «تعلمونهن» حال من ضمير مكلبين أي تؤدبونهن «مما علَّمَكُمُ الله» من آداب الصيد «فكلوا مما أمسكن عليكم» وإن قتلته بأن لم يأكلن منه بخلاف غير المعلمة فلا يحل صيدها وعلامتها أن تترسل إذا أرسلت وتنزجر إذا زجرت وتمسك الصيد ولا تأكل منه وأقل ما يعرف به ثلاث مرات فإن أكلت منه فليس مما أمسكن على صاحبها فلا يحل أكله كما في حديث الصحيحين وفيه أن صيد السهم إذا أرسل وذكر اسم الله عليه كصيد المعلم من الجوارح «واذكروا اسم الله عليه» عند إرساله «واتقوا الله إن الله سريع الحساب».'),
(674, 5, 5, '«اليوم أحل لكم الطيبات» المستلذات «وطعام الذين أوتوا الكتاب» أي ذبائح اليهود والنصارى «حلٌ» حلال «لكم وطعامكم» إياهم «حل لهم والمحصنات من المؤمنات والمحصنات» الحرائر «من الذين أوتوا الكتاب من قبلكم» حل لكم أن تنكحوهن «إذا آتيتموهنَّ أجورهنَّ» مهورهنَّ «محصنين» متزوجين «غير مسافحين» معلنين بالزنا بهن «ولا متخذي أخدان» منهن تسرون بالزنا بهن «ومن يكفر بالإيمان» أي يرتد «فقد حبط عمله» الصالح قبل ذلك فلا يعتد به ولا يثاب عليه «وهو في الآخرة من الخاسرين» إذا مات عليه.'),
(675, 5, 6, '«يا أيها الذين آمنوا إذا قمتم» أي أردتم القيام «إلى الصلاة» وأنتم محدثون «فاغسلوا وجوهكم وأيديكم إلى المرافق» أي معها كما بينته السنة «وامسحوا برؤوسكم» الباء للإلصاق أي ألصقوا المسح بها من غير إسالة ماء وهو اسم جنس فيكفي أقل ما يصدق عليه وهو مسح بعض شعرة وعليه الشافعي «وأرجلكم» بالنصب عطفا على أيديكم وبالجر على الجوار «إلى الكعبين» أي معهما كما بينته السنة وهما العظمان الناتئان في كل رجل عند مفصل الساق والقدم والفصل بين الأيدي والأرجل المغسولة بالرأس المسموح يفيد وجوب الترتيب في طهارة هذه الأعضاء وعليه الشافعي ويؤخذ من السنة وجوب النية فيه كغيره من العبادات «وإن كنتم جنبا فاطَّهروا» فاغتسلوا «وإن كنتم مرضى» مَرَضا يضره الماء «أو على سفر» أي مسافرين «أو جاء أحد منكم من الغائط» أي أحدث «أو لا مستم النساء» سبق مثله في آيه النساء «فلم تجدوا ماء» بعد طلبه «فتيمموا» اقصدوا «صعيدا طيَّبا» ترابا طاهرا «فامسحوا بوجوهكم وأيديكم» مع المرفقين «منه» بضربتين والباء للإلصاق وبينت السنة أن المراد استيعاب العضوين بالمسح «ما يريد الله ليجعل عليكم من حرج» ضيق بما فرض عليكم من الوضوء والغسل والتيمم «ولكن يريد ليطهركم» من الأحداث والذنوب «وليتم نعمته عليكم» بالإسلام ببيان شرائع الدين «لعلكم تشكرون» نعمه.'),
(676, 5, 7, '«واذكروا نعمة الله عليكم» بالإسلام «وميثاقه» عهده «الذي واثقكم به» عاهدكم عليه «إذ قلتم» للنبي صلي الله عليه وسلم حين بايعتموه «سمعنا وأطعنا» في كل ما تأمر به وتنهى مما نحب ونكره «واتقوا الله» في ميثاقه أن تنقضوه «إن الله عليم بذات الصدور» بما في القلوب فبغيره أولى.'),
(677, 5, 8, '«يا أيها الذين آمنوا كونوا قوامين» قائمين «لله» بحقوقه «شهداء بالقسط» بالعدل «ولا يجرمنكم» يحملنكم «شنَآن» بغض «قوم» أي الكفار «على ألاَّ تعدلوا» فتنالوا منهم لعدواتهم «إعدلوا» في العدو والولي «هو» أي العدل «أقرب للتقوى واتقوا الله إن الله خبير بما تعملون» فيجازيكم به.'),
(678, 5, 9, '«وعد الله الذين آمنوا وعملوا الصالحات» وعدا حسنا «لهم مغفرة وأجر عظيم» هو الجنة.'),
(679, 5, 10, '«والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم».'),
(680, 5, 11, '«يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ هَمَّ قوم» هم قريش «أن يبسطوا» يمدوا «إليكم أيديهم» ليفتكوا بكم «فكفَّ أيديهم عنكم» وعصمكم مما أرادوا بكم «واتقوا الله وعلى الله فليتوكل المؤمنون».'),
(681, 5, 12, '«ولقد أخذ الله ميثاق بني إسرائيل» بما يذكر بعد «وبعثنا» فيه التفات عن الغيبة أقمنا «منهم اثني عشر نقيبا» من كل سبط نقيب يكون كفيلا على قومه بالوفاء بالعهد توثقة عليهم «وقال» لهم «الله إنِّي معكم» بالعون والنصرة «لئن» لام قسم «أقمتم الصلاة وآتيتم الزكاة وآمنتم برسلي وعزَّرتموهم» نصرتموهم «وأقرضتم الله قرضا حسنا» بالإنفاق في سبيله «لأكفرنَّ عنكم سيئآتكم ولأدخلنكم جنات تجري من تحتها الأنهار فمن كفر بعد ذلك» الميثاق «منكم فقد ضل سواء السبيل» أخطأ طريق الحق. والسواء في الأصل الوسط فنقضوا الميثاق قال تعالى.'),
(682, 5, 13, '«فبما نقضهم» ما زائدة «ميثاقهم لعناهم» أبعدناهم عن رحمتنا «وجعلنا قلوبهم قاسية» لا تلين لقبول الإيمان «يحرِّفون الكلم» الذي في التوراة من نعت محمد صلى الله عليه وسلم وغيره «عن مواضعه» التي وضعه الله عليها أي يبدلونه «ونسوا» تركوا «حظّاً» نصيبا «مما ذكروا» أمروا «به» في التوراة من اتباع محمد «ولا تزال» خطاب للنبي صلى الله علي وسلم «تطَّلع» تظهر «على خائنة» أي خيانة «منهم» بنقض العهد وغيره «إلا قليلا منهم» ممن أسلم «فاعف عنهم واصفح إن الله يحب المحسنين» وهذا منسوخ بآيه السيف.'),
(683, 5, 14, '«ومن الذين قالوا إنا نصارى» متعلق بقوله «أخذنا ميثاقهم» كما أخذنا على بني إسرائيل اليهود «فنسوا حظاً مما ذكِّروا به» في الإنجيل من الإيمان وغيره ونقضوا الميثاق «فأغرينا» أوقعنا «بينهم العداوة والبغضاء إلى يوم القيامة» بتفرقهم واختلاف أهوائهم فكل فرقة تكفر الأخرى «وسوف ينبئهم الله» في الآخرة «بما كانوا يصنعون» فيجازيهم عليه.'),
(684, 5, 15, '«يا أهل الكتاب» اليهود والنصارى «قد جاءكم رسولنا» محمد «يبين لكم كثيرا مما كنتم تخفون» تكتمون «من الكتاب» التوراة والإنجيل كآية الرجم وصفته «ويعفو عن كثير» من ذلك فلا يبينه إذا لم يكن فيه مصلحة إلا افتضاحكم «قد جاءكم من الله نورٌ» هو النبي صلى الله عليه وسلم «وكتابٌ» قرآن «مبين» بين ظاهر.'),
(685, 5, 16, '«يهدي به» أي بالكتاب «الله من اتبع رضوانه» بأن آمن «سبل السلام» طرق السلامة «ويخرجهم من الظلمات» الكفر «إلى النور» الإيمان «بإذنه» بإرادته «ويهديهم إلى صراط مستقيم» دين الإسلام.'),
(686, 5, 17, '«لقد كفر الذين قالوا إن الله هو المسح ابن مريم» حيث جعلوه إلَها وهم اليعقوبية فرقة من النصارى «قل فمن يملك» أي يدفع «من» عذاب «الله شيئا إن أراد أن يُهلك المسيح ابن مريم وأمه ومن في الأرض جميعا» أي لا أحد يملك ذلك ولو كان المسيح إلَها لقدر عليه «ولله ملك السماوات والأرض وما بينهما يخلق ما يشاء والله على كل شيء» شاءه «قدير».'),
(687, 5, 18, '«وقالت اليهود والنصارى» أي كل منهما «نحن أبناء الله» أي كأبنائه في القرب والمنزلة وهو كأبينا في الرحمة والشفقة «وأحباؤه قل» لهم يا محمد «فلم يعذبكم بذنوبكم» إن صدقتم في ذلك ولا يعذب الأب ولده ولا الحبيب حبيبه وقد عذبكم فأنتم كاذبون «بل أنتم بشر ممن» من جملة من «خلق» من البشر لكم ما لهم وعليكم ما عليهم «يغفر لمن يشاء» المغفرة له «ويعذب من يشاء» تعذيبه لا اعتراض عليه «ولله ملك السماوات والأرض وما بينهما وإليه المصير» المرجع.'),
(688, 5, 19, '«يا أهل الكتاب قد جاءكم رسولنا» محمد «يبين لكم» شرائع الدين «على فترة» انقطاع «من الرسل» إذ لم يكن بينه وبين عيسى رسول ومدة ذلك خمسمائة وتسع وستون سنة لـ «أن» لا «تقولوا» إذا عذبتم «ما جاءنا من» زائدة «بشير ولا نذير فقد جاءكم بشير ونذير» فلا عذر لكم إذاً «والله على كل شيء قدير» ومنه تعذيبكم إن لم تتبعوه.'),
(689, 5, 20, '«و» اذكر «إذ قال موسى لقومه يا قوم اذكروا نعمة الله عليكم إذ جعل فيكم» أي منكم «أنبياء وجعلكم ملوكا» أصحاب خدم وحشم «وآتاكم ما لم يؤت أحدا من العالمين» من المن والسلوى وفلق البحر وغير ذلك.'),
(690, 5, 21, '«يا قوم ادخلوا الأرض المقدَّسة» المطهرة «التي كتب الله لكم» أمركم بدخولها وهي الشام «ولا ترتدُّوا على أدباركم» تنهزموا خوف العدو «فتنقلبوا خاسرين» في سعيكم.'),
(691, 5, 22, '«قالوا يا موسى إن فيها قوما جبارين» من بقايا عاد طوالا ذي قوة «وإنا لن ندخلها حتى يخرجوا منها فإن يخرجوا منها فإنا داخلون» لها.'),
(692, 5, 23, '«قال» لهم «رجلان من الذين يخافون» مخالفة أمر الله وهما يوشع وكالب من النقباء الذين بعثهم موسى في كشف أحوال الجبابرة «أنعم الله عليهما» بالعصمة فكتما ما اطَّلعا عليه من حالهم إلا عن موسى بخلاف بقية النقباء فأفشوه فجبنوا «ادخلوا عليهم الباب» باب القرية ولا تخشوهم فإنهم أجساد بلا قلوب «فإذا دخلتموه فإنكم غالبون» قالا ذلك تيقناً بنصر الله وإنجاز وعده «وعلى الله فتوكلوا إن كنتم مؤمنين».'),
(693, 5, 24, '«قالوا يا موسى إنا لن ندخلها أبدا ماداموا فيها فاذهب أنت وربُّك فقاتلا» هم «إنا ها هنا قاعدون» عن القتال.'),
(694, 5, 25, '«قال» موسى حينئذ «رب إني لا أملك إلا نفسي و» إلا «أخي» ولا أملك غيرهما فاجبرهم على الطاعة «فافرق» فافصل «بيننا وبين القوم الفاسقين».'),
(695, 5, 26, '(قال) تعالى له (فإنها) أي الأرض المقدسة (محرمة عليهم) أن يدخلوها (أربعين سنة يتيهون) يتحيرون (في الأرض) وهي تسعة فراسخ قاله ابن عباس (فلا تأس) تحزن (على القوم الفاسقين) روي أنهم كانوا يسيرون الليل جادين فإذا أصبحوا إذا هم في الموضع الذي ابتدأوا منه ويسيرون النهار كذلك حتى انقرضوا كلهم إلا من لم يبلغ العشرين، قيل: وكانوا ستمائة ألف ومات هارون وموسى في التيه وكان رحمة لهما وعذابا لأولئك وسأل موسى ربه عند موته أن يدنيه من الأرض المقدسة رمية بحجر فأدناه كما في الحديث، ونبئ يوشع بعد الأربعين وأمر بقتال الجبارين فسار بمن بقي معه وقاتلهم وكان يوم الجمعة ووقفت له الشمس ساعة حتى فرغ من قتالهم، وروى أحمد في مسنده حديث "" إن الشمس لم تحبس على بشر إلا ليوشع ليالي سار إلى بيت المقدس "".'),
(696, 5, 27, '«واتل» يا محمد «عليهم» على قومك «نبأ» خبر «ابنيْ آدم» هابيل وقابيل «بالحق» متعلق بأُتل «إذ قرَّبا قربانا» إلى الله وهو كبش لهابيل وزرع لقابيل «فتُقبل من أحدهما» وهو هابيل بأن نزلت نار من السماء فأكلت قربانه «ولم يُتقبل من الآخر» وهو قابيل فغضب وأضمر الحسد في نفسه إلى أن حج آدم «قال» له «لأقتلنك» قال: لَم قال لتقبل قربانك دوني «قال إنما يتقبل الله من المتقين».'),
(697, 5, 28, '«لئن» لام قسم «بسطت» مددت «إلى يدك لتقتلني ما أنا بباسط يدي إليك لأقتلك إنِّي أخاف الله ربَّ العالمين» في قتلك.'),
(698, 5, 29, '«إني أريد أن تبوء» ترجع «بإثمي» بإثم قتلي «وإثمك» الذي ارتكبته من قبل «فتكون من أصحاب النار» ولا أريد أن أبوء بإثمك إذا قتلتك فأكون منهم، قال تعالى: «وذلك جزاء الظالمين».'),
(699, 5, 30, '«فطوَّعت» زينت «له نفسه قتل أخيه فقتله فأصبح» فصار «من الخاسرين» بقتله ولم يدرِ ما يصنع به لأنه أول ميت على وجه الأرض من بني آدم فحمله على ظهره.'),
(700, 5, 31, '«فبعث الله غرابا يبحث في الأرض» ينبش التراب بمنقاره وبرجليه ويثيره على غراب ميت حتى واراه «ليريه كيف يواري» يستر «سوأة» جيفة «أخيه قال يا ويلتى أعجزت» عن «أن أكون مثل هذا الغراب فأواري سوأة أخي فأصبح من النادمين» على حمله وحفر له وواره.'),
(701, 5, 32, '«من أجل ذلك» الذي فعله قابيل «كتبنا على بني إسرائيل أنه» أي الشأن «من قتل نفسا بغير نفس» قتلها «أو» بغير «فساد» أتاه «في الأرض» من كفر أو زنا أو قطع طريق أو نحوه «فكأنما قتل الناس جميعا ومن أحياها» بأن امتنع عن قتلها «فكأنما أحيا الناس جميعا» قال ابن عباس: من حيث انتهاك حرمتها وصونها «ولقد جاءتهم» أي بني إسرائيل «رسلنا بالبينات» المعجزات «ثم إن كثيرا منهم بعد ذلك في الأرض لمسرفون» مجاوزون الحد بالكفر والقتل وغير ذلك.'),
(702, 5, 33, 'ونزل في العرنيين لما قدموا المدينة وهم مرضى فأذن لهم النبي صلى الله عليه وسلم أن يخرجوا إلى الإبل ويشربوا من أبوالها وألبانها فلما صحوا قتلوا راعي النبي صلى الله عليه وسلم واستاقوا الإبل «إنما جزاء الذين يحاربون الله ورسوله» بمحاربة المسلمين «ويسعون في الأرض فسادا» بقطع الطريق «أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف» أي أيديهم اليمنى واليسرى «أو يُنفوا من الأرض» أو لترتيب الأحوال فالقتل لمن قتل فقط والصلب لمن قتل وأخذ المال والقطع لمن أخذ المال ولم يقتل والنفي لمن أخاف فقط قاله ابن عباس وعليه الشافعي وأصح قوليه أن الصلب ثلاثا بعد القتل وقيل قبله قليلا ويلحق بالنفي ما أشبهه في التنكيل من الحبس وغيره «ذلك» الجزاء المذكور «لهم خزي» ذل «في الدنيا ولهم في الآخرة عذاب عظيم» هو عذاب النار.'),
(703, 5, 34, '«إلا الذين تابوا» من المحاربين والقطَّاع «من قبل أن تقدروا عليهم فاعلموا أن الله غفور» لهم ما أتوه «رحيم» بهم عبر بذلك دون فلا تحدوهم ليفيد أنه لا يسقط عنه بتوبته إلا حدود الله دون حقوق الآدميين كذا ظهر لي ولم أرَ من تعرض له والله أعلم فإذا قتل وأخذ المال يقتل ويقطع ولا يصلب وهو أصح قولي الشافعي ولا تفيد توبته بعد القدرة عليه شيئا وهو أصح قولي الشافعي ولا تفيد توبته بعد القدرة عليه شيئا وهو أصح قوليه أيضا.'),
(704, 5, 35, '«يا أيها الذين آمنوا اتقوا الله» خافوا عقابه بأن تطيعوه «وابتغوا» اطلبوا «إليه الوسيلة» ما يقربكم إليه من طاعته «وجاهدوا في سبيله» لإعلاء دينه «لعلكم تفلحون» تفوزون.'),
(705, 5, 36, '«إن الذين كفروا لو» ثبت «أن لهم ما في الأرض جميعا ومثله معه ليفتدوا به من عذاب يوم القيامة ما تقبِّل منهم ولهم عذاب أليم».'),
(706, 5, 37, '«يريدون» يتمنَّون «أن يخرجوا من النار وما هم بخارجين منها ولهم عذاب مقيم» دائم.'),
(707, 5, 38, '«والسارق والسارقة» أل فيهما موصولة مبتدأ ولشبهه بالشرط دخلت الفاء في خبره وهو «فاقطعوا أيديهما» أي يمين كل منهما من الكوع وبينت السنة أن يقطع فيه ربع دينار فصاعدا وأنه إذا عاد قطعت رجله اليسرى من مفصل القدم ثم اليد اليسرى ثم الرجل اليمنى وبعد ذلك يعزر «جزاء» نصب على المصدر «بما كسبا نكالا» عقوبة لهما «من الله والله عزيز» غالب على أمره «حكيم» في خلفه.'),
(708, 5, 39, '«فمن تاب من بعد ظلمه» رجع عن السرقة «وأصلح» عمله «فإن الله يتوب عليه إن الله غفور رحيم» في التعبير بهذا ما تقدم فلا يسقط بتوبته حق الآدمي من القطع ورد المال نعم بيَّنت السنة أنه إن عفا عنه قبل الرفع إلى الإمام سقط القطع وعليه الشافعي.'),
(709, 5, 40, '«ألم تعلم» الإستفهام فيه للتقرير «أن الله له ملك السماوات والأرض يعذَّب من يشاء» تعذيبه «ويغفر لمن يشاء» المغفرة له «والله على كل شيء قدير» ومنه التعذيب والمغفرة.'),
(710, 5, 41, '«يا أيها الرسول لا يَحزنك» صنع «الذين يسارعون في الكفر» يقعون فيه بسرعة أي يظهرونه إذا وجدوا فرصة «من» للبيان «الذين قالوا آمنا بأفوههم» بألسنتهم متعلق بقالوا «ولم تؤمن قلوبهم» وهم المنافقون «ومن الذين هادوا» قوم «سماعون للكذب» الذي افترته أحبارهم سماع قبول «سماعون» منك «لقوم» لأجل قوم «آخرين» من اليهود «لم يأتوك» وهم أهل خيبر زنى فيهم محصنان فكرهوا رجمهما فبعثوا قريظة ليسألوا النبي صلى الله عليه وسلم عن حكمهما «يحرفون الكلم» الذي في التوراة كآية الرجم «من بعد مواضعه» التي وضعه الله عليها أي يبدِّلونه «يقولون» لمن أرسلوهم «إن أُوتيتم هذا» الحكم المحرف أي الجلد الذي أفتاكم به محمد «فخذوه» فاقبلوه «وإن لم تؤتوه» بل أفتاكم بخلافه «فاحذروا» أن تقبلوه «ومن يرد الله فتنته» إضلاله «فلن تملك له من الله شيئا» في دفعها «أولئك الذين لم يرد الله أن يطهر قلوبهم» من الكفر ولو أراده لكان «لهم في الدنيا خزي» ذل بالفضيحة والجزية «ولهم في الآخرة عذاب عظيم».'),
(711, 5, 42, 'هم «سماعون للكذب أكالون للسُّحُت» بضم الحاء وسكونها أي الحرام كالرشا «فإن جاؤك» لتحكم بينهم «فاحكم بينهم أو أعرض عنهم» هذا التخيير منسوخ بقوله تعالى (وأن احكم بينهم) الآية فيجب الحكم بينهم إذا ترافعوا إلينا وهو أصح قولي الشافعي فلو ترافعوا إلينا مع مسلم وجب إجماعا «وإن تعرض عنهم فلن يضروك شيئا وإن حكمت» بينهم «فاحكم بينهم بالقسط» بالعدل «إن الله يحب المقسطين» العادلين في الحكم أي يثيبهم.'),
(712, 5, 43, '«وكيف يحكِّمونك وعندهم التوراة فيها حكم الله» بالرجم استفهام تعجب أي لم يقصدوا بذلك معرفة الحق بل ما هو أهون عليهم «ثم يتولَّون» يعرضون عن حكمك بالرجم الموافق لكتابهم «من بعد ذلك» التحكيم «وما أولئك بالمؤمنين».'),
(713, 5, 44, '«إنا أنزلنا التوراة فيها هدى» من الضلالة «ونورٌ» بيان للأحكام «يحكم بها النبيون» من بني إسرائيل «الذين أسلموا» انقادوا لله «للذين هادوا والربانيون» العلماء منهم «والأحبار» الفقهاء «بما» أي بسبب الذي «استحفظوا» استودعوه أي استحفظهم الله إياه «من كتاب الله» أن يبدلوه «وكانوا عليه شهداء» أنه حق «فلا تخشوا الناس» أيها اليهود في إظهار ما عندكم من نعت محمد صلى الله عليه وسلم والرجم وغيرها «واخشوْن» في كتمانه «ولا تشتروا» تستبدلوا «بآياتي ثمنا قليلا» من الدنيا تأخذونه على كتمانها «ومن لم يحكم بما أنزل الله فأولئك هم الكافرون» به.'),
(714, 5, 45, '«وكتبنا» فرضنا «عليهم فيها» أي التوراة «أن النفس» تقتل «بالنفس» إذا قتلتها «والعين» تُفقأ «بالعين والأنف» يجُدع «بالأنف والأذن» تُقطع «بالأذن والسنَّ» تقلع «بالسنِّ» وفي قراءة بالرفع في الأربعة «والجروح» بالوجهين «قصاص» أي فيها إذا كاليد والرجل ونحو ذلك وما لا يمكن فيه الحكومة وهذا الحكم وإن كتب عليهم فهو مقرر في شرعنا «فمن تصدق به» أي القصاص بأن مكن عن نفسه «فهو كفارة له» لما أتاه «ومن لم يحكم بما أنزل الله» في القصاص وغيره «فأولئك هم الظالمون».'),
(715, 5, 46, '«وقفّينا» أتبعنا «على آثارهم» أي النبيين «بعيسى ابن مريم مصدِّقا لما بين يديه» قبله «من التوراة وآتيناه الإنجيل فيه هدى» من الضلالة «ونور» بيان للأحكام «ومصدِّقا» حال «لما بين يديه من التوراة» لما فيها من الأحكام «وهدى وموعظة للمتقين».'),
(716, 5, 47, '«و» قلنا «لْيَحْكُمْ أهل الإنجيل بما أنزل الله فيه» من الأحكام وفي قراءة بنصب يحكم وكسر لامه عطفا على معمول آتيناه «ومن لم يحكم بما أنزل الله فأولئك هم الفاسقون».'),
(717, 5, 48, '«وأنزلنا إليك» يا محمد «الكتاب» القرآن «بالحق»: متعلق بأنزلنا «مصدِّقا لما بين يديه» قبله «من الكتاب ومهيمنا» شاهدا «عليه» والكتاب بمعنى الكتب «فاحكم بينهم» بين أهل الكتاب إذا ترافعوا إليك «بما أنزل الله» إليك «ولا تتبع أهواءهم» عادلا «عما جاءك من الحق لكل جعلنا منكم» أيها الأمم «شرعة» شريعة «ومنهاجا» طريقاً واضحاً في الدين يمشون عليه «ولو شاء الله لجعلكم أمة واحدة» على شريعة واحدة «ولكن» فرقكم فرقاً «ليبلوكم» ليختبركم «فيما آتاكم» من الشرائع المختلفة لينظر المطيع منكم والعاصي «فاستبقوا الخيرات» سارعوا إليها «إلى الله مرجعكم جميعا» بالبعث «فينبئكم بما كنتم فيه تختلفون» من أمر الدين ويجزي كلا منكم بعمله.'),
(718, 5, 49, '«وأن احكم بينهم بما أنزل الله ولا تتبع أهواءهم واحذرهم» «أن» لا «يفتنوك» يضلوك «عن بعض ما أنزل الله إليك فإن تولوا» عن الحكم المنزل وأرادوا غيره «فاعلم أنما يريد الله أن يصيبهم» بالعقوبة في الدنيا «ببعض ذنوبهم» التي أتوها ومنها التولي ويجازيهم على جميعها في الأخرى «وإن كثيرا من الناس لفاسقون».'),
(719, 5, 50, '«أفحكم الجاهلية يبغون» بالياء والتاء يطلبون من المداهنة والميل إذا تولَّوا إستفهام إنكاري «ومن» أي لا أحد «أحسن من الله حُكما لقوم» عند قوم «يوقنون» به خصوا بالذكر لأنهم الذين يتدبرون.'),
(720, 5, 51, '«يا أيها الذين آمنوا لا تتخذوا اليهود والنصارى أولياء» توالونهم وتوادونهم «بعضهم أولياء بعض» لا تحادهم في الكفر «ومن يتولَّهم منكم فإنه منهم» من جملتهم «إن الله لا يهدى القوم الظالمين» بموالاتهم الكفار.'),
(721, 5, 52, '«فترى الذين في قلوبهم مرض» ضعف اعتقاد كعبد الله بن أبي المنافق «يسارعون فيهم» في موالاتهم «يقولون» معتذرين عنها «نخشى أن تصيبنا دائرة» يدور بها الدهر علينا من جدب أو غلبة ولا يتم أمر محمد فلا يميرونا قال تعالى: «فعسى الله أن يأتي بالفتح» بالنصر لنبيه بإظهار دينه «أو أمر من عنده» بهتك ستر المنافقين وافتضاحهم «فيصبحوا على ما أسرُّوا في أنفسهم» من الشك وموالاة الكفار «نادمين».'),
(722, 5, 53, '«ويقولُ» بالرفع استئنافا بواو ودونها وبالنصب عطفا على يأتي «الذين آمنوا» لبعضهم إذا هتك سترهم تعجبا «أهؤلاء الذين أقسموا بالله جَهد أيمانهم» غاية اجتهادهم فيها «إنهم لمعكم» في الدين قال تعالى: «حبطت» بطلت «أعمالهم» الصالحة «فأصبحوا» صاروا «خاسرين» الدنيا بالفضيحة والآخرة بالعقاب.'),
(723, 5, 54, '(يا أيها الذين آمنوا من يرتد) بالفك والإدغام يرجع (منكم عن دينه) إلى الكفر إخبار بما علم الله وقوعه وقد ارتد جماعة بعد موت النبي صلى الله عليه وسلم (فسوف يأتي الله) بدلهم (بقوم يحبهم ويحبونه) قال صلى الله عليه وسلم: "" هم قوم هذا وأشار إلى أبي موسى الأشعري "" رواه الحاكم في صحيحه (أذلة) عاطفين (على المؤمنين أعزة) أشداء (على الكافرين يجاهدون في سبيل الله ولا يخافون لومة لائم) فيه كما يخاف المنافقون لوم الكفار (ذلك فضل الله يؤتيه من يشاء والله واسع) كثير الفضل (عليم) بمن هو أهله، ونزل لما قال ابن سلام يا رسول الله إن قومنا هجرونا.'),
(724, 5, 55, '«إنَّما وليكم الله ورسوله والذين آمنوا الذين يقيمون الصلاة ويؤتون الزكاة وهم راكعون» خاشعون أو يصلون صلاة التطوع.'),
(725, 5, 56, '«ومن يتولَّ الله ورسوله والذين آمنوا» فيعينهم وينصرهم «فإن حزب الله هم الغالبون» لنصره إياهم أوقعه موقع فإنهم بيانا لأنهم من حزبه أي أتباعه.'),
(726, 5, 57, '«يا أيها الذين آمنوا لا تتَّخذوا الذين اتَّخذوا دينكم هزوا» مهزوءا به «ولعبا من» للبيان «الذين أوتوا الكتاب من قبلكم والكفار» المشركين بالجر والنصب «أولياء واتقوا الله» بترك موالاتهم «إن كنتم مؤمنين» صادقين في إيمانكم.'),
(727, 5, 58, '«و» الذين «إذا ناديتم» دعوتم «إلى الصلاة» بالأذان «اتخذوها» أي الصلاة «هزوا ولعبا» بأن يستهزئوا بها ويتضاحكوا «ذلك» الاتخاذ «بأنهم» أي بسبب أنهم «قوم لا يعقلون».'),
(728, 5, 59, 'ونزل لما قال اليهود للنبي صلى الله عليه وسلم بمن تؤمن من الرسل فقال: (بالله وما أنزل إلينا) الآية فلما ذكر عيسى قالوا لا نعلم دينا شرا من دينكم «قل يا أهل الكتاب هل تنقمون» تنكرون «منا إلا أن آمنا بالله وما أنزل إلينا وما أنزل من قبل» إلى الأنبياء «وأن أكثركم فاسقون» عطف على أن آمنا- المعنى ما تنكرون إلا إيماننا ومخالفتكم في عدم قبوله المعبر عنه بالفعل اللازم عنه وليس هذا مما ينكر.'),
(729, 5, 60, '«قل هل أنبئكم» أخبركم «بشر من» أهل «ذلك» الذي تنقمونه «مثوبة» ثوابا بمعنى جزاء «عند الله» هو «من لعنه الله» أبعده عن رحمته «وغضب عليه وجعل منهم القردة والخنازير» بالمسخ «و» من «عَبَدَ الطاغوتَ» الشيطان بطاعته، وروعي في منهم معنى من وفيما قبله لفظها وهم اليهود، وفي قراءة بضم باء عبد وإضافته إلى ما بعد اسم جمع لعبد ونصبه بالعطف على القردة «أولئك شر مكانا» تمييز لأن مأواهم النار «وأضل عن سواء السبيل» طريق الحق وأصل السواء الوسط وذكر شر وأضل في مقابلة قولهم لا نعلم دينا شرا من دينكم.'),
(730, 5, 61, '«وإذا جاءُوكم» أي مُنَافِقُو اليهود «قالوا آمنا وقد دخلوا» إليكم متلبسين «بالكفر وهم قد خرجوا» من عندكم متلبسين «به» ولم يؤمنوا «والله أعلم بما كانوا يكتمونـ» ـه من النفاق.'),
(731, 5, 62, '«وترى كثيرا منهم» أي اليهود «يسارعون» يقعون سريعا «في الإثم» الكذب «والعدوان» الظلم «وأكلهم السُّحُت» الحرام كالرشا «لبئس ما كانوا يعملونـ» ـهُ عملهم هذا.'),
(732, 5, 63, '«لولا» هلا «ينهاهم الربَّانيون والأحبار» منهم «عن قولهم الإثم» الكذب «وأكلهم السحت لبئس ما كانوا يصنعونـ» ـه ترك نهيهم.'),
(733, 5, 64, '«وقالت اليهود» لما ضيق عليهم بتكذيبهم النبي صلى الله عليه وسلم بعد أن كانوا أكثر الناس مالا «يد الله مغلولة» مقبوضة عن إدرار الرزق علينا كنوا به عن البخل - تعالى الله عن ذلك- قال تعالى: «غُلَّتْ» أمسكت «أيديهم» عن فعل الخيرات دعاء عليهم «ولُعنوا بما قالوا بل يداه مبسوطتان» مبالغة في الوصف بالجود وثنى اليد لإفادة الكثرة إذ غاية ما يبذله السخي من ماله أن يعطي بيديه «ينفق كيف يشاء» من توسيع وتضيق لا اعتراض عليه «وليزيدن كثيرا منهم ما أنزل إليك من ربك» من القرآن «طغيانا وكفرا» لكفرهم به «وألقينا بينهم العداوة والبغضاء إلى يوم القيامة» فكل فرقة منهم تخالف الأخرى «كلما أوقدوا نارا للحرب» أي لحرب النبي صلى الله عليه وسلم «أطفأها الله» أي كلما أرادوه ردهم «ويسعَون في الأرض فسادا» أي مفسدين بالمعاصي «والله لا يحب المفسدين» بمعنى أنه يعاقبهم.'),
(734, 5, 65, '«ولو أن أهل الكتاب آمنوا» بمحمد صلى الله عليه وسلم «واتقوا» الكفر «لكفرنا عنهم سيئاتهم ولأدخلناهم جنات النعيم».'),
(735, 5, 66, '«ولو أنهم أقاموا التوراة والإنجيل» بالعمل بما فيهما ومنه الإيمان بالنبي صلى الله عليه وسلم «وما أنزل إليهم» من الكتب «من ربِّهم لأكلوا من فوقهم ومن تحت أرجلهم» بأن يوسع عليهم الرزق ويفيض من كل جهة «منهم أمَّة» جماعة «مقتصدة» تعمل به وهم من آمن بالنبي صلى الله عليه وسلم كعبد الله بن سلام وأصحابه «وكثير منهم ساء» بئس «ما» شيئا «يعملونـ» ـه.'),
(736, 5, 67, '(يا أيها الرسول بلغ) جميع (ما أنزل إليك من ربك) ولا تكتم شيئا منه خوفا أن تنال بمكروه (وإن لم تفعل) أي لم تبلغ جميع ما أنزل إليك (فما بلغت رسالته) بالإفراد والجمع لأن كتمان بعضها ككتمان كلها (والله يعصمك من الناس) أن يقتلوك وكان صلى الله عليه وسلم يحرس حتى نزلت فقال: "" انصرفوا فقد عصمني الله "" رواه الحاكم (إن الله لا يهدي القوم الكافرين).'),
(737, 5, 68, '«قل يا أهل الكتاب لستم على شيء» من الدين معتد به «حتى تقيموا التوراة والإنجيل وما أنزل إليكم من ربكم» بأن تعملوا بما فيه ومنه الإيمان بي «وليزيدن كثيرا منهم ما أنزل إليك من ربك» من القرآن «طغيانا وكفرا» لكفرهم به «فلا تأس» تحزن «على القوم الكافرين» إن لم يؤمنوا بك أي لا تهتم بهم.'),
(738, 5, 69, '«إن الذين آمنوا والذين هادوا» هم اليهود مبتدأ «والصابئون» فرقة منهم «والنصارى» ويبدل من المبتدأ «من آمن» منهم «بالله واليوم الآخر وعمل صالحا فلا خوف عليهم ولا هم يحزنون» في الآخرة خبر المبتدأ ودال على خبر إن.'),
(739, 5, 70, '«لقد أخذنا ميثاق بني إسرائيل» على الإيمان بالله ورسله «وأرسلنا إليهم رسلا كلما جاءهم رسول» منهم «بما لا تهوى أنفسهم» من الحق كذبوه «فريقا» منهم «كذَّبوا وفريقا» منهم «يقتلون» كزكريا ويحيى والتعبير به دون قتلوا حكاية للحال الماضية للفاصلة.'),
(740, 5, 71, '«وحسِبوا» ظنوا «أ» ن «لا تكونُ» بالرفع فأن مخففة والنصب فهي ناصبة أي تقع «فتنة» عذاب بهم على تكذيب الرسل وقتلهم «فعموا» عن الحق فلم يبصروه «وصمّوا» عن استماعه «ثم تاب الله عليهم» لما تابوا «ثم عموا وصمُّوا» ثانيا «كثير منهم» بدل من الضمير «والله بصير بما يعملون» فيجازيهم به.'),
(741, 5, 72, '«لقد كفر الذين قالوا إن الله هو المسيح ابن مريم» سبق مثله «وقال» لهم «المسيح يا بني إسرائيل اعبدوا الله ربي وربكم» فإني عبد ولست بإله «إنه من يشرك بالله» في العبادة غيره «فقد حرَّم الله عليه الجنة» منعه أن يدخلها «ومأواه النار وما للظالمين من» زائدة «أنصار» يمنعونهم من عذاب الله.'),
(742, 5, 73, '«لقد كفر الذين قالوا إن الله ثالث» آلهة «ثلاثة» أي أحدها والآخران عيس وأمه وهم فرقة من النصارى «وما من إله إلا إله واحد وإن لم ينتهوا عما يقولون» من التثليث ويوحدوا «ليمسنَّ الذين كفروا» أي ثبتوا على الكفر «منهم عذاب أليم» مؤلم وهو النار.'),
(743, 5, 74, '«أفلا يتوبون إلى الله ويستغفرونه» مما قالوا. استفهام توبيخ «والله غفور» لمن تاب «رحيم» به.'),
(744, 5, 75, '«ما المسيح ابن مريم إلا رسول قد خلت» مضت «من قبله الرسل» فهو يَمضي مثلهم وليس بإله كما زعموا وإلا لما مضى «وأُمُّه صدَيقة» مبالغة في الصدق «كانا يأكلان الطعام» كغيرهما من الناس ومن كان كذلك لا يكون إلها لتركيبه وضعفه وما ينشأ منه من البول والغائط «أنظر» متعجبا «كيف نبين لهم الآيات» على وحدانيتنا «ثم أنظر أنَّى» كيف «يُؤفكون» يصرفون عن الحق مع قيام البرهان.'),
(745, 5, 76, '«قل أتعبدون من دون الله» أي غيره «ما لا يملك لكم ضرّا ولا نفعا والله هو السميع» لأقوالكم «العليم» بأحوالكم والاستفهام للإنكار.'),
(746, 5, 77, '«قل يا أهل الكتاب» اليهود والنصارى «لا تغلوا» لا تجاوزوا الحد «في دينكم» غلوّا «غير الحق» بأن تضعوا عيسى أو ترفعوه فوق حقه «ولا تتبعوا أهواء قوم قد ضلوا من قبل» بغلوهم وهم أسلافُهم «وأضلوا كثيرا» من الناس «وضلُّوا عن سواء السبيل» عن طريق الحق والسواء في الأصل الوسط.'),
(747, 5, 78, '«لعن الذين كفروا من بني إسرائيل على لسان داوود» بأن دعا عليهم فمسخوا قردة وهم أصحاب إيلة «وعيسى ابن مريم» بأن دعا عليهم فمسخوا خنازير وهم أصحاب المائدة «ذلك» اللعن «بما عصوا وكانوا يعتدون».'),
(748, 5, 79, '«كانوا لا يتناهون» أي لا ينهى بعضهم بعضا «عن» معاودة «منكر فعلوه لبئس ما كانوا يفعلون» فعلهم هذا.'),
(749, 5, 80, '«ترى» يا محمد «كثيرا منهم يتولَّون الذين كفروا» من أهل مكة بغضا لك «لبئس ما قدَّمت لهم أنفسهم» من العمل لمعادهم الموجب لهم «أن سخط الله عليهم وفي العذاب هم خالدون».'),
(750, 5, 81, '«ولو كانوا يؤمنون بالله والنبي» محمد «وما أنزل إليه ما اتخذوهم» أي الكفار «أولياء ولكنَّ كثيرا منهم فاسقون» خارجون عن الإيمان.'),
(751, 5, 82, '«لتجدنَّ» يا محمد «أشدَّ الناس عداوة للذين آمنوا اليهود والذين أشركوا» من أهل مكة لتضاعف كفرهم وجهلهم وانهماكهم في اتباع الهوى «ولتجدن أقربهم مودة للذين آمنوا الذين قالوا إنَّا نصارى ذلك» أي قرب مودتهم للمؤمنين «بأن» بسبب أن «منهم قسيسين» علماء «ورهبانا» عبادا «وأنهم لا يستكبرون» عن اتباع الحق كما يستكبر اليهود وأهل مكة نزلت في وفد النجاشي القادمين عليه من الحبشة قرأ سورة يس فبكوا وأسلموا وقالوا ما أشبه هذا بما كان ينزل على عيسى.'),
(752, 5, 83, 'قال تعالى: «وإذا سمعوا ما أنزل إلى الرسول» من القرآن «ترى أعينهم تفيض من الدمع ممَّا عرفوا من الحق يقولون ربَّنا آمنا» صدقنا بنبيك وكتابك «فاكتبنا مع الشاهدين» المقرين بتصديقهما.'),
(753, 5, 84, '«و» قالوا في جواب من عيرهم بالإسلام من اليهود «ما لنا لا نؤمن بالله وما جاءنا من الحق» القرآن أي لا مانع لنا من الإيمان مع وجود مقتضيه «ونطمع» عطف على نؤمن «أن يدخلنا ربُّنا مع القوم الصالحين» المؤمنين الجنة قال تعالى.'),
(754, 5, 85, '«فأثابهم الله بما قالوا جنات تجري من تحتها الأنهار خالدين فيها وذلك جزاء المحسنين» بالإيمان.'),
(755, 5, 86, '«والذين كفروا وكذَّبوا بآياتنا أولئك أصحاب الجحيم».'),
(756, 5, 87, 'ونزل لما همَّ قوم من الصحابة أن يلازموا الصوم والقيام ولا يقربوا النساء والطيب ولا يأكلوا اللحم ولا يناموا على الفراش «يا أيها الذين آمنوا لا تحرموا طيبات ما أحل الله لكم ولا تعتدوا» تتجاوزوا أمر الله «إن الله لا يحب المعتدين».'),
(757, 5, 88, '«وكلوا مما رزقكم الله حلالا طيبا» مفعول والجار والمجرور قبله حال متعلق به «واتقوا الله الذي أنتم به مؤمنون».'),
(758, 5, 89, '«لا يؤاخذكم الله باللغو» الكائن «في أيمانكم» هو ما يسبق إليه اللسان من غير قصد الحلف كقول الإنسان: لا والله، وبلى والله «ولكن يؤاخذكم بما عَقّدتُمُ» بالتخفيف والتشديد وفي قراءة عاقدتم «الأيمان» عليه بأن حلفتم عن قصد «فكفارته» أي اليمين إذا حنثتم فيه «إطعام عشرة مساكين» لكل مسكين مدٌ «من أوسط ما تطعمون» منه «أهليكم» أي أقصده وأغلبه لا أعلاه ولا أدناه «أو كسوتهم» بما يسمى كسوة كقميص وعمامة وإزار ولا يكفي دفع ما ذكر إلى مسكين واحد وعليه الشافعي «أو تحرير» عتق «رقبة» أي مؤمنة كما في كفارة القتل والظهار حملا للمطلق على المقيد «فمن لم يجد» واحدا مما ذكر «فصيام ثلاثة أيام» كفارته وظاهره أنه لا يشترط التتابع وعليه الشافعي «ذلك» المذكور «كفارة أيمانكم إذا حلفتم» وحنثتم «واحفظوا أيمانكم» أن تنكثوها ما لم تكن على فعل بر أو إصلاح بين الناس كما في سورة البقرة «كذلك» أي مثل ما بين لكم ما ذكر «يبيِّن الله لكم آياته لعلكم تشكرونـ» ـه على ذلك.'),
(759, 5, 90, '«يا أيها الذين آمنوا إنما الخمر» المسكر الذي يخامر العقل «والميسر» القمار «والأنصاب» الأصنام «والأزلام» قداح الاستقسام «رجس» خبيث مستقذر «من عمل الشيطان» الذي يزيّنه «فاجتنبوه» أي الرجس المعبر به عن هذه الأشياء أن تفعلوه «لعلكم تفلحون».'),
(760, 5, 91, '«إنما يريد الشيطان أن يوقع بينكم العداوة والبغضاء في الخمر والميسر» إذا أتيتموهما لما يحصل فيهما من الشر والفتن «ويصدَّكم» بالاشتغال بهما «عن ذكر الله وعن الصلاة» خصها بالذكر تعظيما لها «فهل أنتم منتهون» عن إتيانهما أي انتهوا.'),
(761, 5, 92, '«وأطيعوا الله وأطيعوا الرسول واحذروا» المعاصي «فإن تولَّيتم» عن الطاعة «فاعلموا أنما على رسولنا البلاغ المبين» الإبلاغ البيِّن وجزاؤكم علينا.'),
(762, 5, 93, '«ليس على الذين آمنوا وعملوا الصالحات جناح فيما طعموا» أكلوا من الخمر والميسر قبل التحريم «إذا ما اتقوا» المحرمات «وآمنوا وعملوا الصالحات ثم اتقوا وآمنوا» ثبتوا على التقوى والإيمان «ثم اتقوا وأحسنوا» العمل «والله يحب المحسنين» بمعني أنه يثيبهم.'),
(763, 5, 94, '«يا أيها الذين آمنوا لَيبلونَّكم» ليختبرنكم «الله بشيء» يرسله لكم «من الصيد تناله» أي الصغار منه «أيديكم ورماحكم» الكبار منه، وكان ذلك بالحديبية وهم مُحرمون فكانت الوحش والطير تغشاهم في حالهم «ليعلم الله» علم ظهور «من يخافه بالغيب» حال أي غائبا لم يره فيجتنب الصيد «فمن اعتدى بعد ذلك» النهي عنه فاصطاده «فله عذاب أليم».'),
(764, 5, 95, '«يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حُرُم» محرمون بحج أو عمرة «ومن قتله منكم متعمِّدا فجزاءٌ» بالتنوين ورفع ما بعده أي فعليه جزاء هو «مثلُ ما قتل من النعم» أي شبهه في الخلقة وفي قراءة بإضافة جزاء «يحكم به» أي بالمثل رجلان «ذوا عدل منكم» لهما فطنة يميزان بها أشبه الأشياء به، وقد حكم ابن عباس وعمر وعلى رضي الله عنهم في النعامة ببدنة، وابن عباس وأبوعبيدة في بقر الوحش وحماره ببقره وابن عمر وابن عوف في الظبي بشاة وحكم بها ابن عباس وعمر وغيرهما في الحمام لأنه يشبهها في العَبِّ «هديا» حال من جزاء «بالغ الكعبة» أي يبلغ به الحرم فيذبح فيه ويتصدق به على مساكينه ولا يجوز أن يذبح حيث كان ونصبه نعتا لما قبله وإن أضيف لأن إضافته لفظية لا تفيد تعريفا فإن لم يكن للصيد مثل من النعم كالعصفور والجراد فعليه قيمته «أو» عليه «كفارةٌ» غير الجزاء وإن وجده هي «طعامُ مساكين» من غالب قوت البلد ما يساوى قيمته الجزاء لكل مسكين مد، وفي قراءة بإضافة كفارة لما بعده وهي للبيان «أو» عليه «عدل» مثل «ذلك» الطعام «صياما» يصومه عن كل مد يوما وإن وجده وجب ذلك عليه «ليذوق وبال» ثقل جزاء «أمره» الذي فعله «عفا الله عما سلف» من قتل الصيد قبل تحريمه «ومن عاد» إليه «فينتقم الله منه والله عزيز» غالب على أمره «ذو انتقام» ممن عصاه، والحق بقتله متعمداً فيما ذكر الخطأ.'),
(765, 5, 96, '«أُحل لكم» أيها الناس حلالا كنتم أو محرمين «صيد البحر» أن تأكلوه وهو ما لا يعيش إلا فيه كالسمك بخلاف ما يعيش فيه وفي البر كالسرطان «وطعامُه» ما يقذفه ميتا «متاعا» تمتيعا «لكم» تأكلونه «وللسيَّارة» المسافرين منكم يتزودونه «وحرِّم عليكم صيد البر» وهو ما يعيش فيه من الوحش المأكول أن تصيدوه «ما دمتم حرما» فلو صاده حَلاَل فللمحرم أكله كما بينته السنة «واتقوا الله الذي إليه تحشرون».'),
(766, 5, 97, '«جعل الله الكعبة البيت الحرام» المحرم «قياما للناس» يقوم به أمر دينهم بالحج إليه ودنياهم بأمن داخله وعدم التعرض له وجبي ثمرات كل شيء إليه، وفي قراءة قيما بلا ألف مصدر قام غير معل «والشهر الحرام» بمعنى الأشهر الحرم ذو القعدة وذو الحجة والمحرم ورجب قياما لهم بأمنهم من القتال فيها «والهدي والقلائد» قياما لهم بأمن صاحبهما من التعرض له «ذلك» الجعل المذكور «لتعلموا أن الله يعلم ما في السموات وما في الأرض وأن الله بكل شيء عليم» فإن جعله ذلك لجلب المصالح لكم ودفع المضار عنكم قبل وقوعها دليل على علمه بما هو في الوجود وما هو كائن.'),
(767, 5, 98, '«اعلموا أن الله شديد العقاب» لأعدائه «وأن الله غفور» لأوليائه «رحيم» بهم.'),
(768, 5, 99, '«ما على الرسول إلا البلاغ» لكم «والله يعلم ما تبدون» تظهرون من العمل «وما تكتمون» تخفون منه فيجازيكم به.'),
(769, 5, 100, '«قل لا يستوي الخبيث» الحرام «والطيب» الحلال «ولو أعجبك» أي سرك «كثرة الخبيث فاتقوا الله» في تركه «يا أولي الألباب لعلكم تفلحون» تفوزون.'),
(770, 5, 101, 'ونزل لما أكثروا سؤاله صلىالله عليه وسلم «يا أيها الذين آمنوا لا تسألوا عن أشياء إن تُبدَ» تظهر «لكم تسؤكم» لما فيها من المشقة «وإن تسألوا عنها حين ينزَّل القرآن» في زمن النبي صلىالله عليه وسلم «تُبدَ لكم» المعنى إذا سألتم عن أشياء في زمنه ينزل القرآن بإبدائها ومتى أبداها ساءتكم فلا تسألوا عنها قد «عفا الله عنها» من مسألتكم فلا تعودوا «والله غفور رحيم».'),
(771, 5, 102, '«قد سألها» أي الأشياء «قوم من قبلكم» أنبياءهم فأجيبوا ببيان أحكامها «ثم أصبحوا» صاروا «بها كافرين» بتركهم العمل بها.'),
(772, 5, 103, '«ما جعل» شرع «الله من بحيرة ولا سائبة ولا وصيلة ولا حام» كما كان أهل الجاهلية يفعلونه، روى البخاري عن سعيد بن المسيب قال: البحيرة التي يمنع درها للطواغيت فلا يحلبها أحد من الناس، والسائبة التي كانوا يسيبونها لآلهتم فلا يحمل عليها شيء، والوصيلة الناقة البكر تبكر في أول نتاج الإبل بأنثى ثم تثني بعد بأنثى وكانوا يسيبونها لطواغيتهم إن وصلت إحداهما بأخرى ليس بينهما ذكر، والحام فحل الإبل يضرب الضراب المعدودة فإذا قضى ضرابه ودعوه للطواغيت وأعفوه من أن يحمل عليه شيء وَسَمَّوْه الحامي «ولكنَّ الذين كفروا يفترون على الله الكذب» في ذلك وفي نسبته إليه «وأكثرهم لا يعقلون» أن ذلك افتراء لأنهم قلدوا فيه آباءهم.'),
(773, 5, 104, '«وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول» أي إلى حكمه من تحليل ما حرمتم «قالوا حسبنا» كافينا «ما وجدنا عليه آباءنا» من الدين والشريعة قال تعالى: «أ» حسبهم ذلك «ولو كان آباؤهم لا يعلمون شيئا ولا يهتدون» إلى الحق والاستفهام للإنكار.'),
(774, 5, 105, '(يا أيها الذين آمنوا عليكم أنفسكم) أي احفظوها وقوموا بصلاحها (لا يضركم من ضل إذا اهتديتم) قيل المراد لا يضركم من ضل من أهل الكتاب وقيل المراد غيرهم لحديث أبي ثعلبة الخشني: سألت عنها رسول الله صلى الله عليه وسلم فقال: "" إئتمروا بالمعروف وتناهوا عن المنكر حتى إذا رأيت شحا مطاعا ودنيا مؤثرة وإعجاب كل ذي رأي برأيه فعليك نفسك "" رواه الحاكم وغيره (إلى الله مرجعكم جميعا فينبئكم بما كنتم تعملون) فيجازيكم به.'),
(775, 5, 106, '«يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموتُ» أي أسبابه «حين الوصية اثنان ذوا عدل منكم» خبر بمعنى الأمر أي ليشهد وإضافة شهادة لبين على الإتساع وحين بدل من إذا أو ظرف لحضر «أو آخران من غيركم» أي غير ملتكم «إن أنتم ضربتم» سافرتم «في الأرض فأصابكم مصيبة الموت تحبسونهما» توقفونهما صفة آخران «من بعد الصلاة» أي صلاة العصر «فيقسمان» يحلفان «بالله إن أرتبتم» شككتم فيها ويقولان «لا نشتري به» بالله «ثمنا» عوضا نأخذه بدله من الدنيا بأن نحلف به أو نشهد كذبا لأجله «ولو كان» المقسم له أو المشهود لله «ذا قربى» قرابة منا «ولا نكتم شهادة الله» التي أمرنا بها «إنا إذا» إن كتمناها «لمن الآثمين».'),
(776, 5, 107, '«فإن عُثر» اطُّلع بعد حلفهما «على أنهما استحقا إثما» أي فعلا ما يوجبه من خيانة أو كذب في الشهادة بأن وجد عندهما مثلا ما اتُّهما به وادعيا أنهما ابتاعاه من الميت أو وصى لهما به «فآخران يقومان مقامهما» في توجيه اليمين عليهما «من الذين استحق عليهم» الوصية وهم الورثة ويبدل من آخران «الأوليان» بالميت أي الأقربان إليه وفي قراءة الأَوَّلينَ جمع أوَّل صفة أو بدل من الذين «فيقسمان بالله» على خيانة الشاهدين ويقولان «لشهادتنا» يمننا «أحق» أصدق «من شهادتهما» يمينهما «وما اعتدينا» تجاوزنا الحق في اليمين «إنا إذا لمن الظالمين» المعنى ليشهد المحتضر على وصيته اثنين أو يوصي إليهما من أهل دينه أو غيرهم إن فقدهم لسفر ونحو فإن ارتاب الورثة فيهما فادعوا أنهما خانا بأخذ شيء أو دفعه إلى شخص زعما أن الميت أوصي له به فليحلفا إلى آخره فإن اطلع على أمارة تكذيبهما فادعيا دافعا له حلف أقرب الورثة على كذبهما وصدق ما ادعوه والحكم ثابت في الوصيين منسوخ في الشاهدين وكذا شهادة غير أهل الملة منسوخة واعتبار صلاة العصر للتغليظ وتخصيص الحلف في الآية باثنين من أقرب الورثة لخصوص الواقعة التي نزلت لها وهي ما رواه البخاري أن رجلا من بني سهم خرج مع تميم الداري وعدي بن بداء أي وهما نصرانيان فمات السهمي بأرض ليس فيها مسلم فلما قدما بتركته فقدوا جاما من فضة مخوصا بالذهب فرفعا إلى النبي صلىالله عليه وسلم فنزلت فأحلفهما ثم وجد الجام بمكة فقالوا ابتعناه من تميم وعدى فنزلت الآية الثانية فقام رجلان من أولياء السهمي فحلفا، وفي الترمذي فقام عمرو بن العاص ورجل آخر منهم فحلفا وكان أقرب إليه، وفي رواية فمرض فأوصى إليهما وأمرهما أن يبلغا ما ترك أهله فلما مات أخذا الجام ودفعا إلى أهله ما بقي.'),
(777, 5, 108, '«ذلك» الحكم المذكور من رد اليمين على الورثة «أدنى» أقرب إلى «أن يأتوا» أي الشهود أو الأوصياء «بالشهادة على وجهها» الذي تحملوها عليه من غير تحريف ولا خيانة «أو» أقرب إلى أن «يخافوا أن تُرد أيمان بعد أيْمانهم» على الورثة المدعين فيحلفون على خيانتهم وكذبهم فيفتضحون ويغرمون فلا يكذبوا «واتقوا الله» بترك الخيانة والكذب «واسمعوا» ما تؤمرون به سماع قبول «والله لا يهدي القوم الفاسقين» الخارجين عن طاعته إلى سبيل الخير.'),
(778, 5, 109, 'اذكر «يوم يجمع الله الرسل» هو يوم القيامة «فيقول» لهم توبيخا لقومهم «ماذا» أي الذي «أُجبتم» به حين دعوتم إلى التوحيد «قالوا لا علم لنا» بذلك «إنك أنت علام الغيوب» ما غاب عن العباد وذهب عنهم علمه لشدة هول يوم القيامة وفزعهم ثم يشهدون على أممهم لما يسكنون.'),
(779, 5, 110, 'اذكر «إذ قال الله يا عيسى ابن مريم اذكر نعمتي عليك وعلى والدتك» بشكرها «إذ أيَّدتك» قويتك «بروح القدس» جبريل «تكلِّم الناس» حال من الكاف في أيدتك «في المهد» أي طفلا «وكهلا» يفيد نزوله قبل الساعة لأنه رفع قبل الكهولة كما سبق آل عمران «وإذ علَّمتك الكتاب والحكمة والتوراة والإنجيل وإذ تخلق من الطين كهيئة» كصورة «الطير» والكافُ اسم بمعنى مثل مفعول «بإذني فتنفخ فيها فتكون طيرا بإذني» بإرادتي «وتُبرئ الأكمه والأبرص بإذني وإذ تخرج الموت» من قبورهم أحياء «بإذني وإذ كففت بني إسرائيل عنك» حين هموا بقتلك «إذ جئتهم بالبيانات» المعجزات «فقال الذين كفروا منهم إن» ما «هذا» الذي جئت به «إلا سحر مبين» وفي قراءة ساحر أي عيسى.'),
(780, 5, 111, '«وإذ أوحيت إلى الحواريين» أمرتهم على لسانه «أن» أي بأن «آمنوا بي وبرسولي» عيسى «قالوا آمنا» بهما «واشهد بأننا مسلمون».'),
(781, 5, 112, 'اذكر «إذ قال الحواريون يا عيسى ابن مريم هل يستطيع» أي يفعل «ربك» وفي قراءة بالفوقانية ونصب ما بعده أي تقدر أن تسأله «أن ينزل علينا مائدة من السماء قال» لهم عيسى «اتقوا الله» في اقتراح الآيات «إن كنتم مؤمنين».'),
(782, 5, 113, '«قالوا نريد» سؤالها من أجل «أن نأكل منها وتطمئنَّ» تسكن «قلوبنا» بزيادة اليقين «ونعلم» نزداد علما «أن» مخففه أي أنك «قد صدقتنا» في ادعاء النبوة «ونكون عليها من الشاهدين».'),
(783, 5, 114, '«قال عيسى ابن مريم اللَّهم ربنا أنزل علينا مائدة من السماء تكون لنا» أي يوم نزولها «عيدا» نعظمه ونشرفه «لأوَّلنا» بدل من لنا بإعادة الجار «وآخرنا» ممن يأتي بعدنا «وآية منك» على قدرتك ونبوتي «وارزقنا» إياها «وأنت خير الرازقين».'),
(784, 5, 115, '«قال الله» مستجيب له «إني منزِّلها» بالتخفيف والتشديد «عليكم فمن يكفر بعد» أي بعد نزولها «منكم فإني أعذبه عذابا لا أعذبه أحدا من العالمين» فنزلت الملائكة بها من السماء عليها سبعة أرغفة وسبعة أحوات فأكلوا منها حتى شبعوا قاله ابن عباس وفي حديث أنزلت المائدة من السماء خبزا ولحما فأمروا أن لا يخونوا ولا يدَّخروا لغد فخافوا وادخروا فمسخوا قردة وخنازير.'),
(785, 5, 116, '«و» اذكر «إذ قال» أي يقول «الله» لعيسى في القيامة توبيخا لقومه «يا عيسى ابن مريم أأنت قلت للناس اتخذوني وَأُمِّي إلهين من دون الله قال» عيسى وقد أرعد «سبحانك» تنزيها لك عما لا يليق بك من الشريك وغيره «ما يكون» ما ينبغي «لي أن أقول ما ليس لي بحق» خبر ليس، ولي للتبيين «إن كنت قلته فقد علمته تعلم ما» أخفيه «في نفس ولا أعلم ما في نفسك» أي ما تخفيه من معلوماتك «إنك أنت علام الغيوب».'),
(786, 5, 117, '«ما قلتُ لهم إلا ما أمرتني به» وهو «أن اعبدوا الله ربي وربكم وكنتُ عليهم شهيدا» رقيبا أمنعهم مما يقولون «ما دمت فيهم فلما توفيتني» قبضتني بالرفع إلى السماء «كنتَ أنت الرقيب عليهم» الحفيظ لأعمالهم «وأنت على كل شيء» من قولي لهم وقولهم بعدي وغير ذلك «شهيد» مطلع عالم به.'),
(787, 5, 118, '«إن تعذبهم» أي من أقام على الكفر فيهم «فإنهم عبادك» وأنت مالكهم تتصرف فيهم كيف شئت لا اعتراض عليك «وإن تغفر لهم» أي لمن آمن منهم «فإنك أنت العزيز» الغالب على أمره «الحكيم» في صنعه.'),
(788, 5, 119, '«قال الله هذا» أي يوم القيامة «يوم ينفع الصادقين» في الدنيا كعيسى «صدقهم» لأنه يوم الجزاء «لهم جنات تجري من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم» بطاعته «ورضوا عنه» بثوابه «ذلك الفوز العظيم» ولا ينفع الكاذبين في الدنيا صدقهم فيه كالكفار لما يؤمنون عند رؤية العذاب.'),
(789, 5, 120, '«لله ملك السماوات والأرض» خزائن المطر والنبات والرزق وغيرها «وما فيهن» أتى بما تغليبا لغير العاقل «وهو على كل شيء قدير» ومنه إثابة الصادق وتعذيب الكاذب وخص العقلُ ذاته فليس عليها بقادر.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, '«الحمد» وهو الوصف الجميل ثابت «لله» وهل المراد الإعلام بذلك للإيمان به الثناء به أوهما احتمالات أفيدها الثالث قاله الشيخ في سورة الكهف «الذي خلق السماوات والأرض» خصهما بالذكر لأنهما أعظم المخلوقات للناظرين «وجعل» خلق «الظلمات والنور» أي كل ظلمة ونور وجمعها دونه لكثرة أسبابها، وهذا من دلائل وحدانيته «ثم الذين كفروا» مع قيام هذا الدليل «بربهم يعدلون» يسوون غيره في العبادة.'),
(791, 6, 2, '«هو الذي خلقكم من طين» بخلق أبيكم آدم منه «ثم قضى أجلا» لكم تموتون عند انتهائه «وأجلٌ مسمّىّ» مضروب «عنده» لبعثكم «ثم أنتم» أيها الكفار «تمترون» تشكون في البعث بعد علمكم أنه ابتدأ خلقكم ومن قدر على الإبتداء فهو على الإعادة أقدر.'),
(792, 6, 3, '«وهو الله» مستحق للعبادة «في السماوات وفي الأرض يعلم سركم وجهركم» ما تسرون وما تجهرون به بينكم «ويعلم ما تكسبون» تعملون من خير وشرِّ.'),
(793, 6, 4, '«وما تأتيهم» أي أهل مكة «من» زائدة «آية من آيات ربهم» من القرآن «إلا كانوا عنها معرضين».'),
(794, 6, 5, '«فقد كذٌبوا بالحق» بالقرآن «لما جاءهم فسوف يأتيهم أنباء» عواقب «ما كانوا به يستهزئون».'),
(795, 6, 6, '«ألم يروا» في أسفارهم إلى الشام وغيرها «كم» خبرية بمعنى كثيرا «أهلكنا من قبلهم من قرن» أمة من الأمم الماضية «مكَّناهم» أعطيناهم مكانا «في الأرض» بالقوة والسعة «ما لم نمكن» نعط «لكم» فيه التفات عن الغيبة «وأرسلنا السماء» المطر «عليهم مدرارا» متتابعا «وجعلنا الأنهار تجري من تحتهم» تحت مساكنهم «فأهلكناهم بذنوبهم» بتكذيبهم الأنبياء «وأنشأنا من بعدهم قرنا آخرين».'),
(796, 6, 7, '«ولو نزلَّنا عليك كتابا» مكتوبا «في قرطاس» رَق كما اقترحوا «فلمسوه بأيديهم» أبلغ من عاينوه لأنه أنفى للشك «لقال الذين كفروا إن» ما «هذا إلا سحر مبين» تعنُّنا وعنادا.'),
(797, 6, 8, '«وقالوا لولا» هلا «أنزل عليه» على محمد صلى الله عليه وسلم «ملك» يصدقه «ولو أنزلنا ملكا» كما اقترحوا فلم يؤمنوا «لقضي الأمر» بهلاكهم «ثم لا ينظرون» يمهلون لتوبة أو معذرة كعادة الله فيمن قبلهم من إهلاكهم عند وجود مقترحهم إذا لم يؤمنوا.'),
(798, 6, 9, '«ولو جعلناه» أي المنزل إليهم «ملكا لجعلناه» أي الملك «رجلا» أي على صورته ليتمكنوا من رؤيته إذ لا قوَّه للبشر على رؤية الملك «و» لو أنزلناه وجعلنا رجلا «للبسنا» شبهنا «عليهم ما يلبسون» على أنفسهم بأن يقولوا ما هذا إلا بشر مثلكم.'),
(799, 6, 10, '«ولقد استُهزئ برسل من قبلك» فيه تسلية للنبي صلى الله عليه وسلم «فحاق» نزل «بالذين سخروا منهم ما كانوا به يستهزئون» وهو العذاب فكذا يحيق بمن استهزأ بك.'),
(800, 6, 11, '«قل» لهم «سيروا في الأرض ثم انظروا كيف كان عاقبة المكذبين» الرسل من هلاكهم بالعذاب ليعتبروا.'),
(801, 6, 12, '«قل لمن ما في السماوات والأرض قل لله» إن لم يقولوه لا جواب غيره «كتب على نفسه» قضى على نفسه «الرحمة» فضلا منه وفيه تلطف في دعائهم إلى الإيمان «لَيجمعنكم إلى يوم القيامة» ليجازيكم بأعمالكم «لا ريب» شك «فيه الذين خسروا أنفسهم» بتعريضها للعذاب مبتدأ خبره «فهم لا يؤمنون».'),
(802, 6, 13, '«وله» تعالى «ما سكن» حلُ «في الليل والنهار» أي كل شيء فهو ربه وخالقه ومالكه «وهو السميع» لما يقال «العليم» بما يفعل.'),
(803, 6, 14, '«قل» لهم «أغير الله أتَّخذ وليّاً» أعبده «فاطر السماوات والأرض» مبدعهما «وهو يُطعم» يرزق «ولا يُطعم» يُرزق «قل إني أمرت أن أكون أول من أسلم» لله من هذه الأمة «و» قيل لي «لا تكوننَّ من المشركين» به.'),
(804, 6, 15, '«قل إني أخاف إن عصيت ربي» بعبادة غيره «عذاب يوم عظيم» هو يوم القيامة.'),
(805, 6, 16, '«من يُصرف» بالبناء للمفعول أي العذاب وللفاعل أي الله والعائد محذوف «عنه يومئذ فقد رحمه» تعالى أي أراد له الخير «وذلك الفوز المبين» النجُاة الظاهرة.'),
(806, 6, 17, '«وإن يمسسك الله بضر» بلاء كمرض وفقر «فلا كاشف» رافع «له إلا هو وإن يمسسك بخير» كصحة وغنىّ «فهو على كل شىء قدير» ومنه مسُّك به ولا يقدر على ردِّه عنك غيره.'),
(807, 6, 18, '«وهو القاهر» القادر الذي لا يعجزه شيء مستعليا «فوق عباده وهو الحكيم» في خلقه «الخبير» ببواطنهم كظواهرهم.'),
(808, 6, 19, 'ونزل لما قالوا للنبي صلى الله عليه وسلم إئتنا بمن يشهد لك بالنبوة فإن أهل الكتاب أنكروك «قل» لهم «أيُّ شيءٍ أكبر شهادة» تمييز محول عن المبتدأ «قل اللهُ» إن لم يقولوه لا جواب غيره، وهو «شهيد بيني وبينكم» على صدقي «وأوحي إليَّ هذا القرآن لأنذركم» أخوفكم يا أهل مكة «به ومن بلغ» عطف على ضمير أنذركم أي بلغة القرآن عن الأنس والجن «أئنَّكم لتشهدون أن مع الله آلهة أخرى» إستفهام إنكاري «قل» لهم «لا أشِهدُ» بذلك «قل إنما هو إله واحد وإنني بريء مما تشركون» معه من الأصنام.'),
(809, 6, 20, '«الذين آتيناهم الكتاب يعرفونه» أي محمدا بنعته في كتابهم «كما يعرفون أبناءهم الذين خسروا أنفسهم» منهم «فهم لا يؤمنون» به.'),
(810, 6, 21, '«ومن» أي لا أحد «أظلم ممن افترى على الله كذبا» بنسبه الشريك إليه «أو كذَّب بآياته» القرآن «إنه» أي الشأن «لا يفلح الظالمون» بذلك.'),
(811, 6, 22, '«و» اذكر «يوم نحشرهم جميعا ثم نقول للذين أشركوا» توبيخا «أين شركاؤكم الذين كنتم تزعمون» أنهم شركاء الله.'),
(812, 6, 23, '«ثم لم تكن» بالتاء والياء «فتنتّهم» بالنصب والرفع أي معذرتهم «إلا أن قالوا» أي قولهم «والله ربِّنا» بالجر نعت والنصب نداء «ما كنا مشركين».'),
(813, 6, 24, 'قال تعال: «أنظر» يا محمد «كيف كذبوا على أنفسهم» بنفي الشرك عنهم «وضلَّ» غاب «عنهم ما كانوا يفترونـ» ـه على الله من الشركاء.'),
(814, 6, 25, '«ومنهم من يستمع إليك» إذا قرأت «وجعلنا على قلوبهم أكنة» أغطية لـ «أن» لا «يفقهوه» يفهموا القرآن «وفي آذانهم وقرا» صما فلا يسمعونه سماع قبول «وإن يروا كل آية لا يؤمنوا بها حتى إذا جاءُوك يجادلونك يقول الذين كفروا إن» ما «هذا» القرآن «إلا أساطير» أكاذيب «الأولين» كالأضاحيك والأعاجيب جمع أسطورة بالضم.'),
(815, 6, 26, '«وهم ينهون» الناس «عنه» عن اتباع النبي صلى الله عليه وسلم «وينأون» يتباعدون «عنه» فلا يؤمنون به، وقيل: نزلت في أبي طالب كان ينهى عن أذاه ولا يؤمن به «وإن» ما «يهلكون» بالنأي عنه «إلا أنفسهم» لأن ضرره عليهم «وما يشعرون» بذلك.'),
(816, 6, 27, '«ولو ترى» يا محمد «إذ وُقفوا» عرضوا «على النار فقالوا يا» للتنبيه «ليتنا نردُّ» إلى الدنيا «ولا نكذِّبُ بآيات ربنا ونكونُ من المؤمنين» برفع الفعلين إستئنافا ونصبهما في جواب التمني ورفع الأول ونصب الثاني وجواب لو رأيت أمرا عظيما.'),
(817, 6, 28, 'قال تعالى: «بل» للإضراب عن إرادة الإيمان المفهوم من التمني «بدا» ظهر «لهم ما كانوا يخفون من قبل» يكتمون بقولهم (والله ربنا ما كنا مشركين) شهادة جوارحهم فتمنوا ذلك «ولو ردوا» إلى الدنيا فرضا «لعادوا لما نُهوا عنه» من الشرك «وإنهم لكاذبون» في وعدهم بالإيمان.'),
(818, 6, 29, '«وقالوا» أي منكر والبعث «إن» ما «هي» أي الحياة «إلا حياتنا الدنيا ومانحن بمبعوثين».'),
(819, 6, 30, '«ولو ترى إذ وقفوا» عرضوا «على ربِّهم» لرأيت أمرا عظيما «قال» لهم على لسان الملائكة توبيخا «أليس هذا» البعث والحساب «بالحق قالوا بلى وربِّنا» إنه لحق «قال فذوقوا العذاب بما كنتم تكفرون» به في الدنيا.'),
(820, 6, 31, '«قد خسر الذين كذَّبوا بلقاء الله» بالبعث «حتى» غاية للتكذيب «إذا جاءتهم الساعة» القيامة «بغتة» فجأة «قالوا يا حسرتنا» هي شدة التألم ونداؤها مجاز أي هذا أوانك فاحضري «على ما فرَّطنا» قصَّرنا «فيها» أي الدنيا «وهم يحملون أوزارهم على ظهورهم» بأن تأتيهم عند البعث في أقبح شيء صورة وأنتنه ريحا فتركبهم «ألا ساء» بئس «ما يزرون» يحملونه حملهم ذلك.'),
(821, 6, 32, '«وما الحياة الدنيا» أي الاشتغال بها «إلا لعب ولهو» وأما الطاعة وما يعين عليها فمن أمور الآخرة «وللدَّار الاخرةُ» وفي قراءة ولدار الآخرة أي الجنة «خير للذين يتقون» الشرك «أفلا يعقلون» بالياء والتاء ذلك فيؤمنون.'),
(822, 6, 33, '«قد» للتحقيق «نعلم إنه» أي الشأن «ليحزنك الذي يقولون» لك من التكذيب «فإنهم لا يكذِّبونك» في السر لعلمهم أنك صادق وفي قراءة بالتخفيف أي لا ينسبونك إلى الكذب «ولكن الظالمين» وضعه موضع المضمر «يآيات الله» القرآن «يجحدون» يكذبون.'),
(823, 6, 34, '«ولقد كذِّبت رسل من قبلك» فيه تسلية للنبي صلى الله عليه وسلم «فصبروا على ما كذِّبوا وأوذوا حتى أتاهم نصرنا» بإهلاك قومهم فاصبر حتى يأتيك النصر بإهلاك قومك «ولا مبدِّل لكلمات الله» مواعيده «ولقد جاءك من نبأ المرسلين» ما يسكن به قلبك.'),
(824, 6, 35, '«وإن كان كبر» عظم «عليك إعراضهم» عن الإسلام لحرصك عليهم «فإن استطعت أن تبتغى نفقا» سربا «في الأرض أو سلَّما» مصعدا «في السماء فتأتيهم بآية» مما اقترحوا فافعل، المعنى أنك لا تستطيع ذلك فاصبر حتى يحكم الله، «ولو شاء الله» هدايتهم «لجمعهم على الهدى» ولكن لو لم يشأ ذلك فلم يؤمنوا «فلا تكوننَّ من الجاهلين» بذلك.'),
(825, 6, 36, '«إنَّما يستجيب» دعاءك إلى الإيمان «الذين يسمعون» سمع تفهُّم واعتبار «و الموتى» أي الكفار شبههم بهم في عدم السماع «يبعثهم الله» في الآخرة «ثم إليه يرجعون» يُردون فيجازيهم بأعمالهم.'),
(826, 6, 37, '«وقالوا» أي كفار مكة «لولا» هلا «نزَّل عليه آية من ربه» كالناقة والعصا والمائدة «قل» لهم «إن الله قادر على أن ينزِّل» بالتشديد والتخفيف «آية» مما اقترحوا «ولكن أكثرهم لا يعلمون» أن نزولها بلاء عليهم لوجوب هلاكهم إن جحدوها.'),
(827, 6, 38, '«وما من» زائدة «دابة» تمشي «في الأرض ولا طائر يطير» في الهواء «بجناحيه إلا أمم أمثالكم» في تدبير خلقها ورزقها وأحوالها «ما فرطنا» تركنا «في الكتاب» اللوح المحفوظ «من» زائدة «شىء» فلم نكتبه «ثم إلى ربهم يحشرون» بينهم ويقتص للجماء من القرناه ثم يقول لهم كونوا ترابا.'),
(828, 6, 39, '«والذين كذبوا بآياتنا» القرآن «صم» عن سماعه سماع قبول «وبكم» عن النطق بالحق «في الظلمات» الكفر «من يشأ الله» إضلاله «يضلله ومن يشأ» هدايته «يجعله على صراط» طريق «مستقيم» دين الإسلام.'),
(829, 6, 40, '«قل» يا محمد لأهل مكة «أرأيتكم» أخبروني «أن أتاكم عذاب الله» في الدنيا «أو أتتكم الساعة» القيامة المشتملة عليه بغتة «أغير الله تدعون» لا «إن كنتم صادقين» في أن الأصنام تنفعكم فادعوها.'),
(830, 6, 41, '«بل إياه» لا غيره «تدعون» في الشدائد «فيكشف ما تدعون إليه» أن يكشفه عنكم من الضر ونحوه «إن شاء» كشفه «وتنسَوْن» تتركون «ما تشركون» معه من الأصنام فلا تدعونه.'),
(831, 6, 42, '«ولقد أرسلنا إلى أمم من» زائدة «قبلك» رسلا فكذبوهم «فأخذناهم بالبأساء» شدة الفقر «والضراء» المرض «لعلهم يتضرعون» يتذللون فيؤمنون.'),
(832, 6, 43, '«فلولا» فهلا «إذ جاءهم بأسنا» عذابنا «تضرَّعوا» أي لم يفعلوا ذلك مع قيام المقتضي له «ولكن قست قلوبهم» فلم تلن للإيمان «وزيَّن لهم الشيطان ما كانوا يعملون» من المعاصي فأصرُّوا عليها.'),
(833, 6, 44, '«فلما نسوا» تركوا «ما ذكروا» وُعظوا وخوفوا «به» من البأساء والضراء فلم يتعظوا «فتحنا» بالتخفيف والتشديد «عليهم أبواب كلِّ شيء» من النعم استدراجا لهم «حتى إذا فرحوا بما أوتوا» فرح بطر «أخذناهم» بالعذاب «بغتة» فجأة «فإذا هم مبلسون» آيسون من كل خير.'),
(834, 6, 45, '«فقطع دابر القوم الذين ظلموا» أي آخرهم بأن استؤصلوا «والحمد لله رب العالمين» على نصر الرسل وإهلاك الكافرين.'),
(835, 6, 46, '«قل» لأهل مكة «أرأيتم» أخبروني «إن أخذ الله سمعكم» أصمَّكم «وأبصاركم» أعماكم «وختم» طبع «على قلوبكم» فلا تعرفون شيئا «من إله غير الله يأتيكم به» بما أخذه منكم بزعمكم «أنظر كيف نصرف» نبين «الآيات» الدلالات على وحدانيتنا «ثم هم يصدفون» يعرِضون عنها فلا يؤمنون.'),
(836, 6, 47, '«قل» لهم «أرأيتكم إن أتاكم عذاب الله بغتة أو جهرة» ليلا أو نهارا «هل يُهلك إلا القوم الظالمون» الكافرون أي ما يهلك إلا هم.'),
(837, 6, 48, '«وما نرسل المرسلين إلا مبشِّرين» من آمن بالجنة «ومنذرين» من كفر بالنار «فمن آمن» بهم «وأصلح» عمله «فلا خوف عليهم ولا هم يحزنون» في الآخرة.'),
(838, 6, 49, '«والذين كذبوا بآياتنا يمسهم العذاب بما كانوا يفسقون» يخرجون عن الطاعة.'),
(839, 6, 50, '«قل» لهم «لا أقول لكم عندي خزائن الله» التي منها يرزق «ولا» إني «أعلم الغيب» ما غاب عني ولم يوح إلي «ولا أقول لكم إني ملك» من الملائكة «إن» ما «أتبع إلا ما يوحى إليَّ قل هل يستوي الأعمى» الكافر «والبصير» المؤمن؟ لا «أفلا تتفكرون» في ذلك فتؤمنون.'),
(840, 6, 51, '«وأنذر» خوِّف «به» أي القرآن «الذين يخافون أن يُحشروا إلى ربِّهم ليس لهم من دونه» أي غيره «ولي» ينصرهم «ولا شفيع» يشفع لهم وجملة النفي حال من ضمير يحشروا وهي محل الخوف والمراد بهم العاصون «لعلهم يتقون» الله بإقلاعهم عما هم فيه وعمل الطاعات.'),
(841, 6, 52, '«ولا تطرد الذين يدعون ربَّهم بالغداة والعشي يريدون» بعبادتهم «وجهه» تعالى لا شيئا من أعراض الدنيا وهم الفقراء، وكان المشركون طعنوا فيهم وطلبوا أن يطردهم ليجالسوه وأراد النبي صلي الله عليه وسلم ذلك طمعا في إسلامهم «ما عليك من حسابهم من» زائدة «شيء» إن كان باطنهم غير مرضي «وما من حسابك عليهم من شيء فتطردهم» جواب النفي «فتكون من الظالمين» إن فعلت ذلك.'),
(842, 6, 53, '«وكذلك فَتنَا» ابتلينا «بعضهم ببعض» أي الشريف بالوضيع والغني بالفقير بأن قدَّمناه بالسبق إلى الإيمان «ليقولوا» أي الشرفاء والأغنياء منكرين «أهؤلاء» الفقراء «منَّ الله عليهم من بيننا» بالهداية أي لو كان ما هم عليه هدى ما سبقونا إليه قال تعالى: «أليس الله بأعلم بالشاكرين» له فيهديهم: بلى.'),
(843, 6, 54, '«وإذا جاءك الذين يؤمنون بآياتنا فقل» لهم «سلام عليكم كتب» قضى «ربكم على نفسه الرحمة إنهُ» أي الشأن وفي قراءة بالفتح بدل من الرحمة «من عمل منكم سوءا بجهالة» منه حيث ارتكبه «ثم تاب» رجع «من بعده» بعد عمله عنه «وأصلح» عمله «فإنه» أي الله «غفور» له «رحيم» به، وفي قراءة بالفتح أي فالمغفرة له.'),
(844, 6, 55, '«وكذلك» كما بينا ما ذكر «نفصِّل» نبين «الآيات» القرآن ليظهر الحق فيعمل به «ولتستبين» تظهر «سبيلُ» طريق «المجرمين» فتجتنب، وفي قراءة بالتحتانية، وفي أخرى بالفوقانية ونصب سبيل خطاب للنبي صلى الله عليه وسلم'),
(845, 6, 56, '«قل إني نُهيت أن أعبد الذين تدعون» تعبدون «من دون الله قل لا أتبع أهواءكم» في عبادتها «قد ضللت إذا» إن اتبعتها «وما أنا من المهتدين».'),
(846, 6, 57, '«قل إني على بيِّنة» بيان «من ربي و» قد «كذَّبتم به» بربي حيث أشركتم «ما عندي ما تستعجلون به» من العذاب «إن» ما «الحكم» في ذلك وغيره «إلا لله يقضي» القضاء «الحق وهو خير الفاصلين» الحاكمين، وفي قراءة يقصُّ أي يقول.'),
(847, 6, 58, '«قل» لهم «لو أن عندي ما تستعجلون به لقُضي الأمر بيني وبينكم» بأن أعجله لكم وأستريح ولكنه عند الله «والله أعلم بالظالمين» متى يعاقبهم.'),
(848, 6, 59, '(وعنده) تعالى (مفاتح الغيب) خزائنه أو الطرق الموصلة إلى عمله (لا يعلمها إلا هو) وهي الخمسة التي في قوله (إن الله عنده علم الساعة) الآية كما رواه البخاري (ويعلم ما) يحدث (في البر) القفار (والبحر) القرى التي على الأنهار (وما تسقط من) زائدة (ورقة إلا يعلمها ولا حبة في ظلمات الأرض ولا رطب ولا يابس) عطف على ورقة (إلا في كتاب مبين) هو اللوح المحفوظ، والاستثناء بدل اشتمال من الاستثناء قبله.'),
(849, 6, 60, '«وهو الذي يتوفاكم بالليل» يقبض أرواحكم عند النوم «ويعلم ما جرحتم» كسبتم «بالنهار ثم يبعثكم فيه» أي النهار برد أرواحكم «ليُقضى أجل مسمى» هو أجل الحياة «ثم إليه مرجعكم» «ثم ينبئكم بما كنتم تعملون» فيجازيكم به.'),
(850, 6, 61, '«وهو القاهر» مستعليا «فوق عباده ويرسل عليكم حفظة» ملائكة تحصي أعمالكم «حتى إذا جاء أحدكم الموت توفَّته» وفي قراءة توفاه «رسلنا» الملائكة الموكلون بقبض الأرواح «وهم لا يفرَّطون» يقصرون فيما يؤمرون به.'),
(851, 6, 62, '«ثم ردُّوا» أي الخلق «إلى الله مولاهم» مالكهم «الحق» الثابت العدل ليجزيهم «ألا له الحكم» القضاء النافذ فيهم «وهو أسرع الحاسبين» يحاسب الخلق كلهم في قدر نصف نهار من أيام الدنيا لحديث بذلك.'),
(852, 6, 63, '«قل» يا محمد لأهل مكة «من ينجيِكم من ظلمات البر والبحر» أهوالهما في أسفاركم حين «تدعونه تضرعا» علانية «وخفية» سرا تقولون «لئن» لام قسم «أنجيتنا» وفي قراءة أنجانا أي الله «من هذه» الظلمات والشدائد «لنكونن من الشاكرين» المؤمنين.'),
(853, 6, 64, '«قل» لهم «الله يُنجيكم» بالتخفيف والتشديد «منها ومن كل كرب» غمِّ سواها «ثم أنتم تشركون» به.'),
(854, 6, 65, '(قل هو القادر) على أن يبعث عليكم عذابا من فوقكم) من السماء كالحجارة والصيحة (أو من تحت أرجلكم) كالخسف (أو يلبسكم) يخلطكم (شيعا) فرقا مختلفة الأهواء (ويذيق بعضكم بأس بعض) بالقتال، قال صلى الله عليه وسلم: لما نزلت (هذا أهون وأيسر) ولما نزل ما قبله:، (أعوذ بوجهك رواه البخاري وروى مسلم حديث "" سألت ربي ألا يجعل بأس أمتي بينهم فمنعنيها "" وفي حديث "" لما نزلت قال أما إنها كائنة ولم يأت تأويلها بعد "" (أنظر كيف نصرف) نبين لهم (الآيات) الدلالات على قدرتنا (لعلهم يفقهون) يعلمون أن ما هم عليه باطل.'),
(855, 6, 66, '«وكذب به» بالقرآن «قومك وهو الحق» الصدق «قل» لهم «لست عليكم بوكيل» فأجازيكم إنما أنا منذر وأمركم إلى الله وهذا قبل الأمر بالقتال.'),
(856, 6, 67, '«لكل نبأ» خبر «مستقر» وقت يقع فيه ويستقر ومنه عذابكم «وسوف تعلمون» تهديد لهم.'),
(857, 6, 68, '«وإذا رأيت الذين يخوضون في آياتنا» القرآن بالاستهزاء «فأعرض عنهم» ولا تجالسهم «حتى يخوضوا في حديث غيره وإما» فيه إدغام نون إن الشرطية في ما المزيدة «يُنسِيَنَّكَ» بسكون النون والتخفيف وفتحها والتشديد «الشيطان» فقعدت معهم «فلا تقعد بعد الذكرى» أي تذكرة «مع القوم الظالمين» فيه وضع الظاهر موضع المضمر وقال المسلمون إن قمنا كلما خاضوا لم نستطيع أن نجلس في المسجد وأن نطوف فنزل.'),
(858, 6, 69, '«وما على الذين يتقون» الله «من حسابهم» أي الخائضين «من» زائدة «شيء» إذا جالسوهم «ولكن عليهم «ذكرى» تذكرة لهم وموعظة «لعلهم يتقون» الخوض.'),
(859, 6, 70, '«وذر» أترك «الذين اتخذوا دينهم» الذي كلفوه «لعبا ولهوا» باستهزائهم به «وغرتهم الحياة الدنيا» فلا تتعرض لهم وهذا قبل الأمر بالقتال «وذكِّر» عظ «به» بالقرآن الناس لـ «أن» لا «تُبسل نفس» تسلم إلى الهلاك «بما كسبت» عملت «ليس لها من دون الله» أي غيره «ولي» ناصر «ولا شفيع» يمنع عنها العذاب «وإن تعدل كل عدل» تفد كل فداء «لا يؤخذ منها» ما تفدي به «أولئك الذين أبسلوا بما كسبوا لهم شراب من حميم» ماء بالغ نهاية الحرارة «وعذاب أليم» مؤلم «بما كانوا يكفرون» بكفرهم.'),
(860, 6, 71, '«قل أندعوا» أنعبد «من دون الله ما لا ينفعنا» بعبادته «ولا يضرنا» بتركها وهو الأصنام «ونُرد على أعقابنا» نرجع مشركين «بعد إذ هدانا الله» إلى الإسلام «كالذي استهوته» أضلته «الشياطين في الأرض حيران» متحيرا لا يدري أين يذهب حال من الهاء «له أصحاب» رفقة «يدعونه إلى الهدى» أي ليهدوه الطريق يقولون له «ائتنا» فلا يجيبهم فيهلك والاستفهام للإنكار وجمله التشبيه حال من ضمير نرد «قل إن هدى الله» الذي هو الإسلام «هو الهدى» وما عداه ضلال «وأمرنا لنسلم» أي بأن نسلم «لرب العالمين».'),
(861, 6, 72, '«وأن» أي بأن «أقيموا الصلاة واتقوه» تعالى «وهو الذي إليه تحشرون» تجمعون يوم القيامة للحساب.'),
(862, 6, 73, '«وهو الذي خلق السماوات والأرض بالحق» أي محقا «و» اذكر «يوم يقول» للشيء «كن فيكون» هو يوم القيامة يقول للخلق قوموا «قولُه الحق» الصدق الواقع لا محالة «وله الملك يوم ينفخ في الصور» القرن النفخة الثانية من إسرافيل لا ملك فيه لغيره (لمن الملك اليوم؟ لله) «عالُم الغيب والشهادة» ما غاب وما شوهد «وهو الحكيم» في خلقه «الخبير» بباطن الأشياء كظاهرها.'),
(863, 6, 74, '«و» اذكر «إذ قال إبراهيم لأبيه آزرَ» هو لقبه واسمه تارخ «أتتخذ أصناما آلهة» تعبدها إستفهام توبيخ «إني أراك وقومك» باتخاذها «في ضلال» عن الحق «مبين» بيِّن.'),
(864, 6, 75, '«وكذلك» كما أريناه إضلال أبيه وقومه «نري إبراهيم ملكوت» ملك «السماوات والأرض» ليستدل به على وحدانيتنا «وليكون من الموقنين» بها وجملة وكذلك وما بعدها اعتراض وعطف على قال.'),
(865, 6, 76, '«فلما جن» أظلم «عليه الليل رأى كوكبا» قيل هو الزهرة «قال» لقومه وكانوا نجامين «هذا ربي» في زعمكم «فما أفل» غاب «قال لا أحب الآفلين» أن أتخذهم أربابا لأن الرب لا يجوز عليه التغير والانتقال لأنهما من شأن الحوادث فلم ينجع فيهم ذلك.'),
(866, 6, 77, '«فما رأى القمر بازغا» طالعا «قال» لهم «هذا ربي فلما أفل قال لئن لم يهدني ربي» يثبتني على الهدى «لأكوننَّ من القوم الضَّالين» تعريض لقومه بأنهم على ضلال فلم ينجع فيهم ذلك.'),
(867, 6, 78, '«فلما رأى الشمس بازغة قال هذا» ذكره لتذكير خبره «ربي هذا أكبر» من الكواكب والقمر «فلما أفلت» وقويت عليهم الحجة ولم يرجعوا «قال يا قوم إني بريء مما تشركون» بالله من الأصنام والأجرام المحدثة المحتاجة إلى محدث فقالوا له ما تبعد؟.'),
(868, 6, 79, 'قال «إني وجهت وجهي» قصدت بعبادتي «للذي فطر» خلق «السماوات والأرض» أي الله «حنيفا» مائلا إلى الدين القيم «وما أنا من المشركين» به.'),
(869, 6, 80, '«وحاجَّه قومه» جادلوه في دينه وهدَّدوه بالأصنام أن تصيبه بسوء إن تركها «قال أتُحَآجُّونِّي» بتشديد النون وتخفيفها بحذف إحدى النونين وهي نون الرفع عند النحاة ونون الوقاية عند القراء أتجادلونني «في» وحدانية «الله وقد هدان» تعالى إليها «ولا أخاف ما تشركونـ» ـه «به» من الأصنام أن تصيبني بسوء لعدم قدرتها على شيء «إلا» لكن «أن يشاء ربي شيئا» من المكروه يصيبني فيكون «وسع ربي كل شيء علما» أي وسع علمه كل شيء «أفلا تتذكرون» هذا فتؤمنون.'),
(870, 6, 81, '«وكيف أخاف ما أشركتم» بالله وهي لا تضر ولا تنفع «ولا تخافونَ» أنتم من الله «أنكم أشركتم بالله» في العبادة «ما لم ينزِّل به» بعبادته «عليكم سلطانا» حجة وبرهانا وهو القادر على كل شيء «فأي الفريقيْن أحق بالأمن» أنحن أم أنتم «إن كنتم تعلمون» مَن الحق به: أي وهو نحن فاتبعوه، قال تعالى.'),
(871, 6, 82, '«الذين آمنوا ولم يلبسوا» يخلطوا «إيمانهم بظلم» أي شرك كما فسر بذلك في حديث الصحيحين «أولئك لهم الأمن» من العذاب «وهم مهتدون».'),
(872, 6, 83, '«وتلك» مبتدأ ويبدل منه «حجتنا» التي احتج بها إبراهيم على وحدانية الله من أفول الكوكب وما بعده والخبر «آتيناها إبراهيم» أرشدناه لها حجة «على قومه نرفع درجات من نشاء» بالإضافة والتنوين في العلم والحكمة «إن ربك حكيم» في صنعه «عليم» بخلقه.'),
(873, 6, 84, '«وهبنا له إسحاق ويعقوب» ابنه «كلا» منهما «هدينا ونوحا هدينا من قبل» أي قبل إبراهيم «ومن ذريته» أي نوح «داوود وسليمان» ابنه «وأيوب ويوسف» ابن يعقوب «وموسى وهارون وكذلك» كما جزيناهم «نجزي المحسنين».'),
(874, 6, 85, '«وزكريا ويحيى» ابنه «وعيسى» ابن مريم يفيد أن الذرية تتناول أولاد البنت «وإلياس» ابن أخي هارون أخي موسى «كل» منهم «من الصالحين».'),
(875, 6, 86, '«وإسماعيل» بن إبراهيم «واليسع» اللام زائدة «ويونس ولوطا» بن هاران أخي إبراهيم «وكلا» منهم «فضَّلنا على العالمين» بالنبوة.'),
(876, 6, 87, '«ومن آبائهم وذرِّيَّاتهم وإخوانهم» عطف على كلا أو نوحا ومن للتبعيض لأن بعضهم لم يكن له ولد وبعضهم كان في ولده كافر «واجتبيناهم» إخترناهم «وهديناهم إلى صراط مستقيم».'),
(877, 6, 88, '«ذلك» الذين هُدوا إليه «هدى الله يهدي به من يشاء من عباده ولو أشركوا» فرضا «لحبط عنهم ما كانوا يعلمون».'),
(878, 6, 89, '«أولئك الذين آتيناهم الكتاب» بمعنى الكتب «والحكم» الحكمة «والنبوة فإن يكفر بها» أي بهذه الثلاثة «هؤلاء» أي أهل مكة «فقد وكَّلنا بها» أرصدنا لها «قوما ليسوا بها بكافرين» هم المهاجرون والأنصار.'),
(879, 6, 90, '«أولئك الذين هدى» هم «الله فبهداهم» طريقهم من التوحيد والصبر «اقتده» بهاء السكت وقفا ووصلا وفي قراءة بحذفها وصلا «قل» لأهل مكة «لا أسألكم عليه» أي القرآن «أجرا» تعطونيه «إن هو» ما القرآن «إلا ذكرى» عظة «للعالمين» الإنس والجن.'),
(880, 6, 91, '«وما قدروا» أي اليهود «الله حق قدره» أي ما عظموه حق عظمته أو ما عرفوه حق معرفته «إذ قالوا» للنبي صلى الله عليه وسلم وقد خاصموه في القرآن «ما أنزل الله على بشر من شيء قل» لهم «من أنزل الكتاب الذي جاء به موسى نورا وهدى للناس تجعلونه» بالياء والتاء في المواضع الثلاثة «قراطيس» أي يكتبونه في دفاتر مقطعة «يبدونها» أي ما يحبون إبداءه منها «ويخفون كثيرا» ما فيها كنعت محمد صلى الله عليه وسلم «وعلمتم» أيها اليهود في القرآن «ما لم تعلموا أنتم ولا آباؤكم» من التوراة ببيان ما التبس عليكم واختلفتم فيه «قل الله» أنزله إن لم يقولوه لا جواب غيره «ثم ذرهم في خوضهم» باطلهم «يلعبون».'),
(881, 6, 92, '«وهذا» القرآن «كتاب أنزلناه مبارك مصدق الذي بين يديه» قبله من الكتب «ولتنذر» بالتاء والياء عطف على معنى ما قبله أي أنزلناه للبركة والتصديق ولتنذر به «أم القرى ومن حولها» أي أهل مكة وسائر الناس «والذين يؤمنون بالآخرة يؤمنون به وهم على صلاتهم يحافظون» خوفا من عقابها.'),
(882, 6, 93, '«ومن» أي لا أحد «أظلم ممن افترى على الله كذبا» بادعاء النبوة ولم ينبأ «أو قال أُوحي إليَّ ولم يوح إليه شيء» نزلت في مسيلمة «ومن قال سأنزل مثل ما أنزل الله» وهم المستهزئون قالوا لو نشاء لقلنا مثل هذا «ولو ترى» يا محمد «إذ الظالمون» المذكورون «في غمرات» سكرات «الموت والملائكةُ باسطوا أيديهم» إليهم بالضرب والتعذيب يقولون لهم تعنيفا «أخرجوا أنفسكم» إلينا لنقبضها «اليوم تجزون عذاب الهون» الهوان «بما كنتم تقولون على الله غير الحق» يدعون النبوة والإيحاء كذب «وكنتم عن آياته تستكبرون» تتكبرون عن الإيمان بها وجواب لو رأيت أمرا فظيعا.'),
(883, 6, 94, '«و» يقال لهم إذا بعثوا «لقد جئتمونا فرادى» منفردين عن الأهل والمال والولد «كما خلقناكم أول مرة» أي حفاة عراة غرلا «وتركتم ما خولناكم» أعطيناكم من الأموال «وراء ظهوركم» في الدنيا بغير اختباركم «و» يقال لهم توبيخا «ما نرى معكم شفعاءكم» الأصنام «الذين زعمتم أنهم فيكم» أي في استحقاق عبادتكم «شركاء» لله «لقد تقطع بينكُمْ» وصلكم أي تشتيت جمعكم وفي قراءة بالنصب ظرف أي وصلكم بينكم «وضل» ذهب «عنكم ما كنتم تزعمون» في الدنيا من شفاعتها.'),
(884, 6, 95, '«إن الله فالق» شاق «الحبِّ» عن النبات «والنوى» عن النخل «يخرج الحي من الميت» كالإنسان والطائر من النطفة والبيضة «ومخرج الميت» النطفة والبيضة «من الحي ذلكم» الفالق المخرج «الله فأنَّي تؤفكون» فكيف تصرفون عن الإيمان مع قيام البرهان.'),
(885, 6, 96, '«فالق الإصباح» مصدر بمعنى الصبح أي شاق عمود الصبح وهو أول ما يبدو من نور النهار عن ظلمة الليل «وجاعلُ اللَّيْل سكنا» تسكن فيه الخلق من التعب «والشمس والقمر» بالنصب عطفا على محل الليل «حسبانا» للأوقات أو الباء محذوفة وهو حال من مقدر أي يجريان بحسبان كما في آية الرحمن «ذلك» المذكور «تقدير العزيز» في ملكه «العليم» بخلقه.'),
(886, 6, 97, '«وهو الذي جعل لكم النجوم لتهتدوا بها في ظلمات البر والبحر» في الأسفار «قد فصَّلنا» بينا «الآيات» الدلالات على قدرتنا «لقوم يعلمون» يتدبرون.'),
(887, 6, 98, '«وهو الذي أنشأكم» خلقكم «من نفس واحدة» من آدم «فّمُستقِرٌ» منكم في الرحم «ومستودع» منكم في الصلب وفي قراءة بفتح القاف أي مكان قرار لكم «قد فصَّلنا الآيات لقوم يفقهون» ما يقال لهم.'),
(888, 6, 99, '«وهو الذي أنزل من السماء ماء فأخرجنا» فيه التفات عن الغيبة «به» بالماء «نبات كل شيء» ينبت «فأخرجنا منه» أي النبات شيئا «خَضِرا» بمعنى أخضر «نخرج منه» من الخضر «حبا متراكبا» يركب بعضه بعضا كسنابل الحنطة ونحوها «ومن النخل» خبر ويبدل منه «من طلعها» أول ما يخرج منها والمبتدأ «قنوان» عراجين «دانية» قريب بعضها من بعض «و» أخرجنا به «جناتِ» بساتين «من أعناب والزيتون والرمان مشتبها» ورقهما حال «وغير متشابه» ثمرها «انظروا» يا مخاطبون نظر اعتبار «إلى ثمره» بفتح الثاء والميم وبضمهما وهو جمع ثمرة كشجرة وشجر وخشبة وخشب «إذا أثمر» أول ما يبدو كيف هو «و» إلى «ينعه» نضجه إذا أدرك كيف يعود «إن في ذلكم لآيات» دلالات على قدرته تعالى على البعث وغيره «لقوم يؤمنون» خصوا بالذكر لأنهم المنتفعون بها في الإيمان بخلاف الكافرين.'),
(889, 6, 100, '«وجعلوا لله» مفعول ثان «شركاءَ» مفعول أول ويبدل منه «الجنَّ» حيث أطاعوهم في عبادة الأوثان «و» قد «خلقهم» فكيف يكونون شركاء «وخرَقوا» بالتخفيف والتشديد أي اختلقوا «له بنين وبنات بغير علم» حيث قالوا عزيز ابن الله والملائكة بنات الله «سبحانه» تنزيها له «وتعالى عما يصفون» بأن له ولدا.'),
(890, 6, 101, 'هو «بديع السماوات والأرض» مبدعهما من غير مثال سبق «أنَّى» كيف «يكون له ولد ولم تكن له صاحبة» زوجة «وخلق كلَّ شيء» من شأنه أن يخلق «وهو بكل شيء عليمٌ».'),
(891, 6, 102, '«ذلكم الله ربكم لا إله إلا هو خالقُ كلَّ شيء فاعبدوه» وحِّدوه «وهو على كل شيء وكيل» حفيظ.'),
(892, 6, 103, '«لا تدركه الأبصار» أي لا تراه وهذا مخصوص لرؤية المؤمنين له في الآخرة لقوله تعالى: «وجوه يومئذ ناضرة إلى ربها ناظرة» وحديث الشيخين «إنكم سترون ربكم كما ترون القمر ليلة البدر» وقيل المراد لا تحيط به «وهو يدرك الأبصار» أي يراها ولا تراه ولا يجوز في غيره أن يدرك البصر وهو لا يدركه أويحيط به علمًا «وهو اللطيف» بأوليائه «الخبير» بهم.'),
(893, 6, 104, 'قل يا محمد لهم: «قد جاءكم بصائر» حجج «من ربكم فمن أبصر» ها فآمن «فلنفسه» أبصر لأن ثواب إبصاره له «ومن عَمي» عنها فضل «فعليها» وبال إضلاله «وما أنا عليكم بحفيظ» رقيب لأعمالكم إنما أنا نذير.'),
(894, 6, 105, '«وكذلك» كما بينا ما ذكر «نصرِّف» نبين «الآيات» ليعتبروا «وليقولوا» أي الكفار في عاقبة الأمر «دارست» ذاكرت أهل الكتاب وفي قراءة دَرَسْت أي كتب الماضين وجئت بهذا منها «ولنبيِّنه لقوم يعلمون».'),
(895, 6, 106, '«إتَّبع ما أوحي إليك من ربك» أي القرآن «لا إله إلا هو وأعرض عن المشركين».'),
(896, 6, 107, '«ولو شاء الله ما أشركوا وما جعلناك عليهم حفيظا» رقيبا فتجازيهم بأعمالهم «وما أنت عليهم بوكيل» فتجبرهم على الإيمان وهذا قبل الأمر بالقتال.'),
(897, 6, 108, '«ولا تسبوا الذين يدعونـ» ـهم «من دون الله» أي الأصنام «فيسبوا الله عدْوا» اعتداء وظلما «بغير علم» أي جهلا منهم بالله «كذلك» كما زيَّنا لهؤلاء ما هم عليه «زيَّنا لكل أمة عملهم» من الخير والشر فأتوه «ثم إلى ربهم مرجعهم» في الآخرة «فينبِّئهم بما كانوا يعلمون» فيجازيهم به.'),
(898, 6, 109, '«وأقسموا» أي كفار مكة «بالله جهد أيمانهم» أي غاية اجتهادهم فيها «لئن جاءتهم آية» مما اقترحوا «ليؤمنن بها قل» لهم «إنما الآيات عند الله» ينزلها كما يشاء وإنما أنا نذير «وما يشعركم» يدريكم بإيمانهم إذا جاءت: أي أنتم لا تدرون ذلك «إنهَّا إذا جاءت لا يؤمنون» لما سبق في علمي، وفي قراءة بالتاء خطابا للكفار وفي أخر بفتح أن بمعنى لعل أو معمولة لما قبلها.'),
(899, 6, 110, '«ونقلَّب أفئدتهم» نحول قلوبهم عن الحق فلا يفهمونه «وأبصارهم» عنه فلا يبصرونه فلا يؤمنون «كما لم يؤمنوا به» أي بما أنزل من الآيات «أوَّل مرّةِ ونذرهم» نتركهم «في طغيانهم» ضلالهم «يعمهون» يترددون متحيرين.'),
(900, 6, 111, '«ولو أننا نزَّلنا إليهم الملائكة وكملهم الموتى» كما اقترحوا «وحشرنا» جمعنا «عليهم كل شيء قبلا» بضمتين جمع قبيل أي فوجا فوجا وبكسر القاف وفتح الياء أي معاينة فشهدوا بصدقك «ما كانوا ليؤمنوا» لما سبق في علم الله «إلا» لكن «أن يشاء الله» إيمانهم فيؤمنوا «ولكن أكثرهم يجهلون» ذلك.'),
(901, 6, 112, '«وكذلك جعلنا لكل نبي عدوا» كما جعلنا هؤلاء أعداءك ويبدل منه «شياطين» مردة «الإنس والجن يوحي» يوسوس «بعضهم إلى بعض زخرف القول» مموهه من الباطل «غرورا» أي ليغروهم «ولو شاء ربُّك ما فعلوه» أي الإيحاء المذكور «فذرْهم» دع الكفار «وما يفترون» من الكفر وغيره مما زين لهم وهذا قبل الأمر بالقتال.'),
(902, 6, 113, '«ولتصغى إليه» عطف على غرورا أي الزخرف «أفئدة» قلوب «الذين لا يؤمنون بالآخرة وليرضوه وليقترفوا» يكتسبوا «ما هم مقترفون» من الذنوب فيعاقبوا عليه.'),
(903, 6, 114, 'ونزل لما طلبوا من النبي صلى الله عليه وسلم أن يجعل بينه وبينهم حكما، قل «أفغير الله أبتغي» أطلب «حكما» قاضيا بيني وبينكم «وهو الذي أنزل إليكم الكتاب» القرآن «مفصّلا» مبينا في الحق من الباطل «والذين آتيناهم الكتاب» التوراة كعبد الله بن سلام وأصحابه «يعلمون أنه منزَل» بالتخفيف والتشديد «من ربَّك بالحق فلا تكونن من الممترين» الشاكين فيه والمراد بذلك التقرير للكفار أنه حق.'),
(904, 6, 115, '«وتمت كَلِمَاتُ ربَك» بالأحكام والمواعيد «صدقا وعدلا» تمييز «لا مبدِّل لكلماته» بنقص أو خلف «وهو السميع» لما يقال «العليم» بما يفعل.'),
(905, 6, 116, '«وإن تطع أكثر من في الأرض» أي الكفار «يضلوك عن سبيل الله» دينه «إن» ما «يتبعون إلا الظنَّ» في مجادلتهم لك في أمر الميتة إذ قالوا ما قتل الله أحق أن تأكلوه مما قتلتم «وإن» ما «هو إلا يخرصون» يكذبون في ذلك.'),
(906, 6, 117, '«إن ربَّك هو أعلم» أي عالم «من يَضل عن سبيله وهو أعلم بالمهتدين» فيجازي كلا منهم.'),
(907, 6, 118, '«فكلوا مما ذكر اسم الله عليه» أي ذبح على اسمه «إن كنتم بآياته مؤمنين».'),
(908, 6, 119, '«وما لكم أ» ن «لا تأكلوا مما ذكر اسم الله عليه» من الذبائح «وقد فُصل» بالبناء للمفعول وللفاعل في الفعلين «لكم ما حُرِّمَ عليكم» في آية (حرمت عليكم الميتة) «إلا ما اضطُررتم إليه» منه فهو أيضا حلال لكم- المعنى لا مانع لكم من أكل ما ذكر وقد بين لكم المحرّم أكله، وهذا ليس منه - «وإن كثيرا لَيَضِلُّونَ» بفتح الياء وضمها «بأهوائهم» بما تهواه أنفسهم من تحليل الميتة وغيرها «بغير علم» يعتمدونه في ذلك «إن ربَّك هو أعلم بالمعتدين» المتجاوزين.'),
(909, 6, 120, '«وذروا» أُتركوا «ظاهر الإثم وباطنه» علانيته وسره والإثم قيل الزنا، وقيل كل معصية «إن الذين يكسبون الإثم سيُجزون» في الآخرة «بما كانوا يقترفون» يكتسبون.'),
(910, 6, 121, '«ولا تأكلوا مما لم يُذكر اسم الله عليه» بأن مات أو ذبح على اسم غيره وإلا فما ذبحه المسلم ولم يسم فيه عمدا أو نسيانا فهو حلال قاله ابن عباس وعليه الشافعي «وإنه» أي الأكل منه «لفسق» خروج عما يحل «وإن الشياطين ليوحون» يوسوسون «إلى أوليائهم» الكفار «ليجادلوكم» في تحليل الميتة «وإن أطعتموهم» فيه «إنكم لمشركون».'),
(911, 6, 122, 'ونزل في أبي جهل وغيره: «أو من كان ميتا» بالكفر «فأحييناه» بالهدى «وجعلنا له نورا يمشي به في الناس» يتبصر به الحق من غيره وهو الإيمان «كمن مثله» مثل زائدة أي كمن هو «في الظلمات ليس بخارج منها» وهو الكافر؟ لا «كذلك» كما زيِّن للمؤمنين الإيمان «زيِّن للكافرين ما كانوا يعلمون» من الكفر والمعاصى.'),
(912, 6, 123, '«وكذلك» كما جعلنا فُسَّاق مكة أكابرها «جعلنا في كل قرية أكابر مجرميها ليمكروا فيها» بالصد عن الإيمان «وما يمكرون إلا بأنفسهم» لأن وباله عليهم «وما يشعرون» بذلك.'),
(913, 6, 124, '«وإذا جاءتهم» أي أهل مكة «آية» على صدق النبي صلى الله عليه وسلم «قالوا لن نؤمن» به «حتى نؤتى مثل ما أوتي رسلُ الله» من الرسالة والوحي إلينا لأنا أكثر مالا وأكبر سنّا قال تعالى: «الله أعلم حيث يجعل رِسَالاَتِهِ» بالجمع والإفراد وحيث مفعول به لفعل دل عليه أعلم: أي يعلم الموضع الصالح لوضعها فيه فيضعها وهؤلاء ليسوا أهلا لها «سيصيب الذين أجرموا» بقولهم ذلك «صغار» ذلٌ «عند الله وعذاب شديد بما كانوا يمكرون» أي بسبب مكرهم.'),
(914, 6, 125, '«فمن يرد الله أن يهديه يشرح صدره للإسلام» بأن يقذف في قلبه نورا فينفسح له ويقبله كما ورد في حديث «ومن يردْ» الله «أن يضلَّه يجعل صدره ضَيْقا» بالتخفيف والتشديد عن قبوله «حرَجا» شديد الضيق بكسر الراء صفة وفتحها مصدر وصف فيه مبالغة «كأنما يصَّعَّد» وفي قراءة يصَّاعد وفيهما إدغام التاء في الأصل في الصاد وفي أخرى بسكونها «في السماء» إذا كلف الإيمان لشدته عليه «كذلك» الجعل «يجعل الله الرجس» العذاب أو الشيطان أي يسلطه «على الذين لا يؤمنون».'),
(915, 6, 126, '«وهذا» الذي أنت عليه يا محمد «صراطُ» طريق «ربِّك مستقيما» لا عوج فيه ونصبه على الحال المؤكد للجملة والعامل فيها معنى الإشارة «قد فصّلنا» بينا «الآيات لقوم يذكَّرون» فيه إدغام التاء في الأصل في الذال أي يتعظون وخُصوا بالذكر لأنهم المنتفعون.'),
(916, 6, 127, '«لهم دار السلام» أي السلام وهي الجنة «عند ربِّهم وهو وليهم بما كانوا يعلمون».'),
(917, 6, 128, '«و» اذكر «يوم نحشرهم» بالنون والياء أي الله الخلق «جميعا» ويقال لهم «يا معشر الجن قد استكثرتم من الإنس» بإغوائكم «وقال أولياؤهم» الذين أطاعوهم «من الإنس ربنا استمع بعضنا ببعض» انتفع الإنس بتزيين الجن لهم الشهوات والجن بطاعة الإنس لهم «وبلغنا أجلنا الذي أجَّلْتَ لنا» وهو يوم القيامة وهذا تحسر منهم قال تعالى لهم على لسان الملائكة: «النار مثواكم» مأواكم «خالدين فيها إلا ما شاء الله» من الأوقات التي يخرجون فيها لشرب الحميم فإنه خارجها كما قال تعالى (ثم إن مرجعهم لإلى الجحيم) وعن ابن عباس أنه فيمن علم الله أنهم يؤمنون فما بمعني من «إن ربك حكيم» في صنعه «عليم» بخلقه.'),
(918, 6, 129, '«وكذلك» كما متَّعنا عُصاة الإنس والجن بعضهم ببعض «نولي» من الولاية «بعض الظالمين بعضا» أي على بعض «بما كانوا يكسبون» من المعاصي.'),
(919, 6, 130, '«يا معشر الجن والإنس ألم يأتكم رسل منكم» أي من مجموعكم أي بعضكم الصادق بالإنس أو رسل الجن نذرهم الذين يسمعون كلام الرسل فيبلغون قومهم «يقصون عليكم آياتي وينذرونكم لقاء يومكم هذا قالوا شهدنا على أنفسنا» أن قد بلغنا قال تعالى: «وغرَّتهم الحياة الدنيا» فلم يؤمنوا «وشهدوا على أنفسهم أنهم كانوا كافرين».'),
(920, 6, 131, '«ذلك» أي إرسال الرسل «أن» اللام مقدرة وهي مخففة أي لأنه «لم يكن ربَّك مهلك القرى بظلم» منها «وأهلها غافلون» لم يرسل إليهم رسول يبين لهم؟.'),
(921, 6, 132, '«ولكل» من العاملين «درجات» جزاء «مما عملوا» من خير وشر «وما ربك بغافل عما يعملون» بالياء والتاء.'),
(922, 6, 133, '«وربُّك الغني» عن خلقه وعبادتهم «ذو الرحمة إن يشأ يذهبكم» يا أهل مكة بالإهلاك «ويستخلف من بعدكم ما يشاء» من الخلق «كما أنشأكم من ذرية قوم آخرين» أذهبهم ولكنه أبقاكم رحمة لكم.'),
(923, 6, 134, '«إن ما توعدون» من الساعة والعذاب «لآت» لا محالة «وما أنتم بمعجزين» فائتين عذابنا.'),
(924, 6, 135, '«قل» لهم «يا قوم اعملوا على مكانتكم» حالتكم «إني عامل» على حالتي «فسوف تعلمون من» موصولة مفعول العلم «تكون له عاقبة الدار» أي العاقبة المحمودة في الدار الآخرة أنحن أم أنتم «إنه لا يفلح» يسعد «الظالمون» الكافرون.'),
(925, 6, 136, '«وجعلوا» أي كفار مكة «لله مما ذرأ» خلق «من الحرث» الزرع «والأنعام نصيبا» يصرفونه إلى الضيفان والمساكين ولشركائهم نصيبا يصرفونه إلى سدنتها «فقالوا هذا لله بزعمهم» بالفتح والضم «وهذا لشركائنا» فكانوا إذا سقط في نصيب الله شيء من نصيبها التقطوه أو نصيبها شيء من نصيبه تركوه وقالوا إن الله غني عن هذا كما قال تعالى «فما كان لشركائهم فلا يصل إلى الله» أي لجهته «وما كان لله فهو يصل إلى شركائهم ساء» بئس «ما يحكمون» حكمهم هذا.'),
(926, 6, 137, '«وكذلك» كما زين لهم ما ذكر «زَيَّنَ لكثير من المشركين قتل أولادهم» بالوأد «شركاؤُهم» من الجن بالرفع فاعل زين وفي قراءة ببنائه للمفعول ورفع قتل ونصب الأولاد به وجر شركائهم بإضافته وفيه الفصل بين المضاف والمضاف إليه بالمفعول - ولا يضر - وإضافة القتل إلى الشركاء لأمرهم به «ليردوهم» يهلكوهم «وليلبسوا» يخلطوا «عليهم دينهم ولو شاء الله ما فعلوه فذرهم وما يفترون».'),
(927, 6, 138, '«وقالوا هذه أنعام وحرث حجر» حرام «لا يطعمها إلا من شاء» من خَدَمَةِ الأوثان وغيرهم «بزَعمهم» أي لا حجة لهم فيه «وأنعام حرمت ظهورها» فلا تركب كالسوائب والحوامي «وأنعام لا يذكرون اسم الله عليها» عند ذبحها بل يذكرون اسم أصنامهم ونسبوا ذلك إلى الله «افتراء عليه سيجزيهم بما كانوا يفترون» عليه.'),
(928, 6, 139, '«وقالوا ما في بطون هذه الأنعام» المحرمة وهي السوائب والبحائر «خالصة» حلال «لذكورنا ومحرَّم على أزواجنا» أي النساء «وإن تَكُنْ مَيْتَةٌ» بالرفع والنصب مع تأنيث الفعل وتذكيره «فهم فيه شركاء سيجزيهم» الله «وصفَهم» ذلك بالتحليل والتحريم أي جزاءه «إنه حكيم» في صنعه «عليم» بخلقه.'),
(929, 6, 140, '«قد خسر الذين قتلوا» بالتخفيف والتشديد «أولادهم» بالوأد «سفها» جهلا «بغير علم وحرَّموا ما رزقهم الله» مما ذكر «افتراء على الله قد ضلوا وما كانوا مهتدين».'),
(930, 6, 141, '«وهو الذي أنشأ» خلق «جنات» بساتين «معروشات» مبسوطات على الأرض كالبطيخ «وغير معروشات» بأن ارتفعت على ساق كالنخل «و» أنشأ «النخل والزرع مختلفا أكلُهُ» ثمره وحبه في الهيئة والطعم «والزيتون والرمان متشابها» ورقهما حال «وغير متشابه» طعمهما «كلوا من ثمره إذا أثمر» قبل النضج «وآتوا حقه» زكاته «يوم حصاده» بالفتح والكسر من العشر أو نصفه «ولا تُسرفوا» بإعطاء كله فلا يبقى لعيالكم شيء «إنه لا يحب المسرفين» المتجاوزين ما حدَّ لهم.'),
(931, 6, 142, '«و» أنشأ «من الأنعام حمولة» صالحة للحمل عليها كالإبل الكبار «وفرشا» لا تصلح له كالإبل الصغار والغنم سميت فرشا لأنها كالفرش للأرض لدونها منها «كلوا مما رزقكم الله ولا تتبعوا خطوات الشيطان» طرائقه من التحريم والتحليل «إنه لكم عدوٌ مبين» بين العداوة.'),
(932, 6, 143, '«ثمانية أزواج» أصناف بدل من حمولة وفرشا «من الضأن» زوجين «اثنين» ذكر وأنثى «ومن المعَز» بالفتح والسكون «اثنين قل» يا محمد لمن حرم ذكور الأنعام تارة وإناثها أخرى ونسب ذلك إلى الله «آلذكرين» من الضأن والمعز «حرم» الله عليكم «أم الأنثيين» منهما «أما اشتملت عليه أرحام الأنثيين» ذكرا كان أو أنثى «نَبِّئوني بعلم» عن كيفية تحريم ذلك «إن كنتم صادقين» فيه المعنى من أين جاء التحريم؟ فإن كان من قبل الذكورة فجميع الذكور حرام أو الأنوثة فجميع الإناث، أو اشتمال الرحم فالزوجان، فمن أين التخصيص؟ والإستفهام للإنكار.'),
(933, 6, 144, '«ومن الإبل اثنين ومن البقر اثنين قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين أم» بل «كنتم شهداء» حضورا «إذ وصَّاكم الله بهذا» التحريم فاعتمدتم ذلك! لا بل أنتم كاذبون فيه «فمن» أي لا أحد «أظلم ممن افترى على الله كذبا» بذلك «لُيضل الناس بغير علم إن الله لا يهدي القوم الظالمين».'),
(934, 6, 145, '«قل لا أجد فيما أوحي إليَّ» شيئا «محرَّما على طاعم يطعمه إلا أن يكون» بالياء والتاء «ميتة» بالنصب وفي قراءة بالرفع مع التحتانية «أو دما مسفوحا» سائلا بخلاف غيره كالكبد والطحال «أو لحم خنزير فإنه رجس» حرام «أو» إلا أن يكون «فسقا أهل لغير الله به» أي ذبح على اسم غيره «فمن اضطرَّ» إلى شيء مما ذكر فأكله «غير باغ ولا عاد فإن ربَّك غفور» له ما أكل «رحيم» به ويلحق بما ذكر بالسنة كل ذي ناب من السباع ومخلب من الطير.'),
(935, 6, 146, '«وعلى الذين هادوا» أي اليهود «حرَّمنا كل ذي ظفر» وهو ما لم تفرق أصابعه كالإبل والنعام «ومن البقر والغنم حرمنا عليهم شحومهما» الثروب وشحم الكلي «إلا ما حملت ظهورهما» أي ما علق بها منه «أو» حملته «الحوايا» الأمعاء جمع حاوياء أو حاوية «أو ما اختلط بعظم» منه وهو شحم الإلية فإنه أحل لهم «ذلك» التحريم «جزيناهم» به «ببغيهم» بسبب ظلمهم بما سبق في سورة النساء «وإنا لصادقون» في أخبارنا ومواعيدنا.'),
(936, 6, 147, '«فإن كذَّبوك» فيما جئت به «فقل» لهم «ربكم ذو رحمة واسعة» حيث لم يعاجلكم بالعقوبة وفيه تلطف بدعائهم إلى الإيمان «ولا يُرد بأسه» عذابه إذا جاء «عن القوم المجرمين».'),
(937, 6, 148, '«سيقول الذين أشركوا لو شاء الله ما أشركنا» نحن «ولا آباؤنا ولا حرَّمنا من شيء» فإشراكنا وتحريمنا بمشيئته فهو راض به قال تعالى: «كذلك» كما كذب هؤلاء «كذَّب الذين من قبلهم» رسلهم «حتى ذاقوا بأسنا» عذابنا «قل هل عندكم من علم» بأن الله راضى بذلك «فتخرجوه لنا» أي لا علم عندكم «إن» ما «تتَّبعون» في ذلك «إلا الظن وإن» ما «أنتم إلا تخرصون» تكذبون فيه.'),
(938, 6, 149, '«قل» إن لم يكن لكم حجة «فللَّه الحجة البالغة» التامة «فلو شاء» هدايتكم «لهداكم أجمعين».'),
(939, 6, 150, '«قل هلمَّ» أحضروا «شهداءكم الذين يشهدون أن الله حرَّم هذا» الذي حرمتموه «فإن شهدوا فلا تشهد معهم ولا تتبع أهواء الذين كذبوا بآياتنا والذين لا يؤمنون بالآخرة وهم بربهم يعدلون» يشركون.'),
(940, 6, 151, '«قل تعالوا أتل» أقرأ «ما حرم ربكم عليكم أ» ن مفسرة «لا تشركوا به شيئا و» أحسنوا «بالوالدين إحسانا ولا تقتلوا أولادكم» بالوأد «من» أجل «إملاق» فقر تخافونه «نحن نرزقكم وإياكم ولا تقربوا الفواحش» الكبائر كالزنا «ما ظهر منها وما بطن» أي علانيتها وسرها «ولا تقتلوا النفس التي حرَّم الله إلا بالحق» كالقود وحد الردة ورجم المحصن «ذلكم» المذكور «وصاكم به لعلكم تعقلون» تتدبرون.'),
(941, 6, 152, '«ولا تقربوا مال اليتيم إلا بالتي» أي بالخصلة التي «هي أحسن» وهي ما فيه صلاحه «حتى يبلغ أشدَّه» بأن يحتلم «وأوفوا الكيل والميزان بالقسط» بالعدل وترك البخس «لا نكلف نفسا إلا وسعها» طاقتها في ذلك فإن أخطأ في الكيل والوزن والله يعلم صحة نيته فلا مؤاخذة عليه كما ورد في حديث «وإذا قلتم» في حكم أو غيره «فاعدلوا» بالصدق «ولو كان» المقول له أو عليه «ذا قربى» قرابة «وبعهد الله أوفوا ذلكم وصَّاكم به لعلكم تذكَّرون» بالتشديد تتعظون والسكون.'),
(942, 6, 153, '«وأنَّ» بالفتح على تقدير اللام والكسر استئنافا «هذا» الذي وصيتكم به «صراطي مستقيما» حال «فاتَّبعوه ولا تتبعوا السبل» الطرق المخالفة له «فتفرَّق» فيه حذف إحدى التاءين تميل «بكم عن سبيله» دينه «ذلكم وصاكم به لعلكم تتقون»'),
(943, 6, 154, '«ثم آتينا موسى الكتاب» التوراة وثم لترتيب الأخبار «تماما» للنعمة «على الذي أحسن» بالقيام به «وتفصيلا» بيانا «لكل شيء» يحتاج إليه في الدين «وهدىّ ورحمة لعلهم» أي بني إسرائيل «بلقاء ربهم» بالبعث «يؤمنون».'),
(944, 6, 155, '«وهذا» القرآن «كتاب أنزلناه مبارك فاتَّبعوه» يا أهل مكة بالعمل بما فيه «واتقوا» الكفر «لعلكم ترحمون».'),
(945, 6, 156, 'أنزلناه لـ «أن» لا «تقولوا إنما أنزل الكتاب على طائفتين» اليهود والنصارى «من قبلنا وإن» مخففة واسمها محذوف أي إنا «كنَّا عن دراستهم» قراءتهم «لغافلين» لعدم معرفتنا لها إذ ليست بلغتنا.'),
(946, 6, 157, '«أو تقولا لو أنا أنزل علينا الكتابُ لكنا أهدى منهم» لجودة أذهاننا «فقد جاءكم بينة» بيان «من ربِّكم وهدى ورحمة» لمن اتبعه «فمن» أي لا أحد «أظلم ممن كذَّب بآيات الله وصدف» أعرض «عنها سنجزي الذين يصدفون عن آياتنا سوء العذاب» أي أشده «بما كانوا يصدفون».'),
(947, 6, 158, '«هل ينظرون» ما ينتظر المكذبون «إلا أن تأتيهم» بالتاء والياء «الملائكة» لقبض أرواحهم «أو يأتي ربُّك» أي أمره بمعنى عذابه «أو يأتي بعض آيات ربِّك» أي علاماته الدالة على الساعة «يوم يأتي بعض آيات ربَّك» وهي طلوع الشمس من مغربها كما في حديث الصحيحين «لا ينفع نفسا إيمانها لم تكن آمنت من قبل» الجملة صفة النفس «أو» نفسا لم تكن «كسبت في إيمانها خيرا» طاعة أي لا تنفعها توبتها كما في الحديث «قل انتظروا» أحد هذه الأشياء «إنا منتظرون» ذلك.'),
(948, 6, 159, '«إن الذين فرَّقوا دينهم» باختلافهم فيه فأخذوا بعضه وتركوا بعضه «وكانوا شيعا» فرقا في ذلك، وفي قراءة فارقوا أي تركوا دينهم الذي أمروا به وهم اليهود والنصارى «لست منهم في شيء» أي فلا تتعرض لهم «إنما أمرهم إلى الله» يتولاه «ثم ينبِّئهم» في الآخرة «بما كانوا يفعلون» فيجازيهم به وهذا منسوخ بآيه السيف.'),
(949, 6, 160, '«من جاء بالحسنة» أي لا إله إلا الله «فله عشرُ أمثالها» أي جزاء عشر حسنات «ومن جاء بالسيئة فلا يجزى إلا مثلها» أي جزاءه «وهم لا يُظلمون» ينقصون من جزائهم شيئا.'),
(950, 6, 161, '«قل إنني هداني ربي إلى صراط مستقيم» ويبدل من محله «دينا قيما» مستقيما «ملة إبراهيم حنيفا وما كان من المشركين».'),
(951, 6, 162, '«قل إن صلاتي ونسكي» عبادتي من حج وغيره «ومحياي» حياتي «ومماتي» موتي «لله رب العالمين».'),
(952, 6, 163, '«لا شريك له» في ذلك «وبذلك» أي التوحيد «أمرت وأنا أول المسلمين» من هذه الأمة.'),
(953, 6, 164, '«قل أغير الله أبغي ربّا» إلها أي لا أطلب غيره «وهو ربُّ» مالك «كل شيء ولا تكسب كل نفس» ذنبا «إلا عليها ولا تزر» تحمل نفس «وازرة» آثمة «وزر» نفس «أخرى ثم إلى ربكم مرجعكم فينبئكم بما كنتم فيه تختلفون».'),
(954, 6, 165, '«وهو الذي جعلكم خلائف الأرض» جمع خليفة: أي يخلف بعضكم بعضا فيها «ورفع بعضكم فوق بعض درجات» بالمال والجاه وغير ذلك «ليبلوكم» ليختبركم «فيما آتاكم» أعطاكم ليظهر المطيع منكم والعاصي «إن ربك سريع العقاب» لمن عصاه «وإنه لغفور» للمؤمنين «رحيم» بهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, '«المص» الله أعلم بمراده بذلك.'),
(956, 7, 2, 'هذا «كتاب أنزل إليك» خطاب للنبي صلى الله عليه وسلم «فلا يكن في صدرك حرج» ضيق «منه» أن تبلغه مخافة أن تكذب «لتنذر» متعلق بأنزل أي للإنذار «به وذكرى» تذكرة «للمؤمنين» به.'),
(957, 7, 3, 'قل لهم «أتبعوا ما أنزل إليكم من ربكم» أي القرآن «ولا تتبعوا» تتخذوا «من دونه» أي الله أي غيره «أولياء» تطيعونهم من معصيته تعالى «قليلا ما تَذَّكَّرون» بالتاء والياء تتعظون وفيه إدغام التاء في الأصل في الذال، وفي قراءة بسكونها وما زائدة لتأكيد القلة.'),
(958, 7, 4, '«وكم» خبرية مفعول «من قرية» أريد أهلها «أهلكناها» أردنا إهلاكها «فجاءها بأسنا» عذابا «بياتا» ليلا «أو هم قائلون» نائمون بالظهيرة والقيلولة استراحة نصف النهار وإن لم يكن معها نوم أي مرة جاءها ليلا ومرَّة جاءها نهارا.'),
(959, 7, 5, '«فما كان دعواهم» قولهم «إذ جاءهم بأسنا إلا أن قالوا إنا كنا ظالمين».'),
(960, 7, 6, '«فلنسألن الذين أرسل إليهم» أي الأمم عن إجابتهم الرسل وعملهم «ولنسألن المرسلين» عن الإبلاغ.'),
(961, 7, 7, '«فلنقصَّن عليهم بعلم» لنخبرنهم عن علم بما فعلوه «وما كنا غائبين» عن إبلاغ الرسل والأمم الخالية فيما عملوا.'),
(962, 7, 8, '«والوزن» للأعمال أو لصحائفها بميزان له لسان وكتفان كما ورد في حديث كائن «يومئذ» أي يوم السؤال المذكور وهو يوم القيامة «الحق» العدل صفة الوزن «فمن ثقلت موازينه» بالحسنات «فأولئك هم المفلحون» الفائزون.'),
(963, 7, 9, '«ومن خفّت موازينه» بالسيئات «فأولئك الذين خسروا أنفسهم» بتصييرها إلى النار «بما كانوا بآياتنا يظلمون» يجحدون.'),
(964, 7, 10, '«ولقد مكَّناكم» يا بني آدم «في الأرض وجعلنا لكم فيها معايش» بالياء أسبابا تعيشون بها جمع معيشة «قليلا ما» لتأكيد القلة «تشكرون» على ذلك.'),
(965, 7, 11, '«ولقد خلقناكم» أي أباكم آدم «ثم صوَّرناكم» أي صورناه وأنتم في ظهره «ثم قلنا للملائكة اسجدوا لآدم» سجود تحية بالانحناء «فسجدوا إلا إبليس» أبا الجن كان بين الملائكة «لم يكن من الساجدين».'),
(966, 7, 12, '«قال» تعالى «ما منعك أن» «لا» زائدة «تسجد إذ» حين «أمرتك قال أنا خير منه خلقتني من نار وخلقته من طين».'),
(967, 7, 13, '«قال فاهبط منها» أي من الجنة وقيل من السماوات «فما يكون» ينبغي «لك أن تتكبَّر فيها فاخرج» منها «إنَّك من الصاغرين» الذليلين.'),
(968, 7, 14, '«قال أنظرني» أخرى «إلى يوم يُبعثون» أي الناس.'),
(969, 7, 15, '«قال إنك من المنظرين» وفي آية أخرى «إلى يوم الوقت المعلوم» أي يوم النفخة الأولى.'),
(970, 7, 16, '«قال فبما أغويتني» أي بإغوائك لي والياء للقسم وجوابه «لأقعدن لهم» أي لبني آدم «صراطك المستقيم» أي على الطريق الموصل إليك.'),
(971, 7, 17, '«ثم لآتينهم من بين أيديهم ومن خلفهم وعن أيمانهم وعن شمائلهم» أي من كل جهة فأمنعهم عن سلوكه قال ابن عباس ولا يستطيع أن يأتي من فوقهم لئلا يحول بين العبد وبين رحمة الله تعالى «ولا تجد أكثرهم شاكرين» مؤمنين.'),
(972, 7, 18, '«قال اخرج منها مذءوما» بالهمزة معيبا أو ممقوتا «مدحورا» مبعدا عن الرحمة «لمن تبعك منهم» من الناس واللام للابتداء أو موطئة للقسم وهو «لأملأنَّ جهنم منكم أجمعين» أي منك بذريتك ومن الناس وفيه تغليب الحاضر على الغائب وفي الجملة معنى جزاء من الشرطية أي من تبعك أعذبه.'),
(973, 7, 19, '«و» قال «يا آدم اسكن أنت» تأكيد للضمير في اسكن ليعطف عليه «وزوجك» حواء بالمد «الجنة فكلا من حيث شئتما ولا تقربا هذه الشجرة» بالأكل منها وهي الحنطة «فتكونا من الظالمين».'),
(974, 7, 20, '«فوسوس لهما الشيطان» إبليس «ليبدي» يظهر «لهما ما ووري» فوعل من الموارة «عنهما من سوآتهما وقال ما نهاكما ربُّكما عن هذه الشجرة إلا» كراهة «أن تكونا مَلَكَيْنِ» وقرئ بكسر اللام «أو تكونا من الخالدين» أي وذلك لازم عن الأكل منها في آية أخرى (هل أدلك على شجرة الخلد ومُلك لا يبلى).'),
(975, 7, 21, '«وقاسمهما» أي أقسم لهما بالله «إني لكما لمن الناصحين» في ذلك.'),
(976, 7, 22, '«فدلاهما» حطهما عن منزلتهما «بغرور» منه «فلما ذاقا الشجرة» أي أكلا منها «بدت لهما سوآتهما» أي ظهر لكل منهما قبله وقبل الآخر ودبره وسمي كل منها سوأة لأن انكشافه يسوء صاحبه «وطفقا يخصفان» أخذ يلزقان «عليهما من ورق الجنة» ليستترا به «وناداهما ربهما ألم أنهكما عن تلكما الشجرة وأقل لكما إن الشيطان لكما عدو مبين» بيِّن العداوة والاستفهام للتقرير.'),
(977, 7, 23, '«قالا ربَّنا ظلمنا أنفسنا» بمعصيتنا «وإن لم تغفر لنا وترحمنا لنكونن من الخاسرين».'),
(978, 7, 24, '«قال اهبطوا» أي آدم وحواء بما اشتملتما عليه من ذريتكما «بعضكم» بعض الذرية «لبعض عدوٌ» من ظلم بعضهم بعضا «ولكم في الأرض» مستقر» أي مكان استقرار «ومتاع» تمتع «إلى حين» تنقضي فيه آجالكم.'),
(979, 7, 25, '«قال فيها» أي الأرض «تحيون وفيها تموتون ومنها تخرجون» بالبعث، وبالباء للفاعل والمفعول.'),
(980, 7, 26, '«يا بني آدم قد أنزلنا عليكم لباسا» أي خلقناه لكم «يواري» يستر «سوآتكم وريشا» وهو ما يتحمل به من الثياب «ولباس التقوى» العمل الصالح والسمت الحسن بالنصب عطف على لباسا والرفع مبتدأ خبره جملة «ذلك خيرٌ ذلك من آيات الله» دلائل قدرته «لعلهم يذَّكرون» فيؤمنون فيه التفات عن الخطاب.'),
(981, 7, 27, '«يا بني آدم لا يفتننَّكم» يضلكم «الشيطان» أي لا تتبعوه فتفتنوا «كما أخرج أبويكم» بفتنته «من الجنة ينزع» حال «عنهما لباسهما ليريهما سوآتهما إنه» أي الشيطان «يراكم هو وقبيله» جنوده «من حيث لا ترونهم» للطافة أجسادهم أو عدم ألوانهم «إنا جعلنا الشياطين أولياء» أعوانا وقرناء «للذين لا يؤمنون».'),
(982, 7, 28, '«وإذا فعلوا فاحشة» كالشرك وطوافهم بالبيت عراة قائلين لا نطوف في ثياب عصينا الله فيها فنهوا عنها «قالوا وجدنا عليها آباءنا» فاقتدينا بهم «والله أمرنا بها» أيضا «قل» لهم «إن الله لا يأمر بالفحشاء أتقولون على الله ما لا تعلمون» أنه قاله، استفهام إنكار.'),
(983, 7, 29, '«قل أمر ربي بالقسط» بالعدل «وأقيموا» معطوف على معنى بالقسط أي قال أقسطوا وأقيموا أو قبله فاقبلوا مقدرا «وجوهكم» لله «عند كل مسجد» أي أخلصوا له سجودكم «وادعوه» اعبدوه «مخلصين له الدين» من الشرك «كما بدأكم» خلقكم ولم تكونوا شيئا «تعودون» أي يعيدكم أحياء يوم القيامة.'),
(984, 7, 30, '«فريقا» منكم «هدى وفريقا حق عليهم الضلالة إنهم اتخذوا الشياطين أولياء من دون الله» أي غيره «ويحسبون أنهم مهتدون».'),
(985, 7, 31, '«يا بني آدم خذوا زينتكم» ما يستر عورتكم «عند كل مسجد» عند الصلاة والطواف «وكلوا واشربوا» ما شئتم «ولا تسرفوا إنه لا يحب المسرفين».'),
(986, 7, 32, '«قل» إنكارا عليهم «من حرَّم زينة الله التي أخرج لعباده» من اللباس «والطيبات» المستلذات «من الرزق قل هي للذين آمنوا في الحياة الدنيا» بالاستحقاق وإن فيها غيرهم «خالصةٌ» خاصة بهم بالرفع والنصب حال «يوم القيامة كذلك نفصِّل الآيات» نبينها مثل ذلك التفصيل «قوم يعلمون» يتدبَّرون فإنهم المنتفعون بها.'),
(987, 7, 33, '«قل إنما حرَّم ربي الفواحش» الكبائر كالزنا «ما ظهر منها وما بطن» أي جهرها وسرها «والإثم» المعصية «والبغي» على الناس «بغير الحق» وهو الظلم «وأن تشركوا بالله ما لم ينزل به» بإشراكه «سلطانا» حجة «وأن تقولوا على الله ما لا تعلمون» من تحريم ما لم يحرم وغيره.'),
(988, 7, 34, '«ولكل أمَّة أجل» مدة «فإذا جاء أجلهم لا يستأخرون» عنه «ساعة ولا يستقدمون» عليه.'),
(989, 7, 35, '«يابني آدم إمَّا» فيه إدغام نون إن الشرطية في ما المزيدة «يأتينكم رسل منكم يقصُّون عليكم آياتي فمن اتقى» الشرك «وأصلح» عمله «فلا خوف عليهم ولا هم يحزنون» في الآخرة.'),
(990, 7, 36, '«والذين كذبوا بآياتنا واستكبروا» تكبروا «عنها» فلم يؤمنوا بها «أولئك أصحاب النار هم فيها خالدون».'),
(991, 7, 37, '«فمن» أي لا أحد «أظلم ممن افترى على الله كذبا» بنسبة الشريك والولد إليه «أو كذَّب بآياته» القرآن «أولئك ينالهم» يصيبهم «نصيبهم» حظهم «من الكتاب» مما كتب لهم في اللوح المحفوظ من الرزق والأجل وغير ذلك «حتى إذا جاءتهم رسلنا» أي الملائكة «يتوفونهم قالوا» لهم تبكيتا «أين ما كنتم تدعون» تعبدون «من دون الله قالوا ضلُّوا» غابوا «عنا» فلم نرهم «وشهدوا على أنفسهم» عند الموت «أنهم كانوا كافرين».'),
(992, 7, 38, '«قال» تعالى لهم يوم القيامة «ادخلوا في» جملة «أمم قد خلت من قبلكم من الجن والإنس في النار» متعلق بادخلوا «كلما دخلت أُمة» النار «لعنت أختها» التي قبلها لضلالها بها «حتى إذا ادَّاركوا» تلاحقوا «فيها جميعا قالت أخراهم» وهم الأتباع «لأولاهم» أي لأجلائهم وهم المتبوعون «ربنا هؤلاء أضلونا فآتهم عذابا ضعفا» مضعفا «من النار قال» تعالى «لكل» منكم ومنهم «ضعف» عذاب مضعف «ولكن لا يعلمون» بالياء والتاء ما لكل فريق.'),
(993, 7, 39, '«وقالت أُولاهم لأخراهم فما كان لكم علينا من فضل» لأنكم لم تكفروا بسببنا فنحن وأنتم سواء قال تعالى لهم «فذوقوا العذاب بما كنتم تكسبون».'),
(994, 7, 40, '«إن الذين كذبوا بآياتنا واستكبروا» تكبروا «عنها» فلم يؤمنوا بها «لا تفتَّح لهم أبواب السماء» إذا عرج بأرواحهم إليها بعد الموت فيهبط بها إلى سجين بخلاف المؤمن فتفتح له ويصعد بروحه إلى السماء السابعة كما ورد في حديث «ولا يدخلون الجنة حتى يلج» يدخل «الجمل في سمِّ الخياط» ثقب الإبرة وهو غير ممكن فكذا دخولهم «وكذلك» الجزاء «نجزي المجرمين» بالكفر.'),
(995, 7, 41, '«لهم من جهنم مهاد» فراش «ومن فوقهم غواشٍ» أغطية من النار جمع غاشية وتنوينه عوض من الياء المحذوفة «وكذلك نجزي الظالمين».'),
(996, 7, 42, '«والذين آمنوا وعملوا الصالحات» مبتدأ وقوله «لا نكلّف نفسا إلا وسعها» طاقتها من العمل اعتراض بينه وبين خبره وهو «أولئك أصحاب الجنة هم فيها خالدون».'),
(997, 7, 43, '«ونزعنا ما في صدورهم من غل» حقد كان بينهم في الدنيا «تجري من تحتهم» تحت قصورهم «الأنهار وقالوا» عند الاستقرار في منازلهم «الحمد لله الذي هدانا لهذا» العمل الذي هذا جزاؤه «وما كنا لنهتدي لولا أن هدانا الله» حذف جواب لولا لدلالة ما قبله عليه «لقد جاءت رسل ربنا بالحق ونودوا أن» مخففة أي أنه أو مفسرة في المواضع الخمسة «تلكم الجنة أورثتموها بما كنتم تعملون».'),
(998, 7, 44, '«ونادى أصحابُ الجنة أصحاب النار» تقريرا أو تبكيتا «أن قد وجدنا ما وعدنا ربنا» من الثواب «حقا فهل وجدتم ما وعد» كم «ربكُم» من العذاب «حقا؟ قالوا نعمْ فأذَّن مؤذَّن» نادى منادٍ «بينهم» بين الفريقين أسمعهم «أن لعنة الله على الظالمين».'),
(999, 7, 45, '«الذين يصدون» الناس «عن سبيل الله» دينه «ويبغونها» أي يطلبون السبيل «عوجا» معوجة «وهم بالآخرة كافرون».'),
(1000, 7, 46, '(وبينهما) أي أصحاب الجنة والنار (حجاب) حاجز قيل هو سور الأعراف (وعلى الأعراف) وهو سور الجنة (رجال) استوت حسناتهم وسيئاتهم كما في الحديث (يعرفون كلا) من أهل الجنة والنار (بسيماهم) بعلامتهم وهي بياض الوجوه للمؤمنين وسوادها للكافرين لرؤيتهم لهم إذ موضعهم عال (ونادوا أصحاب الجنة أن سلام عليكم) قال تعالى (لم يدخلوها) أي أصحاب الأعراف الجنة (وهم يطمعون) في دخولها قال الحسن: لم يطمعهم إلا لكرامة يريدها بهم وروى الحاكم عن حذيفة قال "" بينما هم كذلك إذ طلع عليهم ربك فقال قوموا ادخلوا الجنة فقد غفرت لكم "".'),
(1001, 7, 47, '«وإذا صرفت أبصارهم» أي أصحاب الأعراف «تلقاء» جهة «أصحاب النار قالوا ربنا لا تجعلنا» في النار «مع القوم الظالمين».'),
(1002, 7, 48, '«ونادى أصحاب الأعراف رجالا» من أصحاب النار «يعرفونهم بسيماهم قالوا ما أغنى عنكم» من النار «جمعكم» المال أو كثرتكم «وما كنتم تستكبرون» أي واستكباركم عن الإيمان، ويقولون لهم مشيرين إلى ضعفاء المسلمين.'),
(1003, 7, 49, '«أهؤلاء الذين أقسمتم لا ينالهم الله برحمة» قد قيل لهم «ادخلوا الجنة لا خوف عليكم ولا أنتم تحزنون» وقرئ: أُدخِلوا بالبناء للمفعول ودخلوا فجملة النفي حال أي مقولا لهم بذلك.'),
(1004, 7, 50, '«ونادى أصحاب النار أصحاب الجنة أن أفيضوا علينا من الماء أو مما رزقكم الله» من الطعام «قالوا إن الله حرَّمهما» منعهما «على الكافرين».'),
(1005, 7, 51, '«الذين اتخذوا دينهم لهوا ولعبا وغرتهم الحياة الدنيا فاليوم ننساهم» نتركهم في النار «كما نسوا لقاء يومهم هذا» بتركهم العمل له «وما كانوا بآياتنا يجحدون» أي وكما جحدوا.'),
(1006, 7, 52, '«ولقد جئناهم» أي أهل مكة «بكتاب» قرآن «فصَّلناه» بيَّناه بالأخبار والوعد والوعيد «على علم» حال أي عالمين بما فصَّل فيه «هدى» حال من الهاء «ورحمة لقوم يؤمنون» به.'),
(1007, 7, 53, '«هل ينظرون» ما ينتظرون «إلا تأويله» عاقبة ما فيه «يوم يأتي تأويله» هو يوم القيامة «يقول الذين نسوه من قبل» تركوا الإيمان به «قد جاءت رسل ربنا بالحق فهل لنا من شفعاء فيشفعوا لنا أو» هل «نُرد» إلى الدنيا «فنعمل غير الذي كنا نعمل» نوحِّد الله ونترك الشرك، فيقال لهم: لا، قال تعالى: «قد خسروا أنفسهم» إذ صاروا إلى الهلاك «وضلّ» ذهب «عنهم ما كانوا يفترون» من دعوى الشريك.'),
(1008, 7, 54, '«إن ربكم الله الذي خلق السماوات والأرض في ستة أيام» من أيام الدنيا، أي في قدرها لأنه لم يكن ثَمَّ شمس ولو شاء خلقهم في لمحة، والعدول عنه لتعليم خلقه التثبيت «ثم استوى على العرش» هو في اللغة: سرير الملك استواء يليق به «يُغْشِي الليل النهار» مخففا ومشددا أي يغطي كلا منهما بالآخر طلبا «يطلبه حثيثا» سريعا «والشمس والقمر والنجوم» بالنصب عطفا على السماوات والرفع مبتدأ خبره «مسخرات» مذلَّلات «بأمره» بقدرته «ألا له الخلق» جميعا «والأمر» كله «تبارك» تعاظم «الله ربُّ» مالك «العالمين».'),
(1009, 7, 55, '«ادعوا ربَّكم تضرُّعا» حال تذللا «وخُفية» سرا «إنه لا يحب المعتدين» في الدعاء بالتشدق ورفع الصوت.'),
(1010, 7, 56, '«ولا تُفسدوا في الأرض» بالشرك والمعاصي «بعد إصلاحها» ببعث الرسل «وادعوه خوفا» من عقابه «وطمعا» في رحمته «إن رحمة الله قريب من المحسنين» المطيعين وتذكير قريب المخبر به عن رحمة لإضافتها إلى الله.'),
(1011, 7, 57, '«وهو الذي يرسل الرياح نُشُرا بين يدي رحمته» أي متفرقة قدام المطر، وفي قراءة سكون الشين تخفيفا، وفي أخرى بسكونها وفتح النون مصدرا، وفي أخرى بسكونها وضم الموحدة بدل النون: أي مبشرا ومفرد الأولى نشور كرسول والأخيرة بشير «حتى إذا أقلت» حملت الرياح «سحابا ثقالا» بالمطر «سقناه» أي السحاب وفيه التفات عن الغيبة «لبلد ميت» لا نبات به أي لإحيائها «فأنزلنا به» بالبلد «الماء فأخرجنا به» بالماء «من كل الثمرات كذلك» الإخراج «نخرج الموتى» من قبورهم بالإحياء «لعلكم تذكرون» فتؤمنون.'),
(1012, 7, 58, '«والبلد الطيب» العذب التراب «يخرج نباته» حسنا «بإذن ربه» هذا مثل للمؤمن يسمع الموعظة فينتفع بها «والذي خبث» ترابه «لا يخرج» نباته «إلا نكدا» عسرا بمشقة وهذا مثل للكافر «كذلك» كما بينا ما ذكر «نُصرِّف» نبين «الآيات لقوم يشكرون» الله يؤمنون.'),
(1013, 7, 59, '«لقد» جواب قسم محذوف «أرسلنا نوحا إلى قومه فقال يا قوم اعبدوا الله ما لكم من إله غَيْرُِهُ» بالجر صفة لإله والرفع بدل من محله «إني أخاف عليكم» إن عبدتم غيره «عذاب يوم عظيم» هو يوم القيامة.'),
(1014, 7, 60, '«قال الملأ» الأشراف «من قومه إنا لنراك في ضلالٍ مبين» بيَّن.'),
(1015, 7, 61, '«قال يا قوم ليس بي ضلالة» هي أعم من الضلال فنفيها أبلغ من نفيه «ولكني رسول من رب العالمين».'),
(1016, 7, 62, '«أبلِّغُكم» بالتخفيف والتشديد «رسالات ربَّي وأنصح» أريد الخبر «لكم وأعلم من الله ما لا تعلمون».'),
(1017, 7, 63, '«أ» كذبتم «وعجبتم أن جاءكم ذكر» موعظة «من ربكم على» لسان «رجل منكم لينذركم» العذاب إن لم تؤمنوا «ولتتَّقوا» الله «ولعلكم ترحمون» بها.'),
(1018, 7, 64, '«فكذَّبوه فأنجيناه والذين معه» من الغرق «في الفلك» السفينة «وأغرقنا الذين كذَّبوا بآياتنا» بالطوفان «إنهم كانوا قوما عَمين» عن الحق.'),
(1019, 7, 65, '«و» أرسلنا «إلى عاد» الأولى «أخاهم هودا قال يا قوم اعبدوا الله» وحِّدوه «ما لكم من إله غيرُه أفلا تتقون» تخافونه فتؤمنون.'),
(1020, 7, 66, '«قال الملأ الذين كفروا من قومه إنا لنراك في سفاهة» جهالة «وإنا لنظنُّك من الكاذبين» في رسالتك.'),
(1021, 7, 67, '«قال يا قوم ليس بي سفاهة ولكني رسول من ربِّ العالمين».'),
(1022, 7, 68, '«أبلَّغكم رسالات ربي وأنا لكم ناصح أمين» مأمون على الرسالة.'),
(1023, 7, 69, '«أو عجبتم أن جاءكم ذِكْرٌ من ربكم على» لسان «رجل منكم لينذركم واذكروا إذ جعلكم خلفاء» في الأرض «من بعد قوم نوح وزادكم في الخلق بسطة» قوة وَطَوْلا وكان طويلهم مائة ذراع وقصيرهم ستين «فاذكروا آلاء الله» نعمه «لعلكم تفلحون» تفوزون.'),
(1024, 7, 70, '«قالوا أجئتنا لنعبد الله وحده ونذر» نترك «ما كان يعبد آباؤنا فأتنا بما تعدنا» به من العذاب «إن كنت من الصادقين» في قولك.'),
(1025, 7, 71, '«قال قد وقع» وجب «عليكم من ربَّكم رجس» عذاب «وغضب أتجادلونني في أسماء سميتموها» أي سميتم بها «أنتم وآباؤكم» أصناما تعبدونها «ما نزَّل الله بها» أي بعبادتها «من سلطان» حجة وبرهان «فانتظروا» العذاب «إني معكم من المنتظرين» ذلكم بتكذيبكم لي فأرسلت عليهم الريح العقيم.'),
(1026, 7, 72, '«فأنجيناه» أي هودا «والذين معه» من المؤمنين «برحمة منا وقطعنا دابر» القوم «الذين كذبوا بآياتنا» أي استأصلناكم «وما كانوا مؤمنين» عطف على كذبوا.'),
(1027, 7, 73, '«و» أرسلنا «إلى ثمود» بترك الصرف مرادا به القبيلة «أخاهم صالحا قال يا قوم اعبدوا الله ما لكم من إله غيره قد جاءتكم بيَّنة» معجزة «من ربكم» على صدقي «هذه ناقة الله لكم آية» حال عاملها معنى الإشارة وكانوا سألوه أن يخرجها لهم صخرة عينوها «فذروها تأكل في أرض الله ولا تمسوها بسوء» بعقر أو ضرب «فيأخذكم عذاب أليم».'),
(1028, 7, 74, '«واذكروا إذ جعلكم خلفاء» في الأرض «من بعد عاد وبوَّأكم» أسكنكم «في الأرض تتَّخذون من سهولها قصورا» تسكنونها في الصيف «وتنحتون الجبال بيوتا» تسكنونها في الشتاء ونصبه على الحال المقدرة «فاذكروا آلاء الله ولا تعثوا في الأرض مفسدين».'),
(1029, 7, 75, '«قال الملأ الذين استكبروا من قومه» تكبروا عن الإيمان به «للذين استُضعفوا لمن آمن منهم» أي من قومه بدل مما قبله بإعادة الجار «أتعلمون أن صالحا مرسل من ربه» إليكم «قالوا» نعم «إنا بما أرسل به مؤمنون».'),
(1030, 7, 76, '«قال الذين استكبروا إنا بالذي آمنتم به كافرون».'),
(1031, 7, 77, 'وكانت الناقة لها يوم في الماء ولهم يوم فملوا ذلك «فعقروا الناقة» عقرها قدار بأمرهم بأن قتلها بالسيف «وعتوا عن أمر ربهم وقالوا يا صالح ائتنا بما تعدنا» به من العذاب على قتلها «إن كنت من المرسلين».'),
(1032, 7, 78, '«فأخذتهم الرجفة» الزلزلة الشديدة من الأرض والصيحة من السماء «فأصبحوا في دارهم جاثمين» باركين على الركب ميِّتين.'),
(1033, 7, 79, '«فتولى» أعرض صالح «عنهم وقال يا قوم لقد أبلغتكم رسالة ربي ونصحت لكم ولكن لا تحبون الناصحين».'),
(1034, 7, 80, '«و» اذكر «لوطا» ويبدل منه «إذا قال لقومه أتأتون الفاحشة» أي أدبار الرجال «ما سبقكم بها من أحد من العالمين» الإنس والجن.'),
(1035, 7, 81, '«أئِنَّكُم» بتحقيق الهمزتين وتسهيل الثانية وإدخال الألف بينهما وفي قراءة إنَّكُمْ «لتأتون الرجال شهوة من دون النساء بل أنتم قوم مسرفون» متجاوزون الحلال إلى الحرام.'),
(1036, 7, 82, '«وما كان جواب قومه إلا أن قالوا أخرجوهم» أي لوطا وأتباعه «من قريتكم إنهم أناس يتطهرون» من أدبار الرجال.'),
(1037, 7, 83, '«فأنجيناه وأهله إلا امرأته كانت من الغابرين» الباقين في العذاب.'),
(1038, 7, 84, '«وأمطرنا عليهم مطرا» هو حجارة السجيل فأهلكتهم «فانظر كيف كان عاقبة المجرمين».'),
(1039, 7, 85, '«و» أرسلنا «إلى مدين أخاهم شعيبا قال يا قوم اعبدوا الله ما لكم من إله غيره قد جاءتكم بينة» معجزة «من ربكم» على صدقي «فأوفوا» أتموا «الكيل والميزان ولا تبخسوا» تنقصوا «الناس أشياءهم ولا تفسدوا في الأرض» بالكفر والمعاصي «بعد إصلاحها» ببعث الرسل «ذلكم» المذكور «خير لكم إن كنتم مؤمنين» مريدي الإيمان فبادروا إليه.'),
(1040, 7, 86, '«ولا تقعدوا بكل صراط» طريق «تُوعدون» تخوفون الناس بأخذ ثيابهم أو المكس منهم «وتصدون» تصرفون «عن سبيل الله» دينه «من آمن به» بتوعدكم إياه بالقتل «وتبغونها» تطلبون الطريق «عوجا» معوجة «واذكروا إذ كنتم قليلا فكثَّركم وانظروا كيف كان عاقبة المفسدين» قبلكم بتكذيب رسلهم أي آخر أمرهم من الهلاك.'),
(1041, 7, 87, '«وإن كان طائفةٌ منكم آمنوا بالذي أُرسلت به وطائفة لم يؤمنوا» به «فاصبروا» انتظروا «حتى يحكم الله بيننا» وبينكم بإنجاء المحق وإهلاك المبطل «وهو خير الحاكمين» أعدلهم.'),
(1042, 7, 88, '«قال الملأ الذين استكبروا من قومه» عن الإيمان «لنخرجنك يا شعيب والذين آمنوا معك من قريتنا أو لتعودُن» ترجعن «في ملتنا» ديننا وغلبوا في الخطاب الجمع على الواحد لأن شعيبا لم يكن في ملتهم قط وعلى نحوه أجاب «قال أ» نعود فيها «ولو كنا كارهين» لها استفهام إنكار.'),
(1043, 7, 89, '«قد افترينا على الله كذبا إن عدنا في ملَّتكم بعد إذ نجَّانا الله منها وما يكون» ينبغي «لنا أن نعود فيها إلا أن يشاء الله ربُّنا» ذلك فيخذلنا «وسع ربُّنا كلَّ شيء علما» أي وسع علمه كل شيء ومنه حالي وحالكم «على الله توكلنا ربنا افتح» احكم «بيننا وبين قومنا بالحق وأنت خير الفاتحين» الحاكمين.'),
(1044, 7, 90, '«وقال الملأ الذين كفروا من قومه» أي قال بعضهم لبعض «لئن» لام قسم «اتبعتم شعيبا إنكم إذًا الخاسرون».'),
(1045, 7, 91, '«فأخذتهم الرجفة» الزلزلة الشديدة «فأصبحوا في دارهم جاثمين» باركين على الركب ميِّتين.'),
(1046, 7, 92, '«الذين كذَّبوا شعيبا» مبتدأ خبره «كأن» مخففة واسمها محذوف أي كأنهم «لم يَغنوا» يقيموا «فيها» في ديارهم «الذين كذَّبوا شعيبا كانوا هم الخاسرين» التأكيد بإعادة الموصول وغيره للرد عليهم في قولهم السابق.'),
(1047, 7, 93, '«فتولى» أعرض «عنهم وقال يا قوم لقد أبلغتكم رسالات ربي ونصحت لكم» فلم تؤمنوا «فكيف آسى» أحزن «على قوم كافرين» استفهام بمعنى النفي.'),
(1048, 7, 94, '«وما أرسلنا في قرية من نبي» فكذبوا «إلا أخذنا» عاقبنا «أهلها بالبأساء» شدة الفقر «والضرَّاء» المرض «لعلهم يضَّرَّعون» يتذللون فيؤمنون.'),
(1049, 7, 95, '«ثم بدَّلنا» أعطيناهم «مكان السيئة» العذاب «الحسنة» الغنى والصحة «حتى عفوا» كثروا «وقالوا» كفرا للنعمة «قد مس آباءنا الضرَّاء والسرَّاء» كما مسنا وهذه عادة الدهر وليست بعقوبة من الله فكونوا على ما أنتم عليه قال تعالى: «فأخذناهم» بالعذاب «بغتة» فجأة «وهم لا يشعرون» بوقت مجيئه قبله.'),
(1050, 7, 96, '«ولو أنَّ أهل القرى» المكذَّبين «آمنوا» بالله ورسلهم «واتقوا» الكفر والمعاصي «لفتحنا» بالتخفيف والتشديد «عليهم بركات من السماء» بالمطر «والأرض» بالنبات «ولكن كذَّبوا» الرسل «فأخذناهم» عاقبناهم «بما كانوا يكسبون».'),
(1051, 7, 97, '«أفأمن أهل القرى» المكذِّبون «أن يأتيهم بأسنا» عذابنا «بياتا» ليلا «وهم نائمون» غافلون عنه.'),
(1052, 7, 98, '«أو أمن أهل القرى أن يأتيهم بأسنا ضحى» نهارا «وهم يلعبون».'),
(1053, 7, 99, '«أفأمنوا مكر الله» استدراجه إياهم بالنعمة وأخذهم بغتة «فلا يأمن مكر الله إلا القوم الخاسرون».'),
(1054, 7, 100, '«أو لم يهد» يتبيَّن «للذين يرثون الأرض» بالسكنى «من بعد» هلاك «أهلها أن» فاعل مخففة واسمها محذوف أي أنه «لو نشاء أصبناهم» بالعذاب «بذنوبهم» كما أصبنا من قبلهم. والهمزةُ في المواضع الأربعة للتوبيخ والفاء والواو الداخلة عليهما للعطف، وفي قراءة بسكون الواو في الموضع الأول عطفا بأو «و» نحن «نطبع» نختم «على قلوبهم فهم لا يسمعون» الموعظة سماع تدبر.'),
(1055, 7, 101, '«تلك القرى» التي مرَّ ذكرها «نقصُّ عليك» يا محمد «من أنبائها» أخبار أهلها «ولقد جاءتهم رسلهم بالبينات» المعجزات الظاهرات «فما كانوا ليؤمنوا» عند مجيئهم «بما كذبوا» كفروا به «من قبل» قبل مجيئهم بل استمروا على الكفر «كذلك» الطبع «يطبع الله على قلوب الكافرين».'),
(1056, 7, 102, '«وما وجدنا لأكثرهم» أي الناس «من عهد» أي وفاء بعهدهم يوم أخذ الميثاق «وإن» مخففة «وجدنا أكثرهم لفاسقين».'),
(1057, 7, 103, '«ثم بعثنا من بعدهم» أي الرسل المذكورين «موسى بآياتنا» التسع «إلى فرعون وملئِهِ» قومه «فظلموا» كفروا «بها فانظر كيف كان عاقبة المفسدين» بالكفر من إهلاكهم.'),
(1058, 7, 104, '«وقال موسى يا فرعون إني رسول من ربِّ العالمين» إليك فكذبه فقال أنا.'),
(1059, 7, 105, '«حقيق» جدير «على أن» أي بأن «لا أقول على الله إلا الحق» وفي قراءة بتشديد الياء فحقيق مبتدأ خبره أن وما بعده «قد جئتكم ببينة من ربِّكم فأرسل معي» إلى الشام «بني إسرائيل» وكان استعبدهم.'),
(1060, 7, 106, '«قال» فرعون له «إن كنت جئت بآية» على دعواك «فأت بها إن كنت من الصادقين» فيها.'),
(1061, 7, 107, '«فألقى عصاه فإذا هي ثعبان مبين» حية عظيمة.'),
(1062, 7, 108, '«ونزع يده» أخرجها من جيبه «فإذا هي بيضاء» ذات شعاع «للناظرين» خلاف ما كانت عليه من الأدمة.'),
(1063, 7, 109, '«قال الملأ من قوم فرعون إنَّ هذا لساحر عليم» فائق في علم السحر. وفي الشعراء أنه من قول فرعون نفسه فكأنهم قالوه معه على سبيل التشاور.'),
(1064, 7, 110, '«يريد أن يخرجكم من أرضكم فماذا تأمرون».'),
(1065, 7, 111, '«قالوا أرجه وأخاه» أخِّرْ أمرهما «وأرسل في المدائن حاشرين» جامعين.'),
(1066, 7, 112, '«يأتوك بكل ساحر» وفي قراءة سحَّار «عليم» بفضل موسى في علم السحر فجمعوا.'),
(1067, 7, 113, '«وجاء السحرة فرعون قالوا أئِنَّ» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين «لنا لأجرا إن كنا نحن الغالبين».'),
(1068, 7, 114, '«قال نعم وإنكم لمن المقربين».'),
(1069, 7, 115, '«قالوا يا موسى إما أن تُلقي» عصاك «وإما أن نكون نحن الملقين» ما معنا.'),
(1070, 7, 116, '«قال ألقوا» أمر للإذن بتقديم إلقائهم توصلا به إلى إظهار الحق «فلما ألقوا» حبالهم وعصيهم «سحروا أعين الناس» صرفوها عن حقيقة إدراكها «واسترهبوهم» خوفوهم حيث خيلوها حيات تسعى «وجاءوا بسحر عظيم».'),
(1071, 7, 117, '«وأوحينا إلى موسى أن ألق عصاك فإذا هي تلقف» بحذف إحدى التاءين في الأصل تبتلع «ما يأفكون» يقلبون بتمويههم.'),
(1072, 7, 118, '«فوقع الحق» ثبت وظهر «وبطل ما كانوا يعملون» من السحر.'),
(1073, 7, 119, '«فغُلبوا» أي فرعون وقومه «هنالك وانقلبوا صاغرين» صاروا ذليلين.'),
(1074, 7, 120, '«وأُلقي السحرة ساجدين».'),
(1075, 7, 121, '«قالوا آمنا برب العالمين».'),
(1076, 7, 122, '«رب موسى وهارون» لعلمهم بأن ما شهدوه من العصا لا يتأتى بالسحر.'),
(1077, 7, 123, '«قال فرعون أآمنتم» بتحقيق الهمزتين وإبدال الثانية ألفا «به» بموسى «قبل أن آذن» «لكم إنَّ هذا» الذي صنعتموه «لمكر مكرتموه في المدينة لتخرجوا منها أهلها فسوف تعلمون» ما ينالكم مني.'),
(1078, 7, 124, '«لأقطعنَّ أيديكم وأرجلكم من خلاف» أي يد كل واحد اليمنى ورجله اليسرى «ثم لأصلِّبنَّكم أجمعين».'),
(1079, 7, 125, '«قالوا إنا إلى ربِّنا» بعد موتنا بأي وجه كان «منقلبون» راجعون في الآخرة.'),
(1080, 7, 126, '«وما تنقم» تنكر «منا إلا أن آمنَّا بآيات ربِّنا لما جاءتنا ربنا أفرغ علينا صبرا» عند فعل ما توعدنا به لئلا نرجع كفارا «وتوفنا مسلمين».'),
(1081, 7, 127, '«وقال الملأ من قوم فرعون» له «أتذر» تترك «موسى وقومه ليفسدوا في الأرض» بالدعاء إلى مخالفتك «ويذرك وآلهتك» وكان صنع لهم أصناما صغارا يعبدونها وقال أنا ربُّكم وربها ولذا قال أنا ربكم الأعلى «قال سنُقَتِّل» بالتشديد والتخفيف «أبناءهم» المولودين «ونستحي» نستبقي «نساءهم» كفعلنا بهم من قبل «وإنا فوقهم قاهرون» قادرون ففعلوا بهم ذلك فشكا بنو إسرائيل.'),
(1082, 7, 128, '«قال موسى لقومه استعينوا بالله واصبروا» على أذاهم «إنَّ الأرض لله يورثها» يعطيها «من يشاء من عباده والعاقبة» المحمودة «للمتقين» الله.'),
(1083, 7, 129, '«قالوا أوذينا من قبل أن تأتينا ومن بعد ما جئتنا قال عسى ربُّكم أن يهلك عدوكم ويستخلفكم في الأرض فينظر كيف تعملون» فيها.'),
(1084, 7, 130, '«ولقد أخذنا آل فرعون بالسِّنين» بالقحط «ونقص من الثمرات لعلهم يذكرون» يتعظون فيؤمنون.'),
(1085, 7, 131, '«فإذا جاءتهم الحسنة» الخصب والغنى «قالوا لنا هذه» أي نستحقها ولم يشكروا عليها «وإن تصبهم سيئة» جدب وبلاء «يَطَّيَّروا» يتشاءموا «بموسى ومن معه» من المؤمنين «ألا إنما طائرهم» شؤمهم «عند الله» يأتيهم به «ولكن أكثرهم لا يعلمون» أنَّ ما يصيبهم من عنده.'),
(1086, 7, 132, '«وقالوا» لموسى «مهما تأتنا به من آية لتسحرنا بها فما نحن لك بمؤمنين» فدعا عليهم.'),
(1087, 7, 133, '«فأرسلنا عليهم الطُّوفان» وهو ماء دخل بيوتهم ووصل إلى حلوق الجالسين سبعة أيام «والجراد» فأكل زرعهم وثمارهم، كذلك «والقمَّل» السوس أو نوع من القراد، فتتبع ما تركه الجراد «والضفادع» فملأت بيوتهم وطعامهم «والدم» في مياههم «آياتِ مفصَّلاتِ» مبينات «فاستكبروا» عن الإيمان بها «وكانوا قوما مجرمين».'),
(1088, 7, 134, '«ولما وقع عليهم الرجز» العذاب «قالوا يا موسى ادع لنا ربَّك بما عهد عندك» من كشف العذاب عنا إن آمنا «لئن» لام قسم «كشفت عنا الرجز لنؤمنن لك ولنرسلنَّ معك بني إسرائيل».'),
(1089, 7, 135, '«فلمًّا كشفنا» بدعاء موسى «عنهم الرجز إلى أجل هم بالغوه إذا هم ينكثون» ينقضون عهدهم ويصرون على كفرهم.'),
(1090, 7, 136, '«فانتقمنا منهم فأغرقناهم في اليمِّ» البحر الملح «بأنهم» بسبب أنهم «كذَّبوا بآياتنا وكانوا عنها غافلين» لا يتدبرونها.'),
(1091, 7, 137, '«وأورثنا القوم الذين كانوا يُستضعفون» بالاستعباد، وهم بنو إسرائيل «مشارق الأرض ومغاربها التي باركنا فيها» بالماء والشجر، صفة للأرض وهي الشام «وتمت كلمة ربَّك الحسنى» وهي قوله تعالى (ونريد أن نمنَّ على الذين استضعفوا في الأرض) إلخ «على بني إسرائيل بما صبروا» على أذى عدوهم «ودمَّرنا» أهلكنا «ما كان يصنع فرعون وقومه» من العمارة «وما كانوا يعرُِشون» بكسر الراء وضمها، يرفعون من البنيان.'),
(1092, 7, 138, '«وجاوزنا» عبرنا «ببني إسرائيل البحر فأتوا» فمروا «على قوم يعكُِفون» بضم الكاف وكسرها «على أصنام لهم» على عبادتها «قالوا يا موسى لنا اجعل إلها» صنما نعبده «كما لهم آلهة قال إنكم قوم تجهلون» حيث قابلتم نعمة الله عليكم بما قلتموه.'),
(1093, 7, 139, '«إن هؤلاء مُتَبَّرٌ» هالك «ما هم فيه وباطل ما كانوا يعملون».'),
(1094, 7, 140, '«قال أغير الله أبغيكم إلها» معبودا وأصله أبغي لكم «وهو فضَّلكم على العالمين» في زمانكم بما ذكره في قوله.'),
(1095, 7, 141, '«و» اذكروا «إذ أنجيناكم» وفي قراءة أنجاكم «من آل فرعون يسومونكم» يكلفونكم ويذيقونكم «سوء العذاب» أشده وهو «يقتلون أبناءكم ويستحيون» يستَبْقون «نساءكم وفي ذلكم» الإنجاء أو العذاب «بلاء» إنعام أو ابتلاء «من ربِّكم عظيم» أفلا تتعظون فتنتهوا عما قلتم.'),
(1096, 7, 142, '«وواعدنا» بألف ودونها «موسى ثلاثين ليلة» نكلمه عند انتهائها بأن يصومها، وهي ذو القعدة فصامها فلما تمَّت أنكر خلوف فمه فاستاك فأمره الله بعشرة أخرى ليكلمه بخلوف فمه كما قال تعالى: «وأتممناها بعشر» من ذي الحجة «فتم ميقات ربِّه» وقت وعده بكلامه إياه «أربعين» حال «ليلة» تتميز «وقال موسى لأخيه هارون» عند ذهابه إلى الجبل للمناجاة «اخلفني» كن خليفتي «في قومي وأصلح» أمرهم «ولا تتبع سبيل المفسدين» بموافقتهم على المعاصي.'),
(1097, 7, 143, '«ولما جاء موسى لميقاتنا» أي للوقت الذي وعدناه بالكلام فيه «وكلَّمه ربُّه» بلا واسطة كلاما سمعه من كل جهة «قال رب أرني» نفسك «أنظر إليك قال لن تراني» أي لا تقدر على رؤيتي، والتعبير به دون لن أرى يفيد إمكان رؤيته تعالى «ولكن انظر إلى الجبل» الذي هو أقوى منك «فإن استقر» ثبت «مكانه فسوف تراني» أي تثبيت لرؤيتي وإلا فلا طاقة لك «فلما تجلَّى ربُّه» أي ظهر من نوره قدر نصف أنملة الخنصر كما في حديث صححه الحاكم «للجبل جعله دكا» بالقصر والمد، أي مدكوكا مستويا بالأرض «وخرَّ موسى صَعقا» مغشيا عليه لهول ما رأى «فلما أفاق قال سبحانك» تنزيها لك «تبت إليك» من سؤال ما لم أؤمر به «وأنا أوَّلُ المؤمنين» في زماني.'),
(1098, 7, 144, '«قال» تعالى له «يا موسى إني اصطفيتك» اخترتك «على الناس» أهل زمانك «برسالاتي» بالجمع والإفراد «وبكلامي» أي تكليمي إياك «فخذ ما آتيتك» من الفضل «وكن من الشاكرين» لأنعمي.'),
(1099, 7, 145, '«وكتبا له في الألواح» أي ألواح التوراة وكانت من سدر الجنة أو زبرجد أو زمرد سبعة أو عشرة «من كل شيء» يحتاج إليه في الدين «موعظة وتفصيلا» تبينا «لكل شيء» بدل من الجار والمجرور قبله «فخذها» قبله قلنا مقدرا «بقوة» بجد واجتهاد «وأمر قومك يأخذوا بأحسنها سأريكم دار الفاسقين» فرعون وأتباعه وهي مصر لتعتبروا بهم.'),
(1100, 7, 146, '«سأصرف عن آياتي» دلائل قدرتي من المصنوعات وغيرها «الذين يتكبرون في الأرض بغير الحق» بأن أخذلهم فلا يتكبرون فيها «وإن يروا كل آية لا يؤمنوا بها وإن يروا سبيل» طريق «الرُّشد» الهدى الذي جاء من عند الله «لا يتخذوه سبيلا» يسلكوه «وإن يروا سبيل الغي» الضلال «يتخذوه سبيلا ذلك» الصرف «بأنهم كذبوا بآياتنا وكانوا عنها غافلين» تقدم مثله.'),
(1101, 7, 147, '«والذين كذبوا بآياتنا ولقاء الآخرة» البعث وغيره «حبطت» بطَلَت «أعمالهم» ما عملوه في الدنيا من خير كصلة رحم وصدقة فلا ثواب لهم لعدم شرطه «هل» ما «يُجزون إلا» جزاء «ما كانوا يعملون» من التكذيب والمعاصي.'),
(1102, 7, 148, '«واتخذ قوم موسى من بعده» أي بعد ذهابه إلى المناجاة «من حُليِّهم» الذي استعاروه من قوم فرعون بعلَّة عرس فبقي عندهم «عجلا» صاغه لهم منه السامري «جسدا» بدل لحما ودما «له خُوارٌ» أي صوت يسمع، انقلب كذلك بوضع التراب الذي أخذه من حافر فرس جبريل في فمه فإن أثره الحياة فيما يوضع فيه، ومفعول اتخذ الثاني محذوف أي إلها «ألم يروا أنه لا يكلِّمهم ولا يهديهم سبيلا» فكيف يُتَّخذ إلها «اتخذوه» إلها «وكانوا ظالمين» باتخاذه.'),
(1103, 7, 149, '«ولما سُقط في أيديهم» أي ندموا على عبادته «ورأوْا» علموا «أنهم قد ضلوا» بها وذلك بعد رجوع موسى «وقالوا لئن لم يرحْمنا ربنا ويغفرْ لنا» بالياء والتاء فيهما «لنكونن من الخاسرين».'),
(1104, 7, 150, '«ولما رجع موسى إلى قومه غضبان» من جهتهم «أسفا» شديد الحزن «قال» «بئسما» أي بئس خلافة «خلفتموني» ها «من بعدي» خلافتكم هذه حيث أشركتم «أعجلتم أمر ربكم وألقى الألواح» ألواح التوراة غضبا لربه فتكسرت «وأخذ برأس أخيه» أي شعره بيمينه ولحيته بشماله «يجره إليه» غضبا «قال» يا «ابْنَ أُمِّ» بكسر الميم وفتحها، أراد أمي وذكرها أعطف لقبله «إن القوم استضعفوني وكادوا» قاربوا «يقتلونني فلا تُشْمت» تُفرح «بي الأعداء» بإهانتك إياي «ولا تجعلني مع القوم الظالمين» بعبادة العجل في المؤاخذة.'),
(1105, 7, 151, '«قال رب اغفر لي» ما صنعت بأخي «ولأخي» أشركه في الدعاء إرضاءً له ودفعا للشماتة به «وأدخلنا في رحمتك وأنت أرحم الراحمين» قال تعالى.'),
(1106, 7, 152, '«إن الذين اتخذوا العجل» إلها «سينالهم غضب» عذاب «من ربِّهم وذلَّة في الحياة الدنيا» فعذبوا بالأمر بقتل أنفسهم وضربت عليهم الذلة إلى يوم القيامة «وكذلك» كما جزيناهم «نجزي المفترين» على الله بالإشراك وغيره.'),
(1107, 7, 153, '«والذين عملوا السيَّئات ثم تابوا» رجعوا عنها «من بعدها وآمنوا» بالله «إن ربَّك من بعدها» أي التوبة «لغفور» لهم «رحيم» بهم.'),
(1108, 7, 154, '«ولمَّا سكت» سكن «عن موسى الغضب أخذ الألواح» التي ألقاها «وفي نسخَتها» أي ما نسخ فيها، أي كتب «هدىّ» من الضلالة «ورحمهٌ للذين هم لربِّهم يرهبون» يخافون، وأدخل اللام على المفعول لتقدمه.'),
(1109, 7, 155, '«واختار موسى قومه» أي من قومه «سبعين رجلا» ممن لم يعبدوا العجل بأمره تعالى «لميقاتنا» أي للوقت الذي وعدناه بإتيانهم فيه ليعتذروا من عبادة أصحابهم العجل فخرج بهم «فلما أخذتهم الرجفة» الزلزلة الشديدة، قال ابن عباس: لأنهم لم يزايلوا قومهم حين عبدوا العجل، قال: وهم غير الذين سألوا الرؤية وأخذتهم الصاعقة «قال» موسى «ربِّ لو شئت أهلكتهم من قبل» أي قبل خروجي بهم ليعاين بنو إسرائيل ذلك ولا يتهموني «وإياي أتهلكنا بما فعل السفهاء منا» استفهام استعطاف، أي لا تعذبنا بذنب غيرنا «إن» ما «هي» أي الفتنة التي وقع فيها السفهاء «إلا فتنتُك» ابتلاؤك «تضل بها من تشاء» إضلاله «وتهدي من تشاء» هدايته «أنت ولينا» متولي أمورنا «فاغفر لنا وارحمنا وأنت خير الغافرين».'),
(1110, 7, 156, '«واكتب» وأجب «لنا في هذه الدنيا حسنة وفي الآخرة» حسنة «إنَّا هُدْنا» تبنا «إليك قال» تعالى: «عذابي أصيب به من أشاء» تعذيبه «ورحمتي وسعت» عمَّت «كلَّ شيء» في الدنيا «فسأكتبها» في الآخرة «للذين يتقون ويؤتون الزكاة والذين هم بآياتتا يؤمنون».'),
(1111, 7, 157, '«الذين يتبعون الرسول النبي الأمي» محمدا صلى الله عليه وسلم «الذي يجدونه مكتوبا عندهم في التوراة والإنجيل» باسمه وصفته «يأمرهم بالمعروف وينهاهم عن المنكر ويحل لهم الطيبات» مما حُرم في شرعهم «ويحرم عليهم الخبائث» من الميتة ونحوها «ويضع عنهم إصرَهُم» ثقلهم «والأغلال» الشدائد «التي كانت عليهم» كقتل النفس من التوبة وقطع أثر النجاسة. «فالذين آمنوا به» منهم «وعَزَّروه» ووقروه «ونصروه واتبعوا النور الذي أنزل معه» أي القرآن «أولئك هم المفلحون».'),
(1112, 7, 158, '«قل» خطاب للنبي صلى الله عليه وسلم «يا أيها الناس إني رسول الله إليكم جميعا الذي له ملك السماوات والأرض لا إله إلا هو يحيي ويميت فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته» القرآن «واتَّبعوه لعلكم تهتدون» ترشدون.'),
(1113, 7, 159, '«ومن قوم موسى أمَّةٌ» جماعة «يهدون» الناس «بالحق وبه يعدلون» في الحكم.'),
(1114, 7, 160, '«وقَطَّعناهم» فّرَّقنا بني إسرائيل «اثنتي عشرة» حال «أسباطا» بدل منه، أي قبائل «أمما» بدل مما قبله «وأوحينا إلى موسى إذ استسقاه قومه» في التيه «أن اضرب بعصاك الحجر» فضربه «فانبجست» انفجرت «منه اثنتا عشرة عينا» بعدد الأسباط «قد علم كل أُناس» سبط منهم «مشربهم وظللنا عليهم الغمام» في التيه من حر الشمس «وأنزلنا عليهم المن والسلوى» هما الترنجبين والطير السماني بتخفيف الميم والقصر وقلنا لهم «كلوا من طيبات ما رزقناكم وما ظلمونا ولكن كانوا أنفسهم يظلمون».'),
(1115, 7, 161, '«و» اذكر «إذ قيل لهم اسكنوا هذه القرية» بيت المقدس «وكلوا منها حيث شئتم وقولوا» أمرنا «حطَّةٌ وادخلوا الباب» أي باب القرية «سجَّدا» سجود انحناء «نغفر» بالنون والتاء مبينا للمفعول «لكم خطيئاتكم سنزيد المحسنين» بالطاعة ثوابا.'),
(1116, 7, 162, '«فبدَّل الذين ظلموا منهم قولا غير الذي قيل لهم» فقالوا: حبة في شعرة ودخلوا يزحفون على أستاههم «فأرسلنا عليهم رجزا» عذابا «من السماء بما كانوا يظلمون».'),
(1117, 7, 163, '«واسألهم» يا محمد توبيخا «عن القرية التي كانت حاضرة البحر» مجاورة بحر القلزم وهي أيلة ما وقع بأهلها «إذ يعدون» يعتدون «في السبت» بصيد السمك المأمورين بتركه فيه «إذ» ظرف ليعدون «تأتيهم حيتانهم يوم سبتهم شُرَّعا» ظاهرة على الماء «ويوم لا يسبتون» لا يعظمون السبت أي سائر الأيام «لا تأتيهم» ابتلاء من الله «كذلك نبلوهم» بما كانوا يفسقون» ولما صادوا السمك افترقت القرية أثلاثا، ثلث صادوا معهم وثلث نهوهم، وثلث أمسكوا عن الصيد والنهي.'),
(1118, 7, 164, '«وإذ» عطف على إذ قبله «قالت أمَّة منهم» لم تصد ولم تنهَ لمن نهى «لم تعظون قوما الله مُهلكهم أو معذبهم عذابا شديدا قالوا» موعظتنا «معذرة» نعتذر بها «إلى ربِّكم» لئلا ننسب إلى تقصير في ترك النهي «ولعلَّهم يتقون» الصيد.'),
(1119, 7, 165, '«فما نسوا» تركوا «ما ذكِّروا» وعظوا «به» فلم يرجعوا «أنجينا الذين ينهوْن عن السوء وأخذنا الذين ظلموا» بالاعتداء «بعذابٍ بَئيس» شديد «بما كانوا يفسقون».'),
(1120, 7, 166, '«فلما عَتَوا» تكبروا «عن» ترك «ما نهوا عنه قلنا لهم كونوا قردة خاسئين» صاغرين فكانوها، وهذا تفصيل لما قبله، قال ابن عباس: ما أدري ما فعل بالفرقة الساكنة وقال عكرمة: لم تهلك لأنها كرهت ما فعلوه، وقالت: لم تعظون إلخ، وروى الحاكم عن ابن عباس: أنه رجع إليه وأعجبه.'),
(1121, 7, 167, '«وإذ تأذَّن» أعلم «ربُّك ليبعثنَّ عليهم» أي اليهود «إلى يوم القيامة من يسومهم سوء العذاب» بالذل وأخذ الجزية، فبعث عليهم سليمان وبعده بختنصر فقتلهم وسباهم وضرب عليهم الجزية فكانوا يؤدونها إلى المجوس إلى أن بعث نبينا صلى الله عليه وسلم فضربها عليهم «إن ربك لسريع العقاب» لمن عصاه «وإنه لغفور» لأهل طاعته «رحيم» بهم.'),
(1122, 7, 168, '«وقطَّعناهم» فرَّقناهم «في الأرض أُمما» فرقا «منهم الصالحون ومنهم» ناس «دون ذلك» الكفار والفاسقون «وبلوناهم بالحسنات» بالنعم «والسيئات» النقم «لعلهم يرجعون» عن فسقهم.'),
(1123, 7, 169, '«فخلف من بعدهم خَلْفٌ ورثوا الكتاب» التوراة عن آبائهم «يأخذون عرض هذا الأدنى» أي حطام هذا الشيء الدنيء أي الدنيا من حلال وحرام «ويقولون سيغفر لنا» ما عملناه لنا «و إن يأتهم عرض مثله يأخذوه» الجملة حال، أي يرجون المغفرة وهم عائدون إلى ما فعلوه مصرون عليه، وليس في التوراة وعد المغفرة مع الإصرار «ألم يؤخذ» استفهام تقرير «عليهم ميثاق الكتب» الإضافة بمعنى في «أن لا يقولوا على الله إلا الحقَّ ودرسوا» عطف على يؤخذ قرءوا «ما فيه» فلم كذبوا عليه بنسبة المغفرة إليه مع الإصرار «والدَّار الآخرة خير للَّذين يتقون» الحرام «أفلا يعقلون» بالياء والتاء أنها خير فيؤثرونها على الدنيا.'),
(1124, 7, 170, '«والذين يمسِّكون» بالتشديد والتخفيف «بالكتاب» منهم «وأقاموا الصلاة» كعبد الله بن سلام وأصحابه «إنا لا نضيع أجر المصلحين» الجملة خبر الذين، وفيه وضع الظاهر موضع المضمر أي أجرهم.'),
(1125, 7, 171, '«و» «إذ نتقنا الجبل» رفعناه من أصله «فوقهم كأنه ظُلَّةٌ وظنوا» أيقنوا «أنه واقع بهم» ساقط عليهم بوعد الله إياهم بوقوعه إن لم يقبلوا أحكام التوراة ن وكانوا أبَوْها لثقلها فقلبوا وقلنا لهم «خذوا ما آتيناكم بقوة» بجد واجتهاد «واذكروا ما فيه» بالعمل به «لعلكم تتقون».'),
(1126, 7, 172, '«و» اذكر «إذ» حين «أخذ ربُّك من بني آدم من ظهورهم» بدل اشتمال مما قبله بإعادة الجار «ذرِّيّاتهم» بأن أخرج بعضهم من صلب بعض من صلب آدم نسلا بعد نسل كنحو ما يتوالدون كالذّر بنعمان يوم عرفة ونصب لهم دلائل على ربوبيته وركب فيهم عقلا «وأشْهدهم على أنفسهم» قال «ألست بربكم؟ قالوا بلى» أنت ربنا «شهدنا» بذلك والإشهاد لـ «أن» لا «يقولوا» بالياء والتاء في الموضعين، أي الكفار «يوم القيامة إنا كنَّا عن هذا» التوحيد «غافلين» لا نعرفه.'),
(1127, 7, 173, '«أو يقولوا إنما أشرك آباؤنا من قبل» أي قبلنا «وكنا ذريَّة من بعدهم» فاقتدينا بهم «أفتهلكنا» تعذبنا «بما فعل المبطلون» من آبائنا بتأسيس الشرك، المعنى لا يمكنهم الاحتجاج بذلك مع إشهادهم على أنفسهم بالتوحيد، والتذكير به على لسان صاحب المعجزة قائم مقام ذكره في النفوس.'),
(1128, 7, 174, '«وكذلك نفصِّل الآيات» نبيّنها مثل ما بينا الميثاق ليتدبروها «ولعلهم يرجعون» عن كفرهم.'),
(1129, 7, 175, '«واتل» يا محمد «عليهم» أي اليهود «نبأ» خبر «الذي آتيناه آياتنا فانسلخ منها» خرج بكفره كما تخرج الحية من جلدها، وهو بلعم بن باعوراء من علماء بني إسرائيل، سُئل أن يدعو على موسى وأُهدي إليه شيء، فدعا فانقلب عليه اندلع لسانه على صدره «فأتبعه الشيطان» فأدركه فصار قرينه «فكان من الغاوين».'),
(1130, 7, 176, '«ولو شئنا لرفعناه» إلى منازل العلماء «بها» بأن نوقفه للعمل «ولكنه أخلد» سكن «إلى الأرض» أي الدنيا ومال إليها «واتَّبع هواه» في دعائه إليها فوضعناه «فمثله» صفته «كمثل الكلب إن تحمل عليه» بالطرد والزجر «يلهث» يدلع لسانه «أو» إن «تتركه يلهث» وليس غيره من الحيوان كذلك، وجملتا الشرط حال، أي لاهثا ذليلا بكل حال، والقصد التشبيه في الوضع والخسة بقرينة الفاء المشعرة بترتيب ما بعدها على ما قبلها من الميل إلى الدنيا واتباع الهوى وبقرينة، قوله «ذلك» المثل «مَثَلُ القوم الذين كذَّبوا بآياتنا فاقصص القَصَصَ» على اليهود «لعلهم يتفكرون» يتدبرون فيها فيؤمنون.'),
(1131, 7, 177, '«ساء» بئس «مثلا القوم» أي مثل القوم «الذين كذَّبوا بآياتنا وأنفسهم كانوا يظلمون» بالتكذيب.'),
(1132, 7, 178, '«من يهَدِ الله فهو المهتدي ومن يُضْللْ فأولئك هم الخاسرون».'),
(1133, 7, 179, '«ولقد ذَرأْنا» خلقنا «لجهنم كثيرا من الجن والإنس لهم قلوب لا يفقهون بها» الحق «ولهم أعين لا يبصرون بها» دلائل قدرة الله بصر اعتبار «ولهم آذان لا يسمعون بها» الآيات والمواعظ سماع تدبر واتعاظ «أولئك كالأنعام» في عدم الفقه والبصر والاستماع «بل هم أضل» من الأنعام لأنها تطلب منافعها وتهرب من مضارها وهؤلاء يقدمون على النار معاندة «أولئك هم الغافلون».'),
(1134, 7, 180, '«ولله الأسماء الحسنى» التسعة والتسعون الوارد بها الحديث، والحسنى مؤنث الأحسن «فادعوه» سموه «بها وذروا» اتركوا «الذين يُلحدون» من ألحد ولحد، يميلون عن الحق «في أسمائه» حيث اشتقوا منها أسماء لآلهتهم: كاللات من الله، والعزى من العزيز، ومناة من المنَّان «سيجزون» في الآخرة جزاء «ما كانوا يعملون» وهذا قبل الأمر بالقتال.'),
(1135, 7, 181, '«وممن خلقنا أمة يهدون بالحق وبه يَعدِلون» هم أمة محمد صلى الله عليه وسلم كما في حديث.'),
(1136, 7, 182, '«والذين كذبوا بآياتنا» القرآن من أهل مكة «سنستدرجهم» نأخذهم قليلا قليلا «من حيث لا يعلمون».'),
(1137, 7, 183, '«وأُملي لهم» أمهلهم «إن كيدي متينٌ» شديد لا يطاق.'),
(1138, 7, 184, '«أو لم يتفكروا» فيعلموا «ما بصاحبهم» محمد صلى الله عليه وسلم «من جِنَّة» جنون «إن» ما «هو إلا نذير مبين» بيَّن الإنذار.'),
(1139, 7, 185, '«أو لم ينظروا في ملكوت» ملك «السماوات والأرض و» في «ما خلق الله من شيء» بيان لما، فيستدلوا به على قدرة صانعه ووحدانيته «و» في «أن» أي أنه «عسى أن يكون قد اقترب» قرب «أجلهم» فيموتوا كفارا فيصيروا إلى النار فيبادروا إلى الإيمان «فبأي حديث بعده» أي القرآن «يؤمنون».'),
(1140, 7, 186, '«من يضلل الله فلا هادي له ويذرهم» بالياء والنون مع الرفع استئنافا، والجزم عطفا على محل ما بعد الفاء «في طغيانهم يعمهون» يترددون تحيُّرا.'),
(1141, 7, 187, '«يسألونك» أي أهل مكة «عن الساعة» القيامة «أيَّان» متى «مُرساها قل» لهم «إنَّما علمها» متى تكون «عند ربي لا يُجلّيها» يظهرها «لوقتها» اللام بمعنى في «إلا هو ثقُلت» عظمت «في السماوات والأرض» على أهلها لهولها «لا تأتيكم إلا بغتة» فجأة «يسألونك كأنك حَفيٌ» مبالغ في السؤال «عنها» حتى علمتها «قل إنما علمها عند الله» تأكيد «ولكن أكثر الناس لا يعلمون» أن علمها عنده تعالى.'),
(1142, 7, 188, '«قل لا أملك لنفسي نفعا» أجلبه «ولا ضرا» أدفعه «إلا ما شاء الله ولو كنت أعلم الغيب» ما غاب عني «لاستكثرت من الخير وما مسَّني السوء» من فقر وغيره لاحترازي عنه باجتناب المضار «إن» ما «أنا إلا نذير» بالنار للكافرين «وبشير» بالجنة «لقوم يؤمنون».'),
(1143, 7, 189, '«هو» أي الله «الذي خلقكم من نفس واحدة» أي آدم «وجعل» خلق «منها زوجها» حواء «ليسكن إليها» ويألفها «فلما تغشَّاها» جامعها «حملت حملا خفيفا» هو النطفة «فمرت به» ذهبت وجاءت لخفته «فلما أثقلت» بكبر الولد في بطنها وأشفقا أن يكون بهيمة «دعوا الله ربَّهما لئن آتيتنا» ولدا «صالحا» سويا «لنكونن من الشاكرين» لك عليه.'),
(1144, 7, 190, '«فلمَّا آتاهما» ولدا «صالحا جعلا له شركاء» وفي قراءة بكسر الشين والتنوين أي شريكا «فيما آتاهما» بتسميته عبد الحارث ولا ينبغي أن يكون عبدا إلا لله، وليس بإشراك في العبودية لعصمة آدم وروى سمرة عن النبي صلى الله عليه وسلم قال: لما ولدت حواء طاف بها إبليس وكان لا يعيش لها ولد فقال: سميه عبد الحارث فإنه يعيش فسمته فعاش فكان ذلك من وحي الشيطان وأمره رواه الحاكم وقال صحيح والترمذي وقال حسن غريب «فتعالى الله عما يشركون» أي أهل مكة به من الأصنام، والجملة مسببة عطف على خلقكم وما بينهما اعتراض.'),
(1145, 7, 191, '«أيشركون» به في العبادة «ما لا يخلق شيئا وهم يُخلقون».'),
(1146, 7, 192, '«ولا يستطيعون لهم» أي لعابديهم «نصرا ولا أنفسهم يَنْصرون» بمنعها ممن أراد بهم سوءا من كسر أو غيره، والاستفهام للتوبيخ.'),
(1147, 7, 193, '«وإن تدعوهم» أي الأصنام «إلى الهدى لا يتبعوكم» بالتخفيف والتشديد «سواء عليكم أدعوتموهم» إليه «أم أنتم صامتون» عن دعائهم لا يتبعوه لعدم سماعهم.'),
(1148, 7, 194, '«إن الذين تدعون» تعبدون «من دون الله عبادٌ» مملوكة «أمثالكم فادعوهم فليستجيبوا لكم» دعاءكم «إن كنتم صادقين» في أنها آلهة، ثم بين غاية عجزهم وفضل عابديهم عليهم فقال.'),
(1149, 7, 195, '«ألهم أرجل يمشون بها أم» بل أ «لهم أيد» جمع يد «يبطشون بها أم» بل أ «لهم آذان يسمعون بها» استفهام إنكاري، أي ليس لهم شيء من ذلك مما هو لكم فكيف تعبدوهم وأنتم حالا منهم «قل» لهم يا محمد «ادعوا شركاءكم» إلى هلاكي «ثم كيدون فلا تُنظرون» تمهلون فإني لا أبالي بكم.'),
(1150, 7, 196, '«إن وليي الله» متولي أموري «الذي نزَّل الكتاب» القرآن «وهو يتولى الصالحين» بحفظه.'),
(1151, 7, 197, '«والذين تدعون من دونه لا يستطيعون نصركم ولا أنفسهم ينصرون» فكيف أبالي بهم.'),
(1152, 7, 198, '«وإن تدعوهم» أي الأصنام «إلى الهدى لا يسمعوا وتراهم» أي الأصنام يا محمد «ينظرون إليك» أي يقابلونك كالناظر «وهم لا يبصرون».'),
(1153, 7, 199, '«خذ العفو» اليسر من أخلاق الناس ولا تبحث عنها «وأمر بالعرف» بالمعروف «وأعرض عن الجاهلين» فلا تقابلهم بسفههم.'),
(1154, 7, 200, '«وإما» فيه إدغام نون إن الشرطية في ما المزيدة «ينزغنَّك من الشيطان نَزْغٌ» أي إن يصرفك عما أمرت به صارف «فاستعذ بالله» جواب الشرط وجواب الأمر محذوف، أي يدفعه عنك «إنه سميع» للقول «عليم» بالفعل.'),
(1155, 7, 201, '«إن الذين اتقوا إذا مسَّهم» أصابهم «طيفٌ» وفي قراءة طائف أي شيء ألَّم بهم «من الشيطان تذكَّروا» عقاب الله وثوابه «فإذا هم مبصرون» الحق من غيره فيرجعون.'),
(1156, 7, 202, '«وإخوانهم» أي إخوان الشياطين من الكفار «يَمدُّونَهُمْ» أي الشياطين «في الغي ثم» هم «لا يُقْصِرُونَ» يكفون عنه بالتبصر كما تبصَّر المتقون.'),
(1157, 7, 203, '«وإذا لم تأتهم» أي أهل مكة «بآية» مما اقترحوا «قالوا لولا» هلا «اجتبيتها» أنشأتها من قبل نفسك «قل» لهم «إنَّما أتَّبع ما يوحى إلي من ربي» وليس لي أن آتي من عند نفسي بشيء «هذا» القرآن «بصائر» حجج «من ربِّكم وهدى ورحمة لقوم يؤمنون».'),
(1158, 7, 204, '«وإذا قُرئ القرآن فاستمعوا له وأنصتوا» عن الكلام «لعلكم ترحمون» نزلت في ترك الكلام في الخطبة وعبَّر عنها بالقرآن لاشتمالها عليه، وقيل في قراءة القرآن مطلقا.'),
(1159, 7, 205, '«واذكر ربَّك في نفسك» أي سرا «تضرُّعا» تدللا «وخيفة» خوفا منه «و» فوق السر «دون الجهر من القول» أي قصدا بينهما «بالغدو والآصال» أوائل النهار وأواخره «ولا تكن من الغافلين» عن ذكر الله.'),
(1160, 7, 206, '«إن الذين عند ربك» أي الملائكة «لا يستكبرون» يتكبَّرون «عن عبادته ويسبِّحونه» ينزهوِّنه عما لا يليق به «وله يسجدون» أي يخصونه بالخضوع والعبادة فكونوا مثلهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '«يسألونك» يا محمد «عن الأنفال» الغنائم لمن هي «قل» لهم «الأنفال لله» يجعلها حيث يشاء «والرسول» يقسِّمها بأمر الله فقسَّمها صلى الله عليه وسلم بينهم على السواء، رواه الحاكم في المستدرك «فاتقوا الله وأصلحوا ذات بينكم» أي حقيقة ما بينكم بالمودة وترك النزاع «وأطيعوا الله ورسوله إن كنتم مؤمنين» حقا.'),
(1162, 8, 2, '«إنما المؤمنون» الكاملون الإيمان «الذين إذا ذكر الله» أي وعيده «وجلت» خافت «قلوبهم وإذا تليت عليهم آياته زادتهم إيمانا» تصديقا «وعلى ربهم يتوكلون» به يثقون لا بغيره.'),
(1163, 8, 3, '«الذين يقيمون الصلاة» يأتون بها بحقوقها «ومما رزقناهم» أعطيناهم «ينفقون» في طاعة الله.'),
(1164, 8, 4, '«أولئك» الموصوفون بما ذكر «هم المؤمنون حقا» صدقا بلا شك «لهم درجاتٌ» منازل في الجنة «عند ربهم ومغفرة ورزق كريم» في الجنة.'),
(1165, 8, 5, '«كما أخرجك ربُّك من بيتك بالحق» متعلق بأخرج «وإن فريقا من المؤمنين لكارهون» الخروج والجملة حال من كاف أخرجك وكما خبر مبتدأ محذوف أي هذه الحال في كراهتهم لها مثل إخراجك في حال كراهتهم وقد كان خيرا لهم فكذلك أيضا وذلك أن أبا سفيان قدم بعير من الشام فخرج النبي صلى الله عليه وسلم وأصحابه ليغنموها فعلمت قريش فخرج أبو جهل ومقاتلوا مكة ليذبُّوا عنها وهم النفير وأخذ أبو سفيان بالعير طريق الساحل فنجت فقيل لأبي جهل ارجع فأبى وسار إلى بدر. فشاور النبيُّ صلى الله عليه وسلم أصحابه وقال إن الله وعدني إحدى الطائفتين فوافقوه على قتال النفير وكره بعضهم ذلك وقالوا لم نستعدَّ له كما قال تعالى.'),
(1166, 8, 6, '«يجادلونك في الحق» القتال «بعد ما تبيَّن» ظهر لهم «كأنما يُساقون إلى الموت وهم ينظرون» إليه عيانا في كراهتهم له.'),
(1167, 8, 7, '«و» اذكر «إذ يعدكم الله إحدى الطائفتين» العير أو النفير «أنها لكم وتودُّون» تريدون «أن غير ذات الشوكة» أي البأس والسلاح وهي العير «تكون لكم» لقلة عددها ومدَدها بخلاف النفير «ويريد الله أن يُحق الحق» يظهر «بكلماته» السابقة بظهور الإسلام «ويقطع دابر الكافرين» آخرهم بالاستئصال فأمركم بقتال النفير.'),
(1168, 8, 8, '«ليُحق الحق ويبطل» يمحق «الباطل» الكفر «ولو كره المجرمون» المشركون ذلك.'),
(1169, 8, 9, 'اذكر «إذ تستغيثون ربَّكم» تطلبون منه الغوث بالنصر عليهم «فاستجاب لكم أني» أي بأني «مُمدُّكم» معينكم «بألف من الملائكة مردفين» متتابعين يردف بعضهم بعضا وعدهم بها أوَّلا ثم صارت ثلاثة آلاف ثم خمسة كما في آل عمران وقرئ بآلُف كأفُلس جمع.'),
(1170, 8, 10, '«وما جعله الله» أي الإمداد «إلا بشرى ولتطمئن به قلوبكم وما النصر إلا من عند الله إن الله عزيز حكيم».'),
(1171, 8, 11, 'اذكر «إذ يُغشِّيكم النعاس أمنة» أمنا مما حصل لكم من الخوف «منه» تعالى «وَيُنَزِّلُ عليكم من السماء ماء ليطهركم به» من الأحداث والجنابات «ويذهب عنكم رجز الشيطان» وسوسته إليكم بأنكم لو كنتم على الحق ما كنتم ظمأى محدثين والمشركون على الماء «وليربط» يحبس «على قلوبكم» باليقين والصبر «ويثبِّت به الأقدام» أن تسوخ في الرمل.'),
(1172, 8, 12, '«إذ يوحي ربك إلى الملائكة» الذين أمد بهم المسلمين «أني» أي بأني «معكم» بالعون والنصر «فثبِّتوا الذين آمنوا» بالإعانة والتبشير «سألقي في قلوب الذين كفروا الرعب» الخوف «فاضربوا فوق الأعناق» أي الرءوس «واضربوا منهم كل بنان» أي أطراف اليدين والرجلين فكان الرجل يقصد ضرب رقبة الكافر فتسقط قبل أن يصل إليه سيفه ورماهم صلى الله عليه وسلم بقبضة من الحصى فلم يبق مشرك إلا دخل في عينيه منها شيء فهزموا.'),
(1173, 8, 13, '«ذلك» العذاب الواقع بهم «بأنهم شاقوا» خالفوا «الله ورسوله ومن يشاقق الله ورسوله فإن الله شديد العقاب» له.'),
(1174, 8, 14, '«ذلكم» العذاب «فذوقوه» أيها الكفار في الدنيا «وأن للكافرين» في الآخرة «عذاب النار».'),
(1175, 8, 15, '«يا أيها الذين آمنوا إذا لقيتم الذين كفروا زحفا» أي مجتمعين كأنهم لكثرتهم يزحفون «فلا تولُّوهم الأدبار» منهزمين.'),
(1176, 8, 16, '«ومن يولهم يومئذ» أي يوم لقائهم «دُبُرَهُ إلا متحرفا» منعطفا «لقتال» بأن يريهم الغرَّة مكيدة وهو يريد الكرَّة «أو متحيزا» منضما «إلى فئة» جماعة من المسلمين يستنجد بها «فقد باء» رجع «بغضب من الله ومأواه جنهم وبئس المصير» المرجع هي وهذا مخصوص بما لم يزد الكفار على الضعف.'),
(1177, 8, 17, '«فلم تقتلوهم» ببدر بقوتكم «ولكنَّ الله قتلهم» بنصره إيّاكم «وما رميت» يا محمد لأعين القوم «إذ رميت» بالحصى لأن كفا من الحصى لا يملأ عيون الجيش الكثير برمية بشر «ولكنَّ الله رمى» بإيصال ذلك إليهم فعل ذلك ليقهر الكافرين «وليبلي المؤمنين منه بلاءً» عطاء «حسنا» هو الغنيمة «إن الله سميع» لأقوالهم «عليم» بأحوالهم.'),
(1178, 8, 18, '«ذلكم» الإبلاء حق «وأن الله موهنُ» مضعف «كيد الكافرين».'),
(1179, 8, 19, '«إن تستفتحوا» أيها الكفار إن تطلبوا الفتح أي القضاء حيث قال أبو جهل منكم: اللهم أينا كان أقطع للرحمن وأتانا بما لا نعرف فأحنه الغداة أي أهلكه «فقد جاءكم الفتح» القضاء بهلاك من هو كذلك وهو أبو جهل ومن قتل معه دون النبي صلى الله عليه وسلم والمؤمنين «وإن تنتهوا» عن الكفر والحرب «فهو خير لكم وإن تعودوا» لقتال النبي صلى الله عليه وسلم «نعد» لنصره عليكم «ولن تغني» تدفع «عنكم فئتكم» جماعاتكم «شيئا ولو كثرت وإنَّ الله مع المؤمنين» بكسر إن استئنافا وفتحها على تقدير اللام.'),
(1180, 8, 20, '«يا أيها الذين آمنوا أطيعوا الله ورسوله ولا تولَّوْا» تعرضوا «عنه» بمخالفة أمره «وأنتم تسمعون» القرآن والمواعظ.'),
(1181, 8, 21, '«ولا تكونوا كالذين قالوا سمعنا وهم لا يسمعون» سماع تدبر واتعاظ وهم المنافقون أو المشركون.'),
(1182, 8, 22, '«إن شرَّ الدواب عند الله الصمٌّ» عن سماع الحق «البكم» عن النطق به «الذين لا يعقلونـ» ـه.'),
(1183, 8, 23, '«ولو علم الله فيهم خيرا» صلاحا بسماع الحق «لأسمعهم» سماع تفهم «ولو أسمعهم» فرضا وقد علم أن لا خير فيهم «لتوَّلوا» عنه «وهم معرضون» عن قبوله عنادا وجحودا.'),
(1184, 8, 24, '«يا أيها الذين آمنوا استجيبوا لله وللرسول» بالطاعة «إذا دعاكم لما يحييكم» من أمر الدين أنه سبب الحياة الأبدية «واعلموا أن الله يحول بين المرء وقلبه» فلا يستطيع أن يؤمن أو يكفر إلا بإرادته «وأنه إليه تُحشرون» فيجازيكم بأعمالكم.'),
(1185, 8, 25, '«واتقوا فتنة» إن أصابتكم «لا تصيبنَّ الذين ظلموا منكم خاصة» بل تعمهم وغيرهم واتقاؤها بإنكار موجبها من المنكر «واعلموا أن الله شديد العقاب» لمن خالفه.'),
(1186, 8, 26, '«واذكروا إذ أنتم قليل مستضعَفون في الأرض» أرض مكة «تخافون أن يتخطَّفكم الناس» يأخذكم الكفار بسرعة «فآواكم» إلى المدينة «وأيَّدكم» قوَّاكم «بنصره» يوم بدر بالملائكة «ورزقكم من الطيبات» الغنائم «لعلكم تشكرون» نعمه.'),
(1187, 8, 27, 'ونزل في أبي لبابة مروان بن عبد المنذر وقد بعثه رسول الله صلى الله عليه وسلم إلى قريظة لينزلوا على حكمه فاستشاروه فأشار إليهم أنه الذبح لأن عياله وماله فيهم «يا أيها الذين آمنوا لا تخونوا الله والرسول و تخونوا أماناتكم» ما ائتمنتم عليه من الدين وغيره «وأنتم تعلمون».'),
(1188, 8, 28, '«واعلموا أنما أموالكم وأولادُكم فتنهٌ» لكم صادة عن أمور الآخرة «وأن الله عنده أجر عظيم» فلا تفوِّتوه بمراعاة الأموال والأولاد والخيانة لأجلهم، ونزل في توبته.'),
(1189, 8, 29, '«يا أيها الذين آمنوا إن تتقوا الله» بالإنابة وغيرها «يجعل لكم فرقانا» بينكم وبين ما تخافون فتنجون «ويكفِّر عنكم سيئآتكم ويغفر لكم» ذنوبكم «والله ذو الفضل العظيم».'),
(1190, 8, 30, '«و» اذكر يا محمد «إذ يمكر بك الذين كفروا» وقد اجتمعوا للمشاورة في شأنك بدار الندوة «ليثبتوك» يوثقوك ويحبسوك «أو يقتلوك» كلهم قَتلَة رجل واحد «أو يخرجوك» من مكة «ويمكرون» بك «ويمكرُ الله» بهم بتدبير أمرك بأن أوحى إليك ما دبروه وأمرك بالخروج «والله خير الماكرين» أعلمهم به.'),
(1191, 8, 31, '«وإذا تُتلى عليهم آياتنا» القرآن «قالوا قد سمعنا لو نشاء لقلنا مثل هذا» قاله النضر بن الحارث لأنه كان يأتي الحيرة يتجر فيشتري كتب أخبار الأعاجم ويحدث بها أهل مكة «إن» ما «هذا» القرآن «إلا أساطير» أكاذيب «الأولين».'),
(1192, 8, 32, '«وإذ قالوا اللهم إن كان هذا» الذي يقرؤه محمد «هو الحقَّ» المنزل «من عندك فأمطر علينا حجارة من السماء أو ائتنا بعذاب أليم» مؤلم على إنكاره قاله النصر، وغيره استهزاءً وإيهاما أنه على بصيرة وجزم ببطلانه.'),
(1193, 8, 33, 'قال تعالى: «وما كان الله ليعذَّبهم» بما سألوه «وأنت فيهم» لأن العذاب إذا نزل عَمَّ ولم تعذَّب أمة إلا بعد خروج نبيها والمؤمنين منها «وما كان الله معذبَهم وهم يستغفرون» حيث يقولون في طوافهم: غفرانك، غفرانك. وقيل أهم المؤمنون المستضعفون فيهم كما قال تعالى: (لو تزيَّلوا لعذبنا الذين كفروا منهم عذابا أليما).'),
(1194, 8, 34, '«ومالهم أ» ن «لا يعذبهم الله» بالسيف بعد خروجك والمستضعفين وعلى القول الأول هي ناسخة لما قبلها وقد عذَّبهم الله ببدر وغيره «وهم يصدُّون» يمنعون النبي صلى الله عليه وسلم والمسلمين «عن المسجد الحرام» أن يطوفوا به «وما كانوا أولياءه» كما زعموا «إن» ما «أولياؤه إلا المتقون ولكن أكثرهم لا يعلمون» أن لا ولاية لهم عليه.'),
(1195, 8, 35, '«وما كان صلاتُهم عند البيت إلا مُكاءً» صفيرا «وتصديةً» تصفيقا أي جعلوا ذلك موضع صلاتهم التي أمروا بها «فذوقوا العذاب» ببدر «بما كنتم تكفرون».'),
(1196, 8, 36, '«إن الذين كفروا ينفقون أموالهم» في حرب النبي صلى الله عليه وسلم «ليصدوا عن سبيل الله فسينفقونها ثم تكون» في عاقبة الأمر «عليهم حسرة» ندامة لفواتها وفوات ما قصدوه «ثم يغلبون» في الدنيا «والذين كفروا» منهم «إلى جهنم» في الآخرة «يحشرون» يساقون.'),
(1197, 8, 37, '«ليميزَ» متعلق بتكون بالتخفيف والشديد أي يفصل «الله الخبيث» الكافر «من الطيب» المؤمن «ويجعل الخبيث بعضه على بعض فيَرْكُمَهُ جميعا» يجمعه متراكما بعضه على بعض «فيجعله في جهنَّم أولئك هم الخاسرون».'),
(1198, 8, 38, '«قل للذين كفروا» كأبي سفيان وأصحابه «إن ينتهوا» عن الكفر وقتال النبي صلى الله عليه وسلم «يُغفر لهم ما قد سلف» من أعمالهم «وإن يعودوا» إلى قتال «فقد مضت سنَّةُ الأولين» أي سنتنا فيهم بالإهلاك فكذا نفعل بهم.'),
(1199, 8, 39, '«وقاتلوهم حتى لا تكون» توجد «فتنةٌ» شرك «ويكون الدِّين كله لله» وحده ولا يعبد غيره «فإن انتهوا» عن الكفر «فإن الله بما يعملون بصير» فيجازيهم به.'),
(1200, 8, 40, '«وإن تولَّوا» عن الإيمان «فاعلموا أن الله مولاكم» ناصركم ومتولي أموركم «نعم المولى» هو «ونعم النصير» أي الناصر لكم.'),
(1201, 8, 41, '«واعلموا أنما غنمتم» أخذتم من الكفار قهرا «من شيء فأن لله خمسه» يأمر فيه بما يشاء «وللرسول ولذي القربى» قرابة النبي صلى الله عليه وسلم من بني هاشم وبني المطلب «واليتامى» أطفال المسلمين الذين هلك آباؤهم وهم فقراء «والمساكين» ذوي الحاجة من المسلمين «وابن السبيل» المنقطع في سفره من المسلمين، أي يستحقه النبي صلى الله عليه وسلم والأصناف الأربعة على ما كان يقسمه من أن لكلٍ خُمسَ الخمس، والأخماس الأربعة الباقية للغانمين «إن كنتم آمنتم بالله» فاعملوا ذلك «وما» عطف على بالله «أنزلنا على عبدنا» محمد صلى الله عليه وسلم من الملائكة والآيات «يوم الفرقان» أي يوم بدر الفارق بين الحق والباطل «يوم التقى الجمعان» المسلمون والكفار «والله على كل شيء قدير» ومنه نصركم مع قلتكم وكثرتهم.'),
(1202, 8, 42, '«إذ» بدل من يوم «أنتم» كائنون «بالعُدوة الدنيا» القربى من المدينة وهى بضم العين وكسرها جانب الوادي «وهم بالعدوة القصوى» البعدى منها «والركب» العير كائنون بمكان «أسفل منكم» مما يلي البحر «ولو تواعدتم» أنتم والنفير للقتال «لاختلفتم في الميعاد ولكن» جمعكم بغير ميعاد «ليقضى الله أمرا كان مفعولا» في علمه وهو نصر الإسلام وَمَحْقُ الكفر فعل ذلك «ليهلك» يكفر «من هلك عن بينةٍ» أي بعد حجة ظاهرة قامت عليه وهي نصر المؤمنين مع قلتهم على الجيش الكثير «ويحيى» يؤمن «من حيَّ عن بينة وإن الله لسميع عليم».'),
(1203, 8, 43, 'اذكر «إذ يريكهُم الله في منامك» أي نومك «قليلا» فأخبرت به أصحابك فسروا «ولو أراكهم كثيرا لفشلتم» جبنتم «ولتنازعتم» اختلفتم «في الأمر» أمر القتال «ولكن الله سلَّمـ» ـكم من الفشل والتنازع «إنه عليم بذات الصدور» بما في القلوب.'),
(1204, 8, 44, '«وإذ يريكموهم» أيها المؤمنون «إذ التقيتم في أعينكم قليلا» نحو سبعين أو مائة وهم ألف لتقدموا عليهم «ويقللكم في أعينهم» ليقدموا ولا يرجعوا عن قتالكم وهذا قبل التحام الحرب، فلما التحم أراهم إياهم مثليهم كما في آل عمران «ليقضيَ الله أمرا كان مفعولا وإلى الله ترجع» تصير «الأمور».'),
(1205, 8, 45, '«يا أيها الذين آمنوا إذا لقيتم فئة» جماعة كافرة «فاثبتوا» لقتالهم ولا تنهزموا «واذكروا الله كثيرا» ادعوه بالنصر «لعلكم تفلحون» تفوزون.'),
(1206, 8, 46, '«وأطيعوا الله ورسوله ولا تنازعوا» تختلفوا فيما بينكم «فتفشلوا» تجبنوا «وتذهب ريحكم» قوتكم ودولتكم «واصبروا إن الله مع الصابرين» بالنصر والعون.'),
(1207, 8, 47, '«ولا تكونوا كالذين خرجوا من ديارهم» ليمنعوا غيرهم ولم يرجعوا بعد نجاتها «بطرا ورئاء الناس» حيث قالوا لا نرجع حتى نشرب الخمر وننحر الجزور وتضرب علينا القيان ببدر فيتسامع بذلك الناس «ويصدون» الناس «عن سبيل الله والله بما يعملون» بالياء والتاء «محيط» علما فيجازيهم به.'),
(1208, 8, 48, '«و» اذكر «إذ زيَّن لهم الشيطان» إبليس «أعمالهم» بأن شجعهم على لقاء المسلمين لما خافوا الخروج من أعدائهم بني بكر «وقال» لهم «لا غالب لكم اليوم من الناس وإني جار لكم» من كنانة وكان أتاهم في صورة سراقة بن مالك سيد تلك الناحية «فلما تراءت» التقت «الفئتان» المسلمة والكافرة ورأى الملائكة يده في يد الحارث بن هشام «نكص» رجع «على عقبيه» هاربا «وقال» لما قالوا له أتخذلنا على هذه الحال: «إني بريء منكم» من جواركم «إني أرى ما لا ترون» من الملائكة «إني أخاف الله» أن يهلكني «والله شديد العقاب».'),
(1209, 8, 49, '«إذ يقول المنافقون والذين في قلوبهم مرض» ضعف اعتقاد «غرَّ هؤلاء» أي المسلمين «دينُهم» إذ خرجوا مع قتلهم يقاتلون الجمع الكثير توهما أنهم ينصرون بسببه قال تعال في جوابهم: «ومن يتوكل على الله» يثق به يغلب «فإن الله عزيز» غالب على أمره «حكيم» في صنعه.'),
(1210, 8, 50, '«ولو تَرى» يا محمد «إذ يتوفى» بالياء والتاء «الذين كفروا الملائكة يضربون» حال «وجوههم وأدبارهم» بمقامعَ من حديد «و» يقولون لهم «ذوقوا عذاب الحريق» أي النار وجواب لو: لرأيت أمرا عظيما.'),
(1211, 8, 51, '«ذلك» التعذيب «بما قدَّمت أيديكم» عبَّر بها دون غيرها لأن أكثر الأفعال تزاول بها «وأن الله ليس بظلام» أي بذي ظلم «للعبيد» فيعذِّبهم بغير ذنب.'),
(1212, 8, 52, 'دأبُ هؤلاء «كدأب» كعادة «آل فرعون والذين من قبلهم كفروا بآيات الله فأخذهم الله» بالعقاب «بذنوبهم» جملة كفروا وما بعدها مفسِّرة لما قبلها «إن الله قويٌ» على ما يريده «شديد العقاب».'),
(1213, 8, 53, '«ذلك» أي تعذيب الكفرة «بأن» أي بسبب أن «الله لم يكُ مغيِّرا نعمة أنعمها على قوم» مبدلا لها بالنقمة «حتَّى يغيِّروا ما بأنفسهم» يبدلوا نعمتهم كفرا كتبديل كفار مكة إطعامهم من جوع وأمنُهم من خوف وبعْث النبي صلى الله عليه وسلم إليهم بالكفر والصد عن سبيل الله وقتال المؤمنين «وأن الله سميع عليم».'),
(1214, 8, 54, '«كدأب آل فرعون والذين من قبلهم كذَّبوا بآيات ربهم فأهلكناهم بذنوبهم وأغرقنا آل فرعون» قومه معه «وكلّ» من الأمم المكذبة «كانوا ظالمين».'),
(1215, 8, 55, 'ونزل في قريظة: «إن شرَّ الدواب عند الله الذين كفروا فهم لا يؤمنون».'),
(1216, 8, 56, '«الذين عاهدت منهم» ألا يعينوا المشركين «ثم ينقضون عهدهم في كل مرة» عاهدوا فيها «وهم لا يتقون» الله في غدرهم.'),
(1217, 8, 57, '«فإما» فيه إدغام نون إن الشرطية في ما المزيدة «تثقفنَّهم» تجدنهم «في الحرب فشرِّد» فرق «بهم من خلفهم» من المحاربين بالتنكيل بهم والعقوبة «لعلَّهم» أي الذين خلفهم «يذَّكرون» يتعظون بهم.'),
(1218, 8, 58, '«وإما تخافن من قوم» عاهدوك «خيانة» في عهد بأمارةٍ تلوح لك «فانبذ» اطرح عهدهم «إليهم على سواء» حال أي مستويا أنت وهم في العلم بنقض العهد بأن تعلمهم به لئلا يتهموك بالغدر «إن الله لا يحب الخائنين».'),
(1219, 8, 59, 'ونزل فيمن أفلت يوم بدر «ولا تحسبنَّ» يا محمد «الذين كفروا سبقوا» الله أي فأتوه «إنهم لا يعجزون» لا يفوتونه وفي قراءة بالتحتانية فالمفعول الأول محذوف أي أنفسهم وفي أخرى بفتح إن على تقدير اللام.'),
(1220, 8, 60, '(وأعدوا لهم) لقتالهم (ما استطعتم من قوة) قال صلى الله عليه وسلم "" هي الرمي "" رواه مسلم (ومن رباط الخيل) مصدر بمعنى حبسها في سبيل الله (ترهبون) تخوفون (به عدو الله وعدوكم) أي كفار مكة (وآخرين من دونهم) أي غيرهم وهم المنافقون أو اليهود (لا تعلمونهم الله يعلمهم وما تنفقوا من شيء في سبيل الله يوف إليكم) جزاؤه (وأنتم لا تظلمون) تنقصون منه شيئا.'),
(1221, 8, 61, '«وإن جنحوا» مالوا «للسَّلْم» بكسر السين وفتحها: الصلح «فاجنح لها» وعاهدهم، وقال ابن عباس: هذا منسوخ بآية السيف وقال مجاهد: مخصوص بأهل الكتاب إذ نزلت في بني قريظة «وتوكل على الله» ثق به «إنه هو السميع» للقول «العليم» بالفعل.'),
(1222, 8, 62, '«وإن يريدوا أن يخدعوك» بالصلح ليستعدوا لك «فإن حسبك» كافيك «اللهُ هو الذي أيَّدك بنصره وبالمؤمنين».'),
(1223, 8, 63, '«وألَّف» جمع «بين قلوبهم» بعد الإحن «لو أنفقت ما في الأرض جميعا ما ألَّفت بين قلوبهم ولكنَّ الله ألف بينهم» بقدرته «إنه عزيز» غالب على أمره «حكيم» لا يخرج شيء عن حكمته.'),
(1224, 8, 64, '«يا أيها النبي حسبُك اللهُ و» حسبك «من اتبعك من المؤمنين».'),
(1225, 8, 65, '«يا أيها النبي حرِّض» حث «المؤمنين على القتال» للكفار «إن يكن منكم عشرون صابرون يغلبوا مائتين» منهم «وإنْ يكن» بالياء والتاء «منكم مائة يغلبوا ألفا من الذين كفروا بأنهم» أي بسبب أنهم «قوم لا يفقهون» وهذا خبر بمعنى الأمر أي ليقاتل العشرون منكم المائتين والمائة الألف ويثبتوا لهم ثم نُسخ لما كثروا بقوله.'),
(1226, 8, 66, '«الآن خفف الله عنكم وعلم أن فيكم ضُعفا» بضم الضاد وفتحها عن قتال عشرة أمثالكم «فإن يكن» بالياء والتاء «منكم مائة صابرة يغلبوا مائتين» منهم «وإن يكن منكم ألف يغلبوا ألفين بإذن الله» بإرادته وهو خبر بمعنى الأمر أي لتقاتلوا مثليكم وتثبتوا لهم «والله مع الصابرين» بعونه.'),
(1227, 8, 67, 'ونزل لما أخذوا الفداء من أسرى بدر «ما كان لنبي أن تكون» بالتاء والياء «له أسرى حتى يثخن في الأرض» يبالغ في قتل الكفار «تريدون» أيها المؤمنون «عَرَض الدنيا» حطامها بأخذ الفداء «والله يريد» لكم «الآخرة» أي ثوابها بقتلهم «والله عزيز حكيم» وهذا منسوخ بقوله (فإما منّا بعد وإما فداءً).'),
(1228, 8, 68, '«لولا كتاب من الله سبق» بإحلال الغنائم والأسرى لكم «لمسَّكم فيما أخذتم» من الفداء «عذاب عظيم».'),
(1229, 8, 69, '«فكلوا مما غنمتم حلالا طيبا واتقوا الله إن الله غفور رحيم».'),
(1230, 8, 70, 'يا أيها النبي قل لمن في أيديكم من الأسارى» وفي قراءة الأسرى «إن يعلم الله في قلوبكم خيرا» إيمانا وإخلاصا «يؤتكم خيرا مما أخذ منكم» من الفداء بأن يضعِّفه لكم في الدنيا ويثيبكم في الآخرة «ويغفر لكم» ذنوبكم «والله غفور رحيم».'),
(1231, 8, 71, '«وإن يريدوا» أي الأسرى «خيانتك» بما أظهروا من القول «فقد خانوا الله من قبل» قبل بدر بالكفر «فأمكن منهم» ببدر قتلا وأسرا فليتوقعوا مثل ذلك إن عادوا «والله عليم» بخلقه «حكيم» في صنعه.'),
(1232, 8, 72, '«إن الذين آمنوا وهاجروا وجاهدوا بأموالهم وأنفسهم في سبيل الله» وهم المهاجرون «والذين آوَوْا» النبي صلى الله عليه وسلم «ونصروا» وهم الأنصار «أولئك بعضهم أولياء بعض» في النصرة والإرث «والذين آمنوا ولم يُهاجروا مالكم من ولايَتِهِمْ» بكسر الواو وفتحها «من شيء» فلا إرث بينكم وبينهم ولا نصيب لهم في الغنيمة «حتى يهاجروا» وهذا منسوخ بآخر السورة «وإن استنصروكم في الدين فعليكم النصر» لهم على الكفار «إلا على قوم بينكم وبينهم ميثاق» عهد فلا تنصروهم عليهم وتنقضوا عهدهم «والله بما تعملون بصير».'),
(1233, 8, 73, '«والذين كفروا بعضهم أولياء بعض» في النصرة والإرث فلا إرث بينكم وبينهم «إلا تفعلوه» أي تولي المسلمين وقمع الكفار «تكن فتنة في الأرض وفساد كبير» بقوة الكفر وضعف الإسلام.'),
(1234, 8, 74, '«والذين آمنوا وهاجروا وجاهدوا في سبيل الله والذين آوَوْا ونصروا أولئك هم المؤمنون حقا لهم مغفرة ورزق كريم» في الجنة.'),
(1235, 8, 75, '«والذين آمنوا من بعد» أي بعد السابقين إلى الإيمان والهجرة «وهاجروا وجاهدوا معكم فأولئك منكم» أيها المهاجرون والأنصار «وأولو الأرحام» ذوو القرابات «بعضهم أوْلى ببعض» في الإرث من التوارث في الإيمان والهجرة المذكورة في الآية السابقة «في كتاب الله» اللوح المحفوظ «إن الله بكل شيء عليم» ومنه حكمة الميراث.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'هذه «براءة من الله ورسوله» وأصله «إلى الذين عاهدتم من المشركين» عهدا مطلقا أو دون أربعة أشهر أو فوقها ونص العهد بما يذكر في قوله.'),
(1237, 9, 2, '«فسيحوا» سيروا آمنين أيها المشركون «في الأرض أربعة أشهر» أولها شوال بدليل ما سيأتي ولا أمان لكم بعدها «واعلموا أنكم غيرُ معجزي الله» أي فائتي عذابه «وأنَّ الله مخزي الكافرين» مذلُّهم في الدنيا بالقتل والأخرى بالنار.'),
(1238, 9, 3, '(وأذان) إعلام (من الله ورسوله إلى الناس يوم الحج الأكبر) يوم النحر (أن) أي بأن (الله برئ من المشركين) وعهودهم (ورسوله) برئ أيضا "" وقد بعث النبي صلى الله عليه وسلم عليا من السنة وهي سنة تسع فأذن يوم النحر بمنى بهذه الآيات وأن لا يحج بعد العام مشرك ولا يطوف بالبيت عريان "" رواه البخاري (فإن تبتم) من الكفر (فهو خير لكم وإن توليتم) عن الإيمان (فاعلموا أنكم غير معجزي الله وبشر) أخبر (الذين كفروا بعذاب أليم) مؤلم وهو القتل والأسر في الدنيا والنار في الآخرة.'),
(1239, 9, 4, '«إلا الذين عاهدتم من المشركين ثم لم ينقصوكم شيئا» من شروط العهد «ولم يظاهروا» يعاونوا «عليكم أحدا» من الكفار «فأتموا إليهم عهدهم إلى» انقضاء «مدتهم» التي عاهدتم عليها «إن الله يحب المتقين» بإتمام العهود.'),
(1240, 9, 5, '«فإذا انسلخ» خرج «الأشهر الحرم» وهي آخر مدة التأجيل «فاقتلوا المشركين حيث وجدتموهم» في حل أو حرم «وخذوهم» بالأسر «واحصروهم» في القلاع والحصون حتى يضطروا إلى القتل أو الإسلام «واقعدوا لهم كلَّ مرصد» طريق يسلكونه ونصب كل على نزع الخافض «فإن تابوا» من الكفر «وأقاموا الصلاة وآتوا الزكاة فخلُّوا سبيلهم» ولا تتعرضوا لهم «إن الله غفور رحيم» لمن تاب.'),
(1241, 9, 6, '«وإن أحد من المشركين» مرفوع بفعل يفسره «استجارك» استأمنك من القتل «فأجره» أمِّنه «حتى يسمع كلام الله» القرآن «ثم أبلغه مأمنه» وهو دار قومه إن لم يؤمن لينظر في أمره «ذلك» المذكور «بأنهم قوم لا يعلمون» دين الله فلا بد لهم من سماع القرآن ليعلموا.'),
(1242, 9, 7, '«كيف» أي لا «يكون للمشركين عهد عند الله وعند رسوله» وهم كافرون بالله ورسوله غادرون «إلا الذين عاهدتم عند المسجد الحرام» يوم الحديبية وهم قريش المستثنون من قبل «فما استقاموا لكم» أقاموا على العهد ولم ينقضوه «فاستقيموا لهم» على الوفاء به وما شرطية «إن الله يحب المتقين» وقد استقام النبي صلى الله عليه وسلم على عهدهم حتى نقضوا بإعانة بني بكر على خزاعة.'),
(1243, 9, 8, '«كيف» يكون لهم عهد «وإن يظهروا عليكم» يظفروا بكم «لا يرقبوا» يراعوا «فيكم إلاّ» قرابة «ولا ذمة» عهدا بل يؤذوكم ما استطاعوا وجملة الشرط حال «يرضونكم بأفواههم» بكلامهم الحسن «وتأبى قلوبهم» الوفاء به «وأكثرهم فاسقون» ناقضون للعهد.'),
(1244, 9, 9, '«اشترَوا بآيات الله» القرآن «ثمنا قليلا» من الدنيا أي تركوا اتباعها للشهوات والهوى «فصدُّوا عن سبيله» دينه «إنهم ساء» بئس «ما كانوا يعملونـ» ـه عملهم هذا.'),
(1245, 9, 10, '«لا يرقُبون في مؤمن إلاّ ولا ذمَّة وأولئك هم المعتدون».'),
(1246, 9, 11, '«فإن تابوا وأقاموا الصلاة وآتوا الزكاة فإخوانكم» أي فهم إخوانكم «في الدين ونفصِّل» نبين «الآيات لقوم يعلمون» يتدبرون.'),
(1247, 9, 12, '«وإن نكثوا» نقضوا «أيمانهم» مواثيقهم «من بعد عهدهم وطعنوا في دينكم» عابوه «فقاتلوا أئمة الكفر» رؤساءه، فيه وضع الظاهر موضع المضمر «إنهم لا أيمان» عهود «لهم» وفي قراءة بالكسر «لعلهم ينتهون» عن الكفر.'),
(1248, 9, 13, '«ألا» للتحضيض «تقاتلون قوما نكثوا» نقضوا «أيمانهم» عهودهم «وهمُّوا بإخراج الرسول» من مكة لما تشاوروا فيه بدار الندوة «وهم بدءوكم» بالقتال «أوَّل مرَّة» حيث قاتلوا خزاعة حلفاءكم مع بني بكر فما يمنعكم أن تقاتلوهم «أتخشونهم» أتخافونهم «فاللهُ أحق أن تخشوه» في ترك قتالهم «إن كنتم مؤمنين».'),
(1249, 9, 14, '«قاتلوهم يعذبهم الله» يقتلهم «بأيديكم ويخزهم» يذلهم بالأسر والقهر «وينصركم عليهم ويشف صدور قوم مؤمنين» بما فُعل بهم هم بنو خزاعة.'),
(1250, 9, 15, '«ويذُهب غيظ قلوبهم» كربها «ويتوب الله على من يشاء» بالرجوع إلى الإسلام كأبي سفيان «والله عليم حكيم».'),
(1251, 9, 16, '«أم» بمعنى همزة الإنكار «حسبتم أن تُتركوا ولما» لم «يعلم الله» علم ظهور «الذين جاهدوا منكم» بإخلاص «ولم يتخذوا من دون الله ولا رسوله ولا المؤمنين وليجة» بطانة وأولياء، المعنى ولم يظهر المخلصون وهم الموصوفون بما ذكر من غيرهم «والله خبير بما تعملون».'),
(1252, 9, 17, '«ما كان للمشركين أن يعمُروا مَسْجِدَ الله» بالإفراد والجمع بدخوله والقعود فيه «شاهدين على أنفسهم بالكفر أولئك حبطت» بطلت «أعمالهم» لعدم شرطها «وفي النار هم خالدون».'),
(1253, 9, 18, '«إنما يعمُر مساجد الله من آمن بالله واليوم الآخر وأقام الصلاة وآتى الزكاة ولم يخش» أحدا «إلا الله فعسى أولئك أن يكونوا من المهتدين».'),
(1254, 9, 19, '«أجعلتم سقاية الحاج وعمارة المسجد الحرام» أي أهل ذلك «كمن آمن بالله واليوم الآخر وجاهد في سبيل الله لا يستوون عند الله» في الفضل «والله لا يهدي القوم الظالمين» الكافرين، نزلت ردا على من قال ذلك وهو العباس أو غيره.'),
(1255, 9, 20, '«الذين آمنوا وهاجروا وجاهدوا في سبيل الله بأموالهم وأنفسهم أعظم درجة» رتبة «عند الله» من غيرهم «وأولئك هم الفائزون» الظافرون بالخير.'),
(1256, 9, 21, '«يبشرهم ربهم برحمة منه ورضوان وجنات لهم فيها نعيم مقيم» دائم.'),
(1257, 9, 22, '«خالدين» حال مقدرة «فيها أبدا إن الله عنده أجر عظيم».'),
(1258, 9, 23, 'ونزل فيمن ترك الهجرة لأجل أهله وتجارته: «يا أيها الذين آمنوا لا تتخذوا آباءكم وإخوانكم أولياء إن استحبوا» اختاروا «الكفر على الإيمان ومن يتولهم منكم فأولئك هم الظالمون».'),
(1259, 9, 24, '«قل إن كان آباؤكم وأبناؤكم وإخوانكم وأزواجكم وعشيرتكم» أقرباؤكم وفي قراءة عشيرتكم «وأموال اقترفتموها» اكتسبتموها «وتجارة تخشون كسادها» عدم نفادها «ومساكن ترضونها أحبَّ إليكم من الله ورسوله وجهادِ في سبيله» فقعدتم لأجله عن الهجرة والجهاد «فتربَّصوا» انتظروا «حتى يأتي الله بأمره» تهديد لهم «والله لا يهدي القوم الفاسقين».'),
(1260, 9, 25, '«لقد نصركم الله في مواطن» للحرب «كثيرة» كبدر وقريظة والنضير «و» اذكر «يوم حنين» واد بين مكة والطائف أي يوم قتالكم فيه هوازن وذلك في شوّال سنة ثمان «إذ» بدل من يوم «أعجبتكم كثرتكم» فقلتم لن نُغلب اليوم من قلة وكانوا اثني عشر ألفا والكفار أربعة آلاف «فلم تغن عنكم شيئا وضاقت عليكم الأرض بما رحبت» ما مصدرية أي مع رحبها أي سعتها فلم تجدوا مكانا تطمئنون إليه لشدة ما لحقكم من الخوف «ثم وليتم مدبرين» منهزمين وثبت النبي صلى الله عليه وسلم على بغلته البيضاء وليس معه غير العباس وأبو سفيان آخذ بركابه.'),
(1261, 9, 26, '«ثم أنزل الله سكينته» طمأنينته «على رسوله وعلى المؤمنين» فردوا إلى النبي صلى الله عليه وسلم لما ناداهم العباس بإذنه وقاتلوا «وأنزل جنودا لم تروها» ملائكة «وعذَّب الذين كفروا» بالقتل والأسر «وذلك جزاء الكافرين».'),
(1262, 9, 27, '«ثم يتوب الله من بعد ذلك على من يشاء» منهم بالإسلام «والله غفور رحيم».'),
(1263, 9, 28, '«يا أيها الذين آمنوا إنما المشركون نجس» قذر لخبث باطنهم «فلا يقربوا المسجد الحرام» أي لا يدخلوا الحرم «بعد عامهم هذا» عام تسع من الهجرة «وإن خفتم عَيْلةٌ» فقرا بانقطاع تجارتهم عنكم «فسوف يغنيكم الله من فضله إن شاء» وقد أغناهم بالفتوح والجزية «إن الله عليم حكيم».'),
(1264, 9, 29, '«قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر» وإلا لآمنوا بالنبي صلى الله عليه وسلم «ولا يحرِّمون ما حرَّم الله ورسوله» كالخمر «ولا يدينون دين الحق» الثابت الناسخ لغيره من الأديان وهو دين الإسلام'),
(1265, 9, 30, '«وقالت اليهود عُزَيْرٌ ابن الله وقالت النصارى المسيح» عيسى «ابن الله ذلك قولهم بأفواههم» لا مستند لهم عليه بل «يضاهئون» يشابهون به «قول الذين كفروا من قبل» من آبائهم تقليدا لهم «قاتلهم» لعنهم «الله أنَّى» كيف «يُؤفكون» يُصرفون عن الحق مع قيام الدليل.'),
(1266, 9, 31, '«اتَّخذوا أحبارهم» علماء اليهود «ورهبانهم» عبَّاد النصارى «أربابا من دون الله» حيث اتبعوهم في تحليل ما حرم الله وتحريم ما أحل «والمسيح بن مريم وما أمروا» في التوراة والإنجيل «إلا ليعبدوا» أي بأن يعبدوا «إلها واحدا لا إله إلا هو سبحانه» تنزيها له «عما يشركون».'),
(1267, 9, 32, '«يريدون أن يطفئوا نور الله» شرعه وبراهينه «بأفواههم» بأقوالهم فيه «ويأبى الله إلا أن يتم» يظهر «نوره ولو كره الكافرون» ذلك.'),
(1268, 9, 33, '«هو الذي أرسل رسوله» محمدا صلى الله عليه وسلم «بالهدى ودين الحق ليُظهره» يعليه «على الدين كله» جميع الأديان المخالفة له «ولو كره المشركون» ذلك.'),
(1269, 9, 34, '«يا أيها الذين آمنوا إن كثيرا من الأحبار والرهبان ليأكلون» يأخذون «أموال الناس بالباطل» كالرشا في الحكم «ويصدون» الناس «عن سبيل الله» دينه «والذين» مبتدأ «يكنزون الذهب والفضة ولا ينفقونها» أي الكنوز «في سبيل الله» أي لا يأدون منها حقه من الزكاة والخير «فبشرهم» أخبرهم «بعذاب أليم» مؤلم.'),
(1270, 9, 35, '«يوم يحمى عليها في نار جهنم فتكوى» تحرق «بها جباههم وجنوبهم وظهورهم» وتوسع جلودهم حتى توضع عليها كلها ويقال لهم «هذا ما كنزتم لأنفسكم فذوقوا ما كنتم تكنزون» أي جزاءه.'),
(1271, 9, 36, '«إن عدة الشهور» المعتد بها للسنة «عند الله اثنا عشر شهرا في كتاب الله» اللوح المحفوظ «يوم خلق السماوات والأرض منها» أي الشهور «أربعة حرم» محرَّمة ذو القعدة وذو الحجة والمحرم ورجب «ذلك» أي تحريمها «الدين القيم» المستقيم «فلا تظلموا فيهن» أي الأشهر الحرم «أنفسكم» بالمعاصي فإنها فيها أعظم وزرا وقيل في الأشهر كلها «وقاتلوا المشركين كافة» جميعا في كل الشهور «كما يقاتلونكم كافة واعلموا أن الله مع المتقين» بالعون والنصر.'),
(1272, 9, 37, '«إنما النسيء» أي التأخير لحرمة شهر إلى آخر كما كانت الجاهلية تفعله من تأخير حرمة المحرم إذا هلَّ وهم في القتال إلى صفر «زيادة في الكفر» لكفرهم بحكم الله فيه «يُضَلٌ» بضم الياء وفتحها «به الذين كفروا يحلونه» أي النسيء «عاما ويحرمونه عاما ليواطئوا» يوافقوا بتحليل شهر وتحريم آخر بدله «عدة» عدد «ما حرم الله» من الأشهر فلا يزيدوا على تحريم أربعة ولا ينقصوا ولا ينظروا إلى أعيانها «فيحلوا ما حرم الله زُيِّن لهم سوء أعمالهم» فظنوه حسنا «والله لا يهدي القوم الكافرين».'),
(1273, 9, 38, 'ونزل لما دعا النبي صلى الله عليه وسلم الناس إلى غزوة تبوك وكانوا في عسرة وشدة حر فشق عليهم «يا أيها الذين آمنوا مالكم إذا قيل لكم انفروا في سبيل الله اثاقلتم» بإدغام التاء في الأصل في المثلثة واجتلاب همزة الوصل أي تباطأتم وملتم عن الجهاد «إلى الأرض» والقعود فيها والاستفهام للتوبيخ «أرضيتم بالحياة الدنيا» ولذاتها «من الآخرة» أي بدل نعيمها «فما متاع الحياة الدنيا في» جنب متاع «الآخرة إلا قليل» حقير.'),
(1274, 9, 39, '«إلاّ» بإدغام لا في نون إن الشرطية في الموضعين «تنفروا» تخرجوا مع النبي صلى الله عليه وسلم للجهاد «يعذبكم عذابا أليما» مؤلماً «ويستبدل قوما غيركم» أي يأت بهم بدلكم «ولا تضروه» أي الله أو النبيَّ صلى الله عليه وسلم «شيئا» بترك نصره فإن الله ناصر دينه «والله على كل شيء قدير» ومنه نصر دينه ونبيه.'),
(1275, 9, 40, '«إلاّ تنصروه» أي النبيَّ صلى الله عليه وسلم «فقد نصره الله إذ» حين «أخرجه الذين كفروا» من مكة أي الجؤوه إلى الخروج لما أرادوا قتله أو حبسه أو نفيه بدار الندوة «ثاني اثنين» حال أي أحد اثنين والآخر أبو بكر - المعنى نصره الله في مثل تلك الحالة فلا يخذله في غيرها - «إذ» بدل من إذ قبله «هما في الغار» نقب في جبل ثور «إذ» بدل ثان «يقول لصاحبه» أبي بكر وقد قال له لما رأى أقدام المشركين لو نظر أحدهم تحت قدميه لأبصرنا «لا تحزن إن الله معنا» بنصره «فأنزل الله سكينته» طمأنينته «عليه» قيل على النبي * وقيل على أبي بكر «وأيَّده» أي النبي صلى الله عليه وسلم «بجنود لم تروها» ملائكة في الغار ومواطن قتاله «وجعل كلمة الذين كفروا» أي دعوة الشرك «السفلى» المغلوبة «وكلمة الله» أي كلمة الشهادة «هي العليا» الظاهرة الغالبة «والله عزيز» في ملكه «حكيم» في صنعه.'),
(1276, 9, 41, '«انفِروا خفافا وثقالا» نشاطا وغير نشاط، وقيل أقوياء وضعفاء، أو أغنياء وفقراء، وهي منسوخة بآية (ليس على الضعفاء) «وجاهدوا بأموالكم وأنفسكم في سبيل الله ذلكم خير لكم إن كنتم تعلمون» أنه خير لكم فلا تثاقلوا.'),
(1277, 9, 42, 'ونزل في المنافقين الذين تخلفوا «لو كان» ما دعوتهم إليه «عرضا» متاعا من الدنيا «قريبا» سهل المأخذ «وسفرا قاصدا» وسطا «لاتَّبعوك» طلبا للغنيمة «ولكن بعدت عليهم الشُّقَّةُ» المسافة فتخلفوا «وسيحلفون بالله» إذا رجعتم إليهم «لو استطعنا» الخروج «لخرجنا معكم يهلكون أنفسهم» بالحلف الكاذب «والله يعلم إنهم لكاذبون» في قولهم ذلك.'),
(1278, 9, 43, 'وكان صلى الله عليه وسلم أذن لجماعة في التخلف باجتهاد منه، فنزل عتابا له وقدم العفو تطمينا لقلبه «عفا الله عنك لِمَ أذنت لهم» في التخلف وهلا تركتهم «حتى يتبين لك الذين صدقوا» في العذر «وتعلم الكاذبين» فيه.'),
(1279, 9, 44, '«لا يستأذنك الذين يؤمنون بالله واليوم الآخر» في التخُّلف عن «أن يجاهدوا بأموالهم وأنفسهم والله عليم بالمتقين».'),
(1280, 9, 45, '«إنما يستأذنك» في التخلُّف «الذين لا يؤمنون بالله واليوم الآخر وارتابت» شكت «قلوبهم» في الدين «فهم في ريبهم يترددون» يتحيرون.'),
(1281, 9, 46, '«ولو أرادوا الخروج» معك «لأعدوا له عدة» أهبة من الآلة والزاد «ولكن كره الله انبعاثهم» أي لم يرد خروجهم «فثبطهم» كسَّلهم «وقيل» لهم «اقعدوا مع القاعدين» المرضى والنساء والصبيان، أي قدر الله تعالى ذلك.'),
(1282, 9, 47, '«لو خرجوا فيكم ما زادوكم إلا خبالا» فسادا بتخذيل المؤمنين «ولأوضعوا خلالكم» أي أسرعوا بينكم بالمشي بالنميمة «يبغونكم» يطلبون لكم «الفتنة» بإلقاء العداوة «وفيكم سماعون لهم» ما يقولون سماع قبول «والله عليم بالظالمين».'),
(1283, 9, 48, '«لقد ابتغوا» لك «الفتنة من قبل» أول ما قدمت المدينة «وقلَّبوا لك الأمور» أي أحالوا الفكر في كيدك وإبطال دينك «حتى جاء الحق» النصر «وظهر» عَزَّ «أمر الله» دينه «وهم كارهون» له فدخلوا فيه ظاهرا.'),
(1284, 9, 49, '«ومنهم من يقول ائذن لي» في التخلف «ولا تفتنِّي» وهو الجد بن قيس قال له النبي صلى الله عليه وسلم هل لك في جلاد بني الأصفر؟ فقال: إني مغرّم بالنساء وأخشى إن رأيت ُ نساء بني الأصفر ألا أصبر عنهن فأُفتتن، قال تعالى: «ألا في الفتنة سقطوا» بالتخلُّف، وقرئ سقط «وإن جهنم لمحيطة بالكافرين» لا محيص لهم عنها.'),
(1285, 9, 50, '«إن تصبك حسنة» كنصر وغنيمة «تسؤهم وإن تصبك مصيبة» شدة «يقولوا قد أخذنا أمرنا» بالحزم حين تخلفنا «من قبل» قبل هذه المعصية «ويتولَّوا وهم فرحون» بما أصابك.'),
(1286, 9, 51, '«قل» لهم «لن يصيبنا إلا ما كتب الله لنا» إصابته «هو مولانا» ناصرنا ومتولي أمورنا «وعلى الله فيتوكل المؤمنون».'),
(1287, 9, 52, '«قل هل تربصون» فيه حذف إحدى التاءين من الأصل أي تنتظرون أن يقع «بنا إلا إحدى» العاقبتين «الحسنيين» تثنية حسنى تأنيث أحسن: النصر أو الشهادة «ونحن نتربص» ننتظر «بكم أن يصيبكم الله بعذاب من عنده» بقارعة من السماء «أو بأيدينا» بأن يؤذن لنا في قتالكم «فتربصوا» بنا ذلك «إنا معكم متربِّصون» عاقبتكم.'),
(1288, 9, 53, '«قل أنفقوا» في طاعة الله «طوعا أو كرها لن يتقبل منكم» ما أنفقتموه «إنكم كننتم قوما فاسقين» والأمر هنا بمعنى الخبر.'),
(1289, 9, 54, '«وما منعهم أن تُقبل» بالياء والتاء «منهم نفقاتهم إلا أنهم» فاعل وأن تقبل مفعول «كفروا بالله وبرسوله ولا يأتون الصلاة إلا وهم كسالى» متثاقلون «ولا ينفقون إلا وهم كارهون» النفقة لأنهم يعدونها مغرما.'),
(1290, 9, 55, '«فلا تعجبْك أموالهم ولا أولادهم» أي لا تستحسن نعمنا عليهم فهي استدراج «إنَّمَا يريد الله ليعذبهم» أي أن يعذبهم «بها في الحياة الدنيا» بما يلقون في جمعها من المشقة وفيها من المصائب «وتزهَق» تخرج «أنفسهم وهم كافرون» فيعذبهم في الآخرة أشد العذاب.'),
(1291, 9, 56, '«ويحلفون بالله إنهم لمنكم» أي مؤمنون «وما هم منكم ولكنهم قوم يفرقون» يخافون أن تفعلوا بهم كالمشركين فيحلفون تقية.'),
(1292, 9, 57, '«لو يجدون ملجأً» يلجأون إليه «أو مغارات» سراديب «أو مُدَّخَلا» موضعاً يدخلونه «لَوَلَّوْا إليه وهم يجمحون» يسرعون في دخوله والانصراف عنكم إسراعا لا يرده شيء كالفرس الجموح.'),
(1293, 9, 58, '«ومنهم من يلمزك» يعيبك «في» قَسْم «الصدقات فإن أُعطوا منها رضوا وإن لم يُعْطوْا منها إذا هم يسخطون».'),
(1294, 9, 59, '«ولو أنهم رضوا ما آتاهم الله ورسوله» من الغنائم ونحوها «وقالوا حسبنا» كافينا «الله سيؤتينا الله من فضله ورسوله» من غنيمة أخرى ما يكفينا «إنا إلى الله راغبون» أن يغنينا وجواب لو لكان خيرا لهم.'),
(1295, 9, 60, '«إنما الصدقات» الزكوات مصروفة «للفقراء» الذين لا يجدون ما يقع موقعا من كفايتهم «والمساكين» الذين لا يجدون ما يكفيهم «والعاملين عليها» أي الصدقات من جاب وقاسم وكاتب وحاشر «والمؤلفة قلوبهم» ليسلموا أو يثبت إسلامهم أو يسلم نظراؤهم أو يذبوا عن المسلمين أقسام، الأول والأخير لا يعطيان اليوم عند الشافعي رضي الله تعالى عنه لعز الإسلام بخلاف الآخرين فيعطيان على الأصح «وفي» فك «الرقاب» أي المكاتبين «والغارمين» أهل الدَّين إن استدانوا لغير معصية أو تابوا وليس لهم وفاء أو لإصلاح ذات البين ولو أغنياء «وفي سبيل الله» أي القائمين بالجهاد ممن لا فيء لهم ولو أغنياء «وابن السبيل» المنقطع في سفره «فريضة» نصب بفعله المقدر «من الله والله عليم» بخلقه «حكيم» في صنعه فلا يجوز صرفها لغير هؤلاء ولا منع صنف منهم إذا وجد فيقسمها الإمام عليهم على السواء وله تفضيل بعض آحاد الصنف على بعض، وأفادت اللام وجوب استغراق أفراده لكن لا يجب على صاحب المال إذا قسم لعسره بل يكفي إعطاء ثلاثة من كل صنف ولا يكفي دونها كما أفادته صيغة الجمع وبيَّنت السنة أن شرط المعطى منها الإسلام وأن لا يكون هاشميا ولا مطلبيا.'),
(1296, 9, 61, '«ومنهم» أي المنافقين «الذين يؤذون النبي» بعيبه وبنقل حديثه «ويقولن» إذا نُهوا عن ذلك لئلا يبلغه «هو أُذُنُ» أي يسمع كل قيل ويقبله فإذا حلفنا له أنَّا لم نقل صدَّقنا «قل» هو «أُذُن» مستمع «خيرٍ لكم» لا مستمع شر «يؤمن بالله ويؤمن» يصدق «للمؤمنين» فيما أخبروه به لا لغيرهم واللام زائدة للفرق بين إيمان التسليم وغيره «ورحمةٌ» بالرفع عطفا على أذن والجر عطفا على خير «للذين آمنوا منكم والذين يؤذون رسول الله لهم عذاب أليم».'),
(1297, 9, 62, '«يحلفون بالله لكم» أيها المؤمنين فيما بلغكم عنهم من أذى الرسول أنهم ما آذوه «ليرضوكم والله ورسوله أحقُّ أن يرضوه» بالطاعة «إن كانوا مؤمنين» حقا وتوحيد الضمير لتلازم الرضاءين أو خبر الله ورسوله محذوف.'),
(1298, 9, 63, '«ألم يعلموا» بـ «أنه» أي الشأن «من يحادد» يشاقق «الله ورسولَه فأن له نار جهنم» جزاء «خالدا فيها ذلك الخزي العظيم».'),
(1299, 9, 64, '«يحذر» يخاف «المنافقون أن تنزل عليهم» أي المؤمنين «سورة تنبئهم بما في قلوبهم» من النفاق وهم مع ذلك يستهزئون «قل استهزؤا» أمر تهديد «إن الله مخرج» مظهر «ما تحذرون» إخراجه من نفاقكم.'),
(1300, 9, 65, '«ولئن» لام قسم «سألتهم» عن استهزائهم بك وبالقرآن وهم سائرون معك إلى تبوك «ليقولن» معتذرين «إنما كنا نخوض ونلعب» في الحديث لنقطع به الطريق ولم نقصد ذلك «قل» لهم «أبالله وآياته ورسوله كنتم تستهزئون».'),
(1301, 9, 66, '«لا تعتذروا» عنه «قد كفرتم بعد إيمانكم» أي ظهر كفركم بعد إظهار الإيمان «إن يُعْفَ» بالياء مبنيا للمفعول والنون مبنيا للفاعل «عن طائفة منكم» بإخلاصها وتوبتها كجحش بن حمير «تُعَذَّبْ» بالتاء والنون «طائفةٌٌ بأنهم كانوا مجرمين» مصرّين على النفاق والاستهزاء.'),
(1302, 9, 67, '«المنافقون والمنافقات بعضهم من بعض» أي متشابهون في الدين كأبعاض الشيء الواحد «يأمرون بالمنكر» الكفر والمعاصي «وينهَون عن المعروف» الإيمان والطاعة «ويقبضون أيديهم» من الإنفاق في الطاعة «نسوا الله» تركوا طاعته «فنسيهم» تركهم من لطفه «إن المنافقين هم الفاسقون».'),
(1303, 9, 68, '«وعد الله المنافقين والمنافقات والكفار نار جهنم خالدين فيها هي حسبهم» جزاءً وعقابا «ولعنهم الله» أبعدهم عن رحمته «ولهم عذاب مقيم» دائم.'),
(1304, 9, 69, 'أنتم أيها المنافقون «كالذين من قبلكم كانوا أشد منكم قوة وأكثر أموالا وأولادا فاستمتعوا» تمتعوا «بخلاقهم» نصيبهم من الدنيا «فاستمتعتم» أيها المنافقون «بخلاقكم كما استمتع الذين من قبلكم بخلاقهم وخضتم» في الباطل والطعن في النبي صلى الله عليه وسلم «كالذي خاضوا» أي كخوضهم «أولئك حبطت أعمالهم في الدنيا والآخرة وأولئك هم الخاسرون».'),
(1305, 9, 70, '«ألم يأتهم نبأ» خبر «الذين من قبلهم قوم نوح وعاد» قوم هود «وثمود» قوم صالح «وقوم إبراهيم وأصحاب مدين» قوم شعيب «والمؤتفكات» قرى قوم لوط أي أهلها «أتتهم رسلهم بالبيِّنات» بالمعجزات فكذبوهم فأُهلكوا «فما كان الله ليظلمهم» بأن يعذبهم بغير ذنب «ولكن كانوا أنفسهم يظلمون» بارتكاب الذنب.'),
(1306, 9, 71, '«والمؤمنون والمؤمنات بعضهم أولياء بعض يأمرون بالمعروف وينهون عن المنكر ويقيمون الصلاة ويؤتون الزكاة ويطيعون الله ورسوله أولئك سيرحمهم الله إن الله عزيز» لا يعجزه شيء عن إنجاز وعده ووعيده «حكيم» لا يضع شيئا إلا في محله.'),
(1307, 9, 72, '«وعد الله المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ومساكن طيبة في جنات عدن» إقامة «ورضوانُُ من الله أكبر» أعظم من ذلك كله «ذلك هو الفوز العظيم».'),
(1308, 9, 73, '«يا أيها النبيُّ جاهد الكفَّار» بالسيف «والمنافقين» باللسان والحجة «وأغلظ عليهم» بالانتهار والمقت «ومأواهم جهنم وبئس المصير» المرجع هي.'),
(1309, 9, 74, '«يحلفون» أي المنافقون «بالله ما قالوا» ما بلغك عنهم من السب «ولقد قالوا كلمة الكفر وكفروا بعد إسلامهم» أظهروا الكفر بعد إظهار الإسلام «وهمَّوا بما لم ينالوا» من الفتك بالنبي ليلة العقبة عند عوده من تبوك وهم بضعة عشر رجلا فضرب عمار بن ياسر وجوه الرواحل لما غشوه فردوا «وما نقموا» أنكروا «إلا أن أغناهم الله ورسوله من فضله» بالغنائم بعد شدة حاجتهم المعنى: لم ينلهم منه إلا هذا وليس مما ينقم «فإن يتوبوا» عن النفاق ويؤمنوا بك «يكُ خيرا لهم وإن يتولوْا» عن الإيمان «يعذبهم الله عذابا أليما في الدنيا» بالقتل «والآخرة» بالنار «ومالهم في الأرض من ولي» يحفظهم منه «ولا نصير» يمنعهم.'),
(1310, 9, 75, '«ومنهم من عاهد الله لئن آتانا من فضله لنصًّدقن» فيه إدغام التاء في الأصل في الصاد «ولنكونن من الصالحين» وهو ثعلبة بن حاطب سأل النبي صلى الله عليه وسلم أن يدعو له أن يرزقه الله مالا ويؤدي منه كل ذي حق حقه فدعا له فوسع عليه فانقطع عن الجمعة والجماعة ومنع الزكاة كما قال تعالى.'),
(1311, 9, 76, '«فلما آتاهم من فضله بخلوا به وتولَّوْا» عن طاعة الله «وهم معرضون».'),
(1312, 9, 77, '«فأعقبهم» أي فصير عاقبتهم «نفاقا» ثابتا «في قلوبهم إلى يوم يلقونه» أي الله وهو يوم القيامة «بما أخلفوا الله ما وعدوه وبما كانوا يكذبون» فيه فجاء بعد ذلك إلى النبي صلى الله عليه وسلم بزكاته فقال إن الله منعني أن أقبل منك، فجعل يحثو التراب على رأسه ثم جاء إلى أبي بكر فلم يقبلها ثم إلى عمر فلم يقبلها ثم إلى عثمان فلم يقبلها ومات في زمانه.'),
(1313, 9, 78, '«ألم يعلموا» أي المنافقون «أن الله يعلم سرهم» ما أسروه في أنفسهم «ونجواهم» ما تناجوا به بينهم «وأنّ الله علام الغيوب» ما غاب عن العيان ولما نزلت آية الصدقة جاء رجل فتصدق بشيء كثير فقال المنافقون: مُراءٍ وجاء رجل فتصدق بصاع فقالوا: إنّ الله غني عن صدقة هذا فنزل.'),
(1314, 9, 79, '«الذين» مبتدأ «يلمزون» يعيبون «المطوعين» المتنفلين «من المؤمنين في الصدقات والذين لا يجدون إلا جهدهم» طاقتهم فيأتون به «فيسخرون منهم» والخبر «سخر الله منهم» جازاهم على سخريتهم «ولهم عذاب أليم».'),
(1315, 9, 80, '(استغفر) يا محمد (لهم أو لا تستغفر لهم) تخيير له في الاستغفار وتركه قال صلى الله عليه وسلم: "" إني خيرت فاخترت يعني الاستغفار "" رواه البخاري (إن تستغفر لهم سبعين مرة فلن يغفر الله لهم) قيل المراد بالسبعين المبالغة في كثرة الاستغفار وفي البخاري حديث "" لو أعلم أني لو زدت على السبعين غفر لزدت عليها "" وقيل المراد العدد المخصوص لحديثه أيضاً "" وسأزيد على السبعين "" فبين له حسم المغفرة بآية [سواء عليهم استغفرت لهم أم لم تستغفر لهم] (ذلك بأنهم كفروا بالله ورسوله والله لا يهدي القوم الفاسقين).'),
(1316, 9, 81, '«فرح المخلَّفون» عن تبوك «بمقعدهم» أي بقعودهم «خلاف» أي بعد «رسول الله وكرهوا أن يجاهدوا بأموالهم وأنفسهم في سبيل الله وقالوا» أي قال بعضهم لبعض «لا تنفروا» تخرجوا إلى الجهاد «في الحر قل نار جهنم أشدُّ حرا» من تبوك فالأولى أن يتقوها بترك التخلف «لو كانوا يفقهون» يعلمون ذلك ما تخلفوا.'),
(1317, 9, 82, '«فليضحكوا قليلا» في الدنيا «وليبكوا» في الآخرة «كثيرا جزاءً بما كانوا يكسبون» خبر عن حالهم بصيغة الأمر.'),
(1318, 9, 83, '«فإنْ رجعك» ردك «الله» من تبوك «إلى طائفة منهم» ممن تخلف بالمدينة من المنافقين «فاستأذنوك للخروج» معك إلى غزوة أُخرى «فقل» لهم «لن تخرجوا معي أبدا ولن تقاتلوا معي عدوا إنكم رضيتم بالقعود أول مرة فاقعدوا مع الخالفين» المتخلفين عن الغزو من النساء والصبيان وغيرهم.'),
(1319, 9, 84, 'ولما صلى النبي صلى الله عليه وسلم على ابن أبيّ نزل «ولا تُصلِّ على أحد منهم مات أبدا ولا تقم على قبره» لدفن أو زيارة «إنهم كفروا بالله ورسوله وماتوا وهم فاسقون» كافرون.'),
(1320, 9, 85, '«ولا تعجبك أموالهم وأولادهم إنما يريد الله أن يعذبهم بها في الدنيا وتزهق» تخرج «أنفسهم وهم كافرون».'),
(1321, 9, 86, '«وإذا أُنزلت سورة» أي طائفة من القرآن «أن» أي بأن «آمنوا بالله وجاهدوا مع رسوله استأذنك أولوا الطَّوْل» ذوو الغنى «منهم وقالوا ذرنا نكن مع القاعدين».'),
(1322, 9, 87, '«رضوا بأن يكونوا مع الخوالف» جمع خالفة أي النساء اللاتي تخلَّفن في البيوت «وطبع على قلوبهم فهم لا يفقهون» الخير.'),
(1323, 9, 88, '«لكن الرسولُ والذين آمنوا معه جاهدوا بأموالهم وأنفسهم وأولئك لهم الخيرات» في الدنيا والآخرة «وأولئك هم المفلحون» أي الفائزون.'),
(1324, 9, 89, '«أعد الله لهم جنات تجري من تحتها الأنهار خالدين فيها ذلك الفوز العظيم».'),
(1325, 9, 90, '«وجاء المعذِّرون» بإدغام التاء في الأصل في الدال أي المعتذرون بمعنى المعذورين وقرئ به «من الأعراب» إلى النبي صلى الله عليه وسلم «ليؤذن لهم» في القعود لعذرهم فأذن لهم «وقعد الذين كذبوا الله ورسوله» في ادعاء الإيمان من منافقي الأعراب عن المجيء للاعتذار «سيصيب الذين كفروا منهم عذاب أليم».'),
(1326, 9, 91, '«ليس على الضعفاء» كالشيوخ «ولا على المرضى» كالعمي والزمنى «ولا على الذين لا يجدون ما ينفقون» في الجهاد «حرج» إثم في التخلف عنه «إذا نصحوا لله ورسوله» في حال قعودهم بعدم الإرجاف والتثبيط والطاعة «ما على المحسنين» بذلك «من سبيل» طريق بالمؤاخذة «والله غفور» لهم «رحيم» بهم في التوسعة في ذلك.'),
(1327, 9, 92, '«ولا على الذين إذا ما أتوك لتحملهم» معك إلى الغزو وهم سبعة من الأنصار وقيل بنو مقرن «قلتَ لا أجد ما أحملكم عليه» حال «تولَّوا» جواب إذا أي انصرفوا «وأعينهم تفيض» تسيل «من» للبيان «الدمع حزنا» لأجل «ألا يجدوا ما ينفقون» في الجهاد.'),
(1328, 9, 93, '«إنما السَّبيل على الذين يستأذنوك» في التخلُّف «وهم أغنياء رضوا بأن يكونوا مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون» تقدم مثله.'),
(1329, 9, 94, '«يعتذرون إليكم» في التخلف «إذا رجعتم إليهم» من الغزو «قل» لهم «لا تعتذروا لن نؤمن لكم» نصدقكم «قد نبأنا الله من أخباركم» أي أخبرنا بأحوالكم «وسيرى الله عملَكم ورسوله ثم تُردون» بالبعث «إلى عالم الغيب والشهادة» أي الله «فينبئكم بما كنتم تعملون» فيجازيكم عليه.'),
(1330, 9, 95, '«سيحلفون بالله لكم إذا انقلبتم» رجعتم «إليهم» من تبوك أنهم معذورون في التخلف «لتعرضوا عنهم» بترك المعاتبة «فأعرضوا عنهم إنهم رجس» قذر لخبث باطنهم «ومأواهم جهنم جزاءً بما كانوا يكسبون».'),
(1331, 9, 96, '«يحلفون لكم لترضوا عنهم فإن ترضوا عنهم فإنّ الله لا يرضى عن القوم الفاسقين» أي عنهم'),
(1332, 9, 97, '«الأعراب» أهل البدو «أشدُّ كفرا ونفاقا» من أهل المدن لجفائهم وغلظ طباعهم وبعدهم عن سماع القرآن «وأجدر» أولى «أ» ن أي بأن «لا يعلموا حدود ما أنزل الله على رسوله» من الأحكام والشرائع «والله عليم» بخلقه «حكيم» في صنعه بهم.'),
(1333, 9, 98, '«ومن الأعراب من يتخذ ما ينفق» في سبيل الله «مَغرما» غرامة وخسرانا لأنه لا يرجو ثوابه بل ينفقه خوفا وهم بنو أسد وغطفان «ويتربص» ينتظر «بكم الدوائر» دوائر الزمان أن تنقلب عليكم فيتلخص «عليهم دائرة السُّوء» بالضم والفتح، أي يدور العذاب والهلاك عليهم لا عليكم «والله سميع» لأقوال عباده «عليم» بأفعالهم.'),
(1334, 9, 99, '«ومن الأعراب من يؤمن بالله واليوم الآخر» كجهينة ومزينة «ويتخذ ما ينفق» في سبيل الله «قربات» تقربه «عند الله و» وسيلة إلى «صلوات» دعوات «الرسول» له «ألا إنها» أي نفقتهم «قُرُبةٌ» بضم الراء وسكونها «لهم» عنده «سيدخلهم الله في رحمته» جنته «إن الله غفور» لأهل طاعته «رحيم» بهم.'),
(1335, 9, 100, '«والسابقون الأولون من المهاجرين والأنصار» وهم من شهد بدرا أو جميع الصحابة «والذين اتبعوهم» إلى يوم القيامة «بإحسان» في العمل «رضي الله عنهم» بطاعته «ورضوا عنه» بثوابه «وأعد لهم جنات تجري تحتها الأنهار» وفي قراءة بزيادة من «خالدين فيها أبدا ذلك الفوز العظيم».'),
(1336, 9, 101, '«وممن حولكم» يا أهل المدينة «من الأعراب منافقون» كأسلم وأشجع وغفار «ومن أهل المدينة» منافقون أيضا «مردوا على النفاق» لُّجوا فيه واستمروا «لا تعلمهم نحن نعلمهم سنعذبهم مرتين» بالفضيحة أو القتل في الدنيا وعذاب القبر «ثم يردون» في الآخرة «إلى عذاب عظيم» هو النار.'),
(1337, 9, 102, '«و» قوم «آخرون» مبتدأ «اعترفوا بذنوبهم» من التخلف نعته والخبر «خلطوا عملا صالحا» وهو جهادهم قبل ذلك أو اعترافهم بذنوبهم أو غير ذلك «وآخر سيئا» وهو تخلفهم «عسى الله أن يتوب عليهم إن الله غفور رحيم» نزلت في أبي لبابة وجماعة أوثقوا أنفسهم في سواري المسجد لما بلغهم ما نزل في المتخلفين وحلفوا لا يحلهم إلا النبي صلى الله عليه وسلم فحلُّهم لما نزلت.'),
(1338, 9, 103, '«خذ من أموالهم صدقة تطهرهم وتزكيهم بها» من ذنوبهم فأخذ ثلث أموالهم وتصدق بها «وصل عليهم» أي ادع لهم «إن صلاتك سكن» رحمة «لهم» وقيل طمأنينة بقبول توبتهم «والله سميع عليم».'),
(1339, 9, 104, '«ألم يعلموا أن الله هو يقبل التوبة عن عباده ويأخذ» يقبل «الصدقات وأن الله هو التواب» على عباده بقبول توبتهم «الرحيم» بهم، والاستفهام للتقرير، والقصد به هو تهييجهم إلى التوبة والصدقة.'),
(1340, 9, 105, '«وقل» لهم أو للناس «اعملوا» ما شئتم «فسيرى الله عملكم ورسوله والمؤمنون وستردون» بالبعث «إلى عالم الغيب والشهادة» أي الله «فينبئكم بما كنتم تعملون» فيجازيكم به.'),
(1341, 9, 106, '«وآخرون» من المتخلفين «مُرْجَؤُن» بالهمز وتركه: مؤخرون عن التوبة «لأمر الله» فيهم بما يشاء «إما يعذبهم» بأن يميتهم بلا توبة «وإما يتوب عليهم والله عليم» بخلقه «حكيم» في صنعه بهم، وهم الثلاثة الآتون بعد: مرارة بن الربيع وكعب بن مالك وهلال بن أمية، تخلفوا كسلا وميلا إلى الدعة، لا نفاقا ولم يعتذروا إلى النبي صلى الله عليه وسلم كغيرهم فوقف أمرهم خمسين ليلة وهجرهم الناس حتى نزلت توبتهم بعد.'),
(1342, 9, 107, '«و» منهم «الذين اتخذوا مسجدا» وهم اثنا عشر من المنافقين «ضرارا» مضارة لأهل مسجد قباء «وكفرا» لأنهم بنوه بأمر أبي عامر الراهب ليكون معقلا له يقدم فيه من يأتي من عنده وكان ذهب ليأتي بجنود من قيصر لقتال النبي صلى الله عليه وسلم «وتفريقا بين المؤمنين» الذين يصلون بقباء بصلاة بعضهم في مسجدهم «وإرصادا» ترقبا «لمن حارب الله ورسوله من قبل» أي قبل بنائه، وهو أبو عامر المذكور «وليحلفن إن» ما «أردنا» ببنائه «إلا» الفعلة «الحسنى» من الرفق بالمسكين في المطر والحر والتوسعة على المسلمين «والله يشهد إنهم لكاذبون» في ذلك، وكانوا سألوا النبي صلى الله عليه وسلم أن يصلي فيه فنزل.'),
(1343, 9, 108, '(لا تقم) تصل (فيه أبدا) فأرسل جماعة هدموه وحرقوه وجعلوا مكانه كناسة تلقى فيها الجيف (لمسجد أسس) بنيت قواعده (على التقوى من أول يوم) وضع يوم حللت بدار الهجرة، وهو مسجد قباء كما في البخاري (أحق) منه (أن) أي بأن (تقوم) تصلي (فيه، فيه رجال) هم الأنصار (يحبون أن يتطهروا والله يحب المطهرين) أي يثيبهم، فيه إدغام التاء في الأصل في الطاء، روي ابن خزيمة في صحيحه عن عويمر بن ساعدة: "" أنه صلى الله عليه وسلم أتاهم في مسجد قباء فقال: إن الله تعالى قد أحسن عليكم الثناء في الطهور في قصة مسجدكم فما هذا الطهور الذي تطهرون به؟ قالوا: والله يا رسول الله ما نعلم شيئا إلا أنه كان لنا جيران من اليهود وكانوا يغسلون أدبارهم من الغائط فغسلنا كما غسلوا "" وفي حديث رواه البزار فقالوا نتبع الحجارة بالماء "" فقال هو ذاك فعليكموه "".'),
(1344, 9, 109, '«أفمن أسَّس بنيانه على تقوى» مخافة «من الله» رجاء «ورضوان» منه «خيرٌ أم من أسَّس بنيانه على شفا» طرف «جُرُفِ» بضم الراء وسكونها، جانب «هارِ» مشرف على السقوط «فانهار به» سقط مع بانيه «في نار جهنم» خير تمثيل لبناء على ضد التقوى بما يؤول إليه، والاستفهام للتقرير، أي الأول خير وهو مثال مسجد قباء، والثاني مثال مسجد الضرار «والله لا يهدي القوم الظالمين».'),
(1345, 9, 110, '«لا يزال بنيانهم الذي بنوا ريبة» شكا «في قلوبهم إلاً أن تقطَّع» تنفصل «قلوبهم» بأن يموتوا «والله عليم» بخلقه «حكيم» في صنعه بهم.'),
(1346, 9, 111, '«إن الله اشترى من المؤمنين أنفسهم وأموالهم» بأن يبذلوها في طاعته كالجهاد «بأن لهم الجنة يقاتلون في سبيل الله فيَقتلون ويُقتلون» جملة استئناف بيان للشراء، وفي قراءة بتقديم المبني للمفعول، أي فيقتل بعضهم ويقاتل الباقي «وعدا عليه حقا» مصدران منصوبان بفعلهما المحذوف «في التوراة والإنجيل والقرآن ومن أوفي بعهده من الله» أي لا أحد أوفى منه «فاستبشروا» فيه التفات عن الغيبة «ببيعكم الذي بايعتم به وذلك» البيع «هو الفوز العظيم» المنيل غاية المطلوب.'),
(1347, 9, 112, '«التائبون» رفع على المدح بتقدير مبتدأ من الشرك والنفاق «العابدون» المخلصون العبادة لله «الحامدون» له على كل حال «السائحون» الصائمون «الراكعون الساجدون» أي المصلون «الآمرون بالمعروف والناهون عن المنكر والحافظون لحدود الله» لأحكامه بالعمل بها «وبشر المؤمنين» بالجنة.'),
(1348, 9, 113, 'ونزل في استغفاره صلى الله عليه وسلم لعمه أبي طالب واستغفار بعض الصحابة لأبويه المشركين (ما كان للنبي والذين آمنوا أن يستغفروا للمشركين ولو كانوا أولي قربى) ذوي قرابة (من بعد ما تبين لهم أنهم أصحاب الجحيم) النار، بأن ماتوا على الكفر.'),
(1349, 9, 114, '(وما كان استغفار إبراهيم لأبيه إلا عن موعدة وعدها إياه) بقوله "" سأستغفر لك ربي "" رجاء أن يسلم (فلما تبين له أنه عدو لله) بموته على الكفر (تبرأ منه) وترك الاستغفار له (إن إبراهيم لأواه) كثير التضرع والدعاء (حليم) صبور على الأذى.'),
(1350, 9, 115, '«وما كان الله ليضل قوما بعد إذ هداهم» للإسلام «حتى يبين لهم ما يتقون» من العمل فلا يتقوه فيستحقوا الإضلال «إن الله بكل شيء عليم» ومنه مستحق الإضلال والهداية.'),
(1351, 9, 116, '«إن الله له مُلك السماوات والأرض يحيي ويميت وما لكم» أيها الناس «من دون الله» أي غيره «من ولي» يحفظكم منه «ولا نصير» يمنعكم عن ضرره.'),
(1352, 9, 117, '«لقد تاب الله» أي أدام توبته «على النبي والمهاجرين والأنصار الذين اتبعوه في ساعة العُسرة» أي وقتها، وهي حالهم في غزوة تبوك كان الرجلان يقتسمان تمرة والعشرة يعتقبون البعير الواحد، واشتد الحر حتى شربوا الفرث «من بعد ما كاد تزيغ» بالتاء والياء تميل «قلوب فريق منهم» عن اتباعه إلى التخلف لما هم فيه من الشدة «ثم تاب عليهم» بالثبات «إنه بهم رؤوف رحيم».'),
(1353, 9, 118, '«و» تاب «على الثلاثة الذين خُلِّفوا» عن التوبة عليهم بقرينة «حتى إذا ضاقت عليهم الأرض بما رحُبت» أي مع رحبها، أي سعتها فلا يجدون مكانا يطمئنون إليه «وضاقت عليهم أنفسهم» قلوبهم للغمِّ والوحشة بتأخير توبتهم فلا يسعها سرور ولا أنس «وظنُّوا» أيقنوا «أن» مخففة «لا ملجأ من الله إلاّ إليه ثم تاب عليهم» وفقهم للتوبة «ليتوبوا إن الله هو التواب الرحيم».'),
(1354, 9, 119, '«يا أيها الذين آمنوا اتقوا الله» بترك معاصيه «وكونوا مع الصادقين» في الإيمان والعهود بأن تلزموا الصدق.'),
(1355, 9, 120, '«ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله» إذا غزا «ولا يرغبوا بأنفسهم عن نفسه» بأن يصونوها عما رضيه لنفسه من الشدائد، وهو نهي بلفظ الخبر «ذلك» أي النهي عن التخلف «بأنهم» بسبب أنهم «لا يصيبهم ظمأ» عطش «ولا نصب» تعب «ولا مخمصة» جوع «في سبيل الله ولا يطؤون موطئا» مصدر بمعنى وطأ «يغيظ» يغضب «الكفار ولا ينالون من عدو» لله «نيلا» قتلا أو أسرا أو نهبا «إلا كتب لهم به عمل صالح» ليجازوا عليه «إن الله لا يضيع أجر المحسنين» أي أجرهم بل يثيبهم.'),
(1356, 9, 121, '«ولا ينفقون» فيه «نفقة صغيرة» ولو تمرة «ولا كبيرة ولا يقطعون واديا» بالسير «إلا كُتب لهم» به عمل صالح «ليجزيهم الله أحسن ما كانوا يعملون» أي جزاءهم.'),
(1357, 9, 122, 'ولما وبِّخوا على التخلف وأرسل النبي صلى الله عليه وسلم سرية نفروا جميعا فنزل «وما كان المؤمنون لينفروا» إلى الغزو «كافة فلولا» فهلا «نفر من كلِّّّّ فرقة» قبيلة «منهم طائفة» جماعة، ومكث الباقون «ليتفقَّهوا» أي الماكثون «في الدين ولينذروا قومهم إذا رجعوا إليهم» من الغزو بتعليمهم ما تعلموه من الأحكام «لعلهم يحذرون» عقاب الله بامتثال أمره ونهيه، قال ابن عباس فهذه مخصوصة بالسرايا، والتي قبلها بالنهي عن تخلُّف واحد فيما إذا خرج النبي صلى الله عليه وسلم.'),
(1358, 9, 123, '«يا أيها الذين آمنوا قاتلوا الذين يلونكم من الكفار» أي الأقرب فالأقرب منهم «وليجدوا فيكم غلظة» شدة، أي أغلظوا عليهم «واعلموا أن الله مع المتقين» بالعون والنصر.'),
(1359, 9, 124, '«وإذا ما أنزلت سورة» من القرآن «فمنهم» أي المنافقين «من يقول» لأصحابه استهزاءً «أيكم زادته هذه إيمانا» تصديقا، قال تعالى: «فأما الذين آمنوا فزادتهم إيمانا» لتصديقهم بها «وهم يستبشرون» يفرحون بها.'),
(1360, 9, 125, '«وأما الذين في قلوبهم مرض» ضعف اعتقاد «فزادتهم رجسا إلى رجسهم» كفرا إلى كفرهم لكفرهم بها «وماتوا وهم كافرون».'),
(1361, 9, 126, '«أولا يرون» بالياء أي المنافقون، والتاء أيها المؤمنون «أنهم يُفتنون» يُبتلون «في كل عام مرة أو مرتين» بالقحط والأمراض «ثم لايتوبون» من نفاقهم «ولا هم يذَّكرون» يتعظون.'),
(1362, 9, 127, '«وإذا ما أنزلت سورة» فيها ذكرهم وقرأها النبي صلى الله عليه وسلم «نظر بعضهم إلى بعض» يريدون الهرب يقولون «هل يراكم من أحد» إذا قمتم فإن لم يرهم أحد قاموا وإلا ثبتوا «ثم انصرفوا» على كفرهم «صرف الله قلوبهم» عن الهدى «بأنهم قوم لا يفقهون» الحق لعدم تدبرهم.'),
(1363, 9, 128, '«لقد جاءكم رسول من أنفسكم» أي منكم: محمد صلى الله عليه وسلم «عزيز» شديد «عليه ما عَنِتُّم» أي عنتكم، أي مشقتكم ولقاءكم المكروه «حريص عليكم» أن تهتدوا «بالمؤمنين رءوف» شديد الرحمة «رحيم» يريد لهم الخير.'),
(1364, 9, 129, '«فإن تولَّوا» عن الإيمان بك «فقل حسبي» كافيّ «الله لا إله إلا هو عليه توكلت» به وثقت لا بغيره «وهو ربُّ العرش» الكرسي «العظيم» خصه بالذكر لأنه أعظم المخلوقات، وروى الحاكم في المستدرك عن أبيّ بن كعب قال: آخر آية نزلت «لقد جاءكم رسول» إلى آخر السورة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, '«الر» الله أعلم بمراده بذلك «تلك» أي هذه الآيات «آيات الكتاب» القرآن والإضافة بمعنى من «الحكيم» المحكم.'),
(1366, 10, 2, '«أكان للناس» أي أهل مكة، استفهام إنكار والجار والمجرور حال من قوله «عَجبا» بالنصب خبر كان، وبالرفع اسمها والخبر وهو اسمها على الأولى «أن أوحينا» أي إيحاؤنا «إلى رجل منهم» محمد «أن» مفسرة «أنذر» خوِّف «الناس» الكافرين بالعذاب «وبشر الذين آمنوا أن» أي بأن «لهم قدم» سلف «صدق عند ربهم» أي أجرا حسناً بما قدموه من الأعمال «قال الكافرون إن هذا» القرآن المشتمل على ذلك «لَسِحْرٌ مبين» بيِّن، وفي قراءة لَساحرٌ، والمشار إليه النبي.'),
(1367, 10, 3, '«إن ربكم الله الذي خلق السماوات والأرض في ستة أيام» من أيام الدنيا، أي في قدرها، لأنه لم يكن ثَم شمس ولا قمر، ولو شاء لخلقهن في لمحة، والعدول عنه لتعليم خلقه التثبت «ثم استوى على العرش» استواءً يليق به «يدبر الأمر» بين الخلائق «ما من» صلة «شفيع» يشفع لأحد «إلا من بعد إذنه» رد لقولهم إن الأصنام تشفع لهم «ذلكم» الخالق المدبر «الله ربكم فاعبدوه» وحدوه «أفلا تذَّكرون» بإدغام التاء في الأصل في الذال.'),
(1368, 10, 4, '«إليه» تعالى «مرجعكم وعد الله حقا» مصدران منصوبان بفعلهما المقدر «إنه» بالكسر استئنافاً والفتح على تقدير اللام «يبدأ الخلق» أي بدأه بالإنشاء «ثم يعيده» بالبعث «ليجزي» يثيب «الذين آمنوا وعملوا الصالحات بالقسط والذين كفروا لهم شراب من حميم» ماء بالغ نهاية الحرارة «وعذاب أليم» مؤلم «بما كانوا يكفرون» أي بسبب كفرهم.'),
(1369, 10, 5, '«هو الذي جعل الشمس ضياءً» ذات ضياء، أي نور «والقمر نورا وقدره» من حيث سيره «منازل» ثمانية وعشرين منزلا في ثمان وعشرين ليلة من كل شهر، ويستتر ليلتين إن كان الشهر ثلاثين يوما، أو ليلة إن كان تسعة وعشرين يوما «لتعلموا» بذلك «عدد السنين والحساب ما خلق الله ذلك» المذكور «إلا بالحق» لا عبثا تعالى عن ذلك «يفصل» بالياء والنون يبين «الآيات لقوم يعلمون» يتدبرون.'),
(1370, 10, 6, '«إن في اختلاف الليل والنهار» بالذهاب والمجيء والزيادة والنقصان «وما خلق الله في السماوات» من ملائكة وشمس وقمر ونجوم وغير ذلك «و» في «الأرض» من حيوان وجبال وبحار وأنهار وأشجار وغيرها «لآيات» دلالات على قدرته تعالى «لقوم يتقونـ» ـه فيؤمنون، خصهم بالذكر لأنهم المنتفعون بها.'),
(1371, 10, 7, '«إن الذين لا يرجون لقاءنا» بالبعث «ورضوا بالحياة الدنيا» بدل الآخرة لإنكارهم لها «واطمأنوا بها» سكنوا إليها «والذين هم عن آياتنا» دلائل وحدانيتنا «غافلون» تاركون النظر فيها.'),
(1372, 10, 8, '«أولئك مأواهم النار بما كانوا يكسبون» من الشرك والمعاصي.'),
(1373, 10, 9, '«إن الذين آمنوا وعملوا الصالحات يهديهم» يرشدهم «ربهم بإيمانهم» به بأن يجعل لهم نورا يهتدون به يوم القيامة «تجري من تحتهم الأنهار في جنات النعيم».'),
(1374, 10, 10, '«دعواهم فيها» طلبهم يشتهونه في الجنة أن يقولوا «سبحانك اللهم» أي يا الله فإذا ما طلبوه وجدوه بين أيديهم «وتحيتهم» فيما بينهم «فيها سلام وآخر دعواهم أن» مفسرة «الحمد لله رب العالمين» ونزل لما استعجل المشركون العذاب.'),
(1375, 10, 11, '«ولو يُعَجِل الله للناس الشر استعجالهم» أي كاستعجالهم «بالخير لقضي» بالبناء للمفعول وللفاعل «إليهم أجلُهم» بالرفع والنصب، بأن يهلكهم ولكن يمهلهم «فَنَذَرُ» نترك «الذين لا يرجون لقاءنا في طغيانهم يعمهون» يترددون متحيرين.'),
(1376, 10, 12, '«وإذا مس الإنسان» الكافر «الضُّرُّ» المرض والفقر «دعانا لجنبه» أي مضطجعا «أو قاعدا أو قائما» أي في كل حال «فلما كشفنا عنه ضُره مرَّ» على كفره «كأن» مخففة واسمها محذوف، أي كأنه «لم يدعنا إلى ضرٍّ مسه كذلك» كما زُيّن له الدعاء عند الضرر والإعراض عند الرخاء «زُيِّن للمسرفين» المشركين «ما كانوا يعملون».'),
(1377, 10, 13, '«ولقد أهلكنا القرون» الأمم «من قبلكم» يا أهل مكة «لما ظلموا» بالشرك «و» قد «جاءتهم رسلهم بالبينات» الدالات على صدقهم «وما كانوا ليؤمنوا» عطف على ظلموا «كذلك» كما أهلكنا أولئك «نجزي القوم المجرمين» الكافرين.'),
(1378, 10, 14, '«ثم جعلناكم» يا أهل مكة «خلائف» جمع خليفة «في الأرض من بعدهم لننظر كيف تعملون» فيها وهل تعتبرون بهم فتصدقوا رسلنا.'),
(1379, 10, 15, '«وإذا تُتلى عليهم آياتنا» القرآن «بينات» ظاهرات حال «قال الذين لا يرجون لقاءنا» لا يخافون البعث «ائت بقرآن غير هذا» ليس فيه عيب آلهتنا «أو بدله» من تلقاء نفسك «قل» لهم «ما يكون» ينبغي «لي أن أبدله من تلقاء» قبل «نفسي إن» ما «أتبع إلا ما يوحي إليّ إني أخاف إن عصيت ربي» بتبديله «عذاب يوم عظيم» هو يوم القيامة.'),
(1380, 10, 16, '«قل لو شاء الله ما تلوته عليكم ولا أدراكم» أعلمكم «به» ولا نافية عطف على ما قبله، وفي قراءة بلام جواب لو أي لأعلمكم به على لسان غيري «فقد لبثت» مكثت «فيكم عمرا» سنينا أربعين «من قبله» لا أحدثكم بشيء «أفلا تعقلون» أنه ليس من قِبَلي.'),
(1381, 10, 17, '«فمن» أي لا أحد «أظلم ممن افترى على الله كذبا» بنسبة الشريك إليه «أو كذَّب بآياته» القرآن «إنه» أي الشأن «لا يفلح» يسعد «المجرمون» المشركون.'),
(1382, 10, 18, '«ويعبدون من دون الله» أي غيره «ما لا يضرهم» إن لم يعبدوه «ولا ينفعهم» إن عبدوه وهو الأصنام «ويقولون» عنها «هؤلاء شفعاؤنا عند الله قل» لهم «أتنبئون الله» جبروته «بما لا يعلم في السموات والأرض» استفهام إنكار إذ لو كان له شريك لعلمه، إذا لا يخفى عليه شيء «سبحانه» تنزيها له «وتعالى عما يشركونـ» به معه.'),
(1383, 10, 19, '«وما كان الناس إلا أمة واحدة» على دين واحد وهو الإسلام، من لدن آدم إلى نوح، وقيل من عهد إبراهيم إلى عمرو بن لحيِّ «فاختلفوا» بأن ثبت بعض وكفر بعض «ولولا كلمة سبقت من ربك» بتأخير الجزاء إلى يوم القيامة «لقضي بينهم» أي الناس في الدنيا «فيما فيه يختلفون» من الدين بتعذيب الكافرين.'),
(1384, 10, 20, '«ويقولون» أي أهل مكة «لولا» هلا «أنزل عليه» على محمد «آية من ربه» كما كان للأنبياء من الناقة والعصا واليد «فقل» لهم «إنما الغيب» ما غاب عن العباد أي أمره «لله» ومنه الآيات فلا يأتي بها إلا هو وإنما عليَّ التبليغ «فانتظروا» العذاب إن لم تؤمنوا «إني معكم من المنتظرين».'),
(1385, 10, 21, '«وإذا أذقنا الناس» أي كفار مكة «رحمة» مطرا وخصبا «من بعد ضراء» بؤس وجدب «مستهم إذا لهم مكر في آياتنا» بالاستهزاء والتكذيب «قل» لهم «الله أسرع مكراً» مجازاة «إن رسلنا» الحفظة «يكتبون ما تمكرون» بالتاء والياء.'),
(1386, 10, 22, '«هو الذي يسيركم» وفي قراءة ينشركم «في البر والبحر حتى إذا كنتم في الفلك» السفن «وجرين بهم بريح» وجرين فيه التفات عن الخطاب بريح طيبة» لينة «وفرحوا بها جاءتها ريح عاصف» شديد الهبوب تكسر كل شيء «وجاءهم الموج من كل مكان وظنوا أنهم أحيط بهم» أي أهلكوا «دعوا الله مخلصين له الدين» الدعاء «لئن» لام قسم «أنجيتنا من هذه» الأهوال «لنكونن من الشاكرين» الموحدين.'),
(1387, 10, 23, '«فلما أنجاهم إذا هم يبغون في الأرض بغير الحق» بالشرك «يا أيها الناس إنما بغيكم» ظلمكم «على أنفسكم» لأن إثمه عليها هو «متاعُ الحياة الدنيا» تمتعون فيها قليلا «ثم إلينا مرجعكم» بعد الموت «فننبئكم بما كنتم تعملون» فنجاريكم عليه وفي قراءة بنصب متاع: أي تتمتعون.'),
(1388, 10, 24, '«إنما مَثَل» صفة «الحياة الدنيا كماء» مطر «أنزلناه من السماء فاختلط به» بسببه «نبات الأرض» واشتبك بعضه ببعض «مما يأكل الناس» من البرّ والشعير وغيرهما «والأنعام» من الكلأ «حتى إذا أخذت الأرض زخرفها» بهجتها من النبات «وازَّيَّنت» بالزهر، وأصله تزينت، أبدلت التاء زايا وأدغمت في الزاي «وظن أهلها أنهم قادرون عليها» متمكنون من تحصيل ثمارها «أتاها أمرنا» قضاؤنا أو عذابنا «ليلا أو نهارا فجعلناها» أي زرعها «حصيدا» كالمحصود بالمناجل «كأن» مخففة أي كأنها «لم تغن» تكن «بالأمس كذلك نفصَّل» نبين «الآيات لقوم يتفكرون».'),
(1389, 10, 25, '«والله يدعو إلى دار السلام» أي السلامة، وهي الجنة بالدعاء إلى الإيمان «ويهدي من يشاء» هدايته «إلى صراط مستقيم» دين الإسلام.'),
(1390, 10, 26, '«للذين أحسنوا» بالإيمان «الحسنى» الجنة «وزيادة» هي النظر إليه تعالى كما في حديث مسلم «ولا يرهق» يغشى «وجوههم قترٌ» سواد «ولا ذلة» كآبة «أولئك أصحاب الجنة هم فيها خالدون».'),
(1391, 10, 27, '«والذين» عطف على الذين أحسنوا، أي وللذين «كسبوا السيئات» عملوا الشرك «جزاء سيئة بمثلها وترهقهم ذلة ما لهم من الله من» زائدة «عاصم» مانع «كأنما أغشيت» أُلبست «وجوههم قطعا» بفتح الطاء جمع قطعة، وإسكانها جزءًا «من الليل مظلما أولئك أصحاب النار هم فيها خالدون».'),
(1392, 10, 28, '«و» اذكر «يوم نحشرهم» أي الخلق «جميعا ثم نقول للذين أشركوا مكانكم» نصب بإلزموا مقدرا «أنتم» تأكيد للضمير المستتر في الفعل المقدر ليعطف عليه «وشركاؤكم» أي الأصنام «فزيَّلنا» ميزنا «بينهم» وبين المؤمنين كما في آية (وامتازوا اليوم أيها المجرمون) «وقال» لهم «شركاؤهم ما كنتم إيانا تعبدون» ما نافية وقدم المفعول للفاصلة.'),
(1393, 10, 29, '«فكفى بالله شهيدا بيننا وبينكم إن» مخففة أي إنا «كنا عن عبادتكم لغافلين».'),
(1394, 10, 30, '«هنالك» أي ذلك اليوم «تبلوا» من البلوى، وفي قراءة بتاءين من التلاوة «كل نفس ما أسلفت» قدمت من العمل «وردوا إلى الله مولاهم الحق» الثابت الدائم «وضل» غاب «عنهم ما كانوا يفترون» عليه من الشركاء.'),
(1395, 10, 31, '«قل» لهم «من يرزقكم من السماء» بالمطر «والأرض» بالنبات «أمَّن يملك السمع» بمعنى الأسماع، أي خلقها «والأبصار ومن يخرج الحي من الميت ويخرج الميت من الحي ومن يدبِّر الأمر» بين الخلائق «فسيقولون» هو «الله فقل» لهم «أفلا تتقونـ» ـه فتؤمنون.'),
(1396, 10, 32, '«فذلكم» الفاعل لهذه الأشياء «الله ربكم الحق» الثابت «فماذا بعد الحق إلا الضلال» استفهام تقرير، أي ليس بعده غيره فمن أخطأ الحق وهو عبادة الله وقع في الضلال «فأنَّى» كيف «تُصرفون» عن الإيمان مع قيام البرهان.'),
(1397, 10, 33, '«كذلك» كما صرف هؤلاء عن الإيمان «حقَّت كلمة ربِّك على الذين فسقوا» كفروا وهي (لأملأن جهنم) الآية، أو هي «أنهم لا يؤمنون».'),
(1398, 10, 34, '«قل هل من شركائكم من يبدأ الخلق ثم يعيده قل الله يبدأ الخلق ثم يعيده فأنى تؤفكون» تصرفون عن عبادته مع قيام الدليل.'),
(1399, 10, 35, '«قل هل من شركائكم من يهدي إلى الحق» بنصب الحجج وخلق الاهتداء «قل الله يهدي للحق أفمن يهدي إلى الحق» وهو الله «أحق أنْ يُتبع أمَّن لا يهدي» يهتدي «إلا أن يهدى» أحق أن يتبع استفهام تقرير وتوبيخ، أي الأول أحق «فما لكم كيف تحكمون» هذا الحكم الفاسد من اتَّباع ما لا يحق اتباعه.'),
(1400, 10, 36, '«وما يتبع أكثرهم» في عبادة الأصنام «إلا ظنا» حيث قلدوا فيه آباءهم «إن الظن لا يُغني من الحق شيئا» فيما المطلوب منه العلم «إن الله عليم بما يفعلون» فيجازيهم عليه.'),
(1401, 10, 37, '«وما كان هذا القرآن أن يُفترى» أي افتراءً «من دون الله» أي غيره «ولكن» أنزل «تصديق الذي بين يديه» من الكتب «وتفصيل الكتاب» تبيين ما كتبه الله من الأحكام وغيرها «لا ريب» شك «فيه من رب العالمين» متعلق بتصديق أو بأنزل المحذوف، وقرئ برفع تصديق وتفصيل بتقدير هو.'),
(1402, 10, 38, '«أم» بل أ «يقولون افتراه» اختلقه محمد «قل فأتوا بسورة مثله» في الفصاحة والبلاغة على وجه الافتراء فإنكم عربيون فصحاء مثلي «وادعوا» للإعانة عليه «من استطعتم من دون الله» أي غيره «إن كنتم صادقين» في أنه افتراء فلم تقدروا على ذلك، قال تعالى.'),
(1403, 10, 39, '«بل كذبوا بما لم يحيطوا بعلمه» أي القرآن ولم يتدبروه «ولما» لم «يأتهم تأويله» عاقبة ما فيه من الوعيد «كذلك» التكذيب «كذَّب الذين من قبلهم» رسلهم «فانظر كيف كان عاقبة الظالمين» بتكذيب الرسل أي آخر أمرهم من الهلاك فكذلك نُهلك هؤلاء.'),
(1404, 10, 40, '«ومنهم» أي أهل مكة «من يؤمن به» لعلم الله ذلك منهم «ومنهم من لا يؤمن به» أبدا «وربك أعلم بالمفسدين» تهديد لهم.'),
(1405, 10, 41, '«وإن كذبوك فقل» لهم «لي عملي ولكم عملكم» أي لكلٍّ جزاء عمله «أنتم بريئون مما أعمل وأنا بريء مما تعلمون» وهذا منسوخ بآية السيف.'),
(1406, 10, 42, '«ومنهم من يستمعون إليك» إذا قرأت القرآن «أ فأنت تُسمع الصم» شبههم بهم في عدم الانتفاع بما يتلى عليهم «ولو كانوا» مع الصمم «لا يعقلون» يتدبرون.'),
(1407, 10, 43, '(ومنهم من ينظر إليك أفأنت تهدي العمي ولو كانوا لا يبصرون) شبههم بهم في عدم الاهتداء بل أعظم "" فإنها لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور "".'),
(1408, 10, 44, '«إن الله لا يظلم الناس شيئا ولكن الناس أنفسهم يظلمون».'),
(1409, 10, 45, '«ويوم يحشرهم كأن» أي كأنهم «لم يلبثوا» في الدنيا أو القبور «إلا ساعة من النهار» لهول ما رأوا، وجملة التشبيه حال من الضمير «يتعارفون بينهم» يعرف بعضهم بعضا إذا بعثوا ثم ينقطع التعارف لشدة الأهوال، والجملة حال مقدرة أو متعلق الظرف «قد خسر الذين كذبوا بلقاء الله» بالبعث «وما كانوا مهتدين».'),
(1410, 10, 46, '«وإما» فيه إدغام نون إن الشرطية في ما المزيدة «نرينَّك بعض الذي نعدهم» به من العذاب في حياتك وجواب الشرط محذوف، أي فذاك «أو نتوفينَّك» قبل تعذيبهم «فإلينا مرجعهم ثم الله شهيد» مطلع «على ما يفعلون» من تكذيبهم وكفرهم فيعذبهم أشد العذاب.'),
(1411, 10, 47, '«ولكل أمة» من الأمم «رسول فإذا جاء رسولهم» إليهم فكذبوه «قضي بينهم بالقسط» بالعدل فيعذبون وينجى الرسول ومن صدقه «وهم لا يظلمون» بتعذيبهم بغير جرم فكذلك نفعل بهؤلاء.'),
(1412, 10, 48, '«ويقولون متى هذا الوعد» بالعذاب «إن كنتم صادقين» فيه.'),
(1413, 10, 49, '«قل لا أملك لنفسي ضرا» أدفعه «ولا نفعا» أجلبه «إلا ما شاء الله» أن يقدرني عليه، فكيف أملك لكم حلول العذاب «لكل أمة أجل» مدة معلومة لهلاكهم «إذ جاء أجلهم فلا يستأخرون» يتأخرون عنه «ساعة ولا يستقدمون» يتقدمون عليه.'),
(1414, 10, 50, '«قل أرأيتم» أخبروني «إن أتاكم عذابه» أي الله «بياتا» ليلا «أو نهارا ماذا» أيُّ شيء «يستعجل منه» أي العذاب «المجرمون» المشركون، فيه وضع الظاهر موضع المضمر، وجملة الاستفهام جواب الشرط: كقولك إذا أتيتك ماذا تعطيني، والمراد به التهويل أي ما أعظم ما استعجلوه.'),
(1415, 10, 51, '«أثُمَّ إذا ما وقع» حل بكم «آمنتم به» أي الله أو العذاب عند نزوله، والهمزة لإنكار التأخير فلا يقبل منكم ويقال لكم «آلآن» تؤمنون «وقد كنتم به تستعجلون» استهزاء.'),
(1416, 10, 52, '«ثم قيل للذين ظلموا ذوقوا عذاب الخلد» أي الذي تخلدون فيه «هل» ما «تجزون إلا» جزاء «بما كنتم تكسبون».'),
(1417, 10, 53, '«ويستنبئونك» يستخبرونك «أحق هو» أي ما وعدتنا به من العذاب والبعث «قل إي» نعم «وربي إنه لحق وما أنتم بمعجزين» بفائتين العذاب.'),
(1418, 10, 54, '«ولو أن لكل نفس ظلمت» كفرت «ما في الأرض» جميعا من الأموال «لافتدت به» من العذاب يوم القيامة «وأسرُّوا الندامة» على ترك الإيمان «لما رأوا العذاب» أخفاها رؤساؤهم عن الضعفاء الذين أضلوهم مخافة التعيير «وقضي بينهم» بين الخلائق «بالقسط» بالعدل «وهم لا يظلمون» شيئا.'),
(1419, 10, 55, '«ألا إن لله ما في السماوات والأرض ألا إن وعد الله» بالبعث والجزاء «حق» ثابت «ولكن أكثرهم» أي الناس «لا يعلمون» ذلك.'),
(1420, 10, 56, '«هو يحيي ويميت وإليه ترجعون» في الآخرة فيجازيكم بأعمالكم.'),
(1421, 10, 57, '«يا أيها الناس» أي أهل مكة «قد جاءتكم موعظة من ربكم» كتاب فيه ما لكم وما عليكم وهو القرآن «وشفاء» دواء «لما في الصدور» من العقائد الفاسدة والشكوك «وهدى» من الضلال «ورحمة للمؤمنين» به.'),
(1422, 10, 58, '«قل بفضل الله» الإسلام «وبرحمته» القرآن «فبذلك» الفضل والرحمة «فليفرحوا هو خير مما يجمعون» من الدنيا بالياء والتاء.'),
(1423, 10, 59, '«قل أرأيتم» أخبروني «ما أنزل الله» خلق «لكم من رزق فجعلتم منه حراما وحلالا» كالبحيرة والسائبة والميتة «قل آلله أذن لكم» في ذلك بالتحليل والتحريم لا «أم» بل «على الله تفترون» تكذبون بنسبة ذلك إليه.'),
(1424, 10, 60, '«وما ظن الذين يفترون على الله الكذب» أي أيّ شيء ظنهم به «يوم القيامة» أيحسبون أنه لا يعاقبهم لا «إن الله لذو فضل على الناس» بإمهالهم والإنعام عليهم «ولكن أكثرهم لا يشكرون».'),
(1425, 10, 61, '«وما تكون» يا محمد «في شأن» أمر «وما تتلو منه» أي من الشأن أو الله «من قرآن» أنزله عليك «ولا تعملون» خاطبهُ وأمته «من عمل إلا كنا عليكم شهودا» رقباء «إذ تُفيضون» تأخذون «فيه» أي العمل «وما يَعْزُبُ» يغيب «عن ربك من مثقال» وزن «ذرة» أصغر نملة «في الأرض ولا في السماء ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين» بيِّن هو اللوح المحفوظ.'),
(1426, 10, 62, '«ألا إن أولياء الله لا خوف عليهم ولا هم يحزنون» في الآخرة.'),
(1427, 10, 63, 'هم «الذين آمنوا وكانوا يتقون» الله بامتثال أمره ونهيه.'),
(1428, 10, 64, '«لهم البشرى في الحياة الدنيا» فسرت في حديث صححه الحاكم بالرؤيا الصالحة يراها الرجل أو تُرى له «وفي الآخرة» الجنة والثواب «لا تبديل لكلمات الله» لا خلف لمواعيده «ذلك» المذكور «هو الفوز العظيم».'),
(1429, 10, 65, '«ولا يحزنك قولهم» لك لست مرسلا وغيره «إن» استئناف «العزة» القوة «لله جميعا هو السميع» للقول «العليم» بالفعل فيجازيهم وينصرك.'),
(1430, 10, 66, '«ألا إن لله من في السماوات ومن في الأرض» عبيدا وملكا وخلقا «وما يتبع الذين يدعون» يعبدون «من دون الله» أي غيره أصناما «شركاء» له على الحقيقة تعالى عن ذلك «إن» ما «يتبعون» في ذلك «إلا الظن» أي ظنهم أنها آلهة تشفع لهم «وإن» ما «هم إلا يخرصون» يكذبون في ذلك.'),
(1431, 10, 67, '«هو الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا» إسناد الإبصار إليه مجاز لأنه يبصر فيه «إن في ذلك لآيات» دلالات على وحدانيته تعالى «لقوم يسمعون» سماع تدبر واتعاظ.'),
(1432, 10, 68, '«قالوا» أي اليهود والنصارى ومن زعم أن الملائكة بنات الله «اتخذ الله ولدا» قال تعالى لهم «سبحانه» تنزيها له عن الولد «هو الغني» عن كل أحد وإنما يطلب الولد من يحتاج إليه «له ما في السماوات وما في الأرض» ملكا وخلقا وعبيدا «إن» ما «عندكم من سلطان» حجة «بهذا» الذي تقولونه «أتقولون على الله ما لا تعلمون» استفهام توبيخ.'),
(1433, 10, 69, '«قل إن الذين يفترون على الله الكذب» بنسبه الولد إليه «لا يفلحون» لا يسعدون.'),
(1434, 10, 70, 'لهم «متاع» قليل «في الدنيا» يتمتعون به مدة حياتهم «ثم إلينا مرجعهم» بالموت «ثم نذيقهم العذاب الشديد» بعد الموت «بما كانوا يكفرون».'),
(1435, 10, 71, '«واتل» يا محمد «عليهم» أي كفار مكة «نبأ» خبر «نوح» ويبدل منه «إذ قال لقومه يا قوم إن كان كُبر» شق «عليكم مقامي» لبثي فيكم «وتذكيري» وعظي إياكم «بآيات الله فعلى الله توكلت فأجمعوا أمركم» اعزموا على أمر تفعلونه بي «وشركاءكم» الواو بمعني مع «ثم لا يكن أمركم عليكم غُمة» مستورا بل أظهروه وجاهروني به «ثم اقضوا إليَّ» امضوا فيما أردتموه «ولا تنظرون» تمهلون فإني لست مباليا بكم.'),
(1436, 10, 72, '«فإن تولَّيتم» عن تذكيري «فما سألتكم من أجر» ثواب عليه فتولوا «إن» ما «أجري» ثوابي «إلا على الله وأمرت أن أكون من المسلمين».'),
(1437, 10, 73, '«فكذبوه فنجيناه ومن معه في الفلك» السفينة «وجعلناهم» أي من معه «خلائف» في الأرض «وأغرقنا الذين كذبوا بآياتنا» بالطوفان «فانظر كيف كان عاقبة المنذرين» من إهلاكهم فكذلك نفعل بمن كذب.'),
(1438, 10, 74, '«ثم بعثنا من بعده» أي نوح «رسلا إلى قومهم» كإبراهيم وهود وصالح «فجاءُوهم بالبينات» المعجزات «فما كانوا ليؤمنوا بما كذبوا به من قبل» أي بعث الرسل إليهم «كذلك نطبع» نختم «على قلوب المعتدين» فلا تقبل الإيمان كما طبعنا على قلوب أولئك.'),
(1439, 10, 75, '«ثم بعثنا من بعدهم موسى وهارون إلى فرعون وملئه» قومه «بآياتنا» التسع «فاستكبروا» عن الإيمان بها «وكانوا قوما مجرمين».'),
(1440, 10, 76, '«فلما جاءهم الحق من عندنا قالوا إنَّ هذا لسحر مبين» بيّنّ ظاهر.'),
(1441, 10, 77, '«قال موسى أتقولون للحق لما جاءكم» إنه لسحر «أسحر هذا» وقد أفلح من أتى به وأبطل سحر السحرة «ولا يفلح الساحرون» والاستفهام في الموضعين للإنكار.'),
(1442, 10, 78, '«قالوا أجئتنا لتَلفِتَنا» لتردنا «عما وجدنا عليه آباءنا وتكون لكما الكبرياء» الملك «في الأرض» أرض مصر «وما نحن لكما بمؤمنين» مصدقين.'),
(1443, 10, 79, '«وقال فرعون ائتوني بكل ساحر عليم» فائق في علم السحر.'),
(1444, 10, 80, '(فلما جاء السحرة قال لهم موسى) بعد ما قالوا له "" إما أن تلقي وإما أن نكون نحن الملقين "": (ألقوا ما أنتم ملقون).'),
(1445, 10, 81, '«فلما ألقوا» حبالهم وعصيهم «قال موسى ما» استفهامية مبتدأ خبره «جئتم به السحر» بدل وفي قراءة بهمزة واحدة إخبار فما اسم موصول مبتدأ «إن الله سيبطله» أي سيمحقه «إن الله لا يصلح عمل المفسدين».'),
(1446, 10, 82, '«ويحق» يثبت ويظهر «الله الحق بكلماته» بمواعيده «ولو كره المجرمون».'),
(1447, 10, 83, '«فما آمن لموسى إلا ذرية» طائفة «من» أولاد «قومه» أي فرعون «على خوف من فرعون وملئهم أن يفتنهم» يصرفهم عن دينه بتعذيبه «وإن فرعون لعال» متكبر «في الأرض» أرض مصر «وإنه لمن المسرفين» المتجاوزين الحد بادعاء الربوبية.'),
(1448, 10, 84, '«وقال موسى يا قوم إن كنتم آمنتم بالله فعليه توكلوا إن كنتم مسلمين».'),
(1449, 10, 85, '«فقالوا على الله توكلنا ربنا لا تجعلنا فتنة للقوم الظالمين» أي لا تظهرهم علينا فيظنوا أنهم على الحق فيفتتنوا بنا.'),
(1450, 10, 86, '«ونجّنا برحمتك من القوم الكافرين».'),
(1451, 10, 87, '«وأوحينا إلى موسى وأخيه أن تبوّأا» اتخذا «لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة» مصلًّى تصلون فيه لتأمنوا من الخوف وكان فرعون منعهم من الصلاة «وأقيموا الصلاة» أتموها «وبشّر المؤمنين» بالنصر والجنة.'),
(1452, 10, 88, '«وقال موسى ربنا إنك آتيت فرعون وملأهُ زينة وأموالا في الحياة الدنيا ربنا» آتيتهم ذلك «ليضلوا» في «عن سبيلك» دينك «ربنا اطمس على أموالهم» امسخها «واشدد على قلوبهم» اطبع عليها واستوثق «فلا يؤمنوا حتى يروا العذاب الأليم» المؤلم دعا عليهم وأمَّنَ هارون على دعائه.'),
(1453, 10, 89, '«قال» تعالى «قد أجيبت دعوتكما» فمسخت أموالهم حجارة ولم يؤمن فرعون حتى أدركه الغرق «فاستقيما» على الرسالة والدعوة إلى أن يأتيهم العذاب «ولا تتبعان سبيل الذين لا يعلمون» في استعجال قضائي روي أنه مكث بعدها أربعين سنة.'),
(1454, 10, 90, '«وجاوزنا ببني إسرائيل البحر فأتْبَعَهُمْ» لحقهم «فرعون وجنود بغيا وعدوا» مفعول له «حتى إذا أدركه الغرق قال آمنت أنه» أي بأنه وفي قراءة بالكسر استئنافا «لا إله الذي آمنت به بنو إسرائيل وأنا من المسلمين» كرره ليقبل منه فلم يقبل، ودس جبريل في فيه من حمأة البحر مخافة أن تناله الرحمة، وقال له.'),
(1455, 10, 91, '«آلآن» تؤمن «وقد عصيت قبل وكنت من المفسدين» وإضلالك عن الإيمان.'),
(1456, 10, 92, '«فاليوم ننجيك» نخرجك من البحر «ببدنك» جسدك الذي لا روح فيه «لتكون لمن خلفك» بعدك «آية» عبرة فيعرفوا عبوديتك ولا يقدموا على مثل فعلك وعن ابن عباس أن بعض بني إسرائيل شكوا في موته فأخرج لهم ليروه «وإن كثيرا من الناس» أي أهل مكة «عن آياتنا لغافلون» لا يعتبرون بها.'),
(1457, 10, 93, '«ولقد بوأنا» أنزلنا «بني إسرائيل مُبَوّأ صدق» منزل كرامة وهو الشام ومصر «ورزقناهم من الطيبات فما اختلفوا» بأن آمن بعض وكفر بعض «حتى جاءهم العلم إن ربَّك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون» من أمر الدين بإنجاء المؤمنين وتعذيب الكافرين.'),
(1458, 10, 94, '(فإن كنت) يا محمد (في شك مما أنزلنا إليك) من القصص فرضاً (فاسأل الذين يقرءون الكتاب) التوراة (من قبلك) فإنه ثابت عندهم يخبروك بصدقه قال صلى الله عليه وسلم: "" لا أشك ولا أسأل "" (لقد جاءك الحق من ربك فلا تكونن من الممترين) الشاكين فيه.'),
(1459, 10, 95, '«ولا تكونن من الذين كذبوا بآيات الله فتكون من الخاسرين».'),
(1460, 10, 96, '«إن الذين حَقَّت» وجبت «عليهم كلمة ربك» بالعذاب «لا يؤمنون».'),
(1461, 10, 97, '«ولو جاءتهم كل آية حتى يروا العذاب الأليم» فلا ينفعهم حينئذ.'),
(1462, 10, 98, '«فلولا» فهلا «كانت قرية» أريد أهلها «آمنت» قبل نزول العذاب بها «فنفعها إيمانها إلا» لكن «قوم يونس لما آمنوا» عند رؤية أمارة العذاب ولم يؤخروا إلى حلوله «كشفنا عنهم عذاب الخزي في الحياة الدنيا ومتعناهم إلى حين» انقضاء آجالهم.'),
(1463, 10, 99, '«ولو شاء ربك لآمن من في الأرض كلهم جميعا أفأنت تُكره الناس» بما لم يشأه الله منهم «حتى يكونوا مؤمنين» لا.'),
(1464, 10, 100, '«وما كان لنفس أن تؤمن إلا بإذن الله» بإرادته «ويجعل الرجس» العذاب «على الذين لا يعقلون» يتدبرون آيات الله.'),
(1465, 10, 101, '«قل» لكفار مكة «انظروا ماذا» أي الذي «في السماوات والأرض» من الآيات الدالة على وحدانية الله تعالى «وما تغني الآيات والنذر» جمع نذير أي الرسل «عن قوم لا يؤمنون» في علم الله أي ما تنفعهم.'),
(1466, 10, 102, '«فهل» فما «ينتظرون» بتكذيبك «إلا مثل أيام الذين خلوا من قبلهم» من الأمم أي مثل وقائعهم من العذاب «قل فانتظروا» ذلك «إني معكم من المنتظرين».'),
(1467, 10, 103, '«ثم نُنجّي» المضارع لحكاية الحال الماضي «رسلنا والذين آمنوا» من العذاب «كذلك» الإنجاء «حقا علينا نُنجي المؤمنين» النبي وأصحابه حين تعذيب المشركين.'),
(1468, 10, 104, '«قل يا أيها الناس» أي أهل مكة «إن كنتم في شك من ديني» أنه حق «فلا أعبد الذين تعبدون من دون الله» أي غيره، وهو الأصنام لشككم فيه «ولكن أعبد الله الذي يتوفّاكم» يقبض أرواحكم «وأمرت أن» أي بأن «أكون من المؤمنين».'),
(1469, 10, 105, '«و» قيل لي «أن أقمِ وجهك للدين حنيفا» مائلا إليه «ولا تكونن من المشركين».'),
(1470, 10, 106, '«ولا تدع» تعبد «من دون الله ما لا ينفعك» إن عبدته «ولا يضرك» إن لم تعبده «فإن فعلت» ذلك فرضا «فإنك إذًا من الظالمين».'),
(1471, 10, 107, '«وإن يَمسسك» يصبك «الله بضر» كفقر ومرض «فلا كاشف» رافع «له إلا هو وإن يردك بخير فلا راد» دافع «لفضله» الذي أرادك به «يصيب به» أي بالخير «من شاء من عباده وهو الغفور الرحيم».'),
(1472, 10, 108, '«قل يا أيها الناس» أي أهل مكة «قد جاءكم الحق من ربكم فمن اهتدى فإنما يهتدي لنفسه» لأن ثواب اهتدائه له «ومن ضل فإنما يضل عليها» لأن وبال ضلاله عليها «وما أنا عليكم بوكيل» فأجبركم على الهدى.'),
(1473, 10, 109, '«واتبع ما يوحى إليك» من ربِّك «واصبر» على الدعوة وأذاهم «حتى يحكم الله» فيهم بأمره «وهو خير الحاكمين» أعدَلهم، وقد صبر حتى حكم على المشركين بالقتال وأهل الكتاب بالجزية.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, '«الر» الله أعلم بمراده بذلك، هذا «كتاب أُحكمت آياته» بعجب النظم وبديع المعاني «ثم فصِّلت» بينت بالأحكام والقصص والمواعظ «من لَدُن حكيم خبير» أي الله.'),
(1475, 11, 2, '«أ» أي بأن «لا تعبدوا إلا الله إنني لكم منه نذير» بالعذاب إن كفرتم «وبشير» بالثواب إن آمنتم.'),
(1476, 11, 3, '«وأن استغفروا ربكم» من الشرك «ثم توبوا» ارجعوا «إليه» بالطاعة «يمتعكم» في الدنيا «متاعا حسنا» بطيب عيش وسعة رزق «إلى أجل مسمى» هو الموت «ويؤت» في الآخرة «كل ذي فضل» في العمل «فضله» جزاءه «وإن تولّوا» فيه حذف إحدى التاءين، أي تُعرضوا «فإني أخاف عليكم عذاب يوم كبير» هو يوم القيامة.'),
(1477, 11, 4, '«إلى الله مرجعكم وهو على كل شيء قدير» ومنه الثواب والعذاب.'),
(1478, 11, 5, 'ونزل كما رواه البخاري عن ابن عباس فيمن كان يستحي أن يتخلى أو يجامع فيقبض إلى السماء وقبل في المنافقين «ألا إنهم يثنون صدورهم ليستخفوا منه» أي الله «ألا حين يستغشون ثيابهم» يتغطون بها «يعلم» تعالى «ما يُسرون وما يُعلنون» فلا يُغني استخفاؤهم «إنه عليم بذات الصدور» أي بما في القلوب.'),
(1479, 11, 6, '«وما من» زائدة «دابة في الأرض» هي ما دبَّ عليها «إلا على الله رزقها» تكفل به فضلا منه تعالى «ويعلم مستقرها» مسكنها في الدنيا أو الصُلب «ومستودعها» من الموت أو في الرحم «كل» ما ذكر «في كتاب مبين» بيِّن هو اللوح المحفوظ.'),
(1480, 11, 7, '«وهو الذي خلق السماوات والأرض في ستة أيام» أولها الأحد وآخرها الجمعة «وكان عرشه» قبل خلقهما «على الماء» وهو على متن الريح «ليبلوكم» متعلق بخلق أي خلقهما وما فيهما من منافع لكم ومصالح ليختبركم «أيكم أحسن عملا» أي أطوع لله «ولئن قلت» يا محمد لهم «إنكم مبعوثون من بعد الموت ليقولن الذين كفروا إن» ما «هذا» القرآن الناطق بالبعث أو الذي تقوله «إلا سحر مبين» بيَّن وفي قراءة ساحر: والمشار إليه النبي.'),
(1481, 11, 8, '«ولئن أخرنا عنهم العذاب إلى» مجيء «أمة» أوقات «معدودة ليقولن» استهزاء «ما يحبسه» ما يمنعه من النزول قال تعالى: «ألا يوم يأتيهم ليس مصروفا» مدفوعا «عنهم وحاق» نزل «بهم ما كانوا به يستهزئون» من العذاب.'),
(1482, 11, 9, '«ولئن أذقنا الإنسان» الكافر «منا رحمة» غنى وصحة «ثم نزعناها منه إنه ليئوس» قنوط من رحمة الله «كفور» شديد الكفر به.'),
(1483, 11, 10, '«ولئن أذقناه نعماءَ بعد ضرَّاء» فقر وشدة «مَسَّته ليقولون ذهب السيئات» المصائب «عني» ولم يتوقع زوالها ولا شكر عليها «إنه لفرح» بطر «فخور» على الناس بما أوتي.'),
(1484, 11, 11, '«إلا» لكن «الذين صبروا» على الضراء «وعملوا الصالحات» في النعماء «أولئك لهم مغفرة وأجر كبير» هو الجنة.'),
(1485, 11, 12, '«فلعلك» يا محمد «تارك بعض ما يوحى إليك» فلا تبلغهم إياه لتهاونهم به «وضائق به صدرك» بتلاوته عليهم لأجل «أن يقولوا لوْلا» هلا «أنزل عليه كنز أو جاء معه مَلَكٌ» يصدقه كما اقترحنا «إنما أنت نذير» فما عليك إلا البلاغ لا الإتيان بما اقترحوا «والله على كل شيء وكيل» حفيظ فيجازيهم.'),
(1486, 11, 13, '«أم» بل أ «يقولون افتراه» أي القرآن «قل فأتوا بعشر سور مثله» في الفصاحة والبلاغة «مفتريات» فإنكم عربيون فصحاء مثلي تحداهم بها أولا ثم بسورة «وادعوا» للمعاونة على ذلك «من استطعتم من دون الله» أي غيره «إن كنتم صادقين» في أنه افتراء.'),
(1487, 11, 14, '«فإ» ن «لم يستجيبوا لكم» أي من دعوتموهم للمعاونة «فاعلموا» خطاب للمشركين «أنما أنزل» ملتبسا «بعلم الله» وليس افتراء عليه «وأن» مخففة أي أنه «لا إله إلا هو فهل أنتم مسلمون» بعد هذه الحجة القاطعة أي أسلموا.'),
(1488, 11, 15, '«من كان يريد الحياة الدنيا وزينتها» بأن أصَرَّ على الشرك، وقيل هي في المرائين «نوفِّ إليهم أعمالهم» أي جزاء ما عملوه من خير كصدقة وصلة رحم «فيها» بأن نوسع عليهم رزقهم «وهم فيها» أي الدنيا «لا يبخسون» ينقصون شيئا.'),
(1489, 11, 16, '«أولئك الذين ليس لهم في الآخرة إلا النار وحبط» بطل «ما صنعوا» ـه «فيها» أي الآخرة فلا ثواب له «وباطل ما كانوا يعملون».'),
(1490, 11, 17, '«أفمن كان على بيِّنة» بيان «من ربه» وهو النبي أو المؤمنون، وهي القرآن «ويتلوه» يتبعه «شاهد» له بصدقه «منه» أي من الله وهو جبريل «ومن قبله» القرآن «كتاب موسى» التوراة شاهد له أيضا «إماما ورحمة» حال كمن ليس كذلك؟ لا «أولئك» أي من كان على بينة «يؤمنون به» أي بالقرآن فلهم الجنة «ومن يكفر به من الأحزاب» جميع الكفار «فالنار موعده فلا تَكُ في مِرْيَةِ» شك «منه» من القرآن «إنه الحق من ربك ولكن أكثر الناس» أي أهل مكة «لا يؤمنون».'),
(1491, 11, 18, '«ومن» أي لا أحد «أظلم ممن افترى على الله كذبا» بنسبة الشريك والولد إليه «أولئك يُعرضون على ربهم» يوم القيامة في جملة الخلق «ويقول الأشهاد» جمع شاهد، وهم الملائكة يشهدون للرسل بالبلاغ وعلى الكفار بالتكذيب «هؤلاء الذين كذبوا على ربهم ألا لعنة الله على الظالمين» المشركين.'),
(1492, 11, 19, '«الذين يصدون عن سبيل الله» دين الإسلام «ويبغونها» يطلبون السبيل «عوجا» «وهم بالآخرة هم» تأكيد «كافرون».'),
(1493, 11, 20, '«أولئك لم يكونوا معجزين» الله «في الأرض وما كان لهم من دون الله» أي غيره «من أولياء» أنصار يمنعونهم من عذابه «يضاعف لهم العذاب» بإضلالهم غيرهم «وما كانوا يستطيعون السمع» للحق «وما كانوا يبصرونـ» ـه أي لفرط كراهتهم له كأنهم لم يستطيعوا ذلك.'),
(1494, 11, 21, '«أولئك الذين خسروا أنفسهم» لمصيرهم إلى النار المؤبدة عليهم «وضلَّ» غاب «عنهم ما كانوا يفترون» على الله من دعوى الشريك.'),
(1495, 11, 22, '«لا جَرَمَ» حقا «أنهم في الآخرة هم الأخسرون».'),
(1496, 11, 23, '«إن الذين آمنوا وعملوا الصالحات وأخبتوا» سكنوا واطمأنوا أو أنابوا «إلى ربهم أولئك أصحاب الجنة هم فيها خالدون».'),
(1497, 11, 24, '«مثل» صفة «الفريقين» الكفار والمؤمنين «كالأعمى والأصم» هذا مثل الكافر «والبصير والسميع» هذا مثل المؤمن «هل يستويان مثلا» لا «أفلا تذَّكرون» فيه إدغام التاء في الأصل في الذال تتعظون.'),
(1498, 11, 25, '«ولقد أرسلنا نوحا إلى قومه أني» أي بأني وفي قراءة بالكسر على حذف القول «لكم نذير مبين» بيّن الإنذار.'),
(1499, 11, 26, '«أن» أي بأن «لا تعبدوا إلا الله إني أخاف عليكم» إن عبتم غيره «عذاب يوم أليم» مؤلم في الدنيا والآخرة.'),
(1500, 11, 27, '«فقال الملأ الذين كفروا من قومه» وهم الأشراف «ما نراك إلا بشرا مثلنا» ولا فضل لك علينا «وما نراك اتبعك إلا الذين هم أراذلنا» أسافلنا كالحاكة والأساكفة «بادئ الرأي» بالهمز وتركه أي ابتداء من غير تفكر فيك ونصبه على الظرف أي وقت حدوث أول رأيهم «وما نرى لكم علينا من فضل» فتستحقون به الاتباع منا «بل نظنكم كاذبين» في دعوى الرسالة أدرجوا قومه معه في الخطاب.'),
(1501, 11, 28, '«قال يا قوم أرأيتم» أخبروني «إن كنت على بينة» بيان «من ربي وآتاني رحمة» نبوة «من عنده فَعَميتْ» خفيت «عليكم» وفي قراءة بتشديد الميم والبناء للمفعول «أنُلزمُكُموها» أنجبركم على قبولها «وأنتم لها كارهون» لا نقدر على ذلك.'),
(1502, 11, 29, '«ويا قوم لا أسألكم عليه» على تبليغ الرسالة «مالا» تعطونيه «إن» ما «أجري» ثوابي «إلا على الله وما أنا بطارد الذين آمنوا» كما أمرتموني «إنهم ملاقو ربهم» بالبعث فيجازيهم ويأخذ لهم ممن ظلمهم وطردهم «ولكني أراكم قوما تجهلون» عاقبة أمركم.'),
(1503, 11, 30, '«ويا قوم من ينصرني» يمنعني «من الله» أي عذابه «إن طردتهم» أي لا ناصر لي «أفلا» فهلا تذَّكرون» بإدغام التاء الثانية في الأصل في الذال تتعظون.'),
(1504, 11, 31, '«ولا أقول لكم عندي خزائن الله ولا» إني «أعلم الغيب ولا أقول إني مَلَكٌ» بل أنا بشر مثلكم «ولا أقول للذين تزدري» تحتقر «أعينكم لن يؤتيهم الله خيرا الله أعلم بما في أنفسهم» قلوبهم «إني إذًا» إن قلت ذلك «لمن الظالمين».'),
(1505, 11, 32, '«قالوا يا نوح قد جادلتنا» خاصمتنا «فأكثرت جدالنا فأتنا بما تعدنا» به من العذاب «إن كنت من الصادقين» فيه.'),
(1506, 11, 33, '«قال إنما يأتيكم به الله إن شاء» تعجيله لكم فإن أمره إليه لا إليَّ «وما أنتم بمعجزين» بفائتين الله.'),
(1507, 11, 34, '(ولا ينفعكم نصحي إن أردت أن أنصح لكم إن كان الله يريد أن يغويكم) أي إغواءكم، وجواب الشرط دل عليه "" ولا ينفعكم نصحي "" (هو ربكم وإليه ترجعون) قال تعالى:'),
(1508, 11, 35, '«أم» بل أ «يقولون» أي كفار مكة «افتراه» اختلق محمد القرآن «قل إن افتريته فعليَّ إجرامي» إثمي، أي عقوبته «وأنا بريء مما تجرمون» من إجرامكم في نسبة الافتراء إلي.'),
(1509, 11, 36, '(وأوحي إلى نوح أنه لن يؤمن من قومك إلا من قد آمن فلا تبتئس) تحزن (بما كانوا يفعلون) من الشرك فدعا عليهم بقوله، "" رب لا تذر على الأرض "" إلخ، فأجاب الله دعاءه فقال:'),
(1510, 11, 37, '«واصنع الفلك» السفينة «بأعيننا» بمرأى منا وحفظنا «ووحينا» أمرنا «ولا تخاطبني في الذين ظلموا» كفروا بترك إهلاكهم «إنهم مُغرقون».'),
(1511, 11, 38, '«ويصنع الفلك» حكاية حال ماضية «وكلما مرَّ عليه ملاٌ» جماعة «من قومه سخروا منه» استهزءوا به «قال إن تسخروا منا فإنا نسخر منكم كما تسخرون» إذا نجونا وغرقتم.'),
(1512, 11, 39, '«فسوف تعلمون من» موصولة مفعول العلم «يأتيه عذاب يخزيه ويحلّ» ينزل «عليه عذاب مقيم».'),
(1513, 11, 40, '«حتى» غاية للصنع «إذا جاء أمرنا» بإهلاكهم «وفار التنور» للخباز بالماء وكان ذلك علامة لنوح «قلنا احمل فيها» في السفينة «من كلِ زوجين» ذكرا وأنثى، أي من كل أنواعهما «اثنين» ذكرا وأنثى وهو مفعول وفي القصة أن الله حشر لنوح السباع والطير وغيرها، فجعل يضرب بيديه في كل نوع فتقع يده اليمنى على الذكر واليسرى على الأنثى فيحملهما في السفينة «وأهلك» أي زوجته وأولاده «إلا من سبق عليه القول» أي منهم بالإهلاك وهو زوجته وولده كنعان بخلاف سام وحام ويافث فحملهم وزوجاتهم الثلاثة «ومن آمن وما آمن معه إلا قليل» قيل كانوا ستة رجال ونساءهم وقيل: جميع من كان في السفينة ثمانون نصفهم رجال ونصفهم نساء.'),
(1514, 11, 41, '«وقال» نوح «اركبوا فيها بسم الله مجراها ومرساها» بفتح الميمين وضمهما مصدران أي جريها ورسوها أي منتهى سيرها «إن ربي لغفور رحيم» حيث لم يهلكنا.'),
(1515, 11, 42, '«وهي تجري بهم في موج كالجبال» في الارتفاع والعظم «ونادى نوح ابنه» كنعان «وكان في معزل» عن السفينة «يا بني اركب معنا ولا تكن مع الكافرين».'),
(1516, 11, 43, '«قال سآوي إلى جبل يعصمني» يمنعني «من الماء قال لا عاصم اليوم من أمر الله» عذابه «إلا» لكن «من رحم» الله فهو المعصوم قال تعالى «وحال بينهما الموج فكان من المغرقين».'),
(1517, 11, 44, '«وقيل يا أرض ابلعي ماءك» الذي نبع منك فشربته دون ما نزل من السماء فصار أنهارا وبحارا «ويا سماء أقلعي» أمسكي عن المطر فأمسكت «وغيض» نقص «الماء وقضي الأمر» تم أمر هلاك قوم نوح «واستوت» وقفت السفينة «على الجودِي» جبل بالجزيرة بقرب الموصل «وقيل بُعدا» هلاكا «للقوم الظالمين» الكافرين.'),
(1518, 11, 45, '«ونادى نوح ربه فقال رب إن ابني» كنعان «من أهلي» وقد وعدتني بنجاتهم «وإن وعدك الحق» الذي لا خلف فيه «وأنت أحكم الحاكمين» أعلمهم وأعدلهم.'),
(1519, 11, 46, '«قال» تعالى «يا نوح إنه ليس من أهلك» الناجين أو من أهل دينك «إنه» أي سؤالك إياي بنجاته «عمل غير صالح» فإنه كافر ولا نجاة للكافرين وفي قراءة بكسر ميم عمل فعل ونصب غير فالضمير لابنه «فلا تسألنّ» بالتشديد والتخفيف «وما ليس لك به علم» من إنجاء ابنك «إني أعظك أن تكون من الجاهلين» بسؤالك ما لم تعلم.'),
(1520, 11, 47, '«قال ربّ إني أعوذ بك» من «أن أسألك ما ليس لي به علم وإلا تغفر لي» ما فرط مني «وترحمني أكن من الخاسرين».'),
(1521, 11, 48, '«قيل يا نوح اهبط» انزل من السفينة «بسلام» بسلامة أو بتحية «منا وبركات» خيرات «عليك وعلى أمم ممن معك» في السفينة أي من أولادهم وذريتهم وهم المؤمنون «وأمم» بالرفع ممن معك «سنمتعهم» في الدنيا «ثم يَمَسُّهم منا عذاب أليم» في الآخرة وهم الكفار.'),
(1522, 11, 49, '«تلك» أي هذه الآيات المتضمنة قصة نوح «من أنباء الغيب» أخبار ما غاب عنك «نوحيها إليك» يا محمد «ما كنت تعلمها أنت ولا قومك من قبل هذا» القرآن «فاصبر» على التبليغ وأذى قومك كما صبر نوح «إن العاقبة» المحمودة «للمتقين».'),
(1523, 11, 50, '«و» أرسلنا «إلى عاد أخاهم» من القبيلة «هودا قال يا قوم اعبدوا الله» وَحِّدوهُ «ما لكم من» زائدة «إله غيره إن» ما «أنتم» في عبادتكم الأوثان «إلا مفترون» كاذبون على الله.'),
(1524, 11, 51, '«يا قوم لا أسألكم عليه» على التوحيد «أجرا إن» ما «أجري إلا على الذي فطرني» خلقني «أفلا تعقلون».'),
(1525, 11, 52, '«ويا قوم استغفروا ربكم» من الشرك «ثم توبوا» ارجعوا «إليه» بالطاعة «يرسل السماء» المطر وكانوا قد منعوه «عليكم مِدرارا» كثير الدرور «ويزدكم قوة إلى» مع «قوتكم» بالمال والولد «ولا تتولوا مجرمين» مشركين.'),
(1526, 11, 53, '«قالوا يا هود ما جئتنا ببيِّنة» برهان على قولك «وما نحن بتاركي آلهتنا عن قولك» أي لقولك «وما نحن لك بمؤمنين».'),
(1527, 11, 54, '«إن» ما «نقول» في شأنك «إلا اعتراك» أصابك «بعض آلهتنا بسوءٍ» فخبلك لسبك إياها فأنت تهذي «قال إني أشهد الله» عليَّ «واشهدوا أني بريء مما تشركونـ» ـه به.'),
(1528, 11, 55, '«من دونه فكيدوني» احتالوا في هلاكي «جميعا» أنتم وأوثانكم «ثم لا تُنظرون» تمهلون.'),
(1529, 11, 56, '«إني توكلت على الله ربي وربكم ما من» زائدة «دابة» نسمة تدب على الأرض «إلا هو آخذ بناصيتها» أي مالكها وقاهرها فلا نفع ولا ضرر إلا بإذنه، وخص الناصية بالذكر لأن من أخذ بناصيته يكون في غاية الذل «إن ربي على صراط مستقيم» أي طريق الحق والعدل.'),
(1530, 11, 57, '«فإن تولوْا» فيه حذف إحدى التاءين، أي تعرضوا «فقد أبلغتكم ما أرسلت به إليكم ويستخلف ربي قوما غيركم ولا تضرونه شيئا» بإشراككم «إن ربي على كل شيء حفيظ» رقيب.'),
(1531, 11, 58, '«ولما جاء أمرنا» عذابنا «نجينا هودا والذين آمنوا معه برحمة» هداية «منا ونجيناهم من عذاب غليظ» شديد.'),
(1532, 11, 59, '«قيل يا نوح اهبط» انزل من السفينة «بسلام» بسلامة أو بتحية «منا وبركات» خيرات «عليك وعلى أمم ممن معك» في السفينة أي من أولادهم وذريتهم وهم المؤمنون «وأمم» بالرفع ممن معك «سنمتعهم» في الدنيا «ثم يَمَسُّهم منا عذاب أليم» في الآخرة وهم الكفار.'),
(1533, 11, 60, '«وأتبعوا في هذه الدنيا لعنة» من الناس «ويوم القيامة» لعنة على رءوس الخلائق «ألا إن عادا كفروا» جحدوا «ربهم ألا بُعدا» من رحمة الله «لعاد قوم هود».'),
(1534, 11, 61, '«و» أرسلنا «إلى ثمود أخاهم» من القبيلة «صالحا قال يا قوم اعبدوا الله» وحدوه «ما لكم من إله غيره هو أنشأكم» ابتدأ خلقكم «من الأرض» بخلق أبيكم آدم منها «واستعمركم فيها» جعلكم عمارا تسكنون بها «فاستغفروه» من الشرك «ثم توبوا» ارجعوا «إليه» بالطاعة «إن ربي قريب» من خلقه بعلمه «مجيب» لمن سأله.'),
(1535, 11, 62, '«قالوا يا صالح قد كنت فينا مرجوًّا» نرجو أن تكون سيدا «قبل هذا» الذي صدر منك «أتنهانا أن نعبد ما يعبد آباؤنا» من الأوثان «وإننا لفي شك مما تدعونا إليه» من التوحيد «مريب» موقع في الريب.'),
(1536, 11, 63, '«قال يا قوم أرأيتم إن كنت على بيِّنة» بيان «من ربي وآتاني منه رحمة» نبوة «فمن ينصرني» يمنعني «من الله» أي عذابه «إن عصيته فما تزيدونني» بأمركم لي بذلك «غير تخسير» تضليل.'),
(1537, 11, 64, '«ويا قوم هذه ناقة الله لكم آية» حال عامله الإشارة «فذروها تأكل في أرض الله ولا تمسوها بسوء» عقر «فيأخذكم عذاب قريب» إن عقرتموها.'),
(1538, 11, 65, '«فعقروها» عقرها قدار بأمرهم «فقال» صالح «تمتعوا» عيشوا «في داركم ثلاثة أيام» ثم تهلكون «ذلك وعدٌ غير مكذوب» فيه.'),
(1539, 11, 66, '«فلما جاء أمرنا» بإهلاكهم «نجَّينا صالحا والذين آمنوا معه» وهم أربعة آلاف «برحمة منا و» نجيناهم «من خزي يومِئذ» بكسر الميم إعرابا وفتحها بناء لإضافته إلى مبني وهو الأكثر «إن ربك هو القوي العزيز» الغالب.'),
(1540, 11, 67, '«وأخذ الذين ظلموا الصيحة فأصبحوا في ديارهم جاثمين» باركين على الركب ميِّتين.'),
(1541, 11, 68, '«كأن» مخففة واسمها محذوف أي كأنهم «لم يَغنوا» يقيموا «فيها» في دارهم «ألا إن ثمود كفروا ربهم ألا بُعدا لثمود» بالصرف وتركه على معنى الحي والقبيلة.'),
(1542, 11, 69, '«ولقد جاءت رسلنا إبراهيم بالبشرى» بإسحاق ويعقوب بعده «قالوا سلاما» مصدر «قال سلام» عليكم «فما لبث أن جاء بعجل حنيذِ» مشوي.'),
(1543, 11, 70, '«فلما رأى أيديهم لا تصل إليه نكرهم» بمعنى أنكرهم «وأوجس» أضمر في نفسه «منهم خيفة» خوفا «قالوا لا تخف إنا أرسلنا إلى قوم لوط» لنهلكهم.'),
(1544, 11, 71, '«وامرأته» أي امرأة إبراهيم سارة «قائمة» تخدمهم «فضحكت» استبشارا بهلاكهم «فبشرناها بإسحاق ومن وراء» بعد «إسحاق يعقوب» ولده تعيش إلى أن تراه.'),
(1545, 11, 72, '«قالت يا ويلتى» كلمة تقال عند أمر عظيم والألف مبدلة من ياء الإضافة «أألد وأنا عجوز» لي تسع وتسعون سنة «وهذا بعلي شيخا» له مائة وعشرون نصبه على الحال والعامل فيه ما في ذا من الإشارة «إن هذا لشيء عجيب» أن يولد ولد لهرمين.'),
(1546, 11, 73, '«قالوا أتعجبين من أمر الله» قدرته «رحمة الله وبركاته عليكم» يا «أهل البيت» بيت إبراهيم «إنه حميد» محمود «مجيد» كريم.'),
(1547, 11, 74, '«فلما ذهب عن إبراهيم الروع» الخوف «وجاءته البشرى» بالولد أخذ «يجادلنا» يجادل رسلنا «في» شأن «قوم لوط».'),
(1548, 11, 75, '«إن إبراهيم لحليم» كثير الأناة «أوَّاهٌ مُنيب» رجّاع، فقال لهم أتهلكون قرية فيها ثلاثمائة مؤمن؟ قالوا لا، أفتهلكون قرية فيها مائتا مؤمن؟ قالوا لا، قال أفتهلكون قرية فيها أربعون مؤمنا؟ قالوا لا، قال أفتهلكون قرية فيها أربعة عشر مؤمنا؟ قالوا لا، قال أفرأيتم إن كان فيها مؤمن واحد قالوا لا، إن فيها لوطا قالوا نحن أعلم بمن فيها إلخ.'),
(1549, 11, 76, 'فلما أطال مجادلتهم قالوا: «يا إبراهيم أعرض عن هذا» الجدال «إنه قد جاء أمر ربك» بهلاكهم «وإنهم آتيهم عذاب غير مردود».'),
(1550, 11, 77, '«ولما جاءت رسلنا لوطا سيء بهم» حزن بسبهم «وضاق بهم ذرعا» صدرا لأنهم حسان الوجوه في صورة أضياف فخاف عليهم قومه «وقال هذا يوم عصيب» شديد.'),
(1551, 11, 78, '«وجاءه قومه» لما علموا بهم «يُهرعون» يسرعون «إليه ومن قبل» قبل مجيئهم «كانوا يعملون السيئات» وهي إتيان الرجال في الأدبار «قال» لوط «يا قوم هؤلاء بناتي» فتزوجوهن «هن أطهر لكم فاتقوا الله ولا تخزون» تفضحون «في ضيفي» أضيافي «أليس منكم رجل رشيد» يأمر بالمعروف وينهى عن المنكر.'),
(1552, 11, 79, '«قالوا لقد علمت ما لنا في بناتك من حق» حاجة «وإنك لتعلم ما نريد» من إتيان الرجال.'),
(1553, 11, 80, '«قال لو أن لي بكم قوة» طاقة «أو آوي إلى ركن شديد» عشيرة تنصرني لبطشت بكم فلما رأت الملائكة ذلك.'),
(1554, 11, 81, '«قالوا يا لوط إنا رسل ربك لن يصلوا إليك» بسوء «فأسرِ بأهلك بقطع» طائفة «من الليل ولا يلتفت منكم أحد» لئلا يرى عظيم ما ينزل بهم «إلا امرأتُك» بالرفع بدل من أحد وفي قراءة بالنصب استثناء من الأهل أي فلا تسر بها «إنه مصيبها ما أصابهم» فقيل لم يخرج بها وقيل خرجت والتفتت فقالت واقوماه فجاءها حجر فقتلها، وسألهم عن وقت هلاكهم فقالوا «إن موعدهم الصبح» فقال أريد أعجل من ذلك قالوا «أليس الصبح بقريب».'),
(1555, 11, 82, '«فلما جاء أمرنا» بإهلاكهم «جعلنا عاليها» أي قراهم «سافلها» أي بأن رفعها جبريل إلى السماء وأسقطها مقلوبة إلى الأرض «وأمطرنا عليها حجارة من سجيل» طين طبخ بالنار «منضود» متتابع.'),
(1556, 11, 83, '«مُسوَّمة» معلمة عليها اسم من يرمى بها «عند ربك» ظرف لها «وما هي» الحجارة أو بلادهم «من الظالمين» أي أهل مكة «ببعيد».'),
(1557, 11, 84, '«و» أرسلنا «إلى مدين أخاهم شعيبا قال يا قوم اعبدوا الله» وَحّدوه «ما لكم من إله غيره ولا تنقصوا المكيال والميزان إني أراكم بخير» نعمة تغنيكم عن التطفيف «وإني أخاف عليكم» إن لم تؤمنوا «عذاب يوم محيط» بكم يهلككم ووصف اليوم به مجاز لوقوعه فيها.'),
(1558, 11, 85, '«ويا قوم أوفوا المكيال والميزان» أتموهما «بالقسط» بالعدل «ولا تبخسوا الناس أشياءهم» لا تنقصوهم من حقهم شيئا «ولا تعثوا في الأرض مفسدين» بالقتل وغيره من عثي بكسر المثلثة أفسد ومفسدين حال مؤكدة لمعنى عاملها تعثوا.'),
(1559, 11, 86, '«بقيَّتُ الله» رزقه الباقي لكم بعد إيفاء الكيل والوزن «خير لكم» من البخس «إن كنتم مؤمنين» «وما أنا عليكم بحفيظ» رقيب أجازيكم بأعمالكم إنما بعثت نذيرا.'),
(1560, 11, 87, '«قالوا» له استهزاء «يا شعيب أصلاتك تأمرك» بتكليف «أن نترك ما يعبد آباؤنا» من الأصنام «أو» نترك «أن نفعل في أموالنا ما نشاء» المعنى هذا أمر باطل لا يدعو إليه داع بخير «إنك لأنت الحليم الرشيد» قالوا ذلك استهزاء.'),
(1561, 11, 88, '«قال يا قوم أرأيتم إن كنت على بينة من ربي ورزقني منه رزقا حسنا» حلالا أفأشوبه بالحرام من البخس والتطفيف «وما أريد أن أخالفكم» وأذهب «إلى ما أنهاكم عنه» فأرتكبه «إن» ما «أريد إلا الإصلاح» لكم بالعدل «ما استطعت وما توفيقي» قدرتي على ذلك وغيره من الطاعات «إلا بالله عليه توكلت وإليه أنيب» أرجع.'),
(1562, 11, 89, '«ويا قوم لا يجرمنكم» يكسبنكم «شقاقي» خلافي فاعل يجرم والضمير مفعول أول، والثاني «أن يصيبكم مثل ما أصاب قوم نوح أو قوم هود أو قوم صالح» من العذاب «وما قوم لوط» أي منازلهم أو زمن هلاكهم «منكم ببعيد» فاعتبروا.'),
(1563, 11, 90, '«واستغفروا ربكم ثم توبوا إليه إن ربي رحيم» بالمؤمنين «ودود» محب لهم.'),
(1564, 11, 91, '«قالوا» إيذانا بقلة المبالاة «يا شعيب ما نفقهُ» نفهم «كثيرا مما تقول وإنا لنراك فينا ضعيفا» ذليلا «ولولا رهطك» عشيرتك «لرجمناك» بالحجارة «وما أنت علينا بعزيز» كريم عن الرجم وإنما رهطك هم الأعزة.'),
(1565, 11, 92, '«قال يا قوم أرهطي أعز عليكم من الله» فتتركوا قتلي لأجلهم ولا تحفظوني لله «واتخذتموه» أي الله «وراءكم ظهريا» منبوذا خلف ظهوركم لا تراقبونه «إن ربي بما تعملون محيط» علما فيجازيكم.'),
(1566, 11, 93, '«ويا قوم اعملوا على مكانتكم» حالتكم «إني عاملٌ» على حالتي «سوف تعلمون من» موصولة مفعول العلم «يأتيه عذاب يخزيه ومن هو كاذب وارتقبوا» انتظروا عاقبة أمركم «إني معكم رقيب» منتظر.'),
(1567, 11, 94, '«ولما جاء أمرنا» بإهلاكهم «نجينا شعيبا والذين آمنوا معه برحمة منا وأخذت الذين ظلموا الصيحة» صاح بهم جبريل «فأصبحوا في ديارهم جاثمين» باركين على الركب ميتين.'),
(1568, 11, 95, '«كأن» مخففة: أي كأنهم «لم يغنوا» يقيموا «فيها ألا بُعدا لمدين كما بعدت ثمود».'),
(1569, 11, 96, '«ولقد أرسلنا موسى بآياتنا وسلطان مبين» برهان بيّن ظاهر.'),
(1570, 11, 97, '«إلى فرعون وَمَلئِهِ فاتبعوا أمر فرعون وما أمرُ فرعون برشيد» سديد.'),
(1571, 11, 98, '«يقدم» يتقدم «قومه يوم القيامة» فيتبعونه كما اتبعوه في الدنيا «فأوردهم» أدخلهم «النار وبئس الوِرْدُ المورودُ» هي.'),
(1572, 11, 99, '«وأتبعوا في هذه» أي الدنيا «لعنةّ ويوم القيامة» لعنة «بئس الرِّفد» العون «المرفود» رفدهم.'),
(1573, 11, 100, '«ذلك» المذكور مبتدأ خبره «من أنباء القرى نقصّه عليك» يا محمد «منها» أي القرى «قائم» هلك أهله دونه «و» منها «حصيد» هلك بأهله فلا أثر له كالزرع المحصود بالمناجل.'),
(1574, 11, 101, '«وما ظلمناهم» بإهلاكهم بغير ذنب «ولكن ظلموا أنفسهم» بالشرك «فما أغنت» دفعت «عنهم آلهتهم التي يدعون» يعبدون «من دون الله» أي غيره «من» زائدة «شيء لما جاء أمر ربك» عذابه «وما زادوهم» بعبادتهم لها «غير تتبيب» تخسير.'),
(1575, 11, 102, '«وكذلك» مثل ذلك الأخذ «أخذ ربك إذا أخذ القرى» أريد أهلها «وهي ظالمة» بالذنوب: أي فلا يغني عنهم من أخذه شيء «إن أخذه أليم شديد» روى الشيخان عن أبي موسى الأشعري قال: قال رسول الله صلى الله عليه وسلم (إن الله ليملي للظالم حتى إذا أخذه لم يفلته)، ثم قرأ رسول الله صلى الله عليه وسلم (وكذلك أخذ ربك) الآية.'),
(1576, 11, 103, '«إن في ذلك» المذكور من القصص «لآية» لعبرة «لمن خاف عذاب الآخرة ذلك» أي يوم القيامة «يوم مجموع له» فيه «الناس وذلك يوم مشهود» يشهده جميع الخلائق.'),
(1577, 11, 104, '«وما نؤخره إلا لأجل معدود» لوقت معلوم عند الله.'),
(1578, 11, 105, '«يوم يأت» ذلك اليوم «لا تكلم» فيه حذف إحدى التاءين «نفس إلا بإذنه» تعالى «فمنهم» أي الخلق «شقي و» منهم «سعيد» كتب كل في الأزل.'),
(1579, 11, 106, '«فأما الذين شقوا» في علمه تعالى «ففي النار لهم فيها زفير» صوت شديد «وشهيق» صوت ضعيف.'),
(1580, 11, 107, '«خالدين فيها ما دامت السماوات والأرض» أي مدة دوامهما في الدنيا «إلا» غير «ما شاء ربك» من الزيادة على مدتهما مما لا منتهى له والمعنى خالدين فيها أبدا «إن ربك فعال لما يريد».'),
(1581, 11, 108, '«وأما الذين سَُعدوا» بفتح السين وضمها «ففي الجنة خالدين فيها ما دامت السماوات والأرض إلا» غير «ما شاء ربك» كما تقدم، ودل عليه فيهم قوله «عطاءً غير مجذوذ» مقطوع وما تقدم من التأويل هو الذي ظهر وهو خال من التكلف والله أعلم بمراده.'),
(1582, 11, 109, '«فلا تَكُ» يا محمد «في مرية» شك «مما يعبد هؤلاء» من الأصنام إنا نعذبهم كما عذبنا من قبلهم وهذا تسلية للنبي صلى الله عليه وسلم «ما يعبدون إلا كما يعبد آباؤهم» أي كعبادتهم «من قبل» وقد عذبناهم «وإنا لموفوهم» مثلهم «نصيبهم» حظهم من العذاب «غير منقوص» أي تاما.'),
(1583, 11, 110, '«ولقد آتينا موسى الكتاب» التوراة «فاختلف فيه» بالتصديق والتكذيب كالقرآن «ولولا كلمة سبقت من ربك» بتأخير الحساب والجزاء للخلائق إلى يوم القيامة «لقضي بينهم» في الدنيا فيما اختلفوا فيه «وإنهم» أي المكذِّبين به «لفي شك منه مريب» موقع في الريبة.'),
(1584, 11, 111, '«وإن» بالتخفيف والتشديد «كلا» أي كل الخلائق «لما» ما زائدة واللام موطئة لقسم مقدر أو فارقة وفي قراءة بتشديد لما بمعنى إلا فإن نافية «ليوفينهم ربك أعمالهم» أي جزاءها «إنه بما يعملون خبير» عالم ببواطنه كظواهره.'),
(1585, 11, 112, '«فاستقم» على العمل بأمر ربك والدعاء إليه «كما أمرت و» ليستقم «من تاب» آمن «معك ولا تطغوْا» تجاوزوا حدود الله «إنه بما تعملون بصير» فيجازيكم به.'),
(1586, 11, 113, '«ولا تركنوا» تميلوا «إلى الذين ظلموا» بمودة أو مداهنة أو رضا بأعمالهم «فتمسكم» تصيبكم «النار وما لكم من دون الله» أي غيره «من» زائدة «أولياء» يحفظونكم منه «ثم لا تنصرون» تمتعون من عذابه.'),
(1587, 11, 114, '(وأقم الصلاة طرفي النهار) الغداة والعشي أي: الصبح والظهر والعصر (وزلفا) جمع زلفة أي: طائفة (من الليل) المغرب والعشاء (إن الحسنات) كالصلوات الخمس (يذهبن السيئات) الذنوب الصغائر نزلت فيمن قبل أجنبية فأخبره النبي صلى الله عليه وسلم فقال ألي هذا؟ فقال "" لجميع أمتي كلهم "" رواه الشيخان (ذلك ذكرى للذاكرين) عظة للمتعظين.'),
(1588, 11, 115, '«واصبر» يا محمد على أذى قومك أو على الصلاة «فإن الله لا يضيع أجر المحسنين» بالصبر على الطاعة.'),
(1589, 11, 116, '«فلولا» فهلا «كان من القرون» الأمم الماضية «من قبلكم أولو بقية» أصحاب دين وفضل «ينهون عن الفساد في الأرض» المراد به النفي: أي ما كان فيهم ذلك «إلا» لكن «قليلا ممن أنجينا منهم» نهوا فنجوا ومن للبيان «واتبع الذين ظلموا» بالفساد وترك النهي «ما أترفوا» نعموا «فيه وكانوا مجرمين».'),
(1590, 11, 117, '«وما كان ربك ليهلك القرى بظلم» منه لها «وأهلها مصلحون» مؤمنون.'),
(1591, 11, 118, '«ولو شاء ربك لجعل الناس أمَّة واحدة» أهل دين واحد «ولا يزالون مختلفين» في الدين.'),
(1592, 11, 119, '«إلا من رحم ربك» أراد لهم الخير فلا يختلفون فيه «ولذلك خلقهم» أي أهل الاختلاف له وأهل الرحمة لها «وتمت كلمة ربك» وهي «لأملأن جهنم من الجِنَّة والناس أجمعين».'),
(1593, 11, 120, '«وكلا» نصب بنقص وتنوينه عوض المضاف إليه أي كل ما يحتاج إليه «نقص عليك من أنباء الرسل ما» بدل من «كلا»، «نثبت» نطمئن «به فؤادك» قلبك «وجاءك في هذه» الأنباء أو الآيات «الحق وموعظة وذكرى للمؤمنين» خصوا بالذكر لانتفاعهم بها في الإيمان بخلاف الكفار.'),
(1594, 11, 121, '«وقل للذين لا يؤمنون اعملوا على مكانتكم» حالتكم «إنا عاملون» على حالتنا تهديد لهم.'),
(1595, 11, 122, '«وانتظروا» عاقبة أمركم «إنا منتظرون» ذلك.'),
(1596, 11, 123, '«ولله غيب السماوات والأرض» أي علم ما غاب فيهما «وإليه يَرجع» بالبناء للفاعل يعود وللمفعول يرد «الأمر كله» فينتقم ممن عصى «فاعبده» وحده «وتوكل عليه» ثق به فإنه كافيك «وما ربك بغافل عما يعملون» وإنما يؤخرهم لوقتهم وفي قراءة بالفوقانية.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, '«الر» الله أعلم بمراده بذلك «تلك» هذه الآيات «آيات الكتاب» القرآن والإضافة بمعنى من «المبين» المظهر للحق من الباطل.'),
(1598, 12, 2, '«إنا أنزلناه قرآنا عربيا» بلغة العرب «لعلكم» يا أهل مكة «تعقلون» تفقهون معانيه.'),
(1599, 12, 3, '«نحن نقص عليك أحسن القصص بما أوحينا» بإيحائنا «إليك هذا القرآن وإن» مخففة أي وإنه «كنت من قبله لمن الغافلين».'),
(1600, 12, 4, 'اذكر «إذ قال يوسف لأبيه» يعقوب «يا أبت» بالكسر دلالة على ياء الإضافة المحذوفة والفتح دلالة على ألف محذوفة قلبت عن الياء «إني رأيت» في المنام «أحد عشر كوكبا والشمس والقمر رأيتهم» تأكيد «لي ساجدين» جمع بالياء والنون للوصف بالسجود الذي هو من صفات العقلاء.'),
(1601, 12, 5, '«قال يا بني لا تقصص رؤياك على إخوتك فيكيدوا لك كيدا» يحتالون في هلاكك حسدا لعلمهم بتأويلها من أنهم الكواكب والشمس أمك والقمر أبوك «إن الشيطان للإنسان عدو مبين» ظاهر العداوة.'),
(1602, 12, 6, '«وكذلك» كما رأيت «يجتبيك» يختارك «ربك ويعلمك من تأويل الأحاديث» تعبير الرؤيا «ويتم نعمته عليك» بالنبوة «وعلى آل يعقوب» أولاده «كما أتمها» بالنبوة «على أبويك من قبل إبراهيم وإسحاق إن ربك عليم» بخلقه «حكيم» في صنعه بهم.'),
(1603, 12, 7, '«لقد كان في» خبر «يوسف وإخوته» وهم أحد عشر «آيات» عبر «للسائلين» عن خبرهم.'),
(1604, 12, 8, 'اذكر «إذ قالوا» أي بعض إخوة يوسف لبعضهم «ليوسف» مبتدأ «وأخوه» شقيقه بنيامين «أحب» خبر «إلى أبينا منا ونحن عصبة» جماعة «إن أبانا لفي ضلال» خطأ «مبين» بين بإيثارهما علينا.'),
(1605, 12, 9, '«اقتلوا يوسف أو اطرحوه أرضا» أي بأرض بعيدة «يخل لكم وجه أبيكم» بأن يقبل عليكم ولا يلتفت لغيركم «وتكونوا من بعده» أي بعد قتل يوسف أو طرحه «قوما صالحين» بأن تتوبوا.'),
(1606, 12, 10, '«قال قائل منهم» هو يهوذا «لا تقتلوا يوسف وألقوه» اطرحوه «في غيابت الجب» مظلم البئر في قراءة بالجمع «يلتقطه بعض السيارة» المسافرين «إن كنتم فاعلين» ما أردتم من التفريق فاكتفوا بذلك.'),
(1607, 12, 11, '«قالوا يا أبانا مالك لا تأمنا على يوسف وإنا له لناصحون» لقائمون بمصالحه.'),
(1608, 12, 12, '«أرسله معنا غدا» إلى الصحراء «نرتع ونلعب» بالنون والياء فيهما ننشط ونتسع «وإنَّا له لحافظون».'),
(1609, 12, 13, '«قال إني ليحزنني أن تذهبوا» أي ذهابكم «به» لفراقه «وأخاف أن يأكله الذئب» المراد به الجنس وكانت أرضهم كثيرة الذئاب «وأنتم عنه غافلون» مشغلون.'),
(1610, 12, 14, '«قالوا لإن» لام قسم «أكله الذئب ونحن عصبة» جماعة «إنا إذا لخاسرون» عاجزون فأرسله معهم.'),
(1611, 12, 15, '«فلما ذهبوا به وأجمعوا» عزموا «أن يجعلوه في غيابت الجب» وجواب لما محذوف أي فعلوا ذلك بأن نزعوا قميصه بعد ضربه وإهانته وإرادة قتله وأدلوه فلما وصل إلى نصف البئر ألقوه ليموت فسقط في الماء ثم أوى إلى صخرة فنادوه فأجابهم يظن رحمتهم فأرادوا رضخه بصخرة فمنعهم يهوذا «وأوحينا إليه» في الجب وحي حقيقة وله سبع عشرة سنة أو دونها تطمينا لقلبه «لتنبئنهم» بعد اليوم «بأمرهم» بصنيعهم «هذا وهم لا يشعرون» بك حال الإنباء.'),
(1612, 12, 16, '«وجاءُوا أباهم عِشاءً» وقت المساء «يبكون».'),
(1613, 12, 17, '«قالوا يا أبانا إنا ذهبا نستبقُ» نرمي «وتركنا يوسف عند متاعنا» ثيابنا «فأكله الذئب وما أنت بمؤمن» بمصدق «لنا ولو كنا صادقين» عندك لاتهمتنا في هذه القصة لمحبة يوسف فكيف وأنت تسيء الظن بنا.'),
(1614, 12, 18, '«وجاءُوا على قميصه» محله نصب على الظرفية أي فوقه «بدم كذب» أي ذي كذب بأن ذبحوا سخلة ولطخوه بدمها وذهلوا عن شقه وقالوا إنه دمه «قال» يعقوب لما رآه صحيحا وعلم كذبهم «بل سوَّلت» زينت «لكم أنفسكم أمرا» ففعلتموه به «فصبر جميل» لا جزع فيه، وهو خبر مبتدأ محذوف أي أمري «والله المستعان» المطلوب منه العون «على ما تصفون» تذكرون من أمر يوسف.'),
(1615, 12, 19, '«وجاءت سيارة» مسافرون من مدين إلى مصر فنزلوا قريبا من جب يوسف «فأرسلوا واردهم» الذي يرد الماء ليستقي منه «فأدلى» أرسل «دلوه» في البئر فتعلق بها يوسف فأخرجه فلما رآه «قال يا بشراي» وفي قراءة بشرى ونداؤها مجاز أي احضري فهذا وقتك «هذا غلام» فعلم به إخوته فأتوه «وأسَرُّوه» أي أخفوا أمره جاعليه «بضاعة» بأن قالوا هذا عبدنا أبق، وسكت يوسف خوفا من أن يقتلوه «والله عليم بما يعملون».'),
(1616, 12, 20, '«وشروه» باعوه منهم «بثمن بخس» ناقص «دراهم معدودة» عشرين أو اثنين وعشرين «وكانوا» أي إخوته «فيه من الزاهدين» فجاءت به السيارة إلى مصر فباعه الذي اشتراه بعشرين دينارا وزوجي نعل وثوبين.'),
(1617, 12, 21, '«وقال الذي اشتراه من مصر» وهو قطفير العزيز «لامرأته» زليخا «أكرمي مثواه» مقامه عندنا «عسى أن ينفعنا أو نتخذه ولدا» وكان حصورا «وكذلك» كما نجيناه من القتل والجب وعطفنا عليه قلب العزيز «مكنَّا ليوسف في الأرض» أرض مصر حتى بلغ ما بلغ «ولنعلِّمه من تأويل الأحاديث» تعبير الرؤيا عطف على مقدر متعلق بمكنا أي لنملكه أو الواو زائدة «والله غالب على أمره» تعالى لا يعجزه شيء «ولكن أكثر الناس» وهم الكفار «لا يعلمون» ذلك.'),
(1618, 12, 22, '«ولما بلغ أشده» وهو ثلاثون سنة أو وثلاث «آتيناه حكما» حكمة «وعلما» فقها في الدين قبل أن يبعث نبيا «وكذلك» كما جزيناه «نجزي المحسنين» لأنفسهم.'),
(1619, 12, 23, '«وراودته التي هو في بيتها» هي زليخا «عن نفسه» أي طلبت منه أن يواقعها «وغلَّقت الأبواب» للبيت «وقالت» له «هيْت لك» أي هلم واللام للتبيين وفي قراءة بكسر الهاء وأخرى بضم التاء «قال معاذ الله» أعوذ بالله من ذلك «إنه» الذي اشتراني «ربي» سيدي «أحسن مثواي» مقامي فلا أخونه في أهله «إنه» أي الشأن «لا يفلح الظالمون» الزناة.'),
(1620, 12, 24, '«ولقد همَّت به» قصدت منه الجماع «وهمَّ بها» قصد ذلك «لولا أن رأى برهان ربه» قال ابن عباس مَثُل له يعقوب فضرب صدره فخرجت شهوته من أنامله وجواب لولا لجامعها «كذلك» أريناه البرهان «لنصرف عنه السوء» الخيانة «والفحشاء» الزنا «إنه من عبادنا المخلصين» في الطاعة وفي قراءة بفتح اللام أي المختارين.'),
(1621, 12, 25, '«واستبقا الباب» بادر إليه يوسف للفرار وهي للتشبث به فأمسكت ثوبه وجذبته إليها «وقدَّت» شقت «قميصه من دبر وألفيا» وجدا «سيدها» زوجها «لدى الباب» فنزعت نفسها ثم «قالت ما جزاء من أراد بأهلك سوءا» زنا «إلا أن يسجن» يحبس في سجن «أو عذاب أليم» مؤلم بأن يضرب.'),
(1622, 12, 26, '«قال» يوسف متبرئا «هي راودتني عن نفسي وشهد شاهد من أهلها» ابن عمها، روي أنه كان في المهد فقال «إن كان قميصه قُدَّ من قُبل» قدام «فصدقت وهو من الكاذبين»'),
(1623, 12, 27, '«وإن كان قميصه قُدَّ من دبُر» خلف «فكذبت وهو من الصادقين».'),
(1624, 12, 28, '«فلما رأى» زوجها «قميصه قُدّ من دبر قال إنه» أي قولك (ما جزاء من أراد) إلخ «من كيدكن» أيها النساء «إن كيدكن عظيم».'),
(1625, 12, 29, 'ثم قال يا «يوسف أعرض عن هذا» الأمر ولا تذكره لئلا يشيع «واستغفري» يا زليخا «لذنبك إنك كنت من الخاطئين» الآثمين، واشتهر الخبر وشاع.'),
(1626, 12, 30, '«وقال نسوة في المدينة» مدينة مصر «امرأة العزيز تراود فتاها» عبدها «عن نفسه قد شغفها حبا» تمييز، أي دخل حبه شغاف قلبها، أي غلافه «إنا لنراها في ضلال» أي في خطأ «مبين» بيِّن بحبها إياه.'),
(1627, 12, 31, '«فلما سمعت بمكرهن» غيبتهن لها «أرسلت إليهن وأعتدت» أعدت «لهن متكأ» طعاما يقطع بالسكين للاتكاء عنده وهو الأترج «وآتت» أعطت «كل واحدة منهن سكينا وقالت» ليوسف «اخرج عليهن فلما رأينه أكبرنه» أعظمنه «وقطَّعن أيديهن» بالسكاكين ولم يشعرن بالألم لشغل قلبهن بيوسف «وقلن حاش لله» تنزيها له «ما هذا» أي يوسف «بشرا إن» ما «هذا إلا ملك كريم» لما حواه من الحسن الذي لا يكون عادة في النسمة البشرية، وفي الحديث (أنه أعطي شطر الحسن).'),
(1628, 12, 32, '«قالت» امرأة العزيز لما رأت ما حل بهن «فذلكن» فهذا هو «الذي لمتنني فيه» في حبه بيان لعذرها «ولقد راودته عن نفسه فاستعصم» امتنع «ولإن لم يفعل ما آمره» به «ليسجنن وليكونا من الصاغرين» الذليلين فقلن له أطع مولاتك.'),
(1629, 12, 33, '«قال رب السجن أحبُ إليّ مما يدعونني إليه وإلا تصرف عني كيدهن أصْبُ» أمل «إليهن وأكن» أصر «من الجاهلين» المذنبين والقصد بذلك الدعاء فلذا قال تعالى.'),
(1630, 12, 34, '«فاستجاب له ربه» دعاءه «فصرف عنه كيدهن إنه هو السميع» للقول «العليم» بالفعل.'),
(1631, 12, 35, '«ثم بدا» ظهر «لهم من بعد ما رأوا الآيات» الدالات على براءة يوسف أن يسجنوه دل على هذا «ليسجننه حتى» إلى «حين» ينقطع فيه كلام الناس فسجن.'),
(1632, 12, 36, '«ودخل معه السجن فتيان» غلامان للملك أحدهما ساقيه والآخر صاحب طعامه فرأياه يعبر الرؤيا فقالا لنختبرنه «قال أحدهما» وهو الساقي «إني أراني أعصر خمرا» أي عنبا «وقال الآخر» وهو صاحب الطعام «إني أراني أحمل فوق رأسي خبزا تأكل الطير منه نبئنا» خبرنا «بتأويله» بتعبيره «إنا نراك من المحسنين».'),
(1633, 12, 37, '«قال» لهما مخبرا أنه عالم بتعبير الرؤيا «لا يأتيكما طعام ترزقانه» في منامكما «إلا نبأتكما بتأويله» في اليقظة «قبل أن يأتيكما» تأويله «ذلكما مما علمني ربي» فيه حث على إيمانهما ثم قوّاه بقوله «إني تركت ملة» دين «قوم لا يؤمنون بالله وهم بالآخرة هم» تأكيد «كافرون».'),
(1634, 12, 38, '«واتبعت ملة آبائي إبراهيم وإسحاق ويعقوب ما كان» ينبغي «لنا أن نشرك بالله من» زائدة «شيء» لعصمتنا «ذلك» التوحيد «من فضل الله علينا وعلى الناس ولكن أكثر الناس» وهم الكفار «لا يشكرون» الله فيشركون ثم صرح بدعائهما إلى الإيمان فقال.'),
(1635, 12, 39, '«يا صاحبي» ساكني «السجن أأرباب متفرقون خير أم الله الواحد القهار» خير؟ استفهام تقرير.'),
(1636, 12, 40, '«ما تعبدون من دونه» أي غيره «إلا أسماء سميتموها» سميتم بها أصناما «أنتم وآباؤكم ما أنزل الله بها» بعادتها «من سلطان» حجة وبرهان «إن» ما «الحكم» القضاء «إلا لله» وحده «أمر ألا تعبدوا إلا إياه ذلك» التوحيد «الدين القيم» المستقيم «ولكنَّ أكثر الناس» وهم الكفار «لا يعلمون» ما يصيرون إليه من العذاب فيشركون.'),
(1637, 12, 41, '«يا صاحبي السجن أما أحدكما» أي الساقي فيخرج بعد ثلاث «فيسقي ربه» سيده «خمرا» على عادته «وأما الآخر» فيخرج بعد ثلاث «فيصلب فتأكل الطير من رأسه» هذا تأويل رؤياكما فقالا ما رأينا شيئا فقال «قضي» تمَّ «الأمر الذي فيه تستفتيان» سألتما عنه صدقتما أم كذبتما.'),
(1638, 12, 42, '«وقال للذي ظن» أيقن «أنه ناج منهما» وهو الساقي «اذكرني عند ربك» سيدك فقل له إن في السجن غلاما محبوسا ظلما فخرج «فأنساه» أي الساقي «الشيطان ذكْرَ» يوسف عند «ربه فلبث» مكث يوسف «في السجن بضع سنين» قيل سبعا وقيل اثنتي عشرة.'),
(1639, 12, 43, '«وقال الملك» ملك مصر الريان بن الوليد «إني أرى» أي رأيت «سبع بقرات سمان يأكلهن» يبتلعهن «سبع» من البقر «عجاف» جمع عجفاء «وسبع سنبلات خضر وأخر» أي سبع سنبلات «يابسات» قد التوت على الخضر وعلت عليها «يا أيها الملأ أفتوني في رؤياي» بينوا لي تعبيرها «إن كنتم للرؤيا تعبرون» فاعبروها.'),
(1640, 12, 44, '«قالوا» هذه «أضغاث أحلام» أخلاط «وما نحن بتأويل الأحلام بعالمين».'),
(1641, 12, 45, '«وقال الذي نجا منهما» أي من الفَتَيِيْنِ وهو الساقي «وادَّكر» فيه إبدال التاء في الأصل دالا وإدغامها في الدال أي تذكر «بعد أُمَّةٍ» حينِ حال يوسف «أنا أنبئكم بتأويله فأرسلون» فأرسلوه فأتى يوسف فقال.'),
(1642, 12, 46, 'يا «يوسف أيها الصدّيق» الكثير الصدق «أفتنا في سبع بقرات سمان يأكلهن سبع عجاف وسبع سنبلات خضر وأخر يابسات لعلي أرجع إلى الناس» أي الملك وأصحابه «لعلهم يعلمون» تعبيرها.'),
(1643, 12, 47, '«قال تزرعون» أي ازرعوا «سبع سنين دأبا» متتابعة وهي تأويل السبع السمان «فما حصدتم فذروه» أي اتركوه «في سنبله» لئلا يفسد «إلا قليلا مما تأكلون» فادرسوه.'),
(1644, 12, 48, '«ثم يأتي من بعد ذلك» أي السبع المخصبات «سبع شداد» مجدبات صعاب وهي تأويل السبع العجاف «يأكلن ما قدمتم لهن» من الحب المزروع في السنين المخصبات أي تأكلونه «إلا قليلا مما تحصنون» تدخرون.'),
(1645, 12, 49, '«ثم يأتي من بعد ذلك» أي السبع المجدبات «عام فيه يغاث الناس» بالمطر «وفيه يعصرون» الأعناب وغيرها لخصبه.'),
(1646, 12, 50, '«وقال الملك» لما جاءه الرسول وأخبره بتأويلها «ائتوني به» أي بالذي عبرها «فلما جاءه» أي يوسف «الرسول» وطلبه للخروج «قال» قاصدا إظهار براءته «ارجع إلى ربك فاسأله» أن يسأل «ما بال» حال «النسوة اللاتي قطعن أيديهن إن ربي» سيدي «بكيدهن عليم» فرجع فأخبر الملك فجمعهن.'),
(1647, 12, 51, '«قال ما خطبكن» شأنكن «إذ راودتن يوسف عن نفسه» هل وجدتن منه ميلا إليكن «قلن حاش لله ما علمنا عليه من سوء قالت امرأة العزيز الآن حصحص» وضح «الحق أنا راودته عن نفسه وإنه لمن الصادقين» في قوله (هي راودتني عن نفسي) فأخبر يوسف بذلك فقال.'),
(1648, 12, 52, '«ذلك» أي طلب البراءة «ليعلم» العزيز «أني لم أخنه» في أهله «بالغيب» حال «وأن الله لا يهدي كيد الخائنين» ثم تواضع لله فقال.'),
(1649, 12, 53, '«وما أبري نفسي» من الزلل «إن النفس» الجنس «لأمَّارة» كثيرة الأمر «بالسوء إلا ما» بمعنى من «رحم ربي» فعصمه «إن ربي غفور رحيم».'),
(1650, 12, 54, '«وقال الملك ائتوني به أستخلصه لنفسي» أجعله خالصا لي دون شريك فجاءه الرسول وقال: أجب الملك فقام وودع أهل السجن ودعا لهم ثم اغتسل ولبس ثيابا حسانا ودخل عليه «فلما كلمه قال» له «إنك اليوم لدينا مكين أمين» ذو مكانة وأمانة على أمرنا فماذا ترى أن نفعل؟ قال: اجمع الطعام وازرع زرعا كثيرا في هذه السنين المخصبة وادخر الطعام في سنبله فتأتي إليك الخلق ليمتاروا منك فقال: ومن لي بهذا؟.'),
(1651, 12, 55, '«قال» يوسف «اجعلني على خزائن الأرض» أرض مصر «إني حفيظ عليم» ذو حفظ وعلم بأمرها، وقيل كاتب حاسب.'),
(1652, 12, 56, '«وكذلك» كإنعامنا عليه بالخلاص من السجن «مكنَّا ليوسف في الأرض» أرض مصر «يتبوّأُ» ينزل «منها حيث يشاء» بعد الضيق والحبس وفي القصة أن الملك توَّجه وختَّمه وولاه مكان العزيز وعزله ومات بعد، فزوجه امرأته فوجدها عذراء وولدت له ولدين، وأقام العدل بمصر ودانت له الرقاب «نصيب برحمتنا من نشاء ولا نضيع أجر المحسنين».'),
(1653, 12, 57, '«ولأجر الآخرة خير» من أجر الدنيا «للذين آمنوا وكانوا يتقون» ودخلت سنو القحط وأصاب أرض كنعان والشام.'),
(1654, 12, 58, '«وجاء إخوة يوسف» إلا بنيامين ليمتاروا لما بلغهم أن عزيز مصر يعطي الطعام بثمنه «فدخلوا عليه فعرفهم» أنهم إخوته «وهم له منكرون» لا يعرفونه لبعد عهدهم به وظنهم هلاكه فكلموه بالعبرانية فقال كالمنكر عليهم: ما أقدمكم بلادي؟ فقالوا للميرة فقال لعلكم عيون قالوا معاذ الله قال فمن أين أنتم؟ قالوا من بلاد كنعان وأبونا يعقوب نبي الله، قال وله أولاد غيركم؟ قالوا نعم كنا اثني عشر فذهب أصغرنا هلك في البرية وكان أحبَّنا إليه وبقي شقيقه فاحتبسه ليتسلى به عنه فأمر بإنزالهم وإكرامهم.'),
(1655, 12, 59, '«ولما جهزهم بجهازهم» وفى لهم كيلهم «قال ائتوني بأخ لكم من أبيكم» أي بنيامين لأعلم صدقكم فيما قلتم «ألا ترون أني أوفي الكيل» أتمه من غير بخس «وأنا خير المنزلين».'),
(1656, 12, 60, '«فإن لم تأتوني به فلا كيل لكم عندي» أي ميرة «ولا تقربون» نهي أو عطف على محل فلا كيل أي تحرموا ولا تقربوا.'),
(1657, 12, 61, '«قالوا سنراود عنه أباه» سنجتهد في طلبه منه «وإنا لفاعلون» ذلك.'),
(1658, 12, 62, '«وقال لفتيته» وفي قراءة لفتيانه غلمانه «اجعلوا بضاعتهم» التي أتوا بها ثمن الميرة وكانت دراهم «في رحالهم» أوعيتهم «لعلهم يعرفونها إذا انقلبوا إلى أهلهم» وفرغوا أوعيتهم «لعلم يرجعون» إلينا لأنهم لا يستحلون إمساكها.'),
(1659, 12, 63, '«فلما رجعوا إلى أبيهم قالوا: يا أبانا منع منا الكيل» إن لم ترسل أخانا إليه «فأرسل معنا أخانا نكتل» بالنون والياء «وإنا له لحافظون».'),
(1660, 12, 64, '«قال هل» ما «آمنكم عليه إلا كما أمنتكم على أخيه» يوسف «من قبل» وقد فعلتم به ما فعلتم «فالله خير حفظا» وفي قراءة حافظا تمييز كقولهم لله دره فارسا «وهو أرحم الراحمين» فأرجو أن يمن بحفظه.'),
(1661, 12, 65, '«ولما فتحوا متاعهم وجدوا بضاعتهم ردت إليهم قالوا يا أبانا ما نبغي» ما استفهامية أي أيَّ نطلب من إكرام الملك أعظم من هذا وقرئ بالفوقانية خطابا ليعقوب وكانوا ذكروا له إكرامه لهم «هذه بضاعتنا ردت إلينا ونمير أهلنا» تأتي بالميرة لهم وهي الطعام «ونحفظ أخانا ونزداد كيل بعير» لأخينا «ذلك كيل يسير» سهل على الملك لسخائه.'),
(1662, 12, 66, '«قال لن أرسله معكم حتى تؤتون موثقا» عهدا «من الله» بأن تحلفوا «لتأتنني به إلا أن يحاط بكم» بأن تموتوا أو تغلبوا فلا تطيقوا الإتيان به فأجابوه إلى ذلك «فلما آتوه موثقهم» بذلك «قال الله على ما نقول» نحن وأنتم «وكيل» شهيد وأرسله معهم.'),
(1663, 12, 67, '«وقال يا بني لا تدخلوا» مصر «من باب واحد وادخلوا من أبواب متفرقة» لئلا تصيبكم العين «وما أغني» أدفع «عنكم» بقولي ذلك «من الله من» زائدة «شيء» قدَّره عليكم وإنما ذلك شفقة «إن» ما «الحكم إلا لله» وحده «عليه توكلت» به وثقت «وعليه فليتوكل المتوكلون».'),
(1664, 12, 68, '«قال تعالى: «ولما دخلوا من حيث أمرهم أبوهم» أي متفرقين «ما كان يغني عنهم من الله» أي قضائه «من» زائدة «شيء إلا» لكن «حاجة في نفس يعقوب قضاها» وهي إرادة دفع العين شفقة «وإنه لذو علم لما علمناه» لتعليمنا إياه «ولكن أكثر الناس» وهم الكفار «لا يعلمون» إلهام الله لأصفيائه.'),
(1665, 12, 69, '«ولما دخلوا على يوسف آوى» ضم «إليه أخاه قال إني أنا أخوك فلا تبتئس» تحزن «بما كانوا يعملون» من الحسد لنا وأمره ألا يخبرهم وتواطأ معه على أنه سيحتال على أن يبقيه عنده.'),
(1666, 12, 70, '«فلما جهزهم بجهازهم جعل السقاية» هي صاع من الذهب مرصع بالجوهر «في رحل أخيه» بنيامين «ثم أذن مؤذن» نادى مناد بعد انفصالهم عن مجلس يوسف «أيتها العير» القافلة «إنكم لسارقون».'),
(1667, 12, 71, '«قالوا و» قد «أقبلوا عليهم ماذا» ما الذي «تفقدونـ» ـه.'),
(1668, 12, 72, '«قالوا نفقد صواع» صاع «الملك ولمن جاء به حمل بعير» من الطعام «وأنا به» بالحمل «زعيم» كفيل.'),
(1669, 12, 73, '«قالوا تالله» قسم فيه معنى التعجب «لقد علمتم ما جئنا لنفسد في الأرض وما كنا سارقين» ما سرقنا قط.'),
(1670, 12, 74, '«قالوا» أي المؤذن وأصحابه «فما جزاؤه» أي السارق «إن كنتم كاذبين» في قولكم ما كنا سارقين ووجد فيكم.'),
(1671, 12, 75, '«قالوا جزاؤه» مبتدأ خبره «من وجد في رحله» يُسْتَرق ثم أكد بقوله «فهو» أي السارق «جزاؤه» أي المسروق لا غير وكانت سنة آل يعقوب «كذلك» الجزاء «نجزي الظالمين» بالسرقة فصرحوا ليوسف بتفتيش أوعيتهم.'),
(1672, 12, 76, '«فبدأ بأوعيتهم» ففتشها «قبل وعاء أخيه» لئلا يتهم «ثم استخرجها» أي السقاية «من وعاء أخيه» قال تعالى: «كذلك» الكيد «كدنا ليوسف» علمناه الاحتيال في أخذ أخيه «ما كان» يوسف «ليأخذ أخاه» رقيقا عن السرقة «في دين الملك» حكم ملك مصر لأن جزاءه عنده الضرب وتغريم مثلي المسروق لا الاسترقاق «إلا أن يشاء الله» أخذه بحكم أبيه أي لم يتمكن من أخذه إلا بمشيئة الله بإلهامه سؤال إخوته وجوابهم بسنتهم «نرفع درجاتِ من نشاء» بالإضافة والتنوين في العلم كيوسف «وفوق كل ذي علم» من المخلوقين «عليم» أعلم منه حتى ينتهي إلى الله تعالى.'),
(1673, 12, 77, '«قالوا إن يسرق فقد سرق أخ له من قبل» أي يوسف وكان سرق لأبي أمه صنما من ذهب فكسره لئلا يعبده «فأسرها يوسف في نفسه ولم يبدها» يظهرها «لهم» والضمير للكلمة التي في قوله «قال» في نفسه «أنتم شر مكانا» من يوسف وأخيه لسرقتكم أخاكم من أبيكم وظلمكم له «والله أعلم» عالم «بما تصفون» تذكرون من أمره.'),
(1674, 12, 78, '«قالوا يا أيها العزيز إن له أبا شيخا كبيرا» يحبه أكثر منا ويتسلى به عن ولده الهالك ويحزنه ففراقه «فخذ أحدنا» استبعده «مكانه» بدلا منه «إنا نراك من المحسنين» في أفعالك.'),
(1675, 12, 79, '«قال معاذ الله» نصب على المصدر حذف فعله وأضيف إلى المفعول أي نعوذ بالله من «أن نأخذ إلا من وجدنا متاعنا عنده» لم يقل من سرق تحرّزا من الكذب «إنا إذا» إن أخذنا غيره «لظالمون».'),
(1676, 12, 80, '«فلما استيأسوا» يئسوا «منه خلصوا» اعتزلوا «نجيا» مصدر يصلح للواحد وغيره أي يناجي بعضهم بعضا «قال كبيرهم» سنًّا روبيل أو رأيا: يهوذا «ألم تعلموا أن أباكم قد أخذ عليكم موثقا» عهدا «من الله» في أخيكم «ومن قبل ما» زائد «فرطتم في يوسف» وقيل ما مصدرية مبتدأ خبره من قبل «فلن أبرح» أفارق «الأرض» أرض مصر «حتى يأذن لي أبي» بالعود إليه «أو يحكم الله لي» بخلاص أخي «وهو خير الحاكمين» أعدلهم.'),
(1677, 12, 81, '«ارجعوا إلى أبيكم فقولوا يا أبانا إن ابنك سرق وما شهدنا» عليه «إلا بما علمنا» تيقنَّا من مشاهدة الصاع في رحله «وما كنا للغيب» لما غاب عنا حين إعطاء الموثق «حافظين» ولو علمنا أنه يسرق لم نأخذه.'),
(1678, 12, 82, '«واسأل القرية التي كنا فيها» هي مصر أي أرسل إلى أهلها فاسألهم «والعير» أصحاب العير «التي أقبلنا فيها» وهم قوم من كنعان «وإنا لصادقون» في قولنا فرجعوا إليه وقالوا له ذلك.'),
(1679, 12, 83, '«قال بل سولت» زينت «لكم أنفسكم أمرا» ففعلتموه اتهمهم لما سبق منهم من أمر يوسف «فصبر جميل» صبري «عسى الله أن يأتيني بهم» بيوسف وأخويه «جميعا إنه هو العليم» بحالي «الحكيم» في صنعه.'),
(1680, 12, 84, '«وتولى عنهم» تاركا خطابهم «وقال يا أسفى» الألف بدل من ياء الإضافة أي يا حزني «على يوسف وابيضت عيناه» انمحق سوادهما وبدل بياضا من بكائه «من الحزن» عليه «فهو كظيم» مغموم مكروب لا يظهر كربه.'),
(1681, 12, 85, '«قالوا تالله» لا «تفتأ» تزال «تذكر يوسف حتى تكون حرضا» مشرفا على الهلاك لطول مرضك وهو مصدر يستوي فيه الواحد وغيره «أو تكون من الهالكين» الموتى.'),
(1682, 12, 86, '«قال» لهم «إنما أشكو بثي» هو عظيم الحزن الذي لا يصبر عليه حتى يبث إلى الناس «وحزني إلى الله» لا إلى غيره فهو الذي تنفع الشكوى إليه «وأعلم من الله ما لا تعلمون» من أن رؤيا يوسف صدق وهو حي ثم قال.'),
(1683, 12, 87, '«يا بني اذهبوا فتحسسوا من يوسف وأخيه» اطلبوا خبرهما «ولا تيأسوا» تقنطوا «من روح الله» رحمته «إنه لا ييأس من روح الله إلا القوم الكافرون» فانطلقوا نحو مصر ليوسف.'),
(1684, 12, 88, '«فلما دخلوا عليه قالوا يا أيها العزيز مسنا وأهلنا الضر» الجوع «وجئنا ببضاعة مزجاة» مدفوعة يدفعها كل من رآها لرداءتها وكانت دراهم زيوفا أو غيرها «فأوف» أتم «لنا الكيل وتصدق علينا» بالمسامحة عن رداءة بضاعتنا «إن الله يجزي المتصدقين» يثيبهم فَرَقَّ لهم وأدركته الرحمة ورفع الحجاب بينه وبينهم.'),
(1685, 12, 89, 'ثم «قال» لهم توبيخا «هل علمتم ما فعلتم بيوسف» من الضرب والبيع وغير ذلك «وأخيه» من هضمكم له بعد فراق أخيه «إذ أنتم جاهلون» ما يئول إليه أمر يوسف.'),
(1686, 12, 90, '«قالوا» بعد أن عرفوه لما ظهر من شمائله متثبتين «أإنك» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين «لأنت يوسف قال أنا يوسف وهذا أخي قد منَّ» أنعم «الله علينا» بالاجتماع «إنه من يتق» يخف الله «ويصبر» على ما يناله «فإن الله لا يضيع أجر المحسنين» فيه وضع الظاهر موضع المضمر.'),
(1687, 12, 91, '«قالوا تالله لقد آثرك» فضلك «الله علينا» بالملك وغيره «وإن» مخففة أي إنَّا «كنا لخاطئين» آثمين في أمرك فأذللناك.'),
(1688, 12, 92, '«قال لا تثريب» عتب «عليكم اليوم» خصه بالذكر لأنه مظنة التثريب فغيره أولى «يغفر الله لكم وهو أرحم الراحمين» وسألهم عن أبيه فقالوا ذهبت عيناه فقال.'),
(1689, 12, 93, '«اذهبوا بقميصي هذا» وهو قميص إبراهيم الذي لبسه حين ألقي في النار كان في عنقه في الجب وهو من الجنة أمره جبريل بإرساله وقال إن فيه ريحها ولا يُلقى على مبتلًى إلا عوفي «فألقوه على وجه أبي يأت» يصر «بصيرا وائتوني بأهلكم أجمعين».'),
(1690, 12, 94, '«ولما فصلت العير» خرجت من عريش مصر «قال أبوهم» لمن حضر من بنيه وأولادهم «إني لأجد ريح يوسف» أوصلته إليه الصبا بإذنه تعالى من مسير ثلاثة أيام أو ثمانية أو أكثر «لولا أن تفندونِ» تسفهون لصدقتموني.'),
(1691, 12, 95, '«قالوا» له «تالله إنك لفي ضلالك» خطئك «القديم» من إفراطك في محبته ورجاء لقائه على بعد العهد.'),
(1692, 12, 96, '«فلما أن» زائدة «جاء البشير» يهوذا بالقميص وكان قد حمل قميص الدم فأحب أن يفرحه كما أحزنه «ألقاه» طرح القميص «على وجهه فارتد» رجع «بصيرا قال ألم أقل لكم إني أعلم من الله ما لا تعلمون».'),
(1693, 12, 97, '«قالوا يا أبانا استغفر لنا ذنوبنا إنا كنا خاطئين».'),
(1694, 12, 98, '«قال سوف أستغفر لكم ربي إنه الغفور الرحيم» أخَّر ذلك إلى السحر ليكون أقرب إلى الإجابة أو إلى ليلة الجمعة ثم توجهوا إلى مصر وخرج يوسف والأكابر لتلقيهم.'),
(1695, 12, 99, '«فلما دخلوا على يوسف» في مضربه «آوى» ضم «إليه أبويه» أباه وأمه أو خالته «وقال» لهم «ادخلوا مصر إن شاء الله آمنين» فدخلوا وجلس يوسف على سريره.'),
(1696, 12, 100, '«ورفع أبويه» أحلهما معه «على العرش» السرير «وخروا» أي أبواه وإخوته «له سجدا» سجود انحناء لا وضع جبهة وكان تحيتهم في ذلك الزمان «وقال يا أبت هذا تأويل رؤياي من قبل قد جعلها ربي حقا وقد أحسن بي» إليَّ «إذ أخرجني من السجن» لم يقل من الحب تكرما لئلا تخجل إخوته «وجاء بكم من البدو» البادية «من بعد أن نزغ» أفسد «الشيطان بيني وبين إخوتي إن ربي لطيف لما يشاء إنه هو العليم» بخلقه «الحكيم» في صنعه وأقام عنده أبوه أربعا وعشرين سنة أو سبع عشرة سنة وكانت مدة فراقه ثماني عشرة أو أربعين أو ثمانين سنة وحضره الموت فوصى يوسف أن يحمله ويدفنه عند أبيه فمضى بنفسه ودفنه ثمة، ثم عاد إلى مصر وأقام بعده ثلاثا وعشرين سنة ولما تم أمره وعلم أنه لا يدوم تاقت نفسه إلى الملك الدائم فقال.'),
(1697, 12, 101, '«رب قد آتيتني من الملك وعلمتني من تأويل الأحاديث» تعبير الرؤيا «فاطر» خالق «السماوات والأرض أنت وليي» متولي مصالحي «في الدنيا والآخرة توفني مسلما وألحقني بالصالحين» من آبائي فعاش بعد ذلك أسبوعا أو أكثر ومات وله مائة وعشرون سنة وتشاح المصريون في قبره فجعلوه في صندوق من مرمر ودفنوه في أعلى النيل لتعم البركة جانبيه فسبحان من لا انقضاء لملكه.'),
(1698, 12, 102, '«ذلك» المذكور من أمر يوسف «من أنباء» أخبار «الغيب» ما غاب عنك يا محمد «نوحيه إليك وما كنت لديهم» لدى إخوة يوسف «إذ أجمعوا أمرهم» في كيده أي عزموا عليه «وهم يمكرون» به أي لم تحضرهم فتعرف قصتهم فتخبر بها وإنما حصل لك علمها من جهة الوحي.'),
(1699, 12, 103, '«وما أكثر الناس» أي أهل مكة «ولو حرصت» على إيمانهم «بمؤمنين».'),
(1700, 12, 104, '«وما تسألهم عليه» أي القرآن «من أجر» تأخذه «إن» ما «هو» أي القرآن «إلا ذكر» عظة «للعالمين».'),
(1701, 12, 105, '«وكأين» وكم «من آية» دالة على وحدانية الله «في السماوات والأرض يمرون عليها» يشاهدونها «وهم عنها معرضون» لا يتفكرون بها.'),
(1702, 12, 106, '«وما يؤمن أكثرهم بالله» حيث يقرون بأنه الخالق الرزاق «إلا وهم مشركون» به بعبادة الأصنام ولذا كانوا يقولون في تلبيتهم: لبيك لا شريك لك، إلا شريكا هو لك، تملكه وما ملك يعنونها.'),
(1703, 12, 107, '«أفأمِنوا أن تأتيهم غاشية» نقمة تغشاهم «من عذاب الله أو تأتيهم الساعة بغتة» فجأة «وهم لا يشعرون» بوقت إتيانها قبله.'),
(1704, 12, 108, '«قل» لهم «هذه سبيلي» وفسرها بقوله «أدعو إلى» دين «الله على بصيرة» حجة واضحة «أنا ومن أتبعني» آمن بي عطف على أنا المبتدأ المخبر عنه بما قبله «وسبحان الله» تنزيها له عن الشركاء «وما أنا من المشركين» من جملة سبيله أيضا.'),
(1705, 12, 109, '«وما أرسلنا من قبلك إلا رجالا يوحى» وفي قراءة بالنون وكسر الحاء «إليهم» لا ملائكة «من أهل القرى» الأمصار لأنهم أعلم وأحلم بخلاف أهل البوادي لجفائهم وجهلهم «أفلم يسيروا» أهل مكة «في الأرض فينظروا كيف كان عاقبة الذين من قبلهم» أي آخر أمرهم من إهلاكهم بتكذيبهم رسلهم «ولدار الآخرة» أي الجنة «خير للذين اتقوا» الله «أفلا تعقلون» بالياء والتاء يا أهل مكة هذا فتؤمنون.'),
(1706, 12, 110, '«حتى» غاية لما دل عليه (وما أرسلنا من قبلك إلا رجالا) أي فتراخى نصرهم حتى «إذا استيئس» يئس «الرسل وظنوا» أيقن الرسل «أنهم قد كذِّبوا» بالتشديد تكذيبا لا إيمان بعده والتخفيف أي ظن الأمم أن الرسل أخلفوا ما وعدوا به من النصر «جاءهم نصرنا فَنُنَجِّي» بنونين مشددا ومخففا وبنون مشددا ماض «من نشاء ولا يرد بأسنا» عذابنا «عن القوم المجرمين» المشركين.'),
(1707, 12, 111, '«لقد كان في قصصهم» أي الرسل «عبرة لأولي الألباب» أصحاب العقول «ما كان» هذا القرآن «حديثا يفترى» يختلق «ولكن» كان «تصديق الذي بين يديه» قبله من الكتب «وتفصيل» تبيين «كل شيء» يحتاج إليه في الدين «وهدى» من الضلالة «ورحمة لقوم يؤمنون» خصوا بالذكر لانتفاعهم به دون غيرهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, '«المر» الله أعلم بمراده بذلك «تلك» هذه الآيات «آيات الكتاب» القرآن والإضافة بمعنى من «والذي أنزل إليك من ربك» أي القرآن مبتدأ خبره «الحق» لا شك فيه «ولكن أكثر الناس» أي أهل مكة «لا يؤمنون» بأنه من عنده تعالى.'),
(1709, 13, 2, '«الله الذي رفع السماوات بغير عمد ترونها» أي العمد جمع عماد وهو الأسطوانة وهو صادق بأن لا عمد أصلا «ثم استوى على العرش» استواء يليق به «وسخر» ذلل «الشمس والقمر كلٌ» منهما «يجري» في فلكه «لأجل مسمى» يوم القيامة «يدبر الأمر» يقضي أمر ملكه «يفصِّل» يبين «الآيات» دلالات قدرته «لعلكم» يا أهل مكة «بلقاء ربكم» بالبعث «توقنون».'),
(1710, 13, 3, '«وهو الذي مد» بسط «الأرض وجعل» خلق «فيها رواسي» جبالا ثوابت «وأنهارا ومن كل الثمرات جعل فيها زوجين اثنين» من كل نوع «يغشي» يغطي «الليل» بظلمته «النهار إن في ذلك» المذكور «لآيات» دلالات على وحدانيته تعالى «لقوم يتفكرون» في صنع الله.'),
(1711, 13, 4, '«وفي الأرض قطع» بقاع مختلفة «متجاورات» متلاصقات فمنها طيب وسبخ وقليل الريع وكثيرهُ وهو من دلائل قدرته تعالى «وجنات» بساتين «من أعناب وزرع» بالرفع عطفا على جنات، والجر على أعناب وكذا قوله «ونخيل صنوان» جمع صنو، وهي النخلات يجمعها أصل واحد وتشعب فروعها «وغير صنوان» منفردة «تسقى» بالتاء، أي الجنات وما فيها والياء، أي المذكور «بماء واحد ونفضّل» بالنون والياء «بعضها على بعض في الأكُل» بضم الكاف وسكونها فمن حلو وحامض وهو من دلائل قدرته تعالى «إن في ذلك» المذكور «لآيات لقوم يعقلون» يتدبرون.'),
(1712, 13, 5, '«وإن تعجب» يا محمد من تكذيب الكفار لك «فعجب» حقيق بالعجب «قولهم» منكرين للبعث «أإذا كنا ترابا أإنا لفي خلق جديد» لأن القادر على إنشاء الخلق وما تقدم على غير مثال قادر على إعادتهم، وفي الهمزتين في الموضعين، وتحقيق الأولى وتسهيل الثانية وإدخال ألف بينهما على الوجهين وتركها وفي قراءة بالاستفهام في الأول، والخبر في الثاني، وأخرى وعكسه «أولئك الذين كفروا بربهم وأولئك الأغلال في أعناقهم وأولئك أصحاب النار هم فيها خالدون».'),
(1713, 13, 6, 'ونزل في استعجالهم العذاب استهزاءً «ويستعجلونك بالسيئة» العذاب «قبل الحسنة» الرحمة «وقد خلت من قبلهم المثلات» جمع المثلة بوزن الثمرة أي عقوبات أمثالهم من المكذبين أفلا يعتبرون بها؟ «وإن ربك لذو مغفرة للناس على» مع «ظلمهم» وإلا لم يترك على ظهرها دابة «وإن ربك لشديد العقاب» لمن عصاه.'),
(1714, 13, 7, '«ويقول الذين كفروا لولا» هلا «أنزل عليه» على محمد «آية من ربه» كالعصا واليد والناقة، قال تعالى: «إنما أنت منذر» مخوِّف الكافرين وليس عليك إتيان الآيات «ولكل قوم هاد» نبي يدعوهم إلى ربهم بما يعطيه من الآيات لا بما يقترحون.'),
(1715, 13, 8, '«الله يعلم ما تحمل كل أنثى» من ذكر وأنثى وواحد ومتعدد وغير ذلك «وما تغيض» تنقص «الأرحام» من مدة الحمل «وما تزداد» منه «وكل شيء عنده بمقدار» بقدر وحدٍّ لا يتجاوزه.'),
(1716, 13, 9, '«عالم الغيب والشهادة» ما غاب وما شوهد «الكبير» العظيم «المتعالي» على خلقه بالقهر، بياء ودونها.'),
(1717, 13, 10, '«سواء منكم» في علمه تعالى «من أسر القول ومن جهر به ومن هو مُستخف» مستتر «بالليل» بظلامه «وسارب» ظاهر بذهابه في سربه، أي طريقه «بالنهار».'),
(1718, 13, 11, '«له» للإنسان «معقبات» ملائكة تتعقبه «من بين يديه» قدامه «ومن خلفه» ورائه «يحفظونه من أمر الله» أي بأمره من الجن وغيرهم «إن الله لا يغيِّر ما بقوم» لا يسلبهم نعمته «حتى يغيِّروا ما بأنفسهم» من الحالة الجميلة بالمعصية «وإذا أراد الله بقوم سوءا» عذابا «فلا مرد له» من المعقبات ولا غيرها «وما لهم» لمن أراد الله بهم سوءا «من دونه» أي غير الله «من» زائدة «وال» يمنعه عنهم.'),
(1719, 13, 12, '«هو الذي يريكم البرق خوفا» للمسافرين من الصواعق «وطمعا» للمقيم في المطر «وينشئ» يخلق «السحاب الثقال» بالمطر.'),
(1720, 13, 13, '«ويسبح الرعد» هو ملك موكل بالسحاب يسوقه متلبسا «بحمده» أي يقول سبحان الله وبحمده «و» يسبح «الملائكة من خيفته» أي الله «ويرسل الصواعق» وهي نار تخرج من السحاب «فيصيب بها من يشاء» فتحرقه نزل في رجل بعث إليه النبي صلى الله عليه وسلم من يدعوه فقال من رسول الله وما الله أمن ذهب هو أم من فضة أم نحاس فنزلت به صاعقة فذهبت بقحف رأسه «وهم» أي الكفار «يجادلون» يخاصمون النبي صلى الله عليه وسلم «في الله وهو شديد المحال» القوة أو الأخذ.'),
(1721, 13, 14, '«له» تعالى «دعوة الحق» أي كلمته وهي لا إله إلا الله «والذين يدعون» بالياء والتاء يعبدون «من دونه» أي غيره وهم الأصنام «لا يستجيبون لهم بشيء» مما يطلبونه «إلا» استجابة «كباسط» أي كاستجابة باسط «كفيه إلى الماء» على شفير البئر يدعوه «ليبلغ فاه» بارتفاعه من البئر إليه «وما هو ببالغه» أي فاه أبدا فكذلك ما هم بمستجيبين لهم «وما دعاء الكافرين» عبادتهم الأصنام أو حقيقة الدعاء «إلا في ضلال» ضياع.'),
(1722, 13, 15, '«ولله يسجد من في السماوات والأرض طوعا» كالمؤمنين «وكرها» كالمنافقين ومن أكره بالسيف «و» يسجد «ظلالهم بالغدو» البكور «والآصال» العشايا.'),
(1723, 13, 16, '«قل» يا محمد لقومك «مَن رب السماوات والأرض قل الله» إن لم يقولوه لا جواب غيره «قل» لهم «أفاتخذتم من دونه» أي غيره «أولياء» أصناما تعبدونها «لا يملكون لأنفسهم نفعا ولا ضرا» وتركتم مالكهما؟ استفهام توبيخ «قل هل يستوي الأعمى والبصير» الكافر والمؤمن «أم هل تستوي الظلمات» الكفر «والنور» الإيمان؟ لا «أم جعلوا لله شركاء خلقوا كخلقه فتشابه الخلق» أي الشركاء بخلق الله «عليهم» فاعتقدوا استحقاق عبادتهم بخلقهم؟ استفهام إنكار؟ أي ليس الأمر كذلك ولا يستحق العبادة إلا الخالق «قل الله خالق كل شيء» لا شريك له فيه فلا شريك له في العبادة «وهو الواحد القهار» لعباده.'),
(1724, 13, 17, 'ثم ضرب مثلا للحق والباطل فقال «أنزل» تعالى «من السماء ماء» مطرا «فسالت أودية بقدرها» بمقدار ملئها «فاحتمل السيل زبدا رابيا» عاليا عليه هو ما على وجهه من قذر ونحوه «ومما توقدون» بالتاء والياء «عليه في النار» من جواهر الأرض كالذهب والفضة والنحاس «ابتغاه» طلب «حلية» زينة «أو متاع» ينتفع به كالأواني إذا أذيبت «زبد مثله» أي مثل زبد السيل وهو خبثه الذي ينفيه الكير «كذلك» المذكور «يضرب الله الحق والباطل» أي مثلهما «فأما الزبد» من السيل وما أوقد عليه من الجواهر «فيذهب جفاءً» باطلا مرميا به «وأما ما ينفع الناس» من الماء والجواهر «فيمكث» يبقى «في الأرض» زمانا كذلك الباطل يضمحل وينمحق وإن علا على الحق في بعض الأوقات والحق ثابت باقي «كذلك» المذكور «يضرب» يبيِّن «الله الأمثال».'),
(1725, 13, 18, '«للذين استجابوا لربهم» أجابوه بالطاعة «الحسنى» الجنة «والذين لم يستجيبوا له» وهم الكافر «لو أن لهم ما في الأرض جميعا ومثله معه لافتدوْا به» من العذاب «أولئك لهم سوء الحساب» وهو المؤاخذة بكل ما علموه لا يغفر منه شيء «ومأواهم جهنم وبئس المهاد» الفراش هي.'),
(1726, 13, 19, 'ونزل في حمزة وأبي جهل «أفمن يعلم أنما أنزل إليك من ربك الحق» فآمن به «كمن هو أعمى» لا يعلمه ولا يؤمن به لا «إنما يتذكر» يتعظ «أولوا الألباب» أصحاب العقول.'),
(1727, 13, 20, '«الذين يوفون بعهد الله» المأخوذ عليهم وهم في عالم الذر أو كل عهد «ولا ينقضون الميثاق» بترك الأيمان أو الفرائض.'),
(1728, 13, 21, '«والذين يصلون ما أمر الله به أن يوصل» من الإيمان والرحم وغير ذلك «ويخشون ربهم» أي وعيده «ويخافون سوء الحساب» تقدم مثله.'),
(1729, 13, 22, '«والذين صبروا» على الطاعة والبلاء وعن المعصية «ابتغاء» طلب «وجه ربهم» لا غيره من أعراض الدنيا «وأقاموا الصلاة وأنفقوا» في الطاعة «مما رزقناهم سرا وعلانية ويدرءُون» يدفعون «بالحسنة السيئة» كالجهل بالحلم والأذى بالصبر «أولئك لهم عُقبى الدار» أي العاقبة المحمودة في الدار الآخرة، وهي.'),
(1730, 13, 23, '«جنات عدن» إقامة «يدخلونها» هم «ومن صلح» آمن «من آبائهم وأزواجهم وذرياتهم» وإن لم يعلموا بعملهم يكونون في درجاتهم تكرمة لهم «والملائكة يدخلون عليهم من كل باب» من أبواب الجنة أو القصور أول دخولهم للتهنئة.'),
(1731, 13, 24, 'يقولون «سلام عليكم» هذا الثواب «بما صبرتم» بصبركم في الدنيا «فنعم عُقْبَى الدار» عقباكم.'),
(1732, 13, 25, '«والذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما أمر الله به أن يوصل ويفسدون في الأرض» بالكفر والمعاصي «أولئك لهم اللعنة» البعد من رحمة الله «ولهم سوء الدار» العاقبة السيئة في الدار الآخرة وهي جهنم.'),
(1733, 13, 26, '«الله يبسط الرزق» يوسعه «لمن يشاء ويقدر» يضيقه لمن يشاء «وفرحوا» أي أهل مكة فرح بطر «بالحياة الدنيا» أي بما نالوه فيها «وما الحياة الدنيا في» جنب حياة «الآخرة إلا متاع» شيء قليل يتمتع به ويذهب.'),
(1734, 13, 27, '«ويقول الذين كفروا» من أهل مكة «لولا» هلا «أنزل عليه» على محمد «آية من ربه» كالعصا واليد والناقة «قل» لهم «إن الله يضل من يشاء» إضلاله فلا تغني عنه الآيات شيئا «ويهدي» يرشد «إليه» إلى دينه «من أناب» رجع إليه، ويبدل مِن مَن.'),
(1735, 13, 28, '«الذين آمنوا وتطمئن» تسكن «قلوبهم بذكر الله» أي وعده «ألا بذكر الله تطمئن القلوب» أي قلوب المؤمنين.'),
(1736, 13, 29, '«الذين آمنوا وعملوا الصالحات» مبتدأ خبره «طوبى» مصدر من الطيب أو شجرة في الجنة يسير الراكب في ظلها مائة عام ما يقطعها «لهم وحسن مآب» مرجع.'),
(1737, 13, 30, '«كذلك» كما أرسلنا الأنبياء قبلك «أرسلناك في أمة قد خلت من قبلها أمم لتَتْلوَ» تقرأ «عليهم الذي أوحينا إليك» أي القرآن «وهم يكفرون بالرحمن» حيث قالوا لما أمروا بالسجود له وما الرحمن؟ «قل» لهم يا محمد «هو ربي لا إله إلا هو عليه توكلت وإليه متاب».'),
(1738, 13, 31, 'ونزل لما قالوا له إن كنت نبيا فسيّر عنا جبال مكة، واجعل لنا فيها أنهارا وعيونا لنغرس ونزرع وابعث لنا آباءنا الموتى يكلمونا أنك نبي «ولو أن قرآنا سُيّرت به الجبال» نقلت عن أماكنها «أو قطّعت» شققت «به الأرض أو كلم به الموتى» بأن يحيوا لما آمنوا «بل لله الأمر جميعا» لا لغيره فلا يؤمن إلا من شاء إيمانه دون غيره إن أوتوا ما اقترحوا، ونزل لما أراد الصحابة إظهار ما اقترحوا طمعا في إيمانهم «أفلم ييأس» يعلم «الذين آمنوا أن» مخففة أي أنه «لو يشاء الله لهدي الناس جميعا» إلى الإيمان من غير آية «ولا يزال الذين كفروا» من أهل مكة «تصيبهم بما صنعوا» بصنعهم أي كفرهم «قارعهٌ» داهية تقرعهم بصنوف البلاء من القتل والأسر والحرب والجدب «أو تحل» يا محمد بجيشك «قريبا من دارهم» مكة «حتى يأتي وعد الله» بالنصر عليهم «إن الله لا يخلف الميعاد» وقد حلَّ بالحديبية حتى أتى فتح مكة.'),
(1739, 13, 32, '«ولقد استهزئ برسل من قبلك» كما استهزئ بك وهذا تسلية للنبي صلى الله عليه وسلم «فأمْليتُ» أمهلت «للذين كفروا ثم أخذتهم» بالعقوبة «فكيف كان عقاب» أي هو واقع موقعه فكذلك أفعل بمن استهزأ بك.'),
(1740, 13, 33, '«أفمن هو قائم» رقيب «على كل نفس بما كسبت» عملت من خير وشر وهو الله كمن ليس كذلك من الأصنام لا، دل على هذا «وجعلوا الله شركاء قل سمّوهم» له من هم؟ «أم» بل أ «تنبئونه» تخبرون الله «بما» أي بشريك «لا يعلمـ» ـه «في الأرض» استفهام إنكار أي لا شريك له إذ لو كان لعلمه تعالى عن ذلك «أم» بل تسمونهم شركاء «بظاهر من القول» بظن باطل لا حقيقة له في الباطن «بل زُيّن للذين كفروا مكرهم» كفرهم «وصدوا عن السبيل» طريق الهدى «ومن يضلل الله فما له من هاد».'),
(1741, 13, 34, '«لهم عذاب في الحياة الدنيا» بالقتل والأسر «ولعذاب الآخرة أشق» أشد منه «ومالهم من الله» أي عذابه «من واق» مانع.'),
(1742, 13, 35, '«مثل» صفة «الجنة التي وعد المتقون» مبتدأ خبره محذوف، أي فيما نقص عليكم «تجري من تحتها الأنهار أُكُلُها» ما يؤكل فيها «دائم» لا يفنى «وظلها» دائم لا تنسخه شمس لعدمها فيها «تلك» أي الجنة «عقبى» عاقبة «الذين اتقوْا» الشرك «وعقبى الكافرين النارُ».'),
(1743, 13, 36, '«والذين آتيناهم الكتاب» كعبد الله بن سلام وغيره من مؤمني اليهود «يفرحون بما أنزل إليك» لموافقته ما عندهم «ومن الأحزاب» الذين تحزَّبوا عليك بالمعادة من المشركين واليهود «من ينكر بعضه» كذكر الرحمن وما عدا القصص «قل إنما أمرت» فيما أنزل إليَّ «أن» أي بأن «أعبد الله ولا أشرك به إليه أدعو وإليه مآب» مرجعي.'),
(1744, 13, 37, '«وكذلك» الإنزال «أنزلناه» أي القرآن «حكما عربيا» بلغة العرب تحكم به بين الناس «ولئن اتبعت أهواءهم» أي الكفار فيما يدعونك إليه من ملتهم فرضا «بعد ما جاءك من العلم» بالتوحيد «ما لك من الله من» زائدة «وليٍّ» ناصر «ولا واق» مانع من عذابه.'),
(1745, 13, 38, 'ونزل لما عيروه بكثرة النساء: «ولقد أرسلنا رسلا من قبلك وجعلنا لهم أزواجا وذرية» أولادا وأنت مثلهم «وما كان لرسول» منهم «أن يأتي بآية إلا بإذن الله» لأنهم عبيد مربوبون «لكل أجل» مدة «كتاب» مكتوب فيه تحديده.'),
(1746, 13, 39, '«يمحو الله» منه «ما يشاء ويثبت» بالتخفيف والتشديد فيه ما يشاء من الأحكام وغيرها «وعنده أم الكتاب» أصله الذي لا يتغير منه شيء وهو ما كتبه في الأزل.'),
(1747, 13, 40, '«وإما» فيه إدغام نون إن الشرطية في ما المزيدة «نرينك بعض الذي نعدهم» به من العذاب من حياتك وجواب الشرط محذوف أي فذاك «أو نتوفينك» قبل تعذيبهم «فإنما عليك البلاغ» ما عليك إلا التبليغ «وعلينا الحساب» إذا صاروا إلينا فنجازيهم.'),
(1748, 13, 41, '«أو لم يروْا» أي أهل مكة «أنا نأتي الأرض» نقصد أرضهم «ننقصها من أطرافها» بالفتح على النبي صلى الله عليه وسلم «والله يحكم» في خلقه بما يشاء «لا مُعَقّب» لا راد «لحكمه وهو سريع الحساب».'),
(1749, 13, 42, '«وقد مكر الذين من قبلهم» من الأمم بأنبيائهم كما مكروا بك «فلله المكر جميعا» وليس مكرهم كمكره لأنه تعالى «يعلم ما تكسب كل نفس» فيعد لها جزاءه وهذا هو المكر كله لأنه يأتيهم به من حيث لا يشعرون «وسيعلم الكافر» المراد به الجنس وفي قراءة الكفار «لمن عقبى الدار» أي العاقبة المحمودة في الدار الآخرة ألهم أم للنبي صلى الله عليه وسلم وأصحابه.'),
(1750, 13, 43, '«ويقول الذين كفروا» لك «لست مرسلا قل» لهم «كفى بالله شهيدا بيني وبينكم» على صدقي «ومن عنده علم الكتاب» من مؤمني اليهود والنصارى.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, '«الر» الله أعلم بمراده بذلك، هذا القرآن «كتاب أنزلناه إليك» يا محمد «لتخرج الناس من الظلمات» الكفر «إلى النور» الإيمان «بإذن» بأمر «ربهم» ويبدل من: إلى النور «إلى صراط» طريق «العزيز» الغالب «الحميد» المحمود.'),
(1752, 14, 2, '«الله» بالجر بدل أو عطف بيان وما بعده صفة والرفع مبتدأ خبره «الذي له ما في السماوات وما في الأرض» ملكا وخلقا وعبيدا «وويل للكافرين من عذاب شديد».'),
(1753, 14, 3, '«الذين» نعت «يستحبون» يختارون «الحياة الدنيا على الآخرة ويصدون» الناس «عن سبيل الله» دين الإسلام «ويبغونها» أي السبيل «عوجا» معوجة «أولئك في ضلال بعيد» عن الحق.'),
(1754, 14, 4, '«وما أرسلنا من رسول إلا بلسان» بلغة «قومه ليبين لهم» ليفهمهم ما أتى به «فيضلُّ الله من يشاء ويهدي من يشاء وهو العزيز» في ملكه «الحكيم» في صنعه.'),
(1755, 14, 5, '«ولقد أرسلنا موسى بآياتنا» التسع وقلنا له «أن أخرج قومك» بني إسرائيل «من الظلمات» الكفر «إلى النور» الإيمان «وذكّرهم بأيام الله» بنعمه «إن في ذلك» التذكير «لآيات لكل صبار» على الطاعة «شكور» للنعم.'),
(1756, 14, 6, '«و» أذكر «إذ قال موسى لقومه اذكروا نعمة الله عليكم إذ أنجاكم من آل فرعون يسومونكم سوء العذاب ويذبِّحون أبناءكم» المولودين «ويستحيون» يستبقون «نساءكم» لقول بعض الكهنة إن مولودا يولد في بني إسرائيل يكون سبب ذهاب ملك فرعون «وفي ذلكم» الإنجاء أو العذاب «بلاء» إنعام أو ابتلاء «من ربكم عظيم».'),
(1757, 14, 7, '«وإذ تأذَّن» أعلم «ربكم لئن شكرتم» نعمتي بالتوحيد والطاعة «لأزيدنكم ولئن كفرتم» جحدتم النعمة بالكفر والمعصية لأعذبنكم دل عليه «إن عذابي لشديد».'),
(1758, 14, 8, '«وقال موسى» لقومه «إن تكفروا أنتم ومن في الأرض جميعا فإن الله لغني» عن خلقه «حميد» محمود في صنعه بهم.'),
(1759, 14, 9, '«ألم يأتكم» استفهام تقرير «نبأ» خبر «الذين من قبلكم قوم نوح وعاد» قوم هود «وثمود» قوم صالح «والذين من بعدهم لا يعلمهم إلا الله» لكثرتهم «جاءتهم رسلهم بالبينات» بالحجج الواضحة على صدقهم «فردوا» أي الأمم «أيديهم في أفواههم» أي إليها ليعضوا عليها من شدة الغيظ «وقالوا إنا كفرنا بما أرسلتم به» في زعمكم «وإنا لفي شك مما تدعوننا إليه مريب» موقع في الريبة.'),
(1760, 14, 10, '«قالت رسلهم أفي الله شك» استفهام إنكار لا شك في توحيده لدلائل الظاهرة عليه «فاطر» خالق «السماوات والأرض يدعوكم» إلى طاعته «ليغفر لكم من ذنوبكم» من زائدة. فإن الإسلام يغفر به ما قبله، أو تبعيضية لإخراج حقوق العباد «ويؤخركم» بلا عذاب «إلى أجل مسمى» أجل الموت «قالوا إن» ما «أنتم إلا بشر مثلنا تريدون أن تصدونا عما كان يعبد آباؤنا» من الأصنام «فأتونا بسلطان مبين» حجة ظاهرة على صدقكم.'),
(1761, 14, 11, '«قالت لهم رسلهم إن» ما «نحن إلا بشر مثلكم» كما قلتم «ولكن الله يمنُّ على من يشاء من عباده» بالنبوة «وما كان» ما ينبغي «لنا أن نأتيكم بسلطان إلا بإذن الله» بأمره لأننا عبيد مربوبون «وعلى الله فليتوكل المؤمنون» يثقوا به.'),
(1762, 14, 12, '«وما لنا أ» ن «لا نتوكل على الله» أي لا مانع لنا من ذلك «وقد هدانا سبلنا ولنصبرن على ما آذيتمونا» على أذاكم «وعلى الله فليتوكل المتوكلون».'),
(1763, 14, 13, '«وقال الذين كفروا لرسلهم لنخرجنكم من أرضنا أو لتعودنَّ» لتصيرن «في ملتنا» ديننا «فأوحى إليهم ربهم لنهلكن الظالمين» الكافرين.'),
(1764, 14, 14, '«ولنسكننكم الأرض» أرضهم «من بعدهم» بعد هلاكهم «ذلك» النصر وإيراث الأرض «لمن خاف مقامي» أي مقامه بين يدي «وخاف وعيد» بالعذاب.'),
(1765, 14, 15, '«واستفتحوا» استنصر الرسل بالله على قومهم «وخاب» خسر «كل جبار» متكبر عن طاعة الله «عنيد» معاند للحق.'),
(1766, 14, 16, '«من ورائه» أي أمامه «جهنم» يدخلها «ويسقى» فيها «من ماء صديد» هو ما يسل من جوف أهل النار مختلطا بالقيح والدم.'),
(1767, 14, 17, '«يتجرعه» يبتلعه مرة بعد مرة لمرارته «ولا يكاد يسيغه» يزدرده لقبحه وكراهته «ويأتيه الموت» أي أسبابه المقتضية له من أنواع العذاب «من كل مكان وما هو بميت ومن ورائه» بعد ذلك العذاب «عذاب غليظ» قوي متصل.'),
(1768, 14, 18, '«مثل» صفة «الذين كفروا بربهم» مبتدأ ويبدل منه «أعمالهم» الصالحة كصلة وصدقة في عدم الانتفاع بها «كرماد اشتدت به الريح في يوم عاصف» شديد هبوب الريح فجعلته هباءً منثورا لا يقدر عليه والمجرور خبر المبتدأ «لا يقدرون» أي الكفار «مما كسبوا» عملوا في الدنيا «على شيء» أي لا يجدون له ثوابا لعدم شرطه «ذلك هو الضلال» الهلاك «البعيد».'),
(1769, 14, 19, '«ألم تر» تنظر يا مخاطب استفهام تقرير «أن الله خلق السماوات والأرض بالحق» متعلق بخلق «إن يشأ يذهبكم» أيها الناس «ويأت بخلق جديد» بدلكم.'),
(1770, 14, 20, '«وما ذلك على الله بعزيز» شديد.'),
(1771, 14, 21, '«وبرزوا» أي الخلائق والتعبير فيه وفيما بعده بالماضي لتحقيق وقوعه «لله جميعا فقال الضعفاء» الأتباع «للذين استكبروا» المتبوعين «إنا كنا لكم تبعا» جمع تابع «فهل أنتم مغنون» دافعون «عنا من عذاب الله من شيء» من الأولى للتبيين والثانية للتبعيض «قالوا» المتبوعون «لو هدانا الله لهديناكم» لدعوناكم إلى الهدى «سواء علينا أجزعنا أم صبرنا ما لنا من» زائدة «محيص» ملجأ.'),
(1772, 14, 22, '«وقال الشيطان» إبليس «لما قضي الأمر» وأدخل أهل الجنة الجنة وأهل النار النار واجتمعوا عليه «إن الله وعدكم وعد الحق» بالبعث والجزاء فصدقكم «ووعدتكم» أنه غير كائن «فأخلفتكم وما كان لي عليكم من» زائدة «سلطان» قوة وقدرة أقهركم على متابعتي «إلا» لكن «أن دعوتكم فاستجبتم لي فلا تلوموني ولموا أنفسكم» على إجابتي «ما أنا بمصرخكم» بمغيثكم «وما أنتم بمصرخيَِّ» بفتح الياء وكسرها «إني كفرت بما أشركتمون» بإشراككم إياي مع الله «من قبل» من الدنيا قال تعالى «إن الظالمين» الكافرين «لهم عذاب أليم» مؤلم.'),
(1773, 14, 23, '«وأدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار خالدين» حال مقدرة «فيها بإذن ربهم تحيتهم فيها» من الله ومن الملائكة وفيما بينهم «سلام».'),
(1774, 14, 24, '«ألم تر» تنظر «كيف ضرب الله مثلا» ويبدل منه «كلمة طيبة» أي لا إله إلا الله «كشجرة طيبة» هي النخلة «أصلها ثابت» في الأرض «وفرعها» غصنها «في السماء».'),
(1775, 14, 25, '«تؤتي» تعطي «أكلها» ثمرها «كل حين بإذن ربها» بإرادته كذلك كلمة الإيمان ثابتة في قلب المؤمن وعمله يصعد إلى السماء ويناله بركته وثوابه كل وقت «ويضرب» يبين «الله الأمثال للناس لعلهم يتذكرون» يتعظون فيؤمنون.'),
(1776, 14, 26, '«ومثل كلمة خبيثة» هي كلمة الكفر «كشجرة خبيثة» هي الحنظل «اجتثت» استؤصلت «من فوق الأرض ما لها من قرار» مستقر وثبات كذلك كلمة الكفر لا ثبات لها ولا فرع ولا بركة.'),
(1777, 14, 27, '«يثبت الله الذين آمنوا بالقول الثابت» هي كلمة التوحيد «في الحياة الدنيا وفي الآخرة» أي في القبر لما يسألهم الملكان عن ربهم ودينهم ونبيهم فيجيبون بالصواب كما في حديث الشيخين «ويضل الله الظالمين» الكفار فلا يهتدون للجواب بالصواب بل يقولون لا ندري كما في الحديث «ويفعل الله ما يشاء».'),
(1778, 14, 28, '«ألم تر» تنظر «إلى الذين بدلوا نعمة الله» أي شكرها «كفرا» هم كفار قريش «وأحلوا» أنزلوا «قومهم» بإضلالهم إياهم «دار البوار» الهلاك.'),
(1779, 14, 29, '«جهنم» عطف بيان «يصلوْنها» يدخلونها «وبئس القرار» المقر هي.'),
(1780, 14, 30, '«وجعلوا لله أندادًا» شركاء «ليَُضِلُّوا» بفتح الياء وضمها «عن سبيله» دين الإسلام «قل» لهم «تمتعوا» بدنياكم قليلا «فإن مصيركم» مرجعكم «إلى النار».'),
(1781, 14, 31, '«قل لعبادي الذين آمنوا يقيموا الصلاة وينفقوا مما رزقناهم سرا وعلانية من قبل أن يأتي يوم لا بيع» فداء «فيه ولا خلال» مُخالة أي صداقة تنفع، هو يوم القيامة.'),
(1782, 14, 32, '«الله الذي خلق السماوات والأرض وأنزل من السماء ماءً فأخرج به من الثمرات رزقا لكم وسخر لكم الفلك» السفن «لتجري في البحر» بالركوب والحمل «بأمره» بإذنه «وسخر لكم الأنهار».'),
(1783, 14, 33, '«وسخر لكم الشمس والقمر دائبين» جاريين في فلكهما لا يفتران «وسخر لكم الليل» لتسكنوا فيه «والنهار» لتبتغوا فيه من فضله.'),
(1784, 14, 34, '«وآتاكم من كل ما سألتموه» على حسب مصالحكم «وإن تعدوا نعمة الله» بمعنى إنعامه «لا تحصوها» لا تطيقوا عدها «إن الإنسان» الكافر «لظلوم كفار» كثير الظلم لنفسه بالمعصية والكفر لنعمة ربه.'),
(1785, 14, 35, '«و» اذكر «إذ قال إبراهيم رب اجعل هذا البلد» مكة «آمنا» ذا أمن وقد أجاب الله دعاءه فجعله حرما لا يسفك فيه دم إنسان ولا يظلم فيه أحد ولا يُصاد صيده ولا يتخلى خلاه «واجنبني» بعدني «وبنيَّ» عن «أن نعبد الأصنام».'),
(1786, 14, 36, '«رب إنهن» أي الأصنام «أضللن كثيرا من الناس» بعبادتهم لها «فمن تبعني» على التوحيد «فإنه مني» من أهل ديني «ومن عصاني فإنك غفور رحيم» هذا قبل علمه أنه تعالى لا يغفر الشرك.'),
(1787, 14, 37, '«ربنا إني أسكنت من ذريتي» أي بعضها وهو إسماعيل مع أمه هاجر «بواد غير ذي زرع» هو مكة «عند بيتك المحرم» الذي كان قبل الطوفان «ربنا ليقيموا الصلاة فاجعل أفئدة» قلوبا «من الناس تهوي» تميل وتحنُّ «إليهم» قال ابن عباس لو قال أفئدة الناس لحنت إليه فارس والروم والناس كلهم «وارزقهم من الثمرات لعلهم يشكرون» وقد فعل بنقل الطائف إليه.'),
(1788, 14, 38, '«ربنا إنك تعلم ما نخفي» نسر «وما نعلن وما يخفى على الله من» زائدة «شيء في الأرض ولا في السماء» يحتمل أن يكون من كلامه تعالى أو كلام إبراهيم.'),
(1789, 14, 39, '«الحمد لله الذي وهب لي» أعطاني «على» مع «الكبر إسماعيل» ولد وله تسع وتسعون سنة «وإسحاق» ولد وله مائة واثنتا عشرة سنة «إن ربي لسميع الدعاء».'),
(1790, 14, 40, '«رب اجعلني مقيم الصلاة و» اجعل «من ذريتي» ومن يقيمها وأتى بمن لإعلام الله تعالى له أن منهم كفارا «ربنا وتقبل دعاء» المذكور.'),
(1791, 14, 41, '«ربنا اغفر لي ولوالدي» هذا قبل أن يتبين له عداوتهما لله عز وجل وقيل أسلمت أمه وقرئ والدي مفردا وولدي «وللمؤمنين يوم يقوم» يثبت «الحساب» قال تعالى.'),
(1792, 14, 42, '«ولا تحسبن الله غافلا عما يعمل الظالمون» الكافرين من أهل مكة «إنما يؤخرهم» بلا عذاب «ليوم تشخص فيه الأبصار» لهول ما ترى يقال شخص بصر فلان أي فتحه فلم يغمضه.'),
(1793, 14, 43, '«مهطعين» مسرعين حال «مقنعي» رافعي «رءُوسهم» إلى السماء «لا يرتد إليهم طرفهم» بصرهم «وأفئدتهم» قلوبهم «هواء» خالية من العقل لفزعهم.'),
(1794, 14, 44, '«وأنذر» خوِّف يا محمد «الناس» الكفار «يوم يأتيهم العذاب» هو يوم القيامة «فيقول الذين ظلموا» كفروا «ربنا أخرنا» بأن تردنا إلى الدنيا «إلى أجل قريب نجب دعوتك» بالتوحيد «ونتبع الرسل» فيقال لهم توبيخا «أو لم تكونوا أقسمتم» حلفتم «من قبل» في الدنيا «ما لكم من» زائدة «زوال» عنها إلى الآخرة.'),
(1795, 14, 45, '«وسكنتم» فيها «في مساكن الذين ظلموا أنفسهم» بالكفر من الأمم السابقة «وتبين لكم كيف فعلنا بهم» من العقوبة فلم تنزجروا «وضربنا» بينا «لكم الأمثال» في القرآن فلم تعتبروا.'),
(1796, 14, 46, '(وقد مكروا) بالنبي صلى الله عليه وسلم (مكرهم) حيث أرادوا قتله أو تقييده أو إخراجه (وعند الله مكرهم) أي علمه أو جزاؤه (وإن) ما (كان مكرهم) وإن عظم (لتزول منه الجبال) المعنى لا يعبأ به ولا يضر إلا أنفسهم والمراد بالجبال هنا قيل حقيقتها وقيل شرائع الإسلام المشبهة بها في القرار والثبات وفي قراءة بفتح لام لتزول ورفع الفعل فإن مخففة والمراد تعظيم مكرهم وقيل المراد بالمكر كفرهم ويناسبه على الثانية "" تكاد السماوات يتفطرن منه وتنشق الأرض وتخر الجبال هدا "" وعلى الأول ما قرئ وما كان.'),
(1797, 14, 47, '«فلا تحسبنَّ الله مخلف وعده رسله» بالنصر «إن الله عزيز» غالب لا يعجزه شيء «ذو انتقام» ممن عصاه.'),
(1798, 14, 48, 'اذكر (يوم تبدل الأرض غير الأرض والسماوات) هو يوم القيامة فيحشر الناس على أرض بيضاء نقية كما في حديث الصحيحين وروى مسلم حديث: سئل النبي صلى الله عليه وسلم أين الناس يومئذ قال: "" على الصراط "" (وبرزوا) خرجوا من القبور (لله الواحد القهار).'),
(1799, 14, 49, '«وترى» يا محمد تبصر «المجرمين» الكافرين «يومئذ مقرنين» مشدودين مع شياطينهم «في الأصفاد» القيود أو الأغلال.'),
(1800, 14, 50, '«سرابيلهم» قمصهم «من قطران» لأنه أبلغ لاشتعال النار «وتغشى» تعلو «وجوههم النار».'),
(1801, 14, 51, '«ليجزي» متعلق ببرزوا «الله كل نفس ما كسبت» من خير وشر «إن الله سريع الحساب» يحاسب جميع الخلق في قدر نصف نهار من أيام الدنيا لحديث بذلك.'),
(1802, 14, 52, '«هذا» القرآن «بلاغ للناس» أي أنزل لتبليغهم «ولينذروا به وليعلموا» بما فيه من الحجج «أنما هو» أي الله «إله واحد وليذكّر» بإدغام التاء في الأصل في الذال يتعظ «أولوا الألباب» أصحاب العقول.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, '«الر» الله أعلم بمراده بذلك «تلك» هذه الآيات «آيات الكتاب» القرآن والإضافة بمعنى من «وقرآن مبين» مظهر للحق من الباطل عطف بزيادة صفة.'),
(1804, 15, 2, '«رُبَّمَا» بالتشديد والتخفيف «يود» يتمنى «الذين كفروا» يوم القيامة إذا عاينوا حالهم وحال المسلمين «لو كانوا مسلمين» ورب للتكثير فإنه يكثر منهم تمني ذلك وقيل للتقليل فإن الأهوال تدهشهم فلا يفيقون حتى يتمنوا ذلك إلا في أحيان قليلة.'),
(1805, 15, 3, '«ذرهم» اترك الكفار يا محمد «يأكلوا ويتمتعوا» بدنياهم «ويلههم» يشغلهم «الأمل» بطول العمر وغيره عن الإيمان «فسوف يعلمون» عاقبة أمرهم وهذا قبل الأمر بالقتال.'),
(1806, 15, 4, '«وما أهلكنا من» زائدة «قرية» أريد أهلها «إلا ولها كتاب» أجل «معلوم» محدود لإهلاكها.'),
(1807, 15, 5, '«ما تسبق من» زائدة «أمة أجلها وما يستأخرون» يتأخرون عنه.'),
(1808, 15, 6, '«وقالوا» أي كفار مكة للنبي صلى الله عليه وسلم «يا أيها الذي نُزّل عليه الذكر» القرآن في زعمه «إنك لمجنون».'),
(1809, 15, 7, '«لو ما» هلا «تأتينا بالملائكة إن كنت من الصادقين» في قولك إنك نبي وإن هذا القرآن من عند الله.'),
(1810, 15, 8, 'قال تعالى «ما تَنَزَّلُ» فيه حذف إحدى التاءين «الملائكة إلا بالحق» بالعذاب «وما كانوا إذاً» أي حين نزول الملائكة بالعذاب «منظرين» مؤخرين.'),
(1811, 15, 9, '«إنا نحن» تأكيد لاسم إن أو فصل «نزلنا الذكر» القرآن «وإنا له لحافظون» من التبديل والتحريف والزيادة والنقص.'),
(1812, 15, 10, '«ولقد أرسلنا من قبلك» رسلا «في شيع» فرق «الأولين».'),
(1813, 15, 11, '«وما» كان «يأتيهم من رسول إلا كانوا به يستهزئون» كاستهزاء قومك بك وهذا تسلية له صلى الله عليه وسلم.'),
(1814, 15, 12, '«كذلك نسلكه» أي مثل إدخالنا التكذيب في قلوب أولئك ندخله «في قلوب المجرمين» أي كفار مكة.'),
(1815, 15, 13, '«لا يؤمنون به» بالنبي صلى الله عليه وسلم «وقد خلت سنة الأولين» أي سنة الله فيهم من تعذيبهم بتكذيبهم أنبياءهم وهؤلاء مثلهم.'),
(1816, 15, 14, '«ولو فتحنا عليهم بابا من السماء فظلوا فيه» في الباب «يعرجون» يصعدون.'),
(1817, 15, 15, '«لقالوا إنما سُكِّرت» سدت «أبصارنا بل نحن قوم مسحورون» يخيل إلينا ذلك.'),
(1818, 15, 16, '«ولقد جعلنا في السماء بروجا» اثني عشر: الحمل والثور والجوزاء والسرطان والأسد والسنبلة والميزان والعقرب والقوس والجدي والدلو والحوت، وهي منازل الكواكب السبعة السيارة: المريخ وله الحمل والعقرب، والزهرة ولها الثور والميزان، وعطارد وله الجوزاء والسنبلة، والقمر وله السرطان، والشمس ولها الأسد، والمشتري وله القوس والحوت، وزحل له الجدي والدلو «وزيناها» بالكواكب «للناظرين».'),
(1819, 15, 17, '«وحفظناها» بالشهب «من كل شيطان رجيم» مرجوم.'),
(1820, 15, 18, '«إلا» لكن «من استرق السمع» خطفه «فأتبعه شهاب مبين» كوكب يضيء ويحرقه أو يثقبه أو يخبله.'),
(1821, 15, 19, '«والأرض مددناها» بسطناها «وألقينا فيها رواسي» جبالا ثوابت لئلا تتحرك بأهلها «وأنبتنا فيها من كل شيء موزون» معلوم مقدر.'),
(1822, 15, 20, '«وجعلنا لكم فيها معايش» بالياء من الثمار والحبوب «و» جعلنا لكم «من لستم له برازقين» من العبيد والدواب والأنعام فإنما يرزقهم الله.'),
(1823, 15, 21, '«وإن» ما «من» زائدة «شيء إلا عندنا خزائنه» مفاتيح خزائنه «وما ننزله إلا بقدر معلوم» على حسب المصالح.'),
(1824, 15, 22, '«وأرسلنا الرياح لواقح» تلقح السحاب فيمتلئ ماء «فأنزلنا من السماء» السحاب «ماء» مطرا «فأسقيناكموه وما أنتم له بخازنين» أي ليست خزائنه بأيديكم.'),
(1825, 15, 23, '«وإنا لنحن نحي ونميت ونحن الوارثون» الباقون نرث جميع الخلق.'),
(1826, 15, 24, '«ولقد علمنا المستقدمين منكم» أي من تقدم من الخلق من لدن آدم «ولقد علمنا المستأخرين» المتأخرين إلى يوم القيامة.'),
(1827, 15, 25, '«وإن ربك هو يحشرهم إنه حكيم» في صنعه «عليم» بخلقه.'),
(1828, 15, 26, '«ولقد خلقنا الإنسان» آدم «من صلصال» طين يابس يسمع له صلصلة إذا نقر «من حمأ» طين أسود «مسنون» متغير.'),
(1829, 15, 27, '«والجان» أبا الجان وهو إبليس «خلقناه من قبل» أي قبل خلق آدم «من نار السموم» هي نار لا دخان لها تنفذ من المسام.'),
(1830, 15, 28, '«و» اذكر «إذ قال ربك للملائكة إني خالق بشرا من صلصال من حمأ مسنون».'),
(1831, 15, 29, '«فإذا سويته» أتممته «ونفخت» أجريت «فيه من روحي» فصار حيا وإضافة الروح إليه تشريف لآدم «فقعوا له ساجدين» سجود تحية بالانحناء.'),
(1832, 15, 30, '«فسجد الملائكة كلهم أجمعون» فيه تأكيدان.'),
(1833, 15, 31, '«إلا إبليس» هو أبو الجن كان بين الملائكة «أبى» امتنع من «أن يكون مع الساجدين».'),
(1834, 15, 32, '«قال» تعالى «يا إبليس مالك» ما منعك «أ» ن «لا» زائدة «تكون مع الساجدين».'),
(1835, 15, 33, '«قال لم أكن لأسجد» لا ينبغي لي أن أسجد «لبشر خلقته من صلصال من حمأ مسنون».'),
(1836, 15, 34, '«قال فاخرج منها» أي الجنة وقيل من السماوات «فإنك رجيم» مطرود.'),
(1837, 15, 35, '«وإن عليك اللعنة إلى يوم الدين» الجزاء.'),
(1838, 15, 36, '«قال رب فأنظرني إلى يوم يبعثون» أي الناس.'),
(1839, 15, 37, '«قال فإنك من المنظرين».'),
(1840, 15, 38, '«إلى يوم الوقت المعلوم» وقت النفخة الأولى.'),
(1841, 15, 39, '«قال ربّ بما أغويتني» أي بإغوائك لي والباء للقسم وجوابه «لأزيِّنَنَّ لهم في الأرض» المعاصي «ولأغوينهم أجمعين»,'),
(1842, 15, 40, '«إلا عبادك منهم المخلصين» أي المؤمنين.'),
(1843, 15, 41, '«قال» تعالى «هذا صراط عليَّ مستقيم».'),
(1844, 15, 42, 'وهو «إن عبادي» أي المؤمنين «ليس لك عليهم سلطان» قوة «إلا» لكن «من اتبعك من الغاوين» الكافرين.'),
(1845, 15, 43, '«وإن جهنم لموعدهم أجمعين» أي من اتبعك معك.'),
(1846, 15, 44, '«لها سبعة أبواب» أطباق «لكل باب» منها «منهم جزء» نصيب «مقسوم».'),
(1847, 15, 45, '«إن المتقين في جنات» بساتين «وعيون» تجري فيها.'),
(1848, 15, 46, 'ويقال لهم «ادخلوها بسلام» أي سالمين من كل مخوّف أو مع سلام أي سلموا وادخلوا «آمنين» من كل فزع.'),
(1849, 15, 47, '«ونزعنا ما في صدورهم من غِلِّ» حقد «إخوانا» حال منهم «على سُرر متقابلين» حال أيضا أي لا ينظر بعضهم إلى قفا بعض لدورات الأسرَّة بهم.'),
(1850, 15, 48, '«لا يمسهم فيها نَصَبٌ» تعب «وما هم منها بمخرجين» أبدا.'),
(1851, 15, 49, '«نبئ» خبر يا محمد «عبادي أني أنا الغفور» للمؤمنين «الرحيم» بهم.'),
(1852, 15, 50, '«وأن عذابي» للعصاة «هو العذاب الأليم» المؤلم.'),
(1853, 15, 51, '«ونبئهم عن ضيف إبراهيم» وهم الملائكة اثنا عشر أو عشرة أو ثلاثة منهم جبريل.'),
(1854, 15, 52, '«إذ دخلوا عليه فقالوا سلاما» أي هذا اللفظ «قال» إبراهيم لما عرض عليم الأكل فلم يأكلوا «إنا منكم وجلون» خائفون.'),
(1855, 15, 53, '«قالوا لا توجل» لا تخف «إنا» رسل ربك «نبشرك بغلام عليم» ذي علم كثير هو إسحاق كما ذكرنا في سورة هود.'),
(1856, 15, 54, '«قال أبشرتموني» بالولد «على أن مسني الكبر» حال أي مع مسه إياي «فبم» فبأي شيء «تبشرون» استفهام تعجب.'),
(1857, 15, 55, '«قالوا بشرناك بالحق» بالصدق «فلا تكن من القانطين» الآيسين.'),
(1858, 15, 56, '«قال ومن» أي لا «يقنَِط» بكسر النون وفتحها «من رحمة ربه إلا الضالون» الكافرون.'),
(1859, 15, 57, '«قال فما خطبكم» شأنكم «أيها المرسلون».'),
(1860, 15, 58, '«قالوا إنا أرسلنا إلى قوم مجرمين» كافرين أي لوط لإهلاكهم.'),
(1861, 15, 59, '«إلا آل لوط إنا لمنجوهم أجمعين» لإيمانهم.'),
(1862, 15, 60, '«إلا امرأته قدرنا إنها لمن الغابرين» الباقين في العذاب لكفرها.'),
(1863, 15, 61, '«فلما جاء آل لوط» أي لوطا «المرسلون».'),
(1864, 15, 62, '«قال» لهم «إنكم قوم منكرون» لا أعرفكم.'),
(1865, 15, 63, '«قالوا بل جئناك بما كانوا» أي قومك «فيه يمترون» يشكون وهو العذاب.'),
(1866, 15, 64, '«وأتيناك بالحق وإنا لصادقون» في قولنا.'),
(1867, 15, 65, '«فأَسْرِ بأهلك بقطع من الليل واتبع أدبارهم» امش خلفهم «ولا يلتفت منكم أحد» لئلا يرى عظيم ما ينزل بهم «وامضوا حيث تؤمرون» وهو الشام.'),
(1868, 15, 66, '«وقضينا» أوحينا «إليه ذلك الأمر» وهو «أن دابر هؤلاء مقطوع مصبحين» حال أي يتم استئصالهم في الصباح.'),
(1869, 15, 67, '«وجاء أهل المدينة» مدينة سدوم وهم قوم لوط لما أخبروا أن في بيت لوط مردا حسانا وهم الملائكة «يستبشرون» حال طمعا في فعل الفاحشة بهم.'),
(1870, 15, 68, '«قال» لوط «إن هؤلاء ضيفي فلا تفضحون».'),
(1871, 15, 69, '«واتقوا الله ولا تخزون» بقصدكم إياهم بفعل الفاحشة بهم.'),
(1872, 15, 70, '«قالوا أو لم ننهك عن العالمين» عن إضافتهم.'),
(1873, 15, 71, '«قال هؤلاء بناتي إن كنتم فاعلين» ما تريدون من قضاء الشهوة فتزوجوهن. قال تعالى:'),
(1874, 15, 72, '«لعمرك» خطاب للنبي صلى الله عليه وسلم أي وحياتك «إنهم لفي سكرتهم يعمهون» يترددون.'),
(1875, 15, 73, '«فأخذتهم الصيحة» صيحة جبريل «مشرقين» وقت شروق الشمس.'),
(1876, 15, 74, '«فجعلنا عاليها» أي قراهم «سافلها» بأن رفعها جبريل إلى السماء وأسقطها مقلوبة إلى الأرض «وأمطرنا عليهم حجارة من سجيل» طين طبخ بالنار.'),
(1877, 15, 75, '«إن في ذلك» المذكور «لآيات» دلالات على وحدانية الله «للمتوسمين» للناظرين المعتبرين.'),
(1878, 15, 76, '«وإنها» أي قرى قوم لوط «لبسبيل مقيم» طريق قريش إلى الشام لم تندرس أفلا يعتبرون بهم؟.'),
(1879, 15, 77, '«إن في ذلك لآية» لعبرة «للمؤمنين».'),
(1880, 15, 78, '«وإن» مخففة أي إنه «كان أصحاب الأيكة» هي غيضة شجر بقرب مدين وهم قوم شعيب «لظالمين» بتكذيبهم شعيبا.'),
(1881, 15, 79, '«فانتقمنا منهم» بأن أهلكناهم بشدة الحر «وإنهما» أي قرى قوم لوط والأيكة «لبإمام» طريق «مبين» واضح أفلا تعتبرون بهم يا أهل مكة.'),
(1882, 15, 80, '«ولقد كذب أصحاب الحجر» واد بين المدينة والشام وهم ثمود «المرسلين» بتكذيبهم صالحا تكذيب لباقي الرسل لاشتراكهم في المجيء بالتوحيد.'),
(1883, 15, 81, '«وآتيناهم آياتنا» في الناقة «فكانوا عنها معرضين» لا يتفكرون فيها.'),
(1884, 15, 82, '«وكانوا ينحتون من الجبال بيوتا آمنين».'),
(1885, 15, 83, '«فأخذتهم الصيحة مصبحين» وقت الصباح.'),
(1886, 15, 84, '«فما أغنى» دفع «عنهم» العذاب «ما كانوا يكسبون» من بناء الحصون وجمع الأموال.'),
(1887, 15, 85, '«وما خلقنا السماوات والأرض وما بينهما إلا بالحق وإن الساعة لآتية» لا محالة فيجازى كل أحد بعمله «فاصفح» يا محمد عن قومك «الصفح الجميل» أعرض عنهم إعراضا لا جزع فيه وهذا منسوخ بآية السيف.'),
(1888, 15, 86, '«إن ربك هو الخلاق» لكل شيء «العليم» بكل شيء.'),
(1889, 15, 87, '«ولقد آتيناك سبعا من المثاني» قال صلى الله عليه وسلم هي الفاتحة رواه الشيخان لأنها تثنى في كل ركعة «والقرآن العظيم».'),
(1890, 15, 88, '«لا تمدّن عينيك إلى ما متعنا به أزواجا» أصنافا «منهم ولا تحزن عليهم» إن لم يؤمنوا «واخفض جناحك» ألن جانبك «للمؤمنين».'),
(1891, 15, 89, '«وقل إني أنا النذير» من عذاب الله أن ينزل عليكم «المبين» البين الإنذار.'),
(1892, 15, 90, '«كما أنزلنا» العذاب «على المقتسمين» اليهود والنصارى.'),
(1893, 15, 91, '«الذين جعلوا القرآن» أي كتبهم المنزلة عليهم «عضين» أجزاء، حيث آمنوا ببعض وكفروا ببعض، وقيل المراد بهم الذين اقتسموا طرق مكة يصدون الناس عن الإسلام، وقال بعضهم في القرآن سحر وبعضهم كهانة وبعضهم شعر.'),
(1894, 15, 92, '«فوربك لنسألنهم أجمعين» سؤال توبيخ.'),
(1895, 15, 93, '«عما كانوا يعملون».'),
(1896, 15, 94, '«فاصدع» يا محمد «بما تؤمر» به أي اجهر به وأمضه «وأعرض عن المشركين» هذا قبل الأمر بالجهاد.'),
(1897, 15, 95, '«إنا كفيناك المستهزئين» بك بإهلاكنا كلا بآفة وهم الوليد بن المغيرة والعاص بن وائل وعدي بن قيس والأسود بن المطلب والأسود بن عبد يغوث.'),
(1898, 15, 96, '«الذين يجعلون مع الله إلها آخر» صفة وقيل مبتدأ ولتضمنه معنى الشرط دخلت الفاء في خبره وهو «فسوف يعلمون» عاقبة أمرهم.'),
(1899, 15, 97, '«ولقد» للتحقيق «نعلم أنك يضيق صدرك بما يقولون» من الاستهزاء والتكذيب.'),
(1900, 15, 98, '«فسبح» ملتبسا «بحمد ربك» أي قل سبحان الله وبحمده «وكن من الساجدين» المصلين.'),
(1901, 15, 99, '«واعبد ربك حتى يأتيك اليقين» الموت.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'لما استبطأ المشركون العذاب نزل: «أتى أمر الله» أي الساعة، وأتى بصيغة الماضي لتحقق وقوعه أي قرب «فلا تستعجلوه» تطلبوه قبل حينه فإنه واقع لا محالة «سبحانه» تنزيهاً له «وتعالى عما يشكرون» به غيره.'),
(1903, 16, 2, '«ينزل الملائكة» أي جبريل «بالروح» بالوحي «من أمره» بإرادته «على من يشاء من عباده» وهم الأنبياء «أن» مفسرة «أنذروا» خوفوا الكافرين بالعذاب وأعلموهم «أنه لا إله إلا أنا فاتقون» خافون.'),
(1904, 16, 3, '(خلق السماوات والأرض بالحق) أي محقا (تعالى عما يشركون) به من الأصنام.'),
(1905, 16, 4, '(خلق الإنسان من نطفة) مَنِيّ إلى أن صيره قويا شديداً (فإذا هو خصيم) شديد الخصومة (مبين) بينها في نفي البعث قائلاً "" من يحيي العظام وهي رميم "".'),
(1906, 16, 5, '«والأنعام» الإبل والبقر والغنم، ونصبه بفعل مقدر يفسره «خلقها لكم» من جملة الناس «فيها دفءٌ» ما تستدفئون به من الأكسية والأردية من أشعارها وأصوافها «ومنافع» من النسل والدرّ والركوب «ومنها تأكلون» قدم الظرف للفاصلة.'),
(1907, 16, 6, '«ولكم فيها جمال» زينة «حين تريحون» تردونها إلى مراحها بالعشي «وحين تسرحون» تخرجونها إلى المرعى بالغداة.'),
(1908, 16, 7, '«وتحمل أثقالكم» أحمالكم «إلى بلدٍ لم تكونوا بالغيه» واصلين على غير الإبل «إلا بشق الأنفس» بجهدها «إن ربكم لرءُوف رحيم» حيث خلقها لكم.'),
(1909, 16, 8, '«و» خلق «الخيل والبغال والحمير لتركبوها وزينة» مفعول له، والتعليل بهما بتعريف النعم لا ينافي خلقها لغير ذلك كالأكل في الخيل، الثابت بحديث الصحيحين «ويخلق ما لا تعلمون» من الأشياء العجيبة الغريبة.'),
(1910, 16, 9, '«وعلى الله قصد السبيل» أي بيان الطريق المستقيم «ومنها» أي السبيل «جائر» حائد عن الاستقامة «ولو شاء» هدايتكم «لهداكم» إلى قصد السبيل «أجمعين» فتهتدون إليه باختيار منكم.'),
(1911, 16, 10, '«هو الذي أنزل من السماء ماء لكم منه شراب» تشربونه «ومنه شجر» ينبت بسببه «فيه تسيمون» ترعون دوابكم.'),
(1912, 16, 11, '«ينبت لكم به الزرع والزيتون والنخيل والأعناب ومن كل الثمرات إن في ذلك» المذكور «لآية» دالة على وحدانيته تعالى «لقوم يتفكرون» في صنعه فيؤمنون.'),
(1913, 16, 12, '«وسخَّر لكم الليل والنهار والشمس» بالنصب عطفاً على ما قبله والرفع مبتدأ «والقمر والنجوم» بالوجهين «مسخرات» بالنصب حال والرفع خبر «بأمره» بإرادته «إن في ذلك لآيات لقوم يعقلون» يتدبرون.'),
(1914, 16, 13, '«و» سخر لكم «ما ذرَأ» خلق «لكم في الأرض» من الحيوان والنبات وغير ذلك «مختلفاً ألوانه» كأحمر وأصفر وأخضر وغيرها «إن في ذلك لآية لقوم يذكَّرون» يتعظون.'),
(1915, 16, 14, '«وهو الذي سخَّر البحر» ذلله لركوبه والغوص فيه «لتأكلوا منه لحماً طرياً» هو السمك «وتستخرجوا منه حليه تلبسونها» هي اللؤلؤ والمرجان «وترى» تبصر «الفلك» السفن «مواخر فيه» تمخر الماء، أي تشقه يجريها فيه مقبلة ومدبرة بريح واحدة «ولتبتغوا» عطف على لتأكلوا، تطلبوا «من فضله» تعالى بالتجارة «ولعلكم تشكرون» الله على ذلك.'),
(1916, 16, 15, '«وألقى في الأرض رواسي» جبالاً ثوابت لـ «أن» لا «تميد» تتحرك «بكم و» جعل فيها «أنهاراً» كالنيل «وسبلاً» طرقاً «لعلكم تهتدون» إلى مقاصدكم.'),
(1917, 16, 16, '«وعلامات» تستدلون بها على الطرق كالجبال بالنهار «وبالنجم» بمعنى النجوم «هم يهتدون» إلى الطرق والقبلة بالليل.'),
(1918, 16, 17, '«أفمن يخلق» وهو الله «كمن لا يخلق» وهو الأصنام حيث تشركونها معه في العبادة؟ لا «أفلا تذكرون» هذا فتؤمنون.'),
(1919, 16, 18, '«وإن تعدّوا نعمة الله لا تحصوها» تضبطوها فضلاً أن تطيقوا شكرها «إن الله لغفور رحيم» حيث ينعم عليكم مع تقصيركم وعصيانكم.'),
(1920, 16, 19, '«والله يعلم ما تسرون وما تعلنون».'),
(1921, 16, 20, '«والذين تدعون» بالتاء والياء تعبدون «من دون الله» وهم الأصنام «لا يخلقون شيئاً وهم يُخلقون» يصورون من الحجارة وغيرها.'),
(1922, 16, 21, '«أموات» لا روح فيه خبر ثان «غير أحياء» تأكيد «وما يشعرون» أي الأصنام «أيان» وقت «يبعثون» أي الخلق فكيف يعبدون، إذ لا يكون إلهاً إلا الخالق الحي العالم بالغيب.'),
(1923, 16, 22, '«إلهكم» المستحق للعبادة منكم «إله واحد» لا نظير له في ذاته ولا في صفاته وهو الله تعالى «فالذين لا يؤمنون بالآخرة قلوبهم منكرة» جاحدة للوحدانية «وهم مستكبرون» متكبرون عن الإيمان بها.'),
(1924, 16, 23, '«لا جرم» حقا «أن الله يعلم ما يسرون وما يعلنون» فيجازيهم بذلك «إنه لا يحب المستكبرين» بمعنى أنه يعاقبهم.'),
(1925, 16, 24, 'ونزل في النضر بن الحارث «وإذا قيل لهم ما» استفهامية «ذا» موصولة «أنزل ربكم» على محمد «قالوا» هو «أساطير» أكاذيب «الأولين» إضلالاً للناس.'),
(1926, 16, 25, '«ليحملوا» في عاقبة الأمر «أوزارهم» ذنوبهم «كاملة» لم يُكفَّر منها شيء «يوم القيامة ومن» بعض «أوزار الذين يضلونهم بغير علم» لأنهم دعوهم إلى الضلال فاتبعوهم فاشتركوا في الإثم «ألا ساء» بئس «ما يزرون» يحملونه حملهم هذا.'),
(1927, 16, 26, '«قد مكر الذين من قبلهم» وهو نمروذ بنى صرحاً طويلا ليصعد منه إلى السماء ليقاتل أهلها «فأتى اللهُ» قصد «بنيانهم من القواعد» الأساس فأرسل عليه الريح والزلزلة فهدمته «فخر عليهم السقف من فوقهم» أي هم تحته «وأتاهم العذاب من حيث لا يشعرون» من جهة لا تخطر ببالهم وقيل هذا تمثيل لإفساد ما أبرموه من المكر بالرسل.'),
(1928, 16, 27, '«ثم يوم القيامة يخزيهم» يذلهم «ويقول» الله لهم على لسان الملائكة توبيخاً «أين شركائي» بزعمكم «الذين كنتم تشاقون» تخالفون المؤمنين «فيهم» في شأنهم «قال» أي يقول «الذين أوتوا العلم» من الأنبياء والمؤمنين «إن الخزي اليوم والسوء على الكافرين» يقولونه شماتة بهم.'),
(1929, 16, 28, '«الذين تتوفاهم» بالتاء والياء «الملائكة ظالمي أنفسهم» بالكفر «فألقوا السلم» انقادوا واستسلموا عند الموت قائلين «ما كنا نعمل من سوء» شرك فتقول الملائكة «بلى إن الله عليم بما كنتم تعملون» فيجازيكم به.'),
(1930, 16, 29, 'ويقال لهم «فادخلوا أبواب جهنم خالدين فيها فلبئس مثوى» مأوى «المتكبرين».'),
(1931, 16, 30, '«وقيل للذين اتقوْا» الشرك «ماذا أنزل ربكم قالوا خيراً للذين أحسنوا» بالإيمان «في هذه الدنيا حسنة» حياة طيبة «ولدار الآخرة» أي الجنة «خير» من الدنيا وما فيها قال تعالى فيها «ولنعم دار المتقين» هي.'),
(1932, 16, 31, '«جنات عدن» إقامة مبتدأ خبره «يدخلونها تجري من تحتها الأنهار لهم فيها ما يشاءُون كذلك» الجزاء «يجزي الله المتقين».'),
(1933, 16, 32, '«الذين» نعت «تتوفاهم الملائكة طيبين» طاهرين من الكفر «يقولون» عند الموت «سلام عليكم» ويقال لهم في الآخرة «ادخلوا الجنة بما كنتم تعملون».'),
(1934, 16, 33, '«هل» ما «ينظرون» ينتظر الكفار «إلا أن تأتيهم» بالتاء والياء «الملائكة» لقبض أرواحهم «أو يأتي أمر ربك» العذاب أو القيامة المشتملة عليه «كذلك» كما فعل هؤلاء «فعل الذين من قبلهم» من الأمم كذبوا رسلهم فأهلكوا «وما ظلمهم الله» بإهلاكهم بغير ذنب «ولكن كانوا أنفسهم يظلمون» بالكفر.'),
(1935, 16, 34, 'فأصابهم سيئات ما عملوا» أي جزاءها «وحاق» نزل «بهم ما كانوا به يستهزءُون» أي العذاب.'),
(1936, 16, 35, '«وقال الذين أشركوا» من أهل مكة «لو شاء الله ما عبدنا من دونه من شيء نحن ولا آباؤنا ولا حرمنا من دونه من شيء» من البحائر والسوائب فإشراكنا وتحريمنا بمشيئته فهو راض به، قال تعالى: «كذلك فعل الذين من قبلهم» أي كذبوا ربهم فيما جاءوا به «فهل» فما «على الرسل إلا البلاغ المبين» إلا البلاغ البيِّن وليس عليهم الهداية.'),
(1937, 16, 36, '«ولقد بعثنا في كل أمة رسولاً» كما بعثناك في هؤلاء «أن» بأن «اعبدوا الله» وحدوه «واجتنبوا الطاغوت» الأوثان أن تعبدوها «فمنهم من هدى الله» فآمن «ومنهم من حقت» وجَبَتْ «عليه الضلالة» في علم الله فلم يؤمن «فسيروا» يا كفار مكة «في الأرض فانظروا كيف كان عاقبة المكذبين» رسلهم من الهلاك.'),
(1938, 16, 37, '«إن تحرص» يا محمد «على هداهم» وقد أضلهم الله لا تقدر على ذلك «فإن الله لا يُهدِي» بالبناء للمفعول وللفاعل «من يضل» من يريد إضلاله «وما لهم من ناصرين» مانعين من عذاب الله.'),
(1939, 16, 38, '«وأقسموا بالله جهد أيمانهم» أي غاية اجتهادهم فيها «لا يبعث الله من يموت» قال تعالى «بلى» يبعثهم «وعداً عليه حقا» مصدران مؤكدان منصوبان بفعلهما المقدر أي وعد ذلك وحقه حقا «ولكن أكثر الناس» أي أهل مكة «لا يعلمون» ذلك.'),
(1940, 16, 39, '«ليبين» متعلق ببعثهم المقدر «لهم الذي يختلفون» مع المؤمنين «فيه» من أمر الدين بتعذيبهم وإثابة المؤمنين «وليعلم الذين كفروا أنهم كانوا كاذبين» في إنكار البعث.'),
(1941, 16, 40, '«إنما قوْلنا لشيء إذا أردناه» أي أردنا إيجاده وقولنا مبتدأ خبره «أن نقول له كن فيكونُ» أي فهو يكون وفي قراءة بالنصب عطفاً على نقول والآية لتقرير القدرة على البعث.'),
(1942, 16, 41, '«والذين هاجروا في الله» لإقامة دينه «من بعدما ظلموا» بالأذى من أهل مكة وهم النبي وأصحابه «لنبوِّئَنهم» ننزلهم «في الدنيا» داراً «حسنة» هي المدينة «ولأجر الآخرة» أي الجنة «أكبر» أعظم «لو كانوا يعلمون» أي الكفار أو المتخلفون عن الهجرة ما للمهاجرين من الكرامة لوافقوهم.'),
(1943, 16, 42, 'هم «الذين صبروا» على أذى المشركين والهجرة لإظهار الدين «وعلى ربهم يتوكلون» فيرزقهم من حيث لا يحتسبون.'),
(1944, 16, 43, '«وما أرسلنا من قبلك إلا رجالاً نوحي إليهم» لا ملائكة «فاسألوا أهل الذكر» العلماء بالتوراة والإنجيل «إن كنتم لا تعلمون» ذلك، فإنهم يعلمونه وأنتم إلى تصديقهم أقرب من تصديق المؤمنين بمحمد صلى الله عليه وسلم.'),
(1945, 16, 44, '«بالبينات» متعلق بمحذوف أي أرسلناهم بالحجج الواضحة «والزُّبُر» الكتب «وأنزلنا إليك الذكر» القرآن «لتبين للناس ما نزل إليهم» فيه من الحلال والحرام «ولعلهم يتفكرون» في ذلك فيعتبرون.'),
(1946, 16, 45, '«أفأمِنَ الذين مكروا» المكرات «السيئات» بالنبي صلى اله عليه وسلم في دار الندوة من تقييده أو قتله أو إخراجه كما ذكر في الأنفال «أن يخسف الله بهم الأرض» كقارون «أو يأتيهم العذاب من حيث لا يشعرون» أي جهة لا تخطر ببالهم وقد أهلكوا ببدر ولم يكونوا يقدّرون ذلك.'),
(1947, 16, 46, '«أو يأخذهم في تقلبهم» في أسفارهم للتجارة «فما هم بمعجزين» بفائتي العذاب.'),
(1948, 16, 47, '«أو يأخذهم على تخوف» تنقص شيئاً فشيئاً حتى يهلك الجميع حال من الفاعل أو المفعول «فإن ربكم لرءُوف رحيم» حيث لم يعاجلهم بالعقوبة.'),
(1949, 16, 48, '«أولم يروْا إلى ما خلق الله من شيء» له ظل كشجرة وجبل «تتفيَّؤ» تتميل «ظلاله عن اليمين والشمائل» جمع شمال أي عن جانبيهما أول النهار وآخره «سجداً لله» حال أي خاضعين له بما يراد منهم «وهم» أي الظلال «داخرون» صاغرون نزلوا منزلة العقلاء.'),
(1950, 16, 49, '«ولله يسجد ما في السماوات وما في والأرض من دابة» أي نسمة تدب عليها أي تخضع له بما يراد منها، وغلب في الإتيان بما لا يعقل لكثرته «والملائكة» خصهم بالذكر تفضيلاً «وهم لا يستكبرون» يتكبرون عن عبادته.'),
(1951, 16, 50, '«يخافون» أي الملائكة حال من ضمير يستكبرون «ربهم من فوقهم» حال من هم أي عالياً عليهم بالقهر «ويفعلون ما يؤمرون» به.'),
(1952, 16, 51, '«وقال الله لا تتخذوا إلهين اثنين» تأكيد «إنما هو إله واحد» أتى به لإثبات الإلهية والوحدانية «فإياي فارهبون» خافون دون غيري وفيه التفات عن الغيبة.'),
(1953, 16, 52, '«وله ما في السماوات والأرض» ملكاً وخلقاً وعبيداً «وله الدين» الطاعة «واصباً» دائماً حال من الدين والعامل فيه معنى الظرف «أفغير الله تتقون» وهو الإله الحق ولا إله غيره والاستفهام للإنكار والتوبيخ.'),
(1954, 16, 53, '«وما بكم من نعمة فمن الله» لا يأتي بها غيره و"ما" شرطية أو موصولة «ثم إذا مسكم» أصابكم «الضر» الفقر والمرض «فإليه تجأرون» ترفعون أصواتكم بالاستغاثة والدعاء ولا تدعون غيره.'),
(1955, 16, 54, '«ثم إذا كشف الضر عنكم إذا فريق منكم بربهم يشركون».'),
(1956, 16, 55, '«ليكفروا بما آتيناهم» من النعمة «فتمتعوا» باجتماعكم على عبادة الأصنام أمر تهديد «فسوف تعلمون» عاقبة ذلك.'),
(1957, 16, 56, '«ويجعلون» أي المشركون «لما لا يعلمون» أنها تضر ولا تنفع وهي الأصنام «نصيباً مما رزقناهم» من الحرث والأنعام بقولهم هذا لله لشركائنا «تالله لتسألن» سؤال توبيخ وفيه التفات عن الغيبة «عما كنتم تفترون» على الله من أنه أمركم بذلك.'),
(1958, 16, 57, '(ويجعلون لله البنات) بقولهم الملائكة بنات الله (سبحانه) تنزيهاً له عما زعموا (ولهم ما يشتهون) ـه أي البنون والجملة في محل رفع أو نصب بيجعل، المعنى يجعلون له البنات التي يكرهونها وهو منزه عن الولد ويجعلون لهم الأبناء الذين يختارونهم فيختصون بالأسنى كقوله "" فاستفتهم ألربك البنات ولهم البنون "".'),
(1959, 16, 58, '«وإذا بُشّر أحدهم بالأنثى» تولد له «ظل» صار «وجهه مسوداً» متغيراً تغير مغتَمّ «وهو كظيم» ممتلئ غمًّا فكيف تنسب البنات إليه تعالى.'),
(1960, 16, 59, '«يتوارى» يختفي «من القوم» أي قومه «من سوء ما بشر به» خوفاً من التعيير متردداً فيما يفعل به «أيمسكه» يتركه بلا قتل «على هون» هوان وذل «أم يدسه في التراب» بأن يئده «ألا ساء» بئس «ما يحكمون» حكمهم هذا حيث نسبوا لخالقهم البنات اللاتي هن عندهم بهذا المحل.'),
(1961, 16, 60, '«للذين لا يؤمنون بالآخرة» أي الكفار «مثل السَّوء» أي الصفة السوأى بمعنى القبيحة وهي وأدهم البنات مع احتياجهم إليهن للنكاح «ولله المثل الأعلى» الصفة العليا وهو أنه لا إله إلا هو «وهو العزيز» في ملكه «الحكيم» في خلقه.'),
(1962, 16, 61, '«ولو يؤاخذ الله الناس بظلمهم» بالمعاصي «ما ترك عليها» أي الأرض «من دابة» نسمة تدب عليها «ولكن يؤخرهم إلى أجل مسمى فإذا جاء أجلهم لا يستأخرون» عنه «ساعة ولا يستقدمون» عليه.'),
(1963, 16, 62, '«ويجعلون لله ما يكرهون» لأنفسهم من البنات والشريك في الرياسة وإهانة الرسل «وتصف» تقول «ألسنتهم» مع ذلك «الكذب» وهو «أن لهم الحسنى» عند الله أي الجنة لقوله: (ولئن رجعت إلى ربي إن لي عنده للحسنى) «لا جرم» حقا «أن لهم النار وأنهم مفرطون» متروكون فيها أو مقدمون إليها وفي قراءة بكسر الراء أي متجاوزون الحد.'),
(1964, 16, 63, '«تالله لقد أرسلنا إلى أمم من قبلك» رسلاً «فزين لهم الشيطان أعمالهم» السيئة فرأوها حسنة فكذبوا الرسل «فهو وليهم» متولي أمورهم «اليوم» أي في الدنيا «ولهم عذاب أليم» مؤلم في الآخرة وقيل المراد باليوم يوم القيامة على حكاية الحال الآتية لا ولي لهم غيره وهو عاجز عن نصر نفسه فكيف ينصرهم.'),
(1965, 16, 64, '«وما أنزلنا عليك» يا محمد «الكتاب» القرآن «إلا لتبين لهم الذي اختلفوا فيه» من أمر الدين «وهدى» عطف على لتبين «ورحمة لقوم يؤمنون» به.'),
(1966, 16, 65, '«والله أنزل من السماء ماء فأحيا به الأرض» بالنبات «بعد موتها» يبسها «إن في ذلك» المذكور «لآية» دالة على البعث «لقوم يسمعون» سماع تدبر.'),
(1967, 16, 66, '«وإن لكم في الأنعام لعبرة» اعتبار «نسقيكم» بيان للعبرة «مما في بطونه» أي الأنعام «من» للابتداء متعلقة بنسقيكم «بين فرث» ثفل الكرش «ودمٍ لبناً خالصاً» لا يشوبه شيء من الفرث والدم من طعم أو ريح أو لون أو بينهما «سائغاًً للشاربين» سهل المرور في حلقهم لا يغص به.'),
(1968, 16, 67, '«ومن ثمرات النخيل والأعناب» ثمر «تتخذون منه سكراً» خمراً يسكِر سميت بالمصدر وهذا قبل تحريمها «ورزقاً حسناً» كالتمر والزبيب والخل والدبس «إن في ذلك» المذكور «لآية» دالة على قدرته تعالى «لقوم يعقلون» يتدبرون.'),
(1969, 16, 68, '«وأوحى ربك إلى النحل» وحي إلهام «أن» مفسرة أو مصدرية «اتخذي من الجبال بيوتاً» تأوين إليها «ومن الشجر» بيوتًا «ومما يعرشون» أي الناس يبنون لك من الأماكن وإلا لم تأو إليها.'),
(1970, 16, 69, '«ثم كلي من كل الثمرات فاسلكي» ادخلي «سبل ربك» طرقه في طلب المرعى «ذللا» جمع ذلول حال من السبل أي مسخرة لك فلا تعسر عليك وإن توعرت ولا تضلي على العود منها وإن بعدت، وقيل من الضمير في اسلكي أي منقادة لما يراد منك «يخرج من بطونها شراب» هو العسل «مختلف ألوانه فيه شفاء للناس» من الأوجاع قيل لبعضها كما دل عليه تنكير شفاء أو لكلها بضميمته إلى غيره وبدونها بنيته وقد أمر به صلى الله عليه وسلم من استطلق عليه بطنه رواه الشيخان «إن في ذلك لآية لقوم يتفكرون» في صنعه تعالى.'),
(1971, 16, 70, '«والله خلقكم» ولم تكونوا شيئاً «ثم يتوفاكم» عند انقضاء آجالكم «ومنكم من يرد إلى أرذل العمر» أي أخسه من الهرم والخرف «لكي لا يعلم بعد علم شيئاً» قال عكرمة من قرأ القرآن لم يصر بهذه الحالة «إن الله عليم» بتدبير خلقه «قدير» على ما يريده.'),
(1972, 16, 71, '«والله فضل بعضكم على بعض في الرزق» فمنكم غني وفقير ومالك ومملوك «فما الذين فضلوا» أي الموالي «برادي رزقهم على ما ملكت أيمانهم» أي بجاعلي ما رزقناهم من الأموال وغيرها شركة بينهم وبين مماليكهم «فهم» أي المماليك والموالي «فيه سواء» شركاء المعنى ليس لهم شركاء من مماليكهم في أموالهم فكيف يجعلون بعض مماليك الله شركاء له «أفبنعمة الله يجحدون» يكفرون حيث يجعلون له شركاء.'),
(1973, 16, 72, '«والله جعل لكم من أنفسكم أزواجا» فخلق حواء من ضلع آدم وسائر الناس من نطف الرجال والنساء «وجعل لكم من أزواجكم بنين وحفدة» أولاد الأولاد «ورزقكم من الطيبات» من أنواع الثمار والحبوب والحيوان «أفبالباطل» الصنم «يؤمنون وبنعمة الله هم يكفرون» بإشراكهم.'),
(1974, 16, 73, '«ويعبدون من دون الله» أي غيره «ما لا يملك لهم رزقا من السماوات» بالمطر «والأرض» بالنبات «شيئا» بدل من رزقا «ولا يستطيعون» يقدرون على شيء وهو الأصنام.'),
(1975, 16, 74, '«فلا تضربوا لله الأمثال» لا تجعلوا لله أشباها تشركون به «إن الله يعلم» أن لا مثل له «وأنتم لا تعلمون» ذلك.'),
(1976, 16, 75, '«ضرب الله مثلاً» ويبدل منه «عبدا مملوكا» صفة تميزه من الحر فإنه عبد الله «لا يقدر على شيء» لعدم ملكه «ومن» نكرة موصوفة أي حرا «رزقناه منا رزقا حسنا فهو ينفق منه سرا وجهرا» أي يتصرف فيه كيف يشاء والأول مثل الأصنام والثاني مثله تعالى «هل يستوون» أي العبيد العجزة والحر المتصرف؟ لا «الحمد لله» وحده «بل أكثرهم» أي أهل مكة «لا يعلمون» ما يصيرون إليه من العذاب فيشركون.'),
(1977, 16, 76, '«وضرب الله مثلاً» ويبدل منه «رجلين أحدهما أبكم» ولد أخرس «لا يقدر على شيء» لأنه لا يفهم ولا يُفهم «وهو كلّ» ثقيل «على مولاه» وليّ أمره «أينما يوجهه» يصرفه «لا يأت» منه «بخير» ينجح وهذا مثل الكافر «هل يستوي هو» أي الأبكم المذكور «ومن يأمر بالعدل» أي ومن هو ناطق نافع للناس حيث يأمر به ويحث عليه «وهو على صراط» طريق «مستقيم» وهو الثاني المؤمن؟ لا، وقيل هذا مثل الله، والأبكم للأصنام والذي قبله مثل الكافر والمؤمن.'),
(1978, 16, 77, '«ولله غيب السماوات والأرض» أي علم ما غاب فيهما «وما أمر الساعة إلا كلمح البصر أو هو أقرب» لأنه بلفظ كن فيكون «إن الله على كل شيء قدير».'),
(1979, 16, 78, '«والله أخرجكم من بطون أمهاتكم لا تعلمون شيئا» الجملة حال «وجعل لكم السمع» بمعنى الأسماع «والأبصار والأفئدة» القلوب «لعلكم تشكرونـ» ـه على ذلك فتؤمنون.'),
(1980, 16, 79, '«ألم يروْا إلى الطير مسخرات» مذللات للطيران «في جو السماء» أي الهواء بين السماء والأرض «ما يمسكهن» عند قبض أجنحتهن أو بسطها أن يقعن «إلا الله» بقدرته «إن في ذلك لآيات لقوم يؤمنون» هي خلقها بحيث يمكنها الطيران وخلق الجو بحيث يمكن الطيران فيه وإمساكها.'),
(1981, 16, 80, '«والله جعل لكم من بيوتكم سكنا» موضعا تسكنون فيه «وجعل لكم من جلود الأنعام بيوتا» كالخيام والقباب «تستخفونها» للحمل «يوم ظعنكم» سفركم «ويوم إقامتكم ومن أصوافها» أي الغنم «وأوبارها» أي الإبل «وأشعارها» أي المعز «أثاثا» متاعا لبيوتكم كبسط وأكسية «ومتاعا» تتمتعون به «إلى حين» يبلى فيه.'),
(1982, 16, 81, '«والله جعل لكم مما خلق» من البيوت والشجر والغمام «ظلالاً» جمع ظل، تقيكم حر الشمس «وجعل لكم من الجبال أكنانا» جمع كن، وهو ما يستكن فيه كالغار والسرب «وجعل لكم سرابيل» قمصا «تقيكم الحر» أي والبرد «وسرابيل تقيكم بأسكم» حربكم أي الطعن والضرب فيها كالدروع والجواشن «كذلك» كما خلق هذه الأشياء «يُتم نعمته» في الدنيا «عليكم» بخلق ما تحتاجون إليه «لعلكم» يا أهل مكة «تسلمون» توحدونه.'),
(1983, 16, 82, '«فإن تولوْا» أعرضوا عن الإسلام «فإنما عليك» يا محمد «البلاغ المبين» الإبلاغ البيِّن وهذا قبل الأمر بالقتال.'),
(1984, 16, 83, '«يعرفون نعمة الله» أي يقرّون بأنها من عنده «ثم ينكرونها» بإشراكهم «وأكثرهم الكافرون».'),
(1985, 16, 84, '«و» اذكر «يوم نبعث من كل أمة شهيداً» هو نبيها يشهد لها وعليها وهو يوم القيامة «ثم لا يؤذن للذين كفروا» في الاعتذار «ولا هم يستعتبون» لا يطلب منهم العتبى أي الرجوع إلى ما يرضي الله.'),
(1986, 16, 85, '«وإذا رأى الذين ظلموا» كفروا «العذاب» النار «فلا يُخفف عنهم» العذاب «ولا هم يُنظرون» يمهلون عنه إذا رأوه.'),
(1987, 16, 86, '(وإذا رأى الذين أشركوا شركاءهم) من الشياطين وغيرها (قالوا ربنا هؤلاء شركاؤنا الذين كنا ندعو) نعبدهم (من دونك فألقوا إليهم القول) أي قالوا لهم (إنكم لكاذبون) في قولكم إنكم عبدتمونا كما في آية أخرى " ما كانوا إيانا يعبدون "، سيكفرون بعبادتهم.'),
(1988, 16, 87, '«وألقوا إلى الله يومئذ السلم» أي استسلموا لحكمه «وضل» غاب «عنهم ما كانوا يفترون» من أن آلهتهم تشفع لهم.'),
(1989, 16, 88, '«الذين كفروا وصدوا» الناس «عن سبيل الله» دينه «زدناهم عذابا فوق العذاب» الذي استحقوه بكفرهم قال ابن مسعود: عقارب أنيابها كالنخل الطوال «بما كانوا يفسدون» بصدهم الناس عن الإيمان.'),
(1990, 16, 89, '«و» اذكر «يوم نبعث في كل أمة شهيدا عليهم من أنفسهم» وهو نبيهم «وجئنا بك» يا محمد «شهيدا على هؤلاء» أي قومك «ونزلنا عليك الكتاب» القرآن «تبيانا» بيانا «لكل شيء» يحتاج إليه الناس من أمر الشريعة «وهدى» من الضلالة «ورحمة وبشرى» بالجنة «للمسلمين» الموحدين.'),
(1991, 16, 90, '«إن الله يأمر بالعدل» التوحيد أو الإنصاف «والإحسان» أداء الفرائض أو أن تعبد الله كأنك تراه كما في الحديث «وإيتاءِ» إعطاء «ذي القربى» القرابة خصه بالذكر اهتماما به «وينهى عن الفحشاء» الزنا «والمنكر» شرعا من الكفر والمعاصي «والبغي» الظلم للناس خصه بالذكر اهتماما كما بدأ بالفحشاء كذلك «يعظكم» بالأمر والنهي «لعلكم تذكرون» تتعظون وفيه إدغام التاء في الأصل في الذال، وفي المستدرك عن ابن مسعود وهذه أجمع آية في القرآن للخير والشر.'),
(1992, 16, 91, '«وأوْفوا بعهد الله» من البيع والأيمان وغيرها «إذا عاهدتم ولا تنقضوا الأيمان بعد توكيدها» توثيقها «وقد جعلتم الله عليكم كفيلاً» بالوفاء حيث حلفتم به والجملة حال «إن الله يعلم ما تفعلون» تهديد لهم.'),
(1993, 16, 92, '«ولا تكونوا كالتي نقضت» أفسدت «غزلها» ما غزلته «من بعد قوة» إحكام له وبرم «أنكاثا» حال جمع نكث وهو ما ينكث أي يحل إحكامه وهي امرأة حمقاء من مكة كانت تغزل طوال يومها ثم تنقضه «تتخذون» حال من ضمير تكونوا: أي لا تكونوا مثلها في اتخاذكم «أيمانكم دخلاً» هو ما يدخل في الشيء وليس منه أي فسادا أو خديعة «بينكم» بأن تنقضوها «أن» أي لأن «تكون أُمة» جماعة «هي أربى» أكثر «من أمة» وكانوا يحالفون الحلفاء فإذا وجد أكثر منهم وأعز نقضوا حلف أولئك وحالفوهم «إنما يبلوكم» يختبركم «الله به» أي بما أمر به من الوفاء بالعهد لينظر المطيع منكم والعاصي أو يكون أمة أربى لينظر أتفون أم لا «وليبينن لكم يوم القيامة ما كنتم فيه تختلفون» في الدنيا من أمر العهد وغيره بأن يعذب الناكث ويثبت الوافي.'),
(1994, 16, 93, '«ولو شاء الله لجعلكم أمة واحدة» أهل دين واحد «ولكن يضل من يشاء ويهدي من يشاء ولتسألُن» يوم القيامة سؤال تبكيت «عما كنتم تعملون» لتجازوا عليه.'),
(1995, 16, 94, '«ولا تتخذوا أيمانكم دخلاً بينكم» كرره تأكدا «فتزل قدم» أي أقدامكم عن محجة الإسلام «بعد ثبوتها» استقامتها عليها «وتذوقوا السوء» أي العذاب «بما صددتم عن سبيل الله» أي بصدكم عن الوفاء بالعهد أو بصدكم غيركم عنه لأنه يستن بكم «ولكم عذاب عظيم» في الآخرة.'),
(1996, 16, 95, '«ولا تشتروا بعهد الله ثمنا قليلاً» من الدنيا بأن تنقضوه لأجله «إنما عند الله» من الثواب «هو خير لكم» مما في الدنيا «إن كنتم تعلمون» ذلك فلا تنقضوا.'),
(1997, 16, 96, '«ما عندكم» من الدنيا «يَنفدُ» يفنى «وما عند الله باقي» دائم «وليجزينَّ» بالياء والنون «الذين صبروا» من الوفاء بالعهود «أجرهم بأحسن ما كانوا يعملون» أحسن بمعنى حسن.'),
(1998, 16, 97, '«من عمل صالحاً من ذكر أو أنثى وهو مؤمن فلنحيينه حياة طيبة» قيل هي حياة الجنة وقيل في الدنيا بالقناعة أو الرزق الحلال «ولنجزينهم أجرهم بأحسن ما كانوا يعملون».'),
(1999, 16, 98, '«فإذا قرأت القرآن» أي أردت قراءته «فاستعذ بالله من الشيطان الرجيم» أي قل: أعوذ بالله من الشيطان الرجيم.'),
(2000, 16, 99, '«إنه ليس له سلطان» تسلط «على الذين آمنوا وعلى ربهم يتوكلون».'),
(2001, 16, 100, '«إنما سلطانه على الذين يتولوْنه» بطاعته «والذين هم به» أي الله «مشركون».'),
(2002, 16, 101, '«وإذا بدلنا آية مكان آية» بنسخها وإنزال غيرها بمصلحة العباد «والله أعلم بما ينزل قالوا» أي الكفار للنبي صلى الله عليه وسلم «إنما أنت مفتر» كذاب تقوله من عندك «بل أكثرهم لا يعلمون» حقيقة القرآن وفائدة النسخ.'),
(2003, 16, 102, '«قل» لهم «نزَّله روح القدس» جبريل «من ربك بالحق» متعلق بنزل «ليثبت الذين آمنوا» بإيمانهم به «وهدى وبشرى للمسلمين».'),
(2004, 16, 103, '«ولقد» للتحقيق «نعلم أنهم يقولون إنما يعلمه» القرآن «بشر» وهو قين نصراني كان النبي صلى الله عليه وسلم يدخل عليه قال تعالى «لسان» لغة «الذي يلحدون» يميلون «إليه» أنه يعلمه «أعجمي وهذا» القرآن «لسان عربي مبين» ذو بيان وفصاحة فكيف يعلمه أعجمي.'),
(2005, 16, 104, '«إن الذين لا يؤمنون بآيات الله لا يهديهم الله ولهم عذاب أليم» مؤلم.'),
(2006, 16, 105, '(إنما يفتري الكذب الذين لا يؤمنون بآيات الله) القرآن بقولهم هذا من قول البشر (وأولئك هم الكاذبون) والتأكيد بالتكرار، وإن وغيرهما رد لقولهم "" إنما أنت مفتر "".'),
(2007, 16, 106, '«من كفر بالله من بعد إيمانه إلا من أكره» على التلفظ بالكفر فتلفظ به «وقلبه مطمئن بالإيمان» ومن مبتدأ أو شرطية والخبر أو الجواب لهم وعيد شديد دل على هذا «ولكن من شرح بالكفر صدرا» له أي فتحه ووسعه بمعنى طابت به نفسه «فعليهم غضب من الله ولهم عذاب عظيم».'),
(2008, 16, 107, '«ذلك» الوعيد لهم «بأنهم استحبوا الحياة الدنيا» اختاروها «على الآخرة وأن الله لا يهدي القوم الكافرين».'),
(2009, 16, 108, '«أولئك الذين طبع الله على قلوبهم وسمعهم وأبصارهم وأولئك هم الغافلون» عما يراد بهم.'),
(2010, 16, 109, '«لا جرم» حقاً «أنهم في الآخرة هم الخاسرون» لمصيرهم إلى النار المؤبدة عليهم.'),
(2011, 16, 110, '«ثم إن ربك للذين هاجروا» إلى المدينة «من بعد ما فُتنوا» عذبوا وتلفظوا بالكفر وفي قراءة بالبناء للفاعل أي كفروا أو فتنوا الناس عن الأيمان «ثم جاهدوا وصبروا» على الطاعة «إن ربك من بعدها» أي الفتنة «لغفور» لهم «رحيم» بهم وخبر إن الأولى دل عليه خبر الثانية.'),
(2012, 16, 111, 'اذكر «يوم تأتي كل نفس تجادل» تحاج «عن نفسها» لا يهمها غيرها وهو يوم القيامة «وتوفى كل نفس» جزاء «ما عملت وهم لا يظلمون» شيئا.'),
(2013, 16, 112, '«وضرب الله مثلاً» ويبدل منه «قرية» هي مكة والمراد أهلها «كانت آمنة» من الغارات لا تهاج «مطمئنة» لا يحتاج إلى الانتقال عنها لضيق أو خوف «يأتيها رزقها رغدا» واسعا «من كل مكان فكفرت بأنعم الله» بتكذيب النبي صلى الله عليه وسلم «فأذاقها الله لباس الجوع» فقحطوا سبع سنين «والخوف» بسرايا النبي صلى الله عليه وسلم «بما كانوا يصنعون».'),
(2014, 16, 113, '«ولقد جاءهم رسول منهم» محمد صلى الله عليه وسلم «فكذبوه فأخذهم العذاب» الجوع والخوف «وهم ظالمون».'),
(2015, 16, 114, '«فكلوا» أيها المؤمنون «مما رزقكم الله حلالاً طيبا واشكروا نعمة الله إن كنتم إياه تعبدون».'),
(2016, 16, 115, '«إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به فمن اضطر غير باغ ولا عاد فإن الله غفور رحيم».'),
(2017, 16, 116, '«ولا تقولوا لما تصف ألسنتكم» أي لوصف ألسنتكم «الكذب هذا حلال وهذا حرام» لما لم يحله الله ولم يحرمه «لتفتروا على الله الكذب» بنسبة ذلك إليه «إن الذين يفترون على الله الكذب لا يفلحون».'),
(2018, 16, 117, 'لهم «متاع قليل» في الدنيا «ولهم» في الآخرة «عذاب أليم» مؤلم.'),
(2019, 16, 118, '(وعلى الذين هادوا) أي اليهود (حرمنا ما قصصنا عليك من قبل) في آية "" وعلى الذين هادوا حرمنا كل ذي ظفر "" إلى آخرها (وما ظلمناهم) بتحريم ذلك (ولكن كانوا أنفسهم يظلمون) بارتكاب المعاصي الموجبة لذلك.'),
(2020, 16, 119, '«ثم إن ربك للذين عملوا السوء» الشرك «بجهالة ثم تابوا» رجعوا «من بعد ذلك وأصلحوا» عملهم «إن ربك من بعدها» أي الجهالة أو التوبة «لغفور» لهم «رحيم» بهم.'),
(2021, 16, 120, '«إن إبراهيم كان أُمَّة» إماما قدوة جامعا لخصال الخير «قانتا» مطيعا «لله حنيفا» مائلاً إلى الدين القيم «ولم يك من المشركين».'),
(2022, 16, 121, '«شاكرا لأنعمه اجتباه» اصطفاه «وهداهُ إلى صراط مستقيم».'),
(2023, 16, 122, '«وآتيناه» فيه التفات عن الغيبة «في الدنيا حسنة» هي الثناء الحسن في كل أهل الأديان «وإنه في الآخرة لمن الصالحين» الذين لهم الدرجات العلى.'),
(2024, 16, 123, '«ثم أوحينا إليك» دينه يا محمد «أن اتبع ملة» دينه «إبراهيم حنيفا وما كان من المشركين» كرر ردا على زعم اليهود والنصارى أنهم على دينه.'),
(2025, 16, 124, '«إنما جعل السبت» فرض تعظيمه «على الذين اختلفوا فيه» على نبيهم، وهم اليهود أمروا أن يتفرغوا للعبادة يوم الجمعة فقالوا: لا نريده واختاروا السبت فشدد عليه فيه «وإن ربك ليحكم بينهم يوم القيامة فيما كانوا يختلفون» من أمره بأن يثيب الطائع ويعذب العاصي بانتهاك حرمته.'),
(2026, 16, 125, '«ادع» الناس يا محمد «إلى سبيل ربك» دينه «بالحكمة» بالقرآن «والموعظة الحسنة» مواعظه أو القول الرقيق «وجادلهم بالتي» أي بالمجادلة التي «هي أحسن» كالدعاء إلى الله بآياته والدعاء إلى حججه «إن ربك هو أعلم» أي عالم «بمن ضل عن سبيله وهو أعلم بالمهتدين» فيجازيهم وهذا قبل الأمر بالقتال ونزل لما قتل حمزة ومثِّل به فقال صلى الله عليه وسلم وقد رآه: لأمثلن بسبعين منهم مكانك.'),
(2027, 16, 126, '«وإن عاقبتم فعاقبوا بمثل ما عوقبتم به ولئن صبرتم» عن الانتقام «لهو» أي الصبر «خير للصابرين» فكف صلى الله عليه وسلم وكفَّر عن يمينه رواه البزار.'),
(2028, 16, 127, '«واصبر وما صبرك إلا بالله» بتوفيقه «ولا تحزن عليهم» أي الكفار. إن لم يؤمنوا لحرصك على إيمانهم «ولا تك في ضيق مما يمكرون» أي لا تهتم بمكرهم فأنا ناصرك عليهم.'),
(2029, 16, 128, '«إن الله مع الذين اتقوا» الكفر والمعاصي «والذين هم محسنون» بالطاعة والصبر، بالعون والنصر.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, '(سبحان) أي تنزيه (الذي أسرى بعبده) محمد صلى الله عليه وسلم (ليلاً) نصب على الظرف والإسراء سير الليل وفائدة ذكره الإشارة بتنكيره إلى تقليل مدته (من المسجد الحرام إلى المسجد الأقصى) بيت المقدس لبعده منه (الذي باركنا حوله) بالثمار والأنهار (لنريه من آياتنا) عجائب قدرتنا (إنه هو السميع البصير) أي العالم بأقوال النبي صلى الله عليه وسلم وأفعاله فأنعم عليه بالإسراء المشتمل على اجتماعه بالأنبياء وعروجه إلى السماء، ورؤية عجائب الملكوت، ومناجاته له تعالى، فإنه صلى الله عليه وسلم قال: "" أتيت بالبراق وهو دابة أبيض فوق الحمار ودون البغل يضع حافره عند منتهى طرفه فركبته فسار بي حتى أتيت بيت المقدس، فربطت الدابة بالحلقة التي تربط فيها الأنبياء، ثم دخلت فصليت فيه ركعتين، ثم خرجت فجاءني جبريل بإناء من خمر وإناء من لبن فاخترت اللبن، قال جبريل: أصبت الفطرة، قال: ثم عرج بي إلى السماء الدنيا، فاستفتح جبريل قيل: من أنت قال: جبريل، قيل: ومن معك؟ قال: محمد قيل: أو قد أرسل إليه؟ قال: قد أرسل إليه، ففتح لنا فإذا أنا بآدم فرحب بي ودعا لي بالخير، ثم عرج بي إلى السماء الثانية فاستفتح جبريل فقيل: من أنت فقال: جبريل قيل: ومن معك قال: محمد قيل أو قد بعث إليه قال: قد بعث إليه ففتح لنا فإذا بابني الخالة يحيى وعيسى فرحبا بي ودعوا لي بالخير ثم عرج بنا إلى السماء الثالثة فاستفتح جبريل فقيل: من أنت؟ قال: جبريل فقيل: ومن معك قال: محمد فقيل: أو قد أرسل إليه قال: قد أرسل إليه ففتح لنا فإذا أنا بيوسف وإذا هو قد أعطي شطر الحسن فرحب بي ودعا لي بخير ثم عرج بنا إلى السماء الرابعة فاستفتح جبريل فقيل: من أنت قال جبريل فقيل: ومن معك قال: محمد فقيل: أو قد بعث إليه قال: قد بعث إليه ففتح لنا فإذا أنا بإدريس فرحب بي ودعا لي بخير ثم عرج بنا إلى السماء الخامسة فاستفتح جبريل فقيل: من أنت قال: جبريل فقيل: ومن معك قال: محمد فقيل: أو قد بعث إليه قال: قد بعث إليه ففتح لنا فإذا أنا بهارون فرحب بي ودعا لي بخير ثم عرج بنا إلى السماء السادسة فاستفتح جبريل فقيل: من أنت فقال: جبريل فقيل: ومن معك قال: محمد فقيل: أو قد بعث إليه قال: قد بعث إليه ففتح لنا فإذا أنا بموسى فرحب بي ودعا لي بخير ثم عرج بنا إلى السماء السابعة فاستفتح جبريل فقيل: من أنت فقال: جبريل قيل ومن معك فقال: محمد قيل: أو قد بعث إليه قال: قد بعث إليه ففتح لنا فإذا أنا بإبراهيم فإذا هو مستند إلى البيت المعمور وإذا هو يدخله كل يوم سبعون ألف ملك ثم لا يعودون إليه; ثم ذهب إلى سدرة المنتهى فإذا أوراقها كآذان الفيلة وإذا ثمرها كالقلال فلما غشيها من أمر الله ما غشيها تغيرت فما أحد من خلق الله تعالى يستطيع أن يصفها من حسنها قال: فأوحى الله إلي ما أوحى وفرض علي في كل يوم وليلة خمسين صلاة فنزلت حتى انتهيت إلى موسى فقال: ما فرض ربك على أمتك قلت: خمسين صلاة في كل يوم وليلة قال: ارجع إلى ربك فاسأله التخفيف لأمتك فإن أمتك لا تطيق ذلك وإني قد بلوت بني إسرائيل وخبرتهم قال: فرجعت إلى ربي فقلت: أي رب خفف عن أمتي فحط عني خمسا فرجعت إلى موسى قال: ما فعلت فقلت قد حط عني خمسا قال: إن أمتك لا تطيق ذلك فارجع إلى ربك فاسأله التخفيف لأمتك قال: فلم أزل أرجع بين ربي وبين موسى ويحط عني خمسا خمسا حتى قال: يا محمد هي خمس صلوات في كل يوم وليلة بكل صلاة عشر فتلك خمسون صلاة ومن هم بحسنة فلم يعملها كتبت له حسنة فإن عملها كتبت له عشرا ومن هم بسيئة ولم يعملها لم تكتب فإن عملها كتبت له سيئة واحدة فنزلت حتى انتهيت إلى موسى فأخبرته فقال ارجع إلى ربك فاسأله التخفيف فإن أمتك لا تطيق ذلك فقلت:"قد رجعت إلى ربي حتى استحييت" رواه الشيخان واللفظ لمسلم وروى الحاكم في المستدرك عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم"رأيت ربي عز وجل".'),
(2031, 17, 2, 'قال تعالى «وآتينا موسى الكتاب» التوراة «وجعلناه هدى لبني إسرائيل» لـ «أ» ن «لا يتخذوا من دوني وكيلاً» يفوضون إليه أمرهم وفي قراءة تتخذوا بالفوقانية التفاتا فأن زائدة والقول مضمر.'),
(2032, 17, 3, 'يا «ذرية من حملنا مع نوح» في السفينة «إنه كان عبدا شكورا» كثير الشكر لنا حامدا في جميع أحواله.'),
(2033, 17, 4, '«وقضينا» أوحينا «إلى بني إسرائيل في الكتاب» التوراة «لتفسدن في الأرض» أرض الشام بالمعاصي «مرتين ولتعلُن علوا كبيرا» تبغون بغيا عظيما.'),
(2034, 17, 5, '«فإذا جاء وعد أولاهما» أولى مَرَّتي الفساد «بعثنا عليكم عبادا لنا أولي بأس شديد» أصحاب قوة في الحرب والبطش «فجاسوا» ترددوا لطلبكم «خلال الديار» وسط دياركم ليقتلوكم ويسبوكم «وكان وعدا مفعولاً» وقد أفسدوا الأولى بقتل زكريا فبعث عليهم جالوت وجنوده فقتلوهم وسبوا أولادهم وضربوا بين المقدس.'),
(2035, 17, 6, '«ثم رددنا لكم الكرة» الدولة والغلبة «عليهم» بعد مائة سنة بقتل جالوت «وأمددناكم بأموال وبنين وجعلناكم أكثر نفيرا» عشيرة.'),
(2036, 17, 7, 'وقلنا «إن أحسنتم» بالطاعة «أحسنتم لأنفسكم» لأن ثوابه لها «وإن أسأتم» بالفساد «فلها» إساءتكم «فإذا جاء وعد» المرة «الآخرة» بعثناهم «ليسوءوا وجوهكم» يحزنوكم بالقتل والسبي حزنا يظهر في وجوهكم «وليدخلوا المسجد» بيت المقدس فيخربوه «كما دخلوه» وخربوه «أول مرة وليتبروا» يهلكوا «ما علوْا» غلبوا عليه «تتبيرا» هلاكا وقد أفسدوا ثانيا بقتل يحيى فبعث عليهم بختنصر فقتل منهم ألوفا وسبى ذريتهم وخرب بيت المقدس.'),
(2037, 17, 8, 'وقلنا في الكتاب «عسى ربكم أن يرحمكم» بعد المرة الثانية إن تبتم «وإن عدتم» إلى الفساد «عدنا» إلى العقوبة وقد عادوا بتكذيب محمد صلى الله عليه وسلم فسلط عليهم بقتل قريظة ونفي النضير وضرب الجزية عليهم «وجعلنا جهنم للكافرين حصيرا» محبسا وسجنا.'),
(2038, 17, 9, '«إن هذا القرآن يهدي للتي» أي للطريقة التي «هي أقوم» أعدل وأصوب «ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا كبيرا».'),
(2039, 17, 10, '«و» يخبر «أن الذين لا يؤمنون بالآخرة أعتدنا» أعددنا «لهم عذابا أليما» مؤلما هو النار.'),
(2040, 17, 11, '«ويَدْعُ الإنسان بالشر» على نفسه وأهله إذا ضجر «دعاءه» أي كدعائه له «بالخير وكان الإنسان» الجنس «عجولا» بالدعاء على نفسه وعدم النظر في عاقبته.'),
(2041, 17, 12, '«وجعلنا الليل والنهار آيتين» دالتين على قدرتنا «فمحونا آية الليل» طمسنا نورها بالظلام لتسكنوا فيه والإضافة للبيان «وجعلنا آية النهار مبصرة» أي مبصرا فيها بالضوء «لتبتغوا» فيه «فضلاً من ربكم» بالكسب «ولتعلموا» بهما «عدد السنين والحساب» للأوقات «وكل شيء» يحتاج إليه «فصلناه تفصيلاً» بيناه تبيينا.'),
(2042, 17, 13, '«وكل إنسان ألزمناه طائره» عمله يحمله «في عنقه» خص بالذكر لأن اللزوم فيه أشد وقال مجاهد: ما من مولد يولد إلا وفي عنقه ورقة مكتوب فيها شقي أو سعيد «ونخرج له يوم القيامة كتابا» مكتوبا فيه عمله «يلقاه منشورا» صفتان لكتابا.'),
(2043, 17, 14, 'ويقال له «اقرأ كتابك كفى بنفسك اليوم عليك حسيبا» محاسبا.'),
(2044, 17, 15, '«من اهتدى فإنما يهتدي لنفسه» لأن ثواب اهتدائه له «ومن ضل فإنما يضل عليها» لأن إثمه عليها «ولا تزر» نفس «وازرة» آثمة أي لا تحمل «وزر» نفس «أخرى وما كنا معذبين» أحدا «حتى نبعث رسولاً» يبين له ما يجب عليه.'),
(2045, 17, 16, '«وإذا أردنا أن نهلك قرية أمرنا مترفيها» منعميها بمعنى رؤسائها بالطاعة على لسان رسلنا «ففسقوا فيها» فخرجوا عن أمرنا «فحق عليها القول» بالعذاب «فدمرناها تدميرا» أهلكناها بإهلاك أهلها وتخريبها.'),
(2046, 17, 17, '«وكم» أي كثيرا «أهلكنا من القرون» الأمم «من بعد نوح وكفى بربك بذنوب عباده خبيرا بصيرا» عالما ببواطنها وظواهرها وبه يتعلق بذنوب.'),
(2047, 17, 18, '«من كان يريد» بعمله «العاجلة» أي الدنيا «عجلنا له فيها ما نشاء لمن نريد» التعجيل له بدل من له بإعادة الجار «ثم جعلنا له» في الآخرة «جهنم يصلاها» يدخلها «مذموما» ملوما «مدحورا» مطرودا عن الرحمة.'),
(2048, 17, 19, '«ومن أراد الآخرة وسعى لها سعيها» عمل عملها اللائق بها «وهو مؤمن» حال «فأولئك كان سعيهم مشكورا» عند الله أي مقبولاً مثابا عليه.'),
(2049, 17, 20, '«كلا» من الفريقين «نمد» نعطي «هؤلاء وهؤلاء» بدل «من» متعلق بنمد «عطاء ربك» في الدنيا «وما كان عطاء ربك» فيها «محظورا» ممنوعا عن أحد.'),
(2050, 17, 21, '«انظر كيف فضلنا بعضهم على بعض» في الرزق والجاه «وللآخرة أكبر» أعظم «درجات وأكبر تفضيلاً» من الدنيا فينبغي الاعتناء بها دونها.'),
(2051, 17, 22, '«لا تجعل مع الله إلها آخر فتقعد مذموما مخذولاً» لا ناصر لك.'),
(2052, 17, 23, '«وقضى» أمر «ربك أ» ن أي بأن «لا تعبدوا إلا إياه و» أن تحسنوا «بالوالدين إحسانا» بأن تبروهما «إما يبلغن عندك الكبر أحدهما» فاعل «أو كلاهما» وفي قراءة يَبْلُغان فأحدهما بدل من ألفه «فلا تقل لهما أف» بفتح الفاء وكسرها منونا وغير منون مصدر بمعنى تبا وقبحا «ولا تنهرهما» تزجرهما «وقل لهما قولاً كريما» جميلاً لينا.'),
(2053, 17, 24, '«واخفض لهما جناح الذل» ألن لهما جانبك الذليل «من الرحمة» أي لرقتك عليهما «وقل رب ارحمهما كما» رحماني حين «ربياني صغيرا».'),
(2054, 17, 25, '«ربكم أعلم بما في نفوسكم» من إضمار البر والعقوق «إن تكونوا صالحين» طائعين لله «فإنه كان للأوابين» الرجّاعين إلى طاعته «غفورا» لما صدر منهم في حق الوالدين من بادرة وهم لا يضمرون عقوقا.'),
(2055, 17, 26, '«وآت» أعط «ذا القربى» القرابة «حقه» من البر والصلة «والمسكين وابن السبيل ولا تبذر تبذيرا» بالإنفاق في غير طاعة الله.'),
(2056, 17, 27, '«إن المبذرين كانوا إخوان الشياطين» أي على طريقتهم «وكان الشيطان لربه كفورا» شديد الكفر لنعمه فكذلك أخوه المبذر.'),
(2057, 17, 28, '«وإما تعرضن عنهم» أي المذكورين من ذي القربى وما بعدهم فلم تعطهم «ابتغاء رحمة من ربك ترجوها» أي لطلب رزق تنتظره يأتيك تعطيهم منه «فقل لهم قولاً ميسورا» لينا سهلاً بأن تعدهم بالإعطاء عند مجيء الرزق.'),
(2058, 17, 29, '«ولا تجعل يدك مغلولة إلى عنقك» أي لا تمسكها عن الإنفاق كل المسك «ولا تبسطها» في الإنفاق «كل البسط فتقعد ملوما» راجع للأول «محسورا» منقطعا لا شيء عندك راجع للثاني.'),
(2059, 17, 30, '«إن ربك يبسط الرزق» يوسعه «لمن يشاء ويقدر» يضيقه لمن يشاء «إنه كان بعباده خبيرا بصيرا» عالما ببواطنهم وظواهرهم فيرزقهم على حسب مصالحهم.'),
(2060, 17, 31, '«ولا تقتلوا أولادكم» بالوأد «خشية» مخافة «إملاق» فقر «نحن نرزقهم وإياكم إن قتلهم كان خطأ» إثما «كبيرا» عظيما.'),
(2061, 17, 32, '«ولا تقربوا الزنى» أبلغ من لا تأتوه «إنه كان فاحشة» قبيحا «وساء» بئس «سبيلاً» طريقا هو.'),
(2062, 17, 33, '«ولا تقتلوا النفس التي حرم الله إلا بالحق ومن قُتل مظلوما فقد جعلنا لوليه» لوارثه «سلطانا» تسلطا على القاتل «فلا يسرف» يتجاوز الحد «في القتل» بأن يقتل غير قاتله أو بغير ما قتل به «إنه كان منصورا».'),
(2063, 17, 34, '«ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده وأوفوا بالعهد» إذا عاهدتم الله أو الناس «إن العهد كان مسؤولا» عنه.'),
(2064, 17, 35, '«وأوْفوا الكيل» أتموه «إذا كلتم وزنوا بالقسطاس المستقيم» الميزان السوي «ذلك خير وأحسن تأويلاً» مآلاً.'),
(2065, 17, 36, '«ولا تقفُ» تتبع «ما ليس لك به علم إن السمع والبصر والفؤاد» القلب «كل أولئك كان عنه مسؤولاً» صاحبه ماذا فعل به.'),
(2066, 17, 37, '«ولا تمش في الأرض مرحا» أي ذا مرح بالكبر والخيلاء «إنك لن تخرق الأرض» تثقبها حتى تبلغ آخرها بكبرك «ولن تبلغ الجبال طولاً» المعنى أنك لا تبلغ هذا المبلغ فكيف تختال.'),
(2067, 17, 38, '«كل ذلك» المذكور «كان سيِّئُهُ عند ربك مكروها».'),
(2068, 17, 39, '«ذلك مما أوحى إليك» يا محمد «ربك من الحكمة» الموعظة «ولا تجعل مع الله إلها آخر فتلقى في جهنم ملوما مدحورا» مطرودا من رحمة الله.'),
(2069, 17, 40, '«أفأصفاكم» أخلصكم يا أهل مكة «ربكم بالبنين واتخذ من الملائكة إناثا» بنات لنفسه بزعمكم «إنكم لتقولون» بذلك «قولاً عظيما».'),
(2070, 17, 41, '«ولقد صرفنا» بينا «في هذا القرآن» من الأمثال والوعد والوعيد «ليذكروا» يتعظوا «وما يزيدهم» ذلك «إلا نفروا» عن الحق.'),
(2071, 17, 42, '«قل» لهم «لو كان معه» أي الله «آلهة كما يقولون إذا لابتغوْا» طلبوا «إلى ذي العرش» أي الله «سبيلاً» ليقاتلوه.'),
(2072, 17, 43, '«سبحانه» تنزيها له «وتعالى عما يقولون» من الشركاء «علوا كبيرا».'),
(2073, 17, 44, '«تسبح له» تنزهه «السماوات السبع والأرض ومن فيهن وإن» ما «من شيء» من المخلوقات «إلا يسبح» متلبسا «بحمده» أي يقول سبحان الله وبحمده «ولكن لا تفقهون» تفهمون «تسبيحهم» لأنه ليس بلغتكم «إنه كان حليما غفورا» حيث لم يعاجلكم بالعقوبة.'),
(2074, 17, 45, '«وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا» أي ساترا لك عنهم فلا يرونك، نزل فيمن أراد الفتك به صلى الله عليه وسلم'),
(2075, 17, 46, '«وجعلنا على قلوبهم أكنة» أغطية «أن يفقهوه» من أن يفهموا القرآن أي فلا يفهمونه «وفي آذانهم وقرا» ثقلاً فلا يسمعونه «وإذا ذكرت ربك في القرآن وحده ولَّوا على أدبارهم نفورا» عنه.'),
(2076, 17, 47, '«نحن أعلم بما يستمعون به» بسببه من الهزء «إذ يستمعون إليك» قراءتك «وإذ هم نجوى» يتناجون بينهم أي يتحدثون «إذ» بدل من إذ قبله «يقول الظالمون» في تناجيهم «إن» ما «تتبعون إلا رجلاً مسحورا» مخدوعا مغلوبا على عقله. قال تعالى:'),
(2077, 17, 48, '«انظر كيف ضربوا لك الأمثال» بالمسحور والكاهن والشاعر «فضَلُّوا» بذلك عن الهدى «فلا يستطيعون سبيلاً» طريقا إليه «وقالوا» منكرين للبعث:'),
(2078, 17, 49, '«أئذا كنا عظاما ورفاتا أئنا لمبعوثون خلقا جديدا».'),
(2079, 17, 50, '«قل» لهم «كونوا حجارة أو حديدا».'),
(2080, 17, 51, '«أو خلقا مما يكبر في صدوركم» يعظم عن قبول الحياة فضلا عن العظام والرفات فلا بد من إيجاد الروح فيكم «فسيقولون من يعيدنا» إلى الحياة «قل الذي فطركم» خلقكم «أول مرة» ولم تكونوا شيئا لأن القادر على البدء قادر على الإعادة بل هي أهون «فسينغضون» يحركون «إليك رءوسهم» تعجبا «ويقولون» استهزاء «متى هو» أي البعث «قل عسى أن يكون قريبا».'),
(2081, 17, 52, '«يوم يدعوكم» يناديكم من القبور على لسان إسرافيل «فتستجيبون» فتجيبون دعوته من القبور «بحمده» بأمره وقيل وله الحمد «وتظنون إن» ما «لبثتم» في الدنيا «إلا قليلا» لهول ما ترون.'),
(2082, 17, 53, '«وقل لعبادي» المؤمنين «يقولوا» للكفار الكلمة «التي هي أحسن إن الشيطان ينزغ» يفسد «بينهم إن الشيطان كان للإنسان عدوا مبينا» بين العداوة والكلمة التي هي أحسن هي:'),
(2083, 17, 54, '«ربكم أعلم بكم إن يشأ يرحمكم» بالتوبة والإيمان «أو إن يشأ» تعذيبكم «يعذبكم» بالموت على الكفر «وما أرسلناك عليهم وكيلاً» فتجبرهم على الإيمان وهذا قبل الأمر بالقتال.'),
(2084, 17, 55, '«وربك أعلم بمن في السماوات والأرض» فيخصهم بما شاء على قدر أحوالهم «ولقد فضلنا بعض النبيين على بعض» بتخصيص كل منهم بفضيلة كموسى بالكلام وإبراهيم بالخلة ومحمد بالإسراء «وآتينا داود زبورا».'),
(2085, 17, 56, '«قل» لهم «ادعوا الذين زعمتم» أنهم آلهة «من دونه» كالملائكة وعيسى وعزير «فلا يملكون كشف الضر عنكم ولا تحويلا» له إلى غيركم.'),
(2086, 17, 57, '«أولئك الذين يدعونـ» ـهم آلهة «يبتغون» يطلبون «إلى ربهم الوسيلة» القربة بالطاعة «أيهم» بدل من واو يبتغون أي يبتغيها الذي هو «أقرب» إليه فكيف بغيره «ويرجون رحمته ويخافون عذابه» كغيرهم فكيف تدعونهم آلهة «إن عذاب ربك كان محذوراً».'),
(2087, 17, 58, '«وإن» ما «من قرية» أريد أهلها «إلا نحن مهلكوها قبل يوم القيامة» بالموت «أو معذبوها عذابا شديدا» بالقتل وغيره «كان ذلك في الكتاب» اللوح المحفوظ «مسطورا» مكتوبا.'),
(2088, 17, 59, '«وما منعنا أن نرسل بالآيات» التي اقترحها أهل مكة «إلا أن كذب بها الأولون» لما أرسلناها فأهلكناهم ولو أرسلناها إلى هؤلاء لكذبوا بها واستحثوا الإهلاك وقد حكمنا بإمهالهم لإتمام أمر محمد صلى الله عليه وسلم «وآتينا ثمود الناقة» أي «مبصرة» بينة واضحة «فظلموا» كفروا «بها» فأهلكوا «وما نرسل بالآيات» المعجزات «إلا تخويفا» للعباد فيؤمنوا.'),
(2089, 17, 60, '«و» اذكر «إذ قلنا لك إن ربك أحاط بالناس» علما وقدرة فهم في قبضته فبلّغهم ولا تخف أحدا فهو يعصمك منهم «وما جعلنا الرؤيا التي أريناك» عيانا ليلة الإسراء «إلا فتنة للناس» أهل مكة إذ كذبوا بها وارتد بعضهم لما أخبرهم بها «والشجرة الملعونة في القرآن» هي الزقوم التي تنبت في أصل الجحيم جعلناها فتنة لهم إذ قالوا: النار تحرق الشجر فكيف تنبته «ونخوفهم» بها «فما يزيدهم» تخويفنا «إلا طغيانا كبيرا».'),
(2090, 17, 61, '«و» اذكر «إذ قلنا للملائكة اسجدوا لآدم» سجود تحية بالانحناء «فسجدوا إلا إبليس قال أأسجد لمن خلقت طينا» نصب بنزع الخافض أي من طين.'),
(2091, 17, 62, '(قال أرأيتك) أي أخبرني (هذا الذي كرمت) فضلت (علي) بالأمر بالسجود له "" وأنا خير منه خلقتني من نار "" (لئن) لام قسم (أخرتن إلى يوم القيامة لأحتنكن) لأستأصلن (ذريته) بالإغواء (إلا قليلاً) منهم ممن عصمته.'),
(2092, 17, 63, '«قال» تعالى له «اذهب» منظرا إلى وقت النفخة الأولى «فمن تبعك منهم فإن جهنم جزاؤكم» أنت وهم «جزاءً موفورا» وافرا كاملاً.'),
(2093, 17, 64, '«واستفزز» استخف «من استطعت منهم بصوتك» بدعائك بالغناء والمزامير وكل داع إلى المعصية «وأجلب» صحْ «عليهم بخيلك ورجلك» وهم الركاب والمشاة في المعاصي «وشاركهم في الأموال» المحرمة كالربا والغصب «والأولاد» من الزنى «وعدهم» بأن لا بعث ولا جزاء «وما يعدهم الشيطان» بذلك «إلا غرورا» باطلاً.'),
(2094, 17, 65, '«إن عبادي» المؤمنين «ليس لك عليهم سلطان» تسلط وقوة «وكفى بربك وكيلا» حافظا لهم منك.'),
(2095, 17, 66, '«ربكم الذي يزجي» يجري «لكم الفلك» السفن «في البحر لتبتغوا» تطلبوا «من فضله» تعالى بالتجارة «إنه كان بكم رحيما» في تسخيرها لكم.'),
(2096, 17, 67, '«وإذا مسكم الضر» الشدة «في البحر» خوف الغرق «ضل» غاب عنكم «من تدعون» تعبدون من الآلهة فلا تدعونه «إلا إياه» تعالى فإنكم تدعونه وحده لأنكم في شدة لا يكشفها إلا هو «فلما نجاكم» من الغرق وأوصلكم «إلى البر أعرضتم» عن التوحيد «وكان الإنسان كفورا» جحودا للنعم.'),
(2097, 17, 68, '«أفأمنتم أن يخسف بكم جانب البر» أي الأرض كقارون «أو يرسل عليكم حاصبا» أي يرميكم بالحصباء كقوم لوط «ثم لا تجدوا لكم وكيلاً» حافظا منه.'),
(2098, 17, 69, '«أم أمنتم أن نعيدكم فيه» أي البحر «تارة» مرة «أخرى فنرسل عليكم قاصفا من الريح» أي ريحا شديدة لا تمر بشيء إلا قصفته فتكسر فلككم «فتغرقكم بما كفرتم» بكفركم «ثم لا تجدوا لكم علينا به تبيعا» ناصرا وتابعا يطالبنا بما فعلنا بكم.'),
(2099, 17, 70, '«ولقد كرمنا» فضلنا «بني آدم» بالعلم والنطق واعتدال الخلق وغير ذلك ومنه طهارتهم بعد الموت «وحملناهم في البر» على الدواب «والبحر» على السفن «ورزقناهم من الطيبات وفضلناهم على كثير ممن خلقنا» كالبهائم والوحوش «تفضيلاً» فمن بمعنى ما أو على بابها وتشمل الملائكة والمراد تفضيل الجنس، ولا يلزم تفضيل أفراده إذ هم أفضل من البشر غير الأنبياء.'),
(2100, 17, 71, 'اذكر «يوم ندعو كل أناس بإمامهم» نبيهم فيقال يا أمة فلان أو بكتاب أعمالهم فيقال يا صاحب الشر وهو يوم القيامة «فمن أوتي» منهم «كتابه بيمينه» وهم السعداء أولو البصائر في الدنيا «فأولئك يقرءُون كتابهم ولا يُظلمون» ينقصون من أعمالهم «فتيلاً» قدر قشرة النواة.'),
(2101, 17, 72, '«ومن كان في هذه» أي الدنيا «أعمى» عن الحق «فهو في الآخرة أعمى» عن طريق النجاة وقراءة القرآن «وأضل سبيلاً» أبعد طريقا عنه. ونزل في ثقيف وقد سألوا صلى الله عليه وسلم أن يحرم واديهم وألحوا عليه.'),
(2102, 17, 73, '«وإن» مخففة «كادوا» قاربوا «ليفتنونك» ليستنزلون «عن الذي أوحينا إليك لتفتري علينا غيره وإذا» لو فعلت ذلك «لاتخذوك خليلاً».'),
(2103, 17, 74, '«ولولا أن ثبتناك» على الحق بالعصمة «لقد كدت» قاربت «تركن» تميل «إليهم شيئا» ركونا «قليلاً» لشدة احتيالهم وإلحاحهم، وهو صريح في أنه صلى الله عليه وسلم لم يركن ولا قارب.'),
(2104, 17, 75, '«إذاً» لو ركنت «لأذقناك ضعف» عذاب «الحياة وضعف» عذاب «الممات» أي مثلي ما يعذب غيرك في الدنيا والآخرة «ثم لا تجد لك علينا نصيرا» مانعا منه.'),
(2105, 17, 76, 'ونزل لما قال له اليهود: إن كنت نبيا فالحق بالشام فإنها أرض الأنبياء «وإن» مخففة «كادوا ليستفزونك من الأرض» أرض المدينة «ليخرجوك منها وإذا» لو أخرجوك «لا يلبثون خلافك» فيها «إلا قليلاً» ثم يهلكون.'),
(2106, 17, 77, '«سنّة من قد أرسلنا قبلك من رسلنا» أي كسنتنا فيهم من إهلاك من أخرجهم «ولا تجد لسنتنا تحويلاً» تبديلاً.'),
(2107, 17, 78, '«أقم الصلاة لدلوك الشمس» أي من وقت زوالها «إلى غسق الليل» إقبال ظلمته أي الظهر والعصر والمغرب والعشاء «وقرآن الفجر» صلاة الصبح «إن قرآن الفجر كان مشهودا» تشهده ملائكة الليل وملائكة النهار.'),
(2108, 17, 79, '«ومن الليل فتهجد» فصلِّ «به» بالقرآن «نافلة لك» فريضة زائدة لك دون أمتك أو فضيلة على الصلوات المفروضة «عسى أن يبعثك» يقيمك «ربك» في الآخرة «مقاما محمودا» يحمدك فيه الأولون والآخرون وهو مقام الشفاعة في فصل القضاء، ونزل لما أمر بالهجرة.'),
(2109, 17, 80, '«وقل ربِّ أدخلني» المدينة «مُدخل صدق» إدخالاً مرضيا لا أرى فيه ما أكره «وأخرجني» من مكة «مُخرج صدق» إخراجا لا ألتفت بقلبي إليها «واجعل لي من لدنك سلطانا نصيرا» قوة تنصرني بها على أعدائك.'),
(2110, 17, 81, '(وقل) عند دخولك مكة (جاء الحق) الإسلام (وزهق الباطل) بطل الكفر (إن الباطل كان زهوقا) مضمحلاً زائلاً "" وقد دخلها صلى الله عليه وسلم وحول البيت ثلاثمائة وستون صنما فجعل يطعنها بعود في يده ويقول ذلك حتى سقطت "" رواه الشيخان.'),
(2111, 17, 82, '«وننزل من» للبيان «القرآن ما هو شفاء» من الضلالة «ورحمة للمؤمنين» به «ولا يزيد الظالمين» الكافرين «إلا خسارا» لكفرهم به.'),
(2112, 17, 83, '«وإذا أنعمنا على الإنسان» الكافر «أعرض» عن الشكر «ونأى بجانبه» ثنى عطفه متبخترا «وإذا مسه الشر» الفقر والشدة «كان يؤوسا» قنوطا من رحمة الله.'),
(2113, 17, 84, '«قل كل» منا ومنكم «يعمل على شاكلته» طريقته «فربكم أعلم بمن هو أهدى سبيلاً» طريقا فيثيبه.'),
(2114, 17, 85, '«ويسألونك» أي اليهود «عن الروح» الذي يحيا به البدن «قل» لهم «الروح من أمر ربي» أي علمه لا تعلمونه «وما أويتم من العلم إلا قليلاً» بالنسبة إلى علمه تعالى.'),
(2115, 17, 86, '«ولئن» لام قسم «شئنا لنذهبن بالذي أوحينا إليك» أي القرآن بأن نمحوه من الصدور والمصاحف «ثم لا تجد لك به علينا وكيلاً».'),
(2116, 17, 87, '«إلا» لكن أبقيناه «رحمة من ربك إن فضله كان عليك كبيرا» عظيما حيث أنزله عليك وأعطاك المقام المحمود وغير ذلك من الفضائل.'),
(2117, 17, 88, '(قل لئن اجتمعت الإنس والجن على أن يأتوا بمثل هذا القرآن) في الفصاحة والبلاغة. (لا يأتون بمثله ولو كان بعضهم لبعض ظهيرا) معينا نزل ردا لقولهم "" ولو نشاء لقلنا مثل هذا "".'),
(2118, 17, 89, '«ولقد صرفنا» بينا «للناس في هذا القرآن من كل مثل» صفة لمحذوف أي مثلاً من جنس كل مثل ليتعظوا «فأبى أكثر الناس» أي أهل مكة «إلا كفورا» جحودا للحق.'),
(2119, 17, 90, '«وقالوا» عطف على أبى «لن نؤمن لك حتى تفجر لنا من الأرض ينبوعا» عينا ينبع منها الماء.'),
(2120, 17, 91, '«أو تكون لك جنة» بستان «من نخيل وعنب فتفجر الأنهار خلالها» وسطها «تفجيرا».'),
(2121, 17, 92, '«أو تسقط السماء كما زعمت علينا كسفا» قطعا «أو تأتي بالله والملائكة قبيلاً» مقابلة وعيانا فنراهم.'),
(2122, 17, 93, '«أو يكون لك بيت من زخرف» ذهب «أو ترقى» تصعد «في السماء» بسُلَّم «ولن نؤمن لرقيك» لو رقيت فيها «حتى تنزل علينا» منها «كتابا» فيه تصديقك «نقرؤه قل» لهم «سبحان ربي» تعجب «هل» ما «كنت إلا بشرا رسولاً» كسائر الرسل ولم يكونوا يأتون بآية إلا بإذن الله.'),
(2123, 17, 94, '«وما منع الناس أن يؤمنوا إذ جاءهم الهدى إلا أن قالوا» أي قولهم منكرين «أبعث الله بشرا رسولاً» ولم يبعث ملكا.'),
(2124, 17, 95, '«قل» لهم «لو كان في الأرض» بدل البشر «ملائكة يمشون مطمئنين لنزّلنا عليهم من السماء ملكا رسولاً» إذ لا يرسل إلى قوم رسول إلا من جنسهم ليمكنهم مخاطبته والفهم عنه.'),
(2125, 17, 96, '«قل كفى بالله شهيدا بيني وبينكم» على صدقي «إنه كان بعباده خبيرا بصيرا» عالما ببواطنهم وظواهرهم.'),
(2126, 17, 97, '«ومن يهد اللهُ فهو المهتد ومن يضلل فلن تجد لهم أولياء» يهدونهم «من دونه ونحشرهم يوم القيامة» ماشين «على وجوههم عميا وبكما وصمّا مأواهم جهنم كلما خبت» سكن لهبها «زدناهم سعيرا» تلهبا واشتعالاً.'),
(2127, 17, 98, '«ذلك جزاؤهم بأنهم كفروا بآياتنا وقالوا» منكرين للبعث «أئذا كنا عظاما ورفاتا أئنا لمبعوثون خلقا جديدا».'),
(2128, 17, 99, '«أولم يرَوْا» يعلموا «أن الله الذي خلق السماوات والأرض» مع عظمهما «قادر على أن يخلق مثلهم» أي الأناسي في الصغر «وجعل لهم أجلاً» للموت والبعث «لا ريب فيه فأبى الظالمون إلا كفورا» جحودا له.'),
(2129, 17, 100, '«قل» لهم «لو أنتم تملكون خزائن رحمة ربي» من الرزق والمطر «إذا لأمسكتم» لبخلتم «خشية الإنفاق» خوف نفادها بالإنفاق فتقتروا «وكان الإنسان قتورا» بخيلاً.'),
(2130, 17, 101, '«ولقد آتينا موسى تسع آيات بينات» وهي اليد والعصا والطوفان والجراد والقمل والضفادع والدم أو الطمس ونقص الثمرات «فاسأل» يا محمد «بني إسرائيل» عنه سؤال تقرير للمشركين على صدقك، أو فقلنا له: اسأل وفي قراءة بلفظ الماضي «إذ جاءهم فقال له فرعون إني لأظنك يا موسى مسحورا» مخدوعا مغلوبا على عقلك.'),
(2131, 17, 102, '«قال لقد علمت ما أنزل هؤلاء» الآيات «إلا رب السماوات والأرض بصائر» عبرا، ولكنك تعاند وفي قراءة بضم التاء «وإني لأظنك يا فرعون مثبورا» هالكا أو مصروفا عن الخير.'),
(2132, 17, 103, '«فأراد» فرعون «أن يستفزهم» يخرج موسى وقومه «من الأرض» أرض مصر «فأغرقناه ومن معه جميعا».'),
(2133, 17, 104, '«وقلنا من بعده لبني إسرائيل اسكنوا الأرض فإذا جاء وعد الآخرة» أي الساعة «جئنا بكم لفيفا» جميعا أنتم وهم.'),
(2134, 17, 105, '«وبالحق أنزلناه» أي القرآن «وبالحق» المشتمل عليه «نزل» كما أنزل لم يعتره تبديل «وما أرسلناك» يا محمد «إلا مبشرا» من آمن بالجنة «ونذيرا» من كفر بالنار.'),
(2135, 17, 106, '«وقرآنا» منصوب بفعل يفسره «فرقناه» نزلناه مفرقا في عشرين سنة أو ثلاث «لتقرأه على الناس على مكث» مهل وتؤدة ليفهموه «ونزلناه تنزيلا» شيئا بعد شيء على حسب المصالح.'),
(2136, 17, 107, '«قل» لكفار مكة «آمنوا به أو لا تؤمنوا» تهديد لهم «إن الذين أوتوا العلم من قبله» قبل نزوله وهم مؤمنو أهل الكتاب «إذا يتلى عليهم يخرون للأذقان سُجَّدا».'),
(2137, 17, 108, '«ويقولون سبحان ربنا» تنزيها له عن خلف الوعد «إن» مخففة «كان وعد ربنا» بنزوله وبعث النبي صلى الله عليه وسلم «لمفعولا».'),
(2138, 17, 109, '«ويخرون للأذقان يبكون» عطف بزيادة صفة «ويزيدهم» القرآن «خشوعا» تواضعا لله.'),
(2139, 17, 110, 'وكان صلى الله عليه وسلم يقول "" يا الله يا رحمن "" فقالوا: ينهانا أن نعبد إلهين وهو يدعو إلها آخر معه فنزل (قل) لهم (ادعوا الله أو ادعوا الرحمن) أي سموه بأيهما أو نادوه بأن تقولوا: يا الله يا رحمن (أيا) شرطية (ما) زائدة أي هذين (تدعوا) فهو حسن دل على هذا (فله) أي لمسماهما (الأسماء الحسنى) وهذان منها فإنها كما في الحديث "" الله لا إله إلا هو الرحمن الرحيم الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر الخالق البارئ المصور الغفار القهار الوهاب الرزاق الفتاح العليم القابض الباسط الخافض الرافع المعز المذل السميع البصير الحكم العدل اللطيف الخبير الحليم العظيم الغفور الشكور العلي الكبير الحفيظ المقيت الحسيب الجليل الكريم الرقيب المجيب الواسع الحكيم الودود المجيد الباعث الشهيد الحق الوكيل القوي المتين الولي الحميد المحصي المبدئ المعيد المحيي المميت الحي القيوم الواجد الماجد الواحد الأحد الصمد القادر المقتدر المقدم المؤخر الأول الآخر الظاهر الباطن الوالي المتعالي البر التواب المنتقم العفو الرءوف مالك الملك ذو الجلال والإكرام المقسط الجامع الغني المغني المانع الضار النافع النور الهادي البديع الباقي الوارث الرشيد الصبور" رواه الترمذي «ولا تجهر بصلاتك» بقراءتك بها فيسمعك المشركون فيسبوك ويسبوا القرآن ومن أنزله «ولا تخافت» تسر «بها» لينتفع أصحابك «وابتغ» اقصد «بين ذلك» الجهر والمخافتة «سبيلا» طريقا وسطا.'),
(2140, 17, 111, '«شريك في الملك» في الألوهية «ولم يكن له ولي» ينصره «من» أجل «الذل» أي لم يذل فيحتاج إلى ناصر «وكبره تكبيرا» عظمه عظمة تامة عن اتخاذ الولد الشريك والذل وكل ما لا يليق به وترتيب الحمد على ذلك للدلالة على أنه المستحق لجميع المحامد لكمال ذاته وتفرده في صفاته وروى الإمام أحمد في مسنده عن معاذ الجهني عن رسول الله صلى الله عليه وسلم أنه كان يقول: (آية العز الحمد لله الذي لم يتخذ ولدا ولم يكن له شريك في الملك) إلى آخر السورة والله تعالى أعلم قال مؤلفه هذا آخر ما كملت به تفسير القرآن الكريم الذي ألفه الشيخ الإمام العالم المحقق جلال الدين المحلي الشافعي رضي الله عنه وقد أفرغت لمكمل وعليه في الآي المتشابهة الاعتماد والمعول فرحم الله امرأ نظر بعين الإنصاف إليه ووقف فيه على خطأ فأطلعني عليه وقد قلت: حمدت الله ربي إذ هداني لما أبديت مع عجزي وضعفي فمن لي بالخطأ فأرد عنه ومن لي بالقبول ولو بحرف هذا ولم يكن قط في خلدي أن أتعرض لذلك لعلمي بالعجز عن الخوض في هذه المسالك وعسى الله أن ينفع به نفعا جما ويفتح به قلوبا غلفا وأعينا وآذانا صما وكأني بمن اعتاد المطولات وقد أضرب عن هذه التكملة وأصلها حسما وعدل إلى صريح العناد ولم يوجه إلى دقائقها فهما «ومن كان في هذه أعمى فهو في الآخرة أعمى» رزقنا الله به هداية إلى سبيل الحق وتوفيقا واطلاعا على دقائق كلماته وتحقيقا وجعلنا به «مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين وحسن أولئك رفيقا» وفرغ من تأليفه يوم الأحد عاشر شوال سنة سبعين وثمانمائة وكان الابتداء في يوم الأربعاء مستهل رمضان من السنة المذكورة وفرغ من تبييضه يوم الأربعاء سادس صفر سنة إحدى وسبعين وثمانمائة والله أعلم قال الشيخ شمس الدين محمد بن أبي بكر الخطيب الطوخي أخبرني صديقي الشيخ العلامة كمال الدين المحلي أخو شيخنا الشيخ جلال الدين المحلي رحمهما الله تعالى أنه رأى أخاه الشيخ جلال الدين المذكور في النوم وبين يديه صديقنا الشيخ العلامة المحقق جلال الدين السيوطي مصنف هذه التكملة وقد أخذ الشيخ هذه التكملة في يده وتصفحها ويقول لمصنفها المذكور أيهما أحسن وضعي أو وضعك فقال: وضعي فقال: انظر وعرض عليه مواضع فيها وكأنه يشير إلى اعتراض فيها بلطف ومصنف هذه التكملة كلما أورد عليه شيئا يجيبه والشيخ يبتسم ويضحك قال شيخنا الإمام العلامة جلال الدين عبد الرحمن بن أبي بكر السيوطي مصنف هذه التكملة: الذي أعتقده وأجزم به أن الوضع الذي وضعه الشيخ جلال الدين المحلي رحمه الله تعالى في قطعته أحسن من وضعي أنا بطبقات كثيرة كيف وغالب ما وضعته هنا مقتبس من وضعه ومستفاد منه لا مرية عندي في ذلك وأما الذي رئي في المنام المكتوب أعلاه فلعل الشيخ أشار به إلى المواضع القليلة التي خالفت وضعه فيها لنكتة وهي يسيرة جدا ما أظنها تبلغ عشرة مواضع منها أن الشيخ قال في سورة ص: والروح جسم لطيف يحيا به الإنسان بنفوذه فيه وكنت تبعته أولا فذكرت هذا الحد في سورة الحجر ثم ضربت عليه لقوله تعالى «ويسألونك عن الروح قل الروح من أمر ربي» الآية فهي صريحة أو كالصريحة في أن الروح من علم الله تعالى لا نعلمه فالإمساك عن تعريفها أولى ولذا قال الشيخ تاج الدين بن السبكي في جمع الجوامع: والروح لم يتكلم عليها محمد صلى الله عليه وسلم فنمسك عنها ومنها أن الشيخ قال في سورة الحج: الصابئون فرقة من اليهود فذكرت ذلك في سورة البقرة وزدت أو النصارى بيانا لقول ثان فإنه المعروف خصوصا عند أصحابنا الفقهاء وفي المنهاج وإن خالفت السامرة اليهود والصابئة النصارى في أصل دينهم وفي شرحه أن الشافعي رضي الله عنه نص على أن الصابئين فرقة من النصارى ولا أستحضر الآن موضعا ثالثا فكأن الشيخ رحمه الله تعالى يشير إلى مثل هذا والله أعلم بالصواب وإليه المرجع والمآب.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, '«الحمد» وهو الوصف بالجميل، ثابت «لله» تعالى وهل المراد الإعلام بذلك للإيمان به أو الثناء به أو هما؟ احتمالات، أفيدها الثالث «الذي أنزل على عبده» محمد «الكتاب» القرآن «ولم يجعل له» أي فيه «عوجا» اختلافا أو تناقضا، والجملة حال من الكتاب.'),
(2142, 18, 2, '«قيّما» مستقيما حال ثانية مؤكدة «لينذر» يخوف بالكتاب الكافرين «بأسا» عذابا «شديدا من لدنه» من قبل الله «ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا حسنا».'),
(2143, 18, 3, '«ماكثين فيه أبدا» هو الجنة.'),
(2144, 18, 4, '«وينذر» من جملة الكافرين «الذين قالوا اتخذ الله ولدا».'),
(2145, 18, 5, '«ما لهم به» بهذا القول «من علم ولا لآبائهم» من قبلهم القائلين له «كبرت» عظمت «كلمة تخرج من أفواههم» كلمة تمييز مفسر للضمير المبهم والمخصوص بالذم محذوف أي مقالتهم المذكورة «إن» ما «يقولون» في ذلك «إلا» مقولا «كذبا».'),
(2146, 18, 6, '«فلعلك باخع» مهلك «نفسك على آثارهم» بعدهم أي بعد توليهم عنك «إن لم يؤمنوا بهذا الحديث» القرآن «أسفا» غيظا وحزنا منك لحرصك على إيمانهم، ونصبه على المفعول له.'),
(2147, 18, 7, '«إنا جعلنا ما على الأرض» من الحيوان والنبات والشجر والأنهار وغير ذلك «زينة لها لنبلوهم» لنختبر الناس ناظرين إلى ذلك «أيهم أحسن عملاً» فيه أي أزهد له.'),
(2148, 18, 8, '«وإنا لجاعلون ما عليها صعيدا» فتاتا «جرزا» يابسا لا ينبت.'),
(2149, 18, 9, '«أم حسبت» أي ظننت «أن أصحاب الكهف» الغار في الجبل «والرقيم» اللوح المكتوب فيه أسماؤهم وأنسابهم وقد سئل صلى الله عليه وسلم عن قصتهم «كانوا» في قصتهم «من» جملة «آياتنا عجبا» خبر كان وما قبله حال، أي كانوا عجبا دون باقي الآيات أو أعجبها ليس الأمر كذلك.'),
(2150, 18, 10, 'اذكر «إذ أوى الفتية إلى الكهف» جمع فتى وهو الشاب الكامل، خائفين على إيمانهم من قومهم الكفار «فقالوا ربنا آتنا من لدنك» من قبلك «رحمة وهيئ» أصلح «لنا من أمرنا رشدا» هداية.'),
(2151, 18, 11, '«فضربنا على آذانهم» أي أنمناهم «في الكهف سنين عددا» معدودة.'),
(2152, 18, 12, '«ثم بعثناهم» أيقظناهم «لنعلم» علم مشاهدة «أي الحزبين» الفريقين المختلفين في مدة لبثهم «أحصى» أفعل بمعنى أضبط «لما لبثوا» لبثهم متعلق بما بعده «أمدا» غاية.'),
(2153, 18, 13, '«نحن نقص» نقرأ «عليك نبأهم بالحق» بالصدق «إنهم فتية آمنوا بربِّهم وزدناهم هدى».'),
(2154, 18, 14, '«وربطنا على قلوبهم» قويناها على قول الحق «إذ قاموا» بين يدي ملكهم وقد أمرهم بالسجود للأصنام «فقالوا ربنا رب السماوات والأرض لن ندعو من دونه» أي غيره «إلها لقد قلنا إذا شططا» أي قولاً ذا شطط أي إفراط في الكفر إن دعونا إلها غير الله فرضا.'),
(2155, 18, 15, '«هؤلاء» مبتدأ «قومنا» عطف بيان «اتخذوا من دونه آلهة لولا» هلا «يأتون عليهم» على عبادتهم «بسلطان بين» بحجة ظاهرة «فمن أظلم» أي لا أحد أظلم «ممن افترى على الله كذبا» بنسبة الشريك إليه تعالى قال بعض الفتية لبعض:'),
(2156, 18, 16, '«وإذ اعتزلتموهم وما يعبدون إلا الله فأووا إلى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من أمركم مرفقا» بكسر الميم وفتح الفاء وبالعكس ما ترتفقون به من غداء وعشاء.'),
(2157, 18, 17, '«وترى الشمس إذا طلعت تزّاور» بالتشديد والتخفيف تميل «عن كهفهم ذات اليمين» ناحيته «وإذا غربت تقرضهم ذات الشمال» تتركهم وتتجاوز عنهم فلا تصيبهم ألبتة «وهم في فجوة منه» متسع من الكهف ينالهم برد الريح ونسيمها «ذلك» المذكور «من آيات الله» دلائل قدرته «من يهد الله فهو المهتد ومن يضلل فلن تجد له وليا مرشدا».'),
(2158, 18, 18, '«وتحسبهم» لو رأيتهم «أيقاظا» أي منتبهين لأن أعينهم منفتحة، جمع يقظ بكسر القاف «وهم رقود» نيام جمع راقد «ونقلبهم ذات اليمين وذات الشمال» لئلا تأكل الأرض لحومهم «وكلبهم باسط ذراعيه» يديه «بالوصيد» بفناء الكهف وكانوا إذا انقلبوا انقلب هو مثلهم في النوم واليقظة «لو اطلعت عليهم لولَّيت منهم فرارا ولملِّئت» بالتشديد والتخفيف «منهم رعْبا» بسكون العين وضمها منعهم الله بالرعب من دخول أحد عليهم.'),
(2159, 18, 19, '«وكذلك» كما فعلنا بهم ما ذكرنا «بعثناهم» أيقظناهم «ليتساءلوا بينهم» عن حالهم ومدة لبثهم «قال قائل منهم كم لبثتم قالوا لبثنا يوما أو بعض يوم» لأنهم دخلوا الكهف عند طلوع الشمس وبُعثوا عند غروبها فظنوا أنه غروب يوم الدخول ثم «قالوا» متوقفين في ذلك «ربكم أعلم بما لبثتم فابعثوا أحدكم بوَرقِكُمْ» بسكون الراء وكسرها بفضتكم «هذه إلى المدينة» يقال إنها المسماة الآن طرسوس بفتح الراء «فلينظر أيها أزكى طعاما» أي أيّ أطعمة المدينة أحل «فليأتكم برزق منه وليتلطف ولا يشعرن بكم أحداً».'),
(2160, 18, 20, '«إنهم إن يظهروا عليكم يرجموكم» يقتلوكم بالرجم «أو يعيدوكم في ملتهم ولن تفلحوا إذا» أي إن عدتم في ملتهم «أبدا».'),
(2161, 18, 21, '«وكذلك» كما بعثناهم «أعثرنا» أطلعنا «عليهم» قومهم والمؤمنين «ليعلموا» أي قومهم «أن وعد الله» بالبعث «حق» بطريق أن القادر على إنامتهم المدة الطويلة وإبقائهم على حالهم بلا غذاء قادر على إحياء الموتى «وأن الساعة لا ريب» لا شك «فيها إذ» معمول لأعثرنا «يتنازعون» أي المؤمنون والكفار «بينهم أمرهم» أمر الفتية في البناء حولهم «فقالوا» أي الكفار «ابنوا عليهم» أي حولهم «بنيانا» يسترهم. «ربهم أعلم بهم قال الذين غلبوا على أمرهم» أمر الفتية وهم المؤمنون «لنتخذن عليهم» حولهم «مسجدا» يصلى فيه، وفعل ذلك على باب الكهف.'),
(2162, 18, 22, '«سيقولون» أي المتنازعون في عدد الفتية في زمن النبي صلى الله عليه وسلم أي يقول بعضهم هم «ثلاثة رابعهم كلبهم ويقولون» أي بعضهم «خمسة سادسهم كلبهم» والقولان لنصارى نجران «رجما بالغيب» أي ظنا في الغيبة عنهم وهو راجع إلى القولين معا ونصبه على المفعول له أي لظنهم ذلك «ويقولون» أي المؤمنون «سبعة وثامنهم كلبهم» الجملة من المبتدأ وخبره صفة سبعة بزيادة الواو، وقيل تأكيد أو دلالة على لصوق الصفة بالموصوف ووصف الأولين بالرجم دون الثالث دليل على أنه مرضي وصحيح «قل ربي أعلم بعدتهم ما يعلمهم إلا قليل» قال ابن عباس أنا من القليل وذكرهم سبعة «فلا تمار» تجادل «فيهم إلا مراءً ظاهرا» بما أنزل عليك «ولا تستفت فيهم» تطلب الفتيا «منهم» من أهل الكتاب اليهود «أحدا» وسأله أهل مكة عن خبر أهل الكهف فقال أخبركم به غدا ولم يقل إن شاء الله فنزل:'),
(2163, 18, 23, '«ولا تقولن لشيء» أي لأجل شيء «إني فاعل ذلك غدا» أي قيما يستقبل من الزمان.'),
(2164, 18, 24, '«إلا يشاء الله» أي إلا ملتبسا بمشيئة الله تعالى بأن تقول إن شاء الله «واذكر ربك» أي مشيئته معلقا بها «إذا نسيت» ويكون ذكرها بعد النسيان كذكرها مع القول الحسن وغيره ما دام في المجلس «وقل عسى أن يهدين ربي لأقرب من هذا» من خبر أهل الكهف في الدلالة على نبوتي «رشدا» هداية وقد فعل الله ذلك.'),
(2165, 18, 25, '«ولبثوا في كهفهم ثلاث مائة» بالتنوين «سنين» عطف بيان لثلاثمائة وهذه السنون الثلاثمائة عند أهل الكهف شمسية وتزيد القمرية عليها عند العرب تسع سنين وقد ذكرت في قوله «وازدادوا تسعا» أي تسع سنين فالثلاثمائة الشمسية: ثلاثمائة وتسع قمرية.'),
(2166, 18, 26, '«قل الله أعلموا بما لبثوا» ممن اختلفوا فيه وهو ما تقدم ذكره «له غيب السماوات والأرض» أي علمه «أبصرْ به» أي بالله هي صيغة تعجب «وأسمعْ» به كذلك بمعنى ما أبصرهُ وما أسمعهُ وهما على جهة المجاز والمراد أنه تعالى لا يغيب عن بصره وسمعه شيء «ما لهم» لأهل السماوات والأرض «من دونه من ولي» ناصر «ولا يشرك في حكمه أحدا» لأنه غني عن الشريك.'),
(2167, 18, 27, 'واتل ما أوحي إليك من كتاب ربك لا مبدل لكلماته ولن تجد من دونه ملتحدا» ملجأ.'),
(2168, 18, 28, '«واصبر نفسك» احبسها «مع الذين يدعون ربهم بالغداة والعشي يريدون» بعبادتهم «وجهه» تعالى لا شيئا من أعراض الدنيا وهم الفقراء «ولا تعدُ» تنصرف «عيناك عنهم» عبر بهما عن صاحبهما «تريد زينة الحياة الدنيا ولا تطع من أغفلنا قلبه عن ذكرنا» أي القرآن هو عيينة بن حصن وأصحابه «واتبعَ هواه» في الشرك «وكان أمره فرطا» إسرافا.'),
(2169, 18, 29, '«وقل» له ولأصحابه هذا القرآن «الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر» تهديد لهم «إنا أعتدنا للظالمين» أي الكافرين «نارا أحاط بهم سرادقها» ما أحاط بها «وإن يستغيثوا يغاثوا بماء كالمهل» كعكر الزيت «يشوي الوجوه» من حره إذا قرب إليها «بئس الشراب» هو «وساءت» أي النار «مرتفقا» تمييز منقول عن الفاعل أي قبح مرتفقها وهو مقابل لقوله الآتي في الجنة «وحسنت مرتفقا» وإلا فأي ارتفاق في النار.'),
(2170, 18, 30, '«إن الذين آمنوا وعملوا الصالحات إنا لا نضيع أجر من أحسن عملاً» الجملة خبر إن الذين وفيها إقامة الظاهر مقام المضمر والمعنى أجرهم أي نثيبهم بما تضمنه.'),
(2171, 18, 31, '(أولئك لهم جنات عدن) إقامة (تجري من تحتهم الأنهار يحلون فيها من أساور) قيل من زائدة وقيل للتبعيض، وهي جمع أسورة كأحمرة جمع سوار (من ذهب ويلبسون ثيابا خضرا من سندس) ما رقَّ من الديباج (وإستبرق) ما غلظ منه وفي آية الرحمن "" بطائنها من إستبرق "" (متكئين فيها على الأرائك) جمع أريكة وهي السرير في الحجلة وهي بيت يزين بالثياب والستور للعروس (نعم الثواب) الجزاء الجنة (وحسنت مرتفقا).'),
(2172, 18, 32, '«واضرب» اجعل «لهم» للكفار مع المؤمنين «مثلاً رجلين» بدل وهو وما بعده تفسير للمثل «جعلنا لأحدهما» الكافر «جنتين» بستانين «من أعناب وحففناهما بنخل وجعلنا بينهما زرعا» يقتات به.'),
(2173, 18, 33, '«كلتا الجنتين» كلتا مفرد يدل على التثنية مبتدأ «آتت» خبره «أكلها» ثمرها «ولم تظلم» تنقص «منه شيئا» «وفجرنا» أي شقتنا «خلالهما نهرا» يجري بينهما.'),
(2174, 18, 34, '«وكان له» مع الجنتين «ثمر» بفتح الثاء والميم وبضمهما وبضم الأول وسكون الثاني وهو جمع ثمرة كشجرة وشجر وخشبة وخشب وبدنة وبدن «فقال لصاحبه» المؤمن «وهو يحاوره» يفاخره «أنا أكثر منك مالاً وأعز نفرا» عشيرة.'),
(2175, 18, 35, '«ودخل جنته» بصاحبه يطوف به فيها ويريه أثمارها ولم يقل جنتيه إرادة للروضة وقيل اكتفاء بالواحد «وهو ظالم لنفسه» بالكفر «قال ما أظن أن تبيد» تنعدم «هذه أبدا».'),
(2176, 18, 36, '«وما أظن الساعة قائمة ولئن رُدِدت إلى ربي» في الآخرة على زعمك «لأجدن خيرا منها منقلبا» مرجعا.'),
(2177, 18, 37, '«قال له صاحبه وهو يحاوره» يجاوبه «أكفرت بالذي خلقك من تراب» لأن آدم خُلق منه «ثم نطقه» منيّ «ثم سواك» عدلك وصيرك «رجلاً».'),
(2178, 18, 38, '«لكنا» أصله لكن أنا نقلت حركة الهمزة إلى النون أو حذفت الهمزة ثم أدغمت النون في مثلها «هو» ضمير الشأن تفسره الجملة بعده والمعنى أنا أقول «الله ربي ولا أشرك بربي أحدا».'),
(2179, 18, 39, '(ولولا) هلا (إذ دخلت جنتك قلت) عند إعجابك بها هذا (ما شاء الله لا قوة إلا بالله) وفي الحديث "" من أعطي خيرا من أهل أو مال فيقول عند ذلك ما شاء الله لا قوة إلا بالله لم ير فيه مكروها "" (إن ترن أنا) ضمير فصل بين المفعولين (أقل منك مالاً وولدا).'),
(2180, 18, 40, '«فعسى ربي أن يؤتين خيرا من جنتك» جواب الشرط «ويرسل عليها حسبانا» جمع حسبانة أي صواعق «من السماء فتصبح صعيدا زلقا» أرضا ملساء لا يثبت عليها قدم.'),
(2181, 18, 41, '«أو يصبح ماؤها غورا» بمعنى غائرا عطف على يرسل دون تصبح لأن غور الماء لا يتسبب عن الصواعق «فلن تستطيع له طلبا» حيلة تدركه بها.'),
(2182, 18, 42, '«وأحيط بثمره» بأوجه الضبط السابقة مع جنته بالهلاك فهلكت «فأصبح يقلب كفيه» ندما وتحسرا «على ما أنفق فيها» في عمارة جنته «وهي خاوية» ساقطة «على عروشها» دعائمها للكرم بأن سقطت ثم سقط الكرم «ويقول يا» للتنبيه «ليتني لم أشرك بربي أحدا».'),
(2183, 18, 43, '«ولم تكن» بالتاء والياء «له فئة» جماعة «ينصرونه من دون الله» عند هلاكها «وما كان منتصرا» عن هلاكها بنفسه.'),
(2184, 18, 44, '«هنالك» أي يوم القيامة «الولاية» بفتح الواو النصرة وبكسرها الملك «لله الحق» بالرفع صفة الولاية وبالجر صفة الجلالة «هو خير ثوابا» من ثواب غيره لو كان يثيب «وخير عقبا» بضم القاف وسكونها عاقبة للمؤمنين ونصبهما على التمييز.'),
(2185, 18, 45, '«واضرب» صير «لهم» لقومك «مثل الحياة الدنيا» مفعول أول «كماء» مفعول ثان «أنزلناه من السماء فاختلط به» تكاثف بسبب نزول الماء «نبات الأرض» أو امتزج الماء بالنبات فَرَوِيَ وَحَسُن «فأصبح» صار النبات «هشيما» يابسا متفرقة أجزاؤه «تذروه» تنثره وتفرقه «الرياح» فتذهب به، المعنى: شبه الدنيا بنبات حسن فيبس فتكسر ففرقته الرياح وفي قراءة الريح «وكان الله على كل شيء مقتدرا» قادرا.'),
(2186, 18, 46, '«المال والبنون زينة الحياة الدنيا» يتجمل بها فيها «والباقيات الصالحات» هي سبحان الله والحمد لله ولا إله إلا الله والله أكبر زاد بعضهم ولا حول ولا قوة إلا بالله «خير عند ربك ثوابا وخير أملاً» أي ما يأمله الإنسان ويرجوه عند الله تعالى.'),
(2187, 18, 47, '«و» اذكر «يوم تُسَيَّرُ الجبالُ» نذهب بها عن وجه الأرض فتصير هباء منبثا وفي قراءة بالنون وكسر الياء ونصب الجبال «وترى الأرض بارزة» ظاهرة ليس عليها شيء من جبل ولا غيره «وحشرناهم» المؤمنين والكافرين «فلم نغادر» نترك «منهم أحدا».'),
(2188, 18, 48, '«وعرضوا على ربك صفا» حال أي مصطفين كل أمة صف ويقال لهم «لقد جئتمونا كما خلقناكم أول مرة» أي فرادى حفاة عراة غُرْلاً ويقال لمنكري البعث «بل زعمتم أ» ن مخففة من الثقيلة أي أنه «لن نجعل لكم موعدا» للبعث.'),
(2189, 18, 49, '«ووضع الكتاب» كتاب كل امرئ في يمينه من المؤمنين وفي شماله من الكافرين «فترى المجرمين» الكافرين «مشفقين» خائفين «مما فيه ويقولن» عند معاينتهم ما فيه من السيئات «يا» للتنبيه «ويلتنا» هلكتنا وهو مصدر لا فعل له من لفظه «ما لهذا الكتاب لا يغادر صغيرة ولا كبيرة» من ذنوبنا «إلا أحصاها» عدها وأثبتها تعجبوا منه في ذلك «ووجدوا ما عملوا حاضرا» مثبتا في كتابهم «ولا يظلم ربك أحدا» لا يعاقبه بغير جرم ولا ينقص من ثواب مؤمن.'),
(2190, 18, 50, '«وإذ» منصوب باذكر «قلنا للملائكة اسجدوا لآدم» سجود انحناء لا وضع جبهة تحية له «فسجدوا إلا إبليس كان من الجن» قيل هم نوع من الملائكة فالاستثناء متصل وقيل هو منقطع وإبليس هو أبو الجن فله ذرية ذكرت معه بعد والملائكة لا ذرية لهم «ففسق عن أمر ربه» أي خرج عن طاعته بترك السجود «أفتتخذونه وذريته» الخطاب لآدم وذريته والهاء في الموضعين لإبليس «أولياء من دوني» تطيعونهم «وهم لكم عدو» أي أعداء حال «بئس للظالمين بدلاً» إبليس وذريته في إطاعتهم بدل إطاعة الله.'),
(2191, 18, 51, '«ما أشهدتهم» أي إبليس وذريته «خلق السماوات والأرض ولا خلق أنفسهم» أي لم أحضر بعضهم خلق بعض «وما كنت متخذ المضلين» الشياطين «عضدا» أعوانا في الخلق، فكيف تطيعونهم.'),
(2192, 18, 52, '«ويوم» منصوب باذكر «يقول» بالياء والنون «نادوا شركاءي» الأوثان «الذين زعمتم» ليشفعوا لكم بزعمكم «فدعوهم فلم يستجيبوا لهم» لم يجيبوهم «وجعلنا بينهم» بين الأوثان وعابديها «موبقا» واديا من أودية جهنم يهلكون فيه جميعا وهو من وبق بالفتح هلك.'),
(2193, 18, 53, '«ورأى المجرمون النار فظنوا» أي أيقنوا «أنهم مواقعوها» أي واقعون فيها «ولم يجدوا عنها مَصرفا» معدلاً.'),
(2194, 18, 54, '«ولقد صرفنا» بينا «في هذا القرآن للناس من كل مثل» صفة لمحذوف، أي مثلاً من جنس كل مثل ليتعظوا «وكان الإنسان» أي الكفار «أكثر شيء جدلاً» خصومة في الباطل وهو تمييز منقول من اسم كان، المعنى: وكان جدل الإنسان أكثر شيء فيه.'),
(2195, 18, 55, '«وما منع الناس» أي كفار مكة «أن يؤمنوا» مفعول ثان «إذ جاءهم الهدى» القرآن «ويستغفروا ربهم إلا أن تأتيهم سنة الأولين» فاعل أي سنتنا فيهم وهي الإهلاك المقدر عليهم «أو يأتيهم العذاب قبلاً» مقابلة وعيناً، وهو القتل يوم بدر وفي قراءة بضمتين جمع قبيل أي أنواعا.'),
(2196, 18, 56, '(وما نرسل المرسلين إلا مبشرين) للمؤمنين (ومنذرين) مخوفين للكافرين (ويجادل الذين كفروا بالباطل) بقولهم: "" أبعث الله بشرا رسولاً "" ونحوه (ليدحضوا به) ليبطلوا بجدالهم (الحق) القرآن (واتخذوا آياتي) أي القرآن (وما أنذروا) به من النار (هزوا) سخرية.'),
(2197, 18, 57, '«ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه» ما عمل من الكفر والمعاصي «إنا جعلنا على قلوبهم أكنَّة» أغطية «أن يفقهوه» أي من أن يفهموا القرآن أي فلا يفهمونه «وفي آذانهم وقرا» ثقلاً فلا يسمعونه «وإن تدعهم إلى الهدى فلن يهتدوا إذا» أي بالجعل المذكور «أبدا».'),
(2198, 18, 58, '«وربك الغفور ذو الرحمة لو يؤاخذهم» في الدنيا «بما كسبوا لعجل لهم العذاب» فيها «بل لهم موعد» وهو يوم القيامة «لن يجدوا من دونه موئلاً» ملجأ.'),
(2199, 18, 59, '«وتلك القرى» أي أهلها كعاد وثمود وغيرهما «أهلكناهم لما ظلموا» كفروا «وجعلنا لمهلكهم» لإهلاكهم وفي قراءة بفتح الميم أي لهلاكهم «موعدا».'),
(2200, 18, 60, '«و» اذكر «إذ قال موسى» هو ابن عمران «لفتاهُ» يوشع بن نون كان يتبعه ويخدمه ويأخذ عنه العلم «لا أبرح» لا أزال أسير «حتى أبلغ مجمع البحرين» ملتقى بحر الروم وبحر فارس مما يلي المشرق أي المكان الجامع لذلك «أو أمضي حقبا» دهرا طويلاً في بلوغه إن بعد.'),
(2201, 18, 61, '«فلما بلغا مجمع بينهما» بين البحرين «نسيا حوتهما» نسي يوشع حمله عند الرحيل ونسي موسى تذكيره «فاتخذ» الحوت «سبيله في البحر» أي جعله بجعل الله «سربا» أي مثل السرب، وهو الشق الطويل لا نفاذ له، وذلك أن الله تعالى أمسك عن الحوت جري الماء فانجاب عنه فبقي كالكوة لم يلتئم وجمد ما تحته منه.'),
(2202, 18, 62, '«فلما جاوزا» ذلك المكان بالسير إلى وقت الغداء من ثاني يوم «قال» موسى «لفتاهُ آتينا غداءنا» هو ما يؤكل أول النهار «لقد لقينا من سفرنا هذا نصبا» تعبا وحصوله بعد المجاوزة.'),
(2203, 18, 63, '«قال أرأيت» أي تنبه «إذ أوينا إلى الصخرة» بذلك المكان «فإني نسيت الحوت وما أنسانيهُ إلا الشيطان» يبدل من الهاء «أن أذكره» بدل اشتمال أي أنساني ذكره «واتخذ» الحوت «سبيله في البحر عجبا» مفعول ثان، أي يتعجب منه موسى وفتاه لما تقدم في بيانه.'),
(2204, 18, 64, '«قال» موسى «ذلك» أي فقدنا الحوت «ما» أي الذي «كنا نبغ» نطلبه فإنه علامة لنا على وجود من نطلبه «فارتدا» رجعا «على آثارهما» يقصانها «قصصا» فأتيا الصخرة.'),
(2205, 18, 65, '(فوجدا عبدا من عبادنا) هو الخضر (آتيناه رحمة من عندنا) نبوة في قول وولاية في آخر وعليه أكثر العلماء (وعلمناه من لدنا) من قبلنا (علما) مفعول ثان أي معلوما من المغيبات، روى البخاري حديث "" إن موسى قام خطيبا في بني إسرائيل فسئل أي الناس أعلم؟ فقال: أنا، فعتب الله عليه إذ لم يرد العلم إليه، فأوحى الله إليه: إن لي عبدا بمجمع البحرين هو أعلم منك قال موسى: يا رب فكيف لي به قال: تأخذ معك حوتا فتجعله في مكتل فحيثما فقد الحوت فهو ثم، فأخذ حوتا فجعله في مكتل ثم انطلق وانطلق معه فتاه يوشع بن نون حتى أتيا الصخرة ووضعا رأسيهما فناما واضطرب الحوت في المكتل فخرج منه فسقط في البحر "" فاتخذ سبيله في البحر سربا "" وأمسك الله عن الحوت جرية الماء فصار عليه مثل الطاق فلما استيقظ نسي صاحبه أن يخبره بالحوت فانطلقا بقية يومهما وليلتهما حتى إذا كانا من الغداة قال موسى لفتاه آتنا غداءنا إلى قوله واتخذ سبيله في البحر عجبا قال وكان للحوت سربا ولموسى ولفتاه عجبا إلخ "".'),
(2206, 18, 66, '«قال له موسى هل أتَّبعك على أن تعلَّمن مما عُلمت رَشَداً» أي صواباً أرشد به وفي قراءة بضم الراء وسكون الشين وسأله ذلك لأن الزيارة في العلم مطلوبة.'),
(2207, 18, 67, '«قال إنك لن تستطيع معي صبرا».'),
(2208, 18, 68, '(وكيف تصبر على ما لم تحط به خبرا) في الحديث السابق عقب هذه الآية "" يا موسى إني على علم من الله علمنيه لا تعلمه وأنت على علم من الله علمكه الله لا أعلمه، وقوله خبرا مصدر بمعنى لم تحط أي لم تخبر حقيقته.'),
(2209, 18, 69, '«قال ستجدني إن شاء الله صابرا ولا أعصي» أي وغير عاص «لك أمرا» تأمرني به، وقيد بالمشيئة لأنه لم يكن على ثقة من نفسه فيما التزم، وهذه عادة الأنبياء والأولياء ألا يثقوا إلى أنفسهم طرفة عين.'),
(2210, 18, 70, '«قال فإن اتبعتني فلا تسألني» وفي قراءة بفتح اللام وتشديد النون «عن شيء» تنكره مني في علمك واصبر «حتى أحدث لك منه ذكرا» أي أذكره لك بعلته، فقبل موسى شرطه رعاية لأدب المتعلم مع العالم.'),
(2211, 18, 71, '«فانطلقا» يمشيان على البحر «حتى إذا ركبا في السفينة» التي مرت بهما «خرقها» الخضر بأن اقتلع لوحا أو لوحين منها من جهة البحر بفأس لما بلغت اللجج «قال» له موسى «اخرقتها لتغرق أهلها» وفي قراءة بفتح التحتانية والراء ورفع أهلها «لقد جئت شيئا إمرا» أي عظيما منكرا روي أن الماء لم يدخلها.'),
(2212, 18, 72, '«قال ألم أقل إنك لن تستطيع معي صبرا».'),
(2213, 18, 73, '«قال لا تؤاخذني بما نسيت» أي غفلت عن التسليم لك وترك الانكار عليك «ولا ترهقني» تكلفني «من أمري عسرا» مشقة في صحبتي إياك أي عاملني فيها بالعفو واليسر.'),
(2214, 18, 74, '«فانطلقا» بعد خروجهما من السفينة يمشيان «حتى إذا لقيا غلاما» لم يبلغ الحنث يلعب مع الصبيان أحسنهم وجها «فقتله» الخضر بأن ذبحه بالسكين مضطجعا أو اقتلع رأسه بيده أو ضرب رأسه بالجدار، أقوال وأتى هنا بالفاء العاطفة لأن القتل عقب اللقاء وجواب إذا «قال» له موسى «أقتلت نفسا زاكية» أي طاهرة لم تبلغ حد التكليف وفي قراءة زكية بتشديد الياء بلا ألف «بغير نفس» أي لم تقتل نفسا «لقد جئت شيئا نكرا» بسكون الكاف وضمها أي منكرا.'),
(2215, 18, 75, '«قال ألم أقل لك إنك لن تستطيع معي صبرا» زاد لك على ما قبله لعدم العذر هنا.'),
(2216, 18, 76, 'ولهذا «قال إن سألتك عن شيء بعدها» أي بعد هذه المرة «فلا تصاحبني» لا تتركني أتبعك «قد بلغت من لدني» بالتشديد والتخفيف من قبلي «عذرا» في مفارقتك لي.'),
(2217, 18, 77, '«فانطلقا حتى إذا أتيا أهل قرية» هي أنطاكية «استطعما أهلها» طلبا منهم الطعام بضيافة «فأبوا أن يضيفوهما فوجدا فيها جدارا» ارتفاعه مائة ذراع «يريد أن ينقضَّ» أي يقرب أن يسقط لميلانه «فأقامه» الخضر بيده «قال» له موسى «لو شئت لاتخذت» وفي قراءة لتخذت «عليه أجرا» جُعْلاً حيث لم يضيفونا مع حاجتنا إلى الطعام.'),
(2218, 18, 78, '«قال» له الخضر «هذا فراق» أي وقت فراق «بيني وبينك» فيه إضافة بين إلى غير متعدد سوغها تكريره بالعطف بالواو «سأُنبئك» قبل فراقي لك «بتأويل ما لم تستطع عليه صبرا».'),
(2219, 18, 79, '«أما السفينة فكانت لمساكين» عشرة «يعملون في البحر» بها مؤاجرة لها طلبا للكسب «فأردت أن أعيبها وكان وراءهم» إذا رجعوا أو أمامهم الآن «ملك» كافر «يأخذ كل سفينة» صالحة «غصبا» نصبه على المصدر المبين لنوع الأخذ.'),
(2220, 18, 80, '«وأما الغلام فكان أبواه مؤمنين فخشينا أن يرهقهما طغيانا وكفرا» فإنه كما في حديث مسلم طبع كافرا ولو عاش لأرهقهما ذلك لمحبتهما له يتبعانه في ذلك.'),
(2221, 18, 81, '«فأردنا أن يبدِّلهما» بالتشديد والتخفيف «ربهما خيرا منه زكاة» أي صلاحا وتقى «وأقرب» منه «رحْما» بسكون الحاء وضمها رحمة وهي البرّ بوالديه فأبدلهما تعالى جارية تزوجت نبيا فولدت نبيا فهدى الله تعالى به أمة.'),
(2222, 18, 82, '«وأما الجدار فكان لغلامين يتيمين في المدينة وكان تحته كنز» مال مدفون من ذهب وفضة «لهما وكان أبوهما صالحا» فحفظا بصلاحه في أنفسهما ومالهما «فأراد ربك أن يبلغا أشدهما» أي إيناس رشدهما «ويستخرجا كنزهما رحمة من ربك» مفعول له عامله أراد «وما فعلته» أي ما ذكر من خرق السفينة وقتل الغلام وإقامة الجدار «عن أمري» أي اختباري بل بأمر إلهام من الله «ذلك تأويل ما لم تسطع عليه صبرا» يقال اسطاع واستطاع بمعنى أطاق، ففي هذا وما قبله جمع بين اللغتين ونوعت العبارة في: فأردت، فأردنا فأراد ربك.'),
(2223, 18, 83, '«ويسألونك» أي اليهود «عن ذي القرنين» اسمه الاسكندر ولم يكن نبيا «قل سأتلو» سأقص «عليكم منه» من حاله «ذكرا» خبرا.'),
(2224, 18, 84, '«إنا مكنا له في الأرض» بتسهيل السير فيها «وآتيناه من كل شيء» يحتاج إليه «سببا» طريقا يوصله إلى مراده.'),
(2225, 18, 85, '«فأتبع سببا» سلك طريقا نحو الغرب.'),
(2226, 18, 86, '«حتى إذا بلغ مغرب الشمس» موضع غروبها «وجدها تغرب في عين حمئة» ذات حمأة وهي الطين الأسود وغروبها في العين في رأي وإلا فهي أعظم من الدنيا «ووجد عندها» أي العين «قوما» كافرين «قلنا يا ذا القرنين» بإلهام «إما أن تُعذّب» القوم بالقتل «وإما أن تتخذ فيهم حُسنا» بالأسر.'),
(2227, 18, 87, '«قال أما من ظلم» بالشرك «فسوف نعذبه» نقتله «ثم يُرد إلى ربه فيعذبه عذابا نكرا» بسكون الكاف وضمها شديدا في النار.'),
(2228, 18, 88, '«وأما من آمن وعمل صالحا فله جزاء الحسنى» أي الجنة والإضافة للبيان وفي قراءة بنصب جزاء وتنوينه قال الفراء: ونصبه على التفسير أي لجهة النسبة «وسنقول له من أمرنا يُسرا» أي نأمره بما سهل عليه.'),
(2229, 18, 89, '«ثم أتبع سببا» نحو المشرق.'),
(2230, 18, 90, '«حتى إذا بلغ مطلع الشمس» موضع طلوعها «وجدها تطلع على قوم» هم الزنج «لم نجعل لهم من دونها» أي الشمس «سترا» من لباس ولا سقف، لأن أرضهم لا تحمل بناء ولهم سروب يغيبون فيها عند طلوع الشمس ويظهرون عند ارتفاعها.'),
(2231, 18, 91, '«كذلك» أي الأمر كما قلنا «وقد أحطنا بما لديه» أي عند ذي القرنين من الآلات والجند وغيرهما «خبرا» علما.'),
(2232, 18, 92, '«ثم أتبع سببا».'),
(2233, 18, 93, '«حتى إذا بلغ بين السدين» بفتح السين وضمها هنا وبعدهما جبلان بمنقطع بلاد الترك، سد الإسكندر ما بينهما كما سيأتي «وجد من دونهما» أي أمامهما «قوما لا يكادون يفقهون قولاً» أي لا يفهمونه إلا بعد بطء، وفي قراءة بضم الياء وكسر القاف.'),
(2234, 18, 94, '«قالوا يا ذا القرنين إن يأجوج ومأجوج» بالهمز وتركه: هما اسمان أعجميان لقبيلتين فلم ينصرفا «مفسدون في الأرض» بالنهب والبغي عند خروجهم إلينا «فهل نجعل لك خرجا» جعلاً من المال وفي قراءة خراجا «على أن تجعل بيننا وبينهم سداً» حاجزا فلا يصلون إلينا.'),
(2235, 18, 95, '«قال ما مكَّني» وفي قراءة بنونين من غير إدغام «فيه رب» من المال وغيره «خير» من خرجكم الذي تجعلونه لي فلا حاجة بي إليه وأجعل لكم السد تبرعا «فأعينوني بقوة» لما أطلبه منكم «أجعل بينكم وبينهم رَدما» حاجزا حصينا.'),
(2236, 18, 96, '«آتوني زبر الحديد» قطعه على قدر الحجارة التي يبني بها فبنى بها وجعل بينها الحطب والفحم «حتى إذا ساوى بين الصدفين» بضم الحرفين وفتحهما وضم الأول وسكون الثاني، أي جانبي الجبلين بالبناء ووضع المنافخ والنار حول ذلك «قال انفخوا» فنفخوا «حتى إذا جعله» أي الحديد «نارا» أي كالنار «قال آتوني أفرغ عليه قِطرا» هو النحاس المذاب تنازع فيه الفعلان، وحذف من الأول لإعمال الثاني النحاس المذاب على الحديد المحمى فدخل بين زبره فصارا شيئا واحدا.'),
(2237, 18, 97, '«فما اسطاعوا» أي يأجوج ومأجوج «أن يظهروه» يعلوا ظهره لارتفاعه وملاسته «وما استطاعوا له نقبا» لصلابته وسمكه.'),
(2238, 18, 98, '«قال» ذو القرنين «هذا» أي السد، أي الإقدار عليه «رحمة من ربي» نعمة لأنه مانع من خروجهم «فإذا جاء وعد ربي» بخروجهم القريب من البعث «جعله دكاء» مدكوكا مبسوطا «وكان وعد ربي» بخروجهم وغيره «حقا» كائنا قال تعالى:'),
(2239, 18, 99, '«وتركنا بعضهم يومئذ» يوم خروجهم «يموج في بعض» يختلط به لكثرتهم «ونفخ في الصور» أي القرن للبعث «فجمعناهم» أي الخلائق في مكان واحد يوم القيامة «جمعا».'),
(2240, 18, 100, '«وعرضنا» قربنا «جهنم يومئذ للكافرين عرضا».'),
(2241, 18, 101, '«الذين كانت أعينهم» بدل من الكافرين «في غطاء عن ذكري» أي القرآن فهم عمي لا يهتدون به «وكانوا لا يستطيعون سمعا» أي لا يقدرون أن يسمعوا من النبي ما يتلو عليهم بغضا له فلا يؤمنون به.'),
(2242, 18, 102, '«أفحسب الذين كفروا أن يتخذوا عبادي» أي ملائكتي وعيسى وعزيرا «من دوني أولياء» أربابا مفعول ثان ليتخذوا والمفعول الثاني لحسب محذوف المعنى أظنوا أن الاتخاذ المذكور لا يغضبني ولا أعاقبهم عليه كلا «إنا أعتدنا جهنم للكافرين» هؤلاء وغيرهم «نُزلاً» أي هي معدة لهم كالمنزل المعد للضيف.'),
(2243, 18, 103, '«قل هل ننبِّئكم بالأخسرين أعمالاً» تمييز طابق المميز، وبيَّنهم بقوله:'),
(2244, 18, 104, '«الذين ضل سعيهم في الحياة الدنيا» بطل عملهم «وهم يحسبون» يظنون «أنهم يحسنون صنعا» عملاً يجازون عليه.'),
(2245, 18, 105, '«أولئك الذين كفروا بآيات ربهم» بدلائل توحيده من القرآن وغيره «ولقائه» أي وبالبعث والحساب والثواب والعقاب «فحبطت أعمالهم» بطلت «فلا نقيم لهم يوم القيامة وزنا» أي لا نجعل لهم قدرا.'),
(2246, 18, 106, '«ذلك» أي الأمر الذي ذكرت عن حُبوط أعمالهم وغيره مبتدأ خبره «جزاؤهم جهنم بما كفروا واتخذوا آياتي ورسلي هزوا» أي مهزوءا بهما.'),
(2247, 18, 107, '«إن الذين آمنوا وعملوا الصالحات كانت لهم» في علم الله «جناتُ الفردوس» هو وسط الجنة وأعلاها والإضافة إليه للبيان «نُزُلاً» منزلاً.'),
(2248, 18, 108, '«خالدين فيها لا يبغون» يطلبون «عنها حولاً» تحولاً إلى غيرها.'),
(2249, 18, 109, '«قل لو كان البحر» أي ماؤه «مدادا» هو ما يكتب به «لكلمات ربي» الدالة على حكمه وعجائبه بأن تكتب به «لنفد البحر» في كتابتها «قبل أن تنفد» بالتاء والياء: تفرغ «كلمات ربي ولو جئنا بمثله» أي البحر «مَدَدا» زيادة فيه لنفد، ولم تفرغ هي، ونصبه على التمييز.'),
(2250, 18, 110, '«قل إنما أنا بشر» آدمي «مثلكم يوحى إليَّ أنما إلهكم إله واحد» أن المكفوفة بما باقية على مصدريتها والمعنى: يوحى إليَّ وحدانية الإله «فمن كان يرجو» يأمل «لقاء ربه» بالبعث والجزاء «فليعمل عملاً صالحا ولا يشرك بعبادة ربه» أي فيها بأن يرائي «أحدا».');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, '«كَهيعَص» الله أعلم بمراده بذلك.'),
(2252, 19, 2, 'هذا «ذكر رحمة ربك عبده» مفعول رحمة «زكريا» بيان له.'),
(2253, 19, 3, '«إذ» متعلق برحمة «نادى ربه نداءً» مشتملاً على دعاء «خفيا» سرا جوف الليل لأنه أسرع للإجابة.'),
(2254, 19, 4, '«قال رب إني وهن» ضعف «العظم» جميعه «مني واشتعل الرأس» مني «شيبا» تمييز محوَّل عن الفاعل أي: انتشر الشيب في شعره كما ينتشر شعاع النار في الحطب وإني أريد أن أدعوك «ولم أكن بدعائك» أي: بدعائي إياك «ربّ شقيا» أي: خائبا فيما مضى فلا تخيبني فيما يأتي.'),
(2255, 19, 5, '«وإني خفت الموالي» أي الذين يلوني في النسب كبني العم «من ورائي» أي بعد موتي على الدين أن يُضيعوه كما شاهدته في بني إسرائيل من تبديل الدين «وكانت امرأتي عاقرا» لا تلد «فهب لي من لدنك» من عندك «وليا» ابنا.'),
(2256, 19, 6, '«يَرثني» بالجزم جواب الأمر وبالرفع صفة وليا «ويرث» بالوجهين «من آل يعقوب» جدّي، العلم والنبوة «واجعله رب رضيا» أي: مرضيا عندك. قال تعالى في إجابة طلبه الابن الحاصل به رحمته:'),
(2257, 19, 7, '«يا زكريا إنا نبشرك بغلامِ» يَرثُ كما سألت «اسمه يحيى لم نجعل له من قبل سميا» أي: مسمى بيحيى.'),
(2258, 19, 8, '«قال ربّ أنَّى» كيف «يكون لي غلام وكانت امرأتي عاقرا وقد بلغت من الكبر عتيا» من عتا: يبس، أي نهاية السن مائة وعشرين سنة وبلغت امرأته ثمانية وتسعين سنة وأصل عتى: عتو وكسرت التاء تخفيفا وقلبت الواو الأولى ياء لمناسبة الكسرة والثانية ياء لتدغم فيها الياء.'),
(2259, 19, 9, '«قال» الأمر «كذلك» من خلق غلام منكما «قال ربك هو عليَّ هين» أي: بأن أرد عليك قوة الجماع وأفتق رحم امرأتك للعلوق «وقد خلقتك من قبل ولم تكُ شيئا» قبل خلقك ولإظهار الله هذه القدرة العظيمة ألهمه السؤال ليجاب بما يدل عليها ولما تاقت نفسه إلى سرعة المبشر به.'),
(2260, 19, 10, '«قال رب اجعل آية» أي علامة على حمل امرأتي «قال آيتك» عليه «ألا تكلم الناس» أي تمتنع من كلامهم بخلاف ذكر الله «ثلاث ليال» أي بأيامها كما في آل عمران ثلاثة أيام «سَويا» حال من فاعل تكلم أي بلا علة.'),
(2261, 19, 11, '«فخرج على قومه من المحراب» أي المسجد وكانوا ينتظرون فتحه ليصلوا فيه بأمره على العادة «فأوحى» أشار «إليهم أن سبحوا» صلوا «بُكرة وعشيا» أوائل النهار وأواخره على العادة فعلم بمنعه من كلامهم حملها بيحيى، وبعد ولادته بسنتين قال الله تعالى له:'),
(2262, 19, 12, '«يا يحيى خذ الكتاب» أي: التوراة «بقوة» بجد «وآتيناه الحكم» النبوة «صبيا» ابن ثلاث سنين.'),
(2263, 19, 13, '«وحنانا» رحمة للناس «من لدنا» من عندنا «وزكاة» صدقة عليهم «وكان تقيا» روي أنه لم يعمل خطيئة ولم يهم بها.'),
(2264, 19, 14, '«وبرّا بوالديه» أي: محسنا إليهما «ولم يكن جبارا» متكبرا «عصيا» عاصيا لربه.'),
(2265, 19, 15, '«وسلامٌ» منا «عليه يوم وُلد ويوم يموت ويوم يُبعث حيا» أي: في هذه الأيام المخوفة التي يرى ما لم يره قبلها فهو آمن فيها.'),
(2266, 19, 16, '«واذكر في الكتاب» القرآن «مريم» أي: خبرها «إذ» حين «انتبذت من أهلها مكانا شرقيا» أي: اعتزلت في مكان نحو الشرق من الدار.'),
(2267, 19, 17, '«فاتخذت من دونهم حجابا» أرسلت سترا تستتر به لتفلي رأسها أو ثيابها أو تغسل من حيضها «فأرسلنا إليها روحنا» جبريل «فتمثل لها» ب لبسها ثيابها «بشرا سويا» تام الخلق.'),
(2268, 19, 18, '«قالت إني أعوذ بالرحمن منك إن كنت تقيا» فتنتهي عني بتعوذي.'),
(2269, 19, 19, '«قال إنما أنا رسول ربك ليهب لك غلاما زكيا» بالنبوة.'),
(2270, 19, 20, '«قالت أنَّى يكون لي غلام ولم يمسسني بشر» يتزوج «ولم أك بغيّا» زانية.'),
(2271, 19, 21, '«قال» الأمر «كذلك» من خلق غلام منك من غير أب «قال ربك هو عليَّ هينٌ» أي: بأن ينفخ بأمري جبريل فيك فتحملي به ولكون ما ذكر في معنى العلة عطف عليه «ولنجعله آيةً للناس» على قدرتنا «ورحمة منا» لمن آمن به «وكان» خلقه «أمرا مقضيا» به في علمي فنفخ جبريل في جيب درعها فأحست بالحمل في بطنها مصورا.'),
(2272, 19, 22, '«فحملته فانتبذت» تنحَّت «به مكانا قصيا» بعيدا من أهلها.'),
(2273, 19, 23, '«فأجاءَها» جاءَ بها «المخاض» وجع الولادة «إلى جذع النخلة» لتعتمد عليه فولدت والحمل والتصوير والولادة في ساعة «قالت يا» للتنبيه «ليتني متُّ قبل هذا» الأمر «وكنت نسيا منسيا» شيئا متروكا لا يعرف ولا يذكر.'),
(2274, 19, 24, '«فناداها من تحتها» أي: جبريل وكان أسفل منها «ألا تحزني قد جعل ربك تحتك سريا» نهر ماء كان قد انقطع.'),
(2275, 19, 25, '«وهزي إليك بجذع النخلة» كانت يابسة والباء زائدة «تساقط» أصله بتاءين قلبت الثانية سينا وأدغمت في السين، وفى قراءة تركها «عليك رطبا» تمييز «جنيا» صفته.'),
(2276, 19, 26, '«فكلي» من الرطب «واشربي» من السري «وقري عينا» بالولد تمييز محول من الفاعل أي: لتقر عينك به أي: تسكن فلا تطمح إلى غيره «فإما» فيه إدغام نون إن الشرطية في ما الزائدة «ترين» حذفت منه لام الفعل وعينه وألقيت حركتها على الراء وكسرت ياء الضمير لالتقاء الساكنين «من البشر أحدا» فيسألك عن ولدك «فقولي إني نذرت للرحمن صوما» أي إمساكا عن الكلام في شأنه وغيره من الأناسي بدليل «فلن أكلم اليوم إنسا» أي: بعد ذلك.'),
(2277, 19, 27, '«فأتت به قومها تحمله» حال فرأوه «قالوا يا مريم لقد جئت شيئا فريا» عظيما حيث أتيت بولد من غير أب.'),
(2278, 19, 28, '«يا أخت هارون» هو رجل صالح أي: يا شبيهته في العفة «ما كان أبوك أمرأ سوء» أي: زانيا «وما كانت أمك بغيا» زانية فمن أين لك هذا الولد.'),
(2279, 19, 29, '«فأشارت» لهم «إليه» أن كلموه «قالوا كيف نكلم من كان» أي وجد «في المهد صبيا.'),
(2280, 19, 30, '«قال إني عبد الله آتاني الكتاب» أي: الإنجيل «وجعلني نبيا».'),
(2281, 19, 31, '«وجعلني مباركا أينما كنت» أي: نفاعا للناس إخبار بما كتب له «وأوصاني بالصلاة والزكاة» أمرني بهما «ما دمت حيا».'),
(2282, 19, 32, '«وبرا بوالدتي» منصوب بجعلني مقدرا «ولم يجعلني جبارا» متعاظما «شقيا» عاصيا لربه.'),
(2283, 19, 33, '«والسلام» من الله «عليّ يوم ولدت ويوم أموت ويوم أبعث حيا» يقال فيه ما تقدم في السيد يحيى. قال تعالى:'),
(2284, 19, 34, '«ذلك عيسى ابن مريم قولُ الحق» بالرفع خبر مبتدأ مقدر أي: قول ابن مريم وبالنصب بتقدير قلت، والمعنى القول الحق «الذي فيه يمترون» من المرية أي: يشكون وهم النصارى: قالوا إن عيسى ابن الله، كذبوا.'),
(2285, 19, 35, '«ما كان لله أن يتخذ من ولد سبحانه» تنزيها له عن ذلك «إذا قضى أمرا» أي: أراد أن يحدثه «فإنما يقول له كن في فيكونُ» بالرفع بتقدير هو، وبالنصب بتقدير أن ومن ذلك خلق عيسى من غير أب.'),
(2286, 19, 36, '(وأن الله ربي وربكم فاعبدوه) بفتح أن بتقدير اذكر، وبكسرها بتقدير قل بدليل "" ما قلت لهم إلا ما أمرتني به أن اعبدوا الله ربي وربكم "" (هذا) المذكور (صراط) طريق (مستقيم) مؤد إلى الجنة.'),
(2287, 19, 37, '«فاختلف الأحزاب من بينهم» أي النصارى في عيسى أهو ابن الله أو إله معه أو ثالث ثلاثة «فويل» فشدة عذاب «للذين كفروا» بما ذكر وغيره «من مشهد يوم عظيم» أي: حضور يوم القيامة وأهواله.'),
(2288, 19, 38, '«أسمع بهم وأبصر» بهم صيغتا تعجب بمعنى ما أسمعهم وما أبصرهم «يوم يأتوننا» في الآخرة «لكن الظالمون» من إقامة الظاهر مقام المضمر «اليوم» أي: في الدنيا «في ضلال مبين» أي بين به صموا عن سماع الحق وعموا عن إبصاره أي: اعجب منهم يا مخاطب في سمعهم وإبصارهم في الآخرة بعد أن كانوا في الدنيا صما عميا.'),
(2289, 19, 39, '«وأنذرهم» خوّف يا محمد كفار مكة «يوم الحسرة» هو يوم القيامة يتحسر فيه المسيء على ترك الإحسان في الدنيا «إذ قُضي الأمر» لهم فيه بالعذاب «وهم» في الدنيا «في غفلة» عنه «وهم لا يؤمنون» به.'),
(2290, 19, 40, '«إنا نحن» تأكيد «نرث الأرض ومن عليها» من العقلاء وغيرهم بإهلاكهم «وإلينا يرجعون» فيه للجزاء.'),
(2291, 19, 41, '«واذكر» لهم «في الكتاب إبراهيم» أي: خبره «إنه كان صديقا» مبالغا في الصدق «نبيا» ويبدل من خبره.'),
(2292, 19, 42, '«إذ قال لأبيه» آزر «يا أبت» التاء عوض عن ياء الإضافة ولا يجمع بينهما وكان يعبد الأصنام «لمَ تعبد مالا يسمع ولا يبصر ولا يغني عنك» لا يكفيك «شيئا» من نفع أو ضر.'),
(2293, 19, 43, '«يا أبت إني قد جاءني من العلم ما لم يأتك فاتبعني أهدك صراطا» طريقا «سويا» مستقيما.'),
(2294, 19, 44, '«يا أبت لا تعبد الشيطان» بطاعتك إياه في عبادة الأصنام «إن الشيطان كان للرحمن عصيا» كثير العصيان.'),
(2295, 19, 45, '«يا أبت إني أخاف أن يمسك عذاب من الرحمن» إن لم تتب «فتكون للشيطان وليا» ناصرا وقرينا في النار.'),
(2296, 19, 46, '«قال أراغب أنت عن آلهتي يا إبراهيم» فتعيبها «لئن لم تنته» عن التعرض لها «لأرجمنَّك» بالحجارة أو بالكلام القبيح فاحذرني «واهجرني مليّا» دهرا طويلاً.'),
(2297, 19, 47, '(قال سلام عليك) مني أي لا أصيبك بمكروه (سأستغفر لك ربي إنه كان بي حفيا) من حفي أي بارا فيجيب دعائي وقد أوفى بوعده المذكور في الشعراء "" واغفر لأبي "" وهذا قبل أن يتبين له أنه عدو الله كما ذكره في براءة.'),
(2298, 19, 48, '«وأعتزلكم وما تدعون» تعبدون «من دون الله وأدعو» أعبد «ربي عسى أ» ن «لا أكون بدعاء ربي» بعبادته «شقيا» كما شقيتم بعبادة الأصنام.'),
(2299, 19, 49, '«فلما اعتزلهم وما يعبدون من دون الله» بأن ذهب إلى الأرض المقدسة «وهبنا له» ابنين يأنس بهما «إسحاق ويعقوب وكلا» منهما «جعلنا نبيا».'),
(2300, 19, 50, '«ووهبنا لهم» للثلاثة «من رحمتنا» المال والولد «وجعلنا لهم لسان صدق عليا» رفيعا هو الثناء الحسن في جميع أهل الأديان.'),
(2301, 19, 51, '«واذكر في الكتاب موسى إنه كان مخلصا» بكسر اللام وفتحها من أخلص في عبادته وخلصه الله من الدنس «وكان رسولاً نبيا».'),
(2302, 19, 52, '(وناديناه) بقول "" يا موسى إني أنا الله "" (من جانب الطور) اسم جبل (الأيمن) أي الذي يلي يمين موسى حين أقبل من مدين (وقربناه نجيا) مناجيا بأن أسمعه الله تعالى كلامه.'),
(2303, 19, 53, '«ووهينا له من رحمتنا» نعمتنا «أخاه هارون» بدل أو عطف بيان «نبيا» حال هي المقصودة بالهبة إجابة لسؤاله أن يرسل أخاه معه وكان أسنَّ منه.'),
(2304, 19, 54, '«واذكر في الكتاب إسماعيل إنه كان صادق الوعد» لم يعد شيئا إلا وفى به وانتظر من وعده ثلاثة أيام أيام أو حولاً حتى رجع إليه في مكانه «وكان رسولاً» إلى جُرهم «نبيا».'),
(2305, 19, 55, '«وكان يأمر أهله» أي قومه «بالصلاة والزكاة وكان عند ربه مرضيا» أصله مرضوو قبلت الواوان ياءين والضمة كسرة.'),
(2306, 19, 56, '«واذكر في الكتاب إدريس» هو جدّ أبي نوح «إنه كان صديقا نبيا».'),
(2307, 19, 57, '«ورفعناه مكانا عليا» هو حي في السماء الرابعة أو السادسة أو السابعة أو في الجنة أدخلها بعد أن أذيق الموت وأحيي ولم يخرج منها.'),
(2308, 19, 58, '«أولئك» مبتدأ «الذين أنعم الله عليهم» صفة له «من النبيين» بيان له وهو في معنى الصفة وما بعده إلى جملة الشرط صفة للنبيين فقوله «من ذرية آدم» أي إدريس «وممن حملنا مع نوح» في السفينة أي إبراهيم ابن ابنه سام «ومن ذرية إبراهيم» أي إسماعيل وإسحاق ويعقوب «و» من ذرية «إسرائيل» هو يعقوب أي موسى وهارون وزكريا ويحيى وعيسى «وممن هدينا واجتبينا» أي من جملتهم وخبر أولئك «إذا تتلى عليهم آيات الرحمن خروا سجدا وبكيا» جمع ساجد وباك أي فكونوا مثلهم وأصل بكي بكوي قبلت الواو ياء والضمة كسرة.'),
(2309, 19, 59, '«فخلف من بعدهم خلف أضاعوا الصلاة» بتركها كاليهود والنصارى «واتبعوا الشهوات» من المعاصي «فسوف يلقون غيّا» هو واد في جهنم، أي يقعون فيه.'),
(2310, 19, 60, '«إلا» لكن «من تاب وآمن وعمل صالحا فأولئك يدخلون الجنة ولا يظلمون» ينقصون «شيئا» من ثوابهم.'),
(2311, 19, 61, '«جنات عدن» إقامة بدل من الجنة «التي وعد الرحمن عباده بالغيب» حال، أي غائبين عنها «إنه كان وعده» أي موعده «مأتيا» بمعنى آتيا وأصله مأتوي أو موعوده هنا الجنة يأتيه أهله.'),
(2312, 19, 62, '«لا يسمعون فيها لغوا» من الكلام «إلا» لكن يسمعون «سلاما» من الملائكة عليهم أو من بعضهم على بعض «ولهم رزقهم فيها بكرة وعشيا» أي على قدرهما في الدنيا، وليس في الجنة نهار ولا ليل بل ضوء ونور أبدا.'),
(2313, 19, 63, '«تلك الجنة التي نورث» نعطي وننزل «من عبادنا من كان تقيا» بطاعته، ونزل لما تأخر الوحي أياما وقال النبي صلى الله عليه وسلم لجبريل: ما يمنعك أن تزورنا أكثر مما تزورنا؟.'),
(2314, 19, 64, '«وما نتنزل إلا بأمر ربك له ما بين أيدينا» أي أمامنا من أمور الآخرة «وما خلفنا» من أمور الدنيا «وما بين ذلك» أي: ما يكون في هذا الوقت إلى قيام الساعة أي له علم ذلك جمعيه «وما كان ربك نسيّا» بمعنى ناسيا أي: تاركا لك بتأخير الوحي عنك.'),
(2315, 19, 65, 'هو «ربّ» مالك «السماوات والأرض وما بينهما فاعبده واصطبر لعبادته» أي: اصبر عليها «هل تعلم له سميا» مسمى بذلك؟ لا.'),
(2316, 19, 66, '«ويقول الإنسان» المنكر للبعث أبي بن خلف أو الوليد بن المغيرة النازل فيه الآية: «أئذا» بتحقيق الهمزة الثانية وإدخال ألف بينها بوجهيها وبين الأخرى «ما متّ لسوف أخرج حيا» من القبر كما يقول محمد، فالاستفهام بمعنى النفي أي: لا أحيا بعد الموت وما زائدة للتأكيد وكذا اللام ورد عليه بقوله تعالى:'),
(2317, 19, 67, '«أولا يَذكرُ الإنسان» أصله يتذكر أبدلت التاء ذالا وأدغمت في الذال وفي قراءة تركها وسكون الذال وضم الكاف «أنا خلقناه من قبل ولم يك شيئا» فيستدل بالابتداء على الإعادة.'),
(2318, 19, 68, '«فوربك لنحشرنهم» أي المنكرين للبعث «والشياطين» أي نجمع كلا منهم وشيطانه في سلسلة «ثم لنحضرنهم حول جهنم» من خارجها «جثيا» على الركب جمع جاث وأصله جثوو أو جثوي من جثا يجثو أو يجثي لغتان.'),
(2319, 19, 69, '«ثم لننزعن من كل شيعة» فرقة منهم «أيهم أشد على الرحمن عتيا» جراءة.'),
(2320, 19, 70, '«ثم لنحن أعلم بالذين هم أولى بها» أحق بجهنم الأشد وغيره منهم «صليا» دخولا واحتراقا فنبدأ بهم وأصله صلوي من صلي بكسر اللام وفتحها.'),
(2321, 19, 71, '«وإن» أي ما «منكم» أحد «إلا واردها» أي داخل جهنم «كان على ربك حتما مقضيا» حتمه وقضى به لا يتركه.'),
(2322, 19, 72, '«ثم ننجِّي» مشددا ومخففا «الذين اتقوا» الشرك والكفر منها «ونذر الظالمين» بالشرك والكفر «فيها جثيا» على الركب.'),
(2323, 19, 73, '«وإذا تتلى عليهم» أي المؤمنين والكافرين «آياتنا» من القرآن «بينات» واضحات حال «قال الذين كفروا للذين آمنوا أيُّ الفريقين» نحن وأنتم «خير مقاما» منزلا ومسكنا بالفتح من قام وبالضم من أقام «وأحسن نديا» بمعنى النادي وهو مجتمع القوم يتحدثون فيه، يعنون نحن فنكون خيرا منكم قال تعالى:'),
(2324, 19, 74, '«وكم» أي كثيرا «أهلكنا قبلهم من قرن» أي أمة من الأمم الماضية «هم أحسن أثاثا» مالاً ومتاعا «ورئيا» منظرا من الرؤية فكما أهلكناهم لكفرهم هلك هؤلاء.'),
(2325, 19, 75, '«قل من كان في الضلالة» شرط جوابه «فليمدد» بمعنى الخبر أي يمد «له الرحمن مدا» في الدنيا يستدرجه «حتى إذا رأوا ما يوعدون إما العذاب» كالقتل والأسر «وإما الساعة» المشتملة على جهنم فيدخلونها «فسيعلمون من هو شر مكانا وأضعف جندا» أعوانا أهم أم المؤمنون وجندهم الشياطين وجند المؤمنين عليهم الملائكة.'),
(2326, 19, 76, '«ويزيد الله الذين اهتدوا» بالإيمان «هدى» يما ينزل عليهم من الآيات «والباقيات الصالحات» هي الطاعة تبقى لصاحبها «خير عند ربك ثوابا وخير مردّا» أي ما يرد إليه ويرجع بخلاف أعمال الكفار والخيرية منا في مقابلة قولهم أي الفرقين خير مقاما.'),
(2327, 19, 77, '«أفرأيت الذي كفر بآياتنا» العاص بن وائل «وقال» لخباب بن الأرث القائل له نبعث بعد الموت والمطالب له بمال «لأوتينَّ» على تقدير البعث «مالاً وولدا» فأقضيك، قال تعالى:'),
(2328, 19, 78, '«أطلع الغيب» أي أعلمه وأن يؤتى ما قاله واستغنى بهمزة الاستفهام عن همزة الوصل فحذفت «أم اتخذ عند الرحمن عهدا» بأن يؤتى ما قاله.'),
(2329, 19, 79, '«كلا» أي لا يؤتى ذلك «سنكتب» نأمر بكتب «ما يقول ونمدّ له العذاب مدا» نزيده بذلك عذابا فوق عذاب كفره.'),
(2330, 19, 80, '«ونرثه ما يقول» من المال والولد «ويأتينا» يوم القيامة «فردا» لا مال له ولا ولد.'),
(2331, 19, 81, '«واتخذوا» أي كفار مكة «من دون الله» الأوثان «آلهة» يعبدونهم «ليكونوا لهم عزا» شفعاء عند الله بألا يعذبوا.'),
(2332, 19, 82, '(كلا) أي لا مانع من عذابهم (سيكفرون) أي الآلهة (بعبادتهم) أي ينفونها كما في آية أخرى "" ما كانوا إيانا يعبدون "" (ويكونون عليهم ضدا) أعوانا وأعداء.'),
(2333, 19, 83, '«ألم تر أنا أرسلنا الشياطين» سلطانهم «على الكافرين تؤزهم» تهيجهم إلى المعاصي «أزّا».'),
(2334, 19, 84, '«فلا تعجل عليهم» بطلب العذاب «إنما نعد لهم» الأيام والليالي أو الأنفاس «عدا» إلى وقت عذابهم.'),
(2335, 19, 85, 'اذكر «يوم نحشر المتقين» بإيمانهم «إلى الرحمن وفدا» جمع وافد بمعنى: راكب.'),
(2336, 19, 86, '«ونسوق المجرمين» بكفرهم «إلى جهنم وردا» جمع وارد بمعنى ماش عطشان.'),
(2337, 19, 87, '«لا يملكون» أي الناس «الشفاعة إلا من اتخذ عند الرحمن عهدا» أي شهادة أن لا إله إلا الله ولا حول ولا قوة إلا بالله.'),
(2338, 19, 88, '«وقالوا» أي اليهود والنصارى ومن زعم أن الملائكة بنات الله «اتخذ الرحمن ولدا» قال تعالى لهم:'),
(2339, 19, 89, '«لقد جئتم شيئا إدا» أي منكرا عظيما.'),
(2340, 19, 90, '«تكاد» بالتاء والياء «السماوات يتفطرن» بالتاء وتشديد الطاء بالانشقاق وفي قراءة بالنون «منه وتنشق الأرض وتخر الجبال هدّا» أي تنطبق عليهم من أجل.'),
(2341, 19, 91, '«أن دعوا للرحمن ولدا» قال تعالى:'),
(2342, 19, 92, '«وما ينبغي للرحمن أن يتخذ ولدا» أي ما يليق به ذلك.'),
(2343, 19, 93, '«إن» أي ما «كل من في السماوات والأرض إلا آتي للرحمن عبداً» ذليلا خاضعا يوم القيامة منهم عزير وعيسى.'),
(2344, 19, 94, '«لقد أحصاهم وعدهم عدا» فلا يخفى عليه مبلغ جميعهم ولا واحد منهم'),
(2345, 19, 95, '«وكلهم آتيه يوم القيامة فردا» بلا مال ولا نصير يمنعه.'),
(2346, 19, 96, '«إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن وُدّا» فيما بينهم يتوادون ويتحابون ويحبهم الله تعالى.'),
(2347, 19, 97, '«فإنما يسرناه» أي القرآن «بلسانك» العربي «لتبشر به المتقين» الفائزين بالإيمان «وتنذر» تخوف «به قوما لُدّا» جمع ألد أي جدل بالباطل وهم كفار مكة.'),
(2348, 19, 98, '«وكم» أي كثيرا «أهلكنا قبلهم من قرن» أي أمة من الأمم الماضية بتكذيبهم الرسل «هل تحس» تجد «منهم من أحد أو تسمع لهم ركزا» صوتا خفيا؟ لا، فكما أهلكنا أولئك نهلك هؤلاء.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, '«طه» الله أعلم بمراده بذلك.'),
(2350, 20, 2, '«ما أنزلنا عليك القرآن» يا محمد «لتشقى» لتتعب بما فعلت بعد نزوله من طول قيامك بصلاة الليل أي خفف عن نفسك.'),
(2351, 20, 3, '«إلا» لكن أنزلناه «تذكرة» به «لمن يخشى» يخاف الله.'),
(2352, 20, 4, '«تنزيلا» بدل من اللفظ بفعله الناصب له «ممن خلق الأرض والسماوات العلى» جمع عليا ككبرى وكبر.'),
(2353, 20, 5, 'هو «الرحمن على العرش» وهو في اللغة سرير الملك «استوى» اكتفاء يليق به.'),
(2354, 20, 6, '«له ما في السماوات وما في الأرض وما بينهما» من المخلوقات «وما تحت الثرى» هو التراب الندي، والمراد الأرضون السبع لأنها تحته.'),
(2355, 20, 7, '«وإن تجهر بالقول» في ذكر أو دعاء فالله غني عن الجهر به «فإنه يعلم السر وأخفى» منه: أي ما حدثت به النفس وما خطر ولم تحدث به فلا تجهد نفسك بالجهر.'),
(2356, 20, 8, '«الله لا إله إلى هو له الأسماء الحسنى» التسعة والتسعون الوارد بها الحديث والحسنى مؤنث الأحسن.'),
(2357, 20, 9, '«وهل» قد «أتاك حديث موسى».'),
(2358, 20, 10, '«إذا رأى نارا فقال لأهله» لامرأته «امكثوا» هنا، وذلك في مسيره من مدين طالبا مصر «إني آنست» أبصرت «نارا لعلي آتيكم منها بقبس» بشعلة في رأس فتيلة أو عود «أو أجد على النار هدى» أي هاديا يدلني على الطريق وكان أخطأها لظلمة الليل، وقال لعل لعدم الجزم بوفاء الوعد.'),
(2359, 20, 11, '«فلما أتاها» وهي شجرة عوسج «نُوديَ يا موسى».'),
(2360, 20, 12, '«إني» بكسر الهمزة بتأويل نودي بقيل وفتحها بتقدير الباء «أنا» تأكيد لياء المتكلم «ربُّك فاخلع نعليك إنك بالواد المقدس» المطهر أو المبارك «طُوى» بدل أو عطف بيان، بالتنوين وتركه مصروف باعتبار المكان وغير مصروف لتأنيث باعتبار البقعة مع العلمية.'),
(2361, 20, 13, '«وأنا اخترتك» من قومك «فاستمع لما يُوحى» إليك مني.'),
(2362, 20, 14, '«إنني أنا الله لا إله إلا أنا فاعبدني وأقم لصلاة لذكري» فيها.'),
(2363, 20, 15, '«إن الساعة آتية أكاد أخفيها» عن الناس ويظهر لهم قربها بعلاماتها «لتجزى» فيها «كل نفس بما تسعى» به من خير أو شر.'),
(2364, 20, 16, '«فلا يَصُدنَّكَ» يصرفنَّك «عنها» أي عن الإيمان بها «من لا يؤمن بها واتبع هواه» في إنكارها «فَتَردى» أي فتهلك إن صددت عنها.'),
(2365, 20, 17, '«وما تلك» كائنة «بيمينك يا موسى» الاستفهام للتقرير ليرتب عليه المعجزة فيها.'),
(2366, 20, 18, '«قال هي عصاي أتوكَّأ» أعتمد «عليها» عند الوثوب والمشي «وأهش» أخبط ورق الشجر «بها» ليسقط «على غنمي» فتأكله «ولي فيها مآرب» جمع مأربة مثلث الراء أي: حوائج «أخرى» كحمل الزاد والسقاء وطرد الهوام زاد في الجواب بيان حاجاته بها.'),
(2367, 20, 19, '«قال ألقها يا موسى».'),
(2368, 20, 20, '«فألقاها فإذا هي حية» ثعبان عظيم «تسعى» تمشي على بطنها سريعا كسرعة الثعبان الصغير المسمى بالجان المعبر به فيها في آية أخرى.'),
(2369, 20, 21, '«قال خذها ولا تخف» منها «سنعيدها سيرتها» منصوب بنزع الخافض أي: إلى حالتها «الأولى» فأدخل يده في فمها فعادت عصا، فتبين أن موضع الإدخال موضع مسكها بين شعبتيها، وأي ذلك السيد موسى لئلا يجزع إذا انقلبت حية لدى فرعون.'),
(2370, 20, 22, '«واضمم يدك» اليمنى بمعنى الكف «إلى جناحك» أي جنبك الأيسر تحت العضد إلى الإبط وأخرجها «تخرج» خلاف ما كانت عليه من الأدمة «بيضاء من غير سوءٍ» أي بَرَص تضيء كشعاع الشمس تغشى البصر «آية أخرى» وهي بيضاء حالان من ضمير تخرج.'),
(2371, 20, 23, '«لنريك» بها إذا فعلت ذلك لإظهارها «من آياتنا» الآية «الكبرى» أي العظمى على رسالتك، وإذا أراد عودها إلى حالتها الأولى ضمها إلى جناحه كما تقدم وأخرجها.'),
(2372, 20, 24, '«اذهب» رسولا «إلى فرعون» ومن معه «إنه طغى» جاوز الحد في كفره إلى ادعاء الإلهية.'),
(2373, 20, 25, '«قال رب اشرح لي صدري» وسّعه لتحمل الرسالة.'),
(2374, 20, 26, '«ويسِّر» سَهِّلْ «لي أمري» لأبلغها.'),
(2375, 20, 27, '«واحلل عقدة من لساني» حدثت من احتراقه بجمرة وضعها بفيه وهو صغير.'),
(2376, 20, 28, '«يفقهوا» يفهموا «قولي» عند تبليغ الرسالة.'),
(2377, 20, 29, '«واجعل لي وزيرا» معينا عليها «من أهلي».'),
(2378, 20, 30, '«هارون» مفعول ثان «أخي» عطف بيان.'),
(2379, 20, 31, '«اشدد به أزري» ظهري.'),
(2380, 20, 32, '«وأشركه في أمري» أي الرسالة والفعلان بصيغتي الأمر والمضارع المجزوم وهو جواب الطلب.'),
(2381, 20, 33, '«كي نسبحك» تسبيحا «كثيرا».'),
(2382, 20, 34, '«ونذكرك» ذكرا «كثيرا».'),
(2383, 20, 35, '«إنك كنت بصيرا» عالما فأنعمت بالرسالة.'),
(2384, 20, 36, '«قال قد أوتيت سُؤلك يا موسى» منا عليك.'),
(2385, 20, 37, '«ولقد مننا عليك مرة أخرى».'),
(2386, 20, 38, '«إذ» للتعليل «أوحينا إلى أمك» مناما أو إلهاما لما ولدتك وخافت أن يقتلك فرعون في جملة من يولد «ما يوحى» في أمرك ويبدل منه:'),
(2387, 20, 39, '«أن اقذفيه» ألقيه «في التابوت فاقذفيه» بالتابوت «في اليم» بحر النيل «فلْيُلقه اليم بالساحل» أي شاطئه والأمر بمعنى الخبر «يأخذه عدو لي وعدو له» وهو فرعون «وألقيت» بعد أن أخذك «عليك محبة مني» لتحب في الناس فأحبك فرعون وكل من رآك «ولتُصنع على عيني» تربى على رعايتي وحفظي لك.'),
(2388, 20, 40, '«إذ» للتعليل «تمشي أختك» مريم لتتعرف من خبرك وقد أحضروا مراضع وأنت لا تقبل ثدي واحدة منهن «فتقول هل أدلكم على من يكفله» فأجيبت فجاءت بأمه فقبل ثديها «فرجعناك إلى أمك كي تقر عينها» بلقائك «ولا تحزن» حينئذ «وقتلت نفسا» هو القبطي بمصر، فاغتممت لقتله من جهة فرعون «فنجيناك من الغم وفتناك فتونا» اختبرناك بالإيقاع في غير ذلك وخلصناك منه «فلبثت سنين» عشرا «في أهل مدين» بعد مجيئك إليها من مصر عند شعيب النبي وتزوجك بابنته «ثم جئت على قدر» في علمي بالرسالة وهو أربعون سنة من عمرك «يا موسى».'),
(2389, 20, 41, '«واصطنعتك» اخترتك «لنفسي» بالرسالة.'),
(2390, 20, 42, '«اذهب أنت وأخوك» إلى الناس «بآياتي» التسع «ولا تَنِيا» تفترا «في ذكري» بتسبيح وغيره.'),
(2391, 20, 43, '«اذهبا إلى فرعون إنه طغى» بادعائه الربوبية.'),
(2392, 20, 44, '«فقولا له قولا لينا» في رجوعه عن ذلك «لعله يتذكر» يتعظ «أو يخشى» الله فيرجع والترجي بالنسبة إليهما لعلمه تعالى بأنه لا يرجع.'),
(2393, 20, 45, '«قالا ربنا إننا نخاف أن يفرط علينا» أي يعجل بالعقوبة «أو أن يطغى» علينا أي يتكبر.'),
(2394, 20, 46, '«قال لا تخافا إنني معكما» بعوني «أسمع» ما يقول «وأرى» ما يفعل.'),
(2395, 20, 47, '«فأتياه فقولا إنَّا رسولا ربك فأرسل معنا بني إسرائيل» إلى الشام «ولا تعذبهم» أي خل عنهم من استعمالك إياهم في أشغالك الشاقة كالحفر والبناء وحمل الثقيل «قد جئناك بآية» بحجة «من ربك» على صدقنا بالرسالة «والسلام على من اتبع الهدى» أي السلامة له من العذاب.'),
(2396, 20, 48, '«إنا قد أوحي إلينا أن العذاب على مَن كذب» ما جئنا له «وتولى» أعرض عنه، فأتياه وقالا جميع ما ذكر.'),
(2397, 20, 49, '«قال فمن ربكما يا موسى» اقتصر عليه لأنه الأصل ولإدلاله عليه بالتربية.'),
(2398, 20, 50, '«قال ربنا الذي أعطى كل شيء» من الخلق «خلقه» الذي هو عليه متميز به عن غيره «ثم هدى» الحيوان منه إلى مطعمه ومشربه ومنكحه وغير ذلك.'),
(2399, 20, 51, '«قال» فرعون «فما بال» حال «القرون» الأمم «الأولى» كقوم نوح وهود ولوط وصالح في عبادتهم الأوثان.'),
(2400, 20, 52, '«قال» موسى «علمها» أي علم حالهم محفوظ «عند ربي في كتاب» هو اللوح المحفوظ يجازيهم عليها يوم القيامة «لا يضل» يغيب «ربي» عن شيء «ولا ينسى» ربي شيئا.'),
(2401, 20, 53, 'هو «الذي جعل لكم» في جملة الخلق «الأرض مهادا» فراشا «وسلك» سهل «لكم فيها سبلا» طرقا «وأنزل من السماء ماءً» مطرا قال تعالى تتميما لما وصفه به موسى وخطابا لأهل مكة «فأخرجنا به أزواجا» أصنافا «من نبات شتى» صفة أزواجا أي مختلفة الألوان والطعوم وغيرهما، وشتى جمع شتيت كمريض ومرضى، من شت الأمر تفرق.'),
(2402, 20, 54, '«كلوا» منها «وارعوا أنعامكم» فيها جمع نعم، وهي الإبل والبقر والغنم، يقال رعت الأنعام ورعيتها والأمر للإباحة وتذكير النعمة والجملة حال من ضمير أخرجنا، أي مبيحين لكم الأكل ورعي الأنعام «إن في ذلك» المذكور «لآيات» لعبرا «لأولي النهى» لأصحاب العقول ينهى صاحبه عن ارتكاب القبائح.'),
(2403, 20, 55, '«منها» أي من الأرض «خلقناكم» بخلق أبيكم آدم منها «وفيها نعيدكم» مقبورين بعد الموت «ومنها نخرجكم» عند البعث «تارة» مرة «أخرى» كما أخرجناكم عند ابتداء خلقكم.'),
(2404, 20, 56, '«ولقد أريناه» أي أبصرنا فرعون «آياتنا كلها» التسع «فكذب» بها وزعم أنها سحر «وأبى» أن يوحد الله تعالى.'),
(2405, 20, 57, '«قال أجئتنا لتخرجنا من أرضنا» مصر ويكون لك فيها «بسحرك يا موسى».'),
(2406, 20, 58, '«فلنأتينك بسحر مثله» يعارضه «فاجعل بيننا وبينك موعدا» لذلك «لا نخلفه نحن ولا أنت مكانا» منصوب بنزع الخافض في «سِوى» بكسر أوله وضمه أي وسطا تستوي مسافة الجائي إليه من الطرفين.'),
(2407, 20, 59, '«قال» موسى «موعدكم يوم الزينة» يوم عيد لهم يتزينون فيه ويجتمعون «وأن يُحشر الناس» يجمع أهل مصر «ضحى» وقته للنظر فيما يقع.'),
(2408, 20, 60, '«فتولى فرعون» أدير «فجمع كيده» أي ذوي كيده من السحرة «ثم أتى» بهم الموعد.'),
(2409, 20, 61, '«قال لهم موسى» وهم اثنان وسبعون مع كل واحد حبل وعصا «ويلكم» أي ألزمكم الله الويل «لا تفتروا على الله كذبا» بإشراك أحد معه «فيُسحتكم» بضم الياء وكسر الحاء وبفتحهما أي يهلككم «بعذاب» من عنده «وقد خاب» خسر «من افترى» كذب على الله.'),
(2410, 20, 62, '«فتنازعوا أمرهم بينهم» في موسى وأخيه «وأسرُّوا النجوى» أي الكلام بينهم فيهما.'),
(2411, 20, 63, '«قالوا» لأنفسهم «إن هذان» وهو موافق للغة من يأتي في المثنى بالألف في أحواله الثلاث ولأبي عمرو: هذين «لساحران يريدان أن يخرجاكم من أرضكم بسحرهما ويذهبا بطريقتكم المثلى» مؤنث أمثل بمعنى أشرف أي بأشرافكم بميلهم إليهما لغلبتهما.'),
(2412, 20, 64, '«فأجمعوا كيدكم» من السحر بهمزة وصل وفتح الميم من جمع أي لمَّ وبهمزة قطع وكسر الميم من أجمع أحكم «ثم أئتوا صفا» حال أي مصطفين «وقد أفلح» فاز «اليوم من استعلى» غلب.'),
(2413, 20, 65, '«قالوا يا موسى» اختر «إما أن تُلقيَ» عصاك أولا «وإما أن نكون أول من ألقى» عصاه.'),
(2414, 20, 66, '«قال بل ألقوا» فألقوا «فإذا حبالهم وعصيهم» أصله عصوو قبلت الواوان ياءين وكسرت العين والصاد «يخيل إليه من سحرهم أنها» حيات «تسعى» على بطونها.'),
(2415, 20, 67, '«فأوجس» أحس «في نفسه خيفة موسى» أي خاف من جهة أن سحرهم من جنس معجزته أن يتلبس أمره على الناس فلا يؤمنوا به.'),
(2416, 20, 68, '«قلنا» له «لا تخف إنك أنت الأعلى» عليهم بالغلبة.'),
(2417, 20, 69, '«وألقِ ما في يمينك» وهي عصاه «تَلْقَف» تبتلع «ما صنعوا إنما صنعوا كيد ساحر» أي جنسه «ولا يفلح الساحر حيث أتى» بسحره فألقى موسى عصاه فتلقَّفت كل ما صنعوه.'),
(2418, 20, 70, '«فألقي السحرة سجدا» خرّوا ساجدين لله تعالى «قالوا آمنا برب هارون وموسى».'),
(2419, 20, 71, '«قال» فرعون «آمنتم» بتحقيق الهمزتين وإبدال الثانية ألفا «له قبل أن آذن» أنا «لكم إنه لكبيركم» معلمكم «الذي علمكم السحر فلأقطعن أيديكم وأرجلكم من خلاف» حال بمعنى مختلعة أي الأيدي اليمنى والأرجل اليسرى «ولأصلبنكم في جذوع النخل» أي عليها «ولتعلمن أينا» يعني نفسه ورب موسى «أشد عذابا وأبقى» أدوم على مخالفته.'),
(2420, 20, 72, '«قالوا لن نؤثرك» نختارك «على ما جاءنا من البيانات» الدالة على صدق موسى «والذي فطرنا» خلفنا قسم أو عطف على ما «فاقض ما أنت قاض» أي اصنع ما قتله «إنما تقضي هذه الحياة الدنيا» النصب على الاتساع أي فيها وتجزى عليه في الآخرة.'),
(2421, 20, 73, '«إنا آمنا بربنا ليغفر لنا خطايانا» من الإشراك وغيره «وما أكرهتنا عليه من السحر» تعلما وعملاً لمعارضة موسى «والله خير» منك ثوابا إذا أطيع «وأبقى» منك عذابا إذا عصي.'),
(2422, 20, 74, 'قال تعالى «إنه من يأت ربه مجرما» كافرا كفرعون «فإن له جهنم لا يموت فيها» فيستريح «ولا يحيى» حياة تنفعه.'),
(2423, 20, 75, '«ومن يأته مؤمنا قد عمل الصالحات» الفرائض والنوافل «فأولئك لهم الدرجات العلى» جمع مؤنث أعلى.'),
(2424, 20, 76, '«جنات عدن» أي إقامة بيان له «تجري من تحتها الأنهار خالدين فيها وذلك جزاءُ من تزكى» تَطهَّر من الذنوب.'),
(2425, 20, 77, '«ولقد أوحينا إلى موسى أن أسْر بعبادي» بهمزة قطع من أسرى، وبهمز وصل وكسر النون من سرى لغتان أي سر بهم ليلا من أرض مصر «فاضرب لهم» اجعل لهم بالضرب بعصاك «طريقا في البحر يبسا» أي يابسا فامتثل ما أمر به وأيبس الله الأرض فمروا فيها «لا تخاف دَرَكا» أي أن يدركك فرعون «ولا تخشى» غرقا.'),
(2426, 20, 78, '«فأتبعهم فرعون بجنوده» وهو معهم «فغشيهم من اليَمِّ» أي البحر «ما غشيهم» فأغرقهم.'),
(2427, 20, 79, '«وأضل فرعون قومه» بدعائهم إلى عبادته «وما هدى» بل أوقعهم في الهلاك خلاف قوله «وما أهديكم إلا سبيل الرشاد».'),
(2428, 20, 80, '«يا بني إسرائيل قد أنجيناكم من عدوكم» فرعون بإغراقه «ووعدناكم جانب الطور الأيمن» فنؤتي موسى التوراة للعمل بها «ونزلنا عليكم المن والسلوى» الترنجبين والطير السمانى بتخفيف الميم والقصر، والمنادى من وُجد من اليهود زمن النبي صلى الله عليه وسلم وخُوطبوا لما أنعم الله به على أجدادهم زمن النبي موسى توطئة لقوله تعالى لهم:'),
(2429, 20, 81, '«كلوا من طيبات ما رزقناكم» أي المنعم به عليكم «ولا تطغوا فيه» بأن تكفروا النعمة به «فيحل عليكم غضبي» بكسر الحاء: أي يجب وبضمها أي ينزل «ومن يحلل عليه غضبي» بكسر اللام وضمها «فقد هوى» سقط في النار.'),
(2430, 20, 82, '«وإني لغفار لمن تاب» من الشرك «وآمن» وحَّد الله «وعمل صالحا» يصدق بالفرض والنفل «ثم اهتدى» باستمراره على ما ذكر إلى موته.'),
(2431, 20, 83, '«وما أعجلك عن قومك» لمجيء ميعاد أخذ التوراة «يا موسى».'),
(2432, 20, 84, '«قال هم أولاء» أي بالقرب مني يأتون «على أثري وعجلت إليك رب لترضى» عني أي زيادة في رضاك وقبل الجواب أتى بالاعتذار حسب ظنه، وتخلف المظنون لما:'),
(2433, 20, 85, '«قال» تعالى «فإنا قد فتنا قومك من بعدك» أي بعد فراقك لهم «وأضلهم السامري» فعبدوا العجل.'),
(2434, 20, 86, '«فرجع موسى إلى قومه غضبان» من جهتهم «أسفا» شديد الحزن «قال يا قوم ألم يعدكم ربكم وعدا حسنا» أي صدقا أنه يعطيكم التوراة «أفطال عليكم العهد» مدة مفارقتي إياكم «أم أردتم أن يحل» يجب «عليكم غضب من ربكم» بعبادتكم العجل «فأخلفتم موعدي» وتركتم المجيء بعدي.'),
(2435, 20, 87, '«قالوا ما أخلفنا موعدك بملكنا» مثلث الميم أي بقدرتنا أو أمرنا «ولكنا حَمَلْنا» بفتح الحاء مخففا وبضمها وكسر الميم مشددا «أوزارا» أثقالاً «من زينة القوم» أي قوم فرعون، استعارها بنو إسرائيل بعلَّة عرس فبقيت عندهم «فقذفناها» طرحناها في النار بأمر السامري «فكذلك» كما ألقينا «ألقى السامري» ما معه من حليهم، ومن التراب الذي أخذه من أثر حافر فرس جبريل على الوجه الآتي:'),
(2436, 20, 88, '«فأخرج لهم عجلاً» صاغه من الحلي «جسدا» لحما ودما «له خوار» أي صوت يُسمع أي انقلب كذلك بسبب التراب الذي أثره الحياة فيما يوضع ووضعه بعد صوغه في فمه «فقالوا» أي السامري وأتباعه «هذا إلهكم وإله موسى فنسيَ» موسى ربه هنا وذهب يطلبه قال تعالى:'),
(2437, 20, 89, '(أفلا يرون أ) أن مخففة من الثقيلة واسمها محذوف أي أنه (لا يرجع) العجل (إليهم قولاً) أي لا يرد لهم جوابا (ولا يملك لهم ضرا) أي دفعه (ولا نفعا) أي جلبه أي فكيف يتخذ إلها؟'),
(2438, 20, 90, '«ولقد قال لهم هارون من قبل» أي قبل أن يرجع موسى «يا قوم إنما فُتنْتم به وإنَّ ربكم الرحمن فاتَّبعوني» في عبادته «وأطيعوا أمري» فيها.'),
(2439, 20, 91, '«قالوا لن نبرح» نزال «عليه عاكفين» على عبادته مقيمين «حتى يرجع إلينا موسى».'),
(2440, 20, 92, '«قال» موسى بعد رجوعه «يا هارون ما منعك إذ رأيتهم ضلوا» بعبادته.'),
(2441, 20, 93, '«أ» ن «لا تتبعن» لا زائدة «أفعصيت أمري» بإقامتك بين من يعبد غير الله تعالى.'),
(2442, 20, 94, '«قال» هارون «يا ابن أم» بكسر الميم وفتحها أراد أمي وذكرها أَعْطَف لقلبه «لا تأخذ بلحيتي» وكان أخذها بشماله «ولا برأسي» وكان أخذ شعره بيمينه غضبا «إني خشيت» لو اتبعك ولا بد أن يتبعني جمع ممن لم يعبدوا العجل «أن تقول فرقت بين بني إسرائيل» وتغضب عليَّ «ولم ترقب» تنتظر «قولي» فيما رأيته في ذلك.'),
(2443, 20, 95, '«قال فما خطبك» شأنك الداعي إلى ما صنعت «يا سامري».'),
(2444, 20, 96, '«قال بصرت بما لم يبصروا به» بالياء والتاء أي علمت ما لم يعلموه «فقبضت قبضة من» تراب «أثر» حافر فرس «الرسول» جبريل «فنبذتها» ألقيتها في صورة العجل المصاغ «وكذلك سولت» زينت «لي نفسي» وألقى فيها أن أخذ قبضة من تراب ما ذكر، وألقيها على ما لا روح، به يصير له روح ورأيت قومك طلبوا منك أن تجعل لهم إلها فحدثتني نفسي أن يكون ذلك العجل إلههم.'),
(2445, 20, 97, '«قال» له موسى «فاذهب» من بيننا «فإن لك في الحياة» أي مدة حياتك «أن تقول» لمن رأيته «لا مساس» أي لا تقربني فكان بهم في البرية وإذا مس أحدا أو مسه أحد حُمَّا جميعا «وإن لك موعدا» لعذابك «لن تخلفه» بكسر اللام: أي لن تغيب عنه، وبفتحها أي بل تبعث إليه «وانظر إلى إلهك الذي ظلْت» أصله ظللت بلامين أولاهما مكسورة حذفت تخفيفا أي دمت «عليه عاكفا» أي مقيما تعبده «لنحرقنه» بالنار «ثم لننسفنه في اليمّ نسفا» نذرينه في هواء البحر، وفعل موسى بعد ذبحه ما ذكره.'),
(2446, 20, 98, '«إنما إلهكم الله الذي لا اله إلا هو وسع كل شيء علما» تمييز محول عن الفاعل وسع علمه كل شيء.'),
(2447, 20, 99, '«كذلك» أي كما قصصنا عليك يا محمد هذه القصة «نقص عليك من أنباء» أخبار «ما قد سبق» من الأمم «وقد آتيناك» أعطيناك «من لدنا» من عندنا «ذكرا» قرآنا.'),
(2448, 20, 100, '«من أعرض عنه» فلم يؤمن به «فإنه يحمل يوم القيامة وزرا» حملا ثقيلا من الإثم.'),
(2449, 20, 101, '«خالدين فيه» أي في عذاب الوزر «وساء لهم يوم القيامة حملا» تمييز مفسر للضمير في ساء والمخصوص بالذم محذوف تقديره وزرهم، واللام للبيان ويبدل من يوم القيامة.'),
(2450, 20, 102, '«يوم ننفخ في الصور» القرن، النفخة الثانية «ونحشر المجرمين» الكافرين «يومئذ زرقا» عيونهم مع سواد وجوههم.'),
(2451, 20, 103, '«يتخافتون بينهم» يتسارون «إن» ما «لبثتم» في الدنيا «إلا عشرا» من الليالي بأيامها.'),
(2452, 20, 104, '«نحن أعلم بما يقولون» في ذلك: أي ليس كما قالوا «إذ يقول أمثلهم» أعدلهم «طريقة» فيه «إن لبثتم إلا يوما» يستقلون لبثهم في الدنيا جدا بما يعاينونه في الآخرة من أهوالها.'),
(2453, 20, 105, '«ويسألونك عن الجبال» كيف تكون يوم القيامة «فقل» لهم «ينسفها ربي نسفا» بأن يفتتها كالرمل السائل ثم يطيرها بالرياح.'),
(2454, 20, 106, '«فيذرها قاعا» منبسطا «صفصفا» مستويا.'),
(2455, 20, 107, '«لا ترى فيها عوجا» انخفاضا «ولا أمْتا» ارتفاعا.'),
(2456, 20, 108, '«يومئذ» أي يوم إذ نسفت الجبال «يتبعون» أي الناس بعد القيام من القبور «الداعي» إلى المحشر بصوته وهو إسرافيل يقول: هلموا إلى عرض الرحمن «لا عوج له» أي لاتباعهم: أي لا يقدرون أن لا يتعبوا «وخشعت» سكنت «الأصوات للرحمن فلا تسمع إلا همسا» صوت وطء الأقدام في نقلها إلى المحشر كصوت أخفاف الإبل في مشيها.'),
(2457, 20, 109, '«يومئذ لا تنفع الشفاعة» أحداً «إلا من أذن له الرحمن» أن يشفع له «ورضي له قولا» بأن يقول: لا إله إلا الله.'),
(2458, 20, 110, '«يعلم ما بين أيديهم» من أمور الآخرة «وما خلفهم» من أمور الدنيا «ولا يحيطون به علماً» لا يعلمون ذلك.'),
(2459, 20, 111, '«وعنت الوجوه» خضعت «للحي القيوم» أي الله «وقد خاب» خسر «من حَمَلَ ظلماً» أي شركاً.'),
(2460, 20, 112, '«ومن يعمل من الصالحات» الطاعات «وهو مؤمن فلا يخاف ظلماً» بزيادة في سيئاته «ولا هضماً» بنقص من حسناته.'),
(2461, 20, 113, '«وكذلك» معطوف على كذلك نقص: أي مثل إنزال ما ذكر «أنزلناه» أي القرآن «قرآناً عربيا وصرفنا» كررنا «فيه من الوعيد لعلهم يتقون» الشرك «أو يُحدث» القرآن «لهم ذكراً» بهلاك من تقدمهم من الأمم فيعتبرون.'),
(2462, 20, 114, '«فتعالى الله الملك الحق» عما يقول المشركون «ولا تعجل بالقرآن» أي بقراءته «من قبل أن يُقضى إليك وحيه» أي يفرغ جبريل من إبلاغه «وقل ربّ زدني علما» أي بالقرآن فكلما أنزل عليه منه زاد به علمه.'),
(2463, 20, 115, '«ولقد عهدنا إلى آدم» وصيناه أن لا يأكل من الشجرة «من قبل» أي قبل أكله منها «فنسي» ترك عهدنا «ولم نجد له عزماً» حزماً وصبراً عما نهيناه عنه.'),
(2464, 20, 116, '(و) اذكر (إذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس) وهو أبو الجن كان يصحب الملائكة ويعبد الله معهم (أبى) عن السجود لآدم "" قال أنا خير منه "".'),
(2465, 20, 117, '«فقلنا يا آدم إنَّ هذا عدو لك ولزوجك» حواء بالمد «فلا يخرجنكما من الجنة فتشقى» تتعب بالحرث والزرع والحصد والطحن والخبز وغير ذلك واقتصر على شقائه لأن الرجل يسعى على زوجته.'),
(2466, 20, 118, '«إن لك أ» «لا تجوع فيها ولا تعرى».'),
(2467, 20, 119, '«وأنك» بفتح الهمزة وكسرها عطف على اسم إن وجملتها «لا تظمأ فيها» تعطش «ولا تضحى» لا يحصل لك حر شمس الضحى لانتفاء الشمس في الجنة.'),
(2468, 20, 120, '«فوسوس إليه الشيطان قال يا آدم هل أدلك على شجرة الخلد» أي التي يخلد من يأكل منها «ومُلْك لا يبلى» لا يفنى وهو لازم الخلد.'),
(2469, 20, 121, '«فأكلا» أي آدم وحواء «منها فبدت لهما سوآتهما» أي ظهر لكل منهما قبله وقبل الآخر ودُبره وسمي كل منهما سوأة لأن انكشافه يسوء صاحبه «وطفقا يخصفان» أخذا يلزقان «عليهما من ورق الجنة» ليستترا به «وعصى آدم ربه فغوى» بالأكل من الشجرة.'),
(2470, 20, 122, '«ثم اجتباه ربه» قربه «فتاب عليه» قبل توبته «وهدى» أي هداه إلى المداومة على التوبة.'),
(2471, 20, 123, '«قال اهبطا» أي آدم وحواء بما اشمتلنا عليه من ذريتكما «منها» من الجنة «جميعاً بعضكم» بعض الذرية «لبعض عدو» من ظلم بعضهم بعضاً «فإما» فيه إدغام نون إن الشرطية في ما المزيدة «يأتينكم مني هدى فمن اتبع هدايَ» القرآن «فلا يضل» في الدنيا «ولا يشقى» في الآخرة.'),
(2472, 20, 124, '«ومن أعرض عن ذكري» القرآن فلم يؤمن به «فإن له معيشة ضنكاً» بالتنوين مصدر بمعنى ضيقة، وفسرت في حديث بعذاب الكافر في قبره «ونحشره» أي المعرض عن القرآن «يوم القيامة أعمى» أعمى البصر.'),
(2473, 20, 125, '«قال ربِّ لمَ حشرتني أعمى وقد كنت بصيراً» في الدنيا وعند البعث.'),
(2474, 20, 126, '«قال» الأمر «كذلك أتتك آياتنا فنسيتها» تركتها ولم تؤمن بها «وكذلك» مثل نسيانك آياتنا «اليوم تنسى» تترك في النار.'),
(2475, 20, 127, '«وكذلك» ومثل جزائنا من أعرض عن القرآن «نجزي من أسرف» أشرك «ولم يؤمن بآيات ربه ولعذاب الآخرة أشد» من عذاب الدنيا وعذاب القبر «وأبقى» أدوم.'),
(2476, 20, 128, '«أفلم يهد» يتبين «لهم» لكفار مكة «كم» خبرية مفعول «أهلكنا» أي كثيرا إهلاكنا «قبلهم من القرون» أي الأمم الماضية بتكذيب الرسل «يمشون» حال من ضمير لهم «في مساكنهم» في سفرهم إلى الشام وغيرها فيعتبروا، وما ذكر من أخذ إهلاك من فعله الخالي عن حرف مصدري لرعاية المعنى لا مانع منه «إنَّ في ذلك لآيات» لعبراً «لأولي النهى» لذوي العقول.'),
(2477, 20, 129, '«ولولا كلمة سبقت من ربك» بتأخير العذاب عنهم إلى الآخرة «لكان» الإهلاك «لزاماً» لازماً لهم في الدنيا «وأجل مسمى» مضروب لهم معطوف على الضمير المستتر في كان وقام الفصل بخبرها مكان التأكيد.'),
(2478, 20, 130, '«فاصبر على ما يقولون» منسوخ بآية القتال «وسبّح» صلّ «بحمد ربك» حال: أي ملتبساً به «قبل طلوع الشمس» صلاة الصبح «وقبل غروبها» صلاة العصر «ومن آناء الليل» ساعاته «فسبح» صل المغرب والعشاء «وأطراف النهار» عطف على محل من آناء المنصوب: أي صل الظهر لأن وقتها يدخل بزوال الشمس، فهو طرف النصف الأول وطرف النصف الثاني «لعلك ترضى» بما تعطى من الثواب.'),
(2479, 20, 131, '«ولا تمدنَّ عينيك إلى ما متَّعنا به أزواجاً» أصنافا «منهم زهرة الحياة الدنيا» زينتها وبهجتها «لنفتنهم فيه» بأن يطغوا «ورزق ربك» في الجنة «خير» مما أوتوه في الدنيا «وأبقى» أدوم.'),
(2480, 20, 132, '«وأمر أهلك بالصلاة واصطبر» اصبر «عليها لا نسألك» نكلفك «رزقاً» لنفسك ولا لغيرك «نحن نرزقك والعاقبة» الجنة «للتقوى» لأهلها.'),
(2481, 20, 133, '«وقالوا» المشركون «لولا» هلا «يأتينا» محمد «بآية من ربه» مما يقترحونه «أو لم تأتيهم» بالتاء والياء «بينة» بيان «ما في الصحف الأولى» المشتمل عليه القرآن من أنباء الأمم الماضية وإهلاكهم بتكذيب الرسل.'),
(2482, 20, 134, '«ولو أنا أهلكناهم بعذاب من قبله» قبل محمد الرسول «لقالوا» يوم القيامة «ربنا لولا» هلا «أرسلت إلينا رسولاً فنتبع آياتك» المرسل بها «من قبل أن نذل» في القيامة «ونخزى» في جهنم.'),
(2483, 20, 135, '«قل» لهم «كل» منا ومنكم «متربص» منتظر ما يؤول إليه الأمر «فتربصوا فستعلمون» في القيامة «مَن أصحاب الصراط» الطريق «السويّ» المستقيم «ومن اهتدى» من الضلالة أنحن أم أنتم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, '«اقترب» قرب «للناس» أهل مكة منكري البعث «حسابهم» يوم القيامة «وهم في غفلة» عنه «معرضون» عن التأهب له بالإيمان.'),
(2485, 21, 2, '«ما يأتيهم من ذكر من ربهم محدث» شيئاً فشيئاً أي لفظ قرآن «إلا استمعوه وهم يلعبون» يستهزئون.'),
(2486, 21, 3, '«لاهية» غافلة «قلوبهم» عن معناه «وأسَرُّوا النجوى» الكلام «الذين ظلموا» بدل من واو «وأسروا النجوى الذين ظلموا» «هلْ هذا» أي محمد «إلا بشر مثلكم» فما يأتي به سحر «أفتأتون السحر» تتبعونه «وأنتم تبصرون» تعلمون أنه سحر.'),
(2487, 21, 4, '«قال» لهم «ربي يعلم القول» كائناً «في السماء والأرض وهو السميع» لما أسروه «العليم» به.'),
(2488, 21, 5, '«بل» للانتقال من غرض إلى آخر في المواضع الثلاثة «قالوا» فيما أتى به من القرآن هو «أضغاث أحلام» أخلاط رآها في النوم «بل افتراه» اختلقه «بل هو شاعر» فما أتى به شعر «فليأتنا بآية كما أرسل الأولون» كالناقة والعصا واليد قال تعالى:'),
(2489, 21, 6, '«ما آمنت قبلهم من قرية» أي أهلها «أهلكناها» بتكذيبها ما أتاها من الآيات «أفهم يؤمنون» لا.'),
(2490, 21, 7, '«وما أرسلنا قبلك إلا رجالاً نوحي» وفي قراءة بالياء وفتح الحاء «إليهم» لا ملائكة «فاسألوا أهل الذكر» العلماء بالتوراة والإنجيل «إن كنتم لا تعلمون» ذلك فإنهم يعلمونه، وأنتم إلى تصديقهم أقرب من تصديق المؤمنين بمحمد.'),
(2491, 21, 8, '«وما جعلناهم» أي الرسل «جسداً» بمعنى أجساداً «لا يأكلون الطعام» بل يأكلونه «وما كانوا خالدين» في الدنيا.'),
(2492, 21, 9, '«ثم صدقناهم الوعد» بإنجائهم «فأنجيناهم ومن نشاء» المصدقين لهم «وأهلكنا المسرفين» المكذبين لهم.'),
(2493, 21, 10, 'لقد أنزلنا إليكم» يا معشر قريش «كتاباً فيه ذكركم» لأنه بلغتكم «أفلا تعقلون» فتؤمنون به.'),
(2494, 21, 11, '«وكم قصمنا» أهلكنا «من قرية» أي أهلها «كانت ظالمة» كافرة «وأنشأنا بعدها قوماً آخرين».'),
(2495, 21, 12, '«فلما أحسُّوا بأسنا» شعر أهل القرية بالإهلاك «إذا هم منها يركضون» يهربون مسرعين.'),
(2496, 21, 13, 'فقالت لهم الملائكة استهزاء «لا تركضوا وارجعوا إلى ما أترفتم» نعمتم «فيه ومساكنكم لعلكم تسألون» شيئاً من دنياكم على العادة.'),
(2497, 21, 14, '«قالوا يا» للتنبيه «ويلنا» هلاكنا «إنا كنا ظالمين» بالكفر.'),
(2498, 21, 15, '«فما زالت تلك» الكلمات «دعواهم» يدعون بها ويرددونها «حتى جعلناهم حصيداً» كالزرع المحصود بالمناجل بأن قتلوا بالسيف «خامدين» ميتين كخمود النار إذا طفئت.'),
(2499, 21, 16, '«وما خلقنا السماء والأرض وما بينهما لاعبين» عابثين بل دالين على قدرتنا ونافعين عبادنا.'),
(2500, 21, 17, '«لو أردنا أن نتخذ لهواً» ما يلهى به من زوجة أو ولد «لاتخذناه من لدنا» من عندنا من الحور العين والملائكة «إن كنا فاعلين» ذلك، لكنا لم نفعله فلم نُرده.'),
(2501, 21, 18, '«بل نقذف» نرمي «بالحق» الإيمان «على الباطل» الكفر «فيدمغه» يذهبه «فإذا هو زاهق» ذاهب، ودمغه في الأصل: أصاب دماغه بالضرب وهو مقتل «ولكم» يا كفار مكة «الويْل» العذاب الشديد «مما تصفون» الله به من الزوجة أو الولد.'),
(2502, 21, 19, '«وله» تعالى «من في السماوات والأرض» ملكاً «ومن عنده» أي الملائكة مبتدأ خبره «لا يستكبرون عن عبادته ولا يستحسرون» لا يعيون.'),
(2503, 21, 20, '«يسبحون الليل والنهار لا يفترون» عنه فهو منهم كالنفس منا لا يشغلنا عنه شاغل.'),
(2504, 21, 21, '«أم» بمعني بل للانتقال والهمزه للإنكار «اتخذوا آلهة» كائنة «من الأرض» كحجر وذهب وفضة «هم» أي الآلهة «ينشرون» أي يحيون الموتى؟ لا، ولا يكون إلهاً إلا من يحيي الموتى.'),
(2505, 21, 22, '«لو كان فيهما» أي السماوات والأرض «آلهة إلا الله» أي غيره «لفسدتا» أي خرجتا عن نظامهما المشاهد، لوجود التمانع بينهم على وفق العادة عند تعدد الحاكم من التمانع في الشيء وعدم الاتفاق عليه «فسبحان» تنزيه «الله رب» خالق «العرش» الكرسي «عما يصفون» الكفار الله به من الشريك له وغيره.'),
(2506, 21, 23, '«لا يُسأل عما يفعل وهم يسألون» عن أفعالهم.'),
(2507, 21, 24, '«أم اتخذوا من دونه» تعالى أي سواه «آلهة» فيه استفهام توبيخ «قل هاتوا برهانكم» على ذلك ولا سبيل إليه «هذا ذكر من معي» أمتي وهو القرآن «وذكر من قبلي» من الأمم وهو التوراة والإنجيل وغيرهما من كتب الله ليس في واحد منها أن مع الله إلهاً مما قالوا، تعالى عن ذلك «بل أكثرهم لا يعلمون الحق» توحيد الله «فهم معرضون» عن النظر الموصل إليه.'),
(2508, 21, 25, '«وما أرسلنا من قبلك من رسول إلا نوحي» وفي قراءة بالياء وفتح الحاء «إليه أنه لا إله إلا أنا فاعبدون» أي وحدوني.'),
(2509, 21, 26, '«وقالوا اتخذ الرحمن ولداً» من الملائكة «سبحانه بل» هم «عباد مكرمون» عنده والعبودية تنافي الولادة.'),
(2510, 21, 27, '«لا يسبقونه بالقول» لا يأتون بقولهم إلا بعد قوله «وهم بأمره يعملون» أي بعده.'),
(2511, 21, 28, '«يعلم ما بين أيديهم وما خلفهم» ما عملوا وما هم عاملون «ولا يشفعون إلا لمن ارتضى» تعالى أن يشفع له «وهم من خشيته» تعالى «مشفقون» خائفون.'),
(2512, 21, 29, '«ومن يقل منهم إني إله من دونه» أي الله أي غيره، وهو إبليس دعا إلى عبادة نفسه وأمر بطاعتها «فذلك نجزيه جهنم كذلك» كما نجريه «نجزي الظالمين» المشركين.'),
(2513, 21, 30, '«أوَلم» بواو وتركها «يرَ» يعلم «الذين كفروا أن السماوات والأرض كانتا رَتقاً» سدا بمعنى مسدودة «ففتقناهما» جعلنا السماء سبعاً والأرض من سبعاً، أو فتق السماء أن كانت لا تمطر فأمطرت، وفتق الأرض أن كانت لا تنبت فأنبتت «وجعلنا من الماء» النازل من السماء والنابع من الأرض «كل شيء حي» من نبات وغيره أي فالماء سبب لحياته «أفلا يؤمنون» بتوحيدي.'),
(2514, 21, 31, '«وجعلنا في الأرض رواسي» جبالاً ثوابت لـ «أن» لا «تميد» تتحرك «بهم وجعلنا فيها» الرواسي «فجاجاً» مسالك «سبلاً» بدل، طرقاً نافذة واسعة «لعلهم يهتدون» إلى مقاصدهم في الأسفار.'),
(2515, 21, 32, '«وجلعنا السماء سقفاً» للأرض كالسقف للبيت «محفوظاً» عن الوقوع «وهم عن آياتها» من الشمس والقمر والنجوم «معرضون» لا يتفكرون فيها فيعلمون أن خالقها لا شريك له.'),
(2516, 21, 33, '«وهو الذي خلق الليل والنهار والشمس والقمر كل» تنوينه عوض عن المضاف إليه من الشمس والقمر وتابعه وهو النجوم «في فلك» مستدير كالطاحونة في السماء «يسبحون» يسيرون بسرعة كالسابح في الماء، وللتشبيه به أتى بضمير جمع من يعقل.'),
(2517, 21, 34, 'ونزل لما قال الكفار إنَّ محمداً سيموت: «وما جعلنا لبشر من قبلك الخلد» البقاء في الدنيا «أفإن مت فهم الخالدون» فيها؟ لا، فالجملة الأخيرة محل الاستفهام الإنكاري.'),
(2518, 21, 35, '«كل نفس ذائقة الموت» في الدنيا «ونبلوكم» نختبركم «بالشر والخير» كفقر وغنى وسقم وصحة «فتنة» مفعول له، أي لننظر أتصبرون وتشكرون أم لا «وإلينا ترجعون» فنجازيكم.'),
(2519, 21, 36, '«وإذا رآك الذين كفروا إن» ما «يتخذونك إلا هزواً» أي مهزوءاً به يقولون «أهذا الذي يذكر آلهتكم» أي يعيبها «وهم بذكر الرحمن» لهم «هم» تأكيد «كافرون» به إذا قالوا ما نعرفه.'),
(2520, 21, 37, 'ونزل في استعجالهم العذاب «خلق الإنسان من عجل» أي أنه لكثرة عجله في أحواله كأنه خلق منه «سأريكم آياتي» مواعيدي بالعذاب «فلا تستعجلون» فيه فأراهم القتل ببدر.'),
(2521, 21, 38, '«ويقولون متى هذا الوعد» بالقيامة «إن كنتم صادقين» فيه.'),
(2522, 21, 39, 'قال تعالى: «لو يعلم الذين كفروا حين لا يكفون» يدفعون «عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون» يمنعون منها في القيامة وجواب لو ما قالوا ذلك.'),
(2523, 21, 40, '«بل تأتيهم» القيامة «بغتة فتبهتهم» تحيرهم «فلا يستطيعون ردها ولا هم ينظرون» يمهلون لتوبة أو معذرة.'),
(2524, 21, 41, '«ولقد استهزئ برسل من قبلك» فيه تسلية للنبي صلى الله عليه وسلم «فحاق» نزل «بالذين سخروا منهم ما كانوا به يستهزءون» وهو العذاب فكذا يحيق بمن استهزأ بك.'),
(2525, 21, 42, '«قل» لهم «من يكلؤكم» يحفظكم «بالليل والنهار من الرحمن» من عذابه إن نزل بكم، أي: لا أحد يفعل ذلك، والمخاطبون لا يخافون عذاب الله لإنكارهم له «بل هم عن ذكر ربهم» أي القرآن «معرضون» لا يتفكرون فيه.'),
(2526, 21, 43, '«أم» فيها معنى الهمزة للإنكار: أي أ «لهم آلهة تمنعهم» مما يسوؤهم «من دوننا» أي ألَهم من يمنعهم منه غيرنا؟ لا «لا يستطيعون» أي الآلهة «نصر أنفسهم» فلا ينصرونهم «ولا هم» أي الكفار «منا» من عذابنا «يصحبون» يجارون، يقال صحبك الله: أي حفظك وأجارك.'),
(2527, 21, 44, '«بل متعنا هؤلاء وآباءهم» بما أنعمنا عليهم «حتى طال عليهم العمر» فاغتروا بذلك «أفلا يرون أنا نأتي الأرض» نقصد أرضهم «ننقصها من أطرافها» بالفتح على النبي «أفهم الغالبون»؟ لا، بل النبي وأصحابه.'),
(2528, 21, 45, '«قل» لهم «إنما أنذركم بالوحي» من الله لا من قبل نفسي «ولا يسمع الصم الدعاء إذا» بتحقيق الهمزتين وتسهيل الثانية بينها وبين الياء «ما ينذرون» هم لتركهم العمل بما سمعوه من الإنذار كالصم.'),
(2529, 21, 46, '«ولئن مستهم نفحة» وقعة خفيفة «من عذاب ربك ليقولن يا» للتنبيه «ويلنا» هلاكنا «إنا كنا ظالمين» بالإشراك وتكذيب محمد.'),
(2530, 21, 47, '«ونضع الموازين القسط» ذوات العدل «ليوم القيامة» أي فيه «فلا تظلم نفس شيئاً» من نقص حسنة أو زيادة سيئة «وإن كان» العمل «مثقال» زنة «حبة من خردل أتينا بها» بموزونها «وكفى بنا حاسبين» مُحْصين كل شيء.'),
(2531, 21, 48, '«ولقد آتينا موسى وهارون الفرقان» أي التوراة الفارقة بين الحق والباطل والحلال والحرام «وضياءً» بها «وذكراً» عظة بها «للمتقين».'),
(2532, 21, 49, '«الذين يخشون ربهم بالغيب» عن الناس أي في الخلاء عنهم «وهم من الساعة» أي أهوالها «مشفقون» خائفون.'),
(2533, 21, 50, '«وهذا» أي القرآن «ذكر مبارك أنزلناه أفأنتم له منكرون» الاستفهام فيه للتوبيخ.'),
(2534, 21, 51, '«ولقد آتينا إبراهيم رشده من قبل» أي هداه قبل بلوغه «وكنا به عالمين» بأنه أهل لذلك.'),
(2535, 21, 52, '«إذ قال لأبيه وقومه ما هذه التماثيل» الأصنام «التي أنتم لها عاكفون» أي على عبادتها مقيمون.'),
(2536, 21, 53, '«قالوا وجدنا آباءنا لها عابدين» فاقتدنيا بهم.'),
(2537, 21, 54, '«قال» لهم «لقد كنتم أنتم وآباؤكم» بعبادتها «في ضلال مبين» بَيّن.'),
(2538, 21, 55, '«قالوا أجئتنا بالحق» في قولك هذا «أم أنت من اللاعبين» فيه.'),
(2539, 21, 56, '«قال بل ربكم» المستحق للعبادة «رب» مالك «السماوات والأرض الذي فطرهن» خلقهن على غير مثال سبق «وأنا على ذلكم» الذي قلته «من الشاهدين» به.'),
(2540, 21, 57, '«وتالله لأكيدن أصنامهم بعد أن تولوا مدبرين».'),
(2541, 21, 58, '«فجعلهم» بعد ذهابهم إلى مجتمعهم في يوم عيد لهم «جُذاذاً» بضم الجيم وكسرها: فتاتاً بفأس «إلا كبيراً لهم» علق الفأس في عنقه «لعلهم إليه» أي إلى الكبير «يرجعون» فيرون ما فعل بغيره.'),
(2542, 21, 59, '«قالوا» بعد رجوعهم ورؤيتهم ما فعل «من فعل هذا بآلهتنا إنه لمن الظالمين» فيه.'),
(2543, 21, 60, '«قالوا» أي بعضهم لبعض «سمعنا فتى يذكرهم» أي يعيبهم «يقال له إبراهيم».'),
(2544, 21, 61, '«قالوا فأتوا به على أعين الناس» أي ظاهراً «لعلهم يشهدون» عليه أنه الفاعل.'),
(2545, 21, 62, '«قالوا» له بعد إتيانه «أأنت» بتحقيق الهمزتين وإبدال الثانية ألفاً وتسهيلها وإدخال ألف بين المسهلة والأخرى وتركه «فعلت هذا بآلهتنا يا إبراهيم».'),
(2546, 21, 63, '«قال» ساكتاً عن فعله «بل فعله كبيرهم هذا فاسألوهم» عن فاعله «إن كانوا ينطقون» فيه تقديم جواب الشرط وفيها قبله تعرض لهم بأن الصنم المعلوم عجزه عن الفعل لا يكون إلهاً.'),
(2547, 21, 64, '«فرجعوا إلى أنفسهم» بالتفكر «فقالوا» لأنفسهم «إنكم أنتم الظالمون» بعبادتكم من لا ينطق.'),
(2548, 21, 65, '«ثم نكسوا» من الله «على رءُوسهم» أي ردوا إلى كفرهم وقالوا والله «لقد علمتَ ما هؤلاء ينطقون» أي فكيف تأمرنا بسؤالهم.'),
(2549, 21, 66, '«قال أفتعبدون من دون الله» أي بدله «ما لا ينفعكم شيئاً» من رزق وغيره «ولا يضركم» شيئاً إذا لم تعبدوه.'),
(2550, 21, 67, '«أف» بكسر الفاء وفتحها بمعنى مصدر أي نتناً وقبحاً «لكم ولما تعبدون من دون الله» أي غيره «أفلا تعقلون» أن هذه الأصنام لا تستحق العبادة ولا تصلح لها، وإنما يستحقها الله تعالى.'),
(2551, 21, 68, '«قالوا حرِّقوه» أي إبراهيم «وانصروا آلهتكم» أي بتحريقه «إن كنتم فاعلين» نصرتها فجمعوا له الحطب الكثير وأضرموا النار في جميعه وأوثقوا إبراهيم وجعلوه في منجنيق ورموه في النار قال تعالى:'),
(2552, 21, 69, '(قلنا يا نار كوني برداً وسلاماً على إبراهيم) فلم تحرق منه غير وثاقه، وذهبت حرارتها وبقيت إضاءتها وبقوله "" وسلاماً "" سلم من الموت ببردها.'),
(2553, 21, 70, '«وأرادوا به كيداً» وهو التحريق «فجعلناهم الأخسرين» في مرادهم.'),
(2554, 21, 71, '«ونجيناه ولوطاً» ابن أخيه هاران من العراق «إلى الأرض التي باركنا فيها للعالمين» بكثرة الأنهار والأشجار وهي الشام نزل إبراهيم بفلسطين ولوط بالمؤتفكة وبينهما يوم.'),
(2555, 21, 72, '«ووهبنا له» أي لإبراهيم وكان سأل ولداً كما ذكر في الصافات «إسحاق ويعقوب نافلة» أي زيادة على المسؤول أو هو ولد الولد «وكلاً» أي هو وولداه «جعلنا صالحين» أنبياء.'),
(2556, 21, 73, '«وجعلناهم أئمة» بتحقيق الهمزتين وإبدال الثانية ياء يقتدى بهم في الخير «يهدون» الناس «بأمرنا» إلى ديننا «وأوحينا إليهم فعل الخيرات وإقام الصلاة وإيتاء الزكاة» أي أن تفعل وتقام وتؤتى منهم ومن أتباعهم، وحذف هاء إقامة تخفيف «وكانوا لنا عابدين».'),
(2557, 21, 74, '«ولوطاً آتيناه حكماً» فصلاً بين الخصوم «وعلماً ونجيناه من القرية التي كانت تعمل» أي أهلها الأعمال «الخبائث» من اللواط والرمي بالبندق واللعب بالطيور وغير ذلك «إنهم كانوا قوم سوء» مصدر ساءه نقيض سرَّه «فاسقين».'),
(2558, 21, 75, '«وأدخلناه في رحمتنا» بأن أنجيناه من قومه «إنه من الصالحين».'),
(2559, 21, 76, '(و) اذكر (نوحاً) وما بعده بدل منه (إذ نادى) دعا على قومه بقوله "" رب لا تذر "" إلخ (من قبل) أي قبل إبراهيم ولوط (فاستجبنا له فنجيناه وأهله) الذين في سفينته (من الكرب العظيم) أي الغرق وتكذيب قومه له.'),
(2560, 21, 77, '«ونصرناه» منعناه «من القوم الذين كذبوا بآياتنا» الدالة على رسالته أن لا يصلوا إليه بسوء «إنهم كانوا قوم سوء فأغرقناهم أجمعين».'),
(2561, 21, 78, '«و» اذكر «داود وسليمان» أي قصتهما ويبدل منهما «إذ يحكمان في الحرث» هو زرع أو كرم «إذ نفشت فيه غنم القوم» أي رعته ليلاً بلا راع بأن انفلتت «وكنا لحكمهم شاهدين» فيه استعمال ضمير الجمع لاثنين، قال داود لصاحب الحرث رقاب الغنم، وقال سليمان: ينتفع بدرها ونسلها وصوفها إلى أن يعود الحرث كما كان بإصلاح صاحبها فيردها إليه.'),
(2562, 21, 79, '«ففهمناها» أي الحكومة «سليمان» وحكمهما باجتهاد ورجع داود إلى سليمان وقيل بوحي والثاني ناسخ للأول «وكلا» منهما «آتينا» ه «حكماً» نبوة «وعلماً» بأمور الدين «وسخرنا مع داود الجبال يسبحن والطير» كذلك سخرا للتسبيح معه لأمره به إذا وجد فترة لينشط له «وكنا فاعلين» تسخير تسبيحهما معه، وإن كان عجباً عندكم: أي مجاوبته للسيد داود.'),
(2563, 21, 80, '«وعلمناه صنعة لَبُوس» وهي الدرع لأنها تلبس، وهو أول من صنعها وكان قبلها صفائح «لكم» في جملة الناس «لنحصنكم» بالنون لله وبالتحتانية لداود وبالفوقانية للبوس «من بأسكم» حربكم مع أعدائكم «فهل أنتم» يا أهل مكة «شاكرون» نعمتي بتصديق الرسول: أي اشكروني بذلك.'),
(2564, 21, 81, '«و» سخرنا «لسليمان الريح عاصفة» وفي آية أخرى: رخاء، أي شديدة الهبوب وخفيفته حسب إرادته «تجري بأمره إلى الأرض التي باركنا فيها» وهي الشام «وكنا بكل شيء عالمين» من ذلك علم الله تعالى بأن ما يعطيه سليمان يدعوه إلى الخضوع لربه ففعله تعالى على مقتضى علمه.'),
(2565, 21, 82, '«و» سخرنا «من الشياطين من يغوصون له» يدخلون في البحر فيُخرجون منه الجواهر لسليمان «ويعلمون عملاً دون ذلك» أي سوى الغوص من البناء وغيره «وكنا لهم حافظين» من أن يُفسدوا ما عملوا، لأنهم كانوا إذا فرغوا من عمل قبل الليل أفسدوه إن لم يشتغلوا بغيره.'),
(2566, 21, 83, '«و» اذكر «أيوب» ويبدل منه «إذ نادى ربه» لما ابتلي بفقد جميع ماله وولده وتمزيق جسده وهجر جميع الناس له إلا زوجته سنين ثلاثا أو سبعاً أو ثماني عشرة وضيق عيشه «أني» بفتح الهمزة بتقدير الياء «مسنيَ الضر» أي الشدة «وأنت أرحم الراحمين».'),
(2567, 21, 84, '«فاستجبنا له» نداءه «فكشفنا ما به من ضر وآتيناه أهله» أولاده الذكور والإناث بأن أحيوا له وكل من الصنفين ثلاث أو سبع «ومثلهم معهم» من زوجته وزيد في شبابها، وكان له أندر للقمح وأندر للشعير فبعث الله سحابتين أفرغت إحداهما على أندر القمح الذهب وأفرغت الأخرى على أندر الشعير الورق حتى فاض «رحمة» مفعول له «من عندنا» صفة «وذكرى للعابدين» ليصبروا فيثابوا.'),
(2568, 21, 85, '«و» اذكر «إسماعيل وإدريس وذا الكفل كل من الصابرين» على طاعة الله وعن معاصيه.'),
(2569, 21, 86, '«وأدخلناهم في رحمتنا» من النبوة «إنهم من الصالحين» لها وسمي ذا الكفل لأنه تكفل بصيام جميع نهاره وقيام جميع ليله وأن يقضي بين الناس ولا يغضب فوفى بذلك وقيل لم يكن نبيا.'),
(2570, 21, 87, '«و» اذكر «ذا النون» صاحب الحوت وهو يونس بن متى ويبدل منه «إذ ذهب مغاضباً» لقومه أي غضبان عليهم مما قاسى منهم ولم يؤذن له في ذلك «فظن أن لن نقدر عليه» أي نقضي عليه بما قضيناه من حبسه في بطن الحوت، أو نضيق عليه بذلك «فنادى في الظلمات» ظلمة الليل وظلمة البحر وظلمة بطن الحوت «أن» أي بأن «لا إله إلا أنت سبحانك إني كنت من الظالمين» في ذهابي من بين قومي بلا إذن.'),
(2571, 21, 88, '«فاستجبنا له ونجيناه من الغم» بتلك الكلمات «وكذلك» كما نجيناه «ننجي المؤمنين» من كربهم إذا استغاثوا بنا داعين.'),
(2572, 21, 89, '«و» اذكر «زكريا» ويبدل منه «إذ نادى ربه» بقوله «رب لا تذرني فرداً» أي بلا ولد يرثني «وأنت خير الوارثين» الباقي بعد فناء خلقك.'),
(2573, 21, 90, '«فاستجبنا له» نداءه «ووهبنا له يحيى» ولداً «وأصلحنا له زوجه» فأتت بالولد بعد عقمها «إنهم» أي من ذُكر من الأنبياء «كانوا يسارعون» يبادرون «في الخيرات» الطاعات «ويدعوننا رغباً» في رحمتنا «ورهباً» من عذابنا «وكانوا لنا خاشعين» متواضعين في عبادتهم.'),
(2574, 21, 91, '«و» اذكر مريم «التي أحصنت فرجها» حفظته من أن ينال «فنفخنا فيها من روحنا» أي جبريل حيث نفخ في جيب درعها فحملت بعيسى «وجعلناها وابنها آية للعالمين» الإنس والجن والملائكة حيث ولدته من غير فحل.'),
(2575, 21, 92, '«إن هذه» أي ملة الإسلام «أمتكم» دينكم أيها المخاطبون أي يجب أن تكونوا عليها «أمة واحدة» حال لازمة «وأنا ربكم فاعبدون» وحدون.'),
(2576, 21, 93, '«وتَقطَّعوا» أي بعض المخاطبين «أمرهم بينهم» أي تفرقوا أمر دينهم متخالفين فيه، وهم طوائف اليهود والنصارى قال تعالى: «كل إلينا راجعون» أي فنجازيه بعمله.'),
(2577, 21, 94, '«فمن يعمل من الصالحات وهو مؤمن فلا كفران» أي لا جحود «لسعيه وإنا له كاتبون» بأن نأمر الحفظة بكتبه فنجازيه عليه.'),
(2578, 21, 95, '«وحرام على قرية أهلكناها» أريد أهلها «أنهم لا» زائدة «يرجعون» أي ممتنع رجوعهم إلى الدنيا.'),
(2579, 21, 96, '«حتى» غاية لامتناع رجوعهم «إذا فتحت» بالتخفيف والتشديد «يأجوج ومأجوج» بالهمز وتركه اسمان أعجميان لقبيلتين، ويقدر قبله مضاف أي سدهما وذلك قرب القيامة «وهم من كل حدب» مرتفع من الأرض «ينسلُون» يسرعون.'),
(2580, 21, 97, '«واقترب الوعد الحق» أي يوم القيامة «فإذا هي» أي القصة «شاخصة أبصار الذين كفروا» في ذلك اليوم لشدته، يقولون «يا» للتنبيه «ويلنا» هلاكنا «قد كنا» في الدنيا «في غفلة من هذا» اليوم «بل كنا ظالمين» أنفسنا بتكذيبنا للرسل.'),
(2581, 21, 98, '«إنكم» يا أهل مكة «وما تعبدون من دون الله» أي غيره من الأوثان «حصب جهنم» وقودها «أنتم لها واردون» داخلون فيها.'),
(2582, 21, 99, '«لو كان هؤلاء» الأوثان «آلهة» كما زعمتم «ما وردوها» دخلوها «وكل» من العابدين والمعبودين «فيها خالدون».'),
(2583, 21, 100, '«لهم» للعابدين «فيها زفير وهم فيها لا يسمعون» شيئاً لشدة غليانها. ونزل لما قال ابن الزبعري عبد عزير والمسيح والملائكة فهم في النار على مقتضى ما تقدم.'),
(2584, 21, 101, '«إن الذين سبقت لهم منا» المنزلة «الحسنى» ومنهم من ذكر «أولئك عنها مبعدون».'),
(2585, 21, 102, '«لا يسمعون حسيسها» صوتها «وهم في ما اشتهت أنفسهم» من النعيم «خالدون».'),
(2586, 21, 103, '«لا يحزنهم الفزع الأكبر» وهو أن يؤمر بالعبد إلى النار «وتتلقاهم» تستقبلهم «الملائكة» عند خروجهم من القبور يقولون لهم «هذا يومكم الذي كنتم توعدون» في الدنيا.'),
(2587, 21, 104, '«يوم» منصوب باذكر مقدراً قبله «نطوي السماء كطي السجل» اسم ملك «للكتاب» صحيفة ابن آدم عند موته واللام زائدة أو السجل الصحيفة والكتاب بمعنى المكتوب واللام بمعنى على وفي قراءة للكتب جمعاً «كما بدأنا أول خلق» من عدم «نُعيده» بعد إعدامه فالكاف متعلقة بنعيد وضميره عائد إلى أول وما مصدرية «وعداً علينا» منصوب بوعدنا مقدراً قبله وهو مؤكد لمضمون ما قبله «إنا كنا فاعلين» ما وعدناه.'),
(2588, 21, 105, '«ولقد كتبنا في الزبور» بمعنى الكتاب أي كتب الله المنزلة «من بعد الذكر» بمعنى أم الكتاب الذي عند الله «أن الأرض» أرض الجنة «يرثها عبادي الصالحون» عام في كل صالح.'),
(2589, 21, 106, '«إن في هذا» القرآن «لبلاغاً» كفاية في دخول الجنة «لقوم عابدين» عاملين به.'),
(2590, 21, 107, '«وما أرسلناك» يا محمد «إلا رحمة» أي للرحمة «للعالمين» الإنس والجن بك.'),
(2591, 21, 108, '«قل إنما يوحى إليَّ أنما إلهكم إله واحد» أي ما يوحى إليَّ في أمر الإله إلا وحدانيته «فهل أنتم مسلمون» منقادون لما يوحى إليَّ من وحدانية الإله والاستفهام بمعنى الأمر.'),
(2592, 21, 109, '«فإن تولوا» عن ذلك «فقل آذنتكم» أعلمتكم بالحرب «على سواء» حال من الفاعل والمفعول، أي مستوين في علمه لا أستبد به دونكم لتتأهبوا «وإن» ما «أدري أقريب أم بعيد ما توعدون» من العذاب أو القيامة المشتملة عليه وإنما يعلمه الله.'),
(2593, 21, 110, '«إنه» تعالى «يعلم الجهر من القول» والفعل منكم ومن غيركم «ويعلم ما تكتمون» أنتم وغيركم من السر.'),
(2594, 21, 111, '«وإن» ما «أدري لعله» أي ما أعلمتكم به ولم يعلم وقته «فتنة» اختبار «لكم» ليرى كيف صنعكم «ومتاع» تمتع «إلى حين» أي انقضاء آجالكم وهذا مقابل للأول المترجى بلعل وليس الثاني محلا للترجي.'),
(2595, 21, 112, '(قل) وفي قراءة قال (رب احكم) بيني وبين مكذبي (بالحق) بالعذاب لهم أو النصر عليهم، فعذبوا ببدر وأحد وحنين والأحزاب والخندق ونصر عليهم (وربنا الرحمن المستعان على ما تصفون) من كذبكم على الله في قولكم "" اتخذ ولداً "" وعلي في قولكم: ساحر، وعلى القرآن في قولكم: شعر.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, '«يا أيها الناس» أي أهل مكة وغيرهم «اتقوا ربكم» أي عقابه بأن تطيعوه «إنَّ زلزلة الساعة» أي الحركة الشديدة للأرض التي يكون بعدها طلوع الشمس من مغربها الذي هو قرب الساعة «شيء عظيم» في إزعاج الناس الذي هو نوع من العقاب.'),
(2597, 22, 2, '«يوم ترَوْنها تذُهَلُ» بسببها «كل مرضعة» بالفعل «عما أرضعت» أي تنساه «وتضع كل ذات حمل» أي حبلى «حملها وترى الناس سكارى» من شدة الخوف «وما هم بسكارى» من الشراب «ولكن عذاب الله شديد» فهم يخافونه.'),
(2598, 22, 3, 'ونزل في النضر بن الحارث وجماعته «ومن الناس من يجادل في الله بغير علم» قالوا: الملائكة بنات الله، والقرآن أساطير الأولين، وأنكروا البعث وإحياء من صار ترابا «ويتبع» في جداله «كل شيطان مريد» أي متمرد.'),
(2599, 22, 4, '«كتب عليه» قضي على الشيطان «أنه من تولاه» أي اتبعه «فأنه يضله ويهديه» يدعوه «إلى عذاب السعير» أي النار.'),
(2600, 22, 5, '«يا أيها الناس» أي أهل مكة «إن كنتم في ريب» شك «من البعث فإنا خلقناكم» أي أصلكم آدم «من تراب ثم» خلقنا ذريته «من نطفة» منيّ «ثم من علقة» وهي الدم الجامد «ثم من مضغة» وهي لحمة قدر ما يمضغ «مخلقة» مصورة تامة الخلق «وغير مخلقة» أي غير تامة الخلق «لنبين لكم» كمال قدرتنا لتستدلوا بها في ابتداء الخلق على إعادته «ونُقرُّ» مستأنف «في الأرحام ما نشاء إلى أجل مسمى» وقت خروجه «ثم نخرجكم» من بطون أمهاتكم «طفلا» بمعنى أطفالا «ثم» نعمركم «لتبلغوا أشدكم» أي الكمال والقوة وهو ما بين الثلاثين إلى الأربعين سنة «ومنكم من يُتوفى» يموت قبل بلوغ الأشد «ومنكم من يرد إلى أرذل العمر» أخسه من الهرم والخرف «لكيلا يعلم من بعد علم شيئا» قال عكرمة من قرأ القرآن لم يصر بهذه الحالة «وترى الأرض هامدة» يابسة «فإذا أنزلنا عليها الماء اهتزت» تحركت «وَرَبَتْ» ارتفعت وزادت «وأنبتت من» زائدة «كلّ زوج» صنف «بهيج» حسن.'),
(2601, 22, 6, '«ذلك» المذكور من بدء خلق الإنسان إلى آخر إحياء الأرض «بأن» بسبب أن «الله هو الحق» الثابت الدائم «وأنه يحيى الموتى وأنه على كل شيء قدير».'),
(2602, 22, 7, '«وأن الساعة آتية لا ريب» شك «فيها وأن الله يبعث من في القبور» ونزل في أبي جهل.'),
(2603, 22, 8, '«ومن الناس من يجادل في الله بغير علم ولا هدى» معه «ولا كتاب منير» له نور معه.'),
(2604, 22, 9, '«ثانيَ عطفه» حال أي لاويَ عنقه تكبرا عن الإيمان والعطف الجانب عن يمين أو شمال «ليَضِلَّ» بفتح الياء وضمها «عن سبيل الله» أي دينه «له في الدنيا خزي» عذاب فقتل يوم بدر «ونذيقه يوم القيامة عذاب الحريق» أي الإحراق بالنار، ويقال له.'),
(2605, 22, 10, '«ذلك بما قدمت يداك» أي قدمته عبر عنه بهما دون غيرهما لأن أكثر الأفعال تزاول بهما «وأن الله ليس بظلام» أي بذي ظلم «للعبيد» فيعذبهم بغير ذنب.'),
(2606, 22, 11, '«ومن الناس من يعبد الله على حرف» أي شك في عبادته، شبه بالحالِّ على حرف جبل في عدم ثباته «فإن أصابه خير» صحة وسلامة في نفسه وماله «اطمأن به وإن أصابته فتنة» محنة وسقم في نفسه وماله «انقلب على وجهه» أي رجع إلى الكفر «خسر الدنيا» بفوات ما أمله منها «والآخرة» بالكفر «ذلك هو الخسران المبين» البيِّن.'),
(2607, 22, 12, '«يدعو» يعبد «من دون الله» من الصنم «ما لا يضره» إن لم يعبده «وما لا ينفعه» إن عبده «ذلك» الدعاء «هو الضلال البعيد» عن الحق.'),
(2608, 22, 13, '«يدعو لمن» اللام زائدة «ضره» بعبادته «أقرب من نفعه» إن نفع بتخيله «لبئس المولى» هو أي الناصر «ولبئس العشير» الصاحب هو، وعقب ذكر الشاك بالخسران بذكر المؤمنين بالثواب في.'),
(2609, 22, 14, '«إن الله يدخل الذين آمنوا وعملوا الصالحات» من الفروض والنوافل «جناتِ تجري من تحتها الأنهار إن الله يفعل ما يريد» من إكرام من يعطيه وإهانة من يعصيه.'),
(2610, 22, 15, '«من كان يظن أن لن ينصره الله» أي محمدا نبيه «في الدنيا والآخرة فليمدد بسبب» بحبل «إلى السماء» أي سقف بيته يشدّه فيه وفي عنقه «ثم ليقطع» أي ليختنق به بأن يقطع نفسه من الأرض كما في الصحاح «فلينظر هل يُذهبن كيده» في عدم نصرة النبي «ما يغيظ» منها المعنى فليختنق غيظا منها فلا بد منها.'),
(2611, 22, 16, '«وكذلك» أي مثل إنزالنا الآية السابقة «أنزلناه» أي القرآن الباقي «آيات بينات» ظاهرات حال «وأن الله يهدي من يريد» هداه معطوف على هاء أنزلناه.'),
(2612, 22, 17, '«إن الذين آمنوا والذين هادوا» هم اليهود «والصابئين» طائفة منهم «والنصارى والمجوس والذين أشركوا إن الله يفصل بينهم يوم القيامة» بإدخال المؤمنين الجنة وإدخال غيرهم النار «إن الله على كل شيء» من عملهم «شهيد» عالم به علم مشاهدة'),
(2613, 22, 18, '«ألم تر» تعلم «أن الله يسجد له من في السماوات ومن في الأرض والشمس والقمر والنجوم والجبال والشجر والدواب» أي يخضع له بما يراد منه «وكثير من الناس» وهم المؤمنون بزيادة على الخضوع في سجود الصلاة «وكثير حق عليه العذاب» وهم الكافرون لأنهم أبوا السجود المتوقف على الإيمان «ومن يهن الله» يشقه «فما له من مكرم» مسعد «إن الله يفعل ما يشاء» من الإهانة والإكرام.'),
(2614, 22, 19, '«هذان خصمان» أي المؤمنون خصم، والكفار الخمسة خصم، وهو يطلق على الواحد والجماعة «اختصموا في ربهم» أي في دينه «فالذين كفروا قطعت لهم ثياب من نار» يلبسونها يعني أحيطت بهم النار «يصب من فوق رؤوسهم الحميم» الماء البالغ نهاية الحرارة.'),
(2615, 22, 20, '«يصهر» يذاب «به ما في بطونهم» من شحوم وغيرها «و» تشوى به «الجلود».'),
(2616, 22, 21, '«ولهم مقامع من حديد» لضرب رؤوسهم.'),
(2617, 22, 22, '«كلما أرادوا أن يخرجوا منها» أي النار «من غم» يلحقهم بها «أعيدوا فيها» ردوا إليها بالمقامع «و» قيل لهم «ذوقوا عذاب الحريق» أي البالغ نهاية الإحراق.'),
(2618, 22, 23, 'وقال في المؤمنين «إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار يحلون فيها من أساور من ذهب ولؤلؤ» بالجرّ أي منهما بأن يرصع اللؤلؤ بالذهب، وبالنصب عطفا على محل من أساور «ولباسهم فيها حرير» هو المحرَّم لبسه على الرجال في الدنيا.'),
(2619, 22, 24, '«وهدوا» في الدنيا «إلى الطيب من القول» وهو لا إله إلا الله «وهدوا إلى صراط الحميد» أي طريق الله المحمود ودينه.'),
(2620, 22, 25, '«إن الذين كفروا ويصدون عن سبيل الله» طاعته «و» عن «المسجد الحرام الذي جعلناه» منسكا ومتعبدا «للناس سواءً العاكف» المقيم «فيه والباد» الطارئ «ومن يرد فيه بإلحاد» الباء زائدة «بظلم» أي بسببه بأن ارتكب منهيا، ولو شتم الخادم «نذقه من عذاب أليم» مؤلم: أي بعضه، ومن هذا يؤخذ خبر إن: أي نذيقهم من عذاب أليم.'),
(2621, 22, 26, '«و» اذكر «إذ بوأنا» بينا «لإبراهيم مكان البيت» ليبنيه، وكان قد رفع زمن الطوفان، وأمرناه «أن لا تشرك بي شيئا وطهر بيتي» من الأوثان «للطائفين والقائمين» المقيمين به «والركع السجود» جمع راكع وساجد: المصلين.'),
(2622, 22, 27, '«وأذِّن» ناد «في الناس بالحج» فنادى على جبل أبي قبيس: يا أيها الناس إن ربكم بنى بيتاً وأوجب عليكم الحج إليه فأجيبوا ربكم، والتفت بوجهه يمينا وشمالا وشرقا وغربا، فأجابه كل من كتب له أن يحج من أصلاب الرجال وأرحام الأمهات: لبيك اللهم لبيك، وجواب الأمر «يأتوك رجالا» مشاة جمع راجل كقائم وقيام «و» ركبانا «على كل ضامر» أي بعير مهزول وهو يطلق على الذكر والأنثى «يأتين» أي الضوامر حملا على المعنى «من كل فج عميق» طريق بعيد.'),
(2623, 22, 28, '«ليشهدوا» أي يحضروا «منافع لهم» في الدنيا بالتجارة أو في الآخرة أو فيهما أقوال «ويذكروا اسم الله في أيام معلومات» أي عشر ذي الحجة أو يوم عرفة أو يوم النحر إلى آخر أيام التشريق أقوال «على ما رزقهم من بهيمة الأنعام» الإبل والبقر والغنم التي تنحر في يوم العيد، وما بعده من الهدايا والضحايا «فكلوا منها» إذا كانت مستحبة «وأطعموا البائس الفقير» أي الشديد الفقر.'),
(2624, 22, 29, '«ثم ليقضوا تفثهم» أي يزيلوا أوساخهم وشعثهم كطول الظفر «وليوفوا» بالتخفيف والشديد «نذورهم» من الهدايا والضحايا «وليطوفوا» طواف الإفاضة «بالبيت العتيق» أي القديم لأنه أول بيت وضع للناس.'),
(2625, 22, 30, '«ذلك» خبر مبتدأ مقدر: أي الأمر أو الشأن ذلك المذكور «ومن يعظم حرمات الله» هي ما لا يحل انتهاكه «فهو» أي تعظيمها «خير له عند ربه» في الآخرة «وأحلت لكم الأنعام» أكلا بعد الذبح «إلا ما يتلى عليكم» تحريم في (حرمت عليكم الميتة) الآية فالاستثناء منقطع، ويجوز أن يكون متصلا، والتحريم لما عرض من الموت ونحوه «فاجتنبوا الرجس من الأوثان» من للبيان أي الذي هو الأوثان «واجتنبوا قول الزور» أي الشرك بالله في تلبيتكم أو شهادة الزور.'),
(2626, 22, 31, '«حنفاء لله» مسلمين عادلين عن كل دين سوى دينه «غير مشركين به» تأكيد لما قبله، وهما حالان من الواو «ومن يشرك بالله فكأنما خر» سقط «من السماء فتخطفه الطير» أي تأخذه بسرعة «أو تهوي به الريح» أي تسقطه «في مكان سحيق» بعيد فهو لا يرجى خلاصه.'),
(2627, 22, 32, '«ذلك» يقدر قبله الأمر، مبتدأ «ومن يعظم شعائر الله فإنها» أي فإن تعظيمها وهي البدن التي تهدى للحرم بأن تُستَحسَنَ وتُستمنَ «من تقوى القلوب» منهم، وسميت شعائر لإشعارها بما تعرف به أنها هدي طعن حديد بسنامها.'),
(2628, 22, 33, '«لكم فيها منافع» كركوبها ولحمل عليها ما لا يضرها «إلى أجل مسمى» وقت نحرها «ثم محلها» أي مكان حل نحرها «إلى البيت العتيق» أي عنده، والمراد الحرم جميعه.'),
(2629, 22, 34, '«ولكل أمة» أي جماعة مؤمنة سلفت قبلكم «جعلنا منسكا» بفتح السين مصدر وبكسرها اسم مكان: أي ذبحا قربانا أو مكانه «ليذكروا اسم الله على ما رزقهم من بهيمة الأنعام» عند ذبحها «فإلهكم إله واحد فله أسلموا» انقادوا «وبشر المخبتين» المطيعين المتواضعين.'),
(2630, 22, 35, '«الذين إذا ذكر الله وجلت» خافت «قلوبهم والصابرين على ما أصابهم» من البلايا «والمقيمي الصلاة» في أوقاتها «ومما رزقناهم ينفقون» يتصدقون.'),
(2631, 22, 36, '«والبدن» جمع بدنة: وهي الإبل «جعلناها لكم من شعائر الله» أعلام دينه «لكم فيها خير» نفع في الدنيا كما تقدم، وأجر في العقبى «فاذكروا اسم الله عليها» عند نحرها «صوافَّ» قائمة على ثلاث معقولة اليد اليسرى «فإذا وجبت جنوبها» سقطت إلى الأرض بعد النحر: وهو وقت الأكل منها «فكلوا منها» إن شئتم «وأطعموا القانع» الذي يقنع بما يعطى ولا يسأل ولا يتعرّض «والمعترَّ» والسائل أو المتعرض «كذلك» أي مثل ذلك التسخير «سخرناها لكم» بأن تُنحر وتركب، وإلا لم تطق «لعلكم تشكرون» إنعامي عليكم.'),
(2632, 22, 37, '«لن ينال الله لحومها ولا دماؤها» أي لا يرفعان إليه «ولكن يناله التقوى منكم» أي يرفع إليه منكم العمل الصالح الخالص له مع الإيمان «كذلك سخرها لكم لتكبروا الله على ما هداكم» أرشدكم لمعالم دينه ومناسك حجه «وبشر المحسنين» أي الموحدين.'),
(2633, 22, 38, '«إن الله يدافع عن الذين آمنوا» غوائل المشركين «إن الله لا يحب كل خوَّانِ» في أمانته «كفور» لنعمته، وهم المشركون المعنى أنه يعاقبهم.'),
(2634, 22, 39, '«أذن للذين يقاتلون» أي للمؤمنين أن يقاتلوا، وهذه أول آية نزلت في الجهاد «بأنهم» أي بسبب أنهم «ظلموا» لظلم الكافرين إياهم «وإن الله على نصرهم لقدير».'),
(2635, 22, 40, 'هم «الذين أخرجوا من ديارهم بغير حق» في الإخراج، ما أخرجوا «إلا أن يقولوا» أي بقولهم «ربنا الله» وحده وهذا القول حق فالإخراج به إخراج بغير حق «ولولا دفع الله الناس بعضهم» بدل بعض من الناس «ببعض لهدمت» بالتشديد للتكثير وبالتخفيف «صوامع» للرهبان «وبيع» كنائس للنصارى «وصلوات» كنائس لليهود بالعبرانية «ومساجد» للمسلمين «يذكر فيها» أي المواضع المذكورة «اسم الله كثيرا» وتنقطع العبادات بخرابها «ولينصرن الله من ينصره» أي ينصر دينه «إن الله لقويٌ» على خلقه «عزيز» منيع في سلطانه وقدرته.'),
(2636, 22, 41, '«الذين إن مكانهم في الأرض» بنصرهم على عدوهم «أقاموا الصلاة وآتوا الزكاة وأمروا بالمعروف ونهوا عن المنكر» جواب الشرط، وهو وجوابه صلة الموصول، ويقدر قبله هم بمبتدأ «ولله عاقبة الأمور» أي إليه مرجعها في الآخرة.'),
(2637, 22, 42, '«وإن يكذبوك» إلى آخره فيه تسليه للنبي صلى الله عليه وسلم «فقد كذبت قبلهم قوم نوح» تأنيث قوم باعتبار المعنى «وعاد» قوم هود «وثمود» قوم صالح.'),
(2638, 22, 43, '«وقوم إبراهيم وقوم لوط».'),
(2639, 22, 44, '«وأصحاب مدين» قوم شعيب «وكُذب موسى» كذبه القبط لا قومه بنو إسرائيل أي كذب هؤلاء رسلهم فلك أسوة بهم «فأمليتُ للكافرين» أمهلتهم بتأخير العقاب لهم «ثم أخذتهم» بالعذاب «فكيف كان نكير» أي إنكاري عليم بتكذيبهم بإهلاكهم والاستفهام للتقرير: أي هو واقع موقعه.'),
(2640, 22, 45, '«فكأين» أي كم «من قرية أهلكتها» وفي قراءة أهلكناها «وهي ظالمة» أي أحلها بكفرهم «فهي خاوية» ساقطة «على عروشها» سقوفها «و» كم من «بئر معطلة» متروكة بموت أهلها «وقصر مشيد» رفيع خال بموت أهله.'),
(2641, 22, 46, '«أفلم يسيروا» أي كفار مكة «في الأرض فتكون لهم قلوب يعقلون بها» ما نزل بالمكذبين قبلهم «أو آذان يسمعون بها» أخبارهم بإلاهلاك وخراب الديار فيعتبروا «فإنها» أي القصة «لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور» تأكيد.'),
(2642, 22, 47, '«ويستعجلونك بالعذاب ولن يخلف الله وعده» بإنزال العذاب فأنزله يوم بدر «وإنَّ يوما عند ربك» من أيام الآخرة بسبب العذاب «كألف سنة مما تعدون» بالتاء والياء في الدنيا.'),
(2643, 22, 48, '«وكأيِّن من قرية أمليت لها وهي ظالمة ثم أخذتها» المراد أهلها «وإليَّ المصير» المرجع.'),
(2644, 22, 49, '«قل يا أيها الناس» أهل مكة «إنما أنا لكم نذير مبين» الإنذار وأنا بشير للمؤمنين.'),
(2645, 22, 50, '«فالذين آمنوا وعملوا الصالحات لهم مغفرة» من الذنوب «ورزق كريم» هو الجنة.'),
(2646, 22, 51, '«والذين سعوْا في آياتنا» القرآن بإبطالها «معجِّزين» من اتبع النبي أي ينسبونهم إلى العجز، ويثبطونهم عن الإيمان أو مقدرين عجزنا عنهم، وفي قراءة معاجزين: مسابقين لنا، أي يظنون أن يفوتونا بإنكارهم البعث والعقاب «أولئك أصحاب الجحيم» النار.'),
(2647, 22, 52, '«وما أرسلنا من قبلك من رسول» هو نبي أمر بالتبليغ «ولا نبي» أي لم يؤمر بالتبليغ «إلا إذا تمنى» قرأ «ألقى الشيطان في أمنيته» قراءته ما ليس من القرآن مما يرضاه المرسل إليهم، وقد قرأ النبي صلى الله عليه وسلم في سورة النجم بمجلس من قريش بعد: (أفرأيتم اللات والعزى، ومناة الثالثة الأخرى) بإلقاء الشيطان على لسانه من غير علمه صلى الله عليه وسلم به: تلك الغرانيق العلا، وإن شفاعتهن لترجي، ففرحوا لذلك، ثم أخبره جبريل بما ألقه الشيطان على لسانه من ذلك، بحزن فسلي بهذه الآية ليطمئن «فينسخ الله» يبطل «ما يلقي الشيطان ثم يحكم الله آياته» يثبتها «والله عليم» بإلقاء الشيطان ما ذكر «حكيم» في تمكنيه منه يفعل ما يشاء.'),
(2648, 22, 53, '«ليجعل ما يلقي الشيطان فتنة» محنة «للذين في قلوبهم مرض» شقاق ونفاق «والقاسية قلوبهم» أي المشركين عن قبول الحق «وإن الظالمين» الكافرين «لفي شقاق بعيد» خلاف طويل مع النبي صلى الله عليه وسلم والمؤمنين حيث جرى على لسانه ذكر آلهتهم بما يرضيهم ثم أبطل ذلك.'),
(2649, 22, 54, '«وليعلم الذين أوتوا العلم» التوحيد والقرآن «أنه» أي القرآن «الحق من ربك فيؤمنوا به فتخبت» نطمئن «له قلوبهم وإن الله لهاد الذين آمنوا إلى صراط» طريق «مستقيم» أي دين الإسلام.'),
(2650, 22, 55, '«ولا يزال الذين كفروا في مرية» شك «منه» أي القرآن بما ألقاه الشيطان على لسان النبي ثم أبطل «حتى تأتيهم الساعة بغتة» أي ساعة موتهم أو القيامة فجأة «أو يأتيهم عذاب يوم عقيم» هو يوم بدر لا خير فيه للكفار كالريح العقيم التي لا تأتي بخير، أو هو يوم القيامة لا ليل بعده.'),
(2651, 22, 56, '«الملك يومئذ» أي يوم القيامة «لله» وحده وما تضمنه من الاستقرار ناصب للظرف «يحكم بينهم» بين المؤمنين والكافرين بما بيّن بعده «فالذين آمنوا وعملوا الصالحات في جنات النعيم» فضلا من الله.'),
(2652, 22, 57, '«والذين كفروا وكذبوا بآياتنا فأولئك لهم عذاب مُهين» شديد بسبب كفرهم.'),
(2653, 22, 58, '«والذين هاجروا في سبيل الله» أي طاعته من مكة إلى المدينة «ثم قُتلوا أو ماتوا ليرزقنهم الله رزقا حسنا» هو رزق الجنة «وإن الله لهو خير الرازقين» أفضل المعطين'),
(2654, 22, 59, '«ليدخلنهم مدخلا» بضم الميم وفتحها أي إدخالا أو موضعا «يرضونه» وهو الجنة «وإن الله لعليم» بنياتهم «حليم» عن عقابهم.'),
(2655, 22, 60, 'الأمر «ذلك» الذي قصصناه عليك «ومن عاقب» جازي من المؤمنين «بمثل ما عوقب به» ظلما من المشركين: أي قاتلهم كما قاتلوه في الشهر الحرام «ثم بغي عليه» منهم أي ظلم بإخراجه من منزله «لينصرنه الله إن الله لعفوٌ» عن المؤمنين «غفور» لهم عن قتالهم في الشهر الحرام.'),
(2656, 22, 61, '«ذلك» النصر «بأن الله يولج الليل في النهار ويولج النهار في الليل» أي يدخل كلا منهما في الآخر بأن يزيد به، من أثر قدرته تعالى التي بها النصر «وأن الله سميع» دعاء المؤمنين «بصير» بهم حيث جعل فيهم الإيمان فأجاب دعائهم.'),
(2657, 22, 62, '«ذلك» النصر أيضا «بأن الله هو الحق» الثابت «وأن ما يدعون» بالياء والتاء يعبدون «من دونه» وهو الأصنام «هو الباطل» الزائل «وأن الله هو العلي» أي العالي على كل شيء بقدرته «الكبير» الذي يصغر كل شيء سواه.'),
(2658, 22, 63, '«ألم تر» تعلم «أن الله أنزل من السماء ماءً» مطرا «فتصبح الأرض مخضرة» بالنبات وهذا من أثر قدرته «إن الله لطيف» بعباده في إخراج النبات بالماء «خبير» بما في قلوبهم عند تأخير المطر.'),
(2659, 22, 64, '«له ما في السماوات وما في الأرض» على جهة الملك «وإن الله لهو الغني» عن عباده «الحميد» لأوليائه.'),
(2660, 22, 65, '«ألم ترَ» تَعلم «أن الله سخر لكم ما في الأرض» من البهائم «والفلك» السفن «تجري في البحر» للركوب والحمل «بأمره» بإذنه «ويمسك السماء» من «أن» أو لئلا «تقع على الأرض إلا بإذنه» فتهلكوا «إن الله بالناس لرؤوف رحيم» في التسخير والإمساك.'),
(2661, 22, 66, '«وهو الذي أحياكم» بالإنشاء «ثم يميتكم» عند انتهاء آجالكم «ثم يحييكم» عند البعث «إن الإنسان» أي المشرك «لكفور» لنعم الله بتركه توحيده.'),
(2662, 22, 67, '«لكل أمة جعلنا منسكا» بفتح السين وكسرها شريعة «هم ناسكوه» عاملون به «فلا يُنازعُنَّك» يراد به لا تنازعهم «في الأمر» أي أمر الذبيحة إذ قالوا: ما قتل الله أحق أن تأكلوه مما قتلتم «وادع إلى ربك» إلى دينه «إنك لعلى هدى» دين «مستقيم».'),
(2663, 22, 68, '«وإن جادلوك» في أمر الدين «فقل الله أعلم بما تعملون» فيجازيكم عليه، وهذا قبل الأمر بالقتال.'),
(2664, 22, 69, '«الله يحكم بينكم» أيها المؤمنون والكافرون «يوم القيامة فيما كنتم فيه تختلفون» بأن يقول كل من الفريقين خلاف قول الآخر.'),
(2665, 22, 70, '«ألم تعلم» الاستفهام فيه للتقرير «أن الله يعلم ما في السماء والأرض إن ذلك» أي ما ذكر «في كتاب» هو اللوح المحفوظ «إن ذلك» أي علم ما ذكر «على الله يسير» سهل.'),
(2666, 22, 71, '«ويعبدون» أي المشركون «من دون الله ما لم ينزل به» هو الأصنام «سلطانا» حجة «وما ليس لهم به علم» أنها آلهة «وما للظالمين» بالإشراك «من نصير» يمنع عنهم عذاب الله.'),
(2667, 22, 72, '«وإذا تُتلى عليهم آياتنا» من القرآن «بيِّنات» ظاهرات حال «تعرف في وجوه الذين كفروا المنكر» أي الإنكار لها: أي أثره من الكراهة والعبوس «يكادون يسطون بالذين يتلون عليهم آياتنا» أي يقعون فيه بالبطش «قل أفأنبئكم بشرِّ من ذلكم» بأكره إليكم من القرآن المتلو عليكم هو «النار وعَدَها الله الذين كفروا» بأن مصيرهم إليها «وبئس المصير» هي.'),
(2668, 22, 73, '«يا أيها الناس» أي أهل مكة «ضُرب مثل فاستمعوا له» وهو «إن الذين تدعون» تعبدون «من دون الله» أي غيره وهم الأصنام «لن يخلقوا ذباباً» اسم جنس، واحده ذبابة يقع على المذكر والمؤنث «ولو اجتمعوا له» لخلقه «وإن يسلبهم الذباب شيئا» مما عليهم من الطيب والزعفران الملطخين به «لا يستنقذوه» لا يستردوه «منه» لعجزهم، فكيف يعبدون شركاء لله تعالى؟ هذا أمر مستغرب عبر عنه بضرب مثل «ضعف الطالب» العابد «والمطلوب» المعبود.'),
(2669, 22, 74, '«ما قدروا الله» عظموه «حقَّ قدره» عظمته إذ أشركوا به ما لم يمتنع من الذباب ولا ينتصف منه «إن الله لقوي عزيز» غالب.'),
(2670, 22, 75, '«الله يصطفي من الملائكة رسلا ومن الناس» رسلا، نزل لما قال المشركون (أأنزل عليه الذكر من بيننا) «إن الله سميع» لمقالتهم «بصير» بمن يتخذه رسولاً كجبريل وميكائيل وإبراهيم ومحمد وغيرهم صلى الله عليهم وسلم.'),
(2671, 22, 76, '«يعلم ما بين أيديهم وما خلفهم» أي ما قدَّموا وما خلّفوا وما عملوا وما هم عاملون بعد «وإلى الله ترجع الأمور».'),
(2672, 22, 77, '«يا أيها الذين آمنوا اركعوا واسجدوا» أي صلوا «واعبدوا ربكم» وحدوه «وافعلوا الخير» كصلة الرحم ومكارم الأخلاق «لعلكم تفلحون» تفوزون بالبقاء في الجنة.'),
(2673, 22, 78, '«وجاهدوا في الله» لإقامة دينه «حق جهاده» باستفراغ الطاقة فيه ونصب حَقَّ على المصدر «هو اجتباكم» اختاركم لدينه «وما جعل عليكم في الدين من حَرَج» أي ضيق بأن سهله عند الضرورات كالقصر والتيمم وأكل الميتة والفطر للمرض والسفر «مِلة أبيكم» منصوب الخافض الكاف «إبراهيم» عطف بيان «هو» أي الله «سمَّاكم المسلمين من قبل» أي قبل هذا الكتاب «وفي هذا» أي القرآن «ليكون الرسول شهيداً عليكم» يوم القيامة أنه بلَّغكم «وتكونوا» أنتم «شهداء على الناس» أن رسلهم بلَّغوهم «فأقيموا الصلاة» داوموا عليها «وآتوا الزكاة واعتصموا بالله» ثقوا به «هو مولاكم» ناصركم ومتولي أموركم «فنعم المولى» هو «ونعم النصير» الناصر لكم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, '«قد» للتحقيق «أفلح» فاز «المؤمنون».'),
(2675, 23, 2, '«الذين هم في صلاتهم خاشعون» متواضعون.'),
(2676, 23, 3, '«والذين هم عن اللَّغو» من الكلام وغيره «معُرضون».'),
(2677, 23, 4, '«والذين هم للزكاة فاعلون» مؤدون.'),
(2678, 23, 5, '«والذين هم لفروجهم حافظون» عن الحرام.'),
(2679, 23, 6, '«إلا على أزواجهم» أي زوجاتهم «أو ما ملكت أيمانهم» أي السراري «فإنهم غير ملومين» في إتيانه.'),
(2680, 23, 7, '«فمن ابتغى وراء ذلك» من الزوجات والسراري كالاستمناء باليد في إتيانهنَّ «فأولئك هم العادون» المتجاوزون إلى ما لا يحل لهم.'),
(2681, 23, 8, '«والذين هم لأمانتهم» جمعاً ومفرداً «وعهدهم» فيما بينهم أو فيما بينهم وبين الله من صلاة وغيرها «راعون» حافظون.'),
(2682, 23, 9, '«والذين هم على صلواتهم» جمعاً ومفرداً «يحافظون» يقيمونها في أوقاتها.'),
(2683, 23, 10, '«أولئك هم الوارثون» لا غيرهم.'),
(2684, 23, 11, '«الذين يرثون الفردوس» هو جنة أعلى الجنان «هم فيها خالدون» في ذلك إشارة إلى المعاد ويناسبه ذكر المبتدأ بعده.'),
(2685, 23, 12, '«و» الله «لقد خلقنا الإنسان» آدم «من سُلالةِ» هي من سللت الشيء من الشيء أي استخرجته منه وهو خلاصته «من طين» متعلق بسلالة.'),
(2686, 23, 13, '«ثم جعلناه» أي الإنسان نسل آدم «نطفةّ» منياً «في قرار مكين» هو الرحم.'),
(2687, 23, 14, '«ثم خلقنا النطفة عَلَقَةٌ» دماً جامداً «فخلقنا العلقة مضغة» لحمة قدر ما يمضغ «فخلقنا المضغة عظاماً فكسونا العظام لحماً» وفي قراءة عظماً في الموضعين، وخلقنا في المواضع الثلاث بمعنى صيرنا «ثم أنشأناه خلقاً أخر» بنفخ الروح فيه «فتبارك الله أحسن الخالقين» أي المقدرين ومميز أحسن محذوف للعلم به: أي خلقاُ.'),
(2688, 23, 15, '«ثم إنكم بعد ذلك لميتون».'),
(2689, 23, 16, '«ثم إنكم يوم القيامة تبعثون» للحساب والجزاء.'),
(2690, 23, 17, '«ولقد خلقنا فوقكم سبع طرائق» أي سماوات: جمع طريقة لأنها طرق الملائكة «وما كنا عن الخلق» التي تحتها «غافلين» أن تسقط عليهم فتهلكهم بل نمسكها كآية (ويمسك السماء أن تقع على الأَرض).'),
(2691, 23, 18, '«وأنزلنا من السماء ماءً بقدر» من كفايتهم «فأسكنَّاه في الأرض وإنَّا على ذهاب به لقادرون» فيموتون مع دوابهم عطشاً.'),
(2692, 23, 19, '«فأنشأنا لكم به جنات من نخيل وأعناب» هما أكثر فواكه العرب «لكم فيها فواكه كثيرة ومنها تأكلون» صيفاً وشتاء.'),
(2693, 23, 20, '«و» أنشأنا «شجرةٌ تخرج من طور سيناء» جبل بكسر السين وفتحها ومنع الصرف للعلمية والتأنيث للبقعة «تُنبت» من الرباعي والثلاثي «بالدهن» الباء زائدة على الأول ومعدية على الثاني وهي شجرة الزيتون «وصبغ للآكلين» عطف على الدهن أي إدام يصبغ اللقمة بغمسها فيه وهو الزيت.'),
(2694, 23, 21, '«وإن لكم في الأنعام» الإبل والبقر والغنم «لعبرةٌ» عظة تعتبرون بها «نَسقيكم» بفتح النون وضمها «مما في بطونها» اللبن «ولكم فيها منافع كثيرة» من الأصواف والأوبار والأشعار وغير ذلك «ومنها تأكلون».'),
(2695, 23, 22, '«وعليها» الإبل «وعلى الفلك» السفن «تحملون».'),
(2696, 23, 23, '«ولقد أرسلنا نوحاً إلى قومه فقال يا قوم اعبدوا الله» أطيعوا الله ووحدوه «مالكم من إله غيره» وهو اسم ما، وما قبله الخبر، ومن زائدة «أفلا تتقون» تخافون عقوبته بعبادتكم غيره.'),
(2697, 23, 24, '«فقال الملأ الذين كفروا من قومه» لأتباعهم «ما هذا إلا بشرٌ مثلكم يريد أن يتفضَّل» يتشرف «عليكم» بأن يكون متبوعاً وأنتم أتباعه «ولو شاء الله» أن لا يعبد غيره «لأنزل ملائكة» بذلك لا بشراً «ما سمعنا بهذا» الذي دعا إليه نوح من التوحيد «في آبائنا الأولين» الأمم الماضية.'),
(2698, 23, 25, '«إن هو» ما نوح «إلا رجل به جِنَّةٌ» حالة جنون «فتربَّصوا به» انتظروا «حتى حين» إلى زمن موته.'),
(2699, 23, 26, '«قال» نوح «ربَّ انصرني» عليهم «بما كذَّبونِ» بسبب تكذيبهم إياي بأن تهلكهم قال تعالى مجيباً دعاءه.'),
(2700, 23, 27, '«فأوحينا إليه أن اصنع الفلك» السفينة «بأعيننا» برأي منا وحفظنا «ووحينا» أمرنا «فإذا جاء أمرنا» بإهلاكهم «وفار التنور» للخباز بالماء وكان ذلك علامة لنوح «فاسلك فيها» أي أدخِلْ في السفينة «من كلِ زوجين» ذكر وأنثى، أي من كل أنواعهما «اثنين» ذكراً وأنثى وهو مفعول ومن متعلقة باسلك، ففي القصة أن الله تعالى حشر لنوح السباع والطير وغيرهما، فجعل يضرب بيديه في كل نوع فتقع يده اليمنى على الذكر واليسرى على الأنثى فيحملهما في السفينة، وفي قراءة كلِ بالتنوين فزوجين مفعول واثنين تأكيد له «وأهلك» زوجته وأولاده «إلا من سبق عليه القول منهم» بالإهلاك وهو زوجته وولده كنعان بخلاف سام وحام ويافث فحملهم وزوجاتهم ثلاثة، وفي سورة هود (ومن آمن وما آمن معه إلا قليل) قيل كانوا ستة رجال ونساؤهم وقبل جميع من كان في السفينة ثمانية وسبعون نصفهم رجال ونصفهم نساء «ولا تخاطبني في الذين ظلموا» كفروا بترك إهلاكهم «إنهم مغرقون».'),
(2701, 23, 28, '«فإذا استويت» اعتدلت «أنت ومن معك على الفلك فقل الحمد لله الذي نجانا من القوم الظالمين» الكافرين وإهلاكهم.'),
(2702, 23, 29, '«وقل» عند نزولك «رب أنزلني مُنزَلاً» بضم الميم وفتح الزاي مصدراً واسم مكان وبفتح الميم وكسر الزاي مكان النزول «مُباركاً» ذلك الإنزال أو المكان «وأنت خير المنزلين» ما ذكر.'),
(2703, 23, 30, '«إن في ذلك» المذكور من أمر نوح والسفينة وإهلاك الكفار «لآيات» دلالات على قدرة الله تعالى «وإن» مخففة من الثقيلة واسمها ضمير الشأن «كنا لمبتلين» قوم نوح بإرساله إليهم ووعظه.'),
(2704, 23, 31, '«ثم أنشأنا من بعدهم قَرناً» قوماً «آخرين» هم عاد.'),
(2705, 23, 32, '«فأرسلنا فيهم رسولاً منهم» هوداً «أن» بأن «اعبدوا الله ما لكم من إله غيره أفلا تتقون» عقابه فتؤمنون.'),
(2706, 23, 33, '«وقال الملأ من قومه الذين كفروا وكذبوا بلقاء الآخرة» بالمصير إليها «وأترفناهم» نعمناهم «في الحياة الدنيا ما هذا إلا بشر مثلكم يأكل مما تأكلون منه ويشرب مما تشربون».'),
(2707, 23, 34, '«و» الله «لئن أطعتم بشراً مثلكم» فيه قسم وشرط الثاني والجواب لأولهما وهو مغن عن جواب الثاني «أنكم إذاً» أي إذا أطعتموه «لخاسرون» أي مغبون.'),
(2708, 23, 35, '«أيعدكم أنكم إذا متُّم وكنتم تراباً وعظاماً أنكم مخرجون» هو خبر أنكم الأولى وأنكم الثانية تأكيد لها لما طال الفصل.'),
(2709, 23, 36, '«هَيْهات هَيْهات» اسم فعل ماض بمعنى مصدر: أي بعد «لما توعدون» من الإخراج من القبور واللام زائدة للبيان.'),
(2710, 23, 37, '«إن هي» أي ما الحياة «إلا حياتنا الدنيا نموت ونحيا» بحياة أبنائنا «وما نحن بمبعوثين».'),
(2711, 23, 38, '«إن هو» ما الرسول «إلا رجل افترى على الله كذباً وما نحن له بمؤمنين» مصدقين بالبعث بعد الموت.'),
(2712, 23, 39, '«قال رب انصرني بما كذبون».'),
(2713, 23, 40, '«قال عما قليل» من الزمان وما زائدة «ليصبحن» ليصيرن «نادمين» على كفرهم وتكذيبهم.'),
(2714, 23, 41, '«فأخذتهم الصيحة» صيحة العذاب والهلاك كائنة «بالحق» فماتوا «فجعلناهم غثاءً» هو نبت يبس أي صيرناهم مثله في اليبس «فبعداً» من الرحمة «للقوم الظالمين» المكذبين.'),
(2715, 23, 42, '«ثم أنشأنا من بعدهم قروناً» أقواماً «آخرين».'),
(2716, 23, 43, '«ما تسبق من أمة أجلها» بأن تموت قبله «وما يستأخرون» عنه ذكر الضمير بعد تأنيثه رعاية للمعنى.'),
(2717, 23, 44, '«ثم أرسلنا رسلنا تتراً» بالتنوين وعدمه متتابعين بين كل اثنين زمان طويل «كلما جاء أمة» بتحقيق الهمزتين وتسهيل الثانية بينها وبين الواو «رسولها كذبوه فأتبعنا بعضهم بعضاً» في الهلاك «وجعلناهم أحاديث فعبداً لقوم لا يؤمنون».'),
(2718, 23, 45, '«ثم أرسلنا موسى وأخاه هارون بآياتنا وسلطان مبين» حجة وهي اليد والعصا وغيرهما من الآيات.'),
(2719, 23, 46, '«إلى فرعون وملئه فاستكبروا» عن الإيمان بها وبالله «وكانوا قوماً عالين» قاهرين بني إسرائيل بالظلم.'),
(2720, 23, 47, '«فقالوا أنؤمن لبشرين مثلنا وقومهما لنا عابدون» مطيعون خاضعون.'),
(2721, 23, 48, '«فكذبوهما فكانوا من المهلكين».'),
(2722, 23, 49, '«ولقد آتينا موسى الكتاب» التوراة «لعلهم» قومه بني إسرائيل «يهتدون» به من الضلالة، وأوتيها بعد هلاك فرعون وقومه جملة واحدة.'),
(2723, 23, 50, '«وجعلنا ابن مريم» عيسى «وأمه آية» لم يقل آيتين لأن الآية فيهما واحدة: ولادته من غير فحل «وآوينهما إلى ربوة» مكان مرتفع وهو بيت المقدس أو دمشق أو فلسطين، أقوال «ذات قرار» أي مستوية يستقر عليها ساكنوها «ومعين» وماء جار ظاهر تراه العيون.'),
(2724, 23, 51, '«يا أيها الرسل كلوا من الطيبات» الحلالات «واعلموا صالحاً» من فرض ونفل «إني بما تعملون عليم» فأجازيكم عليه.'),
(2725, 23, 52, '«و» اعلموا «إنَّ هذه» أي ملة الإسلام «أمتكم» دينكم أيها المخاطبون أي يجب أن تكونوا عليها «أمة واحدة» حال لازمة وفي قراءة بتخفيف النون وفي أخرى بكسرها مشددة استئنافا «وأنا ربكم فاتقون» فاحذرون.'),
(2726, 23, 53, '«فتقطعوا» أي الأتباع «أمرهم» دينهم «بينهم زبرا» حال من فاعل تقطعوا أي أحزابا متخالفين كاليهود والنصارى وغيرهم «كل حزب بما لديهم» أي عندهم من الدين «فرحون» مسرورون.'),
(2727, 23, 54, '«فذرهم» اترك كفار مكة «في غمرتهم» ضلالتهم «حتى حين» إلى حين موتهم.'),
(2728, 23, 55, '«أيحسبون أنما نمدهم به» نعطيهم «من مال وبنين» في الدنيا.'),
(2729, 23, 56, '«نسارع» نعجل «لهم في الخيرات» لا «بل لا يشعرون» أن ذلك استدراج لهم.'),
(2730, 23, 57, '«إن الذين هم من خشية ربهم» خوفهم منه «مشفقون» خائفون من عذابه.'),
(2731, 23, 58, '«والذين هم بآيات ربهم» القرآن «يؤمنون» يصدقون.'),
(2732, 23, 59, '«والذين هم بربهم لا يشركون» معه غيره.'),
(2733, 23, 60, '«والذين يؤتون» يعطون «ما آتوْا» أعطوا من الصدقة والأعمال الصالحة «وقلوبهم وجلة» خائفة أن لا تقبل منهم «أنهم» يقدر قبله لام الجر «إلى ربهم راجعون».'),
(2734, 23, 61, '«أولئك يسارعون في الخيرات وهم لها سابقون» في علم الله.'),
(2735, 23, 62, '«ولا نكلف نفسا إلا وسعها» طاقتها فمن لم يستطع أن يصلي قائما فليصل جالسا، ومن لم يستطع أن يصوم فليأكل «ولدينا» عندنا «كتاب ينطق بالحق» بما عملته وهو واللوح المحفوظ تسطر فيه الأعمال «وهم» أي النفوس العاملة «لا يظلمون» شيئا منها فلا ينقص من ثواب أعمال الخيرات ولا يزاد في السيئات.'),
(2736, 23, 63, '«بل قلوبهم» أي الكفار «في غمره» جهالة «من هذا» القرآن «ولهم أعمال من دون ذلك» المذكور للمؤمنين «هم لها عاملون» فيعذبون عليها.'),
(2737, 23, 64, '«حتى» ابتدائية «إذا أخذنا مترفيهم» أغنياءهم ورؤساءهم «بالعذاب» أي السيف يوم بدر «إذا هم يجأرون» يضجون يقال لهم.'),
(2738, 23, 65, '«لا تجأروا اليوم إنكم منا لا تنصرون» لا تمنعون.'),
(2739, 23, 66, '«قد كانت آياتي» من القرآن «تتلى عليكم فكنتم على إعقابكم تنكصون» ترجعون القهقرى.'),
(2740, 23, 67, '«مستكبرين» عن الإيمان «به» أي بالبيت أو الحرم بأنهم أهله في أمن بخلاف سائر الناس في مواطنهم «سامرا» حال أي جماعة يتحدثون بالليل حول البيت «تهجرون» من الثلاثي تتركون القرآن، ومن الرباعي أي تقولن غير الحق في النبي والقرآن قال تعالى.'),
(2741, 23, 68, '«أفلم يدَّبروا» أصله يتدبروا فأدغمت التاء في الدال «القول» أي القرآن الدال على صدق النبي «أم جاءهم ما لم يأت آباءهم الأولين».'),
(2742, 23, 69, '«أم لم يعرفوا رسولهم فهم له منكرون».'),
(2743, 23, 70, '«أم يقولون به جنَّة» الاستفهام للتقرير بالحق من صدق النبيّ ومجيء الرسل للأمم الماضية ومعرفة رسولهم بالصدق والأمانة وأن لا جنون به «بل» للانتقال «جاءهم بالحق» أي القرآن المشتمل على التوحيد وشرائع الإسلام «وأكثرهم للحق كارهون».'),
(2744, 23, 71, '«ولو اتبع الحق» أي القرآن «أهواءهم» بأن جاء بما يهوونه من الشريك والولد لله، تعالى الله عن ذلك: «لفسدت السماوات والأرض ومن فيهن» خرجت عن نظامها المشاهد لوجود التمانع في الشيء عادة عند تعدد الحاكم «بل أتيناهم بذكرهم» أي القرآن الذي فيه ذكرهم وشرفهم «فهم عن ذكرهم معرضون».'),
(2745, 23, 72, '«أم تسألهم خرجا» أجرا على ما جئتهم به من الإيمان «فخراج ربك» أجره وثوابه ورزقه «خير» وفي قراءة خرجا في الموضعين وفي قراءة أخرى خراجا فيهما «وهو خير الرازقين» أفضل من أعطى وآجر.'),
(2746, 23, 73, '«وإنك لتدعوهم إلى صراط» طريق «مستقيم» أي دين الإسلام.'),
(2747, 23, 74, '«وإن الذين لا يؤمنون بالآخرة» بالبعث والثواب والعقاب «عن الصراط» أي الطريق «لناكبون» عادلون.'),
(2748, 23, 75, '«ولو رحمناهم وكشفنا ما بهم من ضر» أي جوع أصابهم بمكة سبع سنين «للجوا» تمادوا «في طغيانهم» ضلالتهم «يعمهون» يترددون.'),
(2749, 23, 76, '«ولقد أخذناهم بالعذاب» الجوع «فما استكانوا» تواضعوا «لربهم وما يتضرعون» يرغبون إلى الله بالدعاء.'),
(2750, 23, 77, '«حتى» ابتدائية «إذا فتحنا عليهم بابا ذا» صاحب «عذاب شديد» هو يوم بدر بالقتل «إذا هم فيه مبلسون» آيسون من كل خير.'),
(2751, 23, 78, '«وهو الذي أنشأ» خلق «لكم السمع» بمعنى الأسماع «والأبصار والأفئدة» القلوب «قليلاً ما» تأكيد للقلة «تشكرون».'),
(2752, 23, 79, '«وهو الذي ذرأكم» خلقكم «في الأرض وإليه تحشرون» تبعثون.'),
(2753, 23, 80, '«وهو الذي يحيى» بنفخ الروح في المضغة «ويميت وله اختلاف الليل والنهار» بالسواد والبياض والزيادة والنقصان «أفلا تعقلون» صنعه تعالى فتعتبرون.'),
(2754, 23, 81, '«بل قالوا مثل ما قال الأولون».'),
(2755, 23, 82, '«قالوا» أي الأولون «أئذا متنا وكنا ترابا وعظاما أئنا لمبعوثون» لا وفي الهمزتين في الموضعين التحقيق وتسهيل الثانية وإدخال ألف بينهما على الوجهين.'),
(2756, 23, 83, '«لقد وعدنا نحن وآباؤنا هذا» أي البعث بعد الموت «من قبل إن» ما «هذا إلا أساطير» أكاذيب «الأولين» كالأضاحيك والأعاجيب جمع أسطورة بالضم.'),
(2757, 23, 84, '«قل» لهم «لمن الأرض ومن فيها» من الخلق «إن كنتم تعلمون» خالقها ومالكها.'),
(2758, 23, 85, '«سيقولون لله قل» لهم «أفلا تذَّكرون» بإدغام التاء الثانية في الذال تتعظون فتعلمون أن القادر على الخلق ابتداء قادر على الإحياء بعد الموت.'),
(2759, 23, 86, '«قل من رب السماوات السبع ورب العرش العظيم» الكرسي.'),
(2760, 23, 87, '«سيقولون الله قل أفلا تتقون» تحذرون عبادة غيره.'),
(2761, 23, 88, '«قل من بيده ملكوت» ملك «كل شيء» والتاء للمبالغة «وهو يُجير ولا يُجار عليه» يَحمي ولا يُحمى عليه «إن كنتم تعلمون».'),
(2762, 23, 89, '«سيقولون لله» وفي قراءة بلام الجر في الموضعين نظرا إلى أن المعنى من له ما ذكر «قل فأنى تسحرون» تخدعون وتصرفون عن الحق عبادة الله وحده أي كيف تخيل لكم أنه باطل.'),
(2763, 23, 90, '«بل آتيناهم بالحق» بالصدق «وإنهم لكذبون» في نفيه وهو.'),
(2764, 23, 91, '«ما اتخذ الله من ولد وما كان معه من إله إذاً» أي لو كان معه إله «لذهب كل إله بما خلق» انفرد به ومنع الآخر من الاستيلاء عليه «ولعلا بعضهم على بعض» مغالبة كفعل ملوك الدنيا «سبحان الله» تنزيهاً له «عما يصفونـ» ـه به مما ذكر.'),
(2765, 23, 92, '«عالم الغيب والشهادة» ما غاب وما شوهد بالجر صفة والرفع خبر هو مقدرا «فتعالى» تعظم «عما يشركونـ» ـه معه.'),
(2766, 23, 93, '«قل رب إما» فيه إدغام إن الشرطية في الزائدة «ترينّي ما يوعدونـ» ـه من العذاب هو صادق بالقتل ببدر.'),
(2767, 23, 94, '«رب فلا تجعلني في القوم الظالمين» فأهلك بإهلاكهم.'),
(2768, 23, 95, '«وإنا على أن نريك ما نعدهم لقادرون».'),
(2769, 23, 96, '«أدفع بالتي هي أحسن» أي الخصلة من الصفح والإعراض عنهم «السيئة» أذاهم إياك وهذا قبل الأمر بالقتال «نحن أعلم بما يصفون» يكذبون ويقولون فنجازيهم عليه.'),
(2770, 23, 97, '«وقل رب أعوذ» أعتصم «بك من همزات الشياطين» نزعاتهم بما يوسوسون به.'),
(2771, 23, 98, '«وأعوز بك رب أن يحضرون» في أموري لأنهم إنما يحضرون بسوء.'),
(2772, 23, 99, '«حتى» ابتدائية «إذا جاء أحدهم الموت» ورأى مقعده من النار ومقعده من الجنة لو آمن «قال رب ارجعون» الجمع للتعظيم.'),
(2773, 23, 100, '«لعلي أعمل صالحا» بأن أشهد أن لا إله إلا الله يكون «فيما تركت» ضيعت من عمري أي في مقابلته قال تعال: «كلا» أي لا رجوع «إنها» أي رب ارجعون «كلمة هو قائلها» ولا فائدة له فيها «ومن ورائهم» أمامهم «برزخ» حاجز يصدهم عن الرجوع «إلى يوم يبعثون» ولا رجوع بعده.'),
(2774, 23, 101, '«فإذا نُفخ في الصور» القرن النفخة الأولى أو الثانية «فلا أنساب بينهم يومئذ» يتفاخرون بها «ولا يتساءلون» عنها خلاف حالهم في الدنيا لما يشغلهم من عظم الأمر عن ذلك في بعض مواطن القيامة، وفي بعضها يفيقون وفي آية (فأقبل بعضهم على بعض يتساءلون).'),
(2775, 23, 102, '«فمن ثقلت موازينه» بالحسنات «فأولئك هم المفلحون» الفائزون.'),
(2776, 23, 103, '«ومن خفت موازينه» بالسيئات «فأولئك الذين خسروا أنفسهم» فهم «في جهنم خالدون».'),
(2777, 23, 104, '«تلفح وجوههم النار» تحرقها «وهم فيها كالحون» شمرت شفاههم العليا والسفلى عن أسنانهم، ويقال لهم.'),
(2778, 23, 105, '«ألم تكن آياتي» من القرآن «تتلي عليكم» تُخوَّفُون بها «فكنتم بها تكذبون».'),
(2779, 23, 106, '«قالوا ربنا غلبت علينا شقوتنا» وفي قراءة شقاوتنا بفتح أوله وألف وهما مصدران بمعنى «وكنا قوماً ضالين» عن الهداية.'),
(2780, 23, 107, '«ربنا أخرجنا منها فإن عدنا» إلى المخالفة «فإنا ظالمون».'),
(2781, 23, 108, '«قال» لهم بلسان مالك بعد قدر الدنيا مرتين «أخسؤا فيها» ابعدوا في النار أذلاء «ولا تكلمون» في رفع العذاب عنكم لينقطع رجاؤهم.'),
(2782, 23, 109, '«إنه كان فريق من عبادي» هم المهاجرون «يقولون ربنا آمنا فاغفر لنا وارحما وأنت خير الراحمين».'),
(2783, 23, 110, '«فاتخذتموهم سُخرياً» بضم السين وكسرها مصدر بمعنى الهزء، منهم: بلال وصهيب وعمار وسلمان «حتى أنسوكم ذكري» فتركتموه لاشتغالكم بالاستهزاء بهم فهم سبب الإنساء فنسب إليهم «وكنتم منهم تضحكون».'),
(2784, 23, 111, '«إني جزيتهم اليوم» النعيم المقيم «بما صبروا» على استهزائكم بهم وأذاكم إياهم «إنهم» بكسر الهمزة «هم الفائزون» بمطلوبهم استئناف وبفتحها مفعول ثان لجزيتهم.'),
(2785, 23, 112, '«قال» تعالى لهم بلسان مالك وفي قراءة قل «كم لبثتم في الأرض» في الدنيا وفي قبوركم «عدد سنين» تمييز.'),
(2786, 23, 113, '«قالوا لبثنا يوماً أو بعض يوم» شكّوا في ذلك واستقصروه لعظم ما هم فيه من العذاب «فاسأل العادين» أي الملائكة المحصين أعمال الخلق.'),
(2787, 23, 114, '«قال» تعالى بلسان مالك وفي قراءة قل «إن» أي ما «لبثتم إلا قليلاً لو أنكم كنتم تعلمون» مقدرا لبثكم من الطول كان قليلا بالنسبة إلى لبثكم في النار.'),
(2788, 23, 115, '«أفحسبتم أنما خلقناكم عبثا» لا لحكمة «وأنكم إلينا لا ترجعون» بالبناء للفاعل وللمفعول؟ لا بل لنتعبدكم بالأمر والنهي ترجعوا إلينا ونجازي على ذلك (وما خلقت الجن والإنس إلا ليعبدون).'),
(2789, 23, 116, '«فتعالى الله» عن العبث وغيره مما لا يليق به «الملك الحق لا إله إلا هو رب العرش الكريم» الكرسي: هو السرير الحسن.'),
(2790, 23, 117, '«ومن يدع مع الله إلها آخر لا برهان له به» صفة كاشفة لا مفهوم لها «فإنما حسابه» جزاؤه «عند ربه إنه لا يفلح الكافرون» لا يسعدون.'),
(2791, 23, 118, '«وقل رب اغفر وارحم» المؤمنين في الرحمة زيادة عن المغفرة «وأنت خير الراحمين» أفضل راحم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'هذه «سورة أنزلناها وفرضناها» مخففة ومشددة لكثرة المفروض فيها «وأنزلنا فيها آيات بينات» واضحات الدلالات «لعلكم تذَّكرون» بإدغام التاء الثانية في الذال تتعظون'),
(2793, 24, 2, '«الزانية والزاني» أي غير المحصنين لرجمهما بالسنة وأل فيما ذكر موصولة وهو مبتدأ ولشبه بالشرط دخلت الفاء في خبره وهو «فاجلدوا كل واحد منهما مائة جلدة» ضربة يقال جَلَدهُ: ضربَ جلدهُ ويزاد على ذلك بالسنة تغريب عام والرقيق على النصف مما ذكر «ولا تأخذكم بهما رأفة في دين الله» أي حكمه بأن تتركوا شيئا من حدهما «إن كنتم تؤمنون بالله واليوم الآخر» أي يوم البعث في هذا تحريض على ما قبل الشرط وهو جوابه أو دال على جوابه «وليشهد عذابهما» الجلد «طائفة من المؤمنين» قيل ثلاثة وقيل أربعة عدد شهود الزنا.'),
(2794, 24, 3, '«الزاني لا ينكح» يتزوج «إلا زانية أو مشركة والزانية لا ينكحها إلا زان أو مشرك» أي المناسب لكل منهما ما ذكر «وحرم ذلك» أي نكاح الزواني «على المؤمنين» الأخيار، نزل ذلك لما همَّ فقراء المهاجرين أن يتزوجوا بغيا المشركين وهن موسرات لينفقن عليهم فقيل التحريم خاص بهم وقيل عام ونسخ بقوله تعالى (وأنكحوا الأيامى منكم).'),
(2795, 24, 4, '«والذين يرمون المحصنات» العفيفات بالزنا «ثم لم يأتوا بأربعة شهداء» على زناهن برؤيتهم «فاجلدوهم» أي كل واحد منهم «ثمانين جلدة ولا تقبلوا لهم شهادة» في شيء «أبدا وأولئك هم الفاسقون» لإتيانهم كبيرة.'),
(2796, 24, 5, '«إلا الذين تابوا من بعد ذلك وأصلحوا» عملهم «فإن الله غفور» لهم قذفهم «رحيم» بهم بإلهامهم التوبة فيها ينتهي فسقهم وتقبل شهادتهم وقيل لا تقبل رجوعا بالاستثناء إلى الجملة الأخيرة.'),
(2797, 24, 6, '«والذين يرمون أزواجهم» بالزنا «ولم يكن لهم شهداء» عليه «إلا أنفسهم» وقع ذلك لجماعة من الصحابة «فشهادة أحدهم» مبتدأ «أربع شهادات» نصب على المصدر «بالله إنه لمن الصادقين» فيما رمى به زوجته من الزنا.'),
(2798, 24, 7, '«والخامسة أن لعنة الله عليه إن كان من الكاذبين» في ذلك وخبر المبتدأ: تدفع عنه حد القذف.'),
(2799, 24, 8, '«ويدرأ» أي يدفع «عنها العذاب» حد الزنا الذي ثبت بشهاداته «أن تشهد أربع شهادات بالله إنه لمن الكاذبين» فيما رماها من الزنا.'),
(2800, 24, 9, '«والخامسة أن غضب الله عليها إن كان من الصادقين» في ذلك.'),
(2801, 24, 10, '«ولولا فضل الله عليكم ورحمته» بالستر في ذلك «وأن الله تواب» بقبوله التوبة في ذلك وغيره «حكيم» فيما حكم به في ذلك وغيره ليبين الحق في ذلك وعاجل بالعقوبة من يستحقها.'),
(2802, 24, 11, '(إن الذين جاءوا بالإفك) أسوأ الكذب على عائشة رضي الله عنها، أم المؤمنين بقذفها (عصبة منكم) جماعة من المؤمنين قالت: حسان بن ثابت، وعبد الله بن أبي، ومسطح، وحمنة بنت جحش (لا تحسبوه) أيها المؤمنون غير العصبة (شرا لكم بل هو خير لكم) يأجركم الله به، ويظهر براءة عائشة ومن جاء معها منه وهو صفوان، فإنها قالت: " كنت مع النبي صلى الله عليه وسلم في غزوة بعدما أنزل الحجاب، ففرغ منها ورجع ودنا من المدينة، وآذن بالرحيل ليلة فمشيت وقضيت شأني وأقبلت إلى الرحل فإذا عقدي انقطع ـ هو بكسر المهملة: القلادة ـ فرجعت ألتمسه، وحملوا هودجي ـ هو ما يركب فيه ـ على بعيري يحسبونني فيه، وكانت النساء خفافا إنما يأكلن العلقة ـ هو بضم المهملة وسكون اللام من الطعام: أي القليل ـ ووجدت عقدي وجئت بعدما ساروا فجلست في المنزل الذي كنت فيه، وظننت أن القوم سيفقدونني فيرجعون إلي فغلبتني عيناي فنمت وكان صفوان قد عرس من وراء الجيش فأدلج ـ هما بتشديد الراء والدال أي نزل من آخر الليل للاستراحة ـ فسار منه فأصبح في منزله فرأى سواد إنسان نائم ـ أي شخصه ـ فعرفني حين رآني، وكان يراني قبل الحجاب، فاستيقظت باست'),
(2803, 24, 12, '«لولا» هلا «إذ» حين «سمعتموه ظن المؤمنين والمؤمنات بأنفسهم» أي بعضهم ببعض «خيرا وقالوا هذا إفك مبين» كذب بيِّن، فيه التفات عن الخطاب أي ظننتم أيها العصبة وقلتم.'),
(2804, 24, 13, '«لولا» هلا «جاءُوا» أي العصبة «عليه بأربعة شهداء» شاهدوه «فإذا لم يأتوا بالشهداء فأولئك عند الله» أي في حكمه «هم الكاذبون» فيه.'),
(2805, 24, 14, '«ولولا فضل الله عليكم ورحمته في الدنيا والآخرة لمسكم فيما أفضتم» أيها العصبة أي خضتم «فيه عذاب عظيم» في الآخرة.'),
(2806, 24, 15, 'إذ تلقونه بألسنتكم» أي يرونه بعضكم عن بعض وحذف من الفعل إحدى التاءين وإذ منصوب بمسكم أو بأفضتم «وتقولون بأفواهكم ما ليس لكم به علم وتحسبونه هينا» لا إثم فيه «وهو عند الله عظيم» في الإثم.'),
(2807, 24, 16, '«ولولا» هلا «إذ» حين «سمعتموه قلتم ما يكون» ما ينبغي «لنا أن نتكلم بهذا سبحانك» هو للتعجيب هنا «هذا بهتان» كذب «عظيم».'),
(2808, 24, 17, '«يعظكم الله» ينهاكم «أن تعودوا لمثله أبدا إن كنتم مؤمنين» تتعظون بذلك.'),
(2809, 24, 18, '«ويبين الله لكم الآيات» في الأمر والنهي «والله عليم» بما يأمر به وينهي عنه «حكيم» فيه.'),
(2810, 24, 19, '«إن الذين يحبون أن تشيع الفاحشة» باللسان «في الذين آمنوا» بنسبتها إليهم وهم العصبة «لهم عذاب أليم في الدنيا» بحد القذف «والآخرة» بالنار لحق الله «والله يعلم» انتفاءها عنهم «وأنتم» أيها العصبة بما قلتم من الإفك «لا تعلمون» وجودها فيهم.'),
(2811, 24, 20, '«ولولا فضل الله عليكم» أيها العصبة «ورحمته وأن الله رؤوف رحيم» بكم لعاجلكم بالعقوبة.'),
(2812, 24, 21, '«يا أيها الذين آمنوا لا تتبعوا خطوات الشيطان» أي طرق تزينه «ومن يتبع خطوات الشيطان فإنه» أي المتبع «يأمر بالفحشاء» أي القبيح «والمنكر» شرعاً باتباعها «ولولا فضل الله عليكم ورحمته ما زكى منكم» أيها العصبة بما قلتم من الإفك «من أحد أبداً» أي ما صلح وطهر من هذا الذنب بالتوبة منه «ولكن الله يزكي» يطهر «من يشاء» من الذنب بقبول توبته منه «والله سميع» بما قلتم «عليم» بما قصدتم.'),
(2813, 24, 22, '«ولا يأتل» يحلف «أولوا الفضل» أصحاب الغنى «منكم والسعة أن» لا «يؤتوا أولي القربى والمساكين والمهاجرين في سبيل الله» نزلت في أبي بكر حلف أن لا ينفق على مسطح وهو ابن خالته مسكين مهاجر بدري لما خاض في الإفك بعد أن كان ينفق عليه، وناس من الصحابة أقسموا أن لا يتصدقوا على من تكلم بشيء من الإفك «وليعفوا وليصفحوا» عنهم في ذلك «ألا تحبون أن يغفر الله لكم والله غفور رحيم» للمؤمنين قال أبو بكر: بلى أنا أحب أن يغفر الله لي ورجع إلى مسطح ما كان ينفقه عليه.'),
(2814, 24, 23, '«إن الذين يرمون» بالزنا «المحصنات» العفائف «الغافلات» عن الفواحش بأن لا يقع في قلوبهن فعلها «المؤمنات» بالله ورسوله «لعنوا في الدنيا والآخرة ولهم عذاب عظيم».'),
(2815, 24, 24, '«يوم» ناصبه الاستقرار الذي تعلق به لهم «تشهد» بالفوقانية والتحتانية «عليهم ألسنتهم وأيديهم وأرجلهم بما كانوا يعملون» من قول وفعل وهو يوم القيامة.'),
(2816, 24, 25, '«يومئذٍ يوفيهم الله دينهم الحق» يجازيهم جزاءَه الواجب عليهم «ويعلمون أن الله هو الحق المبين» حيث حقق لهم جزاءه الذي كانوا يشكون فيه ومنهم عبد الله بن أبيّ والمحصنات هنا أزواج النبي صلى الله عليه وسلم لم يذكر في قذفهن توبة ومن ذكر في قذفهن أول سورة التوبة غيرهن.'),
(2817, 24, 26, '«الخبيثات» من النساء ومن الكلمات «للخبيثين» من الناس «والخبيثون» من الناس «للخبيثات» مما ذكر «والطيبات» مما ذكر «للطيبين» من الناس «والطيبون» منهم «للطيبات» مما ذكر أي اللائق بالخبيث مثله وبالطيب مثله «أولئك» الطيبون والطيبات من النساء ومنهم عائشة وصفوان «مبرؤون مما يقولون» أي الخبيثون من الرجال والنساء فيهم «لهم» للطيبين والطيبات «مغفرة ورزق كريم» في الجنة وقد افتخرت عائشة بأشياء منها أنها خلقت طيبة ووعدت مغفرة ورزقاً كريماً.'),
(2818, 24, 27, '«يا أيها الذين آمنوا لا تدخلوا بيوتا غير بيوتكم حتى تستأنوا» أي تستأذنوا «وتسلموا على أهلها» فيقول الواحد السلام عليكم أأدخل؟ كما ورد في حديث «ذلكم خير لكم» من الدخول بغير استئذان «لعلكم تذكرون» بإدغام التاء الثانية في الذال خيريته فتعملون به.'),
(2819, 24, 28, '«فإن لم تجدوا فيها أحدا» يأذن لكم «فلا تدخلوها حتى يؤذن لكم وإن قيل لكم» بعد الاستئذان «ارجعوا فارجعوا هو» أي الرجوع «أزكى» أي خير «لكم» من القعود على الباب «والله بما تعملون» من الدخول بإذن وغير إذن «عليم» فيجازيكم عليه.'),
(2820, 24, 29, '«ليس عليكم جناح أن تدخلوا بيوتا غير مسكونة فيها متاعٌ» أي منفعة «لكم» باستكنان وغيره كبيوت الربط والخانات المسبلة «والله يعلم ما تبدون» تظهرون «وما تكتمون» تخفون في دخول غير بيوتكم من قصد صلاح أو غيره، وسيأتي أنهم إذا دخلوا بيوتهم يسلمون على أنفسهم.'),
(2821, 24, 30, '«قل للمؤمنين يغضوا من أبصارهم» عما لا يحلُّ لهم نظره، ومن زائدة «ويحفظوا فروجهم» عما لا يحل لهم فعله بها «ذلك أزكي» أي خير «لهم إن الله خبير بما يصنعون» بالأبصار والفروج فيجازيهم عليه.'),
(2822, 24, 31, '«وقل للمؤمنات يغضضن من أبصارهنّ» عما لا يحل لهنّ نظره «ويحفظن فروجهنَّ» عما لا يحل لهنَّ فعله بها «ولا يبدين» يُظهرن «زينتهن إلا ما ظهر منها» وهو الوجه والكفان فيجوز نظره لأجني إن لم يخف فتنة في أحد وجهين، والثاني يحرم لأنه مظنة الفتنة، ورجح حسما للباب «وليضرين بخمرهنَّ على جيوبهنَّ» أي يسترن الرؤوس والأعناق والصدور بالمقانع «ولا يبدين زينتهنّ» الخفية، وهي ما عدا الوجه والكفين «إلا لبعولتهن» جمع بعل: أي زوج «أو آبائهن أو آباء بعولتهن أو أبنائهن أو أبناء بعولتهن أو إخواتهن أو بني إخوانهن أو بني أخوانهن أو نسائهن أو ما ملكت أيمانهن» فيجوز لهم نظره إلا ما بين السرة والركبة فيحرم نظره لغير الأزواج وخرج بنسائهن الكافرات فلا يجوز للمسلمات الكشف لهنَّ وشمل ما ملكت أيمانهنَّ العبيد «أو التابعين» في فضول الطعام «غير» بالجر صفة والنصب استثناء «أولي الإربة» أصحاب الحاجة إلى النساء «من الرجال» بأن لم ينتشر ذكر كل «أو الطفل» بمعنى الأطفال «الذين لم يظهروا» يطلعوا «على عورات النساء» للجماع فيجوز أن يبدين لهم ما عدا ما بين السرة والركبة «ولا يضربن بأرجلهن ليعلم ما يخفين من زينتهن» من خلخال يتقعقع «وتوبوا إلى الله جميعا أيها المؤمنون» مما وقع لكم من النظر الممنوع منه ومن غيره «لعلكم تفلحون» تنجون من ذلك لقبول التوبة منه وفي الآية تغليب الذكور على الإناث.'),
(2823, 24, 32, '«وأنكحوا الأيامى منكم» جمع أيم: وهي من ليس لها زوج بكرا كانت أو ثيبا ومن ليس له زوج وهذا في الأحرار والحرائر «والصالحين» المؤمنين «من عبادكم وإمائكم» وعباد من جموع عبد «إن يكونوا» أي الأحرار «فقراء يغنهم الله» بالتزوج «من فضله والله واسع» لخلقه «عليم» بهم.'),
(2824, 24, 33, '«وليستعفف الذين لا يجدون نكاحا» ما ينكحون به من مهر ونفقة عن الزنا «حتى يغنيهم الله» يوسع عليهم «من فضله» فينكحون «والذين يبتغون الكتاب» بمعنى المكاتبة «مما ملكت أيمانكم» من العبيد والإماء «فكاتبوهم إن علمتم فيهم خيرا» أي أمانة وقدرة على الكسب لأداء مال الكتابة وصيغتها مثلا: كاتبتك على ألفين في شهرين كل شهر ألف فإذا أديتها فأنت حر فيقول قبلت «وآتوهم» أمر للسادة «من مال الله الذي آتاكم» ما يستعينون به في أداء ما التزموه لكم وفي معنى الإيتاء حط شيء مما التزموه «ولا تكرهوا فتياتكم» إماءكم «على البغاء» الزنا «إن أردن تحصنا» تعففا عنه وهذه الإرادة محل الإكراه فلا مفهوم للشرط «لتبتغوا» بالإكراه «عرض الحياة الدنيا» نزلت في عبد الله بن أبي كان يكره جواريه على الكسب بالزنا «ومن يكرههن فإن الله من بعد إكراههن غفور» لهن «رحيم» بهن.'),
(2825, 24, 34, '«ولقد أنزلنا إليكم آيات مبيَّنات» بفتح الياء وكسرها في هذه السورة، بيَّن فيها ما ذكر أو بينة «ومثلاً» خبرا عجيبا وهو خبر عائشة «من الذين خلوْا من قبلكم» أي من جنس أمثالهم أي أخبارهم العجيبة كخبر يوسف ومريم «وموعظة للمتقين» في قوله تعالى (ولا تأخذكم بها رائفة في دين الله) (لولا إذ سمعتموه ظن المؤمنون) الخ (ولولا إذ سمعتموه قلتم) الخ (يعظكم الله أن تعودوا) الخ وتخصيصها بالمتقين لأنهم المنتفعون بها.'),
(2826, 24, 35, '«الله نور السماوات والأرض» أي منورهما بالشمس والقمر «مثل نوره» أي صفته في قلب المؤمن «كمشكاة فيها مصباح المصباح في زجاجة» هي القنديل والمصباح السراج: أي الفتيلة الموقودة، والمشكاة: الطاقة غير النافذة، أي الأنبوبة في القنديل «الزجاجة كأنها» والنور فيها «كوكبٌ دِرِّيٌ» أي مضيء بكسر الدال وضمها من الدرء بمعنى الدفع لدفعها الظلام، وبضمها وتشديد الياء منسوب إلى الدر: اللؤلؤ «توَقَّد» المصباح بالماضي، وفي قراءة بمضارع أو قد مبنيا للمفعول بالتحتانية وفي أخرى توقد بالفوقانية، أي الزجاجة «مِن» زيت «شجرة مباركةِ زيتونةِ لا شرقيةِ ولا غربيةِ» بل بينهما فلا يتمكن منها ولا برد مضران «يكاد زيتها يضيء ولو لم تمسسهُ نار» لصفائه «نور» به «على نور» بالنار، ونور الله: أي هداه للمؤمنين نور على نور الإيمان «يهدي الله لنوره» أي دين الإسلام «من يشاء ويضرب» يبين «الله الأمثال للناس» تقريبا لأفهامهم ليعتبروا فيؤمنوا «والله بكل شيء عليم» ومنه ضرب الأمثال.'),
(2827, 24, 36, '«في بيوت» متعلق بيسبح الآتي «أذن الله أن ترفع» تعظم «ويذكر فيها اسمه» بتوحيده «يسبَّح» بفتح الموحدة وكسرها: أي يُصلِي «له فيها بالغدو» مصدر بمعنى الغدوات: أي البُكر «والآصال» العشايا من بعد الزوال.'),
(2828, 24, 37, '«رجال» فاعل يسبح بكسر الباء وعلى فتحها نائب الفاعل له ورجال فاعل فعل مقدر جواب سؤال مقدر كأنه قيل: من يسبحه «لا تلهيهم تجارة» شراء «ولا بيع عن ذكر الله وإقام الصلاة» حذف هاء إقامة تخفيف «وإيتاء الزكاة يخافون يوما تتقلب» تضطرب «فيه القلوب والأبصار» من الخوف، القلوب بين النجاة والهلاك والأبصار بين ناحيتي اليمين والشمال: وهو يوم القيامة.'),
(2829, 24, 38, '«ليجزيهم الله أحسن ما عملوا» أي ثوابه وأحسن بمعنى حسن «ويزيدهم من فضله والله يرزق من يشاءُ بغير حساب» يقال فلان ينفق بغير حساب: أي يوسع كأنه لا يحسب ما ينفقه.'),
(2830, 24, 39, '«والذين كفروا أعمالهم كسراب بقيعةِ» جمع قاع: أي فيلاه وهو شعاع يرى فيها نصف النهار في شدة الحر يشبه الماء الجاري «يحسبه» يظنه «الظمآن» أي العطشان «ماءً حتى إذا جاءه لم يجده شيئا» مما حسبه كذلك الكافر يحسب أن عمله كصدقه ينفعه حتى إذا مات وقدم على ربه لم يجد عمله أي لم ينفعه «ووجد الله عنده» أي عند عمله «فوفَّاه حسابه» أي جازاه عليه في الدنيا «والله سريع الحساب» أي المجازاة.'),
(2831, 24, 40, '«أو» الذين كفروا أعمالهم السيئة «كظلمات في بحر لجَّي» عميق «يغشاه موج من فوقه» أي الموج «موج من فوقه» أي الموج الثاني «سحاب» أي غيم، هذه «ظلمات بعضها فوق بعض» ظلمة البحر وظلمة الموج الأول، وظلمة الثاني وظلمة السحاب «إذا أخرج» الناظر «يده» في هذه الظلمات «لم يكد يراها» أي لم يقرب من رؤيتها «ومن لم يجعل الله له نورا فما له من نور» أي من لم يهده الله لم يهتد.'),
(2832, 24, 41, '«ألم تر أن الله يسبح له من في السماوات والأرض» ومن التسبيح صلاة «والطير» جمع طائر بين السماء والأرض «صافّات» حال باسطات أجنحتهنَّ «كل قد علم» الله «صلاته وتسبيحه والله عليم بما يفعلون» فيه تغليب العاقل.'),
(2833, 24, 42, '«ولله ملك السماوات والأرض» خزائن المطر والرزق والنبات «وإلى الله المصير» المرجع.'),
(2834, 24, 43, '«ألم تر أن الله يزجي سحابا» يسوقه برفق «ثم يؤلف بينه» يضم بعضه إلى بعض فيجعل القطع المتفرقة قطعة واحدة «ثم يجعله ركاما» بعضه فوق بعض «فترى الوَدْق» المطر «يخرج من خلاله» مخارجه «وينزل من السماء من» زائدة «جبال فيها» في السماء بدل بإعادة الجار «من بَرَدِ» أي بعضه «فيصيب به من يشاء ويصرفه عن من يشاء يكاد» يقرب «سنا برقه» لمعانه «يذهب بالأبصار» الناظرة له: أي يخطفها.'),
(2835, 24, 44, '«يقلب الله الليل والنهار» أي يأتي بكل منهما بدل الآخر «إن في ذلك» التقليب «لعبرة» دلالة «لأولي الأبصار» لأصحاب البصائر على قدرة الله تعالى.'),
(2836, 24, 45, '«والله خلق كل دابة» أي حيوان «من ماءٍ» نطفة «فمنهم من يمشي على بطنه» كالحيات والهوام «ومنهم من يمشي على رجلين» كالإنسان والطير «ومنهم من يمشي على أربع» كالبهائم والأنعام «يخلق الله ما يشاء إن الله على كل شيء قدير».'),
(2837, 24, 46, '«لقد أنزلنا آيات مبينات» أي بينات هي القرآن «والله يهدي من يشاء إلى صراط» طريق «مستقيم» أي دين الإسلام.'),
(2838, 24, 47, '«ويقولون» المنافقون «آمنا» صدقنا «بالله» بتوحيده «وبالرسول» محمد «وأطعنا» هما فيما حكما به «ثم يتولى» يعرض «فريق منهم من بعد ذلك» عنه «وما أولئك» المعرضون «بالمؤمنين» المعهودين الموافق قلوبهم لألسنتهم.'),
(2839, 24, 48, '«وإذا دعوا إلى الله ورسوله» المبلغ عنه «ليحكم بينهم إذا فريق منهم معرضون» عن المجيء إليه.'),
(2840, 24, 49, '«وإن يكن لهم الحق يأتوا إليه مذعنين» مسرعين طائعين.'),
(2841, 24, 50, '«أفي قلوبهم مرض» كفر «أم ارتابوا» أي شكوا في نبوته «أم يخافون أن يحيف الله عليهم ورسوله» في الحكم أي فيظلموا فيه؟ لا «بل أولئك هم الظالمون» بالإعراض عنه.'),
(2842, 24, 51, '«إنما كان قول المؤمنين إذا دعوا إلى الله ورسوله ليحكم بينهم» فالقول اللائق بهم «أن يقولوا سمعنا وأطعنا» بالإجابة «وأولئك» حينئذ «هم المفلحون» الناجون.'),
(2843, 24, 52, '«ومن يطع الله ورسوله ويخش الله» يخافه «ويتقهْ» بسكون الهاء وكسرها بأن يطيعه «فأولئك هم الفائزون» بالجنة.'),
(2844, 24, 53, '«وأقسموا بالله جهد إيمانهم» غايتها «لئن أمرتهم» بالجهاد «ليخرجنَّ قل» لهم «لا تقسموا طاعة معروفة» للنبي خير من قسمكم الذي لا تصدقون فيه «إن الله خبير بما تعلمون» من طاعتكم بالقول ومخالفتكم بالفعل.'),
(2845, 24, 54, '«قل أطيعوا الله وأطيعوا الرسول فان تولوْ» عن طاعته بحذف إحدى التاءين خطاب لهم «فإنما عليه ما حمل» من التبليغ «وعليكم ما حملتم» من طاعته «وإن تطيعوه تهتدوا وما على الرسول إلا البلاغ المبين» أي التبليغ البيِّن.'),
(2846, 24, 55, '«وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض» بدلا عن الكفار «كما استخلف» بالبناء للفاعل والمفعول «الذين من قبلهم» من بنى إسرائيل بدلا عن الجبارة «وليمكنن لهم دينهم الذي أرتضى لهم» وهو الإسلام بأن يظهر على جميع الأديان ويوسع لهم في البلاد فيملكوها «وَلَيُبْدلَنَّهُمْ» بالتخفيف والتشديد «من بعد خوفهم» من الكفار «أمنا» وقد أنجز الله وعده لهم بما ذكر وأثنى عليهم بقوله: «يعبدونني لا يشركون بي شيئا» هو مستأنف في حكم التعليل «ومن كفر بعد ذلك» الإنعام منهم به «فأولئك هم الفاسقون» وأول من كفر به قتلة عثمان رضي الله عنه فصاروا يقتتلون بعد أن كانوا إخوانا.'),
(2847, 24, 56, '«وأقيموا الصلاة وآتوا الزكاة وأطيعوا الرسول لعلكم ترحمون» أي رجاء الرحمة.'),
(2848, 24, 57, '«لا تحسبن» بالفوقانية والتحتانية والفاعل الرسول «الذين كفروا معجزين» لنا «في الأرض» بأن يفوتونا «ومأواهم» مرجعهم «النار ولبئس المصير» المرجع هي.'),
(2849, 24, 58, '«يا أيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم» من العبيد آمنوا والإماء «والذين لم يبلغوا الحلم منكم» من الأحرار وعرفوا أمر النساء «ثلاث مرات» في ثلاثة أوقات «من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة» أي وقت الظهر «ومن بعد الصلاة العشاء ثلاث عورات لكم» بالرفع خبر مبتدأ بعده مضاف وقام المضاف إليه مقامه: أي هي أوقات وبالنصب بتقدير أوقات منصوبا بدلاً من محل ما قبله قام المضاف إليه مقامه، وهي لإلقاء الثياب تبدو فيها العورات «ليس عليكم ولا عليهم» أي المماليك والصبيان «جناح» في الدخول عليكم بغير استئذان «بعدهن» أي بعد الأوقات الثلاثة هم «طوافون عليكم» للخدمة «بعضكم» طائف «على بعض» والجملة مؤكدة لما قبلها «كذلك» كما بين ما ذكر «يبين الله لكم الآيات» أي الأحكام «والله عليم» بأمور خلقه «حكيم» بما دبره لهم وآية الاستئذان قيل منسوخة وقيل لا ولكن تهاون الناس في ترك الاستئذان.'),
(2850, 24, 59, '«وإذا بلغ الأطفال» أيها الأحرار «منكم الحلم فليستأذنوا» في جميع الأوقات «كما استأذن الذين من قبلهم» أي الأحرار الكبار «كذلك يبين الله لكم آياته والله عليم حكيم»'),
(2851, 24, 60, '«والقواعد من النساء» قعدن عن الحيض والولد لكبرهن «اللاتي لا يرجون نكاحا» لذلك «فليس عليهن جناح أن يضعن ثيابهن» من الجلباب والرداء والقناع فوق الخمار «غير متبرجات» مظهرات «بزينة» خفيفة كقلادة وسوار وخلخال «وأن يستعففن» بأن لا يضعنها «خير لهن والله سميع» لقولكم «عليم» بما في قلوبكم.'),
(2852, 24, 61, '«ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج» في مؤاكلة مقابليهمْ «ولا» حرج «على أنفسكم أن تأكلوا من بيوتكم» أولادكم «أو بيوت آبائكم أو بيوت أمهاتكم أو بيوت إخوانكم أو بيوت أخواتكم أو بيوت أعمامكم أو بيوت عماتكم أو بيوت أخوالكم أو بيوت خالاتكم أو ما ملكتم مفاتحه» خزنتموه لغيركم «أو صديقكم» وهو من صدقكم في مودته المعنى يجوز الأكل من بيوت من ذكر وإن لم يحضروا إذا علم رضاهم به «ليس عليكم جناح أن تأكلوا جميعا» مجتمعين «أو أشتاتا» متفرقين جمع شت نزل فيمن تحرج أن يأكل وحده وإذا لم بجد يؤاكله يترك الأكل «فإذا دخلتم بيوتا» لكم لا أهل بها «فسلموا على أنفسكم» قولوا السلام علينا وعلى عباد الله الصالحين فإن الملائكة ترد عليكم وإن كان بها أهل فسلموا عليهم «تحية» حيا «من عند الله مباركة طيبة» يثاب عليها «كذلك يبيَّن الله لكم الآيات» أي يفضل لكم معالم دينكم «لعلكم تعقلون» لكي تفهموا ذلك.'),
(2853, 24, 62, '«إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه» أي الرسول «على أمر جامع» كخطبة الجمعة «لم يذهبوا» لعروض عذر لهم «حتى يستأذنوه إن الذين يستأذنونك أولئك الذين يؤمنون بالله ورسوله فإذا استأذنوك لبعض شأنهم» أمرهم «فأذن لمن شئت منهم» بالانصراف «واستغفر لهم الله إن الله غفور رحيم».'),
(2854, 24, 63, '«لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا» بأن تقولوا يا محمد، بل قولوا يا نبيَّ الله، يا رسول الله، في لين وتواضع وخفض صوت «قد يعلم الله الذين يتسللون منكم لواذا» أي يخرجون من المسجد في الخطبة من غير استئذان خفية مستترين بشيء، وقد للتحقيق «فليحذر الذين يخالفون عن أمره» أي الله ورسوله «أن تصيبهم فتنة» بلاء «أو يصيبهم عذاب أليم» في الآخرة.'),
(2855, 24, 64, '«ألا إنَّ لله ما في السماوات والأرض» ملكا وخلفا وعبيدا «قد يعلم ما أنتم» أيها المكلفون «عليه» من الإيمان والنفاق «و» يعلم «يوم يرجعون إليه» فيه التفات عن الخطاب إلى من يكون «فينبئهم» فيه «بما عملوا» من الخير والشر «والله لكل شيء» من أعمالهم وغيرها «عليم».');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, '«تبارك» تعالى «الذي نزَّل الفرقان» القرآن لأنه فرق بين الحق والباطل «على عبده» محمد «ليكون للعالمين» الإنس والجن دون الملائكة «نذيرا» مخوّفا من عذاب الله.'),
(2857, 25, 2, '«الذي له ملك السماوات والأرض، ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء» من شأنه أن يخلق «فقدره تقديرا» سواه تسوية.'),
(2858, 25, 3, '«واتخذوا» أي الكفار «من دونه» أي الله أي غيره «آلهة» هي الأصنام «لا يَخلقون شيئاً وهم يُخلقون ولا يملكون لأنفسهم ضرا» أي دفعه «ولا نفعا» أي جره «ولا يملكون موتا ولا حياةً» أي إماتة لأحد وإحياء لأحد «ولا نشورا» أي بعثا للأموات.'),
(2859, 25, 4, '«وقال الذين كفروا إن هذا» أي ما القرآن «إلا إفك» كذب «افتراه» محمد «وأعانه عليه قوم آخرون» وهم من أهل الكتاب، قال تعالى: «فقد جاءُوا ظلما وزورا» كفرا وكذبا: أي بهما.'),
(2860, 25, 5, '«وقالوا» أيضا هو «أساطير والأولين» أكاذيبهم: جمع أسطورة بالضم «اكتتبها» انتسخها من ذلك القوم بغيره «فهي تملى» تقرأ «عليه» ليحفظها «بكرة وأصيلاً» غدوة وعشيا قال تعالى ردا عليهم.'),
(2861, 25, 6, '«قل أنزله الذي يعلم السرَّ» الغيب «في السماوات والأرض إنه كان غفورا» للمؤمنين «رحيما» بهم.'),
(2862, 25, 7, '«وقالوا مال هذا الرسول يأكل الطعام ويمشي في الأسواق لولا» هلا «أنزل إليه ملك أو جاء معه نذيرا» يصدقه.'),
(2863, 25, 8, '«أو يُلقى إليه كنز» من السماء ينفقه، ولا يحتاج إلى المشي في الأسواق لطلب المعاش «أو تكون له جنة» بستان «يأكل منها» أي من ثمارها فيكتفي بها وفي قراءة نأكل بالنون: أي نحن فيكون له مزية علينا بها «وقال الظالمون» أي الكافرون للمؤمنين «إن» ما «تتبعون إلا رجلا مسحورا» مخدوعا مغلوبا على عقله، قال تعالى.'),
(2864, 25, 9, '«أنظر كيف ضربوا لك الأمثال» بالمسحور، والمحتاج إلى ما ينفقه وإلى ملك يقوم معه بالأمر «فضلوا» بذلك عن الهدى «فلا يستطيعون سبيلا» طريقا إليه.'),
(2865, 25, 10, '«تبارك» تكاثر خير «الذي إن شاء جعل لك خيرا من ذلك» الذي قالوه من الكنز والبستان «جناتٍ تجري من تحتها الأنهار» أي في الدنيا لأنه شاء أن يعطيه إياها في الآخرة «ويجعلْ» بالجزم «لك قصورا» أيضا، وفي قراءة بالرفع استئنافا.'),
(2866, 25, 11, '«بل كذَّبوا بالساعة» القيامة «وأعتدنا لمن كذَّب بالساعة سعيرا» نارا مسعرة: أي مشتدة.'),
(2867, 25, 12, '«إذا رأتهم من مكان بعيد سمعوا لها تغيظا» غليانا كالغضبان إذا غلى صدره من الغضب «وزفيرا» صوتا شديدا، أو سماع التغيظ رؤيته وعلمه.'),
(2868, 25, 13, '«وإذا أُلقوا منها مكانا ضيقا» بالتشديد والتخفيف بأن يضيق عليهم ومنها حال من مكانا لأنه في الأصل صفة له «مقُرَّنين» مصفدين قد قرنت: أي جمعت أيديهم إلى أعناقهم في الأغلال والتشديدُ للتكثير «دعوا هنالك ثبورا» هلاكا فيقال لهم.'),
(2869, 25, 14, '«لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا» كعذابكم.'),
(2870, 25, 15, '«قل أذلك» المذكور من الوعيد وصفة النار «خير أم جنة الخلد التي وعد» ها «المتقون كانت لهم» في علمه تعالى «جزاءً» ثوابا «ومصيرا» مرجعا.'),
(2871, 25, 16, '«لهم فيها ما يشاءُون خالدين» حال لازمة «كان» وعدهم ما ذكر «على ربك وعدا مسؤولا» يسأله من وعد به (ربنا وآتنا ما وعدتنا على رسلك) أو تسأله لهم الملائكة (ربنا وأدخلهم جنات عدن التي وعدتهم).'),
(2872, 25, 17, '«ويوم نحشرهم» بالنون والتحتانية «وما يعبدون من دون الله» أي غيره من الملائكة وعيسى وعزير والجن «فيقول» تعالى بالتحتانية والنون للمعبودين إثباتا للحجة على العابدين: «أأنتم» بتحقيق الهمزتين وإبدال الثانية ألفا وتسهيلها وإدخال ألف بين الُسهَلة والأخرى وتركه «أضْللْتم عبادي هؤلاء» أوقعتموهم في الضلال بأمركم إياهم بعبادتكم «أم هم ضلوا السبيل» طريق الحق بأنفسهم.'),
(2873, 25, 18, '«قالوا سبحانك» تنزيها لك عما لا يليق بك «ما كان ينبغي» يستقيم «لنا أن نتخذ من دونك» أي غيرك «من أولياء» مفعول أول ومن زائدة لتأكيد النفي وما قبله الثاني فكيف نأمر بعبادتنا؟ «ولكن متعتهم وآباءهم» من قبلهم بإطالة العمر وسعة الرزق «حتى نسوا الذكر» تركوا الموعظة والإيمان بالقرآن «وكانوا قوما بورا» هلكى، قال تعالى.'),
(2874, 25, 19, '«فقد كذبوكم» أي كذب المعبودون العابدين «بما تقولون» بالفوقانية أنهم آلهة «فما يستطيعون» بالتحتانية والفوقانية: أي لا هم ولا أنتم «صرفا» دفعا للعذاب عنكم «ولا نصرا» منعا لكم منه «ومن يظلم» يشرك «منكم نُذقْه عذابا كبيرا» شديدا في الآخرة.'),
(2875, 25, 20, '«وما أرسلنا قبلك من المرسلين إلا إنهم ليأكلون الطعام ويمشون في الأسواق» فأنت مثلهم في ذلك، وقد قيل لهم مثل ما قيل لك «وجلنا بعضكم لبعض فتنة» بلية ابتلى الغني بالفقير والصحيح بالمريض، والشريف بالوضيع يقول الثاني في كلّ: ما لي لا أكون كالأول في كلّ: «أتصبرون» على ما تسمعون ممن ابتليتم بهم استفهام بمعنى الأمر: أي اصبروا «وكان ربك بصيرا» بمن يصبر وبمن يجزع.'),
(2876, 25, 21, '«وقال الذين لا يرجون لقاءنا» لا يخافون البعث «لولا» هلا «أنزل علينا الملائكة» فكانوا رسلا إلينا «أو نرى ربنا» فنخبر بأن محمدا رسوله قال تعالى: «لقد استكبروا» تكبروا «في» شأن «أنفسهم وعتوْا» طغوا «عُتُوَّا كبيرا» بطلبهم رؤية الله تعالى في الدنيا، وعتوا بالواو على أصله بخلاف عتى بالإبدال في مريم.'),
(2877, 25, 22, '«يوم يرون الملائكة» في جملة الخلائق هو يوم القيامة ونصبه باذكر مقدرا «لا بشرى يومئذٍ للمجرمين» أي الكافرين بخلاف المؤمنين فلهم البشرى بالجنة «ويقولون حِجرا محجورا» على عادتهم في الدنيا إذا نزلت بهم شدة: أي عوذا معاذا يستعيذون من الملائكة، قال تعالى.'),
(2878, 25, 23, '«وقدمنا» عمدنا «إلى ما عملوا من عمل» من الخير كصدقة وصلة رحم، وقرى ضيف وإغاثة ملهوف في الدنيا «فجعلناه هباءً منشورا» هو ما يرى في الكوى التي عليها الشمس كالغبار المفرق: أي مثله في عدم النفع به إذ لا ثواب فيه لعدم شرطه ويجازون عليه في الدنيا.'),
(2879, 25, 24, '«أصحاب الجنة يومئذ» يوم القيامة «خيرٌ مستقرا» من الكافرين في الدنيا «وأحسن مقيلا» منهم: أي موضع قائلة فيها، وهي الاستراحة نصف النهار في الحر، وأخذ من ذلك انقضاء الحساب في نصف نهار كما ورد في حديث.'),
(2880, 25, 25, '«ويوم تشقق السماء» أي كل سماء «بالغمام» أي معه وهو غيم أبيض «ونزّل الملائكة» من كل سماء «تنزيلا» هو يوم القيامة ونصبه باذكر مقدرا وفي قراءة بتشديد شين تشقق بإدغام التاء الثانية في الأصل فيها، وفي أخرى: ننزل بنونين الثانية ساكنة وضم اللام ونصب الملائكة.'),
(2881, 25, 26, '«الملك يومئذ الحق للرحمن» لا يشركه فيه أحد «وكان» اليوم «يوما على الكافرين عسيرا» بخلاف المؤمنين.'),
(2882, 25, 27, '«ويوم يعضُّ الظالم» المشرك: عقبة بن أبي معيط كان نطق بالشهادتين ثم رجع إرضاءً لأبيّ بن خلف «على يديه» ندما وتحسرا في يوم القيامة «يقول يا» للتنبيه «ليتني اتخذت مع الرسول» محمد «سبيلا» طريقا إلى الهدى.'),
(2883, 25, 28, '«يا وَيْلَتَى» ألفه عوض عن ياء الإضافة أي ويلتي، ومعناه هلكتي «ليتني لم أتخذ فلانا» أي أبيَّا «خليلا».'),
(2884, 25, 29, '«لقد أضلني عن الذكر» أي القرآن «بعد إذ جاءني» بأن ردَّني عن الإيمان به، قال تعالى: «وكان الشيطان للإنسان» الكافر «خذولا» بأن يتركه ويتبرأ منه عند البلاء.'),
(2885, 25, 30, '«وقال الرسول» محمد «يا رب إن قومي» قريشا «اتخذوا هذا القرآن مهجورا» متروكا قال تعالى.'),
(2886, 25, 31, '«وكذلك» كما جعلنا لك عدوّا من مشركي قومك «جعلنا لكل نبي» قبلك «عدوا من المجرمين» المشركين فاصبر كما صبروا «وكفى بربك هاديا» لك «ونصيرا» ناصرا لك على أعدائك.'),
(2887, 25, 32, '«وقال الذين كفروا لولا» هلا «نزل عليه القرآن جملة واحدة» كالتوراة والإنجيل والزَّبور، قال تعالى: نزلناه، «كذلك» متفرّقا «لنثبَّت به فؤادك» نقوي قلبك «ورتلناه ترتيلا» أي أتينا به شيئا بعد شيء بتمهل وتؤدة لتيسر فهمه وحفظه.'),
(2888, 25, 33, '«ولا يأتونك بمثل» في إبطال أمرك «إلا جئناك بالحق» الدافع له «وأحسن تفسيرا» بيانا.'),
(2889, 25, 34, 'هم «الذين يُحشرون على وجوههم» أي يساقون «إلى جهنم أولئك شرٌ مكانا» هو جهنم «وأضَلُّ سبيلا» أخطأ طريقا من غيرهم وهو كفرهم.'),
(2890, 25, 35, '«ولقد آتينا موسى الكتاب» التوراة «وجعلنا معه أخاه هارون وزيرا» معينا.'),
(2891, 25, 36, '«فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا» أي القبط فرعون وقومه فذهبا إليهم بالرسالة فكذبوهما «فدمرناهم تدميرا» أهلكناهم إهلاكا.'),
(2892, 25, 37, '«و» اذكر «قوم نوح لما كذبوا الرسل» بتكذيبهم نوحا لطول لبثه فيهم فكأنه رسل، أو لأن تكذيبه تكذيب لباقي الرسل لاشتراكهم في المجيء بالتوحيد «أغرقناهم» جواب لما «وجعلناهم للناس» بعدهم «آية» عبرة «وأعتدنا» في الآخرة «للظالمين» الكافرين «عذابا أليما» مؤلما سوى ما يحل بهم في الدنيا.'),
(2893, 25, 38, '«و» اذكر «عادا» يوم هود «وثمود» قوم صالح «وأصحاب الرَّسَّ» اسم بئر، ونبيهم قيل شعيب وقيل غيره كانوا قعودا حولها فانهارت بهم وبمنازلهم «وقرونا» أقواما «بين ذلك كثيرا» أي بين عاد وأصحاب الرَّسَّ.'),
(2894, 25, 39, '«وكلا ضربنا له الأمثال» في إقامة الحجة عليهم فلم نهلكهم إلا بعد الإنذار «وكلاٌ تبَّرنا تتبيرا» أهلكنا إهلاكا بتكذيبهم أنبياءهم.'),
(2895, 25, 40, '«ولقد أتوا» أي مرَّ كفار مكة «على القرية التي أمطرت مطر السوء» مصدر ساء أي بالحجارة وهي عظمى قرى قوم لوط فأهلك الله أهلها لفعلهم الفاحشة «أفلم يكونوا يرونَها» في سفرهم إلى الشام فيعتبرون، والاستفهام للتقرير «بل كانوا لا يرجون» يخافون «نشورا» بعثا فلا يؤمنون.'),
(2896, 25, 41, '«وإذا رأوْك إن» ما «يتخذونك إلا هزؤا» مهزوءا به يقولن «أهذا الذي بعث الله رسولا» في دعواه محتقرين له عن الرسالة.'),
(2897, 25, 42, '«إن» مخففة من الثقيلة واسمها محذوف أي إنه «كاد ليضلنا» يصرفنا «عن آلهتنا لولا أن صبرنا عليها» لصرفنا عنها، قال تعالى: «وسوف يعلمون حين يرون العذاب» عينانا في الآخرة «من أضلُّ سبيلا» أخطأ طريقا، أهم أم المؤمنون.'),
(2898, 25, 43, '«أرأيت» أخبرني «من اتخذ إلههُ هواه» أي مُهويه قدَّم المفعول الثاني لأنه أهمّ وجملة من اتخذ مفعول أول لرأيت والثاني «أفأنت تكون عليه وكيلا» حافظا تحفظه عن ابتاع هواه لا.'),
(2899, 25, 44, '«أم تحسب أن أكثرهم يسمعون» سماع تفهم «أو يعقلون» ما تقول لهم «إن» ما «هم إلا كالأنعام بل هم أضل سبيلا» أخطأ طريقا منها لأنها تنقاد لمن يتعهدها، وهم لا يطيعون مولاهم المنعم عليهم.'),
(2900, 25, 45, '«ألم تَرَ» تنظر «إلى» فعل «ربّك كيف مدَّ الظل» من وقت الإسفار إلى وقت طلوع الشمس «ولو شاء» ربك «لجعله ساكنا» مقيما لا يزول بطلوع الشمس «ثم جعلنا الشمس عليه» أي الظل «دليلا» فلولا الشمس ما عرف الظل.'),
(2901, 25, 46, '«ثم قبضناه» أي الظل الممدود «إلينا قبضا يسيرا» خفيفا بطلوع الشمس.'),
(2902, 25, 47, '«وهو الذي جعل لكم الليل لباسا» ساترا كاللباس «والنوم سُباتا» راحة للأبدان بقطع الأعمال «وجعل النهار نشورا» منشورا فيه لابتغاء الرزق وغيره.'),
(2903, 25, 48, '«وهو الذي أرسل الرياح» وفي قراءة الريح «نُشرا بين يديْ رحمته» متفرقة قدام المطر، وفي قراءة بسكون الشين تخفيفا، وفي أخرى بسكونها ونون مفتوحة مصدر، وفي أخرى بسكونها وضم الموحدة بدل النون: أي مبشرات ومفرد الأولى نشور كرسول والأخيرة بشير «وأنزلنا من السماء ماءً طهورا» مطهرا.'),
(2904, 25, 49, '«لنحيي به بلدة ميتا» بالتخفيف يستوي فيه المذكور والمؤنث ذكّره باعتبار المكان «ونسقيه» أي الماء «مما خلقنا أنعاما» إبلا وبقرا وغنما «وأناسيَّ كثيرا» جمع إنسان وأصله أناسين فأبدلت النون ياء وأدغمت فيها الياء أو جمع انسي.'),
(2905, 25, 50, '«ولقد صرفناه» أي الماء «بينهم ليذَّكروا» أصله يتذكروا أدغمت التاء في الذال وفي قراءة ليذْكُروا بسكون الذال وضم الكاف أي نعمة الله به «فأبى أكثر الناس إلا كفورا» جحودا للنعمة حيث قالوا: مطرنا بنوء كذا.'),
(2906, 25, 51, '«ولو شئنا لبعثنا في كل قرية نذيرا» يخوِّف أهلها ولكن بعثناك إلى أهل القرى كلها نذيرا ليعظم أجرك.'),
(2907, 25, 52, '«فلا تُطع الكافرين» في هواهم «وجاهدهم به» أي القرآن «جهادا كبيرا».'),
(2908, 25, 53, '«وهو الذي مرج البحرين» أرسلهما متجاورين «هذا عذبٌ فرات» شديد العذوبة «وهذا مِلْحٌ أُجَاجٌ» شديد الملوحةِ «وجعل بينهما برزخا» حاجزا لا يختلط أحدهما بالآخر «وحجرا محجورا» سترا ممنوعا به اختلاطهما.'),
(2909, 25, 54, '«وهو الذي خلق من الماء بشرا» من المني إنسانا «فجعله نسبا» ذا نسب «وصهرا» ذا صهر بأن يتزوج ذكرا كان أو أنثي طلبا للتناسل «وكان ربُّك قديرا» قادرا على ما يشاء.'),
(2910, 25, 55, '«ويعبدون» أي الكفار «من دون الله ما لا ينفعهم» بعبادته «ولا يضرهم» بتركها وهو الأصنام «وكان الكافر على ربه ظهيرا» معينا للشيطان بطاعته.'),
(2911, 25, 56, '«وما أرسلناك إلا مبشرا» بالجنة «ونذيرا» مخوفا من النار.'),
(2912, 25, 57, '«قل ما أسألكم عليه» أي على تبليغ ما أرسلت به «من أجر إلا» لكن «من شاء أن يتخذ إلى ربه سبيلا» طريقا بإنفاق ماله في مرضاته تعالى فلا أمنعه من ذلك.'),
(2913, 25, 58, '«وتوكل على الحي الذي لا يموت وسَبِّح» متلبسا «بحمده» أي قل: سبحان الله والحمد لله «وكفى به بذنوب عباده خبيرا» عالما تعلق به بذنوب.'),
(2914, 25, 59, '«وهو الذي خلق السماوات والأرض وما بينهما في ستة أيام» من أيام الدنيا: أي في قدرها لأنه لم يكن ثم شمس ولو شاء لخلقهنّ في لمحة والعدول عنه لتعليم خلقه التثبيت «ثم استوى على العرش» هو في اللغة سرير الملك «الرحمن» بدل من ضمير استوى: أي استواءً يليق به «فاسأل» أيها الإنسان «به» بالرحمن «خبيرا» يخبرك بصفاته.'),
(2915, 25, 60, '«وإذا قيل لهم» لكفار مكة «اسجدوا للرحمن قالوا وما الرحمن أنسجد لما تأمرنا» بالفوقانية والتحتانية والآمر محمد ولا نعرفه صلى الله علية وسلم لا «وزادهم» هذا القول لهم «نفورا» عن الإيمان قال تعالى.'),
(2916, 25, 61, '«تبارك» تعاظم «الذي جعل في السماء بروجا» اثني عشر: الحمل والثور والجوزاء والسرطان والأسد، والسنبلة والميزان والعقرب والقوس والجدي والدلو والحوت، وهي منازل الكواكب السبعة السيارة المريخ وله الحمل والعقرب، والزهرة ولها الثور والميزان، وعطارد وله الجوزاء والسنبلة والقمر وله السرطان والشمس ولها الأسد، والمشتري وله القوس والحوت، وزحل وله الجدي والدلو «وجَعَلَ» «فيها» أيضا «سراجا» هو الشمس «وقمرا منيرا» وفي قراءة سُرُجا بالجمع: أي نيرات، وخصّ القمر منها بالذكر لنوع فضيلة.'),
(2917, 25, 62, '«وهو الذي جعل الليل والنهار خلفة» أي يخلف كل منهما الآخر «لمن أراد أن يذَّكَّر» بالتشديد والتخفيف كما تقدم: ما فاته في أحدهما من خير فيفعله في الآخر «أو أراد شُكور» أي شكرا لنعمة ربه عليه فيهما.'),
(2918, 25, 63, '«وعباد الرحمن» مبتدأ وما بعده صفات له إلى أولئك يجزون غير المعترض فيه «الذي يمشون على الأرض هوْنا» أي بسكينة وتواضع «وإذا خاطبهم الجاهلون» بما يكرهونه «قالوا سلاما» أي قولا يَسلمون فيه من الإثم.'),
(2919, 25, 64, '«والذين يبيتون لربهم سُجَّدا» جمع ساجد «وقياما» بمعنى قائمين يصلون بالليل.'),
(2920, 25, 65, '«والذين يقولون ربنا اصرف عنا عذاب جهنم إنَّ عذابها كان غراما» أي لازما.'),
(2921, 25, 66, '«إنها ساءت» بئست «مُستقرا ومُقاما» هي: أي موضع استقرار وإقامة.'),
(2922, 25, 67, '«والذين إذا أنفقوا» على عيالهم «لم يسرفوا ولم يقتروا» بفتح أوله وضمه: أي يضيقوا «وكان» إنفاقهم «بين ذلك» الإسراف والإقتار «قواما» وسطا.'),
(2923, 25, 68, '«والذين لا يدعون مع الله إلها آخر ولا يقتلون النفس التي حرم الله» قتلها «إلا بالحق ولا يزنون ومن يفعل ذلك» أي واحدا من الثلاثة «يلق أثاما» أي عقوبة.'),
(2924, 25, 69, '«يُضاعَف» وفي قراءة يضعَف بالتشديد «له العذاب يوم القيامة ويخلد فيه» بجزم الفعلين بدلا وبرفعها أستئنافا «مهانا» حال.'),
(2925, 25, 70, '«إلا من تاب وآمن وعمل عملا صالحا» منهم «فأولئك يبدِّل الله سيئاتهم» المذكورة «حسنات» في الآخرة «وكان الله غفورا رحيما» أي لم يزل متصفا بذلك.'),
(2926, 25, 71, '«ومن تاب» من ذنوبه غير من ذكر «وعمل صالحا فإنه يتوب إلى الله متابا» أي يرجع إليه رجوعا فيجازيه خيرا.'),
(2927, 25, 72, '«والذين لا يشهدون الزور» أي الكذب والباطل «وإذا مرّوا باللغو» من الكلام القبيح وغيره «مروا كراما» معرضين عنه.'),
(2928, 25, 73, '«والذين إذا ذُكِّروا» وعظوا «بآيات ربِّهم» أي القرآن «لم يخرّوا» يسقطوا «عليها صما وعمياناً» بل خروا سامعين ناظرين منتفعين.'),
(2929, 25, 74, '«والذين يقولون ربنا هب لنا من أزواجنا وذرياتنا» بالجمع والإفراد «قرة أعين» لنا بأن نراهم مطيعين لك «واجعلنا للمتقين إماما» في الخير.'),
(2930, 25, 75, '«أولئك يجزوْن الغرفة» الدرجة العليا في الجنة «بما صبروا» على طاعة الله «ويُلقَّون» بالتشديد والتخفيف مع فتح الياء «فيها» في الغرفة «تحية وسلاما» من الملائكة.'),
(2931, 25, 76, '«خالدين فهيا حسُنت مستقرا ومقاما» موضع إقامة لهم وأولئك وما بعده خبر عباد الرحمن المبتدأ.'),
(2932, 25, 77, '«قل» يا محمد لأهل مكة «ما» نافية «يعبأ» يكترث «بكم ربي لولا دعاؤكم» إياه في الشدائد فيكشفها «فقد» أي فكيف يعبأ بكم وقد «كذبتم» الرسول والقرآن «فسوف يكون» العذاب «لزاما» ملازما لكم في الآخرة بعد ما يحلّ بكم في الدنيا، فقتل منهم يوم بدر سبعون وجواب لولا دلَّ عليه ما قبلها.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, '«طسَمَ» الله أعلم بمراده بذلك.'),
(2934, 26, 2, '«تلك» أي هذه الآيات «آيات الكتاب» القرآن والإضافة بمعنى من «المبين» المظهر الحق من الباطل.'),
(2935, 26, 3, '«لعلك» يا محمد «باخعٌ نفسك» قاتلها غما من أجل «ألا يكونوا» أي أهل مكة «مؤمنين» ولعل هنا للإشفاق أي أشفق عليها بتخفيف هذا الغم.'),
(2936, 26, 4, '«إن نشأ ننزل عليهم من السماء آية فظلّت» بمعنى المضارع: أي تظل، أي تدوم «أعناقهم لها خاضعين» فيؤمنون، ولما وصفت الأعناق بالخضوع الذي هو لأربابها جمعت الصفة جمع العقلاء.'),
(2937, 26, 5, '«وما يأتيهم من ذكر» قرآن «من الرحمن مُحدّث» صفة كاشفة «إلا كانوا عنه معرضين».'),
(2938, 26, 6, '«فقد كذبوا» به «فسيأتيهم أنباء» عواقب «ما كانوا به يستهزءُون».'),
(2939, 26, 7, '«أوَلم يروا» ينظروا «إلى الأرض كم أنبتنا فيها» أي كثيرا «من كل زوجِ كريم» نوع حسن.'),
(2940, 26, 8, '«إن في ذلك لآية» دلالة على كمال قدرته تعالى «وما كان أكثرهم مؤمنين» في علم الله، وكان قال سيبويه: زائدة.'),
(2941, 26, 9, '«وإن ربك لهو العزيز» ذو العزة ينتقم من الكافرين «الرحيم» يرحم المؤمنين.'),
(2942, 26, 10, '«و» اذكر يا محمد لقومك «إذ نادى ربك موسى» ليلة رأى النار والشجرة «أن» أي: بأن «ائت القوم الظالمين» رسولا.'),
(2943, 26, 11, '«قوم فرعون» معه ظلموا أنفسهم بالكفر بالله وبني إسرائيل باستعبادهم «ألا» الهمزة للاستفهام الإنكارى «يتقون» الله بطاعته فيوحدونه.'),
(2944, 26, 12, '«قال» موسى «ربِ إني أخاف أن يكذّبون».'),
(2945, 26, 13, '«ويضيق صدري» من تكذيبهم لي «ولا ينطلق لساني» بأداء الرسالة للعقدة التي فيه «فأرسل إلى» أخي «هارون» معي.'),
(2946, 26, 14, '«ولهم عليَّ ذنبٌ» بقتل القبطي منهم «فأخاف أن يقتلون» به.'),
(2947, 26, 15, '«قال» تعالى: «كلا» لا يقتلونك «فاذهبا» أي أنت وأخوك، ففيه تغليب الحاضر على الغائب «بآياتنا إنا معكم مستمعون» ما تقولون وما يقال لكم، أجريا مجرى الجماعة.'),
(2948, 26, 16, '«فأتِيَا فرعون فقولا إنا» كلاً منا «رسول رب العالمين» إليك.'),
(2949, 26, 17, '«أن» أي: بأن «أرسل معنا» إلى الشام «بني إسرائيل» فأتياه فقالا له ما ذكر.'),
(2950, 26, 18, '«قال» فرعون لموسى «ألم نربِّك فينا» في منازلنا «وليدا» صغيرا قريبا من الولادة بعد فطامه «ولبثت فينا من عمرك سنين» ثلاثين سنة يلبس من ملابس فرعون ويركب من مراكبه وكان يسمى ابنه.'),
(2951, 26, 19, '«وفعلت فعلتك التي فعلت» هي قتله القبطي «وأنت من الكافرين» الجاحدين لنعمتي عليك بالتربية وعدم الاستعباد.'),
(2952, 26, 20, '«قال» موسى «فعلتها إذاً» أي حينئذ «وأنا من الضالين» عما آتاني الله بعدها من العلم والرسالة.'),
(2953, 26, 21, '«ففررتُ منكم لما خفتكم فوهب لي ربي حكما» علما «وجعلني من المرسلين».'),
(2954, 26, 22, '«وتلك نعمةٌ تمنُّها عليَّ» أصله تمن بها عليَّ «أن عبدت بني إسرائيل» بيان لتلك: أي اتخذتهم عبيدا ولم تستعبدني لا نعمة لك بذلك لظلمك باستعبادهم وقدر بعضهم أول الكلام همزة استفهام للإنكار.'),
(2955, 26, 23, '«قال فرعون» لموسى «وما رب العالمين» الذي قلت إنك رسوله أي: أي شيء هو ولما لم يكن سبيل للخلق إلى معرفة حقيقته تعالى وإنما يعرفونه بصفاته أجابه موسى عليه الصلاة والسلام ببعضها.'),
(2956, 26, 24, '«قال ربُّ السماوات والأرض وما بينهما» أي خالق ذلك «إن كنتم موقنين» بأنه تعالى خالقه فآمنوا به وحده.'),
(2957, 26, 25, '«قال» فرعون «لمن حوله» من أشراف قومه «ألا تستمعون» جوابه الذي لم يطابق السؤال.'),
(2958, 26, 26, '«قال» موسى «ربكم ورب آبائكم الأولين» وهذا وإن كان داخلا فيما قبله يغيظ فرعون ولذلك.'),
(2959, 26, 27, '«قال إنَّ رسولكم الذي أرسل إليكم لمجنون».'),
(2960, 26, 28, '«قال» موسى «ربُّ المشرق والمغرب وما بينهما إن كنتم تعقلون» إنه كذلك فآمنوا به وحده.'),
(2961, 26, 29, '«قال» فرعون لموسى «لئن اتخذت إلها غيري لأجعلنَّك من المسجونين» كان سجنه شديدا يحبس الشخص في مكان تحت الأرض وحده لا يبصر ولا يسمع فيه أحدا.'),
(2962, 26, 30, '«قال» له موسى «أوَلوْ» أي: أتفعل ذلك ولو «جئتك بشيءٍ مبينِ» برهان بيِّن على رسالتي.'),
(2963, 26, 31, '«قال» فرعون له «فأتِ به إن كنت من الصادقين» فيه.'),
(2964, 26, 32, '«فألقى عصاه فإذا هي ثعبانٌ مبينٌ» حية عظيمة.'),
(2965, 26, 33, '«ونزع يده» أخرجها من جيبه «فإذا هي بيضاء» ذات شعاع «للناظرين» خلاف ما كانت عليه من الأدمة.'),
(2966, 26, 34, '«قال» فرعون «للملإ حوله إنَّ هذا لساحر عليم» فائق في علم السحر.'),
(2967, 26, 35, '«يريد أن يخرجكم من أرضكم بسحره فماذا تأمرون».'),
(2968, 26, 36, '«قالوا أرجه وأخاه» أخّر أمرهما «وابعث في المدائن حاشرين» جامعين.'),
(2969, 26, 37, '«يأتوك بكل سَحَّار عليم» يفضل موسى في علم السحر.'),
(2970, 26, 38, '«فجمع السحرة لميقات يوم معلوم» وهو وقت الضحى من يوم الزينة.'),
(2971, 26, 39, '«وقيل للناس هل أنتم مجتمعون».'),
(2972, 26, 40, '«لعلنا نتبع السحرة إن كانوا هم الغالبين» الاستفهام للحث على الاجتماع والترجي على تقدير غلبتهم ليستمروا على دينهم فلا يتبعوا موسى.'),
(2973, 26, 41, '«فلما جاء السحرة قالوا لفرعون أئن» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجيهين «لنا لأجرا إن كنا نحن الغالبين».'),
(2974, 26, 42, '«قال نعم وإنكم إذاً» أي حينئذ «لمن المقربين».'),
(2975, 26, 43, '«قال لهم موسى» بعد ما قالوا له (إما أن تُلقي وإما أن نكون نحن الملقين) «ألقوا ما أنتم ملقون» فالأمر فيه للإذن بتقديم إلقائهم توسلا به إلى إظهار الحق.'),
(2976, 26, 44, '«فألقوْا حبالهم وعصيهم وقالوا بعزة فرعون إنا لنحن الغالبون».'),
(2977, 26, 45, '«فألقى موسى عصاه فإذا هي تلقف» بحذف إحدى التاءين من الأصل تبتلع «ما يأفكون» يقلبونه بتمويههم فيخيلون حبالهم وعصيهم أنها حيات تسعى.'),
(2978, 26, 46, '«فألقيَ السحرة ساجدين».'),
(2979, 26, 47, '«قالوا آمنا برب العالمين».'),
(2980, 26, 48, '«رب موسى وهارون» لعلمهم بأن ما شاهدوه من العصا لا يتأتى بالسحر.'),
(2981, 26, 49, '«قال» فرعون «أآمنتم» بتحقيق الهمزتين وإبدال الثانية ألفا «له» لموسى «قبل أن آذن» أنا «لكم إنه لكبيركم الذي علمكم السحر» فعلَّمك شيئا منه وغلبكم بآخر «فلسوف تعلمون» ما ينالكم مني «لأقطعنَّ أيديكم وأرجلكم من خلاف» أي يد كل واحد اليمنى ورجله اليسرى «ولأصلبنكم أجمعين».'),
(2982, 26, 50, '«قالوا لا ضَيْرَ» لا ضرر علينا في ذلك «إنا إلى ربنا» بعد موتنا بأي وجه كان «منقلبون» راجعون في الآخرة.'),
(2983, 26, 51, '«إنا نطمع» نرجوا «أن يغفر لنا ربنا خطايانا أن» أي بأن «كنَّا أول المؤمنين» في زماننا.'),
(2984, 26, 52, '«وأوحينا إلى موسى» بعد سنين أقامها بينهم يدعوهم بآيات الله إلى الحق فلم يزيدوا إلا عتوا «أن أسر بعبادي» بني إسرائيل وفي قراءة بكسر النون ووصل همزة أسر من سرى لغة في أسرى أي سر بهم ليلا إلى البحر «إنكم مُتَّبعون» يتبعكم فرعون وجنوده فيلجون وراءكم البحر فأنجيكم وأغرقهم.'),
(2985, 26, 53, '«فأرسل فرعون» حين أخبر بسيرهم «في المدائن» قيل كان له ألف مدينة واثنا عشر ألف قرية «حاشرين» جامعين الجيش قائلا.'),
(2986, 26, 54, '«إن هؤلاء لشرذمةٌ» طائفة «قليلون» قيل كانوا ستمائة ألف وسبعين ومقدمة جيشه سبعمائة ألف فقللهم بالنظر إلى كثرة جيشه.'),
(2987, 26, 55, '«وإنهم لنا لغائظون» فاعلون ما يغيظنا.'),
(2988, 26, 56, '«وإنا لجميعٌ حذرون» مستعدون وفي قراءة حاذرون متيقظون.'),
(2989, 26, 57, 'قال تعالى: «فأخرجناهم» أي فرعون وقومه من مصر ليلحقوا موسى وقومه «من جنات» بساتين كانت على جانبي النيل «وعيون» أنهار جارية في الدور من النيل.'),
(2990, 26, 58, '«وكنوز» أموال ظاهرة من الذهب والفضة، وسميت كنوزا لأنه لم يعط حق الله تعالى منها «ومقامٍ كريمٍ» مجلس حسن للأمراء والوزراء يحفه أتباعهم.'),
(2991, 26, 59, '«كذلك» أي إخراجنا كما وصفنا «وأورثناها بني إسرائيل» بعد إغراق فرعون وقومه.'),
(2992, 26, 60, '«فأتبعوهم» لحقوهم «مشرقين» وقت شروق الشمس.'),
(2993, 26, 61, '«فلما تراءى الجمعان» رأى كل منهما الآخر «قال أصحاب موسى إنا لمدركون» يدركنا جمع فرعون ولا طاقة لنا به.'),
(2994, 26, 62, '«قال» موسى «كلا» أي لن يدركونا «إن معي ربي» ينصره «سيهدين» طريق النجاة.'),
(2995, 26, 63, 'قال تعالى: «فأوحينا إلى موسى أن اضرب بعصاك البحر» فضربه «فانفلق» فانشق اثنى عشر فرقا «فكان كل فرق كالطود العظيم» الجبل الضخم بينهما مسالك سلكوها لم يبتل منها سرج الراكب ولا لبده.'),
(2996, 26, 64, '«وأزلفنا» قرَّبنا «ثمَّ» هناك «الآخرين» فرعون وقومه حتى سلكوا مسالكهم.'),
(2997, 26, 65, '«وأنجينا موسى ومن معه أجمعين» بإخراجهم من البحر على هيئته المذكورة.'),
(2998, 26, 66, '«ثم أغرقنا الآخرين» فرعون وقومه بإطباق البحر عليهم لما تم دخولهم في البحر وخروج بني إسرائيل منه.'),
(2999, 26, 67, '«إن في ذلك» إغراق فرعون وقومه «لآيةٌ» عبرة لمن بعدهم «وما كان أكثرهم مؤمنين» بالله لم يؤمن منهم غير آسية امرأة فرعون وحزقيل مؤمن آل فرعون ومريم بنت ناموصى التي دلت على عظام يوسف عليه السلام.'),
(3000, 26, 68, '«وإن ربك لهو العزيز» فانتقم من الكافرين بإغراقهم «الرحيم» بالمؤمنين فأنجاهم من الغرق.'),
(3001, 26, 69, '«واتل عليهم» أي كفار مكة «نبأ» خبر «إبراهيم» ويبدل منه.'),
(3002, 26, 70, '«إذا قال لأبيه وقومه ما تعبدون».'),
(3003, 26, 71, '«قالوا نعبد أصناما» صرحوا بالفعل ليعطفوا عليه «فنظل لها عاكفين» نقيم نهارا على عبادتها زادوه في الجواب افتخارا به.'),
(3004, 26, 72, '«قال هل يسمعونكم إذ» حين «تدعون».'),
(3005, 26, 73, '«أو ينفعونكم» إن عبدتموهم «أو يضرونـ» كم إن لم تعبدوهم.'),
(3006, 26, 74, '«قالوا بل وجدنا آباءنا كذلك يفعلون» أي مثل فعلنا.'),
(3007, 26, 75, '«قال أفرآيتم ما كنتم تعبدون».'),
(3008, 26, 76, '«أنتم وآبائكم الأقدمون».'),
(3009, 26, 77, '«فإنهم عدو لي» لا أعبدهم «إلا» لكن «ربَّ العالمين» فإني أعبده.'),
(3010, 26, 78, '«الذي خلقني فهو يهدين» إلى الدين.'),
(3011, 26, 79, '«والذي هو يطعمني ويسقين».'),
(3012, 26, 80, '«وإذا مرضت فهو يشفين».'),
(3013, 26, 81, '«والذي يُميتني ثم يحيين».'),
(3014, 26, 82, '«والذي أطمع» أرجو «أن يغفر لي خطيئتي يوم الدين» الجزاء.'),
(3015, 26, 83, '«رب هب لي حكما» علما «وألحقني بالصالحين» النبيين.'),
(3016, 26, 84, '«واجعل لي لسان صدق» ثناء حسنا «في الآخرين» الذين يأتون بعدي إلى يوم القيامة.'),
(3017, 26, 85, '«واجعلني من ورثة جنة النعيم» ممن يعطاها.'),
(3018, 26, 86, '«واغفر لأبي إنه كان من الضالين» بأن تتوب عليه فتغفر له وهذا قبل أن يتبين له أنه عدو لله كما ذكر في سورة براءة.'),
(3019, 26, 87, '«ولا تخزني» تفضحني «يوم يُبعثون» الناس.'),
(3020, 26, 88, 'قال تعالى فيه: «يوم لا ينفع مال ولا بنون» أحدا'),
(3021, 26, 89, '«إلا» لكن «من أتى الله بقلب سليم» من الشرك والنفاق وهو قلب المؤمن فإنه ينفعه ذلك.'),
(3022, 26, 90, '«وأُزلفت الجنة» قربت «للمتقين» فيرونها.'),
(3023, 26, 91, '«وبرزت الجحيم» أظهرت «للغاوين» الكافرين.'),
(3024, 26, 92, '«وقيل لهم أين ما كنتم تعبدون».'),
(3025, 26, 93, '«من دون الله» أي غيره من الأصنام «هل ينصرونكم» بدفع العذاب عنكم «أو ينتصرون» بدفعه عن أنفسهم، لا.'),
(3026, 26, 94, '«فكُبْكِبوا» ألقوا «فيها هم والغاوون».'),
(3027, 26, 95, '«وجنود إبليس» أتباعه، ومن أطاعه من الجن والإنس «أجمعون».'),
(3028, 26, 96, '«قالوا» أي الغاوون «وهم فيها يختصمون» مع معبوديهم.'),
(3029, 26, 97, '«تالله إن» مخففة من الثقلية واسمها محذوف أي إنه «كنا لفي ضلال مبين» بين.'),
(3030, 26, 98, '«إذ» حيث «نسويكم برب العالمين» في العبادة.'),
(3031, 26, 99, '«وما أضلنا» عن الهدى «إلا المجرمون» أي الشياطين أو أوّلونا الذين اقتدينا بهم.'),
(3032, 26, 100, '«فما لنا من شافعين» كما للمؤمنين من الملائكة والنبيين والمؤمنين.'),
(3033, 26, 101, '«ولا صديق حميم» يهمه أمرنا.'),
(3034, 26, 102, '«فلو أن لنا كرَّة» رجعة إلى الدنيا «فنكون من المؤمنين» لو هنا للتمني ونكون جوابه.'),
(3035, 26, 103, '«إن في ذلك» المذكور من قصة إبراهيم وقومه «لآية وما كان أكثرهم مؤمنين».'),
(3036, 26, 104, '«وإن ربك لهم العزيز الرحيم».'),
(3037, 26, 105, '«كذبت قوم نوح المرسلين» بتكذيبهم له لاشتراكهم في المجيء بالتوحيد، أو لأنه لطول لبثه فيهم كأنه رسل وتأنيث قوم باعتبار معناه وتذكيره باعتبار لفظه.'),
(3038, 26, 106, '«إذ قال لهم أخوهم» نسبا «نوح ألا تتقون» الله.'),
(3039, 26, 107, '«إني لكم رسول أمين» على تبليغ ما أرسلت به.'),
(3040, 26, 108, '«فاتقوا الله وأطيعون» فيما آمركم به من توحيد الله وطاعته.'),
(3041, 26, 109, '«وما أسألكم عليه» على تبليغه «من أجر إن» ما «أجريَ» أي ثوابي «إلا على رب العالمين».'),
(3042, 26, 110, '«فاتقوا الله وأطيعون» كرره تأكيدا.'),
(3043, 26, 111, '«قالوا أنؤمن» نصدق «لك» لقولك «واتبعك» وفي قراءة وأتباعك جمع تابع مبتدأ «الأرذلون» السفلة كالحاكة والأساكفة.'),
(3044, 26, 112, '«قال وما علمي» أيّ علم لي «بما كانوا يعلمون».'),
(3045, 26, 113, '«إن» ما «حسابهم إلا على ربي» فيجازيهم «لو تشعرون» تعلمون ذلك ما عبَّدتموهم.'),
(3046, 26, 114, '«وما أنا بطارد المؤمنين».'),
(3047, 26, 115, '«إن» ما «أنا إلا نذير مبين» بيَّن الإنذار.'),
(3048, 26, 116, '«قالوا لئن لم تنته يا نوح» عما تقول لنا «لتكونن من المرجومين» بالحجارة أو بالشتم.'),
(3049, 26, 117, '«قال» نوح «رب إن قومي كذبون».'),
(3050, 26, 118, '«فافتح بيني وبينهم فتحا» أي احكم «ونجني ومن معي من المؤمنين».'),
(3051, 26, 119, 'قال تعالى «فأنجيناه ومن معه في الفلك المشحون» المملوء من الناس والحيوان والطير.'),
(3052, 26, 120, '«ثم أغرقنا بعد» بعد إنجائهم «الباقين» من قومه.'),
(3053, 26, 121, '«إن ذلك لآية وما كان أكثرهم مؤمنين».'),
(3054, 26, 122, '«وإن ربك لهو العزيز الرحيم».'),
(3055, 26, 123, '«كذبت عادٌ المرسلين».'),
(3056, 26, 124, '«إذ قال لهم أخوهم هود ألا تتقون».'),
(3057, 26, 125, '«إني لكم رسول أمين».'),
(3058, 26, 126, '«فاتقوا الله وأطيعون».'),
(3059, 26, 127, '«وما أسألكم عليه من أجر إن» ما «أجري إلا على رب العالمين».'),
(3060, 26, 128, '«أتبنون بكل ريع» مكان مرتفع «آية» بنا علما للمارة «تعبثون» ممن يمر بكم وتسخرون منهم والجملة حال من ضمير تبنون.'),
(3061, 26, 129, '«وتتخذون مصانع» للماء تحت الأرض «لعلكم» كأنكم «تخلدون» فيها لا تموتون.'),
(3062, 26, 130, '«وإذا بطشتم» بضرب أو قتل «بطشتم جبارين» من غير رأفة.'),
(3063, 26, 131, '«فاتقوا الله» في ذلك «وأطيعون» فيما أمرتكم به.'),
(3064, 26, 132, '«واتقوا الذي أمدكم» أنعم عليكم «بما تعملون».'),
(3065, 26, 133, '«أمدكم بأنعام وبنين».'),
(3066, 26, 134, '«وجنات» بساتين «وعيون» أنهار.'),
(3067, 26, 135, '«إني أخاف عليكم عذاب يوم عظيم» في الدنيا والآخرة إن عصيتموني.'),
(3068, 26, 136, '«قالوا سواء علينا» مستو عندنا «أوعظت أم لم تكن من الواعظين» أصلا أي لا نرعوي لوعظك.'),
(3069, 26, 137, '«إن» ما «هذا» الذي خوفنا به «إلا خَلْق الأولين» اختلافهم وكذبهم وفي قراءة بضم الخاء واللام أي ما هذا الذي نحن عليه من إنكار للبعث إلا خلق الأولين أي طبيعتهم وعادتهم.'),
(3070, 26, 138, '«وما نحن بمعذبين».'),
(3071, 26, 139, '«فكذبوه» بالعذاب «فأهلكناهم» في الدنيا بالريح «إن في ذلك لآية وما كان أكثرهم مؤمنين».'),
(3072, 26, 140, '««إن ربك لهو العزيز الرحيم».'),
(3073, 26, 141, '«كذبت ثمود المرسلين».'),
(3074, 26, 142, '«إذ قال لهم أخوهم صالح ألا تتقون».'),
(3075, 26, 143, '«إني لكم رسول أمين».'),
(3076, 26, 144, '«فاتقوا الله وأطيعون».'),
(3077, 26, 145, '«وما أسألكم عليه من أجر إن» ما «أجري إلا على رب العالمين».'),
(3078, 26, 146, '«أتتركون في ما ههنا» من الخيرات «آمنين».'),
(3079, 26, 147, '«في جنات وعيون».'),
(3080, 26, 148, '«وزروع ونخل طلعها هضيم» لطيف لين.'),
(3081, 26, 149, '«وتنحتون من الجبال بيوتا فرهين» بطرين وفي قراءة فارهين حاذقين.'),
(3082, 26, 150, '«فاتقوا الله وأطيعون» فيما أمرتكم به.'),
(3083, 26, 151, '«ولا تطيعوا أمر المسرفين».'),
(3084, 26, 152, '«الذين يفسدون في الأرض» بالمعاصي «ولا يصلحون» بطاعة الله.'),
(3085, 26, 153, '«قالوا إنما أنت من المسحرين» الذين سحروا كثيرا حتى غلب على عقلهم.'),
(3086, 26, 154, '«ما أنت» أيضا «إلا بشر مثلنا فأت بآية إن كنتم من الصادقين» في رسالتك.'),
(3087, 26, 155, '«قال هذه ناقة لها شرب» نصيب من الماء «ولكم شرب يوم معلوم».'),
(3088, 26, 156, '«ولا تمسوها بسوء فيأخذكم عذاب يوم عظيم» بعظم العذاب.'),
(3089, 26, 157, '«فعقروها» عقرها بعضهم برضاهم «فأصبحوا نادمين» على عقرها.'),
(3090, 26, 158, '«فأخذهم العذاب» الموعود به فهلكوا «إن في ذلك لآية وما كان أكثرهم مؤمنين».'),
(3091, 26, 159, '«وإن ربك لهو العزيز الرحيم».'),
(3092, 26, 160, '«كذبت قوم لوط المرسلين».'),
(3093, 26, 161, '«إذ قال لهم أخوهم لوط ألا تتقون».'),
(3094, 26, 162, '«إني لكم رسول أمين».'),
(3095, 26, 163, '«فاتقوا الله وأطيعون».'),
(3096, 26, 164, '«وما أسألكم عليه من أجر إن» ما «أجري إلا على رب العالمين».'),
(3097, 26, 165, '«أتأتون الذكران من العالمين» الناس.'),
(3098, 26, 166, '«وتذرون ما خلق لكم ربكم من أزواجكم» أي أقبالهن «بل أنتم قوم عادون» متجاوزون الحلال إلى الحرام.'),
(3099, 26, 167, '«قالوا لئن لم تنته يا لوط» عن إنكارك علينا «لتكونن من المخرجين» من بلدتنا.'),
(3100, 26, 168, '«قال» لوط «إني لعملكم من القالين» المبغضين.'),
(3101, 26, 169, '«رب نجني وأهلي مما يعلمون» أي من عذابه.'),
(3102, 26, 170, '«فنجيناه وأهله أجمعين».'),
(3103, 26, 171, '«إلا عجوزا» امرأته «في الغابرين» الباقين أهلكناها.'),
(3104, 26, 172, '«ثم دمرنا الآخرين» أهلناهم.'),
(3105, 26, 173, '«وأمطرنا عليهم مطرا» حجارة من جملة الإهلاك «فساء مطر المنذرين» مطرهم.'),
(3106, 26, 174, '«إن في ذلك لآية وما كان أكثرهم مؤمنين».'),
(3107, 26, 175, '«وإن ربك لهو العزيز الرحيم».'),
(3108, 26, 176, '«كذب أصحاب الأيكة» وفي قراءة بحذف الهمزة وإلقاء حركتها على اللام وفتح الهاء: هي غيضة شجر قرب مدين «المرسلين».'),
(3109, 26, 177, '«إذ قال لهم شعيب» لم يقل أخوهم لأنه لم يكن منهم «ألا تتقون».'),
(3110, 26, 178, '«إني لكم رسول أمين».'),
(3111, 26, 179, '«فاتقوا الله وأطيعون».'),
(3112, 26, 180, '«وما أسألكم عليه من أجر إن» ما «أجري إلا على رب العالمين».'),
(3113, 26, 181, '«أوْفوا الكيل» أتموه «ولا تكونوا من المخسرين» الناقصين.'),
(3114, 26, 182, '«وزنوا بالقسطاس المستقيم» الميزان السوي.'),
(3115, 26, 183, '«ولا تبخسوا الناس أشياءهم» لا تنقصوهم من حقهم شيئا «ولا تعثوْا في الأرض مفسدين» بالقتل وغيره من عَثِيَ بكسر المثلثة أفسد ومفسدين حال مؤكدة لمعنى عاملها.'),
(3116, 26, 184, '«واتقوا الذي خلقكم والجبلة» الخليقة «الأولين».'),
(3117, 26, 185, '«قالوا إنما أنت من المسحرين».'),
(3118, 26, 186, '«وما أنت إلا بشر مثلنا وإن» مخففة من الثقيلة واسمها محذوف أي إنه «نظنك لمن الكاذبين».'),
(3119, 26, 187, '«فأسقط علينا كسْفا» بسكون السين وفتحها قطعا «من السماء إن كنت من الصادقين» في رسالتك.'),
(3120, 26, 188, '«قال ربي أعلم بما تعملون» فيجازيكم به.'),
(3121, 26, 189, '«فكذبوه فأخذهم عذاب يوم الظلة» هي سحابة أظلتهم بعد حر شديد أصابهم فأمطرت عليهم نارا فاحترقوا «إنه كان عذاب يوم عظيم».'),
(3122, 26, 190, '«إن في ذلك لآية وما كان أكثرهم مؤمنين».'),
(3123, 26, 191, '«وإن ربك لهو العزيز الرحيم».'),
(3124, 26, 192, '«وإنه» أي القرآن «لتنزيل رب العالمين».'),
(3125, 26, 193, '«نزل به الروح الأمين» جبريل.'),
(3126, 26, 194, '«على قلبك لتكون من المنذرين».'),
(3127, 26, 195, '«بلسان عربي مبين» بيَّن وفي قراءة بتشديد نزل ونصب الروح والفاعل الله.'),
(3128, 26, 196, '«وإنه» ذكر القرآن المنزل على محمد «لفي زُبُر» كتب «الأولين» كالتوراة والإنجيل.'),
(3129, 26, 197, '«أو لم يكن لهم» لكفار مكة «آية» على ذلك «أن يعلمه علماء بني إسرائيل» كعبد الله بن سلام وأصحابه من الذين آمنوا فإنهم يخبرون بذلك، ويكن بالتحتانية ونصب آية وبالفوقانية ورفع آية.'),
(3130, 26, 198, '«ولو نزلناه على بعض الأعجمين» جمع أعجم.'),
(3131, 26, 199, '«فقرأه عليهم» كفار مكة «ما كانوا به مؤمنين» أنفة من اتباعه.'),
(3132, 26, 200, '«كذلك» أي مثل إدخالنا التكذيب به بقراءة الأعجمي «سلكناه» أدخلنا التكذيب به «في قلوب المجرمين» كفار مكة بقراءة النبي.'),
(3133, 26, 201, '«لا يؤمنون به حتى يروا العذاب الأليم».'),
(3134, 26, 202, '«فيأتيهم بغتة وهم لا يشعرون».'),
(3135, 26, 203, '«فيقولوا هل نحن منظرون» لنؤمن فيقال لهم: لا قالوا: متى هذا العذاب، قال تعالى.'),
(3136, 26, 204, '«أفبعذابنا يستعجلون».'),
(3137, 26, 205, '«أفرأيت» أخبرني «إن متَّعناهم سنين».'),
(3138, 26, 206, '«ثم جاءهم ما كانوا يوعدون» من العذاب.'),
(3139, 26, 207, '«ما» إستفهامية بمعنى: أي شيء «أغنى عنهم ما كانوا يمتعون» في دفع العذاب أو تخفيفه أي: لم يغن.'),
(3140, 26, 208, '«وما أهلكنا من قرية إلا لها منذرون» رسل تنذر أهلها.'),
(3141, 26, 209, '«ذكرى» عظة لهم «وما كنا ظالمين» في إهلاكهم بعد إنذارهم ونزل ردا لقول المشركين.'),
(3142, 26, 210, '«وما تنزلت به» بالقرآن «الشياطين».'),
(3143, 26, 211, '«وما ينبغي» يصلح «لهم» أن ينزلوا به «وما يستطيعون» ذلك.'),
(3144, 26, 212, '«إنهم عن السمع» لكلام الملائكة «لمعزولون» بالشهب.'),
(3145, 26, 213, '«فلا تدع مع الله إلها آخر فتكون من المعذبين» إن فعلت ذلك الذي دعوك إليه.'),
(3146, 26, 214, '(وأنذر عشيرتك الأقربين) وهم بنو هاشم وبنو عبد المطلب "" وقد أنذرهم جهارا "" رواه البخاري ومسلم.'),
(3147, 26, 215, '«واخفض جناحكْ» ألن جانبك «لمن اتبعك من المؤمنين» الموحدين.'),
(3148, 26, 216, '«فإن عصوك» عشيرتك «فقل» لهم «إني بريء مما تعملون» من عبادة غير الله.'),
(3149, 26, 217, '«وتوكل» بالواو والفاء «على العزيز الرحيم» الله أي فوض إليه جميع أمورك.'),
(3150, 26, 218, '«الذي يراك حين تقوم» إلى الصلاة.'),
(3151, 26, 219, '«وتقلبك» في أركان الصلاة قائما وقاعدا وراكعا وساجدا «في الساجدين» المصلين.'),
(3152, 26, 220, '«إنه هو السميع العليم».'),
(3153, 26, 221, '«هل أنبئكم» يا كفرا مكة «على من تنزل الشياطين» بحذف إحدى التاءين من الأصل.'),
(3154, 26, 222, '«تنزل على كل أفاك» كذاب «أثيم» فاجر مثل مسيلمة وغيره من الكهنة.'),
(3155, 26, 223, '«يلقون» الشياطين «السمع» ما سمعوه من الملائكة إلى الكهنة «وأكثرهم كاذبون» يضمنون إلى المسموع كذبا وكان هذا قبل أن حجبت الشياطين عن السماء.'),
(3156, 26, 224, '«والشعراء يتبعهم الغاوون» في شعرهم فيقولن به ويرونه عنهم فهم مذمومون.'),
(3157, 26, 225, '«ألم تر» تعلم «أنهم في كل واد» من أودية الكلام وفنونه «يهيمون» يمضون فيجازون الحد مدحا وهجاء.'),
(3158, 26, 226, '«وأنهم يقولون» فعلنا «مالا يفعلون» يكذبون.'),
(3159, 26, 227, '«إلا الذين آمنوا وعملوا الصالحات» من الشعراء. «وذكروا الله كثيرا» لم يشغلهم الشعر عن الذكر «وانتصروا» بهجوهم الكفار «من بعد ما ظلموا» بهجو الكفار لهم في جملة المؤمنين فليسو مذمومين قال الله تعالى: (لا يحب الله الجهر بالسوء من القول إلا من ظلم) وقال تعالى (فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدي عليكم) «وسيعلم الذين ظلموا» من الشعراء وغيرهم «أي منقلب» مرجع «ينقلبون» يرجعون بعد الموت.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, '«طس» الله أعلم بمراده بذلك «تلك» هذه الآيات «آيات القرآن» آيات منه «وكتاب مبين» مظهر للحق من الباطل عطف بزيادة صفة.'),
(3161, 27, 2, 'هو «هدى» هاد من الضلالة «وبشرى للمؤمنين» المصدقين به بالجنة.'),
(3162, 27, 3, '«الذي يقيمون الصلاة» يأتون بها على وجهها «ويؤتون» يعطون «الزكاة وهم بالآخرة هم يوقنون» يعلمونها بالاستدلال وأعيد «هم» لما فصل بينه وبين الخبر.'),
(3163, 27, 4, '«إن الذين لا يؤمنون بالآخرة زينا لهم أعمالهم» القبيحة بتركيب الشهوة حتى رأوها حسنة «فهم يعمهون» يتحيرون فيها لقبحها عندنا.'),
(3164, 27, 5, '«أولئك الذين لهم سوء العذاب» أشدَّه في الدنيا القتل والأسر «وهم في الآخرة هم الأخسرون» لمصيرهم إلى النار المؤبدة عليهم.'),
(3165, 27, 6, '«وإنك» خطاب للنبي «لتلقَّى القرآن» يُلقى عليك بشدة «من لدن» من عند «حكيم عليم» في ذلك.'),
(3166, 27, 7, 'اذكر: «إذ قال موسى لأهله» زوجته عند مسيره من مدين إلى مصر «إني آنست» أبصرت من بعيد «ناراً سآتيكم منها بخبر» عن حال الطريق وكان قد ضلها «أو آتيكم بشهاب قبس» بالإضافة للبيان أي شعلة نار في رأس فتيلة أو عود «لعلكم تصطلون» والطاء بدل من تاء الافتعال من صلى بالنار بكسر اللام وفتحها: تستدفئون من البرد.'),
(3167, 27, 8, '«فلما جاءها نودي أن» أي بأن «بورك» أي بارك الله «من في النار» أي موسى «ومن حولها» أي الملائكة، أو العكس وبارك يتعدى بنفسه وبالحرف ويقدر بعد في مكان «وسبحان الله رب العالمين» من جملة ما نودي ومعناه تنزيه الله من السوء.'),
(3168, 27, 9, '«يا موسى إنه» أي الشأن «أنا الله العزيز الحكيم».'),
(3169, 27, 10, '«وألق عصاك» فألقاها «فلما رآها تهتز» تتحرك «كأنها جان» حية خفيفة «ولى مدبراً ولم يعقب» يرجع قال تعالى «يا موسى لا تخف» منها «إني لا يخاف لدي» عندي «المرسلون» من حية وغيرها'),
(3170, 27, 11, '«إلا» لكن «من ظلم» نفسه «ثم بدَّل حُسناً» أتاه «بعد سوءٍ» أي تاب «فإني غفور رحيم» أقبل التوبة وأغفر له.'),
(3171, 27, 12, '«وأدخل يدك في جيبك» طوق قميصك «تخرج» خلاف لونها من الأدمة «بيضاء من غير سوءٍ» برص لها شعاع يغشى البصر، آية «في تسع آيات» مرسلا بها «إلى فرعون وقومه إنهم كانوا قوماً فاسقين».'),
(3172, 27, 13, '«فلما جاءتهم آياتنا مبصرة» مضيئة واضحة «قالوا هذا سحر مبين» بيّن ظاهر.'),
(3173, 27, 14, '«وجحدوا بها» لم يقروا «و» قد «استيقنتها أنفسهم» أي تيقنوا أنها من عند الله «ظلماً وعلوّاً» تكبراً عن الإيمان بما جاء به موسى راجع إلى الجحد «فانظر» يا محمد «كيف كان عاقبة المفسدين» التي علمتها من إهلاكهم.'),
(3174, 27, 15, '«ولقد آتينا داود وسليمان» ابنه «علما» بالقضاء بين الناس ومنطق الطير وغير ذلك «وقالا» شكراً لله «الحمد لله الذي فضلنا» بالنبوة وتسخير الجنّ والإنس والشياطين «على كثير من عباده المؤمنين».'),
(3175, 27, 16, '«وورث سليمان داود» النبوة والعلم دون باقي أولاده «وقال يا أيها الناس عُلمنا منطق الطير» أي‌: فهمَ أصواته «وأُوتينا من كل شيء» تؤتاه الأنبياء والملوك «إن هذا» المؤتى «لهو الفضل المبين» البيَّن الظاهر.'),
(3176, 27, 17, '«وحشر» جمع «لسليمان جنوده من الجن والإنس والطير» في مسير له «فهم يوزعون» يجمعون ثم يساقون.'),
(3177, 27, 18, '«حتى إذا أتوْا على وادِ النمل» هو بالطائف أو بالشام، نمله صغار أو كبار «قالت نملة» ملكة النمل وقد رأت جند سليمان «يا أيها النمل ادخلوا مساكنكم لا يحطمنكم» يكسرنكم «سليمان وجنوده وهم لا يشعرون» نزل النمل منزلة العقلاء في الخطاب بخطابهم.'),
(3178, 27, 19, '«فتبسّم» سليمان ابتداء «ضاحكا» انتهاء «من قولها» وقد سمعه من ثلاثة أميال حملته إليه الريح فحبس جنده حين أشرف على واديهم حتى دخلوا بيوتهم وكان جنده ركباناً ومشاة في هذا السير «وقال رب أوزعني» ألهمني «أن أشكر نعمتك التي أنعمت» بها «علىَّ وعلى والديَّ وأن أعمل صالحاً ترضاه وأدخلني برحمتك في عبادك الصالحين» الأنبياء والأولياء.'),
(3179, 27, 20, '«وتفقّد الطير» ليرى الهدهد الذي يرى الماء تحت الأرض ويدل عليه بنقره فيها فتستخرجه الشياطين لاحتياج سليمان إليه للصلاة فلم يره «فقال ماليَ لا أرى الهدهد» أي أعرضَ لي ما منعني من رؤيته «أم كان من الغائبين» فلم أره لغيبته فلما تحققها.'),
(3180, 27, 21, 'قال «لأعذبنه عذاباً» تعذيباً «شديداً» بنتف ريشه وذنبه ورميه في الشمس فلا يمتنع من الهوام «أو لأذبحنه» بقطع حلقومه «أو ليأتيني» بنون مشددة مكسورة أو مفتوحة يليها نون مكسورة «بسلطان مبين» ببرهان بين ظاهر على عذره.'),
(3181, 27, 22, '«فمكث» بضم الكاف وفتحها «غير بعيد» يسيراً من الزمن وحضر لسليمان متواضعاً برفع رأسه وإرخاء ذنبه وجناحيه فعفا عنه وسأله عما لقي في غيبته «فقال أحَطتُ بما لم تحط به» أي: اطلعت على ما لم تطلع عليه «وجئتك من سبأٍ» بالصرف وتركه، قبيلة باليمن سميت باسم جد لهم باعتباره صرف «بنِبأ» خبر «يقين».'),
(3182, 27, 23, '«إني وجدت امرأة تملكهم» أي: هي ملكة لهم اسمها بلقيس «وأوتيت من كل شيء» يحتاج إليه الملوك من الآلة والعدة «ولها عرش» سرير «عظيم» طوله ثمانون ذراعاً وعرضه أربعون ذراعاً وارتفاعه ثلاثون ذراعاً مضروب من الذهب والفضة مكلل بالدر والياقوت الأحمر والزبرجد الأخضر والزمرد وقوائمه من الياقوت الأحمر والزبرجد الأخضر والزمرد عليه سبعة أبواب على كل بيت باب مغلق.'),
(3183, 27, 24, '«وجدتها وقومها يسجدون للشمس من دون الله وزيَّن لهم الشيطان أعمالهم فصدَّهم عن السبيل» طريق الحق «فهم لا يهتدون».'),
(3184, 27, 25, '«ألاً يسجدوا لله» أي: أن يسجدوا له فزيدت لا وأدغم فيها نون أن كما في قوله تعالى: (لئلا يعلم أهل الكتاب) والجملة في محل مفعول يهتدون بإسقاط إلى «الذي يخرج الخبء» مصدر بمعنى المخبوء من المطر والنبات «في السماوات والأرض ويعلم ما يخفون» في قلوبهم «وما يعلنون» بألسنتهم.'),
(3185, 27, 26, '«الله لا إله إلا هو رب العرش العظيم» استئناف جملة ثناء مشتمل على عرش الرحمن في مقابلة عرش بلقيس وبينهما بون عظيم.'),
(3186, 27, 27, '«قال» سليمان للهدهد «سننظر أصدقت» فيما أخبرتنا به «أم كنت من الكاذبين» أي من هذا النوع فهو أبلغ من أم كذبت فيه، ثم دلهم على الماء فاستخرج وارتووا وتوضؤوا وصلوا ثم كتب سليمان كتاباً صورته (من عبد الله سليمان بن داود إلى بلقيس ملكة سبأ "بسم الله الرحمن الرحيم السلام على من اتبع الهدى أما بعد فلا تعلوا علي وأتوني مسلمين) ثم طبعه بالمسك وختمه بخاتمه ثم قال للهدهد:'),
(3187, 27, 28, '«إذهب بكتابي هذا فألقه إليهم» أي بلقيس وقومها «ثم تولَّ» انصرف «عنهم» وقف قريباً منهم «فانظر ماذا يرجعون» يردون من الجواب فأخذه وأتاها وحولها جندها وألقاه في حجرها فلما رأته ارتعدت وخضعت خوفاً ثم وقفت على ما فيه.'),
(3188, 27, 29, 'ثم «قالت» لأشراف قومها «يا أيها الملأ إني» بتحقيق الهمزتين وتسهيل الثانية بقلبها واواً مكسورة «أُلقي إليَّ كتاب كريم» مختوم.'),
(3189, 27, 30, '«إنه من سليمان وإنه» أي مضمونه «بسم الله الرحمن الرحيم».'),
(3190, 27, 31, '«ألا تعلوا عليَّ وأتوني مسلمين».'),
(3191, 27, 32, '«قالت يا أيها الملأ أفتوني» بتحقيق الهمزتين وتسهيل الثانية بقلبها واواً، أي أشيروا عليَّ «في أمري ما كنت قاطعة أمراً» قاضيته «حتى تشهدون» تحضرون.'),
(3192, 27, 33, '«قالوا نحن أولوا قوة وأولو بأس شديد» أي: أصحاب شدة في الحرب «والأمر إليك فانظري ماذا تأمرينـ» ـنا نطعك.'),
(3193, 27, 34, '«قالت إن الملوك إذا دخلوا قريةً أفسدوها» بالتخريب «وجعلوا أعزة أهلها أذلة وكذلك يفعلون» أي: مرسلو الكتاب.'),
(3194, 27, 35, '«وإني مرسلة إليهم بهدية فناظرة بم يرجع المرسلون» من قبول الهدية أو ردها إن كان ملكاً قبلها أو نبياً لم يقبلها فأرسلت خدماً ذكوراً وإناثاً ألفاً بالسوية وخمسمائة لبنة من الذهب وتاجاً مكللا بالجواهر ومسكاً وعنبراً وغير ذلك مع رسول بكتاب فأسرع الهدهد إلى سليمان يخبره الخبر فأمر أن تضرب لبنات الذهب والفضة وأن تبسط من موضعه إلى تسعة فراسخ ميداناً وأن يبنوا حوله حائطاً مشرفاً من الذهب والفضة وأن يؤتي بأحسن دواب البر والبحر مع أولاد الجن عن يمين الميدان وشماله.'),
(3195, 27, 36, '«فلما جاء» الرسول بالهدية ومعه أتباعه «سليمان قال أتمدونن بمال فما أتانيَ الله» من النبوة والملك «خير مما آتاكم» من الدنيا «بل أنتم بهديتكم تفرحون» لفخركم بزخارف الدنيا.'),
(3196, 27, 37, '«إرجع إليهم» بما أتيت من الهدية «فلنأتينهم بجنود لا قبل» لا طاقة «لهم بها ولنخرجنهم منها» من بلد سبأ سميت باسم أبي قبيلتهم «أذلة وهم صاغرون» إن لم يأتوني مسلمين فلما رجع إليها الرسول بالهدية جعلت سريرها داخل سبعة أبواب داخل قصرها وقصرها داخل سبعة قصور وغلقت الأبواب وجعلت عليها حرساً وتجهزت للمسير إلى سليمان لتنظر ما يأمرها به فارتحلت في اثني عشر ألف قَيْل مع كل قَيْل ألوف كثيرة إلى أن قربت منه على فرسخ شعر بها.'),
(3197, 27, 38, '«قال يا أيها الملأ أيكم» في الهمزتين ما تقدم «يأتيني بعرشها قبل أن يأتوني مسلمين» منقادين طائعين فلي أخذه قبل ذلك لا بعده.'),
(3198, 27, 39, '«قال عفريت من الجن» هو القوي الشديد «أنا آتيك به قبل أن تقوم من مقامك» الذي تجلس فيه للقضاء وهو من الغداة إلى نصف النهار «وإني عليه لقوي» أي على حمله «أمين» على ما فيه من الجواهر وغيرها، قال سليمان أريد أسرع من ذلك.'),
(3199, 27, 40, '«قال الذي عنده علم من الكتاب» المنزل وهو آصف بن برخيا كان صدّيقاً يعلم اسم الله الأعظم الذي إذا دعا به أجيب «أنا آتيك به قبل أن يرتد إليك طرفك» إذا نظرت به إلى شيء فقال له انظر إلى السماء فنظر إليها ثم رد بطرفه فوجده موضوعاً بين يديه ففي نظره إلى السماء دعا آصف الاسم الأعظم أن يأتي الله به فحصل بأن جرى تحت الأرض حتى نبع تحت كرسي سليمان «فلما رآه مستقراً» ساكناً «عنده قال هذا» أي الإتيان لي به «من فضل ربي ليبلوني» ليختبرني «أأشكر» بتحقيق الهمزتين وإبدال الثانية ألف وتسهيلها وإدخال ألف بين المسهلة الأخرى وتركه «أم أكفر» النعمة «ومن شكر فإنما يشكر لنفسه» أي لأجلها لأن ثواب شكره له «ومن كفر» النعمة «فإن ربي غني» عن شكره «كريم» بالإفضال على من يكفرها.'),
(3200, 27, 41, '«قال نكّروا لها عرشها» أي غيروه إلى حال تنكره إذا رأته «ننظر أتهتدي» إلى معرفته «أم تكون من الذين لا يهتدون» إلى معرفة ما يغيّر عليهم قصد بذلك اختبار عقلها لما قيل إن فيه شيئاً فغيروه بزيادة أو نقص وغير ذلك.'),
(3201, 27, 42, '«فلما جاءت قيل» لها «أهكذا عرشك» أي أمثل هذا عرشك «قالت كأنه هو» فعرفته وشبهت عليهم كما شبهوا عليها إذ لم يقل أهذا عرشك ولو قيل هذا قالت: نعم، قال سليمان لما رأى لها معرفة وعلماً: «وأوتينا العلم من قبلها وكنا مسلمين».'),
(3202, 27, 43, '«وصدها» عن عبادة الله «ما كانت تعبد من دون الله» أي غيره «إنها كانت من قوم كافرين».'),
(3203, 27, 44, '«قيل لها» أيضاً «ادخلي الصرح» هو سطح من زجاج أبيض شفاف تحته ماء عذب جار فيه سمك اصطنعه سليمان لما قيل له إن ساقيها وقدميها كقدمي الحمار «فلما رأته حسبته لجة» من الماء «وكشفت عن ساقيها» لتخوضه وكان سليمان على سريره في صدر الصرح فرأى ساقيها وقدميها حساناً «قال» لها «إنه صرح ممرد» مملس «من قوارير» من زجاج ودعاها إلى الإسلام «قالت رب إني ظلمت نفسي» بعبادة غيرك «وأسلمت» كائنة «مع سليمان لله رب العالمين» وأراد تزوجها فكره شعر ساقيها فعملت له الشياطين النورة فأزالته بها فتزوجها وأحبها وأقرها على ملكها وكان يزورها في كل شهر مرة ويقيم عندها ثلاثة أيام وانقضى ملكها بانقضاء ملك سليمان روي أنه ملك وهو ابن ثلاث عشرة سنة ومات وهو ابن ثلاث وخمسين سنة فسبحان من لا انقضاء لدوام ملكه.'),
(3204, 27, 45, '«ولقد أرسلنا إلى ثمود أخاهم» من القبيلة «صالحاً أن» أي بأن «اعبدا الله» وحدوه «فإذا هم فريقان يختصمون» في الدين فريق مؤمنون من حين إرساله إليهم وفريق كافرون.'),
(3205, 27, 46, '«قال» للمكذبين «ياقوم لمَ تستعجلون بالسيئة قبل الحسنة» أي بالعذاب قبل الرحمة حيث قلتم إن كان ما أتيتنا به حقاً فأتنا بالعذاب «لولا» هلا «تستغفرون الله» من الشرك «لعلكم ترحمون» فلا تعذبون.'),
(3206, 27, 47, '«قالوا اطّيرنا» أصله تطيرنا أدغمت التاء في الطاء واجتلبت همزة الوصل أي تشاءمنا «بك وبمن معك» المؤمنين حيث قحطوا المطر وجاعوا «قال طائركم» شؤمكم «عند الله» أتاكم به «بل أنتم قوم تفتنون» تختبرون بالخير والشر.'),
(3207, 27, 48, '«وكان في المدينة» مدينة ثمود «تسعة رهط» أي رجال «يفسدون في الأرض» بالمعاصي منها قرضهم الدنانير والدراهم «ولا يصلحون» بالطاعة.'),
(3208, 27, 49, '«قالوا» أي قال بعضهم لبعض «تقاسموا» أي احلفوا «بالله لنبيتنه» بالنون والتاء وضم التاء الثانية «وأهله» أي من آمن به أي نقتلهم ليلاً «ثم لنقولن» بالنون والتاء وضم اللام الثانية «لوليه» لولي دمه «ما شهدنا» حضرنا «مهلك أهله» بضم الميم وفتحها أي إهلاكهم أو هلاكهم فلا ندري من قتلهم «وإنا لصادقون».'),
(3209, 27, 50, '«ومكروا» في ذلك «مكراً ومكرنا مكراً» أي جازيناهم بتعجيل عقوبتهم «وهم لا يشعرون».'),
(3210, 27, 51, '«فانظر كيف كان عاقبة مكرهم أنّا دمرناهم» أهلكناهم «وقومهم أجمعين» بصيحة جبريل أو برمي الملائكة بحجارة يرونها ولا يرونهم.'),
(3211, 27, 52, '«فتلك بيوتهم خاوية» أي خالية ونصبه على الحال والعامل معنى الإشارة «بما ظلموا» بظلمهم أي كفرهم «إن في ذلك لآية» لعبرة «لقوم يعلمون» قدرتنا فيتعظون.'),
(3212, 27, 53, '«وأنجينا الذين آمنوا» بصالح وهم أربعة آلاف «وكانوا يتقون» الشرك.'),
(3213, 27, 54, '«ولوطاً» منصوب باذكر مقدراً قبله ويبدل منه «إذ قال لقومه أتأتون الفاحشة» أي اللواط «وأنتم تبصرون» أي يبصر بعضكم بعضا انهماكاً في المعصية.'),
(3214, 27, 55, '«أإنكم» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين «لتأتون الرجال شهوة من دون النساء بل أنتم قوم تجهلون» عاقبة فعلكم.'),
(3215, 27, 56, '«فما كان جواب قومه إلا أن قالوا أخرجوا آل لوط» أهل «من قريتكم إنهم أناس يتطهرون» من أدبار الرجال.'),
(3216, 27, 57, '«فأنجيناه وأهله إلا امرأته قدرناها» جعلناها بتقديرنا «من الغابرين» الباقين في العذاب.'),
(3217, 27, 58, '«وأمطرنا عليهم مطراً» حجارة السجيل فأهلكتهم «فساء» بئس «مطر المنذرين» بالعذاب مطرهم.'),
(3218, 27, 59, '«قل» يا محمد «الحمد لله» على هلاك الكفار من الأمم الخالية «وسلام على عباده الذين اصطفى» هم «آلله» بتحقيق الهمزتين وإبدال الثانية ألفاً وتسهليها وإدخال ألف بين المسهلة والأخرى وتركه «خير» لمن يعبده «أمّا تشركون» بالتاء والياء أي أهل مكة به الآلهة خير لعابديها.'),
(3219, 27, 60, '«أمَّن خلق السماوات والأرض وأنزل لكم من السماء ماءً فأنبتنا» فيه التفتات من الغيبة إلى التكلم «به حدائق» جمع حديقة وهو البستان المحوط «ذات بهجةِ» حُسن «ما كان لكم أن تنبتوا شجرها» لعدم قدرتكم عليه «أإلهٌ» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين في مواضعه السبعة «مع الله» أعانه على ذلك أي ليس معه إله «بل هم قوم يعدلون» يشركون بالله غيره.'),
(3220, 27, 61, '«أمَّن جعل الأرض قراراً» لا تميد بأهلها «وجعل خلالها» فيما بينها «أنهاراً وجعل لها رواسي» جبالاً أثبت بها الأرض «وجعل بين البحرين حاجزاً» بين العذب والملح لا يختلط أحدهما بالآخر «أإلهٌ مع الله بل أكثرهم لا يعلمون» توحيده.'),
(3221, 27, 62, '«أَمَّن يجيب المضطر» المكروب الذي مسه الضر «إذا دعاه ويكشف السوء» عنه وعن غيره «ويجعلكم خلفاء الأرض» الإضافة بمعنى في، أي يخلف كل قرن القرن الذي قبله: «أَإِله مع الله قليلاً ما تذكَّرون» تتعظون بالفوقانية والتحتانية وفيه إدغام التاء في الذال وما زائدة لتقليل القليل.'),
(3222, 27, 63, '«أمَّن يهديكم» يرشدكم إلى مقاصدكم «في ظلمات البر والبحر» بالنجوم ليلاً وبعلامات الأرض نهاراً «ومن يرسل الرياح بُشراً بين يديْ رحمته» قدام المطر «أَإِله مع الله تعالى الله عما يشركون» به غيره.'),
(3223, 27, 64, '«أمَّن يبدأ الخلق» في الأرحام من نطفة «ثم يعيده» بعد الموت وإن لم تعترفوا بالإعادة لقيام البراهين عليها «ومن يرزقكم من السماء» بالمطر «والأرض» بالنبات «أَإِله مع الله» أي لا يفعل شيئاً مما ذكر إلا الله ولا إله معه «قل» يا محمد «هاتوا برهانكم» حجتكم «إن كنتم صادقين» أن معي إلهاً فعل شيئاً مما ذكر، وسألوه عن وقت قيام الساعة فنزل:'),
(3224, 27, 65, '«قل لا يعلم من في السماوات والأرض» من الملائكة والناس «الغيب» أي ما غاب عنهم «إلا» لكن «الله» يعلمه «وما يشعرون» أي كفار مكة كغيرهم «أيان» وقت «يبعثون».'),
(3225, 27, 66, '«بل» بمعنى هل «أدرك» وزن أكرم، وفي قراءة أخرى ادّارَكَ بتشديد الدال وأصله تدارك أبدلت التاء دالاً وأدغمت في الدال واجتلبت همزة الوصل أي بلغ ولحق أو تتابع وتلاحق «علمهم في الآخرة» أي بها حتى سألوا عن وقت مجيئها ليس الأمر كذلك «بل هم في شك منها بل هم منها عمون» من عمى القلب وهو أبلغ مما قبله والأصل عميون استثقلت الضمة على الياء فنقلت إلى الميم بعد حذف كسرتها.'),
(3226, 27, 67, '«وقال الذين كفروا» أيضاً في إنكار البعث «أئذا كنا تراباً وآباؤنا أئنا لمخرجون» من القبور.'),
(3227, 27, 68, '«لقد وعدنا هذا نحن وآباؤنا من قبل إن» ما «هذا إلا أساطير الأولين» جمع أسطورة بالضم أي ما سطر من الكذب.'),
(3228, 27, 69, '«قل سيروا في الأرض فانظروا كيف كان عاقبة المجرمين» بإنكارهم، وهي هلاكهم بالعذاب.'),
(3229, 27, 70, '«ولا تحزن عليهم ولا تكن في ضيق مما يمكرون» تسلية للنبي * أي لا تهتم بمكرهم عليك فإنا ناصروك عليهم.'),
(3230, 27, 71, '«ويقولون متى هذا الوعد» بالعذاب «إن كنتم صادقين» فيه.'),
(3231, 27, 72, '«قل عسى أن يكون رَدِفَ» قرب «لكم بعض الذي تستعجلون» فحصل لهم القتل ببدر وباقي العذاب يأتيهم بعد الموت.'),
(3232, 27, 73, '«وإن ربك لذو فضل على الناس» ومنه تأخير العذاب عن الكفار «ولكن أكثرهم لا يشكرون» فالكفار لا يشكرون تأخير العذاب لإنكارهم وقوعه.'),
(3233, 27, 74, '«وإن ربك ليعلم ما تكن صدورهم» تخفيه «وما يعلنون» بألسنتهم.'),
(3234, 27, 75, '«وما من غائبة في السماء والأرض» الهاء للمبالغة: أي شيء في غاية الخفاء على الناس «إلا في كتاب مبين» بَيِّن هو اللوح المحفوظ ومكنون علمه تعالى ومنه تعذيب الكفار.'),
(3235, 27, 76, '«إن هذا القرآن يقص على بني إسرائيل» الموجودين في زمان نبينا «أكثر الذي هم فيه يختلفون» أي ببيان ما ذكر على وجهه الرافع للاختلاف بينهم لو أخذوا به وأسلموا.'),
(3236, 27, 77, '«وإنه لهدى» من الضلالة «ورحمة للمؤمنين» من العذاب.'),
(3237, 27, 78, '«إن ربك يقضي بينهم» كغيرهم يوم القيامة «بحكمه» أي عدله «وهو العزيز» الغالب «العليم» بما يحكم به فلا يمكن أحداً مخالفته كما خالف الكفار في الدنيا أنبياءه.'),
(3238, 27, 79, '«فتوكل على الله» ثق به «إنك على الحق المبين» الدين البيِّن فالعاقبة لك بالنصر على الكفار ثم ضرب أمثالاً لهم بالموتى والصم والعمي فقال:'),
(3239, 27, 80, '«إنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا» بتحقيق الهمزتين وتسهيل الثانية بينها وبين الياء «ولوا مدبرين».'),
(3240, 27, 81, '«وما أنت بهاد العمي عن ضلالتهم إن» ما «تسمع» سماع إفهام وقبول «إلا من يؤمن بآياتنا» القرآن «فهم مسلمون» مخلصون بتوحيد الله.'),
(3241, 27, 82, '«وإذا وقع القول عليهم» حق العذاب أن ينزل بهم في جملة الكفار «أخرجنا لهم دابة من الأرض تكلمهم» أي تكلم الموجودين حين خروجها بالعربية تقول لهم من جملة كلامها عنا «إن الناس» كفار مكة وعلى قراءة فتح همزة إن تقدر الباء بعد تكلمهم «كانوا بآياتنا لا يوقنون» لا يؤمنون بالقرآن المشتمل على البعث والحساب والعقاب، وبخروجها ينقطع الأمر بالمعروف والنهى عن المنكر ولا يؤمن كافر كما أوْحى الله إلى نوح [أنه لن يؤمن من قومك إلا من قد آمن].'),
(3242, 27, 83, '«و» اذكر «يوم نحشر من كل أمة فوجاً» جماعة «ممن يكذب بآياتنا» وهم رؤساؤهم المتبعون «فهم يوزعون» أي يجمعون برد آخرهم إلى أولهم ثم يساقون.'),
(3243, 27, 84, '«حتى إذا جاءُوا» مكان الحساب «قال» تعالى لهم «أكذبتم» أنبيائي «بآياتي ولم تحيطوا» من جهة تكذيبكم «بها علماً أما» فيه إدغام ما الاستفهامية «ذا» موصول أي ما الذي «كنتم تعملون» مما أمرتم به.'),
(3244, 27, 85, '«ووقع القول» حق العذاب «عليهم بما ظلموا» أي أشركوا «فهم لا ينطقون» إذ لا حجة لهم.'),
(3245, 27, 86, '«ألم يروا أنا جعلنا» خلقنا «الليل ليسكنوا فيه» كغيرهم «والنهار مبصراً» بمعنى يبصر فيه ليتصرفوا فيه «إن في ذلك لآيات» دلالات على قدرته تعالى «لقوم يؤمنون» خصوا بالذكر لانتفاعهم بها في الإيمان بخلاف الكافرين.'),
(3246, 27, 87, '«ويوم ينفخ في الصور» القرن النفخة الأولى من إسرافيل «ففزع من في السماوات ومن في الأرض» خافوا الخوف المفضي إلى الموت كما في آية أخرى فصعق، والتعبير فيه بالماضي لتحقق وقوعه «إلا من شاء الله» أي جبريل وميكائيل وإسرافيل وملك الموت وعن ابن عباس هم الشهداء إذ هم أحياء عند ربهم يرزقون «وكل» تنوينه عوض عن المضاف إليه، أي وكلهم بعد إحيائهم يوم القيامة «أتوه» بصيغة الفعل واسم الفاعل «داخرين» صاغرين والتعبير في الإتيان بالماضي لتحقق وقوعه.'),
(3247, 27, 88, '«وترى الجبال» تبصرها وقت النفخة «تحسبها» تظنها «جامدة» واقفة مكانها لعظمها «وهي تمر مر السحاب» المطر إذا ضربته الريح أي تسير سيره حتى تقع على الأرض فتستوي بها مبثوثة ثم تصير كالعهن، ثم تصير هباء منثوراً «صنع الله» مصدر مؤكد لمضمون الجملة قبله أضيف فاعله بعد حذف عامله أي صنع الله ذلك صنع «الذي أتقن» أحكم «كل شيء» صنعه «إنه خبير بما يفعلون» بالياء والتاء أي أعداؤه من المعصية وأولياؤه من الطاعة.'),
(3248, 27, 89, '«من جاء بالحسنة» أي لا إله إلا الله يوم القيامة «فله خير» ثواب «منها» أي بسببها وليس للتفضيل إذ لا فعل خير منها وفي آية أخرى "عشر أمثالها" «وهم» الجاءون بها «من فزع يومئذ» بالإضافة وكسر الميم وفتحها وفزع منوناً وفتح الميم «آمنون».'),
(3249, 27, 90, '«ومن جاء بالسيئة» أي الشرك «فكبت وجوههم في النار» بأن وليتها، وذكرت الوجوه لأنها موضع الشرف من الحواس فغيرها من باب أولى ويقال لهم تبكيتاً «هل» ما «تجزون إلا» جزاء «ما كنتم تعملون» من الشرك والمعاصي قل لهم:'),
(3250, 27, 91, '«إنما أمرت أن أعبد رب هذه البلدة» أي مكة «الذي حرمها» جعلها حرماً آمناً لا يسفك فيها دم إنسان ولا يظلم فيها أحد ولا يصاد صيدها ولا يختلى خلاها، وذلك من النعم على قريش أهلها في رفع الله عن بلدهم العذاب والفتن الشائعة في جميع بلاد العرب «وله» تعالى «كل شيء» فهو ربه وخالقه ومالكه «وأمرت أن أكون من المسلمين» لله بتوحيده.'),
(3251, 27, 92, '«وأن أتلوَ القرآن» عليكم تلاوة الدعوة إلى الإيمان «فمن اهتدى» له «فإنما يهتدي لنفسه» أي لأجلها فإن ثواب اهتدائه له «ومن ضل» عن الإيمان وأخطأ طريق الهدى «فقل» له «إنما أنا من المنذرين» المخوفين فليس عليَّ إلا التبليغ وهذا قبل الأمر بالقتال.'),
(3252, 27, 93, '«وقل الحمد لله سيريكم آياته فتعرفونها» فأراهم الله يوم بدر القتل والسبي وضرب الملائكة وجوههم وأدبارهم وعجلهم الله إلى النار «وما ربك بغافل عما يعملون» بالياء والتاء وإنما يمهلهم لوقتهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, '«طسم» الله أعلم بمراده بذلك.'),
(3254, 28, 2, '«تلك» أي هذه الآيات «آيات الكتاب» الإضافة بمعنى من «المبين» المظهر الحق من الباطل.'),
(3255, 28, 3, '«نتلُوا» نقص «عليك من نبإِ» خبر «موسى وفرعون بالحق» الصدق «لقوم يؤمنون» لأجلهم لأنهم المنتفعون به.'),
(3256, 28, 4, '«إن فرعون علا» تعظم «في الأرض» أرض مصر «وجعل أهلها شيعاً» فرقاً في خدمته «يستضعف طائفة منهم» هم بنو إسرائيل «يذِّبح أبناءهم» المولودين «ويستحيي نساءَهم» يستبقيهن أحياء لقول بعض الكهنة له: إن مولوداً يولد في بني إسرائيل يكون سبب زوال ملكك «إنه كان من المفسدين» بالقتل وغيره.'),
(3257, 28, 5, '«ونريد أن نمن على الذين استضعفوا في الأرض ونجعلهم أئمة» بتحقيق الهمزتين وإبدال الثانية ياء يقتدى بهم في الخير «ونجعلهم الوارثين» ملك فرعون.'),
(3258, 28, 6, '«ونمكن لهم في الأرض» أرض مصر والشام «ونريَ فرعون وهامان وجنودهما» وفي قراءة ويرى بفتح التحتانية والراء ورفع الأسماء الثلاثة «منهم ما كانوا يحذرون» يخافون من المولود الذي يذهب ملكهم على يديه.'),
(3259, 28, 7, '«وأوحينا» وحي إلهام أو منام «إلى أم موسى» وهو المولود المذكور ولم يشعر بولادته غير أخته «أن أرضعيه فإذا خفت عليه فألقيه في اليم» البحر أي النيل «ولا تخافي» غرقه «ولا تحزني» لفراقه «إنا رادوه إليك وجاعلوه من المرسلين» فأرضعته ثلاثة أشهر لا يبكي وخافت عليه فوضعته في تابوت مطليٍّ بالقار من داخل ممهد له فيه وأغلقته وألقته في بحر النيل ليلاً.'),
(3260, 28, 8, '«فالتقطه» بالتابوت صبيحة الليل «آل» أعوان «فرعون» فوضعوه بين يديه وفتح وأخرج موسى منه وهو يمص من إبهامه لبناً «ليكون لهم» في عاقبة الأمر «عدواً» يقتل رجالهم «وحزناً» يستعبد نساءهم وفي قراءة بضم الحاء وسكون الزاي لغتان في المصدر وهو هنا بمعنى اسم الفاعل من حزنه كأحزنه «إن فرعون وهامان». وزيره «وجنودهما كانوا خاطئين» من الخطيئة أي عاصين فعوقبوا على يديه.'),
(3261, 28, 9, '«وقالت امرأة فرعون» وقد هم مع أعوانه بقتله هو «قرت عين لي ولك لا تقتلوه عسى أن ينفعنا أو نتخذه ولداً» فأطاعوها «وهو لا يشعرون» بعاقبة أمرهم معه.'),
(3262, 28, 10, '«وأصبح فؤاد أم موسى» لما علمت بالتقاطه «فارغاً» مما سواه. «إن» مخففة من الثقيلة واسمها محذوف أي إنها «كادت لتبدي به» أي بأنه ابنها «لولا أن ربطنا على قلبها» بالصبر أي سكناه «لتكون من المؤمنين» المصدقين بوعد الله وجواب لولا دل عليه ما قبله.'),
(3263, 28, 11, '«وقالت لأخته» مريم «قصيه» اتبعي أثره حتى تعلمي خبره «فبصرت به» أبصرته «عن جُنُب» من مكان بعيد اختلاساً «وهم لا يشعرون» أنها أخته وأنها ترقبه.'),
(3264, 28, 12, '«وحرمنا عليه المراضع من قبل» أي قبل رده إلى أمه أي منعناه من قبول ثدي مرضعة غير أمه فلم يقبل ثدي واحدة من المراضع المحضرة له «فقالت» أخته «هل أدلكم على أهل بيت» لما رأت حنوهم عليه «يكفلونه لكم» بالإرضاع وغيره «وهم له ناصحون» وفسرت ضمير له بالملك جواباً لهم فأجيبت فجاءت بأمه فقبل ثديها وأجابتهم عن قبوله بأنها طيبة الريح طيبة اللبن فأذن لها في إرضاعه في بيتها فرجعت به كما قال تعالى:'),
(3265, 28, 13, '«فرددناه إلى أمه كي تقر عينها» بلقائه «ولا تحزن» حينئذ «ولتعلم أن وعد الله» برده إليها «حق ولكن أكثرهم» أي الناس «لا يعلمون» بهذا الوعد ولا بأن هذه أخته وهذه أمه فمكث عندها إلى أن فطمته وأجرى عليها أجرتها لكل يوم دينار وأخذتها لأنها مال حربي فأتت به فرعون فتربى عنده كما قال تعالى حكاية عنه في سورة الشعراء «ألم نربِّك فينا وليداً ولبثت فينا من عمرك سنين»'),
(3266, 28, 14, '«ولما بلغ أشده» وهو ثلاثون سنة أو وثلاث «واستوى» أي بلغ أربعين سنة «آتيناه حكماً» حكمة «وعلماً» فقهاً في الدين قبل أن يبعث نبياً «وكذلك» كما جزيناه «نجزي المحسنين» لأنفسهم.'),
(3267, 28, 15, '«ودخل» موسى «المدينة» مدينة فرعون وهي منف بعد أن غاب عنه مدة «على حين غفلة من أهلها» وقت القيلولة «فوجد فيها رجلين يقتتلان هذا من شيعته» أي إسرائيلي «وهذا من عدوه» أي قبطي يسخر إسرائيلياً ليحمل حطباً إلى مطبخ فرعون «فاستغاثه الذي من شيعته على الذي من عدوه» فقال له موسى خلِّ سبيله فقيل إنه قال لموسى لقد هممت أن أحمله عليك «فوكزه موسى» أي ضربه بجمع كفه وكان شديد القوة والبطش «فقضى عليه» قتله ولم يكن يقصد قتله ودفنه في الرمل «قال هذا» قتله «من عمل الشيطان» المهيج غضبي «إنه عدو» لابن آدم «مضل» له «مبين» بيّن الإضلال.'),
(3268, 28, 16, '«قال» نادماً «رب إني ظلمت نفسي» بقتله «فاغفر لي فغفر له إنه هو الغفور الرحيم» أي المتصف بهما أزلاً وأبداً.'),
(3269, 28, 17, '«قال رب بما أنعمت» بحق إنعامك «عليَّ» بالمغفرة اعصمني «فلن أكون ظهيراً» عوناً «للمجرمين» الكافرين بعد هذه إن عصمتني.'),
(3270, 28, 18, '«فأصبح في المدينة خائفاً يترقب» ينتظر ما يناله من جهة أهل القتيل «فإذا الذي استنصره بالأمس يستصرخه» يستغيث به على قبطيٍّ آخر «قال له موسى إنك لغوي مبين» بيّن الغواية لما فعلته بالأمس واليوم.'),
(3271, 28, 19, '«فلما أن» زائدة «أراد أن يبطش بالذي هو عدو لهما» لموسى والمستغيث به «قال» المستغيث ظاناً أنه يبطش به لما قال له «يا موسى أتريد أن تقتلني كما قتلت نفساً بالأمس إن» ما «تريد إلا أن تكون جباراً في الأرض وما تريد أن تكون من المصلحين» فسمع القبطي ذلك فعلم أن القاتل موسى فانطلق إلى فرعون فأخبره بذلك فأمر فرعون الذباحين بقتل موسى فأخذوا في الطريق إليه.'),
(3272, 28, 20, '«وجاء رجل» هو مؤمن آل فرعون «من أقصا المدينة» آخرها «يسعى» يسرع في مشيه من طريق أقرب من طريقهم «قال يا موسى إن الملأَ» من قوم فرعون «يأتمرون بك» يتشاورون فيك «ليقتلوك فاخرج» من المدينة «إني لك من الناصحين» في الأمر بالخروج.'),
(3273, 28, 21, '«فخرج منها خائفاً يترقب» لحوق طالب أو غوث الله إياه «قال رب نجني من القوم الظالمين» قوم فرعون.'),
(3274, 28, 22, '«ولما توجه» قصد بوجهه «تلقاء مدين» جهتها وهي قرية شعيب مسيرة ثمانية أيام من مصر سميت بمدين بن إبراهيم ولم يكن يعرف طريقها «قال عسى ربي أن يهديني سواء السبيل» أي قصد الطريق الوسط إليها فأرسل الله ملكاً بيده عنزة فانطلق به إليها.'),
(3275, 28, 23, '«ولما ورد ماء مدين» بئر فيها أي وصل إليها «وجد عليه أُمَّة» جماعة «من الناس يسقون» مواشيهم «ووجد من دونهم» سواهم «امرأتين تذودان» تمنعان أغنامهما عن الماء «قال» موسى لهما «ما خطبكما» ما شأنكما لا تسقيان «قالتا لا نسقي حتى يصدر الرعاء» جمع راع أي يرجعون من سقيهم خوف الزحام فنسقي وفي قراءة يصدر من الرباعي أي يصرفوا مواشيهم عن الماء «وأبونا شيخ كبير» لا يقدر أن يسقي.'),
(3276, 28, 24, '«فسقى لهما» من بئر أخرى بقربهما رفع حجراً عنها لا يرفعه إلا عشرة أنفس «ثم تولى» انصرف «إلى الظل» لسمرة من شدة حر الشمس وهو جائع «فقال رب إني لما أنزلت إليَّ من خير» طعام «فقيرٌ» محتاج فرجعتا إلى أبيهما في زمن أقل مما كانتا ترجعان فيه فسألهما عن ذلك فأخبرتاه بمن سقي لهما فقال لإحداهما: ادعيه لي، قال تعالى:'),
(3277, 28, 25, '«فجاءته إحداهما تمشي على استحياء» أي واضعة كُمَّ درعها على وجهها حياء منه «قالت إن أبي يدعوك ليجزيك أجر ما سقيت لنا» فأجابها منكراً في نفسه أخذ الأجرة كأنها قصدت المكافأة إن كان ممن يريدها فمشت بين يديه فجعلت الريح تضرب ثوبها فتكشف ساقيها فقال لها: امشي خلفي ودليني على الطريق ففعلت إلى أن جاء أباها وهو شعيب عليه السلام وعنده عشاء فقال: اجلس فتعش قال: أخاف أن يكون عوضاً مما سقيت لهما وإنا أهل بيت لا نطلب على عمل خير عوضاً قال: لا، عادتي وعادة آبائي نقري الضيف ونطعم الطعام فأكل وأخبره بحاله قال تعالي «فلما جاءه وقص عليه القصص» مصدر بمعنى المقصوص من قتله القبطي وقصدهم قتله وخوفه من فرعون «قال لا تخف نجوت من القوم الظالمين» إذ لا سلطان لفرعون على مدين.'),
(3278, 28, 26, '«قالت إحداهما» وهي المرسَلة الكبرى أو الصغرى «يا أبت استأجره» اتخذه أجيراً يرعى غنمنا بدلنا «إن خير من استأجرت القوي الأمين» أي استأجره لقوته وأمانته فسألها عنه فأخبرته بما تقدم من رفعه حجر البئر ومن قوله لها: إمشي خلفي وزيادة أنها لما جاءته وعلم بها صوب رأسه فلم يرفعه فرغب في إنكاحه.'),
(3279, 28, 27, '«قال إني أريد أن أُنكحك إحدى ابنتيَّ هاتين» وهي الكبرى أو الصغرى «على أن تأجرني» تكون أجيراً لي في رعي غنمي «ثماني حجج» أي سنين «فإن أتممت عشراً» أي رعي عشر سنين «فمن عندك» التمام «وما أريد أن أشق عليك» باشتراط العشر «ستجدني إن شاء الله» للتبرك «من الصالحين» الوافين بالعهد.'),
(3280, 28, 28, '«قال» موسى «ذلك» الذي قلته «بيني وبينك أيما الأجلين» الثماني أو العشر وما زائدة أي رعيه «قضيت» به أي فرغت منه «فلا عدوان عليَّ» بطلب الزيادة عليه «والله على ما نقول» أنا وأنت «وكيل» حفيظ أو شهيد فتم العقد بذلك وأمر شعيب ابنته أن تعطي موسى عصا يدفع بها السباع عن غنمه وكانت عصيُّ الأنبياء عنده فوقع في يدها عصا آدم من آس الجنة فأخذها موسى بعلم شعيب.'),
(3281, 28, 29, '«فلما قضى موسى الأجل» أي رعيه وهو ثمان أو عشر سنين وهو المظنون به «وسار بأهله» زوجته بإذن أبيها نحو مصر «آنس» أبصر من بعيد «من جانب الطور» اسم جبل «ناراً قال لأهله امكثوا» هنا «إني آنست ناراً لعلي آتيكم منها بخبر» عن الطريق وكان قد أخطئها «أو جذوة» بتثليث الجيم قطعة وشعلة «من النار لعلكم تصطلون» تستدفئون والطاء بدل من تاء الافتعال من صلي بالنار بكسر اللام وفتحها.'),
(3282, 28, 30, '«فلما أتاها نودي من شاطئ» جانب «الواد الأيمن» لموسى «في البقعة المباركة» لموسى لسماعه كلام الله فيها «من الشجرة» بدل من شاطئ بإعادة الجار لنباتها فيه وهي شجرة عناب أو عليق أو عوسج «أن» مفسرة لا مخففة «يا موسى إني أنا الله رب العالمين».'),
(3283, 28, 31, '«وأن ألق عصاك» فألقاها «فلما رآها تهتز» تتحرك «كأنها جان» وهي الحية الصغيرة من سرعة حركتها «ولَّى مدبرا» هاربا منها «ولم يعقب» أي يرجع فنودي «يا موسى أقبل ولا تخف إنك من الآمنين».'),
(3284, 28, 32, '«اسلك» أدخل «يدك» اليمنى بمعنى الكف «في جيبك» هو طوق القميص وأخرجها «تخرج» خلاف ما كانت عليه من الأدمة «بيضاء من غير سوء» أي برص فأدخلها وأخرجها تضيء كشعاع الشمس تغشى البصر «واضمم إليك جناحك من الرَّهَب» بفتح الحرفين وسكون الثاني مع فتح الأول وضمه أي الخوف الحاصل من إضاءة اليد بأن تدخلها في جيبك فتعود إلى حالتها الأولى وعبر عنها بالجناح لأنها للإنسان كالجناح للطائر «فَذَانِّكَ» بالتشديد والتخفيف أي العصا واليد وهما مؤنثان وإنما ذكر المشار به إليهما المبتدأ لتذكير خبره «برهانان» مرسلان «من ربك إلي فرعون وملئه إنهم كانوا قوماً فاسقين».'),
(3285, 28, 33, '«قال رب إني قتلت منهم نفساً» هو القبطي السابق «فأخاف أن يقتلون» به.'),
(3286, 28, 34, '«وأخي هارون هو أفصح مني لسانا» أبين «فأرسله معي ردْءا» معينا وفي قراءة بفتح الدال بلا همزة «يصدقْني» بالجزم جواب الدعاء وفي قراءة بالرفع وجملته صفة ردءاً «إني أخاف أن يكذبون».'),
(3287, 28, 35, '«قال سنشد عضدك» نقويك «بأخيك ونجعل لكما سلطاناً» غلبة «فلا يصلون إليكما» بسوء، اذهبا «بآياتنا أنتما ومن اتبعكما الغالبون» لهم.'),
(3288, 28, 36, '«فلما جاءهم موسى بآياتنا بينات» واضحات حال «قالوا ما هذا إلا سحر مفترى» مختلق «وما سمعنا بهذا» كائنا «في» أيام «آبائنا الأولين».'),
(3289, 28, 37, '«وقال» بواو وبدونها «موسى ربي أعلم» عالم «بمن جاء بالهدى من عنده» الضمير للرب «ومن» عطف على من قبلها «تكون» بالفوقانية والتحتانية «له عاقبة الدار» أي العاقبة المحمودة في الدار الآخرة أي هو أنا في الشقين فأنا محق فيما جئت به «إنه لا يفلح الظالمون» الكافرون.'),
(3290, 28, 38, '«وقال فرعون يا أيها الملأ ما علمت لكم من إله غيري فأوقد لي يا هامان على الطين» فاطبخ لي الآجر «فاجعل لي صرحا» قصرا عاليا «لعلى أطلع إلى إله موسى» أنظر إليه وأقف عليه «وإني لأظنه من الكاذبين» في ادعائه إلها آخر وأنه رسوله.'),
(3291, 28, 39, '«واستكبر هو وجنوده في الأرض» أرض مصر «بغير الحق وظنوا أنهم إلينا لا يرَجِعون» بالبناء للفاعل وللمفعول.'),
(3292, 28, 40, '«فأخذناه وجنوده فنبذناهم» طرحناهم «في اليم» البحر المالح فغرقوا «فانظر كيف كان عاقبة الظالمين» حين صاروا إلى الهلاك.'),
(3293, 28, 41, '«وجعلناهم» في الدنيا «أئمة» بتحقيق الهمزتين وإبدال الثانية ياء رؤساء في الشرك «يدعون إلى النار» بدعائهم إلى الشرك «ويوم القيامة لا يُنصرون» بدفع العذاب عنهم.'),
(3294, 28, 42, '«وأتبعناهم في هذه الدنيا لعنة» خزياً «ويوم القيامة هم من المقبوحين» المبعدين.'),
(3295, 28, 43, '«ولقد آتينا موسى الكتاب» التوراة «من بعد ما أهلكنا القرون الأولى» قوم نوح وعاد وثمود وغيرهم «بصائر للناس» حال من الكتاب جمع بصيرة وهي نور القلب أي أنوارا للقلوب «وهدى» من الضلالة لمن عمل به «ورحمةً» لمن آمن به «لعلهم يتذكرون» يتعظون بما فيه من المواعظ.'),
(3296, 28, 44, '«وما كنت» يا محمد «بجانب» الجبل أو الوادي أو المكان «الغربي» من موسى حين المناجاة «إذ قضينا» أوحينا «إلى موسى الأمر» بالرسالة إلى فرعون وقومه «وما كنت من الشاهدين» لذلك فتعلمه فتخبر به.'),
(3297, 28, 45, '«ولكنا أنشأنا قرونا» أمما من بعد موسى «فتطاول عليهم العمر» طالت أعمارهم فنسوا العهود واندرست العلوم وانقطع الوحي فجئنا بك رسولا وأوحينا إليك خبر موسى وغيره «وما كنت ثاويا» مقيما «في أهل مدين تتلو عليهم آياتنا» خبر ثان فتعرف قصتهم فتخبر بها «ولكنا كنا مرسلين» لك وإليك بأخبار المتقدمين.'),
(3298, 28, 46, '«وما كنت بجانب الطور» الجبل «إذ» حين «نادينا» موسى أن خذ الكتاب بقوة «ولكن» أرسلناك «رحمة من ربك لتنذر قوماً ما أتاهم من نذير من قبلك» وهم أهل مكة «لعلهم يتذكرون» يتعظون.'),
(3299, 28, 47, '«ولولا أن تصيبهم مصيبة» عقوبة «بما قدمت أيديهم» من الكفر وغيره «فيقولوا ربنا لولا» هلا «أرسلت إلينا رسولا فنتبع آياتك» المرسل بها «ونكون من المؤمنين» وجواب لولا محذوف وما بعده مبتدأ، والمعنى لولا الإصابة المسبب عنها قولهم أو لولا قولهم المسبب عنها لعاجلناهم بالعقوبة ولما أرسلناك إليهم رسولا.'),
(3300, 28, 48, '«فلما جاءهم الحق» محمد «من عندنا قالوا لولا» هلا «أوتي مثل ما أوتي موسى» من الآيات كاليد البيضاء والعصا وغيرهما أو الكتاب جملة واحدة قال تعالى «أوَ لم يكفروا بما أوتيَ موسى من قبل» حيث «قالوا» فيه وفي محمد «ساحران» وفي قراءة سحران أي القرآن والتوراة «تظاهراً» تعاوناً «وقالوا إنا بكل» من النبيين والكتابين «كافرون».'),
(3301, 28, 49, '«قل» لهم «فأتوا بكتاب من عند الله هو أهدى منهما» من الكتابين «أتَّبعه إن كنتم صادقين» في قولكم.'),
(3302, 28, 50, '«فإن لم يستجيبوا لك» دعاءك بالإتيان بكتاب «فاعلم أنما يتبعون أهواءهم» في كفرهم «ومن أضل ممن اتبع هواه بغير هدىً من الله» أي لا أضل منه «إن الله لا يهدي القوم الظالمين» الكافرين.'),
(3303, 28, 51, '«ولقد وصَّلنا» بينا «لهم القول» القرآن «لعلهم يتذكرون» يتعظون فيؤمنون.'),
(3304, 28, 52, '«الذين آتيناهم الكتاب من قبله» أي القرآن «هم به يؤمنون» أيضا نزلت في جماعة أسلموا من اليهود كعبد الله بن سلام وغيره ومن النصارى قدموا من الحبشة ومن الشام.'),
(3305, 28, 53, '«وإذا يتلى عليهم» القرآن «قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين» موحدين.'),
(3306, 28, 54, '«أولئك يؤتون أجرهم مرتين» بإيمانهم بالكتابين «بما صبروا» بصبرهم على العمل بهما «ويدرؤون» يدفعون «بالحسنة السيئة» منهم «ومما رزقناهم ينفقون» يتصدقون.'),
(3307, 28, 55, '«وإذا سمعوا اللغو» الشتم والأذى من الكفار «أعرضوا عنه وقالوا لنا أعمالنا ولكم أعمالكم سلام عليكم» سلام متاركة أي سلمتم منا من الشتم وغيره «لا نبتغي الجاهلين» لا نصحبهم.'),
(3308, 28, 56, 'ونزل في حرصه صلى الله عليه وسلم على إيمان عمه أبي طالب «إنك لا تهدي من أحببت» هدايته «ولكن الله يهدي من يشاء وهو أعلم» عالم «بالمهتدين».'),
(3309, 28, 57, '«وقالوا» قومه «إن نتبع الهدى معك نتخطف من أرضنا» ننتزع منها بسرعة قال تعالى «أَو لمْ نمكن لهم حرما آمنا» يأمنون فيه من الإغارة والقتل الواقعين من بعض العرب على بعض «تجبى» بالفوقانية والتحتانية «إليه ثمرات كل شيء» من كل أوب «رزقا» لهم «من لدنا» عندنا «ولكن أكثرهم لا يعلمون» أن ما نقوله حق.'),
(3310, 28, 58, '«وكم أهلكنا من قرية بطرت معيشتها» عيشها وأريد بالقرية أهلها «فتلك مساكنهم لم تسكن من بعدهم إلا قليلا» للمارة يوماً أو بعضه «وكنا نحن الوارثين» منهم.'),
(3311, 28, 59, '«وما كان ربك مهلك القرى» بظلم منها «حتى يبعث في أمها» أي أعظمها «رسولا يتلوا عليهم آياتنا وما كنا مهلكي القرى إلا وأهلها ظالمون» بتكذيب الرسل.'),
(3312, 28, 60, '«وما أوتيتم من شيء فمتاع الحياة الدنيا وزينتها» تتمتعون وتتزينون به أيام حياتكم ثم يفنى «وما عند الله» أي ثوابه «خير وأبقى أفلا تعقلون» بالتاء والياء أن الباقي خير من الفاني.'),
(3313, 28, 61, '«أَفمن وعدناه وعداً حسناً فهو لاقيه» وهو مصيبه وهو الجنة «كمن متعناه متاع الحياة الدنيا» فيزول عن قريب «ثم هو يوم القيامة من المحضرين» النار. الأول المؤمن، والثاني الكافر، أي لا تساوي بينهما.'),
(3314, 28, 62, '«و» اذكر «يوم يناديهم» الله «فيقول أين شركائيَ الذين كنتم تزعمونـ» ـهم شركائي.'),
(3315, 28, 63, '«قال الذين حق عليهم القول» بدخول النار وهم رؤساء الضلالة «ربنا هؤلاء الذين أغوينا» هم مبتدأ وصفة «أغويناهم» خبره فغووا «كما غوينا» لم نكرههم على الغيّ «تبرأنا إليك» منهم «ما كانوا إيانا يعبدون» ما نافية وقدم المفعول للفاصلة.'),
(3316, 28, 64, '«وقيل ادعوا شركاءكم» أي الأصنام الذين تزعمون أنهم شركاء الله «فدعوْهم فلم يستجيبوا لهم» دعاءهم «ورأوا العذاب» أبصروه «لو أنهم كانوا يهتدون» في الدنيا لما رأوه في الآخرة.'),
(3317, 28, 65, '«و» اذكر «يوم يناديهم فيقول ماذا أجبتم المرسلين» إليكم.'),
(3318, 28, 66, '«فعميت عليهم الأنباء» الأخبار المنجية في الجواب «يومئذ» لم يجدوا خبراً لهم فيه نجاة «فهم لا يتساءلون» عنه فيسكتون.'),
(3319, 28, 67, '«فأما من تاب» من الشرك «وآمن» صدق بتوحيد الله «وعمل صالحاً» أدى الفرائض «فعسى أن يكون من المفلحين» الناجين بوعد الله.'),
(3320, 28, 68, '«وربك يخلق ما يشاء ويختار» ما يشاء «ما كان لهم» للمشركين «الخيرة» الاختيار في شيء «سبحان الله وتعالى عما يشركون» عن إشراكهم.'),
(3321, 28, 69, '«وربك يعلم ما تكن صدورهم» تُسِرُّ قلوبهم من الكفر وغيره. «وما يعلنون» بألسنتهم من ذلك.'),
(3322, 28, 70, '«وهو الله لا إله إلا هو له الحمد في الأولى» الدنيا «والآخرة» الجنة «وله الحكم» القضاء النافذ في كل شيء «وإليه ترجعون» بالنشور.'),
(3323, 28, 71, '«قل» لأهل مكة «أرأيتم» أي أخبروني «إن جعل الله عليكم الليل سرمداً» دائماً «إلى يوم القيامة من إله غير الله» بزعمكم «يأتيكم بضياءٍ» نهار تطلبون فيه المعيشة «أفلا تسمعون» ذلك سماع تفهم فترجعون عن الإشراك.'),
(3324, 28, 72, '«قل» لهم «أرأيتم إن جعل الله عليكم النهار سرمداً إلى يوم القيامة من إله غير الله» بزعمكم «يأتيكم بليل تسكنون» تستريحون «فيه» من التعب «أفلا تبصرون» ما أنتم عليه من الخطأ في الإشراك فترجعون عنه.'),
(3325, 28, 73, '«ومن رحمته» تعالى «جعل لكم الليل والنهار لتسكنوا فيه» في الليل «ولتبتغوا من فضله» في النهار للكسب «ولعلكم تشكرون» النعمة فيهما.'),
(3326, 28, 74, '«و» اذكر «يوم يناديهم فيقول أين شركائيَ الذين كنتم تزعمون» ذكر ثانياً ليبنى عليه.'),
(3327, 28, 75, '«ونزعنا» أخرجنا «من كل أمة شهيداً» وهو نبيهم يشهد عليهم بما قالوا «فقلنا» لهم «هاتوا برهانكم» على ما قلتم من الإشراك «فعلموا أن الحق» في الإلهية «لله» لا يشاركه فيه أحد «وضل» غاب «عنهم ما كانوا يفترون» في الدنيا من أن معه شريكاً تعالى عن ذلك.'),
(3328, 28, 76, '«إن قارون كان من قوم موسى» ابن عمه وابن خالته وآمن به «فبغى عليهم» بالكبر والعلو وكثرة المال «وآتيناه من الكنوز ما إن مفاتحه لتنوء» تثقل «بالعصبة» الجماعة «أولي» أصحاب «القوة» أي تثقلهم فالباء للتعدية وعدتهم قيل سبعون وقيل أربعون وقيل عشرة وقيل غير ذلك، اذكر «إذ قال له قومه» المؤمنون من بني إسرائيل «لا تفرح» بكثرة المال فرح بطر «إن الله لا يحب الفرحين» بذلك.'),
(3329, 28, 77, '«وابتغ» اطلب «فيما آتاك الله» من المال «الدار الآخرة» بأن تنفقه في طاعة الله «ولا تنس» تترك «نصيبك من الدنيا» أي أن تعمل فيها للآخرة «وأحسن» للناس بالصدقة «كما أحسن الله إليك ولا تبغ» تطلب «الفساد في الأرض» بعمل المعاصي «إن الله لا يحب المفسدين» بمعنى أنه يعاقبهم.'),
(3330, 28, 78, '«قال إنما أوتيته» أي المال «على علم عندي» أي في مقابلته وكان أعلم بني إسرائيل بالتوراة بعد موسى وهارون قال تعالى «أوَلم يعلم أن الله قد أهلك من قبله من القرون» الأمم «من هو أشد منه قوةً وأكثر جمعا» للمال: أي هو عالم بذلك ويهلكهم الله «ولا يُسأل عن ذنوبهم المجرمون» لعلمه تعالى بها فيدخلون النار بلا حساب.'),
(3331, 28, 79, '«فخرج» قارون «على قومه في زينته» بأتباعه الكثيرين ركبانا متحلين بملابس الذهب والحرير على خيول وبغال متحلية «قال الذين يريدون الحياة الدنيا يا» للتنبيه «ليت لنا مثل ما أوتي قارون» في الدنيا «إنه لذو حظ» نصيب «عظيم» واف فيها.'),
(3332, 28, 80, '«وقال» لهم «الذين أوتوا العلم» بما وعد الله في الآخرة «ويلكم» كلمة زجر «ثواب الله» في الآخرة بالجنة «خير لمن آمن وعمل صالحاً» مما أوتي قارون في الدنيا «ولا يلقاها» أي الجنة المثاب بها «إلا الصابرون» على الطاعة وعن المعصية.'),
(3333, 28, 81, '«فخسفنا به» بقارون «وبداره الأرض فما كان له من فئة ينصرونه من دون الله» أي غيره بأن يمنعوا عنه الهلاك «وما كان من المنتصرين» منه.'),
(3334, 28, 82, '«وأصبح الذين تمنوا مكانه بالأمس» أي من قريب «يقولون ويكأن الله يبسط» يوسع «الرزق لمن يشاء من عباده ويقدر» يضيق على ما يشاء و "وي" اسم فعل بمعنى: أعجب، أي أنا والكاف بمعنى اللام «لولا أن من الله علينا لخسف بنا» بالبناء للفاعل والمفعول «ويكأنه لا يفلح الكافرون» لنعمة الله كقارون.'),
(3335, 28, 83, '«تلك الدار الآخرة» أي الجنة «نجعلها للذين لا يريدون علوّا في الأرض» بالبغي «ولا فسادا» بعمل المعاصي «والعاقبة» المحمودة «للمتقين» عقاب الله، بعمل الطاعات.'),
(3336, 28, 84, '«من جاء بالحسنة فله خير منها» ثواب بسببها وهو عشر أمثالها «ومن جاء بالسيئة فلا يجزى الذين عملوا السيئات إلا» جزاء «ما كانوا يعملون» أي: مثله.'),
(3337, 28, 85, '«إن الذي فرض عليك القرآن» أنزله «لرادّك إلى معاد» إلى مكة وكان قد اشتاقها «قل ربي أعلم من جاء بالهدى، ومن هو في ضلال مبين» نزل جواباً لقول كفار مكة له: إنك في ضلال، أي فهو الجائي بالهدى، وهم في ضلال وأعلم بمعنى عالم.'),
(3338, 28, 86, '«وما كنت ترجو أن يلقى إليك الكتاب» القرآن «إلا» لكن ألقي إليك «رحمة من ربِّك فلا تكوننَّ ظهيراً» معيناً «للكافرين» على دينهم الذي دعوك إليه.'),
(3339, 28, 87, '«ولا يصدنَّك» أصله يصدوننك حذفت نون الرفع للجازم، وواو للفاعل لالتقائها مع النون الساكنة «عن آيات الله بعد إذ أنزلت إليك» أي لا ترجع إليهم في ذلك «وادع» الناس «إلى ربك» بتوحيده وعبادته «ولا تكونن من المشركين» بإعانتهم ولم يؤثر الجازم في الفعل لبنائه.'),
(3340, 28, 88, '«ولا تدعُ» تعبد «مع الله إلها آخر لا إله إلا هو كل شيءٍ هالك إلا وجهه» إلا إياه «له الحكم» القضاء النافذ «وإليه ترجعون» بالنشور من قبوركم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, '«الم» الله أعلم بمراده بذلك.'),
(3342, 29, 2, '«أحسب الناس أن يتركوا أن يقولوا» أي: بقولهم «آمنا وهم لا يفتنون» يختبرون بما يتبين به حقيقة إيمانهم، نزل في جماعة آمنوا فآذاهم المشركون.'),
(3343, 29, 3, '«ولقد فتنَّا الذين من قبلهم فليعلمن الله الذين صدقوا» في إيمانهم علم مشاهدة «وليعلمنَّ الكاذبين» فيه.'),
(3344, 29, 4, '«أم حسب الذين يعملون السيئات» الشرك والمعاصي «أن يسبقونا» يفوتونا فلا ننتقم منهم «ساء» بئس «ما» الذي «يحكمونـ» ـه حكمهم هذا.'),
(3345, 29, 5, '«من كان يرجو» يخاف «لقاء الله فإن أجل الله» به «لآتٍ» فليستعد له «وهو السميع» لأقوال العباد «العليم» بأفعالهم.'),
(3346, 29, 6, '«ومن جاهد» جهاد حرب أو نفس «فإنما يجاهد لنفسه» فإن منفعة جهاده له لا لله «إن الله لغني عن العالمين» الإنس والجن والملائكة وعن عبادتهم.'),
(3347, 29, 7, '«والذين آمنوا وعملوا الصالحات لنكفرن عنهم سيئاتهم» بعمل الصالحات «ولنجزينهم أحسن» بمعنى: حسن ونصبه بنزع الخافض الباء «الذي كانوا يعملون» وهو الصالحات.'),
(3348, 29, 8, '«ووصينا الإنسان بوالديه حسناً» أي إيصاء ذات حسن بأن يبرهما «وإن جاهداك لتشرك بي ما ليس لك به» بإشراكه «علم» موافقة للواقع فلا مفهوم له «فلا تطعهما» في الإشراك «إليَّ مرجعكم فأنبئكم بما كنتم تعملون» فأجازيكم به.'),
(3349, 29, 9, '«والذين آمنوا وعملوا الصالحات لندخلنهم في الصالحين» الأنبياء والأولياء بأن نحشرهم معهم.'),
(3350, 29, 10, '«ومن الناس من يقول آمنا بالله فإذا أوذي في الله جعل فتنة الناس» أي أذاهم له «كعذاب الله» في الخوف منه فيطيعهم فينافق «ولئن» لام قسم «جاء نصرٌ» للمؤمنين «من ربك» فغنموا «ليقولنَّ» حذفت منه نون الرفع لتوالي النونات والواو ضمير الجمع لالتقاء الساكنين «إنا كنا معكم» في الإيمان فأشركونا في الغنيمة قال تعالى: «أوَ ليس الله بأعلم» أي بعالم «بما في صدور العالمين» بقلوبهم من الإيمان والنفاق؟ بلى.'),
(3351, 29, 11, '«وليعلمنَّ الله الذين آمنوا» بقلوبهم «وليعلمنَّ المنافقين» فيجازي الفريقين واللام في الفعلين لام قسم.'),
(3352, 29, 12, '«وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا» ديننا «ولنحمل خطاياكم» في اتباعنا إن كانت والأمر بمعنى الخبر، قال تعالى: «وما هم بحاملين من خطاياهم من شيءٍ إنهم لكاذبون» في ذلك.'),
(3353, 29, 13, '(وليحملن أثقالهم) أوزارهم (وأثقالاً مع أثقالهم) بقولهم للمؤمنين "اتبعوا سبيلنا" وإضلالهم مقلديهم (وليسألن يوم القيامة عما كانوا يفترون) يكذبون على الله سؤال توبيخ واللام في الفعلين لام قسم، وحذف فاعلهما الواو ونون الرفع.'),
(3354, 29, 14, '«ولقد أرسلنا نوحاً إلى قومه» وعمره أربعون سنة أو أكثر «فلبس فيهم ألف سنة إلا خمسين عاماً» يدعوهم إلى توحيد الله فكذبوه «فأخذهم الطوفان» أي الماء الكثير طاف بهم وعلاهم فغرقوا «وهم ظالمون» مشركون.'),
(3355, 29, 15, '«فأنجيناه» أي نوحا «وأصحاب السفينة» الذين كانوا معه فيها «وجعلناها آية» عبرة «للعالمين» لمن بعدهم من الناس إن عصوا رسلهم وعاش نوح بعد الطوفان ستين سنة أو أكثر حتى كثر الناس.'),
(3356, 29, 16, '«و» اذكر «إبراهيم إذ قال لقومه اعبدوا الله واتقوه» خافوا عقابه «ذلكم خير لكم» مما أنتم عليه من عبادة الأصنام «إن كنتم تعلمون» الخير من غيره.'),
(3357, 29, 17, '«إنما تعبدون من دون الله» أي غيره «أوثاناً وتخلقون إفكا» تقولون كذباً إن الأوثان شركاء الله «إن الذين تعبدون من دون الله لا يملكون لكم رزقا» لا يقدرون أن يرزقوكم «فابتغوا عند الله الرزق» اطلبوه منه «واعبدوه واشكروا له إليه ترجعون».'),
(3358, 29, 18, '«إنما تعبدون من دون الله» أي غيره «أوثانا وتخلقون إفكا» تقولون كذبا إن الأوثان شركاء الله «إن الذين تعبدون من دون الله لا يملكون لكم رزقا» لا يقدرون أن يرزقوكم «فابتغوا عند الله الرزق» اطلبوه منه «واعبدوه واشكروا له إليه ترجعون».'),
(3359, 29, 19, '«أَو لم يروْا» بالياء والتاء ينظروا «كيف يُبدئ الله الخلق» هو بضم أوله وقرأ بفتحة من بدأ وأبدأ بمعنى أي يخلقهم ابتداءً «ثم» هو «يعيده» أي الخلق كما بدأه «إن ذلك» المذكور من الخلق الأول والثاني «على الله يسير» فكيف ينكرون الثاني.'),
(3360, 29, 20, '«قل سيروا في الأرض فانظروا كيف بدأ الخلق» لمن كان قبلكم وأماتهم «ثم الله ينشئ النَّشأةَ الآخرة» مداً وقصراً مع سكون الشين «إن الله على كل شيءٍ قدير» ومنه البدء والإعادة.'),
(3361, 29, 21, '«يعذِّب من يشاء» تعذيبه «ويرحم من يشاء» رحمته «وإليه تقبلون» تردون.'),
(3362, 29, 22, '«وما أنتم بمعجزين» ربكم عن إدراككم «في الأرض ولا في السماء» لو كنتم فيها: أي لا تفوتونه «وما لكم من دون الله» أي غيره «من ولي» يمنعكم منه «ولا نصير» ينصركم من عذابه.'),
(3363, 29, 23, '«والذين كفروا بآيات الله ولقائه» أي القرآن والبعث «أولئك يئسوا من رحمتي» أي جنتي «وأولئك لهم عذاب أليم» مؤلم.'),
(3364, 29, 24, 'قال تعالى في قصة إبراهيم عليه السلام: «فما كان جواب قومه إلا أن قالوا اقتلوه أو حرّقوه فأنجاه الله من النار» التي قذفوه فيها بأن جعلها برداً وسلاماً «إن في ذلك» أي إنجائه منها «لآيات» هي عدم تأثيرها فيه مع عظمها وإخمادها وإنشاء روض مكانها في زمن يسير «لقوم يؤمنون» يصدقون بتوحيد الله وقدرته لأنهم المنتفعون بها.'),
(3365, 29, 25, '«وقال» إبراهيم «إنما اتخذتم من دون الله أوثانا» تعبدونها وما مصدرية «مودةُ بينكم» خبر إن، وعلى قراءة النصب مفعول له وما كافة المعنى تواددتم على عبادتها «في الحياة الدنيا ثم يوم القيامة يكفر بعضكم ببعض» يتبرأ القادة من الأتباع «ويلعن بعضكم بعضاً» يلعن الأتباع القادة «ومأواكم» مصيركم جميعاً «النار وما لكم من ناصرين» مانعين منها.'),
(3366, 29, 26, '«فآمن له» صدق إبراهيم «لوط» وهو ابن أخيه هاران «وقال» إبراهيم «إني مهاجر» من قومي «إلى ربي» إلى حيث أمرني ربي وهجر قومه وهاجر من سواد العراق إلى الشام «إنه هو العزيز» في ملكه «الحكيم» في صنعه.'),
(3367, 29, 27, '«ووهبنا له» بعد إسماعيل «إسحاق ويعقوب» بعد إسحاق «وجعلنا في ذريته النبوة» فكل الأنبياء بعد إبراهيم من ذريته «والكتاب» بمعنى الكتب أي التوراة والإنجيل، والزبور والفرقان «وآتيناه أجره في الدنيا» وهو الثناء الحسن في كل أهل الأديان «وإنه في الآخرة لمن الصالحين» الذين لهم الدرجات العلى.'),
(3368, 29, 28, '«و» اذكر «لوطاً إذ قال لقومه أإنكم» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين في الموضعين «لتأتون الفاحشة» أي: أدبار الرجال «ما سبقكم بها من أحد من العالمين» الإنس والجن.'),
(3369, 29, 29, '«أإنكم لتأتون الرجال وتقطعون السبيل» طريق المارة بفعلكم الفاحشة بمن يمر بكم فترك الناس الممر بكم «وتأتون في ناديكم» أي متحدَّثِكم «المنكر» فعل الفاحشة بعضكم ببعض «فما كان جواب قومه إلا أن قالوا ائتنا بعذاب الله إن كنت من الصادقين» في استقباح ذلك وأن العذاب نازل بفاعليه.'),
(3370, 29, 30, '«قال رب انصرني» بتحقيق قولي في إنزال العذاب «على القوم المفسدين» العاصين بإتيان الرجال فاستجاب الله دعاءه.'),
(3371, 29, 31, '«ولما جاءت رسلنا إبراهيم بالبشرى» بإسحاق ويعقوب بعده «قالوا إنا مهلكوا أهل هذه القرية» أي قرية لوط «إن أهلها كانوا ظالمين» كافرين.'),
(3372, 29, 32, '«قال» إبراهيم «إن فيها لوطا قالوا» أي الرسل «نحن أعلم بمن فيها لنُنجينَّه» بالتخفيف والتشديد «وأهله إلا امرأته كانت من الغابرين» الباقين في العذاب.'),
(3373, 29, 33, '«ولما أن جاءت رسلنا لوطا سيء بهم» حزن بسببهم «وضاق بهم ذرعا» صدرا لأنهم حسان الوجوه في صورة أضياف فخاف عليهم قومه فأعلموه أنهم رسل ربه «وقالوا لا تخف ولا تحزن إنا منجُّوك» بالتشديد والتخفيف «وأهلك إلا امرأتك كانت من الغابرين» ونصب أهلك عطف على محل الكاف.'),
(3374, 29, 34, '«إنا منزلون» بالتخفيف والتشديد «على أهل هذه القرية رجزا» عذابا «من السماء بما» بالفعل الذي «كانوا يفسقون» به أي بسبب فسقهم.'),
(3375, 29, 35, '«ولقد تركنا منها آية بينة» ظاهرة هي آثار خرابها «لقوم يعقلون» يتدبرون.'),
(3376, 29, 36, '«و» أَرسلنا «إلى مدْين أخاهم شعيبا فقال يا قوم اعبدوا الله وارجوا اليوم الآخر» اخشوه، هو يوم القيامة «ولا تعثوْا في الأرض مفسدين» حال مؤكدة لعاملها من عثي بكسر المثلثة أفسد.'),
(3377, 29, 37, '«فكذبوه فأخذتهم الرجفة» الزلزلة الشديدة «فأصبحوا في دارهم جاثمين» باركين على الركب ميّتين.'),
(3378, 29, 38, '«و» أهلكنا «عادا وثمودا» بالصرف وتركه بمعنى الحي والقبيلة «وقد تبيّن لكم» إهلاكهم «من مساكنهم» بالحجر واليمن «وزيَّن لهم الشيطان أعمالهم» من الكفر والمعاصي «فصدهم عن السبيل» سبيل الحق «وكانوا مستبصرين» ذوي بصائر.'),
(3379, 29, 39, '«و» أهلكنا «قارون وفرعون وهامان ولقد جاءهم» من قبل «موسى بالبيّنات» الحجج الظاهرات «فاستكبروا في الأرض وما كانوا سابقين» فائتين عذابنا.'),
(3380, 29, 40, '«فكلا» من المذكورين «أخذنا بذنبه فمنهم من أرسلنا عليه حاصبا» ريحاً عاصفة فيها حصباء كقوم لوط «ومنهم من أخذته الصيحة» كثمود «ومنهم من خسفنا به الأرض» كقارون «ومنهم من أغرقنا» كقوم نوح وفرعون وقومه «وما كان الله ليظلمهم» فيعذبهم بغير ذنب «ولكن كانوا أنفسهم يظلمون» بارتكاب الذنب.'),
(3381, 29, 41, '«مثل الذين اتخذوا من دون الله أولياء» أي أصناماً يرجون نفعها «كمثل العنكبوت اتخذت بيتاً» لنفسها تأوي إليه «وإن أوهن» أضعف «البيوت لبيت العنكبوت» لا يدفع عنها حراً ولا برداً كذلك الأصنام لا تنفع عابديها «لو كانوا يعلمون» ذلك ما عبدوها.'),
(3382, 29, 42, '«إن الله يعلم ما» بمعنى الذي «يدعون» يعبدون بالياء والتاء «من دونه» غيره «من شيء وهو العزيز» في ملكه «الحكيم» في صنعه.'),
(3383, 29, 43, '«وتلك الأمثال» في القرآن «نضربها» نجعلها «للناس وما يعقلها» أي يفهمها «إلا العالمون» المتدبرون.'),
(3384, 29, 44, '«خلقَ الله السماوات والأرض بالحق» أي محقا «إن في ذلك لآيةً» دالة على قدرته تعالى «للمؤمنين» خصّوا بالذكر لأنهم المنتفعون بها في الإيمان بخلاف الكافرين.'),
(3385, 29, 45, '«اتل ما أوحي إليك من الكتاب» القرآن «وأقم الصلاة إن الصلاة تنهى عن الفحشاء والمنكر» شرعا أي من شأنها ذلك ما دام المرء فيها «ولذكر الله أكبر» من غيره من الطاعات «والله يعلم ما تصنعون» فيجازيكم به.'),
(3386, 29, 46, '«ولا تجادلوا أهل الكتاب إلا بالتي» أي: المجادلة التي «هي أحسن» كالدعاء إلى الله بآياته والتنبيه على حججه «إلا الذين ظلموا منهم» بأن حاربوا وأبوا أن يقرّوا بالجزية فجادلوهم بالسيف حتى يسلموا أو يعطوا الجزية «وقولوا» لمن قبل الإقرار بالجزية إذا أخبروكم بشيء مما في كتبهم «آمنا بالذي أنزل إلينا وأنزل إليكم» ولا تصدقوهم ولا تكذبوهم في ذلك «وإلهنا وإلهكم واحدٌ ونحن مسلمون» مطيعون.'),
(3387, 29, 47, '«وكذلك أنزلنا إليك الكتاب» القرآن كما أنزلنا إليهم التوراة وغيرها «فالذين آتيناهم الكتاب» التوراة كعبد الله بن سلام وغيره «يؤمنون به» بالقرآن «ومن هؤلاء» أهل مكة «من يؤمن به وما يجحد بآياتنا» بعد ظهورها «إلا الكافرون» أي اليهود وظهر لهم أن القرآن حق والجائي به محق وجحدوا ذلك.'),
(3388, 29, 48, '«وما كنت تتلوا من قبله» أي القرآن «من كتاب ولا تخطه بيمينك إذا» أي لو كنت قارئا كتابا «لارتاب» شك «المبطلون» اليهود فيك وقالوا الذي في التوراة أنه أمي لا يقرأ ولا يكتب.'),
(3389, 29, 49, '«بل هو» أي القرآن الذي جئت به «آيات بينات في صدور الذين أوتوا العلم» أي المؤمنون يحفظونه «وما يجحد بآياتنا إلا الظالمون» أي: اليهود وجحدوها بعد ظهورها لهم.'),
(3390, 29, 50, '«وقالوا» أي كفار مكة «لولا» هلا «أنزل عليه» أي محمد «آية من ربه» وفي قراءة آيات كناقة صالح وعصا موسى ومائدة عيسى «قل» لهم «إنما الآيات عند الله» ينزلها كيف يشاء «وإنما أنا نذير مبين» مظهر إنذاري بالنار أهل المعصية.'),
(3391, 29, 51, '«أو لم يكفهم» فيما طلبوا «أنَّا أنزلنا عليك الكتاب» القرآن «يتلى عليهم» فهو آية مستمرة لا انقضاء لها بخلاف ما ذكر من الآيات «إن في ذلك» الكتاب «لرحمةً وذكرى» عظة «لقوم يؤمنون».'),
(3392, 29, 52, '«قل كفى بالله بيني وبينكم شهيدا» بصدقي «يعلم ما في السماوات والأرض» ومنه حالي وحالكم «والذين آمنوا بالباطل» وهو ما يعبد من دون الله «وكفروا بالله» منكم «أولئك هم الخاسرون» في صفقتهم حيث اشتروا الكفر بالإيمان.'),
(3393, 29, 53, '«ويستعجلونك بالعذاب ولولا أجل مسمى» له «لجاءَهم العذاب» عاجلاً «ولياتينَّهم بغتةً وهم لا يشعرون» بوقت إتيانه.'),
(3394, 29, 54, '«يستعجلونك بالعذاب» في الدنيا «وإن جهنم لمحيطه بالكافرين».'),
(3395, 29, 55, '«يوم يغشاهم العذاب من فوقهم ومن تحت أرجلهم ونقول» فيه بالنون أي نأمر بالقول، وبالياء يقول أي الموكل بالعذاب «ذوقوا ما كنتم تعلمون» أي جزاءه فلا تفوتوننا.'),
(3396, 29, 56, '«يا عباديَ الذين آمنوا إنَّ أرضي واسعة فإياي فاعبدون» في أي أرض تيسَّرت فيها العبادة، بأن تهاجروا إليها من أرض لم تتيسر فيها نزل في ضعفاء مسلمي مكة كانوا في ضيق من إظهار الإسلام بها.'),
(3397, 29, 57, '«كلُّ نفسٍ ذائقة الموت ثم إلينا ترجعون» بالتاء والياء بعد البعث.'),
(3398, 29, 58, '«والذين آمنوا وعملوا الصالحات لنُبوئنَّهم» ننزلنهم، وفي قراءة بالمثلثة بعد النون من الثواء: الإقامة وتعديته إلى غرفا بحذف في «من الجنة غرفا تجري من تحتها الأنهار خالدين» مقدّرين الخلود «فيها نِعم أجر العاملين» هذا الأجر.'),
(3399, 29, 59, 'هم «الذين صبروا» أي على أذى المشركين والهجرة لإظهار الدين «وعلى ربهم يتوكلون» فيرزقهم من حيث لا يحتسبون.'),
(3400, 29, 60, '«وكأين» كم «من دابة لا تحمل رزقها» لضعفها «الله يرزقها وإياكم» أيها المهاجرون وإن لم يكن معكم زاد ولا نفقة «وهو السميع» لأقوالكم «العليم» بضمائركم.'),
(3401, 29, 61, '«ولئن» لام قسم «سألتهم» أي: الكفار «من خلق السماوات والأرض وسخَّر الشمس والقمر ليقولُنَّ الله فأنّى يؤفكون» يصرفون عن توحيده بعد إقرارهم بذلك.'),
(3402, 29, 62, '«الله يبسط الرزق» يوسعه «لمن يشاء من عباده» امتحانا «ويقدر» يضيق «له» بعد البسط أي لمن يشاء ابتلاءه «إن الله بكل شيء عليم» ومنه محل البسط والتضييق.'),
(3403, 29, 63, '«ولئن» لام قسم «سألتهم من نزَّل من السماء ماءً فأحيا به الأرض من بعد موتها ليقولنَّ الله» فكيف يشركون به «قل» لهم «الحمد لله» على ثبوت الحجة عليكم «بل أكثرهم لا يعقلون» تناقضهم في ذلك.'),
(3404, 29, 64, '«وما هذه الحياة الدنيا إلا لهوٌ ولعب» وأما القرَب فمن أمور الآخرة لظهور ثمرتها فيها «وإن الدار الآخرة لهي الحيوان» بمعنى الحياة «لو كانوا يعلمون» ذلك ما آثروا الدنيا عليها.'),
(3405, 29, 65, '«فإذا ركبوا في الفلك دعوا الله مخلصين له الدين» أي الدعاء، أي: لا يدعون معه غيره لأنهم في شدة لا يكشفها إلا هو «فلما نجّاهم إلى البر إذا هم يشركون» به.'),
(3406, 29, 66, '«ليكفروا بما آتيناهم» من النعمة «وليتمتعوا» باجتماعهم على عبادة الأصنام، وفي قراءة بسكون اللام أمر تهديد «فسوف يعلمون» عاقبة ذلك.'),
(3407, 29, 67, '«أَو لم يروْا» يعلموا «أنَّا جعلنا» بلدهم مكة «حرماً آمناً ويتخطف الناس من حولهم» قتلاً وسبياً دونهم «أفبالباطل» الصنم «يؤمنون وبنعمة الله يكفرون» بإشراكهم.'),
(3408, 29, 68, '«ومن» أي لا أحد «أظلم ممن افترى على الله كذبا» بأن أشرك به «أو كذب بالحق» النبي أو الكتاب «لما جاءَه أَليس في جهنم مثوىّ» مأوى «للكافرين» أي فيها ذلك وهو منهم.'),
(3409, 29, 69, '«والذين جاهدوا فينا» في حقنا «لنهدينَّهم سُبُلنا» أي طريق السير إلينا «وإن الله لمع المحسنين» المؤمنين بالنصر والعون.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, '«الم» الله أعلم بمراده في ذلك.'),
(3411, 30, 2, '«غُلبت الروم» وهم أهل الكتاب غلبتها فارس وليسوا أهل كتاب بل يعبدون الأوثان ففرح كفار مكة بذلك، وقالوا للمسلمين: نحن نغلبكم كما غلبت فارس الروم.'),
(3412, 30, 3, '«في أدنى الأرض» أي أقرب أرض الروم إلى فارس بالجزيرة التقى فيها الجيشان والبادي بالغزو الفرس «وهم» أي الروم «من بعد غلبهم» أضيف المصدر إلى المفعول: أي غلبة فارس إياهم «سيغلبون» فارس.'),
(3413, 30, 4, '«في بضع سنين» هو ما بين الثلاث إلى التسع أو العشر، فالتقي الجيشان في السنة السابعة من الالتقاء الأول وغلبت الروم فارس «لله الأمر من قبل ومن بعد» أي من قبل غلب الروم ومن بعده المعنى أن غلبة فارس أولا وغلبة الروم ثانيا بأمر الله: أي إرادته «ويومئذٍ» أي يوم تغلب الروم «يفرح المؤمنون».'),
(3414, 30, 5, '«بنصر الله» إياهم على فارس وقد فرحوا بذلك وعلموا به يوم وقوعه أي يوم بدر بنزول جبريل بذلك مع فرحهم بنصرهم على قتل المشركين فيه «ينصر من يشاء وهو العزيز» الغالب «الرحيم» بالمؤمنين».'),
(3415, 30, 6, '«وعد الله» مصدر بدل من اللفظ بفعله، والأصل وعدهم الله النصر «لا يخلف الله وعده» به «ولكن أكثر الناس» أي كفار مكة «لا يعلمون» وعده تعالى بنصرهم.'),
(3416, 30, 7, '«يعلمون ظاهراً من الحياة الدنيا» أي معايشها من التجارة والزراعة والبناء والغرس وغير ذلك «وهم عن الآخرة هم غافلون» إعادة هم تأكيد.'),
(3417, 30, 8, '«أَو لم يتفكروا في أنفسهم» ليرجعوا عن غفلتهم «ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجلٍ مسمى» لذلك تفنى عند انتهائه وبعد البعث «وإن كثيرا من الناس» أي: كفار مكة «بلقاء ربهم لكافرون» أي لا يؤمنون بالبعث بعد الموت.'),
(3418, 30, 9, '«أوَ لم يسيروا في الأرض فينظروا كيف كانَ عاقبة الذين من قبلهم» من الأمم وهي إهلاكهم بتكذيبهم رسلهم «كانوا أشد منهم قوة» كعاد وثمود «وأثاروا الأرض» حرثوها وقلبوها للزرع والغرس «وعمَروها أكثر ممّا عمروها» أي كفار مكة «وجاءَتهم رسلهم بالبينات» بالحجج الظاهرات «فما كان الله ليظلمهم» بإهلاكهم بغير جرم «ولكن كانوا أنفسهم يظلمون» بتكذيبهم رسلهم.'),
(3419, 30, 10, 'ثم كان عاقبة الذين أساءُوا السُّوأى» تأنيث الأسوأ: الأقبح خبر كان على رفع عاقبة واسم كان على نصب عاقبة، والمراد بها جهنم وإساءتهم «أن» أي: بأن «كذبوا بآيات الله» القرآن «وكانوا بها يستهزءُون».'),
(3420, 30, 11, '«الله يبدأ الخلق» أي: ينشئ خلق الناس «ثم يعيده» أي خلقهم بعد موتهم «ثم إليه يرجعون» بالياء والتاء.'),
(3421, 30, 12, '«ويوم تقوم الساعة يُبلس المجرمون» يسكت المشركون لانقطاع حجتهم.'),
(3422, 30, 13, '«ولم يكن» أي لا يكون «لهم من شركائهم» ممن أشركوهم بالله وهم الأصنام ليشفعوا لهم «شفعاء وكانوا» أي: يكونون «بشركائهم كافرين» أي: متبرئين منهم.'),
(3423, 30, 14, '«ويوم تقوم الساعة يومئذ» تأكيد «يتفرقون» المؤمنون والكافرون.'),
(3424, 30, 15, '«فأما الذين آمنوا وعملوا الصالحات فهم في روضةٍ» جنة «يحبرون» يسرون.'),
(3425, 30, 16, '«وأما الذين كفروا وكذبوا بآياتنا» القرآن «ولقاء الآخرة» البعث وغيره «فأولئك في العذاب محضرون».'),
(3426, 30, 17, '«فسبحان الله» أي: سبحوا الله بمعنى صلوا «حين تمسون» أي: تدخلون في المساء وفيه صلاتان: المغرب والعشاء «وحين تصبحون» تدخلون في الصباح وفيه صلاة الصبح.'),
(3427, 30, 18, '«وله الحمد في السموات والأرض» اعتراض ومعناه يحمده أهلهما «وعشيا» عطف على حين وفيه صلاة العصر «وحين تظهرون» تدخلون في الظهيرة وفيه صلاة الظهر.'),
(3428, 30, 19, '«يُخرج الحي من الميت» كالإنسان من النطفة والطائر من البيضة «ويخرج الميت» النطفة والبيضة «من الحي ويحيي الأرض» بالنبات «بعد موتها» أي يبسها «وكذلك» الإخراج «تَخرجون» من القبور بالبناء للفاعل والمفعول.'),
(3429, 30, 20, '«ومن آياته» تعالى الدالة على قدرته «أن خلقكم من تراب» أي: أصلكم آدم «ثم إذا أنتم بشر» من دم ولحم «تنتشرون» في الأرض.'),
(3430, 30, 21, '«ومن آياته أن خلق لكم من أنفسكم أزواجا» فخلقت حواء من ضلع آدم وسائر الناس من نطف الرجال والنساء «لتسكنوا إليها» وتألفوها «وجعل بينكم» جميعا «مودةً ورحمة إن في ذَلك» المذكور «لآيات لقوم يتفكرون» في صنع الله تعالى.'),
(3431, 30, 22, '«ومن آياته خلق السماوات والأرض واختلاف ألسنتكم» أي لغاتكم من عربية وعجمية وغيرها «وألوانكم» من بياض وسواد وغيرهما، وأنتم أولاد رجل واحد وامرأة واحدة «إن في ذلك لآيات» دلالات على قدرته تعالى «للعالمين» بفتح اللام وكسرها، أي: ذوي العقول وأولي العلم.'),
(3432, 30, 23, '«ومن آياته منامكم بالليل والنهار» بإرادته راحة لكم «وابتغاؤكم» بالنهار «من فضله» أي: تصرفكم في طلب المعِيشة بإرادته «إن في ذلك لآيات لقوم يسمعون» سماع تدبر واعتبار.'),
(3433, 30, 24, '«ومن آياته يريكم» أي إراءتكم «البرق خوفا» للمسافر من الصواعق «وطمعا» للمقيم في المطر «وينزل من السماء ماءً فيحيي به الأرض بعد موتها» أي: يبسطها بأن تنبت «إن في ذلك» المذكور «لآيات لقوم يعقلون» يتدبرون.'),
(3434, 30, 25, '«ومن آياته أن تقوم السماء والأرض بأمره» بإرادته من غير عمد «ثم إذا دعاكم دعوة من الأرض» بأن ينفخ إسرافيل في الصور للبعث من القبور «إذا أنتم تخرجون» منها أحياء فخروجكم منها بدعوة من آياته تعالى.'),
(3435, 30, 26, '«وله من في السماوات والأرض» ملكا وخلقا وعبيدا «كل له قانتون» مطيعون.'),
(3436, 30, 27, '«وهو الذي يبدأ الخلق» للناس «ثم يعيده» بعد هلاكهم «وهو أهون عليه» من البدء بالنظر إلى ما عند المخاطبين من أن إعادة الشيء أسهل من ابتدائه وإلا فهما عند الله تعالى سواء في السهولة «وله المثل الأعلى في السماوات والأرض» أي: الصفة العليا، وهي أنه لا إله إلا هو «وهو العزيز» في ملكه «الحكيم» في خلقه.'),
(3437, 30, 28, '«ضرب» جعل «لكم» أيها المشركون «مثلا» كائنا «من أنفسكم» وهو «هل لكم من ما ملكت أيمانكم» أي من مماليككم «من شركاء» لكم «فيما رزقناكم» من الأموال وغيرها «فأنتم» وهم «فيه سواءٌ تخافونهم كخيفتكم أنفسكم» أي أمثالكم من الأحرار والاستفهام بمعنى النفي المعنى: ليس مماليككم شركاء لكم إلى آخره عندكم فكيف تجعلون بعض مماليك الله شركاء له «كذلك نفصِّل الآيات» نبنيها مثل ذلك التفصيل «لقومٍ يعقلون» يتدبرون.'),
(3438, 30, 29, '«بل اتبع الذين ظلموا» بالإشراك «أهواءَهم بغير علمٍ فمن يهدي من أضل الله» أي: لا هادي له «وما لهم من ناصرين» مانعين من عذاب الله.'),
(3439, 30, 30, '«فأقم» يا محمد «وجهك للدين حنيفا» مائلا إليه: أي أخلص دينك لله أنت ومن تبعك «فطرتَ الله» خلقته «التي فطر الناس عليها» وهي دينه أي: الزموها «لا تبديل لخلق الله» لدينه أي: لا تبدلوه بأن تشركوا «ذلك الدين القيّم» المستقيم توحيد الله «ولكن أكثر الناس» أي كفار مكة «لا يعلمون» توحيد الله.'),
(3440, 30, 31, '«منيبين» راجعين «إليه» تعالى فيما أمر به ونهى عنه حال من فاعل أقم وما أريد به؛ أي أقيموا «واتقوه» خافوه «وأقيموا الصلاة ولا تكونوا من المشركين».'),
(3441, 30, 32, '«من الذين» بدل بإعادة الجار «فرقوا دينهم» باختلافهم فيما يعبدونه «وكانوا شيعاً» فرقاً في ذلك «كل حزبٍ» منهم «بما لديهم» عندهم «فرحون» مسرورون، وفي قراءة فارقوا: أي تركوا دينهم الذي أمروا به.'),
(3442, 30, 33, '«وإذا مسَّ الناس» أي كفار مكة «ضرٌّ» شدة «دعوا ربهم منيبين» راجعين «إليه» دون غيره «ثم إذا أذاقهم منه رحمة» بالمطر «إذا فريقٌ منهم بربهم يشركون».'),
(3443, 30, 34, '«ليكفروا بما آتيناهم» أريد به التهديد «فتمتعوا فسوف تعلمون» عاقبة تمتعكم، فيه التفات عن الغيبة.'),
(3444, 30, 35, '«أَم» بمعنى همزة الإنكار «أنزلنا عليهم سلطاناً» حجة وكتاباً «فهو يتكلم» تكلم دلالة «بما كانوا به يشركون» أي يأمرهم بالإشراك! لا.'),
(3445, 30, 36, '«وإذا أذقنا الناس» كفار مكة وغيرهم «رحمة» نعمةً «فرحوا بها» فرح بطر «وإن تصبهم سيئة» شدة «بما قدمت أيديهم إذا هم يقنطون» ييأسون من الرحمة ومن شأن المؤمن أن يشكر عند النعمة ويرجو ربه عند الشدة.'),
(3446, 30, 37, '«اوَ لم يروا» يعلموا «أن الله يبسط الرزق» يوسعه «لمن يشاء» امتحانا «ويقدر» يضيقه لمن يشاء ابتلاءً «إن في ذلك لآيات لقوم يؤمنون» بها.'),
(3447, 30, 38, '«فآت ذا القربى» القرابة «حقه» من البر والصلة «والمسكين وابن السبيل» المسافر من الصدقة، وأمة النبي تبع له في ذلك «ذلك خير للذين يريدون وجه الله» أي ثوابه بما يعملون «وأولئك هم المفلحون» الفائزون.'),
(3448, 30, 39, '«وما آتيتم من رِبا» بأن يعطي شيء هبة أو هدية ليطلب أكثر منه، فسمي باسم المطلوب من الزيادة في المعاملة «ليربوَ في أموال الناس» المعطين، أي يزيد «فلا يربو» يزكو «عند الله» لا ثواب فيه للمعطين «وما آتيتم من زكاة» صدقة «تريدون» بها «وجه الله فأولئك هم المضعفون» ثوابهم بما أرادوه، فيه التفات عن الخطاب.'),
(3449, 30, 40, '«الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم هل من شركائكم» ممن أشركتم بالله «من يفعل من ذلكم من شيء» لا «سبحانه وتعالى عما يشركون» به.'),
(3450, 30, 41, '«ظهر الفساد في البر» أي القفار بقحط المطر وقلة النبات «والبحر» أي البلاد التي على الأنهار بقلة مائها «بما كسبت أيدي الناس» من المعاصي «ليذيقهم» بالياء والنون «بعض الذي عملوا» أي عقوبته «لعلهم يرجعون» يتوبون.'),
(3451, 30, 42, '«قل» لكفار مكة «سيروا في الأرض فانظروا كيف كان عاقبة الذين من قبل كان أكثرهم مشركين» فأهلكوا بإشراكهم ومساكنهم ومنازلهم خاوية.'),
(3452, 30, 43, '«فأقم وجهك للدين القيِّم» دين الإسلام «من قبل أن يأتي يوم لا مردَّ له من الله» هو يوم القيامة «يومئذ يصَّدَّعون» فيه إدغام التاء في الأصل في الصاد: يتفرقون بعد الحساب إلى الجنة والنار.'),
(3453, 30, 44, '«من كفر فعليه كفره» وبال كفره وهو النار «ومن عمل صالحاً فلأنفسهم يمهدون» يوطئون منازلهم في الجنة.'),
(3454, 30, 45, '«ليجزي» متعلق بيصدعون «الذين آمنوا وعملوا الصالحات من فضله» يثيبهم «إنه لا يحب الكافرين» أي يعاقبهم.'),
(3455, 30, 46, '«ومن آياته» تعالى «أن يرسل الرياح مبشرات» بمعنى لتبشركم بالمطر «وليذيقكم» بها «من رحمته» المطر والخصب «ولتجري الفلك» السفن بها «بأمره» بإرادته «ولتبتغوا» تطلبوا «من فضله» الرزق بالتجارة في البحر «ولعلكم تشكرون» هذه النعم يا أهل مكة فتوحدوه.'),
(3456, 30, 47, 'ولقد أرسلنا من قبلك رسلاً إلى قومهم فجاؤوهم بالبينات» بالحجج الواضحات على صدقهم في رسالتهم إليهم فكذبوهم «فانتقمنا من الذين أجرموا» أهلكنا الذين كذبوهم «وكان حقاً علينا نصر المؤمنين» على الكافرين بإهلاكهم وإنجاء المؤمنين.'),
(3457, 30, 48, '«الله الذي يرسل الرياح فتثير سحابا» تزعجه «فيبسطه في السماء كيف يشاء» من قلة وكثرة «ويجعله كسفا» بفتح السين وسكونها قطعا متفرقة «فترى الودق» المطر «يخرج من خلاله» أي وسطه «فإذا أصاب به» بالودق «من يشاء من عباده إذا هم يستبشرون» يفرحون بالمطر.'),
(3458, 30, 49, '«وإن» وقد «كانوا من قبل أن يُنزَّل عليهم من قبله» تأكيد «لمبلسين» آيسين من إنزاله.'),
(3459, 30, 50, '«فانظر إلى أثر» وفي قراءة آثار «رحمة الله» أي نعمته بالمطر «كيف يحيي الأرض بعد موتها» أي يبسها بأن تنبت «إن ذلك لمحيي الموتى وهو على كل شيءٍ قدير».'),
(3460, 30, 51, '«ولئن» لام قسم «أرسلنا ريحا» مضرة على نبات «فرأوهُ مصفرا لظلوا» صاروا جواب القسم «من بعده» أي بعد إصفراره «يكفرون» يجحدون النعمة بالمطر.'),
(3461, 30, 52, '«فإنك لا تُسمع الموتى ولا تُسمع الصم الدعاء إذا» بتحقيق الهمزتين وتسهيل الثانية بينها وبين الياء «ولَّوْا مدبرين».'),
(3462, 30, 53, '«وما أنت بهادِ العمي عن ضلالتهم إن» ما «تُسمع» سماع إفهام وقبول «إلا من يؤمن بآياتنا» القرآن «فهم مسلمون» مخلصون بتوحيد الله.'),
(3463, 30, 54, '«الله الذي خلقكم من ضعف» ماء مهين «ثم جعل من بعد ضعف» آخر، وهو ضعف الطفولية «قوةً» أي قوة الشباب «ثم من بعد قوةٍ ضعفاً وشيبةً» ضعف الكبر وشيب الهرم والضعف في الثلاثة بضم أوله وفتحه «يخلق ما يشاء» من الضعف والقوة والشباب والشيبة «وهو العليم» بتدبير خلقه «القدير» على ما يشاء.'),
(3464, 30, 55, '«ويوم تقوم الساعة يُقسم» يحلف «المجرمون» الكافرون «ما لبثوا» في القبور «غير ساعة» قال تعالى: «كذلك كانوا يؤفكون» يصرفون عن الحق: البعث كما صرفوا عن الحق الصدق في مدة اللبث.'),
(3465, 30, 56, '«وقال الذين أوتوا العلم والإيمان» من الملائكة وغيرهم «لقد لبثتم في كتاب الله» فيما كتبه في سابق علمه «إلى يوم البعث فهذا يوم البعث» الذي أنكرتموه «ولكنكم كنتم لا تعلمون» وقوعه.'),
(3466, 30, 57, '«فيومئذٍ لا ينفع» بالياء والتاء «الذين ظلموا معذرتُهم» في إنكارهم له «ولا هم يستعتبون» لا يطلب منهم العتبى: أي الرجوع إلى ما يرضي الله.'),
(3467, 30, 58, '«ولقد ضربنا» جعلنا «للناس في هذا القرآن من كل مثل» تنبيهاً لهم «ولئن» لام قسم «جئتهم» يا محمد «بآية» مثل العصا واليد لموسى «ليقولنّ» حذف منه نون الرفع لتوالي النونات، والواو ضمير الجمع لالتقاء الساكنين «الذين كفروا» منهم «إن» ما «أنتم» أي محمد وأصحابه «إلا مبطلون» أصحاب أباطيل.'),
(3468, 30, 59, '«كذلك يطبع الله على قلوب الذين لا يعلمون» التوحيد كما طبع على قلوب هؤلاء.'),
(3469, 30, 60, '«فاصبر إنَّ وعد الله» بنصرك عليهم «حق ولا يستخفنك الذين لا يوقنون» بالبعث: أي لا يحملنَّك على الخفة والطيش بترك الصبر: أي لا تتركه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, '«الم» الله أعلم بمراده به.'),
(3471, 31, 2, '«تلك» أي هذه الآيات «آيات الكتاب» القرآن «الحكيم» ذي الحكمة والإضافة بمعنى من.'),
(3472, 31, 3, 'هو (هدى ورحمة) بالرفع (للمحسنين) وفي قراءة العامة بالنصب حالاً من الآيات العامل فيها ما في "تلك" من معنى الإشارة.'),
(3473, 31, 4, '«الذين يقيمون الصلاة» بيان للمحسنين «ويؤتون الزكاة وهم بالآخرة هم يوقنون» هم الثاني تأكيد.'),
(3474, 31, 5, '«أولئك على هدى من ربهم وأولئك هم المفلحون» الفائزون.'),
(3475, 31, 6, '«ومن الناس من يشتري لهو الحديث» أي ما يلهي منه عما يعني «ليضلَّ» بفتح الياء وضمها «عن سبيل الله» طريق الإسلام «بغير علمٍ ويتخذها» بالنصب عطفاً على يضل، وبالرفع عطفاً على يشتري «هزؤاً» مهزوءاً بها «أولئك لهم عذاب مهين» ذو إهانة.'),
(3476, 31, 7, '«وإذا تتلى عليه آياتنا» أي القرآن «ولَّى مستكبرا» متكبرا «كأن لم يسمعها كأن في أذنيه وقرا» صمما وجملتا التشبيه حالان من ضمير ولَّى أو الثانية بيان للأولى «فبشِّره» أعلمه «بعذاب أليم» مؤلم وذكر البشارة تهكم به وهو النضر بن الحارث، كان يأتي الحيرة يتجر فيشتري كتب أخبار الأعاجم ويحدث بها أهل مكة ويقول: إن محمدا يحدثكم أحاديث عاد وثمود، وأنا أحدثكم أحاديث فارس والروم فيستملحون حديثه ويتركون استماع القرآن.'),
(3477, 31, 8, '«إن الذين آمنوا وعملوا الصالحات لهم جنات النعيم».'),
(3478, 31, 9, '«خالدين فيها» حال مقدرة أي: مقدرا خلودهم فيها إذا دخلوها «وعد الله حقا» أي وعدهم الله ذلك وحقه حقا «وهو العزيز» الذي لا يغلبه شيء فيمنعه من إنجاز وعده ووعيده «الحكيم» الذي لا يضع شيئا إلا في محله.'),
(3479, 31, 10, '«خلق السماوات بغير عمَدٍ ترونها» أي العمد جمع عماد وهو الاسطوانة، وهو صادق بأن لا عمد أصلا «وألقى في الأرض رواسي» جبالا مرتفعة لـ «أن» لا «تميد» تتحرك «بكم وبثَّ فيها من كل دابة وأنزلنا» فيه التفات عن الغيبة «من السماء ماءً فأنبتنا فيها من كل زوجٍ كريمٍ» صنف حسن.'),
(3480, 31, 11, '«هذا خلق الله» أي مخلوقه «فأروني» أخبروني يا أهل مكة «ماذا خلق الذين من دونه» غيره أي آلهتكم حتى أشركتموها به تعالى، وما استفهام إنكار مبتدأ وذا بمعنى الذي بصلته خبره وأروني معلق عن العمل وما بعده سد مسد المفعولين «بل» للانتقال «الظالمون في ضلالٍ مبينٍ» بيّن بإشراكهم وأنتم منهم.'),
(3481, 31, 12, '«ولقد آتينا لقمان الحكمة» منها العلم والديانة والإصابة في القول، وحكمه كثيرة مأثورة، كان يفتي قبل بعثة داود وأدرك بعثته وأخذ عنه العلم وترك الفتيا وقال في ذلك: ألا أكتفي إذا كفيت، وقيل له أي الناس شر؟ قال: الذي لا يبالي إن رآه الناس مسيئاً «أن» أي وقلنا له أن «أشكر لله» على ما أعطاك من الحكمة «ومن يشكر فإنما يشكر لنفسه» لأن ثواب شكره له «ومن كفر» النعمة «فإن الله غني» عن خلقه «حميد» محمود في صنعه.'),
(3482, 31, 13, '«و» اذكر «إذ قال لقمان لابنه وهو يعظه يا بنيّ» تصغير إشفاق «لا تشرك بالله إن الشرك» بالله «لظلم عظيم» فرجع إليه وأسلم.'),
(3483, 31, 14, '«ووصينا الإنسان بوالديه» أمرناه أن يبرهما «حملته أمُه» فْوهنت «وهنا على وهنٍ» أي ضعفت للحمل وضعفت للطلق وضعفت للولادة «وفصاله» أي فطامه «في عامين» وقلنا له «أنِ اشكر لي ولوالديك إلىَّ المصير» أي المرجع.'),
(3484, 31, 15, '«وإن جاهداك على أن تشرك بي ما ليس لك به علم» موافقة للواقع «فلا تطعهما وصاحبهما في الدنيا معروفا» أي بالمعروف البر والصلة «واتبع سبيل» طريق «من أناب» رجع «إليَّ» بالطاعة «ثم إليَّ مرجعكم فأنبئكم بما كنتم تعملون» فأجازيكم عليه وجملة الوصية وما بعدها اعتراض.'),
(3485, 31, 16, '«يا بنيّ إنها» أي الخصلة السيئة «إن تك مثقال حبة من خردلِ فتكن في صخرة أو في السماوات أو في الأرض» أي في أخفى مكان من ذلك «يأت بها الله» فيحاسب عليها «إن الله لطيف» باستخراجها «خبير» بمكانها.'),
(3486, 31, 17, '«يا بنيّ أقْم الصلاة وأمُر بالمعروف وانْهَ عن المنكر واصبر على ما أصابك» بسبب الأمر والنهي «إن ذلك» المذكور «من عزم الأمور» أي معزوماتها التي يعزم عليها لوجوبها.'),
(3487, 31, 18, '«ولا تصعِّر» وفي قراءة تصاعر «خدك للناس» لا تمل وجهك عنهم تكبرا «ولا تمش في الأرض مرحا» أي خيلاء «إن الله لا يحب كل مختالٍ» متبختر في مشيه «فخور» على الناس.'),
(3488, 31, 19, '«واقصد في مشيك» توسط فيه بين الدبيب والإسراع، وعليك السكينة والوقار «واغضض» اخفض «من صوتك إن أنكر الأصوات» أقبحها «لصوت الحمير» أوله زفير وآخره شهيق.'),
(3489, 31, 20, '«ألم تروْا» تعلموا يا مخاطبين «أن الله سخَّر لكم ما في السموات» من الشمس والقمر والنجوم لتنتفعوا بها «وما في الأرض» من الثمار والأنهار والدواب «وأسبغ» أوسع وأتمَّ «عليكم نعمه ظاهرةً» وهي حسن الصورة وتسوية الأعضاء وغير ذلك «وباطنةً» هي المعرفة وغيرها «ومن الناس» أي أهل مكة «من يجادل في الله بغير علم ولا هدىً» من رسول «ولا كتاب منير» أنزله الله، بل بالتقليد.'),
(3490, 31, 21, '«وإذا قيل لهم اتَّبعوا ما أَنزل الله قالوا بل نتبع ما وجدنا عليه آباءنا» قال تعالى: «أ» يتبعونه «ولو كان الشيطان يدعوهم إلى عذاب السعير» أي موجباته؟ لا.'),
(3491, 31, 22, '«ومن يُسلم وجهه إلى الله» أي يقبل على طاعته «وهو محسن» موحد «فقد استمسك بالعروة الوثقى» بالطرف الأوثق الذي لا يخاف انقطاعه «وإلى الله عاقبة الأمور» مرجعها.'),
(3492, 31, 23, '«ومن كفر فلا يَحزُنْك» يا محمد «كفره» لا تهتم بكفره «إلينا مرجعهم فننبئهم بما عملوا إن الله عليم بذات الصدور» أي بما فيها كغيره فمجاز عليه.'),
(3493, 31, 24, '«نمتعهم» في الدنيا «قليلاً» أيام حياتهم «ثم نضطرهم» في الآخرة «إلى عذابٍ غليظٍ» وهو عذاب النار لا يجدون عنه محيصاً.'),
(3494, 31, 25, '«ولئن» لام قسم «سألتهم من خلق السموات والأرض ليقولن الله» حذف منه نون الرفع لتوالي الأمثال، وواو الضمير لالتقاء الساكنين «قل الحمد لله» على ظهور الحجة عليهم بالتوحيد «بل أكثرهم لا يعلمون» وجوبه عليهم.'),
(3495, 31, 26, '«لله ما في السماوات والأرض» ملكا وخلقا وعبيدا فلا يستحق العبادة فيهما غيره «إن الله هو الغني» عن خلقه «الحميد» المحمود في صنعه.'),
(3496, 31, 27, '«ولو أن ما في الأرض من شجرةٍ أقلامٌ والبحر» عطف على اسم أن «يمده من بعده سبعة أبحر» مدادا «ما نفدت كلمات الله» المعبر بها عن معلوماته بكتبها بتلك الأقلام بذلك المداد ولا بأكثر من ذلك لأن معلوماته تعالي غير متناهية «إن الله عزيز» لا يعجزه شيء «حكيم» لا يخرج شئ عن علمه وحكمته.'),
(3497, 31, 28, '«ما خلقكم ولا بعثكم إلا كنفس واحدة» خلقا وبعثا، لأنه بكلمة كن فيكون «إن الله سميعٌ» يسمع كل مسموع «بصيرٌ» يبصر كل مبصر لا يشغله شيء عن شيء.'),
(3498, 31, 29, '«ألم ترَ» تعلم يا مخاطب «أن الله يُولج» يدخل «الليل في النهار ويولج النهار» يدخله «في الليل» فيزيد كل منهما بما نقص من الآخر «وسخَّر الشمس والقمر كلُّ» منهما «يجري» في فلكه «إلى أجل مسمى» هو يوم القيامة «وأن الله بما تعملون خبير».'),
(3499, 31, 30, '«ذلك» المذكور «بأن الله هو الحق» الثابت «وأن ما يدعون» بالياء والتاء يعبدون «من دونه الباطل» الزائل «وأن الله هو العليُّ» على خلقه بالقهر «الكبيرُ» العظيم.'),
(3500, 31, 31, '«ألم ترَ أن الفلك» السفن «تجري في البحر بنعمة الله ليريكم» يا مخاطبين بذلك «من آياته إنَّ في ذلك لآياتٍ» عبرا «لكل صبَّار» عن معاصي الله «شكور» لنعمته.'),
(3501, 31, 32, '«وإذا غشيهم» أي علا الكفار «موجٌ كالظلل» كالجبال التي تُظل من تحتها «دعوا الله مخلصين له الدين» أي الدعاء بأن ينجيهم أي لا يدعون معه غيره «فلما نجاهم إلى البر فمنهم مقتصد» متوسط بين الكفر والإيمان، ومنهم باق على كفره «وما يجحد بآياتنا» ومنها الإنجاء من الموج «إلا كل ختارٍ» غدار «كفورٍ» لنعم الله تعالى.'),
(3502, 31, 33, '«يا أيها الناس» أي أهل مكة «اتقوا ربكم واخشوْا يوما لا يجزي» يغني «والد عن ولده» فيه شيئا «ولا مولود عن هو جاز عن والده» فيه «شيئا إن وعد الله حقٌ» بالبعث «فلا تغرنكم الحياة الدنيا» عن الإسلام «ولا يغرنكم بالله» في حلمه وإمهاله «الغرور» الشيطان.'),
(3503, 31, 34, '(إن الله عنده علم الساعة) متى تقوم (وينزل) بالتخفيف والتشديد (الغيث) بوقت يعلمه (ويعلم ما في الأرحام) أذكر أم أنثى، ولا يعلم واحدا من الثلاثة غير الله تعالى (وما تدري نفس ماذا تكسب غدا) من خير أو شر ويعلمه الله تعالى (وما تدري نفس بأي أرض تموت) ويعلمه الله تعالى (إن الله عليم) بكل شيء (خبير) بباطنه كظاهره، روى البخاري عن ابن عمر حديث: "مفاتيح الغيب خمسة إن الله عنده علم الساعة إلى آخر السورة".');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, '«الم» الله أعلم بمراده به.'),
(3505, 32, 2, '«تنزيل الكتاب» القرآن مبتدأ «لا ريب» شك «فيه» خبر أول «من رب العالمين» خبر ثان.'),
(3506, 32, 3, '«أَم» بل «يقولون افتراه» محمد صلى الله عليه وسلم لا «بل هو الحق من ربك، لتنذر» به «قوما ما» نافية «أَتاهم من نذير من قبلك لعلهم يهتدون» بإنذارك.'),
(3507, 32, 4, '«الله الذي خلق السماوات والأرض وما بينهما في ستة أيام» أولها الأحد وآخرها الجمعة «ثم استوى على العرش» هو في اللغة سرير الملك استواءً يليق به «مالكم» يا كفار مكة «من دونه» أي: غيره «من وليٍّ» اسم ما بزيادة من، أَي: ناصر «ولا شفيع» يدفع عذابه عنكم «أفلا تتذكرون» هذا فتؤمنون.'),
(3508, 32, 5, '(يدبر الأمر من السماء إلى الأرض) مدة الدنيا (ثم يعرج) يرجع الأمر والتدبير (إليه في يوم كان مقداره ألف سنة مما تعدون) في الدنيا، وفي سورة "سأل" خمسين ألف سنة وهو يوم القيامة لشدة أهواله بالنسبة إلى الكافر، وأما المؤمن فيكون أخف عليه من صلاة مكتوبة يصليها في الدنيا كما جاء في الحديث.'),
(3509, 32, 6, '«ذلك» الخالق المدبِّر «عالم الغيب والشهادة» أي ما غاب عن الخلق وما حضر «العزيز» المنيع في ملكه «الرحيم» بأهل طاعته.'),
(3510, 32, 7, '«الذي أحسن كل شيءٍ خلقه» بفتح اللام فعلا ماضيا صفة، وبسكونها بدل اشتمال «وبدأ خلق الإنسان» آدم «من طين».'),
(3511, 32, 8, '«ثم جعل نسله» ذريته «من سلالة» علقة «من ماءٍ مهين» ضعيف هو النطفة.'),
(3512, 32, 9, '«ثم سوَّاه» أي خلق آدم «ونفخ فيه من روحه» أي جعله حيا حساسا بعد أن كان جمادا «وجعل لكم» أي لذريته «السمع» بمعنى الأسماع «والأبصار والأفئدة» القلوب «قليلا ما تشكرون» ما زائدة مؤكدة للقلة.'),
(3513, 32, 10, '«وقالوا» أي منكرو البعث «أإذا ضللنا في الأرض» غبنا فيها، بأن صرنا ترابا مختلطا بترابها «أإنا لفي خلق جديد» استفهام إنكار بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين في الموضعين، قال تعالى: «بل هم بلقاء ربهم» بالبعث «كافرون».'),
(3514, 32, 11, '«قل» لهم «يتوفاكم ملك الموت الذي وكِّل بكم» أي يقبض أرواحكم «ثم إلى ربكم ترجعون» أحياء فيجازيكم بأعمالكم.'),
(3515, 32, 12, '«ولو ترى إذ المجرمون» الكافرون «ناكسوا رؤوسهم عند ربهم» مطأطئوها حياءً يقولون «ربنا أبصرنا» ما أنكرنا من البعث «وسمعنا» منك تصديق الرسل فيما كذبناهم فيه «فارجعنا» إلى الدنيا «نعمل صالحا» فيها «إنا موقنون» الآن فما نفعهم ذلك ولا يرجعون، وجواب لو: لرأيت أمرا فظيعاً، قال تعالى:'),
(3516, 32, 13, '«ولو شئنا لآتينا كل نفسٍ هداها» فتهتدي بالإيمان والطاعة باختيار منها «ولكن حق القول مني» وهو «لأملأنَّ جهنم من الجِنة» الجن «والناس أجمعين» وتقول لهم الخزنة إذا دخلوها:'),
(3517, 32, 14, '«فذوقوا» العذاب «بما نسيتم لقاء يومكم هذا» أي بترككم الإيمان به «إنا نسيناكم» تركناكم في العذاب «وذوقوا عذاب الخلد» الدائم «بما كنتم تعملون» من الكفر والتكذيب.'),
(3518, 32, 15, '«إنما يؤمن بآياتنا» القرآن «الذين إذا ذُكِّروا» وعظوا «بها خرُّوا سجَّدا وسبَّحوا» متلبسين «بحمد ربهم» أي قالوا: سبحان الله وبحمده «وهم لا يستكبرون» عن الإيمان والطاعة.'),
(3519, 32, 16, '«تتجافى جنوبهم» ترتفع «عن المضاجع» مواضع الاضطجاع بفرشها لصلاتهم بالليل تهجدا «يدعون ربهم خوفا» من عقابه «وطمعا» في رحمته «ومما رزقناهم ينفقون» يتصدقون.'),
(3520, 32, 17, '«فلا تعلم نفسٌ ما أخفىَ» خبئ «لهم من قرة أعين» ما تقر به أعينهم، وفي قراءة بسكون الياء مضارع «جزاءً بما كانوا يعملون».'),
(3521, 32, 18, '«أَفمن كان مؤمنا كمن كان فاسقا لا يستوون» أي المؤمنون والفاسقون.'),
(3522, 32, 19, '«أما الذين آمنوا وعملوا الصالحات فلهم جنات المأوى نزلا» هو ما يعد للضيف «بما كانوا يعملون».'),
(3523, 32, 20, '«وأما الذين فسقوا» بالكفر والتكذيب «فمأواهم النار كلما أرادوا أن يخرجوا منها أعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون».'),
(3524, 32, 21, '«ولنذيقنهم من العذاب الأدنى» عذاب الدنيا بالقتل والأسر والجدب سنين والأمراض «دون» قبل «العذاب الأكبر» عذاب الآخرة «لعلهم» أي من بقي منهم «يرجعون» إلى الإيمان.'),
(3525, 32, 22, '«ومن أظلم ممن ذُكِّرَ بآيات ربِّه» القرآن «ثم أعرض عنها» أي لا أحد أظلم منه «إنا من المجرمين» المشركين «منتقمون».'),
(3526, 32, 23, '«ولقد آتينا موسى الكتاب» التوراة «فلا تكن في مِريةٍ» شكٍ «من لقائه» وقد التقيا ليلة الإسراء «وجعلناهُ» أي موسى أو الكتاب «هدىً» هاديا «لبني إسرائيل».'),
(3527, 32, 24, '«وجعلنا منهم أئمةً» بتحقيق الهمزتين وإبدال الثانية ياء: قادة «يهدون» الناس «بأمرنا لما صبروا» على دينهم وعلى البلاء من عدوهم، وفي قراءة بكسر اللام وتخفيف الميم «وكانوا بآياتنا» الدالة على قدرتنا ووحدانيتنا «يوقنون».'),
(3528, 32, 25, '«إن ربك هو يفصل بينهم يوم القيامة فيما كانوا فيه يختلفون» من أمر الدين.'),
(3529, 32, 26, '«أو لم يهدِ لهم كم أهلكنا من قبلهم» أي يتبيَّن لكفار مكة إهلاكنا كثيرا «من القرون» الأمم بكفرهم «يمشون» حال من ضمير لهم «في مساكنهم» في أسفارهم إلى الشام وغيرها فيعتبروا «إن في ذلك لآيات» دلالات على قدرتنا «أفلا يسمعون» سماع تدبر واتعاظ.'),
(3530, 32, 27, '«أو لم يروا أنا نسوق الماء إلى الأرض الجرز» اليابسة التي لا نبات فيها «فنخرج به زرعا تأكل منه أنعامهم وأنفسهم أفلا يبصرون» هذا فيعلمون أنا نقدر على إعادتهم.'),
(3531, 32, 28, '«ويقولون» للمؤمنين «متى هذا الفتح» بيننا وبينكم «إن كنتم صادقين».'),
(3532, 32, 29, '«قل يوم الفتح» بإنزال العذاب بهم «لا ينفع الذين كفروا إيمانهم ولا هم ينظرون» يمهلون لتوبة أو معذرة.'),
(3533, 32, 30, '«فأعرض عنهم وانتظر» إنزال العذاب بهم «إنهم منتظرون» بك حادث موت أو قتل فيستريحون منك، وهذا قبل الأمر بقتالهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, '«يا أيها النبي اتق الله» دم على تقواه «ولا تطع الكافرين والمنافقين» فيما يخالف شريعتك «إن الله كان عليما» بما يكون قبل كونه «حكيما» فيما يخلقه.'),
(3535, 33, 2, '«واتبع ما يوحى إليك من ربك» أي القرآن «إن الله كان بما تعملون خبيرا» وفي قراءة بالتحتانية.'),
(3536, 33, 3, '«وتوكل على الله» في أمرك «وكفى بالله وكيلا» حافظا لك، وأمته تبع له في ذلك كله.'),
(3537, 33, 4, '«ما جعل الله لرجل من قلبين في جوفه» ردا على من قال من الكفار إن له قلبين يعقل بكل منهما أفضل من عقل محمد «وما جعل أزواجكم اللائي» بهمزة وياء وبلا ياء «تَظَّهَّروُنَ» بلا ألف قبل الهاء وبها والتاء الثانية في الأصل مدغمة في الظاء «منهن» يقول الواحد مثلا لزوجته أنت على كظهر أمي «أمهاتكم» أي كالأمهات في تحريمها بذلك المعد في الجاهلية طلاقا، وإنما تجب به الكفارة بشرطه كما ذكر في سورة المجادلة «وما جعل أدعياءَكم» جمع دعي وهو من يدعي لغير أبيه أبنا له «أبناءَكم» حقيقة «ذلكم قولكم بأفواهكم» أي اليهود والمنافقين قالوا لما تزوج النبي صلى الله عليه وسلم زينب بنت جحش التي كانت امرأة زيد بن حارثة الذي تبناه النبي صلى الله عليه وسلم قالوا: تزوج محمد امرأة ابنه فأكذبهم الله تعالى في ذلك «والله يقول الحق» في ذلك «وهو يهدي السبيل» سبيل الحق.'),
(3538, 33, 5, 'لكن «ادعوهم لآبائهم هو أقسطُ» أعدل «عند الله، فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم» بنو عمكم «وليس عليكم جناح فيما أخطأتم به» في ذلك «ولكن» في «ما تعمدت قلوبكم» فيه أي بعد النهي «وكان الله غفورا» لما كان من قولكم قبل النهي «رحيما» بكم في ذلك.'),
(3539, 33, 6, '«النبيُّ أولى بالمؤمنين من أنفسهم» فيما دعاهم إليه ودعتهم أنفسهم إلى خلافه «وأزواجه أمهاتهم» في حرمة نكاحهن عليهم «وأولوا الأرحام» ذوو القرابات «بعضهم أولى ببعض» في الإرث «في كتاب الله من المؤمنين والمهاجرين» أي من الإرث بالإيمان والهجرة الذي كان أول الإسلام فنسخ «إلا» لكن «أن تفعلوا إلى أوليائكم معروفا» بوصية فجائز «كان ذلك» أي نسخ الإرث بالإيمان والهجرة بإرث ذوي الأرحام «في الكتاب مسطورا» وأريد بالكتاب في الموضعين اللوح المحفوظ.'),
(3540, 33, 7, '«و» اذكر «إذ أخذنا من النبيين ميثاقهم» حين أخرجوا من صلب آدم كالذرّ جمع ذرة وهي أصغر النمل «ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم» بأن يعبدوا الله ويدعوا إلى عبادته وذكر الخمسة من عطف الخاص على العام «وأخذنا منهم ميثاقا غليظا» شديدا بالوفاء بما حملوه وهو اليمين بالله تعالى ثم أخذ الميثاق.'),
(3541, 33, 8, '«ليسأل» الله «الصادقين عن صدقهم» في تبليغ الرسالة تبكيتا للكافرين بهم «وأعد» تعالى «للكافرين» بهم «عذابا أليما» مؤلما هو عطف على أخذنا.'),
(3542, 33, 9, '«يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود» من الكفار متحزبون أيام حفر الخندق «فأرسلنا عليهم ريحا وجنودا لم تروها» من الملائكة «وكان الله بما تعملون» بالتاء من حفر الخندق وبالياء من تحزيب المشركين «بصيرا».'),
(3543, 33, 10, '«إذ جاؤوكم من فوقكم ومن أسفل منكم» من أعلى الوادي وأسفله من المشرق والمغرب «وإذ زاغت الأبصار» مالت عن كل شيء إلى عدوّها من كل جانب «وبلغت القلوب الحناجر» جمع حنجرة وهي منتهى الحلقوم من شدة الخوف «وتظنون بالله الظنونا» المختلفة بالنصر والبأس.'),
(3544, 33, 11, '«هنالك ابتُليَ المؤمنون» اختبروا ليتبين المخلص من غيره «وزلزلوا» حركوا «زلزالا شديدا» من شدة الفزع.'),
(3545, 33, 12, '«و» اذكر «إذ يقول المنافقون والذين في قلوبهم مرض» ضعف اعتقاد «ما وعدنا الله ورسوله» بالنصر «إلا غرورا» باطلا.'),
(3546, 33, 13, '«وإذ قالت طائفة منهم» أي المنافقون «يا أهل يثرب» هي أرض المدينة ولم تصرف للعلمية ووزن الفعل «لا مقام لكم» بضم الميم وفتحها: أي لا إقامة ولا مكانة «فارجعوا» إلى منازلكم من المدينة وكانوا خرجوا مع النبي صلى الله عليه وسلم إلى سلع جبل خارج المدينة للقتال «ويستأذن فريق منهم النبي» في الرجوع «يقولون إنَّ بيوتنا عورة» غير حصينة يخشى عليها، قال تعالى: «وما هي بعورة إن» ما «يريدون إلا فرارا» من القتال.'),
(3547, 33, 14, '«ولو دُخلت» أي المدينة «عليهم من أقطارها» نواحيها «ثم سُئِلوا» أي سألهم الداخلون «الفتنة» الشرك «لآتوها» بالمد والقصر أي أعطوها وفعلوها «وما تلبَّثوا بها إلا يسيراً».'),
(3548, 33, 15, '«ولقد كانوا عاهدوا الله من قبل لا يولون الأدبار وكان عهد الله مسؤولا» عن الوفاء به.'),
(3549, 33, 16, '«قل لن ينفعكم الفرار إن فررتم من الموت أو القتل وإذا» إن فررتم «لا تمتعون» في الدنيا بعد فراركم «إلا قليلا» بقية آجالكم.'),
(3550, 33, 17, '«قل من ذا الذي يعصمكم» يجيركم «من الله إن أراد بكم سوءا» هلاكا وهزيمة «أو» يصيبكم بسوء إن «أراد» الله «بكم رحمة» خيرا «ولا يجدون لهم من دون الله» أي غيره «وليا» ينفعهم «ولا نصيرا» يدفع الضُرَّ عنهم.'),
(3551, 33, 18, '«قد يعلم الله المعوقين» المثبطين «منكم والقائلين لإخوانهم هلمَّ» تعالوا «إلينا ولا يأتون البأس» القتال «إلا قليلا» رياء وسمعة.'),
(3552, 33, 19, '«أشحة عليكم» بالمعاونة، جمع شحيح وهو حال من ضمير يأتون «فإذا جاء الخوف رأيتهم ينظرون إليك تدور أعينهم كالذي» كنظر أو كدوران الذي «يغشى عليه من الموت» أي سكراته «فإذا ذهب الخوف» وحيزت الغنائم «سلقوكم» آذوكم أو ضربوكم «بألسنة حداد أشحة على الخير» أي الغنيمة يطلبونها «أولئك لم يؤمنوا» حقيقة «فأحبط الله أعمالهم وكان ذلك» الإحباط «على الله يسيرا» بإرادته.'),
(3553, 33, 20, '«يحسبون الأحزاب» من الكفار «لم يذهبوا» إلى مكة لخوفهم منهم «وإن يأت الأحزاب» كرة أخرى «يودُّوا» يتمنوا «لو أنهم بادون في الأعراب» أي كائنون في البادية «يسألون عن أنبائكم» أخباركم مع الكفار «ولو كانوا فيكم» هذه الكرة «ما قاتلوا إلا قليلا» رياءً وخوفا من التعيير.'),
(3554, 33, 21, '«لقد كان لكم في رسول الله أسوة» بكسر الهمزة وضمها «حسنة» اقتداء به في القتال والثبات في مواطنه «لمن» بدل من لكم «كان يرجو الله» يخافه «واليوم الآخر وذكر الله كثيرا» بخلاف من ليس كذلك.'),
(3555, 33, 22, '«ولمَّا رأى المؤمنون الأحزاب» من الكفار «قالوا هذا ما وعدنا الله ورسوله» من الابتلاء والنصر «وصدق الله ورسوله» في الوعد «وما زادهم» ذلك «إلا إيمانا» تصديقا بوعد الله «وتسليما» لأمره.'),
(3556, 33, 23, '«من المؤمنين رجال صدقوا ما عاهدوا الله عليه» من الثبات مع النبي صلى الله عليه وسلم «فمنهم من قضى نحبه» مات أو قتل في سبيل الله «ومنهم من ينتظر» ذلك «وما بدَّلوا تبديلا» في العهد، وهم بخلاف حال المنافقين.'),
(3557, 33, 24, 'ليجزيَ الله الصادقين بصدقهم ويعذب المنافقين إن شاء» بأن يميتهم على نفاقهم «أو يتوب عليهم إن الله كان غفورا» لمن تاب «رحيما» به.'),
(3558, 33, 25, '«وردَّ الله الذين كفروا» أي الأحزاب «بغيظهم لم ينالوا خيرا» مرادهم من الظفر بالمؤمنين «وكفى الله المؤمنين القتال» بالريح والملائكة «وكان الله قويا» على إيجاد ما يريده «عزيزا» غالبا على أمره.'),
(3559, 33, 26, '«وأنزل الذين ظاهروهم من أهل الكتاب» أي قريظة «من صياصيهم» حصونهم جمع صيصة وهو ما يتحصن به «وقذف في قلوبهم الرعب» الخوف «فريقا تقتلون» منهم وهم المقاتلة «وتأسرون فريقا» منهم أي الذراري.'),
(3560, 33, 27, '«وأورثكم أرضهم وديارهم وأموالهم وأرضا لم تطؤوها» بعد وهي خيبر أخذت بعد قريظة «وكان الله على كل شيءٍ قديرا».'),
(3561, 33, 28, '«يا أيها النبي قل لأزواجك» وهن تسع وطلبن منه من زينة الدنيا ما ليس عنده «إن كنتنَّ تردن الحياة الدنيا وزينتها فتعالين أمتعكن» أي متعة الطلاق «وأسرحكن سراحا جميلا» أطلقكنَّ من غير ضرار.'),
(3562, 33, 29, '«وإن كنتنَّ تردن الله ورسوله والدار الآخرة» أي الجنة «فإن الله أعد للمحسنات منكن» بإرادة الآخرة «أجرا عظيما» أي الجنة: فاخترن الآخرة على الدنيا.'),
(3563, 33, 30, '«يا نساء النبي من يأت منكن بفاحشةٍ مبيَنةٍ» بفتح الياء وكسرها، أي بينت أو هي بينة «يضاعف» وفي قراءة يضعف بالتشديد وفي أخرى نضعف بالنون معه ونصب العذاب «لها العذاب ضعفين» ضعفي عذاب غيرهن، أي مثليه «وكان ذلك على الله يسيرا».'),
(3564, 33, 31, '«ومن يقنت» يطع «منكن الله ورسوله وتعمل صالحا نؤتها أجرها مرتين» أي مثلي ثواب غيرهن من النساء، وفي قراءة بالتحتانية في تعمل ونؤتها «وأعتدنا لها رزقا كريما» في الجنة زيادة.'),
(3565, 33, 32, '«يا نساء النبي لستنَّ كأحد» كجماعة «من النساء إن اتقيتن» الله فإنكن أعظم «فلا تخضعن بالقول» للرجال «فيطمع الذي في قلبه مرض» نفاق «وقلن قولا معروفا» من غير خضوع.'),
(3566, 33, 33, '(وقرن) بكسر القاف وفتحها (في بيوتكن) من القرار وأصله: اقررن بكسر الراء وفتحها من قررت بفتح الراء وكسرها نقلت حركة الراء إلى القاف وحذفت مع همزة الوصل (ولا تبرجن) بترك إحدى التاءين من أصله (تبرج الجاهلية الأولى) أي ما قبل الإسلام من إظهار النساء محاسنهن للرجال والإظهار بعد الإسلام مذكور في آية "ولا يبدين زينتهن إلا ما ظهر منها" (وأقمن الصلاة وآتين الزكاة وأطعن الله ورسوله إنما يريد الله ليذهب عنكم الرجس) الإثم يا (أهل البيت) أي نساء النبي صلى الله عليه وسلم (ويطهركم) منه (تطهيرا).'),
(3567, 33, 34, '«واذكرن ما يتلى في بيوتكن من آيات الله» القرآن «والحكمة» السنة «إن الله كان لطيفا» بأوليائه «خبيرا» بجميع خلقه.'),
(3568, 33, 35, '«إن المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات» المطيعات «والصادقين والصادقات» في الإيمان «والصابرين والصابرات» على الطاعات «والخاشعين» المتواضعين «والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات» عن الحرام «والذاكرين الله كثيرا والذاكرات أعد الله لهم مغفرة» للمعاصي «وأجرا عظيما» على الطاعات.'),
(3569, 33, 36, '(وما كان لمؤمن ولا مؤمنة إذا قضي الله ورسوله أمرا أن تكون) بالتاء والياء (لهم الخيرة) أي الاختيار (من أمرهم) خلاف أمر الله ورسوله، نزلت في عبد الله بن جحش وأخته زينب خطبها النبي لزيد بن حارثة فكرها ذلك حين علما لظنهما قبل أن النبي صلى الله عليه وسلم خطبها لنفسه ثم رضيا للآية (ومن يعص الله ورسوله فقد ضل ضلالا مبينا) بينا فزوجها النبي صلى الله عليه وسلم لزيد ثم وقع بصره عليها بعد حين فوقع في نفسه حبها وفي نفس زيد كراهتها، ثم قال للنبي صلى الله عليه وسلم أريد فراقها فقال: "أمسك عليك زوجك" كما قال تعالى:'),
(3570, 33, 37, '«وإذ» منصوب باذكر «تقول للذي أنعم الله عليه» بالإسلام «وأَنعمت عليه» بالإعتاق وهو زيد بن حارثة كان من سبي الجاهلية اشتراه رسول الله صلى الله عليه وسلم قبل البعثة وأعتقه وتبناه «أمسك عليك زوجك واتق الله» في أمر طلاقها «وتخفي في نفسك ما الله مبديه» مظهره من محبتها وأن لو فارقها زيد تزوجتها «وتخشى الناس» أن يقولوا تزوج زوجة ابنه «والله أحق أن تخشاه» في كل شيء وتزوجها ولا عليك من قول الناس، ثم طلقها زيد وانقضت عدتها قال تعالى: «فلما قضى زيد منها وطرا» حاجة «زوجناكها» فدخل عليها النبي صلى الله عليه وسلم بغير إذن وأشبع المسلمين خبزا ولحما «لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوْا منهن وطرا وكان أمر الله» مقضيه «مفعولا».'),
(3571, 33, 38, '«ما كان على النبي من حرج فيما فرض» أحل «الله له سنة الله» أي كسنة الله فنصب بنزع الخافض «في الذين خلوْا من قبل» من الأنبياء أن لا حرج عليهم في ذلك توسعة لهم في النكاح «وكان أمر الله» فعله «قدرا مقدورا» مقضيا.'),
(3572, 33, 39, '«الذين» نعت للذين قبله «يبلغون رسالات الله ويخشونه ولا يخشون أحدا إلا الله» فلا يخشون مقالة الناس فيما أحل الله لهم «وكفى بالله حسيبا» حافظا لأعمال خلقه ومحاسبهم.'),
(3573, 33, 40, '«ما كان محمد أبا أحد من رجالكم» فليس أبا زيد: أي والده فلا يحرم عليه التزوج بزوجته زينب «ولكن» كان «رسول الله وخاتِم النبيين» فلا يكون له ابن رجل بعده يكون نبيا، وفي قراءة بفتح التاء كآلة الختم: أي به ختموا «وكان الله بكل شيءٍ عليما» منه بأن لا نبيّ بعده وإذا نزل السيد عيسى يحكم بشريعته.'),
(3574, 33, 41, '«يا أيها الذين آمنوا اذكروا الله ذكرا كثيرا».'),
(3575, 33, 42, '«وسبحوه بكرةً وأصيلا» أول النهار وآخره.'),
(3576, 33, 43, '«هو الذي يصلي عليكم» أي يرحمكم «وملائكته» يستغفرون لكم «ليخرجكم» ليديم إخراجه إياكم «من الظلمات» أي الكفر «إلى النور» أي الإيمان «وكان بالمؤمنين رحيما».'),
(3577, 33, 44, '«تحيتهم» منه تعالى «يوم يلقوْنه سلام» بلسان الملائكة «وأعد لهم أجرا كريما» هو الجنة.'),
(3578, 33, 45, '«يا أيها النبي إنا أرسلناك شاهدا» على من أرسلت إليهم «ومبشرا» من صدقك بالجنة «ونذيرا» منذرا من كذبك بالنار.'),
(3579, 33, 46, '«وداعيا إلى الله» إلى طاعته «بإذنه» بأمره «وسراجا منيرا» أي مثله في الاهتداء به.'),
(3580, 33, 47, '«وبشّر المؤمنين بأن لهم من الله فضلا كبيرا» هو الجنة.'),
(3581, 33, 48, '«ولا تطع الكافرين والمنافقين» فيما يخالف شريعتك «ودع» اترك «أذاهم» لا تجازهم عليه إلى أن تؤمر فيهم بأمر «وتوكل على الله» فهو كافيك «وكفى بالله وكيلا» مفوضا إليه.'),
(3582, 33, 49, '«يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن من قبل أن تمسوهن» وفي قراءة تماسوهنَّ، أي تجامعوهنَّ «فما لكم عليهن من عدة تعتدونها» تحصونها بالأقراء وغيرها «فمتعوهن» أعطوهن ما يستمتعن به، أي إن لم يسم لهن أصدقة وإلا فلهن نصف المسمى فقط، قاله ابن عباس وعليه الشافعي «وسرّحوهن سراحا جميلا» خلوا سبيلهن من غير إضرار.'),
(3583, 33, 50, 'يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن» مهورهن «وما ملكت يمينك مما أفاء الله عليك» من الكفار بالسبي كصفية وجويرية «وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك» بخلاف من لم يهاجرن «وامرأةً مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها» يطلب نكاحها بغير صداق «خالصة لك من دون المؤمنين» النكاح بلفظ الهبة من غير صداق «قد علمنا ما فرضنا عليهم» أي المؤمنين «في أزواجهم» من الأحكام بأن لا يزيدوا على أربع نسوة ولا يتزوجوا إلا بوليٍّ وشهود ومهر «و» في «ما ملكت أيمانهم» من الإماء بشراء وغيره بأن تكون الأمة ممن تحل لمالكها كالكتابية بخلاف المجوسية والوثنية وأن تستبرأ قبل الوطء «لكيلا» متعلق بما قبل ذلك «يكون عليك حرج» ضيق في النكاح «وكان الله غفورا» فيما يَعسر التحرز عنه «رحيما» بالتوسعة في ذلك.'),
(3584, 33, 51, '«ترجئ» بالهمزة والياء بدله تؤخر «من تشاء منهنَّ» أي أزواجك عن نوبتها «وتؤوي» تضم «إليك من تشاء» منهن فتأتيها «ومن ابتغيت» طلبت «ممن عزلت» من القسمة «فلا جُناح عليك» في طلبها وضمها إليك خُيِّر في ذلك بعد أن كان القسم واجبا عليه «ذلك» التخيير «أدنى» أقرب إلى «أن تقرَّ أعينهنَّ ولا يحزن ويرضين بما آتيتهن» ما ذكر المخيّر فيه «كلهن» تأكيد للفاعل في يرضين «والله يعلم ما في قلوبكم» من أمر النساء والميل إلى بعضهن، وإنما خيَّرناك فيهن تيسيرا عليك في كل ما أردت «وكان الله عليما» بخلقه «حليما» عن عقابهم.'),
(3585, 33, 52, '«لا تحل» بالتاء وبالياء «لك النساء من بعد» التسع اللاتي اخترنك «ولا أن تبدل» بترك إحدى التاءين في الأصل «بهن من أزواج» بأن تطلقهن أو بعضهن وتنكح بدل من طلقت «ولو أعجبك حسنهن إلا ما ملكت يمينك» من الإماء فتحل لك وقد ملك صلى الله عليه وسلم بعدهن مارية وولدت له إبراهيم ومات في حياته «وكان الله على شيءٍ رقيبا» حفيظا.'),
(3586, 33, 53, '«يا أيها الذين آمنوا لا تدخلوا بيوت النبي إلا أن يؤذن لكم» في الدخول بالدعاء «إلى طعام» فتدخلوا «غير ناظرين» منتظرين «إناه» نضجه مصدر أنى يأنى «ولكن إذا دعيتم فادخلوا فإذا طعمتم فانتشروا ولا» تمكثوا «مستأنسين لحديث» من بعضكم لبعض «إن ذلكم» المكث «كان يؤذي النبي فيستحيي منكم» أن يخرجكم «والله لا يستحيي من الحق» أن يخرجكم، أي لا يترك بيانه، وقرئ يستحي بياء واحدة «وإذا سألتموهن» أي أزواج النبي صلى الله عليه وسلم «متاعا فاسألوهن من وراء حجابٍ» ستر «ذلكم أطهر لقلوبكم وقلوبهن» من الخواطر المريبة «وما كان لكم أن تؤذوا رسول الله» بشيء «ولا أن تنكحوا أزواجه من بعده أبدا إن ذلكم كان عند الله» ذنبا «عظيما».'),
(3587, 33, 54, '«إن تبدوا شيئا أو تخفوه» من نكاحهنَّ بعده «فإن الله كان بكل شيءٍ عليماً» فيجازيكم عليه.'),
(3588, 33, 55, '«لا جناح عليهنَّ في آبائهن ولا أبنائهن ولا إخوانهن ولا أبناء إخوانهن ولا أبناء أخواتهن ولا نسائهن» أي المؤمنات «ولا ما ملكت أيمانهن» من الإماء والعبيد أن يروهن ويكلموهن من غير حجاب «واتقين الله» فيما أمرتن به «إن الله كان على كل شيءٍ شهيدا» لا يخفى عليه شيء.'),
(3589, 33, 56, '«إن الله وملائكته يصلُّون على النبي» محمد صلى الله عليه وسلم «يا أيها الذين آمنوا صلوا عليه وسلموا تسليماً» أي قولوا: اللهم صل على سيدنا محمد وسلم.'),
(3590, 33, 57, '«إن الذين يؤذون الله ورسوله» وهم الكفار يصفون الله بما هو منزه عنه من الولد والشريك ويكذبون رسوله «لعنهم الله في الدنيا والآخرة» أبعدهم «وأعدَّ لهم عذاباً مهيناً» ذا إهانة وهو النار.'),
(3591, 33, 58, '«والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا» يرمونهم بغير ما عملوا «فقد احتملوا بهتانا» تحملوا كذبا «وإثما مبينا» بيِّنا.'),
(3592, 33, 59, '«يا أيها النبي قل لأزواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن» جمع جلباب وهي الملاءة التي تشتمل بها المرأة، أي يرخين بعضها على الوجوه إذا خرجن لحاجتهن إلا عينا واحدة «ذلك أدنى» أقرب إلى «أن يعرفن» بأنهن حرائر «فلا يؤذين» بالتعرض لهن بخلاف الإماء فلا يغطين وجوههن، فكان المنافقون يتعرضون لهم «وكان الله غفورا» لما سلف منهن من ترك الستر «رحيما» بهن إذ سترهن.'),
(3593, 33, 60, '«لئن» لام قسم «لم ينته المنافقون» عن نفاقهم «والذين في قلوبهم مرض» بالزنا «والمرجفون في المدينة» المؤمنين بقولهم قد أتاكم العدو وسراياكم قتلوا أو هزموا «لنغرينك بهم» لنسلطنك عليهم «ثم لا يجاورونك» يساكنوك «فيها إلا قليلا» ثم يخرجون.'),
(3594, 33, 61, '«ملعونين» مبعدين عن الرحمة «أينما ثقفوا» وجدوا «أخذوا وقتِّلوا تقتيلا» أي الحكم فيهم هذا على جهة الأمر به.'),
(3595, 33, 62, '«سُنَّة الله» أي سنَّ الله ذلك «في الذين خلوْا من قبل» من الأمم الماضية في منافقيهم المرجفين المؤمنين «ولن تجد لسنة الله تبديلا» منه.'),
(3596, 33, 63, '«يسألك الناس» أهل مكة «عن الساعة» متى تكون «قل إنما علمها عند الله وما يدريك» يعلمك بها: أي أنت لا تعلمها «لعلَّ الساعة تكون» توجد «قريبا».'),
(3597, 33, 64, '«إن الله لعن الكافرين» أبعدهم «وأعدَّ لهم سعيرا» نارا شديدة يدخلونها.'),
(3598, 33, 65, '«خالدين» مقدرا خلودهم «فيها أبدا لا يجدون وليا» يحفظهم عنها «ولا نصيرا» يدفعها عنهم.'),
(3599, 33, 66, '«يوم تُقلب وجوههم في النار يقولون يا» للتنبيه «ليتنا أطعنا الله وأطعنا الرسولا».'),
(3600, 33, 67, '«وقالوا» أي الأتباع منهم «ربنا إنا أطعنا سادتنا» وفي قراءة ساداتنا، جمع الجمع «وكبراءنا فأضلونا السبيلا» طريق الهدى.'),
(3601, 33, 68, '«ربنا آتهم ضعفين من العذاب» أي مثليْ عذابنا «والعنهم» عذبهم «لعنا كثيرا» عدده، وفي قراءة بالموحدة، أي عظيماً.'),
(3602, 33, 69, '(يا أيها الذين آمنوا لا تكونوا) مع نبيكم (كالذين آذوا موسى) بقولهم مثلا: ما يمنعه أن يغتسل معنا إلا أنه آذر (فبرأه الله مما قالوا) بأن وضع ثوبه على حجر ليغتسل ففر الحجر به حتى وقف بين ملأ من بني إسرائيل فأدركه موسى فأخذ ثوبه فاستتر به فرأوه ولا أدرة به وهي نفخة في الخصية (وكان عند الله وجيها) ذا جاه: ومما أوذي به نبينا صلى الله عليه وسلم أنه قسم قسما فقال رجل هذه قسمة ما أريد بها وجه الله تعالى، فغضب النبي صلى الله عليه وسلم من ذلك وقال: "يرحم الله موسى لقد أوذي بأكثر من هذا فصبر" رواه البخاري.'),
(3603, 33, 70, '«يا أيها الذين آمنوا اتقوا الله وقولوا قوْلا سديدا» صوابا.'),
(3604, 33, 71, '«يصلح لكم أعمالكم» يتقبلها «ويغفر لكم ذنوبكم ومن يطع الله ورسوله فقد فاز فوزا عظيما» نال غاية مطلوبة.'),
(3605, 33, 72, '«إنا عرضنا الأمانة» الصلوات وغيرهما مما في فعلها من الثواب وتركها من العقاب «على السماوات والأرض والجبال» بأن خلق فيهما فهما ونطقا «فأبين أن يحملنها وأشفقن» خفن «منها وحملها الإنسان» آدم بعد عرضها عليه «إنه كان ظلوما» لنفسه بما حمله «جهولا» به.'),
(3606, 33, 73, '«ليعذب الله» اللام متعلقة بعرضنا المترتب عليه حمل آدم «المنافقين والمنافقات والمشركين والمشركات» المضيعين الأمانة «ويتوب الله على المؤمنين والمؤمنات» المؤدين الأمانة «وكان الله غفورا» للمؤمنين «رحيما» بهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, '«الحمد لله» حمد تعالى نفسه بذلك، والمراد به الثناء بمضمونه من ثبوت الحمد وهو الوصف بالجميل لله تعالى «الذي له ما في السماوات وما في الأرض» ملكا وخلقا «وله الحمد في الآخرة» كالدنيا يحمده أولياؤه إذا دخلوا الجنة «وهو الحكيم» في فعله «الخبير» في خلقه.'),
(3608, 34, 2, '«يعلم ما يلج» يدخل «في الأرض» كماء وغيره «وما يخرج منها» كنبات وغيره «وما ينزل من السماء» من رزق وغيره «وما يعرج» يصعد «فيها» من عمل وغيره «وهو الرحيم» بأوليائه «الغفور» لهم.'),
(3609, 34, 3, '«وقال الذين كفروا لا تأتينا الساعة» القيامة «قل» لهم «بلى وربي لتأتينكم عالم الغيب» بالجر صفة والرفع خبر مبتدأ وعلام بالجر «لا يعزب» يغيب «عنه مثقال» وزن «ذرة» أصغر نملة «في السماوات ولا في الأرض ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين» بيَّن وهو اللوح المحفوظ.'),
(3610, 34, 4, '«ليجزي» فيها «الذين آمنوا وعملوا الصالحات أولئك لهم مغفرة ورزق كريم» حسن في الجنة.'),
(3611, 34, 5, '«والذين سعوا في» إبطال «آياتنا» القرآن «معجزين» وفي قراءة هنا وفيما يأتي معاجزين، أي مقدرين عجزنا أو مسابقين لنا فيفوتونا لظنهم أن لا بعث ولا عقاب «أولئك لهم عذاب من رجز» سيء العذاب «أليم» مؤلم بالجر والرفع صفة لرجز أو عذاب.'),
(3612, 34, 6, '«ويرى» يعلم «الذين أوتوا العلم» مؤمنوا أهل الكتاب كعبد الله بن سلام وأصحابه «الذي أنزل إليك من ربك» أي القرآن «هو» فصل «الحق ويهدي إلى صراط» طريق «العزيز الحميد» أي الله ذي العزة المحمود.'),
(3613, 34, 7, '«وقال الذين كفروا» أي قال بعضهم على جهة التعجب لبعض «هل ندلكم على رجل» هو محمد «ينبئكم» يخبركم أنكم «إذا مزقتم» قطعتم «كل ممزق» بمعنى تمزيق «إنكم لفي خلق جديد».'),
(3614, 34, 8, '«أفترى» بفتح الهمزة للاستفهام واستغني بها عن همزة الوصل «على الله كذبا» في ذلك «أم به جنة» جنون تخيل به ذلك قال تعالى: «بل الذين لا يؤمنون بالآخرة» المشتملة على البعث والعذاب «في العذاب» فيها «والضلال البعيد» عن الحق في الدنيا.'),
(3615, 34, 9, '«أفلم يروا» ينظروا «إلى ما بين أيديهم وما خلفهم» ما فوقهم وما تحتهم «من السماء والأرض إن نشأ نخسف بهم الأرض أو نسقط عليهم كسْفا» بسكون السين وفتحها قِطَعَا «من السماء» وفي قراءة في الأفعال الثلاثة بالياء «إن في ذلك» المرئي «لآيه لكل عبد منيب» راجع إلى ربه تدل على قدرة الله على البعث وما يشاء.'),
(3616, 34, 10, '«ولقد آتينا داود منا فضلا» نبوة وكتابا وقلنا «يا جبال أوّبي» رجعي «معه» بالتسبيح «والطير» بالنصب عطفا على محل الجبال، أي ودعوناها تسبح معه «وألنا له الحديد» فكان في يده كالعجين.'),
(3617, 34, 11, 'وقلنا «أن اعمل» منه «سابغات» دروعا كوامل يجرها لابسها على الأرض «وقدر في السرد» أي نسج الدروع قيل لصانعها سراد، أي اجعله بحيث تتناسب حلقه «واعملوا» أي آل داود معه «صالحا إني بما تعملون بصير» فأجازيكم به.'),
(3618, 34, 12, '«و» سخرنا «لسليمان الريح» وقراءة الرفع بتقدير تسخير «غدوها» مسيرها من الغدوة بمعنى الصباح إلى الزوال «شهر ورواحها» سيرها من الزوال إلى الغروب «شهر» أي مسيرته «وأسلنا» أذبنا «له عين القطر» أي النحاس فأجريت ثلاثة أيام بلياليهن كجري الماء وعمل الناس إلى اليوم مما أعطي سليمان «ومن الجن من يعمل بين يديه بإذن» بأمر «ربه ومن يزغ» يعدل «منهم عن أمرنا» له بطاعته «نذقه من عذاب السعير» النار في الآخرة، وقيل في الدنيا بأن يضربه ملك بسوط منها ضربة تحرقه.'),
(3619, 34, 13, '«يعملون له ما يشاء من محاريب» أبنية مرتفعة يصعد اليها بدرج «وتماثيل» جمع تمثال وهو كل شيء مثلته بشيء، أي صور من نحاس وزجاج ورخام، ولم يكن اتخاذ الصور حراما في شريعته «وجفان» جمع جفنه «كالجوابـ» ـي جمع جابية وهو حوض كبير، يجتمع على الجفنة ألف رجل يأكلون منها «وقدور راسيات» ثابتات لها قوائم لا تتحرك عن أماكنها تتخذ من الجبال باليمن يصعد إليها بالسلالم وقلنا «اعملوا» يا «آل داود» بطاعة الله «شكرا» له على ما أتاكم «وقليل من عبادي الشكور» العامل بطاعتي شكرا لنعمتي.'),
(3620, 34, 14, '«فلما قضينا عليه» على سليمان «الموت» أي مات ومكث قائما على عصاه حولا ميتا والجن تعمل تلك الأعمال الشاقة على عادتها لا تشعر بموته حتى أكلت الأرضة عصاه فخرّ ميتا «ما دلهم على موته إلا دابة الأرض» مصدر أرضت الخشبة بالبناء للمفعول أكلتها الأرضه «تأكل منسأته» بالهمز وتركه بألف عصاه لأنها ينسأ يطرد ويزجر بها «فلما خرَّ» ميتا «تبينت الجن» انكشف لهم «أن» مخففة أي أنهم «لو كانوا يعلمون الغيب» ومنه ما غاب عنهم من موت سليمان «ما لبثوا في العذاب المهين» العمل الشاق لهم لظنهم حياته خلاف ظنهم علم الغيب وعلم كونه سنة بحساب ما أكلته الأرضة من العصا بعد موته يوما وليلة مثلا.'),
(3621, 34, 15, '«لقد كان لسبأ» بالصرف وعدمه قبيلة سميت باسم جد لهم من العرب «في مسكنهم» باليمن «آية» دالة على قدرة الله تعالى «جنتان» بدل «عن يمين وشمال» من يمين واديهم وشماله وقيل لهم: «كلوا من رزق ربكم واشكروا له» على ما رزقكم من النعمة في أرض سبأ «بلدة طيبة» ليس فيها سباخ ولا بعوضة ولا ذبابة ولا برغوث ولا عقرب ولا حية ويمر الغريب فيها وفي ثيابه قمل فيموت لطيب هوائها «و» الله «رب غفور».'),
(3622, 34, 16, '«فأعرضوا» عن شكره وكفروا «فأرسلنا عليهم سيل العرم» جمع عرمة وهو ما يمسك الماء من بناء وغيره إلى وقت حاجته، أي سيل واديهم الممسوك بما ذكر فأغرق جنتيهم وأموالهم «وبدلناهم بجنتيهم جنتين ذواتي» تثنية ذوات مفرد على الأصل «أُكُلٍ خَمْطِ» مرِّ بشع بإضافة أكل بمعنى مأكول وتركها ويعطف عليه «وأثل وشيء من سدر قليل».'),
(3623, 34, 17, '«ذلك» التبديل «جزيناهم بما كفرواْ» بكفرهم «وهل يجازِى إلا الكفور» بالياء والنون مع كسر الزاي ونصب الكفور، أي ما يناقش إلا هو.'),
(3624, 34, 18, '«وجعلنا بينهم» بين سبأ، وهم باليمن «وبين القرى التي باركنا فيها» بالماء والشجر وهي قرى الشام التي يسيرون إليها للتجارة «قرى ظاهرة» متواصلة من اليمن إلى الشام «وقدرنا فيها السير» بحيث يقيلون في واحدة ويبيتون في أخرى إلى انتهاء سفرهم ولا يحتاجون فيه إلى حمل زاد وماء أي وقلنا «سيروا فيها ليالي وأياما آمنين» لا تخافون في ليل ولا في نهار.'),
(3625, 34, 19, '«فقالوا ربنا بَعِّدْ» وفي قراءة باعد «بين أسفارنا» إلى الشام اجعلها مفاوز ليتطاولوا على الفقراء بركوب الرواحل وحمل الزاد والماء فبطروا النعمة «وظلموا أنفسهم» بالكفر «فجعلناهم أحاديث» لمن بعدهم في ذلك «ومزقناهم كل ممزق» فرقناهم في البلاد كل التفريق «إن في ذلك» المذكور «لآيات» عبر «لكل صبَّار» عن المعاصي «شكور» على النعم.'),
(3626, 34, 20, '«ولقد صدق» بالتخفيف والتشديد «عليهم» أي الكفار منهم سبأ «إبليس ظنه» أنهم بإغوائه يتبعونه «فاتبعوه» فصدق بالتخفيف في ظنه أو صدق بالشديد ظنه أي وجده صادقا «إلا» بمعنى لكن «فريقا من المؤمنين» لبيان أي هم المؤمنون لم يتبعوه.'),
(3627, 34, 21, '«وما كان له عليهم من سلطان» تسليط «إلا لنعلم» علم ظهور «من يؤمن بالآخرة ممن هو منها في شك» فنجازي كلا منهما «وربك على شيء حفيظ» رقيب.'),
(3628, 34, 22, '«قل» يا محمد لكفار مكة «ادعوا الذين زعمتم» أي زعمتموهم آلهة «من دون الله» أي غيره لينفعوكم بزعمكم قال تعالى فيهم: «لا يملكون مثقال» وزن «ذرة» من خير أو شر «في السماوات ولا في الأرض وما لهم فيهما من شرك» شركة «وما له» تعالى «منهم» من الآلهة «من ظهير» معين.'),
(3629, 34, 23, '«ولا تنفع الشفاعة عنده» تعالى ردا لقولهم إن آلهتهم تشفع عنده «إلا لمن أذن» بفتح الهمزة وضمها «له» فيها «حتى إذا فَزَّع» بالبناء والمفعول «عن قلوبهم» كشف عنها الفزع بالإذن فيها «قالوا» قال بعضهم لبعض استبشارا «ماذا قال ربكم» فيها «قالوا» القول «الحق» أي قد أذن فيها «وهو العليّ» فوق خلقه بالقهر «الكبير» العظيم.'),
(3630, 34, 24, '«قل من يرزقكم من السماوات» المطر «والأرض» النبات «قل الله» إن لم يقولوه، لا جواب غيره «وإنا أو إياكم» أي أحد الفريقين «لعلى هدى أو في ضلال مبين» بين، في الإبهام إذا وفقوا له.'),
(3631, 34, 25, '«قل لا تسألون عما أجرمنا» أذنبنا «ولا نسأل عما تعملون» لأنا بريئون منكم.'),
(3632, 34, 26, '«قل يجمع بينا ربنا» يوم القيامة «ثم يفتح» يحكم «بيننا بالحق» فيدخل المحقين الجنة والمبطلين النار «وهو الفتاح» الحاكم «العليم» بما يحكم به.'),
(3633, 34, 27, '«قل أروني» أعلموني «الذين ألحقتم به شركاء» في العبادة «كلا» ردع لهم عن اعتقاد شريك له «بل هو الله العزيز» الغالب على أمره «الحكيم» في تدبيره لخلقه فلا يكون له شريك في ملكه.'),
(3634, 34, 28, '«وما أرسلناك إلا كافة» حال من الناس قدم للاهتمام «للناس بشيرا» مبشرا للمؤمنين بالجنة «ونذيرا» منذرا للكافرين بالعذاب «ولكن أكثر الناس» أي كفار مكة «لا يعلمون» ذلك.'),
(3635, 34, 29, '«ويقولون متى هذا الوعد» بالعذاب «إن كنتم صادقين» فيه.'),
(3636, 34, 30, '«قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون» عليه وهو يوم القيامة.'),
(3637, 34, 31, '«وقال الذين كفروا» من أهل مكة «لن نؤمن بهذا القرآن ولا بالذي بين يديه» أي تقدمه كالتوراة والإنجيل الدالين على البعث لإنكارهم له قال تعالى فيهم «ولو ترى» يا محمد «إذ الظالمون» الكافرون «موقوفون عند ربهم يرجع بعضهم إلى بعض القول يقول الذين استضعفوا» الأتباع «للذين استكبروا» الرؤساء «لولا أنتم» صددتمونا عن الإيمان «لكنا مؤمنين» بالنبي.'),
(3638, 34, 32, '«قال الذين استكبروا للذين استضعفوا أنحن صددناكم عن الهدى بعد إذ جاءكم» لا «بل كنتم مجرمين» في أنفسكم.'),
(3639, 34, 33, '«وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار» أي مكر فيهما منكم بنا «إذ تأمروننا أن نكفر بالله ونجعل له أندادا» شركاء «وأسرُّوا» أي الفريقين «الندامة» على ترك الإيمان به «لما رأوا العذاب» أي أخفاها كل عن رفيقه مخافة التعيير «وجعلنا الأغلال في أعناق الذين كفروا» في النار «هل» ما «يجزون إلا» جزاء «ما كانوا يعملون» في الدنيا.'),
(3640, 34, 34, '«وما أرسلنا في قرية من نذير إلا قال مترفوها» رؤساؤها المتنعمون «إنا بما أرسلتم به كافرون».'),
(3641, 34, 35, '«وقالوا نحن أكثر أموالا وأولادا» ممن آمن «وما نحن بمعذبين».'),
(3642, 34, 36, '«قل إن ربي يبسط الرزق» يوسعه «لمن يشاء» امتحانا «ويقدر» يضيقه لمن يشاء ابتلاءً «ولكن أكثر الناس» أي كفار مكة «لا يعلمون» ذلك.'),
(3643, 34, 37, '«وما أموالكم ولا أولادكم بالتي تقربكم عندنا زلفى» قربى، أي تقريبا «إلا» لكن «من آمن وعمل صالحا فأولئك لهم جزاءُ الضعف بما عملوا» أي جزاء العمل: الحسنة مثلا بعشر فأكثر «وهم في الغرفات» من الجنة «آمنون» من الموت وغيره، وفي قراءة الغرفة بمعنى الجمع.'),
(3644, 34, 38, '«والذين يسعوْن في آياتنا» القرآن بالإبطال «معجِّزين» لنا مقدّرين عجزنا وأنهم يفوتوننا «أولئك في العذاب محضرون».'),
(3645, 34, 39, '«قل إن ربي يبسط الرزق» يوسعه «لمن يشاء من عباده» امتحانا «ويقدر» يضيقه «له» بعد البسط أو لمن يشاء ابتلاءً «وما أنفقتم من شيء» في الخير «فهو يخلفه وهو خير الرازقين» يقال: كل إنسان يرزق عائلته، أي من رزق الله.'),
(3646, 34, 40, '«و» اذكر «يوم نحشرهم جميعا» أي المشركين «ثم نقول للملائكة أهؤلاء إياكم» بتحقيق الهمزتين وإبدال الأولى ياء وإسقاطها «كانوا يعبدون».'),
(3647, 34, 41, '«قالوا سبحانك» تنزيها لك عن الشريك «أنت ولينا من دونهم» أي لا موالاة بيننا وبينهم من جهتنا «بل» للانتقال «كانوا يعبدون الجن» الشياطين، أي يطيعونهم في عبادتهم إيانا «أكثرهم بهم مؤمنون» مصدقون فيما يقولون لهم.'),
(3648, 34, 42, 'قال تعالى: «فاليوم لا يملك بعضكم لبعض» أي بعض المعبودين لبعض العابدين «نفعا» شفاعة «ولاضرا» تعذيبا «ونقول للذين ظلموا» كفروا «ذوقوا عذاب النار التي كنتم بها تكذبون».'),
(3649, 34, 43, '«وإذا تتلى عليهم آياتنا» القرآن «بيّنات» واضحات بلسان نبينا محمد صلى الله عليه وسلم «قالوا ما هذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم» من الأصنام «وقالوا ما هذا» القرآن «إلا إفك» كذب «مفترى» على الله «وقال الذين كفروا للحق» القرآن «لما جاءهم إن» ما «هذا إلا سحرٌ مبين» بيّن.'),
(3650, 34, 44, 'قال تعالى: «وما آتيناهم من كتب يدرسونها وما أرسلنا إليهم قبلك من نذير» فمن أين كذبوك.'),
(3651, 34, 45, '«وكذب الذين من قبلهم وما بلغوا» أي هؤلاء «معشار ما آتيناهم» من القوة وطول العمر وكثرة المال «فكذبوا رسلي» إليهم «فكيف كان نكير» إنكاري عليهم العقوبة والإهلاك، أي هو واقع موقعه.'),
(3652, 34, 46, '«قل إنما أعظكم بواحدة» هي «أن تقوموا لله» أي لأجله «مثنى» أي اثنين اثنين «وفرادى» واحدا واحدا «ثم تتفكروا» فتعلموا «ما بصاحبكم» محمد «من جنة» جنون «إن» ما «هو إلا نذير لكم بين يدي» أي قبل «عذاب شديد» في الآخرة إن عصيتموه.'),
(3653, 34, 47, '«قل» لهم «ما سألتكم» على الإنذار والتبليغ «من أجر فهو لكم» أي لا أسألكم عليه أجرا «إن أجري» ما ثوابي «إلا على الله وهو على كل شيء شهيد» مطلع يعلم صدقي.'),
(3654, 34, 48, '«قل إن ربي يقذف بالحق» يلقيه إلى أنبيائه «علاًم الغيوب» ما غاب عن خلقه في السماوات والأرض.'),
(3655, 34, 49, '«قل جاء الحق» الإسلام «وما يبدئ الباطل» الكفر «وما يعيد» أي لم يبق له أثر.'),
(3656, 34, 50, '«قل إن ضللت» عن الحق «فإنما أضل على نفسي» أي إثم ضلالي عليها «وإن اهتديت فيما يوحي إليَّ ربي» من القرآن والحكمة «إنه سميع» للدعاء «قريب».'),
(3657, 34, 51, '«ولوْ ترى» يا محمد «إذْ فزعوا» عند البعث لرأيت أمرا عظيما «فلا فوت» لهم منا، أي لا يفوتوننا «وأخذوا من مكان قريب» أي القبور.'),
(3658, 34, 52, '«وقالوا آمنا به» بمحمد أو القرآن «وأنَّى لهم التناوش» بواو وبالهمزة بدلها، أي تناول الإيمان «من مكان بعيد» عن محله إذ هم في الآخرة ومحله الدنيا.'),
(3659, 34, 53, '«وقد كفروا به من قبل» في الدنيا «ويقذفون» يرمون «بالغيب من مكان بعيد» أي بما غاب علمه عنهم غيبة بعيدة حيث قالوا في النبي ساحر، شاعر، كاهن، وفي القرآن سحر، شعر، كهانة.'),
(3660, 34, 54, '«وحيلَ بينهم وبين ما يشتهون» من الإيمان، أي قبوله «كما فُعل بأشياعهم» أشباههم في الكفر «من قبل» أي قبلهم «إنهم كانوا في شك مريب» موقع في الريبة لهم فيما آمنوا به الآن ولم يعتدوّا بدلائله في الدنيا.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, '«الحمد لله» حمد تعالى نفسه بذلك كما بُيّن في أول سورة سبأ «فاطر السماوات والأرض» خالقهما على غير مثال سبق «جاعل الملائكة رسلا» إلى الأنبياء «أولي أجنحة مثنى وثلاث ورباع يزيد في الخلق» في الملائكة وغيرها «ما يشاء إن الله على كل شيء قدير».'),
(3662, 35, 2, '«ما يفتح الله للناس من رحمة» كرزق ومطر «فلا ممسك لها وما يمسك» من ذلك «فلا مرسل له من بعده» أي بعد إمساكه «وهو العزيز» الغالب على أمره «الحكيم» في فعله.'),
(3663, 35, 3, '«يا أيها الناس» أي أهل مكة «اذكروا نعمة الله عليكم» بإسكانكم الحرم ومنع الغارات عنكم «هل من خالق» من زائدة وخالق مبتدأ «غيرُ الله» بالرفع والجر نعت لخالق لفظا ومحلا، وخبر المبتدأ «يرزقكم من السماء» المطر «و» من «الأرض» النبات، والاستفهام للتقرير، أي لا خالق رازق غيره «لا إله إلا هو فأنَّى تؤفكون» من أين تصرفون عن توحيده مع إقراركم بأنه الخالق الرازق.'),
(3664, 35, 4, '«وإن يكذبوك» يا محمد في مجيئك بالتوحيد والبعث، والحساب والعقاب «فقد كُذِّبت رسل من قبلك» في ذلك فاصبر كما صبروا «وإلى الله ترجع الأمور» في الآخرة فيجازي المكذبين وينصر المسلمين.'),
(3665, 35, 5, '«يا أيها الناس إن وعد الله» بالبعث وغيره «حق فلا تغرنكم الحياة الدنيا» من الإيمان بذلك «ولا يغرنكم بالله» في حلمه وإمهاله «الغرور» الشيطان.'),
(3666, 35, 6, '«إن الشيطان لكم عدوٌ فاتخذوه عدوّا» بطاعة الله ولا تطيعوه «إنما يدعو حزبه» أتباعه في الكفر «ليكونوا من أصحاب السعير» النار الشديدة.'),
(3667, 35, 7, '«الذين كفروا لهم عذاب شديد والذين آمنوا وعملوا الصالحات لهم مغفرة وأجر كبير» هذا بيان ما لموافقي الشيطان وما لمخالفيه.'),
(3668, 35, 8, 'ونزل في أبي جهل وغيره «أفمن زينَ له سوء عمله» بالتمويه «فرآه حسنا» من مبتدأ خبره كمن هداه الله؟ لا، دل عليه «فإن الله يضل من يشاء ويهدي من يشاء فلا تذهب نفسك عليهم» على المزيَّن لهم «حسرات» باغتمامك ألا يؤمنوا «إن الله عليم بما يصنعون» فيجازيهم عليه.'),
(3669, 35, 9, '«والله الذي أرسل الرياح» وفي قراءة الريح «فتثير سحابا» المضارع لحكاية الحال الماضية، أي تزعجه «فسقناه» فيه التفات عن الغيبة «إلى بلد ميت» بالتشديد والتخفيف لا نبات بها «فأحيينا به الأرض» من البلد «بعد موتها» يبسها، أي أنبتنا به الزرع والكلأ «كذلك النشور» أي البعث والإحياء.'),
(3670, 35, 10, '«من كان يريد العزة فلله العزة جميعا» أي في الدنيا والآخرة فلا تنال منه إلا بطاعته فليطعه «إليه يصعد الكلم الطيب» يعلمه وهو لا إله إلا الله ونحوها «والعمل الصالح يرفعه» يقبله «والذين يمكرون» المكرات «السيئات» بالنبي في دار الندوة من تقييده أو قتله أو إخراجه كما ذكر في الأنفال «لهم عذاب شديد ومكر أولئك هو يبور» يهلك.'),
(3671, 35, 11, '«والله خلقكم من تراب» بخلق أبيكم آدم منه «ثم من نطفة» أي منيّ بخلق ذريته منها «ثم جعلكم أزواجا» ذكورا وإناثا «وما تحمل من أنثى ولا تضع إلا بعلمه» حال، أي معلومة له «وما يعمَّر من معمَّر» أي ما يزاد في عمر طويل العمر «ولا ينقص من عمره» أي ذلك المعمَّر أو معمَّر آخر «إلا في كتاب» هو اللوح المحفوظ «إن ذلك على الله يسير» هيِّن.'),
(3672, 35, 12, '«وما يستوي البحران هذا عذب فرات» شديد العذوبة «سائغ شرابه» شربه «وهذا ملح أجاج» شديد الملوحة «ومن كل» منهما «تأكلون لحما طريا» هو السمك «وتستخرجون» من الملح، وقيل منهما «حلية تلبسونها» هي اللؤلؤ والمرجان «وترى» تُبصر «الفلك» السفن «فيه» في كل منهما «مواخر» تمخر الماء، أي تشقه بجريها فيه مقبلة ومدبرة بريح واحدة «لتبتغوا» تطلبوا «من فضله» تعالى بالتجارة «ولعلكم تشكرون» الله على ذلك.'),
(3673, 35, 13, '«يولج» يدخل الله «الليل في النهار» فيزيد «ويولج النهار» يدخله «في الليل» فيزيد «وسخر الشمس والقمر كل» منهما «يجري» في فلكه «لأجل مسمى» يوم القيامة «ذلكم الله ربكم له الملك والذين تدعون» تعبدون «من دونه» أي غيره وهم الأصنام «ما يملكون من قِطْمير» لفاقة النواة.'),
(3674, 35, 14, '«إن تدعوهم لا يسمعوا دعاءكم ولو سمعوا» فرضا «ما استجابوا لكم» ما أجابوكم «ويوم القيامة يكفرون بشرككم» بإشراككم إياهم مع الله، أي يتبرءون منكم ومن عبادتكم إياهم «ولا يُنبئك» بأحوال الدارين «مثل خبير» عالم هو الله تعالى.'),
(3675, 35, 15, '«يا أيها الناس أنتم الفقراء إلى الله» بكل حال «والله هو الغنيُّ» عن خلقه «الحميد» المحمود في صنعه بهم.'),
(3676, 35, 16, '«إن يشأ يذهبكم ويأت بخلق جديد» بدلكم.'),
(3677, 35, 17, '«وما ذلك على الله بعزيز» شديد.'),
(3678, 35, 18, '«ولا تزر» نفس «وازرة» آثمة، أي لا تحمل «وزرَ» نفس «أخرى وإن تدع» نفس «مثقلة» بالوزر «إلى حملها» منه أحدا ليحمل بعضه «لا يُحمل منه شيء ولو كان» المدعو «ذا قربى» قرابة كالأب والابن وعدم الحمل في الشقين حكم من الله «إنما تنذر الذين يخشون ربهم بالغيب» أي يخافونه وما رأوه لأنهم المنتفعون بالإنذار «وأقاموا الصلاة» أداموها «ومن تزكَّى» تطهر من الشرك وغيره «فإنما يتزكَّى لنفسه» فصلاحه مختص به «وإلى الله المصير» المرجع فيجزي بالعمل في الآخرة.'),
(3679, 35, 19, '«وما يستوي الأعمى والبصير» الكافر والمؤمن.'),
(3680, 35, 20, '«ولا الظلمات» الكفر «ولا النور» الإيمان.'),
(3681, 35, 21, '«ولا الظل ولا الحرور» الجنة والنار.'),
(3682, 35, 22, '«وما يستوي الأحياء ولا الأموات» المؤمنون ولا الكفار، وزيادة لا في الثلاثة تأكيد «إن الله يسمع من يشاء» هدايته فيجيبه بالإيمان «وما أنت بمسمع من في القبور» أي الكفار شبههم بالموتى فيجيبون.'),
(3683, 35, 23, '«إن» ما «أنت إلا نذير» منذر لهم.'),
(3684, 35, 24, '«إنا أرسلناك بالحق» بالهدى «بشيرا» من أجاب إليه «ونذيرا» من لم يجب إليه «وإن» ما «من أمة إلا خلا» سلف «فيها نذير» نبي ينذرها.'),
(3685, 35, 25, '«وإن يكذبوك» أي أهل مكة «فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات» المعجزات «وبالزبر» كصحف إبراهيم «وبالكتاب المنير» هو التوراة والإنجيل، فاصبر كما صبروا.'),
(3686, 35, 26, '«ثم أخذت الذين كفروا» بتكذيبهم «فكيف كان نكير» إنكاري عليهم بالعقوبة والإهلاك، أي هو واقع موقعه.'),
(3687, 35, 27, '«ألم ترَ» تعلم «أن الله أنزل من السماء ماءً فأخرجنا» فيه التفات عن الغيبة «به ثمرات مختلفا ألوانها» كأخضر وأحمر وأصفر وغيرها «ومن الجبال جدد» جمع جدة، طريق في الجبل وغيره «بيض وحمر» وصفر «مختلف ألوانها» بالشدة والضعف «وغرابيب سود» عطف على جدد، أي صخور شديدة السواد، يقال كثيرا: أسود غربيب، وقليلا: غربيب أسود.'),
(3688, 35, 28, '«ومن الناس والدواب والأنعام مختلف ألوانه كذلك» كاختلاف الثمار والجبال «إنما يخشى الله من عباده العلماءُ» بخلاف الجهال ككفار مكة «إن الله عزيز» في ملكه «غفور» لذنوب عباده المؤمنين.'),
(3689, 35, 29, '«إن الذين يتلون» يقرءُون «كتاب الله وأقاموا الصلاة» أداموها «وأنفقوا مما رزقناهم سرا وعلانية» زكاة وغيرها «يرجون تجارة لن تبور» تهلك.'),
(3690, 35, 30, '«ليوفيهم أجورهم» ثواب أعمالهم المذكورة «ويزيدهم من فضله إنه غفور» لذنوبهم «شكور» لطاعتهم.'),
(3691, 35, 31, '«والذي أوحينا إليك من الكتاب» القرآن «هو الحق مصدقا لما بين يديه» تقدمه من الكتب «إن الله بعباده لخبير بصير» عالم بالبواطن والظواهر.'),
(3692, 35, 32, '«ثم أورثنا» أعطينا «الكتاب» القرآن «الذين اصطفينا من عبادنا» وهم أمتك «فمنهم ظالم لنفسه» بالتقصير في العمل به «ومنهم مقتصد» يعمل به أغلب الأوقات «ومنهم سابق بالخيرات» يضم إلى العلم التعليم والإرشاد إلى العمل «بإذن الله» بإرادته «ذلك» أي إيراثهم الكتاب «هو الفضل الكبير».'),
(3693, 35, 33, '«جنات عدن» أي إقامة «يدخلونها» الثلاثة بالبناء وللمفعول خبر جنات المبتدأ «يُحلَّون» خبر ثان «فيها من» بعض «أساور من ذهب ولؤلؤا» مرصع بالذهب «ولباسهم فيها حرير».'),
(3694, 35, 34, '«وقالوا الحمد لله الذي أذهب عنا الحَزَنَ» جميعه «إن ربنا لغفور» للذنوب «شكور» للطاعة.'),
(3695, 35, 35, '«الذي أحلّنا دار المقامة» الإقامة «من فضله لا يمسنا فيها نصب» تعب «ولا يمسنا فيها لغوب» إعياء من التعب لعدم التكليف فيها، وذكر الثاني التابع للأول للتصريح بنفيه.'),
(3696, 35, 36, '«والذين كفروا لهم نار جهنم لا يقضى عليهم» بالموت «فيموتوا» يستريحوا «ولا يُخفف عنهم من عذابها» طرفة عين «كذلك» كما جزيناهم «يجزِى كلَّ كفورِ» كافر بالياء والنون المفتوحة مع كسر الزاي ونصب كل.'),
(3697, 35, 37, '«وهم يصطرخون فيها» يستغيثون بشدة وعويل يقولون «ربنا أخرجنا» منها «نعمل صالحا غير الذي كنا نعمل» فيقال لهم «أوَ لم نعمّركم ما» وقتا «يتذكر فيه من تذكر وجاءكم النذير» الرسول فما أجبتم «فذوقوا فما للظالمين» الكافرين «من نصير» يدفع العذاب عنهم.'),
(3698, 35, 38, '«إن الله عالم غيب السماوات والأرض إنه عليم بذات الصدور» بما في القلوب، فعلمه بغيره أولى بالنظر إلى حال الناس.'),
(3699, 35, 39, '«هو الذي جعلكم خلائف في الأرض» جمع خليفة، أي يخلف بعضكم بعضا «فمن كفر» منكم «فعليه كفره» أي وبال كفره «ولا يزيد الكافرين كفرهم عند ربهم إلا مقتا» غضبا «ولا يزيد الكافرين كفرهم إلا خسارا» للآخرة.'),
(3700, 35, 40, '«قل أرأيتم شركاءكم الذين تدعون» تعبدون «من دون الله» أي غيره، وهم الأصنام الذين زعمتم أنهم شركاء الله تعالى «أروني» أخبروني «ماذا خلقوا من الأرض أم لهم شرك» شركة مع الله «في» خلق «السماوات» «أم آتيناهم كتابا فهم على بينة» حجة «منه» بأن لهم معه شركة «بل إن» ما «يعد الظالمون» الكافرون «بعضهم بعضا إلا غرورا» باطلا بقولهم الأصنام تشفع لهم.'),
(3701, 35, 41, '«إن الله يمسك السماوات والأرض أن تزولا» أي يمنعهما من الزوال «ولئن» لام قسم «زالتا إن» ما «أمسكهما» يمسكهما «من أحد من بعده» أي سواه «إنه كان حليما غفورا» في تأخير عقاب الكفار.'),
(3702, 35, 42, '«وأقسموا» أي كفار مكة «بالله جهد أيمانهم» غاية اجتهادهم فيها «لئن جاءهم نذير» رسول «ليكوننَّ أهدى من إحدى الأمم» اليهود والنصارى وغيرهم، أي أيَّ واحدة منها لما رأوا من تكذيب بعضهم بعضا، إذ قالت اليهود: ليست النصارى على شيء، «فلما جاءهم نذير» محمد صلى الله عليه وسلم «ما زادهم» مجيئه «إلا نفورا» تباعدا عن الهدى.'),
(3703, 35, 43, '«استكبارا في الأرض» عن الإيمان مفعول له «ومكر» العمل «السيء» من الشرك وغيره «ولا يحيق» يحيط «المكر السيء إلا بأهله» وهو الماكر، ووصف المكر بالسيء أصل، وإضافته إليه قيل استعمال آخر قدر فيه مضاف حذرا من الإضافة إلى الصفة «فهل ينظرون» ينتظرون «إلا سُنَّةَّ الأولين» سنة الله فيهم من تعذيبهم بتكذيبهم رسلهم «فلن تجد لسنَّةِ الله تبديلا ولن تجد لسنَّةِ الله تحويلا» أي لا يبدل بالعذاب غيره ولا يحول إلى غير مستحقه.'),
(3704, 35, 44, '«أوْ لم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم وكانوا أشد منهم قوة» فأهلكهم الله بتكذيبهم رسلهم «وما كان الله ليعجزه من شيء» يسبقه ويفوته «في السماوات ولا في الأرض إنه كان عليما» أي بالأشياء كلها «قديرا» عليها.'),
(3705, 35, 45, '«ولو يؤاخذ الله الناس بما كسبوا» من المعاصي «ما ترك على ظهرها» أي الأرض «من دابة» نسمة تدبّ عليها «ولكن يؤخرهم إلى أجل مسمى» أي يوم القيامة «فإذا جاء أجلهم فإن الله كان بعباده بصيرا» فيجازيهم على أعمالهم، بإثابة المؤمنين وعقاب الكافرين.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, '«يس» الله أعلم بمراده به.'),
(3707, 36, 2, '«والقرآن الحكيم» المحكم بعجيب النظم، وبديع المعاني.'),
(3708, 36, 3, '«إنك» يا محمد «لمن المرسلين».'),
(3709, 36, 4, '(على) متعلق بما قبله (صراط مستقيم) أي طريق الأنبياء قبلك التوحيد والهدى، والتأكيد بالقسم وغيره رد لقول الكفار له "لست مرسلا".'),
(3710, 36, 5, '«تنزيل العزيز» في ملكه «الرحيم» بخلقه خبر مبتدأ مقدر، أي القرآن.'),
(3711, 36, 6, '«لتنذرَ» به «قوما» متعلق بتنزيل «ما أنذر آباؤهم» أي لم ينذروا في زمن الفترة «فهم» أي القوم «غافلون» عن الإيمان والرشد.'),
(3712, 36, 7, '«لقد حق القول» وجب «على أكثرهم» بالعذاب «فهم لا يؤمنون» أي الأكثر.'),
(3713, 36, 8, '«إنا جعلنا في أعناقهم أغلالا» بأن تضم اليها الأيدي لأن الغل يجمع اليد إلى العنق «فهي» أي الأيدي مجموعة «إلى الأذقان» جمع ذقن، وهي مجتمع اللحيين «فهم مقمحون» رافعون رؤوسهم لا يستطيعون خفضها، وهذا تمثيل، والمراد أنهم لا يذعنون للإيمان ولا يخفضون رؤوسهم له.'),
(3714, 36, 9, '«وجعلنا من بين أيديهم سدا ومن خلفهم سدا» بفتح السين وضمها في الموضعين «فأغشيناهم فهم لا يبصرون» تمثيل أيضا لسدّ طرق الإيمان عليهم.'),
(3715, 36, 10, '«وسواء عليهم أأنذرتهم» بتحقيق الهمزتين وإبدال الثانية ألفا وتسهيلها وإدخال ألف بين المسهلة والأخرى وتركه «أم لم تنذرهم لا يؤمنون».'),
(3716, 36, 11, '«إنما تنذر» ينفع إنذارك «من أتَّبع الذكر» القرآن «وخشي الرحمن بالغيب» خافه ولم يره «فبشّره بمغفرة وأجر كريم» هو الجنة.'),
(3717, 36, 12, '«إنا نحن نحي الموتى» للبعث «ونكتب» في اللوح المحفوظ «ما قدَّموا» في حياتهم من خير وشر ليجازوا عليه «وآثارهم» ما استنَّ به بعدهم «وكل شيء» نصبه بفعل يفسره «أحصيناه» ضبطناه «في إمام مبين» كتاب بيّن، هو اللوح المحفوظ.'),
(3718, 36, 13, '«واضرب» اجعل «لهم مثلا» مفعول أول «أصحاب» مفعول ثان «القرية» أنطاكية «إذ جاءها» إلى آخره بدل اشتمال من أصحاب القرية «المرسلون» أي رسل عيسى.'),
(3719, 36, 14, '«إذْ أرسلنا إليهم اثنين فكذبوهما» إلى آخره بدل من إذ الأولى «فعَزَزْنا» بالتخفيف والتشديد: قوَّينا الاثنين «بثالث فقالوا إنا إليكم مرسلون».'),
(3720, 36, 15, '«قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمن من شيء إن» ما «أنتم إلا تكذبون».'),
(3721, 36, 16, '«قالوا ربنا يعلم» جار مجرى القسم، وزيد التأكيد به وباللام على ما قبله لزيادة الإنكار في «إنا إليكم لمرسلون».'),
(3722, 36, 17, '«وما علينا إلا البلاغ المبين» التبليغ المبين الظاهر بالأدلة الواضحة وهي إبراء الأكمه والأبرص والمريض وإحياء الميت.'),
(3723, 36, 18, '«قالوا إنا تطيرنا» تشاءَمنا «بكم» لانقطاع المطر عنا بسببكم «لئن» لام قسم «لم تنتهوا لنرجمنكم» بالحجارة «وليمسنكم منا عذاب أليم» مؤلم.'),
(3724, 36, 19, '«قالوا طائركم» شؤمكم «معكم» بكفركم «أإن» همزة استفهام دخلت على إن الشرطية وفى همزتها التحقيق والتسهيل وإدخال ألف بينها بوجهيها وبين الأخرى «ذكرتم» وعظتم وخوِّفتم، وجواب الشرط محذوف، أي تطيرتم وكفرتم وهو محل الاستفهام، والمراد به التوبيخ «بل أنتم قوم مسرفون» متجاوزون الحدَّ بشرككم.'),
(3725, 36, 20, '«وجاء من أقصا المدينة رجل» هو حبيب النجار كان قد آمن بالرسل ومنزله بأقصى البلد «يسعى» يشتد عدوا لما سمع بتكذيب القوم الرسلَ «قال يا قوم اتبعوا المرسلين».'),
(3726, 36, 21, '«اتبعوا» تأكيد للأول «مَن لا يسألكم أجرا» على رسالته «وهم مهتدون» فقيل له: أنت على دينهم.'),
(3727, 36, 22, 'فقال «وما لي لا أعبد الذي فطرني» خلقني، أي لا مانع لي من عبادته الموجود مقتضيها وأنتم كذلك «وإليه ترجعون» بعد الموت فيجازيكم بكفركم.'),
(3728, 36, 23, '«أأتخذ» في الهمزتين ما تقدم في أأنذرتهم وهو استفهام بمعنى النفي «من دونه» أي غيره «آلهة» أصناما «إن يُردْن الرحمن بضر لا تغن عني شفاعتهم» التي زعمتموها «شيئا ولا ينقذون» صفة آلهة.'),
(3729, 36, 24, '«إني إذا» أي إن عبدت غير الله «لفي ضلال مبين» بيَّن.'),
(3730, 36, 25, '«إني آمنت بربكم فاسمعون» أي اسمعوا قولي، فرجموه فمات.'),
(3731, 36, 26, '«قيل» له عند موته «ادخل الجنة» وقيل دخلها حيا «قال يا» حرف تنبيه «ليت قومي يعلمون».'),
(3732, 36, 27, '«بما غفر لي ربي» بغفرانه «وجعلني من المكرمين».'),
(3733, 36, 28, '«وما» نافية «أنزلنا على قومه» أي حبيب «من بعده» بعد موته «من جند من السماء» أي ملائكة لإهلاكهم «وما كنا منزلين» ملائكة لإهلاك أحد.'),
(3734, 36, 29, '«إن» ما «كانت» عقوبتهم «إلا صيحة واحدة» صاح بهم جبريل «فإذا هم خامدون» ساكنون ميتون.'),
(3735, 36, 30, '«يا حسرة على العباد» هؤلاء ونحوهم ممن كذبوا الرسل فأهلكوا، وهي شدة التألم ونداؤها مجاز، أي هذا أوانك فاحضري «ما يأتيهم من رسول إلا كانوا به يستهزءُون» مسوق لبيان سببها لاشتماله على استهزائهم المؤدى إلى إهلاكهم المسبب عنه الحسرة.'),
(3736, 36, 31, '(ألم يروا) أي أهل مكة القائلون للنبي "لست مرسلا" والاستفهام للتقرير أي علموا (كم) خبرية بمعنى كثيرا معمولة لها بعدها معلقة لما قبلها عن العمل، والمعنى إنا (أهلكنا قبلهم) كثيرا (من القرون) الأمم (أنهم) أي المهلكين (إليهم) أي المكذبين (لا يرجعون) أفلا يعتبرون بهم، وأنه الخ بدل مما قبله برعاية المعنى المذكور.'),
(3737, 36, 32, '«وإن» نافية أو مخففة «كل» أي كل الخلائق مبتدأ «لما» بالتشديد بمعنى إلا، أو بالتخفيف، فاللام فارقة وما مزيدة «جميع» خبر المبتدأ، أي مجموعون «لدينا» عندنا في الموقف بعد بعثهم «محضرون» للحساب خبر ثان.'),
(3738, 36, 33, '«وآية لهم» على البعث خبر مقدم «الأرض الميتة» بالتخفيف والتشديد «أحييناها» بالماء مبتدأ «وأخرجنا منها حبا» كالحنطة «فمنه يأكلون».'),
(3739, 36, 34, '«وجعلنا فيها جنات» بساتين «من نخيلٍ وأعنابٍ وفجَّرنا فيها من العيون» أي بعضها.'),
(3740, 36, 35, '«ليأكلوا من ثمره» بفتحتين وضمتين، أي ثمر المذكور من النخيل وغيره «وما عملته أيديهم» أي لم تعمل الثمر «أفلا يشكرون» أنعمه تعالى عليهم.'),
(3741, 36, 36, '«سبحان الذي خلق الأزواج» الأصناف «كلها مما تنبت الأرض» من الحبوب وغيرها «ومن أنفسهم» من الذكور والإناث «ومما لا يعلمون» من المخلوقات العجيبة الغريبة.'),
(3742, 36, 37, '«وآية لهم» على القدرة العظيمة «الليل نسلخ» نفصل «منه النهار فإذا هم مظلمون» داخلون في الظلام.'),
(3743, 36, 38, '«والشمس تجري» إلى آخره من جملة الآية لهم آية أخرى والقمر كذلك «لمستقر لها» أي إليه لا تتجاوزه «ذلك» أي جريها «تقدير العزيز» في ملكه «العليم» بخلقه.'),
(3744, 36, 39, '«والقمرُ» بالرفع والنصب وهو منصوب بفعل يفسره ما بعده «قدَرناه» من حيث سيره «منازل» ثمانية وعشرين منزلا في ثمان وعشرين ليلة من كل شهر، ويستتر ليلتين إن كان الشهر ثلاثين يوما وليلة إن كان تسعة وعشرين يوما «حتى عاد» في آخر منازله في رأي العين «كالعرجون القديم» أي كعود الشماريخ إذا عتق فإنه يرق ويتقوس ويصفر.'),
(3745, 36, 40, '«لا الشمس ينبغي» يسهل ويصح «لها أن تدرك القمر» فتجتمع معه في الليل «ولا الليل سابق النهار» فلا يأتي قبل انقضائه «وكل» تنويه عوض عن المضاف إليه من الشمس والقمر والنجوم «في فلك» مستدير «يسبحون» يسيرون نزلوا منزلة العقلاء.'),
(3746, 36, 41, '«وآية لهم» على قدرتنا «أنا حملنا ذريتهم» وفي قراءة ذرياتهم، أي آباءهم الأصول «في الفلك» أي سفينة نوح «المشحون» المملوء.'),
(3747, 36, 42, '«وخلقنا لهم من مثله» أي مثل فلك نوح وهو ما عملوه على شكله من السفن الصغار والكبار بتعليم الله تعالى «ما يركبون» فيه.'),
(3748, 36, 43, '«وإن نشأ نغرقهم» مع إيجاد السفن «فلا صريخ» مغيث «لهم ولا هم ينقذون» ينجون.'),
(3749, 36, 44, '«إلا رحمة منا ومتاعا إلى حين» أي لا ينجيهم إلا رحمتنا لهم وتمتيعنا إياهم بلذاتهم إلى انقضاء آجالهم.'),
(3750, 36, 45, '«وإذا قيل لهم اتقوا ما بين أيديكم» من عذاب الدنيا كغيرهم «وما خلفكم» من عذاب الآخرة «لعلكم ترحمون» أعرضوا.'),
(3751, 36, 46, '«وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين».'),
(3752, 36, 47, '«وإذا قيل» أي قال فقراء الصحابة «لهم أنفقوا» علينا «مما رزقكم الله» من الأموال «قال الذين كفروا للذين آمنوا» استهزاءً بهم «أنطعم من لو يشاء الله أطعمه» في معتقدكم هذا «إن» ما «أنتم» في قولكم لنا ذلك مع معتقدكم هذا «إلا في ضلال مبين» بيَّن وللتصريح بكفرهم موقع عظيم.'),
(3753, 36, 48, '«ويقولون متى هذا الوعد» بالبعث «إن كنتم صادقين» فيه.'),
(3754, 36, 49, 'قال تعالى: «ما ينظرون» أي ينتظرون «إلا صيحة واحدة» وهي نفخة إسرافيل الأولى «تأخذهم وهم يخصِّمون» بالتشديد أصله يختصمون نقلت حركة التاء إلى الحاء وأدغمت في الصاد، أي وهم في غفلة عنها بتخاصم وتبايع وأكل وشرب وغير ذلك، وفي قراءة يخصمون كيضربون، أي يخصم بعضهم بعضا.'),
(3755, 36, 50, '«فلا يستطيعون توصية» أي أن يوصوا «ولا إلى أهلهم يرجعون» من أسواقهم وأشغالهم بل يموتون فيها.'),
(3756, 36, 51, '«ونفخ في الصور» هو قرن النفخة الثانية للبعث، وبين النفختين أربعون سنة «فإذا هم» أي المقبورين «من الأجداث» القبور «إلى ربهم ينسلون» يخرجون بسرعة.'),
(3757, 36, 52, '«قالوا» أي الكفار منهم «يا» للتنبيه «ويلنا» هلاكنا وهو مصدر لا فعل له من لفظه «من بعثنا من مرقدنا» لأنهم كانوا بين النفختين نائمين لم يعذبوا «هذا» أي البعث «ما» أي الذي «وعد» به «الرحمن وصدق» فيه «المرسلون» أقروا حين لا ينفعهم الإقرار، وقيل: يقال لهم ذلك.'),
(3758, 36, 53, '«إن» ما «كانت إلا صيحة واحدة فإذا هم جميع لدينا» عندنا «محضرون».'),
(3759, 36, 54, '«فاليوم لا تظلم نفسٌ شيئا ولا تجزون إلا» جزاء «ما كنتم تعملون».'),
(3760, 36, 55, '«إن أصحاب الجنة اليوم في شغْل» بسكون الغين وضمها عما فيه أهل النار مما يتلذذون به كافتضاض الأبكار، لا شغل يتعبون فيه، لأن الجنة لا نصب فيها «فاكهون» ناعمون خبر ثان لإن، والأول في شغل.'),
(3761, 36, 56, '«هم» مبتدأ «وأزواجهم في ظلال» جمع ظلة أو ظل خبر أي لا تصيبهم الشمس «على الأرائك» جمع أريكة، وهو السرير في الحجلة أو الفرش فيها «متكئون» خبر ثان متعلق بعلى.'),
(3762, 36, 57, '«لهم فيها فاكهة ولهم» فيها «ما يدَّعون» يتمنون.'),
(3763, 36, 58, '«سلام» مبتدأ «قولا» أي بالقول خبره «من رب رحيم» بهم، أي يقول لهم: سلام عليكم.'),
(3764, 36, 59, '«و» يقول «امتازوا اليوم أيها المجرمون» أي انفردوا عن المؤمنين عند اختلاطهم بهم.'),
(3765, 36, 60, '«ألم أعهد إليكم» آمركم «يابني آدم» على لسان رسلي «أن لا تعبدوا الشيطان» لا تطيعوه «إنه لكم عدوٌ مبين» بيَّن العداوة.'),
(3766, 36, 61, '«وأن اعبدوني» وحِّدوني وأطيعوني «هذا صراط» طريق «مستقيم».'),
(3767, 36, 62, '«ولقد أضل منكم جبلا» خلقا جمع جبيل كقديم، وفي قراءة بضم الباء «كثيرا أفلم تكونوا تعقلون» عداوته وإضلاله أو ما حل بهم من العذاب فتؤمنون، ويقال لهم في الآخرة.'),
(3768, 36, 63, '«هذه جهنم التي كنتم توعدون» بها.'),
(3769, 36, 64, '«اصلوْها اليوم بما كنتم تكفرون».'),
(3770, 36, 65, '(اليوم نختم على أفواههم) أي الكفار لقولهم "والله ربنا ما كنا مشركين" (وتكلمنا أيديهم وتشهد أرجلهم) وغيرها (بما كانوا يكسبون) فكل عضو ينطق بما صدر منه.'),
(3771, 36, 66, '«ولو نشاء لطمسنا على أعينهم» لأعميناها طمسا «فاستَبَقُوا» ابتدروا «الصراط» الطريق ذاهبين كعادتهم «فأنَّا» فكيف «يبصرون» حينئذ؟ أي لا يبصرون.'),
(3772, 36, 67, '«ولو نشاء لمسخناهم» قردة وخنازير أو حجارة «على مكانتهم» وفي قراءة مكاناتهم جمع مكانة بمعنى مكان أي في منازلهم «فما استطاعوا مضيا ولا يرجعون» أي لم يقدروا على ذهاب ولا مجيء.'),
(3773, 36, 68, '«ومن نعمِّره» بإطالة أجله «نَنْكُسْهُ» وفي قراءة بالتشديد من التنكيس «في الخلق» فيكون بعد قوته وشبابه ضعيفا وهرما «أفلا يعقلون» أن القادر على ذلك المعلوم عندهم قادر على البعث فيؤمنون، وفي قراءة بالتاء.'),
(3774, 36, 69, '«وما علمناه» أي النبي «الشعر» رد لقولهم إن ما أتى به من القرآن شعر «وما ينبغي» يسهل «له» الشعر «إن هو» ليس الذي أتى به «إلا ذكر» عظة «وقرآن مبين» مظهر للأحكام وغيرها.'),
(3775, 36, 70, '«لينذر» بالياء والتاء به «من كان حيا» يعقل ما يخاطب به وهم المؤمنون «ويحق القول» بالعذاب «على الكافرين» وهم كالميتين لا يعقلون ما يخاطبون به.'),
(3776, 36, 71, '«أوَ لم يروْا» يعلموا والاستفهام للتقرير والواو الداخلة عليه للعطف «أنا خلقنا لهم» في جملة الناس «مما عملت أيدينا» عملناه بلا شريك ولا معين «أنعاما» هي الإبل والبقر والغنم «فهم لها مالكون» ضابطون.'),
(3777, 36, 72, '«وذللناها» سخرناها «لهم فمنها ركوبهم» مركوبهم «ومنها يأكلون».'),
(3778, 36, 73, '«ولهم فيها منافع» كأصوافها وأوبارها وأشعارها «ومشارب» من لبنها جمع مشرب بمعنى شِرْب أو موضعه «أفلا يشكرون» المنعم عليهم بها فيؤمنون أي ما فعلوا ذلك.'),
(3779, 36, 74, '«واتخذوا من دون الله» أي غيره «آلهة» أصناما يعبدونها «لعلهم يُنصرون» يمنعون من عذاب الله تعالى بشفاعة آلهتهم بزعمهم.'),
(3780, 36, 75, '«لا يستطيعون» أي آلهتهم، نزلوا منزلة العقلاء «نصرهم وهم» أي آلهتهم من الأصنام «لهم جندٌ» بزعمهم نصرهم «محضرون» في النار معهم.'),
(3781, 36, 76, '«فلا يحزنك قولهم» لك: لست مرسلا وغير ذلك «إنا نعلم ما يسرون وما يعلنون» من ذلك وغيره فنجازيهم عليه.'),
(3782, 36, 77, '«أوَ لم يرَ الإنسان» يعلم، وهو العاصي بن وائل «أنَّا خلقناه من نطفة» منيِّ إلى أن صيَّرناه شديدا قويا «فإذا هو خصيم» شديد الخصومة لنا «مبينٌ» بيِّنها في نفي البعث.'),
(3783, 36, 78, '(وضرب لنا مثلا) في ذلك (ونسي خلقه) من المني وهو أغرب من مثله (قال من يحيي العظام وهي رميم) أي بالية ولم يقل رميمة بالتاء لأنه اسم لا صفة، وروي أنه أخذ عظما رميما ففتته وقال للنبي صلى الله عليه وسلم: أترى يحيي الله هذا بعد ما بلي ورم؟ فقال صلى الله عليه وسلم: "نعم ويدخلك النار".'),
(3784, 36, 79, '«قل يحييها الذي أنشأها أول مرة وهو بكل خلق» مخلوق «عليم» مجملا ومفصلا قبل خلقه وبعد خلقه.'),
(3785, 36, 80, '«الذي جعل لكم» في جملة الناس «من الشجر الأخضر» المرخ والعفار أو كل شجر إلا العناب «نارا فإذا أنتم منه توقدون» تقدحون وهذا دال على القدرة على البعث فإنه جمع فيه بين الماء والنار والخشب، فلا الماء يطفأ النار، ولا النار تحرق الخشب.'),
(3786, 36, 81, '«أوَ ليس الذي خلق السماوات والأرض» مع عظمهما «بقادر على أن يخلق مثلهم» أي الأناسي في الصغر «بلى» أي هو قادر على ذلك أجاب نفسه «وهو الخلاًق» الكثير الخلق «العليم» بكل شيء.'),
(3787, 36, 82, '«إنما أمره» شأنه «إذا أراد شيئا» أي خلق شيء «أن يقول له كن فيكونُ» أي فهو يكون، وفي قراءة بالنصب عطفا على يقول.'),
(3788, 36, 83, '«فسبحان الذي بيده ملكوت» مُلك زيدت الواو والتاء للمبالغة، أي القدرة على «كل شيء وإليه ترجعون» تردُّون في الآخرة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, '«والصافات صفّا» الملائكة تصف نفوسها في العبادة أو أجنحتها في الهواء تنتظر ما تؤمر به.'),
(3790, 37, 2, '«فالزاجرات زجرا» الملائكة تزجر السحاب أي تسوقه.'),
(3791, 37, 3, '«فالتاليات» أي قراء القرآن يتلونه «ذكرا» مصدر من معنى التاليات.'),
(3792, 37, 4, '«إن إلهكم» يا أهل مكة «لواحد».'),
(3793, 37, 5, '«ربُّ السماوات والأرض وما بينهما وربُّ المشارق» أي والمغارب للشمس، لها كل يوم مشرق ومغرب.'),
(3794, 37, 6, '«إنَّا زينا السماء الدنيا بزينة الكواكب» أي بضوئها أو بها، والإضافة للبيان كقراءة تنوين زينة المبينة بالكواكب.'),
(3795, 37, 7, '«وحفظا» منصوب بفعل مقدر: أي حفظناها بالشهب «من كل» متعلق بالمقدر «شيطان مارد» عاتٍ خارج عن الطاعة.'),
(3796, 37, 8, '«لا يسمعون» أي الشياطين مستأنف، وسماعهم هو في المعنى المحفوظ عنه «إلى الملأ الأعلى» الملائكة في السماء، وعدِّي السماع بإلى لتضمنه معنى الإصغاء وفي قراءة بتشديد الميم والسين أصله يتسمعون أدغمت التاء في السين «ويقذفون» أي الشياطين بالشهب «من كل جانب» من آفاق السماء.'),
(3797, 37, 9, '«دُحُورا» مصدر دحره: أي طرده وأبعده وهو مفعول له «ولهم» في الآخرة «عذاب واصب» دائم.'),
(3798, 37, 10, '«إلا من خطف الخطفة» مصدر: أي المرة، والاستثناء من ضمير يسمعون: أي لا يسمع إلا الشيطان الذي سمع الكلمة من الملائكة فأخذها بسرعة «فأتبعه شهاب» كوكب مضيء «ثاقب» يثقبه أو يحرقه أو يخبله.'),
(3799, 37, 11, '«فاستفتهم» استخبر كفار مكة تقريرا أو توبيخا «أهم أشد خلقا أم من خلقنا» من الملائكة والسماوات والأرضين وما فيهما وفي الإتيان بمن تغليب العقلاء «إنا خلقناهم» أي أصلهم آدم «من طين لازب» لازم يلصق باليد: المعنى أن خلقهم ضعيف فلا يتكبروا بإنكار النبي والقرآن المؤدي إلى هلاكهم اليسير.'),
(3800, 37, 12, '«بل» للانتقال من غرض إلى آخر وهو الإخبار بحاله وحالهم «عجبتَ» بفتح التاء خطابا للنبي صلى الله عليه وسلم، أي من تكذيبهم إياك «و» هم «يسخرون» من تعجبك.'),
(3801, 37, 13, '«وإذا ذكروا» وعظوا بالقرآن «لا يذكرون» لا يتعظون.'),
(3802, 37, 14, '«وإذا رأوْا آية» كانشقاق القمر «يستسخرون» يستهزئُون بها.'),
(3803, 37, 15, '«وقالوا» فيها «إن» ما «هذا إلا سحر مبين» بيّن وقالوا منكرين للبعث.'),
(3804, 37, 16, '«أئذا متنا وكنا ترابا وعظاما أئنا لمبعوثون» في الهمزتين في الموضوعين التحقيق وتسهيل الثانية وإدخال ألف بينهما على الوجهين.'),
(3805, 37, 17, '«أوْ آباؤنا الأولون» بسكون الواو عطفا بأو، وبفتحها والهمزة للاستفهام والعطف بالواو والمعطوف عليه محل إن واسمها أو الضمير في لمبعوثون والفاصل همزة الاستفهام.'),
(3806, 37, 18, '«قل نعم» تبعثون «وأنتم داخرون» أي صاغرون.'),
(3807, 37, 19, '«فإنما هي» ضمير مبهم يفسره «زجرة» أي صيحة «واحدة فإذا هم» أي الخلائق أحياء «ينظرون» ما يفعل بهم.'),
(3808, 37, 20, '«وقالوا» أي الكفار «يا» للتنبيه «ويْلنا» هلاكنا، وهو مصدر لا فعل له من لفظه وتقول لهم الملائكة: «هذا يوم الدين» يوم الحساب والجزاء.'),
(3809, 37, 21, '«هذا يوم الفصل» بين الخلائق «الذي كنتم به تكذبون» ويقال للملائكة.'),
(3810, 37, 22, '«أُحشروا الذين ظلموا» أنفسهم بالشرك «وأزواجهم» قرناءهم من الشياطين «وما كانوا يعبدون».'),
(3811, 37, 23, '«من دون الله» أي غيره من الأوثان «فاهدوهم» دلوهم وسوقوهم «إلى صراط الجحيم» طريق النار.'),
(3812, 37, 24, '«وقفوهم» احبسوهم عن الصراط «إنهم مسئولون» عن جميع أقوالهم، ويقال لهم توبيخا.'),
(3813, 37, 25, '«ما لكم لا تناصرون» لا ينصر بعضكم بعضا كحالكم في الدنيا ويقال لهم.'),
(3814, 37, 26, '«بل هم اليوم مستسلمون» منقادون أذلاء.'),
(3815, 37, 27, '«وأقبل بعضهم على بعض يتساءلون» يتلاومون ويتخاصمون.'),
(3816, 37, 28, '«قالوا» أي الأتباع منهم للمتبوعين «إنكم كنتم تأتوننا عن اليمين» عن الجهة التي كنا نأمنكم منها لحلفكم أنكم على الحق فصدقناكم واتبعناكم، المعنى أنكم أضللتمونا.'),
(3817, 37, 29, '«قالوا» أي المتبعون لهم «بل لم تكونوا مؤمنين» وإنما يصدق الإضلال منا أن لو كنتم مؤمنين فرجعتم عن الإيمان إلينا.'),
(3818, 37, 30, '«وما كان لنا عليكم من سلطان» قوة وقدرة تقهركم على متابعتنا «بل كنتم قوما طاغين» ضالين مثلنا.'),
(3819, 37, 31, '(فحق) وجب (علينا) جميعا (قول ربنا) بالعذاب: أي قوله "" لأملأن جهنم من الجنة والناس أجمعين "" (إنا) جميعا (لذائقون) العذاب بذلك القول ونشأ عنه قولهم.'),
(3820, 37, 32, '«فأغويناكم» المعلل بقولهم «إنا كنا غاوين».'),
(3821, 37, 33, 'قال تعالى «فإنهم يومئذ» يوم القيامة «في العذاب مشتركون» أي لاشتراكهم في الغواية.'),
(3822, 37, 34, '«إنا كذلك» كما نفعل بهؤلاء «نفعل بالمجرمين» غير هؤلاء: أي نعذبهم التابع منهم والمتبوع.'),
(3823, 37, 35, '«إنهم» أي هؤلاء بقرينة ما بعده «كانوا إذا قيل لهم لا إله إلا الله يستكبرون».'),
(3824, 37, 36, '«ويقولون أئنا» في همزتيه ما تقدم «لتاركوا آلهتنا لشاعر مجنون» أي لأجل محمد.'),
(3825, 37, 37, 'قال تعالى: «بل جاء بالحق وصدَّق المرسلين» الجائين به، وهو أن لا إله إلا الله.'),
(3826, 37, 38, '«إنكم» فيه التفات «لذائقوا العذاب الأليم».'),
(3827, 37, 39, '«وما تجزوْن إلا» جزاء «ما كنتم تعملون».'),
(3828, 37, 40, '«إلا عباد الله المخلصين» أي المؤمنين استثناء منقطع، أي ذكر جزائهم في قوله.'),
(3829, 37, 41, '«أولئك لهم» في الجنة «رزق معلوم» بكرة وعشيا.'),
(3830, 37, 42, '«فواكه» بدل أو بيان للرزق وهو ما يؤكل تلذذا لحفظ صحة لأن أهل الجنة مستغنون عن حفظها بخلق أجسامهم للأبد «وهم مكرمون» بثواب الله سبحانه وتعالى.'),
(3831, 37, 43, '«في جنات النعيم».'),
(3832, 37, 44, '«على سرر متقابلين» لا يرى بعضهم قفا بعض.'),
(3833, 37, 45, '«يطاف عليهم» على كل منهم «بكأس» هو الإناء بشرابه «من معين» من خمر يجري على وجه الأرض كأنهار الماء.'),
(3834, 37, 46, '«بيضاء» أشد بياضا من اللبن «لذة» لذيذة «للشاربين» بخلاف خمر الدنيا فإنها كريهة عند الشرب.'),
(3835, 37, 47, '«لا فيها غول» ما يغتال عقولهم «ولا هم عنها ينزَِفون» بفتح الزاي وكسرها من نزف الشارب وأنزف: أي يسكرون بخلاف خمر الدنيا.'),
(3836, 37, 48, '«وعندهم قاصرات الطرْف» حابسات الأعين على أزواجهن لا ينظرن إلى غيرهم لحسنهم عندهن «عين» ضخام الأعين حسانها.'),
(3837, 37, 49, '«كأنهن» في اللون «بيض» للنعام «مكنون» مستور بريشه لا يصل إليه غبار، ولونه وهو البياض في صفرة، أحسن ألوان النساء.'),
(3838, 37, 50, '«فأقبل بعضهم» بعض أهل الجنة «على بعض يتساءلون» عما مر بهم في الدنيا.'),
(3839, 37, 51, '«قال قائل منهم إني كان لي قرين» صاحب ينكر البعث.'),
(3840, 37, 52, '«يقول» لي تبكيتا «أئنك لمن المصدقين» بالبعث.'),
(3841, 37, 53, '«أئذا متنا وكنا ترابا وعظاما أئنا» في الهمزتين في الثلاثة مواضع ما تقدم «لمدينون» مجزيون ومحاسبون؟ أنكر ذلك أيضا.'),
(3842, 37, 54, '«قال» ذلك القائل لإخوانه: «هل أنتم مطلعون» معي إلى النار لننظر حاله؟ فيقولون: لا.'),
(3843, 37, 55, '«فاطلع» ذلك القائلون من بعض كوى الجنة «فرآه» أي رأى قرينه «في سواء الجحيم» في وسط النار.'),
(3844, 37, 56, '«قال» له تشميتا «تالله إن» مخففة من الثقيلة «كدت» قاربت «لتردين» لتهلكني بإغوائك.'),
(3845, 37, 57, '«ولو لا نعمة ربي» عليَّ بالإيمان «لكنت من المحضرين» معك في النار وتقول أهل الجنة.'),
(3846, 37, 58, '«أفما نحن بميتين».'),
(3847, 37, 59, '«إلا موتتنا الأولى» أي التي في الدنيا «وما نحن بمعذبين» هو استفهام تلذذ وتحدُّث بنعمة الله تعالى من تأبيد الحياة وعدم التعذيب.'),
(3848, 37, 60, '«إن هذا» الذي ذكرت لأهل الجنة «لهو الفوز العظيم».'),
(3849, 37, 61, '«لمثل هذا فليعمل العاملون» قيل يقال لهم ذلك، وقيل هم يقولونه.'),
(3850, 37, 62, '«أذلك» المذكور لهم «خير نزلا» وهو ما يعدّ للنازل من ضيف وغيره «أم شجرة الزقوم» المعدة لأهل النار وهي من أخبث الشجر المرّ بتهامة ينبتها الله في الجحيم كما سيأتي.'),
(3851, 37, 63, '«إنا جعلناها» بذلك «فتنة للظالمين» أي الكافرين من أهل مكة، إذ قالوا: النار تحرق الشجر فكيف تنبته.'),
(3852, 37, 64, '«إنها شجرة تخرج في أصل الجحيم» أي قعر جهنم، وأغصانها ترتفع إلى دركاتها.'),
(3853, 37, 65, '«طلعها» المشبه بطلع النخل «كأنه رءوس الشياطين» الحيات القبيحة المنظر.'),
(3854, 37, 66, '«فإنهم» أي الكفار «لآكلون منها» مع قبحها لشدة جوعهم «فمالئون منها البطون».'),
(3855, 37, 67, '«ثم إن لهم عليها لشوبا من حميم» أي ماء حار يشربونه فيختلط بالمأكول منها فيصير شوبا له.'),
(3856, 37, 68, '«ثم إن مرجعهم لإلى الجحيم» يفيد أنهم يخرجون منها لشرب الحميم وأنه خارجها.'),
(3857, 37, 69, '«إنهم ألفوْا» وجدوا «آباءهم ضالين».'),
(3858, 37, 70, '«فهم على آثارهم يُهرعون» يزعجون إلى اتباعهم فيسرعون إليه.'),
(3859, 37, 71, '«ولقد ضل قبلهم أكثر الأولين» من الأمم الماضية.'),
(3860, 37, 72, '«ولقد أرسلنا فيهم منذرين» من الرسل مخوِّفين.'),
(3861, 37, 73, '«فانظر كيف كان عاقبة المنذَرين» الكافرين: أي عاقبتهم العذاب.'),
(3862, 37, 74, '«إلا عباد الله المخلصين» أي المؤمنين فإنهم نجوا من العذاب لإخلاصهم في العبادة، أو لأن الله أخلصهم لها على قراءة فتح اللام.'),
(3863, 37, 75, '(ولقد نادانا نوح) بقوله "" رب إني مغلوب فانتصر "" (فلنعم المجيبون) له نحن: أي دعانا على قومه فأهلكناهم بالغرق.'),
(3864, 37, 76, '«ونجيناه وأهله من الكرب العظيم» أي الغرق.'),
(3865, 37, 77, '«وجعلنا ذريته هم الباقين» فالناس كلهم من نسله وكان له ثلاثة أولاد: سام وهو أبو العرب والفرس والروم، وحام وهو أبو السودان، ويافث وهو أبو الترك والخزر ويأجوج ومأجوج وما هنالك.'),
(3866, 37, 78, '«وتركنا» أبقينا «عليه» ثناء حسنا «في الآخرين» من الأنبياء والأمم إلى يوم القيامة.'),
(3867, 37, 79, '«سلام» منا «على نوح في العالمين».'),
(3868, 37, 80, '«إنا كذلك» كما جزيناهم «نجزي المحسنين».'),
(3869, 37, 81, '«إنه من عبادنا المؤمنين».'),
(3870, 37, 82, '«ثم أغرقنا الآخرين» كفار قومه.'),
(3871, 37, 83, '«وإن من شيعته» أي ممن تابعه في أصل الدين «لإبراهيم» وإن طال الزمان بينهما وهو ألفان وستمائة وأربعون سنة وكان بينهما هود وصالح.'),
(3872, 37, 84, '«إذ جاء ربهَّ» أي تابعه وقت مجيئه «بقلب سليم» من الشك وغيره.'),
(3873, 37, 85, '«إذ قال» في هذه الحالة المستمرة له «لأبيه وقومه» موبخا «ماذا» ما الذي «تعبدون».'),
(3874, 37, 86, '«أئفكا» في همزتيه ما تقدم «آلهة دون الله تريدون» وإفكا مفعول له، وآلهة مفعول به لتريدون والإفك: أسوأ الكذب، أي أتعبدون غير الله؟.'),
(3875, 37, 87, '«فما ظنكم برب العالمين» إذ عبدتم غيره أنه يترككم بلا عقاب؟ لا، وكانوا نجامين، فخرجوا إلى عيد لهم وتركوا طعامهم عند أصنامهم زعموا التبرك عليه فإذا رجعوا أكلوه، وقالوا للسيد: اخرج معنا.'),
(3876, 37, 88, '«فنظر نظرة في النجوم» إيهاما لهم أنه يعتمد عليها ليعتمدوه.'),
(3877, 37, 89, '«فقال إني سقيم» عليل أي سأسقم.'),
(3878, 37, 90, '«فتولوا عنه» إلى عيدهم «مدبرين».'),
(3879, 37, 91, '«فراغ» مال في خفية «إلى آلهتهم» وهي الأصنام وعندها الطعام «فقال» استهزاءً «ألا تأكلون» فلم ينطقوا.'),
(3880, 37, 92, 'فقال «ما لكم لا تنطقون» فلم يجب.'),
(3881, 37, 93, '«فراغ عليهم ضربا باليمين» بالقوة فكسرها فبلغ قومه ممن رآه.'),
(3882, 37, 94, '«فأقبلوا إليه يزفون» أي يسرعون المشي فقالوا له: نحن نعبدها وأنت تكسرها.'),
(3883, 37, 95, '«قال» لهم موبخا «أتعبدون ما تنحتون» من الحجارة وغيرها أصناما.'),
(3884, 37, 96, '«والله خلقكم وما تعملون» من نحتكم ومنحوتكم فاعبدوه وحده، وما مصدرية وقيل موصولة وقيل موصوفة.'),
(3885, 37, 97, '«قالوا» بينهم «ابنوا له بنيانا» فاملئوه حطبا وأضرموه بالنار فإذا التهب «فألقوه في الجحيم» النار الشديدة.'),
(3886, 37, 98, '«فأرادوا به كيدا» بإلقائه في النار لتهلكه «فجعلناهم الأسفلين» المقهورين فخرج من النار سالما.'),
(3887, 37, 99, '«وقال إني ذاهب إلى ربي» مهاجر إليه من دار الكفر «سيهدين» إلى حيث أمرني ربي بالمصير إليه وهو الشام فلما وصل إلى الأرض المقدسة قال.'),
(3888, 37, 100, '«رب هب لي» ولدا «من الصالحين».'),
(3889, 37, 101, '«فبشرناه بغلام حليم» أي ذي حلم كثير.'),
(3890, 37, 102, '«فلما بلغ معه السعي» أي أن يسعى معه ويعينه قيل بلغ سبع سنين وقيل ثلاث عشرة سنة «قال يا بنيَّ إني أرى» أي رأيت «في المنام أني أذبحك» ورؤيا الأنبياء حق وأفعالهم بأمر الله تعالى «فانظر ماذا ترى» من الرأي شاوره ليأنس بالذبح وينقاد للأمر به «قال يا أبت» التاء عوض عن ياء الإضافة «افعل ما تؤمر» به «ستجدني إن شاء الله من الصابرين» على ذلك.'),
(3891, 37, 103, '«فلما أسلما» خضعا وانقادا لأمر الله تعالى «وتله للجبين» صرعه عليه، ولكل إنسان جبينان بينهما الجبهة وكان ذلك بمنى، وأمرَّ السكين على حلقه فلم تعمل شيئا بمانع من القدرة الإلهية.'),
(3892, 37, 104, '«وناديناه أن يا إبراهيم».'),
(3893, 37, 105, '«قد صدقت الرؤيا» بما أتيت به مما أمكنك من أمر الذبح: أي يكفيك ذلك فجملة ناديناه جواب لما بزيادة الواو «إنا كذلك» كما جزيناك «نجزي المحسنين» لأنفسهم بامتثال الأمر بإفراج الشدة عنهم.'),
(3894, 37, 106, '«إن هذا» الذبح المأمور به «لهو البلاء المبين» أي الاختبار الظاهر.'),
(3895, 37, 107, '«وفديناه» أي المأمور بذبحه، وهو إسماعيل أو إسحاق قولان «بذبح» بكبش «عظيم» من الجنة وهو الذي قربه هابيل جاء به جبريل عليه السلام فذبحه السيد إبراهيم مكبرا.'),
(3896, 37, 108, '«وتركنا» أبقينا «عليه في الآخرين» ثناءً حسنا.'),
(3897, 37, 109, '«سلام» منا «على إبراهيم».'),
(3898, 37, 110, '«كذلك» كما جزيناه «نجزي المحسنين» لأنفسهم.'),
(3899, 37, 111, '«إنه من عبادنا المؤمنين».'),
(3900, 37, 112, '«وبشرناه بإسحاق» استدلَّ بذلك على أن الذبح غيره «نبيا» حال مقدرة: أي يوجد مقدرا نبوته «من الصالحين».'),
(3901, 37, 113, '«وباركنا عليه» بكثير ذريته «وعلى إسحاق» ولده بجعلنا أكثر الأنبياء من نسله «ومن ذريتهما محسن» مؤمن «وظالم لنفسه» كافر «مبين» بيَّن الكفر.'),
(3902, 37, 114, '«ولقد مننا على موسى وهارون» بالنبوة.'),
(3903, 37, 115, '«ونجيناهما وقومهما» بني إسرائيل «من الكرب العظيم» أي استعباد فرعون إياهم.'),
(3904, 37, 116, '«ونصرناهم» على القبط «فكانوا هم الغالبين».'),
(3905, 37, 117, '«وآتيناهما الكتاب المستبين» البليغ البيان فيما أتى به من الحدود والأحكام وغيرها وهو التوراة.'),
(3906, 37, 118, '«وهديناهما الصراط» الطريق «المستقيم».'),
(3907, 37, 119, '«وتركنا» أبقينا «عليهما في الآخرين» ثناءً حسنا.'),
(3908, 37, 120, '«سلام» منا «على موسى وهارون».'),
(3909, 37, 121, '«إنا كذلك» كما جزيناهما «نجزي المحسنين».'),
(3910, 37, 122, '«إنهما من عبادنا المؤمنين».'),
(3911, 37, 123, '«وإن إلياس» بالهمزة أوله وتركه «لمن المرسلين» قيل هو ابن أخي هارون أخي موسى، وقيل غيره أرسل إلى قوم ببعلبك ونواحيها.'),
(3912, 37, 124, '«إذ» منصوب باذكر مقدرا «قال لقومه ألا تتقون» الله.'),
(3913, 37, 125, '«أتدعون بعلا» اسم صنم لهم من ذهب، وبه سمي البلد أيضا مضافا إلى بك: أي أتعبدونه «وتذرون» تتركون «أحسن الخالقين» فلا تعبدونه.'),
(3914, 37, 126, '«اللهُ ربُّكم وربُّ آبائكم الأولين» برفع الثلاثة على إضمار هو، وبنصبها على البدل من أحسن.'),
(3915, 37, 127, '«فكذبوه فإنهم لمحضرون» في النار.'),
(3916, 37, 128, '«إلا عباد الله المخلصين» أي المؤمنين منهم فإنهم نجوا منها.'),
(3917, 37, 129, '«وتركنا عليه في الآخرين» ثناءً حسنا.'),
(3918, 37, 130, '«سلام» منا «على إلْ ياسين» قيل هو إلياس المتقدم ذكره، وقيل هو ومن آمن معه فجمعوا معه تغليبا كقولهم للمهلب وقومه المهلبون وعلى قراءة آل ياسين بالمد، أي أهله المراد به إلياس أيضا.'),
(3919, 37, 131, '«إنا كذلك» كما جزيناه «نجزي المحسنين».'),
(3920, 37, 132, '«إنه من عبادنا المؤمنين».'),
(3921, 37, 133, '«وإن لوطا لمن المرسلين».'),
(3922, 37, 134, 'اذكر «إذ نجيناه وأهله أجمعين».'),
(3923, 37, 135, '«إلا عجوزا في الغابرين» أي الباقين في العذاب.'),
(3924, 37, 136, '«ثم دمرنا» أهلكنا «الآخرين» كفار قومه.'),
(3925, 37, 137, '«وإنكم لتمرون عليهم» على آثارهم ومنازلهم في أسفاركم «مصبحين» أي وقت الصباح يعني بالنهار.'),
(3926, 37, 138, '«وبالليل أفلا تعقلون» يا أهل مكة ما حل بهم فتعتبرون به.'),
(3927, 37, 139, '«وإن يونس لمن المرسلين».'),
(3928, 37, 140, '«إذْ أبق» هرب «إلى الفلك المشحون» السفينة المملوءة حين غاضب قومه لما لم ينزل بهم العذاب الذي وعدهم به فركب السفينة فوقفت في لجة البحر، فقال الملاحون هنا عبد أبق من سيده تظهره القرعة.'),
(3929, 37, 141, '«فساهم» قارع أهل السفينة «فكان من المدحضين» المغلوبين بالقرعة فألقوه في البحر.'),
(3930, 37, 142, '«فالتقمه الحوت» ابتلعه «وهو مليم» أي آت بما يلام عليه من ذهابه إلى البحر وركوبه السفينة بلا إذن من ربه.'),
(3931, 37, 143, '(فلولا أنه كان من المسبحين) الذاكرين بقوله كثيرا في بطن الحوت "" لا إله إلا أنت سبحانك إني كنت من الظالمين "".'),
(3932, 37, 144, '«للبث في بطنه إلى يوم يبعثون» لصار بطن الحوت قبرا له إلى يوم القيامة.'),
(3933, 37, 145, '«فنبذناه» أي ألقيناه من بطن الحوت «بالعراء» بوجه الأرض: أي بالساحل من يومه أو بعد ثلاثة أو سبعة أيام أو عشرين أو أربعين يوما «وهو سقيم» العليل كالفرخ الممعط.'),
(3934, 37, 146, '«وأنبتنا عليه شجرة من يقطين» وهي القرع تظله بساق على خلاف العادة في القرع معجزة له، وكانت تأتيه وعلة صباحا ومساء يشرب من لبنها حتى قوي.'),
(3935, 37, 147, '«وأرسلناه» بعد ذلك كقبله إلى قوم بنينوى من أرض الموصل «إلى مائة ألف أو» بل «يزيدون» عشرين أو ثلاثين أو سبعين ألفا.'),
(3936, 37, 148, '«فآمنوا» عند معاينة العذاب الموعودين به «فمتعناهم» أبقيناهم ممتعين بمالهم «إلى حين» تنقضي آجالهم فيه.'),
(3937, 37, 149, '«فاستفتهم» استخبر كفار مكة توبيخا لهم «ألربك البنات» بزعمهم أن الملائكة بنات الله «ولهم البنون» فيختصون بالأسنى.'),
(3938, 37, 150, '«أم خلقنا الملائكة إناثا وهم شاهدون» خلقنا فيقولون ذلك.'),
(3939, 37, 151, '«ألا أنهم من إفكهم» كذبهم «ليقولون» بقولهم الملائكة بنات الله.'),
(3940, 37, 152, '«ولد الله» بقولهم الملائكة بنات الله «وإنهم لكاذبون» فيه.'),
(3941, 37, 153, '«أصطفى» بفتح الهمزة للاستفهام واستغني بها عن همزة الوصل فحذفت، أي أختار «البنات على البنين».'),
(3942, 37, 154, '«ما لكم كيف تحكمون» هذا الحكم الفاسد.'),
(3943, 37, 155, '«أفلا تذَّكرون» بإدغام التاء في الذال، أنه سبحانه وتعالى منزه عن الولد.'),
(3944, 37, 156, '«أم لكم سلطان مبين» حجة واضحة أن لله ولدا.'),
(3945, 37, 157, '«فأتوا بكتابكم» التوراة فأروني ذلك فيه «إن كنتم صادقين» في قولكم ذلك.'),
(3946, 37, 158, '«وجعلوا» أي المشركون «بينه» تعالى «وبين الجنة» أي الملائكة لاجتنانهم عن الأبصار «نسبا» بقولهم إنها بنات الله «ولقد علمت الجنَّة إنهم» أي قائلي ذلك «لمحضرون» للنار يعذبون فيها.'),
(3947, 37, 159, '«سبحان الله» تنزيها له «عما يصفون» بأن لله ولدا.'),
(3948, 37, 160, '«إلا عباد الله المخلصين» أي المؤمنين استثناء منقطع أي فإنهم ينزهون الله تعالى عما يصفه هؤلاء.'),
(3949, 37, 161, '«فإنكم وما تعبدون» من الأصنام.'),
(3950, 37, 162, '«ما أنتم عليه» أي على معبودكم عليه متعلق بقوله «بفاتنين» أي أحدا.'),
(3951, 37, 163, '«إلا من هو صال الجحيم» في علم الله تعالى.'),
(3952, 37, 164, 'قال جبريل للنبي صلى الله عليه وسلم «وما منا» معشر الملائكة أحد «إلا له مقام معلوم» في السماوات يعبد الله فيه لا يتجاوزه.'),
(3953, 37, 165, '«وإنا لنحن الصَّافون» أقدامنا في الصلاة.'),
(3954, 37, 166, '«وإنا لنحن المسبحون» المنزهون الله عما لا يليق به.'),
(3955, 37, 167, '«وإن» مخففة من الثقيلة «كانوا» أي كفار مكة «ليقولون».'),
(3956, 37, 168, '«لو أن عندنا ذكرا» كتابا «من الأولين» أي من كتب الأمم الماضية.'),
(3957, 37, 169, '«لكنا عباد الله المخلصين» العابدة له.'),
(3958, 37, 170, 'قال تعالى: «فكفروا به» بالكتاب الذي جاءهم وهو القرآن الأشرف من ذلك الكتب «فسوف يعلمون» عاقبة كفرهم.'),
(3959, 37, 171, '(ولقد سبقت كلمتنا) بالنصر (لعبادنا المرسلين) وهي "" لأغلبن أنا ورسلي "".'),
(3960, 37, 172, 'أو هي قوله «إنهم لهم المنصورون».'),
(3961, 37, 173, '«وإن جندنا» أي المؤمنين «لهم الغالبون» الكفار بالحجة والنصرة عليهم في الدنيا، وإن لم ينتصر بعض منهم في الدنيا ففي الآخرة.'),
(3962, 37, 174, '«فتول عنهم» أي أعرض عن كفار مكة «حتى حين» تؤمر فيه بقتالهم.'),
(3963, 37, 175, '«وأبصرهم» إذ نزل بهم العذاب «فسوف يبصرون» عاقبة كفرهم.'),
(3964, 37, 176, 'فقالوا استهزاء: متى نزول هذا العذاب؟ قال تعالى تهديدا لهم: «أفبعذابنا يستعجلون».'),
(3965, 37, 177, '«فإذا نزل بساحتهم» بفنائهم قال الفراء: العرب تكتفي بذكر الساحة عن القوم «فَساء» بئس صباحا «صباح المنذَرين» فيه إقامة الظاهر مقام المضمر.'),
(3966, 37, 178, '«وتول عنهم حتى حين».'),
(3967, 37, 179, '«وأبصر فسوف يبصرون» كرر تأكيدا لتهديدهم وتسلية له صلى الله عليه وسلم.'),
(3968, 37, 180, '«سبحان ربك رب العزة» الغلبة «عما يصفون» بأن له ولدا.'),
(3969, 37, 181, '«وسلام على المرسلين» المبلغين عن الله التوحيد والشرائع.'),
(3970, 37, 182, '«والحمد لله رب العالمين» على نصرهم وهلاك الكافرين.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, '«ص» الله أعلم بمراده به «والقرآن ذي الذكر» أي البيان أو الشرف، وجواب هذا القسم محذوف: أي ما الأمر كما قال كفار مكة من تعدد الآلهة.'),
(3972, 38, 2, '«بل الذين كفروا» من أهل مكة «في عزة» حمية وتكبر عن الإيمان «وشقاق» خلاف وعداوة للنبي صلى الله عليه وسلم.'),
(3973, 38, 3, '«كم» أي كثيرا «أهلكنا من قبلهم من قرنِ» أي أمة من الأمم الماضية «فنادوْا» حين نزول العذاب بهم «ولاتَ حين مناص» أي ليس حين فرار والتاء زائدة، والجملة حال من فاعل نادوا، أي استغاثوا، والحال أن لا مهرب ولا منجى وما اعتبر بهم كفار مكة.'),
(3974, 38, 4, '«وعجبوا أن جاءهم منذر منهم» رسول من أنفسهم ينذرهم ويخوفهم النار بعد البعث وهو النبي * صلى الله عليه وسلم «وقال الكافرون» فيه وضع الظاهر موضع المضمر «هذا ساحر كذاب».'),
(3975, 38, 5, '«أجعل الآلهة إلها واحدا» حيث قال لهم قولوا: لا إله إلا الله، أي كيف يسع الخلق كلهم إله واحد «إن هذا لشيء عجاب» أي عجيب.'),
(3976, 38, 6, '«وانطلق الملأ منهم» من مجلس اجتماعهم عند أبي طالب وسماعهم فيه من النبي صلى الله عليه وسلم قولوا: لا إله إلا الله «أن امشوا» يقول بعضهم لبعض امشوا «واصبروا على آلهتكم» اثبتوا على عبادتها «إن هذا» المذكور من التوحيد «لشيء يراد» منا.'),
(3977, 38, 7, '«ما سمعنا بهذا في الملة الآخرة» أي ملة عيسى «إن» ما «هذا إلا اختلاق» كذب.'),
(3978, 38, 8, '«أَأُنزل» بتحقيق الهمزتين وتسهيل الثانية، وإدخال ألف بينهما على الوجهين وتركه «عليه» على محمد «الذكر» أي القرآن «من بيننا» وليس بأكبرنا ولا أشرفنا: أي لم ينزل عليه، قال تعالى: «بل هم في شك من ذكري» وحْيي أي القرآن حيث كذبوا الجائي به «بل لما» لم «يذوقوا عذاب» ولو ذاقوه لصدقوا النبي صلى الله عليه وسلم فيما جاء به ولا ينفعهم التصديق حينئذ.'),
(3979, 38, 9, '«أم عندهم خزائن رحمة ربك العزيز» الغالب «الوهاب» من النبوَّة وغيرها فيعطونها من شاءوا.'),
(3980, 38, 10, '«أم لهم ملك السماوات والأرض وما بينهما» إن زعموا ذلك «فليرتقوا في الأسباب» الموصلة إلى السماء فيأتوا بالوحي فيخصوا به من شاءوا، وأمْ في الموضعين بمعنى همزة الإنكار.'),
(3981, 38, 11, '«جند ما» أي هم جند حقير «هنالك» في تكذيبهم لك «مهزوم» صفة جند «من الأحزاب» صفة جند أيضا: أي كالأجناد من جنس الأحزاب المتحزبين على الأنبياء قبلك وأولئك قد قهروا وأهلكوا فكذا نهلك هؤلاء.'),
(3982, 38, 12, '«كذبت قبلهم قوم نوح» تأنيث قوم باعتبار المعنى «وعاد وفرعون ذو الأوتاد» كان يتد لكل من يغضب عليه أربعة أوتاد يشد إليها يديه ورجليه ويعذبه.'),
(3983, 38, 13, '«وثمود وقوم لوط وأصحاب الأيكة» أي الغيضة، وهم قوم شعيب عليه السلام «أولئك الأحزاب».'),
(3984, 38, 14, '«إن» ما «كل» من الأحزاب «إلا كذب الرسل» لأنهم إذا كذبوا واحدا منهم فقد كذبوا جميعهم لأن دعوتهم واحدة، وهي دعوة التوحيد «فحق» وجب «عقاب».'),
(3985, 38, 15, '«وما ينظر» ينتظر «هؤلاء» أي كفار مكة «إلا صيحة واحدة» هي نفخة القيامة تحل بهم العذاب «ما لها من فواق» بفتح الفاء وضمها: رجوع.'),
(3986, 38, 16, '«وقالوا» لما نزل (فأما من أوتي كتابه بيمينه) الخ «ربنا عجل لنا قطنا» أي كتاب أعمالنا «قبل يوم الحساب» قالوا ذلك استهزاء.'),
(3987, 38, 17, 'قال تعالى: «اصبر على ما يقولون واذكر عبدنا داود ذا الأيد» أي القوة في العبادة كان يصوم يوما ويفطر يوما ويقوم نصف الليل وينام ثلثه ويقوم سدسه «إنه أوَّاب» رجّاع إلى مرضاة الله.'),
(3988, 38, 18, '«إنا سخرنا الجبال معه يسبحن» بتسبيحه «بالعشيّ» وقت صلاة العشاء «والإشراق» وقت صلاة الضحى وهو أن تشرق الشمس ويتناهى ضوءُها.'),
(3989, 38, 19, '«و» سخرنا «الطير محشورة» مجموعة إليه تسبح معه «كل» من الجبال والطير «له أوَّاب» رجّاع إلى طاعته بالتسبيح.'),
(3990, 38, 20, '«وشددنا ملكه» قوَّيناه بالجرس والجنود وكان يحرس محرابه في كل ليلة ثلاثون ألف رجل «وآتيناه الحكمة» النبوة والإصابة في الأمور «وفصل الخطاب» البيان الشافي في كل قصد.'),
(3991, 38, 21, '«وهل» معنى الاستفهام هنا التعجيب والتشويق إلى استماع ما بعده «أتاك» يا محمد «نبأ الخصم إذ تسوَّروا المحراب» محراب داود: أي مسجده حيث منعوا الدخول عليه من الباب لشغله بالعبادة، أي خبرهم وقصتهم.'),
(3992, 38, 22, '«إذ دخلوا على داود ففزع منهم قالوا لا تخف» نحن «خصمان» قيل فريقان ليطابق ما قبله من ضمير الجمع، وقيل اثنان والضمير بمعناهما، والخصم يطلق على الواحد وأكثر، وهما ملكان جاءا في صورة خصمين وقع لهما ما ذكر على سبيل الغرض لتنبيه داود عليه السلام على ما وقع منه وكان له تسع وتسعون امرأة وطلب امرأة شخص ليس له غيرها وتزوجها ودخل بها «بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط» تَجُرْ «واهدنا» أرشدنا «إلى سواء الصراط» وسط الطريق الصواب.'),
(3993, 38, 23, '«إن هذا أخي» أي على ديني «له تسع وتسعون نعجة» يعبر بها عن المرأة «وليَ نعجة واحدة فقال أكفلنيها» أي اجعلني كافلها «وعزني» غلبني «في الخطاب» أي الدال، وأقره الآخر على ذلك.'),
(3994, 38, 24, '«قال لقد ظلمك بسؤال نعجتك» ليضمها «إلى نعاجه وإن كثيرا من الخلطاء» الشركاء «ليبغي بعضهم على بعض إلا الذين آمنوا وعملوا الصالحات وقليل ما هم» ما لتأكيد القلة فقال الملكان صاعدين في صورتيهما إلى السماء: قضى الرجل على نفسه فتنبه داود قال تعالى: «وظن» أي أيقن «داود أنما فتناه» أوقعناه في فتنة أي بلية بمحبته تلك المرأة «فاستغفر ربَّه وخرَّ راكعا» أي ساجدا «وأناب».'),
(3995, 38, 25, '«فغفرنا له ذلك وإن له عندنا لزلفى» أي زيادة خير في الدنيا «وحسن مآب» مرجع في الآخرة.'),
(3996, 38, 26, '«يا داود إنا جعلناك خليفة في الأرض» تدبر أمر الناس «فاحكم بين الناس بالحق ولا تتبع الهوى» أي هوى النفس «فيضلك عن سبيل الله» أي عن الدلائل الدالة على توحيده «إن الذين يضلون عن سبيل الله» أي عن الإيمان بالله «لهم عذاب شديد بما نسوا» بنسيانهم «يوم الحساب» المرتب عليه تركهم الإيمان، وقالوا أيقنوا بيوم الحساب لآمنوا في الدنيا.'),
(3997, 38, 27, '«وما خلقنا السماء والأرض وما بينهما باطلا» عبثا «ذلك» أي خلق ما ذكر لا لشيء «ظن الذين كفروا» من أهل مكة «فويل» وادِ «للذين كفروا من النار».'),
(3998, 38, 28, '«أم نجعل الذين آمنوا وعملوا الصالحات كالمفسدين في الأرض أم نجعل المتقين كالفجار» نزل لما قال كفار مكة للمؤمنين إنا نعطى في الآخرة مثل ما تعطون، وأم بمعنى همزة الإنكار.'),
(3999, 38, 29, '«كتاب» خبر مبتدأ محذوف أي هذا «أنزلناه إليك مبارك ليدَّبروا» أصله يتدبروا أدغمت التاء في الدال «آياته» ينظروا في معانيها فيؤمنوا «وليتذكر» يتعظ «أولوا الألباب» أصحاب العقول.'),
(4000, 38, 30, '«ووهبنا لداود سلمان» ابنه «نعم العبد» أي سليمان «إنه أوَّاب» رجّاع في التسبيح والذكر في جميع الأوقات.'),
(4001, 38, 31, '«إذ عرض عليه بالعشي» هو ما بعد الزوال «الصافنات» الخيل جمع صافنة وهي القائمة على ثلاث وإقامة الأخرى على طرف الحافر وهو من صفن يصفن صفونا «الجياد» الخيل جمع جواد وهو السابق، المعنى أنها إذا استوقفت سكنت وإن ركضت سبقت وكانت ألف فرس عرضت عليه بعد ان صلى الظهر لإرادته الجهاد عليها لعدو فعند بلوغ العرض منها تسعمائة غربت الشمس ولم يكن صلى العصر فاغتم.'),
(4002, 38, 32, '«فقال إني أحببت» أي أردت «حب الخير» أي الخيل «عن ذكر ربي» أي صلاة العصر «حتى توارت» أي الشمس «بالحجاب» أي استترت بما يحجبها عن الأبصار.'),
(4003, 38, 33, '«ردُّوها عليَّ» أي الخيل المعروضة فردوها «فطفق مسحا» بالسيف «بالسوق» جمع ساق «والأعناق» أي ذبحها وقطع أرجلها تقربا إلى الله تعالى حيث اشتغل بها عن الصلاة بلحمها فعوضه الله خيرا منها وأسرع، وهي الريح تجري بأمره كيف شاء.'),
(4004, 38, 34, '«ولقد فتنا سليمان» ابتليناه بسلب ملكه وذلك لتزوجه بامرأة هواها وكانت تعبد الصنم في داره من غير علمه وكان ملكه في خاتمه فنزعه مرة عند إرادة الخلاء ووضعه عند امرأته المسماة بالأمينة على عادته فجاءها جني في صورة سليمان فأخذه منها «وألقينا على كرسيه جسدا» هو ذلك الجني وهو صخر أو غيره جلس على كرسي سليمان وعكفت عليه الطير وغيرها فخرج سليمان في غير هيئته فرآه على كرسيه وقال للناس أنا سليمان فأنكره «ثم أناب» رجع سليمان إلى ملكه بعد أيام بأن وصل إلى الخاتم فلبسه وجلس على كرسيه.'),
(4005, 38, 35, '«قال رب اغفر لي وهب لي ملكا لا ينبغي» لا يكون «لأحد من بعدي» أي سواي نحو (فمن يهديه من بعد الله) أي سوى الله «إنك أنت الوهاب».'),
(4006, 38, 36, '«فسخرنا له الريح تجري بأمره رُخاء» لينة «حيث أصاب» أراد.'),
(4007, 38, 37, '«والشياطين كل بناءٍ» يبني الأبنية العجيبة «وغوَّاص» في البحر يستخرج اللؤلؤ.'),
(4008, 38, 38, '«وآخرين» منهم «مقرنين» مشدودين «في الأصفاد» القيود بجمع أيديهم إلى أعناقهم.'),
(4009, 38, 39, 'وقلنا له «هذا عطاؤنا فامنن» أعط منه من شئت «أو أمسك» عن الإعطاء «بغير حساب» أي لا حساب عليك في ذلك.'),
(4010, 38, 40, '«وإن له عندنا لزلفى وحسن مآب» تقدم مثله.'),
(4011, 38, 41, '«واذكر عبدنا أيوب إذ نادى ربه أني» أي بأني «مسنيَ الشيطان بنصب» ضر «وعذاب» ألم، ونسب ذلك إلى الشيطان وإن كانت الأشياء كلها من الله تأدبا معه تعالى.'),
(4012, 38, 42, 'وقيل له «اركض» اضرب «برجلك» الأرض فضرب فنبعت عين ماء فقيل: «هذا مغتسل» ماء تغتسل به «بارد وشراب» تشرب منه، فاغتسل وشرب فذهب عنه كل داء كان بباطنه وظاهره.'),
(4013, 38, 43, '«ووهبنا له أهله ومثلهم معهم» أي أحيا الله له من مات من أولاده ورزقه مثلهم «رحمة» نعمة «منا وذكرى» عظة «لأولي الألباب» لأصحاب العقول.'),
(4014, 38, 44, '«وخذ بيدك ضغثا» هو حزمة من حشيش أو قضبان «فاضرب به» زوجتك وكان قد حلف ليضربها مائة ضربة لإبطائها عليه يوما «ولا تحنث» بترك ضربها فأخذ مائة عود من الإذخر أو غيره فضربها به ضربة واحدة «إنا وجدناه صابرا نعم العبد» أيوب «إنه أوَّاب» رجّاع إلى الله تعالى.'),
(4015, 38, 45, '«واذكر عبادنا إبراهيم وإسحاق ويعقوب أولي الأيدى» أصحاب القوى في العبادة «والأبصار» البصائر في الدين، وفي قراءة عبدنا وإبراهيم بيان له وما بعده عطف على عبدنا.'),
(4016, 38, 46, '«إنا أخلصناهم بخالصة» هي «ذكرى الدار» الآخرة، أي ذكرها والعمل لها، وفي قراءة بالإضافة وهي للبيان.'),
(4017, 38, 47, '«وإنهم عندنا لمن المصطفين» المختارين «الأخيار» جمع خيّر بالتشديد.'),
(4018, 38, 48, '«واذكر إسماعيل واليسع» وهو نبيّ واللام زائدة «وذا الكفل» اختلف في نبوَّته، قيل كفل مائة نبي فروا إليه من القتل «وكل» أي كلهم «من الأخيار» جمع خيّر بالتثقيل.'),
(4019, 38, 49, '«هذا ذكر» لهم بالثناء الجميل هنا «وإن للمتقين» الشاملين لهم «لحسن مآب» مرجع في الآخرة.'),
(4020, 38, 50, '«جنات عدن» بدل أو عطف بيان لحسن مآب «مفتتحة لهم الأبواب» منها.'),
(4021, 38, 51, '«متكئين فيها» على الأرائك «يدعون فيها بفاكهة كثيرة وشراب».'),
(4022, 38, 52, '«وعندهم قاصرات الطرف» حابسات العين على أزواجهن «أتراب» أسنانهن واحدة وهن بنات ثلاث وثلاثين سنة جمع ترب.'),
(4023, 38, 53, '«هذا» المذكور «ما يوعدون» بالغيبة وبالخطاب التفافا «ليوم الحساب» أي لأجله.'),
(4024, 38, 54, '«إن هذا لرزقنا ما له من نفاد» أي انقطاع والجملة حال من رزقنا أو خبر ثان لإن، أي دائما أو دائم.'),
(4025, 38, 55, '«هذا» المذكور للمؤمنين «وإن للطاغين» مستأنف «لشر مآب».'),
(4026, 38, 56, '«جهنم يصلونها» يدخلونها «فبئس المهاد» الفراش.'),
(4027, 38, 57, '«هذا» أي العذاب المفهوم مما بعده «فليذوقوه حميم» أي ماء حار محرق «وغسَّاق» بالتخفيف والتشديد: ما يسيل من صديد أهل النار.'),
(4028, 38, 58, '«وآخر» بالجمع والإفراد «من شكله» أي مثل المذكور من الحميم والغساق «أزواج» أصناف، أي عذابهم من أنواع مختلفة.'),
(4029, 38, 59, 'ويقال لهم عند دخولهم النار بأتباعهم «هذا فوج» جمع «مقتحم» داخل «معكم» النار بشدة فيقول المتبعون «لا مرحبا بهم» أي لا سعة عليهم «إنهم صالوا النار».'),
(4030, 38, 60, '«قالوا» أي الأتباع «بل أنتم لا مرحبا بكم أنتم قدمتموه» أي الكفر «لنا فبئس القرار» لنا ولكم النار.'),
(4031, 38, 61, '«قالوا» أيضا «ربنا من قدم لنا هذا فزده عذابا ضعفا» أي مثل عذابه على كفره «في النار».'),
(4032, 38, 62, '«وقالوا» أي كفار مكة وهم في النار «ما لنا لا نرى رجالا كنا نعدهم» في الدنيا «من الأشرار».'),
(4033, 38, 63, '«اتَّخذناهم سخريا» بضم السين وكسرها: كنا نسخر بهم في الدنيا، والياء للنسب: أي أمفقودون هم «أم زاغت» مالت «عنهم الأبصار» فلم ترهم، وهم فقراء المسلمين كعمار وبلال وصهيب وسليمان.'),
(4034, 38, 64, '«إن ذلك لحق» واجب وقوعه وهو «تخاصم أهل النار» كما تقدم.'),
(4035, 38, 65, '«قل» يا محمد لكفار مكة «إنما أنا منذر» مخوّف بالنار «وما من إله إلا الله الواحد القهار» لخلقه.'),
(4036, 38, 66, '«رب السماوات والأرض وما بينهما العزيز» الغالب على أمره «الغفار» لأوليائه.'),
(4037, 38, 67, '«قل» لهم «هو نبأ عظيم».'),
(4038, 38, 68, '«أنتم عنه معرضون» أي القرآن الذي أنبأتكم به وجئتكم فيه بما لا يعلم إلا بوحي وهو قوله.'),
(4039, 38, 69, '«ما كان ليَ من علم بالملإ الأعلى» أي الملائكة «إذ يختصمون» في شأن آدم حين قال الله تعالى: (إني جاعل في الأرض خليفة) الخ.'),
(4040, 38, 70, '«إن» ما «يوحى إليَّ إلا أنما أنا» أي أني «نذير مبين» بيَّن الإنذار.'),
(4041, 38, 71, 'اذكر «إذ قال ربك للملائكة إني خالق بشرا من طين» هو آدم.'),
(4042, 38, 72, '«فإذا سويته» أتمته «ونفخت» أجريت «فيه من روحي» فصار حيا، وإضافة الروح إليه تشريف لآدم والروح جسم لطيف يحيا به الإنسان بنفوذه فيه «فقعوا له ساجدين» سجود تحية بالانحناء.'),
(4043, 38, 73, '«فسجد الملائكة كلهم أجمعون» فيه تأكيدان.'),
(4044, 38, 74, '«إلا إبليس» هو أبو الجنّ كان بين الملائكة «استكبر وكان من الكافرين» في علم الله تعالى.'),
(4045, 38, 75, '«قال يا إبليس ما منعك أن تسجد لما خلقت بيديَّ» أي توليت خلقه وهذا تشريف لآدم فإن كل مخلوق تولى الله خلقه «أستكبرت» الآن عن السجود استفهام توبيخ «أم كنت من العالين» المتكبرين فتكبرت عن السجود لكونك منهم.'),
(4046, 38, 76, '«قال أنا خير منه خلقتني من نار وخلقته من طين».'),
(4047, 38, 77, '«قال فاخرج منها» أي من الجنة، وقيل من السماوات «فإنك رجيم» مطرود.'),
(4048, 38, 78, '«وإن عليك لعنتي إلى يوم الدين» الجزاء.'),
(4049, 38, 79, '«قال رب فأنظرني إلى يوم يبعثون» أي الناس.'),
(4050, 38, 80, '«قال فإنك من المنظرين».'),
(4051, 38, 81, '«إلى يوم الوقت المعلوم» وقت النفخة الأولى.'),
(4052, 38, 82, '«قال فبعزتك لأغوينهم أجمعين».'),
(4053, 38, 83, '«إلا عبادك منهم المخلصين» أي المؤمنين.'),
(4054, 38, 84, '«قال فالحقَّ والحقَّ أقول» بنصبهما ورفع الأول ونصب الثاني، فنصبه بالفعل بعده ونصب الأول، قيل بالفعل المذكور، وقيل على المصدر: أي أحق الحق، وقيل على نزع حرف القسم ورفعه على أنه مبتدأ محذوف الخبر: أي فالحق مني، وقيل فالحق قسمي، وجواب القسم.'),
(4055, 38, 85, '«لأملأن جهنم منك» بذريتك «وممن تبعك منهم» أي الناس «أجمعين».'),
(4056, 38, 86, '«قل ما أسألكم عليه» على تبليغ الرسالة «من أجر» جُعل «وما أنا من المتكلفين» المتقولين القرآن من تلقاء نفسي.'),
(4057, 38, 87, '«إن هو» أي ما القرآن «إلا ذكر» عظة «للعالمين» للإنس والجن والعقلاء دون الملائكة.'),
(4058, 38, 88, '«ولتعلمن» يا كفار مكة «نبأه» خبر صدقه «بعد حين» أي يوم القيامة، وعلم بمعنى: عرف واللام قبلها لام قسم مقدَّر: أي والله.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, '«تنزيل الكتاب» القرآن مبتدأ «من الله» خبره «العزيز» في ملكه «الحكيم» في صنعه.'),
(4060, 39, 2, '«إنا أنزلنا إليك» يا محمد «الكتاب بالحق» متعلق بأنزل «فاعبد الله مخلصا له الدين» من الشرك: أي موحدا له.'),
(4061, 39, 3, '«ألا الله الدين الخالص» لا يستحقه غيره «والذين اتخذوا من دونه» الأصنام «أولياء» وهم كفار مكة قالوا «ما نعبدهم إلا ليقربونا إلى الله زلفى» قربى مصدر بمعنى تقريبا «إن الله يحكم بينهم» وبين المسلمين «في ما هم فيه يختلفون» من أمر الدين فيدخل المؤمنين الجنة، والكافرين النار «إن الله لا يهدي من هو كاذب» في نسبة الولد إليه «كفار» بعبادته غيره الله.'),
(4062, 39, 4, '«لو أراد الله أن يتخذ ولدا» كما قالوا: (اتخذ الرحمن ولدا) «لاصطفى مما يخلق ما يشاء» واتخذه ولدا غير من قالوا الملائكة بنات الله وعزير ابن الله والمسيح ابن الله «سبحانه» تنزيها له عن اتخاذ الولد «هو الله الواحد القهار» لخلقه.'),
(4063, 39, 5, '«خلق السماوات والأرض بالحق» متعلق بخلق «يكوّر» يدخل «الليل على النهار» فيزيد «ويكور النهار» يدخله «على الليل» فيزيد «وسخَّر الشمس والقمر كل يجري» في فلكه «لأجل مسمى» ليوم القيامة «ألا هو العزيز» الغالب على أمره المنتقم من أعدائه «الغفار» لأوليائه.'),
(4064, 39, 6, '«خلقكم من نفس واحدة» أي آدم «ثم جعل منها زوجها» حواء «وأنزل لكم من الأنعام» الإبل والبقر والغنم الضأن والمعز «ثمانية أزواج» من كلٍ زوجان ذكر وأنثى كما بيّن في سورة الأنعام «يخلقكم في بطون أمهاتكم خلقا من بعد خلق» أي نطفا ثم علقا ثم مضغا «في ظلمات ثلاث» هي ظلمة البطن وظلمة الرحم وظلمة المشيمة «ذلكم الله ربكم له الملك لا إله هو فأنَّى تصرفون» عن عبادته إلى عبادة غيره.'),
(4065, 39, 7, '«إن تكفروا فإن الله غنيٌ عنكم ولا يرضى لعباده الكفر» وإن أراده من بعضهم «وإن تشكروا» الله فتؤمنوا «يرضه» بسكون الهاء وضمها مع إشباع ودونه: أي الشكر «لكم ولا تزر» نفس «وازرة وزر» نفس «أخرى» أي لا تحمله «ثم إلى ربكم مرجعكم فينبئكم بما كنتم تعملون إنه عليم بذات الصدور» بما في القلوب.'),
(4066, 39, 8, '«وإذا مسَّ الإنسان» أي الكافر «ضرٌ دعا ربه» تضرَّع «منيبا» راجعا «إليه ثم إذا خوّله نعمة» أعطاه إنعاما «منه نسيَ» ترك «ما كان يدعو» يتضرّع «إليه من قبل» وهو الله، فما في موضع من «وجعل الله أندادا» شركاء «ليضل» بفتح الياء وضمها «عن سبيله» دين الإسلام «قل تمتع بكفرك قليلا» بقية أجلك «إنك من أصحاب النار».'),
(4067, 39, 9, '«أمَّن» بتخفيف الميم «هو قانت» قائم بوظائف الطاعات «آناء الليل» ساعاته «ساجدا وقائما» في الصلاة «يحذر الآخرة» أي يخاف عذابها «ويرجو رحمة» جنة «ربه» كمن هو عاص بالكفر أو غيره، وفي قراءة أم من فأم بمعنى بل والهمزة «قل هل يستوي الذين يعلمون والذين لا يعلمون» أي لا يستويان كما لا يستوي العالم والجاهل «إنما يتذكر» يتعظ «أولوا الألباب» أصحاب العقول.'),
(4068, 39, 10, '«قل يا عباد الذين آمنوا اتقوا ربكم» أي عذابه بأن تطيعوه «للذين أحسنوا في هذه الدنيا» بالطاعة «حسنة» هي الجنة «وأرض الله واسعة» فهاجروا إليها من بين الكفار ومشاهدة المنكرات «إنما يوفى الصابرون» على الطاعة وما يبتلون به «أجرهم بغير حساب» بغير مكيال ولا ميزان.'),
(4069, 39, 11, '«قل إني أمرت أن أعبد الله مخلصا له الدين» من الشرك.'),
(4070, 39, 12, '«وأمرت لأن» أي بأن «أكون أول المسلمين» من هذه الأمة.'),
(4071, 39, 13, '«قل إني أخاف إن عصيت ربي عذاب يوم عظيم».'),
(4072, 39, 14, '«قل الله أعبد مخلصا له ديني» من الشرك.'),
(4073, 39, 15, '«فاعبدوا ما شئتم من دونه» غيره، فيه تهديد لهم وإيذان بأنهم لا يعبدون الله تعالى «قل إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة» بتخليد الأنفس في النار وبعدم وصولهم إلى الحور المعدَّة لهم في الجنة لو آمنوا «ألا ذلك هو الخسران المبين» البيَّن.'),
(4074, 39, 16, '«لهم من فوقهم ظلل» طباق «من النار ومن تحتهم ظلل» من النار «ذلك يخوِّف الله به عباده» أي المؤمنين ليتقوه يدل عليه «يا عباد فاتقون».'),
(4075, 39, 17, '«والذين اجتنبوا الطاغوت» الأوثان «أن يعبدوها وأنابوا» أقبلوا «إلى الله لهم البشرى» بالجنة «فبشر عباد».'),
(4076, 39, 18, '«الذين يستمعون القول فيتبعون أحسنه» وهو ما فيه صلاحهم «أولئك الذين هداهم الله وأولئك هم أولوا الألباب» أصحاب العقول.'),
(4077, 39, 19, '«أفمن حق عليه كلمة العذاب» أي: (لأملأن جهنم) «أفأنت تنقذ» تخرج «من في النار» جواب الشرط وأقيم فيه الظاهر مقام المضمر والهمزة للإنكار، والمعنى لا تقدر على هدايته فتنقذه من النار.'),
(4078, 39, 20, '«لكن الذين اتقوْا ربهم» بأن أطاعوه «لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار» أي من تحت الغرف الفوقانية والتحتانية «وعد الله» منصوب بفعله المقَّدر «لا يخلف الله الميعاد» وعده.'),
(4079, 39, 21, '«ألم تر» تعلم «أن الله أنزل من السماء ماءً فسلكه ينابيع» أدخله أمكنة نبع «في الأرض ثم يخرج به زرعا مختلفا ألوانه ثم يهيج» ييبس «فتراه» بعد الخضرة مثلا «مصفرا ثم يجعله حطاما» فتاتا «إن في ذلك لذكرى» تذكيرا «لأولي الألباب» يتذكرون به لدلالته على وحدانية الله تعالى وقدرته.'),
(4080, 39, 22, '«أفمن شرح الله صدره للإسلام» فاهتدى «فهو على نور من ربه» كمن طبع على قلبه، دلَّ على هذا «فويلٌ» كلمة عذاب «للقاسية قلوبهم من ذكر الله» أي عن قبول القرآن «أولئك في ضلال مبين» بيّن.'),
(4081, 39, 23, '«الله نزَّل أحسن الحديث كتابا» بدل من أحسن، أي قرآنا «متشابها» أي يشبه بعضه بعضا في النظم وغيره «مثانَي» ثني فيه الوعد والوعيد وغيرهما «تقشعر منه» ترتعد عند ذكره وعيده «جلود الذين يخشوْن» «يخافون ربهم ثم تلين» تطمئن «جلودهم وقلوبهم إلى ذكر الله» أي عند ذكر وعده «ذلك» أي الكتاب «هدى الله يهدي به من يشاء ومن يضلل الله فما له من هاد».'),
(4082, 39, 24, '«أفمن يتقي» يلقى «بوجهه سوء العذاب يوم القيامة» أي أشده بأن يلقى في النار مغلولة يداه إلى عنقه كمن أمن منه بدخول الجنة «وقيل للظالمين» أي كفار مكة «ذوقوا ما كنتم تكسبون» أي جزاءَه.'),
(4083, 39, 25, '«كذَّب الذين من قبلهم» رسلهم في إتيان العذاب «فأتاهم العذاب من حيث لا يشعرون» من جهة لا تخطر ببالهم.'),
(4084, 39, 26, '«فأذاقهم الله الخزي» الذل والهوان من المسخ والقتل وغيره «في الحياة الدنيا ولعذاب الآخرة أكبر لو كانوا» أي المكذبون «يعلمون» عذابها ما كذبوا.'),
(4085, 39, 27, '«ولقد ضربنا» جعلنا «للناس في هذا القرآن من كل مثل لعلهم يتذكرون» يتعظون.'),
(4086, 39, 28, '«قرآنا عربيا» حال مؤكدة «غير ذي عوج» أي لبس واختلاف «لعلهم يتقون» الكفر.'),
(4087, 39, 29, '«ضرب الله» للمشرك والموحِّد «مثلا رجلا» بدل من مثلا «فيه شركاء متشاكسون» متنازعون سيئة أخلاقهم «ورجلا سالما» خالصا «لرجل هل يستويان مثلا» تميز أي لا يستوي العبد بجماعه والعبد لواحد، فإن الأول إذا طلب منه كل من مالكيه خدمته في وقت واحد تحيَّر فيمن يخدمه منهم وهذا مثل للمشرك، والثاني مثل للموحّد «الحمد لله» وحده «بل أكثرهم» أي أهل مكة «لا يعلمون» ما يصيرون إليه من العذاب فيشركون.'),
(4088, 39, 30, '«إنك» خطاب للنبي صلى الله عليه وسلم «ميت وإنهم ميتون» ستموت ويموتون فلا شماتة بالموت، نزلت لما استبطؤوا موته صلى الله عليه وسلم.'),
(4089, 39, 31, '«ثم إنكم» أيها الناس فيما بينكم من المظالم «يوم القيامة عند ربكم تختصمون».'),
(4090, 39, 32, '«فمن» أي لا أحد «أظلم ممن كذب على الله» بنسبة الشريك والولد إليه «وكذَّب بالصدق» بالقرآن «إذ جاءه أليس في جهنم مثوىً» مأوى «للكافرين» بلى.'),
(4091, 39, 33, '«والذى جاء بالصدق» هو النبي صلى الله عليه وسلم «وصدق به» هم المؤمنون فالذي بمعنى الذين «أولئك هم المتقون» الشرك.'),
(4092, 39, 34, '«لهم ما يشاءون عند ربهم ذلك جزاء المحسنين» لأنفسهم بإيمانهم.'),
(4093, 39, 35, '«ليكفّر الله عنهم أسوأ الذي عملوا ويجزيهم أجرهم بأحسن الذي كانوا يعملون» أسوأ وأحسن بمعنى السيء والحسن.'),
(4094, 39, 36, '«أليس الله بكاف عبده» أي النبي، بلى «ويخوّفونك» الخطاب له «بالذين من دونه» أي الأصنام، أن تقتله أو تخبله «ومن يضلل الله فما له من هاد».'),
(4095, 39, 37, '«ومن يهد الله فما له من مضلِّ أليس الله بعزيز» غالب على أمره «ذي انتقام» من أعدائه؟ بلى.'),
(4096, 39, 38, '«ولئن» لام قسم «سألتهم من خلق السماوات والأرض ليقولنَّ الله قل أفرأيتم ما تدعون» تعبدون «من دون الله» أي الأصنام «إن أرادني الله بضرِّ هل هن كاشفات ضرَّه» لا «أو أرادني برحمة هل هن ممسكاتٌ رحمتَه» لا، وفي قراءة بالإضافة فيهما «قل حسبيَ الله عليه يتوكل المتوكلون» يثق الواثقون.'),
(4097, 39, 39, '«قل يا قوم اعملوا على مكانتكم» حالتكم «إني عامل» على حالتي «فسوف تعلمون».'),
(4098, 39, 40, '«من» موصولة مفعول العلم «يأتيه عذاب يخزيه ويحل» ينزل «عليه عذاب مقيم» دائم هو عذاب النار، وقد أخزاهم الله ببدر.'),
(4099, 39, 41, '«إنا أنزلنا عليك الكتاب للناس بالحق» متعلق بأنزل «فمن اهتدى فلنفسه» اهتداؤه «ومن ضل فإنما يضل عليها وما أنت عليهم بوكيل» فتجبرهم على الهدى.'),
(4100, 39, 42, '«الله يتوفى الأنفس حين موتها و» يتوفى «التي لم تمت في منامها» أي يتوفاها وقت النوم «فيمسك التي قضى عليها الموت ويرسل الأخرى إلى أجل مسمى» أي وقت موتها والمرسلة نفس التمييز تبقى بدونها نفس الحياة بخلاف العكس «إن في ذلك» المذكور «لآيات» دلالات. «لقوم يتفكرون» فيعلمون أن القادر على ذلك، قادر على البعث، وقريش لم يتفكروا في ذلك.'),
(4101, 39, 43, '«أم» بل «اتخذوا من دون الله» أي الأصنام آلهة «شفعاء» عن الله بزعمهم «قل» لهم «أ» يشفعون «ولو كانوا لا يملكون شيئا» من الشفاعة وغيرها «ولا يعقلون» أنكم تعبدونهم ولا غير ذلك؟ لا.'),
(4102, 39, 44, '«قل لله الشفاعة جميعا» أي هو مختص بها فلا يشفع أحد إلا بإذنه «له ملك السماوات والأرض ثم إليه ترجعون».'),
(4103, 39, 45, '«وإذا ذكر الله وحده» أي دون آلهتهم «اشمأزَّت» نفرت وانقبضت «قلوب الذين لا يؤمنون بالآخرة وإذا ذكر الذين من دونه» أي الأصنام «إذا هم يستبشرون».'),
(4104, 39, 46, '«قل اللهم» بمعنى يا الله «فاطر السماوات والأرض» مبدعهما «عالم الغيب والشهادة» ما غاب وما شوهد «أنت تحكم بين عبادك فيما كانوا فيه يختلفون» من أمر الدين اهدني لما اختلفوا فيه من الحق.'),
(4105, 39, 47, '«ولو أن للذين ظلموا ما في الأرض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة وبدا» ظهر «لهم من الله ما لم يكونوا يحتسبون» يظنون.'),
(4106, 39, 48, '«وبدا لهم سيئات ما كسبوا وحاق» نزل «بهم ما كانوا به يستهزءُون» أي العذاب.'),
(4107, 39, 49, '«فإذا مسَّ الإنسان» الجنس «ضر دعانا ثم إذا خوّلناه» أعطيناه «نعمة» إنعاما «منا قال إنما أوتيته على علم» من الله بأني له أهل «بل هي» أي القولة «فتنة» بلية يبتلى بها العبد «ولكن أكثرهم لا يعلمون» أي التخويل استدراج وامتحان.'),
(4108, 39, 50, '«قد قالها الذين من قبلهم» من الأمم كقارون وقومه الراضين بها «فما أغنى عنهم ما كانوا يكسبون».'),
(4109, 39, 51, '«فأصابهم سيئات ما كسبوا» أي جزاؤها «والذين ظلموا من هؤلاء» أي قريش «سيصيبهم سيئات ما كسبوا وما هم بمعجزين» بفائتين عذابنا فقحطوا سبع سنين ثم وسع عليهم.'),
(4110, 39, 52, '«أوَ لم يعلموا أن الله يبسط الرزق» يوسعه «لمن يشاء» امتحانا «ويقدر» يضيقه لمن يشاء ابتلاء «إن في ذلك لآيات لقوم يؤمنون» به.'),
(4111, 39, 53, '«قل يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا» بكسر النون وفتحها، وقرئ بضمها تيأسوا «من رحمة الله إن الله يغفر الذنوب جميعا» لمن تاب من الشرك «إنه هو الغفور الرحيم».'),
(4112, 39, 54, '«وأنيبوا» ارجعوا «إلى ربكم وأسلموا» أخلصوا العمل «له من قبل أن يأتيكم العذاب ثم لا تنصرون» بمنعه إن لم تتوبوا.'),
(4113, 39, 55, '«واتبعوا أحسن ما أنزل إليكم من ربكم» هو القرآن «من قبل أن يأتيكم العذاب بغتة وأنتم لا تشعرون» قبل إتيانه بوقته.'),
(4114, 39, 56, 'فبادروا قبل «أن تقول نفسٌ يا حسرتى» أصله يا حسرتي، أي ندامتي «على ما فرطت في جنب الله» أي طاعته «وإن» مخففة من الثقيلة، وإني «كنت لمن الساخرين» بدينه وكتابه.'),
(4115, 39, 57, '«أو تقول لو أن الله هداني» بالطاعة فاهتديت «لكنت من المتقين» عذابه.'),
(4116, 39, 58, '«أو تقول حين ترى العذاب لو أن لي كرَّة» رجعة إلى الدنيا «فأكون من المحسنين» المؤمنين، فيقال له من قبل الله.'),
(4117, 39, 59, '«بلى قد جاءتك آياتي» القرآن وهو سبب الهداية «فكذبت بها واستكبرت» تكبرت عن الإيمان بها «وكنت من الكافرين».'),
(4118, 39, 60, '«ويوم القيامة ترى الذين كذبوا على الله» بنسبة الشريك والولد إليه «وجوههم مسودة أليس في جهنم مشوى» مأوى «للمتكبرين» عن الإيمان؟ بلى.'),
(4119, 39, 61, '«وينجَّي الله» من جهنم «الذين اتقوْا» الشرك «بمفازتهم» أي بمكان فوزهم من الجنة بأن يجعلوا فيه «لا يمسهم السوء ولا هم يحزنون».'),
(4120, 39, 62, '«الله خالق كل شيء وهو على كل شيء وكيل» متصرف فيه كيف يشاء.'),
(4121, 39, 63, '«له مقاليد السماوات والأرض» أي مفاتيح خزائنهما من المطر والنبات وغيرهما «والذين كفروا بآيات الله» القرآن «أولئك هم الخاسرون» متصل بقوله: (وينجي الله الذين اتقوا) 000الخ وما بينهما اعتراض.'),
(4122, 39, 64, '«قل أفغير الله تأمروني أعبد أيها الجاهلون» غير منصوب بأعبد المعمول لتأمروني بتقدير أن بنون واحدة وبنونين بإدغام وفك.'),
(4123, 39, 65, '«ولقد أوحيَ إليك وإلى الذين من قبلك» والله «لئن أشركت» يا محمد فرضا «ليحبطن عملك ولتكوننَّ من الخاسرين».'),
(4124, 39, 66, '«بل الله» وحده «فاعبدْ وكن من الشاكرين» إنعامه عليك.'),
(4125, 39, 67, '«وما قدروا الله حق قدره» ما عرفوه حق معرفته، أو ما عظموه حق عظمته حين أشركوا به غيره «والأرض جميعا» حال: أي السبع «قبضته» أي مقبوضة له: أي في ملكه وتصرفه «يوم القيامة والسماوات مطويات» مجموعات «بيمينه» بقدرته «سبحانه وتعالى عما يشركون» معه.'),
(4126, 39, 68, '«ونفخ في الصور» النفخة الأولى «فصعق» مات «من في السماوات ومن في الأرض إلا من شاء الله» من الحور والوالدان وغيرهما «ثم نفخ فيه أخرى فإذا هم» أي جميع الخلائق الموتى «قيام ينظرون» ينتظرون ما يفعل بهم.'),
(4127, 39, 69, '«وأشرقت الأرض» أضاءت «بنور ربها» حين يتجلى الله لفصل القضاء «ووضع الكتاب» كتاب الأعمال للحساب «وجيء بالنبيين والشهداء» أي بمحمد صلى الله عليه وسلم وأمته يشهدون للرسل بالبلاغ «وقُضيَ بينهم بالحق» أي العدل «وهم لا يظلمون» شيئا.'),
(4128, 39, 70, '«ووفّيَتْ كلُّ نفس ما عملت» أي جزاءه «وهو أعلم» عالم «بما يفعلون» فلا يحتاج إلى شاهد.'),
(4129, 39, 71, '«وسيق الذين كفروا» بعنف «إلى جهنم زمرا» جماعات متفرقة «حتى إذا جاءُوها فتحت أبوابها» جواب إذا «وقال لهم خزنتها ألم يأتكم رسل منكم يتلون عليكم آيات ربكم» القرآن وغيره «وينذرونكم لقاء يومكم هذا قالوا بلى ولكن حقت كلمة العذاب» أي: (لأملأن جهنم) الآية. «على الكافرين».'),
(4130, 39, 72, '«قيل ادخلوا أبواب جهنم خالدين فيها» مقدّرين الخلود «فبئس مثوى» مأوى «المتكبرين» جهنم.'),
(4131, 39, 73, '«وسيق الذين اتقوا ربهم» بلطف «إلى الجنة زمرا حتى إذا جاءُوها وفتحت أبوابها» الواو فيه للحال بتقدير قد «وقال لهم خزنتها سلام عليكم طبتم» حال «فادخلوها خالدين» مقدّرين الخلود فيها، وجواب إذا مقدر، أي دخولها وسوقهم وفتح الأبواب قبل مجيئهم تكرمة لهم، وسوق الكفار وفتح أبواب جهنم عند مجيئهم ليبقى حرها إليهم إهانة لهم.'),
(4132, 39, 74, '«وقالوا» عطف على دخولها المقدر «الحمد لله الذي صدقنا وعده» بالجنة «وأورثنا الأرض» أي أرض الجنة «نتبوأ» ننزل «من الجنة حيث نشاء» لأنها كلها لا يختار فيها مكان على مكان «فنعم أجر العاملين» الجنة.'),
(4133, 39, 75, '«وترى الملائكة حافّين» حال «من حول العرش» من كل جانب منه «يسبحون» حال من ضمير حافين «بحمد ربهم» ملابسين للحمد: أي يقولون: سبحان الله وبحمده «وقضيَ بينهم» بين جميع الخلائق «بالحق» أي العدل فيدخل المؤمنون الجنة، والكافرين النار «وقيل الحمد لله رب العالمين» ختم استقرار الفريقين بالحمد من الملائكة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, '«حَم» الله أعلم بمراده به.'),
(4135, 40, 2, '«تنزيل الكتاب» القرآن مبتدأ «من الله» خبره «العزيز» في ملكه «العليم» بخلقه.'),
(4136, 40, 3, '«غافر الذنب» للمؤمنين «وقابل التوب» لهم مصدر «شديد العقاب» للكافرين أي مشدده «ذي الطول» الإنعام الواسع، وهو موصوف على الدوام بكل هذه الصفات، فإضافة المشتق منها للتعريف كالأخيرة «لا إله إلا هو إليه المصير» المرجع.'),
(4137, 40, 4, '«ما يجادل في آيات الله» القرآن «إلا الذين كفروا» من أهل مكة «فلا يغررك تقلبهم في البلاد» للمعاش سالمين فإن عاقبتهم النار.'),
(4138, 40, 5, '«كذبت قبلهم قوم نوح والأحزاب» كعاد وثمود وغيرهما «من بعدهم وهمّت كل أمة برسولهم ليأخذوه» يقتلوه «وجادلوا بالباطل ليدحضوا» يزيلوا «به الحق فأخذتهم» بالعقاب «فكيف كان عقاب» لهم، أي هو واقع موقعه.'),
(4139, 40, 6, '(وكذلك حقت كلمة ربك) أي "" لأملأن جهنم "" الآية (على الذين كفروا أنهم أصحاب النار) بدل من كلمة.'),
(4140, 40, 7, '«الذين يحملون العرش» مبتدأ «ومن حوله» عطف عليه «يسبحون» خبره «بحمد ربهم» ملابسين للحمد، أي يقولون: سبحان الله وبحمده «ويؤمنون به» تعالى ببصائرهم أي يصدقون بوحدانيته «ويستغفرون للذين آمنوا» يقولون «ربنا وسعت كل شيءٍ رحمة وعلما» أي وسعت رحمتك كلَّ شيء وعلمك كل شيء «فاغفر للذين تابوا» من الشرك «واتبعوا سبيلك» دين الإسلام «وقهم عذاب الجحيم» النار.'),
(4141, 40, 8, '«ربنا وأدخلهم جنات عدن» إقامة «التي وعدتهم ومن صلح» عطف على هم في وأدخلهم أو في وعدتهم «من آبائهم وأزواجهم وذرياتهم إنك أنت العزيز الحكيم» في صنعه.'),
(4142, 40, 9, '«وقهم السيئات» أي عذابها «ومن تق السيئات يومئذ» يوم القيامة «فقد رحمته وذلك هو الفوز العظيم».'),
(4143, 40, 10, '«إن الذين كفروا ينادوْن» من قبل الملائكة وهم يمقتون أنفسهم عند دخولهم النار «لمقت الله» إياكم «أكبر من مقتكم أنفسكم إذ تدعوْن» في الدنيا «إلى الإيمان فتكفرون».'),
(4144, 40, 11, '«قالوا ربنا أمتَّنا اثنتين» إماتتين «وأحييتنا اثنتين» إحياءتين لأنهم نطفٌ أموات فأحيوا ثم أميتوا ثم أحيوا للبعث «فاعترفنا بذنوبنا» بكفرنا بالبعث «فهل إلى خروج» من النار والرجوع إلى الدنيا لنطيع ربنا «من سبيل» طريق وجوابهم: لا.'),
(4145, 40, 12, '«ذلكم» أي العذاب أنتم فيه «بأنه» أي بسبب أنه في الدنيا «إذا دعي الله وحده كفرتم» بتوحيده «وإن يُشرَك به» يجعل له شريك «تؤمنوا» تصدقوا بالإشراك «فالحكم» في تعذيبكم «لله العليِّ» على خلقه «الكبير» العظيم.'),
(4146, 40, 13, '«هو الذي يريكم آياته» دلائل توحيده «وينزل لكم من السماء رزقا» بالمطر «وما يتذكر» يتعظ «إلا من ينيب» يرجع عن الشرك.'),
(4147, 40, 14, '«فادعوا الله» اعبدوه «مخلصين له الدين» من الشرك «ولو كره الكافرون» إخلاصكم منه.'),
(4148, 40, 15, '«رفيع الدرجات» أي الله عظيم الصفات، أو رافع درجات المؤمنين في الجنة «ذو العرش» خالقه «يلقي الروح» الوحي «من أمره» أي قوله «على من يشاء من عباده لينذر» يخوَّف الملقى عليه الناس «يوم التلاق» بحذف الياء وإثباتها يوم القيامة لتلاقي أهل السماء والأرض، والعابد والمعبود، والظالم والمظلوم فيه.'),
(4149, 40, 16, '«يوم هم بارزون» خارجون من قبورهم «لا يخفى على الله منهم شيءٌ لمن الملك اليوم» بقوله تعالى، ويجيب نفسه «لله الواحد القهار» أي لخلقه.'),
(4150, 40, 17, '«اليوم تجزى كل نفس بما كسبت لا ظلم اليوم إن الله سريع الحساب» يحاسب جميع الخلق في قدر نصف نهار من أيام الدنيا لحديث بذلك.'),
(4151, 40, 18, '(وأنذرهم يوم الآزفة) يوم القيامة من أزف الرحيل: قرب (إذ القلوب) ترتفع خوفا (لدى) عند (الحناجر كاظمين) ممتلئين غما حال من القلوب عوملت بالجمع بالياء والنون معاملة أصحابها (ما للظالمين من حميم) محب (ولا شفيع يطاع) لا مفهوم للوصف إذ لا شفيع لهم أصلا "" فما لنا من شافعين "" أوله مفهوم بناء على زعمهم أن لهم شفعاء، أي لو شفعوا فرضا لم يقبلوا.'),
(4152, 40, 19, '«يعلم أي الله «خائنة الأعين» بمارقتها النظر إلى محرَّم «وما تخفي الصدور» القلوب.'),
(4153, 40, 20, '«والله يقضي بالحق والذين يدعون» يعبدون، أي كفار مكة بالياء والتاء «من دونه» وهم الأصنام «لا يقضون بشيءٍ» فكيف يكونون شركاء لله «إن الله هو السميع» لأقوالهم «البصير» بأفعالهم.'),
(4154, 40, 21, '«أوَ لم يسيروا في الأرض فينظروا كيف كان عاقبة الذين كانوا من قبلهم كانوا هم أشد منهم» وفي قراءة: منكم «قوة وآثارا في الأرض» من مصانع وقصور «فأخذهم الله» أهلكهم «بذنوبهم وما كان لهم من الله من واق» عذابه.'),
(4155, 40, 22, '«ذلك بأنهم كانت تأتيهم رسلهم بالبينات» بالمعجزات الظاهرات «فكفروا فأخذهم الله إنه قويٌ شديد العقاب».'),
(4156, 40, 23, '«ولقد أرسلنا موسى بآياتنا وسلطان مبين» برهان بيَّن ظاهر.'),
(4157, 40, 24, '«إلى فرعون وهامان وقارون فقالوا» هو «ساحرٌ كذاب».'),
(4158, 40, 25, '«فلما جاءهم بالحق» بالصدق «من عندنا «قالوا اقتلوا أبناء الذين آمنوا معه واستحيوا» استبقوا «نساءهم وما كيْد الكافرين إلا في ضلال» هلاك.'),
(4159, 40, 26, '«وقال فرعون ذروني أقتل موسى» لأنهم كانوا يكفونه عن قتله «وليدع ربه» ليمنعه مني «إني أخاف أن يبدل دينكم» من عبادتكم إياي فتتبعوه «وأن يُظهر في الأرض الفساد» من قتل وغيره، وفي قراءة: أو، وفي أخرى بفتح الياء والهاء وضم الدال.'),
(4160, 40, 27, '«وقال موسى» لقومه وقد سمع ذلك إني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب».'),
(4161, 40, 28, '«وقال رجل مؤمن من آل فرعون» قيل: هو ابن عمه «يكتم إيمانه أتقتلون رجلا أن» أي لأن «يقول ربيَ الله وقد جاءَكم بالبينات» بالمعجزات الظاهرات «من ربكم وإن يك كاذبا فعليه كذبه» أي ضرر كذبه «وإن يك صادقا يصبكم بعض الذي يعدكم» به من العذاب عاجلا «إن الله لا يهدي من هو مسرف» مشرك «كذاب» مفتر.'),
(4162, 40, 29, '«يا قوم لكم الملك اليوم ظاهرين» غالبين حال «في الأرض» أرض مصر «فمن ينصرنا من بأس الله» عذابه إن قتلتم أولياءه «إن جاءنا» أي لا ناصر لنا «قال فرعون ما أريكم إلا ما أرى» أي ما أشير عليكم إلا بما أشير به على نفسي وهو قتل موسى «وما أهديكم إلا سبيل الرشاد» طريق الصواب.'),
(4163, 40, 30, '«وقال الذي آمن يا قوم إنِّي أخاف عليكم مثل يوم الأحزاب» أي يوم حزب بعد حزب.'),
(4164, 40, 31, '«مثل دأب قوم نوح وعاد وثمود والذين من بعدهم» مثل بدل من مثل قبله، أي مثل جزاء عادة من كفر قبلكم من تعذيبهم في الدنيا «وما الله يريد ظلما لعباد».'),
(4165, 40, 32, '«ويا قوم إني أخاف عليكم يوم التناد» بحذف الياء وإثباتها، أي يوم القيامة يكثر فيه نداء أصحاب الجنة أصحاب النار وبالعكس، والنداء بالسعادة لأهلها وبالشقاوة لأهلها وغير ذلك.'),
(4166, 40, 33, '«يوم تولون مدبرين» عن موقف الحساب إلى النار «ما لكم من الله» أي من عذابه «من عاصم» مانع «ومن يضلل الله فما له من هادِ».'),
(4167, 40, 34, '«ولقد جاءكم يوسف من قبل» أي قبل موسى وهو يوسف بن يعقوب في قول، عمَّر إلى زمن موسى، أو يوسف بن إبراهيم بن يوسف بن يعقوب في قول «بالبينات» بالمعجزات الظاهرات «فما زلتم في شك مما جاءكم به حتى إذا هلك قلتم» من غير برهان «لن يبعث الله من بعده رسولا» أي فلن تزالوا كافرين بيوسف وغيره «كذلك» أي مثل إضلالكم «يضل الله من هو مسرف» مشرك «مرتاب» شاك فيما شهدت به البينات.'),
(4168, 40, 35, '«الذين يجادلون في آيات الله» معجزاته مبتدأ «بغير سلطان» برهان «أتاهم كَبُر» جدالهم خبر المبتدأ «مقتا عند الله وعند الذين آمنوا كذلك» مثل إضلالهم «يطبع» يختم «الله» بالضلال «على كل قلب متكبَّرِ جبار» بتنوين قلب ودونه، ومتى تكبَّر القلب، تكبَّر صاحبه وبالعكس، وكل على القراءتين لعموم الضلال جميع القلب لا لعموم القلب.'),
(4169, 40, 36, '«وقال فرعون يا هامان ابن لي صرحا» بناءً عاليا «لعلي أبلغ الأسباب».'),
(4170, 40, 37, '«أسباب السماوات» طرقها الموصولة إليها «فأطلع» بالرفع عطفا على أبلغ وبالنصب جوابا لابنِ «إلى إله موسى وإني لأظنه» أي موسى «كاذبا» في أن له إلها غيري قال فرعون ذلك تمويها «وكذلك زيَّن لفرعون سوء عمله وصدَّ عن السبيل» طريق الهدي بفتح الصاد وضمها «وما كيد فرعون إلا في تباب» خسار.'),
(4171, 40, 38, '«وقال الذي آمن يا قوم اتَّبعون» بإثبات الياء وحذفها «أهدكم سبيل الرشاد» تقدم.'),
(4172, 40, 39, '«يا قوم إنما هذه الحياة الدنيا متاع» تمتع يزول «وإن الآخرة هي دار القرار».'),
(4173, 40, 40, '«من عمل سيئة فلا يُجزى إلا مثلها ومن عمل صالحا من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة» بضم الياء وفتح الخاء وبالعكس «يرزقون فيها بغير حساب» رزقا واسعا بلا تبعة.'),
(4174, 40, 41, '«ويا قوم مالي أدعوكم إلى النجاة وتدعونني إلى النار».'),
(4175, 40, 42, '«تدعونني لأكفر بالله وأشرك به ما ليس لي به علم وأنا أدعوكم إلى العزيز» الغالب على أمره «الغفار» لمن تاب.'),
(4176, 40, 43, '«لا جرم» حقا «أنما تدعونني إليه» لأعبده «ليس له دعوة» أي استجابة دعوة «في الدنيا ولا في الآخرة وأن مردنا» مرجعنا «إلى الله وأن المسرفين» الكافرين «هم أصحاب النار».'),
(4177, 40, 44, '«فستذكرون» إذا عاينتم العذاب «ما أقول لكم وأفوض أمري إلى الله إن الله بصير بالعباد» قال ذلك لما توعدوه بمخالفة دينهم.'),
(4178, 40, 45, '«فوقاه الله سيئات ما مكروا» به من القتل «وحاق» نزل «بآل فرعون» قومه معه «سوء العذاب» الغرق.'),
(4179, 40, 46, 'ثم (النار يعرضون عليها) يحرقون بها «غدوا وعشيا» صباحا ومساءً «ويوم تقوم الساعة» يقال «ادخلُوا» يا «آل فرعون» وفي قراءة: بفتح الهمزة وكسر الخاء أمر للملائكة «أشد العذاب» عذاب جهنم.'),
(4180, 40, 47, '«و» اذكر «إذ يتحاجون» يتخاصم الكفار «في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا» جمع تابع «فهل أنتم مغنون» دافعون «عنا نصيبا» جزاءً «من النار».'),
(4181, 40, 48, '«قال الذين استكبروا إنا كلَّ فيها إن الله قد حكم بين العباد» فأدخل المؤمنين الجنة والكافرين النار.'),
(4182, 40, 49, '«وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما» أي قدر يوم «من العذاب».'),
(4183, 40, 50, '«قالوا» أي الخزنة تهكما «أوَ لم تك تأتيكم رسلكم بالبينات» بالمعجزات الظاهرات «قالوا بلى» أي فكفروا بهم «قالوا فادعوا» أنتم فإنا لا نشفع للكافرين، قال تعالى: «وما دعاءُ الكافرين إلا في ضلال» انعدام.'),
(4184, 40, 51, '«إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا ويوم يقوم الأشهاد» جمع شاهد، وهم الملائكة يشهدون للرسل بالبلاغ وعلى الكفار بالتكذيب.'),
(4185, 40, 52, '«يوم لا ينفع» بالياء والتاء «الظالمين معذرتهم» عذرهم لو اعتذروا «ولهم اللعنة» أي البعد من الرحمة «ولهم سوءُ الدار» الآخرة، أي شدة عذابها.'),
(4186, 40, 53, '«ولقد آتينا موسى الهدى» التوراة والمعجزات «وأورثنا بني إسرائيل» من بعد موسى «الكتاب» التوراة.'),
(4187, 40, 54, '«هدىّ» هاديا «وذكرى لأولي الألباب» تذكرة لأصحاب العقول.'),
(4188, 40, 55, '«فاصبر» يا محمد «إن وعد الله» بنصر أوليائه «حق» وأنت ومن تبعك منهم «واستغفر لذنبك» ليستن بك «وسبِّح» صل متلبساً «بحمد ربك بالعشي» وهو من بعد الزوال «والإبكار» الصلوات الخمس.'),
(4189, 40, 56, '«إن الذين يجادلون في آيات الله» القرآن «بغير سلطان» برهان «أتاهم إن» ما «في صدورهم إلا كبْر» تكبّر وطمع أن يعلوا عليك «ما هم ببالغيه فاستعذ» من شرِّهم «بالله إنه هو السميع» لأقوالهم «البصير» بأحوالهم، ونزل في منكري البعث.'),
(4190, 40, 57, '«لخلق السماوات والأرض» ابتداءً «أكبر من خلق الناس» مرة ثانية، وهي الإعادة «ولكن أكثر الناس» أي كفار مكة «لا يعلمون» ذلك فهم كالأعمى، ومن يعلمه كالبصير.'),
(4191, 40, 58, '«وما يستوي الأعمى والبصير و» لا «الذين آمنوا وعملوا الصالحات» وهو المحسن «ولا المسيء» فيه زيادة لا «قليلا ما يتذكرون» يتعظون بالياء والتاء، أي تذكرهم قليل جدا.'),
(4192, 40, 59, '«إن الساعة لآتية لا ريب» شك «فيها ولكن أكثر الناس لا يؤمنون» بها.'),
(4193, 40, 60, '«وقال ربكم ادعوني أستجب لكم» أي اعبدوني أثبكم بقرينة ما بعده «إن الذين يستكبرون عن عبادتي سيدخلون» بفتح الياء وضم الخاء وبالعكس «جهنم داخرين» صاغرين.'),
(4194, 40, 61, '«الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا» إسناد الإبصار إليه مجازيّ لأنه يبصر فيه «إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون» الله فلا يؤمنون.'),
(4195, 40, 62, '«ذلكم الله ربكم خالق كل شيء لا إله إلا هوَ فأنّى تؤفكون» فكيف تصرفون عن الإيمان مع قيام البرهان.'),
(4196, 40, 63, '«كذلك يؤفك» أي مثل إفك هؤلاء إفك «الذين كانوا بآيات الله» معجزاته «يجحدون».'),
(4197, 40, 64, '«الله الذي جعل لكم الأرض قرارا والسماء بناءً» سقفا «وصوركم فأحسن صوركم ورزقكم من الطيبات ذلكم الله ربكم فتبارك الله رب العالمين».'),
(4198, 40, 65, '«هو الحي لا إله إلا هو فادعوه» اعبدوه «مخلصين له الدين» من الشرك «الحمد لله رب العالمين».'),
(4199, 40, 66, '«قل إني نُهيت أن أعبد الذين تدعون» تعبدون «من دون الله لما جاءني البينات» دلائل التوحيد «من ربي وأمرت أن أسلم لرب العالمين».'),
(4200, 40, 67, '«هو الذي خلقكم من تراب» بخلق أبيكم آدم منه «ثم من نطفة» منيّ «ثم من علقة» دم غليظ «ثم يخرجكم طفلا» بمعنى أطفالا «ثم» يبقيكم «لتبلغوا أشدكم» تكامل قوتكم من الثلاثين سنة إلى الأربعين «ثم لتكونوا شيوخا» بضم الشين وكسرها «ومنكم من يتوفى من قبل» أي قبل الأشد والشيخوخة، فعل ذلك بكم لتعيشوا «ولتبلغوا أجلا مسمى» وقتا محدودا «ولعلكم تعقلون» دلائل التوحيد فتؤمنون.'),
(4201, 40, 68, '«هو الذي يحيى ويُميت فإذا قضى أمرا» أراد إيجاد شيء «فإنما يقول له كن فيكون» بضم النون وفتحها بتقدير أن، أي يوجد عقب الإرادة التي هي معنى القول المذكور.'),
(4202, 40, 69, '«ألم ترَ إلى الذين يجادلون في آيات الله» القرآن «أنَّى» كيف «يصرفون» عن الإيمان.'),
(4203, 40, 70, '«الذين كذبوا بالكتاب» القرآن «وبما أرسلنا به رسلنا» من التوحيد والبعث وهم كفار مكة «فسوف يعلمون» عقوبة تكذيبهم.'),
(4204, 40, 71, '«إذ الأغلال في أعناقهم» إذ بمعنى إذا «والسلاسل» عطف على الأغلال فتكون في الأعناق، أو مبتدأ خبره محذوف، أي في أرجلهم أو خبره «يسحبون» أي يجرون بها.'),
(4205, 40, 72, '«في الحميم» أي جهنم «ثم في النار يسجرون» يوقدون.'),
(4206, 40, 73, '«ثم قيل لهم» تبكيا «أين ما كنتم تشركون».'),
(4207, 40, 74, '«من دون الله» معه وهي الأصنام «قالوا ضلوا» غابوا «عنا» فلا نراهم «بل لم نكن تدعوا من قبل شيئا» أنكروا عبادتهم إياها ثم أحضرت قال تعالى: (إنكم وما تعبدون من دون الله حصب جهنم) أي وقودها «كذلك» أي مثل إضلال هؤلاء المكذبين «يضل الله الكافرين».'),
(4208, 40, 75, 'ويقال لهم أيضا «ذلكم» العذاب «بما كنتم تفرحون في الأرض بغير الحق» من الإشراك وإنكار البعث «وبما كنتم تمرحون» تتوسعون في الفرح.'),
(4209, 40, 76, '«ادخلوا أبواب جهنم خالدين فيها فبئس مثوى» مأوى «المتكبرين».'),
(4210, 40, 77, '«فاصبر إن وعد الله» بعذابهم «حق فإما نرينَّك» فيه إن الشرطية مدغمة وما زائدة تؤكد معنى الشرط أول الفعل والنون تؤكد آخره «بعض الذي نعدهم» به من العذاب في حياتك وجواب الشرط محذوف، أي فذاك «أو نتوفينك» أي قبل تعذيبهم «فإلينا يرجعون» فنعذبهم أشد العذاب فالجواب المذكور للمعطوف فقط.'),
(4211, 40, 78, '«ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك» روي أنه تعالى بعث ثمانية آلاف نبي: أربعة آلاف نبي من بني إسرائيل، وأربعة آلاف من سائر الناس «وما كان لرسولِ» منهم «أن يأتي بآية إلا بإذن الله» لأنهم عبيد مربوبون «فإذا جاء أمر الله» بنزول العذاب على الكفار «قضي» بين الرسل ومكذبيها «بالحق وخسر هنالك المبطلون» أي ظهر القضاء والخسران للناس وهم خاسرون في كل وقت قبل ذلك.'),
(4212, 40, 79, '«الله الذي جعل لكم الأنعام» قيل: الإبل خاصة هنا والظاهر والبقر والغنم «لتركبوا منها ومنها تأكلون».'),
(4213, 40, 80, '«ولكم فيها منافع» من الدر والنسل والوبر والصوف «ولتبلغوا عليها حاجة في صدوركم» هي حمل الأثقال إلى البلاد «وعليها» في البر «وعلى الفلك» السفن في البحر «تحملون».'),
(4214, 40, 81, '«ويريكم آياته فأيَّ آيات الله» أي الدالة على وحدانيته «تنكرون» استفهام توبيخ، وتذكير أي أشهر من تأنيثه.'),
(4215, 40, 82, '«أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم كانوا أكثر منهم وأشد قوَّةً وأثارا في الأرض» من مصانع وقصور «فما أغنى عنهم ما كانوا يكسبون».'),
(4216, 40, 83, '«فلما جاءتهم رسلهم بالبينات» المعجزات الظاهرات «فرحوا» أي الكفار «بما عندهم» أي الرسل «من العلم» فرح استهزاء وضحك منكرين له «وحاق» نزل «بهم ما كانوا به يستهزءُون» أي العذاب.'),
(4217, 40, 84, '«فلما رأوْا بأسنا» أي شدة عذابنا «قالوا آمنا بالله وحده وكفرنا بما كنا به مشركين».'),
(4218, 40, 85, '«فلم يكُ ينفعهم إيمانهم لما رأوْا بأسنا سُنَّتَ الله» نصبه على المصدر بفعل مقدِّر من لفظه «التي قد خلت في عباده» في الأمم أن لا ينفعهم الإيمان وقت نزول العذاب «وخسر هنالك الكافرون» تبين خسرانهم لكل أحد وهم خاسرون في كل وقت قبل ذلك.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, '«حم» الله أعلم بمراده.'),
(4220, 41, 2, '«تنزيل من الرحمن الرحيم» مبتدأ.'),
(4221, 41, 3, '«كتاب» خبره «فصلت آياته» بينت بالأحكام والقصص والمواعظ «قرآناً عربيا» حال من كتاب بصفته «لقوم» متعلق بفصلت «يعلمون» يفهمون ذلك، وهم العرب.'),
(4222, 41, 4, '«بشيراً» صفة قرآناً «ونذيراً فأعرض أكثرهم فهم لا يسمعون» سماع قبول.'),
(4223, 41, 5, '«وقالوا» للنبي «قلوبنا في أكنَّةٍ» أغطية «مما تدعونا إليه وفي آذاننا وقر» ثقل «ومن بيننا وبينك حجاب» خلاف في الدين «فاعمل» على دينك «إننا عاملون» على ديننا.'),
(4224, 41, 6, '«قل إنما أنا بشر مثلكم يوحى إليَّ أنما إلهكم إله واحد فاستقيموا إليه» بالإيمان والطاعة «واستغفروه وويل» كلمة عذاب «للمشركين».'),
(4225, 41, 7, '«الذين لا يؤتون الزكاة وهم بالآخرة هم» تأكيد «كافرون».'),
(4226, 41, 8, '«إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون» مقطوع.'),
(4227, 41, 9, '«قل أئنكم» بتحقيق الهمزة الثانية وتسهيلها وإدخال ألف بينها بوجهيها وبين الأولى «لتكفرون بالذي خلق الأرض في يومين» الأحد والاثنين «وتجعلون له أنداداً» شركاء «ذلك رب» أي مالك «العالمين» جمع عالم، وهو ما سوى الله وجمع لاختلاف أنواعه بالياء والنون، تغليباً للعقلاء.'),
(4228, 41, 10, '«وجعل» مستأنف ولا يجوز عطفه على صلة الذي للفاصل الأجنبي «فيها رواسي» جبالاً ثوابت «من فوقها وبارك فيها» بكثرة المياه والزروع والضروع «وقدَّر» قسَّم «فيها أقواتها» للناس والبهائم «في» تمام «أربعة أيام» أي الجعل وما ذكر معه في يوم الثلاثاء والأربعاء «سواءً» منصوب على المصدر، أي استوت الأربعة استواءً لا تزيد ولا تنقص «للسائلين» عن خلق الأرض بما فيها.'),
(4229, 41, 11, '«ثم استوى» قصد «إلى السماء وهي دخان» بخار مرتفع «فقال لها وللأرض ائتيا» إلى مرادي منكما «طوعاً أو كرهاً» في موضع الحال، أي طائعتين أو مكرهتين «قالتا أتينا» بمن فينا «طائعين» فيه تغليب المذكر العاقل أو نزلنا لخاطبها منزلته.'),
(4230, 41, 12, '«فقضاهنَّ» الضمير يرجع إلى السماء لأنها في معنى الجمع الآيلة إليه، أي صيَّرها «سبع سماوات في يومين» الخميس والجمعة فرغ منها في آخر ساعة منه، وفيها خلق آدم ولذلك لم يقل هنا سواء، ووافق ما هنا آيات خلق السماوات والأرض في ستة أيام «وأوحى في كل سماءٍ أمرها» الذي أمر به من فيها من الطاعة والعبادة «وزينا السماء الدنيا بمصابيح» بنجوم «وحفظاً» منصوب بفعله المقدَّر، أي حفظناها من استراق الشياطين السمع بالشهب «ذلك تقدير العزيز» في ملكه «العليم» بخلقه.'),
(4231, 41, 13, '«فإن أعرضوا» أي كفار مكة عن الإيمان بعد هذا البيان «فقل أنذرتكم» خوَّفتكم «صاعقةً مثل صاعقة عادٍ وثمود» عذاباً يهلككم مثل الذي أهلكهم.'),
(4232, 41, 14, '«إذْ جاءتهم الرسل من بين أيديهم ومن خلفهم» أي مقبلين عليهم ومدبرين عنهم فكفروا كما سيأتي، والإهلاك في زمنه فقط «أن» أي بأن «لا تعبدوا إلا الله قالوا لو شاء ربنا لأنزل» علينا «ملائكة فإنا بما أرسلتم به» على زعمكم «كافرون».'),
(4233, 41, 15, '«فأما عاد فاستكبروا في الأرض بغير الحق وقالوا» لما خوِّفوا بالعذاب «من أشد منا قوة» أي لا أحد، كان واحدهم يقلع الصخرة العظيمة من الجبل يجعلها حيث يشاء «أولم يروْا» يعلموا «أن الله الذي خلقهم هو أشد منهم قوَّة وكانوا بآياتنا» المعجزات «يجحدون».'),
(4234, 41, 16, '«فأرسلنا عليهم ريحاً صرصراً» باردة شديدة الصوت بلا مطر «في أيام نحسات» بكسر الحاء وسكونها مشؤومات عليهم «لنذيقهم عذاب الخزي» الذل «في الحياة الدنيا ولعذاب الآخرة أخزى» أشد «وهم لا ينصرون» بمنعه عنهم.'),
(4235, 41, 17, '«وأما ثمود فهديناهم» بيّنا لهم طريق الهدى «فاستحبوا العمى» اختاروا الكفر «على الهدى فأخذتهم صاعقة العذاب الهون» المهين «بما كانوا يكسبون».'),
(4236, 41, 18, '«ونجينا» منها «الذين آمنوا وكانوا يتقون» الله.'),
(4237, 41, 19, '«و» اذكر «يوم يُحشر» بالياء والنون المفتوحة وضم الشين وفتح الهمزة «أعداء الله إلى النار فهم يوزعون» يساقون.'),
(4238, 41, 20, '«حتى إذا ما» زائدة «جاءُوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون».'),
(4239, 41, 21, '«وقالوا لجلودهم لم شهدتم علينا قالوا أنطقنا الله الذي أنطق كل شيء» أي أراد نطقه «وهو خلقكم أول مرةٍ وإليه ترجعون» قيل: هو من كلام الجلود، وقيل: هو من كلام الله تعالى كالذي بعده وموقعه قريب مما قبله بأن القادر على إنشائكم ابتداء وإعادتكم بعد الموت أحياء قادر على إنطاق جلودكم وأعضائكم.'),
(4240, 41, 22, '«وما كنتم تستترون» عن ارتكابكم الفواحش من «أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم» لأنكم لم توقنوا بالبعث «ولكن ظننتم» عند استتاركم «أن الله لا يعلم كثيراً مما تعملون».'),
(4241, 41, 23, '«وذلكم» مبتدأ «ظنكم» بدل منه «الذي ظننتم بربكم» نعت والخبر «أرداكم» أي أهلككم «فأصبحتم من الخاسرين».'),
(4242, 41, 24, '«فإن يصبروا» على العذاب «فالنار مثوى» مأوى «لهم وإن يستعتبوا» يطلبوا العتبى، أي الرضا «فما هم من المعتبين» المرضيين.'),
(4243, 41, 25, '(وقيضنا) سببنا (لهم قرناء) من الشياطين (فزينوا لهم ما بين أيديهم) من أمر الدنيا واتباع الشهوات (وما خلفهم) من أمر الآخرة بقولهم لا بعث ولا حساب (وحق عليهم القول) بالعذاب وهو "" لأملأن جهنم "" الآية (في) جملة (أمم قد خلت) هلكت (من قبلهم من الجن والإنس إنهم كانوا خاسرين).'),
(4244, 41, 26, '«وقال الذين كفروا» عند قراءة النبي صلى الله عليه وسلم «لا تسمعوا لهذا القرآن والغوْا فيه» ائتوا باللغط ونحوه وصيحوا في زمن قراءته «لعلكم تغلبون» فيسكت عن القراءة.'),
(4245, 41, 27, 'قال الله تعالى فيهم: «فلنذيقنَّ الذين كفروا عذاباً شديداً ولنجزينهم أسوأ الذي كانوا يعملون» أي أقبح جزاء عملهم.'),
(4246, 41, 28, '«ذلك» العذاب الشديد وأسوأ الجزاء «جزاء أعداء الله» بتحقيق الهمزة الثانية وإبدالها واواً «النار» عطف بيان للجزاء المخبر به عن ذلك «لهم فيها دار الخلد» أي إقامة لا انتقال منها «جزاءً» منصوب على المصدر بفعله المقدر «بما كانوا بآياتنا» القرآن «يجحدون».'),
(4247, 41, 29, '«وقال الذين كفروا» في النار «ربنا أرنا الذيْن أضلانا من الجن والإنس» أي إبليس وقابيل سنَّا الكفر والقتل «نجعلهما تحت أقدامنا» في النار «ليكونا من الأسفلين» أي أشد عذاباً منا.'),
(4248, 41, 30, '«إن الذين قالوا ربنا الله ثم استقاموا» على التوحيد وغيره بما وجب عليهم «تتنزل عليهم الملائكة» عند الموت «أن» بأن «لا تخافوا» من الموت وما بعده «ولا تحزنوا» على ما خلفتم من أهل وولد فنحن نخلفكم فيه «وأبشروا بالجنة التي كنتم توعدون».'),
(4249, 41, 31, '«نحن أولياؤكم في الحياة الدنيا» أي نحفظكم فيها «وفي الآخرة» أي نكون معكم فيها حتى تدخلوا الجنة «ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون» تطلبون.'),
(4250, 41, 32, '«نزلاً» رزقاً مهيئاً منصوب بجعل مقدراً «من غفور رحيم» أي الله.'),
(4251, 41, 33, '«ومن أحسن قولاً» أي لا أحد أحسن قولاً «ممن دعا إلى الله» بالتوحيد «وعمل صالحاً وقال إنني من المسلمين».'),
(4252, 41, 34, '«ولا تستوي الحسنة ولا السيئة» في جزئياتهما بعضهما فوق بعض «ادفع» السيئة «بالتي» أي بالخصلة التي «هي أحسن» كالغضب بالصبر والجهل بالحلم والإساءة بالعفو «فإذا الذي بينك وبينه عداوة كأنه ولي حميم» أي فيصير عدوك كالصديق القريب في محبته إذا فعلت ذلك فالذي مبتدأ وكأنه الخبر وإذا ظرف لمعنى التشبيه.'),
(4253, 41, 35, '«وما يلقاها» أي يؤتى الخصلة التي هي أحسن «إلا الذين صبروا وما يُلقاها إلا ذو حظ» ثواب «عظيم».'),
(4254, 41, 36, '«وإما» فيه إدغام نون إن الشرطية في ما الزائدة «ينزغنك من الشيطان نزغ» أي يصرفك عن الخصلة وغيرها من الخير صارف «فاستعذ بالله» جواب الشرط وجواب الأمر محذوف، أي يدفعه عنك «إنه هو السميع» للقول «العليم» بالفعل.'),
(4255, 41, 37, '«ومن آياته الليل والنهار والشمس والقمر لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن» أي الآيات الأربع «إن كنتم إياه تعبدون».'),
(4256, 41, 38, '«فإن استكبروا» عن السجود لله وحده «فالذين عند ربك» أي فالملائكة «يسبحون» يصلون «له بالليل والنهار وهم لا يسأمون» لا يملون.'),
(4257, 41, 39, '«ومن آياته أنك ترى الأرض خاشعةً» يابسة لا نبات فيها «فإذا أنزلنا عليها الماء اهتزت» تحركت «وربت» انتفخت وعلت «إن الذي أحياها لمحيي الموتى إنه على كل شيء قدير».'),
(4258, 41, 40, '«إن الذين يلحدون» من ألحد ولحد «في آياتنا» القرآن بالتكذيب «لا يخفون علينا» فنجازيهم «أفمن يُلقى في النار خير أم من يأتي آمناً يوم القيامة اعملوا ما شئتم إنه بما تعملون بصير» تهديد لهم.'),
(4259, 41, 41, '«إن الذين كفروا بالذكر» القرآن «لما جاءهم» نجازيهم «وإنه لكتاب عزيز» منيع.'),
(4260, 41, 42, '«لا يأتيه الباطل من بين يديه ولا من خلفه» أي ليس قبله كتاب يكذبه ولا بعده «تنزيل من حكيم حميد» أي الله المحمود في أمره.'),
(4261, 41, 43, '«ما يقال لك» من التكذيب «إلا» مثل «ما قد قيل للرسل من قبلك إن ربك لذو مغفرة» للمؤمنين «وذو عقاب أليم» للكافرين.'),
(4262, 41, 44, '«ولو جعلناه» أي الذكر «قراناً أعجميا لقالوا لولا» هلا «فصلت» بينت «آياته» حتى نفهمها «أ» قرآن «اعجمي و» نبي «عربي» استفهام إنكار منهم بتحقيق الهمزة الثانية وقلبها ألفا بإشباع، ودونه «قل هو للذين آمنوا هدى» من الضلالة «وشفاء» من الجهل «والذين لا يؤمنون في آذانهم وقر» ثقل فلا يسمعونه «وهو عليهم عمى» فلا يفهمونه «أولئك ينادون من مكان بعيد» أي هم كالمنادى من مكان بعيد لا يسمع ولا يفهم ما ينادى به.'),
(4263, 41, 45, '«ولقد آتينا موسى الكتاب» التوراة «فاختلف فيه» بالتصديق والتكذيب كالقرآن «ولولا كلمة سبقت من ربك» بتأخير الحساب والجزاء للخلائق إلى يوم القيامة «لقضي بينهم» في الدنيا فيما اختلفوا فيه «وإنهم» أي المكذبين به «لفي شك منه مريب» موقع في الريبة.'),
(4264, 41, 46, '«من عمل صالحاً فلنفسه» عمل «ومن أساء فعليها» أي فضرر إساءته على نفسه «وما ربك بظلام للعبيد» أي بذي ظلم لقوله تعالى (إن الله لا يظلم مثقال ذرة).'),
(4265, 41, 47, '«إليه يردُّ علم الساعة» متى تكون لا يعلمها غيره «وما تخرج من ثمرة» وفي قراءة ثمرات «من أكمامها» أوعيتها جمع كِم بكسر الكاف إلا بعلمه «وما تحمل من أنثى ولا تضع إلا بعلمه ويوم يناديهم أين شركائي قالوا آذناك» أعلمناك الآن «ما منا من شهيد» أي شاهد بأن لك شريكاً.'),
(4266, 41, 48, '«وضلَّ» غاب «عنهم ما كانوا يدعون» يعبدون «من قبل» في الدنيا من الأصنام «وظنوا» أيقنوا «ما لهم من محيص» مهرب من العذاب والنفي في الموضعين معلق عن العمل وجملة النفي سدت مسد المفعولين.'),
(4267, 41, 49, '«لا يسأم الإنسان من دعاء الخير» أي لا يزال يسأل ربه المال والصحة وغيرهما «وإن مسه الشر» الفقر والشدة «فيؤس قنوط» من رحمة الله، وهذا وما بعده في الكافرين.'),
(4268, 41, 50, '«ولئن» لام قسم «أذقناه» آتيناه «رحمة» غنى وصحة «منا من بعد ضراء» شدة وبلاء «مسته ليقولن هذا لي» أي بعلمي «وما أظن الساعة قائمة ولئن» لام قسم «رجعت إلى ربي إن لي عنده للحسنى» أي الجنة «فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ» شديد، واللام في الفعلين لام قسم.'),
(4269, 41, 51, '«وإذا أنعمنا على الإنسان» الجنس «أعرض» عن الشكر «وناء بجانبه» ثنى عطفه متبختراً، وفي قراءة بتقديم الهمزة «وإذا مسه الشر فذو دعاءٍ عريض» كثير.'),
(4270, 41, 52, '«قل أرأيتم إن كان» أي القرآن «من عند الله» كما قال النبي «ثم كفرتم به من» أي لا أحد «أضل ممن هو في شقاق» خلاف «بعيد» عن الحق أوقع هذا موقع منكم بياناً لحالهم.'),
(4271, 41, 53, '«سنريهم آياتنا في الآفاق» أقطار السماوات والأرض من النيرات والنبات والأشجار «وفي أنفسهم» من لطيف الصنعة وبديع الحكمة «حتى يتبين لهم أنه» أي القرآن «الحق» المنزل من الله بالبعث والحساب والعقاب، فيعاقبون على كفرهم به وبالجائي به «أوَلم يكف بربك» فاعل يكف «أنه على كل شيءٍ شهيد» بدل منه، أي أوَلم يكفهم في صدقك أن ربك لا يغيب عنه شيء ما.'),
(4272, 41, 54, '«ألا إنهم في مرية» شك «من لقاء ربهم» لإنكارهم البعث «ألا إنه» تعالى «بكل شيء محيط» علماً وقدرة فيجازيهم بكفرهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, '«حم».'),
(4274, 42, 2, '«عسق» الله أعلم بمراده به.'),
(4275, 42, 3, '«كذلك» أي مثل ذلك الإيحاء «يوحي إليك و» أوحى «إلى الذين من قبلك الله» فاعل الإيحاء «العزيز» في ملك «الحكيم» في صنعه.'),
(4276, 42, 4, '«له ما في السماوات وما في الأرض» ملكاً وخلقاً وعبيداً «وهو العلي» على خلقه «العظيم» الكبير.'),
(4277, 42, 5, '«تكاد» بالتاء والياء «السماوات يتفطرن» بالنون، وفي قراءة بالتاء والشديد «من فوقهن» أي تنشق كل واحدة فوق التي تليها من عظمة الله تعالى «والملائكة يسبحون بحمد ربهم» أي ملابسين للحمد «ويستغفرون لمن في الأرض» من المؤمنين «ألا إن الله هو الغفور» لأوليائه «الرحيم» بهم.'),
(4278, 42, 6, '«والذين اتخذوا من دونه» أي الأصنام «أولياء الله حفيظ» محص «عليهم» ليجازيهم «وما أنت عليهم بوكيل» تحصل المطلوب منهم، ما عليك إلا البلاغ.'),
(4279, 42, 7, '«وكذلك» مثل ذلك الإيحاء «أوحينا إليك قرآناً عربيا لتنذر» تخوّف «أم القرى ومن حولها» أي أهل مكة وسائر الناس «وتنذر» الناس «يوم الجمع» يوم القيامة فيه الخلائق «لا ريب» شك «فيه فريق» منهم «في الجنة وفريقٌ في السعير» النار.'),
(4280, 42, 8, '«ولو شاء الله لجعلهم أمة واحدة» أي على دين واحد، وهو الإسلام «ولكن يدخل من يشاء في رحمته والظالمون» الكافرون «ما لهم من وليّ ولا نصير» يدفع عنهم العذاب.'),
(4281, 42, 9, '«أم اتخذوا من دونه» أي الأصنام «أولياء» أم منقطعة بمعنى: بل التي للانتقال، والهمزة للإنكار أي ليس المتخذون أولياء «فالله هو الولي» أي الناصر للمؤمنين والفاء لمجرد العطف «وهو يحيي الموتى وهو على كل شيءٍ قدير».'),
(4282, 42, 10, '«وما اختلفتم» مع الكفار «فيه من شيءٍ» من الدين وغيره «فحكمه» مردود «إلى الله» يوم القيامة يفصل بينكم، قل لهم «ذلكم الله ربي عليه توكلت وإليه أنيب» أرجع.'),
(4283, 42, 11, '«فاطر السماوات والأرض» مبدعهما «جعل لكم من أنفسكم أزواجاً» حيث خلق حواء من ضلع آدم «ومن الأنعام أزواجاً» ذكوراً وإناثاً «يذرؤكم» بالمعجمة بخلقكم «فيه» في الجعل المذكور، أي يكثركم بسببه بالتوالد والضمير للأناسي والأنعام بالتغليب «ليس كمثله شيء» الكاف زائدة لأنه تعالى لا مثل له «وهو السميع» لما يقال «البصير» لما يفعل.'),
(4284, 42, 12, '«له مقاليد السماوات والأرض» أي مفاتيح خزائنهما من المطر والنبات وغيرهما «يبسط الرزق» يوسعه «لمن يشاء» امتحاناً «ويقدر» يضيقه لمن يشاء ابتلاءً «إنه بكل شيء عليم».'),
(4285, 42, 13, '«شرع لكم من الدين ما وصى به نوحاً» هو أول أنبياء الشريعة «والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى أن أقيموا الدين ولا تتفرقوا فيه» هذا هو المشروع الموصى به، والموحى إلى محمد صلى الله عليه وسلم وهو التوحيد «كبر» عظم «على المشركين ما تدعوهم إليه» من التوحيد «الله يجتبي إليه» إلى التوحيد «من يشاء ويهدي إليه من ينيب» يقبل إلى طاعته.'),
(4286, 42, 14, '«وما تفرَّقوا» أي أهل الأديان في الدين بأن وحد بعض وكفر بعض «إلا من بعد ما جاءهم العلم» بالتوحيد «بغياً» من الكافرين «بينهم ولولا كلمة سبقت من ربك» بتأخير الجزاء «إلى أجل مسمى» يوم القيامة «لقضي بينهم» بتعذيب الكافرين في الدنيا «وإن الذين أورثوا الكتاب من بعدهم» وهم اليهود والنصارى «لفي شك منه» من محمد صلى الله عليه وسلم «مريب» موقع في الريبة.'),
(4287, 42, 15, '«فلذلك» التوحيد «فادع» يا محمد الناس «واستقم» عليه «كما أمرت ولا تتبع أهواءهم» في تركه «وقل آمنت بما أنزل الله من كتاب وأمرت لأعدل» أي بأن أعدل «بينكم» في الحكم «الله ربنا وربكم لنا أعمالنا ولكم أعمالكم» فكل يجازى بعمله «لا حجة» خصومة «بيننا وبينكم» هذا قبل أن يؤمر بالجهاد «الله يجمع بيننا» في المعاد لفصل القضاء «وإليه المصير» المرجع.'),
(4288, 42, 16, '«والذين يحاجُّون في» دين «الله» نبيه «من بعد ما استجيب له» بالإيمان لظهور معجزته وهم اليهود «حجتهم داحضة» باطلة «عند ربهم وعليهم غضب ولهم عذاب شديد».'),
(4289, 42, 17, '«الله الذي أنزل الكتاب» القرآن «بالحق» متعلق بأنزل «والميزان» العدل «وما يدريك» يعلمك «لعل الساعة» أي إتيانها «قريب» ولعل معلق للفعل عن العمل وما بعده سد مسد المفعولين.'),
(4290, 42, 18, '«يستعجل بها الذين لا يؤمنون بها» يقولون متى تأتي ظنا منهم أنها غير آتية «والذين آمنوا مشفقون» خائفون «منها ويعلمون أنها الحق ألا إن الذين يمارون» يجادلون «في الساعة لفي ضلال بعيد».'),
(4291, 42, 19, '«الله لطيف بعباده» برهم وفاجرهم حيث لم يهلكهم جوعاً بمعاصيهم «يرزق من يشاء» من كل منهم ما يشاء «وهو القوي» على مراده «العزيز» الغالب على أمره.'),
(4292, 42, 20, '«من كان يريد» بعمله «حرث الآخرة» أي كسبها وهو الثواب «نزد له في حرثه» بالتضعيف فيه الحسنة إلى العشرة وأكثر «ومن كان يريد حرث الدنيا نؤته منها» بلا تضعيف ما قسم له «وما له في الآخرة من نصيب».'),
(4293, 42, 21, '«أم» بل «لهم» لكفار مكة «شركاء» هم شياطينهم «شرعوا» أي الشركاء «لهم» للكفار «من الدين» الفاسد «ما لم يأذن به الله» كالشرك وإنكار البعث «ولولا كلمة الفصل» أي القضاء السابق بأن الجزاء في يوم القيامة «لقضي بينهم» وبين المؤمنين بالتعذيب لهم في الدنيا «وإن الظالمين» الكافرين «لهم عذاب أليم» مؤلم.'),
(4294, 42, 22, '«ترى الظالمين» يوم القيامة «مشفقين» خائفين «مما كسبوا» في الدنيا من السيئات أن يجازوا عليها «وهو» أي الجزاء عليها «واقع بهم» يوم القيامة لا محالة «والذين آمنوا وعملوا الصالحات في روضات الجنات» أنزهها بالنسبة إلى من دونهم «لهم ما يشاءُون عند ربهم ذلك هو الفضل الكبير».'),
(4295, 42, 23, '«ذلك الذي يَبْشُرُ» من البشارة مخففاً ومثقلاً به «الله عباده الذين آمنوا وعملوا الصالحات قل لا أسألكم عليه» على تبليغ الرسالة «أجراً إلا المودة في القربى» استثناء منقطع، أي لكن أسألكم أن تودوا قرابتي التي هي قرابتكم أيضاً فإن له في كل بطن من قريش قرابة «ومن يقترف» يكتسب «حسنة» طاعة «تزد له فيها حسناً» بتضعيفها «إن الله غفور» للذنوب «شكور» للقليل فيضاعفه.'),
(4296, 42, 24, '«أم» بل «يقولون افترى على الله كذباً» بنسبة القرآن إلى الله تعالى «فإن يشأ الله يختم» يربط «على قلبك» بالصبر على أذاهم بهذا القول وغيره، وقد فعل «ويَمْحُ الله الباطل» الذي قالوه «ويحق الحق» يثبته «بكلماته» المنزلة على بنيه «إنه عليم بذات الصدور» بما في القلوب.'),
(4297, 42, 25, '«وهو الذي يقبل التوبة عن عباده» منهم «ويعفو عن السيئات» المتاب عنها «ويعلم ما يفعلون» بالياء والتاء.'),
(4298, 42, 26, '«ويستجيب الذين آمنوا وعملوا الصالحات» يجيبهم إلى ما يسألون «ويزيدهم من فضله والكافرون لهم عذاب شديد».'),
(4299, 42, 27, '«ولو بسط الله الرزق لعباده» جميعهم «لبغوا» جميعهم أي طغوا «في الأرض ولكن ينزل» بالتخفيف وضده من الأرزاق «بقدر ما يشاء» فيبسطها لبعض عباده دون، وينشأ عن البسط البغي «إنه بعباده خبير بصير».'),
(4300, 42, 28, '«وهو الذي ينزل الغيث» المطر «من بعدما قنطوا» يئسوا من نزوله «وينشر رحمته» يبسط مطره «وهو الولي» المحسن للمؤمنين «الحميد» المحمود عندهم.'),
(4301, 42, 29, '«ومن آياته خلق السماوات والأرض» خلق «ما بث» فرق ونشر «فيهما من دابة» هي ما يدب على الأرض من الناس وغيرهم «وهو على جمعهم» للحشر «إذا يشاء قدير» في الضمير تغليب العاقل على غيره.'),
(4302, 42, 30, '«وما أصابكم» خطاب للمؤمنين «من مصيبة» بلية وشدة «فبما كسبت أيديكم» أي كسبتم من الذنوب وعبر بالأيدي لأن أكثر الأفعال تزاول بها «ويعفو عن كثير» منها فلا يجازي عليه وهو تعالى أكرم من أن يثني الجزاء في الآخرة، وأما غير المذنبين فما يصيبهم في الدنيا لرفع درجاتهم في الآخرة.'),
(4303, 42, 31, '«وما أنتم» يا مشركون «بمعجزين» الله هرباً «في الأرض» فتفوتوه «وما لكم من دون الله» أي غيره «من وليّ ولا نصير» يدفع عذابه عنكم.'),
(4304, 42, 32, '«ومن آياته الجوار» السفن «في البحر كالأعلام» كالجبال في العظم.'),
(4305, 42, 33, '«إن يشأ يسكن الريح فيظللن» يصرن «رواكد» ثوابت لا تجري «على ظهره إن في ذلك لآيات لكل صبَّار شكور» هو المؤمن يصبر في الشدة ويشكر في الرخاء.'),
(4306, 42, 34, 'أو يوبقهنَّ» عطف على يسكن أي يغرقهن بعصف الريح بأهلهن «بما كسبوا» أي أهلهن من الذنوب «ويعف عن كثير» منها فلا يغرق أهله.'),
(4307, 42, 35, '«ويعلمُ» بالرفع مستأنف وبالنصب معطوف على تعليل مقدر، أي يغرقهم لينتقم منهم، ويعلم «الذين يجادلون في آياتنا ما لهم من محيص» مهرب من العذاب، وجملة النفي سدت مسد مفعولي يعلم، والنفي معلق عن العمل.'),
(4308, 42, 36, '«فما أوتيتم» خطاب للمؤمنين وغيرهم «من شيءٍ» من أثاث الدنيا «فمتاع الحياة الدنيا» يتمتع به فيها ثم يزول «وما عند الله» من الثواب «خير وأبقى للذين آمنوا وعلى ربهم يتوكلون» ويعطف عليه.'),
(4309, 42, 37, '«والذين يجتنبون كبائر الإثم والفواحش» موجبات الحدود من عطف البعض على الكل «وإذا ما غضبوا هم يغفرون» يتجاوزون.'),
(4310, 42, 38, '«والذين استجابوا لربهم» أجابوه إلى ما دعاهم إليه عن التوحيد والعبادة «وأقاموا الصلاة» أداموها «وأمرهم» الذي يبدو لهم «شورى بينهم» يتشاورون فيه ولا يعجلون «ومما رزقناهم» أعطيناهم «ينفقون» في طاعة الله، ومن ذُكر صنف:'),
(4311, 42, 39, '«والذين إذا أصابهم البغي» الظلم «هم ينتصرون» صنف، أي ينتقمون ممن ظلمهم بمثل ظلمه، كما قال تعالى:'),
(4312, 42, 40, '«وجزاءُ سيئة سيئة مثلها» سميت الثانية سيئة لمشابهتها للأولى في الصورة، وهذا ظاهر فيما يقتص فيه من الجراحات، قال بعضهم: وإذا قال له أخزاك الله، فيجيبه أخزاك الله «فمن عفا» عن ظالمه «وأصلح» الود بينه وبين المعفو عنه «فأجره على الله» أي إن الله يأجره لا محالة «إنه لا يحب الظالمين» أي البادئين بالظلم فيترتب عليهم عقابه.'),
(4313, 42, 41, '«ولمن انتصر بعد ظلمه» أي ظلم الظالم إياه «فأولئك ما عليهم من سبيل» مؤاخذة.'),
(4314, 42, 42, '«إنما السبيل على الذين يظلمون الناس ويبغون» يعملون «في الأرض بغير الحق» بالمعاصي «أولئك لهم عذاب أليم» مؤلم.'),
(4315, 42, 43, '«ولمن صبر» فلم ينتصر «وغفر» تجاوز «إن ذلك» الصبر والتجاوز «لمن عزم الأمور» أي معزوماتها، بمعنى المطلوبات شرعاً.'),
(4316, 42, 44, '«ومن يضلل الله فما له من ولي من بعده» أي أحد يلي هدايته بعد إضلال الله إياه «وترى الظالمين لما رأوا العذاب يقولون هل إلى مَرَدّ» إلى الدنيا «من سبيل» طريق.'),
(4317, 42, 45, '«وتراهم يعرضون عليها» أي النار «خاشعين» خائفين متواضعين «من الذل ينظرون» إليها «من طرف خفيّ» ضعيف النظر مسارقه، ومن ابتدائية، أو بمعنى الباء «وقال الذين آمنوا إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة» بتخليدهم في النار وعدم وصولهم إلى الحور المعدة لهم في الجنة لو آمنوا، والموصول خبر إن «ألا إن الظالمين» الكافرين «في عذاب مقيم» دائم هو من مفعول الله تعالى.'),
(4318, 42, 46, '«وما كان لهم من أولياء ينصرونهم من دون الله» أي غيره يدفع عذابه عنهم «ومن يضلل الله فما له من سبيل» طريق إلى الحق في الدنيا وإلى الجنة في الآخرة.'),
(4319, 42, 47, '«استجيبوا لربكم» أجيبوه بالتوحيد والعبادة «من قبل أن يأتي يوم» هو يوم القيامة «لا مرد له من الله» أي أنه إذا أتى به لا يرده «ما لكم من ملجأ» تلجؤون إليه «يومئذ وما لكم من نكير» إنكار لذنوبكم.'),
(4320, 42, 48, '«فإن أعرضوا» عن الإجابة «فما أرسلناك عليهم حفيظاً» تحفظ أعمالهم بأن توافق المطلوب منهم «إن» ما «عليك إلا البلاغ» وهذا قبل الأمر بالجهاد «وإنا إذا أذقنا الإنسان منا رحمة» نعمة كالغنى والصحة «فرح وإن تصبهم» الضمير فلإنسان باعتبار الجنس «سيئة» بلاء «بما قدمت أيديهم» أي قدموه وعبر بالأيدي لأن أكثر الأفعال تزاول بها «فإن الإنسان كفور» للنعمة.'),
(4321, 42, 49, '«لله ملك السماوات والأرض يخلق ما يشاء يهب لمن يشاء» من الأولاد «إناثاً ويهب لمن يشاء الذكور».'),
(4322, 42, 50, '«أو يزوجهم» أي يجعلهم «ذكراناً وإناثاً ويجعل من يشاء عقيماً» فلا يلد ولا يولد له «إنه عليم» بما يخلق «قدير» على ما يشاء.'),
(4323, 42, 51, '«وما كان لبشر أن يكلمه الله إلا» أن يوحي إليه «وحياً» في المنام أو بإلهام «أو» إلا «من وراء حجاب» بأن يسمعه كلامه ولا يراه كما وقع لموسى عليه السلام «أو» إلا أن «يرسل رسولاً» ملكاً كجبريل «فيوحي» الرسول إلى المرسل إليه أي يكلمه «بإذنه» أي الله «ما يشاء» الله «إنه عليّ» عن صفات المحدثين «حكيم» في صنعه.'),
(4324, 42, 52, '«وكذلك» أي مثل إيحائنا إلى غيرك من الرسل «أوحينا إليك» يا محمد «روحاً» هو القرآن به تحيا القلوب «من أمرنا» الذي نوحيه إليك «ما كنت تدري» تعرف قبل الوحي إليك «ما الكتاب» القرآن «ولا الإيمان» أي شرائعه ومعالمه والنفي معلق للفعل عن العمل وما بعده سد مسد المفعولين «ولكن جعلناه» أي الروح أو الكتاب «نوراً نهدي به من نشاء من عبادنا وإنك لتهدي» تدعو بالوحي إليك «إلى صراط» طريق «مستقيم» دين الإسلام.'),
(4325, 42, 53, '«صراط الله الذي له ما في السماوات وما في الأرض» ملكاً وخلقاً وعبيداً «ألا إلى الله تصير الأمور» ترجع.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, '«حم» الله أعلم بمراده به.'),
(4327, 43, 2, '«والكتاب» القرآن «المبين» المظهر طريق الهدى وما يحتاج إليه من الشريعة.'),
(4328, 43, 3, '«إنا جعلناه» أوجدنا الكتاب «قرآنا عربيا» بلغة العرب «لعلكم» يا أهل مكة «تعقلون» تفهمون معانيه.'),
(4329, 43, 4, '«وإنه» مثبت «في أم الكتاب» أصل الكتب أي اللوح المحفوظ «لدينا» بدل: عندنا «لعلي» على الكتب قبله «حكيم» ذو حكمة بالغة.'),
(4330, 43, 5, '«أفنضرب» نمسك «عنكم الذكر» القرآن «صفحاً» إمساكاً فلا تؤمرون ولا تنهون لأجل «أن كنتم قوماً مسرفين» مشركين لا.'),
(4331, 43, 6, '«وكم أرسلنا من نبي في الأولين».'),
(4332, 43, 7, '«وما» كان «يأتيهم» أتاهم «من نبي إلا كانوا به يستهزئون» كاستهزاء قومك بك وهذا تسلية له صلى الله عليه وسلم.'),
(4333, 43, 8, '«فأهلكنا أشد منهم» من قومك «بطشاً» قوة «ومضى» سبق في آيات «مثل الأولين» صفتهم في الإهلاك فعاقبة قومك كذلك.'),
(4334, 43, 9, '«ولئن» لام قسم «سألتهم من خلق السماوات والأرض ليقولنَّ» حذف منه نون الرفع لتوالي النونات وواو الضمير لالتقاء الساكنين «خلقهن العزيز العليم» آخر جوابهم أي الله ذو العزة والعلم، زاد تعالى:'),
(4335, 43, 10, '«الذي جعل لكم الأرض مهادا» فراشاً كالمهد للصبي «وجعل لكم فيها سبلا» طرقاً «لعلكم تهتدون» إلى مقاصدكم في أسفاركم.'),
(4336, 43, 11, '«والذي نزل من السماء ماءً بقدر» أي بقدر حاجتكم إليه ولم ينزله طوفاناً «فأنشرنا» أحيينا «به بلدة ميتا كذلك» أي مثل هذا الإحياء «تخرجون» من قبوركم أحياء.'),
(4337, 43, 12, '«والذي خلق الأزواج كلها وجعل لكم من الفلك» السفن «والأنعام» كالإبل «ما تركبون» حذف العائد اختصاراً، وهو مجرور في الأول، أي فيه منصوب في الثاني.'),
(4338, 43, 13, '«لتستووا» لتستقروا «على ظهوره» ذكر الضمير وجمع الظهر نظراً للفظ ما ومعناها «ثم تذكروا نعمة ربكم إذا استويتم عليه وتقولوا سبحان الذي سخر لنا هذا وما كنا له مقرنين» مطيقين.'),
(4339, 43, 14, '«وإنا إلى ربنا لمنقلبون» لمنصرفون.'),
(4340, 43, 15, '«وجعلوا له من عباده جزءاً» حيث قالوا والملائكة بنات الله لأن الولد جزء من الوالد والملائكة من عباده تعالى «إن الإنسان» القائل ما تقدم «لكفور مبين» بين ظاهر الكفر.'),
(4341, 43, 16, '«أم» بمعنى همزة الإنكار والقول مقدر، أي أتقولون «اتخذ مما يخلق بنات» لنفسه «وأصفاكم» أخلصكم «بالبنين» اللازم من قولكم السابق فهو من جملة المنكر.'),
(4342, 43, 17, '«وإذا بشر أحدهم بما ضرب للرحمن مثلا» جعل له شبهاً بنسبة البنات إليه لأن الولد يشبه الوالد، المعنى إذا أخبر أحدهم بالبنت تولد له «ظل» صار «وجهه مسودا» متغيراً تغير مغتم «وهو كظيم» ممتلئ غما فكيف ينسب البنات إليه؟ تعالى عن ذلك.'),
(4343, 43, 18, '«أو» همزة الإنكار وواو العطف بجملة، أي يجعلون لله «من يُنشأ في الحلية» الزينة «وهو في الخصام غير مبين» مظهر الحجة لضعفه عنها بالأنوثة.'),
(4344, 43, 19, '«وجعلوا الملائكة الذين هم عباد الرحمن إناثا أشهدوا» حضروا «خلقهم ستكتب شهادتهم» بأنهم إناث «ويسألون» عنها في الآخرة فيترتب عليهم العقاب.'),
(4345, 43, 20, '«وقالوا لو شاء الرحمن ما عبدناهم» أي الملائكة فعبادتنا إياهم بمشيئته فهو راض بها قال تعالى: «ما لهم بذلك» المقول من الرضا بعبادتها «من علم إن» ما «هم إلا يخرصون» يكذبون فيه فيترتب عليهم العقاب به.'),
(4346, 43, 21, '«أم آتيناهم كتاباً من قبله» أي القرآن بعبادة غير الله «فهم مستمسكون» أي لم يقع ذلك.'),
(4347, 43, 22, '«بل قالوا إنا وجدنا آباءنا على أمة» ملة «وإنا» «على آثارهم مهتدون» بهم وكانوا يعبدون غير الله.'),
(4348, 43, 23, '«وكذلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها» منعموها مثل قول قومك «إنا وجدنا آباءنا على أمة» ملة «وإنا على آثارهم مقتدون» متبعون.'),
(4349, 43, 24, '«قل» لهم «أ» تتبعون ذلك «ولو جئتكم بأهدى مما وجدتم عليه آباءكم قالوا إنا بما أرسلتم به» أنت ومن قبلك «كافرون» قال تعالى تخويفاً لهم:'),
(4350, 43, 25, '«فانتقمنا منهم» أي من المكذبين للرسل قبلك «فانظر كيف كان عاقبة المكذبين».'),
(4351, 43, 26, '«و» اذكر «قال إبراهيم لأبيه وقومه إنني بَرَاء» أي بريء «مما تعبدون».'),
(4352, 43, 27, '«إلا الذي فطرني» خلقني «فإنه سيهدين» يرشدني لدينه.'),
(4353, 43, 28, '(وجعلها) أي كلمة التوحيد المفهومة من قوله "" إني ذاهب إلى ربي سيهدين "" (كلمة باقية في عقبه) ذريته فلا يزال فيهم من يوحد الله (لعلهم) أي أهل مكة (يرجعون) عما هم عليه إلى دين إبراهيم أبيهم.'),
(4354, 43, 29, '«بل متعت هؤلاء» المشركين «وآباءهم» ولم أعاجلهم بالعقوبة «حتى جاءهم الحق» القرآن «ورسول مبين» مظهر لهم الأحكام الشرعية، وهو محمد صلى الله عليه وسلم.'),
(4355, 43, 30, '«ولما جاءهم الحق» القرآن «قالوا هذا سحر وإنا به كافرون».'),
(4356, 43, 31, '«وقالوا لولا» هلا «نزل هذا القرآن على رجل من» أهل «القريتين» من أية منهما «عظيم» أي الوليد بن المغيرة بمكة أو عروة بن مسعود الثقفي بالطائف.'),
(4357, 43, 32, '«أهم يقسمون رحمة ربك» النبوة «نحن قسمنا بينهم معيشتهم في الحياة الدنيا» فجعلنا بعضهم غنيا وبعضهم فقيراً «ورفعنا بعضهم» بالغنى «فوق بعض درجات ليتخذ بعضهم» الغني «بعضاً» الفقير «سخريا» مسخراً في العمل له بالأجرة، والياء للنسب، وقرئ بكسر السين «ورحمة ربك» أي الجنة «خير مما يجمعون» في الدنيا.'),
(4358, 43, 33, '«ولولا أن يكون الناس أمة واحدة» على الكفر «لجعلنا لمن يكفر بالرحمن لبيوتهم» بدل من لمن «سَقْفاً» بفتح السين وسكون القاف وبضمهما جمعاً «من فضة ومعارج» كالدرج من فضة «عليها يظهرون» يعلون إلى السطح.'),
(4359, 43, 34, '«ولبيوتهم أبواباً» من فضة «و» جعلنا لهم «سرراً» من فضة جمع سرير «عليها يتكئون».'),
(4360, 43, 35, '«وزخرفاً» ذهباً، المعنى لولا خوف الكفر على المؤمن من إعطاء الكافر ما ذكر لأعطيناه ذلك لقلة خطر الدنيا عندنا وعدم حظه في الآخرة في النعيم «وإن» مخففة من الثقيلة «كل ذلك لما» بالتخفيف فما زائدة، وبالتشديد بمعنى إلا فإن نافية «متاع الحياة الدنيا» يتمتع به فيها ثم يزول «والآخرة» الجنة «عند ربك للمتقين».'),
(4361, 43, 36, '«ومن يعش» يعرض «عن ذكر الرحمن» أي القرآن «نقيض» نسبب «له شيطاناً فهو له قرين» لا يفارقه.'),
(4362, 43, 37, '«وإنهم» أي الشياطين «ليصدونهم» أي العاشين «عن السبيل» أي طرق الهدى «ويحسبون أنهم مهتدون» في الجمع رعاية معنى من.'),
(4363, 43, 38, '«حتى إذا جاءنا» العاشي بقرينه يوم القيامة «قال» له «يا» للتنبيه «ليت بيني وبينك بعد المشرقين» أي مثل بعد ما بين المشرق والمغرب «فبئس القرين» أنت لي، قال تعالى:'),
(4364, 43, 39, '«ولن ينفعكم» أي العاشين تمنيكم وندمك «اليوم إذ ظلمتم» أي تبين لكم ظلمكم بالإشراك في الدنيا «أنكم» مع قرنائكم «في العذاب مشتركون» علة بتقدير اللام لعدم النفع وإذ بدل من اليوم.'),
(4365, 43, 40, '«أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين» بيّن، أي فهم لا يؤمنون.'),
(4366, 43, 41, '«فإما» في إدغام نون إن الشرطية في ما الزائدة «نذهبن بك» بأن نميتك قبل تعذيبهم «فإنا منهم منتقمون» في الآخرة.'),
(4367, 43, 42, '«أو نرينك» في حياتك «الذي وعدناهم» به من العذاب «فإنا عليهم» على عذابهم «مقتدرون» قادرون.'),
(4368, 43, 43, '«فاستمسك بالذي أوحي إليك» أي القرآن «إنك على صراط» طريق «مستقيم».'),
(4369, 43, 44, '«وإنه لذكر» لشرف «لك ولقومك» لنزوله بلغتهم «وسوف تُسألون» عن القيام بحقه.'),
(4370, 43, 45, '«واسأل من أرسلنا من قبلك من رسلنا أجعلنا من دون الرحمن» أي غيره «آلهة يعبدون» قيل هو على ظاهره بأن جمع له الرسل ليلة الإسراء، وقيل المراد أمم من أي أهل الكتابين، ولم يسأل على واحد من القولين لأن المراد من الأمر بالسؤال التقرير لمشركي قريش أنه لم يأت رسول من الله ولا كتاب بعبادة غير الله.'),
(4371, 43, 46, '«ولقد أرسلنا موسى بآياتنا إلى فرعون وملئه» أي القبط «فقال إني رسول رب العالمين».'),
(4372, 43, 47, '«فلما جاءهم بآياتنا» الدالة على رسالته «إذا هم منها يضحكون».'),
(4373, 43, 48, '«وما نريهم من آية» من آيات العذاب كالطوفان، وهو ماء دخل بيوتهم ووصل إلى حلوق الجالسين سبعة أيام، والجراد «إلا هي أكبر من أختها» قرينتها التي قبلها «وأخذناهم بالعذاب لعلهم يرجعون» عن الكفر.'),
(4374, 43, 49, '«وقالوا» لموسى لما رأوا العذاب «يا أيها الساحر» أي العالم الكامل لأن السحر عندهم علم عظيم «ادع لنا ربك بما عهد عندك» عن كشف العذاب عنا إن آمنا «إننا لمهتدون» أي مؤمنون.'),
(4375, 43, 50, '«فلما كشفنا» بدعاء موسى «عنهم العذاب إذا هم ينكثون» ينقضون عهدهم ويصرون على كفرهم.'),
(4376, 43, 51, '«ونادى فرعون» افتخاراً «في قومه قال يا قوم أليس لي ملك مصر وهذه الأنهار» من النيل «تجري من تحتي» أي تحت قصوري «أفلا تبصرون» عظمتي.'),
(4377, 43, 52, '«أم» تبصرون، وحينئذ «أنا خير من هذا» أي موسى «الذي هو مهين» ضعيف حقير «و يكاد يُبين» يظهر كلامه للثغته بالجمرة التي تناولها في صغره.'),
(4378, 43, 53, '«فلولا» هلا «ألقي عليه» إن كان صادقاً «أسورة من ذهب» جمع أسورة كأغربة جمع سوار كعادتهم فيمن يسودونه أن يلبسوه أسورة ذهب ويطوقونه طوق ذهب «أو جاء معه الملائكة مقترنين» متتابعين يشهدون بصدقه.'),
(4379, 43, 54, '«فاستخف» استفز فرعون «قومه فأطاعوه» فيما يريد من تكذيب موسى «إنهم كانوا قوماً فاسقين».'),
(4380, 43, 55, '«فلما آسفونا» أغضبونا «انتقمنا منهم فأغرقناهم أجمعين».'),
(4381, 43, 56, '«فجعلناهم سلفاً» جمع سالف كخادم وخدم أي سابقين غيره «ومثلا للآخرين» بعدهم يتمثلون بحالهم فلا يقدمون على مثل أفعالهم.'),
(4382, 43, 57, '(ولما ضرب) جعل (ابن مريم مثلا) حين نزل قوله تعالى "" إنكم وما تعبدون من دون الله حصب جهنم "" فقال المشركون: رضينا أن تكون آلهتنا مع عيسى لأنه عبد من دون الله (إذا قومك) أي المشركون (منه) من المثل (يصدون) يضحكون فرحاً بما سمعوا.'),
(4383, 43, 58, '«وقالوا أألهتنا خير أم هو» أي عيسى فنرضى أن تكون آلهتنا معه «ما ضربوه» أي المثل «لك إلا جدلا» خصومة بالباطل لعلمهم أن ما لغير العاقل فلا يتناول عيسى عليه السلام «بل هم قوم خصمون» شديدو الخصومة.'),
(4384, 43, 59, '«إن» ما «هو» عيسى «إلا عبد أنعمنا عليه» بالنبوة «وجعلناه» بوجوده من غير أب «مثلا لبني إسرائيل» أي كالمثل لغرابته يستدل به على قدرة الله تعالى على ما يشاء.'),
(4385, 43, 60, '«ولو نشاء لجعلنا منكم» بدلكم «ملائكة في الأرض يخلفون» بأن نهلككم.'),
(4386, 43, 61, '«وإنه» أي عيسى «لعلم للساعة» تعلم بنزوله «فلا تمترن بها» أي تشكن فيها، حذف منه نون الرفع للجزم، وواو الضمير لالتقاء الساكنين «و» قل لهم «اتبعون» على التوحيد «هذا» الذي آمركم به «صراط» طريق «مستقيم».'),
(4387, 43, 62, '«ولا يصدنكم» يصرفنكم عن دين الله «الشيطان إنه لكم عدو مبين» بيِّن العداوة.'),
(4388, 43, 63, '«ولما جاء عيسى بالبينات» بالمعجزات والشرائع «قال قد جئتكم بالحكمة» بالنبوة وشرائع الإنجيل «ولأبيِّن لكم بعض الذي تختلفون فيه» من أحكام التوراة من أمر الدين وغيره فبيَّن لهم أمر الدين «فاتقوا الله وأطيعون».'),
(4389, 43, 64, '«إن الله هو ربي وربكم فاعبدوه هذا صراط» طريق «مستقيم».'),
(4390, 43, 65, '«فاختلف الأحزاب من بينهم» في عيسى أهو الله أو ابن الله أو ثالث ثلاثة «فويلٌ» كلمة عذاب «للذين ظلموا» كفروا بما قالوه في عيسى «من عذاب يوم أليم» مؤلم.'),
(4391, 43, 66, '«هل ينظرون» أي كفار مكة، أي ما ينتظرون «إلا الساعة أن تأتيهم» بدل من الساعة «بغتةً» فجأة «وهم لا يشعرون» بوقت مجيئها قبله.'),
(4392, 43, 67, '«الأخلاءُ» على المعصية في الدنيا «يومئذ» يوم القيامة متعلق بقوله «بعضهم لبعض عدو إلا المتقين» المتحابين في الله على طاعته فإنهم أصدقاء ويقال لهم:'),
(4393, 43, 68, '«يا عبادِ لا خوف عليكم اليوم ولا أنتم تحزنون».'),
(4394, 43, 69, '«الذين آمنوا» نعت لعبادي «بآياتنا» القرآن «وكانوا مسلمين».'),
(4395, 43, 70, '«ادخلوا الجنة أنتم» مبتدأ «وأزواجكم» زوجاتكم «تحبرون» تسرون وتكرمون، خبر المبتدأ.'),
(4396, 43, 71, '«يطاف عليهم بصحاف» بقصاع «من ذهب وأكواب» جمع كوب وهو إناء لا عروة له ليشرب الشارب من حيث شاء «وفيها ما تشتهيه الأنفس» تلذذاً «وتلذ الأعين» نظراً «وأنتم فيها خالدون».'),
(4397, 43, 72, '«وتلك الجنة التي أورثتموها بما كنتم تعملون».'),
(4398, 43, 73, '«لكم فيها فاكهة كثيرة منها» أي بعضها «تأكلون» وكل ما يؤكل يخلف بدله.'),
(4399, 43, 74, '«إن المجرمين في عذاب جهنم خالدون».'),
(4400, 43, 75, '«لا يُفَتَّرُ» يخفف «عنهم وهم فيه مبلسون» ساكتون سكوت يأس.'),
(4401, 43, 76, '«وما ظلمناهم ولكن كانوا هم الظالمين».'),
(4402, 43, 77, '«ونادوا يا مالك» هو خازن النار «ليقض علينا ربك» ليمتنا «قال» بعد ألف سنة «إنكم ماكثون» مقيمون في العذاب دائماً.'),
(4403, 43, 78, 'قال تعالى: «لقد جئناكم» أي أهل مكة «بالحق» على لسان الرسول «ولكن أكثركم للحق كارهون».'),
(4404, 43, 79, '«أم أبرموا» أي كفار مكة: أحكموا «أمراً» في كيد محمد النبي «فإنا مبرمون» محكمون كيدنا في إهلاكهم.'),
(4405, 43, 80, '«أم يحسبون أنا لا نسمع سرهم ونجواهم» ما يسرون إلى غيرهم وما يجهرون به بينهم «بلى» نسمع ذلك «ورسلنا» الحفظة «لديهم» عندهم «يكتبون» ذلك.'),
(4406, 43, 81, 'قل إن كان للرحمن ولد» فرضاً «فأنا أول العابدين» للولد لكن ثبت أن لا ولد له تعالى فانتفت عبادته.'),
(4407, 43, 82, '«سبحان رب السماوات والأرض رب العرش» الكرسي «عما يصفون» يقولون من الكذب بنسبة الولد إليه.'),
(4408, 43, 83, '«فذرهم يخوضوا» في باطلهم «ويلعبوا» في دنياهم «حتى يلاقوا يومهم الذي يوعدون» فيه العذاب وهو يوم القيامة.'),
(4409, 43, 84, '«وهو الذي» هو «في السماء إله» بتحقيق الهمزتين وإسقاط الأولى وتسهيلها كالياء، أي معبود «وفي الأرض إله» وكل من الظرفين متعلق بما بعده «وهو الحكيم» في تدبير خلقه «العليم» بمصالحهم.'),
(4410, 43, 85, '«وتبارك» تعظم «الذي له ملك السموات والأرض وما بينهما وعنده علم الساعة» متى تقوم «وإليه يرجعون» بالياء والتاء.'),
(4411, 43, 86, '«ولا يملك الذين يدعون» يعبدون، أي الكفار «من دونه» أي من دون الله «الشفاعة» لأحد «إلا من شهد بالحق» أي قال: لا إله إلا الله «وهم يعلمون» بقلوبهم ما شهدوا به بألسنتهم، وهم عيسى وعزير والملائكة فإنهم يشفعون للمؤمنين.'),
(4412, 43, 87, '«ولئن» لام قسم «سألتهم من خلقهم ليقولنَّ الله» حذف منه نون الرفع وواو الضمير «فأنَّى يؤفكون» يصرفون عن عبادة الله.'),
(4413, 43, 88, '«وقيله» أي قول محمد النبي، ونصبه على المصدر بفعله المقدر، أي وقال «يا رب إن هؤلاء قوم لا يؤمنون».'),
(4414, 43, 89, 'قال تعالى: «فاصفح» أعرض «عنهم وقل سلام» منكم وهذا قبل أن يؤمر بقتالهم «فسوف يعلمون» بالياء والتاء تهديد لهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, '«حم» الله أعلم بمراده به.'),
(4416, 44, 2, '«والكتاب» القرآن «المبين» المظهر الحلال من الحرام.'),
(4417, 44, 3, '«إنا أنزلناه في ليلة مباركة» هي ليلة القدر أو ليلة النصف من شعبان، نزل فيها من أم الكتاب من السماء السابعة إلى سماء الدنيا «إنا كنا منذرين» مخوِّفين به.'),
(4418, 44, 4, '«فيها» أي في ليلة القدر أو ليلة النصف من شعبان «يفرق» يفصل «كل أمر حكيم» محكم من الأرزاق والآجال وغيرهما التي تكون في السنة إلى مثل تلك الليلة.'),
(4419, 44, 5, '«أمراً» فرقاً «من عندنا إنا كنا مرسلين» الرسل محمداً ومن قبله.'),
(4420, 44, 6, '«رحمة» رأفة بالمرسل إليهم «من ربك إنه هو السميع» لأقوالهم «العليم» بأفعالهم.'),
(4421, 44, 7, '«رب السماوات والأرض وما بينهما» برفع رب خبر ثالث وبجره بدل من ربك «إن كنتم» يا أهل مكة «موقنين» بأنه تعالى رب السماوات والأرض فأيقنوا بأن محمداً رسوله.'),
(4422, 44, 8, '«لا إله إلا هو يحيي ويميت ربكم ورب آبائكم الأولين».'),
(4423, 44, 9, '(بل هم في شك) من البعث (يلعبون) استهزاء بك يا محمد، فقال: "" اللهم أعني عليهم بسبع كسبع يوسف "".'),
(4424, 44, 10, 'قال تعالى: «فارتقب يوم تأتي السماء بدخان مبين» فأجدبت الأرض واشتد بهم الجوع إلى أن رأوا من شدته كهيئة الدخان بين السماء والأرض.'),
(4425, 44, 11, '«يغشى الناس» فقالوا «هذا عذاب أليم».'),
(4426, 44, 12, '«ربنا اكشف عنا العذاب إنا مؤمنون» مصدقون نبيك.'),
(4427, 44, 13, 'قال تعالى: «أنَّى لهم الذكرى» أي لا ينفعهم الإيمان عند نزول العذاب «وقد جاءهم رسول مبين» بين الرسالة.'),
(4428, 44, 14, '«ثم تولوْا عنه وقالوا معلم» أي يعلمه القرآن بشر «مجنون».'),
(4429, 44, 15, '«إنا كاشفو العذاب» أي الجوع عنكم زمناً «قليلا» فكشف عنهم «إنكم عائدون» إلى كفركم فعادوا إليه.'),
(4430, 44, 16, 'اذكر «يوم نبطش البطشة الكبرى» هو يوم بدر «إنا منتقمون» منهم والبطش الأخذ بقوة.'),
(4431, 44, 17, '«ولقد فتنا» بلونا «قبلهم قوم فرعون» معه «وجاءهم رسول» هو موسى عليه السلام «كريم» على الله تعالى.'),
(4432, 44, 18, '«أن» أي بأن «أدُّوا إليَّ» ما أدعوكم إليه من الإيمان، أي أظهروا إيمانكم لي يا «عباد الله إني لكم رسول أمين» على ما أُرسلت به.'),
(4433, 44, 19, '«وأن لا تعلوا» تتجبروا «على الله» بترك طاعته «إني آتيكم بسلطان» برهان «مبين» بين على رسالتي فتوعَّدوه بالرجم.'),
(4434, 44, 20, 'فقال «وإني عذت بربي وربكم أن ترجمون» بالحجارة.'),
(4435, 44, 21, '«وإن لم تؤمنوا لي» تصدقوني «فاعتزلون» فاتركوا أذاي فلم يتركوه.'),
(4436, 44, 22, '«فدعا ربه أن» أي بأن «هؤلاء قوم مجرمون» مشركون.'),
(4437, 44, 23, 'فقال تعالى: «فأسر» بقطع الهمزة ووصلها «بعبادي» بنى إسرائيل «ليلاً إنكم متبَعون» يتبعكم فرعون وقومه.'),
(4438, 44, 24, '«واترك البحر» إذا قطعته أنت وأصحابك «رهواً» ساكناً منفرجاً حتى يدخله القبط «إنهم جند مغرقون» فاطمأن بذلك فأغرقوا.'),
(4439, 44, 25, '«كم تركوا من جنات» بساتين «وعيون» تجري.'),
(4440, 44, 26, '«وزروع ومقام كريم» مجلس حسن.'),
(4441, 44, 27, '«ونعمة» متعة «كانوا فيها فاكهين» ناعمين.'),
(4442, 44, 28, '«كذلك» خبر مبتدأ، أي الأمر «وأورثناها» أي أموالهم «قوماً آخرين» أي بني إسرائيل.'),
(4443, 44, 29, '«فما بكت عليهم السماء والأرض» بخلاف المؤمنين يبكي عليهم بموتهم مصلاهم من الأرض ومصعد عملهم من السماء «وما كانوا منظرين» مؤخرين للتوبة.'),
(4444, 44, 30, '«ولقد نجينا بني إسرائيل من العذاب المهين» قتل الأبناء واستخدام النساء.'),
(4445, 44, 31, '«من فرعون» قيل بدل من العذاب بتقدير مضاف، أي عذاب، وقيل حال من العذاب «إنه كان عالياً من المسرفين».'),
(4446, 44, 32, '«ولقد اخترناهم» أي بني إسرائيل «على علم» منا بحالهم «على العالمين» أي عالمي زمانهم أي العقلاء.'),
(4447, 44, 33, '«وآتيانهم من الآيات ما فيه بلاء مبين» نعمة ظاهرة من فلق البحر والمن والسلوى وغيرها.'),
(4448, 44, 34, '«إن هؤلاء» أي كفار مكة «ليقولون».'),
(4449, 44, 35, '«إن هي» ما الموتة التي بعدها الحياة «إلا موتتنا الأولى» أي وهم نطف «وما نحن بمنشرين» بمبعوثين أحياء بعد الثانية.'),
(4450, 44, 36, '«فأتوا بآبائنا» أحياء «إن كنتم صادقين» أنا نبعث بعد موتنا، أي نحيا.'),
(4451, 44, 37, 'قال تعالى: «أهم خير أم قوم تُبَّع» هو نبي أو رجل صالح «والذين من قبلهم» من الأمم «أهلكناهم» بكفرهم، والمعنى ليسوا أقوى منهم وأهلكوا «إنهم كانوا مجرمين».'),
(4452, 44, 38, '«وما خلقنا السماوات والأرض وما بينهما لاعبين» بخلق ذلك، حال.'),
(4453, 44, 39, '«ما خلقناهما» وما بينهما «إلا بالحق» أي محقين في ذلك ليستدل به قدرتنا ووحدانيتنا وغير ذلك «ولكن أكثرهم» أي كفرا مكة «لا يعلمون».'),
(4454, 44, 40, '«إن يوم الفصل» يوم القيامة يفصل الله فيه بين العباد «ميقاتهم أجمعين» للعذاب الدائم.'),
(4455, 44, 41, '«يوم لا يغني مولى عن مولى» بقرابة أو صداقة، أي لا يدفع عنه «شيئاً» من العذاب «ولا هم ينصرون» يمنعون منه، ويوم بدل من يوم الفصل.'),
(4456, 44, 42, '«إلا من رحم الله» وهم المؤمنون فإنه يشفع بعضهم لبعض بإذن الله «إنه هو العزيز» الغالب في انتقامه من الكفار «الرحيم» بالمؤمنين.'),
(4457, 44, 43, '«إن شجرة الزقوم» هي من أخبث الشجر المر بتهامة ينبتها الله تعالى في الجحيم.'),
(4458, 44, 44, '«طعام الأثيم» أبي جهل وأصحابه ذوي الإثم الكبير.'),
(4459, 44, 45, '«كالمهل» أي كدردي الزيت الأسود خبر ثان «تغلي في البطون» بالفوقانية خبر ثالث وبالتحتانية حال من المهل.'),
(4460, 44, 46, '«كغلي الحميم» الماء الشديد الحرارة.'),
(4461, 44, 47, '«خذوه» يقال للزبانية: خذوا الأثيم «فاعتلوه» بكسر التاء وضمها جروه بغلظة وشدة «إلى سواء الجحيم» وسط النار.'),
(4462, 44, 48, '(ثم صبوا فوق رأسه من عذاب الحميم) أي من الحميم الذي لا يفارقه العذاب فهو أبلغ مما في آية "" يصب من فوق رؤوسهم الحميم "".'),
(4463, 44, 49, 'ويقال له: «ذق» أي العذاب «إنك أنت العزيز الكريم» بزعمك وقولك ما بين جبليها أعز وأكرم مني.'),
(4464, 44, 50, 'وقال لهم: «إن هذا» الذي ترون من العذاب «ما كنتم به تمترون» فيه تشكون.'),
(4465, 44, 51, '«إن المتقين في مقام» مجلس «أمين» يؤمن فيه الخوف.'),
(4466, 44, 52, '«في جنات» بساتين «وعيون».'),
(4467, 44, 53, '«يلبسون من سندس وإستبرق» أي ما رقَّ من الديباج وما غلظ منه «متقابلين» حال، أي لا ينظر بعضهم إلى قفا بعض لدوران الأسرة بهم.'),
(4468, 44, 54, '«كذلك» يقدر قبله الأمر «وزوجناهم» من التزويج أو قرناهم «بحور عين» بنساء بيض واسعات الأعين حسانها.'),
(4469, 44, 55, '«يدعون» يطلبون الخدم «فيها» أي الجنة أن يأتوا «بكل فاكهة» منها «آمنين» من انقطاعها ومضرتها ومن كل مخوف حال.'),
(4470, 44, 56, '«لا يذوقون فيها الموت إلا الموتة الأولى» أي التي في الدنيا بعد حياتهم فيها، قال بعضهم إلا بمعنى بعد «ووقاهم عذاب الجحيم».'),
(4471, 44, 57, '«فضلاً» مصدر بمعنى تفضلاً منصوب بتفضل مقدراً «من ربك ذلك هو الفوز العظيم».'),
(4472, 44, 58, '«فإنما يسرناه» سهلنا القرآن «بلسانك» بلغتك لتفهمه العرب منك «لعلهم يتذكرون» يتعظون فيؤمنون لكنهم لا يؤمنون.'),
(4473, 44, 59, '«فارتقب» انتظر هلاكهم «إنهم مرتقبون» هلاكك، وهذا قبل نزول الأمر بجهادهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, '«حم» الله أعلم بمراده به.'),
(4475, 45, 2, '«تنزيل الكتاب» القرآن مبتدأ «من الله» خبره «العزيز» في مُلكه «الحكيم» في صنعه.'),
(4476, 45, 3, '«إن في السماوات والأرض» أي في خلقهما «لآيات» دالة على قدرة الله ووحدانيته تعالى «للمؤمنين».'),
(4477, 45, 4, '«وفي خلقكم» أي في خلق كل منكم من نطفة ثم علقة ثم مضغة إلى أن صار إنساناً «و» خلق «ما يبث» يفرق في الأرض «من دابة» هي ما يدب على الأرض من الناس وغيرهم «آيات لقوم يوقنون» بالبعث.'),
(4478, 45, 5, '«و» في «اختلاف الليل والنهار» ذهابهما ومجيئهما «وما أنزل الله من السماء من رزق» مطر لأنه سبب الرزق «فأحيا به الأرض بعد موتها وتصريف الريح» تقليبها مرة جنوباً ومرة شمالاً وباردة وحارة «آيات لقوم يعقلون» الدليل فيؤمنون.'),
(4479, 45, 6, '«تلك» الآيات المذكورة «آيات الله» حججه الدالة على وحدانيته «نتلوها» نقصها «عليك بالحق» متعلق بنتلو «فبأي حديث بعد الله» أي حديثه وهو القرآن «وآياته» حججه «يؤمنون» أي كفار مكة، أي لا يؤمنون، وفي قراءة بالتاء.'),
(4480, 45, 7, '«ويل» كلمة عذاب «لكل أفاك» كذاب «أثيم» كثير الإثم.'),
(4481, 45, 8, '«يسمع آيات الله» القرآن «تتلى عليه ثم يصر» على كفره «مستكبراً» متكبراً عن الإيمان «كأن لم يسمعها فبشره بعذاب أليم» مؤلم.'),
(4482, 45, 9, '«وإذا علم من آياتنا» أي القرآن «شيئاً اتخذها هزواً» أي مهزوءاً بها «أولئك» أي الأفاكون «لهم عذاب مهين» ذو إهانة.'),
(4483, 45, 10, '«من ورائهم» أي أمامهم لأنهم في الدنيا «جهنم ولا يغني عنهم ما كسبوا» من المال والفعال «شيئاً ولا ما اتخذوا من دون الله» أي الأصنام «أولياء ولهم عذاب عظيم».'),
(4484, 45, 11, '«هذا» أي القرآن «هدى» من الضلالة «والذين كفروا بآيات ربهم لهم عذاب» حظ «من رجز» أي عذاب «أليم» موجع.'),
(4485, 45, 12, '«الله الذي سخر لكم البحر لتجري الفلك» السفن «فيه بأمره» بإذنه «ولتبتغوا» تطلبوا بالتجارة «من فضله ولعلكم تشكرون».'),
(4486, 45, 13, '«وسخر لكم ما في السماوات» من شمس وقمر ونجوم وماء وغيره «وما في الأرض» من دابة وشجر ونبات وأنهار وغيرها أي خلق ذلك لمنافعكم «جميعاً» تأكيد «منه» حال، أي سخرها كائنة منه تعالى «إن في ذلك لآيات لقوم يتفكرون» فيها فيؤمنون.'),
(4487, 45, 14, '«قل للذين آمنوا يغفروا للذين لا يرجون» يخافون «أيام الله» وقائعه، أي اغفر للكفار ما وقع منهم من الأذى لكم وهذا قبل الأمر بجهادهم «ليجزي» أي الله وفي قراءة بالنون «قوماً بما كانوا يكسبون» من الغفر للكفار أذاهم.'),
(4488, 45, 15, '«من عمل صالحاً فلنفسه» عمل «ومن أساء فعليها» أساء «ثم إلى ربكم ترجعون» تصيرون فيجازي المصلح والمسيء.'),
(4489, 45, 16, '«ولقد آتينا بني إسرائيل الكتاب» التوراة «والحكم» به بين الناس «والنبوة» لموسى وهارون منهم «ورزقناهم من الطيبات» الحلالات كالمنّ والسلوى «وفضلناهم على العالمين» عالمي زمانهم العقلاء.'),
(4490, 45, 17, '«وآتيانهم بينات من الأمر» أمر الدين من الحلال والحرام وبعثة محمد عليه أفضل الصلاة والسلام «فما اختلفوا» في بعثته «إلا من بعد ما جاءهم العلم بغياً بينهم» أي لبغي حدث بينهم حسداً له «إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون».'),
(4491, 45, 18, '«ثم جعلناك» يا محمد «على شريعة» طريقة «من الأمر» أمر الدين «فاتبعها ولا تتبع أهواء الذين لا يعلمون» في عبادة غير الله.'),
(4492, 45, 19, '«إنهم لن يغنوا» يدافعوا «عنك من الله» من عذابه «شيئاً وإن الظالمين» الكافرين «بعضهم أولياء بعض والله وليُّ المتقين».'),
(4493, 45, 20, '«هذا» القرآن «بصائر للناس» معالم يتبصرون بها في الأحكام والحدود «وهدى ورحمة لقوم يوقنون» بالبعث.'),
(4494, 45, 21, '«أم» بمعنى همزة الإنكار «حسب الذين اجترحوا» اكتسبوا «السيئات» الكفر والمعاصي «أن نجعلهم كالذين آمنوا وعملوا الصالحات سواءً» خبر «محياهم ومماتهم» مبتدأ ومعطوف والجملة بدل من الكاف والضميران للكفار، المعنى: أحسبوا أن نجعلهم في الآخرة في خير كالمؤمنين في رغد من العيش مساو لعيشهم في الدنيا حيث قالوا للمؤمنين: لئن بعثنا لنُعطى من الخير مثل ما تعطون قال تعالى على وفق إنكاره بالهمزة: «ساءَ ما يحكمون» أي ليس الأمر كذلك فهم في الآخرة في العذاب على خلاف عيشهم في الدنيا والمؤمنون في الآخرة في الثواب بعملهم الصالحات في الدنيا من الصلاة والزكاة والصيام وغير ذلك، وما مصدرية، أي بئس حكماً حكمهم هذا.'),
(4495, 45, 22, '«وخلق الله السماوات» خلق «والأرض بالحق» متعلق بخلق ليدل على قدرته ووحدانيته «ولتجزى كل نفس بما كسبت» من المعاصي والطاعات فلا يساوي الكافر المؤمن «وهم لا يظلمون».'),
(4496, 45, 23, '«أفرأيت» أخبرني «من اتخذ إلهه هواه» ما يهواه من حجر بعد حجر يراه أحسن «وأضله الله على علم» منه تعالى: أي عالما بأنه من أهل الضلاله قبل خلق «وختم على سمعه وقلبه» فلم يسمع الهدى ولم يعقله «وجعل على بصره غشاوة» ظلمة فلم يبصر الهدى، ويقدر هنا المفعول الثاني لرأيت أيهتدي «فمن يهديه من بعد الله» أي بعد إضلاله إياه، أي لا يهتدي «أفلا تذكرون» تتعظون، فيه إدغام إحدى التاءين في الذال.'),
(4497, 45, 24, '«وقالوا» أي منكرو البعث «ما هي» أي الحياة «إلا حياتنا» التي في «الدنيا نموت ونحيا» أي يموت بعض ويحيا بعض بأن يولدوا «وما يهلكنا إلا الدهر» أي مرور الزمان، قال تعالى: «وما لهم بذلك» المقول «من علم إن» ما «هم إلا يظنون».'),
(4498, 45, 25, '«وإذا تتلى عليهم آياتنا» من القرآن الدالة على قدرتنا على البعث «بينات» واضحات حال «ما كان حجتهم إلا أن قالوا ائتوا بآياتنا» أحياء «إن كنتم صادقين» أنا نبعث.'),
(4499, 45, 26, '«قل الله يحييكم» حين كنتم نطفاً «ثم يميتكم ثم يجمعكم» أحياء «إلى يوم القيامة لا ريب» شلك «فيه ولكن أكثر الناس» وهم القائلون ما ذكر «لا يعلمون».'),
(4500, 45, 27, '«ولله ملك السماوات والأرض ويوم تقوم الساعة» يبدل منه «يومئذ يخسر المبطلون» الكافرون، أي يظهر خسرانهم بأن يصيروا إلى النار.'),
(4501, 45, 28, '«وترى كل أمة» أي أهل دين «جاثية» على الركب أو مجتمعة «كل أمة تدعى إلى كتابها» كتاب أعمالها ويقال لهم: «اليوم تجزون ما كنتم تعملون» أي جزاءه.'),
(4502, 45, 29, '«هذا كتابنا» ديوان الحفظة «ينطق عليكم بالحق إنا كنا نستنسخ» نثبت ونحفظ «ما كنتم تعملون».'),
(4503, 45, 30, '«فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته» جنته «ذلك هو الفوز المبين» البين الظاهر.'),
(4504, 45, 31, '«وأما الذين كفروا» فيقال لهم: «أفلم تكن آياتي» القرآن «تتلى عليكم فاستكبرتم» تكبرتم «وكنتم قوماً مجرمين» كافرين.'),
(4505, 45, 32, '«وإذا قيل» لكم أيها الكفار «إن وعد الله» بالبعث «حق والساعة» بالرفع والنصب «لا ريب» شك «فيها قلتم ما ندري ما الساعة إن» ما «نظن إلا ظنا» قال المبرد: أصله إن نحن إلا نظن ظنا «وما نحن بمستيقنين» أنها آتية.'),
(4506, 45, 33, '«وبدا» ظهر «لهم» في الآخرة «سيئات ما عملوا» في الدنيا، أي جزاؤها «وحاق» نزل «بهم ما كانوا به يستهزئون» أي العذاب.'),
(4507, 45, 34, '«وقيل اليوم ننساكم» نترككم في النار «كما نسيتم لقاء يومكم هذا» أي تركتم العمل للقائه «ومأواكم النار وما لكم من ناصرين» مانعين منه.'),
(4508, 45, 35, '«ذلكم بأنكم اتخذتم آيات الله» القرآن «هزواً وغرتكم الحياة الدنيا» حتى قلتم لا بعث ولا حساب «فاليوم لا يخرجون» بالبناء للفاعل وللمفعول «منها» من النار «ولا هم يستعتبون» لا يطلب منهم أن يرضوا ربهم بالتوبة والطاعة لأنها لا تنفع يومئذ.'),
(4509, 45, 36, '«فلله الحمد» الوصف بالجميل على وفاء وعده في المكذبين «رب السماوات ورب الأرض رب العالمين» خالق ما ذكر، والعالم ما سوى الله وجمع لاختلاف أنواعه، ورب بدل.'),
(4510, 45, 37, '«وله الكبرياء» العظمة «في السماوات والأرض» حال، أي كائنة فيهما «وهو العزيز الحكيم» تقدم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, '«حم» الله أعلم بمراده به.'),
(4512, 46, 2, '«تنزيل الكتاب» القرآن مبتدأ «من الله» خبره «العزيز» في ملكه «الحكيم» في صنعه.'),
(4513, 46, 3, '«ما خلقنا السماوات والأرض وما بينهما إلا» خلقا «بالحق» ليدل على قدرتنا ووحدانيتنا «وأجل مسمى» إلى فنائهما يوم القيامة «والذين كفروا عما أنذروا» خوفوا به من العذاب «معرضون».'),
(4514, 46, 4, '«قل أرأيتم» أخبروني «ما تدعون» تعبدون «من دون الله» أي الأصنام مفعول أول «أروني» أخبروني ما تأكيد «ماذا خلقوا» مفعول ثان «من الأرض» بيان ما «أم لهم شرك» مشاركة «في» خلق «السماوات» مع الله وأم بمعنى همزة الإنكار «ائتوني بكتاب» منزل «من قبل هذا» القرآن «أو أثارة» بقية «من علم» يؤثر عن الأولين بصحة دعواكم في عبادة الأصنام أنها تقربكم إلى الله «إن كنتم صادقين» في دعواكم.'),
(4515, 46, 5, '«ومن» استفهام بمعنى النفي، أي لا أحد «أضل ممن يدعو» يعبد «من دون الله» أي غيره «من لا يستجيب له إلى يوم القيامة» وهم الأصنام لا يجيبون عابديهم إلى شيء يسألونه أبدا «وهم عن دعائهم» عبادتهم «غافلون» لأنهم جماد لا يعقلون.'),
(4516, 46, 6, '«وإذا حشر الناس كانوا» أي الأصنام «لهم» لعابديهم «أعداءً وكانوا بعبادتهم» بعبادة عابديهم «كافرين» جاحدين.'),
(4517, 46, 7, '«وإذا تتلى عليهم» أي أهل مكة «آياتنا» القرآن «بينات» ظاهرات حال «قال الذين كفروا» منهم «للحق» أي القرآن «لما جاءهم هذا سحر مبين» بيّن ظاهر.'),
(4518, 46, 8, '«أم» بمعني بل وهمزة الإنكار «يقولون افتراه» أي القرآن «قل إن افتريته» فرضا «فلا تملكون لي من الله» أي من عذابه «شيئا» أي لا تقدرون على دفعه عنى إذا عذبني الله «هو أعلم بما تفيضون فيه» يقولون في القرآن «كفى به» تعالى «شهيدا بيني وبينكم وهو الغفور» لمن تاب «الرحيم» به فلم يعاجلكم بالعقوبة.'),
(4519, 46, 9, '«قل ما كنت بدعا» بديعا «من الرسل» أي أول مرسل، قد سبق قبلي كثيرون منهم، فكيف تكذبوني «وما أدري ما يفعل بي ولا بكم» في الدنيا أأخرج من بلدي أم أقتل كما فعل بالأنبياء قبلي، أو ترموني بالحجارة أم يخسف بكم كالمكذبين قبلكم «إن» ما «أتبع إلا ما يوحى إليَّ» أي القرآن ولا أبتدع من عندي شيئا «وما أنا إلا نذير مبين» بيّن الإنذار.'),
(4520, 46, 10, '«قل أرأيتم» أخبروني ماذا حالكم «إن كان» أي القرآن «من عند الله وكفرتم به» جملة حالية «وشهد شاهد من بني إسرائيل» هو عبد الله بن سلام «على مثله» أي عليه أنه من عند الله «فآمن» الشاهد «واستكبرتم» تكبرتم عن الإيمان وجواب الشرط بما عطف عليه: ألستم ظالمين دل عليه «إن الله لا يهدي القوم الظالمين».'),
(4521, 46, 11, '«وقال الذين كفروا للذين آمنوا» أي في حقهم «لو كان» الإيمان «خيرا ما سبقونا إليه وإذ لم يهتدوا» أي القائلون «به» أي القرآن «فسيقولون هذا» أي القرآن «إفك» كذب «قديم».'),
(4522, 46, 12, '«ومن قبله» أي القرآن «كتاب موسى» أي التوراة «إماما ورحمة» للمؤمنين به حالان «وهذا» أي القرآن «كتاب مصدق» للكتب قبله «لسانا عربيا» قال لمن الضمير في مصدق «لينذر الذين ظلموا» مشركي مكة «و» هو «بشرى للمحسنين» المؤمنين.'),
(4523, 46, 13, '«إن الذين قالوا ربنا الله ثم استقاموا» على الطاعة «فلا خوف عليهم ولا هم يحزنون».'),
(4524, 46, 14, '«أولئك أصحاب الجنة خالدين فيها» حال «جزاءً» منصوب على المصدر بفعله المقدر، أي يجزون «بما كانوا يعملون».'),
(4525, 46, 15, '«ووصَّينا الإنسان بوالديه حُسنا» وفي قراءة إحسانا، أي أمرناه أن يحسن إليهما فنصب إحسانا على المصدر بفعله المقدر ومثله حسنا «حملته أمه كرها ووضعته كرها» أي على مشقة «وحمله وفصاله» من الرضاع «ثلاثون شهرا» ستة أشهر أقل مدة الحمل والباقي أكثر مدة الرضاع، وقيل إن حملت به ستة أو تسعة أرضعته الباقي «حتى» غاية لجملة مقدرة، أي وعاش حتى «إذا بلغ أشده» هو كمال قوته وعقله ورأيه أقله ثلاث وثلاثون سنة أو ثلاثون «وبلغ أربعين سنة» أي تمامها وهو أكثر الأشد «قال رب» إلخ، نزل في أبي بكر الصديق لما بلغ أربعين سنة بعد سنتين من مبعث النبي صلى الله عليه وسلم آمن به ثم آمن أبواه ثم ابنه عبد الرحمن وابن عبد الرحمن أبو عتيق «أوزعني» ألهمني «أن أشكر نعمتك التي أنعمت» بها «عليَّ وعلى والديَّ» وهي التوحيد «وأن أعمل صالحا ترضاه» فأعتق تسعة من المؤمنين يعذبون في الله «وأصلح لي في ذريتي» فكلهم مؤمنون «إني تبت إليك وإني من المسلمين».'),
(4526, 46, 16, '(أولئك) أي قائلوا هذا القول أبو بكر وغيره (الذين نتقبل عنهم أحسن) بمعنى حسن (ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة) حال، أي كائنين في جملتهم (وعد الصدق الذي كانوا يوعدون) في قوله تعالى "" وعد الله المؤمنين والمؤمنات جنات "".'),
(4527, 46, 17, '«والذي قال لوالديه» وفي قراءة بالإدغام أريد به الجنس «أفَِ» بكسر الفاء وفتحها بمعنى مصدر، أي نتنا وقبحا «لكما» أتضجر منكما «أتعدانني» وفي قراءة بالإدغام «أن أخرج» من القبر «وقد خلت القرون» الأمم «من قبلي» ولم تخرج من القبور «وهما يستغيثان الله» يسألانه الغوث برجوعه ويقولان إن لم ترجع «ويلك» أي هلاكك بمعنى هلكت «آمن» بالبعث «إن وعد الله حق فيقول ما هذا» أي القول بالبعث «إلا أساطير الأولين» أكاذيبهم.'),
(4528, 46, 18, '«أولئك الذين حق» وجب «عليهم القول» بالعذاب «في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين».'),
(4529, 46, 19, '«ولكل» من جنس المؤمن والكافر «درجات» فدرجات المؤمنين في الجنة عالية ودرجات الكافرين في النار سافلة «مما عملوا» أي المؤمنون من الطاعات والكافرون من المعاصي «وليوفيهم» أي الله، وفي قراءة بالنون «أعمالهم» أي جزاءها «وهم لا يظلمون» شيئا ينقص للمؤمنين ويزاد للكفار.'),
(4530, 46, 20, '«ويوم يُعرض الذي كفروا على النار» بأن تكشف لهم يقال لهم «أذهبتم» بهمزة وبهمزتين وبهمزة ومدة وبهما وتسهيل الثانية «طيباتكم» باشتغالكم بلذاتكم «في حياتكم الدنيا واستمتعتم» تمتعتم «بها فاليوم تجزون عذاب الهُون» أي الهوان «بما كنتم تستكبرون» تتكبرون «في الأرض بغير الحق وبما كنتم تفسقون» به وتعذبون بها.'),
(4531, 46, 21, '«واذكر أخا عاد» هود عليه السلام «إذ» إلخ بدل اشتمال «أنذر قومه» خوَّفهم «بالأحقاف» واد باليمن به منازلهم «وقد خلت النذر» مضت الرسل «من بين يديه ومن خلفه» أي من قبل هود ومن بعده إلى أقوامهم «أن»، أي بأن قال «لا تعبدوا إلا الله» وجملة وقد خلت معترضة «إني أخاف عليكم» إن عبدتم غير الله «عذاب يوم عظيم».'),
(4532, 46, 22, '«قالوا أجئتنا لتأفكنا عن آلهتنا» لتصرفنا عن عبادتها «فأتنا بما تعدنا» من العذاب على عبادتها «إن كنت من الصادقين» في أنه يأتينا.'),
(4533, 46, 23, '«قال» هود «إنما العلم عند الله» هو الذي يعلم متى يأتيكم العذاب «وأبلغكم ما أرسلت به» إليكم «ولكني أراكم قوما تجهلون» باستعجالكم العذاب.'),
(4534, 46, 24, '«فلما رأوه» أي ما هو العذاب «عارضا» سحابا عرض في أفق السماء «مستقبل أوديتهم قالوا هذا عارض ممطرنا» أي ممطر إيانا، قال تعالى: «بل هو ما استعجلتم به» من العذاب «ريح» بدل من ما «فيها عذاب أليم» مؤلم.'),
(4535, 46, 25, '«تُدَمِّر» تهلك «كل شيء» مرت عليه «بأمر ربها» بإرادته، أي كل شيء أراد إهلاكه بها، فأهلكت رجالهم ونساءهم وصغارهم وأموالهم بأن طارت بذلك بين السماء والأرض ومزقته وبقي هود ومن آمن معه «فأصبحوا لايُرى إلا مساكنهم كذلك» كما جزيناهم «نجزي القوم المجرمين» غيرهم.'),
(4536, 46, 26, '«ولقد مكناهم فيما» في الذي «إن» نافية أو زائدة «مكناكم» يا أهل مكة «فيه» من القوة والمال «وجعلنا لهم سمعا» بمعنى أسماعا «وأبصارا وأفئدة» قلوبا «فما أغنى عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء» أي شيئا من الإغناء ومن زائدة «إذ» معمولة لأغنى وأشربت معنى التعليل «كانوا يجحدون بآيات الله» بحججه البينة «وحاق» نزل «بهم ما كانوا به يستهزءُون» أي العذاب.'),
(4537, 46, 27, '«ولقد أهلكنا ما حولكم من القرى» أي من أهلها كثمود وعاد وقوم لوط «وصرفنا الآيات» كررنا الحجج البينات «لعلهم يرجعون».'),
(4538, 46, 28, '«فلولا» هلا «نصرهم» بدفع العذاب عنهم «الذين اتخذوا من دون الله» أي غيره «قربانا» متقربا بهم إلى الله «آلهة» معه وهم الأصنام ومفعول اتخذ الأول ضمير محذوف يعود على الموصول أي هم، وقربانا الثاني وآلهة بدل منه «بل ضلوا» غابوا «عنهم» عند نزول العذاب «وذلك» أي اتخاذهم الأصنام آلهة قربانا «إفكهم» كذبهم «وما كانوا يفترون» يكذبون، وما مصدرية أو موصولة والعائد محذوف، أي فيه.'),
(4539, 46, 29, '(و) اذكر (إذ صرفنا) أملنا (إليك نفرا من الجن) جن نصيبين باليمن أو جن نينوى وكانوا سبعة أو تسعة "" وكان صلى الله عليه وسلم ببطن نخل يصلي بأصحابه الفجر "" رواه الشيخان (يستمعون القرآن فلما حضروه قالوا) أي قال بعضهم لبعض (أنصتوا) أصغوا لاستماعه (فلما قضي) فرغ من قراءته (ولوا) رجعوا (إلى قومهم منذرين) مخوفين قومهم العذاب إن لم يؤمنوا وكانوا يهودا وقد أسلموا.'),
(4540, 46, 30, '«قالوا يا قومنا إنا سمعنا كتابا» هو القرآن «أنزل من بعد موسى مصدقا لما بين يديه» أي تقدمه كالتوراة «يهدي إلى الحق» الإسلام «وإلى طريق مستقيم» أي طريقه.'),
(4541, 46, 31, '«يا قومنا أجيبوا داعي الله» محمدا صلى الله عليه وسلم إلى الإيمان «وآمنوا به يغفر» الله «لكم من ذنوبكم» أي بعضها لأن منها المظالم ولا تغفر إلا برضا أصاحبها «ويجركم من عذاب أليم» مؤلم.'),
(4542, 46, 32, '«ومن لا يجب داعي الله فليس بمعجز في الأرض» أي لا يعجز الله بالهرب منه فيفوته «وليس له» لمن لا يجب «من دونه» أي الله «أولئك» الذين لم يجيبوا «في ضلال مبين» بيَّن ظاهر.'),
(4543, 46, 33, '«أولم يروْا» يعلموا، أي منكرو البعث «أن الله الذي خلق السماوات والأرض ولم يَعْيَ بخلقهن» لم يعجز عنه «بقادر» خبر أن وزيدت الباء فيه لأن الكلام في قوة أليس الله بقادر «على أن يحيي الموتى بلى» هو قادر على إحياء الموتى «إنه على كل شيءٍ قدير».'),
(4544, 46, 34, '«ويوم يعرض الذين كفروا على النار» بأن يعذبوا بها يقال لهم «أليس هذا» التعذيب «بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون».'),
(4545, 46, 35, '(فاصبر) على أذى قومك (كما صبر أولو العزم) ذوو الثبات والصبر على الشدائد (من الرسل) قبلك فتكون ذا عزم، ومن للبيان فكلهم ذوو عزم وقيل للتبعيض فليس منهم آدم لقوله تعالى "" ولم نجد له عزما "" ولا يونس لقوله تعالى "" ولا تكن كصاحب الحوت "" (ولا تستعجل لهم) لقومك نزول العذاب بهم، قيل كأنه ضجر منهم فأحب نزول العذاب بهم، فأمر بالصبر وترك الاستعجال للعذاب فإنه نازل لا محالة (كأنهم يوم يرون ما يوعدون) من العذاب في الآخرة لطوله (لم يلبثوا) في الدنيا في ظنهم (إلا ساعة من نهار) هذا القرآن (بلاغ) تبليغ من الله إليكم (فهل) أي لا (يهلك) عند رؤية العذاب (إلا القوم الفاسقون) أي الكافرون.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, '«الذين كفروا» من أهل مكة «وصدُّوا» غيرهم «عن سبيل الله» أي الإيمان «أضل» أحبط «أعمالهم» كإطعام الطعام وصلة الأرحام، فلا يرون لها في الآخرة ثوابا ويجزون بها في الدنيا من فضله تعالى.'),
(4547, 47, 2, '«والذين آمنوا» أي الأنصار وغيرهم «وعملوا الصالحات وآمنوا بما نُزِّل على محمد» أي القرآن «وهو الحق من ربهم كفَّر عنهم» غفر لهم «سيئاتهم وأصلح بالهم» حالهم فلا يعصونه.'),
(4548, 47, 3, '«ذلك» أي إضلال الأعمال وتكفير السيئات «بأن» بسبب أن «الذين كفروا اتبعوا الباطل» الشيطان «وأن الذين آمنوا اتبعوا الحق» القرآن «من ربهم كذلك» أي مثل ذلك البيان «يضرب الله للناس أمثالهم» يبيِّن أحوالهم، أي فالكافر يحبط عمله، والمؤمن يغفر له.'),
(4549, 47, 4, '«فإذا لقيتم الذين كفروا فضرب الرقاب» مصدر بدل من اللفظ بفعله، أي فاضربوا رقابهم، أي اقتلوهم وعبَّر بضرب الرقاب لأن الغالب في القتل أن يكون بضرب الرقبة «حتى إذا أثخنتموهم» أكثرتم فيهم القتل «فشدوا» فأمسكوا عنهم وأسروهم وشدوا «الوثاق» ما يوثق به الأسرى «فإما منا بعد» مصدر بدل من اللفظ بفعله أي تمنون عليهم بإطلاقهم من غير شيء «وإما فداءً» تفادونهم بمال أو أسرى مسلمين «حتى تضع الحرب» أي أهلها «أوزارها» أثقالها من السلاح وغيره بأن يسلم الكفار أو يدخلوا في العهد وهذه غاية للقتل والأسر «ذلك» خبر مبتدأ مقدر، أي الأمر فيهم ما ذكر «ولو يشاء الله لانتصر منهم» بغير قتال «ولكن» أمركم به «ليبلو بعضكم ببعض» منهم في القتال فيصير من قتل منكم إلى الجنة ومنهم إلى النار «والذين قتلوا» وفي قراءة قاتلوا، الآية نزلت يوم أحد وقد فشا في المسلمين القتل والجراحات «في سبيل الله فلن يضل» يحبط «أعمالهم».'),
(4550, 47, 5, '«سيهديهم» في الدنيا والآخرة إلى ما ينفعهم «ويصلح بالهم» حالهم فيهما وما في الدنيا لمن لم يقتل وأدرجوا في قتلوا تغليبا.'),
(4551, 47, 6, '«ويدخلهم الجنة عرَّفها» بيَّنها «لهم» فيهتدون إلى مساكنهم منها وأزواجهم وخدمهم من غير استدلال.'),
(4552, 47, 7, '«يا أيها الذين آمنوا إن تنصروا الله» أي دينه ورسوله «ينصركم» على عدوكم «ويثبِّت أقدامكم» يثبتكم في المعترك.'),
(4553, 47, 8, '«والذين كفروا» من أهل مكة مبتدأ خبره تعسوا يدل عليه «فتعسا لهم» أي هلاكا وخيبة من الله «وأضل أعمالهم» عطف على تعسوا.'),
(4554, 47, 9, '«ذلك» التعس والإضلال «بأنهم كرهوا ما أنزل الله» من القرآن المشتمل على التكاليف «فأحبط أعمالهم».'),
(4555, 47, 10, '«أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم دمَّر الله عليهم» أهلك أنفسهم وأولادهم وأموالهم «وللكافرين أمثالها» أي أمثال عاقبة ما قبلهم.'),
(4556, 47, 11, '«ذلك» نصر المؤمنين وقهر الكافرين «بأن الله مولى» ولي وناصر «الذين آمنوا وأن الكافرين لا مولى لهم».'),
(4557, 47, 12, '«إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار والذين كفروا يتمتعون» في الدنيا «ويأكلون كما تأكل الأنعام» أي ليس لهم هَمٌ إلا بطونهم وفروجهم ولا يلتفتون إلى الآخرة «والنار مثوى لهم» منزل ومقام ومصير.'),
(4558, 47, 13, '«وكأين» وكم «من قرية» أريد بها أهلها «هي أشد قوة من «قريتك» مكة أي أهلها «التي أخرجتك» روعي لفظ قرية «أهلكناهم» روعي معنى قرية الأولى «فلا ناصر لهم» من إهلاكنا.'),
(4559, 47, 14, '«أفمن كان على بيِّنة» حجة وبرهان «من ربه» وهو المؤمنون «كمن زُيّن له سوءُ عمله» فرآه حسنا وهم كفار مكة «واتبعوا أهواءهم» في عبادة الأوثان، أي لا مماثلة بينهما.'),
(4560, 47, 15, '«مثل» أي صفة «الجنة التي وعد المتقون» المشتركة بين داخليها مبتدأ خبره «فيها أنهار من ماءٍ غير آسن» بالمد والقصر كضارب وحذر، أي غير متغير بخلاف ماء الدنيا فيتغير بعارض «وأنهار من لبن لم يتغير طعمه» بخلاف لبن الدنيا لخروجه من الضروع «وأنهار من خمر لذة» لذيذة «للشاربين» بخلاف خمر الدنيا فإنها كريهة عند الشرب «وأنهار من عسل مصفى» بخلاف عسل الدنيا فإنه بخروجه من بطون النحل يخالط الشمع وغيره «ولهم فيها» أصناف «من كل الثمرات ومغفرة من ربهم» فهو راض عنهم مع إحسانه إليهم بما ذكر بخلاف سيد العبيد في الدنيا فإنه قد يكون مع إحسانه إليهم ساخطا عليهم «كمن هو خالد في النار» خبر مبتدأ مقدر، أي أمن هو في هذا النعيم «وسقوا ماء حميما» أي شديد الحرارة «فقطَّع أمعاءهم» أي مصارينهم فخرجت من أدبارهم، وهو جمع معى بالقصر، وألفه عن ياء لقولهم ميعان.'),
(4561, 47, 16, '«ومنهم» أي الكفار «من يستمع إليك» في خطبة الجمعة وهم المنافقون «حتى إذا خرجوا من عندك قالوا للذين أوتوا العلم» لعلماء الصحابة منهم ابن مسعود وابن عباس استهزاء وسخرية «ماذا قال آنفا» بالمد والقصر، الساعة، أي لا نرجع إليه «أولئك الذين طبع الله على قلوبهم بالكفر «واتبعوا أهواءهم» في النفاق.'),
(4562, 47, 17, '«والذين اهتدوا» وهم المؤمنون «زادهم» الله «هدىّ وآتاهم تقواهم» ألهمهم ما يتقون به النار.'),
(4563, 47, 18, '«فهل ينظرون» ما ينتظرون، أي كفار مكة «إلا الساعة أن تأتيهم» بدل اشتمال من الساعة، أي ليس الأمر إلا أن تأتيهم «بغتة» فجأة «فقد جاء أشرطها» علاماتها: منها النبي صلى الله عليه وسلم وانشقاق القمر والدخان «فأنَّى لهم إذا جاءتهم» الساعة «ذكراهم» تذكرهم، أي لا ينفعهم.'),
(4564, 47, 19, '(فاعلم أنه لا إله إلا الله) أي دم يا محمد على علمك بذلك النافع في القيامة (واستغفر لذنبك) لأجله قيل له ذلك مع عصمته لتستن به أمته، وقد فعله قال صلى الله عليه وسلم: "" إني لأستغفر الله في كل يوم مائة مرة "" (وللمؤمنين والمؤمنات) فيه إكرام لهم بأمر نبيهم بالاستغفار لهم (والله يعلم متقلبكم) متصرفكم لأشغالكم في النهار (ومثواكم) مأواكم إلى مضاجعكم بالليل، أي هو عالم بجميع أحوالكم لا يخفى عليه شيء منها فاحذروه، والخطاب للمؤمنين وغيرهم.'),
(4565, 47, 20, '«ويقول الذين آمنوا» طلبا للجهاد «لولا» هلا «نزلت سورة» فيها ذكر الجهاد «فإذا أنزلت سورة محكمة» أي لم ينسخ منها شيء «وذكر فيها القتال» أي طلبه «رأيت الذين في قلوبهم مرض» أي شك وهم المنافقون «ينظرون إليك نظر المغشي عليه من الموت» خوفا منه وكراهة له، أي فهم يخافون من القتال ويكرهونه «فأولى لهم» مبتدأ خبره.'),
(4566, 47, 21, '«طاعة وقول معروف» أي حسن لك «فإذا عزم الأمر» أي فرض القتال «فلو صدقوا الله» في الإيمان والطاعة «لكان خيرا لهم» وجملة لو جواب إذا.'),
(4567, 47, 22, '«فهل عسَِيتم» بكسر السين وفتحها وفيه التفات عن الغيبة إلى الخطاب، أي لعلكم «إن توليتم» أعرضتم عن الإيمان «أن تفسدوا في الأرض وتقطعوا أرحامكم» أي تعودوا إلى أمر الجاهلية من البغي والقتال.'),
(4568, 47, 23, '«أولئك» أي المفسدون «الذين لعنهم الله فأصمهم» عن استماع الحق «وأعمى أبصارهم» عن طريق الهدى.'),
(4569, 47, 24, '«أفلا يتدبرون القرآن» فيعرفون الحق «أم» بل «على قلوب» لهم «أقفالها» فلا يفهمونه.'),
(4570, 47, 25, '«إن الذين ارتدوا» بالنفاق «على أدبارهم من بعد ما تبين لهم الهدى الشيطان سوَّل» أي زيَّن «لهم وأَُملى لهم» بضم أوله وبفتحه واللام والمملي الشيطان بإرادته تعالى فهو المضل لهم.'),
(4571, 47, 26, '«ذلك» أي إضلالهم «بأنهم قالوا للذين كرهوا ما نزَّل الله» أي للمشركين «سنطيعكم في بعض الأمر» أي المعاونة على عداوة النبي صلى الله عليه وسلم وتثبيط الناس عن الجهاد معه، قالوا ذلك سرا فأظهره الله تعالى «والله يعلم أَِسرارهم» بفتح الهمزة جمع سر وبكسرها مصدر.'),
(4572, 47, 27, '«فكيف» حالهم «إذا توفتهم الملائكة يضربون» حال من الملائكة «وجوههم وأدبارهم» ظهورهم بمقامع من حديد.'),
(4573, 47, 28, '«ذلك» التوفي على الحالة المذكورة «بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه» أي العمل بما يرضيه «فأحبط أعمالهم».'),
(4574, 47, 29, '«أم حسب الذين في قلوبهم مرض أن لن يخرج الله أضغانهم» يظهر أحقادهم على النبي صلى الله عليه وسلم والمؤمنين.'),
(4575, 47, 30, '«ولو نشاء لأريناكهم» عرفناكهم وكررت اللام في «فلعرفتهم بسيماهم» علامتهم «ولتعرفنهم» الواو لقسم محذوف وما بعدها جوابه «في لحن القوْل» أي معناه إذا تكلموا عندك بأن يعرضوا بما فيه تهجين أمر المسلمين «والله يعلم أعمالكم».'),
(4576, 47, 31, '«ولنبلونكم» نختبرنكم بالجهاد وغيره «حتى نعلم» علم ظهور «المجاهدين منكم والصابرين» في الجهاد وغيره «ونبلوَ» نظهر «أخباركم» من طاعتكم وعصيانكم في الجهاد وغيره بالياء والنون في الأفعال الثلاثة.'),
(4577, 47, 32, '«إن الذين كفروا وصدوا عن سبيل الله» طريق الحق «وشاقوا الرسول» خالفوه «من بعد ما تبيَّن لهم الهدى» هو معنى سبيل الله «لن يضروا الله شيئا وسيحبط أعمالهم» يبطلها من صدقة ونحوها فلا يرون لها في الآخرة ثوابا، نزلت في المطعمين من أصحاب بدر أو في قريظة والنضير.'),
(4578, 47, 33, '«يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول ولا تبطلوا أعمالكم» بالمعاصي مثلا.'),
(4579, 47, 34, '«إن الذين كفروا وصدوا عن سبيل الله» طريقه وهو الهدى «ثم ماتوا وهم كفار فلن يغفر الله لهم» نزلت في أصحاب القليب.'),
(4580, 47, 35, '«فلا تهنوا» تضعفوا «وتدعوا إلى السَِّلم» بفتح السين وكسرها، أي الصلح مع الكفار إذا لقيتموهم «وأنتم الأعلوْن» حذف منه واو لام الفعل الأغلبون القاهرون «والله معكم» بالعون والنصر «ولن يَتِركُم» ينقصكم «أعمالكم» أي ثوابها.'),
(4581, 47, 36, '«إنما الحياة الدنيا» أي الاشتغال فيها «لعب ولهوٌ وإن تؤمنوا وتتقوا» الله وذلك من أمور الآخرة «يؤتكم أجوركم ولا يسألكم أموالكم» جميعها بل الزكاة المفروضة فيها.'),
(4582, 47, 37, '«إن يسألكموها فحيفكم» يبالغ في طلبها «تبخلوا ويخرج» البخل «أضغانكم» لدين الإسلام.'),
(4583, 47, 38, '«ها أنتم» يا «هؤلاء تدعون لتنفقوا في سبيل الله» ما فرض عليكم «فمنكم من يبخل ومن يبخلْ فإنما يبخل عن نفسه» يقال بخل عليه وعنه «والله الغني» عن نفقتكم «وأنتم الفقراء» إليه «وإن تتولوا» عن طاعته «يستبدل قوما غيركم» أي يجعلهم بدلكم «ثم لا يكونوا أمثالكم» في التولي عن طاعته بل مطيعين له عز وجل.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, '«إنا فتحنا لك» قضينا بفتح مكة وغيرها في المستقبل عَنوة بجهادك «فتحا مبينا» بيِّنا ظاهرا.'),
(4585, 48, 2, '«ليغفر لك الله» بجهادك «ما تقدم من ذنبك وما تأخر» منه لترغب أمتك في الجهاد وهو مؤول لعصمة الأنبياء عليهم الصلاة والسلام بالدليل العقلي القاطع من الذنوب واللام للعلة الغائية فمدخولها مسبب لا سبب «ويتم» بالفتح المذكور «نعمته» إنعامه «عليك ويهديك» به «صراطا» طريقا «مستقيما» يثبتك عليه وهو دين الإسلام.'),
(4586, 48, 3, '«وينصرك الله» به «نصرا عزيزا» ذا عز لا ذل له.'),
(4587, 48, 4, '«هو الذي أنزل السكينة» الطمأنينة «في قلوب المؤمنين ليزدادوا إيمانا مع إيمانهم» بشرائع الدين كلما نزَّل واحدة منها آمنوا بها ومنها الجهاد «ولله جنود السماوات والأرض» فلو أراد نصر دينه بغيركم لفعل «وكان الله عليما» بخلقه «حكيما» في صنعه، أي لم يزل متصفا بذلك.'),
(4588, 48, 5, '«ليدخل» متعلق بمحذوف، أي أمر الجهاد «المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفّر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما».'),
(4589, 48, 6, '«ويُعذَِب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السَّوء» بفتح السين وضمها في المواضع الثلاثة، ظنوا أنه لا ينصر محمدا صلى الله عليه وسلم والمؤمنين «عليهم دائرة السَّوء» بالذل والعذاب «وغضب الله عليهم ولعنهم» أبعدهم «وأعَدَّ لهم جهنم وساءت مصيرا» مرجعا.'),
(4590, 48, 7, '«ولله جنود السماوات والأرض وكان الله عزيزا» في ملكه «حكيما» في صنعه، أي لم يزل متصفا بذلك.'),
(4591, 48, 8, '«إنا أرسلناك شاهدا» على أمتك في القيامة «ومبشرا» لهم في الدنيا «ونذيرا» منذرا مخّوفا فيها من عمل سوءا بالنار.'),
(4592, 48, 9, '«ليؤمنوا بالله ورسوله» بالياء والتاء فيه وفي الثلاثة بعده «ويعزروه» ينصروه وقرئ بزايين مع الفوقانية «ويوقروه» يعظموه وضميرها لله أو لرسوله «ويسبحوه» أي الله «بكرة وأصيلا» بالغداة والعشيّ.'),
(4593, 48, 10, '(إن الذين يبايعونك) بيعة الرضوان بالحديبية. (إنما يبايعون الله) هو نحو "" من يطع الرسول فقد أطاع الله "" (يد الله فوق أيديهم) التي بايعوا بها النبي، أي هو تعالى مطلع على مبايعتهم فيجازيهم عليها (فمن نكث) نقض البيعة (فإنما ينكث) يرجع وبال نقضه (على نفسه ومن أوفى بما عاهد عليه الله فسيؤتيه) بالياء والنون (أجرا عظيما).'),
(4594, 48, 11, '«سيقول لك المخلفون من الأعراب» حول المدينة، أي الذين خلفهم الله عن صحبتك لما طلبتهم ليخرجوا معك إلى مكة خوفا من تعرض قريش لك عام الحديبية إذا رجعت منها «شغلتنا أموالنا وأهلونا» عن الخروج معك «فاستغفر لنا» الله من تَرْك الخروج معك قال تعالى مكذبا لهم: «يقولون بألسنتهم» أي من طلب الاستغفار وما قبله «ما ليس في قلوبهم» فهم كاذبون في اعتذارهم «قل فمن» استفهام بمعنى النفي أي لا أحد «يملك لكم من الله شيئا إن أراد بكم ضَُرا» بفتح الضاد وضمها «أو أراد بكم نفعا بل كان الله بما تعملون خبيرا» أي لم يزل متصفا بذلك.'),
(4595, 48, 12, '«بل» في الموضوعين للانتقال من غرض إلى آخر «ظننتم أن لن ينقلب الرسول والمؤمنون إلى أهليهم أبدا وزُيِّن ذلك في قلوبكم» أي أنهم يستأصلون بالقتل فلا يرجعون «وظننتم ظن السَّوء» هذا وغيره «وكنتم قوما بورا» جمع بائر، أي هالكين عند الله بهذا الظن.'),
(4596, 48, 13, '«ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا» نارا شديدة.'),
(4597, 48, 14, '«ولله ملك السماوات والأرض يغفر لمن يشاء ويعذِّب من يشاء وكان الله غفورا رحيما» أي لم يزل متصفا بما ذكر.'),
(4598, 48, 15, '«سيقول المخلفون» المذكورون «إذا انطقتم إلى مغانم» هي مغانم خيبر، «لتأخذوها ذرونا» اتركونا «نتبعكم» لنأخذ منها «يريدون» بذلك «أن يبدّلوا كلام الله» وفي قراءة: كلِم الله بكسر اللام أي مواعيده بغنائم خيبر أهل الحديبية خاصة «قل لن تتبعونا كذالكم قال الله من قبل» أي قبل عودنا «فسيقولون بل تحسدوننا» أن نصيب معكم من الغنائم فقلتم ذلك «بل كانوا لا يفقهون» من الدين «إلا قليلا» منهم.'),
(4599, 48, 16, '«قل للمخلفين من الأعراب» المذكورين اختبارا «ستدعوْن إلى قوم أولي» أصحاب «بأس شديد» قيل هم بنو حنيفة أصحاب اليمامة، وقيل فارس والروم «تقاتلونهم» حال مقدرة هي المدعو إليها في المعنى «أو» هم «يسلمون» فلا تقاتلون «فإن تطيعوا» إلى قتالهم «يؤتكم الله أجرا حسنا وإن تتولوا كما توليتم من قبل يعذّبكم عذابا أليما» مؤلما.'),
(4600, 48, 17, '«ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج» في ترك الجهاد «ومن يطع الله ورسوله يدخله» بالياء والنون «جنات تجري من تحتها الأنهار ومن يتول يعذّبه» بالياء والنون «عذابا أليما».'),
(4601, 48, 18, '«لقد رضي الله عن المؤمنين إذ يبايعونك» بالحديبية «تحت الشجرة» هي سمرة، وهم ألف وثلثمائة أو أكثر ثم بايعهم على أن يناجزوا قريشا وأن لا يفروا من الموت «فعلم» الله «ما في قلوبهم» من الصدق والوفاء «فأنزل السكينة عليهم وأثابهم فتحا قريبا» هو فتح خيبر بعد انصرافهم من الحديبية.'),
(4602, 48, 19, '«ومغانم كبيرة يأخذونها» من خيبر «وكان الله عزيزا حكيما» أي لم يزل متصفا بذلك.'),
(4603, 48, 20, '«وعدكم الله مغانم كثيرة تأخذونها» من الفتوحات «فعجَّل لكم هذه» غنيمة خيبر «وكفَّ أيدي الناس عنكم» في عيالكم لما خرجتم وهمت بهم اليهود فقذف الله في قلوبهم الرعب «ولتكون» أي المعجلة عطف على مقدر، أي لتشكروه «آية للمؤمنين» في نصرهم «ويهديكم صراطا مستقيما» أي طريق التوكل عليه وتفويض الأمر إليه تعالى.'),
(4604, 48, 21, '«وأخرى» صفة مغانم مقدرا مبتدأ «لم تقدروا عليها» هي من فارس والروم «قد أحاط الله بها» علم أنها ستكون لكم «وكان الله على كل شيء قديرا» أي لم يزل متصفا بذلك.'),
(4605, 48, 22, '«ولو قاتلكم الذين كفروا» بالحديبية «لولّوا الأدبار ثم لا يجدون وليا» يحرسهم «ولا نصيرا».'),
(4606, 48, 23, '«سنة الله» مصدر مؤكد لمضمون الجملة قبله من هزيمة الكافرين ونصر المؤمنين، أي سَنَّ الله ذلك سُنَّة «التي قد خلت من قبل ولن تجد لسنة الله تبديلا» منه.'),
(4607, 48, 24, '«وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة» بالحديبية «من بعد أن أظفركم عليهم» فإن ثمانين منهم طافوا بعسكركم ليصيبوا منكم فأخذوا وأتي بهم إلى رسول الله صلى الله عليه وسلم فعفا عنهم وخلى سبيلهم فكان ذلك سبب الصلح «وكان الله بما يعملون بصيرا» بالياء والتاء، أي لم يزل متصفا بذلك.'),
(4608, 48, 25, '«هم الذين كفروا وصدوكم عن المسجد الحرام» أي عن الوصول إليه «والهدي» معطوف على كم «معكوفا» محبوسا حال «أن يبلغ محله» أي مكانه الذي ينحر فيه عادة وهو الحرم بدل اشتمال «ولولا رجال مؤمنون ونساء مؤمنات» موجودون بمكة مع الكفار «لم تعلموهم» بصفة الإيمان «أن تطئوهم» أي تقتلوهم مع الكفار لو أذن لكم في الفتح بدل اشتمال من هم «فتصيبكم منهم معرة» أي إثم «بغير علم» منكم به وضمائر الغيبة للصنفين بتغليب الذكور، وجواب لولا محذوف، أي لأذن لكم في الفتح لكن لم يؤذن فيه حينئذ «ليدخل الله في رحمته من يشاء» كالمؤمنين المذكورين «لو تزيَّلوا» تميزوا عن الكفار «لعذَّبنا الذين كفروا منهم» من أهل مكة حينئذ بأن نأذن لكم في فتحها «عذابا أليما» مؤلما.'),
(4609, 48, 26, '«إذ جعل» متعلق بعذبنا «الذين كفروا» فاعل «في قلوبهم الحمية» الأنفة من الشيء «حمية الجاهلية» بدل من الحمية وهي صدهم النبي وأصحابه عن المسجد الحرام «فأنزل الله سكينته على رسول وعلى المؤمنين» فصالحوهم على أن يعودوا من قابل ولم يلحقهم من الحمية ما لحق الكفار حتى يقاتلوهم «وألزمهم» أي المؤمنين «كلمة التقوى» لا إله إلا الله محمد رسول الله وأضيفت إلى التقوى لأنها سببها «وكانوا أحق بها» بالكلمة من الكفار «وأهلها» عطف تفسيري «وكان الله بكل شيء عليما» أي لم يزل منصفا بذلك ومن معلومة تعالى أنهم أهلها.'),
(4610, 48, 27, '«لقد صدق الله رسوله الرؤيا بالحقِّ» رأى رسول الله صلى الله عليه وسلم في النوم عام الحديبية قبل خروجه أنه يدخل مكة هو وأصحابه آمنين ويحلقون ويقصرون فأخبر بذلك أصحابه ففرحوا فلما خرجوا معه وصدهم الكفار بالحديبية ورجعوا وشق عليهم ذلك وراب بعض المنافقين نزلت، وقوله بالحق متعلق بصدق أو حال من الرؤيا وما بعدها تفسيرها «لتدخلنِّ المسجد الحرام إنْ شاء الله» للتبرك «آمنين محلقين رءوسكم» أي جميع شعورها «ومقصرين» بعض شعورها وهما حالان مقدرتان «لا تخافون» أبدا «فعلم» في الصلح «ما لم تعلموا» من الصلاح «فجعل من دون ذلك» أي الدخول «فتحا قريبا» هو فتح خيبر وتحققت الرؤيا في العام القابل.'),
(4611, 48, 28, '«هو الذي أرسل رسوله بالهدى ودين الحقّ ليظهره» أي دين الحق «على الدين كله» على جميع باقي الأديان «وكفى بالله شهيدا» أنك مرسل بما ذكر كما قال الله تعالى.'),
(4612, 48, 29, '«محمد» مبتدأ «رسول الله» خبره والذين معه» أي أصحابه من المؤمنين مبتدأ خبره «أشداء» غلاظ «على الكفار» لا يرحمونهم «رحماء بينهم» خبر ثان، أي متعاطفون متوادون كالوالد مع الولد «تراهم» تبصرهم «ركعا سجدا» حالان «يبتغون» مستأنف يطلبون «فضلا من الله ورضوانا سيماهم» علامتهم مبتدأ «في وجوههم» خبره وهو نور وبياض يُعرفون به في الآخرة أنهم سجدوا في الدنيا «من أثر السجود» متعلق بما تعلق به الخبر، أي كائنة وأعرب حالا من ضميره المنتقل إلى الخبر «ذلك» الوصف المذكور «مثلهم» صفتهم مبتدأ «في التوراة» خبره «ومثلهم في الإنجيل» مبتدأ خبره «كزرع أخرج شطأه» بسكون الطاء وفتحها: فراخه «فآزره» بالمد والقصر قواه وأعانه «فاستغلظ» غلظ «فاستوى» قوي واستقام «على سوقه» أصوله جمع ساق «يعجب الزراع» أي زرَّاعه لحسنه، مثل الصحابة رضي الله عنهم بذلك لأنهم بدأوا في قلة وضعف فكثروا وقووا على أحسن الوجوه «ليغيظ بهم الكفار» متعلق بمحذوف دل عليه ما قبله، أي شبهوا بذلك «وعد الله الذين آمنوا وعملوا الصالحات منهم» الصحابة ومن لبيان الجنس لا للتبعيض لأنهم كلهم بالصفة المذكورة «مغفرة وأجرا عظيما» الجنة وهما لمن بعدهم أيضا في آيات.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, '«يا أيها الذين آمنوا لا تقدموا» من قدم بمعنى تقدم، أي لا تَقَدَّمُوا بقول ولا فعل «بين يدي الله ورسوله» المبلغ عنه، أي بغير إذنهما «واتقوا الله إن الله سميع» لقولكم «عليم» بفعلكم، نزلت في مجادلة أبي بكر وعمر رضي الله عنهما عند النبي صلى الله عليه وسلم في تأمير الأقرع بن حابس أو القعقاع بن معبد ونزل فيمن رفع صوته عند النبي صلى الله عليه وسلم.'),
(4614, 49, 2, '«يا أيها الذين آمنوا لا ترفعوا أصواتكم» إذا نطقتم «فوق صوت النبي» إذا نطق «ولا تجهروا له بالقول» إذا ناجيتموه «كجهر بعضكم لبعض» بل دون ذلك إجلالا له «أن تحبط أعمالكم وأنتم لا تشعرون» أي خشية ذلك بالرفع والجهر المذكورين، ونزل فيمن كان يخفض صوته عند النبي صلى الله عليه وسلم كأبي بكر وعمر وغيرهما رضى الله عنهم.'),
(4615, 49, 3, '«إن الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن» اختبر «الله قلوبهم للتقوى» أي لتظهر منهم «لهم مغفرة وأجر عظيم» الجنة، ونزل في قوم جاءُوا وقت الظهيرة والنبي صلى الله عليه وسلم في منزله فنادوه.'),
(4616, 49, 4, '«إن الذين ينادونك من وراء الحجرات» حجرات نسائه صلى الله عليه وسلم جمع حجرة وهي ما يحجر عليه من الأرض بحائط ونحوه، وكان كل واحد منهم نادى خلف حجرة لأنهم لم يعلموه في أي حجرة مناداة الأعراب بغلظة وجفاء «أكثرهم لا يعقلون» فيما فعلوه محلَّك الرفيع وما يناسبه من التعظيم.'),
(4617, 49, 5, '«ولو أنهم صبروا» أنهم في محل رفع بالابتداء، وقيل فاعل لفعل مقدر، أي ثبت «حتى تخرج إليهم لكان خيرا لهم والله غفور رحيم» لمن تاب منهم، ونزل في الوليد بن عقبة وقد بعثه النبي صلى الله عليه وسلم إلى بني المصطلق مصدقا فخافهم لترة كانت بينه وبينهم في الجاهلية فرجع وقال إنهم منعوا الصدقة وهموا بقتله، فهمَّ النبي صلى الله عليه وسلم بغزوهم فجاءوا منكرين ما قاله عنهم.'),
(4618, 49, 6, '«يا أيها الذين آمنوا إن جاءكم فاسق بنبأ» خبر «فتبينوا» صدقه من كذبه، وفي قراءة فتثبتوا من الثبات «أن تصيبوا قوما» مفعول له، أي خشية ذلك «بجهالة» حال من الفاعل، أي جاهلين «فتصبحوا» تصيروا «على ما فعلتم» من الخطأ بالقوم «نادمين» وأرسل صلى الله عليه وسلم إليهم بعد عودهم إلى بلادهم خالدا فلم يرَ فيهم إلا الطاعة والخير فأخبر النبي بذلك.'),
(4619, 49, 7, '«واعلموا أن فيكم رسول الله» فلا تقولوا الباطل فإن الله يخبره بالحال «لو يطيعكم في كثير من الأمر» الذي تخبرون به على خلاف الواقع فيرتب على ذلك مقتضاه «لعنتُّم» لأثمتم دونه إثم التسبب إلى المرتب «ولكن الله حبَّب إليكم الإيمان وزينه» حسنه «في قلوبكم وكرَّه إليكم الكفر والفسوق والعصيان» استدراك من حيث المعنى دون اللفظ لأن من حبب إليه الإيمان إلخ غايرت صفته صفة من تقدم ذكره «أولئك هم» فيه التفات عن الخطاب «الراشدون» الثابتون على دينهم.'),
(4620, 49, 8, '«فضلا من الله» مصدر منصوب بفعله المقدر، أي أفضل «ونعمة» منه «والله عليم» بهم «حكيم» في إنعامه عليهم.'),
(4621, 49, 9, '«وإن طائفتان من المؤمنين» الآية، نزلت في قضية هي أن النبي صلى الله عليه وسلم ركب حمارا ومر على ابن أبيّ فبال الحمار فسد ابن أبيّ أنفه فقال ابن رواحة: والله لبول حماره أطيب ريحا من مسكك فكان بين قوميهما ضرب بالأيدي والنعال والسعف «اقتتلوا» جمع نظرا إلى المعنى لأن كل طائفة جماعة، وقرئ اقتتلتا «فأصلحوا بينهما» ثنى نظرا إلى اللفظ «فإن بغت» تعدت «إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء» ترجع «إلى أمر الله» الحق «فإن فاءت فأصلحوا بينهما بالعدل» بالإنصاف «وأقسطوا» اعدلوا «إن الله يحب المقسطين».'),
(4622, 49, 10, '«إنما المؤمنون إخوة» في الدين «فأصلحوا بين أخويكم» إذا تنازعا، وقرئ إخوتكم بالفوقانية «واتقوا الله لعلكم ترحمون».'),
(4623, 49, 11, '«يا أيها الذين آمنوا لا يسخر» الآية، نزلت في وفد تميم حين سخروا من فقراء المسلمين كعمار وصهيب، والسخرية: الازدراء والاحتقار «قوم» أي رجال منكم «من قوم عسى أن يكونوا خيرا منهم» عند الله «ولا نساء» منكم «من نساءٍ عسى أن يكنَّ خيرا منهن ولا تلمزوا أنفسكم» لا تعيبوا فتعابوا، أي لا يعب بعضكم بعضا «ولا تنابزوا بالألقاب» لا يدعون بعضكم بعضا بلقب يكرهه، ومنه يا فاسق يا كافر «بئس الاسم» أي المذكور من السخرية واللمز والتنابز «الفسوق بعد الإيمان» بدل من الاسم أنه فسق لتكرره عادة «ومن لم يتب» من ذلك «فأولئك هم الظالمون».'),
(4624, 49, 12, '«يا أيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم» أي مؤثم وهو كثير كظن السوء بأهل الخير من المؤمنين، وهم كثير بخلافه بالفساق منهم فلا إثم فيه في نحو يظهر منهم «ولا تجسسوا» حذف منه إحدى التاءين لا تتبعوا عورات المسلمين ومعايبهم بالبحث عنها «ولا يغتب بعضكم بعضا» لا يذكره بشيء يكرهه وإن كان فيه «أيجب أحدكم أن يأكل لحم أخيه ميتا» بالتخفيف والتشديد، أي لا يحسن به «فكرهتموه» أي فاغتيابه في حياته كأكل لحمه بعد مماته وقد عرض عليكم الثاني فكرهتموه فاكرهوا الأول «واتقوا الله» أي عقابه في الاغتياب بأن تتوبوا منه «إن الله توَّاب» قابل توبة التائبين «رحيم» بهم.'),
(4625, 49, 13, '«يا أيها الناس إنا خلقناكم من ذكر وأنثى» آدم وحواء «وجعلناكم شعوبا» جمع شعب بفتح الشين هو أعلى طبقات النسب «وقبائل» هي دون الشعوب وبعدها العمائر ثم البطون ثم الأفخاذ ثم الفصائل آخرها، مثاله خزيمة: شعب، كنانة: قبيلة، قريش: عمارة بكسر العين، قُصي: بطن، هاشم: فخذ، العباس: فصيلة «لتعارفوا» حذف منه إحدى التاءين ليعرف بعضكم بعضا لا لتفاخروا بعلو النسب وإنما الفخر بالتقوى «إن أكرمكم عند الله أتقاكم إن الله عليم» بكم «خبير» ببواطنكم.'),
(4626, 49, 14, '«قالت الأعراب» نفر من بني أسد «آمنا» صدقنا بقلوبنا «قل» لهم «لم تؤمنوا ولكن قولوا أسلمنا» انقدنا ظاهرا «ولما» أي: لم «يدخل الإيمان في قلوبكم» إلى الآن لكنه يتوقع منكم «وإن تطيعوا الله ورسوله» بالإيمان وغيره «لا يَأْلِتْكُمْ» بالهمز وتركه وبإبداله ألفا: لا ينقصكم «من أعمالكم» أي من ثوابها «شيئا إن الله غفور» للمؤمنين «رحيم» بهم.'),
(4627, 49, 15, '«إنما المؤمنون» أي الصادقون في إيمانهم كما صرح به بعد «الذين آمنوا بالله ورسوله ثم لم يرتابوا» لم يشكوا الإيمان «وجاهدوا بأموالهم وأنفسهم في سبيل الله» فجهادهم يظهر بصدق إيمانهم «أولئك هم الصادقون» في إيمانهم، لا من قالوا آمنا ولم يوجد منهم غير الإسلام.'),
(4628, 49, 16, '«قل» لهم «أتعلمون الله بدينكم» مضعف علم بمعنى شعر، أي أتُشْعِرونه بما أنتم عليه في قولكم آمنا «والله يعلم ما في السماوات وما في الأرض والله بكل شيء عليم».'),
(4629, 49, 17, '«يمنون عليك أن أسلموا» من غير قتال بخلاف غيرهم ممن أسلم بعد قتاله منهم «قل لا تمنوا عليَّ إسلامكم» منصوب بنزع الخافض الباء ويقدر قبل أن في الموضعين «بل الله يمنُّ عليكم أن هداكم للإيمان إن كنتم صادقين» في قولكم آمنا.'),
(4630, 49, 18, '«إن الله يعلم غيب السماوات والأرض» أي ما غاب فيهما «والله بصير بما يعملون» بالياء والتاء لا يخفى عليه شيء منه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, '«ق» الله أعلم بمراده به «والقرآن المجيد» الكريم ما آمن كفار مكة بمحمد صلى الله عليه وسلم.'),
(4632, 50, 2, '«بل عجبوا أن جاءهم منذر منهم» رسول من أنفسهم يخوّفهم بالنار بعد البعث «فقال الكافرون هذا» الإنذار «شيء عجيب».'),
(4633, 50, 3, '«أإذا» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين «متنا وكنا ترابا» نرجع «ذلك رجع بعيد» في غاية البعد.'),
(4634, 50, 4, '«قد علمنا ما تنقص الأرض» تأكل «منهم وعندنا كتاب حفيظ» هو اللوح المحفوظ فيه جميع الأشياء المقدرة.'),
(4635, 50, 5, '«بل كذبوا بالحق» بالقرآن «لما جاءهم فهم» في شأن النبي صلى الله عليه وسلم والقرآن «في أمر مريج» مضطرب قالوا مرة: ساحر وسحر، ومرة: شاعر وشعر، ومرة: كاهن وكهانة.'),
(4636, 50, 6, '«أفلم ينظروا» بعيونهم معتبرين بعقولهم حين أنكروا البعث «إلى السماء» كائنة «فوقهم كيف بنيناها» بلا عمد «وزيناها» بالكواكب «وما لها من فروج» شقوق تعيبها.'),
(4637, 50, 7, '«والأرض» معطوف على موضع إلى السماء، كيف «مددناها» دحوناها على وجه الماء «وألقينا فيها رواسي» جبالا تثبتها «وأنبتنا فيها من كل زوج» صنف «بهيج» يبهج به لحسنه.'),
(4638, 50, 8, '«تبصرة» مفعول له، أي فعلنا ذلك تبصيرا منا «وذكرى» تذكيرا «لكل عبد منيب» رجّاع إلى طاعتنا.'),
(4639, 50, 9, '«ونزلنا من السماء ماءً مباركا» كثير البركة «فأنبتنا به جنات» بساتين «وحب» الزرع «الحصيد» المحصود.'),
(4640, 50, 10, '«والنخل باسقات» طوالا حال مقدرة «لها طلع نضيد» متراكب بعضه فوق بعض.'),
(4641, 50, 11, '«رزقا للعباد» مفعول له «وأحيينا به بلدة ميتا» يستوي فيه المذكر والمؤنث «كذلك» أي مثل هذا الإحياء «الخروج» من القبور فكيف تنكرونه والاستفهام للتقرير والمعنى أنهم نظروا وعلموا ما ذكر.'),
(4642, 50, 12, '«كذبت قبلهم قوم نوح» تأنيث الفعل بمعنى قوم «وأصحاب الرس» هي بئر كانوا مقيمين عليها بمواشيهم يعبدون الأصنام ونبيهم: قيل حنظلة بن صفوان وقيل غيره «وثمود» قوم صالح.'),
(4643, 50, 13, '«وعاد» قوم هود «وفرعون وإخوان لوط».'),
(4644, 50, 14, '«وأصحاب الأيكة» الغيضة قوم شعيب «وقوم تُبَّع» هو ملك كان باليمن أسلم ودعا قومه إلى الإسلام فكذبوه «كل» من المذكورين «كذب الرسل» كقريش «فحق وعيد» وجب نزول العذاب على الجميع فلا يضيق صدرك من كفر قريش بك.'),
(4645, 50, 15, '«أفعيينا بالخلق الأول» أي لم نعي به فلا نعيا بالإعادة «بل هم في لبس» شك «من خلق جديد» وهو البعث.'),
(4646, 50, 16, '«ولقد خلقنا الإنسان ونعلم» حال بتقدير نحن «ما» مصدرية «توسوس» تحدث «به» الباء زائدة أو للتعدية والضمير للإنسان «نفسه ونحن أقرب إليه» بالعلم «من حبل الوريد» الإضافة للبيان والوريدان عرقان بصفحتي العنق.'),
(4647, 50, 17, '«إذ» منصوبة باذكر مقدرا «يتلقى» يأخذ ويثبت «المتلقيان» الملكان الموكلان بالإنسان ما يعمله «عن اليمين وعن الشمال» منه «قعيد» أي قاعدان وهو مبتدأ خبره ما قبله.'),
(4648, 50, 18, '«ما يلفظ من قول إلا لديه رقيب» حافظ «عتيد» حاضر وكل منهما بمعنى المثنى.'),
(4649, 50, 19, '«وجاءت سكرة الموت» غمرته وشدته «بالحق» من أمر الآخرة حتى المنكر لها عيانا وهو نفس الشدة «ذلك» أي الموت «ما كنت منه تحيد» تهرب وتفزع.'),
(4650, 50, 20, '«ونفخ في الصور» للبعث «ذلك» أي يوم النفخ «يوم الوعيد» للكفار بالعذاب.'),
(4651, 50, 21, '«وجاءت» فيه «كل نفس» إلى المحشر «معها سائق» ملك يسوقها إليه «وشهيد» يشهد عليها بعملها وهو الأيدي والأرجل وغيرها ويقال للكافر.'),
(4652, 50, 22, '«لقد كنت» في الدنيا «في غفلة من هذا» النازل بك اليوم «فكشفنا عنك غطاءك» أزلنا غفلتك بما تشاهده اليوم «فبصرُك اليوم حديد» حاد تدرك به ما أنكرته في الدنيا.'),
(4653, 50, 23, '«وقال قرينه» الملك الموكل به «هذا ما» أي الذي «لديَّ عتيد» حاضر فيقال لمالك.'),
(4654, 50, 24, '«ألقيا في جهنم» أي: ألق ألق أو ألقين وبه قرأ الحسن فأبدلت النون ألفا «كلَّ كفار عنيد» معاند للحق.'),
(4655, 50, 25, '«منَّاع للخير» كالزكاة «معتد» ظالم «مريب» شاك في دينه.'),
(4656, 50, 26, '«الذي جعل مع الله إلها آخر» مبتدأ ضُمن معنى الشرط خبره «فألقياه في العذاب الشديد» تفسيره مثل ما تقدم.'),
(4657, 50, 27, '«قال قرينه» الشيطان «ربنا ما أطغيته» أضللته «ولكن كان في ضلال بعيد» فدعوته فاستجاب لي، وقال هو أطغاني بدعائه له.'),
(4658, 50, 28, '«قال» تعالى «لا تختصموا لديَّ» أي ما ينفع الخصام هنا «وقد قدمت إليكم» في الدنيا «بالوعيد» بالعذاب في الآخرة لو لم تؤمنوا ولا بد منه.'),
(4659, 50, 29, '(ما يبدل) يغير (القول لدي) في ذلك (وما أنا بظلام للعبيد) فأعذبهم بغير جرم، وظلام بمعنى ذي ظلم لقوله "" لا ظلم "".'),
(4660, 50, 30, '«يوم» ناصبه ظلام «نقول» بالنون والياء «لجهنم هل امتلأت» استفهام لوعده بمثلها «وتقول» بصورة الاستفهام كالسؤال «هل من مزيد» أي لا أسع غير ما امتلأت به، أي قد امتلأت.'),
(4661, 50, 31, '«وأزلفت الجنة» قربت «للمتقين» مكانا «غير بعيد» منهم فيرونها ويقال لهم.'),
(4662, 50, 32, '«هذا» المرئي «ما توعدون» بالتاء والياء في الدنيا ويبدل من للمتقين قوله «لكل أوَّاب» رجّاع إلى طاعة الله «حفيظ» حافظ لحدوده.'),
(4663, 50, 33, '«من خشي الرحمن بالغيب» خافه ولم يره «وجاء بقلب منيب» مقبل على طاعته، ويقال للمتقين أيضا.'),
(4664, 50, 34, '«ادخلوها بسلام» سالمين من كل مخوف أو مع سلام، أي سلموا وادخلوا «ذلك» اليوم الذي حصل فيه الدخول «يومُ الخلود» الدوام في الجنة.'),
(4665, 50, 35, '«لهم ما يشاءُون فيها ولدينا مزيد» زيادة على ما عملوا وطلبوا.'),
(4666, 50, 36, '«وكم أهلكنا قبلهم من قرن» أي أهلكنا قبل كفار قريش قرونا كثيرة من الكفار «هم أشد منهم بطشا» قوة «فنقبوا» فتشوا «في البلاد هل من محيص» لهم أو لغيرهم من الموت فلم يجدوا.'),
(4667, 50, 37, '«إن في ذلك» المذكور «لذكرى» لعظة «لمن كان له قلب» عقل «أو ألقى السمع» استمع الوعظ «وهو شهيد» حاضر بالقلب.'),
(4668, 50, 38, '(ولقد خلقنا السماوات والأرض وما بينهما في ستة أيام) أولها الأحد وآخرها الجمعة (وما مسنا من لغوب) تعب، نزل ردا على اليهود في قولهم: إن الله استراح يوم السبت وانتفاء التعب عنه لتنزهه تعالى عن صفات المخلوقين ولعدم المماثلة بينه وبين غيره "" إنما أمره إذا أراد شيئا أن يقول له كن فيكون "".'),
(4669, 50, 39, '«فاصبر» خطاب للنبي صلى الله عليه وسلم «على ما يقولون» أي اليهود وغيرهم من التشبيه والتكذيب «وسبح بحمد ربك» صل حامدا «قبل طلوع الشمس» أي صلاة الصبح «وقبل الغروب» أي صلاة الظهر والعصر.'),
(4670, 50, 40, '«ومن الليل فسبحه» أي صل العشاءين «وأَِدبار السجود» بفتح الهمزة جمع دبر وكسرها مصدر أدبر، أي صل النوافل المسنونة عقب الفرائض وقيل المراد حقيقة التسبيح في هذه الأوقات ملابسا للحمد.'),
(4671, 50, 41, '«واستمع» يا مخاطب مقولي «يوم يناد المناد» هو إسرافيل «من مكان قريب» من السماء وهو صخرة بيت المقدس أقرب موضع من الأرض إلى السماء يقول: أيتها العظام البالية والأوصال المتقطعة واللحوم المتمزقة والشعور المتفرقة إن الله يأمركن أن تجتمعن لفصل القضاء.'),
(4672, 50, 42, '«يوم» بدل من يوم قبله «يسمعون» أي الخلق كلهم «الصيحة بالحق» بالبعث وهي النفخة الثانية من إسرافيل ويحتمل أن تكون قبل ندائه وبعده «ذلك» أي يوم النداء والسماع «يوم الخروج» من القبور وناصب يوم ينادي مقدرا، أي يعلمون عاقبة تكذيبهم.'),
(4673, 50, 43, '«إنا نحن نحيي ونميت وإلينا المصير».'),
(4674, 50, 44, '«يوم» بدل من يوم قبله وما بينهما اعتراض «تشقق» بتخفيف الشين وتشديدها بإدغام التاء الثانية في الأصل فيها «الأرض عنهم سراعا» جمع سريع حال من مقدر، أي فيخرجون مسرعين «ذلك حشر علينا يسير» فيه فصل بين الموصوف والصفة بمتعلقها للاختصاص وهو لا يضر وذلك إشارة إلى معنى الحشر المخبر به عنه، وهو الإحياء بعد الفناء والجمع للعرض والحساب.'),
(4675, 50, 45, '«نحن أعلم بما يقولون» أي كفار قريش «وما أنت عليهم بجبار» تجبرهم على الإيمان وهذا قبل الأمر بالجهاد «فذكِّر بالقرآن من يخاف وعيد» وهم المؤمنون.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, '«والذاريات» الرياح تذرو التراب وغيره «ذروا» مصدر، ويُقال تذريه ذريا تهب به.'),
(4677, 51, 2, '«فالحاملات» السحب تحمل الماء «وقراً» ثقلا مفعول الحاملات'),
(4678, 51, 3, '«فالجاريات» السفن تجري على وجه الماء «يُسرا» بسهولة مصدر في موضع الحال، أي ميسرة.'),
(4679, 51, 4, '«فالمقسمات أمرا» الملائكة تقسم الأرزاق والأمطار وغيرها بين البلاد والعباد.'),
(4680, 51, 5, '«إنما توعدون» ما مصدرية، أي وعدهم بالبعث وغيره «لصادق» لوعد صادق.'),
(4681, 51, 6, '«وإن الدين» الجزاء بعد الحساب «لواقع» لا محالة.'),
(4682, 51, 7, '«والسماء ذات الحبك» جمع حبيكة كطريقة وطريق أي صاحبة الطرق في الخلقة كالطريق في الرمل.'),
(4683, 51, 8, '«إنكم» يا أهل مكة في شأن النبي صلى الله عليه وسلم والقرآن «لفي قول مختلف» قيل شاعر ساحر كاهن شعر سحر كهانة.'),
(4684, 51, 9, '«يؤفك عنه من أفك» صرف عن الهداية في علم الله تعالى.'),
(4685, 51, 10, '«قُتل الخراصون» لعن الكذابون أصحاب القول المختلف.'),
(4686, 51, 11, '«الذين هم في غمرة» جهل يغمرهم «ساهون» غافلون عن أمر الآخرة.'),
(4687, 51, 12, '«يسألون» النبي استفهام استهزاء «أيان يوم الدين» أي متى مجيئه وجوابهم: يجيء.'),
(4688, 51, 13, '«يوم هم على النار يفتنون» أي يعذبون فيها ويقال لهم حين التعذيب.'),
(4689, 51, 14, '«ذوقوا فتنتكم» تعذيبكم «هذا» التعذيب «الذي كنتم به تستعجلون» في الدنيا استهزاء.'),
(4690, 51, 15, '«إن المتقين في جنات» بساتين «وعيون» تجري فيها.'),
(4691, 51, 16, '«آخذين» حال من الضمير في خبر إن «ما آتاهم» أعطاهم «ربهم» من الثواب «إنهم كانوا قبل ذلك» أي دخولهم الجنة «محسنين» في الدنيا.'),
(4692, 51, 17, '«كانوا قليلا من الليل ما يهجعون» ينامون، وما زائدة ويهجعون خبر كان وقليلا ظرف، أي ينامون في زمن يسير من الليل ويصلون أكثره.'),
(4693, 51, 18, '«وبالأسحار هم يستغفرون» يقولون: اللهم اغفر لنا.'),
(4694, 51, 19, '«وفي أموالهم حق للسائل والمحروم» الذي لا يسأل لتعففه.'),
(4695, 51, 20, '«وفي الأرض» من الجبال والبحار والأشجار والثمار والنبات وغيرها «آيات» دلالات على قدرة الله سبحانه وتعالى ووحدانيته «للموقنين».'),
(4696, 51, 21, '«وفي أنفسكم» آيات أيضا من مبدأ خلقكم إلى منتهاه، وما في تركيب خلقكم من العجائب «أفلا تبصرون» ذلك فتستدلون به على صانعه وقدرته.'),
(4697, 51, 22, '«وفي السماء رزقكم» أي المطر المسبب عنه النبات الذي هو رزق «وما توعدون» من المآب والثواب والعقاب أي مكتوب ذلك في السماء.'),
(4698, 51, 23, '«فورب السماء والأرض إنه» أي ما توعدون «لحق مثلُ ما أنكم تنطقون» برفع مثل صفة، وما مزيدة وبفتح اللام مركبة مع ما، المعنى: مثل نطقكم في حقيقته أي معلوميته عندكم ضرورة صدوره عنكم.'),
(4699, 51, 24, '«هل أتاك» خطاب للنبي صلى الله عليه وسلم «حديث ضيف إبراهيم المكرمين» وهم ملائكة اثنا عشر أو عشرة أو ثلاثة، منهم جبريل.'),
(4700, 51, 25, '«إذ» ظرف لحديث ضيف «دخلوا عليه فقالوا سلاما» أي هذا اللفظ «قال سلام» أي هذا اللفظ «قوم منكرون» لا نعرفهم قال ذلك في نفسه وهو خبر مبتدأ مقدر أي هؤلاء.'),
(4701, 51, 26, '(فراغ) مال (إلى أهله) سرا (فجاء بعجل سمين) وفي سورة هود "" بعجل حنيذ "" أي مشوي.'),
(4702, 51, 27, '«فقربه إليهم قال ألا تأكلون» عرض عليهم الأكل فلم يجيبوا.'),
(4703, 51, 28, '«فأوجس» أضمر في نفسه «منهم خيفة قالوا لا تخف» إنا رسل ربك «وبشروه بغلام عليم» ذي علم كثير وهو إسحاق كما ذكر في هود.'),
(4704, 51, 29, '«فأقبلت امراته» سارة «في صرَّة» صيحة حال، أي جاءت صائحة «فصكت وجهها» لطمته «وقالت عجوز عقيم» لم تلد قط وعمرها تسع وتسعون سنة وعمر إبراهيم مائة سنة، أو عمره مائة وعشرون سنة وعمرها تسعون سنة.'),
(4705, 51, 30, '«قالوا كذلك» أي مثل قولنا في البشارة «قال ربك إنه هو الحكيم» في صنعه «العليم» بخلقه.'),
(4706, 51, 31, '«قال فما خطبكم» شأنكم «أيها المرسلون».'),
(4707, 51, 32, '«قالوا إنا أرسلنا إلى قوم مجرمين» كافرين هم قوم لوط.'),
(4708, 51, 33, '«لنرسل عليهم حجارة من طين» مطبوخ بالنار.'),
(4709, 51, 34, '«مُسَوَّمة» معلمة عليها اسم من يْرمى بها «عند ربك» ظرف لها «للمسرفين» بإتيانهم الذكور مع كفرهم.'),
(4710, 51, 35, '«فأخرجنا من كان فيها» أي قرى قوم لوط «من المؤمنين» لإهلاك الكافرين.'),
(4711, 51, 36, '«فما وجدنا فيها غير بيت من المسلمين» وهو لوط وابنتاه وصفوا بالإيمان والإسلام، أي هم مصدقون بقلوبهم عاملون بجوارحهم الطاعات.'),
(4712, 51, 37, '«وتركنا فيها» بعد إهلاك الكافرين «آية» علامة على إهلاكهم «للذين يخافون العذاب الأليم» فلا يفعلون مثل فعلهم.'),
(4713, 51, 38, '«وفي موسى» معطوف على فيها، المعنى وجعلنا في قصة موسى آية «إذ أرسلناه إلى فرعون» ملتبسا «بسلطان مبين» بحجة واضحة.'),
(4714, 51, 39, '«فتولى» أعرض عن الإيمان «بركنه» مع جنوده لأنهم له كالركن «وقال» لموسى هو «ساحر أو مجنون».'),
(4715, 51, 40, '«فأخذناه وجنوده فنبذناهم» طرحناهم «في اليم» البحر فغرقوا «وهو» أي فرعون «مُليم» آت بما يلام عليه من تكذيب الرسل ودعوى الربوبية.'),
(4716, 51, 41, '«وفي» إهلاك «عاد» آية «إذ أرسلنا عليهم الريح العقيم» هي التي لا خير فيها لأنها لا تحمل المطر ولا تلقح الشجر وهي الدبور.'),
(4717, 51, 42, '«ما تذر من شيء» نفس أو مال «أتت عليه إلا جعلته كالرميم» كالبالي المتفتت.'),
(4718, 51, 43, '(وفي) إهلاك (ثمود) آية (إذ قيل لهم) بعد عقر الناقة (تمتعوا حتى حين) إلى انقضاء آجالكم كما في آية "" تمتعوا في داركم ثلاثة أيام "".'),
(4719, 51, 44, '«فعتوْا» تكبروا «عن أمر ربهم» أي عن امتثاله «فأخذتهم الصاعقة» بعد مضي الثلاثة أيام أي الصيحة المهلكة «وهم ينظرون» أي بالنهار.'),
(4720, 51, 45, '«فما استطاعوا من قيام» أي ما قدروا على النهوض حين نزول العذاب «وما كانوا منتصرين» على من أهلكهم.'),
(4721, 51, 46, '«وقوم نوح» بالجر عطف على ثمود أي وفى إهلاكهم بما في السماء والأرض آية, وبالنصب أي وأهلكنا قوم نوح «من قبل» أي قبل إهلاك هؤلاء المذكورين «إنهم كانوا قوما فاسقين».'),
(4722, 51, 47, '«والسماء بنيناها بأيد» بقوة «وإنا لموسعون» قادرون يقال: أد الرجل يئيد قوي، وأوسع الرجل: صار ذا سعة وقوة.'),
(4723, 51, 48, '«والأرض فرشناها» مهدناها «فنعم الماهدون» نحن «ومن كل شيء» متعلق بقوله خلقنا زوجين.'),
(4724, 51, 49, '«ومن كل شيء» متعلق بقوله: خلقنا «خلقنا زوجين» صنفين كالذكر والأنثى والسماء والأرض، والشمس والقمر، والسهل والجبل، والصيف والشتاء، والحلو والحامض والنور والظلمة «لعلكم تذكَّرون» بحذف إحدى التاءين من الأصل فتعلمون أن خالق الأزواج فرد فتعبدوه.'),
(4725, 51, 50, '«ففروا إلى الله» أي إلى ثوابه من عقابه بأن تطيعوه ولا تعصوه «إني لكم منه نذير مبين» بيِّن الإنذار.'),
(4726, 51, 51, '«ولا تجعلوا مع الله إلها آخر إني لكم منه نذير مبين» يقدر قبل ففروا قل لهم.'),
(4727, 51, 52, '«كذلك ما أتى الذين من قبلهم من رسول إلا قالوا» هو «ساحر أو مجنون» أي مثل تكذيبهم لك بقولهم إنك ساحر أو مجنون تكذيب الأمم قبلهم رسلهم بقولهم ذلك.'),
(4728, 51, 53, '«أتواصوْا» كلهم «به» استفهام بمعنى النفي «بل هم قوم طاغون» جمعهم على هذا القول طغيانهم.'),
(4729, 51, 54, '«فتولَّ» أعرض «عنهم فما أنت بملوم» لأنك بلغتهم الرسالة.'),
(4730, 51, 55, '«وذكر» عظ بالقرآن «فإن الذكرى تنفع المؤمنين» من علم اللهُ تعالى أنه يؤمن.'),
(4731, 51, 56, '«وما خلقت الجن والإنس إلا ليعبدون» ولا ينافي ذلك عدم عبادة الكافرين، لأن الغاية لا يلزم وجودها كما في قولك: بريت هذا القلم لأكتب به، فإنك قد لا تكتب به.'),
(4732, 51, 57, '«ما أريد منهم من رزق» لي ولأنفسهم وغيرهم «وما أريد أن يطعمون» ولا أنفسهم ولا غيرهم.'),
(4733, 51, 58, '«إن الله هو الرزاق ذو القوة المتين» الشديد.'),
(4734, 51, 59, '«فإن للذين ظلموا» أنفسهم بالكفر من أهل مكة وغيرهم «ذنوبا» نصيبا من العذاب «مثل ذنوب» نصيب «أصحابهم» الهالكين قبلهم «فلا يستعجلون» بالعذاب إن أخرتهم إلى يوم القيامة.'),
(4735, 51, 60, '«فويل» شدة عذاب «للذين كفروا من» في «يومهم الذي يوعدون» أي يوم القيامة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, '«والطور» أي الجبل الذي كلم الله عليه موسى.'),
(4737, 52, 2, '«وكتاب مسطور».'),
(4738, 52, 3, '«في رقٍّ منشور» أي التوراة أو القرآن.'),
(4739, 52, 4, '«والبيت المعمور» هو في السماء الثالثة أو السادسة أو السابعة بحيال الكعبة يزوره كل يوم سبعون ألف ملك بالطواف والصلاة لا يعودون إليه أبدا.'),
(4740, 52, 5, '«والسقف المرفوع» أي السماء.'),
(4741, 52, 6, '«والبحر المسجور» أي المملوء.'),
(4742, 52, 7, '«إن عذاب ربك لواقع» لنازل بمستحقه.'),
(4743, 52, 8, '«ماله من دافع» عنه.'),
(4744, 52, 9, '«يوم» مفعول لواقع «تمور السماء مورا» تتحرك وتدور.'),
(4745, 52, 10, '«وتسير الجبال سيرا» تصير هباء منثورا وذلك في يوم القيامة.'),
(4746, 52, 11, '«فويل» شدة عذاب «يومئذ للمكذبين» للرسل.'),
(4747, 52, 12, '«الذين هم في خوض» باطل «يلعبون» أي يتشاغلون بكفرهم.'),
(4748, 52, 13, '«يوم يُدعُّون إلى نار جهنم دعّا» يدفعون بعنف بدل من يوم تمور، ويقال لهم تبكيتا.'),
(4749, 52, 14, '«هذه النار التي كنتم بها تكذبون».'),
(4750, 52, 15, '«أفسحر هذا» العذاب الذي ترون كما كنتم تقولون في الوحي هذا سحر «أم أنتم لا تبصرون».'),
(4751, 52, 16, '«اصلوْها فاصبروا» عليها «أو لا تصبروا» صبركم وجزعكم «سواء عليكم» لأن صبركم لا ينفعكم «إنما تجزون ما كنتم تعملون» أي جزاءه.'),
(4752, 52, 17, '«إن المتقين في جنات ونعيم».'),
(4753, 52, 18, '«فاكهين» متلذذين «بما» مصدرية «آتاهم» أعطاهم «ربهم ووقاهم ربهم عذاب الجحيم» عطفا على آتاهم، أي بإتيانهم ووقايتهم ويقال لهم.'),
(4754, 52, 19, '«كلوا واشربوا هنيئا» حال أي: مهنئين «بما» الباء سببية «كنتم تعملون».'),
(4755, 52, 20, '(متكئين) حال من الضمير المستكن في قوله "" في جنات "" (على سرر مصفوفة) بعضها إلى جنب بعض (وزوجناهم) عطف على جنات، أي قرناهم (بحور عين) عظام الأعين حسانها.'),
(4756, 52, 21, '«والذين آمنوا» مبتدأ «أتبعناهم» وفي قراءة واتبعتهم معطوف على آمنوا «ذرياتهم» وفي قراءة ذريتهم الصغار والكبار «بإيمان» من الكبار ومن أولادهم الصغار والخبر «ألحقنا بهم ذرياتهم» المذكورين في الجنة فيكونون في درجتهم وإن لم يعملوا تكرمة للآباء باجتماع الأولاد إليهم «وما ألتناهم» بفتح اللام وكسرها نقصناهم «من عملهم من» زائدة «شيء» يزاد في عمل الأولاد «كل امرئ بما كسب» من عمل خير أو شر «رهين» مرهون يؤاخذ بالشر ويجازى بالخير.'),
(4757, 52, 22, '«وأمددناهم» زدناهم في وقت بعد وقت «بفاكهة ولحم ما يشتهون» وإن لم يصرحوا بطلبه.'),
(4758, 52, 23, '«يتنازعون» يتعاطون بينهم «فيها» أي الجنة «كأسا» خمرا «لا لغوٌ فيها» أي بسبب شربها يقع بينهم «ولا تأثيم» به يلحقهم بخلاف خمر الدنيا.'),
(4759, 52, 24, '«ويطوف عليهم» للخدمة «غلمان» أرقاء «لهم كأنهم» حسنا ولطافة «لؤلؤ مكنون» مصون في الصدف لأنه فيها أحسن منه في غيرها.'),
(4760, 52, 25, '«وأقبل بعضهم على بعض يتساءلون» يسأل بعضهم بعضا عما كانوا عليه وما وصلوا إليه تلذذا واعترافا بالنعمة.'),
(4761, 52, 26, '«قالوا» إيماء إلى علة الوصول «إنا كنا قبل في أهلنا» في الدنيا «مشفقين» خائفين من عذاب الله.'),
(4762, 52, 27, '«فمنّ الله علينا» بالمغفرة «ووقانا عذاب السموم» النار لدخولها في المسام وقالوا إيماء أيضا.'),
(4763, 52, 28, '«إنا كنا من قبل» أي في الدنيا «ندعوه» نعبده موحدين «إنه» بالكسر استئنافا وإن كان تعليلا معنى وبالفتح تعليلا لفظا «هو البر» المحسن الصادق في وعده «الرحيم» العظيم الرحمة.'),
(4764, 52, 29, '«فذكِّر» دم على تذكير المشركين ولا ترجع عنه لقولهم لك كاهن مجنون «فما أنت بنعمة ربك» بإنعامه عليك «بكاهن» خبر ما «ولا مجنون» معطوف عليه.'),
(4765, 52, 30, '«أم» بل «يقولون» هو «شاعر نتربص به ريْب المنون» حوادث الدهر فيهلك كغيره من الشعراء.'),
(4766, 52, 31, '«قل تربصوا» هلاكي «فإني معكم من المتربصين» هلاككم فعذبوا بالسيف يوم بدر، والتربص الانتظار.'),
(4767, 52, 32, '«أم تأمرهم أحلامهم» عقولهم «بهذا» قولهم له: ساحر كاهن مجنون، أي لا تأمرهم بذلك «أم» بل «هم قوم طاغون» بعنادهم.'),
(4768, 52, 33, '«أم يقولون تقوَّلهُ» اختلق القرآن، لم يختلقه «بل لا يؤمنون» استكبارا، فإن قالوا اختلقه.'),
(4769, 52, 34, '«فليأتوا بحديث» مختلق «مثله إن كانوا صادقين» في قولهم.'),
(4770, 52, 35, '«أم خُلقوا من غير شيء» من غير خالق «أم هم الخالقون» أنفسهم ولا يعقل مخلوق بغير خالق ولا معدوم يخلق فلابد من خالق هو الله الواحد فلم لا يوحدونه ويؤمنون برسوله وكتابه.'),
(4771, 52, 36, '«أم خلقوا السماوات والأرض» ولا يقدر على خلقهما إلا الله الخالق فلم لا يعبدونه «بل لا يوقنون» به وإلا لآمنوا بنبيه.'),
(4772, 52, 37, '«أم عندهم خزائن ربك» من النبوة والرزق وغيرهما فيخصوا من شاءُوا بما شاءُوا «أم هم المسيطرون» المتسلطون الجبارون وفعله سيطر ومثله بيطر وبيقر.'),
(4773, 52, 38, '«أم لهم سلم» مرقى إلى السماء «يستمعون فيه» أي عليه كلام الملائكة حتى يمكنهم منازعة النبي بزعمهم إن ادعوا ذلك «فليأت مبين» بحجة مستمعهم مدعي الاستماع عليه «بسلطان مبين» بينة واضحة ولشبه هذا الزعم بزعمهم أن الملائكة بنات الله قال تعالى.'),
(4774, 52, 39, '«أم له البنات» بزعمكم «ولكم البنون» تعالى الله عما زعمتموه.'),
(4775, 52, 40, '«أم تسألهم أجرا» على ما جئتهم به من الدين «فهم من مغرم» غرم ذلك «مثقلون» فلا يسلمون.'),
(4776, 52, 41, '«أم عندهم الغيب» أي علمه «فهم يكتبون» ذلك حتى يمكنهم منازعة النبي صلى الله عليه وسلم في البعث وأمور الآخرة بزعمهم.'),
(4777, 52, 42, '«أم يريدون كيدا» بك ليهلكوك في دار الندوة «فالذين كفروا هم المكيدون» المغلوبون المهلكون فحفظه الله منهم ثم أهلكهم ببدر.'),
(4778, 52, 43, '«أم لهم إله غير الله سبحان الله عما يشركون» به من الآلهة والاستفهام بأم في مواضعها للتقبيح والتوبيخ.'),
(4779, 52, 44, '(وإن يروا كسفا) بعضا (من السماء ساقطا) عليهم كما قالوا: "" فأسقط علينا كسفا من السماء "" أي تعذيبا لهم (يقولوا) هذا (سحاب مركوم) متراكب نروى به ولا يؤمنون.'),
(4780, 52, 45, '«فذرهم حتى يلاقوا يومهم الذي فيه يصعقون» يموتون.'),
(4781, 52, 46, '«يوم لا يغني» بدل من يومهم «عنهم كيدهم شيئا ولا هم يُنصرون» يمنعون من العذاب في الآخرة.'),
(4782, 52, 47, '«وإنَّ للذين ظلموا» بكفرهم «عذابا دون ذلك» في الدنيا قبل موتهم فعذبوا بالجوع والقحط سبع سنين وبالقتل يوم بدر «ولكن أكثرهم لا يعلمون» أن العذاب ينزل بهم.'),
(4783, 52, 48, '«واصبر لحكم ربك» بإمهالهم ولا يضق صدرك «فإنك بأعيننا» بمرأى منا نراك ونحفظك «وسبح» متلبسا «بحمد ربك» أي قل: سبحان الله وبحمده «حين تقوم» من منامك أو من مجلسك.'),
(4784, 52, 49, '«ومن الليل فسبحه» حقيقة أيضا «وإدبار النجوم» مصدر، أي عقب غروبها سبحه أيضا، أو صلّ في الأول العشاءين، وفي الثاني الفجر وقيل الصبح.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, '«والنَّجم» الثريا «إذا هوى» غاب.'),
(4786, 53, 2, '«ما ضل صاحبكم» محمد عليه الصلاة والسلام عن طريق الهداية «وما غوى» ما لابس الغي وهو جهل من اعتقاد فاسد.'),
(4787, 53, 3, '«وما ينطق» بما يأتيكم به «عن الهوى» هوى نفسه.'),
(4788, 53, 4, '«إن» ما «هو إلا وحي يوحى» إليه.'),
(4789, 53, 5, '«علمه» إياه ملك «شديد القوى».'),
(4790, 53, 6, '«ذو مرة» قوة وشدة أو منظر حسن، أي جبريل عليه السلام «فاستوى» استقر.'),
(4791, 53, 7, '«وهو بالأفق الأعلى» أفق الشمس، أي عند مطلعها على صورته التي خلق عليها فرآه النبي صلى الله عليه وسلم وكان بحراء قد سد الأفق إلى المغرب فخر مغشيا عليه وكان قد سأله أن يريه نفسه على صورته التي خلق عليها فواعده بحراء فنزل جبريل له في سورة الآدميين.'),
(4792, 53, 8, '«ثم دنا» قرب منه «فتدلى» زاد في القرب.'),
(4793, 53, 9, '«فكان» منه «قاب» قدر «قوسين أو أدنى» من ذلك حتى أفاق وسكن روعه.'),
(4794, 53, 10, '«فأوحى» تعالى «إلى عبده» جبريل «ما أوحى» جبريل إلى النبي صلى الله عليه وسلم ولم يذكر الموحي تفخيما لشأنه.'),
(4795, 53, 11, '«ما كذب» بالتخفيف والتشديد أنكر «الفؤاد» فؤاد النبي «ما رأى» ببصره من صورة جبريل.'),
(4796, 53, 12, '«أفتمارونه» تجادلونه وتغلبونه «على ما يرى» خطاب للمشركين المنكرين رؤية النبي صلى الله عليه وسلم لجبريل.'),
(4797, 53, 13, '«ولقد رآه» على صورته «نزلةً» مرة «أخرى».'),
(4798, 53, 14, '«عند سدرة المنتهى» لما أسري به في السماوات، وهي شجرة نبق عن يمين العرش لا يتجاوزها أحد من الملائكة وغيرهم.'),
(4799, 53, 15, '«عندها جنة المأوى» تأوي إليها الملائكة وأرواح الشهداء والمتقين.'),
(4800, 53, 16, '«إذ» حين «يغشى السدرة ما يغشى» من طير وغيره، وإذ معمولة لرآه.'),
(4801, 53, 17, '«ما زاغ البصر» من النبي صلى الله عليه وسلم «وما طغى» أي ما مال بصره عن مرئيه المقصود له ولا جاوزه تلك الليلة.'),
(4802, 53, 18, '«لقد رأى» فيها «من آيات ربه الكبرى» العظام، أي بعضها فرأى من عجائب الملكوت رفرفا أخضر سد أفق السماء وجبريل له ستمائة جناح.'),
(4803, 53, 19, '«أفرأيتم اللات والعزى».'),
(4804, 53, 20, '«ومناة الثالثة» للتين قبلها «الأخرى» صفة ذم للثالثة وهي أصنام من حجارة كان المشركون يعبدونها ويزعمون أنها تشفع لهم عند الله، ومفعول أفرأيتم الأول اللات وما عطف عليه والثاني محذوف والمعنى أخبروني ألهذه الأصنام قدرة على شيء ما فتعبدونها دون الله القادر على ما تقدم ذكره، ولما زعموا أيضا أن الملائكة بنات الله مع كراهتهم البنات نزلت.'),
(4805, 53, 21, '«ألكم الذكر وله الأنثى».'),
(4806, 53, 22, '«تلك إذا قسمة ضيزى» جائزة من ضازه يضيزه إذا ظلمه وجار عليه.'),
(4807, 53, 23, '«إن هي» أي ما المذكورات «إلا أسماء سميتموها» أي سميتم بها «أنتم وآباؤكم» أصناما تعبدونها «ما أنزل الله بها» أي بعبادتها «من سلطان» حجة وبرهان «إن» ما «يتبعون» في عبادتها «إلا الظن وما تهوى الأنفس» مما زين لهم الشيطان من أنها تشفع لهم عند الله تعالى «ولقد جاءهم من ربهم الهدى» على لسان النبي صلى الله عليه وسلم بالبرهان القاطع فلم يرجعوا عما هم عليه.'),
(4808, 53, 24, '«أم للإنسان» أي لكل إنسان منهم «ما تمنى» من أن الأصنام تشفع لهم؟ ليس الأمر كذلك.'),
(4809, 53, 25, '«فلله الآخرة والأولى» أي الدنيا فلا يقع فيهما إلا ما يريده تعالى.'),
(4810, 53, 26, '(وكم من ملك) أي وكثير من الملائكة (في السماوات) وما أكرمهم عند الله (لا تغني شفاعتهم شيئا إلا من بعد أن يأذن الله) لهم فيها (لمن يشاء) من عباده (ويرضى) عنه لقوله "" ولا يشفعون إلا لمن ارتضى "" ومعلوم أنها لا توجد منهم إلا بعد الإذن فيها "" من ذا الذي يشفع عنده إلا بإذنه ".'),
(4811, 53, 27, '«إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثى» حيث قالوا: هم بنات الله.'),
(4812, 53, 28, '«وما لهم به» بهذا القول «من علم إن» ما «يتبعون» فيه «إلا الظن» الذي تخيلوه «وإن الظن لا يغني من الحق شيئا» أي عن العلم فيما المطلوب فيه العلم.'),
(4813, 53, 29, '«فأعرض عن من تولى عن ذكرنا» أي القرآن «ولم يُرد إلا الحياة الدنيا» وهذا قبل الأمر بالجهاد.'),
(4814, 53, 30, '«ذلك» أي طلب الدنيا «مبلغهم من العلم» أي نهاية علمهم أن آثروا الدنيا على الآخرة «إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بمن اهتدى» عالم بهما فيجازيهما.'),
(4815, 53, 31, '«ولله ما في السماوات وما في الأرض» هو مالك لذلك، ومنه الضال والمهتدي يُضل من يشاء ويهدي من يشاء «ليجزي الذين أساءوا بما عملوا» من الشرك وغيره «ويجزي الذين أحسنوا» بالتوحيد وغيره من الطاعات «بالحسنى» الجنة وبيَّن المحسنين بقوله.'),
(4816, 53, 32, '«الذين يجتنبون كبائر الإثم والفواحش إلا الَّلمَمَ» هو صغار الذنوب كالنظرة والقبلة واللمسة فهو استثناء منقطع والمعنى لكن اللمم يغفر باجتناب الكبائر «إن ربك واسع المغفرة» بذلك وبقبول التوبة، ونزل فيمن كان يقول: صلاتنا صيامنا حجنا: «هو أعلم» أي عالم «بكم إذ أنشأكم من الأرض» أي خلق آباكم آدم من التراب «وإذ أنتم أجنة» جمع جنين «في بطون أمهاتكم فلا تزكوا أنفسكم» لا تمدحوها على سبيل الإعجاب أما على سبيل الاعتراف بالنعمة فحسن «هو أعلم» أي عالم «بمن اتقى».'),
(4817, 53, 33, '«أفرأيت الذي تولى» عن الإيمان ارتد لما عير به وقال إني خشيت عقاب الله فضمن له المعير له أن يحمل عنه عذاب الله إن رجع إلى شركه وأعطاه من ماله كذا فرجع.'),
(4818, 53, 34, '«وأعطى قليلا» من المال المسمى «وأكدى» منع الباقي مأخوذ من الكدية وهي أرض صلبة كالصخرة تمنع حافر البئر إذا وصل إليها من الحفر.'),
(4819, 53, 35, '«أعنده علم الغيب فهو يرى» يعلم جملته أن غيره يتحمل عنه عذاب الآخرة؟ لا، وهو الوليد بن المغيرة أو غيره، وجملة أعنده المفعول الثاني لرأيت بمعنى أخبرني.'),
(4820, 53, 36, '«أم» بل «لم ينبأ بما في صحف موسى» أسفار التوراة أو صحف قبلها.'),
(4821, 53, 37, '(و) صحف (إبراهيم الذي وفى) تمم ما أمر به نحو "" وإذ ابتلى إبراهيم ربه بكلمات فأتمهن "" وبيان ما:'),
(4822, 53, 38, '«أ» ن «لا تزر وازرة وزر أخرى» إلخ وأن مخففة من الثقيلة، أي لا تحمل نفس ذنب غيرها.'),
(4823, 53, 39, '«وأن» بأنه «ليس للإنسان إلا ما سعى» من خير فليس له من سعي غيره الخير شيء.'),
(4824, 53, 40, '«وأن سعيه سوف يُرى» يبصر في الآخرة.'),
(4825, 53, 41, '«ثم يجزاه الجزاء الأوفى» الأكمل يقال: جزيته سعيه وبسعيه.'),
(4826, 53, 42, '«وأن» بالفتح عطفا وقرئ بالكسر استئنافا وكذا ما بعدها فلا يكون مضمون الجمل في الصحف على الثاني «إلى ربك المنتهى» المرجع والمصير بعد الموت فيجازيهم.'),
(4827, 53, 43, '«وأنه هو أضحك» من شاء أفرحه «وأبكى» من شاء أحزنه.'),
(4828, 53, 44, '«وأنه هو أمات» في الدنيا «وأحيا» للبعث.'),
(4829, 53, 45, '«وأنه خلق الزوجين» الصنفين «الذكر والأنثى».'),
(4830, 53, 46, '«من نطفة» منيٍّ «إذا تُمنى» تصب في الرحم.'),
(4831, 53, 47, '«وأن عليه النَّشآءَةَ» بالمد والقصر «الأخرى» الخلقة الأخرى للبعث بعد الخلقة الأولى.'),
(4832, 53, 48, '«وأنه هو أغنى» الناس بالكفاية بالأموال «وأقنى» أعطى المال المتخذ قُنية.'),
(4833, 53, 49, '«وأنه هو رب الشعرى» هي كوكب خلف الجوزاء كانت تعبد في الجاهلية.'),
(4834, 53, 50, '«وأنه أهلك عادا الأولى» وفي قراءة بإدغام التنوين في اللام وضمها بلا همزة وهي قوم عاد والأخرى قوم صالح.'),
(4835, 53, 51, '«وثمودا» بالصرف اسم للأب وبلا صرف للقبيلة وهو معطوف على عادا «فما أبقى» منهم أحدا.'),
(4836, 53, 52, '(وقوم نوح من قبل) أي قبل عاد وثمود أهلكناهم (إنهم كانوا هم أظلم وأطغى) من عاد وثمود لطول لبث نوح فيهم "" فلبث فيهم ألف سنة إلا خمسين عاما "" وهم مع عدم إيمانهم به يؤذونه ويضربونه.'),
(4837, 53, 53, '«والمؤتفكة» وهي قرى قوم لوط «أهوى» أسقطها بعد رفعها إلى السماء مقلوبة إلى الأرض بأمره جبريل بذلك.'),
(4838, 53, 54, '«فغشاها» من الحجارة بعد ذلك «ما غشى» أبْهم تهويلا، وفي هود: (جعلنا عاليها سافلها وأمطرنا عليها حجارة من سجيل).'),
(4839, 53, 55, '«فبأي آلاء ربك» أنعمه الدالة على وحدانيته وقدرته «تتمارى» تتشكك أيها الإنسان أو تكذب.'),
(4840, 53, 56, '«هذا» محمد «نذير من النذر الأولى» من جنسهم، أي رسول كالرسل قبله أرسل إليكم كما أرسلوا إلى أقوامهم.'),
(4841, 53, 57, '«أزفت الآزفة» قربت القيامة.'),
(4842, 53, 58, '«ليس لها من دون الله» نفس «كاشفة» أي لا يكشفها ويظهرها إلا هو كقوله «لا يجليها لوقتها إلا هو».'),
(4843, 53, 59, '«أفمن هذا الحديث» أي القرآن «تعجبون» تكذيبا.'),
(4844, 53, 60, '«وتضحكون» استهزاءً «ولا تبكون» لسماع وعده ووعيده.'),
(4845, 53, 61, '«وأنتم سامدون» لاهون غافلون عما يطلب منكم.'),
(4846, 53, 62, '«فاسجدوا لله» الذي خلقكم «واعبدوا» ولا تسجدوا للأصنام ولا تعبدوها.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, '«اقتربت الساعة» قربت القيامة «وانشق القمر» انفلق فلقتين على أبي قبيس وقيقعان آية له صلى الله عليه وسلم وقد سئلها فقال (اشهدوا) رواه الشيخان.'),
(4848, 54, 2, '«وإن يروْا» أي كفار قريش «آية» معجزة له صلى الله عليه وسلم «يعرضوا ويقولوا» هذا «سحر مستمر» قوي من المرة: القوة أو دائم.'),
(4849, 54, 3, '«وكذبوا» النبي صلى الله عليه وسلم «واتبعوا أهواءهم» في الباطل «وكل أمر» من الخير والشر «مستقر» بأهله في الجنة أو النار.'),
(4850, 54, 4, '«ولقد جاءهم من الأنباء» أخبار إهلاك الأمم المكذبة رسلهم «ما فيه مزدجر» لهم اسم مكان والدال بدل من تاء الافتعال وازدجرته وزجرته: نهيته بغلظة وما موصولة أو موصوفة.'),
(4851, 54, 5, '«حكمة» خبر مبتدأ محذوف أو بدل من ما أو من مزدجر «بالغة» تامة «فما تغن» تنفع فيهم «النذر» جمع نذير بمعنى منذر، أي الأمور المنذورة لهم وما للنفي أو للاستفهام الإنكاري وهي على الثاني مفعول مقدم.'),
(4852, 54, 6, '«فتول عنهم» هو فائدة ما قبله وتم به الكلام «يوم يدع الداع» هو إسرافيل وناصب يوم يخرجون بعد «إلى شيءٍ نكُر» بضم الكاف وسكونها، أي منكر تنكره النفوس وهو الحساب.'),
(4853, 54, 7, '«خاشعا» أي ذليلا، وفي قراءة خُشَّعا بضم الخاء وفتح الشين مشددة «أبصارهم» حال من الفاعل «يخرجون» أي الناس «من الأجداث» القبور «كأنهم جراد منتشر» لا يدرون أين يذهبون من الخوف والحيرة، والجملة حال من فاعل يخرجون وكذا قوله.'),
(4854, 54, 8, '«مهطعين» مسرعين مادين أعناقهم «إلى الداعِ يقول الكافرون» منهم «هذا يوم عَسِرٌ» صعب على الكافرين كما في المدثر (يوم عسير على الكافرين).'),
(4855, 54, 9, '«كذَّبت قبلهم» قبل قريش «قوم نوح» تأنيث الفعل لمعنى قوم «فكذبوا عبدنا» نوحا «وقالوا مجنون وازدجر» انتهروه بالسب وغيره.'),
(4856, 54, 10, '«فدعا ربه أني» بالفتح، أي بأني «مغلوب فانتصر».'),
(4857, 54, 11, '«ففتحنا» بالتخفيف والتشديد «أبواب السماء بماءٍ منهمر» منصب انصبابا شديدا.'),
(4858, 54, 12, '«وفجرنا الأرض عيونا» تنبع «فالتقى الماء» ماء السماء والأرض «على أمر» حال «قد قُدِر» قضي به في الأزل وهو هلاكهم غرقا.'),
(4859, 54, 13, '«وحملناه» أي نوحا «على» سفينة «ذات ألواح ودُسر» وهو ما تشد به الألواح من المسامير وغيرها وأحدها دسار ككتاب.'),
(4860, 54, 14, '«تجري بأعيننا» بمرأى منا، أي محفوظة «جزاءً» منصوب بفعل مقدر، أي أغرقوا انتصارا «لمن كان كفر» وهو نوح عليه السلام، وقرئ كفر بالبناء للفاعل، أي أغرقوا عقابا.'),
(4861, 54, 15, '«ولقد تركناها» أبقينا هذه الفعلة «آية» لمن يعتبر بها، أي شاع خبرها واستمر «فهل من مدَّكر» معتبر ومتعظ بها وأصله مذتكر أبدلت التاء دالا مهملة وكذا المعجمة وأدغمت فيها.'),
(4862, 54, 16, '«فكيف كان عذابي ونُذُر» أي إنذاري استفهام تقرير، وكيف خبر كان وهي للسؤال عن الحال والمعنى حمل المخاطبين على الإقرار بوقوع عذابه تعالى بالمكذبين لنوح موقعه.'),
(4863, 54, 17, '«ولقد يسرنا القرآن للذكر» سهلناه للحفظ وهيأناه للتذكر «فهل من مدكر» متعظ به وحافظ له والاستفهام بمعنى الأمر، أي احفظوا واتعظوا به وليس يحفظ من كتب الله عن ظهر القلب غيره.'),
(4864, 54, 18, '«كذبت عاد» نبيهم هودا فعذبوا «فكيف كان عذابي ونذر» إنذاري لهم بالعذاب قبل نزوله أي وقع موقعه وقد بينه بقوله.'),
(4865, 54, 19, '«إنا أرسلنا عليهم ريحا صرْصرا» أي شديدة الصوت «في يوم نحس» شؤم «مستمر» دائم الشؤم أو قويه وكان يوم الأربعاء آخر الشهر.'),
(4866, 54, 20, '«تنزع الناس» تقلعهم من حفر الأرض المندسين فيها وتصرعهم على رءوسهم فتدق رقابهم فتبين الرأس عن الجسد «كأنهم» وحالهم ما ذكر «أعجاز» أصول «نخل منقعر» منقطع ساقط على الأرض وشبهوا بالنخل لطولهم وذكر هنا وأنث في الحاقة (نخل خاوية) مراعاة للفواصل في الموضعين.'),
(4867, 54, 21, '«فكيف كان عذابي ونذر».'),
(4868, 54, 22, '«ولقد يسرنا القرآن للذكر فهل من مدكر».'),
(4869, 54, 23, '«كذبت ثمود بالنذر» جمع نذير بمعنى منذر، أي بالأمور التي أنذرهم بها نبيهم صالح إن لم يؤمنوا به ويتبعوه.'),
(4870, 54, 24, '«فقالوا أبشرا» منصوب على الاشتغال «منا واحدا» صفتان لـ بشرا «نتبعه» مفسر للفعل الناصب له والاستفهام بمعنى النفي المعنى كيف نتبعه ونحن جماعة كثيرة وهو واحد منا وليس بملك، أي لا نتبعه «إنا إذًا» إن اتبعناه «لفي ضلال» ذهاب عن الصواب «وسعر» جنون.'),
(4871, 54, 25, '«أألقي» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما على الوجهين وتركه «الذكر» الوحي «عليه من بيننا» أي لم يوح إليه «بل هو كذاب» في قوله إنه أوحي إليه ما ذكر «أشر» متكبر بطر، قال تعالى.'),
(4872, 54, 26, '«سيعلمون غدا» في الآخرة «من الكذاب الأشر» وهو أو هم بأن يعذبوا على تكذيبهم نبيهم صالحا.'),
(4873, 54, 27, '«إنا مرسلو الناقة» مخرجوها من الهضبة الصخرة كما سألوا «فتنة» محنة «لهم» لنخبرهم «فارتقبهم» يا صالح أي انتظر ما هم صانعون وما يصنع بهم «واصطبر» الطاء بدل من تاء الافتعال أي اصبر على أذاهم.'),
(4874, 54, 28, '«ونبئهم أن الماء قسمة» مقسوم «بينهم» وبين الناقة يوم لهم ويوم لها «كلُّ شرب» نصيب من الماء «محتضر» يحضره القوم يومهم والناقة يومها فتمادوا على ذلك ثم ملوه فهموا بقتل الناقة.'),
(4875, 54, 29, '«فنادوا صاحبهم» قدرا ليقتلها «فتعاطى» تناول السيف «فعقر» به الناقة، أي قتلها موافقة لهم.'),
(4876, 54, 30, '«فكيف كان عذابي ونذر» إنذاري لهم بالعذاب قبل نزوله، أي وقع موقعه وبيَّنه بقوله.'),
(4877, 54, 31, '«إنا أرسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر» هو الذي يجعل لغنمه حظيرة من يابس الشجر والشوك يحفظهن فيها من الذئاب والسباع وما سقط من ذلك فداسته هو الهشيم.'),
(4878, 54, 32, '«ولقد يسرنا القرآن للذكر فهل من مدكر».'),
(4879, 54, 33, '«كذبت قوم لوط بالنذر» بالأمور المنذرة لهم على لسانه.'),
(4880, 54, 34, '«إنا أرسلنا عليهم حاصبا» ريحا ترميهم بالحصباء وهي صغار الحجارة الواحد دون ملء الكف فهلكوا «إلا آل لوط» وهم ابنتاه معه «نجيناهم بسحر» من الأسحار وقت الصبح من يوم غير معين ولو أريد من يوم معين لمنع من الصرف لأنه معرفة معدول عن السحر لأن حقه أن يستعمل في المعرفة بأل وهل أرسل الحاصب على آل لوط أولا؟ قولان وعبر عن الاستثناء على الأول بأنه متصل وعلى الثاني بأنه منقطع وإن كان من الجنس تسمحا.'),
(4881, 54, 35, '«نعمة» مصدر، أي إنعاما «من عندنا كذلك» أي مثل ذلك الجزاء «نجزي من شكر» أنعمنا وهو مؤمن أو من آمن بالله ورسوله وأطاعهما.'),
(4882, 54, 36, '«ولقد أنذرهم» خوفهم لوط «بطشتنا» أخذتنا إياهم بالعذاب «فتماروا» تجادلوا وكذبوا «بالنذر» بإنذاره.'),
(4883, 54, 37, '«ولقد راودوه عن ضيفه» أن يخلي بينهم وبين القوم الذين أتوه في صورة الأضياف ليخبثوا بهم وكانوا ملائكة «فطمسنا أعينهم» أعميناها وجعلناها بلا شق كباقي الوجه بأن صفقها جبريل بجناحه «فذوقوا» فقلنا لهم ذوقوا «عذابي ونذر» إنذاري وتخويفي، أي ثمرته وفائدته.'),
(4884, 54, 38, '«ولقد صبحهم بكرة» وقت الصبح من يوم غير معين «عذاب مستقر» دائم متصل بعذاب الآخرة.'),
(4885, 54, 39, '«فذوقوا عذابي ونذر».'),
(4886, 54, 40, '«ولقد يسرنا القرآن للذكر فهل مدكر».'),
(4887, 54, 41, '«ولقد جاء آل فرعون» قومه معه «النذر» الإنذار على لسان موسى وهارون فلم يؤمنوا بل'),
(4888, 54, 42, '«كذبوا بآياتنا كلها» التسع التي أوتيها موسى «فأخذناهم» بالعذاب «أخذ عزيز» قوي «مقتدر» قادر لا يعجزه شيء.'),
(4889, 54, 43, '«أكفاركم» يا قريش «خير من أولئكم» المذكورين من قوم نوح إلى فرعون فلم يعذروا «أم لكم» يا كفار قريش «براءة» من العذاب «في الزبر» الكتب والاستفهام في الموضعين بمعنى النفي أي ليس الأمر كذلك.'),
(4890, 54, 44, '«أم يقولون» أي كفار قريش «نحن جميع» جمع «منتصر» على محمد، ولما قال أبو جهل يوم بدر إنا جمع منتصر نزل.'),
(4891, 54, 45, '«سيهزم الجمع ويولون الدبر» فهزموا ببدر ونصر رسول الله صلى الله عليه وسلم عليهم.'),
(4892, 54, 46, '«بل الساعة موعدهم» بالعذاب «والساعة» أي عذابها «أدهى» أعظم بلية «وأمر» أشد مرارة عذاب الدنيا.'),
(4893, 54, 47, '«إن المجرمين في ضلال» هلاك بالقتل في الدنيا «وسعر» نار مستعرة بالتشديد أي مهيجة في الآخرة.'),
(4894, 54, 48, '«يوم يسحبون في النار على وجوههم» في الآخرة ويقال لهم «ذوقوا مس سقر» إصابة جهنم لكم.'),
(4895, 54, 49, '«إنا كل شيء» منصوب بفعل يفسره «خلقناه بقدر» بتقدير حال من كل أي مقدرا وقرئ كل بالرفع مبتدأ خبره خلقناه.'),
(4896, 54, 50, '«وما أمرنا» لشيء نريد وجوده «إلا» مرة «واحدة كلمح بالبصر» في السرعة وهي قول: كن فيوجد (إنما أمره إذا أراد شيئا أن يقول له كن فيكون).'),
(4897, 54, 51, '«ولقد أهلكنا أشياعكم» أشباهكم في الكفر من الأمم الماضية «فهل من مدكر» استفهام بمعنى الأمر، أي اذكروا واتعظوا.'),
(4898, 54, 52, '«وكل شيء فعلوه» أي العباد مكتوب «في الزبر» كتب الحفظة.'),
(4899, 54, 53, '«وكل صغير وكبير» من الذنب أو العمل «مستطر» مكتوب في اللوح المحفوظ.'),
(4900, 54, 54, '«إن المتقين في جنات» بساتين «ونَهر» أريد به الجنس، وقرئ بضم النون والهاء جمعا كأسد وأسد، والمعنى أنهم يشربون من أنهار الماء واللبن والعسل والخمر.'),
(4901, 54, 55, '«في مقعد صدق» مجلس حق لا لغو فيه ولا تأثيم أريد به الجنس، وقرئ مقاعد، المعنى أنهم في مجالس من الجنات سالمة من اللغو والتأثيم بخلاف مجالس الدنيا فقلَّ أن تسلم من ذلك وأعرب هذا خبرا ثانيا وبدلا وهو صادق ببدل البعض وغيره «عند مليك» مثال مبالغة، أي عزيز الملك واسعه «مقتدر» قادر لا يعجزه شيء وهو الله تعالى وفيه إشارة إلى الرتبة والقربة من فضله تعالى.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '«الرحمن» الله تعالى.'),
(4903, 55, 2, '«علَّم» من شاء «القرآن».'),
(4904, 55, 3, '«خلق الإنسان» أي الجنس.'),
(4905, 55, 4, '«علَّمه البيان» النطق.'),
(4906, 55, 5, '«الشمس والقمر بحسبان» يجريان.'),
(4907, 55, 6, '«والنجم» ما لا ساق له من النبات «والشجر» ما له ساق «يسجدان» يخضعان لما يراد منهما.'),
(4908, 55, 7, '«والسماء رفعها ووضع الميزان» أثبت العدل.'),
(4909, 55, 8, '«ألا تطغوا» أي لأجل أن لا تجوروا «في الميزان» ما يوزن به.'),
(4910, 55, 9, '«وأقيموا الوزن بالقسط» بالعدل «ولا تخسروا الميزان» تنقصوا الموزون.'),
(4911, 55, 10, '«والأرض وضعها» أثبتها «للأنام» للخلق الإنس والجن وغيرهم.'),
(4912, 55, 11, '«فيها فاكهة والنخل» المعهود «ذات الأكمام» أوعية طلعها.'),
(4913, 55, 12, '«والحب» كالحنطة والشعير «ذو العصف» التين «والريحان» الورق المشموم.'),
(4914, 55, 13, '(فبأي آلاء) نعم (ربكما) أيها الإنس والجن (تكذبان) ذكرت إحدى وثلاثين مرة، والاستفهام فيها للتقرير لما روي الحاكم عن جابر قال: "" قرأ علينا رسول الله صلى الله عليه وسلم سورة الرحمن حتى ختمها، ثم قال: مالي أراكم سكوتا، لَلْجن كانوا أحسن منكم ردا ما قرأت عليهم هذه الآية من مرة (فبأي آلاء ربكما تكذبان) إلا قالوا: ولا بشيء من نعمك ربنا نكذب فلك الحمد "".'),
(4915, 55, 14, '«خلق الإنسان» آدم «من صلصال» طين يابس يسمع له صلصلة، أي صوت إذا نقر «كالفخار» وهو ما طبخ من الطين.'),
(4916, 55, 15, '«وخلق الجان» أبا الجن وهو إبليس «من مارج من نار» هو لهبها الخالص من الدخان.'),
(4917, 55, 16, '«فبأي آلاء ربكما تكذبان».'),
(4918, 55, 17, '«رب المشرقين» مشرق الشتاء ومشرق الصيف «ورب المغربين» كذلك.'),
(4919, 55, 18, '«فبأي آلاء ربكما تكذبان».'),
(4920, 55, 19, '«مرج» أرسل «البحرين» العذب والملح «يلتقيان» في رأي العين.'),
(4921, 55, 20, '«بينهما برزخ» حاجز من قدرته تعالى «لا يبغيان» لا يبغي واحد منهما على الآخر فيختلط به.'),
(4922, 55, 21, '«فبأي آلاء ربكما تكذبان».'),
(4923, 55, 22, '«يَُخرج» بالبناء للمفعول والفاعل «منهما» من مجموعهما الصادق بأحدهما وهو الملح «اللؤلؤ المرجان» خرز أحمر أو صغار اللؤلؤ.'),
(4924, 55, 23, '«فبأي آلاء ربكما تكذبان».'),
(4925, 55, 24, '«وله الجوار» السفن «المنشآت» المحدثات «في البحر كالأعلام» كالجبال عظما وارتفاعا.'),
(4926, 55, 25, '«فبأي آلاء تكذبان».'),
(4927, 55, 26, '«كل من عليها» أي الأرض من الحيوان «فان» هالك وعبر بمن تغليبا للعقلاء.'),
(4928, 55, 27, '«ويبقى وجه ربك» ذاته «ذو الجلال» العظمة «والإكرام» للمؤمنين بأنعمه عليهم.'),
(4929, 55, 28, '«فبأي آلاء ربكما تكذبان».'),
(4930, 55, 29, '«يسأله من في السماوات والأرض» بنطق أو حال: ما يحتاجون إليه من القوة على العبادة والرزق والمغفرة وغير ذلك «كل يوم» وقت «هو في شأن» أمر يُظهره على وفق ما قدره في الأزل من إحياء و إماتة وإعزاز وإذلال وإغناء وإعدام وإجابة داع وإعطاء سائل وغير ذلك.'),
(4931, 55, 30, '«فبأي آلاء ربكما تكذبان».'),
(4932, 55, 31, '«سنفرغ لكم» سنقصد لحسابكم «أيها الثقلان» الإنس والجن.'),
(4933, 55, 32, '«فبأي آلاء ربكما تكذبان».'),
(4934, 55, 33, '«يا معشر الجن والإنس إن استطعتم أن تنفذوا» تخرجوا «من أقطار» نواحي «السماوات والأرض فانفذوا» أمر تعجيز «لا تنفذون إلا بسلطان» بقوة ولا قوة لكم على ذلك.'),
(4935, 55, 34, '«فبأي آلاء ربكما تكذبان».'),
(4936, 55, 35, '«يرسل عليكما شواظ من نار» هو لهبها الخالص من الدخان أو معه «ونحاس» أي دخان لا لهب فيه «فلا تنتصران» تمتنعان من ذلك بل يسوقكم إلى المحشر.'),
(4937, 55, 36, '«فبأي آلاء ربكما تكذبان».'),
(4938, 55, 37, '«فإذا انشقت السماء» انفرجت أبوابا لنزول الملائكة «فكانت وردة» أي مثلها محمرة «كالدهان» كالأديم الأحمر على خلاف العهد بها وجواب إذا فما أعظم الهول.'),
(4939, 55, 38, '«فبأي آلاء ربكما تكذبان».'),
(4940, 55, 39, '«فيومئذ لا يُسأل عن ذنبه إنس ولا جان» عن ذنبه ويُسألون في وقت آخر (فوربك لنسألنَّهم أجمعين) والجان هنا وفيما سيأتي بمعنى الجن والإنس فيهما بمعنى الإنسي.'),
(4941, 55, 40, '«فبأي آلاء ربكما تكذبان».'),
(4942, 55, 41, '«يعرف المجرمون بسيماهم» سواد الوجوه وزرقة العيون «فيؤخذ بالنواصي الأقدام».'),
(4943, 55, 42, '«فبأي آلاء ربكما تكذبان» تضم ناصية كل منهم إلى قدميه من خلف أو قدام ويلقى في النار ويقال لهم.'),
(4944, 55, 43, '«هذه جهنم التي يكذب بها المجرمون».'),
(4945, 55, 44, '«يطوفون» يسعون «بينها وبين حميم» ماء حار «آن» شديد الحرارة يسقونه إذا استغاثوا من حر النار، وهو منقوص كقاض.'),
(4946, 55, 45, '«فبأي آلاء ربكما تكذبان».'),
(4947, 55, 46, '«ولمن خاف» أي لكل منهم أو لمجموعهم «مقام ربه» قيامه بين يديه للحساب فترك معصيته «جنتان».'),
(4948, 55, 47, '«فبأي آلاء ربكما تكذبان».'),
(4949, 55, 48, '«ذواتا» تثنية ذوات على الأصل ولامها ياء «أفنان» أغصان جمع فنن كطلل.'),
(4950, 55, 49, '«فبأي آلاء ربكما تكذبان».'),
(4951, 55, 50, '«فيهما عينان تجريان».'),
(4952, 55, 51, '«فبأي آلاء ربكما تكذبان».'),
(4953, 55, 52, '«فيهما من كل فاكهة» في الدنيا أو كل ما يتفكه به «زوجان» نوعان رطب ويابس والمر منهما في الدنيا كالحنظل حلو.'),
(4954, 55, 53, '«فبأي آلاء ربكما تكذبان».'),
(4955, 55, 54, '«متكئين» حال عامله محذوف، أي يتنعمون «على فرش بطائنها من إستبرق» ما غلظ من الديباج وخشن والظهائر من السندس «وجنى الجنتين» ثمرهما «دان» قريب يناله القائم والقاعد والمضطجع.'),
(4956, 55, 55, '«فبأي آلاء ربكما تكذبان».'),
(4957, 55, 56, '«فيهن» في الجنتين وما اشتملتا عليه من العلالي والقصور «قاصرات الطرف» العين على أزواجهن المتكئين من الإنس والجن «لم يطمثهن» يفتضهن وهن من الحور أو من نساء الدنيا المنشآت «إنس قبلهم ولا جان».'),
(4958, 55, 57, '«فبأي آلاء ربكما تكذبان».'),
(4959, 55, 58, '«كأنهن الياقوت» صفاء «والمرجان» اللؤلؤ بياضا.'),
(4960, 55, 59, '«فبأي آلاء ربكما تكذبان».'),
(4961, 55, 60, '«هل» ما «جزاء الإحسان» بالطاعة «إلا الإحسان» بالنعيم.'),
(4962, 55, 61, '«فبأي آلاء ربكما تكذبان».'),
(4963, 55, 62, '«ومن دونهما» الجنتين المذكورتين «جنتان» أيضا لمن خاف مقام ربه.'),
(4964, 55, 63, '«فبأي آلاء ربكما تكذبان».'),
(4965, 55, 64, '«مدهامتان» سوداوان من شدة خضرتهما.'),
(4966, 55, 65, '«فبأي آلاء ربكما تكذبان».'),
(4967, 55, 66, '«فيهما عينان نضاختان» فوارتان بالماء.'),
(4968, 55, 67, '«فبأي آلاء ربكما تكذبان».'),
(4969, 55, 68, '«فيهما فاكهة ونخل ورمان» هما منها وقيل من غيرها.'),
(4970, 55, 69, '«فبأي آلاء ربكما تكذبان».'),
(4971, 55, 70, '«فيهن» أي الجنتين وما فيهما «خيرات» أخلاقا «حسان» وجوها.'),
(4972, 55, 71, '«فبأي آلاء ربكما تكذبان».'),
(4973, 55, 72, '«حور» شديدات سواد العيون وبياضها «مقصورات» مستورات «في الخيام» من در مجوف مضافة إلى القصور شبيهة بالخدور.'),
(4974, 55, 73, '«فبأي آلاء ربكما تكذبان».'),
(4975, 55, 74, '«لم يطمثهن إنس قبلهم» قبل أزواجهن «ولا جان».'),
(4976, 55, 75, '«فبأي آلاء ربكما تكذبان».'),
(4977, 55, 76, '«متكئين» أي أزواجهم وإعرابه كما تقدم «على رفرف خضر» جمع رفرفة، أي بسط أو وسائد «وعبقري حسان» جمع عبقرية، أي طنافس.'),
(4978, 55, 77, '«فبأي آلاء ربكما تكذبان».'),
(4979, 55, 78, '«تبارك اسم ربك ذي الجلال والإكرام» تقدم ولفظ اسم زائد.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, '«إذا وقعت الواقعة» قامت القيامة.'),
(4981, 56, 2, '«ليس لوقعتها كاذبة» نفس تكذب بأن تنفيها كما نفتها في الدنيا.'),
(4982, 56, 3, '«خافضة رافعة» أي هي مظهرة لخفض أقوام بدخولهم النار ولرفع آخرين بدخولهم الجنة.'),
(4983, 56, 4, '«إذا رجت الأرض رجا» حركت حركة شديدة.'),
(4984, 56, 5, '«وبست الجبال بسا» فتتت.'),
(4985, 56, 6, '«فكانت هباءً» غبارا «منبثا» منتشرا، وإذا الثانية بدل من الأولى.'),
(4986, 56, 7, '«وكنتم» في القيامة «أزواجا» أصنافا «ثلاثة».'),
(4987, 56, 8, '«فأصحاب الميمنة» وهم الذين يؤتون كتبهم بإيمانهم مبتدأ خبره «ما أصحاب الميمنة» تعظيم لشأنهم بدخولهم الجنة.'),
(4988, 56, 9, '«وأصحاب المشأمة» أي الشمال بأن يؤتى كل منهم كتابه بشماله «ما أصحاب المشأمة» تحقير لشأنهم بدخولهم النار.'),
(4989, 56, 10, '«والسابقون» إلى الخير وهم الأنبياء مبتدأ «السابقون» تأكيد لتعظيم شأنهم.'),
(4990, 56, 11, '«أولئك المقربون».'),
(4991, 56, 12, '«في جنات النعيم».'),
(4992, 56, 13, '«ثلة من الأولين» مبتدأ، أي جماعة من الأمم الماضية.'),
(4993, 56, 14, '«وقليل من الآخرين» من أمة محمد صلى الله عليه وسلم وهم السابقون من الأمم الماضية وهذه الأمة والخبر.'),
(4994, 56, 15, '«على سرر موضونة» منسوجة بقضبان الذهب والجواهر.'),
(4995, 56, 16, '«متكئين عليها متقابلين» حالان من الضمير في الخبر.'),
(4996, 56, 17, '«يطوف عليهم» للخدمة «ولدان مخلدون» على شكل الأولاد لا يهرمون.'),
(4997, 56, 18, '«بأكواب» أقداح لا عرى لها «وأباريق» لها عرى وخراطيم «وكأس» إناء شرب الخمر «من معين» أي خمر جارية من منبع لا ينقطع أبدا.'),
(4998, 56, 19, '«لا يصدعون عنها ولا ينزَِفون» بفتح الزاي وكسرها من نزف الشارب وأنزف، أي لا يحصل لهم منها صداع ولا ذهاب عقل بخلاف خمر الدنيا.'),
(4999, 56, 20, '«وفاكهة مما يتخيرون».'),
(5000, 56, 21, '«ولحم طير مما يشتهون و» لهم للاستمتاع.'),
(5001, 56, 22, '«وحور» نساء شديدات سواد العيون وبياضها «عين» ضخام العيون كسرت عينه بدل ضمها لمجانسة الياء ومفرده عيناء كحمراء وفي قراءة بجر حور عين.'),
(5002, 56, 23, '«كأمثال اللؤلؤ المكنون» المصون.'),
(5003, 56, 24, '«جزاءً» مفعول له أو مصدر والعامل المقدر أي جعلنا لهم ما ذكر للجزاء أو جزيناهم «بما كانوا يعملون».'),
(5004, 56, 25, '«لا يسمعون فيها» في الجنة «لغوا» فاحشا من الكلام «ولا تأثيما» ما يؤثم.'),
(5005, 56, 26, '«إلا» لكن «قيلا» قولا «سلاما» سلاما بدل من قيلا فإنهم يسمعونه.'),
(5006, 56, 27, '«وأصحاب اليمين ما أصحاب اليمين».'),
(5007, 56, 28, '«في سدر» شجر النبق «مخضود» لا شوك فيه.'),
(5008, 56, 29, '«وطلح» شجر الموز «منضود» بالحمل من أسفله إلى أعلاه.'),
(5009, 56, 30, '«وظل ممدود» دائم.'),
(5010, 56, 31, '«وماء مسكوب» جار دائما.'),
(5011, 56, 32, '«وفاكهة كثيرة».'),
(5012, 56, 33, '«لا مقطوعة» في زمن «ولا ممنوعة» بثمن.'),
(5013, 56, 34, '«وفرش مرفوعة» على السرر.'),
(5014, 56, 35, '«إنا أنشأناهن إنشاءً» أي الحور العين من غير ولادة.'),
(5015, 56, 36, '«فجعلناهن أبكارا» عذارى كلما أتاهن أزواجهن وجدوهن عذارى ولا وجع.'),
(5016, 56, 37, '«عرُْبا» بضم الراء وسكونها جمع عروب وهي المتحببة إلى زوجها عشقا له «أترابا» جمع ترب، أي مستويات في السن.'),
(5017, 56, 38, '«لأصحاب اليمين» صلة أنشأناهن أو جعلناهن وهم.'),
(5018, 56, 39, '«ثلة من الأولين».'),
(5019, 56, 40, '«وثلة من الآخرين».'),
(5020, 56, 41, '«وأصحاب الشمال ما أصحاب الشمال».'),
(5021, 56, 42, '«في سموم» ريح جارة من النار تنفذ في المسام «وحميم» ماء شديد الحرارة.'),
(5022, 56, 43, '«وظل من يحموم» دخان شديد السواد.'),
(5023, 56, 44, '«لا بارد» كغيره من الظلال «ولا كريم» حسن المنظر.'),
(5024, 56, 45, '«إنهم كانوا قبل ذلك» في الدنيا «مترفين» منعمين لا يتعبون في الطاعة.'),
(5025, 56, 46, '«وكانوا يصرون على الحنث» الذنب «العظيم» أي الشرك.'),
(5026, 56, 47, '«وكانوا يقولون أئذا متنا وكنا ترابا وعظاما أئنا لمبعوثون» في الهمزتين في الموضوعين التحقيق وتسهيل الثانية وإدخال ألف بينهما على الوجهين.'),
(5027, 56, 48, '«أو آباؤنا الأولون» بفتح الواو للعطف والهمزة للاستفهام وهو في ذلك وفيما قبله للاستبعاد وفي قراءة بسكون الواو عطفا بأو والمعطوف عليه محل إن واسمها.'),
(5028, 56, 49, '«قل إن الأولين والآخرين».'),
(5029, 56, 50, '«لمجموعون إلى ميقات» لوقت «يوم معلوم» أي يوم القيامة.'),
(5030, 56, 51, '«ثم إنكم أيها الضالون المكذبون».'),
(5031, 56, 52, '«لآكلون من شجر من زقوم» بيان للشجر.'),
(5032, 56, 53, '«فمالئون منها» من الشجر «البطون».'),
(5033, 56, 54, '«فشاربون عليه» أي الزقوم المأكول «من الحميم».'),
(5034, 56, 55, '«فشاربون شَُرب» بفتح الشين وضمها مصدر «الهيم» الإبل العطاش جمع هيمان للذكر وهيمى للأنثى، كعطشان وعطشى.'),
(5035, 56, 56, '«هذا نزلهم» ما أعد لهم «يوم الدين» يوم القيامة.'),
(5036, 56, 57, '«نحن خلقناكم» أوجدناكم من عدم «فلولا» هلا «تصدقون» بالبعث إذ القادر على الإنشاء قادر على الإعادة.'),
(5037, 56, 58, '«أفرأيتم ما تمنون» تريقون من المني في أرحام النساء.'),
(5038, 56, 59, '«أأنتم» بتحقيق الهمزتين وإبدال الثانية ألفا وتسهيلها وإدخال ألف بين المسهلة والأخرى وتركه في المواضع الأخرى «تخلقونه» أي المني بشرا «أم نحن الخالقون».'),
(5039, 56, 60, '«نحن قدَّرنا» بالتشديد والتخفيف «بينكم الموت وما نحن بمسبوقين» بعاجزين.'),
(5040, 56, 61, '«على» عن «أن نبدل» نجعل «أمثالكم» مكانكم «وننشئكم» نخلقكم «في مالا تعلمون» من الصور كالقردة والخنازير.'),
(5041, 56, 62, '«ولقد علمتم النّشاءَةَ الأولى» وفي قراءة بسكون الشين «فلولا تذكرون» فيه إدغام التاء الثانية في الأصل في الذال.'),
(5042, 56, 63, '«أفرأيتم ما تحرثون» تثيرون في الأرض وتلقون البذر فيها.'),
(5043, 56, 64, '«أأنتم تزرعونه» تنبتونه «أم نحن الزارعون».'),
(5044, 56, 65, '«لو نشاء لجعلناه حطاما» نباتا يابسا لا حب فيه «فظلتم» أصله ظللتم بكسر اللام حذفت تخفيفا أي أقمتم نهارا «تفكهون» حذفت منه إحدى التاءين في الأصل تعجبون من ذلك وتقولون.'),
(5045, 56, 66, '«إنا لمغرمون» نفقة زرعنا.'),
(5046, 56, 67, '«بل نحن محرومون» ممنوعون رزقنا.'),
(5047, 56, 68, '«أفرأيتم الماء الذي تشربون».'),
(5048, 56, 69, '«أأنتم أنزلتموه من المزن» السحاب جمع مزنة «أم نحن المنزلون».'),
(5049, 56, 70, '«لو نشاء جعلناه أجاجا» ملحا لا يمكن شربه «فلولا» هلا «تشكرون».'),
(5050, 56, 71, '«أفرأيتم النار التي تورون» تُخرجون من الشجر الأخضر.'),
(5051, 56, 72, '«أأنتم أنشأتم شجرتها» كالمرخ والعفار والكلخ «أم نحن المنشئون».'),
(5052, 56, 73, '«نحن جعلناها تذكرة» لنار جهنم «ومتاعا» بُلْغَة «للمقوين» للمسافرين من أقوى القوم: أي صاروا بالقوى بالقصر والمد أي القفر وهو مفازة لا نبات فيها ولا ماء.'),
(5053, 56, 74, '«فسبح» نزه «باسم» زائدة «ربك العظيم» الله.'),
(5054, 56, 75, '«فلا أقسم» لا زائدة «بمواقع النجوم» بمساقطها لغروبها.'),
(5055, 56, 76, '«وإنه» أي القسم بها «لقسم لو تعلمون عظيم» لو كنتم من ذوي العلم لعلمتم عظم هذا القسم.'),
(5056, 56, 77, '«إنه» أي المتلو عليكم «لقرآن كريم».'),
(5057, 56, 78, '«في كتاب» مكتوب «مكنون» مصون وهو المصحف.'),
(5058, 56, 79, '«لايمسّه» خبر بمعنى النهي «إلا المطهرون» الذين طهروا أنفسهم من الأحداث.'),
(5059, 56, 80, '«تنزيل» منزل «من رب العالمين».'),
(5060, 56, 81, '«أفبهذا الحديث» القرآن «أنتم مدهنون» متهاونون مكذبون.'),
(5061, 56, 82, '«وتجعلون رزقكم» من المطر، أي شكره «أنكم تكذبون» بسقيا الله حيث قلتم مطرنا بنوء كذا.'),
(5062, 56, 83, '«فلولا» فهلا «إذا بلغت» الروح وقت النزع «الحلقوم» هو مجرى الطعام.'),
(5063, 56, 84, '«وأنتم» يا حاضري الميت «حينئذ تنظرون» إليه.'),
(5064, 56, 85, '«ونحن أقرب إليه منكم» بالعلم «ولكن لا تبصرون» من البصيرة، أي لا تعلمون ذلك.'),
(5065, 56, 86, '«فلولا» فهلا «إن كنتم غير مدينين» مجزيين بأن تبعثوا، أي غير مبعوثين بزعمكم.'),
(5066, 56, 87, '«ترجعونها» تردون الروح إلى الجسد بعد بلوغ الحلقوم «إن كنتم صادقين» فيما زعمتم فلولا الثانية تأكيد للأولى وإذا ظرف لترجعون المتعلق به الشرطان والمعنى: هلا ترجعونها إن نفيتم البعث صادقين في نفيه، أي لينتفي عن محلها الموت كالبعث.'),
(5067, 56, 88, '«فأما إن كان» الميت «من المقربين».'),
(5068, 56, 89, '«فروْح» أي فله استراحة «وريحان» رزق حسن «وجنة نعيم» وهل الجواب لأما أو لإن أو لهما؟ أقوال.'),
(5069, 56, 90, '«وأما إن كان من أصحاب اليمين».'),
(5070, 56, 91, '«فسلام لك» أي له السلامة من العذاب «من أصحاب اليمين» من جهة أنه منهم.'),
(5071, 56, 92, '«وأما إن كان من المكذبين الضالين».'),
(5072, 56, 93, '(فنزل من حميم) من إضافة الموصوف إلى صفته.'),
(5073, 56, 94, '(وتصلية جحيم) من إضافة الموصوف إلى صفته.'),
(5074, 56, 95, '«إن هذا لهو حق اليقين» من إضافة الموصوف إلى صفته.'),
(5075, 56, 96, '«فسبح باسم ربك العظيم» تقدم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, '«سبَّح لله ما في السماوات والأرض» أي نزهه كل شيء فاللام مزيدة وجيء بما دون من تغليبا للأكثر «وهو العزيز» في ملكه «الحكيم» في صنعه.'),
(5077, 57, 2, '«له ملك السماوات والأرض يحيي» بالإنشاء «ويميت» بعده «وهو على كل شيء قدير».'),
(5078, 57, 3, '«هو الأول» قبل كل شيء بلا بداية «والآخر» بعد كل شيء بلا نهاية «والظاهر» بالأدلة عليه «والباطن» عن إدراك الحواس «وهو بكل شيء عليم».'),
(5079, 57, 4, '«هو الذي خلق السماوات والأرض في ستة أيام» من أيام الدنيا أولها الأحد وآخرها الجمعة «ثم استوى على العرش» الكرسي استواءً يليق به «يعلم ما يلج» يدخل «في الأرض» كالمطر والأموات «وما يخرج منها» كالنبات والمعادن «وما ينزل من السماء» كالرحمة والعذاب «وما يعرج» يصعد «فيها» كالأعمال الصالحة والسيئة «وهو معكم» بعلمه «أين ما كنتم والله بما تعملون بصير».'),
(5080, 57, 5, '«له ملك السماوات والأرض وإلى الله ترجع الأمور» الموجودات جميعها.'),
(5081, 57, 6, '«يولج الليل» يدخله «في النهار» فيزيد وينقص الليل «ويولج النهار في الليل» فيزيد وينقص النهار «وهو عليم بذات الصدور» بما فيها من الأسرار والمعتقدات.'),
(5082, 57, 7, '«آمنوا» داوموا على الإيمان «بالله ورسوله وأنفقوا» في سبيل الله «مما جعلكم مستخلفين فيه» من مال من تقدمكم وسيخلفكم فيه من بعدكم، نزل في غزوة العسرة وهي غزوة تبوك «فالذين آمنوا منكم وأنفقوا» إشارة إلى عثمان رضي الله عنه «لهم أجر كبير».'),
(5083, 57, 8, '(وما لكم لا تؤمنون) خطاب للكفار، أي لا مانع لكم من الإيمان (بالله والرسول يدعوكم لتؤمنوا بربكم وقد أخذ) بضم الهمزة وكسر الخاء وبفتحها ونصب ما بعده (ميثاقكم) عليه أي أخذه الله في عالم الذر حين أشهدهم على أنفسهم "" ألست بربكم قالوا بلى "" (إن كنتم مؤمنين) أي مريدين الإيمان به فبادروا إليه.'),
(5084, 57, 9, '«هو الذي ينزل على عبده آيات بينات» آيات القرآن «ليخرجكم من الظلمات» الكفر «إلى النور» الإيمان «وإن الله بكم» في إخراجكم من الكفر إلى الإيمان «لرؤوف رحيم».'),
(5085, 57, 10, '«ومالكم» بعد إيمانكم «ألا» فيه إدغام نون أن في لام لا «تنفقوا في سبيل الله ولله ميراث السماوات والأرض» بما فيهما فتصل إليه أموالكم من غير أجر الإنفاق بخلاف ما لو أنفقتم فتؤجرون «لا يستوي منكم من أنفق من قبل الفتح» لمكة «وقاتل أولئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا وكلا» من الفريقين، وفي قراءة بالرفع مبتدأ «وعد الله الحسنى» الجنة «والله بما تعملون خبير» فيجازيكم به.'),
(5086, 57, 11, '«من ذا الذي يقرض الله» بإنفاق ماله في سبيل الله «قرضا حسنا» بأن ينفقه لله «فيضاعفه» وفي قراءة فيضعفه بالتشديد «له» من عشر إلى أكثر من سبعمائة كما ذكر في البقرة «وله» مع المضاعفة «أجر كريم» مقترن به رضا وإقبال.'),
(5087, 57, 12, 'اذكر «يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم» أمامهم «و» يكون «بأيمانهم» ويقال لهم «بُشراكم اليوم جنات» أي ادخلوها «تجري من تحتها الأنهار خالدين فيها ذلك هو الفوز العظيم».'),
(5088, 57, 13, '«يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا» أبصرونا وفي قراءة بفتح الهمزة وكسر الظاء: أمهلونا «نقتبس» نأخذ القبس والإضاءة «من نوركم قيل» لهم استهزاءً بهم «ارجعوا وراءكم فالتمسوا نورا» فرجعوا «فضرب بينهم» وبين المؤمنين «بسور» قيل هو سور الأعراف «له باب باطنه فيه الرحمة» من جهة المؤمنين «وظاهره» من جهة المنافقين «من قبله العذاب».'),
(5089, 57, 14, '«ينادونهم ألم نكن معكم» على الطاعة «قالوا بلى ولكنكم فتنتم أنفسكم» بالنفاق «وتربصتم» بالمؤمنين الدوائر «وارتبتم» شككتم في دين الإسلام «وغرتكم الأمانيُّ الأطماع «حتى جاء أمر الله» الموت «وغركم بالله الغَرور» الشيطان.'),
(5090, 57, 15, '«فاليوم لا يُؤخذ» بالياء والتاء «منكم فدية ولا من الذين كفروا مأواكم النار هي مولاكم» أولى بكم «وبئس المصير» هي.'),
(5091, 57, 16, '«ألم يَأنِ» يَحِن «للذين آمنوا» نزلت في شأن الصحابة لما أكثروا المزاح «أن تخشع قلوبهم لذكر الله وما نزَّل» بالتشديد والتخفيف «من الحق» القرآن «ولا يكونوا» معطوف على تخشع «كالذين أوتوا الكتاب من قبل» هم اليهود والنصارى «فطال عليهم الأمد» الزمن بينهم وبين أنبيائهم «فقست قلوبهم» لم تلن لذكر الله «وكثير منهم فاسقون».'),
(5092, 57, 17, '«اعلموا» خطاب للمؤمنين المذكورين «أن الله يحيي الأرض بعد موتها» بالنبات فكذلك يفعل بقلوبكم يردها إلى الخشوع «قد بينا لكم الآيات» الدالة على قدرتنا بهذا وغيره «لعلكم تعقلون».'),
(5093, 57, 18, '«إن المصَّدقين» من التصديق أدغمت التاء في الصاد، أي الذين تصدقوا «والمصَّدقات» اللاتي تصدقن وفي قراءة بتخفيف الصاد فيهما من التصديق والإيمان «وأقرضوا الله قرضا حسنا» راجع إلى الذكور والإناث بالتغليب وعطف الفعل على الاسم في صلة أل لأنه فيها حل محل الفعل، وذكر القرض بوصفه بعد التصدق تقييد له «يضاعف» وفي قراءة يضعف بالتشديد، أي قرضهم «لهم ولهم أجر كريم».'),
(5094, 57, 19, '«والذين آمنوا بالله ورسله أولئك هم الصديقون» المبالغون في التصديق «والشهداء عند ربهم» على المكذبين من الأمم «لهم أجرهم ونورهم والذين كفروا وكذبوا بآياتنا» الدالة على وحدانيتنا «أولئك أصحاب الجحيم» النار.'),
(5095, 57, 20, '«اعلموا أنما الحياة الدنيا لعب ولهو وزينة» تزين «وتفاخر بينكم وتكاثر في الأموال والأولاد» أي الاشتغال فيها، وأما الطاعات وما يعين عليها فمن أمور الآخرة «كمثل» أي هي في إعجابها لكم واضمحلالها كمثل «غيث» مطر «أعجب الكفار» الزارع «نباته» الناشئ، عنه «ثم يهيج» ييبس «فتراه مصفرا ثم يكون حطاما» فتاتا يضمحل بالرياح «وفي الآخرة عذاب شديد» لمن آثر عليها الدنيا «ومغفرة من الله ورضوان» لمن لم يؤثر عليها الدنيا «وما الحياة الدنيا» ما التمتع فيها «إلا متاع الغرور».'),
(5096, 57, 21, '«سابقوا إلى مغفرة من ربكم وجنة عرضها كعرض السماء والأرض» لو وصلت إحداهما بالأخرى والعرض: السعة «أعدت للذين آمنوا بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم».'),
(5097, 57, 22, '«ما أصاب من مصيبة في الأرض» بالجدب «ولا في أنفسكم» كالمرض وفقد الولد «إلا في كتاب» يعنى اللوح المحفوظ «من قبل أن نبرأها» نخلقها، ويقال في النعمة كذلك «إن ذلك على الله يسير».'),
(5098, 57, 23, '«لكيلا» كي ناصبة للفعل بمعنى أن، أي أخبر تعالى بذلك لئلا «تأسوا» تحزنوا «على ما فاتكم ولا تفرحوا» فرح بطر بل فرح شكر على النعمة «بما آتاكم» بالمد أعطاكم وبالقصر جاءكم منه «والله لا يحب كل مختال» متكبر بما أوتي «فخور» به على الناس.'),
(5099, 57, 24, '«والذين يبخلون» بما يجب عليهم «ويأمرون الناس بالبخل» به لهم وعيد شديد «ومن يتولى» عما يجب عليه «فإن الله هو» ضمير فصل وفي قراءة بسقوطه «الغني» عن غيره «الحميد» لأوليائه.'),
(5100, 57, 25, '«لقد أرسلنا رسلنا» الملائكة إلى الأنبياء «بالبينات» بالحجج القواطع «وأنزلنا معهم الكتاب» بمعنى الكتب «والميزان» العدل «ليقوم الناس بالقسط وأنزلنا الحديد» أخرجناه من المعادن «فيه بأس شديد» يقاتل به «ومنافع للناس وليعلم الله» علم مشاهدة، معطوف على ليقوم الناس «من ينصره» بأن ينصر دينه بآلات الحرب من الحديد وغيره «ورسوله بالغيب» حال من هاء ينصره، أي غائبا عنهم في الدنيا، قال ابن عباس: ينصرونه ولا يبصرونه «إن الله قوي عزيز» لا حاجة له إلى النصرة لكنها تنفع من يأتي بها.'),
(5101, 57, 26, '«ولقد أرسلنا نوحا وإبراهيم وجعلنا في ذريتهما النبوة والكتاب» يعني الكتب الأربعة: التوراة والإنجيل والزبور والفرقان فإنها في ذرية إبراهيم «فمنهم مهتد وكثير منهم فاسقون».'),
(5102, 57, 27, '«ثم قفينا على آثارهم برسلنا وقفينا بعيسى ابن مريم وآتيناه الإنجيل وجعلنا في قلوب الذين اتبعوه رأفة ورحمة ورهبانية» هي رفض النساء واتخاذ الصوامع «ابتدعوها» من قبل أنفسهم «ما كتبناها عليهم» ما أمرناهم بها «إلا» لكن فعلوها «ابتغاء رضوان» مرضاة «الله فما رعوْها حق رعايتها» إذ تركها كثير منهم وكفروا بدين عيسى ودخلوا في دين ملكهم وبقي على دين عيسى كثير منهم فآمنوا بنبينا «فآتينا الذين آمنوا» به «منهم أجرهم وكثير منهم فاسقون».'),
(5103, 57, 28, '«يا أيها الذين آمنوا» بعيسى «اتقوا الله وآمنوا برسوله» محمد صلى الله عليه وسلم وعيسى «يؤتكم كفلين» نصيبين «من رحمته» لإيمانكم بالنبيين «ويجعل لكم نورا تمشون به» على الصراط «ويغفر لكم والله غفور رحيم».'),
(5104, 57, 29, '«لئلا يعلم» أي أعلمكم بذلك ليعلم «أهل الكتاب» بالتوراة الذين لم يؤمنوا بمحمد صلى الله عليه وسلم «أن» مخففة من الثقيلة واسمها ضمير الشأن والمعنى أنهم «لا يقدرون على شيءٍ من فضل الله» خلاف ما في زعمهم أنهم أحباء الله وأهل رضوانه «وأن الفضل بيد الله يؤتيه» يعطيه «من يشاء» فآتى المؤمنين منهم أجرهم مرتين كما تقدم «والله ذو الفضل العظيم».');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, '«قد سمع الله قول التي تجادلك» تراجعك أيها النبي «في زوجها» المظاهر منها وكان قال لها: أنت عليَّ كظهر أمي، وقد سألت النبي صلى الله عليه وسلم عن ذلك فأجابها بأنها حرمت عليه على ما هو المعهود عندهم من أن الظهار موجبه فرقة مؤبدة وهي خولة بنت ثعلبة، وهو أوس بن الصامت «وتشتكي إلى الله» وحدتها وفاقتها وصبية صغارا إن ضمتهم إليه ضاعوا أو إليها جاعوا «والله يسمع تحاوركما» تراجعكما «إن الله سميع بصير» عالم.'),
(5106, 58, 2, '«الذين يظَّهَّرون» أصله يتظهرون أدغمت التاء في الظاء، وفي قراءة بألف بين الظاء والهاء الخفيفة وفي أخرى كيقاتلون والموضع الثاني كذلك «منكم من نسائهم ما هن أمهاتهم إن أمهاتهم إلا اللأئي» بهمزة وياء وبلا ياء «ولدنهم وإنهم ْ» بالظهار «ليقولن منكرا من القول وزورا» كذبا «وإن الله لعفو غفور».'),
(5107, 58, 3, '«والذين يظاهرون من نسائهم ثم يعودوا لما قالوا» أي فيه بأن يخالفوه بإمساك المظاهر منها الذي هو خلاف مقصود الظهار من وصف المرأة بالتحريم «فتحرير رقبة» أي إعتاقها عليه «من قبل أن يتماسا» بالوطء «ذلكم توعظون به والله بما تعملون خبير».'),
(5108, 58, 4, '«فمن لم يجد» رقبة «فصيام شهرين متتابعين من قبل أن يتماسا فمن لم يستطع» أي الصيام «فإطعام ستين مسكينا» عليه: أي من قبل أن يتماسا حملا للمطلق عل المقيد لكل مسكين مد من غالب قوت البلد «ذلك» أي التخفيف في الكفارة «لتؤمنوا بالله ورسوله وتلك» أي الأحكام المذكورة «حدود الله وللكافرين» بها «عذاب أليم» مؤلم.'),
(5109, 58, 5, '«إن الذين ُيَحادُّونَ» يخالفون «الله ورسوله كبتوا» أذلوا «كما كبت الذين من قبلهم» في مخالفتهم رسلهم «وقد أنزلنا آيات بينات» دالة على صدق الرسول «وللكافرين» بالآيات «عذاب مهين» ذو إهانة.'),
(5110, 58, 6, '«يوم يبعثهم الله جميعا فينبئهم بما عملوا أحصاه الله ونسوه والله على كل شيء شهيد».'),
(5111, 58, 7, '«ألم تر» تعلم «أن الله يعلم ما في السماوات وما في الأرض ما يكون من نجوى ثلاثة إلا هو رابعهم» بعلمه «ولا خمسة إلا هو سادسهم ولا أدنى من ذلك ولا أكثر إلا هو معهم أين ما كانوا ثم ينبئهم بما عملوا يوم القيامة إن الله بكل شيء عليم».'),
(5112, 58, 8, '«أم ترَ» تنظر «إلى الذين نُهوا على النجوى ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان ومعصية الرسول» الريبة «وإذا جاءُوك حيوك» أيها النبي «بما لم يحيك به الله» وهو قولهم: السام عليك، أي الموت «ويقولون في أنفسهم لولا» هلا «يعذبنا الله بما نقول» من التحية وأنه ليس بنبي إن كان نبيا «حسبهم جهنم يصلونها فبئس المصير» هي.'),
(5113, 58, 9, '«يا أيها الذين آمنوا إذا تناجيتم فلا تتناجوا بالإثم والعدوان ومعصية الرسول وتناجوا بالبر والتقوى واتقوا الله الذي إليه تحشرون».'),
(5114, 58, 10, '«إنما النجوى» بالإثم ونحوه «من الشيطان» بغروره «ليحزن الذين آمنوا وليس» هو «بضارهم شيئا إلا بإذن الله» أي إرادته «وعلى الله فليتوكل المؤمنون».'),
(5115, 58, 11, '«يا أيها الذين آمنوا إذا قيل لكم تفسحوا» توسعوا «في المجلسْ» مجلس النبي صلى الله عليه وسلم والذكر حتى مجلس من جاءكم وفي قراءة المجالس «فافسحوا يفسح الله لكم» في الجنة «وإذا قيل انشِزوا» قوموا إلى الصلاة وغيرها من الخيرات «فانشِزوا» وفي قراءة بضم الشين فيهما «يرفع الله الذين آمنوا منكم» بالطاعة في ذلك «و» يرفع «الذين أوتوا العلم درجات» في الجنة «والله بما تعملون خبير».'),
(5116, 58, 12, '«يا أيها الذين آمنوا إذا ناجيتم الرسول» أردتم مناجاته «فقدموا بين يدي نجواكم» قبلها «صدقة ذلك خير لكم وأطهر» لذنوبكم «فإن لم تجدوا» ما تتصدقون به «فإن الله غفور» لمناجاتكم «رحيم» بكم، يعني فلا عليكم في المناجاة من غير صدقة، ثم نسخ ذلك بقوله.'),
(5117, 58, 13, '«أأشفقتم» بتحقيق الهمزتين وإبدال الثانية ألفا وتسهليها وإدخال ألف بين المسهلة والأخرى وتركه، أي خفتم من «أن تقدِّموا بين يدي نجواكم صدقات» لفقر «فإذا لم تفعلوا» الصدقة «وتاب الله عليكم» رجع بكم عنها «فأقيموا الصلاة وآتوا الزكاة وأطيعوا الله ورسوله» أي داوموا على ذلك «والله خبير بما تعملون».'),
(5118, 58, 14, '«ألم ترَ» تنظر «إلى الذين تولَّوْا» هم المنافقون «قوما» هم اليهود «غضب الله عليهم ما هم» أي المنافقين «منكم» من المؤمنين «ولا منهم» من اليهود بل هم مذبذبون «ويحلفون على الكذب» أي قولهم إنهم مؤمنون «وهم يعلمون» أنهم كاذبون فيه.'),
(5119, 58, 15, '«أعد الله لهم عذابا شديدا إنهم ساء ما كانوا يعملون» من المعاصي.'),
(5120, 58, 16, '«اتخذوا إيمانهم جُنَّةٌ» سترا على أنفسهم وأموالهم «فصدوا» بها المؤمنين «عن سبيل الله» أي الجهاد فيهم بقتلهم وأخذ أموالهم «فلهم عذاب مهين» ذو إهانة.'),
(5121, 58, 17, '«لن تغني عنهم أموالهم ولا أولادهم من الله» من عذابه «شيئا» من الإغناء «أولئك أصحاب النار هم فيها خالدون».'),
(5122, 58, 18, 'اذكر «يوم يبعثهم الله جميعا فيحلفون له» أنهم مؤمنون «كما يحلفون لكم ويحسبون أنهم على شيء» من نفع حلفهم في الآخرة كالدنيا «ألا إنهم هم الكاذبون».'),
(5123, 58, 19, '«استحوذ» استولى «عليهم الشيطان» بطاعتهم له «فأنساهم ذكر الله أولئك حزب الشيطان» أتباعه «ألا إن حزب الشيطان هم الخاسرون».'),
(5124, 58, 20, '«إن الذين يحادون» يخالفون «الله ورسوله أولئك في الأذلين» المغلوبين.'),
(5125, 58, 21, '«كتب الله» في اللوح المحفوظ أو قضى «لأغلبن أنا ورسلي» بالحجة أو السيف «إن الله قوي عزيز».'),
(5126, 58, 22, '«لا تجد قوما يؤمنون بالله واليوم الآخر يوادون» يصادقون «من حادَّ الله ورسوله ولو كانوا» أي المحادون «آباءهم» أي المؤمنين «أو أبناءهم أو إخوانهم أو عشيرتهم» بل يقصدونهم بالسوء ويقاتلونهم على الإيمان كما وقع لجماعة من الصحابة رضي الله عنهم «أولئك» الذين يوادونهم «كتب» أثبت «في قلوبهم الإيمان وأيدهم بروح» بنور «منه» تعالى «ويدخلهم جنات تجري من تحتها الأنهار خالدين فيها رضي الله عنهم» بطاعته «ورضوا عنه» بثوابه «أولئك حزب الله» يتبعون أمره ويجتنبون نهيه «ألا إن حزب الله همُ المفلحون» الفائزون.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, '«سبح لله ما في السماوات وما في الأرض» أي ننزهه فاللام مزيدة وفي الإتيان بما تغليب للأكثر «وهو العزيز الحكيم» في ملكه وصنعه.'),
(5128, 59, 2, '«هو الذي أخرج الذين كفروا من أهل الكتاب» هم بنو النضير من اليهود «من ديارهم» مساكنهم بالمدينة «لأول الحشر» هو حشرهم إلى الشام وآخره أن أجلاهم عمر في خلافته إلى خيبر «ما ظننتم» أيها المؤمنون «أن يخرجوا وظنوا أنهم مانعتهم» خبر أن «حصونهم» فاعله تم به الخبر «من الله» من عذابه «فأتاهم الله» أمره وعذابه «من حيث لم يحتسبوا» لم يخطر ببالهم من جهة المؤمنين «وقذف» ألقى «في قلوبهم الرعب» بسكون العين وضمها، الخوف بقتل سيدهم كعب بن الأشرف «يخرِّبون» بالتشديد والتخفيف من أخرب «بيوتهم» لينقلوا ما أستحسنوه منها من خشب وغيره «بأيديهم وأيدي المؤمنين فاعتبروا يا أولي الأبصار».'),
(5129, 59, 3, '«ولولا أن كتب الله» قضى «عليهم الجلاء» الخروج من الوطن «لعذبهم في الدنيا» بالقتل والسبي كما فعل بقريظة من اليهود «ولهم في الآخرة عذاب النار».'),
(5130, 59, 4, '«ذلك بأنهم شاقوا» خالفوا «الله ورسوله ومن يشاق الله فإن الله شديد العقاب» له.'),
(5131, 59, 5, '«ما قطعتم» يا مسلمون «من لينة» نخلة «أو تركتموها قائمة على أصولها فبأذن الله» أي خيَّركم في ذلك «وليخزيَ» بالإذن في القطع «الفاسقين» اليهود في اعتراضهم أن قطع الشجر المثمر فساد.'),
(5132, 59, 6, '«وما أفاء» رد «الله على رسوله منهم فما أوجفتم» أسرعتم يا مسلمون «عليه من» زائدة «خيل ولا ركاب» إبل، أي لم تقاسوا فيه مشقة «ولكن الله يسلط رسله على من يشاء والله على كل شيءٍ قدير» فلا حق لكم فيه ويختص به النبي صلى الله عليه وسلم ومن ذكر معه في الآية الثانية من الأصناف الأربعة على ما كان يقسمه من أن لكل منهم خمس الخمس وله صلى الله عليه وسلم الباقي يفعل فيه ما يشاء فأعطى منه المهاجرين وثلاثة من الأنصار لفقرهم.'),
(5133, 59, 7, '«ما أفاء الله على رسوله من أهل القرى» كالصفراء ووادي القرى وينبع «فلله» يأمر فيه بما يشاء «وللرسول ولذي» صاحب «القربى» قرابة النبي من بني هاشم وبني المطلب «واليتامى» أطفال المسلمين الذين هلكت آباؤهم وهم فقراء «والمساكين» ذوي الحاجة من المسلمين «وابن السبيل» المنقطع في سفره من المسلمين، أي يستحقه النبي صلى الله عليه وسلم والأصناف الأربعة على ما كان يقسمه من أن لكل من الأربعة خمس الخمس وله الباقي «كي لا» كي بمعنى اللام وأن مقدرة بعدها «يكون» الفيء علة لقسمه كذلك «دولةً» متداولا «بين الأغنياء منكم وما آتاكم» أعطاكم «الرسول» من الفيء وغيره «فخذوه وما نهاكم عنه فانتهوا واتقوا الله إن الله شديد العقاب».'),
(5134, 59, 8, '«للفقراء» متعلق بمحذوف، أي اعجبوا «المهاجرين الذين أخرجوا من ديارهم وأموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله أولئك هم الصادقون» في إيمانهم.'),
(5135, 59, 9, '«والذين تبوؤا الدار» أي المدينة «والإيمان» أي ألفوه وهم الأنصار «من قبلهم يحبون من هاجر إليهم ولا يجدون في صدورهم حاجة» حسدا «مما أوتوا» أي آتى النبي صلى الله عليه وسلم المهاجرين من أموال بني النضير المختصة بهم «ويؤثرون على أنفسهم ولو كان بهم خصاصة» حاجة إلى ما يؤثرون به «ومن يوق شُحَّ نفسه» حرصها على المال «فأولئك هم المفلحون».'),
(5136, 59, 10, '«والذين جاءُوا من بعدهم» من بعد المهاجرين والأنصار إلى يوم القيامة «يقولون ربنا اغفر لنا ولإخواننا الذين سبقونا بالإيمان ولا تجعل في قلوبنا غلا» حقدا «للذين آمنوا ربنا إنك رؤوف رحيم».'),
(5137, 59, 11, '«ألم ترْ» تنظر «إلى الذين نافقوا يقولون لإخوانهم الذين كفروا من أهل الكتاب» وهم بنو النضير وإخوانهم في الكفر «لئن» لام قسم في الأربعة «أخرجتم» من المدينة «لنخرجن معكم ولا نطيع فيكم» في خذلانكم «أحدا أبدا وإن قوتلتم» حذفت منه اللام الموطئة «لننصرنكم والله يشهد إنهم لكاذبون».'),
(5138, 59, 12, '«لئن أخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم» أي جاؤوا لنصرهم «ليولنَّ الأدبار» واستغني بجواب القسم المقدر عن جواب الشرط في المواضع الخمسة «ثم لا يُنصرون» أي اليهود.'),
(5139, 59, 13, '«لأنتم أشد رهبة» خوفا «في صدورهم» أي المنافقين «من الله» لتأخير عذابه «ذلك بأنهم قوم لا يفقهون».'),
(5140, 59, 14, '«لا يقاتلونكم» أي اليهود «جميعا» مجتمعين «إلا في قرى محصنة أو من وراء جدار» سور، وفي قراءة جدر «بأسهم» حربهم «بينهم شديد تحسبهم جميعا» مجتمعين «وقلوبهم شتى» متفرقة خلاف الحسبان «ذلك بأنهم قوم لا يعقلون».'),
(5141, 59, 15, 'مثلهم في ترك الإيمان «كمثل الذين من قبلهم قريبا» بزمن قريب وهم أهل بدر من المشركين «ذاقوا وبال أمرهم» عقوبته في الدنيا من القتل وغيره «ولهم عذاب أليم» مؤلم في الآخرة.'),
(5142, 59, 16, 'مثلهم أيضا في سماعهم من المنافقين وتخلفهم عنهم «كمثل الشيطان إذ قال للإنسان اكفر فلما كفر قال إني بريء منك إني أخاف الله رب العالمين» كذبا منه ورياءً.'),
(5143, 59, 17, '«فكان عاقبتهما» أي الغاوي والمغوي وقرئ بالرفع اسم كان «أنهما في النار خالدين فيها وذلك جزاء الظالمين» أي الكافرين.'),
(5144, 59, 18, '«يا أيها الذين آمنوا اتقوا الله ولتنظر نفس ما قدمت لغدٍ» ليوم القيامة «واتقوا الله إن الله خبير بما تعملون».'),
(5145, 59, 19, '«ولا تكونوا كالذين نسوا الله» تركوا طاعته «فأنساهم أنفسهم» أن يقدموا لها خيرا «أولئك هم الفاسقون».'),
(5146, 59, 20, '«لا يستوي أصحاب النار وأصحاب الجنة أصحاب الجنة هم الفائزون».'),
(5147, 59, 21, '«لو أنزلنا هذا القرآن على جبل» وجعل فيه تمييز كالإنسان «لرأيته خاشعا متصدعا» متشققا «من خشية الله وتلك الأمثال» المذكورة «نضربها للناس لعلهم يتفكرون» فيؤمنون.'),
(5148, 59, 22, '«هو الله الذي لا إله إلا هو عالم الغيب والشهادة» السر والعلانية «هو الرحمن الرحيم».'),
(5149, 59, 23, '«هو الله الذي لا إله إلا هو الملك القدُّوس» الطاهر عما لا يليق به «السلام» ذو السلامة من النقائص «المؤمن» المصدق رسله بخلق المعجزة لهم «المهيمن» من هيمن يهيمن إذا كان رقيبا على الشيء، أي الشهيد على عباده بأعمالهم «العزيز» القوي «الجبار» جبر خلقه على ما أراد «المتكبر» عما لا يليق به «سبحان الله» نزَّه نفسه «عما يشركون» به.'),
(5150, 59, 24, '«هو الله الخالق البارىء» المنشىء من العدم «المصور له الأسماء الحسنى» التسعة والتسعون الوارد بها الحديث، والحسنى مؤنث الأحسن «يسبح له ما في السماوات والأرض وهو العزيز الحكيم» تقدم أولها.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, '«يا أيها الذين آمنوا لا تتخذوا عدوي وعدوَّكم» أي كفار مكة «أولياء تلقون» توصلون «إليهم» قصد النبي صلى الله عليه وسلم غزوهم الذي أسرَّوُ إليكم وَوَرَّى بحُنَين «بالمودة» بينكم وبينهم كتب حاطب بن أبي بلتعة إليهم كتابا بذلك لما له عندهم من الأولاد والأهل المشركين فاسترده النبي صلى الله عليه وسلم ممن أرسله معه بإعلام الله تعالى له بذلك وقبل عذر حاطب فيه «وقد كفروا بما جاءكم من الحق» أي دين الإسلام والقرآن «يخرجون الرسول وإياكم» من مكة بتضييقهم عليكم «أن تؤمنوا» أي لأجل أن آمنتم «بالله ربكم إن كنتم خرجتم جهادا» للجهاد «في سبيلي وابتغاء مرضاتي» وجواب الشرط دل عليه ما قبله، أي فلا تتخذوهم أولياء «تُسرون إليهم بالمودة وأنا أعلم بما أخفيتم وما أعلنتم ومن يفعله منكم» أي إسرار خبر النبي إليهم «فقد ضل سواء السبيل» أخطأ طريق الهدى، والسواء في الأصل الوسط.'),
(5152, 60, 2, '«إن يثقفوكم» يظفروا بكم «يكونوا لكم أعداءً ويبسطوا إليكم أيديهم» بالقتل والضرب «وألسنتهم بالسوء» بالسب والشتم «وودوا» تمنوا «لو تكفرون».'),
(5153, 60, 3, '«لن تنفعكم أرحامكم» قراباتكم «وْلا أولادكم» المشركون الذين لأجلهم أسررتم الخبر من العذاب في الآخرة «يوم القيامة يُفصَل» بالبناء للمفعول والفاعل «بينكم» وبينهم فتكونون في الجنة وهم جملة الكفار في النار «والله بما تعلمون بصير».'),
(5154, 60, 4, '(قد كانت لكم أسوة) بكسر الهمزة وضمها في الموضعين، قدوة (حسنة في إبراهيم) أي به قولا وفعلا (والذين معه) من المؤمنين (إذ قالوا لقومهم إنا برءاء) جمع بريء كظريف (منكم ومما تعبدون من دون الله كفرنا بكم) أنكرناكم (وبدا بيننا وبينكم العداوة والبغضاء أبدا) بتحقيق الهمزتين وإبدال الثانية واوا (حتى تؤمنوا بالله وحده إلا قول إبراهيم لأبيه لأستغفرن لك) مستثنى من أسوة، فليس لكم التأسي به في ذلك بأن تستغفروا للكفار وقوله (وما أملك لك من الله) أي من عذابه وثوابه (من شيء) كني به عن أنه لا يملك له غير الاستغفار فهو مبني عليه مستثنى من حيث ظاهره مما يتأسى فيه (قل فمن يملك لكم من الله شيئا) واستغفاره له قبل أن يتبين له أنه عدو لله كما ذكره في "" براءة "" (ربنا عليك توكلنا وإليك أنبنا وإليك المصير) من مقول الخليل ومن معه أي قالوا:'),
(5155, 60, 5, '«ربنا لا تجعلنا فتنة للذين كفروا» أي لا تظهرهم علينا فيظنوا أنهم على الحق فيفتنوا أي تذهب عقولهم بنا «واغفر لنا ربنا إنك أنت العزيز الحكيم» في ملكك وصنعك.'),
(5156, 60, 6, '«لقد كان لكم» يا أمة محمد جواب قسم مقدر «فيهم أسوة حسنة لمن كان» بدل اشتمال من كم بإعادة الجار «يرجوا الله واليوم الآخر» أي يخافهما أو يظن الثواب والعقاب «ومن يتول» بأن يوالي الكفار «فإن الله هو الغني» عن خلقه «الحميد» لأهل طاعته.'),
(5157, 60, 7, '«عسى الله أن يجعل بينكم وبين الذين عاديتم منهم» من كفار مكة طاعة لله تعالى «مودة» بأن يهديهم للإيمان فيصيروا لكم أولياء «والله قدير» على ذلك وقد فعله بعد فتح مكة «والله غفور» لهم ما سلف «رحيم» بهم.'),
(5158, 60, 8, '«لا ينهاكم الله عن الذين لم يقاتلوكم» من الكفار «في الدين ولم يخرجوكم من دياركم أن تبروهم» بدل اشتمال من الذين «وتقسطوا» تفضوا «إليهم» بالقسط، أي بالعدل وهذا قبل الأمر بجهادهم «إن الله يحب المقسطين» بالعادلين.'),
(5159, 60, 9, '«إنما ينهاكم الله عن الذين قاتلوكم في الدين وأخرجوكم من دياركم وظاهروا» عاونوا «على إخراجكم أن تولوهم» بدل اشتمال من الذين، أي تتخذونهم أولياء «ومن يتولهم فأولئك هم الظالمون».'),
(5160, 60, 10, '«يا أيها الذين آمنوا إذا جاءكم المؤمنات» بألسنتهن «مهاجرات» من الكفار بعد الصلح معهم في الحديبية على أن من جاء منهم إلى المؤمنين يرد «فامتحنوهن» بالحلف على أنهن ما خرجن إلا رغبة في الإسلام لا بغضاً لأزواجهن الكفار ولا عشقا لرجال من المسلمين كذا كان النبي صلى الله عليه وسلم يحلفهن «الله أعلم بإيمانهن فأن علمتموهن» ظننتموهن بالحلف «مؤمنات فلا ترجعوهن» تردوهن «إلى الكفار لا هن حل لهم ولا هم يحلون لهن وآتوهم» أي أعطوا الكفار أزواجهن «ما أنفقوا» عليهن من المهور «ولا جناح عليكم أن تنكحوهن» بشرطه «إذا آتيتموهن أجورهن» مهورهن «ولا تمسِّكوا» بالتشديد والتخفيف «بعصم الكوافر» زوجاتكم لقطع إسلامكم لها بشرطه، أو اللاحقات بالمشركين مرتدات لقطع ارتدادهن نكاحكم بشرطه «واسألوا» اطلبوا «ما أنفقتم» عليهن من المهور في صورة الارتداد ممن تزوجهنَّ من الكفار «وليسألوا ما أنفقوا» على المهاجرات كما تقدم أنهم يؤتونه «ذلكم حكم الله يحكم بينكم» به «والله عليكم حكيم».'),
(5161, 60, 11, '«وإن فاتكم شيء من أزواجكم» أي واحدة فأكثر منهن أو شيء من مهور بالذهاب «إلى الكفار» مرتدات «فعاقبتم» فغزوتم وغنمتم «فآتوا الذين ذهبت أزواجهم» من الغنيمة «مثل ما أنفقوا» لفواته عليهم من جهة الكفار «واتقوا الله الذي أنتم به مؤمنون» وقد فعل المؤمنون ما أمروا به من الإيتاء للكفار والمؤمنين ثم ارتفع هذا الحكم.'),
(5162, 60, 12, '«يا أيها النبي إذا جاءك المؤمنات يبايعنك على أن لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن أولادهن» كما كان يفعل في الجاهلية من وأد البنات، أي دفنهن أحياء خوف العار والفقر «ولا يأتين ببهتان يفترينه بين أيديهن وأرجلهن» أي بولد ملقوط ينسبنه إلى الزوج ووصف بصفة الولد الحقيقي، فإن الأم إذا وضعته سقط بين يديها ورجليها «ولا يعصينك في» فعل «معروف» هو ما وافق طاعة الله كترك النياحة وتمزيق الثياب وجز الشعور وشق الجيب وخمش الوجه «فبايعهن» فعل ذلك صلى الله عليه وسلم بالقول ولم يصافح واحدة منهن «واستغفر لهن الله إن الله غفور رحيم».'),
(5163, 60, 13, '«يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم» هم اليهود «قد يئسوا من الآخرة» من ثوابها مع إيقانهم بها لعنادهم النبي مع علمهم بصدقه «كما يئس الكفار» الكائنون «من أصحاب القبور» أي المقبورين من خير الآخرة، إذ تعرض عليهم مقاعدهم من الجنة لو كانوا آمنوا وما يصيرون إليه من النار.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, '«سبح لله ما في السماوات وما في الأرض» أي نزهه فاللام مزيدة وجيء بما دون من تغليبا للأكثر «وهو العزيز» في ملكه «الحكيم» في صنعه.'),
(5165, 61, 2, '«يا أيها الذين آمنوا لم تقولون» في طلب الجهاد «ما لا تفعلون» إذ انهزمتم بأُحد.'),
(5166, 61, 3, '«كبر» عظم «مقتا» تمييز «عند الله أن تقولوا» فاعل كبر «ما لا تفعلون».'),
(5167, 61, 4, '«إن الله يحب» ينصر ويكرم «الذين يقاتلون في سبيله صفا» حال، أي صافين «كأنهم بنيان مرصوص» ملزق بعضه إلى بعض ثابت.'),
(5168, 61, 5, '«و» اذكر «إذ قال موسى لقومه يا قوم لم تؤذونني» قالوا: إنه آدر، أي منتفخ الخصية وليس كذلك، وكذبوه «وقد» للتحقيق «تعلمون أني رسول الله إليكم» الجملة حال، والرسول يحترم «فلما زاغوا» عدلوا عن الحق بإيذائه «أزاغ الله قلوبهم» أمالها عن الهدى على وفق ما قدره في الأزل «والله لا يهدي القوم الفاسقين» الكافرين في علمه.'),
(5169, 61, 6, '«و» اذكر «إذ قال عيسى ابن مريم يا بني إسرائيل» لم يقل: يا قوم لأنه لم يكن له فيهم قرابة «إني رسول الله إليكم مصدقا لما بين يديَّ» قبلي «من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد» قال تعالى «فلما جاءهم» جاء أحمد الكفار «بالبينات» الآيات والعلامات «قالوا هذا» أي المجيء به «سحر» وفي قراءة ساحر، أي الجائي به «مبين» بيِّن.'),
(5170, 61, 7, '«ومن» أي لا أحد «أظلم» أشد ظلما «ممن افترى على الله الكذب» بنسبة الشريك والولد إليه ووصف آياته بالسحر «وهو يدعى إلى الإسلام والله لا يهدي القوم الظالمين» الكافرين.'),
(5171, 61, 8, '«يريدون ليطفئوا» منصوب بأن مقدرة واللام مزيدة «نور الله» شرعه وبراهينه «بأفواههم» بأقوالهم إنه سحر وشعر وكهانة «والله متمٌّ» مظهر «نورهُ» وفي قراءة بالإضافة «ولو كره الكافرون» ذلك.'),
(5172, 61, 9, '«هو الذي أرسل رسوله بالهدى ودين الحق ليظهره» يعليه «على الدين كله» جميع الأديان المخالفة له «ولو كره المشركون» ذلك.'),
(5173, 61, 10, '«يا أيها الذين آمنوا هل أدلكم على تجارة تنجيكم» بالتخفيف والتشديد «من عذاب أليم» مؤلم، فكأنهم قالوا نعم فقال:'),
(5174, 61, 11, '«تؤمنون» تدومون على الإيمان «بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم ذلكم خير لكم إن كنتم تعلمون» أنه خير لكم فافعلوه.'),
(5175, 61, 12, '«يغفر» جواب شرط مقدر، أي إن تفعلوه يغفر «لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار ومساكن طيبة في جنات عدن» إقامة «ذلك الفوز العظيم».'),
(5176, 61, 13, '«و» يؤتكم «أُخرى تحبونها نصر من الله وفتح قريب وبشر المؤمنين» بالنصر والفتح.'),
(5177, 61, 14, '«يا أيها الذين آمنوا كونوا أنصارا لله» لدينه وفي قراءة بالإضافة «كما قال» الخ المعنى: كما كان الحواريون كذلك الدال عليه قال «عيسى ابن مريم للحواريين من أنصاري إلى الله» أي من الأنصار الذين يكونون معي متوجها إلى نصرة الله «قال الحواريون نحن أنصار الله» والحواريون أصفياء عيسى وهم أول من آمن به وكانوا اثني عشر رجلا من الحور وهو البياض الخالص وقيل كانوا قصارين يحورون الثياب، أي يبيضونها «فآمنت طائفة من بني إسرائيل» بعيسى وقالوا إنه عبد الله رُفِع إلى السماء «وكفرت طائفة» لقولهم إنه ابن الله رفعه إليه فاقتتلت الطائفتان «فأيدنا» قوينا «الذين آمنوا» من الطائفتين «على عدوهم» الطائفة الكافرة «فأصبحوا ظاهرين» غالبين.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, '«يسبح لله» ينزهه فاللام زائدة «ما في السماوات وما في الأرض» في ذكر ما تغليب للأكثر «الملك القدوس» المنزه عما لا يليق به «العزيز الحكيم» في ملكه وصنعه.'),
(5179, 62, 2, '«هو الذي بعث في الأميين» العرب، والأمي: من لا يكتب ولا يقرأ كتابا «رسولا منهم» هو محمد صلى الله عليه وسلم «يتلو عليهم آياته» القرآن «ويزكيهم» يطهرهم من الشرك «ويعلمهم الكتاب» القرآن «والحكمة» ما فيه من الأحكام «وإن» مخففة من الثقيلة واسمها محذوف، أي وإنهم «كانوا من قبل» قبل مجيئه «لفي ضلال مبين» بيّن.'),
(5180, 62, 3, '«وآخرون» عطف على الأميين، أي الموجودين «منهم» والآتين منهم بعدهم «لما» لم «يلحقوا بهم» في السابقة والفضل «وهو العزيز الحكيم» في ملكه وصنعه وهم التابعون والإقتصار عليهم كاف في بيان فضل الصحابة المبعوث فيهم النبي صلى الله عليه وسلم على من عداهم ممن بعث إليهم وآمنوا به من جميع الإنس والجن إلى يوم القيامة لأن كل قرن خير ممن يليه.'),
(5181, 62, 4, '«ذلك فضل الله يؤتيه من يشاء» النبي ومن ذكر معه «والله ذو الفضل العظيم».'),
(5182, 62, 5, '«مثل الذين حملوا التوراة» كلفوا العمل بها «ثم لم يحملوها» لم يعملوا بما فيها من نعته صلى الله عليه وسلم فلم يؤمنوا به «كمثل الحمار يحمل أسفارا» أي كتبا في عدم انتفاعه بها «بئس مثل القوم الذين كذبوا بآيات الله» المصدقة للنبي صلى الله عليه وسلم والمخصوص بالذم محذوف تقديره هذا المثل «والله لا يهدي القوم الظالمين» الكافرين.'),
(5183, 62, 6, '«قل يا أيها الذين هادوا إن زعمتم أنكم أولياء لله من دون الناس فتمنوا الموت إن كنتم صادقين» تعلق بتمنوا الشرطان على أن الأول قيد في الثاني، أي إن صدقتم في زعمكم أنكم أولياء لله، والولي يؤثر الآخرة ومبدؤها الموت فتمنوه.'),
(5184, 62, 7, '«ولا يتمنوْنه أبدا بما قدمت أيديهم» من كفرهم بالنبي المستلزم لكذبهم «والله عليم بالظالمين» الكافرين.'),
(5185, 62, 8, '«قل إن الموت الذي تفرون منه فإنه» الفاء زائدة «ملاقيكم ثم تردون إلى عالم الغيب والشهادة» السر والعلانية «فينبئكم بما كنتم تعملون» فيجازيكم به.'),
(5186, 62, 9, '«يا أيها الذين آمنوا إذا نودي للصلاة من» بمعني في «يوم الجمعة فاسعوْا» فامضوا «إلى ذكر الله» للصلاة «وذروا البيع» اتركوا عقده «ذلكم خير لكم إن كنتم تعلمون» أنه خير فافعلوه.'),
(5187, 62, 10, '«فإذا قضيت الصلاة فانتشروا في الأرض» أمر إباحة «وابتغوا» اطلبوا الرزق «من فضل الله واذكروا الله» ذكرا «كثيرا لعلكم تفلحون» تفوزون، كان صلى الله عليه وسلم يخطب يوم الجمعة فقدمت عير وضرب لقدومها الطبل على العادة فخرج لها الناس من المسجد إلا اثني عشر رجلا فنزلت.'),
(5188, 62, 11, '«وإذا رأوا تجارةً أو لهوا انفضوا إليها» أي التجارة لأنها مطلوبهم دون اللهو «وتركوك» في الخطبة «قائما قل ما عند الله» من الثواب «خير» للذين آمنوا «من اللهو ومن التجارة والله خير الرازقين» يقال: كل إنسان يرزق عائلته، أي من رزق الله تعالى.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, '«إذا جاءك المنافقون قالوا» بألسنتهم على خلاف ما في قلوبهم «نشهد إنك لرسول الله والله يعلم إنك لرسوله والله يشهد» يعلم «إن المنافقين لكاذبون» فيما أضمروه مخالفا لما قالوه.'),
(5190, 63, 2, '«اتخذوا أَيْمانهم جُنَّةً» سترة على أموالهم ودمائهم «فصدوا» بها «عن سبيل الله» أي عن الجهاد فيهم «إنهم ساء ما كانوا يعملون».'),
(5191, 63, 3, '«ذلك» أي سوء عملهم «بأنهم آمنوا» باللسان «ثم كفروا» بالقلب، أي استمروا على كفرهم به «فطبع» ختم «على قلوبهم» بالكفر «فهم لا يفقهون» الإيمان.'),
(5192, 63, 4, '«وإذا رأيتهم تعجبك أجسامهم» لجمالها «وإن يقولوا تسمع لقولهم» لفصاحته «كأنهم» من عظم أجسامهم في ترك التفهم «خُشْب» بسكون الشين وضمها «مسندة» ممالة إلى الجدار «يحسبون كل صيحة» تصاح كنداء في العسكر وإنشاد ضالة «عليهم» لما في قلوبهم من الرعب أن ينزل فيهم ما يبيح دماءهم «هم العدوُّ فاحذرهم» فإنهم يفشون سرك للكفار «قاتلهم الله» أهلكهم «أنَّى يؤفكون» كيف يصرفون عن الإيمان بعد قيام البرهان.'),
(5193, 63, 5, '«وإذا قيل لهم تعالوا» معتذرين «يستغفر لكم رسول الله لوّوا» بالتشديد والتخفيف عطفوا «رؤوسهم ورأيتهم يصدون» يعرضون عن ذلك «وهم مستكبرون».'),
(5194, 63, 6, '«سواء عليهم أستغفرت لهم» استغني بهمزة الاستفهام عن همزة الوصل «أم لم تستغفر لهم لن يغفر الله لهم إن الله لا يهدي القوم الفاسقين».'),
(5195, 63, 7, '«هم الذين يقولون» لأصحابهم من الأنصار «لا تنفقوا على من عند رسول الله» من المهاجرين «حتى ينفضُّوا» يتفرقوا عنه «ولله خزائن السماوات والأرض» بالرزق فهو الرازق للمهاجرين وغيرهم «ولكن المنافقين لا يفقهون».'),
(5196, 63, 8, '«يقولون لئن رجعنا» أي من غزوة بني المصطلق «إلى المدينة ليخرجن الأعز» عنوا به أنفسهم «منها الأذل» عنوا به المؤمنين «ولله العزة» الغلبة «ولرسوله وللمؤمنين ولكن المنافقين لا يعلمون» ذلك.'),
(5197, 63, 9, '«يا أيها الذين آمنوا لا تلهكم» تشغلكم «أموالكم ولا أولادكم عن ذكر الله» الصلوات الخمس «ومن يفعل ذلك فأولئك هم الخاسرون».'),
(5198, 63, 10, '«وأنفقوا» في الزكاة «مما رزقناكم من قبل أن يأتي أحدكم الموت فيقول رب لولا» بمعنى هلا، أو لا زائدة ولو للتمني «أخرتني إلى أجل قريب فأصَّدَّق» بإدغام التاء في الأصل في الصاد أتصدق بالزكاة «وأكن من الصالحين» بأن أحج، قال ابن عباس رضي الله عنهما: ما قصر أحد في الزكاة والحج إلا سأل الرجعة عند الموت.'),
(5199, 63, 11, '«ولن يؤخر الله نفسا إذا جاء أجلها والله خبير بما تعملون» بالتاء والياء.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, '«يسبح لله ما في السماوات وما في الأرض» ينزهه فاللام زائدة، وأتى بما دون من تغليبا للأكثر «له الملك وله الحمد وهو على كل شيء قدير».'),
(5201, 64, 2, '«هو الذي خلقكم فمنكم كافر ومنكم مؤمن» في أصل الخلقة ثم يميتكم ويعيدكم على ذلك «والله بما تعملون بصير».'),
(5202, 64, 3, '«خلق السماوات والأرض بالحق وصوركم فأحسن صوركم» إذ جعل شكل الآدمي أحسن الأشكال «وإليه المصير».'),
(5203, 64, 4, '«يعلم ما في السماوات والأرض ويعلم ما تسرون وما تعلنون والله عليم بذات الصدور» بما فيها من الأسرار والمعتقدات.'),
(5204, 64, 5, '«ألم يأتكم» يا كفار مكة «نبأُ» خبر «الذين كفروا من قبل فذاقوا وبال أمرهم» عقوبة الكفر في الدنيا «ولهم» في الآخرة «عذاب أليم» مؤلم.'),
(5205, 64, 6, '«ذلك» أي عذاب الدنيا «بأنه» ضمير الشأن «كانت تأتيهم رسلهم بالبينات» الحجج الظاهرات على الإيمان «فقالوا أبَشَرٌ» أريد به الجنس «يهدوننا فكفروا وتولوْا» عن الإيمان «واستغنى الله» عن إيمانهم «والله غني» عن خلقه «حميد» محمود في أفعاله.'),
(5206, 64, 7, '«زعم الذين كفروا أن» مخففة واسمها محذوف، أي أنهم «لن يبعثوا قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير».'),
(5207, 64, 8, '«فآمنوا بالله ورسوله والنور» القرآن «الذي أنزلنا والله بما تعملون خبير».'),
(5208, 64, 9, 'اذكر «يوم يجمعكم ليوم الجمع» يوم القيامة «ذلك يوم التغابن» يغبن المؤمنون الكافرين بأخذ منازلهم وأهليهم في الجنة لو آمنوا «ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله» وفي قراءة بالنون في الفعلين «جنات تجري من تحتها الأنهار خالدين فيها أبدا ذلك الفوز العظيم».'),
(5209, 64, 10, '«والذين كفروا وكذبوا بآياتنا» القرآن «أولئك أصحاب النار خالدين فيها وبئس المصير» هي.'),
(5210, 64, 11, '«ما أصاب من مصيبة إلا بإذن الله» بقضائه «ومن يؤمن بالله» في قوله إن المصيبة بقضائه «يهد قلبه» للصبر عليها «والله بكل شىء عليم».'),
(5211, 64, 12, '«وأطيعوا الله وأطيعوا الرسول فإن توليتم فإنما على رسولنا البلاغ المبين» البيَّن.'),
(5212, 64, 13, '«الله لا إله إلا هو وعلى الله فليتوكل المؤمنون».'),
(5213, 64, 14, '«يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوّا لكم فاحذروهم» أن تطيعوهم في التخلف عن الخير كالجهاد والهجرة فإن سبب نزول الآية الإطاعة في ذلك «وإن تعفوا» عنهم في تثبيطهم إياكم عن ذلك الخير معتلين بمشقة فراقكم عليهم «وتصفحوا وتغفروا فإن الله غفور رحيم».'),
(5214, 64, 15, '«إنما أموالكم وأولادكم فتنة» لكم شاغلة عن أمور الآخرة «والله عنده أجر عظيم» فلا تفوتوه باشتغالكم بالأموال والأولاد.'),
(5215, 64, 16, '«فاتقوا الله ما استطعتم» ناسخة لقوله (اتقوا الله حق تقاته) «واسمعوا» ما أُمرتم به سماع قبول «وأطيعوا وأنفقوا» في الطاعة «خيرا لأنفسكم» خبر يكن مقدرة جواب الأمر «ومن يوق شح نفسه فأولئك هم المفلحون» الفائزون.'),
(5216, 64, 17, '«إن تقرضوا الله قرضا حسنا» بأن تتصدقوا عن طيب قلب «يضاعفه لكم» وفي قراءة يضعفه بالتشديد بالواحدة عشرا إلى سبعمائة وأكثر «ويغفر لكم» ما يشاء «والله شكور» مجاز على الطاعة «حليم» في العقاب على المعصية.'),
(5217, 64, 18, '«عالم الغيب» السر «والشهادة» العلانية «العزيز» في ملكه «الحكيم» في صنعه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, '«يا أيها النبي» المراد أمته بقرينه ما بعده أو قل لهم «إذا طلقتم النساء» أي أردتم الطلاق «فطلقوهن لعدتهن» لأولها بأن يكون الطلاق في طهر لم تمس فيه لتفسيره صلي الله عليه وسلم بذلك، رواه الشيخان «وأحصوا العدة» أحفظوها لتراجعوا قبل فراغها «واتقوا الله ربكم» أطيعوه في أمره ونهيه «لا تخرجوهن من بيوتهن ولا يخرجن» منها حتى تنقضي عدتهن «إلا إن يأتين بفاحشة» زنا «مبيَّنة» بفتح الياء وكسرها، بينت أو بينة فيخرجن لإقامة الحد عليهن «وتلك» المذكورات «حدود الله ومن يتعدَّ حدود الله فقد ظلم نفسه لا تدري لعل الله يحدث بعد ذلك» الطلاق «أمراً» مراجعة فيما إذا كان واحدة أو اثنتين.'),
(5219, 65, 2, '«فإذا بلغن أجلهن» قاربن انقضاء عدتهن «فأمسكوهن» بأن تراجعوهن «بمعروف» من غير ضرار «أو فارقوهن بمعروف» أتركوهن حتى تنقضي عدتهن ولا تضاروهن بالمراجعة «وأشهدوا ذوي عدلٍ منكم» على المراجعة أو الفراق «وأقيموا الشهادة لله» لا للمشهود عليه أو له «ذلكم يوعظ به من كان يؤمن بالله واليوم الآخر ومن يتق الله يجعل له مخرجاً» من كرب الدنيا والآخرة.'),
(5220, 65, 3, '«ويرزقه من حيث لا يحتسب» يخطر بباله «ومن يتوكل على الله» في أموره «فهو حسبه» كافية «إن الله بالغٌ أمرَه» مراده وفي قراءة بالإضافة «قد جعل الله لكل شيء» كرخاء وشدة «قدرا» ميقاتا.'),
(5221, 65, 4, '(واللائي) بهمزة وياء وبلا ياء في الموضعين (يئسن من المحيض) بمعنى الحيض (من نسائكم إن ارتبتم) شككتم في عدتهن (فعدتهن ثلاثة أشهر واللائي لم يحضن) لصغرهن فعدتهن ثلاثة أشهر والمسألتان في غير المتوفي عنهن أزواجهن أما هن فعدتهن ما في آية "" يتربصن بأنفسهن أربعة أشهر وعشرا "" (وأولات الأحمال أجلهن) انقضاء عدتهن مطلقات أو متوفى عنهن أزواجهن (أن يضعن حملهن ومن يتق الله يجعل له من أمره يسرا) في الدنيا والآخرة.'),
(5222, 65, 5, '«ذلك» المذكور في العدة «أمر الله» حكمه «أنزله إليكم ومن يتق الله يكفر عنه سيئاته ويعظم له أجرا».'),
(5223, 65, 6, '«أسكنوهن» أي المطلقات «من حيث سكنتم» أي بعض مساكنكم «من وجدكم» أي سعتكم عطف بيان أو بدل مما قبله بإعادة الجار وتقدير مضاف، أي أمكنة سعتكم لا ما دونها «ولا تضاروهن لتضيقوا عليهن» المساكن فيحتجن إلى الخروج أو النفقة فيفتدين منكم «وإن كن أولات حمل فأنفقوا عليهن حتى يضعن حملهن فإن أرضعن لكم» أولادكم منهن «فآتوهن أجورهن» على الإرضاع «وأتمروا بينكم» وبينهن «بمعروف» بجميل في حق الأولاد بالتوافق على أجر معلوم على الإرضاع «وإن تعاسرتم» تضايقتم في الإرضاع فامتنع الأب من الأجرة والأم من فعله «فسترضع له» للأب «أخرى» ولا تكره الأم على إرضاعه.'),
(5224, 65, 7, '«لينفق» على المطلقات والمرضعات «ذو سعة من سعته ومن قدر» ضيق «عليه رزقه فلينفق مما آتاه» أعطاه «الله» على قدره «لا يكلف الله نفسا إلا ما آتاها سيجعل الله بعد عسر يسرا» وقد جعله بالفتوح.'),
(5225, 65, 8, '«وكأين» هي كاف الجر دخلت على أي بمعنى كم «من قرية» أي وكثير من القرى «عتت» عصت يعني أهلها «عن أمر ربها ورسله فحاسبناها» في الآخرة وإن لم تجيء لتحقيق وقوعها «حسابا شديدا وعذبناها عذابا نكرا» بسكون الكاف وضمها فظيعا وهو عذاب النار.'),
(5226, 65, 9, '«فذاقت وبال أمرها» عقوبته «وكان عاقبة أمرها خسرا» خسارا وهلاكا.'),
(5227, 65, 10, '«أعد الله لهم عذابا شديدا» تكرير الوعيد توكيد «فاتقوا الله يا أولي الألباب» أصحاب العقول «الذين آمنوا» نعت للمنادى أو بيان له «قد أنزل الله إليكم ذكرا» هو القرآن.'),
(5228, 65, 11, '«رسولا» أي محمدا صلى الله عليه وسلم منصوب بفعل مقدر، أي وأرسل «يتلو عليكم آيات الله مبيَّنات» بفتح الياء وكسرها كما تقدم «ليخرج الذين آمنوا وعملوا الصالحات» بعد مجيء الذكر والرسول «من الظلمات» الكفر الذي كانوا عليه «إلى النور» الإيمان الذي قام بهم بعد الكفر» ومن يؤمن بالله ويعمل صالحا يدخله» وفي قراءة بالنون «جنات تجري من تحتها الأنهار خالدين فيها أبدا قد أحسن الله له رزقا» هو رزق الجنة التي لا ينقطع نعيمها.'),
(5229, 65, 12, '«الله الذي خلق سبع سماوات ومن الأرض مثلهن» يعني سبع أرضين «يتنزل الأمر» الوحي «بينهن» بين السماوات والأرض ينزل به جبريل من السماء السابعة إلى الأرض السابعة «لتعلموا» متعلق بمحذوف، أي أعلمكم بذلك الخلق والتنزيل «أن الله على كل شيء قدير وأن الله قد أحاط بكل شيء علما».');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, '«يا أيها النبي لم تحرم ما أحل الله لك» من أمَتِكَ مارية القبطية لما واقعها في بيت حفصة وكانت غائبة فجاءت وشق عليها كون ذلك في بيتها وعلى فراشها حيث قلت: هي حرام عليَّ «تبتغي» بتحريمها «مرضات أزواجك» أي رضاهن «والله غفور رحيم» غفر لك هذا التحريم.'),
(5231, 66, 2, '(قد فرض الله) شرع (لكم تحلة أيمانكم) تحليلها بالكفارة المذكورة في سورة "" المائدة "" ومن الإيمان تحريم الأمة وهل كفّر صلى الله عليه وسلم؟ قال مقاتل: أعتق رقبة في تحريم مارية، وقال الحسن: لم يكفّر لأنه صلى الله عليه وسلم مغفور له (والله مولاكم) ناصركم (وهو العليم الحكيم).'),
(5232, 66, 3, '«و» اذكر «إذ أسرَّ النبي إلى بعض أزواجه» هي حفصة «حديثا» هو تحريم مارية وقال لها لا تفشيه «فلما نبأت به» عائشة ظنا منها أن لا حرج في ذلك «وأظهره الله» أطلعه «عليه» على المنبأ به «عرَّف بعضه» لحفصة «وأعرض عن بعض» تكرما منه «فلما نبأها به قالت من أنبأك هذا قال نبأني العليم الخبير» أي الله.'),
(5233, 66, 4, '«إن تتوبا» أي حفصة وعائشة «إلى الله فقد صغت قلوبكما» مالت إلى تحريم مارية، أي سركما ذلك مع كراهة النبي صلى الله عليه وسلم له وذلك ذنب، وجواب الشرط محذوف أي تقبلا، وأطلق قلوب على قلبين ولم يعبر به لاستثقال الجمع بين تثنيتين فيما هو كالكلمة الواحدة «وإن تظَّاهرا» بإدغام التاء الثانية في الأصل في الظاء، وفي قراءة بدونها تتعاونا «عليه» أي النبي فيما يكرهه «فإن الله هو» فصل «مولاه» ناصره «وجبريل وصالح المؤمنين» أبو بكر وعمر رضي الله عنهما معطوف على محل اسم إن فيكونون ناصريه «والملائكة بعد ذلك» بعد نصر الله والمذكورين «ظهير» ظهراء أعوان له في نصره عليكما.'),
(5234, 66, 5, '«عسى ربه إن طلقكن» أي طلق النبي أزواجه «أن يبدِّله» بالتشديد والتخفيف «أزواجا خيرا منكن» خبر عسى والجملة جواب الشرط ولم يقع التبديل لعدم وقوع الشرط «مسلمات» مقرات بالإسلام «مؤمنات» مخلصات «قانتات» مطيعات «تائبات عابدات سائحات» صائمات أو مهاجرات «ثيبات وأبكارا».'),
(5235, 66, 6, '«‌يا أيها الذين آمنوا قوا أنفسكم وأهليكم» بالحمل على طاعة الله «نارًا وقودها الناس» الكفار «والحجارة» كأصنامهم منها، يعني أنها مفرطة الحرارة تتقد بما ذكر لا كنار الدنيا تتقد بالحطب ونحوه «عليها ملائكة» خزنتها عدتهم تسعة عشر كما سيأتي في (المدثر) «غلاظ» من غلظ القلب «شداد» في البطش «لا يعصون الله ما أمرهم» بدل من الجلالة، أي لا يعصون أمر الله «ويفعلون ما يؤمرون» تأكيد والآية تخويف للمؤمنين عن الارتداد وللمنافقين المؤمنين بألسنتهم دون قلوبهم.'),
(5236, 66, 7, '«يا أيها الذين كفروا لا تعتذروا اليوم» يقال لهم ذلك عند دخولهم النار، أي لأنه لا ينفعكم «إنما تجزون ما كنتم تعملون» أي جزاءه.'),
(5237, 66, 8, '«يا أيها الذين آمنوا توبا إلى الله توبة نصوحا» بفتح النون وضمها صادقة، بأن لا يعاد إلى الذنب ولا يُراد العود إليه «عسى ربكم» ترجية تقع «أن يكفر عنكم سيئاتكم ويدخلكم جنات» بساتين «تجري من تحتها الأنهار يوم لا يجزي الله» بإدخال النار «النبي والذين آمنوا معه نورهم يسعى بين أيديهم» أمامهم «و» يكون «بأيمانهم يقولون» مستأنف «ربنا أتمم لنا نورنا» إلى الجنة والمنافقون يطفأ نورهم «واغفر لنا» ربنا «إنك على كل شيءٍ قدير».'),
(5238, 66, 9, '«يا أيها النبي جاهد الكفار» بالسيف «والمنافقين» باللسان والحجة «واغلظ عليهم» بالانتهار والمقت «ومأواهم جهنم وبئس المصير» هي.'),
(5239, 66, 10, '«ضرب الله مثلا للذين كفروا امرأة نوح وامرأة لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما» في الدين إذ كفرتا وكانت امرأة نوح واسمها واهلة تقول لقومه: إنه مجنون، وامرأة لوط واسمها واعلة تدل قومه على أضيافه إذا نزلوا به ليلا بإيقاد النار ونهارا بالتدخين «فلم يغنيا» أي نوح ولوط «عنهما من الله» من عذابه «شيئا وقيل» لهما «ادخلا النار مع الداخلين» من كفار قوم نوح وقوم لوط.'),
(5240, 66, 11, '«وضرب الله مثلا للذين آمنوا امرأة فرعون» آمنت بموسى واسمها آسية فعذبها فرعون بأن أوتد يديها ورجليها وألقى على صدرها رحى عظيمة واستقبل بها الشمس فكانت إذا تفرق عنها من وكل بها ظللتها الملائكة «إذ قالت» في حال التعذيب «رب ابن لي عندك بيتا في الجنة» فكشف لها فرأته فسهل عليها التعذيب «ونجني من فرعون وعمله» وتعذيبه «ونجني من القوم الظالمين» أهل دينه فقبض الله روحها، وقال ابن كيسان: رفعت إلى الجنة حية فهي تأكل وتشرب.'),
(5241, 66, 12, '«ومريم» عطف على امرأة فرعون «ابنة عمران التي أحصنت فرجها» حفظته «فنفخنا فيه من روحنا» أي جبريل حيث نفخ في جيب درعها بخلق الله تعالى فعله الواصل إلى فرجها فحملت بعيسى «وصدقت بكلمات ربها» شرائعه «وكتبه» المنزلة «وكانت من القانتين» من القوم المطيعين.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, '«تبارك» تنزه عن صفات المحدثين «الذي بيده» في تصرفه «الملك» السلطان والقدرة «وهو على كل شيءٍ قدير».'),
(5243, 67, 2, '«الذي خلق الموت» في الدنيا «والحياة» في الآخرة أو هما في الدنيا فالنطفة تعرض لها الحياة وهي ما به الإحساس، والموت ضدها أو عدمها قولان، والخلق على الثاني بمعنى لتقدير «ليبلوكم» ليختبركم في الحياة «أيكم أحسن عملا» أطوع لله «وهو العزيز» في انتقامه ممن عصاه «الغفور» لمن تاب إليه.'),
(5244, 67, 3, '«الذي خلق سبع سماوات طباقا» بعضها فوق بعض من غير مماسة «ما ترى في خلق الرحمن» لهن أو لغيرهن «من تفاوت» تباين وعدم تناسب «فارجع البصر» أعده إلى السماء «هل ترى» فيها «من فطور» صدوع وشقوق.'),
(5245, 67, 4, '«ثم ارجع البصر كرتين» كرة بعد كرة «ينقلب» يرجع «إليك البصر خاسئا» ذليلا لعدم إدراك خلل «وهو حسير» منقطع عن رؤية خلل.'),
(5246, 67, 5, '«ولقد زيَّنا السماء الدنيا» القربى إلى الأرض «بمصابيح» بنجوم «وجعلناها رجوما» مراجم «للشياطين» إذا استرقوا السمع بأن ينفصل شهاب عن الكوكب كالقبس يؤخذ من النار فيقتل الجني أو يخبله لا أن الكوكب يزول عن مكانه «وأعتدنا لهم عذاب السعير» النار الموقدة.'),
(5247, 67, 6, '«وللذين كفروا بربهم عذاب جهنم وبئس المصير» هي.'),
(5248, 67, 7, '«إذا ألقوا فيها سمعوا لها شهيقا» صوتا منكرا كصوت الحمار «وهي تفور» تغلي.'),
(5249, 67, 8, '«تكاد تميز» وقرئ تتميز على الأصل تتقطع «من الغيظ» غضبا على الكافر «كلما ألقي فيها فوج» جماعة منهم «سألهم خزنتها» سؤال توبيخ «ألم يأتكم نذير» رسول ينذركم عذاب الله تعالى.'),
(5250, 67, 9, '«قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء إن» ما «أنتم إلا في ضلال كبير» يحتمل أن يكون من كلام الملائكة للكفار حين أخبروا بالتكذيب وأن يكون كلام الكفار للنذر.'),
(5251, 67, 10, '«وقالوا لو كنا نسمع» أي سماع تفهم «أو نعقل» أي عقل تفكر «ما كنا في أصحاب السعير».'),
(5252, 67, 11, '«فاعترفوا» حيث لا ينفع الأعتراف «بذنبهم» وهو تكذيب النذر «فسحقا» بسكون الحاء وضمها «لأصحاب السعير» فبعدا لهم عن رحمة الله.'),
(5253, 67, 12, '«إن الذين يخشون ربهم» يخافونه «بالغيب» في غيبتهم عن أعين الناس فيطيعونه سرا فيكون علانية أولى «لهم مغفرة وأجر كبير» أي الجنة.'),
(5254, 67, 13, '«وأسِرّوا» أيها الناس «قولكم أو اجهروا به إنه» تعالى «عليم بذات الصدور» بما فيها فكيف بما نطقتم به، وسبب نزول ذلك أن المشركين قال بعضهم لبعض: أسرّوا قولكم لا يسمعكم إله محمد.'),
(5255, 67, 14, '«ألا يعلم من خلق» ما تسرون أي، أينتفي علمه بذلك «وهو اللطيف» في علمه «الخبير» فيه.'),
(5256, 67, 15, '«هو الذي جعل لكم الأرض ذلولا» سهلة للمشي فيها «فامشوا في مناكبها» جوانبها «وكلوا من رزقه» المخلوق لأجلكم «وإليه النشور» من القبور للجزاء.'),
(5257, 67, 16, '«أأمنتم» بتحقيق الهمزتين وتسهيل الثانية وإدخال ألف بينهما وبين الأخرى وتركه وإبدالها ألفا «من في السماء» سلطانه وقدرته «أن يخسف» بدل من مَن «بكم الأرض فإذا هي تمور» تتحرك بكم وترتفع فوقكم.'),
(5258, 67, 17, '«أم أمنتم من في السماء أن يرسل» بدل من مَن «عليكم حاصبا» ريحا ترميكم بالحصباء «فستعلمون» عند معاينة العذاب «كيف نذير» إنذاري بالعذاب، أي أنه حق.'),
(5259, 67, 18, '«ولقد كذب الذين من قبلهم» من الأمم «فكيف كان نكير» إنكاري عليهم بالتكذيب عند إهلاكهم، أي أنه حق.'),
(5260, 67, 19, '«أوَلم يروا» ينظروا «إلى الطير فوقهم» في الهواء «صافات» باسطات أجنحتهن «ويقبضن» أجنحتهن بعد البسط، أي وقابضات «ما يمسكهن» عن الوقوع في حال البسط والقبض «إلا الرحمن» بقدرته «إنه بكل شيء بصير» المعنى: ألم يستدلوا بثبوت الطير في الهواء على قدرتنا أن نفعل بهم ما تقدم وغيره من العذاب.'),
(5261, 67, 20, '«أمَّن» مبتدأ «هذا» خبره «الذي» بدل من هذا «هو جند» أعوان «لكم» صلة الذي «ينصركم» صفة الجند «من دون الرحمن» أي غيره يدفع عنكم عذابه، أي لا ناصر لكم «إن» ما «الكافرون إلا في غرور» غرهم الشيطان بأن العذاب لا ينزل بهم.'),
(5262, 67, 21, '«أمَّن هذا الذي يرزقكم إن أمسك» الرحمن «رزقه» أي المطر عنكم وجواب الشرط محذوف دل عليه ما قبله، أي فمن يرزقكم، أي لا رازق لكم غيره «بل لجوا» تمادوا «في عتو» تكبر «ونفور» تباعد عن الحق.'),
(5263, 67, 22, '«أفمن يمشي مُكِبا» واقعا «على وجهه أهدى أمَّن يمشي سويا» معتدلا «على صراط» طريق «مستقيم» وخبر مَن الثانية محذوف دل عليه خبر الأولى، أي أهدى، والمثل في المؤمن والكافر أيهما على هدى.'),
(5264, 67, 23, '«قل هو الذي أنشأكم» خلقكم «وجعل لكم السمع والأبصار والأفئدة» القلوب «قليلا ما تشكرون» ما مزيدة والجملة مستأنفة مخبرة بقلة شكرهم جدا على هذه النعم.'),
(5265, 67, 24, '«قل هو الذي ذرأكم» خلقكم «في الأرض وإليه تحشرون» للحساب.'),
(5266, 67, 25, '«ويقولون» للمؤمنين «متى هذا الوعد» وعد الحشر «إن كنتم صادقين» فيه.'),
(5267, 67, 26, '«قل إنما العلم» بمجيئه «عند الله وإنما أنا نذير مبين» بيِّن الإنذار.'),
(5268, 67, 27, '«فلما رأوه» أي العذاب بعد الحشر «زلفة» قريبا «سيئت» اسودت «وجوه الذين كفروا وقيل» أي قال الخزنة لهم «هذا» أي العذاب «الذي كنتم به» بإنذاره «تدعون» أنكم لا تبعثون وهذه حكاية حال تأتي عبر عنها بطريق المضي لتحقق وقوعها.'),
(5269, 67, 28, '«قل أرأيتم إن أهلكني الله ومن معي» من المؤمنين بعذابه كما تقصدون «أو رحمنا» فلم يعذبنا «فمن يجير الكافرين من عذاب أليم» أي لا مجير لهم منه.'),
(5270, 67, 29, '«قل هو الرحمن آمنا به وعليه توكلنا فستعلمون» بالتاء والياء عند معاينة العذاب «من هو في ضلال مبين» بيِّن أنحن أم أنتم أم هم.'),
(5271, 67, 30, '(قل أرأيتم إن أصبح ماؤكم غورا) غائرا في الأرض (فمن يأتيكم بماء معين) جار تناله الأيدي والدلاء كمائكم، أي لا يأتي به إلا الله تعالى فكيف تنكرون أن يبعثكم؟ ويستحب أن يقول القارئ عقب "" معين "": الله رب العالمين، كما ورد في الحديث، وتليت هذه الآية عند بعض المتجبرين فقال: تأتي به الفؤوس والمعاول فذهب ماء عينه وعمي نعوذ بالله من الجراءة على الله وعلى آياته.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, '«ن» أحد حروف الهجاء الله أعلم بمراده به «والقلم» الذي كتب به الكائنات في اللوح المحفوظ «وما يسطرون» أي الملائكة من الخير والصلاح.'),
(5273, 68, 2, '«ما أنت» يا محمد «بنعمة ربك بمجنون» أي انتفى الجنون عنك بسبب إنعام ربك عليك بالنبوة وغيرها وهذا رد لقولهم إنه مجنون.'),
(5274, 68, 3, '«وإن لك لأجرا غير ممنون» مقطوع.'),
(5275, 68, 4, '«وإنك لعلى خلق» دين «عظيم».'),
(5276, 68, 5, '«فستبصر ويبصرون».'),
(5277, 68, 6, '«بأيكم المفتون» مصدر كالمعقول، أي الفتون بمعنى الجنون، أي أبك أم بهم.'),
(5278, 68, 7, '«إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين» له وأعلم بمعنى عالم.'),
(5279, 68, 8, '«فلا تطع المكذبين».'),
(5280, 68, 9, '«ودوا» تمنوا «لو» مصدرية «تدهن» تلين لهم «فيدهنون» يلينون لك وهو معطوف على تدهن، وإن جعل جواب التمني المفهوم من ودوا قدر قبله بعد الفاء هم.'),
(5281, 68, 10, '«ولا تطع كل حلاف» كثير الحلف بالباطل «مهين» حقير.'),
(5282, 68, 11, '«هماز» غياب أي مغتاب «مشاء بنميم» ساع بالكلام بين الناس على وجه الإفساد بينهم.'),
(5283, 68, 12, '«مناع للخير» بخيل بالمال عن الحقوق «معتد» ظالم «أثيم» آثم.'),
(5284, 68, 13, '«عتل» غليظ جاف «بعد ذلك زنيم» دعيٍّ في قريش، وهو الوليد بن المغيرة ادَّعاه أبوه بعد ثماني عشرة سنة، قال ابن عباس: لا نعلم أن الله وصف أحدا بما وصفه به من العيوب فألحق به عارا لا يفارقه أبدا، وتعلق بزنيم الظرف قبله.'),
(5285, 68, 14, '«أن كان ذا مال وبنين» أي لأن وهو متعلق بما دل عليه.'),
(5286, 68, 15, '«إذ تتلى عليه آياتنا» القرآن «قال» هي «أساطير الأولين» أي كذب بها لإنعامنا عليه بما ذكر، وفي قراءة أأن بهمزتين مفتوحتين.'),
(5287, 68, 16, '«سنسمه على الخرطوم» سنجعل على أنفه علامة يعير بها ما عاش فخطم أنفه بالسيف يوم بدر.'),
(5288, 68, 17, '«إنا بلوناهم» امتحنا أهل مكة بالقحط والجوع «كما بلونا أصحاب الجنة» البستان «إذ أقسموا ليصرمنَّها» يقطعون ثمرتها «مصبحين» وقت الصباح كي لا يشعر بهم المساكين فلا يعطونهم منها ما كان أبوهم يتصدق به عليهم منها.'),
(5289, 68, 18, '«ولا يستثنون» في يمينهم بمشيئة الله تعالى والجملة مستأنفة، أي وشأنهم ذلك.'),
(5290, 68, 19, '«فطاف عليها طائف من ربك» نار أحرقتها ليلا «وهم نائمون».'),
(5291, 68, 20, '«فأصبحت كالصريم» كالليل الشديد الظلمة، أي سوداء.'),
(5292, 68, 21, '«فتنادوا مصبحين».'),
(5293, 68, 22, '«أن اغدوا على حرثكم» غلتكم تفسير لتنادوا، أو أن مصدرية أي بأن «إن كنتم صارمين» مريدين القطع وجواب الشرط دل عليه ما قبله.'),
(5294, 68, 23, '«فانطلقوا وهم يتخافتون» يتسارون.'),
(5295, 68, 24, '«أن لا يدخلنها اليوم عليكم مسكين» تفسير لما قبله، أو أن مصدرية أي بأن.'),
(5296, 68, 25, '«وغدوا على حرد» منع للفقراء «قادرين» عليه في ظنهم.'),
(5297, 68, 26, '«فلما رأوها» سوداء محترقة «قالوا إنا لضالون» عنها، أي ليست هذه ثم قالوا لما علموها:'),
(5298, 68, 27, '«بل نحن محرومون» ثمرتها بمنعنا الفقراء منها.'),
(5299, 68, 28, '«قال أوسطهم» خيرهم «ألم أقل لكم لولا» هلا «تسبحون» الله تائبين.'),
(5300, 68, 29, '«قالوا سبحان ربنا إنا كنا ظالمين» بمنع الفقراء حقهم.'),
(5301, 68, 30, '«فأقبل بعضهم على بعض يتلاومون».'),
(5302, 68, 31, '«قالوا يا» للتنبيه «ويلنا» هلا كنا «إنا كنا طاغين».'),
(5303, 68, 32, '«عسى ربنا أن يبدِّلنا» بالتشديد والتخفيف «خيرا منها إنا إلى ربنا راغبون» ليقبل توبتنا ويرد علينا خيرا من جنتنا، روي أنهم أُبدلوا خيرا منها.'),
(5304, 68, 33, '«كذلك» أي مثل العذاب لهؤلاء «العذاب» لمن خالف أمرنا من كفار مكة وغيرهم «ولعذاب الآخرة أكبر لو كانوا يعلمون» عذابها ما خالفوا أمرنا، ونزل لما قالوا إن بعثنا نعطى أفضل منكم:'),
(5305, 68, 34, '«إن للمتقين عند ربهم جنات النعيم».'),
(5306, 68, 35, '«أفنجعل المسلمين كالمجرمين» أي تابعين لهم في العطاء.'),
(5307, 68, 36, '«ما لكم كيف تحكمون» هذا الحكم الفاسد.'),
(5308, 68, 37, '«أم» أي بل أ «لكم كتاب» منزل «فيه تدرسون» أي تقرؤون.'),
(5309, 68, 38, '«إن لكم فيه لما تخيرون» تختارون.'),
(5310, 68, 39, '«أم لكم أَيمان» عهود «علينا بالغة» واثقة «إلى يوم القيامة» متعلق معنى بعلينا، وفي هذا الكلام معنى القسم، أي أقسمنا لكم وجوابه «إن لكم لما تحكمون» به لأنفسكم.'),
(5311, 68, 40, '«سلهم أيهم بذلك» الحكم الذي يحكمون به لأنفسهم من أنهم يعطون في الآخرة أفضل من المؤمنين «زعيم» كفيل لهم.'),
(5312, 68, 41, '«أم لهم» أي عندهم «شركاء» موافقون لهم في هذا القول يكفلون به لهم فإن كان كذلك «فليأتوا بشركائهم» الكافلين لهم به «إن كانوا صادقين».'),
(5313, 68, 42, 'اذكر «يوم يكشف عن ساق» هو عبارة عن شدة الأمر يوم القيامة للحساب والجزاء، يقال: كشفت الحرب عن ساق: إذا اشتد الأمر فيها «ويدعوْن إلى السجود» امتحاناً لإيمانهم «فلا يستطيعون» تصير ظهورهم طبقاً واحداً.'),
(5314, 68, 43, '«خاشعة» حال من ضمير يدعون، أي ذليلة «أبصارهم» لا يرفعونها «ترهقهم» تغشاهم «ذلة وقد كانوا يدعوْن» في الدنيا «إلى السجود وهم سالمون» فلا يأتون به بأن لا يصلوا.'),
(5315, 68, 44, '«فذرني» دعني «ومن يكذب بهذا الحديث» القرآن «سنستدرجهم» نأخذهم قليلا قليلا «من حيث لا يعلمون».'),
(5316, 68, 45, '«وأملي لهم» أمهلهم «إن كيدي متين» شديد لا يطاق.'),
(5317, 68, 46, '«أم» بل أ «تسألهم» على تبليغ الرسالة «أجرا فهم من مغرم» مما يعطونكه «مثقلون» فلا يؤمنون لذلك.'),
(5318, 68, 47, '«أم عندهم الغيب» أي اللوح المحفوظ الذي فيه الغيب «فهم يكتبون» منه ما يقولون.'),
(5319, 68, 48, '«فاصبر لحكم ربك» فيهم بما يشاء «ولا تكن كصاحب الحوت» في الضجر والعجلة وهو يونس عليه السلام «إذ نادى» دعا ربه «وهو مكظوم» مملوء غما في بطن الحوت.'),
(5320, 68, 49, '«لولا أن تداركه» أدركه «نعمة» رحمة «من ربه لنبذ» من بطن الحوت «بالعراء» بالأرض الفضاء «وهو مذموم» لكنه رحم فنبذ غير مذموم.'),
(5321, 68, 50, '«فاجتباه ربه» بالنبوة «فجعله من الصالحين» الأنبياء.'),
(5322, 68, 51, '«وإن يكاد الذين كفروا ليزلقونك» بضم الياء وفتحها «بأبصارهم» ينظرون إليك نظرا شديدا يكاد أن يصرعك ويسقطك من مكانك «لما سمعوا الذكر» القرآن «ويقولون» حسدا «إنه لمجنون» بسبب القرآن الذي جاء به.'),
(5323, 68, 52, '«وما هو» أي القرآن «إلا ذكر» موعظة «للعالمين» الجن والإنس لا يحدث بسبب جنون.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, '«الحاقة» القيامة التي يحق فيها ما أنكر من البعث والحساب والجزاء، أو المظهرة لذلك.'),
(5325, 69, 2, '«ما الحاقة» تعظيم لشأنها، وهو مبتدأ وخبر الحاقة.'),
(5326, 69, 3, '«وما أدراك» أعلمك «ما الحاقة» زيادة تعظيم لشأنها، فما الأولى مبتدأ وما بعدها خبره، وما الثانية وخبرها في محل المفعول الثاني لأدرى.'),
(5327, 69, 4, '«كذبت ثمود وعاد بالقارعة» القيامة لأنها تقرع القلوب بأهوالها.'),
(5328, 69, 5, '«فأما ثمود فأهلكوا بالطاغية» بالصيحة المجاوزة للحد في الشدة.'),
(5329, 69, 6, '«وأما عاد فأهلكوا بريح صرصر» شديدة الصوت «عاتية» قوية شديدة على عاد مع قوتهم وشدتهم.'),
(5330, 69, 7, '«سخرها» أرسلها بالقهر «عليهم سبع ليال وثمانية أيام» أولها من صبح يوم الأربعاء لثمان بقين من شوال، وكانت في عجز الشتاء «حسوما» متتابعات شبهت بتتابع فعل الحاسم في إعادة الكي على الداء كرة بعد أخرى حتى ينحسم «فترى القوم فيها صرعى» مطروحين هالكين «كأنهم أعجاز» أصول «نخل خاوية» ساقطة فارغة.'),
(5331, 69, 8, '«فهل ترى لهم من باقية» صفة نفس مقدرة أو التاء للمبالغة، أي باق؟ لا.'),
(5332, 69, 9, '«وجاء فرعون ومن قبَلَهُ» أتباعه، وفي قراءة بفتح القاف وسكون الباء، أي من تقدمه من الأمم الكافرة «والمؤتفكات» أي أهلها وهي قرى قوم لوط «بالخاطئة» بالفعلات ذات الخطأ.'),
(5333, 69, 10, '«فعصوْا رسول ربهم» أي لوطا وغيره «فأخذهم أخذة رابية» زائدة في الشدة على غيرها.'),
(5334, 69, 11, '«إنا لما طغا الماء» علا فوق كل شيء من الجبال وغيرها زمن الطوفان «حملناكم» يعني آباءكم إذ أنتم في أصلابهم «في الجارية» السفينة التي عملها نوح ونجا هو ومن كان معه فيها وغرق الاخرون.'),
(5335, 69, 12, '«لنجعلها» أي هذه الفعلة وهي إنجاء المؤمنين وإهلاك الكافرين «لكم تذكرة» عظة «وتعيها» ولتحفظها «أذن واعية» حافظة لما تسمع.'),
(5336, 69, 13, '«فإذا نفخ في الصور نفخة واحدة» للفصل بين الخلائق وهي الثانية.'),
(5337, 69, 14, '«وحُملت» رفعت «الأرض والجبال فدكتا» دقتا «دكة واحدة».'),
(5338, 69, 15, '«فيومئذ وقعت الواقعة» قامت القيامة.'),
(5339, 69, 16, '«وانشقت السماء فهي يومئذ واهية» ضعيفة.'),
(5340, 69, 17, '«والملك» يعني: الملائكة «على أرجائها» جوانب السماء «ويحمل عرش ربك فوقهم» أي الملائكة المذكورين «يومئذ ثمانية» من الملائكة أو من صفوفهم.'),
(5341, 69, 18, '«يومئذ تعرضون» للحساب «لا تخفى» بالتاء والياء «منكم خافية» من السرائر.'),
(5342, 69, 19, '«فأما من أوتيَ كتابه بيمينه فيقول» خطابا لجماعته لما سر به «هاؤمُ» خذوا «اقرؤوا كتابيه» تنازع فيه هاؤم واقرءُوا.'),
(5343, 69, 20, '«إني ظننت» تيقنت «أني ملاق حسابيه».'),
(5344, 69, 21, '«فهو في عيشة راضية» مرضية.'),
(5345, 69, 22, '«في جنة عالية».'),
(5346, 69, 23, '«قطوفها» ثمارها «دانية» قريبة يتناولها القائم والقاعد والمضطجع.'),
(5347, 69, 24, 'فيقال لهم «كلوا واشربوا هنيئا» حال، أي متهنئين «بما أسلفتم في الأيام الخالية» الماضية في الدنيا.'),
(5348, 69, 25, '«وأما من أوتيَ كتابه بشماله فيقول يا» للتنبيه «ليتني لم أوت كتابيه».'),
(5349, 69, 26, '«ولم أدر ما حسابيه».'),
(5350, 69, 27, '«يا ليتها» أي الموتة في الدنيا «كانت القاضية» القاطعة لحياتي بأن لا أبعث.'),
(5351, 69, 28, '«ما أغنى عني مالية».'),
(5352, 69, 29, '«هلك عني سلطانيه» قوتي وحجتي وهاء كتابيه وحسابيه وماليه وسلطانيه للسكت تثبت وقفا ووصلا اتباعا للمصحف الإمام والنقل، ومنهم من حذفها وصلا.'),
(5353, 69, 30, '«خذوه» خطاب لخزنة جهنم «فغلوه» اجمعوا يديه إلى عنقه في الغل.'),
(5354, 69, 31, '«ثم الجحيم» النار المحرقة «صلُّوه» ادخلوه.'),
(5355, 69, 32, '«ثم في سلسلة ذرعها سبعون ذراعا» بذراع الملك «فاسلكوه» أدخلوه فيها بعد إدخاله النار ولم تمنع الفاء من تعلق الفعل بالظرف المتقدم.'),
(5356, 69, 33, '«إنه كان لا يؤمن بالله العظيم».'),
(5357, 69, 34, '«ولا يحض على طعام المسكين».'),
(5358, 69, 35, '«فليس له اليوم ههنا حميم» قريب ينتفع به.'),
(5359, 69, 36, '«ولا طعام إلا من غسلين» صديد أهل النار أو شجر فيها.'),
(5360, 69, 37, '«لا يأكله إلا الخاطئون» الكافرون.'),
(5361, 69, 38, '«فلا» زائدة «أقسم بما تبصرون» من المخلوقات.'),
(5362, 69, 39, '«وما لا تبصرون» منها، أي بكل مخلوق.'),
(5363, 69, 40, '«إنه» أي القرآن «لقول رسول كريم» أي قاله رسالة عن الله تعالى.'),
(5364, 69, 41, '«وما هو بقول شاعر قليلا ما تؤمنون».'),
(5365, 69, 42, '«ولا بقول كاهن قليلا ما تذكرون» بالتاء والياء في الفعلين وما مزيدة مؤكدة والمعنى أنهم آمنوا بأشياء يسيرة وتذكروها مما أتى به النبي صلى الله عليه وسلم من الخير والصلة والعفاف فلم تغن عنهم شيئا.'),
(5366, 69, 43, 'بل هو «تنزيل من رب العالمين».'),
(5367, 69, 44, '«ولو تقوَّل» أي النبي «علينا بعض الأقاويل» بأن قال عنا ما لم نقله.'),
(5368, 69, 45, '«لأخذنا» لنلنا «منه» عقابا «باليمين» بالقوة والقدرة.'),
(5369, 69, 46, '«ثم لقطعنا منه الوتين» نياط القلب وهو عرق متصل به إذا انقطع مات صاحبه.'),
(5370, 69, 47, '«فما منكم من أحد» هو اسم ما ومن زائدة لتأكيد النفي ومنكم حال من أحد «عنه حاجزين» مانعين خبر ما وجمع لان أحدا في سياق النفي بمعنى الجمع وضمير عنه للنبي صلى الله عليه وسلم، أي لا مانع لنا عنه من حيث العقاب.'),
(5371, 69, 48, '«وإنه» أي القرآن «لتذكرة للمتقين».'),
(5372, 69, 49, '«وإنا لنعلم أن منكم» أيها الناس «مكذبين» بالقرآن ومصدقين.'),
(5373, 69, 50, '«وإنه» أي القرآن «لحسرة على الكافرين» إذا رأوا ثواب المصدقين وعقاب المكذبين به.'),
(5374, 69, 51, '«وإنه» أي القرآن «لحق اليقين» أي اليقين الحق.'),
(5375, 69, 52, '«فسبح» نزه «باسم» الباء زائدة «ربك العظيم» سبحانه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, '«سأل سائل» دعا داع «بعذاب واقع».'),
(5377, 70, 2, '(للكافرين ليس له دافع) هو النضر بن الحارث قال: "" اللهم إن كان هذا هو الحق "" الآية.'),
(5378, 70, 3, '«من الله» متصل بواقع «ذي المعارج» مصاعد الملائكة وهي السماوات.'),
(5379, 70, 4, '«تعرج» بالتاء والياء «الملائكة والروح» جبريل «إليه» إلى مهبط أمره من السماء «في يوم» متعلق بمحذوف، أي يقع العذاب بهم في يوم القيامة «كان مقداره خمسين ألف سنة» بالنسبة إلى الكافر لما يلقى فيه من الشدائد، وأما المؤمن فيكون أخف عليه من صلاة مكتوبة يصليها في الدنيا كما جاء في الحديث.'),
(5380, 70, 5, '«فاصبر» وهذا قبل أن يؤمر بالقتال «صبرا جميلا» أي لا جزع فيه.'),
(5381, 70, 6, '«إنهم يرونه» أي العذاب «بعيدا» غير واقع.'),
(5382, 70, 7, '«ونراه قريبا» واقعا لا محالة.'),
(5383, 70, 8, '«يوم تكون السماء» متعلق بمحذوف تقديره يقع «كالمهل» كذائب الفضة.'),
(5384, 70, 9, '«وتكون الجبال كالعهن» كالصوف في الخفة والطيران بالريح.'),
(5385, 70, 10, '«ولا يسأل حميم حميما» قريب قريبه لاشتغال كل بحاله.'),
(5386, 70, 11, '«يبصرونهم» أي يبصر الأحماء بعضهم بعضا ويتعارفون ولا يتكلمون والجملة مستأنفة «يود المجرم» يتمنى الكافر «لو» بمعنى أن «يفتدي من عذاب يومئذ» بكسر الميم وفتحها «ببنيه».'),
(5387, 70, 12, '«وصاحبته» زوجته «وأخيه».'),
(5388, 70, 13, '«وفصيلته» عشيرته لفصله منها «التي تؤويه» تضمه.'),
(5389, 70, 14, '«ومن في الأرض جميعا ثم ينجيه» ذلك الافتداء عطف على يفتدي.'),
(5390, 70, 15, '«كلا» رد لما يوده «إنها» أي النار «لظى» اسم لجهنم لأنها تتلظى، أي تتلهب على الكفار.'),
(5391, 70, 16, '«نزاعة للشوى» جمع شواة وهي جلدة الرأس.'),
(5392, 70, 17, '«تدعو من أدبر وتولى» عن الإيمان بأن تقول: إليَّ إليَّ.'),
(5393, 70, 18, '«وجمع» المال «فأوعي» أمسكه في وعائه ولم يؤد حق الله منه.'),
(5394, 70, 19, '«إن الإنسان خلق هلوعا» حال مقدرة وتفسيره.'),
(5395, 70, 20, '«إذا مسه الشر جزوعا» وقت مس الشر.'),
(5396, 70, 21, '«وإذا مسه الخير منوعا» وقت مس الخير أي المال لحق الله منه.'),
(5397, 70, 22, '«إلا المصلين» أي المؤمنين.'),
(5398, 70, 23, '«الذين هم على صلاتهم دائمون» مواظبون.'),
(5399, 70, 24, '«والذين في أموالهم حق معلوم» هو الزكاة.'),
(5400, 70, 25, '«للسائل والمحروم» المتعفف عن السؤال فيحرم.'),
(5401, 70, 26, '«والذين يُصدقون بيوم الدين» الجزاء.'),
(5402, 70, 27, '«والذين هم من عذاب ربهم مشفقون» خائفون.'),
(5403, 70, 28, '«إن عذاب ربهم غير مأمون» نزوله.'),
(5404, 70, 29, '«والذين هم لفروجهم حافظون».'),
(5405, 70, 30, '«إلا على أزواجهم أو ما ملكت أيمانهم» من الإماء «فإنهم غير ملومين».'),
(5406, 70, 31, '«فمن ابتغى وراء ذلك فأولئك هم العادون» المتجاوزون الحلال إلى الحرام.'),
(5407, 70, 32, '«والذين هم لأماناتهم» وفي قراءة بالإفراد: ما ائْتُمِنوا عليه من أمر الدين والدنيا «وعهدهم» المأخوذ عليهم في ذلك «راعون» حافظون.'),
(5408, 70, 33, '«والذين هم بشهادتهم» وفي قراءة بالجمع «قائمون» يقيمونها ولا يكتمونها.'),
(5409, 70, 34, '«والذين هم على صلاتهم يحافظون» بأدائها في أوقاتها.'),
(5410, 70, 35, '«أولئك في جنات مكرمون».'),
(5411, 70, 36, '«فمال الذين كفروا قبلك» نحوك «مهطعين» حال، أي مديمي النظر.'),
(5412, 70, 37, '«عن اليمين وعن الشمال» منك «عزين» حال أيضا، أي جماعات حلقا حلقا، يقولون استهزاء بالمؤمنين: لئن دخل هؤلاء الجنة لندخلنها قبلهم قال تعالى:'),
(5413, 70, 38, '«أيطمع كل امرىءٍ منهم أن يدخل جنة نعيم».'),
(5414, 70, 39, '«كلا» ردع لهم عن طمعهم في الجنة «إنا خلقناهم» كغيرهم «مما يعلمون» من نطف فلا يطمع بذلك في الجنة وإنما يطمع فيها بالتقوى.'),
(5415, 70, 40, '«فلا» لا زائدة «أقسم برب المشارق والمغارب» للشمس والقمر وسائر الكواكب «إنا لقادرون».'),
(5416, 70, 41, '«على أن نبدل» نأتي بدلهم «خيرا منهم وما نحن بمسبوقين» بعاجزين عن ذلك.'),
(5417, 70, 42, '«فذرهم» اتركهم «يخوضوا» في باطلهم «ويلعبوا» في دنياهم «حتى يلاقوا» يلقوا «يومهم الذي يوعدون» فيه العذاب.'),
(5418, 70, 43, '«يوم يخرجون من الأجداث» القبور «سراعا» إلى المحشر «كأنهم إلى نَصْبِ» وفي قراءة بضم الحرفين، شيء منصوب كعلم أو راية «يوفضون» يسرعون.'),
(5419, 70, 44, '«خاشعة» ذليلة «أبصارهم ترهقهم» تغشاهم «ذلة ذلك اليوم الذي كانوا يوعدون» ذلك مبتدأ وما بعده الخبر ومعناه يوم القيامة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, '«إنا أرسلنا نوحا إلى قومه أَن أَنذر» أي بإنذار «قومك من قبل أن يأتيهم» إن لم يؤمنوا «عذاب أَليم» مؤلم في الدنيا والآخرة.'),
(5421, 71, 2, '«قال يا قوم إني لكم نذير مبين» بيِّن الإنذار.'),
(5422, 71, 3, '«أن» أي بأن أقول لكم «اعبدوا الله واتقوه وأطيعون».'),
(5423, 71, 4, '«يغفر لكم من ذنوبكم» من زائدة فإن الإسلام يغفر به ما قبله، أو تبعيضية لإخراج حقوق العباد «ويؤخركم» بلا عذاب «إلى أجل مسمى» أجل الموت «إن أجل الله» بعذابكم إن لم تؤمنوا «إذا جاء لا يؤخر لو كنتم تعلمون» ذلك لآمنتم.'),
(5424, 71, 5, '«قال رب إني دعوت قومي ليلا ونهارا» أي دائما متصلا.'),
(5425, 71, 6, '«فلم يزدهم دعائي إلا فرارا» عن الإيمان.'),
(5426, 71, 7, '«وإني كلما دعوتهم لتغفر لهم جعلوا أصابعهم في آذانهم» لئلا يسمعوا كلامي «واستغشوا ثيابهم» غطوا رؤوسهم بها لئلا ينظروني «وأصروا» على كفرهم «واستكبروا» تكبروا عن الإيمان «استكبارا».'),
(5427, 71, 8, '«ثم إني دعوتهم جهارا» أي بأعلى صوتي.'),
(5428, 71, 9, '«ثم إني أَعلنت لهم» صوتي «وأسررت» الكلام «لهم إسرارا».'),
(5429, 71, 10, '«فقلت استغفروا ربكم» من الشرك «إنه كان غفارا».'),
(5430, 71, 11, '«يرسل السماء» المطر وكانوا قد منعوه «عليكم مدرارا» كثير الدرور.'),
(5431, 71, 12, '«ويمددكم بأموال وبنين ويجعل لكم جنات» بساتين «ويجعل لكم أنهارا» جارية.'),
(5432, 71, 13, '«ما لكم لا ترجون لله وقارا» أي تأملون وقار الله إياكم بأن تؤمنوا.'),
(5433, 71, 14, '«وقد خلقكم أطوارا» جمع طور وهو الحال، فطورا نطفة وطورا علقة إلى تمام خلق الإنسان، والنظر في خلقه يوجب الإيمان بخالقه.'),
(5434, 71, 15, '«ألم تروا» تنظروا «كيف خلق الله سبع سماوات طباقا» بعضها فوق بعض.'),
(5435, 71, 16, '«وجعل القمر فيهن» أي في مجموعهن الصادق بالسماء الدنيا «نورا وجعل الشمس سراجا» مصباحا مضيئا وهو أقوي من نور القمر.'),
(5436, 71, 17, '«والله أَنبتكم» خلقكم «من الأرض» إذ خلق أباكم آدم منها «نباتا».'),
(5437, 71, 18, '«ثم يعيدكم فيها» مقبورين «ويخرجكم» للبعث «إخراجا».'),
(5438, 71, 19, '«والله جعل لكم الأرض بساطا» مبسوطة.'),
(5439, 71, 20, '«لتسلكوا منها سبلا» طرقا «فجاجا» واسعة.'),
(5440, 71, 21, '«قال نوح رب إنهم عصوني واتبعوا» أي السفلة والفقراء «من لم يزده ماله وَوُلْدُهُ» وهم الرؤساء المنعم عليهم بذلك، وولد بضم الواو وسكون اللام وبفتحهما، والأول قيل جمع ولد بفتحهما كخشب وخشب وقيل بمعناه كبخل وبخل «إلا خسارا» طغيانا وكفرا.'),
(5441, 71, 22, '«ومكروا» أي الرؤساء «مكرا كبَّارا» عظيما جدا بأن كذبوا نوحا وآذوه ومن اتبعه.'),
(5442, 71, 23, '«وقالوا» للسفلة «لا تَذَرُنَّ آلهتكم ولا تذرن ودا» بفتح الواو وضمها «ولا سواعا ولا يغوث ويعوق ونسرا» هي أسماء أصنامهم.'),
(5443, 71, 24, '«وقد أضلوا» بها «كثيرا» من الناس بأن أمروهم بعبادتهم «ولا تزد الظالمين إلا ضلالا» عطفا على قد أضلوا دعا عليهم لما أوحي إليه أنه لن يؤمن من قومك إلا من قد آمن.'),
(5444, 71, 25, '«مما» ما صلة «خطاياهم» وفي قراءة خطيئآتهم بالهمز «أُغرقوا» بالطوفان «فأُدْخِلوا نارا» عوقبوا بها عقب الإغراق تحت الماء «فلم يجدوا لهم من دون» أي غير «الله أنصارا» يمنعون عنهم العذاب.'),
(5445, 71, 26, '«وقال نوح رب لا تذر على الأرض من الكافرين ديَّارا» أي نازل دار، والمعنى أحدا.'),
(5446, 71, 27, '«إنك إن تذرهم يُضلوا عبادك ولا يلدوا إلا فاجرا كفارا» من يفجر ويكفر، قال ذلك لما تقدم من الإيحاء إليه.'),
(5447, 71, 28, '«رب اغفر لي ولوالديَّ» وكانا مؤمنين «ولمن دخل بيتي» منزلي أو مسجدي «مؤمنا وللمؤمنين والمؤمنات» إلى يوم القيامة «ولا تزد الظالمين إلا تبارا» هلاكا فأهلكوا.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, '«قل» يا محمد للناس «أُحي إليَّ» أي أخبرت بالوحي من الله تعالى «أنه» الضمير للشأن «استمع» لقراءتي «نفر من الجن» جن نصيبين وذلك في صلاة الصبح ببطن نخل، موضوع بين مكة والطائف، وهم الذين ذكروا في قوله تعالى (وإذ صرفنا إليك نفرا من الجن) الآية «فقالوا» لقومهم لما رجعوا إليهم «إنا سمعنا قرآنا عجب» يتعجب منه في فصاحته وغزارة معانيه وغير ذلك.'),
(5449, 72, 2, '«يهدي إلى الرشد» الإيمان والصواب «فآمنا به ولن نشرك» بعد اليوم «بربنا أحدا».'),
(5450, 72, 3, '«وأنه» الضمير للشأن فيه وفي الموضعين بعده «تعالى جد ربنا» تنزه جلاله وعظمته عما نُسب إليه «ما اتخذ صاحبة» زوجة «ولا ولدا».'),
(5451, 72, 4, '«وأنه كان يقول سفيهنا» جاهلنا «على الله شططا» غلوا في الكذب بوصفه بالصاحبة والولد.'),
(5452, 72, 5, '«وأنا ظننا أن» مخففة، أي أنه «لن تقول الإنس والجن على الله كذبا» بوصفه بذلك حتى تبينا كذبهم بذلك قال تعالى:'),
(5453, 72, 6, '«وأنه كان رجال من الإنس يعوذون» يستعيذون «برجال من الجن» حين ينزلون في سفرهم بمخوف فيقول كل رجل أعوذ بسيد هذا المكان من شر سفهائه «فزادوهم» بعوذهم بهم «رهقا» فقالوا سدنا الجن والإنس.'),
(5454, 72, 7, '«وأنهم» أي الجن «ظنوا كما ظننتم» يا إنس «أن» مخففة من الثقيلة، أي أنه «لن يبعث الله أحدا» بعد موته.'),
(5455, 72, 8, 'قال الجن «وأنا لمسنا السماء» رمنا استراق السمع «فوجدناها ملئت حرسا» من الملائكة «شديدا وشهبا» نجوما محرقة وذلك لما بعث النبي صلى الله عليه وسلم.'),
(5456, 72, 9, '«وأنا كنا» أي قبل مبعثه «نقعد منها مقاعد للسمع» أي نستمع «فمن يستمع الآن يجد له شهابا رصدا» أرصد له ليرمى به.'),
(5457, 72, 10, '«وأنا لا ندري أشر أُريد» بعد استراق السمع «بمن في الأرض أم أراد بهم ربهم رشدا» خيرا.'),
(5458, 72, 11, '«وأنا منا الصالحون» بعد استماع القرآن «ومنا دون ذلك» أي قوم غير صالحين «كنا طرائق قددا» فرقا مختلفين مسلمين وكافرين.'),
(5459, 72, 12, '«وأنا ظننا أن» مخففة من الثقيلة أي أنه «لن نعجز الله في الأرض ولن نعجزه هربا» لا نفوته كائنين في الأرض أو هاربين منها في السماء.'),
(5460, 72, 13, '«وأنا لما سمعنا الهدى» القرآن «آمنا به فمن يؤمن بربه فلا يخاف» بتقدير هو «بخسا» نقصا من حسناته «ولا رهقا» ظلما بالزيادة في سيئاته.'),
(5461, 72, 14, '«وأنا منا المسلمون ومنا القاسطون» الجائرون بكفرهم «فمن أسلم فأولئك تحروْا رشدا» قصدوا هداية.'),
(5462, 72, 15, '«وأما القاسطون فكانوا لجهنم حطبا» وقودا وأنا وأنهم وأنه في اثني عشر موضعا هي وأنه تعالى وأنا منا المسلمون وما بينهما بكسر الهمزة استئنافا وبفتحها بما يوجه به.'),
(5463, 72, 16, 'قال تعالى في كفار مكة «وأنْ» مخففة من الثقيلة واسمها محذوف، أي وأنهم وهو معطوف على أنه استمع «لو استقاموا على الطريقة» أي طريقة الإسلام «لأسقيناهم ماء غدقا» كثيرا من السماء وذلك بعد ما رفع المطر عنهم سبع سنين.'),
(5464, 72, 17, '«لنفتنهم» لنختبرهم «فيه» فنعلم كيف شكرهم علم ظهور «ومن يعرض عن ذكر ربه» القرآن «نسلكه» بالنون والياء ندخله «عذابا صعدا» شاقا.'),
(5465, 72, 18, '«وأن المساجد» مواضع الصلاة «لله فلا تدعوا» فيها «مع الله أحدا» بأن تشركوا كما كانت اليهود والنصارى إذا دخلوا كنائسهم وبيعهم أشركوا.'),
(5466, 72, 19, '«وأنه» بالفتح والكسر استئنافا والضمير للشأن «لما قام عبد الله» محمد النبي صلى الله عليه وسلم «يدعوه» يعبده ببطن نخل «كادوا» أي الجن المستمعون لقراءته «يكونون عليه لبدا» بكسر اللام وضمها جمع لبدة كاللبد في ركوب بعضهم بعضا ازدحاما حرصا على سماع القرآن.'),
(5467, 72, 20, '«قال» مجيبا للكفار في قولهم ارجع عما أنت فيه وفي قراءة قل «إنما أدعو ربي» إلها «ولا أشرك به أحدا».'),
(5468, 72, 21, '«قل إني لا أملك لكم ضرا» غيا «ولا رشدا» خيرا.'),
(5469, 72, 22, '«قل إني لن يجيرني من الله» من عذابه إن عصيته «أحد ولن أجد من دونه» أي غيره «ملتحدا» ملتجأ.'),
(5470, 72, 23, '«إلا بلاغا» استثناء من مفعول أملك، أي لا أملك لكم إلا البلاغ إليكم «من الله» أي عنه «ورسالاته» عطف على بلاغا وما بين المستثنى منه والاستثناء اعتراض لتأكيد نفي الاستطاعة «ومن يعص الله رسوله» في التوحيد فلم يؤمن «فإن له نار جهنم خالدين» حال من ضمير من في له رعاية في معناها وهي حال مقدرة والمعنى يدخلونها مقدار خلودهم «فيها أبدا».'),
(5471, 72, 24, '«حتى إذا رأوْا» إبتدائية فيها معنى الغاية لمقدر قبلها أي لا يزالون على كفرهم إلى أن يروا «ما يوعدون» به من العذاب «فسيعلمون» عند حلوله بهم يوم بدر أو يوم القيامة «من أضعف ناصرا وأقل عددا» أعوانا أهم أم المؤمنون على القول الأول أو أنا أم هم على الثاني فقال بعضهم متى هذا الوعد؟ فنزل:'),
(5472, 72, 25, '«قل إن» أي ما «أدري أقريب ما توعدون»؟ من العذاب «أم يجعل له ربي أمدا» غاية وأجلا لا يعلمه إلا هو.'),
(5473, 72, 26, '«عالم الغيب» ما غاب عن العباد «فلا يظهر» يطلع «على غيبه أحدا» من الناس.'),
(5474, 72, 27, '«إلا من ارتضى من رسول فإنه» مع إطلاعه على ما شاء منه معجزة له «يسلك» يجعل ويسير «من بين يديه» أي الرسول «ومن خلفه رصدا» ملائكة يحفظونه حتى يبلغه في جملة الوحي.'),
(5475, 72, 28, '«ليعلم» الله علم ظهور «أن» مخففة من الثقيلة أي أنه «قد أبلغوا» أي الرسل «رسالات ربهم» روعي بجمع الضمير معنى من «وأحاط بما لديهم» عطف على مقدر، أي فعلم ذلك «وأحصى كل شيء عددا» تمييز وهو محول من المفعول والأصل أحصى عدد كل شيء.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, '«يا أيها المزمل» النبي وأصله المتزمل أُدغمت التاء في الزاي، أي المتلفف بثيابه حين مجيء الوحي له خوفا من لهيبته.'),
(5477, 73, 2, '«قم الليل» صل «إلا قليلا».'),
(5478, 73, 3, '«نصفه» بدل من قليلا وقلَّته بالنظر إلى الكل «أو انقص منه» من النصف «قليلا» إلى الثلث.'),
(5479, 73, 4, '«أو زد عليه» إلى الثلثين وأو للتخيير «ورتل القرآن» تثبت في تلاوته «ترتيلا».'),
(5480, 73, 5, '«إنا سنلقي عليك قولا» قرآنا «ثقيلا» مهيبا أو شديدا لما فيه من التكاليف.'),
(5481, 73, 6, '«إن ناشئة الليل» القيام بعد النوم «هي اشد وطئا» موافقة السمع للقلب على تفهم القرآن «وأقوم قليلا» أبين قولا.'),
(5482, 73, 7, '«إن لك في النهار سبحا طويلا» تصرفا في إشغالك لا تفرغ فيه لتلاوة القرآن.'),
(5483, 73, 8, '«واذكر اسم ربك» أي قل بسم الله الرحمن الرحيم في ابتداء قراءتك «وتبتل» انقطع «إليه تبتيلا» مصدر بتلَ جيء به رعاية للفواصل وهو ملزوم التبتل.'),
(5484, 73, 9, 'هو «ربُّ المشرق والمغرب لا إله إلا هو فاتخذه وكيلا» موكلا له أمورك.'),
(5485, 73, 10, '«واصبر على ما يقولون» أي كفار مكة من أذاهم «واهجرهم هجرا جميلا» لا جزع فيه وهذا قبل الأمر بقتالهم.'),
(5486, 73, 11, '«وذرني» اتركني «والمكذبين» عطف على المفعول معه والمعنى أنا كافيكهم وهم صناديد قريش «أُولي النعمة» التنعم «ومهلهم قليلا» من الزمن فقتلوا بعد يسير منه ببدر.'),
(5487, 73, 12, '«إن لدينا أنكالا» قيودا ثقالا جمع نكل بكسر النون «وجحيما» نارا محرقة.'),
(5488, 73, 13, '«وطعاما ذا غصة» يغص به في الحلق وهو الزقوم أو الضريع أو الغسلين أو شوك من نار لا يخرج ولا ينزل «وعذابا أليما» مؤلما زيادة على ما ذكر لمن كذب النبي صلى الله عليه وسلم.'),
(5489, 73, 14, '«يوم ترجف» تزلزل «الأرض والجبال وكانت الجبال كثيبا» رملا مجتمعا «مهيلا» سائلا بعد اجتماعه وهو من هال يهيل وأصله مهيول استثقلت الضمة على الياء فنقلت إلى هاء وحذفت الواو ثاني الساكنين لزيادتها وقلبت الضمة كسرة لمجانسة الياء.'),
(5490, 73, 15, '«إنا أرسلنا إليكم» يا أهل مكة «رسولا» هو محمد صلى الله عليه وسلم «شاهدا عليكم» يوم القيامة بما يصدر منكم من العصيان «كما أرسلنا إلى فرعون رسولا» هو موسى عليه الصلاة والسلام.'),
(5491, 73, 16, '«فعصى فرعون الرسول فأخذناه أخذا وبيلا» شديدا.'),
(5492, 73, 17, '«فكيف تتقون إن كفرتم» في الدنيا «يوما» مفعول تتقون، أي عذابه بأيِّ حصن تتحصنون من عذاب يوم «يَجعل الولدان شيباً» جمع أشيب لشدة هوله وهو يوم القيامة والأصل في شين شيبا الضم وكسرت لمجانسة الياء ويقال في اليوم الشديد يوم يشيب نواصي الأطفال وهو مجاز ويجوز أن يكون المراد في الآية الحقيقة.'),
(5493, 73, 18, '«السماء منفطر» ذات انفطار، أي انشقاق «به» بذلك اليوم لشدته «كان وعده» تعالى بمجيء ذلك «مفعولا» أي هو كائن لا محالة.'),
(5494, 73, 19, '«إن هذه» الآيات المخوِّفة «تذكرة» عظة للخلق «فمن شاء اتخذ إلى ربه سبيلا» طريقا بالإيمان والطاعة.'),
(5495, 73, 20, '«إن ربك يعلم أنك تقوم أَدنى» أقل «من ثلثي الليل ونصفِه وثلثِه» بالجر عطف على ثلثي وبالنصب على أدنى وقيامه كذلك نحو ما أمر به أول السورة «وطائفة من الذين معك» عطف على ضمير تقوم وجاز من غير تأكيد للفصل وقيام طائفة من أصحابه كذلك للتأسي به ومنهم من كان لا يدري كم صلّى من الليل وكم بقي منه فكان يقوم الليل كله احتياطا فقاموا حتى انتفخت أقدامهم سنة أو أكثر فخفف عنهم قال تعالى: «والله يقدر» يحصي «الليل والنهار علم أن» مخففة من الثقيلة واسمها محذوف، أي أنه «لن تحصوه» أي الليل لتقوموا فيما يجب القيام فيه إلا بقيام جميعه وذلك يشق عليكم «فتاب عليكم» رجع بكم إلى التخفيف «فاقرؤوا ما تيسر من القرآن» في الصلاة بأن تصلوا ما تيسر «علم أن» مخففة من الثقيلة، أي أنه «سيكون منكم مرضى وآخرون يضربون في الأرض» يسافرون «يبتغون من فضل الله» يطلبون من رزقه بالتجارة وغيرها «وآخرون يقاتلون في سبيل الله» وكل من الفرق الثلاثة يشق عليهم ما ذكر في قيام الليل فخفف عنهم بقيام ما تيسر منه ثم نسخ ذلك بالصلوات الخمس «فاقرؤوا ما تيسر منه» كما تقدم «وأقيموا الصلاة» المفروضة «وآتوا الزكاة وأقرضوا الله» بأن تنفقوا ما سوى المفروض من المال في سبيل الخير «قرضا حسنا» عن طيب قلب «وما تقدموا لأنفسكم من خير تجدوه عند الله هو خيرا» مما خلفتم وهو فصل وما بعده وإن لم يكن معرفة يشبهها لامتناعه من التعريف «وأعظم أجرا واستغفروا الله إن الله غفور رحيم» للمؤمنين.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, '«يا أيها المدثر» النبي صلى الله عليه وسلم وأصله المتدثر أدغمت التاء في الدال، أي المتلفف بثيابه عند نزول الوحي عليه.'),
(5497, 74, 2, '«قم فأنذر» خوِّف أهل مكة النار إن لم يؤمنوا.'),
(5498, 74, 3, '«وربك فكبر» عظِّم عن إشراك المشركين.'),
(5499, 74, 4, '«وثيابك فطهر» عن النجاسة أو قصرها خلاف جر العرب ثيابهم خيلاء فربما أصابتها نجاسة.'),
(5500, 74, 5, '«والرُّجز» فسره النبي صلى الله عليه وسلم بالأوثان «فاهجر» أي دم على هجره.'),
(5501, 74, 6, '«ولا تمنن تستكثر» بالرفع حال، أي لا تعط شيئا لتطلب أكثر منه وهذا خاص به صلى الله عليه وسلم لأنه مأمور بأجمل الأخلاق وأشرف الآداب.'),
(5502, 74, 7, '«ولربك فاصبر» على الأوامر والنواهي.'),
(5503, 74, 8, '«فإذا نقر في الناقور» نفخ في الصور وهو القرن النفخة الثانية.'),
(5504, 74, 9, '«فذلك» أي وقت النقر «يومئذ» بدل مما قبله المبتدأ وبني لإضافته إلى غير متمكن وخبر المبتدأ «يوم عسير» والعامل في إذا ما دلت عليه الجملة اشتد الأمر.'),
(5505, 74, 10, '«على الكافرين غير يسير» فيه دلالة على أنه يسير على المؤمنين في عسره.'),
(5506, 74, 11, '«ذرني» اتركني «ومن خلقت» عطف على المفعول أو مفعول معه «وحيدا» حال من مَن أو من ضميره المحذوف من خلقت منفردا بلا أهل ولا مال هو الوليد بن المغيرة المخزومي.'),
(5507, 74, 12, '«وجعلت له مالا ممدودا» واسعا متصلا من الزروع والضروع والتجارة.'),
(5508, 74, 13, '«وبنين» عشرة أو أكثر «شهودا» يشهدون المحافل وتسمع شهاداتهم.'),
(5509, 74, 14, '«ومهدت» بسطت «له» في العيش والعمر والولد «تمهيدا».'),
(5510, 74, 15, '«ثم يطمع أن أزيد».'),
(5511, 74, 16, '«كلا» لا أزيده على ذلك «إنه كان لآياتنا» القرآن «عنيدا» معاندا.'),
(5512, 74, 17, '«سأرهقه» أكلفه «صعودا» مشقة من العذاب أو جبلا من نار يصعد فيه ثم يهوي أبدا.'),
(5513, 74, 18, '«إنه فكر» فيما يقول في القرآن الذي سمعه النبي صلى الله عليه وسلم «وقدر» في نفسه ذلك.'),
(5514, 74, 19, '«فقتل» لعن وعذب «كيف قدر» على أي حال كان تقديره.'),
(5515, 74, 20, '«ثم قتل كيف قدر».'),
(5516, 74, 21, '«ثم نظر» في وجوه قومه أو فيما يقدح به فيه.'),
(5517, 74, 22, '«ثم عبس» قبض وجهه وكلحه ضيقا بما يقول «وبسر» زاد في القبض والكلوح.'),
(5518, 74, 23, '«ثم أدبر» عن الإيمان «واستكبر» تكبر عن اتباع النبي صلى الله عليه وسلم.'),
(5519, 74, 24, '«فقال» فيما جاء به «إن» ما «هذا إلا سحر يؤثر» ينقل عن السحرة.'),
(5520, 74, 25, 'ما «إن هذا إلا قول البشر» كما قالوا إنما يعلمه بشر.'),
(5521, 74, 26, '«سأصليه» أُدخله «سقر» جهنم.'),
(5522, 74, 27, '«وما أدراك ما سقر» تعظيم شأنها.'),
(5523, 74, 28, '«لا تبقي ولا تذر» شيئا من لحم ولا عصب إلا أهلكته ثم يعود كما كان.'),
(5524, 74, 29, '«لواحة للبشر» محرقة لظاهر الجلد.'),
(5525, 74, 30, '«عليها تسعة عشر» ملكا خزنتها قال بعض الكفار وكان قويا شديد البأس أنا أكفيكم سبعة عشر واكفوني أنتم اثنين قال تعالى:'),
(5526, 74, 31, '«وما جعلنا أصحاب النار إلا ملائكة» أي فلا يطاقون كما يتوهمون «وما جعلنا عدتهم» ذلك «إلا فتنة» ضلالا «للذين كفروا» بأن يقولوا لم كانوا تسعة عشر «ليستيقن» ليستبين «الذين أوتوا الكتاب» أي اليهود صدق النبي صلى الله عليه وسلم في كونهم تسعة عشر الموافق لما في كتابهم «ويزداد الذين آمنوا» من أهل الكتاب «إيمانا» تصديقا لموافقته ما أتي به النبي صلى الله عليه وسلم لما في كتابهم «ولا يرتاب الذين أُوتوا الكتاب والمؤمنون» من غيرهم في عدد الملائكة «وليقول الذين في قلوبهم مرض» شك بالمدينة «والكافرون» بمكة «ماذا أراد الله بهذا» العدد «مثلا» سموه لغرابته بذلك وأعرب حالا «كذلك» أي مثل إضلال منكر هذا العدد وهدى مصدقه «يضل الله من يشاء ويهدي من يشاء وما يعلم جنود ربك» أي الملائكة في قوتهم وأعوانهم «إلا هو وما هي» أي سقر «إلا ذكرى للبشر».'),
(5527, 74, 32, '«كلا» استفتاح بمعنى ألا «والقمر».'),
(5528, 74, 33, '«والليل إذا» بفتح الذال «دبر» جاء بعد النهار وفي قراءة إذ دبر بسكون الذال بعدها همزة، أي مضى.'),
(5529, 74, 34, '«والصبح إذا أسفر» ظهر.'),
(5530, 74, 35, '«إنها» أي سقر «لإحدى الكبر» البلايا العظام.'),
(5531, 74, 36, '«نذيرا» حال من إحدى وذكر لأنها بمعنى العذاب «للبشر».'),
(5532, 74, 37, '«لمن شاء منكم» بدل من البشر «أن يتقدم» إلى الخير أو الجنة بالإيمان «أو يتأخر» إلى الشر أو النار بالكفر.'),
(5533, 74, 38, '«كل نفس بما كسبت رهينة» مرهونة مأخوذة بعملها في النار.'),
(5534, 74, 39, '«إلا أصحاب اليمين» وهم المؤمنون فناجون منها كائنون.'),
(5535, 74, 40, '«في جنات يتساءلون» بينهم.'),
(5536, 74, 41, '«عن المجرمين» وحالهم ويقولون لهم بعد إخراج الموحدين من النار.'),
(5537, 74, 42, '«ما سلككم» أدخلكم «في سقر».'),
(5538, 74, 43, '«قالوا لم نك من المصلين».'),
(5539, 74, 44, '«ولم نك نطعم المسكين».'),
(5540, 74, 45, '«وكنا نخوض» في الباطل «مع الخائضين».'),
(5541, 74, 46, '«وكنا نكذب بيوم الدين» البعث والجزاء.'),
(5542, 74, 47, '«حتى أتانا اليقين» الموت.'),
(5543, 74, 48, '«فما تنفعهم شفاعة الشافعين» من الملائكة والأنبياء والصالحين والمعنى لا شفاعة لهم.'),
(5544, 74, 49, '«فما» مبتدأ «لهم» خبره متعلق بمحذوف انتقل ضميره إليه «عن التذكرة معرضين» حال من الضمير والمعنى أي شيء حصل لهم في إعراضهم عن الاتعاظ.'),
(5545, 74, 50, '«كأنهم حمر مستنفرة» وحشية.'),
(5546, 74, 51, '«فرت من قسورة» أسد أي هربت منه أشد الهرب.'),
(5547, 74, 52, '«بل يريد كل امرىء منهم أن يؤتى صحفا منشرة» أي من الله تعالى باتباع النبي كما قالوا: لن نؤمن لك حتى تنزل علينا كتابا نقرؤه.'),
(5548, 74, 53, '«كلا» ردع عما أرادوه «بل لا يخافون الآخرة» أي عذابها.'),
(5549, 74, 54, '«كلا» استفتاح «إنه» أي القرآن «تذكرة» عظة.'),
(5550, 74, 55, '«فمن شاء ذكره» قرأه فاتعظ به.'),
(5551, 74, 56, '«وما يذكرون» بالياء والتاء «إلا أن يشاء الله هو أهل التقوى» بأن يتقى «وأهل المغفرة» بأن يغفر لمن اتقاه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, '«لا» زائدة في الموضعين «أقسم بيوم القيامة».'),
(5553, 75, 2, '«ولا أقسم بالنفس اللوامة» التي تلوم نفسها وإن اجتهدت في الإحسان وجواب القسم محذوف، أي لتبعثن، دل عليه:'),
(5554, 75, 3, '«أيحسب الإنسان» أي الكافر «ألن نجمع عظامه» للبعث والإحياء.'),
(5555, 75, 4, '«بلى» نجمعها «قادرين» مع جمعها «على أن نسوِّي بنانه» وهو الأصابع، أي نعيد عظامها كما كانت مع صغرها فكيف بالكبيرة.'),
(5556, 75, 5, '«بل يريد الإنسان ليفجر» اللام زائدة ونصبه بأن مقدرة، أي أن يكذب «أمامه» أي يوم القيامة، دل عليه.'),
(5557, 75, 6, '«يَسأَل أَيان» متى «يوم القيامة» سؤال استهزاء وتكذيب.'),
(5558, 75, 7, '«فإذا برق البصر» بكسر الراء وفتحها دهش وتحير لما رأى مما كان يكذبه.'),
(5559, 75, 8, '«وخسف القمر» أظلم وذهب ضوءه.'),
(5560, 75, 9, '«وجمع الشمس والقمر» فطلعا من المغرب أو ذهب ضوءهما وذلك في يوم القيامة.'),
(5561, 75, 10, '«يقول الإنسان يومئذ أين المفر» الفرار.'),
(5562, 75, 11, '«كلا» ردع عن طلب الفرار «لا وزر» لا ملجأ يتحصن به.'),
(5563, 75, 12, '«إلى ربك يومئذ المستقر» مستقر الخلائق فيحاسبون ويجازون.'),
(5564, 75, 13, '«ينبأ الإنسان يومئذ بما قدم وأخر» بأول عمله وآخره.'),
(5565, 75, 14, '«بل الإنسان على نفسه بصيرة» شاهد تنطق جوارحه بعمله والهاء للمبالغة فلا بد من جزائه.'),
(5566, 75, 15, '«ولو ألقى معاذيره» جمع معذرة على غير قياس، أي لو جاء بكل معذرة ما قُبلت منه.'),
(5567, 75, 16, 'قال تعالى لنبيه: «لا تحرك به» بالقرآن قبل فراغ جبريل منه «لسانك لتعجل به» خوف أن ينفلت منك.'),
(5568, 75, 17, '«إن علينا جمعه» في صدرك «وقرآنه» قراءتك إياه أي جريانه على لسانك.'),
(5569, 75, 18, '«فإذا قرأناه» عليك بقراءة جبريل «فاتبع قرآنه» استمع قراءته فكان صلى الله عليه وسلم يستمع ثم يقرؤه.'),
(5570, 75, 19, '«ثم إن علينا بيانه» بالتفهيم لك، والمناسبة بين هذه الآية وما قبلها أن تلك تضمنت الإعراض عن آيات الله وهذه تضمنت المبادرة إليها بحفظها.'),
(5571, 75, 20, '«كلا» استفتاح بمعنى ألا «بل يحبون العاجلة» الدنيا بالياء والتاء في الفعلين.'),
(5572, 75, 21, '«ويذرون الآخرة» فلا يعملون لها.'),
(5573, 75, 22, '«وجوه يومئذ» أي في يوم القيامة «ناضرة» حسنة مضيئة.'),
(5574, 75, 23, '«إلى ربها ناظرة» أي يرون الله سبحانه وتعالى في الآخرة.'),
(5575, 75, 24, '«ووجوه يومئذ باسرة» كالحة شديدة العبوس.'),
(5576, 75, 25, '«تظن» توقن «أن يُفعل بها فاقرة» داهية عظيمة تكسر فقار الظهر.'),
(5577, 75, 26, '«كلا» بمعنى ألا «إذا بلغت» النفس «التراقي» عظام الحلق.'),
(5578, 75, 27, '«وقيل» قال من حوله «من راق» يرقيه ليشفى.'),
(5579, 75, 28, '«وظن» أيقن من بلغت نفسه ذلك «أنه الفراق» فراق الدنيا.'),
(5580, 75, 29, '«والتفَّت الساق بالساق» أي إحدى ساقيه بالأخرى عند الموت، أو التفت شدة فراق الدنيا بشدة إقبال الآخرة.'),
(5581, 75, 30, '«إلى ربك يومئذ المساق» أي السوق وهذا يدل على العامل في إذا، والمعنى إذا بلغت النفس الحلقوم تساق إلى حكم ربها.'),
(5582, 75, 31, '«فلا صدق» الإنسان «ولا صلى» أي لم يصدق ولم يصلِّ.'),
(5583, 75, 32, '«ولكن كذب» بالقرآن «وتولى» عن الإيمان.'),
(5584, 75, 33, '«ثم ذهب إلى أهله يتمطى» يتبختر في مشيته إعجابا.'),
(5585, 75, 34, '«أوْلى لك» فيه التفات عن الغيبة والكلمة اسم فعل واللام للتبيين، أي وليك ما تكره «فأوْلى» أي فهو أولى بك من غيرك.'),
(5586, 75, 35, '«ثم أوْلى لك فأوْلى» تأكيد.'),
(5587, 75, 36, '«أيحسب» يظن «الإنسان أن يُترك سدى» هملا لا يكلف بالشرائع لا يحسب ذلك.'),
(5588, 75, 37, '«ألم يك» أي كان «نطفة من منيّ يمنى» بالياء والتاء تصب في الرحم.'),
(5589, 75, 38, '«ثم كان» المني «علقة فخلق» الله منها الإنسان «فسوى» عدل أعضاءه.'),
(5590, 75, 39, '«فجعل منه» من المني الذي صار علقة قطعة دم ثم مضغة أي قطعة لحم «الزوجين» النوعين «الذكر والأنثى» يجتمعان تارة وينفرد كل منهما عن الآخر تارة.'),
(5591, 75, 40, '«أَليس ذلك» الفعَّال لهذه الأشياء «بقادر على أن يحيي الموتى» قال صلى الله عليه وسلم: بلى.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, '«هل» قد «أتى على الإنسان» آدم «حين من الدهر» أربعون سنة «لم يكن» فيه «شيئا مذكورا» كان فيه مصورا من طين لا يذكر أو المراد بالإنسان الجنس وبالحين مدة الحمل.'),
(5593, 76, 2, '«إنَّا خلقنا الإنسان» الجنس «من نطفة أمشاج» أخلاط، أي من ماء الرجل وماء المرأة المختلطين الممتزجين «نبتليه» نختبره بالتكليف والجملة مستأنفة أو حال مقدرة، أي مريدين ابتلاءه حين تأهله «فجعلناه» بسبب ذلك «سميعا بصيرا».'),
(5594, 76, 3, '«إنا هديناه السبيل» بينا له طريق الهدى ببعث الرسل «إما شاكرا» أي مؤمنا «وإما كفورا» حالان من المفعول، أي بينا له في حال شكره أو كفره المقدرة، و إما لتفصيل الأحوال.'),
(5595, 76, 4, '«إنا أعتدنا» هيأنا «للكافرين سلاسل» يسحبون بها في النار «وأغلالا» في أعناقهم تشد فيها السلاسل «وسعيرا» نارا مسعرة، أي مهيجة يعذبون بها.'),
(5596, 76, 5, '«إن الأبرار» جمع بر أو بار وهم المطيعون «يشربون من كأس» هو إناء شرب الخمر وهي فيه والمراد من خمر تسمية للحال باسم المحل ومن للتبعيض «كان مزاجها» ما يمزج به «كافورا».'),
(5597, 76, 6, '«عينا» بدل من كافورا فيها رائحته «يشرب بها» منها «عباد الله» أولياؤه «يفجّرونها تفجيرا» يقودونها حيث شاءوا من منازلهم.'),
(5598, 76, 7, '«يوفون بالنذر» في طاعة الله «ويخافون يوما كان شره مستطيرا» منتشرا.'),
(5599, 76, 8, '«ويطعمون الطعام على حبه» أي الطعام وشهوتهم له «مسكينا» فقيرا «ويتيما» لا أب له «وأسيرا» يعني المحبوس بحق.'),
(5600, 76, 9, '«إنما نطعمكم لوجه الله» لطلب ثوابه «لا نريد منكم جزاء ولا شكورا» شكرا فيه علة الإطعام وهل تكلموا بذلك أو علمه الله منهم فأثنى عليهم به، قولان.'),
(5601, 76, 10, '«إنا نخاف من ربنا يوما عبوْسا» تكلح الوجوه فيه أي كريه المنظر لشدته «قمطريرا» شديدا في ذلك.'),
(5602, 76, 11, '«فوقاهم الله شر ذلك اليوم ولقاهم» أعطاهم «نضرة» حسنا وإضاءة في وجوههم «وسرورا».'),
(5603, 76, 12, '«وجزاهم بما صبروا» بصبرهم عن المعصية «جنة» أدخلوها «وحريرا» البسوه.'),
(5604, 76, 13, '«متكئين» حال من مرفوع أدخلوها المقدر «فيها على الأرائك» السرر في الحجال «لا يروْن» لا يجدون حال ثانية «فيها شمسا ولا زمهريرا» لا حرا ولا بردا وقيل الزمهرير القمر فهي مضيئة من غير شمس ولا قمر.'),
(5605, 76, 14, '«ودانية» قريبة عطف على محل لا يرون، أي غير رائين «عليهم» منهم «ظلالها» شجرها «وذللت قطوفها تذليلا» أُدنيت ثمارها فينالها القائم والقاعد والمضطجع.'),
(5606, 76, 15, '«ويطاف عليهم» فيها «بآنية من فضة وأكواب» أقداح بلا عرى «كانت قوارير».'),
(5607, 76, 16, '«قوارير من فضة» أي أنها من فضة يرى باطنها من ظاهرها كالزجاج «قدروها» أي الطائفون «تقديرا» على قدر ريِّ الشاربين من غير زيادة ولا نقص وذلك ألذ الشراب.'),
(5608, 76, 17, '«ويسقون فيها كأسا» خمرا «كان مزاجها» ما تمزج به «زنجبيلا».'),
(5609, 76, 18, '«عينا» بدل زنجبيلا «فيها تسمى سلسبيل» يعني أن ماءها كالزنجبيل الذي تستلذ به العرب سهل المساغ في الحلق.'),
(5610, 76, 19, '«ويطوف عليهم ولدان مخلدون» بصفة الولدان لا يشيبون «إذا رأيتهم حسبتهم» لحسنهم وانتشارهم في الخدمة «لؤلؤا منثورا» من سلكه أو من صدفه وهو أحسن منه في غير ذلك.'),
(5611, 76, 20, '«وإذا رأيت ثم» أي وجدت الرؤية منك في الجنة «رأيت» جواب إذا «نعيما» لا يوصف «وملكا كبيرا» واسعا لا غاية له.'),
(5612, 76, 21, '«عاليهم» فوقهم فنصبه على الظرفية وهو خبر لمبتدأ بعده وفي قراءة بسكون الياء مبتدأ وما بعده خبر والضمير المتصل به للمعطوف عليهم «ثياب سندس» حرير «خضر» بالرفع «وإستبرق» بالجر ما غلظ من الديباج فهو البطائن والسندس الظهائر وفي قراءة عكس ما ذكر فيهما وفي أخرى، برفعهما وفي أخرى بجرهما «وحلُّوا أساور من فضة» وفي موضع من ذهب للايذان بأنهم يحلون من النوعين معا ومفرقا «وسقاهم ربهم شرابا طهورا» مبالغة في طهارته ونظافته بخلاف خمر الدنيا.'),
(5613, 76, 22, '«إن هذا» النعيم «كان لكم جزاءً وكان سعيكم مشكورا».'),
(5614, 76, 23, '«إنا نحن» تأكيد لاسم إن أو فصل «نزلنا عليك القرآن تنزيلا» خبر إن أي فصلناه ولم ننزله جملة واحدة.'),
(5615, 76, 24, '«فاصبر لحكم ربك» عليك بتبليغ رسالته «ولا تطع منهم» أي الكفار «آثما أو كفورا» أي عتبة بن ربيعة والوليد بن المغيرة قالا للنبي صلى الله عليه وسلم ارجع عن هذا الأمر. ويجوز أن يراد كل آثم وكافر أي لا تطع أحدهما أَيا كان فيما دعاك إليه من إثم أو كفر.'),
(5616, 76, 25, '«واذكر اسم ربك» في الصلاة «بكرة وأصيلا» يعني الفجر والظهر والعصر.'),
(5617, 76, 26, '«ومن الليل فاسجد له» يعني المغرب والعشاء «وسبحه ليلا طويلا» صل التطوع فيه كما تقدم من ثلثيه أو نصفه أو ثلثه.'),
(5618, 76, 27, '«إن هؤلاء يحبون العاجلة» الدنيا «ويذرون وراءهم يوما ثقيلا» شديدا أي يوم القيامة لا يعملون له.'),
(5619, 76, 28, '«نحن خلقناهم وشددنا» قوينا «أسرهم» أعضاءهم ومفاصلهم «وإذا شئنا بدلنا» جعلنا «أمثالهم» في الخلقة بدلا منهم بأن نهلكهم «تبديلا» تأكيد ووقعت إذا موقع إن نحو إن يشأ يذهبكم لأنه تعالى لم يشأ ذلك وإذا لمّا يقع.'),
(5620, 76, 29, '«إن هذه» السورة «تذكرة» عظة للخلق «فمن شاء اتخذ إلى ربه سبيلا» طريقا بالطاعة.'),
(5621, 76, 30, '«وما تشاءون» بالتاء والياء اتخاذ السبيل بالطاعة «إلا أن يشاء الله» ذلك «إن الله كان عليما» بخلقه «حكيما» في فعله.'),
(5622, 76, 31, '«يُدخل من يشاء في رحمته» جنته وهم المؤمنون «والظالمين» ناصبه فعل مقدر، أي أعد يفسره «أَعد لهم عذابا أليما» مؤلماً وهم الكافرون.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, '«والمرسلات عُرفا» أي الرياح متتابعة كعرف الفرس يتلو بعضه بعضا ونصبه على الحال.'),
(5624, 77, 2, '«فالعاصفات عصفا» الرياح الشديدة.'),
(5625, 77, 3, '«والناشرات نشرا» الرياح تنشر المطر.'),
(5626, 77, 4, '«فالفارقات فرقا» أي آيات القرآن تفرق بين الحق والباطل والحلال والحرام.'),
(5627, 77, 5, '«فالملقيات ذكرا» أي الملائكة تنزل بالوحي إلى الأنبياء والرسل يلقون الوحي إلى الأمم.'),
(5628, 77, 6, '«عذْرا أو نذرا» أي للإعذار والإنذار من الله تعالى وفي قراءة بضم ذال نذرا وقرئ بضم ذال عذرا.'),
(5629, 77, 7, '«إنما توعدون» أي يا كفار مكة من البعث والعذاب «لواقع» كائن لا محالة.'),
(5630, 77, 8, '«فإذا النجوم طمست» محي نورها.'),
(5631, 77, 9, '«وإذا السماء فرجت» شقت.'),
(5632, 77, 10, '«وإذا الجبال نسفت» فتتت وسيرت.'),
(5633, 77, 11, '«وإذا الرسل أُقتت» بالواو وبالهمزة بدلا منها، أي جمعت لوقت.'),
(5634, 77, 12, '«لأي يوم» ليوم عظيم «أُجلت» للشهادة على أممهم بالتبليغ.'),
(5635, 77, 13, '«ليوم الفصل» بين الخلق ويؤخذ منه جواب إذا، أي وقع الفصل بين الخلائق.'),
(5636, 77, 14, '«وما أدراك ما يوم الفصل» تهويل لشأنه.'),
(5637, 77, 15, '«ويل يومئذ للمكذبين» هذا وعيد لهم.'),
(5638, 77, 16, '«ألم نهلك الأولين» بتكذيبهم، أي أهلكناهم.'),
(5639, 77, 17, '«ثم نتبعهم الآخرين» ممن كذبوا ككفار مكة فنهلكهم.'),
(5640, 77, 18, '«كذلك» مثل ما فعلنا بالمكذبين «نفعل بالمجرمين» بكل من أجرم فيما يستقبل فنهلكهم.'),
(5641, 77, 19, '«ويل يومئذ للمكذبين» تأكيد.'),
(5642, 77, 20, '«أَلم نخلقكم من ماء مهين» ضعيف وهو المني.'),
(5643, 77, 21, '«فجعلناه في قرار مكين» حريز وهو الرحم.'),
(5644, 77, 22, '«إلى قدر معلوم» وهو وقت الولادة.'),
(5645, 77, 23, '«فقَدرنا» على ذلك «فنعم القادرون» نحن.'),
(5646, 77, 24, '«ويل يومئذ للمكذبين».'),
(5647, 77, 25, '«ألم نجعل الأرض كفاتا» مصدر كفت بمعنى ضم، أي ضامة.'),
(5648, 77, 26, '«أحياءً» على ظهرها «وأمواتا» في بطنها.'),
(5649, 77, 27, '«وجعلنا فيها رواسيَ شامخات» جبالا مرتفعات «وأسقيناكم ماء فراتا» عذبا.'),
(5650, 77, 28, '«ويل يومئذ للمكذبين» ويقال للمكذبين يوم القيامة:'),
(5651, 77, 29, '«انطلقوا إلى ما كنتم به» من العذاب «تكذبون».'),
(5652, 77, 30, '«انطلقوا إلى ظل ذي ثلاث شعب» هو دخان جهنم إذا ارتفع افترق ثلاث فرق لعظمه.'),
(5653, 77, 31, '«لا ظليل» كنين يظلهم من حر ذلك اليوم «ولا يغني» يرد عنهم شيئا «من اللهب» النار.'),
(5654, 77, 32, '«إنها» أي النار «ترمي بشرر» هو ما تطاير منها «كالقصر» من البناء في عظمه وارتفاعه.'),
(5655, 77, 33, '(كأنه جمالات) جمالات جمع جمل وفي قراءة جمالت (صفر) في هيئتها ولونها وفي الحديث "" شرار الناس أسود كالقير "" والعرب تسمي سود الإبل صفرا لشوب سوادها بصفرة فقيل صفر في الآية بمعنى سود لما ذكر وقيل لا، والشرر: جمع شرارة، والقير: القار.'),
(5656, 77, 34, '«ويل يومئذ للمكذبين».'),
(5657, 77, 35, '«هذا» أي يوم القيامة «يوم لا ينطقون» فيه بشيء.'),
(5658, 77, 36, '«ولا يؤذن لهم» في العذر «فيعتذرون» عطف على يؤذن من غير تسبب عنه فهو داخل في حيز النفي، أي لا إذن فلا اعتذار.'),
(5659, 77, 37, '«ويل يومئذ للمكذبين».'),
(5660, 77, 38, '«هذا يوم الفصل جمعناكم» أيها المكذبون من هذه الأمة «والأولين» من المكذبين قبلكم فتحاسبون وتعذبون جميعا.'),
(5661, 77, 39, '«فإن كان لكم كيد» حيلة في دفع العذاب عنكم «فكيدون» فافعلوها.'),
(5662, 77, 40, '«ويل يومئذ للمكذبين».'),
(5663, 77, 41, '«إن المتقين في ظلال» أي تكاثف أشجار إذ لا شمس يظل من حرها «وعيون» نابعة من الماء.'),
(5664, 77, 42, '«وفواكه مما يشتهون» فيه إعلام بأن المأكل والمشرب في الجنة بحسب شهواتهم بخلاف الدنيا فبحسب ما يجد الناس في الأغلب ويقال لهم:'),
(5665, 77, 43, '«كلوا واشربوا هنيئا» حال، أي متهنئين «بما كنتم تعملون» من الطاعة.'),
(5666, 77, 44, '«إنا كذلك» كما جزينا المتقين «نجزي المحسنين».'),
(5667, 77, 45, '«ويل يومئذ للمكذبين».'),
(5668, 77, 46, '«كلوا وتمتعوا» خطاب للكفار في الدنيا «قليلا» من الزمان وغايته الموت، وفي هذا تهديد لهم «إنكم مجرمون».'),
(5669, 77, 47, '«ويل يومئذ للمكذبين».'),
(5670, 77, 48, '«وإذا قيل لهم اركعوا» صلوا «لا يركعون» لا يصلون.'),
(5671, 77, 49, '«ويل يومئذ للمكذبين».'),
(5672, 77, 50, '«فبأي حديث بعده» أي القرآن «يؤمنون» أي لا يمكن إيمانهم بغيره من كتب الله بعد تكذيبهم به لاشتماله على الإعجاز الذي لم يشتمل عليه غيره.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, '«عمَّ» عن أي شيء «يتساءلون» يسال بعض قريش بعضا.'),
(5674, 78, 2, '«عن النبأ العظيم» بيان لذلك الشيء والاستفهام لتفخيمه وهو ما جاء به النبي صلى الله عليه وسلم من القرآن المشتمل على البعث وغيره.'),
(5675, 78, 3, '«الذي هم فيه مختلفون» فالمؤمنون يثبتونه والكافرون ينكرونه.'),
(5676, 78, 4, '«كلا» ردع «سيعلمون» ما يحل بهم على إنكارهم له.'),
(5677, 78, 5, '«ثم كلا سيعلمون» تأكيد وجيء فيه بثم للإيذان بأن الوعيد الثاني أشد من الأول، ثم أومأ تعالى إلى القدرة على البعث فقال:'),
(5678, 78, 6, '«ألم نجعل الأرض مهادا» فراشا كالمهد.'),
(5679, 78, 7, '«والجبال أوتادا» تثبت بها الأرض كما تثبت الخيام بالأوتاد، والاستفهام للتقرير.'),
(5680, 78, 8, '«وخلقناكم أزواجا» ذكورا وإناثا.'),
(5681, 78, 9, '«وجعلنا نومكم سباتا» راحة لأبدانكم.'),
(5682, 78, 10, '«وجعلنا الليل لباسا» ساترا بسواده.'),
(5683, 78, 11, '«وجعلنا النهار معاشا» وقتا للمعايش.'),
(5684, 78, 12, '«وبنينا فوقكم سبعا» سبع سماوات «شدادا» جمع شديدة، أي قوية محكمة لا يؤثر فيها مرور الزمان.'),
(5685, 78, 13, '«وجعلنا سراجا» منيرا «وهاجا» وقادا: يعني الشمس.'),
(5686, 78, 14, '«وأنزلنا من المعصرات» السحابات التي حان لها أن تمطر، كالمعصر الجارية التي دنت من الحيض «ماءً ثجاجا» صبابا.'),
(5687, 78, 15, '«لنخرج به حبا» كالحنطة «ونباتا» كالتين.'),
(5688, 78, 16, '«وجنات» بساتين «ألفافا» ملتفة، جمع لفيف كشريف وأشراف.'),
(5689, 78, 17, '«إن يوم الفصل» بين الخلائق «كان ميقاتا» وقتا للثواب والعقاب.'),
(5690, 78, 18, '«يوم ينفخ في الصور» القرن بدل من يوم الفصل أو بيان له والنافخ إسرافيل «فتأتون» من قبوركم إلى الموقف «أفواجا» جماعات مختلفة.'),
(5691, 78, 19, '«وفُتِّحت السماء» بالتشديد والتخفيف شققت لنزول الملائكة «فكانت أبوابا» ذات أبواب.'),
(5692, 78, 20, '«وسيِّرت الجبال» ذهب بها عن أماكنها «فكانت سرابا» هباء، أي مثله في خفة سيرها.'),
(5693, 78, 21, '«إن جهنم كانت مرصادا» راصدة أو مرصدة.'),
(5694, 78, 22, '«للطاغين» الكافرين فلا يتجاوزونها «مآبا» مرجعا لهم فيدخلونها.'),
(5695, 78, 23, '«لابثين» حال مقدرة، أي مقدَّرا لبثهم «فيها أحقابا» دهورا لا نهاية لها جمع حقب بضم أوله.'),
(5696, 78, 24, '«لا يذوقون فيها بردا» نوما فإنهم لا يذوقونه «ولا شرابا» ما يشرب تلذذا.'),
(5697, 78, 25, '«إلا» لكن «حميما» ماءً حارا غاية الحرارة «وغسَّاقا» بالتخفيف والتشديد ما يسيل من صديد أهل النار فإنهم يذوقونه جوزوا بذلك.'),
(5698, 78, 26, '«جزاءً وفاقا» موافقا لعملهم فلا ذنب أعظم من الكفر ولا عذاب أعظم من النار.'),
(5699, 78, 27, '«إنهم كانوا لا يرجون» يخافون «حسابا» لإنكارهم البعث.'),
(5700, 78, 28, '«وكذبوا بآياتنا» القرآن «كِذَّابا» تكذيبا.'),
(5701, 78, 29, '«وكل شيءٍ» من الأعمال «أحصيناه» ضبطناه «كتابا» كتبا في اللوح المحفوظ لنجازي عليه ومن ذلك تكذيبهم بالقرآن.'),
(5702, 78, 30, '«فذوقوا» أي فيقال لهم في الآخرة عند وقوع العذاب ذوقوا جزاءكم «فلن نزيدكم إلا عذابا» فوق عذابكم.'),
(5703, 78, 31, '«إن للمتقين مفازا» مكان فوز في الجنة.'),
(5704, 78, 32, '«حدائق» بساتين بدل من مفازا أو بيان له «وأعنابا» عطف على مفازا.'),
(5705, 78, 33, '«وكواعب» جواري تكعبت ثديهن جمع كاعب «أترابا» على سن واحد، جمع تِرب بكسر التاء وسكون الراء.'),
(5706, 78, 34, '(وكأسا دهاقا) خمرا مالئة محالها، وفي سورة القتال: "" وأنهار من خمر "".'),
(5707, 78, 35, '«لا يسمعون فيها» أي الجنة عند شرب الخمر وغيرها من الأحوال «لغوا» باطلا من القول «ولا كذَابا» بالتخفيف، أي: كذبا، وبالتشديد أي تكذيبا من واحد لغيره بخلاف ما يقع في الدنيا عند شرب الخمر.'),
(5708, 78, 36, '«جزاءً من ربك» أي جزاهم الله بذلك جزاء «عطاءً» بدل من جزاء «حسابا» أي كثيرا، من قولهم: أعطاني فأحسبني، أي أكثر علي حتى قلت حسبي.'),
(5709, 78, 37, '«ربِّ السماوات والأرض» بالجر والرفع «وما بينهما الرحمنِ» كذلك وبرفعه مع جر رب «لا يملكون» أي الخلق «منه» تعالى «خطابا» أي لا يقدر أحد أن يخاطبه خوفا منه.'),
(5710, 78, 38, '«يوم» ظرف لـ لا يملكون «يقوم الروح» جبريل أو جند الله «والملائكة صفا» حال، أي مصطفين «لا يتكلمون» أي الخلق «إلا من أذن له الرحمن» في الكلام «وقال» قولا «صوابا» من المؤمنين والملائكة كأن يشفعوا لمن ارتضى.'),
(5711, 78, 39, '«ذلك اليوم الحق» الثابت وقوعه وهو يوم القيامة «فمن شاء اتخذ إلى ربه مآبا» مرجعا، أي رجع إلى الله بطاعته ليسلم من العذاب فيه.'),
(5712, 78, 40, '«إنا أنذرناكم» يا كفار مكة «عذابا قريبا» عذاب يوم القيامة الآتي، وكل آت قريب «يوم» ظرف لعذابا بصفته «ينظر المرء» كل امرئ «ما قدمت يداه» من خير وشر «ويقول الكافر يا» حرف تنبيه «ليتني كنت ترابا» يعني فلا أعذب يقول ذلك عندما يقول الله تعالى للبهائم بعد الاقتصاص من بعضها لبعض: كوني ترابا.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, '«والنازعات» الملائكة تنزع أرواح الكفار «غرقا» نزعا بشدة.'),
(5714, 79, 2, '«والناشطات نشطا» الملائكة تنشط أرواح المؤمنين، أي تسلها برفق.'),
(5715, 79, 3, '«والسابحات سبحا» الملائكة تسبح من السماء بأمره تعالى، أي تنزل.'),
(5716, 79, 4, '«فالسابقات سبقا» الملائكة تسبق بأرواح المؤمنين إلى الجنة.'),
(5717, 79, 5, '«فالمدبرات أمرا» الملائكة تدبر أمر الدنيا، أي تنزل بتدبيره، وجواب هذه الأقسام محذوف، أي لتبعثنَّ يا كفار مكة وهو عامل في.'),
(5718, 79, 6, '«يوم ترجف الراجفة» النفخة الأولى بها يرجف كل شيء، أي يتزلزل فوصفت بما يحدث منها.'),
(5719, 79, 7, '«تتبعها الرادفة» النفخة الثانية وبينهما أربعون سنة، والجملة حال من الراجفة، فاليوم واسع للنفختين وغيرهما فصح ظرفيته للبعث الواقع عقب الثانية.'),
(5720, 79, 8, '«قلوب يومئذٍ واجفة» خائفة قلقة.'),
(5721, 79, 9, '«أبصارها خاشعة» ذليلة لهول ما ترى.'),
(5722, 79, 10, '«يقولون» أي أرباب القلوب والأبصار استهزاء وإنكارا للبعث «أئنا» بتحقيق الهمزتين وتسهيل الثانية وإدخال الف بينهما على الوجهين في الموضعين «لمردودون في الحافرة» أي أنرد بعد الموت إلى الحياة، والحافرة: اسم لأول الأمر، ومنه رجع فلان في حافرته: إذا رجع من حيث جاء.'),
(5723, 79, 11, '«أئذا كنا عظاما نخرة» وفي قراءة ناخرة بالية متفتتة نحيا.'),
(5724, 79, 12, '«قالوا تلك» أي رجعتنا إلى الحياة «إذا» إن صحت «كرة» رجعة «خاسرة» ذات خسران قال تعالى:'),
(5725, 79, 13, '«فإنما هي» أي الرادفة التي يعقبها البعث «زجرة» نفخة «واحدة» فإذا نفخت.'),
(5726, 79, 14, '«فإذا هم» أي كل الخلائق «بالساهرة» بوجه الأرض أحياءً بعدما كانوا ببطنها أمواتا.'),
(5727, 79, 15, '«هل أتاك» يا محمد «حديث موسى» عامل في.'),
(5728, 79, 16, '«إذ ناداه ربه بالوادِ المقدس طوىً» اسم الوادي بالتنوين وتركه، فقال:'),
(5729, 79, 17, '«اذهب إلى فرعون إنه طغى» تجاوز الحد في الكفر.'),
(5730, 79, 18, '«فقل هل لك» أدعوك «إلى أن تزكى» وفي قراءة بتشديد الزاي بإدغام التاء الثانية في الأصل فيها: تتطهر من الشرك بأن تشهد أن لا إله إلا الله.'),
(5731, 79, 19, '«وأهديك إلى ربك» أدلك على معرفته ببرهان «فتخشى» فتخافه.'),
(5732, 79, 20, '«فأراه الآية الكبرى» من آياته السبع وهي اليد أو العصا.'),
(5733, 79, 21, '«فكذب» فرعون موسى «وعصى» الله تعالى.'),
(5734, 79, 22, '«ثم أدبر» عن الإيمان «يسعى» في الأرض بالفساد.'),
(5735, 79, 23, '«فحشر» جمع السحرة وجنده «فنادى».'),
(5736, 79, 24, '«فقال أنا ربكم الأعلى» لا رب فوقي.'),
(5737, 79, 25, '(فأخذه الله) أهلكه بالغرق (نكال) عقوبة (الآخرة) أي هذه الكلمة (والأولى) أي قوله قبلها: "" ما علمت لكم من إله غيري "" وكان بينهما أربعون سنة.'),
(5738, 79, 26, '«إن في ذلك» المذكور «لعبرة لمن يخشى» الله تعالى.'),
(5739, 79, 27, '«أأنتم» بتحقيق الهمزتين وإبدال الثانية ألفاً وتسهيلها وإدخال ألف بين المسهلة والأخرى وتركه، أي منكرو البعث «أشد خلقا أم السماء» أشد خلقا «بناها» بيان لكيفية خلقها.'),
(5740, 79, 28, '«رفع سمكها» تفسير لكيفية البناء، أي جعل سمتها في جهة العلو رفيعا، وقيل سمكها سقفها «فسواها» جعلها مستوية بلا عيب.'),
(5741, 79, 29, '«وأغطش ليلها» أظلمه «وأخرج ضحاها» أبرز نور شمسها وأضيف إليها الليل لأنه ظلها والشمس لأنها سراجها.'),
(5742, 79, 30, '«والأرض بعد ذلك دحاها» بسطها وكانت مخلوقة قبل السماء من غير دحو.'),
(5743, 79, 31, '«أخرج» حال بإضمار قد أي مخرجا «منها ماءها» بتفجير عيونها «ومرعاها» ما ترعاه النعم من الشجر والعشب وما يأكله الناس من الأقوات والثمار، وإطلاق المرعى عليه استعارة.'),
(5744, 79, 32, '«والجبال أرساها» أثبتها على وجه الأرض لتسكن.'),
(5745, 79, 33, '«متاعا» مفعول له لمقدر، أي فعل ذلك متعة أو مصدر أي تمتيعا «لكم ولأنعامكم» جمع نعم وهي الإبل والبقر والغنم.'),
(5746, 79, 34, '«فإذا جاءت الطامة الكبرى» النفخة الثانية.'),
(5747, 79, 35, '«يوم يتذكر الإنسان» بدل من إذا «ما سعى» في الدنيا من خير وشر.'),
(5748, 79, 36, '«وبرزت» أظهرت «الجحيم» النار المحرقة «لمن يرى» لكل راءٍ وجواب إذا:'),
(5749, 79, 37, '«فأما من طغى» كفر.'),
(5750, 79, 38, '«وآثر الحياة الدنيا» باتباع الشهوات.'),
(5751, 79, 39, '«فإن الجحيم هي المأوى» مأواه.'),
(5752, 79, 40, '«وأما من خاف مقام ربه» قيامه بين يديه «ونهى النفس» الأمارة «عن الهوى» المردي باتباع الشهوات.'),
(5753, 79, 41, '«فإن الجنة هي المأوى» وحاصل الجواب: فالعاصي في النار والمطيع في الجنة.'),
(5754, 79, 42, '«يسَألونك» أي كفار مكة «عن الساعة أيان مرساها» متى وقوعها وقيامها.'),
(5755, 79, 43, '«فيم» في أي شيء «أنت من ذكراها» أي ليس عندك علمها حتى تذكرها.'),
(5756, 79, 44, '«إلى ربك منتهاها» منتهى علمها لا يعلمه غيره.'),
(5757, 79, 45, '«إنما أنت منذر» إنما ينفع إنذارك «من يخشاها» يخافها.'),
(5758, 79, 46, '«كأنهم يوم يرونها لم يلبثوا» في قبورهم «إلا عشية أو ضحاها» عشية يوم أو بكرته وصح إضافة الضحى إلى العشية لما بينهما من الملابسة إذ هما طرفا النهار، وحسن الإضافة وقوع الكلمة فاصلة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, '«عبس» النبي: كلح وجهه «وتولى» أعرضَ لأجل:'),
(5760, 80, 2, '(أن جاءه الأعمى) عبد الله بن أم مكتوم فقطعه عما هو مشغول به ممن يرجو إسلامه من أشراف قريش الذين هو حريص على إسلامهم، ولم يدر الأعمى أنه مشغول بذلك فناداه: علمني مما علمك الله، فانصرف النبي صلى الله عليه وسلم إلى بيته فعوتب في ذلك بما نزل في هذه السورة، فكان بعد ذلك يقول له إذا جاء: "" مرحبا بمن عاتبني فيه ربي "" ويبسط له رداءه.'),
(5761, 80, 3, '«وما يُدريك» يعلمك «لعله يزكى» فيه إدغام التاء في الأصل في الزاي، أي يتطهر من الذنوب بما يسمع منك.'),
(5762, 80, 4, '«أو يذكَّر» فيه إدغام التاء في الأصل في الذال أي يتعظ «فتنفعُه الذكرى» العظة المسموعة منك وفي قراءة بنصب تنفعه جواب الترجي.'),
(5763, 80, 5, '«أما من استغنى» بالمال.'),
(5764, 80, 6, '«فأنت له تصدى» وفي قراءة بتشديد الصاد بإدغام التاء الثانية في الأصل فيها: تقبل وتتعرض.'),
(5765, 80, 7, '«وما عليك ألا يزكى» يؤمن.'),
(5766, 80, 8, '«وأما من جاءك يسعى» حال من فاعل جاء.'),
(5767, 80, 9, '«وهو يخشى» الله حال من فاعل يسعى وهو الأعمى.'),
(5768, 80, 10, '«فأنت عنه تَلَهَّى» فيه حذف التاء الأخرى في الأصل أي تتشاغل.'),
(5769, 80, 11, '«كلا» لا تفعل مثل ذلك «إنها» أي السورة أو الآيات «تذكرة» عظة للخلق.'),
(5770, 80, 12, '«فمن شاء ذكره» حفظ ذلك فاتعظ به.'),
(5771, 80, 13, '«في صحف» خبر ثان لأنها وما قبله اعتراض «مكرمة» عند الله.'),
(5772, 80, 14, '«مرفوعة» في السماء «مطهرة» منزهة عن مس الشياطين.'),
(5773, 80, 15, '«بأيدي سفرة» كتبة ينسخونها من اللوح المحفوظ.'),
(5774, 80, 16, '«كرام بررة» مطيعين لله تعالى وهم الملائكة.'),
(5775, 80, 17, '«قتل الإنسان» لعن الكافر «ما أكفره» استفهام توبيخ، أي ما حمله على الكفر.'),
(5776, 80, 18, '«من أي شيءٍ خلقه» استفهام تقرير، ثم بينه فقال:'),
(5777, 80, 19, '«من نطفة خلقه فقدره» علقة ثم مضغة إلى آخر خلقه.'),
(5778, 80, 20, '«ثم السبيل» أي طريق خروجه من بطن أمه «يَسَّره».'),
(5779, 80, 21, '«ثم أماته فأقبره» جعله في قبر يستره.'),
(5780, 80, 22, '«ثم إذا شاء أنشره» للبعث.'),
(5781, 80, 23, '«كلا» حقا «لمَّا يقض» لم يفعل «ما أمره» به ربه.'),
(5782, 80, 24, '«فلينظر الإنسان» نظر اعتبار «إلى طعامه» كيف قدر ودبر له.'),
(5783, 80, 25, '«أنا صببنا الماء» من السحاب «صبا».'),
(5784, 80, 26, '«ثم شققنا الأرض» بالنبات «شقا».'),
(5785, 80, 27, '«فأنبتنا فيها حبا» كالحنطة والشعير.'),
(5786, 80, 28, '«وعنبا وقضبا» هو القت الرطب.'),
(5787, 80, 29, '«وزيتونا ونخلا».'),
(5788, 80, 30, '«وحدائق غلبا» بساتين كثيرة الأشجار.'),
(5789, 80, 31, '«وفاكهة وأبّا» ما ترعاه البهائم وقيل التبن.'),
(5790, 80, 32, '«متاعا» متعة أو تمتيعا كما تقدم في السورة قبلها «لكم ولأنعامكم» تقدم فيها أيضا.'),
(5791, 80, 33, '«فإذا جاءت الصاخة» النفخة الثانية.'),
(5792, 80, 34, '«يوم يفر المرء من أخيه».'),
(5793, 80, 35, '«وأمه وأبيه».'),
(5794, 80, 36, '«وصاحبته» زوجته «وبنيه» يوم بدل من إذا، وجوابها دل عليه.'),
(5795, 80, 37, '«لكل امرىءٍ منهم يومئذ شأن يغنيه» حال يشغله عن شأن غيره، أي اشتغل كل واحد بنفسه.'),
(5796, 80, 38, 'وجوه يومئذ مسفرة» مضيئة.'),
(5797, 80, 39, '«ضاحكة مستبشرة» فرحة وهم المؤمنون.'),
(5798, 80, 40, '«ووجوه يومئذ عليها غَبَرة» غبار.'),
(5799, 80, 41, '«ترهقها» تغشاها «قترة» ظلمة وسواد.'),
(5800, 80, 42, '«أولئك» أهل هذه الحال «هم الكفرة الفجرة» أي الجامعون بين الكفر والفجور.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, '«إذا الشمس كوِّرت» لففت وذهب بنورها.'),
(5802, 81, 2, '«وإذا النجوم انكدرت» انقضت وتساقطت على الأرض.'),
(5803, 81, 3, '«وإذا الجبال سيِّرت» ذهب بها عن وجه الأرض فصارت هباءً منبثا.'),
(5804, 81, 4, '«وإذا العشار» النوق الحوامل «عُطلت» تركت بلا راع أو بلا حلب لما دهاهم من الأمر، وإن لم يكن مال أعجب إليهم منها.'),
(5805, 81, 5, '«وإذا الوحوش حشرت» جمعت بعد البعث ليقتص لبعض من بعض ثم تصير ترابا.'),
(5806, 81, 6, '«وإذا البحار سجِّرت» بالتخفيف والتشديد: أوقدت فصارت نارا.'),
(5807, 81, 7, '«وإذا النفوس زوجت» قرنت بأجسادها.'),
(5808, 81, 8, '«وإذا الموءودة» الجارية تدفن حية خوف العار والحاجة «سئلت» تبكيتا لقاتلها.'),
(5809, 81, 9, '«بأي ذنب قتلت» وقرئ بكسر التاء حكاية لما تخاطب به وجوابها أن تقول: قتلت بلا ذنب.'),
(5810, 81, 10, '«وإذا الصحف» صحف الأعمال «نشرت» بالتخفيف والتشديد فتح وبسطت.'),
(5811, 81, 11, '«وإذا السماء كشطت» نزعت عن أماكنها كما ينزع الجلد عن الشاة.'),
(5812, 81, 12, '«وإذا الجحيم» النار «سعرت» بالتخفيف والتشديد أُججت.'),
(5813, 81, 13, '«وإذا الجنة أزلفت» قربت لأهلها ليدخلوها وجواب إذا أول السورة وما عطف عليها.'),
(5814, 81, 14, '«علمت نفس» كل نفس وقت هذه المذكورات وهو يوم القيامة «ما أحضرت» من خير وشر.'),
(5815, 81, 15, '«فلا أقسم» لا زائدة «بالخُنَّس».'),
(5816, 81, 16, '«الجوار الكنَّس» هي النجوم الخمسة: زحل والمشتري والمريخ والزهرة وعطارد، تخنس بضم النون، أي ترجع في مجراها وراءها، بينما نرى النجم في آخر البرج إذ كَّر راجعا إلى أوله، وتكنس بكسر النون: تدخل في كناسها، أي تغيب في المواضع التي تغيب فيها.'),
(5817, 81, 17, '«والليل إذا عسعس» أقبل بظلامه أو أدبر.'),
(5818, 81, 18, '«والصبح إذا تنفس» امتد حتى يصير نهارا بينا.'),
(5819, 81, 19, '«إنه» أي القرآن «لقول رسول كريم» على الله تعالى وهو جبريل أضيف إليه لنزوله به.'),
(5820, 81, 20, '«ذي قوة» أي شديد القوى «عند ذي العرش» أي الله تعالى «مكين» ذي مكانة متعلق به عند.'),
(5821, 81, 21, '«مطاع ثَمَّ» تطيعه الملائكة في السماوات «أمين» على الوحي.'),
(5822, 81, 22, '«وما صاحبكم» محمد صلى الله عليه وسلم عطف على إنه إلى آخر المقسم عليه «بمجنون» كما زعمتم.'),
(5823, 81, 23, '«ولقد رآه» رأى محمد صلى الله عليه وسلم جبريل على صورته التي خُلق عليها «بالأفق المبين» البيِّن وهو الأعلى بناحية المشرق.'),
(5824, 81, 24, '«وما هو» محمد صلى الله عليه وسلم «على الغيب» ما غاب من الوحي وخبر السماء «بظنين» أي بمتهم، وفي قراءة بالضاد، أي ببخيل فينتقص شيئا منه.'),
(5825, 81, 25, '«وما هو» أي القرآن «بقول شيطان» مسترق السمع «رجيم» مرجوم.'),
(5826, 81, 26, '«فأين تذهبون» فبأي طريق تسلكون في إنكاركم القرآن وإعراضكم عنه.'),
(5827, 81, 27, '«إن» ما «هو إلا ذكر» عظة «للعالمين» الإنس والجن.'),
(5828, 81, 28, '«لمن شاء منكم» بدل من العالمين بإعادة الجار «أن يستقيم» باتباع الحق.'),
(5829, 81, 29, '«وما تشاءون» الاستقامة على حق «إلا أن يشاء الله رب العالمين» الخلائق استقامتكم عليه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, '«إذا السماء انفطرت» انشقت.'),
(5831, 82, 2, '«وإذا الكواكب انتثرت» انقضت وتساقطت.'),
(5832, 82, 3, '«وإذا البحار فُجِّرت» فتح بعضها في بعض فصارت بحرا واحدا واختلط العذب بالملح.'),
(5833, 82, 4, '«وإذا القبور بُعثرت» قلب ترابها وبعث موتاها وجواب إذا وما عطف عليها.'),
(5834, 82, 5, '«علمت نفس» أي كل نفس وقت هذه المذكورات وهو يوم القيامة «ما قدمت» من الأعمال «و» ما «أَخَّرت» منها فلم تعمله.'),
(5835, 82, 6, '«يا أيها الإنسان» الكافر «ما غرَّك بربك الكريم» حتى عصيته.'),
(5836, 82, 7, '«الذي خلقك» بعد أن لم تكن «فسوَّاك» جعلك مستوي الخلقة، سالم الأعضاء «فعدَلك» بالتخفيف والتشديد: جعلك معتدل الخلق متناسب الأعضاء ليست يد أو رجل أطول من الأخرى.'),
(5837, 82, 8, '«في أي صورة ما» صلة «شاء ركَّبك».'),
(5838, 82, 9, '«كلا» ردع عن الاغترار بكرم الله تعالى «بل تكذبون» أي كفار مكة «بالدين» بالجزاء على الأعمال.'),
(5839, 82, 10, '«وإن عليكم لحافظين» من الملائكة لأعمالكم.'),
(5840, 82, 11, '«كِراما» على الله «كاتبين» لها.'),
(5841, 82, 12, '«يعلمون ما تفعلون» جميعه.'),
(5842, 82, 13, '«إن الأبرار» المؤمنين الصادقين في إيمانهم «لفي نعيم» جنة.'),
(5843, 82, 14, '«وإن الفجار الكفار «لفي جحيم» نار محرقة.'),
(5844, 82, 15, '«يصلونها» يدخلونها ويقاسون حرَّها «يوم الدين» الجزاء.'),
(5845, 82, 16, '«وما هم عنها بغائبين» بمخرجين.'),
(5846, 82, 17, '«وما أدراك» أعلمك «ما يوم الدين».'),
(5847, 82, 18, '«ثم ما أدراك ما يومُ الدين» تعظيم لشأنه.'),
(5848, 82, 19, '«يوم» بالرفع، أي هو يوم «لا تملك نفس لنفس شيئا» من المنفعة «والأمر يومئذ لله» لا أمر لغيره فيه، أي لم يمكن أحدا من التوسط فيه بخلاف الدنيا.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, '«ويل» كلمة عذاب، أو واد في جهنم «للمطففين».'),
(5850, 83, 2, '«الذين إذا اكتالوا على» أي من «الناس يستوفون» الكيل.'),
(5851, 83, 3, '«وإذا كالوهم» أي كالوا لهم «أو وزنوهم» أي وزنوا لهم «يُخْسِرون» ينقصون الكيل أو الوزن.'),
(5852, 83, 4, '«ألا» استفهام توبيخ «يظن» يتيقن «أولئك أنهم مبعوثون».'),
(5853, 83, 5, '«ليوم عظيم» أي فيه وهو يوم القيامة.'),
(5854, 83, 6, '«يوم» بدل من محل ليوم فناصبه مبعوثون «يقوم الناس» من قبورهم «لرب العالمين» الخلائق لأجل أمره وحسابه وجزائه.'),
(5855, 83, 7, '«كلا» حقا «إن كتاب الفجَّار» أي كتاب أعمال «لفي سجّين» قيل هو كتاب جامع لأعمال الشياطين والكفرة، وقيل هو مكان أسفل الأرض السابعة وهو إبليس وجنوده.'),
(5856, 83, 8, '«وما أدراك ما سجين» ما كتاب سجين.'),
(5857, 83, 9, '«كتاب مرقوم» مختوم.'),
(5858, 83, 10, '«ويل يومئذ للمكذبين».'),
(5859, 83, 11, '«الذين يكذبون بيوم الدين» الجزاء بدل أو بيان للمكذبين.'),
(5860, 83, 12, '«وما يكذب به إلا كل معتدٍ» متجاوز الحد «أثيم» صيغة مبالغة.'),
(5861, 83, 13, '«إذا تتلى عليه آياتنا» القرآن «قال أساطير الأولين» الحكايات التي سطرت قديما جمع أسطورة بالضم أو إسطارة بالكسر.'),
(5862, 83, 14, '«كلا» ردع وزجر لقولهم ذلك «بل ران» غلب «على قلوبهم» فغشيها «ما كانوا يكسبون» من المعاصي فهو كالصدأ.'),
(5863, 83, 15, '«كلا» حقا «إنهم عن ربهم يومئذ» يوم القيامة «لمحجوبون» فلا يرونه.'),
(5864, 83, 16, '«ثم إنهم لصالوا الجحيم» لداخلوا النار المحرقة.'),
(5865, 83, 17, '«ثم يقال» لهم «هذا» أي العذاب «الذي كنتم به تكذبون».'),
(5866, 83, 18, '«كلا» حقا «إن كتاب الأبرار» أي كتاب أعمال المؤمنين الصادقين في إيمانهم «لفي عليين» قيل هو كتاب جامع لأعمال الخير من الملائكة ومؤمني الثقلين، وقيل هو مكان في السماء السابعة تحت العرش.'),
(5867, 83, 19, '«وما أدراك» أعلمك «ما عليون» ما كتاب عليين.'),
(5868, 83, 20, 'هو «كتاب مرقوم» مختوم.'),
(5869, 83, 21, '«يشهده المقربون» من الملائكة.'),
(5870, 83, 22, '«إن الأبرار لفي نعيم» جنة.'),
(5871, 83, 23, '«على الأرائك» السرر في الحجال «ينظرون» ما أعطوا من النعيم.'),
(5872, 83, 24, '«تعرف في وجوههم نضرة النعيم» بهجة التنعم وَحُسنه.'),
(5873, 83, 25, '«يُسقوْن من رحيق» خمر خالصة من الدنس «مختوم» على إنائها لا يفك ختمه غيرهم.'),
(5874, 83, 26, '«ختامه مسك» أي آخر شربه تفوح من رائحة المسك «وفي ذلك فليتنافس المتنافسون» فليرغبوا بالمبادرة إلى طاعة الله.'),
(5875, 83, 27, '«ومزاجه» أي ما يمزج به «من تسنيم» فُسر بقوله:'),
(5876, 83, 28, '«عينا» فنصبه بأمدح مقدرا «يشرب بها المقربون» منها، أو ضمن يشرب معنى يلتذ.'),
(5877, 83, 29, '«إن الذين أجرموا» كأبي جهل ونحوه «كانوا من الذين آمنوا» كعمار وبلال ونحوهما «يضحكون» استهزاءً بهم.'),
(5878, 83, 30, '«وإذا مروا» أي المؤمنون «بهم يتغامزون» يشير المجرمون إلى المؤمنين بالجفن والحاجب استهزاء.'),
(5879, 83, 31, '«وإذا انقلبوا» رجعوا «إلى أهلهم انقلبوا فاكهين» وفي قراءة فكهين معجبين بذكرهم المؤمنين.'),
(5880, 83, 32, '«وإذا رأوْهم» أي المؤمنين «قالوا إن هؤلاء لضالون» لإيمانهم بمحمد صلى الله عليه وسلم.'),
(5881, 83, 33, 'قال تعالى: «وما أرسلوا» أي الكفار «عليهم» على المؤمنين «حافظين» لهم أو لأعمالهم حتى يدروهم إلى مصالحهم.'),
(5882, 83, 34, '«فاليوم» أي يوم القيامة «الذين آمنوا من الكفار يضحكون».'),
(5883, 83, 35, '«على الأرائك» في الجنة «ينظرون» من منازلهم إلى الكفار وهم يعذبون فيضحكون منهم كما ضحك الكفار منهم في الدنيا.'),
(5884, 83, 36, '«هل ثُوِّبَ» جوزي «الكفار ما كانوا يفعلون» نعم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, '«إذا السماء انشقت».'),
(5886, 84, 2, '«وأذنت» سمعت وأطاعت في الانشقاق «لربها وحقت» أي وحق لها أن تسمع وتطيع.'),
(5887, 84, 3, '«وإذا الأرض مدت» زيد في سعتها كما يمد الأديم ولم يبق عليها بناء ولا جبل.'),
(5888, 84, 4, '«وألقت ما فيها» من الموتى إلى ظاهرها «وتخلت» عنه.'),
(5889, 84, 5, '«وأذنت» سمعت وأطاعت في ذلك «لربها وحقت» وذلك كله يكون يوم القيامة، وجواب إذا وما عطف عليها محذوف دل عليه ما بعده تقديره لقي الإنسان عمله.'),
(5890, 84, 6, '«يا أيها الإنسان إنك كادح» جاهد في عملك «إلى» لقاء «ربك» وهو الموت «كدحا فملاقيه» أي ملاق عملك المذكور من خير أو شر يوم القيامة.'),
(5891, 84, 7, '«فأما من أوتي كتابه» كتاب عمله «بيمينه» هو المؤمن.'),
(5892, 84, 8, '(فسوف يحاسب حسابا يسيرا) هو عرض عمله عليه كما في حديث الصحيحين وفيه "" من نوقش الحساب هلك "" وبعد العرض يتجاوز عنه.'),
(5893, 84, 9, '«وينقلب إلى أهله» في الجنة «مسرورا» بذلك.'),
(5894, 84, 10, '«وأما من أوتي كتابه وراء ظهره» هو الكافر تغل يمناه إلى عنقه وتجعل يسراه وراء ظهره فيأخذ بها كتابه.'),
(5895, 84, 11, '«فسوف يدعو» عند رؤيته ما فيه «ثبورا» ينادي هلاكه بقوله: يا ثبوراه.'),
(5896, 84, 12, '«ويصلى سعيرا» يدخل النار الشديدة وفي قراءة بضم الياء وفتح الصاد واللام المشددة.'),
(5897, 84, 13, '«إنه كان في أهله» عشيرته في الدنيا «مسرورا» بطرا باتباعه لهواه.'),
(5898, 84, 14, '«إنه ظن أن» مخففة من الثقيلة واسمها محذوف، أي أنه «لن يحور» يرجع إلى ربه.'),
(5899, 84, 15, '«بلى» يرجع إليه «إن ربه كان به بصيرا» عالما برجوعه إليه.'),
(5900, 84, 16, '«فلا أقسم» لا زائدة «بالشفق» هو الحمرة في الأفق بعد غروب الشمس.'),
(5901, 84, 17, '«والليل وما وسق» جمع ما دخل عليه من الدواب وغيرها.'),
(5902, 84, 18, '«والقمر إذا اتسق» اجتمع وتم نوره وذلك في الليالي البيض.'),
(5903, 84, 19, '«لتركبن» أيها الناس أصله تركبونن حذفت نون الرفع لتوالي الأمثال والواو لالتقاء الساكنين «طبقا عن طبق» حالا بعد حال، وهو الموت ثم الحياة وما بعدها من أحوال القيامة.'),
(5904, 84, 20, '«فما لهم» أي الكفار «لا يؤمنون» أي أيُّ مانع من الإيمان أو أي حجة لهم في تركه مع وجود براهينه.'),
(5905, 84, 21, '«و» مالهم «إذا قُرىءَ عليهم القرآن لا يسجدون» يخضعون بأن يؤمنوا به لإعجازه.'),
(5906, 84, 22, '«بل الذين كفروا يكذبون» بالبعث وغيره.'),
(5907, 84, 23, '«والله أعلم بما يوعون» يجمعون في صحفهم من الكفر والتكذيب وأعمال السوء.'),
(5908, 84, 24, '«فبشرهم» أخبرهم «بعذاب أليم» مؤلم.'),
(5909, 84, 25, '«إلا» لكن «الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون» غير مقطوع ولا منقوص ولا يُمَنُّ به عليه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, '«والسماء ذات البروج» الكواكب اثني عشر برجا تقدَّمت في الفرقان.'),
(5911, 85, 2, '«واليوم الموعود» يوم القيامة.'),
(5912, 85, 3, '«وشاهد» يوم الجمعة «ومشهود» يوم عرفة كذا فسرت الثلاثة في الحديث فالأول موعود به والثاني شاهد بالعمل فيه، والثالث تشهده الناس والملائكة، وجواب القسم محذوف صدره، تقديره لقد.'),
(5913, 85, 4, '«قتل» لعن «أصحاب الأخدود» الشق في الأرض.'),
(5914, 85, 5, '«النار» بدل اشتمال منه «ذات الوقود» ما توقد به.'),
(5915, 85, 6, '«إذ هم عليها» حولها على جانب الأخدود على الكراسي «قعود».'),
(5916, 85, 7, '«وهم على ما يفعلون بالمؤمنين» بالله من تعذيبهم بالإلقاء في النار إن لم يرجعوا عن إيمانهم «شهود» حضور، رُوي أن الله أَنجى المؤمنين الملقين في النار بقبض أرواحهم قبل وقوعهم فيها وخرجت النار إلى من ثَمَّ فأحرقتهم.'),
(5917, 85, 8, '«وما نقموا منهم إلا أن يؤمنوا بالله العزيز» في ملكه «الحميد» المحمود.'),
(5918, 85, 9, '«الذي له ملك السماوات والأرض والله على كل شيءٍ شهيد» أي ما أنكر الكفار على المؤمنين إلا إيمانهم.'),
(5919, 85, 10, '«إن الذين فتنوا المؤمنين والمؤمنات» بالإحراق «ثم لم يتوبوا فلهم عذاب جهنم» بكفرهم «ولهم عذاب الحريق» أي عذاب إحراقهم المؤمنين في الآخرة، وقيل في الدنيا بأن أخرجت النار فأحرقتهم كما تقدم.'),
(5920, 85, 11, '«إن الذين آمنوا وعملوا الصالحات لهم جنات تجري من تحتها الأنهار ذلك الفوز الكبير».'),
(5921, 85, 12, '«إن بطش ربك» بالكفار «لشديد» بحسب إرادته.'),
(5922, 85, 13, '«إنه هو يبدىءُ» الخلق «ويعيد» فلا يعجزه ما يريد.'),
(5923, 85, 14, '«وهو الغفور» للمذنبين المؤمنين «الودود» المتودد إلى أوليائه بالكرامة.'),
(5924, 85, 15, '«ذو العرش» خالقه ومالكه «المجيد» بالرفع: المستحق لكمال صفات العلوّ.'),
(5925, 85, 16, '«فعَّال لما يريد» لا يعجزه شيء.'),
(5926, 85, 17, '«هل أتاك» يا محمد «حديث الجنود».'),
(5927, 85, 18, '«فرعون وثمود» بدل من الجنود واستغني بذكر فرعون عن أتباعه، وحديثهم أنهم أُهلكوا بكفرهم وهذا تنبيه لمن كفر بالنبي صلى الله عليه وسلم والقرآن ليتعظوا.'),
(5928, 85, 19, '«بل الذين كفروا في تكذيب» بما ذكر.'),
(5929, 85, 20, '«والله من ورائهم محيط» لا عاصم لهم منه.'),
(5930, 85, 21, '«بل هو قرآن مجيد» عظيم.'),
(5931, 85, 22, '«في لوح» هو في الهواء فوق السماء السابعة «محفوظٍ» بالجر من الشياطين ومن تغيير شيء منه طوله ما بين السماء والأرض، وعرضه ما بين المشرق والمغرب، وهو من درة بيضاء، قاله ابن عباس رضي الله عنهما.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, '«والسماء والطارق» أصله كل آت ليلا ومنه النجوم لطلوعها ليلا.'),
(5933, 86, 2, '«وما أدراك» أعلمك «ما الطارق» مبتدأ وخبر في محل المفعول الثاني لأدرى وما بعد الأولى خبرها وفيه تعظيم لشأن الطارق المفسر بما بعده هو.'),
(5934, 86, 3, '«النجم» أي الثريا أو كل نجم «الثاقب» المضيء لثقبه الظلام بضوئه وجواب القسم.'),
(5935, 86, 4, '«إن كل نفس لمَا عليها حافظ» بتخفيف ما فهي مزيدة وإن مخففة من الثقيلة واسمها محذوف، أي إنه واللام فارقة وبتشديدها فإن نافية ولما بمعنى إلا والحافظ من الملائكة يحفظ عملها من خير وشر.'),
(5936, 86, 5, '«فلينظر الإنسان» نظر اعتبار «ممَّ خُلق» من أي شيء.'),
(5937, 86, 6, 'جوابه «خُلق من ماء دافق» ذي اندفاق من الرجل والمرأة في رحمها.'),
(5938, 86, 7, '«يخرج من بين الصلب» للرجل «والترائب» للمرأة وهي عظام الصدر.'),
(5939, 86, 8, '«إنه» تعالى «على رجعه» بعث الإنسان بعد موته «لقادر» فإذا اعتبر أصله علم أن القادر على ذلك قادر على بعثه.'),
(5940, 86, 9, '«يوم تبلى» تختبر وتكشف «السرائر» ضمائر القلوب في العقائد والنيات.'),
(5941, 86, 10, '«فما له» لمنكر البعث «من قوة» يمتنع بها من العذاب «ولا ناصر» يدفعه عنه.'),
(5942, 86, 11, '«والسماء ذات الرجع» المطر لعوده كل حين.'),
(5943, 86, 12, '«والأرض ذات الصدع» الشق عن النبات.'),
(5944, 86, 13, '«إنه» أي القرآن «لقول فصل» يفصل بين الحق والباطل.'),
(5945, 86, 14, '«وما هو بالهزل» باللعب والباطل.'),
(5946, 86, 15, '«إنهم» أي الكفار «يكيدون كيدا» يعملون المكايد للنبي صلى الله عليه وسلم.'),
(5947, 86, 16, '«وأكيد كيدا» أستدرجهم من حيث لا يعلمون.'),
(5948, 86, 17, '«فمهِّل» يا محمد «الكافرين أمهلهم» تأكيد حسَّنهُ مخالفة اللفظ، أي أنظرهم «رويدا» قليلا وهو مصدر مؤكد لمعنى العامل مصغر رود أو أرواد على الترخيم وقد أخذهم الله تعالى ببدر ونسخ الإمهال بآية السيف، أي الأمر بالقتال والجهاد.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, '«سبح اسم ربك» أي نزه ربك عما لا يليق به واسم زائد «الأعلى» صفة لربك.'),
(5950, 87, 2, '«الذي خلق فسوى» مخلوقه، جعله متناسب الأجزاء غير متفاوت.'),
(5951, 87, 3, '«والذي قدَّر» ما شاء «فهدى» إلى ما قدره من خير وشر.'),
(5952, 87, 4, '«والذي أخرج المرعى» أنبت العشب.'),
(5953, 87, 5, '«فجعله» بعد الخضرة «غُثاء» جافا هشيما «أحوى» أسود يابسا.'),
(5954, 87, 6, '«سنقرئك» القرآن «فلا تنسى» ما تقرؤه.'),
(5955, 87, 7, '«إلا ما شاء الله» أن تنساه بنسخ تلاوته وحكمه، وكان صلى الله عليه وسلم يجهر بالقراءة مع قراءة جبريل خوف النسيان فكأنه قيل له: لا تعجل بها إنك لا تنسى فلا تتعب نفسك بالجهر بها «إنه» تعالى «يعلم الجهر» من القول والفعل «وما يخفى» منهما.'),
(5956, 87, 8, '«ونيسِّرك لليسرى» للشريعة السهلة وهي الإسلام.'),
(5957, 87, 9, '«فذكر» عظ بالقرآن «إن نفعت الذكرى» من تذكرة المذكور في سيذكر، يعني وإن لم تنفع ونفعها لبعض وعدم النفع لبعض آخر.'),
(5958, 87, 10, '«سيذكر» بها «من يخشى» يخاف الله تعالى كآية " فذكر بالقرآن من يخاف وعيد ".'),
(5959, 87, 11, '«ويتجنبها» أي الذكرى، أي يتركها جانبا لا يلتفت إليها «الأشقى» بمعنى الشقي أي الكافر.'),
(5960, 87, 12, '«الذي يصلى النار الكبرى» هي نار الآخرة والصغرى نار الدنيا.'),
(5961, 87, 13, '«ثم لا يموت فيها» فيستريح «ولا يحيى» حياة هنيئة.'),
(5962, 87, 14, '«قد أفلح» فاز «من تزكى» تطهر بالإيمان.'),
(5963, 87, 15, '«وذكر اسم ربه» مكبرا «فصلَّى» الصلوات الخمس وذلك من أمور الآخرة وكفار مكة مُعرضون عنها.'),
(5964, 87, 16, '«بل تؤثرون» بالفوقانية والتحتانية «الحياة الدنيا» على الآخرة.'),
(5965, 87, 17, '«والآخرة» المشتملة على الجنة «خير وأبقى».'),
(5966, 87, 18, '«إن هذا» إفلاحُ من تزكى وكون الآخرة خيرا «لفي الصحف الأولى» أي المنزلة قبل القرآن.'),
(5967, 87, 19, '«صحف إبراهيم وموسى» وهي عشر صحف لإبراهيم والتوراة لموسى.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, '«هل» قد «أتاك حديث الغاشية» القيامة لأنها تغشى الخلائق بأهوالها.'),
(5969, 88, 2, '«وجوه يومئذٍ» عبر بها عن الذوات في الموضوعين «خاشعة» ذليلة.'),
(5970, 88, 3, '«عاملة ناصبة» ذات نصب وتعب بالسلاسل والأغلال.'),
(5971, 88, 4, '«تصلى» بفتح التاء وضمها «نارا حامية».'),
(5972, 88, 5, '«تسقى من عين آنية» شديدة الحرارة.'),
(5973, 88, 6, '«ليس لهم طعام إلا من ضريع» هو نوع من الشوك لا ترعاه دابة لخبثه.'),
(5974, 88, 7, '«لا يسمن ولا يغني من جوع».'),
(5975, 88, 8, '«وجوه يومئذ ناعمة» حسنة.'),
(5976, 88, 9, '«لسعيها» في الدنيا بالطاعة «راضية» في الآخرة لما رأت ثوابه.'),
(5977, 88, 10, '«في جنة عالية» حسا ومعنى.'),
(5978, 88, 11, '«لا يسمع» بالياء والتاء «فيها لاغية» أي نفس ذات لغوٍ: هذيان من الكلام.'),
(5979, 88, 12, '«فيها عين جارية» بالماء بمعنى عيون.'),
(5980, 88, 13, '«فيها سرر مرفوعة» ذاتا وقدرا ومحلا.'),
(5981, 88, 14, '«وأكواب» أقداح لا عرى لها «موضوعة» على حافات العيون معدة لشربهم.'),
(5982, 88, 15, '«ونمارق» وسائد «مصفوفة» بعضها بجنب بعض يستند إليها.'),
(5983, 88, 16, '«وزرابيُّ» بسط طنافس لها خمل «مبثوثة» مبسوطة.'),
(5984, 88, 17, '«أفلا ينظرون» أي كفار مكة نظر اعتبار «إلى الإبل كيف خُلقت».'),
(5985, 88, 18, '«وإلى السماء كيف رُفعت».'),
(5986, 88, 19, '«وإلى الجبال كيف نُصبت».'),
(5987, 88, 20, '«وإلى الأرض كيف سُطحت» أي بسطت، فيستدلون بها على قدرة الله تعالى ووحدانيته، وصدرت بالإبل لأنهم أشد ملابسة لها من غيرها، وقوله: سُطحت ظاهر في الأرض سطح، وعليه علماء الشرع، لا كرة كما قاله أهل الهيئة وإن لم ينقض ركنا من أركان الشرع.'),
(5988, 88, 21, '«فذكر» ـهم نعم الله ودلائل توحيده «إنما أنت مذكر».'),
(5989, 88, 22, '«لست عليهم بمصيطر» وفي قراءة بالسين بدل الصاد، أي بمسلط وهذا قبل الأمر بالجهاد.'),
(5990, 88, 23, '«إلا» لكن «من تولى» أعرض عن الإيمان «وكفر» بالقرآن.'),
(5991, 88, 24, '«فيعَذِّبه الله العذاب الأكبر» عذاب الآخرة والأصغر عذاب الدنيا بالقتل والأسر.'),
(5992, 88, 25, '«إن إلينا إيابهم» رجوعهم بعد الموت.'),
(5993, 88, 26, '«ثم إن علينا حسابهم» جزاءهم لا نتركه أبدا.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, '«والفجر» أي فجر كل يوم.'),
(5995, 89, 2, '«وليال عشر» أي عشر ذي الحجة.'),
(5996, 89, 3, '«والشفع» الزوج «والوتر» بفتح الواو وكسرها لغتان: الفرد.'),
(5997, 89, 4, '«والليل إذا يسْر» مقبلا ومدبرا.'),
(5998, 89, 5, '«هل في ذلك» القسم «قسمٌ لذي حجر» عقل، وجواب القسم محذوف أي: لتعذبنّ يا كفار مكة.'),
(5999, 89, 6, '«ألم ترَ» تعلم يا محمد «كيف فعل ربك بعاد».'),
(6000, 89, 7, '«إرَمَ» هي عاد الأولى، فإرم عطف بيان أو بدل، ومنع الصرف للعلمية والتأنيث «ذات العماد» أي الطول كان طول الطويل منهم أربعمائة ذراع.'),
(6001, 89, 8, '«التي لم يُخلق مثلها في البلاد» في بطشهم وقوتهم.'),
(6002, 89, 9, '«وثمود الذين جابوا» قطعوا «الصخر» جمع صخرة واتخذوها بيوتا «بالواد» وادي القرى.'),
(6003, 89, 10, '«وفرعون ذي الأوتاد» كان يتد أربعة أوتاد يشد إليها يدي ورجلي من يعذبه.'),
(6004, 89, 11, '«الذين طغوا» تجبروا «في البلاد».'),
(6005, 89, 12, '«فأكثروا فيها الفساد» القتل وغيره.'),
(6006, 89, 13, '«فصبَّ عليهم ربك سوط» نوع «عذاب».'),
(6007, 89, 14, '«إن ربك لبالمرصاد» يرصد أعمال العباد فلا يفوته منها شيء ليجازيهم عليها.'),
(6008, 89, 15, '«فأما الإنسان» الكافر «إذا ما ابتلاه» اختبره «ربه فأكرمه» بالمال وغيره «ونعَّمه فيقول ربي أكرمن».'),
(6009, 89, 16, '«وأما إذا ما ابتلاه فقدره» ضيق «عليه رزقه فيقول ربي أهانن».'),
(6010, 89, 17, '«كلا» ردع، أي ليس الإكرام بالغنى والإهانة بالفقر وإنما هو بالطاعة والمعصية، وكفار مكة لا ينتبهون لذلك «بل لا يكرمون اليتيم» لا يحسنون إليه مع غناهم أو لا يعطونه حقه من الميراث.'),
(6011, 89, 18, '«ولا يحضون» أنفسهم أو غيرهم «على طعام» أي طعام «المسكين».'),
(6012, 89, 19, '«وتأكلون التراث» الميراث «أكلا لما» أي شديدا، للمهم نصيب النساء والصبيان من الميراث مع نصيبهم منه أو مع مالهم.'),
(6013, 89, 20, '«ويحبون المال حبا جما» أي: كثيرا فلا ينفقونه، وفي قراءة بالفوقانية في الأفعال الأربعة.'),
(6014, 89, 21, '«كلا» ردع لهم عن ذلك «إذا دكت الأرض دكا دكا» زلزلت حتى ينهدم كل بناء عليها وينعدم.'),
(6015, 89, 22, '«وجاء ربك» أي أمره «والملك» أي الملائكة «صفا صفا» حال، أي مصطفين أو ذوي صفوف كثيرة.'),
(6016, 89, 23, '«وجيء يومئذ بجهنم» تقاد بسبعين ألف زمام كل زمام بأيدي سبعين ألف ملك لها زفير وتغيظ «يومئذ» بدل من إذا وجوابها «يتذكر الإنسان» أي الكافر ما فرط فيه «وأنَّي له الذكرى» استفهام بمعنى النفي، أي لا ينفعه تذكره ذلك.'),
(6017, 89, 24, '«يقول» مع تذكره «يا» للتنبيه «ليتني قدمت» الخير والإيمان «لحياتي» الطيبة في الآخرة أو وقت حياتي في الدنيا.'),
(6018, 89, 25, '«فيومئذٍ لا يعذِّب» بكسر الذال «عذابه» أي الله «أحد» أي لا يكله إلى غيره.'),
(6019, 89, 26, '«و» كذا «لا يوثق» بكسر الثاء «وثاقه أحد» وفي قراءة بفتح الذال والثاء فضمير عذابه ووثاقه للكافر والمعنى لا يعذب أحد مثل تعذيبه ولا يوثق مثل إيثاقه.'),
(6020, 89, 27, '«يا أيتها النفس المطمئنة» الآمنة وهي المؤمنة.'),
(6021, 89, 28, '«إرجعي إلى ربك» يقال لها ذلك عند الموت، أي إرجعي إلى أمره وإرادته «راضية» بالثواب «مرضية» عند الله بعملك، أي جامعة بين الوصفين وهما حالان ويقال لها في القيامة:'),
(6022, 89, 29, '«فادخلي في» جملة «عبادي» الصالحين.'),
(6023, 89, 30, '«وادخلي جنتي» معهم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, '«لا» زائدة «أقسم بهذا البلد» مكة.'),
(6025, 90, 2, '«وأنت» يا محمد «حِلٌ» حلال «بهذا البلد» بأن يحل لك فتقاتل فيه، وقد أنجز الله له هذا الوعد يوم الفتح، فالجملة اعتراض بين المقسم به وما عطف عليه.'),
(6026, 90, 3, '«ووالد» أي آدم «وما ولد» أي ذريته وما بمعنى من.'),
(6027, 90, 4, '«لقد خلقنا الإنسان» أي الجنس «في كبد» نصب وشدة يكابد مصائب الدنيا وشدائد الآخرة.'),
(6028, 90, 5, '«أيحسب» أيظن الإنسان قوي قريش وهو أبو الأشد بن كلدة بقوته «أن» مخففة من الثقيلة واسمها محذوف، أي أنه «لن يقدر عليه أحد» والله قادر عليه.'),
(6029, 90, 6, '«يقول أهلكت» على عداوة محمد «مالا لبدا» كثيرا بعضه على بعض.'),
(6030, 90, 7, '«أيحسب أن» أي أنه «لم يره أحد» فيما أنفقه فيعلم قدره، والله عالم بقدره وأنه ليس مما يتكثر به ومجازيه على فعله السيء.'),
(6031, 90, 8, '«ألم نجعل» استفهام تقرير أي جعلنا «له عينين».'),
(6032, 90, 9, '«ولسانا وشفتين».'),
(6033, 90, 10, '«وهديناه النجدين» بينا له طريق الخير والشر.'),
(6034, 90, 11, '«فلا» فهلا «اقتحم العقبة» جاوزها.'),
(6035, 90, 12, '«وما أدراك» أعلمك «ما العقبة» التي يقتحمها تعظيما لشأنها، والجملة اعتراض وبين سبب جوازها بقوله:'),
(6036, 90, 13, '«فك رقبة» من الرق بأن أعتقها.'),
(6037, 90, 14, '«أو أطْعَمَ في يوم ذي مسغبة» مجاعة.'),
(6038, 90, 15, '«يتيما ذا مقربة» قرابة.'),
(6039, 90, 16, '«أو مسكينا ذا متربة» لصوق بالتراب لفقره، وفي قراءة بدل الفعلين مصدران مرفوعان مضاف الأول لرقبة وينون الثاني فيقدر قبل العقبة اقتحام، والقراءة المذكورة بيانه.'),
(6040, 90, 17, '«ثم كان» عطف على اقتحم وثم للترتيب الذكري، والمعنى كان وقت الاقتحام «من الذين آمنوا وتواصوا» أوصى بعضهم بعضا «بالصبر» على الطاعة وعن المعصية «وتواصوا بالمرحمة» الرحمة على الخلق.'),
(6041, 90, 18, '«أولئك» الموصوفون بهذه الصفات «أصحاب الميمنة» اليمين.'),
(6042, 90, 19, '«والذين كفروا بآياتنا هم أصحاب المشأمة» الشمال.'),
(6043, 90, 20, '«عليهم نار مؤصدة» بالهمزة والواو بدله، مطبقة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, '«والشمس وضحاها» ضوؤها.'),
(6045, 91, 2, '«والقمر إذا تلاها» تبعها طالعا عند غروبها.'),
(6046, 91, 3, '«والنهار إذا جلاها» بارتفاعه.'),
(6047, 91, 4, '«والليل إذا يغشاها» يغطيها بظلمته وإذا في الثلاثة لمجرد الظرفية والعامل فيها فعل القسم.'),
(6048, 91, 5, '«والسماء وما بناها».'),
(6049, 91, 6, '«والأرض وما طحاها» بسطها.'),
(6050, 91, 7, '«ونفس» بمعنى نفوس «وما سوَّاها» في الخلقة وما في الثلاثة مصدرية أو بمعنى من.'),
(6051, 91, 8, '«فألهمها فجورها وتقواها» بيَّن لها طريق الخير والشر وأخر التقوى رعاية لرؤوس الآي وجواب القسم:'),
(6052, 91, 9, '«قد أفلح» حذفت منه اللام لطول الكلام «مَنْ زكَّاها» طهرها من الذنوب.'),
(6053, 91, 10, '«وقد خاب» خسر «مَنْ دسَّاها» أخفاها بالمعصية وأصله دسها أبدلت السين الثانية ألفا تخفيفا.'),
(6054, 91, 11, '«كذَّبت ثمود» رسولها صالحا «بطغواها» بسبب طغيانها.'),
(6055, 91, 12, '«إذ انبعث» أسرع «أشقاها» واسمه قدار إلى عقر الناقة برضاهم.'),
(6056, 91, 13, '«فقال لهم رسول الله» صالح «ناقة الله» أي ذروها «وسقياها» شربها في يومها وكان لها يوم ولهم يوم.'),
(6057, 91, 14, '«فكذبوه» في قوله ذلك عن الله المرتب عليه نزول العذاب بهم إن خالفوه «فعقروها» قتلوها ليسلم لهم ماء شربها «فدمدم» أطبق «عليهم ربهم» العذاب «بذنبهم فسواها» أي الدمدمة عليهم، أي عمهم بها فلم يفلت منهم أحد.'),
(6058, 91, 15, '«ولا» بالواو والفاء «يخاف عقباها» تبعتها.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, '«والليل إذا يغشى» بظلمته كل ما بين السماء والأرض.'),
(6060, 92, 2, '«والنهار إذا تجلى» تكشف وظهر وإذا في الموضوعين لمجرد الظرفية والعامل فيها فعل القسم.'),
(6061, 92, 3, '«وما» بمعنى من أو مصدرية «خلق الذكر والأنثى» آدم وحواء وكل ذكر وكل أنثى، والخنثى المشكل عندنا ذكر أو أنثى عند الله تعالى فيحنث بتكليمه من حلف لا يكلم لا يكلم ذكرا ولا أنثى.'),
(6062, 92, 4, '«إن سعيكم» عملكم «لشتى» مختلف فعامل للجنة بالطاعة وعامل للنار بالمعصية.'),
(6063, 92, 5, '«فأما من أعطى» حق الله «واتقى» الله.'),
(6064, 92, 6, '«وصدَّق بالحسنى» أي بلا إله إلا الله في الموضوعين.'),
(6065, 92, 7, '«فسنسيره لليسرى» للجنة.'),
(6066, 92, 8, '«وأما من بخل» بحق الله «واستغنى» عن ثوابه.'),
(6067, 92, 9, '«وكذب بالحسنى».'),
(6068, 92, 10, '«فسنسيره» نهيئه «للعسرى» للنار.'),
(6069, 92, 11, '«وما» نافية «يغني عنه ماله إذا تردَّى» في النار.'),
(6070, 92, 12, '«إن علينا لَلهدى» لتبيين طريق الهدى من طريق الضلال ليمتثل أمرنا بسلوك الأول ونهينا عن ارتكاب الثاني.'),
(6071, 92, 13, '«وإن لنا للآخرة والأولى» أي الدنيا فمن طلبهما من غيرنا فقد أخطأ.'),
(6072, 92, 14, '«فأنذرتكم» خوفتكم يا أهل مكة «نارا تلظى» بحذف إحدى التاءين من الأصل وقريء بثبوتها، أي تتوقد.'),
(6073, 92, 15, '«لا يصلاها» يدخلها «إلا الأشقى» بمعنى الشقي.'),
(6074, 92, 16, '(الذي كذب) النبي (وتولى) عن الإيمان وهذا الحصر مؤول لقوله تعالى: "" ويغفر ما دون ذلك لمن يشاء "" فيكون المراد الصلي المؤبد.'),
(6075, 92, 17, '«وسيجنبها» يبعد عنها «الأتقى» بمعنى التقي.'),
(6076, 92, 18, '«الذي يؤتي ماله يتزكى» متزكيا به عند الله تعالى بأن يخرجه لله تعالى لا رياء ولا سمعة، فيكون زاكيا عند الله، وهذا نزل في الصدّيق رضي الله عنه لما اشترى بلالا المعذب على إيمانه وأعتقه، فقال الكفار: إنما فعل ذلك ليد كانت له عنده فنزلت.'),
(6077, 92, 19, '«وما لأحد عنده من نعمة تُجزى».'),
(6078, 92, 20, '«إلا» لكن فعل ذلك «ابتغاء وجه ربه الأعلى» أي طلب ثواب الله.'),
(6079, 92, 21, '«ولسوف يرضى» بما يُعطاه من الثواب في الجنة والآية تشمل من فعل مثل فعله رضي الله تعالى عنه فيبعده عن النار ويثاب.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, '«والضحى» أي أول النهار أو كله.'),
(6081, 93, 2, '«والليل إذا سجى» غطى بظلامه أو سكن.'),
(6082, 93, 3, '«ما ودَّعك» تركك يا محمد «ربك وما قلى» أبغضك نزل هذا لما قال الكفار عند تأخر الوحي عنه خمسة عشر يوما: إن ربه ودَّعه وقلاهُ.'),
(6083, 93, 4, '«وللآخرة خير لك» لما فيها من الكرامات لك «من الأولى» الدنيا.'),
(6084, 93, 5, '(ولسوف يعطيك ربك) في الآخرة من الخيرات عطاء جزيلا (فترضى) به فقال صلى الله عليه وسلم: "" إذن لا أرضى وواحد من أمتي في النار "" إلى هنا تم جواب القسم بمثبتين بعد منفيين.'),
(6085, 93, 6, '«ألم يجدك» استفهام تقرير أي وجدك «يتيما» بفقد أبيك قبل ولادتك أو بعدها «فآوى» بأن ضمك إلى عمك أبي طالب.'),
(6086, 93, 7, '«ووجدك ضالا» عما أنت عليه من الشريعة «فهدى» أي هداك إليها.'),
(6087, 93, 8, '(ووجدك عائلا) فقيرا (فأغنى) أغناك بما قنعك به من الغنيمة وغيرها وفي الحديث: "" ليس الغني عن كثرة العرض ولكن الغني غني النفس "".'),
(6088, 93, 9, '«فأما اليتيم فلا تقهر» بأخذ ماله أو غير ذلك.'),
(6089, 93, 10, '«وأما السائل فلا تنهر» تزجره لفقره.'),
(6090, 93, 11, '«وأما بنعمة ربك» عليك بالنبوة وغيرها «فحدّث» أخبر، وحذف ضميره في بعض الأفعال رعاية للفواصل.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, '«ألم نشرح» استفهام تقرير أي شرحنا «لك» يا محمد «صدرك» بالنبوة وغيرها.'),
(6092, 94, 2, '«ووضعنا» حططنا «عنك وزرك».'),
(6093, 94, 3, '(الذي أنقض) أثقل (ظهرك) وهذا كقوله تعالى: "" ليغفر لك الله ما تقدم من ذنبك "".'),
(6094, 94, 4, '«ورفعنا لك ذكرك» بأن تُذكر مع ذكري في الآذان والإقامة والتشهد والخطبة وغيرها.'),
(6095, 94, 5, '«فإن مع العسر» الشدة «يسرا» سهولة.'),
(6096, 94, 6, '«إن مع العسر يسراً» والنبي صلى الله عليه وسلم قاسى من الكفار شدة ثم حصل له اليسر بنصره عليهم.'),
(6097, 94, 7, '«فإذا فرغت» من الصلاة «فانصب» اتعب في الدعاء.'),
(6098, 94, 8, '«وإلى ربك فارغب» تضرع.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, '«والتين والزيتون» أي المأكولين أو جبلين بالشام ينبتان المأكولين.'),
(6100, 95, 2, '«وطور سينين» الجبل الذي كلم الله تعالى عليه موسى ومعنى سينين المبارك أو الحسن بالأشجار المثمرة.'),
(6101, 95, 3, '«وهذا البلد الأمين» مكة لأمن الناس فيها جاهلية وإسلاما.'),
(6102, 95, 4, '«لقد خلقنا الإنسان» الجنس «في أحسن تقويم» تعديل لصورته.'),
(6103, 95, 5, '«ثم رددناه» في بعض أفراده «أسفل سافلين» كناية عن الهرم والضعف فينقص عمل المؤمن عن زمن الشباب ويكون له أجره بقوله تعالى:'),
(6104, 95, 6, '(إلا) لكن (الذين آمنوا وعملوا الصالحات فلهم أجر غير ممنون) مقطوع وفي الحديث: "" إذا بلغ المؤمن من الكبر ما يعجزه عن العمل كتب له ما كان يعمل "".'),
(6105, 95, 7, '«فما يكذبك» أيها الكافر «بعد» بعد ما ذكر من خلق الإنسان في أحسن صورة ثم رده إلى أرذل العمر الدال على القدرة على البعث «بالدين» بالجزاء المسبوق بالبعث والحساب، أي ما يجعلك مكذبا بذلك ولا جاعل له.'),
(6106, 95, 8, '(أليس الله بأحكم الحاكمين) هو أقضى القاضين وحكمه بالجزاء من ذلك وفي الحديث: "" من قرأ والتين إلى آخرها فليقل: بلى وأنا على ذلك من الشاهدين "".');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, '«اقرأ» أوجد القراءة مبتدئا «باسم ربك الذي خلق» الخلائق.'),
(6108, 96, 2, '«خلق الإنسان» الجنس «من علق» جمع علقة وهي القطعة اليسيرة من الدم الغليظ.'),
(6109, 96, 3, '«اقرأ» تأكيد للأول «وربك الأكرم» الذي لا يوازيه كريم، حال من الضمير اقرأ.'),
(6110, 96, 4, '«الذي علم» الخط «بالقلم» وأول من خط به إدريس عليه السلام.'),
(6111, 96, 5, '«علم الإنسان» الجنس «ما لم يعلم» قبل تعليمه الهدى والكتابة والصناعة وغيرها.'),
(6112, 96, 6, '«كلا» حقا «إنَّ الإنسان ليطغى».'),
(6113, 96, 7, '«أن رآه» أي نفسه «استغنى» بالمال، نزل في أبي جهل، ورأى علمية واستغنى مفعول ثان وأن رآه مفعول له.'),
(6114, 96, 8, '«إن إلى ربك» يا إنسان «الرجعى» أي الرجوع تخويف له فيجازي الطاغي بما يستحقه.'),
(6115, 96, 9, '«أرأيت» في الثلاثة مواضع للتعجب «الذي ينهي» هو أبو جهل.'),
(6116, 96, 10, '«عبدا» هو النبي صلى الله عليه وسلم «إذا صلّى».'),
(6117, 96, 11, '«أرأيت إن كان» المنهي «على الهدى».'),
(6118, 96, 12, '«أو» للتقسيم «أمر بالتقوى».'),
(6119, 96, 13, '«أرأيت إن كذب» أي الناهي النبي «وتولى» عن الإيمان.'),
(6120, 96, 14, '«ألم يعلم بأن الله يرى» ما صدر منه، أي يعلمه فيجازيه عليه، أي اعجب منه يا مخاطب من حيث نهيه عن الصلاة ومن حيث أن المنهي على الهدى آمر بالتقوى ومن حيث أن الناهي مكذب متولٍ عن الإيمان.'),
(6121, 96, 15, '«كلا» ردع له «لئن» لام قسم «لم ينته» عما هو عليه من الكفر «لنسفعا بالناصية» لنجرنَّ بناصيته غلى النار.'),
(6122, 96, 16, '«ناصية» بدل نكرة من معرفة «كاذبة خاطئة» وصفها بذلك مجازًا والمراد صاحبها.'),
(6123, 96, 17, '«فليدع ناديه» أي أهل ناديه وهو المجلس ينتدى يتحدث فيه القوم وكان قال للنبي صلى الله عليه وسلم لما انتهره حيث نهاه عن الصلاة: لقد علمت ما بها رجل أكثر ناديا مني لأملأنَّ عليك هذا الوادي إن شئت خيلا جردا ورجالا مردا.'),
(6124, 96, 18, '(سندع الزبانية) الملائكة الغلاظ الشداد لإهلاكه كما في الحديث "" لو دعا ناديه لأخذته الزبانية عيانا "".'),
(6125, 96, 19, '«كلا» ردع له «لا تطعه» يا محمد في ترك الصلاة «واسجد» صلِّ لله «واقترب» منه بطاعته.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, '«إنا أنزلناه» أي القرآن جملة واحدة من اللوح المحفوظ إلى السماء الدنيا «في ليلة القدر» أي الشرف العظيم.'),
(6127, 97, 2, '«وما أدراك» أعلمك يا محمد «ما ليلة القدر» تعظيم لشانها وتعجيب منه.'),
(6128, 97, 3, '«ليلة القدر خير من ألف شهر» ليس فيها ليلة القدر فالعمل الصالح فيها خير منه في ألف شهر ليست فيها.'),
(6129, 97, 4, '«تنزَّل الملائكة» بحذف إحدى التاءين من الأصل «والروح» أي جبريل «فيها» في الليلة «بإذن ربهم» بأمره «من كل أمر» قضاه الله فيها لتلك السنة إلى قابل ومن سببيه بمعنى الباء.'),
(6130, 97, 5, '«سلام هي» خبر مقدم ومبتدأ «حتى مطلع الفجر» بفتح اللام وكسرها إلى وقت طلوعه، جُعلت سلاما لكثرة السلام فيها من الملائكة لا تمر بمؤمن ولا مؤمنة إلا سلمت عليه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, '«لم يكن الذين كفروا من» للبيان «أهل الكتاب والمشركين» أي عبدة الأصنام عطف على أهل «منفكين» خبر يكن، أي زائلين عما هم عليه «حتى تأتيهم» أي أتتهم «البينة» أي الحجة الواضحة وهي محمد صلى الله عليه وسلم.'),
(6132, 98, 2, '«رسول من الله» بدل من البينة وهو النبي صلى الله عليه وسلم «يتلو صحفاً مطهرة» من الباطل.'),
(6133, 98, 3, '«فيها كتب» أحكام مكتوبة «قيمة» مستقيمة، أي يتلو مضمون ذلك وهو القرآن، فمنهم من آمن به ومنهم من كفر.'),
(6134, 98, 4, '«وما تفرق الذين أوتوا الكتاب» في الإيمان به «إلا من بعد ما جاءتهم البينة» أي هو صلى الله عليه وسلم أو القرآن الجائي به معجزة له وقبل مجيئه صلى الله عليه وسلم كانوا مجتمعين على الإيمان به إذا جاءه فحسده من كفر به منهم.'),
(6135, 98, 5, '«وما أمروا» في كتابهم التوراة والإنجيل «إلا ليعبدوا الله» أي أن يعبدوه فحذفت أن وزيدت اللام «مخلصين له الدين» من الشرك «حنفاء» مستقيمين على دين إبراهيم ودين محمد إذا جاء فكيف كفروا به «ويقيموا الصلاة ويؤتوا الزكاة وذلك دين» الملة «القيِّمة» المستقيمة.'),
(6136, 98, 6, '«إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها» حال مقدرة، أي مقدرا خلودهم فيها من الله تعالى «أولئك هم شر البرية».'),
(6137, 98, 7, '«إن الذين آمنوا وعملوا الصالحات أولئك هم خير البرية» الخليقة.'),
(6138, 98, 8, '«جزاؤهم عند ربهم جنات عدن» إقامة «تجرى من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم» بطاعته «ورضوا عنه» بثوابه «ذلك لمن خشي ربه» خاف عقابه فانتهى عن معصيته تعالى.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, '«إذا زُلزلت الأرض» حركت لقيام الساعة «زلزالها» تحريكها الشديد المناسب لعظمتها.'),
(6140, 99, 2, '«وأخرجت الأرض أثقالها» كنوزها وموتاها فألقتها على ظهرها.'),
(6141, 99, 3, '«وقال الإنسان» الكافر بالبعث «مالها» إنكارا لتلك الحالة.'),
(6142, 99, 4, '«يومئذ» بدل من إذا وجوابها «تُحدث أخبارها» تخبر بما عمل عليها من خير وشر.'),
(6143, 99, 5, '(بأن) بسبب أن (ربك أوحى لها) أي أمرها بذلك، وفي الحديث "" تشهد على كل عبد أو أمة بكل ما عمل على ظهرها "".'),
(6144, 99, 6, '«يومئذ يصدر الناس» ينصرفون من موقف الحساب «أشتاتا» متفرقين فآخذ ذات اليمين إلى الجنة وآخذ ذات الشمال إلى النار «ليروا أعمالهم» أي جزاءها من الجنة أو النار.'),
(6145, 99, 7, '«فمن يعمل مثقال ذرة» زنة نملة صغيرة «خيرا يره» ير ثوابه.'),
(6146, 99, 8, '«ومن يعمل مثقال ذرة شرا يره» ير جزاءه.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, '«والعاديات» الخيل تعدو في الغزو وتضبح «ضبحا» هو صوت أجوافها إذا عدت.'),
(6148, 100, 2, '«فالموريات» الخيل توري النار «قدحا» بحوافرها إذا سارت في الأرض ذات الحجارة بالليل.'),
(6149, 100, 3, '«فالمغيرات صبحا» الخيل تغير على العدو وقت الصبح بإغارة أصحابها.'),
(6150, 100, 4, '«فأثرن» هيجن «به» بمكان عدوهن أو بذلك الوقت «نقعا» غبارا بشدة حركتهن.'),
(6151, 100, 5, '«فوسطن به» بالنقع «جمعا» من العدو، أي صرن وسطه وعطف الفعل على الاسم لأنه في تأويل الفعل أي واللاتي عدون فأورين فأغرن.'),
(6152, 100, 6, '«إن الإنسان» الكافر «لربه لكنود» لكفور يجحد نعمته تعالى.'),
(6153, 100, 7, '«وإنه على ذلك» أي كنوده «لشهيد» يشهد على نفسه بصنعه.'),
(6154, 100, 8, '«وإنه لحب الخير» أي المال الحب له فيبخل به.'),
(6155, 100, 9, '«أفلا يعلم إذا بُعثر» أثير وأخرج «ما في القبور» من الموتى، أي بعثوا.'),
(6156, 100, 10, '«وحصِّل» بين وأفرز «ما في الصدور» القلوب من الكفر والإيمان.'),
(6157, 100, 11, '«إن ربهم بهم يومئذ لخبير» لعالم فيجازيهم على كفرهم، أعيد الضمير جمعا نظرا لمعنى الإنسان وهذه الجملة دلت على مفعول يعلم، أي إنا نجازيه وقت ما ذكر وتعلق خبير بيومئذ وهو تعالى خبير دائما لأنه يوم المجازاة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, '«القارعة» القيامة التي تقرع القلوب بأهوالها.'),
(6159, 101, 2, '«ما القارعة» تهويل لشأنها وهما مبتدأ وخبر القارعة.'),
(6160, 101, 3, '«وما أدراك» أعلمك «ما القارعة» زيادة تهويل لها وما الأولى مبتدأ وما بعدها خبره وما الثانية وخبرها في محل المفعول الثاني لأدرى.'),
(6161, 101, 4, '«يوم» ناصبه دل عليه القارعة، أي تقرع «يكون الناس كالفراش المبثوث» كغوغاء الجراد المنتشر يموج بعضهم في بعض للحيرة إلى أن يُدعوا للحساب.'),
(6162, 101, 5, '«وتكون الجبال كالعهن المنفوش» كالصوف المندوف في خفة سيرها حتى تستوي مع الأرض.'),
(6163, 101, 6, '«فأما من ثقلت موازينه» بأن رجحت حسناته على سيئاته.'),
(6164, 101, 7, '«فهو في عيشة راضية» في الجنة، أي ذات رضى بأن يرضاها، أي مرضية له.'),
(6165, 101, 8, '«وأما من خفَّت موازينه» بأن رجحت سيئاته على حسناته.'),
(6166, 101, 9, '«فأمه» فمسكنه «هاوية».'),
(6167, 101, 10, '«وما أدراك ماهيه» أي ما هاوية.'),
(6168, 101, 11, 'هي «نار حامية» شديدة الحرارة وهاء هيَهْ للسكت تثبت وصلا ووقفا وفي قراءة تحذف وصلاً.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, '«ألهاكم» شغلكم عن طاعة الله «التكاثر» التفاخر بالأموال والأولاد والرجال.'),
(6170, 102, 2, '«حتى زرتم المقابر» بأن متم فدفنتم فيها، أو عددتم الموتى تكاثرا.'),
(6171, 102, 3, '«كلا» ردع «سوف تعلمون».'),
(6172, 102, 4, '«ثم كلا سوف تعلمون» سوء عاقبة تفاخركم عند النزع ثم في القبر.'),
(6173, 102, 5, '«كلا» حقا «لو تعلمون علم اليقين» علما يقينا عاقبة التفاخر ما اشتغلتم به.'),
(6174, 102, 6, '«لترون الجحيم» النار جواب قسم محذوف وحذف منه لام الفعل وعينه وألقت حركتها على الراء.'),
(6175, 102, 7, '«ثم لترونها» تأكيد «عين اليقين» مصدر لأن رأى وعاين بمعنى واحد.'),
(6176, 102, 8, '«ثم لتسألُن» حذف منه نون الرفع لتوالي النونات وواو ضمير الجمع لالتقاء الساكنين «يومئذ» يوم رؤيتها «عن النعيم» ما يلتذ به في الدنيا من الصحة والفراغ والأمن والمطعم والمشرب وغير ذلك.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, '«والعصر» الدهر أو ما بعد الزوال إلى الغروب أو صلاة العصر.'),
(6178, 103, 2, '«إنَّ الإنسان» الجنس «لفي خُسر» في تجارته.'),
(6179, 103, 3, '«إلا الذين آمنوا وعملوا الصالحات» فليسوا في خسران «وتواصوا» أوصى بعضهم بعضاً «بالحق» الإيمان «وتواصوا بالصبر» على الطاعة وعن المعصية.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, '«ويل» كلمة عذاب أو وادٍ في جهنم «لكل هُمزة لُمزة» أي كثير الهمز واللمز، أي الغيبة نزلت فيمن كان يغتاب النبي صلى الله عليه وسلم والمؤمنين كأُمية بن خلف والوليد بن المغيرة وغيرهما.'),
(6181, 104, 2, '«الذي جمع» بالتخفيف والتشديد «مالا وعدده» أحصاه وجعله عدة لحوادث الدهر.'),
(6182, 104, 3, '«يحسب» لجهله «أن ماله أخلده» جعله خالدا لا يموت.'),
(6183, 104, 4, '«كلا» ردع «لينبذن» جواب قسم محذوف، أي ليطرحن «في الحطمة» التي تحطم كل ما ألقي فيها.'),
(6184, 104, 5, '«وما أدراك» أعلمك «ما الحطمة».'),
(6185, 104, 6, '«نار الله الموقدة» المسعرة.'),
(6186, 104, 7, '«التي تطَّلع» تشرف «على الأفئدة» القلوب فتحرقها وألمها أشد من ألم غيرها للطفها.'),
(6187, 104, 8, '«إنها عليهم» جمع الضمير رعاية لمعنى كل «مؤصدة» بالهمز والواو بدله، مطبقة.'),
(6188, 104, 9, '«في عمد» بضم الحرفين وبفتحهما «ممددة» صفة لما قبله فتكون النار داخل العمد.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, '«ألم تر» استفهام تعجب، أي اعجب «كيف فعل ربك بأصحاب الفيل» هو محمود وأصحابه أبرهة ملك اليمن وجيشه، بنى بصنعاء كنيسة ليصرف إليها الحجاج عن مكة فأحدث رجل من كنانة فيها ولطخ قبلتها بالعذرة احتقارا بها، فحلف أبره ليهدمنَّ الكعبة، فجاء مكة بجيشه على أفيال اليمن مقدمها محمود، فحين توجهوا لهدم الكعبة أرسل الله عليهم ما قصَّه في قوله:'),
(6190, 105, 2, '«ألم يجعل» أي جعل «كيدهم» في هدم الكعبة «في تضليل» خسارة وهلاك.'),
(6191, 105, 3, '«وأرسل عليهم طيراً أبابيل» جماعات جماعات، قيل لا واحد له كأساطير، وقيل واحده: أبول أو بال أو أبيل كعجول ومفتاح وسكين.'),
(6192, 105, 4, '«ترميهم بحجارة من سجيل» طين مطبوخ.'),
(6193, 105, 5, '«فجعلهم كعصف مأكول» كورق زرع أكلته الدواب وداسته وأفنته، أي أهلكهم الله تعالى كل واحد بحجره المكتوب عليه أسمه، وهو أكبر من العدسة وأصغر من الحمصة يغرق البيضة والرجل والفيل ويصل الأرض، وكان هذا عام مولد النبي صلى الله عليه وسلم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, '«لإيلاف قريش».'),
(6195, 106, 2, '«إيلافهم» تأكيد وهو مصدر آلف بالمد «رحلة الشتاء» إلى اليمن «و» رحلة «الصيف» إلى الشام في كل عام، يستعينون بالرحلتين للتجارة على المقام بمكة لخدمة البيت الذي هو فخرهم، وهم ولد النضر بن كنانة.'),
(6196, 106, 3, '«فليعبدوا» تعلق به لإيلاف والفاء زائدة «رب هذا البيت».'),
(6197, 106, 4, '«الذي أطعمهم من جوع» أي من أجله «وآمنهم من خوف» أي من أجله وكان يصيبهم الجوع لعدم الزرع بمكة وخافوا جيش الفيل.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, '«أرأيت الذي يُكذِّب بالدين» بالجزاء والحساب، أي هل عرفته وإن لم تعرفه.'),
(6199, 107, 2, '«فذلك» بتقدير هو بعد الفاء «الذي يَدُعُّ اليتيم» أي يدفعه بعنف عن حقه.'),
(6200, 107, 3, '«ولا يحض» نفسه ولا غيره «على طعام المسكين» أي إطعامه، نزلت في العاص بن وائل أو الوليد بن المغيرة.'),
(6201, 107, 4, '«فويل للمصلين».'),
(6202, 107, 5, '«الذين هم عن صلاتهم ساهون» غافلون يؤخرونها عن وقتها.'),
(6203, 107, 6, '«الذين هم يراءون» في الصلاة وغيرها.'),
(6204, 107, 7, '«ويمنعون الماعون» كالإبرة والفأس والقدر والقصعة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, '«إنا أعطيناك» يا محمد «الكوثر» هو نهر في الجنة هو حوضه ترد عليه أمته، والكوثر: الخير الكثير من النبوَّة والقرآن والشفاعة ونحوها.'),
(6206, 108, 2, '«فصلِّ لربك» صلاة عيد النحر «وانحر» نسكك.'),
(6207, 108, 3, '«إن شانئك» أي مُبغضك «هو الأبتر» المنقطع عن كل خير، أو المنقطع العقب، نزلت في العاص بن وائل سمى النبي صلى الله عليه وسلم أبتر عند موت ابنه القاسم.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, '«قل يا أيها الكافرون».'),
(6209, 109, 2, '«لا أعبد» في الحال «ما تعبدون» من الأصنام.'),
(6210, 109, 3, '«ولا أنتم عابدون» في الحال «ما أعبد» وهو الله تعالى وحده.'),
(6211, 109, 4, '«ولا أنا عابد» في الاستقبال «ما عبدتم».'),
(6212, 109, 5, '«ولا أنتم عابدون» في الاستقبال «ما أعبد» علم الله منهم أنهم لا يؤمنون، وإطلاق ما على الله على وجه المقابلة.'),
(6213, 109, 6, '«لكم دينكم» الشرك «ولي دين» الإسلام وهذا قبل أن يؤمر بالحرب وحذف ياء الإضافة القراء السبعة وقفا ووصلا وأثبتها يعقوب في الحالين.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, '«إذا جاء نصر الله» نبيَّه صلى الله عليه وسلم على أعدائه «والفتح» فتح مكة.'),
(6215, 110, 2, '«ورأيت الناس يدخلون في دين الله» أي الإسلام «أفواجا» جماعات بعدما كان يدخل فيه واحد واحد، وذلك بعد فتح مكة جاءه العرب من أقطار الأرض طائعين.'),
(6216, 110, 3, '«فسبح بحمد ربك» أي متلبسا بحمده «واستغفره إنه كان توابا» وكان صلى الله عليه وسلم بعد نزول هذه السورة يكثر من قول: سبحان الله وبحمده، أستغفر الله وأتوب إليه، وعلم بها أنه قد أقترب أجله وكان فتح مكة في رمضان سنة ثمان وتوفي صلى الله عليه وسلم في ربيع الأول سنة عشر.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'لما دعا النبي صلى الله عليه وسلم قومه وقال: إني نذير لكم بين يدي عذاب شديد، فقال عمه أبو لهب: تبًا لك ألهذا دعوتنا، نزل «تبت» خسرت «يدا أبي لهب» أي جملته وعبر عنها باليدين مجازًا لأن أكثر الأفعال تزاول بهما، وهذه الجملة دعاء «وتبَّ» خسر هو، وهذه خبر كقولهم: أهلكه الله وقد هلك، ولما خوَّفه النبي بالعذاب، فقال: إن كان ابن أخي حقاً فإني أفتدي منه بمالي وولدي نزل: «ما أغنى عنه ماله وما كسب».'),
(6218, 111, 2, '«ما أَغنى عنه ماله وما كسب» أي وكسبه، أي ولده ما أغنى بمعنى يغني.'),
(6219, 111, 3, '«سيصلى ناراً ذات لهب» أي تلهب وتوقد فهي مآل تكنيته لتلهب وجهه إشراقاً وحمرة.'),
(6220, 111, 4, '«وامرأته» عطف على ضمير يصلى سوغه الفصل بالمفعول وصفته وهي أم جميل «حمالة» بالرفع والنصب «الحطب» الشوك والسعدان تلقيه في طريق النبي صلى الله عليه وسلم.'),
(6221, 111, 5, '«في جيدها» عنقها «حبل من مسد» أي ليف وهذه الجملة حال من حمالة الحطب الذي هو نعت لامرأته أو خبر مبتدأ مقدر.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'سئل النبي صلى الله عليه وسلم عن ربه فنزل: «قل هو الله أحد» فالله خبر هو وأحد بدل منه أو خبر ثان.'),
(6223, 112, 2, '«الله الصمد» مبتدأ وخبر أي المقصود في الحوائج على الدوام.'),
(6224, 112, 3, '«لم يلد» لانتفاء مجانسته «ولم يولد» لانتفاء الحدوث عنه.'),
(6225, 112, 4, '«ولم يكن له كفواً أحد» أي مكافئًا ومماثلاً، وله متعلق بكفوًا، وقُدِّم عليه لأنه مَحطُّ القصد بالنفي وأَخَّر أحد وهو اسم يكن عن خبرها رعاية للفاصلة.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, '«قل أعوذ برب الفلق» الصبح.'),
(6227, 113, 2, '«من شر ما خلق» من حيوان مكلف وغير مكلف وجماد كالسم وغير ذلك.'),
(6228, 113, 3, '«ومن شر غاسق إذا وقب» أي الليل إذا أظلم والقمر إذا غاب.'),
(6229, 113, 4, '«ومن شر النفاثات» السواحر تنفث «في العقد» التي تعقدها في الخيط تنفخ فيها بشيء تقوله من غير ريق، وقال الزمخشري معه كبنات لبيد المذكور.'),
(6230, 113, 5, '«ومن شر حاسد إذا حسد» أظهر حسده وعمل بمقتضاه، كلبيد المذكور من اليهود الحاسدين للنبي صلى الله عليه وسلم، وذكر الثلاثة الشامل لها ما خلق بعده لشدة شرها.');
INSERT INTO `ar_jalalayn` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, '«قل أعوذ برب الناس» خالقهم ومالكهم خُصُّوا بالذكر تشريفا لهم ومناسبة للاستفادة من شر الموسوس في صدورهم.'),
(6232, 114, 2, '«ملك الناس».'),
(6233, 114, 3, '«إله الناس» بدلان أو صفتان أو عطفا بيان وأظهر المضاف إليه فيهما زيادة للبيان.'),
(6234, 114, 4, '«من شر الوسواس» الشيطان سمي بالحدث لكثرة ملابسته له «الخناس» لأنه يخنس ويتأخر عن القلب كلما ذكر الله.'),
(6235, 114, 5, '«الذي يوسوس في صدور الناس» قلوبهم إذا غفلوا عن ذكر الله.'),
(6236, 114, 6, '(من الجنة والناس) بيان للشيطان الموسوس أنه جني وإنسي، كقوله تعالى: "" شياطين الإنس والجن "" أو من الجنة بيان له والناس عطف على الوسواس وعلى كل يشتمل شر لبيد وبناته المذكورين، واعترض الأول بأن الناس لا يوسوس في صدورهم الناس إنما يوسوس في صدورهم الجن، وأجيب بأن الناس يوسوسون أيضاً بمعنى يليق بهم في الظاهر ثم تصل وسوستهم إلى القلب وتثبت فيه بالطريق المؤدي إلى ذلك والله تعالى أعلم.');
