INSERT INTO `ber_mensur` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'S tidep, Nuzen ed Nuê i ugdud is: "endeô agdud ik, uqbel a ten id ias uâaqeb aqeôêan".'),
