INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'U ime Allaha, Milostivog, Milosrdnog!'),
(2, 1, 2, 'Hvala Allahu, Gospodaru svjetova,'),
(3, 1, 3, 'Milostivom, Milosrdnom,'),
(4, 1, 4, 'Vladaru Dana sudnjeg!'),
(5, 1, 5, 'Tebe obožavamo i od Tebe pomoć tražimo.'),
(6, 1, 6, 'Uputi nas na put pravi'),
(7, 1, 7, 'Put onih kojima si blagodat darovao, Ne onih na kojima je srdžba, niti zalutalih.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Alif. Lam. Mim.'),
(9, 2, 2, 'Taj Kitab, u kojeg nema sumnje, Uputa je za bogobojazne,'),
(10, 2, 3, 'One koji vjeruju u nevidljivo i obavljaju salat i od onog čim smo ih opskrbili, udjeljuju,'),
(11, 2, 4, 'I one koji vjeruju u ono šta je objavljeno tebi i šta je objavljeno prije tebe, a u Ahiret su oni sigurni.'),
(12, 2, 5, 'Takvi su na Uputi od Gospodara njihovog, i ti takvi su uspješni.'),
(13, 2, 6, 'Uistinu! Oni koji ne vjeruju - isto im je opominjao ih ili ih ne opominjao - (oni) neće vjerovati.'),
(14, 2, 7, 'Zapečatio je Allah srca njihova i sluh njihov, i na vidovima njihovim je pokrivka; a imaće oni kaznu užasnu.'),
(15, 2, 8, 'I od ljudi je ko govori: "Vjerujemo u Allaha i u Dan posljednji", a oni nisu vjernici,'),
(16, 2, 9, '(Misle da) varaju Allaha i one koji vjeruju - a ne varaju, izuzev duše svoje, a ne opažaju.'),
(17, 2, 10, 'U srcima njihovim je bolest. Pa - povećava Allah njima bolest, a imaće oni kaznu bolnu za ono što lažu.'),
(18, 2, 11, 'A kad im se kaže: "Ne pravite fesad na Zemlji", govore: "Mi smo samo mirotvorci."'),
(19, 2, 12, 'Uistinu oni, oni su mufsidi, ali ne opažaju!'),
(20, 2, 13, 'I kad im se kaže: "Vjerujte kao što vjeruju ljudi", govore: "Zar da vjerujemo kao što vjeruju budale?" Uistinu oni, oni su budale, ali ne znaju!'),
(21, 2, 14, 'I kad sretnu one koji vjeruju, govore: "Vjerujemo!" - a kad se osame sa šejtanima svojim, kažu: "Uistinu, mi smo s vama, mi se samo rugamo."'),
(22, 2, 15, 'Allah se ruga njima i pušta ih da u pretjeranosti svojoj lutaju.'),
(23, 2, 16, 'Takvi su oni koji su kupili zabludu za Uputu; pa ne profitira trgovina njihova i nisu upućeni.'),
(24, 2, 17, 'Primjer njihov je kao primjer onog ko potpali vatru, pa pošto osvijetli ono oko njega, ukloni Allah svjetlo njihovo i ostavi ih u tminama (pa oni) ne vide.'),
(25, 2, 18, 'Gluhi, nijemi, slijepi su pa se ne povraćaju;'),
(26, 2, 19, 'Ili kao kišni oblak s neba, u njemu tmine i grmljavina i sijevanje. Stavljaju prste svoje u uši svoje zbog gromova, bojeći se smrti; a Allah je Onaj koji obuhvata nevjernike.'),
(27, 2, 20, 'Gotovo da munja oduzme vidove njihove. Kad god im bljesne, pođu u njoj, a kad im zamrači, stanu. A da hoće Allah, sigurno bi oduzeo sluh njihov i vidove njihove. Uistinu! Allah nad svakom stvari ima moć.'),
(28, 2, 21, 'O ljudi! Obožavajte Gospodara vašeg koji je stvorio vas i one prije vas, da biste se vi zaštitili;'),
(29, 2, 22, 'Koji je za vas učinio Zemlju ležištem, a nebo zdanjem; i spušta s neba vodu, pa izvodi njome od plodova opskrbu za vas. Zato ne postavljajte Allahu jednake, a vi znate.'),
(30, 2, 23, 'A ako ste u sumnji o onom šta smo objavili robu Našem, tad dajte suru sličnu njoj i pozovite svjedoke svoje mimo Allaha, ako istinu govorite.'),
(31, 2, 24, 'Pa ako ne učinite - a nećete učiniti - tad se bojte vatre, one čije će gorivo biti ljudi i kamenje. Pripremljena je za nevjernike.'),
(32, 2, 25, 'I obraduj one koji vjeruju i čine dobra djela, da će oni imati bašče ispod kojih teku rijeke. Kad god budu opskrbljeni iz njih plodom - opskrbom, reći će: "Ovo je ono čim smo bili opskrbljeni ranije", a biće im dato tome slično; i imaće oni u njima družice čiste, i oni će u njima biti vječito.'),
(33, 2, 26, 'Uistinu! Allah se ne stidi da navede primjer (kao) što je komarac, te ono što je iznad njega. Pa što se tiče onih koji vjeruju, ta znaju da je to Istina od Gospodara njihovog; a što se tiče onih koji ne vjeruju pa govore: "Šta želi Allah ovim primjerom?" - zavodi njime mnoge i upućuje njime mnoge. A ne zavodi njime, izuzev grješnike,'),
(34, 2, 27, 'Oni koji krše zavjet Allahov nakon potvrde njegove, i sijeku ono šta je Allah naredio da bude sjedinjeno i čine fesad na Zemlji: ti takvi su gubitnici.'),
(35, 2, 28, 'Kako ne vjerujete u Allaha, a bili ste mrtvi, pa vas je oživio, zatim će vas usmrtiti! Potom će vas oživiti, zatim ćete Njemu biti vraćeni.'),
(36, 2, 29, 'On je Taj koji je stvorio za vas sve što je na Zemlji, zatim se usmjerio ka nebu, te ih uredio (kao) sedam nebesa; a On je o svakoj stvari Znalac.'),
(37, 2, 30, 'I kad reče Gospodar tvoj melecima: "Uistinu! Ja sam Taj koji će načiniti na Zemlji halifu." Rekoše: "Zar ćeš načiniti na njoj onog ko će fesad praviti na njoj i prolivati krv, a mi (Te) slavimo sa hvalom Tvojom i svetost Ti kličemo?" Reče: "Uistinu! Ja znam ono šta ne znate."'),
(38, 2, 31, 'I pouči Adema imenima - svakom od njih - zatim ih izloži melecima, pa reče: "Obavijestite Me o imenima ovih, ako ste iskreni."'),
(39, 2, 32, 'Rekoše: "Slava neka je Tebi! Nemamo mi znanja, izuzev šta si nas naučio. Uistinu! Ti, Ti si Znalac, Mudri."'),
(40, 2, 33, 'Reče: "O Ademe! Obavijesti ih o imenima njihovim." Pa pošto ih obavijesti o imenima njihovim, reče: \'Zar vam nisam rekao: \'Uistinu! Ja znam nevidljivo nebesa i Zemlje i znam šta objelodanjujete i šta krijete."'),
(41, 2, 34, 'I kad rekosmo melecima: "Učinite sedždu Ademu!" Tad padoše na sedždu, izuzev Iblisa. Odbi i uzoholi se i bi od nevjernika.'),
(42, 2, 35, 'I rekosmo: "O Ademe! Nastani se ti i žena tvoja u Džennetu i jedite (plodove) iz njega obilno, gdje god hoćete; a ne približavajte se ovom drvetu pa (da) budete od zalima."'),
(43, 2, 36, 'Tad učini šejtan da njih dvoje iz njega pokliznu, te ih izvede iz onog u čemu su bili. I rekosmo: "Siđite! Vi ste jedni drugima neprijatelj; a imaćete vi na Zemlji prebivalište i uživanje neko vrijeme."'),
(44, 2, 37, 'Tad primi Adem od Gospodara svog riječi, pa mu oprosti. Uistinu! On, On je Primalac pokajanja, Milosrdni.'),
(45, 2, 38, 'Rekosmo: "Siđite iz njega svi! Pa sigurno će vam doći od Mene Uputa, pa ko bude slijedio Uputu Moju, tad neće biti straha nad njima i neće oni tugovati.'),
(46, 2, 39, 'A oni koji ne budu vjerovali i poricali ajete Naše, takvi će biti stanovnici vatre; oni će u njoj biti vječno.'),
(47, 2, 40, 'O sinovi Israilovi! Sjetite se blagodati Moje kojom sam vas obdario i ispunite zavjet Meni, ispuniću zavjet vama, i Mene - ta Mene se bojte!'),
(48, 2, 41, 'I vjerujte u ono šta sam objavio - potvrdu za ono što je uz vas - i ne budite prvi nevjernik u njega. I ne prodajite ajete Moje za cijenu malu i Mene - ta Mene se bojte,'),
(49, 2, 42, 'I ne presvlačite Istinu s neistinom, i ne skrivajte Istinu, a vi znate.'),
(50, 2, 43, 'I obavljajte salat i dajite zekat, i činite ruku\' sa onima koji čine ruku\'.'),
(51, 2, 44, 'Zar naređujete ljudima dobročinstvo, a zaboravljate duše svoje, a vi čitate Knjigu? Pa zar ne shvatate?'),
(52, 2, 45, 'I tražite pomoć u strpljenju i salatu; a uistinu, to je teško, izuzev skrušenima,'),
(53, 2, 46, 'Koji znaju da će oni sresti Gospodara svog i da će se oni Njemu vratiti.'),
(54, 2, 47, 'O sinovi Israilovi! Sjetite se blagodati Moje kojom sam vas obdario i da sam Ja odlikovao vas nad svjetovima,'),
(55, 2, 48, 'I bojte se Dana (kad) neće koristiti duša duši ništa, niti se primiti od nje posredovanje, niti uzimati od nje nadoknada, niti će oni biti pomognuti;'),
(56, 2, 49, 'I kad vas spasismo od ljudstva faraonovog: izlagali su vas najgoroj kazni, klali sinove vaše, a pošteđivali žene vaše: a u tom je bilo veliko iskušenje od Gospodara vašeg.'),
(57, 2, 50, 'I kad rastavismo za vas more, pa vas spasismo, a potopismo ljudstvo faraonovo i vi gledaste;'),
(58, 2, 51, 'I kad odredismo Musau četrdeset noći, potom uzeste tele poslije njega, a vi ste bili zalimi,'),
(59, 2, 52, 'Zatim smo vam oprostili poslije toga, da biste vi zahvaljivali.'),
(60, 2, 53, 'I kad dadosmo Musau Knjigu i Furkan, da biste se vi uputili;'),
(61, 2, 54, 'I kad reče Musa narodu svom: "O narode moj! Uistinu, vi ste učinili zulm dušama svojim, vašim uzimanjem teleta. Zato se pokajte Tvorcu svom, te ubijte duše svoje." To je najbolje za vas kod Tvorca vašeg, zato će vam oprostiti. Uistinu! On, On je Primalac pokajanja, Milosrdni.'),
(62, 2, 55, 'I kad rekoste: "O Musa! Nećemo ti vjerovati dok ne vidimo Allaha otvoreno." Tad vas je udario grom, a vi ste gledali;'),
(63, 2, 56, 'Zatim smo vas podigli poslije smrti vaše, da biste vi zahvaljivali.'),
(64, 2, 57, 'I zasjenili smo vas oblakom (bijelim), i spustili na vas mennu i prepelice. "Jedite od dobrih stvari kojima smo vas opskrbili." I nisu učinili zulm Nama, nego su činili zulm dušama svojim.'),
(65, 2, 58, 'I kad rekosmo: "Uđite u ovaj grad pa jedite u njemu gdje god hoćete, obilno, i uđite na kapiju sa sedždom i recite: "Hitta" - oprostićemo vam greške vaše i povećaćemo dobročiniteljima.'),
(66, 2, 59, 'Tad su zamijenili oni koji su činili zulm riječ, drugom koja im je rečena, pa smo spustili na one koji su činili zulm kaznu s neba, zato što su griješili.'),
(67, 2, 60, 'I kad zatraži Musa za narod svoj napajanje, tad rekosmo: "Udri štapom svojim kamen!" Tad provri iz njega dvanaest izvora. Doista je znalo svako pleme pojilište svoje. "Jedite i pijte iz opskrbe Allahove, i ne činite zlo na zemlji, praveći fesad."'),
(68, 2, 61, 'I kad rekoste: "O Musa! Nećemo trpiti hranu jednu, zato prizivaj za nas Gospodara svog (da) nam iznikne (nešto) od onog čime rađa zemlja - od povrća njenog i krastavaca njenih i žita njenog i leće njene i luka njenog." Reče: "Hoćete li zamijeniti ono što je bolje, onim što je lošije? Siđite u grad pa ćete uistinu vi imati šta ste tražili." I pogodi ih poniženje i bijeda, a navukli su srdžbu od Allaha. To što oni nisu vjerovali u ajete Allahove i ubijali vjerovjesnike bez prava; to što nisu slušali, a prevršivali su.'),
(69, 2, 62, 'Uistinu! Oni koji vjeruju i koji su jevreji i kršćani i Sabijci - ko vjeruje u Allaha i Dan posljednji i čini dobro - pa imaće oni nagradu svoju kod Gospodara njihovog, i bez straha nad sobom će biti i neće oni žaliti.'),
(70, 2, 63, 'I kad uzesmo zavjet vaš i uzdigosmo nad vama brdo: "Uzmite čvrsto šta smo vam dali i spominjite ono šta je u njemu, da biste se vi zaštitili."'),
(71, 2, 64, 'Zatim ste se okrenuli poslije toga. Pa da nije dobrote Allahove prema vama i milosti Njegove, sigurno biste bili od gubitnika.'),
(72, 2, 65, 'I doista ste znali one između vas koji su prekršili (stvari) sebta, pa smo im rekli: "Budite majmuni prezreni!"'),
(73, 2, 66, 'Pa smo učinili nju zastrašujućim primjerom za ono ispred nje i ono iza nje, i poukom bogobojaznima.'),
(74, 2, 67, 'I kad reče Musa narodu svom: "Uistinu! Allah vam naređuje da zakoljete kravu." Rekoše: "Zar ćeš nas uzeti izrugivanju?" Reče: "Tražim zaštitu u Allaha, da ne budem od neznalica."'),
(75, 2, 68, 'Rekoše: "Moli za nas Gospodara svog, da nam objasni kakva je ona." Reče: "Doista, On kaže: \'Uistinu, to je krava ni stara ni mlada; srednja, između toga; zato učinite šta vam se naređuje."'),
(76, 2, 69, 'Rekoše: "Moli za nas Gospodara svog da nam objasni, kakva je boja njena". Reče: "Doista, On kaže: \'Uistinu, to je žuta krava. Svijetla je boja njena, raduje posmatrače."'),
(77, 2, 70, 'Rekoše: "Moli za nas Gospodara svog da nam objasni kakva je ona. Uistinu, krave su za nas slične, a uistinu mi ćemo, ako htjedne Allah, biti upućeni."'),
(78, 2, 71, 'Reče: "Doista, On kaže: \'Uistinu ta krava nije podvrgnuta oranju zemlje niti natapanju usjeva. Besprijekorna je, bez biljega na sebi." Rekoše: "Sad si iznio istinu." Tad je zaklaše i jedva (to) učiniše.'),
(79, 2, 72, 'I kad ubiste dušu, pa se prepiraste o njoj; a Allah je Taj koji je iznio ono šta ste krili -'),
(80, 2, 73, 'Tad rekosmo: "Udarite ga dijelom njenim!" Eto tako oživljava Allah mrtve i pokazuje vam znakove Svoje, da biste vi shvatili.'),
(81, 2, 74, 'Zatim su otvrdla srca vaša poslije toga, te su ona bila kao kamen ili tvrđa. A uistinu, od kamena (postoji) onaj iz kojeg izviru rijeke, i uistinu od njega (postoji) onaj što raspukne, pa izbija iz njega voda, i uistinu, od njega (postoji) onaj što pada iz straha od Allaha; a nije Allah nemaran za ono šta radite.'),
(82, 2, 75, 'Pa zar žudite da vam vjeruju, a već je grupa njih slušala Riječ Allahovu, zatim su je iskrivljavali nakon što bi je shvatili, a oni znaju.'),
(83, 2, 76, 'I kad sretnu one koji vjeruju, kažu: "Vjerujemo", a kad se osame jedni s drugima, govore: "Pričate li im o onom šta vam je otkrio Allah, da biste se vi prepirali o tome kod Gospodara vašeg." Pa zar ne razumijete?'),
(84, 2, 77, 'A zar ne znaju da Allah zna šta skrivaju i šta objelodanjuju?'),
(85, 2, 78, 'I od njih (ima) neukih, ne znaju Knjigu, jedino želje, a oni samo nagađaju.'),
(86, 2, 79, 'Zato teško onima koji pišu knjigu rukama svojim, pa kažu: "Ovo je od Allaha", da bi stekli time vrijednost malu. Pa teško njima zbog onog šta pišu ruke njihove, i teško njima zbog onog šta zarađuju!'),
(87, 2, 80, 'I govore: "Neće nas doticati vatra, izuzev nekoliko dana." Reci: "Jeste li primili od Allaha obećanje - pa neće prekršiti Allah obećanje Svoje - ili govorite protiv Allaha šta ne znate?"'),
(88, 2, 81, 'Svakako! Ko stekne zlodjelo i opkoli ga pogreška njegova - pa takvi će biti stanovnici vatre, oni će u njoj biti vječito.'),
(89, 2, 82, 'A oni koji vjeruju i rade dobra djela, takvi će biti stanovnici Dženneta, oni će u njemu biti vječito.'),
(90, 2, 83, 'I kad uzesmo zavjet sinova Israilovih: "Ne obožavajte, izuzev Allaha, a roditeljima dobro (činite) i rodbini i siročadi i bijednicima, i govorite ljudima dobro, i obavljajte salat i dajite zekat." Zatim ste se odvratili, izuzev malo vas a vi ste oni koji se odvraćaju.'),
(91, 2, 84, 'I kad uzesmo zavjet vaš: "Ne prolivajte krv svoju i ne protjerujte se iz kuća vaših", zatim ste potvrdili, a vi svjedočite.'),
(92, 2, 85, 'Zatim ste se vi takvi ubijali i protjerali grupu vas iz kuća njihovih, pomagali ste protiv njih grijehom i neprijateljstvom. A ako vam dođu zarobljenici, otkupljujete ih, a njihovo protjerivanje - to vam je zabranjeno. Pa zar vjerujete u dio Knjige, a ne vjerujete u dio? Pa jedino je plaća onome od vas ko to čini, poniženje u životu Dunjaa, i na Dan kijameta biće vraćen najžešćoj kazni; a nije Allah nemaran za ono šta radite.'),
(93, 2, 86, 'Takvi su oni koji su kupili život Dunjaa Ahiretom, zato se neće njima olakšati kazna, niti će oni biti pomognuti.'),
(94, 2, 87, 'I doista smo dali Musau Knjigu i slali iza njega poslanike. I dali smo Isau, sinu Merjeminu, dokaze jasne i pomogli ga Ruhul-kudusom. Zar se niste, kad god bi vam poslanik donio ono što ne žele duše vaše, uzoholili? Tad biste grupu porekli, a grupu pobili.'),
(95, 2, 88, 'I rekoše: "Naša srca su omoti." Naprotiv, prokleo ih je Allah zbog nevjerovanja njihovog - pa malo što vjeruju.'),
(96, 2, 89, 'Pa pošto im dođe Knjiga od Allaha, potvrda za ono što je uz njih - a prije su tražili pobjedu nad onima koji ne vjeruju - pa pošto im dođe šta prepoznaju, ne povjerovaše u njega: zato neka je prokletstvo Allahovo nad nevjernicima.'),
(97, 2, 90, 'Jadno je ono za što su prodali duše svoje: da ne vjeruju u ono šta je objavio Allah, iz zavisti što objavljuje Allah iz dobrote Svoje, kome hoće od robova Svojih. Pa su navukli srdžbu na srdžbu; a imaće nevjernici kaznu prezrenu.'),
(98, 2, 91, 'I kad im se kaže: "Vjerujte u ono šta je objavio Allah", kažu: "Vjerujemo u ono šta je objavljeno nama", a ne vjeruju u ono iza toga, a to je Istina koja potvrđuje ono uz njih. Reci: "Pa zašto ste ubijali vjerovjesnike Allahove ranije, ako ste bili vjernici?"'),
(99, 2, 92, 'I doista vam je došao Musa s dokazima jasnim, zatim ste uzeli tele poslije njega, i vi ste bili zalimi.'),
(100, 2, 93, 'I kad uzesmo zavjet vaš i uzdigosmo nad vama brdo: "Uzmite čvrsto šta smo vam dali i slušajte." Rekoše: "Čuli smo, a nećemo poslušati." I upilo se u srca njihova tele s nevjerovanjem njihovim. Reci: "Jadno je ono što vam naređuje vjerovanje vaše, ako ste vjernici."'),
(101, 2, 94, 'Reci: "Ako je za vas kuća Ahireta kod Allaha posebno mimo ljudi, tad zaželite smrt, ako ste iskreni."'),
(102, 2, 95, 'A neće je zaželiti nikada, zbog onog šta su unaprijed poslale ruke njihove; a Allah je Znalac zalima.'),
(103, 2, 96, 'I sigurno ćeš ih naći pohlepnijim ljudima prema življenju i od onih koji pridružuju. Volio bi pojedinac njihov da živi hiljadu godina. A da živi, ne bi on bio udaljen od kazne. A Allah je Vidilac onog što rade.'),
(104, 2, 97, 'Reci: "Ko je neprijatelj Džibrilu?" Pa uistinu on ga spušta na srce tvoje, s dozvolom Allahovom, (kao) potvrdu za ono ispred njega, i Uputu i radosnu vijest vjernicima.'),
(105, 2, 98, 'Ko je neprijatelj Allahu i melecima Njegovim i poslanicima Njegovim i Džibrilu i Mikalu? Pa uistinu! Allah je neprijatelj nevjernicima!'),
(106, 2, 99, 'I doista smo ti objavili ajete jasne, a u njih neće vjerovati samo grješnici.'),
(107, 2, 100, 'Zar! Kad god sklope sporazum, odbaci ga skupina između njih. Naprotiv, većina njih ne vjeruje.'),
(108, 2, 101, 'I pošto im je došao Poslanik od Allaha, potvrđujući ono što je uz njih, odbacila je skupina onih kojima je data Knjiga, Knjigu Allahovu iza leđa svojih, kao da oni ne znaju,'),
(109, 2, 102, 'I slijede šta su kazivali šejtani o carstvu Sulejmanovom. A nije nevjerovao Sulejman; međutim šejtani nisu vjerovali učeći ljude sihru i onom šta je spušteno dvojici meleka u Babilu, Harutu i Marutu. A nisu učili nikoga dok ne bi rekli: "Mi smo samo iskušenje, zato nemoj nevjerovati." Pa su učili od njih dvojice ono čime će prouzročiti razdvajanje između muža i žene njegove; a nisu oni štetili time nikome, izuzev s dozvolom Allahovom; i učili su šta će im štetiti, a neće im koristiti. I doista su znali da onaj ko to kupuje - sigurno on neće imati u Ahiretu nikakva udjela. A sigurno je loše ono zašto su prodali duše svoje, kad bi znali.'),
(110, 2, 103, 'A da su oni vjerovali i bojali se, sigurno bi nagrada od Allaha bila bolja, kad bi znali!'),
(111, 2, 104, 'O vi koji vjerujete! Ne govorite: "Ra\'ina!" - a govorite "Unzurna!" i slušajte; a imaće nevjernici kaznu bolnu.'),
(112, 2, 105, 'Ne vole oni koji ne vjeruju između sljedbenika Knjige niti mušrici da vam se spusti ikakvo dobro od Gospodara vašeg. A Allah odabire milosti Svojoj koga hoće, i Allah je Posjednik dobrote veličanstvene.'),
(113, 2, 106, 'Ne uklonimo nijedan znak, niti ga učinimo zaboravljenim, (a da ne) donesemo bolji od njega ili njemu jednak. Zar ne znaš da Allah nad svakom stvari ima moć?'),
(114, 2, 107, 'Zar ne znaš da je Allah Taj čija je vlast nebesa i Zemlje?, a nemate vi mimo Allaha nikakva zaštitnika, niti pomagača.'),
(115, 2, 108, 'Zar želite da pitate Poslanika svog, kao što je pitan Musa ranije? A ko zamijeni nevjerovanjem vjerovanje, tad je doista izgubio ravni put.'),
(116, 2, 109, 'Voljeli bi mnogi od sljedbenika Knjige da vas povrate nakon vjerovanja vašeg, u nevjernike, zavišću iz duša svojih, nakon što im je postala jasna Istina. Zato oprostite i prijeđite, dok Allah ne izda naredbu Svoju. Uistinu! Allah nad svakom stvari ima moć.'),
(117, 2, 110, 'I obavljajte salat i dajite zekat; a šta unaprijed pošaljete za duše vaše od dobrog, naći ćete ga kod Allaha. Uistinu! Allah je onog što radite Vidilac.'),
(118, 2, 111, 'I govore: "Ući će u Džennet jedino onaj ko je jevrej ili kršćanin." To su želje njihove! Reci: "Donesite dokaz svoj, ako istinu govorite!"'),
(119, 2, 112, 'Svakako! Ko preda lice svoje Allahu i on bude dobročinitelj, pa imaće on nagradu svoju kod Gospodara svog, i neće biti straha nad njima, niti će oni žaliti.'),
(120, 2, 113, 'I kažu jevreji: "Nisu kršćani ni na čemu", i kažu kršćani: "Nisu Jevreji ni na čemu", a oni čitaju Knjigu. Tako su govorili oni koji ne znaju - poput riječi njihove. Pa presudiće Allah među njima na Dan kijameta o onom u čemu su se razilazili.'),
(121, 2, 114, 'A ko je nepravedniji od onog ko zabranjuje mesdžide Allahove, da se spominje u njima ime Njegovo i trudi se za rušenje njihovo? Takvi - nije za njih da ulaze u njih, izuzev prestrašeni. Imaće oni na Dunjau poniženje i imaće oni na Ahiretu kaznu strahovitu.'),
(122, 2, 115, 'A Allahov je istok i zapad; pa kud god se okrenete, pa tamo je lice Allahovo. Uistinu! Allah je Sveobuhvatni, Znalac.'),
(123, 2, 116, 'I govore: "Uzeo je Allah dijete." Slava neka je Njemu! Naprotiv, Njegovo je šta je na nebesima i Zemlji; sve je Njemu pokorno.'),
(124, 2, 117, 'Začetnik nebesa i Zemlje! A kad odluči stvar, tad samo kaže tome: "Budi", tad biva.'),
(125, 2, 118, 'I govore oni koji ne znaju: "Zašto ne govori s nama Allah ili nam ne dadne znak." Tako su govorili oni prije njih - poput riječi njihove: slična su srca njihova. Doista smo objasnili znakove ljudima koji su sigurni.'),
(126, 2, 119, 'Uistinu! Mi smo poslali tebe s Istinom, (kao) donosioca radosnih vijesti i opominjača. A nećeš biti pitan o stanovnicima džehima.'),
(127, 2, 120, 'I neće biti zadovoljni tobom jevreji ni kršćani, dok ne budeš slijedio millet njihov. Reci: "Uistinu! Uputa Allahova - to je Uputa." A ako bi slijedio strasti njihove, nakon što ti je došlo nešto znanja, ne bi ti imao od Allaha nikakva zaštitnika, niti pomagača.'),
(128, 2, 121, 'Oni kojima smo dali Knjigu, uče je ispravnim učenjem njenim - takvi vjeruju u nju. A ko ne vjeruje u nju - pa ti takvi su gubitnici.'),
(129, 2, 122, 'O sinovi Israilovi! Sjetite se blagodati Moje koju sam vam podario i da sam Ja bio odlikovao vas nad svjetovima.'),
(130, 2, 123, 'I bojte se Dana kad neće koristiti duša duši nimalo, niti će se primiti od nje nadoknada, i neće joj koristiti posredovanje, niti će oni biti pomognuti.'),
(131, 2, 124, 'I kad je kušao Ibrahima Gospodar njegov riječima, pa ih je ispunio, reče: "Uistinu! Ja sam Taj koji je učinio tebe imamom ljudima." Reče: "A od potomstva mog?" (Allah) reče: "Moje obećanje neće doseći zalime."'),
(132, 2, 125, 'I kad smo učinili Kuću stjecištem ljudima i sigurnošću, i: "Uzmite stajalište Ibrahimovo musallom", i naložili Ibrahimu i Ismailu: "Očistite Kuću Moju za one koji čine tawwaf i one koji čine itikaf i one koji čine ruku\', i one koji čine sedždu."'),
(133, 2, 126, 'I kad reče Ibrahim: "Gospodaru moj! Učini ovaj grad sigurnim i opskrbi stanovnike njegove (raznim) plodovima, onog od njih ko vjeruje u Allaha i Dan posljednji." (Allah) reče: "A ko ne vjeruje - pa daću da malo uživa, zatim ću ga natjerati u kaznu vatrom", a loše li je dolazište!'),
(134, 2, 127, 'I kad je podizao Ibrahim temelje Kuće i Ismail: "Gospodaru naš! Primi od nas. Uistinu! Ti, Ti si Onaj koji čuje, Znalac.'),
(135, 2, 128, 'Gospodaru naš! I učini nas dvojicu predanim Tebi i od potomstva našeg ummet predan Tebi. I pokaži nam obrede naše i oprosti nam. Uistinu! Ti, Ti si Primalac pokajanja, Milosrdni.'),
(136, 2, 129, 'Gospodaru naš! I podigni među njima poslanika od njih, da im uči ajete Tvoje i pouči ih Knjizi i mudrosti i očisti ih. Uistinu! Ti, Ti si Moćni, Mudri."'),
(137, 2, 130, 'A ko ne želi millet Ibrahimov, do onaj ko zabudali dušu svoju? I doista, odabrali smo njega na Dunjau, a uistinu, on će na Ahiretu biti od pravednih.'),
(138, 2, 131, 'Kad reče njemu Gospodar njegov: "Predaj se!" Reče: "Predajem se Gospodaru svjetova."'),
(139, 2, 132, 'I naložio je to Ibrahim sinovima svojim, i Jakub: "O sinovi moji! Uistinu je Allah odabrao za vas vjeru, pa umrite vi jedino kao muslimani."'),
(140, 2, 133, 'Zar ste bili svjedoci kad dođe Jakubu smrt, kad reče sinovima svojim: "Šta ćete obožavati poslije mene?" Rekoše: "Obožavaćemo Boga tvog i Boga predaka tvojih, Ibrahima i Ismaila i Ishaka, Boga Jedinog i mi smo Njemu predani."'),
(141, 2, 134, 'Taj narod je već nestao. Njegovo je šta je zaradio, a vaše šta ste zaradili, i nećete biti pitani za ono šta su radili.'),
(142, 2, 135, 'I govore: "Budite jevreji ili kršćani, bit ćete upućeni." Reci: "Naprotiv, millet Ibrahima hanife", a nije bio od mušrika.'),
(143, 2, 136, 'Recite: "Vjerujemo u Allaha i ono što je objavljeno nama, i ono što je objavljeno Ibrahimu i Ismailu i Ishaku i Jakubu i plemenima i ono što je dato Musau i Isau, i što je dato vjerovjesnicima od Gospodara njihovog. Ne pravimo razliku između ijednog od njih, i mi smo Njemu pokorni."'),
(144, 2, 137, 'Pa ako budu vjerovali u slično onom u što vjerujete, tad su se doista uputili. A ako se odvrate, tad su samo oni u raskolu. Pa dovoljan će ti biti protiv njih Allah; a On je Onaj koji čuje, Znalac.'),
(145, 2, 138, 'Bojenje Allahovo!, a ko je bolji od Allaha bojenjem? A mi smo Njegovi obožavatelji.'),
(146, 2, 139, 'Reci: "Zar raspravljate s nama o Allahu, a On je naš Gospodar i vaš Gospodar? I nama pripadaju djela naša, a vama djela vaša, i mi smo Njemu odani."'),
(147, 2, 140, 'Zar govorite: "Uistinu, Ibrahim i Ismail i Ishak i Jakub i plemena, bili su jevreji ili kršćani?" Reci: "Da li ste vi najbolji znalac ili Allah?" A ko je nepravedniji od onog ko skriva kod sebe svjedočanstvo od Allaha? A nije Allah nemaran prema onom šta radite.'),
(148, 2, 141, 'Ta zajednica je već nestala, njeno je šta je zaslužila, a vaše šta ste zaslužili. I nećete biti pitani za ono šta su radili.'),
(149, 2, 142, 'Budale između ljudi će reći: "Šta ih je odvratilo od kible njihove - one za koju su bili?" Reci: "Allahov je istok i zapad, upućuje koga hoće putu ispravnom."'),
(150, 2, 143, 'I tako smo vas učinili zajednicom srednjom, da budete svjedoci protiv ljudi i bude Poslanik protiv vas svjedok. I načinili smo kiblu - onu za koju si - jedino da razaznamo ko slijedi Poslanika, od onog ko će se okrenuti na petama svojim. A sigurno je teško, osim onima koje je uputio Allah. I neće Allah dati da propadne vjerovanje vaše. Uistinu, Allah je ljudima Samilostan, Milosrdan.'),
(151, 2, 144, 'Doista vidimo okretanje tvog lica nebu. Pa sigurno ćemo te okrenuti kibli koja će te zadovoljiti. Zato okreni lice svoje prema Mesdžidul-haramu. I ma gdje bili okrećite lica svoja u pravcu njega. A uistinu, oni kojima je data Knjiga, znaju da je to Istina od Gospodara njihovog; a nije Allah nemaran prema onom šta rade.'),
(152, 2, 145, 'I kad bi donio onima kojima je data Knjiga svaki znak, ne bi slijedili kiblu tvoju. A nećeš ti biti sljedbenik kible njihove, i neće oni biti sljedbenici kible drugih. A ako bi slijedio želje njihove, nakon što ti je došlo nešto znanja, uistinu bi ti tad bio od zalima.'),
(153, 2, 146, 'Oni kojima smo dali Knjigu prepoznaju ga kao što prepoznaju sinove svoje. A uistinu, skupina njih krije Istinu, a oni znaju.'),
(154, 2, 147, 'Istina je od Gospodara tvog, zato nikako ne budi od sumnjivaca.'),
(155, 2, 148, 'Za svakog ima smjer kojem se on okreće. Zato se natječite dobrim djelima. Gdje god bili, dovešće vas Allah sve. Uistinu! Allah nad svakom stvari ima moć.'),
(156, 2, 149, 'I odakle god izađeš, ta okreni lice svoje u pravcu Mesdžidul-harama. A doista, to je Istina od Gospodara tvog, i nije Allah nemaran prema onom šta radite.'),
(157, 2, 150, 'I otkud god izađeš, pa okreni lice svoje u pravcu Mesdžidul-harama; i ma gdje bili, ta okrećite lica svoja u pravcu njega, da ne bi imali ljudi izgovor protiv vas, izuzev onih od njih koji čine zulm - zato se ne bojte njih, a bojte se Mene - da bih upotpunio blagodat Svoju prema vama i da biste se vi uputili.'),
(158, 2, 151, 'Kao što smo poslali među vas Poslanika od vas - uči vam ajete Naše i čisti vas i uči vas Knjizi i mudrosti, i uči vas onom šta niste znali.'),
(159, 2, 152, 'Zato se sjetite Mene, sjetiću se vas, i zahvaljujte Mi i ne niječite Mene!'),
(160, 2, 153, 'O vi koji vjerujete! Tražite pomoć u strpljenju i salatu. Uistinu! Allah je sa strpljivima.'),
(161, 2, 154, 'I ne recite za onog ko je ubijen na putu Allahovom: "Mrtvi su!" Naprotiv, živi su, međutim ne opažate.'),
(162, 2, 155, 'A sigurno ćemo vas iskušavati, malo strahom i glađu i gubljenjem imetaka i duša i plodova; a obraduj strpljive,'),
(163, 2, 156, 'Koji, kad ih zadesi nesreća kažu: "Uistinu, mi smo Allahovi i uistinu, mi smo Njemu povratnici."'),
(164, 2, 157, 'Nad tim takvima su salawati Gospodara njihovog i milost, a ti takvi - oni su upućeni.'),
(165, 2, 158, 'Uistinu! Safa i Merwa su od znakova Allahovih. Zato ko hodočasti Kuću ili obavi umru, tad mu nije grijeh da obiđe njih dvoje. A ko dobrovoljno učini dobro, pa uistinu, Allah je Zahvalni, Znalac.'),
(166, 2, 159, 'Uistinu, oni koji skrivaju šta smo objavili od dokaza jasnih i Upute, nakon što smo to objasnili ljudima u Knjizi - te takve će prokleti Allah i prokleće ih koji proklinju,'),
(167, 2, 160, 'Izuzev onih koji se kaju i popravljaju i obejane - pa to su ti kojima opraštam; a Ja sam Primalac pokajanja, Milosrdni.'),
(168, 2, 161, 'Uistinu, oni koji ne vjeruju i umru, a oni budu nevjernici, na tim takvima je prokletstvo Allahovo, i meleka i ljudi svih.'),
(169, 2, 162, 'Vječno će biti u njemu. Neće im se olakšati kazna, niti će im biti odloženo.'),
(170, 2, 163, 'A vaš Bog je Bog Jedini!, nema boga osim Njega, Milostivog, Milosrdnog.'),
(171, 2, 164, 'Uistinu! U stvaranju nebesa i Zemlje, i promjeni noći i dana, i plovilu koje plovi morem sa onim što koristi ljudima, i onom šta spušta Allah od vode s neba, pa oživljava njome zemlju nakon mrtvila njenog - a rasijao je po njoj od svake životinje - i promjeni vjetrova i oblacima potčinjenim između neba i zemlje - znaci su za ljude koji shvataju.'),
(172, 2, 165, 'I od ljudi je ko uzima mimo Allaha takmace (Njemu), vole ih kao voljenjem Allaha. A koji vjeruju, jače su ljubavi prema Allahu. A kad bi (mogli) vidjeti oni koji su činili zulm, kad ugledaju kaznu, da će moć biti Allahova sva i da će Allah biti žestoke kazne...'),
(173, 2, 166, 'Kad se odreknu oni koji su bili slijeđeni, onih koji su slijedili, i vide kaznu, i presjeku im se veze,'),
(174, 2, 167, 'I reknu oni koji su slijedili: "Da nam je povratak, pa da ih se odreknemo kao što su se odrekli nas." Eto tako će im pokazati Allah djela njihova (kao) žalosti za njih. I neće oni biti ti koji će izaći iz vatre.'),
(175, 2, 168, 'O ljudi! Jedite od onog šta je na zemlji dopušteno, dobro, i ne slijedite korake šejtanove. Uistinu, on vam je neprijatelj otvoreni.'),
(176, 2, 169, 'Samo vam naređuje zlo i razvrat, i da govorite protiv Allaha šta ne znate.'),
(177, 2, 170, 'I kad im se kaže: "Slijedite šta je objavio Allah", govore: "Naprotiv, slijedimo ono na čemu smo zatekli očeve naše." Zar?, mada očevi njihovi nisu shvatali ništa, niti su bili upućeni!'),
(178, 2, 171, 'A primjer onih koji ne vjeruju je kao primjer onog ko viče ono šta čuje samo zov i viku. Gluhi su, nijemi, slijepi, zato oni ne shvataju.'),
(179, 2, 172, 'O vi koji vjerujete! Jedite od dobrih stvari kojima smo vas opskrbili, i zahvaljujte Allahu, ako Njega obožavate.'),
(180, 2, 173, 'Jedino vam zabranjuje lešinu i krv i meso svinje, i ono nad čim je zazvano drugo ime, a ne Allahovo. Pa ko bude natjeran, bez želje i pretjerivanja, tad mu nije grijeh. Uistinu! Allah je Oprosnik, Milosrdni.'),
(181, 2, 174, 'Uistinu, oni koji skrivaju šta je objavio Allah od Knjige i uzimaju time vrijednost malu, takvi jedu u trbuhe svoje samo vatru. I neće govoriti Allah s njima na Dan kijameta, niti će ih očistiti; a imaće oni kaznu bolnu.'),
(182, 2, 175, 'Takvi su oni koji su kupili zabludu s Uputom i kaznu oprostom. Pa kako su strpljivi oni nad vatrom?'),
(183, 2, 176, 'To zato što je Allah objavio Knjigu sa Istinom. A uistinu, oni koji se razilaze o Knjizi, u raskolu su dalekom.'),
(184, 2, 177, 'Nije čestitost da okrećete lica svoja prema istoku i zapadu, nego je čestitost ko vjeruje u Allaha i Dan posljednji i meleke i Knjigu i vjerovjesnike; i daje imetak za ljubav Njegovu rodbini i siročadi i siromasima i sinu puta i prosjacima i za robove; i obavlja salat i daje zekat; i koji budu ispunjavali obećanje svoje kad obećaju, i strpljivi u bijedi i nevolji i vremenu sukoba. Takvi su oni koji su iskreni, a ti takvi su bogobojazni.'),
(185, 2, 178, 'O vi koji vjerujete! Propisuje vam se odmazda za ubojstvo: slobodan za slobodnog, rob za roba i žensko za žensko. Pa onom kome oprosti brat njegov nešto, tad je nastavak uljudan i podmirenje je njemu sa dobročinstvom. To je olakšanje od Gospodara vašeg i milost. Pa ko prevrši nakon ovog, ta imaće on kaznu bolnu.'),
(186, 2, 179, 'A vama je u odmazdi život, o posjednici razuma, da biste se vi zaštitili.'),
(187, 2, 180, 'Propisuje vam se: kad se približi nekom od vas smrt, a ostavlja hajr, oporuka je na dvoje roditelja i rodbinu pristojno; dužnost je bogobojaznih.'),
(188, 2, 181, 'Pa ko je zamijeni nakon što ju je čuo - grijeh toga je samo na onima koji je mijenjaju. Uistinu! Allah je Onaj koji čuje, Znalac.'),
(189, 2, 182, 'A ko se boji skretanja ili grijeha oporučitelja, te ih izmiri, pa nema mu grijeha. Uistinu! Allah je Oprosnik, Milosrdni.'),
(190, 2, 183, 'O vi koji vjerujete! Propisuje vam se post, kao što je propisan onima prije vas, da biste se vi zaštitili,'),
(191, 2, 184, 'Određen broj dana. Pa ko od vas bude bolestan ili na putovanju, tad (isti) broj drugih dana; a koji ga (jedva) podnose, fidja je hranjenje siromaha. Pa ko dobrovoljno učini dobro - pa to je bolje za njega. A da postite - bolje je za vas, kad biste znali!'),
(192, 2, 185, 'Mjesec ramadan je taj u kojem je spušten Kur\'an, Uputa ljudima i jasni dokazi Upute i Furkan. Pa ko bude prisutan od vas (u tom) mjesecu, tad neka ga posti. A ko bude bolestan ili na putovanju, tad (isti) broj drugih dana. Allah vam želi lahkoću, a ne želi vam teškoću, i da ispunite broj, i da veličate Allaha što vas je uputio, i da biste vi zahvaljivali.'),
(193, 2, 186, 'A kad te upitaju robovi Moji o Meni, pa uistinu sam Ja blizu! Odazivam se dowi molitelja kad Me priziva. Zato neka Mi se odazovu i neka vjeruju u Mene, da bi oni bili na putu ispravnom.'),
(194, 2, 187, 'Dopušta vam se (u) noći posta refes sa ženama vašim. One su odjeća vaša, a vi ste odjeća njihova. Zna Allah da ste vi obmanjivali duše svoje pa je primio pokajanje vaše i oprostio vam. Zato im sada pristupajte i tražite šta je propisao Allah za vas. I jedite i pijte dok vi ne budete razlikovali nit bijelu od crne niti zore - zatim ispunite post do noći. A ne pristupajte im i (kad) vi budete mu\'tekifi u mesdžidima; to su granice Allahove, zato im se ne približujte. Tako objašnjava Allah znakove Svoje ljudima, da bi se oni pobojali.'),
(195, 2, 188, 'I ne jedite imetke vaše između sebe uzaludno, i ne nudite ga sudijama da biste pojeli dio imetaka ljudi grješno, a vi znate.'),
(196, 2, 189, 'Pitaju te o mlađacima - reci: "Oni su određena vremena za ljude i hadždž." A nije pravednost da ulazite u kuće s leđa njihovih, nego je pravednost ko se boji. I ulazite u kuće na vrata njihova, i bojte se Allaha da biste vi uspjeli.'),
(197, 2, 190, 'I borite se na putu Allahovom protiv onih koji se bore protiv vas, a ne prekoračujte. Uistinu! Allah ne voli one koji prevršuju.'),
(198, 2, 191, 'I ubijajte ih gdje god ih nađete, i istjerajte ih odakle god istjeruju vas. A fitneluk je gori od ubijanja. I ne borite se protiv njih kod Mesdžidul-harama, dok vas ne napadnu u njemu. Pa ako vas napadnu, tad ih ubijajte. Takva je plaća nevjernicima.'),
(199, 2, 192, 'Pa ako prestanu, pa uistinu, Allah je Oprosnik, Milosrdni.'),
(200, 2, 193, 'I borite se protiv njih dok ne bude (više) fitneluka i (ne) bude vjera Allahova. Pa ako prestanu, tad nema neprijateljstva, izuzev prema zalimima!'),
(201, 2, 194, 'Mjesec sveti za mjesec sveti, i svetinje su odmazda. Pa onog ko napadne vas, tad napadnite (i vi) njega, slično kao što vas je napao. I bojte se Allaha i znajte da je Allah uz bogobojazne.'),
(202, 2, 195, 'I trošite na putu Allahovom, a ne bacajte se rukama svojim u propast; i činite dobro. Uistinu! Allah voli dobročinitelje.'),
(203, 2, 196, 'I obavljajte hadždž i umru radi Allaha! Pa ako budete spriječeni, tad (zakoljite) šta je dostupno od kurbana, i ne brijte glave svoje dok ne stigne kurban odredištu svom. Pa ko od vas bude bolestan ili u njemu bude bol od glave njegove, tad je iskup post ili sadaka ili kurban. Pa kad budete sigurni, onda ko se zadovolji umrom do hadždža, tad (neka zakolje) šta je dostupno od kurbana. Pa ko ne nađe, tad je post tri dana na hadždžu i sedam kad se vratite: to je deset ukupno. To je za onog čija porodica nije prisutna u Mesdžidul-haramu. I bojte se Allaha i znajte da je Allah žestok kaznom.'),
(204, 2, 197, 'Hadždž je u mjesecima poznatim, pa ko se u njima obaveže za hadždž, tad nema refesa i nema griješenja, niti svađe na hadždžu. A šta učinite od dobrog, zna ga Allah. I snabdijte se; pa uistinu, najbolja oprema je bogobojaznost - i bojte se Mene, o posjednici razuma!'),
(205, 2, 198, 'Nije vam grijeh da tražite blagodat od Gospodara vašeg. Zato kad pohrlite sa Arefata, tad spominjite Allaha kod Meš\'aril-harama. I spominjite Ga kao što vas je uputio, mada ste prije toga bili od zalutalih.'),
(206, 2, 199, 'Zatim hrlite kud god hrle ljudi, i tražite oprost od Allaha. Uistinu, Allah je Oprosnik, Milosrdni.'),
(207, 2, 200, 'Pa kad završite obrede svoje tad spominjite Allaha kao vašim spomenom otaca vaših ili jačim spomenom. Ta od ljudi je ko govori: "Gospodaru naš! Daj nam na Dunjau", a neće biti za njega na Ahiretu nikakva udjela.'),
(208, 2, 201, 'I od njih je ko govori: "Gospodaru naš! Daj nam na Dunjau dobro i na Ahiretu dobro i sačuvaj nas kazne vatrom."'),
(209, 2, 202, 'Ti takvi će imati udio od onog šta su zaslužili; a Allah je brz obračunom.'),
(210, 2, 203, 'I spominjite Allaha u danima nabrojanim. Pa ko požuri u dva dana, pa nije mu grijeh. A ko odloži, pa nije grijeh onom ko se boji. I bojte se Allaha i znajte da ćete vi Njemu biti sabrani.'),
(211, 2, 204, 'I od ljudi je onaj čiji te govor zadivljuje u životu Dunjaa, i poziva svjedokom Allaha za ono što je u srcu njegovom, a on je najzagriženiji od protivnika -'),
(212, 2, 205, 'I kad se okrene, brza po zemlji, da bi činio fesad na njoj i uništio usjev i priplod; a Allah ne voli fesad.'),
(213, 2, 206, 'I kad mu se kaže: "Boj se Allaha!", obuzme ga snaženje grijehom. Zato je njemu dovoljan Džehennem; a doista je loše odmorište!'),
(214, 2, 207, 'I između ljudi je ko prodaje dušu svoju tražeći zadovoljstvo Allahovo; a Allah je Samilostan robovima Svojim.'),
(215, 2, 208, 'O vi koji vjerujete! Stupite u Islam svi i ne slijedite korake šejtanove. Uistinu, on vam je neprijatelj otvoreni.'),
(216, 2, 209, 'Pa ako posrnete nakon što su vam došli dokazi jasni, tad znajte da je Allah Moćni, Mudri.'),
(217, 2, 210, 'Da li samo čekaju da im dođe Allah u tminama oblaka i meleci, i bude stvar svršena? A Allahu se vraćaju stvari.'),
(218, 2, 211, 'Upitaj sinove Israilove koliko smo im jasnog znaka dali. A ko izmijeni blagodat Allahovu nakon što mu je došla, pa uistinu Allah je žestok kaznom.'),
(219, 2, 212, 'Uljepšan je život Dunjaa onima koji ne vjeruju i izruguju se onima koji vjeruju. A oni koji se boje, biće iznad njih na Dan kijameta. A Allah opskrbljuje koga hoće bez računa.'),
(220, 2, 213, 'Bili su ljudi zajednica jedna, pa je slao Allah vjerovjesnike, donosioce radosnih vijesti i opominjače, i objavljivao uz njih Knjigu s Istinom, da sudi ljudima o onome u čemu su se razilazili. A nisu se razilazili o njoj, izuzev oni kojima je ona data, nakon što su im došli jasni dokazi, zavišću međusobnom. Pa je uputio Allah one koji su vjerovali onom u čemu su se razilazili od Istine, s dozvolom Svojom; a Allah upućuje koga hoće putu pravom.'),
(221, 2, 214, 'Zar mislite da ćete ući u Džennet, a još vam nije došao primjer onih koji su prošli prije vas. Snalazile su ih bijeda i nevolja i bili su potresani dok je govorio poslanik i oni koji su vjerovali s njim: "Kad će pomoć Allahova?" Pa uistinu! Pomoć Allahova je blizu!'),
(222, 2, 215, 'Pitaju te šta da udjeljuju. Reci: "Ono šta trošite od dobrog, pa (to je) za dvoje roditelja i rodbinu i siročad i siromahe i sina puta." A šta uradite od dobrog, pa uistinu - Allah je o tome Znalac.'),
(223, 2, 216, 'Propisuje vam se borba, a ona ja za vas odvratnost. I moguće je da nešto mrzite, a to je dobro za vas; i moguće je da volite nešto, a to je zlo za vas. A Allah zna, a vi ne znate.'),
(224, 2, 217, 'Pitaju te o mjesecu svetom, ratovanju u njemu. Reci: "Ratovanje u njemu je velik (grijeh) - a odvraćanje od puta Allahovog i nevjerovanje u Njega, i Mesdžidul-harama i progon stanovnika njegovih iz njega - veće je kod Allaha. A fitneluk je gori od ubijanja." I neće se prestati boriti protiv vas dok vas ne odvrate od vjere vaše, ako mognu. A ko od vas odbaci din svoj pa umre, a on bude nevjernik: pa takvima će propasti djela njihova na Dunjau i Ahiretu i takvi će biti stanovnici vatre, oni će u njoj biti vječito.'),
(225, 2, 218, 'Uistinu, oni koji vjeruju i koji se isele i bore na putu Allahovom - takvi se nadaju milosti Allahovoj. A Allah je Oprosnik, Milosrdni.'),
(226, 2, 219, 'Pitaju te o alkoholu i kocki. Reci: "U njima dvoma je grijeh veliki i korist za ljude, a grijeh njihov je veći od koristi njihove." Pitaju te šta da udjeljuju. Reci: "Višak." Tako Allah objašnjava vama znakove, da biste vi razmišljali,'),
(227, 2, 220, 'O Dunjau i Ahiretu. I pitaju te o siročadi. Reci: "Popravljanje je za njih najbolje." A ako se izmiješate s njima, pa braća su vaša. A Allah razaznaje smutljivca od dobročinitelja. A da je htio Allah, sigurno bi vas preopteretio. Uistinu! Allah je Moćni, Mudri.'),
(228, 2, 221, 'I ne ženite mušrikinje, dok ne budu vjerovale. A sigurno je robinja vjernica bolja od idolopoklonke, makar vas zadivljavala. I ne ženite mušrike (vašim ženskima), dok ne budu vjerovali. A sigurno je rob vjernik bolji od idolopoklonika, makar vas zadivljavao. Takvi pozivaju vatri, a Allah poziva Džennetu i oprostu s dopuštenjem Svojim i objašnjava znakove Svoje ljudima, da bi se oni podsjetili.'),
(229, 2, 222, 'I pitaju te o menstruaciji. Reci: "To je neprijatnost." Zato ostavite žene u menstruaciji i ne približujte im se dok se ne očiste. Pa kad se očiste, tad im prilazite kako vam je odredio Allah. Uistinu, Allah voli pokajanike i voli čiste.'),
(230, 2, 223, 'Žene vaše su njiva vaša, zato prilazite njivi svojoj kako hoćete, i pripravite za duše svoje. I bojte se Allaha i znajte da ćete Ga vi susresti; a obraduj vjernike.'),
(231, 2, 224, 'I ne činite Allaha ciljem zakletvi vaših, da ne biste činili dobro i bili bogobojazni i mirili ljude. A Allah je Onaj koji čuje, Znalac.'),
(232, 2, 225, 'Neće vas kazniti Allah zbog nehotičnog u zakletvama vašim. Međutim, kazniće vas za ono šta su zaslužila srca vaša. A Allah je Oprosnik, Blagi.'),
(233, 2, 226, 'Oni koji se zakunu ženama svojim, čekat će četiri mjeseca; pa ako se povrate, pa uistinu Allah je Oprosnik, Milosrdni.'),
(234, 2, 227, 'A ako se odluče na razvod, pa uistinu Allah je Onaj koji čuje, Znalac.'),
(235, 2, 228, 'A raspuštenice čekaju dušama svojim tri menstruacije. I ne dozvoljava im se da skrivaju šta je stvorio Allah u matericama njihovim, ako vjeruju u Allaha i Dan posljednji. A muževi njihovi su dostojniji vraćanju njihovom u tom (roku), ako žele popravljanje. I imaju one slično (pravo) onom (koje imaju ljudi) nad njima pristojno, a imaju ljudi nad njima stepen. A Allah je Moćni, Mudri.'),
(236, 2, 229, 'Razvod (može biti) dva puta, pa je zadržavanje (poslije toga) uljudno ili puštanje lijepo. I ne dopušta vam se da uzmete išta od onog šta ste im dali, izuzev ako se oboje budu bojali da neće stajati u granicama Allahovim. Pa ako se bojite da neće stajati u granicama Allahovim, tad njima dvoma nije grijeh u onome čime će se (ona) iskupiti. To su granice Allahove, zato ih ne prelazite! A ko pređe granice Allahove, pa ti takvi su zalimi.'),
(237, 2, 230, 'Pa ako je razvede (treći put), tad mu se (ona) poslije ne dozvoljava, dok se ne uda za muža mimo njega. Pa ako je razvede (drugi muž), tad nema prestupa njima dvoma da se povrate, ako misle da će čuvati granice Allahove. A to su granice Allahove, objašnjava ih ljudima koji znaju.'),
(238, 2, 231, 'I kad razvedete žene, pa dospiju roku svom, tad ih zadržite uljudno ili ih pustite uljudno. I ne zadržavajte ih nevoljom, da biste prekršili. A ko to učini, pa doista je učinio zulm duši svojoj. I ne uzimajte ajete Allahove ruglom i sjetite se blagodati Allahove prema vama, i šta vam je objavio od Knjige i mudrosti - savjetuje vas njome. I bojte se Allaha i znajte da je Allah o svakoj stvari Znalac.'),
(239, 2, 232, 'I kad razvedete žene pa dospiju roku njihovom, tad ih ne sprječavajte da se udaju za muževe njihove, kad se oni sporazume međsobno uljudno. To se savjetuje od vas onom ko vjeruje u Allaha i Dan posljednji. To je časnije za vas i čišće; a Allah zna, a vi ne znate.'),
(240, 2, 233, 'I majke će zadajati djecu svoju dvije pune (godine), za onog ko želi da upotpuni dojenje. A na ocu: njegovo je opskrbljivanje njihovo i odijevanje njihovo pristojno. Ne opterećuje se duša izuzev sposobnošću njenom. Nek ne bude oštećena majka djetetom svojim, niti otac djetetom svojim. I na nasljedniku je to isto. Pa ako njih dvoje žele odbijanje (djeteta) po sporazumu njihovom i dogovoru, tad im nema prekršaja. A ako želite naći dojilju djeci svojoj, tad vam nije grijeh - kad položite šta dajete uljudno. I bojte se Allaha i znajte da je Allah onog šta radite Vidilac.'),
(241, 2, 234, 'A oni od vas koji umru i ostave žene, čekat će (one) dušama svojim četiri mjeseca i deset (dana). Pa kad dospiju roku njihovom, tad nije na vama grijeh za ono što će učiniti sa dušama svojim pristojno; a Allah je o onom šta radite Obaviješteni.'),
(242, 2, 235, 'I nema vam prestupa u onom čime navijestite prosidbu žena ili (to) sakrijete u dušama vašim. Zna Allah da ćete ih vi spominjati, međutim ne obećavajte im potajno, izuzev da govorite govor pristojan. I ne odlučujte se na bračnu vezu dok ne dospije propisano roku svom, i znajte da Allah zna šta je u dušama vašim, zato Ga se pričuvajte; i znajte da je Allah Oprosnik, Blagi.'),
(243, 2, 236, 'Nije vam grijeh ako razvedete žene koje niste dotakli ili im odredili dio. I dajte im zadovoljštinu: imućan prema mogućnosti svojoj, a siromašan prema mogućnosti svojoj, zadovoljštinu pristojnu; obaveza je dobročinitelja.'),
(244, 2, 237, 'A ako ih razvedete prije no što ih dotaknete, a već ste im odredili dio, tad (dajete) polovinu onog šta ste odredili, izuzev ako poštede ili poštedi onaj u čijoj je ruci ženidbena veza. A da poštedite, bliže je bogobojaznosti. I ne zaboravljajte dobrotu među sobom. Uistinu! Allah je onog šta radite Vidilac.'),
(245, 2, 238, 'Čuvajte salate i salat srednji, i stojte Allahu skrušeni.'),
(246, 2, 239, 'Pa ako se budete bojali, onda pješačeći ili jašući. Pa kad budete bezbjedni, tad spominjite Allaha, kao što vas je naučio što niste znali (ranije).'),
(247, 2, 240, 'A koji umru od vas, i ostave žene, wasijjet je za žene njihove: izdržavanje do godine (dana) bez odlaska (njihovog). Pa ako odu, tad vam nije grijeh u onom šta učine sa dušama svojim od pristojnog; a Allah je Moćni, Mudri.'),
(248, 2, 241, 'I za raspuštenice (obezbijediti) izdržavanje pristojno: obaveza je na bogobojaznima.'),
(249, 2, 242, 'Tako vam objašnjava Allah ajete Svoje, da biste vi shvatili.'),
(250, 2, 243, 'Zar nisi razmislio o onima koji su izašli iz kuća svojih, a bilo ih je na hiljade, strašeći se smrti. Tad im je rekao Allah: "Umrite!", zatim ih je oživio. Uistinu! Allah je Vlasnik blagodati nad ljudima, međutim većina ljudi ne zahvaljuje.'),
(251, 2, 244, 'I borite se na putu Allahovom i znajte da je Allah Onaj koji čuje, Znalac.'),
(252, 2, 245, 'Ko je taj ko će uzajmiti Allahu zajam lijep, pa da mu ga umnoži umnožavanjem velikim; a Allah uskraćuje i pruža, i Njemu se vraćate.'),
(253, 2, 246, 'Zar nisi vidio velikane sinova Israilovih poslije Musaa, kad rekoše vjerovjesniku svom: "Pošalji nam vladara, borićemo se na putu Allahovom." Reče: "Je li moguće - ako vam se propiše borba - da se nećete boriti?" Rekoše: "Zašto se mi ne bismo borili na putu Allahovom, a već smo protjerani iz kuća naših, i sinovi naši." Pa pošto im je propisana borba, okrenuše se, izuzev malo njih; a Allah je Znalac zalima.'),
(254, 2, 247, 'I reče im vjerovjesnik njihov: "Uistinu! Allah vam je već poslao Taluta vladarem." Rekoše: "Otkud da njegova bude vlast nad nama, a mi smo dostojniji vlasti od njega i nije mu dato obilje imetka." Reče: "Uistinu, Allah ga je odabrao nad vama i uvećao ga obimno u znanju i tijelu." A Allah daje vlast Svoju kome hoće, i Allah je Sveobuhvatni, Znalac.'),
(255, 2, 248, 'I reče im vjerovjesnik njihov: "Uistinu, znak vlasti njegove je što će vam doći tabut u kojem je smirenost od Gospodara vašeg i ostatak onog šta je ostavila porodica Musaova i porodica Harunova, nosiće ga meleci. Uistinu, u tome je znak za vas, ako ste vjernici."'),
(256, 2, 249, 'Pa pošto izađe Talut sa vojskama, reče: "Uistinu! Allah će vas iskušati rijekom. Pa ko se iz nje napije, pa nije od mene, a ko je se ne okusi - pa uistinu on je od mene, izuzev ko zagrabi hvatom ruke svoje." Pa se napiše iz nje, izuzev malo njih. Pa pošto je prijeđe, on i oni koji su vjerovali s njim rekoše: "Nemamo mi sile danas protiv Džaluta i vojski njegovih." Oni koji su znali da će oni biti ti koji će sresti Allaha rekoše: "Koliko je malih grupa pobijedilo velike grupe s dozvolom Allahovom!" A Allah je uz strpljive.'),
(257, 2, 250, 'I pošto se istaknuše Džalutu i vojskama njegovim, rekoše: "Gospodaru naš! Ukaži nam strpljenje i osnaži noge naše, i pomozi nas protiv naroda nevjernika."'),
(258, 2, 251, 'Tad ih razbiše s dozvolom Allahovom. I ubi Dawud Džaluta i dade njemu Allah vlast i mudrost i nauči ga ono šta je htio. A da nije Allahovog suzbijanja ljudi jednih drugima - sigurno bi se iskvarila zemlja; međutim, Allah je Vlasnik dobrote nad svjetovima.'),
(259, 2, 252, 'To su ajeti Allahovi, učimo ih tebi s Istinom. A uistinu, ti si od poslanih.'),
(260, 2, 253, 'Ti poslanici! Odlikovali smo ih jedne nad drugima. Od njih je onaj s kojim je govorio Allah; i uzdigao je neke od njih stepenima. I dali smo Isau, sinu Merjeminu, dokaze jasne i pomogli ga Ruhul-kudusom. A da je htio Allah, ne bi se oni međusobno borili poslije njih, nakon što su im došli dokazi jasni. Međutim, razišli su se. Pa od njih je onaj ko je vjerovao i od njih ko nije vjerovao. A da je htio Allah, ne bi se borili međusobno. Međutim, Allah čini šta želi.'),
(261, 2, 254, 'O vi koji vjerujete! Udjeljujte od onog čime smo vas opskrbili, prije nego dođe Dan u kojem neće biti trgovine, niti prijateljstva, a ni posredovanja. A nevjernici - oni su zalimi.'),
(262, 2, 255, 'Allah! Samo je On Bog! Živi, Samoopstojeći! Ne obuzima Ga drijemež, niti san. Njegovo je šta je u nebesima i šta je na Zemlji. Ko je taj ko će posredovati kod Njega, izuzev s dozvolom Njegovom? Zna šta je pred njima, i šta iza njih, a ne obuhvataju ništa od znanja Njegovog, izuzev šta htjedne. Kursijja Njegova obuhvata nebesa i Zemlju, i ne umara Ga održavanje njihovo. A On je Svevišnji, Veličanstveni.'),
(263, 2, 256, 'Nema prisiljavanja u vjeru! Doista se jasno razlikuje ispravnost od zablude! Pa ko zaniječe taguta, a povjeruje u Allaha, tad se doista uhvatio za hvatište najčvršće, kojem nema kidanja. A Allah je Onaj koji čuje, Znalac.'),
(264, 2, 257, 'Allah je Zaštitnik onih koji vjeruju. Izvodi ih iz tmina na svjetlo. A koji ne vjeruju, zaštitnici njihovi su taguti. Izvode ih iz svjetlosti u tmine. Takvi će biti stanovnici vatre, oni će u njoj biti vječito.'),
(265, 2, 258, 'Pa zar nisi vidio onog koji se prepirao s Ibrahimom o Gospodaru njegovom!, jer je Allah dao njemu vlast. Kad reče Ibrahim: "Gospodar moj je Taj koji oživljava i usmrćuje." Reče: "Ja oživljavam i usmrćujem." Ibrahim reče: "Pa uistinu, Allah dovodi Sunce sa istoka, ta dovedi ga sa zapada." Tad se zbuni onaj koji nije vjerovao; a Allah ne upućuje narod zalima.'),
(266, 2, 259, 'Ili kao onaj koji je prošao pored naselja, a ono pusto (palo) sa krovovima svojim. Reče: "Kako će Allah oživiti ovo poslije smrti njegove?" Tad ga usmrti Allah stotinu godina, potom ga podiže. Reče: "Koliko si ostao (mrtav)?" Reče: "Ostao sam dan ili dio dana." Reče: "Naprotiv, ostao si sto godina. Pa pogledaj hranu svoju i piće svoje! Nije se ono pokvarilo; i pogledaj magarca svog; i da učinimo tebe znakom ljudima. I pogledaj kosti: kako ih podižemo, zatim ih zaodjevamo mesom." Pa pošto mu bi jasno, reče: "Znam da Allah nad svakom stvari ima moć."'),
(267, 2, 260, 'I kad reče Ibrahim: "Gospodaru moj! Pokaži mi kako oživljavaš mrtve." Reče: "Zar ne vjeruješ?" Reče: "Svakako, ali da se smiri srce moje." Reče: "Pa uzmi četiri ptice, te ih nakloni sebi, zatim stavi na svako brdo dio njih, potom ih pozovi: doći će ti žurno; a znaj da je Allah Moćni, Mudri."'),
(268, 2, 261, 'Primjer onih koji troše imetke svoje na putu Allahovom je kao primjer zrna (iz kog) izraste sedam klasova, u svakom klasu stotinu zrna. A Allah umnožava kome hoće i Allah je Sveobuhvatni, Znalac.'),
(269, 2, 262, 'Oni koji troše imetke svoje na putu Allahovom, zatim ne budu slijedili ono šta udjeljuju prebacivanjem niti uvredom, imaće oni nagradu svoju kod Gospodara njihovog, i neće biti straha nad njima, niti će oni tugovati.'),
(270, 2, 263, 'Lijepe riječi i oprost su bolji od sadake koju prati uvreda; a Allah je Neovisni, Blagi.'),
(271, 2, 264, 'O vi koji vjerujete! Ne kvarite sadake svoje prebacivanjem i uvredom, kao onaj koji udjeljuje imetak svoj da bi vidjeli ljudi, a ne vjeruje u Allaha i Dan posljednji. Pa primjer njegov je kao primjer stijenja - na njemu prašina, pa ga pogodi pljusak, te ga ostavi golim. Ne vladaju ničim od onog šta su stekli; a Allah ne upućuje narod nevjernika.'),
(272, 2, 265, 'A primjer onih koji troše imetke svoje tražeći zadovoljstvo Allahovo i učvršćenje duša svojih, je kao primjer bašče na brdu: pogodi je pljusak pa dadne plod svoj dvostruko. Pa ako je ne pogodi pljusak, onda (padne) rosa. A Allah je onog šta radite Vidilac.'),
(273, 2, 266, 'Da li bi volio neko od vas da on ima bašču palmi i grožđa ispod koje teku rijeke, (i) imadne on u njoj raznovrsnog voća: i pogodi ga starost, a imadne on potomke nejake, pa pogodi nju orkan - u njemu vatra, te spali? Tako vam objašnjava Allah znakove, da biste vi razmislili.'),
(274, 2, 267, 'O vi koji vjerujete! Udjeljujte od dobrih stvari koje ste zaradili i od onog šta izvodimo za vas iz zemlje. I ne namjenjujte loše od toga da dijelite, i šta ne biste uzeli, izuzev da zažmirite na to. A znajte da je Allah Neovisni, Hvaljeni.'),
(275, 2, 268, 'Šejtan vam obećava bijedu i naređuje vam razvrat, a Allah vam obećava oprost od Sebe i blagodat; a Allah je Sveobuhvatni, Znalac.'),
(276, 2, 269, 'Daje mudrost kome hoće. A kome se da mudrost, pa doista mu je dato mnogo dobro. A ne poučava se (niko), izuzev posjednici razuma.'),
(277, 2, 270, 'I šta udijelite od nafake ili zavjetujete od zavjeta, pa uistinu, Allah to zna. A nema za zalime nikakvih pomagača.'),
(278, 2, 271, 'Ako ističete sadake, pa to je dobro. A ako ih krijete, i dajete ih fukarama, pa to je bolje za vas i pokriće vam (Allah) neka loša djela vaša. A Allah je o onom šta radite Obaviješteni.'),
(279, 2, 272, 'Nije na tebi upućivanje njihovo nego Allah upućuje koga hoće. A šta udijelite od dobrog, pa za duše je vaše; a dijelite jedino tražeći lice Allahovo. I šta udijelite od dobrog, biće vam nadoknađeno i vama se neće zulm učiniti.'),
(280, 2, 273, '(Sadaka je) za fukare koji ograničeni na putu Allahovom ne mogu krstariti po zemlji. Smatra ih neznalica bogatima zbog skromnosti. Poznaćeš ih po obilježju njihovom: ne traže od ljudi nasrtljivo. A šta utrošite od dobrog, pa uistinu, Allah je o tome Znalac.'),
(281, 2, 274, 'Oni koji udjeljuju imetke svoje noću i danju, tajno i javno - pa imaće oni nagradu svoju kod Gospodara njihovog i neće biti straha nad njima i neće oni žaliti.'),
(282, 2, 275, 'Oni koji jedu kamatu, neće ustati, osim kao što ustaje onaj koga udari šejtan svojim dodirom. To zato što oni kažu: "Zaista je trgovina kao kamata" - a dozvoljava Allah trgovinu, a zabranjuje kamatu. Pa onom kome dođe pouka od Gospodara njegovog te se sustegne, tad je njegovo ono šta je prethodno bilo, a stvar njegova je do Allaha (da presudi). A ko se povrati (kamati) - pa takvi će biti stanovnici vatre, oni će u njoj biti vječno.'),
(283, 2, 276, 'Briše Allah kamatu, a povećava sadake. A Allah ne voli nijednog nevjernika, grješnika.'),
(284, 2, 277, 'Uistinu, oni koji vjeruju i čine dobra djela i obavljaju salat i daju zekat, imaće oni nagradu svoju kod Gospodara njihovog, i neće bit straha nad njima i neće oni žaliti.'),
(285, 2, 278, 'O vi koji vjerujete! Bojte se Allaha i ostavite šta je preostalo od kamate, ako ste vjernici.'),
(286, 2, 279, 'Pa ako (to) ne učinite, tad budite upozoreni ratom od Allaha i Poslanika Njegovog. A ako se pokajete, tad su vaše glavnice imetaka vaših. Ne činite zulm i neće vam biti učinjen zulm.'),
(287, 2, 280, 'A ako (dužnik) bude u nevolji, tad je odgoda do lahkoće; a da učinite (to) sadakom bolje je za vas, kad biste znali.'),
(288, 2, 281, 'I bojte se Dana u kojem ćete biti vraćeni Allahu. Zatim će se isplatiti svakoj duši šta je zaslužila i njima se neće učiniti zulm.'),
(289, 2, 282, 'O vi koji vjerujete! Kad dajete zajam do roka određenog, tad to zapišite. I neka zapiše pisar između vas pravedno. I neka ne odbije pisar da zapiše, kao što ga je poučio Allah, ta neka piše. I neka diktira onaj na kojem je dug i neka se boji Allaha Gospodara svog i ne umanji od njega ništa. Pa ako onaj na kojem je dug bude slabouman ili slab ili on ne može da diktira, tad neka diktira staratelj njegov pravedno. I pozovite da svjedoče dva svjedoka od vaših ljudi. Pa ako ne bude dva čovjeka, onda čovjek i dvije žene od onog na koga pristanete za svjedoke - da (ako) zaluta jedna od njih, nek podsjeti jedna od njih drugu. I neka ne odbiju svjedoci kad budu pozvani. I nek vam ne bude mrsko da to zapišete, malo ili veliko, do roka njegovog. To je pravednije kod Allaha i pravilnije za svjedočenje i najpodesnije da ne sumnjate; izuzev ako bude trgovine prisutne koju obrćete među vama, tad vam nije grijeh da je ne pišete. I uzmite svjedoke kad trgujete, i neka se ne ošteti pisar niti svjedok. A ako učinite (štetu), pa uistinu, to je grijeh vaš. I bojte se Allaha, a uči vas Allah. A Allah je o svakoj stvari Znalac.'),
(290, 2, 283, 'A ako budete na putovanju i ne nađete pisara, tad zalog nek bude uzet. Pa ako povjeri jedan od vas drugom, tad nek vrati onaj kojem je povjereno, emanet svoj, i neka se boji Allaha, Gospodara svog. I ne prikrivajte svjedočenje. A ko ga sakrije, pa uistinu, to je grijeh srca njegovog. A Allah je o onom što radite Znalac.'),
(291, 2, 284, 'Allahovo je šta je u nebesima i šta je na Zemlji! I pokazivali vi šta je u dušama vašim ili ga skrivali, obračunaće vas Allah za to, pa će oprostiti kome hoće, a kazniti koga hoće. A Allah nad svakom stvari ima moć.'),
(292, 2, 285, 'Vjeruje Poslanik u ono šta mu je objavljeno od Gospodara njegovog, i vjernici. Svaki vjeruje u Allaha i meleke Njegove i knjige Njegove i poslanike Njegove. "Ne pravimo razliku između ijednog od poslanika Njegovih." I govore: "Čujemo i pokoravamo se. Oprost tvoj, Gospodaru naš! A dolazište je Tebi!"'),
(293, 2, 286, 'Ne opterećuje Allah dušu, izuzev mogućnošću njenom. Imaće ona šta zasluži, a protiv nje je šta zasluži. Gospodaru naš! Ne kazni nas ako zaboravimo ili pogriješimo. Gospodaru naš! I ne tovari na nas teret kao što si ga tovario na one prije nas. Gospodaru naš! I ne tereti nas onim zašto mi nemamo moći. I briši nam (grijehe), i oprosti nam i smiluj nam se. Ti si Zaštitnik naš, zato nas pomozi protiv naroda nevjernika!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Alif. Lam. Mim.'),
(295, 3, 2, 'Allah! Samo je On Bog! Živi, Samoopstojeći!'),
(296, 3, 3, 'Objavljuje tebi Knjigu s Istinom, potvrdu za ono ispred nje, a objavio je Tewrat i Indžil,'),
(297, 3, 4, 'Ranije, Uputu ljudima; i objavio je Furkan. Uistinu, oni koji ne vjeruju u ajete Allahove, imaće oni kaznu žestoku; a Allah je Moćni, Vlasnik osvete.'),
(298, 3, 5, 'Uistinu, Allah - nema skriveno Njemu ništa na Zemlji niti u nebu -'),
(299, 3, 6, 'On je Taj koji vas oblikuje u matericama kako hoće. Nema boga osim Njega, Moćnog, Mudrog!'),
(300, 3, 7, 'On je Taj koji ti objavljuje Knjigu. Od nje su ajeti jasni - oni su majka Knjige, a drugi su nejasni. Pa što se tiče onih u srcima čijim je skretanje, ta slijediće šta je nejasno iz nje, tražeći fitneluk i tražeći joj tumačenje (svoje); a zna tumačenje njeno samo Allah. A jaki u nauci govore: "Vjerujemo u nju; sve je od Gospodara našeg" - a ne shvataju, izuzev posjednici razuma.'),
(301, 3, 8, '"Gospodaru naš! Ne skreni naša srca nakon što si nas uputio i daruj nam od Sebe milost. Uistinu Ti, Ti si Darivalac.'),
(302, 3, 9, 'Gospodaru naš! Uistinu, Ti si Taj koji će sabrati ljude za Dan u koji nema sumnje." Doista Allah neće prekršiti obećanje.'),
(303, 3, 10, 'Uistinu, oni koji ne vjeruju - neće im koristiti imeci njihovi ni djeca njihova protiv Allaha nimalo, a ti takvi će biti gorivo vatre.'),
(304, 3, 11, 'Kao stanje naroda faraonovog i onih prije njih. Poricali su znakove Naše, pa ih je kaznio Allah radi grijeha njihovih; a Allah je žestok kaznom.'),
(305, 3, 12, 'Reci onima koji ne vjeruju: "Bićete poraženi i sabrani Džehennemu; a loše je odmorište."'),
(306, 3, 13, 'Već je za vas bio znak u skupinama dvjema (koje) su se susrele. Skupina se borila na putu Allahovom, a druga je bila nevjernička - vidjeli su ih njima dvostrukim, viđenjem oka. A Allah ojačava pomoći Svojom koga hoće. Uistinu, u tome je pouka za posjednike opažanja.'),
(307, 3, 14, 'Uljepšana je ljudima ljubav: žudnje za ženama i sinovima i gomilama nagomilanim od zlata i srebra i konjem obilježenim i stoci i usjevu. To je uživanje života Dunjaa. A Allah - kod Njega je najbolje povratište!'),
(308, 3, 15, 'Reci: "Hoćete li da vas obavijestim o boljem od toga? Oni koji budu bogobojazni imaće kod Gospodara svog bašče ispod kojih teku rijeke, biće vječno u njima, i žene čiste i zadovoljstvo od Allaha." A Allah je Vidilac robova,'),
(309, 3, 16, 'Koji govore: "Gospodaru naš! Uistinu mi vjerujemo, zato nam oprosti grijehe naše i sačuvaj nas kazne vatrom",'),
(310, 3, 17, 'Strpljivih i istinitih i poslušnih i onih koji udjeljuju i koji traže oprost u rane zore.'),
(311, 3, 18, 'Allah svjedoči da On - nema boga osim Njega - i meleci Njegovi i posjednici znanja - postupa pravedno. Samo je On Bog, Moćni, Mudri.'),
(312, 3, 19, 'Uistinu! Vjera kod Allaha je Islam. A razišli se oni kojima je data Knjiga jedino nakon što im je došlo znanje, zavišću međusobnom. A ko zaniječe ajete Allahove, pa uistinu, Allah je brz obračunom.'),
(313, 3, 20, 'Pa ako se budu prepirali s tobom, tad reci: "Predao sam lice svoje Allahu, i onaj ko me slijedi." I reci onima kojima je data Knjiga i neukima: "Jeste li se predali (Allahu)?" Pa ako se predaju, tad se doista upućuju. A ako se odvrate - pa samo je na tebi dostava; a Allah je Vidilac robova.'),
(314, 3, 21, 'Uistinu, one koji ne vjeruju u ajete Allahove i ubijaju vjerovjesnike bez prava, i ubijaju one od ljudi koji naređuju pravednost - pa obraduj ih kaznom bolnom.'),
(315, 3, 22, 'To su oni čija će djela propasti na Dunjau i Ahiretu, a neće oni imati nikakvih pomagača.'),
(316, 3, 23, 'Zar nisi vidio one kojima je dat dio Knjige? Pozivaju se Knjizi Allahovoj da presudi među njima, potom se okrene skupina njih i oni su odvraćeni.'),
(317, 3, 24, 'To zato što oni govore: "Neće nas doticati vatra, izuzev nekoliko dana." A obmanulo ih je u vjeri njihovoj ono šta su izmišljali.'),
(318, 3, 25, 'Pa kako će biti kad ih skupimo za Dan u koji nema sumnje i bude isplaćeno svakoj duši šta je zaradila? - a njima neće biti učinjen zulm.'),
(319, 3, 26, 'Reci: "O Allahu, Posjedniče vlasti! Daješ vlast kome hoćeš, a oduzimaš vlast od koga hoćeš. Počašćuješ koga hoćeš, a unizuješ koga hoćeš. U ruci Tvojoj je dobro. Uistinu, Ti nad svakom stvari imaš moć.'),
(320, 3, 27, 'Daješ da noć uđe u dan i daješ da dan uđe u noć, i izvodiš živo iz mrtvog i izvodiš mrtvo iz živog, a opskrbljuješ koga hoćeš bez računa."'),
(321, 3, 28, 'Neka ne uzimaju vjernici nevjernike za prijatelje, mimo vjernika. A ko to učini tad nema od Allaha ništa, izuzev (ako bi to bilo) da se sačuvate od njih čuvanjem. I upozorava vas Allah Sobom, a Allahu je dolazište!'),
(322, 3, 29, 'Reci: "Skrivali vi šta je u grudima vašim ili to otkrivali, zna ga Allah. I zna šta je u nebesima i šta je na Zemlji", a Allah nad svakom stvari ima moć.'),
(323, 3, 30, 'Na Dan kad nađe svaka duša ono šta je od dobrog uradila prineseno, i šta je uradila od zla - zaželiće da je između nje i između njega udaljenost velika. I upozorava vas Allah Sobom; a Allah je Samilostan robovima.'),
(324, 3, 31, 'Reci: "Ako volite Allaha, tad mene slijedite, voliće vas Allah i oprostiće vam grijehe vaše," a Allah je Oprosnik, Milosrdni.'),
(325, 3, 32, 'Reci: "Pokoravajte se Allahu i Poslaniku." Pa ako se odvrate, pa uistinu Allah ne voli nevjernike.'),
(326, 3, 33, 'Uistinu! Allah je odabrao Adema i Nuha i porodicu Ibrahimovu i porodicu Imranovu, nad svjetovima.'),
(327, 3, 34, 'Potomstvo su jedni od drugih. A Allah je Onaj koji čuje, Znalac.'),
(328, 3, 35, 'Kad reče žena Imranova: "Gospodaru moj! Uistinu ja zavjetujem Tebi šta je u utrobi mojoj posvećeno, zato primi od mene. Uistinu, Ti, Ti si Onaj koji čuje, Znalac."'),
(329, 3, 36, 'Pa pošto je rodi, reče: "Gospodaru moj! Uistinu! Ja sam je rodila - žensko" - a Allah je Najbolji znalac onog šta je rodila - "a nije muško kao žensko, i uistinu, ja sam joj dala ime Merjem. I uistinu, ja tražim Tvoju zaštitu za nju i potomstvo njeno od šejtana prokletog."'),
(330, 3, 37, 'Pa je primio nju Gospodar njen primanjem lijepim i učinio da uzraste rastom lijepim; i učinio starateljem njenim Zekerijjaa. Kad god bi joj ušao Zekerijja u mihrab, našao bi kod nje opskrbu. Reče: "O Merjem! Otkud ti ovo?" Rekla je: "To je od Allaha!" Uistinu, Allah opskrbljuje koga hoće bez računa.'),
(331, 3, 38, 'Tu je prizivao Zekerijja Gospodara svog. Reče: "Gospodaru moj! Podari mi od Sebe potomstvo dobro. Uistinu! Ti si Onaj koji čuje dowu."'),
(332, 3, 39, 'Pa pozvaše njega meleci, a on je stajao obavljajući salat u mihrabu: "Allah raduje tebe Jahjaom, potvrđivačem Riječi od Allaha, i Sejjidom i neporočnim, i vjerovjesnikom od dobrih."'),
(333, 3, 40, 'Reče: "Gospodaru moj! Otkud meni dječak, a već me je dostigla starost, i žena moja je nerotkinja." Reče: "Istovjetno!" Allah čini šta hoće.'),
(334, 3, 41, 'Reče: "Gospodaru moj! Daj mi znak." Reče: "Znak ti je da nećeš govoriti s ljudima tri dana, izuzev mimikom. I spominji Gospodara svog mnogo, i slavi (Ga) noću i jutrom."'),
(335, 3, 42, 'I kad rekoše meleci: "O Merjem! Uistinu, Allah te je odabrao i očistio te, i odabrao te nad ženama svjetova.'),
(336, 3, 43, 'O Merjem! Pokori se Gospodaru svom i padaj na sedždu i čini ruku\' sa onima koji čine ruku\'."'),
(337, 3, 44, 'To je od vijesti nevidljivog, koje objavljujemo tebi. A nisi bio kod njih kad pobacaše pera svoja - koji od njih će se starati o Merjem - niti si bio kod njih kad su se svađali.'),
(338, 3, 45, 'Kad rekoše meleci: "O Merjem! Uistinu, raduje te Allah Riječju od Sebe. Ime njegovo je Mesih, Isa, sin Merjemin, istaknut na Dunjau i Ahiretu - a od bliskih je -'),
(339, 3, 46, 'I govoriće s ljudima u bešici i odrastao, i od dobrih će biti."'),
(340, 3, 47, 'Reče: "Gospodaru moj! Otkud meni dijete, a nije me dotakao čovjek?" Reče: "Istovjetno!" Allah stvara šta hoće. Kad odluči stvar, tad samo kaže tome: "Budi!", tad biva.'),
(341, 3, 48, 'I poučiće ga Knjizi i mudrosti i Tewratu i Indžilu,'),
(342, 3, 49, 'I poslanikom sinovima Israilovim: "Ja vam doista dolazim sa znakom od Gospodara vašeg da ću vam ja napraviti od gline (nešto) kao lik ptice, pa ću puhnuti u njega, te će biti ptica s dozvolom Allahovom; i ozdraviću slijepe i gubave, i oživiti mrtve s dozvolom Allahovom; i obavijestiću vas o onome šta jedete i šta gomilate u kućama vašim. Uistinu u tome je znak za vas, ako ste vjernici -'),
(343, 3, 50, 'I potvrđivač sam za ono što je ispred mene od Tewrata i da vam dozvolim dio onog šta vam je bilo zabranjeno; i došao sam vam sa znakom od Gospodara vašeg, zato se bojte Allaha i poslušajte mene.'),
(344, 3, 51, 'Uistinu! Allah je Gospodar moj i Gospodar vaš, zato Njega obožavajte. Ovo je put pravi."'),
(345, 3, 52, 'Pa pošto spozna Isa od njih nevjerovanje, reče: "Ko su pomagači moji Allahu?" Hawarijjuni rekoše: "Mi smo pomagači Allahu! Vjerujemo u Allaha i posvjedoči da smo mi muslimani.'),
(346, 3, 53, 'Gospodaru naš! Vjerujemo u ono šta si objavio i slijedimo poslanika, pa nas upiši sa svjedocima."'),
(347, 3, 54, 'I spletkarili su i planirao je Allah; a Allah je Najbolji od planera.'),
(348, 3, 55, 'Kad reče Allah: "O Isa! Ja sam Taj koji će ti smrt dati i Sebi te uzvisiti i Koji će te očistiti od onih koji ne vjeruju, i Koji će učiniti (da) oni koji te slijede budu nad onima koji ne vjeruju do Dana kijameta. Zatim je Meni povratak vaš, pa ću presuditi među vama o onom u čemu ste se razilazili.'),
(349, 3, 56, 'Pa što se tiče onih koji ne vjeruju, ta kazniću ih kaznom žestokom na Dunjau i Ahiretu; a neće oni imati nikakvih pomagača.'),
(350, 3, 57, 'A što se tiče onih koji vjeruju i rade dobra djela, pa isplatit će im nagrade njihove." A Allah ne voli zalime.'),
(351, 3, 58, 'To šta ti učimo je od ajeta i Opomena mudra.'),
(352, 3, 59, 'Uistinu, primjer Isaa kod Allaha je kao primjer Adema. Stvorio ga je od prašine, potom mu rekao: "Budi!", pa je bio.'),
(353, 3, 60, 'Istina je od Gospodara tvog, zato ne budi od sumnjajućih.'),
(354, 3, 61, 'Pa ko se bude prepirao s tobom o njemu, nakon što ti je došlo nešto znanja, tad reci: "Dođite! Pozvaćemo sinove naše i sinove vaše, i žene naše i žene vaše, i duše naše i duše vaše, zatim ćemo se skrušeno moliti pa proizvesti prokletstvo Allahovo nad lažljivcima."'),
(355, 3, 62, 'Doista! Uistinu! Ovo kazivanje je istinito; i nema nikakva boga osim Allaha. A uistinu Allah - On je Moćni, Mudri!'),
(356, 3, 63, 'Pa ako se odvrate, pa uistinu, Allah je Znalac mufsida.'),
(357, 3, 64, 'Reci: "O sljedbenici Knjige! Dođite riječi istoj među nama i među vama: Da obožavamo samo Allaha i da Mu ne pridružujemo ništa i ne uzimamo mi jedni druge gospodarima mimo Allaha." Pa ako se odvrate, tad recite: "Svjedočite da smo mi muslimani."'),
(358, 3, 65, 'O sljedbenici Knjige! Zašto raspravljate o Ibrahimu, a Tewrat i Indžil su objavljeni jedino poslije njega? Pa zar ne shvatate?'),
(359, 3, 66, 'Eto, vi ste ti koji se prepirete o onom o čemu vi imate znanje. Pa zašto se prepirete o onom o čemu vi nemate znanje? A Allah zna, a vi ne znate.'),
(360, 3, 67, 'Nije bio Ibrahim jevrej, niti kršćanin, nego je bio hanif musliman, i nije bio od mušrika.'),
(361, 3, 68, 'Uistinu, najbliži ljudi Ibrahimu su oni koji su ga slijedili i ovaj Poslanik i oni koji vjeruju; a Allah je Zaštitnik vjernika.'),
(362, 3, 69, 'Voljela bi grupa sljedbenika Knjige da vas zavedu. A ne zavode, izuzev duše svoje, a ne opažaju.'),
(363, 3, 70, 'O sljedbenici Knjige! Zašto ne vjerujete u ajete Allahove, a vi svjedočite?'),
(364, 3, 71, 'O sljedbenici Knjige! Zašto presvlačite Istinu s neistinom i prikrivate Istinu, a vi znate?'),
(365, 3, 72, 'I govori grupa sljedbenika Knjige: "Vjerujte u ono šta je objavljeno onima koji vjeruju početkom dana, a ne vjerujte krajem njegovim, da bi se oni povratili.'),
(366, 3, 73, 'I ne vjerujte, osim onom ko slijedi vjeru vašu." Reci: "Uistinu, uputa je Uputa Allahova" - da bi bilo dato nekom slično šta vam je dato ili da bi raspravljali s vama kod Gospodara vašeg." Reci: "Uistinu, blagodat je u ruci Allahovoj." Daje ju kome hoće; a Allah je Sveobuhvatni, Znalac.'),
(367, 3, 74, 'Posebno odabire milosti Svojoj koga hoće; a Allah je Posjednik blagodati velike.'),
(368, 3, 75, 'I od sljedbenika Knjige je ko, ako mu povjeriš gomilu, vratiće ti je. I od njih je ko, ako mu povjeriš dinar, neće ti ga vratiti, izuzev ako stalno budeš nad njima stajao. To zato što oni govore: "Nije nama među neukima put." I govore protiv Allaha laž, a oni znaju.'),
(369, 3, 76, 'Svakako! Ko ispuni obećanje svoje i bude se bojao, pa uistinu, Allah voli bogobojazne.'),
(370, 3, 77, 'Uistinu! Oni koji prodaju zavjet Allahu i zakletve svoje za vrijednost malu, ti takvi neće imati udio na Ahiretu i neće govoriti s njima Allah, niti će ih pogledati na Dan kijameta, niti ih očistiti, a imaće oni kaznu bolnu.'),
(371, 3, 78, 'I uistinu je između njih skupina: izokreću Knjigu jezicima svojim da pomislite to je iz Knjige, a to nije iz Knjige, i govore: "To je od Allaha", a to nije od Allaha; i govore protiv Allaha laž, a oni znaju.'),
(372, 3, 79, 'Nije za smrtnika da mu da Allah Knjigu i sud i vjerovjesništvo, zatim da rekne ljudima: "Budite robovi moji mimo Allaha," nego: "Budite rabbani zato što učite Knjigu i što proučavate."'),
(373, 3, 80, 'I neće vam narediti da uzimate meleke i vjerovjesnike gospodarima. Zar da vam naredi nevjerstvo nakon što ste vi bili muslimani?'),
(374, 3, 81, 'I kad uze Allah zavjet vjerovjesnika: "Doista, šta vam dam od Knjige i mudrosti, zatim vam dođe poslanik potvrđivač za ono šta je uz vas, sigurno ćete vjerovati u njega i sigurno ćete ga pomoći?" Reče: "Da li se slažete i uzimate za ovo zavjet Moj?" Rekoše: "Slažemo se." Reče: "Onda posvjedočite, i Ja ću sa vama biti od svjedoka."'),
(375, 3, 82, 'Pa ko se odvrati poslije toga - pa ti takvi su grješnici.'),
(376, 3, 83, 'Pa zar mimo dina Allahovog traže drugi - a Njemu se pokorava ko je na nebesima i Zemlji, milom i silom, a Njemu će se vratiti.'),
(377, 3, 84, 'Reci: "Vjerujemo u Allaha i ono šta je objavljeno nama i šta je objavljeno Ibrahimu i Ismailu i Ishaku i Jakubu i plemenima, i šta je dato Musau i Isau i vjerovjesnicima od Gospodara njihovog. Ne pravimo razliku između ijednog od njih i mi smo Njemu predani."'),
(378, 3, 85, 'A ko traži drugačiju vjeru od Islama, pa neće se primiti od njega, a on će na Ahiretu biti od gubitnika.'),
(379, 3, 86, 'Kako da uputi Allah ljude koji ne vjeruju nakon vjerovanja njihovog, a svjedočili su da je Poslanik Istina i došli su im dokazi jasni? A Allah ne upućuje narod zalima.'),
(380, 3, 87, 'Tim takvima je plaća što je na njima prokletstvo Allahovo i meleka i ljudi svih.'),
(381, 3, 88, 'Biće vječno u njemu. Neće im se olakšati kazna, niti će im se odgoditi;'),
(382, 3, 89, 'Izuzev koji se pokaju nakon toga i poprave. Pa uistinu! Allah je Oprosnik, Milosrdni.'),
(383, 3, 90, 'Uistinu, oni koji ne vjeruju nakon vjerovanja njihovog, zatim povećaju nevjerovanje, neće se primiti pokajanje njihovo; a ti takvi su zalutali.'),
(384, 3, 91, 'Uistinu, oni koji ne vjeruju i umru, a oni budu nevjernici, pa ne bi se primila ni od jednog od njih puna zemlja zlata, makar se time iskupljivao. Ti takvi će imati kaznu bolnu, a neće oni imati nikakvih pomagača.'),
(385, 3, 92, 'Nećete postići dobročinstvo dok ne udijelite od onog šta volite. A šta utrošite od nečeg - pa uistinu, Allah je o tome Znalac.'),
(386, 3, 93, 'Svaka hrana je bila dozvoljena sinovima Israilovim, izuzev šta je zabranio Israil duši svojoj, prije no što je objavljen Tewrat. Reci: "Pa dajte Tewrat te ga čitajte, ako istinu govorite."'),
(387, 3, 94, 'Pa ko izmisli protiv Allaha laž poslije toga - pa ti takvi su zalimi.'),
(388, 3, 95, 'Reci: "Istinu govori Allah! Zato slijedite millet Ibrahima hanife; a nije bio od mušrika."'),
(389, 3, 96, 'Uistinu, prva Kuća postavljena za ljude je ona u Bekki, blagoslovljena je i putokaz svjetovima;'),
(390, 3, 97, 'U njoj su znakovi jasni: stajalište Ibrahimovo; i ko god uđe u nju biće siguran. A radi Allaha je na ljudima hadždž Kuće, ko smogne njoj put. A ko ne vjeruje, pa uistinu, Allah je Neovisan od svjetova.'),
(391, 3, 98, 'Reci: "O sljedbenici Knjige! Zašto ne vjerujete u ajete Allahove, a Allah je svjedok nad onim šta radite?"'),
(392, 3, 99, 'Reci: "O sljedbenici Knjige! Zašto zavodite s puta Allahovog onog ko vjeruje, i tražite ga iskrivljenim, a vi ste svjedoci?" A nije Allah nemaran prema onom šta radite.'),
(393, 3, 100, 'O vi koji vjerujete! Ako budete slušali grupu onih kojima je data Knjiga, vratiće vas nakon vjerovanja vašeg (u) nevjernike.'),
(394, 3, 101, 'A kako da ne vjerujete, a vi ste ti kojima se uče ajeti Allahovi i među vama je Poslanik Njegov? A ko se drži Allaha, pa doista je upućen putu pravom.'),
(395, 3, 102, 'O vi koji vjerujete! Bojte se Allaha Njegovim istinskim bojanjem, i umrite vi jedino kao muslimani;'),
(396, 3, 103, 'I držite se užeta Allahovog svi, i ne razdvajajte se. I sjetite se blagodati Allahove prema vama: kad ste neprijatelji bili, pa je ujedinio srca vaša, te ste postali blagodati Njegovom, braća; i bili na rubu ponora vatre, pa vas je spasio od nje. Tako vam objašnjava Allah znakove Svoje, da biste se vi uputili.'),
(397, 3, 104, 'I neka bude od vas umma (koja) će pozivati dobru i naređivati pravo, a zabranjivati što je krivo; a ti takvi su oni koji će uspjeti.'),
(398, 3, 105, 'I ne budite kao oni koji su se razdijelili i razišli, nakon što su im došli dokazi jasni. A ti takvi će imati kaznu užasnu,'),
(399, 3, 106, 'Na Dan kad pobijele lica i pocrne lica. Pa što se tiče onih čija će lica pocrniti: "Zar niste vjerovali nakon vjerovanja vašeg? Pa iskusite kaznu zato što niste vjerovali."'),
(400, 3, 107, 'A što se tiče onih čija će lica pobijeliti - pa biće u milosti Allahovoj, oni će u njoj biti vječito.'),
(401, 3, 108, 'To su ajeti Allahovi, učimo ih tebi s Istinom. A ne želi Allah zulm svjetovima.'),
(402, 3, 109, 'I Allahovo je šta je u nebesima i šta je na Zemlji; a Allahu se vraćaju stvari.'),
(403, 3, 110, 'Najbolja ste umma (koja) se svijetu pojavila. Naređujete dobro a spriječavate nevaljalo, i vjerujete u Allaha. A kad bi vjerovali sljedbenici Knjige, sigurno bi za njih bilo bolje. (Neki) od njih su vjernici, a većina njih su grješnici.'),
(404, 3, 111, 'Neće vam nauditi, izuzev vrijeđanja. A ako se budu borili protiv vas, okrenuće vam leđa, zatim neće biti pomognuti.'),
(405, 3, 112, 'Udariće na njih poniženje gdje god stignu, izuzev (gdje prihvate) uže Allahovo i uže od ljudi - i navući će srdžbu od Allaha i pogodiće ih bijeda. To zato što oni ne vjeruju u ajete Allahove, a ubijali su vjerovjesnike bez prava. To zato što ne slušaju i prevršuju.'),
(406, 3, 113, 'Nisu isti. Od sljedbenika Knjige (ima) zajednica ispravna, uče ajete Allahove svu noć i oni padaju na sedždu,'),
(407, 3, 114, 'Vjeruju u Allaha i Dan posljednji, i naređuju dobro a zabranjuju nevaljalo, i natječu se u dobrim djelima; a takvi su od dobrih.'),
(408, 3, 115, 'A šta učine od dobrog, pa neće im se to poreći; a Allah je Znalac bogobojaznih.'),
(409, 3, 116, 'Uistinu, onima koji ne vjeruju - neće im koristiti imeci njihovi niti djeca njihova protiv Allaha nimalo; a takvi će biti stanovnici vatre, oni će u njoj biti vječno.'),
(410, 3, 117, 'Primjer onog što utroše u ovom životu Dunjaa je kao primjer vjetra u kojem je led: pogodi usjev ljudi koji čine zulm dušama svojim, pa ga uništi; a ne čini im Allah zulm, nego zulm čine dušama svojim.'),
(411, 3, 118, 'O vi koji vjerujete! Ne uzimajte savjetnikom (nikog) mimo vas. Ne napuštaju vas ometanjem, vole ono šta će vam štetiti. Doista pokazuju mržnju iz usta svojih, a ono šta kriju grudi njihove je veće. Doista vam objašnjavamo znakove, ako ćete shvatiti.'),
(412, 3, 119, 'Eto, vi ste ti koji ih volite, a ne vole vas, i vjerujete u Knjigu kompletnu. I kad vas sretnu, kažu: "Vjerujemo!", a kad se osame grizu jagodice od srdžbe zbog vas. Reci: "Umrite u srdžbi svojoj!" Uistinu, Allah je Znalac grudi.'),
(413, 3, 120, 'Ako vas dotakne dobro, žao im je. A ako vas dotakne zlo, raduju mu se. A ako se strpite i budete bogobojazni, neće vam štetiti spletka njihova nimalo. Uistinu! Allah ono što rade obuhvata.'),
(414, 3, 121, 'I kad si poranio od porodice svoje da odrediš vjernicima položaje za borbu - a Allah je Onaj koji čuje, Znalac -'),
(415, 3, 122, 'Kad htjedoše dvije skupine vas da klonu, a Allah je zaštitnik njih dvije, i u Allaha zato nek se pouzdaju vjernici.'),
(416, 3, 123, 'I već vas je pomogao Allah na Bedru, a vi ste bili slabi. Zato se bojte Allaha, da biste vi zahvaljivali.'),
(417, 3, 124, 'Kad si rekao vjernicima: "Zar vam neće biti dosta da vas pomogne Gospodar vaš sa tri hiljade meleka spuštenih?"'),
(418, 3, 125, 'Svakako! Ako se strpite i budete bogobojazni i dođu vam u žurbi svojoj, - ovo će vas pomoći Gospodar vaš sa pet hiljada meleka obilježenih.'),
(419, 3, 126, 'I učinio je to Allah samo radosnom viješću za vas i da se smire time srca vaša. A nema pomoći, izuzev od Allaha, Moćnog, Mudrog,'),
(420, 3, 127, 'Da bi odsjekao dio onih koji ne vjeruju ili ih suzbio, pa da se okrenu razočarani.'),
(421, 3, 128, 'Nije stvar do tebe nimalo: ili će primiti pokajanje njihovo ili će ih kazniti, jer uistinu, oni su zalimi.'),
(422, 3, 129, 'A Allahovo je šta je u nebesima i šta je na Zemlji. Oprašta kome hoće i kažnjava koga hoće; a Allah je Oprosnik, Milosrdni.'),
(423, 3, 130, 'O vi koji vjerujete! Ne jedite kamatu dvostruku, mnogostruku, i bojte se Allaha da biste vi uspjeli,'),
(424, 3, 131, 'I bojte se vatre koja je pripremljena za nevjernike,'),
(425, 3, 132, 'I poslušajte Allaha i Poslanika, da bi vam se smilovalo.'),
(426, 3, 133, 'I požurite ka oprostu Gospodara vašeg i Džennetu čije je prostranstvo (kao) nebesa i Zemlja: pripremljen je za bogobojazne,'),
(427, 3, 134, 'Koji udjeljuju u blagostanju i oskudici, i one koji savlađuju srdžbu i one koji praštaju ljudima - a Allah voli dobročinitelje -'),
(428, 3, 135, 'I one koji kad počine nepristojnost ili zulm dušama svojim, sjete se Allaha i zatraže oprost za grijehe svoje - a ko oprašta grijehe do Allah? - i (koji) neće ustrajati na onom šta čine, a oni znaju.'),
(429, 3, 136, 'Takvima je plaća njihova oprost od Gospodara njihovog i bašče ispod kojih teku rijeke, vječno će biti u njima. A divna je nagrada radnika!'),
(430, 3, 137, 'Već su prošli prije vas suneni; zato putujte po Zemlji pa vidite kakav je bio kraj poricatelja.'),
(431, 3, 138, 'Ovo je izlaganje ljudima i Uputa i pouka za bogobojazne.'),
(432, 3, 139, 'I ne malaksajte i ne žalostite se, a vi ćete biti gornji, ako ste vjernici.'),
(433, 3, 140, 'Ako vas dotakne rana, pa već je dotakla narod (nevjernika) rana slična toj. A to su dani koje izmjenjujemo među ljudima, i da zna Allah one koji vjeruju i uzme od vas šehide - a Allah ne voli zalime -'),
(434, 3, 141, 'I da očisti Allah one koji vjeruju i zbriše nevjernike.'),
(435, 3, 142, 'Zar mislite da ćete ući u Džennet, a da ne zna Allah one koji su se borili između vas i ne zna strpljive?'),
(436, 3, 143, 'A doista ste željeli smrt prije no što ste je sreli. Pa doista ste je vidjeli, a vi gledate.'),
(437, 3, 144, 'I Muhammed je samo poslanik; već su prošli prije njega poslanici. Pa zar ćete se, ako umre ili bude ubijen, okrenuti na petama vašim? A ko se okrene na petama svojim, pa neće štetiti Allahu nimalo; a nagradiće Allah zahvalne.'),
(438, 3, 145, 'I nije za dušu da umre, izuzev s dozvolom Allahovom, u roku (u knjizi) zapisanom. A ko želi nagradu Dunjaa, daćemo mu od nje, i ko želi nagradu Ahireta, daćemo mu od nje. I nagradićemo zahvalne.'),
(439, 3, 146, 'A koliko vjerovjesnika se borilo (i) s njima skupine mnoge, pa nisu malaksali zbog onog šta ih je zadesilo na putu Allahovom, i nisu oslabili niti se ponizili; a Allah voli strpljive.'),
(440, 3, 147, 'I bio je govor njihov jedino što bi rekli: "Gospodaru naš! Oprosti nam grijehe naše i neumjerenost našu u stvari našoj, i ojačaj noge naše i pomozi nas protiv naroda nevjernika."'),
(441, 3, 148, 'Pa je dao njima Allah nagradu Dunjaa i lijepu nagradu Ahireta. A Allah voli dobročinitelje.'),
(442, 3, 149, 'O vi koji vjerujete! Ako poslušate one koji ne vjeruju, povratiće vas na petama vašim, pa ćete se pretvoriti u gubitnike.'),
(443, 3, 150, 'Naprotiv, Allah je Zaštitnik vaš, a On je Najbolji od pomagača.'),
(444, 3, 151, 'Ubacićemo u srca onih koji ne vjeruju, strah, zato što pridružuju Allahu ono za što nije spustio autoritet. A sklonište njihovo biće vatra; a loše je boravište zalima.'),
(445, 3, 152, 'I doista vam je obistinio Allah obećanje Svoje kad ste ih uništavali s dozvolom Njegovom, do (momenta) kad ste klonuli i prepirali se o komandi i niste poslušali, nakon što vam je pokazao ono šta volite. Od vas je onaj ko želi Dunja i od vas onaj ko želi Ahiret. Zatim vas je odbio od njih, da bi vas iskušao. I već vam je oprostio; a Allah je Vlasnik blagodati nad vjernicima.'),
(446, 3, 153, 'Kad ste se pentrali i niste obazirali ni na koga, a Poslanik vas zvao u pozadini vašoj - zato vam je uzvratio brigom na brigu, da ne žalite za onim šta vam je izmaklo, niti šta vas je zadesilo. A Allah je o onom šta radite Obaviješteni.'),
(447, 3, 154, 'Zatim je spustio na vas nakon brige, sigurnost. San je obuzeo grupu vas, a grupa - doista su ih zabrinule duše njihove, mislili su o Allahu bespravno, misao džahilijjeta. Govorili su: "Imamo li mi od (ove) stvari išta?" Reci: "Uistinu, svaka stvar je Allahova." Skrivaju u dušama svojim ono šta tebi ne otkrivaju. Govorili su: "Da je bilo za nas od (ove) stvari nešto, ne bismo ovdje bili ubijani." Reci: "Da ste bili u kućama svojim, sigurno bi izašli oni kojima je propisana pogibija, na gubilišta svoja, i da iskuša Allah ono šta je u grudima vašim, i da očisti šta je u srcima vašim." A Allah je Znalac grudi.'),
(448, 3, 155, 'Uistinu, oni od vas koji su se okrenuli na dan (kad) su se susrele dvije skupine, samo ih je šejtan naveo da posrnu sa dijelom onog šta su zaradili. I već im je oprostio Allah. Uistinu! Allah je Oprosnik, Blagi.'),
(449, 3, 156, 'O vi koji vjerujete! Ne budite kao oni koji ne vjeruju i govore braći svojoj kad putuju po Zemlji ili budu ratnici: "Da su bili kod nas, ne bi umrli, niti bi bili ubijeni" - da bi to Allah učinio žalošću u srcima njihovim. A Allah oživljava i usmrćuje i Allah je onog šta radite Vidilac.'),
(450, 3, 157, 'I ako budete ubijeni na putu Allahovom ili umrli, sigurno su oprost od Allaha i milost bolji od onog šta zgrću.'),
(451, 3, 158, 'I ako umrete ili budete ubijeni, sigurno ćete Allahu biti sabrani.'),
(452, 3, 159, 'Pa - zbog milosti Allahove blag si prema njima. A da si bio grub, osorna srca, sigurno bi se rasuli oko tebe. Zato im oprosti i moli oprost za njih i savjetuj se s njima u poslu. Pa kad odlučiš, tad se pouzdaj u Allaha. Uistinu! Allah voli one koji se pouzdavaju.'),
(453, 3, 160, 'Ako vas pomogne Allah, tad nad vama nema pobjednika. A ako vas ostavi, pa ko je taj ko će vas pomoći poslije Njega? A u Allaha zato neka se pouzdaju vjernici.'),
(454, 3, 161, 'I nije za vjerovjesnika da sakrije. A ko sakrije, donijet će ono što je krio na Dan kijameta; zatim će se isplatiti svakoj duši šta je zaradila i njima se neće učiniti zulm.'),
(455, 3, 162, 'Pa da li je onaj ko slijedi zadovoljstvo Allahovo, kao onaj ko je zaradio srdžbu od Allaha i sklonište čije je Džehennem? A loše je dolazište!'),
(456, 3, 163, 'Oni (imaju) stepene kod Allaha, a Allah je Vidilac onog šta rade.'),
(457, 3, 164, 'Doista je Allah obdario vjernike kad im je poslao Poslanika između duša njihovih: uči im ajete Njegove i čisti ih, i uči ih Knjizi i mudrosti, mada su ranije sigurno bili u zabludi očitoj.'),
(458, 3, 165, 'Zar, pošto vas je pogodila nesreća - već ste pogodili (neprijatelje) duplom sličnom njoj - govorite: "Otkud ovo?" Reci: "To je od duša vaših." Uistinu! Allah nad svakom stvari ima moć.'),
(459, 3, 166, 'A ono šta vas je pogodilo na dan susreta dviju skupina, pa s dozvolom je Allahovom i da bi znao vjernike,'),
(460, 3, 167, 'I da zna one koji su dvolični, a rečeno im je: "Dođite, borite se na putu Allahovom ili se branite." Rekli su: "Da znamo ratovanje, sigurno bismo vas slijedili." Oni su nevjerstvu tog dana bili bliži od njih, nego vjerovanju. Govore ustima svojim ono što nije u srcima njihovim; a Allah je Najbolji znalac onog šta prikrivaju,'),
(461, 3, 168, 'Oni (su ti) koji su rekli za braću svoju, a sjedili su: "Da su nas poslušali, ne bi bili ubijeni." Reci: "Pa odbijte od duša svojih smrt, ako istinu govorite!"'),
(462, 3, 169, 'I nikako ne smatrajte one koji su ubijeni na putu Allahovom, mrtvima. Naprotiv, živi su! Kod Gospodara svog su opskrbljeni;'),
(463, 3, 170, 'Radosni zbog onog šta im je dao Allah iz dobrote Svoje, i vesele se onima koji im se još nisu priključili iza njih - što neće biti straha nad njima, niti će oni tugovati.'),
(464, 3, 171, 'Raduju se zbog blagodati od Allaha i dobrote, i što Allah neće dati da propadne nagrada vjernika,'),
(465, 3, 172, 'Oni koji su se odazvali Allahu i Poslaniku, nakon što ih je pogodila rana. Oni od njih koji su činili dobro i bili bogobojazni, imaće nagradu veličanstvenu,'),
(466, 3, 173, 'Oni kojima su rekli ljudi: "Uistinu, ljudi su se već sabrali radi vas, zato ih se bojte", pa im je (to) povećalo vjerovanje i rekli su: "Dosta nam je Allah, a divan li je Zaštitnik!"'),
(467, 3, 174, 'Tad su se vratili s blagodati od Allaha i dobrotom, nije ih dotaklo zlo i slijedili su zadovoljstvo Allahovo; a Allah je Vlasnik dobrote velike.'),
(468, 3, 175, 'Samo to šejtan plaši prijateljima svojim; zato se ne bojte njih, a bojte se Mene, ako ste vjernici.'),
(469, 3, 176, 'I nek te ne žaloste oni koji srljaju u nevjerovanje. Uistinu, oni ne štete Allahu nimalo. Allah želi da im ne učini udio u Ahiretu, a imaće oni kaznu strahovitu.'),
(470, 3, 177, 'Uistinu, oni koji kupuju nevjerovanje vjerovanjem, neće štetiti Allahu nimalo, a imaće oni kaznu bolnu.'),
(471, 3, 178, 'I neka nikako ne smatraju oni koji ne vjeruju, (to) što smo im pustili, dobrim za duše njihove. Samo smo im pustili da uvećaju grijeh, a imaće oni kaznu prezrenu.'),
(472, 3, 179, 'Allah neće ostaviti vjernike na onom na čemu ste vi, dok ne rastavi lošeg od dobrog. I neće vam Allah otkriti nevidljivo. Međutim, Allah odabire od poslanika Svojih koga hoće. Zato vjerujte u Allaha i poslanike Njegove. A ako budete vjerovali i bojali se, pa imaćete vi nagradu veličanstvenu.'),
(473, 3, 180, 'I neka nikako ne smatraju oni koji škrtare u onom šta im je dao Allah iz blagodati Svoje, to dobrim za njih. Naprotiv, to je zlo za njih. Visiće im oko vratova ono s čim su škrtarili na Dan kijameta. A Allahovo su naslijeđe nebesa i Zemlja i Allah je o onom šta radite Obaviješteni.'),
(474, 3, 181, 'Doista je čuo Allah riječi onih koji su rekli: "Uistinu, Allah je fukara, a mi smo bogati." Zapisaćemo šta su rekli, i ubijanje njihovo vjerovjesnika bez prava, i reći ćemo: "Kušajte kaznu gorenjem!"'),
(475, 3, 182, 'To zbog onog šta su unaprijed poslale ruke vaše i što Allah nije nikako zalim robovima.'),
(476, 3, 183, '(Isti su) oni koji govore: "Uistinu! Allah nas je obavezao da ne vjerujemo Poslaniku dok nam ne donese kurban kojeg će progutati vatra." Reci: "Već su vam dolazili poslanici prije mene s jasnim znacima i onim šta rekoste, pa zašto ste ih ubijali, ako istinu govorite?"'),
(477, 3, 184, 'Pa ako te poreknu, pa već su bili poricani poslanici prije tebe - dolazili su sa znakovima jasnim i zeburima i Knjigom rasvjetljavajućom.'),
(478, 3, 185, 'Svaka duša smrt će okusiti, i jedino će vam biti isplaćene nagrade vaše na Dan kijameta. Pa ko bude uklonjen od vatre i bude mu dato da uđe u Džennet, pa doista je uspio; a život Dunjaa je samo užitak obmane.'),
(479, 3, 186, 'Sigurno ćete biti iskušavani u imecima vašim i dušama vašim, i sigurno ćete čuti od onih kojima je data Knjiga prije vas i od onih koji pridružuju, uvredu mnogu. A ako se strpite i budete bogobojazni, pa uistinu, to je od stvari odlučnosti.'),
(480, 3, 187, 'I kad je uzeo Allah obavezu onih kojima je data Knjiga: "Sigurno ćete je objašnjavati ljudima, i nećete je skrivati?" - pa su je odbacili za leđa svoja i pribavili njome vrijednost malu; ta loše je ono šta kupuju!'),
(481, 3, 188, 'Ne smatraj nikako one koji se vesele zbog onog s čim su došli i vole da budu hvaljeni zbog onog šta ne rade - pa nikako ne smatraj da su oni u sigurnosti od kazne; a imaće oni kaznu bolnu.'),
(482, 3, 189, 'A Allahova je vlast nebesa i Zemlje i Allah nad svakom stvari ima moć.'),
(483, 3, 190, 'Uistinu! U stvaranju nebesa i Zemlje i izmjeni noći i dana su znaci za posjednike razuma,'),
(484, 3, 191, 'One koji spominju Allaha stojeći i sjedeći i na bokovima svojim, i razmišljaju o stvaranju nebesa i Zemlje: "Gospodaru naš! Nisi stvorio ovo uzaludno. Slava neka je Tebi! Pa sačuvaj nas kazne vatrom.'),
(485, 3, 192, 'Gospodaru naš! Uistinu, koga Ti učiniš da uđe u vatru, pa doista si ga ponizio; a neće imati zalimi nikakvih pomagača.'),
(486, 3, 193, 'Gospodaru naš! Uistinu, mi smo čuli pozivača, poziva vjerovanju: "Vjerujte u Gospodara vašeg", pa smo vjerovali. Gospodaru naš, zato nam oprosti grijehe naše i pokrij nam loša djela naša i daj da mi umremo sa pravednima.'),
(487, 3, 194, 'Gospodaru naš! I daj nam šta si nam obećao po poslanicima Tvojim i ne ponizi nas na Dan kijameta. Uistinu! Ti ne kršiš obećanje!"'),
(488, 3, 195, 'Pa se odazvao njima Gospodar njihov: "Ja neću uništiti rad radnika od vas, muškarca ili žene. Vi ste jedni od drugih." Pa oni koji su izbjegli, i istjerani iz kuća njihovih i mučeni na putu Mom, i borili se i poginuli, sigurno ću im pokriti loša djela njihova i sigurno ih uvesti u bašče ispod kojih teku rijeke: nagrada od Allaha. A Allah - kod Njega je najbolja nagrada.'),
(489, 3, 196, 'Neka te nikako ne zavarava kretanje po zemljama onih koji ne vjeruju,'),
(490, 3, 197, 'Uživanje je malo, zatim će sklonište njihovo biti Džehennem, a loše je odmorište!'),
(491, 3, 198, 'Međutim, koji se boje Gospodara svog, imaće oni bašče ispod kojih teku rijeke, vječno će biti u njima - ugošćenje od Allaha. A ono što je kod Allaha, najbolje je za čestite.'),
(492, 3, 199, 'A uistinu, između sljedbenika Knjige je ko vjeruje u Allaha i ono šta je objavljeno vama i šta je objavljeno njima, ponizni su Allahu. Ne prodaju ajete Allahove za cijenu malu. Ti takvi će imati nagradu njihovu kod Gospodara njihovog. Uistinu! Allah je brz obračunom.'),
(493, 3, 200, 'O vi koji vjerujete! Istrajte i budite strpljiviji, i rasporedite se i bojte se Allaha, da biste vi uspjeli.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'O ljudi! Bojte se Gospodara vašeg koji vas je stvorio od duše jedne i stvorio od nje paricu njenu, i razasuo od njih dvije mnoge ljude i žene. I bojte se Allaha - Onog kojim potražujete (međusobna prava) i (pazite) srodstva. Uistinu! Allah je nad vama Nadzornik.'),
(495, 4, 2, 'I dajte siročadi imanja njihova. I ne zamjenjujte lošim dobro i ne jedite imetke njihove uz imetke svoje. Uistinu, to je grijeh veliki.'),
(496, 4, 3, 'A ako se bojite da nećete biti pravedni prema siročadi, tad ženite šta je dobro za vas od žena: dvije, i tri i četiri. Pa ako se plašite da nećete biti pravedni, onda jednom ili onim šta posjeduju desnice vaše. To je najpodesnije, da ne biste zastranili.'),
(497, 4, 4, 'I dajte ženama darove njihove (kao) poklone; pa ako vam od sebe ustupe nešto od toga, tad to jedite ugodno i prijatno.'),
(498, 4, 5, 'I ne dajite maloumnima imanja vaša koja vam je Allah dao na upravljanje; a opskrbljujte ih iz toga i odijevajte ih i govorite im govor pristojan.'),
(499, 4, 6, 'I provjeravajte siročad dok ne stignu do braka; pa ako kod njih zapazite zrelost, tad im predajte imetke njihove; i ne jedite ih rasipanjem i brzo, da bi (oni) odrasli. A ko je bogat, tad nek se suzdrži; a ko je fukara, tad nek jede razumno. Pa kad im predate imetke njihove, tad im svjedoke pozovite; a dovoljan je Allah Obračunac.'),
(500, 4, 7, 'Ljudima pripada dio od onoga šta ostave roditelji i bližnji, i ženama pripada dio od onog šta ostave roditelji i bližnji, od onog što je malo ili mnogo, dio obavezni.'),
(501, 4, 8, 'A kad diobi prisustvuju rodbina i siročad i siromasi, tad ih opskrbite iz toga i govorite im riječi uljudne.'),
(502, 4, 9, 'I neka se plaše takvi, da ako iza sebe ostave potomke svoje nejake - (neka) se plaše za njih. Zato neka se boje Allaha i neka govore govor prav.'),
(503, 4, 10, 'Uistinu! Oni koji jedu imetke siročadi nasilno, samo jedu u trbuhe svoje vatru, a pržiće se seirom.'),
(504, 4, 11, 'Nalaže vam Allah za djecu vašu: muškom pripada jednako dijelu dviju ženskih. Pa ako bude ženskih iznad dvije, tad su njihove dvije trećine onog šta je ostavio; a ako bude jedinica, tad je njena polovina. A roditeljima njegovim - svakom pojedinačno od njih dvoje šestina od onog šta ostavi, ako on ima dijete; pa ako on ne bude imao dijete, a nasljednik njegov roditelji njegovi, tad majka njegovu ima trećinu; pa ako on ima braću, tad majci njegovoj pripada šestina, nakon oporuke koju je oporučio ili duga. Očevi vaši i sinovi vaši: ne znate ko vam je od njih bliži korišću. Obaveza je od Allaha. Uistinu! Allah je Znalac, Mudri.'),
(505, 4, 12, 'A vama pripada pola onog šta ostave žene vaše, ako one ne budu imale dijete. Pa ako one imaju dijete, tad je vaša četvrtina onog šta su ostavile, nakon oporuke koju su oporučile ili duga. I njima pripada četvrtina onog šta ostavite, ako vi ne budete imali dijete. Pa ako vi imate dijete, tad je njihova osmina onog šta ste ostavili, nakon oporuke koju ste oporučili ili duga. A ako čovjek koji biva naslijeđen - ili žena, nema direktnih nasljednika, i imadne on brata ili sestru, tad svako od njih dvoje pojedinačno ima šestinu. Pa ako (ih) bude više od toga, tad su oni suvlasnici u trećini, nakon oporuke koja je oporučena ili duga, bez štete. Vasijjet je od Allaha; a Allah je Znalac, Blagi.'),
(506, 4, 13, 'To su granice Allahove. A ko posluša Allaha i Poslanika Njegovog, uvešće ga u bašče ispod kojih teku rijeke, vječno će biti u njima; a to je uspjeh veličanstven.'),
(507, 4, 14, 'A ko ne posluša Allaha i Poslanika Njegova i pređe granice Njegove, uvešće ga u vatru, vječno će biti u njoj i imaće on kaznu prezrenu.'),
(508, 4, 15, 'A koja od žena vaših počini blud, tad zatražite da protiv njih posvjedoče četvorica između vas. Pa ako posvjedoče, tad ih zadržite u kućama dok ih ne uzme smrt ili im Allah načini put.'),
(509, 4, 16, 'A dvoje između vas koje ga počini - pa kaznite njih dvoje! Pa ako se pokaju i poprave, tad ih se okanite. Uistinu! Allah je Primalac pokajanja, Milosrdni.'),
(510, 4, 17, 'Samo je pokajanje (zavisno) od Allaha za one koji urade zlo iz neznanja, zatim se ubrzo pokaju. Pa tim takvima će oprostiti Allah; a Allah je Znalac, Mudri.'),
(511, 4, 18, 'A nije pokajanje za one koji rade loša djela - dok, kad se približi jednom od njih smrt, rekne: "Uistinu! Ja se sad kajem", niti za one koji umru, a oni budu nevjernici. Tim takvima smo pripremili kaznu bolnu.'),
(512, 4, 19, 'O vi koji vjerujete! Ne dopušta vam se da nasljeđujete žene prisilno. I ne dodijavajte im da biste ugrabili dio onog šta ste im dali, izuzev ako počine blud otvoren. I družite se s njima lijepo, pa ako ih mrzite - ta moguće je da nešto mrzite, a učinio je Allah u tome veliko dobro.'),
(513, 4, 20, 'I ako želite zamjenu žene mjesto žene, a dali ste jednoj od njih sumu - tad ne uzimajte ništa od toga. Zar biste to uzeli potvorom i grijehom očitim?'),
(514, 4, 21, 'A kako biste to uzeli, a već ste ušli jedno drugom i uzele su od vas obavezu čvrstu?'),
(515, 4, 22, 'I ne ženite šta su ženili vaši očevi od žena, izuzimajući šta je već prošlo. Uistinu, to je bio blud i odvratnost i zao put.'),
(516, 4, 23, 'Zabranjeno vam je: majke vaše i kćeri vaše i sestre vaše, i tetke vaše (po ocu) i tetke vaše (po majci), i kćeri brata i kćeri sestre, i majke vaše koje su vas zadajale i sestre vaše po dojenju, i majke žena vaših i pastorke vaše koje su pod zaštitom vašom, od žena vaših, onih kojima ste ulazili - pa ako niste ulazili u njih, tad vam nije grijeh - i supruge sinova vaših koji su iz kičmi vaših; i da sastavite dvije sestre, izuzimajući šta je već prošlo. Uistinu! Allah je Oprosnik, Milosrdni;'),
(517, 4, 24, 'I udate od žena, izuzev šta posjeduju desnice vaše. Propis je Allahov vama; a dozvoljeno vam je šta je mimo toga, da tražite imecima svojim (kao) kreposni, ne (kao) bludnici. Pa što imate time uživanje od njih, zato im dajite darove njihove obavezno. I nije vam grijeh u onom u čemu se saglasite nakon obaveznog. Uistinu! Allah je Znalac, Mudri.'),
(518, 4, 25, 'A ko od vas ne može (smoći) sredstava da oženi slobodne vjernice, onda od onog šta posjeduju desnice vaše, od djevojaka vaših vjernica. A Allah je Najbolji znalac vjerovanja vašeg. Vi ste jedni od drugih, zato ih ženite s dozvolom (gospodara) porodica njihovih, i dajte im darove njihove pristojno: kreposnim, ne bludnicama niti uzimačicama (intimnih) prijatelja. Pa ako su udate te počine blud - tad je protiv njih pola onoga šta je protiv kreposnih, od kazne. To je za onog od vas ko se plaši nevolje. A da se strpite, bolje vam je! A Allah je Oprosnik, Milosrdni.'),
(519, 4, 26, 'Allah želi da vam objasni i uputi vas sunenu onih prije vas, i oprosti vam; a Allah je Znalac, Mudri.'),
(520, 4, 27, 'I Allah želi da vam oprosti; a oni koji slijede strasti žele da skrenete skretanjem velikim.'),
(521, 4, 28, 'Allah želi da vam olakša; a stvoren je čovjek slabim.'),
(522, 4, 29, 'O vi koji vjerujete! Ne jedite imetke svoje među sobom zaludno, izuzev da bude trgovina sporazumna među vama. I ne ubijajte duše vaše. Uistinu! Allah je vama Milosrdan.'),
(523, 4, 30, 'A ko to učini neprijateljski i zulmom - pa pržićemo ga vatrom, a to je Allahu lahko.'),
(524, 4, 31, 'Ako se klonite velikog - onog šta vam je zabranjeno, pokrit ćemo vam loša djela vaša i daćemo vam da uđete ulazom plemenitim.'),
(525, 4, 32, 'I ne žudite za onim čim vas je odlikovao Allah jedne nad drugima. Ljudi imaju dio od onog šta su zaradili i žene imaju dio onog šta su zaradile. I tražite od Allaha iz blagodati Njegove. Uistinu! Allah je o svakoj stvari Znalac.'),
(526, 4, 33, 'I svačemu smo učinili nasljednike od onog šta ostave roditelji i rođaci. A oni s kojima su desnice vaše sklopile sporazum, pa dajte im dio njihov. Uistinu! Allah je nad svakom stvari Svjedok.'),
(527, 4, 34, 'Ljudi su poglavari ženama, zato što ih je odlikovao Allah jedne nad drugima, i što troše (oni) iz imetaka svojih. Zato su dobre poslušne, čuvarke nevidljivog, zato što (njih) čuva Allah. A one za koje se plašite nepokornosti njihove, pa posavjetujte ih, i ostavite ih u posteljama, i udarite ih. Pa ako vas poslušaju, tad ne tražite protiv njih puta. Uistinu! Allah je Uzvišeni, Veliki.'),
(528, 4, 35, 'A ako se plašite razdora između njih dvoje, tad pošaljite suca iz porodice njegove i suca iz porodice njene. Ako žele popravljanje, srediće Allah između njih dvoje. Uistinu! Allah je Znalac, Obaviješteni.'),
(529, 4, 36, 'I obožavajte Allaha i ne pridružujte Mu ništa! A roditeljima dobročinstvo - i rodbini i siročadi i siromasima i komšiji bližem i komšiji strancu, i drugu uz bok i sinu puta, i onom šta posjeduju desnice vaše. Uistinu! Allah ne voli onog ko je razmetljiv, hvalisav,'),
(530, 4, 37, 'One koji škrtare i naređuju ljudima tvrdičluk i skrivaju šta im je dao Allah iz blagodati Svoje; a pripremili smo nevjernicima kaznu prezrenu.'),
(531, 4, 38, 'I one koji udjeljuju imetke svoje da vide ljudi, a ne vjeruju u Allaha i Dan posljednji. A onaj kome bude šejtan drug, pa loš je drug.'),
(532, 4, 39, 'A šta bi im bilo da vjeruju u Allaha i Dan posljednji, i udjeljuju od onog čim ih je opskrbio Allah? - a Allah je o njima Znalac.'),
(533, 4, 40, 'Uistinu! Allah ne čini nepravdu težine zerre; i ako bude dobro djelo, udvostručiće ga i dati od Sebe nagradu veličanstvenu.'),
(534, 4, 41, 'Pa kako će biti kad dovedemo svjedoka iz svakog naroda i dovedemo tebe protiv ovih svjedokom?'),
(535, 4, 42, 'Tog dana će voljeti oni koji nisu vjerovali i nisu poslušali Poslanika, da oni budu sravnjeni sa zemljom, a neće sakriti od Allaha priču.'),
(536, 4, 43, 'O vi koji vjerujete! Ne približujte se salatu budete li vi pijani, dok ne znadnete šta govorite, niti (kao) džunubi - izuzev na proputovanju putem - dok se ne operete. A ako budete bolesni ili na putovanju ili neko od vas dođe iz nužnika ili budete imali odnos sa ženama - pa niste našli vode, tad idite tlu čistom te potarite time lica svoja i ruke svoje. Uistinu! Allah je Brisalac (grijeha), Oprosnik.'),
(537, 4, 44, 'Zar nisi vidio one kojima je dat dio Knjige, kupuju zabludu i žele da skrenete s puta.'),
(538, 4, 45, 'A Allah je Najbolji znalac neprijatelja vaših, i dovoljan je Allah Zaštitnik i dovoljan je Allah Pomagač.'),
(539, 4, 46, '(Neki) od onih koji su jevreji iskrivljuju riječi na mjestima njihovim i govore: "^ujemo, a nećemo poslušati" i "^uj ne čuo" i "Raina"- izokrećući jezicima svojim i klevećući vjeru. A da oni kažu: "^ujemo i pokoravamo se" i "Slušaj" i "Unzurna" - sigurno bi bilo bolje za njih i ispravnije. Međutim, prokleo ih je Allah zbog nevjerovanja njihovog, pa ne vjeruju, izuzev malo.'),
(540, 4, 47, 'O vi kojima je data Knjiga! Vjerujte u ono šta smo objavili potvrdom za ono kod vas, prije no što izbrišemo lica, pa ih vratimo na pozadine njihove ili ih prokunemo kao što smo prokleli kršitelje sebta. A naredba Allahova je izvršna.'),
(541, 4, 48, 'Uistinu! Allah ne oprašta da Mu se pridružuje, a oprašta šta je mimo toga, kome hoće. A ko pridruži Allahu - pa doista je izmislio grijeh strašan.'),
(542, 4, 49, 'Zar nisi vidio one koji pripisuju čistotu dušama svojim? Naprotiv, Allah čisti koga hoće, a neće se njima učiniti zulm ni žilice hurmine košpice.'),
(543, 4, 50, 'Pogledaj kako izmišljaju protiv Allaha laž, a to je dovoljno očit grijeh.'),
(544, 4, 51, 'Zar nisi vidio one kojima je dat dio Knjige? Vjeruju u džibta i taguta, i govore za one koji ne vjeruju: "Ovi su upućeniji putu od onih koji vjeruju."'),
(545, 4, 52, 'To su oni koje je prokleo Allah, a koga prokune Allah, pa nećeš mu naći pomagača.'),
(546, 4, 53, 'Zar oni imaju dio vlasti? Pa tad ne bi dali ljudima zareza na hurminoj košpici.'),
(547, 4, 54, 'Zar zavide ljudima na onom šta im je dao Allah iz dobrote Svoje? Pa doista smo dali porodu Ibrahimovom Knjigu i mudrost, i dali im vlast veličanstvenu.'),
(548, 4, 55, 'Pa između njih je ko je vjerovao u nju, i od njih je ko se odvraćao od nje; a dovoljan je Džehennem (kao) seir.'),
(549, 4, 56, 'Uistinu! One koji ne vjeruju u ajete Naše: pržićemo ih vatrom. Kad god se ispeku kože njihove zamijenit ćemo ih kožama drugim, da iskušaju kaznu. Uistinu! Allah je Moćni, Mudri.'),
(550, 4, 57, 'A koji vjeruju i rade dobra djela, uvešćemo ih u bašče ispod kojih teku rijeke, vječno će biti u njima, zauvijek. Imaće oni u njima družice čiste, i uvešćemo ih u hladovinu debelu.'),
(551, 4, 58, 'Uistinu! Allah vam naređuje da vratite emanete vlasnicima njihovim, i kad sudite među ljudima da sudite pravično. Uistinu je lijepo ono čim vas Allah savjetuje. Uistinu! Allah je Onaj koji čuje, Onaj koji vidi.'),
(552, 4, 59, 'O vi koji vjerujete! Pokoravajte se Allahu i pokoravajte se Poslaniku i nosiocima vlasti između vas; pa ako se budete prepirali o nečemu, tad to vratite Allahu i Poslaniku, ako vjerujete u Allaha i Dan posljednji. To je boljeg i ljepšeg ishoda.'),
(553, 4, 60, 'Zar nisi vidio one koji tvrde da oni vjeruju u ono šta je objavljeno tebi i šta je objavljeno prije tebe? Žele da sude prema tagutu, a već im je naređeno da ne vjeruju u njega. I želi šejtan da ih zavede zabludi dalekoj.'),
(554, 4, 61, 'I kad im se kaže: "Idite onom šta je objavio Allah, i Poslaniku!", vidiš munafike okreću se od tebe, odustajući.'),
(555, 4, 62, 'Pa kako će biti kad ih snađe nesreća zbog onog šta su unaprijed poslale ruke njihove?, zatim dođu tebi kunući se Allahom: "Željeli smo samo dobročinstvo i sređivanje."'),
(556, 4, 63, 'To su oni za koje zna Allah šta je u srcima njihovim. Zato se okreni od njih i savjetuj ih, i reci im za duše njihove riječi djelotvorne.'),
(557, 4, 64, 'A nismo poslali nijednog poslanika osim da bude slušan s dozvolom Allahovom. A da oni - kad učine zulm dušama svojim - dođu tebi pa zatraže oprost od Allaha, i (da) im zatraži oprost Poslanik, sigurno bi našli Allaha Primaocem pokajanja, Milosrdnim.'),
(558, 4, 65, 'Pa ne, tako Mi Gospodara tvog, neće vjerovati dok tebe sucem ne učine za ono šta je sporno među njima, zatim ne nađu u dušama svojim tjeskobe zbog onog šta si presudio i predaju se pokorno.'),
(559, 4, 66, 'A da smo im Mi propisali: "Ubijte duše svoje!" ili "Izađite iz kuća vaših", ne bi to učinili, izuzev malo njih. A da oni čine ono čim se savjetuju, sigurno bi bilo bolje za njih i više učvršćujuće;'),
(560, 4, 67, 'I tad bismo im sigurno dali od Nas nagradu veliku,'),
(561, 4, 68, 'I sigurno ih uputili putu pravom.'),
(562, 4, 69, 'A ko posluša Allaha i Poslanika, pa takvi su sa onima koje je obdario Allah od vjerovjesnika i pravednika i šehida i dobrih. A kako su divni takvi drug(ovi).'),
(563, 4, 70, 'To je blagodat od Allaha, a dovoljan je Allah Znalac.'),
(564, 4, 71, 'O vi koji vjerujete! Preduzmite (mjere) opreza vašeg, pa nastupajte grupno ili nastupajte svi zajedno.'),
(565, 4, 72, 'A uistinu, od vas je onaj ko oklijeva. Pa ako vas pogodi nesreća, kaže: "Doista me je obdario Allah kad nisam bio s njima prisutan!"'),
(566, 4, 73, 'A ako vas zadesi blagodat od Allaha, sigurno govori - kao da nije bilo između vas i između njega ljubavi: "O da sam ja bio s njima, te postigao uspjeh veliki!"'),
(567, 4, 74, 'Pa neka se bore na putu Allahovom oni koji prodaju život Dunjaa za Ahiret. A ko se bori na putu Allahovom pa pogine ili pobijedi, ta daćemo mu nagradu veličanstvenu.'),
(568, 4, 75, 'A šta vam je? Ne borite se na putu Allahovom, i (za) onemoćale od ljudi i žena i djece, koji govore: "Gospodaru naš! Izvedi nas iz ovog grada, zalimi su stanovnici njegovi. I učini nam od Sebe zaštitnika i učini nam od Sebe pomagača."'),
(569, 4, 76, 'Oni koji vjeruju, bore se na putu Allahovom; a oni koji ne vjeruju bore se na putu taguta. Zato se borite protiv prijatelja šejtanovih. Uistinu, spletka šejtanova je slaba.'),
(570, 4, 77, 'Zar nisi vidio one kojima je rečeno: "Suzdržite ruke svoje i obavljajte salat i dajite zekat", pa pošto im je propisana borba: Gle!, grupa njih se plašila ljudi, kao bojanjem Allaha ili žešćim strahom, i rekoše: "Gospodaru naš! Zašto si nam propisao borbu? Zašto nam nisi odložio do roka bliskog?" Reci: "Uživanje Dunjaa je malo, a Ahiret je bolji za onog ko se boji"; a neće vam se učiniti zulm ni žilice hurmine košpice.'),
(571, 4, 78, 'Ma gdje bili stići će vas smrt, makar bili u tornjevima podignutim. I ako ih zadesi dobro, govore: "Ovo je od Allaha!"; a ako ih zadesi zlo, govore: "Ovo je od tebe!" Reci: "Sve je od Allaha!" Pa šta je ovim ljudima, skoro da ne shvataju govor?'),
(572, 4, 79, 'Ono šta te zadesi od dobrog - pa od Allaha je, a šta te zadesi od zla - pa od duše je tvoje. I poslali smo tebe ljudima Poslanikom, a dovoljan je Allah Svjedok.'),
(573, 4, 80, 'Ko posluša Poslanika, pa doista je poslušao Allaha; a ko se okrene - pa nismo te poslali nad njima čuvarom.'),
(574, 4, 81, 'I govore: "Poslušnost." Pa kad odu od tebe, skupina njih planira noću drugačije od onog što govoriš; a Allah piše šta smišljaju noću. Zato se okreni od njih i pouzdaj se u Allaha. A dovoljan je Allah Zaštitnik.'),
(575, 4, 82, 'Pa zašto ne razmisle o Kur\'anu? A da je od nekog drugog mimo Allaha, sigurno bi našli u njemu proturječnost mnogu.'),
(576, 4, 83, 'I kad im dođe stvar o sigurnosti ili strahu, razglase je. A da to prenesu Poslaniku i nosiocima vlasti između njih, sigurno bi to otkrili oni od njih koji to ispituju. A da nije dobrote Allahove prema vama i milosti Njegove, sigurno biste slijedili šejtana, izuzev malo (vas).'),
(577, 4, 84, 'Zato se bori na putu Allahovom - ne teretiš se osim dušom svojom - i podstiči vjernike. Možda će Allah da zadrži silu onih koji ne vjeruju. A Allah je žešće sile i žešći strogim kažnjavanjem.'),
(578, 4, 85, 'Ko se zauzme posredovanjem dobrog, biće za njega dio toga. A ko se zauzme posredovanjem zla, biće za njega od toga dio; a Allah je svega Hranitelj.'),
(579, 4, 86, 'I kad budete pozdravljeni pozdravom, tad otpozdravite ljepšim od njega, ili ga uzvratite. Uistinu! Allah je nad svakom stvari Obračunac.'),
(580, 4, 87, 'Allah! Samo je On Bog! Sigurno će vas skupiti za Dan kijameta u koji nema sumnje. A ko je istinitiji od Allaha riječju?'),
(581, 4, 88, 'Pa šta vam je? O munaficima ste (podijeljeni u) dvije skupine, a Allah ih je povratio zbog onog šta su zaradili. Zar želite da uputite onog koga je zabludio Allah? A koga zabludi Allah, pa nećeš mu naći puta.'),
(582, 4, 89, 'Vole da ne vjerujete kao što ne vjeruju, pa da budete isti. Zato ne uzimajte od njih prijatelje, dok se ne isele na putu Allahovom. Pa ako se okrenu, tad ih ščepajte i ubijajte ih gdje god ih nađete, i ne uzimajte od njih prijatelja niti pomagača,'),
(583, 4, 90, 'Izuzev onih koji se pripoje narodu s kojim imate sporazum, ili vam dođu stiješnjenih grudi svojih da bi se borili protiv vas ili borili protiv naroda svog. A da hoće Allah, sigurno bi im dao moć nad vama, pa bi se sigurno borili protiv vas. Pa ako se povuku od vas, pa se ne bore protiv vas i ponude vam mir, tad vam nije Allah načinio protiv njih puta.'),
(584, 4, 91, 'Naći ćete druge - žele da se osiguraju od vas, i osiguraju od naroda njihovog. Kad god se vrate fitneluku, podlegnu mu. Pa ako se ne povuku od vas i (ne) ponude vam mir, i (ne) zadrže ruke svoje, tad ih ščepavajte i ubijajte ih gdje god ih nađete; a to su vam ti protiv kojih smo vam dali ovlaštenje očito.'),
(585, 4, 92, 'I nije za vjernika da ubije vjernika, izuzev greškom. A ko ubije vjernika greškom, tad (slijedi) oslobađanje roba vjernika i davanje krvarine porodici njegovoj, izuzev ako je učine sadakom. Pa ako bude iz naroda neprijateljskog vama - a on bude vjernik - tad (slijedi) oslobođenje roba vjernika. A ako je iz naroda s kojim imate sporazum, tad (slijedi) plaćanje krvarine porodici njegovoj i oslobođenje roba vjernika. Pa ko ne nađe, tad (slijedi) post dva mjeseca uzastopno, kajanjem Allahu. A Allah je Znalac, Mudri.'),
(586, 4, 93, 'A ko ubije vjernika namjerno, tad je plaća njegova Džehennem, vječno će biti u njemu, i rasrdiće se Allah na njega i prokleće ga i pripremiti mu kaznu užasnu.'),
(587, 4, 94, 'O vi koji vjerujete! Kad krenete na putu Allahovom, tad se obavijestite. I ne recite onom ko vam ponudi selam: "Nisi vjernik", tražeći prolazno dobro života Dunjaa. Pa kod Allaha su dobiti mnoge. Takvi ste bili prije, pa vas je Allah obdario. Zato se obavijestite. Uistinu! Allah je o onom šta radite Obaviješteni.'),
(588, 4, 95, 'Nisu isti oni koji sjede od vjernika - izuzev koji imaju teškoću - i mudžahidi na putu Allahovom sa imecima njihovim i dušama njihovim. Odlikovao je Allah mudžahide imecima njihovim i dušama njihovim nad onima koji sjede, stepenom. I svakojem je obećao Allah dobro, a odlikovao je Allah mudžahide nad onima koji sjede, nagradom veličanstvenom:'),
(589, 4, 96, 'Stepenima od Sebe i oprostom i milošću; a Allah je Oprosnik Milosrdni.'),
(590, 4, 97, 'Uistinu, onima koje budu usmrćivali meleci - (dok budu) činili zulm dušama svojim - govorit će: "U čemu ste bili?" Reći će: "Bili smo nemoćni na Zemlji." Reći će: "Zar nije Zemlja Allahova prostrana, pa da se selite po njoj?" Pa tim takvima će sklonište biti Džehennem, a zlog li dolazišta!'),
(591, 4, 98, 'Izuzev nemoćnih od ljudi i žena i djece - ne mogu dovitljivošću, niti se upućuju putu.'),
(592, 4, 99, 'Pa tim takvima će možda Allah da izbriše, a Allah je Brisalac (grijeha), Oprosnik.'),
(593, 4, 100, 'A ko se iseli na putu Allahovom, naći će na Zemlji utočišta mnoga i obilje. I ko napusti kuću svoju (kao) muhadžir Allahu i Poslaniku Njegovom, zatim ga sustigne smrt, pa doista (mu) zapada nagrada njegova od Allaha. A Allah je Oprosnik, Milosrdni.'),
(594, 4, 101, 'A kad budete putovali po Zemlji, tad vam nije grijeh da skratite salat, ako se plašite da će vas napasti oni koji ne vjeruju. Uistinu! Nevjernici su vam neprijatelj otvoreni.'),
(595, 4, 102, 'I kad budeš među njima, pa za njih salat obavljaš, tad neka stoji grupa njih s tobom i neka uzmu oružja svoja. Pa kad obave sedždu, onda neka (odu) iza vas. I neka dođe grupa drugih - (koji) nisu obavili salat - pa neka obave salat s tobom, i neka budu oprezni i (uz) oružja svoja. Voljeli bi oni koji ne vjeruju da zanemarite oružja vaša i opreme vaše, pa da se obruše na vas nasrtajem jednim. I nije vam grijeh - ako vam bude neprijatnost od kiše ili budete bolesni - da odložite oružja vaša. I preduzmite (mjere) opreza vašeg. Uistinu! Allah je nevjernicima pripremio kaznu prezrenu.'),
(596, 4, 103, 'Pa kad završite salat, tad spominjite Allaha stojeći, i sjedeći i na bokovima vašim. Pa kad budete sigurni, tad obavljajte salat. Uistinu, salat je vjernicima propis vremenski određen.'),
(597, 4, 104, 'I ne malaksajte u traženju naroda (neprijatelja). Ako se vi patite, pa uistinu, oni se pate kao što se patite, i nadate se od Allaha čemu se ne nadaju; a Allah je Znalac, Mudri.'),
(598, 4, 105, 'Uistinu! Mi smo ti spustili Knjigu s Istinom, da sudiš među ljudima onim šta ti pokazuje Allah. A ne budi izdajicama zastupnik;'),
(599, 4, 106, 'I traži oprost od Allaha. Uistinu, Allah je Oprosnik, Milosrdni.'),
(600, 4, 107, 'I ne raspravljaj za one koji varaju duše svoje. Uistinu! Allah ne voli onog ko je izdajnik, grješnik.'),
(601, 4, 108, 'Kriju se od ljudi, a ne kriju od Allaha, a On je s njima kad snuju ono šta ne zadovoljava od riječi. A Allah ono šta rade okružuje.'),
(602, 4, 109, 'Eto, vi ste ti koji se prepirete zbog njih u životu Dunjaa, pa ko će se prepirati s Allahom zbog njih na Dan kijameta, ili ko će biti nad njima branilac?'),
(603, 4, 110, 'A ko uradi zlo ili učini zulm duši svojoj, zatim zatraži oprost od Allaha, naći će Allaha Oprosnikom, Milosrdnim.'),
(604, 4, 111, 'A ko počini grijeh, pa samo ga je počinio protiv duše svoje. A Allah je Znalac, Mudri.'),
(605, 4, 112, 'I ko počini grešku ili grijeh, zatim okrivi time nevinog, pa doista se opteretio klevetom i grijehom očitim.'),
(606, 4, 113, 'A da nije dobrote Allahove prema tebi i milosti Njegove, sigurno bi namjerila skupina njih da te zavede, a neće zavesti, izuzev duše svoje, i neće ti štetiti nimalo. I objavljuje ti Allah Knjigu i mudrost, i uči te ono šta nisi znao; a dobrota Allahova je prema tebi velika.'),
(607, 4, 114, 'Nema dobrog u mnogom od tajnog razgovora njihovog, osim ko naredi sadaku ili dobročinstvo ili sređivanje među ljudima. A ko to uradi, tražeći zadovoljstvo Allahovo, pa daćemo mu nagradu veličanstvenu.'),
(608, 4, 115, 'A ko se suprotstavi Poslaniku, nakon što mu je objašnjena Uputa, i bude slijedio drugi - (a ne) put vjernika, upravićemo ga čemu se okrenuo i pržićemo ga Džehennemom, a zlog li dolazišta!'),
(609, 4, 116, 'Uistinu! Allah ne prašta da Mu se pridružuje, a oprašta šta je mimo toga, kome hoće. A ko pridružuje Allahu, pa doista je zabludio zabludom dalekom.'),
(610, 4, 117, 'Ne prizivaju mimo Njega - izuzev ženske; i ne prizivaju, izuzev šejtana buntovnika.'),
(611, 4, 118, 'Prokleo ga je Allah. Rekao je: "Sigurno ću uzeti od robova Tvojih dio određen,'),
(612, 4, 119, 'I sigurno ću ih zavoditi i doista im pobuđivati nadu, i sigurno im naređivati, pa će doista rasjecati uši životinjama, i sigurno ću im naređivati pa će doista preinačavati stvaranje Allahovo." A ko uzme šejtana prijateljem mimo Allaha, pa doista je oštećen gubitkom očitim.'),
(613, 4, 120, 'Obećava im i pobuđuje im nadu, a obećava im šejtan jedino obmanu.'),
(614, 4, 121, 'Tim takvima će sklonište biti Džehennem, a neće naći od njega pribježišta.'),
(615, 4, 122, 'A one koji vjeruju i rade dobra djela, njih ćemo uvesti u bašče ispod kojih teku rijeke, vječno će biti u njima, zauvijek. Obećanje Allahovo je Istina; a ko je istinitiji od Allaha riječju?'),
(616, 4, 123, 'Neće biti po željama vašim, niti po željama sljedbenika Knjige. Ko uradi zlo, biće kažnjen zbog njega i neće sebi naći mimo Allaha zaštitnika, niti pomagača.'),
(617, 4, 124, 'A ko čini dobra djela - muškarac ili žena, a on bude vjernik - pa takvi će ući u Džennet, i neće im se učiniti zulm ni zareza na hurminoj košpici.'),
(618, 4, 125, 'A ko je bolji vjerom od onog ko preda lice svoje Allahu i on bude dobročinitelj i slijedi millet Ibrahima hanife?, a uzeo je Allah Ibrahima prijateljem.'),
(619, 4, 126, 'I Allahovo je šta je u nebesima i šta je na Zemlji; a Allah obuhvata svaku stvar.'),
(620, 4, 127, 'I traže od tebe rješenje o ženama. Reci: "Allah vam daje rješenje za njih i ono šta vam se uči u Knjizi o ženama sirotama, onima kojima ne dajete šta im je propisano, a želite da ih oženite - i nejači od djece, i da stanete spram siročadi s pravdom." A šta učinite od dobrog, pa uistinu, Allah je o tome Znalac.'),
(621, 4, 128, 'A ako se žena bude bojala od muža svog surovosti ili izbjegavanja, tad nije grijeh njima dvoma da se izmire međusobno, pomirenjem. Pomirenje je najbolje. A prisutna je dušama škrtost. I ako budete činili dobro i bojali se, pa uistinu, Allah je o onom šta radite Obaviješteni.'),
(622, 4, 129, 'A nećete moći jednako postupati među ženama, makar željeli. Zato ne budite naklonjeni svakom naklonošću (jednoj), pa da (drugu) ostavite kao viseću. A ako se izmirite i budete se bojali (Allaha), pa uistinu, Allah je Oprosnik, Milosrdni.'),
(623, 4, 130, 'A ako se rastave, učiniće Allah neovisnim svakoje iz obilja Svog. A Allah je Sveobuhvatni, Mudri.'),
(624, 4, 131, 'I Allahovo je šta je u nebesima i šta je na Zemlji. I doista smo preporučili onima kojima je data Knjiga prije vas, i vama, da se bojite Allaha. A ako ne budete vjerovali, pa uistinu, Allahovo je šta je u nebesima i šta je na Zemlji, a Allah je Neovisni, Hvaljeni.'),
(625, 4, 132, 'I Allahovo je šta je u nebesima i šta je na Zemlji, a dovoljan je Allah Zaštitnik.'),
(626, 4, 133, 'Ako htjedne ukloniće vas, o ljudi, i dovesti druge; a Allah je za to Moćni.'),
(627, 4, 134, 'Ko želi nagradu Dunjaa - pa kod Allaha je nagrada Dunjaa i Ahireta; a Allah je Onaj koji čuje, Onaj koji vidi.'),
(628, 4, 135, 'O vi koji vjerujete! Stanite čvrsti pravdom - svjedoci Allahu, makar protiv duša vaših ili roditelja i bližnjih, bio bogat ili fukara - pa Allah je oboma bolji zaštitnik. Zato ne slijedite strast, da ne skrenete; a ako iskrivite ili se odvratite, pa uistinu, Allah je o onom šta radite Obaviješteni.'),
(629, 4, 136, 'O vi koji vjerujete! Vjerujte u Allaha i Poslanika Njegovog i Knjigu koju objavljuje Poslaniku Svom, i Knjigu koju je objavio ranije. A ko ne vjeruje u Allaha i meleke Njegove, i knjige Njegove i poslanike Njegove, i Dan posljednji, pa doista je zabludio zabludom dalekom.'),
(630, 4, 137, 'Uistinu, oni koji vjeruju, zatim ne vjeruju, potom vjeruju, zatim ne vjeruju, potom povećaju nevjerovanje, neće im Allah oprostiti, niti će ih uputiti putu.'),
(631, 4, 138, 'Obraduj munafike da će oni imati kaznu bolnu,'),
(632, 4, 139, 'Oni koji uzimaju nevjernike prijateljima pored vjernika, traže li kod njih moć? - pa uistinu, moć je Allahova sva!'),
(633, 4, 140, 'A već vam je objavio u Knjizi, da kad čujete ajete Allahove - njih poricane i njih izrugivane - tad ne sjedite s njima dok ne uđu u razgovor drugačiji od tog. Uistinu, vi ste tad slični njima. Uistinu! Allah je Taj koji će sakupiti munafike i nevjernike u Džehennem, zajedno;'),
(634, 4, 141, 'One koji iščekuju za vas. Pa ako vi imadnete pobjedu od Allaha, kažu: "Zar nismo s vama?", a ako imadnu nevjernici udio, kažu (im): "Zar nismo ovladali vama i odbranili vas od vjernika?" Pa Allah će presuditi među vama na Dan kijameta, i neće Allah načiniti nevjernicima protiv vjernika puta.'),
(635, 4, 142, 'Uistinu, munafici nastoje prevariti Allaha, a On je Taj koji vara njih. I kad ustaju na salat, ustaju lijeni da ih vide ljudi, i ne spominju Allaha, izuzev malo.'),
(636, 4, 143, 'Kolebljivi su između toga: niti ovima, niti onima. A koga zaludi Allah, tad njemu nećeš naći puta.'),
(637, 4, 144, 'O vi koji vjerujete! Ne uzimajte nevjernike prijateljima mimo vjernika. Zar želite da načinite Allahu protiv sebe dokaz očit?'),
(638, 4, 145, 'Uistinu, munafici će biti u najnižem dnu vatre i nećeš naći njima pomagača;'),
(639, 4, 146, 'Izuzev koji se pokaju i poprave i drže se Allaha i učine iskrenom vjeru svoju Allahu, pa takvi će biti s vjernicima; a daće Allah vjernicima nagradu veličanstvenu.'),
(640, 4, 147, 'Što da da Allah kaznu vašu, ako zahvaljujete i vjerujete? A Allah je Zahvalni, Znalac.'),
(641, 4, 148, 'Allah ne voli javnost zlog govora, izuzev (od) onoga kome je učinjen zulm; a Allah je Onaj koji čuje, Znalac.'),
(642, 4, 149, 'Ako ispoljite dobro ili ga sakrijete, ili oprostite zlo, pa uistinu, Allah je Brisalac (grijeha), Svemoćni.'),
(643, 4, 150, 'Uistinu! Oni koji ne vjeruju u Allaha i poslanike Njegove, i žele da načine razliku između Allaha i poslanika Njegovih (u vjerovanju) i govore: "Vjerujemo u neke, a ne vjerujemo u neke", i žele da uzmu između toga put,'),
(644, 4, 151, 'Ti takvi su nevjernici istinski! A pripremili smo za nevjernike kaznu prezrenu.'),
(645, 4, 152, 'A oni koji vjeruju u Allaha i poslanike Njegove i ne prave razliku između ijednog od njih, tim takvima će dati nagrade njihove; a Allah je Oprosnik, Milosrdni.'),
(646, 4, 153, 'Traže sljedbenici Knjige od tebe da im spustiš knjigu s neba. Pa doista su tražili od Musaa više od toga, te rekli: "Pokaži nam Allaha javno", pa ih pogodi grom zbog zulma njihovog. Zatim uzeše tele, nakon što su im došli dokazi jasni. Pa oprostili smo to! I dadosmo Musau ovlast očitu,'),
(647, 4, 154, 'I uzdigosmo iznad njih brdo, zbog zavjeta njihovog, i rekosmo im: "Uđite na kapiju sa sedždom!", i rekosmo im: "Ne kršite sebt!", i uzesmo od njih zavjet čvrst.'),
(648, 4, 155, 'Pa zbog njihovog kršenja zavjeta njihovog i nevjerovanja njihovog u znakove Allahove, i njihovog ubijanja vjerovjesnika bez prava, i riječi njihovih: "Srca su naša oblande" - naprotiv, zapečatio ih je Allah zbog nevjerovanja njihovog, pa ne vjeruju, izuzev malo (njih)'),
(649, 4, 156, 'I zbog nevjerovanja njihovog i riječi njihovih protiv Merjem - klevete velike;'),
(650, 4, 157, 'I riječi njihovih: "Uistinu! Mi smo ubili Mesiha, Isaa, sina Merjeminog, poslanika Allahovog." A nisu ga ubili i nisu ga raspeli, nego im se pričinilo; a uistinu, oni koji se o tome razilaze, u sumnji su o tom. Nemaju oni o tom nikakva znanja, samo slijede nagađanja. A sigurno ga nisu ubili!'),
(651, 4, 158, 'Naprotiv, uzvisio ga je Allah k Sebi! A Allah je Moćni, Mudri.'),
(652, 4, 159, 'I nema nijednog sljedbenika Knjige, a da neće sigurno u njega povjerovati prije smrti njegove, a na Dan kijameta protiv njih će svjedok biti.'),
(653, 4, 160, 'Pa zbog zulma onih koji su jevreji, zabranili smo im dobre stvari (koje su) im bile dozvoljene, i zbog njihovog odvraćanja mnogih od puta Allahovog;'),
(654, 4, 161, 'I njihovog uzimanja kamate - a doista im je to bilo zabranjeno, i njihovog jedenja imetaka ljudi nepravedno. A pripremili smo za nevjernike među njima kaznu bolnu.'),
(655, 4, 162, 'Međutim, čvrstima u znanju između njih i vjernicima - vjeruju u ono šta se objavljuje tebi i šta je objavljeno prije tebe - i onima koji obavljaju salat i davaocima zekata i vjernicima u Allaha i Dan posljednji - tim takvima ćemo dati nagradu veličanstvenu.'),
(656, 4, 163, 'Uistinu! Mi ti objavljujemo kao što smo objavljivali Nuhu i vjerovjesnicima poslije njega. I objavljivali smo Ibrahimu i Ismailu i Ishaku i Jakubu i plemenima, i Isau i Ejjubu, i Junusu i Harunu i Sulejmanu, a Dawudu smo dali Zebur;'),
(657, 4, 164, 'I poslanicima o kojima smo ti već kazivali ranije, i poslanicima o kojima ti nismo kazivali - a govorio je Allah s Musaom govorom -'),
(658, 4, 165, 'Poslanicima donosiocima radosnih vijesti i opominjačima, da ne imadnu ljudi izgovor Allahu nakon poslanika. A Allah je Moćni, Mudri.'),
(659, 4, 166, 'Međutim, Allah svjedoči o onome šta ti je objavio; objavio ga je znanjem Svojim, i meleci svjedoče; a dovoljan je Allah svjedok.'),
(660, 4, 167, 'Uistinu, oni koji ne vjeruju i odvraćaju od puta Allahovog, doista su zabludili zabludom dalekom.'),
(661, 4, 168, 'Uistinu, oni koji ne vjeruju i čine zulm, neće im Allah oprostiti, niti ih uputiti stazi,'),
(662, 4, 169, 'Izuzev stazi Džehennema! Vječno će biti u njemu, zauvijek, a to je Allahu lahko.'),
(663, 4, 170, 'O ljudi! Doista vam je došao Poslanik s Istinom od Gospodara vašeg - zato vjerujte, bolje je za vas. A ako ne vjerujete, pa uistinu, Allahovo je šta je na nebesima i Zemlji; a Allah je Znalac, Mudri.'),
(664, 4, 171, 'O sljedbenici Knjige! Ne pretjerujte u vjeri vašoj i govorite o Allahu jedino istinu. Mesih, Isa, sin Merjemin, samo je poslanik Allahov i Riječ Njegova koju je uputio Merjemi - i Duh od Njega. Zato vjerujte u Allaha i poslanike Njegove. I ne recite: "Trojica!" - prestanite, bolje je za vas! Samo je Allah Bog, Jedini! Slava neka je Njemu - da On ima dijete!!! Njegovo je šta je u nebesima i šta je na Zemlji; a dovoljan je Allah Zaštitnik.'),
(665, 4, 172, 'Ne prezire Mesih da bude rob Allahov, niti meleci bliski. A ko bude prezirao obožavanje Njegovo i oholio se - pa sabraće ih Sebi sve;'),
(666, 4, 173, 'Pa što se tiče onih koji vjeruju i rade dobra djela, ta isplatit će im nagrade njihove i dodati im iz blagodati Svoje. A što se tiče onih koji preziru i ohole se, pa kaznićemo ih kaznom bolnom. I neće naći mimo Allaha zaštitnika, niti pomagača.'),
(667, 4, 174, 'O ljudi! Doista vam je došao dokaz od Gospodara vašeg i spuštamo vam svjetlost jasnu!'),
(668, 4, 175, 'Pa što se tiče onih koji vjeruju u Allaha i drže se Njega - pa sigurno će dati da oni uđu u milost Njegovu i blagodat, i uputiće ih Sebi putem ispravnim.'),
(669, 4, 176, 'Traže rješenje od tebe. Reci: "Allah vam daje rješenje o kelali: Ako čovjek umre, ne bude li on imao dijete, a imadne on sestru - tad je njeno pola šta je ostavio. A on naslijeđuje nju, ako ona ne bude imala dijete. Pa ako budu dvije, tad njih dvije imaju dvije trećine od onog šta je ostavio. A ako budu braća, ljudi i žene, tad muškarac ima kao dvije ženske." Objašnjava vam Allah da ne zalutate, a Allah je o svakoj stvari Znalac.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'O vi koji vjerujete! Ispunjavajte ugovore. Dozvoljavaju vam se životinje - stoka, izuzev šta vam je navedeno. Nije dopušten lov (kad) ste vi posvećeni. Uistinu! Allah dosuđuje šta želi.'),
(671, 5, 2, 'O vi koji vjerujete! Ne obeščašćujte znakove Allahove, ni mjesec sveti, niti kurbane ni ogrlice, niti one koji održavaju Kuću svetu tražeći blagodat od Gospodara svog i zadovoljstvo. A kad završite, onda lovite. I neka vas nikako ne potakne mržnja naroda što vas je odbio od Mesdžidul-harama, da napadnete. I pomažite se u dobročinstvu i bogobojaznosti, a ne pomažite se u grijehu i neprijateljstvu. I bojte se Allaha. Uistinu! Allah je žestok kaznom.'),
(672, 5, 3, 'Zabranjuje vam se lešina i krv i meso svinje, i ono nad čim je zazvano drugo ime, a ne Allahovo, i udavljeno i ubijeno i strovaljeno i probodeno, i šta je načela zvijer - izuzev šta zakoljete - i šta bude zaklano na žrtveniku i da strelicama tražite podjelu. To je porok. Danas očajavaju oni koji ne vjeruju, zbog Dina vašeg; zato se ne bojte njih, a bojte se Mene! Danas sam vam usavršio Din vaš i upotpunio vam blagodat Svoju i zadovoljan sam za vas Dinom Islamom. Pa ko bude natjeran glađu, ne kao skretalac grijehu - pa uistinu, Allah je Oprosnik, Milosrdni.'),
(673, 5, 4, 'Pitaju te šta im se dozvoljava. Reci: "Dozvoljene su vam dobre stvari i ono šta ste poučili (da ulove) životinje dresirane, učeći ih od onog šta je vas naučio Allah; pa jedite od onog šta vam uhvate, i spominjite ime Allahovo nad tim. I bojte se Allaha. Uistinu! Allah je brz obračunom.'),
(674, 5, 5, 'Danas vam se dozvoljavaju dobre stvari, i hrana onih kojima je data Knjiga je dozvoljena vama - i hrana vaša je dozvoljena njima - i čestite od vjernica, i čestite od onih kojima je data Knjiga prije vas, kad im date darove njihove (kao) kreposni - ne (kao) bludnici, niti (kao) uzimatelji priležnica. A ko poriče vjeru, pa doista će propasti rad njegov i on će na Ahiretu biti od gubitnika.'),
(675, 5, 6, 'O vi koji vjerujete! Kad ustanete na salat, tad operite lica svoja i ruke svoje do lakata, i potarite glave svoje, i (operite) noge svoje do članaka. A ako ste džunubi, onda se očistite. A ako budete bolesni ili na putovanju ili neko od vas dođe iz nužnika, ili imadnete odnos sa ženama, pa (ako) niste našli vode, onda idite tlu čistom, pa time potarite lica svoja i ruke svoje. Ne želi Allah da vam učini ikakvu teškoću, nego želi da vas očisti i da vam upotpuni blagodat Svoju, da biste vi zahvaljivali.'),
(676, 5, 7, 'I sjetite se blagodati Allahove prema vama i zavjeta Njegovog - onog kojim vas je obavezao, kad ste rekli: "Čujemo i poslušaćemo!", i bojte se Allaha. Uistinu! Allah je Znalac grudi.'),
(677, 5, 8, 'O vi koji vjerujete! Stojte čvrsti Allahu svjedoci s pravdom, i neka vas nikako ne potakne mržnja naroda da budete nepravedni. Budite pravedni, to je bliže bogobojaznosti. I bojte se Allaha. Uistinu! Allah je o onom šta radite Obaviješteni.'),
(678, 5, 9, 'Obećao je Allah onima koji vjeruju i čine dobra djela (da) će oni imati oprost i nagradu veličanstvenu.'),
(679, 5, 10, 'A oni koji ne vjeruju i poriču ajete Naše, takvi će biti stanovnici džehima.'),
(680, 5, 11, 'O vi koji vjerujete! Sjetite se blagodati Allahove prema vama, kad je narod namjerio da ispruži na vas ruke svoje, pa je zadržao od vas ruke njihove. I bojte se Allaha, a u Allaha zato neka se pouzdaju vjernici.'),
(681, 5, 12, 'A doista je uzeo Allah zavjet sinova Israilovih, i podigli smo između njih dvanaest poglavara, i reče Allah: "Uistinu! Ja sam sa vama. Ako budete obavljali salat i davali zekat, i vjerovali u poslanike Moje i pomagali ih, i uzajmili Allahu zajam lijep, sigurno ću vam pokriti loša djela vaša i sigurno vam dati da uđete u bašče ispod kojih teku rijeke. Pa ko od vas ne bude poslije toga vjerovao, pa doista je zalutao s pravog puta."'),
(682, 5, 13, 'Pa zbog njihovog kršenja zavjeta njihovog, prokleli smo ih i učinili srca njihova tvrdim. Iskrivljavali su riječi u položajima njihovim i zaboravljali dio onoga čim su opominjani. I nećeš prestati nailaziti na izdaju njihovu, izuzev (kod) malo njih. Pa oprosti im i pređi. Uistinu! Allah voli dobročinitelje.'),
(683, 5, 14, 'I od onih koji govore: "Uistinu, mi smo kršćani." Uzeli smo zavjet njihov, pa su zaboravili dio onog čim su opominjani, te smo podstakli među njima neprijateljstvo i mržnju do Dana kijameta, a obavijestiće ih Allah o onom šta su radili.'),
(684, 5, 15, 'O sljedbenici Knjige! Doista vam je došao Poslanik Naš, razjasniće vam mnogo od onog šta krijete iz Knjige, a preko mnogog prelazite. Doista vam je od Allaha došla svjetlost i Knjiga jasna.'),
(685, 5, 16, 'Allah upućuje njome putevima selama onog ko slijedi zadovoljstvo Njegovo, i izvodi ih iz tmina na svjetlost voljom Svojom, i upućuje ih putu pravom.'),
(686, 5, 17, 'Doista ne vjeruju oni koji govore: "Uistinu! Allah - On je Mesih, sin Merjemin." Reci: "Pa ko može od Allaha išta, ako želi da uništi Mesiha sina Merjeminog i majku njegovu, i onog ko je na Zemlji sve?" A Allahova je vlast nebesa i Zemlje, i onog između njih dvoje. Stvara šta hoće; a Allah nad svakom stvari ima moć.'),
(687, 5, 18, 'I govore jevreji i kršćani: "Mi smo sinovi Allahovi i miljenici Njegovi." Reci: "Pa zašto vas kažnjava zbog grijeha vaših?" Naprotiv, vi ste smrtnici od onog koga je stvorio. Oprašta kome hoće, a kažnjava koga hoće. A Allahova je vlast nebesa i Zemlje i onog između njih, a Njemu je povratište!'),
(688, 5, 19, 'O sljedbenici Knjige! Doista vam je došao Poslanik Naš, razjašnjava vam nakon prestanka (slanja) poslanika, da ne kažete: "Nije nam došao nijedan donosilac radosnih vijesti niti opominjač." Zato vam je već došao donosilac radosnih vijesti i opominjač. A Allah nad svakom stvari ima moć.'),
(689, 5, 20, 'I kad reče Musa narodu svom: "O narode moj! Sjetite se blagodati Allahove prema vama, kad je postavio između vas vjerovjesnike i učinio vas kraljevima i dao vam šta nije dao nikom od svjetova.'),
(690, 5, 21, 'O narode moj! Uđite u zemlju svetu koju vam je propisao Allah. I ne okrećite leđa svoja, pa da se pretvorite u gubitnike."'),
(691, 5, 22, 'Rekoše: "O Musa! Uistinu u njoj je narod jakih (ljudi), i uistinu, mi nećemo ući u nju dok ne izađu iz nje. Pa ako izađu iz nje, tad ćemo uistinu mi biti ti koji će ući."'),
(692, 5, 23, 'Dvojica ljudi od onih koji su se bojali (Allaha) - obdario je Allah njih dvojicu, rekoše: "Uđite im na kapiju! Pa kad uđete na nju, tad ćete uistinu vi biti pobjednici, i u Allaha se zato pouzdajte, ako ste vjernici."'),
(693, 5, 24, 'Rekoše: "O Musa! Uistinu, mi nećemo ući u nju nikada dok su u njoj, zato idi ti i Gospodar tvoj, pa se borite! Uistinu, mi ćemo ovdje sjediti."'),
(694, 5, 25, '(Musa) reče: "Gospodaru moj! Uistinu, ja ne vladam osim dušom svojom i brata svog. Zato načini razlaz između nas i naroda grješnika."'),
(695, 5, 26, '(Allah) reče: "Pa uistinu, ona će im biti zabranjena četrdeset godina, lutaće po zemlji. Zato se ne muči nad narodom grješnika."'),
(696, 5, 27, 'I izrecituj im vijest dvojice sinova Ademovih s Istinom, kad su prinijeli kurban, pa je bio primljen od jednog od njih, a nije primljen od drugog. (Prvi) reče: "Sigurno ću te ubiti!" (Drugi) reče: "Allah prima samo od bogobojaznih.'),
(697, 5, 28, 'Kad bi ispružio prema meni ruku svoju da me ubiješ, ne bih ja bio taj koji pruža ruku svoju prema tebi, da te ubijem. Uistinu, ja se bojim Allaha, Gospodara svjetova.'),
(698, 5, 29, 'Uistinu! Ja želim da podneseš moj grijeh i svoj grijeh, i budeš od stanovnika vatre", a to je plaća zalimima.'),
(699, 5, 30, 'Pa mu dopusti duša njegova ubojstvo brata njegovog, te ga ubi i postade od gubitnika.'),
(700, 5, 31, 'Tad posla Allah gavrana da kopa zemlju, da mu pokaže kako će sakriti leš brata svog. Reče: "O teško meni! Zar sam nesposoban biti kao ovaj gavran, pa da sakrijem leš brata svog?", te postade od pokajanika.'),
(701, 5, 32, 'Zbog tog smo propisali sinovima Israilovim, da onaj ko ubije dušu - sem zbog ubojstva ili fesada na Zemlji - pa kao da je pobio sve ljude. A ko je poživi, pa kao da je poživio ljude sve. A doista su im donosili poslanici Naši dokaze jasne, zatim su uistinu mnogi od njih, poslije toga, na Zemlji bili prekršitelji.'),
(702, 5, 33, 'Plaća onih koji ratuju protiv Allaha i Poslanika Njegovog i žure na Zemlji fesadom je jedino da budu ubijeni ili raspeti ili (im) se odsijeku ruke njihove i noge njihove unakrsno ili da se prognaju iz zemlje. To im je poniženje na Dunjau, a imaće oni na Ahiretu kaznu strahovitu,'),
(703, 5, 34, 'Izuzev onih koji se pokaju prije no što njima ovladate. Pa znajte da je Allah Oprosnik, Milosrdni.'),
(704, 5, 35, 'O vi koji vjerujete! Bojte se Allaha i tražite Njemu približenje i borite se na putu Njegovom, da biste vi uspjeli.'),
(705, 5, 36, 'Uistinu, oni koji ne vjeruju, da je njihovo što je na Zemlji i slično tome uz to, da se iskupe njime od kazne Dana kijameta, ne bi se primilo od njih, a imaće oni kaznu bolnu.'),
(706, 5, 37, 'Želiće da izađu iz vatre, a neće oni biti ti koji će iz nje izaći, i imaće oni kaznu trajnu.'),
(707, 5, 38, 'A kradljivcu i kradljivici - pa odsijecite ruke njihove. Plaća za ono šta su počinili - primjerena od Allaha. A Allah je Moćni, Mudri.'),
(708, 5, 39, 'Pa ko se pokaje nakon zulma svog i popravi - pa uistinu, Allah će mu oprostiti. Uistinu! Allah je Oprosnik, Milosrdni.'),
(709, 5, 40, 'Zar ne znaš da Allah - Njegova je vlast nebesa i Zemlje - kažnjava koga hoće, a oprašta kome hoće? A Allah nad svakom stvari ima moć.'),
(710, 5, 41, 'O Poslaniče! Nek te ne žaloste oni koji žure u nevjerovanje između onih koji kažu: "Vjerujemo!" ustima svojim, a ne vjeruju srca njihova; i od onih koji su jevreji, slušatelji laži, slušatelji naroda drugih (koji) nisu tebi došli. Iskrivljuju riječi na mjestima njihovim, govore: "Ako vam je dato ovo, onda ga uzmite, a ako vam to nije dato, onda se čuvajte." A kome Allah želi kušnju njegovu - pa nećeš mu moći (pomoći) od Allaha ništa. Takvi su oni kojima Allah ne želi da očisti srca njihova. Imaće oni na Dunjau poniženje i imaće oni na Ahiretu kaznu užasnu.'),
(711, 5, 42, 'Slušatelji laži, jedači nedozvoljenog! Pa ako ti dođu, tad među njima presudi, ili se od njih okreni. A ako se okreneš od njih, pa neće te oštetiti nimalo. A ako budeš sudio, onda presudi među njima s Istinom. Uistinu! Allah voli pravedne.'),
(712, 5, 43, 'A kako će tebe uzeti sucem, a kod njih je Tewrat, u njemu sud Allahov? Zatim se okreću poslije toga, a nisu takvi vjernici.'),
(713, 5, 44, 'Uistinu! Mi smo objavili Tewrat u kojem je uputa i svjetlost, sudili su njime vjerovjesnici koji su se predali (Allahu), onima koji su jevreji; i pobožni i učeni, onim šta su sačuvali od Knjige Allahove, a bili su nad njom svjedoci. Zato se ne plašite ljudi, a plašite se Mene. I ne prodajite ajete Moje za vrijednost malu. A ko ne sudi onim šta je Allah objavio - pa ti takvi su nevjernici.'),
(714, 5, 45, 'I propisali smo im u njoj da je duša za dušu, i oko za oko, i nos za nos, i uho za uho, i zub za zub, i (za) rane je odmazda. Pa ko to učini sadakom, onda je to za njega iskup. A ko ne sudi onim šta je objavio Allah: pa ti takvi su zalimi.'),
(715, 5, 46, 'I poslali smo tragovima njihovim Isaa, sina Merjemina, potvrđivačem za ono ispred njega od Tewrata. I dali smo mu Indžil, u njemu je uputa i svjetlost i potvrda za ono prije njega od Tewrata, i uputa i pouka bogobojaznima.'),
(716, 5, 47, 'I neka sljedbenici Indžila sude onim šta je Allah u njemu objavio. A ko ne sudi onim šta je objavio Allah - pa ti takvi su grješnici.'),
(717, 5, 48, 'A objavili smo tebi Knjigu s Istinom, potvrdom za ono prije nje od Knjige, i čuvarom nad tim. Zato sudi među njima onim šta je objavio Allah i ne slijedi strasti njihove umjesto onog šta ti je došlo od Istine. Svakom od vas smo načinili zakon i put. A da je htio Allah, sigurno bi vas učinio ummom jednom. Međutim, (hoće) da vas iskuša u onom šta vam je dao. Zato se natječite u dobrim djelima. Allahu je povratak svih vas, pa će vas obavijestiti o onom u čemu ste se razilazili.'),
(718, 5, 49, 'I da sudiš među njima onim šta je objavio Allah, i ne slijedi strasti njihove, i čuvaj ih se da te ne zavedu od dijela onog šta ti je Allah objavio. Pa ako se okrenu, tad znaj da Allah želi da ih pogodi zbog izvjesnih grijeha njihovih. A uistinu, mnogi od ljudi su grješnici.'),
(719, 5, 50, 'Pa zar sud paganstva traže? A ko je bolji od Allaha sudom, za ljude koji su uvjereni?'),
(720, 5, 51, 'O vi koji vjerujete! Ne uzimajte prijateljima jevreje i kršćane. Oni su prijatelji jedni drugima. A ko se od vas njima okrene, pa on je uistinu od njih. Uistinu! Allah ne upućuje narod zalima.'),
(721, 5, 52, 'Zato vidiš one u srcima čijim je bolest, žure među njih, govore: "Bojimo se da nas ne pogodi kolo (nesreće)." Pa možda će Allah da da pobjedu ili odredbu od Sebe, pa će postati zbog onog šta kriju u dušama svojim, pokajanici.'),
(722, 5, 53, 'I reći će oni koji vjeruju: "Jesu li oni ti koji su se kleli Allahom, najvećom od zakletvi njihovih, da su oni uistinu uz vas?" Uništena su djela njihova, pa su postali gubitnici.'),
(723, 5, 54, 'O vi koji vjerujete! Ko od vas otpane od Dina svog, pa dovešće Allah narod kojeg će voljeti i (koji) će voljeti Njega, blag će biti prema vjernicima, silan prema nevjernicima, boriće se na putu Allahovom i neće se bojati prigovora prigovarača. To je blagodat Allahova, daje ju kome hoće. A Allah je Sveobuhvatni, Znalac.'),
(724, 5, 55, 'Jedino je prijatelj vaš Allah i Poslanik Njegov i oni koji vjeruju, koji obavljaju salat i daju zekat, a oni su ti koji čine ruku\'.'),
(725, 5, 56, 'A ko uzme prijateljem Allaha i Poslanika Njegovog i one koji vjeruju - pa uistinu, stranka Allahova, oni će biti pobjednici.'),
(726, 5, 57, 'O vi koji vjerujete! Ne uzimajte one koji uzimaju vjeru vašu ruglom i zabavom - od onih kojima je data Knjiga prije vas i nevjernike - prijateljima. I bojte se Allaha, ako ste vjernici.'),
(727, 5, 58, 'A kad pozivate salatu, uzimaju ga ruglom i zabavom. To je zato što su oni ljudi koji ne shvataju.'),
(728, 5, 59, 'Reci: "O sljedbenici Knjige! Da li nam se svetite samo što vjerujemo u Allaha i ono što nam je objavljeno i što je objavljeno prije, i što je većina vas grješnika?"'),
(729, 5, 60, 'Reci: "Hoćete li da vas obavijestim o goroj nagradi od ovog, kod Allaha? Onaj koga je prokleo Allah i rasrdio se na njega, i učinio od njih majmune i svinje, i (onom ko) je obožavao taguta. Takvi su goreg mjesta i više su odlutali s pravog puta."'),
(730, 5, 61, 'A kad vam dođu, kažu: "Vjerujemo!", a zaista su ušli s nevjerstvom, i oni su doista izašli s njim; a Allah je Najbolji znalac onog šta kriju.'),
(731, 5, 62, 'I vidiš mnoge od njih, žure u grijeh i neprijateljstvo i njihovo jedenje nedozvoljenog. Doista je loše šta rade.'),
(732, 5, 63, 'Zašto im ne zabrane rabini i svećenici njihov govor griješni i njihovo jedenje nedozvoljenog? Doista je loše šta čine.'),
(733, 5, 64, 'I govore jevreji: "Ruka Allahova je vezana." Sapete neka su ruke njihove i prokleti neka su zbog onog šta govore. Naprotiv, ruke Njegove su raširene, opskrbljuje kako hoće. I sigurno će ono šta ti je objavljeno od Gospodara tvog povećati mnogima od njih pretjerivanje i nevjerovanje. A ubacili smo među njih neprijateljstvo i mržnju do Dana kijameta. Kad god potpale vatru za rat, ugasi je Allah. I trude se na Zamlji fesadom; a Allah ne voli mufside.'),
(734, 5, 65, 'A da sljedbenici Knjige vjeruju i boje se (Allaha), sigurno bismo im pokrili loša djela njihova i sigurno bismo ih uveli u bašče uživanja.'),
(735, 5, 66, 'A da oni stoje uz Tewrat i Indžil i ono šta im je objavljeno od Gospodara njihovog, sigurno bi jeli iznad sebe i ispod nogu svojih. (Postoji) među njima umma srednja, a (od) mnoštva njih je zlo šta rade.'),
(736, 5, 67, 'O Poslaniče! Dostavi šta ti je objavljeno od Gospodara tvog. A ako ne učiniš, onda nisi prenio poslanicu Njegovu; a Allah štiti tebe od ljudi. Uistinu! Allah ne upućuje narod nevjernika.'),
(737, 5, 68, 'Reci: "O sljedbenici Knjige! Niste ni na čemu dok se ne budete držali Tewrata i Indžila, i onog šta vam je objavljeno od Gospodara vašeg." A sigurno će ono šta ti je objavljeno od Gospodara tvog povećati mnogima od njih pretjerivanje i nevjerovanje. Zato ne očajavaj nad narodom nevjernika.'),
(738, 5, 69, 'Uistinu, oni koji vjeruju, i oni koji su jevreji i Sabijci i kršćani - ko vjeruje u Allaha i Dan posljednji i čini dobro - pa neće biti straha nad njima i neće oni žaliti.'),
(739, 5, 70, 'Doista smo uzeli zavjet sinova Israilovih i slali im poslanike. Kad god im je došao poslanik sa onim što ne vole duše njihove, dio (njih) bi porekli, a dio pobili.'),
(740, 5, 71, 'A računali su da neće biti iskušenja, pa su se oglušili i zaslijepili. Zatim im je Allah oprostio, potom su se (opet) oglušili i zaslijepili mnogi od njih; a Allah je onog šta rade Vidilac.'),
(741, 5, 72, 'Doista ne vjeruju oni koji govore: "Uistinu! Allah - On je Mesih, sin Merjemin." A rekao je Mesih: "O sinovi Israilovi! Obožavajte Allaha, Gospodara mog i Gospodara vašeg. Uistinu, onaj ko pridruži Allahu - pa doista će mu Allah zabraniti Džennet, a sklonište njegovo biće vatra", i neće biti za zalime nikakvih pomagača.'),
(742, 5, 73, 'Doista ne vjeruju oni koji govore: "Uistinu! Allah je treći od trojice"; a nema nikakva boga, izuzev Boga Jedinog. I ako ne prestanu s onim šta govore, sigurno će dotaći one od njih koji ne vjeruju, kazna bolna.'),
(743, 5, 74, 'Pa zar se neće pokajati Allahu i tražiti oprost od Njega? A Allah je Oprosnik, Milosrdni.'),
(744, 5, 75, 'Mesih, sin Merjemin, bio je samo poslanik. Već su prošli prije njega poslanici, a majka njegova je bila iskrena. Jeli su oboje hranu. Pogledaj kako im objašnjavamo znakove, potom pogledaj kako se okreću!'),
(745, 5, 76, 'Reci: "Obožavate li mimo Allaha ono što ne vlada vašom štetom, niti korišću? A Allah - On je Onaj koji čuje, Znalac."'),
(746, 5, 77, 'Reci: "O sljedbenici Knjige! Ne pretjerujte u vjeri svojoj mimo Istine, i ne slijedite strasti ljudi koji su već zabludili ranije i zaveli mnoge, i odlutali s pravog puta."'),
(747, 5, 78, 'Prokleti su oni koji ne vjeruju od sinova Israilovih, jezikom Dawuda i Isaa, sina Merjemina. To zato što nisu poslušali i (što) su prevršivali.'),
(748, 5, 79, 'Nisu zabranjivali ružno: činili su ga! Zaista je loše šta su činili!'),
(749, 5, 80, 'Vidiš mnoge od njih prijateljuju s onima koji ne vjeruju. Doista je loše šta su im unaprijed poslale duše njihove: da se rasrdi Allah na njih, a u kazni će oni biti vječito.'),
(750, 5, 81, 'A da vjeruju u Allaha i Vjerovjesnika i ono šta mu je objavljeno, ne bi ih uzimali prijateljima. Međutim, mnogi od njih su grješnici.'),
(751, 5, 82, 'Sigurno ćeš naći ljude najžešće neprijateljstvom prema onima koji vjeruju: jevreje i one koji pridružuju. A sigurno ćeš naći najbliže od njih ljubavlju (prema) onima koji vjeruju - one koji govore: "Uistinu, mi smo kršćani." To zato što među njima ima svećenika i monaha i što se oni ne ohole.'),
(752, 5, 83, 'I kad slušaju ono šta je objavljeno Poslaniku, vidiš oči njihove oblivene suzama zbog onog šta su spoznali od Istine, govore: "Gospodaru naš! Vjerujemo, pa upiši nas sa svjedocima.'),
(753, 5, 84, 'A šta mi imamo (razlogom da) ne vjerujemo u Allaha i ono što nam je došlo od Istine, a žudimo da nas uvede Gospodar naš sa narodom dobrih."'),
(754, 5, 85, 'Zato će ih nagraditi Allah za ono što govore: baščama ispod kojih teku rijeke, vječno će biti u njima; a to je plaća dobročiniteljima.'),
(755, 5, 86, 'A oni koji ne vjeruju i poriču ajete Naše, takvi će biti stanovnici džehima.'),
(756, 5, 87, 'O vi koji vjerujete! Ne zabranjujte dobre stvari koje vam je dozvolio Allah i ne pretjerujte. Uistinu! Allah ne voli one koji pretjeruju.'),
(757, 5, 88, 'I jedite od onog čim vas je opskrbio Allah, dozvoljeno, dobro, i bojte se Allaha - Onog u kojeg ste vi vjernici.'),
(758, 5, 89, 'Neće vas kazniti Allah zbog nehotičnog u zakletvama vašim, međutim, kazniće vas za ono čim ste se obavezali zakletvama. Pa iskup je hranjenje deset siromaha iz osrednjeg čim hranite porodicu svoju ili odijevanje njihovo ili oslobođenje roba. Pa ko ne nađe, onda je post tri dana. To je iskup zakletvi vaših kad se zakunete; i čuvajte zakletve svoje. Tako vam Allah objašnjava znakove Svoje, da biste vi zahvaljivali.'),
(759, 5, 90, 'O vi koji vjerujete! Zaista je alkohol i kocka i kipovi i strelice (za gatanje), prljavština iz posla šejtanovog. Zato se toga klonite, da biste vi uspjeli.'),
(760, 5, 91, 'Šejtan samo želi da izazove među vama neprijateljstvo i mržnju alkoholom i kockom, i odvrati vas od sjećanja Allaha i od salata. Pa hoćete li vi biti ti koji će prestati?'),
(761, 5, 92, 'I poslušajte Allaha i poslušajte Poslanika, i čuvajte se! Pa ako se okrenete - onda znajte da je jedino na Poslaniku Našem dostava jasna.'),
(762, 5, 93, 'Nije grijeh onima koji vjeruju i rade dobra djela u onom čim su se hranili, kad se boje (Allaha) i vjeruju i čine dobra djela, zatim se boje i vjeruju, zatim: boje se i čine dobro; a Allah voli dobročinitelje.'),
(763, 5, 94, 'O vi koji vjerujete! Sigurno će vas iskušati Allah nečim od divljači koju će doseći ruke vaše i koplja vaša, da zna Allah ko Ga se boji u nevidljivosti. Pa ko prevrši poslije toga, pa imaće on kaznu bolnu.'),
(764, 5, 95, 'O vi koji vjerujete! Ne ubijajte divljač, a budete li vi posvećeni. A ko je od vas ubije namjerno, onda je nadoknada slična onom šta je ubio - od stoke. Presudiće to dvojica pravednih između vas kurbanom prinesenim Ka\'bi; ili, iskup je hranjenje siromaha ili ravno tome post, da okuša posljedice odluke svoje. Oprostio je Allah ono šta je prošlo. A ko ponovi, pa osvetiće mu se Allah; a Allah je Moćni, Vlasnik osvete!'),
(765, 5, 96, 'Dozvoljen vam je lov morem i hrana njegova - uživanje za vas i za putnike - a zabranjen vam je lov kopnom, dokle budete posvećeni. I bojte se Allaha, Onog kojem ćete biti sabrani.'),
(766, 5, 97, 'Allah je učinio Ka\'bu Kućom svetom, kijamom za ljude, i mjesec sveti i kurban, i ogrlice (njihove). To zato da znate da Allah zna šta je u nebesima i šta je na Zemlji, i da je Allah o svakoj stvari Znalac.'),
(767, 5, 98, 'Znajte da je Allah žestok kaznom, i da je Allah Oprosnik, Milosrdni.'),
(768, 5, 99, 'Na Poslaniku je jedino dostava, a Allah zna šta objelodanjujete i šta krijete.'),
(769, 5, 100, 'Reci: "Nije isto loše i dobro, makar te čudilo mnoštvo lošeg. Zato se bojte Allaha, o posjednici razuma, da biste vi uspjeli."'),
(770, 5, 101, 'O vi koji vjerujete! Ne pitajte za stvari (koje) će vam, ako vam se obznane, smetati; a ako upitate o njima dok se objavljuje Kur\'an, obznaniće vam se. Allah to oprašta, a Allah je Oprosnik, Blagi.'),
(771, 5, 102, 'Već su to pitali ljudi prije vas, potom su u njih postali nevjernici.'),
(772, 5, 103, 'Nije uspostavio Allah nikakvu bahiru, ni saibu, niti wasilu, a ni hamu. Međutim, oni koji ne vjeruju izmišljaju protiv Allaha laž, a većina njih ne shvata.'),
(773, 5, 104, 'A kad im se kaže: "Dođite onom šta objavljuje Allah i Poslaniku", govore: "Dosta nam je ono na čemu smo našli očeve naše." Zar? Iako očevi njihovi nisu znali ništa, niti su bili upućeni.'),
(774, 5, 105, 'O vi koji vjerujete! Na vama su duše vaše. Neće vam štetiti onaj ko je zabludio, kad ste upućeni. Allahu je povratak vas svih, pa će vas obavijestiti o onom šta ste radili.'),
(775, 5, 106, 'O vi koji vjerujete! Svjedočenje (neka bude) između vas, kad se nekom od vas približi smrt, za vrijeme oporuke: dvojica pravednih između vas ili druga dvojica ne od vas - ako vi budete putovali po Zemlji, te vas pogodi nesreća smrti: Zadržaćete njih dvojicu nakon salata, ako sumnjate, pa će se zakleti Allahom: "Nećemo kupiti time vrijednost, makar bio bližnji, i ne krijemo svjedočenje Allahu, uistinu, mi bismo tada bili od grješnika."'),
(776, 5, 107, 'Pa ako se sazna da su njih dvojica zaslužila grijeh, onda nek druga dvojica stanu na mjesto njihovo, od onih koji zaslužuju - najbliži, pa neka se zakunu Allahom: "Sigurno je naše svjedočenje istinitije od svjedočenja njih dvojice, i nećemo prekoračiti, uistinu, mi bismo tad bili od zalima."'),
(777, 5, 108, 'To je najpodesnije da daju činjenično svjedočenje ili (neka) se boje da će biti ponovljene zakletve, nakon zakletvi njihovih. I bojte se Allaha i slušajte. A Allah ne upućuje narod grješnika.'),
(778, 5, 109, 'Na Dan kad Allah sakupi poslanike, pa rekne: "Šta vam je odgovoreno?" Reći će: "Mi nemamo znanje. Uistinu, Ti, Ti si Znalac nevidljivih."'),
(779, 5, 110, 'Kad Allah rekne: "O Isa sine Merjemin! Sjeti se blagodati Moje prema tebi i majci tvojoj: kad sam te ojačao Ruhul-kudusom, govorio si s ljudima u bešici i sredovječan; i kad sam te poučio Knjizi i mudrosti i Tewratu i Indžilu; i kad si napravio od gline (nešto) kao lik ptice s dozvolom Mojom, pa si puhnuo u nju, te je bila ptica s dozvolom Mojom; i izliječio slijepca i gubavca s dozvolom Mojom; i kad si dizao mrtve s dozvolom Mojom; i kad sam odvratio od tebe sinove Israilove, kad si im došao sa dokazima jasnim, pa su rekli oni od njih koji nisu vjerovali: "Ovo je samo sihr očiti,"'),
(780, 5, 111, 'I kad sam objavio havarijjunima: "Vjerujte u Mene i poslanika Mog", rekli su: "Vjerujemo i posvjedoči da smo mi muslimani."'),
(781, 5, 112, 'Kad rekoše hawarijjuni: "O Isa, sine Merjemin! Da li može Gospodar tvoj da nam spusti trpezu s neba?" Reče: "Bojte se Allaha, ako ste vjernici."'),
(782, 5, 113, 'Rekoše: "Mi želimo da jedemo s nje i da se smire srca naša, i da znamo da si nam doista Istinu govorio, i da budemo nad njom svjedoci."'),
(783, 5, 114, 'Isa, sin Merjemin reče: "O Allahu, Gospodaru naš! Spusti nam trpezu s neba, biće za nas blagdan, za prvog između nas i zadnjeg od nas, i znak od Tebe. I opskrbi nas, a Ti si Najbolji od opskrbljivača."'),
(784, 5, 115, 'Allah reče: "Uistinu! Ja sam Taj koji će vam je spustiti, pa ko od vas ne bude vjerovao poslije - pa uistinu, Ja ću ga kazniti kaznom kakvom nisam kaznio nikog od svjetova!"'),
(785, 5, 116, 'I kad Allah rekne: "O Isa, sine Merjemin! Jesi li ti govorio ljudima: \'Uzmite mene i majku moju (kao) dva boga mimo Allaha?" Reći će: "Slava neka je Tebi! Nije moje da ja kažem šta pravo nemam. Ako sam to rekao, pa doista to znaš. Znaš šta je u duši mojoj, a ne znam šta je u duši Tvojoj. Uistinu! Ti, Ti si Znalac nevidljivih.'),
(786, 5, 117, 'Nisam im govorio, izuzev ono šta si mi naredio: "Obožavajte Allaha, Gospodara mog i Gospodara vašeg." I bio sam nad njima svjedok dok sam bio među njima. Pa pošto si mi smrt dao, bio si Ti Taj koji ih je nadzirao, a Ti si nad svakom stvari Svjedok.'),
(787, 5, 118, 'Ako ih kazniš - pa uistinu, oni su robovi Tvoji; a ako im oprostiš - pa uistinu, Ti, Ti si Moćni, Mudri."'),
(788, 5, 119, 'Allah će reći: "Ovo je Dan (kad) će koristiti istinitima istinitost njihova. Imaće oni bašče ispod kojih teku rijeke, vječno će biti u njima, zauvijek"; zadovoljan će Allah biti njima i zadovoljni (oni) Njime. To je postignuće veličanstveno!'),
(789, 5, 120, 'Allahova je vlast nebesa i Zemlje i šta je u njima; a On nad svakom stvari ima moć.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Hvala Allahu koji je stvorio nebesa i Zemlju i načinio tmine i svjetlost, (pa) opet oni koji ne vjeruju, Gospodaru svom izjednačuju (ortake).'),
(791, 6, 2, 'On je Taj koji vas je stvorio od ilovače, zatim odredio rok. A rok određeni je kod Njega. Opet vi sumnjate!'),
(792, 6, 3, 'A On je Allah, u nebesima i na Zemlji. Zna tajno vaše i javno vaše, i zna šta stičete.'),
(793, 6, 4, 'I nije im došao nijedan znak od znakova Gospodara njihovog, a da nisu bili od njega odvraćeni.'),
(794, 6, 5, 'Pa doista su porekli Istinu pošto im je došla. Pa doći će im vijesti o onom čemu su se rugali.'),
(795, 6, 6, 'Zar nisu vidjeli koliko smo uništili naraštaja prije njih, (koje) smo učvrstili na Zemlji onako kako nismo učvrstili vas? A slali smo nebo na njih obilno i učinili da rijeke teku ispod njih, pa smo ih uništili zbog grijeha njihovih, i stvorili smo poslije njih druga pokoljenja.'),
(796, 6, 7, 'A da smo ti spustili Knjigu na papiru, pa da je opipaju rukama svojim, sigurno bi rekli oni koji ne vjeruju: "Ovo je samo sihr očiti."'),
(797, 6, 8, 'I govore: "Zašto mu se ne spusti melek?" A da spustimo meleka, sigurno bi bila svršena stvar, zatim im se ne bi odložilo.'),
(798, 6, 9, 'A da smo ga učinili melekom, sigurno bismo ga učinili čovjekom i sigurno bismo im (konfuzijom) zbrkali ono šta brkaju.'),
(799, 6, 10, 'A doista su ismijavani poslanici prije tebe, pa je snašlo one od onih koji su se rugali, ono čemu su se rugali.'),
(800, 6, 11, 'Reci: "Putujte po Zemlji, zatim pogledajte kakav je bio kraj poricatelja."'),
(801, 6, 12, 'Reci: "Čije je ono šta je u nebesima i Zemlji?" Reci: "Allahovo!" Propisao je Sebi milost. Sigurno će vas sakupiti za Dan kijameta u koji nema sumnje. Oni koji gube duše svoje - ta oni neće vjerovati.'),
(802, 6, 13, 'I Njegovo je šta miruje u noći i danu, a On je Onaj koji čuje, Znalac.'),
(803, 6, 14, 'Reci: "Zar da mimo Allaha uzmem zaštitnika - Stvoritelja nebesa i Zemlje?", a On hrani, a nije hranjen. Reci: "Uistinu, meni je naređeno da budem prvi koji će se predati", i ne budi nikako od mušrika.'),
(804, 6, 15, 'Reci: "Uistinu, ja se bojim - ako ne poslušam Gospodara svog - kazne Dana velikog."'),
(805, 6, 16, 'Ko bude od nje otklonjen Tog dana, tad mu se (Allah) doista smilovao; a to je uspjeh očiti.'),
(806, 6, 17, 'A ako te dotakne Allah nesrećom, onda nema od nje spasitelja, izuzev Njega; a ako te dotakne dobrim - pa On nad svakom stvari ima moć.'),
(807, 6, 18, 'I On je Pokoritelj nad robovima Svojim, a On je Mudri, Obaviješteni.'),
(808, 6, 19, 'Reci: "Koja je stvar najveća svjedočenjem?" Reci: "Allah. Svjedok je između mene i između vas; i objavljuje mi se ovaj Kur\'an da upozorim njime vas i onog kome stigne. Da li vi uistinu svjedočite da su sa Allahom bogovi drugi?" Reci: "Ne svjedočim!" Reci: "Samo je On Bog, Jedini! A uistinu, ja sam čist od onog šta pridružujete."'),
(809, 6, 20, 'Oni kojima smo dali Knjigu, prepoznaju ga kao što prepoznaju sinove svoje. Oni koji su izgubili duše svoje, ta oni ne vjeruju.'),
(810, 6, 21, 'A ko je nepravedniji od onog ko izmišlja o Allahu laž ili poriče ajete Njegove? Uistinu, zalimi neće uspjeti.'),
(811, 6, 22, 'A na Dan kad ih sve skupimo, zatim reknemo onima koji su pridruživali: "Gdje su ortaci vaši - oni za koje ste tvrdili?"'),
(812, 6, 23, 'Zatim neće biti fitneha njihovog, jedino će reći: "Tako nam Allaha, Gospodara našeg, nismo bili mušrici."'),
(813, 6, 24, 'Vidi kako će lagati protiv duša svojih, a iščeznuće od njih šta su izmišljali.'),
(814, 6, 25, 'I od njih je ko te sluša; a načinili smo na srcima njihovim velove da ga ne razumiju, a u ušima njihovim gluhoću. Iako vide svaki znak, ne vjeruju u njega; dok, kad ti dođu prepiru se s tobom, govore oni koji ne vjeruju: "Ovo su samo priče ranijih."'),
(815, 6, 26, 'A oni ga zabranjuju i udaljuju se od njega; i samo uništavaju duše svoje, a ne opažaju.'),
(816, 6, 27, 'A da vidiš kad budu zaustavljeni nad vatrom, pa reknu: "O da nam je da budemo povraćeni i da ne poreknemo ajete Gospodara našeg, i budemo od vjernika!"'),
(817, 6, 28, 'Naprotiv, ukazaće im se šta su skrivali ranije. I ako bi bili povraćeni, sigurno bi se vratili onom šta im je zabranjeno. A uistinu, oni su lašci.'),
(818, 6, 29, 'I govore: "Nema ništa sem života našeg dunjalučkog i mi nećemo biti podignuti (nakon smrti)!"'),
(819, 6, 30, 'A da vidiš kad budu zaustavljeni pred Gospodarom svojim! (On) će reći: "Zar ovo nije Istina?" Reći će: "Svakako, tako nam Gospodara našeg." Reći će: "Pa kušajte kaznu, zato što niste vjerovali!"'),
(820, 6, 31, 'Doista će izgubiti oni koji poriču susret s Allahom, dok kad im dođe Čas nenadno, reći će: "O žalosti naša nad onim šta smo zanemarili", i oni će nositi terete svoje na leđima svojim. Doista je loše šta će nositi!'),
(821, 6, 32, 'A život Dunjaa je samo igra i zabava, i sigurno je kuća Ahireta bolja za one koji se boje. Pa zar ne shvatate?'),
(822, 6, 33, 'Doista znamo: žalosti te to šta govore. Pa uistinu, oni ne poriču tebe, međutim zalimi ajete Allahove niječu.'),
(823, 6, 34, 'A doista su poricani poslanici prije tebe, pa su se strpili nad (tim) što su poricani i mučeni, dok im nije došla pomoć Naša. I nema tog ko će izmijeniti riječi Allahove. A već ti je došla vijest o poslanicima.'),
(824, 6, 35, 'A ako ti je teško njihovo okretanje, onda ako možeš da potražiš rupu u Zemlji ili ljestve u nebo, pa im doneseš znak! A da hoće Allah, sigurno bi ih prikupio Uputi, zato ne budi nikako od neznalica.'),
(825, 6, 36, 'Odazivaju se jedino oni koji slušaju. A mrtvi - podići će ih Allah, zatim će Njemu biti povraćeni.'),
(826, 6, 37, 'I govore: "Zašto mu se ne spusti znak od Gospodara njegovog?" Reci: "Uistinu! Allah je kadar da spusti znak"; međutim, većina njih ne zna.'),
(827, 6, 38, 'A nema nijedne životinje na Zemlji, niti letećeg stvorenja (koje) leti sa dva krila svoja - a da nisu zajednice slične vama. Nismo zapostavili u Knjizi ništa; zatim će Gospodaru svom biti sakupljeni.'),
(828, 6, 39, 'A oni koji poriču ajete Naše, gluhi i nijemi, u tminama su. Allah koga hoće - zabludi ga, a koga hoće - učini ga na putu pravom.'),
(829, 6, 40, 'Reci: "Da li vidite, ako vam dođe kazna Allahova ili vam dođe Čas, hoćete li mimo Allaha prizivati, ako istinu govorite?"'),
(830, 6, 41, 'Naprotiv! Njega biste prizivali, pa bi otklonio ono za što ste Ga prizivali - ako bi htio - i zaboravili biste šta ste pridruživali.'),
(831, 6, 42, 'A doista, slali smo zajednicama prije tebe, pa smo ih dohvatili bijedom i nesrećom, da bi se oni ponizili.'),
(832, 6, 43, 'Pa zašto se nisu, kad im je došla sila Naša, ponizili? Međutim, otvrdla su srca njihova i uljepšao im je šejtan ono šta su radili!'),
(833, 6, 44, 'Pa pošto bi zaboravili ono čim su bili opominjani, otvorili bismo im kapije svake stvari. Dok, kad bi se obradovali zbog onog šta im je dato, ščepali bismo ih nenadno, tad gle! - oni očajnici.'),
(834, 6, 45, 'Zato je sasječen korjen ljudi koji su činili zulm; i hvala neka je Allahu, Gospodaru svjetova!'),
(835, 6, 46, 'Reci: "Da li vi vidite, ako Allah oduzme sluh vaš i vidove vaše, i zapečati srca vaša, koji bog mimo Allaha bi vam to dao?" Pogledaj kako iznosimo znakove! Opet se oni odvraćaju.'),
(836, 6, 47, 'Reci: "Da li vi vidite, ako vam dođe kazna Allahova iznenada ili otvoreno, hoće li biti uništen iko osim naroda zalima?"'),
(837, 6, 48, 'I šaljemo izaslanike samo kao donosioce radosnih vijesti i opominjače. Pa ko vjeruje i čini dobro, tad nema straha nad njima i neće oni tugovati.'),
(838, 6, 49, 'A oni koji poriču ajete Naše - pogodiće ih kazna zbog onog šta su zgriješili.'),
(839, 6, 50, 'Reci: "Ne kažem vam: \'Kod mene su riznice Allahove\', niti \'Znam nevidljivo\', i ne kažem vam: \'Uistinu, ja sam melek." Slijedim samo šta mi se objavljuje." Reci: "Je li isto: slijepac i ko vidi? Pa zar nećete razmisliti?"'),
(840, 6, 51, 'I opominji njime one koji se plaše da će biti Gospodaru svom sakupljeni - neće imati oni mimo Njega zaštitnika niti zagovornika - da bi se oni bojali (Allaha).'),
(841, 6, 52, 'I ne tjeraj one koji prizivaju Gospodara svog jutrom i navečer, želeći lice Njegovo. Nije na tebi od računa njihovog ništa i nije od računa tvog na njima ništa, pa da ih otjeraš i budeš od zalima.'),
(842, 6, 53, 'A tako ih iskušavamo jedne drugima, da bi rekli: "Jesu li oni ti koje je obdario Allah između nas?" Zar nije Allah Najbolji poznavalac zahvalnih?'),
(843, 6, 54, 'A kad ti dođu oni koji vjeruju u ajete Naše, tad reci: "Selamun \'alejkum!" Propisao je Sebi Gospodar vaš milost, da onaj od vas ko uradi zlo iz neznanja, zatim se pokaje poslije toga i popravi - da je On tad Oprosnik, Milosrdni.'),
(844, 6, 55, 'I tako razlažemo znakove, da bi se razjasnio put prestupnika.'),
(845, 6, 56, 'Reci: "Meni je uistinu zabranjeno da obožavam one koje prizivate mimo Allaha." Reci: "Neću slijediti strasti vaše. Doista bih tad zalutao i ne bih ja bio od upućenih."'),
(846, 6, 57, 'Reci: "Uistinu, ja sam na dokazu jasnom od Gospodara svog, a poričete ga. Nije kod mene to što požurujete. Sud je jedino Allahov; govori Istinu, a On je Najbolji od sudaca."'),
(847, 6, 58, 'Reci: "Da je kod mene to što požurujete, sigurno bi bila svršena stvar između mene i između vas;" a Allah je Najbolji znalac zalima.'),
(848, 6, 59, 'A kod Njega su ključevi nevidljivog - zna ih samo On; i zna šta je na kopnu i moru; i ne otpadne nijedan list, a da ga ne zna. I nema zrnca u tminama Zemlje, ni svježeg ni suhog, a da nije u Knjizi jasnoj.'),
(849, 6, 60, 'I On je Taj koji vas uzima noću - a zna šta privrijedite danju, zatim vas podiže u njemu, da bi se ispunio rok određeni. Zatim je Njemu povratak vaš, potom će vas obavijestiti o onom šta ste radili;'),
(850, 6, 61, 'I On je Pokoritelj nad robovima Svojim; i šalje nad vama čuvare. Dok, kad dođe nekom od vas smrt, uzmu ga izaslanici Naši, a oni ne oklijevaju.'),
(851, 6, 62, 'Zatim će biti vraćeni Allahu, Zaštitniku svom istinskom. Sud je uistinu Njegov!, a On je Najbrži od onih koji obračunavaju.'),
(852, 6, 63, 'Reci: "Ko vas izbavlja iz tmina kopna i mora? Zovete Ga ponizno i tajno: \'Ako nas izbaviš iz ovog, sigurno ćemo biti od zahvalnih."'),
(853, 6, 64, 'Reci: "Allah vas izbavlja iz njih i iz svake nevolje, potom vi pridružujete."'),
(854, 6, 65, 'Reci: "On je kadar da pošalje na vas kaznu iznad vas ili ispod nogu vaših ili vas zaodjene u stranke i (da) iskusite silu jedni drugih." Pogledaj kako iznosimo znakove, da bi oni razumjeli.'),
(855, 6, 66, 'I poriče ga narod tvoj, a on je Istina. Reci: "Nisam nad vama čuvar."'),
(856, 6, 67, 'Za svaku vijest ima rok, a saznaćete.'),
(857, 6, 68, 'A kad vidiš one koji se upuštaju (jalovim raspravama) u ajete Naše, tad se okreni od njih dok ne budu prešli na razgovor drugačiji od tog. A ako te navede šejtan da zaboraviš, tad ne sjedi - nakon prisjećanja - sa narodom zalima.'),
(858, 6, 69, 'I nema ništa od njihovog računa na onima koji se boje, međutim, Opomena je da bi se oni pobojali.'),
(859, 6, 70, 'I ostavi one koji vjeru svoju uzimaju igrom i zabavom, a obmanuo ih je život Dunjaa. I opominji njime, da ne strada duša zbog onog šta zasluži. Neće za nju mimo Allaha biti zaštitnika niti zagovornika. Iako bi dala svaku otkupninu, ne bi se uzela od nje. Takvi su oni koji stradaju zbog onog šta zasluže. Imaće oni piće od ključale vode i kaznu bolnu, zato što ne vjeruju.'),
(860, 6, 71, 'Reci: "Zar da prizivamo mimo Allaha ono šta nam ne koristi, niti nam šteti, i okrenemo se na petama našim, nakon što nas je uputio Allah, kao onaj koga opčine šejtani na Zemlji smetenog. Njegovi drugovi ga pozivaju Uputi: \'Hodi nama!" Reci: "Uistinu, Uputa Allahova, to je Uputa, a naređeno nam je da se predamo Gospodaru svjetova,'),
(861, 6, 72, 'I da obavljamo salat i Njega se bojimo"; a On je Taj kojem ćete biti sabrani,'),
(862, 6, 73, 'I On je Taj koji je stvorio nebesa i Zemlju s Istinom. A na dan kad rekne: "Budi!", tad će biti. Riječ Njegova je Istina i Njegova će vlast biti na Dan kad se puhne u sur. Znalac nevidljivog i vidljivog, a On je Mudri, Obaviješteni.'),
(863, 6, 74, 'I kad reče Ibrahim ocu svom Azeru: "Zar uzimaš kipove bogovima? Uistinu, ja vidim tebe i narod tvoj u zabludi očitoj."'),
(864, 6, 75, 'I tako pokazasmo Ibrahimu carstvo nebesa i Zemlje, i da bi bio od sigurnih.'),
(865, 6, 76, 'Pa pošto se nad njim smrači noć, ugleda zvijezdu. Reče: "Ovo je gospodar moj." Pa pošto zađe, reče: "Ne volim one koji zalaze."'),
(866, 6, 77, 'Pa pošto vidje Mjesec izlazećim, reče: "Ovo je gospodar moj." Pa pošto zađe, reče: "Ako me ne uputi Gospodar moj, sigurno ću biti od naroda zalutalih."'),
(867, 6, 78, 'Pa pošto vidje Sunce izlazećim, reče: "Ovo je gospodar moj. Ovo je najveće." Pa pošto zađe, reče: "O narode moj! Ja sam čist od onog šta pridružujete.'),
(868, 6, 79, 'Uistinu! Ja okrećem lice svoje Onom koji je stvorio nebesa i Zemlju, pravovjeran, a nisam ja od mušrika."'),
(869, 6, 80, 'I raspravljao je s njim narod njegov. Reče: "Raspravljate li sa mnom o Allahu, a već me je uputio? A ne bojim se ja uopće onog što Mu pridružujete, izuzev šta htjedne Gospodar moj. Obuhvata moj Gospodar svaku stvar znanjem. Pa zar se nećete opametiti?'),
(870, 6, 81, 'A kako da se bojim onoga što pridružujete, a ne plašite se što vi pridružujete Allahu ono za što vam nije objavio dokaz?" Pa koja od dvije skupine je dostojnija sigurnosti? Kad biste znali!'),
(871, 6, 82, 'Oni koji vjeruju i ne presvlače vjerovanje svoje sa zulmom, takvi će imati sigurnost, a oni su upućeni.'),
(872, 6, 83, 'I to je dokaz Naš kojeg smo dali Ibrahimu protiv naroda njegovog. Uzdižemo stepenima koga hoćemo. Uistinu! Gospodar tvoj je Mudri, Znalac.'),
(873, 6, 84, 'I podarili smo mu Ishaka i Jakuba; svakojeg smo uputili; i Nuha smo uputili ranije; i od potomstva njegovog Dawuda i Sulejmana i Ejjuba i Jusufa i Musaa i Haruna - a tako nagrađujemo dobročinitelje -'),
(874, 6, 85, 'I Zekerijjaa i Jahjaa i Isaa i Iljasa - svaki je od dobrih -'),
(875, 6, 86, 'I Ismaila i El\'jesea i Junusa i Luta - svakojeg smo odlikovali nad svjetovima -'),
(876, 6, 87, 'I (neke) od očeva njihovih i potomaka njihovih i braće njihove; i odabrali smo ih i uputili ih putu pravom.'),
(877, 6, 88, 'To je Uputa Allahova, upućuje njome koga hoće od robova Svojih. A da su pridruživali, sigurno bi im propalo šta su radili.'),
(878, 6, 89, 'To su oni kojima smo dali Knjigu i sud i vjerovjesništvo. Pa ako takvi ne vjeruju u nju, pa doista ćemo je povjeriti ljudima koji neće u nju biti nevjernici.'),
(879, 6, 90, 'To su oni koje je uputio Allah. Zato slijedi uputu njihovu! Reci: "Ne tražim od vas za nju nagradu; to je samo Opomena svjetovima."'),
(880, 6, 91, 'I ne cijene Allaha istinskim cijenjenjem Njegovim kad kažu: "Nije objavio Allah smrtniku ništa." Reci: "Ko je objavio Knjigu, onu koju je donio Musa, svjetlom i Uputom ljudima? Učinili ste je pergamentima, pokazujete je i skrivate mnogo, a poučeni ste šta niste znali vi niti očevi vaši?" Reci: "Allah!" Zatim ih napusti; u raspravi svojoj se zabavljaju.'),
(881, 6, 92, 'A ova Knjiga koju smo objavili, blagoslovljena je, potvrda one prije nje, i da opomeneš Majku naselja i onog ko je oko nje. A oni koji vjeruju u Ahiret - vjeruju u nju, i oni u salatima svojim ustrajavaju.'),
(882, 6, 93, 'A ko je nepravedniji od onog ko izmišlja protiv Allaha laž ili kaže: "Objavljeno je meni", a nije mu objavljeno ništa; i ko kaže: "Objaviću slično šta je objavio Allah." A da vidiš kad budu zalimi u mukama smrti, a meleci ispruže ruke svoje: "Izbacite duše svoje! Danas ćete biti plaćeni kaznom ponižavajućom što ste govorili protiv Allaha neistinu i prema ajetima Njegovim se oholili."'),
(883, 6, 94, 'A doista, doći ćete Nam pojedinačno, kao što smo vas stvorili prvi put, a ostavićete šta smo vam podarili za leđima svojim. I nećemo vidjeti s vama posrednike vaše za koje ste tvrdili da su oni vaši ortaci. Doista, presječeno je među vama, i iščezlo je od vas ono šta ste tvrdili.'),
(884, 6, 95, 'Uistinu! Allah je Taj koji cijepa zrno i košpice. Izvodi živo iz mrtvog, i On je Taj koji izvodi mrtvo iz živog. To je Allah!, pa kako se odvraćate?'),
(885, 6, 96, 'Cjepalac je zore! I učinio noć odmorom, a Sunce i Mjesec za računanje. To je diktat Moćnog, Znalca.'),
(886, 6, 97, 'I On je Taj koji je načinio za vas zvijezde, da se vodite njima u tminama kopna i mora. Doista razlažemo znakove ljudima koji znaju.'),
(887, 6, 98, 'I On je Taj koji vas je stvorio od duše jedne, te boravište i spremište. Doista, razlažemo znakove ljudima koji shvataju.'),
(888, 6, 99, 'I On je Taj koji spušta vodu s neba, pa izvodimo njome rastinje svake stvari; i izvodimo iz nje zelen iz koje izvodimo zrnje naslagano. A od palme - iz pupoljka njenog - grozdove niske; i bašče od grožđa i maslina i nara, sličnog i različitog. Pogledajte plodove njegove, kad donese plod, i zrenje njegovo. Uistinu, u tome su znaci za ljude koji vjeruju.'),
(889, 6, 100, 'I čine Allahu ortacima džinne, a stvorio ih je, i pripisuju Mu sinove i kćeri, bez znanja. Slava neka je Njemu, a uzvišen je od onog šta pripisuju!'),
(890, 6, 101, 'Začetnik nebesa i Zemlje! Otkud Njemu dijete, a On nema družice?, a stvorio je svaku stvar i On je o svakoj stvari Znalac.'),
(891, 6, 102, 'Takav je Allah, Gospodar vaš! Samo je On Bog, Stvoritelj svake stvari, zato Njega obožavajte. A On je nad svakom stvari Zaštitnik.'),
(892, 6, 103, 'Ne dosežu Ga vizije, a On doseže vizije, i On je Fini, Obaviješteni.'),
(893, 6, 104, 'Već su vam došli uvidi od Gospodara vašeg. Pa ko vidi, ta za dušu je njegovu, a ko je slijepac, pa protiv nje je; a nisam ja nad vama čuvar.'),
(894, 6, 105, 'I tako iznosimo znakove i da kažu: "Naučio si", i da ga objasnimo ljudima koji znaju.'),
(895, 6, 106, 'Slijedi šta ti se objavljuje od Gospodara tvog; nema boga osim Njega, i kloni se mušrika.'),
(896, 6, 107, 'A da je htio Allah, ne bi pridruživali. A nismo učinili tebe nad njima čuvarom, i nisi ti njima zaštitnik.'),
(897, 6, 108, 'I ne vrijeđajte one koje prizivaju mimo Allaha, pa da vrijeđaju Allaha neprijateljski, bez znanja. Tako smo uljepšali svakoj ummi rad njihov. Zatim je Gospodaru njihovom povratak njihov, pa će ih obavijestiti o onom šta su radili.'),
(898, 6, 109, 'I kunu se Allahom, najjačom od zakletvi njihovih, ako bi im došao znak, da bi sigurno vjerovali u njega. Reci: "Samo su znaci kod Allaha." A otkud da vi zapazite, da ga kad dođe neće vjerovati?'),
(899, 6, 110, 'I okrenućemo srca njihova i opažanja njihova - kao što nisu vjerovali u njega prvi put - i ostaviti ih (da) u pretjeranosti svojoj lutaju.'),
(900, 6, 111, 'I čak da im Mi spustimo meleke, i da im govore mrtvi, i da prikupimo protiv njih svaku stvar sučelice, ne bi vjerovali, osim ako bi Allah htio. Međutim, većina njih su neznalice.'),
(901, 6, 112, 'I tako, učinili smo svakom vjerovjesniku neprijateljem šejtane ljudi i džinna - inspirisali su jedni druge kitnjastim govorom, obmanjujućim. A da je htio Gospodar tvoj, ne bi to radili. Zato ostavi njih i ono šta izmišljaju;'),
(902, 6, 113, 'I da se tome naklonu srca onih koji ne vjeruju u Ahiret i da se zadovolje time, i da zarade ono šta oni zarađuju.'),
(903, 6, 114, 'Pa zar da mimo Allaha tražim suca, a On je Taj koji vam je objavio Knjigu detaljnu? A oni kojima smo dali Knjigu, znaju da je ona spuštena od Gospodara tvog s Istinom; zato ne budite nikako od sumnjivaca.'),
(904, 6, 115, 'I ostvaruje se Riječ Gospodara tvog istinski i pravično. Nema tog ko će izmijeniti Riječi Njegove; a On je Onaj koji čuje, Znalac.'),
(905, 6, 116, 'A ako bi poslušao većinu onih na Zemlji, zaveli bi te s puta Allahovog. Slijede samo pretpostavku i oni samo lažu.'),
(906, 6, 117, 'Uistinu, Gospodar tvoj - On je Najbolji znalac onog ko skreće s puta Njegovog i On je Najbolji poznavalac upućenih.'),
(907, 6, 118, 'Pa jedite od onog nad čim je spomenuto ime Allahovo, ako ste u ajete Njegove vjernici.'),
(908, 6, 119, 'A nije za vas da ne jedete ono nad čim je spomenuto ime Allahovo - a doista vam je razložio šta vam je zabranio - izuzimajući ono na što ste prisiljeni. A uistinu, mnogi zavode strastima svojim, bez znanja. Uistinu, Gospodar tvoj - On je Najbolji znalac prekršilaca.'),
(909, 6, 120, 'I ostavite vanjštinu grijeha i unutrašnjost njegovu. Uistinu, oni koji stiču grijeh, biće plaćeni za ono šta su stekli.'),
(910, 6, 121, 'I ne jedite od onog nad čim nije spomenuto ime Allahovo, a uistinu, to je grijeh. I uistinu, šejtani nadahnjuju prijatelje svoje da se raspravljaju s vama. A ako ih poslušate, uistinu vi ste mušrici.'),
(911, 6, 122, 'Zar je onaj ko je bio mrtav - pa smo ga oživili i načinili mu svjetlo kojim ide među ljudima - kao onaj ko mu je sličan, u tminama iz kojih neće biti vani? Eto tako je nevjernicima uljepšano ono šta rade.'),
(912, 6, 123, 'A tako smo načinili u svakom naselju velikaše zloradnicima njegovim, da spletkare u njemu. A ne spletkare izuzev protiv duša svojih, a ne opažaju.'),
(913, 6, 124, 'I kad im dođe znak, govore: "Nećemo vjerovati dok nam se ne dadne slično šta je dato poslanicima Allahovim." Allah je Najbolji znalac gdje postavlja poslanstva Svoja. Pogodiće one koji griješe poniženje kod Allaha, i kazna žestoka za ono što spletkare.'),
(914, 6, 125, 'Pa onome koga Allah želi da uputi, raširi grudi njegove Islamu. A onom koga želi da zavede, učini grudi njegove tijesnim, uskim, kao da se penje u nebo. Tako postavlja Allah prljavštinu na one koji ne vjeruju.'),
(915, 6, 126, 'A ovo je put Gospodara tvog - ispravan. Doista smo razložili znakove ljudima koji se poučavaju.'),
(916, 6, 127, 'Imaće oni kuću selama kod Gospodara svog, a On će biti Zaštitnik njihov zbog onog šta su radili.'),
(917, 6, 128, 'A na Dan kad ih skupi zajedno: "O skupino džinna! Doista ste (zaveli) mnogo ljudi." A reći će njihovi prijatelji između ljudi: "Gospodaru naš! Koristili smo mi jedni drugima i stigli roku našem kojeg si nam odredio." Reći će: "Vatra je boravište vaše, vječno ćete biti u njoj, izuzev šta htjedne Allah." Uistinu, Gospodar tvoj je Mudri, Znalac.'),
(918, 6, 129, 'A tako okrećemo zalime jedne drugima, zbog onog šta su zaradili.'),
(919, 6, 130, 'O skupino džinna i ljudi! Zar vam nisu dolazili poslanici između vas, kazivali vam znakove Moje i upozoravali vas susretom ovog dana vašeg? Reći će: "Svjedočimo protiv duša naših." A obmanuo ih je život Dunjaa i svjedočit će protiv duša svojih da su oni bili nevjernici.'),
(920, 6, 131, 'To zato što neće Gospodar tvoj biti uništavač naselja zbog zulma, a budu li stanovnici njegovi nesvjesni.'),
(921, 6, 132, 'A imaće svako stepene od onog šta su radili; i nije Gospodar tvoj nehajan na ono šta rade.'),
(922, 6, 133, 'A Gospodar tvoj je Neovisni, Vlasnik milosti. Ako hoće uklonit će vas i nadomjestiti nakon vas čime hoće, kao što je stvorio vas od potomstva drugih naroda.'),
(923, 6, 134, 'Uistinu, ono čime vam se prijeti uslijedit će, a niste vi ti koji će umaći.'),
(924, 6, 135, 'Reci: "O narode moj! Radite na mjestu svom. Uistinu! Ja sam radnik. Pa spoznat ćete onog ko će za kraj imati dom." Uistinu, to neće uspjeti zalimi.'),
(925, 6, 136, 'I dodjeljuju Allahu nešto od onog šta je stvorio od ljetine i stoke - dio, pa kažu: "Ovo je za Allaha", po tvrdnji njihovoj - "a ovo je za ortake naše." Pa ono šta je za ortake njihove, tad ne stiže Allahu; a šta je za Allaha, pa to stiže ortacima njihovim. Zlo je kako rasuđuju!'),
(926, 6, 137, 'A isto tako, uljepšali su mnogim mušricima ortaci njihovi ubijanje djece njihove, da bi ih upropastili i da bi im (konfuzijom) zaodijenuli vjeru njihovu. A da hoće Allah, ne bi to činili. Zato ostavi njih i ono šta izmišljaju.'),
(927, 6, 138, 'I govore: "Ova stoka i ljetina je zabranjena. Neće se hraniti njome, izuzev onaj kome dopustimo" - po tvrdnji njihovoj - "i stoka čija su leđa zabranjena i stoka nad kojom nije spomenuto ime Allahovo" - izmišljajući protiv Njega. Platit će im za ono što izmišljaju.'),
(928, 6, 139, 'I govore: "Šta je u trbusima ove stoke posebno je za naše muškarce, a zabranjeno je ženama našim"; a ako bude mrtvo, onda su oni u tom sudionici. Platit će im opisivanje njihovo. Uistinu, On je Mudri, Znalac.'),
(929, 6, 140, 'Doista su izgubili oni koji su ubijali djecu svoju glupo, bez znanja, i zabranjivali ono čim ih je opskrbio Allah, izmišljanjem protiv Allaha. Doista su zabludjeli i nisu bili upućeni.'),
(930, 6, 141, 'A On je Taj koji stvara bašče poduprte i nepoduprte, i palme, i usjev različite jestivosti njegove, i masline i narove, slične i različite. Jedite od ploda njihovog - kad donese plod, i dajte dio toga na dan berbe njegove, i ne rasipajte. Uistinu! On ne voli rasipnike.'),
(931, 6, 142, 'A od stoke, tovarnu i sitnu. Jedite od onog čim vas je opskrbio Allah, i ne slijedite korake šejtanove. Uistinu, on vam je neprijatelj otvoreni.'),
(932, 6, 143, 'Osam parova: od ovaca dvoje i od koza dvoje. Reci: "Da li je dva mužjaka zabranio ili dvije ženke, ili ono šta sadrže utrobe dviju ženki? Obavijestite me znanjem, ako istinu govorite."'),
(933, 6, 144, 'I od deve dvoje i od goveda dvoje. Reci: "Da li je dva mužjaka zabranio ili dvije ženke ili šta sadrže utrobe dviju ženki, ili ste bili svjedoci kad vam je Allah ovo zapovjedio?" Pa ko je nepravedniji od onog ko izmišlja protiv Allaha laž, da bi zavodio ljude bez znanja? Uistinu! Allah ne upućuje narod zalima.'),
(934, 6, 145, 'Reci: "Ne nalazim (ništa) u onom šta mi se objavljuje zabranjenim za onog koji to jede, izuzev što je lešina, ili krv prolivena ili meso svinje - pa uistinu to je pogan - ili ono nad čim je griješno zazvano drugo ime, a ne Allahovo. Pa ko bude natjeran, bez želje i bez prekoračenja, pa uistinu, Gospodar tvoj je Oprosnik, Milosrdni.'),
(935, 6, 146, 'A onima koji su jevreji, zabranili smo svaku s papkom (nerazdvojenim), a od goveda i brava zabranili smo im sala njihova, izuzev šta nose leđa njihova ili crijeva, ili šta je pomješano sa kosti. To smo ih platili zbog bunta njihovog, a uistinu, Mi Istinu govorimo.'),
(936, 6, 147, 'Pa ako te poreknu, tad reci: "Gospodar vaš je Vlasnik milosti sveobuhvatne"; i neće se povući sila Njegova sa naroda prijestupnika.'),
(937, 6, 148, 'Reći će oni koji pridružuju: "Da je htio Allah ne bismo pridruživali, a ni očevi naši, i ne bismo zabranili ništa." Isto tako su poricali oni prije njih, dok nisu iskusili silu Našu. Reci: "Ima li kod vas imalo znanja pa da nam ga iznesete? Slijedite samo pretpostavku, i vi samo lažete."'),
(938, 6, 149, 'Reci: "Pa Allahov argument je konačan. Ta da je htio, sigurno bi vas sve uputio."'),
(939, 6, 150, 'Reci: "Dovedite svjedoke vaše koji svjedoče da je Allah ovo zabranio." Pa ako posvjedoče, tad ne svjedoči s njima; i ne slijedi strasti onih koji poriču ajete Naše i onih koji ne vjeruju u Ahiret - a oni Gospodaru svom izjednačuju (rivale).'),
(940, 6, 151, 'Reci: "Dođite, proučiću ono šta vam je zabranio Gospodar vaš: da ne pridružujete Njemu ništa; roditeljima dobročinstvo, i da ne ubijate djecu vašu zbog siromaštva - Mi opskrbljujemo vas i njih - i ne približujte se razvratnostima, onom šta je vanjština njihova i šta je unutrašnjost, i ne ubijte dušu koju je zabranio Allah, izuzev radi pravde. To vam zapovjeda da biste vi razumjeli;'),
(941, 6, 152, 'I ne približujte se imetku siročeta, izuzev onako kako je najbolje, dok ne dosegne zrelost svoju; i ispunjavajte mjeru i vagu pravedno. Ne opterećujemo dušu, osim mogućnošću njenom. A kad govorite, onda pravedno, makar bio rođak; i obavezu Allahu ispunjavajte. To vam zapovijeda da biste se vi poučili";'),
(942, 6, 153, '"A ovo je Moj put pravi, zato ga slijedite. I ne slijedite puteve (druge), pa da vas odvoje od puta Njegovog; to vam zapovijeda da biste se vi pobojali."'),
(943, 6, 154, 'Zatim smo dali Musau Knjigu potpunom za onog ko čini dobro i detaljnom za svaku stvar, i Uputu i milost, da bi oni u susret sa Gospodarom svojim vjerovali.'),
(944, 6, 155, 'A ova Knjiga koju objavljujemo, blagoslovljena je, zato je slijedite i bojte se (Allaha), da bi vam se smilovalo,'),
(945, 6, 156, 'Da ne biste rekli: "Samo je objavljena Knjiga dvjema grupama prije nas, a doista smo bili prema učenju njihovom nemarni";'),
(946, 6, 157, 'Ili kažete: "Da je nama - nama objavljena Knjiga, sigurno bismo bili upućeniji od njih." Pa već vam je došao dokaz jasan od Gospodara vašeg, i Uputa i milost. Pa ko je nepravedniji od onog ko poriče ajete Allahove i odvraća od njih? Platićemo one koji odvraćaju od ajeta Naših, opakom kaznom, zato što su odvraćali.'),
(947, 6, 158, 'Čekaju li samo da im dođu meleci ili dođe Gospodar tvoj ili dođe neki od znakova Gospodara tvog? Na Dan kad dođe neki od znakova Gospodara tvog, neće koristiti duši vjerovanje njeno, (koja) nije vjerovala prije ili zaradila u vjerovanju svom dobro. Reci: "Očekujte! Uistinu! Mi smo čekatelji."'),
(948, 6, 159, 'Uistinu! Oni koji su razdijelili vjeru svoju i postali sekte, nisi od njih ni u čemu. Samo je stvar njihova do Allaha, zatim će ih obavijestiti o onom šta su činili.'),
(949, 6, 160, 'Ko dođe sa dobrim djelom, pa imaće on deset sličnih njemu; a ko dođe sa zlim djelom, tad će biti plaćen samo sličnim njemu; i njima se neće učiniti zulm.'),
(950, 6, 161, 'Reci: "Uistinu! Mene je uputio Gospodar moj putu pravom: vjeri pravoj, milletu Ibrahima hanife, a nije bio od mušrika."'),
(951, 6, 162, 'Reci: "Uistinu, salat moj i žrtva moja i život moj i smrt moja su za Allaha, Gospodara svjetova.'),
(952, 6, 163, 'On nema sudruga; a to mi je naređeno i ja sam prvi od muslimana."'),
(953, 6, 164, 'Reci: "Zar da mimo Allaha tražim gospodara, a On je Gospodar svake stvari?" A zarađuje svaka duša samo sebi, i neće se teretiti opterećeni teretom drugog. Potom je Gospodaru vašem povratak vaš, te će vas obavijestiti o onom u čemu ste se razilazili.'),
(954, 6, 165, 'I On je Taj koji vas je učinio halifama Zemlje, i uzdigao vas jedne nad drugima stepenima, da vas iskuša u onom šta vam je dao. Uistinu! Gospodar tvoj je brz u kazni, i uistinu, On je Oprosnik, Milosrdni.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Alif. Lam. Mim. Sad.'),
(956, 7, 2, 'Knjiga se spušta tebi - zato neka u grudima tvojim ne bude tjeskobe zbog nje - da opominješ njome, a opomena je vjernicima.'),
(957, 7, 3, 'Slijedite šta vam se objavljuje od Gospodara vašeg, i ne slijedite mimo Njega zaštitnike. Malo čega se sjećate!'),
(958, 7, 4, 'A koliko je naselja koja smo uništili! Tad bi im došla sila Naša noću, ili dok bi oni prilegli u podnevu.'),
(959, 7, 5, 'Zato je bio priziv njihov kad bi im došla sila Naša, jedino što bi rekli: "Uistinu! Mi smo bili zalimi."'),
(960, 7, 6, 'Pa sigurno ćemo pitati one kojima je poslata i sigurno ćemo pitati izaslanike.'),
(961, 7, 7, 'Tad ćemo im sigurno ispričati sa znanjem, a nismo bili odsutni.'),
(962, 7, 8, 'A mjerenje će Tog dana biti istinito. Pa onaj čija mjerila pretegnu - pa ti takvi će biti uspješni;'),
(963, 7, 9, 'A onaj čija mjerila olakšaju - pa takvi su oni koji su izgubili duše svoje, zbog toga što su ajetima Našim bili zalimi.'),
(964, 7, 10, 'A doista smo vas učvrstili na Zemlji i načinili vam na njoj sredstva za život. Malo što zahvaljujete!'),
(965, 7, 11, 'I doista, stvorili smo vas, zatim vas oblikovali, potom rekosmo melecima: "Učinite sedždu Ademu!" Tad padoše ničice, izuzev Iblisa, nije bio od onih koji padaju na sedždu.'),
(966, 7, 12, '(Allah) reče: "Šta te je zadržalo da nisi pao na sedždu kad sam ti naredio?" Reče: "Ja sam bolji od njega. Mene si stvorio od vatre, a njega si stvorio od ilovače."'),
(967, 7, 13, '(Allah) reče: "Onda silazi iz njega! Pa nije za tebe da se oholiš u njemu. Zato izlazi! Uistinu, ti si od poniženih."'),
(968, 7, 14, 'Reče: "Odloži mi do Dana kad budu podignuti."'),
(969, 7, 15, '(Allah) reče: "Uistinu, ti si od onih kojima se odlaže."'),
(970, 7, 16, 'Reče: "Pa zato što si me zaveo, sigurno ću im biti u zasjedi na putu Tvom pravom.'),
(971, 7, 17, 'Zatim ću im sigurno prilaziti ispred njih i iza njih, i s desnih strana njihovih i s lijevih strana njihovih, i nećeš naći većinu njih zahvalnim."'),
(972, 7, 18, '(Allah) reče: "Izlazi iz njega pokuđen i prognan. Sigurno onaj od njih ko te bude slijedio - sigurno ću napuniti Džehennem s vama zajedno."'),
(973, 7, 19, '"I o Ademe! Nastani ti i žena tvoja Džennet, pa jedite odakle god hoćete, i ne približujte se ovom drvetu, pa da budete od zalima."'),
(974, 7, 20, 'Pa je došaptavao njima dvoma šejtan, da bi im otkrio šta im je bilo skriveno od sew\'ata njihovih. I reče: "Zabranio vam je Gospodar vaš sa ovog drveta, samo da ne budete dva meleka ili ne budete od besmrtnika."'),
(975, 7, 21, 'I zaklete se njima dvoma: "Uistinu! Ja sam vam sigurno od savjetnika!"'),
(976, 7, 22, 'Tad prouzroči da njih dvoje padnu na prevaru. Pa pošto okusiše drvo, otkriše im se sew\'ati njihovi i počeše krpiti po sebi nešto lista džennetskog. I pozva ih Gospodar njihov: "Zar vam nisam zabranio sa ovog drveta i rekao vam: \'Uistinu! Šejtan vam je neprijatelj otvoreni?"'),
(977, 7, 23, 'Rekoše: "Gospodaru naš! Učinili smo zulm dušama našim, i ako nam ne oprostiš i ne smiluješ nam se, sigurno ćemo biti od gubitnika."'),
(978, 7, 24, 'Reče: "Siđite! Vi ste jedno drugom neprijatelji. I imaćete vi na Zemlji boravište i opskrbu neko vrijeme."'),
(979, 7, 25, 'Reče: "Na njoj ćete živjeti i na njoj umirati, i iz nje izvedeni biti."'),
(980, 7, 26, 'O sinovi Ademovi! Doista smo vam spustili odjeću da sakrije sew\'ate vaše, i perjanice, a odijelo bogobojaznosti, to je bolje. To je od znakova Allahovih, da bi se oni poučili.'),
(981, 7, 27, 'O sinovi Ademovi! Neka vas nikako ne zavede šejtan kao što je izveo roditelje vaše iz Dženneta, strgavši s njih dvoje odjeću njihovu, da bi im pokazao sew\'ate njihove. Uistinu, on vas vidi, on i vrsta njegova, odakle ne vidite njih. Uistinu! Mi smo učinili šejtane zaštitnicima onih koji ne vjeruju.'),
(982, 7, 28, 'A kad učine razvrat, govore: "Našli smo na tome očeve naše i Allah nam je to naredio." Reci: "Uistinu, Allah ne naređuje razvrat. Zar govorite o Allahu ono šta ne znate?"'),
(983, 7, 29, 'Reci: "Gospodar moj naređuje pravednost; i upravite lica svoja kod svakog mesdžida. Prizivajte Njega, odani Njegovom dinu. Kao što vas je u početku stvorio, (tako se i) vraćate."'),
(984, 7, 30, 'Dio je uputio, a dio - obistinila se na njima zabluda. Uistinu, oni uzimaju šejtane zaštitnicima mimo Allaha i misle da su oni upućeni.'),
(985, 7, 31, 'O sinovi Ademovi! Uzmite ukras svoj kod svakog mesdžida, i jedite i pijte, a ne rasipajte. Uistinu! On ne voli rasipnike.'),
(986, 7, 32, 'Reci: "Ko je zabranio ukras Allahov kojeg je iznio robovima Svojim, i dobre stvari od opskrbe?" Reci: "One su za one koji vjeruju u životu Dunjaa, posebno na Dan kijameta." Eto tako razlažemo znakove ljudima koji znaju.'),
(987, 7, 33, 'Reci: "Gospodar moj je zabranio jedino razvratnosti, ono šta je vidljivo od njih i šta je skriveno, i griješenje i tlačenje bez prava, i da pridružujete Allahu ono za što nije objavio autoritet, i da govorite o Allahu ono šta ne znate."'),
(988, 7, 34, 'A svaka zajednica ima rok, pa kad dođe rok njihov, neće (ga) zadržati čas, niti ubrzati.'),
(989, 7, 35, 'O sinovi Ademovi! Ako vam dođu poslanici između vas, kazivaće vam ajete Moje, pa ko se poboji i popravi, tad neće biti straha nad njima, niti će oni žaliti.'),
(990, 7, 36, 'A oni koji poreknu ajete Naše i uzohole se prema njima, takvi će biti stanovnici vatre, oni će u njoj biti vječito.'),
(991, 7, 37, 'Pa ko je nepravedniji od onog ko izmišlja protiv Allaha laž ili poriče ajete Njegove? Tim takvima će doći dio njihov iz Knjige, dok, kad im dođu izaslanici Naši da ih uzmu, govoriće: "Gdje je ono šta ste prizivali mimo Allaha?" Reći će: "Nestali su od nas." I svjedočiće protiv duša svojih da su oni bili nevjernici.'),
(992, 7, 38, '(Allah) će reći: "Uđite u vatru u zajednice već prošle prije vas, od džinna i ljudi." Kad god uđe zajednica, proklinjaće svoju sestru (zajednicu), dok, kad se u njoj svi sustignu, reći će zadnja njihova prvoj njihovoj: "Gospodaru naš! Ovi su nas zaveli, zato im daj dvostruku kaznu vatrom." Reći će: "Za svakog je dvostruko, međutim ne znate."'),
(993, 7, 39, 'I reći će prva njihova zadnjoj njihovoj: "Pa niste vi imali nad nama nikakvu prednost, zato kušajte kaznu za ono šta ste zaslužili."'),
(994, 7, 40, 'Uistinu, oni koji budu poricali ajete Naše i oholili se prema njima, neće im se otvoriti kapije neba i neće ući u Džennet, dok ne prođe deva kroz ušice igle; a tako plaćamo prestupnike.'),
(995, 7, 41, 'Imaće oni od Džehennema postelju, a iznad njih će biti (vatrene) pokrivke; a tako kažnjavamo zalime.'),
(996, 7, 42, 'A oni koji vjeruju i čine dobra djela - ne opterećujemo dušu, izuzev mogućnošću njenom - takvi će biti stanovnici Dženneta, oni će u njemu biti vječito.'),
(997, 7, 43, 'I istrgnućemo šta je u grudima njihovim od mržnje; teći će ispod njih rijeke i govoriće: "Hvala Allahu koji nas je ovom uputio. A ne bismo se uputili da nas nije Allah uputio! Doista, donosili su poslanici Gospodara našeg Istinu." I viknuće im se: "Ovo je Džennet koji vam je dat u naslijeđe za ono šta ste radili."'),
(998, 7, 44, 'I dozivaće stanovnici Dženneta stanovnike vatre: "Doista smo našli ono što nam je obećavao Gospodar naš istinitim, pa jeste li našli ono šta je obećavao Gospodar vaš istinitim?" Reći će: "Da." Tad će objaviti muezzin među njima: "Prokletstvo Allahovo je nad zalimima,'),
(999, 7, 45, 'Onima koji su odvraćali od puta Allahovog i tražili ga iskrivljenim, a oni su u Ahiret bili nevjernici."'),
(1000, 7, 46, 'I između njih dvaju biće veo, a na uzvisinama će biti ljudi (koji će) prepoznati svakog po obilježjima njihovim. I vikat će stanovnicima Dženneta: "Selamun alejkum!" Nisu ušli u njega, a oni će željeti.'),
(1001, 7, 47, 'I kad skrenu pogledi njihovi prema stanovnicima vatre, reći će: "Gospodaru naš! Ne stavi nas sa narodom zalima."'),
(1002, 7, 48, 'I pozivaće stanovnici uzvisina ljude koje će znati po obilježjima njihovim, govoriće: "Šta vam je vrijedio skup vaš i što ste se oholili?'),
(1003, 7, 49, 'Jesu li to oni (za koje) ste se kleli da im neće stići Allahova milost?" "Uđite u Džennet! Nema straha nad vama i nećete vi žaliti."'),
(1004, 7, 50, 'I zvaće stanovnici vatre stanovnike Dženneta: "Prolijte na nas malo vode ili nešto od onog čim vas je opskrbio Allah." Reći će: "Uistinu! Allah je to dvoje zabranio nevjernicima,'),
(1005, 7, 51, 'Onima koji su uzimali vjeru svoju igrom i zabavom, a obmanuo ih je život Dunjaa." Zato ćemo njih Danas zaboraviti, kao što su zaboravili na susret dana njihovog ovog, i što su ajete Naše nijekali.'),
(1006, 7, 52, 'A doista, donijeli smo im Knjigu, pa smo je bazirali na znanju, Uputu i milost za ljude koji vjeruju.'),
(1007, 7, 53, 'Čekaju li samo ostvarenje njeno? Na Dan kad dođe ostvarenje njeno, govoriće oni koji su je zaboravili ranije: "Doista su poslanici Gospodara našeg donosili Istinu. Pa hoće li biti za nas ikakvih posrednika, pa da posreduju za nas ili da budemo vraćeni, pa da radimo drugačije no što smo radili?" Doista, izgubili su duše svoje, a iščezlo je od njih šta su izmišljali.'),
(1008, 7, 54, 'Uistinu! Gospodar vaš je Allah koji je stvorio nebesa i Zemlju u šest dana, zatim se postavio na Arš. Prekriva noću dan (koji) traži nju žurno, a Sunce i Mjesec i zvijezde potčinjeni su komandi Njegovoj. Uistinu! Njegovo je stvaranje i komanda! Blagoslovljen neka je Allah, Gospodar svjetova!'),
(1009, 7, 55, 'Zazivajte Gospodara svog skrušeno i potajno. Uistinu! On ne voli one koji prevršuju.'),
(1010, 7, 56, 'I ne činite fesad na Zemlji, nakon sređivanja njenog, i prizivajte Njega, strahujući i nadajući se. Uistinu, Allahova milost je bliska dobročiniteljima.'),
(1011, 7, 57, 'A On je Taj koji šalje vjetrove radosnom viješću pred milošću Svojom. Dok, kad ponesu oblake teške, usmjerimo ga ka zemlji zamrloj pa spuštamo na nju vodu, te izvodimo njome od svačeg plodove. Tako ćemo izvesti mrtve, da biste se vi podsjetili.'),
(1012, 7, 58, 'A dobra zemlja izvodi rastinje svoje s dozvolom Gospodara svog; a koja je loša, izvodi samo oskudno. Tako razlažemo znakove ljudima (koji) zahvaljuju.'),
(1013, 7, 59, 'Doista smo poslali Nuha narodu njegovom, pa reče: "O narode moj! Obožavajte Allaha! Nemate vi drugog boga osim Njega. Uistinu! Ja se bojim za vas kazne Dana strahovitog."'),
(1014, 7, 60, 'Uglednici iz naroda njegovog rekoše: "Uistinu, mi te vidimo u zabludi očitoj."'),
(1015, 7, 61, 'Reče: "O narode moj! Nije u meni zabluda, nego sam ja poslanik Gospodara svjetova.'),
(1016, 7, 62, 'Dostavljam vam poslanice Gospodara svog i savjetujem vas, i znam od Allaha ono šta ne znate.'),
(1017, 7, 63, 'Zar se čudite što vam dolazi Opomena od Gospodara vašeg po čovjeku između vas, da bi vas opomenuo, i da biste se pobojali i da bi vam se smilovalo."'),
(1018, 7, 64, 'Tad ga porekoše, pa spasismo njega i one s njim u lađi, a potopismo one koji su poricali ajete Naše. Uistinu, oni su bili narod slijepaca.'),
(1019, 7, 65, 'I Adu, brata njihovog Huda. Reče: "O narode moj! Obožavajte Allaha. Nemate vi drugog boga osim Njega. Pa zar se nećete pobojati?"'),
(1020, 7, 66, 'Uglednici koji nisu vjerovali iz naroda njegovog, rekoše: "Uistinu, mi te vidimo u gluposti, i uistinu, mi mislimo da si ti od lažova."'),
(1021, 7, 67, 'Reče: "O narode moj! Nije u meni glupost, nego sam ja poslanik Gospodara svjetova.'),
(1022, 7, 68, 'Dostavljam vam poslanice Gospodara mog i ja sam vam savjetnik pouzdani.'),
(1023, 7, 69, 'Zar se čudite što vam dolazi Opomena od Gospodara vašeg po čovjeku između vas, da bi vas upozorio? A sjetite se kad vas je učinio nasljednicima nakon naroda Nuhovog, i povećao vas u konstituciji obilno. Pa sjetite se blagodati Allahovih, da biste vi uspjeli."'),
(1024, 7, 70, 'Rekoše: "Zar si nam došao da bismo obožavali Allaha - jedinog Njega, a ostavili ono što su obožavali očevi naši? Pa daj nam ono čim nam prijetiš, ako istinu govoriš!"'),
(1025, 7, 71, 'Reče: "Već je pala na vas od Gospodara vašeg prljavština i gnjev. Zar se prepirete sa mnom o imenima? koja ste nadjenuli vi i očevi vaši; nije spustio Allah za njih nikakav autoritet. Pa očekujte! Uistinu! Ja sam od onih koji sa vama čekaju."'),
(1026, 7, 72, 'Pa smo spasili njega i one s njim milošću od Nas, i odsjekli korijen onih koji su poricali ajete Naše i nisu bili vjernici.'),
(1027, 7, 73, 'I Semudu, brata njihovog Saliha. Reče: "O narode moj! Obožavajte Allaha! Nemate vi drugog boga osim Njega. Već vam je došao jasan dokaz od Gospodara vašeg. Ova deva Allahova za vas je znak; zato je pustite da jede po Zemlji Allahovoj, i ne dotičite je zlom, pa da vas spopadne kazna bolna."'),
(1028, 7, 74, 'I sjetite se kad vas je učinio nasljednicima nakon Ada i nastanio vas na Zemlji. Pravite u nizijama njenim palače i klešete brda (u) kuće. Pa sjetite se blagodati Allahovih i ne činite zlo na Zemlji, praveći fesad.'),
(1029, 7, 75, 'Uglednici koji su se uzoholili iz naroda njegovog rekoše onima koji su učinjeni slabima - onom ko je vjerovao od njih: "Znate li da je Salih izaslanik od Gospodara njegovog?" Rekoše: "Uistinu, mi smo u ono s čim je poslat, vjernici!"'),
(1030, 7, 76, 'Oni koji su se uzoholili rekoše: "Uistinu, mi smo u ono u što vjerujete, nevjernici!"'),
(1031, 7, 77, 'Tad raniše devu i oglušiše se o naredbu Gospodara svog, i rekoše: "O Salih! Donesi nam ono čim nam prijetiš, ako si od izaslanika."'),
(1032, 7, 78, 'Pa ih snađe potres, te osvanuše u kućama svojim povaljani.'),
(1033, 7, 79, 'Tad se okrenu od njih i reče: "O narode moj. Doista sam vam dostavio poslanicu Gospodara svog i savjetovao vas, međutim, ne volite savjetnike."'),
(1034, 7, 80, 'I Luta! Kad reče narodu svom: "Zar činite razvrat u kojem vas nije nadmašio niko od svjetova?'),
(1035, 7, 81, 'Uistinu, vi prilazite strašću ljudima mimo žena. Naprotiv, vi ste ljudi koji pretjeruju."'),
(1036, 7, 82, 'Odgovor naroda njegovog bio je jedino što rekoše: "Protjerajte ih iz naselja vašeg. Uistinu, oni su ljudi koji se čiste."'),
(1037, 7, 83, 'Pa smo spasili njega i porodicu njegovu, izuzev žene njegove, bila je od izostalih.'),
(1038, 7, 84, 'I izlili smo na njih kišu. Pa pogledaj kakav je bio kraj prestupnika!'),
(1039, 7, 85, 'I Medjenu, brata njihovog, Šuajba. Reče: "O narode moj! Obožavajte Allaha! Nemate vi drugog boga sem Njega. Zaista vam je došao jasan dokaz od Gospodara vašeg; zato ispunjavajte mjeru i mjerilo. I ne zakidajte ljudima stvari njihove, i ne činite fesad na Zemlji, nakon sređivanja njenog. To je bolje za vas, ako ste vjernici.'),
(1040, 7, 86, 'I ne sjedajte na svakom putu prijeteći i odvraćajući od puta Allahovog onog ko vjeruje u Njega i tražeći ga iskrivljenim. I sjetite se kad ste bili (brojčano) mali, pa vas je umnožio. I pogledajte kakav je bio kraj mufsida.'),
(1041, 7, 87, 'A ako bude skupina među vama (koja) vjeruje u ono s čim sam poslat, i skupina (koja) ne vjeruje, onda se strpite dok Allah ne presudi među nama, a On je Najbolji od sudaca."'),
(1042, 7, 88, 'Velikani koji su se uzoholili iz naroda njegovog, rekoše: "Sigurno ćemo protjerati tebe, o Šuajb, i one koji s tobom vjeruju, iz naselja našeg ili ćete se sigurno vratiti u millet naš." Reče: "Zar iako nam se gadi?"'),
(1043, 7, 89, 'Doista bismo izmišljali protiv Allaha laž ako bismo se vratili u millet vaš, nakon što nas je izbavio Allah iz njega. A nije za nas da se u njega vraćamo, izuzev ako htjedne Allah, Gospodar naš. Gospodar naš obuhvata svaku stvar znanjem. U Allaha se uzdamo. Gospodaru naš! Presudi između nas i između naroda našeg s Istinom, a Ti si Najbolji od sudaca."'),
(1044, 7, 90, 'I rekoše velikani koji nisu vjerovali iz naroda njegovog: "Ako biste slijedili Šuajba, uistinu biste vi tad bili gubitnici."'),
(1045, 7, 91, 'Pa zadesi njih potres, te osvanuše u kućama svojim povaljani.'),
(1046, 7, 92, 'Oni koji su porekli Šuajba, kao da nisu stanovali u njima. Koji su porekli Šuajba - oni su bili gubitnici.'),
(1047, 7, 93, 'Tad se okrenu od njih i reče: "O narode moj! Doista sam vam dostavio poslanice Gospodara mog i savjetovao vas; pa kako da žalim nad narodom nevjernika?"'),
(1048, 7, 94, 'A nismo poslali u grad nijednog vjerovjesnika, a da nismo pogodili stanovništvo njegovo nesrećom i bijedom, da bi se oni ponizili.'),
(1049, 7, 95, 'Zatim bismo zamijenili namjesto zla dobro, dok se ne bi namnožili pa rekli: "Doista je naše očeve doticala sreća i nesreća." Tad bismo ih dograbili nenadno, a oni ne bi opazili.'),
(1050, 7, 96, 'A da su stanovnici gradova vjerovali i bojali se, sigurno bismo im otvorili berekete iz neba i Zemlje. Međutim poricali su, pa smo ih ščepavali za ono šta su zarađivali.'),
(1051, 7, 97, 'Pa jesu li sigurni stanovnici gradova da im neće doći sila Naša noću, (dok) oni budu spavali?'),
(1052, 7, 98, 'Zar su sigurni stanovnici gradova da im neće doći sila Naša danju, a oni se budu igrali?'),
(1053, 7, 99, 'Pa jesu li se osigurali od plana Allahovog? Pa siguran je u plan Allahov jedino narod gubitnika.'),
(1054, 7, 100, 'Zar ne upućuje one koji naslijeđuju Zemlju nakon (prethodnih) stanovnika njenih, da ćemo ih - ako htjednemo - pogoditi zbog grijeha njihovih i zapečatiti srca njihova, pa oni neće čuti.'),
(1055, 7, 101, 'To su bili gradovi čije ti neke vijesti kazujemo. A doista su im dolazili poslanici njihovi s dokazima jasnim, pa nisu vjerovali u ono što su ranije poricali. Tako Allah pečati srca nevjernika.'),
(1056, 7, 102, 'I nismo našli za većinu njih nikakav zavjet, a doista smo našli većinu njih grješnicima.'),
(1057, 7, 103, 'Zatim smo poslali poslije njih Musaa sa znacima Našim faraonu i velikanima njegovim, pa nisu povjerovali u njih. Zato pogledaj kakav je bio kraj mufsida!'),
(1058, 7, 104, 'I reče Musa: "O faraone! Uistinu, ja sam poslanik Gospodara svjetova.'),
(1059, 7, 105, 'Dužan sam da govorim o Allahu samo Istinu. Doista sam vam došao sa dokazom jasnim od Gospodara vašeg. Zato pošalji sa mnom sinove Israilove."'),
(1060, 7, 106, '(Faraon) reče: "Ako si došao sa znakom, onda ga daj, ako si od iskrenih!"'),
(1061, 7, 107, 'Tad (Musa) baci štap svoj, pa gle! on zmija očita.'),
(1062, 7, 108, 'I izvuče ruku svoju, tad gle! ona bijela za posmatrače.'),
(1063, 7, 109, 'Uglednici iz naroda faraonovog rekoše: "Uistinu, ovo je čarobnjak, znalac,'),
(1064, 7, 110, 'Želi da vas istjera iz zemlje vaše, pa šta naređujete?"'),
(1065, 7, 111, 'Rekoše: "Zadrži njega i brata njegova, a pošalji u gradove sabirače,'),
(1066, 7, 112, 'Dovešće ti svakog čarobnjaka, znalca."'),
(1067, 7, 113, 'I dođoše faraonu čarobnjaci, rekoše: "Uistinu, nama pripada nagrada, ako mi budemo pobjednici."'),
(1068, 7, 114, 'Reče: "Da!, i uistinu, vi ćete biti od bliskih."'),
(1069, 7, 115, 'Rekoše: "O Musa! Ili ćeš baciti, ili ćemo mi biti (prvi) bacači?"'),
(1070, 7, 116, 'Reče: "Bacite!" Pa pošto baciše, začaraše oči ljudi i zastrašiše ih i donesoše sihr silan.'),
(1071, 7, 117, 'I objavismo Musau: "Baci štap svoj!" Tad gle: on proguta ono šta su lažirali.'),
(1072, 7, 118, 'Tad se dogodi Istina, i propade ono šta su uradili.'),
(1073, 7, 119, 'Tad bijahu tu poraženi, i okrenuše se poniženi.'),
(1074, 7, 120, 'I baciše se čarobnjaci na sedždu,'),
(1075, 7, 121, 'Rekoše: "Vjerujemo u Gospodara svjetova,'),
(1076, 7, 122, 'Gospodara Musaovog i Harunovog."'),
(1077, 7, 123, 'Faraon reče: "Povjerovaste u Njega prije nego sam vam dozvolio! Uistinu! Ovo je spletka koju ste ispleli u gradu da biste izveli iz njega stanovništvo njegovo; pa saznaćete!'),
(1078, 7, 124, 'Sigurno ću odsjeći ruke vaše i noge vaše unakrsno, zatim ću vas sigurno sve raspeti."'),
(1079, 7, 125, 'Rekoše: "Uistinu, mi smo Gospodaru našem povratnici!'),
(1080, 7, 126, 'A svetiš nam se samo što mi vjerujemo u znakove Gospodara našeg, pošto su nam došli. Gospodaru naš! Izlij na nas izdržljivost i daj da mi umremo (kao) muslimani."'),
(1081, 7, 127, 'I rekoše velikani iz naroda faraonovog: "Zar ćeš ostaviti Musaa i narod njegov da prave fesad na Zemlji, a ostaviće tebe i bogove tvoje?" Reče: "Pobit ćemo sinove njihove, a poštedit žene njihove, a uistinu smo mi nad njima pokoritelji."'),
(1082, 7, 128, 'Musa reče narodu svom: "Tražite pomoć od Allaha i strpite se. Uistinu, Zemlja je Allahova. Daje je u naslijeđe kome hoće od robova Svojih, a ishod je za bogobojazne."'),
(1083, 7, 129, 'Rekoše: "Zlostavljani smo prije no što si nam došao i nakon što si nam došao." Reče: "Možda će Gospodar vaš uništiti neprijatelja vašeg i učini vas nasljednicima na Zemlji, pa da vidi kako radite."'),
(1084, 7, 130, 'I doista smo pogodili narod faraonov nerodicama i smanjenjem plodova, da bi se oni poučili.'),
(1085, 7, 131, 'Pa kad bi im došlo dobro, rekli bi: "Ovo je naše", a kad bi ih snašlo zlo, pripisali bi Musau i onom ko je s njim. Uistinu! Zla kob njihova je kod Allaha! Međutim, većina njih ne zna.'),
(1086, 7, 132, 'I rekoše: "Kakav god nam doneseš znak da nas njime začaraš, pa nećemo ti mi vjerovati."'),
(1087, 7, 133, 'Pa poslasmo na njih potop i skakavce i uši i žabe i krv, znacima detaljnim, pa se uzoholiše i bijahu narod prijestupnika.'),
(1088, 7, 134, 'Pa pošto pade na njih nevolja, rekoše: "O Musa! Moli za nas Gospodara svog, zbog onog šta je obećao kod tebe. Ako otkloniš od nas nevolju, sigurno ćemo ti vjerovati i sigurno poslati s tobom sinove Israilove."'),
(1089, 7, 135, 'Pa pošto bismo otklonili od njih nevolju do roka kojem su oni stigli - gle! oni bi prekršili.'),
(1090, 7, 136, 'Tad smo im se osvetili i potopili ih u moru što su poricali ajete Naše i bili prema njima nehajni.'),
(1091, 7, 137, 'I dali smo u naslijeđe narodu onih koji su učinjeni slabima istoke zemlje i zapade njene - one u kojoj smo blagosiljali. I ispunila se Riječ Gospodara tvog lijepa nad sinovima Israilovim zato što su se strpili; a razorili smo šta je sagradio faraon i narod njegov i šta su podizali.'),
(1092, 7, 138, 'I prevedosmo sinove Israilove preko mora, te dođoše ljudima odanim idolima njihovim. Rekoše: "O Musa! Načini za nas boga kao što oni imaju bogove." Reče: "Uistinu, vi ste narod koji ne zna."'),
(1093, 7, 139, '"Uistinu, takvi će propasti u onom u čemu su oni, a zaludno je šta su radili.'),
(1094, 7, 140, 'Reče: "Zar mimo Allaha da vam tražim boga, a On vas je odlikovao nad svjetovima?"'),
(1095, 7, 141, 'I kad vas izbavismo od ljudstva faraonovog; podvrgavali su vas opakoj kazni, ubijajući sinove vaše, a pošteđujući žene vaše; a u tom je bilo iskušenje golemo od Gospodara vašeg.'),
(1096, 7, 142, 'I odredili smo Musau trideset noći, i dopunili to sa deset, pa se ispuni određeno vrijeme Gospodara njegovog - četrdeset noći; i reče Musa bratu svom Harunu: "Naslijedi me u narodu mom i sređuj (stvari) i ne slijedi put mufsida."'),
(1097, 7, 143, 'I pošto dođe Musa sastajalištu Našem i (pošto) je govorio s njim Gospodar njegov, reče: "Gospodaru moj! Pokaži mi - da Te vidim." Reče: "Nećeš Me vidjeti, međutim, pogledaj ka brdu: pa ako ono ostane na svom mjestu, vidjećeš Me." Pa pošto se Gospodar njegov otkri brdu učini ga zdrobljenim, a Musa pade onesviješten. Pa pošto se pribra, reče: "Slava neka je Tebi! Kajem Ti se i ja sam prvi od vjernika."'),
(1098, 7, 144, 'Reče: "O Musa! Uistinu, Ja sam odabrao tebe nad ljudima poslanstvom Svojim i govorom Svojim. Zato uzmi šta ti dajem i budi od zahvalnih."'),
(1099, 7, 145, 'I napisasmo mu na pločama savjet o svakoj stvari i detalj za svaku stvar. "Pa uzmi ih čvrsto i naredi svom narodu: \'Uzmite bolje u njima", (a) pokazaću vam kuću grješnika.'),
(1100, 7, 146, 'Okrenut ću od ajeta Svojih one koji se ohole na Zemlji bez prava. Iako vide svaki znak, ne vjeruju u njega; i ako vide put ispravnosti, ne uzimaju ga putem; a ako vide put zablude, uzimaju ga putem. To zato što oni poriču ajete Naše i prema njima nehajni su.'),
(1101, 7, 147, 'A oni koji poriču ajete Naše i susret Ahireta, propašće djela njihova. Hoće li biti plaćeni, osim za ono šta su radili?'),
(1102, 7, 148, 'I uzeo je narod Musaov poslije njega, od ukrasa svojih, tele tjelesno, imalo je ono (moć) mukanja. Zar nisu vidjeli da im ono ne govori, niti ih upućuje putu? Uzeli su ga i bili zalimi.'),
(1103, 7, 149, 'I pošto oni dođoše sebi i vidješe da su oni doista zabludjeli, rekoše: "Ako nam se ne smiluje Gospodar naš i (ne) oprosti nam, sigurno ćemo biti od gubitnika."'),
(1104, 7, 150, 'I pošto se vrati Musa narodu svom gnjevan, žalostan, reče: "Ružno je kako ste me zastupali, poslije (odlaska) mog. Da li biste ubrzali odredbu Gospodara vašeg?" I baci ploče i ščepa za glavu brata svog, tegleći ga sebi. (Harun) reče: "Sine majčin! Uistinu, ljudi su me smatrali slabim i gotovo da me ubiju, zato ne čini da se zluraduju nada mnom neprijatelji i ne čini da budem sa narodom zalima."'),
(1105, 7, 151, '(Musa) reče: "Gospodaru moj! Oprosti meni i bratu mom i uvedi nas u milost Svoju; a Ti si Najmilosrdniji od milosrdnih."'),
(1106, 7, 152, 'Uistinu, oni koji su uzeli tele: Stigla ih je srdžba od Gospodara njihovog i poniženje u životu Dunjaa. A tako kažnjavamo one koji izmišljaju.'),
(1107, 7, 153, 'A oni koji rade zla djela, potom se pokaju nakon toga i budu vjerovali, uistinu, Gospodar tvoj je poslije toga Oprosnik, Milosrdni.'),
(1108, 7, 154, 'I pošto se umiri Musaova srdžba, uze ploče, a u natpisu njihovom bijaše uputa i milost za one koji se Gospodara svog plaše.'),
(1109, 7, 155, 'I odabra Musa iz svog naroda sedamdeset ljudi za Naše određeno vrijeme. Pa pošto ih snađe potres, reče: "Gospodaru moj! Da si htio uništio bi ih ranije, i mene. Hoćeš li nas uništiti zbog onog šta su počinili glupaci naši? Ovo je samo iskušenje Tvoje, zabluđuješ njime koga hoćeš, a upućuješ koga hoćeš. Ti si Zaštitnik Naš, zato nam oprosti i smiluj nam se, a Ti si Najbolji od oprosnika.'),
(1110, 7, 156, 'I propiši nam na ovom Dunjau dobro, i na Ahiretu. Uistinu! Mi smo se uputili Tebi." Reče: "Kazna je Moja - pogodiću njome koga hoću, a milost Moja obuhvata svaku stvar." Zato ću je propisati onima koji se boje i daju zekat, i onima koji u ajate Naše vjeruju,'),
(1111, 7, 157, 'Onima koji slijede Poslanika, Vjerovjesnika ummijja, onog kojeg nalaze kod sebe zapisanog u Tewratu i Indžilu. Naređuje im dobro, a zabranjuje im nevaljalo, i dozvoljava im dobre stvari, a zabranjuje im loše, i oslobađa ih tereta njihovog i okova koji su bili na njima. Pa oni koji vjeruju u njega i poštuju ga, i pomažu ga i slijede svjetlo koje je spušteno s njim - ti takvi su oni koji će uspjeti.'),
(1112, 7, 158, 'Reci: "O ljudi! Doista sam ja poslanik Allahov vama svima - Onog čija je vlast nebesa i Zemlje. Samo je On Bog! Oživljava i usmrćuje, zato vjerujte u Allaha i Poslanika Njegovog, Vjerovjesnika neukog, koji vjeruje u Allaha i Riječi Njegove, i slijedite ga da biste se vi uputili."'),
(1113, 7, 159, 'A od naroda Musaovog je zajednica (onih koji) se vode Istinom i po njoj kroje pravdu.'),
(1114, 7, 160, 'I podijelili smo ih na dvanaest plemena, naroda; i objavili smo Musau - kad je narod njegov zatražio od njega vodu: "Udri štapom svojim kamen." Tad provri iz njega dvanaest izvora. Doista je svako pleme znalo pojilište svoje. I pravili smo hladovinu nad njima oblacima, i spustili na njih mennu i prepelice: "Jedite od dobrih stvari kojima smo vas opskrbili." A nisu učinili zulm Nama, nego su dušama svojim učinili zulm.'),
(1115, 7, 161, 'I kad im bi rečeno: "Nastanite se u ovom naselju i jedite iz njega odakle god hoćete i recite: \'Hitta\', i uđite na kapiju sa sedždom, oprostićemo vam greške vaše, povećaćemo (nagradu) dobročiniteljima."'),
(1116, 7, 162, 'Tad su oni od njih koji su činili zulm, zamijenili riječ drugačijom od one koja im je rečena, te smo poslali na njih kaznu iz neba, zbog toga što su zulm činili.'),
(1117, 7, 163, 'I pitaj ih o gradu koji je bio pored mora, kad su kršili sabat, kad su im dolazile ribe njihove na dan subote njihove - otvoreno, a na dan kad nisu svetkovali subotu, nisu im dolazile. Tako smo ih iskušavali, zato što su griješili.'),
(1118, 7, 164, 'I kad reče zajednica između njih: "Zašto savjetujete narod čiji će Allah uništilac biti ili mučitelj njihov kaznom žestokom?" Rekoše: "Pravdanje Gospodaru vašem i da bi se oni pobojali."'),
(1119, 7, 165, 'Pa pošto zaboraviše ono čim su opominjani, spasismo one koji su zabranjivali zlo, a dograbismo one koji su činili zulm kaznom žestokom, za ono što su griješili.'),
(1120, 7, 166, 'Pa pošto su bili zadrti spram onog što im je zabranjeno, rekosmo im: "Budite majmuni prezreni!"'),
(1121, 7, 167, 'I kad je najavio Gospodar tvoj da će sigurno do Dana kijameta dizati na njih onog ko će ih podvrgavati zloj kazni. Uistinu, Gospodar tvoj je brz kaznom, a uistinu, On je Oprosnik, Milosrdni.'),
(1122, 7, 168, 'I razdijelili smo ih po Zemlji (kao) narode. Neki od njih su dobri, a drugi od njih su mimo toga. I iskušavali smo ih dobrim stvarima i zlim stvarima, da bi se oni povratili.'),
(1123, 7, 169, 'Pa je poslije njih slijedilo pokoljenje (onih koji su) naslijedili Knjigu. Uzimali su prolaznu stvar Dunjaa i govorili: "Oprostiće nam se!" A ako im dođe (ponovo) stvar slična tom, uzimaju je. Zar nije od njih uzet zavjet Knjigom da će o Allahu govoriti samo Istinu? A proučavaju ono šta je u njoj; a kuća Ahireta je bolja za one koji se boje. Pa zar ne shvatate?'),
(1124, 7, 170, 'A oni koji se drže Knjige i obavljaju salat - uistinu, Mi nećemo uništiti nagradu pravednih.'),
(1125, 7, 171, 'I kad podigosmo brdo nad njima, kao da je ono nadstrešica, a mislili su da će ono na njih pasti. Uzmite čvrsto šta vam dajemo i podsjećajte se na ono šta je u tom, da biste se vi pobojali;'),
(1126, 7, 172, 'I kad uze Gospodar tvoj od sinova Ademovih, iz leđa njihovih potomstvo njihovo, i zatraži da oni svjedoče protiv duša svojih: "Zar nisam Gospodar vaš?" Rekoše: "Svakako. Svjedočimo!", da ne biste rekli na Dan kijameta: "Uistinu, mi smo prema ovom bili nehajni."'),
(1127, 7, 173, 'Ili rekli: "Samo su pridruživali očevi naši ranije, a (mi) smo bili potomstvo poslije njih. Pa zar ćeš nas uništiti zbog onog šta su počinili pokvarenjaci?"'),
(1128, 7, 174, 'I tako razlažemo znakove, i da bi se oni povratili.'),
(1129, 7, 175, 'I izrecituj im vijest onog kojem smo dali ajete Naše, pa se povukao od njih, te ga je slijedio šejtan, pa je bio od zalutalih.'),
(1130, 7, 176, 'A da smo htjeli, sigurno bismo ga uzdigli njima, međutim on se priklonuo zemlji i slijedio strast svoju. Pa, primjer njegov je kao primjer psa: ako se okomiš na njega, dahćući plazi jezik, ili ostaviš li ga - plazeći jezik dahće. To je primjer ljudi koji poriču ajete Naše; zato pričaj priču, da bi oni razmislili.'),
(1131, 7, 177, 'Loš je primjer ljudi koji poriču ajete Naše, a dušama svojim čine zulm.'),
(1132, 7, 178, 'Koga uputi Allah - pa on je upućen, a koga zavede - pa ti takvi su gubitnici.'),
(1133, 7, 179, 'A doista, stvorili smo za Džehennem mnoge od džinna i ljudi. Imaju oni srca - ne razumiju njima, i imaju oni oči - ne vide njima, i imaju oni uši - ne čuju njima. Takvi su kao stoka, naprotiv, oni su više zalutali. Ti takvi su nemarni.'),
(1134, 7, 180, 'A Allahova imena su najljepša - zato Ga zovite njima. I ostavite one koji skrnave imena Njegova. Platit će im se šta rade.'),
(1135, 7, 181, 'A od onog koga smo stvorili ima umma koja se vodi Istinom i njome kroji pravdu.'),
(1136, 7, 182, 'A oni koji poriču znakove Naše, vodićemo ih postepeno odakle neće znati.'),
(1137, 7, 183, 'I puštam im. Uistinu, spletka Moja je jaka!'),
(1138, 7, 184, 'Zar neće razmisliti? Nije u drugu njihovom nikakvo ludilo. On je samo opominjač jasni.'),
(1139, 7, 185, 'Zar neće pogledati carstvo nebesa i Zemlje i šta je stvorio Allah od stvari, i da se možda rok njihov već približio? Pa u koji će hadis poslije njega vjerovati?'),
(1140, 7, 186, 'Koga zabludi Allah, pa nema za njega upućivača; a ostavlja ih da u pretjeranosti svojoj lutaju.'),
(1141, 7, 187, 'Pitaju te o Času, kad će dolazak njegov? Reci: "Znanje njegovo je jedino kod Gospodara mog; otkriće ga u vrijeme njegovo samo On; biće težak na nebesima i Zemlji; doći će vam jedino iznenada." Pitaju tebe kao da si ti upoznat o njemu. Reci: "Znanje njegovo je jedino kod Allaha", međutim većina ljudi ne zna.'),
(1142, 7, 188, 'Reci: "Ne vladam za dušu svoju korišću, niti štetom, osim šta hoće Allah; a da znam nevidljivo, sigurno bih umnožio od dobrog i ne bi me dotaklo zlo. Ja sam samo opominjač i donosilac radosnih vijesti za narod koji vjeruje.'),
(1143, 7, 189, 'On je Taj koji vas je stvorio od duše jedne, i načinio od nje paricu njenu, da se smiri uz nju. Pa pošto ju je obljubio, ponijela je teret lahak, pa je išla s njim. Pa pošto oteža, zovnu Allaha, Gospodara njih dvoje: "Ako nam daš dobrog (potomka), sigurno ćemo biti od zahvalnih."'),
(1144, 7, 190, 'Pa pošto im je dao dobrog, pripisivali su Njemu drugove u onom šta im je dao. Pa uzvišen neka je Allah od onog šta pridružuju!'),
(1145, 7, 191, 'Zar pridružuju ono šta ne stvara ništa, a oni su stvoreni?'),
(1146, 7, 192, 'A ne mogu im pružiti pomoć, niti dušama svojim pomažu.'),
(1147, 7, 193, 'I ako ih pozovete Uputi, ne slijede vas. Isto vam je: pozivali ih ili vi bili šutljivi.'),
(1148, 7, 194, 'Uistinu! Oni koje zazivate mimo Allaha, robovi su slični vama. Zato ih prizivajte, pa nek vam se odazovu, ako istinu govorite!'),
(1149, 7, 195, 'Zar imaju oni noge, kojima hode? Ili imaju oni ruke, kojima drže? Ili imaju oni oči, kojima vide? Ili imaju oni uši, kojima čuju? Reci: "Zovite ortake svoje, zatim me prevarite, ta ne odgađajte mi."'),
(1150, 7, 196, 'Uistinu, Zaštitnik moj je Allah koji objavljuje Knjigu, a On prijateljuje s dobrima.'),
(1151, 7, 197, 'A oni koje prizivate mimo Njega, ne mogu vam pružiti pomoć, niti dušama svojim pomažu.'),
(1152, 7, 198, 'I ako ih pozovete Uputi, ne čuju; i vidiš ih gledaju prema tebi, a oni ne vide.'),
(1153, 7, 199, 'Drži se oprosta i naređuj dobročinstvo, i kloni se neznalica.'),
(1154, 7, 200, 'A ako te potakne podsticaj od šejtana, tad zatraži zaštitu u Allaha. Uistinu! On je Onaj koji čuje, Znalac.'),
(1155, 7, 201, 'Uistinu, oni koji se boje (Allaha), kad ih dotakne podsticaj od šejtana, sjete se, tad gle: oni oni koji vide!'),
(1156, 7, 202, 'A braća njihova ih navuku u zabludu, zatim ne odustaju.'),
(1157, 7, 203, 'I kad im ne doneseš znak, govore: "Zašto ga ne izmisliš?" Reci: "Slijedim samo ono šta mi se objavljuje od Gospodara mog. Ovo su uvidi od Gospodara vašeg, i Uputa i milost za narod koji vjeruje."'),
(1158, 7, 204, 'A kad se uči Kur\'an, tad ga slušajte i šutite, da bi vam se smilovalo.'),
(1159, 7, 205, 'I sjećaj se Gospodara svog u duši svojoj skrušeno i sa strahom i ne glasno u govoru, jutrom i navečer, i ne budi od nehajnih.'),
(1160, 7, 206, 'Uistinu, oni koji su kod Gospodara Tvog, ne ohole se prema obožavanju Njegovom, i slave Ga i Njemu čine sedždu.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Pitaju te o (ratnom) plijenu. Reci: "Plijen je Allahov i Poslanikov." Zato se bojte Allaha i sređujte (stvari) među sobom i poslušajte Allaha i Poslanika Njegovog, ako ste vjernici.'),
(1162, 8, 2, 'Vjernici su samo oni čija se srca, kad se spomene Allah, poboje, i kad im se uče ajeti Njegovi, povećavaju njihovo vjerovanje, i (koji) se u Gospodara svog pouzdavaju,'),
(1163, 8, 3, 'Oni koji obavljaju salat i iz onog čim smo ih opskrbili udjeljuju:'),
(1164, 8, 4, 'Ti takvi su vjernici istinski. Imaju oni stupnjeve kod Gospodara svog, i oprost i opskrbu plemenitu.'),
(1165, 8, 5, 'Kao što te je izveo Gospodar tvoj iz tvoje kuće s Istinom, a uistinu, skupina vjernika je bila nerada (tome).'),
(1166, 8, 6, 'Raspravljaju s tobom o Istini nakon što je postala jasna, kao da se gone ka smrti, a oni gledaju.'),
(1167, 8, 7, 'I kad vam je Allah obećao jednu od dvije skupine, da će ona biti vaša, a voljeli ste da ona bez bodlje (koja će ubosti) bude vaša; i želio je Allah da utvrdi Istinu Riječima Svojim i odsiječe korijen nevjernika,'),
(1168, 8, 8, 'Da utvrdi Istinu i uništi neistinu, makar mrzili prestupnici.'),
(1169, 8, 9, 'Kad ste tražili pomoć Gospodara vašeg, pa vam se odazvao: "Uistinu, Ja sam Pomagač vaš sa hiljadu meleka uzastopnih."'),
(1170, 8, 10, 'A učinio je to Allah jedino radosnom viješću i da umiri time srca vaša. A nema pomoći, izuzev od Allaha. Uistinu! Allah je Moćni, Mudri.'),
(1171, 8, 11, 'Kad je učinio da vas obuzme pospanost sigurnosti od Njega, i spustio na vas vodu s neba, da vas očisti njome i otkloni od vas prljavštinu šejtana i da učvrsti srca vaša i osnaži njome stopala.'),
(1172, 8, 12, 'Kad objavi Gospodar tvoj melecima: "Ja sam sa vama, zato učvrstite one koji vjeruju! Ubaciću strah u srca onih koji ne vjeruju; pa udrite iznad vratova i udrite njihov svaki prst.'),
(1173, 8, 13, 'To zato što su se oni opirali Allahu i Poslaniku Njegovom. A ko se suprotstavi Allahu i Poslaniku Njegovom - pa uistinu, Allah je žestok kaznom.'),
(1174, 8, 14, 'To, zato je iskusite - i da je za nevjernike (pripremljena) kazna vatrom.'),
(1175, 8, 15, 'O vi koji vjerujete! Kad sretnete one koji ne vjeruju (kako nastupaju) puzeći, tad im ne okrećite leđa.'),
(1176, 8, 16, 'A ko im okrene tog dana leđa svoja, izuzev povratnika u borbu ili onog ko se priključi formaciji, tad je doista navukao srdžbu od Allaha, i sklonište njegovo biće Džehennem; a loše je dolazište!'),
(1177, 8, 17, 'Tad ih niste ubijali, nego ih je Allah ubijao. I nisi bacio kad si bacio, nego je Allah bacio - i da bi iskušao vjernike od Sebe kušnjom lijepom. Uistinu! Allah je Onaj koji čuje, Znalac.'),
(1178, 8, 18, 'To; i da je Allah Oslabljivač spletke nevjernika.'),
(1179, 8, 19, 'Ako ste tražili presudu, pa već vam je došla presuda. A ako prestanete, pa to je bolje za vas. A ako se povratite, povratićemo se, i neće vam koristiti grupa vaša ništa, ma kako brojna bila; i (znajte) da je Allah uz vjernike.'),
(1180, 8, 20, 'O vi koji vjerujete! Poslušajte Allaha i Poslanika Njegovog, i ne okrećite se od njega, a vi čujete.'),
(1181, 8, 21, 'I ne budite kao oni koji kažu: "Čuli smo", a oni ne slušaju.'),
(1182, 8, 22, 'Uistinu! Najgore životinje kod Allaha su gluhi i nijemi - oni koji ne koriste razum.'),
(1183, 8, 23, 'A da zna Allah u njima dobro, sigurno bi ih pročuo. A da ih pročuje, sigurno bi se okrenuli i oni bi bili odvraćeni.'),
(1184, 8, 24, 'O vi koji vjerujete! Odazovite se Allahu i Poslaniku kad vas pozove onom šta vas oživljava, i znajte da Allah djeluje između čovjeka i srca njegovog, i da je On Taj kojem ćete biti sabrani.'),
(1185, 8, 25, 'I bojte se kušnje - ne pogađa isključivo one od vas koji čine zulm - i znajte da je Allah žestok kaznom.'),
(1186, 8, 26, 'I sjetite se kad ste vi bili mali (brojno), slabi na Zemlji, bojali ste se da vas ne pohvataju ljudi, pa vas je sklonuo i ojačao vas pomoći Svojom, i opskrbio vas dobrim stvarima, da biste vi zahvaljivali.'),
(1187, 8, 27, 'O vi koji vjerujete! Ne iznevjerite Allaha i Poslanika, i ne iznevjerite emanete svoje, a vi znate.'),
(1188, 8, 28, 'I znajte da su imeci vaši i djeca vaša iskušenje, i da je Allah Taj kod kojeg je nagrada veličanstvena.'),
(1189, 8, 29, 'O vi koji vjerujete! Ako se budete bojali Allaha, načiniće vam furkan i odstraniće od vas loša djela vaša, i oprostiće vam. A Allah je Posjednik dobrote velike.'),
(1190, 8, 30, 'I kad su se zavjerili protiv tebe oni koji ne vjeruju, da te zatvore ili te ubiju, ili te protjeraju; a spletkarili su i planirao je Allah; a Allah je Najbolji od planera.'),
(1191, 8, 31, 'I kad im se uče ajeti Naši, govore: "Već smo čuli. Da hoćemo, sigurno bismo govorili slično ovom. Ovo su samo priče ranijih."'),
(1192, 8, 32, 'I kad kažu: "O Allahu! Ako je to to, Istina od Tebe, onda izlij na nas kamenu kišu s neba ili nam daj kaznu bolnu!"'),
(1193, 8, 33, 'A neće ih Allah kazniti, budeš li ti među njima; i neće biti Allah mučitelj njihov, budu li oni tražili oprost.'),
(1194, 8, 34, 'I šta je njihovo da ih ne kazni Allah, a oni odbijaju od Mesdžidul-harama i nisu čuvari njegovi? Čuvari njegovi su samo bogobojazni; međutim većina njih ne zna.'),
(1195, 8, 35, 'A salat njihov kod Kuće je samo zviždanje i pljeskanje. Zato iskusite kaznu što ne vjerujete!'),
(1196, 8, 36, 'Uistinu, oni koji ne vjeruju, troše imetke svoje da odvraćaju od puta Allahovog. Pa potrošiće ih, potom će biti na njima žalost, zatim će biti poraženi. A oni koji ne vjeruju, ka Džehennemu će biti prikupljeni,'),
(1197, 8, 37, 'Da Allah razdvoji nevaljalog od dobrog, i stavi nevaljalog jednog na drugog, te to sve nagomila pa to stavi u Džehennem. Ti takvi su gubitnici.'),
(1198, 8, 38, 'Reci onima koji ne vjeruju, ako prestanu, oprostiće im se šta je već prošlo; a ako se povrate - pa već je prošao sunnet ranijih.'),
(1199, 8, 39, 'I borite se protiv njih, dok (više) ne bude fitneluka i ne bude sve vjera Allahova. Pa ako se okanu - pa uistinu, Allah je onog šta rade Vidilac.'),
(1200, 8, 40, 'A ako se okrenu - tad znajte da je Allah Zaštitnik vaš. Divan li je Zaštitnik i divan Pomagač!'),
(1201, 8, 41, 'I znajte, šta zaplijenite od stvari - da je tad petina Allahova i Poslanikova i onog ko je bližnji, i siročadi i siromaha i sina puta, ako vjerujete u Allaha i ono šta smo objavili robu Našem na Dan razlikovanja, dan (kad) su se susrele dvije skupine. A Allah nad svakom stvari ima moć.'),
(1202, 8, 42, 'Kad ste vi bili na bližoj strani (doline), a oni na daljoj strani i karavan niže vas. I da ste se dogovarali, ne biste se podudarili u vremenu. Međutim (susreli ste se) da bi izvršio Allah odredbu izvršnu; da bi poginuo ko gine od dokaza jasnog i preživio ko živi od dokaza jasnog. A uistinu! Allah je Onaj koji čuje, Znalac.'),
(1203, 8, 43, 'Kad ti ih je pokazao Allah u tvom snu malobrojnim, a da ti ih je pokazao mnogobrojnim, sigurno biste klonuli i sigurno biste se prepirali o naredbi. Međutim, Allah je spasio. Uistinu! On je Znalac grudi.'),
(1204, 8, 44, 'I kad vam ih je prikazao u vašim očima, kad ste se sreli, malobrojnima, i smanjio vas u očima njihovim - da bi izvršio Allah odredbu izvršnu; a Allahu se vraćaju stvari.'),
(1205, 8, 45, 'O vi koji vjerujete! Kad sretnete (neprijateljsku) grupu, tad budite čvrsti i spominjite Allaha mnogo, da biste vi uspjeli.'),
(1206, 8, 46, 'I poslušajte Allaha i Poslanika Njegovog, i ne prepirite se, pa da klonete i izgubi se borbenost vaša; i izdržite! Uistinu! Allah je uz izdržljive.'),
(1207, 8, 47, 'I ne budite kao oni koji su izašli iz kuća svojih obijesno i da vide ljudi, i (koji) odvraćaju od puta Allahovog. A Allah je Onaj koji obuhvata ono što rade.'),
(1208, 8, 48, 'I kad im uljepša šejtan poslove njihove i reče: "Nemate vi danas pobjednika od (strane) ljudi, a uistinu, ja sam komšija vaš." Pa pošto se ugledaše grupe dvije, ustuknu na petama svojim i reče: "Uistinu, ja sam slobodan od vas. Uistinu, ja vidim šta ne vidite! Uistinu, ja se bojim Allaha, a Allah je žestok kaznom."'),
(1209, 8, 49, 'Kad rekoše munafici i oni u srcima čijim je bolest: "Obmanula je ove vjera njihova." A ko se pouzda u Allaha - pa uistinu, Allah je Moćni, Mudri.'),
(1210, 8, 50, 'A da vidiš kad meleci budu usmrćivali one koji ne vjeruju, udarajući lica njihova i leđa njihova i: "Kušajte kaznu gorenjem!"'),
(1211, 8, 51, 'To zbog onog šta su pripravile ruke vaše i što Allah nije nikako zalim robovima.'),
(1212, 8, 52, 'Kao stanje naroda faraonovog i onih prije njih: Nisu vjerovali u ajete Allahove, pa ih je Allah ščepao zbog grijeha njihovih. Uistinu! Allah je Silan, žestok kaznom.'),
(1213, 8, 53, 'To zato što Allah neće biti Izmjenitelj blagodati kojom je obasuo narod, dok ne izmijenu ono šta je u dušama njihovim, i što je Allah Onaj koji čuje, Znalac.'),
(1214, 8, 54, 'Kao stanje naroda faraonovog i onih prije njih: Poricali su ajete Gospodara svog, pa smo ih uništili zbog grijeha njihovih i potopili ljudstvo faraonovo, a svi su bili zalimi.'),
(1215, 8, 55, 'Uistinu, najgore životinje kod Allaha su oni koji prikrivaju - pa oni ne vjeruju;'),
(1216, 8, 56, 'Oni s kojima si sporazum sklopio, zatim su svaki put kršili sporazum svoj, i oni se ne boje.'),
(1217, 8, 57, 'Pa ako naiđete na njih u ratu, tad rasturi s njima onog ko je iza njih - da bi se oni poučili.'),
(1218, 8, 58, 'I ako se od naroda doista bojiš izdaje, onda im baci istovjetno. Uistinu! Allah ne voli izdajnike.'),
(1219, 8, 59, 'I neka nikako ne misle oni koji ne vjeruju, da će preteći; uistinu, oni neće umaći.'),
(1220, 8, 60, 'I pripremite za njih šta možete od sile i konja vezanih. Preplašićete time neprijatelja Allahovog i neprijatelja vašeg i druge pored njih koje ne znate, Allah ih zna. A šta potrošite od stvari na putu Allahovom, biće vam nadoknađeno i vama se neće učiniti zulm.'),
(1221, 8, 61, 'A ako budu skloni miru, onda mu budi sklon, i pouzdaj se u Allaha. Uistinu! On, On je Onaj koji čuje, Znalac.'),
(1222, 8, 62, 'A ako zažele da te prevare, pa uistinu, dovoljan ti je Allah. On je Taj koji te pojačava pomoći Svojom i vjernicima.'),
(1223, 8, 63, 'I sjedinio je srca njihova. Da si potrošio sve što je na Zemlji, ne bi ujedinio srca njihova, međutim, Allah ih je ujedinio. Uistinu! On je Moćni, Mudri.'),
(1224, 8, 64, 'O vjerovjesniče! Dovoljan je tebi Allah i onom ko te slijedi od vjernika.'),
(1225, 8, 65, 'O Vjerovjesniče! Podstiči vjernike na borbu. Ako od vas bude dvadeset izdržljivih, pobijediće dvije stotine. A ako od vas bude stotinu izdržljivih, pobijediće hiljadu onih koji ne vjeruju, zato što su oni ljudi koji ne shvataju.'),
(1226, 8, 66, 'Sad vam je olakšao Allah i zna da je u vama slabost. Zato, ako od vas bude stotina izdržljiva, pobijediće dvije stotine. Pa ako od vas bude hiljada, pobijediće dvije hiljade s dozvolom Allahovom; a Allah je uz izdržljive.'),
(1227, 8, 67, 'Nije za Vjerovjesnika da on imadne zarobljenike, dok ne pobijedi na Zemlji. Želite prolazno dobro Dunjaa, a Allah želi Ahiret; a Allah je Moćni, Mudri.'),
(1228, 8, 68, 'Da nije Knjiga od Allaha prethodila, sigurno bi vas dotakla u onom šta ste uzeli, kazna strahovita.'),
(1229, 8, 69, 'Pa jedite od onog šta ste zaplijenili (kao) dozvoljeno, dobro, i bojte se Allaha. Uistinu! Allah je Oprosnik, Milosrdni.'),
(1230, 8, 70, 'O Vjerovjesniče! Reci onom ko je u vašim rukama od zarobljenika: "Ako zna Allah u srcima vašim (ikakvo) dobro, daće vam bolje od onog šta je uzeto od vas, i oprostiće vam." A Allah je Oprosnik, Milosrdni.'),
(1231, 8, 71, 'A ako zažele izdaju tebi - pa već su izdali Allaha prije, zato je dao moć nad njima; a Allah je Znalac, Mudri.'),
(1232, 8, 72, 'Uistinu, oni koji vjeruju i isele se i bore imecima svojim i dušama svojim na putu Allahovom, i koji daju utočište i pomažu - takvi su zaštitnici jedni drugih. A koji vjeruju i ne isele se, nemate vi sa zaštitom njihovom ništa, dok se ne isele; a ako zatraže od vas pomoć u vjeri, tad je na vama pomaganje, izuzev protiv ljudi s kojima imate sporazum; a Allah je onog šta radite Vidilac.'),
(1233, 8, 73, 'A oni koji ne vjeruju, zaštitnici su jedni drugima. Ako to ne učinite, biće fitneluk na Zemlji i fesad veliki.'),
(1234, 8, 74, 'A oni koji vjeruju i isele se i bore na putu Allahovom, i koji daju utočište i pomažu - takvi su vjernici istinski. Imaće oni oprost i opskrbu plemenitu.'),
(1235, 8, 75, 'A oni koji povjeruju kasnije i isele se i bore s vama - pa takvi su od vas; a srodnici - oni su bliži jedni drugima u Knjizi Allahovoj. Uistinu! Allah je o svakoj stvari Znalac.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Sloboda je od Allaha i Poslanika Njegovog onima od mušrika s kojima ste sklopili sporazum:'),
(1237, 9, 2, 'Zato putujte po Zemlji četiri mjeseca i znajte da vi niste ti koji će umaći Allahu i da je Allah Taj koji će postiditi nevjernike.'),
(1238, 9, 3, 'A obznana je od Allaha i Poslanika Njegovog ljudima na dan hadždža najvećeg, je da je Allah slobodan od mušrika i Poslanik Njegov. Pa ako se pokajete, pa to je bolje za vas, a ako se okrenete, tad znajte da vi niste ti koji će umaći Allahu; a obraduj one koji ne vjeruju kaznom bolnom:'),
(1239, 9, 4, 'Izuzev onih mušrika s kojima ste sklopili sporazum, potom vam nisu umanjili ništa i nisu pomagali protiv vas nikoga - onda im ispunite sporazum njihov do roka njihovog. Uistinu! Allah voli bogobojazne.'),
(1240, 9, 5, 'Pa kad izmaknu sveti mjeseci, tad ubijajte mušrike gdje god ih nađete, i ščepajte ih i opsjedajte ih i postavljajte im se u svaku zasjedu. Pa ako se pokaju i budu obavljali salat i davali zekat, onda oslobodite put njihov. Uistinu! Allah je Oprosnik, Milosrdni.'),
(1241, 9, 6, 'A ako neko od mušrika zatraži od tebe zaštitu, tad ga zaštiti dok ne čuje Riječ Allahovu, potom ga sprovedi sigurnom mjestu njegovom; to zato što su oni ljudi koji ne znaju.'),
(1242, 9, 7, 'Kako da bude za mušrike sporazum kod Allaha i kod Poslanika Njegovog, izuzev onih s kojima ste sklopili sporazum kod Mesdžidul-harama? Pa dok (oni) budu pravi vama, to budite (i vi) pravi njima. Uistinu! Allah voli bogobojazne.'),
(1243, 9, 8, 'Kako - a ako bi vas nadvladali, ne bi cijenili među vama srodstvo niti ugovor? Zadovoljavaće vas ustima svojim, a odbijaće srca njihova, a većina njih su grješnici.'),
(1244, 9, 9, 'Prodaju ajete Allahove za vrijednost malu te odvraćaju od puta Njegovog. Uistinu je loše ono što oni rade.'),
(1245, 9, 10, 'Ne cijene kod vjernika srodstvo, niti ugovor. Ti takvi su prekršitelji.'),
(1246, 9, 11, 'Pa ako se pokaju i budu obavljali salat i davali zekat, tad su braća vaša u vjeri; a razlažemo znakove ljudima koji znaju.'),
(1247, 9, 12, 'A ako prekrše zakletve svoje nakon sporazuma njihovog, i budu li vrijeđali vjeru vašu, onda se borite protiv vođa nevjerovanja. Uistinu, nema zakletvi za njih - da bi se oni okanili.'),
(1248, 9, 13, 'Zar se nećete boriti protiv ljudi koji su prekršili zakletve svoje i namjeravali protjerivanje Poslanika, i počeli (protiv) vas prvi put? Zar ih se plašite? Pa Allah je dostojniji da se plašite Njega, ako ste vjernici.'),
(1249, 9, 14, 'Borite se protiv njih! Kazniće ih Allah rukama vašim i poniziti ih, i pomoći vas protiv njih i izliječiti grudi naroda vjernika,'),
(1250, 9, 15, 'I odstraniće srdžbu srca njihovih, i oprostiće Allah kome hoće. A Allah je Znalac, Mudri.'),
(1251, 9, 16, 'Zar mislite da ćete biti ostavljeni a (da) ne zna Allah one od vas koji su se borili i nisu uzimali bliskog prijatelja mimo Allaha i Poslanika Njegovog i vjernika? A Allah je o onom šta radite Obaviješteni.'),
(1252, 9, 17, 'Nije za mušrike da održavaju mesdžide Allahove, svjedoke protiv duša njihovih u nevjerovanju. Takvima će propasti djela njihova i u vatri će oni biti vječito.'),
(1253, 9, 18, 'Jedino će održavati mesdžide Allahove onaj ko vjeruje u Allaha i Dan posljednji i obavlja salat i daje zekat, i ne boji se, izuzev Allaha. Pa možda takvi budu od upućenih.'),
(1254, 9, 19, 'Zar držite napajanje hadžije i održavanje Mesdžidul-harama, (istim) kao onog ko vjeruje u Allaha i Dan posljednji i bori se na putu Allahovom? Nisu isti kod Allaha; a Allah ne upućuje narod zalima.'),
(1255, 9, 20, 'Oni koji vjeruju i isele se, i bore na putu Allahovom imecima svojim i dušama svojim, većeg su stepena kod Allaha; a ti takvi su oni koji će uspjeti.'),
(1256, 9, 21, 'Obradovaće ih Gospodar njihov milošću od Sebe i zadovoljstvom i baščama; imaće oni u njima uživanje trajno.'),
(1257, 9, 22, 'Vječno će biti u njima, zauvijek. Uistinu, Allah - kod Njega je nagrada veličanstvena.'),
(1258, 9, 23, 'O vi koji vjerujete! Ne uzimajte očeve svoje i braću svoju prijateljima, ako više vole nevjerstvo od vjerovanja. A ko od vas s njima prijateljuje - pa ti takvi su zalimi.'),
(1259, 9, 24, 'Reci: "Ako su očevi vaši i sinovi vaši, i braća vaša i žene vaše, i rod vaš i imeci koje ste stekli, i trgovina čije se stagnacije plašite i stanovi s kojima ste zadovoljni - draži vama od Allaha i Poslanika Njegovog i džihada na putu Njegovom: Onda sačekajte dok Allah ne donese naredbu Svoju", a Allah ne upućuje narod grješnika.'),
(1260, 9, 25, 'Doista vas je pomogao Allah na poljima mnogim, i na dan Hunejna, kad vas je zanijela brojnost vaša, pa vam nije ništa koristila i otješnjala vam Zemlja ma kako prostrana bila, zatim ste se okrenuli bježeći;'),
(1261, 9, 26, 'Potom je spustio Allah smirenost Svoju na Poslanika Svog i na vjernike, i spustio vojske koje niste vidjeli i kaznio one koji nisu vjerovali, a to je plaća nevjernika.'),
(1262, 9, 27, 'Zatim je Allah oprostio nakon toga kome je htio; a Allah je Oprosnik, Milosrdni.'),
(1263, 9, 28, 'O vi koji vjerujete! Samo su mušrici nečist. Zato nek se ne približuju Mesdžidul-haramu nakon ove njihove godine. A ako se bojite oskudice, pa učiniće vas Allah imućnim iz blagodati Svoje, ako htjedne. Uistinu! Allah je Znalac, Mudri.'),
(1264, 9, 29, 'Borite se protiv onih koji ne vjeruju u Allaha niti u Dan posljednji i ne zabranjuju šta je zabranio Allah i Poslanik Njegov, i ne ispovijedaju Vjeru Istine - između onih kojima je data Knjiga - dok ne dadnu džizju iz ruke i oni budu potčinjeni.'),
(1265, 9, 30, 'A govore jevreji: "Uzejr je sin Allahov"; i govore kršćani: "Mesih je sin Allahov." To je govor njihov - usta njihovih, oponašaju govor onih koji nisu vjerovali ranije. Ubio ih Allah! Kako se odvraćaju!'),
(1266, 9, 31, 'Uzimaju rabine svoje i monahe svoje gospodima mimo Allaha, i Mesiha, sina Merjemina. A naređeno im je samo da obožavaju Boga Jedinog, nema boga osim Njega. Slavljen neka je On od onog šta pridružuju!'),
(1267, 9, 32, 'Žele da ugase Allahovu svjetlost ustima svojim, a odbija Allah - izuzev da usavrši svjetlo Svoje, makar mrzili nevjernici.'),
(1268, 9, 33, 'On je Taj koji je poslao Poslanika Svog s Uputom i Vjerom Istine, da ona prevlada nad vjerom svakom, makar mrzili mušrici.'),
(1269, 9, 34, 'O vi koji vjerujete! Uistinu, mnogi od rabina i monaha jedu imanja ljudi zaludno i odvraćaju od puta Allahovog. A koji gomilaju zlato i srebro i ne troše ga na putu Allahovom - pa obraduj ih kaznom bolnom.'),
(1270, 9, 35, 'Na Dan (kad) se ono bude užarilo u vatri Džehennema, tad će biti žigosana njime čela njihova i bokovi njihovi i leđa njihova: "Ovo je to šta ste gomilali dušama svojim, zato kušajte šta ste gomilali."'),
(1271, 9, 36, 'Uistinu! Broj mjeseci kod Allaha je dvanaest mjeseci u Knjizi Allahovoj, na Dan kad je stvorio nebesa i Zemlju. Od njih su četiri sveta: to je vjera prava. Zato ne činite u njima zulm dušama vašim; i borite se protiv mušrika svi, kao što se bore protiv vas svi; i znajte da je Allah sa bogobojaznima.'),
(1272, 9, 37, 'Zaista je odlaganje (svetog mjeseca) povećanje nevjerovanja, zavode se time oni koji ne vjeruju. Dozvoljavaju ga godinu, a zabranjuju ga godinu, da udese broj koji je zabranio Allah, te dozvoljavaju šta je zabranio Allah. Uljepšano im je zlo poslova njihovih, a Allah ne upućuje narod nevjernika.'),
(1273, 9, 38, 'O vi koji vjerujete! [ta vam je kad vam se kaže: "Nastupajte na putu Allahovom", priklonite se zemlji? Zadovoljavate li se životom Dunjaa (više) od Ahireta? Pa užitak života Dunjaa u (odnosu na) Ahiret samo je mali.'),
(1274, 9, 39, 'Ako ne nastupite, kazniće vas kaznom bolnom i zamijeniti vas narodom drugačijim od vas, a nećete Ga oštetiti nimalo; a Allah nad svakom stvari ima moć.'),
(1275, 9, 40, 'Ako ga ne pomognete, pa doista ga je pomogao Allah kad su ga protjerali oni koji ne vjeruju, drugog od dvojice - kad su njih dvojica bili u pećini, kad reče drugu svom: "Ne žalosti se! Uistinu, Allah je s nama." Tad je spustio Allah smirenost Svoju na njega i pojačao ga vojskama koje niste vidjeli. I učinio je riječ onih koji ne vjeruju, najdonjom, a Riječ Allahova - ona je gornja; a Allah je Moćni, Mudri.'),
(1276, 9, 41, 'Nastupajte lahki i teški i borite se imecima vašim i dušama vašim na putu Allahovom! To je bolje za vas, kad biste znali.'),
(1277, 9, 42, 'Da je bio dobitak blizak i pohod kratak, sigurno bi te slijedili, međutim, bio im je dalek, težak. I zaklet će se Allahom: "Da smo mogli, sigurno bismo izašli s vama." Uništavaju duše svoje, a Allah zna da su oni sigurno lašci.'),
(1278, 9, 43, 'Allah ti oprostio! Zašto si im dozvolio dok ti ne bude jasno koji govore Istinu i ne saznaš lažljivce?'),
(1279, 9, 44, 'Neće tražiti dozvolu od tebe oni koji vjeruju u Allaha i Dan posljednji, da se bore imecima svojim i dušama svojim; a Allah je Znalac bogobojaznih.'),
(1280, 9, 45, 'Od tebe će jedino tražiti dozvolu oni koji ne vjeruju u Allaha i Dan posljednji, a sumnjaju srca njihova, pa se u sumnji svojoj kolebaju.'),
(1281, 9, 46, 'A da su željeli izlazak, sigurno bi za to spremili opremu. Međutim, Allah je mrzio dizanje njihovo, pa ih je zadržao, i rečeno je: "Sjedite sa onima koji sjede!"'),
(1282, 9, 47, 'Da su izašli s vama, povećali bi vam samo metež i sigurno bi hitali između vas tražeći vam fitneluk; a među vama su oni koji njih slušaju; a Allah je Znalac zalima.'),
(1283, 9, 48, 'Doista su tražili fitneluk ranije i rovarili ti stvari, dok nije došla Istina i prevladala odredba Allahova, a oni su ti koji mrze.'),
(1284, 9, 49, 'A od njih je ko govori: "Dopusti mi i ne iskušavaj me." Doista su u iskušenje pali! A uistinu, Džehennem će biti okružilac nevjernika.'),
(1285, 9, 50, 'Ako te zadesi dobro, žao im je; a ako te pogodi nesreća, govore: "Već smo uzeli stvar našu ranije", i okreću se i oni se raduju.'),
(1286, 9, 51, 'Reci: "Pogodiće nas samo ono šta nam je Allah propisao." On je Zaštitnik naš, i u Allaha zato neka se pouzdavaju vjernici!'),
(1287, 9, 52, 'Reci: "Očekujete li nam (išta drugo) sem jedno od dva dobra djela? A mi očekujemo za vas, da vas pogodi Allah kaznom od Sebe ili rukama našim. Zato čekajte. Uistinu, mi smo oni koji sa vama čekaju."'),
(1288, 9, 53, 'Reci: "Trošili milom ili silom, neće se primiti od vas. Uistinu, vi ste narod grješnika."'),
(1289, 9, 54, 'A sprječava da davanja njihova od njih budu primljena jedino što oni ne vjeruju u Allaha i Poslanika Njegovog, i ne pristupaju salatu osim kao lijenčine, niti udjeljuju, osim nerado.'),
(1290, 9, 55, 'Zato nek te ne oduševljavaju imeci njihovi, niti djeca njihova. Allah samo želi da ih kazni njima u životu Dunjaa, i da nestanu duše njihove, a oni budu nevjernici.'),
(1291, 9, 56, 'I zaklinju se Allahom (da) su uistinu od vas, a nisu oni od vas, nego su oni narod koji strahuje.'),
(1292, 9, 57, 'Ako bi našli utočište ili pećine ili skrovište, sigurno bi mu se okrenuli i oni bi odjurili.'),
(1293, 9, 58, 'I od njih je ko te kori zbog sadaka. Pa ako im se da od tog, zadovoljni su, a ako im se ne da od tog, gle! oni se ljute.'),
(1294, 9, 59, 'A da su se oni zadovoljili onim šta im je dao Allah i Poslanik Njegov i rekli: "Dosta nam je Allah. Daće nam Allah iz blagodati Svoje i Poslanik Njegov. Uistinu, mi smo oni koji žude Allahu..."'),
(1295, 9, 60, 'Sadake su jedino za fukare i siromahe i one koji rade na njima i one čija srca treba pridobiti, i za zarobljenike i zadužene, i za put Allahov i sina puta, odredbom od Allaha; a Allah je Znalac, Mudri.'),
(1296, 9, 61, 'I od njih su oni koji vrijeđaju Vjerovjesnika i govore: "On je uho." Reci: "Uho je dobro za vas, vjeruje u Allaha i vjeruje vjernicima, i milost je onima od vas koji vjeruju." A koji vrijeđaju Poslanika Allahovog, imaće oni kaznu bolnu.'),
(1297, 9, 62, 'Zaklinju vam se Allahom da bi vas zadovoljili, a Allah i Poslanik Njegov su dostojniji da Njega zadovolje, ako su vjernici.'),
(1298, 9, 63, 'Zar ne znaju da onaj ko se suprotstavlja Allahu i Poslaniku Njegovom - ta da će on imati vatru Džehennema, vječno će biti u njoj? To je poniženje užasno!'),
(1299, 9, 64, 'Plaše se munafici da će im se objaviti sura obavještavajući šta je u srcima njihovim. Reci: "Izrugujte se. Uistinu, Allah je Taj koji će iznijeti ono čega se bojite."'),
(1300, 9, 65, 'A ako ih upitaš, sigurno će reći: "Samo smo pričali i igrali se." Reci: "Zar ste se Allahom i ajetima Njegovim i Poslanikom Njegovim izrugivali?"'),
(1301, 9, 66, 'Ne pravdajte se! Doista niste vjerovali nakon vjerovanja vašeg. Ako izbrišemo (grijehe) grupi vas, kaznićemo grupu, zato što su bili prestupnici.'),
(1302, 9, 67, 'Munafici i munafikinje - oni su jedni od drugih. Naređuju nevaljalo, a zabranjuju dobro i stišću ruke svoje. Zaboravili su Allaha, zato je zaboravio njih. Uistinu, licemjeri - oni su grješnici.'),
(1303, 9, 68, 'Obećao je Allah munaficima i munafikinjama i nevjernicima vatru Džehennema, vječno će biti u njoj. Ona im je dovoljna; a prokleo ih je Allah, i imaće oni kaznu trajnu.'),
(1304, 9, 69, 'Kao oni prije vas; bili su žešći od vas u sili, i veći imecima i djecom, pa su uživali u udjelu njihovom - pa uživajte u udjelu vašem, kao što su uživali oni prije vas u udjelu njihovom - i raspravljate kao što su oni raspravljali. Tim takvima će propasti djela njihova na Dunjau i Ahiretu; a ti takvi su gubitnici.'),
(1305, 9, 70, 'Zar im nije stigla vijest onih prije njih: naroda Nuhovog, i Ada i Semuda, i naroda Ibrahimovog, i stanovnika Medjena i Mu\'tefikata? Dolazili su im poslanici njihovi sa dokazima jasnim. Pa nije Allah bio za činjenje zulma njima, nego su (oni) dušama svojim zulm činili.'),
(1306, 9, 71, 'A vjernici i vjernice - oni su zaštitnici jedni drugima. Naređuju dobro, a zabranjuju nevaljalo, i obavljaju salat i daju zekat, i slušaju Allaha i Poslanika Njegovog. Tim takvima će se Allah smilovati. Uistinu! Allah je Moćni, Mudri.'),
(1307, 9, 72, 'Allah je obećao vjernicima i vjernicama bašče ispod kojih teku rijeke, vječno će biti u njima, i stanove divne u baščama Adna. A zadovoljstvo od Allaha biće najveće. To je to, uspjeh veličanstven!'),
(1308, 9, 73, 'O Vjerovjesniče! Bori se protiv nevjernika i munafika, i budi strog prema njima. A sklonište njihovo biće Džehennem, a loše je dolazište!'),
(1309, 9, 74, 'Kunu se Allahom da nisu govorili, a doista su govorili riječ nevjerstva i nisu vjerovali nakon Islama njihovog i namjeravali ono šta nisu postigli. A svetili su se samo što ih je obogatio Allah i Poslanik Njegov iz blagodati Svoje. Pa ako se pokaju, bolje je za njih; a ako se okrenu, kazniće ih Allah kaznom bolnom na Dunjau i Ahiretu, i neće oni na Zemlji imati nikakva zaštitnika niti pomagača.'),
(1310, 9, 75, 'I od njih je ko se obavezao Allahu: "Ako nam da iz blagodati Svoje, sigurno ćemo davati sadaku i sigurno ćemo biti od dobrih."'),
(1311, 9, 76, 'Pa pošto im je dao iz blagodati Svoje, škrtarili su u tom i okrenuli se, i oni su odvraćeni;'),
(1312, 9, 77, 'Pa im je u srcima njihovim prouzrokovao licemjerstvo do Dana kad će Ga sresti, zato što su Allahu iznevjerili ono što su Mu obećali, i što su lagali.'),
(1313, 9, 78, 'Zar ne znaju da Allah zna tajnu njihovu i sašaptavanje njihovo, i da je Allah Znalac nevidljivih?'),
(1314, 9, 79, 'Oni koji kleveću dobrovoljce od vjernika u sadakama, i one koji (ih) ne nalaze, izuzev truda njihovog, pa ih ismijavaju - ismijava Allah njih, a imaće oni kaznu bolnu,'),
(1315, 9, 80, 'Tražio oprost za njih ili ne tražio... ako tražiš oprost za njih sedamdeset puta... pa neće im oprostiti Allah. To zato što ne vjeruju u Allaha i Poslanika njegovog, a Allah ne upućuje narod grješnika.'),
(1316, 9, 81, 'Radovali su se ostavljeni u počivaljkama svojim, nasuprot Poslaniku Allahovom, i mrzili da se bore imecima svojim i dušama svojim na putu Allahovom, i govorili: "Ne nastupajte po žezi!" Reci: "Vatra Džehennema je žešća žegom", kad bi razumjeli!'),
(1317, 9, 82, 'Zato nek se smiju malo, a neka plaču mnogo; plaća za ono šta su zaradili.'),
(1318, 9, 83, 'Pa ako te vrati Allah skupini njih, te zatraže dozvolu od tebe za izlazak, tad reci: "Nećete izaći sa mnom nikada, i nećete se boriti sa mnom (protiv) neprijatelja. Uistinu, vi ste bili zadovoljni sjedenjem prvi put, zato sjedite sa onima koji ostaju."'),
(1319, 9, 84, 'I ne obavi salat ni nad jednim od njih (koji) umre, nikada, i ne stoj nad kabrom njegovim. Uistinu, oni nisu vjerovali u Allaha i Poslanika Njegovog, i umrli su, a oni su bili grješnici.'),
(1320, 9, 85, 'I neka te ne zadivljuju imanja njihova i djeca njihova! Allah samo želi da ih kazni njima na Dunjau, i da nestanu duše njihove, a oni budu nevjernici.'),
(1321, 9, 86, 'A kad se objavi sura: "Vjerujte u Allaha i borite se uz Poslanika Njegovog!", traže posjednici obilja između njih, dozvolu od tebe, i govore: "Ostavi nas, bićemo sa onima koji sjede."'),
(1322, 9, 87, 'Zadovoljni su da budu sa onima koje ostaju, a zapečaćena su srca njihova, pa oni ne shvataju.'),
(1323, 9, 88, 'Međutim, Poslanik i oni koji vjeruju s njim, bore se imecima svojim i dušama svojim; a ti takvi će imati dobre stvari i ti takvi će biti uspješni.'),
(1324, 9, 89, 'Pripremio je Allah za njih bašče ispod kojih teku rijeke, vječno će biti u njima; to je uspjeh veličanstven.'),
(1325, 9, 90, 'I dolazili su oni koji se pravdaju od beduina, da im se dozvoli, i sjedili su oni koji su lagali Allahu i Poslaniku Njegovom. Pogodiće one od njih, koji nisu vjerovali, kazna bolna.'),
(1326, 9, 91, 'Nema ukora slabima, niti bolesnima, ni onima koji ne nalaze šta bi utrošili, kad su iskreni Allahu i Poslaniku Njegovom. Nema protiv dobročinitelja nikakva puta. A Allah je Oprosnik, Milosrdni.'),
(1327, 9, 92, 'Niti onima kojima, kad ti dođu da ih prebaciš, kažeš: "Ne nalazim ono na čemu bih vas prevezao." Okreću se, a oči njihove oblivene suzama, tužni što ne nalaze šta bi utrošili.'),
(1328, 9, 93, 'Samo je put protiv onih koji traže dozvolu od tebe, a oni su bogati. Zadovoljavaju se da budu sa onima koje ostaju, a zapečatio je Allah srca njihova, pa oni ne znaju.'),
(1329, 9, 94, 'Pravdaju vam se kad im se vratite. Reci: "Ne pravdajte se, nećemo vam vjerovati. Već nas je obavijestio Allah o novostima vašim; a vidjeće Allah rad vaš i Poslanik Njegov, zatim ćete se vratiti Znalcu nevidljivog i vidljivog, pa će vas obavijestiti o onom šta ste radili."'),
(1330, 9, 95, 'Zaklinjaće vam se Allahom kad im se vratite, da biste ih se okanili. Zato ih se okanite. Uistinu, oni su prljavština; a sklonište njihovo biće Džehennem; plaća za ono šta su zaradili.'),
(1331, 9, 96, 'Kunu vam se da se njima zadovoljite, pa ako se zadovoljite njima - pa uistinu, Allah se ne zadovoljava narodom grješnika.'),
(1332, 9, 97, 'Beduini su žešći nevjerovanjem i licemjerstvom i priličniji da ne znaju granice onog šta je objavio Allah Poslaniku Svom; a Allah je Znalac, Mudri.'),
(1333, 9, 98, 'A od beduina je ko uzima ono šta utroši, globom, i očekuju vama daire; na njima je daira zla; a Allah je Onaj koji čuje, Znalac.'),
(1334, 9, 99, 'I od beduina (ima) ko vjeruje u Allaha i Dan posljednji, i uzima ono šta troši blizinama kod Allaha i salatima Poslanika. Uistinu je to za njih približavanje! Daće Allah da oni uđu u milost Njegovu; uistinu, Allah je Oprosnik, Milosrdni.'),
(1335, 9, 100, 'A prethodnici, prvi od muhadžira i ensarija, i oni koji su ih slijedili u dobročinstvu - zadovoljan je Allah njima i zadovoljni su Njime, a pripremio im je bašče ispod kojih teku rijeke, vječno će biti u njima, zauvijek. To je uspjeh veličanstven!'),
(1336, 9, 101, 'A između onog ko je oko vas od beduina, (ima) munafika. A od stanovnika Medine (ima) ustrajnih u munafikluku, ne znate ih; Mi ih znamo; kaznićemo ih dva puta, potom će biti vraćeni kazni užasnoj.'),
(1337, 9, 102, 'A drugi su priznali grijehe svoje, pomiješali su dobro djelo i drugo loše. Možda će Allah da im oprosti; uistinu, Allah je Oprosnik, Milosrdni.'),
(1338, 9, 103, 'Uzmi iz imetaka njihovih sadaku, čisteći ih i oplemenjujući ih njome, i obavi salat za njih. Uistinu, tvoj salat je smirenje za njih; a Allah je Onaj koji čuje, Znalac.'),
(1339, 9, 104, 'Zar ne znaju da Allah - On prima pokajanje od robova Svojih i uzima sadake, i da je Allah - On Primalac pokajanja, Milosrdni.'),
(1340, 9, 105, 'I reci: "Radite! Ta vidjeće Allah rad vaš i Poslanik Njegov i vjernici; a vratićete se Znalcu nevidljivog i vidljivog, pa će vas obavijestiti o onom šta ste radili."'),
(1341, 9, 106, 'I drugi koji iščekuju naredbu Allahovu, da li će ih kazniti ili im oprostiti; a Allah je Znalac, Mudri.'),
(1342, 9, 107, 'A oni koji su uzeli mesdžid nesrećom i nevjerovanjem i razdvajanjem među vjernicima, i osmatračnicom za onog ko je ratovao (protiv) Allaha i Poslanika Njegovog ranije, sigurno će se zakleti: "Željeli smo samo dobro", a Allah svjedoči da su oni uistinu lašci.'),
(1343, 9, 108, 'Ne stani u njega nikada! Sigurno je mesdžid utemeljen na bogobojaznosti od prvog dana dostojniji da staneš u njega. U njemu su ljudi (koji) vole da se čiste; a Allah voli one koji se čiste.'),
(1344, 9, 109, 'Pa da li je onaj koji utemelji građevinu svoju na bogobojaznosti od Allaha i zadovoljstvu - bolji, ili onaj ko utemelji građevinu svoju na rubu podlokane obale rušeće, pa da se surva s njim u vatru Džehennema? A Allah ne upućuje narod zalima.'),
(1345, 9, 110, 'Neće prestati građevina njihova koju su podigli biti (izvor) sumnje u srcima njihovim, izuzev da se sasjeku srca njihova; a Allah je Znalac, Mudri.'),
(1346, 9, 111, 'Uistinu! Allah je kupio od vjernika duše njihove i imetke njihove, što će oni imati Džennet. Bore se na putu Allahovom, pa ubijaju i budu ubijeni. Obećanje Njegovo je istinito u Tewratu i Indžilu i Kur\'anu. A ko je vjerniji zavjetu svom od Allaha? Zato se radujte pogodbi svojoj koju ste ugovorili s Njim - a to je to, uspjeh veličanstven -'),
(1347, 9, 112, 'Pokajanici, obožavatelji, hvalitelji, postači, oni koji čine ruku\', koji padaju na sedždu, naređivači dobrog i sprečavatelji nevaljalog i čuvari granica Allahovih; a obraduj vjernike!'),
(1348, 9, 113, 'Nije za Vjerovjesnika i one koji vjeruju da traže oprost mušricima, makar bili rodbina, nakon što im bude jasno da su oni stanovnici džehima.'),
(1349, 9, 114, 'A Ibrahimovo traženje oprosta za oca svog je samo zbog obećanja kojeg mu je dao. Pa pošto mu je bilo jasno da je on neprijatelj Allahov, oslobodio ga se. Uistinu! Ibrahim je bio osjećajan, blag.'),
(1350, 9, 115, 'A neće Allah zabluditi ljude, nakon što ih je uputio, dok im ne objasni čega se (trebaju) bojati. Uistinu! Allah je o svakoj stvari Znalac.'),
(1351, 9, 116, 'Uistinu! Allah! Njegova je vlast nebesa i Zemlje; oživljava i usmrćuje. A nemate vi mimo Njega nikakva zaštitnika, niti pomagača.'),
(1352, 9, 117, 'Doista je Allah oprostio Vjerovjesniku i muhadžirima i ensarijama koji su ga slijedili u času nevolje, nakon što zamalo nisu skrenula srca skupine njih, zatim im je oprostio - Uistinu! On je njima Samilosni, Milosrdni.'),
(1353, 9, 118, 'I trojici koji su izostali. Dok, kad im je otješnjala Zemlja, ma kako prostrana bila, i stislo im se u dušama njihovim - a znali su da nema utočišta od Allaha, osim kod Njega - potom im je oprostio, da bi se kajali. Uistinu! Allah! On je Primalac pokajanja, Milosrdni.'),
(1354, 9, 119, 'O vi koji vjerujete! Bojte se Allaha i budite sa iskrenima!'),
(1355, 9, 120, 'Nije za stanovništvo Medine i onog ko je oko njih od beduina, da izostaju iza Poslanika Allahovog i da cijene duše svoje više od duše njegove. To zato što njih nije pogodila žeđ, niti umor, niti glad na putu Allahovom, niti će nagaziti gazište (koje) će naljutiti nevjernike, i neće doseći od neprijatelja stečevinu, a da im to neće biti upisano dobrim djelom. Uistinu! Allah neće dati da propadne nagrada dobročinitelja.'),
(1356, 9, 121, 'I neće utrošiti izdatak mali niti veliki, niti će presjeći dolinu, a da im neće biti upisano, da bi im platio Allah najboljim, za ono šta su radili.'),
(1357, 9, 122, 'A ne treba da svi vjernici nastupaju. Pa zašto iz svake jedinice od njih ne istupi skupina, da se obrazuje u vjeri, i da opominju narod svoj kad im se vrate, da bi se oni pazili?'),
(1358, 9, 123, 'O vi koji vjerujete! Borite se protiv onih nevjernika koji su vam blizu, i neka u vama nađu grubost: a znajte da je Allah sa bogobojaznima.'),
(1359, 9, 124, 'I kad god bude objavljena sura, tad od njih bude onaj ko kaže: "Kojem od vas će ovo povećati vjerovanje?" Pa što se tiče onih koji vjeruju, ta njima će povećati vjerovanje i oni se raduju.'),
(1360, 9, 125, 'A što se tiče onih u srcima čijim je bolest - pa dodaće im prljavštinu prljavštini njihovoj, i umrijet će, a oni će biti nevjernici.'),
(1361, 9, 126, 'Zar ne vide da oni bivaju iskušavani u svakoj godini jednom ili dva puta?, zatim se ne kaju, niti se oni poučavaju.'),
(1362, 9, 127, 'I kad god se objavi sura, gledaju oni jedni u druge: "Da li vas je vidio iko?" Zatim se okrenu. Okrenuo je Allah srca njihova, zato što su oni narod koji ne razumije.'),
(1363, 9, 128, 'Doista vam je došao Poslanik između duša vaših. Teško mu je što zapadate u nepriliku, brižan je za vas, vjernicima je samilostan, milosrdan.'),
(1364, 9, 129, 'Pa ako se okrenu, tad reci: "Dovoljan mi je Allah, samo je On Bog; u Njega se pouzdavam, a On je Gospodar Arša veličanstvenog."');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Alif. Lam. Ra. To su znaci Kitaba mudrog.'),
(1366, 10, 2, 'Zar je čudno ljudima što smo objavili čovjeku između njih: "Opominji ljude i obraduj one koji vjeruju, da imaju oni korak istine kod Gospodara njihovog." Rekoše nevjernici: "Doista, ovo je čarobnjak očit!"'),
(1367, 10, 3, 'Uistinu! Gospodar vaš je Allah, koji je stvorio nebesa i Zemlju u šest dana, zatim se postavio na Arš, upravljajući stvar. Nema nikakva posrednika, osim poslije dopuštanja Njegovog. To je Allah, Gospodar vaš, zato Njega obožavajte; pa zar se nećete poučiti?'),
(1368, 10, 4, 'Njemu je povratak svih vas! Obećanje Allahovo je istinito. Uistinu! On počinje stvaranje, potom ga ponavlja, da bi nagradio one koji vjeruju i čine dobra djela, pravedno. A oni koji ne vjeruju, imaće oni piće od ključale vode i kaznu bolnu, zato što nisu vjerovali.'),
(1369, 10, 5, 'On je Taj koji je učinio Sunce blještećim, a Mjesec sjajnim, i odredio mu faze, da znate broj godina i računanje. Allah je to stvorio samo s Istinom. Razlaže znakove ljudima koji znaju.'),
(1370, 10, 6, 'Uistinu! U izmjeni noći i dana, i onom što je stvorio Allah na nebesima i Zemlji, znaci su za ljude koji se boje.'),
(1371, 10, 7, 'Uistinu! Oni koji se ne nadaju susretu Našem i zadovolje se životom Dunjaa i smire u njemu, i oni koji su prema znakovima Našim nemarni,'),
(1372, 10, 8, 'Tim takvima će sklonište biti vatra, zbog onog šta su zaradili.'),
(1373, 10, 9, 'Uistinu! Oni koji vjeruju i rade dobra djela, upućuje ih Gospodar njihov vjerovanjem njihovim. Teći će ispod njih rijeke u baščama uživanja,'),
(1374, 10, 10, 'Priziv njihov u njima će biti: "Slava Tebi, o Allahu"; a pozdrav njihov u njima će biti: "Selam!", a zadnji priziv njihov: "Hvala Allahu, Gospodaru svjetova!"'),
(1375, 10, 11, 'A da Allah ubrzava ljudima zlo, njihovim ubrzavanjem dobrog, sigurno bi im se svršio rok njihov. Zato ostavljamo one koji se ne nadaju susretu Našem - u pretjeranosti svojoj lutaju.'),
(1376, 10, 12, 'A kad čovjeka dotakne nevolja, priziva Nas na boku svom ili sjedeći ili stojeći. Pa pošto otklonimo od njega nevolju njegovu, prođe kao da Nas nije prizivao zbog nesreće koja ga je dotakla. Tako je pretjerujućima uljepšano ono šta rade.'),
(1377, 10, 13, 'A doista smo prije vas uništavali pokoljenja, pošto su činili zulm; a dolazili su im poslanici njihovi sa dokazima jasnim, i ne bi vjerovali. Tako plaćamo narod prestupnika.'),
(1378, 10, 14, 'Potom smo učinili vas halifama na Zemlji poslije njih, da vidimo kako radite.'),
(1379, 10, 15, 'A kad im se uče ajeti Naši, dokazi jasni, govore oni koji se ne nadaju susretu Našem: "Donesi nam Kur\'an drugačiji od ovog ili ga izmijeni." Reci: "Nije za mene da ga samovoljno mijenjam. Slijedim samo ono šta mi se objavljuje. Uistinu! Ja se bojim, ako ne poslušam Gospodara svog, kazne Dana strahovitog."'),
(1380, 10, 16, 'Reci: "Da je htio Allah, ne bih vam ga učio, niti bi (On) dao da ga vi spoznate. Pa doista sam proveo život među vama prije njega; pa zar ne shvatate?"'),
(1381, 10, 17, 'Pa ko je nepravedniji od onog ko izmišlja protiv Allaha laž ili poriče ajete Njegove? Uistinu to neće uspjeti prestupnici.'),
(1382, 10, 18, 'I obožavaju mimo Allaha ono šta im ne šteti niti im koristi, i govore: "Ovi su posrednici naši kod Allaha." Reci: "Da li biste obavijestili Allaha o onom šta ne zna u nebesima i na Zemlji?" Slava neka je Njemu!, a uzvišen je od onog šta pridružuju.'),
(1383, 10, 19, 'I bili su ljudi samo jedna zajednica, pa su se razišli. A da nije prethodila Riječ od Gospodara tvog, sigurno bi među njima bilo presuđeno o onom u čemu su se razišli.'),
(1384, 10, 20, 'I govore: "Zašto mu se ne objavi znak od Gospodara njegovog?" Zato reci: "Nevidljivo je jedino Allahovo. Pa očekujte. Uistinu, ja sam sa vama od čekalaca."'),
(1385, 10, 21, 'I kad damo da ljudi okuse milost nakon nevolje koja ih je dotakla, gle!, imaju oni spletku protiv znakova Naših. Reci: "Allah je najbrži spletkom." Uistinu! Izaslanici Naši pišu šta spletkarite.'),
(1386, 10, 22, 'On je Taj koji vam daje da se krećete po kopnu i moru. Dok, kad budete u plovilima, i zaplove s njima vjetrom povoljnim, i obraduju se tome, nadođe im vjetar olujan, i nadođe im talas sa svake strane, i (kad) pomisle da su oni opkoljeni s njima, prizivaju Allaha odani Njegovoj vjeri: "Ako nas spasiš iz ovog, sigurno ćemo biti od zahvalnih."'),
(1387, 10, 23, 'Pa pošto ih spasi, gle! Šepure se po Zemlji bez prava. O ljudi! Bunt vaš je samo protiv duša vaših, užitak života Dunjaa; potom je Nama povratak vaš, pa ćemo vas obavijestiti o onom šta ste radili.'),
(1388, 10, 24, 'Doista, primjer života Dunjaa je kao voda koju spuštamo s neba pa se izmiješa njome rastinje Zemlje, od čega jedu ljudi i životinje. Dok, kad uzme Zemlja ukras svoj i uljepša se, i pomisle stanovnici njeni da su oni svemoćni nad njom, dođe joj naredba Naša noću ili danju, pa je učinimo pokošenom, kao da nije bujala jučer. Tako razlažemo znakove za ljude koji razmišljaju;'),
(1389, 10, 25, 'A Allah poziva kući selama i upućuje koga hoće putu pravom.'),
(1390, 10, 26, 'Za one koji čine dobro, biće dobro i povećanje. I neće prekriti lica njihova crnilo, niti poniženje. Takvi će biti stanovnici Dženneta; oni će u njemu biti vječito.'),
(1391, 10, 27, 'A koji steknu zla djela, plaća (im) je zlodjelo slično tome - a zahvatiće ih poniženje - neće oni imati od Allaha nikakva zaštitnika - kao da će lica njihova prekrivati dijelovi mrkle noći. Takvi će biti stanovnici vatre; oni će u njoj biti vječito.'),
(1392, 10, 28, 'A na Dan kad ih sve saberemo, zatim reknemo onima koji su pridruživali: "Na svoja mjesta vi, vi i ortaci vaši!" Tad ćemo ih razdvojiti, a reći će ortaci njihovi: "Niste nas obožavali."'),
(1393, 10, 29, '"Pa dovoljan je Allah svjedok između nas i između vas, da smo doista bili o obožavanju vašem nesvjesni."'),
(1394, 10, 30, 'Tu će svaka duša biti upoznata šta je unaprijed poslala, i biće vraćeni Allahu, Gospodaru svom istinskom, i nestaće od njih šta su izmišljali.'),
(1395, 10, 31, 'Reci: "Ko vas opskrbljuje s neba i Zemlje? Ili ko vlada sluhom i vidovima, i ko izvodi živo iz mrtvog i izvodi mrtvo iz živog, i ko upravlja stvar?" Pa reći će: "Allah." Tad reci: "Pa zar se ne bojite?"'),
(1396, 10, 32, 'Pa to je Allah, Gospodar vaš istinski! Pa šta je to poslije Istine, osim zabluda? Pa kako se okrećete!'),
(1397, 10, 33, 'Tako se obistinjuje Riječ Gospodara tvog na onima koji griješe, da oni neće vjerovati.'),
(1398, 10, 34, 'Reci: "Da li je neko od ortaka vaših taj koji počinje stvaranje, zatim ga ponavlja?" Reci: "Allah počinje stvaranje, potom ga ponavlja." Pa kako se odvraćate!'),
(1399, 10, 35, 'Reci: "Da li je neko od ortaka vaših taj ko upućuje Istini?" Reci: "Allah upućuje Istini." Pa da li je ko upućuje Istini dostojniji da bude slijeđen, ili ko ne upućuje, izuzev ako bude vođen? Pa šta vam je? Kako rasuđujete?'),
(1400, 10, 36, 'A većina njih slijedi samo pretpostavku. Uistinu, pretpostavka ne koristi Istini nimalo. Uistinu! Allah je Znalac onog šta čine.'),
(1401, 10, 37, 'I nije bilo (moguće) za ovaj Kur\'an da bude izmišljen mimo Allaha; međutim, potvrda je onog prije njega, i detaljno izlaganje Knjige u koju nema sumnje, od Gospodara svjetova.'),
(1402, 10, 38, 'Zar govore: "Izmišlja ga." Reci: "Pa donesite suru sličnu njemu i pozovite koga možete mimo Allaha, ako istinu govorite."'),
(1403, 10, 39, 'Naprotiv, poriču ono čije znanje ne obuhvataju, a još im nije došlo njegovo tumačenje. Tako su poricali oni prije njih, pa pogledaj kakav je bio kraj zalima!'),
(1404, 10, 40, 'I između njih je ko u njega vjeruje, i od njih je ko u njega ne vjeruje. A Gospodar tvoj je Najbolji znalac mufsida.'),
(1405, 10, 41, 'Pa ako te poreknu, tad reci: "Meni djelo moje, a vama djelo vaše. Vi ste slobodni od onog šta radim, a ja sam slobodan od onog šta radite."'),
(1406, 10, 42, 'I od njih je ko te sluša. Pa zar ti da pročuješ gluhe, iako ne shvataju?'),
(1407, 10, 43, 'I od njih je ko te gleda. Pa zar ti da uputiš slijepce, iako ne vide?'),
(1408, 10, 44, 'Uistinu! Allah ne čini zulm ljudima nimalo, nego ljudi dušama svojim čine zulm.'),
(1409, 10, 45, 'A na Dan kad ih sakupimo, biće kao da su proboravili samo časak dana, prepoznajući se međusobno. Doista će izgubiti oni koji su poricali susret s Allahom i nisu bili upućeni.'),
(1410, 10, 46, 'Ili ti pokazali dio onog šta im obećavamo ili dali da ti umreš - ta Nama je povratak njihov, zatim, Allah je Svjedok nad onim šta čine.'),
(1411, 10, 47, 'A svaka umma imala je poslanika. Pa kad dođe poslanik njihov, presudiće među njima pravedno, i njima se neće učiniti zulm.'),
(1412, 10, 48, 'I govore: "Kad će ovo obećanje, ako istinu govorite?"'),
(1413, 10, 49, 'Reci: "Ne vladam štetom niti korišću duši svojoj, izuzev šta htjedne Allah." Svaka zajednica ima rok; kad dođe rok njihov, tad (ga) ne odlažu čas, niti ubrzavaju.'),
(1414, 10, 50, 'Reci: "Jeste li vidjeli, ako vam dođe kazna Njegova noću ili danju, zašto je požuruju prijestupnici?"'),
(1415, 10, 51, 'Zar ćete nakon što se dogodi vjerovati u nju? Zar sada, a već ste je požurivali!'),
(1416, 10, 52, 'Zatim će biti rečeno onima koji su činili zulm: "Iskusite kaznu vječnosti. Hoćete li biti plaćeni, osim prema onom šta ste zaradili?"'),
(1417, 10, 53, 'I traže od tebe obavještenje: "Je li to Istina?" Reci: "Jeste, tako mi Gospodara mog, doista to je Istina, a niste vi ti koji će umaći."'),
(1418, 10, 54, 'A da ima svaka duša (koja) je činila zulm, ono šta je na Zemlji, sigurno bi se iskupljivala time. I pritajit će kajanje pošto vide kaznu, a presudiće se među njima pravedno i njima se zulm neće učiniti.'),
(1419, 10, 55, 'Besumnje! Uistinu je Allahovo šta je u nebesima i Zemlji. Besumnje! Doista je obećanje Allahovo Istina! Međutim, većina njih ne zna.'),
(1420, 10, 56, 'On oživljava i usmrćuje, i Njemu se vraćate.'),
(1421, 10, 57, 'O ljudi! Doista vam je došla mew\'iza od Gospodara vašeg, i lijek za ono što je u grudima, i Uputa i milost vjernicima.'),
(1422, 10, 58, 'Reci: "U blagodati Allahovoj i milosti Njegovoj - pa tome neka se raduju. To je bolje od onog šta skupljaju."'),
(1423, 10, 59, 'Reci: "Jeste li vidjeli šta vam je Allah spustio od opskrbe, pa ste načinili od nje haram i halal?" Reci: "Je li vam Allah dopustio ili protiv Allaha izmišljate?"'),
(1424, 10, 60, 'A kakvo će biti mišljenje onih koji izmišljaju protiv Allaha laž, na Dan kijameta? Uistinu! Allah je Vlasnik blagodati nad ljudima. Međutim, većina njih ne zahvaljuje.'),
(1425, 10, 61, 'I nećeš biti u nečem, niti ćeš učiti iz njega - iz Kur\'ana, i nećete raditi nikakav posao, a da nećemo biti nad vama svjedoci kad se budete u to udubili. I neće izmaći od Gospodara tvog ništa težine atoma u Zemlji, niti u nebu; ni manje od toga niti veće, a da nije u Knjizi jasnoj.'),
(1426, 10, 62, 'Besumnje! Uistinu, prijatelji Allahovi su (oni), nad kojima nema straha, niti će oni žaliti,'),
(1427, 10, 63, 'Oni koji vjeruju i boje (Ga) se!'),
(1428, 10, 64, 'Za njih su radosne vijesti u životu Dunjaa i na Ahiretu - nema zamjene za Riječi Allahove - to je to, uspjeh veličanstven.'),
(1429, 10, 65, 'I neka te ne žalosti govor njihov. Uistinu, moć je Allahova sva; On je Onaj koji čuje, Znalac.'),
(1430, 10, 66, 'Besumnje! Uistinu, Allahov je ko je u nebesima i ko je na Zemlji! A oni koji prizivaju (druge) mimo Allaha, ne slijede (te) ortake; slijede samo pretpostavku i oni jedino nagađaju.'),
(1431, 10, 67, 'On je Taj koji je za vas načinio noć da se smirite u njoj, a dan - onim koji daje vidljivost. Uistinu, u tome su znaci za ljude koji čuju.'),
(1432, 10, 68, 'Govore: "Allah je uzeo dijete." Slava neka je Njemu! On je Neovisni! Njegovo je šta je u nebesima i šta je u Zemlji. Nema kod vas nikakav dokaz za ovo. Zar govorite o Allahu šta ne znate?'),
(1433, 10, 69, 'Reci: "Uistinu, oni koji izmišljaju protiv Allaha laž, neće uspjeti."'),
(1434, 10, 70, 'Užitak na Dunjau! Zatim je Nama povratak njihov, potom ćemo dati da oni iskuse kaznu žestoku, zato što nisu vjerovali.'),
(1435, 10, 71, 'Izrecituj im vijest Nuhovu, kad reče narodu svom: "O narode moj! Ako vam je teško stajanje moje i opominjanje moje ajetima Allahovim - pa u Allaha se pouzdavam - onda sredite stvar vi i ortaci vaši, zatim neka ne bude odluka vaša sumnjivom za vas, potom mi presudite, i ne odgađajte mi.'),
(1436, 10, 72, 'Pa ako se odvratite, pa nisam tražio od vas nikakvu nagradu. Nagrada moja je jedino do Allaha, a naređeno mi je da budem od muslimana."'),
(1437, 10, 73, 'Tad su ga porekli, pa smo spasili njega i onog ko je bio s njim u lađi, i učinili ih halifama, a potopili one koji su poricali znakove Naše. Pa pogledaj kakav je bio kraj opominjanih!'),
(1438, 10, 74, 'Zatim smo poslije njega slali poslanike narodu njihovom, pa su im donosili dokaze jasne, te nisu (htjeli) da vjeruju u ono šta su poricali ranije. Tako pečatimo srca pretjerujućih.'),
(1439, 10, 75, 'Zatim smo poslije njih poslali Musaa i Haruna faraonu i velikanima njegovim, sa znakovima Našim, pa su se uzoholili i bili narod prijestupnika.'),
(1440, 10, 76, 'Pa pošto im dođe Istina od Nas, rekoše: "Uistinu, ovo je sihr očiti."'),
(1441, 10, 77, 'Musa reče: "Zar govorite (ovo) za Istinu pošto vam je došla? Je li ovo sihr?"; a neće uspjeti čarobnjaci.'),
(1442, 10, 78, 'Rekoše: "Jesi li nam došao da nas okreneš od onog na čemu smo našli očeve naše, i da za vas dvojicu bude veličina na Zemlji?; a nismo mi vama dvojici vjernici."'),
(1443, 10, 79, 'I reče faraon: "Dovedite mi svakog čarobnjaka, znalca."'),
(1444, 10, 80, 'Pa pošto dođoše čarobnjaci, Musa im reče: "Bacite ono čega ste vi bacači!"'),
(1445, 10, 81, 'Pa pošto baciše, Musa reče: "Ono što ste donijeli je sihr. Uistinu, Allah će ga učiniti ništavnim. Uistinu! Allah ne unapređuje posao mufsida;'),
(1446, 10, 82, 'I utvrdiće Allah Istinu Riječima Svojim, makar mrzili prijestupnici."'),
(1447, 10, 83, 'Tad su Musau vjerovali samo potomci naroda njegovog, zbog straha od faraona i uglednika njihovih, da ih ne progoni. A uistinu! Faraon je bio visok na zemlji, i uistinu, on je bio od onih koji pretjeruju.'),
(1448, 10, 84, 'I reče Musa: "O narode moj! Ako vjerujete u Allaha, onda se u Njega pouzdajte, ako ste muslimani!"'),
(1449, 10, 85, 'Tad rekoše: "U Allaha se uzdamo. Gospodaru naš! Ne učini nas kušnjom za narod zalima,'),
(1450, 10, 86, 'I izbavi nas milošću Svojom od naroda nevjernika."'),
(1451, 10, 87, 'I objavismo Musau i bratu njegovom: "Sagradite narodu svom u Egiptu kuće, i učinite kuće svoje kiblom, i obavljajte salat; a obraduj vjernike."'),
(1452, 10, 88, 'I reče Musa: "Gospodaru naš! Doista si Ti dao faraonu i uglednicima njegovim ukras i bogatstva u životu Dunjaa, Gospodaru naš, da bi zavodili s puta Tvog. Gospodaru naš! Uništi bogatstva njihova i otvrdni srca njihova, pa da ne vjeruju dok ne vide kaznu bolnu."'),
(1453, 10, 89, 'Reče: "Već je uslišena dowa vas dvojice, zato budite ispravni, i ne slijedite put onih koji ne znaju."'),
(1454, 10, 90, 'I provedosmo sinove Israilove preko mora, pa ih je slijedio faraon i vojske njegove nasiljem i plahovitošću. Dok, kad ga stiže davljenje, reče: "Vjerujem da nema boga osim Onog u kojeg vjeruju sinovi Israilovi i ja sam od muslimana!"'),
(1455, 10, 91, 'Zar sad! A doista nisi poslušao prije i bio si od mufsida.'),
(1456, 10, 92, 'Zato ćemo danas spasiti tebe - tijelo tvoje - da budeš znak. za onog ko bude iza tebe. A uistinu, mnogi od ljudi su prema znacima Našim nemarni.'),
(1457, 10, 93, 'I doista smo smjestili sinove Israilove smještajem tvrdim, i opskrbili ih od dobrih stvari, pa se nisu razišli dok im nije došlo znanje. Uistinu, Gospodar tvoj će presuditi među njima na Dan kijameta, o onom u čemu su se razilazili.'),
(1458, 10, 94, 'Pa ako si u sumnji o onom šta ti objavljujemo, tad pitaj one koji čitaju Knjigu (objavljenu) prije tebe. Doista, tebi dolazi Istina od Gospodara tvog, zato nikako ne budi od sumnjivaca;'),
(1459, 10, 95, 'I ne budi nikako od onih koji poriču ajete Allahove, pa da budeš od gubitnika.'),
(1460, 10, 96, 'Uistinu! Oni na kojima se obistinila Riječ Gospodara tvog, neće vjerovati,'),
(1461, 10, 97, 'Makar im došao svaki znak, dok ne vide kaznu bolnu.'),
(1462, 10, 98, 'Pa zašto nije bilo naselja koje je vjerovalo, pa iskoristilo to svoje vjerovanje, izuzev naroda Junusovog? Pošto su vjerovali otklonili smo od njih kaznu srama u životu Dunjaa i dali im da uživaju izvjesno vrijeme.'),
(1463, 10, 99, 'A da hoće Gospodar tvoj, sigurno bi vjerovao ko je na Zemlji, svaki od njih, zajedno. Pa zar ćeš ti prisiljavati ljude, dok ne budu vjernici?'),
(1464, 10, 100, 'A nije za dušu da vjeruje, osim s dopuštenjem Allahovim; a stavlja prljavštinu na one koji neće da shvate.'),
(1465, 10, 101, 'Reci: "Pogledajte šta je to u nebesima i Zemlji", a ne koriste znakovi i opomene ljudima koji ne vjeruju.'),
(1466, 10, 102, 'Pa da li očekuju (išta) osim slične dane onih koji su prošli prije njih? Reci: "Pa očekujte! Uistinu, ja sam sa vama među onima koji čekaju."'),
(1467, 10, 103, 'Zatim smo spasavali poslanike Naše i one koji su vjerovali. Tako je dužnost na Nama da spasimo vjernike.'),
(1468, 10, 104, 'Reci: "O ljudi! Ako ste u sumnji o vjeri mojoj - pa ne obožavam one koje obožavate mimo Allaha, nego obožavam Allaha koji vam smrt daje, a naređeno mi je da budem od vjernika."'),
(1469, 10, 105, '"I stani licem svojim vjeri pravoj, i ne budi nikako od mušrika,'),
(1470, 10, 106, 'I ne prizivaj mimo Allaha ono šta ti ne koristi, niti ti šteti. Pa ako učiniš - onda si ti uistinu od zalima."'),
(1471, 10, 107, 'A ako te dotakne Allah nevoljom, tad nema otklonitelja njenog, osim Njega; a ako ti želi dobro, tad nema onog ko će odbiti dobrotu Njegovu. Pogađa njome koga hoće od robova Svojih, a On je Oprosnik, Milosrdni.'),
(1472, 10, 108, 'Reci: "O ljudi! Doista vam je došla Istina od Gospodara vašeg. Pa ko se uputi, pa samo se upućuje za dušu svoju; a ko odluta, pa samo luta protiv nje i nisam ja nad vama čuvar."'),
(1473, 10, 109, 'I slijedi šta ti se objavljuje, i strpi se dok ne presudi Allah; a On je Najbolji od sudaca.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Alif. Lam. Ra. Knjiga - učinjeni su presudnim ajeti njeni, zatim razjašnjeni od Mudrog, Obaviještenog:'),
(1475, 11, 2, '"Obožavajte samo Allaha. Uistinu! Ja sam vam od Njega opominjač i donosilac radosnih vijesti",'),
(1476, 11, 3, 'I: "Tražite oprost od Gospodara vašeg, potom Mu se pokajte, daće vam da uživate uživanjem lijepim do roka određenog, i daće svakom posjedniku dobrote, dobrotu Svoju. A ako se okrenete, pa uistinu, ja se bojim nad vama kazne Dana velikog.'),
(1477, 11, 4, 'Allahu je povratak vaš, a On nad svakom stvari ima moć."'),
(1478, 11, 5, 'Besumnje! Oni uistinu svijaju grudi svoje da se sakriju od Njega. Uistinu, kad se prekrivaju odjećom svojom - zna šta skrivaju i šta obznanjuju. Uistinu! On je Znalac grudi.'),
(1479, 11, 6, 'I nema ni jedne životinje na Zemlji, a da nije na Allahu opskrba njena, i zna boravište njeno i mjesto odlaganja njeno; svakoje je u Knjizi jasnoj.'),
(1480, 11, 7, 'I On je Taj koji je stvorio nebesa i Zemlju u šest dana - a bio je Arš Njegov nad vodom - da bi vas iskušao, koji je od vas bolji djelom. A ako kažeš: "Uistinu, vi ćete biti podignuti poslije smrti!", sigurno će reći oni koji ne vjeruju: "Ovo je samo sihr očiti."'),
(1481, 11, 8, 'A ako im odložimo kaznu do vremena odbrojanog, sigurno će reći: "Šta je zadržava?" Doista, na Dan kad im dođe - neće biti okrenuta od njih, i spopast će ih ono čemu su se izrugivali.'),
(1482, 11, 9, 'I ako damo da čovjek okusi od Nas milost, zatim mu je istrgnemo, uistinu, on je očajan, nezahvalan.'),
(1483, 11, 10, 'A ako damo da on iskusi blagonaklonost, nakon što ga je nevolja dotakla, sigurno će reći: "Zla su otišla od mene." Uistinu, on je veseo, hvalisav,'),
(1484, 11, 11, 'Izuzev onih koji pokažu strpljivost i budu radili dobra djela, ti takvi će imati oprost i nagradu veliku.'),
(1485, 11, 12, 'Pa možda bi ti izostavio dio onog šta ti se objavljuje, i stijesnile time grudi tvoje, što govore: "Zašto mu se ne spusti riznica ili ne dođe s njim melek?" Ti si samo opominjač, a Allah je nad svakom stvari Zaštitnik.'),
(1486, 11, 13, 'Zar govore: "Izmišlja ga." Reci: "Pa donesite deset sura sličnih njemu, izmišljenih, i pozovite koga možete mimo Allaha, ako istinu govorite!"'),
(1487, 11, 14, 'Pa ako vam se ne odazovu, tad znajte da je objavljen znanjem Allahovim, i da nema boga sem Njega. Pa jeste li vi muslimani?'),
(1488, 11, 15, 'Ko želi život Dunjaa i ukras njegov, isplatićemo im djela njihova u njemu i oni u njemu neće biti oštećeni.'),
(1489, 11, 16, 'To su oni koji će na Ahiretu imati samo vatru, a propast će šta su činili na njemu, a zaludno je šta su radili.'),
(1490, 11, 17, 'Pa da li je, ko je na dokazu jasnom od Gospodara njegovog - a uči ga svjedok od Njega, i prije njega Knjiga Musaova - vodič i milost? Takvi vjeruju u njega. A ko od stranaka ne vjeruje u njega - pa vatra će biti mjesto njegovo. Zato ne budi u sumnji o tome. Uistinu, on je Istina od Gospodara tvog; međutim, većina ljudi ne vjeruje.'),
(1491, 11, 18, 'A ko je nepravedniji od onog ko izmišlja protiv Allaha laž? Takvi će biti dovedeni pred Gospodara njihovog, i reći će svjedoci: "To su oni koji su lagali protiv Gospodara svog." Besumnje, prokletsvo Allahovo je na zalimima,'),
(1492, 11, 19, 'Onima koji odvraćaju od puta Allahovog i traže iskrivljenost u njemu, a oni su u Ahiret nevjernici.'),
(1493, 11, 20, 'Takvi neće biti ti koji će umaći na Zemlji i neće oni imati mimo Allaha nikakvih zaštitnika! Udvostručiće im se kazna! Neće se moći (koristiti) sluhom, niti će vidjeti.'),
(1494, 11, 21, 'Takvi će biti oni koji izgube duše svoje, a nestaće od njih šta su izmišljali.'),
(1495, 11, 22, 'Nema sumnje da će oni, (baš) oni na Ahiretu biti najveći gubitnici.'),
(1496, 11, 23, 'Uistinu, oni koji vjeruju i rade dobra djela, i budu skrušeni Gospodaru svom - takvi će biti stanovnici Dženneta. Oni će u njemu biti vječito.'),
(1497, 11, 24, 'Primjer (ovih) dviju skupina, je kao slijepca i gluhog, i onog koji vidi i onog koji čuje. Jesu li isti primjerom? Pa zar nećete razmisliti?'),
(1498, 11, 25, 'I doista smo poslali Nuha narodu njegovom: "Uistinu! Ja sam vam opominjač jasni,'),
(1499, 11, 26, 'Da obožavate samo Allaha. Uistinu! Ja se plašim nad vama kazne Dana bolnog.'),
(1500, 11, 27, 'Tad rekoše uglednici koji nisu vjerovali iz naroda njegovog: "Vidimo te samo kao smrtnika sličnog nama, i vidimo te - slijede te samo oni koji su najniži naši, bez razmišljanja, i ne vidimo nikakve vaše zasluge nad nama; naprotiv, smatramo vas lažovima."'),
(1501, 11, 28, 'Reče: "O narode moj! Da li vidite, ako sam ja na jasnom dokazu od Gospodara svog - a dao mi je od Sebe milost - a vi slijepi na to, zar ćemo učiniti da prionete uz to, a vi njegovi mrzitelji?"'),
(1502, 11, 29, '"I o narode moj! Ne tražim od vas za to imetak. Nagrada moja je jedino do Allaha; a nisam ja progonitelj onih koji vjeruju. Uistinu, oni su ti koji će sresti Gospodara svog. Međutim, ja vas vidim narodom koji ne zna."'),
(1503, 11, 30, '"I o narode moj! Ko će mi pomoći protiv Allaha, ako ih otjeram? Pa zar se nećete opametiti?'),
(1504, 11, 31, 'I ne kažem vam: \'Kod mene su riznice Allahove\', niti \'Znam nevidljivo\', i ne kažem: \'Uistinu, ja sam melek.\' I ne govorim onima koje preziru oči vaše (da) im Allah neće dati dobro. Allah je Najbolji znalac onog šta je u dušama njihovim. Uistinu, ja bih tad bio od zalima."'),
(1505, 11, 32, 'Rekoše: "O Nuhu! Već si raspravljao s nama, pa si umnožio raspravljanje naše; zato nam daj to čime nam prijetiš, ako istinu govoriš."'),
(1506, 11, 33, 'Reče: "Samo će vam Allah to dati, ako hoće, a niste vi ti koji će umaći.'),
(1507, 11, 34, 'I neće vam koristiti savjet moj, ako budem želio da vas posavjetujem, ako Allah bude želio da vas zavede. On je Gospodar vaš i Njemu ćete se vratiti."'),
(1508, 11, 35, 'Zar govore: "Izmišlja ga." Reci: "Ako ga izmišljam - pa na meni je krivica moja, a ja sam slobodan od tog što ste skrivili."'),
(1509, 11, 36, 'I bi objavljeno Nuhu: "Niko neće vjerovati iz naroda tvog, izuzev ko je već vjerovao. Zato se ne sekiraj zbog onog šta čine,'),
(1510, 11, 37, 'I gradi lađu pod očima Našim i objavom Našom i ne obraćaj Mi se zbog onih koji su činili zulm. Uistinu! Oni će biti potopljeni."'),
(1511, 11, 38, 'I gradio je lađu; i kad god bi kraj njega prošli uglednici iz naroda njegovog, rugali bi mu se. Reče: "Ako nam se rugate, pa uistinu, mi ćemo se rugati vama, kao što se rugate.'),
(1512, 11, 39, 'Pa znaćete onog kome će doći kazna, sramoteći ga, i na koga će se sručiti kazna trajna."'),
(1513, 11, 40, 'Dok, kad dođe naredba Naša i šiknu peć, rekosmo: "Ukrcaj u nju od svake vrste dvoje; i porodicu svoju - izuzev onog kome je prethodila Riječ - i ko je vjerovao." A nije vjerovao s njim (niko), izuzev malo (njih).'),
(1514, 11, 41, 'I reče (Nuh): "Ukrcajte se u nju! U ime Allaha je plovidba njena i pristanak njen. Uistinu, Gospodar moj je Oprosnik, Milosrdni."'),
(1515, 11, 42, 'I ona je plovila s njima na talasu kao brda. I zovnu Nuh sina svog - a bio je odvojen: "O sinčiću moj! Ukrcaj se s nama i ne budi s kafirima!"'),
(1516, 11, 43, '(Sin) reče: "Pribjeći ću planini, zaštitiće me od vode." (Nuh) reče: "Nema zaštitnika danas od naredbe Allahove, izuzev kome se smilovao." I nadođe između njih dvojice talas i bi od potopljenih.'),
(1517, 11, 44, 'I bi rečeno: "O Zemljo! Progutaj vodu svoju!" I: "O nebo! Prestani!" I opade voda, i naredba bi izvršena i nasuka se na Džudijj, i bi rečeno: "Daleko bio narod zalima!"'),
(1518, 11, 45, 'I zovnu Nuh Gospodara svog, pa reče: "Gospodaru moj! Uistinu, sin moj je iz porodice moje, a uistinu, obećanje Tvoje je Istina, a Ti si Najpravedniji od sudaca!"'),
(1519, 11, 46, 'Reče: "O Nuhu! Uistinu, on nije od porodice tvoje; uistinu, on je posla drugačijeg od dobrog, zato Me ne pitaj o onom o čemu ti nemaš znanje. Uistinu, Ja te savjetujem da ne budeš od džahila."'),
(1520, 11, 47, 'Reče: "Gospodaru moj! Uistinu, ja tražim zaštitu u Tebe da Te ne upitam ono o čemu ja nemam znanje. I ako mi ne oprostiš i ne smiluješ mi se, biću od gubitnika."'),
(1521, 11, 48, 'Bi rečeno: "O Nuhu! Siđi sa selamom od Nas i blagoslovima tebi i narodima od onog ko je s tobom. A narodi - daćemo da oni uživaju, zatim će ih od Nas dotaći kazna bolna."'),
(1522, 11, 49, 'To su neke vijesti nevidljivog koje ti objavljujemo. Nisi ih znao ti niti narod tvoj prije ovoga. Zato se strpi. Uistinu, ishod je za bogobojazne.'),
(1523, 11, 50, 'I Adu, brata njihovog Huda. Reče: "O narode moj! Obožavajte Allaha! Nemate vi drugog boga sem Njega! Vi ste samo oni koji izmišljaju.'),
(1524, 11, 51, 'O narode moj! Ne tražim od vas za to nagradu, nagrada moja je jedino od Onog koji me je stvorio. Pa zar ne shvatate?"'),
(1525, 11, 52, '"I o narode moj! Tražite oprost Gospodara vašeg, zatim Mu se pokajte, poslaće na vas nebo obilno i dodati vam snagu snazi vašoj, i ne okrećite se (kao) prestupnici!"'),
(1526, 11, 53, 'Rekoše: "O Hude! Nisi nam donio jasan dokaz, i nismo mi ti koji će napustiti bogove svoje zbog riječi tvoje, i nismo mi tebi vjernici.'),
(1527, 11, 54, 'Kažemo da te je samo pogodio zlom neki od bogova naših." Reče: "Uistinu, ja pozivam za svjedoka Allaha, i svjedočite da sam ja slobodan od onog šta pridružujete,'),
(1528, 11, 55, 'Mimo Njega. Pa prevarite me svi, zatim mi ne odgađajte.'),
(1529, 11, 56, 'Uistinu! Ja se pouzdavam u Allaha, Gospodara svog i Gospodara vašeg. Nema nijedne životinje, a da On nije Držalac za kiku njenu! Uistinu, Gospodar moj je na putu pravom.'),
(1530, 11, 57, 'Pa ako se okrenete, pa doista sam vam dostavio ono s čim sam vam poslat, a daće Gospodar moj da (vas) naslijedi narod drugačiji od vas, a nećete Njemu štetiti nimalo. Uistinu, Gospodar moj je nad svakom stvari ^uvar."'),
(1531, 11, 58, 'I pošto dođe naredba Naša, spasismo Huda i one koji su s njim vjerovali, milošću od Nas, i izbavismo ih od kazne grube.'),
(1532, 11, 59, 'A to je bio Ad. Nijekali su ajete Gospodara svog i suprotstavljali se poslanicima Njegovim, i slijedili naredbu svakog tiranina svojeglavog.'),
(1533, 11, 60, 'I bili su slijeđeni na ovom Dunjau prokletstvom, i (biće) na Dan kijameta. Besumnje! Uistinu! Ad - (oni) nisu vjerovali u Gospodara njihovog. Daleko neka je Ad, narod Hudov!'),
(1534, 11, 61, 'I Semudu, brata njihovog Saliha. Reče: "O narode moj! Obožavajte Allaha! Nemate vi drugog boga sem Njega. On vas je sazdao od zemlje i nastanio vas na njoj, zato tražite oprost od Njega, potom Mu se pokajte. Uistinu, Gospodar moj je Bliski, Onaj koji se odaziva."'),
(1535, 11, 62, 'Rekoše: "O Salih! Doista si među nama bio (kao) onaj od kojeg se nada, prije ovog. Zar ćeš nam zabraniti da obožavamo ono šta su obožavali očevi naši, a uistinu, mi smo u ozbiljnoj sumnji prema onom čemu nas pozivaš."'),
(1536, 11, 63, 'Reče: "O narode moj! Da li vidite? - Ako sam na jasnom dokazu od Gospodara svog - a dao mi je od Sebe milost - pa ko će me pomoći protiv Allaha, ako Ga ne poslušam? Ta povećali biste mi samo gubitak.'),
(1537, 11, 64, 'I o narode moj! Ova deva Allahova za vas je znak. Zato je pustite da se hrani po zemlji Allahovoj, i ne dotičite je zlom, pa da vas ne spopane kazna bliska."'),
(1538, 11, 65, 'Tad je raniše, pa (on) reče: "Uživajte u domu vašem tri dana! To je prijetnja drugačija od lažne."'),
(1539, 11, 66, 'Pa pošto dođe naredba Naša, spasismo Saliha i one s njim koji su vjerovali, milošću od Nas, i od poniženja tog dana. Uistinu! Gospodar tvoj, On je Silni, Moćni.'),
(1540, 11, 67, 'I spopade one koji su činili zulm, krik, pa osvanuše u domovima njihovim povaljani,'),
(1541, 11, 68, 'Kao da nisu boravili u njima. Besumnje! Uistinu! Semud - (oni) nisu vjerovali u Gospodara njihovog. Daleko neka je Semud!'),
(1542, 11, 69, 'A doista su izaslanici Naši došli Ibrahimu sa radosnom viješću, rekoše: "Selam!" Reče: "Selam!" Tad nije oklijevao da donese tele pečeno.'),
(1543, 11, 70, 'Pa pošto vidje (da) ga ruke njihove ne dosežu, ne pozna ih i osjeti od njih strah. Rekoše: "Ne boj se! Uistinu, Mi smo poslati narodu Lutovu."'),
(1544, 11, 71, 'A žena njegova je stajala, pa se nasmija. Tad je obradovasmo Ishakom, a iza Ishaka, Jakubom.'),
(1545, 11, 72, 'Reče: "O teško meni! Zar da rodim, a ja sam starica i ovaj moj muž je starac? Uistinu, ovo je stvar čudna!"'),
(1546, 11, 73, 'Rekoše: "Zar se čudiš zbog odredbe Allahove? Milost Allahova i blagoslovi Njegovi su na vama, stanovnici kuće. Uistinu! On je Hvaljeni, Slavni."'),
(1547, 11, 74, 'Pa pošto Ibrahima napusti strah i dođe mu radosna vijest, raspravlaljao je s Nama o narodu Lutovom.'),
(1548, 11, 75, 'Uistinu, Ibrahim je bio blag, osjećajan, onaj koji se obraćao.'),
(1549, 11, 76, 'O Ibrahime! Okani se ovog. Uistinu, to je već došla naredba Gospodara tvog. A uistinu oni - njima će stići kazna neotklonjiva.'),
(1550, 11, 77, 'I pošto izaslanici Naši dođoše Lutu, sneveseli se zbog njih i bi pritješnjen zbog njih, nemoćan, i reče: "Ovo je dan mučan."'),
(1551, 11, 78, 'I dođe mu narod njegov žureći mu, a otprije su radili zla djela. Reče: "O narode moj! Ove kćeri moje, one su čišće za vas, zato se bojte Allaha i ne sramotite me (pred) gostima mojim. Zar (niko) od vas nije čovjek ispravan?"'),
(1552, 11, 79, 'Rekoše: "Doista znaš, nemamo mi na kćeri tvoje nikakvo pravo, a uistinu, ti znaš šta želimo."'),
(1553, 11, 80, 'Reče: "Kad bih ja imao protiv vas silu ili imao potporu žestoku."'),
(1554, 11, 81, 'Rekoše: "O Lute! Uistinu, mi smo izaslanici Gospodara tvog; neće te doseći. Zato kreni s porodicom svojom u dijelu noći i neka se niko od vas ne okreće, izuzev žene tvoje. Uistinu, to što će pogoditi nju, je ono što će pogoditi njih. Uistinu, rok njihov je jutro; zar jutro nije blisko?"'),
(1555, 11, 82, 'Pa pošto dođe naredba Naša, učinismo visoko njihovo niskim njihovim, i izlismo na njih kamenje glineno u slojevima,'),
(1556, 11, 83, 'Obilježeno kod Gospodara tvog; a nije to od zalima daleko.'),
(1557, 11, 84, 'I Medjenu, brata njihovog, Šuajba. Reče: "O narode moj! Obožavajte Allaha. Nemate vi drugog boga sem Njega! I ne umanjujte mjeru i vagu. Uistinu! Ja vas vidim u dobru i uistinu se ja bojim nad vama kazne Dana sveobuhvatnog."'),
(1558, 11, 85, '"I o narode moj! Ispunjavajte mjeru i vagu pravedno i ne zakidajte ljudima stvari njihove. I ne činite zlo na Zemlji, praveći fesad.'),
(1559, 11, 86, 'Ono što je Allah ostavio bolje je za vas, ako ste vjernici; a nisam ja nad vama čuvar."'),
(1560, 11, 87, 'Rekoše: "O Šuajbe! Da li ti salat tvoj naređuje da mi napustimo ono šta su obožavali očevi naši, ili da s imecima našim činimo šta hoćemo? Uistinu, ti, ti si blag, ispravan."'),
(1561, 11, 88, 'Reče: "O narode moj! Da li vidite? Ako sam na dokazu jasnom od Gospodara svog, a opskrbio me je od Sebe opskrbom dobrom? A ne želim da vam se suprotstavljam, izuzev onim šta vam zabranjujem. Želim samo da popravim šta mogu, a povoljnost moja je samo od Allaha; u Njega se pouzdavam i Njemu se obraćam."'),
(1562, 11, 89, '"I o narode moj! Ne skrivite vi nikako (zbog) neslaganja sa mnom, da vas ne pogodi slično šta je pogodilo narod Nuhov ili narod Hudov ili narod Salihov, a nije narod Lutov od vas daleko.'),
(1563, 11, 90, 'I tražite oprost od Gospodara vašeg, zatim Mu se pokajte. Uistinu, Gospodar moj je Milosrdni, Onaj koji voli."'),
(1564, 11, 91, 'Rekoše: "O Šuajbe! Ne razumijemo mnogo od tog šta govoriš, a uistinu, mi te među nama vidimo slabim. A da nije porodice tvoje, sigurno bismo te kamenovali, i nemaš ti nad nama moć."'),
(1565, 11, 92, 'Reče: "O narode moj! Zar vam je porodica moja uglednija od Allaha? A zabacili ste Ga iza sebe zanemarenog. Uistinu, Gospodar moj je Taj koji obuhvata to što radite."'),
(1566, 11, 93, '"I o narode moj! Radite prema moći svojoj, uistinu, ja sam radnik. Saznaćete kome će stići kazna koja će ga poniziti i ko je lažac. I očekujte! Uistinu! Ja sam sa vama čekalac."'),
(1567, 11, 94, 'I pošto dođe naredba Naša, spasismo Šuajba i one s njim koji su vjerovali, milošću od Nas, a one koji su činili zulm pogodi krik, te osvanuše u domovima njihovim povaljani,'),
(1568, 11, 95, 'Kao da nisu boravili u njima. Pa daleko neka je Medjen, kao što je udaljen Semud!'),
(1569, 11, 96, 'A doista, poslali smo Musaa sa znacima Našim i autoritetom očitim,'),
(1570, 11, 97, 'Faraonu i uglednicima njegovim, pa su slijedili naredbu faraonovu, a nije naredba faraonova bila ispravna.'),
(1571, 11, 98, 'Predvodiće narod svoj na Dan kijameta, pa će ih dovesti vatri. A loše je mjesto dolaska!'),
(1572, 11, 99, 'I slijeđeni su na ovom (svijetu) prokletstvom, i na Dan kijameta; a loš je dar poklonjeni.'),
(1573, 11, 100, 'To je iz vijesti gradova koje kazujemo tebi. Neki od njih stoje, a (neki) su pokošeni.'),
(1574, 11, 101, 'I nismo im učinili zulm, nego su zulm učinili dušama svojim. Tad im nisu koristili bogovi njihovi koje su prizivali mimo Allaha, nimalo, pošto je došla naredba Gospodara tvog; a povećali su im samo propast.'),
(1575, 11, 102, 'A takvo je grabljenje Gospodara tvog, kad dograbi (stanovništvo) gradova, a oni budu zulumčarski. Uistinu! Grabljenje Njegovo je bolno, žestoko.'),
(1576, 11, 103, 'Uistinu, u tome je znak za onog ko se boji kazne Ahireta. To je Dan kojem će se sabrati ljudi i to je Dan svjedočeni;'),
(1577, 11, 104, 'A odgađamo ga samo do roka odbrojanog.'),
(1578, 11, 105, 'Na dan kad dođe, neće govoriti duša, osim s dopuštenjem Njegovim. Tad će (neko) od njih biti nesretnik, a (neko) sretnik.'),
(1579, 11, 106, 'Pa što se tiče onih koji su nesretni, pa biće u vatri; imaće oni u njoj uzdisanje i hroptanje.'),
(1580, 11, 107, 'Vječno će biti u njoj dok traju nebesa i Zemlja, osim šta htjedne Gospodar tvoj. Uistinu, Gospodar tvoj je Onaj koji čini šta želi.'),
(1581, 11, 108, 'A što se tiče onih koji će imati sreću, pa biće u Džennetu, vječno u njemu dok traju nebesa i Zemlja, osim šta htjedne Gospodar tvoj: dar neprekidan.'),
(1582, 11, 109, 'Zato ne budi u sumnji zbog onog šta ovi obožavaju. Obožavaju samo kao što su obožavali očevi njihovi prije. A uistinu! Mi ćemo im isplatiti dio njihov neumanjen.'),
(1583, 11, 110, 'I doista, dali smo Musau Knjigu, pa je došlo do razilaženja o njoj; a da nije Riječi prethodne od Gospodara tvog, sigurno bi bilo presuđeno među njima. A uistinu, oni su o njemu u sumnji ozbiljnoj.'),
(1584, 11, 111, 'I doista će svakom od njih isplatiti Gospodar tvoj djela njihova. Uistinu! On je o onom šta rade Obaviješteni.'),
(1585, 11, 112, 'Zato budi prav kao što ti je naređeno i onaj ko se pokajao s tobom, i ne prevršujte. Uistinu! On je onog šta radite Vidilac.'),
(1586, 11, 113, 'I ne oslanjajte se na one koji čine zulm, pa da vas dotakne vatra; a nemate vi mimo Allaha nikakvih zaštitnika, zatim nećete biti pomognuti.'),
(1587, 11, 114, 'I obavljaj salat na dva kraja dana i dolaskom noći. Uistinu dobra djela otklanjaju zla djela. To je opomena za one koji se podsjećaju.'),
(1588, 11, 115, 'I strpi se. Pa uistinu, Allah neće dati da propane nagrada dobročinitelja.'),
(1589, 11, 116, 'Pa zašto nije bilo nekih pokoljenja prije vas, posjednika ostataka (uputa), koji su zabranjivali fesad na Zemlji, izuzev malo koga od njih (kog) smo spasili? A slijedili su oni koji su činili zulm, ono u čemu su uživali, i bili su krivci.'),
(1590, 11, 117, 'A neće Gospodar tvoj uništavati gradove zulmom, budu li stanovnici njihovi dobročinitelji.'),
(1591, 11, 118, 'A da je htio Gospodar tvoj, sigurno bi učinio ljude ummom jednom, a ne prestaju biti različiti,'),
(1592, 11, 119, 'Izuzev kome se smiluje Gospodar tvoj; a radi toga ih je stvorio. I ispuniće se Riječ Gospodara tvog: "Sigurno ću napuniti Džehennem džinnima i ljudima zajedno."'),
(1593, 11, 120, 'I sve (šta) ti kazujemo od vijesti poslanika je ono čim jačamo srce tvoje. A došla ti je u ovome Istina i savjet, i pouka vjernicima.'),
(1594, 11, 121, 'I reci onima koji ne vjeruju: "Radite prema moći svojoj! Uistinu, mi smo radnici.'),
(1595, 11, 122, 'I očekujte! Uistinu, mi smo čekatelji."'),
(1596, 11, 123, 'A Allahovo je nevidljivo nebesa i Zemlje i Njemu se vraća svaka stvar. Zato obožavaj Njega i uzdaj se u Njega; a nije Gospodar tvoj nemaran za ono šta radite.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Alif. Lam. Ra. To su znaci Kitaba jasnog.'),
(1598, 12, 2, 'Uistinu, Mi ga objavljujemo (kao) Kur\'an na arapskom, da biste vi razumjeli.'),
(1599, 12, 3, 'Mi ti kazujemo najljepše kazivanje (time) što ti objavljujemo ovaj Kur\'an, iako si prije njega sigurno bio od nemarnih.'),
(1600, 12, 4, 'Kad reče Jusuf ocu svom: "O oče moj! Uistinu sam ja vidio jedanaest planeta i Sunce i Mjesec. Vidio sam ih sebi potčinjene."'),
(1601, 12, 5, 'Reče: "O sičiću moj! Ne kazuj snoviđenje svoje braći svojoj, pa da ti ispletu spletku. Uistinu! Šejtan je čovjeku neprijatelj otvoreni."'),
(1602, 12, 6, 'I tako te je odabrao Gospodar tvoj i poučio te tumačenju hadisa, i upotpunjuje blagodat Svoju nad tobom i na porodici Jakubovoj, kao što ju je upotpunio na precima tvojim prije, Ibrahimom i Ishakom. Uistinu! Gospodar tvoj je Znalac, Mudri.'),
(1603, 12, 7, 'Doista su u Jusufu i braći njegovoj znaci za one koji pitaju.'),
(1604, 12, 8, 'Kad rekoše: "Sigurno su Jusuf i brat njegov draži ocu našem od nas, a mi smo družina. Uistinu, naš otac je u zabludi očitoj.'),
(1605, 12, 9, 'Ubijte Jusufa ili ga odbacite u (neku) zemlju, biće samo za vas lice oca vašeg, i bićete poslije toga narod pravednih."'),
(1606, 12, 10, 'Govornik između njih reče: "Ne ubijte Jusufa, a bacite ga na dno bunara; pokupiće ga neki karavan, ako ste radini."'),
(1607, 12, 11, 'Rekoše: "O oče naš! Šta ti je, ne vjeruješ nam za Jusufa, a uistinu, mi smo njegovi iskreni savjetnici?'),
(1608, 12, 12, 'Pošalji ga s nama sutra da se provede i izigra, a uistinu, mi ćemo biti njegovi čuvari."'),
(1609, 12, 13, '(Jakub) reče: "Uistinu, mene žalosti da odete s njim, i bojim se da će ga pojesti vuk, a vi biti o njemu nemarni."'),
(1610, 12, 14, 'Rekoše: "Ako ga pojede vuk - a mi družina - uistinu bismo mi tad bili gubitnici."'),
(1611, 12, 15, 'Pa pošto odoše s njim i saglasiše se da ga smjeste na dno bunara, i objavismo mu: "Sigurno ćeš ih obavijestiti o odluci njihovoj, a oni neće opaziti."'),
(1612, 12, 16, 'I dođoše ocu svom uz jaciju plačući,'),
(1613, 12, 17, 'Rekoše: "O oče naš! Uistinu, mi smo išli da se utrkujemo, a ostavili smo Jusufa kod opreme naše, pa ga je pojeo vuk. A nisi ti taj koji će nam povjerovati, iako istinu govorimo."'),
(1614, 12, 18, 'I dođoše s košuljom njegovom sa krvlju lažnom. Reče: "Naprotiv, zavele su vas duše vaše (toj) stvari. Pa strpljivost je lijepa, a Allah je Taj od kojeg se traži pomoć protiv onog šta opisujete."'),
(1615, 12, 19, 'I dođe karavan, pa poslaše vodonošu svog, te spusti svoju kofu. Reče: "O radosne vijesti! Ovo je dječak." I sakriše ga (kao) robu, a Allah je Znalac onog šta su uradili.'),
(1616, 12, 20, 'I prodaše ga po niskoj cijeni, dirhema nekoliko; a bili su o njemu uzdržljivi.'),
(1617, 12, 21, 'I onaj iz Misra koji ga je kupio reče ženi svojoj: "Časnim učini boravište njegovo, možda će nam koristiti ili (da) ga uzmemo djetetom." I tako učvrstismo Jusufa na Zemlji, i da bismo ga naučili tumačenju hadisa. A Allah je Pobjednik nad stvari Svojom, međutim, većina ljudi ne zna.'),
(1618, 12, 22, 'I pošto dostiže zrelost svoju, dadosmo mu sud i znanje; a tako nagrađujemo dobročinitelje.'),
(1619, 12, 23, 'I htjede da ga zavede ona u čijoj je on kući bio i zamandali (sva) vrata i reče: "Dođi ti!" Reče: "MeazAllah! Uistinu, on je gospodar moj, najljepšeg boravka. Uistinu, to neće uspjeti zalimi."'),
(1620, 12, 24, 'A doista ga je željela i poželio bi (on) nju da nije vidio jasan znak Gospodara svog. Tako je bilo, da bismo otklonili od njega zlo i razvrat. Uistinu, on je bio od robova Naših iskrenih.'),
(1621, 12, 25, 'I potrčaše vratima, i razdera košulju njegovu otpozadi, i zatekoše gospodara njenog kod vrata. Rekla je: "Šta je plaća onom ko tvojoj porodici poželi zlo, osim da bude zatvoren - ili kazna bolna?"'),
(1622, 12, 26, '(Jusuf) reče: "Ona je htjela da me zavede." I posvjedoči svjedok iz porodice njene: "Ako je košulja njegova razderana sprijeda, onda je istinu rekla, a on je od lažaca.'),
(1623, 12, 27, 'A ako je košulja njegova razderana otpozadi, onda je slagala, a on je od onih koji istinu govore."'),
(1624, 12, 28, 'Pa pošto vidje košulju njegovu razderanu otpozadi, reče: "Uistinu, to je neka vaša (ženska) spletka. Uistinu, spletka vaša je strašna."'),
(1625, 12, 29, 'Jusufe! Ostavi se ovog; i traži (o ženo) oprost za grijeh svoj. Uistinu, ti si od onih koji čine greške.'),
(1626, 12, 30, 'I govoriše žene u gradu: "Žena Azizova je nastojala da zavede slugu svog, doista je nju zaludio ljubavlju. Uistinu, mi je vidimo u zabludi očitoj."'),
(1627, 12, 31, 'Pa pošto je čula spletku njihovu, posla po njih i pripremi za njih naslonjač, i dade svakoj od njih pojedinačno nož i reče: "Izađi pred njih!" Pa pošto ga vidješe, zadiviše mu se i posjekoše ruke svoje i rekoše: "Hašelillah! Nije ovo čovjek, ovo je jedino melek plemeniti."'),
(1628, 12, 32, 'Reče: "Pa to vam je onaj zbog kojeg ste me korile. I doista, nastojala sam da ga zavedem, pa se uzdržao. I ako ne učini šta mu naređujem, sigurno će biti zatvoren i sigurno će biti od poniženih."'),
(1629, 12, 33, 'Reče: "Gospodaru moj! Tamnica mi je draža od onog čemu me pozivaju; i ako ne odvratiš od mene spletku njihovu, težit ću njima i biću od džahila."'),
(1630, 12, 34, 'Tad mu se odazva Gospodar njegov, te odvrati od njega spletku njihovu. Uistinu On, On je Onaj koji čuje, Znalac.'),
(1631, 12, 35, 'Zatim im se dogodi, nakon što su vidjeli znakove, da ga zatvore neko vrijeme.'),
(1632, 12, 36, 'I s njim u tamnicu uđoše dva mladića. Jedan od njih dvojice reče: "Uistinu ja, ja sam vidio (da) cijedim vino." A drugi reče: "Uistinu ja, ja sam vidio (kako) na svojoj glavi nosim hljeb kojeg je jela ptica." (Rekoše): "Obavijesti nas o tumačenju toga, uistinu, mi vidimo (da) si ti od dobročinitelja."'),
(1633, 12, 37, 'Reče: "Vama dvojici neće doći hrana kojom se opskrbljujete, a da vas ne obavijestim o tumačenju toga prije no vam dođe. To je od onog šta me je naučio Gospodar moj. Uistinu! Ja sam napustio millet naroda koji ne vjeruje u Allaha, a oni su u Ahiret nevjernici,'),
(1634, 12, 38, 'I slijedim millet predaka svojih, Ibrahima i Ishaka i Jakuba. Nije za nas da Allahu pridružujemo ikakvu stvar. To je iz blagodati Allahove prema nama i prema ljudima; međutim, većina ljudi ne zahvaljuje.'),
(1635, 12, 39, 'O drugovi moji u tamnici! Jesu li gospodi raznoliki bolji, ili Allah Jedini, Onaj koji pokorava?'),
(1636, 12, 40, 'Obožavate mimo Njega samo imena koja ste nadjenuli vi i preci vaši - nije objavio Allah za njih nikakav dokaz. Sud je samo Allahov. Naredio je da obožavate jedino Njega. To je vjera prava, međutim, većina ljudi ne zna.'),
(1637, 12, 41, 'O drugovi moji u tamnici! Što se tiče jednog od vas dvojice, pa napajaće gospodara svog vinom; a što se tiče drugog, pa biće raspet te će jesti ptica iz glave njegove. Presuđena je stvar - ona o kojoj pitate."'),
(1638, 12, 42, 'I reče onom od njih dvojice za kojeg je znao da će on biti spašen: "Spomeni me kod gospodara svog." Pa učini šejtan da on zaboravi spomen gospodaru njegovom, te provede u tamnici nekoliko godina.'),
(1639, 12, 43, 'I reče vladar: "Uistinu! Ja sam vidio sedam krava debelih, jede ih sedam mršavih, i sedam klasova zelenih i drugih suhih. O uglednici! Objasnite mi snoviđenje moje, ako san tumačite."'),
(1640, 12, 44, 'Rekoše: "Zbrka od snova! A nismo mi za tumačenje snova znalci."'),
(1641, 12, 45, 'I onaj od njih dvojice koji se spasio i sjetio nakon (dugo) vremena, reče: "Ja ću vas obavijestiti o tumačenju njegovom, zato pošaljite mene."'),
(1642, 12, 46, '"Jusufe! O iskreni! Objasni nam (san) o sedam krava debelih (kako) ih jede sedam mršavih, i sedam klasova zelenih i drugih suhih, da bih se ja vratio ljudima, da bi oni znali."'),
(1643, 12, 47, 'Reče: "Sijaćete sedam godina uobičajeno. Pa šta pokosite, tad to ostavite u klasu njegovom, osim malo od čega ćete jesti.'),
(1644, 12, 48, 'Zatim, doći će poslije toga sedam žestokih, (koje) će pojesti šta ste pripremili za njih, izuzev malo šta ste sačuvali.'),
(1645, 12, 49, 'Zatim, doći će poslije toga godina u kojoj će ljudima kišiti, i u kojoj će cijediti."'),
(1646, 12, 50, 'I reče vladar: "Dovedite mi ga!" Pa pošto mu izaslanik dođe, (Jusuf) reče: "Vrati se gospodaru svom pa ga upitaj kakvo je stanje žena koje su posjekle ruke svoje. Uistinu, Gospodar moj je o spletki njihovoj Znalac."'),
(1647, 12, 51, 'Reče: "Šta je bila stvar vaša kad ste nastojale da Jusufa zavedete?" Rekoše: "Hašelillah! Ne znamo o njemu ništa zlog." Žena Azizova reče: "Sad će se pokazati istina. Ja sam nastojala da ga zavedem, a uistinu, on je od onih koji istinu govore."'),
(1648, 12, 52, 'To zato da zna da ga ja nisam izdao tajno, i da Allah ne vodi spletku izdajica.'),
(1649, 12, 53, 'I ne oslobađam dušu svoju. Uistinu, duša je sklona zlu, izuzev onom čemu se smiluje Gospodar moj. Uistinu, Gospodar moj je Oprosnik, Milosrdni.'),
(1650, 12, 54, 'I reče vladar: "Dovedite mi ga, izabraću ga za dušu svoju." Pa pošto je govorio s njim, reče: "Uistinu, ti si danas kod nas uticajan, pouzdan."'),
(1651, 12, 55, 'Reče: "Postavi me nad magacinima zemlje. Uistinu, ja sam čuvar, znalac."'),
(1652, 12, 56, 'I tako učvrstismo Jusufa na Zemlji, stupao je po njoj kud god je htio. Pogađamo milošću Našom koga hoćemo i ne uništavamo nagradu dobročinitelja.'),
(1653, 12, 57, 'A sigurno je nagrada Ahireta bolja za one koji vjeruju i boje se (Allaha).'),
(1654, 12, 58, 'I dođoše braća Jusufova, te mu uđoše, pa ih prepozna, a oni njega ne prepoznaše.'),
(1655, 12, 59, 'I pošto ih je snabdio potrepštinom njihovom, reče: "Dovedite mi brata vašeg, od oca vašeg. Zar ne vidite da ja dajem po mjeri i da sam ja najbolji od uslužitelja?'),
(1656, 12, 60, 'Pa ako mi ga ne dovedete, tad kod mene neće za vas biti mjere, i ne približujte mi se."'),
(1657, 12, 61, 'Rekoše: "Nastojaćemo ga pridobiti od oca njegovog, a uistinu, mi smo djelatni."'),
(1658, 12, 62, 'I reče (Jusuf) momcima svojim: "Stavite robu njihovu u vreće njihove, da bi je oni prepoznali kad se vrate porodici svojoj, da bi se oni vratili."'),
(1659, 12, 63, 'Pa pošto se vratiše ocu svom, rekoše: "O oče naš! Uskraćena nam je mjera, zato pošalji s nama brata našeg, dobićemo mjeru, a uistinu, mi ćemo biti njegovi čuvari."'),
(1660, 12, 64, 'Reče: "Zar da vam ga povjerim, osim kao što sam vam povjerio brata njegovog ranije? Pa Allah je Najbolji u čuvanju i On je Najmilosrdniji od milosrdnih."'),
(1661, 12, 65, 'I pošto otvoriše svoj prtljag nađoše svoju robu njima vraćenu. Rekoše: "O oče naš! Šta da tražimo? Evo naše robe, vraćena nam je, i snabdjećemo porodicu našu i čuvaćemo brata našeg, i dodat ćemo mjeru deve. To je mjera lahka."'),
(1662, 12, 66, '(Ja\'kub) reče: "Neću ga poslati s vama, dok mi ne date zavjet (u ime) Allaha, da ćete mi ga sigurno dovesti, izuzev ako vi budete opkoljeni." Pa pošto mu dadoše zavjet svoj, reče: "Allah je nad onim šta govorimo, Jamac."'),
(1663, 12, 67, 'I reče: "O sinovi moji! Ne ulazite na jednu kapiju, a ulazite na kapije različite; i neću vam ja koristiti protiv Allaha nimalo. Uistinu, sud je samo Allahov. U Njega se pouzdavam, i u Njega zato neka se pouzdaju oni koji se uzdaju."'),
(1664, 12, 68, 'I pošto uđoše kako im je naredio otac njihov, nije im pomoglo protiv Allaha ništa, izuzev žudnje u duši Jakubovoj, (koju) je zadovoljio; a uistinu, on je bio posjednik znanja za ono čemu smo ga naučili; međutim, većina ljudi ne zna.'),
(1665, 12, 69, 'I pošto uđoše Jusufu, (on) uze sebi brata svog. Reče: "Uistinu! Ja, ja sam brat tvoj, zato se ne žalosti zbog onoga šta su uradili."'),
(1666, 12, 70, 'Pa pošto ih je snabdio potrepštinom njihovom, stavi pehar za piće u vreću brata svog, potom pozva muezzin: "O karavano! Uistinu, vi ste kradljivci!"'),
(1667, 12, 71, 'Rekoše - a približavali su se njima: "Šta ste to zagubili?"'),
(1668, 12, 72, 'Rekoše: "Zagubili smo pehar vladarev, a ko ga donese imaće on devin tovar, a ja sam tome jamac."'),
(1669, 12, 73, 'Rekoše: "Tako nam Allaha, doista znate da nismo došli da činimo fesad na Zemlji i nismo lopovi."'),
(1670, 12, 74, 'Rekoše: "Pa šta je kazna za to, ako budete lažljivci?"'),
(1671, 12, 75, 'Rekoše: "Kazna za to. Onaj u čijoj vreći bude nađen - pa on je kazna za to. Tako kažnjavamo zalime."'),
(1672, 12, 76, 'Tad poče s vrećama njihovim prije vreće brata svog, zatim ga izvadi iz vreće brata svog. Tako smo splanili za Jusufa. Mogao je po zakonu vladarevom uzeti brata svog, jedino što je htio Allah. Uzvisujemo stepene kome hoćemo, a nad svakim posjednikom znanja je Znalac.'),
(1673, 12, 77, 'Rekoše: "Ako krade, pa već je ukrao brat njegov prije." Tad to pritaji Jusuf u duši svojoj i ne otkri to njima. Reče: "Vi ste u gorem položaju, a Allah je Najbolji znalac onog što navodite."'),
(1674, 12, 78, 'Rekoše: "O vladaru! Uistinu, njegov otac je jako star, zato uzmi jednog od nas umjesto njega. Uistinu, mi te vidimo (jednim) od onih koji dobro čine."'),
(1675, 12, 79, 'Reče: "Sačuvaj Allahu! Da uzmemo, izuzev onog kod koga smo našli stvar našu; uistinu, mi bismo tad bili zalimi."'),
(1676, 12, 80, 'Pa pošto izgubiše nadu u njega, odvojiše se za dogovor. Najstariji od njih reče: "Zar ne znate da je vaš otac već uzeo od vas zavjet (u ime) Allaha?, i kako ste prije zanemarili Jusufa. Zato neću napustiti (ovu) zemlju dok mi ne dopusti otac moj ili mi Allah dosudi; a On je Najbolji od sudaca.'),
(1677, 12, 81, 'Vratite se ocu svom i recite: "O oče naš! Uistinu, sin tvoj je ukrao. A ne svjedočimo, izuzev ono šta znamo, i nismo za nevidljivo čuvari.'),
(1678, 12, 82, 'I pitaj grad - onaj u kojem smo bili - i karavan s kojim smo putovali, a doista, mi istinu govorimo."'),
(1679, 12, 83, '(Ja\'kub) reče: "Naprotiv, zavele su vas duše vaše (toj) stvari. Pa strpljenje je lijepo. Možda će Allah sve da mi ih dovede. Uistinu! On, On je Znalac, Mudri."'),
(1680, 12, 84, 'I okrenu se od njih i reče: "O žalosti moja nad Jusufom!" A izbijelilo je oko njegovo od žalosti, pa se on savlađivao.'),
(1681, 12, 85, 'Rekoše: "Tako nam Allaha, ne prestaješ spominjati Jusufa, dok ne budeš poremećen ili budeš od propalih!"'),
(1682, 12, 86, 'Reče: "Samo se žalim za jad svoj i tugu svoju Allahu, a znam od Allaha ono šta ne znate.'),
(1683, 12, 87, 'O sinovi moji! Idite pa se obavijestite o Jusufu i bratu njegovom, i ne gubite nadu u rewh Allahov. Uistinu, u rewh Allahov nadu gubi jedino narod nevjernika."'),
(1684, 12, 88, 'Pa pošto uđoše kod njega, rekoše: "O vladaru! Dotakla je nas i porodicu našu nevolja, donijeli smo robu beznačajnu. Pa napuni nam mjeru i daj nam sadaku. Uistinu! Allah će nagraditi davaoce sadake."'),
(1685, 12, 89, 'Reče: "Znate li šta ste učinili sa Jusufom i bratom njegovim, kad ste vi bili neznalice?"'),
(1686, 12, 90, 'Rekoše: "Jesi li uistinu ti, ti Jusuf?" Reče: "Ja sam Jusuf, a ovo je brat moj. Doista nas je Allah obdario. Uistinu, onaj ko se boji (Allaha) i strpi se - pa uistinu, Allah neće dati da propadne nagrada dobročinitelja."'),
(1687, 12, 91, 'Rekoše: "Tako nam Allaha, doista te je Allah odlikovao nad nama, a zaista smo bili činitelji grešaka."'),
(1688, 12, 92, 'Reče: "Nema vam prijekora danas; vama će oprostiti Allah, a On je Najmilosrdniji od milosrdnih.'),
(1689, 12, 93, 'Otiđite sa ovom mojom košuljom, te je bacite na lice oca mog, postaće onim koji vidi; i dođite mi sa svojim porodicama svi."'),
(1690, 12, 94, 'I pošto ode karavan, otac njihov reče: "Uistinu, ja nalazim miris Jusufov, makar smatrali da buncam."'),
(1691, 12, 95, 'Rekoše: "Tako nam Allaha, uistinu si ti u zabludi svojoj staroj."'),
(1692, 12, 96, 'Pa pošto dođe donosilac radosnih vijesti, baci je na lice njegovo, pa se povrati u onog koji vidi. Reče: "Zar vam nisam rekao: \'Ja uistinu znam od Allaha ono šta ne znate?"'),
(1693, 12, 97, 'Rekoše: "O oče naš! Traži nam oprost grijeha naših, uistinu, mi smo bili oni koji čine greške."'),
(1694, 12, 98, 'Reče: "Tražiću vam oprost Gospodara svog. Uistinu! On je Oprosnik, Milosrdni."'),
(1695, 12, 99, 'Pa pošto uđoše Jusufu, primi k sebi roditelje svoje i reče: "Uđite u Egipt, inšaAllah, sigurni!"'),
(1696, 12, 100, 'I podiže roditelje svoje na prijesto i padoše njemu ničice, i reče: "O oče moj! Ovo je tumačenje snoviđenja mog od prije. Doista ga je učinio Gospodar moj istinitim, i doista mi je učinio dobro kad me je izveo iz tamnice i doveo vas iz pustinje, nakon što je šejtan posijao razdor između mene i braće moje. Uistinu, Gospodar moj je fin kome hoće. Uistinu On, On je Znalac, Mudri.'),
(1697, 12, 101, 'Gospodaru moj! Doista si mi dao nešto vlasti i naučio me tumačenju hadisa - Stvoritelju nebesa i Zemlje. Ti si Zaštitnik moj na Dunjau i Ahiretu. Uzmi me (kao) muslimana, i priključi me dobrima."'),
(1698, 12, 102, 'To je (nešto) od vijesti nevidljivog kojeg objavljujemo tebi. I nisi bio kod njih kad su pripremali stvar svoju, a oni su spletkarili.'),
(1699, 12, 103, 'A nisu većina ljudi - makar žudio - vjernici.'),
(1700, 12, 104, 'I ne tražiš od njih za to nikakvu nagradu. To je samo Opomena svjetovima.'),
(1701, 12, 105, 'A koliko je znakova na nebesima i Zemlji, prolaze pored njih, a oni su od njih odvraćeni!'),
(1702, 12, 106, 'A većina njih ne vjeruje u Allaha, izuzev kao mušrici.'),
(1703, 12, 107, 'Pa jesu li sigurni da im neće doći pokrov kazne Allahove ili im doći Čas iznenada, a oni neće opaziti?'),
(1704, 12, 108, 'Reci: "Ovo je put moj: pozivam Allahu sa uvidom, ja i ko me slijedi. I slava neka je Allahu; a nisam ja od mušrika."'),
(1705, 12, 109, 'I prije tebe smo slali samo ljude kojima smo objavljivali, od stanovnika gradova. Pa zar nisu putovali po Zemlji, te vidjeli kakav je bio kraj onih prije njih? A sigurno je kuća Ahireta bolja za one koji se boje. Pa zar ne razumijete?'),
(1706, 12, 110, 'Dok, kad su očajavali poslanici i mislili da će oni doista biti negirani, došla bi im pomoć Naša, pa bi bio spasen onaj koga smo htjeli. I neće biti vraćena sila Naša od naroda prestupnika.'),
(1707, 12, 111, 'Doista je u pričama njihovim ibret za posjednike razuma. Nije priča koja se izmišlja, nego je potvrda onome prije nje i objašnjenje svake stvari, i Uputa i milost za ljude koji vjeruju.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Alif. Lam. Mim. Ra. To su znaci Kitaba; a ono što ti se objavljuje od Gospodara tvog, Istina je, međutim, većina ljudi ne vjeruje.'),
(1709, 13, 2, 'Allah je Taj koji je podigao nebesa bez stubova koje vidite, zatim se postavio na Arš. I potčinio je Sunce i Mjesec; svakoje plovi do roka određenog. Upravlja stvar; razlaže znakove, da biste vi u susret Gospodara svog bili sigurni.'),
(1710, 13, 3, 'I On je Taj koji je rasprostro Zemlju i načinio na njoj planine stabilne i rijeke, i od svakog od plodova na njoj načinio je par, dvoje. Prekriva dan noću. Uistinu! U tome su znaci za ljude koji razmišljaju.'),
(1711, 13, 4, 'I na Zemlji su predjeli susjedni, i bašče grožđa i usjevi i palme u parovima i koje nisu parnjaci, a napajane vodom jednom. I istakli smo ih jedne nad drugima po ukusu. Uistinu! U tome su znaci za ljude koji razumiju.'),
(1712, 13, 5, 'A ako se čudiš, pa čudo su riječi njihove: "Zar ćemo kad budemo prašina, uistinu mi u stvaranje novo?" Takvi su oni koji ne vjeruju u Gospodara svog, a to su oni na čijim vratovima su okovi i takvi će stanovnici vatre biti. U njoj će vječno biti.'),
(1713, 13, 6, 'I požuruju te zlu prije dobra, a već su prije njih prošli primjeri (kazne). A uistinu, Gospodar tvoj je Vlasnik oprosta prema ljudima, uprkos zulma njihovog; a uistinu, Gospodar tvoj je žestok kaznom.'),
(1714, 13, 7, 'I govore oni koji ne vjeruju: "Zašto mu se ne spusti znak od Gospodara njegovog?" Ti si samo opominjač, a svaki narod imao je vodiča.'),
(1715, 13, 8, 'Allah zna šta svaka žena nosi i šta umanjuju materice i šta uvećavaju; a svaka stvar kod Njega je po mjeri.'),
(1716, 13, 9, 'Znalac nevidljivog i vidljivog, Veliki, Preuzvišeni!'),
(1717, 13, 10, 'Isti je od vas onaj ko skriva govor i ko ga oglašava, i onaj ko je skriven noću i onaj ko hodi danju.'),
(1718, 13, 11, 'Ima on uzastopne (meleke) ispred sebe i iza sebe, čuvaju ga po naredbi Allahovoj. Uistinu! Allah ne mijenja šta je u narodu, dok ne promijenu šta je u dušama njihovim. A kad Allah želi narodu zlo, tad tome nema odbijanja, a nemaju oni mimo Njega nikakva zaštitnika.'),
(1719, 13, 12, 'On je Taj koji vam pokazuje munju, strah i nadu, i stvara oblake teške.'),
(1720, 13, 13, 'I slavi (Ga) grmljavina sa hvalom Njegovom, i meleci iz strahopoštovanja Njegovog; i šalje gromove, te pogađa njima koga hoće, a oni se prepiru o Allahu, a On je žestoke sile.'),
(1721, 13, 14, 'Njemu je priziv istinski. A oni koje prizivaju mimo Njega, ne odgovaraju im ništa, (oni) su samo kao onaj ko ispruži svoj dlan prema vodi da bi stigla ustima njegovim - a neće mu ona stići; a dowa nevjernika je samo zabluda.'),
(1722, 13, 15, 'I Allahu pada na sedždu ko je na nebesima i Zemlji, milom i silom, i sjenke njihove, ranim jutrima i predvečerjima.'),
(1723, 13, 16, 'Reci: "Ko je Gospodar nebesa i Zemlje?" Reci: "Allah!" Reci: "Pa zar uzimate mimo Njega zaštitnike, (koji) neće vladati za duše svoje korišću, niti štetom?" Reci: "Da li je jednak slijepac i onaj koji vidi? Ili, da li su jednake tmine i svjetlost?" Zar su načinili Allahu ortake (koji) stvaraju slično stvaranju Njegovom, pa im stvaranje izgleda nalik?" Reci: "Allah je Stvoritelj svake stvari, a On je Jedini, Onaj koji pokorava."'),
(1724, 13, 17, 'Spušta s neba vodu pa teče koritima s mjerom njihovom, te bujica nosi pjenu narastajuću - a od onog šta pale u vatri, tražeći ukrase ili izrađevine - troska je slična toj. Tako Allah navodi za primjer Istinu i laž. Pa što se tiče troske, tad odlazi (kao) naplavina; a što se tiče onog što koristi ljudima, pa ostaje u Zemlji; tako Allah navodi primjere.'),
(1725, 13, 18, 'Onima koji se odazovu Gospodaru svom, biće najbolje. A koji Mu se ne odazovu, da je njihovo sve što je na Zemlji i slično tome uz to, sigurno bi se time iskupljivali. Ti takvi će imati zao obračun i njihovo sklonište biće Džehennem, a loše je odmorište!'),
(1726, 13, 19, 'Pa zar je onaj ko zna da je ono što ti se objavljuje od Gospodara tvog Istina, kao onaj ko je slijep? Jedino se poučavaju imaoci razuma;'),
(1727, 13, 20, 'Oni koji ispunjavaju zavjet Allahu i ne krše ugovor;'),
(1728, 13, 21, 'I oni koji sastavljaju ono šta je Allah naredio da se sjedini, i plaše se Gospodara svog i boje zlog obračuna;'),
(1729, 13, 22, 'I oni koji trpe tražeći lice Gospodara svog, i obavljaju salat i udjeljuju od onog čim smo ih opskrbili, tajno i javno, i uzvraćaju zlo dobrim. Ti takvi će (kao) ishod imati dom,'),
(1730, 13, 23, 'Bašče Adna u koje će ući, i onaj ko je činio dobro od očeva njihovih i žena njihovih i potomaka njihovih; a meleci će im ulaziti sa svake kapije,'),
(1731, 13, 24, '"Selamun alejkum, zato što ste se strpili. Pa divna je kuća (Ahireta kao) rezultat."'),
(1732, 13, 25, 'A oni koji krše zavjet Allahu nakon ugovaranja njegovog i kidaju šta je Allah naredio da se sjedini, i čine fesad na Zemlji - ti takvi će imati prokletstvo i imaće oni zao dom.'),
(1733, 13, 26, 'Allah pruža opskrbu kome hoće i uskraćuje; i raduju se u životu Dunjaa, a život Dunjaa je samo užitak u (odnosu) na Ahiret.'),
(1734, 13, 27, 'A oni koji ne vjeruju, govore: "Što mu se ne spusti znak od Gospodara njegovog?" Reci: "Uistinu! Allah zabluđuje koga hoće, a Sebi upućuje onog ko se obrati,'),
(1735, 13, 28, 'One koji vjeruju, i one čija se srca spomenom Allaha smiruju. Besumnje! Spomenom Allaha srca se smiruju! -'),
(1736, 13, 29, 'Oni koji vjeruju i čine dobra djela: ugodnost će oni imati i lijepo povratište."'),
(1737, 13, 30, 'Tako smo tebe poslali u zajednicu prije koje su već prošle zajednice, da im učiš ono šta ti objavljujemo, a oni ne vjeruju u Milostivog. Reci: "On je Gospodar moj; nema boga osim Njega; u Njega se pouzdavam i Njemu je povratak moj."'),
(1738, 13, 31, 'A da je da se Kur\'anom pokrenu planine ili njime rasječe Zemlja ili njime progovore mrtvi! Naprotiv, Allahova je odredba svaka! Pa zar nije očajno (jasno) onima koji vjeruju - da hoće Allah - sigurno bi sve ljude uputio? I neće prestati da pogađa udar one koji ne vjeruju zbog onog šta rade, ili padati blizu boravišta njihovog, dok ne dođe obećanje Allahovo. Uistinu! Allah ne krši obećanje.'),
(1739, 13, 32, 'I doista su poslanici prije tebe bili ismijavani, pa sam puštao one koji nisu vjerovali, zatim bih ih dograbio. Pa kakva je bila kazna Moja!'),
(1740, 13, 33, 'Pa da li je On Taj koji stoji nad svakom dušom - onim šta zaradi? I načinili su Allahu ortake. Reci: "Imenujte ih." Zar ćete Njega obavijestiti o onom šta ne zna na Zemlji, ili je (to) vanjština govora? Naprotiv, uljepšana je onima koji ne vjeruju spletka njihova i odvraćaju od puta. A koga Allah zabludi, tad za njega nema nikakva upućivača.'),
(1741, 13, 34, 'Imaće oni kaznu u životu Dunjaa, a sigurno je kazna Ahireta mučnija, i neće oni imati od Allaha nikakva spasitelja.'),
(1742, 13, 35, 'Primjer Dženneta koji je obećan bogobojaznima: teku ispod njega rijeke; plodovi njegovi su stalni i hladovina njegova; to je rezultat onih koji se boje (Allaha), a rezultat nevjernika je vatra.'),
(1743, 13, 36, 'A oni kojima smo dali Knjigu, raduju se onom šta ti je objavljeno, a od stranaka je onaj ko poriče dio toga. Reci: "Samo mi je naređeno da obožavam Allaha i da Mu ne pridružujem. Njemu pozivam i Njemu je povratak moj."'),
(1744, 13, 37, 'I tako smo ga objavili kao Sud na arapskom; a ako bi slijedio strasti njihove, nakon što ti je došlo nešto znanja, ne bi ti imao od Allaha nikakva zaštitnika, niti spasitelja.'),
(1745, 13, 38, 'I doista, slali smo poslanike prije tebe i davali im žene i potomstvo. I nije bilo za poslanika da donese znak, izuzev s dopuštanjem Allahovim. Za svaki rok ima Knjiga.'),
(1746, 13, 39, 'Briše Allah šta hoće i utvrđuje, a kod Njega je Majka knjiga.'),
(1747, 13, 40, 'I ako ti pokažemo dio onog čime im prijetimo ili te uzmemo, pa samo je na tebi dostava, a na Nama je obračun.'),
(1748, 13, 41, 'Zar ne vide da Mi dolazimo Zemlji, umanjujući je s krajeva njenih? A Allah presuđuje - nema tog ko će suzbiti presudu Njegovu; a On je brz obračunom.'),
(1749, 13, 42, 'I doista su spletkarili oni prije njih; pa Allahovo je svo planiranje. Zna šta svaka duša zarađuje, a saznaće nevjernici ko će za rezultat imati kuću (Ahireta).'),
(1750, 13, 43, 'I govore oni koji ne vjeruju: "Nisi izaslanik!" Reci: "Dovoljan je Allah svjedok između mene i između vas i onaj kod kog je znanje Knjige."');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Alif. Lam. Ra. Knjiga - objavljujemo ti je da izvedeš ljude iz tmina na svjetlost s dozvolom Gospodara njihovog, na put Moćnog, Hvaljenog,'),
(1752, 14, 2, 'Allaha, Čije je šta je u nebesima i šta je na Zemlji. A teško nevjernicima od kazne žestoke!,'),
(1753, 14, 3, 'Onima koji više vole život Dunjaa od Ahireta i odvraćaju od puta Allahovog i traže iskrivljenost u njemu; takvi su u zabludi dalekoj.'),
(1754, 14, 4, 'I slali smo svakog poslanika samo s jezikom naroda njegovog, da im objasni, pa Allah zabluđuje koga hoće, a upućuje koga hoće; a On je Moćni, Mudri!'),
(1755, 14, 5, 'I doista smo poslali Musaa sa znakovima Našim: "Izvedi narod svoj iz tmina na svjetlost i opomeni ih danima Allahovim." Uistinu, u tome su znaci svakom strpljivom, zahvalnom.'),
(1756, 14, 6, 'I kad reče Musa narodu svom: "Sjetite se blagodati Allahove nad vama, kad vas je izbavio od ljudstva faraovnovog, mučili su vas zlom kaznom i klali sinove vaše, a davali da žive žene vaše; a u tome je bilo strašno iskušenje od Gospodara vašeg."'),
(1757, 14, 7, 'I kad najavi Gospodar vaš: "Ako budete zahvaljivali, sigurno ću vam uvećati; a ako ne budete zahvaljivali, uistinu, kazna Moja je žestoka."'),
(1758, 14, 8, 'I reče Musa: "Ako budete nezahvalni vi i onaj ko je na Zemlji, svi, pa uistinu, Allah je Neovisni, Hvaljeni."'),
(1759, 14, 9, 'Zar vam nije stigla vijest onih prije vas: naroda Nuhovog i Ada, i Semuda i onih poslije njih? Zna ih samo Allah. Dolazili su im poslanici njihovi sa jasnim znacima, pa su stavljali ruke svoje u usta svoja, i govorili: "Uistinu, mi ne vjerujemo u ono s čim ste poslani, i uistinu, mi smo prema onom čemu nas pozivate u sumnji ozbiljnoj."'),
(1760, 14, 10, 'Rekli bi poslanici njihovi: "Zar je u Allaha sumnja, Stvoritelja nebesa i Zemlje? Poziva vas da vam oprosti grijehe vaše i ostavi vas do roka određenog." Rekli bi: "Vi ste samo smrtnici slični nama! Želite da nas odvratite od onog šta obožavaju očevi naši, zato nam dajte dokaz jasan."'),
(1761, 14, 11, 'Poslanici njihovi bi im rekli: "Mi smo samo smrtnici slični vama, međutim Allah obdaruje koga hoće od robova Svojih. I nije za nas da vam donosimo dokaz, izuzev s dozvolom Allahovom. I u Allaha zato neka se pouzdaju vjernici!'),
(1762, 14, 12, 'A zašto da se ne pouzdamo u Allaha, a već nas je uputio putevima našim. I sigurno ćemo se strpiti nad onim čim nas uznemiravate; a u Allaha zato neka se pouzdaju oni koji se pouzdavaju."'),
(1763, 14, 13, 'A oni koji nisu vjerovali rekli bi, poslanicima svojim: "Sigurno ćemo vas protjerati iz zemlje naše, ili ćete se sigurno vratiti u millet naš." Tad bi im objavljivao Gospodar njihov: "Sigurno ćemo uništiti zalime,'),
(1764, 14, 14, 'I sigurno ćemo Zemlju nastaniti vama, poslije njih. To je za onog ko se boji stajanja preda Mnom i boji prijetnje Moje."'),
(1765, 14, 15, 'I tražili su presudu (pobjedu), i ne bi uspio niti jedan buntovni, svojeglavi.'),
(1766, 14, 16, 'Ispred njega je Džehennem, a biće napajan vodom gnojavom,'),
(1767, 14, 17, 'Žderaće je, a jedva da će je progutati, i dolaziće mu smrt sa svakog mjesta, a neće on biti mrtav, i ispred njega će biti kazna gruba.'),
(1768, 14, 18, 'Primjer onih koji ne vjeruju u Gospodara svog: Djela njihova su kao pepeo kojeg raznese vjetar u danu olujnom. Neće upravljati ni nad čim od onog šta su zaradili. To je ta zabluda daleka.'),
(1769, 14, 19, 'Zar nisi vidio da je Allah stvorio nebesa i Zemlju s Istinom? Ako hoće, odstraniće vas, a dovešće stvorenje novo;'),
(1770, 14, 20, 'A nije to za Allaha teško.'),
(1771, 14, 21, 'I izaći će Allahu svi, pa će slabi reći onima koji su se oholili: "Uistinu, mi smo bili vaše pristalice, pa hoćete li vi biti ti koji će nam imalo koristiti protiv kazne Allahove?" Reći će: "Da je nas uputio Allah, sigurno bismo (mi) vas uputili. Isto nam je uzrujavali se ili trpili; nema za nas nikakva pribježišta."'),
(1772, 14, 22, 'I reći će šejtan, pošto stvar bude presuđena: "Uistinu! Allah vam je dao obećanje istinito, i obećao sam vam, pa sam vas iznevjerio. I nisam ja nad vama imao nikakvu vlast osim što sam vas pozivao, pa ste mi se odazvali. Zato ne korite mene, a korite duše svoje. Nisam ja pomagač vaš, niti ste vi pomagači moji. Uistinu, ja sam poricao to što ste me pridruživali ranije." Uistinu, zalimi - imaće oni kaznu bolnu.'),
(1773, 14, 23, 'I biće dato da oni koji vjeruju i čine dobra djela uđu u bašče ispod kojih teku rijeke, vječno će biti u njima s dozvolom Gospodara njihovog. Pozdrav njihov u njima će biti: "Selam!"'),
(1774, 14, 24, 'Zar ne vidiš kako Allah navodi primjer riječi dobre kao drveta dobrog: korijen njegov je čvrst, a njegova grana u nebu,'),
(1775, 14, 25, 'Daje plodove svoje svake sezone s dozvolom Gospodara svog; a navodi Allah primjere ljudima, da bi se oni podsjetili.'),
(1776, 14, 26, 'A primjer loše riječi je kao loše drvo iščupano nad zemljom, nema ono nikakve stabilnosti.'),
(1777, 14, 27, 'Allah ojačava one koji vjeruju rječju čvrstom u životu Dunjaa i na Ahiretu, a zabluđuje Allah zalime; a Allah čini šta hoće.'),
(1778, 14, 28, 'Zar nisi vidio one koji su blagodat Allahovu zamijenili kufrom i učinili da se narod njihov spusti u kuću propasti,'),
(1779, 14, 29, 'Džehennemu, pržiće se njime, a loše je stanište.'),
(1780, 14, 30, 'I načinili su Allahu suparnike da zavode s puta Njegovog. Reci: "Uživajte! Pa uistinu, dolazište vaše je vatri."'),
(1781, 14, 31, 'Reci robovima Mojim koji vjeruju, (da) obavljaju salat i udjeljuju od onog čim smo ih opskrbili, tajno i javno, prije no dođe Dan u kojem neće biti trgovine, niti prijateljstva.'),
(1782, 14, 32, 'Allah je Taj koji je stvorio nebesa i Zemlju, i spušta s neba vodu, pa izvodi njome voće, (kao) opskrbu za vas; i potčinio vam je plovilo, da plovi morem po naredbi Njegovoj, i potčinio je za vas rijeke;'),
(1783, 14, 33, 'I potčinio je za vas Sunce i Mjesec - oboje (us)trajno, i potčinio je za vas noć i dan,'),
(1784, 14, 34, 'I daje vam od svačeg što od Njega tražite; a ako biste brojali blagodati Allahove, ne biste ih izračunali. Uistinu, čovjek je uveliko zalim, nezahvalnik!'),
(1785, 14, 35, 'I kad reče Ibrahim: "Gospodaru moj! Učini ovaj grad sigurnim, i sačuvaj mene i sinove moje od obožavanja idola.'),
(1786, 14, 36, 'Gospodaru moj! Uistinu, oni su zaveli mnoge od ljudi. Pa ko mene slijedi, pa uistinu on je od mene, a ko me ne posluša - pa uistinu, Ti si Oprosnik, Milosrdni.'),
(1787, 14, 37, 'Gospodaru naš! Uistinu! Ja sam naselio neke od potomaka svojih u dolini neobradivoj, kod Kuće Tvoje Svete, Gospodaru naš, da bi obavljali salat; zato učini da srca ljudi žude ka njima, i opskrbi ih (nekim) od plodova, da bi oni zahvaljivali.'),
(1788, 14, 38, 'Gospodaru naš! Uistinu, Ti znaš šta krijemo i šta obznanjujemo. A neće se sakriti od Allaha nikakva stvar na Zemlji, niti u nebu.'),
(1789, 14, 39, 'Hvala Allahu koji mi je u starosti podario Ismaila i Ishaka! Uistinu, Gospodar moj je Onaj koji čuje dowu.'),
(1790, 14, 40, 'Gospodaru moj! Učini me ustrajnim u salatu i (isto) od potomaka mojih, Gospodaru naš, i primi dowu moju.'),
(1791, 14, 41, 'Gospodaru naš! Oprosti meni i roditeljima mojim i vjernicima na Dan kad krene obračun."'),
(1792, 14, 42, 'I nikako ne misli da je Allah nemaran prema onom šta rade zalimi; samo im odgađa do Dana u kojem će se ukočiti pogledi.'),
(1793, 14, 43, 'Biće trkači uzdignutih glava njihovih, neće im se vraćati treptaj njihov, a srca njihova (kao) vazduh će biti.'),
(1794, 14, 44, 'I upozori ljude Danom (kad) će im doći kazna, pa će reći oni koji su činili zulm: "Gospodaru naš! Ostavi nas do roka bliskog. Odazvaćemo se pozivu Tvom, i slijedićemo poslanike." Zar se niste kleli prije da vam nema nikakva nestanka?'),
(1795, 14, 45, 'A stanovali ste u nastambama onih koji su činili zulm dušama svojim, i bilo vam je jasno kako smo s njima postupili, a naveli smo vam primjere.'),
(1796, 14, 46, 'I doista su pleli spletku svoju, a kod Allaha je spletka njihova, makar spletka njihova bila (takva) da njome brda nestanu'),
(1797, 14, 47, 'Zato nikako ne misli da je Allah prekršilac obećanja Svog poslanicima Svojim. Uistinu! Allah je Moćni, Vlasnik osvete.'),
(1798, 14, 48, 'Na Dan kad bude zamijenjena Zemlja drugačijom Zemljom i nebesa, i kad se pojave Allahu Jedinom, Onom koji pokorava:'),
(1799, 14, 49, 'I vidiš prestupnike Tog dana povezane u lance,'),
(1800, 14, 50, 'Odjeća njihova bit će od katrana, a lica njihova pokrivaće vatra,'),
(1801, 14, 51, 'Da bi isplatio Allah svakoj duši šta je zaradila. Uistinu! Allah je brz obračunom.'),
(1802, 14, 52, 'Ovo je Poruka ljudima - i da se opomenu njome, i da znaju da je On Bog Jedini, i da se pouče posjednici razuma.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Alif. Lam. Ra. To su znaci Kitaba i Kur\'ana jasnog!'),
(1804, 15, 2, 'Često će voljeti oni koji ne vjeruju, da su muslimani.'),
(1805, 15, 3, 'Ostavi ih da jedu i uživaju i neka ih zavara nada. Pa saznaće!'),
(1806, 15, 4, 'I nismo uništili nijedan grad, a da mu Knjiga nije bila poznata.'),
(1807, 15, 5, 'Neće preteći nijedna umma rok svoj, i neće zadržati.'),
(1808, 15, 6, 'I govore: "O ti kojem se objavljuje Zikr, uistinu si ti luđak!'),
(1809, 15, 7, 'Zašto nam ne dovedeš meleke, ako si od iskrenih?"'),
(1810, 15, 8, 'Ne spuštamo meleke, izuzev s Istinom, a oni tad ne bi bili oni kojima se odlaže.'),
(1811, 15, 9, 'Uistinu! Mi, Mi objavljujemo Zikr, i uistinu!, Mi smo njegovi čuvari.'),
(1812, 15, 10, 'I doista, slali smo prije tebe (poslanike) među stranke ranijih (naroda).'),
(1813, 15, 11, 'I nije im došao nijedan poslanik, a da se nisu njime ismijavali.'),
(1814, 15, 12, 'Tako tome činimo put u srca prestupnika.'),
(1815, 15, 13, 'Ne vjeruju u njega, a već je prošao sunnet ranijih.'),
(1816, 15, 14, 'I da im otvorimo kapiju neba, pa da stalno kroz nju uzlaze,'),
(1817, 15, 15, 'Sigurno bi rekli: "Samo su opijeni vidovi naši. Naprotiv, mi smo ljudi opčinjeni."'),
(1818, 15, 16, 'I doista, načinili smo u nebu galaksije i uljepšali ih za posmatrače.'),
(1819, 15, 17, 'I čuvamo ga od svakog šejtana prokletog,'),
(1820, 15, 18, 'Izuzev ko ukrade ono šta se čuje - tad ga slijedi svjetlica očita.'),
(1821, 15, 19, 'I Zemlju - prostrli smo je i razbacali po njoj stabilne planine, i dali da na njoj izraste od svake stvari odmjereno.'),
(1822, 15, 20, 'I načinili smo u njoj sredstva za život vama i onom kome niste opskrbitelji.'),
(1823, 15, 21, 'I nema niti jedne stvari, a da kod Nas nisu riznice njene, a spuštamo je samo s mjerom poznatom.'),
(1824, 15, 22, 'I šaljemo vjetrove oplođavajuće, pa spuštamo s neba vodu, te vas njome napajamo, a niste vi njeni rizničari.'),
(1825, 15, 23, 'A uistinu! Mi, Mi oživljavamo i usmrćujemo i Mi smo Nasljednici.'),
(1826, 15, 24, 'I doista znamo prethodnike vaše, i doista znamo one koji slijede.'),
(1827, 15, 25, 'A uistinu, Gospodar tvoj, On će ih sakupiti. Uistinu! On je Mudri, Znalac.'),
(1828, 15, 26, 'I doista, stvorili smo čovjeka od gline zvečeće, od blata oblikovanog,'),
(1829, 15, 27, 'A džinna - stvorili smo ga ranije od vatre vrele.'),
(1830, 15, 28, 'I kad reče Gospodar tvoj melecima: "Uistinu! Ja sam Taj koji će stvoriti smrtnika od zvečeće ilovače, od blata oblikovanog.'),
(1831, 15, 29, 'Pa kad ga sredim i udahnem u njega od Duha Svog, tad mu padnite ničice."'),
(1832, 15, 30, 'Pa su pali na sedždu meleci, svaki od njih, zajedno,'),
(1833, 15, 31, 'Izuzev Iblisa; odbio je da bude sa onima koji padaju na sedždu.'),
(1834, 15, 32, '(Allah) reče: "O Iblisu! Šta ti je, zar nećeš biti sa onima koji padaju na sedždu?"'),
(1835, 15, 33, 'Reče: "Neću da činim sedždu smrtniku kojeg si stvorio od zvečeće ilovače, od blata oblikovanog."'),
(1836, 15, 34, '(Allah) reče: "Onda izlazi iz njega, pa uistinu, ti si proklet,'),
(1837, 15, 35, 'I uistinu, na tebi će biti prokletstvo do Dana sudnjeg."'),
(1838, 15, 36, 'Reče: "Gospodaru moj! Ta odloži mi do Dana kad će (mrtvi) biti podignuti."'),
(1839, 15, 37, '(Allah) reče: "Pa uistinu, ti si od onih kojima se odlaže,'),
(1840, 15, 38, 'Do Dana, wakta poznatog."'),
(1841, 15, 39, 'Reče: "Gospodaru moj! Zato što si me zaveo, sigurno ću im (zlo) učiniti lijepim na Zemlji i sigurno ću ih sve zavesti,'),
(1842, 15, 40, 'Izuzev robova Tvojih - od njih odanih."'),
(1843, 15, 41, '(Allah) reče: "Ovo je put zbog Mene ispravan!'),
(1844, 15, 42, 'Uistinu, robovi Moji - nemaš ti nad njima vlast, izuzev ko te slijedi od zalutalih."'),
(1845, 15, 43, 'A uistinu, Džehennem je obećano mjesto njih svih:'),
(1846, 15, 44, 'Ima on sedam kapija, svaka od tih kapija ima dio odijeljen.'),
(1847, 15, 45, 'Uistinu, bogobojazni će biti u baščama i (među) izvorima:'),
(1848, 15, 46, '"Uđite u njih sa selamom, sigurni."'),
(1849, 15, 47, 'I istrgnućemo šta je u grudima njihovim od zlobe, (kao) braća će biti na sofama, naspramni.'),
(1850, 15, 48, 'Neće ih u njima dotaći umor, i neće oni iz njih biti izvedeni.'),
(1851, 15, 49, 'Obavijesti robove Moje da sam Ja, Ja Oprosnik, Milosrdni,'),
(1852, 15, 50, 'I da je Moja kazna - to kazna bolna.'),
(1853, 15, 51, 'I obavijesti ih o gostima Ibrahimovim,'),
(1854, 15, 52, 'Kad mu uđoše, pa rekoše: "Selam!" reče: "Uistinu, mi vas se plašimo."'),
(1855, 15, 53, 'rekoše: "Ne boj se! Uistinu, mi ćemo te obradovati dječakom, znalcem."'),
(1856, 15, 54, '(Ibrahim) reče: "Zar ćete me obradovati kad me je pritiskla starost? Pa čime ćete me obradovati?"'),
(1857, 15, 55, 'Rekoše: "Obradovaćemo te Istinom, zato ne budi od očajnih."'),
(1858, 15, 56, 'Reče: "A ko gubi nadu u milost Gospodara svog, osim zalutalih?"'),
(1859, 15, 57, 'Reče: "Pa šta je posao vaš, o izaslanici?"'),
(1860, 15, 58, '(Meleci) rekoše: "Uistinu, mi smo poslani narodu prestupnika,'),
(1861, 15, 59, 'Izuzev porodice Lutove. Uistinu, Mi ćemo ih sve spasiti,'),
(1862, 15, 60, 'Osim žene njegove; odredili smo da ona uistinu bude od izostalih."'),
(1863, 15, 61, 'Pa pošto izaslanici dođoše porodici Lutovoj,'),
(1864, 15, 62, 'Reče: "Uistinu, vi ste ljudi neznani."'),
(1865, 15, 63, 'Rekoše: "Naprotiv, došli smo ti s onim u što sumnjaju,'),
(1866, 15, 64, 'A donijeli smo ti Istinu, i uistinu, mi istinu govorimo.'),
(1867, 15, 65, 'Zato otputuj s porodicom svojom u dijelu noći, i slijedi pozadine njihove, i nek se niko od vas ne okreće, i idite kamo vam se naređuje."'),
(1868, 15, 66, 'I javili smo mu tu stvar, da će korijen takvih biti odsječen u zoru.'),
(1869, 15, 67, 'I dođoše stanovnici grada, radujući se.'),
(1870, 15, 68, '(Lut) reče: "Uistinu, ovo su moji gosti, zato me ne sramotite,'),
(1871, 15, 69, 'I bojte se Allaha, i ne ponižavajte me!"'),
(1872, 15, 70, 'Rekoše: "Zar ti nismo zabranili svjetove?"'),
(1873, 15, 71, 'Reče: "Evo kćeri mojih, ako ste izvršitelji."'),
(1874, 15, 72, 'Tako mi života tvog, uistinu su oni u opijenosti svojoj lutali.'),
(1875, 15, 73, 'Pa ih pogodi krik dok se Sunce rađalo,'),
(1876, 15, 74, 'Te učinismo visoko njegovo niskim njegovim, i sasusmo na njih kamenje od gline otvrdnute.'),
(1877, 15, 75, 'Uistinu! U tome su znaci za one koji čitaju biljege,'),
(1878, 15, 76, 'I uistinu! Oni su uz put postojeći:'),
(1879, 15, 77, 'Uistinu! U tome je znak za vjernike.'),
(1880, 15, 78, 'A doista su stanovnici Ejke bili zalimi:'),
(1881, 15, 79, 'Zato smo im se osvetili; a uistinu, njih dvoje su na drumu vidljivom.'),
(1882, 15, 80, 'I doista su stanovnici Hidžra poricali izaslanike,'),
(1883, 15, 81, 'A dali smo im znakove Naše, pa su od njih bili odvraćeni.'),
(1884, 15, 82, 'I klesali su iz brda kuće sigurni,'),
(1885, 15, 83, 'Pa ih je pogodio krik jutrom,'),
(1886, 15, 84, 'Te im nije koristilo ono što su stekli.'),
(1887, 15, 85, 'I stvorili smo nebesa i Zemlju i ono što je između njih samo s Istinom; a uistinu, Čas dolazi, zato praštaj oprostom lijepim.'),
(1888, 15, 86, 'Uistinu! Gospodar tvoj, On je Tvorac, Znalac.'),
(1889, 15, 87, 'I doista smo ti dali sedam (ajeta) ponavljajućih, i Kur\'an veličanstveni.'),
(1890, 15, 88, 'Ne upravljaj oči svoje onome što smo dali da uživaju parovi između njih, i ne žali za njima, i spusti krilo svoje vjernicima,'),
(1891, 15, 89, 'I reci: "Uistinu! Ja, ja sam opominjač jasan."'),
(1892, 15, 90, 'Kao što smo objavili djeliteljima,'),
(1893, 15, 91, 'Koji su Kur\'an učinili odlomcima:'),
(1894, 15, 92, 'Pa tako mi Gospodara tvog, sigurno ćemo ih sve pitati,'),
(1895, 15, 93, 'O onom šta su radili.'),
(1896, 15, 94, 'Zato iznesi šta ti se naređuje i kloni se mušrika.'),
(1897, 15, 95, 'Uistinu! Mi smo ti dovoljni protiv podrugljivaca,'),
(1898, 15, 96, 'Koji uz Allaha stavljaju boga drugog, pa saznaće.'),
(1899, 15, 97, 'I doista znamo da ti tišti grudi tvoje ono šta govore.'),
(1900, 15, 98, 'Zato slavi sa hvalom Gospodara svog, i budi od onih koji padaju na sedždu,'),
(1901, 15, 99, 'I obožavaj Gospodara svog, dok ti nije došlo zagarantovano.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Doći će naredba Allahova, zato je ne požurujte. Slavljen neka je On i uzvišen od onog šta pridružuju!'),
(1903, 16, 2, 'Spušta meleke s Duhom naredbe Svoje, na koga hoće od robova Svojih: "Upozorite da nema boga osim Mene, zato se Mene bojte."'),
(1904, 16, 3, 'Stvorio je nebesa i Zemlju s Istinom. Uzvišen neka je od onog šta pridružuju!'),
(1905, 16, 4, 'Stvorio je čovjeka od kaplje sjemena, pa gle! on protivnik otvoreni.'),
(1906, 16, 5, 'I stoku - stvorio je nju; imate vi od nje toplotu i koristi, i od nje jedete,'),
(1907, 16, 6, 'I vama je u njoj ljepota kad dogonite i kad izgonite na pašu;'),
(1908, 16, 7, 'I nose terete vaše do zemlje kojoj ćete stići samo naporom duša. Uistinu, Gospodar vaš je Samilosni, Milosrdni;'),
(1909, 16, 8, 'I konja i mazge i magarce - da ih jašete i (za) ukras, a stvara šta ne znate.'),
(1910, 16, 9, 'I na Allahu je usmjeravanje pravom putu, a od njih (puteva) ima zastranjujući. A da je htio, sigurno bi vas sve uputio!'),
(1911, 16, 10, 'On je Taj koji spušta s neba vodu - imate vi od nje piće, i od nje drvlje u kojem napasate.'),
(1912, 16, 11, 'Uzgaja za vas njome usjev i maslinu i palmu i grožđe, i svakovrsne plodove. Uistinu, u tome je znak za ljude koji razmišljaju.'),
(1913, 16, 12, 'I potčinio je za vas noć i dan, i Sunce i Mjesec, i zvijezde su potčinjene naredbi Njegovoj. Uistinu! U tome su znaci za ljude koji razumiju.'),
(1914, 16, 13, 'A ono što je stvorio za vas na Zemlji, različitih je boja svojih. Uistinu! U tome je znak za ljude koji pouku primaju.'),
(1915, 16, 14, 'I On je Taj koji je potčinio more: da jedete iz njega meso svježe i vadite iz njega ukras kojeg nosite. I vidiš plovila brazdaju po njemu - i da tražite iz blagodati Njegove, i da biste vi zahvaljivali.'),
(1916, 16, 15, 'I razbacao je po Zemlji stabilne planine, da vas ne trese, i rijeke i puteve, da biste se vi uputili,'),
(1917, 16, 16, 'I oznake; i po zvijezdi se oni vode.'),
(1918, 16, 17, 'Pa da li je Onaj ko stvara, kao ko ne stvara? Pa zar se nećete poučiti?'),
(1919, 16, 18, 'I ako budete brojali blagodati Allahove, nećete ih izračunati. Uistinu! Allah je Oprosnik, Milosrdni.'),
(1920, 16, 19, 'A Allah zna šta tajite i šta obznanjujete.'),
(1921, 16, 20, 'A oni kojima se (oni) mole mimo Allaha, ne stvaraju ništa, a oni bivaju stvarani;'),
(1922, 16, 21, 'Mrtvi su, nisu živi, i ne opažaju kad će biti podignuti.'),
(1923, 16, 22, 'Bog vaš je Bog Jedini! Pa oni koji ne vjeruju u Ahiret - srca njihova su ta koja poriču, i oni su oholi.'),
(1924, 16, 23, 'Nema sumnje da Allah zna šta skrivaju i šta obznanjuju. Uistinu! On ne voli ohole.'),
(1925, 16, 24, 'I kad im se kaže: "Šta to objavljuje Gospodar vaš?" - kažu: "Priče ranijih",'),
(1926, 16, 25, 'Da bi nosili terete svoje sve na Dan kijameta i nešto od tereta onih koje su zaveli bez znanja. Doista je loše šta će nositi!'),
(1927, 16, 26, 'Doista su spletkarili oni prije njih, pa je došao Allah kućama njihovim od temelja, te je pao na njih krov nad njima, i došla im je kazna otkud nisu zapazili.'),
(1928, 16, 27, 'Zatim će ih na Dan kijameta posramiti i reći: "Gdje su ortaci Moji, oni radi kojih ste uzrokovali rascjep?" Reći će oni kojima je dato znanje: "Uistinu, sramota je Danas i zlo nad nevjernicima,'),
(1929, 16, 28, 'Onima koje su uzimali meleci (dok) su činili zulm dušama svojim." Tad će iskazati pokornost: "Nismo uradili nikakvo zlo." "Svakako! Doista je Allah Znalac onog šta ste radili.'),
(1930, 16, 29, 'Zato uđite na kapije Džehennema, vječno ćete biti u njemu. Pa sigurno je loše boravište oholih."'),
(1931, 16, 30, 'I biće rečeno onima koji su se bojali: "Šta je to objavio Gospodar vaš?" Reći će: "Dobro!" Za one koji čine dobro na ovom Dunjau, biće dobro. A sigurno je dom Ahireta bolji. A doista je divan dom bogobojaznih,'),
(1932, 16, 31, 'Bašče Adna u koje će ući, teku ispod njih rijeke, imaće oni u njima šta budu htjeli. Tako nagrađuje Allah bogobojazne,'),
(1933, 16, 32, 'One koje uzimaju meleci - dobre, govoriće: "Selamun \'alejkum! Uđite u Džennet zbog onog šta ste radili."'),
(1934, 16, 33, 'Čekaju li samo da im dođu meleci ili dođe naredba Gospodara tvog? Isto tako su činili oni prije njih. I nije im Allah učinio zulm, nego su dušama svojim zulm učinili,'),
(1935, 16, 34, 'Pa su ih pogodila zla onog šta su radili, i spopalo ih je ono čemu su se izrugivali.'),
(1936, 16, 35, 'I govore oni koji pridružuju: "Da hoće Allah ne bismo obožavali mimo Njega nijednu stvar, mi, niti očevi naši, i ne bismo mimo Njega zabranjivali nikakvu stvar." Tako su činili oni prije njih. Pa da li je na poslanicima (išta drugo) osim dostava jasna?'),
(1937, 16, 36, 'I doista, podizali smo u svakoj zajednici poslanika: "Obožavajte Allaha, a klonite se taguta!" Pa je bio od njih onaj koga je uputio Allah i od njih onaj na kom se obistinila zabluda. Zato putujte po Zemlji, pa vidite kakav je bio kraj poricatelja!'),
(1938, 16, 37, 'Ako želiš njihovu uputu - pa uistinu, Allah ne upućuje onog koga zabluđuje, a neće oni imati nikakvih pomagača.'),
(1939, 16, 38, 'I zaklinju se Allahom, najjačom od zakletvi svojih: "Neće podići Allah onog ko je umro." Svakako! Obećanje Njegovo je istinito, međutim, većina svijeta ne zna -'),
(1940, 16, 39, 'Da bi im objasnio ono o čemu su se razilazili, i da bi znali oni koji nisu vjerovali - da su oni bili lašci.'),
(1941, 16, 40, 'Naša je riječ nečemu - kad to želimo - samo što mu kažemo: "Budi", tad biva.'),
(1942, 16, 41, 'A koji se isele radi Allaha, nakon što im je učinjen zulm, sigurno ćemo ih smjestiti na Dunjau dobro. A sigurno je nagrada Ahireta veća - kad bi znali!'),
(1943, 16, 42, '(To su) oni koji se strpe i u Gospodara svog pouzdaju.'),
(1944, 16, 43, 'A slali smo prije tebe samo ljude kojima smo objavljivali - ta pitajte sljedbenike Opomene, ako ne znate -'),
(1945, 16, 44, 'Sa jasnim dokazima i zeburima, i objavljujemo tebi Opomenu, da objasniš ljudima ono šta im se objavljuje, i da bi oni razmišljali.'),
(1946, 16, 45, 'Pa zar su sigurni oni koji planiraju zlodjela, da neće učiniti Allah da ih proguta Zemlja, ili im doći kazna otkud neće opaziti,'),
(1947, 16, 46, 'Ili ih dograbiti u kretanju njihovom, pa oni neće umaći?'),
(1948, 16, 47, 'Ili ih dograbiti u strahovanju? Pa uistinu, Gospodar vaš je Samilosni, Milosrdni.'),
(1949, 16, 48, 'Zar nisu vidjeli da ono što je Allah stvorio od stvari baca sjene svoje desno i lijevo, pokorno Allahu, i oni su ponizni?'),
(1950, 16, 49, 'A Allahu čini sedždu (sve) što je u nebesima i šta je na Zemlji od životinje, i meleci, a oni se ne ohole,'),
(1951, 16, 50, 'Boje se Gospodara svog nad sobom i čine šta im se naređuje.'),
(1952, 16, 51, 'A rekao je Allah: "Ne uzimajte dva boga! Samo je On Bog, Jedini! Zato se Mene - Mene se zato bojte."'),
(1953, 16, 52, 'A Njegovo je šta je na nebesima i Zemlji, i Njemu pripada vjera trajna. Pa zar ćete se drugog (a ne) Allaha bojati?'),
(1954, 16, 53, 'A ono što je u vas od blagodati - pa, od Allaha je. Zatim, kad vas dotakne nevolja, tad Njega preklinjete.'),
(1955, 16, 54, 'Potom, kad otkloni nevolju od vas, gle! Skupina vas Gospodaru svom pridružuje,'),
(1956, 16, 55, 'Da ne bi vjerovali u ono što smo im dali. Pa uživajte, ta saznaćete!'),
(1957, 16, 56, 'I dodjeljuju onom šta ne znaju, dio onog čim smo ih opskrbili! Tako mi Allaha, sigurno ćete biti pitani za ono što izmišljate.'),
(1958, 16, 57, 'I pripisuju Allahu kćeri - slavljen neka je On - a sebi šta žele!'),
(1959, 16, 58, 'I kad se neko od njih obavijesti o žensku, postane lice njegovo tamno i on bude gnjevan.'),
(1960, 16, 59, 'Skriva se od naroda zbog zla koje je javljeno: hoće li je zadržati s prezirom ili je zakopati u prašinu. Besumnje je loše kako presuđuju!'),
(1961, 16, 60, 'Za one koji ne vjeruju u Ahiret, primjer je zla, a Allahov primjer je najuzvišeniji; a On je Moćni, Mudri.'),
(1962, 16, 61, 'A da Allah ščepava ljude zbog zulma njihovog, ne bi na njoj ostala nijedna životinja, međutim, ostavlja ih do roka određenog. Pa kad dođe rok njihov, neće (ga) odložiti čas, niti ubrzati.'),
(1963, 16, 62, 'I čine Allahu ono što mrze, a opisuju jezici njihovi laž, da će njihovo biti bolje. Nema sumnje je da će njihova biti vatra, i da će oni prednjačiti.'),
(1964, 16, 63, 'Tako nam Allaha, doista smo slali (poslanike) zajednicama prije tebe, pa im je šejtan uljepšao djela njihova - zato je on danas zaštitnik njihov, a imaće oni kaznu bolnu.'),
(1965, 16, 64, 'I objavljujemo tebi Knjigu - samo da im objasniš ono o čemu se razilaze - i Uputu i milost, za narod koji vjeruje.'),
(1966, 16, 65, 'A Allah spušta s neba vodu i oživljava njome zemlju nakon mrtvila njenog. Uistinu, u tome je znak za ljude koji čuju.'),
(1967, 16, 66, 'I uistinu, vama je u stoci ibret: napajamo vas od onog šta je u trbusima njenim između probave i krvi, mlijeko čisto, pitko pijačima.'),
(1968, 16, 67, 'I od plodova palmi i grožđa - uzimate iz toga opojno sredstvo i opskrbu lijepu. Uistinu, u tome je znak za ljude koji razumiju.'),
(1969, 16, 68, 'I objavio je Gospodar tvoj pčeli: "Pravi u brdima kuće i u drveću i u onom šta usprave,'),
(1970, 16, 69, 'Zatim jedi od svih plodova, i slijedi puteve Gospodara svog ponizno. Iz trbuha njihovih izlazi napitak različitih boja njegovih, u njemu je lijek za ljude. Uistinu, u tome je znak za ljude koji razmišljaju.'),
(1971, 16, 70, 'A Allah vas stvara, potom vas uzima. I od vas je ko biva vraćen najnižem životu, da nakon znanja ne zna ništa. Uistinu! Allah je Znalac, Moćni.'),
(1972, 16, 71, 'I odlikovao vas je Allah jedne nad drugima u opskrbi. Pa ne povraćaju oni koji su odlikovani, opskrbu svoju, onom šta posjeduju desnice njihove: ta oni su u (potrebi) za tim isti. Pa zar blagodat Allahovu niječu?'),
(1973, 16, 72, 'I Allah vam je načinio od duša vaših parice, i načinio vam je od parica vaših sinove i unuke, i opskrbio vas od dobrih stvari. Pa zar u neistinu vjeruju, a blagodati Allahove oni niječu?'),
(1974, 16, 73, 'I obožavaju mimo Allaha ono šta ne vlada opskrbom njihovom iz nebesa i Zemlje nimalo, niti mogu.'),
(1975, 16, 74, 'Zato ne navodite Allahu sličnosti! Uistinu! Allah zna, a vi ne znate.'),
(1976, 16, 75, 'Allah navodi primjer roba posjedovanog (koji) ne upravlja ničim, i onog kog smo od Nas opskrbili opskrbom lijepom, pa on od nje udjeljuje tajno i javno - jesu li isti? Hvala Allahu! Naprotiv! Većina njih ne zna.'),
(1977, 16, 76, 'I navodi Allah primjer dva čovjeka: jedan od njih nijem, ne upravlja ničim i on je zamor gospodaru svom; kud god ga uputi, ne donese dobrog. Da li je on jednak i onaj ko naređuje pravdu, a on je na putu pravom?'),
(1978, 16, 77, 'A Allahovo je nevidljivo nebesa i Zemlje. I stvar Časa je samo kao sijev oka, ili je on bliži. Uistinu! Allah nad svakom stvari ima moć.'),
(1979, 16, 78, 'I Allah vas izvodi iz trbuha majki vaših (kad) ne znate ništa. I načinio vam je sluh i vidove i srca, da biste vi zahvaljivali.'),
(1980, 16, 79, 'Zar ne vide ptice potčinjene u sferi neba? Drži ih samo Allah. Uistinu, u tome su znaci za ljude koji vjeruju.'),
(1981, 16, 80, 'I načinio vam je Allah od kuća vaših prebivalište, i načinio vam je od koža stoke kuće lahke, na dan selidbe vaše i na dan stajanja vašeg; a od vune njihove i kostrijeti njihove i od krzna njihovih, prostirku i opremu za neko vrijeme.'),
(1982, 16, 81, 'I načinio vam je Allah od onog šta je stvorio, skrovišta, i načinio vam je od brda zaklone, i načinio vam je odijela - čuvaju vas od žege - i odijela (koja) vas čuvaju od sile vaše. Tako upotpunjava blagodat Svoju na vama, da biste se vi predali.'),
(1983, 16, 82, 'Pa ako se okrenu - pa samo je na tebi dostava jasna.'),
(1984, 16, 83, 'Spoznaju blagodat Allahovu, potom je niječu, a većina njih su nevjernici.'),
(1985, 16, 84, 'A na Dan kad iz svakog ummeta podignemo svjedoka, zatim se neće dopustiti onima koji nisu vjerovali, niti će im se dopustiti da mole za naklonost.'),
(1986, 16, 85, 'I kad oni koji su činili zulm, vide kaznu, tad im se neće olakšati, niti će im se odložiti.'),
(1987, 16, 86, 'I kad oni koji su pridruživali, vide ortake svoje, reći će: "Gospodaru naš! Ovo su ortaci naši koje smo prizivali mimo Tebe." Tad će im dobaciti riječi: "Uistinu, vi ste lažljivci!"'),
(1988, 16, 87, 'I iskazaće Tog dana Allahu pokornost, a iščeznuće od njih šta su izmišljali.'),
(1989, 16, 88, 'Oni koji nisu vjerovali i odvraćali od puta Allahovog - dodaćemo im kaznu na kaznu, zbog toga što su pravili fesad.'),
(1990, 16, 89, 'A na Dan kad u svakom ummetu podignemo svjedoka protiv njih, između duša njihovih, i dovedemo tebe svjedokom protiv tih ... I objavili smo ti Knjigu (kao) objašnjenje za svaku stvar, i Uputu i milost, i radosnu vijest muslimanima.'),
(1991, 16, 90, 'Uistinu! Allah naređuje pravdu i dobročinstvo i davanje rodbini, a zabranjuje razvrat i ružno i tiraniju; savjetuje vas da biste se vi poučili.'),
(1992, 16, 91, 'I ispunjavajte obavezu Allahu kad se obavežete. I ne kršite zakletve nakon potvrde njihove - a doista ste Allaha učinili nad sobom Jamcem. Uistinu! Allah zna šta činite.'),
(1993, 16, 92, 'I ne budite kao ona koja je rasplela pređu svoju, nakon čvrste ispletenosti, uzimajući zakletve vaše smetnjom među vama - jer je zajednica veća od zajednice. Allah vas samo iskušava time. A sigurno će vam na Dan kijameta objasniti ono o čemu ste se razilazili.'),
(1994, 16, 93, 'A da hoće Allah, sigurno bi vas učinio ummom jednom. Međutim, zabluđuje koga hoće, a upućuje koga hoće. A sigurno ćete biti pitani za ono šta ste radili.'),
(1995, 16, 94, 'I ne uzimajte zakletve vaše smetnjom među vama, pa da ne poklizne noga nakon čvrstine svoje i iskusite zlo što ste odvraćali od puta Allahovog, i imadnete vi kaznu strahovitu.'),
(1996, 16, 95, 'I ne kupujte zavjetom Allahu vrijednost malu. Uistinu, šta je kod Allaha, to je bolje za vas, kad biste znali!'),
(1997, 16, 96, 'Ono što je kod vas iscrpljuje se, a što je kod Allaha, trajno je. A sigurno ćemo platiti onima koji su bili strpljivi - njihova nagrada će biti prema najboljem šta su uradili.'),
(1998, 16, 97, 'Ko uradi dobro - bio muškarac ili žena - a on bude vjernik, pa sigurno ćemo ga poživiti životom dobrim, i sigurno ćemo ih platiti nagradom njihovom prema najboljem šta su uradili.'),
(1999, 16, 98, 'Pa kad učiš Kur\'an, tad zatraži zaštitu u Allaha od šejtana prokletog.'),
(2000, 16, 99, 'Uistinu, on, nema on vlasti nad onima koji vjeruju i u Gospodara svog se pouzdavaju.'),
(2001, 16, 100, 'Njegova vlast je samo nad onima koji ga uzimaju prijateljem, i onima koji Njemu pridružuju.'),
(2002, 16, 101, 'I kad zamijenimo ajet umjesto ajeta - a Allah je Najbolji znalac onog šta spušta - kažu: "Samo si ti izmišljač." Naprotiv, većina njih ne zna.'),
(2003, 16, 102, 'Reci: "Spušta ga Ruhul-kudus od Gospodara tvog s Istinom, da učvrsti one koji vjeruju, i (kao) uputu i radosnu vijest muslimanima."'),
(2004, 16, 103, 'A doista znamo da oni govore: "Samo ga smrtnik uči." Jezik onog kome streme - je strani, a ovo je jezik arapski, jasni.'),
(2005, 16, 104, 'Uistinu, oni koji ne vjeruju u ajete Allahove - neće ih uputiti Allah, a imaće oni kaznu bolnu.'),
(2006, 16, 105, 'Laž izmišljaju jedino oni koji ne vjeruju u ajete Allahove, a ti takvi su lašci.'),
(2007, 16, 106, 'A ko ne vjeruje u Allaha, nakon vjerovanja svog - izuzev ko bude prisiljen, a srce njegovo bude smireno u vjerovanju - međutim, ko otvori nevjerovanju grudi: pa na njima će biti srdžba od Allaha, a imaće oni kaznu užasnu.'),
(2008, 16, 107, 'To zato što oni više vole život Dunjaa od Ahireta i što Allah ne upućuje narod nevjernika.'),
(2009, 16, 108, 'Takvi su oni čija je srca Allah zapečatio, i sluh njihov i vidove njihove, a ti takvi su nemarni.'),
(2010, 16, 109, 'Nema sumnje da će oni na Ahiretu, (baš) oni biti gubitnici.'),
(2011, 16, 110, 'Zatim, uistinu, Gospodar tvoj će onima koji se isele nakon što su proganjani, potom se bore i budu strpljivi - uistinu, Gospodar tvoj će nakon toga biti Oprosnik, Milosrdni.'),
(2012, 16, 111, 'Na Dan kad dođe svaka duša, argumentiraće samo o sebi, i biće svakoj duši plaćeno šta je radila, a njima se neće učiniti zulm.'),
(2013, 16, 112, 'I navodi Allah primjer naselja bezbjednog i spokojnog, dolazila mu je opskrba njegova obilno sa svakog mjesta, pa je bilo nezahvalno na blagodatima Allahovim - te je Allah dao da ono iskusi odjeću gladi i straha, zbog onog šta su radili.'),
(2014, 16, 113, 'I doista, došao im je poslanik između njih, pa su ga porekli - tad ih je spopala kazna, a oni su bili zalimi.'),
(2015, 16, 114, 'Pa jedite od onog čim vas je opskrbio Allah, dozvoljeno, dobro. I zahvaljujte na blagodati Allahovoj, ako Njega obožavate.'),
(2016, 16, 115, 'Samo vam je zabranio lešinu i krv i meso svinje i ono nad čim je zazvano ime drugog, a ne Allaha. Pa ko bude prisiljen, bez želje i bez pretjerivanja - pa uistinu, Allah je Oprosnik, Milosrdni.'),
(2017, 16, 116, 'I ne recite za ono šta opisuju jezici vaši lažno: "Ovo je halal, a ovo haram", da biste izmišljali protiv Allaha laž. Uistinu, oni koji protiv Allaha laž izmišljaju, neće uspjeti.'),
(2018, 16, 117, 'Uživanje je malo, a imaće oni kaznu bolnu.'),
(2019, 16, 118, 'A onima koji su jevreji, zabranili smo šta smo ti kazivali ranije. I nismo im učinili zulm, nego su učinili zulm dušama svojim.'),
(2020, 16, 119, 'Zatim uistinu, Gospodar tvoj će za one koji su uradili zlo iz neznanja, potom se pokajali nakon toga i popravili - uistinu će Gospodar tvoj nakon toga biti Oprosnik, Milosrdni.'),
(2021, 16, 120, 'Uistinu! Ibrahim je bio uzor, pokoran Allahu, pravovjeran, i nije bio od mušrika.'),
(2022, 16, 121, 'Zahvalan je bio na blagodatima Njegovim. Odabrao ga je i uputio putu pravom.'),
(2023, 16, 122, 'I dali smo mu na Dunjau dobro, a uistinu, on će na Ahiretu biti od pravednih.'),
(2024, 16, 123, 'Zatim smo tebi objavili: "Slijedi millet Ibrahima hanife", a nije bio od mušrika.'),
(2025, 16, 124, 'Sebt je određen samo onima koji su se razilazili o njemu. A uistinu, Gospodar tvoj će presuditi među njima na Dan kijameta o onome u čemu su se razilazili.'),
(2026, 16, 125, 'Pozivaj putu Gospodara svog mudrošću i savjetom dobrim, i s njima raspravljaj onako kako je najbolje. Uistinu, Gospodar tvoj - On je Najbolji znalac onog ko skreće s puta Njegovog, i on je Najbolji znalac upućenih.'),
(2027, 16, 126, 'A ako kažnjavate, tad kažnjavajte sličnim čime ste bili kažnjeni. A ako se strpite, to je sigurno bolje za strpljive.'),
(2028, 16, 127, 'I strpi se! A strpljenje tvoje je samo s (pomoći) Allaha, i ne žalosti se nad njima, i ne budi u brizi zbog onog šta spletkare.'),
(2029, 16, 128, 'Uistinu! Allah je uz one koji se boje i one koji su dobročinitelji.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Slavljen neka je Onaj koji je noću prenio roba Svog od Mesdžidul-harama do Mesdžidul-aksaa - onog čiju smo okolinu blagoslovili - da bi mu pokazali neke od znakova Naših. Uistinu! On, On je Onaj koji čuje, Onaj koji vidi.'),
(2031, 17, 2, 'I dali smo Musau Knjigu i učinili je Uputom sinovima Israilovim: "Ne uzimajte mimo Mene nikakva zaštitnika."'),
(2032, 17, 3, 'Potomci su onog koga smo nosili s Nuhom. Uistinu, on je bio rob zahvalan.'),
(2033, 17, 4, 'I odredili smo sinovima Israilovim u Knjizi: "Sigurno ćete napraviti fesad na Zemlji dva puta i sigurno ćete se uznijeti ohološću velikom."'),
(2034, 17, 5, 'Pa kad dođe obećanje prve od dvije (prijetnje), podigosmo na vas robove Naše, posjednike sile žestoke, pa su pretresali po kućama, i bijaše obećanje ispunjeno.'),
(2035, 17, 6, 'Zatim smo vam dali povrat na njih, i pomogli vas imecima i sinovima, i učinili vas većom skupinom.'),
(2036, 17, 7, 'Ako činite dobro, činite dobro za duše svoje, a ako činite zlo, pa njihovo je. Pa kad dođe obećanje druge (prijetnje) - da ožaloste lica vaša, i da uđu u Mesdžid kao što su ušli u njega prvi put, i da potpuno unište šta zauzmu.'),
(2037, 17, 8, 'Možda će Gospodar vaš da vam se smiluje. A ako ponovite, ponovićemo, a učinili smo Džehennem za nevjernike tamnicom.'),
(2038, 17, 9, 'Uistinu, ovaj Kur\'an upućuje onom šta je najispravnije, i donosi radosne vijesti vjernicima koji čine dobra djela, da će oni imati nagradu veliku,'),
(2039, 17, 10, 'I da oni koji ne vjeruju u Ahiret - pripremili smo za njih kaznu bolnu.'),
(2040, 17, 11, 'I priziva čovjek dowom svojom i zlo i dobro; a čovjek je brzoplet.'),
(2041, 17, 12, 'I učinili smo noć i dan dvama znacima. Pa brišemo znak noći i činimo znak dana vidnim, da biste tražili blagodat od Gospodara svog i da znate broj godina i računanje. A svaku stvar - razložili smo je podrobno.'),
(2042, 17, 13, 'I svakom čovjeku - pričvrstićemo mu njegov znamen za vrat njegov i izvadićemo mu na Dan kijameta knjigu koju će naći otvorenu:'),
(2043, 17, 14, '"Čitaj knjigu svoju! Dovoljna je Danas duša tvoja protiv tebe obračunac."'),
(2044, 17, 15, 'Ko se upućuje - pa samo se upućuje za dušu svoju; a ko luta, pa samo luta protiv nje. I neće opterećeni nositi teret drugog. A nismo kažnjavali, dok ne bismo poslali poslanika.'),
(2045, 17, 16, 'A kad želimo da uništimo grad, naredimo u njemu raskošnicima njegovim, pa se odaju griješenju u njemu, te se ispuni na njemu Riječ, pa ga uništimo (potpunim) uništenjem.'),
(2046, 17, 17, 'I koliko smo uništili pokoljenja poslije Nuha! A dovoljno je (što je) Gospodar tvoj o grijesima robova Svojih Obaviješteni, Vidilac.'),
(2047, 17, 18, 'Ko želi ubrzano (ovog života), ubrzaćemo mu u njemu što hoćemo - kome želimo, zatim ćemo mu dodijeliti Džehennem. Pržiće se njime pokuđen, odbačen.'),
(2048, 17, 19, 'A ko želi Ahiret i zalaže se za njega trudom svojskim, a on bude vjernik - pa tim takvima će biti trud njihov pohvalan.'),
(2049, 17, 20, 'Svakom pružamo, takvima i ovakvima, iz dara Gospodara tvog. A nije dar Gospodara tvog ograničen.'),
(2050, 17, 21, 'Pogledaj kako smo ih odlikovali jedne nad drugima, a sigurno je Ahiret veći stepenima i veći odlikovanjem.'),
(2051, 17, 22, 'Ne stavljaj uz Allaha boga drugog, pa da sjedneš pokuđen, ostavljen.'),
(2052, 17, 23, 'A odredio je Gospodar tvoj, da ne obožavate, izuzev Njega, a roditeljima - dobročinstvo. Ako jedno od njih dvoje ili oboje dostigne kod tebe starost, tad im ne reci: "Uf!" i ne grdi ih i reci im riječ plemenitu.'),
(2053, 17, 24, 'I spusti im krilo poniznosti iz milosti, i reci: "Gospodaru moj! Smiluj se njima dvoma, kao što su me odgajali (dok) sam bio mali."'),
(2054, 17, 25, 'Gospodar vaš je Najbolji znalac onog šta je u dušama vašim. Ako budete pravedni - pa uistinu, On je pokajanicima Oprosnik.'),
(2055, 17, 26, 'I daj bližnjem pravo njegovo, i siromahu i sinu puta, i ne rasipaj rasipnošću.'),
(2056, 17, 27, 'Uistinu! Rasipnici su braća šejtanova, a šejtan je Gospodaru svom nezahvalan.'),
(2057, 17, 28, 'A ako se okreneš od njih, tražeći milost Gospodara svog kojoj se nadaš, tad im reci riječ lahku.'),
(2058, 17, 29, 'I ne čini ruku svoju vezanom za svoj vrat, i ne pružaj je svakim pružanjem, pa da sjedneš prekoren, ogoljen.'),
(2059, 17, 30, 'Uistinu, Gospodar tvoj pruža opskrbu kome hoće i uskraćuje. Uistinu, On je o robovima Svojim Obaviješteni, Onaj koji vidi.'),
(2060, 17, 31, 'I ne ubijajte djecu svoju iz straha od siromaštva. Mi opskrbljujemo njih i vas. Uistinu, njihovo ubijanje je greška velika;'),
(2061, 17, 32, 'I ne približujte se bludu! Uistinu, to je razvrat i zao put;'),
(2062, 17, 33, 'I ne ubijte dušu koju je zabranio Allah, izuzev s pravom. A ko bude ubijen nepravedno, pa doista smo nasljedniku njegovom dali ovlaštenje, pa neka ne pretjera u ubijanju. Uistinu, on će biti pomognut.'),
(2063, 17, 34, 'I ne približujte se imetku siročeta, osim onako kako je najbolje, dok ne dostigne zrelost svoju. I ispunjavajte ugovor. Uistinu, za ugovor će se biti pitano.'),
(2064, 17, 35, 'I ispunjavajte mjeru kad mjerite, vagajte mjerilom ispravnim. To je dobro i najljepšeg ishoda.'),
(2065, 17, 36, 'I ne slijedi ono o čemu ti nemaš znanje. Uistinu, sluh i vid i srce, za svako od njih biće se pitano.'),
(2066, 17, 37, 'I ne hodaj po Zemlji likujući. Uistinu, ti nećeš probiti Zemlju, i nećeš doseći planine visinom.'),
(2067, 17, 38, 'Sve to je zlo kod Gospodara tvog, pokudno je.'),
(2068, 17, 39, 'To je od onog šta ti objavljuje Gospodar tvoj od mudrosti. I ne stavljaj uz Allaha boga drugog, pa da budeš bačen u Džehennem, prekoren, odbačen.'),
(2069, 17, 40, 'Pa zar je vas odlikovao Gospodar vaš sinovima, a uzeo od meleka ženske? Uistinu, vi govorite riječ strašnu.'),
(2070, 17, 41, 'I doista, izložili smo u ovom Kur\'anu (opomene) da se pouče, a povećava im jedino izbjegavanje.'),
(2071, 17, 42, 'Reci: "Da su uz Njega bogovi kao što govore, tad bi doista tražili Vlasniku Arša put!"'),
(2072, 17, 43, 'Slavljen neka je On i Uzvišen od onog što govore - uzvišenošću velikom!'),
(2073, 17, 44, 'Slavi Ga sedam nebesa i Zemlja i ko je u njima; i nema nijedne stvari, a da ne slavi sa hvalom Njegovom. Međutim, ne razumijete slavljenje njihovo. Uistinu! On je Blagi, Oprosnik.'),
(2074, 17, 45, 'I kad učiš Kur\'an, načinimo između tebe i između onih koji ne vjeruju u Ahiret, barijeru skrivenu;'),
(2075, 17, 46, 'I stavljamo na srca njihova prekrivače da ga ne razumiju, a u uši njihove gluhoću. I kad spomeneš Gospodara svog u Kur\'anu - Jedinog Njega - okrenu pozadine svoje bježeći.'),
(2076, 17, 47, 'Mi smo Najbolji znalac onog šta slušaju, kad te slušaju, i kad se oni tajno dogovaraju, kad govore zalimi: "Slijedite samo čovjeka začarana."'),
(2077, 17, 48, 'Vidi kako za tebe navode primjere pa lutaju, te ne mogu da nađu put.'),
(2078, 17, 49, 'I govore: "Zar ćemo kad budemo kosti i komadići, - hoćemo li uistinu mi biti podignuti (kao) stvorenje novo?"'),
(2079, 17, 50, 'Reci: "Budite kamenje ili gvožđe,'),
(2080, 17, 51, 'Ili stvorenje od onog šta je veliko u grudima vašim." Tad će reći: "Ko će nas povratiti?" Reci: "Onaj koji vas je stvorio prvi put." Tad će ti zatresti glavama svojim i reći: "Kad će to?" Reci: "Možda je blizu."'),
(2081, 17, 52, 'Na Dan kad vas pozove, tad ćete se odazvati s hvalom Njegovom, i mislićete da ste proboravili samo malo (vremena)!'),
(2082, 17, 53, 'I reci robovima Mojim (da) govore ono što je najbolje. Uistinu, šejtan sije razdor među njima. Uistinu, šejtan je čovjeku neprijatelj otvoreni.'),
(2083, 17, 54, 'Gospodar vaš je vaš Najbolji poznavalac. Ako hoće, smilovaće vam se, a ako hoće kazniće vas. I nismo poslali tebe nad njima zaštitnikom.'),
(2084, 17, 55, 'I Gospodar tvoj je Najbolji znalac onog ko je na nebesima i Zemlji. A doista smo odlikovali vjerovjesnike jedne nad drugima, i dali smo Dawudu Zebur.'),
(2085, 17, 56, 'Reci: "Prizivajte one koje ste držali (bogovima) mimo Njega" - pa ne vladaju otklanjanjem nevolje od vas, niti (njenim) mijenjanjem.'),
(2086, 17, 57, 'Takvi koje oni prizivaju, traže način pristupa Gospodaru svom - koji od njih će biti najbliži - i nadaju se milosti Njegovoj i boje se kazne Njegove. Uistinu, kazna Gospodara tvog je ono čega se treba čuvati.'),
(2087, 17, 58, 'I nema nijednog naselja, a da ga Mi nećemo uništiti prije Dana kijameta ili ga kazniti kaznom žestokom. To je u Knjizi napisano.'),
(2088, 17, 59, 'A sprječava Nas da pošaljemo znakove, jedino što su ih poricali raniji. I dali smo Semudu devu jasnim (znakom), pa su joj učinili zulm. A znakove smo slali samo (kao) zastrašivanje.'),
(2089, 17, 60, 'I kad ti rekosmo: "Uistinu, Gospodar tvoj obuhvata čovječanstvo." I učinili smo snoviđenje koji smo ti pokazali, jedino iskušenjem ljudima, i drvo prokleto u Kur\'anu. A zastrašujemo ih, pa im (to) samo povećava pretjeranost veliku.'),
(2090, 17, 61, 'I kad rekosmo melecima: "Padnite na sedždu Ademu!" Tad padoše ničice, izuzev Iblisa. Reče: "Zar da činim sedždu onome koga si stvorio od ilovače?"'),
(2091, 17, 62, 'Reče: "Vidiš li Ti ovo čemu si ukazao čast nada mnom, ako me ostaviš do Dana kijameta sigurno ću savladati potomstvo njegovo, izuzev malo (njih)."'),
(2092, 17, 63, '(Allah) reče: "Odlazi! Pa ko te od njih bude slijedio, pa uistinu, Džehennem će biti plaća vaša, plaća potpuna.'),
(2093, 17, 64, 'I podbodi koga možeš od njih glasom svojim, i sakupi na njih konjicu svoju i pješadiju svoju, i sudjeluj im u imecima i djeci, i obećavaj im", a obećava im šejtan jedino obmanu.'),
(2094, 17, 65, 'Uistinu! Robovi Moji - nećeš ti imati nad njima autoritet. A dovoljan je Gospodar tvoj Zaštitnik.'),
(2095, 17, 66, 'Gospodar vaš je Taj koji goni za vas plovilo u moru, da tražite iz blagodati Njegove. Uistinu, On vam je Milosrdan.'),
(2096, 17, 67, 'I kad vas dotakne nevolja u moru, iščezne onaj koga prizivate mimo Njega. Pa pošto vas izbavi na kopno, odvratite se. A čovjek je nezahvalan.'),
(2097, 17, 68, 'Pa zar ste sigurni da neće učiniti da vas proguta obronak kopna ili poslati na vas pješčanu oluju?, potom nećete naći sebi zaštitnika.'),
(2098, 17, 69, 'Ili ste sigurni da vas neće vratiti u to drugi put, te poslati na vas uragan vjetra, pa vas potopiti što ste bili nezahvalni, zatim nećete sebi naći protiv Nas za to pristalicu.'),
(2099, 17, 70, 'A uistinu, ukazali smo čast sinovima Ademovim, i nosimo ih kopnom i morem, i opskrbili smo ih dobrim stvarima, i odlikovali ih prednošću nad mnogima od onog kog smo stvorili.'),
(2100, 17, 71, 'Na Dan kad pozovemo sve ljude s imamom njihovim - pa kome se da knjiga njegova u desnicu njegovu, pa takvi će čitati knjigu svoju i neće im se učiniti zulm žilice hurmine košpice.'),
(2101, 17, 72, 'A ko je na ovom (svijetu) slijepac, pa on će biti na Ahiretu slijepac i najodlutaliji od puta.'),
(2102, 17, 73, 'I skoro da te baš odvrate od onog šta ti objavljujemo, da bi protiv Nas izmišljao drugačije od tog, a tad bi te (oni) sigurno uzeli prijateljem.'),
(2103, 17, 74, 'I da te nismo učvrstili, skoro bi im se baš naklonuo nešto malo.'),
(2104, 17, 75, 'Tad bismo doista dali da ti iskusiš dvostruku (kaznu) života i dvostruku (kaznu) smrti, zatim ne bi sebi našao protiv Nas pomagača.'),
(2105, 17, 76, 'I zamalo da te doista preplaše iz zemlje, da te iz nje istjeraju, a tad ne bi ostali iza tebe, izuzev malo.'),
(2106, 17, 77, 'Sunnet onog kog smo poslali prije tebe od poslanika Naših, a nećeš u sunnetu Našem naći izmjenu.'),
(2107, 17, 78, 'Obavljaj salat sa naginjanjem Sunca (zapadu), do mraka noći, i Kur\'an (uči) zorom. Uistinu, Kur\'an zorom je svjedočen.'),
(2108, 17, 79, 'A od noći - pa probdij je nafilom za sebe. Možda će Gospodar tvoj da te uzdigne na mjesto pohvalno.'),
(2109, 17, 80, 'I reci: "Gospodaru moj! Daj mi da uđem ulaskom istinskim, i izvedi me izvođenjem istinskim i dadni mi od Sebe snagu pomažuću."'),
(2110, 17, 81, 'I reci: "Došla je Istina, a nestala je neistina. Uistinu, neistina je nestajuća."'),
(2111, 17, 82, 'I objavljujemo od Kur\'ana ono šta je lijek i milost vjernicima, a zalimima povećava jedino gubitak.'),
(2112, 17, 83, 'I kad obdarimo čovjeka, odvrati se i udalji na svoju stranu. A kad ga dotakne zlo, bude očajan.'),
(2113, 17, 84, 'Reci: "Svako radi na svoj način, pa Gospodar vaš je Najbolji znalac onog ko je najupućeniji putu."'),
(2114, 17, 85, 'I pitaju te o Duhu. Reci: "Duh je po naredbi Gospodara mog, a vama je od znanja dato samo malo."'),
(2115, 17, 86, 'A da hoćemo, sigurno bismo povukli ono šta smo ti objavili, zatim ne bi sebi za to našao protiv Nas zaštitnika,'),
(2116, 17, 87, 'Osim milosti Gospodara tvog. Uistinu! Dobrota Njegova je prema tebi velika.'),
(2117, 17, 88, 'Reci: "Kad bi se sakupilo čovječanstvo i džinni, da donesu sličan ovom Kur\'anu, ne bi sličan njemu donijeli, makar oni jedni drugima bili pomagači.'),
(2118, 17, 89, 'A doista smo čovječanstvu iznijeli u ovom Kur\'anu od svakog primjera (ponešto), pa većina svijeta odbija (sve), osim nevjerstva.'),
(2119, 17, 90, 'I rekoše: "Nećemo ti vjerovati, dok ne učiniš da za nas provrije izvor iz zemlje;'),
(2120, 17, 91, 'Ili imadneš ti bašču palmi i grožđa, pa učiniš da kroz nju poteku rijeke izbijajući;'),
(2121, 17, 92, 'Ili daš da padne nebo - kao što tvrdiš - na nas u komadima, ili dovedeš Allaha i meleke sučelice;'),
(2122, 17, 93, 'Ili ti imadneš kuću od zlata; ili se popneš u nebo - a nećemo vjerovati u uspon tvoj, dok nam ne spustiš knjigu koju ćemo čitati." Reci: "Slavljen neka je Gospodar moj! Jesam li (išta), osim smrtnik, poslanik?"'),
(2123, 17, 94, 'A sprječavalo je ljude da vjeruju, kad bi im dolazila Uputa, samo što su govorili: "Zar je podigao Allah smrtnika poslanikom?"'),
(2124, 17, 95, 'Reci: "Da su na Zemlji meleci koji hode smireno, sigurno bismo im spustili s neba meleka poslanikom."'),
(2125, 17, 96, 'Reci: "Dovoljan je Allah svjedok između mene i između vas." Uistinu! On je o robovima Svojim Obaviješteni, Onaj koji vidi.'),
(2126, 17, 97, 'A koga uputi Allah, tad je on upućen; a koga zabludi - tad im nećeš naći zaštitnike mimo Njega. I sakupićemo ih na Dan kijameta na licima njihovim slijepe i nijeme i gluhe; sklonište njihovo biće Džehennem. Kad god se stiša, povećaćemo im seir.'),
(2127, 17, 98, 'To je plaća njihova, zato što nisu vjerovali u ajete Naše i govorili: "Zar ćemo kad budemo kosti i komadi, uistinu mi biti podignuti stvaranjem novim?"'),
(2128, 17, 99, 'Zar ne vide da je Allah, Koji je stvorio nebesa i Zemlju, kadar da stvori slične njima i dadne im rok u koji nema sumnje. Pa odbijaju zalimi - osim nevjerstvo.'),
(2129, 17, 100, 'Reci: "Da vi vladate riznicama milosti Gospodara mog, tad biste se sigurno suzdržali zbog straha od potrošnje"; a čovjek je škrtac.'),
(2130, 17, 101, 'I doista, dali smo Musau devet znakova, dokaza jasnih - ta pitaj sinove Israilove - kad im dođe, te reče njemu faraon: "Uistinu, ja te smatram, o Musa, opčaranim."'),
(2131, 17, 102, 'Reče: "Doista znaš, ove (znakove) objavljuje samo Gospodar nebesa i Zemlje, kao znamenja, a uistinu, ja te smatram, o faraone, uništenim."'),
(2132, 17, 103, 'Tad je želio da ih protjera iz zemlje, pa potopismo njega i onog ko je bio s njim, sve.'),
(2133, 17, 104, 'I rekli smo poslije njega sinovima Israilovim: "Nastanite zemlju! Pa kad dođe obećanje Ahireta, dovešćemo vas grupisane."'),
(2134, 17, 105, 'A s Istinom smo ga spustili i s Istinom je sišao. I tebe smo poslali samo kao donosioca radosnih vijesti i opominjača.'),
(2135, 17, 106, 'A Kur\'an - razdijelili smo ga, da ga učiš ljudima sa zadrškom, i spustili smo ga (postepenim) objavljivanjem.'),
(2136, 17, 107, 'Reci: "Vjerovali (vi) u njega ili ne vjerovali! Uistinu, oni kojima je dato znanje prije njega, kad im se uči, padaju na brade ničice,'),
(2137, 17, 108, 'I govore: \'Slava neka je Gospodaru našem! Doista je obećanje Gospodara našeg izvršno!\''),
(2138, 17, 109, 'I padaju na brade plačući, a (to) im povećava skrušenost."'),
(2139, 17, 110, 'Reci: "Prizivajte Allaha", ili "Prizivajte Milostivog." Kako god zovete - pa Njegova imena su najljepša. I ne budi glasan u salatu svom, i ne priguši (glas) u njemu, i traži između toga put,'),
(2140, 17, 111, 'I reci: "Hvala neka je Allahu Koji nije uzeo dijete i Koji nema ortaka u vlasti i Koji nema zaštitnika od poniženja;" i veličaj Ga tekbirom!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Hvala Allahu, Onom koji robu Svom objavljuje Knjigu - a nije učinio njenu iskrivljenost -'),
(2142, 18, 2, 'Ispravnu, da opominje silom žestokom od Njega i obraduje vjernike koji rade dobra djela, da će oni imati nagradu lijepu,'),
(2143, 18, 3, 'U kojoj će oni ostati vječito.'),
(2144, 18, 4, 'I (da) upozori one koji govore: "Allah je uzeo dijete."'),
(2145, 18, 5, 'Nemaju oni niti očevi njihovi o tome nikakvo znanje. Velika riječ izlazi iz usta njihovih; govore jedino laž.'),
(2146, 18, 6, 'Pa možda bi ti ubio dušu svoju tugom nad tragovima njihovim, ako neće da vjeruju u ovaj govor.'),
(2147, 18, 7, 'Uistinu! Mi smo ono što je na Zemlji učinili ukrasom njenim, da bismo ih iskušali - koji od njih je bolji djelom.'),
(2148, 18, 8, 'I uistinu, Mi smo Ti koji će učiniti ono šta je na njoj tlom neplodnim.'),
(2149, 18, 9, 'Ili misliš da su stanovnici pećine i Rekima bili od znakova Naših čudo?'),
(2150, 18, 10, 'Kad se mladići sklonuše u pećinu pa rekoše: "Gospodaru naš! Daj nam od Sebe milost i pripremi nam od stvari naše ispravnost."'),
(2151, 18, 11, 'Tad smo udarili na uši njihove u pećini godine brojne;'),
(2152, 18, 12, 'Zatim smo ih podigli, da znamo koja od dvije stranke je bolja za proračun onog što su vremenski proveli.'),
(2153, 18, 13, 'Mi ti pričamo vijest njihovu s Istinom. Uistinu! Oni su bili mladići, vjerovali su u Gospodara svog, a povećali smo im uputu.'),
(2154, 18, 14, 'I utvrdili smo srca njihova, kad ustadoše pa rekoše: "Gospodar naš je Gospodar nebesa i Zemlje, nećemo prizivati mimo Njega boga (drugog). Zaista bismo tad govorili pretjeranost."'),
(2155, 18, 15, 'Ovi naši ljudi su uzeli mimo Njega bogove. Zašto im ne donesu dokaz jasan? Pa ko je nepravedniji od onog ko izmišlja protiv Allaha laž?'),
(2156, 18, 16, 'I kad napustite njih i ono šta obožavaju sem Allaha, tad se sklonite u pećinu; raširiće vam Gospodar vaš (nešto) od milosti Svoje i pripremiće vam za stvar vašu korisno.'),
(2157, 18, 17, 'I vidiš Sunce kad se diže, obilazi pećinu njihovu desno; a kad zalazi, prolazi ih lijevo, a oni u otvoru njenom. To je od znakova Allahovih. Koga uputi Allah, tad je on upućen; a koga zavede, tad mu nećeš naći zaštitnika vodiča.'),
(2158, 18, 18, 'I smatraš ih budnima, a oni usnuli. I prevrtali smo ih desno i lijevo, a pas njihov je opruženih šapa svojih bio na pragu. Da si na njih pogledao, sigurno bi se okrenuo od njih bježeći, i od njih bi se ispunio strahom.'),
(2159, 18, 19, 'I tako smo ih podigli, da se međusobno raspituju. Govornik između njih reče: "Koliko ste ostali?" Rekoše: "Ostali smo dan ili dio dana." Rekoše: "Gospodar vaš je Najbolji znalac toga što ste ostali. Zato pošaljite vas jednog sa ovim vašim srebrenjacima u grad, pa neka vidi ko je najčišće hrane, te nek vam dođe s opskrbom od njega, i neka bude blag. I neka nikako o vama ne obavještava nikoga.'),
(2160, 18, 20, 'Uistinu će oni, ako za vas saznaju - kamenovati vas ili vas vratiti u millet njihov, a tada nećete nikada uspjeti."'),
(2161, 18, 21, 'I tako smo upoznali o njima, da znaju da je obećanje Allahovo Istina, i da u Čas nema sumnje. Kad su međusobno raspravljali stvar svoju, pa rekli: "Sagradite nad njima građevinu, Gospodar njihov je Najbolji znalac o njima." Oni koji su dominirali nad stvari njihovom, rekoše: "Sigurno ćemo nad njima sagraditi mesdžid."'),
(2162, 18, 22, 'Govorit će: "Trojica, četvrti im je pas njihov." I govorit će: "Peterica, šesti im je pas njihov", nagađajući o nevidljivom; i govoriće: "Sedam, a osmi im je pas njihov." Reci: "Gospodar moj je Najbolji znalac broja njihovog." O njima zna samo malo (njih). Zato o njima ne raspravljaj, izuzev raspravom površnom, i ne pitaj o njima od njih nijednog,'),
(2163, 18, 23, 'I nikako ne reci ni za koju stvar: "Uistinu, učiniću to sutra",'),
(2164, 18, 24, 'Bez: "InšaAllah." I spomeni Gospodara svog kad zaboraviš, i reci: "Možda će me uputiti Gospodar moj ispravnosti bližoj od ove."'),
(2165, 18, 25, 'I ostali su u pećini svojoj tri stotine godina i dodali devet.'),
(2166, 18, 26, 'Reci: "Allah je Najbolji znalac tog što su ostali. Njegovo je nevidljivo nebesa i Zemlje. Kakvog je On vida i kakvog sluha! Nemaju oni mimo Njega nikakva zaštitnika, a ne pridružuje u sudu Svom nikoga."'),
(2167, 18, 27, 'I recitiraj šta ti se objavljuje iz Knjige Gospodara tvog. Nema izmjenitelja za Riječi Njegove, i nećeš naći mimo Njega (nikakvo) utočište.'),
(2168, 18, 28, 'I strpi dušu svoju sa onima koji zazivaju Gospodara svog ujutro i navečer, želeći lice Njegovo; i ne skidaj oči svoje s njih, želeći sjaj života Dunjaa; i ne slušaj onog čije smo srce učinili nemarnim za spominjanje Naše i (koji) slijedi strast svoju, a stvar njegova je zanemarena.'),
(2169, 18, 29, 'I reci: "Istina je od Gospodara vašeg! Pa ko hoće neka vjeruje, a ko hoće - pa nek ne vjeruje." Uistinu! Mi smo zalimima pripremili vatru, obuhvatiće ih šator njen. I ako zatraže pomoć, odgovoriće im se vodom kao rastopljen metal, spržiće lica. Loše je piće i zlo počivalište!'),
(2170, 18, 30, 'Uistinu! Onima koji vjeruju i čine dobra djela, uistinu - Mi nećemo uništiti nagradu onoga ko čini dobro djelo.'),
(2171, 18, 31, 'Ti takvi će imati bašče Adna, ispod njih će teći rijeke; kitit će se u njemu narukvicama od zlata i nosiće zelena odijela od svile i brokata, naslonjeni u njemu na sofama. Divne li nagrade i lijepog počivališta!'),
(2172, 18, 32, 'I navedi im primjer dvojice ljudi: Načinili smo dvije bašče grožđa jednom od njih i okružili ih palmama, i između njih dvije načinili njivu.'),
(2173, 18, 33, 'Obje bašče su dale plod svoj i ništa od njega nije falilo; i dali smo da kroz njih dvije poteče rijeka;'),
(2174, 18, 34, 'I imao je on plodove; pa reče drugu svom - a on je govorio s njim: "Veći sam od tebe bogatstvom, i jači ljudstvom."'),
(2175, 18, 35, 'I uđe u bašču svoju - a on je bio zalim duši svojoj - reče: "Ne mislim da će ovo ikada propasti;'),
(2176, 18, 36, 'I ne mislim da će Čas doći, a ako budem vraćen Gospodaru svom, sigurno ću naći bolje povratište od ovog."'),
(2177, 18, 37, 'Drug njegov mu reče - a on je govorio s njim: "Zar ne vjeruješ u Onog koji te je stvorio od prašine, zatim kaplje (sjemena), zatim te upotpunio u čovjeka?'),
(2178, 18, 38, 'Međutim, On, Allah, je Gospodar moj, i ne pridružujem Gospodaru svom nikoga.'),
(2179, 18, 39, 'A zašto nisi, kad si ušao u bašču svoju, rekao: "MašaAllah! Nema moći, izuzev u Allaha!" Ako me vidiš da sam manji od tebe imetkom i djetetom,'),
(2180, 18, 40, 'Pa možda će Gospodar moj da mi da bolje od tvoje bašče - i poslati na nju obračun s neba, pa će osvanuti tlom klizavim,'),
(2181, 18, 41, 'Ili osvanuti voda njena porinuta, pa je nećeš moći naći."'),
(2182, 18, 42, 'I bijahu opasani plodovi njegovi. Tad poče okretati dlanove svoje nad onim šta je na nju utrošio, a ona je bila pusta na potpornjima svojim, i reče: "O da ja nisam pridruživao Gospodaru svom nikoga!"'),
(2183, 18, 43, 'I nije za njega bilo grupe koja će ga pomoći mimo Allaha i nije se mogao odbraniti.'),
(2184, 18, 44, 'Ondje je zaštita Allaha Istinskog; On je Najbolji nagradom i Najbolji završetkom.'),
(2185, 18, 45, 'I navedi im primjer života Dunjaa kao vode: spuštamo je s neba pa se miješa njome zemaljsko bilje, te (potom) postanu suharci koje raznosi vjetar. A Allah nad svakom stvari ima moć!'),
(2186, 18, 46, 'Bogatstvo i sinovi su ukras života Dunjaa; a trajna dobra djela su bolja nagradom kod Gospodara tvog i bolja nadom.'),
(2187, 18, 47, 'A na Dan kad pokrenemo brda i vidiš Zemlju istaknutu, i njih saberemo, tad nećemo ostaviti nijednog od njih,'),
(2188, 18, 48, 'I budu izvedeni pred Gospodara tvog u redovima: "Doista ste nam došli kao što smo vas stvorili prvi put. Naprotiv, tvrdili ste da vam nećemo učiniti rok."'),
(2189, 18, 49, 'I postaviće se Knjiga, pa ćeš vidjeti prestupnike brižne zbog onog šta je u njoj, a govoriće: "O teško nama! Kakva je ovo knjiga? Ne ostavlja malo, niti veliko, da ga nije pobrojala!" I naći će šta su radili prisutno, a Gospodar tvoj neće učiniti zulm nikome.'),
(2190, 18, 50, 'I kad rekosmo melecima: "Padnite na sedždu Ademu", tad učiniše sedždu, izuzev Iblisa. Bio je od džinna, pa se ogriješio o naredbu Gospodara svog. Pa zar ćete uzeti njega i potomstvo njegovo zaštitnicima mimo Mene?, a oni su vam neprijatelji. Loša je zalimima zamjena!'),
(2191, 18, 51, 'Nisam učinio da oni svjedoče stvaranje nebesa i Zemlje, niti stvaranje duša njihovih, i nisam uzimalac zavodnika (kao) pomagača.'),
(2192, 18, 52, 'A na Dan kad rekne: "Pozovite ortake Moje - koje ste smatrali." Tad će ih pozvati, pa im se neće odazvati, a načinićemo među njima stratište.'),
(2193, 18, 53, 'I vidjeće prestupnici vatru, pa će shvatiti da će oni u nju pasti, i neće od nje naći spasa.'),
(2194, 18, 54, 'I doista smo izložili ljudima u ovom Kur\'anu od svačeg primjer. A čovjek je više od ičeg raspravljač.'),
(2195, 18, 55, 'A zadržava ljude da vjeruju - kad im je došla Uputa i da traže oprost Gospodara svog - jedino što će im doći sunnet ranijih ili će im doći kazna sučelice.'),
(2196, 18, 56, 'I ne šaljemo izaslanike, izuzev (kao) donosioce radosnih vijesti i opominjače. A oni koji ne vjeruju, raspravljaju neistinom, radi pobijanja njome Istine, i uzimaju ajete Moje i ono čim su upozoreni, ruglom.'),
(2197, 18, 57, 'A ko je nepravedniji od onog ko je opomenut ajetima Gospodara svog, pa se okrene od njih i zaboravi šta su unaprijed poslale ruke njegove? Uistinu, Mi smo na srcima njihovim načinili velove da ga ne razumiju, i u ušima njihovim gluhoću. I ako ih pozoveš Uputi, pa tad se nikad neće uputiti.'),
(2198, 18, 58, 'A Gospodar tvoj je Oprosnik, Vlasnik milosti. Da ih ščepava za ono šta su zaradili, sigurno bi im ubrzao kaznu. Naprotiv, imaju oni rok, neće naći mimo njega utočište.'),
(2199, 18, 59, 'A to su gradovi - uništili smo ih pošto su činili zulm, i dali za uništenje njihovo rok.'),
(2200, 18, 60, 'I kad reče Musa sluzi svom: "Neću prestati dok ne stignem sastajalištu dvaju mora, ili ću ići godinama."'),
(2201, 18, 61, 'Pa pošto stigoše sastajalištu njih dvoje, zaboraviše ribu svoju, pa (ona) uze put svoj u more tunelom.'),
(2202, 18, 62, 'Pa pošto su prešli, reče sluzi svom: "Daj nam naš doručak. Doista smo na ovom našem putovanju naišli na umor."'),
(2203, 18, 63, 'Reče: "Jesi li vidio kad smo se sklonuli na stijenu? Tad sam ja uistinu zaboravio ribu - a samo je šejtan učinio da zaboravim sjetiti je se, i uzela je put svoj u more, čudnovato!"'),
(2204, 18, 64, '(Musa) reče: "To je ono što smo tražili." Pa se vratiše na svoje tragove, slijedeći (ih).'),
(2205, 18, 65, 'Tad nađoše roba između robova Naših kojem smo dali milost Našu, i poučili ga od Nas znanjem.'),
(2206, 18, 66, 'Musa mu reče: "Hoću li te slijediti da me naučiš onom čemu si naučen - ispravnosti?"'),
(2207, 18, 67, '(Hidr) reče: "Uistinu, ti nećeš moći sa mnom imati strpljenja."'),
(2208, 18, 68, 'I kako da se strpiš nad onim šta ne obuhvataš znanjem?'),
(2209, 18, 69, '(Musa) reče: "Naći ćeš me, ako htjedne Allah, strpljivim, i neću se opirati tvojoj naredbi."'),
(2210, 18, 70, '(Hidr) reče: "Pa ako me budeš slijedio, tad me ništa ne pitaj, dok ti o njemu spomen ne ispričam."'),
(2211, 18, 71, 'Tad odoše, dok - kad su se ukrcali na lađu, (Hidr) je probuši. Reče: "Jesi li je probušio da potopiš ljudstvo njeno? Doista si učinio stvar tešku!"'),
(2212, 18, 72, '(Hidr) reče: "Zar nisam rekao: \'Uistinu, ti nećeš moći sa mnom imati strpljenja?"'),
(2213, 18, 73, '(Musa) reče: "Ne kori me što sam zaboravio, i ne preopterećuj me od posla teškoćom."'),
(2214, 18, 74, 'Pa odoše, dok - kad susretoše dječaka, tad ga (Hidr) ubi. (Musa) reče: "Jesi li ubio dušu čistu koja nije (ubila) drugu dušu? Doista si učinio stvar strašnu!"'),
(2215, 18, 75, '(Hidr) reče: "Zar ti ne rekoh: \'Uistinu, ti nećeš moći sa mnom imati strpljenja?"'),
(2216, 18, 76, '(Musa) reče: "Ako te poslije ovog išta upitam, tad se ne druži sa mnom. Već si od mene dobio opravdanje."'),
(2217, 18, 77, 'Tad odoše, dok - kad dođoše stanovnicima grada, zatražiše od stanovnika njegovih hranu, pa odbiše da ih ugoste. Tad u njemu nađoše zid sklon padu, pa ga (Hidr) uspravi. (Musa) reče: "Da si htio, sigurno bi za to uzeo naknadu."'),
(2218, 18, 78, '(Hidr) reče: "Ovo je rastanak između mene i između tebe! Obavijestiću te o tumačenju onog zbog čega nisi mogao imati strpljenja.'),
(2219, 18, 79, 'Što se tiče lađe, pa pripadala je siromasima koji rade na moru, pa sam želio da je oštetim - a iza njih je bio vladar koji svaku lađu uzima nasilno.'),
(2220, 18, 80, 'A što se tiče dječaka, pa bili su roditelji njegovi vjernici, te smo se bojali da će ih pritisnuti buntovnošću i nevjerovanjem,'),
(2221, 18, 81, 'Pa smo željeli da im zamijeni Gospodar njihov boljeg od njega čistoćom i bližeg samilosti.'),
(2222, 18, 82, 'A što se tiče zida, pa pripadao je dvojici dječaka, siročadi u gradu, a bilo je ispod njega blago njihovo i bio je otac njihov pravedan, pa je želio Gospodar tvoj da dostigu zrelost svoju i izvade blago svoje; milost od Gospodara tvog. I nisam to učinio po odredbi svojoj. To je tumačenje onog nad čim nisi mogao imati strpljenja."'),
(2223, 18, 83, 'I pitaju te o Zul-karnejnu. Reci: "Izrecitovaću vam o njemu spomen."'),
(2224, 18, 84, 'Uistinu, Mi smo ga učvrstili na Zemlji i dali mu od svake stvari put.'),
(2225, 18, 85, 'Pa je slijedio put.'),
(2226, 18, 86, 'Dok, kad stiže zapadu Sunca, nađe ga zalazi u izvor muljevit, i nađe kod njega narod. Rekosmo: "O Zul-karnejne! Ili kazni, ili im iskaži dobrotu."'),
(2227, 18, 87, 'Reče: "Što se tiče onog ko čini zulm, pa kaznićemo ga, zatim će biti vraćen Gospodaru svom, te će ga kazniti kaznom strašnom.'),
(2228, 18, 88, 'A što se tiče onog ko vjeruje i čini dobro, pa imaće on plaću dobru, i govorićemo mu iz odredbe Naše lahko."'),
(2229, 18, 89, 'Zatim je slijedio put,'),
(2230, 18, 90, 'Dok - kad stiže izlazištu Sunca, nađe ga izlazi nad ljudima kojima nismo od njega načinili zaštitu.'),
(2231, 18, 91, 'Tako! I doista smo ono šta je kod njega obuhvatili znanjem.'),
(2232, 18, 92, 'Zatim je slijedio put.'),
(2233, 18, 93, 'Dok - kad stiže među dva brda, nađe pored njih narod, jedva razumiju govor.'),
(2234, 18, 94, 'Rekoše: "O Zul-karnejne! Uistinu su Jedžudž i Medžudž mufsidi na Zemlji, pa hoćemo li ti dati naknadu da između nas i njih načiniš barijeru?"'),
(2235, 18, 95, 'Reče: "Ono u čemu me je učvrstio Gospodar moj je bolje, zato me pomozite snagom; načiniću između vas i njih bedem;'),
(2236, 18, 96, 'Dajte mi komade gvožđa", dok - kad izravna između dva obronka, reče: "Pušite!"; dok - kad ga učini vatrom, reče: "Dajte mi da na njega izlijem (rastopljen) bakar."'),
(2237, 18, 97, 'Tad nisu mogli da ga pređu, niti su mogli da ga prokopaju.'),
(2238, 18, 98, '(Zul-karnejn) reče: "Ovo je milost od Gospodara mog, pa kad dođe obećanje Gospodara mog, učiniće ga sravnjenim. A obećanje Gospodara mog je istinito."'),
(2239, 18, 99, 'I ostavićemo da se oni Tog dana zatalasaju jedni u druge, i puhnuće se u sur, pa ćemo ih sve sabrati.'),
(2240, 18, 100, 'I izložićemo Tog dana Džehennem nevjernicima, izlaganjem,'),
(2241, 18, 101, 'Onima čije su oči bile pod pokrivkom prema Opomeni Mojoj, i nisu mogli da čuju.'),
(2242, 18, 102, 'Pa zar misle oni koji ne vjeruju, da (mogu) uzeti robove Moje mimo Mene zaštitnicima? Uistinu! Mi smo za nevjernike pripremili Džehennem kao ugošćenje.'),
(2243, 18, 103, 'Reci: "Hoćete li da vas obavijestimo o najvećim gubitnicima djelima?'),
(2244, 18, 104, 'Oni koji zalutaju trudom svojim u životu Dunjaa, a oni računaju da oni rade dobar posao!"'),
(2245, 18, 105, 'Takvi su oni koji ne vjeruju u ajete Gospodara svog i susret s Njim. Zato će propasti djela njihova, pa nećemo njima na Dan kijameta dati (nikakvu) težinu.'),
(2246, 18, 106, 'To je plaća njihova: Džehennem, što nisu vjerovali i uzimali ajete Moje i poslanike Moje ruglom.'),
(2247, 18, 107, 'Uistinu! Oni koji vjeruju i rade dobra djela, imaće oni bašče Firdewsa (kao) ugošćenje,'),
(2248, 18, 108, 'Vječno će biti u njima. Neće želiti iz njih premještanje.'),
(2249, 18, 109, 'Reci: "Kad bi more bilo tinta za riječi Gospodara mog, sigurno bi se iscrpilo more prije no što bi se iscrpile riječi Gospodara mog, makar mi donijeli slično njemu, (kao) pojačanje.'),
(2250, 18, 110, 'Reci: "Ja sam samo smrtnik sličan vama! Meni se objavljuje da je Bog vaš, Bog Jedini! Pa ko se nada susretu Gospodara svog, onda nek čini dobro djelo i ne pridružuje u obožavanju Gospodara svog, nikoga."');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kaf. Ha. Ja. \'Ain. Sad.'),
(2252, 19, 2, 'Spomen milosti Gospodara tvog, robu Njegovom Zekerijjau,'),
(2253, 19, 3, 'Kad pozva Gospodara svog pozivom tajnim,'),
(2254, 19, 4, 'Reče: "Gospodaru moj! Uistinu, meni je kost moja oslabila i sjaji glava sjedošću, a nisam bio dowom Tebi Gospodaru, nesretan.'),
(2255, 19, 5, 'A uistinu, ja se bojim svojih rođaka iza sebe, a moja žena je nerotkinja. Zato mi pokloni od Sebe nasljednika,'),
(2256, 19, 6, 'Da me naslijedi, i naslijedi iz porodice Jakubove. I učini ga Gospodaru moj, zadovoljnim."'),
(2257, 19, 7, '"O Zekerijja! Uistinu, Mi ti donosimo radosnu vijest o dječaku, ime njegovo biće Jahja. Nismo mu ranije učinili imenjaka."'),
(2258, 19, 8, 'Reče: "Gospodaru moj! Otkud meni dječak, a žena moja je nerotkinja, i već sam dostigao staračku iznemoglost?"'),
(2259, 19, 9, 'Reče: "Tako. Rekao je Gospodar tvoj: \'To je Meni lahko, i već sam te stvorio ranije, a nisi bio ništa."'),
(2260, 19, 10, 'Reče: "Gospodaru moj! Učini mi znak." Reče: "Znak ti je da nećeš govoriti s ljudima tri noći, (a bićeš) zdrav."'),
(2261, 19, 11, 'Tad iziđe narodu svom iz mihraba, pa im objavi: "Slavite (Allaha) jutrom i navečer."'),
(2262, 19, 12, '"O Jahja! Prihvati Knjigu snažno." I dali smo mu sud (kao) djetetu,'),
(2263, 19, 13, 'I nježnost od Nas i čistotu, a bio je bogobojazan,'),
(2264, 19, 14, 'I čestit roditeljima svojim, a nije bio nepokoran, neposlušan.'),
(2265, 19, 15, 'I mir neka je s njim na dan kad se rodio i dan kada je umro, i dan kad bude podignut živ!'),
(2266, 19, 16, 'I spomeni u Knjizi Merjem, kad se povukla od porodice svoje u mjesto istočno,'),
(2267, 19, 17, 'Te uzela (da se zakloni) od njih zastor. Tad smo joj poslali Duha Našeg, te joj se prikazao čovjekom pravim.'),
(2268, 19, 18, 'Reče: "Uistinu, ja tražim zaštitu Milostivog od tebe, ako si bogobojazan."'),
(2269, 19, 19, '(Duh) reče: "Ja sam samo izaslanik Gospodara tvog, da ti podarim dječaka čista."'),
(2270, 19, 20, 'Reče: "Otkud meni dječak, a nije me dotakao smrtnik i nisam bludnica?"'),
(2271, 19, 21, '(Ona) reče: "Tako. Rekao je Gospodar tvoj: \'To je Meni lahko\' - i da ga učinimo znakom ljudima i milošću od Nas" - a bila je stvar određena.'),
(2272, 19, 22, 'Tad ga je zanijela, te se s njim povukla u mjesto daleko.'),
(2273, 19, 23, 'Pa dovedoše nju porođajni bolovi ka stablu palme. Reče: "O da sam ja umrla prije ovog i bila zaboravom zaboravljena."'),
(2274, 19, 24, 'Tad pozva nju ispod nje: "Ne žalosti se! Doista je načinio Gospodar tvoj ispod tebe potočić.'),
(2275, 19, 25, 'I zatresi sebi stablo palme, pašće na te datule zrele.'),
(2276, 19, 26, 'Zato jedi i pij, i ohladi oko! Pa ako od smrtnika vidiš ijednog, tad reci: "Uistinu! Ja sam se Milostivom zavjetovala postom, zato danas neću govoriti s čovjekom."'),
(2277, 19, 27, 'Pa je došla s njim narodu svom, noseći ga. Rekoše: "O Merjem! Doista si došla sa stvari nečuvenom.'),
(2278, 19, 28, 'O sestro Harunova! Nije bio otac tvoj loš čovjek, niti je majka tvoja bila bludnica."'),
(2279, 19, 29, 'Tad ga je pokazala. Rekoše: "Kako da govorimo s onim ko je u bešici, (s) djetetom?"'),
(2280, 19, 30, '(Isa) reče: "Uistinu! Ja sam rob Allahov. Dao mi je Knjigu i učinio me vjerovjesnikom,'),
(2281, 19, 31, 'I učinio me blagoslovljenim ma gdje bio, i naredio mi salat i zekat dok sam živ,'),
(2282, 19, 32, 'I da budem čestit majci svojoj, a nije me učinio nepokornim, nesretnim.'),
(2283, 19, 33, 'I mir neka je nada mnom na dan kad sam rođen, i dan kad umrem, i dan kad budem podignut živ!"'),
(2284, 19, 34, 'To je Isa, sin Merjemin: Riječ Istine, ona o kojoj raspravljaju.'),
(2285, 19, 35, 'Nije Allahovo da uzima ikakvo dijete. Slavljen neka je On! Kad odredi stvar, tad tome samo kaže: "Budi" - tad biva.'),
(2286, 19, 36, 'A uistinu! Allah je Gospodar moj i Gospodar vaš, zato Njega obožavajte. Ovo je put pravi.'),
(2287, 19, 37, 'Pa su se razišle stranke međusobno: zato teško onima koji ne vjeruju, od poprišta Dana strašnog.'),
(2288, 19, 38, 'Kako će oni čuti i vidjeti na Dan kad Nam dođu! Ali, zalimi su danas u zabludi očitoj.'),
(2289, 19, 39, 'I upozori ih Danom žalosti, kad bude svršena stvar; a oni su u ravnodušnosti i oni ne vjeruju.'),
(2290, 19, 40, 'Uistinu! Mi, Mi nasljeđujemo Zemlju i onog ko je na njoj, a Nama će biti vraćeni.'),
(2291, 19, 41, 'I spomeni u Knjizi Ibrahima. Uistinu, on je bio istinit, vjerovjesnik.'),
(2292, 19, 42, 'Kad reče ocu svom: "O oče moj! Zašto obožavaš ono šta ne čuje niti vidi, niti ti imalo koristi?'),
(2293, 19, 43, 'O oče moj! Uistinu meni, već mi je došlo od znanja šta nije došlo tebi. Zato me slijedi, uputiću te putu pravom.'),
(2294, 19, 44, 'O oče moj! Ne obožavaj šejtana. Uistinu, šejtan je Milostivom neposlušan.'),
(2295, 19, 45, 'O oče moj! Uistinu, ja se bojim da će te dotaći kazna od Milostivog, pa ćeš biti šejtanov drug."'),
(2296, 19, 46, 'Reče: "Jesi li ti odbojan prema bogovima mojim, o Ibrahime? Ako ne odustaneš, sigurno ću te kamenovati, i ostavi me dugo!"'),
(2297, 19, 47, 'Reče: "Selamun alejk! Tražiću ti oprost Gospodara svog. Uistinu, On mi je predusretljiv.'),
(2298, 19, 48, 'I povlačim se od vas i onog šta zazivate mimo Allaha, a prizivat ću Gospodara svog. Možda neću u dowi Gospodaru svom nesretan biti."'),
(2299, 19, 49, 'Pa pošto se povukao od njih i onog šta obožavaju mimo Allaha, podarili smo mu Ishaka i Jakuba, a svakojeg smo učinili vjerovjesnikom.'),
(2300, 19, 50, 'I darovali smo im iz milosti Naše, i učinili za njih jezik istinitošću visokim.'),
(2301, 19, 51, 'I spomeni u Knjizi Musaa. Uistinu, bio je odabran, i bio poslanik, vjerovjesnik.'),
(2302, 19, 52, 'I pozvali smo ga s desnog obronka Tura, i približili ga u povjerljivom razgovoru,'),
(2303, 19, 53, 'I darovali mu iz milosti Naše brata njegovog Haruna, vjerovjesnika.'),
(2304, 19, 54, 'I spomeni u Knjizi Ismaila. Uistinu, on je bio istinit obećanjem, i bio je poslanik, vjerovjesnik,'),
(2305, 19, 55, 'I naređivao je porodici svojoj salat i zekat, i bio je kod Gospodara svog zadovoljavajući.'),
(2306, 19, 56, 'I spomeni u Knjizi Idrisa. Uistinu, on je bio istinit, vjerovjesnik,'),
(2307, 19, 57, 'A uzdigli smo ga na mjesto visoko.'),
(2308, 19, 58, 'To su oni koje je obdario Allah od vjerovjesnika iz potomstva Ademovog - od onog koga smo nosili s Nuhom - i iz potomstva Ibrahimovog i Israilovog, i od onog koga smo uputili i odabrali. Kad su im se učili ajeti Milostivog, padali su na sedždu i plakali.'),
(2309, 19, 59, 'Pa je poslije njih došlo pokoljenje - zapustili su salat i slijedili strasti. Zato će sresti zabludu,'),
(2310, 19, 60, 'Izuzev ko se pokaje i vjeruje, i čini dobro. Pa takvi će ući u Džennet, i neće im se učiniti zulm nimalo -'),
(2311, 19, 61, 'Bašče Adna koje je obećao Milostivi robovima Svojim u nevidljivom. Uistinu! Obećanje Njegovo će doći -'),
(2312, 19, 62, 'Neće čuti u njemu naklapanje, samo "Selam" - i imaće oni svoju opskrbu u njemu, jutrom i navečer.'),
(2313, 19, 63, 'To je Džennet kojeg dajemo u naslijeđe onom od robova Naših ko bude bogobojazan.'),
(2314, 19, 64, '"I ne spuštamo se, izuzev po naredbi Gospodara tvog. Njegovo je šta je ispred nas i šta je iza nas i šta je između toga, a nije Gospodar tvoj zaboravan."'),
(2315, 19, 65, 'Gospodar nebesa i Zemlje i šta je između njih! Zato Njega obožavaj, i istraj u obožavanju Njegovom. Da li znaš Njemu imenjaka?'),
(2316, 19, 66, 'I govori čovjek: "Hoću li kad umrem, zaista biti živ izveden?"'),
(2317, 19, 67, 'Zar se ne prisjeća čovjek, da smo ga Mi stvorili ranije, a nije bio ništa?'),
(2318, 19, 68, 'Pa tako mi Gospodara tvog, sigurno ćemo sabrati njih i šejtane, zatim ćemo ih sigurno privesti oko Džehennema, klečeći.'),
(2319, 19, 69, 'Zatim ćemo sigurno iz svake sekte istrgnuti onog od njih koji je bio najžešći protiv Allaha buntovnošću.'),
(2320, 19, 70, 'Zatim, sigurno smo Mi Najbolji znalac onih koji su najdostojniji da budu u njemu izgarani.'),
(2321, 19, 71, 'I nema od vas nijednog, a da neće njemu stići. Do Gospodara tvog je odluka izvršna.'),
(2322, 19, 72, 'Zatim ćemo spasiti one koji su se bojali, a ostaviti zalime u njemu klečeći.'),
(2323, 19, 73, 'A kad im se uče ajeti Naši jasni, oni koji ne vjeruju govore onima koji vjeruju: "Koja od dvije skupine je bolja mjestom i bolja sazivom?"'),
(2324, 19, 74, 'A koliko smo pokoljenja uništili prije njih, (a) oni su bili bolji namještajem i izgledom!'),
(2325, 19, 75, 'Reci: "Ko je u zabludi, pa neka mu Milostivi produži dužinu. Dok, kad vide ono čime im se prijetilo, ili kaznu ili Čas, tad će saznati ko je taj (ko) je gori položajem i slabiji vojskom."'),
(2326, 19, 76, 'I povećava Allah uputu onima koji se upućuju. A trajna dobra djela su bolja nagradom kod Gospodara tvog i bolja povratištem.'),
(2327, 19, 77, 'Pa da li si vidio onog koji ne vjeruje u ajete Naše i kaže: "Sigurno će mi biti dat imetak i dijete?"'),
(2328, 19, 78, 'Je li spoznao nevidljivo, ili je kod Milostivog uzeo obavezu?'),
(2329, 19, 79, 'Nikako! Zapisaćemo šta govori, i produžićemo mu dužinu kazne.'),
(2330, 19, 80, 'I naslijedićemo (od) njega šta govori, i doći će Nam sam.'),
(2331, 19, 81, 'A uzimali su mimo Allaha bogove, da budu za njih moć.'),
(2332, 19, 82, 'Nikako! Poreći će obožavanje njihovo i njima protivnik biti.'),
(2333, 19, 83, 'Zar ne vidiš da Mi šaljemo šejtane na nevjernike, podstiču ih podsticanjem.'),
(2334, 19, 84, 'Zato ne žuri protiv njih, samo im odbrojavamo broj.'),
(2335, 19, 85, 'Na Dan kad saberemo bogobojazne Milostivom (kao) izaslanstvo -'),
(2336, 19, 86, 'I potjeramo krivce ka Džehennemu žedne -'),
(2337, 19, 87, 'Neće vladati posredovanjem, izuzev ko je kod Allaha uzeo obećanje.'),
(2338, 19, 88, 'I govore: "Milostivi je uzeo dijete."'),
(2339, 19, 89, 'Doista iznosite stvar strašnu:'),
(2340, 19, 90, 'Gotovo da se od toga nebesa rascijepe i raspukne Zemlja, i padnu brda rušeći se,'),
(2341, 19, 91, 'Što prizivaju Milostivom dijete!'),
(2342, 19, 92, 'A nije Milostivom potrebno da uzima dijete.'),
(2343, 19, 93, 'Svako ko je u nebesima i Zemlji, samo će doći Milostivom kao rob.'),
(2344, 19, 94, 'Doista ih je sračunao i pobrojao ih brojanjem.'),
(2345, 19, 95, 'I svaki od njih će Mu doći na Dan kijameta sam.'),
(2346, 19, 96, 'Uistinu, onima koji vjeruju i rade dobra djela, daće im Allah ljubav.'),
(2347, 19, 97, 'Pa olakšali smo ga na jeziku tvom, da obraduješ njime bogobojazne, i opomeneš njime narod svadljivaca.'),
(2348, 19, 98, 'A koliko smo prije njih uništili naraštaja! Da li osjećaš od njih ijednog, ili čuješ njihov tihi šum?');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Ta. Ha.'),
(2350, 20, 2, 'Nismo ti objavili Kur\'an da se mučiš,'),
(2351, 20, 3, 'Nego (kao) Opomenu za onog ko se boji (Allaha).'),
(2352, 20, 4, 'Objava je od Onog ko je stvorio Zemlju i nebesa visoka -'),
(2353, 20, 5, 'Milostivog, na Aršu opstojećeg.'),
(2354, 20, 6, 'Njegovo je šta je u nebesima i šta je na Zemlji, i šta je između njih dvoje, i šta je ispod tla.'),
(2355, 20, 7, 'A ako si glasan u govoru, pa uistinu, On zna tajnu i skrivenije.'),
(2356, 20, 8, 'Allah! Samo je On Bog! Njegova imena su najljepša!'),
(2357, 20, 9, 'A da li ti je stigao hadis o Musau?'),
(2358, 20, 10, 'Kad ugleda vatru pa reče porodici svojoj: "Sačekajte! Uistinu, ja sam opazio vatru; možda ću vam od nje donijeti glavnju, ili ću kod vatre naći uputu."'),
(2359, 20, 11, 'Pa pošto joj stiže, bi pozvan: "O Musa!'),
(2360, 20, 12, 'Uistinu! Ja, Ja sam Gospodar tvoj! Zato izuj obuću svoju. Uistinu si ti u svetoj dolini Tuwa,'),
(2361, 20, 13, 'I Ja sam te odabrao, zato slušaj šta ti se objavljuje.'),
(2362, 20, 14, 'Uistinu! Ja, Ja sam Allah! Nema boga osim Mene! Zato Mene obožavaj i obavljaj salat, da Me se sjetiš.'),
(2363, 20, 15, 'Uistinu, Čas je dolazeći, skori je, tajim ga da bi se platilo svakoj duši ono zašto se trudila.'),
(2364, 20, 16, 'Zato neka te nikako od njega ne odvrati onaj ko u njega ne vjeruje i slijedi strast svoju, pa da stradaš.'),
(2365, 20, 17, 'A šta je to u desnici tvojoj, o Musa?"'),
(2366, 20, 18, 'Reče: "Ovo je moj štap, oslanjam se na njega i mlatim (lišće) njime ovcama svojim, a imam ja u njemu (i) druge svrhe."'),
(2367, 20, 19, '(Allah) reče: "Baci ga, o Musa!"'),
(2368, 20, 20, 'Pa ga baci, tad gle: on zmija, gmiže.'),
(2369, 20, 21, 'Reče: "Uzmi je i ne boj se. Povratićemo je njenom prvom stanju;'),
(2370, 20, 22, 'I stavi ruku svoju pod pazuho svoje, izaći će bijela, bez nedostatka - znak drugi,'),
(2371, 20, 23, 'Da bismo ti pokazali od znakova Naših, veći.'),
(2372, 20, 24, 'Idi faraonu! Uistinu, on je prevršio."'),
(2373, 20, 25, '(Musa) reče: "Gospodaru moj! Proširi mi grudi moje,'),
(2374, 20, 26, 'I olakšaj mi stvar moju,'),
(2375, 20, 27, 'I razveži čvor jezika mog,'),
(2376, 20, 28, '(Da) razumiju govor moj,'),
(2377, 20, 29, 'I učini mi wezirom iz porodice moje,'),
(2378, 20, 30, 'Haruna, brata mog;'),
(2379, 20, 31, 'Pojačaj njime snagu moju,'),
(2380, 20, 32, 'I pridruži ga u stvari mojoj,'),
(2381, 20, 33, 'Da slavimo Tebe mnogo,'),
(2382, 20, 34, 'I spominjemo Te mnogo.'),
(2383, 20, 35, 'Uistinu! Ti si Onaj koji nas vidi."'),
(2384, 20, 36, '(Allah) reče: "Već ti je dato potraživanje tvoje, o Musa",'),
(2385, 20, 37, 'I doista smo te darovali drugi put,'),
(2386, 20, 38, 'Kad smo objavili majci tvojoj šta je objavljeno:'),
(2387, 20, 39, '"Ubaci ga u tabut i baci ga u rijeku, pa će ga izbaciti rijeka na obalu, uzeće ga neprijatelj Moj i neprijatelj njegov." I bacio sam na te ljubav od Sebe, i da budeš odgajan pod okom Mojim.'),
(2388, 20, 40, 'Kad ode sestra tvoja, pa reče: "Hoćete li da vam ukažem na onog ko će ga odgajati?" Tad smo te vratili majci tvojoj da ohladi oko svoje i ne tuguje. I ubio si dušu, pa smo te oslobodili brige, i iskušavali te kušnjom; pa si boravio godinama među stanovnicima Medjena, zatim si došao po odredbi, o Musa.'),
(2389, 20, 41, 'I odabrao sam te Sebi:'),
(2390, 20, 42, '"Idi ti i brat tvoj sa znakovima Mojim, i ne popuštajte u Mom spominjanju.'),
(2391, 20, 43, 'Idite faraonu. Uistinu, on je prevršio,'),
(2392, 20, 44, 'Pa mu recite riječ blagu, da bi se on poučio ili pobojao."'),
(2393, 20, 45, 'Rekoše: "Gospodaru naš! Uistinu! Mi se bojimo da će na nas požuriti ili da će prevršiti."'),
(2394, 20, 46, '(Allah) reče: "Ne bojte se! Uistinu, Ja sam sa vama dvojicom, čujem i vidim.'),
(2395, 20, 47, 'Zato mu idite, pa recite: \'Uistinu mi smo dva poslanika Gospodara tvog, pa pošalji s nama sinove Israilove, i ne muči ih. Doista smo ti donijeli znak od Gospodara tvog, a mir nek\' je nad onim ko slijedi Uputu.'),
(2396, 20, 48, 'Uistinu, nama je već objavljeno da će biti kazna nad onim ko porekne i odvrati se."'),
(2397, 20, 49, '(Faraon) reče: "Pa ko je Gospodar vaš, o Musa?"'),
(2398, 20, 50, '(Musa) reče: "Gospodar naš je onaj koji je svakoj stvari dao stvaranje njeno, zatim uputio."'),
(2399, 20, 51, '(Faraon) reče: "Pa kakvo je stanje pokoljenja ranijih?"'),
(2400, 20, 52, '(Musa) reče: "Znanje o njima je u Knjizi kod Gospodara mog. Ne griješi Gospodar moj, niti zaboravlja,'),
(2401, 20, 53, 'Onaj koji je za vas učinio Zemlju bešikom, i utro vam po njoj puteve, i spustio s neba vodu" - pa smo izveli njome parove bilja različitog.'),
(2402, 20, 54, 'Jedite i napasajte stoku svoju. Uistinu, u tome su znaci za posjednike razuma.'),
(2403, 20, 55, 'Od nje smo vas stvorili i u nju vas vraćamo, i iz nje ćemo vas izvesti drugi put.'),
(2404, 20, 56, 'I doista smo mu pokazali znakove Naše, svakog od njih, pa je porekao i odbio.'),
(2405, 20, 57, '(Faraon) reče: "Jesi li nam došao da nas izvedeš iz zemlje naše sa sihrom svojim, o Musa?'),
(2406, 20, 58, 'Pa sigurno ćemo ti donijeti sihr sličan tome; zato utvrdi između nas i tebe rok, nećemo ga prekršiti mi niti ti, na mjestu odgovarajućem."'),
(2407, 20, 59, '(Musa) reče: "Termin vaš je dan ukrašavanja, i nek se saberu ljudi jutrom."'),
(2408, 20, 60, 'Tad se faraon okrenu, pa skova svoj plan, zatim dođe.'),
(2409, 20, 61, 'Musa im reče: "Teško vama! Ne izmišljajte protiv Allaha laž, pa da vas kaznom iskorijeni. A doista neće uspjeti ko izmišlja."'),
(2410, 20, 62, 'Zatim su se prepirali o stvari svojoj međusobno i krili sašaptavanje.'),
(2411, 20, 63, 'Rekoše: "Uistinu, ova dvojica su dva čarobnjaka, žele da vas izvedu iz zemlje vaše sihrom svojim, i unište tarikat vaš najprimjerniji.'),
(2412, 20, 64, 'Zato skujte plan vaš, zatim dođite u saffu. Pa doista će danas uspjeti ko nadvlada."'),
(2413, 20, 65, 'Rekoše: "O Musa! Ili da baciš, ili da budemo prvi koji baca?"'),
(2414, 20, 66, 'Reče: "Naprotiv, bacite!" Tad gle: konopi njihovi i štapovi njihovi mu se učiniše od sihra njihovog, da oni gmižu.'),
(2415, 20, 67, 'Tad Musa osjeti u svojoj duši strah.'),
(2416, 20, 68, 'Rekosmo: "Ne boj se! Uistinu ti, ti ćeš biti viši.'),
(2417, 20, 69, 'I baci to šta je u desnici tvojoj! Progutaće ono šta su napravili. Uistinu, napravili su lukavstvo čarobnjaka, a čarobnjak neće uspjeti ma gdje došao."'),
(2418, 20, 70, 'Tad se čarobnjaci baciše na sedždu. Rekoše: "Vjerujemo u Gospodara Harunovog i Musaovog."'),
(2419, 20, 71, '(Faraon) reče: "Povjerovaste li mu prije nego vam dopustih? Uistinu, on je velikan vaš koji vas je naučio sihru. Zato ću sigurno odsjeći ruke vaše i noge vaše unakrst, i sigurno ću vas raspeti na stabla palmi. A sigurno ćete saznati koji je od nas žešće kazne i trajnije."'),
(2420, 20, 72, 'Rekoše: "Nećemo te odabrati nad onim šta nam je došlo od jasnih dokaza, i Onim ko nas je stvorio, zato sudi šta ćeš ti presuditi. Samo sudiš ovaj život Dunjaa.'),
(2421, 20, 73, 'Uistinu, mi vjerujemo u Gospodara našeg, da nam oprosti greške naše i ono na šta si nas prisilio od sihra. A Allah je Najbolji i Najtrajniji."'),
(2422, 20, 74, 'Uistinu, onaj ko dođe Gospodaru svom kao krivac, pa uistinu, imaće on Džehennem. Neće umrijeti u njemu, niti živjeti.'),
(2423, 20, 75, 'A ko Mu dođe (kao) vjernik, već radio dobra djela, pa ti takvi će imati stepene visoke;'),
(2424, 20, 76, 'Bašče Adna ispod kojih teku rijeke, biće vječno u njima; a to je plaća onog ko se očisti.'),
(2425, 20, 77, 'I doista smo objavili Musau: "Kreni sa robovima Mojim noću, pa udari za njih put suh u moru, ne bojeći se sustizanja, niti se strašeći."'),
(2426, 20, 78, 'Tad ih je slijedio faraon sa vojskama svojim, pa ih je od mora prekrilo ono što ih je prekrilo.'),
(2427, 20, 79, 'I zaveo je faraon narod svoj, a nije uputio.'),
(2428, 20, 80, 'O sinovi Israilovi! Doista smo vas izbavili od vašeg neprijatelja, i sklopili ugovor s vama (na) obronku Tura desnom i spustili na vas mennu i prepelice.'),
(2429, 20, 81, 'Jedite od dobrih stvari kojima smo vas opskrbili, i ne pretjerujte u tome pa da na vas padne srdžba Moja. A onaj na koga se spusti srdžba Moja, pa doista je propao.'),
(2430, 20, 82, 'I uistinu, Ja sam Oprosnik onom ko se pokaje i uzvjeruje i radi dobro, zatim se uputi.'),
(2431, 20, 83, '"A šta te je požurilo od naroda tvog, o Musa?"'),
(2432, 20, 84, 'Reče: "Oni su ti koji su mi na tragu, a požurio sam Tebi Gospodaru moj, da budeš zadovoljan."'),
(2433, 20, 85, 'Reče: "Pa uistinu, Mi smo već iskušavali narod tvoj poslije tebe i zaveo ih je Samirijja."'),
(2434, 20, 86, 'Tad se vrati Musa narodu svom srdit, tužan. Reče: "O narode moj! Zar vam nije dao Gospodar vaš obećanje lijepo? Pa zar vam se odužilo vrijeme, ili želite da vas snađe srdžba Gospodara vašeg, pa ste mi prekršili obećanje?"'),
(2435, 20, 87, 'Rekoše: "Nismo ti prekršili obećanje svojom voljom, nego smo mi bili natovareni teretima nakita narodnog, pa smo ih ubacili, te je isto tako bacio Samirijja.'),
(2436, 20, 88, 'Pa im je izvadio tele - tijelo, imalo je ono (mogućnost) mukanja, te rekoše: "Ovo je bog vaš i bog Musaov - ta zaboravio je."'),
(2437, 20, 89, 'Pa zar ne vide da im ne uzvraća govor, i ne vlada im štetom niti korišću?'),
(2438, 20, 90, 'A doista im je rekao Harun još prije: "O narode moj! Njime ste samo iskušani. A uistinu, Gospodar vaš je Milostivi, zato me slijedite i poslušajte naredbu moju."'),
(2439, 20, 91, 'Rekoše: "Nećemo njemu prestati biti odani dok nam se Musa ne vrati."'),
(2440, 20, 92, 'Reče: "O Harune! Šta te je zadržalo kad si ih vidio (da) lutaju,'),
(2441, 20, 93, 'Da me ne slijediš? Pa zar si se suprotstavio naredbi mojoj?"'),
(2442, 20, 94, 'Reče: "O sine majke moje! Ne grabi me za moju bradu, niti moju glavu! Uistinu, ja sam se bojao da ne kažeš: \'Razjedinio si sinove Israilove i nisi čekao moju riječ."'),
(2443, 20, 95, 'Reče: "Pa šta je stvar tvoja, o Samirijja?"'),
(2444, 20, 96, 'Reče: "Opazio sam ono šta nisu spazili, pa sam zgrabio šaku (prašine) iz traga izaslanika, te je odbacio; a tako me je navela moja duša."'),
(2445, 20, 97, 'Reče: "Onda idi! Pa uistinu, za tebe je da u životu govoriš: \'Nema doticanja.\' I uistinu, ti imaš termin, neće ti biti iznevjeren. I gledaj ka bogu svom, onom kojem si stalno privržen: sigurno ćemo ga spaliti, zatim ćemo ga sigurno potpuno u more raspršiti.'),
(2446, 20, 98, 'Vaš Bog je jedino Allah koji - nema boga osim Njega - obuhvata svaku stvar znanjem."'),
(2447, 20, 99, 'Tako tebi kazasmo neke vijesti, ono što je već prethodilo, a doista smo ti dali od Nas Opomenu.'),
(2448, 20, 100, 'Ko se od nje odvrati, pa uistinu će on na Dan kijameta nositi teret.'),
(2449, 20, 101, 'Vječno će biti u njemu. A zlo je opterećenje za njih na Dan kijameta,'),
(2450, 20, 102, 'Dan kad se puhne u sur, i saberemo prestupnike Tog dana modre,'),
(2451, 20, 103, 'Tiho će međusobno govoriti: "Ostali ste samo deset (dana)."'),
(2452, 20, 104, 'Mi smo Najbolji znalac onog šta će govoriti, kad rekne tarikatom najprimjerniji njihov: "Ostali ste samo dan."'),
(2453, 20, 105, 'I pitaju te o planinama, pa reci: "Smrvit će ih Gospodar moj u prašinu drobljenjem,'),
(2454, 20, 106, 'Te ih ostaviti ravnicom, pustopoljinom,'),
(2455, 20, 107, 'Nećeš vidjeti na njoj iskrivljenost, niti neravninu."'),
(2456, 20, 108, 'Tog dana slijedit će pozivača, neće biti njegovog skretanja i stišaće se glasovi Milostivom, pa nećeš čuti, izuzev šapat.'),
(2457, 20, 109, 'Tog dana neće koristiti posredovanje, izuzev kome dozvoli Milostivi, i zadovolji (Ga) njegova riječ.'),
(2458, 20, 110, 'Zna šta je ispred njih i šta je iza njih, a ne obuhvataju to znanjem.'),
(2459, 20, 111, 'I poniziće se lica Živom, Samoopstojećem. A doista neće uspjeti ko je nosio (teret) zulma.'),
(2460, 20, 112, 'A ko je radio dobra djela, i on bio vjernik, pa neće se bojati zulma, niti zakidanja.'),
(2461, 20, 113, 'I tako smo ga objavili (kao) Kur\'an na arapskom, i izložili u njemu neke prijetnje, da bi se oni pobojali, ili da im pobudi opomenu.'),
(2462, 20, 114, 'Pa uzvišen neka je Allah, Vladar istinski! I ne žuri s Kur\'anom prije no što ti se okonča objavljivanje njegovo, i reci: "Gospodaru moj, uvećaj mi znanje!"'),
(2463, 20, 115, 'I doista smo obavezali Adema ranije, pa je zaboravio i nismo našli u njega odlučnosti.'),
(2464, 20, 116, 'I kad rekosmo melecima: "Padnite na sedždu Ademu", tad učiniše sedždu, izuzev Iblisa; odbio je.'),
(2465, 20, 117, 'Tad rekosmo: "O Ademe! Uistinu je ovo neprijatelj tebi i ženi tvojoj. Zato neka vas nikako ne izvede iz Dženneta, pa da se mučiš.'),
(2466, 20, 118, 'Uistinu, za tebe je da nećeš gladniti u njemu, niti ogoliti,'),
(2467, 20, 119, 'I da ti u njemu nećeš ožedniti, niti suncu izložen biti."'),
(2468, 20, 120, 'Tad mu došapnu šejtan. Reče: "O Ademe! Hoćeš li da ti ukažem na drvo besmrtnosti i carstvo koje neće nestati?"'),
(2469, 20, 121, 'Pa njih dvoje pojedoše od njega, te im se ukazaše sew\'ati njihovi, i počeše po sebi krpiti (šiti) nešto lišća džennetskog. I nije poslušao Adem Gospodara svog, pa je skrenuo.'),
(2470, 20, 122, 'Zatim ga je odabrao Gospodar njegov, pa mu oprostio i uputio.'),
(2471, 20, 123, '(Allah) reče: "Siđite iz njega oboje - svi! Vi ste jedno drugom neprijatelj." Pa ako vam dođe od Mene Uputa, pa ko bude slijedio Uputu Moju, tad neće zalutati, niti se unesrećiti.'),
(2472, 20, 124, 'A ko se okrene od Moje Opomene, pa uistinu će on imati život tjeskoban, a sakupićemo ga na Dan kijameta slijepog.'),
(2473, 20, 125, 'Reći će: "Gospodaru moj! Zašto si me sakupio slijepog, a već sam bio onaj koji vidi?"'),
(2474, 20, 126, '(Allah) će reći: "Tako. Dolazili su ti znakovi Naši, pa si ih zaboravio, i isto tako ćeš Danas biti zaboravljen!"'),
(2475, 20, 127, 'A tako ćemo platiti onoga ko pretjera i ne bude vjerovao u ajete Gospodara svog; a sigurno je kazna Ahireta žešća i trajnija.'),
(2476, 20, 128, 'Pa zar ih ne upućuje (to) koliko smo prije njih uništili naraštaja - hodaju po naseljima njihovim? Uistinu, u tome su znaci za posjednike razuma.'),
(2477, 20, 129, 'A da nije Riječi prethodne od Gospodara tvog i roka određenog, sigurno bi bila nužna (kazna).'),
(2478, 20, 130, 'Zato se strpi nad onim šta govore i slavi sa hvalom Gospodara svog prije izlaska Sunca i prije zalaska njegovog, i neko vrijeme noći - pa slavi (Ga) i krajevima dana, da bi ti bio zadovoljan.'),
(2479, 20, 131, 'I nikako ne upravljaj oči svoje prema onom šta smo dali da uživaju parovi od njih - sjaju života Dunjaa - da bismo ih time iskušali. A opskrba Gospodara tvoga je bolja i trajnija.'),
(2480, 20, 132, 'I naređuj porodici svojoj salat, i istraj na tome. Ne pitamo te za opskrbu: Mi te opskrbljujemo; a ishod je za bogobojaznost.'),
(2481, 20, 133, 'I govore: "Zašto nam ne donese znak od Gospodara svog?" Zar im nije došao dokaz onoga šta je u listovima pređašnjim?'),
(2482, 20, 134, 'A da smo ih Mi uništili kaznom prije njega, sigurno bi rekli: "Gospodaru naš! Zašto nam nisi poslao poslanika, pa da slijedimo ajete Tvoje, prije no što smo poniženi i osramoćeni?"'),
(2483, 20, 135, 'Reci: "Svako je čekalac, zato čekajte! Pa saznaćete ko su sljedbenici ravnog puta i ko se upućuje."');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Približava se ljudima obračun njihov, a oni u bezbrižnosti odvraćeni.'),
(2485, 21, 2, 'Nije im došla nijedna nova opomena od Gospodara njihovog, a da je nisu slušali i oni se zabavljali,'),
(2486, 21, 3, 'Zauzetih srca svojih. A oni koji čine zulm kriju sašaptavanje: "Je li ovo samo smrtnik sličan vama? Pa zar ćete ići sihru, a vi vidite?"'),
(2487, 21, 4, '(Muhammed) reče: "Gospodar moj zna govor na nebu i Zemlji, a On je Onaj koji čuje, Znalac."'),
(2488, 21, 5, 'Naprotiv, govore: "Zbrka od snova, štaviše izmišlja ga, štaviše on je pjesnik. Pa neka nam donese znak kao što su slati raniji."'),
(2489, 21, 6, 'Nije prije njih vjerovao nijedan grad kojeg smo uništili. Pa zar će oni vjerovati?'),
(2490, 21, 7, 'A prije tebe smo slali samo ljude kojima smo objavljivali, ta pitajte sljedbenike Opomene, ako ne znate.'),
(2491, 21, 8, 'I nismo ih učinili tijelima (koja) ne jedu hranu, i nisu bili besmrtnici.'),
(2492, 21, 9, 'Zatim smo im obistinili obećanje, te spasavali njih i koga smo htjeli, a uništavali razuzdane.'),
(2493, 21, 10, 'Doista, objavili smo vam Knjigu, u njoj je zikr vaš. Pa zar ne razumijete?'),
(2494, 21, 11, 'A koliko smo gradova smrskali koji su bili zulumčarski i stvorili poslije njih narode druge?'),
(2495, 21, 12, 'Pa pošto bi osjetili silu Našu, gle! oni iz njih bježe.'),
(2496, 21, 13, '"Ne bježite i vratite se onom šta vas je raskalašilo u njemu i nastambama vašim, da biste bili pitani."'),
(2497, 21, 14, 'Rekli bi: "O teško nama! Uistinu, mi smo bili zalimi."'),
(2498, 21, 15, 'Pa nije prestajalo to njihovo zazivanje dok ih ne bismo učinili pokošenim, ugaslim.'),
(2499, 21, 16, 'I nismo stvorili nebo i Zemlju i šta je između njih za igru.'),
(2500, 21, 17, 'Da smo željeli da uzmemo zabavu, sigurno bismo to uzeli od Nas - nismo bili (toga) činitelji.'),
(2501, 21, 18, 'Naprotiv, bacamo Istinu na neistinu, pa je poništava, tad gle: ona nestala. A jao vama zbog onog šta pripisujete.'),
(2502, 21, 19, 'I Njegov je ko je na nebesima i Zemlji. A ko je kod Njega, ne ohole se prema obožavanju Njegovom, niti umaraju;'),
(2503, 21, 20, 'Slave (Ga) noću i danju, ne malaksavaju.'),
(2504, 21, 21, 'Zar uzimaju bogove od Zemlje koji proživljuju?'),
(2505, 21, 22, 'Da ima na njima bogova osim Allaha, sigurno bi bili u neredu. Pa slava neka je Allahu, Gospodaru Arša, od onog šta pripisuju!'),
(2506, 21, 23, 'Neće biti pitan za ono što čini, a oni će biti pitani.'),
(2507, 21, 24, 'Zar mimo Njega uzimaju bogove? Reci: "Donesite dokaz vaš." Ovo je opomena onom ko je sa mnom i opomena onog ko je bio prije mene." Naprotiv, većina njih ne zna Istinu, pa su oni odvraćeni.'),
(2508, 21, 25, 'I nismo prije tebe poslali nijednog poslanika, a da mu nismo objavili: "Nema boga osim Mene", zato Mene obožavajte.'),
(2509, 21, 26, 'I govore: "Milostivi je uzeo dijete." Slava Njemu! Naprotiv, robovi su (to) cijenjeni,'),
(2510, 21, 27, 'Ne pretiču Ga govorom, i oni po naredbi Njegovoj rade.'),
(2511, 21, 28, 'Zna šta je pred njima i šta je iza njih. I neće (oni) posredovati, izuzev za onog s kim bude zadovoljan. A oni od straha pred Njim strepe.'),
(2512, 21, 29, 'A ko od njih kaže: "Uistinu, ja sam bog pored Njega", pa tog takvog ćemo platiti Džehennemom. Tako plaćamo zalime.'),
(2513, 21, 30, 'Zar oni koji ne vjeruju ne vide da su nebesa i Zemlja bili zatvorena (cjelina), pa smo ih rastrgali. A od vode smo načinili svaku živu stvar. Pa zar neće vjerovati?'),
(2514, 21, 31, 'I načinili smo na Zemlji stabilne planine, da se njima ne potresa, i načinili na njoj široke puteve, da bi se oni uputili.'),
(2515, 21, 32, 'I učinili smo nebo svodom zaštićenim; a oni su od znakova njegovih odvraćeni.'),
(2516, 21, 33, 'I On je Taj koji je stvorio noć i dan i Sunce i Mjesec: Svakoje u orbiti plovi.'),
(2517, 21, 34, 'I nismo dali smrtniku prije tebe besmrtnost. Pa hoće li, ako umreš, tad oni biti besmrtni?'),
(2518, 21, 35, 'Svakoj duši (predstoji) kušanje smrti, a iskušavamo vas zlom i dobrom kušnjom, i Nama ćete biti povraćeni.'),
(2519, 21, 36, 'A kad te vide oni koji ne vjeruju, samo te uzimaju ruglom: "Je li ovo onaj koji spominje bogove vaše?" A oni su na spomen Milostivog nevjernici.'),
(2520, 21, 37, 'Čovjek je stvoren od žurbe. Pokazat ću vam znakove Svoje, zato Me ne požurujte.'),
(2521, 21, 38, 'I govore: "Kad će ovo obećanje, ako istinu govorite?"'),
(2522, 21, 39, 'Da znaju oni koji ne vjeruju (vrijeme) kad neće od lica svojih otkloniti vatru, niti s leđa svojih, niti će oni biti pomognuti...'),
(2523, 21, 40, 'Naprotiv, doći će im iznenada, pa će ih preneraziti. Tad to neće moći odbiti, niti će im se odložiti.'),
(2524, 21, 41, 'A doista se izrugivalo poslanicima prije tebe, pa je one od njih koji su se rugali, pogodilo ono čemu su se rugali.'),
(2525, 21, 42, 'Reci: "Ko vas štiti noću i danju od Milostivog?" Naprotiv, oni su od spominjanja Gospodara svog odvraćeni!'),
(2526, 21, 43, 'Zar oni imaju bogove koji će ih zaštititi mimo Nas? Neće moći pomoći dušama svojim, niti će oni od Nas biti odbranjeni.'),
(2527, 21, 44, 'Naprotiv, dali smo da uživaju ovi i očevi njihovi, dok im nije odužio život. Pa zar ne vide da Mi dolazimo Zemlji, umanjujući je s krajeva njenih? Pa zar će oni biti pobjednici?'),
(2528, 21, 45, 'Reci: "Samo vas opominjem Objavom." A gluhi ne čuju poziv, kad budu opomenuti.'),
(2529, 21, 46, 'A da ih dotakne dašak kazne Gospodara tvog, sigurno bi rekli: "O teško nama! Uistinu, mi smo bili zalimi."'),
(2530, 21, 47, 'I postavićemo mjerila pravde za Dan kijameta, pa se duši neće učiniti zulm nimalo. I ako bude težine zrnca gorušice, iznijet ćemo ga. I dovoljni smo Mi Obračunci.'),
(2531, 21, 48, 'I doista smo dali Musau i Harunu furkan i svjetlo, i Opomenu za bogobojazne,'),
(2532, 21, 49, 'One koji se boje Gospodara svog tajno, a oni su zbog Časa brižni.'),
(2533, 21, 50, 'A ovo je Opomena blagoslovljena, spustili smo je (na zemlju); pa zar ćete vi biti njeni poricatelji?'),
(2534, 21, 51, 'I doista smo Ibrahimu dali ispravnost njegovu ranije i bili smo o njemu Znalci.'),
(2535, 21, 52, 'Kad reče ocu svom i narodu svom: "Šta su ovi kipovi, ovi kojima ste vi privrženi?"'),
(2536, 21, 53, 'Rekoše: "Našli smo očeve svoje, njihove obožavatelje."'),
(2537, 21, 54, 'Reče: "Doista ste vi i očevi vaši bili u zabludi očitoj."'),
(2538, 21, 55, 'Rekoše: "Jesi li nam donio Istinu, ili si ti od zabavljača?"'),
(2539, 21, 56, 'Reče: "Naprotiv, Gospodar vaš je Gospodar nebesa i Zemlje, Onaj koji ih je stvorio; a ja sam za to od svjedoka.'),
(2540, 21, 57, 'I tako mi Allaha, sigurno ću splanirati protiv idola vaših, nakon što se udaljite okrenuvši leđa."'),
(2541, 21, 58, 'Tad ih je učinio komadićima, izuzev velikog njihovog, da bi se njemu vratili.'),
(2542, 21, 59, 'Rekoše: "Ko ovo učini bogovima našim? Uistinu, on je od zalima."'),
(2543, 21, 60, 'Rekoše: "Čuli smo mladića, spominje ih; on se zove Ibrahim."'),
(2544, 21, 61, 'Rekoše: "Pa dajte ga na oči ljudima da oni svjedoče."'),
(2545, 21, 62, 'Rekoše: "Jesi li ti ovo učinio bogovima našim o Ibrahime?"'),
(2546, 21, 63, 'Reče: "Naprotiv, učinio je to najveći njihov, ovaj, ta pitajte ih ako mogu da govore."'),
(2547, 21, 64, 'Tad se povratiše dušama svojim, pa rekoše: "Uistinu, vi, vi ste zalimi."'),
(2548, 21, 65, 'Zatim oboriše glave svoje: "Doista znaš, ovi ne govore."'),
(2549, 21, 66, '(Ibrahim) reče: "Pa zar obožavate mimo Allaha ono šta vam nimalo ne koristi, niti vam šteti?'),
(2550, 21, 67, 'Jao vama i onom što obožavate mimo Allaha! Pa zar ne shvatate?"'),
(2551, 21, 68, 'Rekoše: "Spalite ga i pomozite bogovima svojim, ako ste radini."'),
(2552, 21, 69, 'Rekosmo: "O vatro, budi hladnoća, i spas Ibrahimu!"'),
(2553, 21, 70, 'I željeli su mu varku, pa smo njih učinili najvećim gubitnicima,'),
(2554, 21, 71, 'I spasili njega i Luta u zemlju koju smo blagoslovili svjetovima.'),
(2555, 21, 72, 'I podarili smo mu Ishaka, i Jakuba (kao) unuka. A svakojeg smo učinili od dobrih.'),
(2556, 21, 73, 'I učinili smo ih imamima, (da) upućuju po naredbi Našoj, i objavili im činjenje dobrih djela, i obavljanje salata i davanje zekata; i bili su Naši obožavatelji.'),
(2557, 21, 74, 'A Lutu - dali smo mu sud i znanje i izbavili ga iz naselja koje je činilo gadosti. Uistinu, oni su bili narod zao, grješnici;'),
(2558, 21, 75, 'I dali smo da on uđe u milost Našu. Uistinu, on je bio od dobrih.'),
(2559, 21, 76, 'I Nuhu - kad je pozivao ranije, pa smo mu se odazvali, i spasili njega i porodicu njegovu od jada golemog.'),
(2560, 21, 77, 'I pomogli ga protiv ljudi koji su poricali ajete Naše. Uistinu, oni su bili narod zao, zato smo ih sve potopili.'),
(2561, 21, 78, 'I Dawudu i Sulejmanu - kad su njih dvojica sudili o usjevu, kad se u njemu noću napasao brav narodni; a bili smo suđenju njihovom Svjedoci.'),
(2562, 21, 79, 'Tad smo učinili da to razumije Sulejman; i svakojem smo dali sud i znanje. I potčinili smo brda (da) sa Dawudom slave (Allaha) i ptice; a bili smo Izvršioci.'),
(2563, 21, 80, 'I naučili smo ga proizvodnji pancira za vas, da vas zaštite od sile vaše. Pa jeste li vi zahvalni?'),
(2564, 21, 81, 'I (potčinili smo) Sulejmanu vjetar silan - puhao je po naredbi njegovoj prema onoj zemlji koju smo blagoslovili. A o svakoj stvari smo Znalci;'),
(2565, 21, 82, 'I neke šejtane koji su ronili za njega i radili posao pored toga, a bili smo njihovi Čuvari.'),
(2566, 21, 83, 'I Ejjubu - kad zovnu Gospodara svog: "Dotakla me je nevolja, a Ti si Najmilosrdniji od milosrdnih."'),
(2567, 21, 84, 'Tad smo mu se odazvali i otklonili šta je s njim bilo od nevolje. I dali smo mu porodicu njegovu, i slične njima uz njih milošću od Nas, i Opomenu za obožavatelje;'),
(2568, 21, 85, 'I Ismailu i Idrisu i Zul-kiflu; svaki je bio od strpljivih.'),
(2569, 21, 86, 'I dali smo da oni uđu u milost Našu. Uistinu, oni su od dobrih.'),
(2570, 21, 87, 'I Zun-nunu, kad ode srdit, tad je mislio da nećemo imati nad njim moć - pa je dozivao u tminama: "Nema boga osim Tebe, slava neka je Tebi! Uistinu, ja sam od zalima."'),
(2571, 21, 88, 'Tad smo mu se odazvali i izbavili ga iz jada. A tako spašavamo vjernike.'),
(2572, 21, 89, 'I Zekerijjaa - kad zovnu Gospodara svog: "Gospodaru moj! Ne ostavljaj me samog, a Ti si Najbolji od nasljednika."'),
(2573, 21, 90, 'Tad smo mu se odazvali i darovali mu Jahjaa, i osposobili mu ženu njegovu. Uistinu, oni su se natjecali u dobrim djelima i zazivali Nas dragovoljno i iz straha, i bili su Nama ponizni.'),
(2574, 21, 91, 'I onu koja je sačuvala ferdž svoj, pa smo udahli u nju od Duha Našeg, i učinili nju i sina njenog znakom za svjetove.'),
(2575, 21, 92, 'Uistinu, ova vaša umma, je umma jedna, a Ja sam Gospodar vaš, zato Mene obožavajte.'),
(2576, 21, 93, 'I rastrgali su stvar svoju između sebe - (a) svi su Nama povratnici.'),
(2577, 21, 94, 'Pa ko radi dobra djela, a on je vjernik, tad nema poricanja truda njegovog. A uistinu, Mi smo njegovi Pisari.'),
(2578, 21, 95, 'I zabrana je za naselje koje smo uništili, da se oni neće povratiti.'),
(2579, 21, 96, 'Dok, kad se pusti Jedžudž i Medžudž - a oni će se sa svake uzvisine spuštati -'),
(2580, 21, 97, 'I približi se obećanje istinito, tad gle: ukočeni pogledi onih koji nisu vjerovali. "O teško nama! Doista smo bili u nemaru prema ovom, štaviše, bili smo zalimi."'),
(2581, 21, 98, 'Uistinu! Vi i ono što ste obožavali mimo Allaha, bićete gorivo Džehennema; vi ćete biti ti koji ćete mu doći.'),
(2582, 21, 99, 'Da su ovi bili bogovi, ne bi mu došli, a svi će u njemu biti vječito.'),
(2583, 21, 100, 'Imaće oni u njemu stenjanje i oni u njemu neće čuti.'),
(2584, 21, 101, 'Uistinu, oni kojima je od Nas prethodilo najljepše - takvi će od njega biti otklonjeni.'),
(2585, 21, 102, 'Neće čuti ni najmanji šum njegov i oni će u onom šta zažele duše njihove vječno biti.'),
(2586, 21, 103, 'Neće ih brinuti strah najveći, a susretaće ih meleci: "Ovo je vaš Dan koji vam se obećavao":'),
(2587, 21, 104, 'Dan kad savijemo nebo kao motanjem svitka za knjige. Kao što smo počeli prvo stvaranje, ponovićemo ga. Obećanje je na Nama. Uistinu, Mi ćemo biti Izvršitelji.'),
(2588, 21, 105, 'A doista smo zapisali u Zeburu nakon Opomene, da će Zemlju naslijediti robovi Moji dobri.'),
(2589, 21, 106, 'Uistinu, u ovom je poruka za narod obožavatelja.'),
(2590, 21, 107, 'I poslali smo te samo (kao) milost svjetovima.'),
(2591, 21, 108, 'Reci: "Samo mi se objavljuje da je Bog vaš Bog Jedini! Pa jeste li vi muslimani?"'),
(2592, 21, 109, 'Pa ako se odvrate, tad reci: "Upozorio sam vas jednako, a ne znam da li je blisko ili daleko ono čime vam se prijeti.'),
(2593, 21, 110, 'Uistinu! On zna javni govor i zna šta krijete.'),
(2594, 21, 111, 'I ne znam: možda je to iskušenje za vas i uživanje do nekog vremena."'),
(2595, 21, 112, '(Muhammed) reče: "Gospodaru moj! Presudi s Istinom" - a Gospodar naš je Milostivi, Onaj od kog se traži pomoć protiv onog što pripisujete.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'O ljudi! Bojte se Gospodara svog. Uistinu, potres Časa (sudnjeg) bit će stvar strahovita!'),
(2597, 22, 2, 'Na Dan kad ga vidite, zaboraviće svaka dojilja šta je zadojila i pobaciće svaka trudnica plod svoj, i vidjećeš ljude pijane: ali neće oni biti pijani, nego će kazna Allahova biti žestoka.'),
(2598, 22, 3, 'A između ljudi je ko raspravlja o Allahu bez znanja, i slijedi svakog šejtana buntovnika.'),
(2599, 22, 4, 'Njemu je propisano - da će onaj ko ga uzme za prijatelja - ta da će ga on zavesti i uputiti ga kazni seira.'),
(2600, 22, 5, 'O ljudi! Ako ste u sumnji o podizanju (mrtvih), pa uistinu, Mi smo vas stvorili od prašine, zatim od kaplje sjemena, zatim od zakvačka, zatim od grude mesa oblikovane i neoblikovane, da bismo vam objasnili. A dajemo da u matericama ostane šta hoćemo, do roka određenog. Zatim vas izvodimo (kao) dojenče, potom da dostignete svoju punu snagu. I između vas je ko bude uzet, i od vas je ko bude vraćen dubokoj starosti, da ne zna - nakon znanja - ništa. I vidiš zemlju beživotnu, pa kad spustimo na nju vodu, ustreperi i uzbuja, i iznikne svaki par prekrasni.'),
(2601, 22, 6, 'To zato što je Allah, On Istina, i što On oživljava mrtve i što On nad svakom stvari ima moć;'),
(2602, 22, 7, 'I što će Čas doći - nema sumnje u to - i što će Allah podići onog ko je u kaburima.'),
(2603, 22, 8, 'I od ljudi je ko raspravlja o Allahu bez znanja i bez Upute i bez Knjige rasvjetljavajuće,'),
(2604, 22, 9, 'Savijajući bok svoj, da zavede s puta Allahovog. Imaće on na Dunjau poniženje, a daćemo da on na Dan kijameta iskusi kaznu gorenjem.'),
(2605, 22, 10, 'To za ono što su unaprijed poslale ruke tvoje i što Allah nije nikako zalim robovima.'),
(2606, 22, 11, 'I od ljudi je ko obožava Allaha na ivici; pa ako ga zadesi dobro, smiri se u njemu, a ako ga zadesi iskušenje, okreće lice svoje. Gubi Dunja i Ahiret. To je taj gubitak očiti.'),
(2607, 22, 12, 'Priziva mimo Allaha ono šta mu ne šteti, niti mu koristi. To je to, zabluda daleka.'),
(2608, 22, 13, 'Priziva onog čija je šteta bliža od koristi njegove. Zaista je loš zaštitnik i zaista loš drug!'),
(2609, 22, 14, 'Uistinu! Allah će dati da oni koji vjeruju i rade dobra djela uđu (u) bašče ispod kojih teku rijeke. Uistinu! Allah čini šta želi.'),
(2610, 22, 15, 'Ko misli da ga neće pomoći Allah na Dunjau i Ahiretu, pa neka rastegne uže ka nebu, zatim neka presječe, pa nek vidi hoće li doista varka njegova otkloniti ono šta (ga) rasrđuje.'),
(2611, 22, 16, 'I tako ga objavljujemo (kao) ajete jasne, i da Allah uputi koga želi.'),
(2612, 22, 17, 'Uistinu, oni koji vjeruju i oni koji su jevreji i Sabijci i kršćani i vatropoklonici i oni koji pridružuju - Uistinu! Allah će presuditi među njima na Dan kijameta. Uistinu, Allah je nad svakom stvari Svjedok.'),
(2613, 22, 18, 'Zar nisi vidio da se Allahu - Njemu potčinjava ko je u nebesima i ko je na Zemlji, i Sunce i Mjesec i zvijezde i planine, i drveće i životinje i mnoštvo ljudi? A mnoštvo - obistiniće se na njemu kazna. A koga ponizi Allah, tad mu nema nikoga ko bi mu čast ukazao. Uistinu! Allah čini što hoće!'),
(2614, 22, 19, 'Ovo su dvojica protivnika, spore se o Gospodaru svom. Pa onima koji ne vjeruju - odsjeći će im se odjeća od vatre; izlivaće se nad glavama njihovim ključala voda.'),
(2615, 22, 20, 'Rastapat će se njome ono šta je u trbusima njihovim, i kože;'),
(2616, 22, 21, 'A za njih su maljevi od gvožđa.'),
(2617, 22, 22, 'Kad god zažele da iz nje izađu zbog jada, biće u nju vraćeni: "Kušajte kaznu gorenjem!"'),
(2618, 22, 23, 'Uistinu! Allah će dati da oni koji vjeruju i rade dobra djela uđu u bašče ispod kojih teku rijeke, kitiće se u njima narukvicama od zlata i bisera, a odjeća njihova će u njima biti svila.'),
(2619, 22, 24, 'I upućeni su lijepoj riječi, i upućeni su putu Hvaljenog.'),
(2620, 22, 25, 'Uistinu! Oni koji ne vjeruju i odvraćaju od puta Allahovog i Mesdžidul-harama - onog kojeg smo ljudima učinili jednakog, mještaninu u njemu i posjetiocu. A ko u njemu zaželi ateizam zulmom - daćemo da on kuša od kazne bolne.'),
(2621, 22, 26, 'I kad smo Ibrahimu odredili mjesto Kuće: "Ne pridružuj Meni ništa, i očisti Kuću Moju za činitelje tawafa i one na kijamu i one koji se pregibaju i čine sedždu;'),
(2622, 22, 27, 'I proglasi među ljudima hadždž: dolaziće ti pješke i na svakoj mršavoj devi; dolaziće sa svakog širokog puta dubokog,'),
(2623, 22, 28, 'Da svjedoče dobit za sebe, i spominju ime Allahovo u danima poznatim, nad onim čim ih je opskrbio od životinje - stoke. Pa jedite od nje i nahranite jadnu fukaru;'),
(2624, 22, 29, 'Zatim da okončaju zaprljanost svoju, i ispune zavjete svoje, i obilaze Kuću drevnu.'),
(2625, 22, 30, 'To; a ko uvećava svetosti Allahove - pa to mu je bolje kod Gospodara njegovog. A dozvoljena vam je stoka, izuzev šta vam je recitirano, zato se klonite prljavštine idola, i klonite se govora lažnog,'),
(2626, 22, 31, 'Hanife budite Allahu, ne budite oni koji Mu pridružuju. A ko pridružuje Allahu - pa kao da je pao s neba te ga zgrabila ptica ili ga vjetar otpuhao u mjesto daleko.'),
(2627, 22, 32, 'To. A ko veliča znakove Allahove, pa uistinu, to je od bogobojaznosti srca.'),
(2628, 22, 33, 'Vi u njima imate koristi do roka određenog; zatim je mjesto njihovo do Kuće drevne.'),
(2629, 22, 34, 'A svakom ummetu smo načinili obrede, da spominju ime Allahovo nad onim čim ih je opskrbio od životinje - stoke. Ta Bog vaš je Bog Jedini, zato se Njemu predajte. I obraduj ponizne,'),
(2630, 22, 35, 'One čija se - kad se Allah spomene - srca uplaše, i strpljive nad onim šta ih zadesi, i one koji obavljaju salat i iz onog čim smo ih opskrbili udjeljuju.'),
(2631, 22, 36, 'I budn: učinili smo vam ih od znakova Allahovih, imate vi u njima dobro. Zato spominjite ime Allahovo nad njima poredanim. Pa kad padnu bokovi njihovi, pa jedite od njih i nahranite zadovoljnog i prosjaka. Tako smo vam ih potčinili, da biste vi zahvaljivali.'),
(2632, 22, 37, 'Neće Allahu stići mesa njihova, niti krvi njihove, nego će Mu stići bogobojaznost od vas. Tako vam ih je potčinio, da biste veličali Allaha što vas je uputio; a obraduj dobročinitelje!'),
(2633, 22, 38, 'Uistinu! Allah odbija (neprijatelje) od onih koji vjeruju. Uistinu! Allah ne voli nijednog izdajnika, nezahvalnika.'),
(2634, 22, 39, 'Dozvoljeno je onima protiv kojih se bori, jer im je učinjen zulm; a uistinu je Allah za pomoć njima kadar;'),
(2635, 22, 40, 'Onima koji su protjerani iz kuća njihovih bez prava, samo što su govorili: "Gospodar naš je Allah." I da nije Allahovog suzbijanja ljudi - njih jednih drugima - sigurno bi bili porušeni manastiri i crkve i sinagoge i mesdžidi - u kojima se mnogo spominje ime Allahovo. A Allah će sigurno pomoći onog ko Njega pomaže. Uistinu, Allah je Silni, Svemoćni,'),
(2636, 22, 41, 'One koji, ako ih učvrstimo na Zemlji, obavljaju salat i daju zekat, i naređuju dobro, a odvraćaju od nevaljalog. A Allahov je konac stvari.'),
(2637, 22, 42, 'A ako te poriču - pa doista je prije njih poricao narod Nuhov, i Ad i Semud;'),
(2638, 22, 43, 'I narod Ibrahimov i narod Lutov;'),
(2639, 22, 44, 'I stanovnici Medjena. I porečen je Musa; tad sam puštao nevjernicima, potom ih ščepavao. Pa kakvo je bilo odbijanje Moje!'),
(2640, 22, 45, 'Pa koliko je gradova koje smo uništili, a oni su bili zulumčarski, pa su bila pusti na krovovima svojim, i bunarom napuštenim i dvorcem podignutim!'),
(2641, 22, 46, 'Pa zar nisu putovali po Zemlji te oni imali srca da njima shvate, ili uši da njima čuju? Pa uistinu, to nisu slijepi vidovi, nego su slijepa srca koja su u grudima.'),
(2642, 22, 47, 'I požuruju te s kaznom. A neće Allah prekršiti obećanje Svoje. I uistinu, dan je kod Gospodara tvog kao hiljadu godina od onog što računate.'),
(2643, 22, 48, 'A koliko je gradova kojima sam pustio, a oni su bili zulumčarski, zatim bih ih dograbio, a Meni je dolazište!'),
(2644, 22, 49, 'Reci: "O ljudi, ja sam vam samo opominjač jasni."'),
(2645, 22, 50, 'Pa koji vjeruju i rade dobra djela, imaće oni oprost i opskrbu plemenitu.'),
(2646, 22, 51, 'A oni koji nastoje da ajete Naše osujete, takvi će biti stanovnici džehima.'),
(2647, 22, 52, 'I nismo prije tebe poslali nijednog poslanika niti vjerovjesnika, a da nije - kad imadne želju - ubacio šejtan u želju njegovu. Tad Allah ukine šta šejtan ubaci, zatim učvrsti Allah ajete Svoje. A Allah je Znalac, Mudri,'),
(2648, 22, 53, 'Da bi ono što je ubacio šejtan učinio iskušenjem za one u srcima čijim je bolest i one čija su srca tvrda. A uistinu, zalimi su u razdoru dalekom,'),
(2649, 22, 54, 'I da znaju oni kojima je dato znanje, da je on Istina od Gospodara tvog, te da vjeruju u njega, te da mu se skruše srca njihova. A uistinu, Allah je Vodič onih koji vjeruju, putu pravom.'),
(2650, 22, 55, 'A oni koji ne vjeruju, ne prestaju sa sumnjom u njega dok im Čas ne dođe iznenada, ili im dođe kazna Dana jalovog.'),
(2651, 22, 56, 'Vlast će Tog dana biti Allahova. Sudit će među njima. Tad će oni koji su vjerovali i radili dobra djela biti u baščama uživanja.'),
(2652, 22, 57, 'A oni koji nisu vjerovali i poricali ajete Naše, pa ti takvi će imati kaznu prezrenu.'),
(2653, 22, 58, 'A oni koji su izbjegli na putu Allahovom, zatim bili ubijeni ili umrli, sigurno će ih opskrbiti Allah opskrbom dobrom. A uistinu! Allah, On je Najbolji od opskrbilaca.'),
(2654, 22, 59, 'Sigurno će ih uvesti ulazom kojim će zadovoljni biti; a uistinu, Allah je Znalac, Blagi.'),
(2655, 22, 60, 'To. A ko se osveti sličnim onom čim je kažnjen, zatim se njemu učini nasilje, sigurno će ga Allah pomoći. Uistinu! Allah je Brisalac (grijeha), Oprosnik.'),
(2656, 22, 61, 'To zato što Allah daje da noć uđe u dan, i daje da dan uđe u noć, i što je Allah Onaj koji čuje, Onaj koji vidi.'),
(2657, 22, 62, 'To zato što je Allah - On Istina, a ono šta prizivaju mimo Njega, to neistina, i što je Allah - On Uzvišeni, Veliki.'),
(2658, 22, 63, 'Zar ne vidiš da Allah spušta s neba vodu, pa postane zemlja zelena. Uistinu! Allah je Fini, Obaviješteni.'),
(2659, 22, 64, 'Njegovo je šta je u nebesima i šta je na Zemlji. A uistinu, Allah, On je Neovisni, Hvaljeni.'),
(2660, 22, 65, 'Zar ne vidiš da vam je Allah potčinio (sve) što je na Zemlji i plovilo (koje) plovi morem po odredbi Njegovoj? I zadržava nebo da ne padne na Zemlju, osim s dozvolom Njegovom. Uistinu! Allah je ljudima Samilosni, Milosrdni.'),
(2661, 22, 66, 'I On je Taj koji vas je oživio, zatim će vas umrtviti, potom će vas oživiti. Uistinu, čovjek je nezahvalnik.'),
(2662, 22, 67, 'Svakom ummetu smo načinili obred kojeg oni obavljaju, zato neka oni nikako ne raspravljaju s tobom o odredbi, a pozivaj Gospodaru svom. Uistinu, ti si na Uputi pravoj.'),
(2663, 22, 68, 'A ako se budu s tobom prepirali, tad reci: "Allah je Najbolji znalac onog šta radite."'),
(2664, 22, 69, 'Allah će presuditi među vama na Dan kijameta o onome u čemu se razilazite.'),
(2665, 22, 70, 'Zar ne znaš da Allah zna šta je na nebu i Zemlji? Uistinu, to je u Knjizi. Uistinu, to je Allahu lahko.'),
(2666, 22, 71, 'I obožavaju mimo Allaha ono za što nije objavio autoritet, i ono o čemu oni nemaju znanje; a neće zalimi imati nikakva pomagača.'),
(2667, 22, 72, 'A kad im se uče ajeti Naši jasni, raspoznaćeš na licima onih koji ne vjeruju, negiranje. Skoro da nasrnu na one koji im uče ajete Naše. Reci: "Pa hoćete li da vas obavijestim o gorem od toga? Vatra! Obećao ju je Allah onima koji ne vjeruju, a loše je dolazište!"'),
(2668, 22, 73, 'O ljudi! Navodi se primjer, zato ga poslušajte. Uistinu! Oni koje prizivate mimo Allaha, neće stvoriti mušicu, makar se skupili radi nje. A ako bi im mušica nešto ugrabila, ne bi to od nje izbavili. Slab je onaj ko traži, i ono šta se traži!'),
(2669, 22, 74, 'Ne cijene Allaha istinskim cijenjenjem Njegovim. Uistinu! Allah je Silni, Svemoćni.'),
(2670, 22, 75, 'Allah od meleka odabire poslanike i od ljudi. Uistinu, Allah je Onaj koji čuje, Onaj koji vidi.'),
(2671, 22, 76, 'Zna šta je ispred njih i šta je iza njih, a Allahu se vraćaju stvari.'),
(2672, 22, 77, 'O vi koji vjerujete! Činite ruku\' i padajte na sedždu i obožavajte Gospodara svog i činite dobro, da biste vi uspjeli.'),
(2673, 22, 78, 'I borite se radi Allaha, istinskim džihadom Njegovim. On vas je odabrao i nije vam u vjeri načinio nikakvu teškoću, milletu oca vašeg Ibrahima. On vas je nazvao muslimanima od ranije, i u ovom, da bi Poslanik bio svjedok protiv vas, i (vi) bili svjedoci protiv ljudi. Zato obavljajte salat i dajite zekat. I držite se Allaha! On je Zaštitnik vaš; pa divan li je Zaštitnik i divan Pomagač!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Doista, uspjet će vjernici,'),
(2675, 23, 2, 'Oni koji su u salatima svojim skrušeni,'),
(2676, 23, 3, 'I oni koji su od besmisla odvraćeni,'),
(2677, 23, 4, 'I oni koji su zekata davaoci;'),
(2678, 23, 5, 'I oni koji su furudža svojih čuvari,'),
(2679, 23, 6, 'Izuzev od žena svojih ili onog šta posjeduju desnice njihove - tad oni uistinu neće biti prekoreni.'),
(2680, 23, 7, 'Pa ko traži iza toga, pa ti takvi su prestupnici -'),
(2681, 23, 8, 'I onih koji su emaneta svojih i ugovora svojih pazitelji;'),
(2682, 23, 9, 'I onih koji su salata svojih čuvari;'),
(2683, 23, 10, 'Ti takvi su nasljednici,'),
(2684, 23, 11, 'Koji će naslijediti Firdews. Oni će u njemu biti vječito.'),
(2685, 23, 12, 'A doista smo čovjeka stvorili od ekstrakta gline,'),
(2686, 23, 13, 'Zatim ga smjestili (kao) kap sjemena u boravište čvrsto;'),
(2687, 23, 14, 'Potom kap sjemena stvorili zakvačkom, pa zakvačak stvorili grudom mesa, pa grudu mesa stvorili kostima, pa zaodjenuli kosti mesom, zatim ga sazdali stvorenjem drugim. Pa blagoslovljen neka je Allah, Najbolji od stvoritelja!'),
(2688, 23, 15, 'Zatim ćete uistinu vi, poslije toga biti mrtvaci!'),
(2689, 23, 16, 'Potom ćete uistinu vi na Dan kijameta biti podignuti!'),
(2690, 23, 17, 'A doista smo iznad vas stvorili sedam putanja, i nismo Mi prema stvaranju nehajni.'),
(2691, 23, 18, 'I spuštamo s neba vodu po mjeri, pa je smještamo u Zemlju. I uistinu, Mi smo za odvođenje njeno kadri.'),
(2692, 23, 19, 'Pa smo njome za vas sazdali vrtove palmi i grožđa, imate vi u njima mnogo voća i od njega jedete;'),
(2693, 23, 20, 'I drvo - niče iz Gore Sinajske, daje ulje i začin za one koji jedu.'),
(2694, 23, 21, 'A uistinu, imate vi u stoci ibret. Napajamo vas od onog što je u trbusima njihovim. I imate vi u njima koristi mnoge, i od njih jedete.'),
(2695, 23, 22, 'I na njima i plovilima se transportujete.'),
(2696, 23, 23, 'A doista smo poslali Nuha narodu njegovom, pa reče: "O narode moj! Obožavajte Allaha, nemate vi drugog boga sem Njega, pa zar se ne bojite?"'),
(2697, 23, 24, 'Tad velikani koji nisu vjerovali iz naroda njegovog rekoše: "Ovo je samo smrtnik sličan vama, želi da se istakne nad vama. A da je htio Allah, sigurno bi spustio meleke. Nismo za ovo čuli od naših očeva ranijih.'),
(2698, 23, 25, 'On je samo čovjek u kojem je ludost, zato ga promatrajte neko vrijeme."'),
(2699, 23, 26, '(Nuh) reče: "Gospodaru moj! Pomozi mi zato što me poriču."'),
(2700, 23, 27, 'Tad mu objavismo: "Pravi lađu pod očima Našim i objavi Našoj. Pa kad dođe naredba Naša i šikne peć, tad ukrcaj u nju od svake vrste dvoje, i porodicu svoju, izuzev onog od njih kome je prethodila riječ. I ne obraćaj mi se za one koji su zulm činili. Uistinu, oni će biti potopljeni.'),
(2701, 23, 28, 'Pa kad se smjestiš ti i ko je s tobom na lađu, tad reci: "Hvala neka je Allahu koji nas je spasio od naroda zalima.'),
(2702, 23, 29, 'I reci: \'Gospodaru moj! Spusti me na spustište blagoslovljeno, a Ti si od onih koji spuštaju, Najbolji."'),
(2703, 23, 30, 'Uistinu, u tome su znaci, i doista smo bili Iskušavatelji.'),
(2704, 23, 31, 'Zatim smo poslije njih stvorili pokoljenje drugih,'),
(2705, 23, 32, 'I među njih poslali poslanika od njih: "Obožavajte Allaha! Nemate vi drugog boga sem Njega. Pa zar se ne bojite?"'),
(2706, 23, 33, 'I uglednici iz naroda njegovog koji nisu vjerovali i poricali susret Ahireta, a raskalašili smo ih u životu Dunjaa, rekoše: "Ovo je samo smrtnik sličan vama, jede od onog šta jedete i pije od onog šta pijete.'),
(2707, 23, 34, 'A ako poslušate smrtnika sličnog vama, uistinu ste tad gubitnici.'),
(2708, 23, 35, 'Da li vam obećava da ćete vi kad umrete i budete prah i kosti - da ćete vi biti izvedeni?'),
(2709, 23, 36, 'Daleko je daleko šta vam se obećava!'),
(2710, 23, 37, 'To je samo naš život dunjalučki; umiremo i živimo, i nećemo mi biti podignuti!'),
(2711, 23, 38, 'On je samo čovjek, izmišlja o Allahu laž, a nećemo mi njemu biti vjernici."'),
(2712, 23, 39, 'Reče: "Gospodaru moj! Pomozi mi zato što me poriču."'),
(2713, 23, 40, '(Allah) reče: "Zakratko će sigurno postati pokajanici."'),
(2714, 23, 41, 'Pa je spopao njih krik s pravdom, te smo ih smetljem učinili. Pa daleko neka je narod zalima!'),
(2715, 23, 42, 'Zatim smo poslije njih stvorili pokoljenja druga.'),
(2716, 23, 43, 'Neće preteći nijedna umma rok svoj, niti će odložiti.'),
(2717, 23, 44, 'Zatim smo slali poslanike Naše redom. Kad god je ummetu došao poslanik njihov, porekli bi ga, pa smo činili da oni slijede jedni druge i činili ih pričama. Pa daleko bio narod (onih) koji ne vjeruju!'),
(2718, 23, 45, 'Zatim smo poslali Musaa i brata njegovog Haruna sa znakovima Našim i autoritetom očitim,'),
(2719, 23, 46, 'Faraonu i velikanima njegovim, pa su se uzoholili i postali narod nadmenih.'),
(2720, 23, 47, 'Pa rekoše: "Zar da vjerujemo dvojici smrtnika sličnih nama, a narod njih dvojice nama su robovi?"'),
(2721, 23, 48, 'Pa su porekli njih dvojicu i bili od uništenih.'),
(2722, 23, 49, 'I doista, dali smo Musau Knjigu, da bi se oni uputili.'),
(2723, 23, 50, 'I učinili smo sina Merjeminog i majku njegovu, znakom, i sklonili njih dvoje na brežuljak sa boravištem i izvorištem.'),
(2724, 23, 51, 'O poslanici! Jedite od dobrih stvari i činite dobro. Uistinu! Ja sam o onom šta radite Znalac.'),
(2725, 23, 52, 'I uistinu, ova umma vaša, je umma jedna! - a Ja sam Gospodar vaš, zato se bojte Mene.'),
(2726, 23, 53, 'Pa su rastrgali stvar svoju na komade. Svaka se stranka raduje s onim šta je kod njih.'),
(2727, 23, 54, 'Zato ih ostavi u zbunjenosti njihovoj neko vrijeme.'),
(2728, 23, 55, 'Misle li da tim što im pružamo od imetka i sinova -'),
(2729, 23, 56, 'Žurimo njima u dobrim djelima? Naprotiv, ne opažaju!'),
(2730, 23, 57, 'Uistinu, oni koji su iz bojazni prema Gospodaru svom brižni,'),
(2731, 23, 58, 'I oni koji u ajete Gospodara svog vjeruju,'),
(2732, 23, 59, 'I oni koji Gospodaru svom ne pridružuju,'),
(2733, 23, 60, 'I oni koji daju šta daju - a njihova srca puna straha što će oni Gospodaru svom biti povratnici -'),
(2734, 23, 61, 'Takvi žure u dobrim djelima, i oni su u njima prethodnici.'),
(2735, 23, 62, 'I ne opterećujemo dušu, osim mogućnošću njenom. A kod Nas je Knjiga, govori Istinu, i njima se neće učiniti zulm.'),
(2736, 23, 63, 'Naprotiv, srca njihova su u zbunjenosti o ovom i oni imaju poslove mimo toga koje oni rade.'),
(2737, 23, 64, 'Dok, kad raskalašene njihove kaznimo kaznom, gle: oni će kukati.'),
(2738, 23, 65, 'Ne kukajte Danas! Uistinu, vi od Nas nećete biti pomognuti.'),
(2739, 23, 66, 'Doista, bili su vam ajeti Moji učeni, pa ste na petama svojim odstupali,'),
(2740, 23, 67, 'Oholi; o njemu ste noću naklapali.'),
(2741, 23, 68, 'Pa zašto ne razmisle o Riječi? Zar im je došlo šta nije dato očevima njihovim pređašnjim?'),
(2742, 23, 69, 'Zar ne prepoznaju poslanika svog, pa su oni poricatelji njegovi?'),
(2743, 23, 70, 'Zar govore: "U njemu je ludilo?" Naprotiv, donio im je Istinu; a većina njih su mrzitelji Istine.'),
(2744, 23, 71, 'A ako bi Istina slijedila strasti njihove, sigurno bi došlo do fesada nebesa i Zemlje i onog ko je u njima. Naprotiv, donijeli smo im Opomenu njihovu, pa su oni od Opomene svoje odvraćeni.'),
(2745, 23, 72, 'Zar od njih tražiš nadoknadu? Pa naknada Gospodara tvog je najbolja, i On je Najbolji od opskrbljivača.'),
(2746, 23, 73, 'A uistinu, ti ih pozivaš putu pravom.'),
(2747, 23, 74, 'I uistinu, oni koji ne vjeruju u Ahiret, s puta su skrenuli.'),
(2748, 23, 75, 'I kad bismo im se smilovali i otklonili šta je s njima od nevolje, sigurno bi ustrajali u pretjeranosti svojoj, lutajući.'),
(2749, 23, 76, 'I doista smo ih dograbili kaznom, pa se ne pokoravaju Gospodaru svom, niti se ponizuju.'),
(2750, 23, 77, 'Dok, kad im otvorimo kapiju kazne žestoke, gle: oni će u njoj biti očajnici.'),
(2751, 23, 78, 'A On je Taj koji vam je sazdao sluh, i vidove i srca. Malo što zahvaljujete!'),
(2752, 23, 79, 'I On je Taj koji vas je rasuo po Zemlji, i Njemu ćete biti sabrani.'),
(2753, 23, 80, 'I On je Taj koji oživljava i umrtvljuje i Njegova je izmjena noći i dana. Pa zar nećete shvatiti?'),
(2754, 23, 81, 'Naprotiv, govore slično šta su govorili raniji;'),
(2755, 23, 82, 'Govorili su: "Hoćemo li kad umremo i budemo prašina i kosti, uistinu mi biti podignuti?'),
(2756, 23, 83, 'Doista, obećano nam je ovo, nama i očevima našim od ranije. Ovo su samo priče ranijih."'),
(2757, 23, 84, 'Reci: "Čija je Zemlja i ko je na njoj, ako znate?"'),
(2758, 23, 85, 'Reći će: "Allahova." Reci: "Pa zar se nećete poučiti?"'),
(2759, 23, 86, 'Reci: "Ko je Gospodar nebesa sedmerih i Gospodar Arša veličanstvenog?"'),
(2760, 23, 87, 'Reći će: "Allahovo je!" Reci: "Pa zar se ne bojite?"'),
(2761, 23, 88, 'Reci: "Ko je taj u čijoj ruci je vlast svake stvari, i On štiti, a nema zaštite od Njega, ako znate?"'),
(2762, 23, 89, 'Reći će: "Allahovo je!" Reci: "Pa kako ste opčinjeni?"'),
(2763, 23, 90, 'Naprotiv, donijeli smo im Istinu, a uistinu, oni su lašci.'),
(2764, 23, 91, 'Nije uzeo Allah nikakvo dijete i nema uz Njega nikakva boga; tad bi sigurno svaki bog nestao s onim šta je stvorio, i sigurno bi se neki od njih uzdizali nad drugima! Slavljen neka je Allah od onog šta pripisuju,'),
(2765, 23, 92, 'Znalac nevidljivog i vidljivog! Pa uzvišen neka je od onog šta pridružuju!'),
(2766, 23, 93, 'Reci: "Gospodaru moj! Ako ćeš mi pokazati čime im se prijeti,'),
(2767, 23, 94, 'Gospodaru moj, tad me ne smjesti među narod zalima."'),
(2768, 23, 95, 'A uistinu, Mi da ti pokažemo čime im prijetimo - Moćni smo.'),
(2769, 23, 96, 'Odbij zlo s onim šta je najbolje. Mi smo Najbolji znalac onog šta pripisuju.'),
(2770, 23, 97, 'I reci: "Gospodaru moj! Tražim zaštitu u Tebe od poticanja šejtana,'),
(2771, 23, 98, 'I tražim zaštitu u Tebe Gospodaru moj, da uz mene ne prisustvuju."'),
(2772, 23, 99, 'Dok, kad jednom od njih dođe smrt, kaže: "Gospodaru moj! Povrati me,'),
(2773, 23, 100, 'Da bih ja radio dobro u onom šta sam ostavio!" Nikako! Uistinu, to je riječ, on je govornik njen; a ispred njih je berzeh do Dana kad će podignuti biti.'),
(2774, 23, 101, 'Pa kad se puhne u sur, tad Tog dana među njima neće biti srodstava, niti će se raspitivati.'),
(2775, 23, 102, 'Pa onaj čije mjere budu teške, pa ti takvi će biti uspješni.'),
(2776, 23, 103, 'A onaj čije mjere budu lahke, pa to su oni koji su izgubili duše svoje, u Džehennemu će vječno biti.'),
(2777, 23, 104, 'Vatra će pržiti lica njihova, a oni će u njoj biti iskeženi.'),
(2778, 23, 105, '"Zar vam nisu bili ajeti Moji učeni, pa ste ih poricali?"'),
(2779, 23, 106, 'Reći će: "Gospodaru naš! Savladala nas je nesreća naša i bili smo narod zabludjelih.'),
(2780, 23, 107, 'Gospodaru naš! Izvadi nas iz nje! Pa ako bismo se povratili, tad bismo mi uistinu bili zalimi."'),
(2781, 23, 108, '(Allah) će reći: "Gubite se u nju (pokunjeni), i ne govorite Mi."'),
(2782, 23, 109, 'Uistinu! Bila je skupina robova Mojih, govorili su: "Gospodaru naš! Vjerujemo, zato nam oprosti i smiluj nam se, a Ti si Najbolji od milostivih."'),
(2783, 23, 110, 'Tad ste ih uzimali ruglom, dok nisu učinili da zaboravite Opomenu Moju, a njima ste se ismijavali.'),
(2784, 23, 111, 'Uistinu! Ja sam ih nagradio Danas što su bili strpljivi, jer oni, oni su pobjednici.'),
(2785, 23, 112, '(Allah) će reći: "Koliko ste ostali na Zemlji, računajući godine?"'),
(2786, 23, 113, 'Reći će: "Ostali smo dan ili dio dana. Ta pitaj one koji su brojali!"'),
(2787, 23, 114, '(Allah) će reći: "Ostali ste samo malo, kad biste znali!"'),
(2788, 23, 115, 'Pa zar mislite da smo vas stvorili zaludno i da vi Nama nećete biti povraćeni?'),
(2789, 23, 116, 'Pa uzvišen neka je Allah, Vladar istinski! Nema boga osim Njega, Gospodara Arša plemenitog!'),
(2790, 23, 117, 'A ko uz Allaha priziva boga drugog - nema on dokaza za to - pa samo je obračun njegov kod Gospodara njegovog. Uistinu! Nevjernici neće uspjeti.'),
(2791, 23, 118, 'I reci: "Gospodaru moj! Oprosti i smiluj se, a Ti si Najbolji od milosrdnih!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Sura - objavili smo je i učinili je obavezom, i objavili u njoj znakove jasne, da biste se vi opomenuli.'),
(2793, 24, 2, 'Bludnicu i bludnika: pa bičujte svako od njih dvoje pojedinačno stotinom udaraca, i neka vas zbog njih ne obuzme sažaljenje u vjeri Allahovoj, ako vjerujete u Allaha i Dan posljednji. I neka kažnjavanju njihovom prisustvuje skupina vjernika.'),
(2794, 24, 3, 'Bludnik neka ne ženi izuzev bludnicu ili mušrikinju, a bludnica, neka nju ne ženi, izuzev bludnik ili mušrik; a to je zabranjeno vjernicima.'),
(2795, 24, 4, 'A oni koji okrive poštene žene, zatim ne dovedu četiri svjedoka, tad ih bičujte sa osamdeset udaraca, i nikad ne prihvatajte njihovo svjedočenje; a ti takvi su grješnici;'),
(2796, 24, 5, 'Izuzev onih koji se pokaju poslije toga i poprave. Pa uistinu, Allah je Oprosnik, Milosrdni!'),
(2797, 24, 6, 'A oni koji okrive žene svoje, a oni ne budu imali svjedoka, izuzev njih samih - pa svjedočenje jednog od njih neka bude sa četiri svjedočenja Allahom, (da) je on uistinu od onih koji istinu govore;'),
(2798, 24, 7, 'A peto (svjedočenje), da je prokletstvo Allahovo nad njim, ako je od lažljivaca.'),
(2799, 24, 8, 'A otkloniće od nje kaznu, ako posvjedoči sa četiri svjedočenja Allahom, (da) je uistinu on od lažaca,'),
(2800, 24, 9, 'A peto (svjedočenje), da je srdžba Allahova nad njom, ako je (on) od istinitih.'),
(2801, 24, 10, 'I da nije dobrote Allahove prema vama i milosti Njegove, i što je Allah Primalac pokajanja, Mudri...'),
(2802, 24, 11, 'Uistinu, oni koji su došli sa laži, skupina su od vas. Ne smatrajte to zlom za vas. Naprotiv, to je bolje za vas. Svaki čovjek od njih će imati ono šta je stekao od grijeha; a onaj od njih koji preuzme glavninu toga, imaće on kaznu strahovitu.'),
(2803, 24, 12, 'Zašto nisu - kad ste to čuli - pomislili vjernici i vjernice o dušama svojim dobro, i rekli: "Ovo je laž očita?"'),
(2804, 24, 13, 'Zašto na to nisu doveli četiri svjedoka? Pa kad nisu doveli svjedoke, pa takvi - kod Allaha - oni su lažljivci.'),
(2805, 24, 14, 'A da nije dobrote Allahove prema vama i milosti Njegove na Dunjau i Ahiretu, sigurno bi vas dotakla u onom u što ste se upustili, kazna užasna.'),
(2806, 24, 15, 'Kad ste to prebacivali jezicima svojim, i govorili ustima svojim ono o čemu vi nemate znanje, i smatrali to lahkim, a to je kod Allaha strašno.'),
(2807, 24, 16, 'Zašto niste - kad ste to čuli - rekli: "Nije za nas da o ovom govorimo. Slava Tebi (o Allahu)! Ovo je potvora strašna."'),
(2808, 24, 17, 'Allah vas savjetuje da ne ponovite slično tome nikada, ako ste vjernici.'),
(2809, 24, 18, 'I objašnjava vam Allah ajete; a Allah je Znalac, Mudri.'),
(2810, 24, 19, 'Uistinu, oni koji vole da šire razvrat među onima koji vjeruju, imaće oni kaznu bolnu na Dunjau i Ahiretu. A Allah zna, a vi ne znate.'),
(2811, 24, 20, 'A da nije dobrote Allahove prema vama i milosti Njegove, i da Allah (nije) Samilostan, Milosrdan...'),
(2812, 24, 21, 'O vi koji vjerujete! Ne slijedite korake šejtanove. A ko slijedi korake šejtanove - pa uistinu, on naređuje razvrat i ružno. A da nije dobrote Allahove nad vama i milosti Njegove, ne bi se očistio od vas nijedan, nikad. Međutim, Allah čisti koga hoće; a Allah je Onaj koji čuje, Znalac.'),
(2813, 24, 22, 'I neka se ne kunu posjednici blagodati između vas i obilja da neće dati rodbini i siromasima i muhadžirima na putu Allahovom. I neka oproste i neka pređu. Zar ne volite da vama Allah oprosti? A Allah je Oprosnik, Milosrdni.'),
(2814, 24, 23, 'Uistinu, oni koji okrive poštene bezazlene vjernice, prokleti su na Dunjau i Ahiretu, a imaće oni kaznu strahovitu!'),
(2815, 24, 24, 'Na Dan kad protiv njih budu svjedočili jezici njihovi i ruke njihove i noge njihove, o onom šta su radili.'),
(2816, 24, 25, 'Tog dana će im Allah ispuniti nagradu njihovu istinski, i znaće da je Allah - On Istina jasna.'),
(2817, 24, 26, 'Nevaljale su za nevaljale, i nevaljali su za nevaljale. A dobre su za dobre, i dobri su za dobre. Takvi su slobodni od onog šta (oni) govore. Imaće oni oprost i opskrbu plemenitu.'),
(2818, 24, 27, 'O vi koji vjerujete! Ne ulazite u kuće koje nisu kuće vaše - dok ne zatražite dozvolu i selamite ukućane njihove. To je bolje za vas, da biste se vi poučili.'),
(2819, 24, 28, 'Pa ako u njima nikoga ne nađete, tad ne ulazite u njih, dok vam se ne dopusti. A ako vam se kaže: "Vratite se!", tad se vratite, to je čišće za vas, a Allah je o onom šta radite Znalac.'),
(2820, 24, 29, 'Nije vam grijeh da uđete u kuće nenaseljene u kojima je korist za vas. A Allah zna šta objelodanjujete i šta krijete.'),
(2821, 24, 30, 'Reci vjernicima da obore poglede svoje i čuvaju furudže svoje, to je čišće za njih. Uistinu! Allah je o onom šta rade Obaviješteni.'),
(2822, 24, 31, 'I reci vjernicama da obore poglede svoje i čuvaju furudže svoje, i neka ne pokazuju ukras svoj, izuzev šta je vanjsko od njega; i neka pokrivače svoje stave na grudi svoje, i neka ne pokazuju ukras svoj, izuzev muževima svojim ili očevima svojim, ili očevima muževa svojih, ili sinovima svojim ili sinovima muževa svojih, ili braći svojoj ili sinovima braće svoje, ili sinovima sestara svojih ili ženama svojim, ili onom šta posjeduju desnice njihove, ili slugama svojim od ljudi koji nemaju sposobnost, ili djeci koja ne spoznaju awrete žena. I neka ne udaraju nogama svojim da bi se saznalo šta kriju od ukrasa svog. I pokajte se svi Allahu, o vjernici, da biste vi uspjeli.'),
(2823, 24, 32, 'I ženite slobodne između vas i dobre od robova vaših i robinja vaših. Ako budu fukare, obogatiće ih Allah iz blagodati Svoje; a Allah je Sveobuhvatni, Znalac.'),
(2824, 24, 33, 'I neka se suzdrže oni koji ne nalaze (sredstva) za ženidbu, dok ih Allah ne učini imućnim iz blagodati Svoje. A oni koji traže knjigu od onog šta posjeduju desnice vaše, pa napišite im, ako ste spoznali u njima dobro. I dajte im od bogatstva Allahovog kojeg vam je dao. I ne primoravajte robinje svoje na prostituciju, ako žele neporočnost, da biste tražili prolazno dobro života Dunjaa. A (ako) ih ko primora, pa uistinu, Allah je nakon prisile njihove (njima) Oprosnik, Milosrdni.'),
(2825, 24, 34, 'I doista smo vam objavili ajete jasne i primjer onih koji su prošli prije vas, i pouku bogobojaznima.'),
(2826, 24, 35, 'Allah je Svjetlost nebesa i Zemlje! Primjer svjetla Njegovog je kao udubina, u njoj svjetiljka, svjetiljka u staklu, staklo - kao da je ono zvijezda blistava; pali se od drveta blagoslovljenog, masline, ne istočne, niti zapadne. Gotovo da ulje njegovo zasija, a da ga vatra ne dotakne. Svjetlost nad svjetlošću! Allah vodi svjetlu svom koga hoće i Allah navodi primjere ljudima, a Allah je o svakoj stvari Znalac.'),
(2827, 24, 36, 'U kućama (koje je) Allah dozvolio da se podignu i spominje u njima ime Njegovo; slave Ga u njima jutrima i večerima,'),
(2828, 24, 37, 'Ljudi koje ne odvraća trgovina niti prodaja od spominjanja Allaha i obavljanja salata i davanja zekata. Plaše se Dana u kojem će se preokrenuti srca i pogledi;'),
(2829, 24, 38, 'Da bi ih Allah nagradio najboljim šta su radili, i povećao im iz dobrote Svoje. A Allah opskrbljuje koga hoće bez računa.'),
(2830, 24, 39, 'A oni koji ne vjeruju - djela njihova su kao fatamorgana u pustinji. Smatra je žedan vodom - dok, kad joj dođe, ne nađe je nikako, a nađe Allaha kod nje, pa mu ispuni račun njegov. A Allah je brz obračunom.'),
(2831, 24, 40, 'Ili kao tmine u moru dubokom, prekriva ga talas, iznad njega talas, iznad njega oblaci. Tmine, jedna iznad druge. Kad bi izvadio ruku svoju, jedva bi je vidio! A onaj kome Allah ne načini svjetlo, tad nema za njega svjetla nikakva.'),
(2832, 24, 41, 'Zar ne vidiš da Allaha - Njega slavi ko je na nebesima i Zemlji i ptice raširenih krila? Svako doista zna salat svoj i slavljenje svoje, a Allah je Znalac o onom šta čine.'),
(2833, 24, 42, 'I Allahova je vlast nebesa i Zemlje, a Allahu je dolazište!'),
(2834, 24, 43, 'Zar ne vidiš da Allah goni oblake, zatim ih spaja, potom ih čini gomilom, pa vidiš kišu izlazi iz sred njih. I spušta iz neba, s brda (oblaka) u njemu nešto grada, pa pogađa njime koga hoće, a otklanja od koga hoće. Skoro da bljesak munje Njegove oduzme vidove.'),
(2835, 24, 44, 'Allah okreće noć i dan. Uistinu, u tome je ibret za posjednike opažanja.'),
(2836, 24, 45, 'A Allah je svaku životinju stvorio od vode. Pa od njih je ko ide na trbuhu svom, i od njih je ko ide na dvije noge, i od njih je ko ide na četiri. Allah stvara šta hoće. Uistinu! Allah nad svakom stvari ima moć.'),
(2837, 24, 46, 'Doista, objavili smo ajete jasne, a Allah upućuje koga hoće putu pravom.'),
(2838, 24, 47, 'I govore (licemjeri): "Vjerujemo u Allaha i Poslanika i slušamo", zatim se odvraća skupina njih poslije toga, a nisu takvi vjernici.'),
(2839, 24, 48, 'I kad se pozivaju Allahu i Poslaniku Njegovom, da sudi među njima, gle: skupina njih su odvraćeni;'),
(2840, 24, 49, 'A ako bude za njih Istina, dolaze mu pokorni.'),
(2841, 24, 50, 'Je li u srcima njihovim bolest, ili sumnjaju ili se plaše da će Allah i Poslanik Njegov nepravedno postupiti protiv njih? Naprotiv, ti takvi su zalimi.'),
(2842, 24, 51, 'Govor vjernika, kad budu pozvani Allahu i Poslaniku Njegovom da presudi među njima - je samo da kažu: "Čujemo i pokoravamo se", a ti takvi su oni koji će uspjeti.'),
(2843, 24, 52, 'A ko posluša Allaha i Poslanika Njegovog, i bude se plašio Allaha i bojao Ga se - pa ti takvi su pobjednici.'),
(2844, 24, 53, 'I zaklinju se Allahom, najjačom od zakletvi njihovih (da) će, ako im narediš, sigurno izaći. Reci: "Ne kunite se, poslušnost pristojna! Uistinu, Allah je o onom šta radite Obaviješteni."'),
(2845, 24, 54, 'Reci: "Poslušajte Allaha i poslušajte Poslanika!" Pa ako se okrenete, tad je na njemu samo ono čim je opterećen, a na vama čim ste opterećeni. A ako ga poslušate, uputićete se; a na Poslaniku je jedino dostava jasna.'),
(2846, 24, 55, 'Allah je obećao onima od vas koji vjeruju i rade dobra djela (da) će sigurno dati da oni naslijede na Zemlji, kao što je dao da naslijede oni prije njih, i sigurno im učvrstiti vjeru njihovu kojom je za njih zadovoljan, i sigurno im dati u zamjenu poslije njihovog straha, sigurnost. Obožavaće Mene, neće pridruživati Meni ništa. A ko ne bude vjerovao poslije toga, pa ti takvi su grješnici.'),
(2847, 24, 56, 'I obavljajte salat i dajite zekat, i poslušajte Poslanika, da bi vam se smilovalo.'),
(2848, 24, 57, 'Nemoj nikako misliti da će oni koji ne vjeruju biti ti koji će umaći na Zemlji, a sklonište njihovo biće vatra; a doista je loše dolazište!'),
(2849, 24, 58, 'O vi koji vjerujete! Neka zatraže dozvolu od vas oni koje posjeduju desnice vaše i od vas oni koji nisu dostigli punoljetstvo, tri puta: prije salatul-fedžra, i kad skidate odjeću svoju zbog podnevne žege, i poslije salatul-išaa. Tri perioda neobučenosti vi imate. Nije vama niti njima grijeh poslije njih - onima koji vas obilaze, vi jedni druge. Tako vam Allah objašnjava znakove Svoje, a Allah je Znalac, Mudri.'),
(2850, 24, 59, 'I kad djeca između vas dostignu punoljetnost, tad neka traže dozvolu, kao što su tražili oni prije njih. Tako vam objašnjava Allah znakove Svoje, a Allah je Znalac, Mudri.'),
(2851, 24, 60, 'A sjedilice od žena koje se ne nadaju udaji, pa nije im grijeh da ostave odjeću svoju, ne kao pokazivačice ukrasa. A suzdrže li se - bolje je za njih. A Allah je Onaj koji čuje, Znalac.'),
(2852, 24, 61, 'Nije slijepcu prijekor, niti je hromu prijekor, i nije bolesniku prijekor, niti vama, da jedete u kućama vašim ili kućama očeva vaših, ili kućama majki vaših, ili kućama braće vaše ili kućama sestara vaših, ili kućama amidža vaših ili kućama tetaka vaših (po ocu), ili kućama daidža vaših ili kućama tetaka vaših (po materi), ili onog čije ključeve posjedujete ili prijatelja vašeg. Nije vam grijeh da jedete zajedno ili odvojeno. Pa kad ulazite u kuće, tad selamite duše vaše pozdravom od Allaha blagoslovljenim, lijepim. Tako vam Allah objašnjava znakove, da biste vi shvatili.'),
(2853, 24, 62, 'Vjernici su samo oni koji vjeruju u Allaha i Poslanika Njegovog - i kad budu s njim na poslu zajedničkom - ne idu dok ne zatraže dozvolu od njega. Uistinu! Oni koji traže dozvolu od tebe, ti takvi vjeruju u Allaha i Poslanika Njegovog. Pa kad zatraže dozvolu od tebe radi nekog svog posla, tad dozvoli kome hoćeš od njih i traži im oprost od Allaha. Uistinu! Allah je Oprosnik, Milosrdni.'),
(2854, 24, 63, 'Ne činite poziv Poslanikov među vama, kao poziv vaš, jednih drugima. Doista Allah zna one od vas koji se izvlače kradom. Zato neka se pripaze oni koji izbjegavaju naredbu njegovu, da ih ne snađe iskušenje ili ih ne pogodi kazna bolna.'),
(2855, 24, 64, 'Besumnje! Uistinu je Allahovo šta je na nebesima i Zemlji! Doista zna ono na čemu ste vi. A na Dan kad Njemu budu povraćeni, tad će ih obavijestiti o onom šta su radili; a Allah je o svakoj stvari Znalac');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Blagoslovljen neka je Onaj koji objavljuje furkan robu Svom, da bude svjetovima opominjač:'),
(2857, 25, 2, 'Onaj čija je vlast nebesa i Zemlje; a nije uzeo djete, niti On ima druga u vlasti. A stvorio je svaku stvar, te joj odmjerio mjeru.'),
(2858, 25, 3, 'I uzimaju mimo Njega bogove, ne stvaraju ništa, a oni bivaju stvarani, i ne vladaju za duše svoje štetom niti korišću, i ne vladaju smrću niti životom, a ni proživljenjem.'),
(2859, 25, 4, 'I govore oni koji ne vjeruju: "Ovo je samo laž koju je izmislio, a pomogli su mu u tome drugi ljudi." Pa doista donose zulm i potvoru,'),
(2860, 25, 5, 'I kažu: "Priče ranijih, zapisao ih je pa mu se diktiraju jutrom i navečer."'),
(2861, 25, 6, 'Reci: "Objavio ga je Onaj koji zna tajnu na nebesima i Zemlji. Uistinu, On je Oprosnik, Milosrdni."'),
(2862, 25, 7, 'I govore: "Šta je ovom Poslaniku? Jede hranu i hoda po trgovima. Što mu se ne spusti melek, pa da bude s njim opominjač?'),
(2863, 25, 8, 'Ili spusti njemu riznica, ili imadne on bašču (da) jede iz nje?" I govore zalimi: "Slijedite samo čovjeka opčinjenog."'),
(2864, 25, 9, 'Pogledaj kako navode za tebe primjere pa lutaju, te ne mogu (da nađu) put!'),
(2865, 25, 10, 'Blagoslovljen neka je Onaj koji će, ako hoće, načiniti tebi bolje od tog: bašče ispod kojih teku rijeke, i načiniti ti palače.'),
(2866, 25, 11, 'Naprotiv, poriču Čas, a onom ko poriče Čas, pripremili smo seir.'),
(2867, 25, 12, 'Kad ih ugleda iz mjesta dalekog, čuće ga pobješnjelog i pucketavog.'),
(2868, 25, 13, 'A kad budu bačeni u tijesno mjesto njegovo povezani, prizivaće tamo propast.'),
(2869, 25, 14, 'Ne prizivajte Danas propast jednu, nego prizivajte propast mnogu!'),
(2870, 25, 15, 'Reci: "Da li je to bolje ili Džennet vječnosti koji je obećan bogobojaznima? Bit će za njih plaćom i (konačnim) dolazištem!'),
(2871, 25, 16, 'Imaće oni u njemu šta htjednu, vječito." Do Gospodara tvog je obećanje traženo.'),
(2872, 25, 17, 'A na Dan kad skupi njih i ono šta su obožavali mimo Allaha, pa rekne: "Jeste li vi zaveli robove Moje ove, ili su oni zalutali s puta?"'),
(2873, 25, 18, 'Reći će: "Slavljen neka si Ti! Nije nam priličilo da mimo Tebe uzimamo ikakve zaštitnike, međutim, dao si da uživaju oni i očevi njihovi, dok nisu zaboravili Opomenu i bili narod propalih."'),
(2874, 25, 19, 'Tad će vas doista poreći u onom šta ćete govoriti, zato nećete moći (postići) otklon, niti pomoć. A ko od vas učini zulm, daćemo da on iskusi kaznu veliku.'),
(2875, 25, 20, 'I nismo poslali prije tebe nijednog od izaslanika, a da nisu uistinu oni jeli hranu i hodali po trgovima. A učinili smo vas jedne drugima iskušenjem. Izdržavate li? A Gospodar tvoj je Onaj koji vidi.'),
(2876, 25, 21, 'I govore oni koji se ne nadaju susretu Našem: "Zašto se ne spuste na nas meleci, ili (ne) vidimo Gospodara svog?" Doista se uznose u dušama svojim i ohole ohološću velikom.'),
(2877, 25, 22, 'Na Dan kad vide meleke, neće Tog dana biti radosne vijesti za prestupnike, a govoriće (meleci): "Barijera zabranjena!"'),
(2878, 25, 23, 'I pristupićemo onom šta su uradili od posla pa ga učiniti prašinom raspršenom.'),
(2879, 25, 24, 'Stanovnici Dženneta će Tog dana biti boljeg boravišta i ljepšeg odmorišta.'),
(2880, 25, 25, 'Na Dan kad se rascijepi nebo oblacima, i meleci spuste (velikim) spuštanjem,'),
(2881, 25, 26, 'Vlast će Tog dana istinski biti Milostivog i biće Dan nevjernicima težak.'),
(2882, 25, 27, 'I na Dan kad zalim bude grizao ruke svoje, govoriće: "O da sam ja uzeo s Poslanikom put!'),
(2883, 25, 28, 'O teško meni! Da ja nisam uzeo tog i tog prijateljem!'),
(2884, 25, 29, 'Doista me je zaveo od Opomene, nakon što mi je došla." A šejtan je prema čovjeku dezerter.'),
(2885, 25, 30, 'I reći će Poslanik: "O Gospodaru moj! Uistinu, moj narod je držao ovaj Kur\'an napuštenim!"'),
(2886, 25, 31, 'A tako smo svakom vjerovjesniku načinili neprijatelja između prestupnika. A dovoljan je Gospodar tvoj Vodič i Pomagač.'),
(2887, 25, 32, 'A oni koji ne vjeruju, govore: "Zašto mu se ne objavi cio Kur\'an odjednom?" Tako, da bismo njime osnažili srce tvoje, a učimo ga tertilom.'),
(2888, 25, 33, 'I neće ti donijeti primjer, a da ti ne donesemo Istinu i bolje tumačenje.'),
(2889, 25, 34, 'Oni koji će biti sabrani na licima svojim Džehennemu: takvi će biti goreg položaja i najodlutaliji s puta.'),
(2890, 25, 35, 'I doista smo dali Musau Knjigu i učinili mu uz njega brata njegovog Haruna pomoćnikom,'),
(2891, 25, 36, 'Pa rekli: "Idite narodu onih koji poriču ajete Naše." Pa smo ih uništili (potpunim) uništenjem.'),
(2892, 25, 37, 'I narod Nuhov - pošto su porekli poslanike - potopili smo ih i učinili ih ljudima znakom. A pripremili smo za zalime kaznu bolnu.'),
(2893, 25, 38, 'I Ada i Semuda i stanovnike Ressa i pokoljenja mnoga između toga.'),
(2894, 25, 39, 'I svakom tom smo navodili primjere, i svakojeg potpuno razbili.'),
(2895, 25, 40, 'A doista prolaze pored grada na koji je izlivena kiša kobna. Pa zar ga ne vide? Naprotiv! Ne nadaju se proživljenju.'),
(2896, 25, 41, 'A kad vide te, samo te uzimaju ruglom. "Je li ovo onaj kojeg je Allah podigao poslanikom?'),
(2897, 25, 42, 'Skoro bi nas zaveo od bogova naših da nismo s njima ustrajali." A saznaće kad vide kaznu, ko je odlutaliji s puta.'),
(2898, 25, 43, 'Jesi li vidio ko je uzeo bogom svojim, strast svoju? Pa zar ti da budeš nad njim čuvar?'),
(2899, 25, 44, 'Ili računaš da većina njih čuje ili shvata? Oni su jedino kao stoka - naprotiv, oni su odlutaliji s puta!'),
(2900, 25, 45, 'Zar ne vidiš - Gospodar tvoj, kako izdužuje sjenu? A da hoće, sigurno bi je učinio nepomičnom; potom činimo nad njom Sunce pokazivačem;'),
(2901, 25, 46, 'Zatim je privlačimo Sebi privlačenjem lagahnim.'),
(2902, 25, 47, 'I On je Taj koji čini za vas noć pokrivkom, a san odmorom, i čini dan proživljenjem.'),
(2903, 25, 48, 'I On je Taj koji šalje vjetrove radosnom viješću ispred milosti Svoje, i spuštamo s neba vodu čistu,'),
(2904, 25, 49, 'Da oživimo njome zamrlu zemlju i napojimo njome onog koga smo stvorili: životinje i ljude mnoge.'),
(2905, 25, 50, 'I doista smo to raspodijelili među njima, da bi se poučili, pa odbija većina ljudi - izuzev nevjerstvo.'),
(2906, 25, 51, 'A da smo htjeli, sigurno bismo u svakom gradu podigli opominjača.'),
(2907, 25, 52, 'Zato ne slušaj nevjernike, i bori se protiv njih njime - džihadom velikim.'),
(2908, 25, 53, 'I On je Taj koji je pustio dva mora: ovo pitko, slatko, a ovo slano, gorko; i načinio je između njih dvoje berzeh i prepreku nesavladivu.'),
(2909, 25, 54, 'I On je Taj koji je od vode stvorio čovjeka, te mu načinio srodstvo i tazbinstvo. A Gospodar tvoj je Moćni.'),
(2910, 25, 55, 'I obožavaju mimo Allaha ono šta im ne koristi, niti im šteti. A nevjernik je protiv Gospodara svog pomagač.'),
(2911, 25, 56, 'I poslali smo tebe samo kao donosioca radosnih vijesti i opominjača.'),
(2912, 25, 57, 'Reci: "Ne tražim od vas za njega nikakvu nagradu, izuzev ko hoće da uzme ka Gospodaru svome put."'),
(2913, 25, 58, 'I pouzdaj se u Živog, Onog koji ne umire, i slavi sa hvalom Njegovom - a dovoljno je (što je) On o grijesima robova Svojih Obaviješten,'),
(2914, 25, 59, 'Onaj koji je stvorio nebesa i Zemlju i šta je između njih, u šest dana, zatim se postavio na Arš. Milostivi! Pa pitaj o Njemu obaviještenog!'),
(2915, 25, 60, 'I kad im se kaže: "Padnite na sedždu Milostivom!" - govore: "A šta je Milostivi? Zar da na sedždu padamo što nam naređuješ?" - i povećava im izbjegavanje.'),
(2916, 25, 61, 'Blagoslovljen neka je Onaj koji je načinio u nebu galaksije, i načinio u njemu svjetiljku i Mjesec osvjetljavajućim!'),
(2917, 25, 62, 'I On je Taj koji je učinio dan i noć uzastopnim, za onog ko želi da se pouči ili želi zahvalnost.'),
(2918, 25, 63, 'A robovi Milostivog, oni koji hodaju po Zemlji ponizno, i kad im se obrate neznalice, kažu: "Selam!"'),
(2919, 25, 64, 'I oni koji provode noć pred Gospodarom svojim, čineći sedždu i stojeći,'),
(2920, 25, 65, 'I oni koji govore: "Gospodaru naš! Okreni od nas kaznu Džehennema; uistinu, kazna njegova je stalna;'),
(2921, 25, 66, 'Uistinu, on je loše boravište i stanište."'),
(2922, 25, 67, 'I oni koji, kad troše, ne rasipaju niti škrtare, i budu između ta (dva) stava;'),
(2923, 25, 68, 'I oni koji ne prizivaju uz Allaha boga drugog, niti ubijaju dušu koju je zabranio Allah - izuzev po pravdi - niti bludniče - a ko to čini, srest će kaznu,'),
(2924, 25, 69, 'Udvostručiće mu se kazna na Dan kijameta i boravit će u njoj prezren;'),
(2925, 25, 70, 'Izuzev ko se pokaje i vjeruje i bude činio djelo dobro; pa takvima će Allah zamijeniti loša djela njihova, dobrim djelima; a Allah je Oprosnik, Milosrdni.'),
(2926, 25, 71, 'A ko se pokaje i čini dobro, pa uistinu on se obratio Allahu obraćenjem;'),
(2927, 25, 72, 'I oni koji ne svjedoče neistinu, i kad prođu pored besmislice, prođu dostojanstveno;'),
(2928, 25, 73, 'I oni koji - kad budu opomenuti znakovima Gospodara svog - ne padaju pred njima gluhi i slijepi.'),
(2929, 25, 74, 'I oni koji govore: "Gospodaru naš! Podari nam od žena naših i potomstva našeg radost očiju, i učini nas za bogobojazne imamima."'),
(2930, 25, 75, 'Takvi će biti nagrađeni visokom odajom, što su bili strpljivi, i biće susretani u njoj pozdravom i selamom.'),
(2931, 25, 76, 'Vječno će biti u njoj. Divno je boravište i stanište!'),
(2932, 25, 77, 'Reci: "Ne bi na vas obratio pažnju Gospodar moj, da nije dowe vaše." Pa doista, porekli ste, zato će biti nužna (kazna).');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Ta. Sin. Mim.'),
(2934, 26, 2, 'To su znaci Kitaba jasnog.'),
(2935, 26, 3, 'Možda bi ti bio uništitelj duše svoje, što (oni) nisu vjernici.'),
(2936, 26, 4, 'Kad bismo htjeli, spustili bismo na njih s neba znak, pa bi mu povili vratove svoje ponizno.'),
(2937, 26, 5, 'I nije im došla nijedna nova opomena od Milostivog, a da nisu bili od nje odvraćeni.'),
(2938, 26, 6, 'Pa doista su poricali, pa doći će im vijesti o onom čemu su se izrugivali.'),
(2939, 26, 7, 'Zar ne vide zemlju, koliko smo dali da iz nje iznikne od svake vrste plemenite?'),
(2940, 26, 8, 'Uistinu, u tome je znak. A nisu većina njih vjernici.'),
(2941, 26, 9, 'I uistinu! Gospodar tvoj! On je Moćni, Milosrdni.'),
(2942, 26, 10, 'A kad Gospodar tvoj pozva Musaa: "Idi narodu zalima,'),
(2943, 26, 11, 'Narodu faraonovom. Zar se neće pobojati?"'),
(2944, 26, 12, 'Reče: "Gospodaru moj! Uistinu, ja se bojim da će me poreći,'),
(2945, 26, 13, 'I stežu se grudi moje, i ne oslobađa se jezik moj, zato pošalji Haruna;'),
(2946, 26, 14, 'I imaju oni protiv mene zločin, pa se bojim da će me ubiti."'),
(2947, 26, 15, 'Reče: "Nikako! Pa idite obojica sa znacima Našim. Uistinu, Mi ćemo biti uz vas slušajući.'),
(2948, 26, 16, 'Pa dođite faraonu, te recite: \'Uistinu, mi smo poslanici Gospodara svjetova,'),
(2949, 26, 17, 'Pošalji s nama sinove Israilove!"'),
(2950, 26, 18, '(Faraon) reče: "Zar te nismo među nama odgojili (kao) dijete? I boravio si među nama (mnoge) godine života svog,'),
(2951, 26, 19, 'I učinio si djelo svoje koje si učinio. A ti si od nezahvalnika."'),
(2952, 26, 20, 'Reče: "Počinio sam ga tad kad sam ja bio od zalutalih.'),
(2953, 26, 21, 'Tad sam pobjegao od vas pošto sam vas se uplašio, pa me je obdario Gospodar moj sudom, i učinio me (jednim) od izaslanika,'),
(2954, 26, 22, 'A to je blagodat kojom mi prigovaraš - da si porobio sinove Israilove."'),
(2955, 26, 23, 'Faraon reče: "A šta je Gospodar svjetova?"'),
(2956, 26, 24, '(Musa) reče: "Gospodar nebesa i Zemlje i onog između njih, ako ste uvjereni."'),
(2957, 26, 25, '(Faraon) reče onima oko sebe: "Zar ne čujete?"'),
(2958, 26, 26, '(Musa) reče: "Gospodar vaš i Gospodar očeva vaših ranijih."'),
(2959, 26, 27, '(Faraon) reče: "Uistinu, poslanik vaš koji vam je poslan, je luđak!"'),
(2960, 26, 28, '(Musa) reče: "Gospodar istoka i zapada i onog što je između njih, ako shvatate."'),
(2961, 26, 29, '(Faraon) reče: "Ako uzmeš drugog boga sem mene, sigurno ću te učiniti (jednim) od zatvorenika."'),
(2962, 26, 30, '(Musa) reče: "Zar kad ti donesem stvar očitu?"'),
(2963, 26, 31, '(Faraon) reče: "Pa daj je, ako si od istinitih!"'),
(2964, 26, 32, 'Tad (Musa) baci štap svoj, pa gle: on zmija očita,'),
(2965, 26, 33, 'I izvuče ruku svoju, tad gle: ona bijela za gledatelje.'),
(2966, 26, 34, '(Faraon) reče uglednicima oko sebe: "Uistinu, ovo je čarobnjak znalac,'),
(2967, 26, 35, 'Želi da vas izvede iz zemlje vaše sihrom svojim; pa šta naređujete?"'),
(2968, 26, 36, 'Rekoše: "Ostavi njega i brata njegovog, a pošalji u gradove sakupljače,'),
(2969, 26, 37, 'Dovešće ti svakog čarobnjaka znalca."'),
(2970, 26, 38, 'Pa su sakupljeni čarobnjaci za određeno vrijeme dana poznatog,'),
(2971, 26, 39, 'I bi rečeno ljudima: "Jeste li vi sakupljeni,'),
(2972, 26, 40, 'Da bismo mi slijedili čarobnjake, ako oni budu pobjednici?"'),
(2973, 26, 41, 'Pa pošto čarobnjaci dođoše, rekoše faraonu: "Hoćemo li mi uistinu imati nagradu, ako mi budemo pobjednici?"'),
(2974, 26, 42, 'Reče: "Da, i uistinu ćete tad biti od približenih."'),
(2975, 26, 43, 'Musa im reče: "Bacite ono čega ste vi bacači!"'),
(2976, 26, 44, 'Tad baciše konope svoje i štapove svoje i rekoše: "Tako nam moći faraonove, uistinu mi, mi smo pobjednici."'),
(2977, 26, 45, 'Tad Musa baci štap svoj, pa gle: on proguta ono šta su lažirali.'),
(2978, 26, 46, 'Tad se čarobnjaci baciše na sedždu.'),
(2979, 26, 47, 'Rekoše: "Vjerujemo u Gospodara svjetova,'),
(2980, 26, 48, 'Gospodara Musaovog i Harunovog."'),
(2981, 26, 49, 'Reče: "Povjerovaste mu prije no što sam vam dopustio. Uistinu, on je vaš velikan koji vas je poučio sihru! Pa sigurno ćete saznati. Sigurno ću odsijeći ruke vaše i noge vaše unakrst, i sigurno ću vas sve raspeti."'),
(2982, 26, 50, 'Rekoše: "Nije šteta! Uistinu, mi smo Gospodaru našem povratnici.'),
(2983, 26, 51, 'Uistinu! Mi žudimo da nam Gospodar naš oprosti greške naše jer smo prvi vjernici."'),
(2984, 26, 52, 'I objavismo Musau: "Kreni s robovima Mojim noću, uistinu, vi ćete biti slijeđeni."'),
(2985, 26, 53, 'Tad posla faraon u gradove sakupljače:'),
(2986, 26, 54, '"Uistinu, ovi su grupa mala,'),
(2987, 26, 55, 'I uistinu, oni su nas rasrdili,'),
(2988, 26, 56, 'I uistinu, mi smo svi oprezni."'),
(2989, 26, 57, 'Pa ih izvedosmo iz vrtova i izvora,'),
(2990, 26, 58, 'I riznica i mjesta plemenitog.'),
(2991, 26, 59, 'Tako; i dali smo ih u naslijeđe sinovima Israilovim.'),
(2992, 26, 60, 'Pa su ih slijedili kad se Sunce rađalo,'),
(2993, 26, 61, 'Pa pošto se vidješe dvije skupine, drugovi Musaovi rekoše: "Uistinu, mi smo sustignuti."'),
(2994, 26, 62, 'Reče: "Nikako! Uistinu, sa mnom je Gospodar moj, uputiće me."'),
(2995, 26, 63, 'Tad objavismo Musau: "Udri štapom svojim more!" Tad se razdvoji, te svaki dio bi kao brijeg velik.'),
(2996, 26, 64, 'I približismo tamo druge,'),
(2997, 26, 65, 'I spasismo Musaa i onog ko je bio s njim, sve,'),
(2998, 26, 66, 'Potom potopismo druge.'),
(2999, 26, 67, 'Uistinu, u tome je znak, a većina njih nisu vjernici.'),
(3000, 26, 68, 'I uistinu, Gospodar tvoj! On je Moćni, Milosrdni.'),
(3001, 26, 69, 'I izrecituj im vijest o Ibrahimu.'),
(3002, 26, 70, 'Kad reče ocu svom i narodu svom: "Šta obožavate?"'),
(3003, 26, 71, 'Rekoše: "Obožavamo idole, pa smo stalno njima privrženi."'),
(3004, 26, 72, 'Reče: "Da li vas čuju kad prizivate,'),
(3005, 26, 73, 'Ili vam koriste ili štete?"'),
(3006, 26, 74, 'Rekoše: "Naprotiv, našli smo očeve naše, tako čine."'),
(3007, 26, 75, 'Reče: "Pa vidite li šta obožavaste,'),
(3008, 26, 76, 'Vi i očevi vaši stariji?"'),
(3009, 26, 77, 'Pa uistinu, oni su moji neprijatelji, izuzev Gospodara svjetova,'),
(3010, 26, 78, 'Koji me je stvorio, pa me On uputio,'),
(3011, 26, 79, 'I Onaj koji me hrani i napaja me.'),
(3012, 26, 80, 'I kad se razbolim, tad On liječi me,'),
(3013, 26, 81, 'I Koji će me usmrtiti, zatim me oživiti,'),
(3014, 26, 82, 'I Koji će mi - nadam se - oprostiti grešku moju na Danu sudnjem.'),
(3015, 26, 83, 'Gospodaru moj! Daruj mi sud i priključi me pravednima.'),
(3016, 26, 84, 'I učini me jezika istinitog u kasnijim (pokoljenjima),'),
(3017, 26, 85, 'I učini me od nasljednika bašče uživanja,'),
(3018, 26, 86, 'I oprosti ocu mom, uistinu, on je bio od zalutalih.'),
(3019, 26, 87, 'I ne ponizi me na Dan kad budu podignuti,'),
(3020, 26, 88, 'Dan kad neće koristiti bogatstvo, niti sinovi,'),
(3021, 26, 89, 'Izuzev onom ko dođe Allahu sa srcem zdravim.'),
(3022, 26, 90, 'I približiće se Džennet bogobojaznima,'),
(3023, 26, 91, 'I pokazaće se džehim zabludjelima,'),
(3024, 26, 92, 'I biće im rečeno: "Gdje je to šta ste obožavali'),
(3025, 26, 93, 'Mimo Allaha? Hoće li pomoći vama ili pomoći sebi?"'),
(3026, 26, 94, 'Tad će biti u njega strmoglavljeni oni, i oni koji su zavodili,'),
(3027, 26, 95, 'I vojske Iblisove zajedno.'),
(3028, 26, 96, 'Govoriće - a oni će se u njemu svađati:'),
(3029, 26, 97, '"Tako nam Allaha, bili smo doista u zabludi očitoj,'),
(3030, 26, 98, 'Kad smo vas izjednačavali sa Gospodarom svjetova,'),
(3031, 26, 99, 'A zaveli su nas jedino prestupnici,'),
(3032, 26, 100, 'Zato mi nemamo nikakvih posrednika,'),
(3033, 26, 101, 'Niti prijatelja bliska.'),
(3034, 26, 102, 'Pa da nam je ponavljanje, pa da budemo od vjernika!"'),
(3035, 26, 103, 'Uistinu! U tome je znak, a većina njih nisu vjernici!'),
(3036, 26, 104, 'A uistinu, Gospodar tvoj! On je Moćni, Milosrdni.'),
(3037, 26, 105, 'Narod Nuhov je poricao izaslanike,'),
(3038, 26, 106, 'Kad im reče brat njihov Nuh: "Zar se ne bojite?'),
(3039, 26, 107, 'Uistinu! Ja sam vama poslanik pouzdan,'),
(3040, 26, 108, 'Zato se bojte Allaha i mene poslušajte!'),
(3041, 26, 109, 'A ne tražim od vas za to nikakvu nagradu, nagrada moja je jedino do Gospodara svjetova.'),
(3042, 26, 110, 'Zato se bojte Allaha i mene poslušajte!"'),
(3043, 26, 111, 'Rekoše: "Zar da tebi vjerujemo, a slijede te najniži?"'),
(3044, 26, 112, 'Reče: "A nemam ja znanje o onom šta rade,'),
(3045, 26, 113, 'Obračun njihov je jedino do Gospodara mog, kad biste zapazili!'),
(3046, 26, 114, 'I nisam ja progonitelj vjernika,'),
(3047, 26, 115, 'Ja sam samo opominjač jasni."'),
(3048, 26, 116, 'Rekoše: "Ako ne prestaneš, o Nuhu, sigurno ćeš biti od kamenovanih."'),
(3049, 26, 117, 'Reče: "Gospodaru moj! Uistinu, moj narod me poriče,'),
(3050, 26, 118, 'Zato presudi između mene i između njih presudom, i spasi mene i onog ko je sa mnom od vjernika."'),
(3051, 26, 119, 'Pa spasismo njega i onog ko je bio s njim, u lađi natovarenoj,'),
(3052, 26, 120, 'Zatim smo poslije potopili preostale.'),
(3053, 26, 121, 'Uistinu, u tome je znak, a većina njih nisu vjernici.'),
(3054, 26, 122, 'I uistinu, Gospodar tvoj! On je Moćni, Milosrdni.'),
(3055, 26, 123, 'Ad je poricao izaslanike,'),
(3056, 26, 124, 'Kad im reče brat njihov Hud: "Zar se ne bojite?'),
(3057, 26, 125, 'Uistinu! Ja sam vam poslanik pouzdani,'),
(3058, 26, 126, 'Zato se bojte Allaha i poslušajte mene!'),
(3059, 26, 127, 'A ne tražim od vas za to nikakvu nagradu, nagrada moja je jedino do Gospodara svjetova.'),
(3060, 26, 128, 'Gradite li na svakoj uzvišici obilježje, poigravate se,'),
(3061, 26, 129, 'I gradite tvrđave, da biste se vi ovjekovječili?'),
(3062, 26, 130, 'I kad napadate, napadate (kao) tirani.'),
(3063, 26, 131, 'Zato se bojte Allaha i poslušajte mene!'),
(3064, 26, 132, 'I bojte se Onog koji vas je pomogao onim šta znate.'),
(3065, 26, 133, 'Pomogao vas je stokom i sinovima,'),
(3066, 26, 134, 'I baščama i izvorima.'),
(3067, 26, 135, 'Uistinu! Ja se plašim nad vama kazne Dana strahovitog."'),
(3068, 26, 136, 'Rekoše: "Isto nam je da li ti wazio ili ne bio od waiza;'),
(3069, 26, 137, 'Ovo je samo običaj ranijih,'),
(3070, 26, 138, 'I nećemo mi biti kažnjeni."'),
(3071, 26, 139, 'Tad su ga porekli, pa smo ih uništili. Uistinu! U tome je znak, a većina njih nisu vjernici.'),
(3072, 26, 140, 'A uistinu! Gospodar tvoj! On je Moćni, Milosrdni.'),
(3073, 26, 141, 'Poricao je Semud izaslanike.'),
(3074, 26, 142, 'Kad im reče brat njihov Salih: "Zar se ne bojite?'),
(3075, 26, 143, 'Uistinu! Ja sam vam poslanik pouzdani,'),
(3076, 26, 144, 'Zato se bojte Allaha i poslušajte mene!'),
(3077, 26, 145, 'A ne tražim od vas za to nikakvu nagradu, nagrada moja je jedino do Gospodara svjetova.'),
(3078, 26, 146, 'Hoćete li biti ostavljeni u onom šta je ovdje, sigurni,'),
(3079, 26, 147, 'U baščama i izvorima,'),
(3080, 26, 148, 'I usjevima i palmama - plodovi njihovi zreli -'),
(3081, 26, 149, 'I klešete iz brda kuće spretno?'),
(3082, 26, 150, 'Zato se bojte Allaha i poslušajte mene,'),
(3083, 26, 151, 'I ne slušajte naredbu onih koji pretjeruju,'),
(3084, 26, 152, 'Koji čine fesad na Zemlji i ne popravljaju."'),
(3085, 26, 153, 'Rekoše: "Ti si samo od opčinjenih;'),
(3086, 26, 154, 'Ti si samo smrtnik sličan nama. Pa daj znak ako si od istinitih."'),
(3087, 26, 155, 'Reče: "Ovo je deva. Imaće ona napajanje, i vi ćete imati napajanje dana poznatog.'),
(3088, 26, 156, 'I ne dotičite je zlom, pa da vas snađe kazna Dana strahovitog."'),
(3089, 26, 157, 'Pa je raniše, i postaše pokajanici.'),
(3090, 26, 158, 'Tad ih spopade kazna. Uistinu, u tome je znak, a većina njih nisu vjernici.'),
(3091, 26, 159, 'A uistinu! Gospodar tvoj! On je Moćni, Milosrdni.'),
(3092, 26, 160, 'Narod Lutov je poricao izaslanike,'),
(3093, 26, 161, 'Kad im reče brat njihov Lut: "Zar se ne bojite?'),
(3094, 26, 162, 'Uistinu! Ja sam vam poslanik pouzdani,'),
(3095, 26, 163, 'Zato se bojte Allaha i poslušajte mene!'),
(3096, 26, 164, 'A ne tražim od vas za to nikakvu nagradu, nagrada moja je jedino do Gospodara svjetova.'),
(3097, 26, 165, 'Zar dolazite muškarcima od svjetova,'),
(3098, 26, 166, 'A ostavljate šta vam je stvorio Gospodar vaš od žena vaših? Naprotiv, vi ste narod prestupnika."'),
(3099, 26, 167, 'Rekoše: "Ako ne prestaneš o Lute, sigurno ćeš biti od protjeranih."'),
(3100, 26, 168, 'Reče: "Uistinu, ja sam od onih koji mrze posao vaš.'),
(3101, 26, 169, 'Gospodaru moj! Spasi mene i porodicu moju od onog šta rade!"'),
(3102, 26, 170, 'Pa smo spasili njega i svu njegovu porodicu,'),
(3103, 26, 171, 'Izuzev starice među zaostalima,'),
(3104, 26, 172, 'Zatim smo uništili druge,'),
(3105, 26, 173, 'A izlili smo na njih kišu. Pa zle li kiše opominjanih!'),
(3106, 26, 174, 'Uistinu, u tome je znak, a većina njih nisu vjernici.'),
(3107, 26, 175, 'I uistinu! Gospodar tvoj! On je Moćni, Milosrdni.'),
(3108, 26, 176, 'Poricali su stanovnici Ejke izaslanike,'),
(3109, 26, 177, 'Kad njima reče Šuajb: "Zar se ne bojite?'),
(3110, 26, 178, 'Uistinu! Ja sam vam poslanik pouzdani,'),
(3111, 26, 179, 'Zato se bojte Allaha i poslušajte mene!'),
(3112, 26, 180, 'A ne tražim od vas nikakvu nagradu za to, nagrada moja je jedino do Gospodara svjetova.'),
(3113, 26, 181, 'Ispunjavajte mjeru i ne budite od zakidatelja.'),
(3114, 26, 182, 'Mjerite mjerilom ispravnim.'),
(3115, 26, 183, 'I ne štetujte ljudima stvari njihove, niti pravite nered na Zemlji čineći fesad,'),
(3116, 26, 184, 'I bojte se Onog koji je stvorio vas i pokoljenja ranija."'),
(3117, 26, 185, 'Rekoše: "Ti si samo od opčinjenih;'),
(3118, 26, 186, 'I ti si samo smrtnik sličan nama i smatramo te sigurno (jednim) od lažaca.'),
(3119, 26, 187, 'Pa daj da padne na nas komad neba, ako si od istinitih."'),
(3120, 26, 188, 'Reče: "Gospodar moj je Najbolji znalac onog šta radite."'),
(3121, 26, 189, 'Tad su ga porekli, pa ih je spopala kazna Dana tame. Uistinu, to je bila kazna Dana strahovitog.'),
(3122, 26, 190, 'Uistinu, u tome je znak, a većina njih nisu vjernici.'),
(3123, 26, 191, 'A uistinu! Gospodar tvoj! On je Moćni, Milosrdni.'),
(3124, 26, 192, 'I uistinu, to je Objava Gospodara svjetova,'),
(3125, 26, 193, 'Spušta ga Duh pouzdani,'),
(3126, 26, 194, 'Na srce tvoje, da budeš od opominjača,'),
(3127, 26, 195, 'Na jeziku arapskom, jasnom.'),
(3128, 26, 196, 'I uistinu, on je u zeburima ranijih.'),
(3129, 26, 197, 'Zar im nije znak (to) što ga znaju učeni sinovi Israilovi?'),
(3130, 26, 198, 'A da smo ga objavili nekom od stranaca,'),
(3131, 26, 199, 'Pa im ga čitao, ne bi bili u njega vjernici.'),
(3132, 26, 200, 'Tako to uvlačimo u srca prestupnika.'),
(3133, 26, 201, 'Neće vjerovati u njega, dok ne vide kaznu bolnu.'),
(3134, 26, 202, 'Pa doći će im iznenadno, a oni neće opaziti.'),
(3135, 26, 203, 'Tad će govoriti: "Hoće li nam se odložiti?"'),
(3136, 26, 204, 'Pa zar kaznu Našu požuruju?'),
(3137, 26, 205, 'Pa zar nisi vidio, ako im damo da uživaju godinama,'),
(3138, 26, 206, 'Zatim im dođe ono čime im se prijetilo,'),
(3139, 26, 207, 'Neće im koristiti ono šta uživaju.'),
(3140, 26, 208, 'I nismo uništili nijedan grad, a da on nije imao opominjače,'),
(3141, 26, 209, 'Opomenom, a nismo zalimi.'),
(3142, 26, 210, 'I nisu ga spustili šejtani,'),
(3143, 26, 211, 'I ne dolikuje im, niti mogu.'),
(3144, 26, 212, 'Uistinu, oni su od prisluškivanja odvojeni.'),
(3145, 26, 213, 'Zato ne prizivaj uz Allaha boga drugog, pa da budeš od kažnjenih.'),
(3146, 26, 214, 'I upozoravaj rodbinu svoju najbližu,'),
(3147, 26, 215, 'I spusti krilo svoje onom ko te slijedi od vjernika.'),
(3148, 26, 216, 'Pa ako te ne poslušaju, tad reci: "Uistinu, ja sam slobodan od onog šta radite."'),
(3149, 26, 217, 'Pa pouzdaj se u Moćnog, Milosrdnog,'),
(3150, 26, 218, 'Koji te vidi kad stojiš,'),
(3151, 26, 219, 'I kretanje tvoje među onima koji čine sedždu.'),
(3152, 26, 220, 'Uistinu! On, On je Onaj koji čuje, Znalac.'),
(3153, 26, 221, 'Hoću li vas obavijestiti na koga se spuštaju šejtani?'),
(3154, 26, 222, 'Spuštaju se na svakog lažljivca, grješnika,'),
(3155, 26, 223, 'Koriste sluh, a većina njih su lažljivci.'),
(3156, 26, 224, 'A pjesnike - slijede ih zalutali.'),
(3157, 26, 225, 'Zar ne vidiš da oni u svakoj dolini lutaju,'),
(3158, 26, 226, 'I da oni govore ono što ne rade?'),
(3159, 26, 227, 'Izuzev onih koji vjeruju i čine dobra djela i spominju Allaha mnogo, i brane se nakon što im se učini zulm. A saznaće oni koji čine zulm, kojem povratištu se vraćaju!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Ta. Sin. To su znaci Kur\'ana i Kitaba jasnog,'),
(3161, 27, 2, 'Upute i radosne vijesti vjernicima,'),
(3162, 27, 3, 'Onima koji obavljaju salat i daju zekat, a oni su u Ahiret uvjereni.'),
(3163, 27, 4, 'Uistinu, oni koji ne vjeruju u Ahiret, uljepšali smo im djela njihova, pa oni lutaju.'),
(3164, 27, 5, 'To su oni koji će imati zlu kaznu i oni će na Ahiretu (baš) oni biti gubitnici.'),
(3165, 27, 6, 'A uistinu, ti primaš Kur\'an od Mudrog, Znalca!'),
(3166, 27, 7, 'Kad reče Musa porodici svojoj: "Uistinu, ja sam opazio vatru; donijet ću vam od nje vijest ili ću vam donijeti plamenu baklju, da biste se vi ogrijali."'),
(3167, 27, 8, 'Pa pošto njoj dođe, bi pozvan: "Blagoslovljen je ko je u vatri i ko je oko nje! I neka je slava Allahu, Gospodaru svjetova!'),
(3168, 27, 9, 'O Musa! Uistinu! To sam Ja, Allah, Moćni, Mudri!'),
(3169, 27, 10, 'I baci štap svoj!" Pa pošto ga vidje da vijuga kao da je on džinn, okrenu se bježeći i ne obazirući se: "O Musa! Ne boj se! Uistinu, kod Mene se ne plaše izaslanici,'),
(3170, 27, 11, 'Izuzev ko učini zulm, zatim zamijeni dobrim poslije zla, pa uistinu, Ja sam Oprosnik, Milosrdni!'),
(3171, 27, 12, 'I zavuci ruku svoju u njedra svoja, izaći će bijela, bez ozljede! (Jedan) od devet znakova za faraona i narod njegov. Uistinu, oni su narod grješnika."'),
(3172, 27, 13, 'Pa pošto im dođoše znakovi Naši vidljivi, rekoše: "Ovo je sihr očiti."'),
(3173, 27, 14, 'I zanijekaše ih - a bile su u njih uvjerene duše njihove - nepravedno i oholo. Pa pogledaj kakav je bio kraj mufsida.'),
(3174, 27, 15, 'I doista smo dali Dawudu i Sulejmanu znanje, a govorili su: "Hvala Allahu koji nas je odlikovao nad mnogima od Njegovih robova vjernika."'),
(3175, 27, 16, 'I naslijedi Sulejman Dawuda i reče: "O ljudi! Naučeni smo govoru ptičijem i dato nam je od svake stvari. Uistinu, ovo je ta blagodat očita."'),
(3176, 27, 17, 'I skupiše se Sulejmanu vojske njegove od džinna i ljudi, i ptice - a oni raspoređeni;'),
(3177, 27, 18, 'Dok, kad dođoše dolini mrava, reče mrav: "O mravi! Uđite u nastambe svoje (da) vas ne zgnječi Sulejman i vojske njegove, a oni ne opaze."'),
(3178, 27, 19, 'Tad se nasmiješi zasmijan govorom njegovim i reče: "Gospodaru moj! Potakni me da zahvaljujem na blagodati Tvojoj koju si podario meni i roditeljima mojim, i da radim dobro koje će Te zadovoljiti, i daj mi da uđem milošću Tvojom među robove Tvoje dobre."'),
(3179, 27, 20, 'I pretraži ptice, pa reče: "Šta mi je? Ne vidim pupavca, ili je među odsutnima?'),
(3180, 27, 21, 'Sigurno ću ga kazniti kaznom žestokom, ili ću ga sigurno preklati ili će mi sigurno dati dokaz jasan."'),
(3181, 27, 22, 'Pa se (pupavac) zadrža nedugo, te reče: "Obuhvatio sam ono šta nisi obuhvatio (znanjem), i donio ti iz Sabe vijest sigurnu.'),
(3182, 27, 23, 'Uistinu! Ja sam našao ženu (koja) vlada njima, i (kojoj) je dato od svake stvari, a ona ima prijestolje veličanstveno.'),
(3183, 27, 24, 'Našao sam nju i narod njen padaju na sedždu Suncu mimo Allaha, a uljepšao im je šejtan djela njihova, te ih okrenuo od puta, pa se oni ne upućuju -'),
(3184, 27, 25, 'Da ne padaju na sedždu Allahu Koji izvodi skriveno u nebesima i Zemlji, i zna šta krijete i šta objelodanjujete.'),
(3185, 27, 26, 'Allah je - nema boga osim Njega - Gospodar Arša veličanstvenog."'),
(3186, 27, 27, '(Sulejman) reče: "Vidjećemo da li istinu govoriš ili si od lažaca.'),
(3187, 27, 28, 'Odnesi ovo moje pismo, pa im ga baci, zatim se okreni od njih i vidi šta vraćaju."'),
(3188, 27, 29, '(Bilkis) reče: "O uglednici! Uistinu meni - meni je bačeno pismo plemenito.'),
(3189, 27, 30, 'Uistinu, ono je od Sulejmana, i uistinu ono je: \'U ime Allaha, Milostivog, Milosrdnog!\''),
(3190, 27, 31, 'Ne uzdižite se nada mnom i dođite mi pokorni."'),
(3191, 27, 32, 'Reče: "O velikani! Izjasnite mi se o stvari mojoj, neću biti donosilac odluke, dok mi ne posvjedočite."'),
(3192, 27, 33, 'Rekoše: "Mi smo posjednici moći i posjednici sile žestoke, a komanda je do tebe; zato gledaj šta ćeš narediti."'),
(3193, 27, 34, 'Reče: "Uistinu, kad kraljevi uđu u grad, unerede ga i učine ugledne stanovnike njegove poniznim, i tako (uvijek) rade.'),
(3194, 27, 35, 'I uistinu, ja ću im poklon poslati, te ću vidjeti s čim se vraćaju izaslanici."'),
(3195, 27, 36, 'Pa pošto (izaslanik) dođe Sulejmanu, (Sulejman) reče: "Zar da mi pružite bogatstvo? Pa ono što mi je dao Allah bolje je od onog šta je dao vama. Naprotiv, vi s poklonom svojim likujete."'),
(3196, 27, 37, '"Vrati se njima. Pa sigurno ćemo im doći s vojskama, neće oni imati moći nad njima, i sigurno ćemo ih protjerati iz nje ponižene, i oni će biti prezreni."'),
(3197, 27, 38, 'Reče: "O velikani! Koji od vas će mi donijeti prijesto njen, prije no što mi dođu pokorni?"'),
(3198, 27, 39, 'Reče Ifrit od džinna: "Ja ću ti ga donijeti prije no što ustaneš sa mjesta svog. A uistinu, ja sam za to jak, pouzdan."'),
(3199, 27, 40, 'Onaj kod kojeg je znanje iz Knjige, reče: "Ja ću ti ga donijeti prije no što ti se vrati treptaj tvoj." Pa pošto ga vidje kod sebe postavljenog, (Sulejman) reče: "Ovo je iz blagodati Gospodara mog, da bi me iskušao - zahvaljujem li ili ne zahvaljujem. A ko zahvaljuje, pa samo zahvaljuje za dušu svoju; a ko poriče - pa uistinu! Gospodar moj je Neovisni, Plemeniti."'),
(3200, 27, 41, 'Reče: "Prerušite za nju prijesto njen! Vidjećemo da li će se uputiti ili će biti od onih koji se ne upućuju."'),
(3201, 27, 42, 'Pa pošto ona dođe, bi rečeno: "Je li ovakav prijesto tvoj?" Reče: "Kao da je to on." A nama je dato znanje prije nego njoj i muslimani smo.'),
(3202, 27, 43, 'A spriječilo je nju ono šta je obožavala mimo Allaha. Uistinu, ona je bila iz naroda nevjernika.'),
(3203, 27, 44, 'Bi joj rečeno: "Uđi u dvoranu!" Pa pošto je vidje, pomisli to je pučina, i odgoli potkoljenice svoje. Reče (Sulejman): "Uistinu, to je dvorana staklima popločana." Reče (ona): "Gospodaru moj! Uistinu, ja sam učinila zulm duši svojoj i predajem se sa Sulejmanom Allahu, Gospodaru svjetova."'),
(3204, 27, 45, 'I doista smo Semudu poslali brata njihovog Saliha: "Obožavajte Allaha!" Tad gle! Oni dvije skupine (koje) se prepiru.'),
(3205, 27, 46, 'Reče: "O narode moj! Zašto požurujete zlo prije dobrog? Zašto ne tražite oprost od Allaha, da bi vam se ukazala milost?"'),
(3206, 27, 47, 'Rekoše: "Slutimo s tobom i s onim ko je s tobom, zlo." Reče: "Zloslutnost vaša je kod Allaha. Naprotiv, vi ste narod koji se iskušava."'),
(3207, 27, 48, 'I bilo je u gradu devet osoba, fesad su činili na Zemlji, a nisu sređivali.'),
(3208, 27, 49, 'Rekoše: "Zakunite se Allahom, (da) ćemo sigurno srediti njega i porodicu njegovu noću, zatim ćemo sigurno reći prijatelju njegovom: \'Nismo prisustvovali uništenju porodice njegove, a uistinu, mi smo istiniti."'),
(3209, 27, 50, 'A pleli su spletku i pleli smo spletku, a oni ne opaziše.'),
(3210, 27, 51, 'Pa pogledaj kakav je bio kraj spletke njihove, da smo Mi uništili njih i narod njihov, sve.'),
(3211, 27, 52, 'Pa to su kuće njihove, puste zbog toga što su činili zulm. Uistinu, u tome je znak za ljude koji znaju.'),
(3212, 27, 53, 'I spasili smo one koji su vjerovali i bojali se,'),
(3213, 27, 54, 'I Luta! Kad reče narodu svom: "Zar ćete činiti razvrat, a vi gledate?'),
(3214, 27, 55, 'Zar vi uistinu ljudima dolazite strašću, mimo žena? Naprotiv, vi ste narod koji ne zna."'),
(3215, 27, 56, 'Pa odgovor naroda njegovog je bio jedino što rekoše: "Istjerajte porodicu Lutovu iz naselja vašeg! Uistinu, oni su ljudi koji se čiste!"'),
(3216, 27, 57, 'Tad spasismo njega i porodicu njegovu, izuzev žene njegove. Odredili smo za nju da bude od zaostalih -'),
(3217, 27, 58, 'I izlili na njih kišu. Pa zle li kiše opomenutih.'),
(3218, 27, 59, 'Reci: "Hvala Allahu i selam neka je robovima Njegovim koje je odabrao!" Da li je Allah bolji ili ono šta (Mu) pridružuju?'),
(3219, 27, 60, 'Zar (nije bolji) Ko je stvorio nebesa i Zemlju, i spušta vam s neba vodu - pa damo da njome izniknu vrtovi krasni? Nije za vas (moguće) da učinite da izraste drveće njihovo. Ima li bog uz Allaha? Naprotiv, oni su ljudi koji izjednačuju.'),
(3220, 27, 61, 'Zar (nije bolji) Ko je učinio Zemlju staništem, i načinio kroz nju rijeke, i načinio na njoj planine stabilne, i načinio između dva mora barijeru? Ima li bog uz Allaha? Naprotiv, većina njih ne zna!'),
(3221, 27, 62, 'Zar (nije bolji) Ko odgovara nevoljniku kad Ga priziva, i otklanja zlo i čini vas halifama Zemlje? Ima li bog uz Allaha? Malo što se poučavate!'),
(3222, 27, 63, 'Zar (nije bolji) Ko vas vodi u tminama kopna i mora, i Ko šalje vjetrove kao radosne vijesti ispred milosti Njegove? Ima li bog uz Allaha? Uzvišen neka je Allah od onog šta pridružuju!'),
(3223, 27, 64, 'Zar (nije bolji) Ko je počeo stvaranje zatim ga ponavlja, i Ko vas opskrbljuje s neba i Zemlje? Ima li bog uz Allaha? Reci: "Donesite dokaz vaš, ako ste istiniti."'),
(3224, 27, 65, 'Reci: "Onaj ko je na nebesima i Zemlji ne zna nevidljivo, izuzev Allah, i ne opažaju kad će biti podignuti."'),
(3225, 27, 66, 'Naprotiv! Seže li znanje njihovo u Ahiret? Naprotiv, oni su u sumnji o njemu. Naprotiv, oni su o njemu slijepci!'),
(3226, 27, 67, 'I govore oni koji ne vjeruju: "Hoćemo li kad budemo prah, mi i očevi naši, hoćemo li uistinu biti izvedeni?'),
(3227, 27, 68, 'Doista, ovo nam je obećavano, nama i očevima našim od ranije. Ovo su samo priče ranijih."'),
(3228, 27, 69, 'Reci: "Putujte po Zemlji pa vidite kakav je bio kraj prestupnika."'),
(3229, 27, 70, 'I ne žalosti se nad njima, i ne budi u tjeskobi zbog onog šta spletkare.'),
(3230, 27, 71, 'A govore: "Kad će ovo obećanje, ako istinu govorite?"'),
(3231, 27, 72, 'Reci: "Možda vas slijedi dio onog šta požurujete."'),
(3232, 27, 73, 'A uistinu, Gospodar tvoj je Vlasnik blagodati nad ljudima, međutim, većina njih ne zahvaljuje.'),
(3233, 27, 74, 'I uistinu, Gospodar tvoj zna šta kriju grudi njihove, a šta objelodanjuju.'),
(3234, 27, 75, 'I nema ništa skriveno na nebu i Zemlji, a da nije u Knjizi jasnoj.'),
(3235, 27, 76, 'Uistinu, ovaj Kur\'an kazuje sinovima Israilovim većinom o onom u čemu se oni razilaze.'),
(3236, 27, 77, 'I uistinu, on je Uputa i milost vjernicima.'),
(3237, 27, 78, 'Uistinu! Gospodar tvoj će presuditi među njima sudom Svojim, a On je Moćni, Znalac:'),
(3238, 27, 79, 'Zato se pouzdaj u Allaha, uistinu si ti na Istini jasnoj.'),
(3239, 27, 80, 'Uistinu, ti nećeš pročuti mrtve, niti ćeš učiniti da čuju gluhi poziv, kad se okrenu bježeći.'),
(3240, 27, 81, 'I nisi ti vodič slijepcima iz zablude njihove. Pročut ćeš samo onog ko vjeruje u ajete Naše, ta oni su muslimani.'),
(3241, 27, 82, 'I kad padne Riječ na njih, izvešćemo im životinju iz Zemlje, (koja) će im govoriti da ljudi u znakove Naše nisu uvjereni.'),
(3242, 27, 83, 'A na Dan kad od svake umme saberemo skupinu, od onog ko je poricao ajete Naše, tad će oni biti raspoređeni;'),
(3243, 27, 84, 'Dok, kad dođu, reći će: "Jeste li poricali ajete Moje, a niste ih obuhvatili znanjem? Šta ste to radili?"'),
(3244, 27, 85, 'I past će na njih Riječ što su činili zulm, pa oni neće govoriti.'),
(3245, 27, 86, 'Zar ne vide da smo mi načinili noć da se smire u njoj, a dan (da daje) vidljivost? Uistinu, u tome su znaci za ljude koji vjeruju!'),
(3246, 27, 87, 'A na Dan kad se puhne u sur, pa se prestraši ko je u nebesima i ko je na Zemlji, izuzev koga htjedne (izuzeti) Allah, a svi će doći Njemu ponizni.'),
(3247, 27, 88, 'I vidiš planine, misliš nepokretne su, a one se kreću kretanjem oblaka. Djelovanje je Allaha Koji je usavršio svaku stvar. Uistinu! On je o onom šta činite Obaviješteni.'),
(3248, 27, 89, 'Ko donese dobro djelo, pa on će od njega imati bolje i oni će od straha Tog dana biti sigurni.'),
(3249, 27, 90, 'A ko donese zlodjelo, pa baciće se lica njihova u vatru. Da li ćete biti plaćeni, osim kako ste radili?'),
(3250, 27, 91, 'Samo mi je naređeno da obožavam Gospodara ovog grada, koji ga je posvetio, a Njegova je svaka stvar; i naređeno mi je da budem od muslimana,'),
(3251, 27, 92, 'I da učim Kur\'an. Pa ko se uputi, pa samo se upućuje za dušu svoju; a ko zaluta, tad reci: "Ja sam samo od opominjača."'),
(3252, 27, 93, 'I reci: "Hvala Allahu, pokazaće vam znakove Svoje pa ćete ih prepoznati." A nije Gospodar tvoj nehajan prema onom šta radite.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Ta. Sin. Mim.'),
(3254, 28, 2, 'To su znaci Kitaba jasnog.'),
(3255, 28, 3, 'Recitiramo ti (dio) vijesti o Musau i faraonu s Istinom, za ljude koji vjeruju.'),
(3256, 28, 4, 'Uistinu! Faraon se uzdigao na Zemlji i učinio stanovnike njene sektama, tlačeći skupinu njih, koljući sinove njihove, a dajući da žive žene njihove. Uistinu, on je bio od mufsida.'),
(3257, 28, 5, 'I željeli smo da obdarimo one koji su učinjeni slabima na Zemlji, i učinimo ih imamima, i učinimo ih nasljednicima,'),
(3258, 28, 6, 'I da ih učvrstimo na Zemlji, i pokažemo faraonu i Hamanu i vojskama njih dvojice - ono čega su se od njih pribojavali.'),
(3259, 28, 7, 'I objavismo majci Musaovoj: "Zadoji ga, pa kad se za njega uplašiš, tad ga baci u rijeku, i ne plaši se, niti tuguj. Uistinu, Mi ćemo ti ga vratiti i učiniti ga (jednim) od izaslanika."'),
(3260, 28, 8, 'Pa je pokupila njega porodica faraonova, da bi im bio neprijatelj i jad. Uistinu! Faraon i Haman i vojske njihove bili su oni koji čine greške.'),
(3261, 28, 9, 'I reče žena faraonova: "Osvježenje oka je meni i tebi. Ne ubijte ga! Možda će nam koristiti ili da ga uzmemo djetetom" - i oni ne zapaziše.'),
(3262, 28, 10, 'I posta srce majke Musaove prazno; skoro bi ga sigurno pokazala, da nismo učvrstili srce njeno, da bude od vjernika.'),
(3263, 28, 11, 'I reče sestri njegovoj: "Slijedi ga!" Pa je gledala njega izdaleka, a oni ne opaziše.'),
(3264, 28, 12, 'I zabranili smo njemu dojilje od ranije, pa (ona) reče: "Hoćete li da vam ukažem na čeljad kuće (koja) će se brinuti o njemu za vas, i oni će mu biti savjetnici?"'),
(3265, 28, 13, 'Tad ga vratismo majci njegovoj da joj se oko osvježi i ne žalosti, i da zna da je obećanje Allahovo Istina. Ali većina njih ne zna.'),
(3266, 28, 14, 'I pošto je dostigao snagu svoju i dozrio, dadosmo mu sud i znanje; a tako plaćamo dobročinitelje.'),
(3267, 28, 15, 'I uđe u grad za vrijeme nehajnosti stanovnika njegovih, i nađe u njemu dva čovjeka (kako) se biju, ovaj iz sekte njegove, a ovaj od neprijatelja njegovog. Tad od njega zatraži pomoć onaj iz sekte njegove, protiv onog od neprijatelja njegovog. Tad ga udari Musa šakom, te ga dokrajči. Reče: "Ovo je od posla šejtanovog." Uistinu, on je neprijatelj, zavodnik očiti.'),
(3268, 28, 16, 'Reče: "Gospodaru moj! Uistinu, ja sam učinio zulm duši svojoj, pa oprosti mi!" Pa mu je oprostio. Uistinu! On, On je Oprosnik, Milosrdni.'),
(3269, 28, 17, 'Reče: "Gospodaru moj! Zato što si me obdario, zato neću biti pomagač prestupnika."'),
(3270, 28, 18, 'Pa osvanu u gradu prestrašen, iščekujući, tad gle! onaj koji ga je zvao u pomoć jučer, zove ga. Musa mu reče: "Uistinu, ti si očito onaj koji je zabludio."'),
(3271, 28, 19, 'Pa pošto je želio da dograbi onog koji je neprijatelj njima dvojici, (taj) reče: "O Musa! Zar želiš da me ubiješ, kao što si ubio dušu jučer? Samo želiš da budeš tiranin na Zemlji, a ne želiš da budeš od onih koji popravljaju."'),
(3272, 28, 20, 'I dođe čovjek s najdaljeg kraja grada trčeći. Reče: "O Musa! Uistinu, velikani o tebi odlučuju da te ubiju, zato izlazi (iz grada). Uistinu, ja sam ti od savjetnika."'),
(3273, 28, 21, 'Tad izađe iz njega prestrašen, iščekujući. Reče: "Gospodaru moj! Spasi me od naroda zalima."'),
(3274, 28, 22, 'Pa pošto se zaputio prema Medjenu, reče: "Možda će Gospodar moj da me uputi pravom putu."'),
(3275, 28, 23, 'Pa pošto stiže vodi Medjena, nađe na njoj zajednicu ljudi napajaju (stoku); i nađe pored njih dvije žene - odbijaju. Reče: "Šta je s vama dvjema?" Rekoše: "Ne napajamo dok ne odu čobani, a naš otac je starac uveliko."'),
(3276, 28, 24, 'Tad napoji njima dvjema, zatim se okrenu sjenci, pa reče: "Gospodaru moj! Uistinu, ja sam - šta god mi spustiš od dobra - onaj kojem treba."'),
(3277, 28, 25, 'Tad mu dođe jedna od njih dvije, idući stidljivo. Reče: "Uistinu, moj otac te poziva da ti isplati nagradu što si napojio za nas." Pa pošto mu je došao i ispričao priču, reče: "Ne boj se! Spasio si se od naroda zalima."'),
(3278, 28, 26, 'Jedna od njih reče: "O oče moj! Unajmi ga! Uistinu je najbolji taj kojeg bi unajmio, snažan, pouzdan."'),
(3279, 28, 27, 'Reče: "Uistinu, ja želim da te oženim jednom od mojih kćeri, ovih dviju, da se unajmiš meni osam godina. Pa ako ispuniš deset, pa od tebe je; i ne želim da ti otežam. Naći ćeš - inšaAllah - da sam od pravednih."'),
(3280, 28, 28, '(Musa) reče: "To je između mene i između tebe. Koji god od dva roka ispunim, pa neće biti neprijateljstva protiv mene; a Allah je nad onim šta govorimo Jamac."'),
(3281, 28, 29, 'Pa pošto Musa ispuni rok i pođe s porodicom svojom, opazi na obronku brda vatru. Reče porodici svojoj: "Sačekajte! Uistinu ja sam spazio vatru; možda ću vam ja od nje donijeti vijest, ili glavnju vatre, da biste se vi ogrijali."'),
(3282, 28, 30, 'Pa pošto joj priđe, bi pozvan s desne strane doline, u kraju blagoslovljenom, iz drveta: "O Musa! Uistinu! Ja, Ja sam Allah, Gospodar svjetova;'),
(3283, 28, 31, 'I baci štap svoj!" Pa pošto ga vidje da se brzo kreće kao da je on džinn, okrenu se bježeći i ne vraćajući se. "O Musa! Priđi i ne plaši se. Uistinu, ti si od sigurnih.'),
(3284, 28, 32, 'Zavuci ruku svoju u njedra svoja, izaći će bijela, bez ozljede. I saberi si krilo svoje od straha. Pa to će biti dva dokaza od Gospodara tvog faraonu i uglednicima njegovim: uistinu, oni su narod grješnika."'),
(3285, 28, 33, 'Reče: "Gospodaru moj! Uistinu, ja sam ubio od njih dušu, zato se bojim da će me ubiti.'),
(3286, 28, 34, 'A brat moj Harun, on je rječitiji od mene jezikom, pa pošalji ga sa mnom (kao) potporu, potvrđujući mene. Uistinu, ja se plašim da će me poreći."'),
(3287, 28, 35, 'Reče: "Ojačat ćemo ruku tvoju bratom tvojim i dati vama dvojici moć, pa vam neće doseći. Sa znacima Našim, vas dvojica i ko vas bude slijedio, bićete pobjednici."'),
(3288, 28, 36, 'Pa pošto im dođe Musa sa znakovima Našim jasnim, rekoše: "Ovo je samo sihr izmišljeni, i nismo mi ovo čuli u očeva naših ranijih."'),
(3289, 28, 37, 'I reče Musa: "Gospodar moj je Najbolji znalac onog ko je došao s Uputom od Njega, i ko će za kraj imati dom. Uistinu, to neće uspjeti zalimi."'),
(3290, 28, 38, 'I reče faraon: "O uglednici! Ne znam da vi imate drugog boga osim mene, zato mi zapali o Hamane, na ilovači, te mi načini toranj, da bih se ja uspeo bogu Musaovom; a uistinu, ja ga smatram (jednim) od lažova."'),
(3291, 28, 39, 'I uzoholi se on i vojske njegove na zemlji bez prava, i misliše da se oni Nama neće vratiti.'),
(3292, 28, 40, 'Pa smo dograbili njega i vojske njegove, te ih bacili u more. Zato pogledaj kakav je bio kraj zalima.'),
(3293, 28, 41, 'I učinili smo ih imamima (koji) pozivaju vatri, a na Dan kijameta neće biti pomognuti.'),
(3294, 28, 42, 'I pratili smo ih na ovom Dunjau prokletstvom, a na Dan kijameta oni će biti od odvratnih.'),
(3295, 28, 43, 'I doista smo dali Musau Knjigu - nakon što smo uništili pokoljenja ranija - jasne uvide ljudima, i uputu i milost, da bi se oni poučili.'),
(3296, 28, 44, 'I nisi bio na strani zapadnoj kad smo Musau izdali naredbu, i nisi bio od svjedoka.'),
(3297, 28, 45, 'Ali, Mi smo stvorili pokoljenja, pa im se život odužio. I nisi bio stanovnik među stanovnicima Medjena, da im učiš ajete Naše. Međutim, Mi smo bili ti koji su slali (poslanike).'),
(3298, 28, 46, 'I nisi bio na obronku planine kad smo pozvali. Ali (poslat si) kao milost Gospodara svog da opominješ narod kojem nije došao nijedan opominjač prije tebe - da bi se oni poučili.'),
(3299, 28, 47, 'I da ih ne snađe nesreća zbog onog šta su unaprijed poslale ruke njihove, pa reknu: "Gospodaru naš! Zašto nam nisi poslao poslanika, pa da slijedimo ajete Tvoje i budemo od vjernika?"'),
(3300, 28, 48, 'Pa pošto im je došla Istina od Nas, rekoše: "Zašto (mu) nije dato slično šta je dato Musau?" Zar nisu nevjerovali u ono šta je dato Musau od ranije? Rekoše: "Sihra dva, međusobno pomažuća", i rekoše: "Uistinu, mi smo u svako nevjernici."'),
(3301, 28, 49, 'Reci: "Pa donesite od Allaha Knjigu koja je bolja uputa od ove dvije, da bih je slijedio, ako istinu govorite."'),
(3302, 28, 50, 'Pa ako ti se ne odazovu, tad znaj da slijede strasti svoje. A ko je zabludjeliji od onog ko slijedi strast svoju bez ikakve upute od Allaha? Uistinu! Allah ne upućuje narod zalima.'),
(3303, 28, 51, 'I doista smo im dopremili Riječ, da bi se oni poučili.'),
(3304, 28, 52, 'Oni kojima smo dali Knjigu prije njega, oni u njega vjeruju.'),
(3305, 28, 53, 'I kad im se uči, govore: "Vjerujemo u njega. Uistinu, on je Istina od Gospodara našeg, uistinu, mi smo prije njega bili muslimani."'),
(3306, 28, 54, 'Takvima će se dati nagrada njihova dva puta, što su se strpili i dobrim djelom odbijali zlo, i iz onog čim smo ih opskrbili, udjeljivali;'),
(3307, 28, 55, 'I kad čuju besmislicu, okrenu se od nje i reknu: "Nama djela naša, a vama djela vaša. Selamun alejkum! Ne tražimo neznalice."'),
(3308, 28, 56, 'Uistinu, ti ne upućuješ koga voliš, nego Allah upućuje koga hoće, a On je Najbolji znalac upućenih.'),
(3309, 28, 57, 'I rekoše: "Ako s tobom budemo slijedili Uputu, bićemo izbačeni iz naše zemlje." A zar nismo za njih Harem učinili sigurnim?, skupljaju se njemu plodovi svake vrste - opskrba od Nas. Međutim, većina njih ne zna.'),
(3310, 28, 58, 'A koliko smo uništili naselja nezadovoljnih svojim sredstvima za život! Pa to su nastambe njihove, nisu nastanjivane poslije njih, izuzev malo, i bili smo Mi nasljednici.'),
(3311, 28, 59, 'I nije bio Gospodar tvoj uništilac gradova, dok ne bi podigao u prijestonici njihovoj poslanika, da im uči ajete Naše. I nismo bili Uništioci gradova, izuzev kad bi stanovnici njihovi bili zalimi.'),
(3312, 28, 60, 'A šta vam je dato od stvari, pa uživanje je života Dunjaa i ukras njegov; a što je kod Allaha, bolje je i trajnije. Pa zar ne razumijete?'),
(3313, 28, 61, 'Pa da li je onaj kome smo dali obećanje lijepo - ta on će ga susresti - kao onaj koga naslađujemo užitkom života Dunjaa, zatim će on na Dan kijameta biti od privedenih?'),
(3314, 28, 62, 'A na Dan kad ih pozove, pa rekne: "Gdje su ortaci Moji, oni za koje ste tvrdili?"'),
(3315, 28, 63, 'Reći će oni na kojima se obistinila Riječ: "Gospodaru naš! Ovo su ti koje smo zaveli. Zaveli smo njih, kao što smo sebe zaveli. Opravdavamo se Tebi: nisu nas obožavali."'),
(3316, 28, 64, 'I biće rečeno: "Pozovite ortake svoje!" Tad će ih pozvati, pa im neće odgovoriti, a vidjeće kaznu. Ah, da su se oni upućivali!'),
(3317, 28, 65, 'A na Dan kad ih pozove pa rekne: "Šta ste odgovorili poslanicima?"'),
(3318, 28, 66, 'Pa nejasne će im biti vijesti Toga dana, te se oni neće raspitivati.'),
(3319, 28, 67, 'Pa što se tiče onog ko se pokaje i bude vjerovao i radio dobro, pa možda da bude od uspjelih.'),
(3320, 28, 68, 'A Gospodar tvoj stvara šta hoće i odabire! Nema za njih izbora! Slava Allahu, i Uzvišen neka je od onog šta pridružuju!'),
(3321, 28, 69, 'A Gospodar tvoj zna šta kriju grudi njihove, i šta (oni) objelodanjuju.'),
(3322, 28, 70, 'I On je Allah - nema boga osim Njega! Njemu hvala u prvom i drugom, a Njegova je presuda, i Njemu se vraćate.'),
(3323, 28, 71, 'Reci: "Vidite li, ako vam Allah učini noć trajnom do Dana kijameta - koji će vam bog pored Allaha donijeti svjetlost? Pa zar ne čujete?"'),
(3324, 28, 72, 'Reci: "Vidite li, ako vam Allah učini dan trajnim do Dana kijameta - koji će vam bog pored Allaha donijeti noć, da se smirite u njoj? Pa zar ne vidite?"'),
(3325, 28, 73, 'I iz milosti Svoje, načinio je za vas noć i dan, da se odmarate u njoj, i da tražite od blagodati Njegove, i da biste vi zahvaljivali.'),
(3326, 28, 74, 'A na Dan kad ih pozove, pa rekne: "Gdje su ortaci Moji, za koje ste tvrdili?"'),
(3327, 28, 75, 'I iz svake zajednice ćemo istrgnuti svjedoka, pa ćemo reći: "Donesite dokaz vaš!" Tad će znati da je Istina Allahova, a nestaće od njih šta su izmišljali.'),
(3328, 28, 76, 'Uistinu, Karun je bio iz naroda Musaovog, pa ih je tlačio. A dali smo mu od blaga toliko, da bi uistinu ključevi njegovi oborili skupinu snažnih. Kad mu reče narod njegov: "Ne likuj! Uistinu, Allah ne voli one koji likuju.'),
(3329, 28, 77, 'I traži u onom šta ti je dao Allah kuću Ahireta, i ne zaboravi udio svoj od Dunjaa, i čini dobro kao što tebi Allah čini dobro, i ne traži fesad na Zemlji. Uistinu! Allah ne voli mufside."'),
(3330, 28, 78, 'Reče: "To mi je dato samo zbog mog znanja." Zar nije znao da je Allah već uništio prije njega od pokoljenja (onog) ko je od njega bio moćniji silom i veći skupom? A prestupnici neće biti pitani za svoje grijehe.'),
(3331, 28, 79, 'Tad je izlazio narodu svom u sjaju svom. Oni koji žele život Dunjaa govorili su: "O da nam je slično šta je dato Karunu! Uistinu, on je posjednik sreće goleme."'),
(3332, 28, 80, 'A oni kojima je dato znanje rekoše: "Teško vama! Allahova nagrada je bolja za onog ko vjeruje i radi dobro, a ona neće biti data, izuzev strpljivima."'),
(3333, 28, 81, 'Pa smo dali da njega i kuću njegovu proguta zemlja. Tad on nije imao nikakvu skupinu (da) ga pomognu mimo Allaha, i nije bio od onih koji se mogu odbraniti.'),
(3334, 28, 82, 'I osvanuše oni koji su željeli njegovo mjesto jučer, govoreći: "Ah, Allah pruža opskrbu kome hoće od robova Svojih i uskraćuje. Da nam nije Allah dobar bio, sigurno bi dao da nas proguta. Ah, ne uspijevaju nezahvalnici."'),
(3335, 28, 83, 'Ta kuća Ahireta - načinili smo je za one koji ne žele visinu na Zemlji, niti fesad. A ishod je za bogobojazne.'),
(3336, 28, 84, 'Ko dođe s dobrim djelom, pa imaće on bolje od njega; a ko dođe sa zlim djelom - pa oni koji su činili zlodjela biće plaćeni jedino (prema) onom šta su radili.'),
(3337, 28, 85, 'Uistinu! Onaj koji te je obavezao Kur\'anom, sigurno će te vratiti povratištu. Reci: "Gospodar moj je Najbolji znalac onog ko donosi Uputu i onog ko je u zabludi očitoj."'),
(3338, 28, 86, 'I nisi se nadao da će se tebi dati Knjiga, izuzev kao milost od Gospodara tvog. Zato nikako ne budi pomagač nevjernicima;'),
(3339, 28, 87, 'I neka te nikako ne odvrate od ajeta Allahovih, nakon što su ti objavljeni; i pozivaj Gospodaru svom, i ne budi nikako od mušrika;'),
(3340, 28, 88, 'I ne prizivaj uz Allaha boga drugog! Samo je On Bog! Svaka stvar je propadajuća, izuzev lica Njegovog! Njegov je sud i Njemu ćete biti vraćeni!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Alif. Lam. Mim.'),
(3342, 29, 2, 'Misle li ljudi da će biti ostavljeni, ako kažu: "Vjerujemo", i da neće biti iskušavani?'),
(3343, 29, 3, 'A doista smo iskušavali one prije njih. Pa sigurno će Allah znati one koji su istiniti i sigurno znati lašce.'),
(3344, 29, 4, 'Zar misle oni koji rade zlodjela, da će Nas preduhitriti? Zlo je to što prosuđuju!'),
(3345, 29, 5, 'Ko se nada susretu s Allahom, pa uistinu, rok Allahov je sigurno nastupajući, a On je Onaj koji čuje, Znalac.'),
(3346, 29, 6, 'A ko se bori, pa samo se bori za dušu svoju. Uistinu! Allah je Neovisan od svjetova.'),
(3347, 29, 7, 'A oni koji vjeruju i čine dobra djela, - sigurno ćemo im pokriti zla djela njihova, i sigurno ćemo ih nagraditi (prema) najboljem šta su radili.'),
(3348, 29, 8, 'I naredili smo čovjeku prema roditeljima njegovim dobro. A ako se budu s tobom borili, da bi ti Meni pridružio ono o čemu ti nemaš znanje, tad ih ne poslušaj. Meni je povratak vaš, pa ću vas obavijestiti o onom šta ste radili.'),
(3349, 29, 9, 'A oni koji vjeruju i čine dobra djela, sigurno ćemo ih uvesti među pravedne.'),
(3350, 29, 10, 'I od ljudi je ko kaže: "Vjerujemo u Allaha" - pa kad bude paćen radi Allaha, smatra kušnju ljudi kao kaznu Allahovu; a ako dođe pomoć od Gospodara tvog, sigurno će reći: "Uistinu, mi smo bili s vama." Zar nije Allah Najbolji znalac o onom što je u grudima svjetova?'),
(3351, 29, 11, 'A sigurno Allah zna one koji vjeruju i sigurno zna munafike.'),
(3352, 29, 12, 'I govore oni koji ne vjeruju, onima koji vjeruju: "Slijedite put naš i ponijećemo greške vaše!" A neće oni biti nosioci grešaka njihovih nimalo. Uistinu, oni su lažljivci.'),
(3353, 29, 13, 'A sigurno će nositi terete svoje i terete uz terete svoje, i sigurno će biti pitani na Dan kijameta o onom šta su izmišljali.'),
(3354, 29, 14, 'I doista smo poslali Nuha narodu njegovom, pa je ostao među njima hiljadu, izuzev pedeset godina, pa ih je zadesio potop, a oni su bili zalimi.'),
(3355, 29, 15, 'Pa smo spasili njega i saputnike na lađi i učinili je znakom svjetovima.'),
(3356, 29, 16, 'I Ibrahima, kad reče narodu svom: "Obožavajte Allaha i bojte se Njega; to je bolje za vas, kad biste znali.'),
(3357, 29, 17, 'Obožavate mimo Allaha jedino idole i izmišljate laž. Uistinu, oni koje obožavate mimo Allaha, ne vladaju vašom opskrbom. Zato kod Allaha tražite opskrbu i obožavajte Njega i zahvaljujte Mu. Njemu ćete biti vraćeni."'),
(3358, 29, 18, 'A ako budete poricali, pa doista su poricale zajednice prije vas. A na Poslaniku je jedino dostava jasna.'),
(3359, 29, 19, 'Zar ne vide kako Allah počinje stvaranje, potom ga ponavlja? Uistinu! To je Allahu lahko.'),
(3360, 29, 20, 'Reci: "Putujte po Zemlji pa vidite kako je počeo stvaranje, zatim će Allah stvoriti stvaranjem drugim. Uistinu! Allah nad svakom stvari ima moć.'),
(3361, 29, 21, 'Kažnjava koga hoće, a smiluje se kome hoće, a Njemu ćete biti vraćeni."'),
(3362, 29, 22, 'I niste vi ti koji će umaći u Zemlji, niti u nebesima. A nemate vi mimo Allaha nikakva zaštitnika, niti pomagača.'),
(3363, 29, 23, 'A oni koji ne vjeruju u ajete Allahove i susret s Njim, takvi se u očaju spram milosti Moje, a ti takvi će imati kaznu bolnu.'),
(3364, 29, 24, 'Pa je bio odgovor naroda njegovog, jedino što su rekli: "Ubijte ga", ili "Spalite ga." Tad ga je Allah spasio od vatre. Uistinu, u tome su znaci za ljude koji vjeruju.'),
(3365, 29, 25, 'I reče: "Mimo Allaha ste uzeli jedino idole ljubavlju među vama u životu Dunjaa, potom ćete na Dan kijameta poricati jedni druge i proklinjati jedni druge, a sklonište vaše biće vatra, i nećete vi imati nikakvih pomagača."'),
(3366, 29, 26, 'Tad njemu povjerova Lut, i reče: "Uistinu! Ja sam muhadžir Gospodaru svom. Uistinu, On, On je Moćni, Mudri."'),
(3367, 29, 27, 'I podarili smo njemu Ishaka i Jakuba, i dali potomstvu njegovom poslanstvo i Knjigu, i dali mu nagradu njegovu na Dunjau, a uistinu, on će na Ahiretu biti od pravednih.'),
(3368, 29, 28, 'I Luta, kad reče narodu svom: "Uistinu, vi činite razvrat u kojem vas nije pretekao niko od svjetova.'),
(3369, 29, 29, 'Zar vi uistinu pristupate ljudima i presjecate put, i činite u sastajalištu svom ružno? Tad je bio odgovor naroda njegovog samo što su rekli: "Daj nam kaznu Allahovu, ako si od istinitih!"'),
(3370, 29, 30, 'Reče: "Gospodaru moj! Pomozi me protiv naroda mufsida."'),
(3371, 29, 31, 'I pošto Naši izaslanici dođoše Ibrahimu s radosnom viješću, rekoše: "Uistinu, Mi ćemo uništiti stanovnike ovog grada, uistinu su stanovnici njegovi zalimi."'),
(3372, 29, 32, '(Ibrahim) reče: "Uistinu, u njemu je Lut." Rekoše: "Mi smo Najbolji znalac onog ko je u njemu. Sigurno ćemo spasiti njega i porodicu njegovu, izuzev žene njegove, biće od onih koji ostaju."'),
(3373, 29, 33, 'I pošto poslanici Naši dođoše Lutu, sneveseli se zbog njih i bi pritješnjen zbog njih, nemoćan, i rekoše: "Ne boj se i ne žalosti se! Uistinu, Mi smo Spasitelji tvoji i porodice tvoje, izuzev žene tvoje, biće od onih koji ostaju.'),
(3374, 29, 34, 'Uistinu, Mi smo ti koji će spustiti na stanovnike ovog grada kaznu s neba, zbog toga što su griješili."'),
(3375, 29, 35, 'I doista smo od njega ostavili znak jasan za ljude koji razmišljaju.'),
(3376, 29, 36, 'A Medjenu, brata njihovog Šuajba, pa reče: "O narode moj! Obožavajte Allaha i nadajte se Danu posljednjem, i ne pravite zlo na Zemlji, čineći fesad."'),
(3377, 29, 37, 'Tad su ga porekli, pa ih je zahvatio potres, i osvanuše u kući svojoj povaljani.'),
(3378, 29, 38, 'I Ada i Semuda! A doista vam je jasno iz nastamba njihovih. I uljepšao im je šejtan djela njihova, te ih odvratio s puta, a bili su pronicljivi.'),
(3379, 29, 39, 'I Karuna i faraona i Hamana! A doista im je donio Musa dokaze jasne, pa su se uzoholili na Zemlji, a nisu (Nas) mogli preteći.'),
(3380, 29, 40, 'Tad smo svakog dograbili zbog grijeha njegovog. Pa od njih je onaj na koga smo poslali pješčanu oluju, i od njih je onaj kojeg je pogodio krik, i od njih je onaj kojeg smo dali da zemlja proguta, i od njih je onaj kojeg smo potopili. A nije im Allah učinio zulm, nego su dušama svojim zulm činili.'),
(3381, 29, 41, 'Primjer onih koji uzimaju mimo Allaha zaštitnike, je kao primjer pauka koji isplete kuću. A uistinu - od najslabijih kuća je kuća paukova - kad bi znali!'),
(3382, 29, 42, 'Uistinu! Allah zna šta prizivaju mimo Njega od stvari. A On je Moćni, Mudri.'),
(3383, 29, 43, 'A to su primjeri, navodimo ih ljudima, i ne shvataju ih, izuzev učeni.'),
(3384, 29, 44, 'Stvorio je Allah nebesa i Zemlju s Istinom. Uistinu, u tome je znak za vjernike.'),
(3385, 29, 45, 'Recitiraj šta ti se objavljuje od Knjige i obavljaj salat. Uistinu, salat odvraća od razvrata i ružnog, a sigurno je spominjanje Allaha najveće; a Allah zna šta radite.'),
(3386, 29, 46, 'I ne raspravljajte sa sljedbenicima Knjige osim s onim šta je najbolje, izuzev onih od njih koji čine zulm, i recite: "Vjerujemo u ono šta je objavljeno nama i šta je objavljeno vama; a Bog naš i Bog vaš je Jedini, i mi smo Njemu predani."'),
(3387, 29, 47, 'I tako smo ti objavili Knjigu. Pa oni kojima smo dali Knjigu, vjeruju u nju; i od ovih je ko u nju vjeruje. A negiraju ajete Naše samo nevjernici.'),
(3388, 29, 48, 'I nisi prije nje recitirao nikakvu knjigu, niti si je pisao desnicom svojom. Tad bi posumnjali lažovi.'),
(3389, 29, 49, 'Naprotiv, to su znaci jasni u grudima onih kojima je dato znanje, a negiraju ajete Naše samo zalimi.'),
(3390, 29, 50, 'I govore: "Zašto mu se ne spuste znaci od Gospodara njegovog?" Reci: "Jedino su znaci kod Allaha, a ja sam samo opominjač jasni."'),
(3391, 29, 51, 'Zar im nije dovoljno što smo ti Mi objavili Knjigu - uči im se? Uistinu, u tome je milost i opomena za narod koji vjeruje.'),
(3392, 29, 52, 'Reci: "Dovoljan je Allah između mene i između vas Svjedok. Zna šta je u nebesima i Zemlji. A oni koji vjeruju u neistinu, a ne vjeruju u Allaha, ti takvi su gubitnici."'),
(3393, 29, 53, 'I požuruju te s kaznom; a da nije roka određenog, sigurno bi im došla kazna. I sigurno će im doći iznenadno, a oni neće opaziti.'),
(3394, 29, 54, 'Požuruju te s kaznom, a uistinu, Džehennem će biti okružilac nevjernika,'),
(3395, 29, 55, 'Na Dan kad ih prekrije kazna iznad njih i ispod nogu njihovih i bude rečeno: "Kušajte ono šta ste radili!"'),
(3396, 29, 56, 'O robovi Moji koji vjerujete! Uistinu, Zemlja Moja je prostrana, zato Mene - ta Mene obožavajte!'),
(3397, 29, 57, 'Svaka duša će iskusiti smrt, zatim ćete Nama biti povraćeni.'),
(3398, 29, 58, 'A oni koji vjeruju i rade dobra djela, sigurno ćemo ih smjestiti u džennetske odaje ispod kojih teku rijeke, vječno će biti u njima. Divna je nagrada radnika,'),
(3399, 29, 59, 'Onih koji se strpe i u Gospodara svog pouzdaju!'),
(3400, 29, 60, 'A koliko je živine koja opskrbu ne snosi? Allah opskrbljuje nju i vas. A On je Onaj koji čuje, Znalac.'),
(3401, 29, 61, 'A ako ih upitaš: "Ko je stvorio nebesa i Zemlju i potčinio Sunce i Mjesec?" - sigurno će reći: "Allah!" Pa kako se odvraćaju?'),
(3402, 29, 62, 'Allah pruža opskrbu kome hoće od robova Svojih, i uskraćuje mu. Uistinu! Allah je o svakoj stvari Znalac.'),
(3403, 29, 63, 'I ako ih upitaš: "Ko spušta s neba vodu, pa oživljava njome zemlju nakon mrtvila njenog?" - sigurno će reći: "Allah!" Reci: "Hvala Allahu!" Naprotiv, većina njih ne razumije.'),
(3404, 29, 64, 'A ovaj život Dunjaa je samo igra i zabava. A uistinu, kuća Ahireta - ona je život, kad bi znali!'),
(3405, 29, 65, 'Pa kad se ukrcaju na plovilo, zazivaju Allaha, odani Njegovom dinu. Pa pošto ih izbavi na kopno, gle! oni (Mu) pridružuju,'),
(3406, 29, 66, 'Da bi nezahvalni bili na onom šta smo im dali, i da bi uživali; pa saznaće!'),
(3407, 29, 67, 'Zar nisu vidjeli da smo Mi učinili Harem sigurnim, a kidnapuju se ljudi oko njih? Pa zar u neistinu vjeruju, a u blagodat Allahovu ne vjeruju?'),
(3408, 29, 68, 'A ko je nepravedniji od onog ko izmišlja laž protiv Allaha ili poriče Istinu, pošto mu dođe? Zar nije u Džehennemu boravište za nevjernike!'),
(3409, 29, 69, 'A oni koji se bore za Nas, sigurno ćemo ih uputiti putevima Našim. A uistinu, Allah je sa dobročiniteljima.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Alif. Lam. Mim.'),
(3411, 30, 2, 'Poraženi su Bizantinci,'),
(3412, 30, 3, 'U zemlji najbližoj, a oni će poslije poraza svog, pobijediti,'),
(3413, 30, 4, 'Za nekoliko godina - Allahova je naredba prije i poslije - a tog dana radovat će se vjernici,'),
(3414, 30, 5, 'S pomoći Allahovom. Pomaže koga hoće, a On je Moćni, Milosrdni.'),
(3415, 30, 6, 'Obećanje je Allahovo; neće Allah iznevjeriti obećanje Svoje, međutim, većina ljudi ne zna.'),
(3416, 30, 7, 'Znaju vanjštinu života Dunjaa, a oni su o Ahiretu nehajni.'),
(3417, 30, 8, 'Zar neće razmisliti u dušama svojim? Allah je stvorio nebesa i Zemlju i šta je između njih, samo s Istinom i rokom određenim. A uistinu, većina ljudi su u susret Gospodara svog nevjernici.'),
(3418, 30, 9, 'Zar nisu putovali po Zemlji, pa vidjeli kakav je bio kraj onih prije njih? Bili su moćniji od njih u sili i kopali su zemlju i gradili je više no što je izgrađuju, a dolazili su im poslanici njihovi s jasnim dokazima. Pa nije Allah učinio zulm njima, nego su dušama svojim zulm učinili.'),
(3419, 30, 10, 'Potom je zlo bilo kraj onih koji su činili zlo, zato što su poricali ajete Allahove i njima se izrugivali.'),
(3420, 30, 11, 'Allah je počeo stvaranje, zatim ga ponavlja, potom ćete Njemu biti vraćeni.'),
(3421, 30, 12, 'A na Dan kad nastupi ^as, očajavat će prestupnici.'),
(3422, 30, 13, 'I neće oni imati od ortaka svojih posrednike, i biće u ortake svoje nevjernici.'),
(3423, 30, 14, 'A na Dan kad nastupi ^as, Tog dana će se rastaviti.'),
(3424, 30, 15, 'Pa što se tiče onih koji budu vjerovali i dobra djela činili - pa oni će se u (bujnom) vrtu veseliti.'),
(3425, 30, 16, 'Pa što se tiče onih koji ne budu vjerovali i znakove Naše poricali, i susret Ahireta - pa takvi će biti u kaznu privedeni.'),
(3426, 30, 17, 'Pa slavljen neka je Allah kad zanoćite i kad osvanete -'),
(3427, 30, 18, 'I Njemu hvala na nebesima i Zemlji, i početkom noći i u podnevu kad ste!'),
(3428, 30, 19, 'Izvodi živo iz mrtvog i izvodi mrtvo iz živog. I oživljava zemlju poslije mrtvila njenog, a tako ćete biti izvedeni.'),
(3429, 30, 20, 'A od znakova Njegovih je što vas je stvorio od prašine, potom gle! vi smrtnici (koji) se širite!'),
(3430, 30, 21, 'I od znakova Njegovih je što je stvorio za vas od duša vaših parice, da se smirite uz njih, i uspostavio između vas ljubav i milost. Uistinu, u tome su znaci za ljude koji razmišljaju!'),
(3431, 30, 22, 'I od znakova Njegovih je stvaranje nebesa i Zemlje, i različitost jezika vaših i boja vaših. Uistinu, u tome su znaci za učene!'),
(3432, 30, 23, 'I od znakova Njegovih je spavanje vaše noću i danju i traženje vaše iz blagodati Njegove. Uistinu, u tome su znaci za ljude koji čuju!'),
(3433, 30, 24, 'I od znakova Njegovih je: pokazuje vam munju strahom i nadom, i spušta s neba vodu, pa oživljava njome zemlju nakon mrtvila njenog. Uistinu, u tome su znaci za ljude koji razumiju!'),
(3434, 30, 25, 'I od znakova Njegovih je što postoji nebo i Zemlja po naredbi Njegovoj. Zatim, kad vas pozove zovom iz Zemlje, gle! vi izlazite!'),
(3435, 30, 26, 'I Njegov je ko je na nebesima i Zemlji. Svi su Njemu pokorni.'),
(3436, 30, 27, 'I On je Taj koji počinje stvaranje, zatim ga ponavlja, a to je Njemu lakše. I Njegov primjer je najuzvišeniji na nebesima i Zemlji, a On je Moćni, Mudri.'),
(3437, 30, 28, 'Navodi vam primjer od duša vaših: imate li vi među onim šta posjeduju desnice vaše ikakve ortake u onom čim smo vas opskrbili, pa ste vi u tome isti? Bojite li se njih kao vašim strahom duša vaših? Tako izlažemo znakove ljudima koji shvataju.'),
(3438, 30, 29, 'Naprotiv, oni koji čine zulm, slijede strasti svoje bez znanja. Pa ko će uputiti onoga koga je zaveo Allah? A neće oni imati nikakvih pomagača.'),
(3439, 30, 30, 'Zato stani licem svojim vjeri pravoj, fitretu Allahovom, onom po kojem je stvorio ljude - nema promjene stvaranja Allahovog. To je vjera prava, ali većina ljudi ne zna.'),
(3440, 30, 31, 'Budite oni koji se obraćaju Njemu, i bojte Ga se i obavljajte salat i ne budite od mušrika,'),
(3441, 30, 32, 'Od onih koji su podijelili vjeru svoju i postali sekte. Svaka stranka se s onim što je kod njih, raduje.'),
(3442, 30, 33, 'A kad ljude dotakne nesreća, zovu Gospodara svog obraćajući Mu se pokajanički; zatim, kad oni okuse od Njega milost, gle! skupina njih Gospodaru svom pridružuje,'),
(3443, 30, 34, 'Da bi bili nezahvalni na onom šta smo im dali. "Pa uživajte, ta saznaćete!"'),
(3444, 30, 35, 'Zar smo im spustili dokaz, pa on govori o onom šta Mu pridružuju?'),
(3445, 30, 36, 'I kad damo da ljudi okuse milost, raduju se u njoj; a kad ih zadesi zlo zbog onog što su prethodno uradile ruke njihove, gle! oni očajavaju!'),
(3446, 30, 37, 'Zar nisu vidjeli da Allah pruža opskrbu kome hoće, i uskraćuje? Uistinu! U tome su znaci za ljude koji vjeruju.'),
(3447, 30, 38, 'Pa daj bližnjem pravo njegovo i siromahu i sinu puta. To je najbolje za one koji žele lice Allahovo, a ti takvi će biti uspješni.'),
(3448, 30, 39, 'A ono što ste dali od kamate da bi porasla u imecima ljudi - pa neće porasti kod Allaha; a ono što date od zekata želeći lice Allahovo - pa ti takvi će imati mnogostruko.'),
(3449, 30, 40, 'Allah je Taj koji vas je stvorio, zatim vas opskrbio, potom će vas usmrtiti, zatim će vas oživiti. Ima li iko od ortaka vaših koji čini išta od toga? Slava Njemu! I uzvišen neka je od onog šta pridružuju!'),
(3450, 30, 41, 'Pojavio se nered na kopnu i moru zbog onog što čine ruke ljudi, da bi dao da oni iskuse dio onog šta su uradili, ne bi li se oni povratili.'),
(3451, 30, 42, 'Reci: "Putujte po Zemlji pa vidite kakav je bio kraj onih od ranije! Bili su većina njih mušrici."'),
(3452, 30, 43, 'Zato stani licem svojim vjeri ispravnoj, prije no dođe od Allaha Dan kojem nema odbijanja. Tog dana će se razdvojiti.'),
(3453, 30, 44, 'Ko ne vjeruje, pa protiv njega je kufr njegov, a ko radi dobro, pa dušama svojim pripremaju,'),
(3454, 30, 45, 'Da bi nagradio one koji vjeruju i rade dobra djela iz dobrote Svoje. Uistinu! On ne voli nevjernike.'),
(3455, 30, 46, 'I od znakova Njegovih je što šalje vjetrove (kao) radosne vijesti, i da vi kušate iz milosti Njegove, i da plovi plovilo po naredbi Njegovoj, i da tražite iz blagodati Njegove, i da biste vi zahvaljivali.'),
(3456, 30, 47, 'A doista, slali smo prije tebe poslanike narodu njihovom, pa su im dolazili s jasnim dokazima, te smo se osvetili onima koji su skrivili; a pomaganje vjernicima bila je na Nama obaveza.'),
(3457, 30, 48, 'Allah je Taj koji šalje vjetrove pa dižu oblak, te ga rasprostire u nebu kako hoće i čini ga komadima; tad vidiš kišu izlazi iz sred njega! Pa kad pogodi njome koga hoće od robova Svojih, gle! oni se vesele,'),
(3458, 30, 49, 'Iako su bili prije no što je spuštena na njih - prije toga - očajnici.'),
(3459, 30, 50, 'Zato pogledaj tragove milosti Allahove, kako oživljava zemlju nakon mrtvila njenog. Uistinu! To je Oživljavač mrtvih, a On nad svakom stvari ima moć!'),
(3460, 30, 51, 'I da pošaljemo vjetar, pa ga vide žutim, sigurno bi poslije toga nastavili da ne vjeruju.'),
(3461, 30, 52, 'Pa uistinu, ti nećeš pročuti mrtve, niti pročuti gluhe dozivanjem, kad se okrenu bježeći.'),
(3462, 30, 53, 'I nisi ti vodič slijepcima iz zablude njihove. Pročut ćeš jedino onog ko vjeruje u ajete Naše - ta oni su muslimani.'),
(3463, 30, 54, 'Allah je Taj koji vas je stvorio od slabog, zatim je poslije slabosti snagu načinio, potom je poslije snage slabost načinio i sijedu kosu. Stvara šta hoće, a On je Znalac, Moćni.'),
(3464, 30, 55, 'A na Dan kad nastupi ^as, zaklinjaće se prestupnici da nisu ostali sem čas; tako su se oni odvratili.'),
(3465, 30, 56, 'I reći će oni kojima je dato znanje i vjerovanje: "Doista ste ostali po Knjizi Allahovoj do Dana podizanja, ta ovo je Dan podizanja, ali vi niste znali!"'),
(3466, 30, 57, 'Pa Tog dana neće koristiti onima koji su činili zulm, pravdanje njihovo, niti će im se dati da traže naklonost.'),
(3467, 30, 58, 'I doista, naveli smo ljudima u ovom Kur\'anu od svakog primjera (ponešto); a da si im donio znak, sigurno bi rekli oni koji ne vjeruju: "Vi ste samo oni koji iznose neistinu!"'),
(3468, 30, 59, 'Tako pečati Allah srca onih koji ne znaju.'),
(3469, 30, 60, 'Zato se strpi! Uistinu, obećanje Allahovo je Istina, i neka te nikako ne smatraju lahkim oni koji nisu uvjereni.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Alif. Lam. Mim.'),
(3471, 31, 2, 'To su znaci Kitaba mudrog,'),
(3472, 31, 3, 'Upute i milosti za dobročinitelje,'),
(3473, 31, 4, 'One koji obavljaju salat i daju zekat, a oni su u Ahiret, oni uvjereni.'),
(3474, 31, 5, 'Takvi su na Uputi Gospodara svog, a ti takvi su uspješni.'),
(3475, 31, 6, 'I od ljudi je ko kupuje priču za zabavu, da bi zavodio s puta Allahovog bez ikakva znanja i uzimao ga ruglom. Ti takvi će imati kaznu prezrenu.'),
(3476, 31, 7, 'A kad mu se uče ajeti Naši, okrene se oholo kao da ih nije čuo, kao da je u oba njegova uha gluhoća. Zato ga obraduj kaznom bolnom.'),
(3477, 31, 8, 'Uistinu, oni koji vjeruju i čine dobra djela, imaće oni bašče uživanja,'),
(3478, 31, 9, 'Vječno će biti u njima. Obećanje Allahovo je istinito; a On je Moćni, Mudri.'),
(3479, 31, 10, 'Stvorio je nebesa bez stubova koje vidite, i postavio po Zemlji planine stabilne da vas ne trese, i razasuo po njoj svakovrsne životinje. I spustili smo s neba vodu, pa učinili da na njoj iznikne (bilje) svake vrste plemenite.'),
(3480, 31, 11, 'Ovo je stvaranje Allahovo. Pa pokažite Mi šta su to stvorili oni mimo Njega! Naprotiv, zalimi su u zabludi očitoj.'),
(3481, 31, 12, 'I doista, dali smo Lukmanu mudrost: "Zahvaljuj Allahu! A ko zahvaljuje - pa samo zahvaljuje za dušu svoju. A ko ne zahvaljuje - pa uistinu, Allah je Neovisni, Hvaljeni."'),
(3482, 31, 13, 'I kad reče Lukman sinu svom - a on ga je savjetovao: "O sinčiću moj! Ne pridružuj Allahu. Uistinu, širk je zulm strašan!"'),
(3483, 31, 14, 'I wassijetili smo čovjeku za roditelje njegove - nosila ga je majka njegova slabošću nad slabosti, a odbijanje njegovo je u dvije godine - "Zahvaljuj Meni i roditeljima svojim, Meni je dolazište!"'),
(3484, 31, 15, 'A ako se bore s tobom da Mi pridružiš ono o čemu ti nemaš znanje, tad ne poslušaj njih dvoje. I druži se s njima na Dunjau uljudno, i slijedi put onog ko Mi se obraća. Zatim je Meni povratak vaš, pa ću vas obavijestiti o onom šta ste radili.'),
(3485, 31, 16, '"O sinčiću moj! Uistinu! Bilo to težine zrna gorušice, u stijeni ili u nebesima ili u Zemlji, iznijeće ga Allah. Uistinu! On je Fini, Obaviješteni.'),
(3486, 31, 17, 'O sinčiću moj! Obavljaj salat i naređuj dobro, a zabranjuj nevaljalo, i strpi se nad onim šta te zadesi. Uistinu, to su odlučujuće stvari.'),
(3487, 31, 18, 'I ne iskrivljuj obraz svoj ljudima, i ne hodaj po Zemlji likujući. Uistinu! Allah ne voli nijednog varalicu, hvalisavca.'),
(3488, 31, 19, 'I budi umjeren u hodu svom i spusti glas svoj. Uistinu, najružniji glasovi su njakanje magaraca."'),
(3489, 31, 20, 'Zar ne vidite da vam je Allah potčinio šta je na nebesima i šta je na Zemlji, i upotpunio vam blagodati Svoje svana i iznutra? A od ljudi je ko raspravlja o Allahu bez znanja i bez Upute, i bez Knjige osvjetljavajuće.'),
(3490, 31, 21, 'A kad im se kaže: "Slijedite ono šta je objavio Allah", govore: "Naprotiv, slijedimo ono na čemu smo našli očeve naše." Zar i kad ih šejtan poziva kazni seira?'),
(3491, 31, 22, 'A ko preda lice svoje Allahu i on bude dobročinitelj, pa doista se uhvatio za hvatište najčvršće. A Allahov je konac stvari.'),
(3492, 31, 23, 'A ko ne vjeruje, pa nek te ne žalosti nevjerovanje njegovo. Nama je povratak njihov, pa ćemo ih obavijestiti o onom šta su radili. Uistinu! Allah je Znalac grudi.'),
(3493, 31, 24, 'Dajemo im da uživaju malo, zatim ćemo ih natjerati kazni gruboj.'),
(3494, 31, 25, 'A ako ih upitaš: "Ko je stvorio nebesa i Zemlju?" - sigurno će reći: "Allah." Reci: "Hvala neka je Allahu!" Naprotiv, većina njih ne zna.'),
(3495, 31, 26, 'Allahovo je šta je na nebesima i Zemlji. Uistinu! Allah! On je Neovisni, Hvaljeni.'),
(3496, 31, 27, 'A da je svako drvo na Zemlji (pretvoreno u) pera, a more - da se ono produži nakon (iscrpljenja) njegovog za sedam mora - ne bi se iscrpile Riječi Allahove. Uistinu! Allah je Moćni, Mudri.'),
(3497, 31, 28, 'Stvaranje vaše i podizanje vaše (iz mrtvih) je samo - kao duše jedne. Uistinu! Allah je Onaj koji čuje, Onaj koji vidi.'),
(3498, 31, 29, 'Zar ne vidiš da Allah daje da noć uđe u dan i daje da dan uđe u noć, a potčinio je Sunce i Mjesec, svakoje plovi roku određenom - i da je Allah o onom šta radite Obaviješteni?'),
(3499, 31, 30, 'To zato što je Allah - On Istina, i što je ono što prizivaju mimo Njega, neistina, i što je Allah, On Uzvišeni, Veliki.'),
(3500, 31, 31, 'Zar ne vidiš da plovilo plovi morem s blagodati Allahovom? - da bi vam pokazao neke znakove Svoje. Uistinu, u tome su znaci za svakog strpljivog, zahvalnog.'),
(3501, 31, 32, 'I kad ih prekrije talas kao nadstrešice, prizivaju Allaha odani njegovoj vjeri. Pa pošto ih izbavi na kopno, tad je neki od njih osrednji. A negira znakove Naše jedino svaki podmukli, nezahvalni.'),
(3502, 31, 33, 'O ljudi! Bojte se Gospodara svog i plašite se Dana kad neće (moći) platiti roditelj za dijete svoje, niti dijete - ono neće biti onaj koji plaća za roditelja svog nimalo. Uistinu, obećanje Allahovo je Istina. Zato, neka vas nikako ne obmane život Dunjaa, i neka vas nikako o Allahu ne obmane garur.'),
(3503, 31, 34, 'Uistinu! Allah! Kod Njega je znanje ^asa. A spušta kišu i zna šta je u matericama. A ne zna duša šta će zaraditi sutra, i ne zna duša u kojoj zemlji će umrijeti. Uistinu, Allah je Znalac, Obaviješteni!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Alif. Lam. Mim.'),
(3505, 32, 2, 'Objava Knjige je - nema sumnje u to - od Gospodara svjetova.'),
(3506, 32, 3, 'Zar govore: "Izmislio ju je?" Naprotiv, ona je Istina od Gospodara tvog, da opominješ narod kojem nije došao nikakav opominjač prije tebe, da bi se oni uputili.'),
(3507, 32, 4, 'Allah je Taj koji je stvorio nebesa i Zemlju i ono što je između njih u šest dana, zatim se postavio na Arš. Nemate vi mimo Njega nikakva zaštitnika, niti posrednika. Pa zar se nećete poučiti?'),
(3508, 32, 5, 'Upravlja stvar od neba ka Zemlji, zatim se uspinje Njemu u danu čija je mjera hiljadu godina od onog šta računate.'),
(3509, 32, 6, 'To je Znalac nevidljivog i vidljivog, Moćni, Milosrdni,'),
(3510, 32, 7, 'Koji je uljepšao svaku stvar, stvorio je i počeo stvaranje čovjeka od ilovače;'),
(3511, 32, 8, 'Zatim je načinio potomstvo njegovo od ekstrakta tekućine prezrene,'),
(3512, 32, 9, 'Potom ga uredio i udahnuo u njega od Duha Svog; i načinio vam sluh i vidove i srca. Malo što zahvaljujete!'),
(3513, 32, 10, 'I govore: "Zar ćemo kad se izgubimo u zemlji, uistinu mi biti u stvaranju novom?" Naprotiv, oni su u susret Gospodara svog nevjernici!'),
(3514, 32, 11, 'Reci: "Uzeće vas melek smrti koji vam je određen, zatim ćete Gospodaru svome biti vraćeni."'),
(3515, 32, 12, 'A da vidiš kad prestupnici objese glave svoje kod Gospodara svog: "Gospodaru naš! Vidjeli smo i čuli, zato nas povrati: radićemo dobro. Uistinu, mi smo uvjereni."'),
(3516, 32, 13, 'A kad bismo htjeli, dali bismo sigurno svakoj duši uputu njenu, ali, obistinila se Riječ od Mene: "Sigurno ću napuniti Džehennem džinnima i ljudima zajedno."'),
(3517, 32, 14, 'Zato kušajte što ste zaboravili na susret ovog dana svog. Uistinu, Mi smo vas zaboravili; i iskusite kaznu vječnosti zbog onog šta ste radili.'),
(3518, 32, 15, 'Jedino u Naše znakove vjeruju oni koji - kad se opomenu njima, padaju na sedždu i slave sa hvalom Gospodara svog, a oni se ne ohole.'),
(3519, 32, 16, 'Lišavaju se bokovi njihovi postelja, zazivaju Gospodara svog iz straha i nade, i od onog čim smo ih opskrbili, udjeljuju.'),
(3520, 32, 17, 'Pa ne zna duša šta je za njih skriveno od osvježenja očiju, plaća za ono šta su radili.'),
(3521, 32, 18, 'Pa zar je ko je vjernik, kao onaj ko je grješnik? Nisu isti!'),
(3522, 32, 19, 'Što se tiče onih koji vjeruju i čine dobra djela, ta imaće oni bašče skloništem. Ugošćenje za ono šta su radili.'),
(3523, 32, 20, 'A što se tiče onih koji su griješili, pa sklonište njihovo biće vatra. Kad god zažele da izađu iz nje, biće vraćeni u nju, i biće im rečeno: "Iskusite kaznu vatrom, onu koju ste poricali."'),
(3524, 32, 21, 'I sigurno ćemo dati da oni kušaju kaznu manju, pred kaznu veću, da bi se oni povratili.'),
(3525, 32, 22, 'A ko je nepravedniji od onog ko se opomene ajetima Gospodara svog, potom se od njih odvrati? Uistinu! Mi smo prestupnicima Osvetnici.'),
(3526, 32, 23, 'I doista, dali smo Musau Knjigu. Zato ne budi u sumnji o susretu njegovom, i učinili je Uputom sinovima Israilovim.'),
(3527, 32, 24, 'I učinili smo od njih imame: upućivali su po naredbi Našoj, pošto su bili strpljivi i u ajete Naše uvjereni.'),
(3528, 32, 25, 'Uistinu, Gospodar tvoj - On će presuditi među njima na Dan kijameta o onom u čemu su se razilazili.'),
(3529, 32, 26, 'Zar ih ne upućuje (to) koliko smo prije njih uništili naraštaja - hodaju po nastambama njihovim? Uistinu! U tome su znaci! Pa zar neće oslušnuti?'),
(3530, 32, 27, 'Zar ne vide da Mi tjeramo vodu ka zemlji jalovoj, pa izvodimo njome usjev - jedu od njega, stoka njihova i duše njihove? Pa zar neće osmotriti?'),
(3531, 32, 28, 'I govore: "Kad će ova pobjeda, ako ste istiniti?"'),
(3532, 32, 29, 'Reci: "Na Dan pobjede neće koristiti onima koji nisu vjerovali, vjerovanje njihovo, niti će im se odgoditi.'),
(3533, 32, 30, 'Zato se okreni od njih i iščekuj, uistinu, oni su čekaoci.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'O vjerovjesniče! Boj se Allaha i ne poslušaj nevjernike i munafike. Uistinu! Allah je Znalac, Mudri.'),
(3535, 33, 2, 'I slijedi šta ti se objavljuje od Gospodara tvog. Uistinu, Allah je o onom šta radite Obaviješteni;'),
(3536, 33, 3, 'I pouzdaj se u Allaha, a dovoljan je Allah Zaštitnik.'),
(3537, 33, 4, 'Nije načinio Allah nikakva dva srca čovjeku u utrobi njegovoj. I nije učinio žene vaše od kojih se razvodite ziharom, majkama vašim, i nije učinio posinke vaše, sinovima vašim. To su riječi vaše - usta vaših; a Allah govori Istinu i On upućuje putu.'),
(3538, 33, 5, 'Zovite ih po očevima njihovim, to je pravednije kod Allaha. Pa ako ne znate očeve njihove, pa braća su vaša u vjeri i štićenici vaši. I nije vam grijeh u onom u čemu pogriješite, već šta namjerno učine srca vaša. A Allah je Oprosnik, Milosrdni.'),
(3539, 33, 6, 'Vjerovjesnik je bliži vjernicima od duša njihovih, a žene njegove su (kao) majke njihove. A srodnici su bliži jedni drugima u Knjizi Allahovoj, od vjernika i muhadžira, izuzev da učinite prijateljima svojim dobro. To je u Knjizi zapisano.'),
(3540, 33, 7, 'I kad smo od vjerovjesnika uzeli zavjet njihov, i od tebe, i od Nuha i Ibrahima i Musaa i Isaa, sina Merjeminog. A uzeli smo od njih zavjet čvrst;'),
(3541, 33, 8, 'Da bi upitao istinite o istinitosti njihovoj, a pripremio je za nevjernike kaznu bolnu.'),
(3542, 33, 9, 'O vi koji vjerujete! Sjetite se blagodati Allahove prema vama, kad vam dođoše vojske, pa poslasmo na njih vjetar i vojske koje niste vidjeli! A Allah je onog šta radite Vidilac.'),
(3543, 33, 10, 'Kad vam dođoše iznad vas i ispod vas, i kad skrenuše pogledi, i dospješe srca grlima - a mislili ste o Allahu misli (različite) -'),
(3544, 33, 11, 'Tu su iskušani vjernici i potreseni potresom žestokim.'),
(3545, 33, 12, 'I kad rekoše munafici i oni u srcima čijim je bolest: "Obećao nam je Allah i Poslanik Njegov jedino obmanu",'),
(3546, 33, 13, 'I kad skupina njih reče: "O stanovnici Jesriba! Nema mjesta za vas, zato se vratite." A tražila je grupa njih dozvolu od Vjerovjesnika, govorili su: "Uistinu, kuće naše su izložene." A nisu one izložene. Željeli su jedino bježanje.'),
(3547, 33, 14, 'A da se ušlo protiv njih sa strana njenih, zatim bio od njih zatražen fitneluk, sigurno bi ga počinili, a ostali bi u njoj samo kratko.'),
(3548, 33, 15, 'I već su se bili obavezali Allahu od ranije da neće okretati leđa; a biće se za obavezu Allahu pitano.'),
(3549, 33, 16, 'Reci: "Neće vam koristiti bježanje, ako bježite od smrti ili pogibije, i tad nećete uživati, izuzev malo."'),
(3550, 33, 17, 'Reci: "Ko je taj ko će vas zaštititi od Allaha, ako vam želi zlo, ili vam želi milost?" A neće oni naći sebi mimo Allaha zaštitnika, niti pomagača.'),
(3551, 33, 18, 'Doista Allah zna one među vama koji sprečavaju, i one koji su govorili braći svojoj: "Hodite k nama!" A nisu išli sili, izuzev malo,'),
(3552, 33, 19, '[krti prema vama. Pa kad dođe strah, vidiš ih gledaju prema tebi, okreću oči njihove kao onaj koji se onesvijesti od smrti. Pa kad strah ode peckaju vas jezicima oštrim, pohlepni prema dobru. Takvi ne vjeruju, zato će Allah poništiti djela njihova, a to je Allahu lahko.'),
(3553, 33, 20, 'Računaju da saveznici nisu otišli; a ako bi došli saveznici voljeli bi oni da su u pustinji među beduinima, tražili bi vijesti o vama; a da su s vama, ne bi se borili, izuzev malo.'),
(3554, 33, 21, 'Doista vi imate u Poslaniku Allahovom uzor lijep - za onog ko se nada Allahu i Danu posljednjem, i spominje Allaha mnogo.'),
(3555, 33, 22, 'I pošto su vjernici vidjeli saveznike, rekoše: "Ovo je ono šta nam je obećao Allah i Poslanik Njegov, a Istinu je govorio Allah i Poslanik Njegov", a povećalo im je jedino vjerovanje i predanost.'),
(3556, 33, 23, 'Od vjernika su ljudi (koji) su obistinili ono na što su se zavjetovali Allahu. Pa od njih je ko je ispunio zavjet svoj, i od njih je ko očekuje, i nisu učinili promjenu,'),
(3557, 33, 24, 'Da bi nagradio Allah istinite za istinitost njihovu, i kaznio munafike ako hoće, ili im oprostio. Uistinu! Allah je Oprosnik, Milosrdni.'),
(3558, 33, 25, 'I povratio je Allah one koji nisu vjerovali u srdžbi njihovoj; nisu postigli dobro. A dovoljan je Allah vjernicima u borbi, i Allah je Silni, Moćni;'),
(3559, 33, 26, 'I spustio je one od sljedbenika Knjige koji su ih pomagali, iz utvrda njihovih, i ubacio u srca njihova strah. Skupinu ste pobili, a skupinu zarobili.'),
(3560, 33, 27, 'I dao vam je da naslijedite zemlju njihovu i kuće njihove i imetke njihove i zemlju koju niste gazili. A Allah nad svakom stvari ima moć.'),
(3561, 33, 28, 'O Vjerovjesniče! Reci ženama svojim: "Ako želite život Dunjaa i sjaj njegov, tad dođite! Daću vam zadovoljštinu i pustiti vas puštanjem lijepim."'),
(3562, 33, 29, 'A ako želite Allaha i Poslanika Njegovog i kuću Ahireta - pa uistinu, Allah je za dobročiniteljke od vas pripremio nagradu veličanstvenu.'),
(3563, 33, 30, 'O žene Vjerovjesnikove! Ko od vas počini razvrat otvoren, uvećaće joj se kazna dvostruko; a to je Allahu lahko.'),
(3564, 33, 31, 'A koja se od vas pokori Allahu i Poslaniku Njegovom i čini dobro, daćemo joj nagradu njenu dva puta, a pripremili smo joj opskrbu plemenitu.'),
(3565, 33, 32, 'O žene Vjerovjesnikove! Niste kao neke od žena. Ako ste bogobojazne, onda ne budite podložne u govoru pa da imadne želju onaj u srcu čijem je bolest, i govorite riječ pristojnu.'),
(3566, 33, 33, 'I ostajte u kućama svojim, i ne pokazujte draži pokazivanjem džahilijjeta pređašnjeg; i obavljajte salat i dajite zekat, i poslušajte Allaha i Poslanika Njegovog. Doista Allah želi da odstrani od vas nečistoću Ehlul-bejte, i očisti vas (potpunim) čišćenjem.'),
(3567, 33, 34, 'I pamtite šta se uči u kućama vašim od ajeta Allahovih i mudrosti. Uistinu! Allah je Fini, Obaviješteni.'),
(3568, 33, 35, 'Uistinu, muslimani i muslimanke, i vjernici i vjernice, i poslušni i poslušne, i iskreni i iskrene, i strpljivi i strpljive, i ponizni i ponizne, i davaoci sadake i davateljke sadake, i postači i postačice, i čuvari furudža svojih i čuvarke, i koji spominju Allaha mnogo i koje spominju - pripremio je Allah njima oprost i nagradu veličanstvenu.'),
(3569, 33, 36, 'I nije za vjernika niti za vjernicu, kad odluči Allah i Poslanik Njegov stvar, da oni imaju izbor iz stvari svoje. A ko ne posluša Allaha i Poslanika Njegovog - pa zaista je zalutao zabludom očitom.'),
(3570, 33, 37, 'I kad si rekao onome koga je obdario Allah, i koga si obdario: "Zadrži sebi ženu svoju i boj se Allaha!" A krio si u duši svojoj ono šta je Allah pokazao i bojao se ljudi, a Allah je dostojniji da se Njega bojiš. Pa pošto je izvršio Zejd za nju potrebno, vjenčali smo nju za te, da ne bude za vjernike grijeh u ženama posinaka njihovih, kad izvrše za njih potrebno. A naredba Allahova je izvršna.'),
(3571, 33, 38, 'Nema Vjerovjesniku nikakvog prijekora u onom čim ga je obavezao Allah. (To) je sunnet Allahov onih koji su minuli ranije - a naredba Allahova je sudbina predodređena -'),
(3572, 33, 39, 'Onih koji su dostavljali poslanice Allahove i Njega se bojali, i nebojali se nikog izuzev Allaha. A dovoljan je Allah Obračunac.'),
(3573, 33, 40, 'Nije Muhammed otac nikom od ljudi vaših, nego je poslanik Allahov i Pečat vjerovjesnika! A Allah je o svakoj stvari Znalac.'),
(3574, 33, 41, 'O vi koji vjerujete! Spominjite Allaha zikrom mnogim,'),
(3575, 33, 42, 'I slavite Ga jutrom i navečer.'),
(3576, 33, 43, 'On je Taj koji vas blagosilja i meleci Njegovi - da bi vas izveo iz tmina na svjetlo; a za vjernike je Milosrdan.'),
(3577, 33, 44, 'Pozdrav njihov, na Dan kad Ga sretnu, biće: "Selam!" A pripremio im je nagradu plemenitu.'),
(3578, 33, 45, 'O Vjerovjesniče! Uistinu, Mi smo te poslali (kao) svjedoka i donosioca radosnih vijesti i opominjača,'),
(3579, 33, 46, 'I pozivača Allahu s dozvolom Njegovom i (kao) svjetiljku rasvjetljavajuću.'),
(3580, 33, 47, 'I obraduj vjernike, da će oni imati od Allaha blagodat veliku.'),
(3581, 33, 48, 'I ne poslušaj nevjernike i munafike i pusti vrijeđanje njihovo, a pouzdaj se u Allaha; a Allah je dovoljan Zaštitnik.'),
(3582, 33, 49, 'O vi koji vjerujete! Kad se oženite vjernicama, zatim ih razvedete prije nego ih dotaknete, tad vi nemate prema njima nikakva iddeta kojeg biste računali. Tad im dajte zadovoljštinu i pustite ih puštanjem lijepim.'),
(3583, 33, 50, 'O Vjerovjesniče! Uistinu, Mi smo ti dozvolili žene tvoje kojima si dao darove njihove, i one kojima vlada desnica tvoja od onog šta ti je kao plijen dao Allah, i kćeri amidže tvog i kćeri tetaka tvojih (po ocu), i kćeri daidže tvoga i kćeri tetaka tvojih (po materi) koje su izbjegle s tobom, i ženu vjernicu ako dušu svoju daruje Vjerovjesniku, ukoliko Vjerovjesnik želi da se oženi njome - privilegija za tebe, mimo vjernika. Doista znamo šta smo im stavili u obavezu o ženama njihovim i onom šta posjeduju desnice njihove, da tebi ne bude grijeh. A Allah je Oprosnik, Milosrdni.'),
(3584, 33, 51, 'Odstranjuješ koga hoćeš od njih, a uzimaš sebi koga hoćeš, a koga zatražiš od onih koje si udaljio - pa nema ti grijeha. To je najpodesnije da budu hladne oči njihove i da se ne žaloste, i zadovolje onim šta si im dao, svaka od njih. A Allah zna šta je u srcima vašim, i Allah je Znalac, Blagi.'),
(3585, 33, 52, 'Nisu tebi dozvoljene žene od sada, niti da ih mijenjaš ikakvim ženama, makar te zadivila ljepota njihova, izuzev onog što posjeduje desnica tvoja. A Allah je nad svakom stvari Nadzornik.'),
(3586, 33, 53, 'O vi koji vjerujete! Ne ulazite u kuće Vjerovjesnika, izuzev kad vam se dozvoli radi jela, ne čekajući spremanje njegovo. Međutim, kad se pozovete, tad uđite, pa kad jedete tad se raziđite bez slušanja razgovora. Uistinu, to smeta Vjerovjesniku, pa vas se stidi; a Allah se ne stidi Istine. I kad od njih tražite predmet, tad ih pitajte iza zavjese. To je čišće za srca vaša i srca njihova. I nije za vas da uznemiravate Poslanika Allahovog niti da poslije njega oženite žene njegove nikada. Uistinu, to je kod Allaha krupno.'),
(3587, 33, 54, 'Ako otkrijete stvar ili je sakrijete - pa uistinu, Allah je o svakoj stvari Znalac.'),
(3588, 33, 55, 'Nije im grijeh pred očevima njihovim i sinovima njihovim, niti braćom njihovom, niti sinovima braće njihove, niti sinovima sestara njihovih, niti ženama njihovim, niti onim šta posjeduju desnice njihove. I bojte se Allaha (o žene). Uistinu, Allah je nad svakom stvari Svjedok!'),
(3589, 33, 56, 'Uistinu! Allah i meleci Njegovi donose salawat na Vjerovjesnika. O vi koji vjerujete! Donosite salawat na njega i selamite predano.'),
(3590, 33, 57, 'Uistinu, one koji vrijeđaju Allaha i Poslanika Njegovog, prokleo ih je Allah na Dunjau i Ahiretu i pripremio za njih kaznu prezrenu.'),
(3591, 33, 58, 'A oni koji vrijeđaju vjernike i vjernice bez ikakve zasluge, pa doista se opterećuju klevetom i grijehom očitim.'),
(3592, 33, 59, 'O vjerovjesniče! Reci ženama svojim i kćerima svojim i ženama vjernika, da spuste niza se dželabibe svoje. To je najpodesnije da se prepoznaju, te ne uznemiravaju. A Allah je Oprosnik, Milosrdni.'),
(3593, 33, 60, 'Ako se ne okanu munafici i oni u srcima čijim je bolest, i agitatori u Medini, sigurno ćemo te podstaći protiv njih, zatim neće biti tvoje komšije u njoj, izuzev malo.'),
(3594, 33, 61, 'Prokletnici! Gdje god se nađu biće ščepani i skroz pobijeni.'),
(3595, 33, 62, 'Sunnet je (to) Allahov kod onih koji su nestali ranije, a nećeš naći u sunnetu Allahovom promjenu.'),
(3596, 33, 63, 'Pitaju te ljudi o Času. Reci: "Znanje o njemu je jedino kod Allaha." A šta znaš ti? Možda je Čas blizu!'),
(3597, 33, 64, 'Uistinu! Allah je prokleo nevjernike i pripremio za njih seir.'),
(3598, 33, 65, 'Vječno će biti u njemu, zauvijek; neće naći zaštitnika, niti pomagača.'),
(3599, 33, 66, 'Na Dan kad se lica njihova budu prevrtala u vatri, govoriće: "O da smo poslušali Allaha i poslušali Poslanika!"'),
(3600, 33, 67, 'I govoriće: "Gospodaru naš! Uistinu, mi smo poslušali poglavare naše i velikane naše, pa su nas zaveli s puta.'),
(3601, 33, 68, 'Gospodaru naš! Daj im dvostruku kaznu i prokuni ih prokletstvom velikim."'),
(3602, 33, 69, 'O vi koji vjerujete! Ne budite kao oni koji su uznemiravali Musaa, pa ga je Allah oslobodio onog šta su govorili, a kod Allaha je ugledan.'),
(3603, 33, 70, 'O vi koji vjerujete! Bojte se Allaha i govorite riječ pravu;'),
(3604, 33, 71, 'Popraviće vam djela vaša i oprostiti vam grijehe vaše. A ko posluša Allaha i Poslanika Njegovog - pa zaista je postigao uspjeh veličanstven.'),
(3605, 33, 72, 'Uistinu! Mi smo ponudili emanet nebesima i Zemlji i planinama, pa su se sustegli da ga ponesu i pobojali su ga se, a ponio ga je čovjek. Uistinu, on je uveliko zalim, neznalica -'),
(3606, 33, 73, 'Da bi kaznio Allah munafike i munafikinje i mušrike i mušrikinje, i oprostio Allah vjernicima i vjernicama. A Allah je Oprosnik, Milosrdni!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Hvala Allahu - Onome čije je šta je u nebesima i šta je u Zemlji - i Njemu hvala na Ahiretu; a On je Mudri, Obaviješteni.'),
(3608, 34, 2, 'Zna šta prodire u Zemlju i šta iz nje izlazi, i šta se spušta iz neba i šta uzlazi u njega; a On je Milosrdni, Oprosnik.'),
(3609, 34, 3, 'A oni koji ne vjeruju govore: "Neće nam doći Čas." Reci: "Svakako, i tako mi Gospodara mog, sigurno vam dolazi." - Znalca nevidljivog! Ne izmiče od Njega težina atoma u nebesima, niti u Zemlji, ni sitnije od toga, niti krupnije, a da nije u Knjizi jasnoj,'),
(3610, 34, 4, 'Da bi nagradio one koji vjeruju i rade dobra djela. Ti takvi će imati oprost i opskrbu plemenitu.'),
(3611, 34, 5, 'A oni koji se upinju protiv ajeta Naših - da (ih) oslabe - ti takvi će imati kaznu prljavštine bolne.'),
(3612, 34, 6, 'I vide oni kojima je dato znanje, da je ono šta ti je objavljeno od Gospodara tvog, to Istina, i vodi putu Moćnog, Hvaljenog.'),
(3613, 34, 7, 'A oni koji ne vjeruju, govore: "Hoćete li da vam pokažemo čovjeka (koji) će vas obavijestiti: \'Kad se budete raspadali potpunim raspadom, zaista ćete vi biti u stvaranju novom?\''),
(3614, 34, 8, 'Da li je izmislio protiv Allaha laž ili je u njemu ludilo?" Naprotiv! Oni koji ne vjeruju u Ahiret, u muci su i zabludi dalekoj.'),
(3615, 34, 9, 'Pa zar ne vide šta je ispred njih i šta je iza njih od neba i Zemlje? Da hoćemo učinili bismo da oni propadnu u Zemlju ili dali da padnu na njih komadi neba. Uistinu, u tome je znak za svakog roba koji se obraća.'),
(3616, 34, 10, 'I doista smo dali Dawudu od Nas dobrotu: "O brda! Slavite (Allaha) s njim i pticama", i Mi smo mu omekšali gvožđe.'),
(3617, 34, 11, '"Izrađuj prostrane pancire i određuj plet, i radite dobro. Uistinu, Ja sam onog šta radite Vidilac."'),
(3618, 34, 12, 'I Sulejmanu vjetar - jutro njegovo mjesec i večer njegova mjesec. I pustili smo da mu teče izvor bakra (rastopljenog); i od džinna onog ko je radio pred njim s dozvolom Gospodara njegovog; a ko je od njih odstupio od naredbe Naše, daćemo da on iskusi od kazne seira.'),
(3619, 34, 13, 'Radili su mu šta je htio od mihraba i statua i posuda kao rezervoari, i kotlove nepokretne. "Radite porodico Dawudova zahvaljujući." A malo je robova Mojih zahvalnih.'),
(3620, 34, 14, 'Pa pošto smo mu odredili smrt, ukazao im je na smrt njegovu jedino gmizavac zemlje, jedući štap njegov. Pa pošto je pao, bi jasno džinnima: da su znali nevidljivo, ne bi ostali u kazni prezrenoj.'),
(3621, 34, 15, 'Doista je za Sebu bio u njihovom prebivalištu znak: dvije bašče zdesna i slijeva: "Jedite od opskrbe Gospodara vašeg i zahvaljujte Mu: zemlja lijepa, a Gospodar Oprosnik!"'),
(3622, 34, 16, 'Pa su se odvratili, te smo poslali na njih bujicu Arima, i zamijenili smo im dvije bašče njihove sa dvije bašče gorkog okusa i tamariskom i nešto malo lotosa.'),
(3623, 34, 17, 'Tako smo ih platili što nisu vjerovali. A da li kažnjavamo (ikog) osim nezahvalnika?'),
(3624, 34, 18, 'I načinili smo između njih i između gradova koje smo blagoslovili, gradove vanjske i odredili u njima putovanje: "Putujte u njima noćima i danima, bezbjedni!"'),
(3625, 34, 19, 'Tad su govorili: "Gospodaru naš! Produži (razmak) među putovanjima našim!" Pa su činili zulm dušama svojim, te smo ih učinili pričama i rastrgali ih potpunim kidanjem. Uistinu, u tome su znaci za svakog strpljivog, zahvalnog.'),
(3626, 34, 20, 'I doista je Iblis obistinio na njima pretpostavku svoju, pa su ga slijedili, izuzev skupine vjernika.'),
(3627, 34, 21, 'A nije on imao nad njima nikakvog autoriteta - izuzev da znamo ko vjeruje u Ahiret od onog ko je u sumnji o njemu. A Gospodar tvoj je nad svakom stvari Čuvar.'),
(3628, 34, 22, 'Reci: "Zovite one koje pretpostavljate mimo Allaha! Ne vladaju težinom atoma u nebesima niti u Zemlji, i nemaju oni u njima nikakva udjela, i nema On među njima nikakva pomagača."'),
(3629, 34, 23, 'I neće koristiti kod Njega posredovanje, izuzev onom kome dopusti. Dok, kad se otkloni strah iz srca njihovih, reći će: " [ta to reče Gospodar naš?" Reći će: "Istinu." A On je Uzvišeni, Veliki.'),
(3630, 34, 24, 'Reci: "Ko vas opskrbljuje iz neba i Zemlje?" Reci: "Allah. A uistinu smo mi ili vi na Uputi ili u zabludi očitoj."'),
(3631, 34, 25, 'Reci: "Nećete biti pitani za ono šta smo skrivili, niti ćemo biti pitani za ono šta ste radili."'),
(3632, 34, 26, 'Reci: "Sakupiće nas Gospodar naš, zatim će presuditi među nama s Istinom; a On je Sudac, Sveznajući."'),
(3633, 34, 27, 'Reci: "Pokažite mi one koje Mu (kao) rivale priključujete?" Nikako! Naprotiv! On je Allah, Moćni, Mudri.'),
(3634, 34, 28, 'I poslali smo te cijelom čovječanstvu, samo (kao) donosioca radosnih vijesti i opominjača, ali, većina ljudi ne zna.'),
(3635, 34, 29, 'I govore: "Kad će ovo obećanje, ako istinu govorite?"'),
(3636, 34, 30, 'Reci: "Imaćete vi rok na Dan kojeg nećete čas usporiti, niti ubrzati."'),
(3637, 34, 31, 'I govore oni koji ne vjeruju: "Nećemo vjerovati u ovaj Kur\'an, niti u ono prije njega." A kad bi vidio kad zalimi budu zaustavljeni kod Gospodara svog, obraćaće se jedni drugima govorom. Oni koji su učinjeni slabima reći će onima koji su se oholili: "Da nije vas bilo, sigurno bismo bili vjernici."'),
(3638, 34, 32, 'Oni koji su se oholili reći će onima koji su učinjeni slabima: "Zar smo vas mi odvratili od Upute, nakon što vam je došla? Naprotiv, bili ste prestupnici!"'),
(3639, 34, 33, 'I reći će koji su učinjeni slabima, onima koji su se oholili: "Naprotiv! Spletkaranje noću i danju, kad ste nam naređivali da ne vjerujemo u Allaha i činimo Njemu ravne." I tajit će žal pošto vide kaznu. A načinićemo okove na vratovima onih koji nisu vjerovali. Zar će biti plaćeni, osim kako su radili?'),
(3640, 34, 34, 'I nismo u grad poslali nijednog opominjača, a da nisu rekli raskalašnici njegovi: "Uistinu, mi smo u ono s čim ste poslani - nevjernici."'),
(3641, 34, 35, 'I govore: "Mi smo veći po bogatstvima i djeci, i nećemo mi biti kažnjeni!"'),
(3642, 34, 36, 'Reci: "Uistinu, Gospodar moj pruža opskrbu kome hoće i uskraćuje", ali većina ljudi ne zna.'),
(3643, 34, 37, 'I nisu bogatstva vaša niti djeca vaša ono što će vas Nama približiti bliskošću, izuzev onog ko vjeruje i čini dobro. Pa ti takvi će imati plaću dvostruku za ono što su radili i oni će u odajama biti sigurni.'),
(3644, 34, 38, 'A oni koji se trude protiv ajeta Naših - da (ih) oslabe - takvi će u kaznu biti privedeni.'),
(3645, 34, 39, 'Reci: "Uistinu, Gospodar moj pruža opskrbu kome hoće od robova Svojih i uskraćuje mu. A šta god udijelite od stvari, pa - On će to zamijeniti, i On je Najbolji od opskrbljivača."'),
(3646, 34, 40, 'A na Dan kad ih sve skupi, zatim kaže melecima: "Jesu li ovi vas obožavali?"'),
(3647, 34, 41, 'Reći će: "Slavljen neka si Ti! Ti si Zaštitnik naš mimo njih. Naprotiv, obožavali su džinne; većina njih su u njih bili vjernici."'),
(3648, 34, 42, 'Zato vi Danas ne vladate jedni za druge korišću, niti štetom. I reći ćemo onima koji su činili zulm: "Kušajte kaznu vatrom, onu koju ste poricali!"'),
(3649, 34, 43, 'I kad im se uče ajeti Naši jasni, kažu: "Ovo je samo čovjek, želi da vas odvrati od onog šta su obožavali očevi vaši", i kažu: "Ovo je samo laž izmišljena." A govore koji ne vjeruju za Istinu, pošto im je došla: "Ovo je samo sihr očiti."'),
(3650, 34, 44, 'A nismo im dali nikakve knjige da ih proučavaju, i nismo im poslali prije tebe nijednog opominjača.'),
(3651, 34, 45, 'I poricali su oni prije njih, a nisu (ovi) dosegli desetinu onog šta smo njima dali, pa su poricali poslanike Moje! Pa kakvo je bilo odbijanje Moje!'),
(3652, 34, 46, 'Reci: "Samo vas jednom savjetujem: da ustajete radi Allaha po dva i pojedinačno, zatim razmišljate: nije u drugu vašem nikakva ludost. On vam je samo opominjač pred kaznom žestokom."'),
(3653, 34, 47, 'Reci: "Ne tražim od vas nikakvu nagradu, ta ona je vaša; nagrada moja je jedino od Allaha, a On je nad svakom stvari Svjedok."'),
(3654, 34, 48, 'Reci: "Uistinu, Gospodar moj spušta Istinu, Znalac nevidljivih."'),
(3655, 34, 49, 'Reci: "Došla je Istina, i neće se pojaviti neistina, niti se vratiti."'),
(3656, 34, 50, 'Reci: "Ako zalutam, pa samo lutam protiv duše svoje, a ako se uputim, pa to je stoga što mi objavljuje Gospodar moj. Uistinu! On je Onaj koji čuje, Bliski."'),
(3657, 34, 51, 'A kad bi vidio kad se preplaše, pa ne bude uzmicanja, i budu dograbljeni iz mjesta bliskog,'),
(3658, 34, 52, 'I reknu: "Vjerujemo u njega!" A otkud im da prihvate iz mjesta dalekog?'),
(3659, 34, 53, 'A doista nisu vjerovali u njega od ranije, i ciljali su na nevidljivo iz mjesta dalekog.'),
(3660, 34, 54, 'A između njih i onog šta žele je prepreka, kao što je učinjeno prije sa njima sličnima. Uistinu, oni su bili u sumnji ozbiljnoj.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Hvala Allahu, Stvoritelju nebesa i Zemlje, Onom koji je meleke učinio poslanicima sa krilima: dva i tri i četiri. Dodaje u stvaranju šta hoće. Uistinu! Allah nad svakom stvari ima moć.'),
(3662, 35, 2, 'Ono što Allah otvori ljudima od milosti - pa nema tog ko će je zadržati; a šta zadrži - pa nema joj pošiljaoca poslije Njega; a On je Moćni, Mudri.'),
(3663, 35, 3, 'O ljudi! Sjetite se blagodati Allahove prema vama! Ima li ikakav stvoritelj pored Allaha, (da) vas opskrbljuje iz neba i Zemlje? Nema boga osim Njega, pa kud se odvraćate?'),
(3664, 35, 4, 'A ako te poreknu, pa već su poricani poslanici prije tebe. A Allahu se vraćaju stvari.'),
(3665, 35, 5, 'O ljudi! Zaista je obećanje Allahovo Istina! Zato neka vas nikako ne zavara život Dunjaa, i neka vas nikako ne zavara o Allahu garur.'),
(3666, 35, 6, 'Uistinu, šejtan vam je neprijatelj, zato ga uzmite neprijateljem. Poziva jedino partiju svoju, da budu od stanovnika seira.'),
(3667, 35, 7, 'Oni koji ne vjeruju - imaće oni kaznu žestoku. A oni koji vjeruju i rade dobra djela - imaće oni oprost i nagradu veliku.'),
(3668, 35, 8, 'Pa da li je onaj kome je uljepšano zlo djela njegovog, pa ga vidi lijepim...? Pa uistinu, Allah zabluđuje koga hoće, a upućuje koga hoće. Zato nek ne ide duša tvoja zbog žalosti nad njima. Uistinu, Allah je Znalac o onom što rade.'),
(3669, 35, 9, 'A Allah je Taj koji šalje vjetrove, pa dižu oblak, te ga tjeramo ka zemlji mrtvoj, pa oživljavamo njime zemlju nakon mrtvila njenog. Takvo će biti proživljenje!'),
(3670, 35, 10, 'Ko želi moć - pa Allahova je moć sva. Njemu se uzdiže riječ lijepa; a dobro djelo - uzvisuje ga; a oni koji planiraju zla djela, imaće oni kaznu žestoku; a spletkarenje takvih - ono će propasti.'),
(3671, 35, 11, 'A Allah vas je stvorio od prašine, zatim od kapi sperme, potom vas učinio parovima. I ne zatrudni nijedna žena i ne rodi bez znanja Njegovog. I ne produži se nijednom dugovječnom, niti umanji od života njegovog, a da nije u Knjizi: uistinu, to je Allahu lahko.'),
(3672, 35, 12, 'I nisu jednaka dva mora: ovo svježe, slatkovodno, pitko je piće njegovo, a ovo slano, gorko, a iz svakog jedete svježe meso i vadite nakit kojeg nosite. I vidiš plovila po njemu brazdaju, da tražite iz blagodati Njegove i da biste vi zahvaljivali.'),
(3673, 35, 13, 'Daje da noć uđe u dan i daje da dan uđe u noć, i potčinio je Sunce i Mjesec; svakoje plovi roku određenom. To je Allah, Gospodar vaš!; Njegova je vlast. A oni koje prizivate mimo Njega, ne vladaju opnom na hurminoj košpici.'),
(3674, 35, 14, 'Ako ih prizivate, ne čuju dowu vašu, a da čuju - ne bi vam se odazvali. A na Dan kijameta će poreći širk vaš, i neće te obavijestiti slično Obaviještenom.'),
(3675, 35, 15, 'O ljudi! Vi ste fukare prema Allahu; a Allah - On je Bogati, Hvaljeni.'),
(3676, 35, 16, 'Ako hoće, ukloniće vas i dovesti stvorenje novo;'),
(3677, 35, 17, 'A nije to Allahu teško.'),
(3678, 35, 18, 'I neće se opterećeni teretiti teretom drugog; a ako pretovareni pozove teretu svom, neće se ponijeti od njega nimalo, makar bio rod. Samo ćeš opomenuti one koji se boje Gospodara svog u nevidljivosti i obavljaju salat; a ko se čisti, pa doista se čisti za dušu svoju. A Allahu je dolazište!'),
(3679, 35, 19, 'I nisu isti slijepac i onaj koji vidi,'),
(3680, 35, 20, 'Niti tmine, niti svjetlo,'),
(3681, 35, 21, 'Ni hlad, niti žega,'),
(3682, 35, 22, 'I nisu isti živi i mrtvi. Uistinu! Allah daje da čuje kome hoće. I nisi ti taj koji će pročuti onog ko je u kaburima.'),
(3683, 35, 23, 'Ti si samo opominjač.'),
(3684, 35, 24, 'Uistinu, Mi smo te poslali s Istinom, donosiocem radosnih vijesti i opominjačem. A nema umme a da nije (kroz) nju prošao opominjač.'),
(3685, 35, 25, 'A ako te poreknu, pa već su poricali oni prije njih. Dolazili su im poslanici njihovi sa dokazima jasnim i zeburima i Knjigom svjetiljom.'),
(3686, 35, 26, 'Zatim bih dograbio one koji nisu vjerovali; pa kakvo je bilo odbijanje Moje!'),
(3687, 35, 27, 'Zar ne vidiš da Allah spušta s neba vodu, pa izvodimo njome plodove različitih boja njihovih; a (ima) planina bijelih i crvenih pruga - različitih boja njihovih - i jako crnih;'),
(3688, 35, 28, 'A ljudi i životinje i stoka su također različitih boja njihovih. Samo se boje Allaha između robova Njegovih, učeni. Uistinu! Allah je Moćni, Oprosnik.'),
(3689, 35, 29, 'Uistinu, oni koji uče Knjigu Allahovu i obavljaju salat i udjeljuju od onog čim smo ih opskrbili, javno i tajno, nadaju se trgovini (koja) neće propasti,'),
(3690, 35, 30, 'Da bi im ispunio nagrade njihove i dodao im iz dobrote Svoje. Uistinu, On je Oprosnik, Zahvalni.'),
(3691, 35, 31, 'A ono što smo ti objavili od Knjige, to je Istina, potvrda onome što je ispred nje. Uistinu! Allah je o robovima Svojim Obaviješteni, Vidilac.'),
(3692, 35, 32, 'Zatim smo Knjigu dali u naslijeđe onima koje smo odabrali od robova naših, pa je od njih zalim duši svojoj, a (neki) od njih je osrednji, a (neki) od njih je prednjak u dobrim djelima s dozvolom Allahovom. To je to, blagodat velika!'),
(3693, 35, 33, 'Bašče Adna! Ući će u njih, nosiće u njima narukvice od zlata i biser, a odjeća njihova će u njima biti svila.'),
(3694, 35, 34, 'I reći će: "Hvala Allahu koji je odstranio od nas tugu. Uistinu! Gospodar naš je Oprosnik, Zahvalni,'),
(3695, 35, 35, 'Koji nas je nastanio u kući boravka, iz dobrote Svoje. Neće nas dotaći u njoj umor, niti će nas u njoj dotaći klonulost."'),
(3696, 35, 36, 'A oni koji ne vjeruju, imaće oni vatru Džehennema. Neće se s njima svršiti pa da umru, i neće im se olakšati (ništa) od kazne njegove. Tako ćemo platiti svakog nezahvalnika.'),
(3697, 35, 37, 'I oni će u njemu zvati u pomoć: "Gospodaru naš! Izbavi nas; radićemo dobro, ne šta smo radili." "Zar vam nismo dali dug život, da se pouči u njemu ko se poučava? - i došao vam je opominjač. Zato kušajte!" Pa nemaju zalimi nikakva pomagača.'),
(3698, 35, 38, 'Uistinu, Allah je Znalac nevidljivog nebesa i Zemlje. Uistinu! On je Znalac grudi.'),
(3699, 35, 39, 'On je Taj koji vas je učinio halifama na Zemlji; pa ko ne vjeruje, ta protiv njega je nevjerstvo njegovo. A povećava nevjernicima nevjerstvo njihovo, kod Gospodara njihovog, jedino mržnju, i povećava nevjernicima kufr njihov samo gubitak.'),
(3700, 35, 40, 'Reci: "Vidite li ortake vaše koje prizivate mimo Allaha? Pokažite mi šta su to stvorili od zemlje! Ili imaju oni udio u nebesima? Zar smo im dali Knjigu, pa su oni na jasnim dokazima iz nje? Naprotiv! Zalimi jedni drugima obećavaju samo obmanu."'),
(3701, 35, 41, 'Uistinu! Allah drži nebesa i Zemlju da ne nestanu. A ako krenu da nestanu, neće ih zadržati niko poslije Njega. Uistinu! On je Blagi, Oprosnik.'),
(3702, 35, 42, 'I kunu se Allahom, najjačom od zakletvi njihovih (da će) ako im dođe opominjač sigurno biti upućeniji od svih ummeta; pa pošto im je došao opominjač, povećalo im je samo izbjegavanje,'),
(3703, 35, 43, 'Oholost na Zemlji i planiranje zla; a planiranje zla opkoljava jedino počinitelje njegove. Pa da li očekuju išta osim sunneta ranijh? Pa nećeš naći u sunnetu Allahovom zamjenu, i nećeš naći u sunnetu Allahovom promjenu.'),
(3704, 35, 44, 'Zar ne putuju po Zemlji pa gledaju kakav je bio kraj onih prije njih, a bili su jači od njih u sili? I nije Allah takav da će Mu umaći ikakva stvar u nebesima, niti u Zemlji. Uistinu! On je Znalac, Moćni.'),
(3705, 35, 45, 'A da ščepava Allah ljude za ono šta su zaslužili, ne bi na površini njenoj ostavio nijedno živo biće; ali, ostavlja ih do roka određenog; pa kad dođe rok njihov, ta uistinu je Allah robova svojih Vidilac.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Ja. Sin.'),
(3707, 36, 2, 'Tako Mi Kur\'ana mudrog,'),
(3708, 36, 3, 'Uistinu, ti si (jedan) od izaslanika,'),
(3709, 36, 4, 'Na putu pravom:'),
(3710, 36, 5, 'Objavom Moćnog, Milosrdnog,'),
(3711, 36, 6, 'Da opominješ ljude čiji očevi nisu bili opominjani, pa su oni nemarni.'),
(3712, 36, 7, 'Doista se obistinila Riječ na većini njih, pa oni ne vjeruju.'),
(3713, 36, 8, 'Uistinu! Mi smo na vratovima njihovim načinili okove, pa su oni do brada, zato su oni uzdignutih glava.'),
(3714, 36, 9, 'I načinili smo ispred njih barijeru i iza njih barijeru, pa ih prekrili - zato oni ne vide.'),
(3715, 36, 10, 'A isto im je, opominjao ih ili ih ne opominjao - (oni) neće vjerovati.'),
(3716, 36, 11, 'Jedino opominješ onog ko slijedi Opomenu i boji se Milostivog u nevidljivosti. Zato ga obraduj oprostom i nagradom plemenitom.'),
(3717, 36, 12, 'Uistinu! Mi, Mi ćemo oživiti mrtve, a pišemo šta su unaprijed poslali, i tragove njihove. A svaku stvar - pobrojat ćemo je u Vodiču jasnom.'),
(3718, 36, 13, 'I navedi im primjer stanovnika grada, kad mu dođoše izaslanici;'),
(3719, 36, 14, 'Kad poslasmo njima dvojicu - pa porekoše njih dvojicu - te pojačasmo trećim, te rekoše: "Uistinu, mi smo vama izaslanici!"'),
(3720, 36, 15, 'Rekoše: "Vi ste samo smrtnici slični nama, a nije objavio Milostivi ništa; vi samo lažete!"'),
(3721, 36, 16, 'Rekoše: "Gospodar naš zna. Uistinu, mi smo vama izaslanici,'),
(3722, 36, 17, 'A na nama je jedino dostava jasna."'),
(3723, 36, 18, 'Rekoše: "Uistinu, mi s vama zlo slutimo; ako ne odustanete, sigurno ćemo vas kamenovati, i sigurno će vas od nas dotaći kazna bolna."'),
(3724, 36, 19, '(Izaslanici) rekoše: "Vaša zla kob je sa vama! Zar zato što ste opomenuti? Naprotiv, vi ste ljudi koji pretjeruju."'),
(3725, 36, 20, 'I dođe s najdaljeg kraja grada čovjek, trčeći. Reče: "O narode moj! Slijedite izaslanike!'),
(3726, 36, 21, 'Slijedite onog ko od vas ne traži nagradu, a oni su upućeni.'),
(3727, 36, 22, 'I zašto (da) ja ne obožavam Onog ko me je stvorio, a Njemu ćete biti vraćeni?'),
(3728, 36, 23, 'Zar da uzmem mimo Njega bogove? Ako mi Milostivi želi štetu, neće mi njihovo posredovanje koristiti nimalo, niti će me oni spasiti.'),
(3729, 36, 24, 'Uistinu, ja bih tad bio u zabludi očitoj.'),
(3730, 36, 25, 'Uistinu! Ja vjerujem u Gospodara vašeg, zato me čujte!"'),
(3731, 36, 26, 'Rečeno je: "Uđi u Džennet." Rekao je: "O da moj narod zna,'),
(3732, 36, 27, 'Zašto mi je oprostio Gospodar moj i učinio me od počašćenih!"'),
(3733, 36, 28, 'I nismo na narod njegov poslije njega spustili nikakvu vojsku s neba, i nismo (nikad) spuštali.'),
(3734, 36, 29, 'Bio je samo krik jedan, tad gle: oni ugašeni.'),
(3735, 36, 30, 'O žalosti nad robovima! Nije im došao nijedan poslanik, a da mu se nisu rugali!'),
(3736, 36, 31, 'Zar ne vide koliko smo prije njih uništili naraštaja, da im se oni ne vraćaju?'),
(3737, 36, 32, 'A svi zajedno će kod nas biti privedeni.'),
(3738, 36, 33, 'A znak za njih je zemlja zamrla: oživljavamo je i izvodimo iz nje žito, pa od njega jedu;'),
(3739, 36, 34, 'I načinili smo na njoj bašče od palmi i loza, i provrili smo na njoj izvore,'),
(3740, 36, 35, 'Da jedu od voća njenog - a nisu to uradile ruke njihove. Pa zar neće zahvaljivati?'),
(3741, 36, 36, 'Slava neka je Onom koji je stvorio parove svega: od onog šta izrasta iz zemlje, i od duša njihovih, i od onog šta ne znaju!'),
(3742, 36, 37, 'A znak za njih je noć: povlačimo od nje dan, tad gle! oni zamračeni.'),
(3743, 36, 38, 'I Sunce plovi staništu svom. To je diktat Moćnog, Znalca;'),
(3744, 36, 39, 'I Mjesec, odredili smo mu faze dok se vraća kao grančica stara.'),
(3745, 36, 40, 'Sunce - ne treba njemu da dostigne Mjesec, niti noći da pretekne dan. A svakoje u orbiti plovi.'),
(3746, 36, 41, 'I znak im je što smo Mi nosili potomke njihove u lađi natovarenoj.'),
(3747, 36, 42, 'I stvorili smo za njih nešto njoj slično, (na čemu) se transportuju,'),
(3748, 36, 43, 'A ako htjednemo, potopićemo ih, pa im neće biti pomoći, niti će oni biti izbavljeni,'),
(3749, 36, 44, 'Izuzev milošću od Nas, i (kao) uživanje do izvjesnog vremena.'),
(3750, 36, 45, 'I kad im se kaže: "Bojte se onog šta je ispred vas i šta je iza vas, da bi vam se smilovalo."'),
(3751, 36, 46, 'I nije im došao nijedan znak od znakova Gospodara njihovog, a da nisu bili od njega odvraćeni!'),
(3752, 36, 47, 'I kad im se kaže: "Udjeljujte od onog čim vas je opskrbio Allah", govore oni koji ne vjeruju, onima koji vjeruju: "Zar da hranimo onog koga bi, da je htio Allah, nahranio? Vi ste samo u zabludi očitoj!"'),
(3753, 36, 48, 'I govore: "Kad će ova prijetnja, ako ste istiniti?"'),
(3754, 36, 49, 'Očekuju samo krik jedan, obuzeće ih, a oni će se prepirati.'),
(3755, 36, 50, 'Tad neće moći wasijet (sačiniti), niti će se porodicama svojim vratiti.'),
(3756, 36, 51, 'I puhnuće se u sur, tad gle: oni iz kabura ka Gospodaru svom hitaju.'),
(3757, 36, 52, 'Reći će: "O teško nama! Ko nas podiže iz spavališta našeg?" "Ovo je ono šta je obećavao Milostivi, a Istinu su govorili izaslanici."'),
(3758, 36, 53, 'Biće samo krik jedan, tad gle: oni svi kod Nas privedeni!'),
(3759, 36, 54, 'Pa Danas neće duši biti učinjen zulm nimalo, i bićete plaćeni jedino kako ste radili.'),
(3760, 36, 55, 'Uistinu, stanovnici Dženneta će Danas biti zauzeti, veseli,'),
(3761, 36, 56, 'Oni i žene njihove, u sjenci, na divanima naslonjeni;'),
(3762, 36, 57, 'Imaće oni u njemu voće, i imaće oni šta zatraže.'),
(3763, 36, 58, '"Selam!" - biće riječ Gospodara Milosrdnog.'),
(3764, 36, 59, '"I odvojite se Danas o prestupnici!"'),
(3765, 36, 60, 'Zar vam nisam naložio, o sinovi Ademovi, da ne obožavate šejtana? Uistinu, on vam je neprijatelj otvoreni -'),
(3766, 36, 61, 'Već da Mene obožavate? Ovo je put pravi.'),
(3767, 36, 62, 'I zaista je između vas zaveo mnoštvo veliko. Pa zar niste razumjeli?'),
(3768, 36, 63, 'Ovo je Džehennem kojim vam je prijećeno!'),
(3769, 36, 64, 'Pržite se njime Danas što niste vjerovali.'),
(3770, 36, 65, 'Danas ćemo zapečatiti usta njihova, a govoriće Nam ruke njihove, i svjedočiće noge njihove, za ono šta su zaradili.'),
(3771, 36, 66, 'A da smo htjeli, sigurno bismo ugasili oči njihove, te bi se utrkivali putem, pa kako bi vidjeli?'),
(3772, 36, 67, 'I da smo htjeli, sigurno bismo ih preoblikovali u mjestu njihovom, pa ne bi mogli otići, niti se vratiti.'),
(3773, 36, 68, 'A onog koga poživimo, unazadimo ga u stvaranju, pa zar ne shvataju?'),
(3774, 36, 69, 'I nismo ga poučili pjesništvu, niti mu priliči, to je samo Opomena i Kur\'an jasni,'),
(3775, 36, 70, 'Da opomene onog ko je živ, i obistini Riječ protiv nevjernika.'),
(3776, 36, 71, 'Zar ne vide da smo Mi stvorili za njih - od onog šta su uradile ruke Naše - stoku, pa su oni njeni gospodari?'),
(3777, 36, 72, 'I potčinili smo ih njima, pa su od njih jahalice njihove, i od njih jedu.'),
(3778, 36, 73, 'I imaju oni u njima korist i piće; pa zar neće zahvaljivati?'),
(3779, 36, 74, 'A uzimaju mimo Allaha bogove, da bi se oni pomogli.'),
(3780, 36, 75, 'Ne mogu im (dati) pomoć i oni su za njih vojska privedenih.'),
(3781, 36, 76, 'Zato neka te ne žalosti govor njihov. Uistinu! Mi znamo šta kriju i šta objelodanjuju.'),
(3782, 36, 77, 'Zar ne vidi čovjek da smo ga Mi stvorili od kapi sperme, pa gle! on protivnik otvoreni.'),
(3783, 36, 78, 'I Nama navodi primjer, a zaboravio je stvaranje svoje. Govori: "Ko će oživiti kosti - a one budu truhle?"'),
(3784, 36, 79, 'Reci: "Oživiće ih ko ih je sazdao prvi put, a On je o svakom stvaranju Znalac,'),
(3785, 36, 80, 'Koji vam je iz drveta zelenog načinio vatru, pa gle! vi od nje pripaljujete."'),
(3786, 36, 81, 'Zar nije Onaj koji je stvorio nebesa i Zemlju kadar da stvori slične njima? Svakako!, a On je Tvorac, Znalac!'),
(3787, 36, 82, 'Zaista, komanda Njegova kad nešto želi, jest da mu rekne: "Budi!" - tad biva.'),
(3788, 36, 83, 'Pa slavljen neka je Onaj u ruci čijoj je vlast svake stvari, a Njemu se vraćate!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Tako mi u saffove poredanih,'),
(3790, 37, 2, 'Te onih koji gone ukorom,'),
(3791, 37, 3, 'Te onih koji recituju Opomenu,'),
(3792, 37, 4, 'Uistinu! Bog vaš je sigurno Jedini,'),
(3793, 37, 5, 'Gospodar nebesa i Zemlje i onog između njih, i Gospodar istoka.'),
(3794, 37, 6, 'Uistinu! Mi smo najniže nebo ukrasili ukrasom - planetama,'),
(3795, 37, 7, 'I zaštitom od svakog šejtana zadrtog.'),
(3796, 37, 8, 'Ne čuju Najviši skup, a bivaju gađani sa svake strane,'),
(3797, 37, 9, 'Prognani, a njihova kazna je trajna.'),
(3798, 37, 10, 'Izuzev ko otrgne grabljenjem - tad ga slijedi svjetlica prodorna.'),
(3799, 37, 11, 'Pa upitaj njih: "Da li su oni jače stvorenje ili onaj koga smo stvorili?" Uistinu! Mi smo ih stvorili od ilovače ljepljive.'),
(3800, 37, 12, 'Naprotiv, zadivio si se, a rugaju se,'),
(3801, 37, 13, 'I kad se opomenu, ne paze,'),
(3802, 37, 14, 'A kad znak vide, rugaju se,'),
(3803, 37, 15, 'I govore: "Ovo je samo sihr očiti!'),
(3804, 37, 16, 'Hoćemo li kad umremo i budemo prah i kosti, uistinu mi biti podignuti,'),
(3805, 37, 17, 'Ili očevi naši raniji?"'),
(3806, 37, 18, 'Reci: "Da, i vi ćete biti poniženi."'),
(3807, 37, 19, 'Pa to će biti samo povik jedan, tad gle! - oni će gledati,'),
(3808, 37, 20, 'I reći: "O teško nama! Ovo je Dan sudnji!"'),
(3809, 37, 21, '"Ovo je Dan odluke, onaj kojeg ste poricali."'),
(3810, 37, 22, 'Sakupite one koji su činili zulm i drugare njihove, i ono šta su obožavali,'),
(3811, 37, 23, 'Mimo Allaha, pa ih povedite na put džehimu,'),
(3812, 37, 24, 'I zaustavite ih, uistinu, oni će biti pitani.'),
(3813, 37, 25, '"Šta vam je, ne pomažete se?"'),
(3814, 37, 26, 'Naprotiv, oni su Danas pokorni.'),
(3815, 37, 27, 'I pristupaće oni jedni drugima - raspitivaće se,'),
(3816, 37, 28, 'Govoriće: "Uistinu, vi ste nam dolazili zdesna."'),
(3817, 37, 29, 'Reći će: "Naprotiv, niste bili vjernici,'),
(3818, 37, 30, 'I nismo mi imali nad vama nikakve moći. Naprotiv! Bili ste narod pretjerujućih.'),
(3819, 37, 31, 'Zato se na nama obistinila Riječ Gospodara našeg: uistinu, mi smo ti koji kušamo.'),
(3820, 37, 32, 'Tad smo vas zaveli. Uistinu, mi smo bili zalutali."'),
(3821, 37, 33, 'Pa uistinu, oni će u kazni Tog dana biti sudionici.'),
(3822, 37, 34, 'Uistinu, Mi ćemo tako postupiti sa prestupnicima.'),
(3823, 37, 35, 'Uistinu, oni su se - kad im je rečeno: "Nema boga osim Allaha" - uzoholili,'),
(3824, 37, 36, 'I govorili: "Zar da mi uistinu napustimo bogove naše radi pjesnika ludog?"'),
(3825, 37, 37, 'Naprotiv, došao je s Istinom i potvrdio izaslanike.'),
(3826, 37, 38, 'Uistinu, vi ćete kušati kaznu bolnu -'),
(3827, 37, 39, 'I bićete plaćeni samo kako ste radili -'),
(3828, 37, 40, 'Izuzev robova Allahovih čistih,'),
(3829, 37, 41, 'Ti takvi će imati opskrbu poznatu:'),
(3830, 37, 42, 'Voće; i oni će biti počašćeni,'),
(3831, 37, 43, 'U baščama uživanja,'),
(3832, 37, 44, 'Na sofama će naspramni biti,'),
(3833, 37, 45, 'Obilaziće se oko njih sa čašom izvora,'),
(3834, 37, 46, 'Bijelog, prijatnog za pijače.'),
(3835, 37, 47, 'Neće biti u njemu glavobolje, niti će se oni od njega opiti.'),
(3836, 37, 48, 'A kod njih će biti (hurije) kratkih pogleda, krupnooke,'),
(3837, 37, 49, 'Kao da su one jaja skrivena.'),
(3838, 37, 50, 'Tad će pristupati oni jedni drugima, raspitivaće se.'),
(3839, 37, 51, 'Reći će govornik između njih: "Uistinu, ja sam imao druga,'),
(3840, 37, 52, 'Govorio je: \'Jesi li uistinu ti od onih koji potvrđuju?'),
(3841, 37, 53, 'Zar ćemo kad umremo i budemo prah i kosti, uistinu mi polagati račun?"'),
(3842, 37, 54, 'Reći će: "Da li biste vi pogledali?"'),
(3843, 37, 55, 'Tad će pogledati pa će ga vidjeti usred džehima.'),
(3844, 37, 56, 'Reći će: "Tako mi Allaha, zamalo da me upropastiš.'),
(3845, 37, 57, 'I da nije bilo blagodati Gospodara mog, sigurno bih bio od privedenih."'),
(3846, 37, 58, '"Pa zar nećemo mi biti mejjiti,'),
(3847, 37, 59, 'Izuzev smrti naše ranije, i nećemo mi biti mučeni?"'),
(3848, 37, 60, 'Uistinu, ovo je to, uspjeh veličanstven.'),
(3849, 37, 61, 'Za slično ovome, ta neka rade radnici!'),
(3850, 37, 62, 'Da li je to bolje ugošćenje ili drvo zekkum?'),
(3851, 37, 63, 'Uistinu, Mi ćemo ga učiniti iskušenjem za zalime.'),
(3852, 37, 64, 'Uistinu, to drvo izlazi iz dna džehima,'),
(3853, 37, 65, 'Plod njegov - kao da su to glave šejtana.'),
(3854, 37, 66, 'Pa uistinu, oni će jesti od njega te puniti njime trbuhe.'),
(3855, 37, 67, 'Zatim će oni uistinu imati na njega mješavinu ključale vode,'),
(3856, 37, 68, 'Potom je uistinu povratište njihovo džehimu.'),
(3857, 37, 69, 'Uistinu, oni su našli očeve svoje zalutale,'),
(3858, 37, 70, 'Pa oni tragovima njihovim hite.'),
(3859, 37, 71, 'A doista je prije njih zalutala većina prijašnjih,'),
(3860, 37, 72, 'I doista smo slali među njih opominjače.'),
(3861, 37, 73, 'Zato pogledaj kakav je bio kraj opominjanih,'),
(3862, 37, 74, 'Izuzev robova Allahovih čistih.'),
(3863, 37, 75, 'A doista Nas je zvao Nuh, pa divni su Oni koji su se odazvali.'),
(3864, 37, 76, 'I spasili smo njega i porodicu njegovu od žala strašnog,'),
(3865, 37, 77, 'I učinili potomstvo njegovo - njih preživjelima,'),
(3866, 37, 78, 'I ostavili nad njim u kasnijim (pokoljenjima):'),
(3867, 37, 79, '"Selam Nuhu u svjetovima!"'),
(3868, 37, 80, 'Uistinu, Mi tako nagrađujemo dobročinitelje!'),
(3869, 37, 81, 'Uistinu, on je bio od robova Naših vjernika,'),
(3870, 37, 82, 'Zatim smo potopili druge.'),
(3871, 37, 83, 'A uistinu, od stranke njegove je Ibrahim.'),
(3872, 37, 84, 'Kad dođe Gospodaru svom sa srcem zdravim,'),
(3873, 37, 85, 'Kad reče ocu svom i narodu svom: "Šta to obožavate?'),
(3874, 37, 86, 'Zar lažne bogove mimo Allaha želite?'),
(3875, 37, 87, 'Pa šta je vaše mišljenje o Gospodaru svjetova?"'),
(3876, 37, 88, 'Tad osmotri pogledom zvijezde,'),
(3877, 37, 89, 'Pa reče: "Uistinu, ja sam bolestan!"'),
(3878, 37, 90, 'Tad se okrenuše od njega odlazeći.'),
(3879, 37, 91, 'Pa se prikrade bogovima njihovim, te reče: "Zar ne jedete?,'),
(3880, 37, 92, 'Šta vam je, ne govorite?"'),
(3881, 37, 93, 'Pa im se prikrade udarajući desnicom.'),
(3882, 37, 94, 'Tad mu se približiše, žureći.'),
(3883, 37, 95, 'Reče: "Zar obožavate ono što klešete,'),
(3884, 37, 96, 'A Allah je stvorio vas i ono što pravite?"'),
(3885, 37, 97, 'Rekoše: "Sagradite mu građevinu, pa ga bacite u vatru."'),
(3886, 37, 98, 'Tad mu zaželiše spletku, pa njih učinismo donjima.'),
(3887, 37, 99, 'I reče: "Uistinu, ja sam putnik Gospodaru svom, uputiće me.'),
(3888, 37, 100, 'Gospodaru moj! Daruj mi (potomka) od dobrih."'),
(3889, 37, 101, 'Pa smo ga obradovali dječakom blagim.'),
(3890, 37, 102, 'Pa pošto je dostigao s njim (dobu) privređivanja, reče: "O sinčiću moj! Uistinu sam ja u snu vidio da te ja koljem, pa gledaj, šta vidiš?" Reče: "O oče moj! Učini šta ti se naređuje. Naći ćeš me - ako htjedne Allah - (jednim) od strpljivih."'),
(3891, 37, 103, 'Pa pošto se oba pokoriše, i položi ga na čelo,'),
(3892, 37, 104, 'I pozvasmo ga: "O Ibrahime!'),
(3893, 37, 105, 'Već si ispunio snoviđenje. Uistinu, mi tako nagrađujemo dobročinitelje.'),
(3894, 37, 106, 'Uistinu, ovo je to, kušnja očita."'),
(3895, 37, 107, 'I iskupismo ga žrtvom golemom,'),
(3896, 37, 108, 'I ostavismo mu među kasnijima:'),
(3897, 37, 109, '"Selam Ibrahimu!"'),
(3898, 37, 110, 'Tako nagrađujemo dobročinitelje!'),
(3899, 37, 111, 'Uistinu, on je bio od robova Naših vjernika,'),
(3900, 37, 112, 'A obradovali smo ga Ishakom, vjerovjesnikom, od pravednih,'),
(3901, 37, 113, 'I blagoslovili njega i Ishaka; a od potomstva njih dvojice je dobročinitelj i zalim duši svojoj, očiti;'),
(3902, 37, 114, 'I doista smo obdarili Musaa i Haruna,'),
(3903, 37, 115, 'I spasili njih dvojicu i narod njihov od žala strašnog,'),
(3904, 37, 116, 'I pomogli ih, pa su oni bili pobjednici;'),
(3905, 37, 117, 'I dali smo njima dvojici Knjigu jasnih (stvari),'),
(3906, 37, 118, 'I uputili ih putem pravim,'),
(3907, 37, 119, 'I ostavili njima dvojici među kasnijima:'),
(3908, 37, 120, '"Selam Musau i Harunu!"'),
(3909, 37, 121, 'Uistinu, Mi tako nagrađujemo dobročinitelje!'),
(3910, 37, 122, 'Uistinu, njih dvojica su bili od robova Naših vjernika.'),
(3911, 37, 123, 'I uistinu, Il\'jas je bio od izaslanika.'),
(3912, 37, 124, 'Kad reče narodu svom: "Zar se ne bojite?'),
(3913, 37, 125, 'Zar prizivate Ba\'la, a ostavljate Najboljeg od stvoritelja,'),
(3914, 37, 126, 'Allaha, Gospodara vašeg i Gospodara očeva vaših ranijih?"'),
(3915, 37, 127, 'Pa su ga porekli, pa uistinu će oni biti privedeni,'),
(3916, 37, 128, 'Izuzev robova Allahovih čistih.'),
(3917, 37, 129, 'I ostavili smo njemu među kasnijima:'),
(3918, 37, 130, '"Selam Il\'jasu!"'),
(3919, 37, 131, 'Uistinu, Mi tako nagrađujemo dobročinitelje!'),
(3920, 37, 132, 'Uistinu, on je bio od robova Naših vjernika.'),
(3921, 37, 133, 'I uistinu, Lut je bio od izaslanika.'),
(3922, 37, 134, 'Kad spasismo njega i porodicu njegovu - sve,'),
(3923, 37, 135, 'Izuzev starice među izostalima,'),
(3924, 37, 136, 'Zatim smo uništili druge.'),
(3925, 37, 137, 'A uistinu, vi prolazite pored njih jutrom,'),
(3926, 37, 138, 'I noću; pa zar ne shvatate?'),
(3927, 37, 139, 'I uistinu, Junus je bio od izaslanika;'),
(3928, 37, 140, 'Kad pobježe k lađi natovarenoj,'),
(3929, 37, 141, 'Pa je bacio kocku, te bio od odbačenih,'),
(3930, 37, 142, 'Tad ga je progutala (velika) riba, i on je prijekora vrijedan:'),
(3931, 37, 143, 'Pa da on nije bio od onih koji slave (Allaha),'),
(3932, 37, 144, 'Sigurno bi ostao u njenoj utrobi do Dana (kad) će biti podignuti.'),
(3933, 37, 145, 'Pa smo ga izbacili na pusto mjesto, a on je bio bolestan,'),
(3934, 37, 146, 'I dali smo da nad njim izraste žbun tikve;'),
(3935, 37, 147, 'I poslali ga ka stotini hiljada ili više,'),
(3936, 37, 148, 'Pa su vjerovali, te smo im dali uživanje do izvjesnog vremena.'),
(3937, 37, 149, 'Zato ih upitaj: "Jesu li Gospodara tvog kćeri, a njihovi sinovi?"'),
(3938, 37, 150, 'Ili smo stvarali meleke ženskima, a oni svjedoci bili?'),
(3939, 37, 151, 'Besumnje! Uistinu oni zbog laži svoje govore:'),
(3940, 37, 152, '"Rodio je Allah", a uistinu, oni su lažljivci.'),
(3941, 37, 153, 'Zar je odabrao kćeri nad sinovima?'),
(3942, 37, 154, 'Šta vam je? Kako sudite?'),
(3943, 37, 155, 'Pa zar se nećete poučiti?'),
(3944, 37, 156, 'Ili vi imate dokaz očit?'),
(3945, 37, 157, 'Pa donesite knjigu svoju, ako istinu govorite!'),
(3946, 37, 158, 'I uspostavljaju između Njega i džinna srodstvo, a doista džinni znaju da će oni uistinu biti privedeni -'),
(3947, 37, 159, 'Slavljen neka je Allah od onog šta pripisuju -'),
(3948, 37, 160, 'Izuzev robova Allahovih čistih.'),
(3949, 37, 161, 'Pa uistinu, vi i ono šta obožavate,'),
(3950, 37, 162, 'Niste vi protiv Njega zavodnici,'),
(3951, 37, 163, 'Izuzev onog koji će biti pržen džehimom.'),
(3952, 37, 164, 'A nema od nas nijednog, a da njegovo mjesto nije poznato,'),
(3953, 37, 165, 'I uistinu! Mi, Mi smo poredani,'),
(3954, 37, 166, 'I uistinu, Mi, Mi smo slavitelji.'),
(3955, 37, 167, 'A oni su sigurno govorili:'),
(3956, 37, 168, '"Da je kod nas Opomena od ranijih,'),
(3957, 37, 169, 'Sigurno bismo bili robovi Allahovi čisti."'),
(3958, 37, 170, 'Pa nisu u njega vjerovali, pa saznaće!'),
(3959, 37, 171, 'A već je prethodila Riječ Naša robovima Našim poslanim.'),
(3960, 37, 172, 'Uistinu, oni - oni će biti pomognuti,'),
(3961, 37, 173, 'I uistinu, vojska Naša - oni će biti pobjednici.'),
(3962, 37, 174, 'Zato se okreni od njih izvjesno vrijeme,'),
(3963, 37, 175, 'I posmatraj ih, pa vidjeće.'),
(3964, 37, 176, 'Pa zar kaznu Našu požuruju?'),
(3965, 37, 177, 'Pa kad se spusti u avliju njihovu, tad će biti zlo jutro upozoravanih.'),
(3966, 37, 178, 'I okreni se od njih izvjesno vrijeme,'),
(3967, 37, 179, 'I posmatraj, pa vidjet će!'),
(3968, 37, 180, 'Slavljen neka je Gospodar tvoj, Gospodar moći, od onog šta pripisuju!'),
(3969, 37, 181, 'I selam izaslanicima,'),
(3970, 37, 182, 'I hvala Allahu, Gospodaru svjetova!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Sad. Tako mi Kur\'ana, posjednika Opomene,'),
(3972, 38, 2, 'Naprotiv, oni koji ne vjeruju u oholosti su i razdoru.'),
(3973, 38, 3, 'Koliko smo prije njih uništili pokoljenja, pa su prizivali, a prošlo bi vrijeme za uzmicanje.'),
(3974, 38, 4, 'A čudili su se da im je došao opominjač između njih, i govorili nevjernici: "Ovo je čarobnjak, lažac.'),
(3975, 38, 5, 'Je li učinio bogove Bogom Jedinim? Uistinu, ovo je stvar čudesna!"'),
(3976, 38, 6, 'I krenuše velikani izmeđe njih: "Idite i ustrajte uz bogove svoje! Uistinu, ovo je stvar koja se želi!'),
(3977, 38, 7, 'Nismo o ovome čuli u milletu zadnjem. Ovo je samo izmišljotina!'),
(3978, 38, 8, 'Zar je njemu objavljena Opomena između nas?" Naprotiv! Oni su u sumnji o Opomeni Mojoj. Naprotiv, još nisu okusili kaznu Moju.'),
(3979, 38, 9, 'Zar su kod njih riznice milosti Gospodara tvog, Moćnog, Darivaoca?'),
(3980, 38, 10, 'Zar je njihova vlast nebesa i Zemlje i onog šta je između njih? Pa neka se uspnu (kakvim) sredstvima!'),
(3981, 38, 11, 'Vojska su što je ondje poražena od saveznika.'),
(3982, 38, 12, 'Poricali su prije njih narod Nuhov i Ad i faraon, posjednik stubova,'),
(3983, 38, 13, 'I Semud i narod Lutov i stanovnici Ejke - takve su partije.'),
(3984, 38, 14, 'Svaki je samo poricao poslanike, pa se obistinila kazna Moja.'),
(3985, 38, 15, 'A takvi čekaju samo krik jedan, za kojeg nema odlaganja.'),
(3986, 38, 16, 'I govore: "Gospodaru naš! Ubrzaj nam dio naš prije Dana obračuna."'),
(3987, 38, 17, 'Strpi se nad onim šta govore, i sjeti se roba Našeg Dawuda, posjednika moći. Uistinu, on je bio taj koji se obraćao (Allahu).'),
(3988, 38, 18, 'Uistinu! Mi smo potčinili brda (da) s njim slave u sutonu i obasjavanju,'),
(3989, 38, 19, 'I ptice okupljene; svako je Njemu obraćenik.'),
(3990, 38, 20, 'I ojačali smo carstvo njegovo, i dali mu mudrost i odlučan govor.'),
(3991, 38, 21, 'A da li je tebi stigla vijest o parničarima? - kad se ispeše na mihrab;'),
(3992, 38, 22, 'Kad uđoše Dawudu, pa se prestraši od njih, rekoše: "Ne boj se! Dva smo parničara, učinili smo nepravdu jedan drugom, zato među nama presudi s Istinom; i ne budi nepravedan; i uputi nas putu ravnom.'),
(3993, 38, 23, 'Uistinu, ovo je brat moj. On ima devedeset devet ovaca, a ja imam jednu ovcu, pa je rekao: \'Povjeri je meni!\' - i nadmaši me u govoru."'),
(3994, 38, 24, '(Dawud) reče: "Zaista ti je učinio zulm traženjem ovce tvoje ovcama svojim. A uistinu, mnogi od ortaka čine nasilje jedni drugima, izuzev koji vjeruju i čine dobra djela - a njih je malo." I Dawud pomisli da ga iskušavamo, pa zamoli oprost Gospodara svog i pade na ruku\' i pokaja se.'),
(3995, 38, 25, 'Tad smo mu to oprostili, a uistinu, on ima kod Nas bliskost i lijepo povratište.'),
(3996, 38, 26, 'O Dawude! Uistinu! Mi smo te učinili halifom na Zemlji, zato sudi među ljudima s Istinom, i ne slijedi strast, pa da te zavede s puta Allahovog. Uistinu, koji zavode s puta Allahovog, imaće oni kaznu žestoku što su zaboravili Dan obračuna.'),
(3997, 38, 27, 'I nismo stvorili nebo i Zemlju i šta je između njih, uzaludno. To je mišljenje onih koji ne vjeruju. Pa teško onima koji ne vjeruju - od vatre!'),
(3998, 38, 28, 'Zar tretiramo one koji vjeruju i rade dobra djela, kao mufside na Zemlji? Ili tretiramo bogobojazne kao razvratnike?'),
(3999, 38, 29, 'Knjiga koju smo ti objavili je blagoslovljena; da razmatraju ajete njene i da se pouče posjednici razuma.'),
(4000, 38, 30, 'I podarili smo Dawudu Sulejmana. Divan rob! Uistinu, on je bio onaj koji se obraćao.'),
(4001, 38, 31, 'Kad mu se izvedoše u sumrak safinati brzi,'),
(4002, 38, 32, 'Tad reče: "Uistinu! Ja sam iskazao ljubav prema dobru (više) no zikru Gospodara svog" - dok ne bi skriveno u velu (noći).'),
(4003, 38, 33, '"Vratite mi ih", pa je počeo glađenjem nogu i vratova.'),
(4004, 38, 34, 'I doista smo iskušavali Sulejmana, i bacili na prijesto njegov tijelo - zatim se obratio.'),
(4005, 38, 35, 'Reče: "Gospodaru moj! Oprosti mi, daruj mi vlast kakvu niko neće imati poslije mene! Uistinu, Ti, Ti si Darivalac."'),
(4006, 38, 36, 'Pa smo potčinili njemu vjetar - puhao je povjetarac po naredbi njegovoj blago kud je htio,'),
(4007, 38, 37, 'I šejtane, svakog graditelja i ronioca,'),
(4008, 38, 38, 'I druge povezane u okove.'),
(4009, 38, 39, '"Ovo je poklon Naš, pa daruj ili zadrži, bez računa."'),
(4010, 38, 40, 'A uistinu, on kod Nas ima bliskost i lijepo povratište.'),
(4011, 38, 41, 'I spomeni roba Našeg Ejjuba, kad pozva Gospodara svog: "Uistinu me šejtan dotiče jadom i patnjom."'),
(4012, 38, 42, '"Udri nogom svojom!" Ovo je mjesto pranja - hladno, i (voda za) piće.'),
(4013, 38, 43, 'I podarili smo mu porodicu njegovu, i slično njima uz njih - milost od Nas - i Opomenu za imaoce razuma.'),
(4014, 38, 44, '"I uzmi rukom svojom snop, pa udri njime i ne prekrši zakletvu!" Uistinu, Mi smo ga našli strpljivim. Divan rob! Uistinu, on je bio pokajanik.'),
(4015, 38, 45, 'I spomeni robove Naše, Ibrahima i Ishaka i Jakuba - posjednike čvrstine i zapažanja.'),
(4016, 38, 46, 'Uistinu! Mi smo ih pročistili čistom (osobinom), sjećanjem Kuće.'),
(4017, 38, 47, 'I uistinu, oni su kod Nas od odabranih, najbolji.'),
(4018, 38, 48, 'I spomeni Isma\'ila i Eljese\'a i Zul\'kifla; a svaki je bio od najboljih.'),
(4019, 38, 49, 'Ovo je podsjećanje; a uistinu, bogobojazni će imati divno povratište,'),
(4020, 38, 50, 'Bašče Adna, za njih otvorenih kapija,'),
(4021, 38, 51, 'Naslonjeni u njima, tražiće u njima mnogo voća, i piće.'),
(4022, 38, 52, 'A kod njih će biti (hurije) kratkih pogleda, vršnjakinje.'),
(4023, 38, 53, 'Ovo je ono šta vam se obećava za Dan obračuna.'),
(4024, 38, 54, 'Uistinu, ovo je opskrba Naša, nema za nju nikakva iscrpljenja.'),
(4025, 38, 55, 'Ovo! A uistinu, prevršitelji će imati zlo povratište,'),
(4026, 38, 56, 'Džehennem - pržiće se njime, ta loše je odmorište.'),
(4027, 38, 57, 'Ovo! Zato neka ga kušaju - ključalu tekućinu i ledenu,'),
(4028, 38, 58, 'I druge, od sličnih tome, u parovima!'),
(4029, 38, 59, 'Ovo je skupina koja se s vama gura; neće za njih biti dobrodošlice! Uistinu, oni će biti prženi vatrom.'),
(4030, 38, 60, 'Reći će: "Naprotiv, vi - vama nema merhaba: vi ste ga pripremili za nas, pa loše je stanište!"'),
(4031, 38, 61, 'Reći će: "Gospodaru naš! Ko je ovo pripremio za nas, pa dodaj mu dvostruku kaznu u vatri!"'),
(4032, 38, 62, 'I govoriće: "Šta nam je, ne vidimo ljude koje smo brojali ozloglašenima?'),
(4033, 38, 63, 'Jesmo li ih uzeli ruglom ili su skrenuli s njih pogledi (naši)?"'),
(4034, 38, 64, 'Doista, to će biti istina: svađa stanovnika vatre.'),
(4035, 38, 65, 'Reci: "Ja sam samo opominjač, i nema nikakva boga, izuzev Allaha Jedinog, Onog koji pokorava,'),
(4036, 38, 66, 'Gospodara nebesa i Zemlje i šta je između njih, Moćnog, Oprosnika."'),
(4037, 38, 67, 'Reci: "To je vijest veličanstvena,'),
(4038, 38, 68, 'Vi ste od nje odvraćeni!'),
(4039, 38, 69, 'Nisam imao nikakvog znanja o Skupu najvišem, kad su raspravljali;'),
(4040, 38, 70, 'Meni se objavljuje da sam ja samo opominjač jasni."'),
(4041, 38, 71, 'Kad reče Gospodar tvoj melecima: "Uistinu, Ja sam Tvorac smrtnika od ilovače,'),
(4042, 38, 72, 'Zato kad ga sredim i udahnem u njega od Duha Svog, tad padnite njemu ničice."'),
(4043, 38, 73, 'Pa su učinili sedždu meleci, svi oni zajedno,'),
(4044, 38, 74, 'Osim Iblisa; uzoholio se i bio od nevjernika.'),
(4045, 38, 75, '(Allah) reče: "O Iblisu! Šta te je spriječilo da padneš na sedždu onom šta sam stvorio rukama Svojim? Jesi li se uzoholio ili si od uzvišenih?"'),
(4046, 38, 76, 'Reče: "Ja sam bolji od njega, mene si stvorio od vatre, a njega si stvorio od ilovače."'),
(4047, 38, 77, '(Allah) reče: "Pa izlazi iz njega, pa uistinu si ti protjeran,'),
(4048, 38, 78, 'I uistinu! Na tebi je prokletstvo Moje do Dana sudnjeg."'),
(4049, 38, 79, 'Reče: "Gospodaru moj! Pa odgodi mi do Dana kad će biti podignuti."'),
(4050, 38, 80, '(Allah) reče: "Pa uistinu si ti od onih kojima se odgađa,'),
(4051, 38, 81, 'Do Dana - wakta poznatog."'),
(4052, 38, 82, 'Reče: "Pa tako mi moći Tvoje, sigurno ću ih sve zavesti,'),
(4053, 38, 83, 'Izuzev robova Tvojih - od njih čistih."'),
(4054, 38, 84, '(Allah) reče: "Pa Istina je, a Istinu govorim,'),
(4055, 38, 85, 'Sigurno je da ću napuniti Džehennem tobom i onim ko te između njih bude slijedio - svima."'),
(4056, 38, 86, 'Reci (Muhammede): "Ne tražim od vas za njega nikakvu nagradu, i nisam ja od izvještačenih.'),
(4057, 38, 87, 'On je samo Opomena svjetovima.'),
(4058, 38, 88, 'A sigurno ćete saznati vijest njegovu nakon (izvjesnog) vremena."');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Objava Knjige je od Allaha, Moćnog, Mudrog.'),
(4060, 39, 2, 'Uistinu! Mi smo ti objavili Knjigu s Istinom, zato obožavaj Allaha, odan Njegovoj vjeri.'),
(4061, 39, 3, 'Uistinu, čista vjera je za Allaha! A oni koji mimo Njega uzimaju zaštitnike (kažu): "Obožavamo ih samo da nas približe Allahu bliskošću." Doista će Allah presuditi među njima o onom u čemu se oni razilaze. Uistinu, Allah ne upućuje onog ko je lažac, nezahvalnik.'),
(4062, 39, 4, 'Da je Allah želio da uzme dijete, sigurno bi od onog šta stvara izabrao šta hoće. Slava neka je Njemu! On, Allah, je Jedini, Onaj koji pokorava!'),
(4063, 39, 5, 'Stvorio je nebesa i Zemlju s Istinom. Zavija noću dan, i zavija danom noć. I potčinio je Sunce i Mjesec. Svakoje plovi roku određenom. Besumnje! On je Moćni, Oprosnik!'),
(4064, 39, 6, 'Stvorio vas je od duše jedne, zatim od nje načinio paricu njenu, i dao vam od stoke osam parova. Stvara vas u utrobama majki vaših, stvaranjem iza stvaranja, u tri tmine. To je Allah, Gospodar vaš!, Njegova je vlast. Nema boga osim Njega, pa kako se odvraćate?'),
(4065, 39, 7, 'Ako ne vjerujete - pa uistinu, Allah je od vas Neovisan, a nije zadovoljan nezahvalnošću robova Svojih. A ako zahvaljujete, zadovoljan je On s vama. I neće se teretiti opterećeni teretom drugog. Zatim je Gospodaru vašem povratak vaš, pa će vas obavijestiti o onom šta ste radili. Uistinu! On je Znalac grudi.'),
(4066, 39, 8, 'I kad čovjeka dotakne nevolja, priziva Gospodara svog, obraćajući se Njemu. Potom, kad mu podari blagodat od Sebe, zaboravi ono za što Ga je prizivao ranije, i postavlja Allahu jednake da bi zavodio s puta Njegovog. Reci: "Uživaj malo u nevjerovanju svom, uistinu si ti od stanovnika vatre."'),
(4067, 39, 9, 'Da li je onaj ko je pokoran za vrijeme noći, padajući na sedždu i stojeći, pazi na Ahiret i nada se milosti Gospodara svog...? Reci: "Da li su isti oni koji znaju i koji ne znaju?" Jedino se poučavaju posjednici razuma!'),
(4068, 39, 10, 'Reci: "O robovi Moji koji vjerujete! Bojte se Gospodara svog. Onima koji rade dobro na ovom Dunjau, biće dobro, a Zemlja Allahova je prostrana. Samo će strpljivima biti isplaćena nagrada njihova bez računa."'),
(4069, 39, 11, 'Reci: "Uistinu! Meni je naređeno da obožavam Allaha, odan Njegovoj vjeri,'),
(4070, 39, 12, 'I naređeno mi je da budem prvi od muslimana."'),
(4071, 39, 13, 'Reci: "Uistinu, ja se plašim ako ne poslušam Gospodara svog, kazne Dana strašnog."'),
(4072, 39, 14, 'Reci: "Allaha obožavam - odan Njegovoj vjeri.'),
(4073, 39, 15, 'Pa obožavajte šta hoćete mimo Njega." Reci: "Gubitnici će biti oni koji izgube duše svoje i porodice svoje na Dan kijameta. Zar to nije taj gubitak očiti?"'),
(4074, 39, 16, 'Imaće oni iznad sebe nadstrešice od vatre i ispod sebe (vatreni) podijum. To je ono čime Allah zastrašuje robove Svoje: "O robovi Moji, ta bojte se Mene!"'),
(4075, 39, 17, 'A koji se klonu taguta - da ga ne obožavaju i obrate se Allahu - za njih je radosna vijest. Zato obraduj robove Moje,'),
(4076, 39, 18, 'One koji slušaju Riječ i slijede najbolje od njega. Takvi su oni koje je uputio Allah, a ti takvi su posjednici razuma.'),
(4077, 39, 19, 'Pa zar onog na kom se ispunila riječ kazne - pa zar ti spasavaš onog ko će u vatri biti?'),
(4078, 39, 20, 'Međutim, oni koji se boje Gospodara svog, imaće oni prostorije iznad kojih su prostorije izgrađene, ispod kojih teku rijeke. Obećanje je Allahovo, ne krši Allah obećanje!'),
(4079, 39, 21, 'Zar ne vidiš da Allah spušta iz neba vodu, pa daje da otiče izvorima na Zemlji, zatim njome izvodi usjev raznolikih boja njegovih, potom svehne, pa ga vidiš požutjelog, zatim ga učini izlomljenim. Uistinu, u tome je opomena za imaoce razuma.'),
(4080, 39, 22, 'Pa da li je onaj kome je Allah proširio grudi njegove za Islam, pa je on na svjetlu Gospodara svog...? Pa teško tvrdim srcima njihovim od Opomene Allahove. Takvi su u zabludi očitoj.'),
(4081, 39, 23, 'Allah je objavio Najbolji hadis, Knjigu jednoobraznu, ponavljajuću. Ježe se od nje kože onih koji se boje Gospodara svog. Zatim se smekšavaju kože njihove i srca njihova na spomen Allaha. To je Uputa Allahova, upućuje njome koga hoće. A koga zavede Allah, tad on nema nikakvog upućivača.'),
(4082, 39, 24, 'Pa da li je onaj ko će se štititi licem svojim od zla kazne Dana kijameta...? I reći će se zalimima: "Iskusite šta ste zaradili!"'),
(4083, 39, 25, 'Poricali su oni prije njih, pa im je došla kazna odakle nisu zapazili.'),
(4084, 39, 26, 'Zato je Allah dao da oni iskuse poniženje u životu Dunjaa, a sigurno je kazna Ahireta veća - kad bi znali!'),
(4085, 39, 27, 'I zaista smo ljudima naveli u ovom Kur\'anu od svega primjer, da bi se oni poučili;'),
(4086, 39, 28, 'Kur\'an na arapskom, bez ikakve iskrivljenosti, da bi se oni pobojali.'),
(4087, 39, 29, 'Navodi Allah primjer čovjeka: on pod ortacima svađalicama, i čovjeka potčinjenog čovjeku. Jesu li isti primjerom? Hvala neka je Allahu! Naprotiv, većina njih ne zna!'),
(4088, 39, 30, 'Uistinu, ti ćeš biti mrtav, i uistinu, oni će biti mrtvaci,'),
(4089, 39, 31, 'Zatim ćete se uistinu vi, na Dan kijameta kod Gospodara svog prepirati.'),
(4090, 39, 32, 'Pa ko je nepravedniji od onog ko laže na Allaha i poriče Istinu kad mu dođe? Zar neće biti u Džehennemu boravište za nevjernike?'),
(4091, 39, 33, 'A onaj ko donosi Istinu, i vjeruje u nju, ti takvi su bogobojazni.'),
(4092, 39, 34, 'Imaće oni šta htjednu kod Gospodara svog. To je plaća dobročinitelja,'),
(4093, 39, 35, '[to će im oprostiti Allah najgore šta su uradili, i podmiriti im nagradu njihovu za najbolje šta su uradili.'),
(4094, 39, 36, 'Zar nije Allah dovoljan robu Svom? A plaše te sa onima mimo Njega. A koga zavede Allah - pa njemu nema nikakvog upućivača.'),
(4095, 39, 37, 'A koga upućuje Allah - pa nema za njega nikakva zabluđivača. Zar nije Allah Moćni, Vlasnik osvete?'),
(4096, 39, 38, 'A ako ih upitaš: "Ko je stvorio nebesa i Zemlju?" - sigurno će reći: "Allah." Reci: "Pa vidite li ono što prizivate mimo Allaha - ako mi Allah želi štetu - jesu li oni ti koji će otkloniti nevolju Njegovu? Ili mi želi milost - jesu li oni ti koji će zadržati milost Njegovu?" Reci: "Dovoljan mi je Allah; u Njega se pouzdavaju koji se pouzdavaju."'),
(4097, 39, 39, 'Reci: "O narode moj! Radite u poziciji svojoj, uistinu, ja sam radnik, pa saznaćete,'),
(4098, 39, 40, 'Ko je taj kome će doći kazna - poniziće ga, i na njega će se obarati kazna neprestana."'),
(4099, 39, 41, 'Uistinu! Mi smo ti objavili Knjigu za (sve) ljude, s Istinom. Pa ko se uputi, pa za dušu je njegovu; a ko zaluta, pa samo luta protiv nje. A nisi ti nad njima čuvar.'),
(4100, 39, 42, 'Allah uzima duše u vrijeme smrti njihove - a koja nije umrla - u snu njenom. Tad zadržava onu kojoj je odredio smrt, a šalje drugu do roka određenog. Uistinu, u tome su znaci za ljude koji razmišljaju.'),
(4101, 39, 43, 'Zar uzimaju mimo Allaha posrednike? Reci: "Zar iako ne vladaju ničim, niti razumiju?"'),
(4102, 39, 44, 'Reci: "Allahovo je posredovanje svako, Njegova je vlast nebesa i Zemlje, zatim se Njemu vraćate."'),
(4103, 39, 45, 'I kad se spomene Allah - Jedini On, zgrče se srca onih koji ne vjeruju u Ahiret, a kad se spomenu oni mimo Njega, gle! - oni se raduju.'),
(4104, 39, 46, 'Reci: "O Allahu, Stvoritelju nebesa i Zemlje, Znalče nevidljivog i vidljivog! Ti ćeš presuditi među robovima Svojim o onom u čemu su se razilazili."'),
(4105, 39, 47, 'I kad bi oni koji čine zulm imali sve šta je na Zemlji i slično tome uz to, sigurno bi se time iskupljivali od zla kazne Dana kijameta; a pojaviće im se od Allaha ono s čime nisu računali.'),
(4106, 39, 48, 'I pojaviće im se zla onog šta su zaradili, i snaći će ih ono čemu su se ismijavali.'),
(4107, 39, 49, 'Pa kad čovjeka dotakne nesreća, priziva Nas, zatim kad mu podarimo blagodat od Nas, kaže: "To mi je dato samo zbog znanja (mog)." Naprotiv, to je iskušenje, ali većina njih ne zna!'),
(4108, 39, 50, 'Već su to govorili oni prije njih, pa im nije koristilo ono šta su stekli.'),
(4109, 39, 51, 'Tad su ih pogodila zla koja su zaradili. A oni od takvih koji čine zulm, pogodiće ih zla koja stiču; a nisu oni ti koji će umaći.'),
(4110, 39, 52, 'Zar ne znaju da Allah pruža opskrbu kome hoće i uskraćuje? Uistinu, u tome su znaci za ljude koji vjeruju.'),
(4111, 39, 53, 'Reci: "O robovi Moji koji ste pretjerali protiv duša svojih! Ne očajavajte o milosti Allahovoj, uistinu, Allah oprašta grijehe sve. Uistinu, On, On je Oprosnik, Milosrdni.'),
(4112, 39, 54, 'I obratite se Gospodaru svom i Njemu se predajte, prije nego vam dođe kazna - poslije nećete biti pomognuti.'),
(4113, 39, 55, 'I slijedite najbolje šta vam se objavljuje od Gospodara vašeg, prije nego vam dođe kazna iznenada, a vi ne opazite.'),
(4114, 39, 56, 'Da ne kaže duša: \'O tugo moja zbog onog što sam zanemario spram Allaha, i doista bio od izrugivača."'),
(4115, 39, 57, 'Ili rekne: "Da me je Allah uputio, sigurno bih bio od bogobojaznih!"'),
(4116, 39, 58, 'Ili kaže u vrijeme kad vidi kaznu: "Da mi je povratak, pa da budem od dobročinitelja!"'),
(4117, 39, 59, 'Svakako! Doista su ti dolazili ajeti Moji pa si ih poricao i oholio se i bio od nevjernika.'),
(4118, 39, 60, 'A na Dan kijameta vidjećeš one koji su lagali na Allaha, lica njihova biće pocrnila. Zar neće u Džehennemu biti boravište oholih!'),
(4119, 39, 61, 'I spasit će Allah one koji su se bojali, zbog postignuća njihovog, neće ih dotaći zlo, niti će oni tugovati.'),
(4120, 39, 62, 'Allah je Stvoritelj svake stvari, i On je nad svakom stvari Zaštitnik!'),
(4121, 39, 63, 'Njegovi su ključevi nebesa i Zemlje! A oni koji ne vjeruju u ajete Allahove - ti takvi su gubitnici.'),
(4122, 39, 64, 'Reci: "Pa zar mi naređujete da drugog, a ne Allaha obožavam? O neznalice!"'),
(4123, 39, 65, 'A doista je objavljeno tebi i onima prije tebe: "Ako pridružiš - sigurno će propasti rad tvoj i sigurno ćeš biti od gubitnika.'),
(4124, 39, 66, 'Naprotiv! Allaha zato obožavaj i budi od zahvalnih!"'),
(4125, 39, 67, 'I ne cijene Allaha pravim cijenjenjem Njegovim, a Zemlja sva biće u šaci Njegovoj na Dan kijameta, i nebesa će smotana biti u desnici Njegovoj! Slavljen neka je On i uzvišen od onog šta pridružuju!'),
(4126, 39, 68, 'I puhnuće se u sur pa će se onesvijestiti ko je u nebesima i ko je na Zemlji, izuzev koga htjedne (zaštititi) Allah. Zatim će se u njega puhnuti drugi put, tad gle! - oni stoje, gledaju,'),
(4127, 39, 69, 'I zablistaće Zemlja svjetlošću Gospodara svog, i postaviće se Knjiga, i dovešće se vjerovjesnici i svjedoci, i presudiće se među njima s Istinom, i neće im biti učinjen zulm.'),
(4128, 39, 70, 'I biće plaćeno svakoj duši šta je radila, a On je Najbolji znalac onog šta čine.'),
(4129, 39, 71, 'I biće sprovođeni oni koji ne vjeruju ka Džehennemu u skupinama, dok - kad mu dođu, otvorit će se kapije njegove, a reći će im čuvari njegovi: "Zar vam nisu dolazili poslanici između vas - učili vam ajete Gospodara vašeg i opominjali vas na vaš susret ovog dana vašeg?" Reći će: "Svakako, ali obistinila se riječ kazne nad nevjernicima."'),
(4130, 39, 72, 'Biće rečeno: "Uđite na kapije Džehennema, vječno ćete biti u njemu." Pa lošeg li boravišta oholih.'),
(4131, 39, 73, 'I biće sprovođeni oni koji su se bojali Gospodara svog ka Džennetu u skupinama, dok - kad mu dođu i otvorene budu kapije njegove i reknu im čuvari njegovi: "Selamun alejkum! Bili ste dobri, zato uđite u njega da vječno boravite!"'),
(4132, 39, 74, 'A (oni) će reći: "Hvala Allahu koji nam je ispunio obećanje Svoje i dao nam da naslijedimo Zemlju, nastanjujemo se u Džennetu gdje hoćemo!" Pa divna je nagrada radnika!'),
(4133, 39, 75, 'I vidjećeš meleke, okružitelje okolo Arša, slave sa hvalom Gospodara svog. I presudiće se među njima s Istinom, i biće rečeno: "Hvala neka je Allahu, Gospodaru svjetova!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Ha. Mim.'),
(4135, 40, 2, 'Objava Knjige je od Allaha, Moćnog, Znalca,'),
(4136, 40, 3, 'Oprosnika grijeha i Primaoca pokajanja, Žestokog u kazni, Posjednika darežljivosti. Nema boga osim Njega, Njemu je dolazište.'),
(4137, 40, 4, 'O ajetima Allahovim raspravljaju jedino oni koji ne vjeruju, zato neka te ne zavara njihovo kolebanje na Zemlji.'),
(4138, 40, 5, 'Poricao je prije njih narod Nuhov i stranke poslije njih. Namjeravao je svaki ummet za poslanika njihovog - da ga se dočepaju. I raspravljali su neistinom, da bi njome pobili Istinu, zato sam ih dograbio. Pa kakva je bila kazna Moja!'),
(4139, 40, 6, 'I tako se obistinila Riječ Gospodara tvog na onima koji nisu vjerovali: da će oni biti stanovnici vatre.'),
(4140, 40, 7, 'Oni koji nose Arš i ko je oko njega, slave sa hvalom Gospodara svog i vjeruju u Njega i traže oprost za one koji vjeruju. "Gospodaru naš! Obuhvatio si svaku stvar milošću i znanjem, pa oprosti onima koji se pokaju i slijede put Tvoj, i sačuvaj ih kazne džehima.'),
(4141, 40, 8, 'Gospodaru naš! I uvedi ih u bašče Adna koje si im obećao, i onog ko čini dobro od očeva njihovih i žena njihovih i potomaka njihovih. Uistinu! Ti, Ti si Moćni, Mudri.'),
(4142, 40, 9, 'I sačuvaj ih zlih djela, a koga sačuvaš zlih djela Tog dana - pa zaista si mu se smilovao. A to je taj uspjeh veličanstven.'),
(4143, 40, 10, 'Uistinu, oni koji ne vjeruju, biće pozvani: "Doista je mržnja Allahova veća od mržnje vaše - duša vaših - kad ste pozivani vjerovanju, pa niste vjerovali."'),
(4144, 40, 11, 'Reći će: "Gospodaru naš! Usmrtio si nas dva puta i oživio nas dva puta, pa smo priznali grijehe naše; pa ima li za izlazak ikakva puta?"'),
(4145, 40, 12, '"To zbog toga što - kad se prizivao Allah, Jedini On - niste vjerovali, a kad Mu se pridruživalo, vjerovali ste! Pa sud pripada Allahu, Uzvišenom, Velikom."'),
(4146, 40, 13, 'On je Taj koji vam pokazuje znakove Svoje i spušta vam s neba opskrbu. A poučava se samo ko se obrati.'),
(4147, 40, 14, 'Zato prizivajte Allaha, odani Njegovoj vjeri - makar mrzili nevjernici -'),
(4148, 40, 15, 'Posjednik najviših deredža, Vlasnik Arša! Stavlja Duh naredbe Svoje na koga hoće od robova Svojih, da upozori na Dan susreta,'),
(4149, 40, 16, 'Dan kad oni izađu, neće biti skriveno Allahu njihovo ništa. Čija je vlast Danas? Allaha Jedinog, Onog koji pokorava!'),
(4150, 40, 17, 'Danas će se platiti svakoj duši šta je zaslužila. Nema zulma Danas! Uistinu! Allah je brz obračunom.'),
(4151, 40, 18, 'I upozori ih Danom približavajućim, kad će srca kod grla biti zaprečena; neće imati zalimi nikakvog prijatelja niti posrednika uslišanog.'),
(4152, 40, 19, 'Zna nevjerne oči i ono šta kriju grudi.'),
(4153, 40, 20, 'I Allah sudi s Istinom, a oni koje prizivaju mimo Njega, ne sude ničim. Uistinu! Allah - On je Onaj koji čuje, Onaj koji vidi.'),
(4154, 40, 21, 'Zar nisu putovali po Zemlji, pa vidjeli kakav je bio kraj onih prije njih? Bili su oni žešći od njih u sili i tragovima na Zemlji, pa ih je dograbio Allah zbog grijeha njihovih, i nije im bilo od Allaha nikakvog zaštitnika.'),
(4155, 40, 22, 'To zato što su im poslanici njihovi dolazili s dokazima jasnim, pa nisu vjerovali. Zato ih je dograbio Allah. Uistinu! On je Silan, Žestok kaznom!'),
(4156, 40, 23, 'I doista smo poslali Musaa sa znakovima Našim i autoritetom očitim,'),
(4157, 40, 24, 'Faraonu i Hamanu i Karunu, pa su rekli: "Vračar, lažov!"'),
(4158, 40, 25, 'Pa pošto im dođe s Istinom od Nas, rekoše: "Ubijajte sinove onih koji s njim vjeruju, a poštedite žene njihove." A spletka nevjernika je samo zabluda.'),
(4159, 40, 26, 'I reče faraon: "Pustite me da ubijem Musaa, a nek pozove Gospodara svog. Uistinu, ja se plašim da ne promijeni vjeru vašu ili da ne prouzrokuje fesad na Zemlji."'),
(4160, 40, 27, 'A Musa reče: "Uistinu, ja tražim zaštitu u Gospodara svog i Gospodara vašeg od svakog oholog (koji) ne vjeruje u Dan obračuna."'),
(4161, 40, 28, 'I čovjek vjernik iz porodice faraonove, (koji) je krio vjerovanje svoje, reče: "Zar da ubijete čovjeka što kaže: \'Gospodar moj je Allah\', a doista vam je došao s dokazima jasnim od Gospodara vašeg? A ako je lažac, pa protiv njega je laž njegova - a ako je iskren, pogodiće vas dio onog čime vam prijeti. Uistinu, Allah ne upućuje onoga ko pretjeruje, ko laže.'),
(4162, 40, 29, 'O narode moj! Vaša je vlast danas, pobjednici ste na Zemlji. Pa ko će nas pomoći protiv sile Allahove, ako nam dođe?" Reče faraon: "Pokazujem vam jedino šta vidim, i samo vas upućujem putu ispravnom."'),
(4163, 40, 30, 'A onaj koji je vjerovao reče: "O narode moj! Uistinu se ja plašim protiv vas (nečeg) sličnog danu partija (prethodnih),'),
(4164, 40, 31, 'Sličnog stanju naroda Nuhovog, i Ada i Semuda, i onih poslije njih. A ne želi Allah zulm robovima."'),
(4165, 40, 32, '"I o narode moj! Uistinu, ja se plašim za vas Dana pozivanja,'),
(4166, 40, 33, 'Dana kad ćete se okrenuti, bjegunci ćete biti, nećete vi od Allaha imati nikakvog zaštitnika. A koga zavede Allah - pa nema njemu nikakvog upućivača.'),
(4167, 40, 34, 'I doista vam je Jusuf još prije došao sa dokazima jasnim, pa niste prestali biti u sumnji o onom šta vam je donio - dok, kad je umro, rekli ste: "Neće Allah poslije njega podići poslanika." Tako Allah zabluđuje onog ko je rasipnik, sumnjivac,'),
(4168, 40, 35, 'One koji raspravljaju o ajetima Allahovim bez ikakva dokaza prispjelog njima. Velika je mržnja kod Allaha (zbog toga) i kod onih koji vjeruju. Tako pečati Allah svako srce oholog, silnika.'),
(4169, 40, 36, 'I reče faraon: "O Hamane! Sagradi mi toranj, da bih ja dosegao sredstva,'),
(4170, 40, 37, 'Sredstva nebeska, pa se uspeo bogu Musaovom - a uistinu, ja ga smatram lašcem." I tako je uljepšano faraonu zlo djela njegovog i odvraćen je od puta, a spletka faraonova (vodi) samo u propast.'),
(4171, 40, 38, 'I reče onaj koji je vjerovao: "O narode moj! Slijedite mene, uputiću vas putu ispravnom.'),
(4172, 40, 39, 'O narode moj! Doista je ovaj život Dunjaa uživanje, a uistinu, Ahiret - on je kuća ostanka.'),
(4173, 40, 40, 'Ko učini zlo, pa biće plaćen samo sličnim njemu, a ko učini dobro, muškarac ili žena, a on bude vjernik - pa takvi će ući u Džennet - biće u njemu opskrbljivani bez ikakvog računa.'),
(4174, 40, 41, 'I o narode moj! [ta mi je? Pozivam vas spasu, a pozivate me vatri.'),
(4175, 40, 42, 'Pozivate me da ne vjerujem u Allaha i pridružujem Njemu ono o čemu ja nemam znanje, a ja vas pozivam Moćnom, Oprosniku.'),
(4176, 40, 43, 'Nema sumnje da me pozivate onom kome nema pozivanja na Dunjau niti na Ahiretu i da je povratak naš Allahu; i da će prekršitelji - oni biti stanovnici vatre.'),
(4177, 40, 44, 'Pa sjetićete se šta vam govorim, a stvar svoju povjeravam Allahu. Uistinu! Allah je Vidilac robova."'),
(4178, 40, 45, 'Zato ga je Allah sačuvao zala koja su planirali, a ljudstvo faraonovo će pogoditi zla kazna:'),
(4179, 40, 46, 'Vatra - izlagaće se na nju jutrom i navečer; a na Dan kad nastupi Čas: "Uvedite ljudstvo faraonovo najžešćoj kazni."'),
(4180, 40, 47, 'I kad se budu prepirali u vatri, tad će slabi reći onima koji su se oholili: "Uistinu! Mi smo bili vaši sljedbenici, pa jeste li vi ti koji će od nas otkloniti dio vatre?"'),
(4181, 40, 48, 'Oni koji su se oholili će reći: "Uistinu, svi smo mi u njoj. Uistinu, Allah je već presudio među robovima!"'),
(4182, 40, 49, 'I reći će oni koji su u vatri, čuvarima Džehennema: "Zovite Gospodara vašeg da nam olakša dan kazne."'),
(4183, 40, 50, 'Reći će: "A zar vam nisu poslanici vaši dolazili sa dokazima jasnim?" Reći će: "Svakako." Reći će: "Onda dozivajte!" A dowa nevjernika je samo zabluda.'),
(4184, 40, 51, 'Uistinu! Mi pomažemo poslanike Naše i one koji vjeruju u životu Dunjaluka i na Dan kad ustanu svjedoci,'),
(4185, 40, 52, 'Dan kad neće koristiti zalimima opravdanje njihovo, a imaće oni prokletstvo i imaće oni zlu kuću.'),
(4186, 40, 53, 'A doista smo Musau dali Uputu i sinovima Israilovim u naslijeđe dali Knjigu,'),
(4187, 40, 54, 'Uputu i Opomenu za posjednike razuma.'),
(4188, 40, 55, 'Zato se strpi! Uistinu, obećanje Allahovo je Istina; i traži oprost za grijeh svoj, i slavi sa hvalom Gospodara svog navečer i ujutro.'),
(4189, 40, 56, 'Uistinu, oni koji raspravljaju o ajetima Allahovim bez ikakvog dokaza njima došlog - u grudima njihovim je jedino ponos kojeg oni neće postići. Zato traži zaštitu u Allaha. Uistinu! On, On je Onaj koji čuje, Onaj koji vidi.'),
(4190, 40, 57, 'Sigurno je stvaranje nebesa i Zemlje veće od stvaranja ljudi, ali većina ljudi ne zna.'),
(4191, 40, 58, 'I nije isti slijepac i onaj koji vidi, niti oni koji vjeruju i rade dobra i činitelj zla. Malo što se poučavate!'),
(4192, 40, 59, 'Uistinu, Čas je dolazeći, nema sumnje u to, ali većina ljudi ne vjeruje.'),
(4193, 40, 60, 'A rekao je Gospodar vaš: "Zovite Me, odazvaću vam se." Uistinu! Oni koji se budu oholili prema obožavanju Mom, ući će u Džehennem poniženi.'),
(4194, 40, 61, 'Allah je Taj koji vam je načinio noć da se odmarate u njoj, a dan onim koji daje vidljivost. Uistinu, Allah je Vlasnik blagodati nad ljudima, ali, većina ljudi ne zahvaljuje.'),
(4195, 40, 62, 'To je Allah, Gospodar vaš, Stvoritelj svake stvari! Nema boga osim Njega; pa kud se odvraćate?'),
(4196, 40, 63, 'Tako su se odvraćali oni koji su ajete Allahove nijekali.'),
(4197, 40, 64, 'Allah je Taj koji vam je Zemlju učinio staništem, a nebo zdanjem, i oblikovao vas, pa uljepšao likove vaše i opskrbio vas iz dobrih stvari. To je Allah, Gospodar vaš; pa blagoslovljen neka je Allah, Gospodar svjetova!'),
(4198, 40, 65, 'On je Živi! Nema boga osim Njega! Zato prizivajte Njega, odani Njegovoj vjeri! Hvala neka je Allahu Gospodaru svjetova!'),
(4199, 40, 66, 'Reci: "Uistinu, meni je zabranjeno da obožavam one koje prizivate mimo Allaha, pošto su mi došli jasni dokazi od Gospodara mog, a naređeno mi je da se predam Gospodaru svjetova."'),
(4200, 40, 67, 'On je Taj koji vas je stvorio od prašine, zatim od kapi (sjemena), potom od zakvačka, zatim vas izvodi kao dojenče, potom da dostignete svoju punu snagu, zatim da budete starci - a od vas je onaj ko bude uzet prije - i da dostignete rok određeni, i da biste vi shvatili.'),
(4201, 40, 68, 'On je Taj koji oživljava i usmrćuje. Pa kad odluči stvar, tad tome samo kaže: "Budi!" - tad biva.'),
(4202, 40, 69, 'Zar ne vidiš one koji raspravljaju o ajetima Allahovim, kako se okreću,'),
(4203, 40, 70, 'Oni koji poriču Knjigu i ono s čim smo slali poslanike Naše - pa saznaće,'),
(4204, 40, 71, 'Kad na vratovima njihovim budu okovi i lanci, biće vučeni,'),
(4205, 40, 72, 'U ključaloj vodi, zatim u vatri prženi.'),
(4206, 40, 73, 'Potom će im se reći: "Gdje je ono što ste pridruživali,'),
(4207, 40, 74, 'Mimo Allaha?" Reći će: "Nestali su od nas; naprotiv, nismo ranije prizivali ništa." Tako Allah zabluđuje nevjernike.'),
(4208, 40, 75, 'To stoga što ste likovali na Zemlji bez prava, i što ste (obijesno) veseli bili.'),
(4209, 40, 76, 'Uđite na kapije Džehennema, vječno ćete biti u njemu; pa loše je boravište oholih.'),
(4210, 40, 77, 'Zato se strpi, uistinu je obećanje Allahovo Istina. Pa ako ti pokažemo dio onog šta im obećavamo ili te uzmemo - ta Nama će se vratiti.'),
(4211, 40, 78, 'I doista smo slali poslanike prije tebe. Između njih je onaj o kom smo ti kazivali, i od njih onaj o kom ti nismo kazivali; i nije bilo za poslanika da donese znak, izuzev s dozvolom Allahovom. Pa kad dođe naredba Allahova, sudiće se s Istinom, a izgubiće ondje lažljivci.'),
(4212, 40, 79, 'Allah je Taj koji vam je načinio stoku, da jašete (neke) od njih, i od njih jedete,'),
(4213, 40, 80, 'A imate vi u njoj koristi - i da njome zadovoljite potrebu u grudima vašim, a na njoj i na plovilima se transportujete.'),
(4214, 40, 81, 'I pokazuje vam znakove Svoje. Pa koji od znakova Allahovih poričete?'),
(4215, 40, 82, 'Pa zar nisu putovali po Zemlji, te vidjeli kakav je bio kraj onih prije njih. Bili su brojniji od njih i žešći u sili i tragovima u Zemlji, pa im nije koristilo šta su bili stekli.'),
(4216, 40, 83, 'Pa pošto su im poslanici njihovi došli sa dokazima jasnim, likovali su s onim šta je kod njih od znanja, a spopalo ih je ono čemu su se izrugivali.'),
(4217, 40, 84, 'Pa pošto bi vidjeli silu Našu, rekli bi: "Vjerujemo u Allaha - Njega Jedinog, a ne vjerujemo u ono šta smo pridruživali."'),
(4218, 40, 85, 'Tad im ne bi koristilo vjerovanje njihovo pošto bi vidjeli silu Našu. Sunnet je to Allahov koji je doista prošao na robovima Njegovim, a izgubili bi ondje nevjernici!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Ha. Mim.'),
(4220, 41, 2, 'Objava je od Milostivog, Milosrdnog.'),
(4221, 41, 3, 'Knjiga čiji su ajeti razloženi, Kur\'an na arapskom, za ljude koji znaju.'),
(4222, 41, 4, 'Donosilac je radosne vijesti i Opomene. Pa odvratila se većina njih, pa oni ne čuju,'),
(4223, 41, 5, 'I govore: "Srca su naša pod pokrivkama prema onom čemu nas pozivaš, i u ušima našim je gluhoća, a između nas i između tebe je veo; zato radi, uistinu, mi smo radnici."'),
(4224, 41, 6, 'Reci: "Ja sam samo smrtnik sličan vama. Meni se objavljuje da je uistinu Bog vaš, Bog Jedini, zato uzmite put pravi Njemu i tražite oprost Njegov. A teško mušricima,'),
(4225, 41, 7, 'Onima koji ne daju zekat, a oni su u Ahiret nevjernici.'),
(4226, 41, 8, 'Uistinu, oni koji vjeruju i čine dobra djela, imaće oni nagradu neprekidnu."'),
(4227, 41, 9, 'Reci: "Ne vjerujete li uistinu vi u Onog ko je stvorio Zemlju u dva dana, i pripisujete Mu suparnike? To je Gospodar svjetova!"'),
(4228, 41, 10, 'I načinio je u njoj planine stabilne - iznad nje, i blagoslovio je i odmjerio na njoj njene proizvode prehrane u četiri dana jednaka - (informacija) za one koji pitaju.'),
(4229, 41, 11, 'Zatim se usmjerio nebu - a ono je bilo dim - pa rekao njemu i Zemlji: "Dođite milom ili silom!" Rekoše: "Dolazimo poslušni."'),
(4230, 41, 12, 'Potom ih je uredio kao sedam nebesa u dva dana i objavio u svakom nebu stvar njegovu. A uljepšali smo nebo najbliže svjetiljkama i zaštitom. To je diktat Moćnog, Znalca.'),
(4231, 41, 13, 'Pa ako se odvrate, tad reci: "Upozoravam vas munjom sličnoj munji Ada i Semuda",'),
(4232, 41, 14, 'Kad su im dolazili poslanici ispred njih i iza njih: "Ne obožavajte, izuzev Allaha!" Rekli su: "Da je htio Gospodar naš, sigurno bi spustio meleke. Pa uistinu, mi smo u ono s čim ste poslati, nevjernici."'),
(4233, 41, 15, 'Pa što se tiče Ada - pa oholili su se na Zemlji bez ikakvog prava i govorili: "Ko je žešći od nas u sili?" Zar nisu vidjeli da je Allah koji ih je stvorio - On žešći od njih u sili; a znakove Naše su nijekali.'),
(4234, 41, 16, 'Zato smo na njih poslali vjetar olujni u danima nesretnim, da damo da oni iskuse kaznu poniženja u životu Dunjaa; a sigurno je kazna Ahireta sramnija, i oni neće biti pomognuti.'),
(4235, 41, 17, 'A što se tiče Semuda, upućivali smo ih, pa su više voljeli sljepilo od Upute, te ih je pogodila munja kazne ponižavajuće zbog onog što su zaslužili,'),
(4236, 41, 18, 'A spasili smo one koji su vjerovali i bili bogobojazni.'),
(4237, 41, 19, 'A na Dan kad budu sakupljeni neprijatelji Allahovi k vatri - tad će oni biti raspodijeljeni.'),
(4238, 41, 20, 'Dok, kad joj dođu, svjedočiće protiv njih sluh njihov i vidovi njihovi i kože njihove, o onom šta su radili.'),
(4239, 41, 21, 'I reći će kožama svojim: "Zašto svjedočite protiv nas?" Reći će: "Dao nam je Allah da govorimo - Onaj koji je govorom obdario svaku stvar" - a On vas je stvorio prvi put, i Njemu se vraćate.'),
(4240, 41, 22, 'A niste se krili da ne svjedoči protiv vas sluh vaš, niti vidovi vaši, niti kože vaše, nego ste mislili da Allah ne zna mnogo od onog šta ste radili.'),
(4241, 41, 23, 'A to vaše mišljenje koje ste pretpostavljali o Gospodaru svom, upropastilo vas je, pa ste postali od gubitnika.'),
(4242, 41, 24, 'Pa ako trpe - pa vatra je boravište njihovo; a ako pitaju za naklonost, pa nisu oni od onih kojima je ona data.'),
(4243, 41, 25, 'I dodijelili smo im drugove, pa su im uljepšali ono što je ispred njih i što je iza njih. I obistinila se na njima Riječ u zajednicama već prošlim prije njih od džinna i ljudi. Uistinu, oni su bili gubitnici.'),
(4244, 41, 26, 'I kažu oni koji ne vjeruju: "Ne slušajte ovaj Kur\'an i brbljajte u njemu, da biste vi nadvladali."'),
(4245, 41, 27, 'Pa sigurno ćemo dati da oni koji ne vjeruju iskuse kaznu žestoku, i sigurno ćemo ih platiti za najgore šta su radili.'),
(4246, 41, 28, 'To je plaća neprijateljima Allahovim: Vatra! Imaće oni u njoj kuću vječnosti: plaća što su ajete Naše nijekali.'),
(4247, 41, 29, 'I reći će oni koji nisu vjerovali: "Gospodaru naš! Pokaži nam one od džinna i ljudi koji su nas zaveli, da ih stavimo pod noge naše, da budu od najdonjih."'),
(4248, 41, 30, 'Uistinu, oni koji govore: "Gospodar naš je Allah", zatim budu ispravni, spuštaju se na njih meleci: "Ne plašite se i ne žalostite i radujte se Džennetu koji vam se obećava.'),
(4249, 41, 31, 'Mi smo zaštitnici vaši u životu Dunjaa i na Ahiretu. A imaćete vi u njemu šta zažele duše vaše, i imaćete vi u njemu šta zatražite,'),
(4250, 41, 32, 'Ugošćenje od Oprosnika, Milosrdnog.'),
(4251, 41, 33, 'A ko je bolji govorom od onog ko poziva Allahu i radi dobro i kaže: "Uistinu! Ja sam od muslimana."'),
(4252, 41, 34, 'I nije isto dobro i zlo. Odbij (zlo) s onim što je bolje, tad gle! onaj što je između tebe i njega neprijateljstvo - kao da je on prijatelj prisan.'),
(4253, 41, 35, 'A pružiće se to samo onima koji se strpe, i neće se to pružiti, izuzev posjedniku sreće goleme.'),
(4254, 41, 36, 'A ako te navede od šejtana podsticaj, tad potraži zaštitu u Allaha. Uistinu! On, On je Onaj koji čuje, Znalac.'),
(4255, 41, 37, 'I od znakova Njegovih je noć i dan, i Sunce i Mjesec. Ne padajte na sedždu Suncu, ni Mjesecu, a padajte na sedždu Allahu koji ih je stvorio, ako Njega obožavate!'),
(4256, 41, 38, 'Pa ako se uzohole, ta oni koji su kod Gospodara tvog, slave Ga noću i danju i oni se ne umaraju.'),
(4257, 41, 39, 'I od znakova Njegovih je što ti vidiš zemlju smirenu, pa kad na nju spustimo vodu, zatreperi i uzbuja. Uistinu, Onaj koji je oživljava je Oživljavač mrtvih. Uistinu, On nad svakom stvari ima moć.'),
(4258, 41, 40, 'Uistinu, oni koji iskreću ajete Naše, neće se od Nas sakriti. Pa je li ko u vatru bude bačen, bolji, ili ko dođe siguran na Dan kijameta? Radite šta hoćete! Uistinu, On je onog šta radite Vidilac.'),
(4259, 41, 41, 'Uistinu, oni koji ne vjeruju u Opomenu pošto im je došla a uistinu, on je Knjiga silna.'),
(4260, 41, 42, 'Ne prilazi joj neistina ispred nje, niti iza nje; Objava je od Mudrog, Hvaljenog.'),
(4261, 41, 43, 'Govori ti se samo ono što je već rečeno poslanicima prije tebe. Uistinu, Gospodar tvoj je Vlasnik oprosta i Vlasnik kazne bolne.'),
(4262, 41, 44, 'A da smo ga učinili Kur\'anom na stranom (jeziku), sigurno bi rekli: "Što nisu razjašnjeni ajeti njegovi? Zar strani (jezik) a Arap?" Reci: "On je onima koji vjeruju Uputa i Lijek." A koji ne vjeruju, u ušima njihovim je gluhoća i on je za njih sljepilo. Takvi će biti pozivani iz mjesta dalekog.'),
(4263, 41, 45, 'I doista smo Musau dali Knjigu, pa se o njoj prepiralo. A da nije Riječi prethodne od Gospodara tvog, sigurno bi među njima bilo presuđeno. A uistinu, oni su o njemu u sumnji ozbiljnoj.'),
(4264, 41, 46, 'Ko radi dobro - pa za dušu je njegovu, a ko čini zlo - pa protiv nje je; i nije Gospodar tvoj nikako zalim robovima.'),
(4265, 41, 47, 'Njemu se vraća znanje Časa. I ne izlaze plodovi iz čaški svojih, i ne nosi nijedno žensko, niti rodi - osim sa znanjem Njegovim. A na Dan kad ih zovne: "Gdje su ortaci Moji?" - reći će: "Objavljujemo Ti: nije od nas nijedan svjedok."'),
(4266, 41, 48, 'I nestaće od njih ono šta su prizivali ranije i znaće da oni neće imati nikakvog pribježišta.'),
(4267, 41, 49, 'Ne zamara čovjeka nimalo iskanje dobra, a ako ga dodirne zlo, tad je onaj koji zdvaja, očajan je.'),
(4268, 41, 50, 'A ako damo da on okusi milost od Nas, nakon što ga je nesreća dotakla, sigurno govori: "Ovo je moje i ne mislim da će Čas nastupiti. A ako se vratim Gospodaru svom, uistinu ću ja kod Njega imati dobro." Pa sigurno ćemo obavijestiti one koji ne vjeruju, o onome šta su radili, i sigurno ćemo dati da oni okuse kaznu grubu.'),
(4269, 41, 51, 'I kad čovjeka obdarimo, odvrati se i udalji u svoju stranu, a kad ga dotakne zlo, tad je dowe široke.'),
(4270, 41, 52, 'Reci: "Da li vidite, ako je od Allaha, potom niste vjerovali u njega, ko je zabludjeliji od onog ko je u protivrječju dalekom?"'),
(4271, 41, 53, 'Pokazivaćemo im znakove Naše na horizontima i u dušama njihovim, dok im ne bude jasno da je on Istina. Zar nije dovoljno, Gospodar tvoj, što je On nad svakom stvari svjedok?'),
(4272, 41, 54, 'Uistinu! Oni su u sumnji o susretu Gospodara svog! Besumnje! Uistinu! On je svake stvari Okružitelj!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Ha. Mim.'),
(4274, 42, 2, '\'Ain. Sin. Kaf.'),
(4275, 42, 3, 'Tako objavljuje tebi (kao) i onima prije tebe, Allah, Moćni, Mudri.'),
(4276, 42, 4, 'Njegovo je šta je u nebesima i šta je na Zemlji, a On je Uzvišeni, Veličanstveni.'),
(4277, 42, 5, 'Gotovo da se iznad njih nebesa raspuknu, a meleci slave sa hvalom Gospodara svog, i traže oprost za onog ko je na Zemlji. Besumnje, uistinu Allah - On je Oprosnik, Milosrdni.'),
(4278, 42, 6, 'A oni koji mimo Njega uzimaju zaštitnike, Allah je Čuvar nad njima; a nisi ti nad njima zaštitnik.'),
(4279, 42, 7, 'I tako ti objavljujemo Kur\'an na arapskom, da upozoriš Majku gradova i onog ko je oko nje, i upozoriš Danom okupljanja u koji nema sumnje. Skupina će u Džennet, a skupina u seir.'),
(4280, 42, 8, 'A da je htio Allah, sigurno bi ih učinio ummom jednom, međutim, uvodi koga hoće u milost Svoju. A zalimi - neće oni imati nikakva zaštitnika, niti pomagača.'),
(4281, 42, 9, 'Zar su uzeli mimo Njega zaštitnike? Pa Allah - On je Zaštitnik! A On oživljava mrtve i On nad svakom stvari ima moć.'),
(4282, 42, 10, 'A ono od stvari u čemu se razilazite, sud toga je Allahov. To je Allah, Gospodar moj; u Njega se pouzdavam i Njemu se obraćam:'),
(4283, 42, 11, 'Stvoritelj nebesa i Zemlje! Načinio vam je od duša vaših parice, i od stoke parove - tako vas umnožava. Nema Njemu sličnosti ništa; a On je Onaj koji čuje, Onaj koji vidi.'),
(4284, 42, 12, 'Njegovi su ključevi nebesa i Zemlje; pruža opskrbu kome hoće, i uskraćuje. Uistinu, On je o svakoj stvari Znalac.'),
(4285, 42, 13, 'Propisao vam je od vjere ono što je naložio Nuhu, i šta smo objavili tebi, i šta smo naložili Ibrahimu i Musau i Isau: "Ispovijedajte vjeru i ne podvajajte se u njoj!" Teško je mušricima to čemu ih pozivaš. Allah odabire Sebi koga hoće i Sebi upućuje onog ko se obraća.'),
(4286, 42, 14, 'A podijelili su se jedino nakon što im je došlo znanje, zavišću između njih. A da nije Riječi prethodne od Gospodara tvog do roka određenog, zaista bi među njima bilo presuđeno. A uistinu, oni kojima je Knjiga data u naslijeđe poslije njih o njoj su u sumnji ozbiljnoj.'),
(4287, 42, 15, 'Pa zato ovom pozivaj i budi prav kao što ti je naređeno, i ne slijedi strasti njihove, i reci: "Vjerujem u ono što je Allah objavio od Knjige, i naređeno mi je da među vama budem pravedan. Allah je Gospodar naš i Gospodar vaš. Nama djela naša, a vama djela vaša. Nema dokazivanja između nas i između vas. Allah će nas sastaviti, a Njemu je dolazište!'),
(4288, 42, 16, 'A oni koji raspravljaju o Allahu, nakon što Mu se odazvalo, dokaz njihov je ništavan kod Gospodara njihovog i na njima je srdžba, a imaće oni kaznu žestoku.'),
(4289, 42, 17, 'Allah je Taj koji je objavio Knjigu s Istinom i mjerilom. A šta znaš ti? Možda je Čas blizu!'),
(4290, 42, 18, 'Požuruju ga oni koji u njega ne vjeruju, a oni koji vjeruju, boje ga se i znaju da je on Istina. Besumnje! Uistinu, oni koji raspravljaju o Času, u zabludi su dalekoj?'),
(4291, 42, 19, 'Allah je Fin robovima Svojim. Opskrbljuje koga hoće; a On je Silni, Moćni.'),
(4292, 42, 20, 'Ko bude želio usjev Ahireta, povećaćemo mu usjev njegov, a ko bude želio usjev Dunjaa, daćemo mu od njega i neće on imati u Ahiretu nikakva udjela.'),
(4293, 42, 21, 'Zar oni imaju ortake koji su im od vjere propisali ono što Allah ne dozvaljava? A da nije Riječi odlučujuće, sigurno bi bilo presuđeno među njima. A uistinu, zalimi - imaće oni kaznu bolnu.'),
(4294, 42, 22, 'Vidjećeš zalime brižne zbog onog šta su zaradili, a to će pasti na njih. A oni koji budu vjerovali i radili dobra djela, biće u bujnim vrtovima Dženneta. Imat će šta hoće kod Gospodara svog. To je to, blagodat velika.'),
(4295, 42, 23, 'To je ono čime Allah obveseljava robove Svoje koji vjeruju i čine dobra djela. Reci: "Ne tražim od vas za to nagradu, osim ljubavi prema rodbini." A ko zaradi dobro, dodaćemo mu u tome dobro. Uistinu! Allah je Oprosnik, Zahvalni.'),
(4296, 42, 24, 'Zar govore: "Izmislio je o Allahu laž?" Pa da je htio Allah, zapečatio bi srce tvoje. A briše Allah neistinu i utvrđuje Istinu riječima Svojim. Uistinu! On je Znalac grudi.'),
(4297, 42, 25, 'I On je Taj koji prima pokajanje robova Svojih. A oprašta zla djela i zna šta činite,'),
(4298, 42, 26, 'I odaziva se onima koji vjeruju i rade dobra djela, i uvećava im iz dobrote Svoje. A kafiri - imaće oni kaznu žestoku.'),
(4299, 42, 27, 'I kad bi Allah pružio opskrbu robovima Svojim, sigurno bi se pobunili na Zemlji, ali, spušta po mjeri, šta hoće. Uistinu! On je o robovima Svojim Obaviješteni, Vidilac.'),
(4300, 42, 28, 'I On je Taj koji spušta kišu nakon što postanu očajni, i rasprostire milost Svoju; a On je Zaštitnik, Hvaljeni.'),
(4301, 42, 29, 'I od znakova Njegovih je stvaranje nebesa i Zemlje i šta je po njima rasijao od živih bića. I on je za skupljanje njihovo - kad bude htio - kadar.'),
(4302, 42, 30, 'A šta vas pogodi od nesreće - to je šta ste zaradili rukama svojim, a oprašta mnogo.'),
(4303, 42, 31, 'I niste vi ti koji će umaći u Zemlji, a nemate vi mimo Allaha nikakvog zaštitnika, niti pomagača.'),
(4304, 42, 32, 'I od znakova Njegovih su plovila na moru kao brda;'),
(4305, 42, 33, 'Ako hoće umiri vjetar, pa postanu nepomična na površini njegovoj. Uistinu, u tome su znaci za svakog strpljivog, zahvalnog -'),
(4306, 42, 34, 'Ili ih uništi zbog onog šta su zaradili, a oprašta mnogo.'),
(4307, 42, 35, 'A znaju oni koji raspravljaju o ajetima Našim, (da) njima nema nikakva pribježišta.'),
(4308, 42, 36, 'Pa šta vam je od stvari dato - pa (to) je uživanje života Dunjaa, a šta je kod Allaha, bolje je i trajnije za one koji vjeruju i u Gospodara svog se pouzdavaju,'),
(4309, 42, 37, 'I one koji se klonu velikih grijeha i nemorala, a kad se rasrde, oni opraštaju,'),
(4310, 42, 38, 'I one koji se odazovu Gospodaru svom i obave salat - a posao njihov je (predmet) savjetovanja među njima - i od onog čim smo ih opskrbili udjeljuju,'),
(4311, 42, 39, 'I one koji, kad ih pogodi nasilje, oni se brane.'),
(4312, 42, 40, 'A plaća za zlodjelo je zlodjelo slično tome. Zato ko oprosti i popravi, pa njegova nagrada je od Allaha. Uistinu! On ne voli zalime.'),
(4313, 42, 41, 'I doista, ko se odupre nakon zulma (nanešenog) njemu - pa takvi - nema protiv njih nikakva puta.'),
(4314, 42, 42, 'Jedino je put protiv onih koji čine zulm ljudima i bune se na Zemlji bez ikakva prava; ti takvi će imati kaznu bolnu.'),
(4315, 42, 43, 'A doista, ko se strpi i oprosti - uistinu, to je od odlučujućih stvari.'),
(4316, 42, 44, 'A koga zabludi Allah, pa nema njemu nikakvog zaštitnika poslije Njega. I vidjećeš zalime pošto vide kaznu, govoriće: "Ima li povratku ikakva puta?"'),
(4317, 42, 45, 'I vidjećeš ih, izlagaće se na nju poniženi od srama, gledaće ispod oka prikriveno. I reći će oni koji vjeruju: "Uistinu! Gubitnici su oni koji su izgubili duše svoje i porodice svoje na Dan kijameta." Besumnje! Uistinu! Zalimi će biti u kazni trajnoj!'),
(4318, 42, 46, 'I neće oni imati nikakvih zaštitnika da im pomognu mimo Allaha. A koga zabludi Allah - pa nema za njega nikakva puta.'),
(4319, 42, 47, 'Odazovite se Gospodaru svom prije nego dođe Dan kojem od Allaha neće biti odbijanja. Nećete vi Tog dana imati nikakvo sklonište i nećete vi imati (mogućnost) nikakvog poricanja.'),
(4320, 42, 48, 'Pa ako se odvrate - ta nismo te poslali nad njima čuvarom. Na tebi je jedino dostava. A uistinu, kad Mi damo da čovjek od Nas okusi milost, raduje joj se. A ako ih pogodi zlo zbog onog šta su pripravile ruke njihove, tad je čovjek uistinu nezahvalan.'),
(4321, 42, 49, 'Allahova je vlast nebesa i Zemlje. Stvara šta hoće! Daruje kome hoće žensko, a daruje kome hoće muško;'),
(4322, 42, 50, 'Ili ih sparuje, muške i ženske, a koga hoće - učini ga neplodnim. Uistinu! On je Znalac, Moćni.'),
(4323, 42, 51, 'A nije za smrtnika da mu govori Allah izuzev objavom, ili iza hidžaba, ili (da) pošalje poslanika, pa se objavljuje s dopuštanjem Njegovim šta hoće. Uistinu! On je Uzvišeni, Mudri.'),
(4324, 42, 52, 'I tako smo ti objavili Duh naredbe Naše. Nisi znao šta je Knjiga, niti iman. Ali, učinili smo je svjetlom - upućujemo njime koga hoćemo od robova Naših. I uistinu, ti upućuješ putu pravom,'),
(4325, 42, 53, 'Putu Allahovom, Onog čije je šta je u nebesima i šta je na Zemlji. Besumnje! Allahu putuju stvari!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Ha. Mim.'),
(4327, 43, 2, 'Tako mi Kitaba jasnog!'),
(4328, 43, 3, 'Uistinu, Mi smo ga učinili Kur\'anom na arapskom, da biste vi razumjeli.'),
(4329, 43, 4, 'A uistinu, on je u Majci-knjizi kod Nas uzvišen, mudar.'),
(4330, 43, 5, 'Pa zar da potpuno okrenemo od vas Opomenu što ste narod pretjerujućih?'),
(4331, 43, 6, 'A koliko smo vjerovjesnika poslali ranijima!'),
(4332, 43, 7, 'I nije im došao nijedan vjerovjesnik, a da mu se nisu rugali.'),
(4333, 43, 8, 'Zato smo uništavali (one) žešće od ovih u sili, i prošao bi primjer ranijih.'),
(4334, 43, 9, 'A ako ih upitaš: "Ko je stvorio nebesa i Zemlju?" - sigurno će reći: "Stvorio ih je Moćni, Znalac",'),
(4335, 43, 10, 'Onaj koji je za vas Zemlju učinio ležajem, i načinio vam na njoj puteve, da biste se vi uputili;'),
(4336, 43, 11, 'I Koji iz neba spušta vodu po mjeri, pa njome proživljujemo predio mrtvi - tako ćete biti izvedeni -'),
(4337, 43, 12, 'I Koji je stvorio parove svega, i načinio vam plovila i stoku što jašete,'),
(4338, 43, 13, 'Da se smjestite na leđa njena, potom sjetite blagodati Gospodara svog kad se na njoj uspravite, i reknete: "Slava Onom koji nam je ovo potčinio, a ne bismo mi bili njeni ukrotitelji."'),
(4339, 43, 14, 'A uistinu, mi smo Gospodaru našem povratnici!'),
(4340, 43, 15, 'I pripisuju Mu od robova Njegovih dio! Uistinu, čovjek je nezahvalnik očiti.'),
(4341, 43, 16, 'Zar uzima od onog šta stvara, kćeri, a vas odlikuje sinovima?'),
(4342, 43, 17, 'A kad se neki od njih obraduje onim šta je Milostivom navodio kao primjer, zasjeni lice njegovo crnilom, i on je pun gnjeva.'),
(4343, 43, 18, 'Zar onaj ko se podiže u nakitu, a on nije u prepirci jasan?'),
(4344, 43, 19, 'I tretiraju meleke - one koji su robovi Milostivog - ženskima. Jesu li bili svjedoci stvaranja njihovog? Zapisaće se svjedočenje njihovo i biće pitani.'),
(4345, 43, 20, 'I govore: "Da je htio Milostivi, ne bismo ih obožavali." Nemaju oni o tome nikakvo znanje. Oni samo nagađaju.'),
(4346, 43, 21, 'Zar smo im dali Knjigu prije njega, pa su oni ti koji je se pridržavaju?'),
(4347, 43, 22, 'Naprotiv, govore: "Uistinu, mi smo našli očeve naše u ummetu i uistinu, mi smo na tragove njihove upućeni."'),
(4348, 43, 23, 'I isto tako, nismo u grad poslali nijednog od opominjača, a da nije rekao raskošnik njegov: "Uistinu, mi smo našli očeve naše u ummetu, i uistinu, mi tragove njihove slijedimo."'),
(4349, 43, 24, 'Rekao bi: "Zar iako sam vam donio bolju Uputu od onog na čemu ste našli očeve svoje?" Rekli bi: "Uistinu, mi smo u ono s čim ste poslati, nevjernici."'),
(4350, 43, 25, 'Zato bismo im se osvetili, pa pogledaj kakav je bio kraj poricatelja!'),
(4351, 43, 26, 'I kad reče Ibrahim ocu svom i narodu svom: "Uistinu, ja sam slobodan od onog šta obožavate,'),
(4352, 43, 27, 'Izuzev Onog koji me je stvorio, pa uistinu, On će me uputiti."'),
(4353, 43, 28, 'I učinio je to riječju trajnom u potomstvu svom, da bi se oni povratili.'),
(4354, 43, 29, 'Naprotiv, dao sam da uživaju ovi i očevi njihovi, dok im nije došla Istina i Poslanik jasni.'),
(4355, 43, 30, 'I pošto im je došla Istina, rekoše: "Ovo je sihr, i uistinu, mi smo u njega nevjernici."'),
(4356, 43, 31, 'I rekoše: "Što ovaj Kur\'an nije objavljen velikom čovjeku iz dva grada?"'),
(4357, 43, 32, 'Hoće li oni dijeliti milost Gospodara tvog? Mi raspodjeljujemo između njih njihova sredstva za izdržavanje u životu Dunjaa, i uzdigli ih jedne nad drugima po stepenima, da jedan od njih drugog uzima u službu. A milost Gospodara tvog je bolja od onog šta skupljaju.'),
(4358, 43, 33, 'A da ljudi neće postati jedna zajednica, sigurno bismo onom ko ne vjeruje u Milostivog, za kuće njihove načinili krovove od srebra i stepeništa uz koja se penju,'),
(4359, 43, 34, 'I za kuće njihove vrata i sofe na koje se naslanjaju,'),
(4360, 43, 35, 'I zlatne ukrase, iako je to sve užitak života Dunjaa. A Ahiret je kod Gospodara tvog za bogobojazne.'),
(4361, 43, 36, 'A ko bude slijep na sjećanje Milostivog, dodijelit ćemo mu šejtana, pa će mu on biti drug.'),
(4362, 43, 37, 'I uistinu, oni će ih odvraćati s puta, a misliće da su oni upućeni!'),
(4363, 43, 38, 'Dok, kad Nam dođe, reći će: "O da je između mene i između tebe rastojanje dvaju istoka. O loš si drug!"'),
(4364, 43, 39, 'I neće vam koristiti (ovaj) Dan - kad ste zulm činili - što ste vi u kazni saučesnici.'),
(4365, 43, 40, 'Pa zar ti da pročuješ gluhe ili uputiš slijepce i onog ko je u zabludi očitoj?'),
(4366, 43, 41, 'Pa ako uklonimo tebe, pa uistinu, Mi ćemo njima biti Osvetnici,'),
(4367, 43, 42, 'Ili ti pokažemo čime im prijetimo; pa uistinu, Mi smo nad njima Svemoćni.'),
(4368, 43, 43, 'Zato se drži onoga šta ti je objavljeno. Uistinu, ti si na putu pravom.'),
(4369, 43, 44, 'I uistinu, on je Opomena tebi i narodu tvom; a bićete pitani.'),
(4370, 43, 45, 'I pitaj onog koga smo poslali prije tebe od poslanika Naših, jesmo li učinili mimo Milostivog bogove da budu obožavani?'),
(4371, 43, 46, 'I doista smo poslali Musaa sa znakovima Našim faraonu i uglednicima njegovim, pa je rekao: "Uistinu, ja sam poslanik Gospodara svjetova."'),
(4372, 43, 47, 'Pa pošto im dođe sa znakovima Našim, gle - oni su im se ismijavali.'),
(4373, 43, 48, 'I nismo im pokazali nijedan znak, a da on nije bio veći od sestre svoje, i dograbili smo ih kaznom, da bi se oni povratili.'),
(4374, 43, 49, 'I rekoše: "O čarobnjače! Zovi nam Gospodara svog s onim što je s tobom sklopio. Uistinu, mi ćemo biti upućeni."'),
(4375, 43, 50, 'Pa pošto smo otklonili od njih kaznu, gle - oni prekršiše.'),
(4376, 43, 51, 'I objavi faraon u narodu svom, reče: "O narode moj! Zar nije moja vlast Egipta, i ovih rijeka (što) teku ispod mene? Pa zar ne vidite?'),
(4377, 43, 52, 'Zar (nisam) ja bolji od ovog - njega koji je prezren i jedva jasan?'),
(4378, 43, 53, 'Pa zašto na njega nisu stavljene narukvice od zlata ili s njim došli meleci (kao) pratioci?"'),
(4379, 43, 54, 'Tad učini lahkim narod svoj, te ga poslušaše. Uistinu, oni su bili narod grješnika.'),
(4380, 43, 55, 'Pa pošto su Nas rasrdili, osvetili smo im se te ih sve potopili,'),
(4381, 43, 56, 'Te ih učinili (stvari) prošlom, i primjerom kasnijima.'),
(4382, 43, 57, 'I pošto je naveden sin Merjemin kao primjer - gle! narod tvoj na to zagalami.'),
(4383, 43, 58, 'I rekoše: "Jesu li bogovi naši bolji ili on?" Naveli su ti ga samo radi rasprave. Naprotiv, oni su narod svađalački.'),
(4384, 43, 59, 'On je samo rob. Obdarili smo ga i učinili ga primjerom sinovima Israilovim.'),
(4385, 43, 60, 'A da smo htjeli, sigurno bismo od vas načinili meleke na Zemlji, da naslijede.'),
(4386, 43, 61, 'I uistinu, to je znanje o Času, zato nikako ne sumnjajte u to, i slijedite Mene: ovo je put pravi.'),
(4387, 43, 62, 'I neka vas nikako ne odvrati šejtan; uistinu, on vam je neprijatelj otvoreni.'),
(4388, 43, 63, 'I pošto Isa dođe sa dokazima jasnim, reče: "Doista sam vam došao s mudrošću i da vam objasnim dio onog o čemu se razilazite. Zato se bojte Allaha i mene poslušajte.'),
(4389, 43, 64, 'Uistinu, Allah! On je Gospodar moj i Gospodar vaš, zato Njega obožavajte; ovo je put pravi."'),
(4390, 43, 65, 'Tad su se razišle partije među njima. Pa teško onima koji čine zulm, od kazne Dana bolnog.'),
(4391, 43, 66, 'Čekuju li samo Čas da im dođe iznenada, a oni ne opaze?'),
(4392, 43, 67, 'Prijatelji će Tog dana jedni drugima neprijatelji biti - izuzev bogobojaznih.'),
(4393, 43, 68, '"O robovi Moji! Nema za vas straha Danas, niti ćete vi tugovati;'),
(4394, 43, 69, 'Oni koji su vjerovali u ajate Naše i bili muslimani.'),
(4395, 43, 70, 'Uđite u Džennet vi i žene vaše, veseleći se."'),
(4396, 43, 71, 'Obilaziće se oko njih sa posudama od zlata i peharima, a u njima će biti ono šta zažele duše i naslađuju oči, a u njemu ćete vi biti vječito.'),
(4397, 43, 72, 'A to je Džennet koji vam je dat u naslijeđe zbog onog šta ste radili.'),
(4398, 43, 73, 'Imaćete u njemu mnogo voća - od njega ćete jesti.'),
(4399, 43, 74, 'Uistinu, krivci će biti u kazni Džehennema vječito.'),
(4400, 43, 75, 'Neće im se ublažiti, a oni će u njoj biti očajnici.'),
(4401, 43, 76, 'I nismo im učinili zulm, nego su oni bili zalimi.'),
(4402, 43, 77, 'I dozivat će: "O Malik! Nek svrši s nama Gospodar tvoj." Reći će: "Uistinu, vi ste ti koji ostajete!"'),
(4403, 43, 78, 'Doista, donijeli smo vam Istinu, međutim, većina vas ste ti koji Istinu mrze.'),
(4404, 43, 79, 'Zar postavljaju stvar? - pa uistinu, Mi smo Ti koji postavljamo.'),
(4405, 43, 80, 'Zar misle da Mi ne čujemo tajnu njihovu i sašaptavanje njihovo? Svakako! A izaslanici Naši su kod njih - zapisuju.'),
(4406, 43, 81, 'Reci: "Kad bi Milostivi imao dijete, pa ja bih prvi bio od obožavatelja."'),
(4407, 43, 82, 'Slavljen neka je Gospodar nebesa i Zemlje, Gospodar Arša, od onog šta pripisuju.'),
(4408, 43, 83, 'Zato ih ostavi - upuštaju se i zabavljaju, dok ne sretnu Dan njihov kojim im se prijeti.'),
(4409, 43, 84, 'A On je Taj koji je u nebu Bog i na Zemlji Bog, i On je Mudri, Znalac.'),
(4410, 43, 85, 'I neka je blagoslovljen Onaj čija je vlast nebesa i Zemlje i onog što je između njih; a kod Njega je znanje Časa i Njemu ćete biti vraćeni.'),
(4411, 43, 86, 'I ne vladaju posredništvom oni koje mimo Njega prizivaju - izuzev ko svjedoči Istinu - a oni znaju.'),
(4412, 43, 87, 'A ako ih upitaš ko ih je stvorio, sigurno će reći: "Allah!" Pa kako se odvraćaju?'),
(4413, 43, 88, 'I govor njegov je: "O Gospodaru moj! Uistinu, ovi su ljudi koji ne vjeruju."'),
(4414, 43, 89, 'Pa okreni se od njih i reci: "Selam!" - pa saznat će.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Ha. Mim.'),
(4416, 44, 2, 'Tako mi Knjige jasne!'),
(4417, 44, 3, 'Uistinu, Mi smo je spustili u noći blagoslovljenoj - uistinu, Mi smo Opominjači -'),
(4418, 44, 4, 'U njoj se razdjeljuje svaka stvar mudra,'),
(4419, 44, 5, 'Naredbom od Nas - uistinu, Mi smo Pošiljaoci -'),
(4420, 44, 6, 'Milošću Gospodara tvog. Uistinu, On, On je Onaj koji čuje, Onaj koji zna,'),
(4421, 44, 7, 'Gospodara nebesa i Zemlje i šta je između njih - ako ste uvjereni.'),
(4422, 44, 8, 'Samo je On Bog! Oživljava i usmrćuje; Gospodar vaš i Gospodar očeva vaših ranijih.'),
(4423, 44, 9, 'Naprotiv, oni se u sumnji zabavljaju.'),
(4424, 44, 10, 'Pa sačekaj Dan kad se nebo pojavi s dimom očitim,'),
(4425, 44, 11, 'Prekrit će ljude! Ovo će biti kazna bolna.'),
(4426, 44, 12, '"Gospodaru naš! Otkloni od nas kaznu. Uistinu, mi smo vjernici."'),
(4427, 44, 13, 'Otkud im Opomena, a već im je došao Poslanik jasni,'),
(4428, 44, 14, 'Potom su se od njega odvratili i rekli: "Poučeni, luđak!"'),
(4429, 44, 15, 'Uistinu! Mi ćemo kaznu malo otkloniti. Doista! Vi ste povratnici.'),
(4430, 44, 16, 'Na Dan kad dograbimo grabljenjem najvećim, uistinu, Mi ćemo biti Osvetnici.'),
(4431, 44, 17, 'I doista smo iskušali prije njih narod faraonov, a došao im je poslanik plemeniti:'),
(4432, 44, 18, '"Izručite mi robove Allahove. Uistinu! Ja sam vam poslanik pouzdani.'),
(4433, 44, 19, 'I ne uzvisujte se protiv Allaha. Uistinu! Ja vam dolazim s autoritetom očitim,'),
(4434, 44, 20, 'I uistinu, ja tražim zaštitu u Gospodara svog i Gospodara vašeg, da me ne kamenujete.'),
(4435, 44, 21, 'A ako mi ne vjerujete, onda me ostavite."'),
(4436, 44, 22, 'Tad pozva (Musa) Gospodara svog: "Ovi su narod prestupnika."'),
(4437, 44, 23, '"Pa otputuj s robovima Mojim noću. Uistinu, vi ćete biti slijeđeni.'),
(4438, 44, 24, 'I ostavi more smirenim! Uistinu, oni će biti vojska potopljenih."'),
(4439, 44, 25, 'Koliko ostaviše bašča i izvora,'),
(4440, 44, 26, 'I žitnih polja i mjesta plemenitog,'),
(4441, 44, 27, 'I blagodati u kojoj su radosni bili.'),
(4442, 44, 28, 'Tako - a dali smo to u naslijeđe narodu drugih;'),
(4443, 44, 29, 'Pa nije plakalo nad njima nebo i Zemlja, i nije (im) bilo odgođeno.'),
(4444, 44, 30, 'I doista smo spasili sinove Israilove od kazne prezrene,'),
(4445, 44, 31, 'Od faraona; Uistinu, on je bio nadmen, od onih koji pretjeruju.'),
(4446, 44, 32, 'I doista smo ih odabrali sa znanjem nad svjetovima,'),
(4447, 44, 33, 'I dali im znakove u kojima je bila kušnja očita.'),
(4448, 44, 34, 'Uistinu, ovi govore:'),
(4449, 44, 35, '"To je samo naša prva smrt, i nećemo mi biti podignuti.'),
(4450, 44, 36, 'Pa dajte očeve naše, ako ste iskreni!"'),
(4451, 44, 37, 'Jesu li oni bolji ili narod Tubba i oni prije njih? Uništili smo ih. Uistinu, oni su bili prestupnici!'),
(4452, 44, 38, 'I nismo stvorili nebesa i Zemlju i šta je između njih zabavljajući se.'),
(4453, 44, 39, 'Stvorili smo ih jedino s Istinom, ali većina njih ne zna.'),
(4454, 44, 40, 'Uistinu, Dan odluke je njihovo vrijeme određeno za sve,'),
(4455, 44, 41, 'Dan kad neće prijatelj koristiti prijatelju nimalo, niti će oni biti pomognuti,'),
(4456, 44, 42, 'Izuzev kome se smiluje Allah. Uistinu! On, On je Moćni, Milosrdni.'),
(4457, 44, 43, 'Uistinu, drvo zekkum će biti'),
(4458, 44, 44, 'Hrana grješnika!'),
(4459, 44, 45, 'Kao rastopljen metal vriće u trbusima,'),
(4460, 44, 46, 'Kao vrenje ključale vode.'),
(4461, 44, 47, '"Zgrabite ga te ga odvucite usred džehima,'),
(4462, 44, 48, 'Potom nad glavom njegovom za kaznu izlivajte ključalu vodu.'),
(4463, 44, 49, 'Kušaj! Uistinu ti, ti si \'moćni, plemeniti!\''),
(4464, 44, 50, 'Uistinu, ovo je ono u što ste sumnjali."'),
(4465, 44, 51, 'Uistinu, bogobojazni će biti u mjestu sigurnom,'),
(4466, 44, 52, 'U baščama i izvorima,'),
(4467, 44, 53, 'Odijevat će se svilom i brokatom, naspramni će biti.'),
(4468, 44, 54, 'Tako; i oženićemo ih hurijama lijepih krupnih očiju,'),
(4469, 44, 55, 'Pozivat će u njima za svako voće, sigurni,'),
(4470, 44, 56, 'Neće u njima okusiti smrt, izuzev smrti prve, i sačuvaće ih kazne džehima,'),
(4471, 44, 57, 'Dobrotom od Gospodara tvog. To je to, uspjeh veličanstven!'),
(4472, 44, 58, 'Pa doista smo ga olakšali na jeziku tvom, da bi se oni poučili.'),
(4473, 44, 59, 'Zato pričekaj! Uistinu, oni su čekatelji.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Ha. Mim.'),
(4475, 45, 2, 'Objava Knjige je od Allaha, Moćnog, Mudrog.'),
(4476, 45, 3, 'Uistinu, na nebesima i Zemlji su znaci za vjernike,'),
(4477, 45, 4, 'I u stvaranju vašem i onom šta je rasijao od životinje - znaci su za ljude koji su uvjereni,'),
(4478, 45, 5, 'I variranju noći i dana i šta Allah spusti s neba od opskrbe - pa oživljava njome zemlju nakon mrtvila njenog - i izmjeni vjetrova - znaci su za ljude koji shvataju.'),
(4479, 45, 6, 'To su ajeti Allahovi, učimo ih tebi s Istinom. Pa u koji će hadis nakon Allaha i ajeta Njegovih vjerovati?'),
(4480, 45, 7, 'Teško svakom lažljivcu, grješniku,'),
(4481, 45, 8, 'Sluša ajete Allahove, uče mu se, zatim ustrajava oholo kao da ih nije čuo. Zato ga obraduj kaznom bolnom!'),
(4482, 45, 9, 'A kad od ajeta Naših sazna nešto, uzme ih ruglom. Ti takvi će imati kaznu prezrenu!'),
(4483, 45, 10, 'Pred njima je Džehennem, i nimalo im neće koristiti ono što su stekli, niti ono šta su mimo Allaha uzimali zaštitnicima. A imaće oni kaznu strahovitu.'),
(4484, 45, 11, 'Ovo je Uputa. A oni koji ne vjeruju u ajete Gospodara svog, imaće oni bolnu kaznu prljavštine.'),
(4485, 45, 12, 'Allah je Taj koji je za vas potčinio more, da plovilo plovi njime po odredbi Njegovoj, i da tražite iz blagodati Njegove, i da biste vi zahvaljivali.'),
(4486, 45, 13, 'I potčinio je za vas šta je u nebesima i šta je na Zemlji; sve je od Njega. Uistinu, u tome su znaci za ljude koji razmišljaju.'),
(4487, 45, 14, 'Reci onima koji vjeruju, da oproste onima koji se ne nadaju danima Allahovim, da bi ljudima platio za ono šta su zaradili.'),
(4488, 45, 15, 'Ko uradi dobro, pa za dušu je njegovu, a ko uradi zlo, pa protiv nje je; zatim ćete Gospodaru svom biti vraćeni.'),
(4489, 45, 16, 'I zaista smo sinovima Israilovim dali Knjigu i sud i poslanstvo, i opskrbili ih od dobrih stvari, i odlikovali ih nad svjetovima.'),
(4490, 45, 17, 'I dali smo im dokaze jasne o stvari, pa su se razišli jedino nakon što im je došlo znanje, zavišću međusobnom. Uistinu, Gospodar tvoj će presuditi među njima na Dan kijameta, o onome u čemu su se razilazili.'),
(4491, 45, 18, 'Zatim smo učinili da ti (ideš) Šeriatom po naredbi, zato ga slijedi, i ne slijedi strasti onih koji ne znaju.'),
(4492, 45, 19, 'Uistinu, oni ti neće koristiti protiv Allaha nimalo. A uistinu, zalimi - oni su zaštitnici jedni drugima; a Allah je Zaštitnik bogobojaznih.'),
(4493, 45, 20, 'Ovo su jasni uvidi za ljude, i Uputa i milost za ljude koji su uvjereni.'),
(4494, 45, 21, 'Zar misle oni koji su činili zla djela, da ćemo njih držati kao one koji vjeruju i čine dobra djela - (sa) istim životom njihovim i smrću njihovom? Loše je kako prosuđuju!'),
(4495, 45, 22, 'A stvorio je Allah nebesa i Zemlju s Istinom, i da se plati svakoj duši za ono šta je zaradila, a neće im se učiniti zulm.'),
(4496, 45, 23, 'Pa vidiš li onog ko je bogom svojim uzeo strast svoju, a zaveo ga je Allah znanjem, i zapečatio sluh njegov i srce njegovo i postavio na vid njegov pokrivku? Pa ko će ga uputiti mimo Allaha? Pa zar se nećete poučiti?'),
(4497, 45, 24, 'I govore: "Ovo je samo naš život dunjalučki; umiremo i živimo, uništava nas jedino vrijeme." A nemaju oni o tome nikakvo znanje, oni samo nagađaju.'),
(4498, 45, 25, 'I kad im se uče ajeti Naši jasni, nemaju argumenta svog, izuzev da kažu: "Dajte nam očeve naše, ako ste istiniti."'),
(4499, 45, 26, 'Reci: "Allah vas oživljava, zatim vas usmrćuje, potom će vas sabrati na Dan kijameta u koji nema sumnje", ali većina ljudi ne zna.'),
(4500, 45, 27, 'A Allahova je vlast nebesa i Zemlje. A na Dan kad nastupi Čas, Tog dana će izgubiti lažljivci.'),
(4501, 45, 28, 'I vidjet ćeš svaki ummet - klečat će - svaki ummet će biti pozvan knjizi svojoj: "Danas ćete biti plaćeni za ono šta ste radili.'),
(4502, 45, 29, 'Ovo je Knjiga Naša, govoriće o vama s Istinom. Uistinu, Mi smo prepisali šta ste radili."'),
(4503, 45, 30, 'Pa što se tiče onih koji vjeruju i rade dobra djela - pa uvešće ih Gospodar njihov u milost Svoju. To je taj uspjeh očiti.'),
(4504, 45, 31, 'A što se tiče onih koji ne vjeruju: "Ta zar vam ajeti Moji nisu bili učeni? Tad ste se oholili i bili narod prestupnika."'),
(4505, 45, 32, 'I kad se govorilo: "Uistinu! Obećanje Allahovo je Istina i Čas u koji nema sumnje", rekli ste: "Ne znamo šta je Čas i nagađamo jedino pretpostavke i nismo mi uvjereni."'),
(4506, 45, 33, 'I ukazaće im se zla koja su uradili i spopašće ih ono čemu su se izrugivali,'),
(4507, 45, 34, 'I biće rečeno: "Danas vas zaboravljamo, kao što ste zaboravili na susret dana vašeg ovog. A sklonište vaše je vatra i nema za vas nikakvih pomagača."'),
(4508, 45, 35, 'To stoga što ste vi ajete Allahove uzimali ruglu, a zavarao vas je život Dunjaa. Zato se Danas neće izvući iz nje, i neće im se dopustiti da traže naklonost.'),
(4509, 45, 36, 'Pa Allahu neka je hvala, Gospodaru nebesa i Gospodaru Zemlje, Gospodaru svjetova!"'),
(4510, 45, 37, 'A Njegova je veličanstvenost na nebesima i Zemlji, a On je Moćni, Mudri.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Ha. Mim.'),
(4512, 46, 2, 'Objava Knjige je od Allaha, Moćnog, Mudrog.'),
(4513, 46, 3, 'Stvorili smo nebesa i Zemlju i šta je između njih jedino s Istinom i rokom određenim. A oni koji ne vjeruju u ono čime se opominju - odvraćeni su.'),
(4514, 46, 4, 'Reci: "Jeste li vidjeli šta prizivate mimo Allaha? Pokažite mi šta su to stvorili od zemlje, ili imaju oni udio u nebesima? Dajte mi Knjigu prije ove ili tračak znanja, ako istinu govorite."'),
(4515, 46, 5, 'A ko je zabludjeliji od onog ko mimo Allaha priziva onog ko mu se neće odazvati do Dana kijameta, a oni su prema zovu njihovom nehajni.'),
(4516, 46, 6, 'A kad se ljudi saberu, biće im neprijatelji, i biće u obožavanje njihovo nevjernici.'),
(4517, 46, 7, 'I kad im se uče ajeti Naši jasni, govore oni koji ne vjeruju za Istinu kad im dođe: "Ovo je sihr očiti!"'),
(4518, 46, 8, 'Ili govore: "Izmišlja ga!" Reci: "Ako sam ga izmislio - pa ne vladate sa mnom zbog Allaha nimalo. On je Najbolji znalac onog šta o njemu govorite. Dovoljan je On svjedok između mene i između vas, a On je Oprosnik, Milosrdni."'),
(4519, 46, 9, 'Reci: "Nisam bio novina od poslanika, i ne znam šta će se sa mnom učiniti, niti sa vama. Slijedim jedino šta mi se objavljuje i ja sam samo opominjač jasni."'),
(4520, 46, 10, 'Reci: "Vidite li: ako je od Allaha, a ne vjerujete u njega, a posvjedočio je svjedok od sinova Israilovih na sličnom njemu pa je vjerovao - a uzoholili ste se?" Uistinu! Allah ne upućuje narod zalima.'),
(4521, 46, 11, 'I oni koji ne vjeruju govore o onima koji vjeruju: "Da je dobro, ne bi nas u tome pretekli." A kako se njime ne upućuju, to će reći: "Ovo je laž stara."'),
(4522, 46, 12, 'I prije njega je Knjiga Musaova bila vodilja i milost, a ova Knjiga je potvrda na jeziku arapskom, da upozori one koji zulm čine, i radosna vijest za dobročinitelje.'),
(4523, 46, 13, 'Uistinu, oni koji kažu: "Gospodar naš je Allah", zatim istraju na pravom putu, tad neće biti straha nad njima i neće oni tugovati.'),
(4524, 46, 14, 'Takvi će biti stanovnici Dženneta, vječno će biti u njemu: plaća za ono što su radili.'),
(4525, 46, 15, 'A wassijetili smo čovjeku prema roditeljima njegovim: dobro. Nosila ga je majka njegova prisilno i rodila ga prisilno; a nošenje njegovo i odbijanje njegovo od dojenja je trideset mjeseci; dok, kad dostigne zrelost svoju i dostigne četrdeset godina, rekne: "Gospodaru moj! Potakni me da zahvaljujem na blagodati Tvojoj koju si podario meni i roditeljima mojim, i da radim dobro s kojim si zadovoljan, i napredak meni u potomstvu mom. Uistinu, ja sam Ti se pokajao i uistinu sam ja od muslimana."'),
(4526, 46, 16, 'To su oni od kojih primamo najbolje šta su radili, a preći ćemo preko zlih djela njihovih, među stanovnicima Dženneta će biti. Obećanje koje im se daje istinito je.'),
(4527, 46, 17, 'A onaj koji roditeljima svojim rekne: "Uff vama oboma! Plašite li me da ću biti izvađen, a već su minule generacije prije mene?" A oni oboje preklinju Allaha: "Teško tebi! Vjeruj! Doista, obećanje Allahovo je Istina." Tad govori: "Ovo su samo priče ranijih."'),
(4528, 46, 18, 'Takvi su oni na kojima se obistinila Riječ u zajednicama već prošlim prije njih, od džinna i ljudi. Uistinu! Oni su gubitnici.'),
(4529, 46, 19, 'I za sve što su radili biće stepeni, i da im ispuni djela njihova, a njima se neće zulm učiniti.'),
(4530, 46, 20, 'A na Dan kad se oni koji nisu vjerovali, izlože na vatru: "Proćerdali ste svoje dobre stvari u životu svom dunjalučkom i uživali u njima. Pa danas ćete biti plaćeni kaznom prezrenom, zbog toga što ste se oholili na Zemlji bez ikakva prava, i što ste griješili."'),
(4531, 46, 21, 'I spomeni brata Adovog, kad je upozoravao narod svoj u pješčanim brdima - a doista su prolazili opominjači prije njega i poslije njega: "Obožavajte samo Allaha; uistinu, ja se plašim nad vama kazne Dana strašnog."'),
(4532, 46, 22, 'Rekoše: "Jesi li nam došao da nas odvratiš od bogova naših? Pa daj nam to čime nam prijetiš, ako si od istinitih!"'),
(4533, 46, 23, 'Reče: "Samo je znanje kod Allaha, a prenosim vam ono s čim sam poslat, ali ja vas vidim ljudima koji ne znaju."'),
(4534, 46, 24, 'Pa pošto ga ugledaše (kao) oblak (koji) napreduje ka dolinama njihovim, rekoše: "Ovo je oblak koji će nam kišu dati." Naprotiv, to je ono šta ste požurivali - vjetar, u njemu kazna bolna,'),
(4535, 46, 25, 'Razara svaku stvar po naredbi Gospodara njegovog! Pa su osvanuli: vidjele su se samo nastambe njihove. Tako plaćamo narod prestupnika.'),
(4536, 46, 26, 'I zaista smo osposobili njih u onom u čemu nismo osposobili vas. I načinili smo im sluh i vidove i srca; pa nije im koristio sluh njihov, niti vidovi njihovi, a ni srca njihova nimalo, kad su nijekali ajete Allahove, i snašlo ih je ono čemu su se ismijavali.'),
(4537, 46, 27, 'I doista smo uništili ono šta je bilo gradova oko vas i izlagali znakove, da bi se oni povratili.'),
(4538, 46, 28, 'Pa što ih nisu pomogli oni koje su mimo Allaha uzimali bogovima kao sredstvo približenja? Naprotiv, iščezli su od njih. A to je bila laž njihova i ono što su izmišljali.'),
(4539, 46, 29, 'I kad ti upravismo skupinu džinna da slušaju Kur\'an - pa pošto su mu prisustvovali, rekoše: "Šutite!" - pa pošto je završeno, vratiše se narodu svom (kao) opominjači.'),
(4540, 46, 30, 'Govorili su: "O narode naš! Uistinu, mi smo slušali Knjigu objavljenu poslije Musaa, potvrdu onog ispred nje, upućuje Istini i tariku pravom.'),
(4541, 46, 31, 'O narode naš! Odazovite se pozivaču Allahovom, i vjerujte u njega. Oprostiće vam grijehe vaše i zaštititi vas od kazne bolne."'),
(4542, 46, 32, 'A ko se ne odazove pozivaču Allahovom - pa nije on taj koji će umaći u Zemlji, i neće on mimo Njega imati zaštitnike. Takvi su u zabludi očitoj.'),
(4543, 46, 33, 'Zar nisu vidjeli da je Allah koji je stvorio nebesa i Zemlju - a nije se umorio stvaranjem njihovim - kadar da oživi mrtve? Svakako! Uistinu! On nad svakom stvari ima moć.'),
(4544, 46, 34, 'A na Dan kad se oni koji ne vjeruju izlože na vatru: "Zar ovo nije Istina?" - reći će: "Svakako, Gospodara nam našeg." Reći će: "Pa iskusite kaznu za to što niste vjerovali."'),
(4545, 46, 35, 'Zato se strpi, kao što se strpio odlučni između poslanika, i ne požuruj im. Kao da su oni - na Dan kad vide ono čime im se prijetilo - zadržani samo čas dana. Informacija (je to). Pa hoće li biti uništen (iko) osim naroda grješnika?');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Onima koji ne vjeruju i odvraćaju od puta Allahovog - (Allah) će učiniti zaludnim djela njihova.'),
(4547, 47, 2, 'A oni koji vjeruju i rade dobra djela i vjeruju u ono što se objavljuje Muhammedu - a to je Istina od Gospodara njihovog - (Allah) će im oprostiti zla djela njihova i popraviti stanje njihovo.'),
(4548, 47, 3, 'To zato što oni koji ne vjeruju slijede neistinu, a što oni koji vjeruju slijede Istinu od Gospodara njihovog. Tako Allah navodi ljudima primjere njihove.'),
(4549, 47, 4, 'Pa kad sretnete one koji ne vjeruju, tad udrite šije, dok, kad ih iznurite, tad ojačajte okov. Zatim ili dobročinstvo poslije i, ili otkup, dok rat ne položi terete svoje. To! A da Allah hoće, osvetio bi im se, međutim (hoće) da vas iskuša jedne drugima. A koji poginu na putu Allahovom - pa neće (On) poništiti djela njihova.'),
(4550, 47, 5, 'Vodiće ih i popraviti stanje njihovo,'),
(4551, 47, 6, 'I uvešće ih u Džennet kojeg im je predstavio.'),
(4552, 47, 7, 'O vi koji vjerujete! Ako pomognete Allaha, pomoći će vas i osnažiti stope vaše.'),
(4553, 47, 8, 'A oni koji ne vjeruju - pa, propast njima, a učiniće zaludnim djela njihova.'),
(4554, 47, 9, 'To zato što oni mrze ono što je objavio Allah - zato će (On) upropastiti djela njihova.'),
(4555, 47, 10, 'Pa zašto ne putuju po Zemlji pa vide kakav je bio kraj onih prije njih? Uništio ih je Allah, a nevjernicima će biti slično njima.'),
(4556, 47, 11, 'To zato što je Allah Zaštitnik onih koji vjeruju i što nevjernici nemaju zaštitnika za sebe.'),
(4557, 47, 12, 'Uistinu! Allah će uvesti one koji vjeruju i rade dobra djela u bašče ispod kojih teku rijeke. A koji ne vjeruju, (oni) uživaju i jedu kao što životinje jedu, a vatra će biti boravište njihovo.'),
(4558, 47, 13, 'A koliko je gradova koji su bili moćniji silom od tvog grada koji te je protjerao: uništili smo ih, tad oni nisu imali pomagača.'),
(4559, 47, 14, 'Pa je li onaj ko je uz dokaz jasni od Gospodara svog, kao onaj kojem je uljepšano zlo djelo njegovo i (koji) slijede strasti svoje?'),
(4560, 47, 15, 'Primjer Dženneta koji je obećan bogobojaznima: u njemu rijeke od vode neustajale; i rijeke od mlijeka - neće se izmijeniti okus njegov; i rijeke od vina prijatnog pijačima; i rijeke od meda prečišćenog. I imaće oni u njemu raznovrsnog voća i oprost Gospodara svog. (Da li su oni) kao onaj koji će biti vječno u vatri i biti napajani vrelom vodom, pa će kidati crijeva njihova?'),
(4561, 47, 16, 'I od njih je ko te prisluškuje, dok, kad izađu od tebe, govore onima kojima je dato znanje: "Šta to reče malo prije?" Takvi su oni kojima je Allah zapečatio srca njihova, a slijede strasti svoje.'),
(4562, 47, 17, 'A oni koji se upute, povećaće im uputu i daće im bogobojaznost njihovu.'),
(4563, 47, 18, 'Pa očekuju li samo Čas - da im dođe iznenada? Ta već su došli predznaci njegovi. Pa kako će oni - kad im dođe - imati upozorenje svoje?'),
(4564, 47, 19, 'Pa znaj da je samo On Bog - Allah, i traži oprost za grijeh svoj, i za vjernike i vjernice. A Allah zna vaše mjesto kretanja i stanište vaše.'),
(4565, 47, 20, 'I govore oni koji ne vjeruju: "Što se ne objavi sura?" Pa kad je objavljena sura presudna i pomenuta u njoj borba, vidio si one u srcima čijim je bolest - gledaju u tebe pogledom smrću onesvještenog. Pa teško njima!'),
(4566, 47, 21, 'Poslušnost i govor uljudan! Pa kad je odlučena stvar, da su tad iskreni Allahu, sigurno bi bilo bolje za njih.'),
(4567, 47, 22, 'Pa da li biste možda - ako biste zavladali - da napravite fesad na Zemlji i pokidate srodstvo vaše?'),
(4568, 47, 23, 'Takvi su oni koje je prokleo Allah, pa ih ogluhnuo i oslijepio vidove njihove.'),
(4569, 47, 24, 'Pa zar neće razmišljati o Kur\'anu, ili su na srcima njihovim brave?'),
(4570, 47, 25, 'Uistinu, oni koji su okrenuli leđa svoja, nakon što im bila jasna Uputa, šejtan ih je zaveo i odužio im.'),
(4571, 47, 26, 'To zato što oni kažu onima koji mrze šta je Allah objavio: "Poslušaćemo vas u nekoj stvari"; a Allah zna tajne njihove.'),
(4572, 47, 27, 'Pa kako će biti kad ih uzmu meleci, udarajući lica njihova i leđa njihova?'),
(4573, 47, 28, 'To zato što su oni slijedili ono šta srdi Allaha i mrzili zadovoljstvo Njegovo, zato će uništiti djela njihova.'),
(4574, 47, 29, 'Zar misle oni u srcima čijim je bolest, da Allah neće iznijeti zlobe njihove?'),
(4575, 47, 30, 'A da hoćemo, zaista bismo ti dali da ih vidiš, pa bi ih sigurno prepoznao po znacima njihovim, i sigurno bi ih prepoznao po tonu govora! A Allah zna djela vaša.'),
(4576, 47, 31, 'I doista ćemo vas iskušavati, dok ne spoznamo mudžahide između vas i strpljive, i provjerimo habere vaše.'),
(4577, 47, 32, 'Uistinu, oni koji ne vjeruju i odvraćaju od puta Allahovog i suprotstavljaju se Poslaniku, nakon što im je postala jasna Uputa, neće naškoditi Allahu nimalo, a upropastiće djela njihova.'),
(4578, 47, 33, 'O vi koji vjerujete! Poslušajte Allaha i poslušajte Poslanika i ne poništavajte djela vaša.'),
(4579, 47, 34, 'Uistinu, oni koji ne vjeruju i odvraćaju od puta Allahovog, zatim umru, a oni budu nevjernici - pa Allah im neće oprostiti.'),
(4580, 47, 35, 'Zato ne malaksajte, i pozivajte miru a ste vi nadmoćniji, i Allah je s vama i neće vas oštetiti djelima vašim.'),
(4581, 47, 36, 'Život Dunjaa je samo igra i zabava. I ako budete vjerovali i bojali se, daće vam nagrade vaše i neće vas pitati za imetke vaše.'),
(4582, 47, 37, 'Ako bi vas pitao za njih, pa vas pritisnuo, škrtarili biste i iznio bi zlobe vaše.'),
(4583, 47, 38, 'Eto, vi ste ti koji se pozivate da trošite na putu Allahovom, pa od vas je ko škrtari. A ko škrtari - pa zaista škrtari od duše svoje. A Allah je bogat, a vi ste fukare. A ako se odvratite, zamijeniće (vas) narodom drugačijim od vas, potom neće biti primjeri vaši.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Uistinu, Mi smo ti dali pobjedu očitu,'),
(4585, 48, 2, 'Da bi ti Allah oprostio šta je prethodilo od grijeha tvog i šta će uslijediti, i upotpunio ti blagodat Svoju i uputio te putem pravim,'),
(4586, 48, 3, 'I da te Allah pomogne pomoći silnom.'),
(4587, 48, 4, 'On je Taj koji je spustio smirenost u srca vjernika, da bi dodali vjerovanje uz vjerovanje svoje. A Allahove su vojske nebesa i Zemlje i Allah je Znalac, Mudri -'),
(4588, 48, 5, 'Da bi uveo vjernike i vjernice u bašče ispod kojih teku rijeke, u kojima će vječno biti, i oprostio im zla djela njihova - a to je kod Allaha uspjeh veličanstven,'),
(4589, 48, 6, 'I da bi kaznio munafike i munafikinje i mušrike i mušrikinje - one koji misle o Allahu misao zlu - na njima je kolo zla, i rasrdio se Allah na njih i prokleo ih i pripremio im Džehennem, a zlog li dolazišta!'),
(4590, 48, 7, 'A Allahove su vojske nebesa i Zemlje, i Allah je Moćni, Mudri!'),
(4591, 48, 8, 'Uistinu, Mi smo te poslali svjedokom i donosiocem radosnih vijesti i opominjačem,'),
(4592, 48, 9, 'Da biste vjerovali u Allaha i Poslanika Njegovog, i pomogli ga, i poštovali ga i slavili Ga jutrom i navečer.'),
(4593, 48, 10, 'Uistinu, oni koji su se tebi zakleli na vjernost - doista su se na vjernost zakleli Allahu. Ruka Allahova je iznad ruku njihovih. Pa ko prekrši, zaista krši na dušu svoju, a ko ispuni ono šta je od sebe obećao Allahu, pa daće mu nagradu veličanstvenu.'),
(4594, 48, 11, 'Reći će ti izostali od beduina: "Zaokupila su nas imanja naša i porodice naše, zato nam traži oprost!" Govore jezicima svojim ono što nije u srcima njihovim. Reci: "Pa ko će vama vladati od Allaha imalo, ako vam želi štetu ili vam želi korist?" Naprotiv, Allah je o onom što radite Obaviješteni.'),
(4595, 48, 12, 'Naprotiv, mislili ste da se neće vratiti Poslanik i vjernici porodicama svojim nikada, i to je lijepo bilo u srcima vašim, a mislili ste misao zlu i bili narod propalih.'),
(4596, 48, 13, 'A ko ne bude vjerovao u Allaha i poslanika Njegovog - pa uistinu, Mi smo za nevjernike pripremili seir.'),
(4597, 48, 14, 'A Allahova je vlast nebesa i Zemlje; oprašta kome hoće i kažnjava koga hoće. A Allah je Oprosnik, Milosrdni.'),
(4598, 48, 15, 'Reći će izostali kad krenete ka trofejima, da ih uzmete: "Pustite nas da vas slijedimo." Žele da izmijene govor Allahov. Reci: "Nećete nas slijediti; tako je rekao Allah od ranije." Tad će reći: "Naprotiv, zavidite nam." Naprotiv! Ne shvataju, izuzev malo.'),
(4599, 48, 16, 'Reci izostalima od beduina: "Bićete pozvani narodu koji je sile žestoke, da se izborite protiv njih ili da se pokore. Pa ako poslušate, daće vam Allah nagradu lijepu; a ako se okrenete kao što ste se okretali ranije, kazniće vas kaznom bolnom."'),
(4600, 48, 17, 'Nije slijepcu ukor, niti je hromu ukor, niti je bolesniku ukor. A ko posluša Allaha i Poslanika Njegovog, uvešće ga u bašče ispod kojih teku rijeke; a ko se okrene, kazniće ga kaznom bolnom.'),
(4601, 48, 18, 'Doista, zadovoljan je Allah vjernicima koji su ti se zakleli na vjernost pod drvetom - ta znao je šta je u srcima njihovim, pa je spustio smirenost na njih i nagradio ih pobjedom bliskom,'),
(4602, 48, 19, 'I trofejima mnogim koje će uzeti. A Allah je Moćni, Mudri.'),
(4603, 48, 20, 'Obećao vam je Allah trofeje mnoge koje ćete uzeti, zato vam je ovo požurio i sustegao ruke ljudi od vas, i da bude znak za vjernike i uputi vas putem pravim -'),
(4604, 48, 21, 'I drugi kojeg se niste domogli, doista ga je obuhvatio Allah. A Allah nad svakom stvari ima moć.'),
(4605, 48, 22, 'A da su se protiv vas borili oni koji ne vjeruju, sigurno bi okrenuli leđa, zatim ne bi našli zaštitnika, niti pomagača.'),
(4606, 48, 23, 'Sunnet je (to) Allahov koji je već minuo ranije. A nećeš naći u sunnetu Allahovom izmjenu!'),
(4607, 48, 24, 'I On je Taj koji je sustegao ruke njihove od vas i ruke vaše od njih unutar Mekke, nakon što vas je učinio pobjednicima nad njima. A Allah je onog šta radite Vidilac.'),
(4608, 48, 25, 'Oni su ti koji nisu vjerovali i odbijali vas od Mesdžidul-harama i zadržan kurban da dospije mjestu svom. A da nije ljudi vjernika i žena vjernica koje ne poznajete - da ih ne pregazite, pa vas pogodi zbog njih sramota bez ikakva znanja, da bi uveo Allah u milost Svoju koga hoće - da su bili izdvojeni, sigurno bismo kaznili one od njih koji ne vjeruju, kaznom bolnom.'),
(4609, 48, 26, 'Kad su oni koji ne vjeruju punili u srca svoja žar - žar džahilijeta, tad je Allah spustio smirenost Svoju na Poslanika Svog i na vjernike, i učvrstio ih uz riječ bogobojaznosti - a bili su nje najdostojniji i sljedbenici njeni. A Allah je o svakoj stvari Znalac.'),
(4610, 48, 27, 'Doista, učiniće Allah snoviđenje Poslanika Svog istinom: Doista ćete ući u Mesdžidul-haram - ako htjedne Allah - bezbjedni, obrijanih glava svojih i potkraćeni, ne bojeći se. Pa znao je šta niste znali, te je pored toga dao pobjedu blisku.'),
(4611, 48, 28, 'On je Taj koji je poslao Poslanika Svog sa Uputom i Vjerom istine, da bi dao da ona prevlada nad vjerom svakom. A dovoljan je Allah Svjedok.'),
(4612, 48, 29, 'Muhammed je poslanik Allahov; a oni koji su s njim, žestoki su prema nevjernicima, milosrdni međusobno. Vidiš ih čine ruku\', padaju na sedždu, traže blagodat od Allaha i zadovoljstvo. Znak njihov je na licima njihovim od učinka sedždi. To je primjer njihov u Tewratu. A primjer njihov u Indžilu je: kao sjemenka su - probije izdanak svoj pa ga ojača, pa zadeblja, te stane čvrsto na stabljici svojoj zadivljujući sijače - da bi (On) njima rasrdio nevjernike. Obećao je Allah onima koji vjeruju od njih i rade dobra djela, oprost i nagradu veličanstvenu.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'O vi koji vjerujete! Ne istupajte ispred Allaha i Poslanika Njegovog, i bojte se Allaha. Uistinu! Allah je Onaj koji čuje, Znalac.'),
(4614, 49, 2, 'O vi koji vjerujete! Ne dižite glasove svoje iznad glasa Vjerovjesnika, i ne vičite na njega u govoru, kao što vi vičete jedan drugom, da ne propadnu djela vaša, a vi ne opazite.'),
(4615, 49, 3, 'Uistinu! Oni koji ublažavaju glasove svoje kod Poslanika Allahovog, to su oni čija je Allah provjerio srca na bogobojaznost. Imaće oni oprost i nagradu veličanstvenu.'),
(4616, 49, 4, 'Uistinu! Oni koji te dozivaju iza soba - većina njih ne shvata.'),
(4617, 49, 5, 'A da su se strpili dok im ne izađeš, sigurno bi bilo bolje za njih. A Allah je Oprosnik, Milosrdni.'),
(4618, 49, 6, 'O vi koji vjerujete! Ako vam grješnik dođe sa viješću - tad provjerite, da ne pogodite narod iz neznanja, pa da ne budete zbog onog što ste učinili, pokajanici.'),
(4619, 49, 7, 'I znajte da je među vama Poslanik Allahov. Ako bi vas slušao u mnogo (čemu) od stvari, pali biste u nevolju, ali, Allah vam je omilio vjerovanje i uljepšao ga u srcima vašim, a omrznuo vam je nevjerovanje i griješenje i neposlušnost. Ti takvi su ispravni,'),
(4620, 49, 8, 'Dobrotom od Allaha i blagodati. A Allah je Znalac, Mudri.'),
(4621, 49, 9, 'A ako se dvije grupe vjernika sukobe, tad međusobno izmirite obje; pa ako jedna od dvije napadne drugu, tad se borite protiv one koja je napala, dok se ne povrati naredbi Allahovoj; pa ako se povrati, tad izmirite međusobno obje jednako i budite pravedni. Uistinu! Allah voli pravedne.'),
(4622, 49, 10, 'Doista su vjernici braća, zato izmirite braću svoju, i bojte se Allaha, da bi vam se smilovalo.'),
(4623, 49, 11, 'O vi koji vjerujete! Neka se ljudi ne rugaju ljudima, možda su bolji od njih, niti žene ženama, možda su bolje od njih. I ne kudite se, niti vrijeđajte nadimcima. Griješenje je loše ime poslije vjerovanja. A ko se ne pokaje - pa ti takvi su zalimi.'),
(4624, 49, 12, 'O vi koji vjerujete! Klonite se mnogo sumnje: uistinu je neka sumnja grijeh. I ne uhodite i ne ogovarajte vi jedni druge! Voli li ijedan od vas da jede meso brata svog mrtvog? Pa - mrzite to! I bojte se Allaha. Uistinu! Allah je Primalac pokajanja, Milosrdni.'),
(4625, 49, 13, 'O ljudi! Uistinu, Mi smo vas stvorili od muška i ženska, i učinili vas narodima i plemenima, da biste se upoznavali. Uistinu! Najplemenitiji od vas kod Allaha, je najbogobojazniji između vas. Uistinu, Allah je Znalac, Obaviješteni.'),
(4626, 49, 14, 'Kažu beduini: "Vjerujemo!" - reci: "Ne vjerujete, ali recite: \'Pokoravamo se\', a još nije ušao iman u srca vaša. A ako poslušate Allaha i Poslanika Njegovog, neće vam umanjiti od djela vaših nimalo." Uistinu! Allah je Oprosnik, Milosrdni.'),
(4627, 49, 15, 'Vjernici su samo oni koji vjeruju u Allaha i Poslanika Njegovog i poslije ne sumnjaju, i bore se imecima svojim i dušama svojim na putu Allahovom. Ti takvi su iskreni.'),
(4628, 49, 16, 'Reci: "Zar da Allaha učite o vjeri svojoj? A Allah zna šta je u nebesima i šta je na Zemlji, i Allah je o svakoj stvari Znalac!"'),
(4629, 49, 17, 'Prebacuju ti što su se pokorili. Reci: "Ne prebacujte mi zbog Islama svog. Naprotiv, Allah vas je usrećio što vas je uputio vjerovanju, ako istinu govorite."'),
(4630, 49, 18, 'Uistinu! Allah zna nevidljivo nebesa i Zemlje, i Allah je onog što radite Vidilac.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Qaf. Tako mi Kur\'ana slavnog,'),
(4632, 50, 2, 'Naprotiv, čude se da im je došao opominjač između njih, pa nevjernici govore: "Ovo je stvar čudna:'),
(4633, 50, 3, 'Zar kad umremo i budemo prašina? To je povratak dalek!"'),
(4634, 50, 4, 'Doista znamo šta će Zemlja od njih umanjiti, a kod Nas je Knjiga čuvar.'),
(4635, 50, 5, 'Naprotiv, porekli su Istinu pošto im je došla, pa su oni o stvari zbrkani.'),
(4636, 50, 6, 'Pa zar neće pogledati nebo iznad sebe - kako smo ga sazdali i ukrasili ga - a nema ono nikakvih pukotina!'),
(4637, 50, 7, 'I Zemlju! Rasprostrli smo je i pobacali po njoj planine stabilne, i dali da na njoj nikne od svake vrste lijepe, -'),
(4638, 50, 8, 'Uvid i opomena za svakog roba koji se obraća.'),
(4639, 50, 9, 'I spuštamo s neba vodu blagoslovljenu, te dajemo da (pomoću) nje izrastu bašče i zrno žetveno,'),
(4640, 50, 10, 'I palme visoke - grozdovi njihovi naslagani,'),
(4641, 50, 11, 'Opskrba za robove - i oživljavamo njome predio mrtvi. Takav će biti izlazak.'),
(4642, 50, 12, 'Poricali su prije njih: narod Nuhov i stanovnici Ressa i Semud,'),
(4643, 50, 13, 'I Ad i faraon i braća Lutova,'),
(4644, 50, 14, 'I stanovnici Ejke i narod Tubba\' - svaki je porekao poslanike, pa se obistinila prijetnja Moja.'),
(4645, 50, 15, 'Pa zar smo malaksali pri stvaranju prvom? Naprotiv, oni su u sumnji o stvaranju novom.'),
(4646, 50, 16, 'A doista smo stvorili čovjeka i znamo šta mu došaptava duša njegova, a Mi smo mu bliže od vratne vene.'),
(4647, 50, 17, 'Kad se sretnu susretača dva, zdesna i slijeva, sjedioci,'),
(4648, 50, 18, 'Ne izusti nijednu riječ, a da nije kod njega promatrač spreman.'),
(4649, 50, 19, 'I doći će agonija smrti uistinu; to je ono od čega nećeš umaći.'),
(4650, 50, 20, 'I puhnut će se u sur; to je Dan prijetnje.'),
(4651, 50, 21, 'I doći će svaka duša, sa njom vodič i svjedok.'),
(4652, 50, 22, '"Zaista si o ovom bio u ravnodušnosti, pa smo s tebe odstranili pokrov tvoj, zato je vid tvoj Danas oštar."'),
(4653, 50, 23, 'A drug njegov će reći: "Ovo što je kod mene, spremno je."'),
(4654, 50, 24, '"Bacite vas dvojica u Džehennem svakog nezahvalnika, buntovnika,'),
(4655, 50, 25, 'Sprečavatelja dobrog, napasnika, sumnjivca,'),
(4656, 50, 26, 'Koji je stavljao uz Allaha boga drugog - ta bacite ga u kaznu žestoku."'),
(4657, 50, 27, 'Reći će drug njegov: "Gospodaru naš! Nisam ja uzrokovao da on prevrši, nego je bio u zabludi dalekoj."'),
(4658, 50, 28, '(Allah) će reći: "Ne prepirite se kod Mene, a već sam vam unaprijed poslao prijetnju."'),
(4659, 50, 29, 'Ne mijenja se Riječ kod Mene, i nisam Ja zalim robovima.'),
(4660, 50, 30, 'Na Dan kad reknemo Džehennemu: "Jesi li se napunio?" - i rekne: "Ima li dodatka?",'),
(4661, 50, 31, 'I približi se Džennet bogobojaznima nedaleko:'),
(4662, 50, 32, '"Ovo je ono šta vam se obećavalo, svakom pokajaniku, čuvaru,'),
(4663, 50, 33, 'Ko se plašio Milostivog u nevidljivosti i došao sa srcem obraćenim.'),
(4664, 50, 34, 'Uđite u njega u miru; to je Dan vječnosti."'),
(4665, 50, 35, 'Imaće oni u njemu šta htjednu, a kod Nas je dodatak.'),
(4666, 50, 36, 'A koliko smo uništili prije njih pokoljenja; oni su bili moćniji od njih u sili, pa su krstarili zemljama! Ima li ikakvog pribježišta?'),
(4667, 50, 37, 'Uistinu, u tome je opomena za onog ko srce ima ili koristi sluh, a on je svjedok.'),
(4668, 50, 38, 'I zaista smo stvorili nebesa i Zemlju i šta je među njima u šest dana, i nije Nas dotakao nikakav umor.'),
(4669, 50, 39, 'Pa strpi se nad onim šta govore i slavi sa hvalom Gospodara svog, prije izlaska Sunca, i prije zalaska.'),
(4670, 50, 40, 'I od noći: pa slavi Ga i poslije sedždi.'),
(4671, 50, 41, 'I osluškuj Dan (kad) pozove pozivač iz mjesta bliskog,'),
(4672, 50, 42, 'Dan kad budu čuli krik istinski - to će biti Dan izlaska.'),
(4673, 50, 43, 'Uistinu! Mi, Mi oživljavamo i usmrćujemo i Nama je dolazište.'),
(4674, 50, 44, 'Na Dan kad raspukne Zemlja od njih, u žurbi (će biti). To će biti sabiranje za Nas lahko.'),
(4675, 50, 45, 'Mi smo Najbolji znalac onog šta govore, i nisi ti nad njima prisila. Zato podsjećaj Kur\'anom onog ko se boji prijetnje Moje.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Tako Mi onih koji rasijavaju vijanjem,'),
(4677, 51, 2, 'Pa onih koji teret nose,'),
(4678, 51, 3, 'Te onih koji teku lagahno,'),
(4679, 51, 4, 'Te onih koji raspodjeljuju naredbom'),
(4680, 51, 5, 'Uistinu, ono čime vam se prijeti, Istina je,'),
(4681, 51, 6, 'I doista, Sud će se zbiti! -'),
(4682, 51, 7, 'Tako Mi neba punog putanja,'),
(4683, 51, 8, 'Uistinu, vi ste u govoru različiti.'),
(4684, 51, 9, 'Odvraća se od njega ko je odvraćen.'),
(4685, 51, 10, 'Prokleti bili oni koji nagađaju,'),
(4686, 51, 11, 'Oni koji su u gamretu nemarni!'),
(4687, 51, 12, 'Pitaju: "Kad će Dan sudnji?"'),
(4688, 51, 13, 'Onog dana kad oni budu na vatri mučeni.'),
(4689, 51, 14, '"Iskusite fitneluk svoj. Ovo je ono šta ste požurivali."'),
(4690, 51, 15, 'Uistinu, bogobojazni će u bašče i izvore,'),
(4691, 51, 16, 'Uzimaće šta im da Gospodar njihov. Uistinu, oni su prije toga bili dobročinitelji;'),
(4692, 51, 17, 'Bilo je malo što su od noći spavali,'),
(4693, 51, 18, 'I ranim zorama oni su oprost tražili,'),
(4694, 51, 19, 'I u imecima njihovim je bio dio za prosjaka i lišenog.'),
(4695, 51, 20, 'A u Zemlji su znaci za uvjerene,'),
(4696, 51, 21, 'I u dušama vašim. Pa zar ne vidite?'),
(4697, 51, 22, 'I u nebu je opskrba vaša i ono čime vam se prijeti.'),
(4698, 51, 23, 'Pa tako mi Gospodara neba i Zemlje, doista je to Istina, kao što (je istina da) vi govorite!'),
(4699, 51, 24, 'Da li ti je stigao hadis o gostima Ibrahimovim časnim?'),
(4700, 51, 25, 'Kad uđoše k njemu, pa rekoše: "Selam!" Reče: "Selam, ljudi neznani."'),
(4701, 51, 26, 'Tad ode do porodice svoje, te donese tele debelo,'),
(4702, 51, 27, 'Pa im ga primače. Reče: "Zar nećete jesti?"'),
(4703, 51, 28, 'Tad osjeti od njih strah. Rekoše: "Ne boj se!" I obradovaše ga dječakom učenim.'),
(4704, 51, 29, 'Tad priđe žena njegova u galami, pa udari lice svoje i reče: "Starica, nerotkinja!"'),
(4705, 51, 30, 'Rekoše: "Tako je rekao Gospodar tvoj. Uistinu, On, On je Mudri, Znalac."'),
(4706, 51, 31, 'Reče: "Pa šta je posao vaš, o izaslanici?"'),
(4707, 51, 32, 'Rekoše: "Uistinu, mi smo poslani narodu prestupnika,'),
(4708, 51, 33, 'Da na njih spustimo kamenje od gline,'),
(4709, 51, 34, 'Kod Gospodara tvog označeno za razuzdane."'),
(4710, 51, 35, 'Pa izvedosmo onog ko je u njemu bio od vjernika -'),
(4711, 51, 36, 'Ta nismo u njemu našli, osim kuću muslimana -'),
(4712, 51, 37, 'I ostavismo u njemu znak za one koji se plaše kazne bolne.'),
(4713, 51, 38, 'I u Musau, kad ga poslasmo faraonu sa autoritetom jasnim,'),
(4714, 51, 39, 'Pa se okrenu s osloncem svojim, i reče: "Čarobnjak ili luđak!"'),
(4715, 51, 40, 'Pa smo dograbili njega i vojske njegove te ih bacili u more, a on je bio prijekora vrijedan.'),
(4716, 51, 41, 'I u Adu, kad na njih poslasmo vjetar jalovi,'),
(4717, 51, 42, 'Nije poštedio ništa što je dohvatio, a da ga nije učinio kao truhlež.'),
(4718, 51, 43, 'I u Semudu, kad im je rečeno: "Uživajte neko vrijeme!"'),
(4719, 51, 44, 'Pa su se oglušili o naredbu Gospodara svog, te ih je udario grom, a oni su gledali;'),
(4720, 51, 45, 'Tad nikako nisu mogli ustati, niti se odbraniti.'),
(4721, 51, 46, 'I narodu Nuhovom od ranije. Uistinu, oni su bili narod grješnika.'),
(4722, 51, 47, 'A nebo - sazdali smo ga energijom - i uistinu, Mi smo Širitelji.'),
(4723, 51, 48, 'A Zemlju - raširili smo je - pa divni su Oni koji su (je) prostrli.'),
(4724, 51, 49, 'I od svake stvari stvorili smo par, da biste se vi poučili.'),
(4725, 51, 50, 'Zato bježite k Allahu; uistinu, ja sam vam od Njega opominjač jasni,'),
(4726, 51, 51, 'I ne stavljajte uz Allaha boga drugog. Uistinu, ja sam vam od Njega opominjač jasni.'),
(4727, 51, 52, 'Isto tako, nije došao onima prije njih nijedan poslanik, a da nisu rekli: "Čarobnjak" ili "Luđak!"'),
(4728, 51, 53, 'Zar su to preporučivali jedni drugima? Naprotiv, oni su bili narod prekršitelja.'),
(4729, 51, 54, 'Pa okreni se od njih, ta nećeš ti biti prekoren.'),
(4730, 51, 55, 'I opominji, pa uistinu će opomena koristiti vjernicima.'),
(4731, 51, 56, 'A stvorio sam ljude i džinne jedino da Me obožavaju.'),
(4732, 51, 57, 'Ne želim od njih nikakvu opskrbu, niti želim da Me hrane.'),
(4733, 51, 58, 'Uistinu! Allah! On je Opskrbljivač, Posjednik energije, Jaki!'),
(4734, 51, 59, 'Pa uistinu, oni koji čine zulm imaće udio slično udjelu drugova njihovih - zato neka Me ne požuruju.'),
(4735, 51, 60, 'Pa teško onima koji ne vjeruju, od Dana njihovog kojim im se prijeti!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Tako mi Gore,'),
(4737, 52, 2, 'I Knjige napisane -'),
(4738, 52, 3, 'Na pergamentu razvijenom -'),
(4739, 52, 4, 'I Kuće posjećivane,'),
(4740, 52, 5, 'I svoda uzdignutog,'),
(4741, 52, 6, 'I mora nabujalog,'),
(4742, 52, 7, 'Uistinu, kazna Gospodara tvog će se zbiti;'),
(4743, 52, 8, 'Nema tog ko će je odbiti:'),
(4744, 52, 9, 'Na Dan kad se zaljulja nebo talasanjem,'),
(4745, 52, 10, 'I pokrenu brda kretanjem!'),
(4746, 52, 11, 'Pa teško Tog dana poricateljima,'),
(4747, 52, 12, 'Onima koji se u upuštanju zabavljaju,'),
(4748, 52, 13, 'Na Dan kad budu gurnuti ka vatri Džehennema, (žestokim) guranjem.'),
(4749, 52, 14, '"Ovo je ta vatra koju ste poricali,'),
(4750, 52, 15, 'Pa je li ovo sihr, ili vi ne vidite?'),
(4751, 52, 16, 'Pržićete se njome, pa strpili se ili ne strpili, isto vam je. Bićete plaćeni samo za ono što ste radili."'),
(4752, 52, 17, 'Uistinu, bogobojazni će biti u baščama i uživanju,'),
(4753, 52, 18, 'Radovaće se u onom šta im je dao Gospodar njihov, a sačuvaće ih Gospodar njihov kazne džehima.'),
(4754, 52, 19, '"Jedite i pijte prijatno, zbog onog što ste radili."'),
(4755, 52, 20, 'Biće naslonjeni na sofama poredanim, a oženićemo ih hurijama lijepih krupnih očiju.'),
(4756, 52, 21, 'A oni koji su vjerovali - i slijedilo ih potomstvo njihovo u vjerovanju - priključićemo im potomstvo njihovo, i nećemo im zakinuti od djela njihovih ništa. Svaki čovjek je zalog onog šta je zaradio.'),
(4757, 52, 22, 'I pružit ćemo im voće i meso od čega žele.'),
(4758, 52, 23, 'Dodavat će u njemu čašu, neće u njoj biti nepromišljenosti, niti grijeha.'),
(4759, 52, 24, 'I obilaziće oko njih mladići njihovi, kao da su oni biser skriveni.'),
(4760, 52, 25, 'I neki od njih će prilaziti nekima, pitat će,'),
(4761, 52, 26, 'Reći će: "Uistinu, mi smo prije u porodicama našim bili brižni,'),
(4762, 52, 27, 'Pa nam je Allah bio dobar i sačuvao nas kazne vrelog vjetra.'),
(4763, 52, 28, 'Uistinu, mi smo od prije Njega prizivali, uistinu, On, On je Dobrostivi, Milosrdni."'),
(4764, 52, 29, 'Zato opominji - pa nisi ti milošću Gospodara tvog prorok, niti luđak.'),
(4765, 52, 30, 'Zar govore: "Pjesnik! Sačekaćemo za njega sudbinu sumnjivu!"'),
(4766, 52, 31, 'Reci: "Iščekujte! Pa zaista sam ja sa vama od čekalaca."'),
(4767, 52, 32, 'Zar im naređuju njihovi umovi ovo ili su oni narod koji granice prelazi?'),
(4768, 52, 33, 'Zar govore: "Izmislio ga je." Naprotiv! Ne vjeruju!'),
(4769, 52, 34, 'Pa neka donesu hadis sličan njemu, ako istinu govore!'),
(4770, 52, 35, 'Zar su stvoreni bez ičeg, ili su oni stvoritelji?'),
(4771, 52, 36, 'Zar su stvorili nebesa i Zemlju? Naprotiv, nisu uvjereni!'),
(4772, 52, 37, 'Zar su kod njih riznice Gospodara tvog, ili su oni vladari?'),
(4773, 52, 38, 'Zar imaju ljestve - prisluškuju na njima? Pa neka prisluškivač njihov donese dokaz jasan!'),
(4774, 52, 39, 'Zar su Njegove kćeri, a vaši sinovi?'),
(4775, 52, 40, 'Zar ih pitaš za nagradu, pa su oni nametom opterećeni?'),
(4776, 52, 41, 'Zar je kod njih nevidljivo, pa oni pišu?'),
(4777, 52, 42, 'Zar žele spletku? Pa oni koji ne vjeruju - oni su zapleteni!'),
(4778, 52, 43, 'Zar imaju boga sem Allaha? Slavljen neka je Allah od onog šta pridružuju!'),
(4779, 52, 44, 'I ako bi vidjeli komad neba koji pada, rekli bi: "Oblaci nagomilani!"'),
(4780, 52, 45, 'Zato ih pusti dok ne sretnu Dan svoj - onaj u kojem će biti zgromljeni,'),
(4781, 52, 46, 'Dan kad im neće koristiti spletka njihova nimalo, niti će oni biti pomognuti.'),
(4782, 52, 47, 'A uistinu, oni koji čine zulm, imaće kaznu mimo toga, ali većina njih ne zna.'),
(4783, 52, 48, 'I strpi se radi presude Gospodara tvog: ta doista si ti pod očima Našim; i slavi sa hvalom Gospodara svog u vrijeme kad ustaješ,'),
(4784, 52, 49, 'I noću - pa slavi Ga i s odstupanjem zvijezda.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Tako mi zvijezde kad zalazi,'),
(4786, 53, 2, 'Nije zalutao drug vaš, niti skrenuo,'),
(4787, 53, 3, 'I ne govori po želji.'),
(4788, 53, 4, 'To je jedino Objava - objavljuje se.'),
(4789, 53, 5, 'Uči ga Jedan žestokih sila,'),
(4790, 53, 6, 'Posjednik snage. Tad se postavio,'),
(4791, 53, 7, 'I on bio na horizontu najuzvišenijem,'),
(4792, 53, 8, 'Zatim se približio, pa spustio,'),
(4793, 53, 9, 'Pa bio na rastojanju dva luka ili bliži,'),
(4794, 53, 10, 'Te objavio (Allah) robu Svom, šta je objavio.'),
(4795, 53, 11, 'Nije poreklo srce šta je vidio.'),
(4796, 53, 12, 'Pa zar raspravljate s njim o onom što je vidio?'),
(4797, 53, 13, 'I zaista ga je vidio pri spuštanju drugom,'),
(4798, 53, 14, 'Kod Sidre krajnje'),
(4799, 53, 15, 'Kod koje je džennetsko sklonište,'),
(4800, 53, 16, 'Kad je Sidru pokrivalo šta je pokrivalo.'),
(4801, 53, 17, 'Nije skrenuo pogled, niti prekoračio,'),
(4802, 53, 18, 'Doista je vidio od znakova Gospodara svog - najveće.'),
(4803, 53, 19, 'Pa jeste li vidjeli Lata i Uzzaa,'),
(4804, 53, 20, 'I Menat, treću, posljednju?'),
(4805, 53, 21, 'Zar vama muško, a Njemu žensko?'),
(4806, 53, 22, 'To je onda podjela nepravedna!'),
(4807, 53, 23, 'To su samo imena - nadjeli ste ih vi i očevi vaši. Nije objavio Allah za njih nikakav dokaz. Slijede samo pretpostavku i ono šta žele duše, a već im je došla Uputa od Gospodara njihovog!'),
(4808, 53, 24, 'Ili: hoće li imati čovjek šta zaželi?'),
(4809, 53, 25, 'Pa, Allahov je zadnji i prvi.'),
(4810, 53, 26, 'I koliko je meleka u nebesima čije posredovanje neće ništa koristiti, izuzev nakon što dopusti Allah kome hoće i bude zadovoljan!'),
(4811, 53, 27, 'Uistinu, oni koji ne vjeruju u Ahiret, imenuju meleke imenovanjem ženskim,'),
(4812, 53, 28, 'A nemaju oni o tome nikakvo znanje. Slijede samo pretpostavku, a uistinu, pretpostavka ništa ne koristi Istini.'),
(4813, 53, 29, 'Zato se okreni od onog ko se odvraća od Opomene Naše i samo želi život Dunjaa.'),
(4814, 53, 30, 'To je njihov domet od znanja. Uistinu, Gospodar tvoj, On je Najbolji znalac ko je odlutao s puta Njegovog, i On je Najbolji znalac onog ko se upućuje.'),
(4815, 53, 31, 'A Allahovo je šta je u nebesima i šta je na Zemlji, da bi platio one koji čine zlo, onim što rade, i platio one koji rade lijepo - lijepim,'),
(4816, 53, 32, 'One koji izbjegavaju velike grijehe i razvrate, izuzev neznatnih. Uistinu, Gospodar tvoj je sveobuhvatnog oprosta. On je vaš Najbolji poznavalac otkad vas je stvorio od zemlje, i kad ste vi bili zameci u utrobama majki vaših. Zato ne pripisujte čistotu dušama svojim. On je Najbolji znalac onog ko se boji.'),
(4817, 53, 33, 'Pa jesi li vidio onog koji se okrenuo,'),
(4818, 53, 34, 'I dao malo i zadržao,'),
(4819, 53, 35, 'Je li kod njega znanje nevidljivog, pa on vidi?'),
(4820, 53, 36, 'Zar nije obaviješten o onom što je u listovima Musaovim,'),
(4821, 53, 37, 'I Ibrahimovim, koji je ispunio:'),
(4822, 53, 38, 'Da se neće teretiti opterećeni teretom drugog,'),
(4823, 53, 39, 'I da će čovjekovo biti samo za što se potrudi,'),
(4824, 53, 40, 'I da će trud njegov vidljiv biti -'),
(4825, 53, 41, 'Zatim će za njega biti plaćen plaćom najpotpunijom -'),
(4826, 53, 42, 'I da je Gospodaru tvom kraj (svega);'),
(4827, 53, 43, 'I da On, On zasmijava i rasplakuje,'),
(4828, 53, 44, 'I da On, On usmrćuje i oživljava,'),
(4829, 53, 45, 'I da On stvara par: muško i žensko,'),
(4830, 53, 46, 'Od kapi sjemena kada se izbaci -'),
(4831, 53, 47, 'I da je na Njemu stvaranje drugo;'),
(4832, 53, 48, 'I da je On, On Onaj koji obogaćuje i Koji pribavlja,'),
(4833, 53, 49, 'I da je On, On Gospodar Siriusa,'),
(4834, 53, 50, 'I da je On uništio Ada prvog,'),
(4835, 53, 51, 'I Semuda tad nije poštedio,'),
(4836, 53, 52, 'I narod Nuhov od ranije. Uistinu oni - bili su oni nepravedniji i više pretjerujući;'),
(4837, 53, 53, 'I Mutefiket je porušio,'),
(4838, 53, 54, 'Pa ih je pokrilo šta je pokrilo.'),
(4839, 53, 55, 'Pa o kojoj od blagodati Gospodara svog se raspravljaš?'),
(4840, 53, 56, 'Ovo je Opomena od opomena ranijih.'),
(4841, 53, 57, 'Približio se blisko,'),
(4842, 53, 58, 'Nema za njega mimo Allaha otkrivanja.'),
(4843, 53, 59, 'Pa zar se ovom hadisu iščuđujete,'),
(4844, 53, 60, 'I smijete se, a ne plačete,'),
(4845, 53, 61, 'I vi ste bezbrižni?'),
(4846, 53, 62, 'Pa padnite na sedždu Allahu i obožavajte!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Približio se čas i razdvojio Mjesec.'),
(4848, 54, 2, 'A ako vide znak, okreću se i govore: "Sihr neprestani!"'),
(4849, 54, 3, 'I porekli su i slijedili strasti svoje; a svaka stvar (ima) rok.'),
(4850, 54, 4, 'I već im je od vijesti došlo ono u čemu je preventiva,'),
(4851, 54, 5, 'Mudrost savršena - pa ne koriste opomene.'),
(4852, 54, 6, 'Zato se okreni od njih! Na dan kad pozove pozivač stvari ružnoj,'),
(4853, 54, 7, 'Oborenih pogleda svojih izaći će iz kabura, kao da su oni skakavci raštrkani,'),
(4854, 54, 8, 'Žureći k pozivaču. Govoriće nevjernici: "Ovo je dan težak."'),
(4855, 54, 9, 'Porekao je prije njih narod Nuhov: tad su porekli roba Našeg i rekli: "Luđak", i onemogućen je bio.'),
(4856, 54, 10, 'Tad je pozvao Gospodara svog: "Ja sam poražen, zato pomozi."'),
(4857, 54, 11, 'Pa smo otvorili kapije neba sa vodom lijućom,'),
(4858, 54, 12, 'I dali da provrije zemlja izvorima, pa se susrela voda na stvari već predodređenoj,'),
(4859, 54, 13, 'I nosili smo njega na (lađi) od samih dasaka i klinaca,'),
(4860, 54, 14, 'Plovila je pod očima Našim; plaća za onog ko je bio zanijekan.'),
(4861, 54, 15, 'I zaista smo to ostavili znakom; pa ima li iko poučen?'),
(4862, 54, 16, 'Pa kakva je bila kazna Moja i opomene Moje!'),
(4863, 54, 17, 'I zaista smo olakšali Kur\'an za pouku, pa ima li iko da se pouči?'),
(4864, 54, 18, 'Porekao je Ad, pa kakva je bila kazna Moja i opomene Moje?'),
(4865, 54, 19, 'Uistinu! Mi smo poslali na njih vjetar ledeni u danu nesreće neprestajuće,'),
(4866, 54, 20, 'Čupao je ljude kao da su oni debla palmi izvaljena.'),
(4867, 54, 21, 'Pa kakva je bila kazna Moja i opomene Moje!'),
(4868, 54, 22, 'I zaista smo olakšali Kur\'an za zikr, pa ima li iko ko zikri?'),
(4869, 54, 23, 'Poricao je Semud opomene,'),
(4870, 54, 24, 'Pa su rekli: "Zar smrtnika - od nas jednoga - njega (da) slijedimo? Uistinu, mi bismo tad bili u zabludi i bezumlju.'),
(4871, 54, 25, 'Zar da njemu bude spuštena Opomena između nas? Naprotiv, on je lažov drski!"'),
(4872, 54, 26, 'Saznat će sutra ko je lažov drski.'),
(4873, 54, 27, 'Uistinu! Mi im šaljemo devu iskušenjem, zato ih promatraj i strpi se!'),
(4874, 54, 28, 'I obavijesti ih da će biti dioba vode među njima. Svakom pojenju biće se prisutno.'),
(4875, 54, 29, 'Tad pozvaše druga svog, pa uze (mač), te (je) rani.'),
(4876, 54, 30, 'Pa kakva je bila kazna Moja i opomene Moje!'),
(4877, 54, 31, 'Uistinu! Mi smo poslali na njih krik jedan, pa bijahu kao suharci graditelja tora.'),
(4878, 54, 32, 'A zaista smo olakšali Kur\'an za podsjećanje, pa ima li iko ko se podsjeća?'),
(4879, 54, 33, 'Poricao je narod Lutov opomene.'),
(4880, 54, 34, 'Uistinu! Mi smo poslali na njih kišu kamenu, izuzev porodice Lutove; spasli smo ih u ranu zoru,'),
(4881, 54, 35, 'Blagodat od Nas; tako nagrađujemo onog ko zahvaljuje.'),
(4882, 54, 36, 'I doista ih je upozorio na udar Naš, pa su sumnjali u opomene.'),
(4883, 54, 37, 'I doista su od njega htjeli goste njegove, pa smo oslijepili oči njihove: "Ta iskusite kaznu Moju i opomene Moje!"'),
(4884, 54, 38, 'I doista, pogodila ih je jutrom kazna trajna.'),
(4885, 54, 39, 'Pa iskusite kaznu Moju i opomene Moje!'),
(4886, 54, 40, 'I doista smo olakšali Kur\'an za pouku, pa ima li iko ko se poučava?'),
(4887, 54, 41, 'A doista su ljudstvu faraonovu došle opomene.'),
(4888, 54, 42, 'Porekli su znakove Naše, sve njih, pa smo ih dograbili grabljenjem Svemoćnog, Snažnog.'),
(4889, 54, 43, 'Jesu li nevjernici vaši bolji od takvih, ili imate imunitet u zeburima?'),
(4890, 54, 44, 'Zar kažu: "Mi smo skup pobjednički?"'),
(4891, 54, 45, 'Biće poražen skup, i okrenut će leđa.'),
(4892, 54, 46, 'Naprotiv! Čas je obećano vrijeme njihovo, a Čas će biti teži i gorči.'),
(4893, 54, 47, 'Uistinu! Biće prestupnici u zabludi i bezumlju,'),
(4894, 54, 48, 'Na Dan kad budu dovučeni u vatru na licima svojim: "Iskusite dodir sekara!"'),
(4895, 54, 49, 'Uistinu! Mi smo svaku stvar stvorili po mjeri.'),
(4896, 54, 50, 'A komanda Naša je samo jedna, kao sijev oka.'),
(4897, 54, 51, 'I doista smo uništili pobornike vaše; pa ima li iko ko se poučava?'),
(4898, 54, 52, 'A svaku stvar koju su učinili, u zeburima je,'),
(4899, 54, 53, 'I sve malo i veliko, zapisano je.'),
(4900, 54, 54, 'Uistinu, bogobojazni će u baščama i rijekama biti,'),
(4901, 54, 55, 'U sjedištu Istine, kod Vladara Svemoćnog.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Milostivi!'),
(4903, 55, 2, 'Poučava Kur\'anu,'),
(4904, 55, 3, 'Stvara čovjeka,'),
(4905, 55, 4, 'Uči ga izražavanju.'),
(4906, 55, 5, 'Sunce i Mjesec su po proračunu,'),
(4907, 55, 6, 'I zvijezda i drvo čini sedždu,'),
(4908, 55, 7, 'I nebo! Uzdigao ga je, i postavio mjerilo,'),
(4909, 55, 8, 'Da ne prelazite mjeru.'),
(4910, 55, 9, 'I uspostavljajte težinu po pravdi i ne zakidajte na vagi.'),
(4911, 55, 10, 'I Zemlja! Postavio je nju za stvorenja,'),
(4912, 55, 11, 'Na njoj je voće i palme sa čaškama,'),
(4913, 55, 12, 'I zrno sa ljuskom i mirisna biljka.'),
(4914, 55, 13, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4915, 55, 14, 'Stvorio je čovjeka od gline zvečeće kao grnčarija,'),
(4916, 55, 15, 'I stvorio je džinna od plamena vatre.'),
(4917, 55, 16, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4918, 55, 17, 'Gospodar dvaju istoka i Gospodar dvaju zapada,'),
(4919, 55, 18, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4920, 55, 19, 'Pustio je mora dva - susreću se,'),
(4921, 55, 20, 'Između njih je berzeh, ne prelaze (ga),'),
(4922, 55, 21, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4923, 55, 22, 'Izlaze iz njih biser i merdžan,'),
(4924, 55, 23, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4925, 55, 24, 'I Njegova su plovila - naprave u moru kao brda,'),
(4926, 55, 25, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4927, 55, 26, 'Svako ko je na njoj, prolazan je,'),
(4928, 55, 27, 'A traje lice Gospodara tvog, Vlasnika uzvišenosti i plemenitosti.'),
(4929, 55, 28, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4930, 55, 29, 'Pita Ga ko je na nebesima i Zemlji. Svakog dana On je u dejstvu.'),
(4931, 55, 30, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4932, 55, 31, 'Posvetićemo se vama, o tereta dva,'),
(4933, 55, 32, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4934, 55, 33, 'O skupino džinna i ljudi! Ako uzmognete da prodrete iz zona nebesa i Zemlje, tad prodrite! Nećete prodrijeti, izuzev s energijom!'),
(4935, 55, 34, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4936, 55, 35, 'Slaće se na vas oba plamen vatre i bakar, pa se nećete odbraniti.'),
(4937, 55, 36, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4938, 55, 37, 'Pa kad se rascijepi nebo i bude ružičasto kao balzam,'),
(4939, 55, 38, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4940, 55, 39, 'Tad, Tog dana, neće se pitati za grijeh svoj čovjek, niti džinn.'),
(4941, 55, 40, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4942, 55, 41, 'Poznaće se krivci po biljegu njihovom, pa će biti uzimani za kike i stopala.'),
(4943, 55, 42, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4944, 55, 43, 'Ovo je taj Džehennem kojeg su poricali prestupnici.'),
(4945, 55, 44, 'Kružiće između njega i vruće ključale vode,'),
(4946, 55, 45, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4947, 55, 46, 'A onaj ko se bojao stajanja pred Gospodarom svojim, imaće bašče dvije,'),
(4948, 55, 47, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4949, 55, 48, 'Pune grana,'),
(4950, 55, 49, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4951, 55, 50, 'U njima su izvora dva (koja) teku.'),
(4952, 55, 51, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4953, 55, 52, 'U njima su od svakog voća vrste dvije,'),
(4954, 55, 53, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4955, 55, 54, 'Naslonjeni na posteljama će biti, naličje njihovo od brokata, a plodovi bašče obje nisko,'),
(4956, 55, 55, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4957, 55, 56, 'U njima su (hurije) kratkih pogleda, nije ih prije njih razdjevičio čovjek, niti džinn.'),
(4958, 55, 57, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4959, 55, 58, 'Kao da su one jakut i merdžan,'),
(4960, 55, 59, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4961, 55, 60, 'Da li će plaća dobroti biti (išta), izuzev dobrota?'),
(4962, 55, 61, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4963, 55, 62, 'A osim njih, biće bašče dvije,'),
(4964, 55, 63, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4965, 55, 64, 'Obje tamnozelene,'),
(4966, 55, 65, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4967, 55, 66, 'U njima dva izvora prskajuća,'),
(4968, 55, 67, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4969, 55, 68, 'U njima je voće i hurme i nar,'),
(4970, 55, 69, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4971, 55, 70, 'U njima su dobre, lijepe,'),
(4972, 55, 71, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4973, 55, 72, 'Hurije zadržane u paviljonima,'),
(4974, 55, 73, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4975, 55, 74, 'Nije ih prije njih razdjevičio čovjek, niti džinn -'),
(4976, 55, 75, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4977, 55, 76, 'Naslonjeni na jastuku zelenom i ćilimima prekrasnim.'),
(4978, 55, 77, 'Pa koju od blagodati Gospodara svoga poričete!?'),
(4979, 55, 78, 'Blagoslovljeno neka je ime Gospodara tvog, Vlasnika uzvišenosti i plemenitosti!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Kad se dogodi događaj'),
(4981, 56, 2, 'Neće biti poricanja događanja njegovog,'),
(4982, 56, 3, 'Ponižavajući, uzdižući;'),
(4983, 56, 4, 'Kad se zatrese Zemlja potresom,'),
(4984, 56, 5, 'I smrve brda drobljenjem,'),
(4985, 56, 6, 'I budu prašina rasuta,'),
(4986, 56, 7, 'I budete vrste tri:'),
(4987, 56, 8, 'Pa drugovi desne strane - šta su drugovi desne strane?'),
(4988, 56, 9, 'I drugovi lijeve strane - šta su drugovi lijeve strane?'),
(4989, 56, 10, 'I prednjaci, prednjaci:'),
(4990, 56, 11, 'Takvi će biti približeni,'),
(4991, 56, 12, 'U bašče uživanja,'),
(4992, 56, 13, 'Mnoštvo od prvih,'),
(4993, 56, 14, 'A malo od posljednjih,'),
(4994, 56, 15, 'Na sofama (ukrasom) obloženim,'),
(4995, 56, 16, 'Naslonjeni na njima, naspramni,'),
(4996, 56, 17, 'Obilaziće oko njih mladići vječni,'),
(4997, 56, 18, 'S peharima i ibricima i čašom s izvorišta,'),
(4998, 56, 19, 'Neće dobiti glavobolju od njega, niti se zamoriti,'),
(4999, 56, 20, 'I voćem od kojeg će birati,'),
(5000, 56, 21, 'I mesom ptičjim od čega zažele,'),
(5001, 56, 22, 'I hurije lijepih krupnih očiju,'),
(5002, 56, 23, 'Kao primjerci bisera skrivenog -'),
(5003, 56, 24, 'Plaća za ono šta su radili.'),
(5004, 56, 25, 'Neće čuti u njemu besmislicu, niti griješnost,'),
(5005, 56, 26, 'Izuzev govora: "Selam! Selam!"'),
(5006, 56, 27, 'A drugovi desne strane - šta su drugovi desne strane?'),
(5007, 56, 28, 'U lotosovom drveću bez bodlji,'),
(5008, 56, 29, 'I bananama nanizanim,'),
(5009, 56, 30, 'I hladu rasprostrtom,'),
(5010, 56, 31, 'I vodi tekućoj,'),
(5011, 56, 32, 'I voću mnogobrojnom -'),
(5012, 56, 33, 'Nije odsječeno, niti zabranjeno -'),
(5013, 56, 34, 'I posteljama uzdignutim.'),
(5014, 56, 35, 'Uistinu! Mi smo ih stvorili stvaranjem,'),
(5015, 56, 36, 'I učinili ih djevicama,'),
(5016, 56, 37, 'Umilnim vršnjakinjama,'),
(5017, 56, 38, 'Drugovima desne strane,'),
(5018, 56, 39, 'Mnoštvu od prvih,'),
(5019, 56, 40, 'I mnoštvu od posljednjih.'),
(5020, 56, 41, 'A drugovi lijeve strane - šta su drugovi lijeve strane?'),
(5021, 56, 42, 'U pržećem vjetru i vrijućoj vodi će biti,'),
(5022, 56, 43, 'I sjenci crnog dima,'),
(5023, 56, 44, 'Ni hladnoj, ni oplemenjujućoj.'),
(5024, 56, 45, 'Uistinu, oni su prije toga bili raskošnici,'),
(5025, 56, 46, 'I ustrajali na prekršaju strašnom,'),
(5026, 56, 47, 'I govorili: "Hoćemo li kad pomremo i budemo prah i kosti, uistinu mi biti podignuti?'),
(5027, 56, 48, 'Ili očevi naši raniji?"'),
(5028, 56, 49, 'Reci: "Uistinu, raniji i kasniji."'),
(5029, 56, 50, 'Doista će biti sakupljeni za određeno vrijeme Dana poznatog,'),
(5030, 56, 51, 'Zatim ćete uistinu vi, o zabludjeli poricatelji,'),
(5031, 56, 52, 'Sigurno jesti od drveta zekkuma,'),
(5032, 56, 53, 'Pa ćete njime trbuhe puniti,'),
(5033, 56, 54, 'Te piti na njega vrijuću vodu,'),
(5034, 56, 55, 'Ta pit ćete pijenjem žedne kamile.'),
(5035, 56, 56, 'Ovo će biti ugošćenje njihovo na Dan sudnji.'),
(5036, 56, 57, 'Mi smo stvorili vas, pa zašto ne povjerujete?'),
(5037, 56, 58, 'Pa jeste li vidjeli šta izbacujete?'),
(5038, 56, 59, 'Da li vi to stvarate, ili smo Mi Stvoritelji?'),
(5039, 56, 60, 'Mi smo odredili među vama smrt, i nećemo Mi biti preteknuti,'),
(5040, 56, 61, 'Da izmijenimo likove vaše i oblikujemo vas u ono šta ne znate,'),
(5041, 56, 62, 'A doista znate nastanak prvi. Pa zašto se ne poučite?'),
(5042, 56, 63, 'Pa vidite li šta sijete?'),
(5043, 56, 64, 'Da li vi dajete da to uzraste, ili smo Mi Uzgajatelji?'),
(5044, 56, 65, 'Da hoćemo, sigurno bismo ga učiniti kršom, tad biste se počeli čuditi:'),
(5045, 56, 66, '"Uistinu, mi smo oglobljeni!'),
(5046, 56, 67, 'Naprotiv, mi smo lišeni."'),
(5047, 56, 68, 'Pa vidite li vodu koju pijete?'),
(5048, 56, 69, 'Da li je vi spuštate iz oblaka, ili smo Mi Spuštaoci?'),
(5049, 56, 70, 'Da hoćemo, učinili bismo je slanom; pa zašto ne zahvaljujete?'),
(5050, 56, 71, 'Pa vidite li vatru koju palite?'),
(5051, 56, 72, 'Da li vi stvarate drvo njeno, ili smo Mi Stvoritelji?'),
(5052, 56, 73, 'Mi smo je učinili podsjetnikom i korišću za stanovnike divljine.'),
(5053, 56, 74, 'Zato slavi ime Gospodara svog Veličanstvenog!'),
(5054, 56, 75, 'Pa ne! Kunem se položajima zvijezda,'),
(5055, 56, 76, 'A uistinu je to zakletva - kad biste znali - velika,'),
(5056, 56, 77, 'Uistinu, on je Kur\'an plemeniti,'),
(5057, 56, 78, 'U Knjizi skrivenoj;'),
(5058, 56, 79, 'Koju ne dotiču, izuzev očišćeni,'),
(5059, 56, 80, 'Objava od Gospodara svjetova.'),
(5060, 56, 81, 'Pa da li vi ovaj hadis prezirete,'),
(5061, 56, 82, 'I postupate (tako) da vi opskrbu svoju poričete?'),
(5062, 56, 83, 'Pa zašto ne - kad stigne grlu -'),
(5063, 56, 84, 'I vi tad budete gledali,'),
(5064, 56, 85, 'A Mi smo mu bliže od vas, ali ne vidite,'),
(5065, 56, 86, 'Pa zašto ne - ako niste dužni (polagati račun) -'),
(5066, 56, 87, 'Vratite nju, ako ste iskreni?'),
(5067, 56, 88, 'Pa, ako bude od približenih,'),
(5068, 56, 89, 'Onda je rewh i miomiris, i Džennet zadovoljstva.'),
(5069, 56, 90, 'A ako bude od drugova desne strane,'),
(5070, 56, 91, 'Tad: "Selam tebi" od drugova desne strane.'),
(5071, 56, 92, 'A ako bude od poricatelja zabludjelih,'),
(5072, 56, 93, 'Tad je ugošćenje od vrijuće vode,'),
(5073, 56, 94, 'I prženje džehimom.'),
(5074, 56, 95, 'Uistinu! Ovo je ta Istina sigurna.'),
(5075, 56, 96, 'Zato slavi ime Gospodara svog Veličanstvenog!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Allaha slavi ono što je na nebesima i Zemlji, a On je Moćni, Mudri.'),
(5077, 57, 2, 'Njegova je vlast nebesa i Zemlje; oživljava i usmrćuje i On nad svakom stvari ima moć.'),
(5078, 57, 3, 'On je Prvi i Posljednji, i Vanjski i Unutarnji, i On je o svakoj stvari Znalac.'),
(5079, 57, 4, 'On je Taj koji je stvorio nebesa i Zemlju u šest dana, zatim se postavio na Arš. Zna šta prodire u Zemlju i šta izlazi iz nje, i šta se spušta iz neba, a šta uzlazi u njega. I On je sa vama ma gdje bili. A Allah je onog šta radite Vidilac.'),
(5080, 57, 5, 'Njegova je vlast nebesa i Zemlje. A Allahu se vraćaju stvari.'),
(5081, 57, 6, 'Daje da noć uđe u dan i daje da dan uđe u noć, a On je znalac grudi.'),
(5082, 57, 7, 'Vjerujte u Allaha i Poslanika Njegovog i trošite od onog čemu vas je učinio nasljednicima. Pa oni od vas koji budu vjerovali i udjeljivali, imaće oni nagradu veliku.'),
(5083, 57, 8, 'I šta vam je? Ne vjerujete u Allaha, a Poslanik vas poziva da vjerujete u Gospodara vašeg, i već je uzeo zavjet vaš, ako ste vjernici.'),
(5084, 57, 9, 'On je Taj koji objavljuje robu Svom ajete jasne, da vas izvede iz tmina na svjetlost. A uistinu, Allah je vama Samilosni, Milosrdni.'),
(5085, 57, 10, 'I šta vam je pa ne trošite na putu Allahovom? A Allahovo je naslijeđe nebesa i Zemlje. Nije jednak od vas ko je trošio prije pobjede i borio se. Takvi su veći stepenom od onih koji su trošili poslije i borili se. A svakojem je Allah obećao dobro; a Allah je o onom šta radite Obaviješteni.'),
(5086, 57, 11, 'Ko je taj ko će uzajmiti Allahu zajam lijep, pa da mu ga udvostruči, a imaće on nagradu plemenitu?'),
(5087, 57, 12, 'Na Dan kad budeš vidio vjernike i vjernice - kretaće se svjetlo njihovo ispred njih i s desnih strana njihovih: "Radosna vijest vama Danas! Bašče ispod kojih teku rijeke, bićete vječno u njima." To je to, uspjeh veličanstven!'),
(5088, 57, 13, 'Na Dan kad budu munafici i munafikinje govorili onima koji su vjerovali: "Čekajte nas (da) pozajmimo od svjetla vašeg!" Biće rečeno: "Vratite se vi nazad pa tražite svjetlo." Tad će između njih biti postavljen bedem, on će imati kapiju. Unutrašnjost njegova u njemu, biće milost, a vanjština njegova, ispred njega - kazna.'),
(5089, 57, 14, 'Dozivaće ih: "Zar nismo bili s vama?" Reći će (vjernici): "Svakako, međutim, vi ste iskušavali duše svoje i iščekivali i sumnjali, i zavarale su vas želje - dok nije došla naredba Allahova. A obmanuo vas je o Allahu garur.'),
(5090, 57, 15, 'Zato se Danas od vas neće uzeti fidja, niti od onih koji nisu vjerovali. Sklonište vaše je vatra, ona je zaštitnik vaš, a loše je dolazište!"'),
(5091, 57, 16, 'Zar nije vrijeme onima koji vjeruju, da se srca njihova ponize spominjanju Allaha i onom šta je objavio od Istine, i ne budu kao oni kojima je data Knjiga prije, pa im se odužio rok, te otvrdla srca njihova, a mnogi od njih su grješnici.'),
(5092, 57, 17, 'Znajte da Allah oživljava zemlju nakon mrtvila njenog! Doista smo vam objasnili ajete, da biste vi shvatili.'),
(5093, 57, 18, 'Uistinu! Oni koji daju sadaku i koje daju sadaku i uzajmljuju Allahu zajam lijep, udvostručiće im, a imaće oni nagradu plemenitu.'),
(5094, 57, 19, 'A oni koji vjeruju u Allaha i Poslanika Njegovog, ti takvi su istiniti, i šehidi kod Gospodara svog; imaće oni nagradu svoju i svjetlo svoje. A oni koji ne vjeruju i poriču ajete Naše - takvi će biti stanovnici džehima.'),
(5095, 57, 20, 'Znajte da je život Dunjaa samo zabava i igra i dekor i hvalisanje među vama, i povećavanje imetaka i djece! Kao primjer kiše - zadivljuje zemljoradnike bilje njeno - zatim svehne, pa ga vidiš požutjelog, potom bude pljeva. A na Ahiretu je kazna žestoka i oprost od Allaha i zadovoljstvo, a život Dunjaa je samo stvar varljiva.'),
(5096, 57, 21, 'Utrkujte se prema oprostu Gospodara svog i Džennetu čija je širina kao širina neba i Zemlje - pripremljen je za one koji vjeruju u Allaha i poslanike Njegove. To je blagodat Allahova, daje je kome hoće, a Allah je Posjednik blagodati veličanstvene!'),
(5097, 57, 22, 'Nikakva nesreća se ne dogodi na Zemlji, niti u dušama vašim, a da nije u Knjizi prije nego je stvorimo. Uistinu, to je Allahu lahko,'),
(5098, 57, 23, 'Da ne biste očajavali za onim šta vam je izmaklo, niti likovali zbog onog šta vam je dao. A Allah ne voli nikojeg razmetljivog hvalisavca,'),
(5099, 57, 24, 'One koji škrtare i naređuju svijetu tvrdičluk. A ko se okrene, pa uistinu, Allah, On je Neovisni, Hvaljeni.'),
(5100, 57, 25, 'Zaista smo slali poslanike Naše sa dokazima jasnim i objavljivali s njima Knjigu i mjerilo, da svijet postupa po pravdi. I spustili smo gvožđe - u njemu je sila žestoka i koristi za ljude - i da Allah zna ko pomaže Njega i poslanike Njegove u nevidljivom. Uistinu! Allah je Silni, Svemoćni.'),
(5101, 57, 26, 'I doista smo poslali Nuha i Ibrahima, i načinili u potomstvu njihovom vjerovjesništvo i Knjigu, pa je od njih onaj ko je upućen, a mnogi od njih su grješnici.'),
(5102, 57, 27, 'Zatim smo stopama njihovim slali poslanike Naše, i poslali Isaa, sina Merjeminog i dali mu Indžil, i načinili u srcima onih koji su ga slijedili blagost i milost. A monaštvo - izmislili su ga, nismo im ga propisali - izuzev traženja zadovoljstva Allahovog. Pa ne paze ga istinskom pažnjom njegovom, te ćemo onima od njih koji vjeruju dati nagradu njihovu, a mnogi od njih su grješnici.'),
(5103, 57, 28, 'O vi koji vjerujete! Bojte se Allaha i vjerujte u Poslanika Njegovog: dat će vam dvostruko od milosti Svoje, i načiniće vam svjetlo s kojim ćete ići, i oprostiće vam. A Allah je Oprosnik, Milosrdni;'),
(5104, 57, 29, 'Da bi znali sljedbenici Knjige da ne upravljaju ničim od blagodati Allahove, i da je blagodat u ruci Allahovoj, daje je kome hoće. A Allah je Posjednik blagodati veličanstvene!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Doista je Allah čuo riječi one koja se s tobom raspravljala o mužu svom i jadala se Allahu - a Allah čuje razgovor vas dvoje. Uistinu, Allah je Onaj koji čuje, Onaj koji vidi.'),
(5106, 58, 2, 'Oni od vas koji se razvedu od žena njihovih ziharom - nisu one majke njihove. Majke njihove su samo one koje su ih rodile. A uistinu, oni govore ružne riječi i laž. I uistinu, Allah je Brisalac (grijeha), Oprosnik.'),
(5107, 58, 3, 'A oni koji se razvedu od žena svojih ziharom, zatim povuku šta su rekli, pa (kazna) je oslobođenje roba, prije nego se dodirnu. To je ono šta vam se savjetuje. A Allah je o onom što radite Obaviješteni.'),
(5108, 58, 4, 'Pa ko ne nađe, tad je post dva mjeseca uzastopno prije nego se dotaknu. Pa ko ne mogne, tad je hranjenje šezdeset siromaha. To da vjerujete u Allaha i Poslanika Njegovog, a to su granice Allahove. A imaće nevjernici kaznu bolnu.'),
(5109, 58, 5, 'Uistinu! Oni koji se suprotstavljaju Allahu i Poslaniku Njegovom, biće suzbijeni kao što su suzbijani oni prije njih. I doista smo spustili znakove jasne, a imaće nevjernici kaznu prezrenu.'),
(5110, 58, 6, 'Na Dan kad ih Allah sve podigne, tad će ih obavijestiti o onom šta su radili: Allah je to zbrojao, a oni su to zaboravili. A Allah je nad svakom stvari Svjedok.'),
(5111, 58, 7, 'Zar ne vidiš da Allah zna šta je u nebesima i šta je na Zemlji? Ne biva nikakav tajni razgovor troje, a da On nije četvrti njihov, niti petero, a da On nije šesti njihov, ni manje od tog, ni više, a da On nije sa njima ma gdje bili; zatim će ih obavijestiti o onom šta su radili, na Dan kijameta. Uistinu! Allah je o svakoj stvari Znalac.'),
(5112, 58, 8, 'Zar nisi vidio one kojima je zabranjeno tajno dogovaranje - zatim se vraćaju onom što im je zabranjeno i tajno se dogovaraju o grijehu i neprijateljstvu i neposlušnosti Poslaniku. A kad ti dođu pozdravljaju te onim čim te nije pozdravljao Allah, i kažu u dušama svojim: "Da nas ne kazni Allah zbog onog što govorimo?" Dovoljan je njima Džehennem, pržiće se njime, ta loše je dolazište!'),
(5113, 58, 9, 'O vi koji vjerujete! Kad se budete tajno dogovarali, tad se ne dogovarajte o grijehu i neprijateljstvu i neposlušnosti Poslaniku - a dogovarajte se o pravednosti i bogobojaznosti; i bojte se Allaha, Onog kojem ćete biti sabrani.'),
(5114, 58, 10, 'Doista je tajno dogovaranje od šejtana, da bi ražalostio one koji vjeruju. A neće im ništa štetiti, izuzev s dozvolom Allahovom; i u Allaha zato neka se pouzdaju vjernici.'),
(5115, 58, 11, 'O vi koji vjerujete! Kad vam se kaže: "Načinite mjesta pri sjedenju", tad načinite mjesta - načiniće Allah mjesta vama. A kad se kaže: "Dignite se", tad se podignite. Allah uzdiže one od vas koji vjeruju i one kojima je dato znanje, stepenima. A Allah je o onom šta radite Obaviješteni.'),
(5116, 58, 12, 'O vi koji vjerujete! Kad se budete savjetovali s Poslanikom, tad prije savjetovanja vašeg dajte sadaku. To je bolje za vas i čišće. Pa ako ne nađete - pa uistinu, Allah je Oprosnik, Milosrdni.'),
(5117, 58, 13, 'Da li se plašite da prije savjetovanja vašeg date sadake? Pa ako ne učinite, a oprosti vam Allah, tad obavljajte salat i dajite zekat i poslušajte Allaha i Poslanika Njegovog. A Allah je o onom šta radite Obaviješteni.'),
(5118, 58, 14, 'Zar nisi vidio one koji prijateljuju sa narodom onih na koje se rasrdio Allah? Nisu oni od vas, niti od njih, a kunu se na laž i oni znaju.'),
(5119, 58, 15, 'Allah je za njih pripremio kaznu žestoku. Uistinu, zlo je ono šta oni rade.'),
(5120, 58, 16, 'Uzeli su zakletve svoje štitom, te odvraćaju s puta Allahovog. Pa imaće oni kaznu prezrenu.'),
(5121, 58, 17, 'Neće im koristiti imeci njihovi, niti djeca njihova protiv Allaha, nimalo. Takvi će biti stanovnici vatre, oni će u njoj biti vječito.'),
(5122, 58, 18, 'Na Dan kad ih Allah sve podigne, tad će se zakleti Njemu kao što se zaklinju vama - i misliće da su oni na nečemu. Besumnje, uistinu oni - oni su lašci!'),
(5123, 58, 19, 'Ovladao je njima šejtan pa učinio da oni zaborave sjećanje Allaha. Takvi su partija šejtanova. Besumnje. Uistinu! Partija šejtanova - oni su gubitnici!'),
(5124, 58, 20, 'Uistinu, oni koji se suprotstavljaju Allahu i Poslaniku Njegovom, takvi će biti među najnižima.'),
(5125, 58, 21, 'Propisao je Allah: "Sigurno ću pobijediti Ja i poslanici Moji!" Uistinu! Allah je Silni, Svemoćni.'),
(5126, 58, 22, 'Nećeš naći ljude koji vjeruju u Allaha i Dan posljednji, a da vole onog ko se suprotstavlja Allahu i Poslaniku Njegovom, makar bili očevi njihovi ili sinovi njihovi ili braća njihova ili rod njihov. Takvima je upisao u srca njihova iman i ojačao ih Duhom od Sebe, a uvešće ih u bašče ispod kojih teku rijeke, vječno će biti u njima. Zadovoljan je Allah njima i zadovoljni Njime. Takvi su stranka Allahova! Besumnje! Uistinu! stranka Allahova - oni su oni koji će uspjeti!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Allaha slavi šta je u nebesima i šta je na Zemlji, a On je Moćni, Mudri.'),
(5128, 59, 2, 'On je Taj koji je istjerao one od sljedbenika Knjige koji nisu vjerovali, iz domova njihovih, pri prvom progonstvu. Niste mislili da će izaći, a oni su mislili da će ih tvrđave njihove zaštititi od Allaha. Tad im je došao Allah otkud nisu računali i ubacio u srca njihova strah; rušili su kuće svoje rukama svojim i rukama vjernika. Zato primite pouku, o posjednici opažanja!'),
(5129, 59, 3, 'I da im Allah nije propisao izgon, sigurno bi ih kaznio na Dunjau, a imaće oni na Ahiretu kaznu vatrom.'),
(5130, 59, 4, 'To zato što su se oni suprotstavljali Allahu i Poslaniku Njegovom. A ko se suprotstavi Allahu - pa uistinu, Allah je žestok kaznom.'),
(5131, 59, 5, 'To što ste posjekli od palmi ili ih ostavili uspravno na korjenju njihovom, pa s dozvolom je Allahovom, i da ponizi grješnike.'),
(5132, 59, 6, 'A ono što je Allah od njih dao Poslaniku Svom kao plijen - pa niste potjerali zbog njega nijednog konja, niti devu. Ali, Allah pruža vlast poslanicima Svojim nad kim hoće; a Allah nad svakom stvari ima moć.'),
(5133, 59, 7, 'Ono što je Allah dao kao plijen Poslaniku Svom od stanovnika naselja, to je za Allaha i Poslanika Njegovog i za rod i za siročad i za siromahe i sina puta - da ne bude obrtanja među bogatašima vašim. A šta vam da Poslanik, pa uzmite to. A ono što vam zabrani, pa suspregnite se i bojte se Allaha. Uistinu! Allah je žestok kaznom -'),
(5134, 59, 8, '(I) fukarama muhadžirima koji su protjerani iz domova njihovih i imanja njihovih, tražeći blagodat od Allaha i zadovoljstvo, i pomažući Allahu i Poslaniku Njegovom. Ti takvi su iskreni.'),
(5135, 59, 9, 'A oni koji su stupili u kuću i vjerovanje prije njih, vole onog ko im se doselio, i ne nalaze u grudima svojim potrebu za onim što im je dato, i više vole (njima) nego dušama svojim, makar oni bili u oskudici. A onaj ko sačuva dušu svoju od pohlepe - pa ti takvi će biti uspješni.'),
(5136, 59, 10, 'A oni koji su došli poslije njih, govore: "Gospodaru naš! Oprosti nama i braći našoj koji su nas pretekli u vjerovanju, i ne načini u srcima našim mržnju prema onima koji vjeruju. Gospodaru naš! Uistinu, Ti si Samilosni, Milosrdni."'),
(5137, 59, 11, 'Zar nisi vidio licemjere koji govore braći svojoj od sljedbenika Knjige koji ne vjeruju: "Ako budete protjerani sigurno ćemo izaći s vama i nećemo protiv vas poslušati nikoga nikada, a ako budete napadnuti, sigurno ćemo vam pomoći." A Allah svjedoči: uistinu su oni lašci.'),
(5138, 59, 12, 'Sigurno, ako bi bili protjerani, ne bi izašli s njima, i sigurno, ako bi bili napadnuti, ne bi im pomogli. A doista, ako bi im pomogli, sigurno bi okrenuli leđa - potom ne bi bili pomognuti.'),
(5139, 59, 13, 'Sigurno ste vi jačeg straha u grudima njihovim, od Allaha. To zato što su oni ljudi koji ne shvataju.'),
(5140, 59, 14, 'Neće se protiv vas boriti skupno, izuzev u naseljima utvrđenim ili iza zidova. Sila njihova je među njima žestoka. Misliš oni su složni, a srca njihova su razjedinjena. To zato što su oni ljudi koji ne razumiju.'),
(5141, 59, 15, 'Slični su onima koji su bili prije njih nedavno. Iskusili su posljedicu stvari svoje, a imaće oni kaznu bolnu.'),
(5142, 59, 16, 'Slični su šejtanu kad rekne čovjeku: "Ne vjeruj." Pa pošto ne vjeruje, rekne: "Uistinu, ja sam slobodan od tebe. Uistinu! Ja se bojim Allaha, Gospodara svjetova."'),
(5143, 59, 17, 'Pa biće obojici kraj što će oba biti u vatri, vječno u njoj, a to je plaća zalimima.'),
(5144, 59, 18, 'O vi koji vjerujete! Bojte se Allaha, i neka gleda duša šta je pripravila za sutra, i bojte se Allaha! Uistinu! Allah je o onom šta radite Obaviješteni.'),
(5145, 59, 19, 'I ne budite kao oni koji su zaboravili Allaha, pa je učinio da oni zaborave duše svoje. Ti takvi su grješnici.'),
(5146, 59, 20, 'Nisu jednaki stanovnici vatre i stanovnici Dženneta: Stanovnici Dženneta - oni su uspješni.'),
(5147, 59, 21, 'Da smo spustili ovaj Kur\'an planini, sigurno bi je vidio poniznu, rascijepljenu iz straha od Allaha. A to su primjeri - navodimo ih ljudima, da bi oni razmislili.'),
(5148, 59, 22, 'On je Allah, sem Kojeg boga nema, Znalac nevidljivog i vidljivog. On je Milostivi, Milosrdni.'),
(5149, 59, 23, 'On je Allah, sem Kojeg boga nema, Vladar, Presveti, Onaj koji daje mir, Onaj koji daje sigurnost, Nadzirući, Silni, Gigantski, Gordi! Slava Allahu od onog šta pridružuju!'),
(5150, 59, 24, 'On je Allah, Stvaralac, Tvorac, Oblikovalac! Njegova imena su najljepša. Slavi Ga šta je u nebesima i Zemlji, a On je Moćni, Mudri.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'O vi koji vjerujete! Ne uzimajte neprijatelja Mog i neprijatelja vašeg, prijateljima! Pružate im ljubav, a zaista ne vjeruju u ono što vam je došlo od Istine, izgone Poslanika i vas, jer vjerujete u Allaha, Gospodara vašeg. Ako ste izašli džihadom na putu Mom i tražeći zadovoljstvo Moje, tajeći prema njima ljubav, a Ja sam Najbolji znalac onog šta tajite i šta objelodanjujete! A ko to čini od vas, pa sigurno je zalutao s pravog puta.'),
(5152, 60, 2, 'Ako naiđu na vas biće vam neprijatelji i ispružiće na vas ruke svoje i jezike svoje sa zlom, i voliće da ne vjerujete.'),
(5153, 60, 3, 'Neće vam koristiti rodbina vaša, niti djeca vaša na Dan kijameta. (On) će vas rastaviti. A Allah je onog što radite Vidilac.'),
(5154, 60, 4, 'Doista je za vas lijep uzor u Ibrahimu i onima s njim, kad su rekli narodu svom: "Uistinu, mi smo čisti od vas i onog šta obožavate mimo Allaha: nemamo ništa s vama i nastalo je između nas i vas neprijateljstvo i mržnja vječna, dok ne budete vjerovali u Allaha, Njega Jedinog - izuzev riječi Ibrahima ocu njegovom: \'Sigurno ću ti tražiti oprost, a ne vladam za tebe od Allaha ništa.\' Gospodaru naš! U Tebe se pouzdavamo i Tebi se obraćamo, a Tebi je dolazište!'),
(5155, 60, 5, 'Gospodaru naš! Ne učini nas iskušenjem za one koji ne vjeruju i oprosti nam. Gospodaru naš! Uistinu, Ti, Ti si Moćni, Mudri"'),
(5156, 60, 6, 'Zaista je za vas u njima uzor lijep - za onog ko se nada Allahu i Danu posljednjem. A ko se okrene - pa uistinu, Allah, On je Neovisni, Hvaljeni.'),
(5157, 60, 7, 'Možda će Allah uspostaviti ljubav između vas i između onih od njih s kojima ste u neprijateljstvu. A Allah je Svemoćan i Allah je Oprosnik, Milosrdni.'),
(5158, 60, 8, 'Allah vam ne zabranjuje prema onima koji se ne bore protiv vas zbog vjere, i ne izgone vas iz domova vaših - da im činite dobro i ispravno postupate prema njima. Uistinu! Allah voli pravedne!'),
(5159, 60, 9, 'Allah vam jedino zabranjuje da prijateljujete s onima koji se bore protiv vas zbog vjere i izgone vas iz domova vaših i pomažu istjerivanju vašem - da prijateljujete s njima. A ko s njima prijateljuje - pa ti takvi su zalimi.'),
(5160, 60, 10, 'O vi koji vjerujete! Kad vam dođu vjernice muhadžirke, tad ih provjerite! Allah je Najbolji znalac imana njihovog. Pa ako ih prepoznate vjernicama, tad ih ne vraćajte nevjernicima. Nisu one njima dozvoljene, niti su oni dozvoljeni njima. I dajte im šta su utrošili. I nije vam grijeh da ih oženite kada im date darove njihove. I ne držite u (bračnoj) vezi nevjernice. I tražite šta ste potrošili, i neka traže šta su potrošili. To je sud Allahov, sudi među vama. A Allah je Znalac, Mudri.'),
(5161, 60, 11, 'Ako vam umakne od žena vaših nešto k nevjernicima, pa budete slijedili, tad dajte onima kojima su otišle žene njihove, slično onom šta su utrošili. I bojte se Allaha, Onog u kojeg ste vi vjernici.'),
(5162, 60, 12, 'O Vjerovjesniče! Kad ti dođu vjernice da ti daju bej\'a da neće pridruživati Allahu ništa, niti krasti, niti bludničiti, niti ubijati djecu svoju, niti iznositi potvoru izmišljajući to između ruku svojih i nogu svojih, niti tebi biti neposlušne u dobru, tad primi njihov bej\'a i moli da im oprosti Allah. Uistinu! Allah je Oprosnik, Milosrdni.'),
(5163, 60, 13, 'O vi koji vjerujete! Ne budite prijatelji ljudi na koje se rasrdio Allah. Doista očajavaju zbog Ahireta, kao što očajavaju nevjernici zbog stanovnika kabura.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Allaha slavi šta je u nebesima i šta je na Zemlji, a On je Moćni, Mudri.'),
(5165, 61, 2, 'O vi koji vjerujete! Zašto govorite ono što ne činite?'),
(5166, 61, 3, 'Velika je odvratnost kod Allaha što govorite ono što ne činite.'),
(5167, 61, 4, 'Uistinu! Allah voli one koji se bore na putu Njegovom u saffu, kao da su oni bedem zbijeni.'),
(5168, 61, 5, 'I kad reče Musa narodu svom: "O narode moj! Zašto me patite, a sigurno znate da sam vam ja poslanik Allahov?" Pa pošto skrenuše, Allah učini da skrenu srca njihova. A Allah ne upućuje narod grješnika.'),
(5169, 61, 6, 'I kad reče Isa, sin Merjemin: "O sinovi Israilovi! Uistinu, ja sam vam poslanik Allahov, potvrđivač za ono šta je bilo prije mene od Tewrata i donosilac radosne vijesti o Poslaniku (koji) dolazi poslije mene, njegovo ime je Ahmed." Pa pošto im je došao s dokazima jasnim, rekoše: "Ovo je sihr očiti."'),
(5170, 61, 7, 'A ko je nepravedniji od onog ko izmišlja protiv Allaha laž, a on se poziva Islamu? A Allah ne upućuje narod zalima.'),
(5171, 61, 8, 'Žele da ugase svjetlo Allahovo ustima svojim, a Allah je Taj koji usavršava svjetlo Svoje, makar mrzili nevjernici.'),
(5172, 61, 9, 'On je Taj koji je poslao Poslanika Svog s Uputom i Vjerom Istine, da bi učinio da ona nadvlada vjeru svaku, makar mrzili mušrici.'),
(5173, 61, 10, 'O vi koji vjerujete! Hoćete li da vam ukažem na trgovinu (koja) će vas spasiti od kazne bolne:'),
(5174, 61, 11, 'Vjerujte u Allaha i Poslanika Njegovog, i borite se na putu Allahovom imecima svojim i dušama svojim. To je bolje za vas, kad biste znali!'),
(5175, 61, 12, 'Oprostiće vam grijehe vaše i uvešće vas u bašče ispod kojih teku rijeke, i stanove ugodne u baščama Adna. To je uspjeh veličanstven!'),
(5176, 61, 13, 'I drugi kojeg volite: pomoć od Allaha i pobjedu blisku. A radosnom viješću obraduj vjernike!'),
(5177, 61, 14, 'O vi koji vjerujete! Budite pomagači Allahu, kao što je rekao Isa, sin Merjemin, hawarijjunima: "Ko su pomagači moji Allahu?" Rekoše hawarijjuni: "Mi smo pomagači Allahovi." Pa je vjerovala skupina sinova Israilovih, a nije vjerovala skupina. Tad smo ojačali one koji vjeruju, protiv neprijatelja njihovih, pa su postali nadmoćni.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Allaha slavi šta je u nebesima i šta je na Zemlji, Vladara, Presvetog, Moćnog, Mudrog!'),
(5179, 62, 2, 'On je Taj koji je podigao među neuke Poslanika između njih - uči im ajete Njegove i čisti ih i poučava ih Knjizi i mudrosti, iako su ranije doista bili u zabludi očitoj -'),
(5180, 62, 3, 'I drugima od njih koji im se još nisu priključili; a On je Moćni, Mudri.'),
(5181, 62, 4, 'To je blagodat Allahova, daje je kome hoće, a Allah je Posjednik blagodati veličanstvene.'),
(5182, 62, 5, 'Primjer onih koji su zaduženi Tewratom, zatim ga se nisu držali, je kao primjer magarca koji nosi tomove knjiga. Loš je primjer ljudi koji poriču ajete Allahove. A Allah ne upućuje ljude zalime.'),
(5183, 62, 6, 'Reci: "O (vi) koji ste Jevreji! Ako tvrdite da ste vi prijatelji Allahovi mimo ljudi, tad zaželite smrt, ako istinu govorite."'),
(5184, 62, 7, 'A ne žele je nikada zbog onog što su unaprijed poslale ruke njihove! A Allah je Znalac zalima.'),
(5185, 62, 8, 'Reci: "Uistinu, smrt od koje bježite - pa sigurno će vas ona sresti, zatim ćete biti vraćeni Znalcu nevidljivog i vidljivog, pa će vas obavijestiti o onom šta ste radili."'),
(5186, 62, 9, 'O vi koji vjerujete! Kad se pozove na salat na dan petak, tad požurite na zikr Allaha i ostavite trgovinu; to je bolje za vas - kad biste znali!'),
(5187, 62, 10, 'Pa kad se završi salat, tad se raširite po zemlji i tražite od blagodati Allahove, i spominjite Allaha mnogo, da biste vi uspjeli.'),
(5188, 62, 11, 'I kad ugledaju trgovinu ili zabavu, raspu se k njoj, a ostave tebe stojeći. Reci: " [ta je kod Allaha, bolje je od zabave i od trgovine. A Allah je Najbolji od opskrbljivača.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Kad ti dođu munafici, kažu: "Svjedočimo da si uistinu ti poslanik Allahov." A Allah zna da si ti uistinu poslanik Njegov i Allah svjedoči da su doista licemjeri lažovi.'),
(5190, 63, 2, 'Uzeli su zakletve svoje štitom, pa odvraćaju od puta Allahovog. Doista je zlo šta oni rade.'),
(5191, 63, 3, 'To zato što su oni vjerovali, zatim nisu vjerovali, pa su zapečaćena srca njihova - zato oni ne shvataju.'),
(5192, 63, 4, 'I kad ih vidiš, zadivljuju te tijela njihova; a ako govore, slušaš govor njihov. Oni su kao balvani poduprti - misle svaki je povik protiv njih. Oni su neprijatelj, pa ih se pripazi. Ubio ih Allah, kako se odvraćaju.'),
(5193, 63, 5, 'I kad im se kaže: "Dođite! Tražiće za vas oprost Poslanik Allahov" - iskrive glave svoje i vidiš ih odvraćaju se, i oni su oholi.'),
(5194, 63, 6, 'Isto je njima, tražio im oprost ili im ne tražio, neće im oprostiti Allah. Uistinu! Allah ne upućuje narod grješnika.'),
(5195, 63, 7, 'Oni su ti koji govore: "Ne trošite na onog ko je kod Poslanika Allahovog, dok se ne raziđu." A Allahove su riznice nebesa i Zemlje, ali munafici ne shvataju.'),
(5196, 63, 8, 'Govore: "Ako se vratimo u Medinu, sigurno će moćniji iz nje protjerati slabijeg!" A Allahova je moć i Poslanika Njegovog i vjernika, ali - munafici ne znaju.'),
(5197, 63, 9, 'O vi koji vjerujete! Neka vas ne odvrate imanja vaša, niti djeca vaša od spominjanja Allaha. A ko to uradi, pa ti takvi su gubitnici.'),
(5198, 63, 10, 'I trošite od onog čime vas opskrbljujemo, prije nego nekom od vas dođe smrt, pa rekne: "Gospodaru moj! Kad bi mi odložio do roka bliskog, pa da dajem sadaku i budem od dobročinitelja."'),
(5199, 63, 11, 'A Allah neće odložiti duši kad dođe rok njen. A Allah je o onom šta radite Obaviješteni.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Allaha slavi šta je u nebesima i šta je na Zemlji. Njegova je vlast i Njegova je hvala, a On nad svakom stvari ima moć.'),
(5201, 64, 2, 'On je Taj koji vas je stvorio, pa je od vas nevjernik i od vas vjernik. A Allah je onog što radite Vidilac.'),
(5202, 64, 3, 'Stvorio je nebesa i Zemlju s Istinom i oblikovao vas i uljepšao likove vaše, a Njemu je dolazište!'),
(5203, 64, 4, 'Zna šta je u nebesima i Zemlji, i zna šta tajite i šta otkrivate; a Allah je Znalac grudi.'),
(5204, 64, 5, 'Zar vam nije stigla vijest o onima koji nisu vjerovali prije, pa su iskusili pogubnost postupka svog, a imaće oni kaznu bolnu.'),
(5205, 64, 6, 'To zato što su im dolazili poslanici njihovi s dokazima jasnim, pa su govorili: "Zar da nas smrtnik upućuje?" Pa nisu vjerovali i okretali su se. A Allah je Neovisani i Allah je Bogati, Hvaljeni!'),
(5206, 64, 7, 'Oni koji ne vjeruju tvrde da neće biti podignuti. Reci: "Svakako, tako mi Gospodara mog, sigurno ćete biti podignuti, zatim sigurno obaviješteni šta ste radili." A to je Allahu lahko.'),
(5207, 64, 8, 'Zato vjerujte u Allaha i Poslanika Njegovog i svjetlo koje smo spustili. A Allah je o onom što radite Obaviješteni.'),
(5208, 64, 9, 'Na Dan kad vas skupi za Dan sastanka, to će biti Dan međusobnog obmanjivanja. A ko vjeruje u Allaha i radi dobro, pokriće mu zla djela njegova i uvešće ga u bašče ispod kojih teku rijeke, vječno će biti u njima, zauvijek. To je uspjeh veličanstven!'),
(5209, 64, 10, 'A oni koji ne vjeruju i poriču ajete Naše, takvi će biti stanovnici vatre, vječno će biti u njoj. A loše je dolazište!'),
(5210, 64, 11, 'Ne pogađa nikakva nesreća, izuzev s dozvolom Allahovom. A ko vjeruje u Allaha - upućuje srce njegovo. A Allah je o svakoj stvari Znalac.'),
(5211, 64, 12, 'I poslušajte Allaha i poslušajte Poslanika! Pa ako se okrenete, ta samo je na Poslaniku Našem dostava jasna.'),
(5212, 64, 13, 'Allah! Samo je On Bog! I u Allaha zato nek se pouzdaju vjernici.'),
(5213, 64, 14, 'O vi koji vjerujete! Uistinu, među ženama vašim i djecom vašom neprijatelja imate, zato ih se pričuvajte. A ako izbrišete i pređete i oprostite, pa doista je Allah Oprosnik, Milosrdni!'),
(5214, 64, 15, 'Bogatstva vaša i djeca vaša samo su iskušenje - a Allah - kod Njega je nagrada veličanstvena.'),
(5215, 64, 16, 'Pa bojte se Allaha koliko možete, i slušajte i pokorite se i udjeljujte, bolje je za duše vaše. A ko spasi pohlepe dušu svoju - pa ti takvi su od uspjelih.'),
(5216, 64, 17, 'Ako zajmite Allahu zajam lijep, udvostručiće vam ga i oprostiće vam. A Allah je Zahvalni, Blagi,'),
(5217, 64, 18, 'Znalac nevidljivog i vidljivog, Moćni, Mudri!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'O Vjerovjesniče! Kad budete razvodili žene, tad ih razvodite u iddetu njihovom, i računajte iddet, i bojte se Allaha, Gospodara vašeg. Ne istjerujte ih iz kuća njihovih, i neka ne izlaze, izuzev ako su počinile razvrat otvoreni. A to su granice Allahove. A ko pređe granice Allahove, pa doista je učinio zulm duši svojoj. Ne znaš: možda Allah daje da se desi poslije toga (nova) stvar.'),
(5219, 65, 2, 'Pa kad dospiju roku svom, tad ih zadržite uljudno ili se rastavite od njih uljudno. I pozovite da svjedoče dvojica pravedna između vas i obavite svjedočenje Allahu. To je ono što se savjetuje onom ko vjeruje u Allaha i Dan posljednji. A ko se boji Allaha - (On) će mu načiniti izlaz,'),
(5220, 65, 3, 'I opskrbiće ga otkud ne računa. A ko se uzda u Allaha - pa On mu je dovoljan. Uistinu, Allah je Taj koji ostvaruje naredbu Svoju. Zaista je načinio Allah svakoj stvari mjeru.'),
(5221, 65, 4, 'A one od žena vaših koje su izgubile nadu u menstruaciju, ako ste posumnjali - pa iddet njihov je tri mjeseca, i onima koje ne menstruiraju. A koje su noseće - rok njihov je da rode breme svoje. A ko se boji Allaha, naćiniće mu po odredbi Svojoj lahkoću.'),
(5222, 65, 5, 'To je naredba Allahova, objavio ju je vama. A ko se boji Allaha, pokriće mu zla djela njegova i uvećaće mu nagradu.'),
(5223, 65, 6, 'Nastanite ih tamo gdje stanujete prema sredstvima vašim, i ne štetite im da biste ih pritijesnili. Ako takve budu noseće - tad trošite na njih dok ne rode breme svoje. Pa ako vam doje (djecu), tad im dajte darove njihove i dogovarajte se međusobno uljudno. A ako se ne složite, tad će mu zadajati druga.'),
(5224, 65, 7, 'Neka troši imalac obilja od obilja svog, a onaj kome je uskraćena opskrba njegova - pa neka troši od onog šta mu je dao Allah. Ne opterećuje Allah dušu, izuzev onim šta joj je dao. Učiniće Allah poslije teškoće olakšanje.'),
(5225, 65, 8, 'I koliko se gradova oglušilo prema naredbi Gospodara njihovog i poslanika Njegovih, pa smo ih obračunali obračunom žestokim i kaznili ih kaznom strašnom.'),
(5226, 65, 9, 'Tad su iskusili pogubnost postupka svog. A rezultat postupka njihovog bio je gubitak.'),
(5227, 65, 10, 'Pripremio je Allah za njih kaznu žestoku. Pa bojte se Allaha, o imaoci razuma koji vjerujete! Doista vam je Allah poslao Opomenu,'),
(5228, 65, 11, 'Poslanika - uči vam ajete Allahove jasne, da izvede one koji vjeruju i rade dobra djela iz tmina na svjetlost. A ko vjeruje u Allaha i radi dobro, uvešće ga u bašče ispod kojih teku rijeke, vječno će biti u njima, zauvijek. Doista je Allah uljepšao njegovu opskrbu.'),
(5229, 65, 12, 'Allah je Taj koji je stvorio sedam nebesa, a od Zemlje, slično njima. Spušta se naredba među njih, da znate, da Allah nad svakom stvari ima moć, i da je Allah zaista svaku stvar obuhvatio znanjem.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'O Vjerovjesniče! Zašto zabranjuješ ono što ti je Allah dopustio, tražeći zadovoljstva žena svojih? A Allah je Oprosnik, Milosrdni.'),
(5231, 66, 2, 'Već vam je Allah naložio iskup zakletvi vaših, a Allah je Zaštitnik vaš i On je Znalac, Mudri.'),
(5232, 66, 3, 'I kad je Vjerovjesnik povjerio jednoj od svojih žena vijest, pa pošto je obavijestila o njoj - a otkrio to njemu Allah - on je iznio dio njen, a prešutio dio. Pa pošto je nju obavijestio o tome, rekla je: "Ko te je obavijestio o ovome?" Reče: "Obavijestio me je Znalac, Obaviješteni."'),
(5233, 66, 4, 'Ako se vas dvije pokajete Allahu, pa doista su nagela srca vaša; a ako se budete pomagale protiv njega - pa uistinu, Allah - On je Zaštitnik njegov, i Džibril i pravedni vjernici; i meleci su sem toga pomagač.'),
(5234, 66, 5, 'Možda će Gospodar njegov, ako vas razvede, da mu zamijeni boljih žena od vas: predanih, vjernica, poslušnih, pokajanica, obožavateljki, onih koje poste, hudovica i djevica.'),
(5235, 66, 6, 'O vi koji vjerujete! Zaštitite duše vaše i porodice svoje vatre čije će gorivo biti ljudi i kamenje. Nad njom su meleci grubi, snažni, ne opiru se Allahu šta im naredi, i čine šta im se naređuje.'),
(5236, 66, 7, 'O vi koji niste vjerovali! Ne pravdajte se Danas! Bićete plaćeni jedino za ono šta ste radili.'),
(5237, 66, 8, 'O vi koji vjerujete! Pokajte se Allahu kajanjem iskrenim. Možda će Gospodar vaš da odstrani od vas zla djela vaša i uvede vas u bašče ispod kojih teku rijeke na Dan kad Allah neće poniziti Vjerovjesnika i one koji su s njim vjerovali; svjetlo njihovo će juriti ispred njih i s desnih strana njihovih. Govoriće: "Gospodaru naš. Upotpuni nam svjetlo naše i oprosti nam. Uistinu, Ti nad svakom stvari imaš moć!"'),
(5238, 66, 9, 'O Vjerovjesniče! Bori se protiv nevjernika i munafika, i strogo prema njima. A sklonište njihovo biće Džehennem, a loše je dolazište!'),
(5239, 66, 10, 'Allah navodi primjer za one koji ne vjeruju: ženu Nuhovu i ženu Lutovu. Bile su pod dvojicom robova, od robova Naših pravednih, pa su ih prevarile. Pa neće im njih dvojica koristiti od Allaha ništa, i biće rečeno: "Uđite obje u vatru sa onima koji ulaze."'),
(5240, 66, 11, 'I navodi Allah primjer za one koji vjeruju, ženu faraonovu, kad reče: "Gospodaru moj! Sagradi mi kod Sebe kuću u Džennetu i spasi me od faraona i djela njegovog i spasi me od naroda zalima."'),
(5241, 66, 12, 'I Merjem, kćer Imranovu, koja je sačuvala ferdž svoj, pa smo u njega udahli od Duha Našeg, i vjerovala je u riječi Gospodara svog i knjige Njegove, i bila od pokornih.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Blagoslovljen neka je Onaj u ruci čijoj je vlast - a On nad svakom stvari ima moć -'),
(5243, 67, 2, 'Koji je stvorio smrt i život, da vas iskuša koji je od vas bolji djelom. A On je Moćni, Oprosnik -'),
(5244, 67, 3, 'Koji je stvorio sedam nebesa slojevito. Ne vidiš u stvaranju Milostivog nikakvog nesklada. Pa povrati pogled, da li vidiš ikakve napukline?'),
(5245, 67, 4, 'Ponovo vrati pogled drugi put, povratiće ti se pogled oslabljen i biće on pomućen.'),
(5246, 67, 5, 'I zaista smo ukrasili nebo najbliže svjetiljkama i učinili ih projektilima za šejtane, a pripremili smo im kaznu seira.'),
(5247, 67, 6, 'A imaće oni koji ne vjeruju u Gospodara svog, kaznu Džehennema, a loše je dolazište!'),
(5248, 67, 7, 'Kad budu bačeni u njega, ćut će ga, hroptaće i on će vriti.'),
(5249, 67, 8, 'Skoro da prsne od srdžbe. Kad god se u njega baci skupina, upitaće ih čuvari njegovi: "Zar vam nije dolazio opominjač?"'),
(5250, 67, 9, 'Reći će: "Svakako; diosta nam je dolazio opominjač, pa smo porekli i rekli: \'Nije Allah objavio ništa, vi ste samo u zabludi velikoj!"'),
(5251, 67, 10, 'I reći će: "Da smo slušali ili shvatali, ne bismo bili među stanovnicima seira."'),
(5252, 67, 11, 'Tad će priznati grijeh svoj. Pa daleko bili stanovnici seira.'),
(5253, 67, 12, 'Uistinu! Oni koji se boje Gospodara svog nevidljivo, imaće oni oprost i nagradu veliku.'),
(5254, 67, 13, 'I tajili vi govor vaš ili ga razglašavali, uistinu, On je Znalac onog šta je u grudima.'),
(5255, 67, 14, 'Zar ne zna Onaj koji je stvorio? A On je Fini, Obaviješteni.'),
(5256, 67, 15, 'On je Taj koji vam je Zemlju učinio podložnom, pa hodajte predjelima njenim i jedite od opskrbe Njegove, a na Njemu je proživljenje.'),
(5257, 67, 16, 'Jeste li sigurni od Onog ko je u nebu, da neće dati da vas proguta zemlja, tad kad se ona zatalasa?'),
(5258, 67, 17, 'Ili ste sigurni od Onog ko je u nebu, da ne pošalje na vas oluju? Pa saznaćete kakva je opomena Moja!'),
(5259, 67, 18, 'I zaista su poricali oni koji su bili prije njih, pa kakvo je bilo odbijanje Moje!'),
(5260, 67, 19, 'Zar ne vide ptice iznad sebe - šire krila i skupljaju ih? Drži ih jedino Milostivi. Uistinu, On je svake stvari Vidilac.'),
(5261, 67, 20, 'Ili, ko je taj ko je vojska vaša, da vam pomogne mimo Milostivog? Nevjernici su samo u iluziji.'),
(5262, 67, 21, 'Ili, ko je taj ko će vas opskrbiti, ako zadrži opskrbu Svoju? Naprotiv, ustrajavaju u prevršivanju i izbjegavanju.'),
(5263, 67, 22, 'Pa da li je ko ide ničice na licu svom upućeniji, ili ko ide pravo na putu pravom?'),
(5264, 67, 23, 'Reci: "On je Taj koji vas je stvorio, i načinio vam sluh i vidove i srca. Malo što zahvaljujete."'),
(5265, 67, 24, 'Reci: "On je Taj koji vas je umnožio na Zemlji, a Njemu ćete biti sabrani."'),
(5266, 67, 25, 'I govore: "Kad će ta prijetnja, ako istinu govorite?"'),
(5267, 67, 26, 'Reci: "Znanje je jedino kod Allaha, a ja sam samo opominjač jasni."'),
(5268, 67, 27, 'Pa pošto ga ugledaju blizu, rastužiće se lica onih koji ne vjeruju, i biće rečeno: "Ovo je to šta ste prizivali."'),
(5269, 67, 28, 'Reci: "Vidite li? Ako uništi Allah mene i onog ko je sa mnom, ili nam se smiluje - pa ko će zaštititi nevjernike od kazne bolne?"'),
(5270, 67, 29, 'Reci: "On je Milostivi. U Njega vjerujemo i u Njega se pouzdavamo. Pa saznaćete ko je taj ko je u zabludi očitoj."'),
(5271, 67, 30, 'Reci: "Vidite li? Ako voda vaša osvane porinuta, pa ko će vam donijeti vodu tekuću?"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nun. Tako mi pera i onog što pišu!'),
(5273, 68, 2, 'Ti nisi milošću Gospodara svoga lud.'),
(5274, 68, 3, 'A uistinu, ti ćeš imati nagradu neprekidnu,'),
(5275, 68, 4, 'I uistinu, ti si naravi uzvišene, divne.'),
(5276, 68, 5, 'Pa vidjećeš (Muhammede) i vidjeće,'),
(5277, 68, 6, 'Koji je od vas zaluđen.'),
(5278, 68, 7, 'Doista, Gospodar tvoj - On je Najbolji znalac onog ko je zalutao s puta Njegovog i On je Najbolji znalac upućenih.'),
(5279, 68, 8, 'Zato ne slušaj poricatelje -'),
(5280, 68, 9, 'Voljeli bi da popustiš, pa bi popustili -'),
(5281, 68, 10, 'I ne slušaj svakog zakletnika prezrenog,'),
(5282, 68, 11, 'Klevetnika, koji hoda s potvorom,'),
(5283, 68, 12, 'Sprečavatelja dobrog, prestupnika, grješnika,'),
(5284, 68, 13, 'Nasilnika, sem toga kopileta,'),
(5285, 68, 14, 'Jer je imalac bogatstva i sinova.'),
(5286, 68, 15, 'Kad mu se uče ajeti Naši, rekne: "Priče ranijih!"'),
(5287, 68, 16, 'Obilježićemo ga na surli!'),
(5288, 68, 17, 'Uistinu! Mi smo ih iskušavali kao što smo iskušavali vlasnike bašče, kad su se zakleli da će je sigurno ujutro obrati:'),
(5289, 68, 18, 'A ne zatražiše izuzeće,'),
(5290, 68, 19, 'Pa je obiđe taifun od Gospodara tvog - a oni su bili usnuli -'),
(5291, 68, 20, 'Pa osvanu kao posječena.'),
(5292, 68, 21, 'Tad su se jutrom dozivali:'),
(5293, 68, 22, '"Poranite na nasad svoj, ako ste berači."'),
(5294, 68, 23, 'Pa su otišli i oni su šaputali:'),
(5295, 68, 24, '"Neće vam danas u nju nikako ući siromah",'),
(5296, 68, 25, 'I poraniše za sprječavanje kadri.'),
(5297, 68, 26, 'Pa pošto je vidješe, rekoše: "Doista, mi smo zalutali.'),
(5298, 68, 27, 'Naprotiv! Mi smo lišeni!"'),
(5299, 68, 28, 'Reče srednji njihov: "Zar vam nisam rekao zašto ne slavite?"'),
(5300, 68, 29, 'Rekoše: "Slava Gospodaru našem, uistinu, mi smo bili zalimi."'),
(5301, 68, 30, 'Pa oni priđoše jedan drugom, koreći se.'),
(5302, 68, 31, 'Rekoše: "O teško nama! Uistinu, mi smo bili oni koji prevršuju.'),
(5303, 68, 32, 'Možda će Gospodar naš da nam zamijeni bolju od ove. Uistinu, mi Gospodaru svom žudimo."'),
(5304, 68, 33, 'Takva je kazna. A doista je kazna Ahireta veća - kad bi znali!'),
(5305, 68, 34, 'Uistinu, bogobojazni će imati kod Gospodara svog bašče zadovoljstva.'),
(5306, 68, 35, 'Pa hoćemo li tretirati muslimane kao prestupnike?'),
(5307, 68, 36, '[ta vam je? Kako prosuđujete?'),
(5308, 68, 37, 'Ili, imate li vi knjigu u kojoj učite,'),
(5309, 68, 38, 'Da ćete vi uistinu imati u njoj šta odaberete?'),
(5310, 68, 39, 'Ili, imate li vi zakletve od Nas dalekosežne - do Dana kijameta, da ćete uistinu imati vi šta dosudite?'),
(5311, 68, 40, 'Pitaj ih, koji od njih je tome jamac!'),
(5312, 68, 41, 'Ili imaju oni ortake? Pa neka dovedu ortake svoje, ako istinu govore.'),
(5313, 68, 42, 'Na Dan kad se otkrije potkoljenica, i pozovu na sedžde - pa ne mognu,'),
(5314, 68, 43, 'Oboreni biće pogledi njihovi, prekriće ih poniženje; a doista su oni bili pozivani na sedžde i bili su oni zdravi.'),
(5315, 68, 44, 'Pa ostavi Mene i onog ko poriče ovaj hadis Približavaćemo ih postepeno, odakle ne znaju.'),
(5316, 68, 45, 'I pustiću im, uistinu, spletka Moja je jaka.'),
(5317, 68, 46, 'Zar njih pitaš za nagradu? - pa su oni globom opterećeni!'),
(5318, 68, 47, 'Ili je kod njih nevidljivo, pa oni pišu?'),
(5319, 68, 48, 'Pa strpi se radi presude Gospodara tvog, i ne budi kao saputnik (velike) ribe, kad je povikao, a on je bio očajan.'),
(5320, 68, 49, 'I da ga nije stigla blagodat od Gospodara njegovog, doista bi bio izbačen na pusto mjesto i on bi bio prekoren.'),
(5321, 68, 50, 'Pa ga je odabrao Gospodar njegov te ga učinio (jednim) od pravednih.'),
(5322, 68, 51, 'I gotovo da oni koji ne vjeruju učine pogledima svojim da ti poklizneš pošto su čuli Opomenu, i govore: "Doista je on luđak."'),
(5323, 68, 52, 'A on je samo Opomena svjetovima.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Istinitost!'),
(5325, 69, 2, 'Šta je istinitost?'),
(5326, 69, 3, 'I šta znaš ti šta je istinitost!'),
(5327, 69, 4, 'Poricao je Semud i Ad udar.'),
(5328, 69, 5, 'Pa što se tiče Semuda, pa uništeni su tagijetom,'),
(5329, 69, 6, 'A što se tiče Ada, pa uništeni su vjetrom žestokim, silovitim,'),
(5330, 69, 7, 'Nametnuo ga je na njih sedam noći i osam dana uzastopno, pa si vidio ljude u njemu oborene, kao da su oni debla datulina prazna.'),
(5331, 69, 8, 'Pa da li vidiš od njih ikakav ostatak?'),
(5332, 69, 9, 'I došao je faraon i onaj ko je bio prije njega, i Mu\'tefikat sa greškom.'),
(5333, 69, 10, 'Pa nisu poslušali poslanika Gospodara svog, pa ih je dograbio grabljenjem žestokim.'),
(5334, 69, 11, 'Uistinu, Mi smo - pošto je porasla voda - nosili vas u lađi,'),
(5335, 69, 12, 'Da bismo vam to učinili podsjećanjem i (da) to zadrži uho koje pamti.'),
(5336, 69, 13, 'Pa kad se puhne u sur, puhanjem jednim:'),
(5337, 69, 14, 'I ponese se Zemlja i planine, pa se zdrobe drobljenjem jednim,'),
(5338, 69, 15, 'Tad, Tog dana, dogodiće se događaj,'),
(5339, 69, 16, 'I raspuknuće nebo, pa će ono Tog dana krhko biti:'),
(5340, 69, 17, 'I meleci će na krajevima njegovim biti, a nosiće Arš Gospodara tvoga nad njima Tog dana osmerica.'),
(5341, 69, 18, 'Tog dana bićete izloženi; neće se sakriti vaša tajna.'),
(5342, 69, 19, 'Pa kome se da knjiga njegova u desnicu njegovu, tad će reći: "Evo vam, čitajte knjigu moju!'),
(5343, 69, 20, 'Uistinu, ja sam znao da ću ja sresti obračun svoj."'),
(5344, 69, 21, 'Pa on će biti u životu zadovoljstva,'),
(5345, 69, 22, 'U Džennetu visokom,'),
(5346, 69, 23, 'Plodovi njegovi biće blizu.'),
(5347, 69, 24, '"Jedite i pijte prijatno, zbog onog što ste činili u danima prošlim."'),
(5348, 69, 25, 'A kome se da knjiga njegova u ljevicu njegovu, tad će reći: "O da mi nije data knjiga moja,'),
(5349, 69, 26, 'I da nisam saznao šta je obračun moj!'),
(5350, 69, 27, 'O da je ona bila kraj,'),
(5351, 69, 28, 'Nije mi koristilo bogatstvo moje,'),
(5352, 69, 29, 'Propala je moć moja."'),
(5353, 69, 30, '"Ščepajte ga, pa ga okujte,'),
(5354, 69, 31, 'Zatim džehimom pržite ga,'),
(5355, 69, 32, 'Potom (stavite) u lanac čija je dužina sedamdeset lakata, pa ga uvežite."'),
(5356, 69, 33, 'Uistinu, on nije vjerovao u Allaha Veličanstvenog,'),
(5357, 69, 34, 'Niti podsticao hranjenju siromaha.'),
(5358, 69, 35, 'Zato on Danas ovdje nema prisnog prijatelja,'),
(5359, 69, 36, 'Niti hrane, izuzev od splačina,'),
(5360, 69, 37, 'Ješće je samo oni koji su činili greške!'),
(5361, 69, 38, 'Pa ne! Kunem se onim što vidite,'),
(5362, 69, 39, 'I onim što ne vidite,'),
(5363, 69, 40, 'Uistinu je on govor Poslanika plemenitog,'),
(5364, 69, 41, 'I nije on govor pjesnika; malo što vjerujete!'),
(5365, 69, 42, 'Niti je govor proroka, malo čega se sjećate!'),
(5366, 69, 43, 'Objava je od Gospodara svjetova.'),
(5367, 69, 44, 'A da je slagao na Nas neke riječi,'),
(5368, 69, 45, 'Doista bismo ga zgrabili za desnicu,'),
(5369, 69, 46, 'Potom doista presjekli njegovu aortu,'),
(5370, 69, 47, 'Pa ne biste bili njegovi branitelji, nijedan od vas.'),
(5371, 69, 48, 'I uistinu, on je bogobojaznima Opomena,'),
(5372, 69, 49, 'A uistinu, Mi znamo da su od vas poricatelji.'),
(5373, 69, 50, 'I uistinu, on je žalost za nevjernike,'),
(5374, 69, 51, 'I uistinu, on je Istina sigurna.'),
(5375, 69, 52, 'Zato slavi ime Gospodara svog, Veličanstvenog!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Upitao je pitalac o kazni koja će se dogoditi,'),
(5377, 70, 2, 'Nevjernicima - nema tog ko će je odbiti -'),
(5378, 70, 3, 'Od Allaha, Vlasnika usponišta,'),
(5379, 70, 4, 'Penju se meleci i Duh Njemu, u danu čija je mjera pedeset hiljada godina.'),
(5380, 70, 5, 'Zato se strpi strpljenjem lijepim.'),
(5381, 70, 6, 'Uistinu! Oni ga vide dalekim,'),
(5382, 70, 7, 'A vidimo ga bliskim.'),
(5383, 70, 8, 'Na Dan kad nebo bude kao rastalina,'),
(5384, 70, 9, 'I brda budu kao vuna obojena,'),
(5385, 70, 10, 'I ne bude pitao bližnji bližnjega,'),
(5386, 70, 11, '(A) vidjeće ih - voliće krivac da se iskupi od kazne Tog dana sinovima svojim,'),
(5387, 70, 12, 'I ženom svojom i bratom svojim,'),
(5388, 70, 13, 'I porodicom svojom koja ga je štitila,'),
(5389, 70, 14, 'I onim ko je na Zemlji, svima, zatim da ga spasi.'),
(5390, 70, 15, 'Nikako! Uistinu, ona će biti buktinja,'),
(5391, 70, 16, 'Trgajući, da bi pržila.'),
(5392, 70, 17, 'Pozivaće onog ko je bježao i okretao se,'),
(5393, 70, 18, 'I skupljao pa zadržavao.'),
(5394, 70, 19, 'Uistinu, čovjek je stvoren nestrpljivim,'),
(5395, 70, 20, 'Kad ga takne zlo, razdražljiv je,'),
(5396, 70, 21, 'A kad ga takne dobro, odbojan je,'),
(5397, 70, 22, 'Osim musallija,'),
(5398, 70, 23, 'Onih koji su u salatu svom stalni,'),
(5399, 70, 24, 'I onih u imecima čijim je pravo poznato,'),
(5400, 70, 25, 'Za prosjaka i lišenog,'),
(5401, 70, 26, 'I onih koji vjeruju u Dan sudnji,'),
(5402, 70, 27, 'I onih koji od kazne Gospodara svog strahuju -'),
(5403, 70, 28, 'Uistinu! Kazna Gospodara njihovog je (stvar) od koje nema sigurnog -'),
(5404, 70, 29, 'I onih koji su ferdžova svojih čuvari,'),
(5405, 70, 30, 'Izuzev od žena svojih ili onog šta posjeduju desnice njihove - pa uistinu, oni neće biti prekoreni;'),
(5406, 70, 31, 'Pa ko traži iza toga, pa ti takvi su prestupnici;'),
(5407, 70, 32, 'I onih koji su prema emanetima svojim i zavjetu svom čuvari,'),
(5408, 70, 33, 'I onih koji uz svjedočenja svoja stoje,'),
(5409, 70, 34, 'I onih koji salat svoj čuvaju -'),
(5410, 70, 35, 'Takvi će u baščama biti počašćeni.'),
(5411, 70, 36, 'Pa šta je onima koji ne vjeruju? - prema tebi žure,'),
(5412, 70, 37, 'Zdesna i slijeva u grupama.'),
(5413, 70, 38, 'Želi li svaki čovjek od njih da mu bude dato da uđe u Džennet zadovoljstva?'),
(5414, 70, 39, 'Nikako! Uistinu, Mi smo ih stvorili, od čega - znaju.'),
(5415, 70, 40, 'Pa ne! Kunem se Gospodarom istoka i zapada, uistinu, Mi smo kadri,'),
(5416, 70, 41, 'Da (ih) zamijenimo boljim od njih, i nećemo Mi biti pretečeni.'),
(5417, 70, 42, 'Zato ih pusti (da) se udube i zabavljaju, dok ne sretnu dan svoj kojim im se prijeti.'),
(5418, 70, 43, 'Na Dan kad iz kabura izađu žurno, kao da oni statui hrle,'),
(5419, 70, 44, 'Oborenih pogleda svojih, prekrivaće ih poniženje. To će biti Dan kojim im se prijeti.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Uistinu, Mi smo poslali Nuha narodu njegovom: "Upozori narod svoj, prije nego im dođe kazna bolna."'),
(5421, 71, 2, 'Reče: "O narode moj! Uistinu! Ja sam vam opominjač jasni,'),
(5422, 71, 3, 'Obožavajte Allaha i bojte Ga se i poslušajte mene,'),
(5423, 71, 4, 'Oprostiće vam neke grijehe vaše i odložiće vam do roka utvrđenog. Uistinu, rok Allahov kad dođe, ne odlaže se, kad biste znali!"'),
(5424, 71, 5, 'Reče: "Gospodaru moj! Doista sam ja pozivao narod svoj noću i danju,'),
(5425, 71, 6, 'Pa im je moje pozivanje samo povećalo izbjegavanje;'),
(5426, 71, 7, 'I uistinu, kad god sam ih ja pozivao da im oprostiš, stavljali su prste svoje u uši svoje i pokrivali se odjećom svojom, i ustrajali i uznosili se ohološću.'),
(5427, 71, 8, 'Zatim sam ih ja uistinu pozivao glasno,'),
(5428, 71, 9, 'Potom sam im ja doista razglašavao i povjeravao im tajno,'),
(5429, 71, 10, 'Pa govorio: \'Tražite oprost Gospodara svog! Uistinu, On je Oprosnik.'),
(5430, 71, 11, 'Poslaće na vas nebo lijuće,'),
(5431, 71, 12, 'I pomoći vas imecima i sinovima, i načiniće vam bašče i načiniće vam rijeke.'),
(5432, 71, 13, 'Šta vam je? Ne nadate se od Allaha dostojanstvenosti,'),
(5433, 71, 14, 'A uistinu, stvorio vas je u fazama.'),
(5434, 71, 15, 'Zar ne vidite kako je Allah stvorio sedam nebesa slojevito?'),
(5435, 71, 16, 'I učinio Mjesec u njima svjetlom, a učinio Sunce svjetiljkom.'),
(5436, 71, 17, 'A Allah vas razvija od zemlje izrastanjem,'),
(5437, 71, 18, 'Zatim vas vraća u nju i izvešće vas (novim) izvođenjem.'),
(5438, 71, 19, 'I učinio vam je Allah zemlju sagom,'),
(5439, 71, 20, 'Da vi stupate po njoj putevima širokim."'),
(5440, 71, 21, 'Nuh reče: "Gospodaru moj! Doista me oni nisu poslušali i slijedili su onog čiji mu imetak i njegovo dijete ne povećava - izuzev gubitak."'),
(5441, 71, 22, 'I spleli su spletku veliku,'),
(5442, 71, 23, 'Pa rekli: "Ne napuštajte nipošto bogove svoje, i ne napuštajte nipošto Wedda, niti Suwaa, niti Jegusa i Jeuka i Nesra."'),
(5443, 71, 24, 'I zaista su zaveli mnoge, a povećaj zalimima jedino zabludu.'),
(5444, 71, 25, 'Zbog grešaka njihovih su potopljeni, pa će biti u vatru uvedeni. Pa nisu našli sebi mimo Allaha pomagače.'),
(5445, 71, 26, 'I reče Nuh: "Gospodaru moj! Ne ostavljaj na Zemlji (nijednog) ukućanina od nevjernika.'),
(5446, 71, 27, 'Uistinu, Ti ako ih ostaviš, zavešće robove Tvoje i rađati samo razvratnika, nevjernika.'),
(5447, 71, 28, 'Gospodaru moj! Oprosti meni i roditeljima mojim, i ko uđe u kuću moju vjernikom, i vjernicima i vjernicama, a povećaj zalimima samo propast."');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Reci: "Objavljeno mi je da je prisluškivala grupa džinna, pa su rekli: \'Uistinu, mi smo čuli Kur\'an zadivljujući,'),
(5449, 72, 2, 'Upućuje ispravnosti, pa smo povjerovali u njega, i nećemo pridruživati Gospodaru našem nikoga.'),
(5450, 72, 3, 'I da On - uzvišeno neka je dostojanstvo Gospodara našeg - nije uzeo ženu, niti dijete,'),
(5451, 72, 4, 'I da je budala naša govorila o Allahu pretjeranost,'),
(5452, 72, 5, 'I da smo mi mislili da ljudi i džinni neće o Allahu govoriti laž,'),
(5453, 72, 6, 'I da je bilo individua od ljudi (koji) su tražili zaštitu individua od džinna, pa su im povećali obijest.'),
(5454, 72, 7, 'I da su oni mislili - kao što mislite - da neće Allah podići nikoga,'),
(5455, 72, 8, 'I da smo mi dotakli nebo, pa ga našli ispunjenog čuvarima žestokim i svjetlicama,'),
(5456, 72, 9, 'I da smo mi sjedili na njegovim mjestima za prisluškivanje. Pa ko sad prisluškuje, nalazi sebi svjetlicu vrebajuću.'),
(5457, 72, 10, 'I da mi ne znamo da li se zlo želi onom ko je na Zemlji ili im želi Gospodar njihov ispravnost,'),
(5458, 72, 11, 'I da od nas (ima) pravednih, i od nas mimo toga - različitih tarikata -'),
(5459, 72, 12, 'I da mi znamo da nećemo umaći Allahu u Zemlji, i nećemo Mu umaći bježanjem,'),
(5460, 72, 13, 'I da mi, pošto smo čuli Uputu, vjerujemo u nju - pa ko vjeruje u Gospodara svog, tad se ne boji gubitka, niti nasilja.'),
(5461, 72, 14, 'I da od nas (ima) muslimana i od nas nepravičnih. Pa ko se predao, pa takvi su se upravili ispravnosti.'),
(5462, 72, 15, 'A oni koji su nepravični - pa biće za Džehennem drvo",'),
(5463, 72, 16, 'A kad bi ustrajali na tarikatu - sigurno bismo ih napojili vodom obilnom,'),
(5464, 72, 17, 'Da ih kušamo u tome. A ko se odvraća od sjećanja Gospodara svog, uvući će ga kazni strogoj,'),
(5465, 72, 18, 'I da su mesdžidi za Allaha, zato ne prizivajte uz Allaha nikoga,'),
(5466, 72, 19, 'I da su - pošto je ustao rob Allahov prizivajući Ga - bili gotovo stiskani nad njim.'),
(5467, 72, 20, 'Reci: "Samo prizivam Gospodara svog i ne pridružujem Mu nikoga."'),
(5468, 72, 21, 'Reci: "Uistinu, ja ne vladam za vas štetom, niti ispravnošću."'),
(5469, 72, 22, 'Reci: "Uistinu, mene od Allaha neće zaštititi niko, niti ću naći mimo Njega utočište,'),
(5470, 72, 23, 'Sem dostave od Allaha i poslanica Njegovih; a ko ne posluša Allaha i Poslanika Njegovog - pa uistinu će on imati vatru Džehennema, vječno će biti u njoj, zauvijek."'),
(5471, 72, 24, 'Dok, kad vide ono čime im se prijetilo, tad će saznati ko je slabiji pomagačem i manji brojčano.'),
(5472, 72, 25, 'Reci: "Ne znam da li je blisko ono čime vam se prijeti ili će za njega Gospodar moj načiniti termin",'),
(5473, 72, 26, 'Znalac nevidljivog - pa ne otkriva nevidljivo Svoje nikome,'),
(5474, 72, 27, 'Osim poslaniku s kojim je zadovoljan, pa uistinu On daje da idu ispred njega i iza njega motritelji,'),
(5475, 72, 28, 'Da bi znao da su zaista dostavili poslanice Gospodara svog, i obuhvatio šta je kod njih i utvrdio svaku stvar brojčano.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'O umotani!'),
(5477, 73, 2, 'Ustaj noću, izuzev malo,'),
(5478, 73, 3, 'Polovinu njenu, ili umanji od nje malo,'),
(5479, 73, 4, 'Ili joj dodaj, i uči Kur\'an tertilom.'),
(5480, 73, 5, 'Uistinu, Mi ćemo ti objavljivati govor težak.'),
(5481, 73, 6, 'Uistinu, podizanje noću - ono je jačeg gaza i ispravnijeg izgovora.'),
(5482, 73, 7, 'Uistinu, tebi je danju posao dug.'),
(5483, 73, 8, 'I spominji ime Gospodara svog i posveti Mu se (punom) posvećenošću.'),
(5484, 73, 9, 'Gospodar Istoka i Zapada! Nema boga osim Njega, pa uzmi Njega zaštitnikom.'),
(5485, 73, 10, 'I otrpi šta govore i napusti ih napuštanjem lijepim,'),
(5486, 73, 11, 'A ostavi Mene i poricatelje, posjednike raskoši, i odgodi im malo.'),
(5487, 73, 12, 'Uistinu, kod Nas su okovi i džehim,'),
(5488, 73, 13, 'I hrana koja guši i kazna bolna:'),
(5489, 73, 14, 'Na Dan kad se zatrese Zemlja i planine, i budu planine pješčana hrpa sasuta.'),
(5490, 73, 15, 'Uistinu, Mi smo vam poslali Poslanika, svjedokom protiv vas, kao što smo faraonu poslali poslanika.'),
(5491, 73, 16, 'Pa nije faraon poslušao poslanika, te smo ga zgrabili grabljenjem kobnim.'),
(5492, 73, 17, 'Pa kako ćete se zaštititi, ako ne vjerujete, Dana (koji) će učiniti djecu sijedom?'),
(5493, 73, 18, 'Nebo će tad biti rascijepljeno. Obećanje Njegovo biće ispunjeno.'),
(5494, 73, 19, 'Uistinu! Ovo je Opomena, pa ko htjedne, uzeće Gospodaru svom put.'),
(5495, 73, 20, 'Uistinu! Gospodar tvoj zna da ti bdiješ blizu dvije trećine noći i polovinu njenu i trećinu njenu, i grupa onih koji su s tobom; a Allah odmjerava noć i dan. Znao je da to ne možete, pa vam je oprostio. Zato učite šta je lahko iz Kur\'ana. Znao je da će među vama biti bolesnih i drugih: putuju po zemlji, traže od blagodati Allahove, i drugih: bore se na putu Allahovom. Pa učite šta je lahko iz njega! I obavljajte salat i dajite zekat i uzajmite Allahu zajam lijep; a šta unaprijed pošaljete za duše svoje od dobrog, naći ćete ga kod Allaha; to je najbolje i najveličanstvenije nagrade! I tražite oprost od Allaha! Uistinu, Allah je Oprosnik, Milosrdni.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'O pokriveni!'),
(5497, 74, 2, 'Ustani pa upozoravaj!'),
(5498, 74, 3, 'I Gospodara svog tad veličaj!'),
(5499, 74, 4, 'I odjeću svoju tad očisti!'),
(5500, 74, 5, 'I prljavštine se tad kloni!'),
(5501, 74, 6, 'I ne prigovaraj da je mnogo!'),
(5502, 74, 7, 'I radi Gospodara tvog se tad strpi!'),
(5503, 74, 8, 'Pa kad se puhne u nakur,'),
(5504, 74, 9, 'Pa to će tad biti Dan težak,'),
(5505, 74, 10, 'Za nevjernike lahak neće biti.'),
(5506, 74, 11, 'Ostavi Mene i kog sam stvorio sam,'),
(5507, 74, 12, 'I načinio mu imetak ogroman,'),
(5508, 74, 13, 'I sinove prisutnim (dao),'),
(5509, 74, 14, 'I olakšao mu lahkoćom:'),
(5510, 74, 15, 'Ponovo žudi da uvećam!'),
(5511, 74, 16, 'Nikako! Uistinu, on je ajetima Našim prkosan,'),
(5512, 74, 17, 'Opteretiću ga (teškim) usponom,'),
(5513, 74, 18, 'Zaista je on smišljao i računao,'),
(5514, 74, 19, 'Pa proklet bio kako je odredio,'),
(5515, 74, 20, 'Ponovo, proklet bio kako je odredio!'),
(5516, 74, 21, 'Zatim je pogledao,'),
(5517, 74, 22, 'Onda se namrštio i smrknuo,'),
(5518, 74, 23, 'Potom se okrenuo i uzoholio,'),
(5519, 74, 24, 'Pa rekao: "Ovo je samo sihr, prenosi se;'),
(5520, 74, 25, 'Ovo je samo govor smrtnika."'),
(5521, 74, 26, 'Bacit ću ga sekaru!'),
(5522, 74, 27, 'A šta znaš ti šta je Sekar?'),
(5523, 74, 28, 'Neće poštediti, niti ostaviti,'),
(5524, 74, 29, 'Palitelj je za smrtnika,'),
(5525, 74, 30, 'Nad njim je devetnaest.'),
(5526, 74, 31, 'I učinili smo nadzornicima vatre samo meleke, i broj njihov učinili samo iskušenjem za one koji ne vjeruju: da se uvjere oni kojima je data Knjiga, i onima koji vjeruju poveća vjerovanje; i da ne sumnjaju oni kojima je data Knjiga i vjernici; i da kažu oni u srcima čijim je bolest i nevjernici: "Šta želi Allah ovim primjerom?" Tako zabluđuje Allah koga hoće, a upućuje koga hoće. A zna vojske Gospodara tvoga samo On; i ovo je samo opomena smrtnicima.'),
(5527, 74, 32, 'Nikako! Tako mi Mjeseca,'),
(5528, 74, 33, 'I noći kad mine,'),
(5529, 74, 34, 'I jutra kad zasjaji -'),
(5530, 74, 35, 'Zaista je on jedan najveći,'),
(5531, 74, 36, 'Upozorenje smrtnicima,'),
(5532, 74, 37, 'Onom od vas ko hoće da napreduje ili zaostane.'),
(5533, 74, 38, 'Svaka duša je onom što je zaslužila - zalog,'),
(5534, 74, 39, 'Osim drugova zdesna.'),
(5535, 74, 40, 'U baščama će se raspitivati,'),
(5536, 74, 41, 'O prestupnicima:'),
(5537, 74, 42, '"Šta vas je dovelo u sekar?"'),
(5538, 74, 43, 'Reći će: "Nismo bili od musallija,'),
(5539, 74, 44, 'Niti smo hranili siromaha,'),
(5540, 74, 45, 'I dangubili smo s dangubama,'),
(5541, 74, 46, 'I poricali Dan sudnji,'),
(5542, 74, 47, 'Dok nam nije došlo zagarantovano."'),
(5543, 74, 48, 'Tad im neće koristiti posredovanje posrednika.'),
(5544, 74, 49, 'Pa šta im je?, od Opomene su odvraćeni,'),
(5545, 74, 50, 'Kao da su oni magarci preplašeni,'),
(5546, 74, 51, 'Pobjegli od lava.'),
(5547, 74, 52, 'Naprotiv, svaki čovjek od njih želi da mu se daju listovi rašireni.'),
(5548, 74, 53, 'Nikako! Naprotiv! Ne plaše se Ahireta.'),
(5549, 74, 54, 'Nikako! Zaista je on Pouka,'),
(5550, 74, 55, 'Pa ko htjedne imaće ga na umu,'),
(5551, 74, 56, 'A poučit će se samo ako htjedne Allah. On je Vlasnik bogobojaznosti i Vlasnik oprosta.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Ne! Kunem se Danom kijameta,'),
(5553, 75, 2, 'I ne! Kunem se dušom samooptužujućom!'),
(5554, 75, 3, 'Misli li čovjek da nećemo sastaviti kosti njegove?'),
(5555, 75, 4, 'Svakako! Kadri smo da uredimo jagodice njegove!'),
(5556, 75, 5, 'Naprotiv! Želi čovjek da griješi pred sobom,'),
(5557, 75, 6, 'Pita: "Kad će Dan kijameta?"'),
(5558, 75, 7, 'Pa kad se ukoči pogled,'),
(5559, 75, 8, 'I pomrači Mjesec,'),
(5560, 75, 9, 'I spoji Sunce i Mjesec.'),
(5561, 75, 10, 'Tog dana reći će čovjek: "Gdje je pribježište?"'),
(5562, 75, 11, 'Nikako! Nema utočišta!'),
(5563, 75, 12, 'Gospodaru tvom je Tog dana stanište.'),
(5564, 75, 13, 'Obavijestit će se čovjek Tog dana šta je unaprijed poslao i (šta) zapostavio.'),
(5565, 75, 14, 'Štaviše, čovjek će protiv duše svoje dokaz biti,'),
(5566, 75, 15, 'Makar davao opravdanja svoja.'),
(5567, 75, 16, 'Ne pokreći njime jezik svoj, da bi ga ubrzao.'),
(5568, 75, 17, 'Uistinu, na Nama je sastavljanje njegovo i učenje njegovo.'),
(5569, 75, 18, 'Pa kad ga učimo, slijedi učenje njegovo,'),
(5570, 75, 19, 'Zatim, uistinu na Nama je objašnjavanje njegovo.'),
(5571, 75, 20, 'Nikako! Naprotiv! Volite ubrzano,'),
(5572, 75, 21, 'A ostavljate Ahiret.'),
(5573, 75, 22, 'Lica će Tog dana biti blistava,'),
(5574, 75, 23, 'Prema Gospodaru svome gledajući,'),
(5575, 75, 24, 'I lica će Tog dana biti smrknuta,'),
(5576, 75, 25, 'Znat će da će im se učiniti nesreća.'),
(5577, 75, 26, 'Nikako! Kad dospije ključnjačama,'),
(5578, 75, 27, 'I bude rečeno: "Ko je vrač?"'),
(5579, 75, 28, 'I znadne da je to rastanak,'),
(5580, 75, 29, 'I savije potkoljenica uz potkoljenicu,'),
(5581, 75, 30, 'Gospodaru tvom biće Tog dana sprovođenje!'),
(5582, 75, 31, 'Pa nije vjerovao, ni salat obavljao,'),
(5583, 75, 32, 'Nego je poricao i okretao se,'),
(5584, 75, 33, 'Zatim odlazio porodici svojoj kočoperno.'),
(5585, 75, 34, 'Teško tebi i teško,'),
(5586, 75, 35, 'Ponovo, teško tebi i teško!'),
(5587, 75, 36, 'Misli li čovjek da će se ostaviti besciljnim?'),
(5588, 75, 37, 'Zar nije bio kap sperme izbačene?'),
(5589, 75, 38, 'Zatim je bio zakvačak - pa je stvarao, te usavršio,'),
(5590, 75, 39, 'Pa načinio od njega par - muško i žensko.'),
(5591, 75, 40, 'Zar nije Taj kadar da oživi mrtve!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Da li je dolazio čovjeku period vremena (kad) nije bio stvar pominjana?'),
(5593, 76, 2, 'Uistinu! Mi smo čovjeka stvorili od sjemenih smjesa, da bismo ga iskušali, zato smo ga učinili čujućim, videćim.'),
(5594, 76, 3, 'Uistinu, Mi smo mu pokazali put - bio zahvalan ili nezahvalan.'),
(5595, 76, 4, 'Uistinu, Mi smo pripremili za nevjernike lance i okove i seir.'),
(5596, 76, 5, 'Uistinu, pravedni će piti iz čaše čija će mješavina biti kafur,'),
(5597, 76, 6, 'Sa izvora iz kojeg će piti robovi Allahovi, puštaće je šikljajući.'),
(5598, 76, 7, 'Ispunjavaju zavjet i plaše se Dana čije će zlo biti rasprostranjeno,'),
(5599, 76, 8, 'I hrane hranom za ljubav Njegovu siromaha i siroče i zarobljenika:'),
(5600, 76, 9, '"Samo vas hranimo radi lica Allahovog. Ne želimo od vas plaćanje, niti zahvalnosti;'),
(5601, 76, 10, 'Uistinu, mi se bojimo od Gospodara našeg Dana tmurnog i kobnog."'),
(5602, 76, 11, 'Pa sačuvaće ih Allah zla Tog dana i pružiti im ozarenost i veselje,'),
(5603, 76, 12, 'I nagraditi ih za ono što su trpili, Džennetom i svilom,'),
(5604, 76, 13, 'Naslonjeni u njemu na divanima će biti, neće u njemu vidjeti Sunce, niti studen žestoku,'),
(5605, 76, 14, 'I nisko nad njima biće hladovi njegovi, i visiće grozdovi njegovi nisko,'),
(5606, 76, 15, 'A obilaziće ih s posudama od srebra i peharima - staklenkama,'),
(5607, 76, 16, 'Peharima od srebra; odmjeriće ih mjerom,'),
(5608, 76, 17, 'I napajati u njemu čašom čija će mješavina biti zendžebil,'),
(5609, 76, 18, 'Sa izvora u njemu zvanog Selsebil.'),
(5610, 76, 19, 'Obilaziće ih mladići vječni, kad ih vidiš, mislićeš oni su biser rasuti,'),
(5611, 76, 20, 'Pa kad budeš gledao, tamo ćeš vidjeti blagodat i carstvo veličanstveno,'),
(5612, 76, 21, 'Na njima odjeća od svile zelene i brokata, a biće okićeni narukvicama od srebra, i napajaće ih Gospodar njihov pićem čistim.'),
(5613, 76, 22, 'Uistinu, ovo je za vas plaća i biće trud vaš pohvalan.'),
(5614, 76, 23, 'Uistinu, Mi, Mi smo ti spustili Kur\'an (postepenim) objavljivanjem.'),
(5615, 76, 24, 'Pa trpi do presude Gospodara svog i ne slušaj od njih grešnika ili nevjernika.'),
(5616, 76, 25, 'I spominji ime Gospodara svog jutrom i predvečerjem,'),
(5617, 76, 26, 'I (dio) noći - pa čini sedždu Njemu i slavi Ga noću dugo!'),
(5618, 76, 27, 'Uistinu! Takvi vole ubrzano, a zanemaruju pred sobom Dan tegobni.'),
(5619, 76, 28, 'Mi smo ih stvorili i ojačali vezišta njihova, a kad htjednemo, zamijenićemo sličnim njima (kompletnom) promjenom.'),
(5620, 76, 29, 'Uistinu! Ovo je Opomena, pa ko hoće, uzeće Gospodaru svom put,'),
(5621, 76, 30, 'A hoćete samo šta hoće Allah! Uistinu! Allah je Znalac, Mudri.'),
(5622, 76, 31, 'Uvodi koga hoće u milost Svoju, a zalimima: pripremio im je kaznu bolnu.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Tako mi izaslanika uzastopnih,'),
(5624, 77, 2, 'Te onih koji bjesne uraganski,'),
(5625, 77, 3, 'I onih koji rasprostiru prostiranjem,'),
(5626, 77, 4, 'Te onih koji razdvajaju dijeljenjem,'),
(5627, 77, 5, 'Te onih koji dostavljaju Opomenu,'),
(5628, 77, 6, 'Opravdanje ili upozorenje,'),
(5629, 77, 7, 'Uistinu, ono čime vam se prijeti dogodiće se.'),
(5630, 77, 8, 'Pa kad se zvijezde pogase,'),
(5631, 77, 9, 'I kad se nebo rastvori,'),
(5632, 77, 10, 'I kad se brda zdrobe (u prašinu),'),
(5633, 77, 11, 'I kad se poslanicima vrijeme odredi -'),
(5634, 77, 12, 'Za koji dan je odgođeno?'),
(5635, 77, 13, 'Za Dan razdvajanja!'),
(5636, 77, 14, 'A šta znaš ti šta je Dan razdvajanja?'),
(5637, 77, 15, 'Teško Tog dana poricateljima!'),
(5638, 77, 16, 'Zar nismo uništili ranije?,'),
(5639, 77, 17, 'Zatim dali da ih slijede kasniji.'),
(5640, 77, 18, 'Tako postupamo sa prestupnicima.'),
(5641, 77, 19, 'Teško Tog dana poricateljima!'),
(5642, 77, 20, 'Zar vas ne stvaramo od tekućine prezrene?'),
(5643, 77, 21, 'Pa je stavljamo u boravište postojano,'),
(5644, 77, 22, 'Do roka poznatog,'),
(5645, 77, 23, 'Pa uređujemo, ta divni su Uređivači!'),
(5646, 77, 24, 'Teško Tog dana poricateljima!'),
(5647, 77, 25, 'Zar nismo učinili Zemlju sabiralištem'),
(5648, 77, 26, 'Živih i mrtvih,'),
(5649, 77, 27, 'I načinili na njoj gore stabilne, visoke, i napojili vas vodom pitkom?'),
(5650, 77, 28, 'Teško Tog dana poricateljima!'),
(5651, 77, 29, 'Idite onom šta ste poricali!'),
(5652, 77, 30, 'Idite prema sjeni sa tri grane,'),
(5653, 77, 31, 'Bez hlada će biti, a neće koristiti protiv plamena.'),
(5654, 77, 32, 'Uistinu! Ona će izbacivati iskre kao dvorac,'),
(5655, 77, 33, 'Kao da su one kamile žute.'),
(5656, 77, 34, 'Teško Tog dana poricateljima!'),
(5657, 77, 35, 'Ovo je Dan kad neće govoriti,'),
(5658, 77, 36, 'Niti će im se dopustiti, pa da se opravdavaju.'),
(5659, 77, 37, 'Teško Tog dana poricateljima!'),
(5660, 77, 38, 'Ovo je Dan odluke! Sabrali smo vas i prve,'),
(5661, 77, 39, 'Pa ako vi imate varku - ta prevarite Me!'),
(5662, 77, 40, 'Teško Tog dana poricateljima!'),
(5663, 77, 41, 'Uistinu, bogobojazni će biti u hladovima i izvorima,'),
(5664, 77, 42, 'I voću od čega žele.'),
(5665, 77, 43, 'Jedite i pijte prijatno, za ono što ste radili.'),
(5666, 77, 44, 'Uistinu, Mi tako nagrađujemo dobročinitelje.'),
(5667, 77, 45, 'Teško Tog dana poricateljima!'),
(5668, 77, 46, 'Jedite i uživajte malo, uistinu, vi ste prestupnici.'),
(5669, 77, 47, 'Teško Tog dana poricateljima!'),
(5670, 77, 48, 'A kad im se kaže: "Činite ruk\'u!" - ne čine ruk\'u.'),
(5671, 77, 49, 'Teško Tog dana poricateljima!'),
(5672, 77, 50, 'Pa u koji hadis će poslije njega vjerovati?');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'O čemu se raspituju?'),
(5674, 78, 2, 'O vijesti krupnoj,'),
(5675, 78, 3, 'Onoj o kojoj se oni razilaze.'),
(5676, 78, 4, 'Nikako! Saznaće!'),
(5677, 78, 5, 'Ponovo, nikako, saznaće!'),
(5678, 78, 6, 'Zar nismo učinili Zemlju posteljom,'),
(5679, 78, 7, 'A brda stubovima?'),
(5680, 78, 8, 'I stvorili vas u parovima,'),
(5681, 78, 9, 'I učinili san vaš odmorom,'),
(5682, 78, 10, 'I učinili noć pokrivkom,'),
(5683, 78, 11, 'I načinili dan za privređivanje,'),
(5684, 78, 12, 'I sazdali iznad vas sedam jakih,'),
(5685, 78, 13, 'I načinili svjetiljku plamteću,'),
(5686, 78, 14, 'I spustili iz oblaka vodu obilnu,'),
(5687, 78, 15, 'Da izvedemo njome zrno i bilje,'),
(5688, 78, 16, 'I bašče guste.'),
(5689, 78, 17, 'Uistinu, Dan odluke je vrijeme određeno.'),
(5690, 78, 18, 'Na Dan kad se puhne u sur pa dođete u grupama,'),
(5691, 78, 19, 'I otvori nebo, pa bude kapijama,'),
(5692, 78, 20, 'I pokrenu brda, pa bude fatamorgana,'),
(5693, 78, 21, 'Uistinu, Džehennem će biti busija,'),
(5694, 78, 22, 'Za prevršitelje povratište.'),
(5695, 78, 23, 'Oni će ostati u njemu godinama,'),
(5696, 78, 24, 'Neće okusiti u njemu hladnoću, niti piće,'),
(5697, 78, 25, 'Izuzev vrele vode i ledene,'),
(5698, 78, 26, 'Plaće odgovarajuće.'),
(5699, 78, 27, 'Uistinu, oni se nisu nadali obračunu,'),
(5700, 78, 28, 'I poricali su ajete Naše laganjem.'),
(5701, 78, 29, 'A svaku stvar: pobrojali smo je Knjigom.'),
(5702, 78, 30, 'Zato kušajte. Pa nećemo vam povećati - sem kaznu.'),
(5703, 78, 31, 'Uistinu, bogobojazni će imati uspjeh:'),
(5704, 78, 32, 'Bašče i vinograde,'),
(5705, 78, 33, 'I jedrih grudi vršnjakinje,'),
(5706, 78, 34, 'I čašu punu.'),
(5707, 78, 35, 'Neće u njemu čuti besmislicu, niti laganje,'),
(5708, 78, 36, 'Plaća od Gospodara tvog, dar obračunat,'),
(5709, 78, 37, 'Gospodara nebesa i Zemlje i šta je između njih, Milostivog. Neće moći govoriti s Njim.'),
(5710, 78, 38, 'Na Dan kad bude stajao Duh i meleci posafani, neće govoriti, izuzev onaj kome dopusti Milostivi - a govoriće tačno.'),
(5711, 78, 39, 'Taj dan je istinit, pa ko hoće, uzeće ka Gospodaru svom povratište.'),
(5712, 78, 40, 'Uistinu, Mi vas upozoravamo kaznom bliskom, na Dan kad vidi čovjek šta su unaprijed poslale ruke njegove i rekne nevjernik: "O da sam prašina!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Tako mi onih koji čupaju žestoko,'),
(5714, 79, 2, 'I onih koji vade (blagim) vađenjem,'),
(5715, 79, 3, 'I onih koji plove klizeći,'),
(5716, 79, 4, 'Te onih koji prethode prethođenjem,'),
(5717, 79, 5, 'Te onih koji upravljaju stvar,'),
(5718, 79, 6, 'Na Dan kad uzdrma potres,'),
(5719, 79, 7, 'Slijediće ga naredni.'),
(5720, 79, 8, 'Srca će Tog dana biti uzdrhtala,'),
(5721, 79, 9, 'Pogledi njihovi biće oboreni.'),
(5722, 79, 10, 'Govore: "Da li ćemo uistinu mi biti vraćeni u prvobitno stanje?'),
(5723, 79, 11, 'Zar kad budemo kosti istruhle?"'),
(5724, 79, 12, 'Govore: "To je onda ponavljanje pogubno."'),
(5725, 79, 13, 'Pa uistinu, to će krik jedan biti,'),
(5726, 79, 14, 'Tad gle: oni budni!'),
(5727, 79, 15, 'Da li ti je došao hadis o Musau?'),
(5728, 79, 16, 'Kad ga zovnu Gospodar njegov u dolini svetoj Tuwa:'),
(5729, 79, 17, '"Idi faraonu, zaista je on prevršio,'),
(5730, 79, 18, 'Pa reci: \'Da li bi ti da se očistiš?\''),
(5731, 79, 19, 'I: \'Uputiću te Gospodaru tvom, pa da se bojiš."'),
(5732, 79, 20, 'Tad mu je pokazao znak najveći.'),
(5733, 79, 21, 'Pa - porekao je i nije poslušao,'),
(5734, 79, 22, 'Zatim se okrenuo žureći,'),
(5735, 79, 23, 'Pa sakupio (ljude), te povikao,'),
(5736, 79, 24, 'Pa rekao: "Ja sam gospodar vaš najuzvišeniji."'),
(5737, 79, 25, 'Zato ga je Allah dograbio primjernom kaznom drugog i prvog.'),
(5738, 79, 26, 'Zaista je u tome ibret za onog ko se boji.'),
(5739, 79, 27, 'Da li je vas teže stvoriti ili nebo? Sazdao ga je,'),
(5740, 79, 28, 'Uzdigao visinu njegovu, pa ga sredio,'),
(5741, 79, 29, 'I zatamnio noć njegovu, i izveo svjetlo njegovo,'),
(5742, 79, 30, 'I Zemlju poslije toga - rasprostro je nju,'),
(5743, 79, 31, 'Izveo iz nje vodu njenu i pašnjake njene,'),
(5744, 79, 32, 'I brda: ustabilio ih je,'),
(5745, 79, 33, 'Uživanje za vas i stoku vašu.'),
(5746, 79, 34, 'Pa kad dođe tammet najveći,'),
(5747, 79, 35, 'Na Dan kad se sjeti čovjek šta se trudio,'),
(5748, 79, 36, 'I pokaže se džehim onom ko vidi:'),
(5749, 79, 37, 'Pa što se tiče onog ko je pretjerivao,'),
(5750, 79, 38, 'I više volio život Dunjaa,'),
(5751, 79, 39, 'Pa uistinu, džehim - to će sklonište biti.'),
(5752, 79, 40, 'A što se tiče onog ko se plašio stajanja pred Gospodarom svojim, i suzdržao dušu od strasti -'),
(5753, 79, 41, 'Pa uistinu, Džennet - to će sklonište biti.'),
(5754, 79, 42, 'Pitaju te o Času: "Kad će dolazak njegov?"'),
(5755, 79, 43, 'Čemu? Ti si za podsjećanje na njega,'),
(5756, 79, 44, 'Do Gospodara tvoga je vrhunac njegov!'),
(5757, 79, 45, 'Ti si samo opominjač onom ko ga se plaši,'),
(5758, 79, 46, 'Na dan kad ga vide, biće kao da su oni ostali samo večer ili jutro njegovo.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Namrštio se i okrenuo,'),
(5760, 80, 2, 'Jer mu je slijepac došao.'),
(5761, 80, 3, 'A šta znaš ti? - možda se on čisti,'),
(5762, 80, 4, 'Ili podsjeća, pa mu koristi Opomena.'),
(5763, 80, 5, 'A što se tiče onog koji se smatra neovisnim,'),
(5764, 80, 6, 'Ta ti se njime baviš.'),
(5765, 80, 7, 'A nije do tebe ako se ne očisti.'),
(5766, 80, 8, 'A onaj ko ti dođe žureći,'),
(5767, 80, 9, 'I on strahuje,'),
(5768, 80, 10, 'Pa ti se od njega odvraćaš.'),
(5769, 80, 11, 'Nikako! Uistinu, on je Opomena,'),
(5770, 80, 12, 'Pa ko hoće imaće ga na umu -'),
(5771, 80, 13, 'Na listovima časnim,'),
(5772, 80, 14, 'Uzvišenim, pročišćenim,'),
(5773, 80, 15, 'U rukama pisara,'),
(5774, 80, 16, 'Plemenitih, čestitih.'),
(5775, 80, 17, 'Proklet bio čovjek! Kako je on nezahvalan!'),
(5776, 80, 18, 'Od koje stvari ga stvori?'),
(5777, 80, 19, 'Od kapi sjemena ga stvara, pa mu razmjer da,'),
(5778, 80, 20, 'Zatim mu put olakša,'),
(5779, 80, 21, 'Potom ga usmrti, pa ga sahrani,'),
(5780, 80, 22, 'Zatim, kad htjedne, proživiće ga.'),
(5781, 80, 23, 'Nikako! Još nije izvršio šta mu je naredio.'),
(5782, 80, 24, 'Pa nek pogleda čovjek hranu svoju:'),
(5783, 80, 25, 'Da mi prolivamo vodu izlivom,'),
(5784, 80, 26, 'Zatim iscjepkamo zemlju pucanjem,'),
(5785, 80, 27, 'Pa damo da iznikne u njoj zrno,'),
(5786, 80, 28, 'I grožđe i jestivo bilje,'),
(5787, 80, 29, 'I masline i palme,'),
(5788, 80, 30, 'I bašče bujne,'),
(5789, 80, 31, 'I voće i ispašu -'),
(5790, 80, 32, 'Uživanje za vas i stoku vašu.'),
(5791, 80, 33, 'Pa kad dođe glas zaglušujući,'),
(5792, 80, 34, 'Na Dan kad bude bježao čovjek od brata svog,'),
(5793, 80, 35, 'I majke svoje i oca svog,'),
(5794, 80, 36, 'I žene svoje i sina svog:'),
(5795, 80, 37, 'Imaće svaki čovjek od njih Tog dana stvar koja će ga zaokupiti.'),
(5796, 80, 38, 'Lica će Tog dana biti svijetla,'),
(5797, 80, 39, 'Nasmijana, radosna;'),
(5798, 80, 40, 'I biće lica Tog dana - na njima prašina,'),
(5799, 80, 41, 'Pokriće ih tama,'),
(5800, 80, 42, 'To će oni - nevjernici, razvratnici biti.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Kada se Sunce smota,'),
(5802, 81, 2, 'I kad se zvijezde razlete,'),
(5803, 81, 3, 'I kad se brda pokrenu,'),
(5804, 81, 4, 'I kad se steone kamile napuste,'),
(5805, 81, 5, 'I kad se zvijeri sakupe,'),
(5806, 81, 6, 'I kad se mora zapale,'),
(5807, 81, 7, 'I kad se duše spare,'),
(5808, 81, 8, 'I kad živo zakopana bude upitana,'),
(5809, 81, 9, 'Zbog kojeg grijeha je ubijena;'),
(5810, 81, 10, 'I kad se listovi rašire,'),
(5811, 81, 11, 'I kad nebo bude svučeno,'),
(5812, 81, 12, 'I kad se džehim raspali,'),
(5813, 81, 13, 'I kad se Džennet približi:'),
(5814, 81, 14, 'Znaće duša šta je pripremila.'),
(5815, 81, 15, 'Pa ne! Kunem se onima koje odstupaju,'),
(5816, 81, 16, 'Koje se kreću, koje se skrivaju,'),
(5817, 81, 17, 'I noću, kad odlazi,'),
(5818, 81, 18, 'I jutrom, kad diše,'),
(5819, 81, 19, 'Uistinu, to je riječ izaslanika plemenitog,'),
(5820, 81, 20, 'Posjednika moći, kod Vlasnika Arša cijenjenog,'),
(5821, 81, 21, 'Slušanog, tamo pouzdanog.'),
(5822, 81, 22, 'I nije drug vaš lud,'),
(5823, 81, 23, 'A uistinu, vidio ga je na horizontu jasnom,'),
(5824, 81, 24, 'I nije on nad nevidljivim škrt,'),
(5825, 81, 25, 'I nije to govor šejtana prokletog.'),
(5826, 81, 26, 'Pa kamo idete?'),
(5827, 81, 27, 'Zaista je on samo Opomena svjetovima,'),
(5828, 81, 28, 'Za onog od vas ko hoće da ide pravo -'),
(5829, 81, 29, 'A nećete, izuzev šta hoće Allah, Gospodar svjetova.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Kad se nebo rascijepi,'),
(5831, 82, 2, 'I kad se planete rasprše,'),
(5832, 82, 3, 'I kad mora uzavriju,'),
(5833, 82, 4, 'I kad se kaburi ispreturaju,'),
(5834, 82, 5, 'Znaće duša šta je unaprijed poslala i (šta) zapostavila.'),
(5835, 82, 6, 'O čovječe! Šta te je obmanulo o Gospodaru tvom plemenitom,'),
(5836, 82, 7, 'Koji te je stvorio, pa te uredio, te te simetričnim učinio?,'),
(5837, 82, 8, 'U kojem obliku, kako je htio, sastavio te!'),
(5838, 82, 9, 'Nikako! Naprotiv, poričete Sud.'),
(5839, 82, 10, 'A uistinu, nad vama su čuvari,'),
(5840, 82, 11, 'Plemeniti pisari,'),
(5841, 82, 12, 'Znaju šta činite.'),
(5842, 82, 13, 'Uistinu, pravedni će u zadovoljstvo,'),
(5843, 82, 14, 'I uistinu, razvratnici će u džehim.'),
(5844, 82, 15, 'Pržiće se njime Dana sudnjeg,'),
(5845, 82, 16, 'I neće oni iz njega biti odsutni.'),
(5846, 82, 17, 'A šta znaš ti šta je Dan sudnji?'),
(5847, 82, 18, 'Ponovo, šta znaš ti šta je Dan sudnji?'),
(5848, 82, 19, 'Dan kad neće vladati duša za dušu nimalo, a komanda će Tog dana biti Allahova.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Teško zakidateljima,'),
(5850, 83, 2, 'Koji kad uzimaju od svijeta, traže potpuno,'),
(5851, 83, 3, 'A kad njima mjere ili im vagaju, zakidaju!'),
(5852, 83, 4, 'Zar ne misle takvi da će oni biti podignuti,'),
(5853, 83, 5, 'Za Dan strašni,'),
(5854, 83, 6, 'Dan kad će stati ljudi pred Gospodara svjetova!'),
(5855, 83, 7, 'Nikako! Zaista je knjiga razvratnika u Sidždžinu!'),
(5856, 83, 8, 'A šta znaš ti šta je Sidždžin?'),
(5857, 83, 9, 'Knjiga ispisana!'),
(5858, 83, 10, 'Teško Tog dana poricateljima,'),
(5859, 83, 11, 'Koji niječu Dan sudnji!'),
(5860, 83, 12, 'A ne niječe ga, izuzev svaki prestupnik, grješnik.'),
(5861, 83, 13, 'Kad mu se uče ajeti Naši, rekne: "Priče ranijih!"'),
(5862, 83, 14, 'Nikako! Naprotiv, ono što su stekli je hrđa na srcima njihovim.'),
(5863, 83, 15, 'Nikako! Zaista će oni od Gospodara svoga Tog dana biti zaklonjeni,'),
(5864, 83, 16, 'Zatim će se uistinu oni pržiti džehimom,'),
(5865, 83, 17, 'Potom će biti rečeno: "Ovo je to što ste poricali."'),
(5866, 83, 18, 'Nikako! Uistinu je Knjiga čestitih u Illijjunu!'),
(5867, 83, 19, 'A šta znaš ti šta je Illijjun?'),
(5868, 83, 20, 'Knjiga ispisana!'),
(5869, 83, 21, 'Svjedočiće je približeni.'),
(5870, 83, 22, 'Uistinu će čestiti u zadovoljstvu biti,'),
(5871, 83, 23, 'Na divanima će gledati,'),
(5872, 83, 24, 'Spoznaćeš na licima njihovim sjaj zadovoljstva,'),
(5873, 83, 25, 'Napajani će biti nektarom zapečaćenim.'),
(5874, 83, 26, 'Pečat njegov biće misk; a za to neka se natječu takmičari -'),
(5875, 83, 27, 'A mješavina njegova od Tesnima,'),
(5876, 83, 28, 'Izvora s kojeg će piti približeni.'),
(5877, 83, 29, 'Uistinu! Oni koji su skrivili ismijavali su se onima koji su vjerovali,'),
(5878, 83, 30, 'I kad su pored njih prolazili, namigivali,'),
(5879, 83, 31, 'A kad su se vraćali porodici svojoj, vraćali su se šaleći se,'),
(5880, 83, 32, 'I kad bi ih vidjeli, rekli bi: "Zaista su ovi zalutali."'),
(5881, 83, 33, 'A nisu poslani nad njima čuvarima.'),
(5882, 83, 34, 'Pa, Danas će se oni koji su vjerovali, nevjernicima ismijavati,'),
(5883, 83, 35, 'Na divanima će gledati!'),
(5884, 83, 36, 'Zar nisu nevjernici nagrađeni kako su radili?');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Kad nebo raspukne,'),
(5886, 84, 2, 'I posluša Gospodara svog - a obavezno je -'),
(5887, 84, 3, 'I kad se Zemlja rastegne,'),
(5888, 84, 4, 'I izbaci šta je u njoj i isprazni se,'),
(5889, 84, 5, 'I posluša Gospodara svog - a obavezna je -'),
(5890, 84, 6, 'O čovječe! Zaista si ti trudbenik Gospodaru svom trudom, pa ti si taj koji ćeš Ga sresti.'),
(5891, 84, 7, 'Pa, kome se da knjiga njegova u desnicu njegovu,'),
(5892, 84, 8, 'Pa biće obračunat obračunom lahkim,'),
(5893, 84, 9, 'I vratiće se porodici svojoj obradovan;'),
(5894, 84, 10, 'A kome se da knjiga njegova iza leđa njegovih,'),
(5895, 84, 11, 'Pa prizivaće propast,'),
(5896, 84, 12, 'I pržiće se seirom.'),
(5897, 84, 13, 'Uistinu, on je u porodici svojoj radostan bio.'),
(5898, 84, 14, 'Uistinu, on je smatrao da se neće vratiti.'),
(5899, 84, 15, 'Svakako! Uistinu, Gospodar njegov je Taj koji ga gleda!'),
(5900, 84, 16, 'Pa ne! Kunem se rumenilom večernjim,'),
(5901, 84, 17, 'I noću i onim šta prikupi,'),
(5902, 84, 18, 'I Mjesecom kad se ispuni,'),
(5903, 84, 19, 'Sigurno ćete prelaziti iz sfere u sferu.'),
(5904, 84, 20, 'Pa šta im je?, ne vjeruju,'),
(5905, 84, 21, 'I kad im se uči Kur\'an na sedždu ne padaju.'),
(5906, 84, 22, 'Naprotiv, oni koji ne vjeruju - poriču,'),
(5907, 84, 23, 'A Allah je Najbolji znalac onog šta imaju na umu.'),
(5908, 84, 24, 'Pa obraduj ih kaznom bolnom,'),
(5909, 84, 25, 'Sem onih koji vjeruju i čine dobra djela: imaće oni nagradu neprekidnu.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Tako mi neba punog galaksija,'),
(5911, 85, 2, 'I Dana obećanog,'),
(5912, 85, 3, 'I svjedoka i svjedočenog:'),
(5913, 85, 4, 'Prokleti neka su vlasnici rova,'),
(5914, 85, 5, 'Vatre pune goriva,'),
(5915, 85, 6, 'Kad su oni nad njom sjedili,'),
(5916, 85, 7, 'I oni onome šta su činili vjernicima, svjedoci bili.'),
(5917, 85, 8, 'A svetili su im se samo što su vjerovali u Allaha, Moćnog, Hvaljenog,'),
(5918, 85, 9, 'Onog čija je vlast nebesa i Zemlje! A Allah je nad svakom stvari Svjedok.'),
(5919, 85, 10, 'Uistinu, oni koji progone vjernike i vjernice, zatim se ne pokaju - pa imaće oni kaznu Džehennema, a njihova kazna je gorenje.'),
(5920, 85, 11, 'Uistinu, oni koji vjeruju i čine dobra djela, imaće oni bašče ispod kojih teku rijeke. To je uspjeh veliki.'),
(5921, 85, 12, 'Uistinu, sila Gospodara tvog je žestoka.'),
(5922, 85, 13, 'Uistinu, On, On počinje i ponavlja,'),
(5923, 85, 14, 'I On je Oprosnik, Onaj koji voli,'),
(5924, 85, 15, 'Vlasnik Arša, Slavni,'),
(5925, 85, 16, 'Onaj koji čini šta želi.'),
(5926, 85, 17, 'Da li ti je došo hadis o vojskama,'),
(5927, 85, 18, 'Faraonu i Semudu?'),
(5928, 85, 19, 'Naprotiv! Oni koji ne vjeruju, u poricanju su,'),
(5929, 85, 20, 'A Allah je njih Okružitelj.'),
(5930, 85, 21, 'Naprotiv! To je Kur\'an slavni,'),
(5931, 85, 22, 'Na Ploči čuvanoj.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Tako mi neba i Tarika!'),
(5933, 86, 2, 'A šta znaš ti šta je Tarik?'),
(5934, 86, 3, 'Zvijezda prodorna!'),
(5935, 86, 4, 'Nema duše nijedne bez nad njom čuvara.'),
(5936, 86, 5, 'Pa neka pogleda čovjek od čega je stvoren!'),
(5937, 86, 6, 'Stvoren je od tekućine brizgajuće,'),
(5938, 86, 7, 'Izlazi između kičme i rebara!'),
(5939, 86, 8, 'Uistinu! On je za povratak njegov kadar.'),
(5940, 86, 9, 'Na Dan ispitivanja tajni,'),
(5941, 86, 10, 'Tad on neće imati snage, niti pomagača.'),
(5942, 86, 11, 'Tako mi neba punog vraćanja,'),
(5943, 86, 12, 'I Zemlje pune raspukline,'),
(5944, 86, 13, 'Uistinu! On je riječ odlučujuća,'),
(5945, 86, 14, 'I nije on šala.'),
(5946, 86, 15, 'Uistinu, oni pletu spletku,'),
(5947, 86, 16, 'I pletem spletku.'),
(5948, 86, 17, 'Pa odloži nevjernicima, daj im vremena, polahko.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Slavi ime Gospodara svog Svevišnjeg,'),
(5950, 87, 2, 'Koji stvara, pa usklađuje,'),
(5951, 87, 3, 'I Koji odmjerava, pa upućuje,'),
(5952, 87, 4, 'I Koji izvodi pašnjak,'),
(5953, 87, 5, 'Pa ga učini otpadom tamnozelnim.'),
(5954, 87, 6, 'Poučićemo te učenju, pa nećeš zaboraviti,'),
(5955, 87, 7, 'Izuzev šta htjedne Allah. Uistinu! On zna otkriveno i šta je skriveno.'),
(5956, 87, 8, 'A olakšaćemo ti za lahkoću,'),
(5957, 87, 9, 'Zato opominji, ako koristila opomena.'),
(5958, 87, 10, 'Opomenuće se ko se boji,'),
(5959, 87, 11, 'A izbjegava je najnesrećniji,'),
(5960, 87, 12, 'Koji će se pržiti vatrom velikom;'),
(5961, 87, 13, 'Potom neće u njoj umrijeti, niti živjeti.'),
(5962, 87, 14, 'Zaista će uspjeti ko se očisti,'),
(5963, 87, 15, 'I sjeti imena Gospodara svog, pa salat obavi.'),
(5964, 87, 16, 'Naprotiv, više volite život Dunjaa,'),
(5965, 87, 17, 'A Ahiret je bolji i trajniji.'),
(5966, 87, 18, 'Uistinu! Ovo je u listovima ranijim,'),
(5967, 87, 19, 'Listovima Ibrahimovim i Musaovim.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Da li ti je došao hadis o prekriću?'),
(5969, 88, 2, 'Lica će Tog dana biti ponižena,'),
(5970, 88, 3, 'Izmorena, napaćena,'),
(5971, 88, 4, 'Vatrom užarenom pržena,'),
(5972, 88, 5, 'Sa izvora uzavrelog pojena.'),
(5973, 88, 6, 'Neće biti hrane za njih, izuzev od trna,'),
(5974, 88, 7, 'Neće ugojiti, niti protiv gladi koristiti.'),
(5975, 88, 8, 'Lica će Tog dana biti radosna,'),
(5976, 88, 9, 'Trudom svojim zadovoljna,'),
(5977, 88, 10, 'U Džennetu visokome;'),
(5978, 88, 11, 'Neće čuti u njemu besmislicu.'),
(5979, 88, 12, 'U njemu je izvor tekući,'),
(5980, 88, 13, 'U njemu su sofe uzdignute,'),
(5981, 88, 14, 'I čaše postavljene,'),
(5982, 88, 15, 'I jastučići poredani,'),
(5983, 88, 16, 'I ćilimi razastrti.'),
(5984, 88, 17, 'Pa zar neće pogledati kamilu, kako je stvorena?'),
(5985, 88, 18, 'I nebo, kako je uzdignuto!'),
(5986, 88, 19, 'I brda, kako su postavljena!'),
(5987, 88, 20, 'I Zemlju, kako je prostrta!'),
(5988, 88, 21, 'Pa opominji, ti si samo opominjač:'),
(5989, 88, 22, 'Nisi nad njima pazitelj;'),
(5990, 88, 23, 'A ko se okrene i ne vjeruje,'),
(5991, 88, 24, 'Pa kazniće ga Allah kaznom najvećom.'),
(5992, 88, 25, 'Uistinu, Nama je povratak njihov,'),
(5993, 88, 26, 'Zatim, uistinu, na Nama je obračun njihov.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Tako mi zore,'),
(5995, 89, 2, 'I deset noći,'),
(5996, 89, 3, 'I parnog i neparnog,'),
(5997, 89, 4, 'I noći kad odlazi!'),
(5998, 89, 5, 'Da li je u tome zakletva za posjednika razuma?'),
(5999, 89, 6, 'Zar nisi vidio kako je postupio Gospodar tvoj sa Adom,'),
(6000, 89, 7, 'Iremom punim stubišta,'),
(6001, 89, 8, 'Kojima nisu stvoreni slični u zemljama?'),
(6002, 89, 9, 'I Semudom, koji su klesali stijene u dolini,'),
(6003, 89, 10, 'I faraonom, posjednikom kolčeva? -'),
(6004, 89, 11, 'Koji su prevršili u zemljama,'),
(6005, 89, 12, 'Te namnožili u njima fesad,'),
(6006, 89, 13, 'Pa je Gospodar tvoj pustio na njih bič kazne.'),
(6007, 89, 14, 'Uistinu, Gospodar tvoj je u zasjedi.'),
(6008, 89, 15, 'Pa što se tiče čovjeka, kad ga iskušava Gospodar njegov, pa mu ukaže čast i podari mu blagodat, tad kaže: "Gospodar moj mi ukaza čast!"'),
(6009, 89, 16, 'A kad ga iskušava, pa mu uskrati opskrbu njegovu, tad kaže: "Gospodar moj me prezreo!"'),
(6010, 89, 17, 'Nikako! Naprotiv! Ne ukazujete čast siročetu,'),
(6011, 89, 18, 'I ne podstičete hranjenju siromaha,'),
(6012, 89, 19, 'I nasljedstvo jedete žderačkom pohlepom,'),
(6013, 89, 20, 'I volite bogatstvo ljubavlju neizmjernom.'),
(6014, 89, 21, 'Nikako! Kad se zdrobi Zemlja, u mrvice, komadiće,'),
(6015, 89, 22, 'I dođe Gospodar tvoj i meleci red do reda,'),
(6016, 89, 23, 'I prinese se Tog dana Džehennem -Tog dana sjećat će se čovjek - a čemu mu sjećanje -'),
(6017, 89, 24, 'Govorit će: "O da sam (što) unaprijed poslao za život svoj!"'),
(6018, 89, 25, 'Pa Tog dana neće kazniti kaznom Njegovom niko,'),
(6019, 89, 26, 'Niti će vezati vezom Njegovom iko (kao On)!'),
(6020, 89, 27, 'O dušo smirena!'),
(6021, 89, 28, 'Vrati se Gospodaru svom zadovoljna, zadovoljavajuća,'),
(6022, 89, 29, 'Pa uđi među robove Moje,'),
(6023, 89, 30, 'I uđi u Džennet Moj!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Ne! Kunem se gradom ovim,'),
(6025, 90, 2, 'A ti si slobodan u gradu ovom -'),
(6026, 90, 3, 'I roditeljem i šta je rodio.'),
(6027, 90, 4, 'Uistinu, stvorili smo čovjeka (da) bude u trpnji.'),
(6028, 90, 5, 'Misli li da nema moć nad njim niko?'),
(6029, 90, 6, 'Govori: "Uništio sam bogatstvo mnogo."'),
(6030, 90, 7, 'Misli li da ga ne vidi niko?'),
(6031, 90, 8, 'Zar mu nismo načinili oka dva,'),
(6032, 90, 9, 'I jezik i usne dvije,'),
(6033, 90, 10, 'I uputili ga stazama dvjema,'),
(6034, 90, 11, 'Pa nije pokušao uspon.'),
(6035, 90, 12, 'A šta znaš ti šta je uspon?'),
(6036, 90, 13, 'Oslobođenje roba,'),
(6037, 90, 14, 'Ili hranjenje u danu gladi'),
(6038, 90, 15, 'Siročeta bliskog,'),
(6039, 90, 16, 'Ili siromaha u prašini;'),
(6040, 90, 17, 'Zatim biti od onih koji vjeruju i preporučuju jedni drugima sabr, i preporučuju jedni drugima merhamet.'),
(6041, 90, 18, 'Takvi će biti drugovi desne strane.'),
(6042, 90, 19, 'A oni koji ne vjeruju u ajete Naše, oni će biti drugovi lijeve strane,'),
(6043, 90, 20, 'Nad njima će vatra biti zatvorena.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Tako mi Sunca i svjetla njegova,'),
(6045, 91, 2, 'I Mjeseca, kada ga slijedi,'),
(6046, 91, 3, 'I dana, kad ga otkrije,'),
(6047, 91, 4, 'I noći, kad ga prekrije,'),
(6048, 91, 5, 'I neba i Šta ga sazda,'),
(6049, 91, 6, 'I Zemlje i Šta je rasprostr\','),
(6050, 91, 7, 'I duše i Šta je usavrši,'),
(6051, 91, 8, 'Pa je nadahnu razvratu njenom i bogobojaznosti njenoj:'),
(6052, 91, 9, 'Zaista uspije ko je očisti,'),
(6053, 91, 10, 'I zaista ne uspije ko je pokvari.'),
(6054, 91, 11, 'Poricao je Semud u pretjeranosti svojoj,'),
(6055, 91, 12, 'Kad se podigao najnesretniji njihov.'),
(6056, 91, 13, 'Tad reče njima poslanik Allahov: "Kamila Allahova i napajanje njeno!"'),
(6057, 91, 14, 'Pa, porekli su ga te je ranili, pa ih je zgromio Gospodar njihov i sravnio ih,'),
(6058, 91, 15, 'A ne plaši se posljedice toga!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Tako mi noći kad prekrije,'),
(6060, 92, 2, 'I dana, kad zasjaji,'),
(6061, 92, 3, 'I Onog Šta muško i žensko stvara:'),
(6062, 92, 4, 'Uistinu, trud vaš je različit.'),
(6063, 92, 5, 'Pa što se tiče onog ko daje i boji se,'),
(6064, 92, 6, 'I povjeruje u najbolje,'),
(6065, 92, 7, 'Pa - olakšaćemo mu (put) za lahkoću;'),
(6066, 92, 8, 'A što se tiče onog ko škrtari i osjeća se neovisnim,'),
(6067, 92, 9, 'I zaniječe najbolje,'),
(6068, 92, 10, 'Pa - olakšaćemo mu za teškoću,'),
(6069, 92, 11, 'I neće mu koristiti bogatstvo njegovo kad se strovali.'),
(6070, 92, 12, 'Uistinu! Na Nama je upućivanje,'),
(6071, 92, 13, 'I uistinu, Naš je drugi i prvi (svijet).'),
(6072, 92, 14, 'Pa upozoravam vas vatrom plamtećom:'),
(6073, 92, 15, 'Neće se pržiti njome, sem najnesrećniji,'),
(6074, 92, 16, 'Koji poriče i okreće se,'),
(6075, 92, 17, 'A odstraniće se od nje najbogobojazniji,'),
(6076, 92, 18, 'Koji daje imetak svoj, čisteći se,'),
(6077, 92, 19, 'I kod kojeg nema niko nikakve blagodati da bi bio nagrađen,'),
(6078, 92, 20, 'Izuzev traženja lica Gospodara njegovog Uzvišenog.'),
(6079, 92, 21, 'A doista će zadovoljan biti.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Tako mi jutra,'),
(6081, 93, 2, 'I noći kad se utiša!'),
(6082, 93, 3, 'Nije te napustio Gospodar tvoj i nije omrznuo,'),
(6083, 93, 4, 'A sigurno je drugo bolje za tebe od prvog.'),
(6084, 93, 5, 'I zaista će ti dati Gospodar tvoj, pa ćeš zadovoljan biti.'),
(6085, 93, 6, 'Zar te nije našao siročetom, pa utočište dao?'),
(6086, 93, 7, 'I našao te zalutalim, pa uputio?'),
(6087, 93, 8, 'I našao te siromašnim, pa obogatio?'),
(6088, 93, 9, 'Pa što se tiče siročeta - pa ne tlači,'),
(6089, 93, 10, 'I što se tiče prosjaka - pa ne grdi,'),
(6090, 93, 11, 'A što se tiče blagodati Gospodara tvoga - ta kazuj!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Zar ti nismo raširili grudi tvoje,'),
(6092, 94, 2, 'I skinuli s tebe teret tvoj,'),
(6093, 94, 3, 'Koji je pritiskao pleća tvoja,'),
(6094, 94, 4, 'I uzdigli ti spomen tvoj?'),
(6095, 94, 5, 'Pa uistinu, uz teškoću je olakšanje,'),
(6096, 94, 6, 'Uistinu, uz teškoću je olakšanje.'),
(6097, 94, 7, 'Pa kad si slobodan - tad se trudi,'),
(6098, 94, 8, 'I Gospodaru svom tad žudi!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Tako mi smokve i masline,'),
(6100, 95, 2, 'I Gore Sinajske,'),
(6101, 95, 3, 'I grada ovog sigurnog!'),
(6102, 95, 4, 'Zaista smo čovjeka stvorili u skladu najboljem,'),
(6103, 95, 5, 'Zatim ćemo ga vratiti najnižem od niskih:'),
(6104, 95, 6, 'Izuzev onih koji vjeruju i čine dobra djela - pa imaće oni nagradu neprekidnu.'),
(6105, 95, 7, 'Pa šta te navodi da poričeš Sud nakon (ovog)?'),
(6106, 95, 8, 'Zar nije Allah Najmudriji od sudaca?');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Čitaj u ime Gospodara svoga koji stvara,'),
(6108, 96, 2, 'Stvara čovjeka od zakvačka.'),
(6109, 96, 3, 'Čitaj! A Gospodar tvoj je Najplemenitiji,'),
(6110, 96, 4, 'Onaj koji poučava perom,'),
(6111, 96, 5, 'Poučava čovjeka ono šta ne zna.'),
(6112, 96, 6, 'Nikako! Zaista čovjek prevrši,'),
(6113, 96, 7, 'Čim se neovisnim vidi!'),
(6114, 96, 8, 'Uistinu, Gospodaru tvom je povratak.'),
(6115, 96, 9, 'Jesi li vidio onog koji sprječava'),
(6116, 96, 10, 'Roba kad salat obavlja?'),
(6117, 96, 11, 'Jesi li vidio - ako je na Uputi,'),
(6118, 96, 12, 'Ili naređuje bogobojaznost?'),
(6119, 96, 13, 'Jesi li vidio - ako poriče i okreće se?'),
(6120, 96, 14, 'Zar ne zna da Allah vidi?'),
(6121, 96, 15, 'Nikako! Ako ne prestane, zaista ćemo ga ščepati za kiku,'),
(6122, 96, 16, 'Kiku lažnu, grješnu,'),
(6123, 96, 17, 'Pa neka pozove skup svoj,'),
(6124, 96, 18, 'Pozvaćemo zebanije.'),
(6125, 96, 19, 'Nikako! Ne poslušaj ga, a učini sedždu i približi se.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Uistinu! Mi smo ga spustili u noći Kadr.'),
(6127, 97, 2, 'A šta znaš ti šta je noć Kadr?'),
(6128, 97, 3, 'Noć Kadr je bolja od hiljadu mjeseci.'),
(6129, 97, 4, 'Spuštaju se meleci i Duh u njoj s dozvolom Gospodara njihovog, po svakoj naredbi.'),
(6130, 97, 5, 'Mir je ona, do pojave zore.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Nisu se oni koji ne vjeruju od pripadnika Knjige i mušrici razdvojili, dok im nije došao jasan dokaz:'),
(6132, 98, 2, 'Poslanik od Allaha, recitira listove čiste,'),
(6133, 98, 3, 'U njima knjige ispravne.'),
(6134, 98, 4, 'Razdijelili su se oni kojima je data Knjiga tek pošto im je došao jasan dokaz,'),
(6135, 98, 5, 'A naređeno im je da obožavaju jedino Allaha, odani Njegovoj vjeri, hanife da budu i obavljaju salat i daju zekat, a to je vjera prava.'),
(6136, 98, 6, 'Uistinu! Oni od sljedbenika Knjige i mušrici koji ne vjeruju biće u vatri Džehennema, vječno u njoj. Ti takvi su najgora stvorenja.'),
(6137, 98, 7, 'Uistinu! Oni koji vjeruju i čine dobra djela - ti takvi su najbolja stvorenja.'),
(6138, 98, 8, 'Plaća njihova kod Gospodara njihovog je: bašče Adna ispod kojih teku rijeke, vječno će biti u njima, zauvijek. Zadovoljan će Allah biti sa njima, i zadovoljni Njime. To će biti za onog ko se boji Gospodara svog.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Kad se zatrese Zemlja potresom svojim,'),
(6140, 99, 2, 'I izbaci Zemlja terete svoje,'),
(6141, 99, 3, 'I rekne čovjek: "Šta joj je?"'),
(6142, 99, 4, 'Tog dana kazivat će vijesti svoje,'),
(6143, 99, 5, 'Jer će joj Gospodar tvoj objaviti.'),
(6144, 99, 6, 'Tog dana pojaviće se ljudi odvojeni, da im se pokažu djela njihova.'),
(6145, 99, 7, 'Pa ko je uradio težinu atoma dobrog, vidjeće ga,'),
(6146, 99, 8, 'A ko je uradio težinu atoma zla, vidjeće ga.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Tako mi onih koji dahćući jure,'),
(6148, 100, 2, 'Pa onih koji iskre krešu,'),
(6149, 100, 3, 'Te onih koji napadaju jutrom,'),
(6150, 100, 4, 'Pa dižu time prašinu,'),
(6151, 100, 5, 'Pa prodiru time u sred skupine:'),
(6152, 100, 6, 'Uistinu! Čovjek je Gospodaru svome nezahvalan,'),
(6153, 100, 7, 'I uistinu, on je tome svjedok,'),
(6154, 100, 8, 'I uistinu, on je u ljubavi prema dobru žestok.'),
(6155, 100, 9, 'Pa zar ne zna, kad se rasturi šta je u kaburima,'),
(6156, 100, 10, 'I otkrije šta je u prsima -'),
(6157, 100, 11, 'Uistinu će Gospodar njihov o njima Tog dana biti Obaviješteni.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Udar!'),
(6159, 101, 2, 'Šta je udar?'),
(6160, 101, 3, 'I šta znaš ti šta je udar?'),
(6161, 101, 4, 'Dan kad budu ljudi kao leptiri raštrkani,'),
(6162, 101, 5, 'I budu brda kao vuna raščupana.'),
(6163, 101, 6, 'Pa onaj čije mjere budu teške,'),
(6164, 101, 7, 'Pa on će u životu zadovoljstva biti.'),
(6165, 101, 8, 'A onaj čije mjere budu lahke,'),
(6166, 101, 9, 'Pa majka njegova biće hawija!'),
(6167, 101, 10, 'A šta znaš ti šta je ona?'),
(6168, 101, 11, 'Vatra žestoka.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Odvraća vas uvećavanje,'),
(6170, 102, 2, 'Dok greblja ne posjetite.'),
(6171, 102, 3, 'Nikako! Saznaćete!'),
(6172, 102, 4, 'Ponovo ne, saznaćete!'),
(6173, 102, 5, 'Nikako! Kad biste znali znanjem sigurnim!'),
(6174, 102, 6, 'Zaista ćete vidjeti džehim;'),
(6175, 102, 7, 'Ponovo, zaista ćete ga vidjeti okom sigurnim,'),
(6176, 102, 8, 'Zatim ćete zaista Tog dana biti pitani o uživanju.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Tako mi vremena,'),
(6178, 103, 2, 'Uistinu, čovjek je u gubitku,'),
(6179, 103, 3, 'Izuzev onih koji vjeruju i čine dobra djela, i preporučuju jedni drugima Istinu i preporučuju jedni drugima strpljenje.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Teško svakom klevetniku, podrugljivcu,'),
(6181, 104, 2, 'Koji bogatstvo skuplja i prebrojava ga.'),
(6182, 104, 3, 'Misli da će ga bogatstvo njegovo besmrtnim učiniti.'),
(6183, 104, 4, 'Nikako! Sigurno će bačen biti u hutamu.'),
(6184, 104, 5, 'A šta znaš ti šta je hutama?'),
(6185, 104, 6, 'Vatra Allahova raspaljena,'),
(6186, 104, 7, 'Koja će na srca dosezati.'),
(6187, 104, 8, 'Uistinu, ona će na njima biti zatvorena,'),
(6188, 104, 9, 'U stupovima izduženim.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Zar nisi vidio kako je Gospodar tvoj sa vlasnicima slona postupio?'),
(6190, 105, 2, 'Zar nije osujetio (ratno) lukavstvo njihovo,'),
(6191, 105, 3, 'I poslao na njih jata ptica?'),
(6192, 105, 4, 'Gađale su ih kamenjem od gline spečene,'),
(6193, 105, 5, 'Te ih učinio kao vlat izjedenu.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Zbog navike Kurejšija,'),
(6195, 106, 2, 'Navike putovanja njihovog, zimi i ljeti,'),
(6196, 106, 3, 'Pa neka obožavaju Gospodara ove Kuće,'),
(6197, 106, 4, 'Koji ih za gladi hrani i od straha brani.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Jesi li vidio onog koji poriče Sud?'),
(6199, 107, 2, 'Pa to je onaj koji odbija siroče,'),
(6200, 107, 3, 'I ne podstiče hranjenju siromaha.'),
(6201, 107, 4, 'Pa teško musallijama,'),
(6202, 107, 5, 'Onima koji su prema salatu svom nemarni,'),
(6203, 107, 6, 'Onima koji bi da budu viđeni,'),
(6204, 107, 7, 'A odbijaju el-ma\'un!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Uistinu, Mi smo tebi dali Kewser,'),
(6206, 108, 2, 'Zato čini salat Gospodaru svom i kolji (kurban).'),
(6207, 108, 3, 'Uistinu, mrzitelj tvoj - on je ebter!');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Reci: "O nevjernici!'),
(6209, 109, 2, 'Ne obožavam šta obožavate,'),
(6210, 109, 3, 'I niste vi obožavatelji onog šta obožavam,'),
(6211, 109, 4, 'I nisam ja obožavalac onog šta obožavate,'),
(6212, 109, 5, 'Niti ste vi obožavaoci onog šta obožavam.'),
(6213, 109, 6, 'Vama vjera vaša, a meni vjera moja!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Kad dođe pomoć Allahova i pobjeda,'),
(6215, 110, 2, 'I vidiš ljude ulaze u vjeru Allahovu u grupama,'),
(6216, 110, 3, 'Tad slavi sa hvalom Gospodara svog i traži oprost od Njega. Uistinu! On je Primalac pokajanja.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Propale ruke Ebu Lehebove - i propao je.'),
(6218, 111, 2, 'Neće mu koristiti bogatstvo njegovo i šta je stekao.'),
(6219, 111, 3, 'Pržiće se vatrom rasplamsalom,'),
(6220, 111, 4, 'I žena njegova, nosačica drva,'),
(6221, 111, 5, 'Na vratu njenom biće konop od palmina lika.');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Reci: "On, Allah, je Jedan!'),
(6223, 112, 2, 'Allah je Apsolutni!'),
(6224, 112, 3, 'Ne rađa i nije rođen!,'),
(6225, 112, 4, 'I ravan Mu nije niko!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Reci: "Tražim zaštitu u Gospodara svitanja,'),
(6227, 113, 2, 'Od zla onog šta stvara,'),
(6228, 113, 3, 'I od zla tame kad zamrači,'),
(6229, 113, 4, 'I od zla onih koje pušu u čvorove,'),
(6230, 113, 5, 'I od zla zavidnika kad zavidi!"');
INSERT INTO `bs_mlivo` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Reci: "Tražim zaštitu u Gospodara ljudi,'),
(6232, 114, 2, 'Vladara ljudi,'),
(6233, 114, 3, 'Boga ljudi,'),
(6234, 114, 4, 'Od zla došaptavanja hannasa,'),
(6235, 114, 5, 'Koji došaptava u grudi ljudi,'),
(6236, 114, 6, 'Od džinna i ljudi."');
