INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Ve jménu Boha milosrdného, slitovného.'),
(2, 1, 2, 'Chvála Bohu, Pánu lidstva veškerého,'),
(3, 1, 3, 'Milosrdnému, Slitovnému,'),
(4, 1, 4, 'vládci dne soudného!'),
(5, 1, 5, 'Tebe uctíváme a Tebe o pomoc žádáme,'),
(6, 1, 6, 'veď nás stezkou přímou,'),
(7, 1, 7, 'stezkou těch, jež zahrnuls milostí Svou, ne těch, na něž jsi rozhněván, ani těch, kdo v bludu jsou.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Alif lám mím!'),
(9, 2, 2, 'Toto Písmo, o němž pochyby není, je vedením pro bohabojné,'),
(10, 2, 3, 'kteří v nepoznatelné věří, modlitbu dodržují a z toho, co jsme jim uštědřili, rozdávají,'),
(11, 2, 4, 'kteří věří v to, co bylo sesláno tobě, i v to, co bylo sesláno před tebou, a pevně jsou o životě budoucím přesvědčeni,'),
(12, 2, 5, 'ti Pánem svým jsou správně vedení a ti budou blažení.'),
(13, 2, 6, 'Však věru je lhostejno pro nevěřící, zda napomínáš je, či nikoliv - stejně neuvěří!'),
(14, 2, 7, 'Bůh srdce jejich i sluch jejich zapečetil a přes oči jejich clonu položil a pro ně je určen trest hrozný.'),
(15, 2, 8, 'Jsou mezi lidmi někteří, kdož říkají: "Věříme v Boha i v den soudný," zatím však jsou nevěřící.'),
(16, 2, 9, 'Snaží se oklamat Boha a ty, kdož uvěřili, avšak klamou jen sami sebe, aniž o tom mají tušení.'),
(17, 2, 10, 'V srdcích jejich jest choroba a Bůh tuto chorobu rozmnožil; jim dostane se trestu bolestného za to, že lhali.'),
(18, 2, 11, 'A když se jim řekne: "Nešiřte pohoršení na zemi!", tu odpovídají: "My ji jen polepšujeme!"'),
(19, 2, 12, 'Což však to nejsou právě oni, kdož pohoršení šíří, ale nemají o tom ponětí?'),
(20, 2, 13, 'A když se jim řekne: "Uvěřte, jako již uvěřili tito lidé!", tu odpovídají: "Cožpak máme věřit tak, jako tito hlupáci?" Zdaž však nejsou právě oni hlupáci, jenže o tom nevědí?'),
(21, 2, 14, 'Když potkají ty, kdož uvěřili, říkají: "Uvěřili jsme", když však zůstanou sami se svými satany, hovoří: "Jsme s vámi a tamtěm se jen posmíváme."'),
(22, 2, 15, 'Bůh se jim však sám vysměje a ponechá je dlouho tápat jak slepé ve vzpurnosti jejich.'),
(23, 2, 16, 'To jsou ti, kdož koupili blud za správné vedení, však obchod jejich jim nepřinesl zisk a po správné cestě se neubírají.'),
(24, 2, 17, 'Podobají se tomu, jenž rozžehl oheň: když osvětleno jim bylo vše, co je kolem, Bůh odnesl jim světlo a nechal je v temnotě, nevidoucí.'),
(25, 2, 18, 'Hluší, němí a slepí - z bludu svého se nenavrátí!'),
(26, 2, 19, 'Anebo jako když objeví se na nebi mrak deštivý, plný temnoty, hromů a blesků: lidé si zacpávají prsty uši své před hromobitím, bojíce se smrti; avšak Bůh obklopuje nevěřící ze všech stran.'),
(27, 2, 20, 'Blesk div jim zrak neodňal, však dokud je blesky ozařují, kráčí při svitu jejich, a když nad nimi se zatmí, zastaví se. Kdyby Bůh chtěl, odňal by jim zrak i sluch, neb Bůh je nad věci každou všemocný.'),
(28, 2, 21, 'Lidé, uctívejte Pána svého, jenž stvořil vás i ty, kdož před vámi byli - snad budete bát se Boha,'),
(29, 2, 22, 'jenž pro vás učinil zemi lůžkem a nebesa kupolí a jenž seslal z nebe vodu a dal pomocí jí vyrůst plodům pro obživu vaši. Nedávejte Bohu rovných, vždyť dobře to víte!'),
(30, 2, 23, 'Jste-li na pochybách o tom, co jsme seslali služebníku svému, tedy přineste súru podobnou této a předvolejte si svědky své kromě Boha, jste-li pravdomluvní!'),
(31, 2, 24, 'Však neučiníte-li to - a vám se to věru nepodaří - pak střezte se ohně, jehož palivem jsou lidé a kamení a jenž připraven je pro nevěřící.'),
(32, 2, 25, 'Oznam radostnou zvěst těm, kdož uvěřili a zbožné skutky konali, že pro ně jsou připraveny zahrady, pod nimiž řeky tekou. A kdykoliv se jim za odměnu dostane z nich ovoce, tu řeknou: "Toto je jako to, co nám již dříve bylo uštědřeno," avšak dostalo se jim jen něco podobného. A budou tam mít manželky čisté? a budou tam nesmrtelní.'),
(33, 2, 26, 'Bůh neostýchá se věru uvádět v podobenství komára nebo to, co větší je než komár. Ti, kdož uvěřili, vědí, že to je pravda od Pána jejich, zatímco ti, kdož nevěří, hovoří: "Co chtěl Bůh říci tímto podobenstvím?" On nechává jím mnohé zbloudit, však mnohé jím správně vede - a budou tím uvedeni v blud jedině hanebníci,'),
(34, 2, 27, 'kteří porušují smlouvu Boží po jejím uzavření a rozdělují to, co Bůh přikázal spojit, a po zemi pohoršení šíří. A to jsou ti, kdož ztrátu utrpí.'),
(35, 2, 28, 'Jak to, že nevěříte v Boha? Když byli jste mrtví, On dal vám život, potom vás usmrtí a posléze znovu oživí a nakonec budete k Němu navráceni.'),
(36, 2, 29, 'On je ten, jenž pro vás stvořil vše, co na zemi je, potom se k nebi obrátil a v sedmi nebesích je vyrovnal. On věru všech věcí je znalý.'),
(37, 2, 30, 'Hle, Pán tvůj k andělům pravil: "Umístím na zemi náměstka!" I řekli: "Chceš tam ustanovit někoho, kdo pohoršení na ní bude šířit a krev na ní prolévat, zatímco my slávu Tvou pějeme a svatost Tvou prohlašujeme?" Pravil: "Já věru znám to, co vy neznáte!"'),
(38, 2, 31, 'A naučil Adama jména všechna, potom je předvedl andělům a řekl: "Sdělte mi jména těchto, jste-li pravdomluvní!"'),
(39, 2, 32, '"Sláva Tobě," řekli, "my máme vědění jen o tom, čemus nás Ty naučil, neboť Tys jediný vševědoucí, moudrý!"'),
(40, 2, 33, 'Pravil: "Adame, sděl jim jména toho všeho!" A když jim sdělil ta jména, pravil Bůh: "Neřekl jsem vám, že znám nepoznatelné na nebesích i na zemi a že znám dobře i to, co najevo dáváte, i to, co skrýváte?"'),
(41, 2, 34, 'A když řekli jsme andělům: "Padněte na zem před Adamem!", tu padli všichni kromě Iblíse, jenž to odmítl, zpychl a stal se jedním z nevěřících.'),
(42, 2, 35, 'I pravili jsme: "Adame, obývej s manželkou svou zahradu tuto! Jezte z ní podle libosti, kdekoliv se vám zachce, avšak nepřibližujte se k tomuto stromu, abyste se nestali nespravedlivými!"'),
(43, 2, 36, 'Avšak satan jim dal zhřešit a způsobil vyhnání jejich z místa, kde žili. I pravili jsme: "Odejděte! Jeden druhému budete nepřáteli; země bude místem pobytu vašeho a užívání dočasného!"'),
(44, 2, 37, 'Adam přijal pak od Pána svého slova a On mu odpustil, vždyť On odpouštějící je i slitovný.'),
(45, 2, 38, 'Pravili jsme: "Odejděte odtud všichni! A až k vám přijde ode Mne správné vedení, ti, kdož následovat budou vedení Mé, nemusí se bát a nebudou zarmouceni.'),
(46, 2, 39, 'Však ti, kdož neuvěří a znamení Naše za lež prohlásí, ti budou ohně obyvateli a budou v něm nesmrtelní."'),
(47, 2, 40, 'Dítka Izraele, pomněte milosti Mé, již jsem vám prokázal! Dodržujte věrně úmluvu se Mnou uzavřenou, tak jako Já ji dodržuji vůči vám, a jen Mne se obávejte!'),
(48, 2, 41, 'Věřte v to, co jsem seslal pro potvrzení pravdivosti toho, co již máte! Nebuďte prvními z těch, kdo nevěří v toto zjevení, a neprodávejte znamení Má za nízkou cenu! A buďte bohabojní!'),
(49, 2, 42, 'Neodívejte pravdu falší a nezakrývejte pravdu, když ji dobře znáte!'),
(50, 2, 43, 'Dodržujte modlitbu, rozdávejte almužnu a sklánějte se s těmi, kdož se sklánějí!'),
(51, 2, 44, 'Chcete přikazovat lidem zbožné skutky, zatímco vy sami na ně zapomínáte, ačkoliv Písmo odříkáváte? Což tomu neporozumíte?'),
(52, 2, 45, 'Hledejte pomoc v trpělivosti a v modlitbě! To věc je věru velmi obtížná, kromě pro pokorné,'),
(53, 2, 46, 'kteří soudí, že s Pánem svým se setkají a že k Němu se navrátí.'),
(54, 2, 47, 'Dítka Izraele! Pomněte milosti Mé, již jsem vám prokázal, i toho, že jsem vás vyznamenal před lidstvem veškerým.'),
(55, 2, 48, 'Střezte se dne, kdy duše žádná nebude odměněna ničím za duši jinou, kdy nebude od ní přijata přímluva žádná a nebude za ni vzato výkupné a nedostane se jim pomoci.'),
(56, 2, 49, 'Zachránili jsme vás před rodem Faraónovým, jenž zlé útrapy vám působil, zabíjeje syny vaše a nechávaje naživu jen ženy vaše. A byla v tom zkouška nesmírná od Pána vašeho!'),
(57, 2, 50, 'A hle, rozdělili jsme moře před vámi a zachránili jsme vás a utopili jsme rod Faraónův, zatímco vy jste na to hleděli.'),
(58, 2, 51, 'A hle, uzavírali jsme úmluvu s Mojžíšem po čtyřicet nocí a vy pak jste za nepřítomnosti jeho si vzali tele a stali jste se nespravedlivými.'),
(59, 2, 52, 'Potom jsme vám vše odpustili doufajíce, že snad budete vděční.'),
(60, 2, 53, 'A dali jsme věru Mojžíšovi Písmo a spásné rozlišení - snad jím budete správně vedeni.'),
(61, 2, 54, 'A hle, pravil Mojžíš lidu svému: "Lide můj, vy sami sobě jste ukřivdili, když tele jste si vzali. Obraťte se kajícně k Tvůrci svému a zabte hříšníky mezi sebou, to lepší bude pro vás před Tvůrcem vaším a On odpustí vám, neboť On odpouštějící je i slitovný!"'),
(62, 2, 55, 'A hle, řekli jste: "Mojžíši, neuvěříme ti, dokud nespatříme Boha zcela zřetelně!" A zasáhl vás blesk, zatímco jste se dívali.'),
(63, 2, 56, 'Potom jsme vás po smrti vaší opět vzkřísili doufajíce, že snad budete vděční.'),
(64, 2, 57, 'A dali jsme vás zastínit mračnu a seslali jsme vám manu a křepelky. "Jezte z těchto pokrmů výtečných, jež jsme vám uštědřili!" Neublížili nám, ale sami sobě ukřivdili.'),
(65, 2, 58, 'A hle, pravili jsme: "Vejděte do města tohoto a pojídejte tam, kdekoliv se vám zachce, až dosyta. Vejděte do bran, skloňte se a rcete:, Odpuštění!? A My odpustíme vám hříchy vaše a rozmnožíme dobrodiní těm, kdož dobré konali."'),
(66, 2, 59, 'Avšak ti, kdož byli nespravedliví, zaměnili slovo Naše něčím jiným, než bylo jim řečeno. A seslali jsme na ty, kdož nespravedliví byli, trest z nebes za to, že byli zkažení.'),
(67, 2, 60, 'A hle, poprosil Mojžíš o vodu pro lid svůj a pravili jsme: "Udeř holí svou do skály!" A vytrysklo z ní dvanáct pramenů, takže každý (z kmenů) věděl, kde jest mu píti. "Jezte a pijte z toho, co Bůh vám uštědřil, nebuďte však těmi, kdož na zemi šíří pohoršení!"'),
(68, 2, 61, 'A hle, řekli jste: "Mojžíši, nelze nám snášet stále potravu jedinou. Popros Pána svého za nás, aby pro nás dal vyrůst z toho, co plodí země, zelenině, okurkám, česneku, čočce a cibuli!" I řekl Mojžíš: "Přejete si vyměnit to, co lepší je, za mnohem horší? Sestupte do Egypta, tam budete mít to, co si žádáte!" A postihl je nedostatek a bída a pocítili hněv Boží, a to proto, že ve znamení Boží nevěřili a že proroky nespravedlivě zabíjeli. A také za to, že byli neposlušní a vzpurní.'),
(69, 2, 62, 'Věru ti, kdož uvěřili, a ti, kdož jsou židy, křesťany a sabejci, ti, kdož uvěřili v Boha a v den soudný a konali zbožné skutky - ti naleznou odměnu u Pána svého a nemusí mít strach a nebudou zarmouceni.'),
(70, 2, 63, 'A když jsme s vámi uzavřeli úmluvu a vztyčili nad vámi horu Sinaj, řekli jsme: "Přidržujte se toho, co jsme vám dali, co nejpevněji a zapamatujte si dobře, co obsahuje - snad budete bohabojní."'),
(71, 2, 64, 'Pak jste se však odvrátili, a kdyby nebylo přízně a milosrdenství Božího vůči vám, byli byste již věru mezi těmi, kdož ztrátu utrpěli.'),
(72, 2, 65, 'Zajisté znali jste ty mezi sebou, kdož sobotu znesvětili a jimž jsme řekli: "Buďte opicemi opovrženými!"'),
(73, 2, 66, 'A učinili jsme je trestem výstražným pro ty, kteří žili předtím i potom, i varováním pro bohabojné.'),
(74, 2, 67, 'A hle, pravil Mojžíš lidu svému: "Bůh vám přikazuje, abyste obětovali krávu!" Řekli: "Neděláš si z nás posměšky?" Odpověděl: "Utíkám se k Bohu, abych nebyl tak pošetilý!"'),
(75, 2, 68, 'Řekli: "Popros za nás Pána svého, aby nám vysvětlil, jaká má být!" Odpověděl: "Hle, Pán praví: A má to být kráva ani příliš stará, ani jalovice mladá, nýbrž věku středního mezi nimi. Učiňte tedy to, co bylo vám přikázáno!"'),
(76, 2, 69, 'Řekli: "Popros za Nás Pána svého, aby nám vysvětlil, jakou má mít barvu!" Odpověděl: "Hle, Pán praví: Nechť je to kráva žluté jasné barvy pro radost všem, kdož na ni se dívají!"'),
(77, 2, 70, 'Řekli: "Popros za nás Pána svého, aby nám vysvětlil, jaká ještě má být, neboť všechny krávy se nám zdají mezi sebou podobné; a pak budeme - jestliže Bohu se zachce - správně vedeni!"'),
(78, 2, 71, 'Odpověděl: "Hle, Pán praví: Nechť kráva ta není znavena obděláváním země a zavodňováním polí, ať je zachovalá, bez vady." Řekli: "Nyní jsi nám přinesl pravdu." A obětovali krávu, když to málem byli neučinili.'),
(79, 2, 72, 'A hle, když zabili jste někoho, sváděli jste to jeden na druhého. Však Bůh odhalil, co jste skrývali.'),
(80, 2, 73, 'Tehdy jsme řekli: "Udeřte mrtvého částí této krávy obětované!" A takto Bůh oživuje mrtvé a ukazuje vám znamení svá - snad budete rozumní.'),
(81, 2, 74, 'Vaše srdce se však od té doby zatvrdila a stala se jakoby kameny skal aneb ještě tvrdšími, neboť mezi skalami jsou takové, z nichž tryskají potoky, a jsou mezi nimi jiné, jež pukají a vytéká z nich voda, a další, jež převracejí se ze strachu před Bohem. A není Bohu lhostejné, co děláte!'),
(82, 2, 75, 'Jak můžete žádat, aby s vámi uvěřili tito lidé, z nichž část poté, co uslyšela slovo Boží, vědomě je překroutila, když mu porozuměla?'),
(83, 2, 76, 'A když potkají ty, kdož uvěřili, říkají: "Uvěřili jsme!", avšak když sami jsou mezi sebou, tu hovoří: "Chcete jim vyprávět o tom, co vám Bůh odkryl, aby tak měli důkazy proti vám ohledně toho před Pánem vaším? Což nebudete rozumní?"'),
(84, 2, 77, 'Cožpak nevědí, že Bůh dobře zná jak to, co skrývají, tak i to, co odhalují?'),
(85, 2, 78, 'Mezi nimi jsou lidé neučení, kteří neznají z Písma nic než "ameny" a kteří mají jen své domněnky.'),
(86, 2, 79, 'Běda těm, kteří Písmo vlastníma rukama sepisují a potom hovoří: "Toto od Boha pochází!", aby tak nakoupili za nízkou cenu. Běda jim za to, co psaly ruce jejich! Běda jim za to, co provádějí!'),
(87, 2, 80, 'A říkají: "Oheň se nás nedotkne, leda na dobu omezenou." Rci: "Uzavřeli jste snad o tom smlouvu s Bohem? Bůh nikdy nezruší smlouvu svou; anebo říkáte o Bohu něco, o čem nemáte ponětí?"'),
(88, 2, 81, 'Ba naopak ti, kdož zlého se dopustili, a ti, kdož obklopeni jsou hříchy svými, ti stanou se ohně obyvateli a budou v něm nesmrtelní.'),
(89, 2, 82, 'Ti však, kdož uvěřili a zbožné skutky konali, ti budou ráje obyvateli a budou tam nesmrtelní.'),
(90, 2, 83, 'A hle, uzavřeli jsme úmluvu s dítkami Izraele: "Nebudete uctívat nikoho kromě Boha jediného! Buďte dobří k rodičům, příbuzným, sirotkům a nuzným! Mluvte laskavě s lidmi, dodržujte modlitbu a dávejte almužnu!" Potom jste se však odvrátili - kromě několika málo z vás - a vzdálili.'),
(91, 2, 84, 'A hle, uzavřeli jsme s vámi úmluvu řkouce: "Nebudete prolévat krev vlastní a nebudete se vzájemně vyhánět z příbytků svých!" A vy jste to potom potvrdili a dosvědčili.'),
(92, 2, 85, 'A pak jste se stali těmi, kdo se mezi sebou zabíjejí, a vyhnali jste část lidu svého z obydlí jejich, pomáhajíce si vzájemně proti nim hříchem a bezprávím. A když k vám přijdou jako zajatci, vykupujete je a je vám zakázáno je vyhánět. Zdaž věříte v jednu část Písma, zatímco druhou popíráte? Jaká bude odměna těch z vás, kteří si takto počínají, ne-li hanba v životě pozemském; a v den zmrtvýchvstání budou uvrženi do muk nejhorších. A není Bohu lhostejné, co činíte.'),
(93, 2, 86, 'A to jsou ti, kdož koupili život pozemský za život budoucí; těm nebude trest ulehčen a nebude jim pomoženo.'),
(94, 2, 87, 'A již kdysi jsme dali Mojžíšovi Písmo a po něm jsme i jiné posly vyslali; a dali jsme Ježíšovi, synu Marie, důkazy jasné a podpořili jsme jej Duchem svatým. A kdykoliv přišel k vám posel s něčím, co duše vaše si nepřály, zpyšněli jste: jedny jste prohlásili za lháře a druhé jste zabili.'),
(95, 2, 88, 'I pravili: "Neobřezána jsou srdce naše!" Však nikoliv! Proklel je Bůh za nevíru jejich; jak málo oni věří!'),
(96, 2, 89, 'A když se jim nyní dostalo od Boha Písma, jež potvrzuje pravdivost zjevení, které již měli a přitom předtím prosili o rozhodnutí proti nevěřícím - když k nim tedy přišlo to, o čem již byli zpraveni, sami v to neuvěřili. Kéž prokletí Boží padne na nevěřící!'),
(97, 2, 90, 'Jak hnusné je to, zač prodali duše své! Nevěří v to, co seslal Bůh, z čiré závisti, že Bůh seslal něco z dobrodiní Svého těm ze služebníků Svých, jež si vyvolil. A uvalili na sebe hněv za hněvem, a trest ponižující je určen pro nevěřící.'),
(98, 2, 91, 'A když je jim řečeno: "Věřte v to, co nyní seslal Bůh!", tu hovoří: "Věříme jen v to, co bylo nám sesláno dříve!" A nevěří v to, co bylo sesláno později, ačkoliv je to pravda, jež potvrzuje pravdivost zjevení, která již mají. Rci: "Proč tedy jste zabíjeli dříve posly Boží, jste-li věřící?"'),
(99, 2, 92, 'Vždyť věru přišel k vám Mojžíš s jasnými znameními, a přesto jste si za jeho nepřítomnosti vzali tele a stali jste se nespravedlivými.'),
(100, 2, 93, 'A hle, uzavřeli jsme přece s vámi úmluvu a vztyčili nad vámi horu řkouce: "Přidržujte se toho, co jsme vám dali, co nejpevněji a poslouchejte! I řekli: "Slyšeli jsme a neuposlechli jsme." A byla srdce jejich napojena teletem za nevíru jejich. Rci: "Jak hnusné je to, co přikazuje vám víra vaše, jste-li věřící."'),
(101, 2, 94, 'Rci: "Je-li příbytek poslední u Boha vyhrazen jen pro vás a ne pro ostatní lidi, pak žádejte si smrt, jste-li pravdomluvní!"'),
(102, 2, 95, 'Však oni o to nikdy nepožádají kvůli tomu, co ruce jejich dříve spáchaly. A Bůh dobře zná nespravedlivé.'),
(103, 2, 96, 'Vskutku pak shledáš, že jsou to lidé, kteří nejvíce touží po životě, ba více než modloslužebníci; některý z nich by si přál, aby život jeho trval tisíc let. Avšak ani kdyby tak dlouho živ byl, neodvrátilo by to od něho trest - vždyť Bůh jasně zří, co dělají!'),
(104, 2, 97, 'Rci: "Kdo je nepřítelem Gabriela... neboť on seslal - z dovolení Božího - do srdce tvého zjevení jako potvrzení pravdivosti poselství i jako správné vedení i zvěst radostnou pro věřící.'),
(105, 2, 98, 'Ten, kdo je nepřítelem Boha a andělů a poslů Jeho a Gabriela a Michaela... vždyť i Bůh je nepřítelem nevěřících!"'),
(106, 2, 99, 'A seslali jsme ti věru znamení jasná, jež popírají pouze hanebníci.'),
(107, 2, 100, 'Což pokaždé, když uzavřou úmluvu nějakou, musí být mezi nimi část, která ji odvrhne? Nikoliv, většina z nich jsou nevěřící.'),
(108, 2, 101, 'A když přišel k nim nyní posel od Boha potvrzující pravdivost zjevení, které již měli, skupina z těch, jimž bylo dáno Písmo, odvrhla Písmo Boží za záda svá, jako by je neznali.'),
(109, 2, 102, 'A následovali to, co hlásali satani v době vlády Šalomounovy. Šalomoun nebyl nevěřící, avšak satani jimi byli, učíce lidi kouzelnictví a tomu, co bylo v Babylóně sesláno dvěma andělům, Hárútovi a Márútovi. Ti dva nezačali nikoho učit, aniž řekli: "My jsme toliko pokušitelé, nebuď tedy nevěřící!" A od nich dvou se naučili, jak se zasévá rozkol mezi muže a manželku jeho - avšak oni tím neuškodí nikomu, leda z dovolení Božího. A naučili se lidé tomu, co jim škodí, ale neprospívá; a věděli, že ten, kdo toto koupí, nebude mít podíl na životě budoucím. Jak hnusně pro sebe nakoupili - kéž by to byli věděli!'),
(110, 2, 103, 'Kdyby byli uvěřili a byli bohabojní, věru by byla odměna Boží lepší - kéž by to byli věděli!'),
(111, 2, 104, 'Vy, kteří věříte, neříkejte: "Rá`iná!"" nýbrž říkejte: "Unzurná!" Poslouchejte, neboť pro nevěřící je určen trest bolestný!'),
(112, 2, 105, 'Ti, kdož nevěří z vlastníků Písma a modloslužebníků, si nepřejí, aby na vás sestoupilo dobrodiní od Pána vašeho. Avšak Bůh vyznamenává milosrdenstvím Svým, koho chce - a Bůh vládcem je přízně nesmírné.'),
(113, 2, 106, 'Kdykoliv zrušíme verš nějaký či dáme ti naň zapomenout, přineseme jiný, lepší anebo podobný. Což nevíš, že Bůh je všech věcí mocen?'),
(114, 2, 107, 'Což nevíš, že Bohu náleží království nebes i země a že nemáte kromě Boha ochránce ani pomocníka žádného?'),
(115, 2, 108, 'Či chcete se vyptávat posla svého, tak jako byl předtím Mojžíš dotazován? Ten, kdo víru zaměňuje nevírou, ten zbloudil z cesty rovné.'),
(116, 2, 109, 'Mnozí z vlastníků Písma by si přáli učinit z vás znovu nevěřící poté, co dostalo se vám víry, ze závisti, jež je v nich, i když se pravda stala již zjevnou. Vymažte a odpusťte jim, pokud nepřijde Bůh s rozkazem Svým - a Bůh věru je mocný nade věcmi všemi.'),
(117, 2, 110, 'Dodržujte modlitbu a dávejte almužnu, neboť cokoliv dobrého pro sebe připravíte, to u Boha naleznete - a Bůh jasně vidí, co děláte.'),
(118, 2, 111, 'Říkají: "Nevejde do ráje nikdo kromě toho, kdo je židem či křesťanem!" Takové je jen přání jejich; ty však rci: "Přineste mi důkazy své, jste-li pravdomluvní!"'),
(119, 2, 112, 'Však nikoliv! Ten, kdo odevzdaně se obrací k tváři Boží a koná dobré, ten nalezne odměnu u Pána svého a nemusí mít strach a nebude zarmoucen.'),
(120, 2, 113, 'Říkají židé: "Křesťané nemají v ničem pravdu!" a křesťané zase tvrdí: "Židé nemají v ničem pravdu!" a přitom přec ti i oni čtou Písmo. A hovoří ti, kdož nic nevědí, podobně jako oni. V den zmrtvýchvstání však Bůh rozsoudí mezi nimi to, oč se hádali.'),
(121, 2, 114, 'Kdo je nespravedlivější než ten, jenž zabraňuje, aby se v modlitebnách Božích vzývalo jméno Jeho, a snaží se o jejich zničení? Patřilo by se, aby do nich vstupovali naplněni bázní; hanba jim bude patřit na tomto světě a v životě budoucím je trest strašný očekává.'),
(122, 2, 115, 'Bohu náleží východ i západ, a ať se obrátíte kamkoliv, všude je tvář Boží. A Bůh věru je všezahrnující, vševědoucí!'),
(123, 2, 116, 'A říkají: "Bůh vzal si dítě!" Při slávě Jeho, není tomu tak! Jemu náleží vše, co na nebesích i zemi je, a všichni se před ním pokorně sklánějí!'),
(124, 2, 117, 'On stvořitelem je nebes a země, a když rozhodne věc nějakou, pak řekne toliko "Staniž se!" a stane se.'),
(125, 2, 118, 'Říkají ti, kdož nic nevědí: "Proč pak s námi Bůh nepromluví anebo proč k nám nepřijde se znamením?" A hovořili ti, kdož byli před nimi, jazykem podobným a i srdce jejich jsou si podobna. A My ukázali jsme znamení jasná lidu, jenž je pevně přesvědčen.'),
(126, 2, 119, 'Seslali jsme tě s pravdou jako hlasatele zvěsti radostné i jako varovatele, však nebudeš zodpovědný za ty, kdož pekla obyvateli budou.'),
(127, 2, 120, 'Nebudou s tebou spokojeni ani židé, ani křesťané, pokud nebudeš následovat náboženství jejich. Rci: "Vedení Boží je jediné správné vedení!" Budeš-li však následovat učení jejich scestná poté, co dostalo se ti vědění, nenalezneš pak proti Bohu ani ochránce, ani pomocníka žádného.'),
(128, 2, 121, 'Ti, jimž jsme dali Písmo a kteří je čtou způsobem pravdivým, ti v ně uvěřili, zatímco ti, kdož v ně nevěří, ti ztrátu utrpí.'),
(129, 2, 122, 'Dítka Izraele, pomněte milosti Mé, jíž jsem vás zahrnul, i toho, že vyznamenal jsem vás před lidstvem veškerým!'),
(130, 2, 123, 'Bojte se dne, kdy duše žádná nebude odměněna ničím za duši jinou a nebude od ní přijato výkupné žádné a nebude jí přímluva nic platná a nebude jí pomoženo!'),
(131, 2, 124, 'A hle, Pán zkoušel Abrahama slovy, a když je on splnil, řekl Pán: "Věru tě učiním vůdcem pro lidi." I zeptal se: "A co potomstvo mé?" Pravil: "Úmluva Má je pro nespravedlivé neplatná."'),
(132, 2, 125, 'A hle, učinili jsme chrám tento místem návštěv i útočištěm pro lidi a řekli jsme: "Učiňte si místo Abrahamovo modlitebnou!" A uložili jsme Abrahamovi a Ismaelovi v úmluvě, aby udržovali chrám Můj v čistotě pro ty, kdož obcházení konají, kdož se u něho zdržují a kdož se sklánějí a na tvář padají.'),
(133, 2, 126, 'A hle, pravil Abraham: "Pane můj, učiň zemi tuto bezpečnou a obdaruj obyvatele její plody - ty z nich, kdož v Boha a v den soudný uvěřili!" I odvětil Pán: "Těm pak, kdož neuvěřili, těm krátké užívání poskytnu a potom je do trestu pekelného uvrhnu. Jak hnusný to bude cíl konečný!"'),
(134, 2, 127, 'Když pak Abraham a Ismael kladli základy chrámu, zvolali: "Pane náš, přijmi jej od nás, vždyť Tys věru slyšící, vševědoucí!'),
(135, 2, 128, 'Pane náš, učiň, ať jsme do vůle Tvé odevzdáni, a učiň z potomků našich obec Tobě oddanou; ukaž nám obřady naše a vyslyš pokání naše, vždyť Tys věru odpouštějící, slitovný!'),
(136, 2, 129, 'Pane náš, vyšli mezi obyvatele města tohoto posla ze středu jejich, aby jim sděloval znamení Tvá, naučil je Písmu a vědění a očistil je, vždyť Tys věru mocný i moudrý!"'),
(137, 2, 130, 'A kdo jiný může mít odpor vůči náboženství Abrahamovu než ten, kdo v duši své je pošetilý? My vyvolili jsme si Abrahama již na tomto světě a na onom světě on věru je mezi bezúhonnými.'),
(138, 2, 131, 'A hle, když Pán jeho mu pravil: "Odevzdej se do vůle Mé!", odvětil: "Odevzdávám se do vůle Pána lidstva veškerého."'),
(139, 2, 132, 'A odkázal víru tuto Abraham synům svým, a také Jakub pravil: "Synové moji, Bůh pro vás věru vyvolil náboženství, a proto nesmíte zemřít, aniž se do vůle Jeho odevzdáte!"'),
(140, 2, 133, 'Byli jste svědky toho, když smrt přišla k Jakubovi a když se otázal synů svých: "Co budete uctívat po smrti mé?" Odvětili: "Budeme uctívat Boha tvého a Boha předků tvých, Abrahama, Ismaela a Izáka, jako Boha jediného a do vůle Jeho budeme odevzdáni."'),
(141, 2, 134, 'Obec tato již zmizela; jí dostane se toho, co si vysloužila, a i vám patří to, co jste si vysloužili. A nebudete tázáni na to, co oni dělali.'),
(142, 2, 135, 'Říkají: "Buďte židy, nebo křesťany, a tehdy budete správnou cestou vedeni!" Odpověz: "Nikoliv! Následujte náboženství Abrahama, jenž hanífem byl a mezi modloslužebníky nepatřil!"'),
(143, 2, 136, 'Rcete: "Uvěřili jsme v Boha a v to, co seslal nám, i v to, co seslal Abrahamovi, Ismaelovi, Izákovi, Jakubovi a kmenům, i v to, co bylo dáno Mojžíšovi, Ježíšovi, a v to, co bylo dáno prorokům od Pána jejich. My nečiníme rozdíl mezi nimi a do vůle Pána svého jsme odevzdáni!"'),
(144, 2, 137, 'Jestliže uvěří v něco podobného tomu, čemu vy věříte, pak půjdou po cestě správné; jestliže se však odvrátí, pak věru jsou v rozkolu. Však Bůh ti proti nim postačí, vždyť Bůh je věru slyšící vševědoucí!'),
(145, 2, 138, 'Toto je pomazání Boží, a kdo lepší je než Bůh v pomazání? A Jemu sloužíme.'),
(146, 2, 139, 'Rci: "Chcete se s námi přít o Bohu? Vždyť On Pánem je naším i Pánem je vaším, my skutky své máme a vy skutky své máte. My jen Jemu zasvěcujeme svou víru upřímnou.'),
(147, 2, 140, 'Chcete snad říci, že Abraham, Ismael, Izák, Jakub a kmeny byli židé či křesťané?" Rci: "Kdo to ví lépe, vy, anebo Bůh? Kdo je nespravedlivější než ten, kdo pro sebe ukrývá svědectví, jehož se mu dostalo od Boha? A není Bohu lhostejné, co děláte!"'),
(148, 2, 141, 'Obec tato již zmizela; jí dostane se toho, co si vysloužila, a i vám patří to, co jste si vysloužili. A nebudete tázáni na to, co oni dělali.'),
(149, 2, 142, 'Budou říkat hlupáci mezi lidmi: "Co odvrátilo je od qibly jejich, k níž dříve se obraceli?" Odpověz: "Bohu patří východ i západ a On vede, koho chce, ke stezce přímé."'),
(150, 2, 143, 'A takovýmto způsobem jsme vás učinili obcí vzdálenou krajností, abyste byli svědky o lidech a aby posel byl svědkem o vás. A ustanovili jsme qiblu, k níž až dosud jste se obraceli, jedině proto, abychom rozeznali ty, kdož následují posla, od těch, kdož odvracejí se od stop jeho. A není to nesnází velkou pro ty, které vede Bůh, vždyť Bůh nechce, aby zanikla víra vaše, neboť Bůh vůči lidem je blahovolný a slitovný.'),
(151, 2, 144, 'Vídáme tě často obracet tvář svou k nebi sem a tam a obrátíme tě proto ke qible, jež se ti bude líbit. Obrať tedy tvář svou směrem k Mešitě posvátné! A kdekoliv budete, obracejte tvář svou ve směru jejím! Věru ti, jimž dostalo se Písma, vědí dobře, že to je pravda od Pána jejich přicházející. A není Bohu lhostejné, co dělají.'),
(152, 2, 145, 'A i kdybys přinesl těm, jimž dostalo se Písma, jakékoliv znamení, nebudou se řídit qiblou tvou a ty se nebudeš řídit qiblou jejich. A někteří z nich se ani neřídí qiblou ostatních. Budeš-li pak následovat učení jejich scestná poté, co dostalo se ti vědění, budeš věru jedním z nespravedlivých.'),
(153, 2, 146, 'Ti, jimž jsme dali Písmo, je znají tak, jako znají syny své. A přece jedna skupina z nich utajuje pravdu zcela vědomě.'),
(154, 2, 147, 'Pravda přichází od Pána tvého, nebuď tedy jedním z pochybovačů!'),
(155, 2, 148, 'Každý má směr svůj, k němuž se obrací. Snažte se vzájemně překonávat v dobrých skutcích! Ať již jste kdekoliv, Bůh vás všechny přivede a Bůh věru nad věcmi všemi moc má.'),
(156, 2, 149, 'A ať vyjdeš odkudkoliv, obracej tvář svou směrem k Mešitě posvátné, neb ona je vskutku pravdou od Pána tvého. A není Bohu lhostejné, co děláte.'),
(157, 2, 150, 'A ať vyjdeš odkudkoliv, obracej tvář svou směrem k Mešitě posvátné; a kdekoliv budete, obracejte tváře své ve směru jejím, aby lidé neměli důvod vám něco vytknout, kromě těch z nich, kdož jsou nespravedliví, avšak těch se neobávejte! Obávejte se Mne, abych mohl dovršit dobrodiní své vůči vám - snad budete správnou cestou vedeni!'),
(158, 2, 151, 'A takto jsme vám poslali posla ze středu vašeho, který vám přednáší znamení Naše, očišťuje vás, učí vás Písmu a moudrosti a učí vás tomu, co jste vůbec neznali.'),
(159, 2, 152, 'Buďte Mne pamětlivi a já budu pamětliv vás, děkujte Mi a nebuďte nevděční.'),
(160, 2, 153, 'Vy, kteří věříte, hledejte pomoc v trpělivosti a v modlitbě, vždyť Bůh je věru s trpělivými.'),
(161, 2, 154, 'A neříkejte o těch, kdož byli zabiti na stezce Boží: "Jsou mrtvi!" Nikoliv; oni živi jsou, avšak vy to netušíte.'),
(162, 2, 155, 'Budeme vás zkoušet trochu strachem a hladem a ztrátou na majetku i lidech a plodech - avšak oznam zvěst radostnou trpělivým,'),
(163, 2, 156, 'kteří, když postihne je neštěstí, hovoří: "My Bohu patříme a my k Němu se navrátíme!"'),
(164, 2, 157, 'A to jsou ti, jimž patří požehnání a milosrdenství Pána jejich, to jsou ti, kdož správnou cestou jsou vedeni.'),
(165, 2, 158, 'As-Safá a al-Marwa věru patří k posvátným místům Božím; a kdo plní povinnost pouti k Chrámu nebo koná malou pouť, ten nedopustí se hříchu, když vykoná obcházení okolo obou těch míst. A kdokoliv dobrovolně koná dobré,... vždyť Bůh je za díky uznalý a vševědoucí.'),
(166, 2, 159, 'Ty, kdož zatajují to, co jsme seslali z jasných důkazů a správného vedení, poté, co jsme je lidem v Písmu vysvětlili, věru ty Bůh prokleje a proklejí je i proklínající,'),
(167, 2, 160, 'kromě těch, kdož kajícně se obrátí, polepší se a jasně to projeví; těm popřeji sluchu, vždyť Já jsem vyslyšující, slitovný.'),
(168, 2, 161, 'Nad těmi, kdož jsou nevěřící a jako nevěřící zemřou, bude vyneseno prokletí Boha, andělů i lidí všech'),
(169, 2, 162, 'a budou v něm navěky a nebude jim trest ulehčen a nedostane se jim odkladu.'),
(170, 2, 163, 'Váš Bůh je Bůh jediný a není božstva kromě Něho, milosrdného, slitovného.'),
(171, 2, 164, 'Vskutku je ve stvoření nebes a země, ve střídání noci a dne, v lodi, jež pluje po moři s tím, co užitečné je lidem, ve vodě, již Bůh z nebe sesílá a kterou znovu oživuje zem po smrti její - a na ní všechna zvířata se rozptylují - a v pohybu větrů a oblacích podrobených mezi nebem a zemí - v tom všem jsou věru znamení pro lid rozumný.'),
(172, 2, 165, 'Jsou mezi lidmi někteří, kdož berou si vedle Boha jiné, jemu na úroveň postavené, a které milují, jako milují Boha. Avšak ti, kdož uvěřili, ti v lásce své k Bohu jsou silnější. Kéž by viděli ti, kdož jsou nespravedliví, až spatří trest, že síla je jedině u Boha a že Bůh strašný je ve svém trestání!'),
(173, 2, 166, 'A až se zřeknou ti, kteří byli následováni, těch, kdož je následovali, a až spatří trest a svazky jejich vzájemné budou zpřetrhány,'),
(174, 2, 167, 'zvolají ti, kdož je následovali: "Kéž bychom se mohli navrátit a zříci se jich tak, jako oni se nyní zříkají nás!" A takto jim Bůh ukáže činy jejich k zármutku jejich. A nebude pro ně úniku z ohně pekelného!'),
(175, 2, 168, 'Lidé, jezte dovolené a dobré z toho, co na zemi je, a nenásledujte stopy satanovy, vždyť on vaším nepřítelem je zjevným!'),
(176, 2, 169, 'On přikazuje vám jen špatnosti a hanebnosti, a abyste o Bohu říkali něco, o čem nemáte ponětí.'),
(177, 2, 170, 'A když je nevěřícím řečeno: "Následujte to, co Bůh seslal!", odpovídají: "Nikoliv, my budeme následovat zvyklosti, jež u otců svých jsme nalezli." Avšak což když otcové jejich ničemu nerozuměli a nebyli správnou cestou vedeni?'),
(178, 2, 171, 'Ti, kdož jsou nevěřící, se podobají dobytku, na nějž se volá a který slyší jedině křik a výzvy: hluší, němí, slepí - ničemu nerozumějí.'),
(179, 2, 172, 'Vy, kteří věříte, jezte z výtečných pokrmů, jež jsme vám uštědřili, a děkujte Bohu, jste-li Jeho služebníky'),
(180, 2, 173, 'Bůh zakázal vám jedině zdechliny, krev, vepřové maso a to, co bylo zasvěceno někomu jinému než Bohu. Kdo však byl donucen jísti toto, aniž po tom toužil nebo přestupníkem byl, na tom nebude hříchu, vždyť Bůh je odpouštějící, slitovný.'),
(181, 2, 174, 'Ti, kdož zatajují to, co Bůh seslal z Písma, a prodávají to za nízkou cenu, ti budou krmit břicha svá jen ohněm a Bůh s nimi vůbec nepromluví v den soudný a neočistí je; pro ně určen je trest bolestný.'),
(182, 2, 175, 'To jsou ti, kdož koupili zbloudění místo stezky přímé a potrestání místo odpuštění. Jak budou pak trpěliví vůči ohni?'),
(183, 2, 176, 'A to je proto, že Bůh seslal Písmo pravdu obsahující; ti, kdož se o Písmo hádají, ti věru jsou v rozkolu hlubokém.'),
(184, 2, 177, 'Zbožnost nespočívá v tom, že obracíte tváře své směrem k východu či západu, nýbrž zbožný je ten, kdo uvěřil v Boha, v den soudný, v anděly, Písmo a proroky a rozdává z majetku svého bez ohledu na lásku k Němu příbuzným, sirotkům, nuzným, po cestě (Boží) jdoucímu, žebrákům a na otroků vykoupení, a ten, kdo modlitbu dodržuje a almužnu udílí. A zbožní jsou ti, kdož úmluvy své dodržují, když je jednou uzavřeli a v protivenství, v neštěstí a v dobách násilí jsou trpěliví - to jsou ti, kdož víru pravou mají, a to jsou bohabojní.'),
(185, 2, 178, 'Vám, kteří věříte, jest předepsán zákon krevní msty za zabití: muž svobodný za muže svobodného, otrok za otroka, žena za ženu. A bude-li komu sleveno něco bratrem jeho, pak nechť následován je zvyk uznaný a budiž zaplacení přijato s dobrou vůlí. A toto je ulehčení a milost od Pána vašeho; kdo pak přestoupí toto nařízení, tomu dostane se trestu bolestného.'),
(186, 2, 179, 'V zákonu krevní msty je pro vás život, ó nadaní rozmyslem! Snad budete bohabojní!'),
(187, 2, 180, 'Jest vám předepsáno, že když dostaví se smrt k některému z vás a on zanechá vám majetek, má učinit závěť ve prospěch rodičů a příbuzných podle zvyklosti uznané - a to je povinností pro bohabojné.'),
(188, 2, 181, 'A kdo změní závěť poté, co ji uslyšel, bude potrestán a vina za změnu tuto padne jen na ty, kdož tak učinili, vždyť Bůh je věru slyšící, všemohoucí.'),
(189, 2, 182, 'Ten, kdo se obává, že odkazující byl zaujatý nebo dopustil se chyby, a přičiní se o urovnání mezi dědici, na tom nebude lpět hřích žádný, vždyť Bůh je věru odpouštějící, slitovný.'),
(190, 2, 183, 'Vy, kteří věříte, předepsán vám jest půst, tak jako byl již předepsán těm, kdož před vámi byli - snad budete bohabojní!'),
(191, 2, 184, 'Postěte se jen po určený počet dní! Ten z vás, kdo je nemocen anebo na cestách, nechť postí se stejný počet dní později. Ti, kdož mohou se postit, však nečiní tak, ti nechť se vykoupí nakrmením jednoho chudého. Kdo však dobrovolně učiní více dobrého, bude to přičteno k jeho dobru. A půst je pro vás lepší - jste-li rozumní.'),
(192, 2, 185, 'Postním měsícem je měsíc ramadán, v němž Korán byl seslán jako vedení správné pro lidi a jako vysvětlení tohoto vedení i jako spásné rozlišení. Kdo z vás spatří na vlastní oči měsíc, nechť se postí! Ten z vás, kdo je nemocen nebo na cestách, nechť postí se stejný počet dní později; Bůh si přeje vám to usnadnit a nechce na vás obtížné. Dodržujte plně tento počet a chvalte Boha za to, že správně vás vede - snad budete vděční!'),
(193, 2, 186, 'Když se tě zeptají služebníci Moji na Mne, věz, že jsem blízko a že odpovím na prosbu prosícího, když mne poprosí. Nechť však na výzvu Mou odpoví a nechť ve Mne věří - snad půjdou cestou správnou!'),
(194, 2, 187, 'Jest vám dovoleno přiblížit se v noci postní k manželkám svým - ony jsou pro vás oděvem a vy jste oděvem pro ně. Bůh poznal, že křivdíte sami sobě, avšak obrátil se k vám a odpustil vám. Nyní tedy souložte s nimi a usilujte o to, co Bůh vám předepsal. Jezte a pijte až do chvíle, kdy od sebe rozeznáte bílou a černou nit na úsvitu, potom dodržujte plný půst až do noci. A nesouložte s nimi, zatímco se zdržujete v rozjímání v modlitebnách! Toto jsou ohraničení Boží, nepřibližujte se k nim! A takto Bůh činí srozumitelnými znamení svá pro lidi - snad budou bohabojní!'),
(195, 2, 188, 'Nepohlcujte vzájemně majetky své podvodným způsobem! Nedávejte z nich dary soudcům, abyste tak hříšně připravili lidi o část majetku, jsouce si toho vědomi!'),
(196, 2, 189, 'A budou se tě ptát na novoluní. Odpověz: "To jsou znamení času pro lidi a pro stanovení poutě." Zbožnost nespočívá v tom, že přicházíte do domů svých zadní stranou, nýbrž zbožnost je u těch, kdož jsou bohabojní. Vcházejte do domů svých předními dveřmi a bojte se Boha - snad budete blažení!'),
(197, 2, 190, 'A bojujte na stezce Boží proti těm, kdož bojují proti vám, avšak nečiňte bezpráví, neboť Bůh nemiluje ty, kdož se bezpráví dopouštějí.'),
(198, 2, 191, 'Zabíjejte je všude, kde je dostihnete, a vyžeňte je z míst, odkud oni vás vyhnali, vždyť svádění od víry je horší než zabití. Avšak nebojujte s nimi poblíže Mešity posvátné, dokud oni s vámi zde nezačnou bojovat. Jestliže však vás tam napadnou, zabte je - taková je odměna nevěřících!'),
(199, 2, 192, 'Jestliže však přestanou... tedy Bůh věru je odpouštějící, slitovný!'),
(200, 2, 193, 'A bojujte proti nim, dokud nebude konec svádění od víry a dokud nebude všechno náboženství patřit Bohu. Jestliže však přestanou, pak skončete nepřátelství, ale ne proti nespravedlivým.'),
(201, 2, 194, 'Měsíc posvátný proti měsíci posvátnému; i věci posvátné podléhají zákonu krevní msty! Kdokoliv proti vám vystoupí nepřátelsky, pak i vy proti němu tak vystupte, podobně jako on vystupoval proti vám. A bojte se Boha a vězte, že Bůh stojí na straně bohabojných.'),
(202, 2, 195, 'Rozdávejte na stezce Boží, avšak nevrhejte se do zkázy vlastníma rukama! Konejte dobré skutky, neboť Bůh miluje ty, kdož dobré skutky konají.'),
(203, 2, 196, 'Konejte pečlivě pouť i malou pouť kvůli Bohu! Je-li vám v tom zabráněno, tedy se vykupte tím, co je pro vás snadné jako oběť! Neholte si hlavy své, pokud oběť nedospěje na místo své! Bude-li někdo z vás nemocen nebo postižen bolestí hlavy, a oholí se, ten se může vykoupit postem nebo almužnou či obětním darem. A jste-li bezpečni, ať ten, kdo použil příležitosti vykonat malou pouť spolu s velkou, se vykoupí tím, co je pro něj snadné jako oběť. Kdo nenalezne obětní zvíře, ať se vykoupí třídenním postem během pouti a sedmidenním po návratu svém, úhrnem plných deset dní. To platí pro toho, jehož rodina není přítomna v Mešitě posvátné. A bojte se Boha a vězte, že Bůh je strašný ve svém trestání!'),
(204, 2, 197, 'Pouť budiž konána v měsících známých. Ten, kdo se zavázal vykonat v nich pouť, nechť zdržuje se žen, prostopášnosti a hádek během pouti! Ať cokoliv dobrého učiníte, Bůh se o tom dozví; a zásobte se na cestu, však nejlepší zásobou na cestu je bohabojnost! Buďte tedy bohabojní, vy, kdož nadaní jste rozmyslem!'),
(205, 2, 198, 'Není pro vás hříchem, budete-li usilovat o přízeň Pána svého na trzích pouti. A když provádíte běh z Arafátu, vzpomínejte Boha poblíže místa posvátného! Vzpomeňte, že On vás uvedl na správnou cestu, zatímco předtím jste byli z těch, kdož bloudí.'),
(206, 2, 199, 'Potom proveďte běh tudy, kudy jej provádějí ostatní lidé, a proste Boha za odpuštění, neboť Bůh věru je odpouštějící, slitovný!'),
(207, 2, 200, 'A když skončíte poutní obřady své, vzpomeňte Boha, tak jako vzpomínáte na otce své, a ještě usilovně ji! A jsou mezi lidmi někteří, kdož říkají: "Pane náš, obdaruj nás na tomto světě," ale ti věru nebudou mít podíl žádný v životě budoucím.'),
(208, 2, 201, 'A jsou mezi nimi někteří, kdož říkají: "Pane náš, daruj nám dobré na tomto světě i dobré v životě budoucím a ušetří nás muk trestu ohnivého!"'),
(209, 2, 202, 'Těm dostane se podílu z toho, co si vysloužili, neboť Bůh věru jest rychlý v účtování.'),
(210, 2, 203, 'Vzpomínejte Boha ve stanovených dnech! A kdo si pospíší o dva dny, nebude to hřích, a kdo se opozdí o dva dny, ani to nebude hříchem! A to platí pro toho, kdo je bohabojný. A bojte se Boha a vězte, že u Něho budete shromážděni.'),
(211, 2, 204, 'A mezi lidmi jest i takový, jehož řeči o životě pozemském se ti líbí a jenž dovolává se svědectví Božího o tom, co je v srdci jeho; avšak přitom je to protivník nejtvrdošíjnější.'),
(212, 2, 205, 'A když se odvrátí zády, chodí po zemi, aby šířil pohoršení a ničil sadbu i dobytek; Bůh však nemiluje pohoršení.'),
(213, 2, 206, 'A když se mu řekne "Boj se Boha!", zmocní se ho hříšná pýcha. A údělem jeho bude peklo, a jak hnusné to bude lože!'),
(214, 2, 207, 'Mezi lidmi jest i takový, jenž prodá i sebe sama v úsilí dosáhnout zalíbení Božího - a Bůh věru je blahovolný k služebníkům svým.'),
(215, 2, 208, 'Vy, kteří věříte, vejděte všichni v mír a nenásledujte kroky satanovy, vždyť on je pro vás nepřítelem zjevným!'),
(216, 2, 209, 'A jestliže klopýtnete poté, co se vám dostalo jasných znamení, tedy vězte, že Bůh je mocný i moudrý!'),
(217, 2, 210, 'Což mohou čekat něco jiného, než že k nim přijde Bůh a andělé v temnotě mračen? Pak bude věc rozhodnuta; a k Bohu vracejí se věci všechny.'),
(218, 2, 211, 'Zeptej se dítek Izraele na to, kolik jsme jim dali znamení jasných! Jestliže však někdo zfalšuje milost Boží poté, co k němu přišla, pak je Bůh strašný ve svém trestání.'),
(219, 2, 212, 'Byl zkrášlen život pozemský těm, kdož nevěří, a ti se posmívají těm, kdož uvěřili. Avšak ti, kdož jsou bohabojní, budou nad ně povýšeni v den zmrtvýchvstání. Bůh věru uštědřuje, komu chce, bez počítání!'),
(220, 2, 213, 'Lidé byli národem jediným a poslal Bůh proroky jako hlasatele zvěsti radostné i jako varovatele a seslal s nimi Písmo s pravdou, aby rozsoudil mezi lidmi to, v čem se rozcházeli. A byli ve sporu jedině ti, jimž bylo Písmo darováno poté, co přišla k nim znamení jasná, a to ze vzájemné závisti. A nyní Bůh přivedl z dovolení Svého ty, kdož uvěřili, k pravdě, o níž se dříve rozcházeli v názorech. A Bůh vede, koho chce, ke stezce přímé.'),
(221, 2, 214, 'Domníváte se, že vstoupíte do ráje, ač jste dosud nezakusili nic podobného tomu, co zakusili ti, kdož byli před vámi? Byli postiženi neštěstím a bídou a třásli se strachem, až konečně posel a ti, kdož s ním uvěřili, zvolali: "Kdy přijde vítězná pomoc Boží?" Což není pomoc Boží blízká?'),
(222, 2, 215, 'Dotazují se tě na to, co mají rozdávat. Rci: "To, co rozdáváte z majetku, je pro rodiče, příbuzné, sirotky, nuzné a jdoucího po cestě Boží. A cokoliv dobrého učiníte, Bůh zajisté o tom ví."'),
(223, 2, 216, 'A je vám předepsán také boj, i když je vám nepříjemný. Je však možné, že je vám nepříjemné něco, co je pro vás dobré, a je možné, že milujete něco, co je pro vás špatné; jedině Bůh to zná, zatímco vy to neznáte.'),
(224, 2, 217, 'Dotazují se tě na posvátný měsíc a na bojování v jeho průběhu. Rci: "Bojovat v něm je hřích těžký, avšak odvracet od stezky Boží, nevěřit v Něj a v Mešitu posvátnou a vyhánět obyvatele jejich z ní je ještě těžší hřích před Bohem; svádět od víry je horší než zabíjet. Nevěřící nepřestanou proti vám bojovat, dokud vás neodvrátí od náboženství vašeho, budou-li toho schopni. Pro ty z vás, kdož od náboženství svého odpadnou a jako nevěřící zemřou, marné bude jejich konání na tomto i onom světě - ti ohně obyvateli se stanou a nesmrtelní v něm budou.'),
(225, 2, 218, 'Avšak ti, kdož uvěřili, vystěhovali se a na cestě Boží bojují, ti mohou doufat v milost Boží - a Bůh věru je odpouštějící, slitovný."'),
(226, 2, 219, 'Dotazují se tě na víno a na hru majsir. Rci: "V obou z nich je pro lidi hřích těžký i užitek, avšak jejich hřích je větší než užitek." A dotazují se tě na to, co mají rozdávat. Rci: "Přebytek!" A takto vám Bůh objasňuje znamení - snad budete přemýšlet'),
(227, 2, 220, 'o tomto i o onom světě. A dotazují se tě na sirotky; odpověz: "Spravedlivě se k nim chovat je dobré; jestliže s nimi jednáte, nechť jsou jako bratři vaši!" Bůh rozeznává dobře toho, kdo pohoršení šíří, od toho, kdo dobré skutky činí. A kdyby chtěl, mohl by vás věru postihnout neštěstím, neboť Bůh je věru mocný, moudrý.'),
(228, 2, 221, 'Nevstupujte ve sňatek s modloslužebnicemi, pokud neuvěří; a věřící otrokyně je věru lepší než modloslužebnice, i když se vám tato více líbí. Neprovdávejte dcery své za modloslužebníky, pokud tito neuvěří; a věřící otrok je věru lepší než modloslužebník, i když se vám tento více líbí. Takoví lidé vás zvou do ohně pekelného, zatímco Bůh vás zve do ráje a k odpuštění, s dovolením Svým, a objasňuje znamení Svá lidem - snad si to připomenou!'),
(229, 2, 222, 'A dotazují se tě na menstruaci. Rci: "To je neduh; zdržujte se tedy žen svých po dobu menstruace a nepřibližujte se k nim, dokud se neočistí. A když se očistí, přicházejte k nim tak, jak Bůh vám to přikázal. A Bůh věru miluje ty, kdož se kají, a miluje ty, kdož se očišťují!"'),
(230, 2, 223, 'Ženy vaše jsou pro vás polem; vcházejte tedy na pole své, odkud chcete, však učiňte předtím něco pro duše své a bojte se Boha a vězte, že se s Ním setkáte! Oznam tuto zvěst radostnou věřícím!'),
(231, 2, 224, 'A nečiňte Boha předmětem přísah svých, že budete zbožní a bohabojní a že se budete snažit o zlepšení vztahů mezi lidmi, neboť Bůh věru je slyšící, vševědoucí.'),
(232, 2, 225, 'Bůh vás nepotrestá za nevážnost řeči vaší v přísahách, ale potrestá vás za to, co srdce vaše si vysloužila. Bůh věru je odpouštějící, laskavý.'),
(233, 2, 226, 'Těm, kteří přísahají, že rozvedou se svými ženami, těm určena je lhůta čekací čtyř měsíců. Jestliže však odvolají přísahu svou, vždyť Bůh je odpouštějící, slitovný.'),
(234, 2, 227, 'Jestliže se však rozhodnou pro rozvod, vždyť Bůh je věru slyšící, vševědoucí.'),
(235, 2, 228, 'A rozvedené ženy nechť samy čekají po tři periody a není jim dovoleno zatajovat to, co Bůh stvořil v lůnech jejich, věří-li v Boha a v den soudný. A je spravedlivější, jestliže manželé jejich si je vezmou nazpět v tomto stavu, pokud si přejí usmíření. A ony mají pro sebe stejné právo jako oni podle zvyklosti, nicméně muži jsou o stupeň výše - a Bůh je mocný, moudrý.'),
(236, 2, 229, 'Rozvod je možný dvakrát, potom lze podržet manželku podle správné zvyklosti nebo ji propustit v dobré vůli. Není vám dovoleno vzít nic z toho, co jste jí dali, leda že oba se obáváte, že nedodržíte omezení Bohem daná. Jestliže se obáváte, že nedodržíte omezení Bohem daná, tedy nebude pro žádného z obou vás hříchem, jestliže žena se vykoupí. Toto jsou omezení Bohem daná, nepřekračujte je! Ti, kdož překračují Boží omezení, ti jsou nespravedliví!'),
(237, 2, 230, 'Jestliže zapudí muž ženu svou, není mu potom dovolena dříve, než byla vdána za jiného muže. A když tento se s ní rozvede, není pro žádného z obou hříchem, jestliže se k sobě vrátí, domnívají-li se, že budou dodržovat omezení Bohem daná. A toto jsou omezení Boží a On je činí jasnými pro lid vědoucí.'),
(238, 2, 231, 'Když se rozvedete se svými ženami a přijde lhůta jejich, pak si je buď ponechejte podle zvyklosti, anebo je propusťte podle zvyklosti, avšak nezdržujte je násilím, překračujíce tak omezení Boží. Kdo tak činí, ten sám sobě škodí. A nepokládejte znamení Boží za žert, nýbrž pomněte dobrodiní Božího vůči vám a toho, co seslal vám z Písma a moudrosti, napomínaje vás tím! A bojte se Boha a vězte, že Bůh je všech věcí znalý.'),
(239, 2, 232, 'Když se rozvedete se svými ženami a přijde lhůta jejich, nebraňte jim vstoupit v manželství s jejich novými manžely, jestliže se dohodli podle zvyklosti. A to je napomenutí tomu z vás, kdo v Boha i v den soudný uvěřil; a toto je pro vás jednání nejctnostnější a nejčistší. Bůh ví, avšak vy nevíte.'),
(240, 2, 233, 'Matky kojí děti své plné dva roky, a to je pro toho, kdo chce, aby bylo dovršeno kojení. Na otci dítěte pak spočívá výživa i oblékání matek podle zvyklosti uznané. A není nikomu uloženo nad možnosti jeho; nechť matka žádná není zkrácena kvůli dítěti svému a nechť není otec žádný zkrácen kvůli dítěti svému. A dědici připadá stejná povinnost. Jestliže oba si přejí odstavení dítěte po vzájemné dohodě a poradě, pak to pro ně není hříchem. A jestliže chcete vzít pro dítě kojnou, není to pro vás hříchem pod podmínkou, že zaplatíte to, co dáváte podle zvyklosti. A bojte se Boha a vězte, že Bůh jasně vidí vše, co děláte.'),
(241, 2, 234, 'Pro ty, kdož k Bohu byli povoláni a zanechali manželky, platí, že ony mají vyčkat samy o sobě čtyři měsíce a deset dní. A když dosáhnou lhůty své, nebude pro vás hříchem, jestliže si budou počínat samy se sebou podle zvyklosti uznané. A Bůh dobře ví, co děláte.'),
(242, 2, 235, 'A není pro vás hříchem, jestliže se budete ucházet o tyto ženy či budete-li mít podobný skrytý úmysl v duších svých. Bůh ví dobře, že si na ně budete myslit, avšak nesmlouvejte se s nimi v tajnosti, leda promluvíte-li slova zvyklostí uznaná. A nečiňte rozhodnutí o svazku manželském (s vdovou), pokud neuplyne lhůta předepsaná, a vězte, že Bůh dobře zná, co skryto je ve vašich duších! Střezte se Ho a vězte, že Bůh je odpouštějící, slitovný!'),
(243, 2, 236, 'Není pro vás hříchem, rozvedete-li se s ženami svými, dříve než jste se jich dotkli anebo dříve než jste jim určili obvěnění. Dejte jim však zaopatření - zámožný podle prostředků svých a chudý podle prostředků svých - podle zvyklosti uznaných. A to je povinností pro ty, kdož dobré konají.'),
(244, 2, 237, 'Jestliže se s nimi rozvedete dříve, než jste se jich dotkli, ale již jste jim určili obvěnění - pak jim patří polovina toho, k čemu jste se zavázali, pokud se toho nevzdají nebo se toho nevzdá ten, v jehož rukou je uzavření manželské smlouvy. A vzdát se toho je blíže k bohabojnosti. A nezapomínejte si vzájemně prokazovat velkomyslnost, vždyť Bůh jasně vidí vše, co děláte!'),
(245, 2, 238, 'Dodržujte modlitby a modlitbu prostřední a stůjte před Bohem, pokorně mu oddáni!'),
(246, 2, 239, 'A máte-li strach, pak ji konejte stojíce na nohou anebo v sedle, jste-li však v bezpečí, tedy vzpomínejte Boha tak, jak vás tomu naučil - tomu, co dříve jste neznali!'),
(247, 2, 240, 'Ti z vás, kteří budou ke Mně povoláni a zanechají manželky, nechť odkáží manželkám svým zaopatření na celý rok, aniž je nutí odejít z domu. Jestliže však odejdou, potom není pro vás hříchem to, co samy se sebou učiní podle zvyklostí uznaných. A Bůh věru je mocný, moudrý.'),
(248, 2, 241, 'Rozvedeným ženám pak náleží zaopatření podle zvyklostí uznaných, a to povinností je pro bohabojné.'),
(249, 2, 242, 'A takto vám Bůh objasňuje znamení Svá - snad budete rozumní!'),
(250, 2, 243, 'Viděl jsi ty, kdož v tisících vyšli z příbytků svých, smrti se bojíce? I řekl jim Bůh: "Zemřete!" a potom jim znovu život dal, neboť Bůh věru je pln milosti k lidem, avšak většina lidí vděčná není.'),
(251, 2, 244, 'Bojujte na stezce Boží a vězte, že Bůh je slyšící, vševědoucí!'),
(252, 2, 245, 'Kdokoliv dá Bohu půjčku výhodnou, tomu bude vrácena mnohonásobně; Bůh zavírá a otevírá dlaň svou a k Němu budete navráceni.'),
(253, 2, 246, 'Cožpak jsi neviděl velmože z dítek Izraele, když po Mojžíšově smrti pravili jednomu z proroků svých: "Ustanov mezi námi krále, abychom mohli bojovat na stezce Boží!" I odvětil: "Možná, že až vám bude předepsáno bojovat, vy bojovat nebudete!" Odpověděli: "A proč bychom neměli bojovat na stezce Boží, když jsme byli vyhnáni ze sídel svých i od synů svých?" Avšak když jim bylo předepsáno bojovat, obrátili se zády s výjimkou několika málo z nich. A Bůh dobře zná nespravedlivé.'),
(254, 2, 247, 'A řekl jim prorok jejich: "Bůh vám posílá Saula (Talút) jako krále." Pravili: "Jak by nám tento mohl vládnout, když my máme větší nárok na vládu než on, jenž nemá dostatek majetku?" Odvětil: "Bůh jej pro vás vyvolil a obohatil jej ve vědění i v síle tělesné. Bůh dává vládu Svou tomu, komu chce, a Bůh je nesmírný, vševědoucí."'),
(255, 2, 248, 'I pravil jim prorok jejich: "Věru znamením vlády jeho bude to, že přijde k vám schránka úmluvy, v níž je bezpečí od Pána vašeho a ostatky toho, co zanechala rodina Mojžíšova a rodina Árónova, a ponesou ji andělé. A bude to pro vás věru znamení, jste-li věřící!"'),
(256, 2, 249, 'A když Saul vytáhl s vojsky, pravil jim: "Bůh vás bude zkoušet u jedné řeky. Ten, kdo z ní se napije, patřit k mým nebude; ale ten, kdo z ní neokusí, ten bude patřit k mým kromě těch, kdož napijí se z dlaně." A napili se z ní všichni kromě malého počtu. Když pak Saul a ti, kdož s ním uvěřili, přešli přes ni, zvolali: "Nemáme dnes sílu k boji proti Goliášovi a vojskům jeho!" Avšak ti, kdož pevně byli přesvědčeni o setkání svém s Bohem, vykřikli: "Kolikrát již nepočetné oddíly zvítězily nad početnějšími z dovolení Božího, vždyť Bůh je věru na straně trpělivých!"'),
(257, 2, 250, 'A když vytáhli proti Goliášovi a vojskům jeho, zvolali: "Pane náš, dodej nám vytrvalosti a zpevni paty naše a pomoz nám k vítězství nad lidem nevěřícím!"'),
(258, 2, 251, 'A zahnali je na útěk z dovolení Božího a David zabil Goliáše a Bůh dal mu pak moc královskou i moudrost a naučil jej tomu, čemu chtěl. A kdyby Bůh nechránil mezi lidmi jedny před druhými, věru by země dospěla do záhuby, však Bůh je pln laskavosti k lidstvu veškerému.'),
(259, 2, 252, 'Toto jsou znamení Boží a sdělili jsme ti je spolu s pravdou, neboť tys věru jedním z poslů.'),
(260, 2, 253, 'A dali jsme některým z těchto poslů přednost před jinými. A jsou mezi nimi někteří, s nimiž Bůh hovořil, a jiní, jež v hodnostech povznesl. A dali jsme Ježíšovi, synu Marie, znamení jasná a posílili jsme jej Duchem svatým: A kdyby byl Bůh chtěl, pak by se nebyli navzájem zabíjeli ti, kdož po nich přišli poté, co dostalo se jim jasných znamení. Avšak dostali se do rozporů a byli mezi nimi ti, kdož uvěřili, a byli mezi nimi i ti, kdož byli nevěřící. A kdyby byl Bůh chtěl, nebyli by se vzájemně zabíjeli, avšak Bůh věru činí, co chce.'),
(261, 2, 254, 'Vy, kteří věříte, dávejte almužny z toho, co jsme vám uštědřili, dříve než přijde den, kdy nebude ani obchodování, ani přátelství, ani přímluvy. A nevěřící jsou věru nespravedliví!'),
(262, 2, 255, 'Bůh - není božstva kromě Něho, živého, trvalého! Nepadá naň ani dřímota, ani spánek, Jemu náleží vše, co na nebesích je i na zemi! Kdo může se u Něho přimluvit jinak, než s dovolením Jeho? On zná, co je před rukama jejich i co je za zády jejich, zatímco oni neobejmou z vědění Jeho nic leda to, co On chce. Trůn Jeho obepíná nebesa i zemi a střežení jich mu potíží nečiní - On vznešený je a mohutný!'),
(263, 2, 256, 'Nebudiž žádného donucování v náboženství! A již bylo jasně rozlišeno správné vedení od bloudění! Ten, kdo nevěří v Tághúta a věří v Boha, ten uchopil se rukojeti spolehlivé, jež nikdy se neutrhne. A Bůh je slyšící, vševědoucí.'),
(264, 2, 257, 'Bůh je ochráncem těch, kdož uvěřili, a dal jim vyjít z temnot ke světlu. Avšak ochráncem těch, kdož neuvěřili, jsou Tághútové, kteří je vyvedou ze světla do temnot - a takoví budou ohně pekelného obyvateli a v něm budou nesmrtelní.'),
(265, 2, 258, 'Což jsi neviděl toho, jenž hádal se s Abrahamem ohledně Pána jeho jen proto, že Bůh mu dal království? A hle, pravil Abraham: "Pán můj je ten, jenž život i smrt dává." A odvětil: "Já jsem ten, kdo život i smrt dává!" I řekl Abraham: "Bůh přivádí slunce od východu, dej mu tedy vyjít ty na západě!" A byl zmaten ten nevěřící - a Bůh nevede cestou správnou lid nespravedlivý.'),
(266, 2, 259, 'Anebo jsi neviděl toho, jenž prošel okolo místa, jež zničeno bylo do základů, a zvolal: "Jak by mohl Bůh opět přivést k životu toto město poté co je mrtvé?" A usmrtil jej Bůh na sto let, potom jej vzkřísil a zeptal se jej: "Jak dlouho jsi takto setrval?" Odvětil: "Setrval jsem tak den anebo část dne." - "Nikoliv," odvětil Pán, "setrvals tak sto let. Pohleď na potravu svou a nápoj svůj - nejsou zkaženy! A pohleď na osla svého! A věru jsme tě učinili znamením pro lidi. A pohleď na tyto kosti, jak je zvedneme a potom je masem obalíme!" A když se mu to stalo jasným, zvolal: "Nyní vím, že Bůh je mocný nad věcmi všemi!"'),
(267, 2, 260, 'A hle, pravil Abraham: "Pane můj, ukaž mi, jak oživuješ mrtvé!" I odpověděl: "Což ty mi nevěříš?" Řekl: "Ano, avšak je to pro uklidnění srdce mého!" Pravil: "Vezmi čtyři ptáky a přitiskni je k sobě, potom umísti na každé hoře část z nich a posléze je zavolej - a přiletí rychle k tobě. A věz, že Bůh je mocný, moudrý!"'),
(268, 2, 261, 'Ti, kdož vydávají jmění své na stezce Boží, jsou podobni zrnu, z něhož vyrůstá sedm klasů, z nichž každý má sto zrn. A Bůh mnohonásobně odplácí tomu, komu chce, neboť Bůh je velkorysý, vševědoucí.'),
(269, 2, 262, 'Těm, kdož vydávají jmění své na stezce Boží, aniž potom doprovázejí to, co vydali, výčitkami či špatností, těm dostane se odměny u Pána jejich a nemusí mít strach a nebudou zarmouceni.'),
(270, 2, 263, 'Laskavé slovo a odpuštění je lepší než almužna, za níž následuje špatnost. A Bůh je soběstačný a laskavý.'),
(271, 2, 264, 'Vy, kteří věříte, nezmařte almužny své výčitkami a špatností jako ten, kdo rozdává ze jmění svého z licoměrnosti před lidmi, aniž uvěřil v Boha a den soudný. Podobá se skále pokryté prstí: když přijde liják, zanechá ji holou. A takoví lidé nemají vliv žádný na to, co jednou si již vysloužili, vždyť Bůh nepovede lid nevěřící.'),
(272, 2, 265, 'Avšak ti, kdož rozdávají ze jmění svého usilujíce získat zalíbení Boží a pro upevnění duší svých, ti podobají se zahradě na kopci: když přijde liják, přinese plody své dvakrát, když liják nepřijde, je zde alespoň rosa... A Bůh věru jasně vidí vše, co děláte.'),
(273, 2, 266, 'Přeje si někdo z vás, aby měl zahradu palem a vinné révy, pod níž řeky tekou, a v ní měl všechny druhy ovoce a aby jej potom zastihlo stáří a měl slabé potomstvo, a kdyby tehdy postihla zahradu vichřice ohnivá, jež spaluje? Takovým způsobem vám Bůh objasňuje znamení - snad budete o tom přemýšlet?'),
(274, 2, 267, 'Vy, kteří věříte, rozdávejte almužnu z dobrých věcí, jež jste si vysloužili, a z toho, čemu jsme pro vás dali vzejít ze země. A nevybírejte to nejhorší, abyste to rozdali, a to, co sami byste nevzali, leda byste nad tím zavřeli obě oči. A vězte, že Bůh je soběstačný a chvályhodný.'),
(275, 2, 268, 'Satan vám vyhrožuje chudobou a přikazuje vám hanebnosti, avšak Bůh vám slibuje odpuštění Své a laskavost - a Bůh věru je velkorysý, vševědoucí.'),
(276, 2, 269, 'On dává moudrost tomu, komu chce. A komu byla dána moudrost, tomu bylo dáno velké dobro, avšak pouze ti, kdož nadáni jsou rozumem, si to připomenou.'),
(277, 2, 270, 'Ať dáte almužnu na jakékoliv vydání nebo ať učiníte jakýkoliv slib obětní, Bůh o tom věru ví. A nespravedliví nebudou mít pomocníky.'),
(278, 2, 271, 'Jestliže dáváte almužny veřejně, je to krásné, jestliže však to činíte skrytě a dáváte chudým, je to pro vás lepší a vymaže vám to špatné skutky vaše. A Bůh je dobře zpraven o tom, co činíte.'),
(279, 2, 272, 'Není povinností tvou, abys je vedl, ale Bůh vede toho, koho chce. To, co rozdáváte dobrého, je ve prospěch vás samých, avšak vydávejte jen z touhy popatřit na tvář Boží. A to, co vydáte dobrého, bude vám plně nahrazeno a nebude vám ukřivděno.'),
(280, 2, 273, 'Chudáky, kteří se dostali do tísně na stezce Boží a nemohou se po zemi pohybovat za obchodem, pokládají hlupáci za bohaté kvůli zdrženlivosti jejich; ty však poznáš je podle známek jejich, byť i drze neprosili lidi. A co dobrého těmto rozdáte, Bůh o tom dobře ví.'),
(281, 2, 274, 'Ti, kdož rozdávají almužnu z majetku svého za noci i za dne, skrytě i veřejně, těm dostane se odměny u Pána jejich; takoví nemusí mít strach a nebudou zarmouceni.'),
(282, 2, 275, 'Ti, kdož pohlcují zisk z lichvy, vstanou z mrtvých jako ten, koho satan potřísnil svým dotekem. A to proto, že říkali: "Vždyť prodávání je podobné lichvě!" Bůh však dovolil prodávání a zakázal lichvu. Tomu, komu dostalo se varování od Pána jeho a který přestal s lichvou, bude zachováno to, čeho předtím nabyl, a rozhodnutí o něm bude patřit Bohu. Ti však, kdož vrátí se k lichvě, ti se stanou ohně obyvateli a budou v něm nesmrtelní.'),
(283, 2, 276, 'Bůh zničí lichvu, avšak zúročí almužny. Bůh nemiluje žádného nevěřícího hříšného.'),
(284, 2, 277, 'Ti, kdož uvěřili, zbožné skutky konali, modlitbu dodržovali a almužnu dávali, dostanou odměnu od Pána svého a nemusí mít strach a nebudou zarmouceni.'),
(285, 2, 278, 'Vy, kteří věříte, bojte se Boha a zanechte poslední zbytky lichvy, jste-li věřícími!'),
(286, 2, 279, 'Jestliže tak neučiníte, připravte se na válku od Boha a posla Jeho. Jestliže však se kajícně obrátíte, pak vám zůstane váš kapitál - a nebudete tím škodit a nebudete tím poškozeni.'),
(287, 2, 280, 'Je-li dlužník váš v tísni, vyčkejte, až se mu uleví; a dáte-li mu to jako almužnu, je to pro vás lepší, jste-li vědoucí.'),
(288, 2, 281, 'A bojte se toho dne, v němž k Bohu se navrátíte, kdy duše každá bude odměněna podle toho, co si vysloužila, a kdy lidem nebude ukřivděno.'),
(289, 2, 282, 'Vy, kteří věříte, dáváte-li úvěr dlužníkovi na lhůtu stanovenou, zapište to! Nechť písař to mezi vámi sepíše spravedlivě, nechť žádný písař neodmítne to napsat tak, jak jej tomu Bůh naučil, a nechť zapisuje, zatímco mu bude dlužník diktovat, a nechť bojí se Boha, Pána svého, a nechť ničeho z dluhu neubere! A jestliže dlužník je choromyslný či slabý anebo nemůže sám diktovat, nechť diktuje jeho poručník, a to spravedlivě! A žádejte svědectví dvou svědků z mužů vašich, a nemáte-li dva muže, pak vezměte jednoho muže a dvě ženy z těch, které uznáte za vhodné svědkyně; aby, kdyby jedna z nich se zmýlila, druhá ji mohla připomenout. A nechť se svědkové nezdráhají, jsou-li přizváni. Nezanedbávejte zapsat dluh, ať již je malý či velký, až do lhůty jeho! A toto je u Boha spravedlivější a přímější pro svědectví a jistější pro potlačení pochybností, pokud se ovšem nejedná o přímý obchod mezi vámi; tehdy není pro vás hříchem, jestliže to nezapíšete. A berte svědky, jestliže mezi sebou obchodujete, avšak neubližujte ani písaři, ani svědkovi. Jestliže tak učiníte, jest to hanebnost. A bojte se Boha, vždyť Bůh vás věru učí a ví o všech věcech.'),
(290, 2, 283, 'Jste-li na cestách a nenaleznete-li písaře, pak berte zástavu! A jestliže někdo z vás svěří něco druhému, nechť ten, jenž přijal svěřené, to vrátí a nechť bojí se Boha, Pána svého! A nezatajujte svědectví, neboť kdo svědectví zatajuje, je v srdci svém hříšníkem - a Bůh dobře ví, co děláte.'),
(291, 2, 284, 'Bohu náleží vše, co na nebesích je i na zemi. A ať již dáváte najevo to, co je v duších vašich, či to skrýváte, Bůh požádá vás o počet z toho. On odpustí, komu bude chtít, a potrestá toho, koho bude chtít - On nade všemi věcmi je mocný.'),
(292, 2, 285, 'Uvěřil posel v to, co od Pána jeho mu bylo sesláno, a uvěřili všichni věřící v Boha, anděly Jeho i Písma Jeho i posly Jeho - a My neděláme rozdíl mezi posly Jeho - a řekli: "Slyšeli jsme a uposlechli jsme; o odpuštění Tvé prosíme, Pane náš, a u Tebe je konečný cíl náš!"'),
(293, 2, 286, 'Bůh ukládá duši jen to, co je pro ni možné; dostane se jí odměny za to, co si pro sebe i proti sobě vysloužila. "Pane náš, neměj nám za zlé, jestliže jsme zapomněli či chybili! Pane náš, nenakládej na nás břímě tíživé, podobné tomu, jež jsi naložil na ty, kdož byli před námi! Pane náš, nenakládej nám břemena, která nemůžeme unést, a vymaž nám chyby naše, smiluj se nad námi a odpusť nám! Tys ochráncem naším, pomoz nám k vítězství nad lidem nevěřícím!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Alif lám mím!'),
(295, 3, 2, 'Bůh - není božstva kromě Něho - je živý stále trvající.'),
(296, 3, 3, 'On seslal ti Písmo s pravdou, potvrzující pravdivost dřívějšího, a On seslal i Tóru a Evangelium'),
(297, 3, 4, 'již předtím jako správné vedení pro lidi a seslal spásné rozlišení. Zajisté ty, kdož ve znamení Boží nevěří, očekává trest strašný, neboť Bůh věru mocný je a pán pomsty.'),
(298, 3, 5, 'Před Bohem není skryto nic ani na zemi, ani na nebi'),
(299, 3, 6, 'a je to On, jenž utváří vás v lůnech podle přání svého. A není božstva kromě Něho, mocného, moudrého.'),
(300, 3, 7, 'On je ten, jenž seslal ti Písmo, v němž některé verše jsou pevně stanovené, a ty jsou podstatou Písma, zatímco jiné jsou víceznačné. Ti, v jejichž srdcích je odchýlení, následují to, co je v něm víceznačné, usilujíce tak o rozkol a snažíce se o svévolný výklad toho; však nezná výklad toho nikdo kromě Boha. Ti však, kdož pevni jsou ve vědění, hovoří: "My v ně jsme uvěřili; vše, co obsahuje, od Pána našeho je!" A připomínají si to jedině ti, kdož rozmyslem jsou obdařeni.'),
(301, 3, 8, 'Pane náš, nedej odchýlit se srdcím našim poté, cos uvedl nás na cestu správnou, daruj nám milost, jež od Tebe vychází, vždyť Tys věru dárce největší.'),
(302, 3, 9, 'Pane náš, Ty věru shromáždíš lidi v den, o němž pochyby není; a Bůh zajisté slib svůj nezruší.'),
(303, 3, 10, 'V ten den nebudou nevěřícím majetky ani děti jejich proti Bohu nic platny a stanou se palivem pro oheň pekelný.'),
(304, 3, 11, 'Osud jich bude jako osud rodu Faraónova, a těch, kdož před nimi byli a kteří znamení Naše za lež prohlásili; Bůh je uchvátil za hříchy jejich a Bůh strašný je ve svém trestání.'),
(305, 3, 12, 'Rci těm, kdož neuvěřili: "Budete přemoženi a do pekla zahnáni - a jak hnusné to bude místo odpočinku!"'),
(306, 3, 13, 'Dostalo se vám již znamení v dvou houfech, jež se střetly: jeden houfec bojoval na stezce Boží, zatímco druhý byl nevěřící. A zdál se jim druhý houfec na pohled dvojnásobný počtem; a Bůh poskytuje pomoc Svou tomu, komu chce, a věru je v tom poučení pro ty, kdož jsou jasnozřiví.'),
(307, 3, 14, 'Je okrášlena pro lidi láska vášnivá k ženám, k synům, k nahromaděným kintárům zlata a stříbra, k čistokrevným koním, ke stádům i k polím obdělaným. To vše je však jen užívání života pozemského, zatímco u Boha je útočiště nejkrásnější.'),
(308, 3, 15, 'Rci: "Mám vás zpravit o tom, co lepší je než toto? Pro ty, kdož jsou bohabojní, u Pána jejich připraveny jsou zahrady, pod nimiž řeky tekou a v nichž nesmrtelní budou, a manželky čisté a zalíbení Boží. A Bůh jasně zří služebníky Své,'),
(309, 3, 16, 'kteří hovoří:, Pane náš, uvěřili jsme; odpusť nám naše viny a ochraň nás před trestem ohně pekelného,'),
(310, 3, 17, 'trpělivé, pravdomluvné, pokorně oddané, almužny rozdávající a za úsvitu o odpuštění prosící."'),
(311, 3, 18, 'Bůh sám svědčí, že není božstva kromě Něho; a dosvědčují to i andělé a ti, jimž dáno bylo vědění. On stará se o spravedlnost - a není božstva kromě Něho, mocného, moudrého.'),
(312, 3, 19, 'Náboženstvím jediným u Boha jest věru islám. Ti, jimž dáno bylo Písmo, se dostali do sporu teprve tehdy, když přišlo k nim vědění, následkem vzájemné řevnivosti. A kdo nevěří ve znamení Boží, zjistí, že Bůh věru je rychlý v účtování.'),
(313, 3, 20, 'A jestliže se s tebou hádají, rci: "Odevzdal jsem se do vůle Boží stejně jako ti, kdož mne následují." A zeptej se těch, jimž dostalo se Písma, a lidu neučeného: v "Odevzdáváte se do vůle Boží?" A jestliže se odevzdají do vůle Boží, pak budou vedeni cestou správnou, avšak jestliže se odvrátí... vždyť tobě přísluší jen předání poselství. A Bůh jasně zří služebníky Své.'),
(314, 3, 21, 'Však těm, kdož nevěří ve znamení Boží a neoprávněně zabíjejí proroky a zabíjejí ty z lidí, kteří přikazují spravedlnost, těm oznam zvěst radostnou o trestu bolestném.'),
(315, 3, 22, 'A to jsou ti, jejichž činy budou marné na tomto i na onom světě, a nebudou mít pomocníky žádné.'),
(316, 3, 23, 'Což jsi neviděl ty, jimž dostalo se části Písma? Když voláni byli k Písmu Božímu, aby mezi nimi rozhodlo, skupina z nich se zády obrátila a vzdálila.'),
(317, 3, 24, 'A to proto, že řekli: "Nedotkne se nás oheň, leda na dobu omezenou." A byli obelstěni v náboženství svém tím, co si sami vymyslili.'),
(318, 3, 25, 'Ale jak jim bude, až je shromáždíme v den, o němž pochyby není, a kdy každé duši dostane se jen podle toho, co si vysloužila - a nebude jim ukřivděno.'),
(319, 3, 26, 'Rci: "Bože veliký, vládce království! Ty dáváš vládu, komu chceš, a odebíráš ji, komu chceš; Ty povyšuješ, koho chceš, a ponižuješ, koho chceš, v ruce Tvé je dobro a Tys věci každé mocen.'),
(320, 3, 27, 'Ty dáváš přejit noci v den a dáváš přejít dni v noc, Ty dáváš vzejít živému z mrtvého a mrtvému ze živého a uštědřuješ, komu chceš, bez počítání."'),
(321, 3, 28, 'Nechť si věřící neberou nevěřící za přátele místo věřících! Kdo tak učiní, nedostane se mu od Boha ničeho, leda v případě, že byste se od nich obávali nějaké hrozby. A Bůh vás varuje před sebou samým a u Boha je cíl konečný.'),
(322, 3, 29, 'Rci: "Ať již skrýváte to, co je v hrudích vašich, či to dáváte najevo, Bůh dobře o tom ví a On zná vše, co na nebesích je i na zemi - a Bůh je všech věcí mocen."'),
(323, 3, 30, 'V ten den nalezne každá duše před sebou to, co vykonala dobrého, i to, ca provedla špatného, a tehdy zatouží, aby mezi ní a oním byla vzdálenost velká. Bůh vás varuje před sebou samým a Bůh je shovívavý k služebníkům Svým.'),
(324, 3, 31, 'Rci: "Milujete-li Boha, pak mne následujte a Bůh vás za to bude milovat a odpustí vám viny vaše, vždyť Bůh je odpouštějící, slitovný."'),
(325, 3, 32, 'Rci: "Poslouchejte Boha a posla Jeho! Jestliže se obrátíte zády... Bůh věru nemá rád nevěřící."'),
(326, 3, 33, 'Bůh vskutku vyvolil Adama, Noa, rod Abrahamův i rod Imránův nad lidstvo veškeré'),
(327, 3, 34, 'jako pokolení, jež vzešla jedno z druhého. A Bůh je slyšící, vševědoucí.'),
(328, 3, 35, 'A hle, pravila manželka ´Imránova: "Pane můj, zaslibuji Ti, co je v lůně mém, aby se zasvětilo Tobě. Přijmi to ode mne, vždyť Tys slyšící, vševědoucí!"'),
(329, 3, 36, 'A když to přivedla na svět, zvolala: "Pane můj, přivedla jsem na svět dceru" - a Bůh dobře věděl, co přivedla na svět, vždyť pohlaví mužské není jako ženské - "a nazvala jsem ji Marií. A svěřuji ji i potomstvo její do ochrany Tvé před satanem prokletým."'),
(330, 3, 37, 'A přijal ji Bůh přijetím laskavým a dal jí vzrůst vzrůstem krásným; a svěřil ji Zachariášovi. A kdykoliv za ní vešel Zachariáš do svatyně, nalezl u ni potravu. I zeptal se: "Marie, odkud to máš?" Odpověděla: "To přichází od Boha a Bůh věru uštědřuje obživu, komu chce, aniž počítá."'),
(331, 3, 38, 'Tehdy tam Zachariáš poprosil Pána svého: "Pane můj, dej mi z milosti Své potomstvo výtečné, vždyť Ty jsi ten, jenž rád prosby vyslyšuje!"'),
(332, 3, 39, 'A zavolali na něj andělé, zatímco stál a modlil se ve svatyni: "Bůh ti oznamuje zvěst radostnou o narození Jana, jenž potvrdí pravdivost slova Božího a bude pánem, bude zdrženlivý, bude prorokem a jedním z bezúhonných."'),
(333, 3, 40, 'Pravil: "Pane můj, jak bych mohl mít chlapce, když již jsem dospěl do věku vysokého a manželka má je neplodná?" Řekl: "Takto Bůh činí to, co chce!"'),
(334, 3, 41, 'Pravil Zachariáš: "Pane můj, učiň mi znamení!" Odvětil: "Znamením tvým bude, že nebudeš po tři dny mluvit s lidmi jinak než posunky. A vzývej často Pána svého a chval Jej za večera i za jitra!"'),
(335, 3, 42, 'A hle, pravili andělé: "Marie, Bůh si tě věru vyvolil a čistou tě učinil a vyznamenal tě mezi ženami lidstva veškerého.'),
(336, 3, 43, 'Marie, buď Pánu svému pokorně oddaná, padej na tvář svou a skláněj se s těmi, kdož poklekají.'),
(337, 3, 44, 'A toto je jeden z příběhů nepoznatelných, jež ti zjevujeme. Tys nebyl mezi nimi, když vrhali losy třtinami o to, kdo z nich se ujme Marie, a nebyla mezi nimi, když se hádali.'),
(338, 3, 45, 'A hle, pravili andělé: "Marie, Bůh ti oznamuje zvěst radostnou o Slovu, jež od Něho přichází, jehož jméno je Mesiáš, Ježíš syn Mariin, a bude blahoslavený na tomto i onom světě a též jedním z těch, kdož k Bohu budou přiblíženi.'),
(339, 3, 46, 'A bude mluvit s lidmi již v kolébce i jako dospělý a bude patřit mezi bezúhonné."'),
(340, 3, 47, 'Pravila: "Pane můj, jak bych mohla mít syna, když se mne smrtelník žádný dosud nedotkl?" Pravil: "Takto Bůh tvoří to, co chce; když o věci nějaké rozhodne, řekne toliko "Staniž se!", a stane se.'),
(341, 3, 48, 'A naučí jej Bůh Písmu a Moudrosti a Tóře a Evangeliu."'),
(342, 3, 49, 'A byl poslem k dítkám Izraele: "Přišel jsem k vám se znamením Pána vašeho. Utvořím pro vás z hlíny něco podobného ptáku a vdechnu tomu život, a stane se to skutečným ptákem z dovolení Božího. A vyléčím slepého i malomocného a vzkřísím mrtvé z dovolení Božího. A sdělím vám, co jíte a co shromažďujete v příbytcích svých. A věru je v tomto pro vás znamení, jste-li věřící.'),
(343, 3, 50, 'A přišel jsem, abych potvrdil pravdivost toho, čeho se vám dostalo přede mnou z Tóry, a aby vám byla dovolena část z toho, co vám bylo zakázáno. A přicházím k vám se znamením od Pána vašeho - bojte se Boha a poslouchejte mne!'),
(344, 3, 51, 'Zajisté Bůh je Pánem mým i Pánem vaším, uctívejte Jej tedy - a to je stezka přímá!"'),
(345, 3, 52, 'A když Ježíš pocítil nevíru jejich, pravil: "Kdo budou pomocníky mými na cestě Boží?" I odpověděli apoštolové: "My pomocníky Božími budeme, my v Boha věříme, a ty podej svědectví, že do vůle Jeho odevzdáni jsme!'),
(346, 3, 53, 'Pane náš, uvěřili jsme v to, cos nám seslal, a následujeme posla; zapiš nás tedy mezi ty, kdož pravdu dosvědčují!"'),
(347, 3, 54, 'I strojili mu úklady, avšak i Bůh strojil lest a Bůh nejlépe umí strojit lest.'),
(348, 3, 55, 'A hle, pravil Bůh: "Ježíši, nyní tě odvolám a povznesu tě k Sobě a očistím tě od (špíny) těch, kdož neuvěřili. A ty, kdož tě následovali, učiním vyššími než ty, kdož nevěřili, až do dne zmrtvýchvstání. A pak se ke Mně všichni navrátíte a rozsoudím mezi vámi to, v čem jste se rozcházeli.'),
(349, 3, 56, 'A ty, kdož neuvěřili, potrestám trestem přísným na tomto i na onom světě a nebudou mít žádné pomocníky,'),
(350, 3, 57, 'zatímco těm, kdož uvěřili a zbožné skutky konali, se dostane odměny jejich - a Bůh věru nemá rád nespravedlivé!"'),
(351, 3, 58, 'A přednášíme ti toto ze znamení a z moudrého připomenutí.'),
(352, 3, 59, 'A podobá se Ježíš před Bohem Adamovi: On stvořil jej z prachu a potom mu řekl "Budiž!" a on byl!'),
(353, 3, 60, 'Pravda tato od Pána tvého přichází - nebuď tedy jedním z pochybovačů!'),
(354, 3, 61, 'Tomu, kdo se bude s tebou o tom přít poté, co ti bylo dáno vědění, odpověz: "Pojďme, přizveme syny naše a syny vaše, ženy naše a ženy vaše, nás i vás; potom vzájemně si budeme přísahat a přivoláme prokletí Boží na lháře!"'),
(355, 3, 62, 'A toto je skutečně příběh pravdivý - a není božstva kromě Boha a Bůh je mocný, moudrý.'),
(356, 3, 63, 'Jestliže se obrátí zády... vždyť Bůh dobře zná ty, kdož pohoršení šíří!'),
(357, 3, 64, 'Rci: "Vlastníci Písma! Pojďte ke slovu rovnému pro nás i pro vás a shodněme se, že nebudeme sloužit nikomu leč Bohu a nebudeme k Němu nic přidružovat a že si nebude brát jeden druhého za Pána místo Boha!" Obrátí-li se však zády, pak jim řekněte: "Dosvědčte, že my jsme do vůle Boží odevzdáni!"'),
(358, 3, 65, 'Vlastníci Písma! Proč se hádáte o Abrahama, když přece Tóra i Evangelium byly seslány až po něm? Což tomu nerozumíte?'),
(359, 3, 66, 'Hle, zde jste těmi, kteří se hádali o tom, o čem mají nějaké vědění. Proč se tedy hádáte o něčem, o čem vědění nemáte - a Bůh zná nejlépe, zatímco vy neznáte.'),
(360, 3, 67, 'Abraham nebyl ani židem, ani křesťanem, avšak byl hanífem odevzdaným do vůle Boží; a nepatřil mezi modloslužebníky.'),
(361, 3, 68, 'A nejblíže k Abrahamovi byli věru ti, kdož jej následovali, a tento posel a ti, kdož v něj uvěřili. A Bůh je blízkým druhem věřících.'),
(362, 3, 69, 'Někteří z vlastníků Písma by vás rádi uvedli v blud, avšak zabloudí jen sami, aniž to budou tušit.'),
(363, 3, 70, 'Vlastníci Písma! Proč nevěříte ve znamení Boží, když sami jste jejich svědky?'),
(364, 3, 71, 'Vlastníci Písma! Proč odíváte pravdu do lži a proč tajíte vědomě pravdu?'),
(365, 3, 72, 'Někteří z vlastníků Písma říkají: "Věřte na počátku dne v to, co bylo sesláno věřícím, a na jeho konci to popřete, snad se oni odvrátí od víry své.'),
(366, 3, 73, 'Nevěřte nikomu kromě těch, kdož následují náboženství vaše!" Rci: "Jediné správné vedení je vedení Boži! Vy se obáváte, aby se nikomu nedostalo podobného tomu, čeho se dostalo vám, anebo aby se věřící nepřeli s vámi ohledně Pána vašeho." Rci: "Milost je věru v rukou Božích a On ji uštědřuje, komu chce - a Bůh je nesmírný, vševědoucí.'),
(367, 3, 74, 'On milostí svou vyznamenává, koho chce; a Bůh je pln dobrodiní nesmírného."'),
(368, 3, 75, 'Mezi vlastníky Písma jsou někteří, kterým svěříš-li qintár, vrátí ti jej, ale jsou mezi nimi i takoví, kterým svěříš dinár, a oni ti jej nevrátí, leč budeš-li nad nimi neustále stát. A to proto, že říkají: "Nemáme žádné závazky vůči lidem nevědomým." Tím pronášejí vědomě o Bohu lež.'),
(369, 3, 76, 'Však nikoliv! Kdo přesně dodržuje svou úmluvu a je bohabojný... vždyť Bůh miluje bohabojné.'),
(370, 3, 77, 'Věru ti, kdož prodávají úmluvu s Bohem a přísahy své za nepatrnou cenu, ti nebudou mít podíl žádný v životě posmrtném a Bůh na ně v den zmrtvýchvstání nepromluví ani nepohlédne; a neočistí je a pro ně určen je trest bolestný.'),
(371, 3, 78, 'A mezi nimi je vskutku skupina, jež překrucuje jazyky svými Písmo, abyste to pokládali za Písmo, ačkoliv to Písmo není, a říkají: "Toto přichází od Boha!", a přitom to vůbec není od Boha. A pronášejí tak vědomě o Bohu lež.'),
(372, 3, 79, 'Nepřísluší smrtelníkovi, jemuž Bůh dal Písmo, vědění a prorocké poslání, aby potom lidem prohlásil: "Buďte služebníky mými, nikoliv Boha!" Buďte spíše rabíny v tom, že učíte Písmu, a v tom, že v něm studujete.'),
(373, 3, 80, 'Bůh vám nepřikázal, abyste si brali anděly a proroky za pány své. Což by vám mohl přikázat nevěrectví poté, co jste se stali do vůle Jeho odevzdanými?'),
(374, 3, 81, 'A hle, Bůh uzavřel smlouvu s proroky: "Kdykoliv vám dám Písmo a moudrost a potom k vám přijde posel potvrzující pravdivost toho, co jste již měli, uvěřte v něj a pomáhejte mu!" A pravil: "Potvrzujete to a přijímáte to jako závazek vůči Mně?" Odpověděli: "Potvrzujeme to." I řekl: "Dosvědčte to a já s vámi budu jedním ze svědků!"'),
(375, 3, 82, 'A ti, kdož se po tomto obrátí zády, jsou věru hanebníci.'),
(376, 3, 83, 'Což si mohou přát jiné náboženství než náboženství Boží, když již se do vůle Jeho odevzdalo - chtě nechtě - vše, co na nebesích je i na zemi, a když vše se k Němu navrátí?'),
(377, 3, 84, 'Rci: "Uvěřili jsme v Boha i v to, co seslal nám a co seslal Abrahamovi, Ismaelovi, Izákovi, Jakubovi a kmenům, i v to, co bylo dáno Mojžíšovi a Ježíšovi a prorokům od Pána jejich. My nečiníme rozdíl mezi nimi a do vůle Jeho jsme odevzdáni."'),
(378, 3, 85, 'Kdo touží po jiném náboženství než po islámu, nebude to od něho přijato a v životě budoucím bude mezi těmi, kdož ztrátu utrpí.'),
(379, 3, 86, 'Jak by mohl Bůh správně vést lidi, kteří se zřekli víry poté, co uvěřili, a poté, co dosvědčili, že posel je pravdivý, a poté, co k nim přišly jasné důkazy? Bůh přece nepovede cestou správnou lid nespravedlivý.'),
(380, 3, 87, 'A bude odměnou jejich, že padne na ně prokletí Boží a andělů i lidstva veškerého'),
(381, 3, 88, 'a budou v něm navěky a nebude jim zmírněn trest ani odklad dán'),
(382, 3, 89, 'kromě těch, kdož kajícně se potom obrátí a polepší se. A Bůh je věru odpouštějící, slitovný.'),
(383, 3, 90, 'Kdo zřekli se víry poté, když ji přijali, a potom nevíru svou ještě rozmnožili - od těch nebude přijato pokání jejich, neboť to jsou zbloudilí.'),
(384, 3, 91, 'Od žádného z těch, kdož neuvěřili a jako nevěřící zemřou, nebude přijata ani země zlatem naplněná, kdyby se tím chtěli vykoupit. Pro ty je určen trest bolestný a nebudou mít pomocníky žádné.'),
(385, 3, 92, 'Nikdy nedosáhnete pravé zbožnosti, dokud nebudete rozdávat z toho, co milujete - a cokoliv z věcí rozdáte, Bůh o tom dobře ví.'),
(386, 3, 93, 'Všechny pokrmy byly dovoleny dítkám Izraele kromě těch, jež Izrael ještě předtím, než byla seslána Tóra, sám prohlásil za zakázané. Rci: "Přineste tedy Tóru a čtěte z ní, jste-li pravdomluvní!'),
(387, 3, 94, 'Ti, kdož ještě potom si budou vymýšlet o Bohu lži, ti věru jsou nespravedliví."'),
(388, 3, 95, 'Rci: "Bůh je pravdomluvný! Následujte tedy náboženství Abrahamovo, jenž hanífem byl a mezi modloslužebníky nepatřil!"'),
(389, 3, 96, 'A věru prvním chrámem, jenž pro lidstvo byl založen, byl ten, jenž v Bakce stojí, jenž požehnáním je i vedením správným pro lidstvo veškeré. v'),
(390, 3, 97, 'A v něm jsou znamení jasná: místo Abrahamovo, a kdokoliv tam vstoupí, je v bezpečí. A Bůh uložil lidem pouť k chrámu tomuto pro toho, kdo k němu cestu může vykonat. Však ten, kdo nevěří... vždyť Bůh věru je soběstačný a obejde se bez lidstva veškerého.'),
(391, 3, 98, 'Rci: "Vlastníci Písma! Proč popíráte znamení Boží, když Bůh je svědkem všeho, co děláte?"'),
(392, 3, 99, 'Rci: "Vlastníci Písma! Proč odvracíte z cesty Boží ty, kdož uvěřili, snažíce se ji pokřivit, ačkoliv jste svědky? A Bohu není věru lhostejné, co konáte."'),
(393, 3, 100, 'Vy, kteří věříte! Budete-li poslouchat skupinu jednu z těch, jimž bylo dáno Písmo, obrátí vás poté, co jste již uvěřili, opět v nevěrectví.'),
(394, 3, 101, 'Jak byste se však mohli stát opět nevěřícími, když jsou vám přednášena znamení Boží a mezi vám i posel Jeho dlí? Ten, kdo pevně se Boha přidržuje, ten veden je ke stezce přímé.'),
(395, 3, 102, 'Vy, kteří věříte! Bojte se Boha bázní, jež Mu přísluší, a neumírejte jinak, než když jste se do vůle Jeho odevzdali!'),
(396, 3, 103, 'Přidržujte se pevně provazu Božího všichni a nerozdělujte se! Pomněte dobrodiní Božího k vám, když byli jste nepřáteli a On opět sblížil srdce vaše a stali jste se - díky milosti Jeho - bratry; a byli jste již na okraji propasti ohnivé a On vás před ní spasil. A takto vám Bůh objasňuje Svá znamení - snad budete správně vedeni.'),
(397, 3, 104, 'Nechť stane se z vás obec, jež bude vyzývat k dobrému, přikazovat vhodné a zakazovat zavrženíhodné. A takoví budou blažení.'),
(398, 3, 105, 'Nebuďte jako ti, kdož se rozdělili a do sporu se dostali poté, co k nim přišly důkazy jasné; pro ty je určen trest nesmírný'),
(399, 3, 106, 'v den, kdy rozjasní se tváře některé, zatímco jiné se zachmuří. A těm, jejichž tváře se zachmuří, bude řečeno: "Proč jste se stali nevěřícími poté, co přijali jste víru? Ochutnejte trest za to, že jste byli nevěřící!"'),
(400, 3, 107, 'Avšak ti, jejichž tváře se rozjasní, ti budou v milosti u Pána svého a budou v ní navěky.'),
(401, 3, 108, 'Toto jsou znamení Boží, která ti sdělujeme podle pravdy. Bůh pak si nepřeje nespravedlnost pro nikoho z lidstva veškerého.'),
(402, 3, 109, 'Bohu náleží vše, co na nebesích je i na zemi, a k Bohu se všechno navrátí.'),
(403, 3, 110, 'Vy jste národ nejlepší, jenž kdy povstal mezi lidmi; vy přikazujete vhodné, zakazujete zavrženíhodné a věříte v Boha. A kdyby uvěřili vlastníci Písma, bylo by to pro ně lepší; jsou sice mezi nimi věřící, avšak mnozí z nich jsou hanebníci.'),
(404, 3, 111, 'Nemohou vám uškodit, leda nepatrně; budou-li proti vám bojovat, obrátí se k vám brzy zády a potom už jim nebude pomoci.'),
(405, 3, 112, 'A padne na ně ponížení, ať uchýlí se kamkoliv, kromě těch, kdož chráněni jsou provazem Božím a provazem lidí. A přivolali na sebe hněv Boží a postihla je chudoba, a to proto, že neuvěřili ve znamení Boží a neoprávněně zabíjeli proroky. A také proto, že byli neposlušní a přestupníci.'),
(406, 3, 113, 'Nejsou všichni stejní mezi vlastníky Písma; je mezi nimi obec přímá, jejíž členové přednášejí za noci verše Boží a na tváře své padají'),
(407, 3, 114, 'a věří v Boha a v den soudný, přikazují vhodné a zakazují zavrženíhodné a soutěží v konání dobrých skutků; a to jsou praví zbožní.'),
(408, 3, 115, 'Cokoliv dobrého učiní, nebude jim upřeno a Bůh dobře zná bohabojné.'),
(409, 3, 116, 'Těm však, kdož nevěří, nic nepomohou u Boha majetky jejich ani dětí jejich a budou obyvateli ohně pekelného a budou v něm nesmrtelní.'),
(410, 3, 117, 'To, co vydávají v tomto životě pozemském, se podobá vichřici mrazivé, jež postihla a zničila úrodu lidí, kteří sami sobě ukřivdili. Neukřivdil jim Bůh, leč oni sami sobě ukřivdili.'),
(411, 3, 118, 'Vy, kteří věříte! Neberte si jako důvěrníky nikoho kromě lidí z vás! Nevěřící neopominou vám škodit a přáli by si, abyste upadli do nesnází. A již se stala nenávist jejich zjevnou v jejich ústech, však to, co skrývají hrudi jejich, je ještě horší. A učinili jsme vám znamení jasnými, jste-li rozumní.'),
(412, 3, 119, 'A hle, vy jste ti, kdož tyto lidi milují, zatímco oni vás nemilují; vy věříte v Písmo v jeho celku, zatímco oni, když vás potkají, říkají "Uvěřili jsme", když však zůstanou sami, hryžou si prsty zlobou. Rci: "Zemřete zlobou, vždyť Bůh zná i to, co hrudě vaše skrývají!"'),
(413, 3, 120, 'Když se vás dotkne něco dobrého, zarmucuje je to, ale když vás postihne neštěstí, radují se z toho. Budete-li však trpěliví a bohabojní, neuškodí vám nikterak úklady jejich, neboť Bůh ve vědění Svém objímá vše, co dělají.'),
(414, 3, 121, 'Vzpomeň si, když jsi ráno odešel od rodiny své, abys postavil věřící na místa k boji; a Bůh slyšící je i vševědoucí.'),
(415, 3, 122, 'A vzpomeň si, když dva oddíly z vás již bezmála chtěly projevit svou malomyslnost, ačkoli Bůh byl ochráncem jich obou; nechť na Boha se věřící spoléhají!'),
(416, 3, 123, 'Bůh vám již vskutku pomohl u Badru, když jste byli ještě nepatrní. Bojte se Boha, snad Mu budete vděčni.'),
(417, 3, 124, 'A hle, pravil jsi věřícím: "Což vám nestačí, že vás Pán váš posílil třemi tisíci andělů z nebe vyslanými?'),
(418, 3, 125, 'Ba ano! Budete-li vytrvalí a bohabojní; pak až nepřítel na vás prudce zaútočí, Pán váš vás podpoří pěti tisíci andělů, kteří označují známkami."'),
(419, 3, 126, 'A Bůh učinil vám toto pouze zvěstí radostnou a pro uklidnění srdcí vašich. A není vítězné pomoci leč od Boha mocného, moudrého,'),
(420, 3, 127, 'abyste uťali část těm, kdož jsou nevěřící, anebo aby byli potřeni a vrátili se nazpět zklamáni.'),
(421, 3, 128, 'Ty nemáš nic společného s tou věcí: buď On přijme jejich pokání, anebo je potrestá, vždyť oni věru jsou nespravedliví.'),
(422, 3, 129, 'A Bohu náleží vše, co na nebesích je i na zemi, a On odpouští, komu chce, a také trestá, koho chce. A Bůh je odpouštějící, slitovný.'),
(423, 3, 130, 'Vy, kteří věříte! Neživte se lichvou rozmnožujíce dvakrát zisk, nýbrž bojte se Boha, snad budete blažení.'),
(424, 3, 131, 'A bojte se ohně pekelného, připraveného pro nevěřící,'),
(425, 3, 132, 'a poslouchejte Boha a posla, snad dostane se vám slitování.'),
(426, 3, 133, 'Pospíchejte k milosti od Pána svého a k zahradě, jejíž šířka se rovná nebesům a zemi a jež připravena je pro bohabojné,'),
(427, 3, 134, 'kteří rozdávají almužny ve štěstí i v neštěstí a kteří krotí hněv svůj a odpouštějí lidem. Bůh věru miluje ty, kdož dobro činí,'),
(428, 3, 135, 'a ty, kteří potom, co spáchali hanebnost či sami sobě ukřivdili, vzpomínají Boha a prosí Jej za odpuštění hříchů svých - neboť kdo jiný může hříchy odpustit než Bůh - a kteří nesetrvávají vědomě v tom, co provedli.'),
(429, 3, 136, 'A odměnou těchto bude odpuštění od Pána jejich a zahrady, pod nimiž řeky tekou a v nichž budou nesmrtelní. Jak krásná to odměna těm, kdož dobré konali!'),
(430, 3, 137, 'Již naplnil se obvyklý osud těch, kdož před vámi byli! Cestujte po zemi a pohleďte, jaký byl konec těch, kdož posly za lháře prohlašovali!'),
(431, 3, 138, 'Toto je jasné vysvětlení lidem i vedení a varování bohabojným.'),
(432, 3, 139, 'Neochabujte, nermuťte se, vždyť přece budete mít vrch, jste-li věřící!'),
(433, 3, 140, 'Jestliže jste utrpěli poranění, tedy podobné poranění utrpěli i ti druzí. Takové dny šťastné i nešťastné necháváme střídat mezi lidmi, aby Bůh rozeznal ty, kdož uvěřili, a vybral si mezi vámi mučedníky - a Bůh věru nemiluje nespravedlivé -'),
(434, 3, 141, 'a aby Bůh dal zazářit těm, kdož uvěřili, a zavrhl nevěřící.'),
(435, 3, 142, 'Domníváte se snad, že vejdete do ráje, dříve než Bůh rozpozná mezi vámi ty, kdož usilovně se snažili v boji, a dříve než rozpozná neochvějné?'),
(436, 3, 143, 'Vždyť jste si věru přáli smrt, dříve než jste se s ní setkali; a nyní jste ji viděli, zatímco jste se dívali.'),
(437, 3, 144, 'Muhammad je pouze poslem a před ním odešli již jiní poslové. A jestliže zemře anebo bude zabit, obrátíte se nazpět? Ten, kdo se obrátí nazpět, neuškodí tím vůbec Bohu, ale Bůh odmění ty, kdož byli vděční.'),
(438, 3, 145, 'Žádná duše nemůže zemřít jinak než z dovolení Božího a podle zapsané a pevně stanovené lhůty. Kdokoli si přeje odměnu již v životě pozemském, tomu z ní něco dáme, a kdokoli si přeje odměnu v životě budoucím, tomu z ní něco dáme - a My odměníme vděčné.'),
(439, 3, 146, 'Kolik proroků bojovalo, majíc na své straně zástupy početné? A přece neochabli na cestě Boží kvůli tomu, co je postihlo z neštěstí, ani nezeslábli a neustoupili. A Bůh miluje vytrvalé.'),
(440, 3, 147, 'A bylo jejich řečí pouze to, že řekli: "Pane náš, odpusť nám hříchy naše i přehánění naše v naší věci! Upevni paty naše a poskytni nám pomoc vítěznou proti lidu nevěřícímu!"'),
(441, 3, 148, 'A Bůh jim dal odměnu v životě pozemském a ještě krásnější odměnu v životě budoucím. A Bůh miluje ty, kdož dobro konají.'),
(442, 3, 149, 'Vy, kteří věříte! Budete-li poslušní těch, kdož nevěří, věru vás oni nazpět obrátí a vy se k tomu vrátíte a ztrátu utrpíte.'),
(443, 3, 150, 'Nikoliv! Bůh je vašim ochráncem a On nejlepší je z pomocníků.'),
(444, 3, 151, 'Vrhneme v srdce těch, kdož nevěří, hrůzu za to, že přidružili k Bohu něco, k čemu On neseslal žádné zplnomocnění. A útulkem jejich bude oheň pekelný - a jak hnusný bude příbytek nespravedlivých!'),
(445, 3, 152, 'I Bůh splnil vůči vám slib Svůj, když z dovolení Jeho jste je poráželi, dokud jste neztratili odvahu a neupadli mezi sebou do sporu o tu věc a nebyli neposlušní poté, když ukázal vám to, co máte rádi. A jsou mezi vámi ti, kdož touží po životě pozemském, a jsou mezi vámi jiní, kdož touží po životě budoucím. A potom vás od nich odvrátil, aby vás vyzkoušel, a věru vám již nyní odpustil - a Bůh je pln laskavosti vůči věřícím.'),
(446, 3, 153, 'Tehdy jste stoupali nahoru neohlížejíce se na nikoho, zatímco posel vás svolával za vašimi zády - a Bůh vám odplácel jedním utrpením za druhým - abyste se nermoutili nad tím, co vám uniklo z kořisti, ani nad tím, co vás postihlo z neúspěchu. A Bůh je dobře zpraven o všem, co děláte.'),
(447, 3, 154, 'Potom na vás Bůh seslal, po tomto utrpení, bezpečí spánku, jenž pokryl jeden oddíl z vás, zatímco druhý oddíl byl v duchu nespokojen a vyslovoval - pravdě navzdory - o Bohu domněnky, jež se podobaly domněnkám doby nevědomosti. I řekli: "Cožpak my máme vůbec nějaké slovo v téhle věci?" Rci: "Všechna tato věc náleží Bohu!" Avšak oni skryli v duších svých to, co ti najevo nedali, a řekli: "Kdybychom měli nějaké slovo v této věci, nebylo by mezi námi zabitých." Rci: "Kdybyste byli zůstali v domech svých, vskutku ti, jimž bylo předepsáno, aby byli zabiti, by byli věru vyšli k místům, kde nyní spočívají. A stalo se tak, aby Bůh vyzkoušel to, co je v hrudích vašich, a aby odhalil, co je v srdcích vašich." A Bůh zná dobře, co v hrudích je skryto.'),
(448, 3, 155, 'Věru ti z vás, kdož obrátili se na útěk v den, kdy střetly se dva oddíly, byli k tomu svedeni pouze satanem za některé z hříchů, jichž se dopustili. A Bůh jim již odpustil, vždyť Bůh je věru odpouštějící, soucitný.'),
(449, 3, 156, 'Vy, kteří věříte! Nebuďte jako ti, kdož neuvěřili a říkají o bratřích svých, když cestují po zemi nebo vydají se do boje: "Kdyby byli zůstali doma, nebyli by zemřeli a nebyli by zabiti!" Kéž by to Bůh učinil zármutkem v srdcích jejich! A Bůh dává život i smrt a Bůh jasně zří vše, co děláte.'),
(450, 3, 157, 'A vskutku, kdybyste byli zabiti na cestě Boží nebo zemřeli, je odpuštění Boží i milost Jeho lepší než to, co shromažďujete.'),
(451, 3, 158, 'A věru, ať již zemřete, anebo budete zabiti, u Boha shromážděni budete!'),
(452, 3, 159, 'Díky milosrdenství Božímu jsi byl k nim shovívavý; kdybys byl hrubý a srdce tvrdého, byli by věru od tebe odpadli. Odpusť jim tedy a pros za ně o odpuštění a poraď se s nimi v každé záležitosti. A až dospěješ k rozhodnutí, spolehni se na Boha, neboť Bůh miluje ty, kdož na Něj se spoléhají.'),
(453, 3, 160, 'Bude-li vám pomáhat Bůh, nikdo nemůže být nad vámi vítězem! Opustí-li vás však, kde je ten, kdo by vám mohl potom pomoci? Na Boha tedy nechť spoléhají se věřící!'),
(454, 3, 161, 'Nesluší se na proroka, aby zpronevěřoval, však ten, kdo podvádí, přinese to, co zpronevěřil, v den zmrtvýchvstání. A potom duše každá obdrží cenu toho, co si vysloužila, a nebude nikomu ukřivděno.'),
(455, 3, 162, 'Zdaž je ten, kdo usiluje o blahovůli Boží, roven tomu, kdo na sebe přivolal hněv Boží a jehož útočištěm bude peklo? Jak hnusný to bude cíl konečný!'),
(456, 3, 163, 'A budou tvořit stupně různé u Boha, a Bůh jasně zří vše, co dělají.'),
(457, 3, 164, 'Věru Bůh již prokázal přízeň věřícím, když k nim vyslal posla z řad jejich, jenž sděluje jim znamení Jeho, očišťuje je a učí je Písmu a moudrosti, zatímco předtím byli v bludu zjevném.'),
(458, 3, 165, 'Což jste neřekli, když postihlo vás neštěstí poté, co sami jste dosáhli úspěchu dvojnásobného: "Odkud to přišlo?" Rci: "Od vás samých to přišlo!" A Bůh nade všemi věcmi je mocný.'),
(459, 3, 166, 'To, co vás postihlo v den, kdy střetly se dva oddíly, stalo se z dovolení Božího, aby Bůh zjistil, kdo je věřící,'),
(460, 3, 167, 'a aby poznal ty, kdož jsou pokrytci. A bylo jim řečeno: "Pojďte, bojujte na stezce Boží, anebo odrážejte nepřítele!" I řekli: "Kdybychom uměli bojovat, věru bychom vás následovali." V ten den byli blíže k nevěrectví než k víře. A hovoří ústy svými, co není v srdcích jejich, avšak Bůh dobře zná vše, co skrývají.'),
(461, 3, 168, 'A to jsou ti, kdož řekli o bratřích svých, zatímco sami se neúčastnili: "Kdyby nás byli poslechli, nebyli by zabiti." Rci: "Odežeňte tedy od sebe smrt, jste-li pravdomluvní!"'),
(462, 3, 169, 'A nepokládej ty, kdož na stezce Boží byli zabiti, za mrtvé! Naopak, oni jsou živí a u Pána svého odměnu svou užívají'),
(463, 3, 170, 'a radují se z toho, co Bůh jim z přízně Své uštědřil, a radují se, že ti, kteří je následují, avšak dosud se k nim nepřipojili, nemusí se ničeho obávat a nebudou zarmouceni.'),
(464, 3, 171, 'A radují se z přízně Boží a laskavosti Jeho a z toho, že Bůh nedopustí, aby ztratila se odměna věřícím patřící.'),
(465, 3, 172, 'A z těch, kdo odpověděli na výzvu Boha a posla poté, co byli utrpěli zranění - těm, kdož dobré konali a bohabojní byli, dostane se odměny nesmírné.'),
(466, 3, 173, 'A když jim lidé řekli: "Lidé se proti vám shromáždili, bojte se jich!", tu zvětšila se ještě víra jejich a zvolali: "Nám postačí Bůh, a jak výtečný On je ochránce!"'),
(467, 3, 174, 'A tak se vrátili díky přízni a laskavosti Boží a nestalo se jim nic zlého. A usilovali o to, co se líbí Bohu, a Bůh pánem je laskavosti nesmírné.'),
(468, 3, 175, 'Hle, toto je satan, který často vám nahání strach svými přáteli, však nebojte se jich, nýbrž bojte se Mne, jste-li věřící.'),
(469, 3, 176, 'Nermuť se pro ty, kdož do nevěrectví se řítí, vždyť oni ničím Bohu neuškodí; Bůh nehodlá dát jim podíl v životě budoucím, leč připravil pro ně trest nesmírný.'),
(470, 3, 177, 'Ti, kdož koupili nevěrectví za víru, v ničem nemohou uškodit Bohu a je očekává trest bolestný.'),
(471, 3, 178, 'Nechť si ti, kdož nevěří, nemyslí, že odklad, který jsme jim povolili, je pro ně dobrý; povolili jsme jim odklad jen proto, aby hříchy své ještě rozmnožili, a je očekává trest zahanbující.'),
(472, 3, 179, 'A Bůh není takový, aby ponechal věřící v tom stavu, v jakém jste, až do doby, kdy rozdělí špatného od dobrého. A Bůh nehodlá vás seznámit s nepoznatelným, nýbrž vybírá si, koho chce ze Svých poslů. Věřte v Boha a posly Jeho, neboť budete-li věřit a bát se Boha, dostane se vám odměny nesmírné.'),
(473, 3, 180, 'Nechť ti, kdož lakotí s tím, co jim Bůh uštědřil z laskavosti Své, se nedomnívají, že je to pro ně lepší; naopak, je to pro ně horší. A budou mít okolo šíjí v den zmrtvýchvstání to, s čím lakotili - a Bohu připadne dědictví nebes a země a Bůh je dobře zpraven o všem, co děláte.'),
(474, 3, 181, 'Bůh věru slyšel řeči těch, kdož hovořili: "Bůh je chudý, zatímco my jsme bohatí!" A zapíšeme si dobře slova jejich i jejich bezprávné zabíjení proroků a pak jim řekneme: "Ochutnejte trestu spalujícího'),
(475, 3, 182, 'za to, co ruce vaše předtím konaly!" A Bůh věru není nespravedlivý ke služebníkům Svým.'),
(476, 3, 183, 'Těm, kdož řekli: "Vždyť Bůh nám uložil, abychom neuvěřili poslu žádnému, dokud nám nepředvede oběť, kterou oheň pozře," odpověz: "Již přede mnou přišli k vám poslové s důkazy jasnými i s tím, o čem jste hovořili. Proč jste je tedy zabíjeli, jste-li pravdomluvní?"'),
(477, 3, 184, 'Jestliže tě prohlašují za lháře, nevšímej si toho, neboť již před tebou byli za ně prohlašováni poslové jiní, kteří přinesli důkazy jasné, Knihy i Písmo osvícené.'),
(478, 3, 185, 'Každá duše okusí smrti; a teprve v den zmrtvýchvstání obdržíte plně své odměny. A kdo se vyhne ohni a bude do ráje uveden, ten dosáhl úspěchu. A život na tomto světě je pouze klamné užíváni.'),
(479, 3, 186, 'A jistě budete vystaveni zkouškám na majetcích svých i osobách svých a uslyšíte od těch, jimž dostalo se Písma před vámi, i od modloslužebníků mnoho zlého. Budete-li však trpěliví a bohabojní, shledáte, že to patří k podstatě věcí.'),
(480, 3, 187, 'A hle, přijal Bůh od těch, kdož dostali Písmo, úmluvu: "Vysvětlujte je lidem a nezatajujte je!" Avšak oni je zahodili za záda svá a koupili za ně věc ceny nepatrné. A jak hnusné je to, co koupili!'),
(481, 3, 188, 'Nedomnívej se, že ti, kdož se radují z toho, co provedli, a rádi se nechávají chválit za to, co vůbec neudělali - nedomnívej se, že tito jsou bezpečni před trestem. Pro ně je určen trest bolestný.'),
(482, 3, 189, 'A Bohu náleží království nebes i země a Bůh je všech věcí mocen.'),
(483, 3, 190, 'Věru ve stvoření nebes a země a ve střídání noci a dne je znamení pro lidi rozmyslem nadané,'),
(484, 3, 191, 'kteří vzývají Boha stojíce, sedíce i ležíce a kteří přemýšlejí o stvoření nebes a země a říkají: "Pane náš, Tys věru nestvořil toto vše pro nic za nic - sláva Tobě! Ušetři nás trestu ohně pekelného!'),
(485, 3, 192, 'Pane náš, Ty věru každého, koho do ohně uvrhneš, hanbou pokryješ; a nespravedliví nebudou mít pomocníků.'),
(486, 3, 193, 'Pane náš, vskutku jsme uslyšeli hlas volajícího, zvoucího nás k víře: Věřte v Pána svého! a uvěřili jsme. Pane náš, odpusť nám hříchy naše a očisti nás od špatností našich a povolej nás k Sobě spolu s čistými!'),
(487, 3, 194, 'Pane náš, a dej nám to, cos nám přislíbil ústy poslů svých, a nezarmuť nás v den zmrtvýchvstání, vždyť Ty neopomíjíš splnit sliby Své!"'),
(488, 3, 195, 'A Pán jejich je vyslyšel: "Nedopustím věru, aby se ztratil skutek jediný, který kdokoliv z vás učiní, ať je to muž či žena, vždyť jeden k druhému patříte. A těm, kdož se vystěhovali a byli z domovů svých vyhnáni a na cestě mé strádali a bojovali a byli přitom zabiti, těm věru vymažu špatné činy jejich a uvedu je do zahrad, pod nimiž řeky tekou." To bude odměnou od Boha - a Bůh zajisté má u Sebe odměnu nejkrásnější.'),
(489, 3, 196, 'Nechť tě neoklame to, že ti, kdož jsou nevěřící, se volně po zemi procházejí;'),
(490, 3, 197, 'krátké bude jejich užívání a pak stane se peklo příbytkem jejich. A jak hnusné to bude místo odpočinku!'),
(491, 3, 198, 'Avšak pro ty, kdož Pána svého se obávali, jsou určeny zahrady, pod nimiž řeky tekou - a v nichž nesmrtelní budou - jako obydlí od Boha. A to, co u Boha je, je nejlepší pro čisté.'),
(492, 3, 199, 'A jsou mezi vlastníky Písma někteří, kteří věří v Boha i v to, co seslal vám, i v to, co seslal jim; pokorní před Bohem nekupují za znamení Boží věci ceny nepatrné. Ti dostanou odměnu svou u Pána svého a Bůh věru je rychlý v účtování.'),
(493, 3, 200, 'Vy, kteří věříte! Buďte neochvějní a povzbuzujte se k neochvějnosti! Buďte pevní a bojte se Boha - snad budete blažení!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Lidé, bojte se Pána svého, jenž stvořil vás z bytosti jediné a stvořil z ní manželku její a rozmnožil je oba v množství velké mužů i žen. A bojte se Boha, v Jehož jménu se vzájemně prosíte, a dbejte na pravidla o pokrevních svazcích, neboť Bůh zajisté nad vámi je pozorovatelem.'),
(495, 4, 2, 'A dávejte sirotkům majetky jejich a nezaměňujte špatné za dobré! Nepohlcujte jejich jmění přidávajíce je k majetkům svým, vždyť to hřích je veliký!'),
(496, 4, 3, 'Bojíte-li se, že nebudete spravedliví k sirotkům... berte si za manželky ženy takové, které jsou vám příjemné, dvě, tři a čtyři; avšak bojíte-li se, že nebudete spravedliví, tedy si vezměte jen jednu nebo ty, jimiž vládnou pravice vaše. A tak se nejlépe vyhnete odchýlení.'),
(497, 4, 4, 'A dávejte ženám obvěnění jako dar; jestliže vám z laskavosti dají něco z toho, tedy toho užijte ve zdraví a spokojenosti.'),
(498, 4, 5, 'A nedávejte nerozumným jmění své, jež Bůh pro vás učinil podstatou obživy, ale dávejte jim z něho na jejich výživu a odívání a mluvte s nimi slovo vlídné.'),
(499, 4, 6, 'A zkoušejte schopnosti sirotků, dokud nedospějí do věku sňatku; postřehnete-li pak u nich zralost úsudku, tedy jim předejte majetky jejich. A nepohlcujte z něho nadbytečně a chvatně, dokud oni nedospějí. Nechť ten, kdo je bohatý, se zdrží a nechť ten, kdo je chudý, jí z něho jen podle zvyklosti oprávněné. A když jim předáte majetky jejich, dejte si to potvrdit svědky - a Bůh postačí jako zúčtovatel.'),
(500, 4, 7, 'Mužům náleží podíl z toho, co zůstavili rodiče a blízcí příbuzní, a ženám náleží podíl z toho, co zůstavili rodiče a blízcí příbuzní - ať již je toho málo, nebo mnoho - jako podíl určený.'),
(501, 4, 8, 'A jsou-li přítomni při rozdělování příbuzní, sirotci a chudí, dejte jim něco z toho a promluvte s nimi slovo vlídné.'),
(502, 4, 9, 'A nechť se střeží ti, kdož - v případě, že po sobě zanechají slabé potomstvo - se o ně budou také strachovat, utiskovat jiné! Nechť bojí se Boha a nechť hovoří slovo přímé!'),
(503, 4, 10, 'Ti, kdož nespravedlivě pohlcuji majetky sirotků, věru pohlcují to, co v břichách jejich v oheň se promění, a spáleni budou jeho plameny.'),
(504, 4, 11, 'A Bůh vám stanoví o dětech vašich toto: synovi podíl rovný podílu dvou dcer; a je-li dcer více než dvě, patří jim dvě třetiny toho, co zůstavil. A jestliže je dcera pouze sama, tedy jí patří polovina. A rodičům jeho: každému z nich jedna šestina toho, co zůstavil, jestliže měl mužského potomka. Jestliže neměl mužského potomka, pak po něm dědí jeho rodiče, přičemž matce jeho patří jedna třetina. A jestliže měl bratry, pak matce náleží jedna šestina toho, co zůstane po vyplacení odkazů a uhrazení dluhu". A vy nevíte, kdo z rodičů či dětí vašich je vám užitečnější. A toto je ustanovení od Boha a věru Bůh je vševědoucí, moudrý.'),
(505, 4, 12, 'Vám náleží polovina z toho, co zůstavily manželky vaše, jestliže neměly mužského potomka. A jestliže měly mužského potomka, patří vám z toho, co zůstavily, jedna čtvrtina poté, když byly vyplaceny odkazy a uhrazeny dluhy jejich. A manželkám patří jedna čtvrtina toho, co zůstavíte, jestliže nemáte mužského potomka. A jestliže máte mužského potomka, pak patří manželkám jedna osmina toho, co jste zůstavili po vyplacení odkazů a po uhrazení dluhů. Jestliže muž nebo žena zanechají dědictví po boční linii a mají bratra nebo sestru, pak každému z obou patří jedna šestina. A je-li jich více, pak jsou podílníky na jedné třetině po vyplacení odkazů a po uhrazení dluhů aniž se tím někomu uškodí. A toto je ustanovení od Boha a Bůh je vševědoucí, blahovolný.'),
(506, 4, 13, 'A toto jsou omezení Boží, a kdokoliv poslouchá Boha a posla Jeho, tomu Bůh dá vstoupit do zahrad, pod nimiž řeky tekou, a v nich nesmrtelný bude - a to úspěch je nesmírný.'),
(507, 4, 14, 'Kdo však neposlechne Boha a posla Jeho a překračuje omezení Jeho, tomu Bůh dá vstoupit do ohně a tam nesmrtelný bude - a pro něj určen je trest zahanbující.'),
(508, 4, 15, 'Proti těm z vašich žen, jež dopustí se hanebnosti, vezměte jako svědky čtyři z vás. A jestliže tito to dosvědčí, podržte ženy v domech, dokud smrt je nezavolá anebo Bůh pro ně nepřipraví nějaké východisko.'),
(509, 4, 16, 'Ty dva z vás, kteří se toho dopustí, potrestejte! A jestliže se pak kajícně obrátí a polepší, nechte je být! A Bůh věru je milostivý ke kajícníkům a slitovný.'),
(510, 4, 17, 'A jedině Bohu patří rozhodnutí a pokání těch, kdož dopustili se špatného z nevědomosti a potom se kajícně obrátili v krátké době. Těm Bůh věru odpustí a Bůh je vševědoucí a moudrý.'),
(511, 4, 18, 'Však odpuštění nebude dáno těm, kdož dopouštějí se špatností a teprve ve chvíli, když smrt se pro některého z nich dostaví, zvolají: "a já nyní se kaji!" A nebude také pro ty, kdož zemřou jako nevěřící - pro ty jsme připravili trest bolestný.'),
(512, 4, 19, 'Vy, kteří věříte! Není vám dovoleno získávat ženy jako dědictví proti vůli jejich; neutiskujte je jen proto, abyste si mohli odnést část toho, co jste jim dříve darovali, leda že by se dopustily zjevné hanebnosti. A zacházejte s nimi podle zvyklostí uznaných! Jestliže k nim cítíte odpor, je možné, že cítíte odpor k něčemu, do čeho Bůh vložil velké dobro.'),
(513, 4, 20, 'A jestliže chcete vyměnit jednu manželku za jinou a dali jste jedné z nich qintár, pak od ní neberte při rozvodu nic. Což snad byste to chtěli vzít, dopouštějíce se tak nečestnosti a hříchu zjevného?'),
(514, 4, 21, 'A jak byste jí to mohli vzít, když jste byli jeden s druhým úzce svázáni a ony od vás již přijaly závazek pevný?'),
(515, 4, 22, 'A neberte si za manželky ženy, jež byly provdány za vaše otce, leda že se to stalo už v minulosti. A věru je to hanebnost zavrženíhodná a způsob hnusný!'),
(516, 4, 23, 'A jsou vám zakázány jako manželky vaše matky, vaše dcery, vaše sestry, vaše tety z otcovy i matčiny strany, vaše neteře z bratrovy a sestřiny strany, vaše kojné a vaše soukojenky a matky vašich žen a vaše nevlastní dcery, jež jsou ve vaší péči a jež se narodily z žen, k nimž jste již vešli - však jestliže jste k nim nevešli, není to pro vás hříchem - a manželky vašich synů, narozených z vašeho ledví, a dále dvě sestry současně, ledaže se to stalo již v minulosti. A Bůh je věru odpouštějící, slitovný.'),
(517, 4, 24, 'A (jsou vám zakázány také) muhsany mezi ženami kromě těch, jichž zmocnily se vaše pravice a to je vám předepsáno Bohem! A vedle toho je vám dovoleno, abyste si hledali manželky pomocí jmění svého jako muži spořádaní, nikoliv prostopášní. A za to, co jste užívali s těmito ženami, dejte jim jejich odměnu podle ustanovení. A nebude pro vás hříchem, na čem se vzájemně dohodnete po stanovené době - a Bůh je vševědoucí, moudrý.'),
(518, 4, 25, 'Kdo z vás se nemůže pro nedostatek prostředků oženit s věřícími muhsanami, nechť vezme si z těch, jimiž vládnou vaše pravice, věřící dívky; a Bůh zná dobře vaši víru, a že patříte jeden k druhému. Ožeňte se s nimi se svolením jejich lidí a dejte jim jejich odměnu podle uznaných zvyklostí jako muhsanám, a nikoliv jako prostopášnicím nebo jako těm, jež mají milence. A když se stanou muhsanami a dopustí se potom hanebnosti, nechť jejich trestem je poloviční trest příslušející muhsanám. A to je pro ty z vás, kdož obávají se, že dopustí se prohřešku, avšak budete-li trpěliví, bude to pro vás nejlepší. A Bůh je odpouštějící, slitovný.'),
(519, 4, 26, 'A Bůh vás chce vést a chce vám objasnit obvyklý osud těch, kdož byli před vámi, a přijmout milostivě pokání vaše. A Bůh je vševědoucí, moudrý.'),
(520, 4, 27, 'A Bůh chce přijmout pokání vaše, avšak ti, kdož následují vášně své, chtějí, abyste se uchýlili velkou úchylkou.'),
(521, 4, 28, 'Bůh přeje si vám ulehčit, vždyť člověk byl věru stvořen slabým.'),
(522, 4, 29, 'Vy, kteří věříte! Nepohlcujte si vzájemně majetky své podvodným způsobem; jiné je, jde-li o obchod na základě vzájemného souhlasu; a nepřivozujte si smrt! A věru Bůh je vůči vám slitovný.'),
(523, 4, 30, 'A kdokoliv tak učiní jako přestupek a nespravedlivě, toho věru ohněm sežehneme - a to je pro Boha nadmíru snadné!'),
(524, 4, 31, 'Jestliže se vystříháte těžkých hříchů, které jsou vám zakázány, věru vám vymažeme vaše špatné skutky a uvedeme vás do ráje uvedením čestným.'),
(525, 4, 32, 'Nechovejte touhu po tom, čím Bůh vyznamenal některé z vás před jinými! Mužům dostane se části z toho, co si vysloužili, a ženám se také dostane části z toho, co si vysloužily. A proste Boha o část přízně Jeho, vždyť Bůh zná dobře všechny věci.'),
(526, 4, 33, 'A každému jsme určili dědice, mající právo na to, co zůstavil: rodiče, blízké příbuzné a ty, s nimiž spojeni jste přísahou. Všem z nich dejte podíl jejich! Bůh věru je svědkem všeho.'),
(527, 4, 34, 'Muži zaujímají postavení nad ženami proto, že Bůh dal přednost jedněm z vás před druhými, a proto, že muži dávají z majetků svých (ženám). A ctnostné ženy jsou pokorně oddány a střeží skryté kvůli tomu, co Bůh nařídil střežit. A ty, jejichž neposlušnosti se obáváte, varujte a vykažte jim místa na spaní a bijte je! Jestliže vás jsou však poslušny, nevyhledávejte proti nim důvody! A Bůh věru je vznešený, veliký.'),
(528, 4, 35, 'A obáváte-li se rozvratu mezi oběma, povolejte rozhodčího z jeho rodiny a rozhodčího z její rodiny. A přejí-li si oba manželé usmíření, Bůh mezi nimi zařídí shodu, neboť Bůh je vševědoucí a dobře zpraven.'),
(529, 4, 36, 'Ctěte Boha a nepřidružujte k Němu nic! Chovejte se vlídně k rodičům, příbuzným, sirotkům, chudým, sousedovi pokrevně spřízněnému i cizímu, příteli ze sousedství a jdoucímu po cestě Boží a těm, jimiž vládne vaše pravice! Bůh věru nemiluje ty, kdož jsou domýšliví a vychloubační,'),
(530, 4, 37, 'ani ty, kdož jsou lakomí a lidem lakotu nařizují a skrývají to, co Bůh jim uštědřil ze Své přízně. A připravili jsme pro nevěřící trest ponižující.'),
(531, 4, 38, 'A nemiluje ty, kdož rozdávají ze svého jmění na odiv lidem a nevěří ani v Boha, ani v soudný den. A ten, jemuž je satan přítelem, ten špatného má přítele!'),
(532, 4, 39, 'Co by je to bylo stálo, kdyby byli uvěřili v Boha a v den soudný a rozdávali z toho, co jim Bůh uštědřil? A Bůh o nich dobře ví!'),
(533, 4, 40, 'Bůh věru neošidí nikoho ani o váhu prášku jediného, a jde-li o skutek dobrý, oplatí jej dvojnásobně a sám od Sebe dá odměnu nesmírnou.'),
(534, 4, 41, 'A jak jim bude, až z národa každého přivedeme svědka jednoho a přivedeme také tebe jako svědka proti těmto?'),
(535, 4, 42, 'V ten den si budou přáteli, kdož neuvěřili a posla neuposlechli, aby se nad nimi slehla země, avšak neskryjí před Bohem událost žádnou!'),
(536, 4, 43, 'Vy, kteří věříte! Nepřibližujte se k modlitbě, jste-li opilí, dokud nebudete vědět, co říkáte; a nekonejte ji, jste-li znečištěni polucí - leda jste-li na cestě - dokud jste se neumyli! A jste-li nemocní nebo na cestách či někdo z vás přišel ze záchodu či měl styk se ženami, a nenaleznete-li vodu, omývejte se čistým pískem a otřete si jim obličeje i ruce své! A Bůh je věru promíjející a odpouštějící.'),
(537, 4, 44, 'Což jsi neviděl, že ti, kterým dostalo se části Písma, kupují bludy a chtějí, abyste i vy zbloudili z cesty pravé?'),
(538, 4, 45, 'Bůh však dobře zná vaše nepřátele! A Bůh je dostatečným ochráncem i dostatečným pomocníkem.'),
(539, 4, 46, 'Někteří z těch, kdož vyznávají židovství, překrucují smysl slov Písma a říkají "Slyšeli jsme a neuposlechli jsme" a "Slyš, aniž to je slyšet" a "'),
(540, 4, 47, 'Vy, kterým bylo dáno Písmo, věřte v to, co jsme vám seslali jako potvrzení toho, co již máte od dřívějška, dříve než smažeme obličeje a obrátíme je nazad anebo je proklejeme, jako jsme již prokleli lid znesvěcující sobotu. A rozkaz Boží byl již vykonán!'),
(541, 4, 48, 'Bůh věru neodpustí, je-li k Němu něco přidružováno, ale odpustí, komu chce, věci jiné než toto. A kdokoli přidružuje k Bohu, ten dopouští se hříchu těžkého.'),
(542, 4, 49, 'Což jsi neviděl ty, kteří se vydávají za čisté? Nikoliv, jen Bůh očišťuje toho, koho chce, a nikdo nebude ošizen ani o slupku pecky datlové.'),
(543, 4, 50, 'Pohleď, jak vymýšlejí si lži o Bohu - a to stačí k hříchu zjevnému!'),
(544, 4, 51, 'Což jsi neviděl, že ti, kterým dostalo se části Písma, věří v Džibta a Tághúta a hovoří o těch, kdož neuvěřili: "Tito jsou správněji vedeni než ti, kdož uvěřili."'),
(545, 4, 52, 'To jsou ti, které Bůh proklel; pro toho pak, koho Bůh proklel, nenajdeš pomocníka žádného.'),
(546, 4, 53, 'Anebo mají snad podíl nějaký na vládě? A i kdyby jej měli, nedali by lidem ani slupku pecky datlové!'),
(547, 4, 54, 'Či závidí těmto lidem to, co jim Bůh dal z přízně Své? Vždyť dali jsme již rodu Abrahamovu Písmo i moudrost a propůjčili jsme jim království mocné.'),
(548, 4, 55, 'A jsou mezi nimi někteří, kdož v ně uvěřili, a jsou mezi nimi jiní, kteří se od něho odvrátili. Však bude jim stačit peklo jako trest spalující!'),
(549, 4, 56, 'Věru ty, kdož neuvěřili v Naše znamení, My v ohni sežehneme, a kdykoliv vyschnou kůže jejich, vyměníme je za jiné, aby tak trestu okusili. A Bůh zajisté je mocný, moudrý.'),
(550, 4, 57, 'Ty však, kdož uvěřili a dobré skutky konali, uvedeme do zahrad, pod nimiž řeky tekou, a tam, nesmrtelní, navěky budou mít manželky čisté; a uvedeme je do stínu hustého.'),
(551, 4, 58, 'Bůh vám přikazuje, abyste vraceli svěřené majetky jejich vlastníkům, a když soudíte mezi lidmi, abyste soudili spravedlivě. Vždyť Bůh - a jak krásné je to, k čemu vás napomíná - vždyť Bůh slyšící je i jasnozřivý.'),
(552, 4, 59, 'Vy, kteří věříte! Poslouchejte Boha a poslouchejte posla a ty, kdož mezi vámi mají autoritu! A jste-li ve sporu o nějakou věc, předejte její rozhodnutí Bohu a poslu - jestliže věříte v Boha a v den soudný! A to bude nejlepší a povede k nejkrásnějšímu výsledku.'),
(553, 4, 60, 'Což jsi neviděl ty, kdož tvrdí, že uvěřili v to, co ti bylo sesláno a co bylo sesláno před tebou? A přitom chtějí předkládat své spory k rozsouzení Tághútovi, ačkoliv jim bylo nařízeno, aby v něj nevěřili. A satan si přeje, aby zbloudili dalekým zblouděním.'),
(554, 4, 61, 'A když se jim řekne: "Pojďte k tomu, co seslal Bůh, a k poslu Jeho", tu vidíš pokrytce, jak se snaží od tebe odvrátit lidi úplným odvratem.'),
(555, 4, 62, 'A jak jim bude, až je neštěstí postihne za to, co ruce jejich předtím spáchaly, a až k tobě přijdou přisahajíce při Bohu: "My přáli jsme si jen dobré a spolupráci!"'),
(556, 4, 63, 'A to jsou ti, o nichž Bůh dobře ví, co v srdcích svých tají. Odvrať se od nich a napomeň je a promluv do duší jejich slova naléhavá!'),
(557, 4, 64, 'A nevyslali jsme posla žádného, leč aby mu byla, z dovolení Božího, prokazována poslušnost. Však jestliže k tobě přijdou poté, když sami sobě ukřivdili, prosíce Boha o slitování a žádajíce, aby posel poprosil o slitování pro ně, pak věru naleznou, že Bůh odpouští kajícníkům a je slitovný.'),
(558, 4, 65, 'Však nikoliv, při Pánu tvém, oni nebudou věřící, pokud tě neučiní rozhodčím v tom, o čem jsou ve sporu. Potom nenaleznou v duších svých pochybnosti o tom, cos rozhodl, a podrobí se plným podrobením.'),
(559, 4, 66, 'Kdybychom jim byli přikázali, aby se sami zabíjeli anebo aby odešli z příbytků svých, nebyli by tak - leda s výjimkou několika málo z nich - učinili. Avšak kdyby byli učinili to, k čemu jsme je napomínali, bylo by to bývalo pro ně nejlepší a účinnější pro utvrzení víry jejich.'),
(560, 4, 67, 'Tehdy bychom jim byli darovali odměnu nesmírnou'),
(561, 4, 68, 'a byli bychom je uvedli na stezku přímou.'),
(562, 4, 69, 'Ti, kdož poslouchají Boha a posla, budou spolu s proroky, bezúhonnými, mučedníky a zbožnými, jimž všem Bůh prokázal milost. A jak překrásní to budou společníci!'),
(563, 4, 70, 'Takové je dobrodiní od Boha přicházející - a Bůh plně dostačuje jako vševědoucí!'),
(564, 4, 71, 'Vy, kteří věříte! Buďte ostražití a vrhejte se do boje po oddílech anebo se vrhejte hromadně!'),
(565, 4, 72, 'A věru vyskytne se mezi vámi i takový, jenž zaváhá; a když na vás dolehne neštěstí, řekne: "Bůh mi prokázal milost, neboť jsem nebyl spolu s vámi svědkem bojování."'),
(566, 4, 73, 'Však dostane-li se vám od Boha dobrodiní, tu jistě řekne, jako by mezi vámi a jím nebyly nikdy žádné přátelské svazky: "Kéž bych byl býval s vámi a kéž bych byl též dosáhl úspěchu nesmírného!"'),
(567, 4, 74, 'Nechť tedy bojují na stezce Boží ti, kdož kupují za život pozemský život budoucí! A těm, kdož bojují na stezce Boží a budou zabiti či zvítězí, těm dáme odměnu nesmírnou.'),
(568, 4, 75, 'A co je vám, že nebojujete na stezce Boží a za utištěné z mužů, žen a dětí, kteří říkají: "Pane náš, vyveď nás z tohoto města, jehož obyvatelé jsou nespravedliví! Dej nám ochránce Tebou vybraného a pošli nám pomocníka Tebou ustanoveného!"'),
(569, 4, 76, 'Ti, kdož uvěřili, bojují na stezce Boží, a ti, kdož neuvěřili, bojují na stezce Tághúta. Bojujte proti přátelům satanovým, vždyť slabé jsou proti vám úklady satanovy!'),
(570, 4, 77, 'Což jsi neviděl ty, jimž bylo řečeno: "Spusťte své ruce, dodržujte modlitbu a dávejte almužnu!" A když jim bylo předepsáno bojovat, hle, jedna skupina z nich, jež bojí se lidí tak, jak bojí se Boha, anebo ještě větším strachem, zvolala: "Pane náš, proč jsi nám předepsal boj? Nemohl bys nám jej ještě o malou chvíli odložit?" Rci: "Užívání na tomto světě je jen nepatrné, zatímco onen svět je daleko lepší pro toho, jenž je bohabojný - a nebudete ošizeni ani o slupku pecky datlové!"'),
(571, 4, 78, 'Ať kdekoliv budete, smrt vás zastihne, i kdybyste byli ve věžích opevněných. Stane-li se těmto lidem něco dobrého, říkají: "Tohle je od Boha", však postihne-li je co zlého, tu říkají: "Tohle je od tebe, proroku!" Rci: "Všechno od Boha přichází!" A co je těmto lidem, že málem nemohou pochopit to, co se jim říká?'),
(572, 4, 79, 'To dobré, jehož se ti dostane, pochází od Boha a to zlé, jež tě postihne, pochází od tebe samého. A vyslali jsme tě k lidem jako posla a Bůh ti stačí jako svědek!'),
(573, 4, 80, 'Kdokoliv je poslušen posla, je poslušen i Boha, a kdokoliv se obrátí zády... vždyť jsme tě neposlali, abys byl dozorcem nad nimi.'),
(574, 4, 81, 'Říkají "Poslušnost!", ale když od tebe odejdou, tu část z nich tráví noc hloubáním o něčem jiném, než jsi říkal. A Bůh zapisuje to, co v noci dělají. Straň se jich a spolehni se na Boha - a Bůh plně dostačuje jako ochránce!'),
(575, 4, 82, 'Cožpak nepřemýšlejí o Koránu? Kdyby ten byl od někoho jiného než od Boha, věru by v něm nalezli četné rozpory!'),
(576, 4, 83, 'A když k nim dojde nějaká zpráva týkající se bezpečnosti či strach vzbuzující, tu ji hned rozhlásí. Kdyby se však s ní obrátili na posla anebo na ty, kdož mezi nimi mají autoritu, dozvěděli by se ji tak ti, kteří se mezi nimi snaží proniknout k pravdivosti její. A kdyby nebylo dobrodiní a milosrdenství Božího vůči vám, věru byste byli všichni následovali satana, kromě několika málo.'),
(577, 4, 84, 'Bojuj na stezce Boží, vždyť neponeseš břímě jiné než své vlastní! Povzbuzuj věřící a možná že Bůh zadrží nápor nevěřících - a Bůh je mocnější v náporu a silnější v trestáni.'),
(578, 4, 85, 'Kdo se přimluví krásnou přímluvou, bude mít z ní podíl; kdo však se přimluví s úmyslem špatným, dostane také z toho podíl - a Bůh dává na vše dobrý pozor.'),
(579, 4, 86, 'Když jste pozdraveni zdvořilým pozdravem, pozdravte ještě vlídněji nebo odpovězte stejným způsobem. A Bůh věru vše počítá.'),
(580, 4, 87, 'Bůh - a není božstva kromě Něho - vás vskutku shromáždí pro den zmrtvýchvstání, o němž pochyby není. A kdo pravdivější je než Bůh ve svém vyprávění?'),
(581, 4, 88, 'Proč jste utvořili dvě skupiny různého mínění vzhledem k pokrytcům, zatímco sám Bůh je zavrhl za to, co provedli? Chcete vést cestou správnou ty, jimž dal Bůh zbloudit? Jestliže Bůh dal někomu zbloudit, ty pro něj cestu nenalezneš!'),
(582, 4, 89, 'A přáli by si, abyste se stali nevěřícími, jako jsou oni, a abyste byli stejní. Neberte si mezi nimi přátele, dokud se nevystěhují na stezku Boží! A jestliže se obrátí zády, pak je chyťte a zabte, kdekoliv je naleznete! A neberte si z nich ani přátele, ani pomocníky'),
(583, 4, 90, 'kromě těch, kteří jsou spojeni s lidem, mezi nímž a vámi jest úmluva, anebo kteří k vám přišli se stísněnými hruděmi z toho, že mají bojovat proti vám nebo proti lidu svému. Kdyby byl Bůh chtěl, byl by jim dal vládu nad vámi a oni by byli proti vám bojovali. Jestliže však se vás straní, aniž s vámi bojovali, a vzdají se vám na milost, pak Bůh vám nedává proti nim žádné oprávnění k boji.'),
(584, 4, 91, 'A najdete i jiné pokrytce, kteří chtějí získat důvěru vaši i důvěru svého lidu. Kdykoliv se pokusí vás svést k odpadnutí, budou v tom odraženi. A jestliže se nebudou držet stranou od vás a nevzdají se vám na milost a nesloží své ruce, tedy je chyťte a zabte, kdekoliv je naleznete! A nad těmito vám dáváme pravomoc zřetelnou.'),
(585, 4, 92, 'A nehodí se, aby věřící zabil věřícího, leda omylem. A kdokoliv zabije omylem věřícího, vykoupí se propuštěním věřícího otroka a zaplacením odškodného rodině zabitého, ledaže ta je dá jako almužnu. Jestliže byl zabitý z lidu vám nepřátelského, avšak věřící, vykoupíte se propuštěním věřícího otroka. Byl-li z lidu, mezi nímž a vámi je smlouva, vykoupíte se zaplacením odškodného jeho rodině a propuštěním věřícího otroka. Kdo však nenajde prostředek k tomuto, nechť se postí dva po sobě následující měsíce na pokání před Bohem. A Bůh je vševědoucí, moudrý.'),
(586, 4, 93, 'Kdokoliv zabije úmyslně věřícího, bude jeho odměnou peklo a bude v něm nesmrtelný. A Bůh se naň rozhněvá a prokleje ho a připraví mu trest strašlivý.'),
(587, 4, 94, 'Vy, kteří věříte! Když vyrazíte do boje na stezce Boží, buďte obezřetní a neříkejte každému, kdo vám nabídne mír: "Ty nejsi věřící!", snažíce se tak získat statky tohoto života pozemského. Vždyť u Boha je kořist hojná. Takoví jste byli dříve, avšak nyní se Bůh smiloval nad vámi. Buďte tedy obezřetní, neboť Bůh je dobře zpraven o všem, co děláte.'),
(588, 4, 95, 'Nejsou si rovni ti věřící, kteří zůstali sedět doma - kromě těch, kdož jsou neschopní, s těmi, kdož usilovně bojují na stezce Boží majetkem svým i osobami svými. Bůh povýšil ty, kdož usilovně bojují majetkem svým i osobami svými, o stupeň výše než ty, kdož zůstali sedět doma. Všem Bůh přislíbil odměnu překrásnou, avšak vyznamenal ty, kdož usilovně bojují, nad těmi, kdož sedí doma, udělením Své odměny nesmírné'),
(589, 4, 96, 'a ve stupních u Sebe, v odpuštění i v milosrdenství. A Bůh je odpouštějící, slitovný.'),
(590, 4, 97, 'Těch, kdož sami sobě ukřivdili a kteří povoláni budou anděly, se zeptají: "V jakém stavu jste byli?" I odvětí: "Byli jsme poníženými na zemi." I řeknou jim andělé: "Což nebyla země Boží dosti rozlehlá, že jste se nemohli vystěhovat?" A obydlím těchto bude peklo, a jak hnusný je to cíl konečný!'),
(591, 4, 98, 'Jen utištěným mužům, ženám a dětem, kteří nemohli použít žádné lsti k vystěhování a nemohli nalézt správnou cestu,'),
(592, 4, 99, 'Bůh možná vymaže viny jejich, neboť Bůh je odpouštějící, milostivý.'),
(593, 4, 100, 'Kdokoliv se vystěhuje kvůli cestě Boží, ten nalezne na zemi četné jiné možnosti i prostor. A kdo opustí dům svůj a vystěhuje se k Bohu a Jeho poslu a zastihne jej přitom smrt, toho odměnit připadá Bohu a Bůh je odpouštějící, slitovný.'),
(594, 4, 101, 'A pohybujete-li se po zemi, není pro vás hříchem zkrátit modlitbu, obáváte-li se, že vás vystaví pokušení ti, kdož nevěří. A nevěřící jsou vskutku pro vás nepřáteli zjevnými.'),
(595, 4, 102, 'A když budeš mezi nimi řídit pro ně modlitbu, nechť z nich jedna skupina stojí při modlitbě s tebou a vezme si zbraně své. A když vykonají padnutí na tvář, nechť potom odstoupí dozadu. Pak nechť přijde druhá skupina, která se ještě nemodlila, a nechť modlí se s tebou; a nechť jsou ostražití a vezmou si zbraně své. A přáli by si nevěřící, abyste nedávali pozor na své zbraně a zásoby, aby vás mohli napadnout jediným nenadálým úderem. A nebude pro vás hříchem, odložíte-li své zbraně, jste-li obtěžováni deštěm anebo jste-li nemocní - buďte však ostražití! A Bůh věru připravil pro nevěřící trest zahanbující.'),
(596, 4, 103, 'A když končíte modlitbu, vzývejte Boha vstoje, vsedě i vleže! A jste-li v bezpečí, dodržujte modlitbu, neboť modlitba je pro věřící předepsána v čas stanovený!'),
(597, 4, 104, 'Neochabujte v pronásledování těchto lidí! A jestliže vy strádáte, strádají i oni, a stejně jako vy strádáte, avšak vy doufáte od Boha v to, v co oni nemohou doufat. A Bůh je vševědoucí, moudrý.'),
(598, 4, 105, 'A věru jsme ti seslali Písmo s pravdou, abys soudil mezi lidmi podle toho, co ti Bůh ukázal. A nebuď obhájcem zrádcům'),
(599, 4, 106, 'a pros Boha za odpuštění, neboť Bůh je odpouštějící, slitovný!'),
(600, 4, 107, 'Nevstupuj s námi do sporu ve prospěch těch, kdož sami sebe podvádějí, neboť Bůh nemiluje podvodníky ani hříšníky.'),
(601, 4, 108, 'Snaží se ukrýt před lidmi, avšak neukryjí se před Bohem, který je stále s nimi, když v noci vedou řeči, jež se Mu nelíbí - a Bůh objímá věděním Svým vše, co dělají.'),
(602, 4, 109, 'Hle, vy jste ti, kdož přou se v prospěch jejich v životě pozemském, však kdo se bude o ně přít s Bohem v den zmrtvýchvstání či kdo bude jejich ochráncem?'),
(603, 4, 110, 'A kdo se dopustil skutku špatného či ukřivdil sám sobě a potom poprosí Boha o odpuštění, věru shledá, že Bůh je odpouštějící, slitovný.'),
(604, 4, 111, 'A kdo se dopustí hříchu, tedy se ho věru dopouští jen proti sobě samému - a Bůh je vševědoucí, moudrý.'),
(605, 4, 112, 'A kdo se dopustí chyby nebo hříchu a potom z toho obviní nevinného, dopouští se tím osočování a hříchu zjevného.'),
(606, 4, 113, 'A kdyby nebylo přízně Boží vůči tobě a milosti Jeho, věru by byla skupina z nich uvažovala o tom, aby tě zavedla v blud - avšak oni uvedli v blud jen sami sebe a nemohou ti v ničem uškodit. Bůh ti seslal Písmo a moudrost a naučil tě tomu, cos neznal - a přízeň Boží vůči tobě je nesmírná!'),
(607, 4, 114, 'Není nic dobrého ve většině jejich tajných schůzek, leda u toho, kdo přikazuje almužnu či dobré skutky anebo svornost mezi lidmi. A kdo toto činí ve snaze dosíci zalíbení Božího, tomu dáme odměnu nesmírnou.'),
(608, 4, 115, 'A kdo odpadne od posla poté, co mu bylo jasně ukázáno správné vedení, a sleduje pak cestu jinou než věřící, k tomu obrátíme se zády, tak jako on se obrátil, a necháme jej hořet v pekle a jak hnusný je to cíl konečný!'),
(609, 4, 116, 'Bůh neodpustí, je-li k Němu něco přidružováno, ale odpustí vše, co je mimo toto, tomu, komu chce. A kdo přidružuje k Bohu, ten zabloudil zblouděním dalekým.'),
(610, 4, 117, 'A nevzývají vedle Něho nikoho než bytosti ženské a tak vzývají jen satana odbojného,'),
(611, 4, 118, 'jehož Bůh proklel. I řekl satan: "Věru si vezmu určitou část ze služebníků Tvých'),
(612, 4, 119, 'a dám jim zbloudit a vzbudím v nich vášně a nařídím jim, aby uřezávali uši dobytku, a nařídím jim, aby hyzdili stvoření Boží! " Ten, kdo si bere satana jako ochránce místo Boha, ten utrpí ztrátu zjevnou.'),
(613, 4, 120, 'On jim slibuje a vášně v nich vzbuzuje, avšak to, co jim slibuje satan, není leč klam.'),
(614, 4, 121, 'A příbytkem těchto lidí bude peklo a nenaleznou z něho východiska.'),
(615, 4, 122, 'Těm však, kdož uvěřili a zbožné skutky konali, těm dáme vstoupit do zahrad, pod nimiž řeky tekou, v nichž nesmrtelní budou navěky, podle pravdivého slibu Božího. A kdo je pravdivější ve slovu svém než Bůh?'),
(616, 4, 123, 'To nezávisí na přáních vašich ani na přáních vlastníků Písma. Ten, kdo činí zlo, bude za to odměněn a nenalezne pro sebe kromě Boha ani ochránce, ani pomocníka.'),
(617, 4, 124, 'A ten, kdo koná dobré a je věřící - ať je to muž, či žena - ten vejde do ráje a nebude ošizen ani o slupku pecky datlové.'),
(618, 4, 125, 'Kdo lepší má náboženství než ten, jenž odevzdal se do vůle Boží, koná dobré skutky a následuje víru Abrahama, jenž byl hanífem? A učinil Bůh Abrahama Svým přítelem.'),
(619, 4, 126, 'Bohu náleží vše, co na nebesích je i na zemi, a Bůh objímá vše ve vědění Svém.'),
(620, 4, 127, 'A žádají tě o vysvětlení ohledně žen. Odpověz: "Bůh vám již dal o nich vysvětlení a je zde i to, co vám bylo přednášeno z Písma o sirotcích ženského pohlaví, kterým jste nedali to, co je pro ně předepsáno, a které si přejete vzít za manželky, a dále o dětech slabých a o spravedlivém jednání se sirotky. A o všem, co dobrého učiníte, Bůh dobře ví."'),
(621, 4, 128, 'Jestliže se žena obává od manžela svého hrubosti či lhostejnosti, není pro ně oba hříchem, dospějí-li mezi sebou ke vzájemné dohodě, neboť dohoda je to nejlepší. A v duších je stále přítomna lakota; budete-li však konat dobré a budete-li bohabojní - vždyť Bůh věru dobře je zpraven o všem, co činíte.'),
(622, 4, 129, 'Nikdy vám nebude možno, abyste byli spravedliví mezi ženami, i kdybyste si to přáli. Neprojevujte však jedné z nich úplnou náklonnost a nenechávejte jinou jakoby visící ve vzduchu! Dosáhnete-li dohody a budete-li bohabojní - vždyť Bůh je věru odpouštějící, slitovný.'),
(623, 4, 130, 'Jestliže se oba manželé rozvedou, Bůh je oba obohatí ze Své hojnosti a Bůh je pln hojnosti, moudrý.'),
(624, 4, 131, 'A Bohu náleží vše, co na nebesích je i na zemi. A věru jsme přikázali těm, jimž dostalo se Písma před vámi, i vám samým, abyste byli bohabojní. Však budete-li nevěřící - vždyť Bohu náleží vše, co na nebesích je i na zemi, a Bůh soběstačný je i chvályhodný.'),
(625, 4, 132, 'A Bohu náleží vše, co na nebesích je i na zemi, a Bůh je ochráncem dostatečným.'),
(626, 4, 133, 'Kdyby chtěl ó lidé - mohl by vás zničit a přivedl by místo vás jiné, neboť Bůh je toho věru mocen.'),
(627, 4, 134, 'Kdo si přeje odměny na tomto světě, tedy u Boha je odměna jak tohoto, tak i onoho světa - a Bůh slyšící je i jasnozřivý.'),
(628, 4, 135, 'Vy, kteří věříte! Buďte vytrvalí ve spravedlnosti, svědčíte-li před Bohem, byť i to bylo proti vám samým či rodičům anebo blízkým příbuzným a ať jedná se o bohaté či chudé, vždyť Bůh je ochráncem obojích! Nenásledujte tedy vášně své před spravedlností! A jestliže ji zkřivíte anebo od ní uhnete, tedy Bůh je dobře zpraven o všem, co děláte.'),
(629, 4, 136, 'Vy, kteří věříte! Věřte v Boha i Jeho posla, v Písmo, které seslal poslu Svému, i v Písmo, které seslal již předtím. A kdo nevěří v Boha a Jeho anděly a Jeho Písma a Jeho posly a v den soudný, ten zbloudil zblouděním dalekým.'),
(630, 4, 137, 'Věru ti, kdož uvěřili a potom se stali nevěřícími a znovu uvěřili a opět se stali nevěřícími a pak ještě zvětšili nevěrectví své, těm Bůh věru nemůže odpustit a nepovede je správnou cestou.'),
(631, 4, 138, 'Oznam pokrytcům zvěst radostnou o tom, že pro ně je připraven trest bolestný.'),
(632, 4, 139, 'Ti, kdož si berou nevěřící za přátele místo věřících, hledají snad u nich moc? Vždyť moc veškerá patří Bohu.'),
(633, 4, 140, 'A On seslal vám již v Písmu: "Když uslyšíte, že někdo popírá a tropí si posměch ze znamení Božích, nesedejte si s nimi, dokud se nepohrouží do jiného rozhovoru, jinak jim budete podobni." A Bůh věru shromáždí pokrytce i nevěřící v pekle všechny vespolek.'),
(634, 4, 141, 'Ti, kdož vyčkávavě pozorují, co bude s vámi, říkají, když se vám dostane vítězství od Boha: "Cožpak my nejsme s vámi?" Když však něco z úspěchu připadne nevěřícím, tu zase těmto říkají: "Cožpak jsme se vám nesnažili pomoci a nebránili jsme vás před věřícími!" Avšak Bůh vás rozsoudí v den zmrtvýchvstání a Bůh nedá nevěřícím možnost zvítězit nad věřícími.'),
(635, 4, 142, 'Pokrytci se snaží oklamat Boha, ale On oklame je. A když vstávají k modlitbě, zvedají se lenivě a jen před lidmi předstírají a vzpomínají na Boha jen nepatrně,'),
(636, 4, 143, 'váhajíce mezi obojím, nepřiklánějíce se ani k těm, ani k oněm. A komu dal Bůh zbloudit, tomu ty cestu nenajdeš!'),
(637, 4, 144, 'Vy, kteří věříte! Neberte si nevěřící za přátele místo věřících! Chcete snad proti sobě dát Bohu oprávnění zjevné?'),
(638, 4, 145, 'A pokrytci budou vskutku v nejnižším stupni ohně pekelného a nenajdeš pro ně pomocníka'),
(639, 4, 146, 's výjimkou těch, kdož pokání činili a napravili se, na Boha se pevně spoléhali a upřímně mu zasvětili svou víru. Tito budou patřit mezi věřící a Bůh zajisté dá věřícím odměnu nesmírnou.'),
(640, 4, 147, 'Co by dělal Bůh s vaším trestem, budete-li vděční a uvěříte-li? Bůh věru je za vděčnost uznalý a vševědoucí.'),
(641, 4, 148, 'Bůh nemiluje rozšiřování zlých řečí, leda u toho, jemuž bylo ukřivděno. A Bůh věru je slyšící, vševědoucí.'),
(642, 4, 149, 'Ať již ukazujete či skrýváte dobro anebo odpouštíte zlé Bůh zajisté je odpouštějící a všemohoucí.'),
(643, 4, 150, 'Ti, kdož nevěří v Boha a posly Jeho a chtějí oddělit Boha od poslů Jeho, říkají: "Věříme v jednu část a v jinou nevěříme," a chtěli by se ubírat cestou mezitím.'),
(644, 4, 151, 'A takoví jsou vpravdě nevěřící! A pro nevěřící jsme připravili trest ponižující.'),
(645, 4, 152, 'Těm však, kdož věří v Boha a posly Jeho a nedělají mezi nimi rozdíl žádný, těm dáme odměny jejich a Bůh je odpouštějící, slitovný.'),
(646, 4, 153, 'Vlastníci Písma tě žádají, abys jim snesl z nebe Písmo; a žádali již kdysi od Mojžíše více než toto, řkouce: "Dej nám spatřit Boha zřetelně!" A zasáhl je blesk za nespravedlnost jejich. I vzali si tele jako modlu poté, co se jim již dostalo důkazů jasných. A odpustili jsme jim to a dali jsme Mojžíšovi pravomoc zjevnou.'),
(647, 4, 154, 'A vyzdvihli jsme nad nimi horu podle úmluvy s nimi a řekli jsme jim: "Projděte branou poklekajíce!" a také jsme jim řekli: "Neznesvěcujte sobotu!" A uzavřeli jsme s nimi smlouvu přísnou.'),
(648, 4, 155, 'A pak prokleli jsme je za to, že porušili úmluvu svou, neuvěřili ve znamení Boží, zabíjeli nespravedlivě proroky, a také za slova jejich: "Srdce naše jsou neobřezána." Ba ano, Bůh jim je zapečetil kvůli jejich nevěrectví a jen málo z nich uvěřilo;'),
(649, 4, 156, 'a za nevěrectví jejich a za slova jejich o Marii, jež nehoráznou byla pomluvou,'),
(650, 4, 157, 'a za slova jejich: "Věru jsme zabili Mesiáše Ježíše, syna Mariina, posla Božího!" Však nikoliv, oni jej nezabili ani neukřižovali, ale jen se jim tak zdálo. A věru ti, kdož jsou o něm rozdílného mínění, jsou vskutku na pochybách o něm. A nemají o něm vědomosti žádné a sledují jen dohady; a nezabili jej určitě,'),
(651, 4, 158, 'naopak Bůh jej k sobě pozdvihl, neboť Bůh je mocný, moudrý.'),
(652, 4, 159, 'A věru není nikdo mezi vlastníky Písma, kdo by v Něj neuvěřil předtím, než zemře - a v den zmrtvýchvstání bude pak proti nim svědkem.'),
(653, 4, 160, 'A za nespravedlnost některých z těch, kdož vyznávají židovství, jsme jim zakázali výtečné pokrmy, jež dříve jim byly dovoleny, a také za jejich četná uchýlení od cesty Boží'),
(654, 4, 161, 'i za to, že brali lichvářské úroky, ačkoli jim to bylo zakázáno, a za to, že pohlcovali majetky jiných lidí podvodně. A připravili jsme pro ty, kdož mezi nimi nevěří, trest bolestný.'),
(655, 4, 162, 'Avšak těm z nich, kdož ve vědění jsou pevní a věřící a uvěřili v to, co bylo tobě sesláno, a v to, co bylo sesláno před tebou, a dodržují modlitbu a rozdávají almužnu a věří v Boha a v den soudný, těm věru dáme odměnu nesmírnou.'),
(656, 4, 163, 'A dali jsme ti vnuknutí, tak jako jsme je dali Noemovi a prorokům po něm; a dali jsme vnuknutí Abrahamovi, Ismaelovi, Izákovi, Jakubovi a kmenům a Ježíšovi, Jóbovi, Jonášovi, Árónovi a Šalomounovi; a Davidovi jsme dali žalmy.'),
(657, 4, 164, 'A také jiným poslům, o nichž jsme ti již dříve vyprávěli, a dalším, o nichž jsme ti nevyprávěli - a Bůh věru hovořil s Mojžíšem řečí jasnou -'),
(658, 4, 165, 'poslům, hlasatelům zvěsti radostné i varovatelům, aby lidé po jejich příchodu neměli výmluvy právoplatné proti Bohu. A Bůh je mocný, moudrý.'),
(659, 4, 166, 'Avšak Bůh dosvědčí to, co ti seslal; a seslal to ve vědění Svém a andělé to též dosvědčí. Avšak Bůh stačí jako svědek.'),
(660, 4, 167, 'Věru ti, kdož nevěří a odvracejí jiné z cesty Boží, zbloudili blouděním dalekým.'),
(661, 4, 168, 'A těm, kdož nevěří a jsou nespravedliví, Bůh věru neodpustí a neuvede je na cestu pravou,'),
(662, 4, 169, 'spíše na cestu do pekla, kde zůstanou nesmrtelní navěky. A to je pro Boha velmi snadné.'),
(663, 4, 170, 'Ó lidé! Nyní přišel k vám posel s pravdou od Pána vašeho, uvěřte, a bude to pro vás lepší! Avšak neuvěříte-li... vždyť Bohu náleží vše, co na nebesích je i na zemi, a Bůh je vševědoucí, moudrý.'),
(664, 4, 171, 'Vlastníci Písma! Nepřehánějte v náboženství svém a mluvte o Bohu jedině pravdu! Vskutku Mesiáš Ježíš, syn Mariin, je pouze poslem Božím a slovem Jeho, které vložil do Marie, a duchem z Něho vycházejícím. A věřte v Boha a posly Jeho a neříkejte: "Trojice!" Přestaňte, a bude to tak pro vás lepší. Bůh vskutku je jediným Bohem, On povznesen je nad to, aby měl dítě, vždyť náleží Mu vše, co na nebesích je i na zemi; a Bůh dostatečným je ochráncem.'),
(665, 4, 172, 'Ani Mesiáš, ani andělé přiblížení neopovrhují tím, aby byli služebníky Božími.'),
(666, 4, 173, 'Ty, kdož opovrhují službou Bohu a kteří jsou pyšní, ty Bůh shromáždí u Sebe všechny. Těm, kdož uvěřili a zbožné skutky konali, těm Bůh dá plnou odměnu jejich a rozmnoží jim z dobrodiní Svého; a ty, kdož byli pyšní a opovrhovali službou Jemu, ty potrestá trestem bolestným. A nenaleznou vedle Boha ochránce ani pomocníka žádného.'),
(667, 4, 174, 'Ó lidé! Nyní k vám přišel důkaz zřetelný od Pána vašeho a seslali jsme vám světlo jasné.'),
(668, 4, 175, 'A ty, kdož uvěřili v Boha a pevně se ho přidržovali, ty On uvede do milosti a slitování Svého a povede je k Sobě stezkou přímou.'),
(669, 4, 176, 'A žádají tě o vysvětlení. Odpověz: "Bůh vám dává toto vysvětlení o dědictví v boční linii: zahyne-li muž, aniž měl dítě, a má-li sestru, náleží jí polovina toho, co zůstavil, a také on dědí po ní, nezanechala-li ona dítě. Jestliže jsou dvě sestry, náleží jim dvě třetiny toho, co zůstavil. A má-li bratry a sestry, pak muži připadne podíl dvou žen. A Bůh vám to objasňuje, abyste nebloudili. A Bůh je vševědoucí o každé věci."');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Vy, kteří věříte! Dodržujte se závazky! Jsou vám dovolena zvířata ze stád s výjimkou těch, o nichž vám bylo přednášeno; a není vám dovolena lovná zvěř po dobu, kdy jste ve stavu poutního zasvěcení. A Bůh zajisté rozhoduje, o čem chce.'),
(671, 5, 2, 'Vy, kteří věříte, neznesvěcujte ani obřady Boží, ani posvátný měsíc, ani obětní dary, ani oběti ozdobené věnci, ani ty, kdož míří k Posvátnému chrámu a usilují tak o získání přízně a zalíbení Pána svého! A až vyjdete ze stavu zasvěcení, pak můžete lovit! Nechť nenávist k lidu, který vás vypudil z Posvátné mešity, vás nevede k hříchu! Pomáhejte si vzájemně ke zbožnosti a bohabojnosti a nepomáhejte si k hříchu a nenávisti. Buďte bohabojní, vždyť Bůh je přísný v trestání svém.'),
(672, 5, 3, 'A jsou vám zakázány zdechliny, krev, vepřové maso a to, co bylo zasvěceno jinému než Bohu, dále zvířata zardoušená či zabitá úderem, pádem či bodnutím rohu nebo zakousnutá divokou zvěří - kromě těch, jež jste očistili - a dále to, co bylo podřezáno na kamenech vztyčených. Je vám též zakázáno losování pomocí šípů, a to je hanebnost! A dnes jsou ti, kdož neuvěřili, zoufalí z náboženství vašeho, proto se jich nebojte, nýbrž bojte se Mne! Dnešního dne jsem pro vás dovršil vaše náboženství a naplnil nad vámi Své dobrodiní a zlíbilo se mi dát vám islám jako náboženství. A kdo je donucen to pojídat během hladomoru, nemaje úmyslu hřešit... vždyť Bůh je odpouštějící, slitovný.'),
(673, 5, 4, 'A ptají se tě, co je jim tedy dovoleno. Rci: "Jsou vám dovoleny výtečné pokrmy a to, co pro vás ukořistila dravá zvířata, jež jste vycvičili jako psy tak, jak vás tomu naučil Bůh. A pojídejte z toho, co pro vás ukořistila, a vyslovte nad tím jméno Boží a buďte bohabojní. A Bůh věru je rychlý v účtování.'),
(674, 5, 5, 'Dnes jsou vám dovoleny výtečné pokrmy; jídla těch, jimž dostalo se Písma, jsou vám též dovolena a pokrmy vaše jsou zase dovoleny jim. A dovoleny jsou vám muhsany z věřících žen a muhsany z těch, jimž dostalo se Písma před vámi, jestliže jste jim dali jejich odměnu a chováte-li se jako muži spořádaní, nikoliv jako cizoložníci nebo ti, kdož si berou milenky. A kdokoliv zavrhne víru, toho skutky budou marné a v životě budoucím bude mezi těmi, kdož ztrátu utrpí.'),
(675, 5, 6, 'Vy, kteří věříte! Když se chystáte k modlitbě, umyjte si obličeje své i ruce své až k loktům a otřete si hlavy své a nohy své až ke kotníkům! A jste-li potřísněni, očistěte se! Jste-li nemocní nebo na cestách či přišel-li někdo z vás ze záchodu anebo jste měli styk s ženami - a nenaleznete-li vodu - omývejte se dobrým jemným pískem a otřete si jím obličeje své i ruce své; Bůh vám nechce působit žádné nesnáze, nýbrž vás chce očistit a dovršit tak dobrodiní Své vůči vám - snad budete vděční!'),
(676, 5, 7, 'Pomněte tedy dobrodiní Božího vůči vám i úmluvy, kterou s vámi uzavřel, když jste pravili: "Slyšeli jsme a poslechli jsme!" A buďte bohabojní, neboť Bůh dobře zná, co skryto je v hrudích.'),
(677, 5, 8, 'Vy, kteří věříte! Buďte přímí před Bohem a buďte svědky spravedlivými. Nechť nenávist k lidu nevěřících vás neuvede do hříchu tím, že budete nespravedliví. Buďte spravedliví - a to je blíže k bohabojnosti - a bojte se Boha, neboť Bůh je dobře zpraven o všem, co děláte.'),
(678, 5, 9, 'Bůh přislíbil těm, kdož uvěřili a zbožné skutky konali, že dostane se jim odpuštění i nesmírné odměny,'),
(679, 5, 10, 'zatímco ti, kteří neuvěřili a naše znamení za lež prohlásili, budou pekla obyvateli.'),
(680, 5, 11, 'Vy, kteří věříte! Pomněte dobrodiní Božího vůči vám, když tento lid se chystal vztáhnout na vás ruce své a On ruce jejich od vás odvrátil! Buďte bohabojní a na Boha nechť se věřící spoléhají!'),
(681, 5, 12, 'A již kdysi Bůh přijal úmluvu od dítek Izraele a dali jsme povstat mezi nimi dvanácti vůdcům. I pravil Bůh: "Já zajisté jsem s vámi; a budete-li dodržovat modlitbu, dávat almužnu, věřit v posly Mé a pomáhat jim a poskytnete-li Bohu krásnou půjčku, věru vás očistím od vašich skutků špatných a uvedu vás do zahrad, pod nimiž řeky tekou. A kdo z vás ještě po tomto bude nevěřící, ten věru zbloudil z cesty rovné."'),
(682, 5, 13, 'Protože však porušili svou úmluvu, prokleli jsme je a zatvrdili jsme srdce jejich, takže přemísťují slova z jejich míst a zapomněli na část toho, co jim bylo připomenuto. A stále se budeš dovídat, jak zrazují - až na několik málo z nich. Odpusť jim a promiň, neboť Bůh věru miluje ty, kdož dobro konají.'),
(683, 5, 14, 'I od těch, kdož říkají "Jsme křesťany", jsme přijali úmluvu jejich, avšak i oni zapomněli část z toho, co bylo jim připomenuto, a vyvolali jsme mezi nimi nepřátelství a nenávist až do dne zmrtvýchvstání - a tehdy jim Bůh oznámí, co vlastně provedli.'),
(684, 5, 15, 'Vlastníci Písma! Nyní již přišel k vám náš posel, aby vám jasně vyložil mnohé z toho, co skryli jste z Písma, a prominul vám mnohé. A přišlo k vám nyní od Boha světlo a Písmo zjevné,'),
(685, 5, 16, 'jímž Bůh povede ty, kdož usilují o Jeho zalíbení, po cestách míru a jímž je vyvede z temnot na světlo z dovolení Svého a dovede je tak ke stezce přímé.'),
(686, 5, 17, 'Věru jsou nevěřící ti, kdož říkají: "Zajisté je Bohem Mesiáš, syn Mariin!" Odpověz: "Kdo má u Boha takovou moc, aby Mu mohl zabránit, kdyby se mu zachtělo zahubit Mesiáše, syna Mariina, a matku jeho a vůbec všechny, kdož jsou na zemi?" Bohu náleží království na nebesích i na zemi i vše, co je mezi nimi; On tvoří, co chce, a On nade všemi věcmi je mocný.'),
(687, 5, 18, 'Židé a křesťané říkají: "My synové jsme Boží a miláčci Jeho." Rci: "Proč vás tedy trestá za vaše hříchy? Nikoliv, vy jste obyčejní smrtelníci, jedni z těch, které On stvořil. On odpouští, komu chce, a trestá také, koho chce. Bohu náleží království na nebesích i na zemi i vše, co je mezi nimi - a u Něho cíl je konečný."'),
(688, 5, 19, 'Vlastníci Písma! Nyní přišel k vám náš posel, aby vám podal jasný výklad - v přestávce mezi posly - a abyste nemohli říci: "Nepřišel k nám žádný hlasatel ani varovatel." Však nyní k vám již přišel i hlasatel i varovatel! A Bůh je každé věci mocný.'),
(689, 5, 20, 'A hle, pravil Mojžíš lidu svému: "Lide můj, pomněte dobrodiní Božího vůči vám, když mezi vámi učinil proroky a vás učinil vládci a dal vám to, co nedal nikomu z lidstva veškerého!'),
(690, 5, 21, 'Lide můj! Vstupte do svaté země, kterou vám Bůh zaslíbil, a neobracejte kroky své, abyste se neobrátili v ty, kdož ztrátu utrpí!"'),
(691, 5, 22, 'A řekli: "Mojžíši, v té zemi je národ obrů a my do ní nevstoupíme, dokud oni z ní neodejdou. Jestliže však odejdou, my zajisté do ní vstoupíme."'),
(692, 5, 23, 'A řekli dva muži bohabojní, jež Bůh zahrnul Svou přízní: "Vejděte k nim branou! Jestliže tudy vejdete, stanete se vítězi. A na Boha se spoléhejte, jste-li věřící!"'),
(693, 5, 24, 'Však lidé zvolali: "Mojžíši, nevstoupíme nikdy do té to země, dokud v ní budou obři. Vytáhni ty a tvůj Pán a dejte se s nimi do boje, my zůstaneme sedět tady."'),
(694, 5, 25, 'I pravil Mojžíš: "Pane můj, mám moc jen nad sebou samým a nad bratrem svým. Čiň tedy rozdíl mezi námi a mezi tímto lidem hanebníků!"'),
(695, 5, 26, 'Odpověděl Pán: "Tato země jim budiž zakázána, nechť bloudí čtyřicet let po zemi. A ty se nermuť nad tímto lidem hanebníků!"'),
(696, 5, 27, 'A sděl jim podle pravdy příběh dvou synů Adamových, když oba přinášeli oběti; a byla od jednoho z nich obět přijata, zatímco od druhého nikoliv. I pravil tento: "Věru tě zabiji!" I odpověděl onen: "Bůh přijímá oběť jen od bohabojných.'),
(697, 5, 28, 'Vztáhneš-li na mne ruku svou, abys mne zabil, já na tebe svou ruku nevztáhnu, abych tě zabil, neboť se věru bojím Boha, Pána lidstva veškerého.'),
(698, 5, 29, 'Chci, abys vzal na sebe hřích proti mně i hřích proti sobě; a tak staneš se jedním z obyvatel ohně pekelného, neboť to odměna je nespravedlivých."'),
(699, 5, 30, 'A vnukla mu duše jeho zabít bratra, a zabil ho a stal se z těch, kdož ztrátu utrpěli.'),
(700, 5, 31, 'I poslal Bůh havrana, jenž hrabal v zemi, aby mu ukázal, jak skrýt mrtvolu bratra svého. I vzkřikl: "Běda mi! Což nejsem schopen být jako tento havran a ukrýt mrtvolu bratra svého?" A stal se jedním z těch, jež pronásledují výčitky svědomí.'),
(701, 5, 32, 'A kvůli tomuto jsme předepsali dítkám Izraele, aby ten, jenž zabije jednoho člověka - nikoliv pro pomstu na někom anebo za to, že šířil pohoršení na zemi - byl souzen, jako by zabil lidstvo veškeré. A aby ten, kdo oživí jednoho, byl posuzován, jako by oživil lidstvo veškeré. A přišli k nim již dříve poslové naši s jasnými důkazy, avšak mnozí z nich i potom se dopouštěli přestupků na zemi.'),
(702, 5, 33, 'A odměnou těch, kdož vedli válku proti Bohu a Jeho poslu a šířili na zemi pohoršení, bude věru to, že budou zabiti anebo ukřižováni či budou jim useknuty jejich pravé ruce a levé nohy anebo budou ze země vyhnáni. A těm dostane se hanby na tomto světě, zatímco na onom světě je očekává trest nesmírný'),
(703, 5, 34, 'kromě těch, kteří se kajícně obrátí, dříve než nad nimi získáte moc. A vězte, že Bůh je odpouštějící, slitovný.'),
(704, 5, 35, 'Vy, kteří věříte! Bojte se Boha a hledejte způsob, jímž byste se k Němu přiblížili! A bojujte usilovně na Jeho cestě - snad budete blažení!'),
(705, 5, 36, 'A věru ti, kteří neuvěřili, i kdyby měli všechno, co na zemi je, a ještě něco podobného navíc, a nabízeli to, aby se tím vykoupili z trestu dne zmrtvýchvstání, nebylo by to od nich přijato a pro ně určen je trest bolestný.'),
(706, 5, 37, 'Budou si přát, aby mohli vyjít z ohně pekelného, avšak nikdy z něho nevyjdou, neboť je očekává trest trvalý.'),
(707, 5, 38, 'Zloději a zlodějce utněte ruce jejich v odplatu za to, co provedli, a jako varovný příklad od Boha! A Bůh je mocný, moudrý.'),
(708, 5, 39, 'Však kdo se kajícně obrátí poté, co dopustil se nepravosti, a polepší se, tomu Bůh věru odpustí. A Bůh zajisté je odpouštějící, slitovný.'),
(709, 5, 40, 'Což nevíš, že Bohu náleží království na nebesích i na zemi a že On trestá, koho chce, a odpouští, komu chce? A Bůh je všech věcí mocen.'),
(710, 5, 41, 'Posle, nermuť se kvůli těm, kdož předhánějí se v nevěrectví! Ti patří k těm, kdož říkají ústy svými "Uvěřili jsme!", zatímco v srdcích svých neuvěřili, a k těm, kdož vyznávají židovství a horlivě naslouchají lžím a naslouchají i lidem jiným, kteří k tobě nepřicházejí. Oni přemísťují slova z jejich míst říkajíce: "Jestliže je vám toto dáno, přijměte to, avšak není-li vám to dáno, dejte si pozor!" A koho Bůh chce uvést v pokušení, tomu ty ničím u Boha nepomůžeš. A to jsou ti, jichž srdce Bůh nehodlá očistit - těm dostane se hanby na tomto světě, zatímco na onom světě je očekává trest nesmírný.'),
(711, 5, 42, 'Naslouchají lžím a pohlcují to, co neprávem získali. Přijdou-li k tobě, pak mezi nimi buď rozsuď, anebo se od nich odvrať! Odvrátíš-li se od nich, nemohou ti nikterak uškodit, však budeš-li soudit, suď mezi nimi spravedlivě, neboť Bůh věru miluje spravedlivé.'),
(712, 5, 43, 'A jak by si tě mohli vybrat za soudce, když mají Tóru, v níž je obsažen rozsudek Boží? Potom se však od ní odvrátili - a nejsou tedy věřící.'),
(713, 5, 44, 'A seslali jsme Tóru, v níž je správné vedení i světlo, aby podle ní soudili proroci, kteří se odevzdali do vůle Boží, ty, kdož vyznávají židovství; a aby rabíni a učení rozhodovali podle toho, co jim bylo svěřeno ke střežení z Písma Božího a čeho byli svědky. Nebojte se lidí, nýbrž bojte se Mne a nekupujte za Má znamení věci ceny nicotné! Ti pak, kdož nesoudí podle toho, co seslal Bůh, jsou nevěřící.'),
(714, 5, 45, 'A předepsali jsme jim v Tóře: duši za duši, oko za oko, nos za nos, ucho za ucho, zub za zub a za zranění mstu. A kdo místo toho dá almužnu, bude to pro něj vykoupením z hříchů. Ti pak, kdo nesoudí podle toho, co seslal Bůh, ti věru jsou nespravedliví.'),
(715, 5, 46, 'A vypravili jsme ve stopách jejich Ježíše, syna Mariina, aby potvrdil pravdivost toho, co obsaženo bylo předtím v Tóře, a dali jsme mu evangelium, v němž je správné vedení a světlo, aby potvrdilo pravdivost toho, co bylo předtím v Tóře, a stalo se správným vedením i varováním pro bohabojné.'),
(716, 5, 47, 'Nechť vlastníci evangelia soudí podle toho, co Bůh v něm seslal; ti pak, kdož nesoudí podle toho, co Bůh seslal, jsou hanebníci.'),
(717, 5, 48, 'I tobě jsme seslali Písmo s pravdou, aby potvrdilo pravdivost toho, co už před ním bylo zde z Písma, a bylo jeho ochranou. A rozsuzuj mezi nimi podle toho, co seslal Bůh, a nenásleduj jejich učení scestná, vzdaluje se tak od toho, čeho se ti z pravdy dostalo. A každému z vás jsme určili pravidla a dráhu vyšlapanou. Kdyby byl Bůh chtěl, byl by vás věru učinil národem jediným, avšak neučinil tak proto, aby vás vyzkoušel tím, co vám dal. Předstihujte se vzájemně v konání dobrých skutků! K Bohu se uskuteční návrat vás všech, a On vás poučí o tom, o čem jste byli v rozporu.'),
(718, 5, 49, 'A rozsuzuj mezi nimi podle toho, co Bůh seslal, a nenásleduj jejich učení scestná a dej si pozor, aby tě neodvrátili od části z toho, co ti Bůh seslal. Obrátí-li se zády, pak věz, že Bůh je chce postihnout za některé z hříchů jejich, neboť většina z lidí jsou hanebníci.'),
(719, 5, 50, 'Cožpak oni touží po rozsudcích z doby nevědomosti? Kdo může být pro lid pevně přesvědčený lepším soudcem než Bůh?'),
(720, 5, 51, 'Vy, kteří věříte! Neberte si židy a křesťany jako přátele, neboť oni jsou si přáteli jedni druhým. Kdokoliv z vás se s nimi přátelí, ten stane se jedním z nich a Bůh věru nepovede lid nespravedlivý.'),
(721, 5, 52, 'A vidíš ty, v jejichž srdcích je choroba, horlivě mezi nimi obcházet a říkat: "Bojíme se, aby nás nepostihl obrat osudu!" Však možná, že Bůh přinese úspěch anebo nějaké Své rozhodnutí, takže tito lidé začnou litovat toho, co skrývali ve svých duších,'),
(722, 5, 53, 'a řeknou ti, kdož uvěřili: "Což nejsou to tíž, kteří přísahali při Bohu slavnostními přísahami, nejsou doopravdy s námi?" Marné budou jejich skutky a stanou se z nich ti, kdož ztrátu utrpí.'),
(723, 5, 54, 'Vy, kteří věříte! Kdo z vás se odřekne náboženství svého... A Bůh přivede lidi, které bude milovat a kteří budou milovat Jeho a kteří budou pokorní vůči věřícím a přísní vůči nevěřícím, budou bojovat na cestě Boží a nebudou se bát výčitek nikoho. A toto je laskavost Boží a On ji uštědřuje, komu chce, a Bůh je velkorysý, vševědoucí.'),
(724, 5, 55, 'Vaším přítelem je jedině Bůh a Jeho posel a ti, kdož věří, dodržují modlitbu, dávají almužnu a sklánějí se.'),
(725, 5, 56, 'A kdo si bere za přátele Boha a Jeho posla a věřící, ten patří ke straně Boží - a tito zvítězí!'),
(726, 5, 57, 'Vy, kteří věříte! Neberte si za přátele ty, kterým je vaše náboženství pro posměch a pro zábavu, ať jsou již z těch, jimž dostalo se Písma před vámi, či ať jsou z nevěřících. A bojte se Boha, jste-li věřící!'),
(727, 5, 58, 'A když voláte k modlitbě, tropí si z toho posměšky a je jim to pro zábavu - a je to proto, že je to lid, jenž ničemu nerozumí.'),
(728, 5, 59, 'Rci: "Vlastníci Písma! Nenávidíte nás snad jedině proto, že jsme uvěřili v Boha a v to, co bylo sesláno nám, a v to, co bylo sesláno dříve? Však většina z vás jsou hanebníci."'),
(729, 5, 60, 'Rci: "Mám vám oznámit něco horšího než odplatu, která je u Boha? Ti, které Bůh proklel a na něž se rozhněval a z nichž učinil opice a vepře, a ti, kteří uctívají Tághúta - ti jsou v nejhorším postavení a nejdále zbloudili z cesty rovné."'),
(730, 5, 61, 'Když k vám přišli, řekli: "Uvěřili jsme!" Avšak v nevěrectví přišli a s nevěrectvím odešli a Bůh nejlépe zná, co skrývali.'),
(731, 5, 62, 'A vidíš mnohé z nich, jak pospíchají k hříchu, k nespravedlnosti a k pohlcování toho, čeho neprávem nabyli. Jak hnusné je to, co dělají!'),
(732, 5, 63, 'Proč jim nezabrání rabíni a učenci, aby nevedli hříšné řeči a nepohlcovali neprávem nabyté? Jak hnusné je to, co provádějí.'),
(733, 5, 64, 'A říkají židé: "Boží ruka je spoutána okovy!" Nechť jsou jejich ruce spoutány okovy a nechť jsou prokleti za to, co říkají! Nikoliv, obě Jeho ruce jsou rozevřeny a On uštědřuje, jak chce. To, co ti bylo sesláno od Pána tvého, v mnohých z nich jen rozmnožuje vzpurnost a nevěrectví. A uvrhli jsme mezi ně nepřátelství a nenávist až do dne zmrtvýchvstání. A kdykoliv zažehnou oheň války, Bůh jej uhasí; a usilují šířit po zemi pohoršení, avšak Bůh nemiluje ty, kdož pohoršení šíří.'),
(734, 5, 65, 'Kdyby vlastníci Písma uvěřili a báli se Boha, věru bychom jim vymazali jejich špatné skutky a zajisté bychom je uvedli do zahrad rozkoší;'),
(735, 5, 66, 'a kdyby dodržovali Tóru a evangelium a to, co sesláno jim bylo od Pána jejich, věru by mohli požívat z toho, co je nad nimi i co je pod nimi. Mezi nimi je obec, jež kráčí středem, avšak jak hnusné je to, co většina z nich provádí.'),
(736, 5, 67, 'Posle, předej to, co ti bylo sesláno od Pána tvého! Neučiníš-li tak, pak nesplníš své poslání. Vždyť Bůh tě ochrání před lidmi; Bůh věru nevede správnou cestou lid nevěřící.'),
(737, 5, 68, 'Rci: "Vlastnící Písma, nedosáhnete ničeho, pokud nebudete dodržovat Tóru, evangelium a to, co vám bylo Pánem vaším sesláno." Avšak to, co ti bylo sesláno Pánem tvým, v mnohých z nich jen rozmnožuje vzpurnost a nevěrectví; však nermuť se pro lid nevěřící!'),
(738, 5, 69, 'Věru ti, kdož uvěřili, a ti, kdož vyznávají židovství, a sabejci a křesťané - vůbec ti, kdož uvěřili v Boha a v den poslední a dobré skutky konali, ti nemusí mít strach a nebudou zarmouceni.'),
(739, 5, 70, 'A věru jsme kdysi uzavřeli úmluvu s dítkami Izraele a vyslali jsme k nim posly. Kdykoliv však k nim přišli poslové s něčím, čeho si duše jejich nepřály, část z nich prohlásili za lháře a část zabili.'),
(740, 5, 71, 'Domnívali se, že to není zkouška, a byli slepí a hluší, leč Bůh jim později odpustil, avšak i potom většina z nich oslepla a ohluchla. A Bůh jasně zří, co dělají.'),
(741, 5, 72, 'A věru jsou nevěřící ti, kdož říkají: "Mesiáš, syn Mariin, je Bůh!" A pravil Mesiáš: "Dítka Izraele, uctívejte Boha, Pána mého i Pána vašeho! Kdo bude přidružovat k Bohu, tomu Bůh zakáže vstup do ráje a bude mu příbytkem oheň pekelný; a nespravedliví nebudou mít pomocníky."'),
(742, 5, 73, 'A jsou věru nevěřící ti, kdo prohlašují: "Bůh je třetí z trojice" - zatímco není božstva kromě Boha jediného. A nepřestanou-li s tím, co říkají, věru se dotkne těch, kdož z nich jsou nevěřící, trest bolestný!'),
(743, 5, 74, 'Proč se tedy kajícně neobrátí k Bohu a neprosí jej za odpuštění, když Bůh zajisté je odpouštějící a slitovný?'),
(744, 5, 75, 'Mesiáš, syn Mariin, není leč posel, před nímž byli již poslové jiní. A matka jeho byla pravdomluvná - a oba se živili pokrmy. Pohleď, jak jim objasňujeme znamení, a pohleď, do jakých lží se dostali!'),
(745, 5, 76, 'Rci: "Což budete uctívat místo Boha něco, co vám nemůže způsobit škodu ani užitek? A Bůh je ten, jenž slyšící je i vševědoucí."'),
(746, 5, 77, 'Rci: "Vlastníci Písma! Nepřehánějte v náboženství svém, mluvte jen pravdu a nenásledujte scestná učení lidí, kteří zbloudili již před vámi a dali zbloudit mnoha jiným - a ti věru zbloudili z cesty rovné!"'),
(747, 5, 78, 'Byly proklety ty z dětí Izraele, které neuvěřily, jazykem Davida i Ježíše, syna Mariina. A to bylo za to, že neposlouchali a byli přestupníky'),
(748, 5, 79, 'a že si vzájemně nezakazovali konání zavrženíhodného. A jak hnusné bylo to, co dělali!'),
(749, 5, 80, 'A vidíš mnohé z nich se přátelit s nevěřícími. A to, co již dříve pro sebe si připravili, je tak hnusné, že Bůh se na ně rozhněval - a v trestu svém budou nesmrtelní!'),
(750, 5, 81, 'Kdyby byli věřili v Boha, v proroka a v to, co mu bylo sesláno, nebyli by se s nimi přátelili, avšak mnozí z nich jsou věru hanebníci.'),
(751, 5, 82, 'A věru zjistíš, že lidé, kteří jsou největšími nepřáteli věřících, jsou židé, a ti, kdo k Bohu přidružují; a zjistíš, že lidé, kteří jsou největšími přáteli věřících, jsou ti, kdož říkají: "Jsme křesťané!" A je to proto, že jsou mezi nimi kněží a mniši a že nejsou pýchou naplněni.'),
(752, 5, 83, 'A když slyší to, co bylo poslu sesláno, spatříš jejich oči se plnit slzami z poznání pravdy a říkat: "Pane náš, uvěřili jsme, zapiš nás mezi ty, kdož to dosvědčují!'),
(753, 5, 84, 'Proč bychom neměli věřit v Boha a v to, co nám přišlo z pravdy, když toužíme, aby nás Pán náš uvedl do ráje spolu s lidmi bezúhonnými?"'),
(754, 5, 85, 'A Bůh je odmění za to, co říkají, zahradami, pod nimiž řeky tekou a v nichž nesmrtelní budou. A to je odměna za to, že dobro konali.'),
(755, 5, 86, 'Avšak ti, kdož neuvěřili a znamení Naše za lež prohlásili, ti budou pekla obyvateli.'),
(756, 5, 87, 'Vy, kteří věříte! Nezakazujte výtečné pokrmy, jež vám Bůh dovolil, a nepřestupujte Jeho příkazy - vždyť Bůh věru nemiluje přestupníky.'),
(757, 5, 88, 'Jezte z toho, co vám Bůh uštědřil jako dovolené a výtečné, a bojte se Boha, v něhož věříte!'),
(758, 5, 89, 'Bůh vás nebude plísnit za lehkomyslnost ve vašich přísahách, ale bude vás plísnit za to, k čemu jste se v přísahách zavázali a nedodrželi to. Vykoupením za to budiž nakrmení deseti chudáků jídlem průměrným, jímž stravujete své rodiny, anebo jejich ošacení či propuštění otroka. A kdo k tomu nenalezne možnosti, nechť postí se tři dny - a to je vykoupení za přísahy vaše, které jste nedodrželi. Dávejte tedy pozor na přísahy své; a takto vám Bůh znamení Svá objasňuje - snad budete vděční!'),
(759, 5, 90, 'Vy, kteří věříte! Víno, hra majsir, obětní kameny a vrhání losů šípy jsou věru věci hnusné z díla satanova. Vystříhejte se toho - a možná, že budete blaženi.'),
(760, 5, 91, 'Satan chce mezi vámi podnítit pomocí vína a hry majsiru nepřátelství a nenávist a odvést vás od vzývání Boha a od modlitby. Přestanete s tím tedy?'),
(761, 5, 92, 'Poslouchejte Boha a poslouchejte posla a mějte se na pozoru! Jestliže se však odvrátíte, vězte, že poslu Našemu přísluší toliko jasné oznámení.'),
(762, 5, 93, 'Není hříchem pro ty, kdož uvěřili a zbožné skutky konali, to, co pojídají, pokud jsou bohabojní, věřící a zbožné skutky konají a jsou nadále bohabojní a věřící a zůstanou bohabojnými a zbožnými. A Bůh miluje ty, kdož zbožné skutky konají.'),
(763, 5, 94, 'Vy, kteří věříte! Bůh vás bude dozajista zkoušet něčím z kořisti, kterou na lovu získají vaše ruce a vaše kopí. A Bůh tak činí, aby poznal, kdo bojí se Ho v skrytu duše své. A kdo přestoupí po tomto zákazu, toho stihne trest bolestný.'),
(764, 5, 95, 'Vy, kteří věříte! Nezabíjejte zvěř, jste-li ve stavu poutního zasvěcení! Kdo z vás ji úmyslně zabije, ať dá v náhradu tolik skotu, kolik zabil zvěře. A nechť dva muži spravedliví z vás to rozsoudí a nechť je to dáno jako oběť, jež dospěje ke Ka´bě. Anebo bude vykoupením nakrmení chudáků či půst, který se tomu rovná, aby tak člověk okusil následek činu svého. Bůh již vymazal to, co stalo se dříve, kdo však to bude opakovat, tomu se Bůh pomstí - a Bůh mocný je i pán pomsty!'),
(765, 5, 96, 'Je vám dovoleno lovit v moři a mít z toho jídlo k užitku vám i pocestným; však zakázán je vám lov na souši, pokud jste ve stavu poutního zasvěcení. A bojte se Boha, k němuž budete jednou shromážděni.'),
(766, 5, 97, 'A Bůh ustanovil Ka´bu chrámem posvátným, vztyčeným pro lidi, a ustanovil měsíc posvátný i obětní zvířata a ověnčení - a to proto, abyste zvěděli, že Bůh zná vše, co na nebesích je i na zemi, a že Bůh ví o každé věci.'),
(767, 5, 98, 'Vězte, že Bůh je strašný v trestání Svém a že Bůh je i odpouštějící, slitovný.'),
(768, 5, 99, 'A poslu přísluší toliko oznámení; však Bůh dobře ví, co najevo dáváte i co tajíte.'),
(769, 5, 100, 'Rci: "Špatné a dobré si není rovno, třebaže se ti líbí mnohé z toho, co je špatné. Bojte se Boha, lidé obdaření rozmyslem - snad budete blažení!"'),
(770, 5, 101, 'Vy, kteří věříte! Nevyptávejte se na věci, které by vás, kdyby vám byly prozrazeny, zarmoutily. Jestliže však se na ně otážete, až bude seslán Korán celý, budou vám prozrazeny - a Bůh vám to promine, neboť On odpouštějící je i laskavý.'),
(771, 5, 102, 'A dotazovali se na ně již lidé před vámi, avšak poté se kvůli tomu stali nevěřícími.'),
(772, 5, 103, 'A Bůh neustanovil ani bahíru, ani sá\'ibu, ani wasílu, ani hámi, avšak ti, kdož neuvěřili, si vymyslili tyto lži proti Bohu - a většina z nich nemá rozum žádný.'),
(773, 5, 104, 'Když je jim řečeno: "Pojďte k tomu, co seslal Bůh, a k poslovi!", odpovídají: "Nám postačí, co u otců svých jsme nalezli!" Ale což když otcové jejich nic neznali a po správné cestě nebyli vedeni?'),
(774, 5, 105, 'Vy, kteří věříte! Za duše své jste sami odpovědni; neuškodí vám ten, kdo zbloudil, jste-li vy sami správnou cestou vedeni. K Bohu se všichni navrátíte a On vám již oznámí, co jste dělali.'),
(775, 5, 106, 'Vy, kteří věříte! Nechť je vzato svědectví mezi vámi, když smrt se přiblíží k některému z vás, ve chvíli, kdy činí závěť. Nechť svědčí dva muži spravedliví z vás nebo dva jiní, kteří nejsou z vás, cestujete-li po zemi a postihne vás pohroma smrti. Zadržíte ty dva po modlitbě, a máte-li pochybnosti, nechť při Bohu přisahají řkouce: "Neprodáme to za žádnou cenu, i kdyby se jednalo o našeho příbuzného, a neukryjeme svědectví před Bohem dané. A kdybychom tak učinili, nechť patříme mezi hříšníky!"'),
(776, 5, 107, 'Jestliže se však ukáže, že oba si zaslouží obvinění z hříchu, pak nechť místo jejich zaujmou dva jiní z těch, kdož více si toho zasluhují a jsou přáteli (umírajícího?), a nechť přísahají při Bohu: "Věru je naše svědectví pravdivější než svědectví oněch dvou a my se nedopustíme přestupků, jinak bychom patřili mezi nespravedlivé!"'),
(777, 5, 108, 'A toto je nejvhodnější k tomu, aby vydali svědectví v jeho pravé podobě, anebo aby se báli, že jejich přísaha bude odmítnuta poté, co poruší své přísahy. A bojte se Boha a naslouchejte! Bůh nepovede cestou správnou lid hanebníků.'),
(778, 5, 109, 'V den, kdy Bůh shromáždí posly, řekne jim: "Co bylo vám odpověděno?" I odvětí: "Nemáme o tom vědomosti žádné, Tys věru ten, jenž sám zná nepoznatelné."'),
(779, 5, 110, 'A hle, řekne Bůh: "Ježíši, synu Mariin, pomni milosti Mé kterou jsem prokázal tobě i matce tvé, když podpořil jsem tě duchem svatým a hovořils s lidmi v kolébce i jako dospělý; když naučil jsem tě Písmu, moudrosti, Tóře i Evangeliu; a když vytvořils z hlíny podoby ptáků z dovolení Mého a dýchls na ně a staly se ptáky skutečnými z dovolení Mého; a když uzdravils slepého a malomocného z dovoleni Mého a dals vstát mrtvým z hrobů z dovolení Mého. A odehnal jsem od tebe dítka Izraele, když přišels k nim s důkazy jasnými a když řekli ti z nich, kdož neuvěřili: Tohle není leč kouzelnictví zjevné!'),
(780, 5, 111, 'A když jsem vnukl apoštolům:, Věřte ve Mne a v posla Mého!? a oni odpověděli:, Uvěřili jsme a dosvědč, že jsme do vůle Tvé se odevzdali!?"'),
(781, 5, 112, 'A hle, pravili apoštolové: "Ježíši, synu Mariin, může nám Pán tvůj seslat z nebe stůl prostřený?" I odvětil: "Bojte se Boha, jste-li věřící!"'),
(782, 5, 113, 'Řekli: "Chceme z něho pojíst, aby upokojila se srdce naše a abychom zvěděli, žes k nám mluvil pravdu, a mohli pak být svědky o tom!"'),
(783, 5, 114, 'I zvolal Ježíš, syn Mariin: "Veliký Bože, Pane náš! Sešli nám stůl prostřený z nebes, jenž svátkem bude pro nás, jak pro prvního, tak i pro posledního z nás, i znamením od Tebe! A dej nám příděl štědrý, vždyť Tys nejlepší z uštědřovatelů!"'),
(784, 5, 115, 'I řekl Bůh: "Dozajista vám jej sešlu. Kdokoliv z vás však bude i potom nevěřící, toho potrestám trestem, jakým jsem nepotrestal nikoho z lidstva veškerého!"'),
(785, 5, 116, 'A hle, pravil Bůh: "Ježíši, synu Mariin, zdaž jsi to byl ty, kdo řekl lidem, Vezměte si mne a matku mou jako dvě božstva vedle Boha??" I odpověděl: "Sláva Tobě! Nebylo na mne, abych říkal něco, k čemu jsem neměl právo! Kdybych to byl býval řekl, byl bys to dobře věděl, neboť Ty znáš, co je v duši mé, zatímco já neznám, co je v Tvé duši, vždyť Ty jediný znáš nepoznatelné.'),
(786, 5, 117, 'Neříkal jsem jim, leda to, cos mi nařídil, to jest: Uctívejte Boha, Pána mého i Pána vašeho! A byl jsem svědkem o nich, dokud jsem žil mezi nimi. A když jsi mne povolal k Sobě, byls to Ty, kdo byl nad nimi dozorcem - a Tys svědkem věcí všech.'),
(787, 5, 118, 'Potrestáš-li je - vždyť jsou služebníky Tvými; odpustíš-li jim - vždyť Tys věru mocný i moudrý!"'),
(788, 5, 119, 'I řekne Bůh: "Toto je den, kdy prospěje pravdomluvným pravdomluvnost jejich." Pro ně jsou určeny zahrady, pod nimiž řeky tekou, a v nich nesmrtelní navěky budou. A Bůh v nich nalezne zalíbení a oni naleznou zalíbení v Něm a to úspěch bude nesmírný.'),
(789, 5, 120, 'A Bohu náleží království na nebesích i na zemi i vše, co je mezi nimi - a On nade všemi věcmi je mocný!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Chvála Bohu, který stvořil nebesa a zemi a učinil temnotu a světlo. A přece potom ti, kdož neuvěřili, Pánu svému rovné dávají!'),
(791, 6, 2, 'On je ten, jenž z hlíny vás stvořil a pak stanovil lhůtu života vašeho - a lhůta u Něho je pevně stanovena - a přece o tom pochybujete.'),
(792, 6, 3, 'On je Bůh na nebesích i na zemi, On zná vše, co tajíte i co odkrýváte, a zná i to, co jste si vysloužili.'),
(793, 6, 4, 'A ať se jim dostalo od Pána jejich jakékoliv znamení, odvrátili se od něho'),
(794, 6, 5, 'a prohlásili za lež pravdu, když k nim přišla. Však dostane se jim zvěsti o tom, čemu se posmívali!'),
(795, 6, 6, 'Což neviděli, kolik jsme zahubili pokolení, jež byla před nimi a která jsme upevnili na zemi tak, jak jsme neupevnili ani vás; a seslali jsme jim z nebes déšť hojný a dali jsme vytrysknout řekám pod nimi. A pak jsme je zahubili za hříchy jejich a dali jsme po nich vzejít pokolením jiným.'),
(796, 6, 7, 'I kdybychom ti poslali Knihu na pergamenu psanou a oni si ji ohmatali vlastníma rukama, přece by řekli ti, kdož neuvěřili: "To kouzelnictví je jen zjevné!"'),
(797, 6, 8, 'A říkají: "Pročpak k Němu nebyl poslán anděl?" Kdybychom však seslali anděla, byla by věc jejich již rozhodnuta a tehdy by jim nebyl dán odklad žádný.'),
(798, 6, 9, 'A kdybychom byli poslem udělali anděla, věru bychom jej učinili mužem a byli bychom tím pro ně zatemnili to, co sami zatemňují.'),
(799, 6, 10, 'A posmívali se již před tebou poslům, avšak ti, kdož smích si tropili, byli obklopeni tím, čemu se posmívali.'),
(800, 6, 11, 'Rci: "Choďte po zemi a potom pohleďte, jaký byl konec těch, kdož posly za lháře prohlašovali!"'),
(801, 6, 12, 'Zeptej se: "Komu náleží vše, co na nebesích je i na zemi?" Odpověz: "Bohu! On Sobě milosrdenství jako povinnost předepsal a On shromáždí vás v den zmrtvýchvstání, o němž pochyby není. Ti, kteří sami sobě ztrátu způsobili, to jsou ti, kdož nevěří."'),
(802, 6, 13, 'Jemu náleží vše, co v noci i ve dne přebývá a On slyšící je i vševědoucí.'),
(803, 6, 14, 'Rci: "Což si mohu vzít za ochránce někoho jiného než Boha, tvůrce nebes a země, který vše živí, ale sám živen není?" Rci: "Bylo mi nařízeno, abych byl prvním mezi těmi, kdož do vůle Jeho se odevzdají - a nikoliv z těch, kdož k Němu přidružují!"'),
(804, 6, 15, 'Rci: "Obávám se, jestliže Pána svého neuposlechnu, trestu dne nesmírného."'),
(805, 6, 16, 'Od koho bude v ten den trest odvrácen, nad tím se Bůh věru slitoval, a to bude štěstí zjevné.'),
(806, 6, 17, 'Jestliže se tě Bůh dotkne nějakým neštěstím, pak tě ho nikdo nezbaví kromě Něho. A jestliže se tě dotkne něčím dobrým - vždyť On nade všemi je mocný.'),
(807, 6, 18, 'On vládu neomezenou má nad služebníky Svými a On moudrý je i dobře zpravený.'),
(808, 6, 19, 'Zeptej se: "Která věc platí nejvíce jako svědectví?" Odpověz: "Bůh je svědkem mezi vámi a mnou; tento Korán mi byl vnuknut, abych jím varoval vás i všechny, k nimž dospěje. Chcete snad svědčit, že vedle Boha jsou i božstva jiná?" Rci: "Já to nedosvědčím!" a dodej: "Naopak, On Bohem je jediným a já nejsem odpovědný za to, co k Němu přidružujete."'),
(809, 6, 20, 'Ti, jimž jsme dali Písmo, znají je tak dobře, jako znají syny své. Ti, kteří sami sobě ztrátu způsobili, jsou ti, kdož nevěří.'),
(810, 6, 21, 'Kdo je nespravedlivější než ten, kdo proti Bohu lži si vymýšlí či znamení Jeho za lživá prohlašuje? Nespravedliví věru nebudou blažení.'),
(811, 6, 22, 'V den, kdy je všechny shromáždíme, řekneme těm, kdož byli modloslužebníky: "Kde jsou ti, které jste k Bohu přidružovali a jež jste si vymyslili?"'),
(812, 6, 23, 'Potom nebudou mít jinou výmluvu než tu, že řeknou: "Při Bohu, Pánu našem, my jsme nic nepřidružovali."'),
(813, 6, 24, 'Pohleď, jak sami proti sobě lžou a jak je opustili ti, které si vymyslili.'),
(814, 6, 25, 'A jsou mezi nimi někteří, kdož ti naslouchají, ale pokryli jsme srdce jejich clonou a uši jejich hluchotou, aby nepochopili, a i když vidí znamení všemožná, nevěří v ně, a když potom přijdou k tobě, aby se s tebou hádali, říkají ti, kdož nevěří: "To jsou jen povídačky starých!"'),
(815, 6, 26, 'A zabraňují druhým, aby mu naslouchali, a sami se od něho vzdalují, však způsobí ztrátu jen sami sobě, a přitom to nevědí.'),
(816, 6, 27, 'Kéž bys je mohl vidět, až postaveni budou před oheň pekelný a zvolají: "Kdybychom se mohli vrátit, věru bychom pak již neprohlašovali znamení Pána svého za lež a stali bychom se věru jedněmi z věřících!"'),
(817, 6, 28, 'Ba ne, odkryje se jim jen to, co dříve skrývali. A byť i byli navráceni, opět by se obrátili k tomu, co bylo jim zakázáno - vždyť jsou to vskutku lháři'),
(818, 6, 29, 'a řekli by: "Není nic než náš život pozemský a my nebudeme vzkříšeni."'),
(819, 6, 30, 'Kéž bys je mohl vidět, až budou před Pána svého postaveni a ten jim řekne: "Zdaž toto pravda není?" I odvětí: "Ó ano, při Pánu našem!" A řekne Pán: "Ochutnejte tedy trest za to, že jste nevěřící byli!"'),
(820, 6, 31, 'Ztraceni jsou ti, kdož setkání s Bohem za lež prohlašovali. A až na ně znenadání Hodina udeří, zvolají: "Běda nám za to, že jsme o ni nedbali!" A ponesou na bedrech svých břemena svá - a jak hnusné je břímě jejich!'),
(821, 6, 32, 'Život pozemský není leč hra a zábava, však příbytek v životě budoucím je lepší pro ty, kdož jsou bohabojní. Což nikdy to nepochopíte?'),
(822, 6, 33, 'My dobře víme, že zarmucuje tě to, co oni říkají. Ale oni neprohlašují za lháře jen tebe, nespravedliví popírají i znamení Boží!'),
(823, 6, 34, 'Již před tebou byli jiní poslové za lháře prohlašováni, však trpělivě snášeli obvinění ze lži i příkoří, dokud k nim nepřišla vítězná pomoc Naše. Není nikoho, kdo by mohl změnit slovo Boží! A dostalo se ti již ze zvěsti o vyslancích Božích.'),
(824, 6, 35, 'Zajisté na tebe těžce doléhá jejich vzdalování! Kdybys mohl, jistě by sis přál vykopat jámu v zemi nebo postavit žebřík do nebe, abys jim mohl přinést znamení. Kdyby Bůh chtěl, shromáždil by je jistě všechny na cestě správné, nebuď tedy jedním z pošetilých!'),
(825, 6, 36, 'A odpovídají na výzvu jedině ti, kdož naslouchají. Bůh pak vzkřísí mrtvé a potom k Němu budou navráceni.'),
(826, 6, 37, 'Říkají: "Proč mu nebylo sesláno nějaké znamení od Pána jeho?" Odpověz: "Bůh věru je schopen seslat znamení, avšak většina z nich to neví."'),
(827, 6, 38, 'Není zvířete pozemského ani ptáka na křídlech létajícího, aby netvořili společenství podobné vašim. A nezanedbali jsme v Písmu ničeho; a nakonec u Pána svého budete shromážděni.'),
(828, 6, 39, 'Ti, kdož znamení Naše za lež prohlašují, jsou hluší a němí, v temnotách. Bůh dává zbloudit, komu chce, a také uvádí na stezku přímou, koho chce.'),
(829, 6, 40, 'Rci: "Domníváte se snad, že až se k vám dostaví trest Boží či až Hodina udeří, že budete vzývat někoho jiného než Boha? Odpovězte, jste-li pravdomluvní!"'),
(830, 6, 41, 'Nikoliv! Jej vzývat budete, a bude-li On chtít, zbaví vás toho, co vzýváte, a tehdy zapomenete na to, co jste k Němu přidružovali.'),
(831, 6, 42, 'A posílali jsme již k národům předchozím posly a pohromou a neštěstím jsme je postihli doufajíce, že pak budou pokorní!'),
(832, 6, 43, 'Kéž by se byly, když přišla k nim přísnost Naše, pokořily! Však srdce jejich se zatvrdila a satan jim zkrášlil skutky jejich.'),
(833, 6, 44, 'A když zapomněli na to, co bylo jim připomenuto, otevřeli jsme jim brány k radostem všem. A když se potom radovali z toho, čeho se jim dostalo, uchvátili jsme je znenadání a octli se v beznaději.'),
(834, 6, 45, 'A byl zahuben do posledního lid každý, jenž byl nespravedlivý. Chvála Bohu, Pánu lidstva veškerého!'),
(835, 6, 46, 'Rci: "Domníváte se, že jestliže Bůh vám odejme sluch i zrak a zapečetí srdce vaše, že božstvo jiné - kromě Boha - by vám to mohlo navrátit?" Pohleď, jak jim objasňujeme znamení, zatímco oni se odvracejí!'),
(836, 6, 47, 'Rci: "Domníváte se, že až k vám přijde trest Boží - ať znenadání, či oznámen - že zahubí někoho jiného než lid nespravedlivý?"'),
(837, 6, 48, 'My posíláme posly jen jako hlasatele zvěsti radostné a jako varovatele; ti, kdož uvěří a polepší se, ti nemusí mít strach a nebudou zarmouceni.'),
(838, 6, 49, 'Však ty, kdož znamení Naše za lež prohlašují, ty postihne trest za to, že hřešili.'),
(839, 6, 50, 'Rci: "Neříkám vám, že u sebe vlastním pokladnice Boží a znám nepoznatelné, a neříkám ani, že jsem anděl. Já následuji jedině to, co mi bylo vnuknuto." A rci dále: "Jsou si rovni slepý a vidoucí? Což o tom nechcete přemýšlet?"'),
(840, 6, 51, 'A ty, kdož se obávají, že u Pána svého budou shromážděni, jím varuj, že kromě Něho nebudou mít ochránce ani přímluvce žádného - snad budou bohabojní.'),
(841, 6, 52, 'Neodháněj ty, kdož Pána svého za jitra i za večera vzývají toužíce tvář Jeho spatřit! Nepřísluší ti žádat na nich účty a jim rovněž nepřísluší žádat účty na tobě. A odeženeš-li je, tedy věru budeš patřit mezi nespravedlivé.'),
(842, 6, 53, 'A takto jsme je zkoušeli jedny druhými, aby řekli: "Zdaž tito jsou ti z nás, jimž Bůh milost uštědřil?" Což Bůh nezná nejlépe ty, kdož jsou vděčni?'),
(843, 6, 54, 'A když přijdou k tobě ti, kdož v znamení Naše uvěřili, rci: "Mír s vámi! Pán váš Sobě milosrdenství jako povinnost předepsal; dopustí-li se někdo z vás špatného z nevědomosti, potom však pokání koná a polepší se... vždyť On věru je odpouštějící, slitovný."'),
(844, 6, 55, 'A takto My činíme znamení srozumitelnými, aby se stala zjevnou cesta provinilých.'),
(845, 6, 56, 'Rci: "Bylo mi zakázáno uctívat ty, které vedle Boha vzýváte." A rci dále: "Nebudu nikdy následovat vaše učení scestná, neboť zbloudil bych a nepatřil bych k těm, kdož správně jsou vedeni."'),
(846, 6, 57, 'Rci: "Opírám se o důkazy jasné od Pána svého, zatímco vy to prohlašujete za lež. Není v moci mé to, co chcete uspíšit, vždyť rozhodnutí jen Bohu náleží: On podle pravdy rozhoduje a On nejlepší je z rozhodčích."'),
(847, 6, 58, 'Rci: "Kdybych měl moc nad tím, co chcete uspíšit, byla by věru již rozhodnuta věc mezi mnou a vámi - a Bůh nejlépe zná nespravedlivé.'),
(848, 6, 59, 'On klíče má k nepoznatelnému a zná je toliko On; On nejlépe ví, co na souši je i na moři, a nespadne list žádný, aniž by On o něm nevěděl, a není zrnka v temnotách země ani stébla zeleného či uschlého, jež zaznamenáno by nebylo v Knize zjevné.'),
(849, 6, 60, 'On je ten, jenž vás v noci odvolává a jenž zná, co za dne jste vykonali, a potom vás za dne probudí, aby se dovršila lhůta vám stanovená. A posléze budete k Němu navráceni a On vás zpraví o tom, co jste dělali."'),
(850, 6, 61, 'On moc neomezenou má nad služebníky Svými a posílá strážce, aby nad vámi bděli, dokud nezastihne některého z vás smrt; a tehdy poslové Naši jej povolají a nikoho neopomenou.'),
(851, 6, 62, 'A potom budou k Bohu, Pánu svému skutečnému, navráceni. Zdaž Jemu nepřísluší soudit? On věru nejrychlejší je v účtování.'),
(852, 6, 63, 'Rci: "Kdo zachraňuje vás z temnot souše a moře, když vzýváte Jej pokorně a bojácně:, Zachráníš-li nás z tohoto, zajisté budeme patřit k vděčným!? "'),
(853, 6, 64, 'Rci: "Bůh zachrání vás z tohoto i z neštěstí každého, a přesto k Němu pak jiné přidružujete!"'),
(854, 6, 65, 'Rci: "A jest v moci Jeho, aby na vás poslal trest, jenž postihne vás shora i zdola, či aby vás rozdělil na protivné strany a dal jedněm z vás okusit násilí druhých!" Pohleď, jak činíme znamení Svá srozumitelnými - snad to pochopí.'),
(855, 6, 66, 'Lid tvůj to prohlásil za lež, zatímco je to pravda. Rci: "Já nejsem dozorcem vaším.'),
(856, 6, 67, 'Zvěst každá svůj čas stanovený má a záhy se dozvíte!"'),
(857, 6, 68, 'A jestliže uvidíš ty, kteří tlachají o znameních Našich, tak se od nich vzdal, dokud nebudou hovořit o něčem jiném. A dá-li ti satan na to zapomenout, nezdržuj se poté, když sis to znovu připomněl, mezi lidmi nespravedlivými!'),
(858, 6, 69, 'Nepřísluší těm, kdož jsou bohabojní, aby žádali účet od nevěřících. Vždyť toto je pouze připomenutí, snad stanou se oni také bohabojnými.'),
(859, 6, 70, 'Nevšímej si těch, kdož náboženství své za hru a zábavu mají a které zmámil život pozemský! Připomeň jim tímto, že duše každá zahyne jen za vlastní skutky své a nebude mít kromě Boha ochránce ani přímluvce žádného. A nabídne-li duše některá za sebe jakoukoliv náhradu, nebude od ní přijata. Pro ty pak, kdož záhubě propadnou za to, co si vysloužili, určen bude nápoj z vody vroucí a trest bolestný za to, že neuvěřili.'),
(860, 6, 71, 'Rci: "Máme snad vzývat vedle Boha něco, co nám nemůže ani prospět, ani uškodit, a obrátit se nazpět po stopách svých poté, co Bůh nás již jednou uvedl na stezku správnou, podobni tomu, jemuž satani dali zbloudit na zemi, ačkoliv má přátele, kteří volají zvouce jej k cestě správné: Pojď k nám!" Rci: "Vedení Boží je věru jediné správné vedení. A bylo nám nařízeno, abychom se odevzdali do vůle Pána lidstva veškerého."'),
(861, 6, 72, '"Dodržujte modlitbu a bojte se Boha, neb On je ten, u Něhož budete shromážděni!"'),
(862, 6, 73, 'On je ten, jenž nebesa i zemi jako skutečnost vážnou stvořil, a v den, kdy řekne: "Staniž se!", stane se. Slovo Jeho je pravda a Jemu bude patřit vláda v ten den, kdy zatroubeno bude na pozoun; On zná nepoznatelné i všeobecně známé a On moudrý je i vědoucí.'),
(863, 6, 74, 'A hle, pravil Abraham otci svému Ázarovi: "Zdaž bereš si modly jako božstva? Věru já vidím tebe i lid tvůj v bludu zjevném."'),
(864, 6, 75, 'A takto jsme ukázali Abrahamovi království nebeská i pozemská, aby se stal jedním z pevně přesvědčených.'),
(865, 6, 76, 'A když se nad ním rozprostřela noc, spatřil hvězdu a zvolal: "Toto je Pán můj!" Když však zapadla, řekl: "Nemám rád zapadající."'),
(866, 6, 77, 'A když spatřil vycházející měsíc, zvolal: "Toto je Pán můj!" Když však zapadl, řekl: "Jestliže mne Pán můj správně nepovede, budu určitě patřit k lidu zbloudilému!"'),
(867, 6, 78, 'A když spatřil vycházející slunce, zvolal: "Toto je Pán můj, neboť jest největší!" A když zapadlo, řekl: "Lide můj, nejsem zodpovědný za to, co k Bohu přidružujete,'),
(868, 6, 79, 'nýbrž obracím se - jako haníf - k tomu, jenž nebesa i zemi stvořil, a nebudu patřit k těm, kdož k Bohu přidružují."'),
(869, 6, 80, 'A hádal se s ním lid jeho, ale on pravil: "Chcete se přít se mnou o Bohu, když On mne již na správnou cestu uvedl? Nemám strach z toho, co k Němu přidružujete, leda bude-li si to Bůh přát. Pán můj ve vědění Svém všechny věci objímá - což nebudete o tom uvažovat?'),
(870, 6, 81, 'Jak bych se bál toho, co k Němu přidružujete, když vy sami se nebojíte přidružovat k Bohu to, k čemu vám On neseslal žádné oprávnění? Která z obou těchto stran má větší nárok na bezpečí, odpovězte, víte-li!'),
(871, 6, 82, 'Ti, kdož uvěřili a nepotřísnili víru svou nespravedlností, ti jsou v bezpečí a po správné cestě kráčejí."'),
(872, 6, 83, 'A toto jsou důkazy Naše, jež dali jsme Abrahamovi proti lidu jeho. My pak povyšujeme na stupních, koho chceme, a Pán tvůj je věru moudrý, vševědoucí.'),
(873, 6, 84, 'A darovali jsme Abrahamovi Izáka a Jakuba a vedli jsme oba cestou pravou; a již předtím jsme tak vedli Noema a z potomstva jeho Davida, Šalomouna, Jóba, Josefa, Mojžíše a Áróna. A takto My odměňujeme ty, kdož dobré konají.'),
(874, 6, 85, 'A vedli jsme Zachariáše, Jana, Ježíše a Eliáše - ti všichni patří k bohabojným -,'),
(875, 6, 86, 'a Ismaela, Elišu, Jonáše a Lota a vyznamenali jsme všechny nad lidstvo veškeré'),
(876, 6, 87, 'a také některé z jejich předků, potomků a bratrů a vyvolili jsme si je a uvedli jsme je na stezku přímou.'),
(877, 6, 88, 'Hle, toto je vedení Boží, On vede jím, koho chce ze služebníků Svých. Však kdyby byli přidružovali, marné by bylo to, co konali.'),
(878, 6, 89, 'A toto jsou ti, jimž dali jsme Písmo, moudrost i prorocké poslání. Jestliže tihleti dnes v to neuvěří, pověříme tím lid jiný, jenž nebude nevěřící.'),
(879, 6, 90, 'A toto jsou ti, jež Bůh správně vedl - jdi tedy po správné cestě jejich! Rci: "Nežádám za to od vás odměnu žádnou, vždyť toto je pouze připomenutí lidstvu veškerému!"'),
(880, 6, 91, 'A neocenili Boha pravou hodnotou Jeho, když říkají: "Bůh vůbec nic neseslal smrtelníku nějakému!" Zeptej se: "A kdo tedy seslal Písmo, s nímž přišel Mojžíš, aby bylo světlem a vedením pro lidi? Píšete je na svitky pergamenu, které zčásti ukazujete, avšak z větší části skrýváte. A byli jste poučeni o tom, co jste neznali ani vy, ani otcové vaši." - Rci: "Bůh!" - a potom je nech, aby se bavili svým tlacháním marným!'),
(881, 6, 92, 'A toto je Písmo požehnané, jež seslali jsme jako potvrzení pravdivosti toho, co dříve bylo sesláno, abys jím varoval Matku měst i ty, kdož kolem ní žijí. Ti, kdož věří v život budoucí, věří i v ně, a to jsou ti, kdož pečliví jsou v modlitbách svých.'),
(882, 6, 93, 'Kdo je nespravedlivější než ten, kdo proti Bohu lži si vymýšlí či říká: "Bylo mi sesláno vnuknutí," zatímco mu nebylo vnuknuto vůbec nic, anebo ten, jenž hovoří: "Já mohu seslat podobné tomu, co seslal Bůh." Kéž bys jen viděl hříšné v náručí smrti, až andělé rozprostřou ruce své řkouce: "Vypusťte duše své, dnes odměněni budete trestem zahanbujícím za to, že mluvili jste nepravdu o Bohu, a za to, že pyšně jste se odvraceli od znamení Jeho!'),
(883, 6, 94, 'Přicházíte k nám nyní osamělí tak, jak jsme vás poprvé stvořili, a zanecháváte za sebou vše, čím jsme vás obdarovali. A nevidíme u vás přímluvce vaše, o nichž jste se domnívali, že budou s vámi jako společníci vaši. Svazky mezi vámi jsou přetrhány a opustilo vás to, o čem jste mylné názory měli."'),
(884, 6, 95, 'Bůh je ten, jenž rozštěpuje zrno i pecku datlovou, vyvádí živé z mrtvého a mrtvé z živého. Takový je Bůh - jak jen mohli jste se od Něho odvrátit?'),
(885, 6, 96, 'On dává vzejít ranní záři a ustanovil noc pro odpočinek a slunce a měsíc pro měření času. Hle, toto je ustanovení mocného, vševědoucího.'),
(886, 6, 97, 'A On je ten, jenž pro vás stvořil hvězdy, abyste se mohli podle nich řídit v temnotách souše i moře. Takto My činíme srozumitelnými znamení pro lid, jenž schopen je poznáni.'),
(887, 6, 98, 'A On je ten, jenž vám dal vzniknout z jediné bytosti; a pak je spočinutí a složení. A takto jsme učinili znamení Svá srozumitelnými pro lid, jenž schopen je pochopení.'),
(888, 6, 99, 'A On je ten, jenž sesílá vodu z nebe, a pomocí jí dáváme vyrůst rostlinám všech druhů a dáváme z ní vyrazit zeleni, z níž vznikají zrna seřazená v klasy, i palmám, z jejichž vrchlíků husté trsy datlí visí, i zahradám révy vinné, olivám a jablkům granátovým, jež jsou si podobná i nepodobná. Pohleďte na plody jejich, když uzrávají, a pohleďte na zralost jejich - v tom věru znamení jsou pro lid věřící.'),
(889, 6, 100, 'A oni učinili z džinů společníky Bohu, zatímco byl to On, jenž je stvořil, a v neznalosti své vymyslili Mu syny a dcery. Chvála Jemu, jenž povznesen je nade vše, co mu připisují!'),
(890, 6, 101, 'Tvůrce nebes a země jak by mohl mít děti, když nemá družku žádnou a Sám stvořil všechny věci a o všem je vševědoucí.'),
(891, 6, 102, 'Takový je Bůh, Pán váš! A není božstva jiného kromě Něho, stvořitele věcí všech! Uctívejte tedy Jeho, vždyť On věru ochráncem je všeho!'),
(892, 6, 103, 'Zraky lidí Ho nedosáhnou, leč On jich dosahuje; On bystrý je i dobře zpravený.'),
(893, 6, 104, 'A již k vám přišly důkazy viditelné od Pána vašeho; kdo jasně je zří, ku prospěchu svému tak činí, a kdo slepý je, pak to k jeho škodě je a já nejsem nad vámi dozorcem žádným.'),
(894, 6, 105, 'A takto My činíme znamení srozumitelnými, aby mohli říci: "Tys studoval!" a abychom to vysvětlili lidu, jenž pochopení má.'),
(895, 6, 106, 'Následuj to, co bylo ti od Pána tvého vnuknuto: není božstva kromě Boha, odvrať se tedy od těch, kdož k Němu přidružují.'),
(896, 6, 107, 'Kdyby Bůh chtěl, nebyli by modloslužebníky, však neučinili jsme tě ani strážcem jejich, ani dozorcem nad nimi.'),
(897, 6, 108, 'Neurážejte ty, které oni vedle Boha vzývají, aby v nenávisti své neuráželi Boha z neznalosti! A takto jsme zkrášlili národu každému činy jeho; leč posléze se navrátí k Pánu svému a On zpraví je o tom, co dělali.'),
(898, 6, 109, 'A přísahali při Bohu svou přísahou nejslavnostnější, že dostane-li se jim znamení, skutečně v ně uvěří. Rci: "Jedině Bůh může znamení učinit. Ale i kdyby k nim přišla, jak poznáte, že oni uvěří?"'),
(899, 6, 110, 'My odvrátíme srdce jejich i zraky jejich stejně jako tehdy, když neuvěřili poprvé, a ponecháme je bloudit ve svévoli jejich jak nevidomé.'),
(900, 6, 111, 'A i kdybychom k nim seslali anděly a i kdyby s nimi mrtví mluvili a i kdybychom byli shromáždili věci všechny před nimi, věru by neuvěřili, leda by tomu chtěl Bůh, avšak většina z nich jsou pošetilci.'),
(901, 6, 112, 'A takto jsme učinili každému poslu nepřátele: satany z lidí i džinů, kteří si vzájemně našeptávají ozdobná slova plná bludů. Kdyby Pán tvůj chtěl, nebyli by tak činili; ponech je tedy v tom, co si vymýšlejí!'),
(902, 6, 113, 'Nechť se srdce těch, kdož nevěří v život posmrtný, nakloní k těmto výmyslům, nechť v nich naleznou zalíbení a nechť tedy provádějí to, co provádějí!'),
(903, 6, 114, 'Mám si snad žádat jako soudce někoho jiného než Boha, jenž je tím, kdo seslal vám Písmo jasně vyložené? Ti, jimž dali jsme Písmo, dobře vědí, že od Pána tvého bylo s pravdou sesláno. A nebuď tedy z těch, kdož pochybují!'),
(904, 6, 115, 'Nechť tedy dovrší se slova Pána tvého jako pravda a spravedlnost! A nikdo nemůže změnit slova Jeho - a On slyšící je i vševědoucí.'),
(905, 6, 116, 'Uposlechneš-li většinu těch, kdož na zemi žijí, pak tě svedou z cesty Boží, vždyť oni následují jen domněnky své a pouze předpokládají.'),
(906, 6, 117, 'Pán tvůj zná nejlépe ty, kdož zbloudili z cesty Jeho, a zná nejlépe i ty, kdož správnou cestou jsou vedeni.'),
(907, 6, 118, 'Jezte tedy z toho, nad čím bylo vysloveno jméno Boží, věříte-li v Jeho znamení!'),
(908, 6, 119, 'Co je s vámi, že nepojídáte z toho, nad čím bylo proneseno jméno Boží, když vám Bůh již srozumitelně objasnil vše, co je vám zakázáno - leda jste-li donuceni. Zajisté však mnozí jsou sváděni z cesty vášněmi svými z neznalostí. Pán tvůj však nejlépe zná přestupníky.'),
(909, 6, 120, 'Vystříhejte se hříchu, ať skrytého či zjevného, neboť ti, kdož pášou hříchy, budou odměněni za to, co si vysloužili.'),
(910, 6, 121, 'Nepojídejte z toho, nad čím nebylo proneseno jméno Boží, neboť to věru je hanebnost! Satani vskutku budou našeptávat stoupencům svým, aby se s vámi hádali, však uposlechnete-li je, věru budete modloslužebníky.'),
(911, 6, 122, 'Zdaž ten, jenž mrtev byl a jehož jsme oživili a jemuž jsme světlo dali, aby s ním kráčel mezi lidmi, je roven tomu, kdo v temnotách dlí, z nichž východiska není? A takto bylo zkrášleno nevěřícím to, co prováděli.'),
(912, 6, 123, 'A takto jsme v každém městě umístili velmože jako hříšníky, aby tam úklady chystali; však neobelstí nikoho leda sebe samé, aniž o tom mají tušení.'),
(913, 6, 124, 'A když se jim dostane znamení, říkají: "Neuvěříme, dokud se nám nedostane toho, čeho se dostalo poslům Božím." Leč Bůh ví nejlépe, kam umísťuje poselství Své! Avšak hříšníci budou postiženi ponížením před Bohem i trestem strašným za úklady, jež chystali.'),
(914, 6, 125, 'Koho Bůh chce vést cestou správnou, tomu otevře hruď pro islám; tomu, komu chce dát zbloudit, tomu zúží a uzavře hruď, jako by měl stoupat k nebi. A takto Bůh dává pocítit hněv svůj těm, kdož neuvěřili.'),
(915, 6, 126, 'Toto je stezka Pána tvého přímo vedoucí. A vyložili jsme srozumitelně znamení lidu, který si připomíná.'),
(916, 6, 127, 'Pro ně je připraven příbytek míru u Pána jejich a On ochráncem je jejich za to, co konali.'),
(917, 6, 128, 'A v den, kdy shromáždí je Bůh, řekne: "Ó shromáždění džinů! Již příliš mnoho jste lidem učinili!" Tehdy řeknou pomocníci jejich z lidí: "Pane náš, měli jsme prospěch jeden z druhého, avšak dospěli jsme ke lhůtě, kterou jsi nám stanovil." I řekne jim Bůh: "Oheň budiž vaším útočištěm, nesmrtelní v něm budete, leda bude-li Bůh chtít jinak!" A Pán tvůj je věru moudrý, vševědoucí.'),
(918, 6, 129, 'A takto my dáváme jedny nespravedlivé druhým jako přátele v odměnu za to, co si vysloužili.'),
(919, 6, 130, '"Ó shromáždění džinů a lidí! Zdaž k vám nepřišli poslové z vašich řad vzešlí, přednášejíce vám znamení Naše a varujíce vás před setkáním s tímto vaším dnem?" A odpovědí: "Dosvědčujeme to sami proti sobě." Zmámil je život pozemský a dosvědčí sami proti sobě, že byli nevěřící.'),
(920, 6, 131, 'A je tomu tak proto, že Pán tvůj není takový, aby zahubil města nespravedlivě, když obyvatelé jejich nic netušili.'),
(921, 6, 132, 'A pro každého je stanoven stupeň odměny podle toho, co konal, a není Pánu tvému lhostejné, co dělají.'),
(922, 6, 133, 'Pán tvůj je soběstačný a plný milosrdenství; kdyby chtěl, nechal by vás zahynout a zaměnil by vás potom, čím by chtěl, podobně jako vám dal vzejít z potomstva lidu jiného.'),
(923, 6, 134, 'Věru to, co je vám slíbeno, se vskutku dostaví a vy nebudete schopni tomu zabránit.'),
(924, 6, 135, 'Rci: "Lide můj, jednejte podle stanoviska svého, já také tak budu jednat, a záhy se dozvíte kdo v konečném příbytku bude dlít; a věru nestanou se nespravedliví blaženými.'),
(925, 6, 136, 'Nevěřící určují Bohu podíl z toho, čemu On dal vyrůst ze země i z dobytka, a říkají: "Tohle náleží Bohu" - podle úsudku jejich - "a toto patří těm, jež k Němu přidružujeme." Avšak co je určeno pro přidružené, nikdy k Bohu nedospěje, zatímco to, co Bohu náleží, nedospěje nikdy k přidruženým. Jak špatný je úsudek jejich!'),
(926, 6, 137, 'A takto zkrášlili ti, kteří jsou jimi přidružováni, mnohým modloslužebníkům zabíjeni jejich dětí, aby je v záhubu uvrhli a náboženství jejich zatemnili. A kdyby Bůh byl chtěl, nebyli by tak učinili; nech je tedy být i s tím, co si lživě vymýšlejí!'),
(927, 6, 138, 'A říkají: "Tento dobytek a tyto plodiny jsou zasvěceny a smí z nich pojídat jen ti, jimž to dovolujeme" - podle úsudku jejich. A jsou také zvířata, na nichž je zakázáno jezdit a nakládat na ně, a také ta, nad nimiž nepronášejí jméno Boží, vymýšlejíce si lži o Něm. Avšak On je odmění za to, co si vymýšlejí.'),
(928, 6, 139, 'A říkají: "To, co je v lůnech tohoto dobytka, je čisté jen pro naše muže a je zakázáno pro manželky naše." Avšak narodí-li se to mrtvé, rozdělí se o to obě pohlaví. Bůh však je odmění za to, co Mu připisují - a On věru moudrý je i vševědoucí.'),
(929, 6, 140, 'Ztrátu utrpí ti, kdož děti své zabíjejí z hlouposti a nevědomosti, i ti, kdož proti Bohu lži si vymýšlejí zakazujíce to, co jim Bůh jako obživu uštědřil. Zbloudili již a nejsou správnou cestou vedeni.'),
(930, 6, 141, 'A On je ten, jenž dává vyrůst zahradám s révou podepřenou i nepodepřenou loubím; i palmám datlovým, i obilí dávající mu stravu rozličnou, i olivovníkům a granátovým jabloním podobným i nepodobným. Požívejte z plodů jejich, když dozrají, a rozdávejte z nich podíl patřičný v den sklizně jejich, avšak nebuďte marnotratní, neboť Bůh nemiluje marnotratné.'),
(931, 6, 142, 'A mezi dobytčaty jsou ta, jež nosí břemena, a jiná pro koberce. Pojídejte z toho, co vám Bůh uštědřil, a nechoďte ve stopách satana, jenž pro vás je nepřítelem zjevným!'),
(932, 6, 143, 'A je pro vás osm dobytčat v párech: z ovcí dva a z koz také dva. Rci: "Zakázal snad On samce obou či samice anebo to, co skrývají lůna samic obou druhů? Sdělte mi to se znalostí, jste-li pravdomluvní!"'),
(933, 6, 144, 'A z velbloudů dva a ze skotu dva. Rci: "Zakázal snad On samce obou či samice anebo to, co skrývají lůna samic obou druhů? Či byli jste svědky toho, když Bůh vám to nařídil?" Kdo je nespravedlivější než ten, který si o Bohu lži vymýšlí, aby tak svedl lidi bez jakéhokoliv vědění? A Bůh věru nepovede lid nespravedlivý.'),
(934, 6, 145, 'Rci: "Nenalézám v tom, co bylo mi vnuknuto, jiný zákaz pro toho, kdo jí, než zákaz mršiny nebo krve prolité či vepřového masa, neboť to je nečisté a hanebné, a dále to, co bylo zasvěceno jinému než Bohu. Však kdo byl donucen to jísti, aniž po tom toužil či přestupníkem byl, pak Pán tvůj věru je odpouštějící, slitovný."'),
(935, 6, 146, 'Těm, kdož vyznávají víru židovskou, jsme zakázali všechna zvířata mající drápy; a ze skotu a ovcí jsme jim zakázali tuk kromě toho, jejž mají v hřbetech či ve vnitřnostech nebo jenž smíšen je s kostmi. Takto jsme je odměnili za jejich vzpurnost - a My zajisté jsme spravedliví.'),
(936, 6, 147, 'Prohlašují-li tě za lháře, rci: "Pán váš sice vládne nesmírným milosrdenstvím, avšak přísnost Jeho nebude odvrácena od lidu hříšného."'),
(937, 6, 148, 'A řeknou ti, kdož k Bohu přidružují: "Kdyby Bůh chtěl, nebyli bychom modloslužebníky my ani otcové naši a nebyli bychom prohlásili nic za zakázané." A takto prohlašovali posly za lháře již ti, kdož byli před nimi, až ochutnali přísnost Naši. Rci: "Máte-li vůbec nějaké vědění, tedy nám je předveďte! Vy následujete pouze domněnky své a vy se jen dohadujete."'),
(938, 6, 149, 'Rci: "Jedině Bůh má důkaz přesvědčující; kdyby byl chtěl, byl by vás všechny uvedl na cestu správnou."'),
(939, 6, 150, 'Rci: "Přiveďte svědky své, kteří by dosvědčili, že Bůh toto zakázal!" A jestliže to dosvědčí, pak ty spolu s nimi nesvědč a nenásleduj scestná učení těch, kdož znamení Naše za lež prohlašují, a těch, kdož v život budoucí nevěří a kteří Pánu svému rovné dávají.'),
(940, 6, 151, 'Rci: "Pojďte sem, abych vám sdělil to, co Pán váš vám zakázal! Nepřidružujte k Němu nic, buďte laskaví k rodičům, nezabíjejte z nedostatku děti své, vždyť jsme uštědřili nezbytné pro vás i pro ně! Střezte se smilstva veřejného i tajného, nezabíjejte nikoho, koho Bůh vám zakázal, leda podle práva! A toto je to, co On vám přikazuje - snad budete rozumní!'),
(941, 6, 152, 'Nepřibližujte se k majetku sirotků jinak než s dobrými úmysly, dokud nedosáhnou dospělosti! Dávejte dobré míry i spravedlivé váhy! My pak neukládáme duši břemeno žádné nad její síly. A když hovoříte, buďte spravedliví, i když se jedná o příbuzného, a dodržujte úmluvu s Bohem! A toto je to, co On vám přikazuje - snad budete toho pamětlivi!'),
(942, 6, 153, 'A toto je vskutku stezka Má, jež vede přímo. Kráčejte po ní a nenásledujte cesty jiné, jež by vás vzdálily od cesty Jeho. A toto je to, co On vám přikazuje - snad budete bohabojní!"'),
(943, 6, 154, 'A potom jsme dali Mojžíšovi Písmo jako dovršení dobrodiní pro toho, jenž koná dobré, a jako srozumitelné vysvětlení věcí všech i jako vedení a milosrdenství doufajíce, že snad lidé uvěří v setkání s Pánem svým.'),
(944, 6, 155, 'A také toto Písmo, jež seslali jsme nyní, je požehnané; následujte je a buďte bohabojní - snad dosáhnete milosti!'),
(945, 6, 156, 'Abyste nemohli říci: "Dvěma skupinám bylo sesláno před námi Písmo a my věru jsme neměli ponětí o učení jejich;"'),
(946, 6, 157, 'anebo abyste neřekli: "Kdyby nám bylo bývalo sesláno Písmo, byli bychom bývali jistě na cestě správnější než oni!" A dostalo se vám nyní důkazu jasného od Pána vašeho i vedení a milosrdenství. Kdo je tedy nespravedlivější než ten, kdo znamení Boží za lež prohlašuje a od nich se odvrací? A odměníme ty, kdož od znamení Našich se odvracejí, trestem bolestným za to, že se odvraceli.'),
(947, 6, 158, 'Což očekávají, že k nim přijdou andělé nebo že snad přijde sám Pán tvůj či že přijdou nějaká znamení Pána tvého! V ten den, kdy objeví se určitá znamení Pána tvého, nepomůže duši žádné víra její, jestliže neuvěřila již dříve či nevykonala dobro žádné ve víře své. Rci: "Čekejte tedy, my také čekáme!"'),
(948, 6, 159, 'S těmi, kdož rozštěpili náboženství své a stali se sektáři, ty věru nemáš nic společného! Věc jejich patří jedině Bohu, jenž jim pak oznámí, co učinili.'),
(949, 6, 160, 'Kdo přijde se skutky dobrými, tomu dostane se desetinásobku jejich, a kdo přijde s činy špatnými, tomu dostane se odměny podobné; a nebude jim ukřivděno.'),
(950, 6, 161, 'Rci: "Pán můj uvedl mne na stezku přímou, k náboženství nezměnitelnému, k víře Abrahamově, jenž hanífem byl a k modloslužebníkům nepatřil."'),
(951, 6, 162, 'Rci: "Věru modlitba má, obřady mé, život můj i smrt má náleží Bohu, Pánu lidstva veškerého,'),
(952, 6, 163, 'jenž společníka nemá žádného. A toto mi bylo nařízeno a já první jsem z těch, kdož do vůle Jeho se odevzdali."'),
(953, 6, 164, 'Rci: "Jak bych mohl toužit po nějakém pánu jiném než Bohu, když On Pánem je všech věcí?" Každá duše získá jen to, co si vysloužila, a žádná duše hříchy obtížená neponese břímě jiné. A posléze k Pánu svému budete navráceni a On vás zpraví o tom, o čem jste ve sporu byli.'),
(954, 6, 165, 'On je ten, jenž učinil vás nástupci dřívějších na zemi a jenž povýšil jedny z vás nad druhé v hodnostech, aby vás zkoušel tím, čím vás obdařil; Pán tvůj je věru rychlý v trestání, ale také odpouštějící, slitovný!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Alif lám mím sád!'),
(956, 7, 2, 'Toto je Písmo, které ti bylo sesláno - nechť není v hrudi tvé ohledně něho obavy žádné abys jím varoval a aby bylo pro věřící připomenutím.'),
(957, 7, 3, 'Následujte to, co bylo vám sesláno od Pána vašeho, a nenásledujte jiných pánů vedle Něho! Jak málo si to připomínáte!'),
(958, 7, 4, 'Kolik měst jsme již zahubili! Přísnost Naše je zastihla za noci anebo za poledne.'),
(959, 7, 5, 'A když postihla je přísnost Naše, zmohli se jedině na zvolání: "Věru jsme byli nespravedliví!"'),
(960, 7, 6, 'A zajisté se budeme tázat těch, k nimž poslové byli vysláni, a budeme se ptát i vyslanců Božích,'),
(961, 7, 7, 'a věru jim sdělíme se znalostí vše, vždyť My jsme nebyli nepřítomni.'),
(962, 7, 8, 'V ten den bude váhou pravda; ti, jichž váha bude obtěžkána dobrými skutky, ti blaženi budou,'),
(963, 7, 9, 'zatímco ti, jichž váha bude lehká, sami sobě ztrátu způsobili tím, že nespravedliví k znamením Našim byli.'),
(964, 7, 10, 'A umístili jsme vás na zemi a připravili jsme pro vás na ní obživu - a jak málo jste vy vděční!'),
(965, 7, 11, 'A vskutku jsme vás stvořili a potom jsme vám podobu dali a posléze jsme k andělům pravili: "Padněte na zem před Adamem!" A padli všichni před ním kromě Iblíse, jenž nebyl mezi těmi, kdož se klaněli.'),
(966, 7, 12, 'Pravil Bůh: "Co ti zabránilo, abys padl, když jsem ti to poručil?" Odpověděl: "Já lepší jsem než on, mne z ohně jsi stvořil, jeho však jen z hlíny."'),
(967, 7, 13, 'Pravil Bůh: "Sestup odsud, neboť ti nepřísluší, aby ses zde pyšným ukazoval! Odejdi, neboť věru budeš z opovržených!"'),
(968, 7, 14, 'Řekl Iblís: "Popřej mi odklad až do dne, kdy budou vzkříšeni!"'),
(969, 7, 15, 'Pravil Bůh: "Budiž tedy mezi těmi, jimž odklad je dán!"'),
(970, 7, 16, 'Řekl Iblís: "Za to, žes mne odvrhl, budu věru na ně číhat na přímé stezce Tvé'),
(971, 7, 17, 'a pak je budu pokoušet zepředu i zezadu, zprava i zleva, takže většinu z nich shledáš nevděčnými!"'),
(972, 7, 18, 'Pravil Bůh: "Odejdi odsud zahanben a zavržen! Však těmi, kdož následovat tě budou, já věru peklo naplním všemi!'),
(973, 7, 19, 'A ty, Adame, obývej s manželkou svou ráj! Jezte, z čeho se vám zachce, ale nepřibližujte se k tomuto stromu, abyste se nestali nespravedlivými!"'),
(974, 7, 20, 'Však satan jim oběma našeptal, aby odhalili to, co dosud před nimi bylo skryto z nahoty jejich, a řekl: "Pán váš zakázal vám tento strom jen proto, abyste se nestali anděly či nesmrtelnými!"'),
(975, 7, 21, 'A přísahal jim: "Já vaším rádcem jsem dobrým."'),
(976, 7, 22, 'A takto je mámením svým v bloudění zavedl. A když oba ochutnali ze stromu, odhalila se jim nahota jejich a jali se šíti na sebe oděvy z listí rajského. A zavolal na ně Pán jejich: "Což jsem vám dvěma nezakázal tento strom a neřekl vám, že satan je vaším nepřítelem zjevným?"'),
(977, 7, 23, 'Odpověděli: "Pane náš, věru jsme sami sobě ukřivdili; jestliže se nad námi neslituješ a neodpustíš nám, budeme věru z těch, jež ztráta postihla."'),
(978, 7, 24, 'Řekl Pán: "Sestupte z ráje! Jedni druhým nepřáteli budete a na zemi bude místo vašeho pobytu a užívání jen na čas určený!"'),
(979, 7, 25, 'A pravil dále: "Na ní žít budete a na ní i zemřete a z ní pak budete vyvedeni."'),
(980, 7, 26, 'Synové Adamovi, seslali jsme vám již oděvy, které nahotu vaši přikrývají, i ozdoby, avšak oděv bohabojnosti je lepší. A toto je jedno ze znamení Božích - snad si to připomenou!'),
(981, 7, 27, 'Synové Adamovi! Nechť vás nepokouší satan tak, jako způsobil odchod rodičů vašich z ráje, strhávaje z nich oděv, aby jim ukázal nahotu jejich! A dívá se na vás on i jeho spřeženci z místa, kde je nevidíte. A věru jsme učinili satany přáteli těch, kdož nevěří!'),
(982, 7, 28, 'A když se dopustí necudnosti nějaké, říkají: "Nalezli jsme otce své, že takto činí, a nám tak nařídil Bůh!" Odpověz: "Bůh nikdy nepřikazuje necudnosti! Chcete snad mluvit o Bohu něco, co vůbec neznáte?"'),
(983, 7, 29, 'Rci: "Pán můj spravedlnost přikazuje! Obracejte k Němu tváře své u modlitebny každé a vzývejte Jej, upřímně mu zasvěcujíce svou víru! A jak vás stvořil poprvé, tak se k Němu i navrátíte."'),
(984, 7, 30, 'Jednu skupinu On správnou cestou vede, zatímco druhou odsoudil k bloudění, takže si vzali místo Boha za přátele satany, a přitom si myslí, že po správné cestě jsou vedeni.'),
(985, 7, 31, 'Synové Adamovi! Odívejte se do šatů ozdobných v modlitebně každé! Jezte a pijte, avšak nepřehánějte, vždyť On nemiluje ty, kdož přehánějí!.'),
(986, 7, 32, 'Rci: "Kdo zakázal šaty ozdobné, jež Bůh seslal služebníkům Svým podobně jako výtečné věci pro obživu?" Odpověz: "Toto je dáno na dobu života pozemského těm, kdož uvěřili, a bude to pro ně čisté v den zmrtvýchvstání." A takto My činíme srozumitelnými znamení pro lid vědoucí.'),
(987, 7, 33, 'Rci: Pán můj věru zakázal jen necudnosti veřejné i tajné, hřích a bezprávnou svévoli; a dále, abyste přidružovali k Bohu to, k čemu On neseslal oprávnění, a mluvili o Bohu to, o čem nemáte ponětí."'),
(988, 7, 34, 'A každý národ má svou lhůtu danou, a když se lhůta jeho dovrší, nemůže ji odložit ani uspíšit ni o hodinu jedinou.'),
(989, 7, 35, 'Synové Adamovi! Věru k vám přijdou poslové z vašich řad vzešlí, aby vám sdělili Má znamení. Ti, kdož jsou bohabojní a dobré skutky konají, ti strach mít nemusí a nebudou zarmouceni!'),
(990, 7, 36, 'Avšak ti, kdož Má znamení za lež prohlašovali a s pýchou se od nich odvraceli, ti budou ohně obyvateli a v něm zůstanou nesmrtelní.'),
(991, 7, 37, 'A kdo je nespravedlivější než ten, kdo o Bohu si lži vymyslil či znamení Jeho za lež prohlásil? Takovým dostane se podílu jejich podle toho, co v Knize je napsáno. Až k nim přijdou vyslanci Naši, aby je k nám povolali, zeptají se: "Kde jsou ti, které jste vedle Boha vzývali?" A tehdy odvětí: "Ztratili se nám!" - a takto sami o sobě dosvědčí, že byli nevěřící.'),
(992, 7, 38, 'A řekne Bůh:,, Vstupte do ohně mezi národy z džinů a lidí, které již zmizely před vámi!" A kdykoliv vstoupí do něho národ některý, proklínat bude sobě podobné, a až nakonec se v něm octnou všechny vespolek, tu poslední řeknou o prvních: "Pane náš, tihle nás z cesty svedli, vyměř jim trest ohně dvojnásobný!" A odpoví; "Všem dostane se dvojnásobku, avšak vy to ještě nevíte."'),
(993, 7, 39, 'A řeknou národy první posledním: "Nepatří vám, abyste měly přednost nějakou před námi, okuste tedy trest za to, co jste si vysloužily!"'),
(994, 7, 40, 'Věru těm, kdož znamení Naše za lživá prohlašovali a pyšně se od nich odvraceli, nebudou otevřeny nebes brány a oni nevstoupí do ráje, dokud neprojde velbloud uchem jehly. A takto My odměňujeme provinilé.'),
(995, 7, 41, 'Pro ně budou v pekle lůžka připravena a na nich pokrývky ohnivé - a takto My odměňujeme nespravedlivé!'),
(996, 7, 42, 'Avšak ti, kdož uvěřili a dobré skutky konali a My duši každé ukládáme jen to, nač stačí, ti budou ráje obyvateli a v něm budou nesmrtelní.'),
(997, 7, 43, 'A vyjmeme z hrudí jejich nenávist veškerou a pod nimi řeky potekou - a budou říkat: "Chvála Bohu, jenž nás sem uvedl! My sami bychom nebyli nalezli správnou cestu, kdyby nás Bůh nebyl na ni zavedl; a přinesli nám poslové Pána našeho pravdu." A bude na ně zavoláno: "Ráj tento byl vám dán v dědictví za to, co jste konali."'),
(998, 7, 44, 'A zavolají obyvatelé ráje na obyvatele ohně: "My shledali jsme, že to, co Pánem naším bylo slíbeno, je pravda. Zdaž i vy jste shledali slib Pána svého pravdivým?" A odpovědí: "Ano!" A tehdy zavolá hlasatel mezi nimi: "Nechť Bůh prokleje nespravedlivé,'),
(999, 7, 45, 'ty, kdož odvraceli jiné od cesty Boží, přáli si ji pokřivit a nevěřili v život posmrtný!"'),
(1000, 7, 46, 'A mezi nimi bude přepážka; a nad rozpoznáním budou ustanoveni mužové, kteří rozeznají je podle známek jejich - a ti zvolají na obyvatele ráje: "Mír s vámi! Nevstoupí tam nespravedliví, byť po tom i toužili!"'),
(1001, 7, 47, 'A když se obrátí zraky jejich k obyvatelům ohně, zvolají: "Pane náš, neumisťuj nás společně s lidmi nespravedlivými!"'),
(1002, 7, 48, 'A zvolají mužové rozpoznání na lidi, jež rozeznají podle známek jejich, řkouce: "Nebylo vám k ničemu vaše hromadění majetku ani to, čím jste se vychloubali.'),
(1003, 7, 49, 'Zdaž toto nejsou ti, o nichž jste přísahali, že Bůh je milostí Svou neobdaří? Vstupte do ráje a nemějte strach a nebuďte zarmouceni!"'),
(1004, 7, 50, 'A vykřiknou obyvatelé ohně na obyvatele ráje: "Vylijte na nás vodu nebo něco z toho, co uštědřil vám Bůh!" A odpovědí: "Bůh obojí zakázal pro nevěřící,'),
(1005, 7, 51, 'kteří náboženství své za hru a zábavu měli a které zlákal život pozemský!" V ten den na ně zapomeneme, tak jako oni zapomněli na setkání s tímto dnem, a proto, že znamení Naše popírali.'),
(1006, 7, 52, 'Zajisté přišli jsme k nim s Písmem, jež vědomě jsme srozumitelným učinili, aby stalo se věděním a milosrdenstvím pro lid věřící.'),
(1007, 7, 53, 'Což očekávají něco jiného než výklad jeho? V ten den, kdy přijde výklad jeho, řeknou ti, kdož dříve naň zapomněli: "Poslové Pána našeho věru s pravdou přišli. Což nemáme dnes přímluvce, kteří by se za nás přimluvili? Anebo nemohli bychom se vrátit na zem, abychom mohli vykonat něco jiného než to, co jsme konali?" Avšak oni sami duše své zahubili a byli opuštěni tím, co si vymyslili.'),
(1008, 7, 54, 'Pánem vaším je vskutku Bůh, jenž stvořil nebesa i zemi v šesti dnech a potom se usadil na trůně Svém. On pokrývá nocí den, který se bez přestání žene za ní, zatímco slunce, měsíc a hvězdy jsou rozkazu Jeho podrobeny. Což Jemu nenáleží stvoření a rozkazování? Požehnán buď Bůh, Pán lidstva veškerého!'),
(1009, 7, 55, 'Vzývejte Pána svého pokorně a skrytě - vždyť On věru nemiluje přestupníky!'),
(1010, 7, 56, 'A nešiřte pohoršení na zemi poté, co stala se na ní náprava! A vzývejte Jej s bázní a touhou, neboť věru milosrdenství Boží jest blízké pro ty, kdož dobro konají.'),
(1011, 7, 57, 'A On je ten, jenž větry vysílá jako poselství radostné, zvěstující milosrdenství Jeho. Když potom jsou obtíženy mračny těžkými, ženeme je nad krajinu mrtvou a sesíláme z nich vodu, jejíž pomocí dáváme vyrůst všem druhům plodů. A stejně tak dáme vyjíti mrtvým z jejich hrobů - snad si to připomenete!'),
(1012, 7, 58, 'Z dobré země vyrůstají rostliny v hojnosti z dovolení Pána jejího, zatímco ve špatné rostou jen řídce - a takto činíme srozumitelnými znamení Naše pro lid vděčný.'),
(1013, 7, 59, 'A již kdysi jsme vyslali Noema k lidu jeho; i pravil jim: "Lide můj, uctívejte Boha a nemějte žádné božstvo jiné kromě Něho! Já se věru pro vás obávám trestu dne strašného!"'),
(1014, 7, 60, 'Však velmoži z lidu jeho řekli: "My vidíme, žes věru upadl do bludu zjevného."'),
(1015, 7, 61, 'I pravil: "Lide můj, já nejsem v bludu, já posel jsem Pána lidstva veškerého'),
(1016, 7, 62, 'a předávám vám poselství Pána svého, udílím vám radu dobrou a vím od Boha to, co vy nevíte.'),
(1017, 7, 63, 'Jste snad překvapeni, že se vám dostalo připomenutí od Pána vašeho prostřednictvím muže z vás, aby vás varoval a abyste byli bohabojní - snad se nad vámi smiluje.'),
(1018, 7, 64, 'Však oni za lháře jej prohlásili; My pak zachránili jsme jej i ty, kdo spolu s ním na lodi byli, zatímco jsme utopili ty, kdož znamení Naše za lživá měli - a byl to věru lid zaslepený.'),
(1019, 7, 65, 'A vyslali jsme k Ádovcům bratra jejich Húda, který řekl: "Lide můj, uctívejte Boha a nemějte žádné božstvo jiné kromě Něho - což nebudete bohabojní?"'),
(1020, 7, 66, 'Však velmoži, ti, kdož z lidu jeho byli nevěřící, řekli: "My vidíme, žes do šílenství upadl, a my tě za lháře pokládáme!"'),
(1021, 7, 67, 'I pravil: "Lide můj, já nejsem v šílenství, já posel jsem Pána lidstva veškerého'),
(1022, 7, 68, 'a předávám vám poselství Pána svého a jsem upřímným rádcem vaším.'),
(1023, 7, 69, 'Jste snad překvapeni, že se vám dostalo připomenutí od Pána vašeho prostřednictvím muže z vás, aby vás varoval? Vzpomeňte, že On vás nástupci po lidu Noemově ustanovil a v postavách vašich vás mohutnými učinil! Vzpomeňte dobrodiní Božího - snad budete blažení!"'),
(1024, 7, 70, 'I odpověděli: "Zdaž jsi přišel k nám, abychom uctívali Boha jediného a abychom opustili to, co uctívali otcové naši? Předveď nám tedy to, čím nám vyhrožuješ, patříš-li mezi pravdomluvné!"'),
(1025, 7, 71, 'I zvolal: "Již na vás dopadl trest a hněv Pána vašeho! Chcete se mnou přít o jména, kterými jste vy a otcové vaši modly nazvali a ohledně nichž Bůh neseslal žádné oprávnění? Vyčkejte jen a já s vámi budu též čekat!"'),
(1026, 7, 72, 'A zachránili jsme jej i ty, kdož spolu s ním byli, z milosrdenství Svého a zahubili jsme ty, kdož znamení Naše za lživá měli a nevěřící byli!'),
(1027, 7, 73, 'A vyslali jsme k Thamúdovcům bratra jejich Sáliha, který řekl: "Lide můj, uctívejte Boha a nemějte žádného božstva jiného kromě Něho! A nyní již přišel k vám jasný důkaz od Pána vašeho: tato velbloudice Boží je pro vás znamením - nechte ji pásti na zemi Boží, nečiňte jí nic zlého, aby vás nepostihl trest bolestný!'),
(1028, 7, 74, 'Pomněte, že On vás nástupci po Ádovcích učinil a že vás usídlil v zemi, na jejíchž pláních si hrady stavíte a v horách příbytky své si vytesáváte. Pomněte dobrodiní Božího a nešiřte po zemi pohoršení!'),
(1029, 7, 75, 'I řekli velmoži, ti, kdož pyšní byli z lidu jeho, těm, kdo utištění byli, to je těm, kdo z nich uvěřili: "Víte určitě, že Sálih je poslem Pána svého?" Odpověděli: "My věru věříme v to, s čím seslán byl."'),
(1030, 7, 76, 'Řekli ti, kdož pyšní byli: "My však nevěříme v to, v co vy věříte."'),
(1031, 7, 77, 'A ochromili velbloudici a vzepřeli se tak rozkazu Pána svého řkouce: "Sálihu, předveď nyní to, číms nám hrozil, jsi-li jedním z poslů Božích!"'),
(1032, 7, 78, 'A postihlo je zemětřesení a nazítří ráno leželi v domech svých tvářemi k zemi.'),
(1033, 7, 79, 'I odvrátil se od nich Sálih a pravil: "Lide můj, předal jsem vám poselství Pána svého a udělil jsem vám dobrou radu, ale vy zřejmě nemilujete rádce."'),
(1034, 7, 80, 'A hle, pravil Lot lidu svému: "Zdaž budete se oddávat nemravnosti, jíž před vámi se žádný na světě tomto nedopustil?'),
(1035, 7, 81, 'Vždyť vy z vášně své k mužům vcházíte místo k ženám! Vy věru jste lid, jenž všechnu míru překračuje!"'),
(1036, 7, 82, 'A bylo odpovědí národa jeho jedině to, že řekli: "Vyžeňte je z města svého, neboť to jsou lidé, kteří o čistotu usilují!"'),
(1037, 7, 83, 'A zachránili jsme jej i rodinu jeho kromě ženy jeho, jež byla s těmi, kdož se omeškali.'),
(1038, 7, 84, 'A seslali jsme na ně déšť - a pohleď, jaký byl konec provinilců!'),
(1039, 7, 85, 'A vyslali jsme k Madjanovcům bratra jejich Šu´ajba a pravil jim: "Lide můj, uctívejte Boha a nemějte žádného božstva jiného kromě Něho! A již přišel k vám důkaz jasný od Pána vašeho. Dávejte správné míry a váhy, nezkracujte lidi v majetcích jejich a nešiřte pohoršení na zemi poté, co stala se na ní náprava - a věru to bude pro vás lepší, jste-li věřící!'),
(1040, 7, 86, 'A nesedávejte výhružně u stezky každé, snažíce se odstrašit od cesty Boží ty, kdož v Něj uvěřili, a usilujíce o její pokřivení! Pomněte, jak málo vás bylo a jak On vás rozmnožil, a pohleďte, jaký byl konec těch, kdož pohoršení šířili!'),
(1041, 7, 87, 'Jestliže část z vás uvěřila v to, s čím jsem byl poslán, a druhá část nevěří, tedy buďte trpěliví až do chvíle, kdy Bůh mezi námi a jimi rozsoudí, vždyť On nejlepším je soudcem!"'),
(1042, 7, 88, 'I řekli velmoži, ti z lidu jeho, kdož pyšní byli: "Věru tě vyženeme, Šu´ajbe, i ty, kdož s tebou uvěřili, z města našeho - anebo se navrátíš k náboženství našemu!" I řekl: "I když se nám hnusí?'),
(1043, 7, 89, 'Provinili bychom se proti Bohu lží, kdybychom se vrátili k vašemu náboženství poté, co nás Bůh od něho spasil. A nelze se nám k Němu navrátit, leda bude-li Bůh, Pán náš, chtít. A Pán náš objímá věci všechny ve vědění Svém a my na Boha spoléháme. Pane náš, rozhodni mezi námi a lidem naším podle pravdy, vždyť Tys nejlepším z rozhodčích!"'),
(1044, 7, 90, 'I řekli velmoži, ti, kdož z lidu jeho byli nevěřící: "Jestliže budete následovat Šu´ajba, věru budete mezi těmi, kdož ztrátu utrpí!"'),
(1045, 7, 91, 'A postihlo je zemětřesení a nazítří zrána leželi v domech svých tvářemi k zemi'),
(1046, 7, 92, 'právě ti, kdož Šu´ajba za lháře prohlašovali; a zmizeli, jako by ani nikdy nebyli žili - ti, kdož za lháře jej prohlašovali, ti ztrátu utrpěli.'),
(1047, 7, 93, 'I odvrátil se od nich a řekl: "Lide můj, vyřídil jsem vám věru poselství Pána svého a udělil jsem vám dobrou radu - však proč lkát bych měl nad lidem nevěřícím?"'),
(1048, 7, 94, 'A neposlali jsme nikdy do žádného města proroka, aniž bychom předtím byli postihli obyvatele jeho neštěstím a bídou, doufajíce, že snad stanou se pokornými.'),
(1049, 7, 95, 'Pak jsme jim zaměnili zlé dobrým, takže zapomněli a řekli: "Otcové naši také byli postiženi střídavě neštěstím a štěstím." A uchvátili jsme je znenadání, aniž o tom měli tušení.'),
(1050, 7, 96, 'Kdyby byli obyvatelé měst těchto uvěřili a bohabojní byli, byli bychom pro ně zajisté požehnání nebes i země otevřeli. Ale oni to vše za lež prohlašovali a potrestali jsme je za to, co si vysloužili.'),
(1051, 7, 97, 'Jsou si obyvatelé těchto měst jisti tím, že na ně nedopadne přísnost Naše během doby noční, když do spánku jsou pohrouženi?'),
(1052, 7, 98, 'Jsou si obyvatelé těchto měst jisti tím, že na ně nedopadne přísnost Naše během dne jasného, když zábavám se oddávají?'),
(1053, 7, 99, 'A jsou si jisti před lstí Boží? Nikdo si není před lstí Boží jist, leda lid, jenž ztrátě své jde vstříc!'),
(1054, 7, 100, 'Což nebylo ukázáno těm, kdož zdědili zemi po jejich obyvatelích dřívějších, že kdybychom chtěli, postihli bychom je za hříchy jejich? Však zapečetili jsme srdce jejich, takže nic neslyší.'),
(1055, 7, 101, 'A vyprávíme ti příběhy těchto měst: přicházeli k nim poslové jejich s jasnými důkazy, avšak oni nebyli sto uvěřit tomu, co dříve za lež prohlásili. A takto Bůh zapečeťuje srdce nevěřících.'),
(1056, 7, 102, 'A u většiny z nich jsme neshledali, že dodržují úmluvu, nýbrž shledali jsme, že většina z nich jsou hanebníci.'),
(1057, 7, 103, 'A po těchto prorocích jsme posléze vyslali Mojžíše se znameními Našimi k Faraónovi a velmožům jeho, avšak oni byli vůči nim nespravedliví - pohleď, jaký byl konec těch, kdož pohoršení šířili!'),
(1058, 7, 104, 'I pravil Mojžíš: "Faraóne, já věru jsem poslem Pána lidstva veškerého'),
(1059, 7, 105, 'a je spravedlivé; abych o Bohu jen pravdu hovořil. Přišel jsem k vám s důkazem jasným od Pána vašeho, nech tedy odejít se mnou dítka Izraele!"'),
(1060, 7, 106, 'Faraón řekl: ", Jestliže jsi se znamením přišel, pak je předveď, patříš-li mezi pravdomluvné!"'),
(1061, 7, 107, 'I hodil Mojžíš hůl svou na zem - a hle, stala se hadem zjevným.'),
(1062, 7, 108, 'Potom vyňal ruku svou - a hle, zdála se bílá přihlížejícím.'),
(1063, 7, 109, 'I řekli velmožové z lidu Faraónova: "Vskutku člověk tento je kouzelníkem učeným,'),
(1064, 7, 110, 'který vás chce vyhnat ze země vaší, co tedy nařídíte?"'),
(1065, 7, 111, 'I řekli: "Odkaž je na později, jeho i bratra jeho, a pošli do měst shromažďovatele,'),
(1066, 7, 112, 'aby ti přivedli všechny kouzelníky učené!"'),
(1067, 7, 113, 'I přišli kouzelníci k Faraónovi a řekli: "Zajisté že dostane se nám odměny, jestliže budeme vítězi."'),
(1068, 7, 114, 'Odpověděl: "Ano, a stanete se potom těmi, kdož jsou mi přiblíženi."'),
(1069, 7, 115, 'Řekli: "Mojžíši, chceš hodit hůl nejdříve ty, či máme my házet jako první?"'),
(1070, 7, 116, 'Odpověděl: "Házejte vy!" A když hodili, očarovali zraky lidí a naplnili je hrůzou a předvedli kouzlo mocné.'),
(1071, 7, 117, 'A vnukli jsme Mojžíšovi: "Hoď svou hůl!" a hle, ona pohltila vše, co oni vykouzlili.'),
(1072, 7, 118, 'A vyjevila se pravda a ukázalo se nicotným vše, co oni dělali.'),
(1073, 7, 119, 'A byli tam přemoženi a odvrátili se poníženi.'),
(1074, 7, 120, 'A vrhli se kouzelníci na zem, na tvář padajíce'),
(1075, 7, 121, 'a prohlásili: "Uvěřili jsme v Pána lidstva veškerého,'),
(1076, 7, 122, 'Pána Mojžíšova a Árónova!"'),
(1077, 7, 123, 'I zvolal Faraón: "Jak to, že jste v Něj uvěřili, dříve než jsem vám to dovolil? Věru toto je lest, kterou jste si v tomto městě vymyslili, abyste z něho obyvatele jeho vyhnali, však záhy poznáte!'),
(1078, 7, 124, 'Dám vám useknout vaše pravé ruce a vaše levé nohy a potom vás všechny nechám ukřižovat!"'),
(1079, 7, 125, 'Odpověděli:;, My věru k Pánu svému se obrátíme,'),
(1080, 7, 126, 'vždyť ty se nám mstíš jen za to, že uvěřili jsme ve znamení Pána svého, když nám byla předvedena. Pane náš, propůjči nám trpělivost a dej nám zemřít jako těm, kdož do vůle Tvé jsou odevzdáni!"'),
(1081, 7, 127, 'I řekli velmoži z lidu Faraónova: "Zdaž necháš Mojžíše a lid jeho, aby šířili na zemi pohoršení a aby opustili tebe i božstva tvá?" Odpověděl: "Pobijeme syny jejich a ponecháme naživu jen ženy jejich a přemůžeme je vítězně!"'),
(1082, 7, 128, 'I pravil Mojžíš lidu svému: "Proste Boha o pomoc a buďte trpěliví, vždyť země Bohu náleží a On v dědictví ji dává tomu, komu chce ze služebníků Svých. A šťastný konec náleží bohabojným!"'),
(1083, 7, 129, 'Odpověděli: "Byli jsme utištěni předtím, než jsi k nám přišel, i po tom, cos k nám přišel." Řekl: "Možná že Pán váš zahubí nepřátele vaše a učiní vás nástupci jejich na zemi, aby viděl, jak si budete počínat."'),
(1084, 7, 130, 'A již dříve jsme postihli rod Faraónův léty sucha a nedostatku plodin - snad se vzpamatují!'),
(1085, 7, 131, 'Když však k nim přišlo dobré, tu říkali: "Toto nám patří!" A když je postihlo zlé, tu připisovali to osudnému vlivu Mojžíšovu a těch, kdož s ním byli. Osud jejich však v rukou Božích byl, leč většina z nich to nevěděla.'),
(1086, 7, 132, 'I řekli: "I kdybys nám předvedl jakékoliv znamení, abys nás jím očaroval, nikdy ti neuvěříme!"'),
(1087, 7, 133, 'A seslali jsme na ně zátopu, kobylky, vši, žáby i krev jako znamení srozumitelná, ale oni v pýše své setrvali a lidem provinilým se stali.'),
(1088, 7, 134, 'A kdykoliv je postihly tyto rány, říkali: "Mojžíši, pros za nás Pána svého podle úmluvy Jeho s tebou! Jestliže nás vskutku zbavíš těchto ran, věru ti uvěříme a věru s tebou odešleme dítka Izraele!"'),
(1089, 7, 135, 'Avšak když jsme od nich vzdálili tyto rány až do lhůty, jíž měli dosíci, hle, oni porušili slovo své.'),
(1090, 7, 136, 'A pomstili jsme se jim a utopili jsme je v moři, neboť oni znamení Naše za lživá pokládali a lhostejní vůči nim byli.'),
(1091, 7, 137, 'A dali jsme lidu, který utiskovali, v dědictví východní i západní části země, jíž jsme požehnali. A tak se splnilo překrásné slovo Pána tvého vůči dítkám Izraele, neboť věru trpěliví byli. A zničili jsme dílo Faraóna a lidu jeho i vše, co zbudovali.'),
(1092, 7, 138, 'A převedli jsme přes moře dítka Izraele; i přišli k lidem, kteří horlivě uctívali modly své. I řekli: "Mojžíši, udělej nám božstvo podobné božstvům, která mají tito!" Odpověděl: "Vy věru jste lidé pošetilí!'),
(1093, 7, 139, 'To, v čem tihle jsou, je ke zkáze odsouzeno a nicotným se stane vše, co dělali."'),
(1094, 7, 140, 'A pravil dále: "Zdaž mám pro vás hledat nějaké božstvo jiné, než je Bůh, který vám dal přednost před lidstvem veškerým?"'),
(1095, 7, 141, 'A věru jsme vás zachránili před rodem Faraónovým, který vám působil kruté mučení, syny vaše zabíjel a jen ženy vaše naživu nechával. A v tom zajisté byla zkouška velká od Pána vašeho.'),
(1096, 7, 142, 'A uzavřeli jsme úmluvu s Mojžíšem během třiceti nocí, k nimž jsme přidali dalších deset, takže doba setkání s Pánem jeho trvala čtyřicet nocí. A řekl předtím Mojžíš bratru svému Árónovi: "Zastupuj mne u lidu mého, dbej o nápravu a nenásleduj cestu těch, kdož šíří pohoršení!"'),
(1097, 7, 143, 'A když přišel Mojžíš na schůzku Námi stanovenou a když s ním promluvil Pán jeho, zvolal:;, Pane můj, ukaž se mi, abych Tě mohl spatřit!" I odpověděl: "Nikdy Mne nespatříš, ale pohleď k této hoře: jestliže zůstane nehybná na místě svém, tedy Mne uvidíš!" A když pak se Pán jeho zjevil nahoře, obrátil ji v prach a Mojžíš klesl jak bleskem zasažen. A když se vzpamatoval, zvolal: "Sláva Tobě, kajícně jsem se k Tobě obrátil a nyní jsem první z věřících!"'),
(1098, 7, 144, 'I pravil Pán: "Mojžíši, vyvolil jsem tě pro Své poselství i pro slovo Své mezi lidmi všemi. Vezmi to, co ti dávám, a buď jedním z vděčných!"'),
(1099, 7, 145, 'A napsali jsme pro něj na desky připomenutí o věcech všech i srozumitelný výklad všeho toho a řekli jsme: "Přidržuj se jich pevně a poruč lidu svému, aby si vzal z nich to nejlepší, jinak ukáži vám příbytek lidí hanebných!'),
(1100, 7, 146, 'Od znamení Svých odvrátím ty, kdož na zemi neprávem pyšně si vykračují. Spatří-li jakékoliv znamení, neuvěří v ně, a spatří-li cestu správně vedoucí, nedají se po ní; však jestliže spatří cestu bludnou, půjdou po ní! A to za to, že znamení Naše za lživá pokládali a lhostejni k nim byli.'),
(1101, 7, 147, 'Marné jsou skutky těch, kdož prohlašovali znamení Naše i setkání ve světě budoucím za lež! Zdaž dostane se jim odměny za něco jiného než za to, co konali?"'),
(1102, 7, 148, 'Po odchodu Mojžíšově pak lid jeho si vyrobil z ozdob svých tele, jehož tělo vydávalo bučení. Cožpak neviděli, že ono k nim nemluví ani je po správné cestě nevede? A učinili je modlou svou a stali se tak nespravedlivými.'),
(1103, 7, 149, 'A když si tak vlastníma rukama zasadili ránu a spatřili, že zbloudili, zvolali: "Věru, jestliže se Pán náš nad námi nesmiluje a neodpustí nám, budeme patřit k těm, kdož ztrátu utrpěli!"'),
(1104, 7, 150, 'Když se pak Mojžíš vrátil k lidu svému, rozhněván i zarmoucen, zvolal: "Jak hnusné je to, co jste provedli, když jsem odešel! Což chcete uspíšit příchod rozkazu Pána svého?" A mrštil deskami o zem, a chytil hlavu bratra svého, přitáhl jej k sobě. I zvolal Árón: "Synu mé matky! Lidé mne utlačovali a div mne nezabili. Nečiň mi nic, nad čím by se radovali nepřátelé moji, a nesměšuj mne s lidem nespravedlivým!"'),
(1105, 7, 151, 'I řekl Mojžíš: "Pane můj, odpusť mně i bratru mému a uveď nás do milosrdenství Svého - vždyť Tys nejslitovnější ze slitovníků!"'),
(1106, 7, 152, 'Ti, kdož si tele modlou učinili, budou postiženi hněvem Pána svého i ponížením v životě pozemském! A takto odměňujeme ty, kdož lži si vymýšlejí.'),
(1107, 7, 153, 'Ti pak, kdož špatné činy konali, potom se však kajícně obrátili a uvěřili - věru Pán tvůj je pak odpouštějící, slitovný.'),
(1108, 7, 154, 'A když se uklidnil hněv Mojžíšův, uchopil znovu desky a v textu jejich bylo správné vedení i milosrdenství pro ty, kdož Pána svého se obávají.'),
(1109, 7, 155, 'A vybral Mojžíš mezi lidem svým sedmdesát mužů pro schůzku Námi stanovenou. A když je postihlo zemětřesení, zvolal: "Pane můj, kdybys byl chtěl, byl bys je zahubil již dříve a mne s nimi. Hodláš nás zahubit kvůli tomu, co učinili hlupáci mezi námi? Vždyť je to jen Tvé pokušení, jímž dáváš zbloudit, komu chceš, a jímž také vedeš toho, koho chceš! Tys ochráncem naším, odpusť nám a slituj se nad námi, vždyť Tys nejlepší z odpouštějících!'),
(1110, 7, 156, 'A zapiš pro nás na tomto i na onom světě jen dobré, vždyť my jsme židovské víry kvůli Tobě!" I odpověděl Pán: "Trest Můj postihne toho, koho Já chci, ale milosrdenství Mé objímá všechny věci; Já zapíši dobré těm, kdož bohabojní jsou a dávají almužnu, a těm, kdož uvěřili ve znamení Naše,'),
(1111, 7, 157, 'a těm, kdož následují posla, proroka neučeného, jehož naleznou oznámeného jim v Tóře a Evangeliu a který jim přikazuje vhodné a zakazuje zavrženíhodné, který jim dovoluje pokrmy výtečné a zakazuje jim škodlivé a snímá z nich tíživé povinnosti a pouta, jež je tíží. A věru ti, kdož v něj uvěří a podpoří jej a budou následovat světlo, jež bylo s ním sesláno, ti budou blažení!"'),
(1112, 7, 158, 'Rci: "Lidé, já věru jsem poslem Božím k vám všem vyslaným od toho, jemuž náleží království na nebesích i na zemi. Není božstva kromě Něho a On je ten, jenž život i smrt dává. Věřte tedy v Boha a posla Jeho, proroka neučeného, jenž věří v Boha a Jeho slova, a následujte jej - snad se octnete na cestě správné!"'),
(1113, 7, 159, 'A mezi lidem Mojžíšovým jest obec, jež pravdou se řídí a podle ní spravedlivě jedná.'),
(1114, 7, 160, 'A rozdělili jsme je na dvanáct kmenů - obcí. A vnukli jsme Mojžíšovi, když lid jeho jej žádal o napojení: "Udeř holí do skály!" A vytrysklo ze skály dvanáct pramenů a všichni lidé věděli, kde mají pít. A zastínili jsme je mračnem a seslali jsme z něho manu a křepelky. "Jezte z pokrmů výtečných, jež jsme vám uštědřili!" Ale oni neukřivdili nám, nýbrž ukřivdili jen sami sobě!'),
(1115, 7, 161, 'A hle, bylo jim řečeno: "Obývejte toto město a jezte z plodů jeho, kdekoliv chcete, a říkejte:, Odpuštění!? a vstupujte do brány sklánějíce se! Tehdy vám odpustíme vaše hříchy a rozmnožíme těm, kdož dobré konají."'),
(1116, 7, 162, 'A zaměnili ti z nich, kdož nespravedliví byli, slovem jiným to, které bylo jim řečeno, a seslali jsme na ně trest z nebe za to, že byli nespravedliví.'),
(1117, 7, 163, 'A zeptej se jich na město, jež poblíže moře položeno bylo, a jak to bylo, když znesvěcovali obyvatelé jeho den sobotní! Přicházely k nim ryby v den jejich soboty a vyplouvaly nahoru, zatímco v den, kdy sobotu nesvětili, k nim nepřicházely. A takto jsme je v pokušení uváděli za to, že byli nespravedliví.'),
(1118, 7, 164, 'A hle, řekla jedna skupina z nich: "Proč napomínáte lid, který Bůh beztak zahubí anebo jej potrestá trestem krutým?" Odpověděli: "Abychom měli omluvu před Pánem svým, a doufajíce, že snad se stanou bohabojnými!"'),
(1119, 7, 165, 'Když pak zapomněli to, co jim bylo připomenuto, zachránili jsme ty, kdož zabraňovali zlému, a uložili jsme těm, kdož nespravedliví byli, trest krutý za to, že hříšníky byli.'),
(1120, 7, 166, 'A když přestoupili to, co jim bylo přikázáno, pravili jsme jim: "Buďte opicemi odpornými!"'),
(1121, 7, 167, 'A prohlásil tehdy Pán tvůj, že proti nim vyšle ty, kdož utiskovat je budou mukami nejhoršími až do dne zmrtvýchvstání. A Pán tvůj je věru rychlý v trestání a věru On je i odpouštějící, slitovný.'),
(1122, 7, 168, 'A rozdělili jsme je po zemi jako národy; někteří z nich jsou bezúhonní, zatímco jiní z nich nejsou takoví. A vystavili jsme je zkouškám dobrým i zlým doufajíce, že snad se obrátí!'),
(1123, 7, 169, 'A přišli po nich nástupci, kteří zdědili Písmo, avšak chápou se toho, co nabízí jim tento svět, a říkají: "Bude nám odpuštěno!" A když je jim nabízeno ještě více tomu podobného, berou si to. Což však jimi nebyla přijata úmluva Písma, že budou o Bohu jen pravdu hovořit? A přitom studovali to, co v Písmu je obsaženo! Však příbytek poslední bude lepší pro ty, kdož jsou bohabojní - což to vůbec nepochopí? -'),
(1124, 7, 170, 'a pro ty, kdož Písma se přidržují a modlitby dodržují; a My nedopustíme, aby ztratila se odměna těch, kdož o nápravu usilují.'),
(1125, 7, 171, 'A hle, když otřesem zvedli jsme nad nimi horu Sinaj, podobnou střeše stinné, domnívali se, že na ně spadne. I řekli jsme: "Přidržujte se pevně toho, co jsme vám dali, a pomněte toho, co je tam obsaženo - snad budete bohabojní."'),
(1126, 7, 172, 'A hle, Pán tvůj vzal potomstvo všechno ze synů Adamových, jež vzešlo z ledví jejich, a dal jim svědčit o nich samých a řekl: "Což nejsem Já Pánem vaším?" I odpověděli: "Ano, dosvědčujeme to!" A to proto, abyste neřekli v den zmrtvýchvstání, že jsme k tomu byli lhostejní,'),
(1127, 7, 173, 'či abyste neřekli: "Otcové naši věru již před námi k Bohu přidružovali a my jsme jen potomky jejich pozdějšími. Což nás chceš zahubit za to, co dělali ti, kdož nicotného se přidržovali?"'),
(1128, 7, 174, 'A takto My srozumitelně vykládáme znamení Svá - snad se oni obrátí?'),
(1129, 7, 175, 'Vyprávěj jim zvěst o tom, jemuž jsme znamení Svá dali a jenž se pak od nich odvrátil. A satan jej učinil následovníkem Svým a stal se tak jedním ze zbloudilých.'),
(1130, 7, 176, 'Kdybychom byli chtěli, byli bychom jej pozdvihli znamením naším, avšak on přikláněl se k zemi a šel za svými vášněmi a podobal se psu: když přiblížíš se k němu, vyplazuje jazyk, a když necháš ho na pokoji, také vyplazuje jazyk. A podobně je tomu s lidmi, kteří znamení Naše za lež prohlašují. Vyprávěj jim tedy tyto příběhy, snad o tom budou přemýšlet.'),
(1131, 7, 177, 'Jak hnusný je obraz lidí, kteří znamení Naše za lež prohlásili - sami sobě tím ukřivdili.'),
(1132, 7, 178, 'Ten, jehož Bůh vede, je dobře veden, avšak ti, které nechává bloudit, ti ztrátu utrpěli.'),
(1133, 7, 179, 'A stvořili jsme věru pro peklo množství džinů i lidí, kteří mají srdce, jimiž nic nechápou, a mají oči, jimiž nic nevidí, a mají uši, jimiž nic neslyší. Podobají se dobytku, ba jsou ještě zbloudilejší - a to jsou ti, kdož jsou lhostejní!'),
(1134, 7, 180, 'Bohu náleží jména nejkrásnější; vzývejte Jej tedy jimi a nechte být ty, kdož jména Jeho překrucují - ti budou odměněni za to, co činili.'),
(1135, 7, 181, 'Mezi těmi, které jsme stvořili, je národ jeden, jenž správnou cestou podle pravdy je veden a podle ní spravedlivě jedná.'),
(1136, 7, 182, 'Ty, kteří znamení Naše za lživá prohlašují, ty přilákáme postupně do záhuby, aniž o tom budou mít tušení.'),
(1137, 7, 183, 'Já jim však odklad dám, vždyť lest Má je spolehlivá.'),
(1138, 7, 184, 'Což vskutku nepomyslí, že druh jejich není vůbec džiny posedlý, nýbrž že je pouze varovatelem zjevným?'),
(1139, 7, 185, 'Což neuvažovali o království nebes a země a o tom, co Bůh stvořil z věcí, a o tom, že lhůta jejich se již možná přiblížila? Jakému příběhu tedy ještě po tomto Koránu uvěří?'),
(1140, 7, 186, 'Komu Bůh dá zbloudit, ten nenalezne vůdce žádného a On zanechá je v svévoli jejich slepě tápat.'),
(1141, 7, 187, 'Budou se tě dotazovat na Hodinu a na příchod její. Rci: "Vědomost o tom je pouze u Pána mého a jedině On odkryje ji v čas její. Těžká je na nebesích a na zemi a přijde k vám leč znenadání." A budou se tě dotazovat, jako bys byl o ní zpraven. Rci: "Jedině u Boha je o ní vědění, avšak většina lidí to neví."'),
(1142, 7, 188, 'Rci: "Nemohu si sám ani prospět, ani uškodit, leda bude-li Bůh chtít. Kdybych znal nepoznatelné, mohl bych věru rozmnožit dobro své a nedotklo by se mne zlo žádné; já však jsem pouze varovatelem a zvěstovatelem pro lid, jenž věří."'),
(1143, 7, 189, 'A On je ten, jenž vás stvořil z bytosti jediné a z ní utvořil i manželku její, aby přebývala u ní. A když u ní pobyl, otěhotněla nejdříve lehkým těhotenstvím a snadno s ním chodila; když pak se cítila těžkou, oba volali k Bohu, Pánu jich obou: "Dáš-li nám syna bezúhonného, vskutku budeme patřit mezi vděčné!"'),
(1144, 7, 190, 'Avšak když jim daroval syna bezúhonného, oni Mu božstva přidružená přidali za to, co jim daroval. Bůh však daleko je vznešenější než to, co k Němu je přidružováno.'),
(1145, 7, 191, 'Což k Němu budou přidružovat to, co vůbec nic nestvořilo, ale samo bylo stvořeno,'),
(1146, 7, 192, 'a není schopno jim nikterak pomoci a nemůže pomoci ani sobě?'),
(1147, 7, 193, 'Jestliže je vyzvete ke správné cestě, nebudou vás následovat; a jest pro vás lhostejné, zda je k tomu vyzýváte, či zda mlčíte.'),
(1148, 7, 194, 'Věru ti, které vzýváte vedle Boha, jsou služebníky stejnými jako vy. Vzývejte si je tedy a nechť vám odpoví, jste-li pravdomluvní!'),
(1149, 7, 195, 'Mají snad nohy, na nichž kráčí, či mají snad ruce, jimiž bojují, anebo oči, jimiž se dívají, či uši, jimiž naslouchají? Rci: "Jen si vzývejte ty, jež k Bohu přidružujete, a pak strojte proti mně úklady a nenechte mne dlouho čekat!'),
(1150, 7, 196, 'Vždyť přítelem mým je věru Bůh, jenž Písmo seslal - a On bezúhonných je přítelem,'),
(1151, 7, 197, 'zatímco ti, které vedle Něho vzýváte, nejsou schopni pomoci ani vám, ani sobě samým."'),
(1152, 7, 198, 'A když je vyzýváš k cestě správné, tu neslyší. Myslel bys, že se na tebe dívají, avšak oni nevidí.'),
(1153, 7, 199, 'Buď shovívavý, přikazuj vhodné a vyhýbej se pošetilým!'),
(1154, 7, 200, 'A kdykoliv tě bude znepokojovat návod satanův, hledej útočiště u Boha, vždyť On slyšící je i vševědoucí.'),
(1155, 7, 201, 'Když těch, kdož bohabojní jsou, se dotkne přelud satanův, tu připomenou si Boha, a hle, jsou opět jasně vidoucí,'),
(1156, 7, 202, 'zatímco bratři jejich jim prodlužují jejich přebývání v bloudění a pak nejsou schopni se z něho dostat.'),
(1157, 7, 203, 'Když k nim nepřijdeš s nějakým znamením, tu volají: "Proč sis ještě nějaké nevybral?" Odpověz: "Já následuji jedině to, co bylo mi Pánem mým vnuknuto. Toto jsou zřetelné důkazy od Pána vašeho, správné vedení i milosrdenství pro lidi, kteří věří."'),
(1158, 7, 204, 'A když je přednášen Korán, naslouchejte mu a zmlkněte, snad se vám dostane smilování.'),
(1159, 7, 205, 'A vzpomínej na Pána svého v duši své pokorně a bázlivě a polohlasitě za jitra i za večera a nebuď jedním z těch, kdož jsou lhostejní.'),
(1160, 7, 206, 'Vždyť i ti, kdož blízko jsou u Pána tvého, se necítí povzneseni nad to, aby Jej uctívali a slavili a před ním na tváře své padali.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Dotazují se tě na kořist. Rci: "Kořist náleží Bohu a poslu. Bojte se Boha, dohodněte se mezi sebou a poslouchejte Boha a posla Jeho, jste-li věřící!"'),
(1162, 8, 2, 'Věřícími jsou věru ti, jichž srdce se zachvívají, když Boha je vzpomínáno, a jichž víra se znásobí, když přednášena jsou jim Jeho znamení, a na Pána svého spoléhají,'),
(1163, 8, 3, 'modlitbu dodržují a z toho, co jsme jim uštědřili, rozdávají.'),
(1164, 8, 4, 'Toto jsou praví věřící, pro ně jsou připraveny hodnosti vysoké u Pána jejich i odpuštění i příděl štědrý.'),
(1165, 8, 5, 'Vzpomeň, jak tě Pán tvůj vyvedl z domu tvého s pravdou, zatímco některým věřícím se to nelíbilo,'),
(1166, 8, 6, 'takže se s tebou hádali kvůli pravdě poté, co jim byla ukázána, tak jako by byli vlečeni na smrt, sami se na to dívajíce.'),
(1167, 8, 7, 'Tehdy vám Bůh slíbil, že jeden ze dvou oddílů bude patřit vám: a vy jste si přáli, aby na vás připadl oddíl jiný než silou vládnoucí. Bůh chtěl potvrdit pravdu slovy Svými a vyhubit do posledního nevěřící,'),
(1168, 8, 8, 'aby dokázal pravdivost pravdy a zničil falešnost, i když se to nelíbilo provinilým.'),
(1169, 8, 9, 'A hle, obrátili jste se o pomoc k Pánu Svému a On vyslyšel vás řka: "Podpořím vás věru tisícem andělů, jdoucích jeden za druhým!"'),
(1170, 8, 10, 'A učinil to Bůh jen jako zvěst radostnou pro vás, aby se uklidnila srdce vaše. A vítězná pomoc jedině od Boha přichází - a Bůh věru je všemohoucí, moudrý.'),
(1171, 8, 11, 'A hle, zahalil vás dřímotou na znamení bezpečí od Něho a seslal vám s nebe vodu, aby vás jí očistil a odňal od vás špínu satanovu a aby posílil srdce vaše a upevnil paty vaše.'),
(1172, 8, 12, 'A hle, Pán tvůj vnukl andělům: "Jsem s vámi, podpořte tedy ty, kdož uvěřili! Já vrhnu do srdcí těch, kdož nevěří, hrůzu a vy bijte je po šíjích a bijte je po všech prstech!"'),
(1173, 8, 13, 'A toto je za to, že vzpouzeli se Bohu a poslu Jeho. A ten, kdo se vzpouzí Bohu a poslu Jeho... a Bůh je strašný ve svém trestání.'),
(1174, 8, 14, 'Toto je trest váš, ochutnejte ho a vězte, že pro nevěřící přichystán je trest ohně pekelného.'),
(1175, 8, 15, 'Vy, kteří věříte! Když se na pochodu střetnete s nevěřícími, neobracejte se k nim zády!'),
(1176, 8, 16, 'A kdokoliv se k nim v ten den obrátí zády s výjimkou toho, kdo obrátí se k jinému boji nebo se vzdálí, aby se připojil k jinému oddílu, ten uvalí na sebe hněv Boží a útulkem jeho se stane peklo - a jak hnusný je to cíl konečný!'),
(1177, 8, 17, 'Nejste to vy, kdo je zabili, ale Bůh je zabil; a nevrhals ty, když jsi vrhal, nýbrž byl to Bůh, kdo vrhal, aby vyzkoušel věřící zkouškou dobrou, od Něho přicházející, vždyť Bůh slyšící je, vševědoucí!'),
(1178, 8, 18, 'Toto je pro vás! A Bůh věru zmaří každou lest nevěřících!'),
(1179, 8, 19, 'Jestliže jste si přáli rozhodnutí, tedy vám již rozhodnutí přišlo. Jestliže však přestanete, bude to pro vás lepší; začnete-li však znovu, pak i my začneme znovu, a nebudou vám nic platné vaše sbory, ať jakkoliv jsou početné, vždyť Bůh je s věřícími!'),
(1180, 8, 20, 'Vy, kteří věříte! Poslouchejte Boha a posla Jeho a neodvracejte se od něho, zatímco mu nasloucháte!'),
(1181, 8, 21, 'A nebuďte jako ti, kteří říkali "Slyšeli jsme", ale zatím vůbec neposlouchali.'),
(1182, 8, 22, 'Nejhoršími tvary před Bohem jsou věru hluší a němí, kteří neuvažují rozumně.'),
(1183, 8, 23, 'Kdyby v nich byl Bůh rozpoznal něco dobrého, věru by jim byl dal slyšet, ale i kdyby jim byl dal slyšet, byli by se zády otočili a odvrátili se.'),
(1184, 8, 24, 'Vy, kteří věříte! Odpovídejte na výzvu Boha a posla Jeho, když ten vás volá k tomu, co život vám dává. A vězte, že Bůh stojí mezi člověkem a srdcem jeho a že k Němu budete shromážděni!'),
(1185, 8, 25, 'A bojte se pokušení, jež nepostihuje výlučně jen ty, kdož z vás jsou nespravedliví! A vězte, že Bůh je strašný ve svém trestání!'),
(1186, 8, 26, 'Vzpomeňte si, jak bylo vás málo a byli jste poníženými v zemi a báli jste se, že vás lidé odvlekou násilím! Tehdy vám dal útočiště a podpořil vás pomocí Svou a uštědřil vám pokrmy výtečné - snad budete vděční!'),
(1187, 8, 27, 'Vy, kteří věříte! Nezrazujte Boha ani posla, vždyť byste zradili vědomě důvěru, která je vám dána.'),
(1188, 8, 28, 'Vězte, že majetky vaše i děti vaše jsou jen pokušením, zatímco u Boha je odměna nesmírná.'),
(1189, 8, 29, 'Vy, kteří věříte! Budete-li bohabojní, On dá vám spásné rozlišení a promine vám vaše špatné činy a odpustí vám; vždyť Bůh je pln laskavosti nesmírné.'),
(1190, 8, 30, 'A hle, ti, kdož neuvěřili; strojili proti tobě úklady, aby tě zadrželi anebo zabili či vyhnali. A chystali úklady, avšak i Bůh chystal úklady - a Bůh je nejlepší v strojení úkladů.'),
(1191, 8, 31, 'A když byla jim přednášená znamení Naše, řekli: "Tahle už jsme slyšeli, a kdybychom chtěli, mohli bychom říkat podobné věci, vždyť to není nic jiného než povídačky starých!"'),
(1192, 8, 32, 'A řekli: "Bože veliký, jestliže toto je ta pravda, jež od Tebe přichází, pak na nás sešli déšť kamení z nebe nebo nás postihni hned trestem bolestným!"'),
(1193, 8, 33, 'Bůh však není takový, aby je ztrestal, když jsi mezi nimi - ani nechce být Bůh vykonavatelem jich potrestání, dokud budou prosit o slitování.'),
(1194, 8, 34, 'Proč však by je Bůh nepotrestal nyní, když odhánějí věřící od Mešity posvátné, ač nejsou ochránci jejími? Ochránci jejími jsou pouze bohabojní; avšak většina z nevěřících to neví.'),
(1195, 8, 35, 'A modlitba nevěřících u chrámu sestává pouze z pískání a tleskání dlaněmi. Ochutnejte tedy trest za to, že byli jste nevěřící!'),
(1196, 8, 36, 'Ti, kdož nevěří, rozdávají své jmění, aby odvraceli jiné z cesty Boží; a až je úplně rozdají, zbude jim jen zármutek a potom budou poraženi. A ti, kdož neuvěřili, budou v pekle shromážděni,'),
(1197, 8, 37, 'aby Bůh mohl oddělit špatného od dobrého, aby mohl postavit jednoho zlého proti druhému, aby je všechny shromáždil a ujistil v pekle - a to budou ti, kdož ztrátu utrpí.'),
(1198, 8, 38, 'A těm, kdož jsou nevěřící, řekni, že přestanou-li, bude jim odpuštěno to, co se již stalo; jestliže však znovu začnou, pak naplní se obvyklý osud předešlých.'),
(1199, 8, 39, 'Bojujte tedy proti nim, aby už nebylo pokušení k odpadlictví a aby všechno náboženství bylo jen Boží. Jestliže však přestanou, pak Bůh jasně vidí vše, co dělají.'),
(1200, 8, 40, 'A jestliže se obrátí zády, vězte, že Bůh je vaším ochráncem; jak výtečný to ochránce a jak výtečný to pomocník!'),
(1201, 8, 41, 'A vězte, že zmocníte-li se jakékoliv kořisti, náleží z ní jedna pětina Bohu a poslu, příbuzným, sirotkům, nuzným a po cestě Boží jdoucímu, věříte-li v Boha a v to, co jsme seslali služebníku Svému v den spásy, v den, kdy střetla se obě vojska. A Bůh je mocný nade všem věcmi.'),
(1202, 8, 42, 'A hle, byli jste na bližším svahu a oni byli na vzdálenějším svahu a jezdci byli níže pod vámi; a kdybyste si byli umluvili schůzku, věru byste se byli hádali o místo a čas této schůzky, avšak vše se událo tak, aby Bůh rozhodl věc, jež měla být splněna, a aby zahynul ten, kdo zahynul, v souladu s jasným důkazem, a aby zůstal naživu ten, kdo zůstal naživu, v souladu s jasným důkazem. A Bůh je věru slyšící, vševědoucí!'),
(1203, 8, 43, 'A hle, Bůh ti je ukázal ve snu tvém v menším počtu, neboť kdyby ti je byl ukázal početnější, byli byste ztratili odvahu a byli byste se hádali o této věci, avšak Bůh vás uklidnil, neboť On dobře zná, co hrudi vaše skrývají.'),
(1204, 8, 44, 'A hle, On ukázal vám je ve chvíli střetnutí tak, že jevili se zrakům vašim méně početnými, a zmenšil i počet váš ve zracích jejich, aby Bůh mohl rozhodnout věc, jež měla být splněna. A k Bohu se navrátí všechny věci.'),
(1205, 8, 45, 'Vy, kteří věříte! Kdykoliv střetnete se s nějakým oddílem, vytrvejte pevně a vzývejte často Boha - snad dosáhnete úspěchu!'),
(1206, 8, 46, 'Poslouchejte Boha a posla Jeho a nehádejte se, abyste neztratili odvahu a neodvrátil se od vás vítr příznivý! Buďte trpěliví, neboť Bůh stojí na straně trpělivých!'),
(1207, 8, 47, 'Nebuďte jako ti, kdož vyrazili ze svých obydlí domýšlivě a stavějíce se na odiv lidem, aby odvraceli jiné od cesty Boží - však Bůh objímá ve vědění svém vše, co dělají.'),
(1208, 8, 48, 'A hle, satan jim zkrášlil skutky jejich a řekl: "Dnes není mezi lidmi nikoho, kdo by vás porazil, a já jsem spojencem vaším!" Když však obě vojska se octla na dohled, tu dal se na ústup a zvolal: "Já nejsem za vás odpovědný, neboť já věru vidím to, co vy nevidíte. Bojím se Boha, neboť Bůh strašný je ve svém trestání!"'),
(1209, 8, 49, 'A hle, řekli pokrytci a ti, jichž srdce jsou nemocná: "Tyto lidi oklamalo náboženství jejich!" Ale ten, kdo se spoléhá na Boha - vždyť Bůh mocný je i moudrý.'),
(1210, 8, 50, 'Kéž bys mohl vidět, až andělé odvolají ty, kdož byli nevěřící, a budou je bít po tvářích a po zádech volajíce: "Okuste nyní trestu spalujícího'),
(1211, 8, 51, 'za to, co ruce vaše dříve prováděly! Bůh však neukřivdí služebníkům Svým."'),
(1212, 8, 52, 'Osud jejich bude jako osud rodu Faraónova a těch, kdož byli před nimi: neuvěřili ve znamení Boží a Bůh je uchvátil za hříchy jejich, vždyť Bůh je silný a strašný ve svém trestání.'),
(1213, 8, 53, 'A to je proto, že Bůh není takový, aby měnil Své dobrodiní, jímž omilostnil lid nějaký, pokud oni nezmění to, co je v duších jejich - a Bůh věru je slyšící, vševědoucí.'),
(1214, 8, 54, 'A dopadne to s nimi jako s rodem Faraónovým a těch, kdož byli před nimi: znamení Pána svého za lež prohlásili a My jsme je zahubili; a utopili jsme rod Faraónův, neboť všichni byli nespravedliví.'),
(1215, 8, 55, 'Nejhoršími tvory před Bohem jsou ti, kdo byli zatvrzele nevěřící a stále ještě nevěří,'),
(1216, 8, 56, 'a ti, s nimiž jsi uzavřel smlouvu a kteří potom pokaždé tuto smlouvu porušili nejsouce bohabojní.'),
(1217, 8, 57, 'Jestliže se s nimi střetneš ve válce, zastraš pak jimi ty, kdož přijdou po nich - snad se vzpamatují!'),
(1218, 8, 58, 'Obáváš-li se zrady od lidí, hoď jim nazpátek úmluvu stejným způsobem, neboť Bůh věru nemiluje zrádce.'),
(1219, 8, 59, 'Nechť se nedomnívají ti, kdož nevěří, že nás předhoní - nepodaří se jim uniknout našemu zásahu!'),
(1220, 8, 60, 'Připravte tedy proti nim všechno, co schopni jste postavit ze síly a oddílů jezdeckých, abyste zastrašili nepřátele Boží i nepřátele své a jiné mimo ně, které ještě neznáte, ale které Bůh dobře zná. A cokoliv vydáte na cestě Boží, bude vám plně nahrazeno a nebude vám ukřivděno.'),
(1221, 8, 61, 'A budou-li ochotni k míru, buď k němu ochoten i ty a spoléhej na Boha, vždyť On slyšící je i vševědoucí.'),
(1222, 8, 62, 'Chtějí-li tě však oklamat, tedy Bůh ti zajisté postačí - On, jenž tě podpořil vítěznou pomocí Svou i pomocí věřících'),
(1223, 8, 63, 'a sjednotil srdce jejich. A i kdybys vydal vše, co je na zemi, nesjednotil bys srdce jejich, avšak Bůh je sjednotil, vždyť On mocný je i moudrý.'),
(1224, 8, 64, 'Proroku, nechť ti stačí Bůh a ti, kdož následují tě z věřících!'),
(1225, 8, 65, 'Proroku, povzbuzuj věřící k boji! Bude-li mezi vámi dvacet vytrvalých mužů, porazí dvě stě, bude-li jich mezi vámi sto, porazí tisíc nevěřících, neboť to jsou lidé nechápaví.'),
(1226, 8, 66, 'Nyní vám Bůh ulehčil, neboť dobře ví, že mezi vámi je slabost. Bude-li mezi vámi sto vytrvalých, porazí dvě stě, a bude-li jich mezi vámi tisíc, porazí dva tisíce z dovolení Božího, vždyť Bůh je na straně vytrvalých.'),
(1227, 8, 67, 'Nebylo dáno proroku žádnému, aby měl zajatce, pokud dokonale neporazil nepřátele na zemi. Vy toužíte po tom, co nabízí vám život pozemský, zatímco Bůh vám chce dát život budoucí - a Bůh mocný je i moudrý.'),
(1228, 8, 68, 'A kdyby nebylo příkazu již dříve od Boha daného, byl by se vás dotkl trest nesmírný za to, co jste si vzali.'),
(1229, 8, 69, 'Jezte z toho, co jste ukořistili, jen dovolené a výtečné a bojte se Boha, vždyť Bůh je odpouštějící, slitovný.'),
(1230, 8, 70, 'Proroku! Rci těm ze zajatců, kteří jsou v rukou vašich: "Rozezná-li Bůh něco dobrého v srdcích vašich, daruje vám lepší než to, co bylo vám vzato, a odpustí vám, neboť Bůh je odpouštějící, slitovný."'),
(1231, 8, 71, 'A hodlají-li tě zradit nyní, vždyť již předtím zradili Boha, avšak On vám umožnil se jich zmocnit, neboť Bůh vševědoucí je i moudrý.'),
(1232, 8, 72, 'Vskutku ti, kdož uvěřili, přesídlili a vedli boj majetky i osobami svými na stezce Boží, a ti, kdož jim útulek poskytli a pomohli jim - ti všichni jsou si navzájem přáteli. Avšak s těmi, kdož uvěřili, a přesto nepřesídlili, nepěstujte přátelství v ničem, dokud nepřesídlí. Jestliže vás požádají o pomoc ve věci náboženství, jste povinni jim pomoci proti všem kromě těch lidí, s nimiž máte smlouvu. A Bůh jasně vidí vše, co děláte.'),
(1233, 8, 73, 'Ti, kdož neuvěřili, jsou si navzájem přáteli. Jestliže neučiníte podobně, pak zavládne na zemi pokušení i zkaženost veliká.'),
(1234, 8, 74, 'Věru ti, kdož uvěřili, přesídlili a vedli boj na cestě Boží, a ti, kdož jim útulek poskytli a pomohli jim, ti všichni jsou pravými věřícími a jim dostane se odpuštění i přídělu štědrého.'),
(1235, 8, 75, 'A ti, kdož později uvěřili, přesídlili a bojovali spolu s vámi, ti také patří k vám; avšak ti, kdož pokrevně spřízněni jsou, jsou si vzájemně nejbližší podle příkazu Božího a Bůh ví o věcech všech.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Zproštění od Boha a posla Jeho pro ty z modloslužebníků, s nimiž jste uzavřeli úmluvu:'),
(1237, 9, 2, '"Pohybujte se volně po zemi po dobu čtyř měsíců a vězte, že nejste schopni uniknout zásahu Božímu a že Bůh zahanbí nevěřící!"'),
(1238, 9, 3, 'Provolání od Boha a posla Jeho k lidem v den velké pouti: Bůh a Jeho posel se zříkají závazků vůči modloslužebníkům; jestliže se však kajícně obrátíte, bude to pro vás lepší, jestliže se však odvrátíte zády, vězte, že nejste schopni uniknout zásahu Božímu. A oznam těm, kdož neuvěřili, zvěst radostnou o trestu bolestném!'),
(1239, 9, 4, 'Výjimku tvoří ti modloslužebníci, s nimiž jste uzavřeli úmluvu a kteří vám potom nikterak neškodili a nikomu proti vám nepomáhali. Dodržujte tedy plně vůči nim úmluvu až do vypršení lhůty její, neboť Bůh věru miluje ty, kdož jsou bohabojní.'),
(1240, 9, 5, 'A až uplynou posvátné měsíce, pak zabíjejte modloslužebníky, kdekoliv je najdete, zajímejte je, obléhejte je a chystejte proti nim všemožné nástrahy! Jestliže se však kajícně obrátí, budou dodržovat modlitbu a dávat almužnu, nechte je jít cestou jejich, vždyť Bůh je věru odpouštějící, slitovný.'),
(1241, 9, 6, 'Požádá-li tě některý z modloslužebníků o ochranu, mu ji poskytni - aby mohl slyšet slovo Boží. Potom jej nech dospět do místa pro něj bezpečného; a to je proto, že jsou lidé, kteří nic nevědí.'),
(1242, 9, 7, 'Jak by mohli mít modloslužebníci kromě těch, s nimiž jste uzavřeli úmluvu u Posvátné mešity, úmluvu s Bohem a Jeho poslem? Pokud budou přímí v jednání s vámi, buďte i vy vůči nim přímí, neboť Bůh miluje bohabojné!'),
(1243, 9, 8, 'Jak jinak? Kdyby nabyli nad vámi převahy, nedodrželi by vůči vám ani spojenectví, ani přísahu úmluvy! A snaží se vás uchlácholit ústy svými, zatímco srdce jejich jsou odmítavá a většina z nich jsou hanebníci.'),
(1244, 9, 9, 'A oni kupují za znamení Boží věci nepatrné ceny a odvracejí jiné od cesty Jeho. Jak hnusné je to, co provádějí!'),
(1245, 9, 10, 'A nedodržují vůči věřícímu ani spojenectví, ani přísahu úmluvy a jsou to věru přestupníci!'),
(1246, 9, 11, 'Jestliže se však kajícně obrátí, budou dodržovat modlitbu a dávat almužnu, pak se stanou vašimi bratry v náboženství. A My srozumitelně vykládáme znamení pro lid, který je vědoucí.'),
(1247, 9, 12, 'Jestliže však poruší přísahy své poté, co uzavřeli úmluvu, a zaútočí na náboženství vaše, pak bojujte proti vůdcům nevěrectví; a pro ně neplatí žádné přísahy - snad potom přestanou!'),
(1248, 9, 13, 'Což nebudete bojovat proti lidu, jenž porušil přísahy své a usiloval o vyhnání posla a první vás napadl? Bojíte se jich snad? Však Bůh má větší právo na to, abyste se Ho obávali, jste-li vskutku věřící.'),
(1249, 9, 14, 'Bojujte tedy proti nim a Bůh je vašima rukama potrestá a zahanbí a dopomůže vám k vítězství nad nimi a uzdraví hrudě věřících'),
(1250, 9, 15, 'a odstraní hněv ze srdcí jejich. A Bůh přijme pokání, od koho chce, vždyť On vševědoucí je i moudrý.'),
(1251, 9, 16, 'Nebo se snad domníváte, že budete opuštěni, dříve než Bůh pozná ty z vás, kteří bojují na cestě Jeho a kteří si nevzali za spojence nikoho kromě Boha, Jeho posla a věřících? Bůh zajisté je dobře zpraven o všem, co děláte.'),
(1252, 9, 17, 'A nepřísluší modloslužebníkům starat se o modlitebny Boží, když sami o sobě dosvědčili, že jsou nevěřící. To jsou ti, jichž skutky budou marné a v ohni budou nesmrtelní!'),
(1253, 9, 18, 'O modlitebny Boží se mohou starat jedině ti, kdož věří v Boha a v den poslední, modlitby dodržují, almužny dávají a jen Boha se obávají. A možná, že budou mezi těmi, kdož správnou cestou jsou vedení.'),
(1254, 9, 19, 'Domníváte se, že ten, kdo poutníky napájí a o Posvátnou mešitu se stará, je roven tomu, kdo uvěřil v Boha a v den poslední a bojuje na cestě Boží? Ti nebudou si rovni před Bohem a Bůh nepovede správnou cestou lid nespravedlivý.'),
(1255, 9, 20, 'Ti, kdož uvěřili a vystěhovali se a bojovali na cestě Boží majetky i osobami svými, ti dosáhnou u Boha hodnosti nejvyšší a takoví budou úspěšní.'),
(1256, 9, 21, 'Pán jejich jim oznamuje zvěst radostnou o Svém milosrdenství i zalíbení, a o zahradách, v nichž budou mít trvalé potěšení'),
(1257, 9, 22, 'a v nichž budou navěky nesmrtelní, neboť u Boha je věru odměna nesmírná.'),
(1258, 9, 23, 'Vy, kteří věříte! Neberte si za přátele otce své a bratry své, jestliže je jim nevěrectví milejší než víra. A ti z vás, kdož se s nimi přátelí, jsou nespravedliví!"'),
(1259, 9, 24, 'Rci: "Jsou-li vám vaši otcové, vaši synové, vaši bratři, vaše manželky, váš rod a jmění, které jste získali, a obchod, jehož úpadku se obáváte, a příbytky vaše, v nichž zalíbeni máte, milejší než Bůh a Jeho posel a ti, kdož na cestě Jeho bojují, tedy počkejte, až Bůh přijde se Svým rozhodnutím! A Bůh nevede cestou správnou lid hanebníků."'),
(1260, 9, 25, 'Bůh vám dozajista pomohl již na bojištích mnohých i v den bitvy u Hunajnu, kdy jste se těšili ze svého velkého počtu. Ale nijak vám to neprospěje, když se vám země, přes svou rozlehlost, stala úzkou a když jste se potom obrátili zády.'),
(1261, 9, 26, 'Potom Bůh seslal Svou sakínu poslu Svému a věřícím a seslal také vojska, jež byla pro vás neviditelná, a potrestal ty, kdož nevěřili a taková je odměna nevěřících.'),
(1262, 9, 27, 'Později pak Bůh přijme pokání od těch, od nichž chce a Bůh je odpouštějící, slitovný.'),
(1263, 9, 28, 'Vy, kteří věříte! Modloslužebníci jsou věru nečistota! Nechť se tudíž nepřibližují k Posvátné mešitě po tomto roce! A obáváte-li se kvůli tomu nedostatku, pak vás Bůh obohatí ze štědrosti Své, bude-li chtít, a Bůh věru je vševědoucí i moudrý.'),
(1264, 9, 29, 'Bojujte proti těm, kdož nevěří v Boha a v den poslední a nezakazují to, co zakázal Bůh a Jeho posel, a kteří neuctívají náboženství pravdy, z těch, kterým se dostalo Písma dokud nedají poplatek přímo vlastní rukou, jsouce poníženi.'),
(1265, 9, 30, 'A říkají židé: "´Uzajr je syn Boží!" a říkají křesťané: "Mesiáš je syn Boží!" A taková je řeč, již ústy svými pronášejí, a napodobují tak řeč oněch, kdož před nimi byli nevěřící. Nechť Bůh proti nim bojuje! Do jaké lži to upadli!'),
(1266, 9, 31, 'A vzali si učence své a mnichy své za pány vedle Boha a stejně tak i Mesiáše, syna Mariina, ačkoliv jim bylo přikázáno uctívat toliko Boha jediného. A není božstva kromě Něho, jenž slávou Svou povznesen je nad ty, kdož jsou k Němu přidružováni!'),
(1267, 9, 32, 'Chtěli by zhasnout světlo Boží ústy svými, avšak Bůh nechce nic jiného než učinit světlo Své dokonalým, i když se to protiví nevěřícím.'),
(1268, 9, 33, 'On je ten, jenž vyslal posla Svého se správným vedením a náboženstvím pravdivým, aby zvítězilo nad všemi náboženstvími jinými, i když se to nelíbí modloslužebníkům.'),
(1269, 9, 34, 'Vy, kteří věříte! Věru mnoho z učených a z mnichů pohlcuje majetky lidí způsobem podvodným a odvrací lidi od cesty Boží! A těm, kdož shromaždují zlato a stříbro a nevynakládají je na cestě Boží, těm oznam zvěst radostnou o trestu bolestném'),
(1270, 9, 35, 'v den, kdy tyto kovy budou rozpáleny v ohni pekelném a budou jimi vypálena znamení na čela, boky i záda jejich a bude zvoláno: "Toto je to, co jste hromadili, okuste nyní toho, co jste tak hromadili!"'),
(1271, 9, 36, 'Zajisté počet měsíců u Boha je dvanáct měsíců, stanovený v Knize Boží v den, kdy stvořil nebesa a zemi. A z nich jsou čtyři měsíce posvátné, a to je náboženství neměnitelné. Proto si v nich nečiňte vzájemně zlé, nýbrž bojujte proti nevěřícím jako jeden muž, tak jako oni bojují proti vám jako jeden muž a vězte, že Bůh stojí na straně bohabojných.'),
(1272, 9, 37, 'Vsunutý měsíc je nadměrné nevěrectví, jímž jsou uváděni v bloudění ti, kdož neuvěřili. V jednom roce prohlašují, že není posvátný, a v druhém roce, že je posvátný, aby to souhlasilo s počtem měsíců, jež Bůh učinil posvátnými, takže dovolují to, co Bůh zakázal. A tak jim byly okrášleny špatné skutky jejich a Bůh nepovede lid nevěřící.'),
(1273, 9, 38, 'Vy, kteří věříte! Co je s vámi, že když je vám řečeno: "Vytáhněte do boje na cestě Boží!", jste jako přikováni k zemi? Což se vám líbí více život na tomto než na onom světě? Vždyť užívání života pozemského je nepatrné oproti užívání v životě budoucím!'),
(1274, 9, 39, 'Jestliže nevytáhnete do boje, Bůh vás potrestá trestem bolestným, vystřídá vás národem jiným a nebudete Mu moci v ničem uškodit, neboť Bůh je věru mocný nad každou věcí.'),
(1275, 9, 40, 'Jestliže vy mu nebudete pomáhat, Bůh pomohl mu již dříve, když vyhnali jej nevěřící a když byl jedním ze dvou; a když byli oba skryti v jeskyni, pravil svému příteli: "Nermuť se, vždyť Bůh je s námi!" A seslal mu Bůh Svou sakínu a podpořil jej vojsky pro vás neviditelnými a ponížil slova těch, kdož nevěřili, zatímco slovo Boží je nejvyšší - Bůh zajisté je mocný, moudrý.'),
(1276, 9, 41, 'Jděte v boj lehcí i těžcí a bojujte majetky i osobami svými na cestě Boží! A to bude pro vás lepší, jste-li vědoucí.'),
(1277, 9, 42, 'Kdyby to byla záležitost blízká nebo cesta krátká, byli by tě věru následovali, avšak vzdálenost se jim zdála dlouhá, a tak přísahali při Bohu: "Kdyby nám to bylo možné, byli bychom vskutku vytáhli s vámi!" A oni takto zahubili sami sebe a Bůh dobře ví, že jsou lháři.'),
(1278, 9, 43, 'Kéž ti Bůh odpustí (proroku)! Proč jsi jim dovolil zůstat stranou, dříve než se ti stalo jasným, kdo jsou pravdomluvní, a než jsi poznal, kdo jsou lháři?'),
(1279, 9, 44, 'Ti, kdož věří v Boha a v den soudný, nežádají tě o dovolení bojovat majetky a osobami svými; a Bůh dobře zná bohabojné.'),
(1280, 9, 45, 'Však žádají tě o dovolení vyhnout se boji jenom ti, kdož nevěří v Boha a v den soudný; jejich srdce jsou naplněna pochybností a jsou v pochybnostech svých zmítáni.'),
(1281, 9, 46, 'Kdyby byli chtěli vytáhnout do boje, věru by byli činili nějaké přípravy, avšak Bohu se zhnusilo, aby vytáhli, a dal jim zlenivěti a bylo jim řečeno: "Zůstaňte sedět doma se sedícími!"'),
(1282, 9, 47, 'Kdyby byli vytáhli s vámi, byli by jen rozmnožili vaše potíže a byli by zaseli mezi vámi nesvár, snažíce se vás svést k neposlušnosti. A jsou mezi vámi někteří, kdož horlivě jim naslouchají, avšak Bůh dobře zná nespravedlivé.'),
(1283, 9, 48, 'A již dříve usilovali, aby vznikl rozkol, a převraceli věci pro tebe, až vyšla pravda najevo a objevil se rozkaz Boží, přestože se jim to nelíbilo.'),
(1284, 9, 49, 'A jsou mezi nimi někteří, kdož říkají: "Dovol mi neúčastnit se a neuváděj mne v pokušení!" Což však již neupadli do pokušení? A věru peklo již obklopuje nevěřící.'),
(1285, 9, 50, 'Jestliže se ti dostane něčeho dobrého, působí jim to bolest, ale když tě postihne nějaké neštěstí, říkají: "My jsme se o věci své jíž dříve postarali!" a obracejí se zády, radujíce se.'),
(1286, 9, 51, 'Rci: "Budeme postiženi jenom tím, co nám Bůh připsal a On naším je Pánem a nechť na Boha se věřící spoléhají!"'),
(1287, 9, 52, 'A rci: "Co můžete pro nás očekávat jiného, než jednu ze dvou odměn překrásných, zatímco my pro vás očekáváme jedině to, že Bůh vás zasáhne trestem, jenž vás od Něho postihne rukama našima? Jen čekejte, my s vámi budeme také čekat."'),
(1288, 9, 53, 'Rci: "A ať přispíváte dobrovolně, či s nechutí, nebude to od vás přijato, neboť jste lid hanebníků."'),
(1289, 9, 54, 'A nebrání tomu, aby byly od nich přijaty jejich příspěvky, leč to, že neuvěřili v Boha a posla Jeho, že konali modlitbu lenivě a dávali příspěvky s nechutí.'),
(1290, 9, 55, 'Nechť nebudí obdiv tvůj jmění jejich ani děti jejich! Bůh si je tím přeje pouze potrestat v životě pozemském a chce, aby duše jejich odešly, zatímco jsou nevěřící.'),
(1291, 9, 56, 'A přísahají při Bohu, že vskutku patří mezi vás, zatímco mezi vás nepatří, nýbrž jsou to lidé, kteří stojí stranou.'),
(1292, 9, 57, 'Kdyby nalezli nějaké útočiště či jeskyni nebo úkryt, prchli by tam s největší rychlostí.'),
(1293, 9, 58, 'A jsou mezi nimi někteří, kdož tě pomlouvají kvůli rozdělování almužny; když z ní něco dostanou, jsou spokojeni, avšak když z ní nedostanou nic, hned jsou rozzlobeni.'),
(1294, 9, 59, 'Kéž jsou spokojeni s tím, co jim Bůh a posel Jeho dali, a nechť řeknou: "Bůh nám postačí a Bůh nám uštědří ze Své laskavosti a taktéž i posel Jeho. A my toužíme jen po Bohu!"'),
(1295, 9, 60, 'Almužny jsou pouze pro chudé a nuzné, pro ty, kdož je vybírají, pro ty, jichž srdce se sjednotila, pro otroky a zadlužené, pro boj na cestě Boží a pro toho, kdož po ní kráčí podle ustanovení Božího, a Bůh je vševědoucí, moudrý.'),
(1296, 9, 61, 'A jsou mezi nimi ti, kdož urážejí proroka a říkají: "On je jenom uchem!" Odpověz: "On je uchem dobra pro vás, on věří v Boha a má víru ve věřící a je milosrdenstvím pro ty z vás, kdož uvěřili; však pro ty, kdož uráželi posla Božího, je připraven trest bolestný."'),
(1297, 9, 62, 'A přísahají vám při Bohu, aby vás uspokojili, ale spíše Bůh a Jeho posel jsou hodni toho, aby byli uspokojeni, jsou-li tamti věřící.'),
(1298, 9, 63, 'Což nevědí, že pro toho, kdo protiví se Bohu a Jeho poslu, je určen oheň pekelný, v němž bude nesmrtelný? A to je potupa nesmírná!'),
(1299, 9, 64, 'Pokrytci se obávají, aby nebyla ohledně nich seslána súra, jež by jim oznámila to, co v srdcích jejich je skryto. Rci: "Jen se posmívejte, avšak Bůh vyjeví to, čeho se obáváte!"'),
(1300, 9, 65, 'Když se jich tážeš, tu věru řeknou: "My jsme jen tak tlachali a bavili se!" Rci: "Jak jste se mohli posmívat Bohu, Jeho znamením a Jeho poslu?"'),
(1301, 9, 66, 'Nevymlouvejte se! Stali jste se nevěřícími poté, co dostalo se vám víry pravé. Jestliže odpustíme jedné skupině z vás, jinou zase naopak potrestáme za to, že byli hříšníky.'),
(1302, 9, 67, 'Pokrytci, muži i ženy, patří jedni k druhým: přikazují zavrženíhodné a zakazují vhodné a zavírají ruce své. Zapomněli na Boha a také On na ně zapomněl, neboť pokrytci jsou věru hanebníci.'),
(1303, 9, 68, 'A Bůh přislíbil pokrytcům mužům i ženám stejně jako nevěřícím oheň pekelný, v němž nesmrtelní budou dlít; to bude úděl jejich. A proklel je Bůh a pro ně určen je trest trvalý.'),
(1304, 9, 69, 'Jste jako ti, kdož před vámi byli a větší moc a hojnější majetek i děti měli a svého přídělu pozemského si užívali. I vy si užíváte přídělu pozemského, tak jak si ho užívali ti, kdož před vámi byli, a vedete stejné plané řeči, jaké vedli oni; a skutky jejich marné byly na tomto i na onom světě a oni jsou těmi, kdož ztrátu utrpěli.'),
(1305, 9, 70, 'Což se jim nedostalo zprávy o těch, kdož před nimi žili? O lidu Noemově, o Ádovcích a Thamúdovcích, o lidu Abrahamově, o obyvatelích Madjanu a o Městech vyvrácených? K nim všem přišli poslové s důkazy jasnými a Bůh nebyl takový, aby jim ukřivdil, leč oni sami sobě ukřivdili.'),
(1306, 9, 71, 'Věřící muži a věřící ženy jsou si vzájemně přáteli a přikazují vhodné a zakazují zavrženíhodné, dodržují modlitbu, dávají almužnu a jsou poslušní vůči Bohu a Jeho poslu. Nad těmi se Bůh věru slituje, neboť Bůh mocný je i moudrý.'),
(1307, 9, 72, 'Bůh přislíbil věřícím mužům a věřícím ženám zahrady, pod nimiž řeky tekou, v nichž nesmrtelní budou dlít, a přislíbil jim příbytky rozkošné v zahradách Edenu. Však zalíbení Boží bude odměnou největší, a to úspěch bude nesmírný.'),
(1308, 9, 73, 'Proroku, bojuj usilovně proti nevěřícím a pokrytcům a buď přísný na ně! Útočištěm jejich bude peklo, a jak hnusný je to cíl konečný!'),
(1309, 9, 74, 'A přísahají při Bohu, že to neřekli, avšak již pronesli slova nevíry a stali se nevěřícími poté, co byli již muslimy. A zamýšleli něco, čeho pak vůbec nedosáhli, a mstili se jedině za to, že Bůh a posel Jeho je obohatil z dobrodiní Svého. Však jestliže se kajícně obrátí, bude to pro ně lepší, jestliže se naopak zády odvrátí, potrestá je Bůh trestem bolestným na tomto i na onom světě. A nebudou mít na zemi ochránce ani pomocníka žádného.'),
(1310, 9, 75, 'A jsou mezi nimi někteří, kdož uzavřeli s Bohem úmluvu: "Dá-li nám On něco ze své štědrosti, rozdáme to jako almužnu a budeme patřit mezi bezúhonné."'),
(1311, 9, 76, 'Když však jim Bůh dal ze štědrosti Své, skrblili s tím, odvrátili se a vzdálili se.'),
(1312, 9, 77, 'A mělo to za následek, že pokrytectví vstoupilo do jejich srdci až do dne, kdy setkají se s Ním a budou potrestáni za to, že porušili slib svůj daný Bohu, a za to, že prolhaní byli.'),
(1313, 9, 78, 'Což nevědí, že Bůh zná tajemství jejich i skryté rozhovory jejich a že Bůh zná dobře i nepoznatelné?'),
(1314, 9, 79, 'A těm, kdož pomlouvají ty z věřících, kteří dobrovolně dávají almužny, a ty z nich, kteří dávají jen tolik, kolik nalezli s největším úsilím, a vysmívají se jim - těm Bůh se bude posmívat a pro ty je připraven trest bolestný.'),
(1315, 9, 80, 'Pros za odpuštění pro ně, anebo nepros! A i kdybys prosil pro ně za odpuštění sedmdesátkrát, neodpustí jim Bůh! A to je za to, že neuvěřili v Boha a v posla Jeho a Bůh přece nepovede po cestě správné lid hanebníků.'),
(1316, 9, 81, 'Ti, kdo zůstali sedět doma, se radují, že za zády posla Božího se neúčastnili boje; nelíbí se jim bojovat majetky a osobami svými na cestě Boží a říkají: "Nevydávejte se do boje ve vedru!" Rci: "V ohni pekelném bude mnohem horší vedro!" Kéž by to jen pochopili!'),
(1317, 9, 82, 'Budou se nyní smát jen krátce, ale dlouho budou plakat (na onom světě) v odplatu za to, co si vysloužili!'),
(1318, 9, 83, 'A jestliže tě Bůh navrátí k některé skupině z nich a budou-li tě žádat o dovolení vytáhnout do boje, řekni jim: "Už nikdy se mnou nepůjdete do boje a už nikdy se mnou nebudete bojovat proti nepříteli! Zalíbilo se vám sedět doma hned napoprvé, zůstaňte tedy nyní sedět s těmi, kdož doma zůstali!"'),
(1319, 9, 84, 'A nikdy se již nemodli nad žádným z nich, když zemře, a nikdy se nezastav u jeho hrobu! Vždyť oni nevěřili v Boha ani posla Jeho a zemřeli jako hanebníci.'),
(1320, 9, 85, 'Nechť nebudí obdiv tvůj jmění jejich ani dětí jejich! Bůh si je tím přeje pouze potrestat v životě pozemském a chce, aby duše jejich odešly, zatímco jsou nevěřící.'),
(1321, 9, 86, 'A když byla seslána súra nařizující "Věřte v Boha a bojujte spolu s Jeho poslem!", žádali tě blahobytní mezi nimi o dovolení neúčastnit se a řekli: "Nech nás, abychom zůstali s těmi, kdož sedí doma!"'),
(1322, 9, 87, 'Zalíbilo se jim, že byli s těmi, kdož zůstali vzadu, a byla zapečetěna srdce jejich, takže nic nepochopili.'),
(1323, 9, 88, 'Avšak posel a ti, kdož s ním uvěřili, přesto bojovali majetky i osobami svými a pro ně jsou připraveny nejlepší věci a oni budou blažení.'),
(1324, 9, 89, 'Bůh pro ně přichystal zahrady, pod nimiž řeky tekou; v nich budou nesmrtelní, a to úspěch je nesmírný.'),
(1325, 9, 90, 'Ti, kdož se z kočovných Arabů vymlouvají, přišli k tobě, abys jim dovolil neúčastnit se boje; a zůstali sedět doma i ti, kdo prohlašovali Boha a posla Jeho za lháře. A postihne ty z nich, kdož byli nevěřící, trest bolestný.'),
(1326, 9, 91, 'A není hany na slabých, ani na nemocných, ani na těch, kdož nenaleznou prostředky na výdaje, jsou-li upřímně oddáni Bohu a Jeho poslu. A není důvodu proti těm, kdož dobro konají a Bůh je odpouštějící, slitovný -'),
(1327, 9, 92, 'ani proti těm, kteří k tobě přijdou, abys jim opatřil jízdní zvíře, a když ty jim řekneš: "Nenalézám žádné zvíře, na něž bych vás posadil", odvrátí se s očima, z nichž kanou slzy ze zármutku, že nenalezli nic, čím by přispěli.'),
(1328, 9, 93, 'Avšak je důvod proti těm, kdož tě žádají o dovolení neúčastnit se, ačkoliv jsou bohatí. Zalíbilo se jim, že byli s těmi, kdož zůstali vzadu, a Bůh již zapečetil srdce jejich, aniž o tom vědí.'),
(1329, 9, 94, 'A budou se vám vymlouvat, až se k nim navrátíte. Rci: "Nevymlouvejte se, nevěříme vám! Bůh nám již sdělil o vás zprávu a Bůh a posel Jeho uvidí činy vaše a potom budete navráceni k Tomu, jenž zná nepoznatelné i všeobecně známé a On vám oznámí, co jste vlastně dělali."'),
(1330, 9, 95, 'A budou vám přísahat při Bohu, až se k nim navrátíte, abyste se od nich vzdálili. Odvraťte se tedy od nich, neboť jsou to špinavci a jejich příbytkem bude peklo v odměnu za to, co si vysloužili.'),
(1331, 9, 96, 'A budou vás zapřísahat, abyste v nich opět nalezli zalíbení; jestliže vy v nich snad opět naleznete zalíbení, Bůh věru nenalezne zalíbení v lidu hanebníků.'),
(1332, 9, 97, 'Kočovní Arabové jsou nejhorší v nevěře a v pokrytectví a nejvíce nakloněni přehlížet omezení, jež Bůh seslal poslu Svému. A Bůh je vševědoucí, moudrý.'),
(1333, 9, 98, 'A jsou mezi kočovnými Araby ti, kteří pokládají příspěvky své za pokutu a vyčkávají, až postihne vás obrat ke špatnému. Je ať postihne obrat ke špatnému! A Bůh je slyšící, vševědoucí.'),
(1334, 9, 99, 'A jsou mezi kočovnými Araby někteří, kdož věří v Boha a v den poslední a pokládají příspěvky své za přiblížení se k Bohu a k modlitbám posla. Což toto není vskutku cesta, aby se přiblížili? A Bůh je uvede do milosrdenství Svého, neboť Bůh věru je odpouštějící, slitovný.'),
(1335, 9, 100, 'A Bůh nalezl zalíbení v prvních předchůdcích z přesídlenců a pomocníků a v těch, kdož je následovali ve zbožných skutcích a také oni nalezli zalíbeni v Bohu; On pak připravil pro ně zahrady, pod nimiž řeky tekou a v nichž nesmrtelní navěky přebývat budou a to úspěch bude nesmírný.'),
(1336, 9, 101, 'Mezi kočovnými Araby, kteří jsou ve vašem okolí, i mezi obyvateli Medíny jsou pokrytci, zatvrzelí v pokrytectví svém. Ty je neznáš, avšak My je dobře známe a potrestáme je dvakrát a pak budou vráceni k trestu nesmírnému.'),
(1337, 9, 102, 'Avšak jiní uznali hříchy své a smísili tak skutky dobré s jinými, jež jsou špatné; a možná že jim Bůh odpustí, vždyť Bůh je věru odpouštějící, slitovný.'),
(1338, 9, 103, 'Ber ze jmění jejich almužnu, kterou je očišťuješ a ospravedlňuješ! Modli se za ně, neboť modlitba tvá bude pro ně uklidněním a Bůh je slyšící, vševědoucí.'),
(1339, 9, 104, 'Což nevědí, že Bůh je ten, jenž přijímá pokání služebníků Svých a přijímá i almužnu? Bůh věru je blahovolný k pokání a slitovný.'),
(1340, 9, 105, 'Rci: "Jednejte, a Bůh uvidí činy vaše stejně jako Jeho posel a věřící! A pak budete navráceni k Tomu, jenž zná nepoznatelné i všeobecně známé, a On vám oznámí, co jste vlastně dělali."'),
(1341, 9, 106, 'Jiným pak byl dán odklad až do rozhodnutí Božího: buď je potrestá, anebo přijme jejich pokání a Bůh vševědoucí je i moudrý.'),
(1342, 9, 107, 'A ti, kdo si zřídili mešitu ze vzdoru a z nevěry a pro rozkol mezi věřícími a jako nástrahu, jsou ti, kdo vedli válku proti Bohu a Jeho poslu již dříve; a oni věru přísahají: "My chtěli jsme jen to nejlepší!" Však Bůh je svědkem, že jsou vskutku lháři.'),
(1343, 9, 108, 'Nikdy v ní nekonej modlitbu! Mešita založená od prvního dne na bohabojnosti je k tomu, aby ses v ní modlil, věru vhodnější, a jsou v ní muži, kteří milují očišťování a také Bůh miluje ty, kdož se očišťují.'),
(1344, 9, 109, 'Je ten, jenž založil stavbu svou na bázni Boží a pro Jeho zalíbení, lepší než ten, kdo založil stavbu svou na okraji podemletého břehu a zřítí se s ní do ohně pekelného? Bůh věru nepovede cestou správnou lid nespravedlivý.'),
(1345, 9, 110, 'A stavba jejich, kterou si postavili, nepřestane být příčinou pochybností v srdcích jejich, dokud se jejich srdce nerozpadnou na kusy. A Bůh vševědoucí je i moudrý.'),
(1346, 9, 111, 'A Bůh věru koupil od věřících jejich osoby i jejich majetky s tím, že jim budou dány zahrady. A oni bojují na cestě Boží - zabíjejí a jsou zabíjeni. A to je slib Jeho pravdivý, daný v Tóře, v Evangeliu, i v Koránu. A kdo věrněji dodržuje slib svůj než Bůh? Radujte se tedy z obchodu svého s Ním uzavřeného a to úspěch je nesmírný.'),
(1347, 9, 112, 'Kající se, uctívající, chválící, oslavující, sklánějící se, poklekající, přikazující vhodné a zakazující zavrženíhodné, dodržující ustanovení Boží... Oznam věřícím zvěst radostnou!'),
(1348, 9, 113, 'Nepřísluší prorokovi ani věřícím, aby prosili za odpuštění pro modloslužebníky, byť i to byli blízcí příbuzní, poté, co bylo jim jasně ukázáno, že stanou se obyvateli ohně.'),
(1349, 9, 114, 'A byla prosba Abrahamova za odpuštění pro otce jeho pouze splněním slibu, který mu byl dal. Když však mu bylo objasněno, že otec jeho je nepřítel Boží, zřekl se Abraham odpovědnosti za něj. A byl Abraham věru soucitný i blahovolný.'),
(1350, 9, 115, 'A Bůh věru není takový, aby dal zbloudit lidem poté, co uvedl je na správnou cestu, a dříve než jim objasnil to, čeho se mají obávat. A Bůh věru je o každé věci vševědoucí.'),
(1351, 9, 116, 'Bohu náleží království na nebesích i na zemi, On život i smrt dává; a nemáte vedle Boha ochránce ani pomocníka žádného.'),
(1352, 9, 117, 'A Bůh již věru odpustil prorokovi, vystěhovalcům i pomocníkům, kteří jej následovali v hodině nesnází, poté, co srdce jedné části z nich se málem odklonila; a potom přijal pokání jejich a byl vůči nim shovívavý, slitovný.'),
(1353, 9, 118, 'A prominul rovněž těm třem, kteří zůstali vzadu, když se jim země přes svou rozlehlost zdála příliš úzkou a i duše jejich pojala úzkost, takže sami nahlédli, že není jiného útočiště před Bohem než u Něho samého. A potom se k nim blahovolně obrátil, aby se mohli kát, vždyť Bůh přijímá pokání a je slitovný.'),
(1354, 9, 119, 'Vy, kteří věříte! Bojte se Boha a buďte s pravdomluvnými!'),
(1355, 9, 120, 'Neslušelo se obyvatelům Medíny ani těm z kočovných Arabů, kteří jsou v jejich okolí, že zůstali za zády posla Božího a že dali přednost lásce k sobě před láskou k Němu. A to proto, že je nepostihla ani žízeň, ani únava, ani hlad na cestě Boží; neučinili jediný krok, který by rozhněval nevěřící, a neutrpěli z rukou nepřítele nic, aniž jim to bylo připsáno jako zbožný skutek - vždyť Bůh nedopustí, aby ztratila se odměna těch, kdož dobré činí.'),
(1356, 9, 121, 'A neučiní jediný výdaj, ať malý či velký, a nepřekročí žádné údolí, aniž jim to bylo připsáno k dobru, aby je mohl Bůh odměnit podle toho nejlepšího, co konali.'),
(1357, 9, 122, 'Věřící nechť se nevypravují všichni najednou; proč by se z každé skupiny nemohl vypravit jeden oddíl, aby se lidé z něho poučili o náboženství a aby po návratu mohli varovat svůj lid - snad budou se mít na pozoru!'),
(1358, 9, 123, 'Vy, kteří věříte! Bojujte proti těm z nevěřících, kteří jsou poblíže! Nechť ve vás naleznou tvrdost a vězte, že Bůh je na straně bohabojných!'),
(1359, 9, 124, 'A sotva je seslána nějaká súra, řeknou někteří z nich: "Komu z vás přidala tato súra na víře?" Co se týká těch, kdož uvěřili, jejich víra se posílila a zaradovali se z ní,'),
(1360, 9, 125, 'zatímco těm, jichž srdce jsou chorobná, přidala jen špínu k špíně jejich a ti zemřou jako nevěřící.'),
(1361, 9, 126, 'Což nevidí, že v každém roce jsou jednou či dvakrát vystaveni pokušení? A přece se potom nekají a nevzpamatují se.'),
(1362, 9, 127, 'A sotva je seslána nějaká súra, tu dívají se jeden na druhého a říkají: "Vidí nás někdo?" a pak odcházejí. Nechť Bůh odvrátí srdce jejich, neboť jsou to lidé, kteří nic nepochopí.'),
(1363, 9, 128, 'A nyní přišel k vám již posel z vašich řad vzešlý a tíží jej, že špatného se dopouštíte, neboť mu záleží na vašem dobru. A vůči věřícím je shovívavý, slitovný.'),
(1364, 9, 129, 'A jestliže se oni odvrátí, rci: "Bůh mi zcela stačí a není božstva kromě Něho a na Něj se spoléhám; On Pánem je trůnu nesmírného!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Alif lám rá! Toto jsou znamení Písma moudrého!'),
(1366, 10, 2, 'Je snad lidem podivné, že jsme vnukli muži jednomu z nich: "Varuj lidi a oznam zvěst radostnou těm, kdož uvěřili, že u Pána svého budou mít spravedlivé postavení." Nevěřící říkají:,, Toto je věru kouzelník zjevný!"'),
(1367, 10, 3, 'Pánem vaším je věru Bůh, jenž stvořil nebesa a zemi v šesti dnech a potom se na trůnu usadil, aby věci všechny řídil. A není přímluvce, pokud On to předtím nedovolí. Hle, takový je Bůh, Pán váš, pročež Jej vzývejte! Což se nevzpamatujete?'),
(1368, 10, 4, 'K Němu se uskuteční návrat váš, a to slib Boží je pravdivý! On zahájil první stvoření a potom je bude opakovat, aby spravedlivě odměnil ty, kdož uvěřili a zbožné skutky konali; pro ty, kdož nevěřícími byli, pak připraven je nápoj vroucí a trest bolestný za to, že nevěřili.'),
(1369, 10, 5, 'On je ten, kdo slunce jasnou září učinil a měsíc světlem a postavení jeho určil, abyste počet roků i počítání jejich znali. A Bůh to stvořil jako skutečnost vážnou a On činí znamení srozumitelnými pro lidi vědoucí.'),
(1370, 10, 6, 'A zajisté jsou ve střídání noci a dne i ve všem, co Bůh na nebesích a na zemi stvořil, znamení pro lid bohabojný.'),
(1371, 10, 7, 'Věru ti, kdož v setkání s Námi nedoufají a v životě pozemském zalíbení nalézají a jen jím se spokojují, a ti, kteří ke znamením Našim jsou lhostejní,'),
(1372, 10, 8, 'ti za to, co si vysloužili, útočiště své budou mít v ohni.'),
(1373, 10, 9, 'Ty pak, kdož uvěřili a zbožné skutky konali, ty povede Pán jejich ve víře jejich a pod nimi potekou řeky v zahradách blaženosti,'),
(1374, 10, 10, 'v nichž voláním jejich bude: "Sláva Tobě, Bože veliký!" a přivítáním pozdrav: "Mír s vámi!" a koncem zvolání jejich: "Chvála Bohu, Pánu lidstva veškerého!"'),
(1375, 10, 11, 'Kdyby Bůh uspíšil lidem neštěstí, tak jak oni si přejí uspíšit pro sebe dobro, byla by již lhůta jejich rozhodnuta. My pak ponecháváme ty, kdož v setkání s Námi nedoufají, ve vzpurnosti jejich, v níž slepě se potácejí.'),
(1376, 10, 12, 'Když postihne člověka neštěstí, tu volá Nás vleže, vsedě i vestoje, když však od něho neštěstí odejmeme, kráčí dále, jako by Nás nebyl nikdy volal proti neštěstí, jež se ho dotklo. A takto je přestupníkům zkrášlováno vše, co dělají.'),
(1377, 10, 13, 'A zahubili jsme již před vámi pokolení předchozí, když lidé nespravedliví byli a při příchodu poslů svých s důkazy jasnými jim neuvěřili. A takto odměňujeme lid hříšníků.'),
(1378, 10, 14, 'Potom jsme vás učinili nástupci jejich na zemi po záhubě jejich, abychom zjistili, jak počínat si budete.'),
(1379, 10, 15, 'A když jsou jim přednášena znamení Naše jako důkazy, ti, kdož v setkání s Námi nedoufají, říkají: "Přines nám Korán jiný než tento, anebo v něm proveď změny!" Odpověz: "Nepřísluší mi, abych sám o sobě jej měnil, já pouze následuji to, co bylo mi vnuknuto, a bojím se, neuposlechnu-li Pána svého, trestu dne strašného."'),
(1380, 10, 16, 'Rci: "Kdyby Bůh chtěl, nebyl bych vám sdělil tento Korán a nebyl by vám dal o něm vědění. Vždyť strávil jsem mezi vámi život svůj celý již předtím. Což rozumnými nebudete?"'),
(1381, 10, 17, 'Kdo je nespravedlivější než ten, kdo proti Bohu si lži vymýšlí či znamení Jeho za lživá prohlašuje? Věru že hříšníci se nestanou blaženými.'),
(1382, 10, 18, 'Oni uctívají místo Boha něco, co jim nemůže ani škodit, ani prospívat; a říkají: "Toto jsou přímluvci naši u Boha!" Rci: "Chcete poučovat Boha o něčem, co by neznal na nebesích a na zemi? Sláva Mu, oč vznešenější je On než to, co k Němu přidružujete!"'),
(1383, 10, 19, 'Lidé původně jen národ jediný tvořili, avšak pak se v názorech rozešli. A kdyby nebylo slova Pána tvého, již dříve vyřčeného, bylo by věru mezi nimi již rozhodnuto vše, v čem se rozcházeli.'),
(1384, 10, 20, 'Říkají: "Pročpak mu nebylo sesláno od Pána jeho znamení nějaké?" Odpověz: "Nepoznatelné jedině Bohu přísluší. Čekejte a já také s vámi čekat budu!"'),
(1385, 10, 21, 'Když jsme dali lidem okusit milosrdenství Svého poté, co dotklo se jich neštěstí, hle, oni úklady strojí proti znamením Našim. Rci: "Bůh rychlejší je ve lstivosti!" A poslové Naši věru zapisují, co za úklady chystáte.'),
(1386, 10, 22, 'A On je ten, jenž cestovat vám dává po souši i po moři, a když jste na lodi... A plují s nimi pod větrem příznivým a radují se z něho, když se však přižene na ně vítr bouřlivý a vlny se na ně valí ze všech stran a oni si uvědomí, čím jsou obklopeni, tehdy k Bohu se modlí zasvěcujíce Mu víru svou upřímnou a volají: "Jestliže nás z tohoto zachráníš, budeme věru patřit mezi vděčné!"'),
(1387, 10, 23, 'A když je zachrání, hle, znovu páší nepravosti na zemi bezprávně: "Ó lidé, nepravosti vaše se proti vám samým obrátí, vždyť krátké je užívání života pozemského a potom k Nám se uskuteční váš návrat a My vás poučíme o tom, co jste páchali!"'),
(1388, 10, 24, 'Život pozemský se věru podobá vodě, kterou jsme seslali z nebe: mísí se s rostlinstvem země, jímž lidé a zvířata se živí. A když země se přioděje ozdobami svými a krásnou se stane, tu domnívají se její obyvatelé, že jsou vládci jejími; však zasáhne je rozkaz Náš v noci či ve dne a učiníme ji jako pole pokosené, tak jako by včera nebyla ani vzkvétala. A takto činíme znamení Svá srozumitelnými pro lid přemýšlivý.'),
(1389, 10, 25, 'A Bůh zve lidi do příbytku míru a vede po stezce přímé, koho chce.'),
(1390, 10, 26, 'Těm, kdo dobré konali, bude patřit odměna nejkrásnější a ještě jim bude přidáno; jich tváře nepokryje ani chmura, ani ponížení a budou ráje obyvateli a v něm věčné bude jejich přebývání.'),
(1391, 10, 27, 'Těm, kdož špatných skutků se dopustili, dostane se odměny podobně zlé a budou pokryti ponížením. Proti Bohu pak nebudou mít ochránce žádného a tváře jejich budou jakoby zahaleny kusem noci temné, a to obyvatelé budou ohně a v něm přebývat budou věčně.'),
(1392, 10, 28, 'V den, kdy shromáždíme všechny, řekneme těm, kdož modloslužebníky byli: "Na místa svá, vy i ti, jež jste přidružovali!" A až je rozdělíme od sebe, řeknou ti, které oni k Bohu přidružovali: "Věru jste nás neuctívali'),
(1393, 10, 29, 'a Bůh stačí jako svědek mezi námi i vámi, že my dozajista jsme byli nevšímaví k vašemu uctívání!"'),
(1394, 10, 30, 'A tam zakusí duše každá za to, co již dříve konala. A nevěřící budou navráceni k Bohu, vládci svému skutečnému, a vzdálí se od nich to, co si vymyslili.'),
(1395, 10, 31, 'Rci: "Kdo uštědřuje vám obživu z nebes a země? Kdo vládcem je sluchu a zraku? Kdo dává vzejít živému z mrtvého a mrtvému ze živého a kdo řídí rozkazy všechny?" I odvětí: "Bůh!" Rci jim tedy: "Proč tedy nechcete být bohabojní?"'),
(1396, 10, 32, 'Hle, takový je Bůh, Pán váš i pravda! A co zůstane jiného, odejde-li pravda, než bloudění? Kam jste se to jen obrátili?'),
(1397, 10, 33, 'A takto se uskutečňuje slovo Pána tvého nad těmi, kdož nestoudní jsou - že totiž neuvěří!'),
(1398, 10, 34, 'Rci: "Je mezi těmi, jež k Bohu přidružujete, někdo, kdo zahájil první stvoření a potom je opakuje?" A dodej: "Jedině Bůh zahajuje první stvoření a potom je opakuje! Jak jen můžete tak lhát?"'),
(1399, 10, 35, 'Rci: "Je mezi těmi, jež k Bohu přidružujete, někdo, kdo vede k pravdě?" A dodej: "Jedině Bůh vede k pravdě! Zdaž ten, jenž k pravdě vede, je více hoden následování, anebo ten, kdo nevede, byť i byl sám veden? Co je tedy s vámi, že takto usuzujete?"'),
(1400, 10, 36, 'Většina z nich jen domněnky následuje a domněnky nic nezmohou proti pravdě! Však Bůh dobře zná, co dělají.'),
(1401, 10, 37, 'Tento Korán nemohl být vymyšlen nikým kromě Boha; a byl seslán, aby potvrdil pravdivost dřívějších zjevení a aby objasnil Písmo, o němž pochyby není a jež od Pána lidstva veškerého přichází.'),
(1402, 10, 38, 'Anebo jestliže říkají: "On si to vymyslil," odpověz: "Přineste súru podobnou této a zavolejte si na pomoc, koho můžete, kromě Boha, jste-li pravdomluvní!"'),
(1403, 10, 39, 'Avšak oni naopak za lživé prohlašují to, co věděním svým obsáhnout nemohou a k čemu se jim ještě výkladu nedostalo. A takto prohlašovali za lež Písma jiná ti, kdož byli před nimi. A pohleď, jaký byl konec nespravedlivých!'),
(1404, 10, 40, 'A mezi nimi jsou někteří, kdož v ně věří, zatímco jiní mezi nimi v ně vůbec nevěří. A Pán tvůj nejlépe zná ty, kdož pohoršení šíří.'),
(1405, 10, 41, 'A jestliže tě lhářem nazývají, řekni jim: "Já mám své skutky a vy máte své skutky, vy nejste zodpovědní za činy mé a já nejsem zodpovědný za činy vaše."'),
(1406, 10, 42, 'A mezi nimi jsou někteří, kdož ti naslouchají; můžeš však způsobit, aby slyšeli hluší, když k tomu ještě nechápou?'),
(1407, 10, 43, 'A jsou mezi nimi někteří, kteří se na tebe dívají; můžeš však vést slepé, když nevidí?'),
(1408, 10, 44, 'Bůh v ničem neukřivdí lidem, leč lidé věru sami sobě křivdí.'),
(1409, 10, 45, 'A v den, kdy Bůh je shromáždí, bude jim připadat, jako by nestrávili v hrobech ani hodinu jednu ze dne, a poznají se vzájemně. A tehdy utrpí ztrátu ti, kdo setkání s Bohem za lež prohlašovali a správnou cestou vedeni nebyli.'),
(1410, 10, 46, 'Buď ti ukážeme část z toho, čím jsme jim pohrozili, anebo tě k Sobě povoláme dříve; k Nám pak se uskuteční návrat všech a Bůh bude svědčit o tom, co dělali.'),
(1411, 10, 47, 'A národ každý má posla svého; jakmile pak přijde k nim posel jejich, bude mezi nimi spravedlivě rozhodnuto a nebude jim ukřivděno.'),
(1412, 10, 48, 'A ptají se: "Kdy splní se hrozba tato, jste-li pravdomluvní?"'),
(1413, 10, 49, 'Odpověz: "Není v moci mé způsobit si škodu či užitek, leda bude-li Bůh chtít. Národ každý má svou lhůtu, a když se lhůta jeho naplní, neodloží ani neuspíší ji nikdo ni o hodinu jedinou."'),
(1414, 10, 50, 'Rci: "Domníváte se, že až se k vám dostaví trest Jeho v noci či za dne, že hříšníci si budou přát jeho uspíšení?'),
(1415, 10, 51, 'Uvěříte v něj až tehdy, kdy na vás dopadne? Nyní v něj věřte, když jste si přáli jeho uspíšení!"'),
(1416, 10, 52, 'A bude řečeno těm, kdož nespravedliví byli: "Okuste trestu věčného! Což odměňováni jste za něco jiného než za to, co sami jste si vysloužili?"'),
(1417, 10, 53, 'A vyptávají se tě, zda je to pravda. Odpověz: "Ano, při Pánu mém, je to věru pravda a nejste schopni tomu zabránit."'),
(1418, 10, 54, 'A kdyby každá duše nespravedlivá měla vše, co je na zemi, chtěla by se tím vykoupit. A budou tajit lítost, až spatří trest; avšak bude mezi nimi spravedlivě rozsouzeno a nebude jim ukřivděno.'),
(1419, 10, 55, 'Což Bohu nepatří vše, co na nebesích je a na zemi? Což není slib Boží pravdivý? A přece většina z nich to neví.'),
(1420, 10, 56, 'On život i smrt dává a k Němu budete navráceni.'),
(1421, 10, 57, 'Ó lidé, nyní vám přišlo od Pána vašeho varování a uzdravení toho, co v hrudích je skryto, i správné vedení a milosrdenství pro věřící.'),
(1422, 10, 58, 'Rci: "Nechť radují se z dobrodiní a milosrdenství Božího a z toho všeho a to lepší je než to, co shromažďují."'),
(1423, 10, 59, 'Zeptej se: "Co soudíte? Z toho, co Bůh seslal jako obživu, jste si učinili věci dovolené a zakázané." Rci: "Dovolil vám to Bůh; anebo jste si sami vymyslili lež proti Bohu?"'),
(1424, 10, 60, 'A co si budou myslit v den zmrtvýchvstání ti, kdož proti Bohu lži si vymýšleli? Bůh zajisté je pln laskavosti vůči lidem, ale většina z nich jsou nevděčníci.'),
(1425, 10, 61, 'A ať v jakémkoliv budeš postavení a ať cokoliv budeš z Koránu čísti a ať cokoliv budete z činů dělat, My svědky vašimi budeme, když to budete podnikat a neunikne Pánu tvému ani váha zrnka prachu na zemi a na nebi, ba ani nic menšího či většího - aniž by to bylo zapsáno v Písmu zjevném.'),
(1426, 10, 62, 'Nikoliv, přátelé Boží věru nemusí mít strach žádný, a nebudou zarmouceni'),
(1427, 10, 63, 'ti, kdož uvěřili a byli bohabojní.'),
(1428, 10, 64, 'Pro takové platí zvěst radostná v životě pozemském i budoucím. A nezmění se slovo Boží a to štěstí bude nesmírné.'),
(1429, 10, 65, 'Nechť tě nermoutí řeči nevěřících, vždyť moc veškerá Bohu náleží a On slyšící je i vševědoucí.'),
(1430, 10, 66, 'Což Bohu nepatří vše, co na nebesích je i na zemi? Co tedy vlastně následují ti, kdož místo Boha "přidružené" vzývají? Domněnky své jen následují a pouze předpokládají.'),
(1431, 10, 67, 'On je ten, kdo pro vás noc stvořil, abyste v ní odpočívali, a den, abyste v něm zřetelně viděli. A věru jsou v tom znamení pro lid slyšící.'),
(1432, 10, 68, 'Říkají: "Bůh si vzal děti." Sláva Mu, jenž soběstačný je a Jemuž náleží vše, co na nebesích a na zemi je. Máte snad oprávnění k tomuto tvrzení, či chcete říkat o Bohu něco, o čem nemáte ponětí?'),
(1433, 10, 69, 'Rci: "Ti, kdož si o Bohu lži vymýšlejí, nebudou mezi blaženými.'),
(1434, 10, 70, 'Krátké bude jejich života pozemského užívání; k Nám pak se jejich návrat uskuteční a nakonec jim dáme okusit trestu strašného za to, že byli nevěřící.'),
(1435, 10, 71, 'Přednes jim zvěst o Noemovi, když řekl lidu svému: "Lide můj, jestliže je vám nepříjemný můj pobyt mezi vámi a mé připomínky znamení Božích, pak já spoléhám na Boha. Sjednoťte své úmysly i společníky své a kéž vám potom úmysly vaše nezpůsobí starosti! Pak rozhodněte o mně a nenechte mne čekat!'),
(1436, 10, 72, 'Obrátíte-li se zády... nežádal jsem od vás odměnu žádnou, vždyť odměnit mne toliko Bohu náleží a bylo mi přikázáno, abych byl jedním z těch, kdož do vůle Jeho se odevzdávají."'),
(1437, 10, 73, 'Oni však jej za lháře prohlásili a zachránili jsme jej i ty, kdož s ním na lodi byli, a učinili jsme je nástupci na zemi. A utopili jsme ty, kdož znamení Naše za lež prohlašovali a pohleď, jaký byl konec těch, jež marně jsme varovali!'),
(1438, 10, 74, 'Po něm jsme vyslali posly jiné k národům jejich a přišli k nim s důkazy jasnými, avšak lidé nebyli schopni uvěřit v to, co předtím za lež prohlásili. A takto My zapečeťujeme srdce přestupníků!'),
(1439, 10, 75, 'Po nich pak jsme vyslali Mojžíše a Áróna s Našimi znameními k Faraónovi a velmožům jeho, avšak ti pýchou se naduli a lidem hříšným byli.'),
(1440, 10, 76, 'A když k nim přišla pravda od Nás vycházející, zvolali: "Toto je věru jen kouzelnictví zjevné!"'),
(1441, 10, 77, 'Tu otázal se Mojžíš: "Jak můžete říkat toto o pravdě, jež k vám přišla? To že je kouzelnictví? Vždyť kouzelníci věru nebudou patřit mezi blažené!"'),
(1442, 10, 78, 'Odpověděli: "Přišels k nám, abys nás odvrátil od toho, co u otců svých jsme nalezli, a aby vám oběma pak připadla moc nejvyšší v zemi? My nejsme z těch, kdo by vám uvěřili."'),
(1443, 10, 79, 'Rozkázal pak Faraón: "Přiveďte mi všechny kouzelníky učené!"'),
(1444, 10, 80, 'A když se kouzelníci dostavili, pravil jim Mojžíš: "Hoďte to, co hodit chcete!"'),
(1445, 10, 81, 'A když hodili, zvolal Mojžíš: "To, co jste předvedli, je kouzelnictví pouhé, jež Bůh nicotným učiní, vždyť Bůh nedopřeje úspěchu dílu těch, kdož pohoršení šíří,'),
(1446, 10, 82, 'ale Bůh uskutečňuje pravdu slovem Svým, i když se to hříšníkům nelíbí!"'),
(1447, 10, 83, 'A neuvěřil Mojžíšovi nikdo kromě potomstva z vlastního lidu jeho z obavy, že Faraón a velmoži jeho je zkoušce podrobí, neboť Faraón vskutku mocný byl v této zemi a patřil k přestupníkům.'),
(1448, 10, 84, 'I pravil Mojžíš: "Lide můj, věříte-li v Boha, spolehněte se na Něho, jestliže jste se již odevzdali do vůle Jeho."'),
(1449, 10, 85, 'Odpověděli: "Spoléháme se na Boha; Pane náš, nevydávej nás zkouškám národa tyranského,'),
(1450, 10, 86, 'ale zachraň nás z milosrdenství Svého před národem nevěřícím!"'),
(1451, 10, 87, 'A vnukli jsme Mojžíšovi a bratru jeho: "Založte pro lid svůj v Egyptě příbytky a učiňte domy své qiblou a dodržujte modlitbu; a oznam věřícím zvěst radostnou!"'),
(1452, 10, 88, 'Pravil Mojžíš: "Pane náš, Tys dal Faraónovi a velmožům jeho lesk a bohatství v životě pozemském, aby, Pane náš, sváděli z cesty Tvé. Pane náš, znič bohatství jejich a zatvrď srdce jejich a nechť neuvěří, pokud nespatří trest bolestný!"'),
(1453, 10, 89, 'I odpověděl Bůh: "Vyslyšena je prosba vaše; kráčejte oba stezkou přímou a nenásledujte cestu těch, kdož nic nevědí!"'),
(1454, 10, 90, 'A převedli jsme dítka Izraele přes moře, zatímco Faraón a vojska jeho je pronásledovali nespravedlivě a nenávistně. A nakonec, když postihlo jej utopení, zvolal Faraón: "Uvěřil jsem, že není božstva kromě toho, v nějž věří dítka Izraele, a já jsem jedním z těch, kdož do vůle Jeho se odevzdali!"'),
(1455, 10, 91, 'Avšak Bůh pravil: "Teprve nyní?! Ale předtím jsi byl vzpurný a patřils mezi ty, kdož pohoršení šíří.'),
(1456, 10, 92, 'Však dnes tě zachráníme v těle tvém, abys byl znamením pro ty, kdož po tobě přijdou, vždyť věru většina z lidí je lhostejná ke znamením Našim.'),
(1457, 10, 93, 'A usadili jsme dítka Izraele na místě spolehlivém a uštědřili jsme jim obživu výtečnou. A upadli do rozporů teprve tehdy, když dostalo se jim vědění. Avšak Pán tvůj vskutku mezi nimi rozsoudí v den zmrtvýchvstání to, o čem ve sporu byli.'),
(1458, 10, 94, 'Jsi-li na pochybách o tom, co jsme ti seslali, dotaž se těch, kdož Písmo před tebou seslané přednášejí. A věru ti přišla nyní od Pána tvého pravda - nebuď tedy jedním z těch, kdož pochybují.'),
(1459, 10, 95, 'A nebuď též jedním z těch, kdož znamení Boží za lživá prohlásili, abys nebyl z těch, kdož ztrátu utrpěli.'),
(1460, 10, 96, 'Ti, proti nimž se vyplnilo slovo Pána tvého, neuvěří,'),
(1461, 10, 97, 'i kdyby se jim dostalo všech možných znamení, dokud trest bolestný nespatří.'),
(1462, 10, 98, 'Bylo kdy město nějaké, které uvěřilo a jemuž pomohla víra jeho kromě lidu Jonášova? A když uvěřili, odvrátili jsme od nich v životě pozemském trest zahanbující a nechali jsme jim užívati ho po dobu určitou.'),
(1463, 10, 99, 'Kdyby byl Pán tvůj chtěl, věru by byli všichni, kdož na zemi jsou, vesměs uvěřili. Chceš snad donutit lidi, aby se stali věřícími,'),
(1464, 10, 100, 'když není duši žádné dáno, aby uvěřila jinak než z dovolení Boha, a když On uvaluje hněv Svůj na ty, kteří rozum nemají?'),
(1465, 10, 101, 'Rci: "Pohleďte na vše, co na nebesích a na zemi je!" Však co pomohou znamení či varováni lidem, kteří vůbec nevěří?'),
(1466, 10, 102, 'Co jiného mohou očekávat, než dny podobné dnům těch, kdož před nimi již zanikli? Rci: "Čekejte tedy a já také spolu s vámi budu čekat!"'),
(1467, 10, 103, 'My potom posly Své a ty, kdož uvěřili, zachráníme a je Naší povinností věřící zachraňovat.'),
(1468, 10, 104, 'Rci: "Lidé, jste-li na pochybách stran náboženství mého, tedy vězte, že já neuctívám ty, které vy uctíváte místo Boha, nýbrž uctívám Boha, jenž k Sobě vás povolá; a bylo mi poručeno, abych byl jedním z věřících,'),
(1469, 10, 105, 'a bylo mi řečeno: "Obrať tvář svou k náboženství pravému jako haníf a nebuď modloslužebníkem'),
(1470, 10, 106, 'a nevzývej místo Boha to, co ani užitek, ani škodu způsobit nemůže! Jestliže tak učiníš, budeš patřit mezi nespravedlivé."'),
(1471, 10, 107, 'A když se tě Bůh dotkne zlým, nikdo kromě Něho tě toho nezbaví. A když bude pro tebe chtít dobré, nic nemůže odvrátit od tebe milost Jeho, neboť On prokazuje ji těm, komu chce ze služebníků Svých. A On odpouštějící je i slitovný.'),
(1472, 10, 108, 'Rci: "Lidé, nyní k vám přišla věru pravda od Pána vašeho. Kdokoliv po správné cestě kráčí, ten činí tak k prospěchu vlastnímu, zatímco ten, kdo bloudí, ke škodě své tak činí. A já zajisté nejsem nad vámi dozorcem."'),
(1473, 10, 109, 'Následuj tedy to, co ti bylo vnuknuto, a trpělivě vyčkávej, až Bůh bude soudit a Bůh věru nejlepším je soudcem.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Alif lám rá! Toto je Písmo, jehož verše byly potvrzeny a potom srozumitelnými učiněny od Moudrého, Vševědoucího,'),
(1475, 11, 2, 'abyste neuctívali kromě Boha nikoho jiného a já věru jsem od Něho pro vás varovatelem i hlasatelem zvěsti radostné'),
(1476, 11, 3, 'a abyste prosili Pána svého za odpuštění a potom pokání činili; on poskytne vám pak příjemné užívání světa tohoto až do lhůty stanovené a uštědří přízeň Svou každému, kdo zaslouží si ji. Jestliže však se odvrátíte, obávám se pro vás trestu dne těžkého.'),
(1477, 11, 4, 'K Bohu se uskuteční návrat váš a On všech věcí mocen je.'),
(1478, 11, 5, 'Což lidé věru nesvinují hrudi své do záhybů, aby před Ním se ukryli? A byť i se přikrývali oděvy svými, On přesto dobře zná, co tají i co najevo dávají, a On též dobře ví, co skryto je v hrudích.'),
(1479, 11, 6, 'A není na světě zvířete jediného, jehož obživa by od Boha nezávisela; a On zná spočinutí jeho i složení jeho a to vše je v Knize zjevné zapsáno.'),
(1480, 11, 7, 'A On je ten, jenž nebesa a zemi v šesti dnech stvořil, zatímco trůn Jeho se nad vodou vznášel, aby vyzkoušel, kdo z vás lepší bude ve skutcích. A když řekneš: "Po smrti budete vskutku vzkříšeni!", tu odpovídají ti, kdož nevěří: "To kouzelnictví zjevné je toliko!"'),
(1481, 11, 8, 'A jestliže jim odložíme trest až ke lhůtě stanovené, jistě řeknou: "Co jej zadrželo?" Však v den, kdy k nim přijde, nebude nikoho, kdo by jej od nich zadržel, a obklopí je to, čemu se posmívali.'),
(1482, 11, 9, 'Když dáváme člověku okusit milosrdenství od Nás přicházejícího a potom mu je odebereme, tu věru zoufalý je a nevděčný.'),
(1483, 11, 10, 'A když mu dáme okusit přízně poté, co dotklo se jej neštěstí, volá: "Zlé ode mne odešlo!" a pln radosti je a vychloubání,'),
(1484, 11, 11, 'kromě těch, kdož trpěliví byli a zbožné skutky konali, a těm dostane se odpuštění a velké odměny.'),
(1485, 11, 12, 'Možná žes vynechal část toho, co vnuknuto ti bylo, a hruď tvá je stísněna, neboť nevěřící říkají: "Pročpak mu nebyl seslán poklad nějaký či proč s ním nepřišel anděl!" Ty varovatelem jsi pouze, však Bůh ručitelem je všech věcí.'),
(1486, 11, 13, 'Nebo říkají: "On si to vymyslil!" Odpověz: Přineste tedy deset súr podobných tomuto, vámi vymyšlených, a pozvěte si na pomoc, koho jen můžete, kromě Boha, jste-li pravdomluvní!"'),
(1487, 11, 14, 'Jestliže vaší výzvě nevyhoví, vězte, že seslán byl s vědomím Božím a že není božstva kromě Něho. Jste tedy do vůle Boží odevzdáni?'),
(1488, 11, 15, 'Ty, kdož touží po životě pozemském a jeho ozdobách, ty plně odměníme za skutky jejich v něm a nebudou v tom ošizeni.'),
(1489, 11, 16, 'To jsou ti, pro něž v životě budoucím je pouze oheň připraven; marné bude to, co zde vykonali, a nicotné bude to, co zde dělali.'),
(1490, 11, 17, 'Zdaž ten, jenž se opírá o jasný důkaz od Pána svého, zatímco svědek od Něho vyslaný jej sděluje, a před ním bylo Písmo Mojžíšovo jako vedení a milosrdenství... ti věří v něj! Však kdokoliv ze spojenců v něj nevěří, ten s ohněm bude mít schůzku. Nebuď tedy o něm na pochybách, neboť to pravda je od Pána tvého, však většina lidí nevěří.'),
(1491, 11, 18, 'Kdo je nespravedlivější než ten, kdo lži si proti Bohu vymýšlí? Takoví budou před Pána svého předvedeni a svědkové promluví: "Toto jsou ti, kdož o Pánu svém lži pronášeli!" Zdaž prokletí Boží nepadne na nespravedlivé,'),
(1492, 11, 19, 'kteří z cesty Boží svádějí a pokřivit si ji přejí a v život budoucí nevěří?'),
(1493, 11, 20, 'Tito nemohou nikterak uniknout zásahu Božímu na zemi a nenaleznou kromě Boha ochránce žádného. A trest pak bude jim zdvojnásoben, vždyť slyšet ani vidět schopni nebyli.'),
(1494, 11, 21, 'A to jsou ti, kteří sami sobě ztrátu způsobili, a opustilo je to, co si vymyslili,'),
(1495, 11, 22, 'a není pochyby, že v životě budoucím ještě větší ztrátu utrpí.'),
(1496, 11, 23, 'Však ti, kdož uvěřili, zbožné skutky konali a pokorní před Pánem svým byli, ti věru se stanou ráje obyvateli a budou v něm nesmrtelní.'),
(1497, 11, 24, 'A příměr těchto dvou skupin je jako příměr slepého a hluchého s vidoucím a slyšícím; zdaž mohou být si rovni v tomto příměru? Což se tedy nevzpamatujete?'),
(1498, 11, 25, 'I vyslali jsme Noema k lidu jeho, aby pravil jim: "Lide můj, já jsem pro vás varovatelem zjevným,'),
(1499, 11, 26, 'abyste neuctívali kromě Boha nikoho! Já věru obávám se pro vás trestu dne, jenž bude bolestný."'),
(1500, 11, 27, 'A řekli velmoži, ti z lidu jeho, kdož nevěřili: "Nevidíme v tobě nic jiného než smrtelníka nám podobného. A vidíme, že tě z nás následují bez rozmyšlení jen ti nejnižší, a nevidíme, že nějakou přednost nad námi měli byste, ba spíše soudíme, že vy jen lháři jste!"'),
(1501, 11, 28, 'Noe odvětil: "Lide můj, což nechápete? Řídím-li se jasným důkazem od Pána svého, jenž uštědřil mi milosrdenství Své, vůči němuž vy slepí jste, mám snad zapotřebí vám je vnucovat, když k němu odpor cítíte?'),
(1502, 11, 29, 'Lide můj, nežádám od vás za to jmění žádné, odměna má je toliko u Boha. Neodeženu nikdy ty, kdož uvěřili - ti věru setkají se s Pánem svým, však vy jste, jak vidím, lidem nevědomým.'),
(1503, 11, 30, 'Lide můj, kdo pomůže mi proti Bohu, jestliže je odeženu? Což se nevzpamatujete?'),
(1504, 11, 31, 'Nepravím vám:, Mám u sebe pokladnice Boží? ani:, Znám věci nepoznatelné?, a neříkám vám ani, že anděl jsem. A nepravím o těch, které oči vaše přezírají, že Bůh jim nic dobrého nepopřeje. Však Bůh zná nejlépe, co v duších jejich je skryto; a věru bych byl, kdybych to říkal, jedním z nespravedlivých."'),
(1505, 11, 32, 'Odpověděli: "Noe, již dost ses hádal s námi, a ještě spor s námi rozmnožuješ. Přines nám tedy to, co nám slibuješ, jsi-li z pravdomluvných!"'),
(1506, 11, 33, 'Pravil: "Bůh sám vám to přinese, bude-li si to přát, a zabránit tomu nebudete schopni.'),
(1507, 11, 34, 'A neprospěje vám dobrá rada má, budu-li chtít vám poradit, jestliže si Bůh bude přát uvrhnout vás v blud; On Pánem je vaším a k Němu budete navráceni."'),
(1508, 11, 35, 'Řeknou-li: "On sám si to vymyslil", tedy odpověz: "Jestliže jsem si to vymyslil, hřích můj na mou hlavu padne, avšak já odpovědnost nemám za to, v čem vy hřešíte!"'),
(1509, 11, 36, 'I bylo vnuknuto Noemu: "Z lidu tvého již neuvěří nikdo kromě těch, kdo již uvěřili. Nermuť se tedy nad tím, co dělají!'),
(1510, 11, 37, 'Postav loď před zrakem Naším a podle vnuknutí Našeho a nepřimlouvej se u Mne za ty, kdož nespravedliví byli - ti budou utopeni."'),
(1511, 11, 38, 'A stavěl loď, a kdykoliv okolo šli velmoži z lidu jeho, smáli se mu. I říkal: "Jen se mi smějte, však i my se vám budeme jednou smát tak, jako vy jste se smáli mně,'),
(1512, 11, 39, 'a tehdy se dozvíte, koho postihne trest zahanbující a na koho dopadne trest trvalý!"'),
(1513, 11, 40, 'Když pak přišel rozkaz Náš a vzkypěla pec, pravili jsme Noemovi: "Nalož na ni od každého druhu pár, rodinu svou kromě toho, proti němuž již dříve bylo slovo vyřčeno a ty, kteří uvěřili!" A uvěřili s ním jen nemnozí.'),
(1514, 11, 41, 'Pravil Noe: "Vstupte do ni! Nechť ve jménu Boha se plavba její i zakotvení uskuteční! Vždyť Pán můj je věru odpouštějící i slitovný!"'),
(1515, 11, 42, 'A plula s nimi po vlnách jako hory vysokých a Noe syna svého zavolal, jenž na místě vzdáleném stál: "Synáčku, nastup spolu s námi a nezůstávej s nevěřícími!"'),
(1516, 11, 43, 'I odpověděl: "Uchýlím se na horu, jež před vodou mne zachrání." I zvolal Noe: "Nezachrání se dnes nikdo před rozkazem Božím kromě toho, nad nimž se slitoval." A rozdělily je vlny a syn jeho byl mezi utopenými.'),
(1517, 11, 44, 'A bylo řečeno: "Země, pohlť vodu svou! Nebe, zadrž déšť!" I opadla voda, rozkaz byl splněn a loď uvízla na hoře al-Džúdí. Pak bylo řečeno: "Pryč s lidem nespravedlivým!"'),
(1518, 11, 45, 'A zvolal Noe k Pánu svému a řekl: "Pane, syn můj zajisté patřil k rodině mé a slib Tvůj je věru pravdivý a Tys ze soudců nejspravedlnější."'),
(1519, 11, 46, 'Pravil Bůh: "Noe, on ani k rodině tvé nepatřil, vždyť konání jeho bylo nesprávné. A nežádej ode Mne to, o čem vědění nemáš, a napomínám tě, abys nebyl jedním z pošetilých!"'),
(1520, 11, 47, 'Noe odpověděl: "Pane můj, utíkám se k Tobě, abych se již více netázal na to, o čem vědění nemám. A jestliže mi neodpustíš a neslituješ se nade mnou, budu věru patřit mezi ty, kdož ztrátu utrpěli."'),
(1521, 11, 48, 'A bylo řečeno: "Noe, vystup z lodi, provázen pozdravem od Nás, a požehnání budiž s tebou i s národy, jež jsou s tebou; a jsou národy, jimž uštědříme užívání života pozemského a jichž se potom dotkne od Nás trest bolestný."'),
(1522, 11, 49, 'A toto je jedna ze zvěstí skrytých, které ti dáváme jako zjevení a které ani ty, ani lid tvůj jste před tím neznali. Buď tedy trpělivý, vždyť bohabojným patří konec šťastný!'),
(1523, 11, 50, 'A poslali jsme k ´Ádovcům bratra jejich Húda, jenž pravil jim: "Lide můj, uctívejte Boha a nemějte božstvo jiné kromě Něho - vždyť vy si jen vymýšlíte.'),
(1524, 11, 51, 'Lide můj, nežádám na vás odměnu žádnou, odměna má je jedině u toho, jenž mě stvořil. Což to nechápete?'),
(1525, 11, 52, 'Lide můj, proste Pána svého o odpuštění a potom pokání čiňte! On sešle vám z nebe déšť proudem hojným a přidá sílu k vaší síle. Neobracejte se tedy zády způsobem hříšným!"'),
(1526, 11, 53, 'Odpověděli: "Húde, nepřinesl jsi nám žádný důkaz jasný a my neopustíme božstva svá jen na slovo tvé a my ti nevěříme.'),
(1527, 11, 54, 'My pouze říkáme, že některé z božstev našich tě navštívilo zlým!" Pravil: "Odvolávám se k Bohu co svědku a též vy buďtež svědky, že odpovědnost nemám za to, co k Bohu přidružujete'),
(1528, 11, 55, 'vedle Něho. Chystejte si vespolek proti mně své úklady a čekat mne nenechte!'),
(1529, 11, 56, 'Já na Boha spoléhám, Pána svého i vašeho; a není zvířete jediného, aby je nedržel za kštici jeho. A Pán můj věru po stezce přímé kráčí.'),
(1530, 11, 57, 'Jestliže se odvrátíte zády, nevadí, já předal jsem vám již to, s čím poslán jsem byl k vám. Pán můj vás může jiným lidem nahradit, však vy Jemu nikterak nemůžete uškodit; Pán můj je věru strážcem všech věcí."'),
(1531, 11, 58, 'A když přišel rozkaz Náš, zachránili jsme Húda a ty, kdož spolu s ním uvěřili, z milosrdenství od Nás přicházejícího a zachránili jsme je od trestu přísného.'),
(1532, 11, 59, 'Tito ´Ádovci popírali znamení Pána svého, vzpírali se poslu Jeho a následovali rozkazu každého tyrana zarputilého.'),
(1533, 11, 60, 'A na tomto světě i v den zmrtvýchvstání doprovázet je bude prokletí. Ó ano, ´Ádovci věru v Pána svého neuvěřili! Ó ano, pryč tedy s ´Ádovci, lidem Húdovým!'),
(1534, 11, 61, 'A k Thamúdovcům jsme vyslali bratra jejich Sáliha, jenž pravil jim: "Lide můj, uctívejte Boha a nemějte božstvo jiné kromě Něho; On dal vám vzejít ze země a usídlil vás na ní. O odpuštění Jej proste a pokání čiňte, neb Pán můj blízký je a popřeje sluchu prosícím."'),
(1535, 11, 62, 'I řekli: "Sálihu, byls jedním z nás, v něhož jsme dříve naděje své vkládali; chceš nám nyní zakazovat uctívání toho, co již otcové naši uctívali? A máme značné pochybnosti stran toho, k čemu nás vyzýváš."'),
(1536, 11, 63, 'Odpověděl: "Lide můj, co si myslíte? Mám-li jasný důkaz od Pána svého a dostalo-li se mi od Něho milosrdenství, kdo mi tedy proti Němu pomůže, jestliže Jej neuposlechnu? Tím rozmnožili byste jen mou ztrátu!'),
(1537, 11, 64, 'Lide můj, hle, toto je velbloudice Boží, jež pro vás je znamením. Nechte ji pást na zemi Boží a nečiňte jí nic zlého, aby se vám nedostalo trestu blízkého!"'),
(1538, 11, 65, 'Avšak oni ji podřízli a Sálih zvolal: "Užívejte si života v příbytcích svých ještě tři dny a toto není slib vylhaný."'),
(1539, 11, 66, 'A když přišel rozkaz Náš, zachránili jsme Sáliha a ty, kdož spolu s nim uvěřili, z milosrdenství od Nás přicházejícího před hanbou dne onoho. A Pán tvůj věru silný je i mocný.'),
(1540, 11, 67, 'A postihlo ty, kdož nespravedliví byli, vzkřiknutí jediné a jitro nalezlo je v domech jejich tvářemi k zemi ležící,'),
(1541, 11, 68, 'jako by v nich nikdy nebyli žili. Ó ano, Thamúdovci věru v Pána svého nevěřili! Ó ano, pryč tedy s Thamúdovci!'),
(1542, 11, 69, 'A již kdysi přišli vyslanci Naši se zvěstí radostnou k Abrahamovi a řekli: "Mír s tebou!" a on odvětil: "Mír s vámi!" a přinesl tele pečené bez meškání.'),
(1543, 11, 70, 'Když pak spatřil, že ruce jejich se po něm nevztahují, nelíbilo se mu to a pocítil strach před nimi. I řekli: "Neboj se, my k lidu Lotovu jsme vysláni."'),
(1544, 11, 71, 'A žena jeho, jež vedle stála, se smála, zatímco on seděl; a oznámili jsme jí o Izákovi a po Izákovi o Jakubovi zvěst radostnou.'),
(1545, 11, 72, 'I zvolala: "Běda mi, jak porodit mám, když již stařena jsem a tento manžel můj stařec? To věru by bylo věcí podivuhodnou!"'),
(1546, 11, 73, 'Řekli: "Jsi snad udivena rozhodnutím Božím? Nechť na vás spočine milosrdenství Boží i požehnání Jeho, lidé domu tohoto a On chvályhodný je i slávyplný!"'),
(1547, 11, 74, 'Když pak strach opustil Abrahama a on pochopil zvěst radostnou, jal se s Námi přít o lid Lotův,'),
(1548, 11, 75, 'neboť Abraham byl věru člověk soucitný, laskavý a kajícný.'),
(1549, 11, 76, 'I řekli vyslanci: "Abrahame, přestaň s tím, vždyť rozkaz Pána tvého byl již vydán a dolehne na ně trest neodvratný."'),
(1550, 11, 77, 'A když přišli vyslanci Naši k Lotovi, udělalo se mu nevolno a pojala jej úzkost o ně, že nebude je moci chránit, a pravil: "Tohle určitě bude den obtížný!"'),
(1551, 11, 78, 'A seběhl se u něho v chvatu lid jeho a oni již dříve činy hnusné páchali a řekl jim Lot: "Lide můj, zde máte dcery mé, ony jsou pro vás čistší. Bojte se Boha a nezahanbujte mne v hostech mých. Což není mezi vámi jediný muž spravedlivý?"'),
(1552, 11, 79, 'Zvolali: "Ty přece víš, že na dcery tvé si žádné nároky neděláme, a ty dobře víš, čeho si žádáme!"'),
(1553, 11, 80, 'Odpověděl: "Kéž bych měl dosti síly proti vám, či kéž bych se mohl utéci k opoře mocné!"'),
(1554, 11, 81, 'Pravili vyslanci: "Lote, jsme vyslanci Pána tvého a tihle se k tobě nedostanou. Vydej se na cestu s rodinou svou po uplynutí části noci a nikdo z vás nechť se neobrací - jen žena tvá tak učiní, ji postihne to, co i ostatní postihne. A čas pro ně stanovený za jitra nastane, což není již jitro blízko?"'),
(1555, 11, 82, 'A když přišel rozkaz Náš, obrátili jsme město jejich naruby a dali jsme na ně pršet kamení z hlíny upěchované,'),
(1556, 11, 83, 'u Pána tvého označené - a není to vzdáleno od nespravedlivých.'),
(1557, 11, 84, 'A k Madjanovcům jsme vyslali bratra jejich Šu´ajba a on pravil jim: "Lide můj, uctívejte Boha a nemějte božstvo vedle Něho. A nedávejte špatné míry a váhy! Já vidím, že v blahobytu si žijete, však obávám se pro vás trestu dne, jenž vše obklopí.'),
(1558, 11, 85, 'Lide můj, měřte a važte naprosto spravedlivě a nepoškozujte v ničem lidi a nepůsobte na zemi zlo šíříce pohoršení!'),
(1559, 11, 86, 'Dobrota Boží je pro vás lepší, jste-li věřící, a já nejsem věru nad vámi strážcem žádným."'),
(1560, 11, 87, 'Odpověděli: "Šu´ajbe, nařizuje ti snad modlitba tvá, abychom opustili to, co otcové naši uctívali, anebo abychom přestali nakládat s majetkem svým, jak chceme? Tys přece jinak člověk mírný a rozvážný!"'),
(1561, 11, 88, 'I pravil Šu´ajb: "Lide můj, což nevidíte, že mám jasný důkaz od Pána svého a že mi uštědřil od Sebe obživu překrásnou? Já nechci se s vámi příti o to, co vám zakazuji, já pouze chci, abyste se polepšili, pokud jsem toho schopen, a úspěch můj jen od Boha závisí, na Něj se spoléhám a k Němu se kajícně obracím.'),
(1562, 11, 89, 'Lide můj, nechť odpor váš vůči mně na vás neuvalí hřích, abyste nebyli postiženi tím, co již postihlo lid Noemův či lid Lotův nebo lid Sálihův - a lid Lotův není od vás příliš vzdálen!'),
(1563, 11, 90, 'Pána svého o odpuštění proste a pokání před Ním čiňte? vždyť Pán můj slitovný je i láskyplný!"'),
(1564, 11, 91, 'Odpověděli: "Šu´ajbe, chápeme jen málo z toho, co říkáš, avšak vidíme věru, žes slabý mezi námi, a nebýt rodu tvého, věru bychom tě ukamenovali, neboť nejsi vůči nám příliš mocný."'),
(1565, 11, 92, 'Pravil Šu´ajb: "Lide můj, zdaž rod můj je pro vás mocnější než Bůh, takže jste Jej nechali za sebou nestarajíce se o něj? Pán můj však věru objímá ve vědění Svém vše, co děláte.'),
(1566, 11, 93, 'Lide můj, jednejte podle stanoviska svého, já také tak budu jednat a potom poznáte, koho postihne trest zahanbující a kdo vlastně lhářem byl. Dávejte pozor, já také budu dávat pozor s vámi!"'),
(1567, 11, 94, 'A když přišel rozkaz Náš, zachránili jsme Šu´ajba a ty, kdož spolu s ním uvěřili, z milosrdenství od Nás přicházejícího. A postihlo ty, kdož nespravedliví byli, vzkřiknutí jediné a jitro nalezlo je v domech jejich ležící tvářemi k zemi,'),
(1568, 11, 95, 'jako by v nich nikdy nebyli žili. Ó ano, pryč s Madjanovci, tak jako bylo řečeno: pryč s Thamúdovci!'),
(1569, 11, 96, 'A poslali jsme Mojžíše se znameními Našimi a zjevnou pravomocí'),
(1570, 11, 97, 'k Faraónovi a velmožům jeho; ti však následovali rozkazu Faraónova, ačkoliv rozkaz ten k správnému nemohl vésti.'),
(1571, 11, 98, 'A půjde Faraón v čele lidu svého v den zmrtvýchvstání a dovede je k místu, kde napojí je ohněm - a jak hnusné je napajedlo, z něhož píti budou!'),
(1572, 11, 99, 'A byli prokletím pronásledováni na tomto světě a budou tak i v den zmrtvýchvstání - jak hnusný je dar, jenž bude jim darován!'),
(1573, 11, 100, 'A toto je část zvěstí o městech, které ti vyprávíme: některá z nich ještě stojí, jiná však byla smetena.'),
(1574, 11, 101, 'My neukřivdili jsme obyvatelům jejich, nýbrž oni sami sobě ukřivdili, a nebyla jim k ničemu božstva jejich, jež vedle Boha vzývali, když naplnil se rozkaz Pána tvého, ba naopak, pouze zkázu jejich rozmnožila.'),
(1575, 11, 102, 'Takový je zásah Pána tvého, když města zasahuje, jež nespravedlivá jsou. A zásah Jeho věru bolestný je a prudký!'),
(1576, 11, 103, 'A zajisté je v tom znamení pro toho, kdo bojí se trestu dne posledního; a to bude den, v němž lidé budou shromážděni, a bude to den, v němž všichni budou přítomni,'),
(1577, 11, 104, 'Námi pouze do lhůty stanovené odložený.'),
(1578, 11, 105, 'A v den, až se dostaví, nepromluví duše žádná bez Jeho svolení a budou mezi nimi nešťastní i blažení.'),
(1579, 11, 106, 'Pokud se týká nešťastníků, ti do ohně přijdou a v něm bude pro ně jen sténání a úpěni'),
(1580, 11, 107, 'a zůstanou tam navěky, dokud nebesa a země budou trvat a dokud Pán tvůj nebude chtít jinak; a Pán tvůj je věru vykonavatelem toho, co si přeje.'),
(1581, 11, 108, 'Pokud se týká blažených, ti do zahrad se dostanou a nesmrtelní tam zůstanou, dokud nebesa a země budou trvat a dokud Pán tvůj nebude chtít jinak a to dar bude nezkrácený.'),
(1582, 11, 109, 'A nepochybuj nikterak ohledně toho, co lidé tito uctívají: vždyť uctívají jen to, co před nimi již otcové jejich uctívali. A My dáme jim podíl jejich přesně, bez zkrácení.'),
(1583, 11, 110, 'A zajisté jsme již kdysi dali Mojžíšovi Písmo a vznikly o něm spory; a kdyby nebylo slova, jež předtím již přišlo od Pána tvého, bylo by mezi nimi již rozhodnuto. A oni stále o něm vážně pochybují.'),
(1584, 11, 111, 'Pán tvůj pak zajisté přesně každému za skutky jeho odplatí a On dobře je zpraven o tom, co dělají.'),
(1585, 11, 112, 'Jdi přímo tak, jak přikázáno ti bylo, a taktéž nechť činí ti, kdož s tebou k Bohu se obrátili! A nebuďte vzpurní, vždyť On jasně zří vše, co konáte.'),
(1586, 11, 113, 'Neopírejte se o ty, kdož nespravedliví jsou, aby vás nepohladil oheň pekelný. A nemáte kromě Boha ochránců žádných a jinou pomoc nedostanete.'),
(1587, 11, 114, 'Konej modlitbu na obou koncích dne i v nočních hodinách, blízkých k ránu a večeru! A dobré skutky věru rozptylují špatné - a toto je připomenutí pro ty, kdož Boha vzpomínají.'),
(1588, 11, 115, 'Buď neochvějný, neboť Bůh nedopustí, aby ztratila se odměna těch, kdož dobré konají.'),
(1589, 11, 116, 'Proč bylo mezi pokoleními předešlými tak málo těch, kdož byli poctiví a bránili šíření pohoršení na zemi? A ty jsme zachránili. Zatímco nespravedliví následovali to, čeho se jim dostalo z přepychu, a stali se hříšníky.'),
(1590, 11, 117, 'Pán tvůj pak není takový, aby zničil města ona nespravedlivě, zatímco obyvatelé jejich skutky zbožné konali.'),
(1591, 11, 118, 'Kdyby Pán tvůj chtěl, byl by věru učinil lidstvo národem jediným, avšak oni stále jsou ve sporech'),
(1592, 11, 119, 'kromě těch, nad nimiž se Pán tvůj slitoval; a kvůli tomu je stvořil. Nechť tedy splní se slovo Pána tvého: "Já věru peklo džiny i lidmi společně naplním!"'),
(1593, 11, 120, 'A vyprávíme ti toto vše ze zvěstí o poslech, abychom tím povzbudili srdce tvé a aby s tím k tobě přišla pravda i varování a připomenutí věřícím.'),
(1594, 11, 121, 'Rci těm, kdož nevěří: "Jednejte podle stanoviska svého, my také podle svého budeme jednat!'),
(1595, 11, 122, 'Vyčkávejte a my také budeme vyčkávat!"'),
(1596, 11, 123, 'A Bohu náleží nepoznatelné na nebesích i na zemi a k Němu se vrací rozkazy všechny. Uctívej Jej tedy a na Něj spoléhej - a není Pánu tvému lhostejné to, co děláte!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Alif lám rá! Toto jsou Písma zjevného znamení,'),
(1598, 12, 2, 'seslali jsme je jako Korán arabský - snad budete rozumní!'),
(1599, 12, 3, 'Tím, že ti zjevujeme tento Korán, vyprávíme ti příběh nejkrásnější, zatímco dříve byls věru jedním z těch, kdož o tom neměli tušení.'),
(1600, 12, 4, 'Hle, pravil Josef otci svému: "Otče můj, spatřil jsem ve snu jedenáct hvězd, slunce i měsíc a zřel jsem, jak přede mnou se klaní."'),
(1601, 12, 5, 'I odpověděl otec jeho: "Synáčku můj, nevyprávěj bratřím svým sen svůj, jinak proti tobě úklady nastrojí, vždyť satan je člověka nepřítel zjevný.'),
(1602, 12, 6, 'A takto si tě Pán tvůj vyvolí a záhady tě vykládat naučí a tak přízeň svou vůči tobě i rodu Jakubovu završí, stejně jako již dříve ji dovršil vůči tvým dvěma předkům, Abrahamovi a Izákovi. Pán tvůj věru vševědoucí je i moudrý."'),
(1603, 12, 7, 'A stali se Josef a bratři jeho znameními pro ty, kdož se táží.'),
(1604, 12, 8, 'Hle, řekli bratři jeho: "Josef a bratr jeho jsou věru otci našemu milejší než my, ačkoliv jedno příbuzenstvo jsme - a otec náš je věru v bludu zjevném.'),
(1605, 12, 9, 'Zabme tedy Josefa nebo jej vzdalme ze země a tvář otcova se rozzáří jen pro nás a my potom se lidmi bezúhonnými staneme."'),
(1606, 12, 10, 'Pravil jeden z nich: "Nezabíjejme Josefa, nýbrž vhoďme jej do hlubin studny a nějací cestující jej pak vysvobodí, učiníme-li tak."'),
(1607, 12, 11, 'A řekli: "Otče náš, proč nám nedůvěřuješ ohledně Josefa, vždyť my věru jsme jeho rádci upřímnými.'),
(1608, 12, 12, 'Pošli jej zítra s námi, ať povyrazí se a pohraje si a my budeme na něj dobrý pozor dávat!"'),
(1609, 12, 13, 'Odpověděl: "Rmoutí mě, že jej odvádíte, a mám strach, že by jej vlk mohl sežrat, zatímco vy se nebudete o něj starat."'),
(1610, 12, 14, 'Řekli: "Kdyby jej měl sežrat vlk, když je nás takový houf, bylo by to s námi velmi špatné!"'),
(1611, 12, 15, 'A když s ním odešli a domlouvali se, že vhodí jej do hlubin studně, vnukli jsme mu: "Ty zpravíš je o tomto činu jejich ve chvíli, kdy nebudou tušit, oč jde."'),
(1612, 12, 16, 'A vrátili se navečer s pláčem k otci svému'),
(1613, 12, 17, 'a řekli: "Otče náš, odešli jsme, abychom závodili v běhu a nechali jsme Josefa u věcí našich a sežral jej vlk. Ale ty nám neuvěříš, i kdybychom hovořili pravdu."'),
(1614, 12, 18, 'A předložili jeho košili, na níž byla krev falešná; i zvolal otec jejich: "Ó nikoliv, to duše vaše vás svedly ve věci té, leč trpělivost je překrásná a u Boha je třeba hledat útěchu před tím, co mi namlouváte."'),
(1615, 12, 19, 'A přišli cestující a poslali muže svého, aby načerpal vody; když pak vhodil měch svůj do studny, vykřikl: "Dobrá zpráva! Tady je nějaký hoch!" A vzali jej do zajetí jako zboží a Bůh dobře věděl, co dělají.'),
(1616, 12, 20, 'Prodali jej pak za nízkou cenu několika dirhamů odpočítaných a byli v tom zdrženliví.'),
(1617, 12, 21, 'A pravil ten Egypťan, který jej koupil, manželce své: "Ucti jej slušným ubytováním, možná že nám bude užitečný anebo jej za vlastního syna přijmeme." Takto jsme usadili Josefa v zemi té, abychom jej naučili záhady vykládat. Tak Bůh vítězně dovršuje dílo Své, ale většina lidí to neví.'),
(1618, 12, 22, 'A když pak dosáhl dospělosti, darovali jsme mu moudrost a vědění, neboť takto My odměňujeme ty, kdož dobro konají.'),
(1619, 12, 23, 'A snažila se jej ta, v jejímž domě byl, svést a zavřela dveře všechny a řekla mu: "Pojď sem!" I zvolal Josef: "Bůh mě chraniž!, On je mým Pánem a uštědřil mi překrásný pobyt. Vždyť hříšníci věru nebudou blaženi!"'),
(1620, 12, 24, 'A byla by podlehla vášni své a i on byl by podlehl, kdyby byl neviděl důkaz od Pána svého. A takto jsme od něho odvrátili špatnost a smilstvo, neboť on patřil k Našim služebníkům upřímným.'),
(1621, 12, 25, 'A oba běželi jako o závod ke dveřím a roztrhla košili jeho na zádech; u dveří pak potkali jejího manžela, i vykřikla: "Jak odměněn má být ten, jenž chtěl se dopustit špatného na rodině tvé, ne-li vězením či trestem bolestným?"'),
(1622, 12, 26, 'I zvolal Josef: "To ona mne sváděla." A dosvědčil pak svědek z rodiny její: "Je-li košile roztržena vpředu, tedy ona pravdu dí a on ze lhářů je jedním.'),
(1623, 12, 27, 'Však je-li košile vzadu roztržena, tedy lže ona a on je pravdomluvný."'),
(1624, 12, 28, 'A když manžel uviděl, že košile vzadu je roztržena, zvolal: "Tohle je věru jeden z vašich ženských úkladů, vždyť lest vaše zajisté je nesmírná!'),
(1625, 12, 29, 'Josefe, odvrať mysl svou od toho a ty, ženo, pros za odpuštění viny své, neboť jsi vskutku žena hříšná!"'),
(1626, 12, 30, 'A říkaly ženy v městě tom: "Velmožova žena sváděla mladého sluhu svého, jenž vzbudil lásku vášnivou v ní. A věru ji vidíme ve zjevném poblouznění."'),
(1627, 12, 31, 'Když uslyšela jejich pomluvy lstivé, poslala k nim a připravila pro ně pomeranče a každé z nich pak dala nůž. Pak zvolala: "Vejdi k nim, Josefe!" A když jej spatřily, nadchly se jím tak, že až ruce své si pořezaly a vykřikly: "Chraň nás Bůh; to smrtelník věru nemůže být ani, to anděl jest vznešený!"'),
(1628, 12, 32, 'Pravila: "Toto je ten, kvůli němuž jste mne hanily. Vskutku jsem jej sváděla, avšak on svodům odolal. Jestliže však nyní nesplní to, co poručím, bude věru uvězněn a stane se opovrženým."'),
(1629, 12, 33, 'I zvolal Josef: "Pane můj, vězení je mi milejší než to, k čemu mne vyzývají. Jestliže ode mne neodvrátíš úklady jejich, zatoužím po nich a z nerozumných budu jedním."'),
(1630, 12, 34, 'I vyslyšel jej Pán jeho a odvrátil od něho úklady jejich - a On zajisté je slyšící i vševědoucí.'),
(1631, 12, 35, 'Později, když viděli znamení, se jim uzdálo, aby jej na čas uvěznili.'),
(1632, 12, 36, 'A spolu s ním přišli do vězení dva mladící a jeden z nich prohlásil: "Viděl jsem se ve snu, jak víno vytlačuji." A druhý pravil: "Viděl jsem se ve snu, jak nesu na hlavě chleby, z nichž klovou ptáci. Sděl nám, Josefe, výklad toho, neb vidíme, že patříš mezi lidi dobro konající."'),
(1633, 12, 37, 'I odpověděl: "Předtím než dostanete stravu, jež vám je dávána, sdělím vám výklad toho, dříve než ony sny se uskuteční. A toto je to, čemu mne naučil Pán můj, a opustil jsem náboženství lidí, kteří v Boha nevěří a život posmrtný popírají.'),
(1634, 12, 38, 'Pak následoval jsem náboženství předků svých, Abrahama, Izáka a Jakuba. A nehodí se pro nás, abychom cokoliv k Bohu přidružovali a v tom milost Boží je k nám i lidem, však mnozí lidé jsou nevděční.'),
(1635, 12, 39, 'Přátelé moji vězeňští, jsou snad rozdělení páni lepší než Bůh jediný, vše podmaňující?'),
(1636, 12, 40, 'Ti, které uctíváte vedle Něho, nejsou leč jména, jež dali jste jim vy i otcové vaši, a Bůh ohledně nich neseslal žádné zplnomocnění. Rozhodnutí náleží jedině Bohu a On poručil, abyste neuctívali nikoho kromě Něho. A to je náboženství neměnné, však většina lidí to neví.'),
(1637, 12, 41, 'Přátelé moji vězeňští, jeden z vás bude napájet pána svého vínem, a pokud o druhého jde, ten bude ukřižován a ptáci mu budou klovat do hlavy. A rozhodnuta je již věc, o níž jste žádali dobrozdání."'),
(1638, 12, 42, 'A řekl tomu, o němž se domníval, že bude zachráněn: "Zmiň se o mně pánu svému!" Však satan způsobil, že zapomněl vzpomenout Pána svého, a Josef zůstal několik let ve vězení.'),
(1639, 12, 43, 'Pravil král: "Viděl jsem ve snu sedm tučných krav, jež požíralo sedm krav hubených; a viděl jsem sedm zelených klasů a sedm klasů uschlých. Velmožové, vyložte mi sen můj, jste-li výkladu snů schopni."'),
(1640, 12, 44, 'I řekli: "Popletené sny to jsou a my ve výkladu snů se nevyznáme."'),
(1641, 12, 45, 'Tu pravil ten, jenž zachránil se z těch dvou vězňů, vzpomenul si po čase: "Já sdělím vám výklad toho snu, pošlete-li mne."'),
(1642, 12, 46, '"Josefe spravedlivý, vylož nám sen o sedmi kravách tučných, jež budou sežrány sedmi kravami hubenými, a sen o sedmi klasech zelených a sedmi klasech uschlých! Možná, že k lidem se vrátím a možná že vědět budou."'),
(1643, 12, 47, 'Odpověděl Josef: "Budete sít sedm let podle zvyku svého a to, co sklidíte, ponecháte v klasech kromě té malé části, již sníte.'),
(1644, 12, 48, 'A poté později přijde sedm let suchých, jež pozrou vše, co předtím jste shromáždili, kromě malé části, již stranou uschováte.'),
(1645, 12, 49, 'A po tomto přijde jeden rok, v němž lidem dostane se deště a v němž opět tlačit víno budou!"'),
(1646, 12, 50, 'A poručil král: "Přiveďte ho!" Když přišel posel k Josefovi, řekl mu tento: "Vrať se k pánu svému a zeptej se ho, jaké byly záměry těch žen, jež ruce si pořezaly. Pán můj o úkladech jejich dobře ví."'),
(1647, 12, 51, 'Král pak otázal se těch žen: "Jaké byly vaše úmysly, když Josefa jste sváděly?" Odpověděly: "Chraň nás Bůh, my nic špatného o něm nevíme." A žena velmožova řekla: "Nyní pravda jasně vystoupila, byla jsem to já, která jej sváděla, a on věru mezi pravdomluvné patří."'),
(1648, 12, 52, '"A tak je to, aby věděl, že jsem jej neoklamal ohledně skrytého a že Bůh nepřivede úklad proradných ke zdaru.'),
(1649, 12, 53, 'Netvrdím, že duše má je nevinná, vždyť věru duše neustále nabádá ke špatnostem, pokud se Pán můj neslituje - a Pán můj věru je odpouštějící, slitovný."'),
(1650, 12, 54, 'I pravil král: "Přiveďte mi jej, já hodlám si jej pro sebe podržet!" A když s ním promluvil, prohlásil: "Ode dneška budeš mít u nás postavení vysoké i důvěru."'),
(1651, 12, 55, 'I odpověděl Josef: "Pověř mne správou nad sýpkami země, neb já strážce jsem pečlivý a moudrý."'),
(1652, 12, 56, 'A takto jsme upevnili Josefovo postavení v zemi té a mohl se usadit, kdekoli se mu zachtělo. A my zahrnujeme milosrdenstvím Svým, koho chceme, a nedovolíme, aby se ztratila odměna těm, kdož dobro konají.'),
(1653, 12, 57, 'Však odměna života onoho lepší je pro ty, kdož uvěřili a byli bohabojní.'),
(1654, 12, 58, 'A přišli Josefovi bratři a vešli k němu; i poznal je, však oni jeho nepoznali.'),
(1655, 12, 59, 'A když je zaopatřil zásobami jejich, pravil: "Přiveďte mi jednoho bratra svého od otce vašeho. Což nevidíte, že dávám míry spravedlivé a že nejlepší poskytuji pohoštění?'),
(1656, 12, 60, 'Jestliže však jej ke mně nepřivedete, nedostane se vám ode mne míry obilí a nebudete ke mně připuštěni!"'),
(1657, 12, 61, 'Odpověděli: "Požádáme o něj otce jeho a určitě tak uděláme."'),
(1658, 12, 62, 'A řekl Josef sluhům svým: "Dejte jim zboží jejich do zavazadel, snad poznají je po příchodu k rodině své a možná že se navrátí."'),
(1659, 12, 63, 'A když vrátili se k otci svému, řekli mu: "Otče náš, byla nám odmítnuta míra obilí. Pošli s námi bratra našeho, abychom dostali míru, my na něj dobrý pozor dáme."'),
(1660, 12, 64, 'Odpověděl: "Což vaše ochrana bude jiná než předchozí ochrana jeho bratra? Však Bůh nejlepší je z ochránců a nejslitovnější ze slitovníků!"'),
(1661, 12, 65, 'Když otevřeli svá zavazadla a shledali, že zboží jejich jim bylo navráceno, zvolali: "Otče náš, co více si přát? Toto naše zboží bylo nám vráceno a my zásobíme rodinu svou; ochráníme bratra svého a dostaneme o náklad jednoho velblouda navíc a to bude náklad snadný."'),
(1662, 12, 66, 'Pravil otec jejich: "Nepošlu jej s vámi, pokud se mi nezavážete úmluvou před Bohem, že mi jej nazpět přivedete, leda budete-li něčím obklopeni." A když se mu zavázali úmluvou, pravil: "Bůh je toho, co jsme řekli, ručitelem."'),
(1663, 12, 67, 'A dodal: "Synové moji, nevcházejte do města jedinou branou, nýbrž vejděte branami různými; já nemohu vám v ničem pomoci proti Bohu, neboť rozhodnutí je věru jen u Boha. Na Něj se spoléhám a nechť na Něj se spoléhají všichni, kdož se spoléhají."'),
(1664, 12, 68, 'A když vešli do města tak, jak jim to poručil otec jejich, nepomohlo jim to nikterak proti Bohu. A bylo to jen přání v duši Jakubově, jež vyplnil. A byl on věru pln vědění, neboť jsme jej tomu naučili; avšak většina lidí nemá žádné vědění.'),
(1665, 12, 69, 'Když vstoupili k Josefovi, přitáhl blízko k sobě bratra svého a řekl mu: "Já jsem bratr tvůj a nermuť se kvůli tomu, co oni na mně kdysi spáchali!"'),
(1666, 12, 70, 'A když je opatřil zásobami, umístil pohár do sedla bratra svého a potom dal rozhlásit hlasatelem: "Lidé karavany, vy jste věru zloději!"'),
(1667, 12, 71, 'I řekli, zatímco se k nim blížili: "Co vlastně hledáte?"'),
(1668, 12, 72, 'Odpověděli: "Hledáme pohár králův; a ten, kdo jej přinese, dostane za odměnu velbloudí náklad. A já za to ručím."'),
(1669, 12, 73, '"Při Bohu," řekli bratři, "vždyť dobře víte, že nepřišli jsme páchat v zemi nepravosti - my věru nejsme zloději žádní."'),
(1670, 12, 74, 'Odpověděli: "Jaká má být odplata toho, jenž jej ukradl, ukáže-li se, že jste lháři?"'),
(1671, 12, 75, 'Odpověděli: "Odplatou toho, v jehož sedle se nalezne ten pohár, budiž, aby vám byl vydán jako otrok. Takto my odměňujeme nespravedlivé."'),
(1672, 12, 76, 'A začal prohledávat vaky jejich dříve, než vak bratra svého, až nakonec vytáhl pohár z vaku bratra svého. A takto jsme vnukli Josefovi lest, neboť nemohl podržet bratra svého podle zákona královského, leda by tak Bůh chtěl. My obdařujeme různými stupni, koho chceme, a nad každým, kdo vědění ovládá, je vševědoucí.'),
(1673, 12, 77, 'A řekli Josefovi bratři: "Jestliže kradl, tedy již jeden z jeho bratří kradl před ním." Josef pak skryl myšlenky své v duši a nedal jim nic najevo, nýbrž řekl: "Jste ve velmi zlém postavení a Bůh zná nejlépe, co vypovídáte."'),
(1674, 12, 78, 'Odpověděli: "Ó mocný, on má starého a letitého otce, vezmi jednoho z nás místo něho, neboť vidíme, žes jedním z těch, kdož dobro konají."'),
(1675, 12, 79, 'Řekl: "Bůh mě chraniž vzít někoho jiného než toho, u něhož jsme nalezli svůj majetek, vždyť bychom byli nespravedliví!"'),
(1676, 12, 80, 'A když se v něm zklamali, radili se tajně a nejstarší z nich řekl: "Což nevíte, že otec náš zavázal nás úmluvou před Bohem a že jsme se již předtím dopustili nespravedlnosti vůči Josefovi? Neopustím tuto zemi, dokud mi to otec můj nedovolí nebo dokud nerozhodne Bůh v můj prospěch - a On nejlepší je rozhodčí.'),
(1677, 12, 81, 'Vraťte se k otci našemu a řekněte mu:, Otče náš, syn tvůj kradl a my dosvědčujeme jen to, co známe, a žádné tajemství nechováme.'),
(1678, 12, 82, 'Zeptej se obyvatel města, kde jsme byli, a lidí karavany, se kterou jsme šli, že vskutku pravdu hovoříme.? "'),
(1679, 12, 83, 'I pravil Jakub: "Ó nikoliv, to duše vaše vás svedly ve věci té - leč trpělivost je překrásná a snad Bůh mi je navrátí všechny, vždyť On vševědoucí je i moudrý."'),
(1680, 12, 84, 'A odvrátil se od nich a zvolal: "Ó jak smutno je mi po Josefovi!" A zbělely oči jeho od zármutku a byl sklíčen.'),
(1681, 12, 85, 'I řekli: "Při Bohu, což nepřestaneš vzpomínat na Josefa, dokud nezeslábneš zármutkem anebo se nezahubíš?"'),
(1682, 12, 86, 'Řekl: "Žaluji zármutek svůj i lítost jedině Bohu a vím od Boha to, co vy nevíte.'),
(1683, 12, 87, 'Synové moji, jděte a pátrejte po Josefovi a bratru jeho! A neztrácejte naději v laskavost Boží, vždyť jen lidé nevěřící ztrácejí naději v laskavost Boží!"'),
(1684, 12, 88, 'Když pak přišli k Josefovi, pravili: "Ó mocný, přišlo na nás a rodinu naši neštěstí. Přinesli jsme zboží bezvýznamné hodnoty, dej nám plnou míru obilí a ukaž se vůči nám štědrý, vždyť Bůh věru odměňuje ty, kdož jsou štědří."'),
(1685, 12, 89, 'I řekl: "Zdaž jste si vědomi toho, co jste udělali Josefovi a bratru jeho ve své pošetilosti?"'),
(1686, 12, 90, 'Pravili: "Jsi vskutku Josef?" Odpověděl: "Ano, já jsem Josef a toto je můj bratr. Bůh nás zahrnul milostí svou a věru ten, kdo je trpělivý a bohabojný... a Bůh věru nedopustí, aby ztratila se odměna těch, kdož dobro konají."'),
(1687, 12, 91, 'Pravili: "Při Bohu, Bůh ti dal vskutku přednost před námi, zatímco my byli jsme věru hříšníci!"'),
(1688, 12, 92, 'Odpověděl: "Nechť na vás dnes nepadne žádná výčitka! Bůh vám odpustí, vždyť On nejslitovnější je ze slitovníků!'),
(1689, 12, 93, 'Odejděte s touto mou košilí a vložte ji na tvář otce mého a on znovu nabude zraku! A potom ke mně přijďte s celou rodinou svou všichni!"'),
(1690, 12, 94, 'A když se karavana vracela, pravil otec jejich: "Já doopravdy cítím vůni Josefovu! Kéž byste mne nepokládali za blouznivce."'),
(1691, 12, 95, 'Odpověděli: "Při Bohu, tys opět ve starém svém bludu!"'),
(1692, 12, 96, 'A když přišel posel se zvěstí radostnou a vložil mu košili na tvář, stal se Jakub opět vidoucím a řekl: "Což neřekl jsem vám, že vím od Boha to, co vy nevíte?"'),
(1693, 12, 97, 'Pravili: "Otče náš, pros za nás Boha o odpuštění hříchů našich, vždyť my věru jsme byli hříšníky."'),
(1694, 12, 98, 'Řekl: "Budu prosit Pána svého o odpuštění pro vás a On věru je odpouštějící, slitovný."'),
(1695, 12, 99, 'A když všichni přišli k Josefovi, přitáhl blízko k sobě rodiče své a řekl: "Vstupte do Egypta v bezpečí, chce-li tomu Bůh!"'),
(1696, 12, 100, 'A uvedl rodiče své na trůn, zatímco ostatní se mu klaněli padajíce na tváře. Pak pravil: "Otče můj, toto zde je výklad mého snu dávného a Pán můj jej uskutečnil. A byl vůči mně dobrotivý, když mne vyvedl z vězení a přivedl vás z pouště poté, co satan způsobil roztržku mezi mnou a bratry mými. Pán můj je věru obratný v tom, co chce, a On vševědoucí je i moudrý.'),
(1697, 12, 101, 'Pane můj, Tys mne obdařil částí moci královské a naučils mne výkladu záhad; tvůrce nebes a země, Tys mým ochráncem na tomto i onom světě, povolej mne k Sobě oddaného do vůle Tvé a připoj mne k bezúhonným!"'),
(1698, 12, 102, 'A toto je jedna ze zvěstí o nepoznatelném, jež ti zjevujeme, neboť tys nebyl u nich, když shodli se vespolek na svém činu a úklady strojili.'),
(1699, 12, 103, 'Však většina lidí, i kdybys po tom sebevíce toužil, nebude věřit!'),
(1700, 12, 104, 'Nežádáš od nich za to odměnu žádnou a není to leč připomenutí lidstvu veškerému.'),
(1701, 12, 105, 'Kolik je na nebesích i na zemi znamení, kolem nichž chodí a od nichž se odvracejí!'),
(1702, 12, 106, 'Mnozí z nich v Boha nevěří, aniž by k Němu zároveň jiné nepřidružovali.'),
(1703, 12, 107, 'Což jsou si tak jisti, že nepostihne je úder trestu Božího anebo že je nezastihne Hodina znenadání, aniž o tom budou mít tušení?'),
(1704, 12, 108, 'Rci: "Toto je cesta má. Volám vás k Bohu viditelným důkazem, já i ti, kdož mne následují. Sláva Bohu, že nejsem jedním z modloslužebníků!"'),
(1705, 12, 109, 'A také před tebou jsme vyslali pouze lidi z obyvatel měst, jimž dostalo se vnuknutí. Což nechodili po zemi, aby viděli, jaký byl konec těch, kdož před nimi byli? Však příbytek v životě budoucím je lepší pro ty, kdož bohabojní byli. Což k rozumu nepřijdete?'),
(1706, 12, 110, 'A když si pak poslové zoufali a již se domnívali, že za lháře jsou pokládáni, přišla k nim Naše pomoc vítězná. A byl zachráněn ten, koho jsme chtěli, však nelze odvrátit přísnost Naši od lidu hříšného.'),
(1707, 12, 111, 'A v příbězích jejich je věru mnoho příkladů pro ty, kdož rozumem jsou obdařeni. A není to příběh vymyšlený, nýbrž potvrzení toho, co již předcházelo, a podrobné vysvětlení všeho, správné vedení i milosrdenství pro lid věřící.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Alif lám mím rá! Toto jsou znamení Písma; a pravdou je to, co bylo ti Pánem tvým sesláno, avšak většina lidí tomu nevěří.'),
(1709, 13, 2, 'Bůh je ten, jenž vztyčil nebesa bez pilířů viditelných, potom se usadil na trůnu Svém a podmanil si slunce a měsíc; obě tělesa pak spějí k lhůtě stanovené. On rozkaz usměrňuje a činí srozumitelnými znamení Svá snad budete přesvědčeni o setkání s Pánem svým.'),
(1710, 13, 3, 'On je ten, jenž rozprostřel zemi a vytvořil na ni hory pevně zakotvené i řeky; a od každého plodu učinil dvě pohlaví a dává zahalovat den nocí. A v tom věru jsou znamení pro lid přemýšlivý.'),
(1711, 13, 4, 'A na zemi jsou polnosti vzájemně sousedící i zahrady vinné révy a obilí i palmy datlové, vyrůstající z jednoho kořene či hromadně, zavlažovány vodou jednou; některé z nich jsme učinili lepšími k jídlu než druhé - a v tom věru jsou znamení pro lid rozumný.'),
(1712, 13, 5, 'Udivuje-li tě něco, pak věru jsou udivující jejich slova: "Až staneme se prachem, budeme skutečně vzkříšeni novým stvořením?" To jsou ti, kdož v Pána svého nevěří, a budou mít řetězy na šíjích svých a stanou se ohně obyvateli a v něm budou nesmrtelní.'),
(1713, 13, 6, 'Žádají na tobě, abys urychlil příchod zlého před dobrým, zatímco již před nimi se uskutečnily tresty příkladné. Pán tvůj je věru pln slitování vůči lidem přes nespravedlnost jejich, avšak Pán tvůj je rovněž přísný v trestání Svém.'),
(1714, 13, 7, 'Ti, kdož nevěří, říkají: "Pročpak mu nebylo sesláno znamení nějaké od Pána jeho?" Avšak tys toliko varovatelem a každý lid má vůdce svého.'),
(1715, 13, 8, 'Bůh nejlépe zná, čím těhotná je každá samice a nakolik se zužují a rozšiřují lůna jejich - vždyť věc každá má u Něho rozměr svůj.'),
(1716, 13, 9, 'On zná nepoznatelné a všeobecně známé, On veliký je, sám sebou povznesený!'),
(1717, 13, 10, 'Je lhostejno, zda někdo z vás řeč svou tají, či veřejně ji pronáší a zda někdo se v noci skrývá, či za dne vychází,'),
(1718, 13, 11, 'tak jako tak má pozorovatele před sebou i za sebou, kteří jej střeží z rozkazu Božího. Bůh nezmění to, co je v lidu nějakém, pokud se tento sám nezmění; když Bohu se zlíbí postihnout něčím zlým lid některý, je nemožné to odvrátit a nebudou mít kromě Něho ochránce žádného.'),
(1719, 13, 12, 'On je ten, jenž ukazuje vám blesk pro strach i naději a dává vzniknout mračnům vodou obtěžkaným.'),
(1720, 13, 13, 'A slaví hrom chválu Jeho a stejně činí i andělé z bázně před Ním. On blesky vysílá a zasahuje jimi, koho chce, zatímco lidé se hádají o Bohu, jenž mocný je v prudkosti Své.'),
(1721, 13, 14, 'Jen Jemu patří vzývání pravé; ti, kdož vzývají něco jiného kromě Něho, nebudou v ničem vyslyšeni, stejně jako voda nedospěje k ústům toho, jenž po ní dlaně své vztahuje, ona však není v dosahu jeho, vždyť vzývání nevěřících jen bludem je.'),
(1722, 13, 15, 'A sklání se před Bohem vše, co na nebesích i na zemi je, dobrovolně či nuceně, a stejně tak i stíny jejich, za jitra časného i večera pozdního.'),
(1723, 13, 16, 'Rci: "Kdo Pánem je nebes a země?" Odpověz: "Bůh!" Rci: "Chcete si vzít vedle Něho ochránce, kteří nemohou prospět ani uškodit ni sobě samým?" Rci: "Cožpak jsou si rovni slepý a vidoucí či rovná se temnota světlu?" Což budou přidružovat k Bohu společníky, kteří snad stvořili něco podobného Jeho stvoření, takže se obě tvoření zdají podobná? Rci: "Bůh všech věcí je stvořitelem a On je jediný, vše podmaňující."'),
(1724, 13, 17, 'On z nebe vodu sesílá, aby tekla v údolích podle rozměrů jejich; proud pak odnáší pěnu na hladině plující. A z toho, co lidé taví v ohni hodlajíce vyrobit ozdoby či nástroje, vychází pěna jí podobná. A takto Bůh ukazuje rozdíl mezi pravdou a falší. A co se týče pěny, ta mizí jako bublina, zatímco to, co lidem prospěšné je, na zemi zůstává. A takto Bůh činí podobenství.'),
(1725, 13, 18, 'Ti, kdož uposlechnou výzvy Pána svého, obdrží odměnu nejkrásnější, ti však, kdož Jeho neuposlechnou i kdyby jim patřilo všechno na zemi a ještě jednou tolik, se nebudou moci tím vykoupit. Špatné bude pak jejich zúčtování, neb útulkem jejich bude peklo - jak hnusné to bude místo odpočinku!'),
(1726, 13, 19, 'Je ten, jenž ví, že to, co ti bylo sesláno od Pána tvého, je pravda, podoben slepci? To připomenou si věru lidé rozmyslem nadaní,'),
(1727, 13, 20, 'ti, kdož věrně úmluvu s Bohem dodržují a smlouvy své neporušují,'),
(1728, 13, 21, 'i ti, kdož spojují to, co Bůh přikázal, aby bylo spojeno, a Pána svého a účtováni hrozného se obávají,'),
(1729, 13, 22, 'i ti, kdož vytrvalí jsou ve své touze spatřit tvář Pána svého, modlitbu dodržují a z toho, co jsme jim uštědřili, tajně i veřejně rozdávají a zlé dobrým odhánějí - pro ty uchystán je příbytek konečný'),
(1730, 13, 23, 'zahrady Edenu. A vejdou do nich oni i ti z otců, manželek a potomků jejich, kdož zbožni byli. A andělé budou přicházet jim v ústrety u všech dveří'),
(1731, 13, 24, 'zdravíce: "Mír s vámi za to, že trpěliví jste byli!" A jak překrásný bude příbytek konečný!'),
(1732, 13, 25, 'Avšak ti, kdož úmluvu s Bohem porušují po jejím uzavření, kdož přetrhávají svazky, jež Bůh nařídil, aby byly spojeny, a ti, kdož pohoršení na zemi šíří, ti nechť jsou prokleti a nechť příbytek mají odporný!'),
(1733, 13, 26, 'Bůh hojně uštědřuje či odměřuje obživu, komu chce. Oni se radují ze života pozemského, ačkoliv život pozemský je proti životu budoucímu jen dočasným užíváním.'),
(1734, 13, 27, 'Ti, kdož nevěří, říkají: "Pročpak mu nebylo sesláno znamení nějaké od Pána jeho?" Rci: "Bůh dává zbloudit, komu chce, a vede k Sobě toho, kdo obrátil se s pokáním,'),
(1735, 13, 28, 'a ty, kdož uvěřili a jichž srdce se uklidňují při vzpomínce na Boha - jak jinak, než vzpomínkou na Boha se mohou srdce uklidnit? -'),
(1736, 13, 29, 'a ty, kdož uvěřili a dobré skutky konali; těm dostane se blaženosti a překrásného místa pobytu."'),
(1737, 13, 30, 'Takto jsme tě vyslali k národu, před nímž již zanikly národy jiné, abys jim přednesl to, co jsme ti vnukli, zatímco oni nevěří v Milosrdného. Rci: "On mým Pánem je a není božstva kromě Něho, na Něj se spoléhám a k Němu se kajícně obracím."'),
(1738, 13, 31, 'A kdyby mohly být Koránem uvedeny na pochod hory či kdyby jím byla země rozpolcená či mohli by jeho pomocí promluvit mrtví... Ba ano, Bohu patří rozkaz každý! Což pochybují ti, kdož uvěřili, že Bůh, kdyby chtěl, nevedl by cestou správnou lidstvo veškeré? A On nepřestane ty, kdož nevěří, stíhat pohromami za to, co konali, anebo je způsobí poblíže obydlí jejich, až se dostaví slib Boží - a Bůh věru nezmění slibu Svého splnění.'),
(1739, 13, 32, 'Lidé se posmívali již poslům, kteří přišli před tebou, a povolil jsem nevěřícím odklad, potom však jsem je uchvátil a jaký byl trest Můj?'),
(1740, 13, 33, 'Což ten, jenž dozírá nad duší každou a tím, co si vysloužila...? Však oni učinili Bohu společníky. Rci: "Pojmenujte je tedy! Chcete Jej snad poučovat o tom, co na zemi nezná, anebo učinit tak slovem povrchním?" Nikoliv! Těm, kdož neuvěřili, byly úklady jejich zkrášleny a oni byli z cesty svedení. Ten pak, komu Bůh dal zbloudit, nemá vůdce žádného.'),
(1741, 13, 34, 'Pro ně je určen trest v životě pozemském; avšak trest v životě budoucím bude ještě horší a nebudou mít proti Bohu ochránce žádného.'),
(1742, 13, 35, 'Toto je obraz ráje, jenž přislíben byl bohabojným: pod zahradami jeho řeky tekou a plody trvalé jsou stejně jako stíny jeho. A to bude odměna konečná pro ty, kdož bohabojní byli, avšak odměnou těch, kdož nevěřili, bude oheň pekelný.'),
(1743, 13, 36, 'Ti, jimž dali jsme Písmo, se radují z toho, co ti bylo sesláno, zatímco mezi spojenci jsou někteří, kdož část toho zavrhují. Rci: "Bylo mi pouze poručeno, abych Boha uctíval a nikoho k Němu nepřidružoval; vzývám Jej tedy a k Němu se i navrátím."'),
(1744, 13, 37, 'A takto jsme seslali Korán jako moudrost v arabštině; a kdybys následoval scestná učení jejich poté, co dostalo se ti vědění, věru nebudeš mít proti Bohu ochránce ani obhájce žádného.'),
(1745, 13, 38, 'A již před tebou jsme vyslali posly a dali jsme jim manželky i potomstvo. A nestalo se, aby posel některý přinesl znamení bez Božího dovolení - a každé období má pak Písmo vlastní.'),
(1746, 13, 39, 'Bůh vymazává i potvrzuje to, co chce, a u Něho je Kniha původní.'),
(1747, 13, 40, 'A buď ti ukážeme část z toho, čím jim hrozíme, anebo tě k Sobě povoláme dříve - tobě přísluší toliko sdělování, Nám pak zúčtování.'),
(1748, 13, 41, 'Což nevidí, jak pronikáme do země a na krajích ji ubíráme? Bůh rozsuzuje, a není nikoho, kdo by se mohl vzepřít rozhodnutí Jeho - a On rychlý je v účtování.'),
(1749, 13, 42, 'Také ti, kdož před nimi byli, úklady strojili, avšak Bohu jsou známy veškeré úklady a On dobře zná, co duše každá si vysloužila. Pak nevěřící poznají, pro koho připraven je příbytek konečný.'),
(1750, 13, 43, 'Ti, kdož nevěří, říkají: "Ty nejsi žádný vyslanec Boží!" Rci: "Bůh stačí mi jako svědek mezi mnou a vámi i těmi, kdož znalost Písma mají!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Alif lám rá! Toto je Písmo, jež jsme ti seslali, abys vyvedl lidi s dovolením Pána jejich z temnot ke světlu a ke stezce mocného, chvályhodného -'),
(1752, 14, 2, 'Boha, jemuž náleží vše na nebesích i na zemi. Běda nevěřícím před hrozbou trestu strašného'),
(1753, 14, 3, '- těm, kdo život pozemský více než budoucí milují, od cesty Boží odvádějí, a aby křivá byla, si přejí. Ti věru daleko jsou zbloudilí!'),
(1754, 14, 4, 'A vyslali jsme každého posla jedině s jazykem lidu jeho, aby jim vše objasňoval. A Bůh zbloudit nechává, koho chce, a cestou správnou též vede, koho chce, a On mocný je i moudrý.'),
(1755, 14, 5, 'A věru jsme kdysi vyslali Mojžíše se znameními Našimi řkouce mu: "Vyveď lid svůj z temnoty ke světlu a připomínej jim dny Boží!" A věru jsou v tom znamení pro každého člověka trpělivého a vděčného.'),
(1756, 14, 6, 'A hle, pravil Mojžíš lidu svému: "Pomněte dobrodiní Božího vůči vám; když zachránil vás před rodem Faraónovým, jenž zlá muka vám působil, syny vaše zabíjel a jen ženy vaše naživu nechával. A v tom byla pro vás zkouška nesmírná od Pána vašeho.'),
(1757, 14, 7, 'A pomněte, když Pán váš prohlásil: Budete-li vděční, rozmnožíme pro vás dobré, budete-li však nevděční, pak věru trest Můj bude přísný!"'),
(1758, 14, 8, 'I řekl Mojžíš dále: "Budete-li nevděční, vy, i kdož jsou na zemi všichni... pak věru On soběstačný je i chvályhodný."'),
(1759, 14, 9, 'Což nedošla vám zvěst o těch, kdož před vámi byli, o lidu Noemově, o ´Ádovcích a Thamúdovcích a těch, kdož po nich byli a o nichž všech jen Bůh ví? Přišli k nim poslové jejich s jasnými důkazy, však oni ruce své si do úst svých vložili a zvolali: "Nevěříme v to, s čím jste byli posláni, a věru jsme ohledně toho, k čemu nás vyzýváte, v pochybách hlubokých."'),
(1760, 14, 10, 'A odpověděli poslové jejich: "Je snad pochybnost nějaká o Bohu, tvůrci nebes a země? On volá vás, aby vám mohl odpustit hříchy vaše a poskytl vám odklad do lhůty stanovené." I řekli nevěřící: "Vždyť vy jen smrtelníci jste nám podobní a chcete nás odvrátit od toho, co otcové naši uctívali. Přineste nám tedy své zplnomocnění zjevné!"'),
(1761, 14, 11, 'A odpověděli poslové jejich: "Jsme vskutku pouze smrtelníci vám podobní, avšak Bůh zahrnuje přízní svou, koho chce ze služebníků svých. Nepřísluší nám přinášet vám nějaké zplnomocnění, leda s dovolením Božím. A na Boha nechť věřící se spoléhají!'),
(1762, 14, 12, 'Proč bychom se neměli spoléhat na Boha, když nás již vedl po cestách našich? My budeme věru trpělivě snášet to, čím nám škodíte - a nechť na Boha se spoléhají ti, kdož naň se spoléhají!"'),
(1763, 14, 13, 'A pravili ti, kdož nevěřící byli, k poslům svým: "Buď vás vyženeme ze své země, anebo se navrátíte k náboženství našemu!" A vnukl jim Pán jejich: "My vskutku nespravedlivé zahubíme'),
(1764, 14, 14, 'a po nich vás na zemi usídlíme. A to odměnou bude těm, kdož obávali se postavení Mého a báli se hrozby Mé!"'),
(1765, 14, 15, 'A prosili o rozhodnutí Boží a byl zklamán každý tyran tvrdošíjný,'),
(1766, 14, 16, 'a před ním peklo stojí, v němž napájen bude hnisem tekoucím'),
(1767, 14, 17, 'po doušcích malých, jež málem ani nespolkne. A smrt k němu bude přicházet ze všech stran, avšak nezemře, nýbrž za zády jeho bude stát trest přísný.'),
(1768, 14, 18, 'Činy těch, kdož v Pána svého nevěří, se popelu podobají, nad nímž se vítr rozpoutal v den bouřlivý. A oni nemají moc nad ničím z toho, co si vysloužili - a to věru je bloudění daleké.'),
(1769, 14, 19, 'Což nevidíš, že Bůh stvořil nebesa a zemi jako skutečnost? A kdyby se Mu zachtělo, nechal by vás zaniknout a přivedl by nové tvorstvo nějaké,'),
(1770, 14, 20, 'a není to Bohu nikterak obtížné.'),
(1771, 14, 21, 'A předstoupí všichni před Boha a řeknou slabí těm, kdož domýšliví byli: "My následovali jsme vás, jste nyní schopni nám nějak pomoci od trestu Božího?" A odpovědí: "Kdyby nás byl vedl Bůh, také bychom vás byli dobře vedli. Ale nyní je pro nás zcela jedno, zda zarmoucení jsme, či trpěliví - pro nás beztak není východiska žádného."'),
(1772, 14, 22, 'A řekne satan, až věc bude rozhodnuta: "Bůh slib pravdivý vám přislíbil, já také jsem vám slib dal, ten však jsem nesplnil. Vždyť já nad vámi jsem neměl pravomoc nijakou, leda abych vás volal a vy mi odpověděli. Nečiňte mi kvůli tomu předhůzky, ale vyčítejte to sami sobě! Já vám nemohu pomoci a také vy mi nemůžete pomoci a zříkám se toho, že jste mě dříve k Bohu přidružovali." A pro nespravedlivé je věru přichystán trest bolestný,'),
(1773, 14, 23, 'zatímco ti, kdož věřili a zbožné skutky konali, do zahrad uvedeni budou, pod nimiž řeky tekou, a tam, nesmrtelní z dovolení Božího, slovy "Mír s vámi!" budou přivítáni.'),
(1774, 14, 24, 'Což neviděls, jak Bůh uvádí podobenství o slovu dobrém? Je jako strom výtečný, jehož kořen je pevný a k nebesům se pne jeho větvoví,'),
(1775, 14, 25, 'a plody v každé době přináší z Pána tvého dovolení. A Bůh uvádí lidem podobenství - snad se vzpamatují.'),
(1776, 14, 26, 'Slovo špatné pak podobá se stromu špatnému, jenž vyvrácen je nad zemí a jenž nemá vytrvalého stání.'),
(1777, 14, 27, 'Bůh povzbuzuje ty, kdož uvěřili, slovem pevným v životě pozemském i budoucím, zatímco nespravedlivé nechává bloudit. A Bůh činí, co Jemu se zlíbí.'),
(1778, 14, 28, 'Což neviděls ty, kdož vyměřili dobrodiní Boží za nevděk a umístili lid svůj v příbytku zatracení,'),
(1779, 14, 29, 'v pekle, v němž hořet budou? Jak hnusné to bude místo přebývání!'),
(1780, 14, 30, 'Oni Bohu rovné přidávali, aby lid od cesty Jeho svedli. Rci: "Jen si užívejte, vždyť cíl váš konečný je oheň pekelný!"'),
(1781, 14, 31, 'Rci služebníkům Mým, kteří uvěřili, aby modlitbu konali a rozdávali z toho, co jsme jim uštědřili, skrytě i veřejně, dříve než přijde den, v němž nebude ani obchodování, ani přátelství.'),
(1782, 14, 32, 'Bůh je ten, jenž stvořil nebesa a zemi a z nebe vodu seslal a z ní plodům pro obživu vaši vyrůst dal. A podrobil vám lodi, aby po moři pluly podle rozkazu Jeho, a též řeky vám podmanil;'),
(1783, 14, 33, 'a podčinil vám slunce a měsíc, jež neúnavně obíhají, a též noc a den vám podrobil'),
(1784, 14, 34, 'a vše, oč žádali jste Jej, vám uštědřil. A kdybyste chtěli spočítat projevy laskavosti Boží, nikdy je nespočítáte, vždyť člověk je věru nespravedlivý a nevděčný.'),
(1785, 14, 35, 'A hle, pravil Abraham: "Pane můj, učiň město toto bezpečným a oddal od nás, ode mne i synů mých, model uctívání,'),
(1786, 14, 36, 'neboť, Pane můj, ony zbloudit daly již mnohým z lidí. Ten, kdo následovat mne bude, ten bude z mých jedním, však kdo se proti mně postaví... vždyť Tys věru slitovný i odpouštějící,'),
(1787, 14, 37, 'Pane můj, usadil jsem část potomstva svého v údolí bez polí obilných poblíže chrámu Tvého posvátného, Pane náš, aby zde modlitbu konali. Učiň, aby srdce lidí se k nim přiblížila, a uštědři jim plody co obživu - snad budou vděční!'),
(1788, 14, 38, 'Pane náš, Ty dobře víš, co skrýváme i co najevo dáváme, a nic neskryje se před Bohem ani na nebi, ani na zemi.'),
(1789, 14, 39, 'Chvála Bohu, jenž daroval mi přes stáří mé Ismaela a Izáka - a Pán můj věru dobře slyší vzývání.'),
(1790, 14, 40, 'Pane můj, učiň, abych dodržoval modlitbu já i potomstvo mé a vyslyš, Pane náš, mé volání!'),
(1791, 14, 41, 'Pane náš, odpusť mně i rodičům mým i věřícím v den, kdy nastane zúčtování!"'),
(1792, 14, 42, 'Nedomnívej se, že Bohu je lhostejné, co nespravedliví konají; On pouze odklad jim dává do dne, kdy oči budou vytřeštěny,'),
(1793, 14, 43, 'kdy lidé přijdou s krky nataženými a s hlavami strnule zdviženými, se zraky neschopnými se odvrátit a se srdci prázdnými.'),
(1794, 14, 44, 'Varuj lidi přede dnem, kdy trest se k nim dostaví a kdy řeknou ti, kdož byli nespravedliví: "Pane náš, dej nám odklad do blízké lhůty a my pak odpovíme na výzvu Tvou a posly následovat budeme." "Což jste již dříve nepřísahali, že pro vás zánik neplatí?'),
(1795, 14, 45, 'Obývali jste příbytky těch, kdož před vámi sami sobě ukřivdili, a přece vám bylo jasné, jak s nimi jsme naložili, a příklady různé jsme vám uváděli."'),
(1796, 14, 46, 'Oni úklady své chystali, však Bůh si s nimi ví rady, i kdyby úklady jejich byly takové, že horami by hýbaly.'),
(1797, 14, 47, 'Nedomnívej se, že Bůh nedodrží slib Svůj prorokům daný - vždyť Bůh věru mocný je a pomsty je pánem'),
(1798, 14, 48, 'v den, kdy země nahrazena bude zemí jinou a nebe jiným nebem a kdy lidé stanou před Bohem jediným, podmanitelem.'),
(1799, 14, 49, 'V ten den pak spatříš hříšníky pospolu řetězy svázané,'),
(1800, 14, 50, 'oděvy jejich ze smůly budou a tváře jejich ohněm pokryté,'),
(1801, 14, 51, 'aby Bůh mohl odměnit duši každou za to, co si vysloužila, vždyť Bůh věru je rychlý v účtování.'),
(1802, 14, 52, 'A toto je pro lidi sdělení, aby tím byli varováni a aby věděli, že On je Bůh jediný, a aby si to připomenuli ti, kdož jsou rozumní.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Alif lám rá! Toto jsou znamení Písma a Koránu zjevného.'),
(1804, 15, 2, 'Často si nevěřící budou přát, aby byli odevzdanými do vůle Boží!'),
(1805, 15, 3, 'Nech je být, ať si pojídají, užívají a nadějí klamnou se kojí, vždyť záhy se dozvědí!'),
(1806, 15, 4, 'Nezahubili jsme žádné město, aniž se mu dostalo Písma známého,'),
(1807, 15, 5, 'a žádný národ nemůže způsobit uspíšení ani oddálení termínu svého.'),
(1808, 15, 6, 'Říkají: "Ty, jemuž připomenutí bylo sesláno, věru jsi džinem posedlý!'),
(1809, 15, 7, 'Pročpak k nám nepřivedeš anděly, pravdu mluvíš-li?"'),
(1810, 15, 8, 'My anděly jen k vykonání rozhodnutého sesíláme - a potom nebudou nevěřící k těm, jimž odklad je dán, patřit.'),
(1811, 15, 9, 'My zajisté připomenutí jsme seslali a dobře je umíme ochránit.'),
(1812, 15, 10, 'Již před tebou jsme k stranám dávným posly vyslali,'),
(1813, 15, 11, 'však nepřišel k nim posel žádný, by se mu neposmívali.'),
(1814, 15, 12, 'Takto mu razíme cestu do srdcí hříšníků.'),
(1815, 15, 13, 'A neuvěří v něj, i když se už naplnil obvyklý osud předchozích.'),
(1816, 15, 14, 'A i kdybychom jim bránu nebeskou otevřeli a oni stoupali by k ní,'),
(1817, 15, 15, 'přece by říkali: "Zraky naše byly opity nebo spíše jsme lidé očarovaní!"'),
(1818, 15, 16, 'A na nebi jsme umístili znamení zvěrokruhu a učinili jsme je pro ty, kdo na ně patří, krásnými'),
(1819, 15, 17, 'a ochránili jsme je před všemi satany prokletými -'),
(1820, 15, 18, 'kromě těch, kdož pokradmu naslouchají, však ti jsou pronásledováni plamenem jasně zářícím.'),
(1821, 15, 19, 'A zemi jsme rozprostřeli a rozhodili po ní hory pevně stojící a dali na ní vyrůst každé věci správně vyvážené'),
(1822, 15, 20, 'a připravili jsme na ní potravu pro vás i pro ty, jimž obživu neskýtáte.'),
(1823, 15, 21, 'A není žádné věci, jejíž pokladnice by u Nás nebyly, a sesíláme vám ji jedině v míře stanovené.'),
(1824, 15, 22, 'A poslali jsme větry oplodňující a seslali jsme z nebe vodu, jíž vás napájíme, vždyť vy v zásobě ji nemáte.'),
(1825, 15, 23, 'A jsme to My, kdo život i smrt dáváme, a My sami všeho jsme dědici'),
(1826, 15, 24, 'a dobře známe ty z vás, kdo vpředu kráčí, i ty, kdo vzadu se drží.'),
(1827, 15, 25, 'A Pán tvůj je všechny shromáždí, vždyť On moudrý je, vševědoucí.'),
(1828, 15, 26, 'Stvořili jsme člověka z hlíny suché, vzaté z bláta poddajného,'),
(1829, 15, 27, 'zatímco džiny jsme již dříve vytvořili z ohně prudce spalujícího.'),
(1830, 15, 28, 'A hle, pravil Pán tvůj andělům: "Já smrtelníka stvořím z hlíny suché, vzaté z bláta poddajného,'),
(1831, 15, 29, 'a až jej vyrovnám a vdechnu mu něco z ducha svého, padněte na zem, před ním se klaníce!"'),
(1832, 15, 30, 'A padli před ním uctívajíce jej vespolek andělé všichni,'),
(1833, 15, 31, 'kromě Iblíse, jenž odmítl být mezi uctívajícími.'),
(1834, 15, 32, 'I pravil Bůh: "Iblísi, proč nejsi mezi těmi, kdož klaní se, také ty?"'),
(1835, 15, 33, 'Odvětil: "Nebudu padat na zem před smrtelníkem, jehož jsi z hlíny suché stvořil, vzaté z bláta poddajného!"'),
(1836, 15, 34, 'I pravil Bůh: "Odejdi odsud, neboť jsi věru prokletý!'),
(1837, 15, 35, 'A nechť na tobě prokletí lpí až do dne soudného!"'),
(1838, 15, 36, 'Řekl Iblís: "Pane můj, poskytni mi odklad do dne, kdy budou vzkříšeni!"'),
(1839, 15, 37, 'I pravil Bůh: "Budiž tedy mezi těmi, jimž je odloženo'),
(1840, 15, 38, 'až do dne času stanoveného!"'),
(1841, 15, 39, 'Odpověděl: "Pane můj, za to, žes mne svedl, okrášlím jim vše, co na zemi je, a všechny je uvedu v bloudění,'),
(1842, 15, 40, 'kromě těch, kdož jsou služebníci Tvoji upřímní!"'),
(1843, 15, 41, 'I pravil Bůh: "Toto je stezka přímá podle mého mínění,'),
(1844, 15, 42, 'a věru nebudeš mít žádnou pravomoc nad služebníky mými, leda nad těmi, kdož tě následují svedeni,'),
(1845, 15, 43, 'a pro ně všechny bude pak peklo místem setkání slíbeného."'),
(1846, 15, 44, 'A to má sedm bran a u každé brány bude z nich skupina zvláštní.'),
(1847, 15, 45, 'Naopak bohabojní mezi zahradami a u pramenů budou přebývat:'),
(1848, 15, 46, '"Vstupte do nich s pokojem a bezpečni!"'),
(1849, 15, 47, 'Z hrudí jejich jim veškerou zášť vyrveme a budou na lehátkách bratrsky odpočívat a druh na druha se dívat,'),
(1850, 15, 48, 'žádná starost se jich tam nedotkne a nebudou odtamtud nikdy vyhnáni.'),
(1851, 15, 49, 'Zvěstuj služebníkům Mým, že Já jsem věru odpouštějící i slitovný,'),
(1852, 15, 50, 'avšak že trest Můj bude trest bolestný!'),
(1853, 15, 51, 'A vypravuj jim o hostech Abrahamových,'),
(1854, 15, 52, 'když k němu přišli zdravíce "Mír s tebou!" a on jim odpověděl: "My věru se vás bojíme."'),
(1855, 15, 53, 'I pravili: "Neboj se, vždyť my ti přinášíme zvěst radostnou o narození chlapce moudrého!"'),
(1856, 15, 54, 'Otázal se: "Jak byste mi mohli oznamovat takovou zvěst, když na mne již stáří dolehlo? Jménem čeho mi to oznamujete?"'),
(1857, 15, 55, 'Odpověděli: "Oznamujeme ti to jménem pravdy - nebuď tedy z těch, kdo naději ztrácejí."'),
(1858, 15, 56, 'Pravil: "Kdo jiný ztrácí naději v milosrdenství Pána svého než ti, kdož bloudí?"'),
(1859, 15, 57, 'A pokračoval: "A jaká je tedy vaše záležitost, vyslanci?"'),
(1860, 15, 58, 'Odvětili: "Byli jsme vysláni k lidu hříšnému'),
(1861, 15, 59, '- kromě rodu Lotova, jejž věru celý zachráníme'),
(1862, 15, 60, 'vyjma ženy jeho, neboť jsme o ní již rozhodli, že bude mezi opozdilými."'),
(1863, 15, 61, 'A když vyslanci k rodu Lotovu přišli,'),
(1864, 15, 62, 'pravil Lot: "Věru jste pro mne lidé podezřelí!"'),
(1865, 15, 63, 'Odpověděli: "Nikoliv, my přišli k tobě se slibem, jejž oni v pochybnost brali,'),
(1866, 15, 64, 'a pravdu ti přinášíme a věru jsme pravdomluvní.'),
(1867, 15, 65, 'Vydej se na cestu se svou rodinou v temné části noci a kráčej za nimi! A ať nikdo z vás se neobrací! Jděte tam, kam vám přikázáno!"'),
(1868, 15, 66, 'A sdělili jsme mu tento rozkaz, neboť ten z nich, kdo pozadu zůstane, měl zahuben být ráno.'),
(1869, 15, 67, 'Rozradostněni zprávou přispěchali lidé města onoho'),
(1870, 15, 68, 'a Lot jim pravil: "Toto moji hosté jsou, zneuctění mi tedy nezpůsobte,'),
(1871, 15, 69, 'však Boha se bojte a hanbou mne nepokrývejte!"'),
(1872, 15, 70, 'Pravili: "Což jsme tě nebránili před vším lidstvem?"'),
(1873, 15, 71, 'I zvolal Lot: "Hle, zde dcery mé máte, když už to spáchat chcete!"'),
(1874, 15, 72, 'Při tvém životě, tito lidé věru tápali v opilství svém,'),
(1875, 15, 73, 'však výkřik je uchvátil při východu slunce.'),
(1876, 15, 74, 'A obrátili jsme to město vzhůru nohama a seslali jsme na ně déšť kamenů z hlíny pálené;'),
(1877, 15, 75, 'zajisté je v tom znamení pro pozorovatele bedlivé!'),
(1878, 15, 76, 'A věru ono je na cestě trvalé'),
(1879, 15, 77, 'a zajisté je v tom znamení pro věřící!'),
(1880, 15, 78, 'A také lid Houštin se ukázal nespravedlivým,'),
(1881, 15, 79, 'však pomsta naše na ně dopadla; a jsou ti i oni věru příkladem jasným!'),
(1882, 15, 80, 'Též obyvatelé al-Hidžru vyslance Naše za lháře prohlásili'),
(1883, 15, 81, 'a přinesli jsme jim znamení Své, však oni se odvrátili.'),
(1884, 15, 82, 'V horách si příbytky své, jsouce bezpečni, vysekávali'),
(1885, 15, 83, 'a ozval se zrána nad nimi výkřik jediný'),
(1886, 15, 84, 'a nebylo jim k ničemu to, co si vysloužili.'),
(1887, 15, 85, 'Stvořili jsme nebesa a zemi i vše, co je mezi nimi, jako skutečnost; a hodina neodvratně nastane, odpouštěj tedy odpouštěním krásným,'),
(1888, 15, 86, 'vždyť Pán tvůj je věru stvořitelem i vševědoucím.'),
(1889, 15, 87, 'A již dali jsme ti sedm opakovaných i Korán vznešený,'),
(1890, 15, 88, 'neobracej tedy zrak svůj žádostivě k tomu, co dali jsme některým párům z nich v užívání, nermuť se kvůli nim, ale rozprostři křídla svá nad věřícími'),
(1891, 15, 89, 'a rci: "Já věru jsem varovatel zjevný."'),
(1892, 15, 90, 'A podobně jsme seslali trest na ty, kdož rozdělují'),
(1893, 15, 91, 'a kteří Korán na části rozkládají.'),
(1894, 15, 92, 'Při Pánu tvém, věru si od nich všech vyžádáme počet'),
(1895, 15, 93, 'z toho, co konali.'),
(1896, 15, 94, 'Ohlas tedy to, co ti bylo přikázáno, a odvrať se od modloslužebníků,'),
(1897, 15, 95, 'Vždyť My stačíme ti proti posměváčkům,'),
(1898, 15, 96, 'kteří vedle Boha ještě jiné božstvo staví, však záhy poznají!'),
(1899, 15, 97, 'A víme již, že hruď tvá je stísněna tím, co říkají,'),
(1900, 15, 98, 'ty však chválu Pána svého oslavuj a buď mezi těmi, kdož na tvář padají,'),
(1901, 15, 99, 'a Pánu svému služ do chvíle, kdy k tobě nepochybně přijde!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Rozhodnutí Boží určitě přijde, nesnažte se je tedy uspíšit! Sláva Jemu, jenž vznešenější je než vše, co k Němu přidružují!'),
(1903, 16, 2, 'On sesílá anděly s duchem zjevení Svého tomu ze služebníků Svých, komu chce, poroučeje: "Varujte, že není božstva kromě Mne, buďte tedy bohabojní!"'),
(1904, 16, 3, 'On nebesa i zemi jako skutečnost stvořil a On vznešenější je než všechno, co k Němu přidružují.'),
(1905, 16, 4, 'Člověka z kapky semene stvořil, a hle, on přesto je odpůrce zjevný.'),
(1906, 16, 5, 'A stvořil velbloudy, z nichž pro vás jsou oděvy i užitek jiný a z nichž i potravu svou máte,'),
(1907, 16, 6, 'a je v nich pro vás krása, když navečer se z pastvy navracíte i když za jitra je vyháníte.'),
(1908, 16, 7, 'A nosí náklady vaše do zemí, jichž byste jen s velkou námahou dosáhli - a Pán váš věru dobrotivý je i slitovný.'),
(1909, 16, 8, 'A stvořil koně, mezky a osly, abyste na nich jezdili, i jako ozdobu - a On tvoří i věci jiné, o nichž vy nevíte.'),
(1910, 16, 9, 'A Bohu patří určení cesty, z níž někteří se odchylují; a kdyby chtěl, cestou správnou by vás vedl všechny.'),
(1911, 16, 10, 'On je ten, jenž z nebe vodu seslal, z níž pro vás jsou nápoje a z níž žijí rostliny, na nichž stáda svá pasete.'),
(1912, 16, 11, 'A z ní vyrůstá vám obilí, olivovníky, palmy datlové a vinná réva i všechny druhy ovoce. A v tom je věru znamení pro lid přemýšlivý.'),
(1913, 16, 12, 'A podmanil vám noc i den, slunce i měsíc a také hvězdy podléhají rozkazu Jeho. A v tom jsou věru znamení pro lid rozumný.'),
(1914, 16, 13, 'I v tom, co rozsel pro vás na zemi v druzích rozmanitých, je také znamení pro lid myslící.'),
(1915, 16, 14, 'On je ten, jenž podmanil vám moře, abyste z něho mohli maso čerstvé jíst a získávali z něho ozdoby, jež na sebe zavěšujete. A vidíš lodi, jež brázdí vlny jeho, abyste hledali část přízně Jeho - snad budete vděčni!'),
(1916, 16, 15, 'A On rozhodil po zemi hory pevně zakotvené, aby se s vámi nekymácela; a také řeky a cesty - snad budete tím správně vedeni -'),
(1917, 16, 16, 'a ukazovatele, a podle hvězd jsou též vedeni.'),
(1918, 16, 17, 'Což je ten, jenž tvoří, roven tomu, kdo netvoři? Což se nevzpamatujete?'),
(1919, 16, 18, 'Kdybyste chtěli spočítat všechnu laskavost Boží, nikdy ji nespočtete - a Bůh věru odpouštějící je i slitovný.'),
(1920, 16, 19, 'A Bůh nejlépe zná, co skrýváte i co najevo dáváte.'),
(1921, 16, 20, 'Ti však, jež oni místo Boha uctívají, nic nestvořili, ale sami byli stvořeni,'),
(1922, 16, 21, 'a mrtví jsou, bez života, a nevědí ani, kdy budou vzkříšeni.'),
(1923, 16, 22, 'Božstvem vaším budiž Bůh jediný! Ti, kdož nevěří v život posmrtný, mají srdce plno odmítání a jsou hrdopyšní.'),
(1924, 16, 23, 'A není pochyby, že Bůh dobře ví, co skrývají i co najevo dávají, a On věru nemiluje lidi domýšlivé.'),
(1925, 16, 24, 'A když jsou otázáni: "Co tedy seslal Pán váš?", odpovídají: "Povídačky starých!"'),
(1926, 16, 25, 'Nechť tedy nesou plně svá břemena v den zmrtvýchvstání a část břemene těch, kdož z cesty je nevědomky svedli! Jak hnusné je to, čím se obtížili!'),
(1927, 16, 26, 'Také ti, kdož před nimi byli, úklady osnovali, však Bůh zničil budovu jejich v samých základech. A zřítila se na ně střecha shora a dopadl na ně trest z místa, odkud to netušili.'),
(1928, 16, 27, 'A potom, v den zmrtvýchvstání, je Bůh zahanbí a zeptá se: "Kde jsou ti Moji společníci, kvůli nimž jste se do sporu dostali?" A řeknou ti, jimž dostalo se vědění: "Ať věru hanba a vše zlé dnes na nevěřící padne,'),
(1929, 16, 28, 'na ty, jež andělé k sobě vzali, zatímco oni sami na sobě křivdu páchali!" A pak se vzdají na milost: "Nic zlého jsme neprovedli!" Ba naopak, však Bůh dobře zná, co jste činili.'),
(1930, 16, 29, 'Vejděte do bran pekla, nesmrtelní v něm budete! Jak hnusný bude příbytek hrdopyšných!'),
(1931, 16, 30, 'A bohabojní budou dotázáni: "Co tedy seslal Pán váš?" I odvětí: "Dobrodiní!" Těm, kdož dobré v životě tomto činili, těm dobré patří, však pobyt na světě onom bude lepší. A jak krásný bude příbytek bohabojných!'),
(1932, 16, 31, 'A do zahrad Edenu vejdou, pod nimiž řeky tekou, a tam vše, čeho se jim zachce, mít budou. A takto Bůh bohabojné odměňuje,'),
(1933, 16, 32, 'ty, které andělé k sobě vzali, zatímco oni dobré konali; a řeknou jim: "Mír budiž s vámi. V odměnu za to, co jste činili, vejděte do ráje!"'),
(1934, 16, 33, 'Což nevěřící očekávají, že přijdou k nim andělé či rozhodnutí Pána tvého? Takto si počínali již ti, kdož před nimi byli - však Bůh jim neukřivdil, leč oni sami na sobě křivdu spáchali.'),
(1935, 16, 34, 'A postihly je špatnosti, jichž sami se dopouštěli, a obklopilo je to, čemu se posmívali.'),
(1936, 16, 35, 'Říkají ti, kdo k Bohu přidružují: "Kdyby Bůh chtěl, neuctívali bychom my ani naši otcové kromě Něho nic a ani bychom nepokládali za posvátné nic kromě Něho!" A takto si počínali již ti, kdož byli před nimi. Přísluší však poslům něco jiného než zvěstování zřetelné?'),
(1937, 16, 36, 'A vyslali jsme již k národu každému posla, aby hlásal: "Boha uctívejte a Tághútovi se vyhýbejte!" A byli mezi nimi někteří, jimž Bůh dal vedení, a byli mezi nimi jiní, jimž usouzeno bylo bloudění. Choďte jen po zemi a popatřte, jaký byl konec těch, kdo pokládali to za lživé!'),
(1938, 16, 37, 'Snažíš-li se vést nevěřící cestou správnou, pak věz, že Bůh nevede ty, jimž zbloudit dal - a takoví pomocníka žádného nemají.'),
(1939, 16, 38, 'A přísahají při Bohu přísahou svou největší: "Bůh toho, kdo zemřel, nevzkřísí!" Ale on tak učiní podle slibu Svého, jenž se uskuteční - však většina lidi to neví -,'),
(1940, 16, 39, 'aby jim objasnil to, o čem se hádali; a aby poznali ti, kdož nevěřili, že lháři byli.'),
(1941, 16, 40, 'A když cokoliv si přejeme, Naše slovo jediné, jež řekneme, je: "Budiž!" - a stane se.'),
(1942, 16, 41, 'Těm, kdož se vystěhovali kvůli Bohu poté, co ukřivděno jim bylo, těm věru poskytneme přístřeší překrásné na tomto světě a odměnu ještě větší v životě budoucím - kéž by to jen vědět mohli'),
(1943, 16, 42, 'ti, kdož neochvějní byli a na Pána svého spoléhali.'),
(1944, 16, 43, 'A vyslali jsme před tebou jen muže, jimž dali jsme vnuknutí - a zeptejte se lidí, jimž dostalo se připomenutí, nevíte-li!'),
(1945, 16, 44, 'Vyslali jsme je s důkazy jasnými a písmy; a nyní jsme tobě seslali připomenutí, abys lidem objasnil to, co jim bylo (dříve) sesláno - snad budou uvažovat!'),
(1946, 16, 45, 'Zdaž jisti jsou si ti, kdož osnovali zlé, že Bůh je nedá pohltit zemí anebo že k nim nepřikvačí trest z místa, odkud to netuší,'),
(1947, 16, 46, 'či nezasáhne je v činnosti jejich proměnlivé, aniž tomu budou moci zabránit,'),
(1948, 16, 47, 'anebo je neuchvátí ve stavu strachu hrozného? Pán váš však věru shovívavý je a slitovný.'),
(1949, 16, 48, 'Což neviděli, že věc každá, již Bůh stvořil, stín svůj napravo i nalevo sklání, Boha uctívajíc v pokornosti?'),
(1950, 16, 49, 'A Bohu se klaní vše, co na nebesích je i na zemi, zvíře každé i andělé pýchy prostí.'),
(1951, 16, 50, 'Pána svého se obávají, jenž nad nimi dlí, a to, co poručeno je jim, vykonávají.'),
(1952, 16, 51, 'I pravil Bůh: "Neberte si božstva dvě, vždyť jen Božstvo jediné je - Já, a Mne se obávejte!"'),
(1953, 16, 52, 'Jemu náleží vše, co na nebesích i na zemi je, a Jemu patří i uctívání nezbytné. Zdaž jiného než Boha bát se budete?'),
(1954, 16, 53, 'Není dobrodiní žádného, aby od Boha nepřicházelo; když neštěstí vás postihne, tu horlivě Jej vzýváte,'),
(1955, 16, 54, 'však když pak On neštěstí od vás vzdálí, hle, část z vás k Pánu svému jiné přidružuje'),
(1956, 16, 55, 'z nevděčnosti za to, co jsme jim uštědřili. Užívejte si tedy, však záhy seznáte!'),
(1957, 16, 56, 'A dávají božstvům, o nichž ničeho nevědí, podíl z toho, co jsme jim uštědřili. Při Bohu, věru dotázáni budete na to, co jste si vymyslili!'),
(1958, 16, 57, 'A dávají Bohu - sláva Jemu - dcery, zatímco sami mají to, po čem toužili.'),
(1959, 16, 58, 'Když však je někomu z nich oznámeno narození dcery, tu zachmuří se černě jeho tvář a je naplněn zlostí,'),
(1960, 16, 59, 'skrývá se před lidmi pro hanbu toho, co bylo mu oznámeno, a neví, zda pro potupu svou si dítě ponechat má, či zakopat je do země. Což nejsou hnusná jejich rozhodnutí?'),
(1961, 16, 60, 'Ti, kdož nevěří v život budoucí, představují příklad nejhorší, zatímco Bůh představuje příklad nejvznešenější - a On věru mocný je i moudrý.'),
(1962, 16, 61, 'Kdyby Bůh trestal lidi za nespravedlnost jejich, nezůstal by na zemi tvor živý, avšak On dává jim odklad do lhůty stanovené; a až vyprší lhůta jejich, nezadrží ji ni o hodinu ani ji neuspíší.'),
(1963, 16, 62, 'Oni přidávají Bohu to, čím sami opovrhují, a jazyky jejich lež hlásají, když prohlašují, že dostane se jim překrásné odměny; však pochyby není, že údělem jejich oheň bude pekelný a že budou opuštěni.'),
(1964, 16, 63, 'Při Bohu, již před tebou jsme vyslali k národům posly své, však satan jim zkrášlil jejich počínání a přítelem jejich je nyní; a pro ně určen je trest bolestný.'),
(1965, 16, 64, 'A seslali jsme ti Písmo jedině proto, abys jim vysvětlil to, o čem se hádají, a aby bylo správným vedením i milosrdenstvím pro lid věřící.'),
(1966, 16, 65, 'Bůh sesílá z nebe vodu a oživuje jí zemi poté, co zemřela - a věru je v tom znamení pro lid slyšící.'),
(1967, 16, 66, 'A je pro vás též v dobytku vašem poučení: napájíme vás tím, co je v břichách jejich - něčím, co je mezi odpadem a krví - mlékem čistým, jež požitkem je pro pijící.'),
(1968, 16, 67, 'A z plodů datlovníku a z hroznů opojný nápoj získáváte i stravu výtečnou a také v tom je znamení pro lid rozumný.'),
(1969, 16, 68, 'Pán tvůj pak vnukl včelám: "Zřiďte si příbytky své v horách, ve stromech i v tom, co lidé si postaví!'),
(1970, 16, 69, 'Potom požívejte z plodů všech a kráčejte po cestě Pána svého pokojně!" A z útrob jejich pak vychází nápoj barev rozličných, v němž lék jest pro lidi. A to věru je znamení pro lid přemýšlivý.'),
(1971, 16, 70, 'Bůh vás stvořil a posléze vás k sobě povolá. A jsou mezi vámi někteří, kdo věku sešlosti dosáhnou a nic z toho znát nebudou, co již věděli. A Bůh věru je vševědoucí, všemohoucí.'),
(1972, 16, 71, 'Bůh dal jedněm z vás přednost před druhými v podílu na živobytí; avšak ti, jimž přednost byla dána, nedávají z podílu svého těm, jimž pravice jejich vládne, aby si tak byli rovni. Což popírat chtějí dobrodiní Boží?'),
(1973, 16, 72, 'Bůh dal vám manželky mezi vámi zrozené a učinil vám z manželek vašich syny a vnuky a uštědřil vám všemožné věci výtečné. Což věřit budou v božstva falešná, jsouce tak za dobrodiní Boží nevděční?'),
(1974, 16, 73, 'A uctívají místo Boha něco, co jim nic neuštědřuje ani z nebes ani ze země, ba ani toho schopno není.'),
(1975, 16, 74, 'Nečiňte tedy srovnání žádná o Bohu, vždyť Bůh zajisté dobře zná, zatímco z vás nikdo nic neví!'),
(1976, 16, 75, 'A Bůh uvádí podobenství o otroku závislém, jenž sám nemůže nic, a o člověku svobodném, jemuž jsme uštědřili příděl překrásný obživy, z něhož on tajně i veřejně rozdává. Jsou si ti dva rovni? Chvála Bohu nikoliv, však většina z nevěřících nic nezná.'),
(1977, 16, 76, 'Bůh uvádí též podobenství o dvou mužích, z nichž jeden je němý a ničeho schopen není, pánu svému je přítěží, a kamkoliv poslán je, nikdy s dobrým se nevrátí. Je člověk takový roven tomu, kdo spravedlnost přikazuje a po stezce přímé kráčí?'),
(1978, 16, 77, 'Bohu náleží nepoznatelné na nebesích i na zemi; a příchod Hodiny bude jak oka mžiknutí či ještě kratší - vždyť Bůh mocen všech je věcí!'),
(1979, 16, 78, 'Bůh vás vyvedl z útrob matek vašich, aniž jste měli nějaké vědění, a pak vám sluch, zrak i srdce dal - snad budete vděční.'),
(1980, 16, 79, 'Což neviděli ptáky podrobené Pánu v prostoru nebeském, kde nikdo je nepodpírá kromě Boha? Věru jsou v tom znamení pro lid věřící!'),
(1981, 16, 80, 'Bůh učinil vám vaše domy obydlím a opatřil vám z kůží dobytka stany, jež lehce přenášíte v den, kdy zdviháte se k odchodu, i v den, kdy se usazujete, a dal vám v jejich vlně, srsti i kožišině vybavení a dočasné užívání.'),
(1982, 16, 81, 'A z toho, co stvořil, opatřil vám Bůh stín a dal vám v horách útočiště a dal vám oděvy, jež chrání vás před horkem, a dal vám oděvy, jež chrání vás před zraněním. A takto vůči vám dovršil Své dobrodiní - snad budete do vůle Jeho odevzdáni.'),
(1983, 16, 82, 'Jestliže se oni zády k tobě obrátí, pak úkolem tvým je pouze zřetelné oznámení.'),
(1984, 16, 83, 'Ačkoliv znají dobrodiní Boží, přece je popírají a většina z nich jsou nevděčníci.'),
(1985, 16, 84, 'V den, kdy vzkřísíme z každého národa svědka, nebude dovoleno promluvit těm, kdož nevěřící byli, a nebude jim dáno odpuštění,'),
(1986, 16, 85, 'a tehdy spatří ti, kdož byli nespravedliví, trest svůj a nebude jim v něm ulehčeno a nebude jim povolen odklad žádný.'),
(1987, 16, 86, 'A až ti, kdož přidružovali, uvidí ty, jež k Bohu přidružovali, zvolají: "Pane náš, toto jsou ti, které jsme k Tobě přidružovali a místo Tebe jsme vzývali!" A vmetou jim tito v tvář slova: "Vy věru jste lháři!"'),
(1988, 16, 87, 'A tehdy se vydají nevěřící na milost Bohu a ztratí se od nich to, co si lživě vymyslili.'),
(1989, 16, 88, 'Těm, kdož nevěřící byli a od cesty Boží odvraceli, těm k trestu trest přidáme za to, že pohoršení šířili.'),
(1990, 16, 89, 'V den, kdy vzkřísíme z každého národa svědka z něho vzešlého, předvedeme tě jako svědka proti nim. A seslali jsme ti Písmo, jež objasněním je všeho, vedením, milosrdenstvím i zvěstí radostnou pro ty, kdož do vůle Boží se odevzdali.'),
(1991, 16, 90, 'Bůh zajisté přikazuje spravedlnost, dobré skutky i štědrost vůči příbuzným a zakazuje necudnost, zavrženíhodné skutky a vzdornost a varuje vás - snad toho pamětliví budete.'),
(1992, 16, 91, 'Dodržujte věrně úmluvu Boží, když jste ji jednou uzavřeli, a nenarušujte přísahy, když jste je potvrdili a Boha si jako jejich ručitele vzali - vždyť Bůh dobře ví, co děláte.'),
(1993, 16, 92, 'Nebuďte jako žena ona, jež rozpletla přadeno své poté, co s námahou je upředla, a nečiňte přísahy s úmyslem je vzájemně porušit, jestliže jedna skupina z vás je početnější druhé. Bůh vás tímto jen zkouší a věru vám vysvětlí v den zmrtvýchvstání vše, o čem ve sporu jste.'),
(1994, 16, 93, 'Kdyby Bůh chtěl, byl by vás národem jediným učinil; avšak On dává zbloudit, komu chce, a uvádí na správnou cestu, koho chce; a na skutky své zajisté tázáni budete.'),
(1995, 16, 94, 'Nečiníte přísahy své s úmyslem je vzájemně porušit, aby vám neuklouzla noha vaše poté, co pevně stála, abyste neokusili zlého za to, že jiné jste z cesty Boží odvraceli, a abyste se trestu nesmírného nedočkali.'),
(1996, 16, 95, 'Neprodávejte úmluvu Boží za cenu nízkou, vždyť věru to, co u Boha máte, je pro vás lepší - vědoucí jste-li!'),
(1997, 16, 96, 'Cokoliv nyní u vás je, pomine, však cokoliv u Boha je, věčně zůstane. A My věru odměníme ty, kdož trpěliví byli, podle toho nejlepšího, co vykonali.'),
(1998, 16, 97, 'Kdokoliv zbožné skutky koná, ať muž či žena, a je věřící, toho vzkřísíme k životu překrásnému a věru je odměníme odměnou podle toho nejlepšího, co učinili.'),
(1999, 16, 98, 'Když Korán přednášíš, hledej u Boha útočiště před satanem prokletým,'),
(2000, 16, 99, 'jenž věru nemá pravomoc žádnou nad těmi, kdož uvěřili a spoléhají na Pána svého,'),
(2001, 16, 100, 'nýbrž má moc jen nad těmi, kdo za ochránce si jej vzali a kdo modloslužebníky jsou zásluhou jeho.'),
(2002, 16, 101, 'A když zaměníme jeden verš veršem jiným - a Bůh nejlépe zná to, co sesílá - říkají: "Ty si to vymýšlíš!" Však většina z nich nic neví!'),
(2003, 16, 102, 'Odpověz: "Duch svatý je přinesl dolů od Pána tvého s pravdou, aby utvrdilo ve víře ty, kdož uvěřili, a aby bylo vedením i zvěstí radostnou pro ty, kdož odevzdali se do vůle Boží."'),
(2004, 16, 103, 'Víme dobře, že nevěřící hovoří: "Vždyť jej to učí smrtelník nějaký!" Však jazyk toho, na nějž narážku činí, je cizí, zatímco tento Korán je v jasném jazyce arabském.'),
(2005, 16, 104, 'Zajisté ty, kdož ve znamení Boží nevěří, Bůh cestou přímou nepovede a bolestným postihne je trestem.'),
(2006, 16, 105, 'Lži pak vymýšlejí právě ti, kdož ve znamení Boží nevěří, a to jsou věru lháři.'),
(2007, 16, 106, 'Na toho, kdo zapře Boha poté, co již uvěřil - kromě toho, kdo byl donucen, zatímco srdce jeho klidné bylo ve víře - a zejména na toho, kdo hruď svou nevíře otevřel, dopadne hněv Boží a pro něj je připraven trest nesmírný,'),
(2008, 16, 107, 'a to proto, že milovali život pozemský více než budoucí, a také proto, že Bůh nevede lid, jenž nevěří.'),
(2009, 16, 108, 'A to jsou ti, jimž Bůh zapečetil srdce, sluch i zrak, a to jsou ti lhostejní,'),
(2010, 16, 109, 'a není pochyby, že na onom světě budou mezi těmi, kdož ztrátu utrpí.'),
(2011, 16, 110, 'Pán tvůj však bude k těm, kdož se vystěhovali poté, co útisk zakusili, a poté, co bojovali a neochvějní byli - k těm zajisté bude Pán tvůj po tom všem odpouštějící a slitovný'),
(2012, 16, 111, 'v den, kdy duše každá sama sebe přijde hájit a kdy duši každé bude zaplaceno plně podle toho, co učinila, a kdy nebudou ošizeni.'),
(2013, 16, 112, 'A Bůh uvádí podobenství o městě, jež bylo bezpečné a spokojené a jemuž se dostávalo obživy v hojnosti ze všech stran, však nevděčné bylo vůči dobrodiní Božímu. A Bůh dal jim okusit oděvu hladu a strachu za to, čeho se dopustili.'),
(2014, 16, 113, 'A přišel k nim posel z jejich řad vzešlý, ale oni jej za lháře prohlásili a uchvátil je trest, zatímco nespravedliví byli.'),
(2015, 16, 114, 'Pojídejte ze všeho, co Bůh vám uštědřil jako dovolené a výtečné, a buďte vděční za dobrodiní Boží, jestliže je to vskutku On, jehož uctíváte.'),
(2016, 16, 115, 'On zakázal vám jedině zdechliny, krev, vepřové maso a to, co někomu jinému než Bohu bylo zasvěceno. Ten však, kdo z donucení tak učiní, aniž po tom toužil či přestupníkem byl - tedy Bůh zajisté je odpouštějící, slitovný.'),
(2017, 16, 116, 'Neříkejte tedy to, co jazyky vaše lživě tvrdí: "Toto dovoleno je a toto zakázáno!" vymýšlejíce si tak proti Bohu lež. Věru ti, kdož proti Bohu lži si vymýšlejí, ti nebudou mezi blaženými.'),
(2018, 16, 117, 'Jak nepatrné je užívání života tohoto, zatímco čeká je trest bolestný!'),
(2019, 16, 118, 'Těm, kdož židovství vyznávají, jsme zakázali to, co jsme ti již dříve sdělili, a neukřivdili jsme jim, leč oni sami sobě ukřivdili.'),
(2020, 16, 119, 'Nicméně Pán tvůj k těm, kdož špatného se z neznalosti dopustili, však posléze pokání konali a polepšili se - k těm Pán tvůj po tom všem věru bude odpouštějící, slitovný.'),
(2021, 16, 120, 'Abraham věru byl vůdcem Bohu oddaným, hanífem a mezi modloslužebníky nepatřil,'),
(2022, 16, 121, 'vděčný byl za dobrodiní Boha, jenž si jej vyvolil a na stezku přímou jej uvedl.'),
(2023, 16, 122, 'A darovali jsme mu dobré na tomto světě a on zajisté je na onom světě mezi bezúhonnými.'),
(2024, 16, 123, 'A pak jsme ti vnukli: "Následuj náboženství Abrahama, jenž hanífem byl, vždyť on věru mezi modloslužebníky nepatřil!"'),
(2025, 16, 124, 'Den sobotní byl ustanoven pouze pro ty, kdož o něm jsou různého mínění. Pán tvůj pak věru rozsoudí mezi nimi v den zmrtvýchvstání to, o čem ve sporu byli.'),
(2026, 16, 125, 'Vyzývej k cestě Pána svého moudrostí i kázáním krásným a veď s nimi spor slovy nejlepšími! Pán tvůj zná nejlépe ty, kdož z cesty Jeho zbloudili, a zná dobře i ty, kdož správně jsou vedeni.'),
(2027, 16, 126, 'Jestliže trestáte, tedy trestejte podobným způsobem, jako jste byli trestáni. Však jste-li trpělivosti schopni, tedy to lepší je pro trpělivé.'),
(2028, 16, 127, 'Buď trpělivý, však trpělivost tvá je možná jen s pomocí Boží! Nermuť se kvůli nim a nebuď stísněn, že úklady strojí,'),
(2029, 16, 128, 'vždyť Bůh je věru s těmi, kdož jsou bohabojní, i s těmi, kdož dobré konají.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Sláva tomu, jenž za noci přenesl služebníka Svého z Posvátné mešity do mešity nejvzdálenější, jejíž okolí jsme požehnali, abychom mu ukázali některá Svá znamení. A On věru slyšící je a jasnozřivý.'),
(2031, 17, 2, 'A darovali jsme Mojžíšovi Písmo a učinili jsme je pro dítka Izraele vedením řkouce: "Nevezmete sobě vedle Mne nikoho jiného za ochránce,'),
(2032, 17, 3, 'ó potomci těch, jež nesli jsme v arše s Noem, jenž služebníkem byl Naším vděčným!"'),
(2033, 17, 4, 'A stanovili jsme v Písmu o dítkách Izraele toto: "Podvakrát naplníte zemi pohoršením a vyniknete velkým povznesením.'),
(2034, 17, 5, 'A až dostaví se splnění slibu prvního, vyšleme proti vám služebníky Své strašnou mocí vládnoucí a ti mezi příbytky vaše proniknou; slib ten se věru uskuteční.'),
(2035, 17, 6, 'Potom vám opět dáme dosáhnout úspěchu proti nim a rozmnožíme vás v majetcích i v synech a početnějším houfem vás učiníme.'),
(2036, 17, 7, 'Konáte-li dobro, sami pro sebe tak činíte, a konáte-li špatné, proti sobě tak činíte. A až se vyplní slib druhý, pošleme na vás služebníky, aby vás poznamenali na tvářích a aby pronikli do chrámu, stejně jako tam vnikli poprvé, a aby zničili vše, čeho se zmocní, úplným zničením.'),
(2037, 17, 8, 'Možná že Pán váš se nad vámi slituje, však jestliže opět začnete, začneme i My. A učinili jsme peklo pro nevěřící vězením.'),
(2038, 17, 9, 'A věru tento Korán vede k cestě, jež nejpřímější je, a zprávu radostnou věřícím zvěstuje, kteří zbožné skutky konají, že odměna velká určena je pro ně'),
(2039, 17, 10, 'a že pro ty, kdož nevěří v život posmrtný, jsme připravili trest bolestný.'),
(2040, 17, 11, 'A člověk vyzývá ke zlu, tak jak vyzývá k dobru, vždyť člověk je unáhlený.'),
(2041, 17, 12, 'Učinili jsme noc a den dvěma znameními; znamení noci jsme zastřeli, však znamení dne jsme viditelným učinili, abyste mohli získávat v něm z dobrodiní Pána svého a abyste poznali počet roků i času počítání. A každou věc jsme učinili nejvýš srozumitelnou.'),
(2042, 17, 13, 'Každému člověku jsme připevnili ptáka osudu jeho na šíji a v den zmrtvýchvstání vyjmeme pak každému knihu jeho a setká se s ní otevřenou.'),
(2043, 17, 14, '"Čti knihu svou! Dnes sám postačíš si proti sobě jako zúčtovatel!"'),
(2044, 17, 15, 'Kdo po cestě správné se ubírá, sám pro sebe tak činí, však kdo bloudí, ten jen proti sobě tak činí. A žádná duše nákladem obtěžkaná neponese břímě jiného a nepotrestali jsme nikoho dříve, než byl námi vyslán posel.'),
(2045, 17, 16, 'A když si přejeme zahubit město nějaké, poručíme boháčům jeho, aby v něm nepravosti páchali a pak uskuteční se slovo Naše nad ním vyřčené a zahubíme je zničením dokonalým.'),
(2046, 17, 17, 'Kolik pokolení jsme již zahubili po Noemovi! Pán tvůj pak hříchů Svých služebníků znalcem je i pozorovatelem dostatečným.'),
(2047, 17, 18, 'Těm, kdož po věcech rychle pomíjivých touží, pospíšíme dát v něm to, co chceme, a tomu, komu chceme; však potom pro ně peklo připravíme, aby v něm hořeli v ponížení a v zavržení.'),
(2048, 17, 19, 'Kdož však po životě budoucím touží a usilují o něj horlivě, jsouce přitom věřící - vděčně uznáno bude jejich snažení.'),
(2049, 17, 20, 'Všem, těm i oněm, My rozmnožíme Pána tvého dary a Pána tvého dary neznají omezení.'),
(2050, 17, 21, 'Pohleď, jak jsme dali přednost jedněm před druhými! V životě budoucím věru ještě vyšší hodnosti a větší přednosti jsou!'),
(2051, 17, 22, 'A nepřidávej k Bohu božstva jiného, abys nezůstal zahanben a opuštěn.'),
(2052, 17, 23, 'Pán tvůj rozhodl, abyste nikoho kromě Něho neuctívali a abyste rodičům dobré prokazovali. A jestliže jeden či oba z nich u tebe zestárnou, neříkej jim "Fuj!" a neodbývej je stroze, nýbrž mluv s nimi slovem laskavým!'),
(2053, 17, 24, 'Skloň k nim oběma z milosrdenství křídla pokory a řekni: "Pane můj, smiluj se nad nimi oběma, tak jako oni mě vychovali, když jsem byl malý!".'),
(2054, 17, 25, 'Pán váš nejlépe ví, co v duších vašich je a jste-li zbožní, On věru ke kajícníkům je odpouštějící.'),
(2055, 17, 26, 'A dávej příbuznému po právu, a také chuďasovi a po cestě Boží jdoucímu, však nerozhazuj rozhazováním,'),
(2056, 17, 27, 'vždyť marnotratní jsou bratři satanovi a satan byl vůči Pánu svému nevděčný.'),
(2057, 17, 28, 'Jestliže se od nich odvrátíš čekaje na milosrdenství Pána svého, ve které doufáš, potěš je alespoň slovem útěšným!'),
(2058, 17, 29, 'A neměj ruku svou přivázanou ke krku svému ani ji nerozevírej příliš široce, abys nebyl pomlouván a neupadl v nouzi.'),
(2059, 17, 30, 'Pán tvůj otevřenou rukou uštědřuje obživu anebo ji odměřuje, komu chce, neboť On dobře je zpraven o služebnících Svých a jasně je zří.'),
(2060, 17, 31, 'Nezabíjejte děti své z obavy před zchudnutím, vždyť My jim i vám obživu uštědříme. A jejich zabíjení je hříchem velkým.'),
(2061, 17, 32, 'A nepřibližujte se k cizoložství, neboť to ohavnost je i špatná cesta.'),
(2062, 17, 33, 'A nezabíjejte osoby, jež Bůh zakázal zabíjet, leda podle práva. A byl-li někdo zabit nespravedlivě, dali jsme jeho nejbližšímu pravomoc jej pomstít, nechť však při zabití nepřehání, vždyť zajisté mu bude pomoženo.'),
(2063, 17, 34, 'A nepřibližujte se k majetku sirotka, leda způsobem co nejlepším, pokud nedosáhne dospělosti. Dodržujte věrně závazky své, neboť žádáno bude zúčtování o závazku!'),
(2064, 17, 35, 'A buďte spravedliví v míře, když odměřujete, a važte vahami přímými - to pro vás je lepší i nejlepší co do výsledku.'),
(2065, 17, 36, 'A nenásleduj to, o čem vědění nemáš, vždyť sluch, zrak i srdce budou všechny žádány, aby počet vydaly.'),
(2066, 17, 37, 'A nevykračuj si po zemi troufale, vždyť zemi nemůžeš rozpoltit a nedosáhneš výškou svou hor vrcholku.'),
(2067, 17, 38, 'To všechno je špatností u Pána tvého a je mu to odporné.'),
(2068, 17, 39, 'A toto je část toho, co vnukl ti Pán tvůj z moudrosti Své. A nedávej vedle Boha božstvo jiné, bys nebyl do pekla uvržen, potupen a zatracen!'),
(2069, 17, 40, 'Což Pán váš přednostně vás syny vyznamenal, zatímco sám pro sebe si z andělů dcery vzal? Vy věru hovoříte slova nehorázná!'),
(2070, 17, 41, 'A vysvětlili jsme to v Koránu tomto, aby se upamatovali, avšak to jen rozmnožilo odpor jejich.'),
(2071, 17, 42, 'Rci: "Kdyby byla vedle Něho ještě božstva jiná, jak říkají, věru by usilovala najít k Pánu trůnu cestu.'),
(2072, 17, 43, 'Sláva Jemu - a oč vznešenější je On než to, co o něm říkají, povýšením velikým!"'),
(2073, 17, 44, 'A pěje slávu Jeho sedm nebes a země i vše, co na nich je. A není věci, jež by chválu Jeho neslavila, avšak vy nevěřící slavení jejich nechápete. On věru mírný je i odpouštějící.'),
(2074, 17, 45, 'Když přednášíš Korán, spouštíme mezi tebe a ty, kdož nevěří v život budoucí, závěs zahalující'),
(2075, 17, 46, 'a na srdce jejich clony klademe, takže jej nechápou, a hluchotu do jejich uší: A když se v Koránu zmiňuješ o Pána svého jedinosti, zády se obracejí s nechutí.'),
(2076, 17, 47, 'My dobře víme, jakým způsobem naslouchají, když k tobě uši natahují a když tajně rozmlouvají a když nespravedliví říkají: "Vy následujete jen člověka očarovaného!"'),
(2077, 17, 48, 'Pohleď jen, jaká podobenství o tobě uvádějí, jak bloudí a nalézt cestu nemohou'),
(2078, 17, 49, 'a říkají: "Až staneme se kostmi a prachem, budeme vzkříšeni nějakým novým stvořením?"'),
(2079, 17, 50, 'Odpověz: "I kdybyste byli kamenem nebo železem'),
(2080, 17, 51, 'či jakýmkoliv stvořením, jehož vzkříšení se zdá nemožné hrudím vašim!" A řeknou: "A kdo nás k životu navrátí?" Odpověz: "Ten, jenž stvořil vás poprvé." A budou nad tebou hlavami potřásat a zeptají se: "A kdy to bude?" Odpověz jim: "Možná že je to už blízko!'),
(2081, 17, 52, 'V ten den, kdy On vás zavolá a vy odpovíte voláním chvály Jeho, usoudíte, že zůstali jste v hrobech svých jen maličko."'),
(2082, 17, 53, 'Řekni služebníkům Mým, aby hovořili jen to, co nejlepší je. A satan věru rozsévá mezi vámi rozkol, vždyť satan člověku je zjevným nepřítelem.'),
(2083, 17, 54, 'Pán váš zná vás nejlépe, a zachce-li se Mu, smiluje se nad vámi, a bude-li chtít, potrestá vás. A neposlali jsme tě, abys byl nad nimi dozorcem.'),
(2084, 17, 55, 'Pán tvůj zná nejlépe ty, kdož na nebesích jsou i na zemi. A věru jsme posly některé nad druhými vyznamenali a Davidovi jsme žaltář dali.'),
(2085, 17, 56, 'Rci: "Jen si vzývejte ty, jež jste si vedle Něho vymyslili! Ti mocí žádnou nevládnou, aby od vás neštěstí odvrátili či je změnili."'),
(2086, 17, 57, 'Ti, které vzývají, sami prostředek hledají, jak co nejvíce se přiblížit k Pánu svému, v milost Jeho doufajíce a trestu Jeho se obávajíce, neboť Pána tvého trest je věru obav hodný.'),
(2087, 17, 58, 'A nebylo města bezbožného, jež bychom nebyli zahubili či trestem strašným nepotrestali přede dnem zmrtvýchvstání; a takto je to napsáno v Knize původní.'),
(2088, 17, 59, 'A zabránilo nám v tom, abychom znamení seslali, leda to, že národy dávné za lež je prohlašovaly. A Thamúdovcům jsme velbloudici jako ukázku znamení dali, však bezbožně s ní nakládali. A My znamení Svá jen pro postrašení sesíláme.'),
(2089, 17, 60, 'A hle, pravili jsme ti: "Pán tvůj objímá lidi (ve vševědoucnosti své)!" A učinili jsme vidění, jež jsme ti ukázali, jen pokušením pro lidi a taktéž i strom prokletý v Koránu. My strach jim naháníme, však to jen jejich vzpurnost velkou posiluje.'),
(2090, 17, 61, 'A hle, pravili jsme k andělům: "Padněte na zem před Adamem!" I padli všichni kromě Iblíse, jenž řekl: "Mám padnout na zem před tím, jehož jsi z hlíny stvořil?'),
(2091, 17, 62, 'Vidíš tohoto, jehož jsi poctil více než mne? Dáš-li mi odklad až do dne zmrtvýchvstání, já věru zkrotím potomstvo jeho všechno kromě několika málo!"'),
(2092, 17, 63, 'I řekl Bůh: "Odejdi! A kdo z nich tě bude následovat, tomu peklo bude odměnou bohatou.'),
(2093, 17, 64, 'Sváděj tedy hlasem svým ty z nich, které můžeš, a zaútoč na ně jízdou svou i pěchotou, staň se společníkem majetku jejich i dětí a sliby jim čiň, vždyť sliby satanovy jsou jen šalbou pouhou.'),
(2094, 17, 65, 'Nad služebníky Mými však pravomoc nebudeš mít žádnou!" Pán tvůj pak stačí jim co ochránce!'),
(2095, 17, 66, 'Pán váš je ten, jenž pro vás sune lodi na moři, abyste mohli hledat podíl z dobrodiní Jeho; a On vůči vám je slitovný.'),
(2096, 17, 67, 'A když postihne vás pohroma na moři, vzdálí se od vás ti, které vedle Něho vzýváte, když však zachrání vás na pevnině, tu odvrátíte se - vždyť člověk je věru nevděčný.'),
(2097, 17, 68, 'Jste si tak jisti, že On s vámi nepotopí okraj souše nebo že na vás nepošle bouři písečnou? Potom už nenajdete pro sebe ochránce jediného.'),
(2098, 17, 69, 'Anebo jste si tak jisti, že vás neuvrhne do tohoto nebezpečí podruhé a že na vás nepošle vítr bouřlivý a nezahubí vás za to, že jste byli nevděční? Potom už nenajdete proti Nám pomocníka žádného.'),
(2099, 17, 70, 'Věru jsme milostí Svou syny Adamovy poctili a na pevnině i na moři jsme je nosili a obživu jsme jim uštědřili výtečnou a před mnohými z těch, jež jsme stvořili, přednost jsme jim dali významnou.'),
(2100, 17, 71, 'Přijde den, kdy zavoláme lidi všechny i s jejich záznamy; a komu bude dána kniha jeho do pravice - ti čísti budou z knihy své a nebudou ani o nitku ošizeni.'),
(2101, 17, 72, 'A kdo v tomto životě slepý byl, bude slepý i v onom a ještě více z cesty zbloudí.'),
(2102, 17, 73, 'A věru by tě byli nevěřící málem zlákali od toho, co jsme ti vnukli, aby sis o Nás vymyslil něco jiného -a tehdy byli by si tě vzali za přítele svého.'),
(2103, 17, 74, 'A kdybychom tě nebyli posílili, byl by ses málem k nim přiklonil, byť i nemnoho...'),
(2104, 17, 75, 'Tehdy bychom ti byli dali okusit dvojnásob v životě i dvojnásob ve smrti a nebyl bys našel proti Nám pomocníka žádného.'),
(2105, 17, 76, 'A byli by tě málem donutili k opuštění země této, aby tě z ní vyhnali. Avšak zůstali by v ní po odchodu tvém jen nedlouho,'),
(2106, 17, 77, 'podle obvyklého osudu těch poslů, jež vyslali jsme již před tebou - vždyť ve zvyklostech Našich nenalezneš odchýlení nijakého.'),
(2107, 17, 78, 'Konej modlitbu při sklonku slunce až do setmění nočního a přednášej Korán za jitra, vždyť přednášení jitřní je před svědky konáno.'),
(2108, 17, 79, 'A za noci bdi v přednášení z dodatečné horlivosti pro sebe - možná že Pán tvůj tě pošle na místo, jež je chváleno.'),
(2109, 17, 80, 'A rci: "Pane, dej mi vstoupit vstoupením spravedlivým a dej mi vyjít vyjitím spravedlivým. A dej mi od Sebe zplnomocnění jako pomoc!"'),
(2110, 17, 81, 'A rci: "Přišla pravda a zmizel klam, a věru klam je něčím, co zmizí."'),
(2111, 17, 82, 'A sesíláme v Koránu to, co lékem je i milosrdenstvím pro věřící, však nespravedlivým to jen ztrátu zvětšuje.'),
(2112, 17, 83, 'Když člověka dobrodiním zahrnujeme, odvrací se a vzdaluje se, když však neštěstí se ho dotkne, tu zoufalý je.'),
(2113, 17, 84, 'Rci: "Každý jedná podle způsobu svého a Pán váš dobře ví, kdo nejlépe je veden po cestě Jeho."'),
(2114, 17, 85, 'A vyptávat se tě budou na Ducha. Odpověz: "Duch přichází z rozkazu Pána mého a bylo vám dáno z vědění, jen ne mnoho."'),
(2115, 17, 86, 'Kdybychom chtěli, věru bychom odnesli to, co jsme ti vnukli, a potom bys nenašel proti Nám ochránce žádného ve věci té,'),
(2116, 17, 87, 'leda z milosrdenství Pána svého, jehož přízeň vůči tobě veliká je zajisté.'),
(2117, 17, 88, 'Rci: "I kdyby se lidé a džinové spojili, aby něco podobného Koránu tomuto vytvořili, nebyli by schopni přijít s něčím podobným, byť by si vzájemně byli pomocníky."'),
(2118, 17, 89, 'A v Koránu tomto jsme vskutku lidem různé druhy podobenství vyložili, avšak většina z nich vše kromě nevíry odmítá'),
(2119, 17, 90, 'a říká: "Neuvěříme ti, pokud nedáš vytrysknout před námi ze země prameni'),
(2120, 17, 91, 'nebo nebudeš mít zahradu s datlovníky a vinnou révou a nedáš řekám s hojností vody téci mezi nimi'),
(2121, 17, 92, 'či dokud nenecháš, jak tvrdíš, spadnout na nás nebe po kusech anebo si nepřivedeš Boha a anděly jako ručitele'),
(2122, 17, 93, 'či pokud nebudeš mít dům se zlatými ozdobami anebo sám nevystoupíš na nebe. A neuvěříme ve vystoupení tvé na nebe, pokud nám z něho nesešleš Knihu, již bychom čísti mohli." Odpověz: "Sláva Pánu mému, což nejsem toliko smrtelníkem a poslem?"'),
(2123, 17, 94, 'Co brání těmto lidem, aby uvěřili, když se jim dostalo správného vedení, ne-li to, že říkají: "Cožpak by Bůh vyslal smrtelníka jako posla Svého?"'),
(2124, 17, 95, 'Odpověz: "Kdyby na zemi andělé byli a v míru po ní chodili, věru bychom k nim seslali z nebe anděla jako posla Svého."'),
(2125, 17, 96, 'Rci: "Bůh stačí mi jako svědek mezi mnou a vámi, neboť On o služebnících Svých dobře zpraven je a jasně zří je."'),
(2126, 17, 97, 'Ten, koho Bůh vede, dobře je veden, však pro ty, jimž zbloudit dal, nenalezneš ochránce mimo Něho. A v den zmrtvýchvstání je shromáždíme plazící se na tvářích, slepé, němé a hluché; útočištěm jejich pak bude peklo, a kdykoliv uhasínat bude, My přidáme více žáru pro ně.'),
(2127, 17, 98, 'Toto odměnou jejich bude za to, že ve znamení Naše nevěřili a říkali: Až staneme se kostmi a prachem, budeme vzkříšeni nějakým novým stvořením?'),
(2128, 17, 99, 'Což nevidí, že Bůh, jenž stvořil nebesa a zemi, je schopen stvořit jiné podobné jim? A stanovil jim lhůtu, o níž pochyby není, avšak nespravedliví odmítají vše kromě nevíry.'),
(2129, 17, 100, 'Rci: "Kdybyste vlastnili pokladnice milosrdenství Pána mého, věru byste je střežili ze strachu před spotřebováním, neboť člověk je velmi lakomý."'),
(2130, 17, 101, 'A kdysi jsme dali Mojžíšovi devět znamení zřetelných. Zeptej se dítek Izraele na to, jak přišel k oněm a Faraón mu řekl: "Mojžíši, já domnívám se, žes očarovaný!"'),
(2131, 17, 102, 'Mojžíš odpověděl: "Ty věru víš, že to byl jedině Pán nebes a země, jenž seslal to jako znamení objasňující. A já se domnívám, Faraóne, že ke zkáze jsi odsouzený!"'),
(2132, 17, 103, 'A chtěl je Faraón ze země vypudit, avšak utopili jsme jej i ty, kdož byli s ním, všechny'),
(2133, 17, 104, 'a řekli jsme poté dítkám Izraele: "Obývejte tuto zem, a až přijde čas slíbený Hodiny poslední, v houfu vás přivedeme!"'),
(2134, 17, 105, 'A seslali jsme jej s pravdou a s pravdou také sestoupil. A vyslali jsme tě pouze, abys hlasatelem byl zvěsti radostné a varovatelem.'),
(2135, 17, 106, 'A Korán jsme rozdělili, abys jej přednášel lidem pozvolna, a seslali jsme jej sesláním opakovaným.'),
(2136, 17, 107, 'Rci: "Věřte v něj, nebo nevěřte - ti, kterým dáno vědění bylo již předtím, ti na tváře své padají sklánějíce se, když přednášen je jim,'),
(2137, 17, 108, 'a pronášejí: Sláva Pánu našemu, slib Pána našeho se věru vyplnil!'),
(2138, 17, 109, 'A padali na tváře své plačíce, v pokoře vzrůstající."'),
(2139, 17, 110, 'Rci: "Vzývejte Boha či vzývejte Milosrdného, a ať již Jej nazýváte jakýmkoliv jménem, Jemu náleží jména nejkrásnější. A při modlitbě své nemluv ani příliš nahlas, ani příliš potichu, nýbrž hledej mezi obojím cestu střední!"'),
(2140, 17, 111, 'Rci: "Chvála Bohu, jenž nevzal si pro sebe dítě žádné, jenž nemá společníka ve vládě a nepotřebuje před ponížením ochránce!" A oslavuj velikost Jeho!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Sláva Bohu, jenž seslal služebníku Svému Knihu a neučinil ji křivolakou,'),
(2142, 18, 2, 'nýbrž přímou, aby varovala před mocnou přísností od Něho přicházející a aby oznámila věřícím, kteří zbožné skutky konali, že obdařeni budou odměnou překrásnou,'),
(2143, 18, 3, 'zahradou to, v níž navěky zůstanou,'),
(2144, 18, 4, 'a aby varovala ty, kdo říkají, že Bůh pro Sebe si vzal děti.'),
(2145, 18, 5, 'A nemají o tom oni ani otcové jejich žádného vědění; jak hrozné je slovo, jež z jejich úst vychází, a nic jiného než lež oni nemluví.'),
(2146, 18, 6, 'Chceš se snad zahubit smutkem jda ve stopách jejich, jestliže tomuto příběhu neuvěří?'),
(2147, 18, 7, 'To, co na zemi jest, učinili jsme okrasou její, abychom vyzkoušeli, kdo z nich v konání svém je nejlepší.'),
(2148, 18, 8, 'A věru uděláme ze všeho toho, co na ní je, výšinu rostlinstva zbavenou!'),
(2149, 18, 9, 'Domníváš se, že lidé jeskyně a ar-Raqímu byli zázrakem jedním z Našich znamení?'),
(2150, 18, 10, '... A hle, mladíci se do jeskyně uchýlili a pravili: "Pane náš, daruj nám od Sebe milosrdenství a připrav nám pro věc naši správné vedení!"'),
(2151, 18, 11, 'A zakryli jsme uši jejich v jeskyni té během roků mnohých,'),
(2152, 18, 12, 'potom jsme je vzkřísili, abychom poznali, která z obou stran lépe odhadne délku pobytu jejich.'),
(2153, 18, 13, 'My vyprávíme ti příběh jejich pravdivě; vždyť mladíci to byli, kteří v Pána svého uvěřili, a jejich vedení po cestě správné jsme upevnili.'),
(2154, 18, 14, 'A srdce jejich jsme posílili, když vstali a řekli: "Náš Pán je Pánem nebes a země a nikdy nebudeme vzývat božstvo jiné kromě Něho, jinak bychom lež nehoráznou hovořili.'),
(2155, 18, 15, 'Tento náš lid si vzal místo Něho božstva různá, aniž o nich přinesl jasné zmocnění! A kdo je nespravedlivější než ten, kdo proti Bohu lži si vymýšlí?"'),
(2156, 18, 16, 'A až se oddělíte od lidí těch a od toho, co uctívají vedle Boha, uchylte se do jeskyně! Pán váš nad vámi část milosrdenství Svého rozprostře a ve věci vaší vám ulehčení připraví.'),
(2157, 18, 17, 'A byl bys viděl, jak slunce se při svém východu sklání napravo od jeskyně jejich a při svém západu je míjí nalevo, zatímco oni byli v středu jejím. A toto je jedno ze znamení Božích; ten, koho Bůh vede, ten po správné cestě kráčí, avšak pro toho, komu Bůh dal zbloudit, nenajdeš ochránce ani průvodce žádného.'),
(2158, 18, 18, 'A byl bys je pokládal za bdící, zatímco oni spali a My je napravo i nalevo kolébali; a pes jejich s předními tlapami roztaženými na prahu ležel. A kdybys je byl náhodou spatřil, byl by ses dal před nimi na útěk pln strachu z nich hrozného.'),
(2159, 18, 19, 'A tak jsme je vzkřísili, aby se jeden druhého vyptávali. I pravil jeden z nich: "Jak dlouho jsme zde setrvali?" Odpověděli: "Setrvali jsme zde den či část dne jednoho." (Nakonec) řekli: "Pán náš zná nejlépe, jak dlouho jsme zde zůstali. Vyšleme jednoho z nás do města s těmito penězi svými, nechť zjistí, kdo nejlepší pokrmy má, a nechť nám od něho potravu přinese; a nechť jedná obezřetně a slušně, aby na nás neupozornil nikoho.'),
(2160, 18, 20, 'Kdyby nás odkryli, ukamenovali by nás anebo by nás donutili vrátit se k náboženství svému; a pak již nikdy bychom nebyli blaženi."'),
(2161, 18, 21, 'A tak jsme je odhalili (lidem z města), aby poznali, že slib Boží je pravdivý a že o Hodině pochyby žádné není. A hle, lidé se mezi sebou hádali ohledně věci jejích, a říkali jedni: "Vystavme nad nimi stavbu nějakou; Pán jejich přece o nich dobře ví!" A pravili ti, kdož ve sporu o věc tu zvítězili: "Vystavíme nad nimi svatyni!"'),
(2162, 18, 22, 'A budou říkat někteří: "Byli tři a čtvrtý z nich byl pes jejich." Jiní řeknou: "Bylo jich pět a šestý z nich byl pes jejich" dohadujíce se o nepoznatelném; a další řeknou: "Sedm, a osmým z nich byl pes jejich." Odpověz: "Pán můj zná nejlépe počet jejich; a zná to jinak jen lidi nemnoho!" Nehádej se tedy o nich, leda jen způsobem esoterickým, a nevyptávej se na ně (z nevěřících) nikoho'),
(2163, 18, 23, 'a neříkej o ničem: "Já zítra učiním to a to,"'),
(2164, 18, 24, 'aniž připojíš: "Bude-li chtít Bůh." A vzpomínej Pána svého, když zapomnětlivý jsi, a řekni: "Snad Pán můj mne dovede k tomu, co je správnější než toto!"'),
(2165, 18, 25, 'A setrvali oni v jeskyni své tři sta let a přidáno je k tomu ještě devět.'),
(2166, 18, 26, 'Rci: "Bůh zná nejlépe, jak dlouho tam zůstali, a Jemu náleží nepoznatelné na nebesích a na zemi a On nejlépe je vidoucí a slyšící! A nemají oni kromě Něho ochránce žádného a On nečiní nikoho společníkem rozhodnutí Svého!"'),
(2167, 18, 27, 'Sděluj tedy to, co ti bylo vnuknuto z Knihy Pána tvého; a není nikoho, kdo mohl by změnit slovo Jeho, a nenalezneš kromě Něho útočiště žádného.'),
(2168, 18, 28, 'Buď neochvějný v duši své spolu s těmi, kdož Pána svého zrána i navečer vzývají po Jeho tváři toužíce! A neodvracej oči své od nich v žádosti po krásách života pozemského a neposlouchej toho, jehož srdce jsme učinili lhostejným k připomenutí Svému, ani toho, jenž jen vášně své následuje a jehož chování míru překračuje!'),
(2169, 18, 29, 'A rci: "Pravda přichází od Pána vašeho; kdo chce, ať věří, a kdo chce, ať nevěří!" A věru jsme připravili pro nespravedlivé oheň, jenž hustým dýmem je obklopí. A poprosili o pomoc, bude jim pomoženo vodou podobnou kovu roztavenému, jež spálí tváře jejich. A jak odporný to nápoj a jak hnusné to místo pobytu!'),
(2170, 18, 30, 'Ti však, kdož uvěřili a zbožné skutky konali, budou odměněni, a nedopustíme, aby se ztratila odměna těch, kdož dobré činili.'),
(2171, 18, 31, 'Jim budou patřit zahrady Edenu, pod nimiž řeky tekou; tam budou se náramky zlatými zdobit, do šatů zelených z hedvábí a brokátu se odívat a v nich na lehátkách odpočívat. Jak krásná to odměna a jak výtečné to místo pobytu!'),
(2172, 18, 32, 'Uveď jim podobenství o dvou mužích, z nichž jednomu jsme dvě zahrady révy vinné dali, obklopené palmami, a mezi nimi jsme obilí zaseli;'),
(2173, 18, 33, 'a obě zahrady plody své přinesly a pána svého v ničem nezklamaly a mezi oběma jsme řece téci dali.'),
(2174, 18, 34, 'A měl ovoce a řekl druhu svému při rozhovoru: "Mám více majetku než ty a mocnější jsem i rodem svým."'),
(2175, 18, 35, 'Pak do zahrady své vstoupil a sám sobě ukřivdil říkaje: "Že někdy toto zanikne, si nemyslím,'),
(2176, 18, 36, 'a nedomnívám se, že kdy dostaví se Hodina; a věru i kdybych navrácen byl k Pánu svému, něco lepšího než toto naleznu tam výměnou."'),
(2177, 18, 37, 'I pravil mu druh jeho v rozhovoru: "Chceš být nevděčný vůči tomu, jenž stvořil tě z prachu, pak z kapky semene a nakonec tě obdařil lidskou podobou?'),
(2178, 18, 38, 'Však já hovořím: On je Bůh, Pán můj, a já k Pánu svému nepřidružuji nikoho.'),
(2179, 18, 39, 'Kéž bys byl, když do zahrady své jsi vstoupil, pronesl: Nechť stane se, co Bůh chce, a není moci kromě u Boha! A vidíš-li mne majetkem a dětmi chudšího,'),
(2180, 18, 40, 'možná, že mne Bůh obdaří lepším, než tvá je zahrada. A možná že postihne ji z nebes pohromou, takže stane se jednou zrána zemí pustou,'),
(2181, 18, 41, 'či voda její v hlubinách země zmizí a tobě se nezdaří nalézt novou."'),
(2182, 18, 42, 'A stalo se tak a plody jeho byly zničeny a jal se majitel lomit rukama nad tím, co vynaložil na ni a že od základů je zpustošena nyní, a volal: "Kéž bych byl nikoho k Pánu svému nepřidružoval!"'),
(2183, 18, 43, 'A neměl čeleď žádnou, jež by mu pomohla kromě Boha, a nedočkal se pomoci.'),
(2184, 18, 44, 'A tady přísluší ochrana jen Bohu skutečnému; a On nejlepší je v odměňování a k nejlepšímu vede konci.'),
(2185, 18, 45, 'A uveď jim podobenství o životě pozemském: podobá se vodě, již sesíláme z nebe a s níž se mísí rostlinstvo země a pak stane se suchou travinou, již větry rozvanou. A Bůh všemocný je nad věcí každou.'),
(2186, 18, 46, 'Majetek a synové jsou života pozemského ozdobou, však zbožné skutky, jež trvalé hodnoty jsou, u Pána tvého lepší odměnu i naději míti budou'),
(2187, 18, 47, 'v den, kdy horám dáme kráčet a kdy uvidíš zemi jak rovinu srovnanou; a tehdy shromáždíme duše všechny, aniž opomineme z nich jedinou.'),
(2188, 18, 48, 'A předvedeni před Pána tvého v řadách budou. "Teď přišli jste k Nám tak, jak jsme vás poprvé stvořili; a přece jste tvrdili, že neustanovíme pro vás lhůtu žádnou."'),
(2189, 18, 49, 'A předložena bude Kniha každého a uvidíš provinilé v obavách z toho, co v ní je obsaženo. A řeknou:,, Běda nám, co je to za knihu, že ani malé, ani velké skutky zaznamenat neopominula?" A naleznou před sebou vše, co dělali, a neošidí Pán tvůj z nich jediného.'),
(2190, 18, 50, 'A hle, pravili jsme andělům: "Padněte na zem před Adamem!" A padli na zem všichni kromě Iblíse, jenž mezi džiny patřil a rozkazu Pána svého se zprotivil. Zdaž vezmete si jej a potomky jeho jako ochránce místo Mne, ačkoliv vašimi jsou nepřáteli? Jak špatná to bude výměna pro nespravedlivé!'),
(2191, 18, 51, 'Já neučinil jsem je svědky stvoření nebes a země ani jejich vlastního stvoření; a nevzal jsem si ty, kdož k bludu navádějí, za pomocníky Své.'),
(2192, 18, 52, 'V den, kdy Bůh řekne:,, Zavolejte ty, jež pokládali jste za společníky Mé!", budou je vzývat, však vyslyšeni nebudou, neboť mezi nimi umístíme propast zániku;'),
(2193, 18, 53, 'a provinilí spatří oheň pekelný a usoudí, že doň budou svrženi a nenaleznou z něho úniku.'),
(2194, 18, 54, 'A věru jsme předložili lidem v Koránu tomto všemožné druhy příkladů, však člověk rád se o většině věcí pře.'),
(2195, 18, 55, 'Nic nebrání lidem uvěřit či prosit Pána svého o odpuštění, když k nim přišlo správné vedení - leda odmítnutí připustit, že dostaví se k nim obvyklý osud dávných anebo že trest je zblízka postihne.'),
(2196, 18, 56, 'A vysíláme posly Své jen jako hlasatele zvěsti radostné a jako varovatele. Avšak ti, kdož nevěří, se hádají užívajíce falešnosti, aby jí pravdu vyvrátili, a přijímají s posměchem znamení Má i to, před čím varováni jsou.'),
(2197, 18, 57, 'A kdo nespravedlivější je než ten, jenž při zmínce o znameních Pána svého se od nich odvrací a zapomíná na to, co ruce jeho předtím konaly? A srdce jejich jsme pokryli clonou, aby mu neporozuměli, a uši jejich jsme ucpali hluchotou. A i kdybys je k cestě správné vyzval, přece ji nikdy následovat nebudou.'),
(2198, 18, 58, 'Pán tvůj je odpouštějící i pln milosrdenství. Kdyby je chtěl uchvátit za to, co si vysloužili, byl by jim jistě uspíšil trest, avšak mají lhůtu slíbenou a jinde než u Boha útočiště žádné nenajdou.'),
(2199, 18, 59, 'A zahubili jsme obyvatele oněch měst, když stali se nespravedlivými, a stanovili jsme pro záhubu jejich lhůtu slíbenou.'),
(2200, 18, 60, 'A hle, pravil Mojžíš sluhovi svému: "Neustanu, dokud nedospěji k soutoku dvou moří, i kdybych měl věky celé jíti tomu vstříc."'),
(2201, 18, 61, 'A když oba dospěli k soutoku moří dvou, zapomněli na rybu svou, jež dala se svou cestou v moře unikajíc.'),
(2202, 18, 62, 'A když oba ono místo minuli, pravil Mojžíš svému sluhovi: "Přines nám oběd náš, věru nás únava z této naší cesty potkala."'),
(2203, 18, 63, 'Odpověděl: "Což nevidíš, že když jsme se u skály ukrývali, zapomněl jsem na tu rybu? A jedině satan způsobil, že na ni jsem zapomněl, a nedal mi vzpomenout si na ni a ona na cestu svou v moři se zázračně vydala."'),
(2204, 18, 64, 'I řekl Mojžíš: "To právě je to, co hledáme." A oba se obrátili a po stopách svých se navrátili.'),
(2205, 18, 65, 'A nalezli jednoho ze služebníků Našich, jehož jsme milosrdenstvím Svým obdařili a jehož jsme vědění z Nás vycházejícímu naučili,'),
(2206, 18, 66, 'a Mojžíš mu řekl: "Smím tě následovat, abys mne naučil něčemu ze správného vedení, o němž jsi byl poučen?"'),
(2207, 18, 67, 'Odpověděl: "Ty trpělivost se mnou mít nebudeš schopen;'),
(2208, 18, 68, 'a jak bys také mohl mít trpělivost v tom, co je mimo rozsah vědění tvého?"'),
(2209, 18, 69, 'Avšak Mojžíš řekl: "Bude-li Bůh chtít, shledáš mě trpělivým a já neoslyším rozkazu žádného."'),
(2210, 18, 70, '"Chceš-li mne následovat, tedy se mne neptej na nic, pokud já sám tě nevybídnu svou zmínkou."'),
(2211, 18, 71, 'A pokračovali dále, a když pak na lodi pluli, služebník Náš ji provrtal. I otázal se Mojžíš: "Provrtals ji, abys utopil ty, kdož na ní cestují? Tys věru učinil věc nevídanou!"'),
(2212, 18, 72, 'Odvětil: "Neřekl jsem ti, že nebudeš schopen mít trpělivost se mnou?"'),
(2213, 18, 73, 'I řekl Mojžíš: "Nekárej mne za to, že zapomněl jsem, a neukládej mi ve věci mé zkoušku těžkou!"'),
(2214, 18, 74, 'A pokračovali dále, až potkali chlapce nějakého; i zabil jej a Mojžíš zvolal: "Zdaž zabils duši nevinnou, aniž právo pomsty jsi měl? Tys věru spáchal věc ohavnou!"'),
(2215, 18, 75, 'Odpověděl: "Neřekl jsem ti, že nebudeš schopen mít trpělivost se mnou?"'),
(2216, 18, 76, 'Mojžíš pravil: "Jestliže se tě po tomto ještě zeptám na cokoliv, tedy se zbav společnictví mého; a již se ti dostalo ode mne omluvy."'),
(2217, 18, 77, 'A pokračovali dále, a když přišli k obyvatelům města jednoho, o jídlo je požádali, však lidé ti je pohostit odmítli. A nalezli zde zeď, jež spadnutím hrozila; když pak ji služebník Náš do pořádku přivedl, pravil mu Mojžíš: "Kdybys chtěl, mohlo by se ti za to dostat odměny."'),
(2218, 18, 78, 'I odvětil: "Toto znamená rozchod mezi mnou a tebou, nicméně ti chci vysvětlení toho sdělit, v čem nebyls s to trpělivost mít.'),
(2219, 18, 79, 'Co týká se lodi oné, ta patřila chudákům, kteří na moři pracují. A chtěl jsem ji poškodit, aby ztratila cenu pro krále jednoho, jenž na ně číhá a jenž každou dobrou loď násilím jímá.'),
(2220, 18, 80, 'A co týká se chlapce toho, rodiče jeho byli věřící, a obávali jsme se, že vzpurností a nevděkem svým on zavede je oba,'),
(2221, 18, 81, 'a přáli jsme si, aby Pán jejich jim jej vyměnil za jiného, jenž bezúhonnější aby byl a lásky jejich více byl hoden.'),
(2222, 18, 82, 'A co se týká té zdi, ta patřila dvěma chlapcům sirotkům ve městě tom a byl pod ní poklad pro ně předurčený; a otec jejich muž bezúhonný byl a Pán tvůj si přál, aby oni nejdříve dospělosti své dosáhli a teprve pak poklad svůj odkryli z milosrdenství Pána tvého. A já pak nic z toho z rozhodnutí vlastního nečinil; a toto je výklad toho, v čem tys trpělivosti nebyl schopen."'),
(2223, 18, 83, 'A vyptávají se tě na Dvourohého. Odpověz: "Budu vám vyprávět příběh o něm."'),
(2224, 18, 84, 'Upevnili jsme moc jeho na zemi a zahrnuli jsme jej prostředky k věcem všem.'),
(2225, 18, 85, 'A on za nebeským šel lanem,'),
(2226, 18, 86, 'a když dospěl k slunce západu, shledal, že zapadá v prameni vroucím, a nalezl lid nějaký poblíže. I řekli jsme mu: "Dvourohý, na tobě je, zda potrestáš je, či zda zachováš se k nim dobře."'),
(2227, 18, 87, 'Odpověděl: "Toho, kdo z nich je nespravedlivý, potrestám a potom bude navrácen k Pánu svému, jenž potrestá jej trestem odporným.'),
(2228, 18, 88, 'A co týká se toho, kdo věří a zbožné skutky konal, tomu dostane se odměny překrásné a rozkaz, jejž dáme mu, bude snadný."'),
(2229, 18, 89, 'Potom sledoval lano jiné,'),
(2230, 18, 90, 'a když dospěl k slunce východu, shledal, že vychází nad lidem, jemuž jsme nedali na ochranu proti němu clony žádné.'),
(2231, 18, 91, 'A tak tomu bylo a My jsme již obsáhli ve vědění Svém vše, co u něho bylo.'),
(2232, 18, 92, 'Po té sledoval další lano,'),
(2233, 18, 93, 'až k místu mezi dvěma hrázemi dospěl a pod nimi lid, jenž sotva chápal řeč mluvenou, našel.'),
(2234, 18, 94, 'I řekli mu: "Dvourohý, věru Gog a Magog šíří spoušť po zemi; máme ti poplatek zaplatit, abys mezi námi a jimi hráz vystavěl?"'),
(2235, 18, 95, 'Odpověděl: "To, co mi propůjčil Pán můj, je lepší než poplatek. Pomozte mi horlivostí svou a zřídím mezi vámi a jimi hráz mocnou.'),
(2236, 18, 96, 'Přineste mi kusy železa!" A když je narovnal mezi dvě úbočí, rozkázal: "Foukejte!" Když z toho vznikl oheň, dodal: "Přineste mi k vylití do něho mosaz roztavenou!"'),
(2237, 18, 97, 'A nebyli Gog a Magog schopni ji přelézt ani otvor udělat v ní.'),
(2238, 18, 98, 'I řekl Dvourohý: "Toto je od Pána mého milosrdenství, a až splní se slib Pána mého, na prach ji rozdrtí, vždyť Pána mého slib je vždy pravdivý."'),
(2239, 18, 99, 'V ten den je ponecháme, aby jeden na druhého ve vlnách dorážel. A bude zatroubeno na pozoun a shromáždíme je všechny najednou.'),
(2240, 18, 100, 'V ten den pak představíme peklo nevěřícím rovnou,'),
(2241, 18, 101, 'těm, jejichž oči se zavíraly před připomenutím Mým, a těm, kteří nebyli schopni slyšení.'),
(2242, 18, 102, 'Domnívali se snad ti, kdož nevěřili, že si mohou brát služebníky Mé jako ochránce místo Mne?'),
(2243, 18, 103, 'Rci: "Mám vás zpravit o těch, kdož největší ztrátu ve skutcích svých utrpěli,'),
(2244, 18, 104, 'o těch, jichž úsilí v životě pozemském zbloudilo, ač se domnívají, že dobré činili?'),
(2245, 18, 105, 'To jsou ti, kdož neuvěřili v Pána svého znamení a v setkání s Ním. Marné byly jejich skutky a nebudou u Nás nic vážit v den zmrtvýchvstání.'),
(2246, 18, 106, 'Toto odměnou bude jejich: peklo za to, že neuvěřili a že se posmívali znamením i poslům Mým.'),
(2247, 18, 107, 'Však věru ti, kdož uvěřili a zbožné skutky konali, ti budou mít zahrady rajské obydlím,'),
(2248, 18, 108, 'v nich věčně přebývat budou a po změně žádné toužit nebudou."'),
(2249, 18, 109, 'Rci: "Kdyby bylo moře inkoustem ke psaní slov Pána mého, bylo by moře vyčerpáno dříve, než by se spotřebovala slova Pána mého, i kdybychom inkoustem učinili ještě jedno podobné jemu."'),
(2250, 18, 110, 'Rci: "Já pouze smrtelník jsem jako vy a bylo mi vnuknuto, že božstvem vaším je Bůh jediný. A kdo doufá v setkání s Pánem svým, ať zbožné skutky koná a nechť v uctívání nepřidružuje nikoho k Pánu svému!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Káf há já? ajn sád.'),
(2252, 19, 2, 'Připomenutí o milosrdenství Pána tvého nad Zachariášem, služebníkem jeho.'),
(2253, 19, 3, 'Hle, Zachariáš zavolal k Pánu svému voláním tajným'),
(2254, 19, 4, 'řka: "Pane můj, mé kosti již u mne zeslábly a hlava má se šedinami pokryla, a nikdy jsem nebyl při vzývání Tebe, Pane můj, neúspěšný;'),
(2255, 19, 5, 'však obávám se příbuzných, kteří po mně přijdou. Má žena sice neplodná je, leč z moci své mi následníka daruj,'),
(2256, 19, 6, 'jenž dědicem mým i dědicem rodu Jakubova bude, a učiň, by tobě se zalíbil, Pane můj!"'),
(2257, 19, 7, '"Zachariáši, zvěstujeme ti zprávu radostnou o narození chlapce, jehož Jahjá bude jméno a jemuž jsme dříve nedali jmenovce žádného!"'),
(2258, 19, 8, 'Pravil: "Pane můj, jak mohl bych chlapce míti, když žena má je neplodná a já sám jsem již dosáhl slabosti věku vysokého?"'),
(2259, 19, 9, 'I řekl: "Staniž se takto, neb Pán tvůj praví: Toto je pro Mne snadné, vždyť stvořil jsem tě již dříve, když ještě ničíms nebyl."'),
(2260, 19, 10, 'Pravil Zachariáš: "Pane můj, učiň mi znamení!" - "Znamením tvým budiž," pravil, "že po tři noci s lidmi hovořit nebudeš, byť zdraví plnému ses těšil."'),
(2261, 19, 11, 'I vyšel Zachariáš ze svatyně k lidu svému a (Bůh) jim vnukl: "Slavte za jitra i za večera Pána svého!"'),
(2262, 19, 12, '... "Jahjá, pevně se Písma přidržuj!" A dali jsme mu moudrost již v dětství jeho'),
(2263, 19, 13, 'a laskavost a čistotu od Nás přicházející. A byl bohabojný'),
(2264, 19, 14, 'a k rodičům dobrotivý a nebyl ani násilný, ani neposlušný.'),
(2265, 19, 15, 'Mír budiž s ním v den, kdy se narodil, i v den, kdy zemřel, i v den, kdy k životu bude zas probuzen!'),
(2266, 19, 16, 'A připomeň v Knize také Marii, když vzdálila se od své rodiny do místa východního'),
(2267, 19, 17, 'a spustila závěs mezi sebou a jimi. A poslali jsme k ní ducha Svého a zjevil se jí v podobě smrtelníka dokonalého.'),
(2268, 19, 18, 'I zvolala: "Utíkám se před tebou k Milosrdnému - kéž i ty ses bál Jeho!"'),
(2269, 19, 19, 'Pravil: "Já posel jsem Pána tvého, abych ti daroval chlapce čistého."'),
(2270, 19, 20, 'Pravila: "Jak bych mohla chlapce míti, když se mě nedotkl smrtelník žádný a já nejsem ženou nepočestnou!"'),
(2271, 19, 21, 'Odpověděl: "Staniž se tak, neb Pán tvůj pravil: Toto je pro mne snadné, a věru jej učiníme znamením pro lidi i milosrdenstvím od Nás seslaným, a je to již věcí rozhodnutou!"'),
(2272, 19, 22, 'A otěhotněla dítětem a uchýlila se s ním na místo vzdálené.'),
(2273, 19, 23, 'A zastihly ji bolesti poblíže kmene datle palmové a zvolala: "Ach, kéž bych byla raději již dříve zemřela a upadla v zapomenutí úplné!"'),
(2274, 19, 24, 'I zavolal na ni zpod jejích nohou: "Nermuť se, vždyť Pán tvůj dal pod tebou téci říčce plynulé,'),
(2275, 19, 25, 'a zatřes kmenem palmy nad sebou a spadnou k tobě datle čerstvé a uzrálé!'),
(2276, 19, 26, 'Jez a pij a buď mysli radostné! A až spatříš ze smrtelníků někoho, rci:, Přislíbila jsem půst Milosrdnému a nebudu dnes mluvit na člověka žádného?!"'),
(2277, 19, 27, 'Pak přišla s ním k lidu svému, nesouc jej. I zvolali: "Marie, tys věru provedla věc neslýchanou!'),
(2278, 19, 28, 'Sestro Árónova, otec tvůj nebyl mužem špatným ani matka tvá nebyla nepočestnou!"'),
(2279, 19, 29, 'A ukázala Marie na chlapce. I pravili:;, Jak máme mluvit s někým, kdo ještě v kolébce je děckem?"'),
(2280, 19, 30, 'A promluvil chlapec: "Já služebníkem Božím jsem, On dal mi Písmo a učinil mne prorokem.'),
(2281, 19, 31, 'A učinil mne požehnaným, ať kdekoliv dlíti budu, a modlitbu a almužnu; co živ budu, mi poručil'),
(2282, 19, 32, 'a také úctu k matce své; a ani násilníkem, ani bídníkem mne neučinil.'),
(2283, 19, 33, 'A dal, aby mír byl se mnou v den, kdy jsem se narodil, i v den, kdy zemřu, i v den, kdy k životu budu zas probuzen."'),
(2284, 19, 34, 'Takový je Ježíš, syn Mariin, podle slova pravdivého, o němž oni pochybují jen!'),
(2285, 19, 35, 'Není Boha důstojná, aby si bral syna nějakého. Sláva tomu, který, když o věci rozhodne, řekne pouze: "Staniž se!" a stane se.'),
(2286, 19, 36, 'Bůh jest Pánem mým i vaším, uctívejte Jej tedy, neboť toto jest stezka přímo vedoucí!'),
(2287, 19, 37, 'A strany mezi sebou se v názorech rozešly - však běda těm, kdož neuvěřili, při nesmírného dne spatření!'),
(2288, 19, 38, 'Jak dobře však uslyší a uvidí v den, kdy přijdou k Nám! Avšak dnes jsou v bludu zjevném ti, kdož křivdu činí.'),
(2289, 19, 39, 'Varuj je přede dnem nářku, kdy věc bude rozhodnuta, zatímco oni dnes lhostejni jsou a nevěří!'),
(2290, 19, 40, 'A věru My zdědíme zemi se vším, co je na ní, a k nám budou všichni navráceni.'),
(2291, 19, 41, 'A připomeň v Knize též Abrahama, neboť on pravdomluvným byl a prorokem.'),
(2292, 19, 42, 'Hle, pravil otci svému: "Otče můj, proč uctíváš něco, co ani neslyší, ani nevidí a co není k užitku žádnému?'),
(2293, 19, 43, 'Otče můj, dostalo se mi vědění, jehož se tobě nedostalo, následuj mne tedy a já uvedu tě na stezku rovnou.'),
(2294, 19, 44, 'Otče můj, neuctívej satana; vždyť on vzpurný byl vůči Milosrdnému.'),
(2295, 19, 45, 'Otče můj, obávám se, aby se tě nedotkl trest od Milosrdného a aby ses přítelem satanovým nestal."'),
(2296, 19, 46, 'I řekl otec jeho: "Ty tupit chceš božstva moje, Abrahame? Nepřestaneš-li, pak tě věru ukamenuji! A nyní se ode mne na delší dobu vzdal!"'),
(2297, 19, 47, 'Pravil Abraham: "Mír s tebou! Budu prosit Pána svého o odpuštění pro tebe, vždyť On je mi příznivě nakloněn.'),
(2298, 19, 48, 'Odloučím se od vás i od toho, co místo Boha vzýváte, a jen Pána svého vzývat budu; a snad nebudu při modlitbě k Pánu svému oslyšen."'),
(2299, 19, 49, 'A když se od nich a od toho, co místo Boha uctívali, odloučil, Izáka a Jakuba jsme mu darovali a každého z nich jsme prorokem ustanovili.'),
(2300, 19, 50, 'A uštědřili jsme jim část z Našeho milosrdenství a jejich vznešený jazyk jsme pravdivým učinili.'),
(2301, 19, 51, 'A připomeň v Knize také Mojžíše, jenž byl upřímně oddaným a byl poslem i prorokem.'),
(2302, 19, 52, 'A zavolali jsme naň na pravém úbočí hory Sinaje a učinili jsme jej blízkým důvěrníkem.'),
(2303, 19, 53, 'A obdařili jsme jej z milosrdenství Našeho bratrem jeho Árónem, též prorokem.'),
(2304, 19, 54, 'A připomeň v Knize také Ismaela, jenž věrně sliby dodržoval a poslem i prorokem byl'),
(2305, 19, 55, 'a rodině své modlitbu a almužnu přikázal a Pánu svému se zalíbil.'),
(2306, 19, 56, 'A připomeň v Knize též Idríse, jenž byl pravdomluvným a prorokem'),
(2307, 19, 57, 'a jehož jsme na vysoké místo pozvedli.'),
(2308, 19, 58, 'A toto jsou ti, jež Bůh svou přízní vyznamenal: proroci z potomstva Adamova, z těch, které jsme s Noem zachránili, z potomstva Abrahamova a Izraelova a z těch, které jsme vyvolili a správnou cestou vedli. A když jim znamení Milosrdného přednášena byla, na zem padali klaníce se a s pláčem.'),
(2309, 19, 59, 'Po nich však následovali nástupci, kteří modlitbu opustili a jen vášně své sledovali, a ti se setkají s bludem -'),
(2310, 19, 60, 'kromě těch, kdož pokání činili, víru měli a zbožné skutky konali, Ti do ráje budou uvedeni a nebudou nikterak ošizeni,'),
(2311, 19, 61, 'do zahrad Edenu, jež přislíbil Milosrdný služebníkům Svým v nepoznatelné věřícím. A pak věru slib Jeho se splní!'),
(2312, 19, 62, 'A neuslyší tam žádné tlachání, nýbrž jen pozdravy "mír s vámi", a obživu tam budou mít zrána i zvečera.'),
(2313, 19, 63, 'Takový bude ráj, jenž v dědictví dáme těm ze služebníků Svých, kteří bohabojní byli.'),
(2314, 19, 64, 'A nesestupujeme leč z rozkazu Pána tvého a Jemu náleží, co je před námi a co je za námi, i to, co je mezi tím; a Pán tvůj není věru zapomnětlivým,'),
(2315, 19, 65, 'On Pánem je nebes a země a toho, co mezi nimi je. Uctívej Jej tedy a buď vytrvalý v uctívání Jeho. A znáš někoho se jménem stejným?'),
(2316, 19, 66, 'Člověk se ptá: "Až zemřu, budu z hrobu opět vyveden živý?"'),
(2317, 19, 67, 'Což si nevzpomene člověk, že jsme jej stvořili již jednou, když ještě nebyl ničím?'),
(2318, 19, 68, 'Při Pánu tvém, My věru shromáždíme je i satany a pak kol pekla na kolenou je předvedeme'),
(2319, 19, 69, 'a potom z každé skupiny toho, kdo nejvzpurnější byl proti Milosrdnému, vytáhneme'),
(2320, 19, 70, 'a My pak výborně poznáme ty, kdož hořet v něm si nejvíc zaslouží.'),
(2321, 19, 71, 'A mezi vámi není nikoho, kdo by k němu nesestoupil, a to u Pána tvého nezvratné je rozhodnutí.'),
(2322, 19, 72, 'Potom však zachráníme ty, kdož bohabojní byli, a ponecháme v něm nespravedlivé na kolenou klečící.'),
(2323, 19, 73, 'Když jsou jim sdělována Naše znamení jako důkazy jasné, tehdy ti, kdož nevěří, říkají věřícím. "Která z obou skupin je na tom lépe postavením svým a krásnější společností?"'),
(2324, 19, 74, 'Kolik pokolení před vámi jsme již zahubili, jež lepší byla bohatstvím svým i vzhledností!'),
(2325, 19, 75, 'Rci: "Kéž Milosrdný prodlouží těm, kdož v bludu jsou, délku jejich žití, aby spatřili to, co slíbeno jim bylo - buď trest pozemský, či Hodinu. A pak poznají, kdo postavení má horší a kdo vojskem je slabší."'),
(2326, 19, 76, 'A Bůh rozmnoží vedení těch, kdož správnou cestou jdou, vždyť skutky zbožné, jež trvají, lepší jsou u Pána tvého odměnou i lepší náhradou.'),
(2327, 19, 77, 'Co soudíš o tom, jenž ve znamení Naše nevěří a hovoří: "Věru že dostane se mi majetku i dětí"?'),
(2328, 19, 78, 'Pronikl snad on k nepoznatelnému či uzavřel snad s Milosrdným úmluvu?'),
(2329, 19, 79, 'Však pozor! My zapíšeme vše, co říká, a prodloužíme mu délku trestu jeho'),
(2330, 19, 80, 'a zdědíme po něm to, o čem mluví, a přijde k nám osamocen, zbaven všeho.'),
(2331, 19, 81, 'A vzali si kromě Boha božstva různá, aby jim byla posilou.'),
(2332, 19, 82, 'Však pozor! Ona uctívání jejich popřou a protivníky jejich se stanou.'),
(2333, 19, 83, 'Což nevidíš, že jsme vyslali satany proti nevěřícím, aby je ponoukali ke špatnostem?'),
(2334, 19, 84, 'Neusiluj o uspíšení jejich lhůty; vždyť My počítáme jim přesným počtem.'),
(2335, 19, 85, 'V den, kdy shromáždíme bohabojné u Milosrdného jako poselstvo'),
(2336, 19, 86, 'a kdy poženeme hříšníky do pekla jako k napajedlu stádo,'),
(2337, 19, 87, 'tehdy nevěřící nebudou mít žádnou přímluvu kromě těch, kdož s Milosrdným uzavřeli úmluvu.'),
(2338, 19, 88, 'Říkajíce, že Milosrdný si vzal děti,'),
(2339, 19, 89, 'vy spáchali jste věc hnusnou,'),
(2340, 19, 90, 'že div se nebesa kvůli tomu neroztrhla, země se div nerozpoltila a hory se málem na prach rozpadly,'),
(2341, 19, 91, 'že Milosrdnému děti lživě připsali.'),
(2342, 19, 92, 'A nehodí se přece, aby si Milosrdný děti bral,'),
(2343, 19, 93, 'vždyť všichni, kdož na nebesích a na zemi jsou, k Milosrdnému přicházejí jako služebníci.'),
(2344, 19, 94, 'A On je věru sečetl a přesným počtem spočítal'),
(2345, 19, 95, 'a všichni k Němu přijdou v den zmrtvýchvstání jako jedinci.'),
(2346, 19, 96, 'Těm, kdož uvěřili a zbožné skutky konali, těm věru Milosrdný lásku Svou poskytne.'),
(2347, 19, 97, 'A učinili jsme jej snadným pro jazyk tvůj, abys zvěst radostnou bohabojným mohl oznámit a lid hádavý tím varovat.'),
(2348, 19, 98, 'Kolik pokolení před nimi jsme již zahubili! Můžeš snad jediného z nich ucítit či šepotu jejich naslouchat?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Tá há.'),
(2350, 20, 2, 'Neseslali jsme ti Korán, abys byl zoufalý,'),
(2351, 20, 3, 'nýbrž pouze jako připomenutí těm, kdož Boha se bojí,'),
(2352, 20, 4, 'a jako seslání, jež od toho, kdo zemi a nebesa vysoká stvořil, přichází.'),
(2353, 20, 5, 'Milosrdný - On na trůnu svém vznešeně sedí'),
(2354, 20, 6, 'a vše, co na nebesích je a na zemi, mezi nimi i pod povrchem země, Jemu náleží.'),
(2355, 20, 7, 'A jestliže slova svá hlasitě pronášíš, (je to zbytečné), vždyť On zajisté zná tajemství, byť sebelépe bylo skryté;'),
(2356, 20, 8, 'Bůh - a není božstva kromě Něho a Jemu jména překrásná přísluší!'),
(2357, 20, 9, 'Dostalo se ti zprávy o Mojžíšovi?'),
(2358, 20, 10, 'Hle, oheň spatřil a rodině své pravil: "Zastavte se, já postřehl jsem oheň nějaký, možná že vám z něho oharek přinesu anebo u něho správnou cestu naleznu."'),
(2359, 20, 11, 'A když přišel k ohni, ozvalo se: "Mojžíši,'),
(2360, 20, 12, 'já vpravdě jsem tvůj Pán; zuj si opánky své, neboť v posvátném údolí Tuwá jsi.'),
(2361, 20, 13, 'A já jsem si tě vyvolil, proto slyš, co vnuknuto ti bude!'),
(2362, 20, 14, 'Já vpravdě jsem Bůh a není božstva kromě Mne; uctívej Mne tedy a modlitbu konej, na Mne vzpomínaje!'),
(2363, 20, 15, 'Hodina věru se blíží a mám co dělat, abych skryl ji, aby každá duše byla odměněna za úsilí svoje.'),
(2364, 20, 16, 'Kéž tě neodvrátí od myšlenky na ni ten, kdo v ni nevěří a vášeň svou následuje, jinak zahyneš.'),
(2365, 20, 17, 'Co to máš ve své pravici, Mojžíši?"'),
(2366, 20, 18, 'Odpověděl: "To je moje hůl, opírám se o ni a srážím s ní listí pro ovce své a mám pro ni i jiné použití."'),
(2367, 20, 19, 'I pravil mu: "Hoď ji na zem, Mojžíši!"'),
(2368, 20, 20, 'A hodil ji, a hle, stal se z ní had plazící se.'),
(2369, 20, 21, 'Pravil Bůh: "Vezmi ji a neboj se, My vrátíme ji do podoby původní.'),
(2370, 20, 22, 'Nyní přitlač ruku svou k boku, a až ji sejmeš, bude bílá, ale nepoškozená; a toto další je znamení.'),
(2371, 20, 23, 'Abychom ti pak z našich velkých znamení ukázali některá,'),
(2372, 20, 24, 'odeber se k Faraónovi, jenž vzpurně si počíná!"'),
(2373, 20, 25, 'I řekl Mojžíš: "Pane, mou hruď mi rozevři'),
(2374, 20, 26, 'a mé dílo mi usnadni'),
(2375, 20, 27, 'a uzel na mém jazyku uvolni,'),
(2376, 20, 28, 'aby porozuměli mé řeči!'),
(2377, 20, 29, 'A dej mi z rodiny mé pomocníka,'),
(2378, 20, 30, 'Áróna, mého bratra,'),
(2379, 20, 31, 'a podpoř jím mou sílu'),
(2380, 20, 32, 'a učiň jej společníkem v mém úkolu,'),
(2381, 20, 33, 'abychom mohli Tebe často slavit'),
(2382, 20, 34, 'a hojně Tě vzpomínat,'),
(2383, 20, 35, 'vždyť věru nad námi jsi jasnozřivý!"'),
(2384, 20, 36, 'I pravil Bůh: "Vyslyšena je prosba tvá, Mojžíši!'),
(2385, 20, 37, 'A již jednou jsme ti prokázali jiné dobrodiní,'),
(2386, 20, 38, 'když jsme vnukli matce tvé toto vnuknutí:'),
(2387, 20, 39, ', Do truhly jej vlož a do moře jej vhoď, a nechť moře jej na břeh vyvrhne, a tehdy se ho nepřítel Můj i jeho ujme.? A já jsem na tebe Svou lásku vrhl, abys před Mýma očima vyrostl.'),
(2388, 20, 40, 'A hle, přišla sestra tvá a pravila:, Mám vám ukázat toho, kdo se oň postará?? A vrátili jsme tě k tvé matce, aby se ochladilo oko její a nermoutila se. A když zabils onoho člověka, ze starostí jsme tě vysvobodili a zkouškám jsme tě podrobili. A celé roky jsi zůstal mezi obyvateli Madjanu a pak jsi přišel sem podle rozhodnutí Mého, Mojžíši,'),
(2389, 20, 41, 'a pro Sebe jsem si tě vyhradil.'),
(2390, 20, 42, 'Odejděte, ty i bratr tvůj, s Mými znameními a ve vzpomínání na Mne neochabujte,'),
(2391, 20, 43, 'a k Faraónovi, jenž vzpurně si počíná, oba se vydejte'),
(2392, 20, 44, 'a řečí mírnou k němu promluvte, snad vzpamatuje se či zalekne."'),
(2393, 20, 45, 'I řekli oba: "Pane náš, obáváme se, že míru vůči nám překročí či tyranem se ukáže."'),
(2394, 20, 46, 'Pravil Bůh: "Nebojte se, vždyť Já s vámi jsem a slyším i vidím.'),
(2395, 20, 47, 'Jděte k němu a řekněte:, My vpravdě poslové jsme Pána tvého, propusť s námi dítka Izraele a nemuč je! Přišli jsme k tobě s Pána tvého znamením a mír tomu, jenž jde za správným vedením!'),
(2396, 20, 48, 'A bylo nám vnuknuto, že potrestán bude ten, kdo za lháře nás prohlásí a zády se obrátí.? "'),
(2397, 20, 49, 'I otázal se Faraón: "A kdo je ten váš Pán, Mojžíši?"'),
(2398, 20, 50, 'Odpověděl: "Náš Pán je ten, jenž každé věci podobu její dal a pak po správné cestě ji vedl."'),
(2399, 20, 51, 'Otázal se: "A jak to bylo s prvními pokoleními?"'),
(2400, 20, 52, 'Odpověděl: "Znalost toho je u Pána mého v Knize a Pán můj se nemýlí ani nezapomíná'),
(2401, 20, 53, 'a On je ten, jenž pro vás učinil zemi kolébkou a narýsoval pro vás na ní cesty a seslal z nebe vodu, pomocí níž dáváme vyrůst rozličným druhům rostlin."'),
(2402, 20, 54, 'Jezte a paste stáda svá, vždyť věru v tom jsou znamení pro rozumem nadané!'),
(2403, 20, 55, 'Z ní jsme vás stvořili a do ní vás vrátíme a z ní vás vyjmeme i podruhé.'),
(2404, 20, 56, 'A ukázali jsme Faraónovi věru všechna Svá znamení, však on je za lživá prohlásil a odmítl je'),
(2405, 20, 57, 'a řekl: "Zdaž jsi k nám přišel, Mojžíši, abys nás svými kouzly vyhnal z naší země?'),
(2406, 20, 58, 'My ti však rovněž podobná kouzla ukážeme! Urči tedy pro sebe i pro nás schůzku, již ani my, ani ty nezrušíme, na místo vhodné!"'),
(2407, 20, 59, 'Mojžíš odpověděl: "Naše schůzka budiž v den Svátku zdobení a nechť se lidé shromáždí dopoledne!"'),
(2408, 20, 60, 'Faraón se odvrátil, své kouzelníky shromáždil a pak přišel.'),
(2409, 20, 61, 'A řekl jím Mojžíš: "Běda vám, nevymýšlejte si proti Bohu lži, aby vás trestem svým nezničil - a ztroskotal ten, kdo lži si vymýšlel!"'),
(2410, 20, 62, 'A o věci své se mezi sebou radili a tajně se dorozumívali.'),
(2411, 20, 63, 'A řekl Faraón: "Toto jsou dva kouzelníci, kteří vás svými kouzly chtějí vyhnat z vaší země a skoncovat s vaším vzorným způsobem by chtěli.'),
(2412, 20, 64, 'Shromážděte své úklady a potom přijďte po řadě! A bude dnes šťasten ten, kdo vrchu nabude."'),
(2413, 20, 65, 'A řekli: "Mojžíši, chceš házet ty, anebo máme hodit my jako první?"'),
(2414, 20, 66, 'I odpověděl: "Nikoliv, házejte vy!" A hle, kouzly jejich se mu jevily jejich provazy a hole, jako by se plazily,'),
(2415, 20, 67, 'a pocítil Mojžíš ve své duši strach.'),
(2416, 20, 68, 'A pravili jsme mu: "Neboj se, vždyť ty budeš mít vrch!'),
(2417, 20, 69, 'Hoď hůl, již máš v pravici, a ona pohltí to, co oni vyrobili, neboť to, co oni vyrobili, je jen kouzelnický úskok a žádný kouzelník nemůže dosáhnout úspěchu, ať půjde kamkoli!"'),
(2418, 20, 70, 'A padli kouzelníci na zem a zvolali: "V Árónova a Mojžíšova Pána jsme uvěřili!"'),
(2419, 20, 71, 'I řekl Faraón: "Jak to, že jste v něj uvěřili, dříve než jsem vám to dovolil. On zřejmě je vaším mistrem, jenž kouzlům vás naučil. Dám vám useknout vaše ruce pravé a nohy levé a dám vás ukřižovat na kmeny palmové. Pak poznáte, kdo z nás je v trestání silnější a vytrvalejší!"'),
(2420, 20, 72, 'Odpověděli: "Nedáme ti přednost před tím, čeho se nám dostalo z jasných důkazů, ani před tím, jenž nás stvořil. Rozhodni to, o čem chceš rozhodovat, avšak můžeš rozhodovat jen o věcech tohoto života pozemského.'),
(2421, 20, 73, 'Uvěřili jsme v Pána svého, aby nám odpustil naše hříchy a kouzelnictví, k němuž jsi nás ty donutil. A Bůh je lepší a trvalejší!'),
(2422, 20, 74, 'A věru pro toho, kdo k Pánu svému jako hříšník přijde, je peklo určeno, v němž ani nezemře, ani žít nebude,'),
(2423, 20, 75, 'zatímco těm, kdož k Němu jako věřící přicházejí a zbožné skutky vykonali, těm hodnosti nejvyšších se dostane,'),
(2424, 20, 76, 'zahrad Edenu, pod nimiž řeky tekou a v nichž nesmrtelní budou. A taková je odměna těch, kdož se očišťují!"'),
(2425, 20, 77, 'A vnukli jsme Mojžíšovi: "Vydej se za noci na cestu se služebníky Mými a proraz jim cestu suchou v moři a stíháni se neboj a strach neměj!"'),
(2426, 20, 78, 'A stíhal je Faraón s vojsky svými a pokrylo je moře tím, čím je pokrylo.'),
(2427, 20, 79, 'A takto Faraón lid svůj v bloudění zavedl a po cestě správné jej nevedl.'),
(2428, 20, 80, 'Dítky Izraele, zachránili jsme vás před nepřítelem vaším a uzavřeli jsme s vámi úmluvu na pravém úbočí hory Sinaje a seslali jsme vám manu a křepelky.'),
(2429, 20, 81, 'Jezte tedy z výtečných pokrmů, jež jsme vám uštědřili, avšak nepřehánějte v tom, jinak vás postihne hněv Můj! A koho hněv Můj postihne, ten již do záhuby spěje.'),
(2430, 20, 82, 'Já věru jsem odpouštějící tomu, kdo pokání činí, věří, zbožné skutky koná a potom po správné cestě se ubírá.'),
(2431, 20, 83, 'I pravil Bůh: "Co způsobilo, že tak spěcháš od lidu svého, Mojžíši?"'),
(2432, 20, 84, 'Odpověděl: "Oni jdou v mých stopách, zatímco já spěchám k tobě, Pane můj, abych se ti zalíbil."'),
(2433, 20, 85, 'Pravil: "Po tvém odchodu jsme uvedli lid tvůj do pokušení a Samaritán jim dal zbloudit!"'),
(2434, 20, 86, 'I vrátil se Mojžíš k lidu svému rozhněván i zarmoucen a řekl: "Lide můj, cožpak Pán váš vám neučinil slib překrásný? Či byla snad úmluva pro vás příliš dlouhá anebo jste chtěli, aby na vás padl hněv Pána vašeho, že jste tak slib svůj porušili, jímž jste se mi zavázali?"'),
(2435, 20, 87, 'Odpověděli: "Slib tobě daný jsme z vlastní neporušili, avšak bylo nám nařízeno přinést náklady z ozdob tohoto lidu a hodit je na hromadu, a podobně je tam hodil i Samaritán."'),
(2436, 20, 88, 'A učinil pro ně tele, jež mělo tělo a bučelo, a řekli: "Toto je božstvo vaše i božstvo Mojžíšovo, avšak on na ně již zapomněl."'),
(2437, 20, 89, 'Což neviděli, že jim ani slovem neodpovídá a že jim nemůže ani škodit, ani prospívat?'),
(2438, 20, 90, 'Již předtím jim Árón říkal: "Lide můj, vy jste tím jen zkoušeni a Pán váš je věru milosrdný; následujte mne a rozkaz můj poslouchejte!"'),
(2439, 20, 91, 'Odpověděli: "Pokud se k nám nevrátí Mojžíš, nepřestaneme je uctívat."'),
(2440, 20, 92, 'I zvolal Mojžíš po svém návratu: "Áróne, co ti zabránilo, když viděls je takto bloudit,'),
(2441, 20, 93, 'abys mne následoval? Proč jsi nebyl rozkazu mého poslušen?"'),
(2442, 20, 94, 'Odpověděl: "Synu mé matky, nechytej mne za bradu a hlavu mou! Obával jsem se, že řekneš: Vyvolal jsi rozkol mezi dítkami Izraele a slovo mé jsi nedodržel."'),
(2443, 20, 95, 'Potom Mojžíš řekl: "A co je vlastně s tebou, Samaritáne?"'),
(2444, 20, 96, 'I odpověděl: "Spatřil jsem to, co oni neviděli, a vzal jsem hrst prachu ze stopy posla a rozhodil jsem jej; takto mne svedla duše má."'),
(2445, 20, 97, 'I pravil Mojžíš: "Odejdi a nechť po celý život říkáš: Nedotýkejte se mne! A budiž ti určena lhůta, kterou neporušíš. Pohleď na božstvo své, jež jsi uctívat nepřestal - věru je spálíme a jako popel je do moře rozmetáme!'),
(2446, 20, 98, 'Vaším božstvem je jedině Bůh a není božstva kromě Něho a On všechny věci svým věděním objímá!"'),
(2447, 20, 99, 'Takto ti vyprávíme zvěsti doby minulé a přinášíme ti od Sebe připomenutí.'),
(2448, 20, 100, 'A kdo se od něho odvrátí, ten v den zmrtvýchvstání břemeno ponese'),
(2449, 20, 101, 'a věčně bude jím obtížen. Jak špatný to bude náklad v den zmrtvýchvstání,'),
(2450, 20, 102, 'v den, kdy na pozoun bude zatroubeno. A tehdy shromáždíme hříšníky, kteří šilhajíce'),
(2451, 20, 103, 'budou si šeptat mezi sebou: "Zůstali jsme v hrobech pouze deset dní."'),
(2452, 20, 104, 'My nejlépe víme, co spolu hovoří; a hle, řekne ten z nich, kdo je nejpříkladnější svým životem: "Zůstali jste tam jen den jediný!"'),
(2453, 20, 105, 'A budou se tě dotazovat na hory. Odpověz: "Pán můj je na prach rozmetá'),
(2454, 20, 106, 'a jako pustou pláň je zanechá'),
(2455, 20, 107, 'a neuzříš na ní ani zakřivení, ani zvlnění."'),
(2456, 20, 108, 'V ten den následovat budou svolavatele, v němž úchylky není, a hlasy se ztiší před Milosrdným a uslyšíš jen rtů ševelení.'),
(2457, 20, 109, 'V ten den nepomůže přímluva nikomu kromě toho, jemuž to Milosrdný dovolí, a toho, jehož řeč se Mu zalíbí.'),
(2458, 20, 110, 'On zná, co je před nimi i co je za nimi, zatímco oni to nemohou obsáhnout ve svém vědění.'),
(2459, 20, 111, 'A tváře se pokorně skloní před živým a samým o sobě trvajícím, zatímco ti, kdož břímě nespravedlnosti ponesou, budou zklamáni.'),
(2460, 20, 112, 'A kdo zbožné skutky činil a přitom věřící byl, ten nemusí se obávat ani křivdy, ani poškození.'),
(2461, 20, 113, 'A takto jsme je seslali v podobě Koránu arabského a vyjádřili jsme v něm výstrahu - snad stanou se bohabojnými, či přinese jim nové připomenutí.'),
(2462, 20, 114, 'Nechť povýšen je Bůh, vládce a pravda! Nepospíchej s Koránem, dříve než bude ti ukončeno celé jeho vnuknutí, a rci: "Pane můj, rozmnož mi vědění!"'),
(2463, 20, 115, 'A již předtím jsme úmluvu s Adamem uzavřeli, avšak zapomněl na ni a pevnou vůli jsme v něm nenalezli.'),
(2464, 20, 116, 'A hle, řekli jsme andělům: "Padněte na zem před Adamem!" A padli všichni kromě Iblíse, který odmítl.'),
(2465, 20, 117, 'I pravili jsme: "Adame, toto věru je nepřítel tvůj i manželky tvé. Ať vás nevyžene z ráje, neboť pak věru nešťastni budete!'),
(2466, 20, 118, 'Náleží ti, abys v něm nehladověl a nahý nechodil'),
(2467, 20, 119, 'a abys v něm nežíznil a žárem netrpěl!"'),
(2468, 20, 120, 'Však satan mu našeptával a řekl: "Adame, mám ti ukázat strom nesmrtelnosti a království nepomíjející?"'),
(2469, 20, 121, 'A pojedli oba ze stromu a objevila se jim jejich nahota a začali si šíti oděvy z listí rajského. Adam neposlušný vůči Pánu svému byl a z pravé cesty se uchýlil.'),
(2470, 20, 122, 'Potom Pán jeho si jej vyvolil, odpustil mu a správnou cestou jej řídil.'),
(2471, 20, 123, 'I pravil Bůh: "Sestupte z něho společně a buďte jeden druhému nepřítelem! A věru k vám přijde ode Mne správné vedení." A ten, kdo následovat je bude, nezbloudí ani nebude nešťastný,'),
(2472, 20, 124, 'zatímco ten, jenž odvrátí se od připomenutí Mého, život povede stísněný a v den zmrtvýchvstání vzkříšen bude Námi jako slepý'),
(2473, 20, 125, 'a řekne: "Pane, proč jsi mne vzkřísil jako slepého, když dříve byl jsem vidoucí?"'),
(2474, 20, 126, 'I odpoví Bůh: "Tak jako k tobě přicházela Naše znamení a tys na ně zapomněl, stejně tak jsi ty dnes zapomenut!"'),
(2475, 20, 127, 'Takto odměňujeme přestupníky a ty, kdož ve znamení Pána svého nevěřili. A věru je trest v životě budoucím nejtvrdší a nejtrvalejší!'),
(2476, 20, 128, 'Což je neuvedlo na správnou cestu pomyšlení, kolik jsme před nimi zahubili pokolení, nad jejichž obydlími oni chodí nyní? Věru jsou v tom znamení pro vládnoucí rozumem!'),
(2477, 20, 129, 'A kdyby nebylo slova Pána tvého, jež předem bylo již sesláno, a lhůty stanovené, byl by se trest stal nevyhnutelným.'),
(2478, 20, 130, 'Snášej trpělivě to, co oni hovoří, a slav chválu Pána svého před slunce východem i západem! A i v době noční Jej oslavuj a na obou koncích dne - snad zalíbení v tobě nalezne!'),
(2479, 20, 131, 'A nevrhej zrak svůj žádostivě na to, co dali jsme některým párům z nich v užívání - to nádhera pomíjivá je života pozemského, abychom je tím uváděli v pokušení; vždyť štědrost Pána tvého lepší je i trvalejší.'),
(2480, 20, 132, 'Přikaž rodině své modlitby konání a sám buď v ní vytrvalý! My nežádáme od tebe žádnou obživu, vždyť My sami ti ji uštědřujeme. A dobrý konec náleží bohabojnosti.'),
(2481, 20, 133, 'Říkají: "Pročpak nám nepředvede nějaké znamení od Pána svého?" Což se jim nedostalo důkazu jasného z toho, co ve svitcích prvních je obsaženo?'),
(2482, 20, 134, 'A kdybychom je byli zahubili trestem nějakým předtím, věru by byli řekli: "Pane náš, proč jsi k nám neposlal posla nějakého, abychom mohli následovat Tvá znamení, dříve než jsme byli poníženi a zahanbeni?"'),
(2483, 20, 135, 'Odpověz: "Každý je v očekávání, čekejte tedy i vy! A záhy se dozvíte, kdo po stezce přímé kráčí a kdo správně je veden!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Přibližuje se k lidem zúčtování jejich, zatímco lhostejně se odvracejí,'),
(2485, 21, 2, 'a kdykoliv jim od Pána jejich nové připomenutí přijde, jen jako v zábavě mu naslouchají'),
(2486, 21, 3, 's výsměchem v srdcích. A ti, kdož nespravedliví jsou, tajně se domlouvají: "Což není on jen smrtelník vám podobný? Cožpak se chcete podílet na kouzelnictví, přestože oči máte vidoucí?"'),
(2487, 21, 4, 'Rci: "Pán můj dobře zná řeči na nebesích i na zemi, On slyšící je i vševědoucí."'),
(2488, 21, 5, 'Však naopak oni hovoří: "To jsou jen sny popletené či spíše on sám si to vymyslil, ba věru on básníkem je! Ať tedy předloží znamení, s jakými byli vysláni proroci staří!"'),
(2489, 21, 6, 'Neuvěřilo před nimi žádné město, a zahubili jsme je. Uvěří snad nyní tito?'),
(2490, 21, 7, 'A poslali jsme před tebou pouze muže, jimž vnuknutí jsme dali. Zeptejte se tedy lidí, jimž dostalo se připomenutí, nevíte-li!'),
(2491, 21, 8, 'A nedali jsme těmto prorokům těla, jež by pokrmy nejedla, a ani nesmrtelní nebyli.'),
(2492, 21, 9, 'Potom jsme splnili slib jim daný a zachránili jsme je i ty, jež jsme chtěli, zatímco přestupníky jsme zahubili.'),
(2493, 21, 10, 'A nyní jsme vám již Písmo seslali, v němž pro vás je připomenutí - což to nepochopíte?'),
(2494, 21, 11, 'A kolik jsme vyvrátili měst, jež byla nespravedlivá, a po nich vzniknout jsme národům jiným dali!'),
(2495, 21, 12, 'A jakmile pocítili přísnost Naši, prchat z měst se jali.'),
(2496, 21, 13, '"Neprchejte, ale raději se vraťte k přepychu, v němž jste žili, a do příbytků svých - možná že o vydání počtu požádáni budete!"'),
(2497, 21, 14, 'A odpověděli: "Běda nám, my věru byli jsme nespravedliví!"'),
(2498, 21, 15, 'A nepřestalo toto volání jejich, dokud jsme je jak obilí neskosili a životy jejich nezhasili.'),
(2499, 21, 16, 'My nebesa a zemi a vše, co je mezi nimi, jsme pro zábavu nestvořili;'),
(2500, 21, 17, 'kdybychom se byli chtěli pobavit, našli bychom si zábavu u Sebe, kdybychom už tak učinit zamýšleli.'),
(2501, 21, 18, 'Naopak My pravdu proti falši mrštíme, a roztříští ji, a hle, ona se rozplyne; však běda vám za to, co lživě Bohu připisujete!'),
(2502, 21, 19, 'Jemu náleží všichni, kdož na nebesích jsou a na zemi; a ti, kdož u Něho dlí, nejsou tak hrdopyšní, aby Mu nesloužili, a nikdy neumdlí'),
(2503, 21, 20, 'a pějí slávu Jeho dnem i nocí bez přestání.'),
(2504, 21, 21, 'Vzali si snad božstva pozemská, jež k životu probouzejí?'),
(2505, 21, 22, 'Kdyby byla na nebi a na zemi božstva jiná kromě Boha, věru by obojí již zkáze bylo propadlo. A oč je Bůh, Pán trůnu, slavnější než to, co mu připisují!'),
(2506, 21, 23, 'A On nebude tázán na to, co koná. zatímco oni budou dotazováni na počtu vydání.'),
(2507, 21, 24, 'Či vzali si vedle Něho božstva jiná? Rci: "Předložte tedy důkaz svůj! Toto je připomenutí těm, kdož se mnou jsou, i připomenutí těm, kdož byli přede mnou!" Však naopak, většina z nich pravdu nezná a odvrací se od ní.'),
(2508, 21, 25, 'A nevyslali jsme před tebou žádného posla, abychom mu nebyli vnukli: "Není božstva kromě Mne; uctívejte Mne tedy!"'),
(2509, 21, 26, 'Hovoří: "Milosrdný si vzal děti!" Sláva Mu! Ba nikoliv, to služebníci jsou jen Jeho ctihodní,'),
(2510, 21, 27, 'kteří se dříve než On promluvit neodvažují a jen podle rozkazu Jeho jednají.'),
(2511, 21, 28, 'A On zná dobře, co je před nimi a co je za nimi, a oni se přimlouvat nesmějí, leč za toho, v němž On nalezne zalíbení; a oni se před Ním třesou bázní.'),
(2512, 21, 29, 'A kdyby někdo z nich řekl: "Já božstvem vedle Něho jsem," toho bychom peklem odměnili, neboť takto My nespravedlivé odměňujeme.'),
(2513, 21, 30, 'Což ti, kdož nevěří, nevidí, že nebesa a země byly pevně spojeny a že jsme je od sebe odtrhli a že z vody jsme vše živé učinili? Což neuvěří?'),
(2514, 21, 31, 'A vztyčili jsme na zemi hory pevně zakotvené, aby se s nimi nekývala, a učinili jsme na ní průchody jako cesty - snad jimi budou vedeni.'),
(2515, 21, 32, 'A učinili jsme nebe střechou chráněnou a přesto se oni odvracejí od Našich znamení.'),
(2516, 21, 33, 'On je ten, jenž stvořil noc i den, slunce i měsíc a oba po nebeské své dráze plují.'),
(2517, 21, 34, 'Žádnému ze smrtelníků, kteří před tebou byli, jsme nesmrtelnost nedali. A jestliže ty zemřeš, budou snad oni nesmrtelní?'),
(2518, 21, 35, 'Každá duše smrti okusí; a zkoušíme vás zlým i dobrým co pokušením, a k Nám budete navráceni.'),
(2519, 21, 36, 'A když tě nevěřící spatří, posměšky si z tebe jen dělají a hovoří: "Je tohle ten, co zmiňuje se pohrdavě o božstvech vašich?" A při tom sami nevěří v Milosrdného připomenutí!'),
(2520, 21, 37, 'Člověk je stvořen ze spěchu! Já ukáži vám záhy Svá znamení, však nežádejte jejich uspíšení!'),
(2521, 21, 38, 'A říkají nevěřící: "Kdy slib tento se splní, jste-li pravdomluvní?"'),
(2522, 21, 39, 'Kéž by znali ti, kdož nevěří, tu chvíli, kdy nebudou moci oheň odvrátit od tváří svých a zad svých a kdy pomoc žádnou mít nebudou!'),
(2523, 21, 40, 'Ba, Hodina přikvačí na ně znenadání a v úžas je uvede a nebudou schopni ji zadržet a ani odklad nedostanou.'),
(2524, 21, 41, 'A dělali si posměšky již z poslů předchozích, avšak to, čemu se posmívali, obklopilo ty, kdož tomu se posmívali.'),
(2525, 21, 42, 'Rci: "Kdo ochrání vás za noci i za dne před Milosrdným?" A přece od připomenutí Pána svého se odvracejí!'),
(2526, 21, 43, 'Mají snad místo Nás božstva jiná, jež bránit je budou? Ta nejsou schopna pomoci ani sama sobě, a oni jimi nebudou proti Nám chráněni.'),
(2527, 21, 44, 'Ba naopak, nechali jsme je i otce jejich užívat světa tohoto tak, až prodloužily se životy jejich. Což nevidí, že přicházíme na zem zmenšujíce ji ze všech stran? Jsou snad vítězi oni?'),
(2528, 21, 45, 'Rci: "Já varuji vás jedině vnuknutím mně daným, však hluší neslyší volání, jsou-li varováni."'),
(2529, 21, 46, 'A jestliže se jich dotkne jen jediné dechnutí trestu Pána tvého, vykřiknou: "Běda nám, my vskutku nespravedliví jsme byli!"'),
(2530, 21, 47, 'A postavíme v den zmrtvýchvstání váhu spravedlivou a nebude duše žádná o nic ošizena; a i kdyby to mělo jen váhu zrna hořčičného, My je přineseme. A my stačíme si jako účetní.'),
(2531, 21, 48, 'A dali jsme již kdysi Mojžíšovi a Árónovi spásné zjevení, osvícení, i bohabojným připomenutí,'),
(2532, 21, 49, 'těm, kteří se Pána svého kvůli nepoznatelnému obávají a před Hodinou se chvějí.'),
(2533, 21, 50, 'A toto je připomenutí požehnané, Námi seslané. Chcete je snad popírat?'),
(2534, 21, 51, 'A darovali jsme již předtím Abrahamovi správné vedení, neboť jsme o něm věděli.'),
(2535, 21, 52, 'Hle, pravil otci svému a lidu svému: "Co je to za sochy, jichž se tak usilovně přidržujete?"'),
(2536, 21, 53, 'Odpověděli: "Shledali jsme, že otcové naši je uctívali."'),
(2537, 21, 54, 'Pravil: "Vskutku vy i vaši otcové ve zjevném bludu jste!"'),
(2538, 21, 55, 'I řekli: "Přišels k nám s pravdou, anebo jsi jedním z těch, kdo žertují?"'),
(2539, 21, 56, 'Odpověděl: "Ba právě naopak! Váš Pán je Pánem nebe a země a On obé stvořil. A já jsem jedním z těch, kdož to dosvědčují.'),
(2540, 21, 57, 'Při Bohu, dozajista nastrojím úklad modlám vašim, jakmile záda vaše se obrátí."'),
(2541, 21, 58, 'A rozbil je na kousky všechny kromě té největší z nich - snad oni sami se k ní navrátí.'),
(2542, 21, 59, 'I řekli: "Kdo tohle božstvům našim udělal, ten věru mezi nespravedlivé patří!"'),
(2543, 21, 60, 'A pravili jedni: "Slyšeli jsme mladíka nějakého o nich poznámky činit, Abraham mu říkají."'),
(2544, 21, 61, 'I zvolali: "Přiveďte ho před oči lidí - snad budou proti němu svědčit!"'),
(2545, 21, 62, 'A zeptali se jej: "Byls to ty, Abrahame, kdo s našimi božstvy se tohle odvážil učinit?"'),
(2546, 21, 63, 'Odpověděl: "Nikoliv, to tenhle největší z nich udělal, zeptejte se jich, jestliže mohou promluvit!"'),
(2547, 21, 64, 'Nevěřící se obrátili proti sobě a řekli: "Vy věru jste nespravedliví!"'),
(2548, 21, 65, 'Potom však opět nastal obrat v hlavách jejich a prohlásili: "Ty přece víš, že vůbec nemluví!"'),
(2549, 21, 66, 'Abraham pravil: "Pročpak tedy uctíváte vedle Boha něco, co vám ani neprospívá, ani neškodí?'),
(2550, 21, 67, 'Hanba na vás i na to, co místo Boha uctíváte. Cožpak nejste rozumní?"'),
(2551, 21, 68, 'Zvolali: "Upalte ho a pomozte božstvům svým, udělat něco chcete-li."'),
(2552, 21, 69, 'Avšak My jsme pravili: "Ohni, vychladni a buď neškodným pro Abrahama!"'),
(2553, 21, 70, 'A hodlali mu úklad nastrojit, však My jsme způsobili, že sami ztrátu největší utrpěli.'),
(2554, 21, 71, 'A zachránili jsme jeho i Lota vedouce je do země, již jsme pro lidstvo celé požehnali,'),
(2555, 21, 72, 'a darovali jsme mu Izáka a Jakuba nádavkem a všechny jsme bezúhonnými učinili.'),
(2556, 21, 73, 'A ustanovili jsme je vůdci, kteří lid svůj řídí podle rozkazu Našeho, a vnukli jsme jim konání dobrého, dodržování modlitby a rozdáváni almužny - a byli služebníky Našimi.'),
(2557, 21, 74, 'A dali jsme Lotovi osvícení a vědění a zachránili jsme ho z města, jež nepravosti páchalo a jehož obyvatelé byli lidmi špatnými a hanebnými.'),
(2558, 21, 75, 'A uvedli jsme jej do milosrdenství Svého, neboť patřil mezi bezúhonné.'),
(2559, 21, 76, '(A zmiň) se o Noem, který již dříve Nás volal, a vyslyšeli jsme jej a zachránili jsme jej i rodinu jeho od pohromy nesmírné'),
(2560, 21, 77, 'a pomohli jsme mu proti lidu, jenž prohlašoval znamení Naše za vylhaná. A byl to lid špatný a utopili jsme je všechny.'),
(2561, 21, 78, 'A zmiň se o Davidovi a Šalomounovi; hle, oni vynesli rozsudek o poli, jež poškodilo stádo ovcí lidí jedněch, a byli jsme svědky rozsudku jejich.'),
(2562, 21, 79, 'A vnukli jsme Šalomounovi pochopení věci té a oběma jsme darovali osvícení a vědění. A donutili jsme hory a ptáky, aby spolu s Davidem Naši pěli chválu. A tak jsme učinili.'),
(2563, 21, 80, 'A naučili jsme jej vyrábět brnění pro vás, abyste se jimi chránili před útočností vzájemnou - budete za to vděčni?'),
(2564, 21, 81, 'A Šalomounovi jsme podrobili vítr, jenž jako vichřice na rozkaz jeho duje k zemi, již jsme požehnali. A o všech těch věcech My dobře jsme věděli.'),
(2565, 21, 82, 'A také některé satany jsme mu podrobili, aby se do moře potápěli či práce jiné proň vykonávali, a My jsme je při tom střežili.'),
(2566, 21, 83, 'A zmiň se o Jóbovi, jenž volal k Pánu svému: "Postihlo mne neštěstí, však tys nejslitovnější ze slitovníků!"'),
(2567, 21, 84, 'A vyslyšeli jsme jej a odvrátili od něho neštěstí, jež nesl, a vrátili jsme mu jeho rodinu a přidali ještě jednou tolik s nimi jako milosrdenství své a jako připomenutí těm, kdož Nás uctívají.'),
(2568, 21, 85, 'A zmiň se o Ismaelovi, Idrísovi a Dhu l-Kiflovi - a ti všichni byli trpěliví,'),
(2569, 21, 86, 'a uvedli jsme je do milosrdenství Svého a jsou mezi bezúhonnými.'),
(2570, 21, 87, 'A zmiň se o Muži s rybou; když odešel rozzloben a domníval se, že proti němu nic nezmůžeme. A volal nás v temnotách říkaje: "Není božstva kromě Tebe, sláva budiž Tobě! Věru jsem patřil k nespravedlivým!"'),
(2571, 21, 88, 'I vyslyšeli jsme jej a zachránili jsme jej z útrap jeho. A takto My poskytujeme záchranu věřícím.'),
(2572, 21, 89, 'A zmiň se o Zachariášovi, když volal k Pánu svému: "Pane můj, neponechávej mne samotného, Ty, jenž z dědiců jsi nejlepší!"'),
(2573, 21, 90, 'Vyslyšeli jsme jej a darovali jsme mu Jana a učinili jsme manželku jeho plodnou. A všichni ti se předstihovali v dobročinném konání a modlili se k Nám z touhy a úcty a byli vůči Nám pokorní.'),
(2574, 21, 91, 'A zmiň se o té, jež panenství své střežila. A vdechli jsme do ní část ducha Svého a učinili jsme z ní i ze syna jejího pro veškeré lidstvo znamení.'),
(2575, 21, 92, 'Hle, tato vaše obec je obcí jedinou a Já Pánem jsem vaším, uctívejte Mne tedy!'),
(2576, 21, 93, 'Avšak oni se ve věci své mezi sebou rozpoltili, však přesto se všichni k Nám navrátí.'),
(2577, 21, 94, 'Kdokoliv dobré skutky koná a je při tom věřící, tomu úsilí jeho věru nebude upřeno a zajisté mu je k dobru připíšeme.'),
(2578, 21, 95, 'A je zakázáno městu každému, jež jsme zahubili, aby se obyvatelé jeho v ně navrátili,'),
(2579, 21, 96, 'pokud nebudou vypuštěni Gog a Magog; a tehdy se lidé z každého hrobu vynoří!'),
(2580, 21, 97, 'Pravdivý slib se přibližuje, a hle, strnulé jsou zraky těch, kdož neuvěřili. "Běda nám, že lhostejní jsme byli k němu, ba naopak, byli jsme nespravedliví."'),
(2581, 21, 98, 'Vskutku vy i to, co jste místo Boha uctívali, palivem budete peklu a věru se doň dostanete!'),
(2582, 21, 99, 'Kdyby tamti byli skutečnými božstvy, nebyli by se sem dostali; a všichni v něm budou nesmrtelní.'),
(2583, 21, 100, 'A bude tam pro ně jen sténání, však nebudou v něm slyšeni.'),
(2584, 21, 101, 'Ti však, jimž jsme předem slíbili odměnu překrásnou, ti od pekla budou vzdáleni'),
(2585, 21, 102, 'a neuslyší z něho ani šelest nejmenší a v tom, po čem duše jejich toužily, budou nesmrtelní.'),
(2586, 21, 103, 'Ani den hrůzy největší je nezarmoutí a andělé je přivítají slovy: "Toto váš je den, jenž byl vám přislíben!"'),
(2587, 21, 104, 'V ten den svineme nebe, jako se svinují svitky knih, a jako jsme začali již prvním stvořením, tak opakovat to budeme podle slibu Námi daného. A My tak učiníme!'),
(2588, 21, 105, 'A věru jsme již napsali v žalmech, po připomenutí, že zemi podědí Moji bezúhonní služebníci.'),
(2589, 21, 106, 'A je v tom zajisté sdělení pro lid Mne uctívající.'),
(2590, 21, 107, 'A neposlali jsme tě leč jako znamení milosrdenství pro lidstvo veškeré.'),
(2591, 21, 108, 'Rci: "Bylo mi vnuknuto jenom, že božstvem vaším je Bůh jediný. Odevzdáte se tedy do Jeho vůle?"'),
(2592, 21, 109, 'A jestliže se odvrátí, tedy rci: "Já varoval vás bez rozdílu a věru nevím, je-li blízko či daleko to, co vám bylo slíbeno."'),
(2593, 21, 110, 'A On zná dobře to, co veřejně hovoříte, i to, co skrýváte.'),
(2594, 21, 111, 'Nevím, zda to snad není pouze pokušení pro vás a dopřání vám užívat na určitou chvíli.'),
(2595, 21, 112, 'Rci: "Pane můj, rozhodni podle pravdy!" Pán náš je Milosrdný a u Něho budiž vyžadována pomoc proti tomu, co lživě Mu připisujete!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Lidé, bojte se Pána svého, neboť otřesy Hodiny budou věcí nesmírnou.'),
(2597, 22, 2, 'V onen den, až ji spatříte, každá kojící opustí dítě, jež kojila, a každá těhotná potratí plod svůj; tehdy uvidíš lidi jako opilé, aniž se opili. Avšak trest Boží bude strašný.'),
(2598, 22, 3, 'Jsou pak mezi lidmi někteří, kdož hádají se o Bohu, aniž něco vědí, a následují každého satana vzpurného.'),
(2599, 22, 4, 'A je psáno o něm, že toho, kdo vezme si jej za ochránce, on věru z cesty svede a dovede ho k trestu plamene šlehajícího.'),
(2600, 22, 5, 'Lidé, jste-li na pochybách ohledně zmrtvýchvstání, vzpomeňte si, že stvořili jsme vás nejdřív z prachu, pak z kapky semene, pak z kapky přilnavé, pak z kousku masa ztvárněného či beztvarého - abychom vám to objasnili. A umisťujeme do lůna to, co chceme, až do lhůty stanovené, potom vás z něho vyvedeme jako kojence, až posléze dosáhnete dospělosti své. A některý z vás je povolán k Pánu dříve a některý z vás je vržen do věku sešlosti, takže neví nic z toho, co dříve již věděl. A vidíš zemi neplodnou, však když na ni sešleme vodu, pohne se a vzedme a dá vyrůst různým druhům rostlin nádherných.'),
(2601, 22, 6, 'A to je proto, že Bůh je vskutku pravda, a proto, že On oživuje mrtvé a je všemocný nad věcí každou'),
(2602, 22, 7, 'a že Hodina přijde a není o ní pochyby žádné a že Bůh vzkřísí ty, kdož v hrobech jsou.'),
(2603, 22, 8, 'A mezi lidmi je i ten, jenž o Boha se hádá, aniž něco zná a aniž je veden a aniž má Písmo osvícené,'),
(2604, 22, 9, 'a kroutí krkem svým, aby svedl jiné z cesty Boží. Hanba mu na tomto světě! A v den zmrtvýchvstání mu dáme okusit trestu spalujícího.'),
(2605, 22, 10, '"A to je za to, co předtím ruce tvé spáchaly!" A Bůh věru neukřivdí služebníkům Svým.'),
(2606, 22, 11, 'A mezi lidmi je i ten, jenž Bohu slouží jen na okraji; když dostane se mu dobrého, je s tím spokojen, jestliže však je postižen pokušením, tu obrátí se tváří zpět, ztráceje tak tento i onen svět - a to věru je ztráta zjevná.'),
(2607, 22, 12, 'A vzývá místo Boha něco, co nemůže mu ani uškodit, ani prospět - a to zajisté je zbloudění daleké.'),
(2608, 22, 13, 'A vzývá toho, jenž je mu spíše škodlivý než užitečný; jak špatný to pán a jak hnusný to přítel!'),
(2609, 22, 14, 'Bůh věru uvede ty, kdož uvěřili a zbožné skutky konali, do zahrad, pod nimiž řeky tekou, neboť Bůh učiní vše, co chce.'),
(2610, 22, 15, 'Kdo se domnívá, že Bůh mu nepomůže v životě pozemském ani v životě budoucím, nechť natáhne provaz až do nebe, pak ať jej přeřízne a potom nechť pohlédne, zda jeho úklad odstranil to, co jej rozhněvalo.'),
(2611, 22, 16, '... A takto jsme jej seslali jako znamení jasná - a nechť Bůh vede toho, koho chce.'),
(2612, 22, 17, 'A věru ti, kdož uvěřili a ti, kdož vyznávají židovství, sabejci, křesťané a zoroastrovci a ti, kdo k Bohu přidružují, zjistí, že Bůh mezi nimi rozliší v den zmrtvýchvstání, neboť Bůh svědkem je všech věcí.'),
(2613, 22, 18, 'Což jsi neviděl, že před Bohem padají na zem ti, kdož na nebesích jsou i na zemi, a také slunce a měsíc, hvězdy a hory, stromy a zvířata a mnoho lidí? Však nad mnohými z nich se trest uskuteční, neboť kým Bůh opovrhl, tomu nebudou pocty prokazovány. A Bůh věru učiní vše, co chce.'),
(2614, 22, 19, 'Toto jsou dvě skupiny nepřátelské, které se spolu přou ohledně Pána svého; těm, kdož neuvěřili, střižen bude šat z ohně a hlavy jejich budou polévány vodou vroucí,'),
(2615, 22, 20, 'jež rozleptá vše, co v břichách jejich je, i kůži jejich,'),
(2616, 22, 21, 'a pro ně jsou připraveny důtky železné.'),
(2617, 22, 22, 'A kdykoli budou odtamtud chtít odejit kvůli utrpení, budou tam vráceni a bude jim řečeno: "Okuste trestu spalujícího!"'),
(2618, 22, 23, 'Naproti tomu Bůh věru uvede ty, kdož uvěřili a zbožné skutky konali, do zahrad, pod nimiž řeky tekou; a tam budou ozdobeni náramky zlatými a perlami a oděni budou do šatu z hedvábí'),
(2619, 22, 24, 'a budou vedeni k řeči překrásné a budou vedeni ke stezce Toho, jenž hoden je chvály.'),
(2620, 22, 25, 'A věru těm, kdož nevěří a odvádějí jiné z cesty Boží a od Mešity posvátné, již jsme určili pro všechny lidi - jak pro ty, kdož u ní sídlí, tak i pro kočovníky, a těm, kdož chtěli by zde zavést bludnou víru ze svévole, dáme okusit trestu bolestného.'),
(2621, 22, 26, 'A hle, připravili jsme pro Abrahama příbytek na místě chrámu a řekli jsme mu: "Nepřidružuj ke Mně nic, ale očisti Chrám Můj pro ty, kdo obcházení konají, a pro ty, kdož modlí se vestoje, klaní se a na zem padají!'),
(2622, 22, 27, 'A vyzývej lidi ke konání pouti, a nechť k tobě přijdou pěšky anebo jedouce na všelikých velbloudech rychlých, přicházejíce ze všech rozsedlin hlubokých,'),
(2623, 22, 28, 'aby podali svědectví o užitečných darech jim uštědřených a aby vzpomínali jména Božího ve dnech známých nad dobytčetem ze stád, jež Bůh jim v obživu daroval. Jezte z nich a nakrmte nuzného a chudého!'),
(2624, 22, 29, 'Potom nechť skončí se zanedbáváním svým, vyplní přesně sliby své a vykonají obcházení okolo Chrámu starobylého."'),
(2625, 22, 30, 'A takto konejte! A kdo v úctě bude mít posvátné věci Boží, bude to pro něj lepší u Pána jeho. A jsou vám dovolena zvířata ze stád kromě toho, co bylo již dříve jmenováno. Vyhýbejte se špíně model, vystříhejte se řeči falešné!'),
(2626, 22, 31, 'Buďte jako hanífové Bohu oddáni, nikoliv jako ti, kdo k Němu přidružují! Ten, kdo přidružuje k Bohu, podobá se tomu, jenž spadl z nebe a jejž uchvátili ptáci anebo jej zavál vítr do místa dalekého.'),
(2627, 22, 32, 'Takto konejte! A má-li kdo v úctě obřady Boží, je to známka bohabojnosti srdcí.'),
(2628, 22, 33, 'A budete mít z nich užitek až do lhůty určené - a místo obětování jejich bude u Chrámu starobylého.'),
(2629, 22, 34, 'A každé obci jsme stanovili obřady, aby lidé vzpomínali jména Božího nad dobytčetem ze stád, jež jsme jim uštědřili. A božstvem vaším je Bůh jediný, Jemu se do vůle odevzdávejte! A oznam zvěst radostnou pokorným,'),
(2630, 22, 35, 'těm, jichž srdce jsou naplněna bázní, když vzpomínáno je Boha, a kteří jsou trpěliví k tomu, co je postihuje, a kteří dodržují modlitby a rozdávají almužny z toho, co jsme jim uštědřili.'),
(2631, 22, 36, 'A učinili jsme pro vás velbloudice obětmi při obřadech Božích a je v tom pro vás dobro. A vzpomínejte jména Božího nad nimi, když stojí v řadě. A když se jejich boky zhroutí, jezte z nich a nakrmte zdrženlivé i stydlivě prosící. A takto jsme vám podmanili obětní zvířata - snad budete vděční!'),
(2632, 22, 37, 'K Bohu nedospěje ani maso jejich, ani krev jejich, ale dospěje k Němu bohabojnost vaše. A takto vám je podmanil, abyste hlásali velikost Boží za to, že vás uvedl na cestu pravou. A oznam těm, kdož dobré konají, zvěst radostnou!'),
(2633, 22, 38, 'Bůh ubrání ty, kdož uvěřili, neboť Bůh věru nemiluje žádného zrádce nevěřícího.'),
(2634, 22, 39, 'A těm, kdož chtějí, je dovoleno, aby bojovali kvůli tomu, že jim bylo ukřivděno. A Bůh věru je schopen poskytnout jim pomoc,'),
(2635, 22, 40, 'těm, kdož byli bezprávně vyhnáni ze svých domovů jedině proto, že říkali: "Pánem naším je Bůh!" A kdyby byl Bůh nezahnal jedny lidi druhými, věru by byly bývaly zničeny poustevny, kostely, modlitebny a místa klanění, v nichž hojně je vzpomínáno jména Božího. - A Bůh vskutku pomůže těm, kdož pomáhají Jemu - a Bůh věru je silný, mocný.'),
(2636, 22, 41, 'On pomůže těm, kteří - když jsme je upevnili na zemi - dodržují modlitbu a dávají almužnu a nařizují vhodné a zakazují zavrženíhodné. A Bohu patří konečné rozhodnutí o věcech všech.'),
(2637, 22, 42, 'Prohlašují-li tě za lháře, pak již před nimi prohlašoval proroky za lháře lid Noemův, ´Ádovci a Thamúdovci'),
(2638, 22, 43, 'a také lid Abrahamův i Lotovi soukmenovci'),
(2639, 22, 44, 'i obyvatelé Madjanu. Též Mojžíš byl za lháře prohlášen. Já pak poskytl jsem nevěřícím odklad, leč posléze jsem je uchvátil, a jaké bylo mé zavržení!'),
(2640, 22, 45, 'Kolika městům jsme již dali zahynout, protože byla nespravedlivá a dnes jsou od základu zpustošena! A kolik studní je zasypáno a kolik vznosných zámků zbořeno!'),
(2641, 22, 46, 'Což necestovali po zemi a neměli srdce, aby chápali, a uši, aby slyšeli avšak nejsou to oči jejich, jež jsou nevidoucí, nýbrž slepá jsou srdce v hrudích jejich.'),
(2642, 22, 47, 'A vyžadují na tobě, abys uspíšil příchod trestu, avšak Bůh slib Svůj nezruší. A jeden den je věru u Pána tvého jako tisíc let z těch, jež vy počítáte.'),
(2643, 22, 48, 'Kolika městům jsem poskytl odklad, přestože byla nespravedlivá! Potom jsem je však uchvátil a u Mne je cíl konečný.'),
(2644, 22, 49, 'Rci: "Lidé, já jsem pro vás jen varovatelem zjevným!"'),
(2645, 22, 50, 'Těm, kdož uvěřili a zbožné skutky konali, dostane se slitování i přídělu štědrého,'),
(2646, 22, 51, 'avšak ti, kdož ukládali o znamení Naše snažíce se je znemožnit, ti budou obyvateli výhně pekelné.'),
(2647, 22, 52, 'Nevyslali jsme před tebou posla ani proroka, aby satan nebyl podvrhl do přání jeho něco nevhodného, avšak Bůh zruší to, co satan podvrhne, a potom Bůh potvrdí Svá znamení - a Bůh vševědoucí je i moudrý.'),
(2648, 22, 53, 'A jedná takto, aby učinil to, co podvrženo bylo satanem, pokušením pro ty, v jichž srdcích je choroba a jichž srdce jsou zatvrzelá - a nespravedliví jsou věru ve vzdoru hlubokém -'),
(2649, 22, 54, 'a aby ti, jimž dostalo se vědění, poznali, že to je pravda od Pána tvého, a uvěřili v ni a pokořila se před ní srdce jejich. Bůh zajisté povede ty, kdož uvěřili, ke stezce přímé.'),
(2650, 22, 55, 'Avšak ti, kdož neuvěřili, nepřestanou o ní pochybovat, dokud se k nim znenadání Hodina nedostaví anebo dokud k nim nepřijde trest dne zhoubného.'),
(2651, 22, 56, 'V ten den bude náležet vláda Bohu a On bude soudit mezi nimi. A ti, kdož uvěřili a zbožné skutky konali, budou v zahradách slastí,'),
(2652, 22, 57, 'zatímco ty, kdož neuvěřili a znamení Naše za lež prohlašovali, očekává trest ponižující.'),
(2653, 22, 58, 'A těm, kdož vystěhovali se na cestě Boží a potom byli zabiti anebo zemřeli, Bůh věru uštědří podíl překrásný, neboť Bůh nejlepší je z uštědřovatelů.'),
(2654, 22, 59, 'A věru je uvede na místo, jež se jim bude líbit; a Bůh zajisté je vševědoucí, soucitný.'),
(2655, 22, 60, 'Tak bude! Tomu, kdo potrestá stejným způsobem, jakým byl sám trestán, a potom je mu kvůli tomu ukřivděno, tomu věru Bůh pomůže, vždyť Bůh je promíjející, odpouštějící.'),
(2656, 22, 61, 'A je to proto, že Bůh dává noci proniknout v den a dni v noc a protože je slyšící, jasnozřivý.'),
(2657, 22, 62, 'A je to proto, že Bůh je pravda a že to, co je místo Něho vzýváno, je nicotné a Bůh věru je vznešený, veliký.'),
(2658, 22, 63, 'Což jsi neviděl, jak Bůh seslal z nebes vodu, takže se země zazelenala? Vždyť Bůh je věru rychlý a dobře zpravený.'),
(2659, 22, 64, 'Jemu náleží vše, co na nebesích je i na zemi, a On je soběstačný, chvályhodný.'),
(2660, 22, 65, 'Což jsi neviděl, jak Bůh vám podrobil vše, co na zemi je, stejně jako lodi, jež po moři plují podle rozkazu Jeho? A On zadržuje nebe, aby nespadlo na zem, leda z dovolení Jeho. Vždyť Bůh je vůči lidem věru shovívaný, slitovný.'),
(2661, 22, 66, 'A On je ten, jenž dal vám život, potom vás zemřít nechá, aby vás posléze znovu oživil. A člověk je vskutku velmi nevděčný.'),
(2662, 22, 67, 'A každé obci jsme stanovili obřady, jimiž se řídí. Nechť se tedy s tebou nehádají ohledně věci této. Vyzývej k Pánu svému, neboť tys věru na cestě přímé!'),
(2663, 22, 68, 'A jestliže se s tebou hádají, rci: "Bůh zná nejlépe, co děláte,'),
(2664, 22, 69, 'a Bůh rozsoudí mezi vámi v den zmrtvýchvstání to, o čem jste se tak hádali."'),
(2665, 22, 70, 'Což nevíš, že Bůh zná vše, co na nebi je i na zemi? A vše to je věru v Knize zapsáno, a to je pro Boha vskutku velmi snadné.'),
(2666, 22, 71, 'A oni uctívají místo Boha něco, k čemu On neseslal oprávnění a o čem nemají žádné vědění. A nespravedliví nebudou mít pomocníka žádného.'),
(2667, 22, 72, 'A když jsou jim přednášena Naše znamení jasná, uzříš na tvářích těch, kdož neuvěřili, odpor a div že nenapadnou ty, kdož sdělují jim Naše znamení. Rci: "Mám vám sdělit něco ještě horšího, než je toto? Oheň - ten slíbil Bůh těm, kdož neuvěří a jak hnusný to bude cíl konečný!"'),
(2668, 22, 73, 'Lidé, je vám uváděno podobenství, vyslyšte je! Ti, které vzýváte místo Boha, nejsou schopni stvořit ani mouchu, i kdyby se k tomu všichni spojili! A kdyby jim moucha něco vzala, nebyli by schopni si to vzít nazpět od ní. Jak slabí jsou oba: žádající i žádaný!'),
(2669, 22, 74, 'Nevěřící neoceňují Boha pravdivou mírou a Bůh věru je silný a mocný.'),
(2670, 22, 75, 'Bůh si volí mezi anděly a mezi lidmi posly Své, vždyť Bůh je slyšící, jasnozřivý.'),
(2671, 22, 76, 'On zná dobře, co je před vámi i co je za vámi, a k Bohu se věci všechny navrátí.'),
(2672, 22, 77, 'Vy, kteří věříte! Klaňte se, padejte na zem a uctívejte Pána svého! Konejte dobré, snad budete blaženi!'),
(2673, 22, 78, 'A bojujte usilovně za Boha s úsilím, jež si zaslouží! On si vás vyvolil a nepřipravil vám žádné obtíže v náboženství, jež je vírou předka vašeho Abrahama. A On nazval vás muslimy již dříve - i v tomto -, aby posel tento byl vám svědkem a abyste vy byli svědky pro ostatní lidi. Dodržujte modlitbu a rozdávejte almužnu a hledejte oporu u Boha! On vaším je ochráncem - jak překrásný to ochránce a jak výtečný to pomocník!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Šťastní jsou ti věřící,'),
(2675, 23, 2, 'kteří v modlitbách svých jsou pokorní,'),
(2676, 23, 3, 'kteří od planých řečí se odvracejí,'),
(2677, 23, 4, 'kteří almužny rozdávají,'),
(2678, 23, 5, 'kteří pohlaví svá ochraňují'),
(2679, 23, 6, 'jen pro manželky své či otrokyně; a nebudou za to pokáráni -'),
(2680, 23, 7, 'však kdo více než po tomto touží, to věru jsou přestupníci,'),
(2681, 23, 8, 'kteří svěřené opatrují a úmluvy své pevně dodržují,'),
(2682, 23, 9, 'kteří o modlitby své pilně dbají.'),
(2683, 23, 10, 'Tito zajisté dědici se stanou,'),
(2684, 23, 11, 'kteří ráj zdědí a věčně v něm budou.'),
(2685, 23, 12, 'A věru jsme člověka nejdříve z části nejčistší hlíny stvořili'),
(2686, 23, 13, 'a pak jsme jej kapkou semene v příbytku jistém učinili.'),
(2687, 23, 14, 'Potom jsme z kapky semene hmotu přilnavou stvořili a z hmoty přilnavé jsme kousek masa učinili; a z kousku masa jsme kosti stvořili a kosti jsme masem obalili. A potom jsme mu v druhém stvoření vzniknout dali. Požehnán buď Bůh, nejlepší ze stvořitelů!'),
(2688, 23, 15, 'Posléze pak vskutku zemřete'),
(2689, 23, 16, 'a pak v den zmrtvýchvstání vzkříšeni budete.'),
(2690, 23, 17, 'Nad vámi jsme věru stvořili sedm cest a nejsme vůči stvoření Svému lhostejní:'),
(2691, 23, 18, 'z nebes vodu v určitém množství sesíláme a na zemi ji rozmísťujeme, ačkoliv jsme věru schopni odvést ji;'),
(2692, 23, 19, 'a pomocí vody jsme pro vás dali vyrůst zahradám s palmami a révou a hojnost ovoce v nich máte, jež požíváte -'),
(2693, 23, 20, 'a také stromu, jenž z hory Sinaje vzchází a jenž olej a šťávu dává, pro obživu pojídajících.'),
(2694, 23, 21, 'A věru ve stádech vašich je pro vás poučení: napájíme vás tím, co je v břichách jejich, a máte z nich i mnohý jiný užitek. A maso jejich pojídáte'),
(2695, 23, 22, 'a na nich stejně jako na lodích se nést dáváte.'),
(2696, 23, 23, 'A poslali jsme kdysi Noema k lidu jeho, i pravil jim: "Lide můj, uctívejte Boha a nemějte božstva jiného kromě Něho! Což nebudete z bohabojných?"'),
(2697, 23, 24, 'Však velmoži ti, kdož nevěřící byli z lidu jeho prohlásili: "Tohle je pouze smrtelník jako vy a nad vámi chce se jen vyvyšovat. Kdyby byl Bůh chtěl, byl by určitě anděly seslal. A neslyšeli jsme o ničem takovém od otců svých dávných.'),
(2698, 23, 25, 'A není on ničím než člověkem džiny posedlým, pozorujte jej tedy nějakou chvíli!"'),
(2699, 23, 26, 'I zvolal Noe: "Pane můj, pomoz mi za to, že za lháře mne pokládají!"'),
(2700, 23, 27, 'Tehdy vnukli jsme mu: "Postav archu pod dohledem očí Našich a podle vnuknutí Našeho! A až přijde rozkaz Náš a pec až bude vřít,'),
(2701, 23, 28, 'do archy uved\' ze všech druhů po páru a také svou rodinu, kromě toho z ní, o němž již dříve slovo bylo vyřčeno. A nepřimlouvej se u Mne za ty, kdož nespravedliví byli, neboť ti budou utopeni! A až se usadíš ty, a kdož s tebou jsou, v arše, tehdy prones:, Chvála Bohu, jenž zachránil nás před lidem nespravedlivým!?'),
(2702, 23, 29, 'A rci dále:, Pane můj, dej mi sestoupit na místě požehnaném, vždyť Tys nejlepší z těch, kdož sestoupit dávají!? "'),
(2703, 23, 30, 'A věru jsou v tom znamení a My vskutku jsme byli těmi, kdož zkouškám podrobují.'),
(2704, 23, 31, 'Po nich pak dali jsme vyrůst dalšímu pokolení'),
(2705, 23, 32, 'a vyslali jsme k nim proroka z nich, aby hlásal: "Uctívejte Boha a nemějte božstva jiného kromě Něho. Což nebudete bohabojní?"'),
(2706, 23, 33, 'A řekli velmoži z lidu jeho, ti, kdož nevěřícími byli a za lež setkání se světem budoucím prohlásili, a ti, které jsme v životě pozemském zámožnými učinili: "Tohle je pouze smrtelník jako vy. On z toho jí, co i vy jíte, a pije z toho, co i vy pijete.'),
(2707, 23, 34, 'Uposlechnete-li smrtelníka vám podobného, tedy zajisté ztrátu utrpíte.'),
(2708, 23, 35, 'Což neslibuje vám, že poté, co zemřete a prachem a kostmi se stanete, z hrobů vyvedeni budete?'),
(2709, 23, 36, 'Jak chybné, jak chybné je to, co je vám slibováno!'),
(2710, 23, 37, 'Není života jiného než tohoto pozemského; umíráme a žijeme, však nikdy vzkříšeni nebudeme.'),
(2711, 23, 38, 'A je to člověk pouze, jenž o Bohu lež si vymyslil a my mu neuvěříme!"'),
(2712, 23, 39, 'Zvolal: "Pane můj, pomoz mi za to, že za lháře mne prohlašují!"'),
(2713, 23, 40, 'Odpověděl: "Za chviličku budou věru mezi těmi, kdož litují."'),
(2714, 23, 41, 'A uchvátilo je vzkřiknutí skutečně a učinili jsme je podobnými pěně. Pryč s lidem nespravedlivým!'),
(2715, 23, 42, 'Po nich jsme pak dali vzniknout jiným pokolením,'),
(2716, 23, 43, 'a žádný národ lhůtu svou neuspíší ani neodloží!'),
(2717, 23, 44, 'Potom jsme vysílali posly jednoho za druhým, však kdykoliv přišel k nějakému národu posel Jeho, za lháře jej prohlásili. A tak dali jsme následovat jednomu národu za druhým a učinili jsme je příběhy varovnými. Pryč s lidem, který nevěří!'),
(2718, 23, 45, 'Potom jsme vyslali Mojžíše a bratra jeho Áróna se znameními Svými a s pověřením zjevným'),
(2719, 23, 46, 'k Faraónovi a velmožům jeho, však oni zpyšněli a byli lidem povýšeným'),
(2720, 23, 47, 'a řekli: "Máme uvěřit dvěma smrtelníkům stejným jako my, jejichž lid nám otročí?"'),
(2721, 23, 48, 'A oba za lháře prohlásili a stali se těmi, kdož do záhuby spějí.'),
(2722, 23, 49, 'A dali jsme Mojžíšovi Písmo doufajíce, že snad lid jeho půjde po cestě správné.'),
(2723, 23, 50, 'A učinili jsme syna Mariina i matku jeho znameními a poskytli jsme jim útulek na výšině bezpečné, s pramenem vody hojné.'),
(2724, 23, 51, '"Poslové, jezte z výtečných pokrmů a konejte zbožné skutky, vždyť Já o všem, co činíte, dobře jsem zpraven.'),
(2725, 23, 52, 'Tato vaše obec je vskutku obcí jedinou a Já vaším jsem Pánem. Buďte tedy vůči Mně bohabojní!"'),
(2726, 23, 53, 'Však lidé se rozštěpili ve věci své na sekty a strana každá se radovala jen z toho, co jí bylo vlastní.'),
(2727, 23, 54, 'Ponech je tedy v hlubinách jejich omylu po dobu nějakou!'),
(2728, 23, 55, 'Domnívají se snad, že to, čím jsme hojně opatřili z majetku a synů...?'),
(2729, 23, 56, 'Pobízíme je rychle ke konání dobrých skutků, avšak oni o tom nemají tušení.'),
(2730, 23, 57, 'Věru ti, kdož bázní před Pánem svým jsou naplněni,'),
(2731, 23, 58, 'ti, kdož věří v Pána svého znamení,'),
(2732, 23, 59, 'ti, kdož nikoho k Pánu svému nepřidružují,'),
(2733, 23, 60, 'ti, kdož dávají to, co dávají, a jejichž srdce se chví před tím, že k Pánu svému se navrátí,'),
(2734, 23, 61, 'ti všichni se předhánějí v konání dobrých skutků a jako první jich dosahují.'),
(2735, 23, 62, 'Každé duši My ukládáme jedině podle její schopnosti. A v Našem vlastnictví je Kniha, jež pravdu hovoří, takže ublíženo nebude nikomu.'),
(2736, 23, 63, 'Přesto však srdce jejich jsou v omylu hlubokém ohledně Knihy této a jsou u nich činy jiné, jež konají proti tomu.'),
(2737, 23, 64, 'A až postihneme trestem ty z nich, kdož v přepychu žijí, hle, budou volat úpěnlivě!'),
(2738, 23, 65, '"Nevolejte dnešního dne, neboť se vám pomoci od nás nedostane!'),
(2739, 23, 66, 'Byla vám kdysi přednášena Moje znamení, však vy jste se při tom obraceli na patách,'),
(2740, 23, 67, 'jsouce vůči nim pohrdaví a planě tlachajíce v nočních rozmluvách."'),
(2741, 23, 68, 'Což neuvažovali o řeči této, když přišlo k nim to, čeho se jejich otcům dávným nedostalo,'),
(2742, 23, 69, 'anebo snad nepoznali posla svého, takže ho zapírají,'),
(2743, 23, 70, 'či řeknou snad, že džiny je posedlý? Nikoliv, přišel k nim s pravdou, však většina z nich si pravdu hnusí.'),
(2744, 23, 71, 'Kdyby pravda následovala jejich učení scestná, věru by byla nebesa a země a vše, co mezi nimi je, zkaženo. Však nikoliv, My přinesli jsme jim napomenutí jejich, však oni od připomenutí svého se odvracejí.'),
(2745, 23, 72, 'Či snad žádáš od nich odměnu? Vždyť odměna Pána tvého je mnohem lepší, vždyť On nejlepším je z těch, kdož obživu uštědřují.'),
(2746, 23, 73, 'Ty věru je zveš ke stezce přímé,'),
(2747, 23, 74, 'avšak ti, kdož nevěří v život budoucí, se uchylují od stezky té.'),
(2748, 23, 75, 'A i kdybychom se nad nimi slitovali a odstranili to zlé, jež na nich lpí, věru by zatvrzele ve vzpurnosti své setrvali jdouce jako slepí.'),
(2749, 23, 76, 'A zajisté jsme je již postihli trestem, ale oni se Pánu svému nepodrobí ani se nepokoří,'),
(2750, 23, 77, 'pokud před nimi neotevřeme bránu trestu strašného, a hle, v něm si zoufají!'),
(2751, 23, 78, 'A On je ten, jenž stvořil vám sluch, zrak i srdce - a jak málo jste mu vděčni!'),
(2752, 23, 79, 'A On je ten, jenž vás po zemi rozšířil, a u Něho budete shromážděni.'),
(2753, 23, 80, 'A On je ten, jenž život i smrt dává, a On střídání noci a dne ovládá. Což to nepochopíte?'),
(2754, 23, 81, 'Ba nikoliv! Oni hovoří totéž, co již dřívější hovořili,'),
(2755, 23, 82, 'a říkají: "Cožpak až zemřeme a prachem a kostmi se staneme, budeme vzkříšeni?'),
(2756, 23, 83, 'Tím už dříve vyhrožováno bylo nám i otcům našim, a nejsou to leč povídačky starých!"'),
(2757, 23, 84, 'Rci: "Komu náleží země a ti, kdo na ní jsou, víte-li?"'),
(2758, 23, 85, 'Odvětí: "Bohu." Rci: "Proč se tedy nevzpamatujete?"'),
(2759, 23, 86, 'Zeptej se: "Kdo Pánem je sedmi nebí a Pánem trůnu nesmírného?"'),
(2760, 23, 87, 'Odvětí: "Bůh." Rci: "Což tedy bohabojní nebudete?"'),
(2761, 23, 88, 'Otaž se: "V čí ruce je panství nade všemi věcmi a kdo ochraňuje, zatímco proti Němu žádné ochrany není, odpovězte, víte-li?"'),
(2762, 23, 89, 'Odvětí: "Bůh." Rci: "Jak můžete být tedy tak očarováni?"'),
(2763, 23, 90, 'Ba věru, My přišli jsme k nim s pravdou, zatímco oni vskutku lháři jsou.'),
(2764, 23, 91, 'Bůh nevzal si žádné dítě a není vedle Něho božstva jiného, jinak by každé božstvo odneslo to, co stvořilo, a některé z nich by se nad druhé povýšilo. Oč je Bůh slavnější než to, co mu lživě připisují,'),
(2765, 23, 92, 'On, jenž zná nepoznatelné i všeobecně známé. Oč je On vznešenější než to, co k Němu přidružují!'),
(2766, 23, 93, 'Rci: "Pane můj, jestliže mi ukážeš to, co slíbeno bylo jim,'),
(2767, 23, 94, 'pak, Pane můj, nedej, abych byl mezi lidem nespravedlivým!"'),
(2768, 23, 95, 'Jsme věru s to ti ukázat, co jsme jim slíbili,'),
(2769, 23, 96, 'avšak raději odrážej zlé tím, co je lepší! My dobře známe to, co si vymýšlejí.'),
(2770, 23, 97, 'Rci: "Pane můj, utíkám se k Tobě před satanů našeptáváním'),
(2771, 23, 98, 'a utíkám se k Tobě, Pane můj, před jejich přiblížením!"'),
(2772, 23, 99, 'A když k některému z nich přichází smrt, tu volá: "Pane můj, dej mi vrátit se na zemi,'),
(2773, 23, 100, 'možná že vykonám něco dobrého v tom, co jsem zanedbal!" Však pozor! Toto jsou jen slova, jež pronáší, avšak za nimi stojí přehrada až do dne, kdy budou vzkříšeni.'),
(2774, 23, 101, 'A až bude zatroubeno na pozoun, v ten den mezi nimi nebude platit rodokmen a nebudou se vzájemně vyptávat.'),
(2775, 23, 102, 'Ti, jejichž váha bude těžká, ti věru budou blažení,'),
(2776, 23, 103, 'ale ti, jejichž váha bude lehká, ti sami sobě ztrátu způsobili a budou v pekle nesmrtelní,'),
(2777, 23, 104, 'jejich tváře bude oheň ošlehávat a oni zuby budou skřípat.'),
(2778, 23, 105, '"Což vám nebyla Má znamení zvěstována? Vy však jste je za lživá prohlásili!"'),
(2779, 23, 106, 'Řeknou: "Pane náš, přemohla nás naše nešťastná povaha a stali jsme se lidmi bloudícími.'),
(2780, 23, 107, 'Pane náš, vyveď nás odsud, a jestliže to budeme na zemi opakovat, pak vskutku budeme nespravedliví."'),
(2781, 23, 108, 'I řekne Bůh: "Zahnáni buďtež do něho a nemluvte na Mne!'),
(2782, 23, 109, 'Vždyť věru jedna část služebníků Mých hovořila:, Pane náš, uvěřili jsme, odpusť nám a slituj se nad námi, neboť Tys nejlepší ze slitovníků!?'),
(2783, 23, 110, 'A vy jste si z nich posměšky tropili, takže jste na Mé připomenutí zapomněli a ještě jste se jim smáli.'),
(2784, 23, 111, 'Dnes však Já odměňuji věřící za to, co vytrpěli, a oni dnes úspěchu dosáhli."'),
(2785, 23, 112, 'A pak se Bůh otáže: "Jaký počet let jste na zemi setrvali?"'),
(2786, 23, 113, 'Odpovědi: "Setrvali jsme tam den či jeho část. Zeptej se těch, kdož počítat umějí!"'),
(2787, 23, 114, 'I odpoví: "Setrvali jste tam pouze dobu nepatrnou, ach, kéž byste byli věděli!'),
(2788, 23, 115, 'Domníváte se snad, že jsme vás stvořili jen bezúčelně a že k Nám nebudete navráceni?"'),
(2789, 23, 116, 'Nechť je povznesen Bůh, vládce skutečný! Není božstva kromě Něho, Pána trůnu vznešeného!'),
(2790, 23, 117, 'A kdo vzývá vedle Boha ještě božstvo jiné, o němž důkazu nemá, ten Pánu svému z toho musí vydat počet. A věru nevěřící nebudou blaženi!'),
(2791, 23, 118, 'A rci: "Pane můj, odpusť a slituj se, vždyť Tys nejlepší ze slitovníků!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Toto je súra, kterou jsme seslali a učinili závaznou; a seslali jsme v ní znamení jasná - snad se vzpamatujete.'),
(2793, 24, 2, 'Cizoložnici a cizoložníka zbičujte, každého z nich sto ranami! A nechť vás nepojme vůči nim lítost, s ohledem na náboženství Boží, věříte-li v Boha a v den poslední. A nechť skupina věřících je svědkem trestu jejich.'),
(2794, 24, 3, 'A cizoložník se smí oženit jedině s cizoložnicí nebo modloslužebnicí a cizoložnice se smí vdát jedině za cizoložníka nebo modloslužebníka - a takové sňatky jsou věřícím zakázány.'),
(2795, 24, 4, 'Ty, kdo vrhají podezření na počestné ženy a pak nepřivedou čtyři svědky, zbičujte osmdesáti ranami a nepřijímejte od nich již nikdy žádné svědectví, neboť to jsou hanebníci'),
(2796, 24, 5, 'kromě těch, kdož se potom budou kát a napraví se. Bůh věru je odpouštějící, slitovný.'),
(2797, 24, 6, 'U těch, kdož vrhají podezření na manželky své a nemají proti nim svědky kromě sebe samých, nechť svědectví každého z nich sestává ze čtyřnásobného svědectví při Bohu, že vskutku jsou pravdomluvní,'),
(2798, 24, 7, 'a pátým svědectvím dosvědčí, že svolávají na sebe prokletí Boží, ukáže-li se, že jsou lháři.'),
(2799, 24, 8, 'A bude odvrácen od ženy trest, jestliže ona čtyřikrát při Bohu dosvědčí, že žalobce patří vskutku mezi lháře,'),
(2800, 24, 9, 'a pátým svědectvím dosvědčí, že svolává na sebe hněv Boží, ukáže-li se, že žalobce mluvil pravdu.'),
(2801, 24, 10, 'A kdyby nebylo dobrodiní Božího vůči vám a milosrdenství Jeho a toho, že Bůh je blahovolný vůči kajícníkům a moudrý...'),
(2802, 24, 11, 'Věru ti, kdož přišli s pomluvou, jsou jen malou skupinou mezi vámi, však nepokládejte to za něco zlého pro vás. Nikoliv, je to pro vás dobré. Každému muži z nich se dostane toho, co zaslouží si podle hříchu, zatímco ten, kdo z nich vzal na sebe jeho větší část, obdrží trest nesmírný.'),
(2803, 24, 12, 'Proč, když to věřící muži a věřící ženy uslyšeli, nemyslili o tom v duších svých příznivě a proč nezvolali: "Toto je pomluva zjevná"?'),
(2804, 24, 13, 'Proč nepřivedli o tom čtyři svědky? A protože nepřivedli svědky, jsou před Bohem lháři.'),
(2805, 24, 14, 'A kdyby nebylo laskavosti Boží vůči vám a milosrdenství Jeho na tomto i onom světě, byli byste bývali věru postiženi za to, čemu jste se tak horlivě věnovali, trestem nesmírným,'),
(2806, 24, 15, 'když roznášeli jste to jazyky svými a když hovořili jste ústy svými něco, o čem neměli jste žádné vědění. A považovali jste to za nicotné, zatímco u Boha je to nesmírné.'),
(2807, 24, 16, 'Pročpak jste, když jste to slyšeli, neřekli: "Není pro nás vhodné, abychom o tom hovořili. Sláva Tobě, vždyť to pomluva je nesmírná"?'),
(2808, 24, 17, 'Bůh vás varuje, abyste něco podobného již nikdy neopakovali, jste-li věřící.'),
(2809, 24, 18, 'A Bůh vám objasňuje znamení Svá a Bůh vševědoucí je i moudrý.'),
(2810, 24, 19, 'Těm, kdož si přejí, aby se rozšířila nestoudnost mezi věřícími, těm dostane se trestu bolestného na tomto i na onom světě; a Bůh dobře zná, zatímco vy neznáte.'),
(2811, 24, 20, 'A kdyby nebylo laskavosti Boží vůči vám a milosrdenství Jeho a toho, že Bůh je shovívavý, slitovný...'),
(2812, 24, 21, 'Vy, kteří věříte! Nenásledujte kroky satanovy! Tomu, kdo následuje kroky satanovy, přikazuje on nestoudnost a věci zavrženíhodné. A kdyby nebylo laskavosti Boží vůči vám a milosrdenství Jeho, žádný z vás by se byl nikdy neočistil, avšak Bůh očišťuje, koho chce, a Bůh slyšící je i vševědoucí.'),
(2813, 24, 22, 'Nechť ti z vás, kteří těší se přízni a dostatku, nepřísahají, že přestanou dávat příbuzným, chudákům a vystěhovalcům na cestě Boží; nechť raději odpouštějí a promíjejí. Což byste nebyli rádi, aby vám Bůh odpustil - a Bůh je odpouštějící, slitovný.'),
(2814, 24, 23, 'Ti, kdož obviňují lehkovážné, ale věřící počestné ženy, budou prokleti na tomto i na onom světě a očekává je trest nesmírný'),
(2815, 24, 24, 'v den, kdy budou jazyky i ruce i nohy jejích svědčit proti nim o tom, co dělali.'),
(2816, 24, 25, 'V ten den jim dá Bůh v plné míře podle jejich víry skutečné a poznají, že Bůh je skutečnost zjevná.'),
(2817, 24, 26, 'Zkažené ženy patří ke zkaženým mužům a zkažení mužové ke zkaženým ženám, zatímco zachovalé ženy jsou pro zachovalé muže a zachovalí mužové jsou pro zachovalé ženy. A zachovalé ženy budou zproštěny obvinění z toho, co se o nich hovoří, a dostane se jim odpuštění a přídělu štědrého.'),
(2818, 24, 27, 'Vy, kteří věříte! Nevstupujte do jiných domů kromě vlastních, pokud se nezeptáte, zda jste vítáni, a pokud nepozdravíte jejich obyvatele! A toto je pro vás vhodnější - snad si to připomenete.'),
(2819, 24, 28, 'A nenaleznete-li v nich nikoho, pak tam nevstupujte, pokud vám to nebylo dovoleno. A bude-li vám řečeno "Vraťte se!", pak se vraťte. A to bude pro vás čistší. A Bůh dobře ví o všem, co děláte.'),
(2820, 24, 29, 'A není pro vás hříchem, vstoupíte-li do domů neobydlených, v nichž máte nějaké předměty. A Bůh dobře zná to, co najevo dáváte, i to, co skrýváte.'),
(2821, 24, 30, 'Řekni věřícím, aby cudně klopili zrak a střežili svá pohlaví - a to je pro ně čistší, vždyť Bůh dobře je zpraven o všem, co konají.'),
(2822, 24, 31, 'A řekni věřícím ženám, aby cudně klopily zrak a střežily svá pohlaví a nedávaly na odiv své ozdoby kromě těch, jež jsou viditelné. A nechť spustí závoje své na ňadra svá. A nechť ukazují své ozdoby jedině svým manželům nebo otcům nebo tchánům nebo synům nebo synům svých manželů nebo bratřím nebo synům svých bratří či sester anebo jejich ženám anebo těm, jimž vládne jejich pravice, nebo služebníkům, kteří nemají chtíče, anebo chlapcům, kteří nemají pojem o nahotě žen. A nechť nedupou nohama, aby lidé postřehli ozdoby, které skrývají. A konejte všichni pokání před Bohem, ó věřící, snad budete blažení!'),
(2823, 24, 32, 'Umožňujte sňatek svobodným, žijícím mezi vámi, a rovněž bezúhonným z vašich otroků a otrokyň. A jsou-li potřební, Bůh je obohatí z laskavosti Své a Bůh je velkorysý, vševědoucí.'),
(2824, 24, 33, 'A nechť ti, kdož nenalézají možnost sňatku, jsou zdrženliví, pokud je Bůh neobohatí z laskavosti Své. -S těmi z otroků vašich, kteří usilují o písemnou dohodu o propuštění, uzavřete tuto dohodu, jestliže o nich víte, že je v nich dobré, a podarujte je z majetku Božího, který vám dal! - A nenuťte své mladé otrokyně, které chtějí žít jako muhsany, k nemravnosti, toužíce po statcích tohoto života pozemského. A ten, kdo je k tomu nutí, (bude potrestán); a Bůh bude potom, co k tomu byly donuceny, odpouštějící, slitovný.'),
(2825, 24, 34, 'A seslali jsme vám nyní znamení zřetelné a příklady těch, kdož odešli již před vámi, a napomenutí pro bohabojné.'),
(2826, 24, 35, 'A Bůh je světlem nebes i země. Světlo Jeho podobá se výklenku, v němž hoří lampa, a lampa je v nádobě skleněné. A nádoba skleněná se podobá hvězdě zářící; a je zapalována ze stromu požehnaného, stromu olivového, jenž není ani východní, ani západní, a olej jeho lehko vzplane, i když se ho nedotkne oheň žádný. A je to světlo na světle! A Bůh vede k světlu Svému, koho chce, a Bůh uvádí lidem podobenství různá. Bůh vševědoucí je o věci každé.'),
(2827, 24, 36, 'V domech, jež Bůh dovolil postavit a v nichž se vzpomíná jména Jeho, slaví Ho ráno i večer'),
(2828, 24, 37, 'muži, které ani obchod, ani prodej nezláká od vzýváni Boha, od dodržováni modlitby a od dávání almužen, a ti obávají se dne, kdy srdce i zraky se budou obracet,'),
(2829, 24, 38, 'aby je Bůh odměnil za to, co vykonali dobrého, a aby to rozmnožil z laskavosti Své. A Bůh uštědřuje, komu chce, aniž počítá.'),
(2830, 24, 39, 'Skutky těch, kdož neuvěřili, jsou jako přelud na poušti: žíznivý jej pokládá za vodu, avšak když k němu přijde, shledá, že je to nicota; a najde tam Boha, který splatí mu plně účet jeho. A Bůh rychlý je v účtování.'),
(2831, 24, 40, '... Anebo jsou jako temnota nad hlubinou moře: pokrývá je vlna, za níž jde vlna jiná, a nad nimi jest mračno - temnota, nad níž je další temnota. Když člověk vztáhne ruku svou, málem ji nevidí. A komu Bůh neučinil světlo, ten nemá ani část světla.'),
(2832, 24, 41, 'Což jsi neviděl, že Boha slaví všichni, kdož na nebesích jsou i na zemi, a rovněž ptáci, v řadách letící? A vše zná modlitbu i volání slávy Jeho. A Bůh dobře ví o všem, co dělají.'),
(2833, 24, 42, 'Bohu náleží království na nebesích i na zemi a u Boha je cíl konečný.'),
(2834, 24, 43, 'Což jsi neviděl, jak Bůh žene oblaka, potom je spojuje a posléze z nich učiní shluk? A pak vidíš z jeho středu vycházet déšť. A On sesílá z nebe jakoby hory, v nichž jsou kroupy, a zasahuje jimi, koho chce, a odvrací je, od koho chce. A zablesknutí málem zbaví lidi zraku.'),
(2835, 24, 44, 'A Bůh působí střídání noci a dne - a věru je v tom poučení pro lid prozíravý.'),
(2836, 24, 45, 'Bůh stvořil každého živočicha z vody; a jsou mezi nimi ti, kteří plazí se po břiše, jiní, kteří chodí po dvou nohou, a další, kteří chodí po čtyřech. Bůh tvoří, co chce, a Bůh věru všemocný je nad věcí každou.'),
(2837, 24, 46, 'A seslali jsme nyní znamení zřetelná; a Bůh vede, koho chce, ke stezce přímé.'),
(2838, 24, 47, 'A říkají: "Uvěřili jsme v Boha a posla Jeho a jsme poslušní!" Potom se však skupina z nich obrátí zády a takoví nejsou věřící!'),
(2839, 24, 48, 'A když jsou povoláni před Boha a posla Jeho, aby mezi nimi rozsoudil, hle, skupina z nich se zády obrací.'),
(2840, 24, 49, 'Kdyby však byli v právu, pak by k němu věru přišli poslušně.'),
(2841, 24, 50, 'Je v srdcích jejich choroba, či pochybují, anebo snad se bojí, že Bůh a posel Jeho jim ukřivdí? Nikoliv, oni sami sobě křivdu činí.'),
(2842, 24, 51, 'Vždyť řečí věřících, když zavoláni jsou před Boha a posla Jeho, aby mezi nimi rozsoudil, budiž: "Slyšeli jsme a uposlechli jsme." Takoví budou pak blaženi.'),
(2843, 24, 52, 'A ti, kdož poslouchají Boha a posla Jeho a obávají se Boha a jsou bohabojní, ti zajisté budou úspěšní.'),
(2844, 24, 53, 'A přisahali při Bohu přísahou nejsilnější, že nařídíš-li jim, jistě do boje vytáhnou. Rci: "Nepřísahejte! Poslušnost budiž zvyklostí uznanou, vždyť Bůh dobře zpraven je o všem, co děláte!"'),
(2845, 24, 54, 'Rci: "Poslouchejte Boha a poslouchejte posla! Odvrátíte-li se, pak on ponese jedině to, co na něj bylo vloženo, a vy ponesete to, co bylo na vás vloženo. Však uposlechnete-li jej, budete na cestě správné." A poslu přísluší jen hlásání zřetelné.'),
(2846, 24, 55, 'Bůh přislíbil těm z vás, kdož uvěřili a zbožné skutky konali, že z nich učiní nástupce na zemi, tak jako již učinil nástupci ty, kdož byli před nimi. A přislíbil jim, že upevní pro ně náboženství jejich, které se mu zlíbilo jim dát, a že nahradí posléze obavy jejich jistotou. "Uctívejte Mne a nepřidružujte ke Mně nic! "A ti, kdo ještě po tomto budou nevěřící, jsou věru hanebníci!'),
(2847, 24, 56, 'Dodržujte modlitbu, dávejte almužnu a poslouchejte posla - snad se vám dostane slitování!'),
(2848, 24, 57, 'A nedomnívejte se, že nevěřícím se podaří uniknout zásahu Božímu na zemi! Jich příbytkem bude oheň pekelný, a jak hnusný je to cíl konečný!'),
(2849, 24, 58, 'Vy, kteří věříte! Nechť žádají vás o dovolení vstoupit otroci vaši a ti, kdož nedosáhli ještě mužnosti, třikrát: před ranní modlitbou, a když odkládáte oděvy své okolo poledne, a po modlitbě večerní - a to jsou tři chvíle, kdy jste nazí. A není hříchem ani pro vás, ani pro ně mimo tyto doby, jestliže se vzájemně navštěvujete. A takto vám Bůh objasňuje znamení a Bůh vševědoucí je i moudrý.'),
(2850, 24, 59, 'A když děti vaše dosáhnou mužnosti, nechť žádají o dovolení vstoupit, tak jako to žádali ti, kdož byli před nimi. A takto vám Bůh objasňuje znamení Svá a Bůh je vševědoucí, moudrý.'),
(2851, 24, 60, 'A není hříchem pro starší ženy, které již nemají naději na sňatek, jestliže odloží své oděvy, aniž ovšem staví na odiv ozdoby své. Však zdrží-li se toho, bude to pro ně vhodnější. A Bůh je slyšící, vševědoucí.'),
(2852, 24, 61, 'A není hříchem pro slepého ani pro chromého ani pro nemocného - pro vás samy, abyste se vzájemně hostili v domech svých, v domech otců svých, v domech matek svých, v domech bratrů svých, v domech sester svých, v domech strýců svých z otcovy strany, v domech tet svých z otcovy strany, v domech strýců svých z matčiny strany, v domech tet svých z matčiny strany nebo v domech, od nichž máte klíče, anebo v domě svého přítele. A není pro vás hříchem, jíte-li společně nebo odděleně. Vstupujete-li do domu, dejte si vzájemně požehnané a dobré pozdravení přicházející od Boha. A takto vám Bůh objasňuje znamení - snad to pochopíte.'),
(2853, 24, 62, 'Věřícími jsou pouze ti, kdož uvěřili v Boha a v posla Jeho a kteří, když jsou s ním pospolu v nějaké záležitosti, neodcházejí, pokud jej nepožádali o dovolení. A ti, kdož tě žádají o dovolení odejít či vejít, jsou věru ti, kdož skutečně věří v Boha a posla Jeho. A když tě požádají o dovolení vstoupit k tobě kvůli nějaké své záležitosti, dovol to, komu chceš, a popros Boha o odpuštění pro ně, neboť Bůh je odpouštějící, slitovný.'),
(2854, 24, 63, 'Neberte výzvu posla k vám tak, jako by to byla výzva jednoho z vás k druhému! Bůh zná dobře ty z vás, kteří se potajmu okrádají. Nechť se mají na pozoru ti, kdož se vzpouzejí Jeho rozkazu, aby je nepostihla zkouška či trest bolestný.'),
(2855, 24, 64, 'Což nepatří Bohu vše, co na nebesích je i na zemi? On již dobře ví, jak je to s vámi; v den, kdy lidé budou k Němu navráceni, On jim oznámí, co vlastně dělali. A Bůh vševědoucí je o každé věci.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Požehnán budiž ten, jenž seslal spásné zjevení služebníku Svému, aby lidstvu varovatelem byl,'),
(2857, 25, 2, 'ten, jehož je království na nebesích i na zemi a jenž si nevzal pro sebe dítě žádné a společníka žádného ve vládě nemá; a On všechny věci stvořil a každé její osud určil.'),
(2858, 25, 3, 'A přibrali si vedle Něho božstva, která stvořit nic nemohou, leč sama stvořena jsou, která sobě žádnou škodu či užitek způsobit nemohou a ani smrtí ani životem ani vzkříšením nevládnou.'),
(2859, 25, 4, 'A říkají ti, kdož nevěří: "Toto není leč věc padělaná, kterou sám si vymyslil, a jiní mu v tom pomáhali!" A již tím se dopustili křivdy a klamu!'),
(2860, 25, 5, 'A hovoří dále: "To jsou povídačky starých, které si zapsal a jež ráno i večer jsou diktovány jemu!"'),
(2861, 25, 6, 'Odpověz: "Seslal je ten, jenž tajemství zná nebes i země. A On věru odpouštějící je i slitovný."'),
(2862, 25, 7, 'A říkají dále: "Co je to za posla Božího, jenž pokrmy pojídá a po trzích chodí. Kdyby mu byl tak poslán anděl nějaký, aby spolu s ním byl varovatelem,'),
(2863, 25, 8, 'anebo kdyby mu byl shozen poklad či měl nějakou zahradu, z níž by se živil!" A dodávají nespravedliví: "Vždyť jdete jen za očarovaným člověkem!"'),
(2864, 25, 9, 'Pohleď, jaká podobenství ti uvádějí! Bloudí však oni a nejsou schopni správnou cestu najít.'),
(2865, 25, 10, 'Požehnán budiž ten, jenž kdyby chtěl, daroval by ti lepší než vše to - zahrady, pod nimiž řeky tekou, a mohl by ti i zámky postavit.'),
(2866, 25, 11, 'Ba ano, oni Hodinu za lež prohlašuji; však připravili jsme pro ty, kdož za lež Hodinu prohlašují, plamen šlehající;'),
(2867, 25, 12, 'a až spatří jej z místa vzdáleného, uslyší, jak je zuřící a ječící.'),
(2868, 25, 13, 'A až svázáni pospolu na místo úzké budou vhozeni, zvolají: "Přijď sem rychle, ó zničení!"'),
(2869, 25, 14, '"Nevolejte dnes po zničení jediném, ale volejte po mnohém zničení!"'),
(2870, 25, 15, 'Rci: "Je lepší tohle, či zahrada věčnosti, jež byla bohabojným slíbena a jež pro ně bude odměnou i konečným cílem?"'),
(2871, 25, 16, 'A tam, nesmrtelní, budou mít vše, co si jen přát budou a to slib je vyprošený na Pánu tvém.'),
(2872, 25, 17, 'V den, kdy shromáždí je i ty, které vzývali vedle Něho, řekne Bůh: "Jste to vy, kdož tyto Mé služebníky v blud uvedli, či oni sami z cesty zbloudili?"'),
(2873, 25, 18, 'Odvětí: "Sláva budiž Tobě! Nepříslušelo nám, abychom byli zváni místo Tebe jako ochránci, avšak Tys jim i otcům jich k užívání tolik bohatství dal, že na připomenutí zapomněli a lidmi ztracenými se stali."'),
(2874, 25, 19, 'A takto za lež prohlásí to, co říkáte, a nebudou schopni vám pomoci ani trest od vás odvrátit. Tomu, kdo z vás nespravedlivý byl, tomu trest veliký dáme okusit.'),
(2875, 25, 20, 'A neposlali jsme před tebou posly žádné, kteří by nebyli pokrmy pojídali a po trzích chodili. A učinili jsme jedny z vás pokušením pro jiné, abychom zjistili, zda neochvějní budete. A Pán tvůj vše bystře pozoruje.'),
(2876, 25, 21, 'A říkají ti, kdož nemají naději na setkání s Námi: "Kdyby k nám byli sesláni andělé či kdybychom uzřeli Pána svého, věřili bychom!" A zpyšněli v duších svých a povyšovali se velmi domýšlivě.'),
(2877, 25, 22, 'V den, kdy anděly spatří - a ten den nebude pro hříšníky radostnou zvěstí - zvolají: "Přehradu zahrazující postavte mezi nás!"'),
(2878, 25, 23, 'A My posoudíme skutky, které konali, a učiníme z nich prach rozptýlený.'),
(2879, 25, 24, 'V ten den se dostane obyvatelům ráje nejlepšího místa k přebývání a nejkrásnějšího místa k odpočinutí,'),
(2880, 25, 25, 'v den, kdy rozpoltí se nebe i s mračny a andělé sesláním budou sesláni,'),
(2881, 25, 26, 'v ten den bude mít Milosrdný neomezenou vládu; a těžký pro nevěřící to bude den.'),
(2882, 25, 27, 'V ten den si bude nevěřící ruce své hryzat a říkat: "Ó kéž bych se byl ubíral cestou stejnou s poslem!'),
(2883, 25, 28, 'Běda mi, kéž jsem toho a toho za přítele nebral,'),
(2884, 25, 29, 'vždyť on mne věru od připomenutí odvedl poté, co se mi ho dostalo; a satan člověka jen na holičkách nechal!"'),
(2885, 25, 30, 'I řekne posel: "Pane, lid můj pokládal tento Korán za něco, čemu se vyhnout je zapotřebí."'),
(2886, 25, 31, 'A takto jsme udělali každému proroku nepřítele z hříšníků vzešlého, leč Pán tvůj ti zcela jako vůdce i pomocník stačí.'),
(2887, 25, 32, 'A říkají ti, kdož nevěří: "Kdyby mu byl seslán Korán v jednom celku, uvěřili bychom!" A sesíláme jej takto, abychom tím posílili srdce tvé, a předčítáme jej s péčí největší.'),
(2888, 25, 33, 'A neuvedou ti nevěřící podobenství žádné, aniž bychom ti k němu přinesli odpověď pravdivou a výklad nejlepší.'),
(2889, 25, 34, 'Ti, kteří shromážděni budou s tvářemi k peklu obrácenými, ti místa nejhorší mít budou a nejdále z cesty sejdou.'),
(2890, 25, 35, 'A dali jsme již kdysi Mojžíšovi Písmo a učinili jsme bratra jeho Áróna poradcem jemu'),
(2891, 25, 36, 'a řekli jsme: "Odeberte se oba k lidu, jenž znamení Naše za lživá pokládá!" A vyhubili jsme je do samých základů!'),
(2892, 25, 37, 'A podobně jsme lid Noemův, jenž prohlásil posly za lháře, utopili a pro lidi jsme je znamením učinili; a připravili jsme pro nespravedlivé muka bolestná'),
(2893, 25, 38, 'a rovněž pro ´Ádovce Thamúdovce, obyvatele ar-Rassu a četná pokolení, jež mezi nimi byla.'),
(2894, 25, 39, 'A všem těmto jsme podobenství uváděli a všechny jsme zahladili až do úplného zničení.'),
(2895, 25, 40, 'A procházeli nevěřící kolem města, jež bylo zaplaveno deštěm zhoubným. Což nebyli s to je vidět? Ba věru, a přece nedoufají ve vzkříšení!'),
(2896, 25, 41, 'A když tě vidí, jen posměšky si z tebe tropí a říkají: "Je tohle ten, jehož Bůh jako posla vypravil?'),
(2897, 25, 42, 'Vždyť by nás byl málem odvedl od božstev našich, kdybychom nebyli v uctívání jich vytrvalí!" Však záhy se dozví, až trest spatří, kdo více se z cesty správné odchýlil.'),
(2898, 25, 43, 'Myslíš, že tomu, kdo si vzal jako své božstvo vášeň svou, jsi dozorcem,'),
(2899, 25, 44, 'anebo domníváš se, že většina z nich naslouchá či uvažuje rozumně? Vždyť oni jsou jen jak dobytčata ba ještě dále zbloudili z cesty správné.'),
(2900, 25, 45, 'Což nevidíš, jak Pán tvůj stín prodlužuje? Kdyby byl chtěl, věru by jej byl nehybným učinil; a pak jsme slunce ustanovili jemu vůdcem'),
(2901, 25, 46, 'a potom jej k sobě přitahujeme lehkým uchopením.'),
(2902, 25, 47, 'A On je ten, jenž pro vás noc rouchem učinil a spánek odpočinkem a den učinil vzbuzením.'),
(2903, 25, 48, 'A On je ten, jenž větry vysílá jako hlasatele milosrdenství Svého; a sesíláme z nebe vodu čistou,'),
(2904, 25, 49, 'abychom jí oživili zem mrtvou a napojili to, co jsme stvořili z dobytka a lidí, mírou hojnou.'),
(2905, 25, 50, 'A rozdělujeme ji mezi ně, aby se upamatovali, avšak většina lidí odmítá vše kromě nevěrectví.'),
(2906, 25, 51, 'Kdybychom chtěli, věru bychom byli vyslali varovatele do města každého.'),
(2907, 25, 52, 'Neposlouchej nevěřící, ale pusť se jím proti nim do boje úporného.'),
(2908, 25, 53, 'A On je ten, jenž volně dal téci oběma mořím: jedno sladké a pitné je, druhé slané a hořké. A mezi oběma umístil překážku a přehradu zahrazující.'),
(2909, 25, 54, 'A On je ten, jenž stvořil z vody smrtelníka a učinil mu přímé a přišvagřené příbuzenstvo. A Pán tvůj je všemohoucí.'),
(2910, 25, 55, 'Avšak oni uctívají místo Boha něco, co jim prospívat ani škodit nemůže; a nevěřící je proti Pánu svému (satanovým) pomocníkem.'),
(2911, 25, 56, 'A vyslali jsme tě pouze, abys byl hlasatelem zvěsti radostné a varovatelem.'),
(2912, 25, 57, 'Rci: "Nežádám od vás za to odměnu žádnou, leda aby ten, kdo chce, se ubíral k Pánu svému cestou správnou."'),
(2913, 25, 58, 'Spolehni se na Živého, jenž neumírá, a oslavuj chválu Jeho; On dostatečným je znalcem hříchů služebníků Svých.'),
(2914, 25, 59, 'Ten, jenž nebesa a zemi a vše, co je mezi nimi, v šesti dnech stvořil a pak na trůnu se pevně usadil - to je Milosrdný! A zeptej se na Něj dobře zpravených!'),
(2915, 25, 60, 'A když se jim řekne: "Padněte na zem před Milosrdným!", odpovídají: "A co je to Milosrdný? Máme padat na zem jen proto, že ty nám to poroučíš?" A to vše jen rozmnožuje odpor jejich.'),
(2916, 25, 61, 'Požehnán budiž ten, jenž na nebi znamení zvěrokruhu stvořil a svítilnu tam umístil i měsíc osvětlený.'),
(2917, 25, 62, 'A On je ten, jenž stanovil střídání noci i dne jako znamení pro toho, kdo chce si Boha připomenout, a toho, kdo chce být vděčný.'),
(2918, 25, 63, 'Služebníci Milosrdného jsou ti, kdož pokorně chodí po zemi, a když jsou osloveni lidmi pošetilými, odpovídají: "Mír s vámi!" -'),
(2919, 25, 64, 'ti, kdož za noci bdí kvůli Pánu svému padajíce na zem i stojíce'),
(2920, 25, 65, 'a kdož hovoří: "Pane náš, odvrať od nás trest pekla, vždyť jeho trest je trýznivý!"'),
(2921, 25, 66, '- a ono je útočištěm i místem pobytu věru špatným -'),
(2922, 25, 67, 'a ti, kdož při vydáních svých ani nerozhazují, ani neskrblí, leč na střední cestě mezi tím stojí,'),
(2923, 25, 68, 'a kdož nevzývají spolu s Bohem žádná božstva jiná a nezabíjejí duši žádnou, jejíž zabití Bůh zakázal - leda podle práva, a nesmilní. A kdo toto činí, ten hříchu se dopouští,'),
(2924, 25, 69, 'za nějž mu trest zdvojnásoben bude v den zmrtvýchvstání a zůstane v něm věčně ponížený'),
(2925, 25, 70, 'kromě těch, kdož pokání činili, uvěřili a zbožné skutky konali. A takovým Bůh špatné skutky jejich za dobré vymění, vždyť Bůh odpouštějící je a slitovný.'),
(2926, 25, 71, 'A kdokoliv se kál a zbožné skutky konal, ten věru se k Bohu navrátí kajícně.'),
(2927, 25, 72, 'A ti, kdož křivě nesvědčí, a když kolem řečí hnusných jdou, míjejí je důstojně,'),
(2928, 25, 73, 'a ti, kdož k připomenutí znamení Pána svého nezůstávají lhostejní, jako by byli hluší a slepí,'),
(2929, 25, 74, 'a ti, kdož říkají: "Pane náš, dej nám v manželkách našich i potomcích našich oka ochlazení a učiň nás vzorem bohabojným,"'),
(2930, 25, 75, 'takoví odměněni budou komnatami rajskými, neboť trpěliví byli a setkají se tam s pozdravem i přivítáním'),
(2931, 25, 76, 'a budou tam nesmrtelní. A jak krásné to bude místo přebývání a umístění.'),
(2932, 25, 77, 'Rci: "Pán můj se o vás nepostará, nebudete-li jej vzývat; vy však jste již znamení za lež prohlásili a záhy vás postihne trest nevyhnutelný."');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Tá sín mím.'),
(2934, 26, 2, 'Toto jsou Písma zřetelného znamení.'),
(2935, 26, 3, 'Možná že duše tvá se rmoutí proto, že uvěřit nechtějí,'),
(2936, 26, 4, 'však kdybychom chtěli, seslali bychom jim z nebes znamení, před nímž jejich šíje by zůstaly zkrouceny v ponížení.'),
(2937, 26, 5, 'A kdykoliv se jim dostalo od Milosrdného nového připomenutí, vždy se jen od něho odvrátili'),
(2938, 26, 6, 'a za lež je prohlásili; však dostane se jim zvěsti o tom, čemu se posmívali!'),
(2939, 26, 7, 'Což po zemi se nerozhlédli a neviděli, kolika druhům rostlin užitečných jsme na ní vyrůst dali?'),
(2940, 26, 8, 'Věru je v tomto znamení, však většina z nich nevěří.'),
(2941, 26, 9, 'A Pán tvůj dozajista je mocný a slitovný.'),
(2942, 26, 10, 'A hle, Pán tvůj Mojžíše zavolal: "Jdi k lidu, jenž křivdu činí,'),
(2943, 26, 11, 'k lidu Faraónovu; což nebudou bohabojní?"'),
(2944, 26, 12, 'I odpověděl: "Pane, obávám se, že mne ze lži obviní'),
(2945, 26, 13, 'a že hruď má stísněna bude a jazyk můj se nerozváže. Pošli pro Áróna raději!'),
(2946, 26, 14, 'A oni mají proti mně obvinění a bojím se, že mě zabijí."'),
(2947, 26, 15, 'I řekl Bůh: "Nikoliv, půjdete oba s Našimi znameními a My, naslouchajíce, budeme s vámi.'),
(2948, 26, 16, 'K Faraónovi jděte a řekněte: My poslové jsme věru Pána lidstva veškerého,'),
(2949, 26, 17, 'odešli dítka Izraele s námi!"'),
(2950, 26, 18, 'I pravil Faraón: "Což jsme tě jako dítě mezi sebou nevychovali a nezůstals u nás mnohá léta věku svého?'),
(2951, 26, 19, 'A přesto jsi provedl skutek svůj, jejž jsi učinil; tys věru jeden z nevděčných!"'),
(2952, 26, 20, 'Mojžíš odpověděl: "Spáchal jsem jej v době, kdy byl jsem ještě jedním z bloudících,'),
(2953, 26, 21, 'a uprchl jsem před vámi, neb jsem se vás bál. Pán můj mi pak moudrost daroval a jedním ze svých poslů mě učinil.'),
(2954, 26, 22, 'A dobrodiní, jež mi předhazuješ, je v tom snad, žes dítka Izraele zotročil?"'),
(2955, 26, 23, 'I otázal se Faraón: "A kdo je ten Pán lidstva?"'),
(2956, 26, 24, 'Odvětil: "Pán nebes i země a všeho, co mezi nimi je - uvěřit-li dovedete!"'),
(2957, 26, 25, 'I pravil Faraón k okolostojícím: "Zdaž slyšíte?"'),
(2958, 26, 26, 'Řekl Mojžíš: "Pánem vaším je i vašich dávných předků."'),
(2959, 26, 27, 'Pravil: "Váš posel, jenž vyslán byl k vám, je bláznem vskutku!"'),
(2960, 26, 28, 'A Mojžíš pokračoval: "Je Pánem východu i západu a toho, co mezi nimi je - můžete to pochopit?"'),
(2961, 26, 29, 'I zvolal Faraón: "Uznáš-li jiné božstvo než mne, do vězení tě dám věru hodit!"'),
(2962, 26, 30, 'I otázal se Mojžíš: "A což když ti předvedu důkaz zřetelný?"'),
(2963, 26, 31, 'Faraón odpověděl: "Předveď jej tedy, jsi-li pravdomluvný!"'),
(2964, 26, 32, 'I hodil tedy hůl svou, a hle, hadem zjevným se stala'),
(2965, 26, 33, 'a vyňal ruku svou, a hle, bílou přihlížejícím se zdála!'),
(2966, 26, 34, 'I řekl Faraón k velmožům svým, kol něho stojícím: "Tohle věru je kouzelník obratný,'),
(2967, 26, 35, 'jenž kouzly svými vás chce z vaší země vyhnat. Co rozhodnete?"'),
(2968, 26, 36, 'I pravili: "Odkaž jej i bratra jeho na později a do všech měst shromažďovatele vyšli,'),
(2969, 26, 37, 'by k tobě se všemi kouzelníky obratnými přišli!"'),
(2970, 26, 38, 'A shromážděni byli kouzelníci v den pro schůzku stanovený'),
(2971, 26, 39, 'a lidé byli dotázáni: "Chcete se též účastnit shromáždění?'),
(2972, 26, 40, 'Snad i my kouzelníky, budou-li vítězní, následovat budeme."'),
(2973, 26, 41, 'Když kouzelníci se dostavili, řekli Faraónovi: "A dostaneme odměnu, jestliže vítězství dosáhneme?"'),
(2974, 26, 42, 'A odpověděl Faraón: "Zajisté, a tehdy se věru mými přiblíženci stanete."'),
(2975, 26, 43, 'I pravil jim Mojžíš: "Hoďte, co hodit chcete!"'),
(2976, 26, 44, 'A hodili provazy své a hole své, říkajíce: "Při moci Faraónově, vítězství máme zaručené!"'),
(2977, 26, 45, 'Pak hodil Mojžíš hůl svou, a hle, pozřela jejich kousky podvodné.'),
(2978, 26, 46, 'Na zem kouzelníci klaníce se padli,'),
(2979, 26, 47, 'volajíce: "V Pána lidstva jsme uvěřili,'),
(2980, 26, 48, 'v Pána Mojžíšova a Árónova."'),
(2981, 26, 49, 'Faraón vykřikl: "Jak to, že jste v něj uvěřili, dříve než jsem vám dovolil? On věru vaším mistrem je, jenž kouzlům vás naučil. Však záhy dám vám poznat! Dám vaše pravé ruce a levé nohy usekat a všechny vás dám ukřižovat!"'),
(2982, 26, 50, 'Odpověděli: "Nic nevadí, vždyť my se přece k Pánu svému navrátíme,'),
(2983, 26, 51, 'a toužíme jedině, aby nám Pán náš odpustil viny naše, vždyť my k prvním věřícím patříme!"'),
(2984, 26, 52, 'A vnukli jsme Mojžíšovi: "Odejdi v noci se služebníky Mými, neb věru budete pronásledováni."'),
(2985, 26, 53, 'Faraón pak vyslal svolavatele v města svá,'),
(2986, 26, 54, 'aby řekli: "Tamti jsou jen skupina nepočetná'),
(2987, 26, 55, 'a na nás vskutku jsou rozhněváni,'),
(2988, 26, 56, 'však my množství jsme věru, jež má se na pozoru!"'),
(2989, 26, 57, 'A odvedli jsme Faraóna a lid jeho ze zahrad a od pramenů,'),
(2990, 26, 58, 'od pokladnic a ze vzácných příbytků;'),
(2991, 26, 59, 'a tak stalo se a učinili jsme dítka Izraele toho všeho dědici'),
(2992, 26, 60, 'a pronásledovalo je vojsko Faraóna, k východu pochodující.'),
(2993, 26, 61, 'Když obě skupiny se spatřily, druhové Mojžíšovi zvolali: "Teď vskutku dostiženi jsme!"'),
(2994, 26, 62, 'I pravil Mojžíš: "Však nikoliv, vždyť Pán můj je se mnou a On povede mne!"'),
(2995, 26, 63, 'A vnukli jsme Mojžíšovi: "Udeř holí svou do moře!" A rozestoupilo se a část každá podobnou se stala obrovské hoře.'),
(2996, 26, 64, 'A dali jsme jim postoupit a pak i těm druhým,'),
(2997, 26, 65, 'a zachránili jsme Mojžíše a všechny, kdož byli s ním,'),
(2998, 26, 66, 'a pak jsme zničili ty druhé utopením.'),
(2999, 26, 67, 'Věru je v tomto znamení; však většina z nich nevěří.'),
(3000, 26, 68, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3001, 26, 69, 'Vyprávěj jim zvěst o Abrahamovi,'),
(3002, 26, 70, 'když pravil otci svému a lidu svému: "Co to uctíváte?"'),
(3003, 26, 71, 'Odpověděli: "Modly uctíváme a v oddanosti k nim setrváváme."'),
(3004, 26, 72, 'I otázal se jich: "A slyší vás, když je vzýváte?'),
(3005, 26, 73, 'Anebo jsou vám k užitku či ke škodě?"'),
(3006, 26, 74, 'Odvětili: "To nikoliv, však shledali jsme, že tak činili již otcové naši."'),
(3007, 26, 75, 'A pravil Abraham: "Uvažovali jste vůbec o tom, co uctíváte'),
(3008, 26, 76, 'vy i dávní předkové vaši?'),
(3009, 26, 77, 'To jsou nepřátelé moji a není Boha kromě Pána lidstva,'),
(3010, 26, 78, 'jenž stvořil mne a na správnou cestu mne uvádí'),
(3011, 26, 79, 'a krmí mne a napájí,'),
(3012, 26, 80, 'a když nemocen jsem, mne uzdraví'),
(3013, 26, 81, 'a zemřít mi dá a pak mě oživí,'),
(3014, 26, 82, 'a přeji si jen, aby mi viny mé odpustil v soudu den poslední."'),
(3015, 26, 83, 'Pane, daruj mi moudrost a připoj mě k bezúhonným'),
(3016, 26, 84, 'a učiň mě pro pokolení budoucí jazykem pravdomluvným'),
(3017, 26, 85, 'a dej, ať jsem jedním z dědiců zahrady slastí,'),
(3018, 26, 86, 'a odpusť otci mému, že patřil mezi bloudící,'),
(3019, 26, 87, 'a nezahanbi mě v den, kdy vzkříšeni budou,'),
(3020, 26, 88, 'v den, kdy nikomu ani majetek, ani synové neprospějí -'),
(3021, 26, 89, 'kromě těch, kdož se srdcem upřímným k Bohu přijdou,'),
(3022, 26, 90, 'v den, kdy ráj se k bohabojným přiblíží'),
(3023, 26, 91, 'a peklo bude předvedeno těm, kdo sváděli,'),
(3024, 26, 92, 'a bude jim řečeno:, Kde je to, co jste uctívali'),
(3025, 26, 93, 'kromě Boha. Mohou vám anebo sami sobě pomoci??'),
(3026, 26, 94, 'A budou do pekla svrženi oni i ti bloudící'),
(3027, 26, 95, 'a všechna vojska Iblísova.'),
(3028, 26, 96, 'A řeknou tam mezi sebou se hádajíce:'),
(3029, 26, 97, ', Při Bohu, věru jsme ve zjevném bludu byli,'),
(3030, 26, 98, 'když naroveň Pána lidstva jsme vás povýšili.'),
(3031, 26, 99, 'A svedli nás z cesty jedině hříšníci,'),
(3032, 26, 100, 'a nemáme přímluvce žádného'),
(3033, 26, 101, 'ani přítele věrného,'),
(3034, 26, 102, 'kéž bychom se mohli vrátit, abychom byli věřící!`"'),
(3035, 26, 103, 'Věru je v tomto znamení, však většina z nich nevěří.'),
(3036, 26, 104, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3037, 26, 105, 'A také lid Noemův posly za lháře prohlásil,'),
(3038, 26, 106, 'když bratr jejich Noe jim říkal: "Což nebudete bohabojní?'),
(3039, 26, 107, 'Vždyť já posel spolehlivý jsem, k vám vyslaný,'),
(3040, 26, 108, 'Boha se bojte a mne poslouchejte!'),
(3041, 26, 109, 'Nežádám na vás odměnu žádnou, vždyť odměnit mne Pánu lidstva přísluší.'),
(3042, 26, 110, 'Boha se bojte a mne poslouchejte!"'),
(3043, 26, 111, 'I řekli: "Jak máme ti věřit, když následují tě jen ti nejnižší?"'),
(3044, 26, 112, 'A Noe odvětil: "Nemám o tom, co dělali, žádné vědění,'),
(3045, 26, 113, 'a účtování s nimi jedině Pánu mému přísluší máte-li o tom tušení!'),
(3046, 26, 114, 'Já nejsem z těch, kdo věřící odhání,'),
(3047, 26, 115, 'já jsem jen varovatel zjevný!"'),
(3048, 26, 116, 'A zvolali: "Jestliže nepřestaneš, Noe, doopravdy ukamenován budeš!"'),
(3049, 26, 117, 'Řekl: "Pane, lidé moji mě za lháře prohlásili,'),
(3050, 26, 118, 'rozhodni tedy mezi mnou a jimi rozhodnutím zřetelným a zachraň mne i ty, kdož se mnou uvěřili!"'),
(3051, 26, 119, 'A zachránili jsme jej i ty, kdo byli s nim, v arše s nákladem plným'),
(3052, 26, 120, 'a pak jsme utopili ty, kteří zbyli.'),
(3053, 26, 121, 'Věru je v tomto znamení, však většina z nich nevěří.'),
(3054, 26, 122, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3055, 26, 123, 'Rovněž ´Ádovci posly za lháře prohlásili,'),
(3056, 26, 124, 'když bratr jejich Húd jim říkal: "Což nebudete bohabojní?'),
(3057, 26, 125, 'Vždyť já posel spolehlivý jsem, k vám vyslaný,'),
(3058, 26, 126, 'Boha se bojte a mne poslouchejte!'),
(3059, 26, 127, 'Nežádám na vás odměnu žádnou, vždyť odměnit mne Pánu lidstva přísluší.'),
(3060, 26, 128, 'Chcete stále stavět na každé výšině znamení jen tak pro pobavení'),
(3061, 26, 129, 'a chcete stále zakládat stavby - hodláte snad být nesmrtelní?'),
(3062, 26, 130, 'A když násilí činíte, jste jak tyrani násilní!'),
(3063, 26, 131, 'Boha se bojte a mne poslouchejte!'),
(3064, 26, 132, 'A bojte se toho, jenž rozmnožil to, co znáte,'),
(3065, 26, 133, 'a vaše stáda vám rozhojnil a vaše syny'),
(3066, 26, 134, 'a také zahrady a prameny!'),
(3067, 26, 135, 'Já pro vás obávám se trestu dne nesmírného!"'),
(3068, 26, 136, 'Odpověděli: "Je nám jedno, zda varuješ nás či zda nejsi varovatelem;'),
(3069, 26, 137, 'vždyť toto je způsob starých -nic jiného -'),
(3070, 26, 138, 'a my potrestáni nebudem!"'),
(3071, 26, 139, 'A za lháře ho prohlásili a my jsme je zahubili. Věru je v tomto znamení, však většina z nich nevěří.'),
(3072, 26, 140, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3073, 26, 141, 'A také Thamúdovci posly za lháře prohlásili,'),
(3074, 26, 142, 'když bratr jejich Sálih jim říkal: "Což nebudete bohabojní?'),
(3075, 26, 143, 'Vždyť já posel spolehlivý jsem, k vám vyslaný,'),
(3076, 26, 144, 'Boha se bojte a mne poslouchejte!'),
(3077, 26, 145, 'Nežádám na vás odměnu žádnou, vždyť odměnit mne Pánu lidstva přísluší.'),
(3078, 26, 146, 'Myslíte, že ponecháni budete v tom, co zde máte, v bezpečí'),
(3079, 26, 147, 'mezi prameny a zahradami,'),
(3080, 26, 148, 'polnostmi a s visícími trsy palmami'),
(3081, 26, 149, 'a že nadále budete vytesávat obydlí v horách domýšlivě?'),
(3082, 26, 150, 'Boha se bojte a mne poslouchejte!'),
(3083, 26, 151, 'Neposlouchejte přestupníků nařízení,'),
(3084, 26, 152, 'kteří na zemi šíří pohoršení a o nápravu se nestarají!"'),
(3085, 26, 153, 'Odpověděli: "Ty věru jsi jen jeden z očarovaných'),
(3086, 26, 154, 'a nejsi leč smrtelník nám podobný. Předveď nám nějaké znamení, jsi-li z pravdomluvných!"'),
(3087, 26, 155, 'I řekl: "Hle, zde je velbloudice, má právo pít v jednom dni a vy máte právo také pít v den určený.'),
(3088, 26, 156, 'Nečiňte jí však nic zlého, ať nestihne vás trest dne nesmírného!"'),
(3089, 26, 157, 'Přesto však žíly jí přeřezali a již nazítří toho věru litovali,'),
(3090, 26, 158, 'neb zachvátil je trest. Věru je v tomto znamení, však většina z nich nevěří.'),
(3091, 26, 159, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3092, 26, 160, 'Též lid Lotův posly za lháře prohlásil,'),
(3093, 26, 161, 'když bratr jejich Lot jim říkal: "Což nebudete bohabojní?'),
(3094, 26, 162, 'Vždyť já posel spolehlivý jsem, k vám vyslaný.'),
(3095, 26, 163, 'Boha se bojte a mne poslouchejte!'),
(3096, 26, 164, 'Nežádám na vás odměnu žádnou, vždyť odměnit mne Pánu lidstva přísluší.'),
(3097, 26, 165, 'Což se chcete jen k mužům z lidstva všeho přibližovat'),
(3098, 26, 166, 'a manželky své, jež pro vás Pán váš stvořil, zanedbávat? Ba, vy věru jste lid přestupníků!"'),
(3099, 26, 167, 'A zvolali: "Jestliže nepřestaneš, Lote, vyhnán budeš vskutku!"'),
(3100, 26, 168, 'I řekl: "Mně se opravdu hnusí vaše počínání.'),
(3101, 26, 169, 'Pane, zachraň mne i mou rodinu před následky toho, co činí!"'),
(3102, 26, 170, 'A jeho i rodinu jeho celou jsme zachránili'),
(3103, 26, 171, 'kromě stařeny jedné, jež byla mezi opozdilými,'),
(3104, 26, 172, 'a potom všechny ostatní jsme zničili'),
(3105, 26, 173, 'a déšť jsme na ně spustili. Jak hnusný to byl déšť pro ty, jež marně jsme varovali!'),
(3106, 26, 174, 'A věru je v tomto znamení, však většina z nich nevěří.'),
(3107, 26, 175, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3108, 26, 176, 'A také lidé Houštin posly za lháře prohlásili,'),
(3109, 26, 177, 'když jim Šu´ajb říkal: "Což nebudete bohabojní?'),
(3110, 26, 178, 'Vždyť já posel spolehlivý jsem, k vám vyslaný,'),
(3111, 26, 179, 'Boha se bojte a mne poslouchejte!'),
(3112, 26, 180, 'Nežádám na vás odměnu žádnou, vždyť odměnit mne Pánu lidstva přísluší.'),
(3113, 26, 181, 'Dávejte míry spravedlivé a nebuďte mezi podvádějícími'),
(3114, 26, 182, 'a važte vahami vyrovnanými'),
(3115, 26, 183, 'a neubírejte lidem z majetku jejich a nebuďte těmi, kdož na zemi pohoršení rozšiřují!'),
(3116, 26, 184, 'Bojte se toho, kdo stvořil vás i pokolení dřívější!"'),
(3117, 26, 185, 'Odpověděli: "Tys věru jen očarovaný'),
(3118, 26, 186, 'a nejsi leč smrtelník nám podobný, a soudíme věru, žes prolhaný.'),
(3119, 26, 187, 'Nech tedy na nás spadnout nebe kus, jsi-li pravdomluvný!"'),
(3120, 26, 188, 'I řekl: "Pán můj o tom, co děláte, dobře ví."'),
(3121, 26, 189, 'Avšak oni za lháře jej prohlásili a uchvátil je trest dne zamračeného a byl to trest dne nesmírného.'),
(3122, 26, 190, 'A věru je v tomto znamení, však většina z nich nevěří.'),
(3123, 26, 191, 'A Pán tvůj dozajista je mocný a slitovný.'),
(3124, 26, 192, '... A toto je vskutku Pána lidstva seslání,'),
(3125, 26, 193, 'a sestoupil duch spolehlivý s ním'),
(3126, 26, 194, 'na srdce tvé, bys varovatelem byl jedním.'),
(3127, 26, 195, 'A je ve zřetelném jazyce arabském sesláno'),
(3128, 26, 196, 'a je věru v Písmech starých obsaženo.'),
(3129, 26, 197, 'Což není pro ně znamením, že znají je dítek Izraele učenci?'),
(3130, 26, 198, 'A i kdybychom je byli seslali některému cizinci'),
(3131, 26, 199, 'a byl by jim je přednášel, ani tehdy by nebyli věřící.'),
(3132, 26, 200, 'A takto jím pronikáme do srdcí hříšníků,'),
(3133, 26, 201, 'však oni v toto zjevení neuvěří, pokud trest bolestný neuzří,'),
(3134, 26, 202, 'jenž přijde k nim, aniž co tušit budou, znenadání.'),
(3135, 26, 203, 'A tehdy zvolají: "Což nebude nám dán odklad žádný?"'),
(3136, 26, 204, 'Budou si pak ještě přát trestu Našeho uspíšení?'),
(3137, 26, 205, 'Což nevidíš, že dáváme-li jim ještě několik let tohoto světa užívání,'),
(3138, 26, 206, 'že potom dostaví se to, co jim jsme slibovali,'),
(3139, 26, 207, 'a že nebude jim platno nic z toho, co si užívali?'),
(3140, 26, 208, 'Žádné město jsme nezahubili, aniž jsme mu varovatele dali'),
(3141, 26, 209, 'jako připomenutí, a věru jsme žádnou křivdu nespáchali.'),
(3142, 26, 210, 'A nesestoupili s Koránem žádní satani,'),
(3143, 26, 211, 'neb jim to nepřísluší a nejsou toho schopni ani,'),
(3144, 26, 212, 'vždyť zabráněno jim bylo u nebeského trůnu v naslouchání.'),
(3145, 26, 213, 'A spolu s Bohem žádné jiné božstvo nevzývej, abys nebyl z potrestaných,'),
(3146, 26, 214, 'a varuj nejbližší své z příbuzných'),
(3147, 26, 215, 'a rozprostři křídla svá nad těmi, kdož následují tě z věřících.'),
(3148, 26, 216, 'A jestliže tě neuposlechnou, tedy rci: ", Já nejsem za to, co činíte, odpovědný."'),
(3149, 26, 217, 'A na mocného a slitovného se spolehni,'),
(3150, 26, 218, 'který tě vidí, když k modlitbě se zvedáš'),
(3151, 26, 219, 'a jak mezi klanícími se chováš,'),
(3152, 26, 220, 'vždyť On slyšící je i vševědoucí.'),
(3153, 26, 221, 'Mám vám oznámit, na koho sestupují satani?'),
(3154, 26, 222, 'Sestupují na všechny pomlouvače hříšné'),
(3155, 26, 223, 'a sdělují jim slyšené, však lháři jsou to ve většině.'),
(3156, 26, 224, 'A také básníci jsou bloudícími následováni.'),
(3157, 26, 225, 'Což nevidíš, jak v každém údolí zmámeně pobíhají'),
(3158, 26, 226, 'a říkají to, co vůbec nekonají?'),
(3159, 26, 227, 'Kromě těch, kdo uvěřili a zbožné skutky konali a jména Božího hojně vzpomínali a jimž pomoci se dostalo poté, co jim ukřivděno bylo. A záhy se dozví ti, kdož křivdili, k jakému obratu osudu se obrátí!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Tá sín. Toto jsou verše Koránu a Písma zjevného,'),
(3161, 27, 2, 'vedení i zvěst radostná věřícím,'),
(3162, 27, 3, 'kteří dodržují modlitbu, dávají almužnu a jsou pevně přesvědčeni o životě budoucím.'),
(3163, 27, 4, 'Těm však, kdož v onen svět nevěří, jsme zkrášlili jejich konání, takže jen slepě tápají.'),
(3164, 27, 5, 'To jsou ti, jimž dostane se trestu nejhoršího a v životě budoucím ztrátu největší utrpí.'),
(3165, 27, 6, 'A ty zajisté Korán dostáváš od Pána moudrého, vševědoucího.'),
(3166, 27, 7, 'A hle, pravil Mojžíš rodině své: "Zpozoroval jsem oheň jakýsi, přinesu vám buď zprávu o něm, či oharek planoucí - snad budete se moci ohřát od něho."'),
(3167, 27, 8, 'Když přišel k ohni, ozvalo se: "Požehnán budiž ten, jenž v ohni tomto dlí, a ten, jenž okolo něho jest! Sláva Bohu, Pánu lidstva veškerého!'),
(3168, 27, 9, 'Mojžíši, věru jsem to Já, Bůh mocný a moudrý.'),
(3169, 27, 10, 'Hoď svou hůl!" Když Mojžíš viděl, jak hůl se svíjí, jako by hadem byla, otočil se zády, však krok učinit nemohl. "Mojžíši, neboj se, poslové Boží nesmějí u Mne strach mít,'),
(3170, 27, 11, 'kromě těch, kdož nespravedliví byli; jestliže potom zlé dobrým nahradili, tehdy Já odpouštějící jsem, slitovný.'),
(3171, 27, 12, 'Vlož ruku svou do záňadří a vyjmeš ji bílou, bez choroby jakékoliv."... A to je jedno z devíti znamení pro Faraóna a lid jeho, a věru je to hanebníků lid.'),
(3172, 27, 13, 'Když se jim dostalo Našich znamení s jasností vší, řekli: "To zjevné je kouzelnictví!"'),
(3173, 27, 14, 'A odmítli je, přestože duše jejich přesvědčeny byly o pravdivosti jejich, z nespravedlnosti a pýchy. A pohleď, jaký byl konec těch, kdož šířili pohoršení.'),
(3174, 27, 15, 'A vskutku jsme uštědřili Davidovi a Šalomounovi vědění, takže říkali: "Chvála Bohu, který nás vyznamenal nad mnohé Své služebníky věřící!"'),
(3175, 27, 16, 'Šalomoun pak stal se dědicem Davidovým a pravil: "Lidé, byli jsme naučeni řeči ptactva a byli jsme zahrnuti věcí všech hojností. A věru je toto zjevné dobrodiní!"'),
(3176, 27, 17, 'A shromáždila se u Šalomouna vojska jeho z džinů, smrtelníků a ptáků a všichni po oddílech kráčeli;'),
(3177, 27, 18, 'a když došli do údolí mravenců, zvolal jeden mravenec: "Mravenci, uchylte se do obydlí svých, aby vás nerozšlapal Šalomoun se svými vojsky, aniž to zpozorovali!"'),
(3178, 27, 19, 'I usmál se Šalomoun řeči jejich a řekl: "Pane můj, vnukni mi, abych Ti poděkoval za dobrodiní Tvé, jímž zahrnuls mne i rodiče mé! Dej, ať pro Tvé zalíbení konám zbožné skutky, a uveď mne ze Svého milosrdenství mezi bezúhonné služebníky Své!"'),
(3179, 27, 20, 'Potom provedl přehlídku ptactva a otázal se:,, Čím to, že dudka zde nevidím? Nebo snad přítomen není?'),
(3180, 27, 21, 'Věru jej ztrestám trestem těžkým či jej zaříznu, nepřinese-li mi zjevné ospravedlnění!"'),
(3181, 27, 22, 'A zdržel se dudek nepříliš dlouho a po příchodu pravil: "Zjistil jsem něco, co ty sám dosud nevíš, a přicházím k tobě ze Sáby se zvěstí jistou:'),
(3182, 27, 23, 'shledal jsem, že nad ní žena vládne, jež byla obdařena věcmi všemi a trůnu mohutného je majitelkou;'),
(3183, 27, 24, 'a shledal jsem, že ona i lid její se slunci místo Bohu klaní; Satan pak okrášlil pro ně jejich počínání a svedl je z cesty pravé, takže nejsou vedeni správně'),
(3184, 27, 25, 'a Bohu se neklaní, jenž tajemství nebes i země odkrývá a dobře zná, co skrýváte i co dáváte najevo veřejně,'),
(3185, 27, 26, 'a Bůh - není božstva kromě Něho - je trůnu nesmírného Pánem!"'),
(3186, 27, 27, 'I pravil Šalomoun: "Uvidíme, zda pravdu mluvil či zda z prolhaných jsi jen.'),
(3187, 27, 28, 'Odleť s tímto dopisem mým a shoď jim jej, potom se stranou drž a vyčkávej, co v odpověď učiní!"'),
(3188, 27, 29, 'I řekla královna: "Velmožové, byl mi shozen tento dopis vznešený,'),
(3189, 27, 30, 'od Šalomouna je a zní:, Ve jménu Boha milosrdného, slitovného!'),
(3190, 27, 31, 'Nebuďte vůči mně zpupní, nýbrž přijďte ke mně odevzdáni do vůle Boha jediného!? "'),
(3191, 27, 32, 'I pravila dále: "Velmožové, poraďte mi v této mé záležitosti! A já nic nerozhodnu bez vašeho osvědčení."'),
(3192, 27, 33, 'Odpověděli: "My jsme sice lidé síly a lidé statečnosti mocné, však záležitost patří tobě; uvaž sama, jaké vydáš nařízení!"'),
(3193, 27, 34, 'Pravila: "Když králové do města nějakého vcházejí, věru je vyplení a nejmocnější z jeho obyvatel těmi nejbídnějšími učiní a takto obvykle jednají.'),
(3194, 27, 35, 'Já však pošlu Šalomounovi dar a vyčkám, s čím navrátí se vyslanci moji."'),
(3195, 27, 36, 'Když přišli k Šalomounovi, řekl jim: "Chcete snad rozmnožit mé bohatství? Však to, co mi Bůh uštědřil, je mnohem lepší než to, co dal vám! A přesto se z darů svých radujete velice.'),
(3196, 27, 37, 'Vraťte se ke svým! A já věru proti nim vytáhnu s vojsky, jimž neodolají a vyženu je ze země jejich zbídačené a budou poníženi hluboce."'),
(3197, 27, 38, 'Potom řekl: "Shromáždění, kdo z vás mi přinese trůn její, dřív než ke mně přijdou odevzdáni do vůle Boží?"'),
(3198, 27, 39, 'I řekl jeden ifrít z džinů: "Já, já přinesu ti jej, dříve než zvedneš se z místa svého. A věru jsem k tomu silný dost i spolehlivý."'),
(3199, 27, 40, 'Pak řekl ten, u něhož bylo vědění z Písma: "Já, já ti jej přinesu, dříve než pohled tvůj se k tobě vrátí!" A když uviděl Šalomoun trůn postavený před sebou, pravil: "Toto pochází z laskavosti Pána mého, aby mne vyzkoušel, jsem-li vděčný či nevděčný. Ten, kdo je vděčný, je jím pro vlastní duši svou, však ten, kdo nevděčný je a Pán můj soběstačný je i štědrý!"'),
(3200, 27, 41, 'A dodal: "Učiňte trůn její nepoznatelným pro ni, abychom viděli, zda po cestě správné kráčí, či zda je z těch, kdož nejsou vedení."'),
(3201, 27, 42, 'Když dostavila se, tu bylo jí řečeno: "Je toto trůn tvůj?" Odpověděla: "Jako by to byl on. A dostalo se nám již dříve vědění a byli jsme do vůle Boží odevzdáni."'),
(3202, 27, 43, 'Byla však svedena tím, co místo Boha uctívala, vždyť byla z lidu nevěřícího.'),
(3203, 27, 44, 'A bylo jí řečeno: "Vstup do paláce!" A když jej spatřila, domnívala se, že je to plocha vodní, a vykasala si oděv až k lýtkům svým. I řekl Šalomoun: "To je jen palác křišťálem vydlážděný." Zvolala: "Pane můj, sama sobě jsem ukřivdila, avšak nyní se odevzdávám spolu se Šalomounem do vůle Boha, Pána lidstva veškerého."'),
(3204, 27, 45, 'A poslali jsme již kdysi k Thamúdovcům bratra jejich Sáliha, aby jim řekl: "Uctívejte Boha!" A hle, tehdy rozdělili se na skupiny dvě, jež spolu byly rozhádány.'),
(3205, 27, 46, 'Pravil Sálih: "Lide můj, proč chcete uspíšit příchod zlého před dobrým? Kdybyste Boha o odpuštění prosili, snad dostalo by se vám slitování."'),
(3206, 27, 47, 'Odpověděli: "Ve věštbě ptáků jsme viděli špatné znamení o tobě a těch, kdož s tebou jsou." Řekl Sálih: "Pták osudu vašeho je u Boha; a vy jste lid, jenž zkouškám vystaven má být."'),
(3207, 27, 48, 'A bylo v tom městě devět lidí, kteří šířili pohoršení na zemi a nechtěli se polepšit.'),
(3208, 27, 49, 'I pravili: "Přísahejme si vzájemně při Bohu, že v noci napadneme jej i jeho rodinu! A potom řekneme jeho mstiteli: Svědky záhuby jeho rodiny jsme nebyli a my vskutku jsme pravdomluvní."'),
(3209, 27, 50, 'A strojili úklady, ale také my jsme lest připravili, aniž o tom měli tušení.'),
(3210, 27, 51, 'A pohleď, jaký byl konec jejich úkladů: zahubili jsme je i s lidem jejich vším!'),
(3211, 27, 52, 'A to zde jsou obydlí jejich zpustošená za to, že nespravedliví byli, a věru je v tom znamení lidem vědoucím.'),
(3212, 27, 53, 'A jen ty jsme zachránili, kdož uvěřili a bohabojní byli.'),
(3213, 27, 54, 'A poslali jsme Lota, a hle, pravil lidu svému: "Budete se stále neřestem, jež sami dobře vidíte, oddávat?'),
(3214, 27, 55, 'Což budete i nadále z vášně chodit k mužům raději než k ženám? Ba vy věru jste lid zpozdilý!"'),
(3215, 27, 56, 'A bylo odpovědí jeho lidu jedině to, že řekli: "Vyžeňme rod Lotův z města našeho, vždyť jsou to lidé, kteří čistí chtějí zůstat!"'),
(3216, 27, 57, 'A zachránili jsme jej i jeho rodinu kromě ženy jeho, neboť jsme již určili, aby byla mezi těmi, kdož se opozdili.'),
(3217, 27, 58, 'A déšť zhoubný jsme na ně spustili; a jak hnusný to byl déšť pro ty, kdož marně varováni byli!'),
(3218, 27, 59, 'Rci: "Sláva Bohu a mír služebníkům Jeho, které si vyvolil! Je lepší Bůh, anebo to, co k Němu přidružují?"'),
(3219, 27, 60, 'Ten, jenž stvořil nebesa i zemi a seslal vám z nebe vodu, z níž dali jsme vyrůst nádhery plným zahradám, v nichž vy sami byste nemohli dát vyrůst stromům - je ten božstvem nějakým vedle Boha? A přece jsou lidé, kteří Mu rovné dávají!'),
(3220, 27, 61, 'Ten, jenž zemi pevnou učinil a středem jejím řekám téci dal a pevně zakotvené na ní vztyčil a přehradu mezi dvěma moři postavil - je ten božstvem nějakým vedle Boha? A přece většina z nich o tom nic neví!'),
(3221, 27, 62, 'Ten, jenž utištěného vyslyší, když k Němu se modlí, jenž zlo odstraňuje a činí z vás dědice na zemi - je ten božstvem nějakým vedle Boha? Jak málo dbáte na připomenutí!'),
(3222, 27, 63, 'Ten, jenž vede vás v temnotách země a moře a vysílá vítr jako předzvěst příchodu milosrdenství Svého - je ten božstvem nějakým vedle Boha? Oč vznešenější je Bůh než to, co k Němu přidružují!'),
(3223, 27, 64, 'Ten, jenž počátek dává stvoření a potom to opakuje a jenž vám z nebe i země obživu uštědřuje - je ten božstvem nějakým vedle Boha? Rci: Ukažte důkazy své, jste-li pravdomluvní!"'),
(3224, 27, 65, 'Rci: "Nikdo z těch, kdož jsou na nebesích i na zemi, nezná nepoznatelné kromě Boha. A nevěřící nemají ani tušení, kdy budou vzkříšeni!"'),
(3225, 27, 66, 'Zdaž nejsou ve vědomostech svých o životě budoucím omezeni? A přece o něm pochybují, ba více, jsou vůči němu slepí!'),
(3226, 27, 67, 'Ti, kdož nevěří, hovoří: "Až staneme se my i otcové naši prachem, budeme doopravdy z hrobů vyvedeni?'),
(3227, 27, 68, 'Tohle už bylo slibováno nám a předtím i otcům našim. Vždyť toto jen povídačky jsou dávných předků!"'),
(3228, 27, 69, 'Odpověz: "Putujte po zemi a popatřte, jaký byl konec hříšníků!"'),
(3229, 27, 70, 'Nermuť se však nad nimi a nebuď stísněn z úkladů, jež chystají!'),
(3230, 27, 71, 'A říkají: "Kdy splní se tato hrozba, jste-li z těch, kdo pravdu dí?"'),
(3231, 27, 72, 'Odpověz: "Možná že část toho, co uspíšit chcete, za vámi již těsně stojí."'),
(3232, 27, 73, 'Pán tvůj zajisté je vůči lidem pln dobrodiní, však většina z nich vděčná není.'),
(3233, 27, 74, 'Pán tvůj zajisté dobře zná, co skrývají hrudi jejich, i to, co dávají najevo.'),
(3234, 27, 75, 'Na nebi ani na zemi není tajemství žádného, aby nebylo v Knize zjevné zapsáno.'),
(3235, 27, 76, 'A Korán tento vypráví dítkám Izraele většinu z toho, o čem se rozcházejí míněním,'),
(3236, 27, 77, 'a on vskutku je pro věřící vedením i milosrdenstvím.'),
(3237, 27, 78, 'Pán tvůj pak rozsoudí mezi nimi rozhodnutím Svým, vždyť On mocný je i vševědoucí.'),
(3238, 27, 79, 'A na Boha spoléhej, neboť vskutku jsi v právu zjevném.'),
(3239, 27, 80, 'Ty věru nedokážeš, aby mrtví slyšeli či hluší tuto výzvu zaslechli, když zády se odvracejí;'),
(3240, 27, 81, 'ty také nemůžeš vyvést slepé z jejich bloudění. Dokážeš pouze, aby tě slyšeli ti, kdož ve znamení Naše věří a do vůle Boží se odevzdají.'),
(3241, 27, 82, 'Až dopadne na ně slovo rozhodné, dáme vystoupit ze země zvířeti, které promluví k nim a řekne: "Lidé věru o Našich znameních nebyli přesvědčeni!"'),
(3242, 27, 83, 'V den, kdy z národa každého shromáždíme houf z těch, kdož Naše znamení za lež prohlásili, budou na skupiny rozděleni,'),
(3243, 27, 84, 'a až se dostaví, Pán je osloví: "Za lež jste prohlásili Má znamení, aniž jste schopni byli obsáhnout je ve svém vědění anebo jaké bylo vaše počínání?"'),
(3244, 27, 85, 'A dopadne na ně slovo rozhodné za to, že nespravedliví byli, a nebudou s to promluvit ani.'),
(3245, 27, 86, 'Což neviděli, že jsme učinili noc, aby odpočívali během ní, a že den jsme učinili k vidění? A věru jsou v tom pro lid věřící znamení.'),
(3246, 27, 87, 'A v den, kdy zatroubeno bude na pozoun a hrůza pojme ty, kdož na nebesích jsou i na zemi, kromě těch, které Bůh bude chtít - tehdy k Němu všichni přijdou v ponížení'),
(3247, 27, 88, 'a uvidíš hory, jež za nehybné jsi pokládal, pohybovat se mračen pohybem - dílo to Boha, jenž věc každou učinil dokonalou a jenž o všem, co konáte, dobře je zpraven.'),
(3248, 27, 89, 'A kdokoliv s dobrými skutky přijde, ten odměnu ještě lepší dostane a v ten den před hrůzou každou bude bezpečen,'),
(3249, 27, 90, 'zatímco ti, kdož se špatnými skutky přijdou, tvářemi svými do ohně budou vrženi. "Zdaž za něco jiného než za to, co jste konali, jste odměněni?"'),
(3250, 27, 91, 'Rci: "Bylo mi nařízeno pouze, abych uctíval Pána tohoto města, jež On posvátným prohlásil a Jemu věci všechny náleží! A bylo mi rozkázáno, abych byl do vůle Jeho odevzdaný'),
(3251, 27, 92, 'a abych Korán sděloval." A kdo veden je po cestě správné, ten veden je ve prospěch duše své; tomu pak, kdo bloudí, řekni: "Já jsem jen z varovatelů jedním!"'),
(3252, 27, 93, 'A rci: "Sláva Bohu, který vám znamení Svá ukáže, a pak je poznáte! A Pán váš není vůči tomu, co činíte, lhostejný!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Tá sín mím!'),
(3254, 28, 2, 'Toto jsou znamení Písma zjevného.'),
(3255, 28, 3, 'Sdělíme ti část zvěsti o Mojžíšovi a Faraónovi podle pravdy pro poučení lidu věřícího.'),
(3256, 28, 4, 'Faraón se v zemi povyšoval a učinil z jejích obyvatel strany rozličné, přičemž jednu skupinu z nich utiskoval a zabíjel syny jejich a nechával jen ženy jejich naživu. A byl jedním z těch, kdož pohoršení šířili.'),
(3257, 28, 5, 'A chtěli jsme zahrnout přízní Svou ty, kdož poníženi byli v zemi, abychom je příkladem i dědici učinili'),
(3258, 28, 6, 'a upevnili je na zemi a ukázali tak Faraónovi a Hámánovi i vojskům jejich to, čeho se od nich obávali.'),
(3259, 28, 7, 'A vnukli jsme Mojžíšově matce: "Koj ho, a budeš-li mít o něj strach, vhoď jej do řeky. A neboj se a nermuť se, neboť My ti jej vrátíme a jednoho z poslů Svých z něho učiníme."'),
(3260, 28, 8, 'A přijal jej rod Faraónův, aby se stal pro ně nepřítelem a příčinou zármutku, vždyť Faraón, Hamán i vojska jejich hříšníky byli.'),
(3261, 28, 9, 'I řekla žena Faraónova: "Toto dítě bude ochlazením oka mého i tvého. Nezabíjej je, možná že nám ku prospěchu bude anebo si je vezmeme za syna vlastního." A oni nic netušili.'),
(3262, 28, 10, 'Příštího jitra bylo srdce matky Mojžíšovy prázdné a málem by jej byla prozradila, kdybychom nebyli posílili srdce její tak, aby byla jednou z věřících.'),
(3263, 28, 11, 'A řekla sestře jeho: "Sleduj jej!" A pozorovala jej ze strany, aniž lidé něco tušili.'),
(3264, 28, 12, 'A zakázali jsme mu již předtím prsa kojných cizích. I řekla Mojžíšova sestra: "Mám vám ukázat rodinu jednu, jež by si jej pro vás na starost vzala a laskavě s ním zacházela?"'),
(3265, 28, 13, 'A takto jsme jej vrátili matce jeho, aby oko její se ochladilo a aby se nermoutila a aby poznala, že slib Boží je pravdivý - avšak většina lidí to neví.'),
(3266, 28, 14, 'Když pak Mojžíš dosáhl dospělosti a zralosti, dali jsme mu moudrost a vědění. A takto My odměňujeme ty, kdož dobré konají.'),
(3267, 28, 15, 'I vešel Mojžíš jednou do města ve chvíli, kdy obyvatelé to nepozorovali, a nalezl v něm dva muže, kteří spolu bojovali; jeden z nich patřil k jeho straně, zatímco druhý byl z jeho nepřátel. A ten, jenž patřil k jeho straně, požádal Mojžíše o pomoc proti nepříteli svému. I udeřil jej Mojžíš a ukončil život jeho; pak zvolal: "Toto je dílo satanovo, jenž nepřítelem je i svůdcem zjevným!"'),
(3268, 28, 16, 'A pokračoval: "Pane můj, sám sobě jsem ukřivdil, odpusť mi!" A odpustil mu, vždyť On věru je odpouštějící, slitovný.'),
(3269, 28, 17, 'A řekl dále: "Pane můj, pro dobrodiní, jímž jsi mne zahrnul, nebudu již nikdy hříšníků pomocníkem!"'),
(3270, 28, 18, 'A nazítří se zdržoval ve městě pln strachu a ve střehu. A hle, ten, jemuž včera přispěchal na pomoc, opět na něj volal. I řekl Mojžíš: "Tys věru zjevným rozkolníkem!"'),
(3271, 28, 19, 'Když pak chtěl napadnout toho, jenž oběma byl nepřítelem, ten zvolal: "Mojžíši, chceš mne zabít, tak jako jsi zabil včera onoho člověka? Ty zřejmě chceš být jen násilníkem v této zemi a nechceš být vůbec jedním z těch, kdož o nápravu usilují?"'),
(3272, 28, 20, 'Tu přiběhl muž nějaký z druhého konce města a zvolal: "Mojžíši, rada velmožů se radí o tobě, aby tě zabili. Odejdi, neb já jsem ti rádcem dobrým!"'),
(3273, 28, 21, 'I odešel z města pln strachu a ve střehu a řekl: "Pane můj, zachraň mne před tímto lidem nespravedlivým!"'),
(3274, 28, 22, 'A když pak se vydal směrem k Madjanu, hovořil: "Snad mne Pán můj povede cestou přímou."'),
(3275, 28, 23, 'Když dospěl k napajedlu v zemi Madjan, nalezl u něho skupinu lidí stáda napájející a nalezl vedle nich dvě ženy, jež držely se stranou; i zeptal se jich: "Co je s vámi?" Odpověděly: "Nemůžeme napojit, dokud neodejdou tihle pastýři, a otec náš je stařec věku vysokého."'),
(3276, 28, 24, 'I napojil Mojžíš pro ně stádo jejich, potom se uchýlil do stínu a pravil: "Pane můj, jsem potřebný toho dobrodiní, které mi sešleš."'),
(3277, 28, 25, 'A přišla k němu jedna z těch žen, kráčejíc stydlivě, a řekla: "Otec můj tě zve, aby mzdou tě odměnil za to, žes pro nás napojil stádo." Když Mojžíš k němu přišel a vyprávěl mu příběh svůj, řekl tento: "Neboj se, vždyť ses zachránil před lidem nespravedlivým."'),
(3278, 28, 26, 'Jedna z žen řekla: "Otče můj, najmi jej jako pastýře, neboť nemůžeš najmout lepšího než tohoto muže silného a spolehlivého."'),
(3279, 28, 27, 'A pravil: "Přeji si, aby ses oženil s jednou z těchto dvou dcer mých s tím, že budeš u mne sloužit za mzdu po osm let. A chceš-li dokončit deset let, záleží jen na tobě. Nechci ti to nikterak ztížit a shledáš - bude-li Bůh chtít - že jsem z bezúhonných."'),
(3280, 28, 28, 'I řekl Mojžíš: "Dohodnuto je mezi mnou a tebou. A ať se rozhodnu pro kteroukoliv z těchto dvou lhůt, nechť na mne není činěn žádný nátlak. A Bůh je ručitelem toho, co říkáme."'),
(3281, 28, 29, 'A když Mojžíš dovršil lhůtu svou a odcestoval se svou rodinou, postřehl na úbočí hory Sinaje oheň jakýsi a pravil rodině své: "Zastavte se, postřehl jsem oheň a možná že vám o něm přinesu zprávu anebo nějaký oharek hořící, snad se ohřejete."'),
(3282, 28, 30, 'Když k němu přišel, bylo naň zavoláno z pravé strany údolí, z křoví na pláni požehnané: "Mojžíši, to jsem věru Já, Bůh, Pán lidstva veškerého;'),
(3283, 28, 31, 'hoď hůl svou!" Když Mojžíš spatřil, že hůl se svíjí jako had, otočil se zády a již se neobrátil. "Mojžíši, přistup blíže a neboj se, neboť jsi z těch, kdož jsou v bezpečí!'),
(3284, 28, 32, 'Vlož ruku svou do záňadří a ona vyjde ven bílá, bez poškození. A přitiskni k sobě křídlo své beze strachu. Toto jsou dva důkazy od Pána tvého pro Faraóna a velmože jeho. A jsou to věru lidé hanební."'),
(3285, 28, 33, 'Odpověděl: "Pane můj, já zabil jsem jednoho jejich člověka a bojím se, že mne zabijí.'),
(3286, 28, 34, 'Bratr můj Árón má jazyk výmluvnější než já, pošli jej se mnou jako oporu, aby potvrdil mou pravdomluvnost, neboť se obávám, že mne za lháře prohlásí."'),
(3287, 28, 35, 'Pravil Bůh: "Posílíme rámě tvé bratrem tvým a dáme vám oběma Své zplnomocnění a oni na vás nedosáhnou díky Našim znamením. A budete vy oba i ti, kdož následovat vás budou, vítězi."'),
(3288, 28, 36, 'A když k nim Mojžíš přišel se zřetelnými znameními Našimi, zvolali: "Tohle není nic jiného než kouzlo vymyšlené. A neslyšeli jsme o tom nic od otců našich dávných."'),
(3289, 28, 37, 'Mojžíš pravil: "Pán můj zná nejlépe toho, kdo přišel s vedením od Něho pocházejícím, a toho, pro něhož je určeno obývání příbytku věčného. A věru nebudou blažení ti, kdož jsou nespravedliví."'),
(3290, 28, 38, 'I řekl Faraón: "Velmoži, nevím o tom, že byste měli nějaké božstvo kromě mne. Hámáne, dej pro mne vypálit z hlíny cihly a postav mi věž, možná že vystoupím až k božstvu Mojžíšovu, neboť věru jej za lháře pokládám."'),
(3291, 28, 39, 'A byl naplněn on i vojska jeho domýšlivostí na zemi neoprávněně a domnívali se, že nikdy nebudou navráceni k Nám.'),
(3292, 28, 40, 'A uchvátili jsme jej i vojska jeho a svrhli jsme je do hlubin moře. Pohleď jen, jaký byl konec těch, kdož byli nespravedliví.'),
(3293, 28, 41, 'A učinili jsme z nich vůdce zvoucí do ohně pekelného a nedostane se jim pomoci v den zmrtvýchvstání.'),
(3294, 28, 42, 'A způsobili jsme, aby je v životě pozemském pronásledovalo prokletí a v den zmrtvýchvstání budou mezi opovrženými.'),
(3295, 28, 43, 'A darovali jsme Mojžíšovi Písmo poté, co předchozí pokolení jsme zahubili, jako důkazy viditelné pro lidi i jako vedení a milosrdenství - snad se vzpamatují.'),
(3296, 28, 44, 'Tys nebyl přítomen na západním úbočí Sinaje, když rozkaz jsme Mojžíšovi vydali; a nebyls jedním ze svědků toho.'),
(3297, 28, 45, 'Dali jsme však vzejít mnoha pokolením, jichž životy jsme dlouhými učinili. A také jsi nesídlil mezi obyvateli Madjanu přednášeje jim Naše znamení, My však jsme posly k nim vyslali.'),
(3298, 28, 46, 'A nebyls ani na úbočí hory Sinaje, když Mojžíše jsme volali, nýbrž byls vyslán jako milosrdenství od Pána svého, abys varoval lid, k němuž až dosud nepřišel varovatel žádný - snad se vzpamatují -'),
(3299, 28, 47, 'a aby, kdyby je postihla pohroma nějaká za to, co ruce jejich předtím konaly, neříkali: "Pane náš, proč jsi k nám neposlal nějakého posla, abychom znamení Tvá následovat mohli a abychom byli z věřících."'),
(3300, 28, 48, 'Však když pravda od Nás k nim přišla, řekli: "Kdyby mu tak bylo dáno něco podobného tomu, co bylo dáno Mojžíšovi!" Což však neodmítli to, čeho se předtím dostalo Mojžíšovi? A řekli dále: "Tihle dva jsou jen kouzelníci vzájemně se podporující!" a dodali: "My nevěříme ničemu z toho všeho!"'),
(3301, 28, 49, 'Odpověz: "Přineste tedy Písmo od Boha pocházející, které by bylo lepším vedením než obě tato Písma, a budu je následovat, jste-li pravdomluvní."'),
(3302, 28, 50, 'Jestliže ti neodpovědí, tedy věz, že jen vášně své následují. A kdo více bloudí než ten, kdo za vášní svou jde místo za vedením od Boha? Neboť Bůh věru nepovede lid nespravedlivý.'),
(3303, 28, 51, 'A přece jsme k nim dali dospět slovu Svému - snad se vzpamatují.'),
(3304, 28, 52, 'Ti, kterým jsme již předtím dali Písmo, věří v ně,'),
(3305, 28, 53, 'a když je jim přednášeno, hovoří: "Uvěřili jsme v ně, neboť je to pravda od Pána našeho pocházející. A věru jsme byli již předtím odevzdáni do vůle Páně."'),
(3306, 28, 54, 'Těmto dostane se odměny dvojnásobné za to, že trpěliví byli a zlé dobrým opláceli a rozdávali z toho, co jsme jim uštědřili.'),
(3307, 28, 55, 'A když tlachání plané slyší, odvracejí se od něho a říkají: "Nám skutky naše a vám skutky vaše patří! Mír s vámi, ale my nepotřebujeme pošetilé!"'),
(3308, 28, 56, 'Nemůžeš vést ty, které bys rád vedl, avšak Bůh, ten vede, koho chce. A On dobře zná ty, kdož správně jsou vedeni.'),
(3309, 28, 57, 'Říkají: "Jestliže spolu s tebou budeme následovat správné vedení, ze země své budeme vytrženi." Cožpak jsme je pevně neusadili v okrsku posvátném a bezpečném, do něhož dováženy jsou plodiny všech druhů jako obživa od Nás pocházející? Ale většina z nich to neví.'),
(3310, 28, 58, 'Kolik jsme již zahubili měst, jež svým životem se honosila! Hle, toto jsou příbytky jejich, které po nich obýval jen počet nepatrný z lidí a My dědici toho jsme se stali.'),
(3311, 28, 59, 'Pán tvůj nezničil tato města dříve, než poslal do stolice jejich posla nějakého, jenž by jim znamení Naše přednesl, a nezahubili jsme města žádná, leda když obyvatelé jejich nespravedliví byli.'),
(3312, 28, 60, 'Vše to, co bylo dáno vám, je pouze užíváním života pozemského a jeho ozdobou; zatímco to, co u Boha je, lepší a trvalejší bude. Což nebudete rozumní?'),
(3313, 28, 61, 'Je ten, jemuž jsme přislíbili slib překrásný, s nímž vskutku se setká, roven tomu, komu jsme darovali krátké užívání života pozemského a jenž mezi předvedenými bude v den zmrtvýchvstání?'),
(3314, 28, 62, 'V ten den Bůh je zavolá a řekne: "Kde jsou ti, o nichž jste tvrdili, že jsou společníky Mými?"'),
(3315, 28, 63, 'A odpoví pak ti, nad nimiž se slovo uskutečnilo: "Pane náš, toto jsou ti, které jsme do bludu uvedli. A uvrhli jsme je do bludu, stejně jako jsme my byli svedeni. My se jich před Tebou zříkáme a oni nás vůbec neuctívali."'),
(3316, 28, 64, 'A bude jim řečeno: "Nyní se modlete ke společníkům svým!" A budou je vzývat, ale nebudou vyslyšeni, a až spatří trest, litovat budou, že po správné cestě se neubírali.'),
(3317, 28, 65, 'V ten den Bůh je zavolá a řekne: "Co jste poslům Mým odpovídali?"'),
(3318, 28, 66, 'A zahalí se pro ně v ten den příběhy temnotou a nebudou se na ně vzájemně vyptávat.'),
(3319, 28, 67, 'A co týká se toho, jenž pokání činil, uvěřil a zbožné skutky konal, ten snad bude mezi blaženými.'),
(3320, 28, 68, 'Pán tvůj tvoří, cokoli chce, a volí, zatímco pro ně volba není. Sláva Bohu! On vznešenější je než to, co k Němu přidružují.'),
(3321, 28, 69, 'Pán tvůj dobře ví, co hrudi jejich skrývají, i to, co najevo dávají.'),
(3322, 28, 70, 'On je Bůh a není božstva kromě Něho, Jemu náleží chvála na tomto i onom světě a Jemu přísluší rozhodnutí a k Němu budete navráceni.'),
(3323, 28, 71, 'Rci: "Co si myslíte? Kdyby Bůh učinil nad vámi noc nepřetržitou až do dne zmrtvýchvstání, které božstvo kromě Boha by vám mohlo dát světlo denní? Což vůbec neslyšíte?"'),
(3324, 28, 72, 'Rci: "Co si myslíte? Kdyby Bůh učinil nad vámi den nepřetržitým až do dne zmrtvýchvstání, které božstvo kromě Boha by vám mohlo dát noc, abyste v ní mohli odpočívat? Cožpak vůbec nevidíte?'),
(3325, 28, 73, 'A On v milosrdenství Svém učinil pro vás noc i den, abyste si během noci odpočali a abyste během dne usilovali o část z dobrodiní Jeho - snad budete vděční!"'),
(3326, 28, 74, '... A v ten den Bůh je zavolá a zeptá se: "Kde jsou ti, o nichž jste tvrdili, že jsou společníky Mými?"'),
(3327, 28, 75, 'A vyvedeme z národa každého svědka a řekneme: "Předveďte důkaz svůj!" A pak poznají, že pravda jen Bohu náleží, a to, co si lživě vymyslili, je opustí.'),
(3328, 28, 76, 'Qárún byl jedním z lidu Mojžíšova, avšak vzpurně jednal s nimi, neboť jsme mu tolik pokladů dali, že klíče jeho těžké byly i pro houf lidí plných síly. A hle, pravil mu lid jeho: "Nejásej příliš, neboť Bůh nemá rád ty, kdož příliš jásají!'),
(3329, 28, 77, 'Spíše usiluj pomocí toho, co ti Bůh daroval, o dosažení příbytku posledního! Nezapomínej na úděl svůj v životě pozemském a čiň dobré tak, jak Bůh učinil dobré tobě! A neusiluj o pohoršení na zemi, vždyť Bůh nemá rád ty, kdož pohoršení šíří!"'),
(3330, 28, 78, 'Odpověděl: "Všeho toho se mi dostalo jen díky vědění mému vlastnímu!" Což nevěděl, že Bůh zahubil již před ním celá pokolení, jež mocnější byla než on silou a mohutnější bohatstvím? Ale hříšníci nebudou dotazováni na viny své!'),
(3331, 28, 79, 'A vyšel k lidu svému s ozdobami svými; ti, kdož toužili po radovánkách života pozemského, zvolali: "Kéž bychom měli něco podobného tomu, čeho dostalo se Qárúnovi, neb on věru vlastníkem je blahobytu nesmírného!"'),
(3332, 28, 80, 'Naopak ti, jimž bylo dáno vědění, řekli: "Běda vám, odměna Boží je lepší pro toho, kdo uvěřil a zbožné skutky konal, však setkají se s ní toliko trpěliví."'),
(3333, 28, 81, 'A dali jsme pohltit zemí Qárúna i jeho dům a neměl družiny, jež by mu byla pomohla kromě Boha, a nebyl z těch, jimž pomoc by byla poskytnuta.'),
(3334, 28, 82, 'A nazítří zrána ti, kdož včera ještě toužili na jeho místě být, zvolali: "Zdá se, že Bůh věru hojně uštědřuje obživu i odměřuje ji tomu ze služebníků Svých, komu chce. Kdyby nám byl Bůh neposkytl milost Svou, věru by nás byl dal pohltit zemí. Zdá se, že nevěřící vskutku nebudou blažení!"'),
(3335, 28, 83, 'A toto je příbytek poslední. My připravíme jej těm, kdož zpupní nechtěli být na zemi a pohoršení nešířili - a věru dobrý bude konec bohabojných.'),
(3336, 28, 84, 'Kdo přijde s dobrými skutky, tomu dostane se ještě lepšího než ony, ale kdo přijde se špatnými... tedy budou odměněni ti, kdož zlé skutky páchali, jedině podle toho, co dělali.'),
(3337, 28, 85, 'Ten, kdo ti uložil Korán, navrátí tě do místa schůzky poslední. Rci: "Pán můj nejlépe zná toho, kdo přinesl správné vedení, i toho, kdo v zjevném je bloudění."'),
(3338, 28, 86, 'Ty sám jsi nedoufal, že ti bude dáno toto Písmo; a je to jen z milosrdenství Pána tvého, nebuď tedy pomocníkem nevěřících!'),
(3339, 28, 87, 'Nechť tě oni neodvrátí od znamení Božích poté, když již ti byla seslána! Vyzývej lidi k Pánu svému a nebuď jedním z modloslužebníků!'),
(3340, 28, 88, 'Nevzývej spolu s Bohem žádné božstvo jiné, vždyť není božstva kromě Něho! A zahyne všechno kromě tváře Jeho - a Jemu náleží rozhodnutí a k Němu budete navráceni.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Alif lám mím!'),
(3342, 29, 2, 'Což si lidé myslí, že budou ponecháni na pokoji, když řeknou: "Uvěřili jsme," a že zkoušeni nebudou?'),
(3343, 29, 3, 'Vždyť jsme již zkoušeli ty, kdož před nimi byli, a Bůh věru pozná ty, kdo pravdu mluví, i ty, kdo lháři jsou.'),
(3344, 29, 4, 'Myslí si snad ti, kteří špatných činů se dopouštějí, že Nás předstihnou? Jak špatně však usuzují!'),
(3345, 29, 5, 'Kdo doufá, že s Bohem se setká, dočká se, neboť lhůta Boží se vskutku dostaví - a On slyšící je i vševědoucí.'),
(3346, 29, 6, 'Ten, kdo ve víře se usilovně snaží, ten věru sám pro sebe tak činí, vždyť Bůh je soběstačný vůči lidstvu veškerému.'),
(3347, 29, 7, 'Těm, kdož uvěřili a dobré skutky konali, těm vymažeme špatné činy jejich a odměníme je podle toho nejlepšího, co vykonali.'),
(3348, 29, 8, 'Uložili jsme člověku být dobrý vůči rodičům; jestliže však usilují přimět tě, abys ke Mě přidružoval to, o čem nemáš žádného vědění, pak je neposlouchej! A ke Mně budete navráceni a Já vás poučím o tom, co jste dělali.'),
(3349, 29, 9, 'Ty, kdož uvěřili a zbožné skutky konali, ty věru uvedeme mezi svaté.'),
(3350, 29, 10, 'Mezi lidmi jsou pak ti, kdož říkají: "Uvěřili jsme v Boha," ale když kvůli Bohu jsou pronásledováni, srovnávají zkoušku lidmi působenou s trestem Božím. Když však přijde pomoc od Pána tvého, tu věru volají: "My byli jsme s vámi!" Cožpak Bůh nezná nejlépe, co v hrudích je lidstva veškerého?'),
(3351, 29, 11, 'A Bůh vskutku zná ty, kdož věří, a stejně dobře zná i váhavce.'),
(3352, 29, 12, 'Ti, kdož neuvěřili, říkají těm, kdož uvěřili: "Pojďte po cestě naší a my poneseme pak vaše hříchy!" Však nepřevezmou nic z hříchů jejich, neboť jsou to lháři.'),
(3353, 29, 13, 'Ale ponesou věru břemena svá vlastní a další břemena se svými břemeny a budou otázáni v den zmrtvýchvstání na to, co lživě si vymýšleli.'),
(3354, 29, 14, 'A vyslali jsme již kdysi Noema k lidu jeho a zůstal mezi nimi tisíc let bez padesáti. A zachvátila je potopa, neboť byli nespravedliví.'),
(3355, 29, 15, 'A zachránili jsme jej a jeho druhy na lodi a učinili jsme ji znamením pro lidstvo veškeré.'),
(3356, 29, 16, '(A vyslali jsme) Abrahama, a hle, pravil k lidu svému: "Uctívejte Boha a buďte bohabojní, to pro vás bude lepší, jste-li vědoucími.'),
(3357, 29, 17, 'Vy místo Boha pouze modly uctíváte a lež tím vytváříte. Ti, které vedle Boha uctíváte, nemohou vám dát obživu žádnou, usilujte proto nalézt svůj příděl obživy u Boha, uctívejte Jej a buďte Mu vděční, neb k Němu pak budete navráceni."'),
(3358, 29, 18, 'Jestliže to za lež pokládáte, tedy již národy jiné před vámi to za lež prohlašovaly. A poslovi pak přísluší jen sdělení jasné.'),
(3359, 29, 19, 'Což neviděli, jak Bůh tvoří prvním tvořením a pak to opakuje? A to je pro Boha velmi snadné.'),
(3360, 29, 20, 'Rci: "Cestujte po zemi a popatřte, jak On zahájil stvoření! Potom Bůh uskuteční druhé stvoření, vždyť Bůh je všemocný nad každou věcí,'),
(3361, 29, 21, 'trestá, koho chce, a slituje se, nad kým chce, a k Němu budete přivedeni.'),
(3362, 29, 22, 'A nemůžete uniknout zásahu Jeho ani na zemi, ani na nebi a nemáte kromě Boha ochránce ani pomocníka žádného."'),
(3363, 29, 23, 'Ti, kdož nevěří ve znamení Boží a v setkání s Ním, nemají naději na milosrdenství Mé a jim se dostane trestu bolestného.'),
(3364, 29, 24, 'A nebylo odpovědí lidu jeho nic jiného, než že řekli: "Zabte ho anebo jej upalte!" A zachránil jej Bůh před ohněm, a v tom věru jsou znamení pro lid věřící.'),
(3365, 29, 25, 'I zvolal Abraham: "Vy vzali jste si věru místo Boha modly jen z lásky své společné k životu pozemskému, však později, v den zmrtvýchvstání, vy jeden druhého zapřete a vzájemně se proklínat budete, leč útočištěm vaším bude oheň pekelný a nebudete mít pomocníka žádného."'),
(3366, 29, 26, 'A uvěřil mu Lot a pravil: "Vystěhuji se k Pánu svému, neb On věru mocný je i moudrý."'),
(3367, 29, 27, 'A darovali jsme Abrahamovi Izáka a Jakuba a určili jsme pro potomstvo jeho prorocké poslání a Písmo. A dali jsme mu již odměnu na tomto světě, zatímco na onom světě budou věru mezi bezúhonnými.'),
(3368, 29, 28, 'A vyslali jsme Lota, aby pravil k lidu svému: "Vy dozajista pášete takové nemravnosti, jaké nikdo z lidstva veškerého před vámi nedělal.'),
(3369, 29, 29, 'Což budete vskutku vcházet k mužům, přepadávat na cestách a páchat skutky zavrženíhodné ve shromážděních svých?" A bylo odpovědí lidu jeho jedině to, že řekli: "Předveď nám tedy trest Boží, jsi-li pravdomluvný!"'),
(3370, 29, 30, 'I řekl: "Pane můj, pomoz mi proti lidem pohoršení šířícím!"'),
(3371, 29, 31, 'A když vyslanci Naši přišli k Abrahamovi se zvěstí radostnou, prohlásili: "Zahubíme obyvatele města tohoto, neboť nespravedliví byli!"'),
(3372, 29, 32, 'Abraham namítl: "Avšak bydlí v něm Lot." Odpověděli: "Víme velmi dobře, kdo v něm žije. Zajisté zachráníme jej i rodinu jeho kromě ženy jeho, jež bude mezi těmi, kdož se opozdí."'),
(3373, 29, 33, 'Když pak vyslanci Naši přišli k Lotovi, udělalo se mu nevolno a nevěděl si s nimi rady. I pravili: "Neboj se a nermuť se, vždyť my zachráníme tebe i rodinu tvou kromě ženy tvé, jež bude mezi těmi, kdož pozadu zůstanou.'),
(3374, 29, 34, 'My sešleme na obyvatele města tohoto pohromu z nebes za to, že byli neřestní."'),
(3375, 29, 35, 'A ponechali jsme je jako znamení zjevné pro lid rozumný.'),
(3376, 29, 36, 'A vyslali jsme k Madjanovcům bratra jejich Šu´ajba a on pravil: "Lide můj, uctívejte Boha a doufejte v den poslední! A nešiřte po zemi pohoršení!"'),
(3377, 29, 37, 'A prohlásili jej za lháře, ale postihlo je zemětřesení a nazítří zrána leželi v domech svých tvářemi k zemi.'),
(3378, 29, 38, 'A vyslali jsme k ´Ádovcům a Thamúdovcům... a je vám osud jejich jasný podle příbytků jejich. Satan jim okrášlil jejich počínání a svedl je z cesty, ačkoliv byli jasně vidoucí.'),
(3379, 29, 39, 'A podobně bylo s Qárúnem, Faraónem a Hámánem; a přišel k nim Mojžíš se zřetelnými důkazy, a ačkoliv byli na zemi domýšliví, přece nás nepředstihli.'),
(3380, 29, 40, 'A uchvátili jsme všechny z nich za hříchy jejich; proti jedněm z nich jsme vyslali vichřici, jiní byli uchváceni křikem, další jsme dali pohltit zemí a jiné jsme pak utopili. A nebyl to Bůh, který jim ukřivdil, leč oni sami sobě ukřivdili.'),
(3381, 29, 41, 'Ti, kdož si místo Boha vzali jiné ochránce, podobají se pavouku, který si pro sebe staví obydlí z pavučin. A ze všech obydlí zajisté obydlí pavoukovo je nejchatrnější - kéž by to jen věděli!'),
(3382, 29, 42, 'Bůh nejlépe zná, jaké věci oni vzývají vedle Něho - a On mocný je i moudrý.'),
(3383, 29, 43, 'My pak uvádíme podobenství tato lidem, ale pochopí je pouze rozumní.'),
(3384, 29, 44, 'Bůh stvořil nebesa i zemi jako skutečnost vážnou a věru je v tom znamení pro věřící.'),
(3385, 29, 45, 'Sděluj jim to, co bylo ti z Písma vnuknuto! Dodržuj modlitbu, vždyť modlitba odvrací od nemravnosti a věcí zavrženíhodných! Vzpomínání pak Boha je povinností největší a Bůh dobře ví, co děláte.'),
(3386, 29, 46, 'Nepři se s vlastníky Písma, leda způsobem nejvhodnějším a pouze s těmi z nich, kdož nespravedliví jsou. A rcete: "Uvěřili jsme v to, co bylo sesláno nám, i v to, co bylo sesláno vám, a náš Bůh a váš Bůh jedno jsou; a my do vůle Jeho jsme odevzdáni."'),
(3387, 29, 47, 'A takto jsme seslali Písmo i tobě; a ti, kterým jsme dali Písmo, věří v ně. A také mezi těmito zde jsou mnozí, kdož v ně věří; znamení Naše pak odmítají jedině nevěřící.'),
(3388, 29, 48, 'Tys nepředčítal předtím z Písma žádného ani jsi žádné neopisoval pravicí svou, jinak by byli na pochybách ti, kdož za nicotné to pokládají.'),
(3389, 29, 49, 'Nikoliv! Toto jsou znamení jasná v hrudích těch, kterým se vědění dostalo, a jen nespravedliví znamení Naše odmítají.'),
(3390, 29, 50, 'Říkají: "Pročpak mu nebylo sesláno znamení nějaké od Pána jeho?" Odpověz: "Znamení jsou pouze u Boha a já jsem jen varovatelem zjevným."'),
(3391, 29, 51, 'Cožpak jim nestačí, že jsme ti seslali Písmo, jež je jim sdělováno? A v tom věru je milosrdenství i připomenutí lidem věřícím.'),
(3392, 29, 52, 'Rci: "Bůh mi zcela stačí jako svědek mezi mnou a vámi! On zná, co na nebesích je i na zemi. Ti však, kdož ve falešné uvěřili a v Boha nevěří, ti věru ztrátu utrpí."'),
(3393, 29, 53, 'Žádají tě o uspíšení příchodu trestu; kdyby nebylo již lhůty určené, věru by je byl trest již postihl, však přijde k nim znenadání, aniž to tušit budou.'),
(3394, 29, 54, 'Žádají tě o uspíšení příchodu trestu, zatímco peklo zajisté obklopí nevěřící.'),
(3395, 29, 55, 'V ten den je zahalí trest shora i zdola a tehdy jim On řekne: "Okuste trest za to, co jste prováděli!"'),
(3396, 29, 56, 'Služebníci Moji, kteří věříte! Země Má je věru rozlehlá, uctívejte Mne tedy!'),
(3397, 29, 57, 'Duše každá smrti okusí, a potom budete k Nám navráceni.'),
(3398, 29, 58, 'Ty, kdož věřili a zbožné skutky konali, ty ubytujeme v ráji v komnatách, pod nimiž řeky tekou, a budou tam nesmrtelní. Jak krásná bude odměna těch, kdož dobro konali,'),
(3399, 29, 59, 'neochvějní byli a na Pána svého spoléhali!'),
(3400, 29, 60, 'Kolik je zvířat, jež neopatřují si obživu svou, ale Bůh jim ji uštědřuje stejně jako vám - a On slyšící je i vševědoucí.'),
(3401, 29, 61, 'Když se jich zeptáš: "Kdo stvořil nebesa a zemi a podmanil si slunce a měsíc?", odpovídají: "Bůh!" Jak se tedy mohou tak odvracet?'),
(3402, 29, 62, 'Bůh štědře dává obživu, komu chce ze služebníků Svých, a odměřuje ji také, komu chce a Bůh věru je vševědoucí o každé věci.'),
(3403, 29, 63, 'A když se zeptáš: "Kdo sesílá z nebe vodu, jež oživuje zemi po smrti její?", tehdy odpovídají: "Bůh." Rci: "Chvála Bohu!" Ale většina z nich nemá rozum.'),
(3404, 29, 64, 'Tento život pozemský není leč zábava a hra, zatímco pobyt v životě budoucím je tím životem pravým - kéž by to jen věděli!'),
(3405, 29, 65, 'A když plují na lodi, vzývají Boha a upřímně mu zasvěcují svou víru, když však je potom na souši zachrání, hle, modloslužebníky se stávají,'),
(3406, 29, 66, 'aby ukázali svůj nevděk za to, co jsme jim dali, a aby si jen užívali - však záhy se dozví!'),
(3407, 29, 67, 'Což nevidí, že jsme učinili z města tohoto posvátný okrsek bezpečný, zatímco lidé okolo jsou olupováni? Chtějí snad věřit ve falešné a nevěřit v dobrodiní Boží?'),
(3408, 29, 68, 'Kdo je nespravedlivější než ten, kdo si proti Bohu lži vymýšlí anebo prohlašuje pravdu, když k němu přišla, za lež? Což nebude peklo útulkem pro nevěřící?'),
(3409, 29, 69, 'Ty, kdož k vůli Nám se usilovně snaží, ty věru povedeme po cestách Svých. A Bůh zajisté je s těmi, kdož dobro konají.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Alif lám mím!'),
(3411, 30, 2, 'Poražení byli Byzantinci'),
(3412, 30, 3, 'v nejbližší zemi, však po porážce své zase zvítězí'),
(3413, 30, 4, 'v několika letech. Bohu přísluší rozhodnutí jak dřívější, tak i pozdější. V ten den pak se zaradují věřící'),
(3414, 30, 5, 'z vítězné pomoci Boží. A pomáhá On, komu chce, a On mocný je a slitovný.'),
(3415, 30, 6, 'Slib Boží - a Bůh slib Svůj neruší, však většina lidí to neví.'),
(3416, 30, 7, 'Znají jen vnější stránky života pozemského, zatímco k životu budoucímu jsou lhostejní.'),
(3417, 30, 8, 'Což neuvažovali ve svých duších, že Bůh stvořil nebesa a zemi i vše, co je mezi nimi, nejinak než jako skutečnost a na lhůtu stanovenou? Však většina lidí v setkání s Pánem svým nevěří.'),
(3418, 30, 9, 'Což necestovali po zemi a neviděli, jaký byl konec těch, kdož před nimi byli? A přece byli mocnější silou a orali zemi a obdělávali ji více, než oni ji obdělávají. A přišli k nim poslové jejich s jasnými znameními. A nebyl Bůh takový, aby jim ukřivdil, leč oni sami na sobě křivdu spáchali.'),
(3419, 30, 10, 'A konec těch, kdož zlo činili, bude pak ještě horší, neboť za lživá znamení Boží prohlašovali a posměšky si z nich tropili.'),
(3420, 30, 11, 'Bůh zahajuje život prvním stvořením, pak opakovat to bude a posléze k Němu budete navráceni.'),
(3421, 30, 12, 'V den, kdy Hodina udeří, budou hříšníci v zoufalství přivedeni'),
(3422, 30, 13, 'a nebudou mít ve společnících svých přímluvce žádné a ve společníky své věřit přestanou.'),
(3423, 30, 14, 'A v den, kdy Hodina udeří, lidé rozděleni budou:'),
(3424, 30, 15, 'co týká se těch, kdož věřili a dobré skutky konali, ti v zahradě budou obšťastněni,'),
(3425, 30, 16, 'zatímco ti, kdož nevěřili a znamení Naše i setkání na onom světě za lež prohlašovali, ti k trestu budou předvedeni.'),
(3426, 30, 17, 'Pronášejte slávu Bohu, když večer zastihne vás a když ráno procitáte'),
(3427, 30, 18, '#NÁZEV?'),
(3428, 30, 19, 'On dává vzejít živému z mrtvého a mrtvému z živého a oživuje zemi po smrti její. A podobně i vy budete z hrobů vyvedeni.'),
(3429, 30, 20, 'A patří k Jeho znamením, že stvořil vás z prachu, a hle, když lidstvem jste se stali, po zemi jste rozptýleni.'),
(3430, 30, 21, 'A patří k Jeho znamením, že vám z vás samých manželky stvořil, abyste u nich klid nalezli. A vložil mezi vás lásku a dobro - a věru jsou v tom znamení pro lidi přemýšlivé.'),
(3431, 30, 22, 'A patří k Jeho znamením stvoření nebes a země i rozličnost jazyků vašich i barev - a v tom je věru znamení pro lidstvo veškeré.'),
(3432, 30, 23, 'A patří k Jeho znamením, že za noci spíte a za dne o část z Jeho přízně usilujete. I v tom je věru znamení pro lid slyšící.'),
(3433, 30, 24, 'A patří k Jeho znamením, že vám ukazuje blesky pro strach i naději a sesílá z nebe vodu a oživuje jí zemi po smrti její. A v tom je věru znamení pro lid chápavý.'),
(3434, 30, 25, 'A patří k Jeho znamením, že nebesa a země z rozkazu Jeho pevně stojí a že potom, až zvoláním vás zavolá ze země, vy ven vyjdete z ní.'),
(3435, 30, 26, 'A Jemu náleží vše, co na nebesích je i na zemi, a všichni jsou Mu pokorně oddáni.'),
(3436, 30, 27, 'A On je ten, jenž zahajuje život prvním stvořením a pak to opakovat bude - a to pro něj je velmi snadné. On příkladem nejvyšším je na nebesích a na zemi a On mocný je i moudrý.'),
(3437, 30, 28, 'On uvádí vám podobenství o vás samých: máte snad mezi těmi, jimž pravice vaše vládne, společníky v tom, co jsme vám uštědřili, takže jste si v tom rovni? Bojíte se otroků svých tak, jak bojíte se navzájem? Takto činíme znamení Naše srozumitelnými pro lid rozumný.'),
(3438, 30, 29, 'Však nikoliv! Ti, kdož nespravedliví jsou, vášně své jen bez rozmyslu následují. Kdo může vést ty, jimž Bůh dal zbloudit a kteří žádné pomocníky nemají?'),
(3439, 30, 30, 'Obrať tvář svou k náboženství pravému jako haníf podle sklonu přirozeného lidem, v němž Bůh lidi stvořil. Stvoření Boží pak změnit nelze - a toto je náboženství neměnné, však většina lidí to neví.'),
(3440, 30, 31, '(Vzývejte Pána svého) obracejíce se k Němu kajícně! Buďte bohabojní, dodržujte modlitbu a nebuďte z modloslužebníků'),
(3441, 30, 32, 'ani z těch, kdož náboženství své rozštěpili a stali se různými stranami. A každá strana se těší jen ze svých vlastních názorů.'),
(3442, 30, 33, 'Když dotkne se lidí neštěstí, Pána svého vzývají a kajícně se k Němu obracejí. Když jim však potom dá ochutnat milosrdenství Svého, hle, část z nich Pánu svému společníky přidává,'),
(3443, 30, 34, 'aby tak nevděční byli za to, co jsme jim uštědřili. Jen si užívejte, však záhy se dozvíte!'),
(3444, 30, 35, 'Či jsme jim snad seslali plnou moc, jež by hovořila o tom, co k Němu přidružují?'),
(3445, 30, 36, 'Když dáme lidem okusit milosrdenství Svého, tu radují se z něho, však když je postihne něco zlého za to, co ruce jejich předtím učinily, hle, hned si zoufají.'),
(3446, 30, 37, 'Což neviděli, že Bůh rukou otevřenou uštědřuje či odměřuje obživu, komu chce? A v tom věru jsou znamení pro lid věřící.'),
(3447, 30, 38, 'Dávej příbuznému podle práva jeho i chudákovi a tomu, kdo na cestě je Boží - a to pro ty je lepší, kdo po tváři Boží touží, a to jsou ti, kdož budou blažení.'),
(3448, 30, 39, 'To, co jako lichvu berete, aby to přineslo rozmnožení vzaté z majetku jiných, to nepřinese u Boha úrok žádný. Však to, co jako almužnu dáváte, toužíce po tváři Boží - tak činí ti, jimž odměna bude zdvojnásobena.'),
(3449, 30, 40, 'Bůh je ten, jenž vás stvořil, potom vám obživu uštědřil, pak zemřít vám dá a nakonec vás opět k životu přivede. Je mezi těmi, které Mu přidružujete, někdo, kdo mohl by něco podobného učinit? Sláva Mu a On vznešenější je než to, co je k Němu přidružováno.'),
(3450, 30, 41, 'A objevila se na pevnině i na moři spoušť za to, co ruce lidí způsobily, aby tak Bůh jim dal okusit část z toho, co si vysloužili - snad se obrátí.'),
(3451, 30, 42, 'Rci: "Cestujte po zemi a pohleďte, jaký byl konec těch před vámi, neboť většina z nich byli také modloslužebníci!"'),
(3452, 30, 43, 'Obrať tedy tvář svou k náboženství neměnnému, dříve než přijde od Boha den, jejž nelze odvrátit. V ten den budou lidi na dvě skupiny rozděleni:'),
(3453, 30, 44, 'ten, kdo nevěřil obtížen bude nevírou svou, zatímco ti, kdož zbožné skutky konali, sami pro sebe si připravili ulehčení,'),
(3454, 30, 45, 'aby tak Bůh ty, kdož uvěřili a zbožné skutky konali, z laskavosti své mohl odměnit. A On nemiluje nevěřící.'),
(3455, 30, 46, 'A patří k Jeho znamením, že vysílá větry jako zvěstovatele radostné zprávy, a aby vám tím dal ochutnat milosrdenství Svého a aby lodi pluly po moři z rozkazu Jeho a abyste mohli usilovat o část z dobrodiní Jeho - snad budete vděční.'),
(3456, 30, 47, 'A již před tebou jsme vyslali posly k lidu jejich a přišli k nim s jasnými znameními. A pomstili jsme se těm, kdož hřešili, a povinností Naší je, abychom pomohli věřícím.'),
(3457, 30, 48, 'Bůh je ten, kdo vysílá větry, jež zvedají do výše mračna; pak rozptyluje je po obloze, jak chce, a rozděluje je na menší kusy a posléze vidíš, i jak mezi nimi vychází déšť. A když jím zasáhne ze služebníků Svých ty, jež chce, hle, zaradují se,'),
(3458, 30, 49, 'ačkoliv předtím, než seslán byl jim, naplněni byli zoufalstvím.'),
(3459, 30, 50, 'Pohlédni tedy na stopy milosrdenství Božího, pohlédni, jak zemi oživuje po smrti její. A On věru je ten, jenž mrtvé k životu přivádí, vždyť On věcí všech je mocen.'),
(3460, 30, 51, 'I kdybychom vyslali vítr vysušující a lidé by spatřili rostlinstvo zežloutlé, přece by nepřestali být ani potom nevěřícími.'),
(3461, 30, 52, 'Ty věru nedokážeš, aby mrtví slyšeli ani aby hluší uslyšeli volání, když zády se odvracejí.'),
(3462, 30, 53, 'A ty nejsi ani ten, jenž slepé z jejich bloudění vyvádí, ty pouze dokážeš, aby slyšeli ti, kdož věří ve znamení Naše, a to jsou ti, kteří do vůle Naší jsou odevzdáni.'),
(3463, 30, 54, 'Bůh je ten, jenž stvořil vás slabými, po slabosti pak sílu dal a posléze opět ze síly slabost a sivost učinil. On tvoří, co chce, a On vševědoucí je i všemohoucí.'),
(3464, 30, 55, 'V den, kdy Hodina udeří, hříšníci přísahat budou, že v hrobech zůstali jen hodinu jedinou. Takto se sami klamali!'),
(3465, 30, 56, 'Ti, jimž dostalo se vědění i víry, však řeknou: "Věru jste tak zůstali, jak psáno je v knize Boží, až do dne vzkříšení. A toto zde je den vzkříšení, však vy jste to neznali."'),
(3466, 30, 57, 'V ten den nebude těm, kdož nespravedliví byli, nic platna výmluva jejich a nebudou vyslyšeni.'),
(3467, 30, 58, 'A věru jsme uvedli lidem v Koránu tomto různé druhy příkladů; když však přijdeš s nějakým znamením, řeknou nevěřící: ("... ". Rci:) "Vy nečiníte nic jiného, než že za falešné prohlašujete!"'),
(3468, 30, 59, 'A takto Bůh zapečeťuje srdce těch, kdož nic nevědí.'),
(3469, 30, 60, 'Buď však neochvějný, vždyť slib Boží pravdou jest. A nechť ti, kdož nejsou pevně přesvědčeni, tě ve víře tvé nezviklají!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Alif lám mím!'),
(3471, 31, 2, 'Toto jsou znamení Písma moudrého,'),
(3472, 31, 3, 'vedení i milosrdenství pro ty, kdož dobro konají,'),
(3473, 31, 4, 'kdož modlitby dodržují, almužny dávají a v život posmrtný pevně doufají.'),
(3474, 31, 5, 'Tito jsou Pánem svým dobře vedeni a ti budou blažení.'),
(3475, 31, 6, 'Mezi lidmi je i jeden, jenž bez jakéhokoliv vědění zábavné povídání nakupuje, aby tak svedl lidi z cesty Boží, z níž posměšky si tropí - a to jsou ti, kteří se trestu zahanbujícího dočkají'),
(3476, 31, 7, 'Když jsou takovému přednášena Naše znamení, tu odvrací se domýšlivě, jako by je ani neslyšel a jako by měl v uších svých hluchotu - tomu oznam, že čeká jej trest bolestný.'),
(3477, 31, 8, 'Pro ty však, kdož uvěřili a zbožné skutky konali, zahrady slastí jsou připraveny,'),
(3478, 31, 9, 'v nichž budou nesmrtelní, a tehdy slib Boží se uskuteční a On mocný je i moudrý.'),
(3479, 31, 10, 'On stvořil nebesa bez opor, jež jsou viditelné, a rozhodil po zemi hory pevně zakotvené, aby se s vámi nekymácela. A rozšířil po ní zvířata rozličná. A seslali jsme z nebes vodu a dali jsme z ní vyrůst všem druhům rostlin štědrých.'),
(3480, 31, 11, 'Toto je stvoření Boží a nyní mi ukažte, co vlastně stvořila božstva, jež vedle Něho máte. Ano, nespravedliví jsou věru v bludu zjevném!'),
(3481, 31, 12, 'Kdysi jsme darovali Luqmánovi moudrost řkouce: "Buď vděčný Bohu, neboť ten, kdo vděčný je Bohu, je vděčný pro dobro své vlastní. Kdo však je nevděčný... tedy Bůh soběstačný je i chvályhodný."'),
(3482, 31, 13, 'Pravil Luqmán synu svému napomínaje jej: "Synáčku, nepřidružuj nic k Bohu, vždyť přidružování je věru křivda nesmírná."'),
(3483, 31, 14, 'A uložili jsme člověku laskavost k rodičům jeho - neb nosila jej matka jeho se samými nesnázemi a odstavila jej po dvou letech - řkouce: "Buď vděčný Mně i rodičům svým neb u Mne je cíl konečný!"'),
(3484, 31, 15, 'Nutí-li tě však usilovně, abys ke Mě přidružoval něco, o čem nemáš vědění žádné, pak je neposlouchej! Buď k nim na tomto světě laskavý podle zvyklosti uznané, avšak následuj cestu těch, kdož kajícně se ke Mně obrátili! Ke Mně se pak uskuteční návrat váš a Já vás poučím o tom, co jste konali.'),
(3485, 31, 16, '"... Synáčku, i kdyby to byla váha zrnka hořčičného a i kdyby to bylo ve skále či na nebi anebo na zemi, Bůh to přinese! Vždyť Bůh věru bystrý je i dobře zpravený.'),
(3486, 31, 17, 'Synáčku, dodržuj modlitbu, přikazuj vhodné a zakazuj zavrženíhodné! Buď trpělivý vůči tomu, co tě postihne, neboť to patří k podstatě věci!'),
(3487, 31, 18, 'Neodvracej tvář svou od lidí a nechoď nadutě po zemi, neboť Bůh věru nemiluje domýšlivé vychloubače!'),
(3488, 31, 19, 'Buď umírněný v chůzi své a ztišuj hlas svůj, vždyť nejodpornější z hlasů všech je věru hlas oslů."'),
(3489, 31, 20, 'Což nevidíte, že Bůh vám podmanil vše, co je na nebesích a na zemi, a že zahrnul vás dobrodiním Svým zjevným i skrytým? Však mezi lidmi jsou někteří, kdož ohledně Boha se hádají bez jakýchkoliv znalostí či vedení anebo Písma osvíceného.'),
(3490, 31, 21, 'A když je jim řečeno: "Následujte to, co seslal Bůh!", odpovídají: "Nikoliv, my budeme následovat to, co u otců svých jsme nalezli." A což když je satan zve k trestu plamene šlehajícího?'),
(3491, 31, 22, 'Ten, kdo se odevzdal do vůle Boží a koná dobré skutky, ten chopil se rukojeti nejspolehlivější a Bohu patří konečné rozhodnutí.'),
(3492, 31, 23, 'Nechť tě nermoutí nevíra těch, kdož neuvěřili! K Nám se uskuteční návrat jejich a poučíme je již o tom, co konali - vždyť Bůh dobře ví, co hrudi skrývají.'),
(3493, 31, 24, 'Necháváme je užívat trochu radostí, ale potom je podrobíme trestu přísnému.'),
(3494, 31, 25, 'Když se jich zeptáš: "Kdo stvořil nebesa a zemi?", jistě odpoví: "Bůh." Rci: "Chvála Bohu!" Však většina z nich nic neví.'),
(3495, 31, 26, 'Bohu náleží vše, co na nebesích je i na zemi, a Bůh věru je soběstačný, chvályhodný.'),
(3496, 31, 27, 'Kdyby všechno stromoví, jež je na zemi, bylo pery, a kdyby moře, rozmnožené ještě o sedm moří, bylo inkoustem, nevyčerpala by se nikdy slova Boží - a Bůh mocný je i moudrý.'),
(3497, 31, 28, 'Vaše stvoření i vaše vzkříšení je pro Něj jen jako stvoření a vzkříšení duše jediné - a Bůh zajisté je slyšící i vidoucí.'),
(3498, 31, 29, 'Což nevidíš, že Bůh dává přejít noci v den a dni v noc - a podmanil si slunce a měsíc, takže obě tělesa směřují ke lhůtě stanovené a že Bůh dobře je zpraven o všem, co konáte!'),
(3499, 31, 30, 'A je to proto, že Bůh je skutečnost, zatímco to, co vzýváte vedle Něho, je falešné - a Bůh věru je vznešený i veliký.'),
(3500, 31, 31, 'Což nevidíš, že lodi plují po moři díky dobrodiní Božímu a proto, aby vám ukázal některá ze Svých znamení? A věru jsou v tom znamení pro každého člověka trpělivého, vděčného.'),
(3501, 31, 32, 'A když zahalí je vlny jako temnota, vzývají Boha a zasvěcují mu upřímnou svou víru, a když zachrání je na souši, tu někteří z nich k Němu směřují... A popírat znamení Naše může jedině nevděčník proradný!'),
(3502, 31, 33, 'Lidé, bojte se Pána svého a obávejte se dne, kdy otec nevykoupí syna svého a syn nevykoupí ničím otce svého. Slib Boží se věru uskuteční, nechť vás tedy neoklame život pozemský a nechť vás nepodvede podvodník žádný ohledně Boha!'),
(3503, 31, 34, 'Jen u Boha je vědění o příchodu Hodiny. On déšť hojný sesílá a ví, co skryto je v lůnech, zatímco duše žádná nezná, co si zítra vyslouží, a žádná neví, ve které zemře zemi. Bůh věru je vševědoucí a dobře zpravený!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Alif lám mím!'),
(3505, 32, 2, 'Seslání Písma tohoto - o němž pochyby není - od Pána lidstva veškerého pochází;'),
(3506, 32, 3, 'oni však říkají: "Sám si to vymyslil!" Ó nikoliv, to pravda je od Pána tvého pocházející, abys varoval lid, k němuž nepřišel před tebou varovatel žádný - snad půjdou po správné cestě.'),
(3507, 32, 4, 'Bůh je ten, jenž nebesa a zemi i vše, co je mezi nimi, v šesti dnech stvořil a pak na trůnu Svém se usadil. A nemáte kromě Něho ochránce ani přímluvce žádného. Což se nevzpamatujete?'),
(3508, 32, 5, 'On rozkaz z nebe na zem usměrňuje a ten pak k Němu vystoupí v den, jehož délka - podle počítání vašeho - tisíc let činí.'),
(3509, 32, 6, 'A tak tomu je - On zná nepoznatelné i všeobecně známé a On mocný je i slitovný;'),
(3510, 32, 7, 'ten, jenž udělal překrásně každou věc jím stvořenou a člověka nejdříve z hlíny stvořil,'),
(3511, 32, 8, 'pak potomstvo jeho z kapky vody nicotné učinil'),
(3512, 32, 9, 'a potom jej vyrovnal a část ducha Svého v něj vdechl; a sluch, zrak i srdce všem vám dal - a přece jak málo jste vděční!'),
(3513, 32, 10, 'A říkají: "A až zabloudíme kdesi v zemi, což vskutku budeme novým stvořením vzkříšeni?" A přece jen v setkání s Pánem svým nevěří!'),
(3514, 32, 11, 'Rci: "Anděl smrti, jenž vás na starosti má, vás povolá a potom budete k Pánu svému navráceni!"'),
(3515, 32, 12, 'Kdybys viděl, jak provinilci klopíce hlavy před Pánem svým volat budou: "Pane náš, viděli jsme a slyšeli jsme; vrať nás na zem a my zbožné skutky konat budeme, neboť nyní jsme o pravdě přesvědčeni."'),
(3516, 32, 13, 'Kdybychom chtěli, dali bychom duši každé vedení správné, leč nechť uskuteční se slovo Mé: "Věru naplním peklo lidmi spolu s džiny!"'),
(3517, 32, 14, 'Ochutnejte trestu za to, že jste zapomněli na setkání s tímto svým dnem; My také jsme na vás zapomněli, okuste tedy za skutky své trest nesmrtelný!'),
(3518, 32, 15, 'Ve znamení Naše pak věří jedině ti, kdož při zmínce o nich na zem padají klaníce se a chválu Pána svého slaví, aniž jsou domýšliví.'),
(3519, 32, 16, 'Boky jejich se lůžkům vyhýbají, neboť oni Pána svého z bázně i z touhy vzývají, a z toho, co jsme jim uštědřili, rozdávají.'),
(3520, 32, 17, 'Žádná duše neví, co z radostí skryto je pro ně odměnou za skutky jejich.'),
(3521, 32, 18, 'Cožpak je věřící roven tomu, kdo je hanebník? Ne, ti rovni si nebudou.'),
(3522, 32, 19, 'Pro ty, kdož uvěřili a zbožné skutky konali, pro ty jsou určeny zahrady útulku jako příbytek za to, co činili,'),
(3523, 32, 20, 'však těm, kdož nepravosti páchali, těm oheň bude útočištěm. A kdykoliv budou chtít z něho odejít, budou v něj navráceni a řečeno jim bude: "Ochutnejte trest ohnivý, jejž za vylhaný jste měli!"'),
(3524, 32, 21, 'A dáme jim okusit trestu bližšího kromě trestu většího - snad se z bludu svého navrátí.'),
(3525, 32, 22, 'Kdo je nespravedlivější než ten, jenž - když připomínána jsou mu znamení Pána jeho - od nich se odvrátí? My věru se provinilým pomstíme.'),
(3526, 32, 23, 'Věru jsme již dali Mojžíšovi Písmo - a nebuď na pochybách, že se s Nim setkáš - a stanovili jsme je pro dítka Izraele jako vedení'),
(3527, 32, 24, 'a z nich pak učinili jsme vedoucí, aby podle rozkazu Našeho vedli za to, že byli neochvějní a o znameních Našich přesvědčeni.'),
(3528, 32, 25, 'Pán tvůj vskutku pak rozsoudí mezi nimi v den zmrtvýchvstání to, o čem se v mínění rozcházejí.'),
(3529, 32, 26, 'Což nebylo jim ukázáno, kolik před nimi jsme již zahubili pokolení, okolo jichž příbytků tak často procházejí? A věru jsou v tom znamení - což sluchu tomu nepopřejí?'),
(3530, 32, 27, 'Což nepozorují, jak ženeme vodu na zem vyprahlou, takže z ní obilí vzrůstá, jímž živí se jak oni, tak i jejich stáda? Což jasně to nezří?'),
(3531, 32, 28, 'A vyptávají se: "Kdy tedy přijde ono rozhodnutí, jste-li pravdomluvní?"'),
(3532, 32, 29, 'Odpověz: "V den rozhodnutí nebude nic platna víra těm, kdož nevěřící byli, a nebude jim dán odklad žádný!"'),
(3533, 32, 30, 'Odvrať se tedy od nich a vyčkávej! Však také oni čekají.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Proroku, boj se Boha a neposlouchej nevěřící a pokrytce, vždyť Bůh je věru všemohoucí i moudrý!'),
(3535, 33, 2, 'Následuj to, co ti bylo vnuknuto od Pána tvého! A Bůh vskutku je dobře zpraven o všem, co děláte.'),
(3536, 33, 3, 'Spolehni se na Boha, neboť Bůh zajisté stačí jako ochránce. Bůh nevložil žádnému člověku dvě srdce do nitra jeho a z vašich žen, jež zapuzujete slovy "Jsi pro mne jak záda mé matky", neučinil vaše matky skutečné a neučinil z vašich adoptivních dětí vaše syny skutečné. To jsou jen slova vaše, vyřčená ústy vašimi, avšak pouze Bůh mluví pravdu a vede po cestě správné.'),
(3537, 33, 4, 'Nazývejte adoptivní děti své podle otců jejich, neboť to spravedlivější je před Bohem. A neznáte-li jejich otce, pak jsou to vaši bratři v náboženství a vaši bližní. A není pro vás hříchem to, co učiníte omylem, nýbrž jen to, co srdce vaše zamýšlela a Bůh je odpouštějící, slitovný.'),
(3538, 33, 5, 'Prorok je bližší věřícím než oni sobě navzájem a jeho manželky jsou matkami jejich. A pokrevní'),
(3539, 33, 6, 'příbuzní jsou si vzájemně bližší, podle Písma Božího, než (medínští) věřící a vystěhovalci; přesto čiňte přátelům svým to, co je uznáno za vhodné. A tak je to zaznamenáno v Písmu.'),
(3540, 33, 7, 'A hle, uzavřeli jsme s proroky úmluvu, s tebou, s Noem, s Abrahamem, s Mojžíšem i s Ježíšem synem Mariiným, a uzavřeli jsme s nimi úmluvu přísnou'),
(3541, 33, 8, 'a Bůh požádá spravedlivé o počet z pravdomluvnosti jejich, zatímco pro nevěřící připravil trest bolestný.'),
(3542, 33, 9, 'Vy, kteří věříte! Pomněte dobrodiní Božího vůči vám, když vojska táhla proti vám a poslali jsme proti nim vichřici a vojska neviditelná. A Bůh jasně viděl, co jste dělali,'),
(3543, 33, 10, 'když proti vám táhli zdola i shora, když zraky vaše se strachem zakalily a srdce vám stoupala do hrdel a když jste měli o Bohu domněnky různé.'),
(3544, 33, 11, 'A byli tam tehdy věřící podrobeni zkoušce a byli otřeseni silným otřesem.'),
(3545, 33, 12, 'A hle, pokrytci a ti, v jejichž srdcích nemoc dlí, zvolali: "Vše, co nám přislíbil Bůh a posel Jeho, není nic než klam!"'),
(3546, 33, 13, 'A hle, jedna skupina z nich řekla: "Lidé jathribští, není zde pro vás místa, vraťte se!" A druhý oddíl žádal proroka o povolení k návratu řka: "Naše domy jsou obnaženy!", zatímco vůbec nebyly obnaženy, ale oni chtěli jen prchnout.'),
(3547, 33, 14, 'A kdyby byl nepřítel k nim vnikl z různých konců a byli by bývali vyzváni zřeknout se islámu, věru by na to bývali přistoupili, ale byli by v něm zůstali jen nedlouho.'),
(3548, 33, 15, 'A předtím již uzavřeli úmluvu s Bohem, že se neobrátí zády; a přece budou na úmluvu svou s Bohem dotázáni!'),
(3549, 33, 16, 'Rci: "Nic vám neprospěje útěk váš; jestliže utíkáte před smrtí přirozenou či před zabitím, beztak budete užívat světa jen nepatrně."'),
(3550, 33, 17, 'A řekni jim dále: "Kdo je ten, jenž ochrání vás před Bohem, bude-li On si pro vás přáti zlé anebo bude-li si přáti pro vás milosrdenství?" A nenaleznou vedle Boha ochránce ani pomocníka žádného.'),
(3551, 33, 18, 'A Bůh zná dobře ty z vás, kdož druhým brání, i ty, kdož říkají bratřím svým: "Pojďte sem k nám!" a přitom ukazují jen malou statečnost'),
(3552, 33, 19, 'jsouce skoupí vůči vám. A když přiblíží se strach, vidíš je, jak se na tebe dívají očima v sloup obrácenýma jako ten, jenž již smrtí je omámen. Však jakmile strach odejde, bičují vás jazyky ostrými z lakoty po nejlepší kořisti. Tihle lidé vůbec nevěří a Bůh učiní marnými skutky jejich, a to je pro Boha nadmíru snadné.'),
(3553, 33, 20, 'A domnívali se, že spojenci neodejdou; a jestliže spojenci přijdou, tu nejraději by se uchýlili do pouště mezi Araby kočovné a dotazovali se jich na zprávy o vás. A kdyby byli mezi vámi, bojovali by jen málo.'),
(3554, 33, 21, 'A věru máte nyní v poslu Božím příklad překrásný pro každého, kdo doufá v Boha a v den poslední a kdo Boha hojně vzpomíná.'),
(3555, 33, 22, 'Když pak věřící spatřili spojence, zvolali: "Tohle je to, co slíbil nám Bůh a posel Jeho a vskutku měli Bůh i posel Jeho pravdu!" A vše to posílilo jen víru a oddanost jejich.'),
(3556, 33, 23, 'A mezi věřícími jsou muži, kteří věrně dodržují úmluvu, již s Bohem uzavřeli; a jsou mezi nimi někteří, jichž osud již se naplnil, a jiní, kteří ještě čekají a nezměnili se změnou žádnou,'),
(3557, 33, 24, 'aby Bůh mohl odměnit spravedlivé za upřímnost víry jejich a mohl potrestat pokrytce, bude-li chtít, nebo jim prominout. Vždyť Bůh je věru odpouštějící, slitovný.'),
(3558, 33, 25, 'A odrazil Bůh nevěřící zlobou naplněné, aniž dosáhli něčeho dobrého. A ušetřil Bůh věřícím bojování a Bůh věru je silný, mocný.'),
(3559, 33, 26, 'A vyvedl ty z vlastníků Písma, kteří jim pomáhali, z pevností jejich a uvrhl do srdcí jejich hrůzu, takže jste jednu skupinu z nich zabili a druhou zajali.'),
(3560, 33, 27, 'A učinil vás dědici jejich země, obydlí, majetků i jedné země, na niž jste dosud nevkročili. A Bůh je všemocný nad každou věcí.'),
(3561, 33, 28, 'Proroku, rci manželkám svým: "Přejete-li si tento život pozemský a ozdoby jeho, tedy pojďte, nechám vás ho užívat a propustím vás krásným propuštěním.'),
(3562, 33, 29, 'Však přejete-li si Boha, posla Jeho a příbytek na onom světě, tedy vězte, že Bůh připravil pro ty z vás, jež dobro konají, odměnu nesmírnou."'),
(3563, 33, 30, 'Ženy prorokovy! Té z vás, jež dopustí se hanebnosti zjevné, té bude trest zdvojnásoben a to je pro Boha velmi snadné.'),
(3564, 33, 31, 'Avšak té, jež z vás je pokorně oddána Bohu a poslu Jeho a zbožné skutky koná, té dáme odměnu dvojnásobnou a připravíme pro ni přiděl štědrý.'),
(3565, 33, 32, 'Ženy prorokovy! Nebuďte jako lecjaká jiná žena! Chcete-li být bohabojné, pak nebuďte příliš poddajné v řeči své, aby po vás nebažil ten, v jehož srdci je choroba, nýbrž mluvte slova vhodná!'),
(3566, 33, 33, 'Zdržujte se důstojně ve svých domech a nestavte na odiv ozdoby svoje podle způsobu dřívější doby nevědomosti! Dodržujte modlitbu, dávejte almužnu a buďte poslušné vůči Bohu a poslu Jeho! Bůh chce pouze odstranit od vás špínu, lidé domu, a chce vás očistit úplným očištěním.'),
(3567, 33, 34, 'A zapamatujte si to, co bylo přednášeno v domech vašich ze znamení Božích a z moudrosti! A Bůh věru je bystrý, dobře zpravený.'),
(3568, 33, 35, 'Muslimové a muslimky, věřící muži a věřící ženy, poslušní a poslušné, pravdomluvní a pravdomluvné, trpěliví a trpělivé, pokorní a pokorné, dárci a dárkyně almužny, postící se muži a postící se ženy, cudní a cudné, muži a ženy hojně Boha vzpomínající - těm všem Bůh věru připravil odpuštění i odměnu nesmírnou.'),
(3569, 33, 36, 'A ani věřícímu, ani věřící není dána volba v záležitostech jejich, jakmile Bůh a posel Jeho věc jednou rozhodli; a kdo neposlouchá Boha a posla Jeho, ten zbloudil zjevným zblouděním.'),
(3570, 33, 37, 'A hle, pravil jsi tomu, koho Bůh zahrnul dobrodiním a jemuž jsi i ty prokázal dobrodiní: "Podrž si manželku svou a boj se Boha!" A hle, skrýval jsi v duši své to, co Bůh odhalil, a bál ses lidí, zatímco Bůh měl větší právo na to, aby ses bál Jeho. A když s ní Zajd přerušil styky, dali jsme ti ji za manželku, aby nebylo na věřících hříchu, chtějí-li si vzít manželky svých adoptivních synů poté, co tito s nimi přerušili styky. A rozkaz Boži budiž vykonán!'),
(3571, 33, 38, 'A není pro proroka žádný hřích v tom, co mu Bůh uložil podle zvyklosti Boží u těch, kdož již předtím odešli a rozkaz Boží je určením předurčeným'),
(3572, 33, 39, 'pro ty, kdo hlásají poselství Boží a nebojí se nikoho kromě Boha! A Bůh stačí jako účtovatel.'),
(3573, 33, 40, 'A Muhammad není otcem žádného muže z vás, ale je poslem Božím a pečetí proroků. A Bůh je vševědoucí o věci každé.'),
(3574, 33, 41, 'Vy, kteří věříte! Vzývejte hojně Boha'),
(3575, 33, 42, 'a chvalte Jej za jitra i za večera!'),
(3576, 33, 43, 'On je ten, jenž na vás svolává požehnání stejně jako Jeho andělé, aby vás vyvedl z temnot k světlu - a On slitovný je vůči věřícím.'),
(3577, 33, 44, 'A bude jejich přivítáním v den, kdy setkají se s Bohem, "Mír s vámi!" a Bůh pro ně připravil odměnu štědrou.'),
(3578, 33, 45, 'Proroku, vyslali jsme tě jako svědka a hlasatele zvěsti radostné i jako varovatele'),
(3579, 33, 46, 'vyzývajícího k Bohu, s dovolením Jeho, a jako svítilnu zářící.'),
(3580, 33, 47, 'A oznam věřícím zvěst radostnou o tom, že mají u Boha přízeň velikou!'),
(3581, 33, 48, 'Neposlouchej ani nevěřící, ani pokrytce a nevšímej si urážek jejich! Spolehni se na Boha, vždyť Bůh vskutku postačí jako ochránce!'),
(3582, 33, 49, 'Vy, kteří věříte! Když se oženíte s věřícími ženami a pak se s nimi rozvedete, dříve než jste se jich dotkli, nemusíte jim ukládat čekací dobu, již byste jim počítali. Zaopatřete je dary a propusťte je pěkným propuštěním.'),
(3583, 33, 50, 'Proroku! Dovolili jsme ti manželky tvé, jimž jsi dal obvěnění, a ty, jichž zmocnila se tvá pravice z těch, které ti Bůh dal jako kořist, a dále tvé sestřenice z otcovy strany a tvé sestřenice z matčiny strany, které se s tebou vystěhovaly a každou ženu věřící, jestliže se sama darovala prorokovi, chce-li se s ní prorok oženit - a to je výhradně pro tebe, nikoliv pro ostatní věřící. A víme dobře, co jsme jim již uložili ohledně jejich manželek i těch, jichž zmocnily se pravice jejich, aby na tobě nebylo viny. A Bůh je odpouštějící, slitovný.'),
(3584, 33, 51, 'Odkaž na později tu z nich, kterou chceš, a vezmi k sobě tu, kterou chceš, anebo tu, kterou vyhledáš mezi těmi, které jsi dříve odmítl; a není v tom žádný hřích. A toto je vhodnější pro ochlazení očí jejich, nechť se tedy nermoutí a nechť se radují z toho, cos každé z nich dal. A Bůh dobře ví, co v srdcích je vašich, a Bůh je vševědoucí, blahovolný.'),
(3585, 33, 52, 'A není ti dovoleno po tomto, aby sis vzal jiné ženy ani abys je vyměňoval za manželky jiné, i kdyby se ti líbila krása jejich, kromě těch, jichž zmocnila se pravice tvá. A Bůh na věci všechny dozírá.'),
(3586, 33, 53, 'Vy, kteří věříte! Nevcházejte do domů prorokových, pokud vám to nebude dovoleno, kvůli jídlu, nečekajíce, až bude připravené! Jste-li však vyzváni, vstupte! A když dojíte, rozejděte se a nepouštějte se do důvěrných hovorů, neboť to se dotýká proroka a on se před vámi stydí! Avšak Bůh se nestydí říci pravdu. Když žádáte manželky prorokovy o nějaký předmět, žádejte o to přes závěs, to bude čistší pro srdce vaše i srdce jejich. A není vám dovoleno bolestně se dotýkat posla Božího ani se kdy oženit s manželkami jeho po něm; a to je před Bohem hřích nesmírný.'),
(3587, 33, 54, 'A dáváte-li něco najevo anebo to tajíte, tedy Bůh je věru o každé věci vševědoucí.'),
(3588, 33, 55, 'A není pro manželky prorokovy hříchem, jsou-li viděny otci svými, syny svými, bratry svými, syny bratří svých a syny sester svých nebo ženami jejich a otrokyněmi svými. Nechť jsou bohabojné, vždyť Bůh je věru svědkem věci každé.'),
(3589, 33, 56, 'Bůh i andělé Jeho věru svolávají požehnání na proroka; vy, kteří věříte, modlete se také za něj a svolávejte na něj mír!'),
(3590, 33, 57, 'Věru ty, kdož urážejí Boha a posla Jeho, Bůh prokleje na tomto i onom světě a připraví pro ně trest zahanbující.'),
(3591, 33, 58, 'A ti, kdož urážejí věřící muže a věřící ženy, aniž si to oni zasloužili, ti se obtížili pomluvou i hříchem zjevným.'),
(3592, 33, 59, 'Proroku, řekni manželkám svým, dcerám svým i věřícím ženám, aby přitahovaly k sobě své závoje! A toto bude nejvhodnější k tomu, aby byly poznány a nebyly uráženy. A Bůh je odpouštějící, slitovný.'),
(3593, 33, 60, 'Nepřestanou-li pokrytci a ti, v jejichž srdcích je choroba, a ti, kdož pomluvy šíří v Medíně, věru způsobíme, abys proti nim zakročil a pak již budou jen krátce sousedy tvými.'),
(3594, 33, 61, 'Prokleti budou, kdekoliv se octnou, a budou zajati a bezohledně zabiti'),
(3595, 33, 62, 'podle zvyklosti Boží vůči těm, kdož již předtím byli. A nenalezneš ve zvyklosti Boží změnu žádnou.'),
(3596, 33, 63, 'A lidé se tě dotazují na Hodinu. Odpověz: "Vědění o ní je jedině u Boha. A co ty vůbec víš? Možná že Hodina už je blízká!"'),
(3597, 33, 64, 'Bůh dozajista proklel nevěřící a připravil pro ně plamen šlehající,'),
(3598, 33, 65, 'v němž nesmrtelní budou navěky, a nenaleznou ochránce ani pomocníka žádného'),
(3599, 33, 66, 'v den, kdy tváře jejich se budou v ohni otáčet sem a tam a kdy budou volat: "Kéž bychom byli uposlechli Boha a uposlechli posla!"'),
(3600, 33, 67, 'A řeknou; "Pane náš, poslouchali jsme náčelníky své a velmože své a oni nám dali zbloudit z cesty!'),
(3601, 33, 68, 'Pane náš, dej jim trest dvojnásobný a proklej je prokletím velikým!"'),
(3602, 33, 69, 'Vy, kteří věříte! Nebuďte podobni těm, kdož uráželi Mojžíše! Bůh jej pak zprostil odpovědnosti za to, co hovořili; a byl u Boha ve vážnosti.'),
(3603, 33, 70, 'Vy, kteří věříte! Bojte se Boha a mluvte slova přímá!'),
(3604, 33, 71, 'Bůh pak pro vás zlepší skutky vaše a odpustí vám hříchy vaše. A kdo poslouchá Boha a posla Jeho, ten již dosáhl úspěchu nesmírného.'),
(3605, 33, 72, 'Nabídli jsme břemeno víry nebesům, zemi i horám, ale ony odmítly je nésti a zalekly se ho; a vzal je na sebe člověk, ačkoli je nespravedlivý a nevědomý.'),
(3606, 33, 73, 'Bůh potrestá pokrytce, muže i ženy, a modloslužebníky a modloslužebnice, ale přijme pokání věřících mužů a věřících žen, vždyť Bůh je odpouštějící, slitovný.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Chvála Bohu, jemuž náleží vše, co je na nebesích a na zemi, a jemuž patří chvála i v životě budoucím -On moudrý je i dobře zpravený.'),
(3608, 34, 2, 'On zná, co vniká do země a co z ní vychází a co sestupuje z nebe a co na ně vystupuje - On slitovný je i odpouštějící.'),
(3609, 34, 3, 'Ti, kdož neuvěřili, říkají: "Nepřijde na nás Hodina!" Odpověz: "Ale ano, při Pánu mém, přijde na vás! On zná nepoznatelné a neunikne Mu ani váha zrnka prachu na nebesích a na zemi; a není nic menšího ani většího, aby to nebylo v knize zjevné zapsáno."'),
(3610, 34, 4, 'Hodina přijde, aby mohl odměnit ty, kdož uvěřili a zbožné skutky konali, a těm dostane se odpuštění a přídělu štědrého.'),
(3611, 34, 5, 'Avšak těm, kdož snažili se znemožnit znamení Naše, těm dostane se muk trestu bolestného.'),
(3612, 34, 6, 'A vidí ti, jimž dáno bylo vědění, že to, co Pánem tvým bylo sesláno, je pravdou a vede ke stezce mocného, chvályhodného.'),
(3613, 34, 7, 'A říkají ti, kdož neuvěřili: "Máme vám ukázat muže, jenž vám ohlásí, že až se rozpadnete na kousky, budete znovu stvořeni?'),
(3614, 34, 8, 'Vymyslil si o Bohu lež, či snad je džiny posedlý?" Nikoliv, těm, kdož nevěří v život budoucí, těm připraven je trest a jsou v dalekém bloudění.'),
(3615, 34, 9, 'Což neviděli, co z nebes a ze země je před nimi a za nimi? Kdybychom chtěli, dali bychom je pohltit zemí anebo bychom na ně dali spadnout kusu oblohy. A věru je v tom znamení pro každého služebníka, jenž pokání činí.'),
(3616, 34, 10, 'A darovali jsme kdysi Davidovi milost Svou řkouce: "Hory, pějte s ním slávu Boží, a taktéž vy, ptáci!" A změkčili jsme pro něj železo'),
(3617, 34, 11, 'řkouce: "Vyráběj dokonalá brnění a rozměřuj dobře řetízkové pancíře!" - a konejte zbožné skutky, neboť Já jasně vidím, co činíte.'),
(3618, 34, 12, 'A Šalomounovi jsme podrobili vítr; a ranní vítr vane po měsíc celý a večerní vane rovněž celý měsíc. A dali jsme pro něj vytrysknout prameni bronzu tekutého. A jsou mezi džiny někteří, kdož pro něj pracovali z dovolení Pána jeho; a kdo z nich se odchýlí od rozkazu Našeho, tomu dáme ochutnat trestu plamene šlehajícího.'),
(3619, 34, 13, 'A vyráběli pro něj vše, co si přál: chrámy, sochy, poháry velké jako nádrže a kotle pevně zapuštěné. Buďte rodu Davidovu vděční! Jak málo je však vděčných mezi služebníky Mými!'),
(3620, 34, 14, 'A když jsme rozhodli o smrti Šalomounově, upozornilo je na smrt jeho pouze to, že zvíře pozemské jedno sežralo jeho žezla. A když spadl, ukázalo se, že kdyby džinové znali nepoznatelné, nebyli by tak dlouho zůstali v trestu zahanbujícím.'),
(3621, 34, 15, 'A věru měli obyvatelé Sabá? v sídlech svých znamení: dvě zahrady zprava i zleva. Živte se z přídělu obživy Pána svého a buďte vděční! A je to země výtečná a Pán váš je odpouštějící.'),
(3622, 34, 16, 'Avšak odvrátili se od Nás a poslali jsme na ně záplavu hráze a vyměnili jsme jejich dvě zahrady za jiné dvě s plody hořkými, tamaryšky a s několika málo stromy lotosovými.'),
(3623, 34, 17, 'Takto jsme je odměnili za to, že byli nevděční - což takto odměňujeme někoho jiného než nevděčníky?'),
(3624, 34, 18, 'A mezi nimi a městy, jimž jsme požehnali, jsme umístili města pohledná a rozměřili jsme mezi nimi cesty řkouce: "Cestujte po nich za nocí i za dnů v bezpečí!"'),
(3625, 34, 19, 'Ale oni řekli: "Pane náš, prodluž cesty naše! a tím ukřivdili sami sobě. A učinili jsme je příběhy poučnými a roztrhali jsme je na nespočet kousků. A v tom je věru znamení pro každého trpělivého a vděčného.'),
(3626, 34, 20, 'A ujistil se Iblís, že úsudek jeho a nich byl pravdivý, neboť jej následovali všichni kromě skupiny věřící.'),
(3627, 34, 21, 'Neměli však nad nimi žádné pravomoci a stalo se tak jedině proto, abychom rozeznali ty, kdo v život budoucí uvěřili, od těch, kdo o něm na pochybách byli. A Pán tvůj strážcem je všech věcí.'),
(3628, 34, 22, 'Rci: "Jen si vzývejte ty, o nichž soudíte, že jsou vedle Boha! Vždyť nemají vládu ani nad tím, co má váhu zrnka prachu na nebesích a na zemi, a nemají nijakého podílu ani zde, ani tam a Bůh nemá mezi nimi pomocníka žádného."'),
(3629, 34, 23, 'A nebude platná u Něho přímluva žádná, leda pro toho, komu On to dovolí. A až nakonec strach vyprázdní srdce jejich, řeknou: "Co pravil Pán váš?" a bude jim řečeno: "Pravdu, a On vznešený je i veliký!"'),
(3630, 34, 24, 'Zeptej se: "Kdo uštědřuje vám obživu z nebes a ze země?" Odpověz: "Bůh! A vskutku jsme buďto my, anebo vy na správné cestě či ve zjevném bloudění."'),
(3631, 34, 25, 'Rci: "Nebudete dotazováni na to, jak jsme hřešili my, a my nebudeme dotazováni na to, co jste vy dělali."'),
(3632, 34, 26, 'Rci: "Pán náš všechny nás shromáždí a potom rozhodne mezi námi podle pravdy - a On rozhodčí je i vševědoucí!"'),
(3633, 34, 27, 'Rci: "Ukažte mi ty, které jste Mu přidali jako společníky! Však nikoliv! On jediný je Bůh, mocný a moudrý!"'),
(3634, 34, 28, 'A vyslali jsme tě k lidem všem jen jako hlasatele zvěsti radostné a jako varovatele, avšak většina lidí to neví.'),
(3635, 34, 29, 'A říkají: "Kdy vyplní se tato hrozba, jste-li pravdomluvní?"'),
(3636, 34, 30, 'Odpověz: "Je pro vás stanovena schůzka v den, který ani neuspíšíte, ani nezadržíte ni o hodinu jedinou."'),
(3637, 34, 31, 'Ti, kdož neuvěřili, říkají: "Neuvěříme v Korán tento ani v to, co před ním bylo zjeveno." Kéž bys jen mohl vidět, až nespravedliví před Pána svého budou postaveni a budou se vzájemně napadat slovy. A řeknou ti, kdo byli utištění, těm, kdo domýšliví byli: "Kdyby nebylo vás, věru bychom byli věřícími!"'),
(3638, 34, 32, 'A řeknou ti, kdo byli domýšliví, těm utištěným: "My že jsme vás odvrátili od správného vedení poté, co bylo vám ukázáno? Nikoliv, vy sami jste hříšníci!"'),
(3639, 34, 33, 'Ale ti, kdo utištění byli, vykřiknou na ty, kdo byli domýšliví: "Nikoliv, byly to úklady vaše za noci i za dne, když jste nám nařizovali, abychom v Boha nevěřili a abychom Mu podobné přidávali!" A skryjí lítost svou, až spatří trest. A naložíme okovy na šíje těch, kdož byli nevěřící - a budou snad odměněni za něco, co nedělali?'),
(3640, 34, 34, 'A neposlali jsme do žádného města varovatele, aniž by nebyli řekli ti, kdož v přepychu v něm žili: "My nevěříme v to, s čím jste byli posláni!"'),
(3641, 34, 35, 'A dále říkali: "My hojněji jsme obdařeni bohatstvím i dětmi a nejsme z těch, kdož budou potrestáni!"'),
(3642, 34, 36, 'Rci: "Pán můj v hojnosti obživu uštědřuje, komu chce, a odměňuje ji také, komu chce, avšak většina lidí to neví."'),
(3643, 34, 37, 'Ani vaše bohatství, ani vaše děti nejsou tím, co přiblíží vás k Nám nejvíce, kromě těch, kdož uvěřili a zbožné skutky konali. A takoví budou mít odměnu dvojnásobnou za to, co činili, a v komnatách rajských v bezpečí budou dlít.'),
(3644, 34, 38, 'Ti však, kdož usilovali znemožnit znamení Naše - ti v trest svůj budou uvrženi.'),
(3645, 34, 39, 'Rci: "Pán můj věru v hojnosti uštědřuje obživu, komu chce ze služebníků Svých, a odměřuje ji také, komu chce. A cokoliv rozdáte jako almužnu, On vám to vynahradí a On nejlepší je ze všech uštědřujících."'),
(3646, 34, 40, 'Jednoho dne On všechny shromáždí a potom řekne andělům: "Uctívali vás tihleti?"'),
(3647, 34, 41, 'Odpovědí: "Sláva Tobě, Tys ochránce náš, a nikoli oni! Ba naopak, oni džiny uctívali a mnozí z nich v ně věřili!"'),
(3648, 34, 42, 'A v ten den nebudete si moci navzájem prospět ani uškodit. A řekneme těm, kdož nespravedliví byli: "Ochutnejte trestu ohně, jejž za lež jste pokládali!"'),
(3649, 34, 43, 'Když jsou jim sdělována znamení Naše zřetelná, říkají: "Tohle je jenom člověk, který vás chce vzdálit od toho, co otcové vaši uctívali." A dodávají: "A není to nic jiného než lež vymyšlená!" A říkají ti, kdož neuvěřili, o pravdě, když přišla k nim: "Tohle není nic než kouzelnictví zjevné."'),
(3650, 34, 44, 'A nedali jsme jim dříve Písma žádná, která by studovali, a neposlali jsme k nim před tebou varovatele žádné.'),
(3651, 34, 45, 'A také ti, kdož byli před nimi, to za lež pokládali; a přitom tito nedosáhli ani desetiny toho, co tamtěm jsme dali. A prohlásili za lháře posly Mé a jaká byla nevole Má!'),
(3652, 34, 46, 'Rci: "Napomínám vás jen k jednomu: modlete se k Bohu ve dvou či jednotlivě a potom uvažujte, že druh váš není džiny posedlý, nýbrž že pouze varovatelem vaším je před trestem přísným."'),
(3653, 34, 47, 'Rci: "Nežádám od vás odměnu žádnou - ta je pro vás. A není pro mne odměny kromě u Boha a On svědkem je všech věcí."'),
(3654, 34, 48, 'A rci: "Pán můj věru sesílá jen pravdu a On dobře zná nepoznatelné."'),
(3655, 34, 49, 'Rci: "Přišla pravda, a nicotnost se již neobjeví ani nevrátí."'),
(3656, 34, 50, 'Rci: "Jestliže jsem zbloudil, tedy jsem zbloudil jen ke škodě své, jestliže však jdu správnou cestou, jest to z vnuknutí Pána mého - a On věru je slyšící i blízký."'),
(3657, 34, 51, 'Kéž bys mohl vidět, jak budou vděční, bez možnosti úniku, až budou uchváceni z místa blízkého!'),
(3658, 34, 52, 'A budou volat: "Uvěřili jsme v něj!" Ale jak by mohli dosáhnout spásy z místa tak dalekého,'),
(3659, 34, 53, 'když v něj již dříve neuvěřili a zabývali se dohady o nepoznatelném z místa dalekého?'),
(3660, 34, 54, 'A vstoupí překážka mezi ně a to, po čem touží, jak učiněno bylo již dříve s jim podobnými, kteří zmítáni byli pochybnostmi hlubokými!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Chvála Bohu, stvořiteli nebes a země, který učinil anděly vyslanci majícími dva, tři či čtyři páry křídel - a On přidává tvorstvu svému, cokoliv chce, a Bůh je všech věcí mocen.'),
(3662, 35, 2, 'Cokoliv Bůh otevře lidem z milosti Své, to nikdo jim nemůže zadržet, však cokoliv On zadrží, to nikdo nemůže poslat kromě Něho. A On mocný je i moudrý.'),
(3663, 35, 3, 'Lidé, pomněte dobrodiní Božího vůči vám! Je snad nějaký stvořitel kromě Boha, který uštědřuje vám obživu z nebes i ze země? A není božstva kromě Něho - jak mohli jste se tak odvrátit?'),
(3664, 35, 4, 'Jestliže tě prohlašují za lháře, tedy již poslové před tebou byli za ně prohlašováni; a k Bohu budou věci všechny navráceny.'),
(3665, 35, 5, 'Lidé, slib Boží je věru pravdivý, nechť vás tedy neoklame život pozemský a nechť vás neoklame o Bohu podvodník žádný.'),
(3666, 35, 6, 'Satan je věru vaším nepřítelem, proto jej pokládejte i vy za nepřítele svého! On volá k sobě přívržence své jen proto, aby stali se obyvateli plamene šlehajícího.'),
(3667, 35, 7, 'Pro ty, kdož neuvěřili, určen je trest přísný, zatímco ty, kdož uvěřili a zbožné skutky konali, čeká odpuštění odměna veliká.'),
(3668, 35, 8, 'Zdaž ten, jemuž byly špatné skutky jeho okrášleny, takže je vidí jako dobré...? Bůh dává zbloudit, komu chce, a vede správnou cestou také, koho chce. Nechť duše tvá se nepoddává zármutku nad nimi, vždyť Bůh dobře ví, co dělají.'),
(3669, 35, 9, 'Bůh je ten, jenž vysílá vítr, aby zdvihal mraky; a My je ženeme nad mrtvou oblast a oživujeme jimi zemi po smrti její. A podobně tomu bude se vzkříšením.'),
(3670, 35, 10, 'Kdo si přeje moci... vždyť jedině Bohu náleží moc veškerá; k Němu stoupají slova dobrá a On skutek zbožný k sobě pozvedá. Pro ty, kdož úklady zlé chystají, připraven je trest přísný a úklady jich se vniveč obrátí.'),
(3671, 35, 11, 'Bůh vás stvořil z prachu, potom z kapky semene a posléze z vás učinil pohlaví dvojí. A nechodí samice žádná s útěžkem ani neslehne, aniž by o tom On věděl; a neprodlouží se život dlouhověkému a není zkrácen život žádnému, aniž by to bylo v Knize zaznamenáno; a to vše je Bohu velmi snadné:'),
(3672, 35, 12, 'A nejsou si obě velké vody rovné: jedna je sladká, čerstvá a příjemná k pití, zatímco druhá je slaná a hořká; z obou pak pojídáte čerstvé maso a lovíte z nich ozdoby, jež nosíte. A vidíš na nich lodě brázdící vlny, abyste na nich mohli hledat část z přízně Boží - a snad budete vděčni.'),
(3673, 35, 13, 'On dává vniknout noci do dne a dni do noci a podmanil si slunce a měsíc a obě tělesa se pohybují k lhůtě stanovené. Takový je Bůh, Pán váš, jemuž patří moc královská, zatímco ti, které vzýváte vedle Něho, nemají moc ani nad slupkou pecky datlové.'),
(3674, 35, 14, 'Jestliže se k nim modlíte, oni neslyší vzývání vaše, a i kdyby je slyšeli, nemohli by vás vyslyšet. A v den zmrtvýchvstání zapřou vaše přidružování jich k Bohu. A nikdo tě o tom nemůže poučit tak jako ten, jenž o všem je zpraven.'),
(3675, 35, 15, 'Lidé, vy chudáci jste na Boha odkázaní, zatímco On soběstačný je a chvályhodný.'),
(3676, 35, 16, 'Kdyby chtěl, dal by vám zaniknout a nahradil by vás tvorstvem novým,'),
(3677, 35, 17, 'a není to pro Boha ničím obtížným.'),
(3678, 35, 18, 'Žádná duše hříchy obtížená neponese břemeno duše jiné; a bude-li pak obtížená duše prosit, aby jí bylo neseno břímě její, nebude jí z něho nic ubráno, i kdyby to byl příbuzný. Ty můžeš varovat pouze ty, kdož Pána svého se obávají kvůli nepoznatelnému a dodržují modlitbu; kdokoliv se almužnou očišťuje, ten očišťuje se ve prospěch duše vlastní, a u Boha je cíl konečný.'),
(3679, 35, 19, 'Nelze srovnávat slepého a vidoucího,'),
(3680, 35, 20, 'ani temnotu a světlo,'),
(3681, 35, 21, 'ani stín a horko!'),
(3682, 35, 22, 'A nelze srovnávat živé a mrtvé. Bůh dává slyšet, komu chce, ale ty nedokážeš, aby slyšeli ti, kdo jsou v hrobech,'),
(3683, 35, 23, 'vždyť tys jen varovatelem.'),
(3684, 35, 24, 'A poslali jsme tě s pravdou a jako hlasatele zvěsti radostné i jako varovatele; a není národa jediného, k němuž by nebyl přišel varovatel.'),
(3685, 35, 25, 'Jestliže tě prohlašují za lháře, tedy již ti, co byli před nimi, prohlašovali posly své za lháře, i když přišli k nim s důkazy jasnými a Písmy i Knihou osvícenou.'),
(3686, 35, 26, 'A potom jsem uchvátil ty, kdož nevěřili a jaká byla nevole Má?'),
(3687, 35, 27, 'Což nevidíš, jak Bůh z nebe vodu sesílá, jejíž pomocí dáváme vyrůst plodům rozličných druhů? A na horách jsou pruhy bílé a červené a různobarevné i temně černé.'),
(3688, 35, 28, 'A také mezi lidmi, zvířaty a dobytkem jsou druhy rozličné. Tak tomu je! A obávají se Boha ze služebníků Jeho ti nejmoudřejší a Bůh věru je mocný a odpouštějící.'),
(3689, 35, 29, 'Ti, kdož přednášejí Písmo Boží, dodržují modlitbu a dávají almužnu skrytě i veřejně z toho, co jsme jim uštědřili, ti mají naději na obchod, jenž nebude nevýnosný,'),
(3690, 35, 30, 'aby On jim mohl dát v plnosti odměnu jejich a přidat jim k tomu z dobrodiní Svého. A On odpouštějící je i uznalý za vděčnost.'),
(3691, 35, 31, 'To, co jsme ti vnukli z Písma, je pravda potvrzující to, co již předtím bylo sesláno. A Bůh je dobře zpraven o služebnících svých a jasně je vidí.'),
(3692, 35, 32, 'Potom jsme učinili dědici Písma ty, jež jsme si vyvolili mezi služebníky Svými. A jsou mezi nimi ti, kdož sami sobě ukřivdili, jiní, kteří jsou uprostřed, a další, kteří se předstihují v dobrých skutcích z dovolení Božího. A to je přízeň veliká:'),
(3693, 35, 33, 'v zahradách Edenu, do nichž vstoupí, budou ozdobeni náramky zlatými a perlami a jejich oděvy tam budou hedvábné.'),
(3694, 35, 34, 'A budou říkat: "Chvála Boha, jenž od nás zármutek vzdálil! Pán náš věru je odpouštějící a za vděčnost uznalý,'),
(3695, 35, 35, 'On je ten, který nás z milosti své ubytoval v příbytku trvalém, v němž nedotkne se nás námaha žádná a nedotkne se nás v něm ani únava."'),
(3696, 35, 36, 'Pro ty, kdož neuvěřili, připraven je oheň pekelný a nebude jim souzeno, aby zemřeli, a nebude jim v něm trest jejich ulehčen a takto odměňujeme každého nevěřícího.'),
(3697, 35, 37, 'A budou tam křičet: "Pane náš, vyveď nás odtud a my budeme dobré skutky konat, nikoliv to, co dříve jsme dělali!" "Což nedarovali jsme vám věk dlouhý, aby ten, kdo uvažuje, si to mohl uvážit? A nepřišel k vám varovatel? Okuste tedy trestu! A nespravedliví nebudou mít pomocníka žádného!'),
(3698, 35, 38, 'Bůh dobře zná nepoznatelné na nebi i na zemi a On dobře ví, co hrudi skrývají.'),
(3699, 35, 39, 'A jest to On, jenž učinil vás nástupci na zemi. A kdo neuvěřil, toho nevíra se obrátí proti němu. A nevíra nevěřících jen rozmnožuje pro ně nenávist u Pána jejich a nevíra jejich zvětší pro nevěřící jen jejich zatracení.'),
(3700, 35, 40, 'Rci: "Viděli jste společníky své, jež vedle Boha vzýváte? Ukažte mi tedy, co stvořili na zemi či jaký mají podíl na nebi anebo zda dali jsme jim Písmo nějaké, aby opřít se mohli o jasný důkaz z něho?" Nikoliv, nespravedliví si vzájemně slibují jen klam!'),
(3701, 35, 41, 'Bůh věru pevně třímá nebesa a zemi, aby se nesesuly; kdyby se sesuly, pak by je po Něm nikdo jiný nemohl udržet, a On soucitný je a odpouštějící.'),
(3702, 35, 42, 'A přísahali při Bohu přísahami nejslavnostnějšími, že přijde-li k nim varovatel, věru se dají cestou správnější než kterýkoliv národ jiný. Když však k nim přišel varovatel, tu zvětšil se jen odpor jejich'),
(3703, 35, 43, 'následkem pýchy jejich na zemi a úkladů zlovolných. Však úklady zlovolné obklíčí jedině ty, kdož je chystají. Což mohou očekávat něco jiného než obvyklý osud národů dávných? A nenalezneš ve zvyklosti Boží změnu žádnou! A nenalezneš ve zvyklosti Boží odchylku žádnou!'),
(3704, 35, 44, 'Což necestovali po zemi a neviděli, jaký byl konec těch, kdo před nimi žili a kdo mocnější než oni silou byli? Není na nebesích ani na zemi ničeho, co oslabit by mohlo Boha, a On věru je vševědoucí a všemocný.'),
(3705, 35, 45, 'Kdyby Bůh chtěl ztrestat lidi za to, co si vysloužili, nezůstala by na povrchu zemském žádná bytost živá, avšak On dává jim odklad do lhůty stanovené. A až se dostaví lhůta jejich... vždyť Bůh vidí jasně služebníky Své.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Já sín.'),
(3707, 36, 2, 'Při Koránu moudrém,'),
(3708, 36, 3, 'tys vskutku z vyslanců jedním,'),
(3709, 36, 4, 'po stezce přímé jdoucím,'),
(3710, 36, 5, 'mocným a slitovným seslaném,'),
(3711, 36, 6, 'abys varoval lid, jehož otcové varováni nebyli, takže jsou lhostejní.'),
(3712, 36, 7, 'A uskutečnilo se již slovo Naše nad většinou z nich - však nevěří oni.'),
(3713, 36, 8, 'A vložili jsme na šíje jejich řetězy až k bradám sahající, takže hlavy zvednuty mají,'),
(3714, 36, 9, 'a před ně i za ně jsme přehradu umístili a přikryli jsme je, takže nevidí.'),
(3715, 36, 10, 'A je pro ně jedno, zda varuješ je či nikoliv - beztak neuvěří.'),
(3716, 36, 11, 'Varovat můžeš jenom ty, kdož připomenutí následují a Milosrdného se kvůli nepoznatelnému bojí; těm oznam zvěst radostnou o odpuštění a odměně hojné!'),
(3717, 36, 12, 'A jsme to My, kdo mrtvé oživujeme a to, co dříve učinili, i to, co po nich zbude, zapisujeme. A každou věc jsme spočítali v hlavní Knize zjevné.'),
(3718, 36, 13, 'A uveď jim podobenství o obyvatelích města, k nimž přišli vyslanci Boží:'),
(3719, 36, 14, 'Hle, vyslali jsme k nim dva posly, však za lháře je prohlásili, a tehdy jsme je třetím posílili a oni pravili: "Jsme k vám vysláni."'),
(3720, 36, 15, 'I řekli: "Vy jen smrtelníci jste nám podobní a Milosrdný nic neseslal. Vy nejste leč lháři pouzí."'),
(3721, 36, 16, 'Odpověděli: "Pán náš ví, že jsme k vám vysláni,'),
(3722, 36, 17, 'a uloženo nám je toliko zřetelné oznámení."'),
(3723, 36, 18, 'Řekli: "Viděli jsme o vás špatnou ptáků věštbu. Jestliže nepřestanete, věru vás ukamenujeme a stihne vás od nás trest bolestný."'),
(3724, 36, 19, 'Zvolali: "Vaše věštba nechť na vás padne! Kéž byste se vzpamatovali! Však nikoliv, vždyť vy lid jste prostopášný."'),
(3725, 36, 20, 'I přiběhl z druhého konce města muž nějaký a zvolal: "Lide můj, následujte vyslance Boží,'),
(3726, 36, 21, 'následujte ty, kdož od vás odměnu žádnou nežádají a správné cesty se přidržují!'),
(3727, 36, 22, 'Proč neměl bych uctívat toho, jenž stvořil mne a k němuž i vy budete navráceni?'),
(3728, 36, 23, 'Vezmu si snad kromě Něho nějaká božstva jiná? Jestliže Milosrdný mi bude chtít uškodit, přímluva jejich mi nebude nic platná a oni mne nezachrání.'),
(3729, 36, 24, 'A byl bych tedy věru v zjevném bloudění.'),
(3730, 36, 25, 'Poslové, já uvěřil jsem v Pána vašeho! A slyšte mne, vy pohani!"'),
(3731, 36, 26, 'A bylo mu řečeno: "Vejdi do ráje!" I zvolal: "Kéž by lid můj věděl'),
(3732, 36, 27, 'o tom, že Pán můj mi odpustil a jedním z poctěných mě učinil."'),
(3733, 36, 28, 'A po jeho smrti jsme na jeho lid vojsko žádné z nebe neposlali ani žádnou jinou pohromu jsme neseslali'),
(3734, 36, 29, 'a stačilo vykřiknutí jediné, a hle, životy jejich zhasly!'),
(3735, 36, 30, 'Ó, jak jsou služebníci Moji k politování! Vždyť nepřišel k nim posel žádný, aby se mu neposmívali.'),
(3736, 36, 31, 'Což nevidí, kolika pokolením před nimi jsme zahynout dali a že k nim nebudou navráceni,'),
(3737, 36, 32, 'ale že všichni vespolek u Nás budou shromážděni?'),
(3738, 36, 33, 'A je pro ně znamením země mrtvá, již oživujeme a z níž dáváme vyrašit obilí, jež je jim potravou.'),
(3739, 36, 34, 'A umístili jsme na ní háje palmové i vinice a dali jsme pramenům prýštit na ní,'),
(3740, 36, 35, 'aby pojídali z plodů Pána svého i z toho, co jejich ruce vytvořili - což vděční nebudou?'),
(3741, 36, 36, 'Sláva tomu, jenž stvořil v párech všechny druhy, jež ze země vyrůstají, a stvořil i je samé i to, co ještě neznají.'),
(3742, 36, 37, 'A je pro ně znamením i noc; z ní den odlupujeme, a hle, v temnotě se nacházejí.'),
(3743, 36, 38, 'A slunce ubíhá k místu odpočinku svého, a takové je ustanovení vševědoucího, mocného.'),
(3744, 36, 39, 'A pro měsíc jsme příbytky stanovili, pokud se nevrátí podoben staré větvi palmové.'),
(3745, 36, 40, 'A nepřísluší slunci, aby dohonilo měsíc, a noci, aby předhonila den a všechno pluje ve sféře nebeské.'),
(3746, 36, 41, 'A je pro ně znamením, že nesli jsme potomstvo jejich v arše plně naložené'),
(3747, 36, 42, 'a že stvořili jsme pro ně lodě, na nichž plují, jí podobné.'),
(3748, 36, 43, 'A kdybychom chtěli, utopili bychom je a nedostalo by se jim pomoci žádné a zachráněni by nebyli,'),
(3749, 36, 44, 'leda z milosrdenství Našeho a pro dočasné světa tohoto užívání.'),
(3750, 36, 45, 'A když je jim řečeno: "Bojte se toho, co je před vámi, a toho, co je za vámi - snad se vám dostane slitování," (jsou lhostejní).'),
(3751, 36, 46, 'A nepřijde k nim jediné znamení ze znamení Pána jejich, aby se od něho neodvrátili.'),
(3752, 36, 47, 'A když je jim řečeno: "Přispívejte z toho, co Bůh vám uštědřil," odpovídají nevěřící těm, kdož uvěřili: "Máme snad krmit toho, koho by živil Bůh, kdyby chtěl? A vy jste přece jen ve zjevném bloudění."'),
(3753, 36, 48, 'Tak ptají se: "A kdy vlastně se ta hrozba splní, jste-li pravdomluvní?"'),
(3754, 36, 49, 'A nečeká je nic než vykřiknutí jediné, které je uchvátí, zatímco se budou přít,'),
(3755, 36, 50, 'a nebudou schopni ani závěť učinit, ani k rodině své se navrátit.'),
(3756, 36, 51, 'A pak bude zatroubeno na pozoun, a hle, ze svých hrobů budou k Pánu svému mířit.'),
(3757, 36, 52, 'A řeknou: "Běda nám! Kdo nás to probudil z místa odpočinku našeho? A toto je to, čím hrozil nám Milosrdný, a pravdu poslové Jeho hovořili!"'),
(3758, 36, 53, 'A bude to věru jen vykřiknutí jediné, a hle, všichni budou před Nás předvedeni!'),
(3759, 36, 54, 'V ten den nebude ukřivděno v ničem duši žádné a odměněni budete jen za to, co skutečně jste činili.'),
(3760, 36, 55, 'V ten den budou obyvatelé ráje radovánkami zaměstnáni'),
(3761, 36, 56, 'a oni i manželky jejich ve stínu na poduškách spočinou'),
(3762, 36, 57, 'a tam ovoce a vše, oč požádají, míti budou.'),
(3763, 36, 58, '"Mír s vámi" - tak slovy Pána slitovného pozdraveni budou.'),
(3764, 36, 59, '"A vy hříšníci, vy dnes držte se stranou!"'),
(3765, 36, 60, 'Což jsem vám v úmluvě neuložil, synové Adamovi: neuctívejte satana, jenž vám je nepřítelem zjevným,'),
(3766, 36, 61, 'nýbrž uctívejte jenom Mne? Toto je stezka přímá!'),
(3767, 36, 62, 'A dal satan již zbloudit množství početnému z vás. Což rozum žádný jste neměli?'),
(3768, 36, 63, 'Toto je peklo, jež bylo vám přislíbeno'),
(3769, 36, 64, 'hořte v něm dnes za to, že jste neuvěřili!'),
(3770, 36, 65, 'Dnes ústa jejich zapečetíme, však budou mluvit jejich ruce a svědčit jejich nohy o tom, čeho ze špatnosti nabyli.'),
(3771, 36, 66, 'A kdybychom chtěli, vymazali bychom oči jejich a o závod hnali by se ke stezce; ale jak mohli by ji spatřit?'),
(3772, 36, 67, 'Kdybychom chtěli, na místě bychom je proměnili, takže by nebyli schopni ani kupředu jít, ani zpět se navrátit.'),
(3773, 36, 68, 'Komu dlouhý věk dáme, toho ohneme v jeho postavě, - což nepochopí to oni?'),
(3774, 36, 69, 'A nenaučili jsme jej básnění a nehodí se to proň, vždyť toto je pouze připomenutí a Korán zjevný,'),
(3775, 36, 70, 'aby varován byl jím ten, kdo živý je, a aby se uskutečnilo slovo nad nevěřícími.'),
(3776, 36, 71, 'Což neviděli, že jsme pro ně stvořili - mezi tím, co ruce Naše udělali - též dobytek, nad nímž vládci jsou?'),
(3777, 36, 72, 'A podmanili jsme jej pro ně a na jedněch jezdí a jiné jsou jim potravou'),
(3778, 36, 73, 'a mají z něho užitek různý a také nápoje - což vděční Nám nebudou?'),
(3779, 36, 74, 'A vzali si pro sebe místo Boha božstva jiná doufajíce, že snad jim pomohou,'),
(3780, 36, 75, 'však ta jim pomoci nemohou, i když myslí si, že vojskem připraveným pro ně jsou.'),
(3781, 36, 76, 'Nechť tě nermoutí řeči jejich; vždyť My dobře známe, co oni skrývají a co najevo dávají.'),
(3782, 36, 77, 'Což člověk neviděl, že jsme jej z kapky semene stvořili? A hle, on Nám je odpůrcem zjevným nyní,'),
(3783, 36, 78, 'a dokonce Nám podobenství uvádí zapomínaje na své stvoření a hovoří: "Kdo kosti oživí, když již zpráchniví?"'),
(3784, 36, 79, 'Odpověz: "Oživí je ten, kdo poprvé je stvořil a jenž dobře o každém stvoření ví,'),
(3785, 36, 80, 'jenž pro vás ze stromu zeleného oheň učinil, takže si z něho zapalujete."'),
(3786, 36, 81, 'Což není ten, jenž stvořil nebesa a zemi, schopen stvořit jim podobné? Však ano, vždyť On stvořitel je, vševědoucí.'),
(3787, 36, 82, 'A když něčeho si přeje, je rozkaz Jeho pouze: "Staniž se!" a stane se.'),
(3788, 36, 83, 'Sláva tomu, v jehož rukou je panství nade věcmi všemi a k němuž budete navráceni!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Při těch, kdo v řadách stoji,'),
(3790, 37, 2, 'prudce odrážejí,'),
(3791, 37, 3, 'připomenutí přednášejí!'),
(3792, 37, 4, 'Věru Bůh váš jediný jest,'),
(3793, 37, 5, 'Pán nebes a země a toho, co mezi nimi je, Pán východů!'),
(3794, 37, 6, 'Vskutku jsme vyzdobili nejnižší nebe ozdobou hvězd'),
(3795, 37, 7, 'a také na ochranu proti každému satanu vzpurnému,'),
(3796, 37, 8, 'takže nemohou u rady nejvyšší naslouchat a je po nich házeno ze všech stran,'),
(3797, 37, 9, 'aby byli odehnáni; a pro ně trest trvalý je přichystán.'),
(3798, 37, 10, 'Zaslechne něco pouze ten, kdo urve porady útržek, a ten plamenem pronikajícím je pronásledován.'),
(3799, 37, 11, 'Zeptej se jich, zda jsou tělesně silnější nebo ti, které jsme stvořili My? My stvořili jsme je z hlíny tvrdnoucí.'),
(3800, 37, 12, 'Ba věru, ty divíš se, zatímco oni se posmívají,'),
(3801, 37, 13, 'a když napomenutí je jim dáno, na ně nepamatují,'),
(3802, 37, 14, 'a když znamení spatří, žerty si z něho dělají'),
(3803, 37, 15, 'a říkají: "Tohle není leč kouzlo zjevné!'),
(3804, 37, 16, 'Cožpak až zemřeme a prachem a kostmi se staneme, vskutku vzkříšeni budeme,'),
(3805, 37, 17, 'i naši předkové dávní?"'),
(3806, 37, 18, 'Odpověz: "Zajisté, a budete poníženi!"'),
(3807, 37, 19, 'A ozve se jen jeden zvuk jediný, a hle, oni prohlédnou'),
(3808, 37, 20, 'a řeknou: "Běda nám, toto je den soudný!"'),
(3809, 37, 21, 'Ano, toto je den rozhodnutí, jejž za výmysl jste pokládali!'),
(3810, 37, 22, 'Shromážděte ty, kdož křivdili, manželky jejich i to, co uctívali'),
(3811, 37, 23, 'vedle Boha, a doveďte je na stezku pekelnou,'),
(3812, 37, 24, 'pak zastavte je, neb vyslýcháni budou!'),
(3813, 37, 25, '"Co je s vámi, že nepomáháte si navzájem?"'),
(3814, 37, 26, 'Naopak, oni se dnes Bohu vzdávají'),
(3815, 37, 27, 'a jeden k druhému se obracejí a vyptávají se.'),
(3816, 37, 28, 'I řeknou jedni: "Vy věru jste k nám zprava přicházeli!"'),
(3817, 37, 29, 'A odvětí jim: "Nikoliv, vy jste nevěřili'),
(3818, 37, 30, 'a my nad vámi jsme žádnou pravomoc neměli; naopak, vy lidé svévolní jste byli.'),
(3819, 37, 31, 'A uskutečnilo se nad námi slovo Pána našeho a zajisté teď ochutnáme... "'),
(3820, 37, 32, 'My jsme vás svedli, však sami jsme bloudili.'),
(3821, 37, 33, 'V ten den věru budou v trestu společníky'),
(3822, 37, 34, 'a takovým způsobem my jednat budeme s hříšníky,'),
(3823, 37, 35, 'kteří, když řečeno jim bylo, že není božstva kromě Boha, zpyšněli'),
(3824, 37, 36, 'a řekli: "Kvůli básníku bláznivému bychom božstva svá opustit měli?"'),
(3825, 37, 37, 'Naopak, on pravdu přinesl a potvrdil pravdivost poslů dřívějších.'),
(3826, 37, 38, 'A vy jistě trest bolestný ochutnáte'),
(3827, 37, 39, 'a pouze za to, co spáchali jste, odměnu dostanete'),
(3828, 37, 40, 'kromě služebníků Božích upřímných.'),
(3829, 37, 41, 'Těm dostane se přídělu známého,'),
(3830, 37, 42, 'ovoce v hojnosti a místa čestného,'),
(3831, 37, 43, 'v zahradách rajských slastí'),
(3832, 37, 44, 'ležet budou proti sobě na lůžkách.'),
(3833, 37, 45, 'Kolovat mezi nimi budou číše čirého nápoje'),
(3834, 37, 46, 'jasného, jenž rozkoší pro pijící je,'),
(3835, 37, 47, 'v něm opojnosti není a nebudou jím zmoženi.'),
(3836, 37, 48, 'Vedle nich budou dívky zrak klopící, okaté,'),
(3837, 37, 49, 'jako by to perly byly skryté.'),
(3838, 37, 50, 'A přiblíží se k sobě navzájem a bude se vyptávat jeden druhého,'),
(3839, 37, 51, 'a řekne jeden z nich: "Měl jsem přítele dobrého,'),
(3840, 37, 52, 'jenž říkával:, Patříš také mezi ty, kdo za pravdivé prohlašují varování,'),
(3841, 37, 53, 'že až zemřeme a staneme se prachem a kostmi, i před soud budeme postaveni?"'),
(3842, 37, 54, 'A řekne: "Nechcete se tam vzhůru podívat?"'),
(3843, 37, 55, 'A sám vzhůru pohlédne a spatří přítele svého uprostřed pekla stát,'),
(3844, 37, 56, 'i zavolá naň: "Při Bohu, málem jsi mne zahubil,'),
(3845, 37, 57, 'a kdyby nebylo dobrodiní Pána mého, také bych mezi předvedenými byl!'),
(3846, 37, 58, 'Což vskutku již nezemřeme,'),
(3847, 37, 59, 'jen touto svou smrtí první, a potrestáni nebudeme?'),
(3848, 37, 60, 'To věru je štěstí nesmírné!"'),
(3849, 37, 61, 'Nechť k něčemu podobnému se upíná úsilí!'),
(3850, 37, 62, 'A co je lepší: toto, anebo strom Zaqqúm jako pohoštění?'),
(3851, 37, 63, 'A učinili jsme jej pro nespravedlivé předmětem pokušení,'),
(3852, 37, 64, 'a je to strom, jenž vyrůstá z pekla základů,'),
(3853, 37, 65, 'a jeho plody jsou jak hlavy satanů'),
(3854, 37, 66, 'a věru z nich budou jíst a břicha svá si jimi naplní'),
(3855, 37, 67, 'a pak je směsí vody vroucí zapijí'),
(3856, 37, 68, 'a posléze se věru do pekla navrátí.'),
(3857, 37, 69, 'A předky své nalezli vskutku v bloudění,'),
(3858, 37, 70, 'a přece sami po jejich stopách spěchají.'),
(3859, 37, 71, 'Již před nimi zbloudila většina národů starých,'),
(3860, 37, 72, 'ačkoliv jsme k nim varovatele posílali.'),
(3861, 37, 73, 'Pohleď však, jaký byl konec varovaných'),
(3862, 37, 74, 'kromě služebníků Božích upřímných!'),
(3863, 37, 75, 'O pomoc Nás kdys Noe zavolal a jak výtečně prosby splňujeme'),
(3864, 37, 76, 'a před pohromou nesmírnou jsme jej i s rodinou jeho zachránili;'),
(3865, 37, 77, 'a učinili jsme jeho potomky jedinými, kdož přežili,'),
(3866, 37, 78, 'a u posledních jsme mu požehnali:'),
(3867, 37, 79, '"Mír budiž s Noem mezi vším lidstvem!"'),
(3868, 37, 80, 'Takto odměňujeme dobro konající,'),
(3869, 37, 81, 'vždyť on byl služebník Náš věřící!'),
(3870, 37, 82, 'A potom jsme všechny ostatní utopili.'),
(3871, 37, 83, 'K jeho přívržencům také Abraham náležel,'),
(3872, 37, 84, 'když k Pánu svému se srdcem čistým přišel'),
(3873, 37, 85, 'a otci svému a lidu svému pravil: "Co to uctíváte?'),
(3874, 37, 86, 'Což falešná božstva místo Boha si žádáte?'),
(3875, 37, 87, 'A co o Pánu lidstva soudíte?"'),
(3876, 37, 88, 'A pohled svůj ke hvězdám upřel'),
(3877, 37, 89, 'a zvolal: "Věru jsem ochořel!"'),
(3878, 37, 90, 'I odvrátili se od něho, záda mu ukazujíce.'),
(3879, 37, 91, 'Abraham pak vnikl k božstvům jejich a řekl: "Co že nejíte'),
(3880, 37, 92, 'a co je s vámi, že nemluvíte?"'),
(3881, 37, 93, 'A vrhl se na ně, zasazuje jim úder pravicí,'),
(3882, 37, 94, 'však přiblížili se k němu lidé běžící.'),
(3883, 37, 95, 'I otázal se: "Chcete uctívat, co sami jste vytesali,'),
(3884, 37, 96, 'zatímco je to Bůh, jenž stvořil vás i to, co jste udělali?"'),
(3885, 37, 97, 'I zvolali: "Postavte pro něj hranici a vhoďte jej v oheň hořící!"'),
(3886, 37, 98, 'Takto mu chtěli úklad nastrojit, však poníženými jsme je učinili.'),
(3887, 37, 99, 'I pravil Abraham: "K Pánu svému půjdu, aby mne správně řídil."'),
(3888, 37, 100, '"Pane, dejž mi syna, jenž patřit bude mezi bezúhonné!"'),
(3889, 37, 101, 'A oznámili jsme mu radostnou zvěst o chlapci povahy mírné.'),
(3890, 37, 102, 'A když dospěl do věku, kdy s otcem se mohl podílet na úsilí, řekl Abraham: "Synáčku, viděl jsem ve snu, že tě mám obětovat; uvaž a řekni, jaké mínění máš?" I odvětil: "Otče můj, učiň, co ti bylo poručeno; a bude-li Bůh chtít, mne věru neochvějným shledáš."'),
(3891, 37, 103, 'A když se oba do vůle Boží odevzdali a on položil ho čelem k zemi,'),
(3892, 37, 104, 'zvolali jsme naň: "Abrahame,'),
(3893, 37, 105, 'věru jsi již prokázal, že vidění jsi za pravdu měl - a takto ty, kdož dobré činí, odměňujeme -'),
(3894, 37, 106, 'vždyť byla to jen zjevná zkouška!"'),
(3895, 37, 107, 'A velkou obětí jsme syna jeho vykoupili'),
(3896, 37, 108, 'a u posledních jsme mu požehnali:'),
(3897, 37, 109, '"Mír budiž s Abrahamem!"'),
(3898, 37, 110, 'Takto odměňujeme dobro konající,'),
(3899, 37, 111, 'vždyť on byl služebník Náš věřící!'),
(3900, 37, 112, 'A oznámili jsme mu radostnou zvěst o Izákovi, jenž prorok byl, k bezúhonným patřící.'),
(3901, 37, 113, 'Pak požehnali jsme jemu i Izákovi; a mezi jejich potomky jsou někteří, kdož dobro konají, však jiní sami na sobě křivdu zjevnou páchají.'),
(3902, 37, 114, 'A dobrodiním jsme také Mojžíše a Áróna zahrnuli'),
(3903, 37, 115, 'a před pohromou nesmírnou jsme je oba i lid jejich zachránili'),
(3904, 37, 116, 'a pomoc jsme jim poskytli, takže vítěznými se stali,'),
(3905, 37, 117, 'a Písmo nanejvýš zřetelné jsme jim darovali'),
(3906, 37, 118, 'a oba po stezce přímé jsme vedli'),
(3907, 37, 119, 'a u posledních jsme jim požehnali:'),
(3908, 37, 120, '"Mír budiž s Mojžíšem a Árónem!"'),
(3909, 37, 121, 'Takto odměňujeme dobro konající,'),
(3910, 37, 122, 'vždyť oba patří mezi služebníky Naše věřící!'),
(3911, 37, 123, 'Také Eliáš byl z vyslanců Božích,'),
(3912, 37, 124, 'když k lidu svému pravil: "Což bohabojní nebudete, a'),
(3913, 37, 125, 'Baala vzývat chcete a nejlepšího ze stvořitelů opustíte,'),
(3914, 37, 126, 'Boha, Pána svého i Pána vašich předků dávných?"'),
(3915, 37, 127, 'Však za lháře ho prohlásili, a věru budou předvedeni'),
(3916, 37, 128, 'kromě těch, kdož služebníky Božími upřímnými byli.'),
(3917, 37, 129, 'A u posledních jsme mu požehnali:'),
(3918, 37, 130, '"Mír budiž s Eliášem!"'),
(3919, 37, 131, 'Takto odměňujeme dobro konající,'),
(3920, 37, 132, 'vždyť on byl služebník Náš věřící!'),
(3921, 37, 133, 'A také Lot byl vskutku mezi Božími vyslanci,'),
(3922, 37, 134, 'když jsme jej i rodinu jeho celou zachránili'),
(3923, 37, 135, 'kromě stařeny jedné, jež zůstala s opozdilci,'),
(3924, 37, 136, 'a potom jsme všechny ostatní zahubili.'),
(3925, 37, 137, 'A věru kolem měst jejich za jitra procházíte'),
(3926, 37, 138, 'i za noci; což to nepochopíte?'),
(3927, 37, 139, 'A rovněž Jonáš jedním z vyslanců Božích byl,'),
(3928, 37, 140, 'když na loď naloženou se uchýlil'),
(3929, 37, 141, 'a vrhal los a byl tím, jenž ztratil,'),
(3930, 37, 142, 'a pohltila jej ryba, zatímco pokárání zasloužil.'),
(3931, 37, 143, 'A kdyby nebyl z těch, kdož Boha oslavují,'),
(3932, 37, 144, 'byl by jistě v břiše jejím zůstal až do dne vzkříšení.'),
(3933, 37, 145, 'A vyvrhli jsme jej chorého na břeh pustý'),
(3934, 37, 146, 'a dali jsme nad ním vyrůst keři tykvovému.'),
(3935, 37, 147, 'Potom jsme jej ke sto tisícům či více nevěřícím poslali,'),
(3936, 37, 148, 'a uvěřili a užívání života jsme jim na čas popřáli.'),
(3937, 37, 149, 'Dotaž se jich, proč Pán tvůj má dcery mít, když oni přece mají syny,'),
(3938, 37, 150, 'či zda byli svědky toho, že stvořili jsme anděly jako ženy?'),
(3939, 37, 151, 'Což není to jen výmysl jejich, když hovoří:'),
(3940, 37, 152, '"Bůh zplodil" - a věru jsou to lháři!'),
(3941, 37, 153, 'A že by si místo synů vyvolil dcery?'),
(3942, 37, 154, 'Co je s vámi, že máte takové názory?'),
(3943, 37, 155, 'Což se nevzpamatujete?'),
(3944, 37, 156, 'Anebo snad pravomoc zjevnou k tomu máte?'),
(3945, 37, 157, 'Předložte tedy Písmo své, jste-li pravdomluvní!'),
(3946, 37, 158, 'A učinili Boha s džiny příbuzným, však džinové dobře již vědí, že sami budou před Pána předvedeni.'),
(3947, 37, 159, 'Oč slavnější je Bůh než to, co lživě Mu připisují,'),
(3948, 37, 160, 'kromě služebníků Božích upřímných.'),
(3949, 37, 161, 'A ani vy, ani to, co uctíváte,'),
(3950, 37, 162, 'nikoho proti Bohu vzbouřit nemůžete'),
(3951, 37, 163, 'kromě toho, jenž hořet bude v pekle.'),
(3952, 37, 164, 'Není mezi námi nikoho, kdo by neměl místo určené,'),
(3953, 37, 165, 'my věru pak jsme ti, kdo v řadách stojí,'),
(3954, 37, 166, 'a také ti, kdož Pána svého oslavují!'),
(3955, 37, 167, 'Nevěřící sice říkají:'),
(3956, 37, 168, '"Kdybychom měli nějaké připomenutí od předků svých dávných,'),
(3957, 37, 169, 'věru bychom byli také ze služebníků Božích upřímných!"'),
(3958, 37, 170, 'Však přesto v ně nevěří a záhy poznají!'),
(3959, 37, 171, 'Slovo Naše již dříve bylo dáno služebníkům Našim poslaným,'),
(3960, 37, 172, 'že od Nás pomoc vždy dostanou'),
(3961, 37, 173, 'a že vojska Naše pro ně vítězství přinesou.'),
(3962, 37, 174, 'Odvrať se od nich na dobu krátkou'),
(3963, 37, 175, 'a pozoruj je; však oni také záhy zpozorují!'),
(3964, 37, 176, 'Cožpak doopravdy uspíšení trestu Našeho požadují?'),
(3965, 37, 177, 'Až pak trest na jejich nádvoří sestoupí, špatné to bude jitro pro ty, kdož byli varováni marně.'),
(3966, 37, 178, 'Odvrať se tedy od nich dočasně'),
(3967, 37, 179, 'a pozoruj je, však oni také záhy zpozorují!'),
(3968, 37, 180, 'Oč slavnější je Pán tvůj, Pán moci veškeré, než to, co mu lživě připisují!'),
(3969, 37, 181, 'Mír budiž všem vyslancům Božím'),
(3970, 37, 182, 'a chvála Bohu, Pánu lidstva!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Sád. Při Koránu, obsahujícím připomenutí!'),
(3972, 38, 2, 'Ba věru ti, kdož nevěří, v marnivosti a odporu setrvávají.'),
(3973, 38, 3, 'Kolik pokolení před nimi jsme již zahubili! Volali sice o pomoc, však nezbyl jim čas k uniknutí.'),
(3974, 38, 4, 'Diví se nevěřící, že k nim přišel varovatel z jejich řad vzešlý, a říkají: "Tohle je kouzelník a lhář prolhaný.'),
(3975, 38, 5, 'Což neučinil z více božstev Boha jediného? To věru je věc podivná!"'),
(3976, 38, 6, 'A vzdálili se velmoži z nich a řekli: "Jděte a při božstvech svých vytrvejte! Vždyť to věru je věc vyžadovaná.'),
(3977, 38, 7, 'O ničem takovém jsme neslyšeli v náboženství předešlém a není to nic než výmysl jeho.'),
(3978, 38, 8, 'Právě jemu mezi námi všemi že by mělo být připomenutí sesláno?" Ba, oni o připomenutí Mém jsou na pochybách, ba, ještě neokusili trestu Mého!'),
(3979, 38, 9, 'Což oni vlastní pokladnice milosrdenství Pána tvého mocného a dárce?'),
(3980, 38, 10, 'Či náleží jim království nebes a země a toho, co mezi nimi je? Nechť tedy vystoupí po nebeských provazech vzhůru!'),
(3981, 38, 11, 'Vojsko každé tam na útěk zahnáno bude, byť mělo spojence.'),
(3982, 38, 12, 'A již před nimi prohlašoval posly za lháře lid Noemův, ´Ádovci i Faraón, jenž pánem byl kůlů,'),
(3983, 38, 13, 'též Thamúdovci, lid Lotův i obyvatelé Houštin - a to spojenci byli,'),
(3984, 38, 14, 'a všichni bez výjimky posly za lháře prohlásili a trest (Můj) si zasloužili.'),
(3985, 38, 15, 'A také tyto nečeká nic než výkřik jediný a nebude zde odklad žádný.'),
(3986, 38, 16, 'A přesto říkají: "Pane náš, pošli nám rychle rozsudek náš, dříve než dostaví se den zúčtování!"'),
(3987, 38, 17, 'Snášej neochvějně to, co říkají, a vzpomeň si na služebníka Našeho Davida, silou vládnoucího, jenž stále činil pokání,'),
(3988, 38, 18, 'a jak podmanili jsme mu hory, aby spolu s ním pěly slávu Boží za večera i za svítání,'),
(3989, 38, 19, 'a také ptactvo kol něho se shromaždující; a všichni se k Bohu kajícně obracejí.'),
(3990, 38, 20, 'A jeho království jsme upevnili a moudrost a obratnost v souzení jsme mu dali.'),
(3991, 38, 21, 'Dostalo se ti zvěsti o těch dvou ve sporu, když do paláce vystoupili?'),
(3992, 38, 22, 'Když vstoupili k Davidovi, zalekl se jich. I pravili: "Neboj se, my dva jsme ve sporu a jeden z nás učinil příkoří druhému. Rozsuď mezi námi podle pravdy a nebuď stranický, nýbrž uveď nás na stezku rovnou!'),
(3993, 38, 23, 'Toto je můj bratr a devadesát devět ovcí má, zatímco mně patří jen jediná. I řekl mi: Svěř mi ji! a přemohl mne v půtce slovní o ni."'),
(3994, 38, 24, 'Odvětil David: "On vskutku ti ukřivdil žádaje ovci tvou, aby ke svým ji připojil. A zajisté většina společníků se vzájemně podvádí kromě těch, kdož uvěřili a zbožné skutky konali, však takových málo je." A dovtípil se David, že jsme jej pouze zkoušeli, a poprosil Pána svého o slitování a pak padl klaně se a čině pokání.'),
(3995, 38, 25, 'A odpustili jsme mu to a on má u Nás věru místo blízké a překrásné přebývání.'),
(3996, 38, 26, 'Davide, My učinili jsme tebe zástupcem na zemi, rozsuzuj tedy mezi lidmi podle pravdy! Nenásleduj vášně své, jež by tě svedly z cesty Boží, vždyť věru ty, kdož z cesty Boží sejdou, trest strašný čeká za to, že zapomněli na den zúčtování.'),
(3997, 38, 27, 'A nestvořili jsme nebesa a zemi, a co je mezi nimi, jen pro nic za nic; to je jen domněnka nevěřících! Běda těm, kdož neuvěřili, před ohněm pekelným!'),
(3998, 38, 28, 'Máme snad postavit ty, kdož uvěřili a zbožné skutky konali, na roveň s těmi, kdo pohoršení šíří na zemi? A máme bohabojné rovnými učinit s hříšníky?'),
(3999, 38, 29, 'Toto je Písmo požehnané, jež jsme seslali tobě, aby lidé o jeho znameních přemýšleli a aby si je rozmyslem nadaní připomínali.'),
(4000, 38, 30, 'A darovali jsme Davidovi Šalomouna, jenž byl Naším výtečným služebníkem. A byl věru kajícníkem,'),
(4001, 38, 31, 'když byly mu v podvečer předvedeny klisny ušlechtilé,'),
(4002, 38, 32, 'a pravil: "Vskutku jsem dával přednost lásce k statkům pozemským před Pána svého vzpomínáním, pokud se slunce neskrylo za noci závojem.'),
(4003, 38, 33, 'Přiveďte je ke mně!" A jal se jim protínat nohy i šíje.'),
(4004, 38, 34, 'A již dříve jsme Šalomouna zkouškám podrobili a na jeho trůn tělo mu podobné umístili; teprve potom se kál'),
(4005, 38, 35, 'a volal: "Pane můj, odpusť mi a daruj mi království takové, jakého nikdo po mně nedosáhne, vždyť Tys věru štědrý dárce!"'),
(4006, 38, 36, 'A podrobili jsme mu vítr, který lehce podle rozkazu jeho vál tam, kam on si přál,'),
(4007, 38, 37, 'a také satany všechny, stavitele i potápěče,'),
(4008, 38, 38, 'jakož i jiné, řetězy k sobě přivázané.'),
(4009, 38, 39, '"Toto je dar náš, Šalomoune, rozdávej z něho či si jej podrž, a nemusíš dávat zúčtování!"'),
(4010, 38, 40, 'A má on u Nás věru místo blízké a překrásné přebývání.'),
(4011, 38, 41, 'Vzpomeň též služebníka Našeho Jóba, když volal k Pánu svému: "Satan se mne dotkl strádáním a mukami."'),
(4012, 38, 42, '"Dupni nohou svou, a objeví se voda pro koupel chladivou i pro napití.'),
(4013, 38, 43, 'A dali jsme mu zpět jeho rodinu a s nimi ještě jednou tolik z milosrdenství Svého a jako připomenutí těm, kdož nadáni jsou rozmyslem.'),
(4014, 38, 44, 'A vezmi do ruky metlu a udeř jí, a nehřeš!" A shledali jsme jej trpělivým; služebníkem byl on výtečným, stále kajícným.'),
(4015, 38, 45, 'A vzpomeň také služebníků Našich Abrahama, Izáka a Jakuba, lidí dobrotivých a prozíravých,'),
(4016, 38, 46, 'jež jsme věru očistili myšlenkou čistou příbytku věčného připomínkou!'),
(4017, 38, 47, 'A jsou oni u Nás ti nejlepší z vyvolených.'),
(4018, 38, 48, 'A vzpomeň též Ismaele, Eliši a Dhu l-Kifla, kteří všichni jsou z nejlepších!'),
(4019, 38, 49, 'Toto je připomenutí; a bohabojní vskutku překrásné místo pobytu budou míti,'),
(4020, 38, 50, 'zahrady Edenu s branami pro ně vždy otevřenými,'),
(4021, 38, 51, 'v nich odpočívajíce budou si žádat ovoce hojnost i pití'),
(4022, 38, 52, 'a u nich budou dívky se zraky sklopenými stejného věku s nimi.'),
(4023, 38, 53, 'Hle, toto je ono, co vám bylo slíbeno pro den zúčtování!'),
(4024, 38, 54, 'A toto zajisté je Náš příděl, jenž nebude znát nikdy vyčerpání.'),
(4025, 38, 55, 'Tak bude! Však vzpurným dostane se věru nejhoršího přebývání'),
(4026, 38, 56, 'pekla, v němž hořet budou; hnusné to místo pro odpočívání!'),
(4027, 38, 57, 'Tak bude! A nechť ochutnají nápojů vroucích a hnijících,'),
(4028, 38, 58, 'a jiného podoby stejné i druhů různých!'),
(4029, 38, 59, 'Toto je houf do pekel s vámi se řítící; nebudou zde přivítáni a hořet budou v plameni'),
(4030, 38, 60, 'a řeknou: "Nikoliv! Vám nechť se nedostane přivítání! Vy toto jste nám připravili, a jak hnusné je to přebývání!"'),
(4031, 38, 61, 'A zvolají: "Pane náš, těm, kdo nám tohle připravili, těm rozmnož trest dvojnásobně v ohni!"'),
(4032, 38, 62, 'Pak zeptají se: "Co se děje, že zde nevidíme lidi, které jsme za špatné pokládali,'),
(4033, 38, 63, 'kteří nám buď pro smích byli či od nichž se zraky naše odvracely?"'),
(4034, 38, 64, 'Toto je věru skutečnost; tak hádat se budou obyvatelé ohně pekelného.'),
(4035, 38, 65, 'Rci: "Já jenom varovatel jsem a není božstva žádného kromě Boha jediného, podmaňujícího,'),
(4036, 38, 66, 'Pána nebes a země a všeho, co mezi nimi je, mocného a odpouštějícího!"'),
(4037, 38, 67, 'Rci: "Toto je zvěst nesmírná přece,'),
(4038, 38, 68, 'vy však od ní odvracíte se,'),
(4039, 38, 69, 'a neměl jsem vědomost žádnou o shromáždění nejvyšším, když ve sporu byli v něm.'),
(4040, 38, 70, 'A vnuknuto mi bylo jen, že varovatelem zjevným jsem,'),
(4041, 38, 71, 'když Pán tvůj pravil andělům: "Já smrtelníka z hlíny vytvořím,'),
(4042, 38, 72, 'a až jej vyrovnám a vdechnu mu něco z ducha Svého, padněte na zem klaníce se před ním!"'),
(4043, 38, 73, 'A padli před ním na zem andělé všichni sborem'),
(4044, 38, 74, 'kromě Iblíse, jenž pyšný byl a patřil k nevděčným. I pravil Bůh: "Iblísi, co ti bránilo, abys padl před tím, co rukama vlastníma stvořil jsem?'),
(4045, 38, 75, 'Jsi pyšný tak, či patříš snad ke vznešeným?"'),
(4046, 38, 76, 'Odpověděl: "Já lepší jsem než on, mne z ohně jsi stvořil, zatímco jeho jen z hlíny."'),
(4047, 38, 77, 'I zvolal Bůh: "Odejdi odsud, jsi zlořečený!'),
(4048, 38, 78, 'A nechť na tobě prokletí mé až do dne soudného lpí!"'),
(4049, 38, 79, 'Pravil Iblís: "Pane můj, dej mi odklad až do dne, kdy budou vzkříšeni!"'),
(4050, 38, 80, 'Odpověděl: "Budiž tedy mezi těmi, jimž je odloženo'),
(4051, 38, 81, 'až do dne času stanoveného!"'),
(4052, 38, 82, 'Pravil Iblís: "Při moci Tvé, já všechny je uvedu v bloudění'),
(4053, 38, 83, 'kromě těch z nich, kdož Tvoji služebníci jsou upřímní!'),
(4054, 38, 84, 'I pravil Bůh: "Při pravdě, a já pravdu dím:'),
(4055, 38, 85, 'já tebou a všemi těmi, kdož následovat tě budou, peklo naplním!"'),
(4056, 38, 86, 'Rci: "Nežádám od vás za to odměnu žádnou a nejsem z těch, kdož na sebe si berou, co nepřísluší jim.'),
(4057, 38, 87, 'A není toto než připomenutí pro lidstvo veškeré'),
(4058, 38, 88, 'a dozvíte se zprávu o tom po době nějaké."');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Seslání Písma od Boha mocného, moudrého pochází.'),
(4060, 39, 2, 'Věru jsme ti seslali Písmo v pravdě, uctívej tedy Boha a zasvěcuj Mu svou víru upřímnou!'),
(4061, 39, 3, 'Což Bohu víra upřímná nepatří? Ti, kdož si berou jako ochránce jiné než Jeho, hovoří: "My je uctíváme jen proto, aby nás co nejvíce k Bohu přiblížili." Bůh věru pak mezi nimi rozsoudí to, o čem ve sporu byli. A Bůh zajisté nepovede správně ty, kdož lháři jsou a nevěřící zarputilí.'),
(4062, 39, 4, 'Kdyby si byl Bůh přál mít děti, byl by si věru vybral podle libosti z těch, které stvořil. Sláva Jemu - On Bůh je jediný, vše podmaňující.'),
(4063, 39, 5, 'On stvořil nebesa a zemi jako skutečnost vážnou a On zavinuje noc dnem a den nocí; a podmanil si slunce a měsíc, takže obě tělesa ke lhůtě stanovené ubíhají. Což tedy není mocný, odpouštějící?'),
(4064, 39, 6, 'Vás pak stvořil z bytosti jediné, z níž potom udělal družku její. A seslal vám z dobytčat osm v párech. On tvoří vás v lůnech matek vašich jedním tvořením za druhým, v temnotě trojnásobné. Takový je Bůh, Pán váš, a Jemu náleží panství; není božstva kromě Něho. Jak se jen můžete tak odvracet?'),
(4065, 39, 7, 'Jestliže jste vy nevděční, je Bůh věru soběstačný i bez vás a nelíbí se mu nevděk u služebníků Jeho. Jste-li vděční, pak ve vás nalezne zalíbení. A neponese žádná duše břímě duše jiné; posléze navrátíte se k Pánu svému a On vás poučí o tom, co jste dělali, vždyť On dobře zná, co hrudi vaše skrývají.'),
(4066, 39, 8, 'Když člověka neštěstí postihne, tu modlí se k Pánu svému, kajícně se obraceje k Němu; když potom Bůh to v dobrodiní změní, člověk zapomene na to, proč se k Němu předtím modlil, a přidává Bohu podobné, aby svedl jiné z cesty Jeho. Rci: "Jen si užívej v nevděku svém, ty zajisté budeš pak jedním z obyvatel ohně pekelného!"'),
(4067, 39, 9, 'Zdaž ten, jenž pobožnost koná během hodin nočních; padaje na tvář svou a stoje při modlitbě, a před životem budoucím na pozoru se má a v milosrdenství Pána svého doufá...? Zeptej se: "Jsou si rovni ti, kdož vědí, s těmi, kdož nevědí?" Však připomenou si to jen ti, kdož rozmyslem jsou nadáni.'),
(4068, 39, 10, 'Rci: "Služebníci Moji, kteří jste uvěřili, bojte se Pána svého! Těm, kdož na tomto světě konali dobré, dostane se dobrého. A země Boží je rozlehlá a trpělivým bude dána jejich odměna v míře plné, bez počítání."'),
(4069, 39, 11, 'Rci: "Bylo mi poručeno, abych sloužil Bohu, upřímně mu zasvěcuje víru,'),
(4070, 39, 12, 'a bylo mi poručeno, abych byl prvním z těch, kdož do vůle Jeho se odevzdávají."'),
(4071, 39, 13, 'Rci: "Věru se bojím, neuposlechnuli Pána svého, trestu dne nesmírného."'),
(4072, 39, 14, 'Rci: "Boha budu uctívat, upřímně mu zasvěcuje víru svou.'),
(4073, 39, 15, 'A vy nevěřící si tedy uctívejte, koho chcete, místo Boha!" Rci: "Věru prodělají v den zmrtvýchvstání ti, kdož ztrátu sami sobě a rodinám svým způsobili! Což to opravdu není ztráta zjevná?'),
(4074, 39, 16, 'A nad sebou budou mít závěs ohnivý a pod sebou závěs ohnivý." A takto Bůh nahání strach služebníkům Svým. Služebníci Moji, buďte tedy vůči Mně bohabojní!'),
(4075, 39, 17, 'Pro ty, kdož odvrátili se od Taghúta odmítajíce jej uctívat a kajícně se obrátili k Bohu, pro ty je určena zvěst radostná. Oznam tedy zvěst radostnou služebníkům Mým,'),
(4076, 39, 18, 'těm, kdož horlivě naslouchají slovům Mým a následují nejkrásnější z nich! To jsou ti, které Bůh na správnou cestu uvedl, a to jsou ti, kdož rozmyslem jsou nadáni.'),
(4077, 39, 19, 'Cožpak toho, nad nimž se uskutečnilo slovo trestu... což můžeš takového, jenž v ohni pekelném dlí, zachránit?'),
(4078, 39, 20, 'Však ti, kdož vůči Pánu svému jsou bohabojní, ti obývat budou komnaty, nad nimiž komnaty jiné jsou vystavěny a pod nimiž řeky tekou; to je slib Boží a Bůh nikdy slib svůj neporuší.'),
(4079, 39, 21, 'Což nevidíš, jak Bůh sesílá z nebe vodu a rozvádí ji do pramenů v zemi? Z ní potom dává vyrůst obilí druhů rozličných, jež posléze zvadne a žloutne před zrakem tvým. A nakonec je učiní slámou suchou - a v tom je věru připomenutí pro ty, kdož rozmyslem jsou nadáni.'),
(4080, 39, 22, 'Zdaž ten, jehož hruď Bůh rozevřel pro islám a jenž řídí se světlem od Pána jeho vycházejícím...? Běda těm, jichž srdce jsou zatvrzelá vůči připomenutí Božímu, neboť oni jsou v zjevném bloudění.'),
(4081, 39, 23, 'Bůh seslal příběh nejkrásnější, knihu alegorickou i opakovaná vyprávění, z nichž husí kůže naskakuje těm, kdož Pána svého se bojí; potom však kůže i srdce jejich se obměkčí vůči připomenutí Božímu. Toto je vedení Boží, jímž On vede, koho chce, však ten, komu dal Bůh zbloudit, ten nebude mít vedoucího žádného!'),
(4082, 39, 24, 'Zdaž ten, kdož tvář svou si ochrání před trestem nejhorším v den zmrtvýchvstání...? A bude řečeno nespravedlivým: "Ochutnejte to, co jste si vysloužili!"'),
(4083, 39, 25, 'Také ti, kdož před nimi byli, poselství za lživé prohlašovali, však zasáhl je trest z místa, odkud to netušili.'),
(4084, 39, 26, 'A Bůh dal jim okusit zahanbení v životě pozemském, však věru trest na onom světě bude těžší - kéž by to jen věděli!'),
(4085, 39, 27, 'My pak uvádíme lidem v Koránu tomto všemožné druhy podobenství - snad se vzpamatují!'),
(4086, 39, 28, '... jako Korán arabský, bez jakéhokoliv zkřivení - snad budou bohabojní!'),
(4087, 39, 29, 'Bůh uvádí podobenství o muži závislém na hašteřících se společnících a o muži, jenž podřízen je člověku jedinému. Jsou si snad v tomto příkladu rovni? Chvála Bohu nikoliv, však většina lidí to neví.'),
(4088, 39, 30, 'Zajisté budeš jednou mrtev a i oni budou mrtví'),
(4089, 39, 31, 'a později, v den zmrtvýchvstání, před Pánem svým spor povedete.'),
(4090, 39, 32, 'Kdo je nespravedlivější než ten, kdo o Bohu si lež vymýšlí a pravdu, když dostane se mu jí, za lež prohlašuje? Což v pekle příbytek nevěřících nebude?'),
(4091, 39, 33, 'Ti, kdož pravdu přinášejí a ji potvrzují - hle, to jsou bohabojní!'),
(4092, 39, 34, 'U Pána svého budou mít vše, co jen si přát budou - a to odměnou je těch, kdož dobro činí,'),
(4093, 39, 35, 'aby tak Bůh vymazal to, co špatného konali, a dal jim odměnu jejich za to, co vykonali nejlepšího.'),
(4094, 39, 36, 'Což Bůh nestačí služebníku Svému, když nevěřící tě zastrašují těmi, které si vzali vedle Něho? Ten, jemuž dal Bůh zbloudit, ten nemá vedoucího žádného.'),
(4095, 39, 37, 'Však ten, kdo Bohem je veden, ten nemá svůdce žádného. Což není Bůh mocný a pán pomsty?'),
(4096, 39, 38, 'A kdyby ses jich zeptal: "Kdo stvořil nebesa a zemi?", odpověděli by jistě: "Bůh." Rci: "Domníváte se snad, že kdyby Bůh mne chtěl postihnout neštěstím, že ti, které vedle Něho vzýváte, by byli schopni mne tohoto neštěstí zbavit? Anebo kdyby mi chtěl poskytnout milosrdenství Své, že by je mohli zadržet?" A rci: "Bůh mi plně stačí a na Něho nechť se spoléhají ti, kdož se spoléhají."'),
(4097, 39, 39, 'Rci: "Lide můj, jednejte podle svého stanoviska, já také tak budu jednat a záhy se dozvíte,'),
(4098, 39, 40, 'koho postihne trest zahanbující a na koho se vylije trest trvalý."'),
(4099, 39, 41, 'A seslali jsme ti pro lidi Písmo s pravdou. A kdokoliv po správné cestě kráčí, ten činí tak k prospěchu vlastnímu, zatímco ten, kdo bloudí, ke své škodě tak činí. A ty nejsi nad nimi žádným dozorcem.'),
(4100, 39, 42, 'Bůh povolává k sobě duše ve chvíli smrti jejich a ty, jež nezemřely, v době spánku jejich. A podržuje u sebe ty, o jejichž smrti rozhodl, a odesílá ostatní na lhůtu stanovenou. A věru jsou v tom znamení pro lid, který přemýšlí.'),
(4101, 39, 43, 'Což si nevěřící vskutku vezmou za přímluvce jiné vedle Boha? Rci: "A což když tito ničím nevládnou a ani rozumně neuvažují?"'),
(4102, 39, 44, 'Rci: "Jedině Bohu přísluší přímluva veškerá a Jemu náleží království nebes a země a k Němu posléze budete navráceni."'),
(4103, 39, 45, 'Když je vzpomínáno Boha v Jeho jedinosti, tu svírají se srdce těch, kdož nevěří v život budoucí, avšak když jsou zmiňováni ti, které místo Něho mají, tu se nevěřící radují.'),
(4104, 39, 46, 'Rci: "Velký Bože, stvořiteli nebes a země, jenž znáš nepoznatelné i všeobecně známé, Ty rozsoudíš mezi služebníky Svými to, v čem se jejich názory různily."'),
(4105, 39, 47, 'I kdyby nespravedlivým patřilo vše, co na zemi je, a ještě jednou tolik k tomu, vykoupili by se tím z muk trestu v den zmrtvýchvstání? Tehdy se jim objeví od Boha to, s čím vůbec nepočítali.'),
(4106, 39, 48, 'A objeví se jím špatné skutky, které spáchali, a obklopí je to, čemu se posmívali.'),
(4107, 39, 49, 'Když neštěstí se dotkne člověka, tu volá Nás, avšak později, když zahrneme jej dobrodiním Svým, hovoří: "Dostalo se mi toho pouze díky vědomostem mým." Nikoliv, to jenom zkouška byla, avšak většina z nich to neví.'),
(4108, 39, 50, 'A tohle již říkali ti, kdož před nimi byli, a neprospělo jim to, co si vysloužili,'),
(4109, 39, 51, 'a postihla je špatnost toho, co si vysloužili; a stejně tak ti, kteří jsou nespravedliví z těchto lidí, budou postiženi špatností toho, co si vysloužili, a nebudou moci tomu zabránit.'),
(4110, 39, 52, 'Což nevědí, že Bůh štědře dává i odměřuje obživu tomu, komu chce? A věru jsou v tom znamení pro lid věřící.'),
(4111, 39, 53, 'Rci: "Služebníci moji, kteří jste se dopustili přestupků proti sobě samým, neztrácejte naději v milosrdenství Boží, vždyť Bůh věru odpouští viny všechny - On odpouštějící je i slitovný.'),
(4112, 39, 54, 'Obracejte se kajícně k Pánu svému a odevzdejte se do vůle Jeho, dříve než vás trest zasáhne, neboť pak vám pomoženo nebude.'),
(4113, 39, 55, 'A následujte to nejlepší, co vám bylo od Pána vašeho sesláno, dříve než vás trest znenadání zasáhne, aniž to tušit budete,'),
(4114, 39, 56, 'a než člověk vykřikne: "Běda mi za to, že jsem tak zanedbával Boha, a věru jsem patřil mezi ty, kdož se posmívali!",'),
(4115, 39, 57, 'anebo než zavolá: "Kdyby mne byl vedl Bůh, věru bych byl mezi bohabojnými",'),
(4116, 39, 58, 'či než vykřikne při spatření trestu: "Kdybych se mohl navrátit na zem, věru bych patřil k těm, kdož dobro konají!"'),
(4117, 39, 59, 'Ba ano, znamení Má k tobě již přišla, ale tys prohlásil je za lživá a pyšným jsi byl a jedním... z nevěřících!'),
(4118, 39, 60, 'V den zmrtvýchvstání pak uvidíš ty, kdož o Bohu si lži vymýšleli, s tvářemi zčernalými. Což nebude peklo místem útulku pro zpupné?'),
(4119, 39, 61, 'Bůh zachrání ty, kdož bohabojní byli v místě štěstí a nedotkne se jich nic zlého a zarmouceni nebudou.'),
(4120, 39, 62, 'Bůh stvořitelem je věcí všech a On ručitelem je též všeho.'),
(4121, 39, 63, 'Jemu patří klíče od nebes i od země. A ti, kdož nevěří ve znamení Boží, ti věru ztrátu utrpí.'),
(4122, 39, 64, 'Rci: "Chcete mi nařizovat, abych někoho jiného než Boha uctíval, vy nevědomci?"'),
(4123, 39, 65, 'A bylo již vnuknuto tobě i těm, kdož před tebou byli: "Věru, budeš-li přidružovat, marné budou skutky tvé a budeš zajisté mezi těmi, kdož ztrátu utrpí.'),
(4124, 39, 66, 'Naopak, Boha uctívej a buď jedním z vděčných!"'),
(4125, 39, 67, 'Oni neocenili Boha podle ceny Jeho skutečné, ačkoliv země celá bude v hrsti Jeho v den zmrtvýchvstání a nebesa budou svinuta v pravici Jeho. Sláva Jemu, jenž vznešenější je než vše, co k Němu přidružují!'),
(4126, 39, 68, 'A bude zatroubeno na pozoun poprvé a budou zasaženi všichni, kdož na nebesích a na zemi jsou, kromě těch, které Bůh bude chtít ušetřit. A potom bude zatroubeno podruhé, a hle, vstanou všichni a budou se rozhlížet.'),
(4127, 39, 69, 'A rozzáří se země světlem Pána svého a rozložena bude kniha; a předvedení budou proroci a svědkové a bude rozsouzeno mezi nimi podle pravdy a nebude jim ukřivděno.'),
(4128, 39, 70, 'Duše každá pak obdrží v míře plné odměnu za to, co konala; a On zná nejlépe, co lidé činí.'),
(4129, 39, 71, 'A budou hnáni ti, kdož nevěřili, do pekla po skupinách, a až tam přijdou, otevřou se jeho brány a jeho strážci se zeptají: "Což k vám nepřišli poslové z vás vzešlí, sdělujíce vám znamení Pána vašeho a varujíce vás před setkáním s tímto vaším dnem?" I odvětí: "Ba ano!" a uskuteční se nad nevěřícími výrok trestu.'),
(4130, 39, 72, 'A bude jim řečeno: "Vstupte do bran pekla, v němž nesmrtelní budete!" A jak hnusný bude útulek zpupných!'),
(4131, 39, 73, 'A budou pobídnuti ti, kdož byli bohabojní vůči Pánu svému, do ráje po skupinách; a až tam přijdou, otevřou se brány jeho a jeho strážci pronesou: "Mír s vámi! Byli jste dobří, vejděte tedy a buďte v něm nesmrtelní!"'),
(4132, 39, 74, 'A řeknou: "Chvála Bohu, jenž slib Svůj uskutečnil a dal nám zemi v dědictví. Usídlíme se nyní v ráji, kdekoli se nám zachce." Jak krásná je odměna těch, kdož dobro konali!'),
(4133, 39, 75, 'A spatříš anděly stojíce v kruhu kol trůnu Božího a pějící slávu Pána svého. I mezi nimi bude rozsouzeno podle pravdy a bude řečeno: "Chvála Bohu, Pánu lidstva veškerého!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Há mím!'),
(4135, 40, 2, 'Zjevení Písma pochází od Boha mocného, vševědoucího,'),
(4136, 40, 3, 'viny odpouštějícího, pokání přijímajícího a v trestu strašného i shovívavosti plného. Není božstva kromě Něho a u Něho je cíl cesty konečný.'),
(4137, 40, 4, 'A hádají se o znamení Boží jenom ti, kdož nevěří; nechť tě tedy neoklame volné jejich chození po této zemi!'),
(4138, 40, 5, 'Již před nimi prohlašovali lidé Noema za lháře a po nich i spojenci. A každý národ usiloval zmocnit se posla svého a vystupovali proti němu lží, aby pomocí jí pravdu zničili. Však uchvátil jsem je, a jaký byl trest Můj?!'),
(4139, 40, 6, 'A takto se uskutečnilo slovo Pána tvého nad těmi, kdož neuvěřili a ti budou ohně pekelného obyvateli!'),
(4140, 40, 7, 'Ti andělé, kteří trůn nesou, a ti, kteří okolo stojí, chválou Pána svého slaví, v Něj věří a za odpuštění pro ty, kdož uvěřili, prosí: "Pane náš, Ty objímáš v milosrdenství i vědění Svém věc každou. Odpusť těm, kdož pokání činili a Tvou cestu následovali, a ušetři je trestu plamene pekelného'),
(4141, 40, 8, 'a uveď je, Pane náš, do zahrad Edenu, které jsi jim přislíbil, a také ty z otců, manželek a potomstva jejich, kteří ctnostní byli; vždyť Tys věru mocný i moudrý.'),
(4142, 40, 9, 'A zbav je zlého! A koho zbavíš v ten den zlého, tomu jsi milost Svou uštědřil a to úspěch je nesmírný."'),
(4143, 40, 10, 'A bude zvoláno na ty, kdož nevěřili: "Odpor Boží je věru silnější než vaše nenávist vůči vám samým, když jsouce k víře vyzváni, přece jste neuvěřili!"'),
(4144, 40, 11, 'A odvětí: "Pane náš, dals nám dvakrát zemřít a oživils nás také dvakrát a my viny své uznáváme. Je prostředek nějaký, abychom se ven z toho dostali?"'),
(4145, 40, 12, '"A toto je trest za to, že když vyzýváno bylo k víře v Boha jediného, vy jste neuvěřili, však když k Němu byli jiní přidružováni, věřili jste. Rozsudek pak náleží Bohu vznešenému, velikému!"'),
(4146, 40, 13, 'On je ten, jenž ukazuje vám Svá znamení a sesílá vám z nebe obživu; však vzpomene si na to pouze ten, kdo pokání činí.'),
(4147, 40, 14, 'Modlete se tedy k Bohu, jen Jemu zasvěcujíce víru upřímnou, i když se to nevěřícím nelibí.'),
(4148, 40, 15, 'On povýšen je hodností, vládce trůnu - On posílá ducha zjevení Svého tomu, komu chce ze služebníků Svých, aby varoval přede dnem setkání,'),
(4149, 40, 16, 'dnem, kdy mrtví se vynoří a kdy o nich nebude skryto před Bohem nic. "Komu dnes náleží moc královská?" "Bohu jedinému, podmaniteli!'),
(4150, 40, 17, 'Dnes bude odměněna duše každá za to, co si vysloužila, a dnes nebude křivdy žádné. A Bůh rychlý je v účtování!"'),
(4151, 40, 18, 'Varuj je přede dnem, jenž blízko již stojí, kdy srdce budou v hrdlech úzkostí a promluvit nebudou schopni. Nespravedliví nebudou mít přítele vřelého ani přímluvce, jemuž sluchu by bylo popřáno.'),
(4152, 40, 19, 'Bůh dobře zná proradnost očí i vše, co v hrudích je skryto.'),
(4153, 40, 20, 'Bůh rozhoduje podle pravdy, zatímco ti, které vedle Boha vzývají, nerozhodují podle ničeho. A Bůh věru je slyšící i jasnozřivý.'),
(4154, 40, 21, 'Což necestovali po zemi, že neviděli, jaký byl konec těch, kdož před nimi byli! A přece byli silnější než oni mocí i stopami, jež na zemi zanechali. Však uchvátil je Bůh za viny jejich a neměli proti Bohu ochránce žádného.'),
(4155, 40, 22, 'A toto je trest za to, že když k nim přišli poslové jejich s jasnými důkazy, neuvěřili. A uchvátil je Bůh a On věru silný je i strašný v trestání.'),
(4156, 40, 23, 'A vyslali jsme již dříve Mojžíše se znameními svými a se zjevnou pravomocí'),
(4157, 40, 24, 'k Faraónovi, Hámánovi a Qárúnovi, ti však řekli: "To kouzelník je prolhaný!"'),
(4158, 40, 25, 'A když k nim přišel s pravdou od Nás seslanou, tu zvolali: "Zabte syny těch, kdož s ním uvěřili, a nechte jen ženy jejich naživu." Avšak úklady nevěřících jsou odsouzeny k bloudění.'),
(4159, 40, 26, 'I pravil Faraón: "Nechte mne zabít Mojžíše! Ať si pak volá Pána svého! Obávám se, že změní vaše náboženství či způsobí v zemi pohoršení."'),
(4160, 40, 27, 'Pravil Mojžíš: "Utíkám se o pomoc k Pánu svému i Pánu vašemu před každým pyšným, jenž nevěří v den zúčtování!"'),
(4161, 40, 28, 'I pravil nějaký muž věřící z rodu Faraónova, jenž skrýval víru svou: "Což chcete zabít člověka jen proto, že říká: Pánem mým je Bůh? Vždyť přišel k vám s jasnými důkazy od Pána vašeho; je-li lhářem, tedy lež jeho se proti němu obrátí, však hovoří-li pravdu, budete postiženi částí toho, čím vám hrozí. Vždyť Bůh nevede správnou cestou toho, kdo přestupníkem je a lhářem.'),
(4162, 40, 29, 'Lide můj! Dnes náleží vám panství a máte nadvládu v zemi této, však kdo nám pomůže proti přísnosti Boží, jestliže na nás padne?" Faraón však odvětil: "Já ukazuji vám jen to, co sám vidím, a jedině já vás vedu cestou správnou."'),
(4163, 40, 30, 'Ten, jenž byl věřící, zvolal: "Lide můj! Bojím se, že na vás přijde den podobný dni spojenců'),
(4164, 40, 31, 'a osud podobný tomu, jenž postihl lid Noemův, ´Ádovce i Thamúdovce a ty, kdož byli po nich. Bůh přece nechce, aby se dála křivda služebníkům Jeho.'),
(4165, 40, 32, 'Lide můj! Obávám se pro vás dne vzájemného volání,'),
(4166, 40, 33, 'dne, kdy se obrátíte zády nemajíce proti Bohu ochránce žádného, neboť ten, komu Bůh dal zbloudit, nemá žádného vedoucího.'),
(4167, 40, 34, 'A přišel k vám již dříve Josef s jasnými důkazy, a nepřestali jste pochybovat o tom, co vám přinesl. A když zahynul, řekli jste: "Bůh po něm už nevyšle posla žádného." A takto Bůh dává zbloudit tomu, kdo přestupníkem je a pochybovačem.'),
(4168, 40, 35, 'To jsou ti, kdož se hádají ohledně znamení Božích, aniž se jim k tomu dostalo oprávnění. Veliký je vůči nim odpor u Boha i těch, kdož uvěřili. A takto Bůh zapečeťuje srdce každého pyšného a úporně vzdorujícího."'),
(4169, 40, 36, 'Faraón pak pravil: "Hámáne, postav pro mne věž, snad dosáhnu provazů,'),
(4170, 40, 37, 'provazů nebeských, a vystoupím až k božstvu Mojžíšovu, neboť jej za lháře považují." A takto byla pro Faraóna okrášlena špatnost jeho skutků, byl sveden z cesty a úklad jeho skončil jedině záhubou.'),
(4171, 40, 38, 'Pravil pak ten, jenž uvěřil: "Lide můj! Následujte mne a já povedu vás cestou správnou!'),
(4172, 40, 39, 'Lide můj! Tento pozemský život není nic než dočasné užívání, zatímco život budoucí bude příbytkem stálým.'),
(4173, 40, 40, 'Kdokoliv koná špatné, ten odměněn bude něčím podobným, ale kdokoliv koná dobré - ať muž je či žena - a je věřící, ten do ráje vstoupí, kde obdařen bude darem nepočítaným.'),
(4174, 40, 41, 'Lide můj! Proč vás vlastně vyzývám ke spáse, když vy mne zvete k ohni pekelnému?'),
(4175, 40, 42, 'Vyzýváte mne, abych nevěřil v Boha a k Němu přidružoval to, o čem nemám žádné vědomosti, zatímco já vás vyzývám k mocnému, odpouštějícímu.'),
(4176, 40, 43, 'Není pochyby, že to, k čemu mne zvete, nemá být vzýváno ani na tomto, ani na onom světě a že návrat náš se jedině k Bohu uskuteční, zatímco přestupníci se stanou pekla obyvateli.'),
(4177, 40, 44, 'A jednou si vzpomenete na to, co vám říkám; já pak předávám svou věc Bohu, neboť Bůh jasně sleduje služebníky Své!"'),
(4178, 40, 45, 'Bůh jej ochránil od zlého, které mu úkladně chystali, zatímco rod Faraónův obklopil trestem nejhorším,'),
(4179, 40, 46, 'ohněm, jemuž budou vystaveni za jitra i za večera. A v den, kdy udeří Hodina, bude zvoláno: "Uveďte rod Faraónův do trestu nejpřísnějšího!"'),
(4180, 40, 47, 'A hle, budou se navzájem hádat v ohni a řeknou slabí těm, kdož pyšní byli: "My jsme vás následovali, můžete nás zbavit části ohně tohoto?"'),
(4181, 40, 48, 'A řeknou ti, kdož pyšní byli: "My v ohni jsme nyní všichni a věru Bůh již mezi služebníky Svými rozsoudil."'),
(4182, 40, 49, 'A ti, kdož budou v ohni, řeknou strážcům pekla: "Poproste Pána svého, aby alespoň jeden den z trestu našeho nám slevil!"'),
(4183, 40, 50, 'A zeptají se strážci: "Cožpak k vám nepřišli vaši poslové s jasnými důkazy?" Odpovědí: "Ano." A řeknou strážci: "Tak si proste sami!" A prosba nevěřících není leč bloudění.'),
(4184, 40, 51, 'My zajisté pomůžeme poslům svým i těm, kdož uvěřili, v životě pozemském i v den, kdy vstanou svědkové,'),
(4185, 40, 52, 'v den, kdy nebudou nic platny nespravedlivým výmluvy jejich, nýbrž padne na ně prokletí a údělem jich bude příbytek nejhorší.'),
(4186, 40, 53, 'A již kdysi jsme dali Mojžíšovi správné vedení a učinili jsme dítka Izraele dědici Písma,'),
(4187, 40, 54, 'aby bylo vedením a připomenutím pro lidi nadané rozumem.'),
(4188, 40, 55, 'Buď tedy trpělivý, neboť slib Boží je pravdivý! A pros za odpuštění hříchů svých a oslavuj chválou Pána svého za večera i za jitra!'),
(4189, 40, 56, 'Ti, kdož se hádají kvůli znamením Božím, aniž se jim k tomu dostalo oprávnění, v srdcích svých pouze domýšlivost mají a cíle svého nedosáhnou. Obrať se o pomoc k Bohu, vždyť On věru slyšící je i jasnozřivý!'),
(4190, 40, 57, 'Stvoření nebes a země zajisté je velkolepější než stvoření lidí, avšak většina lidí nemá o tom ponětí.'),
(4191, 40, 58, 'Slepý s vidoucím si nejsou rovni a nejsou si rovni ti, kdož věří a zbožné skutky konají, s těmi, kdož zlo páchají - a jak málo jste pamětliví!'),
(4192, 40, 59, 'Hodina se vskutku dostaví, o tom pochyby není, avšak většina lidí tomu nevěří.'),
(4193, 40, 60, 'I pravil Pán váš: "Vzývejte Mne a Já vás vyslyším! Ti, kdo se pyšně povyšují nad uctívání, ti věru vejdou do pekla, kde budou poníženi."'),
(4194, 40, 61, 'Bůh je ten, jenž pro vás učinil noc, abyste v ní odpočívali, a den, abyste v něm jasně viděli. A věru je Bůh vůči lidem pln dobrodiní, avšak mnozí nejsou za to vděční.'),
(4195, 40, 62, 'Takový je Bůh, váš Pán, stvořitel věci každé; není božstva kromě Něho! Jak se jen můžete odvracet od Něho?'),
(4196, 40, 63, 'Takto se od Něho odvraceli již ti, kdož znamení Boží popírali.'),
(4197, 40, 64, 'Bůh je ten, jenž pro vás zemi příbytkem stálým učinil a nebesa nad vámi budovou; on vyrovnal vás a podobu krásnou vám dal a pokrmy výtečné vám uštědřil. Takový je Bůh, váš Pán! Požehnán buď Bůh, Pán lidstva veškerého.'),
(4198, 40, 65, 'On živý je a není božstva kromě Něho! Modlete se k Němu zasvěcujíce Mu upřímnou víru svou! Chvála Bohu, Pánu lidstva veškerého!'),
(4199, 40, 66, 'Rci: "Bylo mi zakázáno, abych uctíval ty, jež vzýváte vedle Boha, když se mi dostalo jasných důkazů od Pána mého. A bylo mi poručeno, abych se odevzdal do vůle Pána lidstva veškerého."'),
(4200, 40, 67, 'On je ten, který vás stvořil z prachu, potom z kapky semene, pak z kapky přilnavé, potom vás vyvedl jako kojence, abyste později dosáhli dospělosti a abyste se posléze stali starci a někteří z vás budou povoláni k Němu dříve a abyste dospěli ke lhůtě stanovené, snad rozumní budete.'),
(4201, 40, 68, 'On je ten, kdo oživuje i usmrcuje, a když o něčem rozhodne, tu řekne pouze: "Staniž se!" a stane se.'),
(4202, 40, 69, 'Což jsi neviděl, kam se dostali ti, kdož se hádali ohledně Božích znamení?'),
(4203, 40, 70, 'Ti, kdož prohlašovali Písmo i to, s čím posly Své jsme vyslali, za lež, ti záhy poznají,'),
(4204, 40, 71, 'až s okovy na šíjích a v řetězech budou vláčeni'),
(4205, 40, 72, 'do vody vroucí a pak v pekle budou páleni!'),
(4206, 40, 73, 'A potom budou dotázáni: "Kde jsou tedy ti, které jste přidružovali'),
(4207, 40, 74, 'k Bohu?" I odvětí: "Ztratili se nám. Ale my vlastně jsme dříve nevzývali nic." A takto Bůh dává zbloudit nevěřícím.'),
(4208, 40, 75, '"Tohle je trest za to, že jste se na zemi neoprávněně radovali, i za to, že jste byli zpupní.'),
(4209, 40, 76, 'Vejděte do bran pekelných, kde zůstanete nesmrtelní! Jak hnusné bude hrdopyšných přebývání!"'),
(4210, 40, 77, 'Buď trpělivý, neboť slib Boží je pravdivý! A ať ti již ukážeme část z toho, co jsme jim slíbili, anebo tě k Sobě povoláme dříve - k Nám budou všichni navráceni.'),
(4211, 40, 78, 'Již před tebou jsme posly vysílali; o některých z nich jsme ti vyprávěli a o jiných jsme ti nevyprávěli. A nebylo dáno poslu žádnému, aby přinesl znamení, leč s dovolením Božím. A až se dostaví rozkaz Boží, bude rozsouzeno podle pravdy a tehdy budou ztraceni ti, kdož za nicotné to měli.'),
(4212, 40, 79, 'Bůh je ten, jenž pro vás stvořil velbloudy, abyste na nich jezdili a jimi se též živili'),
(4213, 40, 80, 'a mnohý užitek jiný z nich měli a abyste pomocí jich splnění úmyslů hrudí svých dosáhli. A na nich stejně jako na lodích jste neseni.'),
(4214, 40, 81, 'Tak ukazuje vám Bůh Svá znamení. A která ze znamení Božích chcete popírat?'),
(4215, 40, 82, 'Což po zemi necestovali, že neviděli, jaký byl konec těch, kdož před nimi byli a kteří byli početnější a mocnější silou i stopami, jež na zemi zanechali! A nebylo jim k ničemu to, čeho nabyli.'),
(4216, 40, 83, 'Když k nim poslové jejich přišli s jasnými důkazy, holedbali se nevěřící tím, co bylo u nich z vědění, však obklopilo je to, čemu se posmívali.'),
(4217, 40, 84, 'A když spatřili přísnost Naši, zvolali: "Uvěřili jsme v Boha i v jedinost Jeho a přestali jsme věřit v to, co jsme k Němu dříve přidružovali."'),
(4218, 40, 85, 'Však neprospěla jim pozdní víra jejich poté, co přísnost Naši spatřili, v souladu se zvyklostí Boží, kterou on vůči služebníkům Svým vždy dodrží. A tehdy nevěřící ztrátu utrpí!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Há mím!'),
(4220, 41, 2, 'Seslání od Milosrdného, slitovného,'),
(4221, 41, 3, 'Písmo, jehož verše byly učiněny srozumitelnými pro lidi znalé v podobě Koránu arabského!'),
(4222, 41, 4, '... jako zvěstovatele a varovatele - většina z nich se však odvrací a nechce naslouchat'),
(4223, 41, 5, 'a hovoří: "Srdce naše jsou zacloněna vůči tomu, k čemu vyzýváš, a v uších našich je hluchota a mezi námi i tebou závěs visí. Jednej tedy podle svého a my také tak budeme jednat!"'),
(4224, 41, 6, 'Rci: "Já jen smrtelník jsem jako vy a bylo mi pouze vnuknuto, že božstvem vaším je Bůh jediný. Miřte k Němu přímo a proste Jej za odpuštění! Běda těm, kdož k Němu jiné přidružují,'),
(4225, 41, 7, 'almužnu nedávají a v život posmrtný nevěří!"'),
(4226, 41, 8, 'Věru těm, kdož uvěřili a zbožné skutky konali, těm dostane se odměny neupomínané.'),
(4227, 41, 9, 'Rci: "Vy vskutku nebudete věřit v toho, jenž stvořil zemi ve dvou dnech, a přidávat mu chcete podobné? On Pánem lidstva všeho je!'),
(4228, 41, 10, 'On umístil na ní pevně zakotvené hory a požehnal ji a rozdělil na ní potravu ve čtyřech dnech pro všechny prosící rovnoměrně.'),
(4229, 41, 11, 'Potom se k nebi vztyčil, jež dýmem jenom bylo, a pravil jemu i zemi: Pojďte ke mně, ať chcete již, či nechcete! A odpověděly: Půjdeme dobrovolně.'),
(4230, 41, 12, 'A ustanovil je jako sedm nebes ve dvou dnech a každému nebi pořádek jeho vnukl. A nebe nejbližší svítilnami a ochranou ozdobil. A takové bylo předurčení mocného, vševědoucího."'),
(4231, 41, 13, 'Jestliže se odvrátí, řekni jim: "Já varuji vás před hromobitím podobným tomu, jímž ´Ádovci a Thamúdovci byli zahubeni."'),
(4232, 41, 14, 'Když poslové k nim přišli zepředu i zezadu hlásajíce, aby neuctívali nikoho kromě Boha, odpověděli: "Kdyby Pán náš chtěl, byl by věru anděly seslal. A my nevěříme v to, s čím byli jste posláni."'),
(4233, 41, 15, 'Pokud se ´Ádovců týká, ti pyšně po zemi chodili, aniž právo na to měli, a říkali: "Kdo silnější je než my mocí svou?" Což neviděli, že Bůh, jenž je stvořil, daleko silnější než oni je mocí? A přitom popírali ještě znamení Naše.'),
(4234, 41, 16, 'A vyslali jsme proti nim vichřici svištivou ve dnech neblahých, abychom jim dali okusit trest zahanbující v tomto životě pozemském, však trest života budoucího ještě více zahanbující bude a pomoci se jim nedostane.'),
(4235, 41, 17, 'A pokud pak se Thamúdovců týká, těm dali jsme správné vedení, však oni více slepotu než vedení milovali a zasáhlo je hromobití trestu potupného za to, co si vysloužili.'),
(4236, 41, 18, 'A zachránili jsme ty, kdož uvěřili a Boha se báli.'),
(4237, 41, 19, 'A v den, kdy nepřátelé Boží budou u ohně pekelného shromážděni, budou na skupiny rozděleni,'),
(4238, 41, 20, 'a když pak k němu přijdou, bude proti nim svědčit sluch a zrak i kůže jejich o tom, co činili.'),
(4239, 41, 21, 'A zeptají se kůží svých: "Proč proti nám svědčíte?" I odvětí: "Bůh, jenž věci každé promluvit dává, nám slovo udělil a On poprvé vás stvořil a k Němu budete navráceni.'),
(4240, 41, 22, 'A nemohli jste se skrýt tak, aby proti vám sluchy a zraky i kůže vaše dnes nesvědčily, a domnívali jste se, že Bůh nezví mnohé z toho, co jste dělali.'),
(4241, 41, 23, 'Tato domněnka vaše, již jste si o Pánu svém utvořili, vás do záhuby přivedla a stali jste se těmi, kdož ztrátu utrpěli."'),
(4242, 41, 24, 'Jestliže v tomto setrvají, stane se oheň útulkem jejich, a budou-li i prosit o slitováni, nebudou vyslyšeni.'),
(4243, 41, 25, 'A určili jsme jim druhy věrné, kteří okrášlili pro ně jich skutky minulé i současné, a uskutečnil se nad nimi rozkaz o národech džinů a lidí, jež zmizely již před nimi! A také oni jsou ztraceni.'),
(4244, 41, 26, 'Ti, kdož nevěří, říkají: "Neposlouchejte tento Korán, nýbrž přehlušujte jej nadávkami - snad nad ním zvítězíte!"'),
(4245, 41, 27, 'My však dáme věru okusit těm, kdo nevěřili, trestu strašného a odměníme je podle skutku jejich nejhoršího.'),
(4246, 41, 28, 'Toto bude odměna nepřátel Božích: oheň, v němž příbytek budou mít věčný v odměnu za to, že znamení Naše popírali.'),
(4247, 41, 29, 'A řeknou pak ti, kdož nevěří: "Pane náš, ukaž nám ty, kdož zbloudit nám dali, ať džinové jsou to již, či lidé! My umístíme je pod nohy své, aby nejnižšími se stali."'),
(4248, 41, 30, 'K těm pak, kdož říkali: "Pánem naším je Bůh," a potom se přímo ubírali, andělé sestoupí hovoříce: "Nebojte se a nermuťte se, nýbrž radujte se z ráje, jenž byl vám přislíben!'),
(4249, 41, 31, 'My ochránci jsme vašimi v pozemském i budoucím životě; a budete mít v něm to, po čem duše vaše touží, a dostane se vám tam, oč požádáte, všeho,'),
(4250, 41, 32, 'jako pohoštění od odpouštějícího, slitovného."'),
(4251, 41, 33, 'Kdo krásněji hovoří než ten, jenž k službě Bohu vyzývá, zbožné skutky koná a prohlašuje: "Já k těm, kdož do vůle Jeho se odevzdali, patřím."'),
(4252, 41, 34, 'Dobré a špatné skutky si nejsou rovné; odplácej tím, co lepší je, a hle, ten, mezi nímž a tebou bylo nepřátelství, se stane jakoby přítelem horoucím.'),
(4253, 41, 35, 'Však dostane se toho pouze těm, kdož trpěliví jsou, a dosáhnou toho jen vlastníci velikého štěstí.'),
(4254, 41, 36, 'A jestliže tě satan pokušením dráždí, pros Boha o ochranu, vždyť On slyšící je i vševědoucí.'),
(4255, 41, 37, 'Ke znamením Jeho patří noc i den, slunce i měsíc, však neklaňte se ani slunci, ani měsíci, nýbrž padejte na tvář svou před Bohem, jenž obojí stvořil, jestliže Jej uctíváte.'),
(4256, 41, 38, 'A jsou-li oni hrdopyšní, pak ti, kdož u Pána tvého jsou, v noci i ve dne Jej slaví neúnavně.'),
(4257, 41, 39, 'A k znamením Jeho patří, že vidíš zem zvadlou, však když vodu na ni sešleme, tu rozhýbá se a rozbují. A vpravdě ten, jenž oživil ji, je též mrtvých oživovatel a On nad každou věcí všemocný je.'),
(4258, 41, 40, 'Ti, kdož znamení Naše neuznávají, nejsou nikterak skryti před Námi. Cožpak ten, jenž vržen bude do ohně, je na tom lépe než ten, kdo ke dni zmrtvýchvstání přijde bezpečně?! Čiňte, co libo vám, On zajisté skutky vaše dobře pozoruje.'),
(4259, 41, 41, 'On zná ty, kdož nevěří v připomenutí, když přijde k nim, přestože je to Písmo vzácné'),
(4260, 41, 42, 'a nic falešného se doň ni zpředu ni zezadu nedostane, vždyť seslání od moudrého, chvályhodného to je!'),
(4261, 41, 43, 'To, co je ti namítáno, není nic jiného než to, co říkáno bylo poslům před tebou. Pán tvůj však vládcem je jak odpuštění, tak i trestu bolestného.'),
(4262, 41, 44, 'Kdybychom je byli učinili Koránem cizojazyčným, věru by byli řekli: "Pročpak nám nebyly učiněny verše jeho srozumitelnými? Je to cizojazyčné, či arabské?" Odpověz: "Korán je pro ty, kdož věří, vedením i vyléčením, zatímco ti, kdož nevěří, mají v uších svých trhlinu a jsou slepí vůči němu; a na ně voláno je z místa dalekého."'),
(4263, 41, 45, 'A darovali jsme již kdysi Mojžíšovi Písmo, a také o něm byli mínění rozdílného. A kdyby nebylo slova, jež od Pána tvého předcházelo, bylo by již mezi nimi rozsouzeno. A přesto o něm oni hluboce pochybují!'),
(4264, 41, 46, 'Kdo zbožné skutky koná, sám pro sebe tak činí, však kdo zlé koná, sám proti sobě samému tak činí. Pán tvůj pak věru služebníkům svým nekřivdí.'),
(4265, 41, 47, 'A jen Jemu přísluší o Hodině vědění. Plod žádný z obalu svého nevystoupí a samice žádná nepočne a neporodí bez Jeho vědomí. V den, kdy zavolá k nim: "Kde že jsou společníci Moji?", odvětí nevěřící: "Sdělujeme ti, že mezi námi svědka žádného o tom není."'),
(4266, 41, 48, 'A ztratí se jim to, k čemu se dříve modlili, a usoudí, že úniku žádného nemají.'),
(4267, 41, 49, 'Člověk si vyprošuje dobro neúnavně, však když zlo se ho dotkne, zoufá si a je malomyslný.'),
(4268, 41, 50, 'A věru dáme-li mu ochutnat milosrdenství Svého poté, co neštěstí se ho dotklo, tu říká: "Tohle mi právem náleží a nedomnívám se, že Hodina jednou udeří. A věru, budu-li navrácen k svému Pánu, budu mít u Něho překrásnou odměnu." My zajisté pak uvědomíme ty, kdož nevěřili, o skutcích jejich a dáme jim okusit trest přísný.'),
(4269, 41, 51, 'Když zahrneme člověka dobrodiním Svým, odvrací se a stranou se vzdálí, když však zlé se ho dotkne, tu hned prošení obšírného je plný.'),
(4270, 41, 52, 'Rci: "Co si myslíte? Jestliže toto od Boha přichází, a vy v to nevěříte, kdo je zbloudilejší než ten, jenž v rozkolu hlubokém dlí?"'),
(4271, 41, 53, 'Ukážeme jim znamení Svá ve všech světa končinách i v nich samých, dokud se jim jasným nestane, že toto pravdou je. A což Pánu tvému nestačí, že svědkem je všech věcí?'),
(4272, 41, 54, 'Což pochybují o tom, že se s Pánem svým setkají? Což ten neobjímá věci všechny ve Svém vědění?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Há mím'),
(4274, 42, 2, '`ajn sín qáf!'),
(4275, 42, 3, 'Takto Bůh mocný, moudrý vnuká tobě a těm, kdož byli před tebou, zjevení.'),
(4276, 42, 4, 'Jemu náleží vše, co je na nebesích a co je na zemi, a On vznešený je i nesmírný.'),
(4277, 42, 5, 'Nebesa se div v krovu svém nerozpoltí, zatímco andělé slávu Pána svého pějí a o odpuštění Jej prosí pro ty, kdož na zemi žijí. Což není Bůh věru odpouštějící a slitovný?'),
(4278, 42, 6, 'Ty, kdož si vzali ochránce jiné vedle Něho, ty Bůh dobře střeží - ty však nad nimi nejsi dozorcem žádným.'),
(4279, 42, 7, 'Takto jsme ti vnukli Korán arabský, abys jím varoval Matku měst i ty, kdož kolem ní žijí, a abys je varoval přede dnem zmrtvýchvstání, o němž pochyby není. Jedna skupina z nich se pak octne v ráji a skupina jiná v plameni šlehajícím.'),
(4280, 42, 8, 'Kdyby Bůh chtěl, učinil by vás věru národem jediným, avšak On uvádí do milosrdenství Svého, koho chce, zatímco nespravedliví nebudou mít ochránce ani pomocníka.'),
(4281, 42, 9, 'Naopak, oni si vzali ochránce jiné vedle Něho, ačkoliv Bůh jediným je ochráncem. On mrtvé oživuje a všemocný je nad každou věcí.'),
(4282, 42, 10, 'A ať již na cokoliv máte různé názory, rozsouzení toho patří Bohu. "Takový je Bůh, Pán můj, na Něho spoléhám a k Němu se kajícně obracím.'),
(4283, 42, 11, 'Stvořitel nebes a země, jenž učinil vám manželky vaše z vás samých a stvořil zvířata v párech, a tím vás rozmnožuje. A není nic, co by podobné Mu bylo a On slyšící je i jasnozřivý.'),
(4284, 42, 12, 'On klíče k nebesům i zemi má a On hojně uštědřuje obživu, komu chce, i odměřuje ji, komu chce - On o každé věci je vševědoucí."'),
(4285, 42, 13, 'On vám uzákonil jako náboženství to, co kdysi uložil Noemu - a to, co jsme vnukli tobě a co jsme uložili i Abrahamovi, Mojžíšovi a Ježíšovi: "Dodržujte náboženské úkony a nerozštěpujte se ohledně jich!" Jak nesnesitelné je pro modloslužebníky to, k čemu je vyzýváš! Bůh si k tomu vybírá, koho chce, a vede k tomu toho, kdo kajícně se k Němu obrací.'),
(4286, 42, 14, 'A rozštěpily se národy předchozí teprve poté, co dostalo se jim vědění, kvůli vzájemné nevraživosti. A kdyby nebylo slova, jež již dříve přišlo od Pána o lhůtě stanovené, bylo by mezi nimi již rozsouzeno. A věru ti, kdož po nich Písmo zdědili, v hlubokých pochybnostech o něm byli.'),
(4287, 42, 15, 'Proto vyzývej k víře pravé a kráčej přímo, jak bylo ti poručeno! Nenásleduj scestná učení jejich, nýbrž rci: "Uvěřil jsem v to, co Bůh z Písma seslal, a bylo mi poručeno, abych jednal spravedlivě mezi vámi. Bůh naším je i vaším Pánem, my skutky své máme i vy skutky své máte a není mezi námi důvodů ke sporu; Bůh shromáždí nás všechny a u Něho je cíl konečný."'),
(4288, 42, 16, 'Ti, kteří uvádějí důvody proti Bohu poté, co Mu bylo odpovězeno, těch důvody jsou u Pána jejich nicotné. Hněv Jeho se proti nim obrátí a je očekává trest přísný.'),
(4289, 42, 17, 'Bůh je ten, kdo seslal Písmo s pravdou i váhu; a víš ty vůbec, zda Hodina není snad již blízká?'),
(4290, 42, 18, 'Ti, kdož v ni nevěří, se ji snaží uspíšit, zatímco ti, kdož věří, se jí obávají a vědí, že ona je skutečností. Což nejsou ti, kdož o Hodině pochybují, v dalekém bloudění?'),
(4291, 42, 19, 'Bůh ke služebníkům Svým je láskyplný, a uštědřuje, komu chce - On silný je i mocný.'),
(4292, 42, 20, 'Kdokoliv si přeje pěstovat pole života budoucího, tomu rozmnožíme pole jeho; a kdokoliv si přeje obdělávat pole života pozemského, tomu z něho dáme část, avšak nebude mít podílu žádného v životě budoucím.'),
(4293, 42, 21, 'Což mají nevěřící společníky, kteří jim jako náboženství uzákonili to, co Bůh nedovolil? Kdyby nebylo slova rozhodnutí, bylo by už mezi nimi rozsouzeno, však nespravedlivé trest bolestný očekává.'),
(4294, 42, 22, 'Pak uvidíš nespravedlivé třást se strachem z toho, co si vysloužili, až trest na ně bude dopadat. Ti však, kdož uvěřili a dobré skutky konali, na lučinách rajských budou dlít a vše, co přát si budou, u Pána svého budou mít - a to přízeň je nesmírná.'),
(4295, 42, 23, 'A toto je to, co Bůh oznamuje jako zvěst radostnou služebníkům Svým, kteří uvěřili a zbožné skutky konali. Rci: "Nežádám od vás odměnu žádnou leč lásku k bližním!" Kdokoliv uskuteční něco dobrého, tomu rovněž rozmnožíme dobré, vždyť Bůh věru je odpouštějící a za vděčnost uznalý.'),
(4296, 42, 24, 'Což neříkají: "On vymyslil si o Bohu lež!" Kdyby Bůh chtěl, zapečetil by srdce tvé. Bůh však zahladí to, co je falešné, a uskuteční pravdu slovem Svým, vždyť On o všem, co v hrudích je skryto, dobře ví.'),
(4297, 42, 25, 'On je ten, jenž od služebníků Svých pokání přijímá a špatné skutky jim odpouští a dobře ví, co konáte.'),
(4298, 42, 26, 'On vyslyší ty, kdož uvěřili a dobré skutky konali, a rozmnoží jim ze Své přízně; pro nevěřící pak určil trest přísný.'),
(4299, 42, 27, 'Kdyby Bůh dal služebníkům Svým obživu v hojnosti, věru by se stali nestoudnými na zemi, avšak On sesílá s mírou to, co chce. On věru je o služebnících Svých dobře zpraven a dobře je vidí.'),
(4300, 42, 28, 'On je ten, kdo déšť hojný sesílá poté, co lidé již naději ztratili, a milosrdenství Své rozšiřuje. On ochránce je a chvályhodný.'),
(4301, 42, 29, 'A k znamením Jeho patří stvoření nebes a země i živočichů, jež na ní rozmnožil; a kdyby chtěl, mohl by je všechny shromáždit.'),
(4302, 42, 30, 'A nepostihne vás neštěstí žádné, leda za to, co ruce vaše si vysloužily, však On mnohé odpouští.'),
(4303, 42, 31, 'Vy nejste s to uniknout zásahu Jeho na zemi a nemáte vedle Boha ochránce ani pomocníka žádného.'),
(4304, 42, 32, 'A ke znamením Jeho patří i lodi moře brázdící, jež jako kopce jsou;'),
(4305, 42, 33, 'a když se mu zlíbí, utišuje vítr a tehdy lodi zůstanou nehybné na hladině moře. A v tom je věru znamení pro každého trpělivého a vděčného.'),
(4306, 42, 34, 'Anebo jim dá ztroskotat za to, co si vysloužili, avšak On také mnohé promíjí.'),
(4307, 42, 35, 'A poznají ti, kdož ohledně znamení Našich se hádají, že pro ně úniku není.'),
(4308, 42, 36, 'Co bylo vám z věcí dáno, je jen užívání krátké života pozemského, však co u Boha je, je lepší i trvalejší pro ty, kdož věří a na Pána svého spoléhají,'),
(4309, 42, 37, 'a pro ty, kdož velkým hříchům a neřestem se vyhýbají, a když rozhněváni jsou, odpouštějí,'),
(4310, 42, 38, 'a pro ty, kdož Pánu svému odpovídají a modlitby své dodržují a záležitosti své na základě porady mezi sebou vyřizují a z toho, co jsme jim uštědřili, rozdávají,'),
(4311, 42, 39, 'a pro ty, kteří si vzájemně pomáhají, když postihne je bezpráví.'),
(4312, 42, 40, 'A odměnou za špatné je jen špatnost podobná. Ten, kdo odpouští a o nápravu se snaží, toho odměna je u Boha, jenž věru nespravedlivé nemiluje.'),
(4313, 42, 41, 'Proti těm pak, kdož vzájemně si pomáhají poté, co bylo jim ukřivděno, není věru důvodu k zakročení;'),
(4314, 42, 42, 'však důvod k zakročení je pouze proti těm, kdož na lidech křivdu a na zemi svévoli neprávem páchali. Pro takové pak určen je trest bolestný.'),
(4315, 42, 43, 'A věru ten, kdo trpělivý je a odpouští... a vskutku to patří k podstatě věci.'),
(4316, 42, 44, 'Komu Bůh dal zbloudit, ten nebude mít po Něm ochránce žádného; a uzříš nespravedlivé volat, až spatří trest svůj: "Je způsob nějaký, jak jej odvrátit?"'),
(4317, 42, 45, 'A uvidíš je, až budou předvedeni před peklo, ponížené a zahanbené hledět zrakem vylekaným; a ti, kdož uvěřili, řeknou: "Ztraceni jsou ti, kdož ztrátu způsobili sami sobě i rodinám svým, v den zmrtvýchvstání." A vskutku budou nespravedliví uvrženi do trestu trvalého.'),
(4318, 42, 46, 'A nebudou mít kromě Boha ochránce žádného, který by mohl jim pomoci. Však ten, komu Bůh dal zbloudit, pro toho není cesta žádná.'),
(4319, 42, 47, 'Odpovězte tedy Pánu svému, dříve než dostaví se od Boha den, jejž nelze zadržet; v ten den nebude pro vás útulku ani zapírání nijakého.'),
(4320, 42, 48, 'Jestliže se však i nyní odvrátí, vždyť jsme tě k nim neposlali jako dozorce; tobě přísluší jedině předat sdělení. Když dáme okusit člověku milosrdenství Svého, raduje se z něho, avšak když postihneme jej něčím zlým za to, co ruce jeho spáchaly... vždyť člověk je vskutku nevděčný.'),
(4321, 42, 49, 'Bohu náleží království na nebesích a na zemi; On tvoří, co chce, a podle libosti dává dcery jednomu a syny druhému'),
(4322, 42, 50, 'anebo z nich činí páry, syny i dcery, anebo činí neplodným, koho chce, neb On vševědoucí je i všemocný.'),
(4323, 42, 51, 'A není dáno smrtelníku žádnému, aby s ním Bůh rozmlouval, leda prostřednictvím vnuknutí či zpoza clony anebo tak, že vyšle posla, jenž vnuká lidem z dovolení Jeho to, co On chce a On vznešený je a moudrý.'),
(4324, 42, 52, 'A takto jsme ti vnukli ducha ze zjevení Svého; předtím jsi neznal, co je to Písmo a co je to víra, avšak učinili jsme to světlem, jímž vedeme, koho chceme ze služebníků Svých. A ty věru také vedeš lidi po stezce přímé,'),
(4325, 42, 53, 'po stezce Boha, jemuž náleží vše, co na nebesích je i na zemi. Což k Bohu se nevracejí věci všechny?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Há mím.'),
(4327, 43, 2, 'Při Písmu zjevném!'),
(4328, 43, 3, 'Učinili jsme je věru Koránem arabským - snad budete rozumní!'),
(4329, 43, 4, 'A uložen je u Nás v Knize původní, vznešený a moudrosti plný.'),
(4330, 43, 5, 'Máme od vás odvrátit připomenutí proto, že lid jste prostopášný?'),
(4331, 43, 6, 'A kolik jsme již proroků k národům dávným vyslali,'),
(4332, 43, 7, 'však nepřišel k nim prorok žádný, aby se mu neposmívaly.'),
(4333, 43, 8, 'A zahubili jsme národy silnější mocí, než jsou tito, a minul již předešlých příklad.'),
(4334, 43, 9, 'Zeptáš-li se jich, kdo stvořil nebesa a zemi, věru odvětí: "Stvořil je dozajista mocný, vševědoucí!"'),
(4335, 43, 10, 'On je ten, který pro vás zemi kolébkou učinil a na ní cesty pro vás připravil, že správným směrem půjdete snad,'),
(4336, 43, 11, 'a který seslal z nebe vodu v množství odměřeném. Jí mrtvou krajinu křísíme, a takto budete i vy z hrobů vyvedeni;'),
(4337, 43, 12, 'a On druhy všechny v párech stvořil a lodi a velbloudy pro vás prostředky k ježdění učinil,'),
(4338, 43, 13, 'abyste se na jejich hřbetech usadili a pak si vzpomněli na dobrodiní Pána svého, když na nich pevně sedíte, a abyste říkali: "Sláva tomu, jenž nám toto vše podrobil, vždyť my sami dosáhnout toho schopni bychom nebyli!'),
(4339, 43, 14, 'A my věru se k Pánu svému navrátíme."'),
(4340, 43, 15, 'A nevěřící prohlašují služebníky Jeho za součást Jeho a věru člověk je nevděčník zjevný.'),
(4341, 43, 16, 'Což vybral si ze stvoření svých zrovna dcery a vás přednostně obdařil syny?'),
(4342, 43, 17, 'A když některému z nich je oznámeno narození toho, co k Milosrdnému připodobňují, tu pokryje se tvář jeho chmurou a sám je hněvu plný.'),
(4343, 43, 18, 'Což chtějí Bohu přiřknout to, co mezi tretkami je vychováno a ve sporu se neumí ani jasně vyjádřit?'),
(4344, 43, 19, 'A učinili z andělů, kteří jsou služebníky Milosrdného, ženské bytosti. Což byli svědky jejich stvoření? A zapsáno bude svědectví jejich a vyslýcháni budou.'),
(4345, 43, 20, 'Říkají: "Kdyby Milosrdný chtěl, neuctívali bychom je." A nemají o tom ponětí žádné a vymýšlejí si lež jen pouhou.'),
(4346, 43, 21, 'Či jsme jim snad dali předtím Písmo nějaké, o něž opírat se mohou?'),
(4347, 43, 22, 'Však oni naopak říkají: "Nalezli jsme otce své v jistém učení a jdeme ve stopách jejich."'),
(4348, 43, 23, 'A podobně jsme před tebou nevyslali do měst varovatele, aby tamní boháči neprohlásili: "Nalezli jsme otce své v jistém učení a jdeme ve stopách jejich!"'),
(4349, 43, 24, 'I řekl jim: "A což když vám přinesu něco, co lepší je vedení než to, jež u otců svých jste nalezli?" Odpověděli: "My vskutku nevěříme v to, s čím posláni jste byli!"'),
(4350, 43, 25, 'A pomstu svou jsme nad nimi vykonali, a pohleď, jaký byl konec těch, kdož za lež to prohlásili.'),
(4351, 43, 26, 'A hle, pravil Abraham otci svému a lidu svému: "Zříkám se toho, co uctíváte,'),
(4352, 43, 27, 'kromě toho, jenž stvořil mě, neboť On správnou cestou mě povede!"'),
(4353, 43, 28, 'A učinil tento výrok slovem trvalým pro potomky své doufaje, že snad k Bohu se obrátí.'),
(4354, 43, 29, 'Ba co více, dal jsem těmto i otcům jejich užívání života pozemského až do chvíle, kdy k nim přišla pravda a posel zjevný.'),
(4355, 43, 30, 'A když k nim pravda přišla, zvolali: "Tohle je kouzlo jen a my v ně věru nevěříme!"'),
(4356, 43, 31, 'A řekli dále: "Pročpak nebyl seslán tento Korán nějakému muži významnému z obou těchto měst?"'),
(4357, 43, 32, 'Jsou snad oni těmi, kdož milosrdenství Pána tvého rozdělují? My rozdělujeme mezi ně živobytí jejich v pozemském životě a my povyšujeme jedny nad druhé v hodnostech, aby si jedni druhé brali do služeb. A milosrdenství Pána tvého je lepší než to, co oni hromadí.'),
(4358, 43, 33, 'A nebýt toho, že by se lidé stali jediným národem nevěřících, věru bychom dali těm, kdož v Milosrdného nevěří, na domy jejich střechy stříbrné a k nim i žebříky stříbrné, aby po nich stoupali,'),
(4359, 43, 34, 'a opatřili bychom domy jejich dveřmi a lůžky stříbrnými, na nichž by lehávali,'),
(4360, 43, 35, 'a zlatými ozdobami. Všechno toto patří k požitkům života vezdejšího, zatímco život budoucí u Pána tvého připraven je bohabojným.'),
(4361, 43, 36, 'A tomu, kdo vyhýbá se připomenutí milosrdného, tomu přidělíme satana, jenž bude druhem jeho věrným.'),
(4362, 43, 37, 'A satani je budou z cesty Boží odvracet, zatímco lidé budou v domnění, že správně jsou vedeni.'),
(4363, 43, 38, 'A když pak dostaví se člověk k Nám, řekne satanovi: "Kéž by mezi mnou a tebou byla vzdálenost jako mezi východem a západem! Jak špatný jsi byl druh!"'),
(4364, 43, 39, 'Nic platno však vám to nebude v ten den, neb neospravedlní jste byli, a společníky budete v trestu pekelném.'),
(4365, 43, 40, 'Jsi schopen učinit hluchého slyšícím anebo vést slepého či ty, kdož v bloudění jsou zjevném?'),
(4366, 43, 41, 'A buď tě vezmeme od nich a pomstu nad nimi vykonáme,'),
(4367, 43, 42, 'anebo ti ukážeme to, co jsme jim přislíbili - vždyť My nad nimi všemocní jsme.'),
(4368, 43, 43, 'Přidržuj se tedy toho, co bylo ti vnuknuto, a tehdy zajisté půjdeš po cestě přímé.'),
(4369, 43, 44, 'A je to věru připomenutí pro tebe i lid tvůj, a budete ohledně toho dotazováni.'),
(4370, 43, 45, 'Zeptej se těch poslů, které jsme před tebou vyslali, zda ustanovili jsme vedle Milosrdného nějaká božstva jiná, aby je uctívali!'),
(4371, 43, 46, 'A vyslali jsme již dříve Mojžíše s Našimi znameními k Faraónovi a velmožům jeho, aby řekl: "Já poslem Pána lidstva jsem!"'),
(4372, 43, 47, 'A když k nim přišel s Našimi znameními, hle, oni se jim smáli jen,'),
(4373, 43, 48, 'ačkoliv jsme jim ukázali znamení, z nichž každé bylo větší než předchozí. A trestem jsme je postihli doufajíce, že k Bohu se obrátí.'),
(4374, 43, 49, 'Oni však řekli: "Kouzelníku, modli se za nás k Pánu svému, aby učinil to, co slíbil ti, neboť my zajisté jsme správně vedení."'),
(4375, 43, 50, 'A když jsme je trestu zbavili, hle, oni přísahu porušili'),
(4376, 43, 51, 'a Faraón zvolal k lidu svému: "Lide můj, což nepatří mi vláda nad Egyptem i nad těmito řekami, jež u nohou tekou mi? Což to snad nevidíte?'),
(4377, 43, 52, 'Což nejsem lepší než tenhle opovržený, jenž sotva vyjádřit se umí?'),
(4378, 43, 53, 'Pročpak mu náramky zlaté shozeny nebyly či proč s ním andělé jako průvodci nepřišli?"'),
(4379, 43, 54, 'A svedl svůj lid k lehkomyslnosti a uposlechli jej, neboť to lidé hanební byli.'),
(4380, 43, 55, 'A když Nás rozhněvali, pomstili jsme se jim a všechny jsme je utopili'),
(4381, 43, 56, 'a předchůdci i výstražným příkladem pro budoucí jsme je učinili.'),
(4382, 43, 57, 'A když je syn Mariin jako příklad uváděn, hle, tvoji soukmenovci se o něm hlasitě hádají'),
(4383, 43, 58, 'a říkají: "Jsou božstva naše lepší, či lepší je on?" A uvádějí ten příklad jen proto, aby hádat se mohli, vždyť věru oni jsou lid hádky milující.'),
(4384, 43, 59, 'On není leč služebníkem Naším, jemuž jsme přízeň svou uštědřili, a příkladem pro dítka Izraele jsme jej učinili.'),
(4385, 43, 60, 'A kdybychom chtěli, udělali bychom z vás anděly, kteří by na zemi nástupci vašimi byli.'),
(4386, 43, 61, 'A to je věru znamením Hodiny; nepochybujte o ní, nýbrž následujte mne, neboť toto stezka přímá je.'),
(4387, 43, 62, 'A nechť vás satan z ní nesvede, vždyť on je vám nepřítelem zjevným.'),
(4388, 43, 63, 'A když přišel Ježíš s jasnými důkazy, pravil: "Přišel jsem k vám s moudrostí, a abych vám objasnil něco z toho, o čem jste různého názoru; bojte se tedy Boha a poslouchejte mne!"'),
(4389, 43, 64, 'Bůh je věru Pánem mým i Pánem vaším; uctívejte Ho tedy, neboť toto stezka přímá je!'),
(4390, 43, 65, 'A byly strany mezi nimi názorů různých o něm; a běda těm, kdož křivdili, před trestem dne bolestného.'),
(4391, 43, 66, 'Což mohou očekávat něco jiného než Hodinu, jež přijde k nim, aniž to tušit budou, znenadání?'),
(4392, 43, 67, 'V ten den stanou se přátelé vzájemně nepřáteli, kromě těch, kdož byli bohabojní.'),
(4393, 43, 68, '"Služebníci Moji, dnes obavy nemějte a nebuďte zarmouceni!'),
(4394, 43, 69, 'Těch, kdož uvěřili v Naše znamení a do vůle Boží byli odevzdáni.'),
(4395, 43, 70, 'Vejděte do ráje, vy i manželky vaše, v radování!"'),
(4396, 43, 71, 'A budou mezi nimi kolovat zlaté nádoby i poháry a budou mít všeho, po čem duše touží a co očím lahodí; a tam budete nesmrtelní.'),
(4397, 43, 72, 'Toto je ta zahrada, již dali jsme vám v dědictví za to, co konali jste dobrého.'),
(4398, 43, 73, 'A bude v ní pro vás ovoce hojnost a pojídat budete z něho.'),
(4399, 43, 74, 'Však hříšníci prodlévají v trestu pekelném nesmrtelní'),
(4400, 43, 75, 'a nedočkají se přerušení, však budou tam v beznaději.'),
(4401, 43, 76, 'My jim neukřivdili, leč oni sami sobě ukřivdili.'),
(4402, 43, 77, 'A budou volat: "Máliku, nechť Pán tvůj s námi skončí!" A odpoví jim: "Zde zůstanete vskutku!"'),
(4403, 43, 78, 'A věru jsme k vám s pravdou přišli, však mnozí z vás si pravdu ošklivili.'),
(4404, 43, 79, 'Že zosnovali nějakou lest? Však my také proti nim zosnujeme léčku!'),
(4405, 43, 80, 'Či se domnívají, že neslyšíme tajemství jejich i skryté rozhovory? Věru že ano a vyslanci Naši to u nich zapisují!'),
(4406, 43, 81, 'Rci: "Kdyby měl Milosrdný syna, já uctíval bych jej jako první.'),
(4407, 43, 82, 'Oč slavnější je Pán nebes a země, Pán trůnu, než to, co mu lživě připisují!"'),
(4408, 43, 83, 'Nech je být, ať si tlachají a hrají až do chvíle, kdy se setkají se svým dnem, jenž byl jim přislíben.'),
(4409, 43, 84, 'On je ten, kdo je Bohem na nebesích i Bohem na zemi, a On moudrý je i vševědoucí.'),
(4410, 43, 85, 'Požehnán budiž ten, jemuž patří království na nebi a na zemi i vše, co je mezi nimi. On vládne věděním o Hodině a k Němu budete navráceni.'),
(4411, 43, 86, 'A nevládnou ti, jež místo Něho vzýváte, přímluvou žádnou, kromě těch, kdož pravdu vyznali, a ti dobře znají.'),
(4412, 43, 87, 'Zeptáš-li se jich, kdo je stvořil, věru odvětí: "Bůh!" Jak se tedy mohli od Něho odvrátit?'),
(4413, 43, 88, '... A slova jeho: "Pane můj, toto je lid, který nevěří!"'),
(4414, 43, 89, 'Odvrať se tedy od nich a rci: "Mír s vámi!" A záhy se dozvědí!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Há mím.'),
(4416, 44, 2, 'Při Písmu zřetelném!'),
(4417, 44, 3, 'Věru seslali jsme je, abychom byli varovateli, v noci požehnané,'),
(4418, 44, 4, 'v níž jsou rozdělována všechna zjevení moudrá'),
(4419, 44, 5, 'podle rozkazu od Nás vycházejícího. A My věru jsme těmi, kdo posly vysílají'),
(4420, 44, 6, 'jako milosrdenství od Pána tvého, a On slyšící je a vševědoucí,'),
(4421, 44, 7, 'Pán nebes i země a všeho, co mezi nimi je - jste-li pevně věřící!'),
(4422, 44, 8, 'Není božstva kromě Něho a On oživuje a usmrcuje a Pánem vaším je i vašich předků dávných.'),
(4423, 44, 9, 'A přesto nevěřící v pochybnostech si hrají.'),
(4424, 44, 10, 'Očekávej tedy den, kdy na nebesích se ukáže dým viditelný,'),
(4425, 44, 11, 'který lidi zahalí; a to bude trest bolestný!'),
(4426, 44, 12, 'Tehdy vzkřiknou: "Pane náš, odvrať od nás trest tento, my nyní jsme věřící!"'),
(4427, 44, 13, 'K čemu je jim nyní připomenutí? Vždyť přišel k nim již posel zjevný,'),
(4428, 44, 14, 'však zády se k němu obrátili a řekli: "Blázen je to, jinými poučený!"'),
(4429, 44, 15, 'Jestliže jen trochu od vás trest tento odvrátíme, vy zcela jistě se do nevěrectví navrátíte,'),
(4430, 44, 16, 'však v den, kdy udeříme velkým útokem, pomsty Naší okusíte!'),
(4431, 44, 17, 'A již před nimi jsme lid Faraónův zkoušce vystavili a přišel k nim posel vznešený,'),
(4432, 44, 18, 'jenž pravil: "Vydejte mi služebníky Boží! Já pro vás jsem posel spolehlivý!'),
(4433, 44, 19, 'Nepovstávejte proti Bohu, neb já k vám se zplnomocněním zřetelným přicházím,'),
(4434, 44, 20, 'a utíkám se k Pánu svému i Pánu vašemu před vaším ukamenováním.'),
(4435, 44, 21, 'A jestliže mi nevěříte, tedy se ode mne držte stranou!"'),
(4436, 44, 22, 'Pak volal k Pánu svému: "Tito lidé hříšníci jsou!"'),
(4437, 44, 23, '"Odejdi za noci se služebníky Mými, neb pronásledovat vás budou,'),
(4438, 44, 24, 'a přejdi přes moře otevřené; a oni vojska jsou, jež utopením zahynou!"'),
(4439, 44, 25, 'Kolik opustili zahrad a pramenů,'),
(4440, 44, 26, 'osetých polí a vznešených příbytků'),
(4441, 44, 27, 'a příjemností, v nichž vesele si žili!'),
(4442, 44, 28, 'A stalo se tak a dědici toho jsme lid jiný učinili.'),
(4443, 44, 29, 'Ani nebesa, ani země nad nimi nezaplakaly a nebyl dán odklad jim,'),
(4444, 44, 30, 'však dítka Izraele jsme zachránili před trestem zahanbujícím'),
(4445, 44, 31, 'Faraonóvým; a byl on mezi přestupníky zpupný.'),
(4446, 44, 32, 'A vyvolili jsme je nad ostatní lidstvo vědomě'),
(4447, 44, 33, 'a ukázali jsme jim znamení, v nichž zkouška zjevná byla pro ně.'),
(4448, 44, 34, 'A věru tito zde hovoří:'),
(4449, 44, 35, '"Není jiné smrti než první a my nebudeme vzkříšeni;'),
(4450, 44, 36, 'přiveďte tedy naše otce, jste-li pravdomluvní!"'),
(4451, 44, 37, 'Jsou snad oni lepší než lid tubba´ů a než jejich předchůdci, jež za hříšnost jejich jsme zahubili?'),
(4452, 44, 38, 'A nestvořili jsme nebesa a zemi, a co je mezi nimi, pro kratochvíli,'),
(4453, 44, 39, 'nýbrž stvořili jsme to jako skutečnost vážnou, však většina z nich to neví.'),
(4454, 44, 40, 'A věru den rozhodnutí bude časem schůzky pro všechny,'),
(4455, 44, 41, 'a to bude den, kdy přítel nebude nic platný příteli a nikdo jim nebude nápomocný'),
(4456, 44, 42, 'kromě těch, nad nimiž Bůh se slituje, vždyť On věru mocný je a slitovný.'),
(4457, 44, 43, 'Vskutku bude strom Zaqqúm'),
(4458, 44, 44, 'patřit k hříšníka pokrmům.'),
(4459, 44, 45, 'Je jak roztavená smůla, jež ve vnitřnostech kypí,'),
(4460, 44, 46, 'podoben vodě vroucí.'),
(4461, 44, 47, '"Vezměte ho a do hlubin pekelných jej dovlecte'),
(4462, 44, 48, 'a potom na jeho hlavu za trest vodu vroucí lijte!"'),
(4463, 44, 49, '"Ochutnej, vždyť tys mocný, vznešený zajisté!'),
(4464, 44, 50, 'Tohle je přece to, o čem vy pochybovali jste."'),
(4465, 44, 51, 'A bohabojní budou věru na místě bezpečném přebývat'),
(4466, 44, 52, 'mezi zahradami a prameny'),
(4467, 44, 53, 'a oděni do atlasu a brokátu se jeden na druhého budou dívat.'),
(4468, 44, 54, 'Tak stane se! A oženíme je s dívkami velkých černých očí,'),
(4469, 44, 55, 'a budou si tam všechny druhy ovoce žádat, jsouce v bezpečí.'),
(4470, 44, 56, 'A neokusí tam smrti - kromě smrti první - a ušetří je trest pekelný'),
(4471, 44, 57, 'z milosti Pána tvého; a to štěstí je nesmírné.'),
(4472, 44, 58, 'A ulehčili jsme ti to pomocí jazyka tvého - snad se upamatují!'),
(4473, 44, 59, 'Pozoruj, vždyť také oni zpozorují!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Há mím!'),
(4475, 45, 2, 'Seslání Písma od Boha mocného, moudrého pochází.'),
(4476, 45, 3, 'Na nebesích i na zemi jsou věru pro věřící znamení,'),
(4477, 45, 4, 'a také ve stvoření vašem i ve zvířatech, jež po zemi rozmnožil, jsou znamení pro lid pevně přesvědčený,'),
(4478, 45, 5, 'i ve střídání noci a dne, i v tom, co Bůh z nebes sesílá jako obživu a oživuje tím zemi poté, co mrtvá byla, a rovněž ve větrů vysílání jsou znamení pro lid rozumný.'),
(4479, 45, 6, 'Toto jsou znamení Boží, jež podle pravdy ti sdělujeme. A jakému tedy uvěří zvěstování, zamítnou-li Boha a Jeho znamení?'),
(4480, 45, 7, 'Běda každému pomluvači hříšnému,'),
(4481, 45, 8, 'jenž naslouchá znamením Božím, když jsou mu sdělována, však potom v pýše své setrvává, jako by je ani neslyšel! O trestu bolestném zvěst oznam jemu!'),
(4482, 45, 9, 'A když naučí se něčemu ze znamení Naších, tropí si z toho jen posměšky. A takové očekává trest potupný.'),
(4483, 45, 10, 'Za nimi peklo číhá a nebude jim platné nic z toho, co si vysloužili, ani to, co místo Boha jako ochránce si vzali a pro ně určen je trest nesmírný.'),
(4484, 45, 11, 'Toto je správné vedení a ti, kdož neuvěřili v Pána svého znamení, trestem muk bolestných budou potrestáni.'),
(4485, 45, 12, 'Bůh je ten, jenž podrobil vám moře, aby po něm lodi podle rozkazu Jeho pluly a abyste na něm o část přízně Jeho usilovali - snad za to budete vděčni!'),
(4486, 45, 13, 'On podmanil vám vše, co na nebesích je i na zemi, a všechno od Něho pochází. A věru jsou v tom znamení pro lid uvažující!'),
(4487, 45, 14, 'Rci těm, kdož uvěřili, ať promíjejí těm, kdož nedoufají ve dny Boží, jež dostaví se, aby byli lidé za to, co si vysloužili, odměněni.'),
(4488, 45, 15, 'Kdo zbožné skutky koná, sám pro sebe tak činí, však kdo zlé koná, sám proti sobě samému tak činí; a posléze budete k Pánu svému navráceni.'),
(4489, 45, 16, 'A dali jsme kdysi již dítkám Izraele Písmo, moudrost i proroctví a uštědřili jsme jim jídel výtečných hojnost a nad lidstvem veškerým jsme je vyznamenali.'),
(4490, 45, 17, 'A dali jsme jim jasné důkazy z rozkazu našeho pocházející. A rozešli se v názorech svých teprve poté, když se jim vědění dostalo, a to ze vzájemné nevraživosti. Pán tvůj pak mezi nimi rozsoudí v den zmrtvýchvstání to, v čem se rozcházeli.'),
(4491, 45, 18, 'A potom jsme tě umístili na cestě zákona z rozkazu Našeho vycházející. Následuj ji a nenásleduj sklonů těch, kdož nevědí o ničem!'),
(4492, 45, 19, 'Vždyť tihle nikterak ti proti Bohu neprospějí; nespravedliví jsou si navzájem přáteli, však Bůh bohabojných je přítelem.'),
(4493, 45, 20, 'Toto Písmo lidem viditelné důkazy dává a je vedením i milosrdenstvím pro lid, jenž pevně je přesvědčen.'),
(4494, 45, 21, 'Což domnívají se ti, kdož zlého se dopouštějí, že učiníme je rovnými s těmi, kdož věří a zbožné skutky konají, v životě i ve smrti? Jak špatný je to úsudek!'),
(4495, 45, 22, 'Bůh stvořil nebesa i zemi jako skutečnost, aby mohl odměnit duši každou za to, co si vysloužila; a nebude jim ukřivděno.'),
(4496, 45, 23, 'Co soudíš o tom, jenž učinil si božstvem svým svou vášeň a jemuž Bůh dal vědomě zbloudit a zapečetil sluch i srdce jeho a zastřel zrak jeho rouškou? Kdo povede jej kromě Boha správnou cestou? Což se nevzpamatují?'),
(4497, 45, 24, 'Říkají: "Neexistuje leč tento náš život pozemský, zemřeme a žijeme a pouze čas nás zahubí!" Však o tom, co říkají, žádných vědomostí nemají a jenom se tak dohadují.'),
(4498, 45, 25, 'A když jsou jim sdělována Naše znamení jasná, nemají jiného důkazu, než že říkají: "Přiveďte tedy předky naše, jste-li pravdomluvní!"'),
(4499, 45, 26, 'Odpověz: "Bůh život vám dává a pak i smrt; a posléze vás shromáždí pro den zmrtvýchvstání, o němž pochybnosti není, však většina z lidí to neví.'),
(4500, 45, 27, 'Bohu náleží království nebes a země; a v den, kdy Hodina udeří, v ten den ztrátu utrpí ti, kdož pravdu popírali."'),
(4501, 45, 28, 'A uvidíš národy všechny klečet a každý národ ke své knize bude povolán se slovy: "Dnes dostanete odměnu svou za to, co jste konali.'),
(4502, 45, 29, 'Tato Naše kniha hovoří proti vám pravdivě a zaznamenávali jsme do ní vše, co jste dělali."'),
(4503, 45, 30, 'Pokud se týká těch, kdož uvěřili a zbožné skutky konali, ty uvede Pán jejich do milosrdenství Svého - a to úspěch je zřetelný.'),
(4504, 45, 31, 'A pokud se týká těch, kdož nevěřili, těm bude řečeno: "Což nebyla vám znamení Naše sdělena? Však byli jste pyšní a byli jste lid hříšný!'),
(4505, 45, 32, 'A když bylo vám řečeno: "Slib Boží je skutečností a věru o Hodině pochyby není!" pak jste odpovídali: "Nevíme, co je to Hodina, máme jen domněnky své a nejsme o ní nikterak přesvědčeni."'),
(4506, 45, 33, 'A objeví se špatnost jejich počínání a obklíčí je to, čemu se posmívali.'),
(4507, 45, 34, 'A bude jim řečeno: "Dnes my na vás zapomeneme tak, jako jste vy zapomněli na setkání s tímto svým dnem; a útočištěm vaším bude oheň a nebudete mít pomocníka žádného.'),
(4508, 45, 35, 'A to je odměna za to, že znamení Boží jste pro smích měli a že vás život pozemský svedl!" A v ten den nebudou odtud vyvedeni a nebudou ani k pokání vyzváni.'),
(4509, 45, 36, 'A budiž chvála Bohu, Pánu nebes i Pánu země, Pánu lidstva veškerého!'),
(4510, 45, 37, 'Jemu náleží velkolepost na nebesích i na zemi a On mocný je i moudrý!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Há mím!'),
(4512, 46, 2, 'Seslání Písma pochází od Boha mocného, moudrého.'),
(4513, 46, 3, 'Stvořili jsme nebesa i zemi a vše, co je mezi nimi, jen jako skutečnost vážnou a na lhůtu stanovenou, avšak ti, kdož neuvěřili, se odvracejí od toho, před čím jsou varováni.'),
(4514, 46, 4, 'Rci: "Viděli jste to, co vedle Boha vzýváte? Ukažte mi tedy to, co stvořili či zda mají podíl nějaký na nebesích! Přineste mi Písmo starší, než je toto, či stopu nějakou vědění, jste-li pravdomluvní!"'),
(4515, 46, 5, 'Kdo je zbloudilejší než ten, jenž vedle Boha vzývá ty, kdož jej ani do dne zmrtvýchvstání nevyslyší a kteří jsou k vzývání jejich lhostejní'),
(4516, 46, 6, 'a kteří, až budou lidé shromážděni, budou jim nepřáteli a budou zapírat jejich uctívání.'),
(4517, 46, 7, 'Když jsou jim přednášena Naše znamení jako důkazy jasné, říkají ti, kdož v pravdu neuvěřili poté, co k nim přišla: "Tohle je kouzelnictví zjevné!"'),
(4518, 46, 8, 'Anebo hovoří: "On si to vymyslil!" Odpověz: "I kdybych si to vymyslil, vy nevládnete od Boha ničím pro mne. On dobře ví, co o něm hlasitě říkáte, a On stačí jako svědek mezi mnou a vámi. A On odpouštějící je i slitovný."'),
(4519, 46, 9, 'Rci: "Já věru nejsem žádnou novotou mezi posly a nevím ani, co stane se mnou a s vámi. Já následuji pouze to, co mi bylo vnuknuto, a jsem jen varovatel zjevný."'),
(4520, 46, 10, 'Rci: "Co si myslíte? Jestliže je toto od Boha a vy jste v to neuvěřili, a jestliže svědek z dítek Izraele dosvědčuje podobnost toho a věří v to, zatímco vy jste hrdopyšní...? Bůh věru nevede lid nespravedlivý."'),
(4521, 46, 11, 'A říkají nevěřící o těch, kdož uvěřili: "Kdyby toto Písmo bylo vskutku dobré, nebyli by nás tihle v tom předešli!" Protože však jím nejsou správně vedeni, hovoří: "Tohle je stará lež!"'),
(4522, 46, 12, 'A již před ním byla Kniha Mojžíšova vůdcem i milosrdenstvím a toto Písmo potvrzuje pravdivost její v jazyce arabském, aby byli varováni nespravedliví a aby bylo zvěstí radostnou pro ty, kdož dobro konají.'),
(4523, 46, 13, 'Ti, kdož hovoří: "Pánem naším je Bůh," a potom se přímou cestou ubírají, ti bát se nemusí a nebudou zarmouceni.'),
(4524, 46, 14, 'Ti stanou se obyvateli zahrady rajské, v níž odměnou za skutky své budou nesmrtelní.'),
(4525, 46, 15, 'Uložili jsme člověku laskavost vůči rodičům jeho; matka jeho jej ve strastech nosila a ve strastech jej porodila a jeho nošení a kojení trvalo třicet městců. A když dosáhl dospělosti své a dosáhl věku čtyřiceti let, zvolal: "Pane můj, vnukni mi, abych byl vděčný za dobrodiní Tvé, jímž jsi zahrnul mne i rodiče mé! Nechť konám skutky zbožné, v nichž nalezneš zalíbení, a učiň pro mne dobrým potomstvo mé. Kajícně se k Tobě obracím a jsem jedním z těch, kdož do vůle Tvé jsou odevzdáni."'),
(4526, 46, 16, 'Od takových přijmeme jejich nejlepší skutky, jež vykonali, a jejich špatné skutky opomineme; ti patří mezi ráje obyvatele, a toto je slib pravdivý, jenž byl jim přislíben.'),
(4527, 46, 17, 'Je však jiný, jenž říká rodičům svým: "Hanba vám! Chcete mi slibovat, že budu vyveden z hrobu, když přede mnou už minula mnohá pokolení, aniž byla vzkříšena?" Oba pak prosí Boha o pomoc řkouce: "Běda ti, uvěř, vždyť slib Boží je pravdivý!" Avšak on odpovídá: "Tohle jsou jen povídačky starých!"'),
(4528, 46, 18, 'Takoví jsou ti, proti nimž se uskuteční slovo Boží o národech džinů a lidí, které již zanikly před nimi - a ty ztrátu utrpěly!'),
(4529, 46, 19, 'A každému se dostane hodnosti podle toho, co vykonal, a Bůh je odmění za jejich činy a nebude jim ukřivděno.'),
(4530, 46, 20, 'A v den, kdy budou předvedeni ti, kdož nevěřili, k ohni, bude jim řečeno: "Utráceli jste dary své výtečné v pozemském životě svém a užívali jste si z nich. Dnes tedy budete odměněni trestem hanby za to, že pyšní jste byli na zemi bez oprávnění, a za to, že jste byli hříšníky!"'),
(4531, 46, 21, 'A zmiň se o bratru ´Ádovců, když varoval lid svůj v zemi přesypů a byli již varovatelé před ním i po něm. "Neuctívejte kromě Boha nikoho! Já věru bojím se pro vás trestu dne nesmírného!"'),
(4532, 46, 22, 'Odpověděli: "Přišels k nám kvůli tomu, abys nás lživě odvrátil od božstev našich? Přines nám tedy to, čím nám hrozíš, jsi-li pravdomluvný!"'),
(4533, 46, 23, 'Řekl: "Vědění je pouze u Boha a já předávám vám jedině to, s čím jsem byl poslán, však vidím, že jste lid pošetilý."'),
(4534, 46, 24, 'A když spatřili mračno blížící se k údolím jejich, zvolali: "Toto mračno nám přinese déšť!" "Nikoliv, to je to, co přáli jste si uspíšit! To vichřice je, v níž trest je bolestný'),
(4535, 46, 25, 'a která zničí dle rozkazu Božího věci všechny!" A zrána bylo vidět jen příbytky jejich opuštěné. A takto odměňujeme lid provinilý.'),
(4536, 46, 26, 'A dali jsme jim věru postavení, jaké jsme vám nedali. A dali jsme jim sluch, zrak i srdce, ale nebyly jim vůbec k ničemu ani uši jejich ani oči jejich ani rozum jejich. A obklopilo je to, čemu se posmívali.'),
(4537, 46, 27, 'A zahubili jsme okolo vás města mnohá a ukazovali jsme jim různá znamení Svá doufajíce, že snad se obrátí.'),
(4538, 46, 28, 'Proč jim nepomohli ti, které si vzali vedle Boha jako božstva, aby jim obětovali? Ba naopak, božstva ta je opustila a taková byla jejich lež i vše, co si vymyslili!'),
(4539, 46, 29, 'A hle, vypravili jsme k tobě houfec džinů, aby naslouchali Koránu. A když přítomni byli jeho přednášení, řekli "Slyšte!", a když bylo skončeno, navrátili se k lidu svému jako varovatelé'),
(4540, 46, 30, 'a řkouce: "Lide náš, slyšeli jsme věru Písmo, jež bylo sesláno po Mojžíšovi, a potvrzující pravdivost toho, co bylo před ním, a vedoucí k pravdě a ke stezce přímé.'),
(4541, 46, 31, 'Lide náš, odpovězte tomu, jenž zve vás k Bohu, a uvěřte v něj! Bůh vám pak odpustí viny vaše a uchrání vás od trestu bolestného.'),
(4542, 46, 32, 'A kdo neodpoví tomu, kdo zve k Bohu, není s to uniknout zásahu Božímu na zemi, a nebude mít kromě Něho ochránce žádného. A takoví jsou v bludu zjevném."'),
(4543, 46, 33, 'Což nevidí, že Bůh, jenž stvořil nebesa a zemi, aniž se unavil jejich stvořením, je též schopen mrtvé oživit? Ba ano, vždyť On všemocný je nad věcmi všemi.'),
(4544, 46, 34, 'V den, kdy budou předvedeni ti, kdož nevěřili, k ohni pekelnému, bude jim řečeno: "Což není toto skutečnost?" A odpoví: "Zajisté, při Pánu našem!" A řekne Bůh: "Ochutnejte tedy trestu za to, že jste nevěřili!"'),
(4545, 46, 35, 'Buď trpělivý, jako byli trpěliví ti z poslů, kteří byli nadáni rozhodností pevnou. A nesnaž se uspíšit pro ně příchod dne, kdy spatří to, co jim bylo slíbeno, a bude se jim zdát, že setrvali v hrobech jen jednu hodinu dne. A toto je sdělení! A kdo jiný bude zahuben než lid hanebníků?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Těm, kdož neuvěřili a odchylují jiné z cesty Boží, nechá Bůh díla jejich zbloudit.'),
(4547, 47, 2, 'Avšak těm, kdož uvěřili a zbožné skutky konali a uvěřili v to, co bylo sesláno Muhammadovi - a to pravda je od Boha pocházející - těm Bůh vymaže špatné činy jejich a napraví myšlení jejich.'),
(4548, 47, 3, 'A to proto, že ti, kdož neuvěřili, následovali nicotné, zatímco ti, kdož uvěřili, následovali pravdu od Pána jejich pocházející. A takto Bůh ukazuje lidem jejich podobenství.'),
(4549, 47, 4, 'A když se střetnete s nevěřícími, udeřte je do šíjí, a až jim způsobíte úplnou porážku, pevně je spoutejte! A potom je buď omilostněte, anebo je propusťte za výkupné, pokud válka neodloží své břímě. A tak budiž! Kdyby Bůh chtěl, pomohl by si sám proti nim, avšak nečiní tak proto, aby vás jedny druhými podrobil zkoušce. A Bůh nedopustí, aby se ztratily skutky těch, kdož byli zabiti na cestě Boží.'),
(4550, 47, 5, 'On správnou cestou je povede a napraví jejich myšlení'),
(4551, 47, 6, 'a uvede je do zahrady rajské, o níž jim dal již vědění.'),
(4552, 47, 7, 'Vy, kteří věříte! Pomůžete-li Bohu, On zajisté pomůže vám a upevní paty vaše!'),
(4553, 47, 8, 'Na ty však, kdož neuvěřili, nechť padne neštěstí a nechť Bůh dá zbloudit dílům jejich!'),
(4554, 47, 9, 'A to proto, že nenáviděli to, co seslal Bůh, a marné bude jejich konání.'),
(4555, 47, 10, 'Což necestovali po zemi a neviděli, jaký byl konec těch, kdož před nimi byli? Bůh je zahubil a podobně se stane i nevěřícím.'),
(4556, 47, 11, 'A to proto, že Bůh je ochráncem těch, kdož uvěřili, zatímco nevěřící nebudou mít ochránce žádného.'),
(4557, 47, 12, 'Bůh věru uvede ty, kdož uvěřili a zbožné skutky konali, do zahrad, pod nimiž řeky tekou, zatímco nevěřící, kteří si užívají a jako dobytek se přecpávají, oheň pekelný budou mít za útulek.'),
(4558, 47, 13, 'A kolik měst, jež byla mocnější silou, než je město tvé, které tě vyhnalo, jsme již zahubili, a neměla pomocníka žádného.'),
(4559, 47, 14, 'Což je ten, jenž důkaz jasný má od Pána svého, roven tomu, jemuž byla zkrášlena špatnost činů jeho, a těm, kdož vášně své následují?'),
(4560, 47, 15, 'Obraz zahrady rajské, jež přislíbena byla bohabojným: v ní řeky jsou s vodou, jež nezahnívá, a řeky mléka, jehož chuť je neměnná, a řeky vína, jež rozkoší je pijícím, a řeky medu očištěného. A bude tam pro ně ovoce všeho druhu i odpuštění Pána jejich. (Je snad ten, kdo bude zde,) roven těm, kdo věčně budou v ohni dlít, a těm, kdož napájeni budou vodou vroucí, jež roztrhá vnitřnosti jejich?'),
(4561, 47, 16, 'A jsou mezi nimi někteří, kdož ti naslouchají, avšak když od tebe odejdou, ptají se těch, jimž dostalo se vědění: "Co to vlastně před chvílí povídal?" A to jsou ti, jichž srdce Bůh zapečetil a kteří následují učení svá scestná.'),
(4562, 47, 17, 'Však těm, kdož po cestě správné jsou vedeni, těm Bůh posílí jejich vedení a dá jim bázeň Boží.'),
(4563, 47, 18, 'Což mohou očekávat něco jiného, než že Hodina k nim znenadání přikvačí, když již se objevily její příznaky? K čemu bude jim dobré její připomínání, až na ně udeří?'),
(4564, 47, 19, 'Věz tedy, že není božstva kromě Boha, a pros za odpuštění vin svých i vin mužů a žen věřících! Bůh dobře ví o přecházení vašem a zná i konečný příbytek váš.'),
(4565, 47, 20, 'A říkají ti, kdož uvěřili: "Proč nebyla seslána nějaká súra?" A když je seslána súra moudře stanovená a je v ní zmíněn boj, tu vidíš ty, v jichž srdcích je choroba, dívat se na tebe pohledem člověka obestřeného mrákotami předsmrtnými. Vhodnější pro ně by byla'),
(4566, 47, 21, 'poslušnost a řeč vhodná. A až se věc stane vážnou, věru bude pro ně lepší, aby byli vůči Bohu upřímní.'),
(4567, 47, 22, 'A je možné, že obrátíte-li se zády, budete šířit pohoršení na zemi a narušíte pokrevní svazky své.'),
(4568, 47, 23, 'Toto jsou ti, jež Bůh proklel, hluchými je učinil a zraky jejich oslepil.'),
(4569, 47, 24, 'Což nebudou uvažovat o Koránu či jsou snad na srdcích jejich zámky?'),
(4570, 47, 25, 'Věru ty, kdož kroky své nazpět obrátili poté, co objasněno jim bylo správné vedení, ty satan svedl a našeptal jim jejich počínání.'),
(4571, 47, 26, 'A to proto, že říkají těm, kterým se hnusí to, co Bůh seslal: "Budeme vás poslouchat ve věcech některých." A Bůh dobře zná jejich tajemství.'),
(4572, 47, 27, 'A jak jim bude, až andělé je povolají, bijíce je po tvářích a po zádech jejich!'),
(4573, 47, 28, 'A to proto, že následovali to, co hněvá Boha, a nenáviděli to, co se Mu líbí; a učinil marnými skutky jejich.'),
(4574, 47, 29, 'Či se domnívají ti, v jichž srdcích je choroba, že Bůh nenávist jejich neodkryje?'),
(4575, 47, 30, 'Kdybychom chtěli, ukázali bychom ti je a věru bys je poznal podle známek jejich; avšak ty poznáš je určitě podle popletené řeči jejich. A Bůh dobře zná vaše konání.'),
(4576, 47, 31, 'A věru vás budeme zkoušet, abychom rozpoznali mezi vámi ty, kdož usilovně se snaží a jsou trpěliví, a abychom prozkoumali zprávy o vás.'),
(4577, 47, 32, 'Věru ti, kdož neuvěřili a od cesty Boží se odvrátili a od posla se odloučili, když už jim bylo objasněno správné vedení, neuškodí v ničem Bohu, jenž marnými učiní skutky jejich.'),
(4578, 47, 33, 'Vy, kteří věříte! Poslouchejte Boha a poslouchejte posla jeho a nečiňte nicotným konání své!'),
(4579, 47, 34, 'Věru těm, kdož neuvěřili a od cesty Boží jiné odvraceli a potom jako nevěřící zemřou, Bůh neodpustí.'),
(4580, 47, 35, 'Neochabujte a nevybízejte k míru, když máte převahu, vždyť Bůh je s vámi a On neošidí vás o dobré skutky vaše.'),
(4581, 47, 36, 'Život pozemský je pouze hra a zábava; jestliže věříte a bojíte se Boha, On dá vám odměny vaše a nebude žádat na vás majetky vaše.'),
(4582, 47, 37, 'A kdyby je na vás žádal a nutil vás, stali byste se lakotnými a On odkryl by zlobu vaši.'),
(4583, 47, 38, 'Hle, takoví jste - jste vyzýváni, abyste rozdávali na cestě Boží, avšak mezi vámi jsou lakomci. Kdo však je lakomý, je jim ke škodě vlastní, neboť Bůh je soběstačný, zatímco vy jste potřební. A obrátíte-li se zády tedy On vás vystřídá lidem jiným, jenž vám nebude podoben.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Věru jsme ti dali dosáhnout úspěchu zřetelného,'),
(4585, 48, 2, '(abys zvěděl), že Bůh ti odpustil tvé viny dřívější i pozdější a že dobrodiní Své k tobě dovršil a že vede tě stezkou přímou'),
(4586, 48, 3, 'a že Bůh ti pomáhá pomocí mocnou!'),
(4587, 48, 4, 'On je ten, jenž sesílá sakínu do srdcí věřících, aby přidali další víru k víře své - a Bohu patří vojska nebeská i pozemská a Bůh vševědoucí je i moudrý'),
(4588, 48, 5, 'a aby věřící muže a věřící ženy uvedl do zahrad, pod nimiž řeky tekou a v nichž nesmrtelní budou, a aby špatné skutky jejich vymazal - a to u Boha je úspěch nesmírný -'),
(4589, 48, 6, 'a aby potrestal pokrytecké muže i ženy a modloslužebníky a modloslužebnice, kteří mají o Bohu špatné mínění. Proti nim ať obrátí se osud špatný, nechť Bůh se na ně rozhněvá a prokleje je a peklo pro ně připraví - a jak hnusný to je cíl konečný.'),
(4590, 48, 7, 'A Bohu patří vojska nebeská i pozemská a Bůh mocný je i moudrý.'),
(4591, 48, 8, 'Vyslali jsme tě věru jako svědka, hlasatele zvěsti radostné i jako varovatele,'),
(4592, 48, 9, 'abyste v Boha a posla Jeho uvěřili, pomáhali Mu a uctívali a slavili Jej za jitra i za večera.'),
(4593, 48, 10, 'Ti, kdož ti věrnost přisahají, přísahají vlastně Bohu a ruka Boží spočívá na rukou jejich. Kdo poruší úmluvu, tedy ji poruší jen ke škodě vlastní, kdo však věrně dodrží úmluvu s Bohem uzavřenou, tomu On uštědří odměnu nesmírnou.'),
(4594, 48, 11, 'A řeknou ti, kdož zůstali pozadu z kočovných Arabů: "Byli jsme zaměstnáni stády svými a rodinami svými, pros za nás o odpuštění!" A hovoří jazyky svými to, co v srdcích nemají. Rci: "Kdo zmůže něco pro vás u Boha, jestliže se Jemu zlíbí vám uškodit anebo vám něčím prospět? Ba ne! Bůh dobře je zpraven o všem, co děláte!'),
(4595, 48, 12, 'Naopak, vy jste se domnívali, že posel a věřící se již nikdy k rodinám svým nevrátí! A bylo vám to okrášleno v srdcích vašich, leč domnívali jste se špatnými domněnkami! A vy jste věru lid nevědomý!"'),
(4596, 48, 13, 'A kdo nevěří v Boha a posla Jeho... pro nevěřící jsme připravili plamen šlehající.'),
(4597, 48, 14, 'Bohu náleží království na nebesích i na zemi a On odpouští, komu chce, a potrestá, koho chce a Bůh je odpouštějící, slitovný.'),
(4598, 48, 15, 'A řeknou ti, kdož zůstali vzadu, když chystáte se na pochod, abyste se zmocnili kořisti: "Dovolte nám, abychom vás následovali!" A rádi by takto změnili slovo Boží! Rci: "Nebudete nás následovat - tak to již dříve rozhodl Bůh!" A řeknou: "Nikoliv, vy nám to jen nepřejete!" Ba ne, oni jsou jen málo chápaví.'),
(4599, 48, 16, 'Rci těm z kočovných Arabů, kteří vzadu zůstali: "Budete vyzváni k boji proti lidu obdařenému velkou bojovností; a budete proti nim bojovat, leč by přijali islám. Uposlechnete-li, Bůh vám dá odměnu překrásnou, avšak obrátíte-li se zády, tak jak jste se již obrátili dříve, potrestá vás trestem bolestným."'),
(4600, 48, 17, 'A není hříchem pro slepého ani pro chromého ani pro nemocného, jestliže se neúčastní. A kdokoliv poslechne Boha a posla Jeho, bude uveden do zahrad, pod nimiž řeky tekou, a kdokoliv se zády obrátí, toho postihne trest bolestný.'),
(4601, 48, 18, 'Bůh věru již nalezl zalíbení ve věřících, když ti přísahali věrnost pod stromem, a On dobře ví, co bylo v srdcích jejich. A seslal k nim sakínu a odmění je brzkým úspěchem'),
(4602, 48, 19, 'i kořistí hojnou, jíž se zmocní. A Bůh mocný je i moudrý.'),
(4603, 48, 20, 'Bůh vám přislíbil, že se zmocníte kořisti hojné, a On ji pro vás uspíšil a odvrátil od vás ruce oněch lidí. A učinil tak, aby se to stalo znamením pro věřící a aby vás uvedl na stezku přímou.'),
(4604, 48, 21, 'A přislíbil vám i jinou, nad níž dosud nemáte moc, zatímco ji Bůh již objal ve Svém vědění, vždyť Bůh nad věcí každou je všemocný.'),
(4605, 48, 22, 'A kdyby proti vám byli bojovali ti, kdož jsou nevěřící, věru by se byli otočili zády; a nenaleznou později ani ochránce, ani pomocníka,'),
(4606, 48, 23, 'podle zvyklosti Boží, která se již mnohokrát předtím naplnila. A ve zvyklosti Boží nenalezneš změnu žádnou.'),
(4607, 48, 24, 'On je ten, jenž odvrátil ruce jejich od vás a ruce vaše od nich v údolí Mekky poté, co vám nad nimi uštědřil vítězství; a Bůh dobře pozoruje vše, co děláte.'),
(4608, 48, 25, 'A to jsou ti, kteří neuvěřili a odháněli vás od Mešity posvátné a zadržovali oběti, aby nedospěly ke svým obětištím. A kdyby nebylo věřících mužů a žen, které jste neznali a které jste mohli pošlapat, takže byste se na nich nevědomky dopustili viny... a Bůh uvádí do milosrdenství Svého, koho chce; a kdyby se byli oddělili, věru bychom byli potrestali ty z nich, kdož nevěří, trestem bolestným.'),
(4609, 48, 26, 'Hle, tehdy ti, kdož neuvěřili, vložili do srdcí svých zuřivost, zuřivost nevědomosti. A Bůh seslal sakínu Svou poslu Svému a věřícím a učinil pro ně neodlučným slovo bohabojnosti; a zasloužili si ho a byli ho nejvýš hodni. A Bůh o každé věci je vševědoucí.'),
(4610, 48, 27, 'Bůh zajisté již dokázal poslu Svému pravdivost jeho snu: "Vskutku vstoupíte do Posvátné mešity, bude-li chtít Bůh, bezpečni a s oholenými hlavami a ostříhanými nehty a vousy a bez jakékoliv bázně!" A On věděl to, co jste neznali, a kromě toho vám připravil blízký úspěch.'),
(4611, 48, 28, 'A On je ten, jenž vyslal posla Svého se správným vedením a s náboženstvím pravdivým, aby mu dal zvítězit nad každým jiným náboženstvím. A Bůh je svědkem dostačujícím!'),
(4612, 48, 29, 'Muhammad je posel Boží; a ti, kdož jsou s ním, strašní jsou proti nevěřícím, však laskaví mezi sebou. A vidíš je při modlitbě se sklánět a padat na tváře v úsilí po přízni a zalíbení Božím. A známky jejich jsou na tvářích jejich následkem modlení. A takto byli spodobněni v Tóře, zatímco podobenství jejich v Evangeliu zní: jsou jako sadba, jejímž výhonkům Bůh dává vyrůst a posiluje je, až stanou se pevnými a drží se zpříma na stéblech svých, k radosti tomu, kdo ji zasel, aby tím Bůh rozhněval nevěřící. A Bůh přislíbil těm z nich, kdož uvěřili a zbožné skutky konali, odpuštění a odměnu nesmírnou.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Vy, kteří věříte! Nesnažte se předejít Boha a posla jeho! Buďte bohabojní, vždyť Bůh věru je slyšící, vševědoucí!'),
(4614, 49, 2, 'Vy, kteří věříte! Nezvedejte hlas svůj nad hlas prorokův a neobracejte se k němu s řečí hlučnou, tak jak to děláte mezi sebou, aby se skutky vaše nestaly marnými, aniž o tom budete mít tušení.'),
(4615, 49, 3, 'Věru ti, kdož snižují hlasy své před poslem Božím jsou ti, jejichž srdce Bůh podrobil zkoušce bohabojnosti a těm náleží odměna nesmírná a odpuštění.'),
(4616, 49, 4, 'Avšak většina z těch, kdož tě hlasitě vyvolávají z komnat tvých, jsou lidé nerozumní.'),
(4617, 49, 5, 'Kdyby trpělivě vyčkali, dokud k nim nevyjdeš, bylo by to pro ně lepší; a Bůh je odpouštějící, slitovný.'),
(4618, 49, 6, 'Vy, kteří věříte! Přijde-li k vám hanebník s nějakou zvěstí, snažte se ji objasnit tak, abyste z nevědomosti své nepoškodili lidi a abyste nemusili litovat toho, co jste učinili.'),
(4619, 49, 7, 'Vězte, že kdyby posel Boží, který mezi vámi dlí, vás uposlechl ve většině záležitostí, věru byste škodu utrpěli! Avšak Bůh způsobil, že milujete víru, a učinil ji krásnou v srdcích vašich, zatímco vám zošklivil nevěrectví, hanebnosti a neposlušnost. A to jsou ti, kdož řídí se cestou správnou'),
(4620, 49, 8, 'díky laskavosti a dobrodiní Božímu. A Bůh je vševědoucí, moudrý.'),
(4621, 49, 9, 'Bojují-li mezi sebou dvě skupiny věřících, usmiřte je! A činí-li jedna skupina z těch dvou bezpráví druhé, bojujte proti těm, kdo činí bezpráví, dokud se před rozkazem Božím neskloní! A když se skloní, usmiřte obě strany spravedlivě a buďte nestranní, neboť Bůh věru miluje ty, kdož jsou nestranní!'),
(4622, 49, 10, 'Věřící jsou si přece bratry; usmiřujte tedy oba bratry své a buďte bohabojní - snad dostane se vám smilování!'),
(4623, 49, 11, 'Vy, kteří věříte! Nechť neposmívají se jedni lidé druhým, možná že tito jsou lepší než oni! A nechť jedny ženy se neposmívají druhým, možná že tyto jsou lepší než ony! Neurážejte se vzájemně a nenazývejte se přezdívkami! A jak hnusný je název "hanebník" poté, co byla přijata víra! A ti, kdož se nebudou kát, jsou věru nespravedliví!'),
(4624, 49, 12, 'Vy, kteří věříte! Střezte se přílišného podezřívání druhých, vždyť některá podezření jsou hříchem! Nevyzvídejte a nepomlouvejte se vzájemně! Což si někdo z vás přeje jíst maso bratra svého mrtvého - vždyť by vám to bylo odporné! Buďte bohabojní, neboť Bůh věru je blahovolný ke kajícníku a slitovný.'),
(4625, 49, 13, 'Lidé, věru jsme vás stvořili z muže a ženy a učinili jsme z vás národy a kmeny, abyste se vzájemně poznali. Avšak nejvznešenější z vás před Bohem je ten, kdo je nejbohabojnější - a Bůh je vševědoucí a dobře zpravený.'),
(4626, 49, 14, 'A říkají kočovní Arabové: "Uvěřili jsme!" Rci: "Neuvěřili jste! Spíše řekněte:, Přistoupili jsme k islámu!?, neboť víra ještě nevešla do srdcí vašich. Budete-li poslouchat Boha a posla Jeho, On věru nedopustí, aby ztratilo se něco ze skutků vašich, vždyť Bůh je odpouštějící, slitovný!"'),
(4627, 49, 15, 'Věřícími jsou pouze ti, kdož uvěřili v Boha a posla Jeho a poté nepodlehli pochybnostem, nýbrž bojovali majetky i osobami svými na cestě Boží - a tito jsou věru upřímní.'),
(4628, 49, 16, 'Rci: "Chcete snad poučovat Boha o náboženství svém, když Bůh dobře zná vše, co na nebesích je i na zemi? Bůh o každé věci je vševědoucí."'),
(4629, 49, 17, 'A domnívají se, že tobě udělali milost, když přistoupili k islámu. Rci: "Netvrďte mi, že vaše přistoupení k islámu je nějakou milostí vůči mně! Naopak Bůh vám učinil milost tím, že vás přivedl k víře - jste-li pravdomluvní!"'),
(4630, 49, 18, 'A Bůh vskutku zná nepoznatelné na nebesích i na zemi a Bůh vše, co děláte, jasně zří.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Qáf. Při Koránu oslavovaném!'),
(4632, 50, 2, 'Ba naopak, oni se diví, že k nim přišel varovatel z jejich středu; a hovoří nevěřící: "To je věc podivná!'),
(4633, 50, 3, 'Jak to, že až zemřeme a staneme se prachem...? To bude ale návrat z daleka!"'),
(4634, 50, 4, 'My dobře víme, které z nich země pohltila, neboť máme knihu, jež záznamy střeží.'),
(4635, 50, 5, 'A naopak za lež pravdu prohlašovali, když přišla k nim, a jsou teď v zamotaném postavení.'),
(4636, 50, 6, 'Což se nepodívali na nebe nad sebou, jak jsme je zbudovali a ozdobili a že není na něm žádných trhlin?'),
(4637, 50, 7, 'A zemi jsme rozprostřeli a po ní pevně stojící hory rozhodili a na ní vyrůst jsme dali druhům všem překrásných rostlin'),
(4638, 50, 8, 'pro potěchu očí i jako připomenutí pro každého služebníka kajícného.'),
(4639, 50, 9, 'A seslali jsme z nebe vodu požehnanou, z níž dali jsme vyrůst zahradám i zrní, jež dává úrodu'),
(4640, 50, 10, 'a palmám ztepilým, jež v řadách nesou trsy plodů,'),
(4641, 50, 11, 'jako obživu služebníkům Našim. A vodou oživujeme i mrtvou zem, a takové bude i vaše vzkříšení.'),
(4642, 50, 12, 'A již před nimi to vše za lež prohlašoval lid Noemův, obyvatelé ar-Rassu i Thamúdovci,'),
(4643, 50, 13, '´Ádovci, Faraón i Lotovi soukmenovci,'),
(4644, 50, 14, 'lidé Houštin i národ tubba´ů - ti všichni posly za lháře prohlásili, však uskutečnila se hrozba nad nimi.'),
(4645, 50, 15, 'Cožpak jsme byli prvním stvořením zmoženi? A přece oni pochybují o novém stvoření!'),
(4646, 50, 16, 'My stvořili jsme člověka a dobře víme, co duše jeho mu našeptává. Vždyť blíže jsme mu než jeho tepna krční,'),
(4647, 50, 17, 'když přijímají jeho slova dva andělé, zprava i zleva sedící.'),
(4648, 50, 18, 'A člověk nevyřkne slovo jediné, aniž je vedle něho připraven dohlížitel.'),
(4649, 50, 19, 'A pak dostaví se smrti opilost jako skutečnost a tohle je to, čemu ses vyhnout chtěl!'),
(4650, 50, 20, 'A bude na pozoun zatroubeno - a to bude den trestu slíbeného!'),
(4651, 50, 21, 'A každá duše se dostaví majíc s sebou průvodce a svědka jednoho.'),
(4652, 50, 22, '"Dosud lhostejný jsi byl k tomuto dni, ale sňali jsme ti roušku tvou, takže zrak tvůj se dnes zaostřil."'),
(4653, 50, 23, 'A řekne jeho věrný druh: "Tady je to, co jsem u sebe ze svědectví připravil!"'),
(4654, 50, 24, '"Vy dva do pekla vhoďte každého nevěřícího zarputilého,'),
(4655, 50, 25, 'bránícího dobrému, nepřátelského a pochybovačného,'),
(4656, 50, 26, 'který vedle Boha jiná božstva postavil! Vhoďte jej do trestu strašného!"'),
(4657, 50, 27, 'A řekne jeho věrný druh: "Pane náš, já jsem ho nesváděl, to on sám v dalekém bloudění byl."'),
(4658, 50, 28, 'I řekne Bůh: "Nehádejte se tu přede mnou, vždyť jsem vás již dříve o své hrozbě uvědomil!'),
(4659, 50, 29, 'Slovo Mé je nezměnitelné a já neukřivdím žádnému svému služebníku'),
(4660, 50, 30, 'v den, kdy pekla se zeptáme:, Jsi již naplněno?? a ono odpoví:, Je ještě přírůstek nějaký?? "'),
(4661, 50, 31, 'A přiblížen bude bohabojným ráj nedaleký'),
(4662, 50, 32, 'a bude řečeno: "Toto je to, co vám bylo slíbeno... všem kajícníkům zákonů dbalým,'),
(4663, 50, 33, 'kteří v tajnosti se Milosrdného obávají a přicházejí se srdcem pokorným.'),
(4664, 50, 34, 'Vstupte tam v míru, toto je den nesmrtelnosti!"'),
(4665, 50, 35, 'A čehokoliv se jim tam zachce, budou mít, a u Nás je ještě přídavek v hojnosti!'),
(4666, 50, 36, 'A kolik jsme již zahubili před nimi pokolení, jež mocnější než oni byla silou! Hledejte tedy v zemích všech, zda naleznete útočiště!'),
(4667, 50, 37, 'V tom věru je připomenutí pro toho, kdo ruce má či uši nastavuje a je sám svědkem očitým.'),
(4668, 50, 38, 'Stvořili jsme nebesa a zemi a to, co je mezi nimi, v šesti dnech, aniž se nás dotkla únava.'),
(4669, 50, 39, 'Snášej trpělivě jejich řeči a před slunce východem a západem oslavuj chválu svého Pána'),
(4670, 50, 40, 'a v noci též Jej oslavuj i na konci každého padnutí na zem!'),
(4671, 50, 41, 'A naslouchej dni, kdy hlasatel zavolá z místa blízkého;'),
(4672, 50, 42, 'ten den, kdy zaslechnou křik skutečně, to bude den vzkříšení.'),
(4673, 50, 43, 'My věru život i smrt dáváme a u Nás je cíl konečný.'),
(4674, 50, 44, 'V den, kdy se země rozpoltí, vyvrhne je se spěchem a bude pro nás toto shromáždění činem snadným;'),
(4675, 50, 45, 'my nejlépe známe, co hovoří, a ty nejsi ustanoven, abys je nutil násilím. Připomínej tedy pomocí Koránu těm, kdo hrozby Mé se obávají.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Při rychle běžících,'),
(4677, 51, 2, 'břímě nesoucích,'),
(4678, 51, 3, 'lehce plynoucích'),
(4679, 51, 4, 'a rozkaz rozdělujících!'),
(4680, 51, 5, 'To, co vám je slíbeno, je vskutku pravdivé'),
(4681, 51, 6, 'a soud dozajista nastane!'),
(4682, 51, 7, 'Při nebi plném drah hvězdných,'),
(4683, 51, 8, 'vy libujete si v řečech rozporných,'),
(4684, 51, 9, 'od nichž odvrátí se ten, kdo byl odvrácen.'),
(4685, 51, 10, 'Nechť zhynou lháři prolhaní,'),
(4686, 51, 11, 'kteří v hlouposti své jsou lhostejní'),
(4687, 51, 12, 'a ptají se: "Kdy dostaví se den poslední?"'),
(4688, 51, 13, 'V den, kdy budou ohněm zkoušeni!'),
(4689, 51, 14, '"Okuste tuto svou zkoušku! Tohle je to, co přáli jste si uspíšit!"'),
(4690, 51, 15, 'Však v zahradách a u pramenů bohabojní budou dlít'),
(4691, 51, 16, 'přijímajíce to, co Pán jejich jim dává, neb před tím dobré skutky konali'),
(4692, 51, 17, 'a jen malou část noci spávali'),
(4693, 51, 18, 'a za úsvitu o odpuštění prosívali'),
(4694, 51, 19, 'a ze svých majetků vždy patřičně žebrákům a nemajetným dávali.'),
(4695, 51, 20, 'Na zemi znamení jsou pro věřící přesvědčené,'),
(4696, 51, 21, 'a také ve vašich duších. Což jasně to nezříte?'),
(4697, 51, 22, 'A na nebesích je váš příděl obživy a rovněž to, co vám je slíbeno.'),
(4698, 51, 23, 'Při Pánu nebes i země, toto je skutečnost stejná jako to, že hovoříte.'),
(4699, 51, 24, 'Zdaž doneslo se ti o Abrahamových poctěných hostech vyprávění?'),
(4700, 51, 25, 'Hle, vešli k němu a pravili "Mír", a on odvětil: "I vám mír, lidé neznámí!"'),
(4701, 51, 26, 'A k rodině své odešel a tučné tele připravil'),
(4702, 51, 27, 'a se slovy "Což jíst nebudete?" jim je předložil.'),
(4703, 51, 28, 'A pocítil strach před nimi, však oni mu pak pravili "Neboj se!" a zvěst o narození chlapce moudrého mu oznámili.'),
(4704, 51, 29, 'Pak žena jeho přiběhla vzrušena a do tváře se bila a volala: "Vždyť já neplodná jsem již stařena!"'),
(4705, 51, 30, 'I odpověděli: "Takto Pán tvůj děl a On věru moudrý je i vševědoucí."'),
(4706, 51, 31, 'A otázal se Abraham: "Jaká věc vás přivádí, vyslanci?"'),
(4707, 51, 32, 'Odvětili: "Posláni jsme byli mezi lidi hříšné,'),
(4708, 51, 33, 'abychom na ně seslali kameny z hlíny pálené,'),
(4709, 51, 34, 'u Pána tvého jmény přestupníků označené."'),
(4710, 51, 35, 'Ty z nich, kdož věřící byli, jsme odtamtud vyvedli,'),
(4711, 51, 36, 'však dům jen jediný těch, kdož do vůle Boží se odevzdali, jsme tam nalezli.'),
(4712, 51, 37, 'A zanechali jsme je jako znamení těm, kdo trestu bolestného se obávají.'),
(4713, 51, 38, '... A o Mojžíšovi; hle, vyslali jsme jej k Faraónovi se zjevnou pravomocí,'),
(4714, 51, 39, 'ten však se obrátil i se svou oporou a prohlásil: "Kouzelník je to nebo blázen!"'),
(4715, 51, 40, 'A uchvátili jsme jej i vojska jeho a do moře jsme je uvrhli, neb pokárání byl hoden.'),
(4716, 51, 41, '... A o Ádovcích; hle, vyslali jsme proti nim vichřici zničující,'),
(4717, 51, 42, 'jež nic z toho, nad čím se převalila, nenechala, aby v prach to neproměnila.'),
(4718, 51, 43, '... A o Thamúdovcích; hle, bylo jim řečeno: Užívejte si ještě nějakou chvíli!'),
(4719, 51, 44, 'Však rozkazu Pána svého neposlechli a zemětřesení je postihlo, zatímco se dívali,'),
(4720, 51, 45, 'a nebyli schopni se zpříma postavit a nebylo jim pomoci.'),
(4721, 51, 46, '... A o lidu Noemově před nimi, a to byli hanebníci!'),
(4722, 51, 47, 'Nebe jsme důkladně postavili, vždyť My jsme rozšiřovateli,'),
(4723, 51, 48, 'a zemi jsme rozprostřeli - a jak výteční jsme v tom byli!'),
(4724, 51, 49, 'A každou věc jsme v párech stvořili - snad si to připomenete!'),
(4725, 51, 50, 'K Bohu se utíkejte, neb já jsem od Něho pro vás varovatel zjevný,'),
(4726, 51, 51, 'a nepřidružujte k Bohu žádné jiné božstvo, neb já jsem od Něho pro vás varovatel zjevný!'),
(4727, 51, 52, 'A stejně tak nepřišel k těm, kdož před těmito byli, žádný posel, aby o něm neprohlásili: "Kouzelník je to nebo blázen!"'),
(4728, 51, 53, 'Což po sobě to dědí? Nikoliv, však lid je to vzpurný.'),
(4729, 51, 54, 'Odvrať se proto od nich, a nebudeš za to kárán!'),
(4730, 51, 55, 'Připomínej, vždyť připomenutí je věřícím prospěšné!'),
(4731, 51, 56, 'A džiny a lidi jsem jedině proto stvořil, aby Mne uctívali,'),
(4732, 51, 57, 'a nechci od nich žádný příděl obživy a netoužím po tom, aby Mne živili,'),
(4733, 51, 58, 'vždyť Bůh sám je živitel, vládce síly a nezviklatelný!'),
(4734, 51, 59, 'Věru těm, kdož křivdili, se dostane přídělu podobného, přídělu jim rovných před nimi; nechť se tedy o uspíšení nesnaží!'),
(4735, 51, 60, 'Běda nevěřícím v ten jejich den, jenž byl jim přislíben!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Při hoře Sinaji,'),
(4737, 52, 2, 'při Písmu sepsaném'),
(4738, 52, 3, 'na pergamenu rozprostřeném,'),
(4739, 52, 4, 'při chrámu navštěvovaném,'),
(4740, 52, 5, 'při střeše zdvižené,'),
(4741, 52, 6, 'při moři vzedmutém,'),
(4742, 52, 7, 'trest Pána tvého nevyhnutelně udeří'),
(4743, 52, 8, 'a není nikoho, kdo jej zadrží!'),
(4744, 52, 9, 'V den, kdy kymácení zakymácí nebem'),
(4745, 52, 10, 'a hory se vydají na cestu pochodem,'),
(4746, 52, 11, 'běda v ten den těm, kdo za lež to pokládali'),
(4747, 52, 12, 'a jen v planém tlachání zábavu nalézali!'),
(4748, 52, 13, 'V ten den budou vrženi do ohně pekelného postrkem!'),
(4749, 52, 14, '"Toto je oheň ten, jenž nazvali jste výmyslem.'),
(4750, 52, 15, 'Kouzlo je to, či snad nevidíte?'),
(4751, 52, 16, 'Hořte v něm a vytrvejte či nevytrvejte - vše pro vás jedno je, vždyť odměnu jen za to, co konali jste, dostáváte!"'),
(4752, 52, 17, 'A bohabojní věru budou v zahradách a ve slastech,'),
(4753, 52, 18, 'radujíce se z toho, co Pán jejich jim uštědřil a že Pán jejich je trestu pekelného ušetřil.'),
(4754, 52, 19, '"Jezte a pijte ve zdraví v odměnu za to, co jste konali,'),
(4755, 52, 20, 'a spočívejte na pohovkách v řadách postavených!" A oženíme je tam s dívkami velkých očí černých.'),
(4756, 52, 21, 'A těm, kdo uvěřili a jež jejich potomci ve víře následovali, potomky jejich do ráje přivedeme a nic jim z jejich skutků neubereme. A každý člověk je toho, co činy svými si vysloužil, ručitelem.'),
(4757, 52, 22, 'A ovocem a masem je opatříme podle jejich přání'),
(4758, 52, 23, 'a tam mezi sebou si poháry podávat budou, z nichž nepojde ani prázdné tlachání, ani k hříchům svádění;'),
(4759, 52, 24, 'a budou je tam chlapci obcházet, kteří perlám střeženým se budou podobat.'),
(4760, 52, 25, 'A budou se jeden k druhému obracet a vyptávat'),
(4761, 52, 26, 'řkouce: "Byli jsme kdysi o rodiny své plni ne klidu úzkostného,'),
(4762, 52, 27, 'však Bůh nám milost uštědřil a ochránil nás před trestem vichru ohnivého,'),
(4763, 52, 28, 'zajisté vzývali jsme Ho již předtím, neb On věru dobroditelem je slitovným!"'),
(4764, 52, 29, 'Připomínej! A díky dobrodiní Božímu nejsi ani věštec, ani džiny posedlý blázen!'),
(4765, 52, 30, 'Hovoří snad oni: "Básník je to! Vyčkejme si tedy, jak osud jeho bude změněn!"'),
(4766, 52, 31, 'Rci: "Jen čekejte, já s vámi jsem také čekající!"'),
(4767, 52, 32, 'Anebo jsou to snad sny, jež jim to vnukují, či lidé to jsou vzpurní?'),
(4768, 52, 33, 'Nebo říkají: "On si to vymyslil!" Však sami tomu nevěří.'),
(4769, 52, 34, 'Nechť tedy přijdou s vyprávěním podobným tomuto, jestliže pravdu hovoří!'),
(4770, 52, 35, 'Cožpak z ničeho byli stvořeni, či sami jsou stvořiteli,'),
(4771, 52, 36, 'anebo stvořili nebesa i zemi? Ne, oni nejsou přesvědčeni!'),
(4772, 52, 37, 'Či pokladnice Pána tvého vlastní, anebo jsou těmi, kdo zapisují,'),
(4773, 52, 38, 'či mají žebřík, na němž naslouchají? Nechť tedy ti, kdo z nich naslouchají, přinesou zjevné oprávnění!'),
(4774, 52, 39, 'Bůh že měl by míti dcery, zatímco vy máte syny?'),
(4775, 52, 40, 'Či žádáš snad nějakou odměnu, a oni jsou dluhy zatíženi?'),
(4776, 52, 41, 'Či znají nepoznatelné, takže zapisovat je mohou,'),
(4777, 52, 42, 'anebo o úklady usilují? Však nevěřící sami obelstěni budou.'),
(4778, 52, 43, 'Či mají božstvo jiné, než Bůh je? Oč slavnější Bůh než to, co se k Němu přidružuje!'),
(4779, 52, 44, 'A i kdyby viděli kusy nebe padat, určitě by říkali: "To jsou jen mraky zhuštěné."'),
(4780, 52, 45, 'Nech je tedy být až do doby, kdy setkají se svým dnem, v němž budou bleskem sraženi,'),
(4781, 52, 46, 'se dnem, v němž nic nebude jim platna jejich lest a nedostane se jim pomoci.'),
(4782, 52, 47, 'Těm, kdo křivdili, se věru trestu dostane a ještě něčeho víc, však většina z nich to neví.'),
(4783, 52, 48, 'Ty však neochvějně na rozsudek svého Pána čekej, vždyť věru jsi Nám na očích! A oslavuj chválu Pána svého, když vstáváš,'),
(4784, 52, 49, 'a za noci i při hvězd odchodu Jej oslavuj!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Při hvězdě padající!'),
(4786, 53, 2, 'Váš druh nebloudí, ani nebyl sveden,'),
(4787, 53, 3, 'ani nehovoří z vlastního popudu svého.'),
(4788, 53, 4, 'Je to vnuknutí pouze, jež bylo mu vnuknuto,'),
(4789, 53, 5, 'jemuž naučil jej silný mocí,'),
(4790, 53, 6, 'silou vládnoucí; a vzpřímeně se tyčil,'),
(4791, 53, 7, 'zatímco na nejvyšším obzoru byl.'),
(4792, 53, 8, 'Potom se přiblížil a dolů se spustil,'),
(4793, 53, 9, 'až na vzdálenost dvou luků či ještě blíže byl,'),
(4794, 53, 10, 'a vnukl svému služebníkovi to, co mu vnukl.'),
(4795, 53, 11, 'A srdce jeho mu v tom, co viděl, nelhalo.'),
(4796, 53, 12, 'Chcete snad pochybnost vyslovit o tom, co zřel?'),
(4797, 53, 13, 'A věru jej viděl již u příležitosti sestoupení jiného'),
(4798, 53, 14, 'poblíže lotosového stromu u okraje nejzazšího,'),
(4799, 53, 15, 'u něhož je zahrada příbytku věčného.'),
(4800, 53, 16, 'A když přikrylo lotosový strom to, co jej krylo,'),
(4801, 53, 17, 'oko se neodvrátilo ani nebloudilo,'),
(4802, 53, 18, 'a věru spatřil on největší ze znamení Pána svého!'),
(4803, 53, 19, 'Uvažovali jste o al-Lát a al-´Uzzá'),
(4804, 53, 20, 'a Manát, té třetí, jiné?'),
(4805, 53, 21, 'Vy syny byste měli mít a On pouze dcery?'),
(4806, 53, 22, 'To vskutku rozdělení by bylo nerovné!'),
(4807, 53, 23, 'Ony nejsou nic než jména, kterými jste je vy a vaši otcové nazvali a o nichž neseslal Bůh žádné zplnomocnění. A oni pouze své dohady a to, k čemu srdce jejich tíhnou, následují, ačkoliv se jim již od Pána jejich dostalo správného vedení.'),
(4808, 53, 24, 'Což člověk má mít vše, po čem touží?'),
(4809, 53, 25, 'Však Bohu poslední i první život patří.'),
(4810, 53, 26, 'A vůbec nic nepomůže přímluva andělů, ať sebevíc jich je na nebi, leda tehdy, když k tomu Bůh svolí, a pro toho, koho chtít bude a jehož si oblíbí.'),
(4811, 53, 27, 'A vskutku ti, kdož nevěří v život budoucí, anděly ženskými jmény nazývají,'),
(4812, 53, 28, 'byť o tom žádné vědění nemají a jen dohady své následují. Však dohady nic proti skutečnosti nezmohou!'),
(4813, 53, 29, 'Odvrať se tedy od těch, kdo zády k Našemu připomenutí se obracejí a pouze k tomuto pozemskému životu tíhnou.'),
(4814, 53, 30, 'Takový je rozsah jejich vědění! A Pán tvůj ví dobře o tom, kdo z cesty Jeho zbloudil, a zná nejlépe i toho, kdo po ní kráčí.'),
(4815, 53, 31, 'Bohu náleží vše na nebesích a na zemi, aby mohl odměnit ty, kdož špatné páchali, a aby odměnil ty, kdož dobré konali, odměnou nejkrásnější,'),
(4816, 53, 32, 'ty, kdož těžkých hříchů a nepravostí se varovali a jen drobných prohřešků se dopouštěli. A Pán tvůj věru rozsáhlým odpuštěním vládne a znal vás dobře už tehdy, když vytvořil vás ze země a když jste byli zárodky v lůnech matek svých. Nepovažujte tedy sebe za očištěné! A On nejlépe zná ty, kdo jsou bohabojní.'),
(4817, 53, 33, 'Co soudíš o tom, jenž zády se obrací'),
(4818, 53, 34, 'a málo jen rozdává a lakotí?'),
(4819, 53, 35, 'Má snad vědomost o nepoznatelném, takže je vidí?'),
(4820, 53, 36, 'Či nebylo mu sděleno, co obsahují svitky Mojžíše'),
(4821, 53, 37, 'a Abrahama, jenž byl věrný?'),
(4822, 53, 38, 'Že totiž žádná duše obtížená břímě jiné duše neponese,'),
(4823, 53, 39, 'že člověku se jen toho, oč usiloval, dostane,'),
(4824, 53, 40, 'že výsledek jeho úsilí bude viděn'),
(4825, 53, 41, 'a potom plnou odměnou bude odměněn,'),
(4826, 53, 42, 'že u Pána tvého je konečný cíl jen,'),
(4827, 53, 43, 'že On rozesmává i rozplakává,'),
(4828, 53, 44, 'že On smrt i život dává,'),
(4829, 53, 45, 'že On stvořil pohlaví mužské a ženské'),
(4830, 53, 46, 'z kapky semene vystříknuté,'),
(4831, 53, 47, 'že Jemu přísluší stvoření druhé,'),
(4832, 53, 48, 'že On obohacuje a uspokojuje,'),
(4833, 53, 49, 'že On je ten, kdo Siria pánem je,'),
(4834, 53, 50, 'že On dávným ´Ádovcům zahynout dal'),
(4835, 53, 51, 'i Thamúdovcům, a žádného nenechal,'),
(4836, 53, 52, 'a před nimi lidu Noemovu, neb to lid velmi nespravedlivý a vzpurný byl,'),
(4837, 53, 53, 'a také Vyvrácené město zahubil,'),
(4838, 53, 54, 'a pokrylo je to, co je pokrylo.'),
(4839, 53, 55, 'A které z dobrodiní Pána svého chceš popírat?'),
(4840, 53, 56, 'A toto je varování podobné varováním dřívějších.'),
(4841, 53, 57, 'Přiblížila se blížící'),
(4842, 53, 58, 'a není kromě Boha nikoho, kdo by ji vyjevil.'),
(4843, 53, 59, 'Tento příběh vás snad údivem naplnil,'),
(4844, 53, 60, 'že místo abyste plakali, se smějete'),
(4845, 53, 61, 'a jen v zábavách dlíte?'),
(4846, 53, 62, 'Před Bohem na zem padejte a Jeho uctívejte!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Blíží se hodina a měsíc se rozpoltí.'),
(4848, 54, 2, 'Když nevěřící znamení vidí, odvracejí se a říkají: "Kouzlo ustavičné!"'),
(4849, 54, 3, 'A za lež je prohlašují a své vášně následují, však ustanovení každé je neměnné.'),
(4850, 54, 4, 'A věru dostalo se jim již některých zvěstí, jež výstrahy obsahují'),
(4851, 54, 5, 'i moudrost dokonalou. Však nic platné není varování!'),
(4852, 54, 6, 'Odvrať se od nich tedy! V den, kdy svolavatel zavolá k věci strašné,'),
(4853, 54, 7, 'se sklopeními zraky vystoupí z hrobů svých jak kobylky rozptýlené,'),
(4854, 54, 8, 'krky ke svolavateli natažené! A řeknou nevěřící: "Toto věru těžký je den!"'),
(4855, 54, 9, 'A již před nimi lid Noemův znamení za výmysl pokládal a Našeho služebníka za lháře prohlásil; i řekli oni: "Blázen!" a on byl jimi odstrašen.'),
(4856, 54, 10, 'Pána svého vzýval: "Pomoz mi, jsem přemožen!"'),
(4857, 54, 11, 'A brány nebeské jsme otevřeli pro vodu tekoucí proudem'),
(4858, 54, 12, 'a ze země vytrysknout jsme dali pramenům, až setkaly se oba proudy dle rozkazu, jenž byl již předurčen.'),
(4859, 54, 13, 'A naložili jsme Noeho na loď postavenou z prken a palmových vláken,'),
(4860, 54, 14, 'aby plula pod naším zrakem v odměnu tomu, jenž byl tak odvržen.'),
(4861, 54, 15, 'A ponechali jsme ji jako znamení. Což není nikoho, kdo připomněl by si to nyní?'),
(4862, 54, 16, 'A jaký byl Můj trest a Mé varování?'),
(4863, 54, 17, 'A učinili jsme Korán snadným pro připomínání; což není nikoho, kdo připomněl by si to nyní?'),
(4864, 54, 18, 'A také ´Ádovci prohlásili za lež Naše znamení a jaký byl Můj trest a Mé varování?'),
(4865, 54, 19, 'A vyslali jsme proti nim vítr svištivý v den neblahý, nekonečný,'),
(4866, 54, 20, 'jenž lidi vytrhával tak, že byli jak palem vyvrácených kmeny.'),
(4867, 54, 21, 'A jaký byl Můj trest a Mé varování?'),
(4868, 54, 22, 'A učinili jsme Korán snadným pro připomínání; což není nikoho, kdo připomněl by si to nyní?'),
(4869, 54, 23, 'A rovněž Thamúdovci varování za lživé pokládali'),
(4870, 54, 24, 'a řekli: "Máme snad následovat smrtelníka z nás vzešlého? To bychom zbloudilí a pošetilí byli!'),
(4871, 54, 25, 'Jak to, že zrovna jemu ze všech nás bylo připomenutí svrženo? Ba nikoliv, on lhář je opovážlivý!"'),
(4872, 54, 26, 'Však zítra se dozvědí, kdo lhář byl opovážlivý!'),
(4873, 54, 27, 'My k nim věru pošleme velbloudici jako pokušení jejich, a ty je trpělivě pozoruj'),
(4874, 54, 28, 'a oznam jim, že voda je mezi ně rozdělena a napití každé má čas svůj.'),
(4875, 54, 29, 'I zavolali svého druha, ten ruku vztáhl a velbloudici zahubil.'),
(4876, 54, 30, 'A jaký byl Můj trest a Mé varování?'),
(4877, 54, 31, 'A poslali jsme na ně jen výkřik jediný a stali se podobnými slámě suché, z níž ohrada se staví.'),
(4878, 54, 32, 'A učinili jsme Korán snadným pro připomínání; což není nikoho, kdo připomněl by si to nyní?'),
(4879, 54, 33, 'Za lživé soukmenovci Lotovi varování prohlásili,'),
(4880, 54, 34, 'a poslali jsme vítr kamení nesoucí proti nim - kromě rodu Lotova, jejž za jitra jsme zachránili'),
(4881, 54, 35, 'z milosti Své. A takto odměňujeme ty, kdož vděčnost projevili.'),
(4882, 54, 36, 'Ačkoliv Lot je varoval před Naší přísností, oni pochybovali o varování'),
(4883, 54, 37, 'a přáli si jeho hosty svést, však oči jejich jsme oslepili: "Okuste trestu Mého a varování!"'),
(4884, 54, 38, 'A nazítří zrána je zasáhl trest trvalý.'),
(4885, 54, 39, '"Okuste trestu Mého a varování!"'),
(4886, 54, 40, 'A učinili jsme Korán snadným pro připomínání; což není nikoho, kdo připomněl by si to nyní?'),
(4887, 54, 41, 'A věru dostalo se i rodu Faraónovu varování,'),
(4888, 54, 42, 'však za lež prohlásili všechna Naše znamení a uchvátili jsme je tak, jak mocný a všemohoucí činí.'),
(4889, 54, 43, 'Jsou snad vaši nevěřící silnější, než byli tamti či bylo vám v Písmech zaručeno bezpečí?'),
(4890, 54, 44, 'Anebo řeknou snad: "My jsme shromáždění vítězící."'),
(4891, 54, 45, 'Však toto shromáždění na útěk bude obráceno a zády se otočí.'),
(4892, 54, 46, 'Ba ano! Hodina bude časem schůzky jejich, Hodina velmi záludná a hořce chutnající.'),
(4893, 54, 47, 'A hříšníci věru budou v pošetilosti a v bloudění,'),
(4894, 54, 48, 'a v den, kdy budou tváří k zemi vlečeni, zvoláno bude: "Ochutnejte Saqaru pohlazení!"'),
(4895, 54, 49, 'A každou věc jsme vskutku v určených rozměrech stvořili'),
(4896, 54, 50, 'a rozkaz náš je pouze slovo jediné, rychlé jak oka mžiknutí.'),
(4897, 54, 51, 'A vskutku jsme již zahubili jiné vám podobné. Což není nikoho, kdo připomněl by si to nyní?'),
(4898, 54, 52, 'A vše, co konali, je v Písmech zapsáno'),
(4899, 54, 53, 'a vše, ať malé či velké, je v nich zaznamenáno.'),
(4900, 54, 54, 'A bohabojní věru budou mezi zahradami a řekami,'),
(4901, 54, 55, 'v příbytku pravdy, u vládce všemohoucího!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Milosrdný!'),
(4903, 55, 2, 'Koránu naučil,'),
(4904, 55, 3, 'člověka stvořil,'),
(4905, 55, 4, 'vysvětlení jej naučil.'),
(4906, 55, 5, 'Slunce i měsíc své počty obsahují'),
(4907, 55, 6, 'a traviny a stromy se před ním sklánějí.'),
(4908, 55, 7, 'Nebe On vyzvedl a váhy stanovil'),
(4909, 55, 8, 'na váze nepodvádějte,'),
(4910, 55, 9, 'spravedlivě ji stanovte a na váhách neubírejte!'),
(4911, 55, 10, 'A zemi, tu pro lidstvo rozprostřel,'),
(4912, 55, 11, 'na ní ovoce a palmy s obaly plodů zrají'),
(4913, 55, 12, 'i obilí na stéblech a rostliny, jež vůni libou mají.'),
(4914, 55, 13, 'Které z dobrodiní Pána svého můžete popírat?'),
(4915, 55, 14, 'A člověka z hlíny podobné hrnčířské stvořil'),
(4916, 55, 15, 'a džiny z plamene bezdýmného vytvořil.'),
(4917, 55, 16, 'Které z dobrodiní Pána svého můžete popírat?'),
(4918, 55, 17, 'On Pánem je obou východů, i Pánem obou západů.'),
(4919, 55, 18, 'Které z dobrodiní Pána svého můžete popírat?'),
(4920, 55, 19, 'A volně dal téci oběma mořím, jež se setkají,'),
(4921, 55, 20, 'však mezi nimi překážka stojí, již nezdolají.'),
(4922, 55, 21, 'Které z dobrodiní Pána svého můžete popírat?'),
(4923, 55, 22, 'A z obou moří se perly velké i malé loví.'),
(4924, 55, 23, 'Které z dobrodiní Pána svého můžete popírat?'),
(4925, 55, 24, 'Jeho jsou i lodě plující, jež na moři ční jak znamení.'),
(4926, 55, 25, 'Které z dobrodiní Pána svého můžete popírat?'),
(4927, 55, 26, 'Všichni, kdož na zemi jsou, dočkají se konce svého'),
(4928, 55, 27, 'a zůstane jen tvář Pána tvého, majestátnosti a velkomyslnosti plného.'),
(4929, 55, 28, 'Které z dobrodiní Pána svého můžete popírat?'),
(4930, 55, 29, 'A vzývají Jej ti, kdož na nebesích jsou i na zemi, a každý den On při díle je.'),
(4931, 55, 30, 'Které z dobrodiní Pána svého můžete popírat?'),
(4932, 55, 31, 'A záhy i pro vás se uvolníme, ó břemena vy dvě!'),
(4933, 55, 32, 'Které z dobrodiní Pána svého můžete popírat?'),
(4934, 55, 33, 'Ó shromáždění lidí a džinů! Jste-li schopni uniknout za nebes a země končiny, pak tam pronikněte! Však podaří se vám to jen s Naší plnou mocí.'),
(4935, 55, 34, 'Které z dobrodiní Pána svého můžete popírat?'),
(4936, 55, 35, 'Bude na vás poslán plamen ohnivý a měď roztavená, a nebude vám pomoci!'),
(4937, 55, 36, 'Které z dobrodiní Pána svého můžete popírat?'),
(4938, 55, 37, 'A až se nebesa rozpoltí a jak čerstvě stažená kůže zrudnou,'),
(4939, 55, 38, '- které z dobrodiní Pána svého můžete popírat? -'),
(4940, 55, 39, 'v ten den lidé ani džinové na viny své tázáni nebudou.'),
(4941, 55, 40, 'Které z dobrodiní Pana svého můžete popírat?'),
(4942, 55, 41, 'Hříšníci podle známek svých budou poznáni a uchopeni za nohy a za kštici.'),
(4943, 55, 42, 'Které z dobrodiní Pána svého můžete popírat?'),
(4944, 55, 43, 'Toto je to peklo, jež za lež prohlašovali hříšníci,'),
(4945, 55, 44, 'a budou se kroutit mezi ním a vodou vroucí a varem kypící!'),
(4946, 55, 45, 'Které z dobrodiní Pána svého můžete popírat?'),
(4947, 55, 46, 'A kdo se postavení Pána svého obával, ten dvě zahrady dostane,'),
(4948, 55, 47, '- které z dobrodiní Pána svého můžete popírat? -'),
(4949, 55, 48, 'dvě zahrady plné listnatého větvoví'),
(4950, 55, 49, '- které z dobrodiní Pána svého můžete popírat? -'),
(4951, 55, 50, 'a v obou z nich dva prameny potekou'),
(4952, 55, 51, '- které z dobrodiní Pána svého můžete popírat? -'),
(4953, 55, 52, 'a v nich od každého ovoce dva druhy budou.'),
(4954, 55, 53, 'Které z dobrodiní Pána svého můžete popírat?'),
(4955, 55, 54, 'Tam budou na kobercích brokátem podšitých odpočívat a plody obou zahrad na dosah ruky mít.'),
(4956, 55, 55, 'Které z dobrodiní Pána svého můžete popírat?'),
(4957, 55, 56, 'Tam dívky sklopených zraků, jichž předtím ani muž, ani džin se nedotkl, budou dlít'),
(4958, 55, 57, '- které z dobrodiní Pána svého můžete popírat? -'),
(4959, 55, 58, 'jež rubínům a perlám se budou podobat.'),
(4960, 55, 59, 'Které z dobrodiní Pána svého můžete popírat?'),
(4961, 55, 60, 'Což odměnou za dobré se něco jiného než dobré může stát?'),
(4962, 55, 61, 'Které z dobrodiní Pána svého můžete popírat?'),
(4963, 55, 62, 'A kromě těch dvou tam budou dvě zahrady jiné'),
(4964, 55, 63, '- které z dobrodiní Pána svého můžete popírat? -'),
(4965, 55, 64, 'dvě zahrady tmavě zelené'),
(4966, 55, 65, '- které z dobrodiní Pána svého můžete popírat? -'),
(4967, 55, 66, 'v nich obou prameny dva budou tryskat.'),
(4968, 55, 67, 'Které z dobrodiní Pána svého můžete popírat?'),
(4969, 55, 68, 'Tam ovoce, palmy a jablka granátová budou zrát.'),
(4970, 55, 69, 'Které z dobrodiní Pána svého můžete popírat?'),
(4971, 55, 70, 'Tam dívky budou rozkošné a překrásné'),
(4972, 55, 71, '- které z dobrodiní Pána svého můžete popírat? -'),
(4973, 55, 72, 'velkých černých očí, ve stanech střežené'),
(4974, 55, 73, '- které z dobrodiní Pána svého můžete popírat? -'),
(4975, 55, 74, 'jichž předtím se nedotkla ruka člověka ni džina žádného.'),
(4976, 55, 75, 'Které z dobrodiní Pána svého můžete popírat?'),
(4977, 55, 76, 'Tam blažení na poduškách zelených a kobercích překrásných budou spočívat.'),
(4978, 55, 77, 'Které z dobrodiní Pána svého můžete popírat?'),
(4979, 55, 78, 'Požehnáno budiž jméno Pána tvého, majestátnosti a velkomyslnosti plného!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Až dopadne událost nezvratná'),
(4981, 56, 2, '#NÁZEV?'),
(4982, 56, 3, 'poníží, povýší.'),
(4983, 56, 4, 'Až země zatřese se záchvěvy'),
(4984, 56, 5, 'a hory na padrť budou rozdrceny'),
(4985, 56, 6, 'a jako prach rozptýleny,'),
(4986, 56, 7, 'rozdělíte se na tři skupiny:'),
(4987, 56, 8, 'na skupinu lidí po pravici - a co bude s lidmi po pravici?'),
(4988, 56, 9, 'a na skupinu lidí po levici - a co bude s lidmi po levici?'),
(4989, 56, 10, 'a na předáky. Předáci,'),
(4990, 56, 11, 'to jsou ti, kdož (k Bohu) jsou přiblíženi,'),
(4991, 56, 12, 'v zahradách slastí usídleni'),
(4992, 56, 13, '#NÁZEV?'),
(4993, 56, 14, 'a jen málo z posledních.'),
(4994, 56, 15, 'Na lehátkách, jež zlatem vypleteny jsou,'),
(4995, 56, 16, 'jeden proti druhému odpočívat budou:'),
(4996, 56, 17, 'obcházet mezi nimi budou chlapci mládí věčného'),
(4997, 56, 18, 's číšemi, konvicemi a poháry nápoje čirého,'),
(4998, 56, 19, 'z něhož je hlava nerozbolí ani jím vyčerpáni nebudou,'),
(4999, 56, 20, 'a s ovocem, které si volně vyberou,'),
(5000, 56, 21, 'a s masem ptačím podle přání svých.'),
(5001, 56, 22, 'Tam budou i dívky velkých očí černých,'),
(5002, 56, 23, 'jež srovnat lze s perlami střeženými,'),
(5003, 56, 24, 'odměnou za to, co vykonali na zemi.'),
(5004, 56, 25, 'A neuslyší tam plané tlachání ani k hříchu svádění,'),
(5005, 56, 26, 'nýbrž jen slova: "Mír, mír s vámi!"'),
(5006, 56, 27, 'A lidé po pravici - co bude s lidmi po pravici?'),
(5007, 56, 28, 'Ti budou mezi lotosovými stromy bezostnými'),
(5008, 56, 29, 'a mimózami plody ověšenými,'),
(5009, 56, 30, 'pod stínem protaženým'),
(5010, 56, 31, 'u vody tekoucí,'),
(5011, 56, 32, 's ovocem v hojnosti'),
(5012, 56, 33, 'nevyčerpatelným, nezakázaným,'),
(5013, 56, 34, 'na kobercích zdvižených.'),
(5014, 56, 35, 'Dívky ty jsme zvláštním stvořením stvořili'),
(5015, 56, 36, 'a pannami jsme je učinili'),
(5016, 56, 37, 'láskyplnými, věku stejného'),
(5017, 56, 38, 's lidmi po pravici -'),
(5018, 56, 39, 'a to zástup je z prvních'),
(5019, 56, 40, 'i zástup z posledních.'),
(5020, 56, 41, 'Však lidé po levici - a co bude s lidmi po levici?'),
(5021, 56, 42, 'Ti budou ve větru žhavém a ve vodě vroucí,'),
(5022, 56, 43, 've stínu dýmu černého,'),
(5023, 56, 44, 'ani ochlazení, ani osvěžení nepřinášejícího.'),
(5024, 56, 45, 'A oni si předtím žili v přepychu,'),
(5025, 56, 46, 'setrvávajíce úporně ve velkém hříchu,'),
(5026, 56, 47, 'a hovořili: "Zdaž až zemřeme a prachem a kostmi se staneme, budeme opravdu vzkříšeni,'),
(5027, 56, 48, 'my sami a předkové naši dávní?"'),
(5028, 56, 49, 'Odpověz: "Věru, první i poslední'),
(5029, 56, 50, 'budou ke schůzce dne známého shromážděni!"'),
(5030, 56, 51, 'A potom vskutku, vy zbloudilí, za lež prohlašující,'),
(5031, 56, 52, 'ze stromu Zaqqúmu se najíte'),
(5032, 56, 53, 'a břicha svá si naplníte'),
(5033, 56, 54, 'a zapíjet to budete vodou vroucí'),
(5034, 56, 55, 'a pít budete tak, jak chlemtají žízniví velbloudi.'),
(5035, 56, 56, 'Toto v den soudný budou jejich hody!'),
(5036, 56, 57, 'Jsme to My, kdo vás stvořili, proč tedy pravdu nepřiznáte?'),
(5037, 56, 58, 'Což jste neuvažovali o tom, co vyměšujete?'),
(5038, 56, 59, 'Stvořili jste to snad vy, anebo jsme My toho stvořiteli?'),
(5039, 56, 60, 'Jsme to My, kdo smrt mezi vámi ustanovili; a nikdo nás nemůže předejít,'),
(5040, 56, 61, 'abychom vás nevystřídali vám podobnými a nestvořili vás něčím, o čem potuchy nemůžete mít.'),
(5041, 56, 62, 'Vždyť přece první stvoření znáte, proč si tedy nevzpomenete?'),
(5042, 56, 63, 'Uvažovali jste někdy o tom, co obděláváte?'),
(5043, 56, 64, 'Zaséváte to snad vy, anebo jsme My toho zasévateli?'),
(5044, 56, 65, 'Kdybychom chtěli, v suchou trávu bychom to změnili, a vy byste se neustále divili:'),
(5045, 56, 66, '"Jsme dluhy obtíženi,'),
(5046, 56, 67, 'ba víc, teď všeho jsme zbaveni!"'),
(5047, 56, 68, 'Uvažovali jste někdy o vodě, kterou pijete?'),
(5048, 56, 69, 'Sesíláte ji snad vy z mraků, anebo jsme My těmi, kdož ji sesílají?'),
(5049, 56, 70, 'Kdybychom chtěli, učinili bychom ji hořkou a slanou - což tedy vděčni nebudete?'),
(5050, 56, 71, 'Uvažovali jste někdy o ohni, který vykřesáváte?'),
(5051, 56, 72, 'Dali jste snad vy vyrůst stromu, který dřevo k tomu skýtá, anebo jsme mu My dali vyrůst?'),
(5052, 56, 73, 'A učinili jsme jej připomenutím a užitkem pro ty, kteří v poušti jsou.'),
(5053, 56, 74, 'Oslavuj tedy jméno Pána svého mocného!'),
(5054, 56, 75, 'Hle, přísahám při hvězd západech,'),
(5055, 56, 76, 'a to vskutku je přísaha - kdybyste věděli - nesmírná,'),
(5056, 56, 77, 'že toto je věru Korán vznešený,'),
(5057, 56, 78, 'v písmu střeženém zapsaný,'),
(5058, 56, 79, 'ať dotýkají se ho jen očištění,'),
(5059, 56, 80, 'od Pána lidstva je to seslání!'),
(5060, 56, 81, 'Tímto vyprávěním vy opovrhujete'),
(5061, 56, 82, 'a vděčnost svou tím projevujete, že za lež je prohlašujete?'),
(5062, 56, 83, 'Proč tedy, až duše dostoupí do hrdla, nezasáhnete,'),
(5063, 56, 84, 'zatímco k tomu přihlížíte'),
(5064, 56, 85, '- a My k tomu jsme blíže než vy, vy však Nás nevidíte.'),
(5065, 56, 86, 'A nejste-li tedy dlužníky Našimi,'),
(5066, 56, 87, 'navraťte ji zpět do těla, jste-li pravdomluvnými!'),
(5067, 56, 88, 'Jestliže byl z přiblížených k Bohu,'),
(5068, 56, 89, 'tedy mu odpočinek, vůně libá i zahrada slastí náleží,'),
(5069, 56, 90, 'a jestliže z lidí po pravici byl, pak uslyší'),
(5070, 56, 91, '"Mír s tebou" od lidí po pravici.'),
(5071, 56, 92, 'Jestliže však byl mezi popírajícími, zbloudilými,'),
(5072, 56, 93, 've vodě vroucí nalezne pohoštění'),
(5073, 56, 94, 'a v ohni pekelném hoření.'),
(5074, 56, 95, 'A věru je toto skutečnost nepochybná.'),
(5075, 56, 96, 'Oslavuj tedy jméno Pána svého mocného!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Chválí Boha vše, co na nebesích je i na zemi - a On mocný je i moudrý.'),
(5077, 57, 2, 'Jemu náleží království nebes i země a On život i smrt dává a On všech věcí je mocný.'),
(5078, 57, 3, 'On počátkem je i koncem, zjevný i skrytý; On o každé věci je vševědoucí.'),
(5079, 57, 4, 'On je ten, jenž stvořil nebesa i zemi v šesti dnech a potom se na trůnu Svém usadil. On zná, co vniká do země a co z ní vychází, co sestupuje z nebe a co na ně vystupuje, a ať jste kdekoliv, On s vámi je a vše, co děláte, zří.'),
(5080, 57, 5, 'Jemu náleží království nebes i země a k Bohu se rozkazy všechny navracejí.'),
(5081, 57, 6, 'On dává přejít noci v den a dni v noc a On dobře zná vše, co v hrudích se skrývá.'),
(5082, 57, 7, 'Věřte v Boha a posla Jeho a rozdávejte z toho, v čem učinil vás posledními vlastníky! Těm z vás, kdož uvěřili a almužnu rozdávali, je určena odměna veliká.'),
(5083, 57, 8, 'Co je s vámi, že nechcete v Boha uvěřit, když posel Jeho vás vyzývá, abyste uvěřili v Pána svého, a když již On s vámi uzavřel smlouvu - jste-li vskutku věřící?'),
(5084, 57, 9, 'On je ten, jenž seslal služebníku Svému znamení jasná, aby vás vyvedl z temnot ke světlu. A Bůh je vůči vám shovívavý, slitovný.'),
(5085, 57, 10, 'Co je s vámi, že nechcete rozdávat na cestě Boží, když Bohu přece připadne dědictví nebes a země? Ti z vás, kdož rozdávali a bojovali ještě před vítězstvím, nejsou stejní jako ti, kdož vyčkávali; ti první jsou mnohem výše na stupních než ti, kdo rozdávali a bojovali až po něm. Však obojím Bůh přislíbil odměnu překrásnou a Bůh je dobře zpraven o všem, co děláte.'),
(5086, 57, 11, 'Kdo tedy bude tím, jenž poskytne Bohu krásnou půjčku, aby mu ji Bůh dvojnásobně splatil a dostalo se mu odměny veliké?'),
(5087, 57, 12, 'V den, kdy spatříš, jak před věřícími muži a ženami a po pravici jejich poběží světlo jejich, bude jim řečeno: "Radostná zpráva pro vás: dnes vstoupíte do zahrad, pod nimiž řeky tekou a v nichž nesmrtelní budete!" A to věru bude úspěch nesmírný.'),
(5088, 57, 13, 'V ten den řeknou pokrytci, muži i ženy, těm, kdož uvěřili:,, Počkejte na nás, abychom si mohli vzít něco ze světla vašeho!" A bude jim řečeno: "Vraťte se nazpět a hledejte světlo jinde!" A bude mezi nimi postavena zeď, v níž bude brána: uvnitř bude milosrdenství a zvnějška, naproti nim, bude trest.'),
(5089, 57, 14, 'A zavolají na ně pokrytci: "Cožpak jsme nebyli s vámi?" Odpovědí: "Ano, avšak vy sami sebe jste v pokušení uvedli, číhali jste, pochybovali jste a zmámily vás vaše tužby pozemské, až přišel rozkaz Boží a byli jste oklamáni ohledně Boha tím, kdo mámí.'),
(5090, 57, 15, 'A nebude proto dnes přijato výkupné ani od vás, ani od těch, kdož nevěřili; útulkem vaším bude oheň pekelný a bude i vaším pánem. Jak hnusný je to cíl konečný!"'),
(5091, 57, 16, 'Což nepřišel čas pro ty, kdož uvěřili, aby srdce jejich se pokořila před připomenutím Božím a před tím, co seslal z pravdy? Nechť nejsou jako ti, jimž zdál se - když dostalo se jim předtím Písma - čas příliš dlouhý; i zatvrdila se srdce jejich a většina z nich byli hanebníci.'),
(5092, 57, 17, 'Vězte, že Bůh oživuje zemi po smrti její; a již jsme vám vysvětlili znamení - snad budete rozumní!'),
(5093, 57, 18, 'Těm, kdož rozdávají almužnu, mužům i ženám, a těm, kdož poskytují Bohu krásnou půjčku, těm bude splacena dvojnásobně a dostane se jim odměny štědré.'),
(5094, 57, 19, 'Věru ti, kdož uvěřili v Boha a posly Jeho, jsou spravedliví a svědky před Pánem svým; jim dostane se jejich odměny a jejich světla. Ti však, kdož znamení Naše za lež prohlašovali, ti budou plamene pekelného obyvateli.'),
(5095, 57, 20, 'Vězte, že život pozemský je pouze hra a zábava, marná okrasa, vzájemné vychloubání mezi vámi a soupeření v rozmnožování majetku a dětí. A podobá se dešti: nevěřícím se líbí rostlinstvo, jež z něho vyrůstá, avšak potom vadne a vidíš je zežloutlé, až nakonec se stane slámou suchou. A v životě budoucím je jednak trest přísný, avšak i odpuštění a zalíbení se Bohu. A život pozemský není leč užívání klamavé.'),
(5096, 57, 21, 'Předstihujte se tedy, abyste dosáhli odpuštění Pána svého a zahrady, jejíž šíře se rovná nebi i zemi, připravené pro ty, kdož v Boha a posly Jeho uvěřili. A toto je přízeň Boží, kterou On uštědřuje, komu chce, vždyť Bůh vládcem je přízně nesmírné.'),
(5097, 57, 22, 'A nepostihne žádné neštěstí zemi ani osoby vaše, aby nebylo zaneseno v Knize dříve, než jsme je stvořili - a to věru je pro Boha nadmíru snadné.'),
(5098, 57, 23, 'A jedná tak, abyste si nezoufali nad tím, co vám uniklo, a abyste se neradovali příliš z toho, čeho se vám dostalo. Bůh nemiluje žádného domýšlivce vychloubačného'),
(5099, 57, 24, 'ani ty, kdož lakotí a lidem lakotu doporučují. A ten, kdo zády se obrátí... vždyť Bůh věru je soběstačný, chvályhodný.'),
(5100, 57, 25, 'A vyslali jsme již posly s důkazy jasnými a seslali jsme s nimi Písmo a váhu, aby lidé jednali poctivě a seslali jsme také železo, v němž nebezpečí je strašné i užitek pro lidi - aby Bůh poznal ty, kdož pomáhají Jemu i poslům Jeho v tajnosti. A Bůh silný je i mocný.'),
(5101, 57, 26, 'A vyslali jsme již Noema a Abrahama a vložili jsme do potomstva jejich prorocké poslání i Písmo; a někteří z nich byli na cestě správné, avšak mnozí z nich byli hanebníci.'),
(5102, 57, 27, 'A potom jsme vypravili v jejich stopách jiné posly své a vypravili jsme Ježíše, syna Mariina, a dali jsme mu Evangelium a vložili do srdcí těch, kdož jej následovali, mírnost a slitování; a mnišství si oni zavedli jako novotu - a nepředepsali jsme jim je - usilujíce zalíbit se Bohu. Avšak oni to nedodrželi vhodným dodržováním. A dali jsme těm z nich, kdož uvěřili, odměnu jejich, avšak mnozí z nich byli hanebníci.'),
(5103, 57, 28, 'Vy, kteří věříte! Bojte se Boha a věřte v posla Jeho! On dá vám dva podíly z milosrdenství Svého a uštědří vám světlo, s nímž půjdete, a odpustí vám - vždyť Bůh je odpouštějící, slitovný,'),
(5104, 57, 29, 'aby poznali vlastníci Písma, že nemají moci nad ničím z přízně Boží a že přízeň je jedině v rukou Božích; z ní dává, komu chce. A Bůh vládcem je přízně nesmírné.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Bůh již slyšel řeč oné, která se s tebou hádala ohledně manžela svého a stěžovala si u Boha. Bůh slyšel váš rozhovor, vždyť Bůh je věru slyšící, jasnozřivý.'),
(5106, 58, 2, 'Ti z vás, kteří nazývají manželky své zádami svých matek, vědí, že ony nejsou jejich matkami; matkami jejich jsou jedině ty, které je porodily. A říkají tím věru slova zavrženíhodná a falešná. A Bůh zajisté je promíjející, odpouštějící!'),
(5107, 58, 3, 'Ti, kdož říkají ženám svým "Jsi pro mne jak záda mé matky" a potom chtějí odvolat to, co řekli, mohou se vykoupit propuštěním otroka, dříve než se opět vzájemně dotknou. A k tomu jste napomínáni a Bůh je dobře zpraven o všem, co děláte.'),
(5108, 58, 4, 'A nechť se ten, kdo nenalezne k tomu možnost, postí dva měsíce po sobě následující, dříve než se opět vzájemně dotknou. A nechť ten, kdo nebude toho schopen, nakrmí šedesát chudáků. A toto je stanoveno, abyste věřili v Boha a posla Jeho, a toto jsou omezení Boží. Nevěřícím se pak dostane trestu bolestného!'),
(5109, 58, 5, 'Ti, kdož se protiví Bohu a poslu Jeho, budou povaleni, tak jako byli povaleni ti, kdož byli před nimi. A nyní jsme již seslali znamení jasná. A nevěřícím se dostane trestu zahanbujícího'),
(5110, 58, 6, 'v den, kdy Bůh je všechny vzkřísí a oznámí jim, co vlastně dělali. Bůh dobře to spočítal, zatímco oni na to zapomněli - a Bůh svědkem je všech věcí.'),
(5111, 58, 7, 'Což jsi neviděl, že Bůh zná vše, co na nebesích je i na zemi? A není rozmluvy tajné ve třech, aby tam nebyl čtvrtý, a v pěti, aby tam nebyl šestý, nebo v menším či větším počtu, aby On nebyl s nimi, ať již jsou kdekoliv. A Bůh jim pak v den zmrtvýchvstání oznámí, co vlastně dělali, neboť Bůh o každé věci je vševědoucí.'),
(5112, 58, 8, 'Což jsi neviděl ty, jimž zakázány byly tajné rozmluvy, jak se potom vrací k tomu, co bylo jim zakázáno, a domlouvají se tajně v hříchu, nepřátelství a neposlušnosti vůči poslu? A když k tobě přijdou, zdraví tě tak, jak tě nezdraví Bůh, a říkají si v duchu: "Pročpak nás Bůh nepotrestá za to, co říkáme?" Však stačit jim bude peklo, v němž hořet budou - a jak hnusný je to cíl konečný!'),
(5113, 58, 9, 'Vy, kteří věříte! Když spolu tajně hovoříte, nedomlouvejte se o hříchu, nepřátelství a neposlušnosti vůči poslu, ale domlouvejte se o zbožnosti a bohabojnosti! Bojte se Boha, k němuž budete shromážděni!'),
(5114, 58, 10, 'Tajné rozmluvy pocházejí jen od satana, aby způsobil zármutek těm, kdož uvěřili. Avšak on nemůže jim v ničem uškodit, leda z dovolení Božího. A na Boha nechť věřící se spoléhají!'),
(5115, 58, 11, 'Vy, kteří věříte! Když je vám řečeno "Uvolněte místo ve shromáždění!", tedy je uvolněte! A Bůh pro vás také uvolní místo (v ráji). A když je vám řečeno "Vzdalte se!", tedy se vzdalte. A Bůh pak povýší ty z vás, kdož uvěřili, a ty, jimž dostalo se vědění, na stupně různé. A Bůh dobře je zpraven o všem, co děláte.'),
(5116, 58, 12, 'Vy, kteří věříte! Jestliže chcete mluvit soukromě s poslem, dejte napřed dobrovolný dar! Toto bude pro vás lepší a čistší. Však nenajdete-li prostředky... vždyť Bůh je odpouštějící, slitovný.'),
(5117, 58, 13, 'Stydíte se snad dát dobrovolný dar před soukromým rozhovorem? Jestliže tak neučiníte a Bůh se nad vámi smiluje - pak vykonejte modlitbu, dejte almužnu a poslouchejte Boha a posla Jeho! A Bůh dobře je zpraven o všem, co děláte.'),
(5118, 58, 14, 'Což jsi neviděl ty, kteří se sdružují s lidmi, na něž se Bůh rozhněval? Ti nepatří ani k vám, ani k nim a přísahají křivě, ačkoliv dobře jsou si toho vědomi.'),
(5119, 58, 15, 'Bůh pro ně připravil trest strašný - a věru jak hnusné je to, co dělají.'),
(5120, 58, 16, 'Zaštítili se přísahami svými a odvracejí od cesty Boží - a pro ně připraven je trest zahanbující.'),
(5121, 58, 17, 'A nijak jim proti Bohu neprospějí majetky jejich ani děti jejich; jsou obyvateli ohně pekelného a budou v něm nesmrtelní.'),
(5122, 58, 18, 'V onen den je Bůh všechny vzkřísí a budou Mu přísahat tak, jako přisahali vám, a budou se domnívat, že jednají správně. Což však nejsou vskutku lháři?'),
(5123, 58, 19, 'Satan je ovládl a dal jim zapomenout na připomenutí Boží; a oni patří ke straně satanově. Což však strana satanova ztrátu neutrpí?'),
(5124, 58, 20, 'Ti, kdož se protiví Bohu a poslu Jeho, ti budou věru patřit k nejopovrhovanějším!'),
(5125, 58, 21, 'A Bůh napsal: "Věru že zvítězím Já i poslové Moji!", vždyť Bůh vskutku je silný a mocný.'),
(5126, 58, 22, 'Nenalezneš žádné lidi, věřící v Boha a v den poslední, přátelit se s těmi, kdož se protiví Bohu a poslu Jeho, byť to byli otcové jejich či synové jejich anebo bratři jejich či rod jejich. Do srdcí těchto vepsal Bůh víru a podpořil je duchem od Něho pocházejícím. A uvede je do zahrad, pod nimiž řeky tekou a v nichž nesmrtelní budou. Bůh nalezl v nich zalíbení a oni nalezli zalíbeni v Něm - a tito tvoří stranu Boží! A což nebudou ti, kdož ke straně Boží patří, blažení?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Slaví Boha vše, co na nebesích je i na zemi, a On mocný je i moudrý.'),
(5128, 59, 2, 'On je ten, jenž vyhnal ty z vlastníků Písma, kteří neuvěřili, z příbytků jejich k prvnímu shromáždění. Nevěřili jste, že odejdou, zatímco oni si myslili, že pevnosti jejich je proti Bohu ochrání. Však Bůh k nim přišel z místa, odkud se nenadáli, a vrhl v srdce jejich děs, takže zbořili domy své vlastníma rukama i rukama věřících. A vezměte si z toho poučení, vy, kdož nadáni jste prozíravostí!'),
(5129, 59, 3, 'Kdyby byl Bůh pro ně nepředepsal vyhnanství, byl by je věru potrestal v životě pozemském jiným způsobem; v životě budoucím pak je očekává trest ohnivý.'),
(5130, 59, 4, 'A to proto, že se vzepřeli Bohu a poslu Jeho. A kdokoliv se vzepře Bohu... vždyť Bůh věru je strašný ve Svém trestáni.'),
(5131, 59, 5, 'Vše, co jste porazili z palem datlových či nechali stát na kořenech jejich, to učinili jste z dovolení Božího, a aby poníženi byli hanebníci.'),
(5132, 59, 6, 'To, co Bůh poskytl jako kořist poslu Svému z majetku jejich, k tomu jste vy nemusili popohánět ani koně, ani velbloudy. Avšak Bůh dává Své zplnomocnění poslům Svým, nad kým chce, a Bůh je nad každou věcí všemocný.'),
(5133, 59, 7, 'To, co Bůh poskytl poslu Svému jako kořist z obyvatelů měst, to patří Bohu a poslu Jeho, příbuzným, sirotkům, chudákům a tomu, kdo po cestě Boží kráčí, aby se to nedostalo do oběhu mezi bohatými z vás. To, co vám dává posel, to si vezměte! Ale toho, co vám odepřel, toho se zdržujte! A bojte se Boha, neboť Bůh je věru strašný v trestání Svém.'),
(5134, 59, 8, 'A část kořisti náleží chudým vystěhovalcům, kteří byli vyhnáni z příbytků svých a od majetků svých a kteří usilují dosáhnout přízně Boží a zalíbení a pomáhají Bohu a poslu Jeho. A toto jsou vskutku lidé spravedliví.'),
(5135, 59, 9, 'Ti, kdož pevně sídlí v tomto obvodu a utvrdili se ve víře ještě před nimi, ti milují ty, kdož se k nim vystěhovali, a nenalézají v hrudích svých žádnou závist kvůli tomu, co dáno bylo vystěhovalcům. A dávají jim přednost před sebou, i kdyby sami měli nedostatek. A ti, kdož ochrání duše své před lakotou, ti budou blažení.'),
(5136, 59, 10, 'A ti, kdož přišli po nich, hovoří: "Pane náš, odpusť nám i bratřím našim, kteří nás předešli ve víře; a nevkládej do srdcí našich zlobu proti těm, kdož před námi uvěřili, Pane náš, vždyť Tys věru shovívavý, slitovný!"'),
(5137, 59, 11, 'Což jsi neviděl pokrytce, jak říkají bratřím svým nevěřícím z vlastníků Písma: "Budete-li vyhnáni, my věru odejdeme s vámi a nikdy neuposlechneme nikoho proti vám. A bude-li proti vám bojováno, my vám určitě pomůžeme." A Bůh je věru svědkem toho, že jsou lháři.'),
(5138, 59, 12, 'A budou-li tito vskutku vyhnáni, oni s nimi neodejdou, a bude-li proti nim bojováno, oni jim nepomohou, anebo poskytnou-li jim pomoc, obrátí se potom zády a nakonec jim samým nebude pomoci.'),
(5139, 59, 13, 'Vy naháníte srdcím jejich větší hrůzu než Bůh, a to je proto, že to jsou lidé, kteří nic nechápou.'),
(5140, 59, 14, 'Nebudou bojovat proti vám společně, nýbrž jednotlivě v sídlištích opevněných či za hradbami. Jsou-li mezi sebou, je statečnost jejich velká a ty je pokládáš za sjednocené, avšak srdce jejich jsou rozdělena. A to proto, že jsou to lidé, kteří rozumně neuvažují,'),
(5141, 59, 15, 'podobní těm, kdož byli nedlouho před nimi: okusili již špatných následků svého počínání a očekává je ještě trest bolestný.'),
(5142, 59, 16, 'A podobají se satanovi, jenž říká člověku: "Buď nevěřící!" A když se stane nevěřícím, tu mu řekne: "Já nejsem za tebe zodpovědný, neboť se věru obávám Boha, Pána lidstva veškerého!"'),
(5143, 59, 17, 'A konec jich obou bude jedině v ohni pekelném a budou tam nesmrtelní. A taková je odměna nespravedlivých.'),
(5144, 59, 18, 'Vy, kteří věříte, bojte se Boha! Nechť duše každá popatří na to, co si již dříve připravila pro zítřek. Bojte se Boha, vždyť Bůh je dobře zpraven o všem, co děláte.'),
(5145, 59, 19, 'Nebuďte jako ti, kdož zapomněli na Boha a jimž On dal zapomenout na sebe samé - a toto jsou věru hanebníci.'),
(5146, 59, 20, 'A nebudou si rovni obyvatelé ohně a obyvatelé ráje; a obyvatelé ráje jsou ti, kdož velkého úspěchu dosáhli.'),
(5147, 59, 21, 'Kdybychom byli seslali tento Korán na horu, byl bys ji viděl pokornou a pukající ze strachu před Bohem. A tato podobenství uvádíme lidem, snad budou o tom přemýšlet.'),
(5148, 59, 22, 'On je Bůh - a není božstva kromě Něho - jenž zná nepoznatelné i všeobecně známé. On milosrdný je i slitovný.'),
(5149, 59, 23, 'On je Bůh - a není božstva kromě Něho - vládce, přesvatý, mír, věrný, ochránce bezpečný, mocný, samovládný, velmožný; oč slavnější je Bůh než vše to, co k Němu přidružují.'),
(5150, 59, 24, 'On je Bůh, stvořitel, tvůrce i utvářející. Jemu přísluší jména překrásná a Jeho slaví vše, co na nebesích je i na zemi - On mocný je i moudrý!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Vy, kteří věříte! Neberte si nepřátele Mé i svoje za přátele, obracejíce se k nim s láskou, zatímco oni nevěří v to, co k vám přišlo z pravdy. Vždyť vyhnali posla i vás za to, že věříte v Boha, Pána svého. Jestliže jste se vydali do boje na Mé cestě a snažíte se získat Mé zalíbení, jak můžete jim projevovat tajně svou lásku? Vždyť Já znám, co skrýváte i co najevo dáváte. A kdo z vás tak učiní, ten zbloudil z cesty přímé.'),
(5152, 60, 2, 'Střetnou-li se s vámi, budou pro vás nepřáteli a vztáhnou po vás ruce své i jazyky své jen ve zlém a přáli by si, abyste byli nevěřícími.'),
(5153, 60, 3, 'Nebudou vám k užitku ani vaši pokrevní příbuzní, ani vaše děti a v den zmrtvýchvstání Bůh mezi vámi rozhodne; Bůh přece jasně zří vše, co děláte.'),
(5154, 60, 4, 'Vždyť máte nyní příklad překrásný v Abrahamovi a v těch, kdož byli s ním, když pravili lidu svému: "My nemáme odpovědnost za vás a za to, co místo Boha ctíte; my se vás zříkáme a propuklo mezi námi a vámi nepřátelství a nenávist až navěky, dokud neuvěříte v Boha jediného!" Výjimkou jsou slova Abrahamova otci jeho: "Budu zajisté prosit za tebe o odpuštění, ačkoliv nevládnu pro tebe žádnou mocí u Boha. Pane náš, na Tebe spoléháme a k Tobě se kajícně obracíme a u Tebe je cíl konečný.'),
(5155, 60, 5, 'Pane náš, neuveď nás v pokušení před těmi, kdož jsou nevěřící, ale odpusť nám, Pane náš, vždyť Tys mocný, moudrý!"'),
(5156, 60, 6, 'Věru máte v nich příklad překrásný pro každého, kdo doufá dosáhnout Boha a dne soudného. Ten však, kdo obrátí se zády... tedy Bůh je soběstačný, chvályhodný!'),
(5157, 60, 7, 'Možná že Bůh způsobí, že mezi vámi a těmi, s nimiž jste v nepřátelství, zavládne láska, neboť Bůh je všemohoucí a Bůh je nejvýš odpouštějící a slitovný.'),
(5158, 60, 8, 'Bůh vám nezakazuje, abyste byli dobří a spravedliví vůči těm, kdož nebojovali proti vám kvůli náboženství a nevyhnali vás z příbytků vašich, neboť Bůh věru miluje poctivé.'),
(5159, 60, 9, 'Avšak Bůh vám zakazuje brát si za přátele ty, kteří proti vám bojovali kvůli náboženství, a ty, kteří vás vyhnali z příbytků vašich, a ty, kteří pomáhali při vašem vyhánění. A ti, kdož se s nimi budou přátelit, jsou nespravedliví!'),
(5160, 60, 10, 'Vy, kteří věříte! Když k vám přicházejí věřící ženy, jež se vystěhovaly, podrobte je zkoušce! Bůh nejlépe pak zná víru jejich; a když zjistíte, že jsou věřící, neposílejte je nazpět k nevěřícím, neboť jim nejsou dovoleny a ani oni nejsou dovoleni jim, ale dejte nevěřícím to, co vydali jako jejich obvěnění! A nebude pro vás hříchem, jestliže se s nimi oženíte poté, co dáte jim obvěnění jejich, avšak nedržte nevěřící ženy ve svazku manželském a žádejte nazpět to, co jste dali jako obvěnění a nevěřící ať požadují to, co dali ženám svým. Takové je rozhodnutí Boží, jímž mezi vámi rozhoduje - a Bůh vševědoucí je i moudrý.'),
(5161, 60, 11, 'A jestliže některá z vašich manželek odejde od vás k nevěřícím a vy jste dosáhli své pomsty, dejte těm, jejichž manželky odešly, totéž, co oni vydali jako obvěnění. A bojte se Boha, v něhož věříte!'),
(5162, 60, 12, 'Proroku, přijdou-li k tobě věřící ženy, přísahajíce ti věrnost, a že nebudou nic přidružovat k Bohu, že nebudou krást, nebudou cizoložit, že nebudou zabíjet své děti, že se nedopustí hanebnosti vymyšlené mezi svýma rukama a nohama a že nebudou neposlušné tebe v tom, co bylo uznáno za vhodné - přijmi přísahu jejich a popros Boha za ně o odpuštění, vždyť Bůh odpouštějící je, slitovný.'),
(5163, 60, 13, 'Vy, kteří věříte! Neberte si za přátele lid, na nějž se Bůh rozhněval; ti ztratili naději na život budoucí, tak jako ji ztratili nevěřící ohledně obyvatel hrobů.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Chválí Boha vše, co na nebesích je i na zemi - a On mocný je i moudrý.'),
(5165, 61, 2, 'Vy, kteří věříte! Proč říkáte to, co nečiníte?'),
(5166, 61, 3, 'Bůh velice nenávidí, že říkáte něco, co neděláte,'),
(5167, 61, 4, 'avšak Bůh věru miluje ty, kdož bojují na cestě Jeho v sevřeném šiku, jako by byli stavbou olovem spojenou.'),
(5168, 61, 5, 'Vzpomeň si, jak pravil Mojžíš lidu svému: "Lide můj, proč se mnou zle nakládáte, když víte, že jsem poslem Božím k vám?" A když se odvrátili, Bůh odvrátil srdce jejich, neboť Bůh nevede cestou správnou lid hanebníků.'),
(5169, 61, 6, 'A vzpomeň si, jak pravil Ježíš, syn Mariin: "Dítka Izraele, já jsem vskutku poslem Božím k vám, potvrzujícím pravdivost toho, co bylo přede mnou sesláno z Tóry, a oznamujícím vám radostnou zvěst o poslu, jenž přijde po mně a jehož jméno Ahmad bude." Avšak když předvedl jim jasné důkazy, řekli: "To je kouzelnictví zjevné."'),
(5170, 61, 7, 'Kdo je nespravedlivější než ten, kdo si proti Bohu lži vymýšlí, zatímco je vyzýván k přijetí islámu? Bůh věru nevede cestou správnou lid nespravedlivý.'),
(5171, 61, 8, 'Rádi by sfoukli světlo Boží ústy svými, avšak Bůh učiní světlo Své dokonalým, i když se to protiví nevěřícím.'),
(5172, 61, 9, 'A On je ten, jenž seslal posla Svého se správným vedením i s náboženstvím pravdivým, aby zvítězilo nad všemi náboženstvími, i když se to protiví modloslužebníkům.'),
(5173, 61, 10, 'Vy, kteří věříte! Mám vám ukázat obchod, jenž ochrání vás před trestem bolestným?'),
(5174, 61, 11, 'Uvěřte v Boha a posla Jeho a veďte boj na cestě Boží majetky svými i osobami svými! A to bude pro vás nejlepší, jestliže jste vědoucí!'),
(5175, 61, 12, 'A tehdy vám On odpustí hříchy vaše a uvede vás do zahrad, pod nimiž řeky tekou, a do příbytků příjemných v zahradách Edenu; a to bude úspěch nesmírný'),
(5176, 61, 13, 'a dá vám i jiné, jež budete milovat: vítěznou pomoc od Boha a blízké vítězství. Oznam věřícím zvěst radostnou!'),
(5177, 61, 14, 'Vy, kteří věříte! Buďte pomocníky Božími, tak jak to řekl Ježíš, syn Mariin, apoštolům: "Kdož budou pomocníky mými na cestě Boží?" I odvětili apoštolové: "My budeme pomocníky Božími." A uvěřila část dítek Izraele, zatímco druhá část neuvěřila. A podpořili jsme ty, kdož uvěřili, proti nepřátelům jejich a stali se vítěznými.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Oslavuje Boha vše, co na nebesích je i na zemi: vládce přesvatého, mocného i moudrého!'),
(5179, 62, 2, 'On je ten, jenž poslal mezi národy nevědomé posla z řad jejich, aby jim přednášel znamení Jeho, očistil je a Písmu a moudrosti je naučil, zatímco dříve žily v bludu zjevném.'),
(5180, 62, 3, 'A jsou jiní mezi nimi, kdož dosud s nimi se nesjednotili. A On mocný je i moudrý.'),
(5181, 62, 4, 'Toto je dobrodiní Boží a On dává je tomu, komu chce; a Bůh je pln dobrodiní nesmírného!'),
(5182, 62, 5, 'Ti, kterým bylo dáno nésti Tóru, a oni ji potom nenesli, jsou podobni oslu, jenž naložen je knihami. Jak hnusná je podoba lidí, kteří za lež prohlašují znamení Boží! Ale Bůh nepovede cestou správnou lid nespravedlivý.'),
(5183, 62, 6, 'Rci: "Vy, kteří vyznáváte židovství! Domníváte-li se, že jste Bohu bližší než ostatní lidé, pak si přejte smrt, jste-li pravdomluvní!"'),
(5184, 62, 7, 'Ale oni si ji nikdy nebudou přát kvůli tomu, co provedly již předtím ruce jejich. A Bůh dobře zná nespravedlivé.'),
(5185, 62, 8, 'Rci: "Smrt, před níž prcháte, se s vámi věru setká! Potom budete navráceni k Tomu, jenž zná nepoznatelné i všeobecně známé, a On vás již poučí o všem, co jste dělali!"'),
(5186, 62, 9, 'Vy, kteří věříte! Když voláno je k modlitbě v den páteční, pospíchejte vzpomínat Boha a zanechte obchodování! To bude pro vás lepší, jste-li vědoucí.'),
(5187, 62, 10, 'A když bude modlitba skončena, rozejděte se po zemi a usilujte o část přízně Boží! A vzpomínejte často Boha, snad budete blažení!'),
(5188, 62, 11, 'Však když spatří zboží nebo zábavu, pospíchají k nim a nechávají tě stát. Rci: "To, co je u Boha, je lepší než zábava a obchodování; Bůh nejlepší je z těch, kdo obživu uštědřují!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Když k tobě přicházejí pokrytci, říkají: "Dosvědčujeme, že jsi vskutku posel Boží!" A Bůh dobře ví, žes vskutku Jeho posel, a Bůh dosvědčuje, že pokrytci jsou vskutku lháři.'),
(5190, 63, 2, 'A zaštíťují se přísahami svými a přitom svádějí jiné od cesty Boží. Jak hnusné je to, co dělají!'),
(5191, 63, 3, 'A to je proto, že zprvu uvěřili a potom se stali nevěřícími a srdce jejich byla zapečetěna, takže nic nechápou.'),
(5192, 63, 4, 'A když je vidíš, líbí se ti osoby jejích, a když hovoří, nasloucháš řeči jejich, jako by to byly trámy podepřené. A domnívají se, že křik každý je proti nim. Oni jsou nepřáteli, střež se jich! Nechť Bůh proti nim bojuje! Do čeho se to jen dostali?'),
(5193, 63, 5, 'A když se jim řekne: "Pojďte, posel Boží poprosí za vás o slitování," odvracejí hlavy své a vidíš je, jak odmítají, jsouce pyšní.'),
(5194, 63, 6, 'Je pro ně zcela jedno, zda za ně prosíš o odpuštění, či nikoliv, Bůh jim beztak neodpustí. Bůh přece nepovede cestou správnou lid hanebníků.'),
(5195, 63, 7, 'To jsou ti, kdož říkají: "Nepřispívejte nic na ty, kdož jsou s poslem Božím, ale ať utečou!" A Bohu patří pokladnice nebes i země, ale pokrytci to nechápou.'),
(5196, 63, 8, 'A říkají dále: "Věru, kdybychom se vrátili do Medíny, vskutku by z ní nejmocnější vyhnal nejubožejšího." Avšak moc náleží Bohu a poslu Jeho a věřícím, leč pokrytci to nevědí.'),
(5197, 63, 9, 'Vy, kteří věříte! Nechť vás majetky vaše a děti vaše neodlákají od vzpomínání Boha. A ti, kdož tak učiní, ti věru ztrátu utrpí.'),
(5198, 63, 10, 'Rozdávejte z toho, co jsme vám uštědřili, dříve než každého z vás zastihne smrt a než vykřikne: "Pane můj, kdybys mi mohl dát odklad do lhůty blízké, abych mohl dávat almužny a stát se jedním z bezúhonných!"'),
(5199, 63, 11, 'Ale Bůh nedá odklad duši žádné, když dostaví se lhůta její, a Bůh dobře je zpraven o všem, co děláte.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Oslavuje Boha vše, co na nebesích je i na zemi; Jemu náleží království a Jemu náleží chvála a On mocný je nade věcmi všemi!'),
(5201, 64, 2, 'On je ten, jenž vás stvořil; a mezi vámi jsou nevěřící a jsou mezi vámi věřící a Bůh jasně vidí vše, co konáte.'),
(5202, 64, 3, 'Stvořil nebesa a zemi jako skutečnost vážnou, dal vám podobu a učinil podoby vaše krásnými; a u Něho je cíl konečný.'),
(5203, 64, 4, 'On zná dobře vše, co na nebesích je i na zemi, On zná, co skrýváte i co najevo dáváte. A Bůh dobře ví, co v hrudích vašich se tají.'),
(5204, 64, 5, 'Což nedostalo se vám zvěsti o těch, kdož před vámi nevěřili a okusili zlých následků jednání svého? A je očekává trest bolestný.'),
(5205, 64, 6, 'A to proto, že přišli k nim poslové jejich s jasnými důkazy, ale oni řekli: "Cožpak nás mají vést smrtelníci?" A neuvěřili, zády se odvrátili a Bůh se bez nich obešel, vždyť Bůh je soběstačný a chvályhodný.'),
(5206, 64, 7, 'A domnívají se ti, kdož jsou nevěřící, že nebudou vzkříšeni: Rci: "Ba naopak, při Pánu mém, budete vskutku vzkříšeni a potom budete poučeni o tom, co jste dělali. A to bude Bohu věcí velmi snadnou."'),
(5207, 64, 8, 'Věřte tedy v Boha, posla Jeho i světlo, jež jsme vám seslali; Bůh dobře je zpraven o všem, co děláte.'),
(5208, 64, 9, 'Den, kdy vás shromáždi pro den shromáždění, bude dnem vzájemného klamání. Těm, kdož v Boha věří a zbožné skutky konají, Bůh vymaže špatné skutky jejich a uvede je do zahrad, pod nimiž řeky tekou a kde navěky nesmrtelní zůstanou - a to bude úspěch nesmírný.'),
(5209, 64, 10, 'Ti však, kdož nevěřili a znamení Naše za lež prohlašovali, ti budou ohně obyvateli a budou v něm nesmrtelní. Jak hnusný je to cíl konečný!'),
(5210, 64, 11, 'Pohroma žádná nepropukne jinak než s dovolením Božím. A kdo věří v Boha, toho srdce On na správnou cestu uvede a Bůh je v každé věci vševědoucí.'),
(5211, 64, 12, 'Poslouchejte Boha a poslouchejte posla; však obrátíte-li se zády... tedy poslu Našemu přísluší jedině hlásání zjevné!'),
(5212, 64, 13, 'Bůh - není božstva kromě Něho - a na Boha nechť se spoléhají věřící!'),
(5213, 64, 14, 'Vy, kteří věříte! Některé z manželek vašich i dětí vašich jsou vám nepřáteli, střezte se jich! Vymažete-li však chyby druhému, prominete a odpustíte, pak i Bůh je odpouštějící, slitovný!'),
(5214, 64, 15, 'Majetky vaše a děti vaše jsou pro vás jen pokušením, zatímco Bůh má u sebe věru odměnu nesmírnou.'),
(5215, 64, 16, 'Bojte se tedy Boha, jak jen můžete! Slyšte a poslouchejte a dávejte almužny pro dobro duší svých! A ti, kdož u sebe se varují skrblictví, ti věru budou blažení!'),
(5216, 64, 17, 'Dáte-li Bohu výhodnou půjčku, On vám ji navrátí dvojnásobně a odpustí vám; vždyť Bůh je za vděčnost uznalý a laskavý,'),
(5217, 64, 18, 'zná nepoznatelné i všeobecně známé, je mocný, moudrý!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Proroku! Rozvádíte-li se svými ženami, tedy se s nimi rozveďte v ustanovené pro ně lhůtě a počítejte přesně dny čekací lhůty a bojte se Boha, Pána svého! Nevyhánějte je z jejich domů a nechť z nich neodcházejí, leda kdyby se dopustily hanebnosti zjevné. A toto jsou omezení Boží a kdo přestupuje omezení Boží, ten sám sobě křivdí. Ty nevíš, ale možná že Bůh později způsobí nějakou věc jinou.'),
(5219, 65, 2, 'A když ženy dosáhnou lhůty své, pak je buď podržte podle zvyklosti vhodné, anebo se od nich odlučte také podle zvyklosti vhodné. A vyžádejte si svědectví dvou osob spravedlivých mezi vámi a přistupte k svědectví při Bohu. A toto je napomenutí tomu, kdo věří v Boha a v den soudný. A kdo je bohabojný, tomu On připraví východisko'),
(5220, 65, 3, 'a uštědří mu obživu z místa, odkud s tím ani nepočítal. A tomu, kdo spoléhá se na Boha, tomu On postačí, vždyť Bůh uskutečňuje rozkaz Svůj a Bůh ustanovil pro věc každou rozměr její.'),
(5221, 65, 4, 'Pro ty z vašich žen, jež již nemají naději na menstruaci, ale jste-li přesto na pochybách, nechť jsou čekací lhůtou tři měsíce; a stejně platí i pro ty, které ještě menstruaci neměly. Pro ty, jež jsou těhotné, skončí čekací lhůta tehdy, když porodí. A kdo je bohabojný, tomu Bůh věc jeho ulehčí.'),
(5222, 65, 5, 'Toto je nařízení Boží, které vám sesílá. A kdo je bohabojný, tomu Bůh vymaže špatné skutky jeho a rozmnoží mu odměnu.'),
(5223, 65, 6, 'Ubytujte ženy své po rozvodu tam, kde sami bydlíte, podle možností svých. Nečiňte jim žádnou újmu s úmyslem je utiskovat. Jsou-li těhotné, přispívejte jim, pokud neporodí! A jestliže kojí pro vás dítě vaše, dávejte jim jejich odměnu a dohodněte se vzájemně podle zvyklosti uznané, však narazíte-li na obtíže, dejte je kojit ženě jiné!'),
(5224, 65, 7, 'Nechť ten, kdo je blahobytný, dává úměrně k blahobytu svému a nechť ten, jehož obživa je odměřena, dává z toho, co Bůh mu uštědřil. A Bůh neukládá duši žádné více než to, co sám jí dal. A Bůh po těžkostech přinese ulehčení.'),
(5225, 65, 8, 'Ať se již kolik chtělo z měst vzepřelo rozkazu Pána svého a posla Jeho, zúčtovali jsme s nimi účtem přísným a potrestali jsme je trestem odporným!'),
(5226, 65, 9, 'A okusila následky počínání svého a konec počínání jejich byla ztráta jejich.'),
(5227, 65, 10, 'A Bůh připravil pro obyvatele jejich trest strašný, bojte se tedy Boha vy, kdož nadáni jste rozmyslem a kteří jste uvěřili! Bůh seslal vám již připomenutí'),
(5228, 65, 11, 'posla, který vám přednáší zřetelná znamení Boží, aby vyvedl ty, kdož uvěřili a zbožné skutky konali, z temnot k světlu. Kdo v Boha věří a zbožné skutky koná, toho Bůh uvede do zahrad, pod nimiž tekou řeky, a tam bude nesmrtelný navěky. A pro takového Bůh připravil obživu překrásnou.'),
(5229, 65, 12, 'Bůh je ten, jenž stvořil sedm nebes a podobný počet zemí. A zjevení Jeho sestupuje mezi nimi, abyste zvěděli, že Bůh je nad každou věcí všemocný a že Bůh objímá věděním Svým věci všechny.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Proroku, proč ve snaze uspokojit své ženy zakazuješ to, co Bůh ti dovolil? Vždyť Bůh je odpouštějící, slitovný.'),
(5231, 66, 2, 'Bůh vám uložil, abyste se vyvázali z přísah svých; Bůh ochráncem je vaším a On vševědoucí je i moudrý.'),
(5232, 66, 3, 'Hle, prorok svěřil v tajnosti jedné ze svých žen novinku; když však to oznámila jiné a Bůh mu to odkryl, část z toho sdělil a část si ponechal pro sebe. A když jí to oznámil, zeptala se: "Kdo ti to sdělil?" I odpověděl: "Sdělil mi to vševědoucí, dobře zpravený."'),
(5233, 66, 4, 'Jestliže se obě kajícně obrátíte k Bohu... a srdce vaše se již odchýlila, avšak budete-li se mu společně protivit, tedy Bůh věru je ochráncem jeho, i Gabriel a každý spravedlivý z věřících a andělé jsou nadto pomocníky jeho.'),
(5234, 66, 5, 'Jestliže se s vámi rozvede, možná že mu Bůh pak dá v náhradu manželky lepší, než jste vy, odevzdané do vůle Jeho, věřící, pokorně oddané, kajícné, Boha uctívající a slavící, ať již předtím vdané či panny.'),
(5235, 66, 6, 'Vy, kteří věříte! Střezte sebe i rodiny své před ohněm, jehož palivem budou lidé a kamení. Na něj dohlížejí andělé přísní a silní, kteří se neodváží neuposlechnout Boha v ničem, co jim přikázal, a vykonají vše, co jim bylo poručeno.'),
(5236, 66, 7, '"Vy, kteří jste neuvěřili, nevymlouvejte se dnes! Dostává se vám jen odměny za to, co jste dělali."'),
(5237, 66, 8, 'Vy, kteří věříte! Obraťte se k Bohu s upřímným pokáním! Snad Pán váš vám vymaže špatné skutky vaše a uvede vás do zahrad, pod nimiž řeky tekou, v den, kdy nezahanbí Bůh ani proroka, ani ty, kdož s ním věřili. A světlo jejich poběží před nimi a po pravici jejich a budou volat: "Pane náš, učiň dokonalým světlo naše a odpusť nám, vždyť Tys všech věcí mocen!"'),
(5238, 66, 9, 'Proroku, bojuj usilovně proti nevěřícím a pokrytcům a buď k nim přísný! Jejich útočištěm bude peklo - jak hnusný je to cíl konečný!'),
(5239, 66, 10, 'Bůh uvádí těm, kdož neuvěřili, podobenství o ženě Noema a o ženě Lota: obě byly pod mocí Našich dvou služebníků bezúhonných a obě je zradily. Avšak nic jim nepomohli manželé jejich proti Bohu a bylo jim řečeno: "Vstupte do ohně s těmi, kdož tam vstupují!"'),
(5240, 66, 11, 'A Bůh uvádí těm, kdož uvěřili, příklad ženy Faraónovy, když zvolala: "Pane můj, postav pro mne u Sebe v ráji příbytek a zachraň mne před Faraónem a jeho skutky a zachraň mne před lidem nespravedlivým!"'),
(5241, 66, 12, 'A uvádí i Marii, dceru ´Imránovu, jež panenství své střežila a do níž jsme vdechli ducha Svého. A za pravdivá prohlásila slova Pána svého i Písma Jeho a patřila mezi Bohu pokorně oddané.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Požehnán buď ten, v jehož rukou je království a jenž všemocný je nad každou věcí,'),
(5243, 67, 2, 'jenž stvořil smrt i život, aby vyzkoušel, kdo z vás bude v skutcích nejlepší - a On mocný je i odpouštějící -'),
(5244, 67, 3, 'jenž stvořil sedm nebes ve vrstvách. A nespatříš ve stvoření Milosrdného nesouměrnost žádnou! Pozvedni jen zrak svůj, zda uvidíš trhlinu nějakou!'),
(5245, 67, 4, 'A potom obrať svůj zrak ještě dvakrát, a pohled tvůj se k tobě navrátí ponížen a vyčerpán námahou marnou.'),
(5246, 67, 5, 'My věru jsme ozdobili nejbližší nebe svítilnami, jež určili jsme i pro satanů kamenováni, a připravili jsme pro ně trest plamenný.'),
(5247, 67, 6, 'Těm, kdož neuvěřili v Pána svého, náleží trest pekelný a jak hnusný je to cíl konečný!'),
(5248, 67, 7, 'A až tam vrháni budou, uslyší řev jeho, vřením vydávaný,'),
(5249, 67, 8, 'a div že nevybuchne zuřivostí. A pokaždé když doň jedna skupina bude házena, zeptají se strážci jeho: "Což nepřišel k vám varovatel žádný?"'),
(5250, 67, 9, 'A odpovědí: "Ano, přišel k nám varovatel, ale za lháře jsme jej prohlásili a řekli: "Bůh vůbec nic neseslal a vy jste jenom ve velkém bludu!"'),
(5251, 67, 10, 'A dodají: "Kdybychom byli naslouchali či pochopili, nebyli bychom dnes věru plamene obyvateli!"'),
(5252, 67, 11, 'Svou vinu tedy doznali - pryč s plamene obyvateli!'),
(5253, 67, 12, 'Těm, kdo bojí se Pána svého kvůli nepoznatelnému, těm věru dostane se odpuštění i převeliké odměny.'),
(5254, 67, 13, 'Utajujte si řeči své či dávejte je najevo co skryto je v hrudích, On dobře ví!'),
(5255, 67, 14, 'Což nezná On ty, které stvořil, On, jenž prozíravý je a dobře zpravený?'),
(5256, 67, 15, 'On je ten, jenž pro vás zemi pokornou učinil; choďte po zádech jejích a jezte z toho, co vám uštědřil a u Něho bude pak vzkříšeni.'),
(5257, 67, 16, 'Jste si jisti tím, že ten, kdo na nebi dlí, vás nedá pohltit zemí? Hle, ona se již chví!'),
(5258, 67, 17, 'Jste si jisti tím, že ten, kdo na nebi dlí, na vás nepošle vichřici plnou kamení? Pak poznáte, co znamená Mé varování!'),
(5259, 67, 18, 'Již ti, kdo před nimi byli, posly za lháře prohlásili. A jaká byla Má nevole?'),
(5260, 67, 19, 'Což neviděli ptactvo nad sebou, jak křídla roztahuje a skládá? Nikdo krom Milosrdného je nedrží a On jasně všechny věci zří.'),
(5261, 67, 20, 'A kdo je ten, jenž pro vás vojskem je, aby vám pomohl, leda Milosrdný? Však nevěřící jsou jen v zaslepení.'),
(5262, 67, 21, 'A kdo je ten, jenž obživu vaši vám uštědří, jestliže On štědrost svou zadrží? Však naopak oni setrvávají ve vzpurnosti a odmítání!'),
(5263, 67, 22, 'Což ten, jenž klopýtá padaje na tvář svou, je veden lépe než ten, kdo vzpřímeně po stezce přímé kráčí?'),
(5264, 67, 23, 'Rci: "On je ten, kdo vzniknout vám dal a daroval vám sluch, zrak i srdce - a jak málo jste vděčni!"'),
(5265, 67, 24, 'Rci: "On je ten, kdo rozšířil vás po zemi - a k Němu budete shromážděni."'),
(5266, 67, 25, 'A ptají se: "A kdy se tento slib splní, jste-li pravdomluvní?"'),
(5267, 67, 26, 'Odpověz: "Vědomost o tom je u Boha jedině - já jsem jen varovatel zjevný."'),
(5268, 67, 27, 'A až zblízka trest uvidí, naplní se tváře těch, kdož nevěřili, hrůzou; a bude jim řečeno: "Tohle je to, co jste požadovali!"'),
(5269, 67, 28, 'Rci: "Co si myslíte? A ať již Bůh mě zahubí spolu s těmi, kdož se mnou jsou, anebo se nad námi slituje, kdo potom ochrání nevěřící před trestem bolestným?"'),
(5270, 67, 29, 'Rci: "On je Milosrdný; uvěřili jsme v Něho a spoléháme na Něho. A záhy se dozvíte, kdo vlastně v bludu zjevném se nalézá!"'),
(5271, 67, 30, 'Rci: "Co si myslíte? Kdyby se jednou zrána vody vaše v hloubce ztratily, kdo tedy, ne-li Bůh, vodu pramenitou vám dá?"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nún. Při peru a při tom, co píší,'),
(5273, 68, 2, 'díky milosti Pána svého ty blázen žádný nejsi!'),
(5274, 68, 3, 'A věru tě očekává odměna neupomínaná,'),
(5275, 68, 4, 'vždyť vskutku povaha tvá je vznešená.'),
(5276, 68, 5, 'A spatříš ty a také oni spatří,'),
(5277, 68, 6, 'kdo z vás je pokušením svedený.'),
(5278, 68, 7, 'Pán tvůj nejlépe zná, kdož z cesty jeho zbloudili, a dobře ví i o těch, kdož správně jsou vedeni.'),
(5279, 68, 8, 'Neposlouchej ty, kdo za lháře tě prohlásili!'),
(5280, 68, 9, 'Rádi by, abys jim lichotil, a pak i oni by ti lichotili.'),
(5281, 68, 10, 'Neposlouchej žádného křivopřísežníka hnusného,'),
(5282, 68, 11, 'klevetníka s pomluvami obcházejícího,'),
(5283, 68, 12, 'dobrému se na odpor stavějícího, nepřítele hříšného,'),
(5284, 68, 13, 'násilníka, a k tomu původu pochybného'),
(5285, 68, 14, '- i když je majetný a syny obdařen!'),
(5286, 68, 15, 'Když jsou před ním přednášeny naše verše, volá: "Povídačky starých!"'),
(5287, 68, 16, 'Však bude námi na rypáku označen!'),
(5288, 68, 17, 'A podrobujeme je zkoušce podobné té, jíž podrobili jsme majitele zahrady, kteří přísahali, že půjdou do ní zrána úrodu sklidit,'),
(5289, 68, 18, 'však nepronesli: "Bude-li Bůh chtít!"'),
(5290, 68, 19, 'A zatímco spali, jedna z metel Pána tvého ji navštívila,'),
(5291, 68, 20, 'takže za jitra zahrada jak očesána byla.'),
(5292, 68, 21, 'A zrána na sebe volali:'),
(5293, 68, 22, '"Pojďme časně na svá pole, abychom všechno sklidili!"'),
(5294, 68, 23, 'A na cestu se vydali a vzájemně si šeptali:'),
(5295, 68, 24, '"Jen aby nám sem dnes žádný nuzák nepřišel!"'),
(5296, 68, 25, 'A tak za jitra vyrazili, aby rozhodnutí své vyplnili,'),
(5297, 68, 26, 'však vidouce zahradu, vykřikli: "Věru jsme v bludu byli!'),
(5298, 68, 27, 'Ba co víc, vždyť nezbylo nám vůbec nic!"'),
(5299, 68, 28, 'I pravil nejobezřetnější z nich: "Což neříkal jsem vám, že slávu Boží pronésti jsme měli?"'),
(5300, 68, 29, 'A zvolali: "Sláva budiž Pánu našemu, vždyť vskutku jsme nespravedliví byli."'),
(5301, 68, 30, 'A vzájemně si výčitky činili'),
(5302, 68, 31, 'řkouce: "Běda nám, vpravdě jsme vzpurní byli;'),
(5303, 68, 32, 'ale snad Pán náš výměnou za ni nám lepší dá, vždyť my věru po Pánu svém touhu máme!"'),
(5304, 68, 33, 'Takový bývá trest na tomto světě, však trest na onom světě ještě větší bude - kéž by si to uvědomili!'),
(5305, 68, 34, 'Bohabojní pak u Pána svého v zahradách slastí žíti budou.'),
(5306, 68, 35, 'Což učiníme ty, kdož do vůle Boží se odevzdali, stejnými jako hříšníky?'),
(5307, 68, 36, 'Co je vám, že tak soudíte?'),
(5308, 68, 37, 'Máte snad Písmo, z něhož studujete,'),
(5309, 68, 38, 'a je v něm pro vás jen to, co si vyberete?'),
(5310, 68, 39, 'Či jste snad s Námi spojeni přísahami platnými do dne zmrtvýchvstání, že dostanete to, co sami rozhodnete?'),
(5311, 68, 40, 'Zeptej se jich, kteří z nich jsou toho ručiteli,'),
(5312, 68, 41, 'či zda mají společníky? Ať tedy přivedou své společníky - pravdu hovoří-li -'),
(5313, 68, 42, 'v den, kdy lýtka budou obnažena a oni výzvu dostanou, aby na zem padli, a schopni toho nebudou.'),
(5314, 68, 43, 'A hanba na ně padne a zraky ponížením sklopí: vždyť k padnutí na zem vyzváni byli, když ještě v bezpečí na zemi žili.'),
(5315, 68, 44, 'Nech mne s těmi, kdož toto vyprávění za lež prohlásili; postupně zkázu k nim přivedu, aniž místo, odkud přišla, tušit budou.'),
(5316, 68, 45, 'Já značný jim dávám odklad, však jistý je můj úklad!'),
(5317, 68, 46, 'Či žádáš od nich odměnu a zatímco je dluhy tíží'),
(5318, 68, 47, 'anebo znají nepoznatelné z něhož si píší?'),
(5319, 68, 48, 'Trpělivě tedy vyčkávej rozsudek Pána svého a nebuď jako onen s rybou, jenž o pomoc volal, když cítil úzkost tísnivou.'),
(5320, 68, 49, 'A kdyby ho nebylo dostihlo Pána jeho dobrodiní, na holou zemi býval by byl vyvržen pln zahanbení.'),
(5321, 68, 50, 'Však Pán jeho si jej vyvolil a jedním z bezúhonných jej učinil.'),
(5322, 68, 51, 'A nevěřící, když připomenutí slyší, div že pohledy tě neproklínají a hovoří: "On džiny je posedlý věru!"'),
(5323, 68, 52, 'Však není to než připomenutí lidstvu všemu!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Nevyhnutelná!'),
(5325, 69, 2, 'Co je to nevyhnutelná?'),
(5326, 69, 3, 'Víš ty vůbec, co je to nevyhnutelná?'),
(5327, 69, 4, 'Jak Thamúdovci, tak i ´Ádovci roztříšťující za lež pokládali.'),
(5328, 69, 5, 'Co Thamúdovců se týče, ti byli ranou hromovou zahubeni,'),
(5329, 69, 6, 'zatímco ´Ádovci byli vichřicí svištivou, nezkrotnou skoseni.'),
(5330, 69, 7, 'Bůh dal jí, zničující, nad nimi vládu po osm dní a sedm nocí; a byl bys tam viděl lidi k zemi vržené, jako by to byly kmeny palem vykotlané.'),
(5331, 69, 8, 'A vidíš snad, že by po nich bylo něco zůstalo?'),
(5332, 69, 9, 'Též Faraón a ti, kdož před ním byli, i města vyvrácená se hříchy provinili'),
(5333, 69, 10, 'a posla Pána svého neposlouchali, takže Bůh trestem nadměrným je uchvátil.'),
(5334, 69, 11, 'A když se vodstva rozlila, na plující archu jsme vás přenesli,'),
(5335, 69, 12, 'abychom ji pro vás připomenutím učinili a aby uši pozorné to podržely.'),
(5336, 69, 13, 'Až bude zatroubeno na pozoun jedním zatroubením'),
(5337, 69, 14, 'a země a hory budou zvednuty a rozdrceny úderem jedním,'),
(5338, 69, 15, 'v ten den nezvratná dopadne'),
(5339, 69, 16, 'a nebe se rozpoltí a bude zející toho dne'),
(5340, 69, 17, 'a stát budou andělé na krajích jeho; v ten den osm jich nad sebou ponese trůn Pána tvého.'),
(5341, 69, 18, 'V ten den předvedeni budete a nezůstane u vás skryto nic tajného.'),
(5342, 69, 19, 'A komu bude dána kniha jeho do pravice, ten řekne: "Zde přečtěte si knihu mou!'),
(5343, 69, 20, 'Já soudil jsem vždy, že s účtem svým se setkám jednou."'),
(5344, 69, 21, 'A takový bude mít žití příjemné'),
(5345, 69, 22, 'v zahradě vznešené,'),
(5346, 69, 23, 'jejíž plody na dosah budou.'),
(5347, 69, 24, '"Jezte a pijte v bezpečí za to, co jste již vykonali v době minulé!"'),
(5348, 69, 25, 'A komu bude dána kniha jeho do levice, ten řekne: "Ach, kéž by mi nebyla dána kniha má'),
(5349, 69, 26, 'a já se nikdy nedozvěděl účtu svého výsledek!'),
(5350, 69, 27, 'Ach, kéž by tato smrt byla konečná!'),
(5351, 69, 28, 'K ničemu dnes mi není můj majetek'),
(5352, 69, 29, 'a zanikla, daleko ode mne, pravomoc má!"'),
(5353, 69, 30, '"Chopte se ho a v řetězy jej zakovejte,'),
(5354, 69, 31, 'pak v ohni pekelném jej palte'),
(5355, 69, 32, 'a potom řetězem sedmdesát loktů dlouhým jej svažte!"'),
(5356, 69, 33, 'On věru nevěřil v Boha mocného'),
(5357, 69, 34, 'a nevybízel k nakrmení nuzného,'),
(5358, 69, 35, 'takže zde nemá dnes druha vřelého'),
(5359, 69, 36, 'ani krom splašek pokrmu jiného,'),
(5360, 69, 37, 'jež jísti budou jen těžcí hříšníci.'),
(5361, 69, 38, 'Hle, já přísahám při tom, co vidíte,'),
(5362, 69, 39, 'i při tom, co nezříte,'),
(5363, 69, 40, 'že toto zajisté je řeč posla vznešeného!'),
(5364, 69, 41, 'Nejsou to slova básníkova - jak málo vy věříte -'),
(5365, 69, 42, 'ani řeč věštcova - jak krátkou paměť máte -'),
(5366, 69, 43, 'leč je to od Pána lidstva seslání!'),
(5367, 69, 44, 'A kdyby si on proti vůli Naší vymýšlel nějaká rčení,'),
(5368, 69, 45, 'věru bychom jej vzali za jeho pravici'),
(5369, 69, 46, 'a pak mu prořízli jeho srdečnici'),
(5370, 69, 47, 'a žádný z vás by tomu nemohl zabránit.'),
(5371, 69, 48, 'Korán vskutku je připomenutí bohabojným'),
(5372, 69, 49, 'a dobře víme, že mezi vámi jsou ti, kdo nazývají jej vylhaným.'),
(5373, 69, 50, 'A věru on zármutku nevěřících je příčinou'),
(5374, 69, 51, 'a vskutku je pravdou jistotnou!'),
(5375, 69, 52, 'Chval tedy jméno Pána svého mocného!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Ptal se tázající na trest, jenž dopadne'),
(5377, 70, 2, 'na nevěřící: není nikoho, kdo jej odrazí,'),
(5378, 70, 3, 'vždyť od Boha, Pána stupňů, přichází!'),
(5379, 70, 4, 'Stoupají k němu po nich andělé i duch v den, jehož délka let padesát tisíc měří.'),
(5380, 70, 5, 'Neochvějný buď a pěkně jen vytrvej!'),
(5381, 70, 6, 'Nevěřící daleko vidí ten den,'),
(5382, 70, 7, 'zatímco my nablízku jej vidíme.'),
(5383, 70, 8, 'V ten den nebe bude roztavené mosazi podobné'),
(5384, 70, 9, 'a hory budou jak vločky vlny barevné'),
(5385, 70, 10, 'a nebude se ptát přítel vřelý na přítele,'),
(5386, 70, 11, 'ačkoliv budou na dohled. Hříšník zatouží se vykoupit z trestu dne onoho syny vlastními,'),
(5387, 70, 12, 'i bratrem svým a družkou svou'),
(5388, 70, 13, 'a klanem vlastním, jenž mu je ochranou,'),
(5389, 70, 14, 'ba všemi, kdož na zemi jsou, aby se to stalo jeho záchranou.'),
(5390, 70, 15, 'Však pozor! Toto věru je oheň šlehající,'),
(5391, 70, 16, 'kůži na lebce spalující,'),
(5392, 70, 17, 'jenž svolává toho, kdo zády se otočil a odvrátil'),
(5393, 70, 18, 'a majetek shromažďoval a nakupil.'),
(5394, 70, 19, 'Člověk byl věru stvořen vrtkavý:'),
(5395, 70, 20, 'když dotkne se ho neštěstí, je ustrašený,'),
(5396, 70, 21, 'když dotkne se jej dobré, je odmítavý.'),
(5397, 70, 22, 'Ne tak ti, kdo se modlí,'),
(5398, 70, 23, 'kdo v modlitbách jsou vytrvalí,'),
(5399, 70, 24, 'kdo stanovili podíl z jmění svého'),
(5400, 70, 25, 'pro žebráka i nešťastníka nemajetného'),
(5401, 70, 26, 'a kdo den soudný za pravdivý uznávají'),
(5402, 70, 27, 'a trestu Pána svého se obávají'),
(5403, 70, 28, '- vždyť před trestem Pána tvého nikdo jist si není -'),
(5404, 70, 29, 'a kdo svá pohlaví chrání'),
(5405, 70, 30, 'jen pro manželky své či otrokyně; a nebudou za to pokáráni -'),
(5406, 70, 31, 'kdož však více než po tomto touží, to věru jsou přestupníci;'),
(5407, 70, 32, 'a kdo svěřené opatrují a úmluvy své pevně dodržují'),
(5408, 70, 33, 'a kdo při svědectvích svých v pravdě stojí'),
(5409, 70, 34, 'a o modlitby své pilně dbají -'),
(5410, 70, 35, 'takoví v zahradách rajských se s poctami setkají!'),
(5411, 70, 36, 'Co děje se s nevěřícími, že k tobě krky natahují'),
(5412, 70, 37, 'a zprava i zleva se shlukují?'),
(5413, 70, 38, 'Žádá snad každý z nich, aby uveden byl do zahrad slastí?'),
(5414, 70, 39, 'Však pozor! Stvořili jsme je věru z toho, co sami dobře znají.'),
(5415, 70, 40, 'Však pozor! Přísahám při Pánu východů i západů, že vskutku schopni jsme'),
(5416, 70, 41, 'lepšími, než oni jsou, je vystřídat, a předejít se v tom nedáme.'),
(5417, 70, 42, 'Nech je být, ať si tlachají a hrají, až se setkají se svým dnem, jenž byl jim přislíben,'),
(5418, 70, 43, 'se dnem, kdy z hrobů jako by k obětištím chvátali vystoupí se spěchem,'),
(5419, 70, 44, 'se zrakem zahanbeným, pokryti ponížením. Takový bude den, jenž byl jim přislíben!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Věru jsme poslali Noema k lidu jeho s rozkazem: "Varuj lid svůj, dříve než postihne jej trest bolestný!"'),
(5421, 71, 2, 'I pravil Noe: "Lide můj, jsem vyslán k vám jako varovatel zjevný,'),
(5422, 71, 3, 'abyste Boha uctívali, bohabojní byli a mne poslouchali!'),
(5423, 71, 4, 'On odpustí vám vaše viny a dá vám odklad až do lhůty určené. Však až lhůta Boží přijde, odložena nebude kéž byste to znali!"'),
(5424, 71, 5, 'A zvolal: "Pane můj, vyzýval jsem lid svůj nocí i dnem,'),
(5425, 71, 6, 'mé kázání však rozmnožilo jejich odpor jen.'),
(5426, 71, 7, 'A kdykoliv jsem je vyzýval - abys jim mohl odpustit - tu prsty si do uší strkali, do šatů svých se halili, zatvrdili se a chovali se hrdopyšně.'),
(5427, 71, 8, 'Potom jsem jim kázal zcela veřejně,'),
(5428, 71, 9, 'pak jsem vystoupil otevřeně a jednal s nimi skrytě i tajně'),
(5429, 71, 10, 'a pravil jsem jim:, Proste Pána svého za odpuštění, vždyť On věru je odpouštějící!'),
(5430, 71, 11, 'On sešle na vás nebe deštěm hojným'),
(5431, 71, 12, 'a rozmnoží vám majetky i syny a připraví pro vás zahrady a řeky.'),
(5432, 71, 13, 'Co je vám, že nedoufáte ve velkomyslnost Boží?'),
(5433, 71, 14, 'On přece stvořil vás v postupných formách;'),
(5434, 71, 15, 'což nevidíte, jak Bůh stvořil sedm nebes ve vrstvách'),
(5435, 71, 16, 'a umístil mezi nimi měsíc jako světlo a slunce učinil pochodní?'),
(5436, 71, 17, 'Bůh dal vám vyrůst ze země jako rostlinám,'),
(5437, 71, 18, 'potom vás do ní vrátí a posléze vás z ní vyvede znenadání.'),
(5438, 71, 19, 'A Bůh zemi jako koberce rozprostřel vám,'),
(5439, 71, 20, 'abyste cestami širokými chodili po ní.? "'),
(5440, 71, 21, 'I pravil Noe dále: Pane můj, oni mne neuposlechli a následovali toho, jehož majetek a děti jen zkázu jeho zvětšily,'),
(5441, 71, 22, 'a nesmírný úklad proti mně připravili'),
(5442, 71, 23, 'a zvolali:, Neopouštějte božstva svá! Neopouštějte Wadda ani Suwá´, ani Jaghútha a Ja´úqa a Nasra!?'),
(5443, 71, 24, 'A věru oni mnohým zbloudit dali; a nerozmnožuj nespravedlivým ničeho, leda jejich bloudění!"'),
(5444, 71, 25, 'Pro hříchy své pak byli utopeni a do ohně pekelného vrženi a nenalezli pro sebe mimo Boha žádné pomocníky.'),
(5445, 71, 26, 'A pravil Noe: "Pane můj, neponechávej na zemi žádného obyvatele z nevěřících,'),
(5446, 71, 27, 'vždyť ponecháš-li je, oni dají zbloudit služebníkům Tvým a nezrodí nic, než zase zarputilé nevěřící a hříšníky.'),
(5447, 71, 28, 'Pane můj, odpusť mi a rodičům mým i těm, kdož jako věřící do mého domu vstoupili, a také věřícím mužům a ženám! A nerozmnožuj nespravedlivým nic, leda záhubu jejich!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Rci: "Bylo mi vnuknuto, že skupina džinů naslouchala a řekla:, Naslouchali jsme Koránu podivuhodnému,'),
(5449, 72, 2, 'k přímé cestě vedoucímu, takže jsme v něj uvěřili a již nikoho nebudeme přidružovat k Pánu, svému.'),
(5450, 72, 3, 'A věru On - povznesena nechť je důstojnost Pána našeho - si nevzal družku ani syna žádného.'),
(5451, 72, 4, 'Avšak hlupák jeden z nás o Bohu vedl řeči míru přesahující,'),
(5452, 72, 5, 'ačkoliv jsme se dosud domnívali, že ani lidé, ani džinové o Bohu nebudou vést řeči lživé!'),
(5453, 72, 6, 'Mužové někteří pak z lidí u mužů z džinů útočiště vyhledali, ti však jen pošetilost jejich rozmnožili'),
(5454, 72, 7, 'a lidé se - podobně jako vy - že Bůh nikoho nevzkřísí, domnívali.'),
(5455, 72, 8, 'A prohledali jsme nebe, avšak nalezli jsme je plné stráží silných a plamenů šlehajících.'),
(5456, 72, 9, 'A sedávali jsme dříve u něho na místech vhodných k naslouchání, však kdokoliv z nás chce naslouchat nyní, ten shledá, že naň číhá plamen šlehající.'),
(5457, 72, 10, 'A my věru nevíme, zda zlo je zamýšleno pro ty, kdož na zemi dlí, či zda Pán jejich si pro ně přeje správného vedení.'),
(5458, 72, 11, 'Mezi námi jsou věru bezúhonní a jsou mezi námi zajisté i jiní než takoví, my na sekty jsme rozděleni.'),
(5459, 72, 12, 'Domníváme se, že nikdy se nám nepodaří uniknout zásahu Božímu na zemi, ba ani pomocí útěku.'),
(5460, 72, 13, 'A když slyšeli jsme o správném vedení, uvěřili jsme v ně; a ten, kdo v Pána svého věří, ať nebojí se ani poškození, ani útlaku.'),
(5461, 72, 14, 'A jsou mezi námi ti, kdož do vůle Boží se odevzdali, a jiní, kteří nespravedliví jsou. A ten, kdo do vůle Boží se odevzdal, ten ubírá se cestou správnou.'),
(5462, 72, 15, 'Co nespravedlivých se týká, ti peklu palivem budou.? "'),
(5463, 72, 16, 'A půjdou-li touto cestou, My věru je napojíme vodou hojnou,'),
(5464, 72, 17, 'abychom je zkoušeli. Kdokoliv však se odvrátí od připomenutí Pána svého, toho On uvede do trestu těžkého.'),
(5465, 72, 18, 'A modlitebny patří Bohu; nevzývejte v nich tedy spolu s Bohem nikoho!'),
(5466, 72, 19, 'A když povstal služebník Boží, vzývaje Jej, tu div se proti němu nezvedli v hustém zástupu.'),
(5467, 72, 20, 'Rci: "Já modlím se pouze k Pánu svému a nikoho nepřidružuji k Němu."'),
(5468, 72, 21, 'Rci: "Nevládnu mocí způsobit vám škodu, či abyste správně byli vedeni.'),
(5469, 72, 22, 'Rci: "Neochrání mě před Bohem nikdo a nenaleznu útočiště žádné kromě Něho,'),
(5470, 72, 23, 'kromě sdělení od Boha a Jeho poselství. Těm, kdo neposlouchají Boha a Jeho posla, těm určen je oheň pekelný a budou v něm navěky nesmrtelní.'),
(5471, 72, 24, 'A až spatří to, co jim bylo slíbeno, poznají, kdo pomocníky má nejslabší a kdo počtem je nejmenší."'),
(5472, 72, 25, 'Rci: "Nevím, zda je blízko to, co vám bylo slíbeno, či zda Pán můj dá odklad tomu další;'),
(5473, 72, 26, 'On jediný zná nepoznatelné a nikoho do svých tajemství nezasvěcuje'),
(5474, 72, 27, 'kromě těch, jež oblíbil si ze Svých poslů; a před ním i za ním dozorce kráčí,'),
(5475, 72, 28, 'aby zjistil, zda poselství Pána svého hlásá. A On Svým věděním vše, co konají, objímá a On každou věc přesným počtem počítá.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ty zahalený!'),
(5477, 73, 2, 'Modli se v noci, kromě chvíle malé,'),
(5478, 73, 3, 'polovinu z ní, či uber z toho nemnoho,'),
(5479, 73, 4, 'či přidej k tomu něco a prozpěvuj Korán slavnostně!'),
(5480, 73, 5, 'Obtížíme tě záhy slovem nadmíru vážným:'),
(5481, 73, 6, 'na počátku noci jsou dojmy věru hlubší a přímější výrazem svým,'),
(5482, 73, 7, 'vždyť během dne starosti máš rozsáhlé.'),
(5483, 73, 8, 'Vzpomínej tedy jména Pána svého a oddej se Mu oddaně!'),
(5484, 73, 9, 'On Pánem je východu i západu a není božstva kromě Něho; učiň jej svým ochráncem'),
(5485, 73, 10, 'a trpělivě snášej to, co oni hovoří, a vzdal se od nich vhodným odchodem.'),
(5486, 73, 11, 'Ponech Mně na starost ty, kdož za lháře tě mají a ve slastech si žijí, a popřej jim lhůtu krátkou!'),
(5487, 73, 12, 'My pouta máme pro ně a výheň ohnivou,'),
(5488, 73, 13, 'pokrm, jenž v hrdle dáví, a trest bolestný'),
(5489, 73, 14, 'v den, kdy země a hory se budou třást a hory jak přesypy písečné se budou sesouvat.'),
(5490, 73, 15, 'A věru jsme k vám vyslali posla svědčícího proti vám, podobně jako jsme vyslali posla k Faraónovi předtím,'),
(5491, 73, 16, 'však Faraón neuposlechl posla tohoto; i uchvátili jsme jej uchvácením prudkým.'),
(5492, 73, 17, 'Jak chcete se chránit jste-li nevěřící přede dnem, jenž děti šedovlasými učiní,'),
(5493, 73, 18, 'v němž nebe se rozpoltí a slib o něm daný se vyplní?'),
(5494, 73, 19, 'Toto je vskutku připomenutí, a kdo chce, ať k Pánu svému cestu nastoupí.'),
(5495, 73, 20, 'Věru Pán tvůj dobře ví, že ty a skupina těch, kdož s tebou jsou, modlitbu konáte skoro dvě třetiny, polovinu či třetinu noci. A Bůh den i noc vyměřuje a On ví, že přesně to nepočítáte, a již vám prominul. Přednášejte tedy z Koránu to, co je pro vás snadné! A On ví, že mezi vámi budou nemocní a jiní, již po zemi cestovat budou část přízně Boží hledajíce, i ti, kteří budou bojovat na Boží stezce. Přednášejte tedy z Koránu to, co je pro vás snadné, modlitbu dodržujte, almužnu dávejte a Bohu dobrou půjčku poskytněte! A co jste z dobrých skutků sami pro sebe si předem připravili, to naleznete u Boha lepší a větší v podobě odměny. A Boha proste za odpuštění, vždyť Bůh je odpouštějící, slitovný!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ty rouchem přikrytý!'),
(5497, 74, 2, 'Vstaň a varuj,'),
(5498, 74, 3, 'Pána svého oslavuj,'),
(5499, 74, 4, 'svůj oděv očišťuj,'),
(5500, 74, 5, 'hněvu Božího se varuj,'),
(5501, 74, 6, 'kvůli rozmnožování zisku neuštědřuj'),
(5502, 74, 7, 'a při Pánu svém trpělivě stůj!'),
(5503, 74, 8, 'Až zatroubeno bude pozounem,'),
(5504, 74, 9, 'ten den bude věru těžkým dnem,'),
(5505, 74, 10, 'pro nevěřící nelehkým břemenem.'),
(5506, 74, 11, 'Nech Mne s tím, jehož jsem Já jediný stvořil,'),
(5507, 74, 12, 'jemuž jsem majetek rozsáhlý daroval'),
(5508, 74, 13, 'a syny zde přítomné uštědřil'),
(5509, 74, 14, 'a jemuž jsem vše hladce uhladil,'),
(5510, 74, 15, 'a on pak ještě dychtí, abych více mu dal!'),
(5511, 74, 16, 'Však pozor! Vždyť proti Našim znamením on brojí,'),
(5512, 74, 17, 'však vyženu ho na vrch příkrý!'),
(5513, 74, 18, 'On věru vymýšlel a rozhodoval,'),
(5514, 74, 19, 'ať tedy zhyne tak, jak rozhodoval!'),
(5515, 74, 20, 'A znovu: ať tedy zhyne tak, jak rozhodoval!'),
(5516, 74, 21, 'Potom kol sebe se rozhlížel,'),
(5517, 74, 22, 'pak zamračil se a nasupil,'),
(5518, 74, 23, 'nakonec zády se obrátil a pyšně odcházel'),
(5519, 74, 24, 'a pravil: "To není leč kouzlo osvojené'),
(5520, 74, 25, 'a slova jen bytosti smrtelné!"'),
(5521, 74, 26, 'Sežehnu však já jej v ohni Saqaru!'),
(5522, 74, 27, 'Víš ty vůbec, co je to Saqar?'),
(5523, 74, 28, 'Nic nenechá a nic nepustí'),
(5524, 74, 29, 'a kůži smrtelníkům spálí'),
(5525, 74, 30, 'a devatenáct andělů nad ním bdí.'),
(5526, 74, 31, 'A za strážce pekla jsme pouze anděly určili a jejich počet jen pro pokušení nevěřících stanovili, aby ti, jímž dostalo se Písma, se utvrdili a rozmnožila se víra těch, kdož uvěřili, a aby věřící a ti, jimž Písmo bylo dáno, nepochybovali a aby ti, v jejichž srdcích nemoc je, a také nevěřící pravili: "Co chtěl Bůh tímto příkladem říci?" Takto Bůh zbloudit nechává, koho chce, a vede cestou přímou, koho chce. A nikdo kromě Něho nezná vojska Pána tvého. A není toto leč připomenutí smrtelníkům!'),
(5527, 74, 32, 'Však pozor! Při měsíci,'),
(5528, 74, 33, 'při noci, když ustupuje,'),
(5529, 74, 34, 'při úsvitu, když se rozjasňuje!'),
(5530, 74, 35, 'Saqar je věru jeden z trestů největších,'),
(5531, 74, 36, 'jako varování smrtelníkům daný,'),
(5532, 74, 37, 'těm z vás, kdož chtějí k víře vpředu jít anebo se chtějí opozdit.'),
(5533, 74, 38, 'Každá duše je ručitelkou toho, co si sama vysloužila,'),
(5534, 74, 39, 'kromě lidi po pravici,'),
(5535, 74, 40, 'kteří v zahradách vzájemně se budou ptát'),
(5536, 74, 41, 'na hříšníky:'),
(5537, 74, 42, '"Co přivedlo vás do Saqaru?"'),
(5538, 74, 43, 'odvětí: "Nebyli jsme mezi modlitbu konajícími'),
(5539, 74, 44, 'ani mezi nuzné krmícími,'),
(5540, 74, 45, 'ale tlachali jsme mezi tlachajícími'),
(5541, 74, 46, 'a den soudný jsme za lež prohlásili,'),
(5542, 74, 47, 'až přišla na nás tato jistota!"'),
(5543, 74, 48, 'A nebude jim nic platná přímluva přímluvců!'),
(5544, 74, 49, 'Co s nimi je, že se odvracejí od připomenutí,'),
(5545, 74, 50, 'jako by byli osly splašenými,'),
(5546, 74, 51, 'před lvem prchajícími?'),
(5547, 74, 52, 'Věru, chce každý z nich, aby svitky rozevřené mu byly přineseny?'),
(5548, 74, 53, 'Však pozor! Oni se naopak neobávají života budoucího.'),
(5549, 74, 54, 'Však pozor! Toto jest připomenutí,'),
(5550, 74, 55, 'a kdo chce, ať na ně si vzpomene!'),
(5551, 74, 56, 'Však vzpomenou si jen tehdy, když Bůh si to bude přát a Jemu náleží, aby se Ho báli, a Jemu patří odpuštění dát!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Hle, přísahám při dni zmrtvýchvstání'),
(5553, 75, 2, 'a při duši, jež stálé výčitky si činí!'),
(5554, 75, 3, 'Myslí si člověk snad, že neshromáždíme jeho kosti?'),
(5555, 75, 4, 'Však ano, a dokonce schopni jsme sestavit jeho prsty!'),
(5556, 75, 5, 'A naopak, člověk si spíše přeje jednat i dál hříšně'),
(5557, 75, 6, 'a ptá se: "Kdy vůbec ten den zmrtvýchvstání nastane?"'),
(5558, 75, 7, 'Až zrak se oslepí,'),
(5559, 75, 8, 'až měsíc se zatmí,'),
(5560, 75, 9, 'až slunce s měsícem se spojí,'),
(5561, 75, 10, 'tehdy člověk vykřikne: "Kam prchnout mám?"'),
(5562, 75, 11, 'Však pozor! Nebude žádný úkryt znám,'),
(5563, 75, 12, 'u Pána tvého v ten den bude shromaždiště jen!'),
(5564, 75, 13, 'V ten den bude člověku sděleno, co zavčas učinil a co zameškal.'),
(5565, 75, 14, 'Ba co víc, člověk sám sebe bude jasně zřít,'),
(5566, 75, 15, 'a kdyby své omluvy mohl předložit...'),
(5567, 75, 16, 'Nehýbej příliš jazykem svým, když je přednášíš, ve snaze je uspíšit,'),
(5568, 75, 17, 'vždyť Nám přísluší je shromáždit i číst.'),
(5569, 75, 18, 'A když ti je předčítáme, sleduj jeho přednášení,'),
(5570, 75, 19, 'a potom na Nás je podat jeho objasnění.'),
(5571, 75, 20, 'Však pozor! Vy naopak milujete rychle plynoucí'),
(5572, 75, 21, 'a zanedbáváte život budoucí.'),
(5573, 75, 22, 'V ten den budou jedny tváře zářící'),
(5574, 75, 23, 'a ke svému Pánu hledící'),
(5575, 75, 24, 'a jiné tváře v ten den budou sinalé, tušící,'),
(5576, 75, 25, 'že dovrší se na nich trest páteř lámající.'),
(5577, 75, 26, 'Však pozor! Až duše vystoupí ke klíční kosti'),
(5578, 75, 27, 'a bude řečeno: "Kdo je zaklínatel?"'),
(5579, 75, 28, 'a ona si pomyslí, že toto je se světem rozluka,'),
(5580, 75, 29, 'a proplete se s nohou noha,'),
(5581, 75, 30, 'v ten den k tvému Pánu všichni hnáni budou.'),
(5582, 75, 31, '"On almužnu nedával ani modlitbu nekonal,'),
(5583, 75, 32, 'však odvracel se a za lež prohlašoval,'),
(5584, 75, 33, 'a pak ke své rodině pyšně kráčeje se odebral!"'),
(5585, 75, 34, 'Ach běda ti, běda!'),
(5586, 75, 35, 'A znovu běda ti, běda!'),
(5587, 75, 36, 'Myslí si člověk snad, že sám sobě bude zůstaven?'),
(5588, 75, 37, 'Což nebyl kapkou semene vystříknutého jen?'),
(5589, 75, 38, 'Potom se kapkou přilnavou stal a Bůh jej stvořil a vyrovnal'),
(5590, 75, 39, 'a dvě pohlaví, mužské a ženské, z něho udělal.'),
(5591, 75, 40, 'Což není v moci jeho, aby mrtvým opět život dal?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Zdaž uběhla nad člověkem chvíle času, kdy nebyl věcí pojmenovanou?'),
(5593, 76, 2, 'Věru jsme člověka stvořili z kapky semene, ze směsí, zkoušejíce jej. A slyšícím a vidoucím jsme jej učinili'),
(5594, 76, 3, 'a na správnou cestu - ať vděčný je, či nevděčný - jsme jej uvedli.'),
(5595, 76, 4, 'Pro nevěřící jsme pak řetězy, okovy a plamen šlehající připravili,'),
(5596, 76, 5, 'zatímco zbožní popíjet z poháru budou, v němž kafr je přimísen,'),
(5597, 76, 6, 'z pramene, z něhož služebníci Boží pijí jen, a v hojnosti tryskajícím ven.'),
(5598, 76, 7, 'A oni důsledně své přísliby plnili a obávali se dne, jehož zlo se rozletí široce,'),
(5599, 76, 8, 'a krmili jídlem přes lásku svou k němu chuďasa, sirotka i zajatce.'),
(5600, 76, 9, '"My krmíme vás z lásky k Bohu a nechceme od vás ani odměny, ani díku velkého,'),
(5601, 76, 10, 'my obáváme se od Pána svého dne mračného, pohrom plného."'),
(5602, 76, 11, 'Avšak Bůh je ochránil přede zlem dne toho a zahrnul je jasem a radostí'),
(5603, 76, 12, 'a odměnil je za to, co vytrpěli, zahradou rajskou a oděvy z hedvábí;'),
(5604, 76, 13, 'tam na poduškách odpočívat budou a nespatří tam ani slunce žár, ani mráz spalující'),
(5605, 76, 14, 'a blízko nad nimi budou stromů stíny a nízko budou se sklánět plody visící'),
(5606, 76, 15, 'a mezi nimi kolovat budou nádoby stříbrné a poháry křišťálové'),
(5607, 76, 16, 'z křišťálu stříbrného, mírou vhodnou naplněné.'),
(5608, 76, 17, 'Napájeni budou z číší, v nichž zázvor je přimíchán,'),
(5609, 76, 18, 'z pramene rajského, jenž Salsabíl je nazýván.'),
(5610, 76, 19, 'Mezi nimi obcházet budou chlapci mládí věčného; při pohledu na ně bys myslil, že jsou to perly rozsypané.'),
(5611, 76, 20, 'A když tam pohlédneš, uzříš slasti rozličné a panství rozlehlé.'),
(5612, 76, 21, 'Oděni budou do šatů z atlasu zeleného a z brokátu a ozdobeni budou náramky stříbrnými a Pán jejich napájet je bude nápojem čirým.'),
(5613, 76, 22, '"Toto vše věru je vám dáno za odměnu a z vděčnosti za vaše úsilí."'),
(5614, 76, 23, 'Zajisté jsme ti seslali Korán sesláním;'),
(5615, 76, 24, 'vytrvej tedy do rozsudku Pána svého a neposlouchej mezi nimi toho, kdo hříšníkem je či nevěřícím!'),
(5616, 76, 25, 'Jména Pána svého za jitra a za večera vzpomínej,'),
(5617, 76, 26, 'i v části noci! A před Ním na zem padej a za noci dlouho Jeho slávu pěj!'),
(5618, 76, 27, 'Ti, kdož milují tento svět pomíjivý, nedbají na den obtížný, jejž mají před sebou.'),
(5619, 76, 28, 'My jsme je stvořili a jejich údy posílili; a kdybychom chtěli, vystřídali bychom vás vám podobnými snadnou výměnou.'),
(5620, 76, 29, 'Toto je věru připomenutí; a kdo si přeje, ať k Pánu svému cestu nastoupí,'),
(5621, 76, 30, 'však přát si toho nebudete, pokud Bůh tak nebude chtíti. A Bůh věru je vševědoucí, moudrý,'),
(5622, 76, 31, 'koho chce, do milosti Své uvádí, zatímco pro nespravedlivé trest připravil bolestný!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Při postupně vysílaných,'),
(5624, 77, 2, 'prudce spěchajících,'),
(5625, 77, 3, 'široce rozevírajících,'),
(5626, 77, 4, 'ostře rozlišujících'),
(5627, 77, 5, 'a připomenutí vrhajících,'),
(5628, 77, 6, 'ať pro omluvu či varování -'),
(5629, 77, 7, 'to, co vám je slíbeno nezvratně se přihodí!'),
(5630, 77, 8, 'Až hvězdy pohasnou,'),
(5631, 77, 9, 'až nebesa se roztrhnou,'),
(5632, 77, 10, 'až hory rozprášeny budou,'),
(5633, 77, 11, 'až čas poslům bude stanoven -'),
(5634, 77, 12, 'ke kterému dni odklad bude udělen?'),
(5635, 77, 13, 'Ke dni rozhodnutí!'),
(5636, 77, 14, 'Víš ty vůbec, co je to den rozhodnutí?'),
(5637, 77, 15, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5638, 77, 16, 'Což jsme již předchozí nezahubili'),
(5639, 77, 17, 'a nedali je následovat jinými?'),
(5640, 77, 18, 'Takto my budeme jednat s hříšnými!'),
(5641, 77, 19, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5642, 77, 20, 'Což jsme vás nestvořili z tekutiny nicotné,'),
(5643, 77, 21, 'již vložili jsme na místo bezpečné'),
(5644, 77, 22, 'až do doby předem určené?'),
(5645, 77, 23, 'Tak určili jsme, a jak výtečnými jsme určovateli!'),
(5646, 77, 24, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5647, 77, 25, 'Což jsme neučinili zem shromaždištěm'),
(5648, 77, 26, 'živých i mrtvých?'),
(5649, 77, 27, 'A na ni jsme pevně zapuštěné a vysoko se pnoucí vztyčili a vodou čerstvou jsme vás napojili.'),
(5650, 77, 28, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5651, 77, 29, 'Jděte si k tomu, co lživým jste nazvali,'),
(5652, 77, 30, 'jděte si ke stínu se třemi větvemi,'),
(5653, 77, 31, 'nepříliš stinnému, jenž nechrání proti plameni,'),
(5654, 77, 32, 'který jiskry jak polena veliká vrhá,'),
(5655, 77, 33, 'žlutým velbloudům podobná!'),
(5656, 77, 34, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5657, 77, 35, 'To bude den, v němž zavržení ani nepromluví,'),
(5658, 77, 36, 'ani dovoleno jim nebude, aby vznesli své omluvy.'),
(5659, 77, 37, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5660, 77, 38, 'To bude den rozhodnutí, a shromáždíme vás i předchozí;'),
(5661, 77, 39, 'a vládnete-li lstí nějakou, pak pokuste se Mne přelstít!'),
(5662, 77, 40, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5663, 77, 41, 'Bohabojní ve stínu a u pramenů budou dlít'),
(5664, 77, 42, 'a ovoce, po němž touží, na dosah ruky mít.'),
(5665, 77, 43, '"Jezte a pijte v bezpečí v odměnu za to, co jste konali!"'),
(5666, 77, 44, 'Takto my věru odměňujeme ty, kdož dobré činili.'),
(5667, 77, 45, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5668, 77, 46, 'A vy, nevěřící, jezte a užívejte si ještě krátce, vždyť vy věru jste hříšníci!'),
(5669, 77, 47, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5670, 77, 48, 'A když je jim řečeno "Na zem padejte!", nepadají.'),
(5671, 77, 49, 'Běda v ten den těm, kdož za lež jej prohlásili!'),
(5672, 77, 50, 'V jaké zvěstování by po tomto ještě mohli uvěřit?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Nač se vzájemně vyptávají?'),
(5674, 78, 2, 'Na zvěst nesmírnou,'),
(5675, 78, 3, 'o níž se mezi sebou přou.'),
(5676, 78, 4, 'Však pozor, záhy poznají,'),
(5677, 78, 5, 'a znovu: však pozor, záhy poznají!'),
(5678, 78, 6, 'Což zemi jsme jak lože nerozprostřeli'),
(5679, 78, 7, 'a hory jejími podpěrami neučinili?'),
(5680, 78, 8, 'A v párech jsme vás stvořili'),
(5681, 78, 9, 'a spánek váš učinili přestávkou'),
(5682, 78, 10, 'a noc pokrývkou'),
(5683, 78, 11, 'a den pro získání obživy jsme vám stanovili'),
(5684, 78, 12, 'a nad vámi sedm pevných postavili'),
(5685, 78, 13, 'a lampu oslňující tam umístili.'),
(5686, 78, 14, 'A z mračen déšť vytlačujících jsme vodu v hojnosti seslali vám,'),
(5687, 78, 15, 'abychom z ní dali vyrůst obilí a rostlinám'),
(5688, 78, 16, 'i hustě zarostlým zahradám.'),
(5689, 78, 17, 'Den rozhodnutí věru přijde v čase určeném;'),
(5690, 78, 18, 'v ten den na pozoun bude zatroubeno a v zástupech dostavíte se'),
(5691, 78, 19, 'a nebe se otevře a branami stane se'),
(5692, 78, 20, 'a hory se do pohybu dají a budou přeludem.'),
(5693, 78, 21, 'A peklo bude věru na číhané,'),
(5694, 78, 22, 'útulek pro vzpurné,'),
(5695, 78, 23, 'a v něm zůstanou věky celé'),
(5696, 78, 24, 'a chládku tam neokusí ani nápoje'),
(5697, 78, 25, 'kromě vody vroucí a hnijící,'),
(5698, 78, 26, 'což odměnou bude odpovídající.'),
(5699, 78, 27, 'Vždyť oni v zúčtování nedoufali'),
(5700, 78, 28, 'a za výmysl Naše znamení lživě prohlašovali,'),
(5701, 78, 29, 'však spočítali jsme věc každou a pořídili záznam.'),
(5702, 78, 30, '"Ochutnejte! Nic než trest dnes nepřidáme vám!"'),
(5703, 78, 31, 'Pro bohabojné však je místo blažené,'),
(5704, 78, 32, 'zahrady a sady révy vinné,'),
(5705, 78, 33, 'dívky s plnými ňadry, věkem s nimi stejné,'),
(5706, 78, 34, 'a poháry po okraj naplněné.'),
(5707, 78, 35, 'Neuslyší tam plané tlachání ani žádné lhaní,'),
(5708, 78, 36, 'odměnou od Pána tvého dar to dobře rozpočtený!'),
(5709, 78, 37, 'Od Pána nebes a země a všeho, co mezi nimi je, Milosrdného, svolené k promluvě nedostanou'),
(5710, 78, 38, 'V den, kdy duch a andělé stát v řadě budou, nehovoříce - kromě těch, jimž Milosrdný to povolí a kteří jen pravdu pronesou.'),
(5711, 78, 39, 'Tento den skutečností je a kdo chce, nechť připraví si k Pánu svému cestu;'),
(5712, 78, 40, 'a věru varování jsme vám již dali o blízkém trestu v den, kdy člověk spatří, co rukama svýma předtím učinil, a kdy nevěřící zvolá: "Ach, kéž bych jen prachem byl!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Při silou vytrhujících,'),
(5714, 79, 2, 'při čile prohánějících,'),
(5715, 79, 3, 'při plavně se nesoucích,'),
(5716, 79, 4, 'při usilovně se předbíhajících,'),
(5717, 79, 5, 'při rozkazy uspořádávajících!'),
(5718, 79, 6, 'V den, kdy rozezní se znějící'),
(5719, 79, 7, 'a přijde po ní následující,'),
(5720, 79, 8, 'v ten den se všechna srdce rozbuší'),
(5721, 79, 9, 'a zraky jejich se hanbou zkruší!'),
(5722, 79, 10, 'Říkají: "Což vskutku budeme vráceni v stav původní,'),
(5723, 79, 11, 'až staneme se kostmi zpráchnivělými?"'),
(5724, 79, 12, 'A připojí: "To věru návrat byl by záhubný!"'),
(5725, 79, 13, 'A ozve se jen zvuk jediný,'),
(5726, 79, 14, 'a hle, octnou se bdící na zemi!'),
(5727, 79, 15, 'Zdaž donesla se ti o Mojžíšovi zpráva,'),
(5728, 79, 16, 'když Pán jeho naň zavolal v údolí posvátném Tuwá:'),
(5729, 79, 17, '"K Faraónovi se vydej, neb on věru zpupným se projevil,'),
(5730, 79, 18, 'a rci mu: "Což se nepatří, aby ses očistil'),
(5731, 79, 19, 'a já abych tě dovedl k Pánu tvému a ty se Ho bál?"'),
(5732, 79, 20, 'A znamení největší mu ukázal,'),
(5733, 79, 21, 'však ten za lež je prohlásil a neposlušný byl,'),
(5734, 79, 22, 'pak zády se otočil a pryč pospíšil'),
(5735, 79, 23, 'a lidi své si shromáždil a pak prohlásil:'),
(5736, 79, 24, '"Já vaším pánem jsem nejvyšším!"'),
(5737, 79, 25, 'A učinil jej Bůh na onom i tomto světě trestem varovným,'),
(5738, 79, 26, 'a pro toho, kdo naplněn je bázní, to věru bude poučením!'),
(5739, 79, 27, 'Jste obtížnější pro stvoření vy, anebo nebe? On je vystavěl,'),
(5740, 79, 28, 'v klenbu je vyzvedl a vhodně vyrovnal,'),
(5741, 79, 29, 'temnotou noc jeho zahalil a jasu dennímu z něho vzejít dal'),
(5742, 79, 30, 'a zemi posléze rozprostřel,'),
(5743, 79, 31, 'a aby voda i pastviny na ní vzešly, způsobil'),
(5744, 79, 32, 'a také hory tam zakotvil,'),
(5745, 79, 33, 'aby užitek v tom pro vás i vaše stáda byl.'),
(5746, 79, 34, 'A až nadejde ten převrat nesmírný'),
(5747, 79, 35, 'v den, kdy člověk si vzpomene na všechno své snažení,'),
(5748, 79, 36, 'tehdy předvedeno bude peklo tomu, kdo uvidí.'),
(5749, 79, 37, 'Ti, kdož vzpurní byli'),
(5750, 79, 38, 'a život pozemský si oblíbili,'),
(5751, 79, 39, 'těm věru peklo za útulek připravili.'),
(5752, 79, 40, 'Však ten, jenž postavení Pána svého se bál a duši svou před vášní spasil,'),
(5753, 79, 41, 'ten věru v zahradě rajské svůj bude mít azyl.'),
(5754, 79, 42, 'Budou se tě na Hodinu, a kdy má nastat, ptát;'),
(5755, 79, 43, 'proč ty máš se vlastně o ní zmiňovat?'),
(5756, 79, 44, 'Vždyť Pánu tvému patří lhůta její konečná!'),
(5757, 79, 45, 'Ty jen varovatelem jsi toho, jenž obavu z ní má.'),
(5758, 79, 46, 'A v den, kdy spatří ji, bude se jim zdát, jako by v hrobech svých víc než večer či jitro nestrávili.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Zakabonil se a odvrátil,'),
(5760, 80, 2, 'že slepec se na něho obrátil.'),
(5761, 80, 3, 'Co můžeš vědět? Snad on chtěl se očistit'),
(5762, 80, 4, 'či připomenutí vyslechnout, a prospěšné mu to mohlo být.'),
(5763, 80, 5, 'Však pokud o boháče jde,'),
(5764, 80, 6, 'o něho ty se zajímáš,'),
(5765, 80, 7, 'a že neočišťuje se, nic nedbáš!'),
(5766, 80, 8, 'O toho však, kdo spěchá k tobě pln snahy'),
(5767, 80, 9, 'a jat z Boha obavami,'),
(5768, 80, 10, 'o toho ty zájem nemáš!'),
(5769, 80, 11, 'Tak ne! Vždyť toto je připomenutí'),
(5770, 80, 12, 'a kdokoliv chce, nechť na ně si vzpomene'),
(5771, 80, 13, 'psané na svitcích úctyhodných,'),
(5772, 80, 14, 'povznesených a očištěných,'),
(5773, 80, 15, 'rukama písařů'),
(5774, 80, 16, 'vznešených, neposkvrněných.'),
(5775, 80, 17, 'Nechť zhyne ten člověk! Ach, jak je nevděčný!'),
(5776, 80, 18, 'Z čeho jej Pán jeho stvořil?'),
(5777, 80, 19, 'Z kapky semene jej stvořil a osud jeho určil'),
(5778, 80, 20, 'a pak cestu mu ulehčil,'),
(5779, 80, 21, 'potom zemřít jej nechá a do hrobu ho vložit dá'),
(5780, 80, 22, 'a posléze, až zachce se mu, z mrtvých vstát mu dá.'),
(5781, 80, 23, 'Však pozor! Člověk ještě nesplnil, co Pán jeho mu poručil!'),
(5782, 80, 24, 'Nechť člověk pohlédne na svou potravu'),
(5783, 80, 25, 'a na to, jak spustili jsme vody záplavu'),
(5784, 80, 26, 'a jak v zemi jsme dali puknout trhlinám'),
(5785, 80, 27, 'a z nich vyrůst obilovinám,'),
(5786, 80, 28, 'vinné révě a travinám,'),
(5787, 80, 29, 'olivovníku a datlovým palmám,'),
(5788, 80, 30, 'hustým zahradám,'),
(5789, 80, 31, 'ovoci i pastvinám'),
(5790, 80, 32, 'v užitek vašim stádům i vám.'),
(5791, 80, 33, 'A až nastane třesk ohlušující,'),
(5792, 80, 34, 'v ten den prchne člověk od bratra svého,'),
(5793, 80, 35, 'matky své i otce svého,'),
(5794, 80, 36, 'družky své i synů svých,'),
(5795, 80, 37, 'neboť každý člověk v ten den bude mít práci sám se sebou jen.'),
(5796, 80, 38, 'V ten den budou tváře zjasněné,'),
(5797, 80, 39, 'rozesmáté a veselé,'),
(5798, 80, 40, 'v ten den budou i tváře jak prach šedivé'),
(5799, 80, 41, 'a chmurou pokryté'),
(5800, 80, 42, '- to věru budou nevěřící, ti hříšníci!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Až slunce bude svinuto,'),
(5802, 81, 2, 'až hvězdy pohasnou,'),
(5803, 81, 3, 'až hory se z místa pohnou,'),
(5804, 81, 4, 'až březí velbloudice bude zanedbána'),
(5805, 81, 5, 'a divoká zvířata shromážděna,'),
(5806, 81, 6, 'až moře se povaří'),
(5807, 81, 7, 'a duše až se spáří,'),
(5808, 81, 8, 'až zaživa pohřbená bude tázána,'),
(5809, 81, 9, 'pro jakou vinu byla utracena,'),
(5810, 81, 10, 'až svitky budou rozvinuty'),
(5811, 81, 11, 'a nebe bude staženo'),
(5812, 81, 12, 'a peklo rozpáleno'),
(5813, 81, 13, 'a zahrada rajská přiblížena,'),
(5814, 81, 14, 'tehdy duše každá, co vykonala, pozná!'),
(5815, 81, 15, 'Hle, přísahám při planetách zpět se vracejících,'),
(5816, 81, 16, 'spěchajících a mizejících,'),
(5817, 81, 17, 'při noci, když v temno se halí,'),
(5818, 81, 18, 'při jitru, když dechu nabírá,'),
(5819, 81, 19, 'že toto věru slova jsou posla vznešeného,'),
(5820, 81, 20, 'silou vládnoucího u Pána trůnu mohutného,'),
(5821, 81, 21, 'poslušnost nalézajícího a spolehlivého!'),
(5822, 81, 22, 'Váš druh není džiny posedlý blázen'),
(5823, 81, 23, 'a vskutku jej viděl na obzoru jasném -'),
(5824, 81, 24, 'a není skoupý o nepoznatelném!'),
(5825, 81, 25, 'A nejsou to slova satana prokletého!'),
(5826, 81, 26, 'Kam tedy odcházíte?'),
(5827, 81, 27, 'Vždyť toto slouží jen lidstvu k připomenutí,'),
(5828, 81, 28, 'těm z vás, kdo přejí si po stezce přímé jíti.'),
(5829, 81, 29, 'Však přát si toho nebudete, pokud Bůh, Pán lidstva veškerého, nebude chtíti!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Až se nebe rozpukne,'),
(5831, 82, 2, 'až se hvězdy rozptýlí,'),
(5832, 82, 3, 'až se moře rozlijí,'),
(5833, 82, 4, 'až se hroby převrátí,'),
(5834, 82, 5, 'pak pozná duše každá, co učinila zavčas a co zmeškala.'),
(5835, 82, 6, 'Člověče, co zmýlilo tě o laskavém Pánu tvém,'),
(5836, 82, 7, 'jenž stvořil tě, vyrovnal a souměrnost ti dal'),
(5837, 82, 8, 'a v oné podobě tě složil, již si přál?'),
(5838, 82, 9, 'Pozor však, vždyť vy nazýváte den soudný výmyslem!'),
(5839, 82, 10, 'Nad vámi věru strážcové dozírají,'),
(5840, 82, 11, 'zapisovatelé vznešení,'),
(5841, 82, 12, 'kteří o činech vašich dobře vědí!'),
(5842, 82, 13, 'Čistí zajisté ve slastech budou,'),
(5843, 82, 14, 'zatímco hříšníci do ohně půjdou,'),
(5844, 82, 15, 'v němž budou hořet v den soudný,'),
(5845, 82, 16, 'a z něho vzdálit se moci nebudou.'),
(5846, 82, 17, 'Víš ty vůbec, co je to soudný den?'),
(5847, 82, 18, 'A znovu: víš ty vůbec, co je to soudný den?'),
(5848, 82, 19, 'Den, kdy žádná duše nezmůže nic pro duši jinou a kdy rozhodnutí bude patřit Bohu jen!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Běda těm, kdo míru zkracují,'),
(5850, 83, 2, 'kteří když jim je měřeno plnou míru od lidí žádají,'),
(5851, 83, 3, 'však když pro druhé měří či váží, ztrátu jim působí!'),
(5852, 83, 4, 'Což si myslí, že nebudou vzkříšeni'),
(5853, 83, 5, 'pro den nesmírný,'),
(5854, 83, 6, 'pro den, kdy lidé před Pána vesmíru předstoupí?'),
(5855, 83, 7, 'Však pozor, vždyť věru kniha hříšníků je v Sidždžínu!'),
(5856, 83, 8, 'Víš ty vůbec, co je to Sidždžín?'),
(5857, 83, 9, 'Kniha označkovaná!'),
(5858, 83, 10, 'Běda v ten den těm, kdož za lež prohlašují'),
(5859, 83, 11, 'a za výmysl den soudný pokládají!'),
(5860, 83, 12, 'Nepokládá jej za výmysl nikdo kromě všech protivníků hříšných,'),
(5861, 83, 13, 'kteří - když jsou jim přednášena Naše znamení - říkají: "To jsou povídačky starých!"'),
(5862, 83, 14, 'Však pozor! Naopak srdce jejich zrezivěla od toho, co si vysloužila.'),
(5863, 83, 15, 'Však pozor! Věru že budou v ten den od svého Pána odděleni závěsem'),
(5864, 83, 16, 'a potom budou hořet v ohni pekelném'),
(5865, 83, 17, 'a posléze jim bude řečeno: "Hle, zde máte, co nazývali jste výmyslem!"'),
(5866, 83, 18, 'Však pozor! Vždyť věru kniha zbožných je v ´Illijún!'),
(5867, 83, 19, 'Víš ty vůbec, co je to ´Illijún?'),
(5868, 83, 20, 'Kniha označkovaná,'),
(5869, 83, 21, 'na vlastní oči ji spatří ten, kdo k Pánu bude přiblížen.'),
(5870, 83, 22, 'A zbožní vskutku budou ve slastech,'),
(5871, 83, 23, 'na pohovkách ležíce se budou rozhlížet.'),
(5872, 83, 24, 'Na jejich tvářích uzříš svěžest slastí,'),
(5873, 83, 25, 'napájeni budou vínem silným, zapečetěným'),
(5874, 83, 26, 'z pižma pečetí - o to nechť soutěží ti, kdož soutěží -'),
(5875, 83, 27, 's vodou Tasnímu smíseným,'),
(5876, 83, 28, 'pramene, z něhož pijí jen přiblížení.'),
(5877, 83, 29, 'Věru že se hříšníci posmívali těm, kdož uvěřili,'),
(5878, 83, 30, 'a když je míjeli; vzájemně na sebe pomrkávali,'),
(5879, 83, 31, 'a když se ke svým rodinám vrátili, smích si tropili,'),
(5880, 83, 32, 'a když je viděli, volali: "Věru jsou to zbloudilí!",'),
(5881, 83, 33, 'A přec nebyli posláni k věřícím, aby je střežili.'),
(5882, 83, 34, 'Dnes však se nevěřícím posmívají ti, kdož věřili,'),
(5883, 83, 35, 'a na pohovkách ležíce se rozhlížejí.'),
(5884, 83, 36, 'Dostalo se nevěřícím odměny za to, co činili?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Až se nebe rozštěpí'),
(5886, 84, 2, 'a uposlechne Pána svého a své povinnosti zadost učiní,'),
(5887, 84, 3, 'až se země zarovná'),
(5888, 84, 4, 'a vyvrhne, co v ní je, a bude prázdná'),
(5889, 84, 5, 'a uposlechne Pána svého a své povinnosti zadost učiní'),
(5890, 84, 6, 'Člověče, věru se usilovně snažíš o Pána svého a vskutku s Ním dojde k setkání'),
(5891, 84, 7, 'Komu bude kniha jeho dána do pravice,'),
(5892, 84, 8, 'tomu zúčtováno bude počtem snadným'),
(5893, 84, 9, 'a on radostně se navrátí k lidem svým.'),
(5894, 84, 10, 'A ten, jemuž za záda bude kniha jeho dána,'),
(5895, 84, 11, 'ten volat bude po zničení'),
(5896, 84, 12, 'a plát bude ve šlehajícím plameni.'),
(5897, 84, 13, 'Žil věru s rodinou svou v radosti na zemi,'),
(5898, 84, 14, 'a že k Bohu se nenavrátí, se domníval.'),
(5899, 84, 15, 'Ba nikoliv! Vždyť Pán jeho jej pozoroval!'),
(5900, 84, 16, 'Hle, přísahám při soumraku,'),
(5901, 84, 17, 'při noci a při tom, co zahaluje,'),
(5902, 84, 18, 'a při měsíci, když vchází do úplňku,'),
(5903, 84, 19, 'že vskutku stoupat budete stupeň po stupínku!'),
(5904, 84, 20, 'Co je jim tedy, že nevěří'),
(5905, 84, 21, 'a k zemi se nevrhají, když Korán jim přednášejí?'),
(5906, 84, 22, 'Ba co víc, ti, kdož nevěří, za lživý jej prohlašují.'),
(5907, 84, 23, 'Však Bůh nejlépe ví, co skrývají!'),
(5908, 84, 24, 'Ohlas jim zvěst radostnou o trestu bolestném, jenž stihne je -'),
(5909, 84, 25, 'kromě těch, kdož uvěřili a zbožné skutky konali, a těm náleží odměna, jež se neupomíná.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Při nebi souhvězdí plném,'),
(5911, 85, 2, 'při dni slíbeném,'),
(5912, 85, 3, 'při svědkovi a dosvědčeném!'),
(5913, 85, 4, 'Byli zabiti lidé příkopu,'),
(5914, 85, 5, 'ohně stále živeného palivem!'),
(5915, 85, 6, 'Zatímco okolo seděli,'),
(5916, 85, 7, 'jsouce svědky toho, co s věřícími dělali,'),
(5917, 85, 8, 'a pouze za to se jim mstili, že v Boha mocného, chváleného uvěřili,'),
(5918, 85, 9, 'jenž nad nebesy a zemí kraluje - a Bůh svědkem každé věci je!'),
(5919, 85, 10, 'Věru těm, kdož věřící muže a ženy pokoušeli a potom pokání nekonali, těm muka pekelná i trest spalující připravili,'),
(5920, 85, 11, 'však pro ty, kdož uvěřili a zbožné skutky konali, pro ty jsou zahrady, pod nimiž řeky tekou, určeny - a to věru úspěch bude nesmírný.'),
(5921, 85, 12, 'Úder Pána tvého vskutku bude mohutný,'),
(5922, 85, 13, 'On je ten, jenž vzniknout všemu dává a návrat působí,'),
(5923, 85, 14, 'On odpouštějící je, láskyplný,'),
(5924, 85, 15, 'Pán trůnu slavený,'),
(5925, 85, 16, 'uskutečňovatel toho, co jemu se zlíbí.'),
(5926, 85, 17, 'Doneslo se ti vyprávění o vojscích,'),
(5927, 85, 18, 'o Faraónovi a Thamúdovcích?'),
(5928, 85, 19, 'Však přesto ti, kdož nevěří, za lež to prohlašují stále,'),
(5929, 85, 20, 'zatímco Bůh za jejich zády je obkličuje!'),
(5930, 85, 21, 'Vždyť toto je Korán chvályhodný,'),
(5931, 85, 22, 'na desce dobře střežené napsaný!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Při nebi a hvězdě nocí putující!'),
(5933, 86, 2, 'Víš ty vůbec, co je to hvězda nocí putující?'),
(5934, 86, 3, 'Hvězda pronikavě zářící.'),
(5935, 86, 4, 'A věru duše každá nad sebou strážce má!'),
(5936, 86, 5, 'Z čeho byl stvořen, nechť člověk posoudí!'),
(5937, 86, 6, 'Stvořen byl z vystříknuté tekutiny,'),
(5938, 86, 7, 'jež z místa mezi bedry a žebry proudí.'),
(5939, 86, 8, 'Bůh zajisté je schopen k životu jej navrátit'),
(5940, 86, 9, 'v den, kdy taje budou zkoumány'),
(5941, 86, 10, 'a kdy člověk nebude ani sílu, ani pomocníka mít.'),
(5942, 86, 11, 'Při nebi návratů plném,'),
(5943, 86, 12, 'při zemi rozpuklin plné!'),
(5944, 86, 13, 'Věru že toto je slovo rozhodné'),
(5945, 86, 14, 'a není řečeno jen žertem.'),
(5946, 86, 15, 'Tito lidé věru strojí úklad,'),
(5947, 86, 16, 'však já rovněž chystám lest!'),
(5948, 86, 17, 'Jen poshov nevěřícím a krátký jim popřej odklad!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Oslavuj jméno Pána svého nejvyššího,'),
(5950, 87, 2, 'jenž vše stvořil a vyrovnal,'),
(5951, 87, 3, 'každý osud předurčil a usměrnil,'),
(5952, 87, 4, 'jenž pastviny k životu vyvolal'),
(5953, 87, 5, 'a potom je v pěnu zčernalou přeměnil!'),
(5954, 87, 6, 'Naučíme tě přednášet a nezapomeneš nic z věci té,'),
(5955, 87, 7, 'leda co Bůh bude chtít - a On zná zjevné i skryté -'),
(5956, 87, 8, 'a ulehčíme ti přístup k věci snadné!'),
(5957, 87, 9, 'Připomínej, vždyť připomenutí je užitečné!'),
(5958, 87, 10, 'A kdo Boha se bojí, ten na ně si vzpomene,'),
(5959, 87, 11, 'však odvrátí se od něho jen bídník nejhorší,'),
(5960, 87, 12, 'jehož spalovat bude oheň největší,'),
(5961, 87, 13, 'v něm potom nezemře ani nebude žít.'),
(5962, 87, 14, 'Prospívat naopak bude ten, kdo je očištěn'),
(5963, 87, 15, 'a jméno Pána svého vzpomíná a modlitbu koná.'),
(5964, 87, 16, 'Vy však přednost životu pozemskému dáváte,'),
(5965, 87, 17, 'ač život budoucí lepší a trvalejší máte.'),
(5966, 87, 18, 'Toto vskutku je psáno ve svitcích dřívějších,'),
(5967, 87, 19, 'svitcích Abrahamových a Mojžíšových.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Dostalo se ti zvěsti o hodině zahalující?'),
(5969, 88, 2, 'V ten den budou tváře jedněch zahanbené,'),
(5970, 88, 3, 'ustarané a ztrhané,'),
(5971, 88, 4, 'a hořet budou v ohni planoucím'),
(5972, 88, 5, 'napájeny pramenem vroucím;'),
(5973, 88, 6, 'jen bodláčí trnité tam za stravu budou mít,'),
(5974, 88, 7, 'z něhož se netloustne a jímž hlad nelze utišit.'),
(5975, 88, 8, 'A v ten den budou tváře jiných blažené,'),
(5976, 88, 9, 'úsilím svým potěšené,'),
(5977, 88, 10, 'v zahradě vyvýšené,'),
(5978, 88, 11, 'kde neuslyší tlachání plané;'),
(5979, 88, 12, 'v ní prameny jsou proudící'),
(5980, 88, 13, 'a pohovky zvýšené'),
(5981, 88, 14, 'a poháry rozestavené'),
(5982, 88, 15, 'a podušky v řadách vyrovnané'),
(5983, 88, 16, 'a drahé koberce rozprostřené.'),
(5984, 88, 17, 'Což neuvažují o tom, jak stvořeni byli velbloudi,'),
(5985, 88, 18, 'jak nebesa byla zdvižena,'),
(5986, 88, 19, 'jak pohoří byla vztyčena,'),
(5987, 88, 20, 'jak země byla rozprostřena?'),
(5988, 88, 21, 'Připomínej, vždyť tys pouze ten, jenž připomíná,'),
(5989, 88, 22, 'a nejsi nad nimi ten, jenž zaznamenává,'),
(5990, 88, 23, 'leda toho, kdo odvrací se a nevěří,'),
(5991, 88, 24, 'a toho Bůh trestem nejtěžším udeří!'),
(5992, 88, 25, 'K Nám věru míří jejich návrat'),
(5993, 88, 26, 'a Nám pak budou účty své skládat!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Při úsvitu'),
(5995, 89, 2, 'a při nocích deseti'),
(5996, 89, 3, 'a při spárovaném a jedinečném'),
(5997, 89, 4, 'a při noci, když ubíhá!'),
(5998, 89, 5, 'Což není v tom pro člověka rozvážného přísaha?'),
(5999, 89, 6, 'Neviděls, co učinil Pán tvůj ´Ádovcům'),
(6000, 89, 7, 'Iramu, jenž proslul sloupy svými,'),
(6001, 89, 8, 'jimž nic podobného nebylo stvořeno v žádné zemi,'),
(6002, 89, 9, 'a kmeni Thamúd, jenž skály v údolí prorážel,'),
(6003, 89, 10, 'a Faraónovi, který na kůly narážel,'),
(6004, 89, 11, 'a těm, kdož v zemích vzpurní byli'),
(6005, 89, 12, 'a pohoršení v nich pak šířili?'),
(6006, 89, 13, 'Však Pán tvůj proti nim důtky trestající rozpoutal,'),
(6007, 89, 14, 'neb Pán tvůj neustále na číhané stál.'),
(6008, 89, 15, 'Co člověka se týče, když Pán jeho jej zkoušce podrobuje a štědrosti a dobrodiním jeho zahrnuje, pak on hovoří: "Pán můj mi poctu prokazuje.'),
(6009, 89, 16, 'Když naopak jej zkouší tím, že příděl obživy mu odměřuje, pak on hovoří: "Pán můj mě ponižuje."'),
(6010, 89, 17, 'Však pozor! Vždyť vy sirotky nectíte,'),
(6011, 89, 18, 'ani k nakrmení nuzných se navzájem nevybízíte'),
(6012, 89, 19, 'však dědictví slabých nenasytně pohlcujete'),
(6013, 89, 20, 'a jen bohatství v lásce vášnivé máte.'),
(6014, 89, 21, 'Však pozor! Až země bude rozdrcena ranou za ranou'),
(6015, 89, 22, 'a přijde Pán tvůj i andělé, řada za řadou,'),
(6016, 89, 23, 'a až v ten den bude peklo předvedeno, tehdy si člověk připomene - však co bude mu připomínání platno?'),
(6017, 89, 24, 'I zvolá: "Kéž bych měl na život budoucí něco předem připraveno!"'),
(6018, 89, 25, 'V ten den takovým trestem nebude nikdo potrestán'),
(6019, 89, 26, 'a nikdo takovými okovy spoután!'),
(6020, 89, 27, 'A ty, duše usmířená,'),
(6021, 89, 28, 'vrať se k Pánu svému v zalíbení a spokojená!'),
(6022, 89, 29, 'Vstup mezi služebníky mé'),
(6023, 89, 30, 'a vejdi do zahrady mé!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Hle, přísahám při tomto městě'),
(6025, 90, 2, '- ty volně si žiješ v tomto městě -'),
(6026, 90, 3, 'při otci a tom, co zplodil,'),
(6027, 90, 4, 'že člověka jsme věru ke strastem stvořili!'),
(6028, 90, 5, 'Počítá snad, že nikdo proti němu nic nezmůže,'),
(6029, 90, 6, 'když říká: "Bohatství obrovské jsem strávil."'),
(6030, 90, 7, 'Domnívá se snad, že nikdo jej nespatřil?'),
(6031, 90, 8, 'Což dvě oči jsme mu nedali'),
(6032, 90, 9, 'a jazyk a dva rty'),
(6033, 90, 10, 'a na dvě stezky jsme jej neuvedli?'),
(6034, 90, 11, 'A přece se nepustil po stezce vzhůru vedoucí.'),
(6035, 90, 12, 'Víš ty vůbec, co je to stezka vzhůru vedoucí?'),
(6036, 90, 13, 'Otroka propuštění'),
(6037, 90, 14, 'či v den hladu nakrmení'),
(6038, 90, 15, 'sirotka příbuzného'),
(6039, 90, 16, 'či chudáka nouzí trpícího,'),
(6040, 90, 17, 'a také být z těch, kdož uvěřili a k neochvějnosti a soucitu se vzájemně nabádají:'),
(6041, 90, 18, 'to budou lidé po pravici stojící,'),
(6042, 90, 19, 'zatímco ti, co ve znamení naše nevěřili, to budou lidé po levici stojící'),
(6043, 90, 20, 'a nad nimi plát bude oheň uzavírající!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Při slunci a jeho jasu,'),
(6045, 91, 2, 'při měsíci, když je následuje,'),
(6046, 91, 3, 'při dni, když je odhaluje,'),
(6047, 91, 4, 'při noci, když je zahaluje,'),
(6048, 91, 5, 'při nebi a při tom, kdo je vystavěl,'),
(6049, 91, 6, 'při zemi a při tom, kdo ji rozprostřel,'),
(6050, 91, 7, 'při duši a při tom, kdo dal jí vyrovnanost'),
(6051, 91, 8, 'a vdechl jí hříšnost i bohabojnost!'),
(6052, 91, 9, 'Kdo ji očistí, ten jistě vydělá,'),
(6053, 91, 10, 'a kdo ji zanedbá, ten jistě prodělá.'),
(6054, 91, 11, 'Ve vzpurnosti své obvinili Thamúdovci (proroka svého) ze lži,'),
(6055, 91, 12, 'když bezbožník největší povstal mezi nimi.'),
(6056, 91, 13, 'A pravil jim posel Boží: "(Nedotýkejte se) velbloudice Boží a nechte ji napít!"'),
(6057, 91, 14, 'Však za lháře jej prohlásili a (velbloudici) podřízli. Pán jejich je pak za jejich hříchy zahubil a se zemí je srovnal,'),
(6058, 91, 15, 'aniž se následků toho obával.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Při noci, když temnem zahaluje,'),
(6060, 92, 2, 'při dni, když v slávě se objevuje,'),
(6061, 92, 3, 'při tom, jenž stvořil mužské i ženské pohlaví,'),
(6062, 92, 4, 'rozdílné je věru vaše snažení!'),
(6063, 92, 5, 'Co týče se toho, jenž rozdával a Boha se bál'),
(6064, 92, 6, 'a za pravdivou nejkrásnější odměnu pokládal,'),
(6065, 92, 7, 'tomu usnadníme přístup ke snadnému.'),
(6066, 92, 8, 'Však co se týče toho, kdo lakotil a za soběstačného se vydával'),
(6067, 92, 9, 'a nejkrásnější odměnu za lež pokládal,'),
(6068, 92, 10, 'tomu ulehčíme přístup do nesnází,'),
(6069, 92, 11, 'a co pomůže mu jmění jeho, až dořítí se do své zkázy?'),
(6070, 92, 12, 'Nám věru přísluší správné vedení,'),
(6071, 92, 13, 'Vždyť Nám patří život první i poslední!'),
(6072, 92, 14, 'Já varuji vás před ohněm jasně šlehajícím,'),
(6073, 92, 15, 'v němž hořet bude jen ten nejbídnější,'),
(6074, 92, 16, 'jenž za lež to pokládal a zády se obracel.'),
(6075, 92, 17, 'Však vzdálen bude od něho bohabojný,'),
(6076, 92, 18, 'jenž ze jmění svého dával, aby čistým se stal,'),
(6077, 92, 19, 'a dobrodiní neprokazoval jen proto, aby byl odměněn,'),
(6078, 92, 20, 'leč jen z touhy po tváři Pána svého nejvyššího to konal.'),
(6079, 92, 21, 'A ten věru bude spokojen.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Při denním jasu'),
(6081, 93, 2, 'a při noci, když je tichá,'),
(6082, 93, 3, 'Pán tvůj tě neopustil ani tebou nepohrdá!'),
(6083, 93, 4, 'A bude pro tebe poslední věru lepší než první'),
(6084, 93, 5, 'a Pán tvůj tě zajisté obdaří k tvému uspokojení.'),
(6085, 93, 6, 'Což nenašel tě jako sirotka - a v útulek tě nepřijal?'),
(6086, 93, 7, 'Bloudícím tě nalezl - a správnou cestu ti ukázal,'),
(6087, 93, 8, 'nuzákem tě nalezl - a bohatství ti věru dal!'),
(6088, 93, 9, 'Co sirotka se týče, toho neutiskuj,'),
(6089, 93, 10, 'co žebráka se týče, toho neodstrkuj,'),
(6090, 93, 11, 'však o dobrodiní Pána svého jiným vypravuj!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Což jsme ti hruď tvou neotevřeli'),
(6092, 94, 2, 'a nesňali s tebe tvé břemeno,'),
(6093, 94, 3, 'jež záda tvá tak tížilo,'),
(6094, 94, 4, 'a pověst tvou jsme do výše nezvedli?'),
(6095, 94, 5, 'Vždyť se strastmi i úleva přichází,'),
(6096, 94, 6, 'věru že se strastmi i úleva přichází!'),
(6097, 94, 7, 'A až se uvolníš, pak dál se namáhej'),
(6098, 94, 8, 'a k Pánu svému v touze spěj!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Při hoře Fíkovníku a hoře Stromu olivového,'),
(6100, 95, 2, 'při hoře Sinaji,'),
(6101, 95, 3, 'při tomto bezpečném území!'),
(6102, 95, 4, 'Stvořili jsme věru člověka v postavě nejkrásnější'),
(6103, 95, 5, 'a pak jsme jej svrhli tam, kde z bídných jsou nejbídnější,'),
(6104, 95, 6, 'kromě těch, kdož uvěřili a zbožné skutky konali - a jim náleží odměna, jež se neupomíná.'),
(6105, 95, 7, 'Co potom dovoluje, aby tě prohlašovali za toho, kdo lže o soudu posledním?'),
(6106, 95, 8, 'Což Bůh není ze soudců všech nejmoudřejší?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Přednášej ve jménu Pána svého, který stvořil,'),
(6108, 96, 2, 'člověka z kapky přilnavé stvořil!'),
(6109, 96, 3, 'Přednášej, vždyť Pán tvůj je nadmíru štědrý,'),
(6110, 96, 4, 'ten, jenž naučil perem,'),
(6111, 96, 5, 'naučil člověka, co ještě neznal.'),
(6112, 96, 6, 'Však pozor, člověk vzpurným se stává,'),
(6113, 96, 7, 'neb za soběstačného se pokládá,'),
(6114, 96, 8, 'však věru k Pánu tvému (vše) svůj návrat má!'),
(6115, 96, 9, 'Co soudíš o tom, jenž zabraňuje'),
(6116, 96, 10, 'služebníkovi, když se modlí?'),
(6117, 96, 11, 'Soudíš, že na správné cestě dlí'),
(6118, 96, 12, 'či bohabojnost přikazuje?'),
(6119, 96, 13, 'Co soudíš o něm, jestliže odvrací se a ze lži obviňuje?'),
(6120, 96, 14, 'Což neví, že Bůh (jej) vskutku pozoruje?'),
(6121, 96, 15, 'Však pozor, nepřestane-li, tedy jej věru chytíme za jeho kštici,'),
(6122, 96, 16, 'tu kštici prolhanou a hříšnou!'),
(6123, 96, 17, 'Pak ať svolává si svůj klan,'),
(6124, 96, 18, 'my přivoláme si stráž pekelnou!'),
(6125, 96, 19, 'Však pozor, neposlouchej ho, leč padni na tvář svou a (k Bohu) se přibliž!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Vskutku jsme jej seslali v noci Úradku.'),
(6127, 97, 2, 'Víš ty vůbec, co je to noc Úradku?'),
(6128, 97, 3, 'Noc Úradku než tisíc měsíců je lepší,'),
(6129, 97, 4, 'v ní podle Pána svého dovolení andělé a duch sestupují kvůli každému zjevení.'),
(6130, 97, 5, 'A až do východu jitřních červánků je mír v ní.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Ti, kdož z vlastníků Písma a z modloslužebníků jsou nevěřícími, se nezačali uvolňovat, pokud k nim nepřišel důkaz jasný,'),
(6132, 98, 2, 'posel od Boha, jenž přednášel jim listy očištěné,'),
(6133, 98, 3, 'obsahující Písmo nezměnitelné.'),
(6134, 98, 4, 'A nerozdělili se ti, jimž bylo dáno Písmo, leč až poté, co k nim přišel důkaz jasný.'),
(6135, 98, 5, 'A přitom jim bylo pouze poručeno, aby uctívali Boha, zasvěcujíce mu upřímně svou víru jako hanífové, aby dodržovali modlitbu a dávali almužnu - a toto je náboženství neměnné.'),
(6136, 98, 6, 'A věru ti, kdož jsou nevěřící z vlastníků Písma a z modloslužebníků, přijdou do ohně pekelného a budou v něm nesmrtelní, a to jsou ti nejhorší ze všech stvoření!'),
(6137, 98, 7, 'Avšak ti, kdož uvěřili a zbožné skutky konali, to jsou ti nejlepší ze všech stvoření.'),
(6138, 98, 8, 'Odměnou jejich u Pána jejich budou zahrady Edenu, pod nimiž řeky tekou a v nichž zůstanou navěky nesmrtelní. Bůh v nich nalezne zalíbení a i oni v Něm naleznou zalíbeni, a toto údělem bude toho, kdo Pána svého se bojí.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Až se země bude otřásat svým zemětřesením,'),
(6140, 99, 2, 'až země svá břemena vyvrhne,'),
(6141, 99, 3, 'člověk se zeptá: "Co se s ní děje?"'),
(6142, 99, 4, 'V ten den bude země vyprávět své děje'),
(6143, 99, 5, 'podle toho, jak jí to Pán tvůj vnukne.'),
(6144, 99, 6, 'V ten den se lidé vynoří v houfech, aby jim byly ukázány jejich činy.'),
(6145, 99, 7, 'A kdo učinil dobra jen za váhu prášku, uzří je,'),
(6146, 99, 8, 'a kdo učinil zla jen za váhu prášku, uzří je.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Při pádících, odfrkujících,'),
(6148, 100, 2, 'jiskry vykřesávajících,'),
(6149, 100, 3, 'za jitra se vynořujících,'),
(6150, 100, 4, 'oblaka prachu v něm zviřujících'),
(6151, 100, 5, 'a do středu tlupy vtrhujících!'),
(6152, 100, 6, 'Vůči svému Pánu člověk vskutku nevděčný je,'),
(6153, 100, 7, 'a dokonce sám to dosvědčuje.'),
(6154, 100, 8, 'Horlivý však je věru v lásce k majetku!'),
(6155, 100, 9, 'Cožpak neví, že až bude to, co je v hrobech, převráceno'),
(6156, 100, 10, 'a to, co v hrudích je, odhaleno,'),
(6157, 100, 11, 'Pán jejich v ten den dokonale o nich bude zpraven?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Roztříštění!'),
(6159, 101, 2, 'Co je to roztříštění?'),
(6160, 101, 3, 'Víš ty vůbec, co je to roztříštěni?'),
(6161, 101, 4, 'V ten den budou lidé jak můry rozptýlené'),
(6162, 101, 5, 'a hory podobat se budou rozcupované vlně.'),
(6163, 101, 6, 'Tehdy ten, jehož skutků váha bude těžká,'),
(6164, 101, 7, 'se octne v blaženém žití,'),
(6165, 101, 8, 'zatímco ten, jehož skutků váha bude lehká -'),
(6166, 101, 9, 'Propast bude jeho mátí.'),
(6167, 101, 10, 'Víš ty vůbec, co je to Propast?'),
(6168, 101, 11, 'Oheň planoucí!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Vy rádi v rozmnožování jmění soupeříte,'),
(6170, 102, 2, 'pokud hroby nenavštívíte.'),
(6171, 102, 3, 'Však pozor! Brzy poznáte!'),
(6172, 102, 4, 'A znovu pozor! Brzy poznáte!'),
(6173, 102, 5, 'Však pozor! Kéž zřetelným poznáním poznáte,'),
(6174, 102, 6, 'a věru oheň planoucí uzříte'),
(6175, 102, 7, 'a potom vskutku okem jistým jej spatříte'),
(6176, 102, 8, 'a posléze v ten den na blahobyt tázáni budete!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Při osudu!'),
(6178, 103, 2, 'Věru že člověk spěje v záhubu!'),
(6179, 103, 3, 'Kromě těch, kdož uvěřili a zbožné skutky konali a vzájemně se k pravdě a neochvějnosti nabádali.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Běda každému pomlouvači, jenž na cti utrhá,'),
(6181, 104, 2, 'který bohatství hromadí a stále je počítá,'),
(6182, 104, 3, 'a že jmění jeho jej učiní nesmrtelným, se domnívá!'),
(6183, 104, 4, 'Pozor však, vždyť věru bude uvržen do Drtící!'),
(6184, 104, 5, 'Víš ty vůbec, co je to Drtící?'),
(6185, 104, 6, 'Boží oheň zažehnutý,'),
(6186, 104, 7, 'až k srdcím šlehající'),
(6187, 104, 8, 'a nad nimi se v klenbu pojící'),
(6188, 104, 9, 'a jak sloupy protažené stojící.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Neviděls, jak naložil Pán tvůj s lidmi slona?'),
(6190, 105, 2, 'Což neobrátil úklad jejich ve zmatky'),
(6191, 105, 3, 'a neposlal na ně v hejnech ptáky,'),
(6192, 105, 4, 'kteří po nich vrhali oblázky z hlíny pálené,'),
(6193, 105, 5, 'a učinil, že podobnými byli slámě ohryzané?');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Pro soudržnost Kurajšovců,'),
(6195, 106, 2, 'pro soudržnost jejich, když připravuji zimní a letní karavanu,'),
(6196, 106, 3, 'nechť slouží chrámu tohoto Pánu,'),
(6197, 106, 4, 'jenž nasytil je a ochránil před hladem');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Co soudíš o tom, jenž nazývá náboženství lží?'),
(6199, 107, 2, 'To je ten, jenž sirotka odhání'),
(6200, 107, 3, 'a k nakrmení chudáka nepobízí.'),
(6201, 107, 4, 'Běda těm, kdož při modlení'),
(6202, 107, 5, 'modlitbu svou se nestarají,'),
(6203, 107, 6, 'kteří chtějí být jen viděni'),
(6204, 107, 7, 'a dávat podporu odmítají.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Hojnosti, dali jsme ti věru plnou míru,'),
(6206, 108, 2, 'modli se proto k svému Pánu a přinášej mu obětinu!'),
(6207, 108, 3, 'Vždyť ten, jenž nenávidí tě, bez mužských potomků je věru!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Rci: "Nevěřící!'),
(6209, 109, 2, 'Já nebudu uctívat to, co vy uctíváte,'),
(6210, 109, 3, 'a vy nejste ctitelé toho, co já uctívám,'),
(6211, 109, 4, 'a já nejsem ctitelem toho, co vy jste uctívali,'),
(6212, 109, 5, 'a vy neuctíváte to, co já uctívám.'),
(6213, 109, 6, 'Vy máte své náboženství a já své náboženství mám."');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Až vítězná pomoc Boží přijde, až spěch se dostaví'),
(6215, 110, 2, 'a uvidíš lidi vstupovat houfně v náboženství Boží,'),
(6216, 110, 3, 'pak oslavuj chválou Pána svého a pros jej o odpuštění, vždyť On věru ke kajícníku je milostivý!');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Zhyňte obě ruce Abú Lahaba, a zhynul již i on!'),
(6218, 111, 2, 'A nebylo mu k ničemu jmění jeho ani to, co si vysloužil,'),
(6219, 111, 3, 'však hořet bude v ohni plném plamenů on'),
(6220, 111, 4, 'i žena jeho, jež dříví palivové nosí'),
(6221, 111, 5, 'a jíž kol hrdla provaz z vláken palmových visí.');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Rci: "On Bůh je jedinečný,'),
(6223, 112, 2, 'Bůh sám o sobě věčný.'),
(6224, 112, 3, 'Neplodil a nebyl zplozen'),
(6225, 112, 4, 'a není nikoho, kdo je mu roven."');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Rci: "Utíkám se v ochranu Pána záře jitřní'),
(6227, 113, 2, 'před zlem Jeho stvoření,'),
(6228, 113, 3, 'před zlem temnoty, když se šíří,'),
(6229, 113, 4, 'před zlem žen, jež do uzlů prskají,'),
(6230, 113, 5, 'před zlem závistníka, když závidí!"');
INSERT INTO `cs_hrbek` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Rci: "Utíkám se v ochranu Pána lidí,'),
(6232, 114, 2, 'vládce lidí,'),
(6233, 114, 3, 'Boha lidí,'),
(6234, 114, 4, 'před zlem našeptavače pokradmého,'),
(6235, 114, 5, 'jenž našeptává do hrudi lidí,'),
(6236, 114, 6, 'ať již je z džinů či z lidí!"');
