INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Ve jménu Boha, Milosrdného, Slitovného.'),
(2, 1, 2, 'Chvála Bohu, Pánu světů,'),
(3, 1, 3, 'milosrdnému, slitovnému,'),
(4, 1, 4, 'vládci dne soudného.'),
(5, 1, 5, 'Tebe vzýváme a k Tobě se utíkáme.'),
(6, 1, 6, 'Veď nás stezkou přímou,'),
(7, 1, 7, 'stezkou oněch, které jsi zahrnul milostí svou: nikoliv těch, na které rozhněván jsi a těch, kteří bloudí.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Alif, Lám, Mím.'),
(9, 2, 2, 'Tato Kniha — o níž není pochyby — jest vedením pro bohabojné,'),
(10, 2, 3, 'již věří ve věci neviditelné a zachovávají modlitbu a štědře rozdávají z toho, čímž jsme je obdařili:'),
(11, 2, 4, 'a pro ty, kteří věří v to, cožkoliv zjeveno bylo tobě a cožkoliv zjeveno bylo před tebou a pevně doufají v život posmrtný:'),
(12, 2, 5, 'tito zajisté vedeni jsou Pánem svým a jim zajisté dobře se povede.'),
(13, 2, 6, 'Nevěřícím pak jedno jest, zda napomínáš je, či ne: neuvěří!'),
(14, 2, 7, 'Zapečetil Bůh srdce a uši jejich a clonou zastřeny jsou zrakové jejich a veliká muka budou jim trestem.'),
(15, 2, 8, 'A jsou někteří mezi lidem; již dí: „Uvěřili jsme v Boha a v den poslední“ a přece nejsou věřícími.'),
(16, 2, 9, 'Klamati chtějí Boha a ty, kteří uvěřili, však ve skutečnosti klamou pouze sebe samy, aniž by o tom věděli.'),
(17, 2, 10, 'Choroba jest v srdcích jejich a rozmnožil Bůh chorobu jejich a bolestný trest očekává je za to, že lhali.'),
(18, 2, 11, 'A řekne-li se jim: „Nenaplňujte zemi nepravostí,“ řeknou: „Naopak, my ji napravujeme.“'),
(19, 2, 12, 'Však právě oni jsou to, kteří ji kazí, ale nejsou si toho vědomi.'),
(20, 2, 13, 'A řekne-li se jim: „Uvěřte jakož uvěřili druzí,“ odvětí: „Budeme-liž věřiti jako uvěřili pošetilci?“ Však právě oni jsou pošetilci, ale nejsou si toho vědomi.'),
(21, 2, 14, 'A potkají-li věřící, dí: „Uvěřili jsme,“ však poodejdou-li stranou k zlým duchům svým, řkou: „Držíme s vámi a z těch druhých si jenom tropíme žert.“'),
(22, 2, 15, 'Bůh si z nich ztropí žert a ponechá je dlouho bez vodítka blouditi ve svévoli jejích.'),
(23, 2, 16, 'Oni jsou ti, kteří koupili blud za dobré vedení, však z obchodu toho nepošel jim užitek a nejsou vedeni stezkou pravou.'),
(24, 2, 17, 'Podobají se v jednání svém tomu. jenž vznítil oheň a když osvětleno jím bylo vše vůkol - odňal jim Bůh oheň a ponechal je v temnotách: nemohou viděti!'),
(25, 2, 18, 'Hluší, němí a slepí jsou; proto nemohou se obrátiti.'),
(26, 2, 19, 'Nebo podobni jsou těm, kteří při spatření mraku na obloze, těžkého temnotami a hromem a bleskem, zacpávají prsty uši své před duněním hromu ze strachu před smrtí: ale Bůh ze všech stran obklopuje nevěřící.'),
(27, 2, 20, 'Málem je oslepuje blesk, však pokud jim svítí, kráčejí v záři jeho a když opět se zatemní, zastavují se. Kdyby Bůh chtěl, mohl by zbaviti je sluchu i zraku jejich, neboť Bůh zajisté jest všemohoucí.'),
(28, 2, 21, 'Ó lidé, klaňte se Pánu svému, jenž stvořil vás i ty, kteří byli před vámi: bojte se Boha!'),
(29, 2, 22, 'Onť stvořil vám zemi lůžkem a nebesa krytbou; a seslal s nebe vodu a vodou dal vzrůsti plodům za potravu vám. U vědomí toho nedávejte tudíž Bohu jemu rovných!'),
(30, 2, 23, 'A jste-li v pochybnostech o zjevení našem služebníku našemu, zkuste přednésti súru podobnou oněm (v této Knize) a předvolejte za svědky své ty, které vedle Boha vzýváte, jste-li pravdomluvnými.'),
(31, 2, 24, 'Však nesvedete-li toho — a jisto jest, že toho nedokážete — tedy bojte se ohně, jehož potravou jsou lidé a kamení a jenž připraven jest nevěřícím.'),
(32, 2, 25, 'A oznam radostnou novinu těm, kteří uvěřili a konali dobré skutky, že přichystány jsou pro ně zahrady, pod nimiž řeky tekou; a pokaždé když okusí plodů jejich, aby nasytili se, řeknou: „V pravdě tyto jsou podobny plodům, jež potravou nám byly dříve,“ ale jen podoba plodů daných jim bude stejná. Tam manželky budou míti bez poskvrny a přebývati tam budou věčně.'),
(33, 2, 26, 'Bůh zajisté neostýchá se vybrati pro podobenství své ať komára, ať předmět vzácnější. Ti, kdož uvěřili, vědí, že jest to pravda přicházející od Pána jejich; nevěřící pak dí: „Co chtěl Bůh objasniti nám tímto podobenstvím?“ Mnoho jich zbloudí jím a mnozí přivedeni jím budou na cestu pravou - ale jen zlí jím zbloudí!'),
(34, 2, 27, 'Ti, kteří ruší smlouvu dříve učiněnou s Bohem a rozdělují to, co Bůh nařídil, aby bylo spojeno, a páší nepravost na zemi: ti zajisté špatně pochodí!'),
(35, 2, 28, 'Jak můžete nevěřiti v Boha, který ve vás mrtvé vdechl život; jenž opětně vás nechá zemříti a znovu vám dá obživnouti : pak k němu se navrátíte?'),
(36, 2, 29, 'Onť stvořil vše, což na zemi jest, pro vás: pak zdvihl se k nebesům a v sedmero nebes je sestavil; onť zajisté jest vševědoucím.'),
(37, 2, 30, 'Když Pán tvůj řekl k andělům: „Aj, učiním sobě náměstka na zemi,“ odpověděli mu: „Zdaž postavíš tam někoho, jenž naplňovati bude zemi nepravostí a prolévati krev, mezitím, co my pějeme hymny chvály a svatosti tvé?“ I řekl jim: „Vímť zajisté, co vy nevíte.“'),
(38, 2, 31, 'A naučil Adama jménům všech věcí: pak ukázal je andělům a řekl: „Nuž zkuste udati mi jména těchto věcí, jste-li pravdomluvnými!“'),
(39, 2, 32, 'I řekli: „Chvála budiž tobě! Nevímeť zajisté jiného, než čemu naučil\'s nás: ty jedině jsi vědoucí, moudrý.“'),
(40, 2, 33, 'Řekl Bůh: „Adame, pověz jim jména oněch věcí.“ A když učinil tak Adam, řekl Bůh: „Zdaž neřekl jsem vám, že známy jsou mi věci skryté nebes i země a známo mi, cožkoliv veřejně přiznáváte i co tajíte?“'),
(41, 2, 34, 'A když řekli jsme andělům: „Skloňte se úctou před Adamem,“ učinili tak všichni, vyjma Iblíse, jenž se vzepřel a byl pyšným a stal se nevěřícím.'),
(42, 2, 35, 'A řekli jsme: „Adame, přebývej s manželkou svojí v ráji a pojídejte v hojnosti z něho, kdekoli se vám zlíbí; však nepřibližujte se k tomuto stromu, jinak zhřešíte.“'),
(43, 2, 36, 'Však Satan způsobil, že překročili zápověd\' tuto a vypuzeni byli z místa, kde se nalézali. I řekli jsme: „Ven s vámi! Navzájem si budete nepřáteli: země útulkem vám bude a zaopatřením dočasným.“'),
(44, 2, 37, 'A naučil se Adam od Pána svého slovům vzývání a Pán popřál mu sluchu, neboť on rád popřává sluchu a jest milosrdným.'),
(45, 2, 38, 'Řekli jsme: „Ven s vámi všemi! Až přijde vám vedení ode mne, kdožkoliv, následovati je bude, tomu netřeba se báti, aniž rmoutiti se.'),
(46, 2, 39, 'Však ti, kdož neuvěří a lží nazývati budou znamení naše, za příbytek dostanou oheň, v němž dlíti budou věčně.“'),
(47, 2, 40, 'Synové Israele, připomeňte si dobrodiní, jež prokázal jsem vám: dodržte smlouvu učiněnou se mnou a dodržím i já smlouvu učiněnou s vámi; ctěte mne!'),
(48, 2, 41, 'A věřte v to, co zjevil jsem na potvrzení toho, co již máte psáno: nebuďte prvními z nevěřících v má znamení a nekupujte za ně věc malé ceny; mne jedině bojte se!'),
(49, 2, 42, 'Neodívejte pravdu lží a neskrývejte pravdu, když jest vám známa.'),
(50, 2, 43, 'A zachovávejte správně modlitbu, plaťte zákonnou daň, a klekejte s těmi, kdož klekají.'),
(51, 2, 44, 'Zdaž nakazovati budete spravedlnost jiným a sami zapomenete se? Přece čtete Knihu: či nikdy neporozumíte?'),
(52, 2, 45, 'Zavolejte trpělivost a modlitbu ku pomoci své: zajisté velká jest to povinnost, však nikoliv pro pokorné,'),
(53, 2, 46, 'kteří pamatují, že jednou setkají se tváří v tvář s Pánem svým a že k němu se navrátí.'),
(54, 2, 47, 'Synové Israele, připomeňte si dobrodiní, jež prokázal jsem vám: pomněte, že dal jsem vám přednost nade všemi.'),
(55, 2, 48, 'A bojte se dne, kdy duše nevykoupí duši v ničem, kdy nebude přijmuta od ní přímluva, aniž vzato vyrovnání a nebude jim poskytnuto pomoci.'),
(56, 2, 49, 'A (pomněte času), kdy vysvobodili jsme vás z rukou lidu Faraonova, který krutě s vámi nakládal; zabíjel syny vaše a šetřil dcery vaše: v tom zajisté byla krutá zkouška od Pána vašeho.'),
(57, 2, 50, 'A kdy rozdělili jsme moře pro vás a utopili lid Faraonův před zraky vašimi.'),
(58, 2, 51, 'A kdy činili jsme smlouvu s Mojžíšem po čtyřicet nocí; tehdy postavili jste si tele modlou za nepřítomnosti jeho a prohřešili jste se.'),
(59, 2, 52, 'Poté nicméně odpustili jsme vám, abyste byli vděčnými.'),
(60, 2, 53, 'A kdy dali jsme Mojžíšovi Knihu a Rozlišení, abyste byli dobře vedeni.'),
(61, 2, 54, 'A kdy řekl Mojžíš k lidu svému: „Lide můj, prohřešil jsi se na sobě samém, vzav sobě tele modlou. Pros za odpuštění Stvořitele svého a krví vinníků smyj hřích svůj: tak nalezneš milost u Stvořitele svého a popřeje sluchu prosbě tvé, neboť on rád popřává sluchu kajícím a jest milosrdný.“'),
(62, 2, 55, 'A kdy řekli jste: „Mojžíši, neuvěříme ti dokud nespatříme Boha v jasné podobě“ a blesk zasáhl vás, jak jste pohlíželi.'),
(63, 2, 56, 'Pak vzkřísili jsme vás z mrtvých, abyste byli vděčnými.'),
(64, 2, 57, 'A dali jsme mračnu vznésti se nad vámi a seslali jsme vám mannu a křepelky — „Jezte z dobrých věcí, kterými jsme vás obdařili.“ Neuškodili tedy nám, nýbrž sobě samým.'),
(65, 2, 58, 'A kdy řekli jsme: „Vejděte do tohoto města a jezte z něho v hojnosti dle libosti své, však vstupujíce do brány padejte na tvář svou řkouce: ‚Odpuštění!‘ i promineme vám hříchy vaše a rozmnožíme přízeň svou těm, kteří konají dobro.“'),
(66, 2, 59, 'Však zlomyslní změnili slovo jim udané za jiné a seslali jsme na ně trest s nebe za zlý skutek jejich.'),
(67, 2, 60, 'A kdy Mojžíš žádal vody k napojení lidu svého i řekli jsme: „Udeř holí do skály“ a vyvřelo z ní dvanácte pramenů: a každý kmen věděl ihned, který z pramenů určen jest mu k pití. „Jezte a pijte z toho, čímž obdařuje vás Bůh,“ (řekli jsme), „a nedopouštějte se nepravostí a nevázaností na zemi.“'),
(68, 2, 61, 'A kdy řekli jste: „Mojžíši, nemůžeme snésti stále stejné potravy: popros za nás u Pána svého, aby dal vzrůsti nám cožkoli plodí země v bylinách a okurkách svých a česneku svém a čočce a cibuli své.“ I řekl: „Zdaž vyměniti chcete lepší za horší? Táhněte zpět do Egypta; tamť naleznete, čeho si žádáte.“ Bídou a nedostatkem stiženi byli a odvrátil se Bůh ve hněvu od nich, protože nevěřili ve znamení boží a nespravedlivě ubíjeli proroky; tak stalo se jim za to, že byli neposlušnými a vzdornými.'),
(69, 2, 62, 'Zajisté ti, kdož uvěřili a ti, kdož drží se víry židovské, a křesťané a sabejští - vůbec všichni, kdož věří v Boha a v den poslední a činí dobré skutky, naleznou odměnu u Pána svého a jim netřeba se báti, aniž rmoutiti se.'),
(70, 2, 63, 'A když přijali jsme smlouvu s vámi a pozdvihli horu nad hlavami vašimi, řkouce: „Přijměte s pevným rozhodnutím vše, což uložili jsme vám a chovejte v dobré paměti obsah slov našich, abyste žili v bázni.“'),
(71, 2, 64, 'Však poté odvrátili jste se a kdyby Bůh nebyl býval milostivým k vám a slitovným, zajisté špatně byste bývali pochodili.'),
(72, 2, 65, 'Neboť znám vám byl osud těch z vás, kteří znesvětili den sobotní a k nimž jsme řekli: „Buďte opicemi hnanými.“'),
(73, 2, 66, 'A učinili jsme je příkladnou výstrahou současníkům a potomkům jejich a napomenutím bohabojným.'),
(74, 2, 67, 'A kdy řekl Mojžíš k lidu svému: „Aj, káže vám Bůh, abyste obětovali KRÁVU,“ řekli: „Zdaž tropíš si z nás žert?“ Řekl: „Uchovejž Bůh, abych patřil k nerozumným.“'),
(75, 2, 68, 'Řekli: „Popros za nás u Pána svého, aby naznačil nám, jaká má býti.“ Řekl: „Zajisté praví Pán: Kráva ta nemá býti ani starou, ani jalovicí, nýbrž věku prostředního mezi těmito. Učiňte tudíž, což nakázáno jest vám.“'),
(76, 2, 69, 'Řekli: „Popros za nás u Pána svého, jaké má býti barvy.“ Řekl: „Zajisté praví Pán: Kráva ta má býti barvy jasně žluté, aby potěšením byla zrakům na ni patřícím.“'),
(77, 2, 70, 'Řekli: „Popros za nás u Pána svého, aby naznačil nám přesně, jaká má býti kráva ona, neboť nám zdají se býti krávy stejnými, a zajisté, zlíbí-li se Bohu, ukáže nám tu pravou.“'),
(78, 2, 71, 'Řekl: „Zajisté praví Pán: Budiž to kráva neznavená prací polní a zavodňováním rolí, zdravá, bez poskvrny.“ Řekli: „Nyní dal jsi nám pravé označení.“ Obětovali pak krávu, však málem by tak nebyli učinili.'),
(79, 2, 72, 'A když zabili jste muže a spor vedli mezi sebou proto: a Bůh odhalil, co ukrývali jste.'),
(80, 2, 73, 'Neboť řekli jsme: „Udeřte mrtvolu částí obětované krávy.“ Tak Bůh křísí mrtvé a ukazuje vám znamení svá, abyste bohdá pochopili.'),
(81, 2, 74, 'Od těch dob ztvrdla srdce vaše, takže podobna jsou kamení skal: ba tvrdšími se stala, neboť ze skal mnohých vyvírají řeky, jiné opět rozpukávají se a vytéká z nich voda, jiné opět padají bázní boží; a Bůh zajisté není lhostejným k tomu, co konáte.'),
(82, 2, 75, 'Žádáte si, aby k vůli vám uvěřili Židé? Část jich poslechla slov božích, však poté, když je byli pochopili, překroutili. je, dobře jsouce si toho vědomi.'),
(83, 2, 76, 'A když potkají věřící, dí: „Uvěřili jsme,“ však když jsou stranou mezi sebou, dí: „Zdaž vyprávěti budete jim, co Bůh zjevil vám, aby použíti toho mohli za důkaz proti vám před Pánem vaším? Nechápete-liž úmyslu jejich?“'),
(84, 2, 77, 'Což nevědí, že Bůh ví, co oni tají i co veřejně prohlašují?'),
(85, 2, 78, 'Mezi nimi. jsou lidé prostí, neznající obsahu Knihy: jedině liché znají pověsti a plni jsou pouhých domněnek.'),
(86, 2, 79, 'A běda těm, kteří píší Knihu svýma rukama a pak praví: „Toto přichází od Boha,“ aby získali z toho nepatrný výtěžek. Běda jim za to, co psaly ruce jejich a běda jim z toho, co tím vytěžili.'),
(87, 2, 80, 'Praví: „Nedotkne se nás oheň, vyjma po několik dní.“ Rci: „Učinili jste ohledně toho s Bohem smlouvu, které Bůh neporuší, anebo vyprávíte o Bohu, co nevíte?“'),
(88, 2, 81, 'Naopak ti, jichž užitkem jsou jen zlé skutky a obklopeni jsou hříchy svými, těm. údělem bude oheň, v němž přebývati budou věčně.'),
(89, 2, 82, 'A ti, kteří uvěřili a konali dobré skutky, jim údělem jest ráj a v něm přebývati budou věčně.'),
(90, 2, 83, 'A když přijali jsme smlouvu synů Israele, řekli jsme: „Vzývejte pouze jednoho Boha a chovejte se laskavě k rodičům, přátelům, sirotkům a chudým; a mluvte přívětivě s lidmi; zachovávejte modlitbu a dávejte povinnou almužnu.“ Poté, až na malý počet, odvrátili jste se a oddálili.'),
(91, 2, 84, 'A když učinili jsme smlouvu s vámi, abyste neprolévali své vlastní krve a nevypuzovali se navzájem z obydlí svých, tehdy souhlasili jste s tím a sobě samým byli svědky.'),
(92, 2, 85, 'Nicméně vy jste to byli, kteří poté vraždili se navzájem a vypudili část lidu svého z obydlí jejich a přispěli jste proti nim v zahrnutí jich příkořím a nepřátelstvím: však přijdou-li k vám jako zajatci, vykupujete je. A přece zapovězeno vám bylo, abyste je vyháněli. Či věříte pouze v část Knihy své a část jí zavrhujete? Jakáž pak bude odměna těch, kdož tak činí mezi vámi, ne-li potupa v tomto životě a že v den zmrtvýchvstání zavrženi budou do muk nejtěžších, neboť Bůh není lhostejným k tomu, co konáte.'),
(93, 2, 86, 'Ti, kdož koupili život pozemský za život budoucí, shledají, že nebudou zlehčena jim muka, aniž bude jim pomoženo.'),
(94, 2, 87, 'Dali jsme pak Mojžíšovi Knihu a poslali jsme po něm jiné proroky: a dali jsme Ježíšovi, synu Mariinu,. jasné důkazy jeho poslání a posílili jsme jej Duchem Svatým. A pokaždé, když přišel k vám prorok se slovy, jež nebyla vám po chuti zpyšněli jste: některé z proroků nazývali jste lháři a některé jste pobili.'),
(95, 2, 88, 'A řekli: „Neobřezána\'jsou srdce naše.“ Tak jest, a k tomu ještě zlořečil jim Bůh pro nevíru jejich. Jak málo jest těch, kdož věří!'),
(96, 2, 89, 'A když přišla jim Kniha od Boha, na potvrzení zjevení, která již měli - a ač předtím byli prosili o vítězství nad nevěřícími - nicméně, když přišlo jim to, o čem byli již zpraveni, neuvěřili v Knihu onu: a zlořečení Boha padniž na nevěřící!'),
(97, 2, 90, 'Za jak bídnou cenu prodali samy sebe tím, že neuvěřili v to, co seslal Bůh, ze závisti nad tím, že Bůh dal zjevení své z milosti tomu ze služebníků svých, jejž vyvolil si! Tím přivedli na sebe od Boha hněv za hněvem, a potupný trest přichystán jest pro nevěřící.'),
(98, 2, 91, 'A když řekne se jim: „Uvěřte v to, co sesláno bylo Bohem,“ dí: „Věříme v to, co zjeveno bylo nám,“ a nevěří v to, co zjeveno jim bylo později, ač jest to pravda seslaná na potvrzení písem jejich. Rci: „Proč tedy zabili jste proroky boží předešlé, jste-li věřícími?“'),
(99, 2, 92, 'Vždyť přece přišel k vám Mojžíš s jasnými důkazy svého posláni: nicméně za zády jeho postavili jste si tele modlou a zhřešili jste.'),
(100, 2, 93, 'A když přijali jsme smlouvu s vámi a pozdvihli horu nad hlavami vašimi, (řkouce): „Přidržte se pevně toho, co dáváme vám a poslouchejte přikázání naše,“ řekli: „Slyšeli jsme, ale neposlechli jsme.“ Srdce jejich napojena byla uctíváním telete následkem nevíry jejich. Rci: „Hanba tomu, k čemu vede vás víra vaše, skutečně-li věříte!“'),
(101, 2, 94, 'Rci: „Máte-li vskutku zajištěné věčné obýváni u Boha, v němž nebude jiných mimo vás, tedy žádejte si smrti, jste-li pravdomluvnými.“'),
(102, 2, 95, 'Však nebudou si jí žádati nikdy, následkem díla rukou svých, neboť Bůh zná hříšníky.'),
(103, 2, 96, 'Věru shledáš je bažící po životě více než ostatní lidé, ba více než modloslužebníci; některý z nich touží kéž žil by tisíc let: však dlouhým životem nezmůže ujíti trestu svému, neboť Bůh vidí skutky jejich.'),
(104, 2, 97, 'Rci: „Kdo byl by nepřítelem Gabriela?“ Vždyť on to byl, jenž z dovolení božího způsobil, aby seslán byl Korán v srdce tvé, na potvrzení zjevení předešlých a jakožto dobré vedení a radostná novina věřícím.'),
(105, 2, 98, 'Kdo byl by nepřítelem Boha a jeho andělů a proroků jeho, a Gabriela a Michaela? Bůh zajisté nepřítelem jest nevěřících.'),
(106, 2, 99, 'Vždyť seslali jsme ti jasná znamení a pouze nepravostní v ně neuvěří.'),
(107, 2, 100, 'Zdaž pokaždé, když vejdou ve smlouvu, najde se mezi nimi skupina, jež odmítne ji? Ba věru, většina jich nevěří!'),
(108, 2, 101, 'A když přišel k nim prorok od Boha, potvrzující písma jejich, odvrhla část těch, kterým Kniha dána byla, Knihu boží za záda svá, jakoby jí neznali.'),
(109, 2, 102, 'A následovali to, co zlí duchové vykládají ohledně vlády Šalamounovy: však Šalamoun nebyl nevěřícím, nýbrž oni zlí duchové; tito učí lid kouzelnictví a tomu, co zjeveno bylo dvěma andělům v Babylóně, Hárútovi a Márútovi. Nicméně tito dva neučili nikoho svému umění, aniž by mu nebyli řekli: „My jsme jen pokušením, pročež nestaň se nevěřícím.“ A od těchto dvou naučili se lidé jak působiti rozbroj mezi mužem a jeho ženou; však oni nikomu tím neškodili, vyjma s dovolením Boha. Tak naučili se lidé tomu, co jim škodilo, nikoli tomu, co jim prospívalo: a věděli, že kdožkoli: získá umění to, ztrácí tím veškerý podíl na životě budoucím. Bídná věru jest cena, za kterou se prodali: kéž by to jen byli věděli!'),
(110, 2, 103, 'Však kdyby byli uvěřili a báli se Boha, odměna od Boha byla by bývala jistě lepší: kéž by jen byli věděli!'),
(111, 2, 104, 'Vy, kteříž jste uvěřili, neříkejte: „Ra\'iná“, nýbrž rcete: „Unzurná“, a poslechněte: neboť nevěřícím trest bolestný jest přichystán.'),
(112, 2, 105, 'Neradi vidí nevěřící z těch, jimž dána byla Kniha a z těch, již klaní se mnoha bohům, aby jakékoliv dobro sesláno vám bylo od Pána vašeho: však Bůh zvláště vyznamenává milosrdenstvím svým toho, který se mu zlíbí, a Bůh zajisté velkým jest v milosrdenství svém.'),
(113, 2, 106, 'Nezrušíme, aniž dáme vymizeti z paměti tvé veršům, kterých bychom nenahradili lepšími, aneb stejnocennými: zdaž nevíš, že Bůh všechno může?'),
(114, 2, 107, 'Zdaž nevíš, že Bohu přináleží království nebes i země, a že nemáte mimo Boha jiného zástupce aniž ochránce?'),
(115, 2, 108, 'Či snad chcete vyptávati se proroka svého, jako tázán byl Mojžíš před tím? Však ten, kdo vyměňuje víru za nevíru, jistě zbloudil s cesty rovné.'),
(116, 2, 109, 'Mnozí z těch, kdož mají Knihu, ze sobecké závisti své rádi by přivedli vás zpět k nevíře poté, když jste byli uvěřili: poté, když jasně vám ukázána byla pravda: odpusťte jim a míjejte je, dokud Bůh neprojeví svého rozhodnutí, neboť zajisté Bůh má moc nade vším.'),
(117, 2, 110, 'A zachovávejte modlitbu a dávejte povinnou almužnu: a cožkoli dobrého činíte, naleznete u Boha, neboť Bůh vidí skutky vaše.'),
(118, 2, 111, 'Říkají: „Nevejde do ráje nikdo, vyjma Židů a křesťanů“ - tak si totiž přejí. Rci: „Podejte mi důkaz svůj, jste-li pravdomluvnými.“'),
(119, 2, 112, 'Naopak: ti, kdož s odevzdaností obrací tvář svou k Bohu a konají dobro, naleznou odměnu u Pána svého a jim netřeba se báti, aniž rmoutiti se.'),
(120, 2, 113, 'Židé tvrdí: „křesťané nemají opory,“ a křesťané tvrdí: „Židé nemají opory“, ač jedni i druzí čtou Knihu. Ti pak, kdož ničeho nevědí, mluví podobně. Však Bůh rozsoudí mezi nimi v den zmrtvýchvstání ohledně toho, v čem se rozcházeli.'),
(121, 2, 114, 'A kdo nespravedlivějším jest toho, jenž zabraňuje, aby prohlašováno bylo jméno boží v svatyních a jenž pracuje ke zpustošení jejich? Tito zajisté mohou jen s bázní vstoupiti do nich: hanba jest údělem jejich na světě tomto a v životě posmrtném velká budou muka jejich.'),
(122, 2, 115, 'Bohu zajisté patří východ i západ: a kamkoli se obrátíte, stkví se tvář boží. Bůh věru nesmírný jest a vševědoucí.'),
(123, 2, 116, 'A praví: „Bůh učinil sobě syna.“ Při slávě jeho, nikoliv! Jedině jemu samému patří, cožkoliv jest na nebi i na zemi: vše poslušno jest jej.'),
(124, 2, 117, 'On Stvořitelem jest nebes i země: když rozhodne o něčem, rce: „Staniž se“ a stane se.'),
(125, 2, 118, 'A dí ti, kdož ničeho nevědí: „Nepromluví-li k nám Bůh, aneb nepřijde-li nám znamení, (neuvěříme).“ Stejně jako oni mluví, tak mluvili ti, kdož byli před nimi a srdce jejich jsou si podobna navzájem. Jasná pak znamení ukázali jsme těm, kdož mají víru pevnou.'),
(126, 2, 119, 'Zajisté poslali jsme tě s pravdou, jakožto nositele radostné zvěsti a napomínače: a nebudeš tázán ohledně těch, jichž údělem bude peklo.'),
(127, 2, 120, 'Ani Židé ani křesťané nebudou souhlasiti s tebou, dokud nebudeš následovati víry jejich. Rci: „V pravdě jedině; vedení boží jest pravým vedením.“ A poddáš-li se přání jejich poté, když přišlo ti vědění, nebude ti Bůh ani zastáncem, ani pomocníkem.'),
(128, 2, 121, 'Ti, kterým jsme dali Knihu a kteří čtou v ní tak, jak má býti čtena, věří v ni: a ti, kdož nevěří v ni, pochodí věru špatně.'),
(129, 2, 122, 'Synové Israele, pomněte dobrodiní, kterými zahrnul jsem vás a pomněte, že povýšil jsem vás nade všechny ostatní v světě.'),
(130, 2, 123, 'A bojte se dne, kdy duše nevykoupí druhé v ničem, kdy nebude přijmuto od ní vyrovnání, aniž prospěje jí přímluva a kdy nebude jí poskytnuto pomoci.'),
(131, 2, 124, 'A když Pán zkoušel Abrahama slovy a tento vyplnil je, řekl Pán: „Učiním tě představeným lidstva.“ Řekl: „A jak bude s potomstvem mým?“ Řekl Bůh: „Smlouva moje nezahrnuje nepravostné.“'),
(132, 2, 125, 'Učinili jsme Dům posvátný bezpečným a pevným útočištěm lidstvu a řekli jsme: „Přijměte místo Abrahamovo za místo modlení:“ a nakázali jsme Abrahamovi a Ismaelovi, aby očistili dům můj pro ty, kdož obcházeti jej budou v průvodu, pro ty, kdož zdržovati se v něm budou ve zbožném rozjímání a pro ty, kdož padati v něm budou na kolena svá a tvář svou.'),
(133, 2, 126, 'Tehdy řekl Abraham: „Pane můj, učiň tuto zemí bezpečnou a poskytni za výživu plodů těm z lidu jejího, kdož uvěřili v Boha a v den poslední.“ I řekl Pán: „Těm, kdož neuvěřili, na krátko jen poskytnu radosti z toho: pak zapudím je do muk ohně.“ Děsná bude to cesta!'),
(134, 2, 127, 'A když Abraham s Ismaelem zbudovali základy domu, řekli: „Pane náš, přijmi jej od nás: tyť zajisté jsi slyšící a vědoucí.'),
(135, 2, 128, 'Pane náš, učiň, abychom odevzdáni byli do vůle tvé a učiň aby potomstvo naše odevzdáno bylo do vůle tvé: a nauč nás posvátným obřadům našim, a popřej sluchu pokání našemu, neboť ty zajisté rád popřáváš sluchu těm, kteří činí pokání, a jsi milosrdný.'),
(136, 2, 129, 'Pane náš, dej vzejíti proroku z jejich středu, aby předčítal jim znamení tvá a učil je Knize a moudrosti a očišťoval je: ty zajisté mocný jsi a moudrý.“'),
(137, 2, 130, 'A kdo bude míti odpor k víře Abrahamově, vyjma toho, jenž pošetilostí naplněn jest? Zajisté vyvolili jsme si jej v tomto světě a ve světě příštím bude mezi spravedlivými.'),
(138, 2, 131, 'Když Pán jeho řekl mu: „Odevzdej se do vůle mé,“ řekl. „Odevzdal jsem se do vůle Pána světů.“'),
(139, 2, 132, 'A víru tu odkázal Abraham dětem svým a taktéž Jakub, řka: „Synové moji, zajisté Bůh vyvolil pro vás víru a proto neumírejte jinak, než odevzdáni do vůle boží.“'),
(140, 2, 133, 'Byli jste svědky toho, když Jakub blízek byl smrti a děl k synům svým: „Komu sloužiti budete po smrti mé?“ Řekli: „Sloužiti budeme Bohu tvému a Bohu otců tvých Abrahama a Ismaela a Isáka, Bohu jedinému, a do vůle jeho jsme odevzdáni.“'),
(141, 2, 134, 'Pokolení ono zašlo: jemu připadne odměna za činy jeho a vám za činy vaše: vy nebudete tázáni na to, co činili oni.'),
(142, 2, 135, 'Říkají vám: „Buďte Židy, anebo. křesťany a tak vedeni budete stezkou pravou.“ Rci: „Nikoliv, jsme při víře Abrahamově, jenž byl pravé víry (haníf) a nesloužil mnoha bohům.“'),
(143, 2, 136, 'Rcete: „Uvěřili jsme v Boha a v to co nám seslal a v to, co sesláno bylo Abrahamovi a Ismaelovi a Isákovi a Jakubovi a kmenům: a v to, co dáno bylo Mojžíšovi a Ježíšovi a v to, co dáno bylo prorokům Pánem jejich: nečiníme nijakého rozdílu mezi nimi a jsme odevzdáni do vůle Páně.“'),
(144, 2, 137, 'A uvěří-li v totéž, co vy, pak nalézati se budou na cestě pravé; však odvrátí-li se od vás, budou v roztržce s vámi: nicméně dostačí ti oproti nim Bůh, neb on zajisté jest slyšící, vševědoucí.'),
(145, 2, 138, 'Totoť jest křest boží a kdo lépe může křtíti než Bůh? A jemu sloužíme.'),
(146, 2, 139, 'Rci: „Zdaž hádati se chcete s námi ohledně Boha, jenž stejně jest Pánem naším jako vaším? My jednáme, jak my jednáme, a vy jednáte, jak vy jednáte: my zajisté jsme Bohu upřímně oddáni.“'),
(147, 2, 140, 'Řeknete snad, že Abraham, Ismael Isák a Jakub a kmeny (israelské) byli Židy anebo křesťany? Rci: „Kdo ví to lépe, vy anebo Bůh? A kdo prohřešuje se více než ten, jenž zatajuje svědectví dané Bohem? Bůh zajisté není lhostejným k tomu, co konáte.“'),
(148, 2, 141, 'Pokolení ono zašlo: jemu připadne odměna za činy jeho a vám za činy vaše: vy nebudete tázáni na to, co činili oni.'),
(149, 2, 142, 'Řeknou pošetilci mezi lidem: „Co odvrátilo je od jejich qibly; které se přidržovali?“ Rci: „Bohu náleží východ i západ: onť vede, koho chce, ke stezce přímé.“'),
(150, 2, 143, 'A tak učinili jsme vás národem středu, abyste byli svědky vůči lidem a aby prorok váš byl svědkem vůči vám. A určili jsme dřívější qiblu jen proto, abychom rozeznali ty, kteří následovati budou proroka od těch, kdož obrátí se k němu zády: byť bylo to velkou nesnází, není jí to pro ty, kdož vedeni jsou Bohem: a Bůh nezanechá bez užitku víru vaši, neboť Bůh dobrotivý jest vůči lidem a slitovný.'),
(151, 2, 144, 'Viděli jsme obracení tváře tvé všemi směry nebeskými, ale v budoucnu chceme, abys obracel se ke qible, v níž nalezneš zalíbení: obracej tudíž tvář svou směrem modlitebnice posvátné; a kdekolivěk budete se nalézati, obracejte tváře své směrem jejím. Ti, kdož obdrželi Knihu, vědí zajisté, že jest pravdou, přicházející od Pána jejich: a Bůh zajisté není lhostejným k tomu, co konají.'),
(152, 2, 145, 'I kdybys předvedl těm, kteří obdrželi Knihu, všemožné zázraky, nebudou následovati tvojí qibly: aniž ty následovati budeš jejich qibly. Mezi nimi samými pak jedni následují qibly druhých. A poddal-li by ses přání jejich poté, když přišlo ti vědění, tehdy i ty staneš se nepravostným.'),
(153, 2, 146, 'Ti, kteří obdrželi Knihu, znají ji stejně dobře jako znají syny své, však část jich skrývá pravdu vědomě.'),
(154, 2, 147, 'Pravda přichází od Pána tvého: nebudiž tedy jedním z pochybovačných.'),
(155, 2, 148, 'Každý má končinu na nebesích, ke které obrací se při modlitbě: přidržujte se konání dobra, kdekolivěk jste a Bůh svede vás všechny jednoho dne, neboť Bůh jest všemohoucí.'),
(156, 2, 149, 'A zkadkoliv vyjdeš, obrať tvář svou směrem k modlitebnici posvátné: totoť zajisté jest pravda, přicházející od Pána tvého a Bůh není lhostejným k tomu, co činíte.'),
(157, 2, 150, 'A zkadkoliv vyjdeš, obrať tvář svou směrem k modlitebnici posvátné: a kdekolivěk budete, obracejte tváře své směrem k ní, aby lidé nemohli vám ničeho. vytknouti. Pokud se týče nepravostných mezi nimi, těch nebojte se: však bojte se mne, abych dovršil dobrodiní svá nad vámi a abyste vedeni byli cestou pravou.'),
(158, 2, 151, 'Jakož poslali jsme vám proroka ze středu vašeho, aby četl vám znamení naše, očišťoval vás, učil vás Knize a moudrosti a učil vás tomu, co jste nevěděli.'),
(159, 2, 152, 'Tedy buďte pamětlivi mne a budu pamětliv vás a čiňte mi díkůvzdání a nebuďte nevěřícími.'),
(160, 2, 153, 'Vy, kteří jste uvěřili, zavolejte trpělivost a modlitbu ku pomoci své, neboť Bůh zajisté pomáhá trpělivým.'),
(161, 2, 154, 'Nenazývejte ty, kdož ubiti byli na stezce Páně, mrtvými: naopak, nazývejte je živými! Však vy toho nechápete.'),
(162, 2, 155, 'Zkoušeti vás budeme poněkud strachem a hladem a ztrátou majetku a životů a plodů zemských: však radostnou hlásiž novinu trpělivým,'),
(163, 2, 156, 'kteří když postiženi jsou neštěstím, dí: „V pravdě patříme Bohu a k němu se navrátíme.“'),
(164, 2, 157, 'Nad těmito požehnání. jest Páně a milosrdenství jeho a tito zajisté vedeni jsou cestou pravou.'),
(165, 2, 158, 'V pravdě, Safá a Marwa jsou vyvolenými místy Boha: a kdožkoli putuje k Domu posvátnému a navštěvuje místa svatá, nezhřeší když obejde je obě dokola: a kdo z vlastního popudu koná dobro, (bude odměněn): neboť Bůh zajisté jest vděčným, vševědoucím.'),
(166, 2, 159, 'Ti, kteří zatajují, ať jest to cokoliv, z jasných důkazů a dobrého vedení, námi seslaných poté, když jasně jsme je byli označili lidem v Knize; těm zlořečiti bude Bůh a zlořečiti jim budou zlořečící.'),
(167, 2, 160, 'Vyjma těch, kteří kajícně obrátí se ke mně a polepší se a poučují druhé: těm popřeji sluchu, neboť rád popřávám sluchu kajícím a jsem milosrdný.'),
(168, 2, 161, 'Ti pak, kdož neuvěřili a zemřeli v nevíře své, nad těmi zlořečení jest Boha i andělů a lidí všechněch.'),
(169, 2, 162, 'Zahrnuti jím budou na věky a nebude ulehčeno trápení jejich a Bůh nikdy na ně nepohlédne.'),
(170, 2, 163, 'Bůh váš jest jediný Bůh: není Boha mimo něho, milosrdného, slitovného.'),
(171, 2, 164, 'Zajisté ve stvoření nebes a země a ve střídání noci se dnem a v korábech přeplouvajících moře s tím, což užitečno jest lidem: a ve vodách, jež sesílá Bůh s nebe a jimiž obživuje zmrtvělou zemi; a po ní pak rozptyluje živočichy všeho druhu: a v přeměně větrů a v oblacích určených ke službě mezi nebem a zemí: v tomť jistá jsou znamení těm, kdož dovedou porozuměti.'),
(172, 2, 165, 'Jsou pak mezi lidmi někteří, kdož staví si vedle Boha jemu rovné a milují je stejnou láskou jako Boha: však ti, kdož uvěřili, milují Boha nade všecko. Ale uvidí nepravostní, až shlédnou trest svůj, že veškerá moc jest pouze u Boha a že Bůh věru přísným jest v trestání.'),
(173, 2, 166, 'A když odděleni budou vůdci od stoupenců svých poté, když shlédnou trest, a přetnuta budou veškerá pojítka mezi nimi,'),
(174, 2, 167, 'řeknou stoupenci jejich: „Kéž vrátiti bychom se mohli (na zemi): tu oddělili bychom se od nich, jako oni nyní oddělili se od nás.“ Takovým způsobem ukáže jim Bůh skutky jejich a marnost jejich: nikdy nevyjdou z ohně (věčného).'),
(175, 2, 168, 'Ó lidé! Požívejte ze všeho, což dobrého jest a dovoleného na zemi a nenásledujte šlépějí Satana, neboť on jest zjevným nepřítelem vaším.'),
(176, 2, 169, 'On podněcuje vás pouze ke zlu a špatnostem a k tomu, abyste říkali o Bohu to, co nevíte.'),
(177, 2, 170, 'A když řekne se jim: „Následujte to, co Bůh zjevil,“ řeknou: „Nikoliv, následujeme to, nač zvykli jsme si od otců svých, ač otcové jejich ničemu nerozuměli, aniž vedeni byli cestou pravou.“'),
(178, 2, 171, 'Nevěřící podobají se tomu; jenž volá na tvora, který slyší jen když se naň křičí a hlasitě volá: hluší, němi, a slepí jsou a neporozumí.'),
(179, 2, 172, 'Vy; kteří jste uvěřili, pojídejte z dobrých věcí, kterých jsme vám poskytli potravou a děkujte Bohu, jste-li jeho služebníky.'),
(180, 2, 173, 'Jedině zapovězeno jest vám pojídati mršiny a krve a masa vepřového a všeho, nad čímž voláno bylo jiné jméno než boží. Však kdo donucen byl by k tomu potřebou, nikoli zvůlí a neposlušností, nebude vinen: neboť Bůh velkým jest v odpouštění, slitovným.'),
(181, 2, 174, 'Ti, kdož zatajují něco z toho, co Bůh zjevil v Knize, a vyměňují to za věc ceny nepatrné, pouze ohněm krmí břicha svá a nepromluví na ně Bůh v den zmrtvýchvstání a neočistí je, a trest bolestný jest pro ně připraven.'),
(182, 2, 175, 'Tito jsou to, kteří koupili zbloudění za dobré vedení a trest za odpuštění: jak dlouho vytrvati jim bude v ohni!'),
(183, 2, 176, 'Tak stane se jim proto, že Bůh seslal Knihu pravdivou a ti, kdož rozcházejí se v tomto mínění o Knize boží, daleko odtrhli se od pravdy.'),
(184, 2, 177, 'Není zbožnosti v tom, když obracíte tváře své směrem k východu anebo k západu: však ten jest zbožným, kdo uvěřil v Boha a v den poslední,,a v anděly, a v Knihu, a v proroky: a ten, kdo z lásky k Bohu dává z majetku svého příbuzným a sirotkům a chudým a poutníkům a prosícím a na výkupné zajatců: a ten, kdo zachovává modlitbu a dává povinnou almužnu: a ti, kdož dodržují smlouvu svou, když v ni byli vešli, a kdož trpěliví jsou v protivenství a v nesnázích a v dobách zlých. Tito jsou spravedlivými a tito jsou, kdož se bojí Boha.'),
(185, 2, 178, 'Vy, kteří jste uvěřili! Předepsán jest vám zákon odvety za vraždu: muž svobodný za muže svobodného, otrok za otroka, žena za ženu. S tím, jemuž sleveno bylo něco s trestu bratrem jeho, má býti zacházeno laskavě: on pak odplatiti má se štědře. Totoť jest polehčením od Pána vašeho a milosrdenstvím: však toho, jenž poté dopustí se odvety, trest bolestný očekává.'),
(186, 2, 179, 'V tomto zákoně odvety jest ochrana života vašeho pro vás, kdož rozumní jste. Tak snad báti budete se Boha.'),
(187, 2, 180, 'Předepsáno jest vám, aby kdožkoli, jenž blízek jest smrti a zanechává majetek, učinil štědrý odkaz svým rodičům a příbuzným: totoť povinností jest pro bohabojné.'),
(188, 2, 181, 'Však ten, kdo mění poslední vůli poté, když byl vyslechl ji od umírajícího, dopouští se tím nespravedlnosti, jež spadá na ty, kteří ji změnili: Bůh zajisté vše slyší a ví.'),
(189, 2, 182, 'A ten, kdož obávaje se, že odkazující buď zmýlil se, nebo byl nespravedlivým, učiní vyrovnání mezi dědici, nebude míti viny: Bůh zajisté velkým jest v odpouštění a slitovným.'),
(190, 2, 183, 'Vy, kteří jste uvěřili! Předepsán jest vám půst, jakož předepsán byl předkům vašim, abyste báli se Boha.'),
(191, 2, 184, 'Jen po určitý počet dní. A bude-li kdo z vás nemocen, nebo na cestě, bude postiti se stejný počet dní později: a kdo může snésti půst, (však nedodrží jej), ten za pokání dejž najísti chudému. A kdo z vlastního popudu koná dílo dobré, k dobru bude mu počítáno: a budete-li se postiti, bude to k dobru vašemu, uvědomíte-li si to.'),
(192, 2, 185, 'V měsíci Ramadánu, v němž seslán byl Korán, aby dobrým vedením byl lidem, vysvětlením tohoto vedení a rozlišením, jakmile jeden každý z vás se octne v tomto měsíci, postiž se v něm. Kdož by byl nemocen neb na cestě, postiž se stejný počet dní později. Bůh chce, aby vám to bylo snadným, nikoli obtížným, a chce, abyste jen dovršili předepsaný počet dní a chválili Boha za vedení jeho a byli mu vděčnými.'),
(193, 2, 186, 'A když tázati se tě budou služebníci moji na mne, budu jim blízek: vyslyším volání vzývajícího mne, bude-li volati ke mně: však nechť poslouchají mne a věří ve mne, aby šli správnou cestou.'),
(194, 2, 187, 'Dovoleno jest vám, abyste přiblížili se v noci postní k ženám svým: ony jsou oděvem vaším a vy oděvem jejich. Bůh věděl dobře, že prohřešujete se v tom proti sobě samým, pročež popřává sluchu pokání vašemu a odpouští vám. Pročež vejděte k nim plni žádosti po tom, co Bůh uložil vám, abyste činili: jezte pak a pijte dokud nebudete moci rozeznati bílou nit od černé v ranní zoře. Od oné chvíle zachovávejte přísně půst až do noci a nevcházejte k ženám svým: raději pobývejte v modlitebnici ve zbožném rozjímání. Tyto jsou meze určené Bohem: pročež nepřibližujte se k nim. Takovým způsobem ukazuje Bůh jasně znamení svá lidem, aby báli se jej.'),
(195, 2, 188, 'Nemrhejte jměním svým mezi sebou za věci liché, aniž dávejte z nich soudcům, abyste promrhati mohli část jmění druhých nespravedlivě, jsouce si toho vědomi.'),
(196, 2, 189, 'Budou dotazovati se tě na období nového měsíce. Rci: ,,Tato jsou určena jakožto měřítka času prospěšná lidem a pro stanovení poutí do Mekky. Neníť pak zbožnosti v tom, že vracíte se do domů svých ze zadu, však zbožnost spočívá v bázni boží. Pročež vcházejte předními dveřmi do domů svých a bojte se Boha, aby se vám dobře vedlo.'),
(197, 2, 190, 'A bojujte na stezce Páně proti těm, kteří bojují proti vám: však nečiňte jim bezpráví (útočíce na ně předem), neboť Bůh nemiluje těch, kdož tak činí.'),
(198, 2, 191, 'A zabte je kdekoliv je najdete a vypuďte je zkadkoliv oni vypudili vás, neboť pokušení modloslužby horší jest vraždění: však nebojujte s nimi u mešity posvátné leda tehdy, kdyby vás v ní napadli. Napadnou-li vás, tedy zabte je: taková jest odměna nevěřících.'),
(199, 2, 192, 'Však přestanou-li, pak věru Bůh velkým jest v odpouštění, slitovným.'),
(200, 2, 193, 'A bojujte proti nim, dokud nepřestane pokušení modloslužby a nepřevládne náboženství (jediného); Boha: však přestanou-li, konec budiž nepřátelství, vyjma oproti nepravostným.'),
(201, 2, 194, 'Měsíc posvátný a místa posvátná jsou pod ochranou zákona odvety. A kdožkoli nepřátelsky napadne vás, napadněte i vy jej stejně nepřátelsky: a bojte se Boha a věztež, že Bůh jest s bohabojnými.'),
(202, 2, 195, 'Dávejte štědře pro účel boží a nevrhejte se vlastníma rukama ve zkázu. Čiňte dobro, neboť Bůh miluje ty, kdož dobro konají.'),
(203, 2, 196, 'Konejte pouť a návštěvu (svatyně boží) z lásky k Bohu: a bylo-li vám v tom zabráněno obklíčením, pošlete nějaký dar, který nejsnáze projde. A neholte hlav svých. dokud dar váš nedošel místa určení svého. Kdož by pak byl z vás nemocen, nebo měl bolest v hlavě, nahradiž tuto povinnost postem, neb almužnou, neb darem. A když jste bezpečni, ten, kdo spokojí se s návštěvou a ponechá pouť na pozdější dobu, obětujž dar, který shledá nejpříležitějším. Komu to nebude možno, nechť postí se po tři dny během pouti a sedm dní po návratu vašem: tedy celkem deset dní. Toto uloženo jest tomu, jehož rodina nebyla přítomna v mešitě posvátné. Bojte se Boha a věztež, že Bůh věru přísným jest v trestání.'),
(204, 2, 197, 'Pouť konána budiž v měsících vám známých. Kdožkoli vezme na se pouť do Mekky v těchto měsících, zdržujž se ženy, přestoupení příkazu a hádek po dobu pouti: cožkoliv dobrého pak učiníte, bude známo Bohu. A zaopatřte se na cestu: však nejlepším zaopatřením jest bázeň boží. Pročež. bojte se mne vy, kdož jste rozumní.'),
(205, 2, 198, 'Není prohřešením, žádáte-li si milosti od Pána svého: a když sestupovati budete v průvodu od `Arafátu, rozpomeňte se na Boha poblíže místa posvátného: připomeňte si, jak uvedl na stezku pravou vás, kteříž před tím byli jste mezi bloudícími.'),
(206, 2, 199, 'Pak postupujte rychle v průvodu, kudy jdou druzí, a proste Boha za odpuštění, neboť Bůh velkým jest v odpouštění, slitovným.'),
(207, 2, 200, 'Když pak skončili jste obřady své posvátné, vzpomeňte Boha jakož vzpomínáte otců svých, ba ještě mocněji. Jsou mezi lidem někteří, již dí: „Pane náš, dej nám (podíl náš) v tomto světě:“ tito věru nebudou míti podílu ve světě budoucím.'),
(208, 2, 201, 'A jsou mezi nimi někteří, již dí: „Pane náš; dej nám dobrý úděl na světě tomto i ve světě budoucím a ochraň nás před trápením ohně věčného.“'),
(209, 2, 202, 'Tito zajisté obdrží takový úděl , jakého si zasloužili, neboť Bůh věru rychlým jest v účtování.'),
(210, 2, 203, 'Pomněte Boha během určeného počtu dní: a přispíší-li si kdo o dva dny, nebo opozdí-li se o dva dny, nebude mu to počítáno za vinu, bojí-li se Boha. Tedy bojte se Boha a věztež, že u něho budete shromážděni.'),
(211, 2, 204, 'Jest mezi lidem jeden, který souhlasí s tvojí řečí ohledně života pozemského a volá Boha za svědka toho, co jest v srdci jeho: však jest tvým nejhorším odpůrcem.'),
(212, 2, 205, 'A sotva, že obrátí se k tobě zády, jde od jednoho konce země ke druhému, aby plnil ji rozbrojem a ničil role i stáda: Bůh pak zajisté nemiluje rozbrojů.'),
(213, 2, 206, 'A řekne-li se mu: „Boj se Boha,“ zmocní se jej hříšná pýcha: však on s dostatek pocítí peklo a špatné to bude lůžko!'),
(214, 2, 207, 'Jest opět jiný mezi lidem, jenž prodá i sebe sama z touhy, aby zalíbil se Bohu: a Bůh zajisté milostivým jest vůči služebníkům svým.'),
(215, 2, 208, 'Vy, kteří jste uvěřili, vejděte všichni plně do pravé víry a nenásledujte stop Satana, neboť on jest zjevným nepřítelem vaším.'),
(216, 2, 209, 'Však dopustíte-li se poklesku poté, když zjevena vám byla jasná znamení, vězte; že Bůh jest mocný a moudrý.'),
(217, 2, 210, 'Zdaž jiného co mohou očekávati, než že přijde na ně Bůh v temnotách mračen a andělé a naplněn bude rozsudek? Neboť k Bohu navracejí se všechny věci.'),
(218, 2, 211, 'Taž se synů Israele, kolik jasných znamení jsme jim dali? Však pozmění-li kdo milost boží poté, když dána mu byla, sezná, že Bůh věru přísným jest v trestání.'),
(219, 2, 212, 'Nevěřícím líbí se tento život pozemský a vysmívají se těm, kteří uvěřili: ti, kdož bojí se Boha, budou povýšeni nad ně v den zmrtvýchvstání, neboť Bůh obdaruje ty, které chce, beze všeho počtu.'),
(220, 2, 213, 'Lidstvo bylo jedním národem: a poslal Bůh proroky, jako posly radostných zvěstí a varovatele a seslal s nimi Knihu plnou pravdy, aby rozsuzovala mezi lidmi ohledně toho, v čemž se rozcházeli: však nikdo neměl odlišného mínění vyjma těch, jimž dána byla Kniha, i poté, když dány byly jim jasné důkazy, a to jen ze vzájemné žárlivosti. A přivedl Bůh ty, kteří uvěřili, ku pravdě o tom, v čem z dovolení jeho se rozcházeli: a Bůh vede, koho chce, ke stezce přímé.'),
(221, 2, 214, 'Zdaž domníváte se, že vejdete v ráj, aniž byste byli zakusili toho, co zakusili ti, kteří zašli před vámi? Dotkly se jich nesnáze a protivenství a tak zmítáni jimi byli, že konečně řekl prorok a ti, kteří uvěřili s ním: „Kdy přijde nám pomoc boží?“ - Což není pomoc boží blízka?'),
(222, 2, 215, 'Budou dotazovati se tě ohledně rozdávání almužny. Rci: „Cožkoliv dobrého rozdáno vámi bude, nechť připadne rodičům, příbuzným, sirotkům a chudým a pocestným: a zajisté Bůh ví o každém dobru, které vykonáte.“'),
(223, 2, 216, 'Předepsáno jest vám válčení, avšak vy cítíte k němu odpor: možná, že cítíte odpor k něčemu, co jest vám ve prospěch, a možná, že máte rádi něco, co jest vám ke škodě. A Bůh ví to, však vy toho nevíte.'),
(224, 2, 217, 'Dotazovati se tě budou ohledně válčení v měsíci posvátném. Rci: „Válčení v něm jest velkým hříchem: však odpuzování od stezky Páně, nevíra v Boha a v modlitebnici posvátnou, vyhánění z ní lidu v ní dlícího, větším jest u Boha hříchem, a pokušení modloslužby horším jest vraždění. Nevěřící neustanou ve válčení proti vám, dokud neodvrátí vás od víry vaší, bude-li jim to možno: a kdožkoli z vás odvrátí se od víry své a zemře jako nevěrec, těch konání marná budou v životě tomto i budoucím: údělem těchto bude oheň, v němž přebývati budou věčně.'),
(225, 2, 218, 'Však ti, kdo uvěřili a opustili vlast svou a bojují na stezce boží, mohou doufati v milosrdenství boží: a Bůh zajisté velkým jest v odpouštění, slitovným.“'),
(226, 2, 219, 'Dotazovati se tě budou ohledně vína a hry hazardní. Rci: „V těchto obou (věcech) jest zlo i užitek lidstvu zároveň: však zlo z nich pochodící větším jest užitku.“ A tázati se tě budou ohledně toho, co mají rozdávati jako almužnu. Rci: „Přebytek.“ Takovým způsobem objasňuje vám Bůh znamení svá, abyste přemýšleli,'),
(227, 2, 220, 'o tomto světě a o budoucím. Budou dotazovati se tě ohledně sirotků. Rci: „Spravedlivé zacházení s nimi jest dobrým skutkem: a máte-li co činiti s nimi, jednejte s nimi, jako s bratry. Bůh dobře rozpoznává toho, jenž zle jedná od toho, jenž spravedlivě jedná: a kdyby Bůh chtěl, mohl by uvésti vás v nesnáz: neboť zajisté Bůh jest mocným a moudrým.“'),
(228, 2, 221, 'A nežeňte se a modloslužebnicemi, dokud neuvěřily: otrokyně věřící jest lepší modloslužebnice, i když tato se vám více líbí. A nedávejte dcer svých za manželky modloslužebníkům, dokud neuvěřili: neboť otrok věřící lepší jest modloslužebníka, i kdyby tento více se vám líbil. Oni zvou vás do ohně věčného: však Bůh zve vás do ráje a k odpuštění z vůle své a jasně označuje znamení svá lidu, aby jich byli pamětlivi.'),
(229, 2, 222, 'A dotazovati se tě budou na menstruace žen. Rci: „Tyto jsou poruchou.“ Protož vzdalujte se žen svých po ono období a nepřibližujte se k nim, dokud nejsou očištěny. Když pak jsou očištěny, vcházejte k nim z oné strany, ze které nakázal vám Bůh. Bůh zajisté miluje kající a miluje ty, kdož snaží se býti čistými.'),
(230, 2, 223, 'Ženy vaše jsou polem vaším: tudíž vcházejte na pole své zkadkoli se vám zlíbí, však před tím učiňte něco ve prospěch duší svých: a bojte se Boha a vězte, že setkáte se s ním. Zvěstuj radostnou tuto novinu věřícím.'),
(231, 2, 224, 'A neberte Boha za svědka přísahy své, že budete ctnostnými, že báti budete se Boha a že šířiti budete dobrou shodu mezi lidmi: neboť Bůh zajisté vše slyší a ví.'),
(232, 2, 225, 'Bůh nebude trestati vás za pochybení v přísaze vaší, ale trestati vás bude za to, co zasloužila si srdce vaše. Bůh zajisté velkým jest v odpouštění, slitovným.'),
(233, 2, 226, 'Těm, kdož vzdáliti se chtějí žen svých, určena jest lhůta čtyř měsíců: a když změní úmysl svůj, Bůh zajisté velkým jest v odpouštění, slitovným.'),
(234, 2, 227, 'Však rozhodli-li se zapuditi svoji ženu, Bůh zajisté vše slyší a ví.'),
(235, 2, 228, 'A zapuzené ženy nechť pozorují samy sebe po tři svá měsíční období: neníť dovoleno jim, aby skrývaly to, co Bůh stvořil v lůnech jejich, věří-li v Boha a v den poslední. A jest spravedlivějším, když manželé jejich vezmou je v tomto stavu k sobě zpět, jsou-li svolni činiti, což spravedlivo jest. A ony pak mají se chovati k manželům svým stejně, jak oni zachovali se k nim, spravedlivě: však mužové jsou o stupeň výše nad ženami. Bůh zajisté jest mocným a moudrým.'),
(236, 2, 229, 'Zapuzení může státi se dvakráte: podržíte-li si ženu svou, chovejte se k ní spravedlivě; vypovíte-li ji, tedy učiňte tak laskavě. A není dovoleno vám, abyste si podrželi něco z toho, co darovali jste jim, leda že báli byste se oba, že nebude vám lze dodržeti mezí určených Bohem. Však obáváte-li se, že nebude vám oběma lze dodržeti mezí určených Bohem, nepojde žádnému z obou manželů viny z toho, co žena by dala za sebe výkupným. Tyto jsou meze zákona daného Bohem; pročež nepřestuptež jich, neboť kdožkoli přestoupí meze zákonů daných Bohem, jest nepravostným.'),
(237, 2, 230, 'Zapudil-li by pak muž ženu svou po třetí, není dovoleno mu vzíti ji zpět, dokud nebyla se provdala za jiného muže: a když tento ji byl od sebe vypověděl, nepojde žádnému z obou viny z toho, když vrátí se k sobě, domnívají-li se, že budou s to dodržeti meze zákona božího. Tyto jsou meze zákona božího a Bůh jasně označuje je těm, kdož dovedou chápati.'),
(238, 2, 231, 'Když zapudili jste ženu a přišel čas, kdy má odejíti od vás, buď podržte ji velkomyslně, aneb propusťte ji velkomyslně: však nezadržujte ji násilím, abyste jí učinili příkoří. Učinil-li by kdo tak, činí násilí sobě samému. Nepovažujte znamení boží za žert, ale pamětlivi buďte dobrodiní, jež Bůh prokázal vám, a Knihy a moudrosti, jež seslal vám, aby vás jimi varoval. A bojte se Boha a vězte, že Bůh o všem ví.'),
(239, 2, 232, 'Když zapudili jste ženu a přišel čas, kdy má odejíti od vás, nezabraňujte jim, aby provdaly se za své muže, dohodli-li se mezi sebou správně. Tímto napomenut budiž kdožkoli z vás věří v Boha a i- den poslední: takto jednati jest nejctnostnějším a nejpřístojnějším pro vás. Bůh ví, však vy nevíte.'),
(240, 2, 233, 'Matky rozvedené kojiti budou dítky své plné dva roky, přeje-li si otec, aby dovršeno bylo kojení. Otci pak dítka přístojí starati se o potravu a oděv matky způsobem spravedlivým. Nikdo nechť není přetížen nad síly své: matka nechť není zkrácena pro dítko své, aniž otec zkrácen pro dítko své. A stejné závazky připadnou otcovu dědici. Však shodnou-li se oba rodičové po vzájemném souhlasu a poradě na odstavení dítka, nebude jim to počítáno za vinu: a shodnete-li se vzíti pro dítko kojnou. nebude vám to rovněž počítáno za vinu, zaplatíte-li kojné, co jste jí slíbili, spravedlivě. A bojte se Boha a vězte, že Bůh vidí všechny skutky vaše.'),
(241, 2, 234, 'Zanechají-li ti, kdož zemrou z vás, ženy, tyto nechť pozorují stav svůj po čtyři měsíce a deset dní. A potom, když vypršela tato lhůta jejich, nebudete míti viny za to, jakým způsobem naloží samy s sebou počestně. Bůh zajisté zpraven jest o tom, co konáte.'),
(242, 2, 235, 'A nebudete míti viny, učiníte-li nabídnutí k sňatku oněm ženám aneb budete-li chovati. v nitru svém podobný úmysl. Bůh ví, že na ně budete mysliti, však nečiňte jim slibu v tajnosti a mluvte s nimi pouze počestně. A nevcházejte ve svazek manželský dokud nevypršela předepsaná lhůta: a vězte, že Bohu známo jest vše. co jest v nitru vašem; mějte se na pozoru před ním a vězte, že Bůh jest velkým v odpouštění a laskavým.'),
(243, 2, 236, 'Nebudete míti viny; zapudíte-li ženy, jichž jste se nebyli dotkli, aniž byli určili jim věno. Zaopatřte je vším potřebným: bohatý dle prostředků svých a chudý dle prostředků svých, spravedlivě, jakž závazno jest pro ty, kdož činí právě.'),
(244, 2, 237, 'Zapudíte-li ženy dříve než jste se byli jich dotkli, však kterým jste již byli určili věno, bude patřiti jim polovina z určeného věna, nezřeknou-li se jí ony, aneb nezřekne-li se jí ten, v jehož rukou spočívá dotyčný svazek manželský. Zřeknou-li se jí, bude to bližší bohabojnosti. A nezapomeňte jednati velkomyslně ve svých vzájemných stycích, neboť Bůh zajisté dobře vidí skutky vaše.'),
(245, 2, 238, 'Zachovávejte bedlivě modlitby a modlitbu prostřední a povstávejte k modlitbě, prodchnouti oddaností k Bohu.'),
(246, 2, 239, 'A obáváte-li se nějakého nebezpečí, tedy modlete se v chůzi, neb na koni: však jste-li v bezpečí, pomněte ve vzývání svém Boha za to, že naučil vás tomu, co jste nevěděli.'),
(247, 2, 240, 'Ti z vás, kdož zemrou, a zanechají po sobě ženy, odkáží jim zaopatření na dobu jednoho roku, bez přinucení opustiti jejich dům. Však opustí-li jej samy, nebude vám viny za způsob, jakým rozhodnou o sobě samých počestně. A Bůh mocný jest a moudrý.'),
(248, 2, 241, 'Zapuzeným určeno budiž spravedlivé zaopatření, jakž závazno jest pro bohabojné.'),
(249, 2, 242, 'Takto jasně označuje vám Bůh znamení svá, abyste rozumně jednali.'),
(250, 2, 243, 'Zdaž neviděls těch, kteří opustili obydlí svá — a na tisíce jich bylo — ze strachu před smrtí? A řekl k nim Bůh: „Zemřete!“, a pak vzkřísil je opět Bůh, neboť Bůh jest nejvýš milostivým k lidem: však většina jich nevzdává mu za to díků.'),
(251, 2, 244, 'A bojujte na stezce Páně a vězte, že Bůh všechno slyší a ví.'),
(252, 2, 245, 'Kdo medle učiní dobrou půjčku Bohu? Tomu mnohonásobně odplatí Bůh, neboť ruka jeho jest zavřena neb otevřena: a k němu se navrátíte.'),
(253, 2, 246, 'Zdaž nevzpomínáš shromáždění synů Israele po smrti Mojžíšově, když řekli jednomu z proroků svých: „Vzbuď krále mezi námi a bojovati budeme na stezce boží.“ I řekl: „Nemohlo-liž by se státi, že byste odepřeli bojovati, kdyby válčení bylo vám přikázáno?“ Řekli: „Což jiného zbývá nám než bojovati na stezce boží, nám, kteří vyhnáni jsme byli, spolu s dětmi svými, z obydlí svých?“ Však poté, když přikázáno jim bylo válčení, couvli, až na malý počet jich: však Bůh dobře zná nepravostné!'),
(254, 2, 247, 'A řekl jim prorok jejich: „Bůh postavil vám Saula za krále.“ Řekli: „Jakž měl by tento panovati nám, když my větší máme způsobilost k panování, než on, aniž oplývá majetkem?“ Řekl: „Bůh vyvolil ho k panování nad vámi a rozhojnil sílu jeho ve vědění a postavě tělesné: a Bůh dává panství své tomu, komu chce. Bůh zajisté nesmírný jest v moci své a vševědoucí.“'),
(255, 2, 248, 'I řekl jim prorok jejich: „Na znamení království jeho poslána vám bude schránka úmluvy: v ní záruka bezpečí bude od Pána. vašeho a ostatky toho, co zanechal po sobě rod Mojžíšův a rod Áronův; a andělé ponesou ji. V tom zajisté znamení bude vám, jste-li věřícími.“'),
(256, 2, 249, 'A když Saul vytáhl s vojsky svými, řekl: „Bůh zkoušeti vás bude u řeky. Ten, jenž napije se z ní, není jedním z mých: však ten, jenž neokusí z ní — vyjma napití se z dlaně - ten bude jedním, z mých.“ A napili se z ní všichni, vyjma malého počtu jich. A když přešel Saul a ti, kdož uvěřili s ním, řeku, řekli ostatní: „Nemáme dnes síly k boji s Goliášem a vojsky jeho.“ Však ti, kteří přesvědčeni byli o jistotě setkání svého s Bohem, řekli: „Kolikráte již malý hlouček porazil velké voje z dopuštění božího! Bůh zajisté jest s těmi, kdož trpělivě vyčkávají.“'),
(257, 2, 250, 'A když vytáhli k boji s Goliášem a vojsky jeho, řekli: „Pane náš, popřej nám vytrvání a upevni kroky naše a pomoz nám k vítězství nad lidem nevěřícím.“'),
(258, 2, 251, 'A porazili je z dopuštění božího a zabil David Goliáše a dal mu Bůh panství a soudnost a naučil jej tomu, čemu chtěl (jej naučiti). Kdyby pak nezdržoval Bůh lidstvo navzájem jedny pomocí druhých, zkázou naplnila by se země: však Bůh jest velký v milosti své vůči veškerenstvu.'),
(259, 2, 252, 'Tato jsou znamení Boha: v pravdě zjevujeme je tobě, neboť ty‘s jedním z proroků.'),
(260, 2, 253, 'Mezi těmito proroky dali jsme jedněm přednost nad druhými. K některým Bůh mluvil, a někteří povýšeni jsou nad druhé: a dali jsme Ježíšovi, synu Mariinu, jasná znamení a posílili jsme jej Duchem Svatým. A kdyby byl býval chtěl Bůh, ti, kteří přišli po nich, nebyli by se bývali pobíjeli navzájem poté, když seslána jim byla jasná znamení. Však oni pustili se do hádek: a někteří z nich uvěřili, druzí pak neuvěřili. Kdyby byl býval chtěl Bůh, nebyli by se bývali bili navzájem: však Bůh činí což přeje si.'),
(261, 2, 254, 'Vy, kteří jste uvěřili, rozdávejte štědře z toho, čím obdařili jsme vás, dříve než přijde den, kdy přestane obchodování a přátelství a přímluva. Nevěřící pak zajisté jsou nepravostni.'),
(262, 2, 255, 'Bůh! Není Boha kromě něho, živoucího, stálého! Jej nezmocňuje se dřímota, aniž spánek: jemu náleží vše, což na nebi jest i na zemi. Kdož může přimluviti se u něho jinak, než s jeho svolením? Onť ví, co bylo před mimi a co bude po nich, však oni nepochopí z vědění jeho ničeho, vyjma toho, co on chce. Rozprostřen jest trůn jeho po nebi i zemi a opatrování obou nepůsobí mu námah: on jest nejvyšší, mocný.'),
(263, 2, 256, 'Nebudiž donucování ve víře: vždyť nyní pravá cesta jasně rozeznává se od pochybení. A kdož přestane věřiti v Tághúta a uvěří v Boha, ten zajisté uchopil se spolehlivého ucha nádoby, jež nikdy se neutrhne. V pravdě, Bůh slyší a ví vše.'),
(264, 2, 257, 'Bůh ochráncem jest těch, kteří uvěřili a vyvede je z temnot na světlo. Těch pak, kdož neuvěřili, ochránci jsou Tághútové a zavedou je ze světla do temnot: a údělem jich bude oheň, v němž přebývati budou věčně.'),
(265, 2, 258, 'Zdaž nevzpomínáš si na toho jenž vedl spor s Ahrahamem ohledně Pána jeho proto, že Bůh dal mu panství? Řekl tehdy Abraham: „Pán můj jest ten, jenž dává život a přivodí smrt.“ Odvětil onen: „Jáť jsem, který dávám život a přivodím smrt.“ I řekl Abraham: „Bůh přivádí slunce od východu: nuže, přiveď ty je od západu.“ A zaražen zůstal onen nevěřící: Bůh pak neuvádí na stezku pravou nepravostných.'),
(266, 2, 259, 'Anebo (nevzpomínáš si na) toho, jenž kráčel kdys kol nadobro zpustošeného města? Řekl: „Jak uvede Bůh v život toto město nyní mrtvé?“ I umrtvil jej Bůh na sto let, pak vzkřísil jej a řekl: „Jak dlouho pobyl‘s zde?“ Řekl: „Pobyl jsem zde asi den, aneb část dne.“ Řekl Bůh: „Nikoliv, pobyl‘s zde celých sto let. Pohleď na jídlo své a nápoj svůj : nejsou zkaženy; a pohleď na osla svého. V tvém případě chtěl jsem učiniti znamení lidem. Pohleď na kosti osla svého, jak zvedneme je a pokryjeme v zápětí masem.“ A když spatřil tento jasný důkaz muž onen, řekl: „Nyní přišel jsem k vědomí toho, že Bůh jest všemohoucí.“'),
(267, 2, 260, 'A když řekl Abraham: „Pane můj, ukaž mi, jak křísíš mrtvé?“, řekl Bůh: „Zdaž ještě nevěříš?“ Řekl: „Zajisté ano, ale chci pouze, aby plně bylo uklidněno srdce mé.“ Řekl Bůh: „Vezmi tedy čtyři ptáky a stáhni je k sobě, rozřež je a části jich rozmetej po všech hřbetech hor: pak zavolej je a přiletí ihned k tobě; a tak věz, že Bůh jest mocný a moudrý.“'),
(268, 2, 261, 'Ti, kdož rozdávají majetek svůj ve stezce boží, podobni jsou zrnu obilnému, z nějž vyroste sedm klasů a každý klas vydá sto zrn. Bůh mnohonásobně odplácí těm, kterým chce, neboť Bůh jest nesmírný v štědrosti své, vševědoucí.'),
(269, 2, 262, 'Ti, kdož rozdávají jmění své ve stezce boží, aniž by v zápětí činili výčitek nebo dopouštěli se špatností, odměnu hojnou naleznou u Pána svého a jim netřeba se báti, aniž rmoutiti se.'),
(270, 2, 263, 'Laskavé slovo a odpuštění lepší jest almužny, již v zápětí následuje špatnost. A Bůh bohatý jest a laskavý.'),
(271, 2, 264, 'Vy, kteří jste uvěřili, nerušte zásluhu almužny své výčitkami neb špatností jako ten, kdo rozdává pouze proto, aby viděn byl lidmi a nevěří v Boha, ani v den poslední. Takový podobá se skalnému vrchu, pokrytému tenkou vrstvou hlíny, na který přišel prudký déšť a zůstavil jen tvrdou skálu. Tito nebudou míti prospěchu z toho co vyzískali; neboť Bůh nevede pravou stezkou nevěřících.'),
(272, 2, 265, 'Ale ti, kdož rozdávají jmění svá z touhy zalíbiti se Bohu a utvrditi v dobru duše své, podobají se zahradě na návrší, na kterou přišel hojný déšť a tím zdvojnásobeny byly plody její. Nepřijde-li pak na ni hojný déšť, tedy rosa : a Bůh dobře vidí všechny skutky vaše.'),
(273, 2, 266, 'Zdaž rád by měl kdo z vás zahradu palem a vinné révy, zavlažovanou řekami, bohatou vším druhem plodu: a kdyby v tom přikvačiti naň mělo stáří a děti jeho měly by býti slabé a smršť ohnivá spáliti by měla zahradu? Takovým způsobem objasňuje vám Bůh znamení svá, abyste přemítali.'),
(274, 2, 267, 'Vy, kteří jste uvěřili, rozdávejte z dobrých věcí, kterých jste nabyli a z toho, čemu vzrůsti jsme dali pro vás ze země; a nevybírejte nejhorší z toho k rozdávání; takové, jehož sami byste nevzali, aniž byste přimhouřili oko. A vězte, že Bůh bohatý jest a chvályhodný.'),
(275, 2, 268, 'Satan slibuje vám chudobu a nabádá vás ke špatnostem: však, Bůh slibuje vám odpuštění a milost svou; a Bůh nesmírný jest v štědrosti své a vševědoucí.'),
(276, 2, 269, 'Dává moudrost tomu, komu chce a ten, jemuž dána byla moudrost, velký obdržel dar; však pouze rozumní jsou toho pamětlivi.'),
(277, 2, 270, 'A jakoukoliv dáte almužnu a jakýkoli učiníte (obětní) slib, Bůh o tom ví: a nebudou míti zastánců ti, kdož zle činí.'),
(278, 2, 271, 'Dáváte-li almužnu veřejně, jest chvalitebná: však dáváte-li ji v skrytosti a chudým, větší máte zásluhu. Tím smazány vám budou hříchy vaše: a Bůh zpraven jest o všem,. co konáte.'),
(279, 2, 272, 'Není tvou věcí, abys vedl je: ale Bůh vede, koho chce. Cožkoli dobrého rozdali jste, bude počítáno ve prospěch duší vašich: a nedávejte jinak, než z touhy po spatření tváře boží. Cožkoli dobrého rozdali jste, odplaceno vám: bude a nebudete zkráceni.'),
(280, 2, 273, 'Jsou chudí mezi vámi, kteří zabráni jsou bojem na stezce boží a nemohou tlouci se zemí za bohatstvím. Nevědomci považují je za bohaté soudíce dle zdrženlivosti jejich: však poznáš je po známkách jejich - neprosí dotěravě lidi. Ale cožkoli dobrého dáte jim, Bůh bude toho pamětliv.'),
(281, 2, 274, 'Ti, kdož rozdávají z majetku svého dnem i nocí, v skrytosti a veřejně, naleznou odměnu u Pána svého a jim netřeba se báti, aniž rmoutiti se.'),
(282, 2, 275, 'Ti, kdož hltají výtěžek lichvy, vstanou z mrtvých jako ten, koho potřísnil Satan dotekem svým. A to proto, že praví: „Prodej jest totéž, co lichva.“ Však Bůh dovolil prodej a zapověděl lichvu. A tomu, jenž přestane lichvařiti poté, když přišlo mu napomenutí od Pána jeho, odpuštěno bude minulé a sudba jeho záležeti bude od Boha. Však ti, kteří vrátí se k lichvě, údělem obdrží oheň, v němž přebývati budou věčně.'),
(283, 2, 276, 'Zničí Bůh lichvu, však úrokem lichvy zmnohonásobí almužnu. Bůh zajisté nemiluje nikoho z nevěřících a zlo činících.'),
(284, 2, 277, 'Ti, kdož uvěřili a konají dobré skutky a zachovávají modlitbu a dávají povinnou almužnu, naleznou odměnu svou u Pána svého a jim netřeba se báti, aniž rmoutiti se.'),
(285, 2, 278, 'Vy, kteří jste uvěřili, bojte se Boha a zanechte poslední zbytky lichvaření, jste-li věřícími.'),
(286, 2, 279, 'A neučiníte-li tak, připravte se na vypovězení války vám Bohem a prorokem jeho: káti-li se budete, zůstanou vám kapitály vaše. Nebudete utiskovati a nebudete utiskováni.'),
(287, 2, 280, 'Je-li dlužník váš v nesnázi, vyčkejte, až usnadněno bude mu zaplacení dluhu: však odpustíte-li mu dluh jeho v podobě almužny, bude to lepším pro vás, dovedete-li si to uvědomiti.'),
(288, 2, 281, 'Bojte se dne, kdy navrátíte se k Bohu; tehdy odměněn bude každý podle zásluh svých a nebudou zkráceni.'),
(289, 2, 282, 'Vy, kteří jste uvěřili, uvázali-li jste se v dluh na určitou lhůtu, poznamenejte to písemně a nechť sepíše písař spravedlivě ujednání a tom mezi vámi: a nechť nezdráhá se písař tak učiniti, jak naučil jej Bůh. Nechť píše a nechť diktuje mu dlužník: a nechť bojí se Boha, Pána svého. a ničeho nestrhne; a kdyby ten, jenž dluhuje, byl hloupý neb slabý a nebyl s to, aby diktoval sám, nechť diktuje zástupce jeho spravedlivě. A zavolejte z mužů svých dvou svědků a není-li dvou mužů, tedy jednoho muže a dvě ženy z těch, které uznáte vhodnými za svědky: aby, kdyby jedna z nich pozapomněla, druhá mohla ji na to upamatovati. A svědkové nemají zdráhati se svědčiti, kdykoliv byli by předvoláni. Nepřezírejte nikdy poznamenati půjčku písemně, ať malá jest či velká, s udáním lhůty splatnosti: způsob tento spravedlivějším jest u Boha, příhodnějším pro svědectví a jistějším pro předejití všem pochybnostem. Vyjma případu, kdy zboží leží před vámi a můžete předávati si je z ruky do ruky: nebudete míti viny, když o tom ničeho nepoznamenáte písemně. A béřte si svědky k obchodům svým a neubližujte ani písaři, ani svědkovi: učiníte-li tak, dopouštíte se tím zlého činu. Bojte se Boha a Bůh poučí vás, neboť Bůh jest vševědoucí.'),
(290, 2, 283, 'A jste-li na cestě a nenaleznete písaře, vezměte zástavu: však svěříte-li jeden druhému něco, nechť ten, jemuž věc ta byla svěřena, odvede tuto v pořádku: a nechť bojí se Boha, Pána svého. A nezdráhejte se vydati svědectví: kdožkoli skrývá svědectví, zlý jest srdcem. A Bůh ví o všem, co činíte.'),
(291, 2, 284, 'Bohu přináleží vše, což na nebi jest i na zemi: přiznáte-li neb skryjete-li co v nitru svém, Bůh účtovati bude. s vámi o všem a odpustí, komu bude chtíti a potresce, koho bude chtíti , neboť Bůh jest všemohoucí.'),
(292, 2, 285, 'Uvěřil prorok v cožkoli zjeveno mu bylo Pánem jeho a z věřících každý uvěřil v Boha a anděly jeho a Knihy jeho a proroky jeho: nečiníme rozdílu mezi proroky jeho. Řekli: „Slyšeli jsme a uposlechli jsme: o odpuštění tvé, Pane náš, prosíme tě a k tobě vede cesta naše.'),
(293, 2, 286, 'Nepřetěžuje Bůh nikterou duši nad síly její: zásluhy, jichž dobyla si a viny, jichž dopustila. se, budou jí spočteny. Pane náš, nebéř nám za zlé viny pošlé ze zapomnění neb omylů našich. Pane náš, nevkládej na bedra naše břemene jež ukládal\'s těm, kteří předešli nás: a neukládej nám břemen nad síly naše. Smaž hříchy naše a odpusť nám a slituj se nad námi; tys svrchovaným Vládcem naším: dopomoz nám tedy k vítězství nad lidem nevěřícím!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Alif. Lám. Mím.'),
(295, 3, 2, 'Bůh, Není Boha kromě něho, živoucího, stálého!'),
(296, 3, 3, 'Onť seslal tobě Knihu v pravdě, na potvrzení toho, co zjeveno bylo dříve: onť seslal Zákon a Evangelium před tím, jakožto dobré vedení lidem a seslal Rozlišení:'),
(297, 3, 4, 'zajisté pro ty, kteří neuvěřili ve znamení boží, těžký připraven jest trest: Bůh pak mocný jest a pán pomsty.'),
(298, 3, 5, 'Před Bohem nic není skryto na zemi a na nebi:'),
(299, 3, 6, 'onť utváří vás v lůně matek vašich, dle libosti své. Není Boha, kromě něho, mocného, moudrého.'),
(300, 3, 7, 'Onť seslal ti Knihu, v níž některé verše jsou nezvratné: tyto základem jsou Knihy, druhé pak jsou přirovnáními. Ti pak, v jichž srdcích sídlí náklonnost k odchylování se, pouští se zapřirovnáními, hledajíce rozkol a ze žádosti po výkladu: však nikdo nezná vykladu zjevení kromě Boha. Ti, kdož pevného jsou vědění, dí: „Uvěřili jsme v Knihu: vše, co obsahuje, přichází od Pána našeho.“ Však nikdo není si toho pamětliv, vyjma lidí přemýšlivých a chápajících.'),
(301, 3, 8, 'Pane náš, nedej odchýliti se srdcím našim poté, když byl\'s uvedl nás na stezku pravou a obdař nás milostí svou, neb tys největším z obdarovatelů.'),
(302, 3, 9, 'Pane náš, ty zajisté shromáždíš lidstvo v den, o němž není pochyby. Bůh zajisté nikdy neodchýlí se od závazků svých.'),
(303, 3, 10, 'V pravdě, neveřícím nepomohou u Boha ani jmění, ani děti jejich pranic: za potravu sloužiti budou ohni.'),
(304, 3, 11, 'Taktéž i lid Faraonův a ti, kteří byli před ním, nazvali vylhanými znamení naše a Bůh pohnal je k zodpovědnosti za viny jejich: a Bůh přísný jest v trestání.'),
(305, 3, 12, 'Rci těm, kdož nevěří: „Budete přemoženi a shromážděni v pekle: špatné to bude místo odpočinku!“'),
(306, 3, 13, 'Viděli jste již znamení ve dvou vojích srazivších se: jeden, bojující na stezce boží, a druhý, sebraný z nevěřících. I zdálo se nevěřícím, že vidí vás v počtu dvojnásobném jich: Bůh přispívá pomocí svou, komu chce. V tom zajisté dobré bylo poučení lidem jasného zraku.'),
(307, 3, 14, 'Pěknými zdají se býti lidem zábavy se ženami a dětmi a pěknou láska k nahromaděným pokladům zlata a stříbra, ke koním vybraným a ke stádům dobytka a rolím obilným: toto vše jest tohoto světa míjivou radostí, však u Boha, tamť krásnější jest bydliště!'),
(308, 3, 15, 'Rci: „Mám oznámiti vám cos lepšího nad vše toto, připraveného pro bohabojné u Pána jejich? Zahrady, pod nimiž řeky tekou, v nichž přebývati budou věčně: a ženy bez poskvrny a zalíbení Boha.“ A Bůh sleduje pohledem služebníky své,'),
(309, 3, 16, 'kteří říkají: „Pane náš, zajisté uvěřili jsme: odpusť nám viny naše a zbav nás trestu ohně,“'),
(310, 3, 17, 'trpělivé, pravdomluvné, ponížené, štědré a za odpuštění prosící při každém úsvitu ranním.'),
(311, 3, 18, 'Bůh sám jest svědkem, že není Boha, kromě něho: a andělé i lidé důkladného vědění a přímé poctivosti, prohlašují: „Není Boha, kromě něho mocného, moudrého!“'),
(312, 3, 19, 'Pravým náboženstvím u Boha jest Islám: ti, jimž dána byla Kniha, počali rozcházeti se v názorech teprve poté, když přišlo jim vědění, ze vzájemné žárlivosti. Kdožkoli pak neuvěří ve znamení boží, shledá, že Bůh zajisté rychlým jest v účtování.'),
(313, 3, 20, 'A budou-li příti se s tebou, rci: „Odevzdal jsem se do vůle boží a rovněž ti, kdož následují mne.“ Rci těm, kteří obdrželi Knihu i lidu obecnému „Zdaž odevzdáváte se do vůle boží?“ Učiní-li tak, vedeni budou stezkou pravou, však odvrátí-li se k tobě zády — (dobře): tvým posláním jest jen hlásání pravdy. A Bůh dobře pozoruje služebníky své!'),
(314, 3, 21, 'Však těm, kdož nevěří ve znamení boží a bezprávně ubíjejí proroky, a ubíjejí ty, kdož nabádají ke spravedlivosti, oznamuj trest bolestný.'),
(315, 3, 22, 'Konání jejich bezvýsledna budou ve světě tomto i budoucím a nebudou míti zastánců.'),
(316, 3, 23, 'Zdaž nevzpomínáš těch, kterým dána byla část Knihy, když zavoláni byli ke Knize boží, aby dle ní souzeno bylo mezi nimi? Tehdy odvrátila se část jich a vzdálila se.'),
(317, 3, 24, 'To proto, že řekli: „Nedotkne se nás oheň, vyjma určitého počtu dní.“ Jich vlastní výmysly zavedly je ve víře jejich.'),
(318, 3, 25, 'A jak bude tomu, až shromáždíme vás v den, o němž není pochyby: kdy každá duše odměněna bude po zásluze své a nikdo nebude zkrácen?'),
(319, 3, 26, 'Rci: „Velký Bože, tys pánem vší moci: dáváš moc, komu chceš a béřeš moc, komu chceš; povyšuješ, koho chceš a ponižuješ, koho chceš: v ruce tvé jest dobro, neboť tys všemocný.'),
(320, 3, 27, 'Dáváš noci přecházeti v den a dáváš dnu přecházeti v noc: a přivádíš živoucí k smrti a k životu přivádíš mrtvé, a obdařuješ živobytím, koho chceš beze všeho počtu.“'),
(321, 3, 28, 'Nechť nebéřou si věřící za přátele nevěřící spíše, než věřící: kdožkoli tak učiní, těm nelze ničeho očekávati od Boha, vyjma tehdy, kdybyste se měli něčeho od nich obávati. Bůh sám varuje vás, abyste se ho báli, neboť k němu vede cesta návratu.'),
(322, 3, 29, 'Rci: „Tajíte-li co v prsou svých, anebo přiznáváte, Bůh ví o všem. Bůh zajisté ví o všem, co na nebi jest i na zemi: a Bůh jest všemocný.“'),
(323, 3, 30, 'V ten den každá duše shledá před sebou, což dobrého byla vykonala a co zlého: a přáti si bude, aby velká vzdálenost dělila ji od zlých skutků jejích. Bůh sám varuje vás, abyste se ho báli a Bůh jest laskavým ke služebníkům svým.'),
(324, 3, 31, 'Rci: „Milujete-li Boha, následujte mne: zamiluje si vás Bůh a odpustí vám viny vaše, a Bůh velkým jest v odpouštění, slitovným.“'),
(325, 3, 32, 'Rci: „Buďte poslušni Boha a proroka jeho: však odvrátíte-li se — zajisté nemiluje Bůh nevěřících.“'),
(326, 3, 33, 'Bůh vyvolil nade všechny ostatní v světě Adama a Noema a rod Abrahamův a ROD `IMRÁNŮV:'),
(327, 3, 34, 'pokolení tato vyšla jedna z druhých. A Bůh všechno slyší a ví.'),
(328, 3, 35, 'Vzpomeň, když řekla manželka `Imránova: „Pane můj, zaslibuji ti plod lůna svého úplně k službě tvé a přijmiž jej ode mne, neboť ty zajisté vše slyšíš a víš.“'),
(329, 3, 36, 'A když porodila, řekla : „Pane můj, porodila jsem děvče“ — a Bůh zajisté nejlépe věděl, co porodila: hoch není jako děvče — „a nazvala jsem ji Marie: a svěřuji ji, jakož i pokolení její, ochraně tvé před ďáblem kamenovaným.“'),
(330, 3, 37, 'A přijmul Pán laskavě slib její a dal vzrůsti děcku krásným vzrůstem. A pečoval Zachariáš o dítě: a kdykoliv vešel k Marii ve svatyni, nalezl vedle ní potravu její. I řekl: „Marie, odkud dostáváš toto.“ Řekla: „Totoť přichází od Boha, neboť Bůh obdařuje živobytím koho chce, bez všeho počtu.“'),
(331, 3, 38, 'Tu jal Zachariáš modliti se, řka: „Pane můj, popřej mi dobrého potomstva z milosti své: ty zajisté rád vyslyšíš prosby modlících se:“'),
(332, 3, 39, 'A zavolali naň andělé, když stál takto ve svatyni, modle se: „Bůh zvěstuje ti narození Jana, jenž potvrzovatelem bude slova božího a velkým a ctnostným prorokem spravedlivým.“'),
(333, 3, 40, 'Řekl: „Pane můj, jakž dostane se mi syna, když dostihlo mne stáří žena má neplodna jest?“ Řekl anděl: „Takovým způsobem učiní Bůh, co chce.“'),
(334, 3, 41, 'Řekl: „Pane můj, učiň mi znamení.“ Řekl anděl: „Znamením tvým bude, že nebudeš po tři dny mluviti k lidu, vyjma posunky: a silně vzývej Pána svého a chval jej z večera i z rána.“'),
(335, 3, 42, 'A (vzpomeň), když řekli andělé Marii: „Marie, Bůh vyvolil a očistil tě a vyvolil tě mezi všemi ženami světů.'),
(336, 3, 43, 'Marie, budiž zbožnou vůči Pánu svému a padej na tvář svou a klekej s klekajícími.“'),
(337, 3, 44, 'Tyto jsou zvěsti pro tebe ze světa skrytého: tobě zjevujeme je, neboť tys nebyl mezi nimi, když vrhali losy stvoly rákosí o to, kdo má býti pěstounem Mariiným, aniž byla mezi nimi, když se o to hádali.'),
(338, 3, 45, 'A (vzpomeň), když řekli andělé Marii: „Marie, Bůh zvěstuje ti slovo své a jméno jeho bude Mesiáš, Ježíš, syn Mariin, a bude slavným ve světě tomto i v budoucím a jedním z blízkých Bohu.'),
(339, 3, 46, 'A on mluviti bude k lidem, jak v kolébce, tak i když bude dospělým, a bude jedním ze spravedlivých.“'),
(340, 3, 47, 'Řekla: „Jak budu míti syna, když nedotekl se mne muž.“ Řekl anděl: „Takovýmto způsobem Bůh tvoří, co chce: určí-li, co má se státi, řekne jen: ‚Staň se,‘ a stane se.'),
(341, 3, 48, 'A naučí jej Knize a moudrosti, Zákonu Mojžíšovu a Evangeliu.'),
(342, 3, 49, 'A bude prorokem poslaným k synům Israele. Řekne: ‚Zajisté přišel jsem k vám se znamením od Pána vašeho: z hlíny utvořím vám podobu ptáka a dechnu na ni a stane se živým ptákem z dopuštění božího. A uzdravovati budu slepé a malomocné, a křísiti budu mrtvé z dopuštění božího: a řeknu vám, co jíte a co skrýváte v domech svých. V tom všem zajisté budou vám znamení, jste-li věřícími.'),
(343, 3, 50, 'Přišel jsem, aby potvrzeno bylo, což psáno jest v Zákoně seslaném přede mnou a abych dovolil vám něco z toho, co dříve zapovězeno bylo vám: a přišel jsem k vám se znamením od Pána vašeho; pročež bojte se Boha a buďte poslušni.'),
(344, 3, 51, 'V pravdě, Bůh jest Pánem mým a Pánem vaším: pročež služte mu. Totoť jest stezka přímá.‘“'),
(345, 3, 52, 'A když Ježíš pocítil nevíru jejich, řekl: „Kdož pomocníky mými budou ve vedení lidí k Bohu?“ Řekli učedníci jeho: „My budeme pomocníky Boha: uvěřili jsme v Boha a ty dosvědč, že do vůle jeho jsme odevzdáni.'),
(346, 3, 53, 'Pane náš, uvěřili jsme v to, co seslal jsi a následujeme proroka: a zapiš nás mezi ty, kdož to dosvědčují.“'),
(347, 3, 54, 'Kladli mu pak léčky a Bůh kladl jim léčky, neboť Bůh nejobratnějším jest v kladení léček.'),
(348, 3, 55, 'A (vzpomeň), když řekl Bůh: „Ježíši, způsobím, abys na pohled zemřel: pak vezmu tě k sobě na výsosti a zbavím tě zodpovědnosti za nevěřící. Ty pak, kteří tě následují povýším nad nevěřící až do dne zmrtvýchvstání. Pak učiním, že ke mně se navrátíte a rozsoudím mezi vámi o tom, v čem jste se rozcházeli.'),
(349, 3, 56, 'Ty pak, kdož neuvěřili, trestati budu trestem přísným na světě tomto i v budoucím a nebude jim zastánců.'),
(350, 3, 57, 'Těm pak, kdož uvěřili a konali dobré skutky, dána bude odměna jejich: a Bůh nemiluje nepravostných.'),
(351, 3, 58, 'Taková jsou znamení, jež vnukáme ti, a napomenutí moudré.“'),
(352, 3, 59, 'Zajisté Ježíš jest u Boha podobným Adamovi. Bůh stvořil jej z prachu a řekl mu pak: „Stan se,“ a stal se.'),
(353, 3, 60, 'Pravda tato přichází od Pána tvého: nebudiž tedy z pochybovačných.'),
(354, 3, 61, 'Těm, kdož hádati se budou s tebou o tom poté, když přislo ti vědění, rci: „Pojďte, zavolejme syny naše a syny vaše, ženy naše a ženy vaše, nás i vás. Pak volati budeme k Bohu a svolávati zlořečení jeho na hlavy lhářů.“'),
(355, 3, 62, 'Vyprávění toto jest skutečnou pravdou a není jiného Boha, kromě Boha: a Bůh zajisté mocný jest a moudrý.'),
(356, 3, 63, 'Však odvrátí-li se, dobře: Bůh zajisté zná zle činící.'),
(357, 3, 64, 'Rci: „Vy, kdož obdrželi jste Knihu: pojďte, promluvme spolu spravedlivé slovo, totiž: že nebudeme sloužiti, vyjma Bohu, že nebudeme dávati mu společníků nijakého druhu a že nikdo z nás nebude bráti si jiných pánů vedle Boha.“ Však odvrátí-li se, rci: „Dosvědčte sami, že my jsme odevzdáni do vůle boží.“'),
(358, 3, 65, 'Vy, kdož obdrželi jste Knihu: proč přete se ohledně Abrahama, když Zákon a Evangelium seslány byly teprve po něm? Což nechápete?'),
(359, 3, 66, 'Hle, zde hádáte se o věci, o kterých něco víte: ale proč hádáte se ohledně věcí, o kterých nevíte ničeho? Bůh ví, však vy nevíte!'),
(360, 3, 67, 'Abraham nebyl ani Židem, ani křesťanem, nýbrž byl pevný ve víre a odevzdán do vůle boží: a nebyl z těch, kteří klaní se mnoha bohům.'),
(361, 3, 68, 'Nejblíže k Abrahamovi jsou zajisté ti, kteří následují jeho příkladu: a tak činí prorok a ti, kteří uvěřili. Búh zajisté zastáncem jest věřících.'),
(362, 3, 69, 'Někteří z těch, kdož obdrželi Knihu, rádi by vás zavedli s pravé cesty: však zavádějí pouze sebe samy, aniž by toho pozorovali.'),
(363, 3, 70, 'Vy, kteří obdrželi jste Knihu: proč nevěříte ve znamení boží, jichž sami byli jste svědky?'),
(364, 3, 71, 'Vy, kteří obdrželi jste Knihu: proč odíváte pravdu lží a proč vědomé tajíte pravdu?'),
(365, 3, 72, 'Někteří z lidu, obdrževšího Knihu, řekli: „Věřte v to, co sesláno bylo věřícím na počátku dne a zapřete to s večerem: takovým způsobem odpadnou snad od víry své.'),
(366, 3, 73, 'A věřte pouze těm, kteří následují víru vaši.“ Rci: „Zajisté pravé vedení jest jedině vedení boží: totiž, aby druhým dáno bylo totéž, co dáno bylo vám.“ Či budou hádati se s vámi před Pánem vaším? Rci: „Milost jest v rukou božích; obdařuje jí, koho chce. A Bůh nesmírný jest v obdarování svém, vševědoucí.'),
(367, 3, 74, 'Zvlášť vyznamenává milosrdenstvím svým toho, koho chce: a Bůh vládne milostí nezměrnou.“'),
(368, 3, 75, 'Jsou někteří mezi těmi, kdož obdrželi Knihu, jimž svěřís-li qintár, vrátí ti jej: a jsou někteří mezi nimi, jimž svěříš-li denár, nevrátí ti ničeho, leda že bys nad nimi úporně stál. To proto, že řekli: „Nejsme vázáni k dodržení slova lidu obecnému“ a tím vědomě prohlašují o Bohu lživé tvrzení.'),
(369, 3, 76, 'Naopak, kdo plní závazky své a bojí se Boha, sezná, že Bůh miluje bohabojné.'),
(370, 3, 77, 'Ti, kteří za smlouvu svou s Bohem a přísahy své kupují věc ceny nepatrné, těm nebude údělu ve světě budoucím a nepromluví k nim Bůh, aniž shlédne na ně v den zmrtvýchvstání a dá jim odpuštění: trest bolestný je očekává.'),
(371, 3, 78, 'Jsou někteří mezi nimi, kdož překrucují jazyky svými Knihu, abyste domnívali se, že to, co říkají, jest z Knihy: však není to vzato z Knihy. A říkají, že jest to od Boha, ale není to od Boha: tak říkají o Bohu lež a to vědomě.'),
(372, 3, 79, 'Nepřístojí muži, kterému dána byla Kniha a soudnost a dar prorocký, aby říkal lidem: „Buďte služebníky mými vedle Boha.“ nýbrž spíše: „Buďte služebníky Boha, ježto znáte Knihu a ježto jste v ní poučeni.“'),
(373, 3, 80, 'Bůh neporoučí vám, abyste vzali si anděly a proroky za pány. Jakž mohl by nakazovatt vám, abyste stali se nevěřícími poté, když odevzdali jste se do vůle jeho?'),
(374, 3, 81, 'A když učinil Bůh smlouvu s proroky, řekl: „Totoť jest Kniha a moudrost, které vám dávám. Přijde pak prorok k vám, aby potvrdil Knihu před ním vám seslanou. Uvěřte jistě v něho a jiste mu pomáhejte.“ Řekl pak: „Jste rozhodnuti a přijímáte smlouvu za těchto podmínek?“ Řekli: „Jsme rozhodnuti.“ Řekl: „Buďtež tedy svědky toho a já též jsem s vámi jedním ze svědků.'),
(375, 3, 82, 'A kdožkoli poté odvrátí se, bude jedním z nepravostných.“'),
(376, 3, 83, 'Chtějí žádati si jiného náboženství, když do vůle boží odevzdalo se vše, což na nebi jest i na zemi, dobrovolně i nedobrovolně, a k němu se navrátí?'),
(377, 3, 84, 'Rci: „Uvěřili jsme v Boha a v to, co sesláno bylo nám a co sesláno bylo Abrahamovi, Ismaelovi, Isákovi a Jakubovi a kmenům: a co dáno bylo Mojžíšovi a Ježíšovi a prorokům Pánem jejich; a nečiníme rozdílu mezi nimi a do vůle boží jsme odevzdáni.“'),
(378, 3, 85, 'A kdo žádá si jiného náboženství na místě Islámu, toto nebude přijato od něho a ve světě budoucím bude mezi těmi, kdož špatně pochodí.'),
(379, 3, 86, 'Jak vésti může Bůh stezkou pravou lid, jenž poté, když byl uvěřil a dosvědčil pravdivost prorokovu a poté, když došly ho jasné důkazy toho, stal se nevěřícím? Zajisté Bůh nevede stezkou pravou nepravostné.'),
(380, 3, 87, 'Těchto odměnou bude zlořečení Boha a andělů a lidstva veškerého.'),
(381, 3, 88, 'To zůstane nad nimi věčně a nebude polehčeno jim v trestu jejich a zrak Boha nespočine na nich nikdy.'),
(382, 3, 89, 'Vyjma těch, kteří poté kajícně obrátí se a polepší se: neb Bůh zajisté velký jest v odpuštění, slitovný.'),
(383, 3, 90, 'Ti pak, kdož vrátili se k nevíře potom, když byli uvěřili a ještě více utvrdili se v nevíře své: pokání jejich nikdy nebude přijmuto. Tito zůstaveni budou bludu svém.'),
(384, 3, 91, 'Od těch pak, kdož neuvěřili a zemřeli v nevíře své, nebude přijmuto výkupným ani tolik zlata, co by obsáhla země: údělem těchto budou muka bolestná a nebudou míti zastánců.'),
(385, 3, 92, 'Nedosáhnete ctnosti dříve, dokud nebude rozdávati z toho, co vám nejmilelším: a cožkoliv dáte, zajisté Bůh o tom ví.'),
(386, 3, 93, 'Všechna potrava dovolena byla synům Israele, vyjma té, kterou Israel sám sobě byl zapověděl před tím, než seslán byl mu Zákon. Rci: „Přineste tedy Zákon a čtěte z něho, jste-li pravdomluvní.“'),
(387, 3, 94, 'Kdožkoli poté vymyslí si lež o Bohu tenť jest z nepravostných.'),
(388, 3, 95, 'Rci: „Bůh jest pravdomluvný: pročež následujte víru Abrahama, muže pravé víry, jenž nepatřil k těm, již slouží mnoha bohům.'),
(389, 3, 96, 'Prvním chrámem, jenž založen byl pro lidstvo, jest zajisté onen v Bekce: chrám požehnaný, jenž vedením jest veškerenstvu.'),
(390, 3, 97, 'V něm jasná jsou znamení: v něm místo jest Abrahamovo: a kdož vstoupí tam, jest bezpečen. A povinností lidstva k Bohu jest pouť do chrámu tohoto, komukoliv lze ji vykonati. Pokud se týče těch, kdež neuvěřili — v pravdě Bůh jest tak bohat, že obejíti se může bez stvoření veškerého.'),
(391, 3, 98, 'Rci: „Vy, kteříž obdrželi jste Knihu, proč zapíráte znamení božích Bůh zajisté jest svědkem toho, co konáte.“'),
(392, 3, 99, 'Rci: „Vy, kteříž obdrželi jste Knihu, proč odpuzujete věřící od stezky boží? Rádi byste křivou ji učinili ač doznati musíte, že jest pravá: a Bůh zajisté není lhostejným k tomu, co konáte.“'),
(393, 3, 100, 'Vy, kteří jste uvěřili: budete-li poslouchati poté, když jste byli uveřili, některé z těch, kdož obdrželi Knihu, tito způsobí, že stanete se nevěrícími.'),
(394, 3, 101, 'Však jak můžete státi se (zas) nevěřícími, kdyz předříkávána jsou vám znamení boží a prorok boží jest mezi vámi? Ten, kdo pevně přidržuje se Boha, veden jest ke stezce přímé.'),
(395, 3, 102, 'Vy, kteří jste uvěřili, bojte se Boha pravou bázní a neumírejte jinak, než odevzdáni do vůle boží.'),
(396, 3, 103, 'A přidržujte se pevně provazu Boha všichni a neodtrhujte se od něho nikdy: a připomínejte si milosrdenství prokázané vám Bohem, jak spojil opětně srdce vaše, když byli jste si nepřáteli a z milosti jeho stali jste se opět bratry: byli jste na pokraji propasti ohnivé a on odtáhl vás od ní. Takovým způsobem objasňuje vám Bůh znamení svá, abyste dobře byli vedeni.'),
(397, 3, 104, 'A aby stal se z vás národ, volající druhé k dobru, nakazující skutky spravedlivé a zabraňující skutkům špatným. Takovému národu zajisté dobře se povede.'),
(398, 3, 105, 'A nebuďte jako ti, kdož rozpadli se a dali se do hádek poté, když byla jim přišla jasná znamení: těmto trest velký jest přichystán,'),
(399, 3, 106, 'v onen den, kdy zbělejí některé tváře a některé zčernají. Těm, jichž obličeje byly zčernaly, řekne Bůh: „Vy tedy stali jste se neveřícími poté, když jste byli uvěřili? Okuste nyní trestu za nevíru svou!“'),
(400, 3, 107, 'Však ti, jichž tváře byly zbělely, obklopeni budou milosrdenstvím Boha a v něm přebývati budou věčně.'),
(401, 3, 108, 'Taková jsou znamení Boha: předříkáváme ti je v pravdě, a Bůh zajisté nechce utiskovati lidstvo.'),
(402, 3, 109, 'Bohu přináleží vše, co na nebi jest i na zemi a k Bohu navracují se všechny věci.'),
(403, 3, 110, 'Jste nejlepším národem, jenž kdy povstal mezi lidstvem: nakazujete skutky spravedlivé a zabraňujete skutkům zlým, a věříte v Boha: a kdyby ti, kterí obdrželi Knihu, byli uvěřili, bylo by to bývalo lepším pro ně. Někteří z nich jsou věřící, ale většina jich jest špatna.'),
(404, 3, 111, 'Uškodí vám pouze nepatrně a budou-li bojovati s vámi, brzy ukáží vám záda svá; a pak nebude jim pomocníků.'),
(405, 3, 112, 'Pohanou zasaženi budou, kdekoliv se zastaví, nebudou-li hledati spojení s Bohem a spojení s lidmi. A přivodili na se hněv boží a zasaženi jsou chudobou, to proto, že neuvěřili ve znamení boží a ubíjeli proroky bezprávně, proto, že neposlušni byli a páchali nepravosti.'),
(406, 3, 113, 'Nejsou stejnými všichni mezi těmi, kteří obdrželi Knihu: jsou mezi nimi lidé přímí, již opakují znamení boží po celé noci a klaní se Bohu.'),
(407, 3, 114, 'Věří v Boha a v den poslední a nakazují skutky spravedlivé a zabraňují skutkům zlým a předhánejí se v konání dobrých skutků: tito zajisté jsou ze spravedlivých.'),
(408, 3, 115, 'A cokoli dobrého učiníte, odměna za to nebude vám zapřena: a Bůh dobře zná ty, kdož bojí se ho.'),
(409, 3, 116, 'Těm pak, kdož nevěří, neprospějí pranic majetky a děti jejich proti Bohu: úděIem jejich bude oheň a v něm přebývati budou věčně.'),
(410, 3, 117, 'To, co rozdávají oni almužnou v tomto životě pozemském, podobno jest ledovému větru, jenž zasáhl a zničil role těch, kteří nespravedlivi byli vůči sobě, samým: Bůh nečiní jim útisku, však oni sami sobě škodí.'),
(411, 3, 118, 'Vy, kteří jste uvěřili, nevcházejte v důvěrné styky, vyjma mezi sebou: nevěřící jistě by vás pokazili, neboť žádají si zkázy vaší. Zášť zjevnou stala se již v ústech jejich, však daleko horším jest to, co skrývají nitra jejich. Důkazy, toho ukázali jsme vám již jasně, dovedete-li chápati.'),
(412, 3, 119, 'Hle, vy jste ti, kdož milují je, však oni nemilují vás: vy věříte v Knihu celou a oni, když potkají vás, praví: „Uvěřih jsme,“ ale jen co poodejdou stranou, zlostí hryzou si prsty. Rci: „Zemřete ve vzteku svém: Bůh zajisté ví, co skrýváte v hlubinách srdcí svých.“'),
(413, 3, 120, 'Potká-li vás co dobrého, hněvají se: a potká-li vás co zlého, mají radost z toho. Však budete-li trpěliv a budete-li báti se Boha, neuškodí vám v nejmenším vychytralost jejich: neboť Bůh dobře zpraven jest o Konání jejich.'),
(414, 3, 121, 'A (vzpomeň), když vyšel\'s z domu svého zrána abys připravil pro věřící pole k bitvě: a Bůh slyšel a věděl o všem.'),
(415, 3, 122, 'Když dva oddíly vaše byly plny obav a počaly ztráceti mysl: a Bůh postavil se na ochranu obou z nich. V ochranu Boha tedy nechť poroučí se věřící.'),
(416, 3, 123, 'Bůh pomohl vám již v boji u Bedru, kdež byli jste slabšími: protož bojte se Boha, abyste mu byli vděčnými.'),
(417, 3, 124, 'Tehdy řekl jsi věřícím: „Zdaž nedostačí vám, když rozmnoží Pán váš vojsko vaše o tři tisíce andělů, sestoupivších s nebe?'),
(418, 3, 125, 'Zajisté: však vytrváte-li a budete báti se Boha, tu když nepřítel prudce udeří na vás, pošle vám Bůh na pomoc pět tisíc andělů vybraných.“'),
(419, 3, 126, 'Toto učinil vám Bůh pouze radostnou zvěstí, aby tak upokojena byla srdce vaše: a vítězství přichází jedině od Boha, Mocného, Moudrého,'),
(420, 3, 127, 'aby posekáni byli do jednoho nevěřící, aneb povaleni a převráceni v pádu a porážce úplné.'),
(421, 3, 128, 'Netýká se tebe, přijmuto-li bude pokání jejich, aneb potrestáni-li budou Bohem: neboť oni v pravdě jsou nepravostni.'),
(422, 3, 129, 'Bohu přináleží, cožkoliv na nebi jest i na zemi: odpouští, komu chce a tresce, koho chce: a Bůh velký jest v odpouštění, slitovný.'),
(423, 3, 130, 'Vy, kteří jste uvěřili: neživte se lichvou, zdvojnásobujíce neustále půjčky, ale hojte se Boha, aby dobře vedlo se vám.'),
(424, 3, 131, 'A bojte se ohně, jenž přichystán jest nevěřícím,'),
(425, 3, 132, 'a poslušni buďte Boha a proroka jeho, abyste nalezli slitování.'),
(426, 3, 133, 'A spěšte o překot, abyste dosáhli odpuštění Pána svého a ráje, jenž rozsahem stejný jest jako nebe i země a jenž přichystán jest bohahojným,'),
(427, 3, 134, 'kteří rozdávají almužnu v dobách dobrých i zlých, přemáhají hněv svůj a odpouštějí druhým, Bůh zajisté miluje ty, kdož dobře činí.'),
(428, 3, 135, 'Kteří když byli dopustili se špatnosti neb něčeho zlého vůči sobě samým, vzpomenou na Boha a prosí jej za odpuštění hříchů svých — a kdož jiný odpouštěti může hříchy kromě Boha? — a nesetrvávají v tom, co byli učinili, když vědí, že jest to hříšno.'),
(429, 3, 136, 'Odměnou těchto bude odpuštění Pána jejich a zahrady, pod nimiž řeky tekou, v nichž přebývati budou věčně. Jak krásná jest odměna těch, kteří konají dobro!'),
(430, 3, 137, 'Již před vámi učiněny byly výstražné příklady. Projděte zemí a vizte, jaký konec vzali ti, kteří nazývali proroky lháři!'),
(431, 3, 138, 'Totoť jasné jest prohlášení lidstvu a vedení i napomenutí bohahojným.'),
(432, 3, 139, 'Neztrácejte mysli, aniž se rmuťte, neboť vy dostanete se na vrch, budete-li věřícími.'),
(433, 3, 140, 'Utrpíte-li poranění, pomněte, že stejná zranění utrpěli lidé před tím: podobným dnům úspěchu či neúspěchu dáváme střídati se mezi lidmi, aby tak seznal Bůh ty, kdož uvěřili mezi vámi a mučedníky si vybral mezi vámi: a Bůh nemiluje nepravostných.'),
(434, 3, 141, 'A aby Bůh vzal na zkoušku ty, kteří uvěřili a zahladil nevěřící.'),
(435, 3, 142, 'Zdaž domnívali jste se, že vstoupíte do ráje dříve, než zví Bůh, kdo zdatnými byli v boji a než sezná, kdo byli vytrvalými?'),
(436, 3, 143, 'Vždyť přáli jste si smrti dříve, než setkali jste se s ní tváří v tvář: nuže, viděli jste ji a na své vlastní oči — a ted váháte!'),
(437, 3, 144, 'Mohammed jest pouze prorok a před ním již byli proroci: a kdyby zemřel, aneb byl zabit, odvrátili byste se od pravé víry? Ten, kdo odvrátí se, neuškodí Bohu v nejmenším: a Bůh odměňuje ty, kteří jsou mu vděčni.'),
(438, 3, 145, 'Nikdo nezemře, vyjma z dovelení božího, podle knihy, jež určuje přesně lhůtu života. Kdožkoli žádá si odměny v tomto světě, bude mu dána: kdožkoli pak žádá si odměny ve světě budoucím, dáme mu ji: a Bůh odměňuje ty, kteří jsou mu vděčni.'),
(439, 3, 146, 'Jak mnohý prorok bojoval s odpůrci, s nimiž byly zástupy četné, však nedali se zastrašiti jakýmkoli protivenstvím, potkavším je v boji na stezce boží, aniž ochabli či zbaběle poddali se. A Bůh miluje ty, kdož pevně vytrvávají.'),
(440, 3, 147, 'Říkali jen: „Pane náš, odpusť nám viny naše a pochybení v poslání našem: a upevni kroky naše a popřej nám vítězství nad lidem nevěřícím.“'),
(441, 3, 148, 'A Bůh dal jim odměnu v tomto světě a krásnou odměnu ve světě budoucím. A Bůh miluje ty, kdož dobře činí.'),
(442, 3, 149, 'Vy, kteří jste uvěřili, poslechnete-li nevěřící, přemluví vás k návratu do bludů vašich a upadnete, ztrativše všechno.'),
(443, 3, 150, 'Ale za to Bůh jest ochranitelem vaším a on zajisté nejlepším jest ze všech ochránců.'),
(444, 3, 151, 'Vrhneme hrůzu v srdce nevěřících za to, že Bohu dávali společníky v božství, aniž by byli obdrželi k tomu splnomocnění s hůry: přebýváním jejich bude oheň a jak hrozné bude bydliště nepravostných!'),
(445, 3, 152, 'Bůh splnil vám již slib svůj, když z dopuštění jeho porazili jste je, dokud neztratili jste odvahu a neopřeli jste se rozkazu a byli jste neposlušnými poté, když byl ukázal vám prorok to, po čem jste toužili. Někteří z vás žádali si odměny pozemské, někteří pak žádali si odměny v životě budoucím: Bůh obrátil vás na útěk před nepřítelem, aby vás zkoušel, však nyní již odpustil vám, neboť Bůh jest nejvýš milostiv vůči věřícím.'),
(446, 3, 153, 'Když prchali jste a nedbali na nikoho a prorok volal vás v zadu k boji: Bůh odplatil vám utrpením za utrpením, abyste nermoutili se pro kořist, jež vám byla ušla, aniž pro nehody, potkavší vás. A Bůh dobře zpraven jest o tom, co konáte.'),
(447, 3, 154, 'Po utrpeních pak seslal vám Bůh bezpečí a dřímota padla na část vás. Druhá část, ponuknuta vlastními náruživostmi, zabrala se v myšlenky nespravedlivé a pošetilé o Bohu, řkouc: „K čemu jest nám to všechno platno?“ Rci: „Všechno v naší věci záleží od Boha.“ Tajili pak v nitrech svých, co nechtěli ti doznati, řkouce: „Kdyby nám tato věc byla k čemu platna, nikdo z nás nebyl by býval zde zabit.“ Kdybyste byli zůstali v domech svých, ti, kterým určeno bylo, aby byli zabiti, byli by vyšli ven, aby uložili se na stejném lůžku, na němž nyní odpočívají; aby Bůh vyzkoušel tajnosti niter vašich a odhalil to, co tajíte v srdcích svých, neboť Bůh dobře ví o tom, co skryto jest v nitru lidí.'),
(448, 3, 155, 'Ti z vás, kteří dali se na útěk v den sražení se obou vojů, svedeni byli Satanem za trest za nějaké poklesky své: však nyní již odpustil jim Bůh, neboť Bůh zajisté velký jest v odpouštění, slitovný.'),
(449, 3, 156, 'Vy, kteří jste uvěřili, nebuďte jako nevěřící, již říkají o bratřích svých, kteří vydali se na cesty po světě, aneb vyšli do války: „Kdyby s námi byli zůstali, nebyli by bývali zemřeli, nebo nebyli by bývali zabiti.“ Bůh chtěl, aby zarmoucena tím byla srdce jejich. Bůh dává život a přivodí smrt: a Bůh pozoruje skutky vaše.'),
(450, 3, 157, 'A budete-li zabiti, aneb zemřete-li na stezce boží, pak zajisté odpuštění Boha a milosrdenství jeho lepší jest všech statků, jež shromažďujete.'),
(451, 3, 158, 'Ať již zemřete, anebo jste zabiti, zajisté u Boha budete shromážděni.'),
(452, 3, 159, 'O milosrdenství božím mluvil\'s k nim měkkou řečí, a kdybys byl býval přísným a tvrdého srdce, byli by se bývali odtrhli od tebe. Tudíž odpouštěj jim a pros za odpuštění pro ně a raď jim v záležitostech války, a když jsi byl k něčemu se rozhodl, spoléhej na Boha, neboť Bůh miluje ty, kteří naň, spoléhají.'),
(453, 3, 160, 'Pomáhati-li vám bude Bůh, nikdo nezvítězí nad vámi, však opustí-li vás, kdo bude pomocníkem vaším poté? Pročež na Boha necht spoléhají věřící,'),
(454, 3, 161, 'Nebude to prorok, jenž ošidí vás: však ten, jenž bude šiditi, objeví se s tím, oč ošidil, v den zmrtvýchvstání. Pak odměněna bude každá duše po zásluze své a nikdo nebude zkrácen.'),
(455, 3, 162, 'Zdaž ten, kdo jednal podle vůle boží, bude roveň tomu, jenž přivedl na se hněv boží a jehož bydlištěm bude peklo? Zlá bude jeho cesta!'),
(456, 3, 163, 'Různé stupně odměny budou u Boha jim dány: a Bůh dobře pozoruje skutky jejich.'),
(457, 3, 164, 'Laskavým byl jistě Bůh k věřícím, vzbudiv jim proroka z jejich středu, aby předčítal jim znamení jeho a očišťoval je od hříchů jejich a učil je Knize a moudrosti: ač před tím byli ve zjevném bludu.'),
(458, 3, 165, 'A když postihlo vás neštěstí, jemuž podobného dali jste před tím vy dvojnásob pocítiti, řekli jste: „Čím to přijde?“ Rci: „Od vás samých.“ Bůh zajisté jest všemocný.'),
(459, 3, 166, 'To, co postihlo vás v den srážky dvou vojů, postihlo vás z dopuštění božího, aby Bůh dobře seznal, kdo jest věřícím,'),
(460, 3, 167, 'a aby seznal, kdo jest pokrytcem. Neb když řečeno jim bylo: „Pojďte a bojujte na stezce boží a odrazte nepřítele,“ řekli: „Kdy bychom vyznali se ve válčení, následovali bychom vás.“ V den onen mnozí z nich blíže byli k nevíře než k víře, neb ústa jejich mluvila, co nebylo v srdcích jejich: však Bůh dobře ví, co skrývají.'),
(461, 3, 168, 'Ti, kdož říkali o bratřích svých, sedíce doma: „Kdyby nás byli poslechli, nebyli by bývali zabiti.“ Rci: „Nuže tedy, ochraňte se před smrtí, mluvíte-li pravdu.“'),
(462, 3, 169, 'Nepovažujte ty, kteří ubiti byli na stezce boží, za mrtvé. Nikoliv, oni žijí u Pána svého, dobře jsouce zaopatřeni.'),
(463, 3, 170, 'Radujíce se z toho, co Bůh dal jim v štědrosti své a naplněni radostí, že těm, kdož následují ve šlépějích jejich, však dosud jich nedostihli, nebude třeba báti, aniž rmoutiti se.'),
(464, 3, 171, 'Naplněni radostí nad dobrodiním Boha a štědrostí jeho: neboť Bůh zajisté nepřipustí, aby ztratila se odměna věřících.'),
(465, 3, 172, 'Těch, kdož poslušni byli Boha a proroka poté, když postiženi byli neštěstím, činili dobro a báli se Boha, velká bude odměna.'),
(466, 3, 173, 'Těch, jimž řekli lidé: „Lid shromažďuje se proti vám, bojte se ho“ a jichž víra tím romnožena byla a řekli: „Bůh dostačí nám: onť nejlepším jest ochráncem.“'),
(467, 3, 174, 'I vrátili se zahrnuti přízní a štědrostí Boha a nic zlého se jim nepřihodilo: a následovali to, co schvaluje Bůh. Bůh pak velký jest v štědrosti své.'),
(468, 3, 175, 'Satan často straší stoupence své: však nebojte se jich, nýbrž mne bojte se, jste-li věřícími.'),
(469, 3, 176, 'Nermuť se pro ty, kteří předhánějí se ve spěchu k nevíře: tito zajisté v nejmenším neuškodí Bohu, Bůh žádá si, aby neměli od něho podílu v životě budoucím a velký trest bude jim údělem.'),
(470, 3, 177, 'Ti, kteří koupili nevíru za víru, zajisté v nejmenším neuškodí Bohu a bolestný trest bude jim údělem.'),
(471, 3, 178, 'Nechť nedomnívají se nevěřící, že ve prospěch jest jim dlouhý věk, kterého jim popřáváme, nýbrž popřáváme jim dlouhého věku jen proto, aby rozmnoženy byly zlí skutkové jejich. A trest potupní bude jim údělem.'),
(472, 3, 179, 'Nezamýšlí Bůh ponechati věřící v témže stavu, němž se nalézáte; neustane, dokud neoddělí zlé od dobrých. Aniž zamýšlí Bůh odhaliti vám věci skryté; k tomu Bůh volí, koho chce z proroků svých. Věřte tedy v Boha a v proroky jeho: budete-li věřiti a báti se Boha, obdržíte odměnu velkou.'),
(473, 3, 180, 'Nechť nedomnívají se ti, kdož lakotí s tím, co dáno bylo jim Bohem ze štědrosti jeho, že v prospěch jim to bude: naopak, ke skodě jim to bude. To, čím lakotili, chomoutem jim bude v den zmrtvýchvstání: a Bohu patří dědictví nebes i země a Bůh dobře zpraven jest o skutcích vašich.'),
(474, 3, 181, 'Zajisté slyšel Bůh řeč oněch, kdož pravili: „Věru, Bůh chud jest a my bohati.“ Zapíšeme si dobře slova jejich a jich bezprávné zabíjení proroků a řekneme: „Okuste trápení ohně,'),
(475, 3, 182, 'za to, co ruce vaše poslaly před vámi: a Bůh zajisté není utiskovatelem služebníků svých.“'),
(476, 3, 183, 'Těm, kdož praví: „V pravdě doporučil nám Bůh, abychom nevěřili proroku, dokud nepředvede nám oběť, kterou ztráví oheň s nebe“, rci: „Vždyť přišli k vám proroci přede mnou s jasnými důkazy i s oním, o kterém mluvíte. Proč tedy zabili jste je? Řekněte, jste-li pravdomluvnými!“'),
(477, 3, 184, 'Nazývají-li tě lhářem, (nedbej): neboť i před tebou lháři nazývali proroky, kteří přišli s jasnými důkazy a s Písmem a Knihou osvícení.'),
(478, 3, 185, 'Každá duše okusí smrt: však odměny své obdržíte teprve v den zmrtvýchvstání, A kdožkoli vyhne se ohni a vstoupí do ráje, bude šťasten: a život tohoto světa jest pouze přeludným požitkem.'),
(479, 3, 186, 'Zajisté zkoušeni budete ve statcích svých i v sobě samých: a zajisté přeslechnete od těch, kdož obdrzeli Knihu před vámi, a od těch, kteří dávají Bohu společníky, mnoho věcí zlých; však budete-li trpělivi a budete se báti Boha, shledáte, že jest to jednou z věcí předurčených.'),
(480, 3, 187, 'Když učinil Bůh smlouvu s těmi, jimž dána byla Kniha, řekl: „Zcela jistě vysvětlujte ji lidu a nezatajujte jí“: však oni zahodili ji za záda svá a koupili za ni věc malé ceny. Hanba tomu, co za ni byli koupili!'),
(481, 3, 188, 'Nedomnívejte se, že ti, kdož radují se z toho, co provedli, a rádi nechávají se chváliti za to, co nevykonali — nedomnívejte se, že uniknou trestu. Údělem jich bude trest bolestný!'),
(482, 3, 189, 'Bohu přináleží panství nebes i země a nade všemi věcmi jest moc jeho.'),
(483, 3, 190, 'Věru ve stvoření nebes i země a ve střídání noci se dnem znamení jsou těm, kdož dovedou chápati.'),
(484, 3, 191, 'Těm, kteří vzývají Boha stojíce, sedíce i ležíce a přemítají o stvoření nebes i země, řkouce: „Pane náš, věru nestvořil\'s toto vše zbytečně: při chvále tvé, nikoliv! I zachovejž nás před trestem ohně.'),
(485, 3, 192, 'Pane náš, každého, jejž uvrhneš do ohně, pokryješ tak potupou: a nebude zastánců nepravostným.'),
(486, 3, 193, 'Pane náš, v pravdě slyšeli jsme hlas volajícího, jenž volal nás k víře: ‚Věřte v Pána svého,‘ a uvěřili jsme. Pane náš, odpusť nám tedy viny naše, smaž nepravosti naše, a dej zemříti nám jako spravedlivým.'),
(487, 3, 194, 'Pane náš, dejž nám, co slíbil\'s nám proroky svými a nezarmucuj nás v den zmrtvýchvstání: neboť zajisté není, abys nedodržel slibu svého.“'),
(488, 3, 195, 'A vyslyšel je Pán jejich, řka: „Jáť zajisté nedopustím, aby ztraceno bylo dílo kohokoliv z vás, ať muže, či ženy, neb jedni vycházíte z druhých. A ti, kdož vystěhovali se z vlasti své a vypuzeni byli z obydlí svých a trpěli pro mne a bojovali, nebo padli: těchto zajisté smažu hříchy a uvedu je v zahrady, pod nimiž řeky tekou.“ To odměnou bude jim od Boha: a u Boha zajisté nejlepší jest odměna.'),
(489, 3, 196, 'Nedej zmýliti se zdánlivou svobodou nevěřících v zemi:'),
(490, 3, 197, 'pouze na krátko popřána jim bude a pak obydlím jich bude peklo: prabídné bude to místo odpočinku!'),
(491, 3, 198, 'Však údělem těch, kdož báli se Pána svého, budou zahrady, pod nimiž řeky tekou: v nich přebývati budou věčně. Takové bude obydlí jejich u Boha a cokoliv u Boha jest dobré jest pro spravedlivé.'),
(492, 3, 199, 'Mezi těmi, kteří obdrželi Knihu, jsou někteří, kdož věří v Boha a v to, co sesláno jím bylo vám, i v to, co sesláno bylo jim, ponižujíce se před Pánem svým: a nekupují za znamení boží věc ceny nepatrné. Pro tyto přichystána jest odměna u Pána jejich a Bůh zajisté rychlý jest v účtování.'),
(493, 3, 200, 'Vy, kteří jste uvěřili, vyčkávejte trpělivě a předstihujte se v trpělivosti navzájem: buďte pevni a bojte se Boha, aby dobře vedlo se vám.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Ó lidé, bojte se Pána svého, jenž stvořil vás z jedné duše, a z ní samé stvořil ženu její a z nich obou vzniknouti dal množství mužů a ŽEN: a bojte se Boha. v jehož jménu kladete si vzájemné otázky a ctěte lůna, jež nosila vás. Neboť zajisté Bůh dobře na vás dává pozor.'),
(495, 4, 2, 'A dávejte sirotkům cožkoliv jejich jest a nezaměňujte pěkné věci špatnými a neubírejte ničeho ze jmění jejich, abyste tím rozmnožili jmění svá: neboť toto jest velkým zločinem.'),
(496, 4, 3, 'Obáváte-li se, že byste nejednali spravedlivě se sirotky, pojměte z žen, které se vám líbí, jen dvě, tři neb čtyři za manželky, však obáváte-li se nějak, že byste nejednali spravedlivě, ožeňte se pouze s jednou, anebo s otrokyní, které pravice vaše dobyla. To usnadní vám vyvarování se nespravedlnosti.'),
(497, 4, 4, 'Dávejte ženám štědře jejich věno: a zlíbí-li se jim odstoupiti vám něco z něho, užijte toho ve zdraví a pohodlí.'),
(498, 4, 5, 'A nedávejte neschopným jmění svého, které Bůh svěřil vám jako zálohu k výživě jejich, nýbrž vydržujte je z něho a odívejte a mluvte k nim řečí laskavou.'),
(499, 4, 6, 'Zkoušejte schopnosti sirotků až dostihnou stáří, kdy mohou vstoupiti v manželství a shledáte-li, že mají zdravý smysl, vy plaťte jim jmění jejich. Však střezte se promarniti je přehnaným utrácením, dříve než byli vyrostli. Bohatý (poručník) nechť zdrží se od dotknutí se jmění sirotka: a kdo jest chudý, nechť béře z něho jenom poctivě. A když vyplácíte jim jmění jejich, dejte si to potvrditi svědky: a Bůh jest postačitelný súčtovatel.'),
(500, 4, 7, 'Mužům přináleží podíl z toho, co byli zanechali rodičové jejich a příbuzní: a ženám přináleží podíl z toho, co byli zanechali rodičové jejich a příbuzní: ať dědictví malé jest, aneb velké, nechť určen jest jim z něho podíl.'),
(501, 4, 8, 'Když při dělení pozůstalosti přítomni jsou příbuzní a sirotci a chuďasové, obdařte je něčím z ní a mluvte k nim řečí laskavou.'),
(502, 4, 9, 'A nechť bojí se utiskovati sirotky ti, kteří v případě, že by sami zanechali slabé potomky, strachovali by se o ně: nechť bojí se Boha a jsou přímí v řeči.'),
(503, 4, 10, 'Zajisté ti, kdož utrácejí jmění sirotků neprávě, krmí ohněm útroby své a ztráveni budou plameny jeho.'),
(504, 4, 11, 'Bůh nařizuje vám ohledně dětí vašich, abyste dávali hochovi podíl dvou děvčat, a kdyby bylo více děvčat, než dvě, nechť dány jsou jim dvě třetiny dědictví po otci: však je-li pouze jedno děvče, má dostati polovinu, a otec s matkou zemřelého mají každý dostati šestinu dědictví, zanechal-li zemřelý dítko, však nezanechal-li dítka a dědici jeho jsou rodiče, tehdy matce jeho přináleží třetina, a má-li bratry, tehdy matka jeho obdrží šestinu poté, když odkazy a dluhy zemřelého byly vyplaceny. Vy nevíte, který z rodičů neb dětí vašich jest bližším vám v prospěchu. Toto zákonem jest od Boha: Bůh zajisté vševědoucí jest a moudrý.'),
(505, 4, 12, 'Polovina z toho, co zanechaly ženy vaše, patří vám, nemají-li dítek: však mají-li děti, přináleží vám čtvrtina z toho, co zanechaly, poté, když vyplaceny byly odkazy a dluhy jejich. A jim patřiti bude čtvrtina z toho, co jste vy zanechali, nemáte-li dítek: však máte-li dítky, tedy patřiti bude jim osmina z toho, co zanechali jste poté, když vyplaceny byly odkazy vaše a dluhy. Učiní-li muž nebo žena dědicem svým vzdáleného příbuzného a má-li tento bratra neb sestru, tu každému z obou náleží šestina a je-li jich více, náleží jim dohromady třetina dědictví poté, když vyplaceny byly odkazy zemřelého a dluhy, aniž by tím bylo komu uškozeno. Toto příkazem jest vám od Boha: a Bůh vševědoucí jest a laskavý.'),
(506, 4, 13, 'Tyto jsou meze božích zákonů a kdo poslechne Boha a proroka jeho, uveden bude Bohem v zahrady, pod nimiž řeky tekou: v nich přebývati bude věčně a velká v tom bude blaženost.'),
(507, 4, 14, 'A kdo neposlechne Boha a proroka jeho a přestoupí meze zákona jeho, uvržen bude Bohem v oheň, v němž přebývati bude věčně a potupný bude jeho trest.'),
(508, 4, 15, 'Dopustí-li se některá z žen vašich smilstva, zavolejte ke svědectví proti nim čtyři z vás: a když tito dosvědčí proti nim, tehdy zavřete je v domech, dokud smrtí nezemřou, aneb Bůh nezjedná pro ně nějakého východiska.'),
(509, 4, 16, 'A dopustí-li se dva mužové mezi vámi smilstva, potrestejte je oba: však obrátí-li se kajícně a polepší, nechte je s pokojem, neboť Bůh rád popřává sluchu kajícným a jest slitovný.'),
(510, 4, 17, 'Bůh sám rozhodne o pokání těch, kdož páchali špatnost z nevědomosti a brzy poté káli se. Těmto odpustí Bůh: a Bůh zajisté jest vševědoucí, moudrý.'),
(511, 4, 18, 'Však nebude popřáno sluchu těm, kdož páchali nepravosti a teprve když některý z nich blízek byl smrti, řekl: „V pravdě, obracím se nyní kajícně k Bohu,“ aniž těm, kteří zemřou v nevíře. Pro tyto přichystali jsme trest bolestný.'),
(512, 4, 19, 'Vy, kteří jste uveřili; není dovoleno vám, abyste dědili po ženách svých proti vůli jejich, aniž zbraňovali jim (v provdání se), abyste odejmouti jim mohli část z toho, co dali jste jim před tím (věnem): vyjma, kdyby dopustily se dokázaného smilstva. Buďte laskavými a spravedlivými ve stycích s nimi: a pociťujete-li k nim odpor, možná, že pociťujete odpor k něčemu, v co Bůh vložil veliké dobro.'),
(513, 4, 20, 'Přejete-li si vyměniti ženu za jinou a dali jste prvé z nich qintár, nestrhujte z obnosu toho ničeho. Zdaž odejmouti chtěli byste jí co nespravedlivě a zjevným násilím?'),
(514, 4, 21, 'A jakž chtěli byste jí co odejmouti, když vešli jste v úzký vzájemný závazek a když ženy vaše přijmuly od vás pevnou smlouvu?'),
(515, 4, 22, 'A nebeřte si za manželky ženy svých otců, neboť smilstvem jest to, odporným a zlým zvykem: vyjma toho, co v minulosti se bylo událo.'),
(516, 4, 23, 'Zapovězeno jest vám bráti si za manželky vaše matky, vaše dcery, vaše sestry, vaše tety se strany otcovy i matčiny, vaše neteře se strany bratrovy i sestřiny, vaše kojné a vaše soukojenky, matky žen vašich, nevlastní dcery, jimž jste poručníky a jež zrodily se z žen, s nimiž jste souložili: však nesouložili-li jste s těmito, nebudete míti viny, když oženíte se s nimi; a ženy synů vašich, vyšlých z ledví vašeho, a dvě sestry: vyjma toho, co v minulosti se bylo událo. Zajisté Bůh velký jest v odpouštění, slitovný.'),
(517, 4, 24, 'Zapovězeny jsou vám též ženy provdané, vyjma těch, které padly do rukou vašich jako otrokyně: takový jest předpis pro vás od Boha. Dovoleno jest vám, vedle toho, abyste hledali si ženy pomocí jmění svého, poctivým způsobem, bez prostopášnosti. Těm, s nimiž jste souložili, dejte věno jim slíbené: toto jest povinností vaší. Však nebude vám počítáno za vinu to, na čem dohodnete se vzájemně vedle povinnosti vám předepsané. A Bůh jest vševědoucí, moudrý.'),
(518, 4, 25, 'Ten z vás, jenž není dosti majetným, aby se oženil se svobodnou, věřící ženou, nechť vezme si věřící dívku z těch, kterých dobyla si pravice vaše a Bůh nejlépe zná víru vaši. Vyšli jste jedni z druhých. A žeňte se s otrokyněmi pouze se svolením pánů jejich a dejte jim štědré věno: nechť jsou zachovalé, nikoli prostopášné a nechť nebéřou si milovníků. A když po provdání dopustí se smilstva, nechť určen jest jim trest poloviční trestu provdaných svobodnic: zákon tento jest pro ty, kteří obávají se, že by se prohřešili, (zůstavše neženatými): však zdržíte-li se trpělivě, bude to lepším pro vás. A Bůh jest velký v odpouštění, slitovný.'),
(519, 4, 26, 'Bůh přeje si vysvětliti vám jasně vůli svou a vésti vás stezkou oněch, kdož byli před vámi a přijmouti pokání vaše: a Bůh vševědoucí jest a moudrý.'),
(520, 4, 27, 'A Bůh přeje si přijmouti pokání vaše, však ti, kteří následují náruživosti své, chtějí, abyste velkým sklonili se sklonem.'),
(521, 4, 28, 'Bůh přeje si ulehčiti vám břímě vaše, neboť člověk stvořen byl slabým.'),
(522, 4, 29, 'Vy, kteří jste uvěřili, nepromrhávejte jmění svých mezi sebou způsobem špatným, nýbrž jen obchodujte na základě vzájemné dohody: a nezabíjejte sebe samy; Bůh zajisté jest vůči vám slitovný.'),
(523, 4, 30, 'Kdožkoliv učiní tak ze zlomyslnosti a špatnosti, za potravu dáme jej ohni: a snadným jest to Bohu!'),
(524, 4, 31, 'Vyvarujete-li se velkých hříchů, jež byly vám zapovězeny, smažeme poklesky vaše a uvedeme vás v ráj způsobem čestným.'),
(525, 4, 32, 'Nežádejte si toho, čím Bůh hojněji obdařil jedny z vás nad druhé. Mužům dostane se podílu dle zásluh jejich a ženám dostane se podílu dle zásluh jejich. Proste tudíž Boha o milost (darů jeho): Bůh zajisté o všem ví.'),
(526, 4, 33, 'Každému určili jsme dědice, kteří převzíti mají to, co byli zanechali rodičové a příbuzní a ti, jimž podali jste pravice své ke smlouvě. Dejte tudíž každému podíl jeho, neboť Bůh zajisté jest všeho svědkem.'),
(527, 4, 34, 'Muži postaveni jsou výše žen pro vlastnosti, kterými je Bůh nad ně vyznamenal a pro výdaje, které muži činí pro ně ze svého jmění. Poctivé ženy jsou poslušné a opatrují bedlivě v době nepřítomnosti (manželovy) to, co Bůh si přeje, aby bedlivě opatrovaly. A ty, u nichž obáváte se neposlušnosti, kárejte: vykažte jim oddělené lůžko a bijte je: však jsou-li vás poslušny, nevyhledávejte příčin proti nim. Bůh zajisté jest vznešený, velký.'),
(528, 4, 35, 'Obáváte-li se roztržky mezi manželi, zavolejte sudího z rodiny mužovy a sudího z rodiny ženiny: a přejí-li si oba manželé smíru, Bůh zavede shodu mezi oběma: neboť Bůh jest vševědoucí, o všem zpraven.'),
(529, 4, 36, 'Uctívejte Boha a nečiňte mu společníků v uctívání. A buďte dobrými k rodičům svým a příbuzným, k sirotkům a chudým, ať spřízněným s vámi anebo cizím, k přidruživším se k vám, poutníkům a těm, jichž dobyla pravice vaše: neboť Bůh nemiluje domýšlivých a chlubilů,'),
(530, 4, 37, 'kteří sami jsou skoupými a k lakotě nabádají druhé a skrývají to, čím obdařil je Bůh v štědrosti své: a trest potupný připravili jsme nevěřícím.'),
(531, 4, 38, 'A nemiluje těch, kteří dávají cos ze jmění svého proto, aby viděni byli lidmi a nevěří v Boha a v den poslední, Ten pak, jemuž Satan jest soudruhem, zajisté špatného má soudruha!'),
(532, 4, 39, 'Jakého zkrácení by byli doznali, kdyby byli uvěřili v Boha a v den poslední a rozdávali z toho, čím obdařil je Bůh? Bůh zajisté dobře ví o nich.'),
(533, 4, 40, 'Bůh nezkrátí nikoho ani o váhu zrna hořčičného a dvojnásob odplatí každý dobrý skutek a dána jím bude odměna velká.'),
(534, 4, 41, 'A jak bude nepravostným, až předvedeme svědky ze všech národů a tebe předvedeme za svědka proti těmto?'),
(535, 4, 42, 'V den onen přáti si budou nevěřící a ti, kdož nebyli poslušni proroka, aby srovnána s nimi byla země; však nezmohou skrýti ničeho před Bohem.'),
(536, 4, 43, 'Vy, kteří jste uvěřili, nekonejte modlitbu, když jste opilými, nýbrž vyčkejte, až budete věděti, co mluvíte: rovněž ne, jste-li nečistými — vyjma, jste-li na cestě — dokud jste se byli neumyli. Jste-li nemocni, aneb na cestě, neb přišli jste z nečistého místa, neb měli jste styk se ženou a nenaleznete vody, rozemněte jemný písek a potřete jím tváře a ruce své: neboť Bůh zajisté velký jest v slitovnosti a odpoustění.'),
(537, 4, 44, 'Zdaž neviděl\'s ony, kterým dána byla část Knihy? Kupují blud za dobré vedení a chtějí, abyste i vy zbloudili s pravé cesty.'),
(538, 4, 45, 'Však Bůh nejlépe zná nepřátele vaše: a Bůh dostačitelným jest vám ochráncem a pomocníkem.'),
(539, 4, 46, 'Jsou mezi Židy někteří, již přemísťují slova (Knihy) a říkají, „Slyšeli jsme a neposlechli jsme, slyš tedy dosud neslyšené a pozoruj nás.“ Pletou slova jazyky svými a popichují proti pravému náboženství. A kdyby řekli; „Slyšeli jsme a poslechli jsme: slyš tedy a pohlédni na nás“ bylo by to lépe pro ně a správnějším. Však Bůh zlořečil jim pro nevíru jejich a pouze málo z nich stalo se věřícími.'),
(540, 4, 47, 'Vy, kteří jste obdrželi Knihu, uvěřte v to, co seslali jsme vám na potvrzení toho, co již máte, dříve než smažeme tahy obličejů vašich a obrátíme je dozadu, nebo proklejeme vás jako prokleli jsme rušitele dne sobotního: a rozkaz Boha byl ihned vykonán.'),
(541, 4, 48, 'Bůh zajisté neodpustí, aby dáváni mu byli společníci v božství. Jiné hříchy odpustí, komu bude chtíti: však ten, kdo staví Bohu společníky, v pravdě vymyslí velkou špatnost.'),
(542, 4, 49, 'Zdaž neviděl\'s ty, kteří chtěli se ospravedlniti? Však nikoliv: Bůh ospravedlní pouze ty, které bude chtíti a nikdo nebude utištěn ani v nejmenším.'),
(543, 4, 50, 'Pohleď, jak vymýšlejí lži o Bohu: to dostačí jako důkaz zjevné nepravosti.'),
(544, 4, 51, 'Zdaž neviděl\'s ty, kteří obdrželi část Knihy? Věří v Džibta a Tághúta a říkají nevěřícím: „Tito vedeni jsou lepší stezkou než ti, kdož uvěřili.“'),
(545, 4, 52, 'Těmto zlořečil Bůh a komu Bůh zlořečil, tomu nelze nalézti pomocníka.'),
(546, 4, 53, 'Zdaž mají obdržeti podíl v království ti, kteří nedávají bližním ani vlákno z pecky datlové?'),
(547, 4, 54, 'Či závidí druhým to, čím obdařil je Bůh z milosti své? Vždyť přece dali jsme rodu Abrahamovu Knihu a moudrost a království velké.'),
(548, 4, 55, 'Někteří z nich věří v proroka a někteří vzdalují se ho: však plamen pekla bude dostačitelným trestem!'),
(549, 4, 56, 'Ty, kteří neuvěřili ve znamení naše, připalovati budeme ohněm; a jakmile spálena bude kůže jejich, nahradíme ji novou, aby znovu okusili trápení, neboť Bůh zajisté mocný jest a moudrý.'),
(550, 4, 57, 'Ty pak, kdož uvěřili a konali dobré skutky, uvedeme do zahrad, pod nimiž řeky tekou: v nich přebývati budou věčně. V nich budou míti ženy bez poskvrny a libým stínem zastíněna budou přebývání jejich.'),
(551, 4, 58, 'Bůh zajisté nařizuje vám, abyste vraceli věci vám svěřené majitelům jejich a rozsuzujete-li mezi lidmi, abyste soudili spravedlivě. Bůh zajisté dává vám nejlepší napomenutí: onť slyší a vidí vše.'),
(552, 4, 59, 'Vy, kteří jste uvěřili, buďte poslušni Boha a proroka a těch, jimž dána jest moc mezi vámi: a nepohodnete-li se v čem, předneste to Bohu a proroku jeho, věříte-li v Boha a v den poslední. Toto bude nejlepší a nejsprávnější cestou k dohodě.'),
(553, 4, 60, 'Zdaž neviděl\'s ty, kdož mají za to, že uvěřili v to, co zjeveno bylo tobě a co zjeveno bylo před tebou? Přejí si, aby rozsouzeno bylo o nich, před Tághútem, ač nakázáno bylo jim, aby v něho nevěřili: a Satan přeje si, aby zavedeni byli do bludu dalekého.'),
(554, 4, 61, 'A když řekne se jim, „Pojďte a přidržte se toho, co sesláno bylo Bohem a proroka,“ vidíš je, pokrytce, odvraceti se od tebe s rozhodností.'),
(555, 4, 62, 'Však jak bude jim, až postihne je neštěstí za minulá díla rukou jejich? Pak přijdou k tobě, zapřísahajíce se Bohem, řkouce: „Přejeme si jedině dobra a svornosti.“'),
(556, 4, 63, 'Bůh dobře ví, co jest v srdcích jejich. Pročež straň se jich a napomeň je a mluv k nim řečí, jež pronikla by do duší jejich.'),
(557, 4, 64, 'Poslali jsme proroky pouze proto, aby poslechnuto jich bylo z dovolení božího. Však přijdou-li k tobě ti, kteří poškodili sebe samy neposlušností svou, prosíce Boha za odpuštění a proroka, aby prosil za odpuštění pro ně u Boha, shledají, že Bůh rád popřává sluchu kajícím, a že jest slitovný.'),
(558, 4, 65, 'A při Pánu tvém (přisahám): nebudou věřícími, dokud neustanoví tě soudcem neshod svých. Pak nenaleznou v myslích svých pochybení ohledně rozsudků tvých a podvolí se jim úplným podvolením.'),
(559, 4, 66, 'Kdybychom byli dali jim zákonem, aby samy sebe ubíjeli a opouštěli obydlí svá, jen málokdo z nich byl by tak učinil. Však kdyby byli učinili to, k čemu napomínali jsme je, bylo by to bývalo lepším pro ně a případnějším k upevnění víry jejich.'),
(560, 4, 67, 'Tehdy byli bychom dali jim velkou odměnu od nás'),
(561, 4, 68, 'a byli bychom vedli je stezkou přímou.'),
(562, 4, 69, 'A kdožkoli poslušen jest Boha a proroka, bude ve společnosti těch, jež zahrnul Bůh milostí svou: proroků, spravedlivých, mučedníků a pravdomluvných. Dobří to budou soudruzi!'),
(563, 4, 70, 'Toto bude milostí jim od Boha: a Bůh dobře ví, koho má vyznamenávati.'),
(564, 4, 71, 'Vy, kteří jste uvěřili, buďte obezřelými v boji a postupujte vpřed, v oddílech, aneb hromadně.'),
(565, 4, 72, 'Budou mezi vámi někteří zdlouhavci, a potká-li vás neštěstí, řeknou: „Bůh zajisté zvláštní prokázal mi milost, že nezúčastnil jsem se s nimi boje.“'),
(566, 4, 73, 'Však docílíte-li úspěchu z milosti boží, řeknou — jako by nikdy nebylo bývalo přátelství mezi nimi a vámi: „Kéž bych byl býval s nimi! Velkého byl bych býval dobyl úspěchu.“'),
(567, 4, 74, 'Nechť tedy bojují na stezce boží ti, kdož za život vezdejší kupují si život budoucí: a kdokoli bojuje na stezce boží, ať padne či zvítězí, zajisté bohatou dostane od nás odměnu.'),
(568, 4, 75, 'A co zbraňuje vám, abyste bojovali na stezce boží, když zesláblí z mužů a ženy a děti volají: „Pane náš, vyveď nás z tohoto města obývaného utiskovateli a pošli nám od sebe zastánce a pošli nám od sebe ochránce.“'),
(569, 4, 76, 'Ti, kdož uvěřili, bojují na stezce boží: a nevěřící bojují na stezce Tághúta: bojujte tedy proti stoupencům Satanovým, neboť slabé budou proti vám léčky Satanovy!'),
(570, 4, 77, 'Zdaž neviděl\'s ty, jimž řečeno bylo: „Zdržte rukou svých na čas od bojů a zachovávejte modlitbu a dávejte povinnou almužnu?“ Však když nařízeno jest jim pak válčení — hle, část jich, bojíc se lidí stejným strachem jako Boha, anebo ještě více, dí: „Pane náš, proč nařizuješ nám teď válčení? Proč neodložíš to ještě na krátkou dobu?“ Rci: „Malým jest požitek tohoto světa a jedině budoucí svět jest pravým dobrem tomu, jenž bojí se Boha: a tam nebudete zkráceni v nejmenším.'),
(571, 4, 78, 'Smrt zastihne vás, kdekolivěk budete, i kdybyste byli ve věžích opevněných!“ Potká-li je co dobrého, říkají: „Toto jest od Boha“ a potká-li je co zlého, říkají: „Toto jest od tebe.“ Rci: „Vše jest od Boha!“ Však co se děje s tímto lidem, že téměř nemůže pochopiti toho, co se mu říká?'),
(572, 4, 79, 'Cožkoli dobrého tě potká, jest od Boha: a cožkoli zlého tě potká, jest od tebe. Tebe poslali jsme lidstvu jako proroka a Bůh dostačí ti k tomu, jako svědek!'),
(573, 4, 80, 'Kdo poslouchá proroka, ten poslouchá Boha. Však neposlali jsme tě jako opatrovníka jejich.'),
(574, 4, 81, 'A říkají: „Posloucháme,“ však jakmile odejdou od tebe, někteří z nich hloubají v noci o jiném, než o slovech tvých: ale Bůh dobře zapisuje to, o čem hloubají v noci, Těch straň se a spoléhej na Boha, neboť Bůh postačitelným jest ochráncem!'),
(575, 4, 82, 'Zdaž nerozbírají pozorně Korán? Kdyby kdo jiný, než Bůh byl původcem jeho, zajisté našli by v něm četné rozpory.'),
(576, 4, 83, 'A jakmile dojde je zpráva, naplňující je buď jistotou anebo strachem, ihned ji rozšiřují. Však kdyby oznámili ji prorokovi nebo vůdcům svým, dostalo by se vysvětlení o ní těm, kdož by si ho přáli. Ale kdyby Bůh nebyl býval milostiv k nám, byli byste následovali Satana, až na málokoho, z vás.'),
(577, 4, 84, 'Bojuj tedy na stezce boží a neukládej břemen, vyjma sobě samému a povzbuzuj věřící! Bůh zadrží moc nevěřících, neboť Bůh mocnějším jest jich v náporu a mocnějším v trestání.'),
(578, 4, 85, 'Kdož dělati bude prostředníka s úmyslem dobrým, najde odměnu svou: a kdož dělati bude prostředníka s úmyslem zlým, najde odplatu svou. Bůh pak dává dobrý pozor na vše.'),
(579, 4, 86, 'A pozdraví-li vás kdo, pozdravte jej ještě lepším pozdravem, neb aspoň opětujte pozdrav. Bůh zajisté vše súčtovává.'),
(580, 4, 87, 'Bůh! není boha kromě něho a on vpravdě shromáždí vás v den zmrtvýchvstání, o němž není pochyby — neboť kdo pravdivějším jest Boha v řeči?'),
(581, 4, 88, 'A jak přijde, že rozdělili jste se na dvě strany, pokud se týče pokrytců? Vždyť Bůh zavrhl je mezi nevěřící pro skutky jejich. Chcete vésti cestou pravou ty, kterým Bůh dal zblouditi? Tomu, jejž Bůh zavedl s pravé cesty, zajisté nenalezneš východiska.'),
(582, 4, 89, 'Rádi by, abyste stali se nevěřícími jako oni, tak abyste byli stejnými. Nepřátelte se tedy s nimi, dokud neopustí zem svou na stezce boží, obrátí-li se k nevíře, tehdy chopte se jich a pobijte je, kdekolivěk je najdete: a nebeřte si ze středu jich ani přátel, ani pomocníků,'),
(583, 4, 90, 'vyjma těch, kteří hledají útočiště u spojenců vašich, nebo těch, kteří přišli k vám proto, že srdce jich zbraňují jim, aby válčili proti vám nebo proti lidu svému. Kdyby byl Bůh býval chtěl, byl by dal jim moc nad vámi a byli by bojovali proti vám. Však drží-li se stranou od vás a nebojují proti vám a nabízejí vám mír, Bůh nedává vám příčiny k útočení na ně.'),
(584, 4, 91, 'Najdete zase jiné, kteří budou chtít získati důvěry vaší, jakož i důvěry lidu svého. Pokaždé, když navrátí se k rozbroji, zapuzeni budou mezi nevěřící: však nebudou-li držeti se stranou od vás a nenabídnou-li vám mír a nepřestanou-li vztahovati na vás ruce své, tehdy chopte se jich a pobijte je, kdekolivěk je naleznete. Nad těmito dali jsme vám zjevnou moc.'),
(585, 4, 92, 'Věřící nezabije věřícího, vyjma omylem: a kdožkoli zabije věřícího omylem, bude nucen vykoupiti věřícího otroka a zaplatiti odškodné rodině zabitého, leda že by tato obrátila obnos ten v almužnu. Však je-li zabitý věřící z národa nepřátelského vám, stačí výkupné věřícího otroka. A přináleží-li k národu, s nímž jste byli učinili smlouvu spojenství, nechť odškodné vyplaceno jest rodině zabitého a vykoupen věřící otrok. A kdo nemá k tomu prostředků, nechť postí se po dva po sobě následující měsíce. Takováto jsou pokání uložená Bohem: a Bůh jest vševědoucí, moudrý.'),
(586, 4, 93, 'Však kdožkoli zabije věřícího zúmyslně, toho odměnou bude peklo, v němž přebývati bude věčně: rozhněvá se naň Bůh a zlořečiti mu bude a připraví mu velký trest.'),
(587, 4, 94, 'Vy, kteří jste uvěřili, vyjdete-li v boj na stezce boží, buďte obezřetni a neříkejte každému, jejž potkáte a jenž pozdraví vás: „Nejsi věřícím,“ ve své žádostivosti po statcích života pozemského. Bůh zajisté má bohatství velká, Tak byli jste zvyklí jednati dříve: však Bůh byl k vám milostiv. Buďte tudíž obezřetnými, neboť Bůh dobře zpraven jest o tom, co konáte!'),
(588, 4, 95, 'Ti z věřících, kteří zůstanou seděti doma, aniž by byli k tomu přinuceni okolnostmi, nebudou postaveni na úroveň s těmi, kdož statečně bojují na stezce boží osobami i statky svými. Bůh vyznamenal vyšším stupněm ty, kteří horlivě bojují statky i osobami svými nad ony, kteří sedí doma. Všem slíbil Bůh krásnou odměnu: však vyznamenal Bůh ty, kdož horlivě bojují, nad ony, kteří sedí doma, velkou odměnou,'),
(589, 4, 96, 'vyššími stupni milosti a odpuštěním a milosrdenstvím; a Bůh zajisté velkým jest v odpouštění, slitovným.'),
(590, 4, 97, 'Těch, kteří neprávě jednali vůči sobě samým, otáží se andělé smrti: „Jakými byli jste?“ Řeknou: „Byli jsme slabými v zemi.“ Řeknou andělé: „Což nebyla země boží dosti rozsáhlou, abyste nebyli mohli vystěhovati se do jiné vlasti?“ Obydlím těchto bude peklo a zlá bude k němu cesta!'),
(591, 4, 98, 'Pouze slabým z mužů a ženám a dětem, již pro slabost svou nebyli s to vymysliti záminku k vystěhování se a nalézti pravou cestu,'),
(592, 4, 99, 'těm snad odpustí Bůh, neboť Bůh velký jest v odpouštění a milosti své.'),
(593, 4, 100, 'Kdožkoli opustí vlast svou pro věc boží, nalezne mnoho jiných na zemi, přinucených k témuž jednání a bohaté pro středky: a kdo opustí dům svůj, aby vystěhoval se k Bohu o proroku jeho a v tom smrt jej překvapí: odměna tohoto spadá v ruce Boha a Bůh velkým jest v odpouštění, slitovným.'),
(594, 4, 101, 'A když potloukati se budete po zemi, nebudete míti viny, když zkrátíte modlitby své, obáváte-li se, že přepadnouti mohli by vás nevěřící: zajisté nevěřící jsou vašimi zjevnými nepřáteli.'),
(595, 4, 102, 'A když budeš ve středu jejich a zavoláš je k modlitbě; tehdy nechť část jich povstane k modlitbě, vzavše k sobě zbraně své: a když byli skončili uctívání svá, nechť odejdou dozadu. Pak nechť přijde druhá část, která dosud nebyla vykonala své modlitby a nechť modlí se s tebou. Nechť jsou obezřetnými a mají zbraně pohotově. Nevěřící rádi by viděli, kdybyste nedali pozor na zbraně své a zavazadla, aby pak najednou mohli vrhnouti se na vás. A nebude počítáno vám za vinu, odložíte-li zbraně své, když obtěžovati vás bude déšť nebo když budete nemocni: však buďte obezřelými! Bůh zajisté připravil trest potupný nevěřícím.'),
(596, 4, 103, 'A když skončili jste modlitbu, mějte stále na paměti Boha a vzývejte ho v stoje i v sedě i v leže: a jakmile jste v bezpečí, vykonávejte pilně modlitbu. Neboť modlitba jest předepsanou povinností věřícím v hodiny pevně stanovené.'),
(597, 4, 104, 'Neochabujte v pronásledování nepřátel: trpíte-li, oni zajisté trpí stejně jako vy: ale vy očekáváte od Boha to, co oni nemohou očekávati. A Bůh vševědoucí jest a moudrý.'),
(598, 4, 105, 'Zajisté seslali jsme ti Knihu pravdy, abys rozsuzoval mezi lidmi dle toho, co zjevil ti Bůh: však nehádej se s proradnými,'),
(599, 4, 106, 'nýbrž pros Boha za odpuštění, neboť Bůh velkým jest v odpouštění, slitovný.'),
(600, 4, 107, 'Nerokuj (s námi) ve prospěch těch, kteří sami sebe zrazují, neboť Bůh nemiluje proradných, zlo činících.'),
(601, 4, 108, 'Ti mohou skrýti se před lidmi, však nemohou skrýti se před Bohem: onť zajisté jest přítomen, když za noci vedou rozhovory, jež se mu nelíbí. A Bůh zahrnuje ve vědění svém cokoliv konají.'),
(602, 4, 109, 'Hle, vy přimlouváte se za ně v tomto životě pozemském: však kdo přimluví se za ně u Boha v den zmrtvýchvstání a kdo bude jejich ochráncem?'),
(603, 4, 110, 'Ale ten, jenž dopouští se zlého, a poškozuje tím vlastní duši, však poté prosí Boha za odpuštění, shledá, že Bůh velký jest v odpouštění, slitovný.'),
(604, 4, 111, 'A kdokoli dopouští se zlého skutku, dopouští se ho k vlastní škodě: a Bůh vševědoucí jest a moudrý.'),
(605, 4, 112, 'Kdokolivěk dopouští se poklesku, neb zlého skutku a pak svaluje jej na nevinného, uvaluje na se vinu klamného osočování a špatnosti zjevné.'),
(606, 4, 113, 'Nebýti milosti boží vůči tobě a milosrdenství jeho, byli by tě zavedli na bludnou stezku ti z nich, kteří umínili si, že zavedou tě v blud: však zavedli jedině sebe samy v blud, a neuškodí ti v nejmenším. Bůh seslal ti Knihu a moudrost a naučil tě tomu, cos nevěděl: zajisté velká byla milost boží nad tebou!'),
(607, 4, 114, 'Nic dobrého není ve většině jejich tajných rozprav: jedině ten, kdo nabádá k almužně a k dobrým skutkům, nebo k svornosti mezi lidmi a činí tak z touhy, aby zalíbil se Bohu, zajisté obdrží od nás odměnu velkou.'),
(608, 4, 115, 'Však kdožkolivěk odtrhne se od proroka poté, když jasně naznačeno bylo mu dobré vedení a následovati bude jinou stezku, než stezku věřících, k tomu obrátíme se zády, stejně jako on obrátil se zády k nám a uvrhneme jej v peklo: věru, zlá bude to cesta.'),
(609, 4, 116, 'Bůh zajisté neodpustí těm, kteří staví mu rovné: vše ostatní odpustí, komu bude chtíti, však ten, kdož staví si jiné bohy vedle Boha, daleko zbloudil v bludu svém.'),
(610, 4, 117, 'Vzývají místo Boha, jen ženská božství; a tak vzývají vlastně jen Satana odbojného,'),
(611, 4, 118, 'jenž zlořečen byl Bohem. Řekl: „Jistotně vezmu si určitou část služebníků tvých'),
(612, 4, 119, 'a jistotně zavedu je a vášně v nich probudím: jistotně poručím jim a jistotně uřezávati budou uši zvířatům: jistotně nakáži jim a jistotně hyzditi budou stvoření boží.“ Kdož pak béře si za ochránce Satana spíše než Boha, vrhá se ve ztrátu nade vše zjevnou.'),
(613, 4, 120, 'Činí jim sliby a probouzí v nich vášně: však pouhým mamem a klamem jsou sliby Satanovy.'),
(614, 4, 121, 'Obydlím těchto bude peklo a nenaleznou z něho východiska.'),
(615, 4, 122, 'Ty pak, kdož uvěřili a konali dobré skutky, uvedeme v zahrady, pod nimiž řeky tekou: v nich přebývati budou věčně. Toto jest pravdivý slib od Boha a kdo pravdivějším jest Boha v daném slovu?'),
(616, 4, 123, 'Ne dle přání vašich (stane se tak), aniž dle přání těch, kteří obdrželi Knihu. Kdo zlo činiti bude, dle skutků svých odměněn bude a nenajde vedle Boha zastánce, ni pomocníka.'),
(617, 4, 124, 'Kdož pak dobro činiti bude, ať muž, či žena, a jsou-li věřícími, vstoupí do ráje a nebudou zkráceni v nejmenším.'),
(618, 4, 125, 'Kdo lepší má náboženství nad toho, jenž odevzdal se do vůle boží a koná dobro a následuje víru Abrahamovu, jenž pevný byl ve víře: a zajisté učinil Bůh Abrahama přítelem svým.'),
(619, 4, 126, 'Bohu náleží vše, což na nebi jest i na zemi: a Bůh vše obsahuje (ve vědění svém).'),
(620, 4, 127, 'Budou dotazovati se tebe ohledně žen. Rci: „Bůh poučil vás (již) ohledně jich a dále máte to, co čteno vám bylo v Knize ohledně sirotků pohlaví ženského, kterým nedáváte to, co po zákonu jim patří a které zdráháte se pojmouti za manželky: a ohledně slabých dětí a spravedlivého zacházení se sirotky. Není dobrého skutku, který byste byli vykonali, aniž Bůh by o něm nevěděl.“'),
(621, 4, 128, 'Obávala-li by se žena, že manžel její bude s ní špatně nakládati, aneb míti k ní odpor, nebude jim počítáno za vinu, dohodnou-li se vzájemně smírem, neboť vzájemný smír jest ze všeho nejlepší. Duše (lidí) nakloněny jsou lakotnosti: však jste-li dobročinnými a bojíte-li se Boha, Bůh zajisté dobře zpraven jest o tom, co činíte.'),
(622, 4, 129, 'Nebudete nikdy s to, abyste se všemi ženami svými zacházeli stejně i když by to bylo vřelým přáním vaším: však nepoddávejte se úplně (ochabnutí) náklonnosti své (k některé z nich), tak abyste ji ponechali jako odloženou. Avšak dohodnete-li se vzájemně a budete báti se Boha; Bůh zajisté velkým jest v odpouštění, slitovným.'),
(623, 4, 130, 'Ale rozejdou-li se oba manželé, Bůh z hojnosti své dá jim náhradu, neboť Bůh nesmírný jest v bohatství, moudrý.'),
(624, 4, 131, 'Bohu přináleží vše, což na nebi jest i na zemi; a napomenuli jsme již ty, kteří obdrželi Knihu před vámi, aby báli se Boha: bojte se tedy Boha! A neuvěříte-li, vězte, že Bohu přináleží vše, což na nebi jest i na zemi: Bůh zajisté nesmírný jest v bohatství a chvályhodný.'),
(625, 4, 132, 'A Bohu přináleží vše, což na nebi jest i na zemi — a Bůh zajisté dostačí jako ochránce!'),
(626, 4, 133, 'Kdyby — ó lidé — zlíbilo se mu, nechal by vás zajíti a stvořil by jiné lidi na vašem místě, neboť Bůh jest všemocný.'),
(627, 4, 134, 'Přeje-li si kdo z vás odměnu tohoto světa, věz, že u Boha jest odměna tohoto světa i světa budoucího: a Bůh vše slyší a pozoruje.'),
(628, 4, 135, 'Vy, kteří jste uvěřili, přidržujte se pevně spravedlivosti, když svědčíte před Bohem, i kdybyste měli svědčiti proti sobě samým, proti svým rodičům nebo příbuzným, proti bohatému nebo chudému, neboť Bůh blíže jest vám, než vy bohatému či chudému. Pročež nenásledujte náklonností svých, abyste neodchýlili se od pravdy. A odepřete-li (svědectví svého), nebo vyhnete se stranou — Bůh zajisté zpraven jest o skutcích vašich.'),
(629, 4, 136, 'Vy, kteří jste uvěřili, věřte v Boha a proroka jeho a v Knihu, seslanou proroku jeho a v Knihu, jež seslána byla před tím. A kdožkoli nevěří v Boha a anděly jeho a Knihy jeho a proroky jeho a v den poslední, ten daleko zbloudil v bludu svém.'),
(630, 4, 137, 'Zajisté ti, kdož (zprvu) uvěřili, pak zpět upadli v nevíru, pak zase uvěřili a poté opět stali se nevěřícími a silněji zatvrdili se v nevíře své — těm neodpustí Bůh a nepovede je stezkou pravou.'),
(631, 4, 138, 'Ohlašuj pokrytcům radostnou zvěst, že očekává je trest bolestný.'),
(632, 4, 139, 'Je, kteří béřou si nevěřící za přátele raději než věřící: činí tak snad a kdož pravdivějším jest z touhy, aby dobyti si u nich cti? Zajisté čest veškerá Bohu přináleží!'),
(633, 4, 140, 'Bůh zjevil vám již v Knize: Když naslouchati budete znamením božím, nebude jim věřeno, nýbrž s výsměchem přijata budou. Pročeš, nesedejte si s nevěřícími, dokud neobrátí řeč jiným směrem, jinak stanete se stejnými. Bůh pak za jisté shromáždí pokrytce a nevěřící v pekle všechny.'),
(634, 4, 141, 'Ty, kteří pozorují vás a vyčkávají, Dobyli-li jste vítězství z vůle boží, praví: „Což nejsme s vámi?“ Když pak nevěřící dobyli úspěchu, říkají těmto: „Neměli-li jsme my převahu a nechránili-li jsme vás před věřícími?“ Bůh pak rozsoudí mezi vámi v den zmrtvýchvstání a nedá zajisté nevěřícím převahy nad věřícími.'),
(635, 4, 142, 'Pokrytci rádi by oklamali Boha, však naopak Bůh oklame je! Když povstávají k modlitbě, činí tak lenivě, a ledabyle a jen proto, aby viděni byli lidmi: však pramálo vzpomínají Boha.'),
(636, 4, 143, 'Potácejí se mezi oběma (stranami), nenáležíce ani k jedněm, ani k druhým: a tomu, jejž zavedl Bůh, nenalezneš stezky pravé.'),
(637, 4, 144, 'Vy, kteří jste uvěřili, nebeřte si nevěřící za přátele raději než věřící. Či chcete dáti Bohu zjevné právo nad sebou (k potrestání vás)?'),
(638, 4, 145, 'Pokrytci zajisté uvrženi budou v nejhlubší propast ohně a nenalezneš jim zastánce,'),
(639, 4, 146, 'vyjma těch, kdož kajícně obrátí se a polepší a přidrží se pevně Boha a ukáží se opravdovými ve víře své: tito zařaděni budou mezi věřící a věřícím dá Bůh odměnu velkou.'),
(640, 4, 147, 'Proč uděloval by vám Bůh trest, jste-li vděčnými a věříte-li? Bůh zajisté jest vděčny a vševědoucí.'),
(641, 4, 148, 'Bůh nemiluje veřejného šíření zlých řečí, vyjma kdyby někomu bylo ublíženo: a Bůh slyší a vidí vše.'),
(642, 4, 149, 'Ať již veřejně šíříte dobré, neb tajíte je, anebo odpouštíte zlé — Bůh zajisté velkým jest i odpouštění, všemocným.'),
(643, 4, 150, 'Zajisté ti, kteří nevěří v Boha a proroky jeho a chtějí odděliti Boha od proroků jeho, praví: „Věříme některým a některým nevěříme,“ a chtějí bráti se střední cestou.'),
(644, 4, 151, 'Tito jsou ti praví nevěřící — a nevěřícím připravili jsme trest potupný!'),
(645, 4, 152, 'Ti pak, kdož uvěřili v Boha a proroky jeho a nečiní rozdílu mezi žádným z nich, obdrží odměnu svou — a Bůh velkým jest v odpouštění, slitovným.'),
(646, 4, 153, 'Budou žádati tě oni, kteří byli obdrželi Knihu, abys učinil, aby seslána byla jim kniha s nebe. Však od Mojžíše žádali již více nad to, řkouce: „Dejž spatřiti nám Boha jasně.“ Tehdy zasáhl je blesk za nepravost jejich. Poté postavili si tele modlou, ač před tím jasná znamení jim byla dána: nicméně, odpustili jsme jim to, a dali jsme Mojžíšovi moc zjevnou.'),
(647, 4, 154, 'A pozdvihli jsme horu nad hlavami jejich, činíce smlouvu s nimi a řekli jsme jim: „Vstupte do brány uctívajíce Boha“ a řeku jsme jim: „Neznesvěcujte dne sobotního“ a přísnou učinili jsme s nimi smlouvu.'),
(648, 4, 155, 'Však proto, že nedodrželi smlouvy své a nevěřili ve znamení boží a neprávě zabíjeli proroky a řeč jejich byla: „Neobřezána jsou srdce naše“ ano, proto zapečetil jim Bůh srdce pro nevíru jejich: a pouze málo jich jest věřícími.'),
(649, 4, 156, 'A pro nevíru jejich a proto, že o Marii nectnou vynašli pomluvu,'),
(650, 4, 157, 'a proto, že říkají: „Zajisté zabili jsme Mesiáše, Ježíše, syna Mariina, proroka božího.“ Však nezabili ho, aniž ukřižovali, nýbrž kohosi jemu podobného a ti, kdož různá o tom měli mínění, byli o tom v pochybnostech. Nevědí o tom ničeho určitého: pouze přidržují se domněnek. Nezabili ho ve skutečnosti,'),
(651, 4, 158, 'nýbrž Bůh pozdvihl jej k sobě: neboť Bůh mocný jest a moudrý.'),
(652, 4, 159, 'Nebude jednoho mezi těmi, kteří obdrželi Knihu, jenž by nevěřil v něho dříve než zemře: a v den zmrtvýchvstání Ježíš bude svědčiti proti nim.'),
(653, 4, 160, 'Pro nešlechetnost některých Židů a proto, že odvracejí mnohé od stezky boží, zapověděli jsme jim jísti některé dobré věci, jež dříve dovoleny jim byly:'),
(654, 4, 161, 'a proto, že provozovali lichvu, ač byla jim zakázána a proto, že promrhávali statky druhých v prostopášnosti: a připravili jsme nevěrícím trest bolestný.'),
(655, 4, 162, 'Však těm z nich, kteří jsou pevného vědění, jakož i věřícím, již věří v to, co sesláno bylo tobě a v to, co sesláno bylo před tebou: a těm, kteří zachovávají modlitbu a dávají almužnu a kdož věří v Boha a v den poslední: těm dáme odměnu velkou.'),
(656, 4, 163, 'Zajisté dali jsme ti zjevení, jakož dali jsme je Noemovi a prorokům po něm: a jakož dali jsme je Abrahamovi a Ismaelovi a Isákovi a Jakubovi a kmenům a Ježíšovi a Jobovi a Jonáši a Áronovi a Šalamounovi, a jakož Davidovi dali jsme žalmy.'),
(657, 4, 164, 'A jiným prorokům, o nichž jsme ti vyprávěli již dříve a prorokům, o nichž ti nebudeme vyprávěti: a Bůh zajisté rozmlouval s Mojžíšem.'),
(658, 4, 165, 'Tito proroci jsou, hlásající zvěsti a varující, aby po příchodu jich lidé neměli právoplatné výmluvy u Boha: a Bůh zajisté mocný jest a moudrý!'),
(659, 4, 166, 'Však Bůh sám dosvědčuje pravost toho, co ti byl seslal: že seslal ti je ve vědění svém a andělé též to dosvědčují, však Bůh zajisté dostačitelným jest svědkem.'),
(660, 4, 167, 'Zajisté ti, kdož nevěří a odvracují druhé od stezky boží, daleko zbloudili v bludu svém.'),
(661, 4, 168, 'Těm, kdož nevěří a dopouštějí se nepravosti, zajisté neodpustí Bůh nikdy a neuvede je na cestu pravou,'),
(662, 4, 169, 'vyjma cesty k peklu, v němž přebývati budou věčně: a zajisté snadným to bude Bohu!'),
(663, 4, 170, 'Ó lidé, nyní přišel k vám prorok s pravdou od Pána vašeho: uvěřte a bude to lepším pro vás. Však neuvěříte-li vězte, že Bohu zajisté přináleží, cožkoli na nebi jest i na zemi a že Bůh jest vševědoucí, moudrý.'),
(664, 4, 171, 'Vy, kteří obdrželi jste Knihu, nepřekročujte mezí víry své a mluvte o Bohu pouze pravdu. V pravdě Mesiáš, Ježíš, syn Mariin, jest pouze prorokem božím a slovem jeho, jež vtěleno bylo v Marii a duchem, přicházejícím od něho. A věřte v Boha a v proroky jeho a neříkejte: „Trojice.“ Zanechte toho: bude to lepším pro vás! V pravdě Bůh jest Bůh Jediný a při slávě jeho! nemá syna: jemu přináleží, cožkoliv na nebi jest i na zemi, a Bůh jest dostačitelným ochráncem.'),
(665, 4, 172, 'Neopovrhuje Mesiáš tím, aby byl služebníkem Boha, aniž andělé, Bohu nejbližší:'),
(666, 4, 173, 'a ty, kdož opovrhují službou Bohu a naplněni jsou pýchou, shromáždí Bůh k sobě všechny. A pokud se týče těch, kdož uvěřili a konali dobré skutky, ty odmění Bůh po zásluhách jejich a rozmnoží milost svoji nad nimi: však pokud se týče těch, kteří opovrhovali Bohem a byli pýchou naplněni, ti potrestáni budou trestem bolestným, a nenaleznou mimo Boha ochránce aneb zastánce.'),
(667, 4, 174, 'Ó lidé, nyní přišel vám důkaz od Pána vašeho a seslali jsme vám světlo jasné.'),
(668, 4, 175, 'A pokud se týče těch, kdož uvěřili v Boha a pevně přidrželi se jej, těm vstoupiti dá Bůh ve slitování a milost svou a povede je k sobě stezkou přímou.'),
(669, 4, 176, 'Budou žádati tě o rozhodnutí. Rci: „Bůh radí vám toto ohledně vzdálených příbuzných. Zahyne-li muž, nezanechav dítek a má sestru, této nechť připadne polovina z toho, co zanechal. A kdyby ona zemřela bezdětna, bude bratr jejím dědicem. A kdyby bylo dvé sester, tedy obdrží dvě třetiny z toho, co zanechal bratr. A jsou-li zde sourozenci, bratři a sestry, každý muž nechť obdrží podíl dvou žen. Bůh poučuje vás jasně, abyste nezbloudili: a Bůh o všem ví.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Vy, kteří jste uvěřili, plňte věrně závazky své. Dovoleno jest vám jísti maso dobytčat, vyjma oněch (později) vyjmenovaných, která vám nejsou dovolena. Rovněž není vám dovoleno jísti zvěřinu po dobu pouti vaší do Mekky: Bůh zajisté nakazuje, co chce.'),
(671, 5, 2, 'Vy, kteří jste uvěřili, neznesvěcujte obřadů božích, aniž měsíce posvátného, aniž darů obětních a ozdob, aniž těch, kdož tlačí se k modlitebnici posvátné, hledajíce v ní milosti od Pána svého a zalíbení jeho. Ale, když skončili jste pouť, dovoleno jest vám loviti. A nechť nepopuzuje vás k činům zlým nenávist lidí, kteří rádi by byli vás zapudili od modlitebnice posvátné: raději pomáhejte si navzájem v konání dobra a zbožnosti, nikoli v konání zla a nešlechetnosti: a bojte se Boha, neboť Bůh zajisté přísný jest v trestání.'),
(672, 5, 3, 'Zapovězena jest vám zdechlina a krev a maso vepřové, jakož i vše, co zabito bylo v jiném jméně než božím; zvířata zardousená a zabitá úderem, zabitá pádem neb bodnutím rohu, neb zadávená divokou šelmou, vyjma takových, která očistili jste vykrvácením; dále což obětováno bylo na postavených kamenech (model). A nedělte mezi sebou obětí, vrhajíce los pomocí šípů: jest to hříchem pro vás. Dnes zoufati si budou ti, kteří zapřeli vaše náboženství. Jich nebojte se, nýbrž mne bojte se! Dnes dokonal jsem vám náboženství vaše a dovršil vám míru milosti své: a zlíbilo se mi dáti vám Islám náboženstvím. Ale vůči tomu, jenž přinucen hladem, nikoliv z náklonnosti své k hříchu, přestoupí zákazy naše, Bůh zajisté bude velkým v odpouštění, slitovným.'),
(673, 5, 4, 'Budou dotazovati se tě na to, co jest jim dovoleno. Rci: „Dovoleny jsou vám věci dobré a kořist honících zvířat, jež vycvičili jste jako psy podle umění, kterému naučil vás Bůh. Jezte tudíž z toho, co pro vás uloví, vyslovivše nad tím jméno Boha: a bojte se Boha, neboť Bůh rychlým jest v účtování.'),
(674, 5, 5, 'Dnes dovoleny jsou vám dobré věci a jídla těch, kteří obdrželi Knihu, dovolena jsou vám, jakož i jim dovolena jsou jídla vaše. (Dovoleno jest vám bráti si za manželky) ctné ženy věřící i ctné ženy z lidu, obdrževšího Knihu před vámi, dáte-li jim jich věna. Žijte s nimi počestně, nikoli smilně, a nebeřte si souložnic. Kdožkoli pak zapře víru svou, niveč jsou konání jeho a mezi zatracené zařaděn bude v životě budoucím.'),
(675, 5, 6, 'Vy, kteří jste uvěřili. když povstáváte k modlitbě, umyjte tváře své a ruce až k lokti a otřete hlavy své a nohy až ke kotníkům. Stali-li jste se nečistými, očisťte se: a jste-li nemocni, neb na cestě, nebo přišli jste z místa potřeby, nebo měli jste styk se ženami — a nenaleznete vody — tehdy rozemněte jemný písek a potřete jím tváře a ruce své. Bůh nechce vám tím uložiti břímě, nýbrž očistiti vás a dovršiti míru milosti své nad vámi, abyste mu byli vděčni.'),
(676, 5, 7, 'Buďte tedy pamětlivi milosti Boha, prokázané vám a smlouvy, kterou učinil s vámi, když řekli jste: „Slyšeli jsme a poslechli jsme“: a bojte se Boha, neboť on zajisté ví, co utajeno, jest v nitrech.'),
(677, 5, 8, 'Vy, kteří jste uvěřili, buďtež přímými a poctivými svědky před Bohem a nedejte zavésti se záští vůči někomu od spravedlivého jednání: jednejte spravedlivě, neboť toto jest nejblíže bázni boží. A bojte se Boha, neboť Bůh zajisté dobře zpraven jest o tom, co konáte.'),
(678, 5, 9, 'Přislíbil Bůh těm, kteří uvěřili a konají dobré skutky, že dáno jim bude odpuštění a odměna velká.'),
(679, 5, 10, 'Však těch, kteří neuvěřili ve znamení naše a lží nazývali je, údělem jest oheň (pekelný).'),
(680, 5, 11, 'Vy, kteří jste uvěřili, buďte pamětlivi milosti boží, prokázané vám, když umínili si jistí lidé, že vztáhnou ruce své na vás: Bůh odrazil ruce jejich od vás. A bojte se Boha: a na, Boha nechť spoléhají věřící.“'),
(681, 5, 12, 'Bůh přijal kdysi smlouvu synů Israele a vzbudili jsme mezi nimi dvanácte vůdců; a řekl Bůh: „V pravdě budu s vámi: budete-li zachovávati modlitbu a dávati povinnou almužnu a věřiti v proroky mé a pomáhati jim, a půjčíte-li Bohu dobrou půjčku — zajisté pak promine vám viny vaše: a uvedu vás zajisté v zahrady, pod nimiž řeky tekou. A kdožkoli po tomto slibu nevěří, ten zajisté zbloudil s pravé cesty.“'),
(682, 5, 13, 'Však proto, že porušili smlouvu svou, zlořečili jsme jim a zatvrdili jsme srdce jejich. Přemísťují slova (Knihy) a zapomínají část z toho, čemu byli naučeni. Nepřestaneš objevovati proradu mezi nimi, až na malý počet jich. Však promiň jim a přejdi přes to: neboť Bůh zajisté miluje ty, kdož dobře činí.'),
(683, 5, 14, 'A přijali jsme též smlouvu těch, kteří praví: „Jsme křesťané.“ I oni zapomněli část toho, čemu byli naučeni: pročež vzbudili jsme nepřátelství a zášť mezi nimi, jež potrvá až do dne zmrtvýchvstání. A Bůh poví jim pak, co provedli.'),
(684, 5, 15, 'Vy, kteří obdrželi jste Knihu, nyní přišel k vám prorok náš a vykládá vám jasně mnoho z toho, co jste byli tajili z knihy a promíjí vám mnoho z toho. Nyní přišlo k vám od Boha světlo a kniha jasná, kterou povede Bůh ty, kdož následovati budou zalíbení jeho cestami míru, a vyvede je z temnot na světlo z dopuštění svého a povede je ke stezce přímé.'),
(685, 5, 16, 'Nevěřícími pak jsou ti, kdož říkají: „Mesiáš, syn Mariin, jest Bůh.“ Rci: „Kdo mohl by v čem zabrániti Bohu, kdyby zachtělo se mu zahubiti Mesiáše, syna Mariina a matku jeho a obyvatele země veškeré?“'),
(686, 5, 17, 'Neboť Bohu přináleží panství nebes i země a všeho, což mezi nimi jest: tvoří, co chce, neboť Bůh jest všemocný.'),
(687, 5, 18, 'A říkají Židé a křesťané: „My syny jsme božími a miláčky jeho.“ Rci: „Proč tedy tresce vás za viny vaše? Nikoliv: jste pouze lidé, jež Bůh stvořil, jako ostatní. Onť odpouští, komu chce a tresce, koho chce: a Bohu přináleží panství nebes i země a všeho, což mezi nimi jest, a k němu vede cesta návratu.“'),
(688, 5, 19, 'Vy, kteří jste obdrželi Knihu, nyní přišel k vám prorok náš, aby objasnil vám přestávku mezi proroky, abyste neříkali: „Nepřisel nám ani hlasatel dobrých zvěstí, ani varovatel.“ Však nyní přišel vám hlasatel radostných zvěstí i varovatel: a Bůh jest všemocný.'),
(689, 5, 20, '(Pomněte), když Mojžíš řekl k lidu svému: „Lide můj, buď pamětliv dobrodiní, jež prokázal ti Bůh, vzbudiv ve středu tvém proroky a učiniv ti krále a dav ti, co nebylo dáno nikterému z národů.'),
(690, 5, 21, 'Ó lide můj, vstup do země svaté, již Bůh ti byl zaslíbil a neobracej se nazpět, neboť učiniv tak, upadl bys v záhubu.“'),
(691, 5, 22, 'Řekli: „Ó Mojžíši, v zemi té obývá lid mohutný a nám nebude možno vstoupiti do ní, dokud neodejdou z ní obyvatelé její: však vyjdou-li ze země, vejdeme do ní.“'),
(692, 5, 23, 'I řekli dva bohabojní, jimž pořehnal byl Bůh: „Vejděte do brány jejich a jakmile vejdete do ní, budete vítězi. A na Boha spoléhejte, jste-li věřícími.“'),
(693, 5, 24, 'Řekli: „Ó Mojžíši, nikdy nebude nám možno vstoupiti do země, dokud zůstanou v ní obyvatelé její: vyjdi proti nim sám s Pánem svým a bojujte: my mezitím zůstaneme zde seděti.“'),
(694, 5, 25, 'I řekl: „Pane můj, zajisté pouze nad sebou a bratrem svým mám moc: nuž rozhodni mezi námi a tímto lidem bezbožným.“'),
(695, 5, 26, 'Řekl Bůh: „Tedy zapovězena budiž jim země ona po čtyřicet let a blouditi budou v poušti. Ty pak nermutiž se pro lid bezbožný.“'),
(696, 5, 27, 'Vypravuj jim příhodu dvou synů Adamových podle pravdy, jak obětovali oběti a jak přijata byla oběť jednoho z nich a odmrštěna byla oběť druhého. I řekl tento: „Dozajista zabiji tě,“ i odvětil první: „Bůh přijímá pouze oběti bohabojných.'),
(697, 5, 28, 'I kdybys vztáhl ruku svou, abys mne zabil, nevztáhnu ruky své, abych zabil tebe, neboť zajisté bojím se Boha, Pána světů.'),
(698, 5, 29, 'Raději nechám tebe nésti vinu mou i tvoji: a údělem tvým bude oheň pekelný, odměna nešlechetných.“'),
(699, 5, 30, 'A dohnala jej vášeň, jeho k bratrovraždě, i zabil jej a stal se jedním ze zatracených.'),
(700, 5, 31, 'A seslal Bůh havrana, jenž hrabaje půdu, ukazoval mu, jak skrýti zločin, jejž dopustil se na bratru svém. Řekl: „Běda mi! Zdaž stal jsem se tak slabým, abych nemohl býti jako tento havran a nemohl skrýti zločin, jehož dopustil jsem se na bratru svému?“ A počal litovati.'),
(701, 5, 32, 'Z této příčiny pak stanovili jsme zákonem synům Israele, že kdokoliv zabije bližního, aniž tento byl vražedníkem anebo šiřitelem nepravostí v zemi, jako by byl zabil veškeré lidstvo: ten pak, kdo zachrání život, jakoby byl zachránil lidstvo veškeré. Již dříve přišli k vám proroci naši s jasnými důkazy: nicméně i poté většina lidí páchala výstřednosti v zemi.'),
(702, 5, 33, 'Však takováto bude odplata těch, kteří bojují proti Bohu a proroku jeho a pracují ke způsobení rozvratu v zemi: pobiti neb ukřižováni budou, uřezány jim budou ruce a nohy střídavě, nebo vypovězeni budou ze země. Takováto bude odměna jejich na světě a v budoucím čekají na ně tresty velké,'),
(703, 5, 34, 'vyjma těch, kdož kajícně obrátí se dříve, než zmocníte se jich: a vězte, že Bůh velkým jest v odpouštění, slitovným.'),
(704, 5, 35, 'Vy, kteří jste uvěřili, bojte se Boha a snažte se dosíci přístupu k němu a bojujte horlivě na stezce jeho, aby dobře vedlo se vám.'),
(705, 5, 36, 'Nevěřící pak, kdyby veškerý majetek země, ba dvojnásobný obnos jeho, nabízeli výkupným od trestu v den zmrtvýchvstání, nebude přijmut od nich a trest bolestný je očekává.'),
(706, 5, 37, 'Rádi by vyprostili se z ohně, však nikdy nevyjdou z něho a věčná budou muka jejich.'),
(707, 5, 38, 'Zloději neb zlodějce uřežte ruce, odplatou za to, co zasloužili si dílem rukou svých. Totoť jest varovný trest boží: a Bůh mocný jest a moudrý.'),
(708, 5, 39, 'Však kdožkoli kajícně obrátí se k Bohu poté, když byl spáchal nepravost svou a polepší se, k tomu též Bůh obrátí se: neboť zajisté Bůh velký jest v odpouštění, slitovný.'),
(709, 5, 40, 'Zdaž nevíš, že Bohu přináleží panství nebes i země, a že tresce, koho chce, a odpouští, komu chce: neboť Bůh jest všemocný.'),
(710, 5, 41, 'Ó proroku, nezarmucuj se pro ty, kdož předhánějí se v nevíře: z těch, kteří řekli ústy svými: „Uvěřili jsme,“ však neuvěřila srdce jejich: a ze Židů, kteří poslouchají lži, kteří poslouchají jiné lidi, však nepřicházejí k tobě. Přemísťují slova (Knihy), řkouce: „Je-li vám dáváno toto (znění), přijměte je; ne-li pak, střezte se přijmouti je.“ Koho Bůh zavésti chce v rozkol, tomu nepřispěje u Boha pranic přímluva tvá. Ti, jichž srdce nezachce se Bohu očistiti, pokryti budou hanbou v tomto světě a v budoucím uchystán jest jim trest velký:'),
(711, 5, 42, 'oněm, kteří poslouchají lež a hltají věci nedovolené. Přijdou-li k tobě, abys rozsoudil mezi nimi, buď vyřkni rozsudek svůj, neb vzdal se jich. A vzdálíš-li se jich, nebudou moci uškoditi tobě v ničem: však budeš-li souditi mezi nimi, tedy rozsuď mezi nimi spravedlivě, neboť Bůh zajisté miluje ty, kteří jednají spravedlivě.'),
(712, 5, 43, 'Však jak vezmou si tebe za soudce, když mají již Zákon, v němž zahrnuta jsou nařízení Boha. Nicméně oni odvrátili se od nich poté, když je byli obdrželi a nejsou věřícími.'),
(713, 5, 44, 'V pravdě seslali jsme Zákon, v němž vedení jest a světlo: dle něho soudili Židy proroci, kteří odevzdám byli do vůle boží: rabíni pak a velekněží souditi měli podle oné části Knihy boží, jíž byli opatrovníky a svědky pravosti její. Pročež, Židé, nebojte se lidí, nýbrž bojte se mne: a nekupujte za znamení má věc ceny nepatrné, neb ti, kdož nesoudí dle toho, co Bůh byl seslal, jsou nevěřícími.'),
(714, 5, 45, 'A předepsali jsme jim v Zákoně: „Život za život, oko za oko, nos za nos, ucho za ucho, zub za zub a odveta za poranění.“ Kdožkoli pak obrátí odvetu v almužnu, přispěje tím k vykoupení hříchů svých: a ti, kdož nesoudí dle toho, co Bůh seslal, jsou nepravostnými.'),
(715, 5, 46, 'A ve stopách proroků dali jsme následovati Ježíši, synu Mariinu, na potvrzení starého Zákona: a dali jsme mu Evangelium, obsahující vedení a světlo, potvrzením starého Zákona a vedením a napomenutím pro bohabojné:'),
(716, 5, 47, 'a aby lid, obdrževší Evangelium, mohl souditi dle toho, co Bůh v něm seslal. A kdožkoli nesoudí dle toho, co Bůh seslal, jest nevěřícím.'),
(717, 5, 48, 'A seslali jsme tobě Knihu pravdy, na potvrzení (písem) předešlých a k ochránění jich před změněním: suď tedy mezi nimi dle toho, co Bůh byl seslal a nenásleduj přání jich, opustiv pravdu, tobě zjevenou. Každému z vás určili jsme pravidlo a stezku (vyšlapanou): a kdyby Bůh byl chtěl, byl by učinil vás národem jedním, však neučinil tak proto, že chtěl vás zkoušeti tím, co každému z vás dal. Buďte tudíž horlivými v konání dobrých skutků: k Bohu navrátíte se všichni a on dá vám pak správné vysvětlení toho, v čem jste se rozcházeli.'),
(718, 5, 49, 'Rozsuzuj tedy mezi nimi dle toho, co Bůh byl seslal a nenásleduj choutek jejich: měj se na pozoru, aby neodloudili tě od některých příkazů, seslaných ti Bohem. A odvrátí-li se od tebe, věz, že Bůh chce potrestati je za některé hříchy jejich: neboť zajisté velký jest počet lidí nešlechetných.'),
(719, 5, 50, 'Zdaž přejí si rozsudků podobných oněm v dobách nevědomosti? Kdo lepším nad Boha jest soudcem pro ty, kdož pevně věří.'),
(720, 5, 51, 'Vy, kteří jste uvěřili, nebeřte si Židy ani křesťany za přátele, neboť oni jsou pouze navzájem mezi sebou přáteli; kdožkoli pak z vás vezme si je za přátele, stane se jedním z nich a Bůh zajisté nevede stezkou přímou lidi nepravostné.'),
(721, 5, 52, 'Uvidíš ty, jichž srdce zasažena jsou chorobou, předbíhati se v přecházení k nevěřícím, řkoucí: „Bojíme se, aby nepostihl nás obrat osudu.“ Však možná, že Bůh přivede vítězství, anebo událost nějakou z rozkazu svého a pak počnou litovati svých tajných domněnek.'),
(722, 5, 53, 'Pak řeknou ti, kteří uvěřili: „Nejsou-liž tito oni, kteří přisahali při Bohu, nejpevnější přísahou, že jsou s vámi?“ Niveč uvedeny budou skutky jejich a propadnou zatracení.'),
(723, 5, 54, 'Vy, kteří jste uvěřili, opustí-li kdo z vás náboženství své, tu vzniknouti dá Bůh lidu, jejž milovati bude a jenž milovati bude jej. Poníženým bude lid onen vůči věřícím, však hrdým vůči nevěřícím: bojovati bude na stezce boží a nebude báti se výčitek kohokoliv. To jest milost boží: Bůh popřává jí, komu chce. On zajisté jest nesmírný, vševědoucí.'),
(724, 5, 55, 'Přáteli vašimi jsou (jedině) Bůh a prorok jeho, a ti, kteří zachovávají modlitbu a dávají povinnou almužnu a kdož klekají.'),
(725, 5, 56, 'A kdožkoli béře si za přátele Boha a proroka jeho a ty, kteří uvěřili, jest na straně Boha: a tito zvítězí.'),
(726, 5, 57, 'Vy, kteří jste uvěřili, nebeřte si přátele těch z lidu, obdrževšího Knihu před vámi, kdož tropí si žert a úsměšky z náboženství vašeho, a nevěřících: nýbrž bojte se Boha, jste-li věřícími.'),
(727, 5, 58, 'Aniž těch, kdož tropí si žerty a úsměšky, když voláte k modlitbě: činí tak proto, že jsou lid nechápající.'),
(728, 5, 59, 'Rci: „Vy, kteří jste obdrželi Knihu, zdaž zříkáte se nás pouze proto, že uvěřili jsme v Boha a v to, co sesláno nám bylo a v to, co sesláno bylo před tím: a proto, že většina vás jest nešlechetna?“'),
(729, 5, 60, 'Rci: „Mohu-liž ohlásiti vám horší odplatu, než jaká čeká vás u Boha? Ty, jimž zlořečil Bůh a nad nimiž stojí hněv jeho: z nichž některé v opice a prasata proměnil: ty, kteří Tághúta uctívají: ti všichni, ve špatném jsou položení a daleko zbloudili od rovné stezky.“'),
(730, 5, 61, 'Když přišli k vám, řekli: „Uvěřili jsme“: však nevěřícími přišli a nevěřícími odešli. Však Bůh nejlépe věděl to, co tajili.'),
(731, 5, 62, 'A mnohé z nich spatříš předstihovati se ve špatnosti a nespravedlnosti a v požívání toho, co zapovězeno bylo jim: hanba budiž jim za skutky jejich!'),
(732, 5, 63, 'A kdyby nebyli zbraňovali jim rabíni jejich a velekněží v mluvení špatností a požívání věcí jim zapovězených, věru hanebné bývaly by byly skutky jejich.'),
(733, 5, 64, 'A praví Židé: „Ruka boží upevněna jest okovy.“ Naopak: ruce jejich nechť jsou v okovech a nechť prokleti jsou za to, co říkali. Nikoliv: ruce jeho vztažené jsou a rozdává dary své dle toho, jak chce! A v mnohých z nich to, co sesláno ti bylo Pánem tvým, zcela jistě rozmnoží odboj a nevíru: a uvrhli jsme mezi ně nepřátelství a nenávist, jež potrvá až do dne zmrtvýchvstání. Kdykoliv vznítí oheň války, Bůh uhasí jej: budou snažiti se síti rozbroj v zemi — a Bůh zajisté nemiluje působitelů nepravostí.'),
(734, 5, 65, 'Však kdyby ti, kteří obdrželi Knihu, uvěřili a báli se Boha, zajisté smazali bychom hříchy jejich a uvedli bychom je v zahrady rozkoší:'),
(735, 5, 66, 'a kdyby zachovávali Zákon a Evangelium a to, co sesláno bylo jim Pánem jejich, mohli by požívati všech rozkoší, jež nad hlavami jsou jejich i pod nohama jejich. Někteří z nich jednají poctivě, však většina jich — jak špatné jsou skutky jejich!'),
(736, 5, 67, 'Ó proroku, zvěstuj vše, co sesláno ti bylo Pánem tvým, neboť neučiníš-li tak, nedostál jsi poslání svému. Bůh ochrání tě před zlými lidmi: Bůh zajisté nevede lid nevěřící.'),
(737, 5, 68, 'Rci: „Vy, kteří jste obdrželi Knihu, nebudete míti v ničem opory, dokud nebudete zachovávati Zákon a Evangelium a to, co sesláno vám bylo Pánem vaším. To pak, co sesláno bylo tobě Pánem tvým, v mnohých z nich zcela jistě rozmnoží odboj a nevíru: však nepřipouštěj si starostí pro lid nevěřící.'),
(738, 5, 69, 'Zajisté těm, kteří uvěřili, a Židům a sabejským a křesťanům: těm, kdož uvěřili v Boha a v den poslední a konali dobré skutky: těm netřeba se báti, aniž rmoutiti se.'),
(739, 5, 70, '(Kdysi) přijali jsme smlouvu se syny Israele a poslali jsme jim proroky: však kdykoliv přišli k nim proroci s něčím, co nebylo jim po chuti, tu některé z nich nazývali lháři a některé z nich povraždili.'),
(740, 5, 71, 'Domnívali se, že nepojde jim z toho nic zlého, však slepotou a němotou raněni jsou: poté pak odpustil jim Bůh, však na to zase mnozí z nich oslepli a oněměli. A Bůh dobře pozoruje konání jejich.'),
(741, 5, 72, 'Nevěřícími jsou, kdož praví: „Bůh jest Mesiáš, syn Mariin,“ neboť Mesiáš řekl: „Synové Israele, klaňte se Bohu, Pánu mému a Pánu vašemu.“ Kdožkolivěk činiti bude Bohu společníků, tomu zapoví Bůh vstup do ráje a příbytkem bude mu oheň: a nepravostným nebude pomocníků.'),
(742, 5, 73, 'Nevěřícími jsou ti, kdož praví: „Bůh jest třetím z Trojice,“ neboť není jiného boha kromě Boha jediného: a nepřestanou-li říkati tak, zajisté ti, kdož nevěřícími jsou mezi nimi, dotčeni budou trestem bolestným.'),
(743, 5, 74, 'Neobrátí-li se tedy k Bohu a nebudou-li pak žádati jej za odpuštění, když Bůh zajisté velkým jest v odpouštění, slitovným?“'),
(744, 5, 75, 'Mesiáš, syn Mariin, byl pouhým prorokem: jiní proroci byli před ním. A matka jeho byla ženou pravdomluvnou a oba živili se jídlem: hle, jak jasnými činíme jim znamení svá a hle, jak odvracejí se od nich!'),
(745, 5, 76, 'Rci: „Zdaž uctívati budete vedle Boha něco, co nemůže způsobiti vám ani škody, ani užitku? Jedině Bůh slyší a ví vše.“'),
(746, 5, 77, 'Rci: „Vy, kteří obdrželi jste Knihu, nepřestupujte meze pravdy v náboženství svém, aniž následujte choutek lidí, zbloudivších před vámi a zavedších mnoho lidí do bludu: ti zajisté zbloudili s cesty rovné.“'),
(747, 5, 78, 'Zlořečeni byli ti, kdož neuvěřili ze synů Israele, jazykem Davida i Ježíše, syna Mariina: to stalo se jim proto, že neposlušnými byli a hříšnými:'),
(748, 5, 79, 'nenapomínali se navzájem k ustání ve zlém konání svém: hanba budiž konání jejich!'),
(749, 5, 80, 'Mnohé spatříš mezi nimi vcházeti v přátelství s nevěřícími. Špatné jsou to činy, jimiž provinili se: i rozhněval se na ně Bůh a v mukách trvati budou věčných.'),
(750, 5, 81, 'Kdyby byli uvěřili v Boha, a proroka jeho a v to, co sesláno bylo mu, nebyli by bývali přátelili se s nimi: však většina jich jsou nešlechetníky.'),
(751, 5, 82, 'Zajisté shledáš, že největšími nepřáteli věřících mezi lidmi jsou Židé a ti, kdož Bohu dávají společníky: a zajisté shledáš, že nejbližšími v lásce k věřícím jsou ti, již dí: „Jsme křesťané.“ To proto, že mají mezi sebou kněze a poustevníky, a že nejsou pyšni.'),
(752, 5, 83, 'A když slyší, co sesláno bylo proroku, slzami přetékají oči jejich nad poznanou v tom pravdou a dí: „Pane náš, uvěřili jsme a zapiš nás mezi ty, kdož to dosvědčují.'),
(753, 5, 84, 'A proč bychom nevěřili v Boha a pravdu, nám zjevenou: a proč netoužili bychom po tom, aby Pán náš uvedl nás tam, kde budou spravedliví?“'),
(754, 5, 85, 'I odměnil je Bůh za slova jejich zahradami, pod nimiž řeky tekou; v nich přebývati budou věčně: taková bude odměna těch, kdož dobře činí.'),
(755, 5, 86, 'Však údělem těch, kdož neuvěřili a lží nazývali znamení naše, bude oheň pekelný.'),
(756, 5, 87, 'Vy, kteří jste uvěřili, nezapovídejte požívání dobrých věcí, jež Bůh dovolil vám: ale nepřestupujte mezí, neboť Bůh zajisté nemiluje přestupníků.'),
(757, 5, 88, 'A jezte z toho, čímž obdařil vás Bůh, dovoleného a dobrého: a bojte se Boha, v něhož věříte.'),
(758, 5, 89, 'Bůh nebude trestati vás za mýlku, jež přihodí se vám v přísaze vaší: ale bude trestati vás za přestoupení vážně učiněné přísahy. Pokáním za to budiž nakrmení deseti nuzných potravou prostředního druhu, jakou živí se rodina vaše: aneb obléknutí jich nebo vykoupení otroka. Však kdo k tomu nenalezne prostředků,. nechť postí se po dobu tří dnů. Takové jest pokání za přestoupení přísah vašich poté, když jste byli přisahali: pročež dodržujte přísahy své. Takovým způsobem objasňuje vám Bůh znamení svá, abyste mu byli vděčnými.'),
(759, 5, 90, 'Vy, kteří jste uvěřili, zajisté víno a hazardní hry a sochy a vrhání losů šípy, jsou neřesti z díla Satanova: pročež straňte se jich, aby se vám dobře vedlo.'),
(760, 5, 91, 'Satan chce vrhnouti mezi vás nepřátelství a zášť vínem, a hazardní hrou a vzdáliti vás od vzpomínání na Boha a modlitbu: zdaž tedy nebudete se vzdalovati jich?'),
(761, 5, 92, 'Poslouchejte Boha a poslouchejte proroka a mějte se na pozoru: však odvrátíte-li se, vězte, že prorok náš jest pouze hlasatelem pravdy zjevné.'),
(762, 5, 93, 'Ti, kteří uvěřili a konali dobré skutky, nebudou míti viny za to, co byli jedli, bojí-li se Boha a věří a konají dobré skutky, a opět bojí se Boha a věří, a opět bojí se Boha a konají dobro: neboť Bůh miluje ty, kdož dobře činí.'),
(763, 5, 94, 'Vy, kteří jste uvěřili, Bůh zajisté bude chtíti zkoušeti vás tím, že pošle vám zvěřinu do cesty tak, že dostihnouti ji budou moci ruce i oštěpy vaše, aby zvěděl, kdo bojí se jej v hloubi srdce svého. A tomu, kdo po té přestoupí zákaz, připraven jest trest bolestný.'),
(764, 5, 95, 'Vy, kteří jste uvěřili, neusmrcujte zvěřiny během pouti své (do Mekky). Kdožkoli svévolně zabije zvěř během pouti, nechť v náhradu dá domácí dobytče stejné ceny a o tom nechť rozsoudí dva mužové spravedliví mezi vámi. Dobytče to pak přivedeno bude jako dar obětní ke Ka\'bě. Jinak za pokání uloženo budiž dání potravy nuzným, aneb stejnocenným vyrovnáním budiž půst, aby provinilec okusil zlé následky svého skutku. Bůh promíjí minulé poklesky, však obnoví-li je kdo, Bůh pomstí se na něm, neboť Bůh jest mocný a pomsta jemu přináleží!'),
(765, 5, 96, 'Dovoleno jest vám loviti ryby a jísti je potravou sobě a poutníkům: však zapovězen jest vám lov na souši, pokud zahaleni jste v háv poutnický. A bojte se Boha, neboť u něho budete shromážděni.'),
(766, 5, 97, 'Bůh učinil Ka\'bu domem posvátným a stanovištěm lidí: a určil měsíc posvátný a dary obětní a ozdoby obětní. To proto, abyste věděli, že Bohu známo jest vše, což na nebi jest i na zemi a že Bůh jest vševědoucí.'),
(767, 5, 98, 'Vězte též, že Bůh přísný jest v trestání, ale též velký v odpouštění, slitovný.'),
(768, 5, 99, 'Prorok jest povinen jedině hlásati vůli boží: Bůh pak dobře ví, co přiznáváte i co tajíte,'),
(769, 5, 100, 'Rci: „Není rovnocenné zlo s dobrem, ač zlo vábí vás svým množstvím: i bojte se Boha, lidé rozumní, aby dobře se vám vedlo.“'),
(770, 5, 101, 'Vy, kteří jste uveřili, netažte se na věci, které kdyby odhaleny vám byly, špatně by na vás působily. Však budete-li tázati se na ony věci poté, až celý Korán vám bude zjeven, budou vám odhaleny: Bůh promine vám Pak dotazování vaše, neboť Bůh velký jest v odpouštění, laskavý.'),
(771, 5, 102, 'Před vámi již tázali se na věci ony lidé, však po zvědění jich stali se nevěřícími.'),
(772, 5, 103, 'Bůh neučinil nařízení ohledně Bahíry, aniž ohledně Sá\'iby, aniž ohledně Wasíly, aniž ohledně Hámi-a: však ti, kdož neuvěřili, vymýšlejí tyto lži na účet Boha a většina jich nemá rozumu.'),
(773, 5, 104, 'Když řečeno bylo jim: „Pojďte a přijměte, co Bůh seslal a skupte se kol proroka,“ řekli: „Dostatečna jest pro nás víra, již našli jsme u otců svých.“ A vždyť přece otcové jejich nevěděli ničeho, aniž vedeni byli stezkou pravou.'),
(774, 5, 105, 'Vy, kteří jste uvěřili, za duše své sami jste zodpovědni, neuškodí vám ti, kteří bloudí, jste-li vy sami dobře vedeni. K Bohu navrátíte se všichni a on pak vám poví, co jste činili.'),
(775, 5, 106, 'Vy, kteří jste uvěřili, vezměte si svědky, když kdo z vás blízek jest smrti a když konáte poslední vůli: zavolejte dva muže spravedlivé mezi vámi, anebo dva jiné z jiného lidu, jste-li na cestě v zemi a tehdy postihl by vás osud smrti. Zavřete je pak oba ve světnici poté, když se byli pomodlili a máte-li ještě pochybnosti o nich, necháte je oba přisahati při Bohu takto: „Nenecháme koupiti se úplatkem, i kdyby nabízen byl nám příbuznými našimi a nezatajíme ničeho ze svědectví vzatého před Bohem: jinak nechť patříme k nešlechetným.“'),
(776, 5, 107, 'Vyjde-li pak na jevo, že oni dva svědci prohřešili se proti přísaze své, dva jiní, z těch, kteří je v tom byli usvědčili, a již blízkými jsou přáteli umírajícího, nechť zaujmou jejich místo a nechť oba přisahají při Bohu: „V pravdě svědectví naše pravdivějším jest svědectví oněch dvou a netvrdíme ničeho nepravdivého: jinak pak byli bychom z nepravostných.“'),
(777, 5, 108, 'Tímto způsobem usnadněno bude, aby lidé dávali pravdivé svědectví, nebo báli se, aby nebylo vzato svědectví jiných poté, když oni byli naň přisahali. A bojte se Boha a slyšte jej: neboť Bůh nevede cestou pravou lid prostopášný.'),
(778, 5, 109, 'V onen den, kdy shromáždí Bůh proroky své, řekne jim: „Co bylo vám odpověděno?“ I řeknou: „Věru, nevíme ničeho, neboť tys znatelem všech tajemství.“'),
(779, 5, 110, 'Tehdy řekne Bůh: „Ježíši, synu Mariin, pomni milosti, kterou jsem prokázal tobě a matce tvé, posíliv tě Duchem Svatým, abys mluvil k lidem, již v kolébce i jako muž dospělý: a když naučil jsem tě Knize a moudrosti a Zákonu i Evangeliu: pak když stvořil\'s z hlíny podobu ptáka a z dopuštění mého dechl\'s na ni a stala se živoucím ptákem z dopuštění mého: a když léčil\'s slepé a malomocné a z dovolení mého křísil\'s mrtvé: když pak chránil jsem tě před výpady synů Israele, když přišel\'s k nim s jasnými důkazy:“ i řekli ti, kdož neuvěřili mezi nimi: „Totoť zjevné jest kouzelnictví.“'),
(780, 5, 111, 'A když vnukl jsem apoštolům: „Věřte ve mne a v proroka mého,“ odvětili: „Uvěřili jsme a tys svědkem, že oddáni jsme do vůle boží.“'),
(781, 5, 112, 'Tehdy pak řekli apoštolové: „Ježíši, synu Mariin, zdaž možno jest Pánu tvému seslati nám s nebe (prostřený) STŮL?“ I řekl: „Bojte se Boha, jste-li věřícími.“'),
(782, 5, 113, 'Řekli: „Chceme jísti s něho, aby uspokojena byla srdce naše a abychom věděli, žes pravdu mluvil nám a pravdu onu mohli dosvědčiti.“'),
(783, 5, 114, 'Řekl tehdy Ježíš, syn Mariin: „Velký Bože, Pane náš, sešli nám stůl (prostřený) s nebe, který nechť svátkem jest prvnímu i poslednímu z nás a znamením od tebe: a nakrm nás, neboť ty zajisté nejlepším jsi ze všech živitelů.“'),
(784, 5, 115, 'I řekl Bůh: „Zajisté sešlu jej vám: a kdožkoli z vás poté neuvěří, toho zajisté potrestám trestem, jakým nikoho ze stvoření jsem dosud neztrestal.“'),
(785, 5, 116, 'Tehdy řekl Bůh: „Ježíši, synu Mariin, zdaž řekl\'s kdy lidem: Vezměte si mne a matku mou za bohy vedle Boha?“ Řekl: „Chvála budiž tobě! Není na mně, abych říkal, co není pravdou. Kdybych byl podobně mluvil, ty zajisté byl bys věděl o tom, neboť ty víš, co ve mně jest, však já nevím, co v tobě jest. Ty zajisté jediným jsi znatelem tajemství.'),
(786, 5, 117, 'Neříkal jsem jim ničeho jiného, než cos nakázal mně: ‚Vzývejte Boha, Pána mého a Pána vašeho!‘ Dokud žil jsem mezi nimi na zemi, mohl jsem svědčiti proti nim ohledně skutků jejich, však poté, když vzal\'s mne k sobě, sám byl\'s dohlížitelem nad nimi, neboť tys všech věcí svědkem.'),
(787, 5, 118, 'Potrestáš-li je — jsou tvými služebníky: odpustíš-li jim — tyť zajisté mocný jsi a moudrý!“'),
(788, 5, 119, 'Tehdy řekne Bůh: „V tento den prospěje pravdomluvným pravdomluvnost jejich: jim připraveny jsou zahrady, pod nimiž řeky tekou, v nichž přebývati budou věčně. Bůh spokojen jest s nimi a oni s ním a to štěstí jest veliké.'),
(789, 5, 120, 'Bohu přináleží panství nebes i země a vše, co v obou se nalézá, a on nade všemi věcmi moc má.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Chvála Bohu, jenž stvořil nebe i zemi a ustanovil temnoty i světlo! A pak ti, kdož neuvěřili, staví Pánu svému rovných.'),
(791, 6, 2, 'On to byl, jenž stvořil vás z hlíny a určil lhůtu vašeho žití: a u něho též určena jest lhůta (zmrtvýchvstání) — vy pak přece ještě pochybujete!'),
(792, 6, 3, 'On jest Bohem na nebi i na zemi: on zná všechna tajemství i zjevná doznání vaše a ví, co si zasluhujete.'),
(793, 6, 4, 'Není jednoho znamení mezi znameními, jež přišla jim od Pána jejich, od kterého by se nebyli odvrátili.'),
(794, 6, 5, 'A lží nazývali pravdu, když přišla jim: však vbrzku dojde je zpráva o tom, z čeho žert si tropili.'),
(795, 6, 6, 'Zdaž nevidí, kolika pokolením dali jsme zahynouti před nimi? Upevnili jsme bydlení jejich na zemi pevněji, než bydlení vaše: přehojné seslali jsme jim deště, a pod nohami jejich téci dali jsme řekám: nicméně zahubili jsme je pro hříchy jejich a jiným dali jsme po nich vzrůsti pokolením.'),
(796, 6, 7, 'I kdybychom byli seslali ti Knihu na papyrech, a kdyby byli mohli; dotknouti se jí rukama svýma, jistě by byli řekli neveřící: „Toto jest kouzelnictví zjevné.“'),
(797, 6, 8, 'A říkají: „Dokud nebude seslán mu anděl —,“ však kdybychom byli seslali anděla, byl by jistě býval s nimi konec: v tom případě nebylo by jim bývalo popřáno odkladu.'),
(798, 6, 9, 'A i kdybychom poslali anděla, tu dali bychom mu podobu muže a oděli bychom jej šatem, podobným jejich.'),
(799, 6, 10, 'Již před tebou tropili si lidé smích z proroků, však obklopeni byli vysměvační z nich tím z čeho žert si tropili.'),
(800, 6, 11, 'Rci: „Projděte zemí a vizte, jaký byl trest oněch, kdož nazývali proroky lháři.“'),
(801, 6, 12, 'Rci: „Komu přináleží vše, což na nebi jest a na zemi?“ Rci: „Bohu!“ On předepsal sobě milosrdenství jako povinnost: on zajisté shromáždí vás v den zmrtvýchstání, o němž není pochyby. Ty, kteří duše své v zatracení uvrhli a (proto) nevěří.'),
(802, 6, 13, 'Jemu přináleží vše, co přebývá v noci i ve dne: a on vše slyší a ví.'),
(803, 6, 14, 'Rci: „Zdaž koho jiného vezmu si za přítele kromě Boha. Stvořitele nebe i země, jenž vše živí, sám ničím nejsa živen?“ Rci: „V pravdě nakázáno jest mi, abych byl prvním, jenž odevzdá se plně do vůle boží: ty pak nepatřiž k mnohobožcům.“'),
(804, 6, 15, 'Rci: „Zajisté bojím se odporovati Pánu svému, aby netrestal mne v onen den velký!'),
(805, 6, 16, 'Kdožkoli pak bude zbaven trestu v den onen, tehdy smiloval se nad ním Bůh: a to dojista bude štěstím zjevným.'),
(806, 6, 17, 'Sešle-li Bůh na tě zlo, nikdo nemůže tě zprostiti jej mimo něho a sešle-li ti dobro - onť zajisté nade všemi věcmi moc má.'),
(807, 6, 18, 'Onť nejvyšším jest vládcem služebníků svých: onť moudrý jest a o všem zpraven!“'),
(808, 6, 19, 'Rci: „Co nejpádnějším jest svědectvím?“ Rci: „Bůh sám Bůh jest svědkem mezi mnou a vámi: a zjeven byl mi tento Korán, abych jím varoval vás a ty, které dojde. Zdaž dosvědčovati chcete snad, že vedle Boha jsou bohové jiní?“ Rci: „Já takového svědectví nedávám.“ Rci: „Naopak, on jest Bůh Jediný a zajisté nenesu viny za to, co vy s ním spolčujete.“'),
(809, 6, 20, 'Ti, kterým jsme dali Knihu, znají ji, jako znají syny své; ti, kteří sebe samy uvrhli v zatracení a (proto) nevěří.'),
(810, 6, 21, 'A kdo nepravostnějším jest nad toho, jenž vymýšlí lži o Bohu a lží nazývá znamení naše? Zajisté, Bůh nepopřeje úspěchu nepravostným.'),
(811, 6, 22, 'A v den, kdy shromáždíme je všechny, řekneme k těm, kteří společníky dávali Bohu: „Nuž, kde společníci jsou vaši, jež stavěli jste na roveň Bohu dle domněnek svých?“'),
(812, 6, 23, 'Pak nenaleznou jiné vytáčky, než to, že řeknou: „Při Bohu, Pánu našem, nedávali jsme mu společníků.“'),
(813, 6, 24, 'Hle, jak lhou proti sobě samým a jak ztratila se jim božstva jimi vymyšlená!'),
(814, 6, 25, 'A někteří z nich naslouchají ti, však clonou zastřeli jsme srdce jejich, takže nelze jim pochopiti Koránu, a tíhu vložili jsme v uši jejich. A i kdyby viděli všemožná znamení, nevěřili by v ně a dokonce přijdou k tobě a hádají se s tebou ti, kteří nevěří, řkouce: „Věru, toto jsou pouze pověsti starých.“'),
(815, 6, 26, 'A zabraňují druhým věřiti a sami vzdalují se: však nicméně sebe samy vrhají v záhubu a nejsou si toho vědomi.'),
(816, 6, 27, 'A kdybys viděl je tehdy, až postaveni budou nad oheň pekelný, volati budou: „Kéž mohli bychom se navrátiti! Již nenazývali bychom lží znamení Pána našeho a stali bychom se věřícími.“'),
(817, 6, 28, 'Ano, zjevným stalo se jim co před tím skrývali: však i kdyby se navrátili, znovu by přidrželi se toho, co bylo jim zapovězeno, neboť zajisté lháři jsou.'),
(818, 6, 29, 'Říkají: „Není jiného života, mimo života našeho pozemského a nikdy budeme vzkříšeni.“'),
(819, 6, 30, 'Však kdybys viděl je tehdy, až postaveni budou před Pána svého, řekne Bůh: „Nuž, není to pravda?“ I řeknou: „Zajisté ano, při Pánu našem!“ Tehdy řekne Bůh: „Okuste tedy trestu za nevíru svou.“'),
(820, 6, 31, 'Ztraceni budou ti, kdož lží nazývali setkání s Bohem, když přikvačí na ně nenadále hodina. Řeknou tehdy: „Běda nám, že na lehkou váhu brali jsme ji,“ a ponesou břemena svá na zádech svých: špatné bude břímě jejich!'),
(821, 6, 32, 'Život pozemský pouze hrou jest a kratochvílí a bydlení v životě budoucím zajisté větší cenu má pro bohabojné: zdaž nikdy toho nepochopíte?'),
(822, 6, 33, 'Víme dobře, že zarmucují tě řeči jejich: však ne tebe nazývají lhářem, nýbrž znamení boží zapírají nepravostní.'),
(823, 6, 34, 'Vždyť již před tebou nazýváni byli proroci lháři: však oni trpělivě snášeli obvinění tato i příkoří, dokud nepřišla jim pomoc naše, neboť nikomu nelze změniti slov božích, jakž známo ti je z dějin proroků.'),
(824, 6, 35, 'Zajisté tíží tě vzdalování jejich a kdybys mohl, přál bys si, abys mohl vyhloubiti propast v zemi, nebo žebřík postaviti k nebi, a tak učinil jim znamení. Kdyby Bůh chtěl, shromáždil by je všechny na cestě pravé: pročež nebuď pošetilým.'),
(825, 6, 36, 'Bůh vyslyší jedině ty, kdož naslouchají: a mrtvé Bůh vzkřísí, načež k němu navrátí se.'),
(826, 6, 37, 'Říkají: „Nebude-li znamení sesláno mu od Pána jeho, neuvěříme.“ Rci: „Bůh zajisté má v moci seslati znamení,“ však většina jich není si toho vědoma.'),
(827, 6, 38, 'Není živočicha na zemi, aniž ptáka létajícího pomocí křídel svých, kteří by netvořili pokolení, podobného vašemu. Neopomenuli jsme ničeho v Knize: všichni tvorové pak shromážděni budou u Pána svého.'),
(828, 6, 39, 'Ti, kdož lží nazývají znamení naše, hluši a němi jsou, v temnotách: Bůh zavádí do bludu, koho chce, a koho chce, uvádí na stezku přímou.'),
(829, 6, 40, 'Rci: „Co míníte? Kdyby přišel na vás trest boží, neb přišla hodina, budete volati někoho jiného, než Boha? Řekněte, jste-li pravdomluvnými!“'),
(830, 6, 41, 'Ano, jej volati budete a on zbaví vás toho, čeho prosíte jej, aby zbavil vás, bude-li chtíti: a tehdy zapomenete na ty, jež dávali jste mu za společníky.'),
(831, 6, 42, 'Již před tebou poslali jsme proroky k národům a seslali jsme na národy ony nehody a protivenství, aby ponížili se.'),
(832, 6, 43, 'Nicméně, když protivenství seslali jsme na ně, neponížili se, nýbrž zatvrdla srdce jejich a Satan způsobil, že zalíbení nalezli ve skutcích svých.'),
(833, 6, 44, 'A když zapomněli varování proroků, otevřeli jsme jim brány veškerých radostí: a když nejvíce radovali se z toho, co obdrželi, z nenadání dotkli jsme se jich a v zoufání uvrženi jsou,'),
(834, 6, 45, 'a do posledního zničen byl lid nepravostný: a chvála Bohu, Pánu světů veškerých!'),
(835, 6, 46, 'Rci: „Co míníte? Kdyby Bůh odejmul vám zrak i sluch a pečeť vložil na srdce vaše, který bůh mimo Boha mohl by vám je nahraditi? Hle, kolikerým způsobem ukazujeme jim znamení svá a přece se od nich odvracejí!“'),
(836, 6, 47, 'Rci: „Co míníte? Kdyby trest boží přišel na vás z čista jasna, anebo byl předběžně oznámen, bude kdo jiný zahuben, vyjma nepravostných?“'),
(837, 6, 48, 'Posíláme proroky své pouze jako hlasatele dobrých zvěstí, neb jako varovatele: a kdožkoli uvěří, a polepší se, tomu netřeba se báti, aniž rmoutiti se.'),
(838, 6, 49, 'A ty, kdož lží nazývali znamení naše, zastihne trest za nešlechetnost jejich.“'),
(839, 6, 50, 'Rci: „Neříkám vám: U mne jsou pokladnice boží, aniž říkám: Známy jsou mi věci skryté, aniž říkám vám: Jsem anděl. — Následuji pouze to, co zjeveno mi bylo.“ Rci: „Zdaž roven jest slepý vidoucímu? Což nedovedete si toho rozložiti?“'),
(840, 6, 51, 'A varuj ty, kdož bojí se, že jednoho dne shromážděni budou u Pána svého, mimo něhož nebudou míti ani zastánce, ani přímluvce — aby báli se.'),
(841, 6, 52, 'A nezapuzuj těch, kteří volají Pána svého zrána i za večera, toužíce po spatření tváře jeho. Nepřináleží ti souditi je v čem pro pohnutky jejich, aniž přináleží jim souditi tebe v čem pro zámysly tvé. Zapudíš-li je pak, patřiti budeš mezi nepravostné.'),
(842, 6, 53, 'Takovýmto, způsobem zkoušeli jsme je navzájem, aby řekli lidé: „Zdaž tito jsou to mezi námi, jež Bůh obdařil přízní svou?“ Což nezná Bůh nejlépe ty, kteří jsou vděčni?'),
(843, 6, 54, 'Když přijdou k tobě ti, kteří uvěřili ve znamení naše, rci: „Pokoj s vámi! Pán váš předepsal sobě milosrdenství za povinnost: a tak, dopustí-li se kdo z vás hříchu z nevědomosti a pak obrátí se kajícně a polepší: Bůh zajisté velký jest v odpouštění, slitovný.'),
(844, 6, 55, 'Takto jasně vysvětlujeme znamení svá, aby zjevnou byla učiněna stezka zločinných.'),
(845, 6, 56, 'Rci: „Zakázáno bylo mi, abych uctíval ty, jež vzýváte vedle Boha.“ Rci: „Nebudu následovati choutek vašich, neboť zbloudil bych pak a nebyl bych veden správně.“'),
(846, 6, 57, 'Rci: „V pravdě jednám dle jasných důkazů od Pána svého, však lží je nazýváte. To, co žádáte si, abych uspíšil nad vámi, není v moci mé: soud pouze u Boha jest; on prohlásí pravdu a on nejlepším jest hádek skončovatelem.“'),
(847, 6, 58, 'Rci: „Kdyby v moci mé bylo to, co žádáte si, abych uspíšil nad vámi, byl by již býval skončen rozpor mezi mnou a vámi: však Bůh nejlépe zná nepravostné.'),
(848, 6, 59, 'Onť klíče má k věcem skrytým: nikdo nezná jich mimo něho. Onť ví, co na souši jest i na moři a nespadne lístku bez vědomí jeho: a není zrnka v temnotách země, ni stébla zeleného, ani zvadlého, jež zanešeno by nebylo v Knize zjevné.'),
(849, 6, 60, 'A on jest to, jenž uspává vás za noci a ví, co zasloužili jste si během dne: pak probouzí vás za dne, aby naplněna byla (vaše) lhůta předurčená: pak navrátíte se k němu: a pak on zpraví vás o tom, co jste činili.'),
(850, 6, 61, 'A on vítězným jest nad služebníky svými: on posílá strážce, aby bděli nad vámi, dokud nepřijde k některému z vás smrt: tehdy poslové naši přijmou duši jeho a nikoho neopomenou.'),
(851, 6, 62, 'Poté navráceni jsou k Bohu, skutečnému Pánu svému. Zdaž jemu jedinému nepřináleží soud? On zajisté nejrychlejším jest z účtovatelů.“'),
(852, 6, 63, 'Rci: „Kdo vysvobodí vás od temnot souše a moře, když voláte k němu pokorně a potají, řkouce: Vysvobodíš-li nás z tohoto neštěstí, zajisté budeme ti vděčni?“'),
(853, 6, 64, 'Rci: „Bůh jedině vysvobodí vás z něho a z každého protivenství: a přece ještě dáváte mu společníky!“'),
(854, 6, 65, 'Rci: „V jeho moci jest seslati na vás trest s hůry i z pod nohou vašich a uvrci vás v nekoj roztržek a dáti jedněm z vás okusiti násilí druhých. Hle, jak různým způsobem vysvětlujeme jim znamení svá, aby konečně pochopili.'),
(855, 6, 66, 'Však lid tvůj lži nazval (Korán), ač pouhou jest pravdou. Rci: „Nejsem nikterak obhájcem vaším.'),
(856, 6, 67, 'Každé proroctví má své určité místo a věru, zvíte o tom!“'),
(857, 6, 68, 'Uvidíš-li pak některé ani pouštějí se do hovoru o znameních našich, drž se jich zpátky, dokud nepřevedou rozmluvu na jiný předmět: a stane-li se, že Satan dá ti zapomenouti na tuto výstrahu, neprodlévej, jakmile sis ji byl připomněl, nadále mezi nepravostnými.'),
(858, 6, 69, 'Ne snad, že bohabojným příslušelo by je za to nějak souditi: ale proto mají býti toho pamětlivi, aby báli se Boha.'),
(859, 6, 70, 'A vzdal se těch, kteří za hru a kratochvíli považují náboženství své a jež omámil život pozemský: a připomeň jim tímto, že každá duše propadne (záhubě) za své vlastní skutky a nebude míti zastánce, ani přímluvce kromě Boha. I kdyby pak nabízela všemožné vyrovnání, nebude přijato od ní: a těm, kdož záhubě propadli věčné za skutky své, nápojem bude voda vroucí a trest bolestný je očekává za nevíru.jejich.'),
(860, 6, 71, 'Rci: „Zdaž vedle Boha budeme vzývati někoho, jenž nemůže nám ani prospěti, ani škoditi? Obrátíme-liž se na podpatcích svých poté, když Bůh uvedl nás na cestu přímou, podobni tomu, jenž vlivem zlých duchů zabloudil v poušti, ač soudruzi jeho naň volají, zvouce jej na pravou cestu: Pojď k nám!“ Rci: „Zajisté vedení boží jest pravé vedení: a poručeno nám bylo, abychom oddali se do vůle Pána světů.“'),
(861, 6, 72, 'A zachovávejte modlitbu a bojte se Boha: neboť u něho budete shromážděni.'),
(862, 6, 73, 'Onť v pravdě stvořil nebe i zemi a v den, kdy řekne: „Staň se,“ stane se. Slovo jeho pravdou jest: jemu jedinému náleží moc v den, kdy zahlaholí roh: on stejně dobře zná svět neviditelný, jako viditelný, neboť on moudrý jest a o všem zpraven.'),
(863, 6, 74, 'A (vzpomeň), když Abraham řekl otci svému Ázarovi: „Zdaž modly béřeš si za bohy? V pravdě vidím, že ty i lid tvůj v bludu jste zjevném.“'),
(864, 6, 75, 'A takto ukázali jsme Abrahamovi panství nebes i země,aby utvrzen byl ve vedení svém.'),
(865, 6, 76, 'A když stíny noci obklopily jej, spatřil hvězdu i řekl: „Totoť jest Pán můj !“ Však když zašla, řekl: „Zalíbení nemám v bohu mizivém.“'),
(866, 6, 77, 'Když pak spatřil vycházeti měsíc, děl: „Totoť jest Pán můj!“ Však když zašel, řekl: „Kdyby můj (pravý) Pán mne nebyl vedl, byl bych jistě jedním v lidu zbloudilém.“'),
(867, 6, 78, 'Když pak spatřil vycházeti slunce, děl: „Totoť jest Pán můj; tenť, zajisté jest největším!“ Však když zašlo, děl: „Lide můj, odříkám se vašeho spolčování s Bohem bohů jiných.'),
(868, 6, 79, 'Obracím tvář svou k tomu, jenž stvořil nebe i zemi, jsa následovníkem pravé víry a nejsem z těch, kdož spolčují s Bohem bohy jiné.“'),
(869, 6, 80, 'I hádal se s ním o tom lid jeho. Řekl: „Zdaž hádati se chcete se mnou o Bohu, jenž uvedl mne na stezku pravou? Jáť nebojím se těch, které spolčujete s ním, leda že by Bůh něco chtěl; neboť Bůh obsahuje vše věděním svým: což nevezmete toho v úvahu?'),
(870, 6, 81, 'A jakž měl bych báti se těch, které spolčujete s Bohem, když vy nebojíte se spolčovati s Bohem cosi, k čemu on nebyl seslal vám nijakého zmocněním ? Která z obou stran má větší nárok na bezpečí — rcete, víte-li?'),
(871, 6, 82, 'Ti, kdož uvěřili a neodívají víry své nepravostí, zajisté v bezpečí jsou a vedeni cestou pravou.”'),
(872, 6, 83, 'Tyto důkazy dali jsme Abrahamovi proti lidu jeho. Povyšujeme na stupně proroctví koho chceme: Pán tvůj zajisté moudrý jest a vševědoucí.'),
(873, 6, 84, 'A dali jsme mu Isáka a Jakuba a vedli jsme oba cestou pravou: a před tím uvedli jsme na cestu pravou Noema a z potomstva jeho Davida a Šalamouna a Joba a Josefa a Mojžíše a Árona. Takovýmto způsobem odměňujeme ty, kdož dobře činí.'),
(874, 6, 85, 'A Zachariáš a Jan a Ježíš a Eliáš: jedenkaždý z nich patřil ke spravedlivým.'),
(875, 6, 86, 'A Ismaele a Elisea a Jonáše a Lota: všechny, tyto vyznamenali jsme nad ostatní lidstvo.'),
(876, 6, 87, 'Rovněž z otců jejich i potomků a bratrů jejich vyvolili jsme mnohé a vedli jsme je na stezku přímou.'),
(877, 6, 88, 'Toto jest vedení Boha: vede jím, koho chce ze služebníků svých: však spolčují-li s ním jiné bohy, pak bezvýslednými budou všechna díla jejich.'),
(878, 6, 89, 'To jsou oni, kterým dali jsme Knihu, a soudnost a proroctví: však neuvěrí-li v ně potomstvo jejich, svěříme je lidu, jenž nebude odpírati jim víry své.'),
(879, 6, 90, 'Tito jsou oni, jež Bůh uvedl na cestu pravou: tudíž následuj vedení jejich. Rci: „Nežádám od vás za to platu: jestiť to pouze napomenutí všemu stvoření.“'),
(880, 6, 91, 'Nečiní si správného úsudku o moci Boha ti, kdož říkají: „Nikdy neseslal Bůh ničeho tvoru lidskému.“ Rci „Kdo seslal Knihu, kterou přinesl Mojžíš, aby světlem a vedením byla lidstu: kterou píšete na svitcích papyru, prohlašujíce část z ní veřejně, ale zatajujíce velký díl její, ač poučeni jste jí byli o tom, co jste neveděli ani vy, ani otcové vaši?“ Rci: „Bůh (to byl),“ a pak ponech je, aby bavili se planými řečmi svými.“'),
(881, 6, 92, 'A tuto Knihu seslali jsme plnou požehnání a na potvrzení Knihy, kterou již máte, abys varoval jí matku měst a všechno vůkolní obyvatelstvo. A ti, kdož věří v život budoucí, věří v ni a přesně dodržují modlitby své.'),
(882, 6, 93, 'Však kdo nepravostnějším jest toho, jenž lži vymýšlí o Bohu, aneb říká: „Bylo dáno mi zjevení,“ ač pranic nebylo mu zjeveno; a toho, jenž praví: „Seslati mohu stejnou knihu,“ jakou seslal Bůh?“ Však kdybys viděl nepravostné v záplavě smrti, a anděly, ani vztahují ruce své řkouce: „Vydejte duše své: dnes odměněni budete trestem potupným za to, co nepravdivého říkali jste o Bohu a za to, že vůči znamením jeho pyšně chovali jste se.'),
(883, 6, 94, 'Vracíte se k nám nyní, samotni, tak jak stvořili jsme vás z prvopočátku, zanechávajíce za sebou všechny statky, jimiž obštědřili jsme vás: a nevidíme přicházeti s vámi přímluvčích vašich, o kterých domnívali jste se, že společníky jsou Boha. Přetrženy jsou všechny pásky mezi vámi a prchli od vás ti, které mnili jste (bohorovnými).“'),
(884, 6, 95, 'A Bůh dává vzcházeti zrnu i pecce datlové: vyvádí, živé z mrtvého a vyvádí mrtvé ze živého: takový jest Bůh! Proč tedy odvracíte se od něhož'),
(885, 6, 96, 'On dává vzcházeti ranní zoře: noc určil k odpočinku a slunce a měsíc k měření času. Takové jest určení Mocného, Vševědoucího.'),
(886, 6, 97, 'On učinil vám hvězdy, abyste vedeni jimi byli v temnotách souše i moře. Jasně ukázali jsme znamení svá těm, kdož chápou.'),
(887, 6, 98, 'On jest to, který vzbudil vás z jedné duše a určil vám příbytek i schránu: takto jasně ukázali jsme znamení svá lidem, kteří dovedou porozuměti.'),
(888, 6, 99, 'On jest to, který sesílá s nebe vodu: a dáváme ní vzrůsti bylinám všeho druhu; vodou dáváme vzrůsti zeleni, z níž vzrůstají pak urozená zrna obilná, a palmám, jichž větve visící hrozny datlí nesou a zahrady révy vinné a olivy a jablka granátová, jež podobají se sobě navzájem, ale nejsou stejná. Pohleďte na plody jejich, jak tvoří se a dozrávaji, v tom zajisté znamení jsou lidu věřícímu.'),
(889, 6, 100, 'Nicméně učinili Džinny společníky Boha, ač on sám je byl stvořil: a vynalezli proň syny a dcery v nevědomosti své. Chvála budiž jemu: on povýšen jest nade vše, co mu připisují!'),
(890, 6, 101, 'Stvořitel nebe i země, jakž mohl by míti syna, když nemá společnice a když všechno stvořil a všechno ví?'),
(891, 6, 102, 'Toto jest Bůh, Pán váš: není Boha, kromě něho, Stvořitele všech věcí; pročež jemu se klaňte, neboť on nade vším vede dozor.'),
(892, 6, 103, 'Ničí pohled nedostihne jej, však on dostihuje pohledy (lidí): on, vše pronikající, o všem zpravený.'),
(893, 6, 104, 'Nyní viditelné přišly vám důkazy od Pána vašeho: kdožkoli vidí je, k jeho vlastnímu jest to prospěchu: kdožkoli pak slepým jest vůči nim, k jeho vlastní jest to škodč. Jáť pak nejsem nad vámi opatrovníkem!'),
(894, 6, 105, 'Takto různým způsobem vysvětlujeme znamení svá, aby řekli: „Jsi učený,“ a abychom objasniti je mohli lidem chápajícím.'),
(895, 6, 106, 'Následuj to, co zjevenoti bylo Pánem tvým; není Boha kromě něho! a vzdaluj se mnohobožců.'),
(896, 6, 107, 'Kdyby Bůh chtěl, nedávali by mu společníků: však neustanovili jsme tě opatrovníkem nad nimi, aniž jsi poručníkem jejich.'),
(897, 6, 108, 'Neurážej nikterak božství, jež vzývají vedle Boha, aby z nenávisti neuráželi Boha v nevědomosti své. Takovýmto způsobem zkrášlili jsme každému národu skutky jeho: pak navrátí se k Pánu svému a on jim poví, jaké byly skutky jejich.'),
(898, 6, 109, 'A nejopravdovější přísahou přisahali při Bohu, že učiněno-li jim bude znamení, jistě uvěří v ně. Rci: „Znamení činiti jest jedině v moci Boha a nedává jich pocítiti vám, neboť i kdyby učiněna byla, neuvěří.“'),
(899, 6, 110, 'A odvrátíme srdce jejich i zraky od pravdy, ježto neuvěřili napoprvé a necháme je blouditi (bez vodítka) ve svévoli jejich.'),
(900, 6, 111, 'I kdybychom anděly seslali jim, a i kdyby mrtví k nim promlouvali, a i kdybychom shromáždili všechny věci, před nimi v zástupech, neuvěřili by, vyjma kdyby Bůh tomu chtěl: však většina jich není si toho vědoma.'),
(901, 6, 112, 'Takovýmto způsobem učinili jsme každému proroku nepřátele: pokušitele mezi lidmi a Džinny, kteří našeptávají si navzájem pozlátkem vyzdobené řeci. A kdyby Pán tvůj byl chtěl, nebyli by tak činili, pročež vzdal se jich a planých jejich výmyslů.'),
(902, 6, 113, 'Nechť tedy nakloní se srdce těch, kteří nevěří v život budoucí k myšlence této a nechť naleznou v ní zalíbení a nechť dobudou si, čeho si dobývají.'),
(903, 6, 114, 'Zdaž jiného soudce budu žádati si vyjma Boha, když on byl to, jenž seslal vám Knihu rozbornou? Ti, jimž dali jsme Knihu, vědí, že jest v pravdě seslána Pánem tvým: pročež nebudiž z pochybovačných!'),
(904, 6, 115, 'Slova Pána tvého dokonalá jsou v pravdě i spravedlnosti a nikdo nemůže změniti slov jeho: neboť on vše slyší a ví.'),
(905, 6, 116, 'Však budeš-li poslouchati většinu obyvatelů této země, zavedou tě se stezky boží, neboť následují pouze domněnky a jsou jen lháři.'),
(906, 6, 117, 'Zajisté Pán tvůj nejlépe ví, kdo zbloudil se stezky jeho a nejlépe ví, kdo správně veden jest.'),
(907, 6, 118, 'A jezte z toho, nad čím prosloveno bylo jméno Boha, věříte-li ve znamení jeho.'),
(908, 6, 119, 'A proč nejedli byste z toho, nad čímž proneseno bylo jméno Boha, když již označil vám, co zapověděl vám jísti, vyjma v případě, kdybyste nezbytím byli k tomu přinuceni? Však mnozí svádějí druhé náruživostmi svými z nevědomosti své: zajisté Pán tvůj nejlépe zná přestupníky!'),
(909, 6, 120, 'A vzdalujte se zdánlivého i skutečného hříchu, neboť ti, kdož hříchu si dobývají, odměněni budou za to, čeho dobyli si.'),
(910, 6, 121, 'Pročež nejezte z ničeho, nad čím nebylo proneseno jméno boží, neboť hříchem jest to: a pokušitelé jistě našeptávati budou stoupencům svým, aby hádali se s vámi o tom; však poslechnete-li je, budete rovni mnohobožcům.'),
(911, 6, 122, 'Zdaž ten, jenž byl mrtev a jejž vzkřísili jsme a jemuž dali jsme světlo, aby kráčel v něm mezi lidem, má býti roveň tomu, který kráčí v temnotách, ze kterých nevyjde nikdy? Takovýmto způsobem zkrášleny byly nevěřícím skutky jejich.'),
(912, 6, 123, 'A takovýmto způsobem učinili jsme v každém městě předáky (hříšníků) jeho, aby prováděli v něm (vychytralé) podvody své: však koho jiného podvádějí, než sebe samy, ač nejsou si toho vědomi?'),
(913, 6, 124, 'A když učiněno jest jim znamení, praví: „Neuvěříme, dokud nebude učiněno nám znamení takové, jaká dána byla prorokům božím.“ Bůh nejlépe ví, kam dáti poslání své. Ty, kdož prohřešili se, postihne opovržení u Boha a trest přísný za (vychytralá) podvodnictví jejich.'),
(914, 6, 125, 'A koho Bůh vésti chce cestou pravou, toho hruď otevře Islámu a koho zavésti chce, toho hruď učiní úzkou a těsnou, jako by pozvednouti chtěla se k nebi. Takovýmto potupným trestem navštíví Bůh nevěřící.'),
(915, 6, 126, 'Toto jest stezka Pána tvého přímá: nyní jasně označili jsme znamení svá těm, kdož přemítají.'),
(916, 6, 127, 'Pro ně obydlí míru připraveno jest u Pána jejich a on přítelem jim bude v odměnu za skutky jejich.'),
(917, 6, 128, 'V den, kdy shromáždí je Bůh všechny, řekne: „Shromáždění Džinnů, příliš mnoho (dostali jste) od lidí.“ Tehdy řeknou stoupenci jejich mezi lidmi: „Pane náš, vypomáhali jsme si navzájem, však nyní dostihli jsme lhůty, kterou předurčil jsi nám.“ I řekne Bůh: „Oheň budiž bydlištěm vaším: v něm přebývati budete věčně, leda že by Bůh jinak chtěl!“ Zajisté, Pán tvůj moudrý jest a vševědoucí.'),
(918, 6, 129, 'Takovýmto způsobem dáváme nepravostným jedny druhým za přátele, v odměnu za skutky jejich.'),
(919, 6, 130, '„Ó shromáždění Džinnů a lidí, což nepřišli vám proroci ze středu vašeho, předříkávajíce vám znamení má a varující vás před setkáním tohoto dne?“ Tehdy řeknou: „Doznáváme to sami proti sobě.“ Zaslepil je život pozemský a sami o sobě doznají, že byli nevěřícími.'),
(920, 6, 131, 'To (stalo se) proto, že Bůh nikdy nezničil měst způsobem krutým, dokud obyvatelé jeho ničeho netuší.'),
(921, 6, 132, 'A každému určen jest stupeň odměny dle skutků jejich a Pán tvůj zajisté není lhostejným k tomu, co činíte.'),
(922, 6, 133, 'A Pán tvůj v pravdě jest bohatým: a plným slitovnosti: kdyby chtěl, zahladil by vás a následníky vašimi učinil by koho by chtěl, stejně jako dal vzrůsti vám ze semene lidí jiných.'),
(923, 6, 134, 'Zajisté to, čím hrozeno jest vám, přijde a nejste s to, tomu zabrániti.'),
(924, 6, 135, 'Rci: „Lide můj, jednej dle nejlepší možnosti své, jakož i já činiti budu: a zvíte pak, kdo skončí v obydlí věčném. Věru, nepovede se dobře nepravostným.'),
(925, 6, 136, 'A určují Bohu podíl z toho, čemu dává vzrůsti z plodin a DOBYTKA jejich, díce: „Toto patří Bohu“ — tak totiž domnívají se — „a toto společníkům, jež dáváme mu.“ Však to, co patří společníkům, dávaným jimi Bohu, nedojde Boha nikdy, ale to, co patří Bohu, dojde společníky, dávané jimi Bohu. Jak špatné jsou názory jejich!'),
(926, 6, 137, 'Takovýmto způsobem společníci, dávaní jimi Bohu, způsobili, aby mnohým modloslužebníkům dobrým zdálo se zabíjení dětí jejich: proto, aby v záhubu uvedli je a spletením pokryli náboženství jejich. A kdyby Bůh, byl chtěl, nebyli by tak činili: pročež vzdal se jich a planých výmyslů jejich.'),
(927, 6, 138, 'A říkají: „Tato dobytčata a tyto plodiny jsou zasvěcené a nikdo jiný nepožije z nich, vyjma toho, jejž budeme chtíti“ — tak totiž domnívají se — „a na hřbety toho dobytka zapovězeno jest nakládati břemena.“ Jest te pak dobytek, nad nímž nevyslovují jméno Boha, vymýšlejíce v tom všem o Bohu lživá tvrzení. Však Bůh odmění je za všechny výmysly jejich.'),
(928, 6, 139, 'A říkají: „Mláďata v útrobách těchto dobytčat povolena jsou mužskému pohlaví a zapovězena našim ženám. Však (narodí-li se) mrtva, nechť obě pohlaví se o ně dělí. Bůh odmění je za jejich rozlišování: on zajisté moudrý jest a vševědoucí!'),
(929, 6, 140, 'Záhubě propadli ti, kdož zabíjeli děti své z pošetilosti, a nevědomosti: a zapověděli to, co Bůh dal jim potravou, vymyšlejíce v tom lživá o Bohu tvrzení: zbloudili a nebyli vedeni cestou pravou.'),
(930, 6, 141, 'On to jest, jenž dává vzrůsti zahradám vinným, podepřeným loubím i nepodepřeným: a palmám datlovým a obilí různého druhu výživy: a olivám a jablkům granátovým, jež jsou si navzájem podobny a nepodobny. Jezte z plodů jejich, když dozrají a odveďte patřičný podíl z nich v den sklizně: však nebuďte marnotratnými, neboť Bůh nemiluje marnotratných.'),
(931, 6, 142, 'Mezi dobytčaty jsou pak některá k nošení břemen a jiná k zabití: i jezte z toho, čím obdařil vás Bůh a nenásledujte stop Satana, neboť on zajisté nepřítelem jest vám zjevným.'),
(932, 6, 143, 'Osm máte na vybranou dobytčat v párech: dvé z bravu, a dvé z druhu koz. Rci: „Zdaž zapovězeno jest vám jísti dva samce, anebo dvě samice, anebo to, co obsazeno jest v lůně samic? Poučte mne o tom, dle pravdomluvnosti své.“'),
(933, 6, 144, 'A z velbloudů dvé a ze skotu dvé. Rci: „Co zapovězeno jest vám jísti: dva samce, anebo dvě samice, anebo to, co obsaženo jest v lůně samic? Zdaž byli jste svědky toho, že Bůh to vše nařídil? A kdož nešlechetnějším jest toho, jenž lži vymýšlí o Bohu v nevědomosti své a zavádí lidi? Bůh zajisté nevede stezkou pravou nepravostných.“'),
(934, 6, 145, 'Rci: „Nenalézám v tom, co zjeveno mi bylo, jiné zápovědi ohledně jídla, vyjma toho, že zapovězeno jest jíst zdechliny, prolitou krev a maso vepřové, neboť toto nečistotou jest a pohoršením: a rovněž, co usmrceno bylo ve jménu jiného boha, než Boha jediného. Však kdožkoli přinucen jest k tomu nezbytím, nikoli ze svévole aneb náklonnosti k hříchu — v pravdě, Bůh velký jest v odpouštění, slitovný.“'),
(935, 6, 146, 'A Židům zapověděli jsme všechna dobytčata mající kopyto a mimo to tuk skotu a bravu, vyjma hřbetního a tuku v útrobách, aneb toho, jenž lne ke kostem. Takto odměnili jsme je za svévolnost jejich a my zajisté jsme spravedlivými.'),
(936, 6, 147, 'A když nazývají tě lhářem, rci: „Pán váš jest nesmírný ve slitovnosti své, však neodvrací hněvu svého od lidu provinilého.“'),
(937, 6, 148, 'Ti, kdož dávají Bohu společníky, řeknou: „Kdyby Bůh chtěl, nebyli bychom mu dali společníků, aniž otcové naši, aniž bychom byli co zapovídali.“ Stejným způsobem nazývali předchůdcové jejich proroky lháři, dokud neokusili přísnosti naší. Rci: „Zdaž máte nějaké vědění? Ano-li, ukažte nám je! Však ve skutečnosti následujete pouze domněnky a ničím jiným nejste, nez lháři.“'),
(938, 6, 149, 'Rci: „Bůh jedině nezvratný má důvod: a kdyby byl chtěl, byl by vás všechny vedl cestou pravou.“'),
(939, 6, 150, 'Rci: „Přiveďte tedy svědky své, aby dosvědčili, že Bůh zapověděl tato (dobytčata).“ A dosvědčí-li to, nesvědč s nimi a nenásleduj choutek těch, kteří lží nazývají znamení naše a těch, kdož nevěří v život budoucí a rovných staví Pánu svému.'),
(940, 6, 151, 'Rci: „Pojďte sem: zopakuji vám, co Pán váš zapověděl vám, totiž: abyste nedávali mu společníků nijakého druhu: abyste laskavi byli k rodičům svým: abyste nezabíjeli dětí svých z nedostatku, neboť my postaráme se o potravu vám i jim: abyste vzdalovali se smilstva zjevného i skrytého: a abyste nikoho nezabíjeli proti zákazu božímu, vyjma vyžaduje-li toho spravedlnost. Toto jsou zákony Boha vám, abyste již jednou pochopili.'),
(941, 6, 152, 'A nepřibližujte se jmění sirotka, vyjma s úmyslem zlepšiti, je, dokud nedospěje plných let. Dávejte plnou míru a správnou váhu. Neukládáme nikomu břemen nad síly jeho. Vyslovujete-li pak úsudek, buďte spravedlivými, i kdyby jednalo se o vašeho příbuzného a naplňujte věrně smlouvu s Bohem: toto příkazem jest vám od Boha, abyste jej byli vždy pamětlivi:'),
(942, 6, 153, 'a toto jest stezka má; jest přímá, pročež následujte ji. Nenásledujte stezek jiných, abyste nebyli zavedeni od stezky boží. Toto příkazem jest vám od Boha, abyste se ho báli.“'),
(943, 6, 154, 'Pak dali jsme Mojžíšovi Knihu: úplnou byla pro toho, jenž dobře činí a podrobným rozlišením mezi všemi věcmi a vedením a milosrdenstvím, aby uvěřili v setkání s Pánem svým.'),
(944, 6, 155, 'A Kniha tato, již seslali jsme, požehnána jest: pročež následujte ji a bojte se Boha, abyste nalezli slitování.'),
(945, 6, 156, 'Abyste pak neřekli: „Dvěma národům seslána byla Kniha před námi, ale lhostejni jsme byli k učení jejich.“'),
(946, 6, 157, 'Nebo abyste neřekli: „Kdyby nám byla bývala seslána Kniha, byli bychom lépe ji následovali než oni.“ Nyní pak přišlo vám jasné vysvětlení od Pána vašeho a vedení i milosrdenství: kdož tedy nešlechetnějším jest toho, jenž lží nazývá znamení Boha a odvrací se od nich? Ty, kdož odvracejí se, odměníme nejhorším trestem za to, že se odvraceli.'),
(947, 6, 158, 'Snad čekají, že přijdou k nim andělé, neb přijde sám Pán tvůj, nebo přijdou nějaká znamení Pána tvého? V den, kdy přijdou znamení Pána tvého, ničeho nepomůže duši víra její, neuvěřila-li před tím, aniž té, jež nevykonala ničeho dobrého ve víře své. Rci: „Jen čekejte: my také čekáme.“'),
(948, 6, 159, 'Nepatřiž nijak k těm, již rozštěpují náboženství své a rozpadávají se v sekty: ponech věc jejich na rozhodnutí Bohu. On pak poví jim, co činili.'),
(949, 6, 160, 'Kdožkoli vykoná dobrý skutek, desateronásobně odmeněn bude: kdožkoli pak vykoná zlý skutek, stejnou pouze mírou bude odměněn: a nikomu nebude nijak ublíženo.'),
(950, 6, 161, 'Rci: „Mne pak uvedl Pán můj na stezku přímou, k náboženství pravému, víře Abrahamově, jenž pravým byl věřícím a nebyl z těch, kdož Bohu dávají společníky.“'),
(951, 6, 162, 'Rci: „V pravdě, modlitby mé a obřady, jež konán, a život a smrt přináležejí Bohu, Pánu světů,'),
(952, 6, 163, 'jenž nemá společníka: toto přikázáno mi bylo a já prvním jsem z muslimů'),
(953, 6, 164, 'Rci: „Zdaž jiného budu přáti si za Pána, když on Pánem jest věcí všechněch? Jedině pro sebe pracuje každá duše a žádná, nesoucí břemeno, neponese břemene druhé: pak k Pánu svému nevrátíte se a on zpraví vás o tom, v čem jste se rozcházeli.'),
(954, 6, 165, 'On jest to, jenž učinil vás nástupci (předchůdců vašich) na zemi a pozvedl jedny z vás nad druhé o různé stupně, aby zkoušeti vás mohl tím, čím obdařil vás. Zajisté Pán můj rychlý jest v trestání, ale též velký v odpouštění, slitovný.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Alif. Lám. Mím. Sád.'),
(956, 7, 2, 'Hle, Kniha, jež seslána ti byla: nechovejž tedy nepokoje v prsou svých ohledně jí; abys varoval jí a aby napomenutím byla věřícím.'),
(957, 7, 3, 'Následujte, což sesláno bylo vám Pánem vaším a nenásledujte jiných pánů vedle něho. Jak málo chováte v paměti napomenutí!'),
(958, 7, 4, 'Jak mnoho měst zahladili jsme! Zastihl je hněv náš: jedny za noci překvapil, druhé za denního světla.'),
(959, 7, 5, 'A jaké bylo volání jejich, kdy překvapil je hněv náš? Říkali jen: „V pravdě byli jsme nepravostnými.“'),
(960, 7, 6, 'Zajisté budeme vyptávati se těch, jimž poslali jsme proroky a stejně budeme ptávati se i proroků,'),
(961, 7, 7, 'a vypovíme jim všechny skutky jejich dle vědění svého, neboť nebyli jsme nepřítomni.'),
(962, 7, 8, 'V ten den váha bude spravedlivá: a těm, jichž váha těžká bude, dobře povede se:'),
(963, 7, 9, 'a ti, jichž váha lehká bude, propadli záhubě duší svých za to, že nešlechetně chovali se vůči znamením našim.'),
(964, 7, 10, 'A postavili jsme vás na zemi a dali jsme vám na ní žívobytí: jak málo jste vděčnými!'),
(965, 7, 11, 'Stvořili jsme vás a dali jsme vám podobu: pak řekli jsme andělům: „Skloňte se úctou před Adamem.“ I sklonili se před ním všichni, vyjma lblíse, jenž nebyl mezi těmi, kteří se sklonili.'),
(966, 7, 12, 'Řekl (Bůh): „Co zabránilo ti, abys se klaněl, když poručil jsem ti?“ Řekl: „Jsem lepším jeho: stvořil’s mne z ohně, kdežto jej stvořil’s z hlíny.“'),
(967, 7, 13, 'Řekl (Bůh): „Ven s tebou odtud; nepřísluší ti, abys pýchou dmul se zde. Vyjdi odtud: zajisté k opovrhovaným patřiti budeš!“'),
(968, 7, 14, 'Řekl: „Poshov mi až do dne, kdy vzkříšeni budou z mrtvých.“'),
(969, 7, 15, 'Řekl: (Bůh): „Budiž ti poshověno.“'),
(970, 7, 16, 'Řekl: „A ježto dal’s mi zblouditi, zajisté číhati na ně budu na stezce tvé přímé.'),
(971, 7, 17, 'Pak zajisté připadnu na ně zpředu i zezadu a zprava i zleva a většinu jich nalezneš pak nevděčnými.“'),
(972, 7, 18, 'Řekl (Bůh): „Odejdi odtud, pokryt hanbou a vypovězen! Kdožkoliv následovati tě bude z nich těmi zajisté naplním peklo všemi!'),
(973, 7, 19, 'A ty, Adame, obývej s manželkou svou v ráji a jezte z čehokoliv se vám zlíbí: jedině nepřibližujte se k tomuto stromu, jinak zhřešíte.“'),
(974, 7, 20, 'I jal se naseptávati jim Satan, aby dal jim zpozorovati nahotu jejich, jež dosud byla jim skryta. Řekl: „Pán váš zapověděl vám tento strom jen proto, abyste nestali se anděly a nesmrtelnými.“'),
(975, 7, 21, 'A přisahal jim: „Zajisté jsem vám dobrým rádcem.“'),
(976, 7, 22, 'A zmámil je tak přeludy svými: a když okusili oba stromu, shlédli nahotu svou i počali sešívati si roušku z listí ráje. Tehdy zavolal na ně Pán jejich: „Zdaž nezapověděl jsem vám strom onen a zdaž neřekl jsem vám. „Zajisté Satan jest vaším nepřítelem zjevným?“'),
(977, 7, 23, 'Řekli: „Pane náš, zhrešili jsme proti sobě samým, a neodpustíš-li nám a neslituješ-li se nad námi, jistotně propadli jsme záhubě.“'),
(978, 7, 24, 'Řekl (Bůh): „Ven s vámi odtud: jedni druhým nepřáteli budete: a na zemi budete míti určené obydlí a zaopatření dočasné.“'),
(979, 7, 25, 'Řekl dále: „Na ní žíti budete a na ní zemřete a z ní vyvedeni budete jednoho dne.“'),
(980, 7, 26, 'Ó lidé, seslali jsme vám teď oděv, abyste pokryli nahotu svou a obleky nádherné však oděv bázně boží nejlepším jest. Toto jest jedním ze znamení božích: snad přemítati budou o něm.'),
(981, 7, 27, 'Ó lidé, nechť nesvede vás Satan, jako způsobil vyhnání rodičů vašich z ráje, odňav jim oděv jejich, aby dal shlédnouti jim nahotu jejich. On zajisté vidí vás, on i stoupenci jeho, zkad nevidíte jich: a zajisté učinili jsme zlé duchy přáteli těch, kdož nevěří.'),
(982, 7, 28, 'A když tito dopustí se smilstva, říkají: „Viděli jsme to u otců svých a tak nakázal nám Bůh.“ Rci: „V pravdě Bůh nenabádá k smilstvu: či chcete tvrditi o Bohu, co nevíte?“'),
(983, 7, 29, 'Rci: „Pán můj nakazuje poctivost. Obracejte tedy tváře své ke každé modlitebnici a vzývejte jej v upřímnosti náboženství svého: jakož stvořil vás, tak k němu se navrátíte.'),
(984, 7, 30, 'Některé vede cestou pravou, některé pak odsoudil k bloudění, ježto vzali si zlé duchy za přátele na místo Boha v domnění, že vedeni jsou cestou pravou.“'),
(985, 7, 31, 'Ó lidé, oblecte pěkný oděv svůj, když vcházíte do kterékoliv modlitebnice, a jezte a pijte, ale nebuďte výstředními, neboť Bůh nemiluje výstředníků.'),
(986, 7, 32, 'Rci: „Kdo zapověděl pěkný oděv, který Bůh dal služebníkům svým a dobré z potravin?“ Rci: „Toto vše dáno jest věřícím po dobu života vezdejšího, a zvláště pak pro den zmrtvýchvstání.“ Takovýmto způsobem objasňujeme znamení svá lidu vědoucímu.'),
(987, 7, 33, 'Rci: „Jedině však zapověděl vám Pán můj smilstvo zjevné i tajné, a nepravost, a nespravedlivé násilí, a dávání Bohu společníků, k čemuž neseslal vám nijaké moci, a říkání o Bohu, co nevíte.“'),
(988, 7, 34, 'Každý národ má svůj předurčený čas: a když dojde čas jejich, nelze jim ani zpozditi hodinu jeho, ani urychliti ji.'),
(989, 7, 35, 'Ó lidé, povstanou zajisté mezi vámi proroci, předříkávající vám znamení má: a kdožkoli báti se bude Boha a polepší se, těm netřeba se strachovati, aniž rmoutiti se.'),
(990, 7, 36, 'Však ti, kdož lží nazývali znamení naše a odvrátili se v pýše od nich, údělem najdou oheň, v němž přebývati budou věčně.'),
(991, 7, 37, 'A kdo nešlechetnějším jest toho, jenž vymýšlí o Bohu lži a lží nazývá znamení jeho? Těmto dostane se podílu jejich, předurčeného jim v Knize (věčné), až do chvíle, kdy přijdou k nim vyslanci naši, aby přijali je v náručí smrti. I řeknou k nim: „Kde jsou ti, jež vzývali jste vedle Boha?“ Odpovědí: „Ztratili se nám,“ a takto sami o sobě dosvědčí, že byli nevěřícími.'),
(992, 7, 38, 'Řekne (Bůh): „Vejděte do ohně za pokoleními Džinnů a lidí, jež byla před vánů.“ Pokaždé, když nové pokolení vstoupí do ohně, proklínati bude předešlé a když shromážděna v něm budou všechna, řeknou posledně přišlá o prvých: „Pane náš, tito zavedli nás i určiž jim dvojnásobná muka ohně.“ Řekne (Bůh): „Všem vám dostane se dvojnásobného podílu, však vy o tom nevíte.“'),
(993, 7, 39, 'A řeknou prvá (pokolení) k posledně přišlým: „Čím zasloužíte si, aby lépe bylo s vámi nakládáno? Okuste jen nyní trestu v odměnu za skutky své.“'),
(994, 7, 40, 'Zajisté těm, kdož lží nazívali znamení naše a v pýše své jimi opovrhovali, nebudou otevřeny brány nebeské a nevstoupí do ráje, leda až projde velbloud uchem jehli. Takovýmto způsobem odměníme provinilce.'),
(995, 7, 41, 'Pro ně peklo bude lůžkem a nad nimi pokrývky budou ohnivé: a takováto bude odměna nepravostných.'),
(996, 7, 42, 'Však ti, kdož uvěřili a konali dobré skutky — my zajisté nikomu neukládáme břemene nad síly jeho — údělem budou míti zahradu ráje, v níž přebývati budou věčně.'),
(997, 7, 43, 'Odejmeme veškerou nevraživost z hrudí jejich: řeky potekou pod nohami jejich, a řeknou: „Chvála budiž Bohu, jenž dovedl nás sem! Jistě nebyli bychom nalezli cesty pravé, kdyby Bůh nás nebyl vedl, však proroci Pána našeho přinesli nám pravdu.“ A zavolá na ně hlas: „Totoť jest zahrada ráje, jehož dědici stali jste se v odměnu za skutky své!“'),
(998, 7, 44, 'A volati budou obyvatelé ráje na obyvatele ohně: „Nyní shledali jsme, že to, co slíbeno nám bylo Pánem naším, bylo pravdou. Zdaž i vy shledali jste to, co slíbil vám Pán náš, pravdivým?“ A řeknou: „Ano.“ A vyvolá vyvolavatel mezi nimi: „Zlořečení Boha trvejž nad nepravostnými,'),
(999, 7, 45, 'nad těmi, kdož odvracejí druhé od stezky boží a rádi by zkřivili ji a nevěří v život budoucí. “'),
(1000, 7, 46, 'A mezi nimi přepažení bude: a na tomto valu AL-A\'RÁF státi budou mužové, kteří každého poznávatí budou dle známek jeho, a volati budou na obyvatele ráje: „Pokoj budiž s vámi!“, však nevstoupí do ráje, ač velice po tom toužiti budou.'),
(1001, 7, 47, 'A když obrátí se pohledy jejich směrem k obyvatelům ohně, řeknou: „Pane náš, nedej octnouti se nám mezi lidem nepravostným!“'),
(1002, 7, 48, 'A ti, kdož státi budou na Al-Á\'ráfu volati budou na muže, jež poznají dle známek jejich, řkouce: „Co prospěly vám hromady bohatství a pýcha vaše?'),
(1003, 7, 49, 'Zdaž onino nejsou to, o kterých přisahali jste, že nikdy nedosáhnou milosrdenství Boha? Vstuptež do ráje: vám netřeba se báti, má rmoutiti se.“'),
(1004, 7, 50, 'A volati budou obyvatelé pekla na obyvatele ráje: „Svlažte nás trochou vody a dejte nám trochu z toho, čímž obdařil vás Bůh.“ I řeknou: „Bůh zajisté obé zakázal nevěřícím,'),
(1005, 7, 51, 'kteří za žert a kratochvíli brali náboženství své a jež zlákal život pozemský přeludy svými.“ A dnes tedy zapomeneme na ně stejně jako oni zapomněli, že setkají se s tímto dnem svým a proto, že odpírali pravdivost znamením našim.'),
(1006, 7, 52, 'Vždyť přece poslali jsme jim Knihu a dle vědění vysvětlili jsme jim ji, aby vedením a milosrdenstvím byla lidu věřícímu.'),
(1007, 7, 53, 'Což jiného očekávají, leda snad výklad její? V den, kdy přijde výklad její, řeknou ti, kteří před tím byli zapomněli jej: „Přišli nám zajisté proroci Pána našeho s pravdou: zdaž nelze nám nyní nalézti přímluvců, kteří by přimluvili se za nás? Anebo nemohli bychom vrátiti se zpět (na zemi)? Pak jistě jinak bychom jednali, než jak jednali jsme.“ Záhubě propadli však již a ztratila se jim božstva, jimi vymyšlená.'),
(1008, 7, 54, 'V pravdě Pánem vaším jest Bůh, jenž stvořil nebe i zemi v šesti dnech a pak vystoupil na trůn svůj: on pokrývá nocí den, jenž žene se za ní spešně. Onť stvořil slunce a měsíc a hvězdy, podrobené rozkazům jeho: nepřináleží-liž tedy jemu jedině stvoření a rozkazování? Požehnán budiž Bůh, Pán veškerenstva!'),
(1009, 7, 55, 'Vzývejte Pána svého v pokoře a skrytosti, onť zajisté nemiluje přestupníků.'),
(1010, 7, 56, 'Nenaplňujte zemi zlořády poté, když vše v ní bylo upraveno a volejte k Bohu v bázni a v touze, neboť milosrdenství boží blízko jest těm, kdož dobře činí.'),
(1011, 7, 57, 'On te jest, jenž posílá větry, zvěstovatele milosrdenství svého, přinášející mračna, těžká deštěm: i ženeme je k zemi zmrtvělé suchem a sesíláme z nich vodu, kterouž vzrůsti dáváme všem plodinám. Takovýmto způsobem oživujeme mrtvé: snad o tom budete přemítati.'),
(1012, 7, 58, 'A půda dobrá vydává plodiny své z dopuštění božího: a půda špatná vydává pouze sporé byliny: takovýmto způsobem přesně vysvětlujeme znamení lidu vděčnému.'),
(1013, 7, 59, 'Poslali jsme Noema k lidu jeho, i řekl: „Lide můj, vzývejte Boha: nemáte jiného Boha, kromě něho a já v pravdě obávám se, že stihne vás trest v den onen velký.“'),
(1014, 7, 60, 'I řekli předáci lidu jeho: „Zajisté vidíme, žes v bludu zjevném.“'),
(1015, 7, 61, 'Řekl: „Lide můj, není ve mně bludu, ale prorokem jsem od Pána všehomíra:'),
(1016, 7, 62, 'oznamuji vám poselství Pána svého a prospěšnou dávám radu vám, neboť od Boha zvěděl jsem, co neznámo jest vám.'),
(1017, 7, 63, 'Podivujete se snad, že napomenutí Pána vašeho přichází vám prostřednictvím muže ze středu vašeho, aby jím varoval vás a abyste báli se Boha a dosíci mohli tak slitování jeho?“'),
(1018, 7, 64, 'Však lhářem nazvali jej. Zachránili jsme tedy jedině jehoa ty, kdož s ním byli v arše a utopili jsme ty, kdož lží nazývali znamení naše, neboť v pravdě slepi byli.'),
(1019, 7, 65, 'A ke (kmeni) \'Ád (poslali jsme) bratra jejich Húda, i řekl jim: „Lide můj, vzývejte Boha; nemáte jiného boha, kromě něho. Zdaž nebudete báti se jej?“'),
(1020, 7, 66, 'I řekli nevěřící z předáků lidu jeho: „V pravdě shledáváme tě pobloudilým v rozumu a jistotně míníme, žes lhářem.“'),
(1021, 7, 67, 'Řekl :,Lide můj. nejsem nikterak pobloudilým v rozumu, nýbrž prorokem jsem Pána všehomíra:'),
(1022, 7, 68, 'oznamuji vám poselství Pána svého a rádcem jsem vám upřímným.'),
(1023, 7, 69, 'Podivujete se snad, že napomenutí Pána vašeho přichází vám prostřednictvím muže ze středu vašeho, aby jím varoval vás? Pomněte, že učinil vás nástupci lidu Noemova a mohutnou obdařil vás postavou. Pomněte tedy dobrodiní Boha, aby bohdá dobře vedlo se vám.“'),
(1024, 7, 70, 'Řekli: „Zdaž přišel\'s nakázati nám, abychom ctili pouze jediného Boha a zanechali těch, které vzývali otcové naši? Tedy učiň, aby vyplnily se na nás hrozby tvé, jsi-li pravdomluvným.“'),
(1025, 7, 71, 'Řekl: „Pomsta a hněv z nenadání připadnou na vás od Pána vašeho. Budete se snad hádati se mnou ohledně jmen, jež vy a otcové vaši dali jste modlám, ač nedostali jste k tomu nijaké moci? Počkejte jen: a i já vyčkám s vámi.“'),
(1026, 7, 72, 'A vysvobodili jsme jej a ty, kdož při něm byli, dle milosrdenství svého a zahladili jsme do posledního ty, kdo lží nazývali znamení naše a nebyli věřícími.'),
(1027, 7, 73, 'A ke (kmeni) Tsemúd (poslali jsme) bratra jejich Sáliha, i řekl jim: „Lide můj, ctěte Boha! Nemáte jiného Boha kromě něho. Nyní jasný vám přišel důkaz od Pána vašeho: tato velbloudice boží jest vám znamením: i ponechte ji, aby pásla se na zemi boží a neubližujte jí: jinak stihne vás trest bolestný.'),
(1028, 7, 74, 'A pomněte, jak Bůh učinil vás nástupci kmene \'Ád a upevnil přebývání vaše na zemi, takže na pláních jejích stavíte si paláce a vytesáváte si domy ve skalinách. Mějte tudíž na paměti dobrodiní Boha a nenaplňujte zemi zlořády.“'),
(1029, 7, 75, 'Řekli pak předáci z lidu jeho, naplněni pýchou, k těm, kdož za slabé považováni byli, k těm, kdož uvěřili z nich: „Víte jistě, že Sálih vyslancem jest Pána svého?“ Řekli: „My zajisté věříme v to, s čím poslán byl.“'),
(1030, 7, 76, 'Řekli ti, kteří pýchou naplněni byli: „My zajisté nevěříme v to, več vy jste uvěřili.“'),
(1031, 7, 77, 'A podřezali kolena velbloudice a protivili se rozkazu Pána svého, řkouce: „Ó Sálihu, učiň, aby vyplnily se na nás hrozby tvé, jsi-li skutečně vyslancem.“'),
(1032, 7, 78, 'A zemětřesení přepadlo je, i nalezeni jsou z rána mrtvi, ležící na tváři v domech svých.'),
(1033, 7, 79, 'Odvrátil se tedy od nich, řka: „Lide můj, v pravdě oznámil jsem vám poselství Pána svého a upřímně radil jsem vám: však vy nemilujete dobrých rádců.“'),
(1034, 7, 80, 'A (poslali jsme) Lota k lidu jeho, i řekl: „Zdaž smilnosti dopouštěti se budete, jaké nikdo na světě před vámi nedopouštěl se? Vcházíte k mužům ve vášni své, místo k ženám? Vskutku výstřednosti se dopouštíte.“'),
(1035, 7, 81, 'Však jaká byla odpověď lidu jeho? Řekli jen: „Vyžeňte je z města svého, neboť za cudné se vydávají.“'),
(1036, 7, 82, 'A zachránili jsme jej i rodinu jeho, vyjma jeho ženy, neboť byla váhavou.'),
(1037, 7, 83, 'A dštili jsme na ně deštěm: a hle, jaký byl konec provinilců!'),
(1038, 7, 84, 'A k Madianským poslali jsme bratra jejich Šu\'ejba, i řekl jim: „Lide můj, ctetě Boha: nemáte jiného boha, mimo něho. Nyní přišel vám jasný důkaz od Pána vašeho: pročež správnou dávejte míru a váhu: nebeřte druhým, což jejich jest'),
(1039, 7, 85, 'a nenaplňujte zemi zlořády poté, když vše na ní bylo uspořádáno. Toto zajisté bude lepším pro vás, uvěříte-li.'),
(1040, 7, 86, 'Nesedávejte v záloze u každé cesty, vyhrožujíce: a neodvracejte od stezky boží ty, kdož uvěrili a nesnažte se zkřiviti ji. A pomněte, že malý vás byl počet a on rozmnožil vás: a hleďte, jaký byl konec kazisvětů.'),
(1041, 7, 87, 'A věří-li část vás v poselství mé a část nevěrí, vyčkejte trpělivě, až Bůh rozsoudí mezi námi, neboť on nejlepším jest ze všech soudců.“'),
(1042, 7, 88, 'A řekli předáci těch, kdož pyšni byli z lidu jeho: „Zajisté vypovíme tě, Šu\'ejbe, i ty, kdož uvěřili s tebou, z města našeho, nenavrátíte-li se k náboženství našemu.“ Řekli: „Jakže? I když je nenávidíme?'),
(1043, 7, 89, 'Vymyslili bychom lež o Bohu, kdybychom navrátili se k náboženství vašemu poté, když nás vysvobodil od něho Bůh, aniž lze nám vrátiti se k němu, leda že by chtěl tomu Bůh, Pán náš. Pán náš obsahuje všechny věci věděním svým. Na Boha spoléháme: Pane náš, rozhodni mezi námi a lidem naším dle spravedlnosti, neboť ty nejlepším jsi z rozhodčích.“'),
(1044, 7, 90, 'I řekli předáci těch, kdož neuvěřili z lidu jeho: „Budete-li následovati Šu\'ejba, pak zajisté záhubě propadli jste.“'),
(1045, 7, 91, 'A zemětřesení přikvačilo na ně a nalezeni jsou zrána mrtvi, ležící na tváři v domech svých.'),
(1046, 7, 92, 'Ti, kdož lhářem nazývali Šu\'ejba, zmizeli, jakoby nikdy tam nebyli bydlili: ti, kdož lhářem nazývali Šu\'ejba, propadli záhubě.'),
(1047, 7, 93, 'I odvrátil se od nich a řekl: „Lide můj, v pravdě nyní oznámil jsem vám poselství Pána svého a dobrou dal jsem vám radu: však proč rmoutil bych se pro lid nevěřící?“'),
(1048, 7, 94, 'Nikdy neposlali jsme proroka do města, aniž bychom nebyli před tím seslali protivenství a neštěstí na obyvatelstvo jeho, aby stali se pokornými.'),
(1049, 7, 95, 'Pak zaměnili jsme zlo dobrem, až zapomněli a řekli: „I otce naše stíhala neštěstí i blaho.“ Pročež přikvačili jsme na ně z nenadání, když ničeho netušili.'),
(1050, 7, 96, 'Však kdyby obyvatelé těchto měst byli uvěřili a báli se nás, byli bychom otevřeli jim požehnání nebes i země: však lží nazývali znamení naše a potrestali jsme je za skutky jejich.'),
(1051, 7, 97, 'Zdaž jisti si byli obyvatelé těchto měst, že nepřijde na ně hněv náš za temnot nočních, když ve spánku pohříženi budou?'),
(1052, 7, 98, 'Zdaž jisti si byli obyvatelé těchto měst, že nepřijde na ně hnev náš za jasného dne, když kratochvílím oddávati se budou?'),
(1053, 7, 99, 'Zdaž bezpečni cítili se před prozíravostí Boha? Však nikdo necítí se bezpečným před prozíravostí Boha, vyjma těch, kdož záhubě propadli.'),
(1054, 7, 100, 'Nebylo-liž přivedeno do poznání těch, kteří zdědili zemi po bývalých obyvatelích jejích, že kdybychom chtěli, trestem bychom je stihli za hříchy jejich a pečeť vtiskli bychom na srdce jejich, tak aby neslyšeli?'),
(1055, 7, 101, 'Oněch měst ti vyložíme děje: přišli k nim proroci jejich s jasnými důkazy, však nechtěli uvěřiti v to, co před tím byli nazvali vylhaným. Takovýmto způsobem vtiskuje Bůh pečeť na srdce nevěřících.'),
(1056, 7, 102, 'A neshledali jsme, že by většina jich dodržovala smlouvu, ale shledali jsme, že většina jich patří k prostopášným.'),
(1057, 7, 103, 'Po těchto prorocích poslali jsme Mojžíše se znameními našimi k Faraonovi a předním z lidu jeho, však nespravedlivě zachovali se vůči nim: a hle, jaký byl konec zle činících!'),
(1058, 7, 104, 'I řekl Mojžíš: „Ó Faraone, v pravdě prorokem jsem od Pána světů:'),
(1059, 7, 105, 'Spravedlivo jest, abych pouze pravdu mluvil o Bohu: přisel jsem k vám s jasným důkazem od Pána vašeho: pročež vyšli syny Israele se mnou ze země.“'),
(1060, 7, 106, 'Řekl Farao: „Přišel-lis se znamením, předveď nám je, jsi-Ii pravdomluvným.“'),
(1061, 7, 107, 'I vrhl Mojžíš hůl svou na zemi a hle, změněna jest v hada zjevného!'),
(1062, 7, 108, 'Vztáhl pak ruku svou a hle, bílou stala se v očích diváků!'),
(1063, 7, 109, 'I řekli předáci z lidu Faraonova: „V pravdě muž tento jest kouzelníkem učeným!'),
(1064, 7, 110, 'Rád by vypudil vás ze země vaší: nuž, co rozhodnete ve věci této?“'),
(1065, 7, 111, 'Řekli: „Posečkej s ním a s bratrem jeho a vyšli do měst shromažďovatele,'),
(1066, 7, 112, 'aby přivedli kde kterého kouzelníka učeného.“'),
(1067, 7, 113, 'I přišli kouzelníci k Faraonovi, řkouce: „Zajisté, že dobrá odměna připadne nám, budeme-li my vítězi.“'),
(1068, 7, 114, 'Řekl: „Ano, a mými nejbližšími oblíbenci budete.“'),
(1069, 7, 115, 'Řekli: „Mojžíši, buď vrhni ty první hůl svou na zem, anebo my vrhneme naše.“'),
(1070, 7, 116, 'Řekl: „Vrhněte vy!“ A když učinili tak, očarovali zraky lidu a postrašili jej a předvedli kouzla velká.'),
(1071, 7, 117, 'Tehdy pak vnukli jsme Mojžíšovi: „Nyní vrhni hůl svou na zem.“ A hle, hůl jeho požrala vše, co byli vykouzlili.'),
(1072, 7, 118, 'Tak jasně zjevena byla pravda a niveč rozpadlo se vše, co oni dělali.'),
(1073, 7, 119, 'I poraženi tak byli na místě a odtíhli zahanbeni.'),
(1074, 7, 120, 'Však ostatní kouzelníci padli na tváře své,'),
(1075, 7, 121, 'řkouce: „Uvěřili jsme v Pána všehomíra,'),
(1076, 7, 122, 'Pána Mojžíše a Árona.“'),
(1077, 7, 123, 'Řekl Farao: „Jakže! Vy uvěřili jste v něho dříve, než dal jsem vám k tomu svolením? Toto zajisté jest vychytralost, kterou schytrale vymyslili jste v tomto městě, abyste způsobili vyjití obyvatelstva z něho. Však brzy zvíte!'),
(1078, 7, 124, 'Dojista dám uříznouti vám ruce a nohy křižmo a pak na kříž pověsiti dám vás všechny!“'),
(1079, 7, 125, 'Řekli: „Zajisté navrátíme se k Pánu svému!'),
(1080, 7, 126, 'A ty mstíš se na nás jen proto, že uvěřili jsme ve znamení Pána svého, když předvedena nám byla. Pane náš, popřej nám vytrvalosti a dej zemříti nám co odevzdaným do vůle tvé.“'),
(1081, 7, 127, 'Tehdy řekli předáci z lidu Faraonova: „Zdaž dovolíš, aby vyšel Mojžíš s lidem svým a zlořádem naplňoval zemi a opustil tebe i bohy tvé?“ Řekl: „Pobíjeti budeme syny jejich a živiti budeme dcery jejich: a tak zůstaneme svrchovanými pány jejich.“'),
(1082, 7, 128, 'I řekl Mojžíš k lidu svému: „Volejte k Bohu o pomoc a vyčkávejte trpělivě, neboť země patří Bohu a on dává ji dědictvím těm ze služebníků svých, kterým chce. A šťastný konec bude bohabojných.“'),
(1083, 7, 129, 'Řekli: „Byli jsme utištěni před tím, než\'s přišel a utištěni jsme i po příchodu tvém.“ Řekl: „Možná, že Bůh zahladí nepřátele vaše a učiní vás nástupci jejich v zemi, aby viděl, jak vy se zachováte.“'),
(1084, 7, 130, 'Již před tím dali jsme pocítiti lidu Faraonovu neúrody a nedostatku plodin země, aby začali přemítati.'),
(1085, 7, 131, 'A když potkalo je co dobrého, řekli, „To nám patří.“ Však když zasáhlo je neštěstí, přičítali to Mojžíšovi a těm, kdož byli s ním. Ale což nebylo neštěstí jejich určeno Bohem? Nicméně většina jich nebyla si toho vědoma.'),
(1086, 7, 132, 'A řekli: „Byť předvedl\'s nám jakékoli znamení, abys očaroval nás jím, nebudeme ti věřiti.“'),
(1087, 7, 133, 'A poslali jsme na ně potopu a kobylky a hmyz a žáby a krev, co jasné důkazy: však pýchou naplněni byli a stali se lidem provinilým.'),
(1088, 7, 134, 'A když zasaženi byli některou ranou, řekli: „Ó Mojžíši, pros za nás u Pána svého dle smlouvy, kterou učinil s tebou, aby zbavil nás této rány. Zajisté uvěříme ti a jistotně pošleme syny Israele, aby vyšli s tebou.“'),
(1089, 7, 135, 'Však když zbavili jsme je rány a vypršela smluvená lhůta, hle, nedodrželi slova svého.'),
(1090, 7, 136, 'I pomstili jsme se nad nimi a utopili jsme je v moři, protože lží nazývali znamení naše a byli k nim lhostejnými.'),
(1091, 7, 137, 'A dědictvím dali jsme lidu, jenž slabým byl, východní i západní končiny země, jimž byli požehnali jsme: i naplnilo se slovo Pána tvého krásné nad syny Israele proto, že trpělivě vyčkávali; a zničili jsme díla Faraonova a lidu jeho, jakož i stavby jejich.'),
(1092, 7, 138, 'A dali jsme synům Israele přejíti moře i přišli do země, jejíž obyvatelé ctili modly. I řekli: „Ó Mojžíši, učiň nám Boha podobného bohům jejich.“ Řekl, „Vyť v pravdě jste lidem nevědomým:'),
(1093, 7, 139, 'neboť to, co konají lidé tito, zahlazeno bude a niveč uvedeno veškeré konání jejich.“'),
(1094, 7, 140, 'Řekl dále: „Zdaž jiného hledati budu pro vás boha, mimo Boha, jenž vyznamenal vás nad veškerenstvem?“'),
(1095, 7, 141, 'A (pomněte), jak vysvobodili jsme vás z rukou lidu Faraonova, jenž nejhorším utlačoval vás zlem, zabíjeje syny vaše a ponechávaje na živu dcery vaše: v tom zajisté zkouška byla od Pána vašeho velká.'),
(1096, 7, 142, 'A stanovili jsme schůzi s Mojžíšem na třicet nocí a doplnili jsme ji pak (dalšími) deseti, takže celkem trvání jeho s Pánem jeho bylo čtyřicet nocí. I řekl Mojžíš k bratru svému Áronovi: „Zastupuj mne nad lidem mým a jednej s ním spravedlivě a nenásleduj stezky zle činících.“'),
(1097, 7, 143, 'A když dostavil se Mojžíš k určené schůzi a Pán jeho mluvil k němu, řekl: „Pane můj, ukaž se mi, abych na tě hleděti mohl.“ Řekl Bůh: „Mne nespatříš: však pohlížej směrem k hoře a zůstane-li nehybnou na svém místě, tehdy spatříš mne.“ A když zjevil se na hoře Pán jeho, v prach obrátil ji: Mojžíš pak, jako bleskem zasažen, padl na zem v bezvědomí. Když Pak přišel k sobě, řekl: „Chvála budiž Tobě. Kajícně obracím se k Tobě a jsem prvním z věřících.“'),
(1098, 7, 144, 'Řekl Bůh: „Mojžíši, mezi všemi lidmi vyvolil jsem tě, abys zvěstoval poselství mé a slova má: přijmi tudíž, což zjevuji ti, a bud vděčný.“'),
(1099, 7, 145, 'A napsali jsme mu na deskách napomenutí o všech věcech a vysvětlení ohledně všech věcí, řkouce: „Vezmi je s pevným rozhodnutím a nařiď lidu svému, aby zachovával je dle nejlepšího jich obsahu: jinak ukáži vám obydlí prostopášných.'),
(1100, 7, 146, 'Odvrátím od znamení svých všechny, kdož neprávem v pýše si libují na zemi, neboť i když spatří všemožná znamení naše, neuvěří v ně; a i když spatří cestu pravého vedení, nepřijmou ji za stezku svou, však spatří-li stezku bludu, přijmou ji za stezku svou: proto, že vylhanými nazývali znamení naše a byli k nim lhostejnými.'),
(1101, 7, 147, 'Bezvýsledna budou veškerá konání těch, kdož vylhanými nazývali znamení naše a za lež prohlašovali setkání s Bohem v životě budoucím. Zdaž jinak mají býti odměněni, než dle toho, jaké byly skutky jejich?“'),
(1102, 7, 148, 'A učinil si lid Mojžíšův za nepřítomnosti jeho ze šperků svých tele jako živé, bučící: však což neviděli, že nemůže k nim promluviti, aniž vésti je cestou pravou? Učinili si je božstvem a stali se nepravostnými.'),
(1103, 7, 149, 'Ale když zkroušeně se káli, shledavše, že zbloudili, řekli: „V pravdě, nesmiluje-li se nad námi Pán náš a neodpustí nám, jistě octneme se mezi těmi, kteří záhubě propadli.“'),
(1104, 7, 150, 'A když vrátil se Mojžíš k lidu svému, rozhněván a lítostí naplněn, řekl: „Hanba tomu, co učinili jste za zády mými! Či chcete uspíšiti soud Pána svého?“ I mrštil deskami o zem a popadnuv bratra svého, za hlavu, přitáhl jej k sobě. Řekl tento: „Ó synu matky mé, zbavil mne moci mé lid, a málem byli by mne zabili. Nenech nepřátelům mým radovati se nade mnou a nepovažuj mne za patřícího k lidem nepravostným.“'),
(1105, 7, 151, 'Řekl Mojžíš: „Pane můj, odpusť mně i bratru mému a uveď nás do milosrdenství svého, neboť ty nejslitovnějším jsi ze slitovných!“'),
(1106, 7, 152, 'Ty pak, kdož učinili si tele božstvem, stihne hněv Pána jejich a pohana v životě pozemském: takovýmto způsobem odměněni budou vymýšlitelé lží.'),
(1107, 7, 153, 'Však k těm, kteří spáchavše zlé skutky, obrátili se na pokání a uvěřili, bude pak Pán tvůj odpouštějícím a slitovným.'),
(1108, 7, 154, 'A když utišil se hněv Mojžíšův, zvedl desky se země: a v písmu jejich obsaženo bylo vedení a milosrdenství těm, kdož bojí se Pána svého.'),
(1109, 7, 155, 'A vybral Mojžíš sedmdesáte mužů z lidu svého ke stanovené schůzce s námi. A když zemětřesení přikvačilo na ně, řekl: „Pane můj, kdybys byl chtěl, byl bys zahladil je i mne dříve. Zdaž zahubiti chceš nás pro skutky pošetilců mezi námi? Zajisté byla to jen zkouška od tebe: jí dáváš zblouditi, komu chceš, a jí vedeš cestou pravou, koho chceš. Tys ochráncem naším: odpusť nám a smiluj se nad námi, neboť tys nejšlechetnějším z odpouštějících.'),
(1110, 7, 156, 'A přiřkni nám dobrý úděl v životě tomto i budoucím, neboť my zajisté k tobě vedeni jsme.“ Řekl Bůh: „Trestem svým postihnu, koho budu chtíti, a milosrdenství mé objímá všechny věci: je přiřknu údělem těm, kdož báti se budou a budou dávati povinnou almužnu a věřiti ve znamení naše:'),
(1111, 7, 157, 'těm, kteří následovati budou vyslance, proroka z lidu, jejž naleznou předpověděného jim v Zákoně i Evangeliu: jenž napomínati je bude k dobru a odvraceti je bude od špatností: a dovolovati jim bude věci dobré a zakazovati jim bude věci špatné: jenž ulehčovati jim bude břímě jejich a uvolňovati okovy je tížící. Těm, kteří uvěří v něho, jej posilnovati budou a jemu pomáhati, a následovati budou světlo, sestoupivší s ním s hůry: těm zajisté dobře povede se.“'),
(1112, 7, 158, 'Rci: „Ó lidé, jáť zajisté jsem prorokem Boha, poslaným k vám všem: Onoho, jemuž přináleží panství nebes a země: není jiného boha, kromě něho: on dává život i usmrcuje. Tudíž věřte v Boha a ve vyslance jeho, proroka z lidu, jenž věří v Boha a slova jeho: jej následujte, abyste vedeni byli stezkou pravou.“'),
(1113, 7, 159, 'A jsou někteří mezi lidem Mojžíšovým, již vedou druhé dle pravdy a dle ní jednají spravedlivě.'),
(1114, 7, 160, 'A rozdělili jsme je na dvanácte kmenů a pokolení a vnuknuli jsme Mojžíšovi, když o napojení žádal jej lid jeho: „Udeř holí svou o skálu“: a vytrysklo z ní dvanácte pramenů a každé pokolení vědělo, který určen jest mu k pití. A zastínili jsme je mračny, z nichž seslali jsme jim mannu a křepelky: „Požívejte z dobrých věcí, které za potravu dali jsme vám.“ Neuškodili tedy nám, nýbrž na sobě samých se prohřešili.'),
(1115, 7, 161, 'A když řečeno bylo jim: „Přebývejte v tomto městě a jezte z potravin jeho, kdekoli zachce se vám, ale rcete „Hittatun“ a při vstupu do brány jeho padejte na tváře své: tehdy odpustíme vám hříchy vaše a rozmnožíme dobrodiní svá nad těmi, kdož dobře činí.“'),
(1116, 7, 162, 'Tu nepravostní mezi nimi zaměnili slovo jim předepsané za jiné i seslali jsme na ně trest s nebe za nepravost jejich.'),
(1117, 7, 163, 'A taž se jich na město, ležící na břehu moře, jehož obyvatelé znesvěcovali den sobotní, když ryby objevovaly se ve vodě v den sobotní a nepřicházely v jiný den. Takovým způsobem zkoušeli jsme je pro nešlechetnost jejich.'),
(1118, 7, 164, 'A když někteří z nich pravili: „Proč napomínáte lid, který bud zahladí Bůh, aneb krutým potresce trestem?“ odpověděli varovatelé: „Abychom omluvu měli před Pánem svým a v naději, že snad báti se budou Boha.“'),
(1119, 7, 165, 'A když zapomněli to, nač upamatováni byli, vysvobodili jsme ty, kteří zabraňovali činění zla a seslali jsme na nepravostné trest přísný za nešlechetnost jejich.'),
(1120, 7, 166, 'A když vyzývavě setrvávali v tom, co zapovězeno jim bylo, řekli jsme k nim: „Buďte opicemi hnanými.“'),
(1121, 7, 167, 'A provolal tehdy Pán tvůj, že jistotně pošle na ně národ, jenž dá jim zakusiti nejhoršího trestu, až do dne zmrtvýchvstání, neboť Pán tvůj zajisté rychlým jest v trestání, a velkým v odpouštění a slitovným.'),
(1122, 7, 168, 'A rozdělili jsme je na zemi v národy: některé z nich spravedlivé jsou, některé pak nikoliv: na zkoušku postavili jsme je věcmi dobrými i zlými, aby k nám se navrátili.'),
(1123, 7, 169, 'A po nich nástupci jejich nastoupili jich místo a zdědili Knihu: a béřou pomíjející statky tohoto světa, řkouce: „Bude nám odpuštěno,“ a když dány jsou jim znovu podobné statky, opětně je přijímají. Nebyla-li však přijmuta od nich smlouva samotné Knihy, že nebudou jiného mluviti o Bohu, než pravdu? Vždyť přece učí se obsahu Knihy: a obydlí světa budoucího jest zajisté lepším pro bohabojné. Což toho nepochopíte?'),
(1124, 7, 170, 'Ale ti, kdož pevně přidržují se Knihy a zachovávají modlitbu, nechť jsou jisti, že nepřipustíme, aby ztracena byla odměna spravedlivých.'),
(1125, 7, 171, 'A když v otřesu pozvedli jsme horu nad hlavy jejich, podobnou stínu, a domnívali se, že spadne na ně, řekli jsme: „Vezměte, což dáváme vám s pevným rozhodnutím a buďte pamětlivi slov našich, abyste báli se Boha.“'),
(1126, 7, 172, 'A když Pán tvůj vzal veškeré potomstvo, jež vzejíti mělo z ledví synů Adamových a dal složiti jim svědectví proti sobě samým, řekl: „Nejsem-liž Pánem vaším?“ Řekli: „Ano, dosvědčujeme to.“ To proto, aby neřekli v den zmrtvýchvstání: „V pravdě, nebylo nám to známo.“'),
(1127, 7, 173, 'A abyste nemohli říci: „Vždyť přece otcové naši dávali Bohu společníky dříve a my jsme potomstvem jejich pozdějším: zdaž zahladiti chceš nás za to, co činili lehkovážní?“'),
(1128, 7, 174, 'Takovýmto způsobem vykládáme jasně znamení svá: a snad navrátí se k Bohu.'),
(1129, 7, 175, 'Zopakuj jim případ onoho, jemuž dali jsme jedno znamení naše a jenž odvrátil se od něho a Satan přijal jej mezi následovníky své, takto zapadl mezi svedené.'),
(1130, 7, 176, 'Kdybychom byli chtěli, byli bychom povýšili jej tímto znamením: však on zůstal lpěti na zemi a následoval choutky své. Podoben jest psu, jenž štěká a vyplazuje jazyk, když dorážíš naň i když necháš jej pokojem. Takovými též jsou oni, kdož lží nazývají znamení naše: vypravuj jim tudíž tyto příběhy, aby mohli přemýšleti.'),
(1131, 7, 177, 'Špatné jest podobenství těch, kteří lží nazývají znamení naše a sami proti sobě se prohřešují.'),
(1132, 7, 178, 'Ten, jejž Bůh vede, dobře jest veden, a ti, které zavádí, záhubě propadnou.'),
(1133, 7, 179, 'Velký počet Džinnů a lidí stvořili jsme pro peklo: srdce mají, jimiž ničeho nechápou, oči mají, jimiž ničeho nevidí, uši maji, jimiž ničeho neslyší. Dobytčatům podobni jsou, ba ještě hlouběji upadají v blud, než dobytčata: toto jsou ti, kdož nedbají.'),
(1134, 7, 180, 'Nejkrásnější jména patří Bohu: těmito volejte jej a straňte se těch, kdož překrucují jména jeho: ti odměněni budou za skutky své.'),
(1135, 7, 181, 'A jest lid mezi těmi, které stvořili jsme, jenž vede druhé cestou pravou a dle pravdy řídí skutky své;'),
(1136, 7, 182, 'však ty, kdož lží nazývali znamení naše, ponenáhlu přivedeme v úpadek způsobem, který netuší:'),
(1137, 7, 183, 'popřeji jim dlouhé lhůty a zajisté lest má ukáže se zdatnou.'),
(1138, 7, 184, 'Zdaž konečně nepřijdou k názoru, že soudruh jejich není posedlý zlým duchem, nýbrž jest jen varovatelem zjevným?'),
(1139, 7, 185, 'Zdaž neobrátí pohled svůj ke království nebes a země a ke všemu, co stvořil Bůh, aby pátrali, zda snad nepřiblížil se konec lhůty jejich? A ve které jiné vyprávění věřiti budou poté, (když odmítli Korán)?'),
(1140, 7, 186, 'Ten, jejž zavede Bůh, nenajde vůdce a blouditi bude bez vodítka v bludu svém.'),
(1141, 7, 187, 'Dotazovati budou se tě na hodin: na kdy určen jest příchod její? Rci: „Pouze Pán můj ví: nikdo jiný nemůže stanoviti dobu příchodu jejího. Těžkou spočívá vahou na nebi i na zemi a přijde jen z nenadání na vás.“ Tázati budou se tě na ni, jakobys byl v to zasvěcen. Rci: „Pouze Bůh má o tom vědomost: nicméně většina lidí není si toho vědoma.“'),
(1142, 7, 188, 'Rci: „Není v moci mé přivoditi na sebe prospěch, neb škodu: vyjma dle vůle boží. Kdyby známy mi byly věci skryté, mohl bych žíti v hojnosti dobra a zlo nikdy by se mne netklo: však já jen varovatelem jsem a věštitelem radostných zpráv lidu věřícímu.“'),
(1143, 7, 189, 'Onť jest to, který stvořil vás z jediné bytosti, a z ní utvořil manželku její, aby přebývala s ní: a když muž poznal ženu svou, nesla zprvu lehké břímě: pak když těžkým stalo se, volali oba k Bohu, Pánu svému, „Dáš-li nám hodného syna, budeme ti za to vděčni.“'),
(1144, 7, 190, 'Však když Bůh dal jim hodného syna, místo díků dali mu společníky odplatou za dar jeho: ale Bůh daleko povýšen jest nade všechna božstva, jež za společníky mu dáváte.'),
(1145, 7, 191, 'Zdaž spolčovati budou s Bohem něco, co ničeho nestvořilo, nýbrž bylo stvořeno,'),
(1146, 7, 192, 'božstva, jež v ničem nejsou s to, aby pomohla jim, aniž sobě samým pomoci mohou?'),
(1147, 7, 193, 'A když voláte je k pravému vedení, nenásledují vás: jedno jest jim, voláte-li je, anebo mlčíte-li.'),
(1148, 7, 194, 'Zajisté ti, které vzýváte vedle Boha jediného, služebníky jeho jsou stejně jako vy: jen volejte k nim a vizte, zda vyslyší vás — jste-li pravdomluvnými!'),
(1149, 7, 195, 'Mají-liž nohy k chůzi? Ruce k uchopení? Oči k vidění? Uši k slyšení? Rci: „Nuž volejte k těm, jež za společníky dáváte Bohu a vymýšlejte lsti proti mně: rychle, nečekejte!“'),
(1150, 7, 196, 'V pravdě, mým zastáncem jest Bůh, jenž seslal Knihu: onť zastáncem jest spravedlivých.'),
(1151, 7, 197, 'Však ti, jež vzýváte vedle něho, nemohou přispěti vám ku pomoci, aniž mohou pomoci sobě samý.'),
(1152, 7, 198, 'A když voláš je k pravému vedení, neslyší tě: a vidíš je vzhlížeti k tobě, však nevidí.'),
(1153, 7, 199, 'Přidrž se shovívavosti a povzbuzuj k dobru a straň se nevědomých.'),
(1154, 7, 200, 'A když zlá ponuknutí Satana znepokojují tě, hledej útočiště u Boha, neboť on vše slyší a ví.'),
(1155, 7, 201, 'Zajisté, když bohabojných dotkne se nějaký přelud, vzbuzený Satanem, připomenou si Boha a ihned jasně vidí oči jejich.'),
(1156, 7, 202, 'Bratři jejich pouze prodlužují setrvávání jejich v bludu: pak nemohou z něho se vyprostiti.'),
(1157, 7, 203, 'A když nepřijdeš k nim se znamením, řeknou: „Ještě\'s nevybral si nějaké?“ Rci: „Jáť zajisté jen následuji to, co vnuknuto mi bylo od Pána mého: toto jasné jsou důkazy vám od Pána vašeho a pravé vedení a milosrdenství lidu věřícímu.“'),
(1158, 7, 204, 'A když čten jest Korán, naslouchejte v tichosti, abyste účastni byli smilování.'),
(1159, 7, 205, 'Mysli na Boha v nitru svém, v pokoře a skrytosti, však nevyslovuj hlasitě jména jeho: čiň tak zrána i zvečera a nebuď z těch, kteří (na to) nedbají.'),
(1160, 7, 206, 'Neboť i ti, kdož kolem Pána tvého stojí, nejsou pyšnými, nýbrž slouží mu a vyvyšují jej ve slávě jeho a padají před ním na tváře své.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Dotazovati budou se tě ohledně KOŘISTI. Rci: „Kořist patří Bohu a proroku. I bojte se Boha a dohodněte se mezi sebou, a poslušni buďte Boha a proroka jeho, jste-li věřícími.'),
(1162, 8, 2, 'Věřící jsou pouze ti, jichž srdce zachvívá se bázní, když vysloveno jest jméno Boha a jichž víra rozmnožena jest pokaždé, kdyz čtena jsou jim znamení jeho, a kteří (plně) spoléhají na Pána svého:'),
(1163, 8, 3, 'kteří zachovávají modlitbu a rozdávají štědře z toho, čímž jsme je obdařili;'),
(1164, 8, 4, 'tito jsou pravými věřícími: pro ně připraveny jsou stupně odměny u Pána jejich a odpuštění a obdaření hojné.'),
(1165, 8, 5, '(Pomni), jak Pán tvůj způsobil, abys vyšel z domu svého s poselstvím pravdy a jak část věřících činila tak pouze nerada.'),
(1166, 8, 6, 'Přeli se s tebou ohledně pravdy poté. když jasně (jim) byla ukázána, jakoby vlečeni byli k smrti před vlastními zraky.'),
(1167, 8, 7, 'Tehdy přislíbil vám Bůh, že vydá jeden z oddílů v ruce vaše: a přáli jste si, aby nebyl to oddíl silnějsí; však Bůh chtěl dokázati pravdu slovy svými a zničiti do posledniho nevěřící:'),
(1168, 8, 8, 'aby dokázal pravdivost pravdy a zničil ničemnost i kdyby odporno to bylo provinilým.'),
(1169, 8, 9, 'Když vzývali jste Pána svého o přispění a vyslyšel vás: „Propůjčím vám tisíc andělů ku pomoci, řadu za řadou“:'),
(1170, 8, 10, 'k čemuž jinému učinil vám Bůh tento slib, než aby radostnou byl vám zprávou a aby uklidněna byla jím srdce vaše? Neboť není pomoci, vyjma u Boha: Bůh zajisté mocný jest a moudrý.'),
(1171, 8, 11, '(Pomněte), když zahalil vás spánkem jistotou od něho, a seslal vém s nebe vodu, aby očistil jí vás, aby jí sejmul s vás poskvrnění Satana, aby opásal srdce vaše a upevnil jí kroky vaše.'),
(1172, 8, 12, 'Tehdy vnukl Pán tvůj andělům: „Zajisté budu s vámi: dodejte tedy odvahu věřícím. Vrhnu postrach v srdce těch, kdož neuvěřili a vy bijte je v šíje jejich a utínejte konečky všech prstů jejich:'),
(1173, 8, 13, 'to proto, že odštěpili se od Boha a proroka jeho. A kdokoli odpadne od Boha a proroka jeho, zví, že Bůh zajisté přísným jest v trestání.'),
(1174, 8, 14, 'Totoť jest odměna vaše: okuste jí! V pravdě, trest ohně přichystán jest nevěřícím.'),
(1175, 8, 15, 'Vy, kteří jste uvěřili, potkáte-li nevěřící táhnoucí ve sboru válečném, neobracejte se k nim zády,'),
(1176, 8, 16, 'neboť kdo obrátí se k nim zády v den onen, — leda že učiní tak, aby k novému chystal se útoku, aneb spojil se s jiným oddílem, — ten přivodí na se hněv boží a příbytkem jehobude peklo: špatné to bude obydlí!'),
(1177, 8, 17, 'Tedy nezabíjeli jste je vy, nýbrž Bůh zabíjel je, a když vrhal\'s střely, nebyl\'s to ty, jenž vrhal je, nýbrž Bůh je vrhal, aby vyzkoušel věřící dobrou zkouškou, jím poslanou: neboť Bůh vše slyší a ví.'),
(1178, 8, 18, 'To proto, že Bůh v pravdě chtěl niveč obrátiti úklady nevěřících.'),
(1179, 8, 19, 'Přáli jste si, (nevěřící), rozhodnutí vítězství: nuže, přišlo vám vítězství. Skončíte-li tedy, bude to lépe pro vás, obnovíte-li boj, též mi jej obnovíme a neprospějí vám pranic pluky vaše, i kdyby rozmnožily se počtem: neboť Bůh zajisté jest s věřícími.'),
(1180, 8, 20, 'Vy, kteří jste uvěrili, buďte poslušni Boha a proroka jeho a neodvracejte se od něho, nyní, když posloucháte:'),
(1181, 8, 21, 'a nebuďte jako oni, kteří říkají: „Slyšeli jsme“ a neposlouchají,'),
(1182, 8, 22, 'neboť zajisté nejhoršími dobytčaty před Bohem jsou hluší a němí a ti, kteří nechápou.'),
(1183, 8, 23, 'Kdyby Bůh byl věděl o něčem dobrém v nich, byl by jim dal slyšeti: však i kdyby byl dal jim slyšeti, byli by odvrátili se a vzdálili.'),
(1184, 8, 24, 'Vy, kteří jste uvěřili, poslyšte vyzvání Boha a proroka, když zve vás k tomu, co dává život vám: a vězte, že Bůh vstupuje mezi člověka a srdce jeho, a že k němu budete shromážděni.'),
(1185, 8, 25, 'A bojte se pokušení, neboť ono nepostihuje pouze ony, kdož nepravostnými jsou mezi vámi: a vězte, že Bůh přísný jest v trestání.'),
(1186, 8, 26, 'A (pomněte), když malý byl vás počet a slabými byli jste v zemi, a báli jste se, aby nevyhladili vás lidé: tehdy útočiště dal vám a posílil vás pomocí svou a dobrou opatřil vás výživou, abyste mu byli povděčni.'),
(1187, 8, 27, 'Vy, kteří jste uvěřili, nezrazujte Boha a proroka a nebuďte zrádnými v závazcích svých vědomě.'),
(1188, 8, 28, 'Vězte, že statky a děti vaše jsou pokušením vám a že u Boha odměna jest velká.'),
(1189, 8, 29, 'Vy, kteří jste uvěřili, budete-li se báti Boha, on učiní vám rozlišení a promine vám špatné skutky vaše a odpustí vám: neboť Bůh jest nesmírný v milosrdenství svém.'),
(1190, 8, 30, 'A (pomni), když nevěřící strojili ti úklady, aby uvěznili tě, neb zavraždili tě, anebo vypudili tě: proti úkladům jejich Bůh strojil úklady a Bůh zajisté jest nejobratnějším z těch, kdož léčky kladou.'),
(1191, 8, 31, 'A když čtena byla jim znamení naše, řekli: „Již slyšeli jsme to. A kdybychom chtěli, mohli bychom stejná rčení pronášeti: jsouť to pouze bájky starých.“'),
(1192, 8, 32, 'A když řekli: „Velké Bože, je-li toto skutečně pravdou, přicházející od tebe, dšti na nás kamení s nebes, nebo pošli na nás trest bolestný!“'),
(1193, 8, 33, 'Však netrestal jich Bůh, pokud tys byl mezi nimi, aniž trestatelem jich chtěl býti Bůh, když za odpuštění vzývali jej.'),
(1194, 8, 34, 'Ale nebude jinak, než že potresce je Bůh, když zapuzovati budou věřící od modlitebnice posvátné, ač nejsou strážci jejími: pouze bohabojní jsou strážci jejími, však většina jich není si toho vědoma.'),
(1195, 8, 35, 'A modlitbou jejich v domě posvátném bylo pouze hvízdání a tleskání rukou. „Okuste tudíž trestu za zarytou nevíru svou!“'),
(1196, 8, 36, 'Ti, kdož nevěří, rozdávají jmění svá, aby odvrátili druhé od stezky boží: nuž nechť utratí je! Pak ve smutek bude jim to obráceno a poraženi budou: a ti, kdož nevěří, v ohni pekelném budou shromážděni,'),
(1197, 8, 37, 'aby Bůh rozděliti mohl zlé od dobrých a položiti zlé jednoho na druhého a nahromaditi je v jednu kupu a uvrci pak v peklo: tito pak záhubě propadnou.'),
(1198, 8, 38, 'Řekni těm, kteří nevěří, že odpustí jim Bůh vše minulé, přestanou-li: však vrátí-li se k nevíře, mají před sebou příklad v osudu národů předešlých.'),
(1199, 8, 39, 'I bojujte proti nim, dokud nebude vyhlazeno pokušení a dokud veškeré náboženství nebude než Boha jediného: však přestanou-li, Bůh zajisté dobře pozoruje skutky jejich.'),
(1200, 8, 40, 'Ale obrátí-li se zpět, věztež, že Bůh ochráncem jest vaším: nejlepším ochráncem a nejlepším pomocníkem!'),
(1201, 8, 41, 'A vězte, že z toho, cožkoli ukořistili jste, pětina náleží Bohu a proroku, a blízkým příbuzným a sirotkům a chudým a pocestným — věříte-li v Boha a v to, co seslali jsme služebníku svému v den rozlišení, v den sražení obou vojů: a Bůh nade všemi věcmi moc má.'),
(1202, 8, 42, 'Když utábořeni jste byli na nejbližším konci údolí a oni na nejzazším, a karavana byla níže pod vámi: a kdybyste se byli zavázali (k útoku), nebyli byste mohli dostáti svému závazku — však nicméně uvedeni jste byli v boj, aby naplnilo se, co Bůh určil, aby se stalo: totiž, aby zahynul ten, jenž zahynul, dle jasného důkazu, a aby na živu zůstal ten, jenž na živu zůstal, dle jasného znamení: a Bůh zajisté vše slyší a ví.'),
(1203, 8, 43, 'Když Bůh ukázal vám je ve snu tvém malými počtem: a kdyby byl ukázal ti je velkými počtem, byli byste jistě zmalomyslněli a v hádku vešli ohledně toho. Avšak před tím ochránil vás Bůh, neboť on dobře ví, co skrývají nitra.'),
(1204, 8, 44, 'A když ukázal je vám, při sražení s nimi, ve zracích vašich malé počtem a zmenšil počet váš ve zracích jejich, aby naplnilo se, co určeno bylo Bohem, aby se stalo: a k Bohu navráceny budou všechny věci.'),
(1205, 8, 45, 'Vy, kteří jste uvěřili, kdykoli potkáte sbor válečný, stůjte neohroženě a volejte opětovně jméno Boha, abyste úspěchu dosáhli.'),
(1206, 8, 46, 'A duďte poslušni Boha a proroka jeho a nevcházejte v hádky, abyste zmalomyslněli jimi a pozbyli úspěchu: buďte vytrvalými, neboť Bůh zajisté pomáhá vytrvalým!'),
(1207, 8, 47, 'A nebuďte jako oni, kteří vyšli chlubně z domů svých, aby viděni byli lidmi: a odvracejí lid od stezky boží — však Bůh dobře zpraven jest o tom, co činí.'),
(1208, 8, 48, 'A když Satan zkrášlil jim skutky jejich a řekl jim: „Nikdo z lidí nepřemůže vás dnes, kdy já jsem společníkem vaším.“ Však když objevily se oba sbory válečné, obrátil se k nim patami a řekl: „Nemám s vámi ničeho společného neboť v pravdě vidím, co vy nevidíte a v pravdě bojím se Boha, neboť Bůh přísným jest v trestání.“'),
(1209, 8, 49, 'Když řekli pokrytci a ti, v jejichž srdcích jest choroba: „Strhlo je náboženství jejich.“ však kdo na Boha spoléhá — Bůh zajisté mocný jest a moudrý.'),
(1210, 8, 50, 'A kdybys viděl, když andělé usmrcují ty, kdož neuvěřili! Bijí je do tváří jejich i do zad jejich a říkají: „Okuste nyní trestu ohně!'),
(1211, 8, 51, 'To za to, co ruce vaše poslaly před vámi!“ — a Bůh zajisté není utiskovatelem služebníků svých.'),
(1212, 8, 52, 'Vede se jim jako lidu Faraonovu a těm před nimi, kteří neuvěřili ve znamení boží: a těžce dotkl se jich Bůh za viny jejich, neboť Bůh zajisté silný jest a přísný v trestání.'),
(1213, 8, 53, 'To proto, že Bůh nemění požehnání, kterým požehnal některému lidu, dokavad oni nezmění nitro své: a Bůh slyší a vidí vše.'),
(1214, 8, 54, 'Vede se jim jako lidu Faraonovu a těm před nimi, kteří lží nazývali znamení Pána svého: i zahladili jsme je pro viny jejich a potopili jsme lid Faraonův, ježto všichni byli nepravostni.'),
(1215, 8, 55, 'Zajisté nejhoršími dobytčaty u Boha jsou zatvrzelci v nevíře, již nechtějí uvěřiti:'),
(1216, 8, 56, 'ti, se kterými jsi učinil smlouvu a kteří pak neustále porušují smlouvu svou, aneb nebojí se (Boha).'),
(1217, 8, 57, 'Padnou-li ti do rukou ve válce, tehdy rozpraš příkladem jejich ty, kdož za nimi jdou — aby si to rozmyslili.'),
(1218, 8, 58, 'Obáváš-li se pak zrady se strany některého lidu, oplácej mu stejně: neboť Bůh zajisté nemiluje zrádců.'),
(1219, 8, 59, 'A nikterak nechť nedomnívají se nevěřící, že budou v popředí: zajisté neseslabí (moc Boha).'),
(1220, 8, 60, 'Schystejte tudíž proti nim voje seč síly vaše stačí a silné čety jízdy, jimiž postrachem naplníte nepřítele Boha a nepřítele svého a jiné mimo nich, o nichž dosud nevíte, však o nichž Bůh ví. A cožkoli vydáte ze jmění na stezce boží, splaceno vám bude a nebudete zkráceni.'),
(1221, 8, 61, 'A kloní-li se k míru, kloň se též k němu a spoléhej na Boha, neboť On vše slyší a ví.'),
(1222, 8, 62, 'Však chtějí-li oklamati tě, Bůh zajisté postačí ti: onť přispěl ti pomocí svou a pomocí věřících.'),
(1223, 8, 63, 'Onť sjednotil srdce jejich: kdybys byl věnoval veškerá bohatství země, nebyl bys sjednotil srdce jejich, však Bůh sjednotil je: Onť zajisté mocný jest a moudrý.'),
(1224, 8, 64, 'Ó proroku, Bůh dostačí ti a oni, kdož následují tě z věřících.'),
(1225, 8, 65, 'Ó proroku, povzbuzuj věřící k boji: bude-li mezi vámi dvacet vytrvalých, přemohou dvě stě, a bude-li mezi vámi takových sto, přemohou tisíc nevěřících, neboť jest to lid nechápavý.'),
(1226, 8, 66, 'Nyní Bůh ulehčil vám, neboť ví, jak slabi jste. Však bude-li vás sto vytrvalých, přemohou dvě stě: a bude-li vás takových tisíc, přemohou dva tisíce z dopuštení božího, neboť Bůh jest na straně vytrvalých.'),
(1227, 8, 67, 'Nikterému proroku dáno bylo činiti zajatce, dokud neučinil krveprolití na zemi. Vy chcete pomíjející věci tohoto světa, však Bůh chce (dáti vám) život budoucí: a Bůh mocný jest a moudrý.'),
(1228, 8, 68, 'Nebýti nařízení, daného vám již před tím Bohem, byl by dotekl se vás za to, co vzali jste, trest velký.'),
(1229, 8, 69, 'Nuž požívejte z toho, co ukořistili jste, což dovoleno jest a dobré: a bojte se Boha, neboť Bůh velký jest zajisté v odpouštění, slitovný.'),
(1230, 8, 70, 'Ó proroku, rci k těm, kdož zajatci jsou v rukou vašich: „Sezná-li Bůh, že dobro jest v srdcích vašich, dá vám lepší statky těch, které odňaty vám byly a odpustí vám: neboť Bůh velký jest v odpouštění, slitovný.“'),
(1231, 8, 71, 'Však zanášejí-li se zradou vůči tobě nebo zachovali-li se proradně vůči Bohu již před tím, (Bůh) dal ti je v moc a on zajisté jest vědoucí. moudrý,'),
(1232, 8, 72, 'Ti, kdož uvěřili, a vystěhovali se z domů svých a bojovali statky i osobami svými na stezce boží: a ti, kdož útočistě a pomoci poskytli (prorokovi), jsou si navzájem přáteli. A ti. kdož uvěřili však nevystěhovali se, nemají nikterak zahrnuti být vámi ve svazek přátelský, dokud nevystěhují se: ale kdyby prosili vás o pomoc v příčině víry, jest na vás, abyste poskytli jim pomoci, vyjma proti lidu, s nímž jste byli učinili smlouvu. A Bůh dobře pozoruje skutky vaše.'),
(1233, 8, 73, 'Ti, kdož neuveřili, jsou si navzájem přáteli: a neučiníte-li stejně tak, rozbroj zavládne v zemi a zkaženost velká.'),
(1234, 8, 74, 'A ti, kdož uvěřili a vystěhovali se a bojovali na stezce boží: a kdož útočiště a pomoci poskytli (prorokovi): tito jsou věřícími v pravdě — jim odpuštění přichystáno jest a odměna štědrá.'),
(1235, 8, 75, 'A ti, kdož uvěřili poté a vystěhovali se a bojovali s vámi: tito patří ve svazek váš. Však ti, kdož krevně spřízněni jsou, jsou si nejblíže přibuzni navzájem. (Toto psáno jest) v Knize boží a Bůh o všem ví.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Zprostění dané Bohem a prorokem jeho těm, s nimiž ve smlouvu jste vstoupili z mnohobožců.'),
(1237, 9, 2, 'Procházejte tudíž zemí v bezpečí po čtyři měsíce a vězte, že tím neseslabujete moc Boha: a Bůh zajisté hanbou pokryje nevěřící.'),
(1238, 9, 3, 'Toto jest provolání od Boha a proroka jeho k lidu v den velké pouti: Bůh zajisté prost jest (všeho závazku) vůči mnohobožcům, stejně jako prorok jeho, a obrátíte-li se kajícně k Bohu, bude to lépe pro vás: však odvrátíte-li se, vězte, že tím neseslabujete moc Boha: a zvěstuj těm, kdož nevěří, trest bolestný.'),
(1239, 9, 4, 'Vyjmuti jsou ti z mnohobožců, s nimiž učinili jste smlouvu a kteří poté nikterak jí neporušili, aniž pomáhali komu proti vám. Pročež vyplňte přesně smlouvu svou s nimi do konce trvání jejího: Bůh zajisté miluje ty, kdož bojí se jej.'),
(1240, 9, 5, 'A když přešly měsíce posvátné, zabíjejte mnohobožce, kdekoliv je naleznete: a jímejte je, a obléhejte je, a číhejte na ně u každé cesty však obrátí-li se kajícně a budou-li zachovávati modlitbu a povinnou dávati almužnu, nechte je pokojně jíti jich cestou, neboť Bůh velký jest v odpouštení, slitovný.'),
(1241, 9, 6, 'Požádá-li tě některý z mnohobožců o útulek, poskytni mu ho, aby poslechnouti mohl slovo boží: pak nech dovésti jej do místa bezpečí: to proto, že lid jest to nevědoumý.'),
(1242, 9, 7, 'Jákž jinak budou mnohobožci moci býti ve smlouvě s Bohem a prorokem jeho, vyjma těch, s nimiž učinili jste smlouvu u modlitebnice posvátné? Dokud přímými k vám budou, buďte i vy přímými k nim: Bůh zajisté miluje ty, kdož bojí se jej.'),
(1243, 9, 8, 'Jakž jinak? neb přemohou-li vás, nebudou bráti ohledu na svazky příbuzenské ani na přísahu smlouvy. Uspokojí vás ústy svými, ač nesouhlasna zůstanou srdce jejich: a většina jich jest nepravostna.'),
(1244, 9, 9, 'Kupují za znamení boží věci ceny malé a odvracejí jiné od stezky boží: věru špatné jsou skutky jejich!'),
(1245, 9, 10, 'Nebéřou u věrícího ohledu na svazky příbuzenské, ani na přísahu smlouvy: tito zajisté přestupníky jsou.'),
(1246, 9, 11, 'Však obrátí-li se kajícně a budou zachovávati modlitbu a povinnou dávati almužnu, jsou vašimi bratry ve víře: a jasně vysvětlujeme znamení svá lidu chápajícímu.'),
(1247, 9, 12, 'Ale zruší-li přísahu svou poté, když byli učinili smlouvu a útočí na víru vaši, tehdy bojujte proti náčelníkům nevíry — neboť není u nich svaté přísahy — aby ustali.'),
(1248, 9, 13, 'Zdaž nebudete bojovati proti lidu, jenž zrušil přísahu svou a snažili se vypuditi proroka (ze země) a první napadli vás? Zdaž báti se jich budete? Zajisté Bůh více nároků má na to, abyste báli se jej, jste-li věřícími.'),
(1249, 9, 14, 'Bojujte tedy proti nim: ať rukama vašima potrestá je Bůh a hanbou pokryje je a dá vám vítězství nad nimi a uzdraví ňadra lidu věřícího:'),
(1250, 9, 15, 'a odejme zlobu ze srdcí jejich a vyslyší pokání, koho bude chtíti, neboť Bůh vševědoucí jest a moudrý.'),
(1251, 9, 16, 'Zdaž domníváte se, že budete opuštěni, a že Bůh nevěděl by o těch, kdo bojovali z vás a nebrali si za spojence nikoho, kromě Boha, proroka jeho a věřící? Bůh zajisté zpraven jest o tom, co činíte.'),
(1252, 9, 17, 'Nepřináleží mnohobožcům, aby navstěvovali modlitebnice boží, dosvědčujíce proti sobě samým nevíru: tito jsou onů, jichž veškerá konání niveč budou a kteří v ohni na věky budou přebývati.'),
(1253, 9, 18, 'Pouze ten navštěvuje modlitebnice boží, kdo uvěřil v Boha a v den poslední: jenž zachovává modlitbu a dává povinnou almužnu: a nebojí se nikoho, vyjma Boha. Tito as budou z těch, kdož cestou pravou vedeni jsou.'),
(1254, 9, 19, 'Zdaž stavíte ty, kdož napájejí poutníky a navštevují modlitebnici posvátnou, na roveň tomu. jenž uvěřil v Boha a v den poslední a bojoval na stezce boží? Tito nebudou si rovni před Bohem: a Bůh nevede stezkou pravou lid nepravostný.'),
(1255, 9, 20, 'Ti, kteří uvěřili a vystěhovali se a bojovali na stezce boží statky svými a osobami svými, na nejvyšší stupeň budou postaveni u Boha: tito budou šťastni.'),
(1256, 9, 21, 'Oznamuje jim Pán jejich milosrdenství své a zalíbení a zahrady, v níž rozkoše jsou stálé:'),
(1257, 9, 22, 'v nich přebývati budou věčně: neboť zajisté u Boha odměna jest velká.'),
(1258, 9, 23, 'Vy, kteří jste uvěřili, nebeřte si za přátele otce a bratry své, je-li jim nevíra milejší víry: a kdožkoli z vás bral by si je za přátele, patřiti bude mezi nepravostné.'),
(1259, 9, 24, 'Rci: „Jsou-li otcové vaši a děti vaše a bratři vaši a ženy vaše a soukmenovci vaši a statky, kterých jste nabyli a obchod, jehož úpadku se obáváte, a obydlí, v nichž zálibu nalézáte, milejší vám Boha a proroka jeho a bojování na stezce boží — tedy vyčkejte, až Bůh sám vloží se do věci své: a Bůh nevede stezkou pravou lid nešlechetný.“'),
(1260, 9, 25, 'Bůh přispěl vám již pomocí svou v místech mnohých, najmě v den Hunainu, kdy radovali jste se z velkého počtu svého a nic vám neprospěl: a úzkou byla vám země při vší rozsáhlosti své a záda svá v útěku jste obrátili.'),
(1261, 9, 26, 'Tehdy seslal Bůh uklidnění proroku svému a věřícím a seslal vojska neviditelná vám a potrestal ty, kdož neuvěřili: taková byla odplata nevěřících.'),
(1262, 9, 27, 'Poté pak Bůh slitovně obrátí se, ke komu bude chtíti: a Bůh velkým jest v odpoustění, slitovným.'),
(1263, 9, 28, 'I Vy, kteří jste uvěřili! Zajisté mnohobožci nečisti jsou: pročež nenechte je přiblížiti se, po tomto roce, k modlitebnici posvátné. A bojíte-li se nedostatku, Bůh hojně obdaří vás z milosti své, bude-li chtíti: neboť Bůh zajisté jest vševědoucí, moudrý.'),
(1264, 9, 29, 'Bojujte proti těm z oněch, kteří obdrželi Knihu, kdož neuvěřili v Boha a v den poslední, a nezachovávají zákazu Boha a proroka jeho, a nevyznávají náboženství pravdy dokud nevydají poplatek z ruky a nebudou poníženi.'),
(1265, 9, 30, 'Židé praví: „\'Ozair jest syn boží,“ a křesťané praví: „Mesiáš jest syn boží“ — takové jsou řeči úst jejich: podobají se reči oněch, kdož nevěřícími byli před nimi. Bůh válčiž proti nim! Jak mohou tak lhát?'),
(1266, 9, 31, 'Vzali si velekněze své a mnichy své za pány vedle Boha, a (taktéž) Mesiáše, syna Mariina: a nebylo nakázáno jim, aby ctili koho jiného vedle Boha jediného; není Boha, kromě něho a on daleko povýšen jest nad cokoliv, co za rovné mu kladou!'),
(1267, 9, 32, 'Chtěli by zhasnouti světlo boží ústy svými: však Bůh nepřeje si jiného, než aby dokonalým učinil světlo své, i kdyby sebe nemilejším to bylo nevěřícím.'),
(1268, 9, 33, 'Onť to byl, jenž vyslal proroka svého s pravým vedením a s náboženstvím pravdy, aby vítězným učinil je nade všemi ostatními, i kdyby sebe nemilejším to bylo mnohobožcům.'),
(1269, 9, 34, 'Vy, kteří jste uvěřili, zajisté mnozí z velekněží a mnichů upotřebují statky lidí za věci liché a odvracejí lid od stezky boží: však těm, kdož (hrabivě) shromažďují zlato a stříbro a nevydávají je ve stezce boží, oznamuj trest bolestný:'),
(1270, 9, 35, 'v den, kdy roztaveny budou poklady jejich v ohni pekelném a vypálena jimi budou znamení na čelech jejich a bocích jejich a zádech jejich: „Totoť jest, co (hrabivě) shromaždovali jste Pro sebe samy — nuž okuste, co (hrabivě) jste shromaždovali!“'),
(1271, 9, 36, 'Zajisté počet měsíců u Boha jest dvanácte měsíců, (ustanovený) v Knize boží v den, kdy stvořil nebe i zemi: čtyři z nich jsou měsíce posvátné — toto jest stálým (článkem) náboženství: pročež neprohřešujte se v těchto měsících proti sobě samým, ale bojujte proti nevěřícím ve všech, jakož i oni bojují proti vám ve všech — a vězte, že Bůh jest s těmi, kdož bojí se ho.'),
(1272, 9, 37, 'Překládání (měsíců posvátných na jinou dobu) jest zvětšování nevíry: a v blud uváděni jsou tím nevěřící. Dovolují to jedním rokem a zapovídají to druhým rokem, aby tak doplnili počet (měsíců) Bohem posvěcených a tak dovolili to, co Bůh zapověděl. (Satanem) zkrášleny byly jim zlé skutky jejich: a Bůh nevede pravou stezkou lid nevěřící.'),
(1273, 9, 38, 'Vy, kteří jste uvěřili, co děje se s vámi, že kdyz řekne se vám: „Jděte v boj na stezce boží“ těžce klesáte k zemi? Zdaž větší zalíbení nalézáte v životě tomto, než v životě budoucím? Však požitky života pozemského oproti oněm v životě budoucím jsou jen malé.'),
(1274, 9, 39, 'Nepůjdete-li v boj, potresce vás Bůh trestem bolestným: zamění vás národem jiným a nebudete s to uškoditi mu v ničem, neboť Bůh nade všemi věcmi moc má.'),
(1275, 9, 40, 'Nebudete-li pomáhati (prorokovi) — Bůh již pomohl mu, když vyhnali jej ti, kdož neuvěřili. jednoho ze dvou: a když oba byli v jeskyni, tehdy řekl prorok ke společníku svému: „Netruchli, neboť zajisté Bůh jest s námi“ — a Bůh seslal mu uklidnění své a vypomohl mu vojsky neviditelnými zrakům vašim a ponížil slova nevěřících: slovo pak boží jest nejvyšší a Bůh mocný jest a moudrý.'),
(1276, 9, 41, 'Jděte v boj lehce i těžce (ozbrojeni) a bojujte na stezce boží statky i osobami svými: toto bude lepším pro vás, dovedete-li pochopiti.'),
(1277, 9, 42, 'Kdyby se bylo jednalo o blízký úspěch a krátkou výpravu, byli by tě následovali: však dalekou zdála se jim cesta a námaha. Nicméně Bohem zapřisahati se budou: „Kdybychom bývali byli s to, byli bychom vyšli s vámi.“ Hubí tak duše své, neboť Bůh dobře ví, že lháři jsou.'),
(1278, 9, 43, 'Bůh ti promiň! Proč dovolil\'s jim (zůstati), dokud nebylo ti jasně zjevno, kdo mluví pravdu a dokud nevěděl\'s, kdo lhářem jest?'),
(1279, 9, 44, 'Ti, kdož věří v Boha a v den poslední, nebudou žádati tě o úlevu v bojování statky i osobami svými, a Bůh dobře zná ty, kdož bojí se ho.'),
(1280, 9, 45, 'Však žádati budou tě o úlevu jen ti, kdož nevěří v Boha a v den poslední a jichž srdce v nejistotě jsou a kdož kolísají v pochybnostech svých.'),
(1281, 9, 46, 'Kdyby byli bývali měli úmysl vyjíti do boje, byli by se k tomu připravili, všemi přípravami: však Bůh nebyl nakloněn k tomu, aby vyšli a zlenivěl je a bylo řečeno jim: „Seďte doma se sedícími.“'),
(1282, 9, 47, 'Kdyby bývali vyšli s vámi, byli by pouze zvětšili vaše potíže: byli by uvrhli nesnáze do středu vašeho ve snaze přivoditi rozkol, neboť jsou mezi vámi někteří, již ochotně jim naslouchají — a Bůh dobře zná nepravostné.'),
(1283, 9, 48, 'Již dříve snažili se přivoditi rozbroj a převraceli nařízení tvá, dokud nepřisla pravda a nepřevládl rozkaz boží přes to, že bylo jim to nemilé.'),
(1284, 9, 49, 'Jsou mez nimi někteří, již dí: „Pozvol mi (zůstati) a neuváděj mne v pokušení.“ Však neupadli-li již v pokušení? A zajisté peklo obklopí nevěřící.'),
(1285, 9, 50, 'Dostane-li se ti úspěchu, zlobí je to: a když postihne tě neštěstí, praví: „Postarali jsme se o své již dříve“ a zády obracejí se k tobě, radujíce se.'),
(1286, 9, 51, 'Rci: „Nic nepostihne nás, vyjma toho, co předurčil nám Bůh: on pánem jest naším a na Boha nechť spoléhají věřící.“'),
(1287, 9, 52, 'Rci: „Zdaž vyčkáváte, že jiný stihne nás osud, než jedna ze dvou krásných věcí? My pak vyčkáváme pro vás, že postihne vás trest od Boha rukama našima: nuž vyčkávejte — my též s vámi vyčkávati budeme!“'),
(1288, 9, 53, 'Rci: „Ať již dávati budete příspěvky své dobrovolně, nebo z přinucení, nebudou přijaty od vás, ježto lid jste nešlechetný.“'),
(1289, 9, 54, 'A nic jiného nestojí v cestě přijetí příspěvku jejich než to, že nevěří v Boha a v proroka jeho a nezachovávají modlitby, leda lenivě a nedávají příspěvku, leda neradi.'),
(1290, 9, 55, 'Nechť nepřivádějí tě v údiv bohatství jejich a děti jejich: Bůh chce pouze potrestati je jimi v tomto živote pozemském, a chce, aby vypustili duši v nevíře své.'),
(1291, 9, 56, 'Při Bohu přisahají, že jistotně drží s vámi, ale nedrží s vámi, nýbrž jsou to lidé, kteří se vás straní.'),
(1292, 9, 57, 'Naleznou-li útočiště nějaké, nebo jeskyně, nebo skrýši, vezmou nohy na ramena a prchají směrem k nim.'),
(1293, 9, 58, 'Jsou mezi nimi někteří, kdož pomlouvají tě ohledně dělení almužny: dostanou-li z ní podíl, jsou spokojeni, však nedostanou-li z ní ničeho, zlobí se.'),
(1294, 9, 59, 'Kéž byli by spokojeni s tím, co dal jim Bůh a prorok jeho a kéž říkali by: „Bůh postačí nám. Bůh zajisté popřeje nám milosti své a taktéž prorok jeho: zajisté pouze k Bohu obracíme prosbu svou.“'),
(1295, 9, 60, 'Však almužny určeny jsou pouze pro chudé a nuzné, pro ty, kdož sbírají je: pro ty, jichž srdce spjata jsou (ve víře), pro výkupné otroků, pro dlužníky, pro (boj za) věc boží a pro pocestné. Takové jest nařízení boží a Bůh vševědoucí jest a moudrý.'),
(1296, 9, 61, 'Jsou mezi nimi, kdož hanobí proroka, řikajíce: „Jest naslouchatelem.“ Rci: „Takový naslouchatel jest k dobru vašemu: věří v Boha a věří věřícím a milosrdenstvím jest pro ty z vás, kdo uvěřili: však těm, kdož hanobí proroka božího, přichystán jest trest bolestný.“'),
(1297, 9, 62, 'Zapřisahají se vám Bohem, aby se vám zachovali: však Bůh a prorok jeho spíše si zaslouží, aby zachovali se oni jim, jsou-li věřícími.'),
(1298, 9, 63, 'Zdaž nevědí, že tomu, kdo protiví se Bohu a proroku jeho přichystán jest oheň pekelný, v němž přebývati bude věčně? Toto potupa bude velká.'),
(1299, 9, 64, 'Střeží se pokrytci, aby nebyla seslána súra ohledně jich, oznamující jim, co skryto jest v srdcích jejich. Rci: „Vysmívejte se jen: však Bůh odhalí to, ohledně čeho se střežíte.“'),
(1300, 9, 65, 'A dotazuješ-li se jich, řeknou jistotně: „Rozmlouvali jsme jen a laškovali.“ Rci: „Jakže? Z Boha a znamení jeho a proroka jeho žerty si tropíte?“'),
(1301, 9, 66, 'Nechtějte omlouvati se: k nevíře přešli jste poté, když jste byli uvěřili. Odpustíme-li části vás, potrestáme zas druhou — za to, že se provinili.'),
(1302, 9, 67, 'Pokrytci, muži i ženy, jsou jedni z druhých: nabádají ke špatnostem a zabraňují dobru a zavírají ruce své. Zapomněli na Boha a zapomněl na ně Bůh, neboť pokrytci zajisté jsou nešlechetní.'),
(1303, 9, 68, 'Slibuje Bůh pokrytcům, mužům i ženám, jakož i nevěřícím oheň pekelný, v němž přebývati budou věčně. Takový jest úděl jejich; zlořečil jim Bůh a trest trvalý jest jim připraven.'),
(1304, 9, 69, 'Jste jako ti, kteří byli před vámi a silnějšími byli vás mocí a četnějšími vládli statky a a hojnější byl počet jich dětí a spokojovali se s užíváním pozemského údělu svého; pročež i vy spokojujete se s užíváním pozemského údělu svého, stejně jako spokojovali se ti, kdož byli před vámi; a stejné hovory vedete, jaké vedli oni; díla těchto niveč byla na světě tomto i v budoucím: tito jsou, kteří záhubě propadli.'),
(1305, 9, 70, 'Zdaž nedošly jim (k sluchu) děje těch, kdož před nimi byli, lidu Noemova a lidu \'Ád a Tsemúd a lidu Abrahamova, obyvatel Madianu a měst vyvrácených? Přišli k nim proroci jejich s jasnými důkazy a nebyl to Bůh, jenž utiskoval je, nýbrž oni sebe samy utiskovali.'),
(1306, 9, 71, 'Věřící muži i ženy jsou si navzájem přáteli: nabádají k dobru a zabraňují špatnostem: zachovávají modlitbu a dávají povinnou almužnu: a poslušni jsou Boha a proroka jeho: nad těmito slituje se Bůh, neboť Bůh zajisté jest mocný a moudrý.'),
(1307, 9, 72, 'Slíbil Bůh věřícím mužům a ženám zahrady, pod nimiž řeky tekou: v nich přebývati budou věčně: příbytky rozkošné v zahradě Edenu. Však zalíbení Boha v nich bude největší jim odměnou: toto blahem bude velkým.'),
(1308, 9, 73, 'Ó proroku, bojuj proti nevěřícím a pokrytcům a zacházej přísně s nimi: obydlím jejich bude peklo a špatný to bude příbytek!'),
(1309, 9, 74, 'Zapřisahají se Bohem, že neřekli ničeho podobného: však nicméně pronesli slova nevíry a stali se nevěřícími poté, když byli přijali Islám. Vzali si do hlavy věc, které nedosáhli: a nemstili se jen proto, že Bůh a prorok jeho obdařili je bohatstvím z milosti své. A obrátí-li se v kajícnosti, bude to lépe pro ně, však vrátí-li se (k nepravostem svým), potresce je Bůh trestem bolestným ve světě tomto i v budoucím a nebude jim přítele na zemi, aniž zastánce.'),
(1310, 9, 75, 'Jsou mezi nimi někteří, kdož učinili tuto smlouvu s Bohem: „Popřeje-li nám Bůh milosti své, jistotně budeme dávati almužmu a jistotně budeme ze spravedlivých.“'),
(1311, 9, 76, 'Však když obstědřil je z milosti své, lakotili darem jeho a odvrátili se a vzdálili.'),
(1312, 9, 77, 'I dal Bůh na to vstoupiti pokrytectví do srdcí jejich až do dne, kdy setkají se s ním: za to, že nedostáli svému slibu Bohu a za to, že lhali.'),
(1313, 9, 78, 'Zdaž nevědí, že Bůh zná tajnosti jejich a rozhovory jejich skyté? Bůh zajisté dobrý jest znatel věcí skrytých.'),
(1314, 9, 79, 'Ti, kdož pomlouvají ony z věřících, kteří víc než ochotnou přinášejí almužnu a ony, kteří nenalézají možnosti přinášeti jí, leda s největším úsilím: ti, kdož vysmívají se jim — těmto zajisté vysměje se Bůh a přichystán jest jim trest bolestný.'),
(1315, 9, 80, 'Pros za odpuštění pro ně, aneb nepros: i kdybys prosil za odpuštění pro ně sedmdesátkrát, neodpustí jim Bůh. To proto, že neuvěřili v Boha a v proroka jeho: a Bůh nevede stezkou pravou nešlechetné.'),
(1316, 9, 81, 'Radovali se ti, kdož zůstali pozadu u pecí svých, za prorokem božím, neb proti mysli jim bylo bojovati statky svými a osobami svými na stezce boží, a řekli: „Nevycházejte (v boj) za vedra.“ Rci: „Oheň pekelný více pálí než vedro!“ Kéž by to byli pochopili!'),
(1317, 9, 82, 'Nechť smějí se jen trochu: však jednou velice budou plakati v odměnu za to, co si zasloužili.'),
(1318, 9, 83, 'Když pak navrátiti dal ti Bůh (z boje) k některým z nich, žádají o dovolení k vyjití s tebou (da pole). Rci pak: „Nevyjdete se mnou (do pole) nikdy a nikdy nebudete bojovati se mnou proti nepříteli: neboť zlíbilo se vám seděti doma (když jednalo se o to) po prvé — nuž, sečte si doma s těmi, kdož pozadu zůstávají.“'),
(1319, 9, 84, 'A nemodli se za žádného z nich nikdy, zemře-li, aniž zastav se u hrobu jeho: neboť neuvěřili v Boha a v proroka jeho a zemřeli co nešlechetní.'),
(1320, 9, 85, 'Nechť nepřivádějí tě v údiv bohatství jejich a děti jejich: Bůh chce pouze potrestati je jimi v životě pozemském, a chce, aby vypustili duše v nevíře své.'),
(1321, 9, 86, 'A když seslána byla súra „Uvěřte v Boha a bojujte s prorokem jeho,“ žádali tě zámožní mezi nimi o úlevu a řeli: „Dovol nám, abychom zůstali s doma sedícími.“'),
(1322, 9, 87, 'Líbilo se jim zůstati s těmi, kdož zůstali pozadu a zapečetěna byla srdce jejich, takže nechápou.'),
(1323, 9, 88, 'Avšak prorok a ti kdož uvěřili s ním, bojovali statky svými a osobami svými: pro tyto přichystány jsou nejlepší věci a jim dobře se povede.'),
(1324, 9, 89, 'Přichystal jim Bůh zahrady, pod nimiž řeky tekou: toto blaho bude velké.'),
(1325, 9, 90, 'A přišli někteří Arabové z pouště omlouvati se, aby úleva dána jim byla: a zůstali seděti doma ti, kdož za lháře prohlašují Boha a proroka jeho: však postihne ty, kdož neuvěřili mezi nimi, trest bolestný.'),
(1326, 9, 91, 'Nemají viny slabí a nemocní, a ti, kdož nemají z čeho by přispěli, jsou li upřímní vůči Bohu a proroku jeho: není příčiny k trestání těch, kdož dobře činí a Bůh zajisté velký jest v odpouštění, slitovný.'),
(1327, 9, 92, 'Aniž těch, kdož přišli k tobě, žádajíce o výzbroj a jimž odvětil\'s: „Nemám, čím bych vás vyzbrojil“ — i odvrátili se se zraky přetékajícími slzami zármutku proto, že nenalezli, čím by přispěli.'),
(1328, 9, 93, 'Však bude příčina k trestání těch, kdož žádají tě o úlevu, ač jsou bohati. Milejší jest jim bytí mezi těmi, kdož zůstali pozadu: a zapečetil Bůh srdce jejich a ničeho nevědí.'),
(1329, 9, 94, 'Omlouvají se vám, když vrátíte se mezi ně. Rci: „Neomlouvejte se: nevěříme vám: Bůh již zpravil nás ohledně vás: a uvidí Bůh činy vaše, jakož i prorok jeho — pak navráceni budete k tomu, jemuž známy jsou věci skryté i viditelné a on poví vám, co jste činili.“'),
(1330, 9, 95, 'Zapřisahati vás budou při Bohu, když byli jste se navrátili mezi ně, abyste vzdálili se jich: nuž vzdalte se jich, neboť nečistými jsou a obydlím jejich bude peklo v odměnu za to, co si zasloužili.'),
(1331, 9, 96, 'Zapřisahati vás budou, abyste zalíbení v nich nalezli: však naleznete-li vy zalíbení v nich Bůh zajisté nenalezne zalíbení v lidu nešlechetném!'),
(1332, 9, 97, 'Arabové z pouště nejzarytějšími jsou v nevíře a pokrytectví a nejblíž k tomu, aby neznali mezí zákonů seslaných Bohem proroku jeho, a Bůh jest vševědoucí, moudrý.'),
(1333, 9, 98, 'Jsou mezi Araby pouště někteří, kdož považují příspěvky své jakožto půjčku a vyčkávají nějaký obrat (osudu) s vámi: špatný obrat očekává je, neboť Bůh vše slyší a ví.'),
(1334, 9, 99, 'Jsou pak mezi Araby pouště někteří, kdož věří v Boha a v den poslední, a považují příspěvky své za přiblížení k Bohu a k modlitbám prorokovým. Zdaž tyto nejsou přiblížením jich k Bohu? I uvede je Bůh do milosrdenství svého, neboť Bůh velký jest v odpuštění, slitovný.'),
(1335, 9, 100, 'Ti, kdož první šli napřed z vystěhovalců a pomocníků a ti, kdož následovali jich krásny příklad — v těchto zalíbení nalézá Bůh a oni zalíbení nalézají v něm: a připravil jim zahrady, pod nimiž řeky tekou, v nichž přebývati budou věčně věkův a blahem to bude velkým.'),
(1336, 9, 101, 'A kdo z Arabů pouště, bydlících kol vás, jsou pokrytci, a kteří z obyvatel Medíny jsou zatvrzení v pokrytectví, neznámo jest ti: však my známe je. Dvakráte potrestáme, je a pak uvrženi budou v trest velký.'),
(1337, 9, 102, 'Druzí pak přiznali se k vinám svým: a tak smísili skutek dobrý se skutkem špatným. Pokání těchto snad popřeje sluchu Bůh, neboť Bůh velký jest v odpouštení, milosrdný.'),
(1338, 9, 103, 'Vezmi almužnu ze statků jejich, abys očistiti jí mohl je a zprostiti hříchu: a modli se za ně, neboť modlitby tvé jiste přispějí k uklidnění (myslí jejich) — a Bůh vše slyší a ví.'),
(1339, 9, 104, 'Zdaž nevědí, že Bůh přijímá POKÁNÍ služebníků svých a přijímá almužnu? Bůh zajisté rád popřává sluchu kajícím a jest milosrdný.'),
(1340, 9, 105, 'Rci: „Mějte se k dílu! a bude viděti Bůh činy vaše, jakož i prorok jeho a věřící: jednoho dne pak přivedeni budete před toho, jenž zná věci skryté i viditelné a on vypoví vám, co jste činili.“'),
(1341, 9, 106, 'Druzí pak nadějí se na rozhodnutí Boha: že buď potrestá je, anebo přijme pokání jejich — a Bůh vševědoucí jest a moudrý.'),
(1342, 9, 107, 'Jiní pak vybudovali si modlitebnici ke škodě a z nevíry a proto, aby způsobili rozkol mezi věřícími a aby v záloze skryli se tam ti, kdož před tím již válčili proti Bohu a proroku jeho. A zapřisáhají se, řkouce: „Zajisté chtěli jsme jen dobro“: však Bůh je svědkem, že lháři jsou.'),
(1343, 9, 108, 'Nikdy nechť nestane v ní noha tvá. Jest v pravdě jiná modlitebnice, založená na bázni boží od prvního dne: tato hodnější jest tvého vstoupení do ní. V ní mužové jsou, kteří touží po očištění a Bůh miluje ty, kdož očištění si žádají.'),
(1344, 9, 109, 'Který z nich jest lepším? Zdaž ten, jenž založil stavbu svou na bázni boží a na touze po zalíbení jeho, aneb ten, jenž založil stavbu svou na okraji hliněné strouhy, bystřinami podemleté a na spadnuti spolu s ním v oheň pekelný? Bůh pak nevede stezkou přímou lid nepravostný.'),
(1345, 9, 110, 'Budova, kterou vystavěli, nepřestane býti zdrojem pochybností v srdcích jejich, dokud nebudou (na kusy) rozřezána srdce jejich: a Bůh jest vševědoucí a moudrý.'),
(1346, 9, 111, 'Zajisté Bůh koupil od věřících osoby jejich i statky jejich s tím, že dány jim budou zahrady rajské: bojovati budou na stezce boží a zabíjeti budou i budou zabíjeni: slib tento potvrzen jest v Zákoně i Evangeliu a v Koránu — a kdo věrnějším jest Boha v dodržování slibů? Radujte se tedy z úmluvy, kterou jste umluvili: toto zajisté blahem bude velkým.'),
(1347, 9, 112, 'Ti, kdož kajícně obracejí se k Bohu, kdož vzývají jej a chválí jej, kdož postí se a padají na kolena i na tváře své, kdož nabádají k dobru a zabraňují špatnostem a zachovávají meze zákonů božích: zvěst radostnou oznamuj věřícím!'),
(1348, 9, 113, 'Nepřísluší prorokovi, ani věřícím, aby prosili za odpuštění pro ty, kdož Bohu dávají společníky, i kdyby příbuznými jich byli, poté, když jasně ukázáno jim bylo, že obyvateli budou ohně pekelného.'),
(1349, 9, 114, 'Neboť ani Abraham neprosil za odpuštění pro otce svého, leda proto jen, že slibem se mu byl zavázal: však když jasne ukázáno mu bylo, že otec jeho nepřítelem jest Boha, prohlásil, že ničeho s ním nechce míti společného: a přece Abraham byl soucitný a laskavý.'),
(1350, 9, 115, 'Aniž Bůh zavádí lid v blud poté, když byl uvedl jej na stezku pravou dříve, než jasně byl mu zjevil, čeho jest jim báti se: neboť zajisté Bůh jest vševědoucí.'),
(1351, 9, 116, 'Bohu zajisté přináleží panství nebes i země: onť jediný dává život a přivodí smrt: a není vám mimo něho ochránce, aniž pomocníka.'),
(1352, 9, 117, 'Bůh odpustil prorokovi a vystěhovalcům i pomocníkům, kterí následovali jej v hodině protivenství, poté, když málem by byla selhala srdce části jejich: tehdy obrátil se s prominutím k nim, neboť laskavým byl k nim a milosrdným.'),
(1353, 9, 118, 'A prominul rovněž třem, kteří zůstaveni byli vzadu: tehdy když přílis úzkou stala se jim země při vší rozlehlosti své, ba ve vlastních duších cítili se úzkými a domnívali se, že není útočiště před Bohem, mimo k němu samotnému. Poté obrátil se k nim Bůh laskavě, aby kajícně obrátili se k němu: neboť Bůh rád obrací se ke kajícným a jest milosrdný.'),
(1354, 9, 119, 'Vy, ktřeří jste uvěřili, bojte se Boha a setrvávejte mezi spravedlivými!'),
(1355, 9, 120, 'Neměli příčiny obyvatelé Medíny a Arabové, bydlící v poušti vůkol, aby opustili proroka božího, aniž by více milovali životy své, než jeho život: a to proto, že nepostihne je žízeň, ani námaha, ani nedostatek v boji na stezce boží: aniž pokročují krokem, jenž by rozzlobiti mohl nevěřící, aniž utrpí co se strany nepřítele, aniž zapsáno jim to bylo co dobrý skutek — neboť Bůh zajisté nedá ztratiti se odměně těch, kdož dobře činí.'),
(1356, 9, 121, 'Aniž dávají almužnu, ať malou, či velkou, aniž překročují potok v údolí, aniž by zapsáno jim to bylo (k dobru): aby odměniti je mohl Bůh lépe, než zasloužili si dle skutků svých.'),
(1357, 9, 122, 'Není záhodno, aby všichni věřící vytáhli najednou (do boje): a když nevytáhne část každého oddílu doma, (tedy proto), aby poučovali se v náboženství a mohli varovati lid svůj poté, když se byli navrátili, tak aby mohli míti se na pozoru.'),
(1358, 9, 123, 'Vy, kteří jste uvěřili, bojujte proti sousedícím s vámi nevěřícím a nechť naleznou ve vás drsnost: a vězte, že Bůh jest s těmi, kdož bojí se ho.'),
(1359, 9, 124, 'Pokaždé, když nová súra jest seslána, jsou mezi nimi někteří, již dí: „Ve kterém z vás rozmnožila víru tato súra?“ Však těch, kdož věří, zajisté rozmnožena jest jí víra a oni radují se z ní.'),
(1360, 9, 125, 'Však těch, jichž srdce zachvácena jsou chorobou, pouze pochybnost bude jí rozmnožena a zemrou co nevěřící.'),
(1361, 9, 126, 'Zdaž nevidí, že v pokušení uvedeni jsou každoročně jednou aneb dvakráte? Nicméně nekají se, aniž přemítají.'),
(1362, 9, 127, 'A pokaždé, když seslána jest nová súra, hledí jeden na druhého, řkouce: „Zdaž vidí vás kdo?“ A (obracejí se pak zády a) odcházejí. Bůh odvrátil srdce jejich od pravdy proto, že lidem jsou nechápajícím.'),
(1363, 9, 128, 'Nyní přišel vám prorok ze středu vašeho: tíží jej poklesky vaše, starostliv jest o vás, abyste byli věřícími a laskavým jest i milosrdným.'),
(1364, 9, 129, 'Však odvrátí-li se od tebe, rci: „Bůh postačitelný jest mi: není jiného Boha, kromě něho: na něho spoléhám a on Pánem jest trůnu přemocného!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Alif. Lám. Rá. Tato jsou znamení Knihy moudré.'),
(1366, 10, 2, 'Zdaž s podivenín jest lidu, že vnuknuli jsme muži z nich: „Varuj lid a oznamuj těm, kdož uvěřili, radostnou zvěst, že budou míti přednost u Pána svého pro upřímnost svou.“ A praví nevěřící: „Zajisté muž tento jest kouzelníkem zjevným.“'),
(1367, 10, 3, 'Zajisté Pánem vaším jest Bůh, jenž stvořil nebe i zemi v šesti dnech: po té usedl na trůn, s něhož všechny věci, řídí. Není přímluvce u něho vyjma s dovolením jeho. Totoť jest Bůh,, Pán váš: pročež uctívejte jej! Zdaž nebudete o tom přemýsleti?'),
(1368, 10, 4, 'K němu navrátíte se všichni. Slib Boha jest pravdivý: dává vzniknouti stvoření a pák navracuje je k sobě, aby spravedlivě odměnil ty, kdož uvěřili a konali dobré skutky: pro ty pak, kteří neuvěřili, připraven jest nápoj vody vroucí a trest bolestný za to, že neuvěřili.'),
(1369, 10, 5, 'On to jest, jenž učinil slunce jasnou září a měsíc světlem a určil postavení jeho, abyste znali počet roků a sčítání jich. Vše to stvořil Bůh jedině v pravdě: vysvětluje tím znamení svá těm, kdož rozumějí.'),
(1370, 10, 6, 'Zajisté ve střídání noci se dnem a ve všem, co stvořil Bůh na nebi i na zemi, znamení jsou pro bohabojné.'),
(1371, 10, 7, 'Zajisté ti, kdož nedoufají v setkání s námi kdož zalíbení nalézají jedině v životě pozemském a spokojují se s ním: a ti, kteří nevšímají si znamení našich:'),
(1372, 10, 8, 'příbytkem těchto bude oheň pekelný v odměnu za to, co si zasloužili.'),
(1373, 10, 9, 'Ty pak, kdož uveřili a konali dobré skutky, povede Pán jejich stezkou přímou ve víře jejich: potekou řeky pod nohama jejich v zahradách rozkoše.'),
(1374, 10, 10, 'Volati v nich budou: „Chválen budiž, velký Bože“ a (vítacím) pozdravem jich tam bude : „Pokoj vám.“ a zakončením vzývání jich bude: „Chvála Bohu, Pánu veškerenstva!“'),
(1375, 10, 11, 'Kdyby Bůh urychloval příchod zla lidem tak, jak oni by si přáli, aby urychleno bylo jim dobro, dávno by již, skončila se určená jim lhůta! Však ty, kdož nedoufají v setkání s námi, necháváme blouditi bez vodítka ve svévolnosti jejich.'),
(1376, 10, 12, 'Když dotkne se člověka zlo, volá nás k sobě v leže, v sedě i v stoje: však sotvaže zbavili jsme jej zla jeho, jde si pěkně jako by nás nikdy nebyl vzýval ohledně zla, jež dotklo se ho. Takovýmto způsobem zkrášleny jsou výstředníkům skutkové jejich.'),
(1377, 10, 13, 'Však zahladili jsme již pokolení před vámi, když páchala nepravosti a přišli k nim proroci jejich s jasnými důkazy a oni neuvěřili: takovýmto způsobem odměňujeme lid provinilý.'),
(1378, 10, 14, 'Poté učinili jsme vás nástupci jejich na zemi, abychom shlédli, jak vy jednati budete.'),
(1379, 10, 15, 'A když čtena jsou znamení naše jasná těm, kteří nedoufají v setkám s námi, praví: „Přines nám nějaké jiné čtení, anebo pozměň toto.“ Rci: „Nepřísluší mi, abych měnil je z vlastního popudu: následuji, pouze to, co vnuknuto mi bylo a v pravdě obávám se, že v případě neposlušnosti mé Pán můj postihl by mne trestem v den velký.“'),
(1380, 10, 16, 'Rci: „Kdyby Bůh byl chtěl, nebyl bych předříkával vám je, aniž bych vám je byl vůbec oznamoval vždyť přece žil jsem mezi vámi po dlouhá léta před tím: což toho nepochopíte?“'),
(1381, 10, 17, 'A kdo nepravostnějším jest toho, jenž vymýšlí o Bohu lež, anebo vylhanými nazývá znamení jeho: zajisté dobře se nepovede provinilým!'),
(1382, 10, 18, 'A vedle Boha vzývají božstva, jež nemohou jim ani uškoditi, ani prospěti, řkouce: „Tito přímluvcí jsou naši u Boha.“ Rci: „Zdaž pověděti chcete Bohu o něčem, co by nevěděl, na nebi i na zemi? Při slávě jeho, daleko povýšen jest nad to, co spolčujete s ním!“'),
(1383, 10, 19, 'Lidé byli jediným pouze národem, však rozešli se v názorech později: a nebýti slova Pána tvého, zjeveného dříve, bylo by již dávno rozhodnuto mezi nimi to, v čem se rozcházeli.'),
(1384, 10, 20, 'Praví: „Nebude-li mu seslán zázrak od Pána jeho, (neuvěříme).“ Tehdy rci: „Znalost věcí skrytých přináleží pouze Bohu: nuž vyčkejte a i já s vámi budu vyčkávati.“'),
(1385, 10, 21, 'A když dali jsme zkusiti jim požehnání našeho po neštěstích, jimiž postiženi byli — hle, lstivé vytáčky mají ohledně znamení našich. Rci: „Bůh rychlejším jest v lstivostí a poslové naši sepisují to, co lstivě vymyslíte.“'),
(1386, 10, 22, 'Onť jest to, jenž umožňuje vám cestování souší i mořem, a když jste na lodi: a když pohání je vítr příznivý, radují se z něho: však když vítr bouřný je zachvátí a vlny valí se na ně se všech stran, takže připadá jim, jako by obklopeni jimi byli, tu volají Boha, upřímnými tvrdíce se býti v náboženství: „Kéž bys jen vysvobodil nás z této nesnáze! Zajisté byli bychom ti vděčni!“'),
(1387, 10, 23, 'Však když vysvobodil je Bůh — hle, dopouštějí se špatností na zemi bezprávně. Ó lidé, zajisté špatnosti, jichž dopouštíte se proti sobě samým, jsou pouze požitky tohoto světa: poté k nám bude vám se navrátiti a pak vypovíme vám, jakých dopouštěli jste se skutků.'),
(1388, 10, 24, 'V pravdě, život tento pozemský podoben jest vodě, kterou sesíláme s nebes: tato mísí se s plodinami země, z nichž pojídají lidé i zvířata; a když země zdobí se a okrašluje, obyvatelé její domnívají se, že pány jsou její: však rozkaz náš přichází za noci neb za dne a ve skosenou obracíme žeň její, jako by včera ještě nebyla bujela! Takovýmto způsobem vysvětlujeme znamení svá lidu přemýšlivému.'),
(1389, 10, 25, 'A Bůh volá k domu míru a vede, koho chce, ke stezce přímé.'),
(1390, 10, 26, 'Těm, kdo dobře činili, dobro s přídavkem bude údělem, a nezhyzdí obličeje jejich černota, ni potupa: tito obyvateli budou ráje a v něm přebývati budou věčně.'),
(1391, 10, 27, 'Těm pak, kdož konali zlo, odměnou bude stejný podíl zla a potupou pokryti budou. Nebudou míti ochránce proti Bohu a tváře jejich jako by pokryty byly kusem černé noci, ztemnělé budou. Tito obyvateli budou ohně a v něm přebývati budou věčně.'),
(1392, 10, 28, 'A jednoho dne shromáždíme je všechny: pak řekneme těm, kdož společníky dávali Bohu: „Na místa svá, vy a vaši společníci Boha!“ Tehdy rozdělíme je od sebe, i řeknou společníci, které oni dávali Bohu: „Zajisté nesloužili jste nám:'),
(1393, 10, 29, 'a Bůh postačitelným jest svědkem mezi námi a vámi, že ani nedbali jsme o uctívání vaše.“'),
(1394, 10, 30, 'Tam vyzkoušena bude každá duše dle zásluh, jež byla poslala před sebou: a navráceni budou k Bohu, Pánu svému pravému a ztratí se jim to, co si byli vymyslili.'),
(1395, 10, 31, 'Rci: „Kdo vyživuje vás z nebe i země, a kdo moc má nad sluchem a zrakem, a kdo přivádí živé z mrtvých a přivádí mrtvé z živých a řídí všechny věci? I řeknou: „Bůh!“ Tehdy rci: „Což tedy nebudete báti se (ho)?'),
(1396, 10, 32, 'Totoť jest tudíž Bůh, Pán váš pravý: a což jiného zůstane, zajde-li pravda, než blud? Jak tedy můžete se od ní vzdalovat?“'),
(1397, 10, 33, 'Takovýmto způsobem uskutečnilo se slovo Pána tvého nad špatnými: že neuvěří.'),
(1398, 10, 34, 'Rci: „Zdaž mezi Bohy vašimi jest některý, jenž vznikati dává stvoření a pak navrací je k sobe?“ Rci: „Jedině Bůh dává vznikati stvoření a navrací je pak k sobě! Jak můžete tak lháti?“'),
(1399, 10, 35, 'Rci: „Zdaž mezi bohy vašimi jest některý, jenž vede k pravdě?“ Rci: „Bůh vede k pravdě. Kohož tedy spíše sluší následovati: toho, jenž vede, aneb toho, jenž nemůže vésti, není-li sám veden? Jak to přijde tedy, že tak usuzujete?“'),
(1400, 10, 36, 'Většina jich následuje pouze domněnky: však domněnka jistě nemá v sobě praničeho pravdivého a Bůh dobře ví, co oni činí.'),
(1401, 10, 37, 'A toto Čtení nebylo nikým vymyšleno, vyjma Bohem: ale jest potvrzením toho, co zjeveno bylo před tím a jest vysvětlenín Knihy, o níž není pochyby, od Pána veškerenstva.'),
(1402, 10, 38, 'Či říkají: „On vymyslil je?“ Rci: „Nuž, předveďte súru podobnou této: a přivolejte si na pomoc, koho můžete kromě Boha, jste-li pravdomluvnými.“'),
(1403, 10, 39, 'Avšak vylhaným nazvali to, čehož moudrost nemohou, obsáhnouti rozumem svým, ač dáno jim bylo toho vysvětlení: stejně tak činili ti, kdož byli před nimi — a hle, jaký byl konec nepravostných!'),
(1404, 10, 40, 'Jsou mezi nimi někteří, kdož věří a jsou mezi nimi někteří, kdož nevěří: a Pán tvůj nejlépe rozpoznává pohoršlivé.'),
(1405, 10, 41, 'A nazývají-li tě lhářem, rci: „Moje dílo jest dílem mým a vašimi jsou díla vaše: prost jsem podílu na činech vašich a vy prosti jste podílu na činech mých.“'),
(1406, 10, 42, 'Jsou mezi nimi někteří, kdož naslouchají ti: však zdaž způsobíš, aby hluší slyšeli, když přece nepochopí?'),
(1407, 10, 43, 'Jsou mezi nimi někteří, kdož na tě pohlížejí: však zdaž povedeš slepé, ač nevidí?'),
(1408, 10, 44, 'Bůh zajisté neutiskuje lidí v ničem, však lidé sebe samy utiskují.'),
(1409, 10, 45, 'Jednoho dne shromáždí je všechny: bude zdáti se jim, jako by nebyli pobyli v hrobě déle než hodinu (jednoho) dne a budou poznávati druh druha. Tehdy zahynou ti, kdož vylhanými nazývali setkání s Bohem a nebyli vedeni stezkou pravou.'),
(1410, 10, 46, 'Buď ukážeme ti některé z trestů jim slibovaných, anebo vezmeme tě k sobě; k nám však navráceni budou všichni: a pak Bůh bude svědkem ohledně toho, co činili.'),
(1411, 10, 47, 'Každý národ měl svého proroka: a když přišel k nim prorok jejich, rozsouzeno bylo mezi nimi spravedlivě a nebylo jim ublíženo.'),
(1412, 10, 48, 'Nicméně říkají: „Kdy tedy uskuteční se tato hrozba? (Rcetež), jste-li pravdomluvnými.“'),
(1413, 10, 49, 'Rci: „Není v mé moci, abych sobě samu přivodil zlé či dobré, vyjma toho, co Bůh chce. Každý národ má svoji lhůtu a když přijde lhůta jejich, ani o hodinu nezpozdí ji, aniž uspíší.“'),
(1414, 10, 50, 'Rci: „Co míníte? Měl-li by trest boží přijíti vám v noci anebo za dne: proč chtěli by provinilci uspíšiti příchod části jeho?'),
(1415, 10, 51, 'Zdaž uvěříte v trest, až padne na vás? Zajisté pak ano: a přece jste urychlovali příchod jeho.“'),
(1416, 10, 52, 'Pak řečeno bude nepravostným: „Okuste trestu věčného! Zdaž jinak měli byste býti odměněni, než jak zasloužili jste si?“'),
(1417, 10, 53, 'Budou pak doptávati se tě: „Bude tomu v pravdě tak?“ Rci: „Ano, při Pánu svém přisahám, že jest to pravda: a nebudete moci (mu to) znemožniti!“'),
(1418, 10, 54, 'A tehdy každá duše, jež nepravosti se dopustila, kdyby měla veškerá bohatství země, dala by je výkupným a budou tajiti lítost, když spatří trest (svůj): tehdy rozsouzeno bude mezi všemi spravedlivě a nebude jim ublíženo.'),
(1419, 10, 55, 'Což nepřináleží vše, co na nebi jest i na zemi, Bohu? Není-liž slib Boha pravdivý? Však většina lidí není si toho vědoma.'),
(1420, 10, 56, 'Onť jest to, jenž přivodí život a smrt a k němu navráceni budete.'),
(1421, 10, 57, 'Ó lidé, nyní přišla vám napomenutí od Pána vašeho a lék pro neduhy niter vašich a pravé vedení a milosrdenství věřícím.'),
(1422, 10, 58, 'Rci: „Při milosti boží a milosrdenství jeho, nechť se tedy z toho radují: to lepším jim bude nade všechno, co shromaždují.“'),
(1423, 10, 59, 'Rci: „Co míníte? Z toho, co Bůh seslal vám za potravu, učinili jste si některé věci zapovězené a jiné dovolené?“ Rci: „Zdaž Bůh vám k tomu dal svolení? Anebo jen cos o Bohu vymýšlíte?“'),
(1424, 10, 60, 'Však jak smýšleti budou ti, kdož o Bohu vymýšlejí lži, v den zmrtvýchvstání? Zajisté Bůh jest velkým v milosti své vůči lidem, však většina jich není mu za to vděčna.'),
(1425, 10, 61, 'A v jakémkoli postavení nalézati se budeš a cokoliv předříkávati budeš ze Čtení a jakoukoli podniknete práci — svědky vždy budeme nad vámi, když zaměstnáni v tom budete: a neujde pozornosti Pána tvého ni co obnáší váha zrnka písečného na zemi i na nebi, aniž co obnáší méně či více: vše to zapsáno jest v Knize zjevné.'),
(1426, 10, 62, 'Zdaž nejsou přáteli Boha ti, jimž netřeba se báti, aniž rmoutiti se?'),
(1427, 10, 63, 'Ti, kdož uvěřili a byli bohabojnými.'),
(1428, 10, 64, 'Pro ně radostná jest zvěst v životě pozemském i v budoucím: není změny ve slovech božích! Toto jistě blahem bude velkým.'),
(1429, 10, 65, 'A nepřipouštěj si zármutku pro slova jejich: zajisté moc veškerá Bohu přináleží a on vše slyší a ví.'),
(1430, 10, 66, 'Zdaž nepřináleží Bohu vše, což na nebi jest i na zemi? Co tedy následují ti, kteří vedle Boha vzývají společníky jeho? Zajisté následují pouze smyšlenky a prolhanci jsou.'),
(1431, 10, 67, 'Onť to byl, jenž ustanovil vám noc k odpočinku a den k vidění: v tom zajisté jsou znamení lidu poslouchajícímu.'),
(1432, 10, 68, 'Říkají: „Bůh zplodil syna.“ Při slávě jeho, (nikoliv)! On postačí sám sobě: jemuť přináleží vše, co na nebi jest i na zemi. Zdaž máte zmocnění k podobným tvrzením, anebo říkáte o Bohu, co nevíte?'),
(1433, 10, 69, 'Rci: „Zajisté těm, kdo lež o Bohu vymýšlejí, nepovede se dobře.'),
(1434, 10, 70, 'Užívati budou dočasně tohoto světa: pak k nám bude jich návrat a pak okusiti jim dáme trestu přísného za to, že byli nevěřícími.'),
(1435, 10, 71, 'Vyprávěj jim případ Noemův, když řekl k lidu svému: „Lide můj, je-li nepříjemným vám pobývání mé mezi vámi a připamatovávání mé vám znamení božích — nicméně spoléhám na Boha: spojte tudíž záměr svůj a božstva svá, však nerozhodujte vykonání záměrů svých v temnu; nýbrž rozsuďte ohledně mne a nenechte mne čekati.'),
(1436, 10, 72, 'A obrátíte-li se ke mně zády, (dobře): nežádám si od vás odměny, neboť odměna má jest jedině u Boha a nakázáno mi bylo, abych odevzdal se do vůle jeho.“'),
(1437, 10, 73, 'Však lhářem nazvali jej: pročež zachránili jsme jej a ty, kdož byli s ním v korábu. A učinili jsme je nástupci všeho lidstva a utopili jsme ty, kdož lží nazývali znamení naše. Hle, jaký byl konec napomenutých!'),
(1438, 10, 74, 'Po něm poslali jsme proroky k národům jejich. Tito přišli k nim s jasnými důkazy, však nechtěli věřiti (národové) v to, co byli před tím nazvali lží: takovýmto způsobem zapečeťujeme srdce přestupníků.'),
(1439, 10, 75, 'Po nich pak poslali jsme Mojžíše a Árona k Faraonovi a předním z lidu jeho s jasnými důkazy: však pýchou naplněni jsou, a stali se lidem provinilým.'),
(1440, 10, 76, 'A když přišla jim pravda od nás, řekli: „Totoť zajisté jest pouze kouzelnictví zjevné.“'),
(1441, 10, 77, 'Tehdy řekl Mojžíš: „Zdaž říkati chcete o pravdě, když přišla vám: ,Jest to kouzelnictví?\' Neb špatně povede se kouzelníkům.“'),
(1442, 10, 78, 'I řekli: „Zdaž přišel\'s, abys odvrátil nás od toho, co viděli jsme zachovávati předky naše a aby pak vám dvěma připadlo vůdcovství v této zemi? My vám nevěříme.“'),
(1443, 10, 79, 'Řekl tehdy Farao: „Přivedte mi všechny kouzelníky učené.“'),
(1444, 10, 80, 'A když přišli kouzelníci, řekl jim Mojžíš: „Vrzte na zem, co máte k vrhání!“'),
(1445, 10, 81, 'A když byli tak učinili, řekl Mojžíš: „Bůh zajisté učiní marným kouzelnictví, jež jste ukázali, neboť Bůh zajisté nepopřeje úspěchu skutkům kazisvětů.'),
(1446, 10, 82, 'Ale Bůh potvrzuje slovy svými pravdu, i kdyby nemilé to bylo provinilým.“'),
(1447, 10, 83, 'A nikdo nevěřil Mojžíšovi, vyjma příbuzných z lidu jeho, ze strachu před útisky Faraona a předáků jeho: neboť zajisté mocným byl Farao v zemi a byl výstředníkem.'),
(1448, 10, 84, 'I řekl Mojžíš: „Lide můj, uvěřili-li jste v Boha, spoléhejte naň, jste-li oddáni do vůle jeho.“'),
(1449, 10, 85, 'Tehdy řekli: „Na Boha spoléháme: Pane náš, nevydávej nás v pokušení lidu nepravostnému!'),
(1450, 10, 86, 'A vysvoboď nás, při milosrdenství svém, od lidu nevěřícího!“'),
(1451, 10, 87, 'I vnukli jsme Mojžíšovi a bratru jeho: „Zaopatřte lidu svému domy v Egyptě a učiňte z domů svých qiblu: a zachovávejte modlitbu a hlásejte radostnou novinu věřícím.“'),
(1452, 10, 88, 'I řekl Mojžíš: „Pane náš, zajisté dal\'s Faraonovi a předním z lidu jeho lesk a bohatství života pozemského, Pane náš, aby zbloudili se stezky tvé: Pane náš, rozdrť bohatství jejich a zatvrď srdce jejich, aby neuvěřili, dokud neshlédnou trest bolestný.“'),
(1453, 10, 89, 'Řekl Bůh: „Vyslyšena jest modlitba vaše: kráčejte tudíž oba stezkou přímou a nenásledujte těch, kdož nevědí.“'),
(1454, 10, 90, 'A dali jsme synům Israele překročiti moře a pronásledoval je Farao a vojska jeho z chtivosti a nepřátelství, až dostihla ho zátopa i zvolal: „Uvěřil jsem, že není boha, kromě onoho, v nějž věří synové Israele a jsem jedním z odevzdaných do vůle jeho.“'),
(1455, 10, 91, '„Ano, teď! však odbojným byl\'s dříve a patřil\'s mezi kazisvěty.'),
(1456, 10, 92, 'Však dnes zachráníme tě v těle tvém, abys byl výstražným znamením pro ty, kdož přijdou po tobě: neb zajisté, většina lidí jest lhostejna ke znamením našim.“'),
(1457, 10, 93, 'Zaopatřili jsme pak synům Israele obývání jisté a obdařili jsme je dobrými věcmi: a počali rozcházeti se v názorech teprve poté, když přišlo jim vědění; však Pán tvůj zajisté rozsoudí mezi nimi v den zmrtvýchvstání ohledně toho, v čem se rozcházeli.'),
(1458, 10, 94, 'A jsi-li v pochybnostech ohledně toho, co seslali jsme ti, taž se těch, kdož (od dob) před tebou čtou Knihu: nyní pak sestoupila na tě pravda od Pána tvého i jistojistě nebuď z pochybovačných.'),
(1459, 10, 95, 'A nebuď jistojistě z těch, kdož lží nazývají znamení boží, abys nebyl z těch, kdož záhubě propadli.'),
(1460, 10, 96, 'V pravdě ti, nad nimiž vyplnilo se slovo Pána tvého, neuvěří,'),
(1461, 10, 97, 'i kdyby uskutečnila se před zraky jejich veškerá znamení, dokud neshlédnou trest bolestný.'),
(1462, 10, 98, 'A kdyby bylo uvěřilo kterékoliv město, byla by prospěla mu víra jeho: však stalo se tak pouze lidu JONÁŠOVU. Když uvěřil, odejmuli jsme od nich trest potupný v životě pozemském a dali jsme jim užívati ho dočasně.'),
(1463, 10, 99, 'A kdyby byl chtěl Pán tvůj, byli by uvěřili všichni, kdož na zemi jsou, vesměs: jakž tedy ty chtěl bys donucovati lidi, aby stali se věřícími?'),
(1464, 10, 100, 'Není dáno duši, aby uvěřila, leda z dopuštění božího: a uvalí Bůh hněv svůj na ty, kdož nezmoudří.'),
(1465, 10, 101, 'Rci: „Pohleďte jen, co všechno jest na nebi a na zemi!“ Však neprospějí ani znamení, ani varování, lidu nevěřícímu.'),
(1466, 10, 102, 'Zdaž očekávají co jiného, než totéž, co stalo se ve dnech oněch (národů), které zašly před nimi. Rci: „Čekejte jen: jáť zajisté s vámi vyčkávati budu!“'),
(1467, 10, 103, 'Pak zachráníme proroky své a ty, kdož uvěřili: takto závazným jest pro nás, abychom zachránili věřící.'),
(1468, 10, 104, 'Rci: „Ó lidé, jste-li v pochybnostech ohledně náboženství mého, vězte, že neklaním se tomu, čemu vy klaníte se vedle Boha, nýbrž že klaním se Bohu, který smrtí vás navštíví: a nařízeno mi bylo, abych byl z věřících,'),
(1469, 10, 105, 'a dále: ,Obrať tvář svou pevně k víře pravé a jistojistě nebuď z mnohobožců!'),
(1470, 10, 106, 'Nevzývej vedle Boha ničeho, co nemůže ti prospěti, aniž může ti přivoditi škodu: a učiníš-li tak, tehdy zajisté jsi z nepravostných.\''),
(1471, 10, 107, 'A navštíví-li tě Bůh nějakým protivenstvím, není nikoho, vyjma něho, jenž může tě ho zbaviti: a sešle-li na tě nějaké dobro, nikdo nemůže odvrátiti od tebe milost jeho, neboť on sesílá ji na koho chce ze služebníků svých: a on velký jest v odpouštění, slitovný.“'),
(1472, 10, 108, 'Rci: „Ó lidé, zajisté přišla vám pravda od Pána vašeho: kdokoli tedy chce bráti se stezkou pravou, činí tak pouze ve prospěch duše své — a kdokoli zbloudí, činí tak pouze ke škode duše své. Jáť pak zajisté nejsem nad vámi poručníkem.“'),
(1473, 10, 109, 'Následuj tedy to, co vnuknuto ti bylo a vyčkávej trpělivě, až souditi bude Bůh: a Bůh zajisté nejlepším jest ze všech soudců!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Alif. Lám. Rá. Tato Kniha, jejíž znamení byla potvrzena, pak rozdělena, přichází od Moudrého, Vševědoucího,'),
(1475, 11, 2, 'abyste nevzývali nikoho, kromě Boha; jáť pak zajisté jsem vám od něho varovatelem a věštitelem!'),
(1476, 11, 3, 'Abyste prosili za odpuštění Pána svého a obrátili se kajícně k němu: poskytne vám požitku krásného do určité lhůty a zahrne každého, jenž zaslouží si toho, milostí svou. Však odvrátíte-li se (od něho), bojím se pro vás trestu v den velký.'),
(1477, 11, 4, 'K Bohu navrátíte se všichni a on nade všemi věcmi moc má.'),
(1478, 11, 5, 'Zdaž nesnaží se (lidé) na dva záhyby složiti hrudi své, aby skryli se před ním? Však i když přikryjí se oděvem, neví Bůh nejlépe, co utajují v hrudích svých i co na venek ukazují? Onť zajisté dobře ví o tajnostech hrudí.'),
(1479, 11, 6, 'Není tvora na zemi, jehož výživa nezávisela by od Boha: a on ví o útulku jeho i o místě uložení jeho. Vše to zapsáno jest v Knize zjevné.'),
(1480, 11, 7, 'Onť jest to, který stvořil nebesa i zemi v šesti dnech: a trůn jeho byl před tím nad vodami: chtěl dříve vyzkoušeti vás, kdož vás nejlépe zachová se v činech svých. A když řekneš jim: „Zajisté vzkříšeni budete po smrti své“ tu říkají ti kdož neuvěřili: „Vskutku, toto jest jen kouzelnictví zjevné.“'),
(1481, 11, 8, 'A odložíme-li trest jejich do určité doby, jistě řeknou: „Co zdržuje jej.“ Zdaž nepřijde na ně v den, kdy nebude nikoho, jenž by odvrátil jej od nich a se všech stran obklopí je to, z čehož posměšky si byli tropili?'),
(1482, 11, 9, 'A když dáme okusiti člověku milosti své a pak odejmeme mu ji — v pravdě zoufá si a jest nevěrcem.'),
(1483, 11, 10, 'A když dáme okusiti mu přízně své po protivenství, jímž stižen byl, jistotně řekne: „Konečně zašly zlé doby ode mne“ a ihned jest rozradován a chlubivý.'),
(1484, 11, 11, 'Vyjma těch, kdož trpělivě vyčkávají a konají dobré skutky: těm dáno bude odpuštění a odměna velká.'),
(1485, 11, 12, 'Možná, že ponecháš u sebe část toho, co vnuknuto ti bylo, a budeš proto stísněn v hrudi své z obavy, aby neřekli: „Nebude-li mu seslán poklad, aneb nepřijde-li s ním anděl, (neuvěříme).“ Však ty pouze varovatelem jsi a Bůh všech věcí jest opatrovníkem.'),
(1486, 11, 13, 'Aneb řeknou-li: „Sám vymyslil jej,“ rci: „Nuže deset předveďte súr, rovných těmto, vámi vymyšlených: a zavolejte si ku pomoci, koho budete moci vedle Boha, jste-li pravdomluvnými.“'),
(1487, 11, 14, 'Však nevyslyší-li vás, vězte, že seslán byl jedině s moudrostí Boha a že není boha, kromě Něho. Jste tedy odevzdáni do vůle jeho?'),
(1488, 11, 15, 'Těch, kdož žádají si jen života pozemského a lákadel jeho, odměníme spravedlivě skutky jejich v něm a nebudou v tom zkráceni.'),
(1489, 11, 16, 'To jsou ti, pro něž v životě budoucím přichystán jest pouze oheň pekelný: niveč bude co konali na světě a nicotno vše, co činili.'),
(1490, 11, 17, 'Zdaž budou jako ti, kdož oporu hledají v jasném důkazu, od Pána svého, předčítaném jim svědkem od Boha: a před nímž přišla kniha Mojžíšova, jako vodítko a milosrdenství? Tito věří v něj: však kdožkoli ze spřeženců modloslužby nevěří v něj, nechť těší se na oheň pekelný! Nechovej tudíž nejmenší pochybnosti o něm, neboť jest pravdou od Pána tvého: však většina lidí tomu nevěří.'),
(1491, 11, 18, 'Kdo nepravostnějším jest toho, jenž o Bohu vymyslí lež? Tito předvedeni budou před Pána svého a řeknou svědci: „Tito jsou oni, kteří lhali ohledně Pána svého.“ Zdaž nepadne zlořečení Boha na nepravostné,'),
(1492, 11, 19, 'kteří odpuzují druhé od stezky boží a rádi by ji zkřivili a nevěří v život budoucí?'),
(1493, 11, 20, 'Tito neznemožní (moc Boha) na zemi a nenaleznou zastánce mimo Boha. Zdvojnásoben bude trest jejich: nedovedli ani slyšeti, ani viděti.'),
(1494, 11, 21, 'Toto jsou ti, kdož sebe samy přivedli v záhubu a (božstva), jimi vymyšlená, ztratila se jim.'),
(1495, 11, 22, 'Není pochyby o tom, že tito na tom nejhůře budou v životě budoucím.'),
(1496, 11, 23, 'Však ti, kdož uvěřili a konali dobré skutky a ponižovali se před Pánem svým, obývati budou v ráji a zůstávati v něm budou věčně.'),
(1497, 11, 24, 'Tyto dva tábory jsou jako slepí a hluší (u porovnání) s vidoucími a slyšícími: zdaž budou si rovno cenny v porovnání? Což konečně neporozumíte?'),
(1498, 11, 25, 'Kdysi poslali jsme Noema k lidu jeho i řekl jim : „Zajisté jsem vám varovatelem zjevným,'),
(1499, 11, 26, 'abyste neuctívali ničeho, kromě Boha: jáť zajisté obávám se, že trest stihne vás v den bolestný!“'),
(1500, 11, 27, 'I řekli předáci lidu jeho, kteří neuvěřili: „Nevidíme v tobě jiného, než člověka jako my sami a vidíme, že následují tě pouze nejsprostší z (lidu) našeho, ukvapení v úsudku: a nevidíme ve vás přednosti, jež by vás stavěla výše nás. Spíše mníme, že jste prolhanci.“'),
(1501, 11, 28, 'Řekl Noe: „Lide můj, co míníte? Mám-li pouze jasný důkaz od Pána svého, jenž obdařil mne milosrdenstvím svým a jste-li slepými vůči němu, zdaž vnutíme je vám, když vy se mu vzpíráte?'),
(1502, 11, 29, 'Lide můj, nežádám od vás za to bohatství: odměna má jest pouze na Bohu. A nebudu odháněti od sebe těch, kdož uvěřili; ti setkají se s Pánem svým —- však vidím, že lid jste nevědomý.'),
(1503, 11, 30, 'A lide můj, kdo pomůže mi proti Bohu, kdybych odehnal je? Což nepředložíte si to?'),
(1504, 11, 31, 'A neříkám vám: ,Vládnu pokladnicemi Boha,‘ ani: ,Znám věci skryté, aniž říkám: ,Jsem anděl,‘ aniž říkám těm, na něž úkosem pohlížíte: ,Nepopřeje jim Bůh nijakého dobra.‘ Bůh nejlépe ví, co jest v myslích jejich: a kdybych to říkal, byl bych jedním z nepravostých.“'),
(1505, 11, 32, 'Řekli: „Noe, dost již nahádal jsi se s námi a stále rozmnožuješ hádání s námi: nuže, konečně sešli na nás to, co nám slibuješ, jsi-li pravdomluvným.“'),
(1506, 11, 33, 'Řekl: „Bůh zajisté sešle to na vás bude-li chtíti, a nikterak neznemožníte mu toho.'),
(1507, 11, 34, 'Aniž prospěje vám rada má, kdybych chtěl (dobrou) dáti vám radu, však Bůh chtěl zavésti vás v blud: onť Pánem jest vaším a k němu se navrátíte.“'),
(1508, 11, 35, 'Řeknou-li ti: „Vymyslil si jej,“ rci: „Vymyslil-li jsem si jej, má bude vina: však prost jsem podílu na tom, v čem vy se proviňujete.“'),
(1509, 11, 36, 'A vnuknuto bylo Noemovi: „Zajisté nikdo jiný z lidu tvého neuvěří, vyjma těch, kdož již uvěřili: pročež nermuť se pro skutky jejich,'),
(1510, 11, 37, 'nýbrž zbuduj koráb před zraky našimi a dle vnuknutí našeho a nepřimlouvej se za nepravostné, neboť oni potopeni budou.“'),
(1511, 11, 38, 'Teda jal se budovati koráb: a pokaždé, když přední z lidu jeho šli kolem, vysmívali se mu. Řekl: „Smějte se nám jen: však i my vysmějeme se vám, jakož i vy nám se vysmíváte;'),
(1512, 11, 39, 'a zvíte pak, na koho přijde trest, jenž potupou pokryje jej: a trest stihne jej věčný.“'),
(1513, 11, 40, 'Až pak přišel rozkaz náš a praskla pec. Tehdy řekli jsme Noemovi: „Nalož na koráb,od každého druhu stvoření párek a rodinu svou, vyjma toho, nad nímž již dříve pronesen byl rozsudek, a ty, kdož uvěřili.“\' Však pouze malý byl počet těch, kteří uvěřili s ním.'),
(1514, 11, 41, 'I řekl: „Vstupte do korábu: ve jménu Boha nechť pluje a zakotví se. Zajisté Pán můj velký jest v odpouštění a milosrdný.“'),
(1515, 11, 42, 'A unášel je (koráb), pluje na vlnách horám podobných: a volal Noe na syna svého, jenž byl v povzdálí: „Synu můj, pluj s námi a nezůstávej s nevěřícími.“'),
(1516, 11, 43, 'Řekl (tento): „Uteku se na vrchol hory, jež ochrání mne před vodami.“ Řekl (Noe): „Není nikoho, jenž ochrání se dnes před rozkazem božím, vyjma toho, nad nímž smiloval se Bůh.“ Tu rozlily se mezi nimi vlny a syn jeho zůstal mezi potopenými.'),
(1517, 11, 44, 'I bylo řečeno: „Země, vpij do sebe vodu svou a nebesa, přestaňte dštíti!“ A opadla voda a vyplněn byl rozkaz: a stanul koráb na vrchu Al-Džúdí a bylo řečeno: „Daleko odsud nechť zůstane lid nepravostný!“'),
(1518, 11, 45, 'A zavolal Noe Pána svého a řekl: „Pane můj, zajisté syn můj byl z rodiny mé a zajisté slib tvůj pravdivý byl: ty pak nejspravedlivějším jsi ze všech soudců!“'),
(1519, 11, 46, 'Řekl (Bůh): „Noe, vpravdě (syn tvůj) nebyl z rodiny tvé, neboť jednání to bylo nesprávné: proto netaž se mne na věci, o nichž nemáš vědění; varuji tě, abys nebyl jedním z nevědomců.“'),
(1520, 11, 47, 'Řekl (Noe): „Pane můj, k tobě utíkám se (o prominutí), abych netázal se tě na věci, o nichž nemám vědění: a neodpustíš-li mi a nesmiluješ-li se nade mnou budu jedním ze záhubě propadlých.“'),
(1521, 11, 48, 'Bylo mu řečeno: „Noe, sestup (s korábu) s pozdravem míru od nás a s požehnáním naším nad tebou a nad národy, jež jsou s tebou: jsou pak národové, kterým poskytneme požitku (tohoto světa) a pak zasáhne je od nás trest bolestný.'),
(1522, 11, 49, 'Tato jest jedna ze zvěstí skrytých: zjevujeme ji tobě nyní: ani ty, ani lid tvůj nevěděli jste o ní před tím — i buď trpěliv, neboť blahý jest konec bohabojných.“'),
(1523, 11, 50, 'A k lidu \'Ád (poslali jsme) bratra jejich HÚDA. Řekl: „Lide můj, uctívejte Boha: nemáte jiného Boha, kromě něho: (ty druhé) si jen vymýšlíte.'),
(1524, 11, 51, 'Lide můj, nežádám za toto odměny od vás: odměna má jest pouze u toho, jenž stvořil mne, což toho nepochopíte?'),
(1525, 11, 52, 'Lide můj, proste za odpuštění Pána svého: poté kajícně obraťte se k němu: sešle vám s nebe deště hojné a rozmnoží sílu vaši nad sílu vaši nynější — pročež neodvracejte se od něho jako provinilci.“'),
(1526, 11, 53, 'Řekli: „Ó Húde, nepřinesl jsi nám jasného důkazu a my neopustíme bohů svých na (pouhé) tvoje slovo: my ti nevěříme.'),
(1527, 11, 54, 'Řekneme ti jen, že někteří z bohů našich postihli tě zlem.“ Řekl: „Zajisté béřu za svědka Boha a vy též dosvědčte, že prost jsem vašeho uctívání bohů jiných'),
(1528, 11, 55, 'vedle Boha: i započněte se svými úklady proti mně všichni a nemeškejte.'),
(1529, 11, 56, 'Jáť pak spoléhám na Boha, Pána svého a Pána vašeho: není tvora, kterého neměl by v moci své: a zajisté Pán můj kráčí stezkou přímou.'),
(1530, 11, 57, 'A odvrátíte-li se, dobře: již oznámil jsem vám to, s čím poslán jsem byl k vám: Pán můj dá jinému lidu zabrati vaše místo a nebudete moci uškoditi Bohu v nejmenším: zajisté Pán můj všech věcí jest strážcem.“'),
(1531, 11, 58, 'A když přišel rozkaz náš, zachránili jsme Húda a ty, kdož uvěřili s ním, z milosrdenství svého: a zachránili jsme je před trestem přísným.'),
(1532, 11, 59, 'Tento (lid) \'Ád zapíral znamení Pána svého a neposlušen byl proroků jeho a následoval výzvu všech mužů mocných a tvrdohlavých.'),
(1533, 11, 60, 'Pročež stiženi jsou zlořečením v tomto světě a v den zmrtvýchvstání (bude jim řečeno) : „Zdaž nebyl (kmen) \'Ád, mezi nevěřícími v Pána svého? Ano, daleko zavržen budiž (kmen) \'Ád, lid Húdův!“'),
(1534, 11, 61, 'A k lidu Tsemúd poslali jsme bratra jejich Sáliha. Řekl: „Lide můj, uctívejte Boha: nemáte jiného Boha, kromě něho. On vzniknouti dal vám ze země a dal vám ji k obývání: nuž proste jej za odpuštěni a po té obraťte se kajícně k němu: zajistéť Pán můj blízek jest a ochoten vyslyšeti (vás).“'),
(1535, 11, 62, 'Řekli: „Ó Sálihu, až dosud byl\'s mezi námi jedním, v nějž naději jsme měli: zdaž nyní zbraňovati chceš nám, abychom vzývali to, co vzývali otcové naši. A my, zajisté v pochybnostech a nejistotě jsme ohledně toho, k čemu zveš nás.“'),
(1536, 11, 63, 'Řekl: „Lide můj, co míníte? Mám-li jasný důkaz od Pána svého a popřál-li mi milosrdenství svého, kdo pomůže mi proti Bohu, kdybych byl ho neposlušen? Zvýšili byste tím jedině moji záhubu.'),
(1537, 11, 64, 'Lide můj, toto jest velbloudice boží, znamením vám. Nechte ji klidně pásti se na půdě boží a neubližujte jí, jinak stiženi budete trestem blízkým.“'),
(1538, 11, 65, 'Nicméně podřezali žíly velbloudice a řekl (Sálih): „Popřejeme vám ještě tři dny pobytu v obydlí vašich: slib tento zajisté nebude odvolán.“'),
(1539, 11, 66, 'A když naplnil se rozkaz náš, zachránili jsme Sáliha a ty, kdež uvěřili s ním, z milosrdenství svého, od potupy onoho dne: zajisté Pán tvůj silný jest a mocný.'),
(1540, 11, 67, 'A zachvátila nepravostné bouře mohutná a nalezeni jsou z rána v domech svých co ztuhlé mrtvoly,'),
(1541, 11, 68, 'jako by nikdy nebyli těšili se z nich. Zdaž nebyl (kmen) Tsemúd nevěřícím v Pána svého? Ano, daleko zavržen budiž Tsemúd!'),
(1542, 11, 69, 'A když přišli kdysi poslové naši k Ábrahamoví s radostnou zprávou, řekli: „Pokoj s tebou.“ Řekl: „Pokoj s vámi“ a nemeškaje, přinesl jim tele pečené.'),
(1543, 11, 70, 'A vida, že ruce jejich nedotýkají se (pokrmu), nerad to viděl a pocítil strach před nimi. I řekli: „Neboj se, neboť my posláni jsme k lidu Lotovu.“'),
(1544, 11, 71, 'A žena Abrahamova stála vedle a smála se: i oznámili jsme jí Isáka a po Isákovi, Jakuba.'),
(1545, 11, 72, 'Řekla: „Já nešťastná, jakž porodím syna, když stařena jsem a manžel můj stařec? To zajisté byla by věc podivuhodná.“'),
(1546, 11, 73, 'Řekli: „Divíš se snad rozkazu božímu? Milosrdenství Boha a požehnání jeho budiž s vámi, lide tohoto domu: Bůh zajisté hoden jest chvály a slávy.“'),
(1547, 11, 74, 'Když pak rozptýlil se úděs Abrahamův, a on slyšel radostnou tuto zvěst, naléhal na nás ve prospěch lidu Lotova,'),
(1548, 11, 75, 'neboť Abraham zajisté byl laskavý, soucitný, promíjející.'),
(1549, 11, 76, '„Ó Abrahame, ustaň ve svém snažení, neboť již vyšel rozkaz Pána tvého a lid onen zasáhne trest neodvratný.“'),
(1550, 11, 77, 'A když přišli poslové naši k Lotovi, zarmoutil se nad nimi a cítil se slabým k ochraně jejich, řka: „Toto zajisté den jest těžký.“'),
(1551, 11, 78, 'A přihnal se k němu lid jeho, neboť již od dřívějška dopouštěli se špatností a řekl (Lot): „Lide můj, tyto dcery mé vezmete-li, méně hanebno to bude pro vás: i bojte se Boha a nezneuctívejte mne v hostech mých. Což není mezi vámi jediného muže spravedlivého?“'),
(1552, 11, 79, 'Řekli: „Však víš již, že málo jest nám po tvých dcerách: a zajisté dobře víš, co chceme.“'),
(1553, 11, 80, 'Řekl : „Kéž bych měl dosti síly s vás, aneb kéž mohl bych se utéci k ochránci mocnému!“'),
(1554, 11, 81, 'Řekli (tehdy cizinci): „Ó Lote, myť posly jsme Pána tvého a oni nedostanou se na tebe: i vyjdi s rodinou svou ještě této noci a nechť nikdo z vás neobrací se dozadu — pouze žena tvá tak učiní a stejný stihne ji osud, jaký stihne lid onen. Zajisté hrozba tato splní se z rána: není-liž pak jitro již blízké?“'),
(1555, 11, 82, 'A když přišel rozkaz náš, obrátili jsme město vrchem dolů a dštili jsme na ně bez ustání kamení z hlíny vypálené, poznamenané Pánem tvým: a není podobný osud dalek od nepravostných!'),
(1556, 11, 83, 'A k Madianským poslali jsme bratra jejich Šu\'ejba. Řekl: „Lide můj, uctívejte Boha, nemáte jiného Boha, kromě něho: A nezkracujte míry ani váhy: zajistéť vidím vás v blahobytu, však bojím se pro vás trestu v den vše obklopující.'),
(1557, 11, 84, 'Lide můj, dodržujte poctivě míry a váhy a nešalte lidi o majetek jejich a nedopouštějte se nespravedlnosti na zemi skutky pohoršlivými.'),
(1558, 11, 85, 'Cokoliv pak přebývati vám bude z milosti boží, bude nejlepším pro vás, jste-li věřícími:'),
(1559, 11, 86, 'jáť pak zajisté nejsem nad vámi strážcem.“'),
(1560, 11, 87, 'Řekli: „Ó Šu\'ejbe, zdaž nakazují ti modlitby tvé, abychom opustili to, co uctívali bohové naši, a abychom nenakládali s majetky svými, jak sami chcemeli? Zajisté přec jseš mužem laskavým a rozšafným.“'),
(1561, 11, 88, 'Řekl: „Lide můj, co míníte? Mám-li jasný důkaz od Pána svého, jenž obdařil mne bohatými dary svými — nechci odporovati vám v tom, co (Bůh) zbraňuje vám činiti, leda proto že chci jedině polepšení vaše, seč síly mé stačí. A důvěru svou skládám pouze v Boha: na něho spoléhám a k němu obracím se.'),
(1562, 11, 89, 'Lide můj, nechť odpor váš vůči mně neuvalí na vás vinu pro kterou postihl by vás osud podobný onomu, jenž postihl lid Noemův, lid Húdův, lid Sálihův: aniž lid Lotův jest od vás (tak) vzdálen!'),
(1563, 11, 90, 'Proste za odpuštění Pána svého a obraťte se kajícně k němu: zajisté Pán můj slitovný jest a láskyplný.“'),
(1564, 11, 91, 'Řekli: „Ó Šu\'ejbe, nerozumíme mnoho z toho, co pravíš, však zajisté vidíme tě, žes slabým mezi námi: a nebýti rodu tvého, byli bychom tě ukamenovali a ty bys nebyl mohl (brániti se nám).“'),
(1565, 11, 92, 'Řekl: „Lide můj, zdaž rod můj vzácnějším jest vám Boha a Boha za záda svá bez povšimnutí chcete zahoditi? Však zajisté Pán můj obklopuje věděním svým skutky vaše.'),
(1566, 11, 93, 'Lide můj, čiňte, cokoliv můžete, jáť taktéž učiním: zvíte pak,'),
(1567, 11, 94, 'na koho přijde trest potupný proň a kdo lhářem jest: nuž dávejte pozor — já s vámi pozor budu dávati.“'),
(1568, 11, 95, 'A když přišel rozkaz náš, zachránili jsme Šu\'ejba a ty, kdož uvěřili s ním, z milosrdenství svého: a zachvátila nepravostné bouře mohutná a nalezeni jsou z rána co ztuhlé mrtvoly v domech svých,'),
(1569, 11, 96, 'jako by nikdy nebyli těšili se z nich. Zdaž nebyl daleko zavržen (lid) Madianský, jakož zavržen byl (lid kmene) Tsemúd?'),
(1570, 11, 97, 'A když poslali jsme Mojžíše se znameními svými a s mocí zjevnou k Faraonovi a předním z lidu jeho, tu následovali tito rozkaz Faraonův: však rozkaz nebyl spravedlivý.'),
(1571, 11, 98, 'Povede lid svůj v den zmrtvýchvstání a přivede je k napajedlu ohně pekelného: a děsné bude napajedlo, kde napájeti se budou!'),
(1572, 11, 99, 'Zlorečením stiženi jsou v tomto světě a v den zmrtvýchvstání děsné bude přispění, kterým přispěno jim bude!'),
(1573, 11, 100, 'Totoť jsou děje měst, jež tobě vyprávíme: některá z nich dosud stojí, jiná již skosena jsou:'),
(1574, 11, 101, 'ne my utiskli jsme je, však sebe sama utiskla a ničeho neprospěla jim božstva jejich, která uctívají vedle Boha, když přišel rozkaz Pána tvého: pouze rozmnožila zkázu jejich:'),
(1575, 11, 102, 'Takovéto jest udeření Pána tvého, když udeřuje jím města, jež byla nepravostna: zajisté udeření jeho skličující jest a bolestné.'),
(1576, 11, 103, 'V tomto zajisté znamení jest onomu, jenž bojí se trestu ve světě budoucím: totoť den bude, kdy veškeré shromážděno bude lidstvo: totoť den bude, jehož bude svědkem (veškerenstvo).'),
(1577, 11, 104, 'A neodložíme den onen, nýbrž přijde ve lhůtě předem stanovené.'),
(1578, 11, 105, 'V den příchodu jeho nepromluví nikdo, vyjma s dovolením Boha: a mezi nimi zavržení budou a blažení.'),
(1579, 11, 106, 'Pokud se týče zavržených, uvrženi budou v oheň (pekelný): a v něm vzdychání bude a naříkání.'),
(1580, 11, 107, 'V něm přebývati budou, dokud potrvají nebesa i země, leč by jinak chtěl Pán tvůj: zajisté Pán tvůj činí, co chce.'),
(1581, 11, 108, 'A pokud se týče těch, již oblaženi byli, půjdou do ráje: v něm přebývati budou, pokud potrvají nebesa i země, leč by chtěl Pán tvůj přidati jim jiný dar neodejmutelný.'),
(1582, 11, 109, 'Nebudiž nikterak v pochybnosti ohledně toho, co uctívají onino — uctívají pouze to, co uctívali otcové jejich před tím, zajisté vyplatíme jim podíl jejich, nijak nezkrácený.'),
(1583, 11, 110, 'Dali jsme Mojžíšovi Knihu a rozešli se v mínění o ní (lidé): a kdyby nebylo předešlo slovo od Pána tvého, bylo by již dávno rozhodnuto mezi nimi: oni pak (dosud) jsou ohledně Knihy v pochybnosti váhavé.'),
(1584, 11, 111, 'A v pravdě každého odmění jistotně Pán tvůj dle skutků jejich, neboť on dobře zpraven jest o tom, co činíte.'),
(1585, 11, 112, 'Pročež čiň právě, jakž nakázáno ti bylo a taktéž oni, kdož uvěřili s tebou, nechť nekonají špatností, neboť Bůh zajisté dobře pozoruje skutky vaše.'),
(1586, 11, 113, 'Nehledejte opory u těch, kdož nepravostni jsou, jinak zasáhne vás oheň pekelný a nebude vám vedle Boha zastánců a nebude vám pak pomoženo.'),
(1587, 11, 114, 'Zachovávej modlitbu na počátku a na konci dne a při příchodu noci: zajisté dobrá konání zahánějí skutky zlé. Takové jest připomenutí pamětlivým.'),
(1588, 11, 115, 'A buď trpěliv, neboť Bůh zajisté nedopustí, aby ztratila se odměna dobrodějných.'),
(1589, 11, 116, 'Zdaž nebylo mezi pokoleními, která vás předešla, lidí s trochou ctnosti takových, kteří zabraňovali špatnosti na zemi — pouze málo, které jsme pak zachránili? Však nepravostní následovali výstřednosti své a stali se provinilci.'),
(1590, 11, 117, 'A Pán tvůj nevyhlazuje měst nespravedlivě, jsou-li obyvatelé jejich lidem řádným.'),
(1591, 11, 118, 'Kdyby byl chtěl Pán tvůj, byl by učinil lidstvo národem jediným; však nepřestanou rozcházeti se v mínění,'),
(1592, 11, 119, 'vyjma těch, jimž popřál milosrdenství Pán tvůj. A k tomu stvořil je; i naplnilo se slovo Pána tvého: „Zajistéť naplním peklo Džinny a lidmi vesměs.“'),
(1593, 11, 120, 'A toto vše z dějů vyslanců našich vyprávíme ti, aby utvrzeno, tím bylo srdce tvé a aby tím přišla ti pravda a napomenutí a připomenutí věřícím.'),
(1594, 11, 121, 'A rci těm, kdož nevěří: „Jednejte podle možnosti své: my taktéž činiti budeme:'),
(1595, 11, 122, 'a vyčkávejte: my taktéž vyčkávati budeme.“'),
(1596, 11, 123, 'Bohu pak přináleží věci skryté nebes i země, a k němu vše navrací se: pročež uctívej jej a spoléhej naň: a Pán tvůj zajisté není lhostejným k tomu, co činíte.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Alif. Lám. Rá. Tato jsou znamení Knihy zjevné:'),
(1598, 12, 2, 'seslali jsme tento Korán v mluvě arabské, abyste jej pochopili.'),
(1599, 12, 3, 'Vyprávěti budeme ti jedno z nejkrásnějších vyprávění, která vnukli jsme ti v tomto Koránu, ač před tím byl\'s jedním z neznalých.'),
(1600, 12, 4, 'Když řekl JOSEF k otci svému: „Otče můj, zajisté viděl jsem jedenácte hvězd a slunce a měsíc, mně klanících se.“'),
(1601, 12, 5, 'Řekl otec: „Synáčku můj, nevyprávěj o vidění svém bratřím svým, neboť jinak úkladem o tebe ukládati budou: zajistéť Satan jest člověku nepřítelem zjevným.'),
(1602, 12, 6, 'A takovýmto způsobem vyvolí tě Pán tvůj a naučí tě výkladu událostí a dovrší milosrdenství své na tobě a na rodu Jakubově, jakož kdysi dovršil je na obou předcích tvých, Abrahamu a Isákovi: zajisté Pán tvůj vševědoucí jest a moudrý.“'),
(1603, 12, 7, 'V případe pak Josefa a bratří jeho znamení byla tazatelům:'),
(1604, 12, 8, 'když řekli (bratří jeho): „Zajisté Josef a bratr jeho milejší jsou otci našemu nás, ač jedna jsme tlupa: dojista otec nás jest v bludu zjevném.'),
(1605, 12, 9, 'Zabte Josefa, aneb zažeňte jej do nějaké země a zůstane pouze pro vás tvář otce vašeho: poté pak povedete život lidí spravedlivých.'),
(1606, 12, 10, 'Řekl jeden z nich: „Nezabíjejte Josefa, nýbrž uvrcte jej do hlubin studna: vysvobodí jej z ní nějací pocestní, tak-li učiníte.“'),
(1607, 12, 11, 'Řekli: „Otče náš, proč nesvěříš nám Josefa, ač jistě upřímně to s ním myslíme?'),
(1608, 12, 12, 'Pošli jej zítra ráno s námi, ať popase si stáda a pohraje si: my bedlivě budeme jej střežiti.“'),
(1609, 12, 13, 'Řekl Jakub: „Zajisté zarmoutí mne, odvedete-li mi jej a bojím se, že sežere jej vlk, když pozor naň nedáte.“'),
(1610, 12, 14, 'Řekli: „Kdyby sežrati měl jej vlk, když jedna jsme tlupa, tu nechť záhubě propadneme!“'),
(1611, 12, 15, 'A když odvedli jej, usnesli se vespolek, že uvrhnou jej do hlubin studnice; tehdy vnukli jsme mu: „Zajisté jednou povíš jim o tomto skutku jejich a oni nebudou věděti, oč jde.“'),
(1612, 12, 16, 'I přišli s večerem k otci svému plačky,'),
(1613, 12, 17, 'řkouce: „Otče nás, vzdálili jsme se poněkud, běžíce o závod, a ponechali jsme Josefa u šatstva našeho: i sežral jej vlk. Však ty neuvěříš nám i kdybychom pravdu mluvili.“'),
(1614, 12, 18, 'Na to přinesli košili Josefovu, potřísněnou falešnou krví. Řekl Jakub: „Nikoliv, sami jste tuto věc navlekli, však trpělivost krásná jest a k Bohu nutno utéci se (o útěchu), abych snésti mohl zprávu vaši.“'),
(1615, 12, 19, 'A přišli pocestní a poslali čerpače vody ke studnici: a když spustili okov svůj dolů, zvolali: „Jaké štěstí! Ve studnici jest mládec!“ A vzali jej do zajetí, jakožto zboží: a Bůh věděl o tom, co činili.'),
(1616, 12, 20, 'A prodali jej za cenu nepatrnou, za několik penízů spočtěných, chtějíce se ho zbaviti.'),
(1617, 12, 21, 'A řekl onen, jenž koupil jej, jistý Egypťan, k manželce své: „Zacházej s ním ctně a pohostinně: může nám býti prospěšným, anebo můžeme přijmouti jej za syna.“ Takovýmto způsobem usadili jsme Josefa v zemi a naučili jsme jej výkladu událostí: Bůh vítězným jest ve vyplnění účelů svých, však většina lidí není si toho vědoma.'),
(1618, 12, 22, 'A když dospěl věku síly své, obdařili jsme jej soudností a věděním: takovýmto způsobem odměnujeme ty, kdož dobře činí.'),
(1619, 12, 23, 'A pocítila vášeň k němu paní domu, v němž nalézal se. I zavřela (všechny) dvéře a řekla: „Pojď sem.“ Řekl: „Uchovejž Bůh: zajisté Pán můj poskytl mi nejkrásnější přebývání a nedobře vede se nepravostným.“'),
(1620, 12, 24, 'Však ona mocně po něm toužila a i on sám byl by podlehl touze, kdyby nebylo bývalo důkazu od Pána jeho. Takovým způsobem odvrátili jsme od něho nepravost a smilstvo, ježto byl ze služebníků našich upřímných.'),
(1621, 12, 25, 'Oba vrhli se o závod ke dveřím a roztrhla košili jeho ze zadu: u dveří pak setkali se s pánem jejím. Řekla: „Co odměnou má býti tomu, jenž chtěl prohřešiti se na tvé rodině, leda uvěznění, aneb trest bolestný?“'),
(1622, 12, 26, 'Řekl Josef: „Ona vášní svou mne sváděla.“ A dosvědčoval svědek z rodiny její: „Je-li košile roztržena zpředu, mluvila pravdu ona a on jest lhářem,'),
(1623, 12, 27, 'však je-li košile jeho roztržena ze zadu, lhala ona a on jest pravdomluvným.“'),
(1624, 12, 28, 'A když viděl (manžel), že košile Josefova roztržena jest ze zadu, řekl: „Toto zajisté jest jedna ze lstí vašich, neboť lest vaše zajisté jest velká!'),
(1625, 12, 29, 'Josefe, nedbej na to pranic a ty, (ženo), pros za odpuštění viny své, neboť byla\'s jednou z hřešících.“'),
(1626, 12, 30, 'A rokovaly ženy v městě: „Žena knížete svádí z vášně mládce svého, neboť rozplamenil ji láskou šílenou: my zajisté shledáváme, že jest v poblouzení zjevném.“'),
(1627, 12, 31, 'Když pak doslechla se o lstivých klepech jejich, poslala pro ně a schystala jim hostinu: davši pak každé z nich nůž, zvolala: „Ukaž se jim, (Josefe.)“ A když spatřily jej, byly jím nadšeny tak, že pořezaly si ruce a zvolaly: „Bůh uchovej! To není člověk: toť sám anděl jest ušlechtilý!“'),
(1628, 12, 32, 'I řekla jim: „Toto jest mládec, pro kterého hanily jste mne: jej sváděla jsem z vášně své, však vzepřel se: a neučiní-li, co poručím mu, jistotně uvězněn bude a uvržen mezi zbědované.“'),
(1629, 12, 33, 'Řekl (Josef): „Pane můj, milejší jest mi vězení než to, k čemu mne vyzývají, však neodvrátíš-li ode mne lest jejich, přemůže mne mládí mé a budu jedním z nerozumných.“'),
(1630, 12, 34, 'A vyslyšel jej Pán jeho a odvrátil od něho lest jejich, neboť on zajisté vše slyší a ví.'),
(1631, 12, 35, 'Poté vsak uznali, ač viděli znamení (neviny jeho), že uvězní jej na nějaký čas.'),
(1632, 12, 36, 'A spolu s ním vešli do vězení dva mladíci. Řekl jeden z nich: „V pravdě zdálo se mi (ve snu), že tlačil jsem víno (z hroznů).“ A řekl druhý: „Mně pak zdálo se, že nesl jsem na hlavě své chléb a ptáci pojídali z něho. Oznam nám výklad toho, neboť vidíme, žes jedním z dobře činících.“'),
(1633, 12, 37, 'Řekl (Josef): „Dříve než přinesen bude vám pokrm, z něhož by pojedli jste, oznámím vám výklad snů vašich před tím, než naplní se. Toto jest, co mezi jiným naučil mne Pán můj, neboť opustil jsem víru lidu nevěřícího v Boha a lidu, jenž život posmrtný popíral.'),
(1634, 12, 38, 'A následoval jsem víru otců svých, Abrahama, Isáka a Jakuba. Není (ve víře naší), abychom spolčovali co s Bohem: totoť milost jest od Boha nám a (všem) lidem však většina lidí není za to vděčna.'),
(1635, 12, 39, 'Ó soudruhové moji dva ve vězení: zdaž páni rozliční lepší jsou, anebo Bůh jediný, všemocný?'),
(1636, 12, 40, 'Co vy uctíváte vedle něho jsou jen jména, která jste dali vy a otcové vaši; Bůh pak neseslal vám ohledně jmen těch nijaké moci. Však rozsouzení přináleží jedině Bohu: on přikázal, abyste uctívali pouze jeho. Toto jest víra pravá, však většina lidí není si toho vědoma.'),
(1637, 12, 41, 'Ó soudruhové moji dva ve vězení: pokud se týče jednoho z vás, nalije pánu svému (do číše, vína a pokud se týče druhého, ukřižován bude a ptáci klovati budou hlavu jeho. (Neodvolatelně) usouzena jest věc, na kterou vy dva tážete se.“'),
(1638, 12, 42, 'A řekl (Josef) k tomu, o němž myslil, že zachráněn bude z nich dvou: „Zmiň se o mně pánu svému.“ Však Satan způsobil že tento zapomněl zmíniti se pánu svému (o Josefovi), i zůstal ve vězení po několik let.'),
(1639, 12, 43, 'Až (jednoho dne) řekl král: „Zajisté viděl jsem (ve snu) jak sedm krav tučných sežráno bylo sedmi kravami hubenými a sedm klasů zelených a sedm zvadlých. Vy, přední mužové země, vyložte mi vidění mé, jste-li s výkladem vidění obeznámeni.“'),
(1640, 12, 44, 'Řekli: „Spletené sny to jsou a my ve výkladu snů se nevyznáme.“'),
(1641, 12, 45, 'Tehdy řekl onen jenž zachránil se z obou vězňů, a rozpomněl se po létech: „Já povím vám výklad toho, tedy pošlete mne.“'),
(1642, 12, 46, '„Josefe, muži pravdomluvný, vylož nám, co značí sedm krav tučných, sežraných sedmi hubenými, a sedm klasů zelených a sedm zvadlých, abych mohl vrátiti se k lidu a aby mohli o tom býti zpraveni.“'),
(1643, 12, 47, 'Řekl (Josef): „Síti budete po sedm let dle obyčeje svého: obilí pak, jež sklidíte, ponechávejte v klasu, vyjma onoho málo, z něhož jísti budete.'),
(1644, 12, 48, 'Poté přijde v zápětí sedm let trapných, jež ztráví vše, co nashromáždili jste pro ně předem, vyjma onoho mála, jež pečlivě jste uschovali.'),
(1645, 12, 49, 'Na to přijde v zápětí rok, v němž lid bude míti mnoho dešťů a v něm tlačiti budete (hrozny).“'),
(1646, 12, 50, 'I řekl král: „Přiveďte jej ke mne.“ Však když přišel posel k Josefovi, řekl tento: „Vrať se k pánu svému a otaž se jej, jaké zámysly měly ženy, jež pořezaly si ruce: zajisté Pán můj dobře zná léčky jejich.“'),
(1647, 12, 51, 'Tehdy řekl (král k ženám): „Jak bylo to s vaším přemlouváním, když sváděly jste Josefa vášní svou?“ Řekly: „Uchovejž Bůh, že bychom věděly o něm co zlého!“ A řekla žena knížete: „Nyní vyšla pravda najevo: já sama sváděla jsem jej vášní svou a on zajisté byl, pravdomluvný.“'),
(1648, 12, 52, 'Řekl (Josef): „Z tohoto všeho nechť pozná (pán můj), že nezradil jsem jej v nepřítomnosti jeho a že Bůh nevede k dobrému konci lest proradných.'),
(1649, 12, 53, 'Však neprohlašuji se prostým viny, neboť přirozenost člověka nabádá ke zlému, vyjma toho, k čemu byl milostiv Pán můj: neboť zajisté Pán můj velký jest v odpouštění, slitovný.“'),
(1650, 12, 54, 'I řekl král: „Přiveďte jej ke mně: vymiňuji si jej zvláště pro sebe.“ A když byl pohovořil si s ním, řekl: „ode dneška budeš u nás, pověřen mocí a důvěrou.“'),
(1651, 12, 55, 'Řekl (Josef): „Učiň mne vrchním nad sýpkami v zemi: jáť zajisté budu strážcem rozšafným.“'),
(1652, 12, 56, 'Takovýmto způsobem usadili jsme Josefa v zemi, aby pobývati mohl v ní, kdekoli chtěl by: podělujeme milosrdenstvím svým, koho chceme a nedáme propadnouti odměně těch, kdož dobře činí.'),
(1653, 12, 57, 'Však odměna života budoucího lepší jest těm, kdož uvěřili a byli bohabojní.'),
(1654, 12, 58, 'A přišli bratři Josefovi a byli mu předvedeni: on poznal je, však oni nepoznávali ho.'),
(1655, 12, 59, 'A když je byl zásobil zásobami jejich, řekl: „Přivedte mi bratra svého, jenž zůstal s otcem vaším. Nevidíte-liž, jak dobrou vám dávám míru a že nejlepším jsem z hostitelů?'),
(1656, 12, 60, 'Však nepřivedete-li mi jej, nebude vám míry u mne, aniž smíte se mi přiblížiti.“'),
(1657, 12, 61, 'Řekli: „Budeme žádati oň otce jeho: zajisté tak učiníme.“'),
(1658, 12, 62, 'Řekl tehdy Josef k sluhům svým: „Položte peníze jejich do zavazadel jejich: aby zpozorovali je až navrátí se k lidu svému a snad přijdou sem zpět.“'),
(1659, 12, 63, 'A když navrátili se k otci svému, řekli: „Otče náš, odepřena nám byla míra: pročež pošli s námi bratra našeho, aby naměřeno nám bylo (obilí). My zajisté dobře stříci ho budeme.“'),
(1660, 12, 64, 'Řekl: „Svěřiti-li mám jej vám, jako kdysi svěřil jsem vám bratra vašeho? Však Bůh nejlepším jest strážcem a nejslitovnějších všech slitovných.“'),
(1661, 12, 65, 'A když otevřeli náklady své nalezli peníze své vrácené jim. Řekli: „Otče náš, čehož více žádati si můžeme? Totoť jsou peníze naše, jež vráceny nám byly: i nakoupíme zásoby pro rodiny své: stříci budeme bratra svého a rozmnoženy budou zásoby naše o náklad jednoho velblouda: náklad lehký.“'),
(1662, 12, 66, 'Řekl (Jakub): „Nepošlu jej s vámi, dokud nezapřisáhnete se mi Bohem, že jistotně přivedete mi jej zpět, leda byste byli obklopeni (překážkami).“ A když dali mu slib svůj, řekl: „Bůh jest toho, co říkáme, poručníkem!“'),
(1663, 12, 67, 'A řekl pak: „Synové moji, nevstupujte do města jednou branou, nýbrž vstupte doň branami rozličnými: nicméně neprospěje vám rada má proti usouzení Boha pranic neboť rozsouzení přináleží jedině Bohu: naň spoléhám a naň nechť spoléhají ti, kdož na něho spoléhají.“'),
(1664, 12, 68, 'A když vešli do města jakž nakázal jim otec jejich, nezpomohlo jim to nijak proti předurčení Boha: jen (vyplněno tím bylo) přání v duši Jakubově, jež uložil jim. Onť pak obdařen byl věděním, jemuž jsme jej byli naučili: však většina lidí nemá vědění (takového).'),
(1665, 12, 69, 'A když předstoupili před Josefa, vzal tento k sobě bratra svého, řka: „Jáť zajisté jsem bratrem tvým: však ty nermuť se pro to, co oni se mnou učinili.“'),
(1666, 12, 70, 'A když zásobil je zásobami jejich, vložil číši svou do nákladu bratra svého. Na to zavolal vyvolavač za nimi: „Hola, pocestní! V pravdě zloději jste!“'),
(1667, 12, 71, 'Řekli, obrátivše se k nim: „Co pohřešujete?“'),
(1668, 12, 72, 'Řekli: „Pohřešujeme číši královu. Kdokoli přinese ji, dostane náklad velblouda odměnou: jáť zajisté za to ručím!“'),
(1669, 12, 73, 'Řekli: „Při Bohu! Víte přec, že nepřišli jsme, abychom špatnosti páchali v zemi: zajisté nejsme zloději.“'),
(1670, 12, 74, 'Řekli (Egypťané): „Jaká má býti odplata toho, jenž ukradl číši, jste-li lháři?“'),
(1671, 12, 75, 'Řekli: „Odplatou toho, v jehož nákladu nalezena bude (číše), bude vydání jej samého vám: takovým způsobem odměňujeme nepravostné.“'),
(1672, 12, 76, 'A počal (Josef) prohledávati pytle jejich dříve, než počal s pytlem bratrovým: a pak vytáhl číši z pytle bratra svého.Takovýmto způsobem vnukli jsme lest Josefovi, neboť nemohl zadržeti bratra svého dle zákona králova, leda že Bůh by tomu byl chtěl. Stavíme na různé stupně moudrosti, koho chceme: a nade všemi, obdařenými věděním, jest vždy jeden, jenž více ví.'),
(1673, 12, 77, 'Tehdy řekli: „Kradl-li, však již bratr jeho kradl před ním.“ Však Josef zadržel tajemství své v sobě a nezjevil je jim, řka (v duchu): „Vy v horším jste postavení a Bůh nejlépe ví o tom, co vyprávíte.“'),
(1674, 12, 78, 'Řekli: „Ó kníže mocný, bratr náš má otce stařičkého věkem: vezmi tedy jednoho z nás za něho : v pravdě vidíme, žes jedním z dobře činících.“'),
(1675, 12, 79, 'Řekl: „Uchovejž Bůh, abych vzal někoho jiného kromě toho, u nějž nalezli jsme majetek svůj: v tom případě, byli bychom z nepravostných.“'),
(1676, 12, 80, 'A když zoufali si ohledně (Benjamina), vzdálili se k poradě. Řekl nejstarší z nich: „Zdaž nevíte, že otec váš vzal od vás slib před Bohem a jak před tím nedostáli jste slibu ohledně Josefa? Jáť neodejdu ze země, dokud nedovolí mi otec můj, nebo Bůh rozhodne o osudu mém: onť nejlepším jest ze všech soudců.'),
(1677, 12, 81, 'Navraťte se k otci svému a rcete: „Otče náš, zajisté syn tvůj kradl: a dosvědčili jsme jen to, co zvěděli jsme a nemohli jsme před věcmi skrytými ostříci se.'),
(1678, 12, 82, 'Taž se sám v městě, v němž jsme byli a karavany, s níž jsme přišli: shledáš, že jsme pravdomluvnými!“'),
(1679, 12, 83, 'Řekl (Jakub): „Nikoliv, sami jste tuto věc navlekli, však trpělivost krásná jest: snad Bůh dá mi je zpět všechny, neboť on vševědoucí jest a moudrý.“'),
(1680, 12, 84, 'A odvrátil se od nich a řekl: „Běda mi pro Josefa!“ A zbělely oči jeho ze zármutku a zmlkl (bolem).'),
(1681, 12, 85, 'Řekli: „Při Bohu, nepřestaneš vzpomínati na Josefa, až přemůže tě zármutek a utrápíš se.“'),
(1682, 12, 86, 'Řekl: „Stěžuji si v bolu a zármutku svém jedině Bohu a vím od Boha, co vy nevíte.'),
(1683, 12, 87, 'Synové moji, jděte a vyzvídejte zprávy o Josefovi a bratru jeho a neztrácejte naděje v dobrotu boží, neboť neztrácí naději v dobrotu boží, leda lid nevěřící.“'),
(1684, 12, 88, 'Když Pak předstoupili před (Josefa), řekli: „Mocný kníže, protivenství postihlo nás a rodinu naši; a málo přinesli jsme peněz: i naměř nám dobrou míru a ustrň se nad námi, neboť Bůh zajisté odměňuje ty, kdož ustrnují se.“'),
(1685, 12, 89, 'Tehdy řekl: „Víte-liž pak, co učinili jste s Josefem a bratrem jeho v nevědomosti své?“'),
(1686, 12, 90, 'Řekli: „Zdaž ty sám jsi Josef?“ Řekl: „Ano, já jsem Josef a toto jest bratr můj. Bůh milostiv byl k nám, neboť kdo bojí se jej a trpělivě vyčkává, věziž, že Bůh nedá propadnout odměně, dobře činících.“'),
(1687, 12, 91, 'Řekli: „Při Bohu, Bůh zajisté vyvolil tě nad nás a my byli jsme hříšní.“'),
(1688, 12, 92, 'Řekl: „Nebudu vám ničeho vyčítati dnes: Bůh odpustí vám, neboť on nejslitovnějším jest ze všech slitovných.'),
(1689, 12, 93, 'Vezměte tuto košili mou a hoďte ji na tvář otce mého i prohlédne: a přiveďte ke mně rodinu vaši veškerou.“'),
(1690, 12, 94, 'A když vyšla karavana, řekl otec jejich: „V pravdě poznávám vůni Josefa: myslíte třeba, že blouzním?“'),
(1691, 12, 95, 'Řekli mu: „Při Bohu, jsi opět ve svém omylu dávném.“'),
(1692, 12, 96, 'Když pak přišel posel s radostnou novinou, hodil košili na tvář jeho, i navrácen jest mu zrak. Řekl tehdy: „Zdaž neřekl jsem vám, že vím od Boha, co vy nevíte?“'),
(1693, 12, 97, 'Řekli: „Otče náš, pros za nás, aby odpuštěny nám byly viny naše neboť zajité byli jsme z hřešících.“'),
(1694, 12, 98, 'Řekl: „Budu prositi za odpuštění pro vás Pána svého, neboť on zajisté velký jest v odpouštění, slitovný.“'),
(1695, 12, 99, 'A když předstoupili před Josefa, vzal tento rodiče své k sobě, řka: „Vejděte do Egypta, chce-li Bůh, v bezpečí!“'),
(1696, 12, 100, 'Vzal pak rodiče své nahoru na stolec knížecí a padli na tváře své, klaníce se mu. I řekl: „Otče můj, toto jest výklad vidění mého kdysi: uskutečnil je Pán můj a milostí svou zahrnul mne, vysvobodiv mne z vězení a přiveda vás ke mně z pouště poté, když Satan byl způsobil různici mezi mnou a bratry mými. Zajisté Pán můj laskav jest vůči komu chce: neboť on vševědoucí jest a moudrý.'),
(1697, 12, 101, 'Pane můj, obdařil\'s mne panstvím a naučil\'s mne výkladu událostí: Stvořiteli nebes i země, tys ochráncem mým v životě tomto i budoucím: dej zemříti mi oddánu do vůle tvé a zařaď mne mezi spravedlivé.“'),
(1698, 12, 102, 'Tento jest jeden z dějů skrytých, jejž vnukáme ti. Tys nebyl přítomen, když usmyslili si vespolek čin svůj a prováděli lest svou:'),
(1699, 12, 103, 'však většina lidí, i kdybys sebe více toho si přál, neuvěří.'),
(1700, 12, 104, 'Ty pak nežádáš od nich odměny za toto (předříkávání), neboť jest pouze připomenutím veškerenstvu.'),
(1701, 12, 105, 'Kolik znamení na nebi jest a na zemi: jdou kolem nich a netečně vzdalují se od nich!'),
(1702, 12, 106, 'A většina jich nevěří v Boha, aniž by jiná s ním spolčovali (božstva).'),
(1703, 12, 107, 'Jsou si snad jisti, že nepřijde na ně záplava trestu božího, aneb nepřijde jim hodina (soudu) z nenadání, když toho tušiti nebudou?'),
(1704, 12, 108, 'Rci: „Toto jest stezka má: volám vás k Bohu jasným důkazem — já a kdožkoli následuje mne: a chvála budiž Bohu, nepatřím k mnohobožcům!“'),
(1705, 12, 109, 'A neposlali jsme před tebou jiných mužů, než takové z obyvatel měst, kterým dali jsme vnuknutí: což necestovali zemí, aby viděli, jaký byl konec těch, kteří byli před nimi? Ale příbytek v životě budoucím lepším bude pro ty, kdož bojí se Boha: což nepochopíte?'),
(1706, 12, 110, 'Až když na konec proroci ztratili veškerou naději a mnili, že považováni jsou za lháře, přišla jim pomoc naše: a zachráněn byl, koho jsme chtěli: však nebude odvrácena pomsta naše od lidu provinilého.'),
(1707, 12, 111, 'Zajisté v dějích jejich plno jest příkladů pro lidi přemýšlivé: nejsou to vyprávění smyšlená, ale potvrzení toho, co již bylo zjeveno, a vysvětlení všeho, a vedení a milosrdenství pro lid věřící.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Alif. Lám. Mím. Rá. Tato jsou znamení Knihy; a co sesláno bylo ti Pánem tvým, jest pravda: však většina lidí (tomu) nevěří.'),
(1709, 13, 2, 'Bůh to jest, jenž vybudoval výsosti nebes bez viditelných (vám) pilířů: pak vystoupil na trůn a podrobil (zákonům) slunce i měsíc: oba sledují běh svůj do lhůty předurčené. Onť řídí všechno a vysvětluje vám znamení svá, abyste pevně uvěřili v setkám s Pánem svým.'),
(1710, 13, 3, 'Onť rozprostřel zemi a vytvořil na ní temena hor a řeky: od každého plodu učinil na ní pohlaví dvé: způsobuje, aby noc zahalovala den: zajisté v tom všem jsou znamení lidu přemýšlivému.'),
(1711, 13, 4, 'A na zemi jedna s druhou sousedí různé části její: zahrady vinné révy a obilí a stromy palmové, (rostoucí) ojediněle, neb v hromadě. Zavlažovány jsou jednou vodou, však některé z nich činíme lepšími druhých k jídlu: zajisté v tom všem znamení jsou lidu chápajícímu.'),
(1712, 13, 5, 'Může-li tě co udiviti, jistě udivitelným jest rčení jejich: „Možno-li jest, abychom — proměněni v prach — stali se stvořením novým?“ To jsou ti, kdož nevěří v Pána svého: řetězy (vrženy) budou na šíje jejich a údělem jim bude oheň pekelný, v němž přebývati budou věčně.'),
(1713, 13, 6, 'Budou tě vyzývati, abys uspíšil zlo raději než dobro, a podobné případy staly se již před tím. Zajisté Pán tvůj nejvýš promíjející jest vůči lidstvu při vší nepravostnosti jejich, však též přísný jest v trestání.'),
(1714, 13, 7, 'Dí t, kdož neuvěřili: „Nebude-li sesláno mu znamení od Pána jeho, (neuvěříme).“ Ale tys pouze varovatelem a každému národu dán jest (jeho) vůdce.'),
(1715, 13, 8, 'Bůh ví o plodech v lůnech veškerého ženského pohlaví a o kolik zúžuje nebo šíří se lůno jejich: všechny věci u něho dle míry jsou.'),
(1716, 13, 9, 'Onť zná věci skryté i zjevné — on Velký Nejvyšší!'),
(1717, 13, 10, 'Stejným jest každý z vás, ať tají řeč svou, anebo mluví otevřeně: ať skrývá se v noci, anebo vychází ven za dne:'),
(1718, 13, 11, 'každý má stále střídající se anděly před sebou a za sebou, kteří střeží jej z rozkazu božího: Bůh zajisté nezmění se vůči lidem, dokud oni sami nezmění podstaty své a když Bůh chce navštíviti lid zlem, nikdo nemůže je odvrátiti, aniž mají vedle Boha jiného ochránce.'),
(1719, 13, 12, 'On to jest, který ukazuje vám blesk pro strach a naději on dává tvořiti se mrakům, deštěm obtěžkaným.'),
(1720, 13, 13, 'A vyvyšuje jej HROM ve chvále jeho, jakož i andělé z bázně před ním: a vysílá šlehy bleskem, jimiž bije, v koho chce, mezitím co oni hádku vedou o (jsoucnosti) Boha; a on mohutným jest v moci své.'),
(1721, 13, 14, 'Jemu jen přísluší vzývání právem a ti, kdož vzývají jiná (božstva) vedle něho, nebudou vyslyšeni v ničem, leda jak onen, jenž vztahuje ruce své v přehršli k vodě, žádaje si, aby dostoupila úst jeho, však nedostoupí jí nikdy: tak i volání nevěřících jen v bludu jest.'),
(1722, 13, 15, 'Před Bohem sklání se (na tvář) cožkoliv na nebi jest i na zemi, ať rádo či nerado: ba i stíny všeho z jitra a s večerem!'),
(1723, 13, 16, 'Rci: „Kdo Pánem jest nebes a země?“ Rci: „Bůh!“ Rci: „A vy že vzali jste si vedle něho zastánce, kteří nemají moci sobě samým prospěti neb uškoditi?“ Rci: „Rovnými-liž jsou si, slepí a vidomí, neb stejnými-liž jsou temnoty a světlo? Budou-liž spolčovati s Bohem božstva, jež by byla tvořila, jako Bůh tvořil, takže tvoření obou zdají se jim býti podobna?“ Rci: „Bůh jedině jest Stvořitelem všeho a on jediný jest, vítězný.'),
(1724, 13, 17, 'On sesílá s nebe vodu i proudí bystřiny svým vyměřenýn tokem a unáší proud s sebou pěnu plující na povrchu. A z toho, co taví lidé za účelem docílení ozdob aneb nástrojů, vylučuje se pěna podobná. Takovýmto způsobem vysvětluje Bůh rozdíl mezi pravdou a nepravdou: pokud se týče pěny, zmizí rychle: a pokud se týče toho, co užitečno jest lidem, zůstane na zemi. Takovýmto způsobem dává Bůh podobenství.'),
(1725, 13, 18, 'Těm, kdož poslechnou výzvu Pána svého, dostane se krásné odměny, však těm, kdož neposlechnou, i kdyby měli vše, co na zemi jest a ještě jednou tolik, nebude lze vykoupiti se tím vším: těmto zlého dostane se súčtování a obydlím jich bude peklo: a špatné bude to místo odpočinku!'),
(1726, 13, 19, 'Zdaž tedy onen, jenž ví, že to, co sesláno ti bylo, jest pravdou, zachová se jako slepý? Toto přec rozmyslí si lidé rozumní,'),
(1727, 13, 20, 'kteří věrně dostávají smlouvě své s Bohem a neporušují úmluvy své:'),
(1728, 13, 21, 'a kteří spojují to, co Bůh nařídil, aby bylo spojeno a bojí se Pána svého a chvějí se bázní před zlým súčtováním:'),
(1729, 13, 22, 'a kteří trpělivi jsou, z touhy po (spatření) tváře boží a zachovávají modlitbu a dávají štědře z toho, čím obdařili jsme je, v tajnosti i veřejně: a odpuzují dobrými skutky špatnosti své: těmto připraven jest příbytek poslední:'),
(1730, 13, 23, 'zahrady Edenu: — vejdou do ní oni a ti, kdož spravedlivými byli z otců jejich a manželek jejich a potomků jejich: a andělé vyjdou jim tam vstříc z každé brány:'),
(1731, 13, 24, '„Pokoj s vámi“ (řeknou), „za to, že trpělivě jste (vše) přečkali!“ Jak krásný bude tento příbytek poslední!'),
(1732, 13, 25, 'Však ti, kdož porušují smlouvu s Bohem poté, když se jí byli zavázali a přetínají to, co Bůh nakázal, aby spojeno bylo a nepravost páší v zemi: nad těmito bude zlořečení a schystán jest jim děsný dům.'),
(1733, 13, 26, 'Bůh hojně dává dary své, komu chce, anebo odměřuje je. (Lidé) radují se ze života pozemského: však život pozemský, u porovnání s (životem) budoucím, jest pouze (dočasný) požitek.'),
(1734, 13, 27, 'A ti, kdož neuvěřili, praví: „Nebude-li sesláno mu znamení od Pána jeho, (neuvěříme).“ Rci: „Zajisté Bůh zavádí v blud, koho chce, a vede k sobě ty, kdož obracejí se k němu:'),
(1735, 13, 28, 'kdož uvěřili a jichž srdce upokojena jsou ve vzpomnění na Boha. Jakž jinak, než vzpomněním na Boha, upokojena mohou býti srdce?'),
(1736, 13, 29, 'Ty, kdož uvěřili a konali dobré skutky, blaženost očekává a krásné přebývání.“'),
(1737, 13, 30, 'Tak poslali jsme tě k lidu, před nímž již jiní národové byli zašli, abys předčítal jim, což vnuknuli jsme ti: však oni odpírají víru v Milosrdného. Rci: „Onť Pánem jest mým: není Boha kromě něho: na něho spoléhám a k němu kajícně obracím se.“'),
(1738, 13, 31, 'I kdyby Koránem mohly v pohyb uvedeny býti hory, nebo rozpoltěna země, nebo dána řeč mrtvým — (neuvěřili by!) Nicméně Bohu přináleží veškerá moc: a snad pochybují věřící o tom, že kdyby Bůh byl chtěl, byl by vedl lidstvo veškeré cestou pravou? A neustane stíhati nevěřící za to, co konali, úder (neštěstí): nebo snese se poblíže domu jejich, dokud nesplní se slib Boha a Bůh zajisté neustoupí od vyplnění slibů svých.'),
(1739, 13, 32, 'Smích tropili si již před tebou (lidé) z proroků: a dlouho poshověl jsem těm, kdož nevěřili: pak ztrestal jsem je. A jak (přísný) byl můj trest!'),
(1740, 13, 33, 'Zdaž ten, kdo stojí nad každou duší a přihlíží k tomu, co si vysluhuje? A přece dali Bohu společníky! Rci: „Nuž tedy jmenujte je! Chcete snad pověděti Bohu, o čem by dosud nevědět na zemi, anebo jsou to pouhé vnější (zdánlivosti) slovní?“ Nikoliv: zkrášlena byla nevěřícím tato lest jejich a daleko oddálili se od stezky pravé; a koho Bůh zavede, tomu není vůdce (k nalezení).'),
(1741, 13, 34, 'Je očekává trest v životě pozemském a trest jich v životě budoucím bolestnější bude: a nebude nikoho, jenž před Bohem by jich hájil.'),
(1742, 13, 35, 'Toto jest vypodobnění ráje, slíbeného bohabojným: zpod zahrad jeho řeky tekou, plody jejich věčné jsou a stín jejich taktéž: takováto bude (poslední) odměna bohabojným, však (poslední) odměnou nevěřících bude oheň pekelný.'),
(1743, 13, 36, 'Ti, kterým seslali jsme Knihu, radují se z toho, co sesláno ti bylo, však mezi spojenými (kmeny) jsou některé, jež popírají část toho. Rci: „Pouze nakázáno mi bylo, abych uctíval Boha jediného a nedával mu společniků: jej vzývám a k němu navrátím se.“'),
(1744, 13, 37, 'Takovýmto způsobem seslali jsme ti jej, jako zákon v jazyku arabském: a následoval-li bys choutky jejich poté, když sesláno ti bylo vědění, nebude ti před Bohem ani ochránce, ani obhajovatele.'),
(1745, 13, 38, '„A poslali jsme již před tebou proroky a dali jsme jim manželky a potomstvo: nebylo pak proroka, jenž by byl přinášel znamení, leda s dovolením božím. Každý věk pak má Knihu svou.'),
(1746, 13, 39, 'Zruší Bůh, co chce, a potvrdí, co chce: u něho nalézá se Kniha prvotní.'),
(1747, 13, 40, 'Ať již dáme ti dočkati se spatření něčeho z toho, co slibujeme jim, anebo vezmeme tě k sobě (před tím) — tobě připadá pouze hlásání, nám pak účtování.'),
(1748, 13, 41, 'Zdaž nevidí, jak vnikáme do země jejich a zmenšujeme hranice její? Bůh vynáší rozsudek a nikomu nelze zvrátiti jej: a on rychlý jest v účtování.'),
(1749, 13, 42, 'Úklady strojili ti, kdož byli před nimi, však Bohu známy jsou úklady veškeré. On ví, co zasluhuje si každá duše a seznají nevěřící, komu poslední připadne obydlí.'),
(1750, 13, 43, 'A říkají ti, kdož neuvěřili: „Nejsi vyslancem Boha.“ Rci: „Bůh postačí mi jako svědek k rozsouzení mezi mnou a vámi, jakož i ti, kdož znalost mají Knihy!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Alif. Lám. Rá. (Tato jest) Kniha, kterou seslali jsme ti, abys vyváděl lidi z temnot na světlo, z dovolení Pána jejich a vedl je ke stezce Mocného, Chvályhodného:'),
(1752, 14, 2, 'Boha, jemuž přináleží vše, což na nebi jest a na zemi: a běda nevěřícím, pro trest přísný!'),
(1753, 14, 3, 'Těm, jimž milejší jest život pozemský nad život budoucí a kteří odvracejí lid od stezky boží a přáli by si zkřiviti ji: tito zajisté v dalekém jsou bludu!'),
(1754, 14, 4, 'A neposlali jsme proroka, vyjma v jazyku lidu jeho, aby jasně mohl vysvětlovati jim své poselství: a Bůh zavádí, koho chce a vede, koho chce, neboť on mocný jest a moudrý.'),
(1755, 14, 5, 'Kdysi poslali jsme Mojžíše se znameními svými, řkouce: „Vyváděj lid svůj z temnot na světlo a připomínej jim dny Boha:“ V tomť zajisté znamení jsou pro každého, jenž trpělivě vyčkává a jest vděčný.'),
(1756, 14, 6, 'Tehdy pak řekl Mojžíš k lidu svému: „Pomněte milosti boží nad vámi, když zachránil vás z rukou lidu Faraonova, který utiskoval vás nejhoršími útrapami: a zabíjeli syny vaše a živili dcery vaše: v tom zajisté byla pro vás zkouška od Pána vašeho velká.'),
(1757, 14, 7, 'A když vyvolati dal Pán váš: „Budete-li vděčni, jistotně rozmnožím milosrdenství své vám: však nebudete-li věřiti, zajisté trest můj jest přísný.“'),
(1758, 14, 8, 'A řekl Mojžíš: „Kdybyste nevěřili i vy, i kdož na zemi jest vesměs, vězte, že Bůh jest nade vše bohatý a chvályhodný.“'),
(1759, 14, 9, 'Zdaž nedošla vás zvěst o těch, kdož byli před vámi: lidu Noemova, o kmeni \'Ád a Tsemúd a těch, kdož byli po nich, jichž děje pouze Bůh zná. Přišli k nim proroci jejich s jasnými důkazy, však oni pokládali ruce své na ústa svá, řkouce: „Nevěříme v to, s čím posláni jste byli a zajisté jsme v pochybnosti ohledně toho, k čemu zvete nás, nemalé.“'),
(1760, 14, 10, 'Řekli proroci jejich: „Zdaž pochybnosti jest o Bohu, Stvořiteli nebes i země, jenž zve vás, aby odpustil vám viny vaše a poshověl vám až do lhůty předurčené?“ Řekli: „Vždyť vy jste pouze lidé jako my: chcete odvrátiti nás od toho, co uctívali, otcové naši. Předveďte nám dříve důkaz moci zjevné.“'),
(1761, 14, 11, 'Řekli jim proroci jejich: „Ano, jsme pouze lidé jako vy, avšak Bůh popřává milosti své, komu chce ze služebníků svých a není dovoleno nám, abychom předvedli vám důkaz moci, vyjma s dovolením božím: a na Boha nechť spoléhají věřící.'),
(1762, 14, 12, 'A proč neměli bychom spoléhati na Boha, když již vedl nás ve stezkách našich: a dojista s trpělivostí sneseme útrapy vámi nám způsobené: a na Boha nechť spoléhají ti, kdož naň spoléhají.“'),
(1763, 14, 13, 'A řekli ti, kdož neuvěřili, k prorokům svým: „Dojista vypudíme vás ze země své, anebo musíte vrátiti se k náboženství našemu.“ Tehdy vnukl jim Pán jejich: „My zajisté zahladíme nepravostné,'),
(1764, 14, 14, 'a dojista učiníme vás obyvateli země po nich: tak naložíme s těmi, kdož bojí se soudné stolice mé a slibů mých.“'),
(1765, 14, 15, 'I žádali Boha o pomoc a zhynul veškerý tvrdohlavec odbojný.'),
(1766, 14, 16, 'Peklo jest v zádi jeho a napájen jest hnisem tekutým,'),
(1767, 14, 17, 'doušek po doušku: a nebude takřka s to spolknouti jej: a hroziti mu bude smrt se všech stran, však nebude moci zemříti: a v zádi jeho čekati naň bude trest přísný.'),
(1768, 14, 18, 'Podobny jsou skutky těch, kdož neuvěřili v Pána svého, popeli zasaženému silným vanem vichru v den bouřný: nebudou míti moci nad výsledky práce své: toť zajisté zbloudění jest daleké!'),
(1769, 14, 19, 'Zdaž nevidíš, že Bůh stvořil nebe i zemi v pravdě? Kdyby chtěl, mohl by dát zajíti vám a vzbuditi stvoření nové:'),
(1770, 14, 20, 'a nebylo by to Bohu těžké.'),
(1771, 14, 21, 'A předstoupí před Boha lidstvo veškeré i řeknou slabí k těm, kdož mocí pyšnili se: „V pravdě byli jsme následovníky vašimi: zdaž tedy pomůžete nám od trestu božího nějak!“ Řeknou: „Kdyby Bůh byl nás vedl (cestou pravou), byli bychom (dobře) vedli vás: nyní jedno jest nám naříkáme-li si, anebo trpělivě snášíme: neníť útočiště nám.“'),
(1772, 14, 22, 'A řekne Satan poté, když dán bude rozsudek: „Zajisté Bůh slíbil vám slib pravdivý, já též dal jsem vám slib, ale nedodržel jsem jej: a neměl jsem nijaké moci nad vámi, leda že volal jsem vás a vy odpověděli jste na volání mé: pročež neviňte mne, nýbrž viňte sebe samy. Nemohu přispěti vám, aniž vy můžete mně přispěti: odepřel jsem věřiti ve vaše spolčování mne (s Bohem) v dřívějšku: a zajisté nepravostným přichystán jest trest bolestný.“'),
(1773, 14, 23, 'A uvedeni budou ti, kdož uvěřili a konali dobré skutky, v zahrady, pod nimiž řeky tekou: v nich přebývati budou věčně z dovolení Pána svého a přivítací pozdrav jich tam bude: „Pokoj!“'),
(1774, 14, 24, 'Zdaž nevidíš, jak činí Bůh podobenství slova dobrého ke stromu dobrému, jehož kořeny pevné jsou a větve jeho k nebi zdvihají se:'),
(1775, 14, 25, 'dává plody své v každé době z dovolení Pána svého: a činí Bůh podobenství lidem, aby přemýšleli.'),
(1776, 14, 26, 'A slovo špatné podobno jest stromu špatnému, sráženému s vrchu země, nemajícímu pevného stání.'),
(1777, 14, 27, 'Utvrzuje Bůh ty, kdož uvěřili, slovem stálým v životě pozemském i budoucím a zavádí Bůh nepravostné v blud: a činí Bůh, cožkoliv chce.'),
(1778, 14, 28, 'Zdaž nevidíš těch, kdož zaměnili milost boží nevírou a uvrhli lid svůj v obydlí zatracení —'),
(1779, 14, 29, 'v peklo, kdež sžíháni budou? A špatné to bude obydlí!'),
(1780, 14, 30, 'A učinili Bohu rovných, aby zavedli lidi od stezky jeho. Rci: „Užívejte si jen, však zajisté cíl cesty vaší bude oheň (pekelný).“'),
(1781, 14, 31, 'Rci služebníkům mým, kteří uvěřili, aby zachovávali modlitbu a rozdávali stědře z toho, čímž obdařili jsme je, potajmu i veřejně, dříve než přijde na ně den, kdy nebude ani výkupu, ani (přímluvy) přátel.'),
(1782, 14, 32, 'Bůh to jest, jenž stvořil nebe i zemi a seslal vodu s nebe, a vzrůstati dal jí plodům za potravu vám, onť podrobil vám loď, aby běžela po moři z rozkazu jeho a podrobil řeky vám:'),
(1783, 14, 33, 'A podmanil vám slunce a měsíc v obíhání jejich a podmanil vám noc a den:'),
(1784, 14, 34, 'a dal vám vše, oč žádali jste jej: a kdybyste spočísti chtěli milosti Boha, nespočetli byste jich! Zajisté člověk nespravedlivý jest a věrolomný.'),
(1785, 14, 35, '(Pomni), když řekl ABRAHAM: „Pane můj, učiň tuto krajinu bezpečnou a oddal mne a syny mé od uctívání model:'),
(1786, 14, 36, 'Pane můj, ony již zavedly mnoho lidí v blud: a kdožkoli následovati mne bude, tenť bude jedním z mých a kdožkoli neposlušen mne bude — tyť zajisté velký jsi v odpouštění, slitovný!'),
(1787, 14, 37, 'Pane náš, ubytoval jsem část potomstva svého v údolí bez polností u domu tvého posvátného — Pane náš — aby zachovávali modlitbu: i učiň, aby srdce lidí láskou naklonila se jim a výživou popřej jim plodů, aby byli vděčni.'),
(1788, 14, 38, 'Pane náš, ty zajisté víš, co skrýváme a veřejně přiznáváme : a není skryto Bohu pranic na zemi i na nebi.'),
(1789, 14, 39, 'Chvála Bohu, jenž podaroval mne v stáří mém Ismaelem a Isákem: zajisté Pán můj dobře slyší volání.'),
(1790, 14, 40, 'Pane můj, učiň, abych věrně zachovával modlitbu a rovněž i potomstvo mé — Pane můj — a vyslyš volání mé: Pane náš, odpusť mně i rodičům mým i věřícím v den súčtování.“'),
(1791, 14, 41, 'A nijak nedomnívej se, že Bůh jest lhostejným k tomu co činí nepravostní: on pouze poshovuje jim do dne, kdy vytřeštěni budou zrakové;'),
(1792, 14, 42, 'spěšíce s hlavou vzhůru obrácenou, nebudou moci odvrátiti zraku svého a srdce jich prázdna budou.'),
(1793, 14, 43, 'I varuj lid přede dnem, kdy přijde jim trest a kdy říkati budou nepravostní: „Pane náš, poshov nám do lhůty nedaleké:'),
(1794, 14, 44, 'odpovíme na volání tvé a následovati budeme proroky!“ — „Což nepřisahali jste, před tím, že nebudete zahlazeni?'),
(1795, 14, 45, 'A bydlili jste dokonce v místech, kdys obydlených těmi, kdož prohřesili se na sobě samých: a jasně vysvětleno vám bylo, jak naložili jsme s nimi, a podobenství činili jsme vám.'),
(1796, 14, 46, 'Úklady své strojili, však Bůh zmůže úklady jejich, i kdyby úklady jejich byly s to, aby hnuly horstvem.'),
(1797, 14, 47, 'A nijak nedomnívej se, že Bůh nedodrží slibu svého, učiněného prorokům svým: Bůh zajisté mocný jest, pán pomsty.'),
(1798, 14, 48, 'V den, kdy zaměněna bude země jinou zemí, a nebe rovněž, a všichni předstoupí před Boha Jediného, Vítězného:'),
(1799, 14, 49, 'a spatříš provinilé v den onen seřazené k sobě v okovech:'),
(1800, 14, 50, 'oděv jich smolný a tváře jejich ohněm pokryté:'),
(1801, 14, 51, 'aby odměnil Bůh každou duši dle zásluh jejích: Bůh zajisté rychlý jest v účtování.'),
(1802, 14, 52, 'Toto jest ohlášení lidem, aby varováni jím byli, a aby věděli, že On jest zajisté Bůh Jediný a aby přemítali o tom lidé přemýšliví.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Alif. Lám. Rá. Tato znamení jsou Knihy a Čtení zjevného.'),
(1804, 15, 2, 'Častokráte přáti si budou nevěřící, aby byli z odevzdaných do vůle Boží.'),
(1805, 15, 3, 'Nech je, ať hodují a užívají a klamati dávají se nadějí: však zvědí!'),
(1806, 15, 4, 'Nezahladili jsme nikdy města, aniž by dáno bylo mu písmo povědomé.'),
(1807, 15, 5, 'Nelze nikterému národu uspíšiti lhůtu svou, aniž zpozditi ji.'),
(1808, 15, 6, 'A říkají: „Ty, jemuž sesláno bylo Napomenutí, zajisté zlým duchem jsi posedlý:'),
(1809, 15, 7, 'zdaž nebyl bys přišel k nám provázen anděly, kdybys byl pravdomluvný?“'),
(1810, 15, 8, 'Nesesíláme andělů svých, leda s právem a tehdy nebylo by již poshověno nevěřícím.'),
(1811, 15, 9, 'Zajistéť seslali jsme Napomenutí a jistě chrániti je budeme.'),
(1812, 15, 10, 'A již před tebou poslali jsme vyslance k sektám předešlých:'),
(1813, 15, 11, 'a nepřisel ani jeden prorok k nim, aniž by se mu nebyli vysmívali:'),
(1814, 15, 12, 'a na stejnou dráhu uvedeme srdce provinilců.'),
(1815, 15, 13, 'Neuvěří v ně, jakž minulá zvyklost byla předešlých:'),
(1816, 15, 14, 'i kdybychom otevřeli nad nimi bránu v nebesích a oni vzestupovali směrem k ní,'),
(1817, 15, 15, 'stále by říkali ještě: „Opojeni jsou zrakové naši, ba v pravdě lid jsme očarovaný!“'),
(1818, 15, 16, 'Ustanovili jsme na nebi znamení zvířetníku a zkrášlili jsme je pro (vzhled) pohlížejících:'),
(1819, 15, 17, 'a ochránili je před každým Satanem kamenovaným,'),
(1820, 15, 18, 'vyjma toho, jenž naslouchá pokradmu a v zápětí stižen jest šlehem plamene viditelným.'),
(1821, 15, 19, 'A rozprostřeli jsme zemi a rozházeli po ní temena hor a vzrůsti jsme dali na ní všem věcem dle zvážení:'),
(1822, 15, 20, 'a zaopatřili jsme na ní výživu vám, jakož i těm, kterých nevyživujete.'),
(1823, 15, 21, 'Není věci, jíž zásoby nebyly by v naší moci: a poskytneme je shůry jen v množství stanoveném.'),
(1824, 15, 22, 'A vyslali jsme větry zúrodňující a seslali jsme s nebe vodu, jíž napájíme vás: a vy nejste pány nádrží jejich.'),
(1825, 15, 23, 'Zajisté my jsme to, kteříž obživujeme a umrtvujeme a my (všeho) jsme dědici.'),
(1826, 15, 24, 'Zajisté znali jsme ony, kdož předešli vás a známe ty, kteří po vás přijdou.'),
(1827, 15, 25, 'V pravdě Pán tvůj shromáždí je všechny, neboť moudrý jest a vševědoucí.'),
(1828, 15, 26, 'Stvořili jsme pak člověka z hlíny, z bláta hrnčířského:'),
(1829, 15, 27, 'před ním pak stvořili jsme Džinny z ohně sžírajícího.'),
(1830, 15, 28, '(Pomni), když řekl Pán tvůj k andělům: „Jáť zajisté tvořím člověka z hlíny, z bláta hrnčířského:'),
(1831, 15, 29, 'a až vytvořím podobu jeho a vdechnu do ní ducha svého, padnete na tváře své, uctívajíce ho.“'),
(1832, 15, 30, 'I padli na tváře své andělé všichni, vesměs,'),
(1833, 15, 31, 'vyjma Iblíse jenž, odepřel připojiti se k uctívajícím.'),
(1834, 15, 32, 'Řekl (Bůh): „Ó Iblísi, proč nepřipojuješ se k uctívajícím?“'),
(1835, 15, 33, 'Řekl: „Nepadnu na tvář svou před člověkem, jejž stvořil\'s z hlíny, z bláta hrnčířského.“'),
(1836, 15, 34, 'Řekl Bůh: „Vyjdiž odsud — zajistéť kamenován jsi!'),
(1837, 15, 35, 'A dojista prokletí bude nad tebou až do dne soudného.“'),
(1838, 15, 36, 'Řekl: „Pane můj, poshov mi do dne vzkříšení jejich.“'),
(1839, 15, 37, 'Řekl (Bůh): „Budiž jedním z těch, jimž poshověno jest,'),
(1840, 15, 38, 'až do dne lhůty stanovené.“'),
(1841, 15, 39, 'Řekl: „Pane můj, za to, že\'s zlákal mne, zajisté krášlit budu lidem (pobyt) na zemi a všechny zlákám je,'),
(1842, 15, 40, 'vyjma služebníků tvojich upřímných.“'),
(1843, 15, 41, 'Řekl (Bůh): „Tato cesta, podle mne, jest správná,'),
(1844, 15, 42, 'neboť nad služebníky mými zajisté nebudeš míti moci, vyjma těch, kdož následovati budou tě, byvše zlákáni.“'),
(1845, 15, 43, 'V pravdě, peklo zaslíbeným jest místem těchto všechněch.'),
(1846, 15, 44, 'V ně sedmero vede bran a u každé brány bude z nich oddíl určitý.'),
(1847, 15, 45, 'Však bohabojní dlíti budou uprostřed zahrad rajských a pramenů:'),
(1848, 15, 46, '„Vstupte do nich v pokoji a bezpečí!“'),
(1849, 15, 47, 'Zprostili jsme nitra jejich veškeré zášti: bratrsky hověti si budou na lehátkách, pohlížejíce si tváří v tvář:'),
(1850, 15, 48, 'tam nepostihne jich nikdy únava, aniž kdy ztad vypuzeni budou.'),
(1851, 15, 49, 'Oznam služebníkům mým, že velký jsem v odpouštění, slitovný:'),
(1852, 15, 50, 'a že trest můj trest jest bolestný:'),
(1853, 15, 51, 'a vypravuj jim o hostech Abrahamových,'),
(1854, 15, 52, 'když vešli k němu a řekli: „Pokoj (s tebou)“ a řekl: „Zajisté ulekli jsme se vás.“'),
(1855, 15, 53, 'Řekli: „Nelekej se, neboť přinášíme ti zvěst, že obdařen budeš synem moudrým.“'),
(1856, 15, 54, 'Řekl: „Zdaž zvěstovati budete mi tuto novinu, když stáří dostihlo mne? Co v pravdě máte mi oznámiti?“'),
(1857, 15, 55, 'Řekli jsme ti novinu pravdivou: tudíž nebuď z těch, kdož ztrácejí naději.“'),
(1858, 15, 56, 'Řekl: „Kdož ztrácí naději v milosrdenství boží, vyjma zbloudilých?“'),
(1859, 15, 57, 'Řekl dále: „Jaké jest poslání vaše, ó poslové?“'),
(1860, 15, 58, 'Řekli: „Zajisté posláni jsme k lidu provinilému,'),
(1861, 15, 59, 'vyjma rodu Lotova, kterýž zachráníme veškerý,'),
(1862, 15, 60, 'kromě ženy jeho, jíž přisoudili jsme aby byla jednou z liknavých.“'),
(1863, 15, 61, 'A když k rodu Lotovu přišli vyslanci,'),
(1864, 15, 62, 'řekl (Lot): „Zajisté lidé jste mi neznámí.“'),
(1865, 15, 63, 'Řekli: „Ovšem, přišli jsme k tobě za účelem, o němž (lid tvůj) pochybuje:'),
(1866, 15, 64, 'přinesli jsme ti pravdu a pravdu mluvíme:'),
(1867, 15, 65, 'vyjdi tudíž s rodinou svou za temna (této) noci a následuj je vzadu: nechť pak neobrací se z vás nikdo, nýbrž kráčejte, kamž nakázáno jest vám.“'),
(1868, 15, 66, 'Dali jsme mu pak tento rozkaz neboť do posledního měl býti vyhlazen lid onen z rána.'),
(1869, 15, 67, 'I přihnal se lid města, rozradován zprávou —'),
(1870, 15, 68, 'Řekl (Lot): „Toto jsou hosté mí, pročež nezneuctívejte mne:'),
(1871, 15, 69, 'a bojte se Boha a potupou nepokrývejte mne.“'),
(1872, 15, 70, 'Řekli: „Zdaž nezapověděli jsme ti, abys (poskytoval útulku) komukoliv?“'),
(1873, 15, 71, 'Řekl: „Zde jsou dcery mé, chcete-li se (smilstva) dopouštěti.“'),
(1874, 15, 72, 'Při životě tvém, lid zajisté bloudil v opojení svém:'),
(1875, 15, 73, 'a bouře zachvátila je při slunce východu:'),
(1876, 15, 74, 'a obrátili jsme město vrchem do spodu a dštili jsme na ně kamení z hlíny vypálené:'),
(1877, 15, 75, 'zajistéť v tomto všem znamení jest pro bedlivě badající.'),
(1878, 15, 76, 'A města ona leží na cestě tam dosud zůstalé:'),
(1879, 15, 77, 'zajistéť v tomto (všem) znamení jest pro věřící.'),
(1880, 15, 78, 'Byli pak obyvatelé hvozdu též nepravostnými:'),
(1881, 15, 79, 'a pomstou svojí zasáhli jsme je a obě města jsou dojista příkladem zjevným.'),
(1882, 15, 80, 'A obyvatelé HEDŽRU lháři nazývali vyslance:'),
(1883, 15, 81, 'a předvedli jsme jim znamení naše, však odvrátili se od nich.'),
(1884, 15, 82, 'Ve skalách hor tesali si obydlí, aby byli v bezpečí,'),
(1885, 15, 83, 'však bouře zachvátila je za jitra časného,'),
(1886, 15, 84, 'a nic neprospěly jim jejich námahy.'),
(1887, 15, 85, 'Zajisté stvořili jsme nebe i země a vše, což mezi nimi jest, jedině v pravdě: hodina (soudu) pak přijde nezvratně. Promíjej tudíž promíjením krásným,'),
(1888, 15, 86, 'neboť Pán tvůj zajisté jest Stvořitelem, jest vševědoucím.'),
(1889, 15, 87, 'Již dali jsme ti sedm veršů ke stálému opakování a Korán velebný.'),
(1890, 15, 88, 'Nespočívej dlouhým pohledem na požitcích, jichž popřáli jsme několika párům z nich: nermuť se nijak pro ně, nýbrž skloň křídla svá nad věřícími.'),
(1891, 15, 89, 'A rci: „Jáť v pravdě jsem varovatelem zjevným.“'),
(1892, 15, 90, 'Seslali jsme (trest) na ty, kdož rozdělují:'),
(1893, 15, 91, 'kdož dělí Korán na části.'),
(1894, 15, 92, 'Při Pánu tvém! Jistotně budeme dotazovati se jich všechněch'),
(1895, 15, 93, 'ohledně toho, co činili.'),
(1896, 15, 94, 'Ohlašuj tudíž, což nakázáno ti bylo a straň se mnohobožců:'),
(1897, 15, 95, 'my pak zajisté dostačíme ti oproti posměváčkům,'),
(1898, 15, 96, 'kteří staví si vedle Boha boha jiného: však zvědí!'),
(1899, 15, 97, 'Dobře pak víme, že svírá se ti hruď (bolem) nad tím, co říkají:'),
(1900, 15, 98, 'pročež vyvyšuj Pána svého ve chvále jeho a druž se k těm, kdož padají na tvář svou:'),
(1901, 15, 99, 'a uctívej Pána svého, dokud nepřijde ti to, co jest jistotné.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Přijde rozkaz boží, pročež neuspěšujte jej! Chvála budiž mu: onť vypovznešen jest nade vše, cokoliv s ním spolčujete!'),
(1903, 16, 2, 'Sestupovati dává andělům svým z rozkazu svého, s duchem (svým), na koho zlíbí se mu ze služebníků jeho: „Varujte lidi, že není Boha, kromě mne: tudíž mne se bojte!“'),
(1904, 16, 3, 'Stvořil nebesa i zemi v pravdě: vysoko povznešen jest nade vše cokoliv s ním spolčujete!'),
(1905, 16, 4, 'Stvořil člověka z kapky semene a hle, on jest mu odpůrcem zjevným.'),
(1906, 16, 5, 'A dobytčata stvořil pro vás: z nich oděv máte a věci užitečné: a z nich i pojídáte.'),
(1907, 16, 6, 'A jich krásou (honosíte se), když domů je přivádíte a když na pastvu je vyháníte.'),
(1908, 16, 7, 'A nosí břemena vaše do krajin, kamž jinak byste nedostali se, leč s velkou námahou, zajisté Pán váš dobrotivý jest a milosrdný.'),
(1909, 16, 8, 'Koně, muly a osly dal vám, abyste jezdili na nich a pro ozdobu: a tvoří věci, o nichž nevíte.'),
(1910, 16, 9, 'Bohu přináleží určení (směru) cesty: někteří jdou stranou, však kdyby Bůh chtěl, vedl by vás všechny (cestou pravou).'),
(1911, 16, 10, 'Onť to byl, jenž seslal vám s nebe vodu za nápoj vám a ke vzrůstu bylin pro pastvu.'),
(1912, 16, 11, 'Jí vyrůstá vám obilí, olivy a palmy a réva vinná a plody všeho druhu: zajisté v tomto znamení jest lidu přemýšlivému.'),
(1913, 16, 12, 'A podrobil vám noc i den: a slunce a měsíc a hvězdy podrobeny jsou vám z rozkazu jeho: zajisté v tomto znamení jsou lidu chápajícímu.'),
(1914, 16, 13, 'A cožkolivěk stvořil vám na zemi z různých barev: zajisté v tomto znamení jest lidu přemýšlivému.'),
(1915, 16, 14, 'Onť to byl, jenž podrobil vám moře, abyste pojídati mohli z čerstvého masa (ryb) jeho a loviti z něho ozdoby, jimiž se odíváte: a viz lodi, any brázdí vlny jeho, aby (tak) hledali jste dary z milosti boží a abyste byli vděčni!'),
(1916, 16, 15, 'A rozházel temena horská po zemi, aby země nehýbala se s vámi: a řeky a stezky, abyste vedeni byli jimi,'),
(1917, 16, 16, 'jakož i známky podél cest: a hvězdami též vedeni jsou.'),
(1918, 16, 17, 'Zdaž ten, jenž tvoří, roveň jest tomu, kdo netvoří? Nepromyslíte-liž si toho?'),
(1919, 16, 18, 'A kdybyste spočísti chtěli milosti boží, nedopočítáte se jich: Bůh zajisté velký jest v odpouštění a slitovný.'),
(1920, 16, 19, 'A Bůh dobře ví, co tajíte i co najevo dáváte:'),
(1921, 16, 20, 'však božstva, jež oni vzývají vedle Boha, netvoří ničeho, nýbrž sama jsou stvořena.'),
(1922, 16, 21, 'Mrtva jsou, bez života, a nevědí, kdy vzkříšena budou.'),
(1923, 16, 22, 'Bůh váš jest Bůh Jediný, a srdce těch, kdož nevěří v život budoucí, oddána jsou zapírání a pyšni jsou:'),
(1924, 16, 23, 'však není pochybnosti o tom, že Bůh dobře ví, co tají i co najevo dávají, a on zajisté nemiluje pyšných.'),
(1925, 16, 24, 'Když řečeno jest jim: „Co seslal vám Pán váš?“, dí: „Pověsti starých,“'),
(1926, 16, 25, 'aby nésti mohli břemena svá úplná v den zmrtvýchvstání, jakož i část břemene těch, jež uvádějí na bludnou cestu z nevědomosti své. Špatné to bude břímě!'),
(1927, 16, 26, 'Úklady strojili již ti, kdož před nimi byli, však udeřil Bůh budovu jejich v základech: a zhroutila se střecha nad hlavami jejich a přikvačil na ně trest, zkad nenadáli se.'),
(1928, 16, 27, 'Pak v den zmrtvýchvstání zahanbí je Bůh a řekne: „Kde jsou ti, které za společníky dávali jste mi a již příčinou byli vašich rozporů?“ Řeknou pak ti, jimž dáno bylo vědění: „Zajisté potupa dnes a vše zlé padne na nevěřící!“'),
(1929, 16, 28, 'Ti, které skositi přijdou andělé (smrti) ve stavu nepravostném, nabízeti budou smíření, (řkouce): „Nečinili jsme zlého.“ — „Nikoliv, naopak: Bůh zajisté dobře ví, co činili jste!'),
(1930, 16, 29, 'Pročež vstupte do bran pekla, v němž přebývati budete věčně: a špatný bude příbytek pyšných!“'),
(1931, 16, 30, 'A řečeno bude bohabojným: „Co seslal vám Pán váš?“ Řeknou: „Vše nejlepší: těm, kdož dobře činili, v životě pozemském dobrou (odplatu), však obývání v životě budoucím daleko lepším bude: jak libý bude příbytek bohabojných!“'),
(1932, 16, 31, 'Do zahrad Edenu vstoupí, pod nimiž řeky tekou: v nich míti budou vše, čehož si přáti budou. Takovýmto způsobem odměňuje Bůh ty, kdož bojí se ho:'),
(1933, 16, 32, 'jimž, když přijdou andělé (smrti) skositi je ve stavu spravedlivosti, řeknou: „Pokoj s vámi! Vstuptež do ráje v odměnu za to, co činili jste.“'),
(1934, 16, 33, 'Což jiného očekávají, než že přijdou na ně andělé (smrti), anebo rozkaz Pána tvého? Stejně tak činili ti, kdož byli před nimi a Bůh neučinil jim příkoří, nýbrž oni sobě samém příkoří přivodili.'),
(1935, 16, 34, 'A padly na ně špatnosti, jichž dopustili se: a se všech stran obklopilo je to, z čeho posměch si byli tropili.'),
(1936, 16, 35, 'Ti, kdož spolčují (jiná božstva) s Bohem, řekli: „Kdyby Bůh byl chtěl, nebyli bychom my, aniž otcové naši, uctívali než jej: aniž bychom byli zapověděli co bez něho.“ Stejně tak jednali ti, kdož byli před nimi: však zdaž jiné povinnosti mají proroci, než ohlašování zjevné?'),
(1937, 16, 36, 'V každém národě pak vzbudili jsme proroka, (volajícího): „Uctívejte Boha a straňte se Tághúta!“ Některé z nich Bůh uvedl (na cestu pravou), jiným pak přisouzeno bylo, aby zůstali v bludu: i putujte zemí, a pohleďte, jaký byl konec těch, kdož lháři nazývali proroky!'),
(1938, 16, 37, 'Máš-li starost ohledně (správného) vedení jich, věz, že Bůh zajisté nevede (pravou stezkou) těch, které zavádí v blud: jim nebude pomocníků.'),
(1939, 16, 38, 'A přisahají při Bohu nejpevnější přísahou svou: „Bůh nevzkřísí toho, jenž zemřel.“ Naopak, (učiní tak): podle slibu svého pravdivého: však většina lidí není si toho vědoma.'),
(1940, 16, 39, '(Učiní tak), aby objasnil jim to, v čem rozcházeli se, a aby seznali ti, kdož nevěřili, že oni lháři byli.'),
(1941, 16, 40, 'Jediné slovo naše k čemukoliv chceme, aby bylo, jest, že řekneme: „Staň se!“ a stane se.'),
(1942, 16, 41, 'Těm, kdož vystěhovali se pro Boha poté, když byli utištěni, jistotně poskytneme krásné obývání na tomto světě, však odměna v životě budoucím daleko krásnější jest, kdyby si to jen uvědomili'),
(1943, 16, 42, 'ti, kdož trpělivě vyčkávají a na Pána svého spoléhají.'),
(1944, 16, 43, 'A poslali jsme před tebou jedině muže, jimž vnukli jsme poslání jejich: a tažte se lidu, jemuž dáno bylo Napomenutí, nevíte-li (o tom).'),
(1945, 16, 44, 'S jasnými důkazy a písmy poslali jsme je, a seslali jsme (i) tobě Napomenutí, abys vysvětlil lidem to, co sesláno jim bylo a aby o tom přemýšleli.'),
(1946, 16, 45, 'Zdaž jisti jsou ti, kdož lstivě vymýšleli špatnosti, že neproboří Bůh zemi pod nimi, anebo že nepřijde jim trest, zkad nenadějí se?'),
(1947, 16, 46, 'Aneb že nepostihne je na cestách jejich, aniž by mohli to znemožniti?'),
(1948, 16, 47, 'Aneb že nepostihne je ponenáhlým úděsem? Však zajisté Pán váš vlídný jest a slitovný.'),
(1949, 16, 48, 'Zdaž neviděli, že cokoliv stvořil Bůh, sklání stín svůj napravo i nalevo úctou k Bohu a v poníženosti sklání se před ním?'),
(1950, 16, 49, 'Bohu pak klaní se vše, což na nebi jest i na zemi, tvorové i andělé, pýchy prosti:'),
(1951, 16, 50, 'bojí se Pána svého na výsostech a konají, což nakázáno jim.'),
(1952, 16, 51, 'A řekl Bůh: „Nebeřte sobě bohů dvé, neboť pouze jeden Jediný jest Bůh: mne tudíž bojte se!“'),
(1953, 16, 52, 'Jemuť přináleží, cožkoliv na nebi jest i na zemi, jemu přináleží uctívání věčné: zdaž jiného báti se budete, kromě Boha?'),
(1954, 16, 53, 'Není dobrodiní, jehož byste nebyli obdrželi od Boha: a když protivenstvím navštíveni jste, jej prosíte o pomoc.'),
(1955, 16, 54, 'Pak, když zbaví vás protivenství hle, část vás spolčuje jiná s Pánem svým (božstva),'),
(1956, 16, 55, 'aby v nevíře popírali to, čím obdařili jsme je. Užívejte jen: však zvíte!'),
(1957, 16, 56, 'A oddělují pro ty, jež nemají, podíl z toho, čímž obdařili jsme je: při Bohu, jistotně tázáni budou ohledně toho, co byli vymýšleli!'),
(1958, 16, 57, 'A dávají Bohu dcery: při slávě jeho, uchovejž! A sami si nepřejí jich míti!'),
(1959, 16, 58, 'Neboť když některému z nich oznámeno jest narození dcery, stínem zachmuří se tvář jeho a tají zlobu svou.'),
(1960, 16, 59, 'Skrývá se před lidmi pro neštěstí, jež bylo mu oznámeno. Má k pohaně ponechati si děcko, anebo zahrabati je v prachu země? Zdaž nejsou pochybenými úsudky jejich?'),
(1961, 16, 60, 'Ti, kdož nevěří v život budoucí přirovnáváni buďtež ke všemu zlému: ke všemu nejvyššímu pak přirovnáván budiž Bůh, neboť on mocný jest a moudrý.'),
(1962, 16, 61, 'Kdyby Bůh měl trestati lid dle nepravosti jejich, nezůstalo by živého tvora na zemi: však on poshovuje jim do lhůty předurčené. Když pak dostaví se lhůta jejich neprodlouží ji ni o hodinu, aniž uspíší.'),
(1963, 16, 62, 'Dávají Bohu co sami nenávidějí; lež pronášejí jazykové jejich řkouce, že krásná odměna bude jim údělem. Není pochyby, že údělem jejich bude oheň a oni do něho uvrženi budou.'),
(1964, 16, 63, 'Při Bohu! Poslali jsme již před tebou proroky své k národům, však těmto zkrášlil Satan činy jejich: nyní on jest ochráncem jejich a trest bolestný je očekává.'),
(1965, 16, 64, 'Seslali jsme ti pak Knihu jen proto, abys vysvětlil jim to, v čem neshodují se a též aby vedením byla a milosrdenstvím lidu věřícímu.'),
(1966, 16, 65, 'A Bůh seslal s nebe vodu, aby obživoval jí odumřevší zemi. V tom zajisté znamení jest lidu slyšícímu.'),
(1967, 16, 66, 'Zajisté pak v dobytčatech naleznete poučení. Poskytujeme vám k pití to, co v útrobách jich nalézá se, nejsouc ani odpadkem, ani krví: mléko čisté, jež příjemno jest pijícím.'),
(1968, 16, 67, 'A z plodů datlovníku a vinné révy béřete opojné nápoje a potravu lahodnou: zajisté v tom všem znamení jest lidu chápajícímu.'),
(1969, 16, 68, 'A vnukl Pán tvůj VČELE: „Beř si obydlí v horách, ve stromech a v tom, co ti (lidé) vystaví:'),
(1970, 16, 69, 'poté požívej ze všech plodů a kráčej poslušně ve stezkách Pána svého.“ Z útrob pak jejích vychází tekutina různých barev, lékem jsouc lidem: zajisté v tomto znamení jest lidu přemýšlivému.'),
(1971, 16, 70, 'A Bůh stvořil vás, poté dá vám zemříti. Některý z vás pak dostihne starobného, sešlého věku, až nebude věděti ničeho z toho, co věděl: zajistéť Bůh vševědoucí jest a mocný.'),
(1972, 16, 71, 'Bůh vyznamenal některé z vás nad jiné v živobytí: však ti, již vyznamenáni byli, nepopřávají podílu z živobytí toho těm, jichž zmocnila se pravice jejich, tak aby rovni si v tom byli: Zdaž dobrodiní Boha zakrývati budou?'),
(1973, 16, 72, 'A Bůh dal vám manželky z vašeho plemene: a z manželek vašich dal vám syny a vnuky; a výživou dal vám věci dobré. Zdaž v plané výmysly ještě věřiti budou a dobrodiní Boha zapírati?'),
(1974, 16, 73, 'A uctívati vedle Boha (božstva), jež nemohou poskytnouti jim ničeho k výživě s nebes či ze země a nejsou s to (ničeho učiniti)?'),
(1975, 16, 74, 'Pročež nečiňte v ničem podobenství s Bohem, neboť Bůh zajisté ví a vy nevíte.'),
(1976, 16, 75, 'Bůh činí vám podobenstvím otroka ovládaného pánem svým, nemohoucího činiti (o své vůli) ničeho: a muže, jemuž popřáli jsme z milosti své živobytí hojného a jenž rozdává z něho potajmu i veřejně. Jsou rovni si tito dva? Chvála Bohu, nikoliv: však většina jich není si toho vědoma.'),
(1977, 16, 76, 'A činí vám Bůh podobenstvím dva muže, z nichž jeden jest němý od narození, nemohoucí činiti (o své vůli) ničeho a jest břemenem pánu svému: ať kamkoli poslán, nevrátí se s úspěchem. Zdaž roveň jest tento muži, který nakazuje, což spravedlivo jest a kráčí stezkou přímou?'),
(1978, 16, 77, 'Bohu jedině známy jsou skryté věci nebes i země: příchod pak hodiny jest jako mžiknutí oka, ba rychlejší, neboť Bůh jest všemohoucí.'),
(1979, 16, 78, 'Bůh vyvedl vás z útrob matek vašich, nemající nejmenšího vědění: a dal vám sluch a zrak a srdce, abyste mu byli vděčnými.'),
(1980, 16, 79, 'Zdaž nikdy nehleděli na ptactvo podrobené (vůli boží) v klenbách nebes? Nikdo nedrží je (v moci své), kromě Boha: zajisté v tomto znamení jsou lidu věřícímu.'),
(1981, 16, 80, 'A Bůh učinil vám domy vaše obydlím a učinil vám z koží dobytčat stany, jež shledáváte lehkými, když stěhujete se z jednoho místa a usazujete se na jiném: a z vlny jejich a chlupů jejich a kožešin jejich učinil vám nářadí a potřeby k (dočasnému) užívání.'),
(1982, 16, 81, 'A Bůh učinil, že věci stvořené jím skýtají vám stín a hory útulek a učinil vám oděv, jenž chrání vás před horkem a oděv, jenž chrání vás v dobách zlých. Takovýmto způsobem dovršuje milosrdenství své nad vámi, abyste do vůle jeho se odevzdali.'),
(1983, 16, 82, 'Však obracejí-li se k tobě zády — tvým posláním jest pouze hlásání zjevné.'),
(1984, 16, 83, 'Znají milosrdenství boží a pak přece je zapírají: a většina jich jsou nevěřící.'),
(1985, 16, 84, 'Však jednoho dne vzbudíme z každého národa svědka: tehdy pak nebude dovoleno nevěřícím, aby vymlouvali se, aniž naleznou přijetí.'),
(1986, 16, 85, 'A tu spatří nepravostní trest svůj: a v ničem nebude uleveno jim, aniž posečkáno.'),
(1987, 16, 86, 'A když shlédnou ti, kdož společníky dávali (Bohu) společníky své, řeknou: „Pane náš, totoť jsou společníci naši, které vzývali jsme vedle tebe.“ Však tito odtuší jim v odvět: „Lháři jste!“'),
(1988, 16, 87, 'A nabízeti budou Bohu v den onen smíření a ztratí se jim božstva jimi smyšlená.'),
(1989, 16, 88, 'Těm, kdož neuvěřili a odvraceli druhé od stezky boží přidáme trest nad trest za pohoršlivé skutky jejich.'),
(1990, 16, 89, 'Jednoho dne vzbudíme v každém národě svědka proti němu z jich vlastního středu: a tebe přivedeme jako svědka proti těmto, neboť tobě seslali jsme Knihu, jež vysvětlením jest všech věcí, věděním a milosrdenstvím a radostnou zvěstí těm, kdož odevzdali se do vůle boží.'),
(1991, 16, 90, 'Bůh zajisté přikazuje, spravedlnost a konání dobra a dávání příbuzným: zakazuje pak necudnosti, zlé skutky a útisk: a varuje vás, abyste toho byli pamětlivi.'),
(1992, 16, 91, 'Dostávejte věrně smlouvě s Bohem, když v ni jste byli vešli a neporušujte přísahy své poté, když jste ji byli dotvrdili: neboť v tom případě učinili jste Boha ručitelem svým. Bůh pak zajisté dobře ví, co činíte.'),
(1993, 16, 92, 'A nebuďte jako žena, jež rozpletla přadeno své poté, když pevně je byla spředla, rušíce zrádně přísahy navzájem si učiněné, když jeden sbor váš jest četnější druhého. V takovém případě Bůh staví vás jen na zkoušku a v den vzkříšení zajisté objasní vám vše, v čem neshodovali jste se.'),
(1994, 16, 93, 'Kdyby Bůh byl chtěl, byl by učinil vás jedním národem: však on zavádí v blud, koho chce a vede cestou přímou, koho chce: a zajisté tázáni budete ohledně skutků svých.'),
(1995, 16, 94, 'Pročež nerušte zrádně přísahy navzájem si učiněné, aby nesklouzla noha vaše poté, když byla pevně stála a abyste neokusili trestu zlého za to, že odvraceli jste druhé od stezky boží a aby nebyl vyměřen vám trest velký.'),
(1996, 16, 95, 'Neprodávejte smlouvy své s Bohem za cenu nepatrnou: neb co u Boha (získáte), bude vám lepší odměnou, dovedete-li pochopiti.'),
(1997, 16, 96, 'Cožkoliv u vás jest, pomine: cožkoliv pak u Boha jest, pobude. V pravdě pak doměříme těm, kdož trpělivě vyčkávali, odměnu jejich, hodnou nejlepších skutků jejich.'),
(1998, 16, 97, 'Kdožkoli jedná spravedlivě, ať muž či žena, a jest věřícím, tomu dopřejeme života blahého a jistotně vyměříme jim odměnu jejich, hodnou nejlepších skutků jejich.'),
(1999, 16, 98, 'Když pak čteš Korán, vezmi útočiště u Boha před Satanem kamenovaným,'),
(2000, 16, 99, 'neboť on jistě nemá moci nad těmi, kdož uvěřili a na Pána svého spoléhají;'),
(2001, 16, 100, 'nýbrž moc jeho vztahuje se jen na ty, kdož odvracejí se od Boha a spolčují s ním (božstva jiná).'),
(2002, 16, 101, 'A zaměníme-li jedno znamení jiným znamením, — Bůh nejlépe ví, co sesílá! — říkají: „Sám jsi\'s to vymyslil.“ Nikoliv: však většina jich není si (pravdy) vědoma.'),
(2003, 16, 102, 'Rci: „Přinesl to dolů duch svatý od Pána tvého v pravdě, aby utvrdil tím ony, kdož uvěřili a jako vedení a radostnou zvěst těm, kdož do vůle boží se odevzdávají.“'),
(2004, 16, 103, 'Dobře víme, že říkají: „Vždyť učí jej jakýsi muž.“ Však jazyk muže, na nějž činí narážku, jest cizí, kdežto tato mluva jest jazyk arabský, jasný.'),
(2005, 16, 104, 'Zajisté ony, kdož nevěří ve znamení boží, nevede Bůh (stezkou pravou) a přichystán jest jim trest bolestný:'),
(2006, 16, 105, 'neboť lež vymýšlejí ti, kdož nevěří ve znamení boží a zajisté oni lháři jsou.'),
(2007, 16, 106, 'Kdožkoli zapřel Boha poté, když byl uvěřil, vyjma toho, jenž proti své vůli k tomu byl donucen, mezitím co srdce jeho uklidněno zůstalo ve víře — hovně na ty, kdož s radostí otevřeli hruď svou nevíře, — Padne hněv Boha a údělem jim bude trest velký.'),
(2008, 16, 107, 'To proto, že milovali život pozemský nad život budoucí: a Bůh zajisté nevede (stezkou pravou) lid nevěřící.'),
(2009, 16, 108, 'Totoť jsou oni, jichž srdce, sluch i zrak zapečetil Bůh: to jsou ti, kdož lhostejni jsou:'),
(2010, 16, 109, 'není pochyby, že v životě budoucím záhubě propadnou.'),
(2011, 16, 110, 'Zajisté pak Pán tvůj k těm, kdož vystěhovali se poté, když byli utrpěli mnoho útisků, pak bojovali na stezce Páně a trpělivými byli: k těm Pán tvůj poté velký jest v odpouštění, slitovný.'),
(2012, 16, 111, 'V den, kdy každá duše obhajovati bude sama sebe a odměněna bude každá duše podle skutků svých a nikdo nebude zkrácen.'),
(2013, 16, 112, 'A činí vám Bůh podobenstvím město, jež žilo v bezpečí a v klidu a obdarováno bylo živobytím svým v hojnosti se všech stran: však popíralo dobrodiní boží i okusiti dal mu Bůh doby hladu a strachu za skutky jejich.'),
(2014, 16, 113, 'A přisel k nim prorok ze středu jejich, však za lháře prohlásili ho: pročež trest zachvátil je ve stavu nepravosti jejich.'),
(2015, 16, 114, 'Požívejte tedy ze všeho, což živobytím dal vám Bůh, dovoleným a dobrým a vděčni buďte za dobrodiní Boha, vskutku-li ho uctíváte.'),
(2016, 16, 115, 'Zapovězeno jest vám pouze jísti zdechliny a krev a maso vepřové a vše, nad čímž vysloveno bylo jiné jméno, než jméno Boha; však byl-li by kdo přinucen jísti z toho, — ale ne ze zvůle neb choutky — Bůh zajisté velký jest v odpouštění, slitovný.'),
(2017, 16, 116, 'A neříkejte v čemž jazykové vaši pronášejí lež — „Toto dovoleno jest a toto zapovězeno,“ aby tím vymýšleli jste lživá tvrzení o Bohu. Zajisté těm, kdož vymýšlejí lživá tvrzení o Bohu, dobře nepovede se:'),
(2018, 16, 117, 'malý požitek (mít) budou, však údělem jich bude trest bolestný.'),
(2019, 16, 118, 'Židům zapověděli jsme táž jídla, která jsme ti již dříve byli vyjmenovali: neučinili jsme jim útisku, nýbrž oni sami sobě útisk učinili.'),
(2020, 16, 119, 'Poté pak Pán tvůj k těm, kdož dopustili se zlého v nevědomosti, však poté na pokání obrátili se a napravili se: zajisté Pán tvůj jest pak velký v odpouštění, slitovný.'),
(2021, 16, 120, 'Zajisté Abraham byl mužem zbožně oddaným (Bohu), pravé víry, a nepatřil k mnohobožcům:'),
(2022, 16, 121, 'vděčným byl Bohu za dobrodiní jeho: pročež vyvolil si jej Bůh a vedl jej ke stezce přímé.'),
(2023, 16, 122, 'A dali jsme mu na tomto světě krásný úděl a ve světě budoucím jest mezi spravedlivými.'),
(2024, 16, 123, 'Vnukli jsme ti pak, abys následoval víru Abrahama, jenž byl víry pravé a nepatřil k mnohobožcům.'),
(2025, 16, 124, 'Ustanoven byl den sobotní pouze těm, kdož rozešli se v mínění o něm: a zajisté Pán tvůj rozsoudí mezi nimi v den zmrtvýchvstání ohledně toho, v čem rozcházeli se.'),
(2026, 16, 125, 'Volej ke stezce Pána svého moudrostí a napomínáním laskavým: a v hádkách přesvědčuj je slovy nejlepšími, neboť Pán tvůj zajisté nejlépe ví, kdo zbloudil se stezky jeho a nejlépe ví, kdo veden jest (stezkou pravou).'),
(2027, 16, 126, 'Činíte-li odvetu, odplácejte stejnou měrou, jakou bylo vám ublíženo: však dovedete-li snésti (bezpráví) trpělivě, bude to lepším pro trpělivě snášející.'),
(2028, 16, 127, 'Budiž tedy trpěliv: však trpělivost svou nalezneš pouze v Bohu a nermuť se nad nimi a nebuď stísněn v srdci pro záludy, jež kují:'),
(2029, 16, 128, 'Bůh zajisté jest s těmi, kdož bojí se ho a s těmi, kdož dobře činí.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Chválen budiž ten, jenž za NOCI PŘENESL služebníka svého z modlitebnice posvátné do modlitebnice nejvzdálenější, jejímuž okruží požehnali jsme, abychom ukázali mu znamení svá: onť zajisté vše slyší a obzírá.'),
(2031, 17, 2, 'A dali jsme Mojžíšovi Knihu a učinili jsme ji vedením SYNŮM ISRAELE, nakázavše jim: „Nebeřte si jiného zastánce vedle mne,'),
(2032, 17, 3, 'potomstvo těch, které na vodách nesli jsme s Noemem: onť zajisté služebníkem byl vděčným!“'),
(2033, 17, 4, 'A usoudili jsme synům Israele v Knize: „Zajisté dvakráte nepravostí naplníte zemi a vzpychnete pýchou velkou.“'),
(2034, 17, 5, 'A když naplnila se lhůta první pohrůžky, poslali jsme na vás služebníky své, lid urputnosti velké; i prohledali nejvnitrnější část obydlí vašich: tím stala se hrozba skutkem.'),
(2035, 17, 6, 'Pak opět dali jsme přijíti řadu na vás, abyste přemohli je; a rozhojnili jsme statky vaše i děti: a učinili jsme vás početnějšími mužstvem.'),
(2036, 17, 7, '(Řekli jsme): „Konáte-li dobro, konáte je pro sebe samy a konáte-li zlo, tedy proti sobě samým.“ A když naplnila se lhůta druhé (pohrůžky, vyslali jsme nepřátele na vás), aby poškodili tváře vaše a vnikli do modlitebnice, jakož byli vnikli do ní po prvé a aby zničili vše dobyté jimi zničením úplným.'),
(2037, 17, 8, 'Snad Pán váš smiluje se nad vámi: však začnete-li znovu, začneme i my znovu: a učinili jsme peklo pro nevěřící vězením.'),
(2038, 17, 9, 'Zajisté pak tento Korán vede ke všemu, což nejpřímějším jest a radostnou zvěst oznamuje věřícím, kteří dobré konají skutky, že připravena jest jim odměna velká:'),
(2039, 17, 10, 'zajisté pak těm, kdož nevěří v život budoucí, schystali jsme trest bolestný.'),
(2040, 17, 11, 'Člověk modlí se o zlo stejně jako o dobro, neboť člověk jest ukvapený.'),
(2041, 17, 12, 'Učinili jsme noc a den dvěma znameními: a zastínili jsme znamení noci a učinili jsme znamení dne jasně viditelným, abyste mohli získávati si milosti od Pána svého a abyste znali počet roků a sčítání (jich): rozdělili jsme pak všechny věci přesným rozdělením.'),
(2042, 17, 13, 'A každému člověku připevnili jsme osud jeho na šíj: a vytáhneme každému v den vzkříšení knihu, již shledá otevřenou:'),
(2043, 17, 14, '„Čti knihu svou! Sám postačíš si dnes jako súčtovatel proti sobě samému!“'),
(2044, 17, 15, 'A kdožkoliv přijímá (pravé) vedení, jen pro sebe sama tak činí: a kdožkoli v blud zachází, jen proti sobě samému tak činí. Nikterá duše, nesoucí vlastní břímě, nebude obtěžkána břemenem duše druhé a nikdy netrestali jsme, dokud nebyli jsme (předem) vyslali posla.'),
(2045, 17, 16, 'A když chtěli jsme, zahladiti město, dali jsme nařízení svá obyvatelům jeho bohatším zkaženým, (však nepřestali nepravostmi plniti je): i potvrzeno bylo nad městem odsouzení a naprostým zničili jsme je zničením.'),
(2046, 17, 17, 'A kolik zahubili jsme pokolení po Noemovi! A Pán tvůj dostačitelným jest znatelem a pozorovatelem hříchů služebníků svých!'),
(2047, 17, 18, 'Z těch, kdož baží po tomto rychle mizícím žití, dáme rychle, cožkoliv chceme, komukoliv chceme: však poté schystali jsme mu peklo, v němž sžíhán bude, potupen, zavržen:'),
(2048, 17, 19, 'ale kdož baží po životě budoucím a snaží se dosíci ho snahou jej hodnou, a jest věřícím — snažení těchto jest bohulibé.'),
(2049, 17, 20, 'Oběma, těmto i oněm, rozhojníme dary Pána tvého, neboť dary Pána tvého nikomu nebudou odmítnuty:'),
(2050, 17, 21, 'pohleď, jak dali jsme přednost jedněm nad druhými: však jistotně v životě budoucím větší budou stupně a větší bude rozdíl v přednosti.'),
(2051, 17, 22, 'Nestav vedle Boha bohů jiných, abys neupadl v pohanu, opuštění.'),
(2052, 17, 23, 'A přikázal Pán tvůj, abyste nectili (jiného boha), mimo něho a abyste laskavě chovali se k rodičům, ať již jeden z nich, či oba dospěli stáří v domě tvém. Nemluv k nim „Pfuj,“ aniž hubuj na ně, nýbrž mluv k nim řečí uctivou:'),
(2053, 17, 24, 'a skloň před nimi křídlo pokory z laskavost a rci: „Pane, buď milostiv jim, tak jako oni vychovali mne za malička.“'),
(2054, 17, 25, 'Pán váš nejlépe ví o tom, co jest v nitrech vašich: jste-li spravedlivými; onť pak zajisté k těm, kdož kají se, velký jest v odpouštění.'),
(2055, 17, 26, 'A dávej příbuzným svým, což přináleží jim, a taktéž nuzným a pocestným a nemarnotratni marnotratně,'),
(2056, 17, 27, 'neboť marnotratní bratry jsou Satana a Satan byl vůči Pánu svému vzdorný:'),
(2057, 17, 28, 'však odstraníš-li se od nich, sám snaže se získati podílu z milosrdenství Pána svého, jejž dosáhnouti doufáš, aspoň rci jim slovo útěšné.'),
(2058, 17, 29, 'Neklaď ruky své přivázané za krk, aniž otvírej ji dokořán, abys v hanu neupadl a bídu.'),
(2059, 17, 30, 'Zajisté Pán tvůj buď otevřenou rukou popřává darů svých, komu chce, aneb odměřuje je, neboť on ohledně služebníků svých dobře jest zpraven a pozoruje je.'),
(2060, 17, 31, 'A nezabíjejte dítek svých ze strachu před nedostatkem: obživíme je i vás: v pravdě ubíjení jich jest hříchem velkým.'),
(2061, 17, 32, 'A nepřibližujte se cizoložství, neboť smilstvem jest a špatnou cestou.'),
(2062, 17, 33, 'A nezabíjejte osoby, kterou Bůh zapověděl vám zabíjeti, vyjma po právu: a kdožkoli zabil by nespravedlivě, tu dali jsme blízkému příbuznému zabitého moc (odvety): však nechť nedopustí se výstřednosti v zabití, jinak vinník najde opět proti němu pomocníky.'),
(2063, 17, 34, 'A nedotýkejte se jmění sirotka, vyjma ve způsobu nejlepším, dokud nedosáhne dospělosti: a dodržujte závazky své, neboť za závazek bude dlužno zodpovídat se.'),
(2064, 17, 35, 'A dodržujte správně míru, když měříte a važte váhou přímou: toto jest lépe pro vás a nejlepším rozhodnutím.'),
(2065, 17, 36, 'A nenásleduj toho, o čem nemáš vědění: zajisté ze sluchu, zraku a (citu) srdce — ze všech těchto bude dlužno zodpovídati se.'),
(2066, 17, 37, 'A nevykračuj si pyšně po zemi, neboť nelze ti rozpoltiti zemi, aniž dosíci vrchu hor (svou) výškou:'),
(2067, 17, 38, 'vše to špatností jest u Pána tvého, jemu odpornou.'),
(2068, 17, 39, 'Toto jest částí toho, což vnuknul ti Pán tvůj z moudrosti: i nestav vedle Boha bohů jiných, abys nebyl vržen v peklo, pohaněn, zavržen.'),
(2069, 17, 40, 'Či snad vybral si vás Bůh za syny a vzal si z andělů ženy? Zajisté mluvíte slova údivná.'),
(2070, 17, 41, 'A rozmanitými učinili jsme napomenutí tohoto Koránu, aby přemýšleli o nich, však tím rozmnoženo jest pouze prchání jejich.'),
(2071, 17, 42, 'Rci : „Kdyby byli vedle Boha bohové jiní, jak díte, tehdy snažili by se nalézti záminku proti pánu trůnu:'),
(2072, 17, 43, 'však při slávě jeho, on daleko povýšen jest nad to, co přirčujete mu, povýšením velkým!'),
(2073, 17, 44, 'Ve chvále jeho vyvyšují jej sedmerá nebesa i země a cožkoliv na nich jest: a není věci, jež nevyvyšovala by jej ve chvále jeho; však vy nechápete slov chvály jejich. Bůh pak laskav jest a velký v odpuštění.“'),
(2074, 17, 45, 'Když čteš Korán, stavíme mezi tebou a těmi, kdož nevěří v život posmrtný, závoj rozestřený:'),
(2075, 17, 46, 'a pokládáme na srdce jejich clony, aby nechápali jej, a do uší jejich tíhu vkládáme: a když jmenuješ Pána svého v Koránu jako Jediného, obracejí se zády svými v útěku.'),
(2076, 17, 47, 'My nejlépe víme s jakým úmyslem naslouchají, když naslouchají ti, a když šeptají si a když říkají nepravostní: „V pravdě následujete jen muže očarovaného.“'),
(2077, 17, 48, 'Pohleď, jaká ohledně tebe činí podobenství! Však zbloudili, a nejsou s to, (aby nalezli) stezku.'),
(2078, 17, 49, 'A říkají: „Zdaž až budeme kosti a prach, budeme vzkříšeni ve stvoření novém?“'),
(2079, 17, 50, 'Rci: „Ano, i kdybyste byli kamení neb železo,'),
(2080, 17, 51, 'anebo stvořením z těch, (jichž vzkříšení) ještě nemožnějším (připadá vám) v nitrech vašich.“ I řeknou: „A kdo vrátí nás k životu?“ Rci: „Onen, jenž stvořil vás prvé.“ I potřásati budou nad tebou hlavami svými, řkouce: „Kdy to bude?“ Rci: „Možná, že bude to v brzku.'),
(2081, 17, 52, 'V den, kdy zavolá vás Bůh a odpovíte mu voláním chvály jeho a domnívati budete se, že dleli jste v hrobě pouze maličko.“'),
(2082, 17, 53, 'A rci služebníkům mým, aby mluvili jen způsobem nejlepším, neboť Satan jinak rozbroje by způsobil mezi nimi: zajisté pak Satan člověku jest nepřítelem zjevným.'),
(2083, 17, 54, 'Pán váš nejlépe ví vše ohledně vás: bude-li chtíti, slituje se nad vámi, a bude-li chtíti, potřesce vás a neposlali jsme tě, abys nad nimi byl poručníkem.'),
(2084, 17, 55, 'A Pán tvůj nejlépe ví, co na nebi jest a na zemi: a vyznamenali jsme některé proroky nad druhé: a dali jsme Davidovi (knihu) žalmů.'),
(2085, 17, 56, 'Rci: „Zavolejte na ty, které jste si vymyslili vedle Boha a shledáte, že nebudou moci zbaviti vás protivenství, aniž odvrátiti je.'),
(2086, 17, 57, 'Ti, které vzýváte, sami přejí si, aby dostati se mohli k Pánu svému každý co možná nejblíže, a prosí o slitování jeho a bojí se trestu jeho: trest pak Pána tvého jest obávání hodný.“'),
(2087, 17, 58, 'A není města, jež nezahubili bychom přede dnem vzkříšení, anebo nepotrestali bychom je trestem přísným: totoť v Knize psáno jest (věčné).'),
(2088, 17, 59, 'A nic nebránilo nám, abychom poslali tě se znameními, leda to, že lživými nazývali je národové předešlí. Neb dali jsme (kmeni) Tsemúd velbloudici jasně viditelnou a nepravost spáchali na ní: i neposíláme proroků se znameními, leda pro postrašení.'),
(2089, 17, 60, 'A (pomni), když řekli jsme ti: „Zajisté Bůh obklopuje lidstvo se všech stran“: a učinili jsme vidění, které jsme ti ukázali, pouze jako příčinu rozkolu mezi lidmi a taktéž i strom proklatý v Koránu: a strašíme je; však tím rozmnoženo jest v nich jen bloudění velké.'),
(2090, 17, 61, 'A když řekli jsme andělům: „Padněte na tváře své před Adamem,“ a učinili tak, vyjma Iblíse, jenž řekl: „Zdaž padnu na tvář svou před nějakým, jejž stvořil jsi z hlíny?“'),
(2091, 17, 62, 'Řekl (dále): „Vidíš onoho, jejž poctil jsi výše mne? V pravdě, poshovíš-li mi do dne zmrtvýchvstání, vyhladím potomstvo jeho vyjma několika málo.“'),
(2092, 17, 63, 'Řekl Bůh: „Odejdi: a kdož následovati tě bude z nich, zajisté peklo bude odměnou jejich, odměnou hojnou.'),
(2093, 17, 64, 'Vab tedy, koho budeš moci z nich, hlasem svým: útoč na ně jízdou svou i pěchotou svou, buď společníkem jejich v bohatství a dětech jejich a čiň jim sliby: Satan pak, slibuje jim pouze přeludy.'),
(2094, 17, 65, 'Však nad služebníky mými nebudeš míti moci: a Bůh postačitelným jest jim ochráncem.“'),
(2095, 17, 66, 'Pán váš jest to, jenž dává plouti loděm vašim na moři, abyste hledati mohli podílu z milosti jeho: onť zajisté k vám jest slitovný.'),
(2096, 17, 67, 'Když neštěstí zastihne vás na moři, ztratí se vám ti, které vzýváte: pouze On slyší vás: však když zachrání vás a přivede na souš, odvrátíte se od něho, zajisté člověk jest vzdorný.'),
(2097, 17, 68, 'Zdaž bezpečni se cítíte před tím, že nerozpoltí pod vámi strany souše anebo nepošle na vás smršť písečnou? Pak jistě nenaleznete ochránce.'),
(2098, 17, 69, 'Aneb bezpečni se cítíte před tím, že nevrhne vás do moře opět a nepošle na vás vítr bouřný a nepotopí vás za vzdornost vaši? Pak zajisté nenaleznete proti nám pomocníka.'),
(2099, 17, 70, 'Milostí zahrnuli jsme přece lidstvo: nosili jsme je po souši i po moři a výživou dali jim dobré věci a vyznamenali jsme je nad mnohými ze stvoření našich vyznamenáním velkým.'),
(2100, 17, 71, 'Jednoho dne svoláme všechno lidstvo s vůdci jeho: a komužkoli dána byla kniha jeho do pravice jeho, tito čísti budou z knihy své a nikdo nebude zkrácen ani o to, co vlákénko obnáší.'),
(2101, 17, 72, 'A kdožkoli byl v tomto životě slepým, bude slepým i v životě budoucím a ještě dále zbloudí od stezky (pravé).'),
(2102, 17, 73, 'A málem by tě byli zvábili, abys odchýlil se od toho, co vnuknuli jsme ti a abys vymyslil cos jiného na náš účet: a pak jistě byli by si tě vzali za přítele!'),
(2103, 17, 74, 'A kdybychom nebyli bývali tě utvrdili ve stanovisku tvém, byl bys málem již přichýlil se k nim něco málo.'),
(2104, 17, 75, 'Tehdy byli bychom dali ti okusiti útrap života i útrap smrti a nebyl bys pak našel proti nám pomocníka.'),
(2105, 17, 76, 'A málem by tě byli přinutili k vyjití ze země, aby tak vyhnali tě z ní: však po tvém odchodu nebyli by sami v ní zůstali, leda maličko.'),
(2106, 17, 77, 'Stejně tak dálo se s proroky, jež poslali jsme před tebou a nenalezneš ve zvyklostech našich odchylky.'),
(2107, 17, 78, 'Zachovávej modlitbu při zapadání slunce až do ztemnění nočního: a (taktéž) čtení jitřní, neboť čtení jitřní svědky své má:'),
(2108, 17, 79, 'a bdi v něm za noci, nadbytečnou horlivostí ve svůj prospěch: snad povznese tě Pán tvůj za to na stupeň vznešený.'),
(2109, 17, 80, 'A rci: „Pane můj, dej vstoupiti mi vstoupením čestným a dej vyjíti mi vyjitím čestným a vzbuď pro mne od sebe moc pomocnou.“'),
(2110, 17, 81, 'A rci: „Přišla pravda a zmizel klam: zajisté klam jest věcí mizivou.“'),
(2111, 17, 82, 'A sesíláme z Koránu, cožkoliv lékem jest a milosrdenstvím věřícím, však nepravostným přispěje pouze k větší záhubě.'),
(2112, 17, 83, 'A když milosti popřejeme člověku, odvrací se a poodchází stranou: však když zlo dotkne se jej, jest zoufalý.'),
(2113, 17, 84, 'Rci: „Každý jedná podle způsobu svého a Pán váš nejlépe ví, kdo na nejlepší nalézá se stezce.“'),
(2114, 17, 85, 'A dotazovati se budou tě ohledně ducha. Rci: „Duch přichází z rozkazu božího: a nebylo dáno vám z vědění, vyjma maličko.“'),
(2115, 17, 86, 'A kdybychom chtěli, mohli bychom jistotně dát zmizeti tomu, co vnukli jsme ti: a pak nenasel bys, kdo v té věci byl by tobě proti nám ochráncem,'),
(2116, 17, 87, 'leda z milosrdenství od Pána tvého: neboť milost jeho vůči tobě zajisté jest velká.'),
(2117, 17, 88, 'Rci: „Kdyby shromáždili se lidé a Džinnové s úmyslem vytvořiti cos rovného tomuto Koránu, nevytvořili by mu rovného, i kdyby navzájem si byli pomocníky.'),
(2118, 17, 89, 'A předvedli jsme v tomto Koránu lidem rozmanitost ze všech podobenství, však odmítla většina lidí vše, vyjma nevíry.'),
(2119, 17, 90, 'A řekli : „Neuvěříme ti, dokud nedáš vytrysknouti nám ze země prameni,'),
(2120, 17, 91, 'aneb dokud nebudeš míti zahradu datlovníků a révy vinné a nedáš vyvírati řekám ve středu jejím vyvíráním,'),
(2121, 17, 92, 'aneb nedáš spadnouti na nás nebesa, jak tvrdil\'s, po kusech, aneb nepředvedeš Boha a anděly co ručitele,'),
(2122, 17, 93, 'aneb nebudeš míti dům pozlacený, aneb nevystoupíš na nebesa: a neuvěříme ve vystoupení tvé, dokud nesešleš nám dolů knihu, již čísti bychom mohli.“ Rci: „Chválen budiž Pán můj! Zdaž jiného což jsem, než člověkem, poslem?“'),
(2123, 17, 94, 'A co zabraňuje lidem, aby věřili, když přišlo jim (dobré) vedení, leda to, že říkají: „Zdaž poslal Bůh člověka co posla?“'),
(2124, 17, 95, 'Rci: „Kdyby žili na zemi andělé a chodili po ní v poklidu, jistě bychom byli seslali jim s nebe anděla co posla.“'),
(2125, 17, 96, 'Rci: „Bůh postačitelným jest svědkem mezi mnou a vámi, neboť on zajisté dobře zpraven jest o služebnících svých a pozoruje je.“'),
(2126, 17, 97, 'A koho Bůh povede, ten (dobře) bude veden a koho zavede v bluď, tomu nenalezneš ochránce, mimo něho: a shromáždíme je v den zmrtvýchvstání, padší na tváře své, slepé a němé a hluché: obydlím jejich bude peklo a pokaždé když zhasne, zvětšíme pro ně vzplání jeho.'),
(2127, 17, 98, 'Toto bude odměna jejich za to, že neuvěřili ve znamení naše a říkali: „Zdaž až budeme kosti a prach, budeme vzkříšeni ve stvoření novém?“'),
(2128, 17, 99, 'Což nevidí, že Bůh, jenž stvořil nebe i zemi, může též stvořiti jim podobné? A předurčil jim lhůtu — o níž není pochyby, však nepravostní zamítají vše, vyjma nevíry.'),
(2129, 17, 100, 'Rci: „Kdybyste měli vládu nad pokladnicemi milosrdenství Pána mého, tehdy pevně drželi byste je ze strachu, abyste jich nevypotřebovali: neboť člověk jest chamtivý.“'),
(2130, 17, 101, 'A kdysi dali jsme Mojžíšovi devatero znamení jasných: i taž se synů Israele ohledně toho, když přišel k nim a když řekl mu Farao: „Zajisté mním, Mojžíši, žes očarován.“'),
(2131, 17, 102, 'Řekl (Mojžíš) : „Však víš ze neseslal tato znamení nikdo jiný, než Pán nebes a země, co znamení viditelná: zajisté pak mním, Faraone, žes záhubě propadlý.“'),
(2132, 17, 103, 'I chtěl vypuditi je ze země a utopili jsme jej a kdožkoliv s ním byl, všechny.'),
(2133, 17, 104, 'A řekli jsme poté synům Israele: „Přebývejte v zemi a když přijde doba slibu života budoucího, shromáždíme vás veškeré.“'),
(2134, 17, 105, 'A v pravdě seslali jsme jej (Korán) a v pravdě sestoupil a poslali jsme tě pouze jako věštitele a varovatele.'),
(2135, 17, 106, 'A Korán ten rozdělili jsme (na části), abys čísti jej mohl lidu pozvolna: a seslali jsme jej sesláním pozvolným.'),
(2136, 17, 107, 'Rci: „Věříte-li v něj, či nevěříte, ti, kterým dáno bylo vědění před tím, padají na tváře své úctou, když čten jest jim,'),
(2137, 17, 108, 'a praví: ,Chválen budiž Pán náš! Neb slib Pána našeho se splňuje.‘ “'),
(2138, 17, 109, 'A padají na tváře své plačíce a roznmožena jest pokora jejich.'),
(2139, 17, 110, 'Rci: „Vzývejte Boha anebo vzývejte Milosrdného: pod kterýmkoli z těchto jmen byste jej vzývali, vězte, že Bohu přináležejí jména nejlepší.“ A nebudiž příliš hlasitým v modlitbě své, aniž příliš skrytým, nýbrž následuj mezi těmito stezku střední:'),
(2140, 17, 111, 'a rci: „Chvála Bohu, jenž neučinil sobě syna, aniž má podílníka v panství svém, a jenž neměl (nikdy) ochránce před ponížením.“ A vyvyšuj Boha vyvyšováním.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Chvála Bohu, jenž seslal služebníku svému Knihu a neučinil v ní zkřivení,'),
(2142, 18, 2, 'přímou jest, aby varovala před trestem přísným od něho: a aby oznamovala věřícím, kteří konají dobré skutky, odměnu krásnou,'),
(2143, 18, 3, 'v níž přebývati budou věčně:'),
(2144, 18, 4, 'a aby varovala ty, kdož praví: „Bůh učinil sobě syna.“'),
(2145, 18, 5, 'Nijakého vědění nemají o tom oni, aniž otcové jejich: velké jest to slovo, jež vychází z úst jejich, a dojista jen lež mluví.'),
(2146, 18, 6, 'A snad udoláš se v zápětí jejich, neuveří-li v toto zjevení, zármutkem.'),
(2147, 18, 7, 'Zajisté učinili jsme vše, což na zemi jest, ozdobou jí, abychom vyzkoušeli lidstvo, kdo z nich nejlepším bude v konání:'),
(2148, 18, 8, 'však dojista učiníme vše, což na ní jest, prachem rozmetaným.'),
(2149, 18, 9, 'Zdaž povážil\'s, že obyvatelé JESKYNĚ a Ar-Raqím byli jedním ze znamení našich obdivuhodných?'),
(2150, 18, 10, 'Když útočiště vzali jinoši v jeskyni, řekli: „Pane náš, dejž nám od sebe milosrdenství a uprav nám ve věci naší výsledek správný.“'),
(2151, 18, 11, 'I postihli jsme hluchotou uši jejich v jeskyni po léta četná:'),
(2152, 18, 12, 'poté vzkřísili jsme je, abychom zvěděli, která z obou stran dovede lépe spočísti pobytu jejich délku.'),
(2153, 18, 13, 'My vypravujeme ti děj jejich v pravdě, neboť byli to jinoši, kteří uvěřili v Pána svého a jež rozmnožili jsme v (dobrém) vedení.'),
(2154, 18, 14, 'A opásali jsme (silou) srdce jejich, když povstali a řekli: „Pán náš jest Pánem nebes i země a my nevzýváme vedle něho boha jiného, neboť v tom případě vyjadřovali bychom bláhovost.'),
(2155, 18, 15, 'Tento lid náš vzal si vedle Boha božstva, ač nepředvádějí ohledně jich jasného zmocnění; — a kdo nepravostnějším jest toho, jenž o Bohu lež vymyslí?'),
(2156, 18, 16, 'A když oddělíte se od nich a od toho, co oni vzývají vedle Boha, a útočiště vezmete v jeskyni, rozestře nad vámi Bůh milosrdenství své a upraví vám ve věci vaší dobrý výsledek.“'),
(2157, 18, 17, 'A byl bys viděl slunce při východu jak přešlo podél jeskyně jejich na straně pravé: a při západu vzdálilo se od nich na straně levé: a oni nalézali se v dutině její. Toto jest jedno ze znamení božích: koho Bůh vede, ten (dobře) jest veden, a koho zavádí, tomu nenalezneš ochránce ani vůdce.'),
(2158, 18, 18, 'Byl bys měl je za bdící a zatím oni spali: a obraceli jsme je na stranu pravou i na stranu levou; a pes jejich, s roztaženými prackami ležel u vchodu. Kdybys byl padl na ně z nenadání, byl bys odvrátil se od nich v útěku a byl bys pohledem na ně býval ustrašen.'),
(2159, 18, 19, 'A tak vzkřísili jsme je, aby navzájem se mohli dotazovati; i řekl jeden z nich: „Jak dlouho dleli jste zde?“ Řekli: „Dleli jsme zde den, aneb část dne.“ (Druzí) řekli: „Pán váš nejlépe ví, jak dlouho jste zde dleli: i pošlete jednoho z vás s tímto penízem svým do města, nechť vyhlédne, kdo v něm má nejčistší potraviny a nechť přinese vám od něho potravu: však nechť ostrovťipným jest a nedá tušiti o vás nikomu.'),
(2160, 18, 20, 'Neboť oni, kdyby objevili vás, kamenovali by vás aneb navrátili k víře své: pak nikdy již by se vám dobře nevedlo.“'),
(2161, 18, 21, 'A takovýmto způsobem odhalili jsme je, aby zvěděli spoluobčané jejich, že slib boží jest pravdivý a že o (příchodu) hodiny není pochyby. I hádali se mezi sebou ohledně případu jinochů a řekli (jedni): „Postavte nad nimi budovu: Pán jejich nejlépe ví vše o případu jejich.“ Ale ti, jichž názor zvítězil ohledně případu jinochů, řekli: „Dojista učiníme si nad nimi modlitebnici.“'),
(2162, 18, 22, 'Někteří říkají: „(Byli) tři a čtvrtým v počtu byl pes jejich.“ Jiní říkají: „Pět jich bylo a šestým byl pes jejich,“ dohadujíce se ohledně věcí skrytých; a říkají jiní: „Bylo jich sedm a osmým byl pes jejich.“ Rci: „Pán můj nejlépe ví počet jejich; ten zná jen málo lidí: pročež nepři se ohledně jich, leda na oko a nevyzvídej o nich u nikoho (z křesťanů).'),
(2163, 18, 23, 'A neříkejž o ničem: „Jáť učiním tak zítra,“'),
(2164, 18, 24, 'aniž bys řekl: „Bude-li chtíti Bůh“: a připomeň si Pána svého, zapomněl-li jsi a rci: „Snad povede mne Pán můj k tomu, co nejblíže jest k pravdě správností.“'),
(2165, 18, 25, 'Dleli pak v jeskyni své tři sta let a k tomu ještě devět.'),
(2166, 18, 26, 'Rci: „Bůh nejlépe ví, jak dlouho tam dleli: jemuť přináleží věci skryté nebes i země: onť nejlépe vidí a slyší! Není jim vedle něho ochránce, aniž má koho za společníka v rozsudcích svých.'),
(2167, 18, 27, 'I předříkávej cožkoliv vnuknuto ti bylo z Knihy Pána tvého: není nikoho, jenž změnit by mohl slova jeho a nenalezneš kromě něho útočiště.'),
(2168, 18, 28, 'Vyčkávej trpělivě s těmi, kdož vzývají Pána svého z rána i za večera, žádajíce si (spatřiti) tvář, jeho, a neodvracej očí svých od nich ze žádosti po zdobě života pozemského a neposlouchej toho, jehož srdce učinili jsme lhostejným ke vzpomínání nás a jenž následuje choutky své a jehož skutky přepjaty jsou.'),
(2169, 18, 29, 'A rci: „Pravda přichází od Pána vašeho: a kdo chce, nechť věří a kdo chce, nechť nevěří. My pak zajisté schystali jsme nepravostným oheň, jehož sloupové obklopí je: a když žebroniti budou o pomoc, pomoženo bude jim vodou podobnou roztavenému kovu, jež osmaží tváře jejich: špatný bude to nápoj a špatné odpočívání!'),
(2170, 18, 30, 'Však ti, kdož uvěřili a konali dobré skutky — my zajisté nedáme ztratiti se odměně těch, kdož dobrého byli konání!'),
(2171, 18, 31, 'Pro tyto budou zahrady Edenu, pod nimiž řeky tekou: zdobiti se v nich budou náramky zlatými a odívati budou se řízami zelenými z hedvábí a brokátu: a odpočívati v nich budou na pohovkách. Jak skvostná bude to odměna a jak krásné odpočívání!'),
(2172, 18, 32, 'A učiň jim podobenství dvou mužů: jednomu z nich dali jsme dvé zahrad révy vinné a obě obklíčili jsme datlovníky a mezi oběma učinili jsme pole obilná:'),
(2173, 18, 33, 'obě zahrady vydaly plody své a nebylo v nich nejmenšího úbytku: dali jsme pak vyvříti ve středu jejím řece.'),
(2174, 18, 34, 'A muž ten měl všechny tyto plody. I řekl k druhu svému v rozhovoru: „Mám větší tebe jmění a značnější tebe rodinu.“'),
(2175, 18, 35, 'A vstoupil do zahrady své a prohřešil se proti sobě samému, řka: „Nemyslím, že zajde kdy tato zahrada:'),
(2176, 18, 36, 'a nemyslím, že hodina dostaví se; a i kdybych navrácen byl k Pánu svému, jistotně lepší této našel bych v záměnu.“'),
(2177, 18, 37, 'Řekl mu druh jeho v rozhovoru: „Zdaž nevěřiti chceš v toho, jenž stvořil tě z prachu pak z kapky (semene) a pak vytvořil tě v muže?'),
(2178, 18, 38, 'Však on jest Bůh, Pán můj a nebudu spolčovati s Pánem svým nikoho.'),
(2179, 18, 39, 'A proč raději, když vstoupíš do zahrady své, neřekneš: Co Bůh chce, (stane se): není moci, vyjma v Bohu? Ač vidíš mne chudším tebe jměním a dětmi,'),
(2180, 18, 40, 'snad Pán můj dá mi darů lepších tvé zahrady: možná, že pošle na ni rány s nebes a stane se z ní jíl jalový:'),
(2181, 18, 41, 'neb propadne se voda její a nebudeš s to, nalézti ji.“'),
(2182, 18, 42, 'A (zničením) obchváceny byly plody jeho: i počal lomiti rukama nad tím, co byl vydal na ni, ana (zničená réva) visela na loubí svém, řka: „Kéž nebyl bych spolčoval s Pánem svým nikoho!“'),
(2183, 18, 43, 'A neměl čet (vojska), jež pomohly by mu kromě Boha, aniž bylo mu pomoženo.'),
(2184, 18, 44, 'Tam ochrana jest pouze u Boha, (Boha) pravého: on nejlepším jest odměňovatelem a k nejlepšímu vede konci.'),
(2185, 18, 45, 'A učiň jim podobenstvím život pozemský: podoben jest vodě, již seslali jsme s nebe i smísily se s ní rostliny zemské: — pak seschnou a rozvanou je větry: neb Bůh nade všemi věcmi moc má.'),
(2186, 18, 46, 'Jmění a děti jsou zdobou života pozemského, však co stálé jest, dobré skutky, lepším jest u Pána tvého jako odměna i jako naděje.'),
(2187, 18, 47, '(Přijde) den, kdy posuneme hory a spatříš zemi rovnou: a shromáždíme lidi všechny, aniž opomeneme jednoho z nich.'),
(2188, 18, 48, 'I předvedeni budou před Pána tvého v řadách: „Nyní přišli jste k nám tak, jak stvořili jsme vás po prvé: tak jest, ač měli jste za to, že nesplníme slibu vám učiněného.“'),
(2189, 18, 49, 'A (každému) dána bude kniha (jeho): i spatříš provinilce ustrašené nad tím, co v ní bude, a říkati budou: „Běda nám! Co značí tato kniha? Neopomíná malého ni velkého skutku, aniž by započítán byl.“ A naleznou, co činili, před sebou: a neukřivdí Pán tvůj žádnému.'),
(2190, 18, 50, 'Když řekli jsme andělům: „Padněte na tváře své před Adamem,“ učinili tak všichni vyjma Iblíse, jenž byl jedním z Džinnů a vzepřel se rozkazu Pána svého. Zdaž vezmete si jej a potomstvo jeho za ochránce kromě mne, ač nepřáteli jsou vám? Špatná to bude pro nepravostné výměna!'),
(2191, 18, 51, 'Neučinil jsem je svědky stvoření nebes a země, aniž stvoření jich samých: a nevzal jsem si těch, kdož v blud zavádějí, za pomocníky.'),
(2192, 18, 52, '(Přijde) den, kdy řekne (Bůh): „Zavolejte ty, jež mnili jste býti společníky mými!“ I vzývati je budou, však nevyslyší jich: postavili jsme pak mezi ně údolí zatracení.'),
(2193, 18, 53, 'A uvidí provinilci oheň a tušiti budou, že do něho budou uvrženi: však nenaleznou od něho vysvobození.'),
(2194, 18, 54, 'Učinili jsme pak rozmanitá v tomto Koránu lidem podobenství všeho druhu, však člověk ve většině věcí pře se.'),
(2195, 18, 55, 'A co zabraňuje lidem, aby věřili, nyní když přišlo jim vedení: a aby prosili za odpuštění Pána svého — leda že snad čekají, aby přišla jim známá sudba starých, aneb aby přišel na ně trest patrný?'),
(2196, 18, 56, 'Posíláme posly své pouze jako zvěstovatele radostných zvěstí a jako varovatele; však ti, kteří neuvěřili, přou se zbytečně, aby zahladili tím pravdu: a béřou znamení naše, jakož i to, před čímž varováni byli, za žert.'),
(2197, 18, 57, 'Kdo nepravostnějším jest toho, jenž když zmíněna jsou mu znamení Pána jeho, odvrací se od nich a zapomíná předešlé skutky rukou svých? Clonou zastřeli jsme srdce jejich, aby nechápali Koránu a tíhu vložili jsme do uší jejich: a když volati je budeš k vedení, nebudou následovati jej přece nikdy.'),
(2198, 18, 58, 'Ale Pán tvůj velký jest v odpouštění, pln milosrdenství: kdyby ztrestati chtěl je za to, co zasloužili si, uspíšil by jim trest: nicméně mají lhůtu slíbenou a nenaleznou jinde než u Boha útulku.'),
(2199, 18, 59, 'A ona města zahladili jsme, když stala se hříšnými a učinili jsme po zahlazení jejich lhůtu slíbenou.'),
(2200, 18, 60, '(Pomni), když řekl Mojžíš sluhovi svému: „Neustanu, dokud nedojdu stoku obou moří: anebo půjdu vpřed po léta.“'),
(2201, 18, 61, 'A když došli ke stoku obou moří, zapomněli na svoji rybu a ta vskočila do moře střemhlav.'),
(2202, 18, 62, 'A když přešli (ono místo), řekl Mojžíš sluhovi svému: „Přines nám oběd náš, neb potkala nás z cesty této naší únava.“'),
(2203, 18, 63, 'Řekl hoch: „Co míníš? Když uchýlili jsme se ke skále, v té chvíli zapomněl jsem na rybu a jedině Satan způsobil, abych zapomněl na ni a nevzpomněl si na ni: a ryba vskočila do moře zázračně.“'),
(2204, 18, 64, 'Řekl Mojžíš: „To právě jsme hledali.“ I vrátili se zpět oba touž cestou, kterou přišli.'),
(2205, 18, 65, 'A nalezli služebníka ze služebníků našich, jejž obdařili jsme milosrdenstvím od nás a jejž naučili jsme vědění svému,'),
(2206, 18, 66, 'i řekl mu Mojžíš: „Zdaž následovati budu tebe, abys naučil mne z toho, čemu\'s byl naučen, pro (pravé) vedení mé?“'),
(2207, 18, 67, 'Řekl: „Zajisté nebudeš s to, abys trpělivě vyčkal se mnou:'),
(2208, 18, 68, 'a jak nalezneš trpělivost v tom, co neobsáhneš pochopením?“'),
(2209, 18, 69, 'Řekl (Mojžíš): „Shledáš mne, bude-li Bůh chtíti, trpělivým a nebudu neposlušen rozkazů tvých.“'),
(2210, 18, 70, 'Řekl: „Budeš-li následovati mne, tehdy netaž se mne na nic, dokud já první neučiním o tom zmínku.“'),
(2211, 18, 71, 'I kráčeli vpřed, až pak vstoupili na loď a on provrtal ji : i řekl (Mojžíš): „Což provrtal jsi ji, abys utopil lidi v ní jedoucí? Zajisté věc učinil\'s divnou.“'),
(2212, 18, 72, 'Řekl: „Neřekl jsem ti, že nebudeš s to, abys trpělivě vyčkal se mnou?“'),
(2213, 18, 73, 'Řekl Mojžíš: „Nekárej mne za to, že zapomněl jsem aniž uvaluj na mne úlohu obtížnou.“'),
(2214, 18, 74, 'I kráčeli vpřed, až potkali jinocha a on zabil jej. Řekl (Mojžíš): „Zdaž zabil jsi osobu nevinnou zabitím osoby jiné? Zajisté věc učinil\'s odpornou.“'),
(2215, 18, 75, 'Řekl: „Neřekl jsem ti, že nebudeš s to, abys trpělivě vyčkal se mnou?“'),
(2216, 18, 76, 'Řekl Mojžíš: „Otáži-li se tě nač po tomto případě, nenech mne déle zůstati tvým druhem: tentokrát ještě podávám ti omluvu.“'),
(2217, 18, 77, 'I kráčeli vpřed, až přišli k lidu jistého města: i žádali jej o jídlo, však lidé oni odmítli pohostiti jich. A nalezli v městě zeď, jež jako by chtěla spadnouti a on narovnal ji.'),
(2218, 18, 78, 'Řekl (Mojžíš): „Kdybys byl chtěl, mohl bys byl za to dostati odměnu.“ Řekl: „Zde dělí se cesta má od cesty tvé: však oznámím ti výklad toho, v čem nebyl\'s s to, abys trpělivě vyčkal.'),
(2219, 18, 79, 'Pokud se týče lodi, patřila nuzným pracovníkům na moři a chtěl jsem poškodití ji, nebo od zadu šel na ni král, jenž každou loď bral násilím.'),
(2220, 18, 80, 'A pokud se týče jinocha, rodičové jeho byli věřícími a obávali jsme se aby neuvalil na ně žal svým zblouděním a nevírou.'),
(2221, 18, 81, 'A chtěli jsme, aby zaměnil jim jej Pán jejich synem lepším jeho v bezúhonnosti a bližším lásce (synovské)'),
(2222, 18, 82, 'A pokud se týče zdi, patřila dvěma mládcům osiřelým v městě a pod ní byl poklad patřící jim; a otec jejich byl mužem spravedlivým: i chtěl Pán tvůj, aby oba dospěli mužnosti a vytáhli zpod zdi poklad svůj z milosrdenství Pána tvého. A neučinil jsem toho ze své vlastní vůle. Toto jest výklad toho, v čem nebyl\'s s to, abys trpělivě vyčkal.“'),
(2223, 18, 83, 'A budou dotazovati se tě ohledně Dvourožce: „Vyprávěti vám budu děje jeho.“'),
(2224, 18, 84, 'Upevnili jsme (moc) jeho na zemi a dali jsme mu ke všem věcem cestu:'),
(2225, 18, 85, 'a on následoval cestu onu,'),
(2226, 18, 86, 'až dospěl k západu slunce a shledal, že zapadá v prameni horkém:“ a nalezl u něho lid. Řekli jsme: „Ó Dvourožče, jest na tobě, abys buď potrestal je, aneb abys naložil s nimi dobře.“'),
(2227, 18, 87, 'Řekl: „Pokud se týče nepravostných, ztresceme je: poté navráceni budou k Pánu svému i ztresce je trestem odporným.'),
(2228, 18, 88, 'Však pokud se týče těch, kdož uvěřili a konali dobré skutky, těm dána bude odměna krásná a rozkazy, jež dáme jim, budou snadné.“'),
(2229, 18, 89, 'Poté pak následoval cestu jinou,'),
(2230, 18, 90, 'až dospěl k východu slunce a shledal, že vychází nad lidem, jimž nedali jsme před ním clony.'),
(2231, 18, 91, 'Tak tomu bylo a zpraveni jsme o všech, kdož byli s ním.'),
(2232, 18, 92, 'Poté následoval cestu (jinou),'),
(2233, 18, 93, 'až došel mezi dvě zdi, pod nimiž nalezl lid, jenž stěží mohl pochopiti mluvu.'),
(2234, 18, 94, 'Řekli: „Ó Dvourožče, zajisté Jádžúdž a Mádžúdž tropí spoušť v zemi: zdaž dáme ti poplatek, abys ty učinil mezi námi a mezi nimi zeď?“'),
(2235, 18, 95, 'Řekl: „To, nad čím upevnil Pán můj (moc moji) lepším jest pro mne: pročež pomozte mi ze všech sil, i vystavím mezi vámi a nimi přehradu.'),
(2236, 18, 96, 'Přineste mi kusy železa, dokud nebude vyrovnáno místo mezi oběma boky (hor).“ Pak řekl: „Foukejte, dokud nerozžhavíte je“ — řekl — „a přineste k vylití do něho mosaz roztavenou.“'),
(2237, 18, 97, 'A nebyli s to, aby slezli zeď, aniž byli s to, aby ji prorazili.“'),
(2238, 18, 98, 'Řekl: „Totoť jest milosrdenství od Pána mého: a když přijde vyplnění slibu Pána mého, obrátí ji v prach: a slib Pána mého pravdivý jest!“'),
(2239, 18, 99, 'V den onen ponecháme je ve vlnách narážeti na se navzájem: i zatroubeno bude na roh a shromáždíme je veškeré.'),
(2240, 18, 100, 'A v den onen představíme peklo před nevěřícími představením:'),
(2241, 18, 101, 'před ony, jichž zrakové byli zastřeni před varováním mým a již nebyli s to, aby slyšeli.'),
(2242, 18, 102, 'Zdaž domnívali se ti, kdož neveřili, že mohou služebníky mé vzíti sobě za ochránce mimo mne? Zajisté přichystali jsme peklo pro nevěřící příbytkem.'),
(2243, 18, 103, 'Rci: „Zdaž povím vám o těch, jichž nejvíce ztraceni byli skutkové?'),
(2244, 18, 104, 'Jichž snažení poblouzené bylo v životě pozemském a kteří přec domnívají se, že dobře činili?'),
(2245, 18, 105, 'To jsou ti, kdož neuvěřili ve znamení Pána svého a v setkání s ním: i niveč obráceny jsou skutky jejich a nepřipočteme jim v den zmrtvýchvstání váhy nijaké.'),
(2246, 18, 106, 'Toto bude odměna jejich: peklo! Za to, že neuvěřili a za žert považovali znamení má a proroky mé.'),
(2247, 18, 107, 'Však těm, kteří uvěřili a konali dobré skutky, budou zahrady rajské příbytkem.'),
(2248, 18, 108, 'V nich přebývati budou věčně: a nebudou přáti si za ně výměny.“'),
(2249, 18, 109, 'Rci: „Kdyby moře bylo inkoustem ke (psaní) slov Pána mého, vyčerpalo by se moře dříve než vyčerpala by se slova Pána mého, i kdybychom ještě jednoho (moře) použili v objemu.“'),
(2250, 18, 110, 'Rci: „Jáť zajisté jsem jen člověk jako vy; vnuknuto bylo mi, Bůh váš jest jenom Bůh Jediný, kdožkoli teda doufá v setkání s Pánem svým, nechť koná skutky dobré a nechť nespolčuje v uctívání Pána svého nikoho.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Káf. Há. Já. `Ain. Sád.'),
(2252, 19, 2, 'Toto jest zmínka o milosrdenství Pána tvého nad služebníkem jeho Zachariášem:'),
(2253, 19, 3, 'když volal k Pánu svému voláním skrytým,'),
(2254, 19, 4, 'a řekl: „Pane můj, zajisté ochably kosti mé a zastkvěla se hlava má šedinami, ale nikdy nebyl jsem ve své modlitbě k tobě, Pane můj, oslyšán:'),
(2255, 19, 5, 'jáť obávám se o ty, kdož přijdou po mně; má žena pak neplodna jest, pročež dej mi od sebe následníka,'),
(2256, 19, 6, 'jenž dědicem byl by mým a dědicem Jakuba: a učiň jej, Pane můj, zalíbeníhodným.“'),
(2257, 19, 7, '„Zachariáši, oznamujeme ti narození mládce, jehož jméno bude Jahjá: neučinili jsme mu před ním jmenovce.“'),
(2258, 19, 8, 'Řekl: „Pane můj, jakž narodí se mi syn, když žena má neplodna jest a já sám dospěl jsem zesláblosti stáří?“'),
(2259, 19, 9, 'Řekl: „Takto pravil Pán tvůj: snadné jest mi to, neboť stvořil jsem tě před tím, když nebyl\'s ničím.“'),
(2260, 19, 10, 'Řekl: „Pane můj, učiň mi znamení.“ Řekl: „Znamením tobě bude, že nebudeš mluviti s lidmi po tři noci, (ač) v plném zdraví.“'),
(2261, 19, 11, 'I vyšel ze svatyně k lidu svému a činil jim posuňky, aby chválili Boha z jitra i z večera.'),
(2262, 19, 12, '„Ó Jahjá, vezmi Knihu s pevným předsevzetím,“ (řekli jsme) a dali jsme mu soudnost již v dětství,'),
(2263, 19, 13, 'jakož i slitovnost od nás a bezúhonnost: a bohabojným byl,'),
(2264, 19, 14, 'a bez provinění vůči rodičům svým a nebyl zpupnýn a neposlušným.'),
(2265, 19, 15, 'Pokoj budiž s ním v den, kdy narodil se i v den, kdy zemřel a v den, kdy vzkříšen bude!'),
(2266, 19, 16, 'A zmiň se v Knize o MARII. když vzdálila se od rodiny své k místu východnímu:'),
(2267, 19, 17, 'a vzala na se závoj (aby skryla se jím) před nimi: a poslali jsme k ní ducha svého, jenž vzal na se podobu před ní muže urostlého.'),
(2268, 19, 18, 'Řekla: „Béřu útočiště k Milosrdnému před tebou, bojíš-li se jej.“'),
(2269, 19, 19, 'Řekl: „Jáť pouze jsem poslem Pána tvého, abych obdařil tě synem svatým.“'),
(2270, 19, 20, 'Řekla: „Jak budu míti syna, když nedotkl se mne muž a nejsem nepočestnou?“'),
(2271, 19, 21, 'Řekl: „Takto pravil Pán tvůj: snadné jest mi to a učiníme jej znamením lidstvu a milosrdenstvím od nás; věc jest to rozhodnutá.“'),
(2272, 19, 22, 'I obtěžkala jím a vzdálila se k místu dalekému,'),
(2273, 19, 23, 'a přišly na ni bolesti u pně datlovníku. Řekla: „Kéž byla bych zemřela před touto chvílí a kéž byla bych zapomenuta, nadobro zapomenuta!“'),
(2274, 19, 24, 'A zavolal na ni (hlas) zpod ní: „Nermuť se, neb Pán tvůj učinil pod tebou ručej:'),
(2275, 19, 25, 'a zatřes k sobě pněm datlovníku i spadnou na tebe datle čerstvé, uzrálé:'),
(2276, 19, 26, 'i jez a pij a buď veselé mysli, a spatříš-li z lidí někoho, rci: Učinila jsem slib Milosrdnému postiti se; a nebudu mluviti dnes s nikým z lidí.“'),
(2277, 19, 27, 'I přišla pak s děckem k lidu svému, nesouc je. Řekli: „Marie, učinila\'s věru, věc podivnou.'),
(2278, 19, 28, 'Sestro Áronova, otec tvůj nebyl mužem špatným, aniž matka tvá byla nepočestná.“'),
(2279, 19, 29, 'I ukázala na děcko. Řekli: „Jak budeme mluviti s někým, jenž v kolébce jest děckem?“'),
(2280, 19, 30, 'Řekl (Ježíš): „Jáť služebník jsem Boha: on dal mi Knihu a učinil mne prorokem;'),
(2281, 19, 31, 'a učinil mne požehnaným kdekoliv byl bych: a poručil mi modlitbu a dávání poviné almužny, pokavaď budu mezi živými.'),
(2282, 19, 32, 'A (dal mi), abych úctu měl vůči matce své: aniž učinil mne zpupným zbídačelcem.'),
(2283, 19, 33, 'A (dal mi), aby pokoj byl nade mnou v den narození mého i v den smrti mé a v den, kdy vzkříšen budu mezi živými.“'),
(2284, 19, 34, 'Totoť jest Ježíš, syn Mariin: totoť jest pravdivé podání ohledně toho, v čem mají pochybnosti.'),
(2285, 19, 35, 'Nepřísluší Bohu, aby učinil sobě syna: chválen budiž! Když rozhodne o věci, řekne pouze: „Staň se!“ a stane se.'),
(2286, 19, 36, 'Bůh zajisté jest Pánem mým a Pánem vaším: pročež uctívejte jej, totoť jest stezka přímá.'),
(2287, 19, 37, 'A rozcházejí se strany mezi sebou v názoru: a běda těm, kdož neuvěřili, při spatření dne velkého!'),
(2288, 19, 38, 'Ohlašuj jim a ukazuj jim den, kdy přijdou před nás: však nepravostní dnes jsou v bloudění zjevném.'),
(2289, 19, 39, 'A varuj je přede dnem nářku, kdy rozhodnuto bude o věci, mezitím co oni jsou v lhostejnosti a nevěří.'),
(2290, 19, 40, 'Zajisté my zdědíme zemi a vše, což na ní jest, a oni k nám se navrátí.'),
(2291, 19, 41, 'A zmiň se v Knize o Abrahamovi, neboť on byl mužem pravdomluvým a prorokem.'),
(2292, 19, 42, 'Když řekl otci svému: „Otče můj, proč uctíváš to, co neslyší a nevidí, aniž prospěšno ti může býti v čem?'),
(2293, 19, 43, 'Otče můj, nyní přišlo mi vědění, které nepřišlo tobě: i následuj mne a povedu tě cestou rovnou.'),
(2294, 19, 44, 'Otče můj, neuctívej Satana, neboť Satan byl Milosrdného neposlušen.'),
(2295, 19, 45, 'Otče můj, zajisté obávám se, aby nedotkl se tě trest od Milosrdného a abys nebyl Satanovi přítelem.“'),
(2296, 19, 46, 'Řekl (otec): „Ty tedy zříkáš se božstev mých, Abrahame? Nezanecháš-li toho, dojista kamenovati tě budu! A teď odejdi ode mne na dlouho!“'),
(2297, 19, 47, 'Řekl: „Pokoj budiž s tebou: budu prositi za odpustění pro tebe Pána svého, neboť on jest ke mně laskavým:'),
(2298, 19, 48, 'a vzdaluji se vás a toho, co uctíváte vedle Boha a uctívati budu Pána svého: možná, že nebudu v modlitbě své k Pánu svému oslyšán.“'),
(2299, 19, 49, 'A když vzdálil se od nich a od toho, co uctívali vedle Pána svého, obdařili jsme jej Isákem a Jakubem: a oba učinili jsme proroky.'),
(2300, 19, 50, 'A poskytli jsme jim darů z milosrdenství svého a dali jsme jim pověst pravdy vznešenou.'),
(2301, 19, 51, 'A zmiň se v Knize o Mojžíšovi, neboť on upřímným byl a byl poslem i prorokem.'),
(2302, 19, 52, 'A volali jsme naň se strany hory Sinai pravé a způsobili jsme, aby přiblížil se k rozmluvě tajné.'),
(2303, 19, 53, 'A obdařili jsme jej z milosrdenství svého bratrem jeho Áronem, též prorokem.'),
(2304, 19, 54, 'A zmiň se v Knize o Ismaelovi, neboť on byl věrným ve slibech svých a byl poslem i prorokem.'),
(2305, 19, 55, 'A nabádal lid svůj k modlitbě a dávání povinné almužny a byl u Pána svého oblíben.'),
(2306, 19, 56, 'A zmiň se v Knize o Idrísovi, neboť on byl spravedlivým a prorokem.'),
(2307, 19, 57, 'A Pozdvihli jsme jej na místo vysoké.'),
(2308, 19, 58, 'Tito jsou, kterým požehnal Bůh z proroků z potomstva Adamova a z těch, které spasili jsme s Noemem: a z potomstva Abrahama a Israele a z těch, které vedli jsme (cestou pravou) a vyvolili jsme si. Když předčítána jim byla znamení Milosrdného, padli k zemi obráceni tvářemi svými úctou a plačíce.'),
(2309, 19, 59, 'Však po nich následovali potomci, kteří stranou odhodili modlitbu a následovali jen náruživosti své: a tito potkají jen blud,'),
(2310, 19, 60, 'vyjma těch, kteří kajícně obrátí se a uvěří a konati budou dobré skutky: tito vstoupí do zahrady (ráje) a nebudou utištěni v nejmenším:'),
(2311, 19, 61, 'do zahrad Edenu, jež slíbil Milosrdný služebníkům svým z věcí skrytých: a zajisté slib jeho naplní se.'),
(2312, 19, 62, 'V nich nebudou slyšeti ničemností, pouze „Pokoj s vámi!“ a v nich výživu budou dostávati z jitra i za večera.'),
(2313, 19, 63, 'Taková bude zahrada, jejímiž dědici učiníme ty ze služebníků našich, kteří byli bohabojní.'),
(2314, 19, 64, 'A nesestupujeme s nebe, vyjma z rozkazu Pána tvého, jemuť přináleží, což před námi jest i za námi a cožkoliv mezi tím jest: a Pán tvůj jistě není zapomětlivým.'),
(2315, 19, 65, 'Onť Pánem jest nebes i země a všeho, což mezi nimi jest: pročež uctívej jej a buď vytrvalým v uctívání jej: zdaž znáš koho, jenž byl by s ním soujmenným?'),
(2316, 19, 66, 'A dí člověk: „Zdaž poté, až zemru, vyveden budu z hrobu živým?“'),
(2317, 19, 67, 'Zdaž nepomní člověk, že stvořili jsme jej před tím, když nebyl ničím?'),
(2318, 19, 68, 'Při Pánu tvém přisahám, že dojista shromáždíme je i zlé duchy: pak předvedeme je kol pekla na kolenou.'),
(2319, 19, 69, 'Pak oddělíme od každé skupiny ty, kdož nejurputnějšími byli proti Milosrdnému v odboji:'),
(2320, 19, 70, 'a pak my nejlépe budeme věděti o těch, kdo nejvíce zaslouží si v něm sžíhání.'),
(2321, 19, 71, 'A není nikoho, jenž nevešel by do něho: to u Pána tvého jest určením rozhodnutým.'),
(2322, 19, 72, 'Poté pak spasíme ty, kdož bohabojnými byli a ponecháme nepravostné v něm klečící.'),
(2323, 19, 73, 'A když předčítána jsou jim znamení naše jasná, říkají ti, kdož neuvěřili, k těm, kdož uvěřili: „Která z obou stran jest lepší postavením a která jest lepší společností?“'),
(2324, 19, 74, 'Však kolik zahladili jsme před nimi pokolení, jež lepší byla jich bohatstvím a nádherou!'),
(2325, 19, 75, 'Rci: „Těch, kdož v bludu nalézají se, nechť prodlouží Milosrdný délku žití, až spatří to, co přislíbeno jim bylo — ať již trest v tomto životě, anebo hodinu (soudu): a tehdy seznají, kdo v horším jest postavení aneb slabším v posile:'),
(2326, 19, 76, 'však Bůh rozmnoží ty, kdož (dobře) jsou vedeni, (dobrým) vedením. a dobré skutky, jež trvají jsou lepší u Pána tvého odměnou a lepší náhradou.“'),
(2327, 19, 77, 'Zdaž viděl\'s onoho, jenž neuvěřil ve znamení naše a řekl: „Dojista dány budou mi statky i děti.“'),
(2328, 19, 78, 'Což dospěl znalosti věcí skrytých, aneb učinil s Milosrdným smlouvu?'),
(2329, 19, 79, 'Jistě, že ne: a zapíšeme si, co říká a prodloužíme mu trestu jeho délku.'),
(2330, 19, 80, 'A my zdědíme to, o čem mluví, a přijde k nám samojediný.'),
(2331, 19, 81, 'A přibrali si vedle Boha božstva, aby byla jim oporou.'),
(2332, 19, 82, 'Však ne: božstva ta zaprou uctívání jich a budou jim protivníky.'),
(2333, 19, 83, 'Zdaž neviděl\'s, že poslali jsme zlé duchy, aby dráždili je ke hříchu?'),
(2334, 19, 84, 'Nesnaž se uspíšiti (lhůtu) jejich, neb my sami spočteme (jich dní) počet.'),
(2335, 19, 85, 'V den, kdy shromáždíme bohabojné u Milosrdného s poctou:'),
(2336, 19, 86, 'a poženeme provinilce k peklu jak k napajedlu:'),
(2337, 19, 87, 'tehdy neprospěje jim přímluva, leda těm, kdož byli učinili s Milosrdným smlouvu.'),
(2338, 19, 88, 'Říkají: „Učinil sobě Milosrdný syna.“'),
(2339, 19, 89, 'Tím vyřkli jste věc úžasnou!'),
(2340, 19, 90, 'Málem nebesa by rozpoltila se z toho a roztrhla se země a hory zbortily se v kusech:'),
(2341, 19, 91, 'z toho, že připisují Milosrdnému syna:'),
(2342, 19, 92, 'když není třeba Milosrdnému, aby učinil si syna,'),
(2343, 19, 93, 'neboť vše, což na nebi jest i na zemi Milosrdnému jen co služebník se blíží:'),
(2344, 19, 94, 'zaznamenal si je všechny a spočetl (přesným) počtem:'),
(2345, 19, 95, 'a každý z nich přijde k němu v den zmrtvýchvstání, jeden za druhým.'),
(2346, 19, 96, 'Těm, kdož uvěřili a konali dobré skutky, popřeje Milosrdný lásky.'),
(2347, 19, 97, 'Zajisté pak učinili jsme jej snadným, v tvé vlastní řeči, abys radostnou zvěst věstil jím bohabojným a abys varoval jím lidi hašteřivé.'),
(2348, 19, 98, 'A kolik zahubili jsme před nimi pokolení! Zdaž pozorovat z nich můžeš koho, aneb slyšet je zašeptnouti?');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Tá. Há.'),
(2350, 20, 2, 'Neseslali jsme ti Korán, abys se trápil,'),
(2351, 20, 3, 'nýbrž napomenutím těm, kdož bojí se:'),
(2352, 20, 4, 'a sesláním od toho, jenž stvořil zemi a nebesa vysoká.'),
(2353, 20, 5, 'Milosrdný na trůn svůj se posadil:'),
(2354, 20, 6, 'jemu přináleží vše, což na nebi jest i na zemi, a cožkoliv mezi oběma jest i pod (půdou) země.'),
(2355, 20, 7, 'Jsi-li hlasitý v mluvě, zbytečno to: neb on jistě zná řeč tajnou i co jest skryto.'),
(2356, 20, 8, 'Bůh! není Boha, kromě něho: jemu přináležejí jména nejkrásnější.'),
(2357, 20, 9, 'Zdaž došly tě děje Mojžíšovy?'),
(2358, 20, 10, 'Když viděl oheň a řekl k lidu svému: „Posečkejte, neb v pravdě shlédl jsem oheň: snad budu moci přinésti vám z něho louč, aneb naleznu v tom ohni vedení.“'),
(2359, 20, 11, 'A když přišel k němu, bylo naň zvoláno: „Ó Mojžíši! —'),
(2360, 20, 12, 'Jáť zajisté jsem Pán tvůj: i sundej opánky své, neboť ve svatém\'s údolí Tuwá.'),
(2361, 20, 13, 'Jáť vyvolil si tě: i slyš, co vnuknuto ti bude:'),
(2362, 20, 14, 'jáť zajisté jsem Bůh, není boha kromě mne: pročež uctívej mne a zachovávej modlitbu, abys vzpomínal na mne!'),
(2363, 20, 15, 'Zajisté pak hodina (soudu) přijde — skorem byl bych již dal jí zjeviti se, abych odměnil každou duši dle skutků jejích.'),
(2364, 20, 16, 'Nechť neodvrátí tě od víry v ni ten, kdo v ni nevěří a následuje choutky své, neb zhynul bys.'),
(2365, 20, 17, 'A co jest to v tvé pravici, Mojžíši?“'),
(2366, 20, 18, 'Řekl: „Toť hůl má, o niž opírám se a jíž srážím dolů listí pro ovce své a ještě jinak používám jí.“'),
(2367, 20, 19, 'Řekl (Bůh): „Vrhni ji na zem Mojžíši.“'),
(2368, 20, 20, 'I vrhl ji na zem, a hle! stal se z ní had plazící se.'),
(2369, 20, 21, 'Řekl (Bůh): „Uchop ji a neboj se: my vrátíme ji v to, co byla prve.'),
(2370, 20, 22, 'A vlož ruku svou v podpaží své: když vytáhneš ji, bude bílá, beze vší bolesti: toť druhé znamení.'),
(2371, 20, 23, 'Abychom pak ukázali ti některé z našich znamení největších,'),
(2372, 20, 24, 'jdi k Faraonovi, neb jedná svévolně.“'),
(2373, 20, 25, 'Řekl Mojžíš: „Pane můj, uvolni hruď mou,'),
(2374, 20, 26, 'a ulehči mi práci mou,'),
(2375, 20, 27, 'a rozvaž uzel na jazyku mém,'),
(2376, 20, 28, 'aby chápali řeč mou:'),
(2377, 20, 29, 'a učiň mi rádce z lidu mého,'),
(2378, 20, 30, 'Árona, bratra mého:'),
(2379, 20, 31, 'opásej jím bedra má,'),
(2380, 20, 32, 'a společníkem jej učiň v práci mé,'),
(2381, 20, 33, 'abychom chváliti tě mohli často'),
(2382, 20, 34, 'a vzpomínati na tě mohli často,'),
(2383, 20, 35, 'neb ty dobře nás pozoruješ.“'),
(2384, 20, 36, 'Řekl (Bůh): „Dáno jest ti, oč žádáš, Mojžíši,'),
(2385, 20, 37, 'vždyť již dobrodiní jsme ti prokázali jindy,'),
(2386, 20, 38, 'když vnuknuli jsme matce tvé, co vnuknuto jí bylo:'),
(2387, 20, 39, ',Polož jej do, bedny a polož jej na moře: i vyvrhne jej moře na břeh, a ujme se jej nepřítel můj a nepřítel jeho.‘ A zahrnul jsem tě láskou svou, abys vychován byl pod zrakem mým.'),
(2388, 20, 40, 'I šla sestra tvá a řekla: ,Mám ukázati vám ženu, jež kojiti jej bude?‘ Tak navrátili jsme tě matce tvé, aby zaradovala se a nermoutila se. Pak zabil\'s kohosi a zachránili jsme tě z nesnáze a četnými zkoušeli jsme tě zkouškami: a pobýval\'s po léta mezi lidem Madianským: pak přišel\'s (sem z mého) určení, Mojžíši.'),
(2389, 20, 41, 'A vychoval jsem tě pro sebe:'),
(2390, 20, 42, 'jdi ty a bratr tvůj se znameními mými a neochabujte ve vzpomínání na mne.'),
(2391, 20, 43, 'Jděte oba k Faraonovi, neb jedná svévolně,'),
(2392, 20, 44, 'i mluvte k němu mluvou laskavou: snad rozmyslí si to, neb zalekne se.“'),
(2393, 20, 45, 'Řekli: „Pane náš, obáváme se, že výstřednosti dopustí se proti nám, aneb svévole.“'),
(2394, 20, 46, 'Řekl (Bůh): „Nebojte se, neboť já jsem s vámi oběma: slyším a vidím.'),
(2395, 20, 47, 'Jděte k němu a rcete: ,Myť zajisté jsme posly Pána tvého: i pošli s námi syny Israele a netrap je. Ted přišli jsme k tobě se znamením od Pána tvého a pokoj budiž nad tím, jenž následuje vedení (pravé).'),
(2396, 20, 48, 'Vnuknuto nám bylo, že zajisté trest čeká toho, jenž lháři nás nazývá a odvrací se.‘“'),
(2397, 20, 49, 'Řekl (Farao): „A kdo jest Pánem vaším, Mojžíši?“'),
(2398, 20, 50, 'Řekl: „Pánem naším jest ten, jenž každé věci dal tvar a pak vedl ji (správně).“'),
(2399, 20, 51, 'Řekl (Farao): „A jak (smýšlela) pokolení dřívější?“'),
(2400, 20, 52, 'Řekl: „Vědění o tom jest u Pána mého v Knize (věčné): nemýlí se Pán můj, aniž zapomíná.'),
(2401, 20, 53, 'Onť učinil vám zemi kolébkou a naznačil vám na ní stezky: a seslal s nebe vodu a jí vzrůsti dáváme druhům rostlin různých:'),
(2402, 20, 54, 'jezte a paste dobytčata svá! Zajisté v tomto znamení jsou těm, kdož rozumem jsou obdařeni.'),
(2403, 20, 55, 'Z ní stvořili jsme vás a do ní navrátíme vás a z ní vyjíti vám dáme opětně.“'),
(2404, 20, 56, 'A tak ukázali jsme na znamení svá veškerá: však vylhanými nazval je a odepřel jim víry.'),
(2405, 20, 57, 'Řekl: „Zdaž přišel\'s k nám, abys vypudil nás ze země naší svými kouzly, Mojžíši?'),
(2406, 20, 58, 'Dojista pak předvedeme ti kouzla stejného druhu: ustanov tedy mezi námi a sebou schůzi: nezůstaneme pozadu my, aniž ty — za okolností stejných.“'),
(2407, 20, 59, 'Řekl Mojžíš: „Schůze s vámi budiž v den zdobení a nechť shromáždí se lidé v poledne.“'),
(2408, 20, 60, 'I odešel Farao a shromáždil kouzelníky své: poté přišel.'),
(2409, 20, 61, 'I řekl jim Mojžíš: „Běda vám! Nevymýšlejte lží o Bohu, aby nezahladil vás trestem, neboť ztroskotali vždy ti, kdož tak činili.“'),
(2410, 20, 62, 'I radili se mezi sebou v rozhovoru tajném.'),
(2411, 20, 63, 'Řekli: „Tito dva jsou zajisté kouzelníky: chtějí vyhnati vás ze země vaší kouzly svými a odstraniti vaši nauku příkladnou.“'),
(2412, 20, 64, 'Řekl Mojžíš: „Shromážděte jen kouzelníky své: pak seřaďte se: a jistě dobře povede se tomu dnes, kdo vyhrá.“'),
(2413, 20, 65, 'Řekli: „Ó Mojžíši, vrhneš ty první hůl svou na zem, anebo my první ji vrhneme?“'),
(2414, 20, 66, 'Řekl Mojžíš: „Tak jest, vrhnete vy první.“ A hle, provazy a hole jejich připadaly mu následkem kouzel jejich, jako by se plazily.'),
(2415, 20, 67, 'I zachvěl se v duchu Mojžíš strachem.'),
(2416, 20, 68, 'Řekli jsme mu (tehdy): „Neboj se, neboť ty vyhráš.'),
(2417, 20, 69, 'I vrhni to, co v pravici své držíš: i požere to, co oni vyrobili, neboť co oni vyrobili, jest pouze lest kouzelníka; a nedojde dobrých výsledků kouzelník, kamkoli by přišel.“'),
(2418, 20, 70, 'I padli kouzelníci úctou na tváře své a řekli: „Uvěřili jsme v Pána Áronova a Mojžíšova.“'),
(2419, 20, 71, 'Řekl (Farao): „Zdaž uvěřili jste v něho dříve, než dovolil jsem? On zajisté jest náčelníkem vaším, který naučil vás kouzlům: pročež uřezati dám vám ruce a nohy pokřižmo, a dojista dám ukřižovati vás na pních datlovníků a tak dojista zvíte, kdo z nás dvou přísnějším jest v trestání a stálejším.“'),
(2420, 20, 72, 'Řekli: „Nebudeme vážit si tebe nad to, co přišlo nám z jasných důkazů, aneb nad toho, jenž stvořil nás: i rozsuď si, jak chceš rozsouditi, však ty souditi můžeš jen ve věcech života pozemského.'),
(2421, 20, 73, 'Myť pak zajisté uvěřili jsme v Pána svého, aby odpustiti mohl nám hříchy naše a kouzelnictví, k němuž donutil\'s nás, neboť Bůh lepším jest a stálejším.“'),
(2422, 20, 74, 'V pravdě ten, jenž přijde před Pána svého jako provinilec, schystáno má peklo: v něm ani nezemře, aniž bude žíti.'),
(2423, 20, 75, 'Ale ten, jenž přijde k němu co věřící, konavší dobré skutky pro tyto připraveny jsou stupně nejvyšší:'),
(2424, 20, 76, 'zahrady Edenu, pod nimiž řeky tekou, v nichž přebývati budou věčně: toto odměna bude bezúhonných.“'),
(2425, 20, 77, 'A pak vnuknuli jsme Mojžíšovi: „Vyjdi ven se služebníky mými a proraz jim stezku v moři suchou: neboj se dostihnutí a nestrachuj se.“'),
(2426, 20, 78, 'I pronásledoval je Farao s vojsky svými a zaplavila je moře záplava:'),
(2427, 20, 79, 'tak zavedl Farao lid svůj v blud a neuvedl je na stezku (pravou).'),
(2428, 20, 80, 'Synové Israele, tak zachránili jsme vás před nepřáteli vašimi a ujednali jsme s vámi schůzku na úbočí hory Sinai pravém a seslali jsme vám mannu a křepelky:'),
(2429, 20, 81, 'požívejte z dobrých věcí, které potravou dali jsme vám však nebuďte v tom svévolnými, aby nepadl na vás hněv můj: neboť ten, na něhož padne hněv můj, zhyne.'),
(2430, 20, 82, 'Ale zajisté velkým jsem v odpouštění k tomu, jenž kajícně obrací se ke mně a věří a koná dobré skutky a tím dobře jest veden.'),
(2431, 20, 83, '„Však co uspíšilo tvé oddálení se od lidu tvého, Mojžíši?“'),
(2432, 20, 84, 'Řekl: „Oni jsou mi těsně v patách i přispěchal jsem k tobě, Pane můj, pro tvé zalíbení.“'),
(2433, 20, 85, 'Řekl (Bůh): „Zajistéť na zkoušku postavili jsme lid tvůj po odchodu tvém: i v blud zavedl je As-Sámirí.“'),
(2434, 20, 86, 'I vrátil se Mojžíš k lidu svému rozlícen, zkormoucen:'),
(2435, 20, 87, 'řekl: „Lide můj, zdaž neslíbil vám Pán vás slib krásný? Či dlouhou již zdála se vám býti smlouva s Bohem, aneb chtěli jste, aby padl na vás hněv Pána vašeho proto, že nedodrželi jste slibu mně učiněného?“'),
(2436, 20, 88, 'Řekli, „Nedodrželi jsme slibu ti učiněného nikoliv z našeho popudu, nýbrž přinuceni byli jsme, abychom snesli náklady šperků lidu a naházeli je v kupu, a tak vmetl je v oheň As-Sámirí. A vytáhl z něho pro ně tele ztělesněné, bučící, i řekli: „Totoť jest Bůh váš a Bůh Mojžíše, na kterého on zapomněl.“'),
(2437, 20, 89, 'Což neseděli že neodvětilo jim mluvou, a že nemohlo jim způsobiti ani škody, ani prospěchu?'),
(2438, 20, 90, 'Vždyť Áron jim byl již řekl dříve: „Lide můj, pouze na zkoušku jste postaveni jím: a Pánem vaším jest Milosrdný, pročež následujte mne a poslouchejte rozkazy mé.“'),
(2439, 20, 91, 'Řekli: „Nepřestaneme uctívati je, dokud nenavrátí se Mojžíš.“'),
(2440, 20, 92, 'Mojžíš řekl: „Ó Árone, co zbraňovalo ti, když viděls s je v bludu,'),
(2441, 20, 93, 'abys mne následoval? Což neposlušen byl\'s rozkazu mého?“'),
(2442, 20, 94, 'Řekl: „Synu matky mé, necuchej vous můj a hlavu mou! Zajistéť obával jsem se, abys neřekl: „Rozštěpil jsi syny Israele a nedával\'s pozor na nařízení mé.“'),
(2443, 20, 95, 'A jak jest to s tebou, As-Sámirí?“'),
(2444, 20, 96, 'Řekl: „Viděl jsem, co oni neviděli: i uchopil jsem hrst prachu ze šlépěje poslovy a vysypal jsem jej do telete: a takto ponukla mne duše má.“'),
(2445, 20, 97, 'Řekl Mojžíš: „Odejdiž: a (trestem ti) v tomto životě budiž, abys říkal,,Nedotýkejte se mne!\' A zajisté bude ti dostáti (tak) slibu, kterého nebudeš moci nedodržeti. A pohleď na boha svého jemuž nepřestal\'s býti oddán: myť dojista spálíme ho a do moře rozprášíme jako prach.'),
(2446, 20, 98, 'Bohem vaším jest pouze Bůh (jediný), není boha kromě něho: onť zahrnuje všechny věci ve vědění svém.“'),
(2447, 20, 99, 'Takovýmto způsobem vyprávíme ti děje minulé a dali jsme ti od sebe varování.'),
(2448, 20, 100, 'Kdokoli odvrátí se od něho, ponese v den zmrtvýchvstání břímě,'),
(2449, 20, 101, 'jež ponesou věčně a špatně ponese se jim v den zmrtvýchvstání:'),
(2450, 20, 102, 'v den, kdy zatroubeno bude na roh a shromáždíme provinilce v den onen, osleplé:'),
(2451, 20, 103, 'budou šeptati si navzájem mezi sebou: „Dleli jste na zemi jen deset dní.“'),
(2452, 20, 104, 'My nejlépe však budeme věděti, co říci budou chtíti nejpříkladnější z nich, když řeknou: „Dleli jste na ní pouze jeden den.“'),
(2453, 20, 105, 'A dotazovati budou se tě ohledně hor; i rci: „Rozpráší je Pán můj v prach'),
(2454, 20, 106, 'a zůstaví je jako pláně srovnané:'),
(2455, 20, 107, 'nespatříš v nich záhybu ni vlnitosti.'),
(2456, 20, 108, 'V den onen následovati budou toho, jenž zavolá je a bez zatáček půjde vpřed: a poníží se hlasy před Milosrdným a slyšány budou jen kroky jejich.'),
(2457, 20, 109, 'V den onen neprospěje přímluva, leda toho, jemuž dovolí to Milosrdný a schválí, aby mluvil.'),
(2458, 20, 110, 'Onť ví, co před nimi jest a co za nimi: však oni nemohou obsáhnouti toho ve vědění svém.'),
(2459, 20, 111, 'A poníženy budou tváře před Živým a Stálým a ztroskotá ten, jenž obtížen bude nepravostí.'),
(2460, 20, 112, 'Však kdo konal dobré skutky a byl věřícím, tomu nebude třeba báti se ukřivdění, aniž ztráty.'),
(2461, 20, 113, 'Takovýmto způsobem seslali jsme jej jako Korán arabský a rozmanité rozsili v něm hrozby: aby báli se Boha, a aby vzbudil v nich vzpomínání.'),
(2462, 20, 114, 'Pročež povýšen budiž, Bůh, Král, Pravda! Nespěchej s Koránem dříve než dokončeno ti bude vnuknutí jeho a rci: „Pane můj, rozmnož mne věděním!“'),
(2463, 20, 115, 'Kdysi učinili jsme již smlouvu s Adamem, však zapomněl ji a nenašli jsme v něm (pevného) rozhodnutí.'),
(2464, 20, 116, 'A když řekli jsme andělům: „Padněte před Adamem na tváře své,“ učinili tak všichni, vyjma Iblíse, jenž se vzepřel.'),
(2465, 20, 117, 'I řekli jsme Adamovi: „Adame, totoť nepřítel jest tvůj a ženy tvé: nechť tedy nevypudí vás ze zahrady ráje a nestanete se zbědovanými.'),
(2466, 20, 118, 'Zajisté (dáno) jest ti, abys nehladověl v ní, aniž chodil nahý:'),
(2467, 20, 119, 'a abys nežíznil v ní, aniž trpěl horkem.“'),
(2468, 20, 120, 'I našeptal mu Satan, řka: „Adame, zdaž ukáži ti strom věčného života a panství, jež nikdy nepomine?“'),
(2469, 20, 121, 'I pojedli z něho oba a zjevila se jim nahota jejich: a počali sešívati si krytbu na sebe z listí zahrady: tak neposlušen byl Adam Pána svého a byl zváben.'),
(2470, 20, 122, 'Poté pak vyvolil si jej Pán jeho a popřál sluchu pokání jeho a uvedl jej (na cestu pravou).'),
(2471, 20, 123, 'Řekl: „Ven s vámi ze zahrady se všemi: budete navzájem sobě nepřáteli! A když přijde vám ode mne vedení, tehdy kdož následovati budou vedení mé, nezbloudí, aniž bude zbědovaný.'),
(2472, 20, 124, 'A kdo odvrátí se od napomenutí mého, ten bude míti život ztísněný: a předvedeme k sobě jej v den zmrtvýchvstání slepým.“'),
(2473, 20, 125, 'Řekne: „Pane můj, proč předvedl\'s mne k sobě slepým, když před tím byl jsem vidoucím?“'),
(2474, 20, 126, 'Řekne Bůh: „Jakož přišla ti znamení naše a zapomněl\'s je: taktéž dnes zapomenut jseš.“'),
(2475, 20, 127, 'A takto odměníme každého, kdož přestoupil (meze) a neuvěřil ve znamení Pána svého: a zajisté trest v životě budoucím přísnějším bude a stálejším.'),
(2476, 20, 128, 'Zdaž nepozorují kolik zahladili jsme před nimi pokolení, v jichž obýváních prochází se nyní? Zajisté v tom znamení jsou rozvážným.'),
(2477, 20, 129, 'Nebýti slova předem vysloveného Pánem tvým, byl by uskutečnil se již trest jejich: však lhůta jeho, jest předurčená.'),
(2478, 20, 130, 'Měj strpení s tím, co mluví a vyvyšuj Pána svého ve chvále před východem slunce i před západem jeho a ve chvílích příchodu noci: a na obou koncích dne, abys zalíbil se mu.'),
(2479, 20, 131, 'A nespočívej dlouho zrakem na tom, čeho dočasně popřáli jsme několika párům z nich — marností života pozemského — abychom na zkoušku postavili je v tom: však úděl daný ti Pánem tvým lepším bude a stálejším.'),
(2480, 20, 132, 'Nakazuj modlitbu rodině své a vytrvávej v ní. My nežádáme od výživy od tebe, nýbrž my tebe živíme: a k (dobrému) konci dovede bázeň boží.'),
(2481, 20, 133, 'A říkají: „Nepředvede-li nám znamení od Pána svého, (neuvěříme). Což nepřišel jim jasný důkaz o tom co psáno jest v knihách předešlých?'),
(2482, 20, 134, 'A kdybychom byli zahladili je trestem před ním, byli by řekli „Pane náš, proč neposlal\'s nám proroka, abychom následovati byli mohli znamení tvá dříve, než potupa a pohana nás stihla?“'),
(2483, 20, 135, 'Rci: „Všichni (pilně) vyčkávejmež konce: vyčkávejte a shledáte, kdo z nás jest na stezce rovné a kdo dobře jest veden!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Přiblížilo se lidem súčtování s nimi, mezitím co v lhostejnosti odvracejí se.'),
(2485, 21, 2, 'Nikdy nepřijde jim napomenutí nové od Pána jejich, aniž by naslouchali mu jen jako ve hře:'),
(2486, 21, 3, 'výsměšná jsou srdce jejich a tajně hovoří ti, kdož nepravostni jsou: „Zdaž není člověkem jako vy? Zdaž připojíte se k pouhým čárům vidomě?“'),
(2487, 21, 4, 'Řekl (prorok): „Pán můj zná, co mluveno jest na nebi i na zemi: onť vše slyší a ví.“'),
(2488, 21, 5, '„Naopak,“ říkají, „spleteniny snů jsou to; naopak, vymyslil si to; naopak, jest básníkem: nechť tedy předvede nám znamení, s jakými byli posláni proroci předešlí!“'),
(2489, 21, 6, 'Neuvěřilo před nimi nikteré z měst, která zahladili jsme: jakž tedy oni uvěří?'),
(2490, 21, 7, 'A neposlali jsme před tebou, leda muže, jimž vnukli jsme zjevení: tažte se jen oněch, jimž dáno bylo napomenutí, nevíte-li!'),
(2491, 21, 8, 'A nedali jsme jim těl, která by nebyla nucena jísti pokrmu a nebyli nesmrtelnými.'),
(2492, 21, 9, 'Poté pak dodrželi jsme pravdivě slib jim učiněný a zachránili jsme je a koho jsme chtěli a zahladili jsme výstředníky.'),
(2493, 21, 10, 'A nyní seslali jsme vám Knihu, v níž napomenutí jest vám: což nepochopíte?'),
(2494, 21, 11, 'A kolik vyvrátili jsme měst, jež nepravostna byla a dali vzejíti po nich lidem jinými!'),
(2495, 21, 12, 'A když pocítili hněv náš, hle, dali se z města na útěk.'),
(2496, 21, 13, '„Neutíkejte: navraťte se k výstřednostem svým a obydlím svým: snad budete dotazováni.“'),
(2497, 21, 14, 'Řekli: „Běda nám! zajisté byli jsme nepravostni.“'),
(2498, 21, 15, 'A neustalo toto volání jejich, dokud neučinili jsme je jako obilí pokosené, bezživotné.'),
(2499, 21, 16, 'A nestvořili jsme nebe a zemi a cožkoliv mezi nimi jest, sobě pro zábavu.'),
(2500, 21, 17, 'A kdybychom byli chtěli učiniti si kratochvíli, byli bychom si ji našli u sebe, kdybychom tak vskutku byli chtěli učiniti.'),
(2501, 21, 18, 'Naopak, mrštíme pravdou na nepravdu a rozdrtí ji a hle, rozptýlí se (ničemnost): však běda vám za to, co vykládáte (o Bohu)!'),
(2502, 21, 19, 'Jemu přináleží cožkoliv na nebi jest a na zemi: a ti, kdož u něho jsou, nejsou nijak pyšni, aby mu nesloužili, aniž unaveni.'),
(2503, 21, 20, 'Chválí jej nocí i dnem a nevymýšlejí ničeho o něm.'),
(2504, 21, 21, 'Že vzali si ze země božstva, která křísí mrtvé?'),
(2505, 21, 22, 'Kdyby na nebi a na zemi byla božstva jiná, kromě Boha, bylo by obé zkáze propadlo: však sláva Bohu, Pánu trůnu, nade vše, co oni vykládají!'),
(2506, 21, 23, 'Onť nebude tázán na skutky své, však oni tázáni budou.'),
(2507, 21, 24, 'Že vzali si božstva (jiná) vedle Boha? Rci: „Přineste důkaz svůj!“ Toto jest napomenutí těch, kdož jsou se mnou a těch, kdož byli přede mnou: však většina lidí nepoznává pravdu, nýbrž odvracejí se.'),
(2508, 21, 25, 'Neposlali jsme před tebou proroka, jemuž bychom nebyli vnukli, že není Boha, kromě mne: mne tedy uctívejte.'),
(2509, 21, 26, 'A říkají: „Učinil sobě Milosrdný syna.“ Při slávě jeho, nikoliv! Jsou jen sluhy jeho ctěnými.'),
(2510, 21, 27, 'Nepředcházejí jej v mluvení, nýbrž rozkazy jeho vykonávají.'),
(2511, 21, 28, 'Onť ví, co před nimi jest i za nimi, a nemohou přimlouvati se, leda za toho, jemuž on popřeje svého zalíbení: a oni bázní před ním třesou se.'),
(2512, 21, 29, 'A kterýkoli z nich by řekl: „Jáť Bůh jsem vedle něho,“ toho odměníme peklem: neboť takto odměňujeme nepravostné.'),
(2513, 21, 30, 'Což nevidí ti, kdož neuvěřili, že nebesa a země byly pevným celkem a že rozdělili jsme je a z vody že učinili jsme veškerou věc živoucí? Což neuvěří?'),
(2514, 21, 31, 'A učinili jsme na zemi vrchole hor, aby nepohnula se pod nimi a učinili jsme na ní průchody jako stezky, aby (lidé) jimi byli vedeni.'),
(2515, 21, 32, 'A učinili jsme nebesa střechou chráněnou: a přece od znamení jeho se odvracejí!'),
(2516, 21, 33, 'Onť jest to, který stvořil noc a den, a slunce a měsíc, oba po obloze svou dráhou spějí.'),
(2517, 21, 34, 'A neučinili jsme žádného člověka před tebou nesmrtelným: a zemřeš-li ty, zdaž oni žíti budou věčně?'),
(2518, 21, 35, 'Každá duše okusí smrti: a vyzkoušíme vás zlem i dobrem pokušeními: a k nám budete navráceni.'),
(2519, 21, 36, 'A když shlédnou tě ti, kteří neuvěřili, tropí si z tebe jen šašky, (řkouce): „Zdaž tento jest to, jenž zmiňuje se vám (nehezky) o božstvech vašich?“ A ve vzpomínání na Milosrdného oni nevěří.'),
(2520, 21, 37, 'Stvořen jest člověk z pospíchání; ukáži vám jistě znamení svá; ale nepospíchejte na mne!“'),
(2521, 21, 38, 'Říkají: „Kdy splní se ona hrozba — jste-li pravdomluvnými?“'),
(2522, 21, 39, 'Kdyby jen věděli ti, kdož nevěří, kdy nebudou s to, aby zdrželi od tváří svých oheň, aniž od zad svých a kdy nebude jim pomoženo!'),
(2523, 21, 40, 'Ano, (oheň) přijde na ně z nenadání a zpitomí je a nebudou s to, aby zdrželi jej, aniž bude jim posečkáno.'),
(2524, 21, 41, 'Posměch tropili si lidé z proroků již před tebou, však obklopilo ty, kdož posmívali se, to, z čeho si byli posměch tropili.'),
(2525, 21, 42, 'Rci: „Kdo ochrání vás, za noci a za dne, před Milosrdným?“ A přece od vzpomínání na Pána svého se odvracejí.'),
(2526, 21, 43, 'Což mají nějaká božstva, jež před námi mohou je ochrániti? Nejsou s to, aby sama sobě pomohla, pročež nebudou pomáhati jim proti nám.'),
(2527, 21, 44, 'Ano, dali jsme jim a otcům jejich užívati světa, pokud potrvaly životy jejich: což nevidí, že přicházíme do země jejich a zúžujeme hranice její. Jsou snad oni vítězi?'),
(2528, 21, 45, 'Rci: „Jáť pouze varuji vás tím, co vnuknuto mi bylo:“ však hluší neslyší volání, když varováni jsou.'),
(2529, 21, 46, 'Však když dotkne se jich dechnutí trestu Pána tvého, říkají: „Běda nám, neb zajisté byli jsme nepravostni.“'),
(2530, 21, 47, 'Postavíme váhu spravedlivou v den zmrtvýchvstání a nebude zkrácena žádná duše v ničem: a i kdyby jednalo se o váhu zrna hořčičného, přineseme je: dostačí, že my jsme súčtovateli.'),
(2531, 21, 48, 'Kdysi dali jsme Mojžíšovi a Áronovi Rozlišení a světlo a napomenutí bohabojným,'),
(2532, 21, 49, 'již bojí se Pána svého v skrytosti a před hodinou (soudu) třesou se.'),
(2533, 21, 50, 'A toto jest Napomenutí požehnané, seslané námi: což zapřete je?'),
(2534, 21, 51, 'Kdysi dali jsme i Abrahamovi jeho (správné) vedení, neb věděli jsme o něm,'),
(2535, 21, 52, 'když řekl otci svému a lidu svému: „Co jsou tyto představy, jež uctíváte?“'),
(2536, 21, 53, 'Řekli: „Nalezli jsme otce naše v uctívání jich.“'),
(2537, 21, 54, 'Řekl: „Zajistéť vy i otcové vaši jste v bludu zjevném.“'),
(2538, 21, 55, 'Řekli: „Přišel\'s k nám s pravdou, aneb jen kratochvíli si tropíš?“'),
(2539, 21, 56, 'Řekl: „Nikoliv: Pánem vaším jest Pán nebes a země, jenž stvořil je oboje, a já jsem z těch, kdož to dosvědčují.'),
(2540, 21, 57, 'A při Bohu (přisahám), že dojista lest učiním modlám vašim poté, až odvrátíte se a záda mi ukážete.“'),
(2541, 21, 58, 'A rozbil je na kousky všechny, vyjma největší z nich, aby k ní vrátiti se mohli.'),
(2542, 21, 59, 'Řekli: „Kdo učinil toto s bohy našimi? Zajistéť jest jedním z nepravostných.“'),
(2543, 21, 60, 'Řekli: „Slyšeli jsme mladíka kéhos zmiňovati se o nich: jménem Abrahama.“'),
(2544, 21, 61, 'Řekli: „Nuž, předveďte jej před zraky lidu, aby svědčili proti němu,“'),
(2545, 21, 62, 'Řekli: „Zdaž ty učinil\'s to s bohy našimi, Abrahamem?“'),
(2546, 21, 63, 'Řekl: „Nikoliv; učinil to tenhle největší z nich: i tažte se jich, dovedou-li mluviti.“'),
(2547, 21, 64, 'I šli do sebe v myšlení svém a řekli: „Zajisté, přátelé, že jste nepravostní.“'),
(2548, 21, 65, 'Však poté zatvrdili se v hlavách svých, řkouce: „Však dobře\'s věděl, že oni nemluví.“'),
(2549, 21, 66, 'Řekl: „Zdaž tedy uctívati budete vedle Boha něco, co nemůže prospěti vám, aniž uškoditi?'),
(2550, 21, 67, 'Pfuj na vás a na to, co uctíváte vedle Boha! Což nepochopíte?“'),
(2551, 21, 68, 'Řekli: „Upalte jej a jděte na pomoc bohům svým, chcete-li mít se k činu!“'),
(2552, 21, 69, 'Řekli jsme: „Ohni, budiž studeným a bezpečným vůči Abrahamovi!“'),
(2553, 21, 70, 'I chtěli lest provésti mu, však uvedli jsme je v záhubu.'),
(2554, 21, 71, 'A zachránili jsme jej a Lota, zavedše je do země, v níž požehnali jsme veškerenstvu.'),
(2555, 21, 72, 'A dali jsme mu Isáka a Jakuba nádavkem a všechny učinili jsme muži spravedlivými.'),
(2556, 21, 73, 'A učinili jsme je představenými, aby vedli lid podle nařízení našeho, a vnukli jsme jim konání dobrých skutků, a zachovávání modlitby, a dávání povinné almužny: a oni uctívali nás.'),
(2557, 21, 74, 'A Lotovi dali jsme soudnost a vědění: a zachránili jsme jej z města, jež konalo špatnosti: zajisté byl to lid zlý, zkažený.'),
(2558, 21, 75, 'A uvedli jsme jej do milosrdenství svého, neboť byl jedním ze spravedlivých.'),
(2559, 21, 76, 'A (pomni) Noema, když kdysi volal k nám i vyslyšeli jsme jej a zachránili jsme jej a rodinu jeho od zkázy velké.'),
(2560, 21, 77, 'A pomohli jsme mu před lidem, který vylhanými nazýval znamení naše: oni zajistéť byli lidem zlým i utopili jsme je všechny.'),
(2561, 21, 78, 'A (pomni) Davida a Šalamouna, když dali rozsudek ohledně role, v němž škodu způsobila stáda ovcí jistého lidu: a byli jsme rozsudky jejich svědky.'),
(2562, 21, 79, 'A dali jsme Šalamounovi rozšafnost v této věci: a oběma jim dali jsme soudnost a vědění a přinutili jsme hory, aby s Davidem pěly chválu naši a taktéž i ptactvo. Tak učinili jsme.'),
(2563, 21, 80, 'A naučili jsme ho řemeslu výroby brnění pro vás na ochranu proti (vzájemné) urputnosti vaší: zdaž budete za to vděčni?'),
(2564, 21, 81, 'A Šalamounovi (podmanili jsme) vichr bouřný, aby hnal lodi dle rozkazu jeho k zemi, jíž požehnali jsme: neboť o všech věcech měli jsme vědomost.'),
(2565, 21, 82, 'A některé zlé duchy, aby potápěli se proň a vykonávali proň různé práce jiné: a my střežili jsme je.'),
(2566, 21, 83, 'A (pomni) Joba, když volal k Pánu svému: „Zajisté protivenství dotklo se mne: a ty nejslitovnějším jsi ze slitovných.“'),
(2567, 21, 84, 'A vyslyšeli jsme jej a zbavili jsme jej protivenství: a vrátili jsme mu rodinu jeho a přidali jsme mu ještě jednu stejnou, jakožto milosrdenství od nás a na paměť těm, kdož uctívají nás.'),
(2568, 21, 85, 'A (pomni) Ismaela a Idríse a Dzu-l-Kifla: ti všichni byli (vytrvalí) v trpělivosti.'),
(2569, 21, 86, 'A uvedli jsme je do milosrdenství svého, neboť byli ze spravedlivých.'),
(2570, 21, 87, 'A Dzu-n-Núna, když odešel rozzloben a domníval se, že nemám moci nad ním: však v temnotách volal: „Není Boha, kromě tebe, chvála budiž tobě: jáť zajisté byl jsem z nepravostných.“'),
(2571, 21, 88, 'A vyslyšeli jsme jej a zachránili z útrap; neboť takovýmto způsobem zachraňujeme věřící.'),
(2572, 21, 89, 'A Zachariáše, když volal k Pánu svému: „Pane můj, nezanech mne samotného, ač ty nejlepším jsi ze všech dědiců.“'),
(2573, 21, 90, 'I vyslyšeli jsme jej a dali jsme mu Jana a způsobilou učinili jsme ženu jeho, neboť oni předstihovali se v konání dobrých skutků a modlili se k nám s láskou a bázní a ponižovali se před námi.'),
(2574, 21, 91, 'A (pomni též) onu, jež zachovala si panenství své a do níž vdechli jsme ducha svého: a učinili jsme ji, jakož i syna jejího znamením veškerenstvu.'),
(2575, 21, 92, 'Zajisté tento národ váš jest národem jedním: a já jsem Pánem vaším: pročež mne uctívejte!'),
(2576, 21, 93, 'Však rozštěpili se v této věci mezi sebou: nicméně všichni k nám navrátí se.'),
(2577, 21, 94, 'A kdožkoli koná dobré skutky a jest věřícím, tomu nebude upřeno snažení jeho: myť zajisté vše mu zapisujeme.'),
(2578, 21, 95, 'A klatba jest na každém městě, jež zahladili jsme a dojista nenavrátí se,'),
(2579, 21, 96, 'dokud nebude otevřeno lidu Jádžúdž a Mádžúdž a tento se všech úbočí hor nepovalí se:'),
(2580, 21, 97, 'tehdy přiblíží se naplnění slibu pravdivého a tehdy údivem pouliti se budou zraky těch, kdož neuvěřili: „Běda nám, že lhostejni jsme byli k této (hrozbě): ano, byli jsme nepravostni!“'),
(2581, 21, 98, 'Zajistéť pak vy a to, co uctívali jste vedle Boha, palivem bude pekla a vy do něho půjdete.'),
(2582, 21, 99, 'Kdyby to byli skuteční bohové, nešli by do něho: však nyní všichni věčně budou v něm přebývati.'),
(2583, 21, 100, 'V něm sténati budou, však hlas jich v něm nebude slyšán.'),
(2584, 21, 101, 'Ale ti, jimž ku předu slíbili jsme krásnou odměnu, daleko od něho vzdáleni budou.'),
(2585, 21, 102, 'Nebudou slyšeti ani šeptnutí z něho a v tom, Čeho přály si duše jejich, na věk trvati budou.'),
(2586, 21, 103, 'Nebude trápiti je hrůza ona velká a vstříc vycházeti budou jim andělé, (řkouce): „Totoť jest den váš: ten, jenž slíben byl vám!“'),
(2587, 21, 104, 'V den onen sbalíme nebesa, jako Sidží sbaluje závitky knih: a jakož dali jsme začátkem prvnímu stvoření, tak navrátíme je zpět. Tento slib jest závazným pro nás a my zajisté tak učiníme.'),
(2588, 21, 105, 'A již napsali jsme ve zpěvech (žalmů) napomenutí: „Zajistéť zemi zdědí služebníci moji spravedliví.“'),
(2589, 21, 106, 'Zajisté pak v tomto (rčení) jest hlásání lidu (Bohu) sloužícímu.'),
(2590, 21, 107, 'A poslali jsme tě jen jako milosrdenství veškerenstvu.'),
(2591, 21, 108, 'Rci: „Bylo mi vnuknuto jen, že Bůh váš jest pouze Bůh Jediný: zdaž tedy odevzdáváte se do vůle jehož?“'),
(2592, 21, 109, 'Však obracejí-li se zády, rci: „Varoval jsem vás všechny stejně, však nevím, blízko-li či daleko jest to, co slíbeno jest vám.“'),
(2593, 21, 110, 'Bůh pak zajisté dobře ví, co nahlas mluvíte i co skrýváte.'),
(2594, 21, 111, 'A nevím, zda snad (odklad trestu) jest pouze pro vyzkoušení vás a popřání vám radostí světa do jisté lhůty.'),
(2595, 21, 112, 'Řekl (prorok): „Pane můj, rozsuď dle pravdy!“ a Pán náš, Milosrdný, má býti vzýván spíše než to, co vy vykládáte!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Ó lidé, bojte se Pána svého, neboť otřesy hodiny (soudu) budou věcí úžasnou.'),
(2597, 22, 2, 'V den, kdy spatříte ji, každá kojitelka upustí kojence svého a každá žena obtěžkaná upustí břímě své: a spatříš lidi jako opilé a nebudou opilí: však trest boží přísný jest.'),
(2598, 22, 3, 'A jsou někteří mezi lidmi, kteří hádají se o Bohu, aniž by měli jakých vědomostí a následují každého Satana odbojného.'),
(2599, 22, 4, 'Předepsáno jest Satanu, aby každého, jenž za zachránce si jej vezme, v blud zavedl a dovedl jej k trestu plamene.'),
(2600, 22, 5, 'Ó lidé, jste-li v pochybnostech ohledně zmrtvýchvstání, (uvažte), že stvořili jsme vás z prachu, pak ze semene, pak ze sražené krve, pak z kousku masa buď ve tvaru aneb beztvárného, abychom dali vám důkazy (moci své). A ponecháváme v lůnech cokoliv chceme až do lhůty předurčené: pak necháme vás vyjíti jako kojence: pak dáme vám dosíci věku dospělosti: a někteří z vás zemřou, jiní pak dojdou věku sešlosti takové, že nevědí ničeho z toho, co byli před tím věděli. A vidíš zemi vyschlou: však jakmile seslali jsme na ni vodu, vzpružila se a zbobtnala: a vzrostl na ní veškerý druh rostlin rozkošných.'),
(2601, 22, 6, 'To proto, že Bůh zajisté jest pravdou (samou) a proto, že obživuje mrtvé a nade všemi věcmi moc má;'),
(2602, 22, 7, 'a že hodina (soudu) jistě přijde; není o ní pochyby; a že Bůh vzkřísí mrtvé z hrobu.'),
(2603, 22, 8, 'A jest mezi lidmi jeden, jenž hádá se ohledně Boha, aniž by měl o tom jakých vědomostí, a aniž by veden byl Knihou osvícenou.'),
(2604, 22, 9, 'Pyšně odvrací se, aby zavedl druhé od stezky boží: tomu v životě pozemském potupa bude údělem a v den zmrtvýchvstání okusiti dáme mu trestu sžíhání:'),
(2605, 22, 10, '„Toto vezmi za to, co kupředu poslaly si ruce tvé: a Bůh nezkracuje služebníků svých!“'),
(2606, 22, 11, 'A jest mezi lidmi takový, jenž slouží Bohu ramenářsky: když postižen jest dobrem, jest spokojen: však když postižen jest zkouškou, obrátí se čelem vzad. Takto ztrácí život pozemský i život budoucí: toť zajisté ztráta jest zjevná.'),
(2607, 22, 12, 'Vzývá vedle Boha, co nemůže uškoditi mu, aniž prospěti: toť zajisté zbloudění jest daleké.'),
(2608, 22, 13, 'Vzývá toho, jenž spíše mu uškodí, než prospěje: špatný to pán a špatný to společník!'),
(2609, 22, 14, 'Zajisté pak uvede Bůh ty, kdož uvěřili a konali dobré skutky, do zahrad, pod nimiž řeky tekou, neboť Bůh zajisté činí, co chce.'),
(2610, 22, 15, 'Kdožkoli domnívá se, ze Bůh nepomůže prorokovi v životě pozemském i budoucím, nechť natáhne provaz k nebi, a pak nechť uřízne jej: pak nechť dává pozor, odstraní-li lest jeho to, nač se zlobí:'),
(2611, 22, 16, 'Takovýmto způsobem seslali jsme jej jako znamení jasná a Bůh vede (cestou přímou), koho chce.'),
(2612, 22, 17, 'Zajisté ti, kdož uvěřili a Židé a sabejští a křesťané a mágové a ti, kdož Bohu dávají společníky, shledají, že Bůh rozhodne mezi nimi v den zmrtvýchvstání: neboť Bůh věcí všechněch jest svědkem.'),
(2613, 22, 18, 'Což nevidíš, že Bohu klaní se cožkoli na nebi jest i na zemi? a slunce a měsíc a hory a stromy a zvířata a většina lidí? Však mnohým již přisouzen byl trest: a koho Bůh potupý toho nikdo nemůže poctíti: Bůh pak zajisté činí, co chce.'),
(2614, 22, 19, 'Tito jsou oni dva soupeři, kteří přou se ohledně Pána svého těm pak, kdož neuvěřili, střižen jest oděv z ohně a lita bude na hlavy jejich voda vroucí.'),
(2615, 22, 20, 'Rozleptáno bude jí, cožkoliv v útrobách jejich jest i kůže:'),
(2616, 22, 21, 'a přichystány jsou pro ně obušky železné.'),
(2617, 22, 22, 'Pokaždé, když chtít budou vyjíti, mukami dohnáni, z pekla, navráceni tam budou (s voláním): „Okuste trestu sžíhání!“'),
(2618, 22, 23, 'Zajisté pak uvede Bůh ty, kdož uvěřili, a konali dobré skutky do zahrad, pod nimiž řeky tekou: ozdobeni v nich budou náramky zlatými a perlami a oděv jich tam bude hedvábný.'),
(2619, 22, 24, 'A vedeni budou k dobré mluvě a vedeni budou ke stezce ctné.'),
(2620, 22, 25, 'Těm pak, kdož neuvěřili, a odvracejí druhé od stezky boží a od modlitebnice posvátné, kterou učinili jsme pro (všechny) lidi: stejně pro ty, kdož v ní obývají, jakož i venkovské: a těm, kdož chtějí znesvětiti ji ze svévole, okusit dáme trestu bolestného.'),
(2621, 22, 26, 'A (pomni), když naznačili jsme Abrahamovi místo domu posvátného, (řkouce): „Nespolčuj se mnou ničeho, ale očišťuj dům můj pro ty, kdož obcházeti jej budou v průvodu, a kdož konati v něm budou modlitbu v stoje, neb v kleče, neb padše na tváře své.“'),
(2622, 22, 27, 'A ohlašuj lidu POUŤ: nechť přijdou k tobě pěšky, neb na velbloudech bystrých, přicházejíce ze všech průsmyků hlubokých:'),
(2623, 22, 28, 'aby dosvědčili dobrodiní poskytnutá jim a aby vyslovovali jméno Boha po určitý počet dnů nad tím, co výživou dal jim ze zvířat a dobytčat: jezte z nich a nakrmte i nuzné a chudé.'),
(2624, 22, 29, 'Pak nechť dovrší nedbalost svou a vyplní sliby své a obejdou v průvodu dům starobylý.'),
(2625, 22, 30, 'Tak (čiňte). A kdožkoli ve velké úctě bude míti své zápovědi Boha, bude to lepším proň u Pána jeho. Dovoleno jest vám maso dobytčat, vyjma dříve vám vyjmenovaných: straňte se pak poskvrny model a vyhýbejte se mluvě lživé.'),
(2626, 22, 31, 'Upřímní (buďte) k Bohu (ve víře), nespolčujíce s ním ničeho, neboť kdožkoli spolčuje co s Bohem, jako by spadl s oblak a schvátil jej pták neb odvál jej vítr do končin dálných.'),
(2627, 22, 32, 'Tak (čiňte). A kdožkoli velké učiní oběti boží, přichází to z bohabojnosti srdcí.'),
(2628, 22, 33, 'Budete míti užitek (z dobytčat obětních) až do určité lhůty: poté pak místo obětování jich bude v domě starobylém.'),
(2629, 22, 34, 'Každému národu ustanovili jsme obřady jeho, aby vyslovoval jméno Boha nad tím, čímž k výživě je obdařil v němé tváři — a Bůh váš jest Bůh Jediný: pročež do jeho vůle odevzdejte se a radostnou zvěst věstiž poníženým,'),
(2630, 22, 35, 'kteří, když vysloveno jest jméno Boha, zachvějí se v srdcích svých úctou: a trpělivi jsou v protivenstvích, jež postihnou je: a zachovávají modlitbu: a z toho, čímž (k výživě) obdařili jsme je, rozdávají.'),
(2631, 22, 36, 'A velbloudy učinili jsme oběťmi při obřadech božích: v nich mnoho dobra máte. Pročež vyslovujte jméno boží nad nimi, když stojí v řadě (před obětováním): a když byli padli na boky své, pojídejte z nich a dejte najísti těm, kdož spokojí se i tomu, jenž si žádá. Takto podmanili jsme je vám, abyste nám byli vděčni.'),
(2632, 22, 37, 'Nedostihne maso jejich, aniž krev, Boha; ale dostihne ho bázeň boží od vás. Takto Bůh podmanil vám je, abyste vyvyšovali Boha ve chvále za vedení vás: a radostnou zvěst oznamuj dobře činícím.'),
(2633, 22, 38, 'Bůh zajisté hájí ty, kdož uvěřili (před úklady); neboť on zajisté nemiluje zrádců a nevěřících.'),
(2634, 22, 39, 'Dovoleno bylo (válčení) těm, kdož válčí proto, že bylo jim ublíženo: a Bůh zajisté pomoci jim jest schopen:'),
(2635, 22, 40, 'těm, kdož vypuzeni byli z příbytků svých bezprávně, jen proto, že říkají: „Pánem naším jest Bůh.“ A kdyby Bůh nebyl zapuzoval jedny lidi druhými, byly by bývaly pokořeny kláštery a chrámy a modlitebnice a mešity, v nichž mnoho vyslovováno jest jméno boží; a Bůh dojista pomáhá tomu, kdo jemu pomáhá: a Bůh silný jest a mocný.'),
(2636, 22, 41, 'Pomůže těm, kteří, budou-li upevněni v této zemi, zachovávati budou modlitbu a dávati budou povinnou almužnu: a nabádati budou k dobru a zbraňovati budou od zla. Bohu pak přináleží konečné (rozhodnutí) věcí.'),
(2637, 22, 42, 'Nazývají-li tě lhářem, však již lháři nazýval před tebou proroky lid Noemův, a kmeny \'Ád a Tsemúd,'),
(2638, 22, 43, 'a lid Abrahamův a lid Lotův.'),
(2639, 22, 44, 'A obyvatelé Medianu: a lhářem zván byl Mojžíš! A dlouhé strpení měl jsem s nevěřícími, však poté stihl jsem je (trestem) : a jaké bylo převrácení mé!'),
(2640, 22, 45, 'A kolik měst zahladili jsme, když byla nepravostna a nyní pusta jsou a vyvrácena! A kolik studnic zasypáno jest a kolik (sbořeno) hradů výstavních!'),
(2641, 22, 46, 'Což necestovali zemí a což nemají srdce k chápání a uši k slyšení? Však nejsou to zrakové jejich, již slepi jsou, ale slepa jsou srdce v prsou jejich.'),
(2642, 22, 47, 'A budou chtíti, abys uspíšil trest (slibovaný jim). Bůh pak nikdy nebéře slibu svého zpět; a den u Pána tvého jest jako tisíc let, jak vy je počítáte.'),
(2643, 22, 48, 'A s kolika městy měl jsem dlouhé strpení, ač nepravostna byla, a pak teprv stihl jsem je trestem! Ke mně pak vše se vrací.'),
(2644, 22, 49, 'Rci: „Lidé, jáť pouze jsem vám varovatelem zjevným:“'),
(2645, 22, 50, 'a těm, kdož uvěřili a konali dobré skutky, dostane se odpuštění a výživy štědré;'),
(2646, 22, 51, 'však ti, kdož snažili se odporem znemožniti znamení naše, obyvateli budou ohně pekelného.'),
(2647, 22, 52, 'Neposlali jsme před tebou ni jednoho posla neb proroka, do jehož přání nebyl by vrhl Satan nějaké přání nesprávné, však Bůh zrušuje to, co vrhá Satan do myšlení: poté pak rozsuzuje Bůh ohledně znamení svých, neboť Bůh zajisté vševědoucí jest a moudrý.'),
(2648, 22, 53, '(Svoluje k tomu), aby učinil to, co vrhá Satan v myšlení zkouškou pro ty, v jichž srdcích je choroba a jichž srdce zatvrzelá jsou: a nepravostní zajisté v roztržce jsou daleké.'),
(2649, 22, 54, 'A aby zvěděli ti, jimž dáno bylo vědění, že jest to pravdou od Pána tvého a aby uvěřili v ni a aby podvolila se jí srdce jejich: Bůh pak zajisté vede ty, kdož uvěřili, ke stezce přímé.'),
(2650, 22, 55, 'Však nepřestanou ti, kdož neuvěřili, pochybovati o ní, dokud nepřijde na ně hodina (soudu) z nenadání a dokud nepřijde na ně trest dne záhubného.'),
(2651, 22, 56, 'A panství nade vším v den onen přináležeti bude Bohu a on rozsoudí mezi nimi: tehdy ti, kdož uvěřili a konali dobré skutky, půjdou do zahrad rozkoše:'),
(2652, 22, 57, 'však těm, kdož neuvěřili a vylhanými nazývali znamení naše, určen jest trest potupný.'),
(2653, 22, 58, 'A ti, kteří vystěhovali se na stezce boží, poté padli v boji aneb zemřeli, Bůh dojista obštědří je údělem krásným, neboť Bůh zajisté nejlepším jest ze všech obštědřovatelů.'),
(2654, 22, 59, 'Onť dojista uvede je do místa, v němž naleznou zalíbení, a Bůh zajisté vševědoucím jest a laskavým.'),
(2655, 22, 60, 'Tak (stane se), A kdožkoli učiní odvetu stejnou měrou, jíž bylo mu ukřivděno a opět bezpráví mu učiněno jest, tomu dojista pomůže Bůh, neboť Bůh zajisté jest promíjející a velký v odpouštění.'),
(2656, 22, 61, 'Tak (stane se): protože Bůh vnáší noc v den a vnáší den v noc a že Bůh vše slyší a obzírá.'),
(2657, 22, 62, 'To proto, že Bůh jest pravdou, a že to, co vzýváte vedle něho, jest marností, a že Bůh jest nejvyšší, velký.'),
(2658, 22, 63, 'Zdaž nevidíš, že Bůh sesílá s nebe vodu a zazelená se země? Bůh zajisté dobrotivý jest a o všem zpraven.'),
(2659, 22, 64, 'Jemu přináleží vše, což na nebi jest a na zemi: a Bůh zajisté bohatý jest a chvályhodný.'),
(2660, 22, 65, 'Zdaž nevidíš, že Bůh podmanil vám cožkoli jest na zemi, jakož i lodi, jež plují po moři z rozkazu jeho? A on zadržuje nebesa, aby nespadla na zem, vyjma z dovolení jeho, neb Bůh zajisté vůči lidem laskavý jest a slitovný.'),
(2661, 22, 66, 'On jest to, který vzbudil vás k životu a poté dá vám zemříti a pak znovu vás vzkřísí: člověk zajisté jest vzdorný.'),
(2662, 22, 67, 'Každému národu určili jsme obřady, jimiž se řídí jedenkaždý: pročež nechť rozhodně nepře se (nikdo) s tebou v této věci; nýbrž volej (je) k Pánu svému, neboť ty dojista jsi veden cestou přímou.'),
(2663, 22, 68, 'A když hádají se s tebou, rci: „Bůh nejlépe ví o skutcích vašich.'),
(2664, 22, 69, 'Bůh rozsoudí mezi vámi v den zmrtvýchvstání ohledně toho, v čem rozcházeli jste se.“'),
(2665, 22, 70, 'Zdaž nevíš, že Bůh ví vše, což na nebi jest i na zemi? Vše to zajisté jest zapsáno v Knize (věčné): a toto zajisté Bohu jest snadným.'),
(2666, 22, 71, 'Oni pak uctívají vedle Boha to, ohledně čeho nebylo sesláno jim zmocnění a to, o čem nemají vědomosti: a nepravostným nebude pomocníka!'),
(2667, 22, 72, 'A když předříkávána jsou jim znamení naše jasná, seznáš na tvářích těch, kdož neuvěřili, odpor: a málo schází k tomu, aby vrhli se na ony, kteří předříkávají jim znamení naše: Rci: „Mám ohlásiti vám cos horšího nad, to, co činíte? Oheň, jejž slíbil Bůh těm, kteří neuvěřili — a špatná to bude cesta!“'),
(2668, 22, 73, 'Ó lidé, činěno jest vám podobenství: i naslouchejte mu: v pravdě ti, které vzýváte mimo Boha, nemohli by stvořiti ani mouchy, i kdyby všichni shromáždili se k tomu; a kdyby odňala jim co moucha, nemohli by odníti toho od ní. Jak slabi jsou oba: prosící i prošený!'),
(2669, 22, 74, 'Neocenili Boha pravým oceněním (moci) jeho: Bůh pak zajisté silný jest a mocný.'),
(2670, 22, 75, 'Bůh vybírá mezi anděly posly a rovněž mezi lidmi: v pravdě, Bůh vše slyší a obzírá.'),
(2671, 22, 76, 'Ví, co jest před nimi a co jest za nimi: a k Bohu navráceny budou všechny věci.'),
(2672, 22, 77, 'Vy, kteří jste uvěřili, klekejte a padejte na tváře své a uctívejte Pána svého, a konejte dobro, aby dobře se vám vedlo!'),
(2673, 22, 78, 'A bojujte pro Boha tak, jak on zaslouží si, aby zaň bylo bojováno: on vyvolil si vás a neučinil vám obtíží v náboženství: ve víře otce vašeho Abrahama. Onť nazval vás muslimy, již dříve a i v této (Knize), aby prorok váš byl svědkem vám a vy abyste svědky byli vůči lidem. Pročež zachovávejte modlitbu a dávejte (povinnou) almužnu a opírejte se o Boha: onť Pánem jest vaším: jak výborný to Pán a jak výborný spomocník!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Jak dobře povede se VĚŘÍCÍM,'),
(2675, 23, 2, 'kteří v modlitbě své se ponižují,'),
(2676, 23, 3, 'a kteří od svévolné řeči se vzdalují'),
(2677, 23, 4, 'a kteří (povinnou) almužnu dávají'),
(2678, 23, 5, 'a kteří pohlavní údy své ostříhají,'),
(2679, 23, 6, '(souložíce) jen s manželkami svými a s (otrokyněmi), dobytými pravicemi jejich: tehdy nebude jim viny:'),
(2680, 23, 7, 'však ti, kdož v žádosti své přestoupí tuto mez, budou přestupníky —'),
(2681, 23, 8, 'a ti, kdož zástavy jim dané (opatrují) a závazky své dobře dodržují,'),
(2682, 23, 9, 'a ti, kdož modlitby své bedlivě zachovávají:'),
(2683, 23, 10, 'tito zajisté budou dědici,'),
(2684, 23, 11, 'kteří zdědí ráj a přebývati v něm budou věčně.'),
(2685, 23, 12, 'Stvořili jsme pak člověka z měkké hlíny:'),
(2686, 23, 13, 'poté učinili jsme jej kapkou (semene) v příbytku jistém:'),
(2687, 23, 14, 'poté utvořili jsme sémě v krev sraženou, a krev sraženou vytvořili jsme v kousek (masa), a kousek (masa) vytvořili jsme v kosti: a oděli jsme kosti masem; pak dali jsme mu vzniknout jako stvoření jinému a požehnán budiž Bůh, nejlepší ze Stvořitelů!'),
(2688, 23, 15, 'Poté pak dojista zemřete;'),
(2689, 23, 16, 'pak dojista v den zmrtvýchvstání vzkříšeni budete.'),
(2690, 23, 17, 'Nad vámi pak stvořili jsme sedm cest a nejsme k tomu, co stvořili jsme, lhostejní.'),
(2691, 23, 18, 'A sesíláme s nebe vodu dle míry a působíme, aby zůstala v zemi, ač máme moc způsobiti, aby zmizela.'),
(2692, 23, 19, 'A jí dali jsme vzrůsti vám zahradám datlovníků a vinné révy, z nichž béřete množství plodů a z nichž pojídáte.'),
(2693, 23, 20, 'A taktéž stromu, jenž vyrůstá na hoře Sinai a jenž skýtá olej a šťávu pojídajícím.'),
(2694, 23, 21, 'A zajisté v dobytčatech vašich jest pro vás poučení: napájíme vás tím, co nalézá se v útrobách jejich a prospěch z nich máte mnohý a z nich též pojídáte.'),
(2695, 23, 22, 'A na nich, jakož i na lodích jste nošeni.'),
(2696, 23, 23, 'A kdysi poslali jsme Noema k lidu jeho, i řekl: „Lide můj, uctívejte Boha, neb nemáte jiného Boha, kromě něho: což nebudete se ho báti?“'),
(2697, 23, 24, 'Však řekli předáci těch, kteří neuvěřili z lidu jeho: „Tohle přec jest pouhý člověk, jako vy, však chce se vyvýšiti nad vás; a kdyby Bůh byl chtěl, byl by seslal anděly. Neslyšeli jsme ničeho podobného od otců našich předešlých.'),
(2698, 23, 25, 'Onť pouze jest mužem, Džinny posedlým: pročež pozorujte jej po jistou dobu.“'),
(2699, 23, 26, 'Řekl: „Pane můj, pomoz mi proti tomuto nazývání mne lhářem.“'),
(2700, 23, 27, 'I vnukli jsme mu: „Zbuduj archu pod dozorem naším a podle vnuknutí našeho: a když přijde rozkaz náš a praskne pec,'),
(2701, 23, 28, 'uveď do ní ode všech druhů po páru, jakož i rodinu svou, vyjma toho z nich, nad nímž ortel vynesen byl již předem a nepřimlouvej se u mne za ty, kdož nepravostni byli, neboť oni potopeni budou. A už usazen budeš ty a kdožkoli s tebou bude, v arše, tehdy rci: ‚Chvála Bohu, jenž zachránil nás před lidem nepravostným.‘“'),
(2702, 23, 29, 'A rci: „Pane můj, dej sestoupiti mi na místě sestupu požehnaném, neboť tys nejlepším z usazovatelů.“'),
(2703, 23, 30, 'V tomto zajisté byla znamení a tím stavěli jsme lidstvo na zkoušku.'),
(2704, 23, 31, 'Po nich pak vzbudili jsme jiná pokolení'),
(2705, 23, 32, 'a poslali jsme k nim posly z jejich středu, (aby hlásali): „Uctívejte Boha: nemáte jiného Boha, kromě něho. Což nebudete se jej báti?“'),
(2706, 23, 33, 'I řekli předáci lidu jeho, kteří neuvěřili a vylhaným nazvali setkání (s Bohem) v životě příštím, a jimž přespříliš dali jsme užívati života pozemského: „Tohle jest pouze člověk jako vy, jenž jí z toho, co vy jíte a pije z toho, co vy pijete.'),
(2707, 23, 34, 'A poslouchati-li budete člověka vám rovného, tehdy zajisté záhubě jste propadli.'),
(2708, 23, 35, 'Slibuje vám snad, že poté, až zemřete a v prach a kosti obráceni jste, zas (z hrobu) vyvedeni budete?'),
(2709, 23, 36, 'Pryč, pryč s tím, co slibováno jest vám.'),
(2710, 23, 37, 'Není nám jiného života, než tohoto života pozemského; umíráme a žijeme, však nikdy nebudeme vzkříšeni.'),
(2711, 23, 38, 'On pouze jest mužem, jenž lež vymyslil o Bohu a my nebudeme věřiti mu.“'),
(2712, 23, 39, 'Řekl: „Pane můj, pomoz mi proti tomuto nazývání mne lhářem.“'),
(2713, 23, 40, 'Řekl (Bůh): „Jen maličko a začnou litovati!“'),
(2714, 23, 41, 'A zastihlo je vykřiknutí (zkázy) právem a učinili jsme je splaveninou. Daleko zavržen budiž lid nepravostný!'),
(2715, 23, 42, 'Po nich pak vzbudili jsme pokolení jiná.'),
(2716, 23, 43, 'Nebude uspíšena nikterému národu lhůta jeho, aniž zpožděna.'),
(2717, 23, 44, 'Poté pak poslali jsme posly své jednoho po druhém. Pokaždé, když k některému národu přišel prorok jeho, nazvali jej lhářem: a tak dali jsme jednomu národu následovati druhý a učinili jsme je předmětem vyprávění. Daleko zavržen budiž lid nevěřící!'),
(2718, 23, 45, 'Poté pak poslali jsme Mojžíše a bratra jeho Árona se znameními svými a mocí zjevnou:'),
(2719, 23, 46, 'Faraonovi a předním lidu jeho; však chovali se pyšně, neboť lid to byl nadutý:'),
(2720, 23, 47, 'a řekli: „Zdaž uvěříme dvěma mužům jako jsme my, jichž lid nám otročí?“'),
(2721, 23, 48, 'A lháři nazvali je oba a stali se jedněmi ze zahubených.'),
(2722, 23, 49, 'Tehdy pak dali jsme Mojžíšovi Knihu, aby lid jeho (dobře) byl veden:'),
(2723, 23, 50, 'a učinili jsme syna Mariina a matku jeho znamením; a (obydlím) dali jsme jim místo na vrchu, bezpečné a pramen (vody) mající.'),
(2724, 23, 51, '„Ó proroci, jezte z dobrých věcí a čiňte dobro, neboť o tom, co činíte, dobře vím.'),
(2725, 23, 52, 'Zajisté pak tento národ váš jest národem jediným a já jsem Pánem vaším: mne tedy bojte se!“'),
(2726, 23, 53, 'Však lidé rozštěpili se ve věcech svých mezi sebou na skupiny: každá strana z toho, což měla, se radujíc:'),
(2727, 23, 54, 'i ponech je v hloubi omylu jejich do lhůty určené.'),
(2728, 23, 55, 'Zdaž domnívají se, že v tom, čeho popřáváme jim v hojnosti, ze statků a dětí'),
(2729, 23, 56, 'předháníme se (v dosažení) pro ně dobra? Naopak: věk oni netuší.'),
(2730, 23, 57, 'Zajisté pak ti, kdož bázní Pána svého naplněni jsou'),
(2731, 23, 58, 'a kteří ve znamení Pána svého věří:'),
(2732, 23, 59, 'a kteří (ničeho) s Pánem svým nespolčují:'),
(2733, 23, 60, 'a kteří dávají to, co dávají ana srdce jejich chvějí se (bázní) před tím, že k Pánu svému se navrátí.'),
(2734, 23, 61, 'Tito předhánějí se v (dosažení) dobra a první jej dosahují.'),
(2735, 23, 62, 'Neukládáme nijaké duši břemene nad síly její: a u nás (uložena) jest Kniha, jež mluví pravdu a nikomu nebude ukřivděno.'),
(2736, 23, 63, 'Však srdce jejich pohroužena jsou (v omylu) ohledně této (Knihy): a skutky jiné kromě oněch konají:'),
(2737, 23, 64, '(a budou) až do chvíle, kdy postihneme, blahobytu se těšící z nich trestem, hle, pak volají zděseně.'),
(2738, 23, 65, '„Nevolejte dnešního dne, neb námi vám nebude pomoženo:'),
(2739, 23, 66, 'vždyť dávno již byla předříkávána vám znamení moje, však vy na podpatcích svých jste se otáčeli,'),
(2740, 23, 67, 'plni pýchy na to vedli jste plané rozmluvy půlnoční.“'),
(2741, 23, 68, 'Což nebudou uvažovati o tom, co říká se jim, anebo snad přišlo jim, co nepřišlo otcům jejich minulým?'),
(2742, 23, 69, 'Aneb snad nepoznávali proroka svého a (proto) jej zapírají?'),
(2743, 23, 70, 'Aneb řeknou, „Džinny jste posedlý?“ Naopak: on přišel k nim s pravdou a většina jich pravdu nenávidí.'),
(2744, 23, 71, 'Však kdyby pravda byla následovala choutky jejich, zkažena by tím byla nebesa i země, a vše, co v obou jest; naopak, přinesli jsme jim napomenutí jich, ale oni od napomenutí svého se odvracejí.'),
(2745, 23, 72, 'Což žádáš od nich poplatek? Plat od Pána tvého lepším jest a on nejlepším jest všech živitelů.'),
(2746, 23, 73, 'Ty pak zajisté voláš je ke stezce přímé:'),
(2747, 23, 74, 'a ti, kteří nevěří v život budoucí, od oné stezky se uchylují.'),
(2748, 23, 75, 'A kdybychom byli slitovali se nad nimi a odňali od nich protivenství jejich, byli by svéhlavě dál stáli na svém bloudění (v nevíře).'),
(2749, 23, 76, 'A přece již postihli jsme je trestem, však neponížili se před Pánem svým, aniž sklonili šíje své:'),
(2750, 23, 77, 'až když otevřeli jsme na ně dveře trestu přísného: hle, zoufají si nad ním!'),
(2751, 23, 78, 'Onť to jest, jenž utvořil vám sluch a zrak a srdce; a jak málo jste mu vděčni.'),
(2752, 23, 79, 'Onť to jest, jenž dal vám vznik na zemi a k němu shromážděni budete.'),
(2753, 23, 80, 'Onť to jest, jenž obživuje a umrtvuje: on řídí přechod noci a dne: což toho nepochopíte (rozumem)?'),
(2754, 23, 81, 'Však ne: říkají totéž, co říkali staří;'),
(2755, 23, 82, 'říkají: „Zdaž až zemřeme a budeme prach a kosti, znovu vzkříšeni že budeme?'),
(2756, 23, 83, 'To již slibováno bylo nám, jakož i otcům našim, před tím: zajistéť jsou to jen báchorky starých:“'),
(2757, 23, 84, 'Rci: „Komu přináleží země a cožkoliv na ní jest — víte-li?“'),
(2758, 23, 85, 'Řeknou: „Bohu.“ Rci: „Což tedy si toho nepřipomenete?“'),
(2759, 23, 86, 'Rci: „Kdo pánem jest sedmera nebes a pán trůnu mocného?“'),
(2760, 23, 87, 'Řeknou: „Patří Bohu.“ Rci: „Což tedy nebudete se báti?“'),
(2761, 23, 88, 'Rci: „V čí ruce jest panství nade všemi věcmi a jenž chrání, sám nemaje třeba ochrany — víte-li?“'),
(2762, 23, 89, 'Řeknou: „V ruce Boha.n Rci: „Jak tedy můžete tak býti očarováni?“'),
(2763, 23, 90, 'Ano, přinesli jsme jim pravdu: však oni zajisté lháři jsou.'),
(2764, 23, 91, 'Bůh zajisté si neučinil syna a není s ním jiného boha: jinak odešel by každý bůh s tím, co stvořil, a jeden nad druhého by se vyvyšoval: chválen budiž Bůh nade vše, co vykládáte!'),
(2765, 23, 92, 'Onť zná věci skryté i věci viditelné a daleko povýšen jest nad to, co s ním spolčujete.“'),
(2766, 23, 93, 'Rci: „Pane můj, ukážeš-li mi to, co slibováno jest jim,'),
(2767, 23, 94, 'Pane můj, nedej mi pak býti mezi lidem nepravostným!“'),
(2768, 23, 95, 'Myť zajisté můžeme ukázat ti, co slibujeme jim;'),
(2769, 23, 96, 'odpuzuj zlé tím, co jest lepší: my nejlépe víme o tom, co vykládají.'),
(2770, 23, 97, 'A rci: „Pane můj, útočiště béřu k tobě před našeptáváním zlých duchů:'),
(2771, 23, 98, 'a útočiště béřu k tobě, Pane můj, před přiblížením se jich ke mne.“'),
(2772, 23, 99, 'Však když k některému z nich přijde smrt, rce: „Pane můj, dej mi vrátiti se,'),
(2773, 23, 100, 'abych činiti mohl dobro v tom, co opominul jsem.“ Však ne, Tato jsou slova, jež bude mluviti, ale za nimi bude přehrada až do dne, kdy budou vzkříšeni.'),
(2774, 23, 101, 'A když troubeno bude na roh, v den onen nebude svazků příbuzenských mezi nimi, aniž navzájem se budou doprošovati.'),
(2775, 23, 102, 'Ti, jichž váha těžká bude, těm dobře povede se.'),
(2776, 23, 103, 'Ti, jichž váha lehká bude, budou oni, jichž duše záhubě propadly a v pekle přebývati budou věčně.'),
(2777, 23, 104, 'Sžírati bude oheň tváře jejich a křiviti budou ústa svá.'),
(2778, 23, 105, '„Což nebyla vám čtena znamení naše a vylhanými jste je nazývali?“'),
(2779, 23, 106, 'Řeknou: „Pane náš, přemohlo nás neštěstí naše a stali jsme se lidem bloudícím.'),
(2780, 23, 107, 'Pane náš, vyveď nás odsud: a vrátíme-li se (k bludu), pak jistě budeme nepravostní.“'),
(2781, 23, 108, 'Řekne Bůh: „Vehnáni buďte do něho a nemluvte na mne.'),
(2782, 23, 109, 'Neb zajisté byla část služebníků mých, kteří říkali: ‚Pane náš, uvěřili jsme, i odpusť nám a slituj se nad námi, neb ty nejlepším jsi ze všech slitovných‘'),
(2783, 23, 110, 'a vy žert tropili jste si z nich, až způsobili jste, že zapomněli napomenutí mé a vysmáli jste se jim.'),
(2784, 23, 111, 'Jáť pak zajisté odměnil jsem je dnes za to, že trpělivě vše snesli: v pravdě oni šťastni jsou!“'),
(2785, 23, 112, 'Řekne (Bůh): „Jak dlouhý prodleli jste na zemi počet roků?“'),
(2786, 23, 113, 'Řeknou: „Prodleli jsme tam den aneb část dne: i taž se početvedoucích.“'),
(2787, 23, 114, 'Řekne Bůh: „Dleli jste tam pouze maličko, kdybyste to mohli věděti.'),
(2788, 23, 115, 'Zdaž domnívali jste se, že stvořili jsme vás z kratochvíle a že k nám nebudete navráceni?“'),
(2789, 23, 116, 'Pročež povýšen budiž Bůh, král pravdivý; není Boha, kromě něho, Pána trůnu vznešeného.'),
(2790, 23, 117, 'A kdo uctívá vedle Boha boha jiného, o němž nemá nijakého důkazu, ten zajisté súčtovati musí s Pánem svým, a zajisté dobře nepovede se nevěřícím!'),
(2791, 23, 118, 'A rci: „Pane můj, odpusť a slituj se, neb ty nejlepším jsi ze všech slitovných.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Toto jest súra, kterou seslali jsme a učinili povinnou: a seslali jsme v ní znamení jasná, abyste si je připomínali.'),
(2793, 24, 2, 'Smilnici a smilníka zbičujete každého z nich stem ran bičem: a nechť soucit s nimi nepřevládne ve vás nad vyplněním nařízení božích, věříte-li v Boha a v den poslední. A nechť trestu jejich svědkem jest skupina věřících.'),
(2794, 24, 3, 'Smilník nesmí bráti si za ženu leda smilnici aneb modloslužebnici: a smilnici nesmí si bráti za manželku leda smilník aneb modloslužebník. Takovéto sňatky pak zakázány jsou věřícím.'),
(2795, 24, 4, 'Ty, kdož vrhají podezření na ženy počestné a nemohou pak předvésti čtvero svědků, zbičujte osmdesáti ranami bičem a nepřijímejte svědectví jejich nikdy poté: tito zajisté jsou pohoršlivci.'),
(2796, 24, 5, 'Vyjma těch, kteří káli se poté a polepšili: Bůh zajisté velkým jest v odpouštění, slitovným.'),
(2797, 24, 6, 'Ti, kdož vrhají (podezření) na manželky své a nemají svědků, vyjma sebe samých, svědectvím dají čtvero dosvědčení při Bohu, že mluví pravdu'),
(2798, 24, 7, 'a pátým dají svolání prokletí Boha na sebe, v případě, že by byli mluvili lež.'),
(2799, 24, 8, 'Však odvrátí od ženy trest čtvernásobné dosvědčení při Bohu, že muž její lže,'),
(2800, 24, 9, 'a za páté, svolání hněvu božího na sebe, v případě, že on byl by mluvil pravdu.'),
(2801, 24, 10, 'Nebýti pak milosti boží nad vámi a milosrdenství jeho, (ztrestal by vás ihned): však Bůh rád popřává sluchu kajícným a jest moudrý.'),
(2802, 24, 11, 'Zajisté pak ti, kteří vzbudili (onu) lež, byli jednou tlupou z vás: však nepovažujte to za zlo pro sebe, naopak, jest to dobré pro vás. Jedenkaždý muž z nich obdrží, co patři mu po zásluze dle viny jeho, a tomu z nich, jenž vzal na se zveličiti (lež onu), připadne trest velký.'),
(2803, 24, 12, 'Proč nesmýšleli věřící mužové a ženy, když slyšeli jste o tom, v nitrech svých dobře (o té věci), řkouce: „Totoť zajisté lež jest zjevná?“'),
(2804, 24, 13, 'Proč nepřivedli (pomlouvači) čtvero svědků k dosvědčení toho? A ježto nemohli přivésti čtvero svědků, u Boha lživými jsou.'),
(2805, 24, 14, 'A nebýti milosti boží nad vámi a milosrdenství jeho ve světě tomto i v budoucím, byl by postihl vás za to, co rozhlásili jste, trest velký:'),
(2806, 24, 15, 'když roznášeli jste to jazyky svými a když mluvili jste ústy svými to, o čem neměli jste vědomosti, považujíce to za věc malou, ač u Boha věcí velkou jest to.'),
(2807, 24, 16, 'Proč, když slyšeli jste to, neřekli jste spíše: „Nepřísluší nám, abychom rokovali o tom? Při slávě tvé, Bože, toto jest pomluva velká!“'),
(2808, 24, 17, 'Napomíná vás pak Bůh, abyste nevrátili se k něčemu podobnému nikdy, jste-li věřícími.'),
(2809, 24, 18, 'A objasňuje vám Bůh znamení svá: neb Bůh vševědoucí jest a moudrý.'),
(2810, 24, 19, 'Zajisté pak těm, kdož jsou rádi, aby šířila se špatná pověst o těch, kdož uvěřili, určen bude trest bolestný ve světě tomto i v budoucím: Bůh pak ví, však vy nevíte.'),
(2811, 24, 20, 'A nebýti milosti boží nad vámi a milosrdenství jeho, (byl by vás ztrestal): však Bůh laskavý jest a slitovný.'),
(2812, 24, 21, 'Vy, kteříž jste uvěřili, nenásledujte šlépějí Satana: neb toho, jenž následuje šlépějí Satana, nabádá on ke špatnostem a činům zavrženíhodným. Nebýti pak milosti boží nad vámi a milosrdenství jeho, žádný z vás nebyl by čistým nikdy: však Bůh očišťuje, koho chce, a Bůh vše slyší a ví.'),
(2813, 24, 22, 'A nechť nezaklínají se ti, kdož přednost a jmění mají mezi vámi, že nebudou ničeho dávati svým příbuzným, nuzným a těm, kdož vystěhovali se na stezce boží, nýbrž ať spíše prominou jim a přehlédnou jich vinu. Což nebyli byste rádi, aby Bůh odpustil vám? A Bůh velkým jest v odpouštění, slitovným!'),
(2814, 24, 23, 'Zajisté ti, kteří vrhají (podezření) na ženy počestné, však lehkovážné, mezi ženami věřícími, proklati budou ve světě tomto i v budoucím a určen bude jim trest velký.'),
(2815, 24, 24, 'V den, kdy svědčiti budou proti nim jazykové jejich a ruce jejich a nohy jejich ohledně toho, co činili.'),
(2816, 24, 25, 'V den onen splatí jim Bůh dluh jejich dle pravdy, a seznají, že Bůh zajisté jest pravdou zjevnou.'),
(2817, 24, 26, 'Podlé ženy jsou pro podlé muže a podlí mužové jsou pro podlé ženy: a dobré ženy jsou pro dobré muže a dobří mužové jsou pro dobré ženy: tito ospravedlněni budou vůči tomu, co se o nich povídá: jim určeno jest odpuštění (Boha) a obdaření hojné.'),
(2818, 24, 27, 'Vy, kteříž jste uvěřili, nevcházejte do domů, jež nejsou vaše, dokud jste nebyli žádali o svolení a pozdravili obyvatele jejich: toto bude lepším pro vás, budete-li toho pamětlivi.'),
(2819, 24, 28, 'A nenajdete-li nikoho v nich, nevstupujte do nich, dokud vám nebylo to dovoleno: a rčeno-li jest vám „Vraťte se,“ tedy vraťte se. To přístojnějším bude pro vás a Bůh o tom, co činíte, dobře ví.'),
(2820, 24, 29, 'Nebude viny vám, vstoupíte-li do domů neobydlených, v nichž byly by pro vás nějaké potřeby: a Bůh dobře ví, co veřejně přiznáváte i co tajíte.'),
(2821, 24, 30, 'Rci věřícím mužům, aby klopili zraky své a střežili pohlavní údy své: to přístojnějším bude pro ně, Bůh pak zajisté zpraven jest o tom, co konají.'),
(2822, 24, 31, 'Rci věřícím ženám, aby klopily zraky své a střežily pohlavní údy své: a aby neukazovaly vnad svých, vyjma vnějších: a aby pokrývaly závoji poprsí svá: a aby neukazovaly vnad svých, vyjma manželům svým, aneb otcům svým, aneb otcům svých manželů, a synům svým, aneb synům manželů svých, aneb bratrům svým, aneb synům bratří svých, aneb synům sester svých, aneb ženám jejich, aneb těm, jichž zmocnila se pravice jejich, aneb služebníkům, nemajícím síly mužné, aneb dětem, jimž není dosud (zjevně) patrna nahota žen: a nechť nedupají nohama, aby vidělo se, co skryto jest ze vnad jejich. A obracejte se kajícně k Bohu všichni, ó věřící, aby dobře se vám vedlo!'),
(2823, 24, 32, 'A spojujte v manželství svobodné mezi vámi, jakož i poctivé ze služebníků svých a služek. Jsou-li chudi, obohatí je Bůh z milosti své a Bůh nesmírný jest (v bohatství svém), vševědoucí.'),
(2824, 24, 33, 'A nechť ve zdrženlivosti žijí ti, kdož nemohou pro chudobu v manželství vstoupiti, dokud neobohatí je Bůh z milosti své. A žádají-li někteří z otroků vašich úpis propuštění, vystavte jim jej, víte-li, že jest v nich dobro: a dejte jim ze statků božích, jež Bůh byl dal vám. A nenuťte otrokyň svých k nemravnosti, — chtějí-li uchovati si počestnost, — ze žádosti po statcích života pozemského: však přinutil-li by je kdo, Bůh zajisté poté, když byly přinuceny, bude velkým v odpuštění, slitovným.'),
(2825, 24, 34, 'Nyní pak seslali jsme vám znamení objasňující a příklady těch, kteří zašli před vámi, jakož i napomenutí bohabojným.'),
(2826, 24, 35, 'Bůh jest SVĚTLEM nebes i země: podobno jest světlo jeho výklenku, v němž hoří lampa — lampa zasklená — a sklo podobné hvězdě zářné: vznímá se (olejem) stromu požehnaného, olivovníku, jenž není východní ani západní: olej jeho svítí takřka i když nedotkne se jej oheň; jest to světlo na světle! Vede pak Bůh ke světlu svému, koho chce a činí podobenství lidem: a Bůh o všech věcech má vědomost.'),
(2827, 24, 36, 'V domech, jež dovolil Bůh, aby vystavěny byly a v nich vyslovováno jméno jeho, chvála jeho pěna jest v nich z jitra i za večera;'),
(2828, 24, 37, 'muži, jež nezvábí ani obchod, ani prodej, od vzpomínání Boha a zachovávání modlitby a dávání povinné almužny, bojí se dne, kdy převraceti se budou srdce a zraky —'),
(2829, 24, 38, 'aby v den onen odměnil je Bůh za nejlepší skutky jejich a rozmnožil vůči nim milost svou, neboť Bůh poskytuje darů svých, komu chce, bez počtu.'),
(2830, 24, 39, 'Ale těch, kdož neuvěřili, díla jsou jako přelud v poušti, jejž žíznivý mní býti vodou, dokud nepřijde k němu a neshledá jej ničím: ale najde tam Boha, jenž splatí mu účet jeho. A Bůh zajisté rychlým jest v účtování.'),
(2831, 24, 40, 'Anebo jako temnoty nad mořem hlubokým, pokrytým vlnami, nad nimiž kupí se vlny: nad nimi mračna a temnoty seskupené nad temnotami: vtáhne-li (lodník) ruku svou, takřka jí nevidí. A komu Bůh neučiní světla, tomu není světla!'),
(2832, 24, 41, 'Zdaž neviděl\'s, že Bohu pěje chválu cožkoliv na nebi jest i na zemi — i ptactvo, rozpínajíc křídla svá? Všechno zná modlitbu svou a pěje chválu jeho: a Bůh dobře ví o tom, co konají.'),
(2833, 24, 42, 'Bohu pak přináleží panství nebes i země a k Bohu (vede) cesta návratu.'),
(2834, 24, 43, 'Zdaž neviděl\'s, jak Bůh pomalu pohání mračna, pak kupí je dohromady a pak hromadí je ve sbory: i vidíš pak bohatý déšť vycházeti ze středu jich: a dává padati s nebe jako by hory plné ledových krup, jimiž stižen jest, koho chce (stihnouti), a jež odvrací od koho chce. A zář blesku jeho málem odnímá lidem zrak.'),
(2835, 24, 44, 'Bůh řídí střídání noci se dnem: v tom zajisté jest poučení pro lidi důvtipné.'),
(2836, 24, 45, 'A stvořil Bůh veškeré živočišstvo z vody: některé z nich pohybuje se na břiše svém: některé pohybuje se na dvou nohou, některé pohybuje se na čtyřech: Bůh stvořil, co chtěl, neboť Bůh nade všemi věcmi moc má.'),
(2837, 24, 46, 'Nyní pak seslali jsme znamení objasňující: a Bůh vede, koho chce, ke stezce přímé.'),
(2838, 24, 47, 'A říkají: „Uvěřili jsme v Boha a v proroka jeho a poslechli jsme,“ však poté někteří z nich odvracejí se: tito nejsou věřící!'),
(2839, 24, 48, 'A když voláni jsou před Boha a proroka jeho, aby rozsoudil mezi nimi — hle, část jich odvrací se.'),
(2840, 24, 49, 'Však kdyby oni byli v právu, byli by přišli k němu v podrobení.'),
(2841, 24, 50, 'Což v srdci jich jest choroba, anebo pochybují, anebo bojí se, aby neoklamal je Bůh a prorok jeho? Nikoliv: tito jsou pouze nepravostní.'),
(2842, 24, 51, 'Vyjádření věřících pak, když voláni byli před Boha a proroka jeho, aby rozsoudil mezi nimi bylo, že řekli jen: „Slyšeli jsme a poslechli jsme.“ Těmto jistě dobře se povede.'),
(2843, 24, 52, 'Neb ti, kdež poslušni jsou Boha a proroka jeho, kdož obávají se Boha a bojí se jej: ti zajisté budou šťastni.'),
(2844, 24, 53, 'Přisahali při Bohu nejpevnější přísahou, že poručíš-li jim, jistě vyjdou (do pole). Rci: „Nepřisahejte: poslušnost jest cennější. A Bůh zajisté dobře jest zpraven o tom, co činíte.“'),
(2845, 24, 54, 'Rci: „Buďte poslušni Boha a buďte poslušni proroka: však odvrátíte-li se, jemu bude (zodpovídati se) jen za břímě své, a vám za břímě vaše. A poslechnete-li ho, budete vedeni (správnou) cestou: povinností pak proroka jest jen hlásání zjevné.“'),
(2846, 24, 55, 'Slíbil Bůh těm, kdož uvěřili z vás a konali dobré skutky, že dojista učiní je nástupci nynějších pánů země, jakož učinil nástupci ony před nimi, a že dojista upevní pro ně náboženství jejich, jež dáti jim ráčil: a že v záměnu obav jejich poskytne jim poté bezpečí: „Uctívati budou mne a nebudou se mnou spolčovati ničeho: však kdo po tomto napomenutí neuvěří, patřiti bude k pohoršlivcům.“'),
(2847, 24, 56, 'A zachovávejte modlitbu a dávejte (povinnou) almužnu a poslušni buďte proroka, abyste došli milosrdenství.'),
(2848, 24, 57, 'A nechť nedomnívají se ti, kdož neuvěřili, že znemožniti mohou vůli Boha na zemi: a příbytkem jich bude oheň (pekelný). Špatná to bude cesta návratu!'),
(2849, 24, 58, 'Vy, kteří jste uvěřili, nechť žádají vás o dovolení otroci vaši a ti z vás, kdož nedosáhli (pohlavní) dospělosti, třikráte: před modlitbou jitřní, a když sejmete oděv svůj v poledne, a po modlitbě večerní. Tyto tři doby jsou doby nahoty vaší. Však nebudete míti viny, aniž oni, po těchto (třech dobách), kdy chodíte navzájem jeden kolem druhého. Takto vysvětluje vám Bůh znamení svá: a Bůh vševědoucí jest a moudrý.'),
(2850, 24, 59, 'A když děti vaše dosáhly (pohlavní) dospělosti, nechť žádají vás o dovolení, stejně jakož byli činili jiní před nimi. Takto vysvětluje vám Bůh znamení svá: a Bůh vševědoucí jest a moudrý.'),
(2851, 24, 60, 'Ženy mimo věk porodu, jež nemají naděje na manželství, nebudou míti viny, když sejmou oděv svůj, nevystavujíce na odiv vnad svých: však zdrží-li se toho, bude to lepším pro ně: a Bůh vše slyší a ví.'),
(2852, 24, 61, 'Nebude provinění slepým, aniž chromým, aniž nemocným, budou-li jísti u vašich stolů, aniž vám, budete-li jísti ve svých domech, aneb v domech svých otců, neb matek, neb bratří, neb sester, neb strýců z otcovy strany, neb tet z otcovy strany, neb strýců z matčiny strany, neb tet z matčiny strany: aneb v domech, k nimž máte klíče, neb v domě přítele svého. Nebude vám viny, budete-li jísti pohromadě aneb odděleně. A když vstupujete do domu, pozdravte se navzájem pozdravem ve jménu Boha, požehnaným a dobrým. Takto vysvětluje vám Bůh znamení svá, abyste je pochopili.'),
(2853, 24, 62, 'Věřícími jsou pouze ti, kdož uvěřili v Boha a v proroka jeho a kteří, když jsou s ním v nějaké společné věci, neodcházejí, dokud by jej nebyli požádali o dovolení: zajistéť ti, kteří tě žádají o dovolení, jsou oni, kdož vskutku věří v Boha a v proroka jeho. Když žádati tě budou o dovolení za účelem nějaké věci soukromé, dej je těm z nich, kterým budeš chtíti. A pros za odpuštění pro ně Boha, neboť Bůh zajisté velkým jest v odpouštění, slitovným.'),
(2854, 24, 63, 'Neoslovujte proroka mezi sebou stejným způsobem, jakým oslovujete se navzájem. Bůh dobře ví o těch z vás, kteří odcházejí potichu, za zády druhých: a nechť ti, kdož jednají oproti nařízení jeho, mají se na pozoru, aby nepadla na ně zkouška nějaká aneb trest bolestný.'),
(2855, 24, 64, 'Ó zdaž nepřináleží Bohu vše, což na nebi jest a na zemi? On ví, jak to s vámi stojí: a jednoho dne navráceni budou (všichni) k němu i zpraví je pak o tom, co činili, neboť Bůh o věcech všechněch má vědomost.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Požehnán budiž ten, jenž seslal ROZLIŠENÍ služebníku svému, aby byl veškerenstvu varovatelem.'),
(2857, 25, 2, 'Ten, jemuž přináleží panství nebes i země, a jenž neučinil sobě syna, a jenž nemá společníka v panství svém, a jenž stvořil všechny věci a určil určením.'),
(2858, 25, 3, 'Oni pak učinili si vedle Boha božstva, jež nestvořila ničeho, nýbrž sama stvořena jsou a nemohou přičiniti sobě ani škody ani prospěchu, aniž moc mají nad smrtí a životem a vzkříšením.'),
(2859, 25, 4, 'A říkají ti, kdož neuvěřili: „Totoť jest pouhá lež, kterou vymyslil a v níž pomohli mu druzí lidé.“ Dopustili se (tím) křivdy a podvodu.'),
(2860, 25, 5, 'A říkají: „Toto jsou báchorky starých, jež dal sepsati a jež předříkávány jsou mu z jitra i s večerem.“'),
(2861, 25, 6, 'Rci: „Seslal je, onen, jenž zná tajnosti nebes i země, neboť on velkým jest v odpouštění, slitovným.“'),
(2862, 25, 7, 'A říkají: „Co jest to za proroka? Požívá pokrmu a prochází se po náměstích: (neuvěříme), leda by seslán byl mu anděl a společně s ním byl nám varovatelem.'),
(2863, 25, 8, 'Anebo kdyby poklad byl shozen mu, aneb měl-li by zahradu, jež živila by jej.“ A říkají nepravostní: „Vyť zajisté následujete pouze muže očarovaného.“'),
(2864, 25, 9, 'Hle, jaká podobenství činí s tebou! Však oni jen v bludu jsou a nejsou s to, aby nalezli stezku (pravou).'),
(2865, 25, 10, 'Požehnán budiž ten, jenž kdyby chtěl, mohl by dáti tobě lepší toho, o čem oni mluví — zahrady, pod nimiž řeky tekou — a dáti tobě paláce!'),
(2866, 25, 11, 'Ano, vylhanou nazvali hodinu (soudu): ale těm, kteří vylhanou nazvali hodinu (soudu), schystali jsme plamen sžíravý.'),
(2867, 25, 12, 'Když spatří je (plamen ten) z místa dálného, slyšeti budou jej zuřící a řvoucí.'),
(2868, 25, 13, 'A když vrženi budou do úzkého místa v něm spřáhnuti vespolek, volati budou po zničení (na místě).'),
(2869, 25, 14, '„Nevolejte dnes po zničení jednom, ale volejte po zničení mnohém!“'),
(2870, 25, 15, 'Rci: „Toto-li lepším jest, či zahrada věčnosti, jež slíbena byla bohabojným? Odměnou bude jim i cílem cesty:'),
(2871, 25, 16, 'v ní budou míti, co budou chtít, na věky věkův: toto slib jest Pána tvého závazný.“'),
(2872, 25, 17, 'V den, kdy shromáždí je, jakož i ty, které oni uctívají vedle Boha, tehdy řekne (těmto): „Zdaž vy zavedli jste tyto služebníky mé v blud, aneb oni sami zbloudili s cesty (pravé)?“'),
(2873, 25, 18, 'Řeknou: „Chválen budiž! Nepříslušeloť zajisté nám, abychom brali si kromě tebe ochránců, však tys zahrnul tolika požitky je i otce jejich, že zapomněli napomenutí a stali se lidem ztraceným.“'),
(2874, 25, 19, '(Řekne Bůh mnohobožcům): „Nyní vylhaným prohlásili to, co říkáte, a nebude vám lze, abyste odvrátili od sebe (trest), aniž dostali pomoci: a kdožkoli nepravostným jest z vás, tomu okusiti dáme trestu velkého.“'),
(2875, 25, 20, 'A neposlali jsme před tebou proroků, kteří by nebyli požívali krmí, aneb nebyli procházeli se po náměstích: a učinili jsme vás navzájem sobě zkouškou, abychom seznali, dovedete-li (trpělivě) vyčkati. A Pán tvůj dobře pozoruje.'),
(2876, 25, 21, 'A říkají ti, kteří nedoufají v setkání s námi: „Leda kdyby seslán byl nám anděl, aneb kdybychom spatřili Pána svého (uvěříme).“ Věru zpyšněli v myslích svých a vypjali se vypjatostí velkou.'),
(2877, 25, 22, 'V den, kdy spatří anděly, v den ten nebude radostných zpráv provinilcům a volati budou: „Za přehradu nepřekročitelnou (s nimi)!“'),
(2878, 25, 23, 'Pak přistoupíme k ocenění činů, jimi vykonaných, a učiníme je prachem rozvátým.'),
(2879, 25, 24, 'V den onen obyvatelé zahrady (rajské) lepší budou míti obývání a krásnější místo ke zdřímnutí.'),
(2880, 25, 25, 'V den onen rozpoltí se nebesa s mračny svými a sesláni budou dolů andělé sestupující.'),
(2881, 25, 26, 'V den onen panství přináležeti bude v pravdě Milosrdnému a den bude to pro nevěřící přetěžký.'),
(2882, 25, 27, 'A hrýzti bude v den ten nepravostný ruce své, řka: „Kéž bych byl šel s prorokem stejnou cestou!'),
(2883, 25, 28, 'Běda mi, kéž nebyl bych vzal si toho a toho přítelem!'),
(2884, 25, 29, 'Onť zajisté zavedl mne od napomenutí poté, když bylo přišlo mi: a Satan dojista jest člověku zrádcem.“'),
(2885, 25, 30, 'I řekne prorok: „Pane můj, zajisté lid můj měl tento Korán za bezvýznamný.“'),
(2886, 25, 31, 'A takovýmto způsobem učinili jsme každému proroku nepřátele z provinilců — však Pán tvůj dostačitelným jest vůdcem a pomocníkem.'),
(2887, 25, 32, 'A říkají nevěřící: „Leda kdyby byl mu seslán Korán celý najednou, (uvěříme).“ Tak (činíme), abychom utvrdili jím srdce tvé; a rozdělili jsme jej na díly.'),
(2888, 25, 33, 'A nedají ti (nevěřící) podobenství, aniž bychom ti řekli (o něm) pravdu a dali ti nejlepší vysvětlení jeho.'),
(2889, 25, 34, 'Ti, kteří shromážděni budou, padší na tváře své, v pekle — ti zajisté nejhorší budou míti místo a nejbludnější stezku.'),
(2890, 25, 35, 'Kdysi dali jsme Mojžíšovi Knihu a učinili jsme Árona, bratra jeho, jemu rádcem:'),
(2891, 25, 36, 'a řekli jsme: „Jděte oba k lidu, který nazval vylhanými znamení naše“ a zničili jsme lid ten úplným zničením.'),
(2892, 25, 37, 'A lid Noemův, když lháři nazvali proroky, potopili jsme a učinili jsme je lidstvu znamením: a přichystali jsme nepravostným trest bolestný.'),
(2893, 25, 38, 'A (kmeny) \'Ád a Tsemúd a obyvatele Ar-Rassu zničili jsme a pokolení, jež mezi nimi žila, mnohá.'),
(2894, 25, 39, 'A všem těmto učinili jsme podobenství na výstrahu: a všechny tyto zahladili jsme úplným zahlazením.'),
(2895, 25, 40, 'Však již šli (nevěřící) kolem města, na něž dštěn byl déšť zlý: což neviděli je? Ovšem; ale nechtějí doufati ve vzkříšení (své).'),
(2896, 25, 41, 'A když vidí tě, pouze šprýmy si z tebe tropí, řkouce: „Zdaž tento jest to, jejž Bůh vzbudil nám prorokem?'),
(2897, 25, 42, 'Málem byl by odvedl nás od božstev našich, kdybychom se jich nebyli vytrvale přidrželi!“ Však seznají, až shlédnou trest svůj, kdo více zbloudil s cesty (pravé).'),
(2898, 25, 43, 'Co míníš? Ten, jenž vzal si za boha svého choutku svoji — budeš ty mu poručníkem?'),
(2899, 25, 44, 'Či snad domníváš se, že většina jich slyší, anebo chápe? Vždyť jsou jen jako dobytčata — ba dokonce dále jsou od cesty (pravé)!'),
(2900, 25, 45, 'Zdaž neviděl\'s, jak Pán tvůj prodlužuje stín? A kdyby byl chtěl, mohl učiniti jej nehybným: a pak učinili Jsme slunce mu vůdcem:'),
(2901, 25, 46, 'pak stahujeme jej k sobě stahováním snadným.'),
(2902, 25, 47, 'Onť to jest, jenž učinil vám noc pláštěm a spánek odpočinkem a učinil den vzbuzením.'),
(2903, 25, 48, 'Onť to jest, jenž poslal větry s poselstvím milosrdenství svého: a sesíláme s nebe vodu čistou,'),
(2904, 25, 49, 'abychom dali obživnouti jí zemi zmrtvělé a napájíme jí vše, co stvořili jsme: dobytčata i lidi četné.'),
(2905, 25, 50, 'A rozdělujeme ji mezi nimi, aby pamětlivi byli (nás), však většina lidí odmítá býti vším, leda vzdornými.'),
(2906, 25, 51, 'A kdybychom byli chtěli, byli bychom vzbudili v každém městě varovatele.'),
(2907, 25, 52, 'I neposlouchej nevěřících, nýbrž bojuj proti nim bojováním velkým.'),
(2908, 25, 53, 'Onť to jest, jenž rozlil obě moře, jedno sladké, čerstvé, druhé slané, hořké — a učinil mezi nimi rozhraní a přehradu nepřekročitelnou.'),
(2909, 25, 54, 'Onť to, jest, jenž stvořil z vody člověka a učinil mu svazky příbuzenské a pokrevní: a Pán tvůj jest všemocný.'),
(2910, 25, 55, 'A (nicméně) uctívají vedle Boha, co nemůže způsobiti jim prospěchu, aniž jim uškoditi: a nevěřící jest proti Pánu svému pomahačem.'),
(2911, 25, 56, 'A poslali jsme tě jedině jako věštitele a varovatele,'),
(2912, 25, 57, 'Rci: „Nežádám od vás odměny za to, vyjma toho, aby ten, kdo chce, vešel na stezku k Pánu svému.“'),
(2913, 25, 58, 'A spoléhej na Živoucího, jenž neumírá a vyvyšuj jej ve chvále jeho: onť dostačitelným jest vin služebníků svých znalcem.'),
(2914, 25, 59, 'On, jenž stvořil nebe i zemi a vše, což mezi oběma jest, v šesti dnech: poté pak povznesl se na trůn svůj — Milosrdný! A taž se o něm zpravených.'),
(2915, 25, 60, 'A když rčeno, jest jim: „Padněte na tváře své před Milosrdným,“ říkají: „A kdo jest (ten) Milosrdný? Máme klaněti se tomu, čemu kážeš nám?“ A tím více (od tebe) prchají.'),
(2916, 25, 61, 'Požehnán budiž ten, jenž učinil na nebi souhvězdí a učinil na něm svítilnu a měsíc osvětlující.'),
(2917, 25, 62, 'Onť jest to, jenž učinil střídání noci a dne pro ty, kdož chtějí býti pamětlivi (Boha) a pro ty, kdož chtějí býti vděčnými.'),
(2918, 25, 63, 'A služebníky Milosrdného jsou ti, kdož chodí po zemi skromně a když mluví na ně nevědoucí, odpovídají: „Pokoj (s vámi).“'),
(2919, 25, 64, 'A ti, kteří celé noci bdí modlíce se k Pánu svému na tvářích svých skloněni, aneb v stoje:'),
(2920, 25, 65, 'a ti, kteří říkají: „Pane náš, odvrať od nás trest pekla, neboť trest jeho zajisté jest věčný:'),
(2921, 25, 66, 'a zlé jest to obývání a umístění:'),
(2922, 25, 67, 'a ti, kteří v dávání nejsou ani výstředními, ani skoupými, nýbrž na střední cestě stojí:'),
(2923, 25, 68, 'a ti, kteří nevzývají zároveň s Bohem bohů jiných, a nezabíjejí osob, jichž zabití zakázal Bůh, leda dle práva, a nesmilní, neb kdo tak činí dojde pokuty.'),
(2924, 25, 69, 'Zdvojnásoben bude mu trest v den zmrtvýchvstání, a v něm na věky zůstane, potupen,'),
(2925, 25, 70, 'vyjma toho, kdož kajícně obrátí se a koná dobré skutky: těm zamění Bůh špatnosti jejich v dobré skutky, neboť Bůh velký jest v odpouštění, slitovný.'),
(2926, 25, 71, 'A kdožkoli na pokání obrátí se a koná dobré skutky, ten zajisté obrátí se k Bohu dobrým obrácením:'),
(2927, 25, 72, 'a ti, kdož nevydávají lživého svědectví a když jdou mimo ničemnosti, jdou mimo počestně:'),
(2928, 25, 73, 'a ti, když připomněna jsou jim znamení Pána jejich, nezůstanou netečnými, jako by byli hluši a slepi:'),
(2929, 25, 74, 'a ti, kteří říkají: „Pane náš, dejž nám z manželek našich a potomstva našeho radost srdce, a učiň nás předními z bohabojných.“'),
(2930, 25, 75, 'Tito odměněni budou vysokým místem za to, že trpělivě vyčkávali a naleznou v ráji přivítání a pozdrav.'),
(2931, 25, 76, 'V něm přebývati budou věčně: krásné to bude obývání a umístění.'),
(2932, 25, 77, 'Rci: „Nestará se o vás Pán můj, voláte-li k němu, či-li nic: vždyť již lhářem nazvali jste (proroka). Však (trest váš) bude lpěti na vás!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Tá. Sín. Mím.'),
(2934, 26, 2, 'Tato jsou znamení Knihy zjevné:'),
(2935, 26, 3, 'ty snad soužíš se proto, že nechtějí uvěřiti.'),
(2936, 26, 4, 'Kdybychom chtěli, seslali bychom jim s nebe znamení, před nímž sklonily by se šíje jejich v ponížení:'),
(2937, 26, 5, 'však není nového napomenutí, přišedšího jim od Milosrdného, aniž by se od něho neodvraceli.'),
(2938, 26, 6, 'A lží nazvali je, však dojde je zpráva o tom, z čeho si byli žert tropili.'),
(2939, 26, 7, 'Což nepohlédli na zemi — kolika na ní dali jsme vzrůsti druhům rostlin vzácných?'),
(2940, 26, 8, 'Zajisté v tom jest znamení: však většina jich nevěří.'),
(2941, 26, 9, 'A dojista Pán tvůj mocný jest, slitovný.'),
(2942, 26, 10, '(Pomni), když zavolal Pán tvůj Mojžíše: „Jdi k lidu nepravostnému:'),
(2943, 26, 11, 'lidu Faraonovu, Což nebudou báti se mne?“'),
(2944, 26, 12, 'Řekl (Mojžíš): Pane můj, bojím se, že lhářem nazvou mne.'),
(2945, 26, 13, 'A stísněna jest hruď, má a není volný jazyk můj: pročež pošli pro Árona.'),
(2946, 26, 14, 'Mají proti mně obvinění a bojím se, aby mne nezabili.“'),
(2947, 26, 15, 'Řekl (Bůh): „Nikoliv. Jděte oba se znameními našimi: my s vámi budeme, naslouchajíce.'),
(2948, 26, 16, 'A jděte k Faraonovi a rcete: „Myt zajisté posly jsme Pána veškerenstva:'),
(2949, 26, 17, 'i vyšli s námi syny Israele.“'),
(2950, 26, 18, 'Řekl (Farao): „Což nevychovali jsme tě mezi námi za dětství a což nedlel jsi mezi námi po mnoho let věku svého?'),
(2951, 26, 19, 'A učinil\'s skutek, kterýs učinil: jsi nevděčníkem.“'),
(2952, 26, 20, 'Řekl (Mojžíš): Ano, učinil jsem jej tehdy a byl jsem pobloudilím:'),
(2953, 26, 21, 'a prchl jsem od vás, ježto bál jsem se vás: však dal mi Pán můj moudrost a učinil mne jedním z poslů.'),
(2954, 26, 22, 'A jest to dobrodiní, jehož popřál\'s mi, že zotročil jsi syny Israele?“'),
(2955, 26, 23, 'Řekl Farao: „Kdo jest (ten) Pán veškerenstva?“'),
(2956, 26, 24, 'Řekl (Mojžíš): „On Pánem jest nebes i země a všeho, což mezi oběma jest — dovedete-li uvěřiti.“'),
(2957, 26, 25, 'Řekl (Farao) k těm, kteří byli kol něho: „Neslyšíte?“'),
(2958, 26, 26, 'Řekl (Mojžíš): „Jest Pánem vaším a Pánem otců vašich předešlých.“'),
(2959, 26, 27, 'Řekl (Farao): „Zajisté prorok váš, jenž poslán byl k vám, jest duchem posedlý.“'),
(2960, 26, 28, 'Řekl (Mojžíš): „Jest Pánem východu a Pánem západu a všeho, což mezi nimi jest — dovedete-li to pochopiti.“'),
(2961, 26, 29, 'Řekl (Farao): „Vezmeš-li si za Boha někoho jiného kromě mne, učiním tě jedním z uvězněných.“'),
(2962, 26, 30, 'Řekl (Mojžíš): „I když předvedu ti důkaz zjevný?“'),
(2963, 26, 31, 'Řekl (Farao): „Předveď jej, jsi-li pravdomluvný.“'),
(2964, 26, 32, 'Tehdy vrhl (Mojžíš) hůl svou na zem, a hle, stala se hadem zjevným.'),
(2965, 26, 33, 'Poté Pak vztáhl ruku svou, a hle, bílá byla (v zraku) hledících.'),
(2966, 26, 34, 'Řekl (Farao) k předákům svým, kolem sebe: „Totoť zajisté kouzelník jest učený.'),
(2967, 26, 35, 'Chce vyhnati vás ze země vaší kouzelnictvím svým: co nařizujete?“'),
(2968, 26, 36, 'Řekli: „Poodlož (odpověď) jemu a bratru jeho: a pošli do všech měst shromažďovatele,'),
(2969, 26, 37, 'kteří přivedou ti všechny kouzelníky učené.“'),
(2970, 26, 38, 'I shromážděni byli kouzelníci na schůzku v den určený:'),
(2971, 26, 39, 'a bylo řečeno lidem: „Shromáždíte-li se všichni,'),
(2972, 26, 40, 'abychom snad následovali kouzelníky, budou-li vítěznými?“'),
(2973, 26, 41, 'A když přišlo kouzelnictvo, řekli Faraonovi: „Dostaneme odměnu, budeme-li vítěznými?“'),
(2974, 26, 42, 'Řekl: „Ano: a v tom případě též budete z těch, kdož mně jsou nejbližší.“'),
(2975, 26, 43, 'Řekl, jim Mojžíš: „Vrhněte na zem, co máte k vrhání.“'),
(2976, 26, 44, 'I vrhli na zem provazy své a hole své a řekli: „Při moci Faraonově, myť zajisté jsme vítěznými!“'),
(2977, 26, 45, 'I vrhl Mojžíš na zem hůl svou a požrala jich divy vylhané.'),
(2978, 26, 46, 'Tehdy padli kouzelníci úctou na tváře své'),
(2979, 26, 47, 'a řekli: „Uvěřili jsme v Pána veškerenstva,'),
(2980, 26, 48, 'Pána Mojžíše a Árona.“'),
(2981, 26, 49, 'Řekl (Farao): „Zdaž uvěřili jste v něho dříve, než dovolil jsem vám? Tenť zajisté jest náčelníkem vaším, jenž naučil vás kouzlům — a dobře seznáte (moc moji): dojista pak dám uřezati vám ruce i nohy pokřižmo a dám ukřižovati všechny vás.“'),
(2982, 26, 50, 'Řekli: „To neuškodí nám, neb v pravdě k Pánu svému navrátíme se.'),
(2983, 26, 51, 'Zajisté přejeme si, aby odpustil nám Pán náš hříchy naše, neb my byli jsme prvními z věřících.“'),
(2984, 26, 52, 'I vnukli jsme Mojžíšovi: „Vyjdi za noci se služebníky mými: neb dojista pronásledováni budete.“'),
(2985, 26, 53, 'A poslal Farao do měst shromažďovače,'),
(2986, 26, 54, 'řkoucí: „Onino (Israelští) jsou hrstka nepočetná'),
(2987, 26, 55, 'a rozzuřeni jsou proti nám:'),
(2988, 26, 56, 'my pak četní jsme a obezřetní.“'),
(2989, 26, 57, 'Tak způsobili jsme, aby vyšli ze zahrad a od pramenů'),
(2990, 26, 58, 'a pokladů a bydlišť převzácných:'),
(2991, 26, 59, 'tak (stalo se): a dali jsme vše to dědictvím synům Israele.'),
(2992, 26, 60, 'Následovali je pak za jitra.'),
(2993, 26, 61, 'A když shlédli se navzájem oba vojové, řekli soudruzi Mojžíšovi: „Zajisté jsme teď dohoněni.“'),
(2994, 26, 62, 'Řekl: „Nikoliv: zajisté Pán můj se mnou jest a (dobře) povede mne.“'),
(2995, 26, 63, 'I vnukli jsme Mojžíšovi: „Udeř holí svou moře.“ I rozstouplo se a každá z obou částí zvedla se jak hora veliká.'),
(2996, 26, 64, 'Poté pak dali jsme přiblížiti se oněm druhým.'),
(2997, 26, 65, 'A zachránili jsme Mojžíše a kdož s ním byli, všechny:'),
(2998, 26, 66, 'poté pak potopili jsme ony druhé.'),
(2999, 26, 67, 'Zajisté v tomto jest znamení: však většina jich nevěří;'),
(3000, 26, 68, 'ale Pán tvůj zajisté mocný jest a slitovný.'),
(3001, 26, 69, 'A předříkávej jim děje Abrahame,'),
(3002, 26, 70, 'když řekl otci svému i lidu svému: „Co uctíváte?“'),
(3003, 26, 71, 'Řekli: „Uctíváme modly a jsme jim stále oddáni.“'),
(3004, 26, 72, 'Řekl: „Zdaž slyší vás, když k nim voláte?'),
(3005, 26, 73, 'Aneb prospěti vám mohou, či uškoditi?“'),
(3006, 26, 74, 'Řekli: „Nikoliv, však shledali jsme otce své již takto činící.“'),
(3007, 26, 75, 'Řekl: „Co míníte? To, co uctívali jste'),
(3008, 26, 76, 'a co uctívali otcové již vaši předešlí,'),
(3009, 26, 77, '(modly ty) mne nepřáteli jsou: (není Boha), kromě Pána veškerenstva,'),
(3010, 26, 78, 'jenž stvořil mne a jenž vede mne (pravou cestou),'),
(3011, 26, 79, 'jenž krmí mne i napájí mne,'),
(3012, 26, 80, 'a když onemocním, uzdraví mne;'),
(3013, 26, 81, 'jenž mi dá zemřít a pak vzkřísí mne:'),
(3014, 26, 82, 'a jenž přeji si, aby odpustil mi hříchy mé v den soudný.'),
(3015, 26, 83, 'Pane můj, dej mi soudnost a připoj mne ke spravedlivým:'),
(3016, 26, 84, 'a dej mi pověst pravdomluvnosti u pokolení příštích:'),
(3017, 26, 85, 'a dej mi podíl na dědictví zahrady rozkoše:'),
(3018, 26, 86, 'a odpusť otci mému, že byl jedním z bloudících:'),
(3019, 26, 87, 'a nepotup mne v den kdy vzkříšeni budou:'),
(3020, 26, 88, 'v den, kdy neprospějí nikomu statky, aniž děti:'),
(3021, 26, 89, 'vyjma toho, kdož přijde k Bohu se srdcem zdravým:'),
(3022, 26, 90, 'a kdy přiblížen bude ráj k bohabojným:'),
(3023, 26, 91, 'a představeno bude peklo svedeným:'),
(3024, 26, 92, 'a řečeno jim bude: „Kde jest to, co uctívali jste'),
(3025, 26, 93, 'vedle Boha? Zdaž mohou pomoci vám, aneb pomoci sobě samým?'),
(3026, 26, 94, 'A vmeteni budou v ně oni i (jimi) svedení,'),
(3027, 26, 95, 'a vojska Iblísova veškerá.'),
(3028, 26, 96, 'Řeknou, odporujíce si v pekle navzájem:'),
(3029, 26, 97, '„Při Bohu. zajisté byli jsme v bludu zjevném,'),
(3030, 26, 98, 'když stavěli jsme vás na roveň Pánu veškerenstva:'),
(3031, 26, 99, 'kdo jiný svedl nás, než provinilci?'),
(3032, 26, 100, 'A není nám přímluvců,'),
(3033, 26, 101, 'aniž přítele účinlivého:'),
(3034, 26, 102, 'a kdyby bylo nám návratu, tehdy jistě byli bychom z věřících!“'),
(3035, 26, 103, 'Zajisté, v tomto jsou znamení: však většina jich nevěří;'),
(3036, 26, 104, 'ale Pán tvůj zajisté mocný jest a slitovný.'),
(3037, 26, 105, 'Lid Noemův též nazval lháři posly své,'),
(3038, 26, 106, 'když řekl jim bratr jejich Noe: „Což nebudete báti se Boha?'),
(3039, 26, 107, 'Jáť pak jsem vám poslem důvěryhodným:'),
(3040, 26, 108, 'pročež bojte se Boha a poslechněte mne.'),
(3041, 26, 109, 'Nežádám si za to od vás odměny, neboť odměna má záleží jen od Pána veškerenstva;'),
(3042, 26, 110, 'pročež bojte se Boha a poslechněte mne.“'),
(3043, 26, 111, 'Řekli: „Zdaž věřit tobě budeme, když následují tě jen nejsprostší?“'),
(3044, 26, 112, 'Řekl: „Co vím o tom, co oni dělali?'),
(3045, 26, 113, 'Súčtování s nimi jest na Pánu mém — kéž byste tomu rozuměli!'),
(3046, 26, 114, 'Já přece neodstrčím těch, kdož věří,'),
(3047, 26, 115, 'neb jsem jen varovatelem zjevným.“'),
(3048, 26, 116, 'Řekli: „Nepřestaneš-li, Noe, dojista budeš jedním z ukamenovaných.“'),
(3049, 26, 117, 'Řekl: „Pane můj, věru lid můj lhářem nazval mne.'),
(3050, 26, 118, 'Rozhodni tedy mezi mnou a jimi jasným rozhodnutím a zachraň mne, jakož i ty, kdož se mnou jsou z věřících.“'),
(3051, 26, 119, 'I zachránili jsme jej a kdož byli s ním, v arše přeplněné,'),
(3052, 26, 120, 'poté pak potopili jsme všechny zůstavší.'),
(3053, 26, 121, 'Zajisté v tomto jest znamení; však většina jich nevěří,'),
(3054, 26, 122, 'ale Pán tvůj zajisté mocný jest a slitovný.'),
(3055, 26, 123, 'Též (kmen) \'Ád lháři nazval posly své,'),
(3056, 26, 124, 'když řekl jim bratr jejich Húd: „Což nebudete báti se Boha?'),
(3057, 26, 125, 'Jáť pak jsem vám poslem důvěryhodným:'),
(3058, 26, 126, 'ať pročež bojte se Boha a poslechněte mne.'),
(3059, 26, 127, 'Nežádám si za to od vás odměny, neboť odměna má záleží jen od Pána veškerenstva.'),
(3060, 26, 128, 'Zdaž stavěti budete na všech pahrbcích znamení pro kratochvíli?'),
(3061, 26, 129, 'A činiti si budete stavby jako by pro věčné obývání?'),
(3062, 26, 130, 'A když vládnete, vládnete jako utiskovatelé?'),
(3063, 26, 131, 'I bojte se Boha a poslechněte mne.'),
(3064, 26, 132, 'A bojte se toho, jenž rozhojnil vás v tom, co víte:'),
(3065, 26, 133, 'a rozhojnil vás v dobytčatech a dětech:'),
(3066, 26, 134, 'a v zahradách a pramenech'),
(3067, 26, 135, 'jáť bojím se pro vás trestu dne velkého.“'),
(3068, 26, 136, 'Řekli: „Nám jedno jest, varuješ-li nás, aneb nejsi-li varovatelem:'),
(3069, 26, 137, 'toto jsou pouze báchorky starých,'),
(3070, 26, 138, 'a my nebudeme ztrestáni.“'),
(3071, 26, 139, 'A lhářem nazvali jej i zahladili jsme je: zajisté v tomto jest znamení; však většina jich nevěří;'),
(3072, 26, 140, 'ale Pán tvůj zajisté mocný jest a slitovný.'),
(3073, 26, 141, 'Též (kmen) Tsemúd lháři posly nazval,'),
(3074, 26, 142, 'když řekl jim bratr jejich Sálih: „Což nebudete báti se Boha“'),
(3075, 26, 143, 'Jáť zajisté jsem vám poslem důvěryhodným:'),
(3076, 26, 144, 'pročež bojte se Boha a poslechněte mne.'),
(3077, 26, 145, 'A nežádám si za to od vás odměny, neboť odměna má záleží jen od Pána veškerenstva?'),
(3078, 26, 146, 'Zdaž ponecháni budete v tom, co máte zde, v bezpečí?'),
(3079, 26, 147, 'V zahradách a u zřídel,'),
(3080, 26, 148, 'a polností a datlovníků, větví, štíhlých:'),
(3081, 26, 149, 'a vytesávati budete si v horách domy dovedně?'),
(3082, 26, 150, 'I bojte se Boha a poslechněte mne;'),
(3083, 26, 151, 'a neposlouchejte nařízení výstředníků,'),
(3084, 26, 152, 'kteří pohoršují zemi a nepolepšují ji.“'),
(3085, 26, 153, 'Řekli: „Tys pouze jedním z očarovaných:'),
(3086, 26, 154, 'jsi pouze člověk, jako my: pročež předveď nám znamení, jsi-li z pravdomluvných.“'),
(3087, 26, 155, 'Řekl: „Tato velbloudice (bude jím): jí patřiti bude napití a vám patřiti bude napití ob den určený.'),
(3088, 26, 156, 'Však nepřičiňte jí zlého, jinak zachvátí vás trest dne velkého.“'),
(3089, 26, 157, 'Nicméně přeřezali žíly jí a litovali toho zrána,'),
(3090, 26, 158, 'neboť zachvátil je trest. Zajisté v tomto jest znamení: však většina jich nevěří;'),
(3091, 26, 159, 'ale Pán tvůj mocný jest a slitovný.'),
(3092, 26, 160, 'Lháři nazval lid Lotův posly své,'),
(3093, 26, 161, 'když řekl jim bratr jejich Lot: „Což nebudete báti se Boha?'),
(3094, 26, 162, 'Jáť jsem vám poslem důvěryhodným:'),
(3095, 26, 163, 'pročež bojte se Boha a poslechněte mne.'),
(3096, 26, 164, 'A nežádám si za to od vás odměny, neboť odměna má záleží jen od Pána veškerenstva.'),
(3097, 26, 165, 'Zdaž vcházeti budete k mužům, z tvorstva veškerého?'),
(3098, 26, 166, 'A zanecháte, co Bůh stvořil vám za manželky? Opravdu jste lidem chybujícím!“'),
(3099, 26, 167, 'Řekli: „Nezanecháš-li této řeči, Lote, zajisté budeš jedním z vyhnanců.“'),
(3100, 26, 168, 'Řekl: „Jáť činů vašich hrozím se:'),
(3101, 26, 169, 'Pane můj, spas mne a rodinu mou od toho, co činí tito:“'),
(3102, 26, 170, 'I zachránili jsme jej a rodinu jeho veškerou,'),
(3103, 26, 171, 'vyjma stařeny, jež omeškala se,'),
(3104, 26, 172, 'a pak zahubili jsme všechny ostatní.'),
(3105, 26, 173, 'A dštili jsme na ně deštěm, a špatný to byl déšť pro ty, jež varovali jsme!'),
(3106, 26, 174, 'Zajisté znamení jest v tomto: však většina jich nevěří;'),
(3107, 26, 175, 'ale Pán tvůj zajisté mocný jest a slitovný.'),
(3108, 26, 176, 'Též obyvatelé hvozdu (Madianského) lháři nazvali posly své,'),
(3109, 26, 177, 'když řekl jim Šu\'ejb: „Což nebudete báti se Boha?'),
(3110, 26, 178, 'Jáť jsem vám poslem důvěryhodným:'),
(3111, 26, 179, 'pročež bojte se Boha a poslechněte mne.'),
(3112, 26, 180, 'A nežádám si za to od vás odměny, neboť odměna má záleží jen od Pána veškerenstva.'),
(3113, 26, 181, 'Dodržujte správnou míru a nebuďte z těch, kdož na ní utrhují:'),
(3114, 26, 182, 'važte dle spravedlnosti přímé:'),
(3115, 26, 183, 'a neokrádejte lidi o věci jejich, aniž prostopášnostmi pohoršujte zemi:'),
(3116, 26, 184, 'a bojte se toho, jenž stvořil vás i pokolení předešlá.“'),
(3117, 26, 185, 'Řekli: „Ty zajisté\'s jen jedním z očarovaných.'),
(3118, 26, 186, 'A tys jen člověk, jako my; a my považujeme tě za jednoho z prolhaných:'),
(3119, 26, 187, 'dej spadnouti na nás kus nebe, jsi-li pravdomluvným.“'),
(3120, 26, 188, 'Řekl: „Pán můj nejlépe ví o tom, co činíte.“'),
(3121, 26, 189, 'Tak lhářem nazvali jej: i zachvátil je trest dne mračného: zajistéť byl to trest dne velkého!'),
(3122, 26, 190, 'Zajisté znamení jest v tomto, však většina jich nevěří,'),
(3123, 26, 191, 'ale Pán tvůj zajisté mocný jest a slitovný.'),
(3124, 26, 192, 'A tento (Korán) zajisté jest sesláním od Pána veškerenstva:'),
(3125, 26, 193, 'sestoupil s ním dolů duch věrný'),
(3126, 26, 194, 'na srdce tvé, abys byl jedním z varovatelů,'),
(3127, 26, 195, 'v jazyku jasném arabském.'),
(3128, 26, 196, 'Onť zajisté předpověděn jest v Písmech předešlých:'),
(3129, 26, 197, 'což není to znamením pro ně, že věděli o něm již učení ze synů Israele?'),
(3130, 26, 198, 'A kdybychom byli seslali jej na některého z cizinců,'),
(3131, 26, 199, 'a byl by četl jim jej, nebyli by bývali v něj věřili.'),
(3132, 26, 200, 'A takovýmto způsobem uvedli jsme jej do srdcí provinilců:'),
(3133, 26, 201, 'že neuvěří v něj, dokud neshlédnou trest svůj bolestný;'),
(3134, 26, 202, 'a přijde na ně znenadání, aniž by tušili.'),
(3135, 26, 203, 'Tehdy řeknou: „Může nám býti poshověno?“'),
(3136, 26, 204, 'Zdaž tedy budou chtíti (nyní), aby uspíšen byl trest náš?'),
(3137, 26, 205, 'Co míníš? Popřejeme-li jim radostí světa po léta'),
(3138, 26, 206, 'a pak přijde na ně, co bylo jim slíbeno,'),
(3139, 26, 207, 'co prospěje jim to, z čeho se byli radovali?'),
(3140, 26, 208, 'Ale nezahladili jsme města, aniž by bylo mělo své varovatele'),
(3141, 26, 209, 'k napomenutí: myť zajisté nebyli jsme křivditeli.'),
(3142, 26, 210, 'Nesestoupili pak (s Koránem) zlí duchové;'),
(3143, 26, 211, 'a nepříslušelo jim to, aniž byli s to tak učiniti,'),
(3144, 26, 212, 'neb oni od slyšení jeho jsou vzdáleni.'),
(3145, 26, 213, 'I nevzývej spolu s Bohem boha jiného, abys nebyl jedním z trestaných,'),
(3146, 26, 214, 'a varuj příbuzné své nejbližší,'),
(3147, 26, 215, 'a stři křídlo své ochrany nad těmi, již následují tě z věřících.'),
(3148, 26, 216, 'A když neposlechnou tě, rci: „Jáť zajisté prost jsem viny na tom, co činíte,“'),
(3149, 26, 217, 'spoléhej na Mocného, Slitovného,'),
(3150, 26, 218, 'jenž vidí tě, když vstáváš k modlitbě,'),
(3151, 26, 219, 'jakož i chování tvé mezi těmi, kdož uctívají jej,'),
(3152, 26, 220, 'neboť on všeslyšící a vševědoucí jest.'),
(3153, 26, 221, 'Oznámím vám, na koho sestupují zlí duchové?'),
(3154, 26, 222, 'Sestupují na všechny prolhance hříšné,'),
(3155, 26, 223, 'kteří házejí (do vzduchu věci) z doslechu a vět šina jich lháři jsou;'),
(3156, 26, 224, 'a BÁSNÍCI, jež následují (jimi) zvábení:'),
(3157, 26, 225, 'zdaž nevidíš, že bloudí po všech údolích?'),
(3158, 26, 226, 'A že říkají, co nečiní?'),
(3159, 26, 227, 'Vyjma těch, kteří uvěřili a konají dobré skutky a vzpomínají často Boha a hájí se poté, když bylo jim ukřivděno: a seznají křivditelé, jakým zvrácením budou zvráceni.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Tá. Sín. Tato jsou znamení Koránu a Knihy zjevné,'),
(3161, 27, 2, '(dobré) vedení a radostná zvěst věřícím,'),
(3162, 27, 3, 'kteří zachovávají modlitbu a dávají (povinnou) almužnu a pevně věří v život budoucí.'),
(3163, 27, 4, 'Zajisté pak těm, kdož nevěří v život budoucí, zkrášlili jsme skutky jejich, takže (bez vodítka) bloudí.'),
(3164, 27, 5, 'Těmto schystán jest trest nejhorší a tito největší utrpí v životě budoucím ztrátu.'),
(3165, 27, 6, 'Zajisté pak obdržel\'s Korán od Moudrého, Vševědoucího.'),
(3166, 27, 7, '(Pomni), když řekl Mojžíš rodině své: „Zajistéť shlédl jsem oheň; půjdu a přinesu vám o něm zprávu a přinesu vám louč planoucí, abyste se možná od ní ohřáli.“'),
(3167, 27, 8, 'A když přišel k ohni, bylo naň zavoláno: Požehnán jest ten, jenž v ohni jest a jenž kol něho jest: a chvála budiž Bohu, Pánu veškerenstva!'),
(3168, 27, 9, 'Ó Mojžíši, to jsem já Bůh, Mocný, Moudrý'),
(3169, 27, 10, 'Vrhni hůl svou na zem!“ A když Mojžíš viděl, že hýbala se, jako by byla hadem, obrátil se zády úprkem a nevracel se. „Neboj se, Mojžíši, není čeho báti se u mne poslům mým,'),
(3170, 27, 11, 'vyjma těm, již spáchali nepravost, ale pak zaměnili zlo dobrem — jáť pak velký jsem v odpouštění, slitovný.'),
(3171, 27, 12, 'Vlož ruku svou za ňadra svá a vytáhneš ji bílou, aniž by byla stižena nemocí. Toto bude jedním z devíti znamení pro Faraona a lid jeho, neboť v pravdě lid jest to pohoršlivý.“'),
(3172, 27, 13, 'A když předvedena byla jim znamení naše jasně před zraky jejich, řekli: „Totoť jest kouzelnictví zjevné.“'),
(3173, 27, 14, 'A odmítali je ač v duších svých seznali pravdivost jich, z nepravosti a pýchy. A hle, jaký byl konec pohoršlivců!'),
(3174, 27, 15, 'A (pomni, jak) když dali jsme Davidovi a Šalamounovi vědění, řekli: „Chvála budiž tomu, jenž vyznamenal nás nad mnohé ze služebníků svých věřících!“'),
(3175, 27, 16, 'A dědil Šalamoun po Davidovi a řekl: „Ó lidé, byli jsme naučeni výřečnosti ptactva, a byli jsme obdařeni (podílem) ze všech věcí: totoť zajisté jest milost zjevná.“'),
(3176, 27, 17, 'I shromážděna byla kol Šalamouna vojska jeho, sestávající z Džinnů a lidí, jakož i ptactva a kráčela v oddílech,'),
(3177, 27, 18, 'až došla do údolí MRAVENCŮ: i řekl jeden mravenec: „Ó mravenci, vstupte do obydlí svých, aby nezdeptal vás dojista Šalamoun s vojsky svými, aniž by toho tušili.“'),
(3178, 27, 19, 'A usmál se vesele Šalamoun nad řečí jeho a řekl: „Pane můj, ponukni mne, abych byl vděčným za dobrodiní tvé, kterým\'s požehnal mi a otci mému, a abych konal dobré skutky, v nichž měl bys zalíbení, a uveď mne z milosrdenství svého mezi služebníky svoje spravedlivé.“'),
(3179, 27, 20, 'A přehlížel ptactvo a řekl: „Jak to přijde, že nevidím dudka? Což jest nepřítomen?'),
(3180, 27, 21, 'Dojista ztrestám jej trestem přísným, aneb dojista zabiji jej, leda že by předvedl mi zmocnění zjevné.“'),
(3181, 27, 22, 'Však neomeškal se (dudek) na dlouho a přišel, řka: „Zvěděl jsem, co tys dosud nezvěděl: a přinesl jsem ti ze Saby novinu jistou.'),
(3182, 27, 23, 'V pravdě nalezl jsem tam ženu vládnoucí jim, obdařenou podílem ze všech věcí, majitelku trůnu velkého.'),
(3183, 27, 24, 'Shledal jsem, že ona i lid její klaní se slunci místo Bohu: neb Satan zkrášlil jim skutky jejich, a odvrátil je od stezky (pravé), takže nejsou (správně) vedeni;'),
(3184, 27, 25, 'takže neklaní se Bohu, jenž na světlo přivádí tajnosti nebes i země a ví, co skrýváte i co veřejně přiznáváte:'),
(3185, 27, 26, 'Bůh, není Boha kromě něho, jenž pánem jest trůnu velkého.“'),
(3186, 27, 27, 'Řekl (Šalamoun): „Uvidíme, mluvil-lis pravdu, anebo byl\'s jedním z prolhaných.'),
(3187, 27, 28, 'Jdi s tímto listem mým a hoď jim jej dolů: pak odvrať se od nich a vyčkej, co odvětí.“'),
(3188, 27, 29, 'Řekla (královna k předním lidu svého): „Pánové, byl mi hozen list vzácný.'),
(3189, 27, 30, 'Jestiť od Šalamouna a zní: ‚Ve jménu Boha, Milosrdného, Slitovného.'),
(3190, 27, 31, 'Nezdvihejte se proti mně, však přijďte ke mně, odevzdáni plně do vůle boží.‘“'),
(3191, 27, 32, 'Řekla (dále): „Pánové, poraďte mi v mé věci: nerozhodnu ničeho ve věci, bez vašeho dosvědčení.“'),
(3192, 27, 33, 'Řekli: „Myť zajisté jsme obdařeni silou a udatenstvím mocným: však rozhodnutí věci jest na tobě, i viz sama, jak rozkážeš.“'),
(3193, 27, 34, 'Řekla: „Zajisté králové, když vstupují do města, ztropí spoušť v něm a činí nejmocnější lidu jeho nejbídnějšími: takto jednají.'),
(3194, 27, 35, 'Pročež pošlu k těmto lidem dary a uvidím, s čím navrátí se poslové.“'),
(3195, 27, 36, 'A když přišel (posel) k Šalamounovi, řekl tento: „Vy tedy chcete rozhojniti mne ve jmění, když co Bůh dal mi lepší jest toho, co dal vám? Nicméně vy v darech svých shledáváte velkou radost!'),
(3196, 27, 37, 'Vrať se ke svým: myť pak dojista přijdeme na ně s vojsky, jimž nebudou moci postavit se na odpor a dojista vypudíme je ze země jejich zbědnělé a ponížené.“'),
(3197, 27, 38, 'Řekl pak: „Pánové, který z vás přinese mi trůn její, dříve než lid její přijde ke mně, odevzdán do vůle boží?“'),
(3198, 27, 39, 'Řekl \'Ifrít, jeden z Džinnů: „Já přinesu ti jej dříve, než zdvihneš se s místa svého: jáť jistě k tomu jsem dosti silný a spolehlivý.“'),
(3199, 27, 40, 'Řekl ten, jenž měl znalost Knihy: „Já přinesu ti jej dříve, než v mrknutí setkají a rozevřou se víčka tvá.“ A když (Šalamoun) viděl trůn postavený před sebou, řekl: „Totoť jest z milosti Pána mého, aby vyzkoušel mne, jsem-li vděčný, aneb vzdorný: a kdo jest vděčný, jest jím ve prospěch vlastní duše. a kdo jest vzdorný — však Pán můj (dost) bohatý jest a štědrý!“'),
(3200, 27, 41, 'Řekl (dále): „Učiň jí nepoznatelným trůn její: uvidíme, je-li na stezce (pravé), aneb jest z těch, kteří nejsou vedeni stezkou (pravou).“'),
(3201, 27, 42, 'A když přišla (královna k němu) bylo jí řečeno: „Jest toto trůn tvůj?“ Řekla: „Vypadá jako on.“ Nám tedy dáno bylo vědění dříve, než jí a my patřili jsme k těm, kdož do vůle boží jsou odevzdáni.'),
(3202, 27, 43, 'Však odvrátilo ji od pravé cesty to, co uctívala na místě Boha, neboť byla z lidu nevěřícího.'),
(3203, 27, 44, 'Bylo jí řečeno: „Vejdi do paláce“ — a když viděla jej, domnívala se, že jest to jezero, i vykasala oděv na nohou svých. Řekl Šalamoun: „Jest to pouze palác vyložený křišťálem.“ Řekla: „Pane můj, křivdila jsem své vlastní duši: nyní pak jako Šalamoun, odevzdávám se do vůle Boha, Pána veškerenstva.“'),
(3204, 27, 45, 'A (kdysi) poslali jsme ke kmeni Tsemúd bratra jejich Sáliha, (aby hlásal): „Uctívejte Boha“ — a tu rozdělili se na dva tábory navzájem si odporující.'),
(3205, 27, 46, 'Řekl Sálih: „Lide můj, proč uspěšujete na sebe (příchod) zlého, raději než dobrého? Proč neprosíte za odpuštění Boha, abyste možná došli slitování?“'),
(3206, 27, 47, 'Řekli: „Ptakověštbou zkoumali jsme tebe, jakož i stoupence tvoje.“ Řekl: „Věštba osudu vašeho jest pouze u Boha: ano, vy jste lid, jenž má býti na zkoušku postaven.“'),
(3207, 27, 48, 'A bylo ve městě devět lidí, již pohoršení páchali v zemi a nejednali spravedlivě.'),
(3208, 27, 49, 'Řekli: „Přisáhněte si navzájem při Bohu, že dojista přepadneme v noci jej a rodinu jeho a dojista pak řekneme mstiteli jeho: ‚Nebyli jsme svědky záhuby rodiny jeho a zajisté, že pravdu mluvíme.‘“'),
(3209, 27, 50, 'A kuli lest, však i my kuli jsme lest, aniž by toho byli tušili,'),
(3210, 27, 51, 'a hle, jaký byl konec jejich, myť zahubili jsme je, jakož i lid jejich veškeren.'),
(3211, 27, 52, 'A domy jejich nyní zpustošeni jsou za to, že nespravedlivě jednali: zajisté, že v tomto znamení jest pro lid rozumný.'),
(3212, 27, 53, 'A zachránili jsme ty, kdož byli uvěřili a báli se Boha.'),
(3213, 27, 54, 'A (poslali jsme) Lota tehdy, když řekl lidu svému: „Zdaž páchati budete smilstvo sami k tomu přihlížejíce?'),
(3214, 27, 55, 'A vcházeti k mužům z vášně, raději než k ženám? Ano, sbor jste nevědomců!“'),
(3215, 27, 56, 'Však odpovědi lidu jeho bylo jedině, že řekli: „Vyžeňte rod Lotův z města svého, neb zajisté jsou to lidé příliš mravopočestní.“'),
(3216, 27, 57, 'I zachránili jsme jej a rodinu jeho, vyjma ženy jeho, jíž předurčili jsme, aby byla z omeškalých.'),
(3217, 27, 58, 'A dštili jsme na ně deštěm: a zlý byl to déšť pro (marně) varované.'),
(3218, 27, 59, 'Rci: „Chvála Bohu a pokoj služebníkům jeho, jež vyvolil si! Jest lepší Bůh, aneb to, co s ním spolčují?'),
(3219, 27, 60, 'Ten, jenž stvořil nebe i zemi a sesílá vám s nebe vodu — a jí dali jsme vzrůsti zahradám smavé krásy: vám pak není dáno, abyste vzrůsti dali stromům jejich! Jest tedy bůh nějaký s Bohem? A přece (tito) lidé dávají mu rovné!'),
(3220, 27, 61, 'Ten, jenž učinil zemi pevnou, a učinil ve středu jejím řeky, a na vrchu jejím temena hor: a učinil mezi oběma moři přehradu: jest tedy bůh nějaký s Bohem? A přece většina jich není si toho vědoma.'),
(3221, 27, 62, 'Ten, jenž vyslyší stísněného, když volá k němu, a odnímá protivenství a učiní vás nástupci na zemi: jest tedy bůh nějaký s Bohem? Jak málo o tom přemítáte?'),
(3222, 27, 63, 'Ten, jenž vede vás v temnotách souše a moře, a jenž posílá větry jako posly milosti své: jest tedy Bůh nějaký s Bohem? Bůh daleko povýšen jest nad to, co oni spolčují s ním.'),
(3223, 27, 64, 'Ten, jenž dává počátek stvoření a pak dává mu navrátiti se: a jenž živí vás z (oblasti) nebe i země: jest tedy bůh nějaký s Bohem?“ Rci: „Předveďte důkaz svůj, jste-li pravdomluvnými!“'),
(3224, 27, 65, 'Rci: „Nikdo z těch, kdož na nebi jsou i na zemi, nezná věcí skrytých, vyjma Boha: a lidé nemají tušení, kdy vzkříšeni budou.“'),
(3225, 27, 66, 'Ač mají jakés vědění o životě budoucím, přece jsou v pochybnostech o něm, přece vůči němu slepi jsou.'),
(3226, 27, 67, 'A říkají ti, kdož neuvěřili: „Zdaž až budeme my i otcové naši v prach obráceni, budeme (z hrobu) vyvedeni?'),
(3227, 27, 68, 'To již slíbeno bylo nám i otcům našim dříve: však toto jsou jen, báchorky (pokolení) předešlých.“'),
(3228, 27, 69, 'Rci: „Cestujte zemí a vizte, jaký byl konec provinilců!“'),
(3229, 27, 70, 'Však nermuť se nad nimi a nebuď stísněn ohledně lstí, jež kují.'),
(3230, 27, 71, 'A říkají: „Kdy naplní se tento slib, jste-li pravdomluvnými?“'),
(3231, 27, 72, 'Rci: „Dost možná, že za zády vašimi jest (již) část z toho, co chcete uspíšiti.“'),
(3232, 27, 73, 'A zajisté Pán tvůj jest pln milosti vůči lidem, však většina jich není vděčna.'),
(3233, 27, 74, 'A zajisté Pán tvůj ví, co v prsou svých skrývají i co veřejně doznávají.'),
(3234, 27, 75, 'A není tajemství na zemi a na zemi, jež nebylo by v Knize zjevné.'),
(3235, 27, 76, 'Zajisté pak tento Korán vykládá synům Israele většinu z toho, v čem míněním se rozcházejí:'),
(3236, 27, 77, 'a zajisté jest vedením a milosrdenstvím pro věřící.'),
(3237, 27, 78, 'Zajisté Pán tvůj rozsoudí mezi nimi moudrostí svou: a on mocný jest a vševědoucí.'),
(3238, 27, 79, 'I spoléhej na Boha, neboť tys v právu zjevném.'),
(3239, 27, 80, 'Nedokážeš, aby slyšeli mrtví, aniž aby slyšeli hluší volání, když obracejí se zády:'),
(3240, 27, 81, 'aniž dokážeš, abys vyvedl na cestu pravou slepé z bloudění jejich: ale dokážeš jen, aby slyšeli tě ti, kdož věří ve znamení naše, odevzdáni jsouce do vůle boží.'),
(3241, 27, 82, 'A když rozsudek bude blízek svému vykonání, dáme vystoupiti na ně zvířeti ze země, jež promluví na ně (takto): „Tito jsou lidé, kteří ve znamení naše nevěřili.“'),
(3242, 27, 83, 'A (jednoho) dne shromáždíme od každého národa skupinu těch, kteří vylhanými nazývali znamení naše: a půjdou v oddílech,'),
(3243, 27, 84, 'až přijdou před Boha: i řekne: „Zdaž vylhanými nazývali jste znamení má, ač, nedovedli jste obsáhnouti jich věděním svým — aneb co vlastně činili jste?“'),
(3244, 27, 85, 'I vykonán bude na nich rozsudek za nepravost jejich, aniž hlesnou.'),
(3245, 27, 86, 'Což neviděli, že učinili jsme noc pro to, aby odpočívali v ní a den, k vidění? Zajisté v tomto jest znamení pro lid věřící.'),
(3246, 27, 87, 'A v den, kdy zatroubeno bude na roh a hrůzou zachví se, cožkoliv na nebi jest a na zemi, vyjma toho, co Bůh zachce: a všichni přijdou k němu v ponížení;'),
(3247, 27, 88, 'a uvidíš hory, jež za nehybné měl\'s, any jdou kupředu chodem mračen: to dílo bude Boha, jenž (dokonale) upravuje všechny věci: onť zajisté dobře zpraven jest o tom, co činíte.'),
(3248, 27, 89, 'A kdokoliv přijde s dobrými skutky, odměněn bude nad jich zásluhu: a před hrůzou dne onoho budou bezpečni.'),
(3249, 27, 90, 'Však kdožkoliv přijde se skutky zlými, vrženi budou tvářemi svými v oheň. Zdaž odměněni budete jinak, než dle toho, co činili jste?'),
(3250, 27, 91, 'Nařízeno mi bylo jen, abych uctíval Pána této země, jenž posvětil ji a jemuž přináležejí všechny věci: a nařízeno bylo mi, abych byl jedním z odevzdaných do vůle boží,'),
(3251, 27, 92, 'a abych předříkával Korán. A kdokoli uveden bude na (pravou) cestu, bude uveden tam ke svému vlastnímu prospěchu a těm, kdož bloudí. rci: „Jáť jsem jen jedním z varovatelů.“'),
(3252, 27, 93, 'A rci: „Chvála Bohu! Ukáže vám znamení svá a pak seznáte je! A Pán tvůj není lhostejným k tomu, co konáte.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Tá. Sín. Mím.'),
(3254, 28, 2, 'Tato jsou znamení knihy zjevné.'),
(3255, 28, 3, 'Předříkávati budeme tobě část dějů Mojžíše a Árona dle pravdy, pro (poučení) lidu věřícího.'),
(3256, 28, 4, 'Zajisté pak Farao povýšil se v zemi a rozštěpil lid její v různé strany, zeslabuje jednu část jich tím, že zabíjel syny jejich a živil dcery jejich, neboť on byl jedním z kazisvětů.'),
(3257, 28, 5, 'I chtěli jsme milostí zahrnouti ty, již zeslabováni byli v zemi, abychom učinili je vůdci (národů) a dědici země.'),
(3258, 28, 6, 'A abychom upevnili je v zemi a ukázali Faraonovi a Hámánovi, jakož i vojskům jejich to, čeho se od nich obávali.'),
(3259, 28, 7, 'I vnukli jsme matce Mojžíšově: „Koj ho a kdybys bála se oň, vrhni jej na moře a neboj se, aniž rmuť se: myť zajisté vrátíme jej tobě a učiníme jej jedním z poslů svých.“'),
(3260, 28, 8, 'A sebrala jej rodina Faraonova, aby byl jim nepřítelem a příčinou smutku: neboť Farao a Hámán a vojska jejich byli hříšníky.'),
(3261, 28, 9, 'A řekla žena Faraonova: „Radostí srdce mně i tobě bude: nezabíjejte jej, neb snad prospěšným nám bude aneb za syna si jej vzíti můžeme.“ Neb oni netušili.'),
(3262, 28, 10, 'A prázdnotou zchváceno bylo srdce matky Mojžíšovy a málem by jej byla prozradila, kdybychom nebyli opásali srdce její stálostí, aby byla jednou z věřících.'),
(3263, 28, 11, 'I řekla sestře jeho: „Jdi za ním“: i pozorovala jej z dáli, aniž by toho byli tušili.'),
(3264, 28, 12, 'A předem již zapověděli jsme mu (cizí) kojné, (dokud nepřišla sestra jeho) i řekla: „Zdaž ukáži vám dům jisté rodiny, jež odchová jej pro vás a zacházet s ním bude upřímně?“'),
(3265, 28, 13, 'Tak navrátili jsme jej matce jeho, aby radovalo se srdce její, a aby nermoutila se, a aby věděla, že slib Boha jest pravdivý. Však většina lidí není si toho vědoma.'),
(3266, 28, 14, 'A když dosáhl (mužné) síly své a dospěl, dali jsme mu soudnost a vědění: neb takovýmto způsobem odměnujeme ty, kdož dobře činí.'),
(3267, 28, 15, 'I vešel jednou do města v čas, kdy toho nepozorovali obyvatelé jeho a nalezl tam dvé mužů potýkajících se: jeden byl z jeho strany a druhý byl z jeho nepřátel. A prosil jej o pomoc ten, jenž byl z jeho strany, proti onomu, jenž byl z nepřátel jeho: i udeřil jej pěstí Mojžíš a byl s ním konec. Tehdy pak řekl (Mojžíš): „Toto jest dílo Satana, neboť on jest nepřítelem a svůdcem zjemním.“'),
(3268, 28, 16, 'Řekl (dále): „Pane můj, ukřivdil jsem sám sobě: pročež odpusť mi.“ A odpustil mu Bůh, neboť on velký jest v odpouštění, stitovný.'),
(3269, 28, 17, 'Řekl (Mojžíš): „Pane můj, za to, žes mi byl milostiv, nikdy nebudu již pomocníkem provinilých.“'),
(3270, 28, 18, 'Zrána pak šel městem pln strachu, pohlížeje opatrně kolem, a hle, onen muž, jenž byl prosil jej o pomoc den před tím, (opětně) volal naň. I řekl mu Mojžíš: „Tys v pravdě rozkolníkem zjevným.“'),
(3271, 28, 19, 'A když chtěl pak násilně zapuditi onoho, jenž byl oběma nepřítelem, řekl: „Zda zabíti chceš mne, jako zabil\'s člověka včera: tyť zajisté chceš býti jen násilníkem v této zemi a nechceš býti jedním z polepšovatelů.“'),
(3272, 28, 20, 'Tu přiběhl muž ze vzdáleného konce města v kvapu, řka: „Mojžíši, zajisté předáci města rokují o tom, jak zabili by tě: pročež prchni z města, neb upřímně ti radím.“'),
(3273, 28, 21, 'I prchl z města pln strachu, pohlížeje opatrně kol sebe, řka: „Pane můj, zachraň mne před lidem nepravostným.“'),
(3274, 28, 22, 'A když ubíral se směrem k Madianu, řekl: „Snad Pán můj povede mne cestou rovnou.“'),
(3275, 28, 23, 'A když došel ke studnici Madianu, našel u ní sbor lidí napájejících stáda a nalezl vedle nich dvé žen, udržujících stáda svá opodál; i řekl: „Co máte v úmyslu?“ Řekly: „Nelze nám napájeti, dokud neodženou odsud pastevci: otec náš pak jest vůdce mocný.“'),
(3276, 28, 24, 'I napojil jim oběma stáda; pak uchýlil se zpět do stínu. řka: „Pane můj, jáť ono dobro, jež poslal\'s mi, v pravdě postrádám.“'),
(3277, 28, 25, 'I přišla k němu jedna z žen, jdouc nesměle, a řekla: „Otec můj zve tě, aby odměnil tě mzdou za to, žes napojil stáda naše.“ A když Mojžíš přišel k němu a vyložil mu PŘÍBĚHY své, řekl (tento): „Neboj se, neb zachránil\'s se před lidem nepravostným.“'),
(3278, 28, 26, 'Řekla jedna z dcer: „Otče, najmi ho, neboť není lepšího, jejž bys najmouti mohl, než muž silný, spolehlivý.“'),
(3279, 28, 27, 'Řekl (otec): „Jáť chci dát tobě za manželku jednu z těchto dvou dcer mých, dáš-li se mi v nájem po dobu osmi let: a dovršíš-li deset, bude to tvoje dobrá vůle. Neb nechci ti nijak uložiti nesnáz: a shledáš, bude-li Bůh chtíti, že jsem jedním ze spravedlivých.“'),
(3280, 28, 28, 'Řekl (Mojžíš): „(Ujednáno) tak budiž mezi mnou a tebou: dovrším-li kteroukoli z oněch dvou lhůt, nebude požadavku vůči mně; a Bůh jest toho, co jsme řekli, ručitelem.“'),
(3281, 28, 29, 'A když dovršil Mojžíš lhůtu svou, a byl na cestě s rodinou svojí, spatřil na úbočí hory oheň. I řekl rodině své: „Posečkejte zde, neb spatřil jsem oheň: možná, že budu moci přinésti vám o něm zprávu, aneb louč z ohně, abyste se možná ohřáli.“'),
(3282, 28, 30, 'A když přišel k ohni, bylo zavoláno naň s břehu údolí pravého, na pláni požehnané, z keře: „Mojžíši, to jsem já, Bůh, Pán veškerenstva:'),
(3283, 28, 31, 'i vrhni na zem hůl svoji.“ A když spatřil ji hýbati se, jako by byla hadem, obrátil se zády v útěku a nevracel se. „Mojžíši,“ (volal hlas), „přibliž se a neboj se, neb zajisté jsi zde v bezpečí.'),
(3284, 28, 32, 'Vlož ruku svou za ňadra svá: vyjde ven bílou, však bez poruchy; a pak přitáhni k sobě ruku svou, prost bázně. Toto pak bude dvé důkazů od Pána tvého pro Faraona a předním lidu jeho, neb oni věru lidem jsou pohoršlivým.“'),
(3285, 28, 33, 'Řekl (Mojžíš): „Pane můj, zabil jsem jednoho z nich i bojím se, že zabijí mne.'),
(3286, 28, 34, 'A bratr můj, Áron jest výřečnější mne mluvou i pošli jej se mnou na pomoc, a aby potvrdil mne v slovech mých, neb věru bojím se, že lhářem nazvou mne.“'),
(3287, 28, 35, 'Řekl (Bůh): „Posílíme rámě tvé bratrem tvým a dáme vám oběma moc a nevyrovnají se vám ve znameních našich. Vy dva a ti, kdož následovati vás budou, zvítězíte.“'),
(3288, 28, 36, 'A když přišel k nim Mojžíš s našimi znameními jasnými, řekli: „Toto jest pouze kouzelnictví vymyšlené: a my nikdy neslyšeli jsme o tom od otců našich předešlých.“'),
(3289, 28, 37, 'I řekl Mojžíš: „Pán můj nejlépe ví o tom, kdo dostal (pravé) vedení od něho a o tom, kdo obdrží přebývání domu (věčného): neboť zajisté dobře nepovede se nepravostným.“'),
(3290, 28, 38, 'I řekl Farao k předním lidu svého: „Pánové, nevím, že byste měli Boha jiného, kromě mne: tedy vypáliti dej mi, Hámáne, cihly z hlíny, abych vystavěl si palác a tak možná dostoupiti mohl k Bohu Mojžíšovu, neboť dojista mám jej za prolhaného.“'),
(3291, 28, 39, 'A pyšně choval se on i vojska jeho v zemi, beze všeho práva a domnívali se, že k nám nebudou navráceni.'),
(3292, 28, 40, 'A zachvátili jsme jej i vojska jeho a vrhli jsme je v moře: a hle, jaké byl konec nepravostných.'),
(3293, 28, 41, 'A učinili jsme je vůdci, kteří zvou lidi k ohni pekelnému, a v den zmrtvýchvstání nebude jim pomoženo.'),
(3294, 28, 42, 'A prokletím pronásledovali jsme je v životě pozemském a v den zmrtvýchvstání v ošklivosti budou.'),
(3295, 28, 43, 'A dali jsme Mojžíšovi Knihu poté, když byli jsme zahladili pokolení předešlá jako viditelný příklad lidem a vedením a milosrdenstvím, aby o tom přemítali.'),
(3296, 28, 44, 'A nebyl\'s na úbočí západním hory (Sinai), když ustanovovali jsme Mojžíšovi věc (jeho), aniž byl\'s jedním ze svědků toho'),
(3297, 28, 45, 'Avšak vzbudili jsme po něm pokolení a dlouhé byly životy jejich: a nedlel\'s mezi lidem Madianským, předčítaje jim znamení naše, avšak my poslali jsme jim posly své.'),
(3298, 28, 46, 'A nebyl\'s na úbočí hory (Sinai), když volali jsme tam (Mojžíše), avšak z milosrdenství Pána tvého poslán jsi, abys varoval lid, jemuž nebyl přišel varovatel před tebou, aby o tom možná rozvažovali:'),
(3299, 28, 47, 'a aby, když postihlo by je neštěstí za to, co před tím konali, neříkali: „Pane náš, kdybys byl poslal nám proroka, zajisté byli bychom následovali znamení tvá a byli bychom z věřících.“'),
(3300, 28, 48, 'A když přišla jim pravda od nás, řekli: „(Neuvěříme), leda že by dáno mu bylo totéž, co dáno bylo i Mojžíšovi.“ Což však nezapřeli toho, co dáno bylo Mojžíšovi před tím? Řekli: „Jsou to dvě díla kouzelníků, kteří si navzájem pomáhali,“ a řekli: „Nevěříme v žádné z nich.“'),
(3301, 28, 49, 'Rci: „Předveďte teda knihu od Boha. jež byla by lepším vedením těchto dvou, abych mohl následovati ji, jste-li pravdomluvnými.“'),
(3302, 28, 50, 'A když neodpoví k tvé (výzvě), tehdy věz, že následuji jen choutky své: a kdo zbloudilejším jest toho, jenž následuje choutku svou, aniž vedení by měl od Boha? Neboť Bůh zajisté nevede lid nepravostný.'),
(3303, 28, 51, 'A přece dali jsme slovu (svému) dojíti jich, aby o tom možná rozvažovali.'),
(3304, 28, 52, 'Ti, kterým dali jsme Knihu před touto, věří v ni.'),
(3305, 28, 53, 'A když předčítána jim jest, říkají: „Uvěřili jsme v ni, neboť jest to pravda od Pána našeho: my pak zajisté již před ní byli jsme odevzdáni do vůle boží.“'),
(3306, 28, 54, 'Tito obdrží odměnu svou dvakráte za to, že trpělivě vyčkávali a zapuzovali dobrým zlé a z toho, čímž obdařili jsme je, rozdávali.'),
(3307, 28, 55, 'A když slyší ničemnost, vzdalují se jí, řkouce: „Nám patří naše skutky a nám patří vaše skutky: pokoj s vámi: nám netřeba jest nevědomců!“'),
(3308, 28, 56, 'Tiť zajisté nevedeš, koho přál bys si, ale Bůh vede, koho chce, a on nejlépe ví o těch, kdož (správně) vedeni jsou.'),
(3309, 28, 57, 'A říkají: „Budeme-li následovati (pravé) vedení s tebou, vyrváni budeme ze země své.“ Což neupevnili jsme se v obvodu posvátném a bezpečném, k němuž plyne hojnost plodů všeho druhu výživou od nás? Však většina jich není si toho vědoma.'),
(3310, 28, 58, 'A kolik zahladili jsme měst, oplývajících živobytím! A obydlí jejich byla obývána po nich pouze malým počtem lidí: a my stali jsme se dědici jejich.'),
(3311, 28, 59, 'A Pán tvůj nezahladil měst oněch, dokud nebyl poslal k nim proroka lidu jejich, jenž předčítal jim znamení naše: a nezahladili jsme měst, leda že lid jejich byl nepravostný.'),
(3312, 28, 60, 'A vše, cožkoli dáno bylo vám, jest (pouze dočasným) požitkem života pozemského a lákadlem jeho: a to, co u Boha jest, bude daleko lepším a stálejším. Což toho nepochopíte?'),
(3313, 28, 61, 'Zdaž pak ten, jemuž, slíbili jsme slib krásný a jemuž dostane se ho, bude roveň onomu, jemuž popřáli jsme požitku hmota pozemského, ale pak, v den zmrtvýchvstání bude jedním z předvedených k soudu?'),
(3314, 28, 62, 'V den, kdy zavolá na ně (Bůh) a řekne: „Kde jsou oni společníci moji, které mnili jste (býti se mnou)?“'),
(3315, 28, 63, 'Řeknou ti, nad nimiž po právu vyřčen jest ortel: „Pane náš, tito jsou, kteréž svedli jsme: a svedli jsme je stejně, jakož i my svedeni jsme byli: prosti jsme viny vůči tobě, neboť oni neuctívali nás.“'),
(3316, 28, 64, 'I řečeno bude oněm: „Volejte nyní ke společníkům svým“ — a volati budou k nim, však nebudou vyslyšeni jimi. A když spatří trest svůj, přáti si budou, aby byli následovali vedení (pravé).'),
(3317, 28, 65, 'V den onen zavolá na ně (Bůh) a řekne: „Co odpověděli jste poslům mým?“'),
(3318, 28, 66, 'A jako osleplí, nebudou s to, dáti zpráv v onen den, aniž navzájem se (na to) doptávati.'),
(3319, 28, 67, 'Však pokud se týče toho, jenž kajícně obrátil se a uvěřil a konal dobré skutky, možná, že bude jedním z těch, jimž dobře povede se.'),
(3320, 28, 68, 'A Pán tvůj tvoří, cokoliv chce a dle vlastní volby, však oni nemají vlastní volby: chválen budiž Bůh a daleko povýšen nad to, co spolčují s ním,'),
(3321, 28, 69, 'A Pán tvůj ví, co skrývají nitra jejich a co na venek přiznávají.'),
(3322, 28, 70, 'Onť jest Bůh, není Boha, kromě něho! Jemu přináleží chvála ve světě tomto i budoucím: a jemu patří moc soudu a k němu budete navráceni.'),
(3323, 28, 71, 'Rci: „Co soudíte? Kdyby učinil Bůh nad vámi noc věčnou až do dne zmrtvýchvstání, který bůh, mimo Boha, mohl by dáti vám světlo? Což nedáte tomu sluchu?“'),
(3324, 28, 72, 'Rci: „Co soudíte? Kdyby učinil Bůh nad vámi den věčný až do dne zmrtvýchvstání, který bůh, kromě Boha, mohl by dáti vám noc, abyste mohli odpočívati v ní? Což neprohlédnete?“'),
(3325, 28, 73, 'A z milosrdenství svého učinil vám noc a den: abyste odpočívati mohli v noci, a abyste hledali si podílu z dobrodiní jeho ve dne, a abyste mu byli vděčni.'),
(3326, 28, 74, 'A v den onen zavolá na ně (Bůh) a řekne jim: „Kde jsou oni společníci moji, které mnili jste (býti se mnou)?“'),
(3327, 28, 75, 'A předvedeme z každého národa svědka a řekneme: „předveďte důkaz svůj.“ Tehdy pak zvědí, že jedině Bůh má pravdu a ztratí se jim z dohledu to, co si byli vymyslili.'),
(3328, 28, 76, 'Zajisté pak Qárún byl jedním z lidu Mojžíšova, však svévolně jednal s nimi; dali jsme mu tolik pokladů, že klíče k nim byly by zatížily cely sbor mužů silných. Tehdy pak řekl mu lid jeho: „Neholedbej se přespříliš, neboť Bůh nemiluje těch, kdož holedbají se:'),
(3329, 28, 77, 'ale hledej pomocí toho, co dal ti Bůh, obydlí života budoucího a neopomíjej úlohy, jež připadla ti na světě: a buď dobročinným, jakož Bůh byl dobročinným vůči tobě, a nehledej konání špatností v zemi, neboť Bůh zajisté nemiluje těch, kdož špatnosti konají.“'),
(3330, 28, 78, 'Řekl: „Bylo mi to dáno jedině následkem vědění, jež ve mně jest.“ Což nevěděl, že Bůh byl zahladil před ním pokolení, která byla mocnější jej silou a většího bohatství? Však nebudou tázáni na proviněni svá provinilci.'),
(3331, 28, 79, 'I vycházel mezi lid svůj ve svém přepychu: a říkali ti, kteří žádali si života pozemského: „Kéž bychom měli to, co dáno bylo Qárúnovi, on zajisté jest šťastlivcem velkým.“'),
(3332, 28, 80, 'Však ti, jimž dáno bylo vědění, říkali: „Běda vám! Odměna Boha lepší jest pro toho, jenž uvěřil a konal dobré skutky: a nikomu nedostane se jí, vyjma těch, kdož trpělivě vyčkávají.“'),
(3333, 28, 81, 'I rozpoltili jsme zemi pod ním a domem jeho a neměl družiny, jež pomohla by mu proti Bohu a nebyl z těch, jimi pomoci dostalo by se.'),
(3334, 28, 82, 'A zrána ti, kteří přáli si býti na jeho místě den před tím, říkali: „Aj, dojista Bůh v hojnosti dává z darů svých, komu chce ze služebníků svých, aneb odměřuje je. Kdyby Bůh nebyl popřál nám milosti své, byla by se země rozpoltila pod námi. Aj, dojista dobře nevede se nevěřícím!'),
(3335, 28, 83, 'Takové jest přebývání života budoucího; dáme je těm, kteří nežádají si povýšení na zemi, aniž konání špatností: a dobrý bude konec bohabojných.'),
(3336, 28, 84, 'Kdokoliv přinese s sebou dobrý skutek, lepší dostane se mu odměny nad zásluhu jeho: a kdo přinese s sebou zlý skutek, věziž, že nebudou odměněni ti, kdož konali zlé skutky, leda dle toho, co činili.'),
(3337, 28, 85, 'Zajisté pak ten, jenž povinně uložil ti Čtení, navrátí tě do místa návratu.“ Rci: „Pán můj nejlépe ví o tom, kdo přinesl (dobré) vedení a kdo jest v bludu zjevném.“'),
(3338, 28, 86, 'Tys pak nikdy nedoufal, že dána ti bude tato Kniha, leda jako milosrdenství od Pána tvého: pročež nebuď podporou nevěřícím:'),
(3339, 28, 87, 'aniž dovol, aby tě odvrátili od znamení božích poté, když seslána ti byla: a volej lidi k Pánu svému a nebuď jedním z mnohobožců;'),
(3340, 28, 88, 'a nevolej k jinému bohu vedle Boha: neníť Boha. kromě něho: všechno zahyne, vyjma zář tváře jeho. Jemu pak přináleží moc soudu a k němu budete navráceni!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Alif. Lám. Mím.'),
(3342, 29, 2, 'Zdaž domnívají se lidé, že budou ponecháni v pokoji poté, když byli řekli: „Uvěřili jsme“ a že nebudou na zkoušku postaveni?'),
(3343, 29, 3, 'Však již postavili jsme na zkoušku ty, kdož byli před nimi: a Bůh dobře ví, kdo upřímný jest a ví, kdo jest lživý.'),
(3344, 29, 4, 'Zdaž domnívají se ti, kdož konají zlé skutky, že předstihnou nás (a uniknou trestu)? Jak špatně usuzují!'),
(3345, 29, 5, 'Ten, kdož doufá v setkání s Bohem, nechť jist jest, že lhůta boží přijde: a on vše slyší a ví.'),
(3346, 29, 6, 'A kdožkoli snaží se ve víře, tenť snaží se ve svůj vlastní prospěch: Bůh zajisté bohat jest nad věci veškeré.'),
(3347, 29, 7, 'A těm, kdož uvěřili a konali dobré skutky, smažeme zlé skutky jejich a dojista odměníme je dle nejlepších skutků jejich.'),
(3348, 29, 8, 'A doporučili jsme člověku ohledně rodičů jeho laskavost: však kdyby snažili se, abys spolčoval se mnou to, o čem nemáš vědomosti, tehdy neposlechni jich. Ke mně navrátíte se a pak zpravím vás ohledne toho, co činili jste:'),
(3349, 29, 9, 'a ty, kdož uvěřili a konali dobré skutky, dojista uvedeme mezi spravedlivé.'),
(3350, 29, 10, 'Jsou mezi lidem někteří, kdož dí: „Uvěřili jsme v Boha,“ však když útrapa stihne je pro (věc) boží, považují zkoušku (působenou jim) lidmi za trest boží. Však když přijde pomoc od Pána tvého, říkají: „Zajisté byli jsme s vámi!“ Což neví Bůh nejlépe o tom, co jest v nitrech lidí všech?'),
(3351, 29, 11, 'A Bůh dojista ví, kdo (skutečně) věří a ví, kdo jest pokrytcem.'),
(3352, 29, 12, 'Říkají pak ti, kdož neuvěřili, k těm, kdož uvěřili: „Následujte stezku naši a my na se vezmeme hříchy vaše.“ Však nevezmou na se hříchy jejich ani v nejmenším, neb zajisté lháři jsou.'),
(3353, 29, 13, 'Však dojista ponesou břemena svá a další břemena mimo břemen svých: a dojista, že tázáni budou v den zmrtvýchvstání ohledně toho, co byli vymýšleli.'),
(3354, 29, 14, 'A (kdysi) poslali jsme Noema k lidu jeho a dlel mezi nimi po tisíc let, vyjma padesáti roků: a zachvátila je potopa v nepravosti jejich.'),
(3355, 29, 15, 'Ale zachránili jsme jej, jakož i ty, kdož byli v arše a učinili jsme ji znamením veškerenstvu.'),
(3356, 29, 16, 'A (poslali jsme) Abrahama i řekl lidu svému: „Uctívejte Boha a bojte se jej: to bude lepším pro vás, dovedete-li toho pochopiti.'),
(3357, 29, 17, 'Vy pouze uctíváte modly vedle Boha a tvoříte tím lež: neboť zajisté ti, jež uctíváte vedle Boha, nemohou dáti vám výživy. Proto žádejte výživu svou od Boha a uctívejte jej a děkujte mu: k němu pak navráceni budete.'),
(3358, 29, 18, 'A když lhářem nazvete mne (dobře): již před vámi zvali národové proroky své lháři: povinností pak proroka jest pouze hlásání zjevné.“'),
(3359, 29, 19, 'Což nevidí, jak Bůh vzbuzuje stvoření a pak způsobuje, aby navrátilo se k němu? Totoť zajisté Bohu jest snadným.'),
(3360, 29, 20, 'Rci: „Cestujte v zemi a vizte, jak Bůh vzbudil stvoření: poté pak vzkřísí je vzkříšením novým, neboť Bůh nade všemi věcmi moc má.'),
(3361, 29, 21, 'Tresce, koho chce, a slitovává se, nad kým chce, a k němu navráceni budete.'),
(3362, 29, 22, 'Vám pak nelze znemožniti rozhodnutí jeho na zemi, aniž na nebi: a není vám kromě Boha ochránce, ni pomocníka.'),
(3363, 29, 23, 'A ti, kdož nevěří ve znamení boží a v setkání s ním, jsou bez naděje v milosrdenství mé: jim přichystán jest trest bolestný.“'),
(3364, 29, 24, 'A odpovědí lidu jeho bylo pouze, že řekli: „Zabte jej, aneb upalte jej.“ Však spasil jej Bůh od ohně: a v tomto zajisté znamení jest lidu věřícímu.'),
(3365, 29, 25, 'A řekl: „Zajisté vzali jste si vedle Boha modly za bohy z lásky k životu pozemskému: v den pak zmrtvýchvstání zapřete se navzájem a proklínati budete se navzájem: a obydlím vaším bude oheň (pekelný) a nebude vátu pomocníků.“'),
(3366, 29, 26, 'Uvěřil mu Lot a řekl „Jáť vystěhuji se k Pánu svému, neb on zajisté mocný jest a moudrý.“'),
(3367, 29, 27, 'A dali jsme Abrahamovi Isáka a Jakuba a ustanovili jsme v potomstvu jeho dar prorocký a Knihu: a dali jsme mu odměnu jeho na tomto světě a v budoucím on zajisté patřiti bude mezi spravedlivé.'),
(3368, 29, 28, 'A (poslali jsme) Lota, když řekl k lidu svému: „Dojista pášete smilstvo, v němž nepředešel vás nikdo ve světě.'),
(3369, 29, 29, 'Zdaž vcházeti budete k mužům a dokonce přepadati (je) na cestách? A nepravost páchati v shromážděních svých?“ Však odpovědí lidu jeho bylo jen, že řekli: „Přiveď na nás tedy trest boží, jsi-li pravdomluvným“'),
(3370, 29, 30, 'Řekl: „Pane můj, pomoz mi proti lidu prostopášnému.“'),
(3371, 29, 31, 'A když přišli poslové naši k Abrahamovi s radostnou zprávou, řekli: „Zahladíme lid onoho města, neboť lid jeho jest nepravostný.“'),
(3372, 29, 32, 'Řekl (Abraham): „Mezi nimi jest i Lot.“ Řekli: „My nejlépe víme o tom, kdo jest v onom městě: a dojista zachráníme jej i rodinu jeho, vyjma ženy jeho, jež bude jednou z omeškalých.“'),
(3373, 29, 33, 'A když přišli poslové naši k Lotovi, trápil se ohledně jich a byl stísněn ohledně jich pro slabost rámě svého, však oni řekli: „Neboj se a nermuť se, neboť my dojista zachráníme tebe i rodinu tvoji, vyjma ženy tvé, jež bude jednou z omeškalých.'),
(3374, 29, 34, 'Myť sešleme na lid tohoto města hrůzu s nebe za pohoršlivost jejich.“'),
(3375, 29, 35, 'A ponechali jsme z něho znamení jasné pro lid chápavý.'),
(3376, 29, 36, 'A k Madianským (poslali jsme) bratra jejich Šu\'ejba, i řekl: „Lide můj, uctívejte Boha a buďte v očekávání dne posledního: a neporušujte zemi prostopášností svou.“'),
(3377, 29, 37, 'Však lhářem nazvali jej a zachvátilo je zemětřesení a zrána jako ztuhlé mrtvoly nelezeni jsou v domech svých.'),
(3378, 29, 38, 'A (tak stalo se i s kmeny) \'Ád a Tsemúd, jakž jasně patrno jest vám ze zbytků obydlí jejich. Satan zkrášlil jim skutky jejich a odvrátil je od (pravé) cesty, ač prozíraví byli.'),
(3379, 29, 39, 'A tak stalo se i Qárúnovi a Faraonovi a Hámánovi. A přece přišel k nim Mojžíš s jasnými důkazy: pyšně chovali se v zemi, ale (nás) přece nepředstihli!'),
(3380, 29, 40, 'A jednohokaždého z nich ztrestali jsme za vinu jeho: jednoho z nich zachvátil vítr bouřný, jednoho zachvátilo vzkřiknutí; pod jedním rozpoltili jsme zemi; jiné pak utopili jsme. A nebyl to Bůh, jenž bezpráví jim činil, nýbrž oni sami sobě bezpráví činili.'),
(3381, 29, 41, 'Podobají se pak ti, kdož vedle Boha vzali si ochránce, PAVOUKU, jenž staví si obydlí: a dojista nejchatrnějším ze všech obydlí jest obydlí pavoukovo. Kéž by si toho jen byli vědomi!'),
(3382, 29, 42, 'Bůh zajisté dobře zná to, co vzývají vedle něho (dopodrobna): a on mocný jest a moudrý.'),
(3383, 29, 43, 'A tato podobenství činíme lidem: však nepochopí jich, leda rozumní.'),
(3384, 29, 44, 'Stvořil jest Bůh nebesa i zemi v opravdovosti: a v tomto zajisté jest znamení pra věřící.'),
(3385, 29, 45, 'Pročež předčítej, co vnuknuto bylo ti z Knihy: a zachovávej modlitbu, neboť modlitba zapuzuje smilnost a nepravost. Vzpomínání pak Boha jest největší (povinností): a Bůh (dobře) ví, co konáte.'),
(3386, 29, 46, 'Neveďte hádek s těmi, jimž dána byla Kniha, leda způsobem nejpřístojnějším: vyjímaje ony z nich, již nepravostni jsou. Rcete pak: „Uvěřili jsme v to, co sesláno bylo nám a sesláno bylo vám: a Bůh náš i Bůh váš jest Jediný: a my do vůle jeho jsme odevzdáni.“'),
(3387, 29, 47, 'A takovýmto způsobem seslali jsme ti Knihu: a ti, jimž dali jsme Knihu, věří v ni, a z těchto též mnozí věří v ni: popírají tedy znamení naše jedině (zatvrzelí) nevěřící.'),
(3388, 29, 48, 'Před tím, než zjevena byla, nepředčítal\'s jiné knihy, aniž psal jsi jakou pravicí svojí: v tom případě byli by mohli pochybovati ti, kdož (ji) zavrhují.'),
(3389, 29, 49, 'Naopak (Korán) jest (jako) znamení jasné v nitrech těch, jimž dáno bylo vědění: a nikdo nepopírá znamení našich, vyjma nepravostných.'),
(3390, 29, 50, 'A říkají: „(Neuvěříme), leda že by sesláno bylo mu znamení od Pána jeho.“ Rci: „Znamení jsou jedině v moci Boha: já pak jsem jedině varovatelem zjevným.“'),
(3391, 29, 51, 'Což nedostačuje jim, že seslali jsme ti Knihu, jež předčítána jest jim? Zajistéť v tom jest milosrdenství a varování lidu věřícímu.'),
(3392, 29, 52, 'Rci: „Bůh postačí co svědek mezi mnou a vámi: on zná vše, co na nebi jest a na zemi. A ti, kdož uvěřili v marnost a neuvěřili v Boha, ti zajisté záhubě propadli.“'),
(3393, 29, 53, 'A vyzývají tě, abys uspíšil jim trest: a nebýti lhůty předurčené, zajisté by byl přišel na ně trest a dojista přijde na ně znenadání, aniž by toho tušili.'),
(3394, 29, 54, 'Vyzývají tě, abys uspíšil jim trest: a zajisté peklo již obklopuje nevěřící.'),
(3395, 29, 55, 'V den, kdy zahalí je trest shůry a zpod nohou jejich, řečeno bude jim: „Okuste (odplaty) za to, co činili jste.“'),
(3396, 29, 56, 'Služebníci moji, kteříž uvěřili jste, zajisté země má rozsáhlá jest: mne tedy uctívejte!'),
(3397, 29, 57, 'Každé duši bude okusiti smrti: poté pak k nám navráceni budete.'),
(3398, 29, 58, 'A ty, kdož uvěřili a konali dobré skutky, dojista usadíme v (povýšených) komnatách zahrady (ráje), pod níž řeky tekou: v ní obývati budou věčně. Jak krásná bude odměna (dobře) činících,'),
(3399, 29, 59, 'kteří trpělivě vyčkávají a spoléhají na Pána svého!'),
(3400, 29, 60, 'A kolik jest živočichů, již nestarají se o potravu svou a Bůh živí je, jakož i vás: on pak vše slyší a ví.'),
(3401, 29, 61, 'A otážeš-li se jich: „Kdo stvořil nebe a zemi a podrobil (řádu) slunce a měsíc,“ tu řeknou: „Bůh.“ Jak tedy mohou tak lháti?'),
(3402, 29, 62, 'Bůh hojně rozdává dary své, komu chce ze služebníků svých, aneb odměřuje mu je: neboť Bůh o všech věcech má vědomost.'),
(3403, 29, 63, 'A otážeš-li se jich: „Kdo sesílá s nebe vodu a oživuje jí zemi zmrtvělou“, tu řeknou: „Bůh,“ Rci: „Chvála budiž tedy Bohu.“ Však většina jich toho nechápe.'),
(3404, 29, 64, 'Tento život pozemský jest pouze kratochvílí a hrou: však zajisté přebývání života budoucího (pravým) jest životem. Kéž by jen byli si toho vědomi!'),
(3405, 29, 65, 'Hle, když jedou na lodi, volají k Bohu v upřímné k němu víře: však když zachránili jsme je, (přivedše je) na souš, hle, opětně spolčují s ním (bohy jiné),'),
(3406, 29, 66, 'aby zapírali to, co zjevili jsme jim, aby jen radovali se z požitků vezdejších: však zvědí!'),
(3407, 29, 67, 'Což nevidí, že ustanovili jsme jim, obvod posvátný a bezpečný, ač olupováni jsou lidé vůkol? Zdaž tedy v marnost věřiti budou a budou dobrodiní boží zapírati?'),
(3408, 29, 68, 'Kdož pak nepravostnějším jest toho, jenž vymyslí o Bohu lež, aneb lží nazývá pravdu, když byla přišla k němu? Zdaž nebude pak v pekle příbytek nevěřících?'),
(3409, 29, 69, 'Ty pak, kdož vynasnažují se pro nás, dojista povedeme stezkami svými: neboť Bůh zajisté jest s těmi, kdož dobře činí.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Alif. Lám. Mím.'),
(3411, 30, 2, 'Poraženi byli Řekové,'),
(3412, 30, 3, 'v blízkosti naší země: však po porážce své budou zase vítězi,'),
(3413, 30, 4, 'za několik let. Bohu přináleží rozsouzení, před i po (každé události). A v den onen rozradují se věřící'),
(3414, 30, 5, 'nad (vítězstvím) z pomoci Boha: pomáháť Bůh, komu chce, neb on mocný jest, slitovný.'),
(3415, 30, 6, 'Toto jest slib Boha a Bůh nikdy nebéře zpět slibu svého, však většina lidí není si toho vědoma.'),
(3416, 30, 7, 'Znají pouze po zevnějšku život pozemský a vůči životu budoucímu jsou lhostejní.'),
(3417, 30, 8, 'Zdaž nerozvažovali sami v duchu, že Bůh nestvořil nebesa a zemi, a vše, což mezi oběma jest, leda v opravdovosti a pouze až do lhůty předurčené? Však zajisté mnoho lidí v setkání s Pánem svým nevěří.'),
(3418, 30, 9, 'Zdaž necestovali v zemi a neviděli, jaký byl konec těch před nimi, kteří byli mocnější jich silou? Rozbrázdili zemi a zalidnili ji ve větším počtu, než jakým oni zalidňují ji: a přišli k nim poslové naši s jasnými důkazy. A nebyl to Bůh, jenž bezpráví jim učinil, nýbrž oni sobě samým bezpráví učinili.'),
(3419, 30, 10, 'A konec těch, již páchali zlo, zlý byl: neboť zajisté vylhanými nazývali znamení naše a žert si z nich tropili.'),
(3420, 30, 11, 'Bůh vzbuzuje stvoření a pak dává mu navraceti se k němu: a pak všichni navráceni k němu budete.'),
(3421, 30, 12, 'A v den, kdy nastane hodina (soudu), oněmí provinilci:'),
(3422, 30, 13, 'a nebude jim mezi těmi, jež spolčovali (s Bohem) přímluvců: oni pak budou tyto společníky své zapírati.'),
(3423, 30, 14, 'A v den, kdy nastane hodina (soudu), v den ten budou lidé od sebe rozděleni:'),
(3424, 30, 15, 'a pokud se týče těch, kdož uvěřili a konali dobré skutky, ti v květnaté lučině radovati se budou:'),
(3425, 30, 16, 'a pokud se týče těch, kdož neuvěřili a vylhanými nazývali znamení naše, jakož i setkání v životě budoucím, ti trestu svému odevzdáni budou.'),
(3426, 30, 17, 'Chvála budiž tedy Bohu za chvil večerních i za chvil ranních:'),
(3427, 30, 18, 'a jemu patří chvála na nebi i na zemi: když noc nadchází i za chvil poledních.'),
(3428, 30, 19, 'On vyvádí živé z mrtvých a vyvádí mrtvé z živých: a oživuje zemi zmrtvělou — a tak i vy z mrtvých vyvedeni budete.'),
(3429, 30, 20, 'A ze znamení jeho jest, že stvořil vás z prachu: a pak, hle, jste lidmi rozptýlenými!'),
(3430, 30, 21, 'A ze znamení jeho jest, že stvořil vám z vás samých manželky, abyste obývali s nimi: a učinil mezi vámi lásku a soucitnost: v tomto zajisté znamení jest pro lid přemýšlivý.'),
(3431, 30, 22, 'A ze znamení jeho jest, že stvořil nebe a zemi a rozmanitost jazyků vašich i pletí vašich: v tomto zajisté znamení jsou veškerenstvu.'),
(3432, 30, 23, 'A ze znamení jeho jest spánek váš v noci a za dne snažení vaše po docílení (podílu) z milosti jeho: zajisté v tomto znamení jsou lidem slyšícím.'),
(3433, 30, 24, 'A ze znamení jeho jest, že ukazuje vám blesk pro strach a naději: a sesílá s nebe vodu, jíž oživuje zemi zmrtvělou: zajisté v tomto znamení jsou pro lid chápavý.'),
(3434, 30, 25, 'A ze znamení jeho, jest, že udržují se v stálosti nebe i země z rozkazu jeho: a pak když vyzve vás výzvou k vystoupení ze země, hle, ven z ní vyjdete.'),
(3435, 30, 26, 'Jemu přináleží, cožkoliv na nebi jest i na zemi: vše před ním poníženo jest.'),
(3436, 30, 27, 'On to jest, jenž vzbuzuje stvoření a pak dává mu navrátiti se k němu: a to přesnadné jest mu. Jemu přináleží, aby přirovnáván byl ke všemu nejvyššímu na nebi i na zemi: a on mocný jest, moudrý.'),
(3437, 30, 28, 'Činí vám podobenství vzatá z vás samých: zdaž máte z těch, jichž zmocnila se pravice vaše, společníky v tom, čímž obdařili jsme vás, rovným dílem? Zdaž stejné rozpaky cítíte vůči nim, jako mezi sebou? Takovýmto způsobem vysvětlujeme znamení svá lidu chápavému.'),
(3438, 30, 29, 'Jistě, že ne: však nepravostní následují choutky své, z nevědomosti: a kdo uvede na cestu pravou toho, jejž Bůh zavedl v blud? Jim nebude pomocníků.'),
(3439, 30, 30, 'Pozdvihni tedy tvář svou věrně k víře, jež ústavou jest Boha, pro kterou byl ustavil lidi: není změny ve stvoření božím. Tato víra jest nepřeměnná, však většina lidí není si toho vědoma.'),
(3440, 30, 31, 'Obracejte se (tedy) k Bohu a bojte se ho: a zachovávejte modlitbu, a nepatřte k mnohobožcům:'),
(3441, 30, 32, 'k těm, kdož rozštěpili se v náboženství svém a rozdělili na strany: každá strana svým vlastním názorem se holedbajíc.'),
(3442, 30, 33, 'Když dotkne se lidí protivenství, volají k Pánu svému, obráceni k němu: pak, když dal okusiti jim milosrdenství svého, hle, část jich s Pánem svým (jiná božstva) spolčují:'),
(3443, 30, 34, 'zapírajíce tak to, co dali jsme jim. Jen radujte se z požitků vezdejších — však zvíte!'),
(3444, 30, 35, 'Zdaž seslali jsme jim nějaké zmocnění, jež mluvilo by o tom, co spolčují (s Bohem)?'),
(3445, 30, 36, 'A když dáme okusiti lidem milosrdenství svého, radují se z něho: však když postihne je něco zlého za to, co byli před tím činili, hle, beznadějní jsou.'),
(3446, 30, 37, 'Což neviděli, že Bůh v hojnosti skýtá darů svých, komu chce, aneb odměňuje je? Zajisté v tomto znamení jsou pro lid věřící.'),
(3447, 30, 38, 'I dávej příbuzným svým, což patří jim: a taktéž nuzným a počestným: toť lepším jest pro ty, kdož žádají si tváře boží a tito jsou, jimž dobře povede se.'),
(3448, 30, 39, 'Cokoliv dáte na lichvu, aby tím lichvařeno bylo se jměním lidí, nebude lichvou rozmnoženo u Boha: však cokoliv dáte na (povinnou) almužnu, žádajíce si tváře boží — tito jsou ti, jimž zdvojnásobeno bude.'),
(3449, 30, 40, 'Bůh jest to, jenž stvořil vás, pak živil vás, pak dá vám zemříti, pak vzkřísí vás. Zdaž který ze společníků vašich jest s to, aby učinil s vámi v nejmenším cos podobného? Chválen budiž (Bůh) a daleko povýšen nad to, co spolčují s ním!'),
(3450, 30, 41, 'Objevila se spoušť na souši i na moři za to, co vykonaly ruce lidí, aby dala jim okusiti část výsledků činů jejich, aby tak snad (k Bohu) se obrátili.'),
(3451, 30, 42, 'Rci: „Procházejte zemí a vizte jaký byl konec těch před vámi, z nichž většina byla mnohobožná.“'),
(3452, 30, 43, 'I pozdvihni tvář svou k víře bezezměnné, dříve než přijde den od Boha, jejž nelze odvrátiti: v den ten na dva tábory rozděleni budou:'),
(3453, 30, 44, 'ten, kdož neuvěřil, nesoucí břímě nevíry své: a ten, kdož konal dobro, chystající si místo pohovení,'),
(3454, 30, 45, 'aby tak Bůh mohl odměniti ty, kteří uvěřili a konali dobré skutky, z milosti své, neboť on nemiluje nevěřících.'),
(3455, 30, 46, 'A ze znamení jeho jest, že posílá větry s radostnou zvěstí, aby okusiti dal vám milosrdenství svého, aby lodi mohly býti hnány z rozkazu jeho, abyste mohli dobývati si (podílu) z milosti jeho, a abyste možná mu byli vděčni.'),
(3456, 30, 47, 'A již před tebou poslali jsme proroky k lidu jejich i přinesli jim jasné důkazy a pomstu vykonali jsme na provinilcích; příslušelo nám pak, abychom pomohli věřícím.'),
(3457, 30, 48, 'Bůh jest to, jenž posílá větry, které dují v mračno: a rozděluje Bůh toto po obloze, jak chce, a trhá je v kusy: a pak vidíš déšť, an vychází ze středu jeho: a když dá padnouti mu na které chce ze služebníků svých, ajhle, tu radují se,'),
(3458, 30, 49, 'třebaže před tím, než byl seslán na ně, trvali v beznaději.'),
(3459, 30, 50, 'I viz tedy stopy milosrdenství božího: jak obživuje zemi zmrtvělou! Takto i Křisitelem jest mrtvých, neb on nade všemi věcmi moc má.'),
(3460, 30, 51, 'Však kdybychom poslali vichr a oni shlédli (obilí své) sežloutlé jím, tu setrvávali by poté v zatvrzelosti své.'),
(3461, 30, 52, 'Ty pak zajisté nedokážeš, aby slyšeli mrtví, aniž aby slyšeli hluší volání, když odvracejí se zády svými odcházejíce.'),
(3462, 30, 53, 'Aniž dokážeš vésti slepé od bloudění jejich: zmůžeš jen, aby slyšeli ti, kdož věří ve znamení naše a odevzdáni jsou do vůle boží.'),
(3463, 30, 54, 'Bůh to jest, jenž stvořil vás slabé, po slabosti pak dal vám sílu, a po síle pak opět učiní vás slabými a šedovlasými. Bůh tvoří, co chce, a on vševědoucí jest, mocný.'),
(3464, 30, 55, 'A v den, kdy nastane hodina (soudu), přisahati budou provinilci, že dleli pouze hodinu v zemi, tak kdysi lhali (za živa).'),
(3465, 30, 56, 'A řeknou ti, jimž dáno bylo vědění a víra: „Dleli jste tam, jak psáno jest v Knize boží, do dne zmrtvýchvstání: a tento jest den zmrtvýchvstání; však vy nebyli jste si toho vědomi.“'),
(3466, 30, 57, 'U den onen neprospěje nepravostným omluva jejich, aniž o smíření s Bohem žádáni budou.'),
(3467, 30, 58, 'A nyní učinili jsme lidem v tomto Koránu všeho druhu podobenství: a kdyby předvedeno bylo jim znamení dojista řekli by ti, kdož neuvěřili: „Vyť pouze jste šejdíři.“'),
(3468, 30, 59, 'Takovýmto způsobem zapečeťuje Bůh srdce těch, kdož nevědí.'),
(3469, 30, 60, 'I vytrvej v trpělivosti, neboť slib Boha pravdivý jest: a nechť nezviklají tě obavou ti, již (ve víře) nejisti jsou.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Alif. Lám. Mím.'),
(3471, 31, 2, 'Tato jsou znamení Knihy moudré,'),
(3472, 31, 3, 'jež vedením jest a milosrdenstvím pro dobře činící,'),
(3473, 31, 4, 'kteří zachovávají modlitbu a dávají povinnou almužnu a pevně doufají v život posmrtný.'),
(3474, 31, 5, 'Tito (dobře) vedeni jsou Pánem svým a jim dobře se povede.'),
(3475, 31, 6, 'Však jest mezi lidem kdosi, jenž kupuje kratochvilnou pověst, aby zavedl lid od stezky boží z nevědomosti a žert si z ní tropil: pro tyto připraven jest trest potupný.'),
(3476, 31, 7, 'A když čtena jsou mu znamení naše, odvrací se pyšně, jako by jich neslyšel, a jako by tíha byla v uších jeho: tomu zvěstuj trest bolestný.'),
(3477, 31, 8, 'Těm pak, kdož uvěřili a konali dobré skutky, schystány jsou zahrady rozkoše:'),
(3478, 31, 9, 'v nich přebývati budou věčně. Slib Boha pravdivý jest a on mocný jest, moudrý.'),
(3479, 31, 10, 'Onť stvořil nebesa i zemi, bez sloupů viditelných: a rozházel po zemi temena hor, aby nehýbala se s vámi: a rozptýlil po ní živočichy všeho druhu. A sesíláme s nebe vodu a dáváme vzrůstati jí všem druhům rostlin vzácných.'),
(3480, 31, 11, 'Toto vše stvořil Bůh: a nyní ukažte mi, co stvořili ti, jež vedle něho vzýváte! Ano, nepravostní jsou v bludu zjevném.'),
(3481, 31, 12, 'Kdysi pak dali jsme LOQMÁNOVI moudrost, řkouce: „Buď vděčným Bohu, neboť kdo vděčným jest Bohu, jest jím ve svůj vlastní prospěch a kdo zatvrzelým jest vůči němu — však Bůh zajisté bohatý jest a chvályhodný.“'),
(3482, 31, 13, '(Pomni), když Loqmán řekl synu svému, napomínaje jej: „Synáčku můj, nespolčuj ničeho s Bohem, neboť spolčování takové jest nepravostí velkou.“'),
(3483, 31, 14, 'A doporučili jsme člověku laskavost vůči rodičům jeho; nosila jej matka jeho s nesnází za nesnází a odkojen jest teprv po dvou letech: „Bud vděčným mně i rodičům svým, neb ke mne vše se sejde.'),
(3484, 31, 15, 'Ale snaží-li se donutiti tě, abys spolčoval se mnou, o čemž nemáš vědomosti, tehdy nebuď poslušen jich: jednej s nimi na tomto světe laskavě, ale následuj stezku toho, jenž obrací se kajícně ke mně. Poté pak navrátíte se ke mně a já povím vám o skutcích vašich.“'),
(3485, 31, 16, '„Synáčku můj, i kdyby jednalo se o váhu zrnka hořčičného, a i kdyby (skryto) bylo to ve skále, aneb na nebi či na zemi, Bůh přivede to (k účtování): Bůh zajisté prozíravý jest a o všem zpraven.'),
(3486, 31, 17, 'Synáčku můj, zachovávej modlitbu a nabádej k dobru a zabraňuj zlu, a bud trpěliv v neštěstí, jež stihnou tě, neb vše to jest věcí (předem) rozhodnutou.'),
(3487, 31, 18, 'A nekruť nijak tváře své na lidi, aniž vykračuj si pyšně na zemi: Bůh zajisté nemiluje nadutců vychloubačných.'),
(3488, 31, 19, 'Ale odměřuj chůzi svou a ponižuj nejprotivnějším hlasů všech jest věru hlas oslů!“'),
(3489, 31, 20, 'Zdaž nevidíte, že Bůh podmanil vám, cožkoliv na nebi jest i na zemi, a že zahrnul vás dobrodiními svými, zevnějšími i vnějšími? Však jsou mezi lidmi někteří, kdož hádají se ohledně Boha, aniž by měli jakého vědění, neb vědění, aniž knihy zářné.'),
(3490, 31, 21, 'A když řečeno jest jim: „Následujte to, co seslal Bůh,“ říkají: „Nikoliv: spíše následovati budeme to, co nalezli jsme u otců svých.“ Zdaž i kdyby Satan volal je k trestu plamene?'),
(3491, 31, 22, 'Ten, kdo odevzdal se do vůle boží a koná dobro, jistě chopil se ucha (nádoby) spolehlivého: a k Bohu navracejí se věci veškeré.'),
(3492, 31, 23, 'Těch pak, kdož neuvěřili, nechť nermoutí tě nevíra: k nám navrátíte se všichni a pak povíme jim, co činili. Bůh zajisté dobře ví o tajnostech niter.'),
(3493, 31, 24, 'Dáme jim těšiti se z požitku světa maličko: pak přinutíme je k (podstoupení) trestu přísného.'),
(3494, 31, 25, 'A otážeš-li se jich: „Kdo stvořil nebe i zemi?“, řeknou dojista: „Bůh.“ Rci: „Chvála budiž (tedy) Bohu!“ Však většina jich není si toho vědoma.'),
(3495, 31, 26, 'Bohu přináleží, cožkoliv na nebi jest i na zemi: neboť Bůh zajisté bohatý jest a chvályhodný.'),
(3496, 31, 27, 'I kdyby veškeré stromy na zemi staly se péry a kdyby (Bůh) rozšířil moře na sedm moří z inkoustu, nebyla by vyčerpána slova boží: Bůh zajisté mocný jest a moudrý.'),
(3497, 31, 28, 'Stvoření vás a vzkříšení vás všech jest proň jako (stvoření a vzkříšení) duše jedné: Bůh pak zajisté vše slyší a obzírá.'),
(3498, 31, 29, 'Zdaž neviděl\'s, jak Bůh zapouští noc do dne a jak zapouští den de noci, a jak podrobil slunce a měsíc (řádu), takže oba běží až do lhůty předurčené, a že Bůh o všem, co konáte, zpraven jest?'),
(3499, 31, 30, 'To proto, že Bůh jest pravda a to, co, vzýváte vedle něho, jest marnost, a že Bůh zajisté jest nejvyšší, velký.'),
(3500, 31, 31, 'Zdaž nevidíš, jak loď pluje po moři z milosti boží, aby ukázal vám znamení svá? Zajisté v tomto jest znamení pro všechny velmi trpělivé, vděčné.'),
(3501, 31, 32, 'A když zahalují je vlny jako temnoty, volají k Bohu, upřímní jsouce vůči němu ve víře; však když zachrání je na souš, jsou někteří z nich ještě na vážkách. Však znamení naše popírá jen každý věrolomník nevděčný.'),
(3502, 31, 33, 'Ó lidé, bojte se Pána svého a strachujte se dne, kdy otec nevykoupí syna, aniž syn vykoupí otce v ničem: zajisté pak slib boží pravdivý jest. Nechť neoklame vás život pozemský a nechť neoklame vás klamatel ohledně Boha.'),
(3503, 31, 34, 'Zajisté Bůh ví, kdy přijde hodina (soudu). On sesílá s nebe (hojný) déšť a ví, co skryto jest v lůnech: však žádná duše neví, čeho získá si zítra a žádná neví, ve které zemře zemi. Bůh pak zajisté vševědoucí jest a o všem zpraven!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Alif. Lám. Mím.'),
(3505, 32, 2, 'Seslání této Knihy — o niž není pochybnosti přichází od Pána veškerenstva.'),
(3506, 32, 3, 'Či snad říkají: „Vymyslil si to?“ Naopak, jest to pravda přicházející od Pána tvého, abys varoval lid, jemuž nepřišel dosud varovatel před tebou, aby (správně) byli vedeni.'),
(3507, 32, 4, 'Bůh jest to, jenž stvořil nebe i zemi a cožkoliv mezi oběma jest v šesti dnech: poté vystoupil na trůn svůj. Není vám vedle něho ochránce ni přímluvce: což si toho nerozvážíte?'),
(3508, 32, 5, 'On řídí všechny věci od nebe k zemi: poté pak všechny věci k němu navrátí se v den, jehož rozsah bude tisíc let, jak vy je počítáte.'),
(3509, 32, 6, 'On jest znatel věcí skrytých i viditelných, mocný, slitovný,'),
(3510, 32, 7, 'jenž co nejlepší dal každé věci tvar a zprvu utvořil člověka z hlíny:'),
(3511, 32, 8, 'pak ustanovil původ jeho z kapky vody odporné:'),
(3512, 32, 9, 'pak dal mu tvar a vdechl do něho část ducha svého: a dal vám sluch a zrak a srdce. Jak málo jste za to vděčni.'),
(3513, 32, 10, 'A říkají: „Když ztratíme se kdesi v zemi, zdaž v novém zjevíme se stvoření!“ A tak v setkání se s Pánem svým oni nevěří.'),
(3514, 32, 11, 'Rci: „Dá zemříti vám anděl smrti, jemuž jste poručeni, a pak k Pánu svému budete navráceni.“'),
(3515, 32, 12, 'Kdybys jen mohl viděti, jak provinilci klopiti budou hlavy své před Pánem svým (řkouce): „Pane náš, viděli jsme a slyšeli jsme: vrať nás na zemi a budeme konati dobro: nyní pevně věříme.“'),
(3516, 32, 13, 'Kdybychom byli chtěli, byli bychom dali každé duši (pravé) vedení: však vysloven byl mnou již rozsudek: „Dojista naplním peklo Džinny a lidmi pospolu.'),
(3517, 32, 14, 'Pročež okuste trestu za to, že zapomněli jste na setkání s námi v tento den váš: my též zapomněli jsme na vás i okuste trestu věčného za to, co činili jste.“'),
(3518, 32, 15, 'Jen ti věří ve znamení naše, kteří, když zmínku o nich obdrží, padají na tvář svou v UCTÍVÁNÍ a pějí chválu Pána svého, a nejsou naduti pýchou:'),
(3519, 32, 16, 'již boky své zvedajíc s lůžek svých, aby volali k Pánu svému s bázní a touhou, a z toho, čímž obdařili jsme je, štědře rozdávají.'),
(3520, 32, 17, 'A žádná duše neví, co skryto jest pro ně radostí odměnou za to, co byli činili.'),
(3521, 32, 18, 'Zdaž ten, kdo uvěřil, měl by býti roveň pohoršlivci? Nebudou si rovni.'),
(3522, 32, 19, 'Pokud se týče těch, kdož uvěřili a konali dobré skutky, těm dány budou zahrady příbytkem a obýváním v odměnu za skutky jejich.'),
(3523, 32, 20, 'A pokud se týče těch, kdož pohoršení páchali, těm příbytkem bude oheň (pekelný): pokaždé, když chtíti budou vylézti z něho, navráceni do něho budou a bude řečeno jim: „Okuste trestu ohně, jejž vylhaným jste nazývali.“'),
(3524, 32, 21, 'A dojista dáme jim okusiti trestu menšího vedle trestu většího, aby snad vrátili se k nám.'),
(3525, 32, 22, 'Kdo nepravostnějším jest toho, jenž když připomenuta jsou mu znamení naše, odvrátí se od nich? Myť zajisté pomstíme se na provinilých!'),
(3526, 32, 23, 'A kdysi dali jsme Mojžíšovi Knihu: i nebuď v pochybnostech o jeho setkání (s námi). A učinili jsme ji vedením synům Israele.'),
(3527, 32, 24, 'A z nich ustanovili jsme vůdce, aby vedli lid dle rozkazu našeho poté, když byli trpělivě vytrvávali a pevně uvěřili ve znamení naše.'),
(3528, 32, 25, 'Zajisté pak Pán tvůj rozhodne mezi nimi v den zmrtvýchvstání ohledně toho, v čem rozcházeli se.'),
(3529, 32, 26, 'Což není jim patrno kolik zahladili jsme před nimi pokolení, v jichž obydlích oni teď procházejí se? V tom zajisté znamení jsou: což nepopřejí (jim) sluchu?'),
(3530, 32, 27, 'Což nevidí, jak ženeme v mračnech vodu k zemi vyprahlé a jí vzcházeti dáváme obilí, z něhož požívají dobytčata jejich i oni sami? Což (toho) nevidí?'),
(3531, 32, 28, 'A říkají: „Kdy tedy přijde, ono skoncováním jste-li pravdomluvnými?“'),
(3532, 32, 29, 'Rci: „V den skoncování neprospěje těm, kdož nevěřili, víra jejich, aniž bude jim poshověno.“'),
(3533, 32, 30, 'I straň se jich a vyčkávej: oni též vyčkávají.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Ó proroku, boj se Boha a neposlouchej nevěřících a pokrytců: Bůh zajisté vševědoucí jest a moudrý.'),
(3535, 33, 2, 'A následuj, co vnuknuto ti bylo Pánem tvým: zajisté Pán tvůj o tom, co činíte, dobře zpraven jest.'),
(3536, 33, 3, 'A spoléhej na Boha: neb Bůh postačitelným jest ochráncem.'),
(3537, 33, 4, 'Bůh nedal člověku dvé srdcí v nitru jeho, aniž učinil manželky vaše, které zapuzujete, jako matky vaše: aniž učinil přijaté syny vaše, jako krevní syny vaše. Tato slova nalézají se v ústech vašich, ale Bůh mluví pravdu a vede cestou pravou.'),
(3538, 33, 5, '(Přijaté syny) nazývejte jmény otců jejich: toto spravedlivějším jest u Boha, a neznáte-li otců jejich, nechť jsou bratry vašimi ve víře a soudruhy vašimi. A není viny vám tehdy, když mýlíte se v tom, aniž by za tím byl úmysl srdcí vašich: Bůh pak velký jest v odpouštění, slitovný.'),
(3539, 33, 6, 'Prorok bližší jest věřícím jich samých a manželky jeho jsou matky jejich. Rodní bratři a sestry jsou si navzájem bližší, dle Knihy boží, věřících a vystěhovalých: leda byste cos dobrého učinili přátelům svým; to zajisté v Knize jest (již) zapsáno.'),
(3540, 33, 7, 'A (pomni), když učinili jsme smlouvu s proroky: s tebou, a s Noemem, a s Abrahamem, a s Mojžíšem, a Ježíšem, synem Mariiným: a učinili jsme s nimi smlouvu přísnou:'),
(3541, 33, 8, 'aby Bůh mohl dotazovati se pravdomluvných ohledně pravdy jejich. Neb připravil nevěřícím trest bolestný.'),
(3542, 33, 9, 'Vy, kteříž jste uvěřili, buďte pamětlivi milosti boží dané vám, když přišla na vás vojska a poslali jsme na ně vichr a vojska neviditelná vám: a Bůh přihlížel k tomu, co činili jste,'),
(3543, 33, 10, 'Tehdy, když napadali vás shůry i zdola, a když zatemňovali se zrakové vaši a srdce vaše vylézala vám ač k hrdlúm: tehdy domýšleli jste si o Bohu mnohé domněnky.'),
(3544, 33, 11, 'Na onom místě velkou zkoušku podstupovali věřící a třásli se třesením silným.'),
(3545, 33, 12, 'A když pokrytci a ti, v jichž srdci jest choroba, říkali: „Slíbil nám Bůh a prorok jeho pouhý klam.“'),
(3546, 33, 13, 'A když říkal jeden sbor jich: „Ó obyvatelé Jathribu, není zde místa (útulku) pro vás: i vraťte se.“ A žádala jiná část jich proroka o dovolení k návratu, řkouce: „Zajistéť domy naše bez ochrany jsou.“ Však nebyly bez ochrany a oni pouze chtěli prchnouti.'),
(3547, 33, 14, 'A kdyby byl (nepřítel) vešel do města se všech stran a oni vyzváni byli k rozbroji, byli by jej způsobili; však byli by zůstali v něm jen na krátko.'),
(3548, 33, 15, 'A přece před tím byli zavázali se Bohu, že neobrátí se zády svými: a za slib daný Bohu nutno zodpovídati se.'),
(3549, 33, 16, 'Rci: „Neprospěje vám útěk, neb prchnete-li smrti či zabití, tehdy těšiti budete se (světu) pouze maličko.“'),
(3550, 33, 17, 'Rci: „Kdo jest ten, který ochrání vás před Bohem, bude-li chtíti poslat na vás zlo, aneb bude-li chtít popřáti vám milosrdenství?“ Nenaleznou vedle Boha sobě ochránce, ni pomocníka.'),
(3551, 33, 18, 'Bůh dobře zná působitele překážek mezi vámi a ty, již říkají bratřím svým: „Pojďte k nám,“ a ukazují udatnost jen malou.'),
(3552, 33, 19, 'To z lakoty vůči vám: a když přijde (chvíle) strachu, tu vidíš je ani hledí na tebe, točíce očima svýma jako ten, na něhož padají mrákoty smrti. Však když zajde (chvíle) strachu, napadají vás ostrými jazyky svými z lakoty pro nejlepší část (kořisti). Tito nemají víry a propadnouti dá Bůh konáním jejich: a věru jest to Bohu snadným.'),
(3553, 33, 20, 'Domnívali se, že SPOJENCI neodejdou: a kdyby opět přišli spojenci, rádi by obývali mezi Araby pouště, vyptávajíce se jich na zprávy o vás: neboť ač byli s vámi, bojovali pouze nepatrně.'),
(3554, 33, 21, 'Zajisté pak měli jste v proroku božím vzor krásný pro všechny, kdož doufají v setkání s Bohem a v den poslední, a vzpomínají Boha často.'),
(3555, 33, 22, 'A když viděli věřící spojence řekli: „Toto jest, co slíbil nám Bůh a prorok jeho: a pravdu děli Bůh a prorok jeho“ a vše to rozhojnilo v nich víru a odevzdanost do vůle boží.'),
(3556, 33, 23, 'Jsou mezi věřícími muži, kteří dodrželi pravdivě závazek svůj vůči Bohu: někteří dosloužili čas svůj, někteří vyčkávají naplnění jeho a nezměnili se nijakou změnou.'),
(3557, 33, 24, 'Věru pak odmění Bůh upřímné za upřímnost jejich a ztresce pokrytce, bude-li chtíti, anebo s odpuštěním obrátí se k nim, neb Bůh velký jest v odpouštění, slitovný.'),
(3558, 33, 25, 'A odrazil Bůh ty, kteří neuvěřili, v zuřivosti jejich a nedosáhli ničeho dobrého. Bůh dostačí věřícím v boji a Bůh zajisté silný jest a mocný.'),
(3559, 33, 26, 'A způsobil, aby ti z lidu obdrževšího Knihu, kteří pomáhali spojencům, vyšli z pevností svých a vrhl v srdce jejich strach, takže pobili jste část jich a část jich zajali.'),
(3560, 33, 27, 'Bůh učinil vás dědici země jejich a obydlí jejich a statků jejich: i země, jíž dosud nedotkla se noha vaše: neboť Bůh nade všemi věcmi moc má.'),
(3561, 33, 28, 'Ó proroku, rci manželkám svým: „Žádáte-li si života pozemského a zdoby jeho, pojďte: dám vám pěkný podíl a propustím vás propuštěním pěkným.'),
(3562, 33, 29, 'Však žádáte-li si Boha a proroka jeho a příbytku v živote budoucím, Bůh zajisté přichystal těm z vás, jež dobře činí, odměnu velkou.'),
(3563, 33, 30, 'Ó ženy prorokovy, dopustí-li se která z vás smilstva dokázaného, zdvojnásoben jí bude trest podílem dvojím: a Bohu bude to snadným.'),
(3564, 33, 31, 'Však bude-li kterákoli z vás pevně oddána Bohu a prorokovi jeho a bude konati dobro, dáme jí odměnu její dvakráte a připravili jsme pro ni podíl štědrý.'),
(3565, 33, 32, 'Ó ženy prorokovy, nejste jako jiné ženy: bojíte-li se Boha, tedy nebuďte příliš blahosklonnými v řeči, aby nepožádal vás ten, v jehož srdci choroba jest: ale mluvte řečí přístojnou.'),
(3566, 33, 33, 'A zůstávejte klidně v domech svých a nevystrojujte se vystrojováním dob nevědomosti své dřívější: a zachovávejte modlitbu a dávejte povinnou almužnu a buďte poslušny Boha a proroka jeho: Bůh jedině chce odejmouti od vás nečistotu domu (svého) a očistiti vás očištěním.'),
(3567, 33, 34, 'A připomínejte si, což čteno jest v domech vašich ze znamení Boha a moudrosti: neboť Bůh prozíravý jest a o všem zpraven.“'),
(3568, 33, 35, 'Mužové a ženy, odevzdaní do vůle boží, věřící mužové a ženy, oddaní mužové a ženy, pravdomluvní mužové a ženy, trpělivě vyčkávající mužové a ženy, ponížení mužové a ženy, almužnu dávající mužové a ženy, postící se mužové a ženy, počestnost zachovávající mužové a ženy, Boha často vzpomínající mužové a ženy — těmto všem přichystal Bůh odpuštění a odměnu velkou.'),
(3569, 33, 36, 'Nepřísluší věřícímu muži neb ženě, aby, když rozhodne Bůh a prorok jeho nějakou věc, měli svou vlastní vůli ve věci své: a kdo neposlušen jest Boha a proroka jeho, tenť zbloudil zblouděním zjevným.'),
(3570, 33, 37, 'A (pomni), když řekl\'s onomu, jemuž dobrodiní prokázal Bůh a jemuž i ty dobrodiní prokázal\'s: „Podrž manželku svou a boj se Boha,“ však skrýval\'s v nitru svém, co Bůh měl přivésti na světlo: a ty bál ses lidí, však spíše měl ses báti Boha. Však když konečně docílil Zeid čeho si žádal od (ženy své), oženili jsme tě s ní, aby nebylo věřícím viny v ženění se s (manželkami přijatých synů svých, když tito byli docílili, čeho si žádali od nich. A rozkaz boží má býti vykonán.'),
(3571, 33, 38, 'Nebylo viny prorokovi v tom, co povinným učinil mu Bůh. Taková byla zvyklost Boha v jednání s těmi, kdož před tebou byli a rozkaz boží je určením předurčeným.'),
(3572, 33, 39, 'S těmi, kdož hlásají poslání boží a bojí se Boha, ale nebojí se nikoho, vyjma Boha: a Bůh dostačitelným jest súčtovatelem.'),
(3573, 33, 40, 'Není Mohammed otcem nikterého muže mezi vámi, nýbrž prorokem Boha a pečetí proroků a Bůh o všech věcech má vědomost.'),
(3574, 33, 41, 'Vy, kteříž jste uvěřili, vzpomínejte Boha vzpomínáním častým:'),
(3575, 33, 42, 'a chvalte jej z jitra i za večera.'),
(3576, 33, 43, 'Onť jest to, jenž žehná vám i andělé jeho, aby vyvedl vás z temnot na světlo: a vůči věřícím jest slitovným.'),
(3577, 33, 44, 'Přivítání jich v den, kdy setkají se s Bohem, bude: „Pokoj vám“ a on přichystal jim odměnu štědrou.'),
(3578, 33, 45, 'Ó proroku, myť zajisté poslali jsme tě jako svědka, věštitele radostných zvěstí a varovatele:'),
(3579, 33, 46, 'a jako toho, jenž zve (lidi) k Bohu z dovolení jeho, a jako svítilnu zářnou.'),
(3580, 33, 47, 'I zvěstuj věřícím, že dojista dána jim bude Bohem milost velká:'),
(3581, 33, 48, 'a neposlouchej nevěřících a pokrytců, však upusť od ubližování jim: však spoléhej na Boha, neb Bůh dostačitelným jest ochráncem.'),
(3582, 33, 49, 'Vy, kteříž jste uvěřili, pojali-li jste za manželky ženy věřící, poté pak zapudili jste je dříve, než byli dotkly jste se jich, nemáte nijaké lhůty ohledně podržení jich: ale poskytněte jim podílu jejich a pak propusťte je propuštěním pěkným.'),
(3583, 33, 50, 'Ó proroku, dovolili jsme ti ženy tvé, jimž dal jsi věna jejich; a ty, jichž zmocnila se pravice tvá z kořisti dané ti Bohem: a dcery tvých strýců a tet se strany otcovy, a dcery tvých strýců a tet se strany matčiny, jez vystěhovaly se s tebou: a každá žena věřící, jež odevzdala osobu svou prorokovi, chce-li prorok oženiti se s ní. To zvláštní (výsadou) jest tobě nade všechny věřící. Dobře víme, co povinným učinili jsme jim ohledně manželek jejich a těch, jichž zmocnila se pravice jejich, aby nebylo tobě viny. A Bůh velký jest v odpoustění, slitovný.'),
(3584, 33, 51, 'Poodlož, kteroukoli bys chtěl z nich a vezmi k sobě na lože, kteroukoli bys chtěl, aneb kterékoliv bys žádal si z těch, které jsi byl od sebe oddálil: a nebude tobě viny v tom. Tak snadnějším bude, abys obradoval srdce jejich, a aby nermoutily se, a aby spokojeny byly s tím, co dáš jim, jednakaždá. A Bůh ví, co v srdcích vašich jest, neb Bůh jest vševědoucí, laskavý.'),
(3585, 33, 52, 'Nejsou dovoleny ti ženy od nynějška, aniž dovoleno jest ti, abys vyměňoval za manželky své jiné, i kdyby oslnila tě krása jejich, vyjma žen, jichž zmocnila se pravice tvá: a Bůh na všechny věci pozor dává.'),
(3586, 33, 53, 'Vy, kteříž jste uvěřili, nevstupujte v domy prorokovy, leda že dovoleno bylo vám vstoupiti k pojedení, aniž byste vyčkávali až bude (jídlo hotovo). Však když pozváni jste, vejděte a když pojedli jste, rozejděte se: a nevcházejte v příliš důvěrný rozhovor, neboť to bolestně dotklo by se proroka a byl by nucen styděti se k vůli vám; však Bůh nestydí se za pravdu. A když žádáte je o nějaký předmět, žádejte oň zpoza clony: to bude čistším pro srdce vaše a srdce jejich. A není přípustno, abyste bolestně dotýkali se proroka božího, aniž ženili se s manželkami jeho po něm nikdy: neb bylo by to u Boha provinění velké.'),
(3587, 33, 54, 'A přiznáváte-li co veřejně, aneb skrýváte to, vězte, že Bůh zajisté o všech věcech vědomost má.'),
(3588, 33, 55, 'Nebude viny jim, (když odkryjí tváře své) před otci svými, neb syny svými, aneb bratry svými, neb syny bratrů svých, neb syny sester svých, neb ženami jejich, nebo před otrokyněmi svými. A bojte se Boha (ženy): Bůh zajisté všech věcí jest svědkem.'),
(3589, 33, 56, 'Zajisté pak Bůh a andělé jeho žehnají prorokovi: pročež i vy, kteříž uvěřili jste, žehnejte mu a zdravte jej pozdravem.'),
(3590, 33, 57, 'Zajisté pak těm, kdož ubližují Bohu a proroku jeho, zlořečil Bůh v životě pozemském i budoucím a schystal jim trest potupný.'),
(3591, 33, 58, 'A ti, kdož urážejí věřící muže a ženy, aniž by si toho byli zasloužili, obtížili se pomluvou a hříchem zjevným.'),
(3592, 33, 59, 'Ó proroku, rci manželkám svým a dcerám svým a ženám věřících, aby spouštěly níže svrchní roucha svá: tak snadnějším bude, aby byly poznány a nebyly uraženy kým: a Bůh velký jest v odpouštění, slitovný.'),
(3593, 33, 60, 'Nepřestanou-li pokrytci a ti, jichž srdce stižena jsou chorobou, a rýpalové v Medíně, dojista rozčilíme tě proti nim: a pak dojista nenecháme je tam býti sousedy tvými, leda na krátko.'),
(3594, 33, 61, 'Zlořečeni jsouce, kdekoliv by nalézali se, jímáni budou a povražděni vražděním.'),
(3595, 33, 62, 'Taková byla zvyklost Boha ohledně těch, kdož před nimi byli: a neshledáš ve zvyklostech Boha změny nijaké.'),
(3596, 33, 63, 'Dotazovati budou se tě lidé ohledně hodiny (soudu). Rci: „Vědomost o ní má jen Bůh a kdo může říci ti, není-li snad hodina (soudu) blízka?“'),
(3597, 33, 64, 'Bůh zajisté zlořečil nevěřícím a přichystal jim plamen (pekelný):'),
(3598, 33, 65, 'v něm přebývati budou věčně věkův a nenaleznou ochránce, ni pomocníka.'),
(3599, 33, 66, 'V den, kdy otáčeny budou tváře jejich v ohni, řeknou: „Kéž bychom byli poslušni bývali Boha a poslušni bývali proroka.!“'),
(3600, 33, 67, 'A řeknou: „Pane náš, my poslušni byli jsme pánů a předáků svých: a oni zavedli nás se stezky (pravé).'),
(3601, 33, 68, 'Pane náš, dej jim trest dvojnásobný a zlořeč jim zlořečením velkým!“'),
(3602, 33, 69, 'Vy, kteříž jste uvěřili, nebuďte jako ti, kteří urazili Mojžíše: Zprostil pak jej Bůh viny v tom, co tvrdili, a (Mojžíš) byl u Boha velmi ctěn.'),
(3603, 33, 70, 'Vy, kteříž jste uvěřili, bojte se Boha a mluvte mluvou přímou.'),
(3604, 33, 71, 'Zlepší vám pak Bůh skutky vaše a odpustí vám viny vaše: a kdo poslušen jest Boha a proroka jeho, oblažen bude blažeností velkou.'),
(3605, 33, 72, 'Nabídli jsme zástavu (víry) nebesům a zemi a horám, však odmítly vzíti ji na se a chvěly se bázní před ní: i vzal ji na se člověk, však zajisté stal se velmi nepravostným, nevědomým;'),
(3606, 33, 73, 'proto, aby ztrestal Bůh pokrytce, muže i ženy, a mnohobožce, muže i ženy: a aby obrátil se (s odpuštěním) k věřícím mužům a ženám, neboť Bůh velký jest v odpouštění, slitovný!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Chvála Bohu, jemuž přináleží, cožkoliv na nebi jest i na zemi: a jemuž přináleží chvála v životě budoucím: onť moudrý jest, o všem zpravený.'),
(3608, 34, 2, 'Ví, co vchází do země a co vychází z ní: a co sestupuje s nebe i co vstupuje na ně: a on slitovný jest, velký v odpouštění.'),
(3609, 34, 3, 'Říkají ti, kdož neuvěřili: „Nepřijde na nás hodina (soudu).“ Rci: „Naopak, při Pánu svém přisahám, že přijde dojista na vás!“ Onť zná věci skryté a neujde mu ani co obnáší váha zrnéčka, na nebi i na zemi; aniž co menšího toho, aneb většího jest, co nebylo by v Knize zjevné,'),
(3610, 34, 4, 'aby Bůh odměnil mohl ty, kdož uvěřili a konali dobré skutky: těmto dáno bude odpustění a odměna hojná.'),
(3611, 34, 5, 'Však těm, kdož snažili se znemožniti znamení naše, těm schystán jest trest muk bolestných.'),
(3612, 34, 6, 'A dobře vidí ti, jimž dáno bylo vědění, že to, co sesláno ti bylo od Pána tvého, jest pravdou a že vede to ke stezce Mocného, Chvályhodného.'),
(3613, 34, 7, 'A říkají ti, kteří neuvěřili: „Chcete, abychom ukázali vám muže, jenž poví vám, že až budete rozpadnuti úplným rozpadnutím, vzbuzeni budete ve stvoření novém?'),
(3614, 34, 8, 'Vymyslil lež o Bohu, aneb posedlí jest Džinny.“ Naopak, ti, kdož nevěří v život budoucí, ztrestáni budou a v bludu dalekém se nalézají.'),
(3615, 34, 9, 'Což nevidí to, co před nimi jest a za nimi z nebe a země? Kdybychom chtěli, rozpoltili bychom zem pod nimi, aneb dali bychom spadnouti na ně kusu oblohy: zajisté v tomto jest znamení pro všechny služebníky boží kajícné.'),
(3616, 34, 10, 'A kdysi poskytli jsme Davidovi milosti své (řkouce): „Ó hory, pějte s ním, i vy, ptáci,“ a změkčili jsme proň železo,'),
(3617, 34, 11, '(řkouce): ,,Rob brnění a uspořádávej kruhy (jeho): a konejte dobro, neb já to, co konáte, (dobře) obzírám.“'),
(3618, 34, 12, 'A (podmanili jsme) Šalamounovi vítr: jitřní (vanutí) jeho trvalo měsíc a i večerní (vanutí) jeho trvalo měsíc: a dali jsme vyvříti mu pramen tekuté mosazi: a mezi Džinny byli někteří, kdož pracovali pod dozorem jeho z dovolení Pána jeho a kterémukoliv z nich, jenž odchýlil se od rozkazu našeho, okusiti dáme trestu plamene.'),
(3619, 34, 13, 'Konali proň, cokoliv chtěl z vysokých kleneb a soch; a tácy jak nádrže (napájecí) a kotly (k vaření) pevně spájené. Čiň, rode Davidův, díky!, neb málo jest služebníků mích vděčných.'),
(3620, 34, 14, 'A když usoudili jsme mu smrt, nic neukazovalo na to, že jest mrtev, vyjma živočicha zemského, jenž hryzl hůl (jej podpírající). A když spadl, bylo jasno Džinnům, že kdyby znali věci skryté, nebylo by jim bývalo dlíti tak dlouho v trestu potupném.'),
(3621, 34, 15, 'Obyvatelé SABY měli pak v sídle svém znamení: dvé zahrad, (jednu) se strany pravé a (druhou) se strany levé: „Požívejte z výživy, dané vám Pánem vaším a buďte mu vděčni: krásnou máte zemi a Pána milostivého.“'),
(3622, 34, 16, 'Však odvrátili se i poslali jsme na ně záplavu hrází a zaměnili jsme dvě zahrady jejich za jiné dvě zahrady, vydávající plody trpké a tamaryšky, a stromů lotusových něco málo.'),
(3623, 34, 17, 'Takto odměnili jsme je za to, že byli nevděčni: však odměníme tak jiné, než nevděčníky?'),
(3624, 34, 18, 'A učinili jsme mezi nimi a mezi městy, jimž požehnali jsme, města patrná a určili jsme v zemi cestu (řkouce): „Cestujte po ní za nocí i za dnů v bezpečí.“'),
(3625, 34, 19, 'Však řekli: „Pane náš, učiň delšími cesty naše“ a tak prohřešili se proti sobě samým. I učinili jsme je příslovečnými a rozervali je úplným rozerváním: zajisté v tomto jest znamení pro všechny trpělivě vyčkávající a vděčné.'),
(3626, 34, 20, 'A shledal Iblís, že správným byl jeho, úsudek o nich; i následovali jej všichni, vyjma hloučku věřících.'),
(3627, 34, 21, 'Ale neměl nad nimi jiné moci, než oné, jíž bychom rozeznati mohli ty, kdož uvěřili v život budoucí, od těch, kdož byli v pochybnostech o něm: a Bůh všechny věci střeže.'),
(3628, 34, 22, 'Rci: „Zavolejte na ty, jež mníte (býti) vedle Boha: vězte, že nemají moci ani nad tím, co obnáší váha zrnéčka, na nebi a na zemi: aniž mají podílu jakého ve (stvoření) obou, aniž má Bůh mezi nimi pomocníka.“'),
(3629, 34, 23, 'Neprospěje nijaká přímluva u Boha, leda tomu, jemuž dovolí (Bůh): teprve když zbavena budou srdce jejich strachu, řeknou: „Co řekl Pán váš?“ Řeknou: „Pravdu.“ A on nejvyšší jest, velký.'),
(3630, 34, 24, 'Rci: „Kdo vyživuje vás s nebes i ze země?“ Rci: „Bůh. A buď my, či vy jste na (správné) cestě, aneb v bloudění zjevném.“'),
(3631, 34, 25, 'Rci: „Nebudete tázáni ohledně provinění našich a my nebudeme tázáni ohledně toho, co vy činíte.“'),
(3632, 34, 26, 'Rci: „Shromáždí nás všechny Pán nás a pak rozsoudí mezi námi dle pravdy: neb on jest Rozsuzovatel, Vševědoucí.“'),
(3633, 34, 27, 'Rci: „Ukažte mi ty, jež připojili jste k němu co společníky! Nikoliv! Naopak on jest Bůh, mocný, moudrý.“'),
(3634, 34, 28, 'A poslali jsme tě nejinak, než abys všem lidem hlásal zvěst radostnou a varoval je: však většina lidí není si toho vědoma.'),
(3635, 34, 29, 'A říkají: „Kdy (naplní se) onen slib, jste-li pravdomluvnými?“'),
(3636, 34, 30, 'Rci: „Vám bude dostaviti se (jednoho) dne, jehož příchod ani nezpozdíte o hodinu, aniž uspíšíte.“'),
(3637, 34, 31, 'A říkají ti, kdož neuvěřili: „Neuvěříme v tento Korán, ani v to, co bylo již zjeveno.“ Však kdybys jen mohl viděti nepravostné, ani státi budou před Pánem svým a metati jeden na druhého slova (výčitek)! I říkati budou ti, kdož slabými byli, k mocným: „Nebýti vás, byli bychom uvěřili.“'),
(3638, 34, 32, 'A řeknou ti, kdož mocnými byli k slabým: „Zdaž my jsme odvrátili vás od (pravého) vedení poté, když přišlo vám? Naopak: vy sami toho nesete vinu!“'),
(3639, 34, 33, 'A řeknou ti, kdož slabými byli, k mocným: „Naopak, byla to lest (vaše) za noci a za dne, když nabádali jste nás, abychom nevěřili v Boha a abychom činili mu rovné.“ A skrývati budou lítost svou, když spatří muka svá. A řetězy obtěžkáme šíje těch, kdož neuvěřili: zdaž jinak budou odměněni, než dle toho, co činili?'),
(3640, 34, 34, 'A neposlali jsme nikdy městu varovatele, aniž by nebyli řekli přebytku těšící se v něm: „Myť zajisté v to, s čím posláni jste byli, nevěříme.“'),
(3641, 34, 35, 'A říkali: „My bohatšími jsme statky i dětmi a nebudeme ztrestáni.“'),
(3642, 34, 36, 'Rci: „Zajisté Pán můj hojně obdarovává (výživou) koho chce, aneb odměřuje (dary své): však většina lidí není si toho vědoma.“'),
(3643, 34, 37, 'A nebudou to statky a děti vaše, jimiž dostanete se blíže k nám: však jen těm, kdož uvěřili a konali dobré skutky, dostane se odměny dvojnásobné za to, co konali: a oni (v povýšených) komnatách ráje bezpečni budou.'),
(3644, 34, 38, 'Však ti, kteří snaží se znemožniti znamení naše, v trest svůj uvedeni budou.'),
(3645, 34, 39, 'Rci: „Zajisté Pán můj hojně obdarovává (výživou), koho chce ze služebníků svých, aneb odměřuje (dary své): a cokoliv vydáte almužnou, zpět bude vám dáno, neb on nejlepším jest ze všech obdarovatelů.'),
(3646, 34, 40, 'Jednoho dne pak shromáždí je Bůh všechny, i řekne andělům: „Zdaž onino vás uctívali?“'),
(3647, 34, 41, 'Řeknou (andělé): „Chválen budiž! Tys spojencem naším, ne oni. Naopak, uctívali Džinny: většina jich v ně věřila“'),
(3648, 34, 42, 'V den onen nebudete s to, navzájem přičiniti si ani užitku, ani škody: a řekneme k těm, kdož nepravostni byli: „Okuste trestu ohně, jejž vylhanými jste nazývali.“'),
(3649, 34, 43, 'A když čtena jsou jim znamení naše jasná, říkají: „Totoť jest pouze muž, jenž chce odvrátiti vás od toho, co uctívali otcové vaši“ a říkají: „Totoť pouze lež jest, jím vymyšlená,“ a říkají ti, kdož neuvěřili, o pravdě, když předvedena jim jest: „Totoť zajisté jest jen kouzelnictví zjevné.“'),
(3650, 34, 44, 'A nedali jsme jim nijakých knih, z nichž by se učili, aniž poslali jsme jim před tebou varovatele.'),
(3651, 34, 45, 'A také ti, kdož byli před nimi, nazývali lháři proroky naše: a (oni) nedosáhli ani desetiny z toho, co dali jsme jim. Ale lháři nazývali proroky mé; pročež jak zlý byl trest můj!'),
(3652, 34, 46, 'Rci: „Radím vám pouze jedno: abyste postavili se před Boha, po dvou, aneb po jednom a pak rozvážili si: není soudruh váš posedlý Džinnem: onť pouze jest varovatelem vám před nedalekým trestem přísným.“'),
(3653, 34, 47, 'Rci: „Nežádám si od vás odměny, neboť odměna má záleží jen od Boha a on všech věcí jest svědkem.“'),
(3654, 34, 48, 'Rci: „Zajisté Pán můj vysílá jen pravdu; onť velmi dobře zná věci skryté.“'),
(3655, 34, 49, 'Rci: „Přišla pravda a neobjeví se marnost a nevrátí se.“'),
(3656, 34, 50, 'Rci: „Zbloudil-li jsem, pak jen ke vlastní škodě bloudím: a jsem-li veden cestou (pravou), jest to ze vnuknutí Pána mého, neboť zajisté on vše slyší a jest blízek.“'),
(3657, 34, 51, 'A kdybys viděl, jak třásti budou se (bázní) a nebude jim východiska: a jak jati budou z místa blízkého!'),
(3658, 34, 52, 'A řeknou (pak): „Uvěřili jsme v něho.“ Ale jak dosáhnou (víry) z místa dálného,'),
(3659, 34, 53, 'když nevěřili v něho před tím a vrhali posměšky na věci skryté z místa dálného?'),
(3660, 34, 54, 'A přehrada bude mezi nimi a tím, po čem toužiti budou: jakž učiněno bylo s podobnými jim před tím; neb zajisté byli v pochybnostech váhavých.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Chvála Bohu, STVOŘITELI nebes i země, jenž činí ANDĚLY posly svými: anděly, mající křídel dvé, neb tré, neb čtvero párů. Onť přidává stvoření svému, co chce: neboť Bůh zajisté nade všemi věcmi moc má.'),
(3662, 35, 2, 'Cokoliv Bůh popřeje lidem z milosrdenství svého, nikdo nemůže jim odebrati, a cokoliv Bůh odebéře z něho, nikdo nemůže poslati poté: a on mocný jest, moudrý.'),
(3663, 35, 3, 'Ó lidé, pomněte dobrodiní daných vám Bohem! Což jiného jest Stvořitele, vyjma Boha? On vyživuje vás s nebe a ze země: není Boha, kromě něho: jak tedy můžete (tak) lháti?'),
(3664, 35, 4, 'A nazývají-li tě lhářem, věz, že lháři nazýváni byli proroci před tebou: a k Bohu navráceny budou (všechny) věci.'),
(3665, 35, 5, 'Ó lidé, slib boží jest pravdivý, i nedejte oklamati se životem pozemským a nedejte oklamati se ohledně Boha klamatelem.'),
(3666, 35, 6, 'Dojista Satan jest nepřítelem vám a proto považujte jej za nepřítele: on jen volá spojence své k sobě, aby stali se obyvateli ohně pekelného.'),
(3667, 35, 7, 'Těm, kdož neuvěřili, dán bude trest přísný: však těm, kdož uvěřili a konali dobré skutky, dáno bude odpuštění a odměna hojná.'),
(3668, 35, 8, 'Zdaž ten, jemuž zkrášleno bylo zlo konání jeho, takže vidí je dobrým, (bude jako muž dobrý)? Ale Bůh zavádí v blud, koho chce, a vede (pravou) cestou koho chce; nenič tedy duše své pro ně nářkem, neboť Bůh zajisté dobře ví o tom, co konají.'),
(3669, 35, 9, 'Bůh jest to, jenž posílá větry, aby zdvihaly mračna: pak ženeme je ke zmrtvělé končině a křísíme vodou zemi zmrtvělou. Stejně tak bude vzkříšení (mrtvých).'),
(3670, 35, 10, 'Žádá-li si kdo moci, (věziž), že moc veškerá jest u Boha: k němu stoupá slovo dobré a činy dobré on k sobě pozdvihuje. Těm pak, kdož lstivě vymýšlejí zlé skutky, dán bude trest přísný: a lest těchto on uvede niveč.'),
(3671, 35, 11, 'Bůh stvořil vás z prachu, poté z kapky (semene): pak učinil vás dvěma pohlavími: a nenosí, aniž porodí čeho samice bez vědomí jeho: a nestárne stařec, aniž co odejmuto jest ze stáří jeho, aby nebylo to v Knize: toto pak Bohu jest snadným.'),
(3672, 35, 12, 'A nejsou si rovna obě moře: jedno jest sladké, čerstvé a snadné k pití; druhé jest solné, hořké — nicméně z obou požíváte masa (ryb) čerstvých a lovíte z nich předměty k ozdobě, jimiž se odíváte: a vidíš lodi, brázdící vlny jejich, aby hledali jste si podílu z milosti Boha a abyste mu za to byli vděčni.'),
(3673, 35, 13, 'Onť dává vcházeti noci do dne a dává vcházeti dnu do noci: a podmanil (řádu) slunce i měsíc, takže oba pohybují se dle lhůty předurčené; totoť jest Bůh, Pán váš: jemu přináleží moc veškerá a ti, jež vzýváte vedle něho, nemají v moci ani co obnáší slupka jádra datlového.'),
(3674, 35, 14, 'Budete-li volati k nim, nebudou slyšeti volání vašeho: a i kdyby slyšeli je, nemohli by vyslyšeti vás a v den zmrtvýchvstání zapřou vaše spolčování (jich s Bohem): a nikdo nemůže ti o tom pověděti jako Ten, jenž o všem zpraven jest.'),
(3675, 35, 15, 'Ó lidé, chuďasové jste, (potřebující) Boha: však Bůh jest bohatý, chvályhodný.'),
(3676, 35, 16, 'Kdyby chtěl, mohl by vám dát zajíti a nahraditi vás stvořením novým:'),
(3677, 35, 17, 'a toto nebylo by Bohu nesnadným.'),
(3678, 35, 18, 'A neponese (duše) obtěžkaná břemene duše druhé: a když volati bude (duše) přetížená, aby neseno bylo břímě její, nebude uleveno jí v ničem, i kdyby to byl příbuzný. Varovati pak budeš jen ty, kdož bojí se Pána svého v skrytosti a zachovávají modlitbu: a kdokoliv snaží se býti čistým, činí tak ve prospěch své vlastní duše, neb k Bohu vede cesta (návratu).'),
(3679, 35, 19, 'Nejsou si rovni slepý a vidoucí,'),
(3680, 35, 20, 'aniž temnoty a světlo,'),
(3681, 35, 21, 'aniž stín a horko:'),
(3682, 35, 22, 'aniž rovni jsou si živí a mrtví: Bůh pak dá slyšeti, komukoliv bude chtíti a ty nedokážeš, aby slyšeli tě oni, kdož v hrobech jsou:'),
(3683, 35, 23, 'neb tys jen varovatelem.'),
(3684, 35, 24, 'Zajisté pak poslali jsme tě v pravdě, jako věštitele (dobré zvěsti) a jako varovatele: a není národa, k němuž by nebyl býval poslán varovatel.'),
(3685, 35, 25, 'A nazývají-li tě lhářem, však již ti, k nimž před nimi přišli proroci jejich s jasnými důkazy a písmy a Knihou zářnou, lháři je nazývali.'),
(3686, 35, 26, 'Poté pak (trestem) postihl jsem ty, kdož neuvěřili, a jak (zlý byl) trest můj!'),
(3687, 35, 27, 'Zdaž nevidíš, jak Bůh seslal s nebe vodu, abychom dali vzrůsti jí plodům různých barev? A že na horách pruhy jsou bílé a červené, odstínů různých, a některé černé:'),
(3688, 35, 28, 'a mezi lidmi, živočichy a dobytčaty nalézají se barvy různé taktéž? Ale Boha bojí se jen služebníci jeho nejmoudřejší: Bůh pak zajisté mocný jest a velký v od-puštění.'),
(3689, 35, 29, 'Zajisté ti, kdož předčítají Knihu boží a zachovávají modlitbu a rozdávají z toho, čímž obdařili jsme je, v skrytosti i veřejně, nadějí se obchodu, jenž nezkazí se:'),
(3690, 35, 30, 'aby Bůh zaplatil jim mzdy jejich a přidal jim z milosti své: onť zajisté velký jest v odpouštění, nejvýš vděčný.'),
(3691, 35, 31, 'A to, co vnuknuli jsme ti z Knihy, jest pravdou, potvrzující to, co zjeveno bylo před tím: zajisté pak Bůh dobře zpraven jest o služebnících svých a obzírá je.'),
(3692, 35, 32, 'Poté pak učinili jsme dědici Knihy ty, jež vyvolili jsme mezi služebníky svými: z nich pak některý prohřešuje se proti vlastní duši své: jiný kolísá (mezi dobrem a zlem): a některý předstihuje v dobrých skutcích (ostatní), z dovolení božího: totoť zajisté milost jest velká.'),
(3693, 35, 33, 'Do zahrad Edenu vstoupí: tam budou pro ně náramky zlaté a perly a oděv jich bude hedvábný'),
(3694, 35, 34, 'a řeknou: „Chvála Bohu, jenž odejmul od nás zármutek, zajisté Pán náš velký jest v odpouštění, nejvýš vděčný,'),
(3695, 35, 35, 'jenž ubytoval nás v příbytku věčném z milosti své, v němž nedotkne se nás námaha, aniž dotkne se nás únava.“'),
(3696, 35, 36, 'Však těm, kdož neuvěřili, schystán jest oheň pekelný: nebude usouzeno jim, aby zemřeli a nebude ulehčen jim trest v něm: tak odměňujeme všechny (zatvrzelé) nevěřící!'),
(3697, 35, 37, 'Tam hlasitě volati budou: „Pane náš, vyveď nás odtud a budeme konati dobro, nikoliv to, co konali jsme dříve.“ „Což neprodloužili jsme vám věk váš, aby ten, kdo, rozvážiti si to chtěl, mohl si to rozvážiti? A (mimo to) přišel vám varovatel: pročež okuste nyní muk! Nepravostným není pomocníka.“'),
(3698, 35, 38, 'Bůh zajisté zná věci skryté nebes i země a on dobře ví, co skrývají nitra.'),
(3699, 35, 39, 'On jest to, jenž učinil vás náměstky svými na zemi: a kdo nevěří, na něho padá nevíra jeho: a nevíra jich rozmnoží pro nevěřící u Pána jejich jenom nenávist: a nevíra jich rozmnoží pro nevěřící pouze záhubu.'),
(3700, 35, 40, 'Rci: „Co míníte o společnících, jež vzýváte vedle Boha? Ukažte mi, co stvořili na zemi, aneb jaký mají podíl v nebesích, aneb dali-li jsme jim knihu, dle níž by měli nějaké jasné důkazy?“ Nikoliv, naopak: a nepravostní slibují si navzájem pouze klam.'),
(3701, 35, 41, 'Bůh zajisté pevně drží nebe i zemi, aby nepominuly a kdyby pomíjely, kdo jiní dovedl by je udržeti, kromě něho Však on laskavý jest a velký v odpouštění.'),
(3702, 35, 42, 'A přisahali při Bohu nejpevnější přísahou, že kdyby přišel jim varovatel, byli by dojista lépe vedeni, než kterýkoli národ jiný: však když přišel jim varovatel, rozhojnil je pouze v prchání,'),
(3703, 35, 43, 'pýše na zemi, a lstivém kutí zla: však ono lstivé kutí zla zaplete v osidla ty, kdož je kují. Očekávají snad jinou zvyklost se strany Boha, než s jakou vystoupil vůči těm, kdož předešli je? Zajisté, že nenajdeš změny ve zvyklostech božích, aniž nalezneš ve zvyklostech božích odchylky!'),
(3704, 35, 44, 'Což necestovali v zemi a neviděli, jak byl konec těch před nimi, již mocnější byli jich mocí? Není ničeho, co znemožniti mohlo by rozkaz Boha na nebi, či na zemi; onť zajisté vševědoucí jest a mocný.'),
(3705, 35, 45, 'Kdyby ztrestati chtěl Bůh lidi za to, co zasloužili si, nenechal by na povrchu (země) ni jednoho živočicha! Však on odkládá s nimi až do lhůty předurčené: a když přijde lhůta jejich, zajisté že Bůh dobře obzírá služebníky své.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'JÁ. SÍN.'),
(3707, 36, 2, 'Při Koránu moudrém,'),
(3708, 36, 3, 'tyť zajisté s jedním z poslaných,'),
(3709, 36, 4, 'na stezce přímé!'),
(3710, 36, 5, 'Seslání jest to Mocného, Slitovného,'),
(3711, 36, 6, 'abys varoval lid, jichž otcové nebyli varováni i žijí v lhostejnosti.'),
(3712, 36, 7, 'A pronesen byl již rozsudek, nad většinou jich: proto neuvěří.'),
(3713, 36, 8, 'Obtěžkali jsme šíje jejich řetězy, sáhajícími jim až k bradám, takže dozadu sražené jsou hlavy jejich.'),
(3714, 36, 9, 'A učinili jsme před nimi přehradu i za nimi přehradu; a clonou pokryli jsme oči jejich, takže nelze jim viděti.'),
(3715, 36, 10, 'A jedno jest jim, varuješ-li je, či ne: neuvěří!'),
(3716, 36, 11, 'Pouze ty varovati budeš, kdož následují Napomenutí a bojí se Milosrdného v skrytosti: těm zvěstuj odpuštění a odměnu hojnou.'),
(3717, 36, 12, 'Myť zajisté křísíme mrtvé a zapisujeme skutky jejich a stopy jejich: a všechny věci zanášíme v Knize prvotní zjevné.'),
(3718, 36, 13, 'A čiň jim podobenství v obyvatelích města, když přišli k nim poslové;'),
(3719, 36, 14, 'když poslali jsme k nim poslů dvé a lháři nazvali je: i na posilu třetího poslali jsme, a řekli: „Myť zajisté posláni jsme k vám!“'),
(3720, 36, 15, 'Řekli: „Vy pouze jste lidé jako my a Milosrdný neseslal ničeho. Vy pouze lháři jste.“'),
(3721, 36, 16, 'Řekli (poslové): „Pán náš ví, že my k vám posláni jsme:'),
(3722, 36, 17, 'a na nás jest pouze hlásání zjevné.“'),
(3723, 36, 18, 'Řekli: „Poradili jsme se ptakověštbou ohledně vás: a nepřestanete-li, dojista ukamenujeme vás a dojista stihne vás od nás trest bolestný.“'),
(3724, 36, 19, 'Řekli (poslové): „Vaše (zlá) věštba, zůstane s vámi, i když jste byli napomenuti. Ale vy jste lidem výstřednickým!“'),
(3725, 36, 20, 'I přiběhl z druhého konce města muž ve spěchu, volaje: „Ó lide můj, následujte poslané:'),
(3726, 36, 21, 'následujte ty, kdož nežádají si od vás odměny a jsou vedeni cestou (pravou)!'),
(3727, 36, 22, 'Proč neměl bych uctívati toho, jenž stvořil mne a k němuž navráceni budete?'),
(3728, 36, 23, 'Zdaž vezmu si vedle něho božstva (jiná)? Chtěl-li pak by Milosrdný přičiniti mi protivenství, neprospěla by mi přímluva jejich pranic, aniž spasiti by mne mohli.'),
(3729, 36, 24, 'Tehdy pak zajisté byl bych v bloudění zjevném.'),
(3730, 36, 25, 'Zajisté uvěřil jsem v Pána našeho: pročež slyšte mne!“'),
(3731, 36, 26, 'Bylo řečeno mu: „Vejdi v ráj!“ I řekl: „Kéž by jen lid můj věděl,'),
(3732, 36, 27, 'jak odpustil mi Pán můj a jak učinil mne jedním z poctěných!“'),
(3733, 36, 28, 'A neseslali jsme na lid jeho poté vojska s nebe, aniž co jiného seslali jsme.'),
(3734, 36, 29, 'Pouze jedno vzkřiknutí zaznělo a hle, byli zhubeni!'),
(3735, 36, 30, 'Běda služebníkům mým! Žádný nepřišel jim prorok, aniž by si nebyli smích z něho tropili.'),
(3736, 36, 31, 'Což neviděli, kolik zahladili jsme pokolení před nimi a že zajisté k nim se nenavrátí?'),
(3737, 36, 32, 'Nýbrž že všichni dohromady nám budou předvedeni.'),
(3738, 36, 33, 'Znamením jim jest země zmrtvělá, již křísíme a dáváme vycházeti z ní zrnům (obilným), z nichž pojídají.'),
(3739, 36, 34, 'A učinili jsme na ní zahrady datlovníků a révy vinné, a dali vyvříti na ní pramenům:'),
(3740, 36, 35, 'aby pojídati mohli z plodů (všeho) toho, ač nevytvořily jich ruce jejich. Což nebudou vděčni?'),
(3741, 36, 36, 'Chválen budiž ten, jenž stvořil druhy všechny, ve všem, co vydává země, v nich samých, a v tom, o čem nevědí!'),
(3742, 36, 37, 'Znamením jim jest noc: svlékneme s ní den a hle, v temnotách jsou!'),
(3743, 36, 38, 'A slunce, běžící k sídlu svému: takové jest určení Mocného, Vševědoucího!'),
(3744, 36, 39, 'A měsíc: určili jsme mu období, dokud nezmění se v podobu starého listu palmového.'),
(3745, 36, 40, 'A slunci není dovoleno, aby dostihlo měsíce; aniž noc předstihne den; však oba svou drahou po obloze jdou.'),
(3746, 36, 41, 'A znamením jest jim, že nesli jsme potomstvo jejich v arše přeplněné,'),
(3747, 36, 42, 'a stvořili jsme pro ně (lodi) jí podobné, na nichž plují:'),
(3748, 36, 43, 'a chceme-li, utápíme je, a není vysvoboditele jim, aniž jsou spaseni,'),
(3749, 36, 44, 'leda z milosrdenství našeho, pro těšení se (světu) dočasné.'),
(3750, 36, 45, 'A když řečeno jest jim: „Bojte se toho, co před vámi jest i toho, co jest za vámi, abyste slitování dosáhli,“ (nedbají).'),
(3751, 36, 46, 'A nepřijde jim znamení ze znamení Pána jejich, aniž by se od něho neodvraceli.'),
(3752, 36, 47, 'A když řečeno jest jim: „Rozdávejte (almužnu) z toho, čímž obdařil vás Bůh,“ říkají ti, kdož neuvěřili, k těm, kteří uvěřili: „Zdaž krmiti budeme ty, jež nakrmil by Bůh, kdyby chtěl? Vyť pouze jste v bludu zjevném.“'),
(3753, 36, 48, 'A říkají: „Kdy naplní se onen slib, jste-li pravdomluvnými?“'),
(3754, 36, 49, 'Co čekají jiného, než vzkřiknutí jediné, jež přikvačí na ně, mezitím, co hašteří se?'),
(3755, 36, 50, 'A nebudou s to, aby učinili závěť, aniž k rodině své se navrátí.'),
(3756, 36, 51, 'A zatroubeno bude na roh, a hle, z hrobů svých k Pánu svému vylézati budou.'),
(3757, 36, 52, 'Řeknou: „Běda nám! Kdo vzbudil nás z našeho místa spánku? Totoť jest, co slíbil nám Milosrdný a pravdu měli poslové.“'),
(3758, 36, 53, 'Pouze jedno vzkřiknutí bude a hle, všichni budou nám předvedeni.'),
(3759, 36, 54, 'A v onen den nebude ukřivděno žádné duši a odměněni budete jen dle toho, co činili jste.'),
(3760, 36, 55, 'V den onen obyvatelé zahrady (rajské) budou zaměstnáni radováním se.'),
(3761, 36, 56, 'Oni a manželky jejich, ve stinném (loubí) na pohovkách odpočívati budou.'),
(3762, 36, 57, 'Tam ovoce budou míti a vše, oč požádají.'),
(3763, 36, 58, '„Pokoj vám!“ bude pozdrav jim od Pána jejich slitovného.'),
(3764, 36, 59, 'Buďte pak odděleni dnes, ó provinilci!'),
(3765, 36, 60, 'Neučinil jsem s vámi úmluvu, synové Adamovi: „Neuctívejte Satana, neb nepřítelem jest vám zjevným?'),
(3766, 36, 61, 'Nýbrž uctívejte mne: totoť jest stezka přímá!'),
(3767, 36, 62, 'Však on již zavedl v blud vás počet velký: což nepochopili jste toho?'),
(3768, 36, 63, 'Totoť jest peklo, jež slíbeno bylo vám:'),
(3769, 36, 64, 'pecte se v něm dnes za to, že nevěřili jste!“'),
(3770, 36, 65, 'V den onen zapečetíme ústa jejich: a mluviti budou k nám ruce jejich a dosvědčí nohy jejich to, co konali.'),
(3771, 36, 66, 'A kdybychom chtěli, zaclonili bychom oči jejich; pak o překot pádili by po stezce, však jak viděli by ji?'),
(3772, 36, 67, 'A kdybychom chtěli, dali bychom ztrnouti jim na místech jejich, takže by nebylo lze jim jíti vpřed, aniž vrátiti se.'),
(3773, 36, 68, 'A tomu, jemuž prodloužíme věk, ohneme tělo stářím: což toho nepochopí?'),
(3774, 36, 69, 'A nenaučili jsme jej básnění, aniž jest mu ho třeba. Neb toto jest pouze Napomenutí a Čtení zjevné,'),
(3775, 36, 70, 'aby varován byl jím, kdožkoli živ jest, a aby pronesen jím byl rozsudek nad nevěřícími.'),
(3776, 36, 71, 'Což neviděli, ze stvořili jsme pro ně mezi věcmi, jež urobily ruce naše, dobytčata, nad nimiž vládci jsou?'),
(3777, 36, 72, 'A podrobili jsme jim je: na nich pak jezdí a z nich požívají:'),
(3778, 36, 73, 'a z nich mají užitek (mnohý) a nápoje — což nebudou, nám vděčni?'),
(3779, 36, 74, 'Nicméně vzali si vedle Boha božstva (jiná), aby snad měli pomocníky.'),
(3780, 36, 75, '(Tato) nejsou s to, aby pomohla jim: a oni jsou jen vojem (pro ně) pohotovým.'),
(3781, 36, 76, 'Nermutiž tě nijak řeč jejich: myť zajisté víme, co tají, i co veřejně přiznávají.'),
(3782, 36, 77, 'Což neviděl člověk, že stvořili jsme jej z kapky (semene): a hle, on protivníkem jest (nám) zjevným!'),
(3783, 36, 78, 'A činí nám podobenství, zapomenuv stvoření své, řka: „Kdo vzkřísí kosti zetlelé?“'),
(3784, 36, 79, 'Rci: „Vzkřísí je ten, jenž stvořil je po prvé, neb on v tvoření veškerém jest učený:'),
(3785, 36, 80, 'ten, jenž učinil vám ze stromu zeleného oheň: a hle, z něho zapalujete si!“'),
(3786, 36, 81, 'Což nemá ten, jenž stvořil nebe i zemi, moci, aby stvořil jim podobné? Věru, že ano: onť Stvořitelem jest učeným!'),
(3787, 36, 82, 'Rozkaz jeho, když chce, aby co bylo, jest jen: „Staň se!“ a stane se.'),
(3788, 36, 83, 'Chválen budiž (tedy) ten, v jehož ruce jest vláda nad věcmi všemi a k němuž navráceni budete!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Při oněch, v řadách SEŘAZENÝCH.'),
(3790, 37, 2, 'a odháněním odhánějících,'),
(3791, 37, 3, 'a Napomenutí Čtoucích,'),
(3792, 37, 4, 'přisahám: zajisté Bůh jest jeden,'),
(3793, 37, 5, 'Pán nebes i země a všeho, což mezi nimi jest a Pán východů.'),
(3794, 37, 6, 'Zajisté pak ozdobili jsme nejspodnější nebe zdobou hvězd:'),
(3795, 37, 7, 'též na ochranu proti všem zlým duchům odbojným,'),
(3796, 37, 8, '(aby) nenaslouchali tomu, co děje se ve shromáždění nejvyšším, neb metáno jest na ně se všech stran,'),
(3797, 37, 9, 'odháněni jsou a (schystán) jest jim trest trvalý:'),
(3798, 37, 10, 'a který z nich pochytí úryvek z porady, pronásledován jest šlehem plamenným.'),
(3799, 37, 11, 'Vyptej se jich, kdo silnějšího jest stvoření; zda oni neb ti, jež stvořili jsme? Zajisté přec stvořili jsme je z hlíny tvárné!'),
(3800, 37, 12, 'Ano, ty divíš se, a oni žert si tropí:'),
(3801, 37, 13, 'neb když napomínáni jsou, nejsou si toho pamětlivi:'),
(3802, 37, 14, 'a když vidí znamení, jen vysmívají se,'),
(3803, 37, 15, 'řkouce: „Totoť jest pouze kouzlo zjevné:'),
(3804, 37, 16, 'Zdaž, když zemřeme, v prach a kosti byvše obráceni, opět že budeme vzkříšeni?'),
(3805, 37, 17, 'A taktéž otcové naši předešlí?“'),
(3806, 37, 18, 'Rci: „Ano, a poníženi budete!“'),
(3807, 37, 19, 'Pouze jednou zazní vřesknutí (trouby) a hle, prohlédnou!'),
(3808, 37, 20, 'A řeknou: „Běda nám, totoť den jest odplaty:'),
(3809, 37, 21, 'totoť den jest rozsouzení, jejž vylhaným nazývali jste.“'),
(3810, 37, 22, '(Řekne Bůh): „Shromážděte nepravostné i ženy jejich a to, co uctívali'),
(3811, 37, 23, 'vedle Boha: a uveďte je na stezku k peklu:'),
(3812, 37, 24, 'pak zastavte je, neb vyslýcháni budou.'),
(3813, 37, 25, 'Proč tedy nepomáháte si navzájem?“'),
(3814, 37, 26, 'Naopak, v den onen poddají se (Bohu)'),
(3815, 37, 27, 'a přiblíživše se k sobě navzájem, budou činiti si vzájemné dotazy,'),
(3816, 37, 28, 'řkouce (jedni): „Vyť zajisté přicházeli jste k nám se strany pravé,“'),
(3817, 37, 29, 'však řeknou (druzí): „Nikoliv, vy nevěřili jste v nás,'),
(3818, 37, 30, 'a my neměli jsme moci nad vámi: naopak, vy byli jste lidem svévolným —'),
(3819, 37, 31, 'i splnil se nad námi rozsudek Pána našeho: myť zajisté okusíme (jej).'),
(3820, 37, 32, 'Zlákali jsme vás, neb i my byli jsme zlákáni.“'),
(3821, 37, 33, 'V den onen budou (tedy) v trestu spolčeni!'),
(3822, 37, 34, 'Zajisté pak takto naložíme s provinilci,'),
(3823, 37, 35, 'neboť byli, když řečeno jim bylo: „Není boha, kromě Boha,“ pýchou naduti,'),
(3824, 37, 36, 'a říkali: „Zdaž opustíme bohy své pro básníře bláznivého?“'),
(3825, 37, 37, 'Naopak, on s pravdou přišel a potvrdil posly předešlé:'),
(3826, 37, 38, 'vy pak dojista okusíte trestu bolestného:'),
(3827, 37, 39, 'a nebudete odměněni jinak, než dle skutků svých —'),
(3828, 37, 40, 'vyjma služebníků božích upřímných!'),
(3829, 37, 41, 'Těm dostane se darů již řečených:'),
(3830, 37, 42, '(chutného) ovoce; a budou poctěni'),
(3831, 37, 43, 'v zahradách (rajských) rozkošných,'),
(3832, 37, 44, 'na pohovkách proti sobě sedíce.'),
(3833, 37, 45, 'Obcházeni budou s číší (pití) z pramene:'),
(3834, 37, 46, '(pití) čistého a příjemného pijícím:'),
(3835, 37, 47, 'v něm tíhy nebude, aniž se jím opojí.'),
(3836, 37, 48, 'Vedle nich (děvy) budou velkých očí, zrak (cudně) klopící,'),
(3837, 37, 49, '(s líčky bílými) jak vejce chráněná.'),
(3838, 37, 50, 'I přiblíživše se k sobě navzájem, budou činiti si dotazy vzájemné.'),
(3839, 37, 51, '„Řekne jeden z nich: „Jáť měl jsem druha (věrného),'),
(3840, 37, 52, 'jenž říkával mi: ‚Jsi jedním z těch, kdož věří v to?'),
(3841, 37, 53, 'Že až zemřeme, prach jsouce a kosti, budeme souzeni?‘“'),
(3842, 37, 54, 'Řekne pak: „Zdaž chcete (tamto) pohlédnout?“'),
(3843, 37, 55, 'I (sám) pohlédne a uprostřed pekla spatří jej.'),
(3844, 37, 56, 'Tehdy řekne mu: „Při Bohu, málem byl bys zničil mne:'),
(3845, 37, 57, 'a nebýti milosti Pána mého, byl bych jedním z jemu předvedených.'),
(3846, 37, 58, 'Tedy (vskutku) již po druhé nezemřeme,'),
(3847, 37, 59, 'vyjma zemření našeho prvého? A (vskutku tedy) nebudeme ztrestáni?'),
(3848, 37, 60, 'Totoť zajisté velká jest blaženost!“'),
(3849, 37, 61, 'Pro podobného cos nechť pracují pracující!'),
(3850, 37, 62, 'Zdaž není toto lepším příbytkem, než strom Az-zaqqúm?'),
(3851, 37, 63, 'Učinili jsme jej předmětem rozkolů mezi nepravostnými:'),
(3852, 37, 64, 'jest to strom, jenž vyrůstá ze dna pekla:'),
(3853, 37, 65, 'plody jeho jsou jako hlavy duchů zlých:'),
(3854, 37, 66, 'však oni pojídati budou z nich a plnit jimi břicha svá:'),
(3855, 37, 67, 'poté pak píti na ně budou vodu vařící:'),
(3856, 37, 68, 'a nato navrátí se v pekla hloub:'),
(3857, 37, 69, 'Viděli své otce bloudící'),
(3858, 37, 70, 'a do šlépějí jejich vrhli se!'),
(3859, 37, 71, 'Před nimi již byla zbloudila většina (národů) předešlých,'),
(3860, 37, 72, 'ač poslali jsme k nim varovatele:'),
(3861, 37, 73, 'a pohleď, jak (zlý) byl konec varovaných,'),
(3862, 37, 74, 'vyjma služebníků božích upřímných!'),
(3863, 37, 75, 'Kdysi volal k nám Noe — a my nejlepší jsme z vyslyšujících —'),
(3864, 37, 76, 'i zachránili jsme jej a rodinu jeho z velké úzkosti:'),
(3865, 37, 77, 'a učinili jsme potomstvo jeho (jedinými) pozůstalými,'),
(3866, 37, 78, 'a zůstavili jsme proň v potomstvu'),
(3867, 37, 79, 'pozdrav: „Pokoj budiž Noemovi ve světech veškerých!“'),
(3868, 37, 80, 'Takto zajisté odměňujeme (lid) dobřečinící;'),
(3869, 37, 81, 'onť byl pak jedním ze služebníků našich věřících.'),
(3870, 37, 82, 'Poté jsme utopili všechny ostatní.'),
(3871, 37, 83, 'Zajisté pak jeho víry byl (též) Abraham,'),
(3872, 37, 84, 'když přinesl Pánu svému srdce celistvé,'),
(3873, 37, 85, 'když, řekl otci a lidu svému: „Co vlastně uctíváte?'),
(3874, 37, 86, 'Zdaž lživě božstva vedle Boha chcete mít?'),
(3875, 37, 87, 'A co mníte tedy o Pánu světů všech?“'),
(3876, 37, 88, 'I vrhl pohled jeden ke hvězdám,'),
(3877, 37, 89, 'a řekl: „V pravdě jsem nemocen.“'),
(3878, 37, 90, 'I odvrátili se od něho a odešli.'),
(3879, 37, 91, 'Poodešel pak k božstvům jejich, řka: „Což nejíte?'),
(3880, 37, 92, 'Proč nepromluvíte?“'),
(3881, 37, 93, 'Poodešel pak k nim a udeřil je pravicí.'),
(3882, 37, 94, 'I přiběhli k němu úprkem.'),
(3883, 37, 95, 'Řekl: „Zdaž uctívati budete, co sami tesáte?'),
(3884, 37, 96, 'A přec Bůh vás stvořil, i to, co děláte!'),
(3885, 37, 97, 'Řekli: „Postavte proň hranici a v plamen žhoucí vrzte jej.“'),
(3886, 37, 98, 'Tak léčku chtěli nastrojiti mu, však ponížili jsme je.'),
(3887, 37, 99, 'Řekl Abraham: „Já utíkám se k Pánu svému, jenž (správně) povede mne:'),
(3888, 37, 100, 'Pane můj, dej mi (syna), jednoho ze spravedlivých.“'),
(3889, 37, 101, 'I zvěstovali jsme mu mládence povahy laskavé:'),
(3890, 37, 102, 'a když dospěl věku, v němž pracovati mohl s ním, řekl: „Synáčku můj, zajisté viděl jsem ve snu, že mám tě obětovati: hle, co mníš?“ Řekl (syn): „Otče můj, učiň to, což nakázáno bylo ti: shledáš, bude-li chtíti Bůh, že jsem z trpělivých.“'),
(3891, 37, 103, 'A když oba odevzdali se do vůle boží, a položil jej čelem dolů,'),
(3892, 37, 104, 'zavolali jsme naň: „Ó Abrahame!'),
(3893, 37, 105, 'Tys uvěřil svému vidění: zajisté takto odměňujeme (lid) dobře činící.'),
(3894, 37, 106, 'V pravdě byla to (jen) zkouška zjevná.“'),
(3895, 37, 107, 'I vykoupili jsme (syna jeho) obětí velkou,'),
(3896, 37, 108, 'a zůstavili jsme proň v potomstvu'),
(3897, 37, 109, '(pozdrav): „Pokoj budiž Abrahamovi!“'),
(3898, 37, 110, 'Tak odměnujeme (lid) dobře činící,'),
(3899, 37, 111, 'neb byltě ze služebníků našich věřících.'),
(3900, 37, 112, 'I zvěstovali jsme mu Isáka, proroka ze spravedlivých.'),
(3901, 37, 113, 'A požehnali jsme mu a Isákovi: mezi potomstvem jich pak někteří dobře činí, jiní zas zjevně prohřešují se proti sobě samým.'),
(3902, 37, 114, 'A kdysi dobrodiním zahrnuli jsme Mojžíše a Árona,'),
(3903, 37, 115, 'a zachránili jsme je oba i lid jejich z trýzně převelké:'),
(3904, 37, 116, 'a pomohli jsme jim i byli vítězni:'),
(3905, 37, 117, 'a dali jsme jim Písmo přejasné:'),
(3906, 37, 118, 'a vedli jsme je ke stezce přímé:'),
(3907, 37, 119, 'a zůstavili jsme jim v potomstvu'),
(3908, 37, 120, '(pozdrav): „Pokoj budu Mojžíšovi a Áronovi!“'),
(3909, 37, 121, 'Takto pak odměnujeme (lid) dobře činící,'),
(3910, 37, 122, 'neb oba byli ze služebníků našich věřících.'),
(3911, 37, 123, 'Též Eliáš byl jedním z našich poslaných,'),
(3912, 37, 124, 'když řekl lidu svému: „Což nebudete báti se?'),
(3913, 37, 125, 'Zdaž uctívati budete Ba\'la a zanecháte nejlepšího ze stvořitelů?'),
(3914, 37, 126, 'Boha, Pána svého a Pána otců vašich předešlých?“'),
(3915, 37, 127, 'Však lhářem nazvali jej a (před nás) jistě předvedeni budou,'),
(3916, 37, 128, 'vyjma služebníků božích upřímných.'),
(3917, 37, 129, 'A zůstavili jsme proň v potomstvu'),
(3918, 37, 130, '(pozdrav): „Pokoj budiž Eliášovi!“'),
(3919, 37, 131, 'Tak v pravdě odměňujeme (lid) dobře činící,'),
(3920, 37, 132, 'neb on byl ze služebníků našich věřících.'),
(3921, 37, 133, 'Též Lot byl jedním z našich poslaných,'),
(3922, 37, 134, 'když zachránili jsme jej a rodinu jeho veškerou,'),
(3923, 37, 135, 'vyjma stařeny, jež omeškala se.'),
(3924, 37, 136, 'Pak zhubili jsme (všechny) ostatní.'),
(3925, 37, 137, 'V pravdě kol obydlí jich za jitra chodíte'),
(3926, 37, 138, 'i za večera: což nepochopíte?'),
(3927, 37, 139, 'Též Jonáš byl jedním z našich poslaných,'),
(3928, 37, 140, 'když uchýlil se na loď přeplněnou'),
(3929, 37, 141, 'a vrhl los a byl jím odsouzen:'),
(3930, 37, 142, 'a ryba shltla jej, neb vinu měl.'),
(3931, 37, 143, 'A kdyby nebyl býval z těch, kdož chvály pějí nám,'),
(3932, 37, 144, 'byl by dlel v břiše jejím až do dne vzkříšení.'),
(3933, 37, 145, 'Vyvrhli jsme jej pak na pustý břeh, chorého:'),
(3934, 37, 146, 'a dali jsme vzrůsti nad ním keři tykvovému.'),
(3935, 37, 147, 'Pak poslali jsme jej ke stu tisícům, neb více snad:'),
(3936, 37, 148, 'i uvěřili: a popřáli jsme (radosti života) jim dočasně.'),
(3937, 37, 149, 'Vyptej se jich, zda má Pán tvůj dcery, neb zda syny má?'),
(3938, 37, 150, 'A stvořili-li jsme anděly ženami? A svědky toho byli-li?'),
(3939, 37, 151, 'Což neříkají jen ze své lživosti,'),
(3940, 37, 152, 'že Bůh děti zplodil? Zajisté lháři jsou!'),
(3941, 37, 153, 'Byl by dal přednost dcerám před syny?'),
(3942, 37, 154, 'Jak přijde to, že takto soudíte?'),
(3943, 37, 155, 'Což si toho nerozvážíte?'),
(3944, 37, 156, 'Neb máte k tomu zjevné zmocnění?'),
(3945, 37, 157, 'Přineste tedy Knihu svou, jste-li pravdomluvnými!'),
(3946, 37, 158, 'A učinili Boha spříbuzněným s Džinny: nicméně dobře vědí Džinnové, že sami budou (Bohu) předvedeni.'),
(3947, 37, 159, 'Chválen budiž Bůh (daleko) nad to, co o něm vykládají,'),
(3948, 37, 160, 'kromě služebníků božích upřímných.'),
(3949, 37, 161, 'Však zajisté vy a to, co uctíváte,'),
(3950, 37, 162, 'nebudete s to, abyste se proti němu vzbouřili,'),
(3951, 37, 163, 'vyjma toho, jenž hřát bude se v žárném plameni.'),
(3952, 37, 164, 'Není nikoho mezi námi, jenž neměl by místo určené;'),
(3953, 37, 165, 'zajisté my pak SEŘAZUJEM\' SE;'),
(3954, 37, 166, 'zajisté my pak chvály pějeme.“'),
(3955, 37, 167, 'A přece ještě oni říkají:'),
(3956, 37, 168, '„Kdybychom byli měli Napomenutí od předků svých,'),
(3957, 37, 169, 'byli bychom služebníky Boha upřímnými.“'),
(3958, 37, 170, 'A přece v ně nevěří! Však (dobře) zvědí!'),
(3959, 37, 171, 'Již dříve dali jsme slovo své služebníkům námi poslaným,'),
(3960, 37, 172, 'že pomoc svoji dáme jim,'),
(3961, 37, 173, 'a že voje naše pro ně zvítězí.'),
(3962, 37, 174, 'Odvrať se tedy od nich dočasně'),
(3963, 37, 175, 'a pozoruj je: též oni spozorují!'),
(3964, 37, 176, 'Chtějí snad uspíšiti (na se) trest náš?'),
(3965, 37, 177, 'Když spadne na ně v jejich ohradách, špatné to bude jitro pro varované!'),
(3966, 37, 178, 'Odvrať se tedy od nich dočasně,'),
(3967, 37, 179, 'a pozoruj: též oni spozorují!'),
(3968, 37, 180, 'Chválen budiž Pán tvůj, Pán moci (vší), nade vše, co o něm vykládají,'),
(3969, 37, 181, 'a pokoj budiž jeho poslaným:'),
(3970, 37, 182, 'a chvála Bohu, Pánu světů veškerých!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'SÁD. Při Koránu, v němž jest napomenutí,'),
(3972, 38, 2, 'věru ti, kdož neuvěřili, jednají zpupně a odbojně!'),
(3973, 38, 3, 'Kolik zahladili jsme pokolení před nimi! Volali sice (o slitování), však nebyla to již doba k uniknutí.'),
(3974, 38, 4, 'Diví se, že přišel jim varovatel ze středu jejich; a říkají nevěřící: „Toť kouzelník jest, velký lhář!'),
(3975, 38, 5, 'Cože? On činí božstva Bohem Jediným? Toť věru věc jest údivná!“'),
(3976, 38, 6, 'A oddálili se předáci jejich, (řkouce): „Jděte a vytrvejte trpělivě při božstvech svých! Toť věru věc jest vyžadovaná.'),
(3977, 38, 7, 'Neslyšeli jsme ničeho podobného v náboženství předešlém: Toť věru jest jen šejdířství!'),
(3978, 38, 8, 'Jemu (jedinému) mezi námi že bylo sesláno Napomenutí?“ Ano, oni pochybují ohledně Napomenutí mého: ano, uvěří, až okusí trestu mého!'),
(3979, 38, 9, 'Což oni mají pokladnice milosrdenství Pána svého, Mocného, Obdarovatele?'),
(3980, 38, 10, 'Což jim přináleží panství nebes a země a všeho, což mezi oběma jest? Nechť tedy vystoupí vzhůru po provazech!'),
(3981, 38, 11, 'Vojsko jakékoliv tam poraženo bude spojenců.'),
(3982, 38, 12, 'Před nimi již lháři nazvali proroky lid Noemův, a (kmen) \'Ád, a Farao, jenž kůly měl!'),
(3983, 38, 13, 'A (kmen) Tsemúd, a lid Lotův, a obyvatelé Al-Aika tito (všichni) spojenci byli;'),
(3984, 38, 14, 'všichni jen lháři zvali proroky: a trest po právu byl vykonán!'),
(3985, 38, 15, 'A nač tito zde čekají než na zavřesknutí jediné, jemuž nebude odkladu?'),
(3986, 38, 16, 'A (přece ještě) říkají: „Pane náš, urychli nám podíl náš, přede dnem súčtování!“'),
(3987, 38, 17, 'Strp, co říkají, a pomni služebníka našeho Davida, muže silného, jenž často (kajícně k nám) obracel se:'),
(3988, 38, 18, 'jak přiměli jsme hory, aby s ním chválily nás s večerem i v zoře východu:'),
(3989, 38, 19, 'a ptactvo, jež shromažďovalo se kol něho a všechno k němu navracelo se.'),
(3990, 38, 20, 'Jak upevnili jsme království jeho a dali jsme mu moudrost a (bystré) sporů rozsuzování.'),
(3991, 38, 21, 'Zdaž došla tě zpráva o sporu oněch dvou, když přelezli zeď výklenku?'),
(3992, 38, 22, 'Když objevili se před Davidem a on lekl se jich, řekli: „Neboj se, jsme dva ve sporu, z nichž jeden učinil příkoří druhému. Rozsuď mezi námi dle pravdy, nejsa stranickým, a uveď nás na stezku rovnou.'),
(3993, 38, 23, 'Toto jest bratr můj; měl devadesát devět ovcí, já pouze jedinou měl; i řekl mi: ‚Dej mi ji v opatrování.‘ A nyní přemohl mne ve sporu o ni.“'),
(3994, 38, 24, 'Řekl David: „Onť bezpráví ti učinil, žádaje tě o tvoji ovci, aby připojil ji k ovcím svým. A v pravdě mnoho společníků činí si navzájem příkoří, vyjma těch, kdož uvěřili a konali dobré skutky, však jak malý jest (počet) těchto!“ Dovtípil se pak David, že zkoušeli jsme jej pouze tímto; i prosil za odpuštění Pána svého, a padl na kolena svá a kál se.'),
(3995, 38, 25, 'I odpustili jsme mu to, a má u nás místo blízké, a krásné přebývání.'),
(3996, 38, 26, 'Ó Davide, učinili jsme tě náměstkem svým na zemi: pročež rozsuzuj mezi lidmi dle pravdy, a nenásleduj choutek svých, neb (jinak) zavedou tě od stezky boží. Těm pak, kdož zbloudí od stezky boží, schystán jest trest přísný za to, že zapomněli na den súčtování.'),
(3997, 38, 27, 'A nestvořili jsme nebe a zemi a cožkoli mezi nimi jest, zbytečně. Tak mní jen ti, kdož neuvěřili: a běda těm, kdož neuvěřili, před ohněm (pekelným)!'),
(3998, 38, 28, 'Zdaž postavíme ty, kdož uvěřili a konali dobré skutky, na roveň těm, kteří spoušť tropí v zemi? Anebo věřící postavíme na roveň bezbožníkům?'),
(3999, 38, 29, 'Kniha, již seslali jsme ti, požehnána jest: aby přemítali o znameních jejích a pamětlivi jich byli lidé (pronikavého) rozumu.'),
(4000, 38, 30, 'A dali jsme Davidovi Šalamouna: výborný to služebník (náš), jenž rád (k nám) obracel se.'),
(4001, 38, 31, '(Pomni), když předvedeni mu byli jednoho večera dva bujní ořové:'),
(4002, 38, 32, 'i řekl: „V pravdě miloval jsem milování statků (pozemských) nad vzpomínání na Pána svého, až (slunce) zašlo za závoj (noci).'),
(4003, 38, 33, 'Přiveďte je zpět přede mne!“ I počal utínati jim nohy a šíje.'),
(4004, 38, 34, 'Taktéž zkoušeli jsme Šalamouna, vrhnuvše na trůn jeho mrtvolu: poté (kajícně k nám) obrátil se,'),
(4005, 38, 35, 'řka: „Pane můj, odpusť mi a dej mi království, jež nebude příslušeti nikomu po mně; neb tys (štědrým) obdarovatelem.“'),
(4006, 38, 36, 'A podrobili jsme mu vítr, aby vál dle rozkazu jeho, klidně kam on jej nařídil:'),
(4007, 38, 37, 'a zlé duchy, stavitele a potápěče;'),
(4008, 38, 38, 'jakož i jiné, připoutané k sobě řetězy.'),
(4009, 38, 39, '„Totoť jest dar náš: obdařuj, neb odnímej, bez skládání účtů.“'),
(4010, 38, 40, 'onť pak má u nás místo blízké a krásné přebývání.'),
(4011, 38, 41, 'A vzpomeň (též) služebníka našeho Joba, když volal k Pánu svému: „V pravdě stihl mne Satan (zlou) nemocí a mukami!“'),
(4012, 38, 42, '„Dupni nohou: toto jest ti k omývání chladivému a k pití.“'),
(4013, 38, 43, 'A dali jsme mu (zpět) rodinu jeho, přidavše mu ještě jednou tolik z milosrdenství svého a jako napomenutí pro lidi (pronikavého) rozumu.'),
(4014, 38, 44, '(Řekli jsme mu): „Vezmi v ruku svou svazek (listů palmových) a udeř jím a neporuš přísahy své.“ V pravdě Pak shledali jsme jej trpělivým; výborný to služebník, jenž často (kajícně k nám) obracel se!'),
(4015, 38, 45, 'Vzpomeň též služebníka našeho Abrahama a Isáka a Jakuba, mužů silných a prozíravých.'),
(4016, 38, 46, 'Zajisté očistili jsme je čistou (vlastností): připomínáním si obydlí (věčného).'),
(4017, 38, 47, 'Oni pak jsou u nás mezi vyvolenými a dobrými.'),
(4018, 38, 48, 'A vzpomeň Ismaele a Elisea a Dzu-l-Kifla: ti všichni byli z dobrých.'),
(4019, 38, 49, 'Totoť jest Napomenutí: zajisté pak bohabojným dáno bude přebývání krásné:'),
(4020, 38, 50, 'zahrady Edenu, s otevřenými jim branami:'),
(4021, 38, 51, 'v nich spočívati budou (na pohovkách) a objednávati si ovoce hojné i pití:'),
(4022, 38, 52, 'a vedle nich budou děvy velkých očí, zrak (cudně) klopící, stejného s nimi věku.'),
(4023, 38, 53, '„Toto jest, co slíbeno vám bylo v den súčtování.“'),
(4024, 38, 54, '„Zajisté, totoť jest výživa naše, jež nikdy se nevyčerpá.“'),
(4025, 38, 55, 'Tak! Však svévolným zajisté dáno bude obývání zlé:'),
(4026, 38, 56, 'peklo, v němž péci budou se: jak hrozné to (bude) místo odpočinku!'),
(4027, 38, 57, 'Tak! Nechť okusí jej: vody vroucí a hnisu,'),
(4028, 38, 58, 'a jiných věcí druhu podobného.'),
(4029, 38, 59, '„Tento sbor dolů zajisté vržen bude střemhlav s vámi. Nebude dáno jim přivítání, neboť pečeni budou v ohni!“'),
(4030, 38, 60, 'Řeknou: „Naopak, vám nebude dáno přivítání! Vy předem schystali jste toto nám: a bídné jest to přebývání!“'),
(4031, 38, 61, 'A řeknou pak: „Pane náš, těm, již schystali nám toto, rozmnož dvojnásob muka v ohni!“'),
(4032, 38, 62, 'A řeknou: „Jak to přijde, že nevidíme mužů, jež počítali jsme mezi zlé,'),
(4033, 38, 63, 'a z nichž šprým tropili jsme si? Nebo snad, přehlédli je (naši) zrakové?“'),
(4034, 38, 64, 'Ano, toto jest pravda: tak zajisté, hádati se budou obyvatelé ohně.'),
(4035, 38, 65, 'Rci: „Jáť pouze jsem varovatelem: a není jiného Boha, kromě Boha Jediného, Vítězného,'),
(4036, 38, 66, 'Pána nebes i země, a všeho, cožkoliv mezi nimi jest, Mocného, Odpouštějícího!“'),
(4037, 38, 67, 'Rci: „Totoť zvěst jest velká,'),
(4038, 38, 68, 'a vy odvracíte se od ní!'),
(4039, 38, 69, 'Neměl jsem nijaké vědomosti o shromáždění nejvyšším, když spor vedli v něm,'),
(4040, 38, 70, 'však toto vnuknuto bylo mi jen proto, že varovatelem jsem zjevným;'),
(4041, 38, 71, 'když řekl Pán tvůj k andělům: ‚Jáť stvořím člověka z hlíny:'),
(4042, 38, 72, 'a až urovnám jej a vdechnu v něho z ducha svého, padněte před ním na tváře své.‘'),
(4043, 38, 73, 'I padli na tváře své andělé, všichni vesměs,'),
(4044, 38, 74, 'vyjma Iblíse, jenž zpyšněl a stal se zatvrzelým. Řekl (Bůh): ‚Iblísi, co zabránilo ti, abys padl na tvář svou, před tím, co stvořil jsem rukama svýma?'),
(4045, 38, 75, 'Zpyšněl\'s, či z vyšších jsi?‘'),
(4046, 38, 76, 'Řekl: ‚Jsem lepším jej: mne stvořil\'s z ohně a jej stvořil\'s z hlíny.‘'),
(4047, 38, 77, 'Řekl (Bůh): ‚Vyjdi odsud: kamenován jsi'),
(4048, 38, 78, 'a nad tebou stojí zlořečení mé až do dne soudného.‘'),
(4049, 38, 79, 'Řekl: ‚Pane můj, poshov mi, až do dne, kdy vzkříšeni budou.‘'),
(4050, 38, 80, 'Řekl (Bůh): ‚Budiž poshověno ti,'),
(4051, 38, 81, 'až do dne doby určené.‘'),
(4052, 38, 82, 'Řekl: ‚Při moci své (přisám), že zavedu je veškeré,'),
(4053, 38, 83, 'až na služebníky tvoje mezi nimi upřímné!‘'),
(4054, 38, 84, 'Řekl (Bůh): ‚Při pravdě, a pravdu pronáším:'),
(4055, 38, 85, 'dojista peklo tebou a těmi naplním, již následovat budou tebe, všechněmi.‘'),
(4056, 38, 86, 'Rci: ‚Nežádám si od vás za to odměny, aniž jsem z těch, kdož béřou na se (úkol) přílišný.'),
(4057, 38, 87, 'Tato (Kniha) pak napomenutím jest veškerenstvu:'),
(4058, 38, 88, 'a dojista zvíte zvěst její po čase!‘“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Seslání Knihy od Boha, Mocného, Moudrého!'),
(4060, 39, 2, 'Zajisté seslali jsme ti Knihu v pravdě: i uctívej Boha, jsa upřímným k němu ve víře.'),
(4061, 39, 3, 'Což nepřináleží Bohu víra upřímná? A ti, kdož vzali si vedle něho ochránce, (řkouce): „Uctíváme je pouze proto, aby přiblížili nás výše k Bohu,“ (shledají, že) Bůh zajisté rozsoudí mezi nimi ohledně toho, v čem rozcházejí se (míněním): zajisté pak Bůh nevede (správně) velkých lhářů a nevěřících.'),
(4062, 39, 4, 'Kdyby Bůh byl chtěl vzíti si syna, byl by vyvolil si, cokoliv byl by chtěl z toho, co tvoří. Chválen budiž! Onť Bůh jest Jediný, Vítězný!'),
(4063, 39, 5, 'Stvořil nebesa i zemi v pravdě: onť způsobuje, aby převalila se noc na den, a den aby převalil se na noc: onť podřídil slunce i měsíc (řádu), takže oba běží do lhůty předurčené. Není tedy Mocným, Odpouštějícím?'),
(4064, 39, 6, 'Stvořil vás z duše jedné, z níž byl potom učinil manželku její, a seslal vám z dobytčat osmero v párech. Tvoří vás v lůnech matek vašich v podobě za podobou, v trojnásobném temnu: totoť jest Bůh. Pán váš: jemu přináleží moc (všechna): není Boha, kromě něho — jak tedy můžete vzdalovati se jej?'),
(4065, 39, 7, 'Jste-li zatvrzelými (vůči němu), Bůh zajisté jest (dost) bohatým bez vás; však on nemá zalíbení v zatvrzelosti služebníků svých. Ale budete-li mu vděčni, nalezne proto zalíbení ve vás. Nebude pak obtěžkána duše nesoucí břímě břemenem duše druhé: poté navrátíte se k Pánu svému všichni a on poví vám o skutcích vašich. neb on dobře ví, co skrývají nitra.'),
(4066, 39, 8, 'Když protivenství stihne člověka, volá k Pánu svému, kajícně obraceje se k němu: poté pak, když byl zahrnul jej milostí svou, zapomíná (člověk) to, co byl volal k němu předtím; a činí Bohu rovné, aby zavedl (druhé) od stezky jeho. Rci: „Potěš se v zatvrzelosti své na krátko: tyť zajisté budeš jedním z obyvatelů ohně.“'),
(4067, 39, 9, 'Zdaž ten, kdož ve zbožnosti za doby noční padá na tvář svou, aneb v stoje (koná modlitbu), střeže se před životem budoucím a doufá v milosrdenství Pána svého — ?“ Rci (v odvět): „Budou rovni si ti, kdož vědí a ti, kdož nevědí?“ Však pouze lidé (pronikavého) rozumu jsou toho pamětlivi.'),
(4068, 39, 10, 'Rci: „Ó služebníci moji, kteří uvěřili jste, bojte se Pána svého. Těm, kdož dobře činili na tomto světě, bude dán (podíl) dobrý. Země pak boží prostorná jest — a v pravdě ti, kdož trpělivě vyčkávají, vyplacenu dostanou odměnu svou bez počtu!“'),
(4069, 39, 11, 'Rci: „Bylo nakázáno mi, abych uctíval Boha, upřímným jsa k němu ve víře.'),
(4070, 39, 12, 'A bylo nakázáno mi, abych byl prvním z odevzdaných do vůle boží.“'),
(4071, 39, 13, 'Rci: „V pravdě obávám se, nebudu-li poslušen Pána svého, trestu dne velkého!“'),
(4072, 39, 14, 'Rci: „Pročež uctívati budu Boha, upřímným jsa k němu ve víře své.'),
(4073, 39, 15, 'Vy pak uctívejte si, co chcete, vedle Boha!“ Rci: „Věru záhubě propadlí jsou ti, kdož v záhubu uvedli sebe samy a rodiny své v den zmrtvýchvstání: toť zajisté záhuba bude zjevná!“'),
(4074, 39, 16, 'Nad nimi clony budou ohnivé a pod nimi rovněž; tímto straší Bůh služebníky své: Ó služebníci moji, bojte se mne!'),
(4075, 39, 17, 'Těm pak, kdož vzdálili se (služby) Tághúta, a obrátili se kajícně k Bohu, dána bude zvěst radostná: i zvěstuj ji služebníkům mým,'),
(4076, 39, 18, 'kteří popřávají sluchu slovům mým a následují nejlepší z nich. Tito jsou oni, jež uvedl Bůh (na cestu pravou) a tito jsou lidé (pronikavého) rozumu.'),
(4077, 39, 19, 'Zdaž toho, nad nímž vynesen byl rozsudek trestu, zdaž takového spasíš od ohně (pekelného)?'),
(4078, 39, 20, 'Však těm, kdož bojí se Pána svého, schystány jsou komnaty (povýšené) nad nimiž jiné komnaty jsou postaveny; pod nimi řeky tekou. Slib jest to Boha a Bůh nikdy nezruší slibů svých!'),
(4079, 39, 21, 'Zdaž neviděl\'s jak Bůh seslal s nebe vodu a zavedl ji ve zřídla (skrytá) v zemi? Poté pak vzrůsti dává jí obilí různých barev: pak zvadne a vidíš je sežloutlé: na to pak mění je v stébla rozvátá? Zajisté v tomto napomenutí jest pro lidi (pronikavého) rozumu.'),
(4080, 39, 22, 'Zdaž ten, jehož nitro Bůh otevřel Islámu a který následuje světlo, přišlé od Pána jeho? — Však běda těm, jichž srdce zatvrzelá jsou vůči vzpomínání na Boha! Tito jsou v bloudění zjevném.'),
(4081, 39, 23, 'Bůh seslal nejlepší vyprávění — Knihu, jež podobá se sobě a opakuje se (ve svých dílech): při (čtení) jejím naskakuje husí kůže těm, kdož bojí se Pána svého: poté však měkne kůže i srdce jejich při vzpomínání Boha. Totoť Vedení jest Boha: jím vede Bůh, koho chce; však koho Bůh zavádí, tomu není vůdce.'),
(4082, 39, 24, 'Zdaž ten, jemuž bát se bude, že tváří svou půjde vstříc mukám trestu v den zmrtvýchvstání? — I řečeno bude nepravostným: „Okuste toho, co zasloužili jste si!“'),
(4083, 39, 25, 'Lháři nazývali (proroky) ti, kdož před nimi byli: a přikvačil na ně trest, zkad jej netušili.'),
(4084, 39, 26, 'A dal okusiti jim Bůh potupy v životě pozemském: však trest v životě budoucím větší bude: kdyby to jen věděli!'),
(4085, 39, 27, 'Zajisté pak učinil Bůh lidem v tomto Koránu podobenství všeho druhu, aby (toho) byli pamětlivi:'),
(4086, 39, 28, '(učinil jej) Koránem arabským, v němž není zkřivení, aby báli se Boha.'),
(4087, 39, 29, 'A učinil vám Bůh podobenstvím muže, jenž patří (několika) společníkům, tahajícím se (oň) mezi sebou: a muže, jenž cele patří muži jednomu: zdaž rovni jsou si tito dva podobou? Chvála Bohu, nikoliv! Však většina jich není si toho vědoma.'),
(4088, 39, 30, 'Zajisté, ty zemřeš a oni též zemrou.'),
(4089, 39, 31, 'Pak ty i oni, v den zmrtvýchvstání, před Pánem svým spor vésti budete.'),
(4090, 39, 32, 'A kdo nepravostnějším jest toho, jenž lže o Bohu a vylhanou nazývá pravdu, když přinesena jest mu? Zdaž nebude v pekle obydlí (schystáno) pro nevěřící?'),
(4091, 39, 33, 'Však ten, jenž přináší pravdu a věří v ni — ti zajisté jsou bohabojni.'),
(4092, 39, 34, 'Jim (schystáno) jest, cokoliv přejí si, u Pána jejich: takováto bude odměna těch, kdo dobře činí:'),
(4093, 39, 35, 'aby tak odejmul od nich Bůh nejhorší z toho, co konali, a odměnil je odměnou jejich za nejlepší z toho, co konali.'),
(4094, 39, 36, 'Což není Bůh postačitelným (ochráncem) služebníku svému? Nicméně, oni tě straší těmi, jež uctívají vedle něho! Však tomu, jejž Bůh zavádí (v blud), není vůdce;'),
(4095, 39, 37, 'a tomu, jejž Bůh (dobře) vede, není svůdce. Což není Bůh mocný, Pán pomsty?'),
(4096, 39, 38, 'Kdybys tázal se jich: „Kdo stvořil nebe a zemi?“ řeknou zcela jistě: „Bůh.“ Rci: „Co míníte? Kdyby Bůh chtěl postihnouti mne protivenstvím, zdaž ony, jež uctíváte vedle Boha, mohly by odejmouti protivenství to ode mne — a kdyby Bůh popřáti chtěl mi milosrdenství, zdaž ony mohly by zadržeti je ode mne?“ Rci: „Bůh jest oporou mojí: na něho spoléhají spoléhající.“'),
(4097, 39, 39, 'Rci: „Lide můj, jednejte dle nejlepší možnosti své, já (taktéž) činiti budu: a pak zvíte,'),
(4098, 39, 40, 'na koho přijde trest potupný a na koho padne trest trvalý!“'),
(4099, 39, 41, 'Zajisté pak seslali jsme ti Knihu pro lidstvo v pravdě: a kdo přiveden jest jí (na cestu pravou), k jeho vlastnímu jest to prospěchu: a kdo zbloudil, bloudí ve svůj vlastní neprospěch. Ty pak nejsi nad nimi poručníkem!'),
(4100, 39, 42, 'Bůh béře k sobě duše v hodině smrti a (taktéž) ony, jež neumírají, v době spánku: zadržuje pak u sebe ty, jimž přisoudil smrt, a posílá zpět druhé, až do lhůty předurčené. Zajisté v tomto znamení jsou lidem přemýšlivým.'),
(4101, 39, 43, 'Že vzali si vedle Boha přímluvce? Rci: „I když nemají moci nad ničím, aniž mají rozum.“'),
(4102, 39, 44, 'Rci: „Veškerá přímluva jest u Boha: jemu přináleží panství nebes i země: a k němu pak budete navráceni.“'),
(4103, 39, 45, 'Když zmíněno jest jméno Boha Jediného, smršťují se (nevolí) srdce těch, kdož nevěří v život budoucí: a když zmíněna jsou jména božstev vedle něho, hle, radostí jsou naplněni.'),
(4104, 39, 46, 'Rci: „Velký Bože, Stvořiteli nebes i země, jenž znáš věci skryté a zřejmé: ty rozsoudíš mezi služebníky svými ohledně toho, v čem rozcházeli se (míněním)!“'),
(4105, 39, 47, 'A kdyby nepravostným patřilo vše, což na zemi jest, vesměs, a ještě jednou tolik k tomu, rádi by vykoupili se tím od útrap trestu v den zmrtvýchvstání, když zjeví se jim od Boha to, nač nebyli počítali.'),
(4106, 39, 48, 'A zjeví se jim špatnosti toho, co si vydobyli, a obklopí je to, z čeho si byli šašky tropili.'),
(4107, 39, 49, 'Když protivenství dotkne se člověka, volá k nám (o pomoc): poté pak, když zahrnuli jsme jej dobrodiním svým, říká: „Bylo mi dáno jen následkem mého vědění.“ Nikoliv, byla to pouze zkouška, však většina jich si toho není vědoma!'),
(4108, 39, 50, 'Tak právě říkali ti, kdož byli před nimi, a co prospělo jim to, co si vydobyli?'),
(4109, 39, 51, 'A postiženi byli špatnostmi toho, co si vydobyli: a ty, kdož nepravostni jsou z těchto, též postihnou špatnosti toho, co si vydobyli, aniž by to mohli znemožniti.'),
(4110, 39, 52, 'Což nevědí, že Bůh hojně rozdává dary své, komu chce, aneb odměřuje je? Zajisté v tomto znamení jsou lidu věřícímu.'),
(4111, 39, 53, 'Rci: „Ó služebníci moji, kdož výstředné chovali jste se proti sobě samým, nezoufejte ohledně milosrdenství božího: Bůh zajisté odpouští viny veškeré, neb on odpouštějící jest a slitovný.'),
(4112, 39, 54, 'I obraťte se kajícně k Bohu a odevzdejte se do vůle jeho, dříve než přijde vám trest: pak již nebude vám pomoženo!'),
(4113, 39, 55, 'A následujte to nejlepší, co sesláno vám bylo od Pána vašeho, dříve než přijde na vás trest z nenadání, aniž byste toho tušili.'),
(4114, 39, 56, 'Aby nemohla říci duše: ‚Běda mi za to, co opomenul jsem (v povinnosti) vůči Bohu a že byl jsem z vysměvačných!‘'),
(4115, 39, 57, 'Aneb říci: ‚Kdyby Bůh byl mne uvedl (na pravou cestu), byl bych patřil k bohabojným.‘“'),
(4116, 39, 58, 'Aneb říci, když spatří trest: „Kdyby bylo mi návratu (na zemi), byl bych z těch, kdož dobře činí.“'),
(4117, 39, 59, '„Ano! Již byla přišla ti znamení má a vylhanými nazval\'s je a pyšným byl\'s a z nevěřících!“'),
(4118, 39, 60, 'A v den zmrtvýchvstání spatříš ty, kdož lhali o Bohu, s tvářemi zčernalými: zdaž nebude v pekle příbytek hrdopýšků?'),
(4119, 39, 61, 'Zachrání pak Bůh ty, kdož báli se jej, v místě bezpečném: nedotkne se jich zlo, aniž budou zarmouceni.'),
(4120, 39, 62, 'Bůh Stvořitelem jest všeho a on nade vším má poručenství;'),
(4121, 39, 63, 'jemu patří klíče nebes a země; a ti, kdož nevěří ve znamení jeho, dojista záhubě propadnou.'),
(4122, 39, 64, 'Rci: „Zdaž co jiného, než Boha budete nakazovati mi, abych uctíval, ó nevědomci"„'),
(4123, 39, 65, 'Vždyť již vnuknuto bylo tobě i těm, kdož před tebou byli, že spolčoval-li bys co s Bohem, niveč uvedeno bylo by dílo tvé a ty dojista byl bys z těch, kdož záhubě propadli.'),
(4124, 39, 66, 'Naopak: Boha jen uctívej a buď jedním z vděčných!'),
(4125, 39, 67, 'Neoceňují Boha dle pravé ceny jeho, ač země celičká bude hrstí hlíny v ruce jeho v den zmrtvýchvstání a nebesa stočena budou v pravici jeho. Chválen budiž a vysoko povýšen nad to, co spolčujete s ním!'),
(4126, 39, 68, 'A zatroubeno bude na roh i zemře vše, což na nebi jest a na zemi, vyjma toho, co bude chtít Bůh: pak zatroubeno bude po druhé a hle, (všichni) vstanou, (kolkolem) se rozhlížejíce.'),
(4127, 39, 69, 'A zazáří země světlem Pána svého, a vyložena bude Kniha (věčná) a předvedeni budou proroci a svědkové: rozsouzeno pak bude mezi nimi dle pravdy, a nikomu nebude ukřivděno.'),
(4128, 39, 70, 'A odměněna bude každá duše dle skutků svých: Bůh pak nejlépe ví, co kdo činí.'),
(4129, 39, 71, 'A hnáni budou ti, kdož neuvěřili, k peklu ve SBORECH : když dojdou k němu, otevřeny budou brány jeho a řeknou jim strážci jeho: „Což nepřišli k vám proroci ze středu vašeho, předčítajíce vám znamení Pána vašeho a varujíce vás před setkáním (s ním) v tento den váš?“ Řeknou: „Ano.“ Ale tu již pronešen bude ortel nad nevěřícími.'),
(4130, 39, 72, 'I řečeno bude (jim): „Vstupte do bran pekla, v němž (přebývati) budete věčně!“ Jak špatné bude přebývání hrdopýšků!'),
(4131, 39, 73, 'A hnáni budou ti, kdož báli se Pána svého, k zahradě (ráje) ve SBORECH: a když dojdou k ní, a otevřeny jsou brány její, řeknou jim strážci její: „Pokoj budiž s vámi! Dobrými jste byli: pročež vstupte do ní (k přebývání) věčnému:“'),
(4132, 39, 74, 'I řeknou: „Chvála Bohu, jenž v pravdě dodržel nám slib svůj a dal dědictvím nám zemi; nyní pak obývati můžeme v zahradě (ráje), kdekoliv bychom chtěli. Jak krásná jest odměna (dobře) pracujících!'),
(4133, 39, 75, 'A spatříš anděly, kroužící kol trůnu božího, vyvyšující Pána svého ve chvále jeho; a rozsouzeno bude mezi nimi dle pravdy, a řečeno bude: „Chvála Bohu, Pánu veškerenstva!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Há. Mím.'),
(4135, 40, 2, 'Seslání Knihy od Boha, Mocného, Moudrého!'),
(4136, 40, 3, 'Jenž odpouští viny a přijímá pokání, přísný jest v trestání, dlouhé má strpení: není Boha, kromě něho; a k němu vede cesta (návratu).'),
(4137, 40, 4, 'Nehádá se ohledně znamení božích nikdo, vyjma těch, kdož neuvěřili: nech nemátne tě jejich chození semo tamo v zemi.'),
(4138, 40, 5, 'Před nimi lhářem nazval Noema lid jeho a spojenci učinili tak po nich: silně zasazoval se každý národ proti proroku svému, snažíce se zmocniti se jej, a odporovali mu nepravdou, aby utlumili v něm pravdu. Však udeřil jsem na ně: a jaký byl trest můj!'),
(4139, 40, 6, 'A stejně tak pronesen byl rozsudek nad těmi, kdož neuvěřili: zajisté budou obyvateli ohně!'),
(4140, 40, 7, 'Ti, kdož nesou trůn a ti, kdož obklopují jej, vyvyšují Pána svého ve chvále jeho a věří v něho: a prosí za odpuštění pro ty, kdož uvěřili: „Pane náš, ty obestíráš vše milosrdenstvím a věděním svým: pročež odpusť těm, kdož kajícně obrátili se k tobě a následují stezku tvou: ochraň je před trestem plamene (pekelného) —'),
(4141, 40, 8, 'Pane náš — a uved je v zahrady Edenu, kterés přislíbil jim a těm, kdož spravedlivi byli z otců jejich a manželek jejich a potomstva jejich: tyť zajisté mocný jsi, moudrý!'),
(4142, 40, 9, 'A chraň je před, špatnostmi, neb nad tím, jejž před špatnostmi ochráníš dne onoho, zajisté smiloval ses; a toto bude blaženost velká.“'),
(4143, 40, 10, 'Zajisté pak zavoláno bude na ty, kdož neuvěřili: „V pravdě nenávist Boha větší jest nenávisti vaší vůči sobě samým, když voláni jste byli k víře a neuvěřili jste.“'),
(4144, 40, 11, 'Řeknou: „Pane náš, dvakráte dal\'s nám zemříti\' a dvakráte vzkřísil\'s nás: přiznáváme viny své: zdaž není možnosti vyjíti odtud?“'),
(4145, 40, 12, '„Toto jest údělem vaším proto, že když vzýván byl Bůh Jediný, nevěřili jste; a když spolčováno bylo s ním, věřili jste: a rozsouzení přináleží Bohu, Nejvyššímu, Velkému.“'),
(4146, 40, 13, 'Onť jest to, jenž ukazuje vám znamení svá a sesílá vám s nebe výživu: však nebude pamětliv toho, leda ten, jenž kajícně k Bohu obrátí se.'),
(4147, 40, 14, 'Pročež vzývejte Boha, upřímnými jsouce k němu ve víře, i když neradi to vidí nevěřící.'),
(4148, 40, 15, 'Povznešený na nejvyšší stupeň, Pán trůnu, vysílá ducha svého, ze svého rozkazu, na koho chce ze služebníků svých, aby varoval ohledně dne setkání,'),
(4149, 40, 16, 'dne, kdy vyjdou (z hrobů) a kdy nebude skryto Bohu ohledně jich pranic. Komu přináležeti bude moc (veškerá) v den (onen)? Bohu Jedinému, Vítěznému!'),
(4150, 40, 17, 'V den ten odměněna bude každá duše dle toho, co vydobyla si; v den ten nebude křivdy; neb Bůh zajisté rychlým jest v účtování.'),
(4151, 40, 18, 'A varuj je přede dnem blížícím se, kdy srdce lidí stoupati budou jim k hrdlu, dusíce je; a kdy nebude nepravostným přímluvce, jemuž by bylo popřáno sluchu.'),
(4152, 40, 19, 'Bůh dobře zná zrádné oči a vše, co skrývají nitra.'),
(4153, 40, 20, 'A Bůh rozsuzuje dle pravdy; však ti, které oni vzývají vedle něho, nerozsuzují o ničem: jedině Bůh dojista vše slyší a obzírá.'),
(4154, 40, 21, 'Což necestovali v zemi a neviděli, jaký byl konec těch, kdož před nimi byli? A byli mocnější jich silou a větší zanechali stopy v zemi: však stihl je Bůh (trestem) za viny jejich a nebylo jim proti Bohu ochránce.'),
(4155, 40, 22, 'To proto, že přišli k nim proroci jejich s jasnými důkazy a oni neuvěřili: pročež trestem stihl je Bůh, neb on silný jest a přísný v trestání.'),
(4156, 40, 23, 'Kdysi poslali jsme Mojžíše se znameními našimi a mocí zjevnou,'),
(4157, 40, 24, 'k Faraonovi a Hámánovi a Qárúnovi, však (tito) řekli: „Kouzelník jest to, (velký) lhář!“'),
(4158, 40, 25, 'A když přišel k nim s pravdou od nás, řekli: „Zabte syny těch, kdož uvěřili s ním, a živte dcery jejich!“ Však lest nevěřících jenom selhala.'),
(4159, 40, 26, 'A řekl Farao: „Nechte mne, abych zabil Mojžíše: pak nechť volá si k Pánu svému! Zajisté bojím se, že změní vaše náboženství a zavede tak v zemi pohoršení.“'),
(4160, 40, 27, 'I řekl Mojžíš: „Zajisté útočiště béřu u Pána svého a Pána vašeho, přede všemi pyšnými, kdož nevěří v den súčtování.“'),
(4161, 40, 28, 'A řekl muž VĚŘÍCÍ z rodu Faraonova, jenž skrýval víru svou: „Zdaž zabijete muže proto, že říká: ‚Pánem mým jest Bůh‘, a jenž přišel k vám s jasnými důkazy od Pána vašeho? Je-li lhářem, na něho padne lež jeho; a je-li pravdomluvným, postihne vás něco z toho, co slibuje vám: zajisté pak Bůh nevede (pravou cestou) toho, jenž výstředníkem jest a (velkým) lhářem!'),
(4162, 40, 29, 'Lide můj, dnes máte moc a vládnete v zemi, však kdo pomůže vám proti hněvu božímu, padne-li na nás?“ Řekl Farao: „Já ukazuji vám pouze to, co (sám) vidím; a vedu vás jedině cestou správnou.“'),
(4163, 40, 30, 'Tehdy řekl ten, jenž byl uvěřil: „Lide můj, obávám se, že přijde na vás den podobný dnu spojenců:'),
(4164, 40, 31, 'podobný případu lidu Noemova, a (kmene) \'Ád a Tsemúd, a těch, kdož přišli po nich: a Bůh jistě nežádá si učiniti útisk služebníkům svým.'),
(4165, 40, 32, 'Lide můj, jáť pak bojím se, že přijde na vás den vzájemného volání:'),
(4166, 40, 33, 'den, kdy obráceni budete zády zpět a vrženi v peklo: a nebude vám ochránce proti Bohu, neb koho Bůh v blud zavádí, tomu není vůdce!'),
(4167, 40, 34, 'Vždyť přišel k vám již před tím Josef s jasnými důkazy; však nepřestali jste pochybovati ohledně toho, s čím byl přišel k vám. Až když zhynul, řekli jste: ‚Nevzbudí Bůh po něm proroka.‘ Takovýmto způsobem zavádí Bůh toho, jenž výstředníkem jest a pochybovačem.'),
(4168, 40, 35, 'Ti, kdož hádají se ohledně znamení božích, aniž by zmocnění jaké bylo přišlo jim, vzbuzují velkou zášť Boha a těch, kdož uvěřili: takovýmto způsobem zapečeťuje Bůh srdce všech pyšných, urputných.“'),
(4169, 40, 36, 'Řekl pak Farao: „Hámáne, postav mi palác, aby dosáhl končin,'),
(4170, 40, 37, 'končin nebes a abych vylézti mohl až k Bohu Mojžíšovu: neb zajisté mám jej za lháře!“ Takto zkrášleny byly v očích Faraona zlé skutky jeho, a odvrácen byl od stezky (pravé): takže lstivé úklady jeho skončily pouze zkázou.'),
(4171, 40, 38, 'A řekl ten, jenž byl uvěřil: „Lide můj, následujte mne i povedu vás stezkou správnou.'),
(4172, 40, 39, 'Lide můj, zajisté tento život pozemský jest jen (pomíjejícím) požitkem: zajisté pak život budoucí jest obýváním trvalým.'),
(4173, 40, 40, 'Kdožkoli koná zlo, bude odměněn jen stejným jemu; a kdožkoli koná dobro, muž či žena, a jest věřícím — tito v pravdě vejdou v zahradu (ráje) a obdařeni budou v ní (blahy) bez počtu.'),
(4174, 40, 41, 'A lide můj, jak to přijde, že já zvu vás ke spáse a vy že zvete mne k ohni (pekelnému)?'),
(4175, 40, 42, 'Zvete mne, abych zapřel Boha a spolčoval s ním něco, o čemž nemám vědomosti: já pak zvu vás k Mocnému, Odpouštějícímu.'),
(4176, 40, 43, 'Není pochyby o tom, že to, k čemu vy zvete mne, nemá býti vzýváno ani v životě pozemském, ani v budoucím; a že k Bohu bude návrat náš, a že výstředníci budou obyvateli ohně.'),
(4177, 40, 44, 'Pak vzpomenete si na to, co říkám vám: a poroučím věc svou Bohu, neb Bůh (dobře) pozoruje služebníky své.“'),
(4178, 40, 45, 'I ochránil jej Bůh před zlem, jež lstivě kuli proti němu a obklopily rod Faraonův bědy trestu:'),
(4179, 40, 46, 'oheň, jemuž vystavováni jsou z jitra i s večerem: a v den, kdy nadejde hodina (soudu), řečeno jim bude: „Vstupte, lidé Faraonovi, do trestu nejpřísnějšího!“'),
(4180, 40, 47, 'A když spor povedou mezi sebou v ohni, řeknou ti, kdož slabými byli, k těm, kdož pyšni byli: „My následovali jsme vás: můžete tedy pomoci nám od části (trestu) ohně?“'),
(4181, 40, 48, 'I řeknou ti, kdož pyšni byli: „My jsme teď všichni v něm: zajistéť Bůh již rozsoudil mezi služebníky svými.“'),
(4182, 40, 49, 'A řeknou ti, kdož budou v ohni, ke strážcům pekla: „Vzývejte Pána svého, aby slevil, nám aspoň den z muk těchto.“'),
(4183, 40, 50, 'Řeknou (tito): „Zdaž nepřišli vám proroci vaši s jasnými důkazy?“ Řeknou: „Zajisté.“ Řeknou strážcové: „Volejte tedy (o \'pomoc)!“ Však volání nevěřících jen ztrácí se v bloudění.'),
(4184, 40, 51, 'Myť pak zajisté přijdeme na pomoc prorokům svým i těm, kteří uvěřili, v životě pozemském a v den, kdy povstanou svědkové:'),
(4185, 40, 52, 'v den, kdy neprospěje nepravostným omluva jejich, však zlořečení na ně padne a dáno bude jim zlé přebývání.'),
(4186, 40, 53, 'Kdysi již dali jsme Mojžíšovi Vedení a učinili jsme syny Israele dědici Knihy,'),
(4187, 40, 54, 'aby Vedením a Napomenutím byla lidem (pronikavého) rozumu.'),
(4188, 40, 55, 'Vyčkej tedy (trpělivě): zajisté slib Boha pravdivým jest: a pros za odpuštění hříchu svých a vyvyšuj Pána svého ve chvále jeho; s večerem i za jitra.'),
(4189, 40, 56, 'Zajisté pak ti, kdož hádají se ohledně znamení božích beze všeho zmocnění, v srdcích svých pouze pýchu chovají, však nedosáhnou jej. Pročež vezmi útočiště u Boha, neboť on vše slyší a obzírá.'),
(4190, 40, 57, 'Stvoření nebes a země zajisté větší jest stvoření lidstva: však většina lidí není si toho vědoma.'),
(4191, 40, 58, 'Nejsou si rovni slepí a vidoucí; taktéž se ti, kdož uvěřili a konají dobré skutky, a zle činící, jak málo jste toho pamětlivi!'),
(4192, 40, 59, 'Zajisté pak hodina (soudu) přijde, není pochyby o ní: však většina lidí tomu nevěří.'),
(4193, 40, 60, 'A řekl Pán váš: „Vzývejte mne a vyslyším vás: ti pak, kdož v pýše odvracejí se od sloužení mi, v peklo vejdou potupeni.“'),
(4194, 40, 61, 'Bůh to jest, jenž učinil vám noc, abyste odpočívali v ní, a den k vidění. Bůh zajisté velkým jest v milosti vůči lidem: však většina lidí není za to vděčna.'),
(4195, 40, 62, 'Totoť jest Bůh, Pán váš, Stvořitel, všeho: není Boha, kromě něho! Jak tedy můžete odvraceti se od něho?'),
(4196, 40, 63, 'Takto odvraceli se od něho ti, kdož znamení jeho zapírali.'),
(4197, 40, 64, 'Bůh jest to, jenž učinil vám zemi pevným podkladem a nebesa budovou: a dal vám podobu a krásnými učinil Podoby vaše: a dobrými věcmi vyživuje vás. Totoť jest Bůh, Pán váš: i požehnán budiž Bůh. Pán veškerenstva!'),
(4198, 40, 65, 'Onť jest Živoucí: není Boha, kromě něho! Pročež vzývejte jej, upřímnými jsouce k němu ve víre: chvála Bohu, Pánu veškerenstva.'),
(4199, 40, 66, 'Rci: „Zajistéť zakázáno bylo mi, abych vzývá ty, jež vzýváte vedle Boha, když jasné důkazy byly dány mi Pánem, mým: a nakázáno bylo mi, abych odevzdal se do vůle Pána veškerenstva.“'),
(4200, 40, 67, 'Onť jest to, který stvořil vás z prachu, poté pak z kapky (semene), pak z krve sražené: na to pak dává vyjíti vám (z lůna) jako nemluvně, aby nato dal dosíci vám věku dospělého: pak abyste dosáhli staroby — ač některý z vás umírá před tím — a tak abyste dosáhli lhůty (vám) předurčené, a možná, pochopili.'),
(4201, 40, 68, 'Onť jest to, který vzbuzuje život a umrtvuje: a když rozhodne něco, řekne jen: „Staň se!“ a stane se.'),
(4202, 40, 69, 'Zdaž neviděl\'s ty, kteří hádají se ohledně znamení božích? Jak mohli býti tak oddáleni?'),
(4203, 40, 70, 'A ti, kdož vylhanou, nazývají Knihu a to, s čím poslali jsme proroky své, dobře zvědí,'),
(4204, 40, 71, 'až chomouty budou na šíjích jejich a řetězy, jimiž taháni budou'),
(4205, 40, 72, 'v plamen (pekelný) a pak v ohni sžíháni budou!'),
(4206, 40, 73, 'Pak řečeno bude jim: „Kde jsou ti, jež s Bohem spolčovali jste a ctili'),
(4207, 40, 74, 'vedle Boha?“ Řeknou: „Ztratili se nám! Ale my nevzývali jsme pranic předtím.“ Takovýmto způsobem v blud zavádí Bůh nevěřící.'),
(4208, 40, 75, '„Toto jest odměna vaše za to, že neoprávněně radovali jste se na zemi a zpupně se chovali!'),
(4209, 40, 76, 'Vstupte do bran pekla, v němž (přebývati) budete věčně: jak špatné bude přebývání hrdopýšků!“'),
(4210, 40, 77, 'I vyčkávej trpělivě; zajisté slib Boha pravdivý jest: a ať již ukážeme ti něco z toho, co slibujeme jim, aneb vezmeme tě k sobě dříve, k nám navráceni budou!'),
(4211, 40, 78, 'A před tebou již poslali jsme proroky: o některých vyprávěli jsme ti, a o některých jsme ti nevyprávěli; a nepříslušelo prorokovi, aby předvedl znamení jaké, leda z dopuštění božího. Však když přijde rozkaz boží, tehdy rozsouzeno bude dle pravdy a záhubě propadnou ti, kdož za marnost jej prohlašovali.'),
(4212, 40, 79, 'Bůh to jest, jenž učinil vám dobytčata, abyste jezdili na některých a z některých měli potravu.'),
(4213, 40, 80, 'A máte z nich prospěch mnohý; a abyste dosáhli pomocí jich zámyslů niter svých, na nich i na lodích jste nošeni.'),
(4214, 40, 81, 'Tak ukazuje vám Bůh znamení svá: a které ze znamení božích chcete zapírati?'),
(4215, 40, 82, 'Což necestovali zemí a neviděli jaký byl konec těch, kdož byli před nimi? A byli početnějšími jich a mocnější silou a stopami, jež zanechali v zemi: a přece pranic neprospělo jim konání jejich!'),
(4216, 40, 83, 'Však když přišli jim proroci jejich s jasnými důkazy, holedbali se oni tím, co měli z vědění: i obklopilo je to, čemu se byli vysmívali.'),
(4217, 40, 84, 'A když viděli sílu hněvu našeho, řekli: „Uvěřili jsme v Boha Jediného a zavrhujeme to, co byli jsme s ním spolčovali.“'),
(4218, 40, 85, 'Však tehdy již neprospěla jim víra jejich — poté, když byli shlédli sílu hněvu našeho — dle zvyklosti Boha, jež předtím již byla vyplnila se nad služebníky jeho. A tak na místě propadli záhubě nevěřící!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Há. Mím.'),
(4220, 41, 2, 'Seslání od Milosrdného, Slitovného!'),
(4221, 41, 3, 'Kniha, jejíž znamení ROZDĚLENA byla jako Korán arabský pro lid vědoucí:'),
(4222, 41, 4, '(aby byl) věštitelem a varovatelem — však většina jich vzdaluje se a nenaslouchá.'),
(4223, 41, 5, 'A říkají: „Zastřena jsou srdce naše před tím, k čemu zveš nás: a v uších našich tíha jest: mezi námi a tebou jest clona. Dělej si, (jak chceš): my (též tak) dělati si budeme.“'),
(4224, 41, 6, 'Rci: „Jáť zajisté pouze jsem člověk, jako vy: vnuknuto pak mi jest, že Bůh váš jest jen Bohem Jediným! Kráčejte tedy přímo k němu a proste jej za odpuštění — a běda mnohobožcům!'),
(4225, 41, 7, 'Těm, kteří nedávají (povinné) almužny a v život budoucí již nevěří!'),
(4226, 41, 8, 'Těm pak, kdož uvěřili a konají dobré skutky, zajisté připadne odměna bezvýhradná.“'),
(4227, 41, 9, 'Rci: „Zdaž nechcete věřiti v toho, jenž stvořil zemi ve dvou dnech a činiti mu chcete rovné? Onť Pánem jest veškerenstva!'),
(4228, 41, 10, 'On učinil na ní temena hor, a požehnal jí, a ustanovil po ní různé potraviny ve čtyřech dnech, rovným dílem pro (všechny) žádající.'),
(4229, 41, 11, 'Pak vznesl se na nebe, jež bylo pouhým dýmem, a řekl jemu i zemi: „Pojďte ke mně, ať poslušné již, či proti vůli své.“ I řekly nebe i země: „K tobě jdeme, poslušně!“'),
(4230, 41, 12, 'I ustanovil je na sedmero nebes ve dvou dnech, a vnukl každému nebi činnost jeho: a ozdobili jsme nebesa nejspodnější pochodněmi a strážci takovéto bylo určení Mocného, Vševědoucího!“'),
(4231, 41, 13, 'A když odvrátí se, rci: „Varuji vás před úderem bouřným, podobným onomu, jenž zachvátil (kmeny) \'Ád a Tsemúd!“'),
(4232, 41, 14, 'Když přišli k nim proroci jejich zpředu jich i ze zadu, nabádajíce je, aby nevzývali ničeho, kromě Boha, řekli: „Kdyby byl chtěl Pán náš, byl by seslal anděly; myť zajisté v to, s čím posláni jste byli, nevěříme.“'),
(4233, 41, 15, 'Pokud se týče (kmene) \'Ád, pyšně chovali se v zemi beze všeho oprávnění, a řekli: „Kdož mocnějším nás jest silou?“ Což neviděli, že Bůh to byl, jenž stvořil je, a že tedy on byl mocnějším jich (silou)? Znamení naše pak popírali.'),
(4234, 41, 16, 'I poslali jsme na ně vichr bouřný ve dnech zlověstných, abychom dali okusiti jim trestu potupného v životě pozemském: však trest života budoucího potupnější bude a nebude jim pomoženo.'),
(4235, 41, 17, 'A pokud se týče (kmene) Tsemúd, dali jsme mu (dobré) vedení; však raději měl slepotu, než dobré vedení — pročež zachvátil je úder bouřný trestu hanlivého za to, co vydobyli si.'),
(4236, 41, 18, 'Zachránili jsme pak ty, kdož uvěřili a báli se Boha.'),
(4237, 41, 19, 'A (přijde) den, kdy shromážděni budou nepřátelé Boha k ohni, jdouce v řadách;'),
(4238, 41, 20, 'a když dojdou k němu, dosvědčí proti nim sluch i zrak i kůže jejich to, co byli činili.'),
(4239, 41, 21, 'A řeknou kůžím svým: „Proč svědčíte proti nám.“ Řeknou: „Dal výřečnost nám Bůh; ten, jenž dal výřečnost všemu: onť stvořil vás po prvé a k němu budete navráceni.'),
(4240, 41, 22, 'A nemohli jste ukrýti se tak, aby nesvědčily proti vám sluch a zrak váš, a kůže vaše; nýbrž mnili jste, že Bůh neví mnoho z toho, co činíte.'),
(4241, 41, 23, 'A tato domněnka vaše, kterou měli jste o Bohu, zničila vás: a stali jste se z oněch, již záhubě propadli.“'),
(4242, 41, 24, 'A i kdyby nyní trpělivě vyčkávali, přece obydlím jejich bude oheň (pekelný); a i kdyby prosili o prominutí, nebude prominuto jim.'),
(4243, 41, 25, 'Přisoudili jsme jim pak (věrné) soudruhy i zkrášlili ve zracích jejich, cokoliv před nimi bylo i co vzadu. A pronesen byl nad nimi (právem) rozsudek, jaký (pronesen byl) nad sbory Džinnů a lidí, které zašly před nimi: zajisté záhubě propadli.'),
(4244, 41, 26, 'A říkají ti, kdož neuvěřili: „Nenaslouchejte tomuto Koránu a přehlušte jej hovorem, abyste možná překonali (čtoucí jej).“'),
(4245, 41, 27, 'Však dojista dáme okusiti těm, kdož neuvěřili, trestu přísného, a dojista odměníme je dle nejhorších skutků jejich.'),
(4246, 41, 28, 'Takováto jest odměna nepřátel Boha: oheň jim schystán jest! V něm přebývání bude jim věčné, odměnou za to, že znamení naše popírali.'),
(4247, 41, 29, 'A řeknou (pak) ti, kdož neuvěřili: „Pane náš, ukaž nám ty, kdož zavedli nás, z Džinnů a lidí: hodíme je, jedny i druhé, pod nohy naše, aby byli z nejspodnějších!“'),
(4248, 41, 30, 'Zajisté pak na ty, kdož říkají: „Pánem naším jest Bůh,“ a poté béřou se přímo k němu, sestupují andělé, (říkajíce jim): „Nebojte a nermuťte se, nýbrž radujte se ze zahrady (ráje), jež slíbena byla vám!'),
(4249, 41, 31, 'My ochránci jsme vašimi v životě pozemském i budoucím: budete míti v ní, cožkoli žádají si duše vaše, vše, oč si požádáte,'),
(4250, 41, 32, 'jako pohostinství Odpouštějícího, Milosrdného!'),
(4251, 41, 33, 'Kdo, krásněji mluví toho, jenž vzývá Boha, a koná dobré skutky, a praví: „Jsem jedním z odevzdaných do vůle boží?“'),
(4252, 41, 34, 'Nejsou rovny si (skutky) dobré a špatné. Odplácej (špatné) tím, co jest lepší: a hle, ten, mezi nímž a tebou bylo nepřátelství, jako by stal se ti přítelem vřelým.'),
(4253, 41, 35, 'Avšak nedosáhnou tohoto leda ti, kdož trpělivě vytrvávají: a nedosáhne toho leda ten, jenž štěstí má velké.'),
(4254, 41, 36, 'A kdyby rozjitřilo tě nějaké svádění Satanovo, hledej útočiště u Boha, neb on všeslyšící jest, vševědoucí.'),
(4255, 41, 37, 'Ze znamení jeho jsou den a noc, a slunce a měsíc: pročež nepadejte na tváře své před sluncem ani před měsícem, nýbrž padejte na tváře své před Bohem, jenž stvořil je oba, chcete-li jej uctívati.'),
(4256, 41, 38, 'A jsou-li pyšni? — za to ti, kdož jsou u Pá na tvého, pějí chválu jeho nocí i dnem a neunavují se!'),
(4257, 41, 39, 'A ze znamení jeho jest, že vidíš zemi schlíplou: však když sešleme na ni vodu, vzpruží se a vzedme. Zajisté ten, jenž vzkřísil ji, jest též křisitelem mrtvých, neboť on nade všemi věcmi moc má.'),
(4258, 41, 40, 'Ti, kteří zneuznávají znamení naše, nejsou skryti nám — a zdaž ten, jenž vržen bude v oheň (pekelný), bude lépe na tom, než ten, kdo přijde v bezpečí vstříc dnu zmrtvýchvstání? Čiňte si, (jak chcete): Bůh zajisté skutky vaše (dobře) obzírá.'),
(4259, 41, 41, 'Zajisté ti, kdož neuvěřili v Napomenutí, když přišlo jim — a přece v pravdě jest to Kniha skvostná!'),
(4260, 41, 42, 'Nedostihne jí marnost, ni zpředu, ni ze zadu: (jest to) seslání Moudrého, Chvály hodného!'),
(4261, 41, 43, 'Nic není ti říkáno, co by nebylo bývalo říkáno prorokům před tebou: zajisté pak Pán tvůj má v moci odpuštění, ale též trest bolestný.'),
(4262, 41, 44, 'A kdybychom byli učinili jej Koránem cizojazyčným, byli by říkali: „Proč nebyla ROZDĚLENA nám znamení jehož (Korán) cizojazyčný a (lidu) arabskému?“ Rci: „Onť pro ty, kdož uvěřili, Vedením jest a vyléčením, a pokud se týče těch, kdo nevěří, v uších jejich jest tíha a jsou vůči němu slepi: na ně voláno jest z místa dalekého.“'),
(4263, 41, 45, 'A kdysi již dali jsme Mojžíšovi Knihu a mínění o ní rozešla se; nebýti pak slova předeslaného Pánem tvým, bylo by bývalo již rozsouzeno mezi nimi: neb zajisté byli ohledně Knihy v pochybnosti podezřívané.'),
(4264, 41, 46, 'Kdokoli činí dobro, činí tak ve prospěch své duše: a kdokoli činí zlo, tedy proti ní: a Pán tvůj jistě neukřivďuje služebníkům svým.'),
(4265, 41, 47, 'Jemu připadá vědomost o hodině (soudu): a není ovoce, jež vzešlo by ze sémě svého, aniž počne, neb porodí samice, bez vědomí jeho. A v den, kdy (Bůh) zavolá na ně: „Kde jsou ti společníci moji?“, řeknou: „Ujišťujeme tě, že není mezi námi nikoho, kdo dosvědčil by (to).“'),
(4266, 41, 48, 'A ztratí se jim ti, které vzývali před tím; oni pak dovtípí se, že není pro ně útulku.'),
(4267, 41, 49, 'Neunavuje se člověk prosením o dobro; však když dotkne se jej zlo, tu zoufalý jest, malomyslný.'),
(4268, 41, 50, 'A když okusiti dáme mu milosrdenství svého po protivenství, jimiž dotčen byl, říká jistě: „To (právem) patří mi, a nemyslím, že hodina (soudu) přijde; a i kdybych navrácen byl k Pánu svému, dostane se mi u něho krásného (úděle).“ Však my dojista zpravíme ty, kdož neuvěřili, ohledně skutků jejich; a dojista okusiti dáme jim trestu přísného.'),
(4269, 41, 51, 'A když milosti své popřáli jsme člověku, odvrací se a vzdaluje se stranou: když pak dotkne se jej zlo, modlitba jeho široká jest (a dlouhá).'),
(4270, 41, 52, 'Rci: „Co míníte? Je-li tato Kniha od Boha a neuvěříte-li v ni, kdo zbloudilejším jest onoho, jenž v rozkolu jest dalekém?“'),
(4271, 41, 53, 'Ukážeme jim znamení svá v končinách různých a na nich samých, až jasně ukázáno jim bude, že (tato Kniha) jest pravdou. Což nepostačilo (říci), že Pán tvůj jest svědkem věcí všechněch?“'),
(4272, 41, 54, 'Ó zajisté v pochybnosti jsou ohledně setkání s Pánem svým! Ó zajisté on obklopuje (věděním) věci všechny!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Há. Mím.'),
(4274, 42, 2, '\'Ain. Qáf.'),
(4275, 42, 3, 'Takto vnuká tobě (zjevení) a těm, kdož před tebou byli, Bůh. Mocný. Moudrý.'),
(4276, 42, 4, 'Jemu přináleží, cožkoli na nebi jest i na zemi: a on nejvyšší jest, velký!'),
(4277, 42, 5, 'Pukají téměř nebesa s hůry — a andělé vyvyšují Pána svého ve chvále jeho a prosí za odpustění pro ty, kdož na zemi jsou. Ó zajisté Bůh velkým jest v odpoustění, slitovným!'),
(4278, 42, 6, 'Ony pak, kdož vzali si vedle Boha ochránce, střeže Bůh, a ty nejsi nad nimi poručníkem!'),
(4279, 42, 7, 'Takto tedy vnuknuli jsme ti Korán a arabský, abys varoval jím matku měst a ty, kdož kolem jsou; a abys varoval je přede dnem shromáždění, o němž není pochyby: kdy jedni budou v zahradě (rajské) a druzí v plamenech.'),
(4280, 42, 8, 'Kdyby Bůh byl chtěl, byl by učinil je lidem jedním; však on, uvádí, koho chce, v milosrdenství své, a nepravostným nebude ochránce, ni pomocníka!'),
(4281, 42, 9, 'Či vzali si vedle něho ochránce? Právě Bůh jest (oním) ochráncem; on křísí mrtvé; a on nade všemi věcmi moc má.'),
(4282, 42, 10, 'V čemkoli pak rozešli jste se v mínění, rozsouzení toho přináleží Bohu. Totoť jest Bůh, Pán můj; na něho spoléhám a k němu kajícně obracím se!'),
(4283, 42, 11, 'Stvořitel nebes i země: učinil vám z vás samých družky a dobytčata (stvořil) v párech; tím rozmnožuje vás. Není ničeho jemu podobného — a on všeslyšící jest, vševědoucí.'),
(4284, 42, 12, 'Jemu patří klíče k nebi a zemi: hojně obdarovává výživou koho chce, aneb odměřuje (ji); neb zajisté on o všech věcech má vědomost.'),
(4285, 42, 13, 'Zákonem učinil vám náboženství, jež byl doporučil Noemovi, a jež vnuknuli jsme i tobě; a jež doporučili jsme Abrahamovi, a Mojžíšovi, a Ježíšovi, (řkouce jim): „Zachovávejte (toto) náboženství a nerozštěpujte je!“ Jest protivno mnohobožcům to, k čemu zveš je: Bůh pak vybéře si, koho chtíti bude, pro (náboženství) toto, a Bůh povede k němu ty, kdož kajícně k němu obrátí se.'),
(4286, 42, 14, 'A rozštěpili se jen poté, když přišlo jim vědění, ze vzájemné svévole; a nebýti slova Pána tvého, jež předcházelo, stanovíc lhůtu předurčenou, bylo by již bývalo rozsouzeno mezi nimi. Zajisté pak ti, kdož zdědili Knihu po nich, jsou v pochybnosti o ní váhavé.'),
(4287, 42, 15, 'Proto tedy zvi (je) a jdi přímým směrem, jakž nakázáno bylo ti, a nenásleduj choutek jejich, a rci: „Uvěřil jsem v Knihu, kterou seslal Bůh a nakázáno bylo mi, abych hlásal spravedlnost mezi vámi. Bůh jest Pánem naším i Pánem vaším: nám patří skutky naše a vám skutky vaše: není důvodu (k rozepři) mezi námi a vámi. Bůh shromáždí nás všechny a k němu vede (cesta) návratu.“'),
(4288, 42, 16, 'Pokud se týče těch, kteří přou se ohledně Boha poté, když byl vyslyšel (proroka), důvody jejich zavrženy budou Bohem, a nad nimi bude hněv jeho, a (schystán jest) jim trest přísný.'),
(4289, 42, 17, 'Bůh to jest, jenž seslal Knihu v pravdě, jakož i váhu; a kdo může tě zpraviti? Dost možná, hodina (soudu) blízka jest.'),
(4290, 42, 18, 'Uspíšiti chtějí ji ti, kdož nevěří v ni; však ti, kdož uvěřili, chvějí se bázní před ní, vědouce, že jest pravdiva. Ó zajisté ti, kdož pochybují o hodině (soudu), v bloudění jsou dalekém.'),
(4291, 42, 19, 'Bůh dobrotivý jest vůči služebníkům svým: hojně obdarovává, koho chce, a on silní jest, mocný.'),
(4292, 42, 20, 'Kdokoliv žádá si pěstění pole života budoucího, rozmnožíme mu pole jeho: a kdokoliv žádá si pěstění pole života pozemského, dáme mu je, však nebude mu v životě budoucím podílu.'),
(4293, 42, 21, 'Či mají snad společníky, kteří zákonem stanovili jim náboženství, jež nebyl dovolil Bůh? Však nebýti slova rozhodnutí, bylo by již bývalo rozsouzeno mezi nimi! Zajisté pak nepravostným schystán jest trest bolestný.'),
(4294, 42, 22, '(Tehdy) spatříš nepravostné chvějící se bázní před tím, co si vydobyli, an (trest) nezvratně padati bude na ně: však ti, kdož uvěřili a konali dobré skutky, budou v květnatých luzích zahrady (ráje); tam míti budou, cokoliv si přáti budou, u Pána svého — totoť milost bude velká!'),
(4295, 42, 23, 'Toto jest, co zvěstuje Bůh služebníkům svým, kteří uvěřili a konali dobré skutky. Rci: „Nežádám si od vás za to odměny, vyjma lásky k příbuzným. A kdokoli dobude si (zásluhy) dobrým skutkem, rozmnožíme mu jím dobro; neb zajisté Bůh jest velkým v odpouštění, vděčným.“'),
(4296, 42, 24, 'Či říkají: „On vymyslil si lež o Bohu?“ Však kdyby Bůh chtěl, dojista mohl by zapečetiti srdce tvé! Ale Bůh zahladí marnost a utvrdí pravdu slovy svými, neb zajisté dobře ví o tom, co skrývají nitra.'),
(4297, 42, 25, 'Onť to jest, jenž přijímá pokání od služebníků svých a promíjí jim hříchy a ví, co činíte.'),
(4298, 42, 26, 'A vyslyší ty, kdož uvěřili a konali dobré skutky a rozmnoží nad nimi milost svou: však pro nevěřící (schystán jest) trest přísný.'),
(4299, 42, 27, 'Kdyby Bůh v hojnosti rozdělával dary své služebníkům svým, svévoli tropili by v zemi; však on sesílá dle míry, cokoliv chce, neboť on (dobře) zpraven jest o služebnících svých a (dobře) je obzírá.'),
(4300, 42, 28, 'Onť to jest, jenž sesílá déšť hojný poté, když lidé byli ztratili naději, a šíří tak milosrdenství své: onť Ochránce jest, Chvályhodný!'),
(4301, 42, 29, 'Ze znamení jeho pak jest stvoření nebes i země a cokoliv rozptýlil po ní ze živočichů: a on ke shromáždění (všech) jich, kdykoli zachce, moc má.'),
(4302, 42, 30, 'Nepostihne vás nijaké neštěstí, jež nebylo by výsledkem skutků rukou vašich: a on promíjí (tak) mnohé!'),
(4303, 42, 31, 'Vám pak nelze znemožniti (jeho vůli) na zemi, a vedle něho nemáte ochránce, ni pomocníka.'),
(4304, 42, 32, 'A ze znamení jeho jsou též moře brázdící lodi, podobné horám:'),
(4305, 42, 33, 'kdyby chtěl, dal by ztichnouti větrům a zůstaly by nehybné na hřbetech (vln): zajisté v tomto znamení jsou všem trpělivě vyčkávajícím, vděčným.'),
(4306, 42, 34, 'Anebo dal by jim ztroskotati (za trest) za skutky jejich: však on promíjí (tak) mnohé!'),
(4307, 42, 35, 'Seznají pak ti, kdož hádají se ohledně znamení našich, že nebude jim útočiště.'),
(4308, 42, 36, 'Cožkoliv dáno bylo vám, jest pouze (dočasný) požitek života pozemského: a co u Boha jest, bude lepším a trvalejším pro ty kdož uvěřili a spoléhají na Pána svého;'),
(4309, 42, 37, 'a ty, kdož vzdalují se velkých hříchů a smilstev, a kdykoliv rozzlobeni jsou, odpouštějí;'),
(4310, 42, 38, 'a ty, kdož odpovídají na volání Pána svého, a zachovávají modlitbu, a jichž věci (řízeny) jsou PORADOU vzájemnou, a kteří z toho, čím obdařili jsme je, štědře (almužnu) rozdávají;'),
(4311, 42, 39, 'a ty, kdož postiženi byvše bezprávím, sami sobě pomáhají;'),
(4312, 42, 40, 'odplata pak špatnosti budiž špatnost jí stejná; — vsak kdo promíjí (bezpráví) a smiřuje se, odplata jeho (záleží) od Boha: neb Bůh nemiluje těch, kdož utiskují.'),
(4313, 42, 41, 'A proti tomu, jenž sám sobě pomohl si poté, když učiněno bylo mu bezpráví, nebude příčiny (k trestu):'),
(4314, 42, 42, 'však bude příčina (taková) vůči těm, kdož utiskují lid a svévoli páší beze všeho oprávnění: pro tyto (připraven) jest trest bolestný.'),
(4315, 42, 43, 'Však kdo trpělivě snáší a odpouští — toť zajisté věcí jest povinnou.'),
(4316, 42, 44, 'Tomu, jejž zavádí Bůh v blud, nebude poté ochránce: a spatříš nepravostné, když shlédnou trest svůj, řkoucí: „Jest možnosti nějaké k návratu?“'),
(4317, 42, 45, 'A spatříš je, když předvedeni budou před trest, ponížené hanbou, vrhati naň pokradmé pohledy. I řeknou ti, kdož uveřili: „Zajisté záhubě propadli ti, kdož v záhubu přivedli sebe samy a rodiny své v den zmrtvýchvstání! Ó zajisté, nepravostní vrženi budou v trest trvalý!“'),
(4318, 42, 46, 'A nebudou míti ochránců, již pomohli by jim vedle Boha; a tomu, jejž Bůh v blud zavádí, není cesty (pravé).'),
(4319, 42, 47, 'Odpovězte tedy (na volání) Pána svého před tím, než přijde den, jehož příchodu nikdo nezabrání Bohu, (aby nenařídil): nebude vám útulku v den onen, aniž bude možno vám zapírati!'),
(4320, 42, 48, 'A odvracejí-li se, věz, že neposlali jsme tě jako strážce nad nimi: tvým úkolem jest pouze hlásání. Zajisté, když dáme okusiti člověku milosrdenství svého, raduje se z něho; však když postihne jej cos špatného za předešlé činy rukou jeho, zajisté člověk jest zatvrzelcem.'),
(4321, 42, 49, 'Bohu přináleží panství nebes i země; tvoří, cokoliv chce: dává, komu chce, dcery; a dává, komu chce, syny.'),
(4322, 42, 50, 'Anebo dává je v párech, syny i dcery: a činí, koho chce, bezdětným: neboť on vševědoucí jest a všemocný!'),
(4323, 42, 51, 'A není dáno člověku, aby mluvil s ním Bůh, vyjma vnuknutím, nebo zpoza clony: aneb když posílá posla, aby vnuknul (lidem), z dopuštění jeho, cokoliv on chce: onť zajisté nejvyšší jest a moudrý.'),
(4324, 42, 52, 'A takovýmto způsobem poslali jsme ti s vnuknutím ducha z rozkazu svého: ty nevěděl\'s, co jest Kniha, aniž co jest víra — však my učinili jsme ji světlem, jímž vedeme, koho chceme ze služebníků svých. Ty pak zajisté vedeš ke stezce přímé,'),
(4325, 42, 53, 'ke stezce Boha, jemuž přináleží, cožkoliv jest na nebi a na zemi. Ó zajisté, k Bohu navracejí se věci všechny!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Há. Mím.'),
(4327, 43, 2, 'Při Knize zjevné!'),
(4328, 43, 3, 'Zajisté učinili jsme ji Koránem arabským, abyste pochopili!'),
(4329, 43, 4, 'Onať pak nalézá se v Knize prvotní u nás: (Korán jest to) vznesený, plný moudrosti.'),
(4330, 43, 5, 'Zdaž odmrštíme od vás Napomenutí proto, že lid jste výstřednický?'),
(4331, 43, 6, 'A kolik poslali jsme proroků mezi (národy) předešlé!'),
(4332, 43, 7, 'A nebylo proroka, jenž přišel k nim, z něhož by si nebyli šašky tropili!'),
(4333, 43, 8, 'Pročež zahladili jsme (národy) silnější těchto mocí: a před nimi stojí příklad předešlých!'),
(4334, 43, 9, 'A tážeš-li se jich: „Kdo stvořil nebe a zemi?“ jistě řeknou: „Stvořil je Mocný, Vševědoucí.“'),
(4335, 43, 10, '(Ano) ten, jenž učinil vám zemi kolébkou a učinil vám na ní stezky, abyste jimi vedeni byli;'),
(4336, 43, 11, 'a jenž seslal vám s nebe vodu, dle odměření: i vzkříjsme jí končinu zmrtvělou. Stejně tak (z hrobů) vyvedeni budete.'),
(4337, 43, 12, 'A jenž stvořil všechno (tvorstvo) v párech, a dal vám v lodích a dobytčatech prostředky k ježdění,'),
(4338, 43, 13, 'abyste seděli v rovnováze na hřbetech jejich a vzpomínali, když tak v rovnováze na nich sedíte, dobrodiní Pána svého, řkouce: „Chválen budiž ten, jenž podmanil nám toto! Neb sami nebyli bychom nikdy se k tomu dostali:'),
(4339, 43, 14, 'a zajisté my k Pánu svému navrátíme se.“'),
(4340, 43, 15, 'Však přece učinili mu ze služebníků jeho děti! Zajisté člověk jest velký zatvrzelec zjevný!'),
(4341, 43, 16, 'Či Bůh by si byl vzal dcery z toho, co stvořil, a syny vybral pro vás?'),
(4342, 43, 17, 'A přec když oznámeno jest jednomu z nich narození toho, o čem podobenství činí ohledně Milosrdného, zasmuší se temným stínem tvář jeho a jest jako zařezaný!'),
(4343, 43, 18, 'Zdaž (dávati budou Bohu za děti tvory), již vychováni jsou v ozdůbkách a sklonni jsou k hádkám bez zjevných důvodů?'),
(4344, 43, 19, 'A učinili z andělů, kteří jsou služebníci Milosrdného, ženy. Což byli svědky stvoření jejich? Zapsáno bude jim toto dosvědčování jich a tázáni (ohledně toho) budou.'),
(4345, 43, 20, 'A říkají: „Kdyby byl chtěl Milosrdný, nebyli bychom jich nikdy uctívali.“ Nijakého vědění nemají o tom: a pouze lhou!'),
(4346, 43, 21, 'Či snad dali jsme jim Knihu nějakou předtím, jíž přidržují se?'),
(4347, 43, 22, 'Naopak; ale říkají: „Shledali jsme otce naše v (jistém) náboženství: a my šlépějemi jejich vedeni jsme.“'),
(4348, 43, 23, 'A stejně tak, pokaždé, když poslali jsme před tebou varovatele některému městu, říkali bohatci jeho: „Zajisté shledali jsme otce své v (jistém) náboženství a my pokračujeme v šlépějích jejich.“'),
(4349, 43, 24, 'Řekli (jim): „A i kdyby dáno vám bylo cos správnějšího toho, co nalezli jste u otců svých?“ I řekli: „Myť zajisté v to, s čím posláni jste byli, nevěříme!“'),
(4350, 43, 25, 'Pročež pomstili jsme se na nich a viz, jaký byl konec těch, kdož lháři nazývali (proroky)!'),
(4351, 43, 26, 'A (vzpomeň), když řekl Abraham otci svému a lidu svému: „Jáť prost jsem viny na tom, co uctíváte,'),
(4352, 43, 27, 'vyjma (uctívání) toho, jenž stvořil mne a jenž povede mne (stezkou pravou).“'),
(4353, 43, 28, 'A toto učinil slovem trvalým v potomstvu svém, aby (k Bohu) navraceli se.'),
(4354, 43, 29, 'Ale popřál jsem tímto i otcům jejich požitků (pozemských), až do dne, kdy přišla jim pravda, a prorok zjevný.'),
(4355, 43, 30, 'A když přišla jim pravda, řekli: „Totoť kouzelnictví jest: my zajisté v ně nevěříme!“'),
(4356, 43, 31, 'A říkají: „Ledaže by byl býval seslán tento Korán muži z obou měst znamenitému, (byli bychom uvěřili).“'),
(4357, 43, 32, 'Zdaž oni jsou rozdělovateli milosrdenství Pána tvého? My rozdělujeme mezi ně živo bytí jejich v životě pozemském: a povznášíme jedny nad druhé v různých stupních, aby vzíti si mohli někteří jiné do poddanství. Však milosrdenství Pána tvého lepším jest toho, co shromažďují!'),
(4358, 43, 33, 'A nebýti toho, že lidé byli by se stali jedním národem (nevěřících) byli bychom dali domům těm, kdož nevěří v Milosrdného, střechy stříbrné a schodiště, po nichž by k nim vzestupovali:'),
(4359, 43, 34, 'a domům jich brány (ze stříbra), a lehátka, na nichž by spočívali:'),
(4360, 43, 35, 'a ZLATÉ OZDOBY; neb vše toto jest rozkoší života pozemského: však (lepší) život budoucí (schystán jest) u Pána tvého bohabojným.'),
(4361, 43, 36, 'A k tomu, jenž straniti bude se napomenutí Milosrdného, připneme řetězem Satana, jenž bude mu pak druhem (věrným):'),
(4362, 43, 37, 'a budou odvraceti (lidi) od stezky Páně; tito pak budou míti za to, ze (správně) jsou vedeni.'),
(4363, 43, 38, 'Až když pak přijde (člověk takový) před nás, řekne: „Kéž by mezi mnou a tebou byla vzdálenost obou východů! Jak špatný jsi to druh!“'),
(4364, 43, 39, 'Však toto neprospěje vám již v den onen, neb nepravostnými jste byli; družně budete sdíleti pak trest!'),
(4365, 43, 40, 'Zdaž dokážeš, abys sluch dal hluchým a správně vedl slepé a ty, kdož v bloudění jsou zjevném?'),
(4366, 43, 41, 'A ať již dojista odejmeme tě ze středu jejich, zajisté že pomstu na nich vykonáme;'),
(4367, 43, 42, 'nebo ať již ukážeme ti to, co slíbili jsme jim, my zajisté zmůžeme je!'),
(4368, 43, 43, 'I přidrž se toho, co vnuknuto ti bylo, neb zajisté jsi na stezce přímé!'),
(4369, 43, 44, 'A (Korán) dojista napomenutím jest tobě i lidu tvému: a dotazováni (ohledni něho) budete;'),
(4370, 43, 45, 'i taž se oněch proroků, jež před tebou poslali jsme, zda učinili jsme jim vedle Milosrdného božstva, která měli by uctívati.'),
(4371, 43, 46, 'Kdys již poslali jsme Mojžíše s jasnými důkazy svými k Faraonovi a předním lidu jeho, i řekl: „Jáť poslem jsem Pána veškerenstva.“'),
(4372, 43, 47, 'A když předvedl jim znamení naše, hle, smáli se jim!'),
(4373, 43, 48, 'A nebylo znamení, jež ukázali jsme jim, jež nebylo by bývalo větší předešlého: a stíhali jsme je tresty, aby k nám snad i obrátili se.'),
(4374, 43, 49, 'I řekli: „Ó kouzelníče, popros pro nás Pána svého, aby učinil to, čím zavázal se tobě, neb my zajisté jsme (již) na cestě správné.“'),
(4375, 43, 50, 'A když odejmuli jsme od nich trest, hle, porušili slib.'),
(4376, 43, 51, 'A dal Farao provolání lidu svému, řka: „Lide můj, což nepřináleží mne panství Egypta, jakož i řek těchto, jež pode mnou tekou? Což nevidíte?'),
(4377, 43, 52, 'Nejsem-liž lepším tohoto opovržence, jenž stěží dovede se vyjádřiti?'),
(4378, 43, 53, 'Kdyby (aspoň) měl na sobě zlaté náramky a kdyby byli přišli s ním andělé v družině!“'),
(4379, 43, 54, 'Takto lehkovážným učinil lid svůj a poslechli jej, neb byli lidem prostopášným:'),
(4380, 43, 55, 'a když rozhněvali nás, pomstili jsme se na nich a všechny utopili je,'),
(4381, 43, 56, 'a učinili jsme je prvním příkladem pro (národy) budoucí.'),
(4382, 43, 57, 'A když syn Mariin dán jest za příklad, hle, lid tvůj od něho odvrací se,'),
(4383, 43, 58, 'a říkají: „Zdaž božstva naše lepšími jsou, či on?“ Učinili ti jej tedy příkladem jen z chuti po sporu, neb věru lidem jsou hádkymilovným.'),
(4384, 43, 59, 'Onť pouze jest služebníkem (naším), jemuž popřáli jsme milosti své a příkladem učinili jej synům Israele.'),
(4385, 43, 60, 'A kdybychom chtěli, mohli bychom z vás učiniti anděly, aby nástupci vašimi byli na zemi.'),
(4386, 43, 61, 'Onť pak zajisté bude poznáním hodiny (soudu): pročež nepochybujte nijak o ní a následujte mne: totoť jest stezka přímá!'),
(4387, 43, 62, 'A nechť neodvrátí vás nijak od ní Satan; onť zajisté jest vám nepřítelem zjevným.'),
(4388, 43, 63, 'A když přišel Ježíš s jasnými důkazy, řekl: „Přišel jsem k vám s moudrostí a abych vysvětlil vám něco z toho, o čem rozcházíte se (v mínění): pročež bojte se Boha a poslechněte mne!'),
(4389, 43, 64, 'Bůh zajisté jest Pánem mým a Pánem vaším, pročež uctívejte jej: totoť jest stezka přímá!“'),
(4390, 43, 65, 'I rozešly se (v mínění různé) strany mezi sebou: a běda těm, kdož nepravostni byli, před trestem dne bolestiplného!'),
(4391, 43, 66, 'Což jiného očekávají, než aby hodina (soudu) přišla na ně, aniž by toho tušili, znenadání?'),
(4392, 43, 67, 'A nejlepší přátelé v den onen stanou se navzájem sobě nepřáteli; vyjma bohabojných:'),
(4393, 43, 68, '„Ó služebníci moji, vám netřeba se báti dnes, aniž rmoutiti se,'),
(4394, 43, 69, 'vám, kteříž uvěřili jste ve znamení naše a do vůle mé jste byli odevzdáni.'),
(4395, 43, 70, 'Vstupte v zahrady (ráje), vy, i manželky vaše, v radování!“'),
(4396, 43, 71, 'Obcházeni budou s mísami zlatými a číšemi, v nichž bude cokoliv přáti si budou duše a co lahoditi bude zrakům; a tam přebývati budete věčně.'),
(4397, 43, 72, 'Toto jest zahrada (ráje), již dědictvím učinili jsme vám v odměnu za skutky vaše.'),
(4398, 43, 73, 'V ní bude pro vás ovoce v hojnosti, z něhož požívati budete.'),
(4399, 43, 74, 'Zajisté pak provinilci budou na věk přebývati v trestu pekelném:'),
(4400, 43, 75, 'jenž nebude jim přerušen, a v úzkosti tam setrvají!'),
(4401, 43, 76, 'My pak neukřivdili jsme jim, nýbrž oni sobě samým ukřivdili.'),
(4402, 43, 77, 'A volati budou: „Ó Máliku! Nechť skončí s námi Pán tvůj!“ Řekne: „Nikoli, zde zajisté zůstanete!“'),
(4403, 43, 78, 'Nyní pak přinesli jsme vám pravdu; však většině vás pravda jest odporná.'),
(4404, 43, 79, 'Že pevně upletli svou léčku? Však i my svoji pevně upleteme.'),
(4405, 43, 80, 'Či domnívají se, že neslyšíme tajný (hovor) jejich a jejich šeptání? Zajisté, a poslové naši vedle nich (stojící), vše spisují.'),
(4406, 43, 81, 'Rci: „Kdyby Milosrdný měl syna, jáť první byl bych z jej uctívajících:'),
(4407, 43, 82, '(však) chválen budiž Pán nebes a země, Pán trůnu, nade (vše) to, co (o něm) vykládají!'),
(4408, 43, 83, 'I ponech je, ať jen žvaní a zahrávají si, až setkají se s oním dnem (soudu) svého, jenž slibován jest jim.'),
(4409, 43, 84, 'Neb on jest ten, jenž na nebi Bohem jest a na zemi Bohem jest: a on moudrý jest a vševědoucí.'),
(4410, 43, 85, 'I požehnán budiž ten, jemuž přináleží panství nebes a země a všeho, což mezi nimi jest: on má vědomost o hodině (soudu) a k němu navráceni budete.'),
(4411, 43, 86, 'A nebudou moci ti, které oni vzývají vedle Boha, přimluviti se (za ně), vyjma těch, kdož svědectví vydali o pravdě: a oni dobře to zvědí!“'),
(4412, 43, 87, 'A tážeš-li se jich, kdo stvořil je, jistě řeknou: „Bůh.“ Jak tedy mohou tak lháti?'),
(4413, 43, 88, 'A na rčení jeho: „Pane můj, tito zajisté jsou lidem nevěřícím,“'),
(4414, 43, 89, '(řekl Bůh): „Odvrať se tedy od nich a rci: ‚Pokoj vám!‘ Však oni zvědí!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Há. Mím.'),
(4416, 44, 2, 'Při knize zjevné!'),
(4417, 44, 3, 'Zajisté seslali jsme ji, abychom varovali jí, v noci požehnané:'),
(4418, 44, 4, 'v noci té ustanoveno jest veškeré nakázání moudré,'),
(4419, 44, 5, 'rozkazem od nás. Myť zajisté vysíláme (posly),'),
(4420, 44, 6, 'jako milosrdenství od Pána svého: onť zajisté všeslyšící jest, vševědoucí,'),
(4421, 44, 7, 'Pán nebes i země a všeho, což mezi oběma jest, pevně-li věříte.'),
(4422, 44, 8, 'Není Boha, kromě něho; obživuje i umrtvuje: Pán váš i Pán otců vašich předešlých.'),
(4423, 44, 9, 'A přec ještě v pochybnosti zahrávají si.'),
(4424, 44, 10, 'Však pozoruj je v den, kdy vydají nebesa DÝM zjevný,'),
(4425, 44, 11, 'jenž zahalí lidstvo: to trest bude bolestný!'),
(4426, 44, 12, '(Volati budou): „Pane náš, odejmi od nás trest, my již věříme!“'),
(4427, 44, 13, 'Jak mohlo by nyní býti jim dáno Napomenutí? Vždyť již byl přišel jim prorok zjevný,'),
(4428, 44, 14, 'však odvrátili se od něho a řekli: „Posedlý, (jinými) naučený!“'),
(4429, 44, 15, 'Kdybychom odejmuli od vás trest jen maličko, (k nevíře) vrátili byste se.'),
(4430, 44, 16, 'V den, kdy udeříme úderem velkým, tehdy pomstíme se!'),
(4431, 44, 17, 'Však již před nimi na zkoušku postavili jsme lid Faraonův, a přišel jim posel vzácný,'),
(4432, 44, 18, '(řka): „Vraťte se mnou služebníky boží: jáť jsem vám poslem důvěryhodným;'),
(4433, 44, 19, 'a nepovyšujte se nad Boha, neb přicházím k vám s mocí zjevnou;'),
(4434, 44, 20, 'a útočiště béřu u Pána svého i Pána vašeho, abyste nekamenovali mne.'),
(4435, 44, 21, 'A jest-li neuvěřili jste jim, oddalte se ode mne.“'),
(4436, 44, 22, 'Vzýval pak Pána svého: „Titoť zajisté jsou lidem provinilým!“'),
(4437, 44, 23, '„Vyjdi se služebníky mými za noci, neb pronásledováni budete.'),
(4438, 44, 24, 'A zanech za sebou moře zklidněné, neboť voje jejich potopeny budou.“'),
(4439, 44, 25, 'Kolik zahrad pak opustili a pramenů!'),
(4440, 44, 26, 'A polí obilných a přebývání vzácných!'),
(4441, 44, 27, 'A slastí, v nichž rozkoš mívali!'),
(4442, 44, 28, 'Tak (stalo se): a dědici jich lid jiný jsme učinili.'),
(4443, 44, 29, 'A nelkalo nad nimi nebe, ni země, a nebylo jim poshověno.'),
(4444, 44, 30, 'I zachránili jsme syny Israele od muk potupných:'),
(4445, 44, 31, 'od Faraona, jenž zajisté zpupným byl, a (jedním z výstředníků,'),
(4446, 44, 32, 'a vyvolili jsme je, vědomě, nad lidstvo veškeré:'),
(4447, 44, 33, 'a předvedli jsme jim znamení, v nichž zkouška byla zjevná.'),
(4448, 44, 34, 'Nicméně tito zde říkají:'),
(4449, 44, 35, '„Není jiné smrti nám, vyjma první, a nebudeme pak vzkříšeni:'),
(4450, 44, 36, 'i přiveďte nám sem otce naše, jste-li pravdomluvnými!“'),
(4451, 44, 37, 'Zdaž lepšími jsou lidu Tubba\' a těch, kdož před nimi byli? Zahladili jsme je, neb byli provinilými!'),
(4452, 44, 38, 'A nestvořili jsme nebe a zemi a což mezi oběma jest z kratochvíle;'),
(4453, 44, 39, 'nýbrž stvořili jsme je jen v opravdovosti! Však většina jich není si toho - vědoma.'),
(4454, 44, 40, 'Zajisté pak den rozdělení bude místem schůzky pro všechny:'),
(4455, 44, 41, 'den, kdy neprospěje pán sluhovi svému pranic, aniž bude jim pomoženo,'),
(4456, 44, 42, 'vyjma těch, nad nimiž slituje se Bůh: neb on zajisté mocný jest, moudrý!'),
(4457, 44, 43, 'Zajisté pak strom Az-Zaqqúm'),
(4458, 44, 44, 'pokrmem bude hříšného:'),
(4459, 44, 45, 'jak olej rozhřátý vříti bude v břiších,'),
(4460, 44, 46, 'vřením vody vroucí.'),
(4461, 44, 47, '„Chopte se ho i dovlečte jej doprostřed plamenů;'),
(4462, 44, 48, 'pak na hlavu jeho lijte muka vody vroucí.'),
(4463, 44, 49, 'Okus tohoto, neb tys zajisté mocný, vzácný.'),
(4464, 44, 50, 'Totoť jest, o čem pochybovali jste!“'),
(4465, 44, 51, 'Zajisté pak bohabojní budou v přebývání bezpečném,'),
(4466, 44, 52, 'v zahradách a u pramenů:'),
(4467, 44, 53, 'oděni hávy z hedvábí a kmentu, proti sobě sedíce.'),
(4468, 44, 54, 'Tak (tomu bude): za manželky pak dáme jim děvy černooké, velkooké;'),
(4469, 44, 55, 'tam objednávati budou si všeho (druhu) ovoce v bezpečí:'),
(4470, 44, 56, 'neokusí tam jiné smrti, vyjma prvního zemření; a ochrání je Bůh před mukami plamene.'),
(4471, 44, 57, 'Milostí to bude od Pána a blaženost bude to velká.'),
(4472, 44, 58, 'Snadným pak učinili jsme (tento Korán) v jazyku tvém, aby snad byli toho pamětlivi.'),
(4473, 44, 59, 'I dávej pozor: neb oni též pozor dávají.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Há. Mím.'),
(4475, 45, 2, 'Seslání Knihy od Boha, Mocného, Moudrého!'),
(4476, 45, 3, 'Zajisté v nebi a v zemi znamení jsou věřícím;'),
(4477, 45, 4, 'jakož i ve stvoření vás a v živočiších rozptýlených po zemi, znamení jsou lidu víry pevné;'),
(4478, 45, 5, 'a ve střídaní noci se dnem, a v tom, co Bůh seslal s nebe výživou, jíž oživuje zemi zmrtvělou; a v přeměně větrů, znamení jsou lidu chápavému.'),
(4479, 45, 6, 'Tato jsou znamení boží, jež předčítáme ti v opravdovosti: a v jakou rozpravu uvěří (tito lidé) po (zamítnutí) Boha a znamení jeho?'),
(4480, 45, 7, 'Běda každému (velkému) lháři hříšnému,'),
(4481, 45, 8, 'jenž naslouchá znamením božím, ana předčítána jsou mu, a pak setrvává v pýše své, jako by jich neslyšel: tomuto věstiž trest bolestný!'),
(4482, 45, 9, 'A naučil-li se čemu ze znamení našich, tropí si z nich šprým: těmto všem (schystán) jest trest potupný!'),
(4483, 45, 10, 'S zadu jich bude peklo: a neprospěje jim pranic to, čeho si byli dobyli, aniž ti, jež brali si za ochránce vedle Boha; jim (schystán) jest trest velký!'),
(4484, 45, 11, 'Toto jest (pravé) Vedení; a těm, kdož neuvěřili ve znamení Pána svého, (schystán) jest trest útrap bolestných.'),
(4485, 45, 12, 'Bůh to byl, jenž podrobil vám moře, aby lodi pluly po něm dle rozkazu jeho, a abyste snažili se dosíci (tak) podílu z milosti, jeho: a abyste snad byli vděčni.'),
(4486, 45, 13, 'A podrobil vám, což na nebi jest i na zemi: vše to jest od něho. Zajisté pak v tomto (všem) znamení jsou lidu přemýšlivému.'),
(4487, 45, 14, 'Rci těm, kdož uvěřili, aby odpustili těm, kteří nedoufají ve dny boží, jež stanovil, aby odměnil lid dle konání jejich.'),
(4488, 45, 15, 'Kdožkoli pak koná dobro, koná je ve svůj vlastní prospěch; a kdožkoli koná zlo, koná je proti sobě samému. Pak k Pánu svému navráceni budete.'),
(4489, 45, 16, 'Kdysi již dali jsme synům Israele Knihu a soudnost, a proroctví: a výživou dali jsme jim věci dobré, a vyznamenali je nad lidstvo veškeré.'),
(4490, 45, 17, 'A dali jsme jim jasné důkazy pro (naše) nařízení: a rozešli se (v mínění o nich) pouze poté, když přišlo jim vědění, ze vzájemné svévole. Zajisté pak Pán tvůj rozsoudí mezi nimi v den zmrtvýchvstání ohledně toho, v čem rozcházeli se.'),
(4491, 45, 18, 'Poté pak ustanovili jsme tebe nad zákonem ohledně nařízení našich: i následuj jej a nenásleduj choutek těch, kdož nevědí.'),
(4492, 45, 19, 'Neb oni zajisté neprospějí ti pranic proti Bohu: zajisté nepravostní jsou navzájem si ochránci, však Bůh ochráncem jest bohabojných.'),
(4493, 45, 20, 'Tato (Kniha) jest prohlédnutím lidstvu a (pravým) vedením a milosrdenstvím lidu pevně věřícímu.'),
(4494, 45, 21, 'Či domnívají se ti, kdož pášou špatnosti, že učiníme je rovnými těm, kteří uvěřili a konají dobré skutky, takže stejné bude jich žití a jich umírání? Jak špatně usuzují!'),
(4495, 45, 22, 'Stvořilť pak Bůh nebesa i zemi v pravdě; aby odměniti mohl každou duši po zásluze její: a nebude jim ukřivděno.'),
(4496, 45, 23, 'Co míníš? Ten, kdo vzal si za Boha choutku svou, a jejž Bůh zavedl vědomě, a pečeť vložil na sluch jeho a srdce jeho, a zastřel zrak jeho clonou — kdo uvede jej (na cestu pravou) poté, když Bůh (byl zavedl jej)? Což nebudete toho pamětlivi?'),
(4497, 45, 24, 'A říkají: „Není nám jiného života, vyjma života našeho pozemského; umíráme a žijeme a pouze čas hubí nás.“ Však nemají o tom nijakého vědění: pouze tak domnívají se.'),
(4498, 45, 25, 'A když předčítána jsou jim znamení naše jasná, jediný důvod jejich (proti nim) jest, že říkají: „Přiveďte nám sem tedy (zemřelé) předky naše, jste-li pravdomluvnými!“'),
(4499, 45, 26, 'Rci: „Bůh vzbouzí vás k životu, pak dá vám zemříti: a pak shromáždí vás ke dni zmrtvýchvstání, o němž není pochybnosti; však většina lidí není si toho vědoma.'),
(4500, 45, 27, 'Bohu přináleží panství nebes a země: a v den, kdy nadejde hodina (soudu), v den onen záhubě propadnou ti, kdož (pravdu) popírají.“'),
(4501, 45, 28, 'A spatříš všechny národy KLEČÍCÍ; každý národ zavolán bude ke Knize své: v den ten odměněni budete dle skutků svých.'),
(4502, 45, 29, 'Tato Kniha naše vyjadřuje se o vás dle pravdy, neb my zanášeli jsme v ní vše, což konali jste.'),
(4503, 45, 30, 'A pokud se týče těch, kdož uvěřili a konali dobré skutky, uvede je Pán jejich v milosrdenství své: toť blaženost bude zjevná.'),
(4504, 45, 31, 'A pokud se týče těch, kdož neuvěřili, (řekne se jim): „Což nebyla vám předčítána znamení naše? Však pyšně chovali jste se a byli jste lidem Provinilým!'),
(4505, 45, 32, 'A když řeklo se (vám): ‚Slib boží pravdivý jest: a není pochybnosti o hodině (soudu),‘ říkali jste: ‚Nevíme co hodina (soudu) jest: máme ji za pouhou domněnku a nejsme o ní nijak ujištěni.‘“'),
(4506, 45, 33, 'Tehdy zjeví se jim špatnost skutků jejich a obklopí je to, čemu se byli posmívali.'),
(4507, 45, 34, 'A bude řečeno (jim), „Dnes zapomeneme na vás, jakož i vy zapomněli jste na setkání se (s námi) v den tento: a obydlím vašim bude oheň a nebude vám pomocníků.'),
(4508, 45, 35, 'To za to, že za žert měli jste znamení boží a že zmámil vás život pozemský.“ Pročež v den ten nebudou již vyvedeni z ohně a nebude popřáno jim prominutí.'),
(4509, 45, 36, 'Bohu tedy budiž chvála, Pánu nebes a Pánu země, Pánu veškerenstva!'),
(4510, 45, 37, 'Jemuť přináleží velikost na nebi a na zemi a on mocný jest, moudrý.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Há. Mím.'),
(4512, 46, 2, 'Seslání Knihy od Boha, Mocného. Moudrého!'),
(4513, 46, 3, 'Stvořili jsme nebesa i zemi a cožkoliv mezi oběma jest, pouze v pravdě, a do lhůty předurčené: však ti, kdož neuvěřili, od toho, před čímž varováni, jsou, se odvracejí.'),
(4514, 46, 4, 'Rci: „Co míníte? To, co vzýváte vedle Boha, — ukažte mi, co stvořili na zemi, aneb mají-li podílu na nebesích? Přineste mi Knihu, (seslanou) před touto, aneb stopy nějakého vědění, jste-li pravdomluvnými!“'),
(4515, 46, 5, 'A kdo zbloudilejším jest toho, jenž vzývá vedle Boha něco, co nevyslyší jej, až do dne zmrtvýchvstání — a lhostejni jsou k volání jejich.'),
(4516, 46, 6, 'A v den, kdy shromážděno bude lidstvo, budou nepřáteli jim a budou uctívání jejich zapírati.'),
(4517, 46, 7, 'A když předčítána jsou jim znamení naše jasná, říkají ti, kdož neuvěřili v pravdu, když přišla jim: „Totoť kouzelnictví jest zjevné.“'),
(4518, 46, 8, 'Či říkají: „Vymyslil si jen?“ Rci: „Vymyslil-li jsem si jej, pak nevymůžete vy pro mne od Boha pranic! Onť nejlépe ví, co (o Koránu) pronášíte: onť dostačí mi jako svědek mezi mnou a vámi; a on velký jest v odpouštění, slitovný.“'),
(4519, 46, 9, 'Rci: „Nejsem novotářem mezi proroky: a nevím, co učiněno bude se mnou, nebo s vámi, když následuji pouze to, co vnuknuto mi bylo, a jsem jen varovatelem zjevným.“'),
(4520, 46, 10, 'Rci: „Co míníte? Je-li (tato Kniha) od Boha a vy neuvěřili jste v ni; dosvědčí-li pak svědek ze synů Israele ohledně souhlasnosti její a uvěří-li v ni, mezitím co vy pyšně chováte se — ? Zajisté Bůh nevede (cestou pravou) lid nepravostný!“'),
(4521, 46, 11, 'A říkají ti, kdož neuvěřili, o těch, kdož uvěřili: „Kdyby to bylo co dobrého, nebyli by předstihli nás v (přidržení se) jej.“ Však nedavše vésti se jím, řeknou jen: „Totoť lež jest stará.“'),
(4522, 46, 12, 'Však před ním byla kniha Mojžíšova vůdcovstvím a milosrdenstvím; a tato Kniha potvrzuje ji, v jazyku arabském, aby varovala nepravostné a zvěstí radostnou byla pro ty, kdož dobře činí,'),
(4523, 46, 13, 'Zajisté pak těm, kdož říkají: „Pánem naším jest Bůh“ a přímo béřou se k němu, netřeba se báti, aniž rmoutiti se.'),
(4524, 46, 14, 'Tito obyvateli budou zahrady (rajské), v níž přebývati budou věčně, v odměnu za skutky své.'),
(4525, 46, 15, 'A doporučili jsme člověku laskavé jednání vůči rodičům jeho: neb s útrapou nosila jej matka jeho a s útrapou porodila jej; a nošení jeho a (kojení až k) odstavení (trvalo) třicet měsíců. A když dosáhl dospělosti své a dosáhl věku čtyřiceti let, řekne: „Pane můj, povzbuď mne, abych vděčným byl za dobrodiní, jež prokázal\'s mi a rodičům mým; a abych konal dobro, v němž zalíbení bych nalezl; a učiň mne správným v potomstvu svém — neb kajícně obracím se k tobě a zajistéť jsem z odevzdaných do vůle tvé.“'),
(4526, 46, 16, 'Tito jsou oni, od nichž přijmeme nejlepší skutky jejich, a pomineme špatnosti jejich: mezi obyvateli budou zahrady (ráje): — tentoť slib jest pravdivý, jenž slíben byl jim!'),
(4527, 46, 17, 'A ten, kdo říká rodičům svým: „Pfuj na vás oba! Chcete slibovati mi, že vyveden budu (z hrobu), když celá pokolení byla zašla přede mnou?“ Oni pak oba prosí Boha zaň, (řkouce): „Běda ti! Uvěř; neb slib boží pravdivý jest.“ Však on říká: „Toť pouze báchorky jsou starých!“'),
(4528, 46, 18, 'Takoví budou z těch, nad nimiž vyřčen byl již rozsudek v národech, jež zašly před nimi, z Džinnů a lidí: tito záhubě propadli.'),
(4529, 46, 19, 'Pro všechny pak budou stupnice v činech jejich, a odplaceno bude jim dle toho, co činili: a nebude jim ukřivděno.'),
(4530, 46, 20, 'A přijde den, kdy ti, kdož neuvěřili, vystaveni budou ohni: „Promrhali jste dobré dary své v životě pozemském a v rozkoši užívali jste jich: pročež dnes tedy odměněni budete trestem potupy za pyšnost svoji na zemi, nikterak oprávněnou, a za prostopášnost svou.“'),
(4531, 46, 21, 'A zmiň se o bratru \'Áda, když varoval lid svůj v AL-AHQÁF — a měli varovatele před ním i po něm, — (řka): „Neuctívejte ničeho kromě Boha, neb zajisté bojím se pro vás trestu dne velkého.“'),
(4532, 46, 22, 'Řekli: „Přišel\'s k nám, abys odvrátil nás od božstev našich? Nuž tedy, přivodiž na nás to, co slibuješ nám, jsi-li pravdomluvným!“'),
(4533, 46, 23, 'Řekl: „Vědění jest pouze u Boha: jáť pouze hlásám vám, s čímž poslán byl jsem; však vidím, že lid jste nevědomý.“'),
(4534, 46, 24, 'A když viděli mračno, ženoucí se přímo k údolím jejich, řekli: „Totoť jest mračno, z nějž naprší nám.“ „Naopak! Totoť jest, co uspíšiti chtěli jste: vichr, v němž jest trest bolestný.'),
(4535, 46, 25, 'Zahubí všechno z rozkazu Pána svého!“ A zrána bylo viděti jen obydlí jejich (pustá)! Takovýmto způsobem odměňujeme lid provinilý.'),
(4536, 46, 26, 'A dali jsme jim postavení, do jakéhož postavili jsme i vás: a dali jsme jim sluch a zrak a srdce: však neprospěl jim sluch jich, aniž zrak jich, aniž srdce jejich pranic, neb popírali znamení boží; a obklopilo je to, čemu se byli vysmívali.'),
(4537, 46, 27, 'A také zahladili jsme kol vás některá města: a rozmanitá dávali jsme jim znamení, aby k nám snad navrátili se.'),
(4538, 46, 28, 'A proč nepomohli jim ti, které vzali si vedle Boha za božstva, jimž obětovali? Naopak, ztratili se jim! Taková byla lež jejich a smyšlenky!'),
(4539, 46, 29, 'A (pomni), když obrátili jsme směrem k tobě sbor Džinnů, aby naslouchati mohli Koránu: a když objevili se (na místě čtení jeho), řekli: „Pst! slyšte!“ A když skončeno bylo (čtení), vrátili se k lidu svému, jako varovatelé.'),
(4540, 46, 30, 'Řekli: „Lide náš, my zajisté slyšeli jsme Knihu, seslanou po Mojžíšovi, potvrzující písma předešlá: vede k pravdě a ke stezce přímé.'),
(4541, 46, 31, 'Lide náš, odpovězte hlasu božího Zvoucího, a uvěřte v Boha: onť odpustí vám viny vaše a vysvobodí vás od trestu bolestného.'),
(4542, 46, 32, 'A kdo neodpoví hlasu božího Zvoucího, neznemožní (moc) Boha na zemi, a nebude mu vedle Boha ochránců: tito jsou v bludu zjevném.“'),
(4543, 46, 33, 'Což neviděli, že Bůh, jenž stvořil nebesa i zemi a neunavil se stvořením jich, má též moc, aby vzkřísil mrtvé. Ano, zajisté; on nade všemi věcmi moc má!'),
(4544, 46, 34, 'V den, kdy ti, kdož neuvěřili, vystaveni budou ohni, (tázáni budou): „Není to pravda?“ Řeknou: „Ano, při Pánu našem!“ Řekne (Bůh): „Nuž okuste tedy trestu za to, že neuvěřili jste!“'),
(4545, 46, 35, 'Tudíž trpělivě vyčkávej, jakož trpělivě vyčkávali muži pevného rozhodnutí mezi proroky, a nesnaž se uspíšiti (trest) jejich. V den, kdy spatří, což slibováno jest jim, (připadne jim), jako by byli dleli v hrobě pouze jednu hodinu dne. (Dosti) hlásání! Zdaž zahlazeni budou jiní, než lidé prostopášní?');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Těch, kdož neuvěřili a odvracejí druhé od stezky boží, Bůh skutky znemožní:'),
(4547, 47, 2, 'od těch pak, kdož uvěřili a dobré skutky konají a věří v to, co sesláno bylo MOHAMMEDOVI — a jest to pravda od Pána jejich — odejme špatnosti jejich; a napraví mysli jejich.'),
(4548, 47, 3, 'To proto, že ti, kdož neuvěřili, následují marnost a ti, kdož uvěřili, následují pravdu od Pána svého: takovýmto způsobem činí Bůh lidem podobenství jejich.'),
(4549, 47, 4, 'A když potkáte nevěřící, (dejte se) do stínání hlav jejich, dokud nepobijete jich mnoho: a (zbylým) upevněte pouta. Poté pak buď omilostněte je, neb propusťte za výkupné, dokud nesloží válka břemen svých. Tak (čiňte). Kdyby Bůh byl chtěl, byl by sám pomohl si od nich; však (dopustil válku), aby vyzkoušel vás jedny druhými. Těch pak, kdož zabiti byli na stezce boží, nebudou zmařeny skutky.'),
(4550, 47, 5, 'Bůh povede je (cestou pravou) a napraví mysli jejich,'),
(4551, 47, 6, 'a uvede je v zahradu (ráje) o níž jim byl již pověděl.'),
(4552, 47, 7, 'Vy, kteří jste uvěřili, pomůžete-li Bohu, on též pomůže vám a upevní kroky vaše.'),
(4553, 47, 8, 'A ti, kdož neuvěřili, nechť zhynou, a Bůh zmaří skutky jejich.'),
(4554, 47, 9, 'To proto, že odpor měli vůči tomu, co Bůh byl seslal: i niveč uvedeny budou skutky jejich!'),
(4555, 47, 10, 'Což necestovali v zemi, aby byli viděli jaký byl konec těch, kdož byli před nimi? Bůh zničil je a nevěřící stihne osud podobný.'),
(4556, 47, 11, 'To proto, že Bůh ochráncem jest těch, kdož uvěřili a nevěřícím není ochránce!'),
(4557, 47, 12, 'Bůh zajisté uvede ty, kdož uvěřili, a konali dobré skutky v zahrady, pod nimiž řeky tekou: ale ti, kdož neuvěřili, užívají a požírají, jako žerou dobytčata! Oheň pekelný bude příbytkem jejich.'),
(4558, 47, 13, 'A kolik měst, mocnějších silou města, jež vyhnalo tě, zahladili jsme a nebylo jim pomocníka!'),
(4559, 47, 14, 'Zdaž ten, jenž opírá se o jasný důkaz od Pána svého, bude jako ten, jemuž zkrášleno bylo zlé konání jeho a (jako ti, kdož) následují choutky své?'),
(4560, 47, 15, 'Vypodobnění zahrady rajské, jež slíbena jest bohabojným: v ní řeky jsou s vodou nehnijící; a řeky mléka, jehož chuť se nikdy nemění; a řeky vína, lahodného pijícím, a řeky medu zčistěného; v ní budou míti plody všeho druhu, a odpuštění od Pána svého. (Zdaž úděl jich bude) jako úděl toho, jenž věčně přebývati bude v ohni, a napájeni budou vodou vroucí, takže drásati bude střeva jejich?'),
(4561, 47, 16, 'Jsou mezi nimi někteří, kdož naslouchají ti, dokud neodejdou od tebe; pak říkají těm, jimž dáno bylo vědění: „Co to žvaní?“, posměšně. Tito jsou oni, na jichž srdce pečeť vložil Bůh a kteří následují choutky své.'),
(4562, 47, 17, 'Však ty, kdož následují cestu pravou, rozmnoží Bůh v (dobrém) vedení, a dá jim bázeň (boží).'),
(4563, 47, 18, 'Což jiného očekávají nevěřící, než že přijde na ně hodina (soudu) znenadání? Vždyť již přišly příznaky její; a když vskutku přijde na ně, k čemu bude dobré jim napomenutí jich?'),
(4564, 47, 19, 'Tedy věz, že není Boha, kromě Boha, a pros za odpuštění vin svých, jakož i (vin) věřících mužů a žen, Bůh pak dobře ví o pochůzkách vašich (s místa na místo) a o (konečném) přebývání vašem.'),
(4565, 47, 20, 'A říkají ti, kdož uvěřili: „Kdyby jen seslána byla súra!“ Však když rozhodná súra seslána jest a v ní zmínka učiněna o boji, spatříš ony, v jichž srdcích jest choroba, hledící na tebe pohledem omdlelého v zápase smrtelném, pročež přístojnější byla by jim poslušnost a řeč zdvořilá.'),
(4566, 47, 21, 'Když pak rozhodnuto jest o věci, tu kdyby věrni byli Bohu, bylo by to pro ně lepším!'),
(4567, 47, 22, 'Zdaž chtěli byste, kdybyste odvrátili se (od Boha), činiti pohoršení v zemi, a chtěli přervati krevní svazky své?'),
(4568, 47, 23, 'Tito jsou oni, jimž zlořečil Bůh a němotou postihl, a slepotou ranil zrak jejich.'),
(4569, 47, 24, 'Což nebudou rozbírati si (v mysli) Korán? Anebo zámky mají na srdcích svých?'),
(4570, 47, 25, 'Zajisté pak těm, kdož navracejí se zpět, poté, když jasně ukázáno bylo jim Vedení, Satan našeptává (klam) a plní je (přeludy).'),
(4571, 47, 26, 'To proto, že říkají těm, kdož odpor mají k tomu, co seslal Bůh: „Uposlechneme vás v některých věcech.“ Však (dobře) zná Bůh tajemství jejich.'),
(4572, 47, 27, 'Jak bude jim, až andělé, přišedší, aby umrtvili je, budou bíti je v tváře a záda jejich?'),
(4573, 47, 28, 'To proto, že následovali to, co hněvem plní Boha a nenáviděli, v čem zalíbení nalézá: i niveč uvede skutky jejich.'),
(4574, 47, 29, 'Či domnívají se ti, v jichž srdcích jest choroba, že Bůh nepřivede (na světlo) zlomyslnost jejich?'),
(4575, 47, 30, 'Kdybychom chtěli, mohli bychom ti je ukázati, i poznal bys je po známkách jejich; ale dojista poznáš je dle spletené řeči jejich, Bůh pak (dobře) zná skutky vaše.'),
(4576, 47, 31, 'A dojista na zkoušku postavíme vás, dokud nerozpoznáme, kteří usilovně bojují a trpělivé vyčkávají mezi vámi: a vyzkoušíme zprávy o vás.'),
(4577, 47, 32, 'Zajisté pak ti, kdož neuvěřili. a odvracejí (druhé) od stezky boží, a odtrhli se od proroka poté, když jasně ukázáno bylo jim Vedení, neuškodí Bohu pranic a on niveč obrátí skutky jejich.'),
(4578, 47, 33, 'Vy, kteří jste uvěřili, poslušni buďte Boha a poslušni buďte proroka, a nečiňte marnými skutky své.'),
(4579, 47, 34, 'Zajisté těm, kdož neuvěřili, a odvracejí (druhé) od stezky boží, a zemrou pak jako nevěrci, Bůh neodpustí.'),
(4580, 47, 35, 'Pročež nebuďte zbabělci a nezvete (nevěřících) k míru, když vy jste navrchu; neb Bůh jest s vámi, a neošidí vás o zásluhu skutků vašich.'),
(4581, 47, 36, 'Život pozemský jest pouhou hrou a kratochvílí: ale věříte-li a bojíte-li se Boha, on dá vám odměny vaše a nebude požadovati ničeho ze statků vašich.'),
(4582, 47, 37, 'Kdyby žádal je od vás a nutil vás, lakotili byste a on tak na venek přivedl by zlomyslnost vaši.'),
(4583, 47, 38, 'Hle, vy jste to, kteří vyzýváni jste, abyste štědře rozdávali ve stezce boží: ale jsou mezi vámi někteří, již lakotí; však kdo lakotí, lakotí jen ve svůj vlastní neprospěch, neb Bůh bohatý jest a vy chudi. A obrátíte-li se, zamění vás Bůh lidem jiným, jenž pak nebude takovým, jakými jste vy!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Zajisté popřáli jsme ti zvítěziti VÍTĚZSTVÍM zjevným.'),
(4585, 48, 2, 'Na důkaz toho, že odpustil ti Bůh dřívější viny tvé i pozdější a dovršuje milost svou nad tebou a vede tě stezkou přímou:'),
(4586, 48, 3, 'a že pomáhá ti Bůh pomocí mocnou.'),
(4587, 48, 4, 'Onť to jest, jenž sesílá poklid v srdce věřících, aby rozmnožili vírou víru svoji: — Bohu pak přináležejí voje nebes i země, a Bůh vševědoucí jest a moudrý; —'),
(4588, 48, 5, 'a aby uvedl věřící muže a ženy do zahrad, pod nimiž řeky tekou, v nichž přebývati budou věčně: a aby odejmul od nich špatnosti jejich. Totoť pak bude u Boha blažeností velkou.'),
(4589, 48, 6, 'A aby potrestal pokrytecké muže a ženy a mnohobožce a mnohobožky, kdož smýšlejí o Bohu smýšlením zlým: proti nim obrátí se zlé smýšlení jejich a rozhněván jest na ně Bůh a zlořečil jim a peklo schystal jim příbytkem: jak zlá to bude cesta!'),
(4590, 48, 7, 'A Bohu přináležejí voje nebes i země a Bůh mocný jest a moudrý!'),
(4591, 48, 8, 'Zajistéť vyslali jsme tě jako svědka a věštitele a varovatele,'),
(4592, 48, 9, 'abyste uvěřili v Boha a proroka jeho: a pomáhali mu a ctili jej a chválili jej za jitra i s večerem.'),
(4593, 48, 10, 'Zajisté ti, kdož vcházejí v úmluvu věrnosti s tebou, vcházejí v úmluvu s Bohem: ruka boží jest nad rukama jejich. Kdožkoli tedy poruší smlouvu tuto, jedině ke škodě vlastní ji porušuje, a kdo věrně dostojí smlouvě své s Bohem, tomu dá Bůh odměnu velkou.'),
(4594, 48, 11, 'Řeknou ti Arabové z pouště, kteří zůstali v zadu: „Byli jsme zaneprázdněni majetky svými a rodinami svými: pročež pros za odpuštění pro nás.“ Říkají jazyky svými, co není v srdcích jejich. Rci jim: „A kdo zpomůže vám proti Bohu, kdyby postihnouti chtěl vás škodou aneb postihnouti chtěl vás prospěchem? V pravdě, Bůh dobře zpraven jest o skutcích vašich.'),
(4595, 48, 12, 'Naopak, mnili jste, že nevrátí se již prorok a věřící k rodinám svým nikdy a líbilo se vám pomyšlení toto v srdcích vašich: smýšleli jste smýšlení zlé a stali jste se lidem nehodným.“'),
(4596, 48, 13, 'A kdožkoli nevěří v Boha a v proroka jeho — zajisté schystali jsme nevěřícím výheň žhavou.'),
(4597, 48, 14, 'Bohu přináleží panství nebes i země: odpouští, komu chce, a tresce, koho chce: a Bůh velkým jest v odpouštění, slitovným.'),
(4598, 48, 15, 'Řeknou ti, kdož zůstali vzadu, když vycházíte, abyste dobyli (jisté) kořisti: „Nechte nás jíti s vámi.“ Tak chtějí způsobiti změnění slova božího. Rci: „Nepůjdete s námi: tak ohledně vás rozhodl Bůh již dříve.“ I budou říkati: „Nikoliv, ze závisti tak činíte.“ Nikoliv, však pochopiti z toho jen málo dovedou.'),
(4599, 48, 16, 'Rci Arabům z pouště, zůstavším vzadu: „Zavoláni budete k boji proti lidu urputnosti velké, s nímž válčiti budete, dokud neodevzdá se do vůle boží. Poslechnete-li, dá vám Bůh odměnu krásnou, však odvrátíte-li se jako odvrátili jste se před tím, ztresce vás trestem bolestným.“'),
(4600, 48, 17, 'Nebude slepcům hříchu, aniž chromým hříchu, aniž nemocným hříchu, (nepotáhnou-li do pole). Kdokoliv pak poslušen bude Boha a proroka jeho, uvede jej Bůh v zahrady, pod nimž řeky tekou: a kdožkoliv odvrátí se, ztresce jej trestem bolestným.'),
(4601, 48, 18, 'Bůh již nalezl zalíbení ve věřících, když smlouvu s tebou činili (rukoudáním) pod stromem: a znal rozpoložení srdcí jejich a seslal jim poklid, a odměnil je vítězstvím brzkým,'),
(4602, 48, 19, 'jakož i kořistí bohatou, jíž dobyli: a Bůh mocným jest a moudrým.'),
(4603, 48, 20, 'Přislíbiltě vám Bůh kořist bohatou, že dobudete jí a uspíšil vám splnění tohoto slibu svého: odvrátil pak ruce lidí od vás, aby znamením to bylo věřícím a aby uvedl vás na stezku přímou.'),
(4604, 48, 21, 'A jinou (ještě kořist), jíž dosud nebylo lze vám dobýti: však Bůh již shrnuje ji pro vás, a Bůh nade všemi věcmi moc má.'),
(4605, 48, 22, 'Budou-li válčiti proti vám ti, kdož neuvěřili, dojista obrátí se zády k vám v útěku: a pak nenaleznou ochránce, aniž pomocníka.'),
(4606, 48, 23, '(Tak bylo) zvyklostí Boha činiti v minulosti a ve zvyklostech Boha nenajdeš změny.'),
(4607, 48, 24, 'Onť to byl, jenž zadržel ruce jejich od vás a ruce vaše od nich v údolí Mekky poté, když dal vám vítězství nad nimi a Bůh (dobře) skutky vaše pozoroval.'),
(4608, 48, 25, 'To jsou ti, kteří neuvěřili a odpírali vám přístup do modlitebnice posvátné, jakož i nedovolovali darům obětním, aby došly místa určení svého. Nebýti pak toho, že byli mezi nevěřícími mužové a ženy věřící, kterých jste neznali, a kteří by byli bývali podupáni s sebou, čímž vy nevědomky byli byste na se uvalili hřích; (a tak) aby Bůh mohl uvésti v milosrdenství své, koho by byl chtěl: nebýti tohoto smíšení, byli bychom jistě ztrestali ty, kdož neuvěřili, trestem bolestným.'),
(4609, 48, 26, 'Tehdy nevěřící plnili srdce svá zuřivostí, zuřivostí nevědomosti; seslal pak Bůh poklid svůj na proroka svého a na věřící a upevnil v nich slovo bázně boží, neb (plně) hodni ho byli a (plně) si je zasloužili: a Bůh o všech věcech má vědomost.'),
(4610, 48, 27, 'Pravdivě naplnila se slova boží prorokovi ve snách: „V pravdě vejdete v modlitebnici posvátnou, bude-li Bůh chtíti, v bezpečí, s hlavami oholenými a ostříháni, beze vší bázně; Bůh pak ví, co vy nevíte; a mimo to přichystal vám vítězství nedaleké.“'),
(4611, 48, 28, 'Onť jest to, jenž vyslal proroka svého s Vedením a náboženstvím pravdivým, aby vyniknouti dal mu nad veškerými náboženstvími: a Bůh postačí tomu jako svědek!'),
(4612, 48, 29, 'Mohammed jest prorokem božím: ti, kdož při něm stojí, hroznými jsou vůči nevěřícím, láskyplnými pak mezi sebou. Spatříš je klečící, skloněné na tváře své, doprošující se blahovolnosti Boha a zalíbení jeho v nich: stopy vzývání jejich jsou na tvářích jejich. Totoť jest podobenství jich v Zákoně a v Evangeliu: jsou jako sémě, z nějž klíčí stéblo: pak roste a sílí a vystupuje do výše na stéble svém, jsouc radostí rozsévače. (Takými jsou), aby zuřili na ně nevěřící: těm pak, kdož uvěřili a konali dobré skutky, slíbil Bůh odpustění a odměnu velkou.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Vy, kteříž jste uvěřili, nepředbíhejte v ničem (řízení) Boha a proroka jeho: a bojte se Boha, Bůh zajisté vše slyší a ví.'),
(4614, 49, 2, 'Vy, kteříž jste uvěřili, nepozdvihujte hlasu svého nad hlas prorokův, aniž mluvte k němu tak hlasitě, jako když mluvíte mezi sebou jinak niveč obráceni budou skutkové vaši, aniž byste naděli se.'),
(4615, 49, 3, 'Zajisté ti, kdož ztišují hlasy své v přítomnosti proroka božího, jsou právě oni, jichž srdce vyzkoušel Bůh pro zbožnost: odpuštění očekává je a odměna hojná.'),
(4616, 49, 4, 'Ti pak, kdož hlasitě vyvolávají tě z KOMNAT tvých, většinou jsou lidé nerozumní.'),
(4617, 49, 5, 'Však kdyby pokojně vyčkali, až vyjdeš k nim, bylo by to lepším pro ně: Bůh pak velkým jest v odpouštění, slitovným.'),
(4618, 49, 6, 'Vy, kteříž jste uvěřili, přijde-li k vám prostopášník s novinou, dobře ji prozkoumejte, abyste neublížili lidem z nevědomosti a nelitovali v brzku ukvapenosti své.'),
(4619, 49, 7, 'A věztež, že prorok jest mezi vámi, kdyby poslouchal vás v mnohých záležitostech, upadli byste ve hřích. Však Bůh dal vám zamilovati si víru a zkrášlil vám ji v srdcích vašich: a zošklivil vám nevíru, prostopášnost a neposlušnost. Tací lidé nalézají se na cestě správné,'),
(4620, 49, 8, 'z dobrodějnosti a milosti Boha: Bůh pak vševědoucí jest a moudrý.'),
(4621, 49, 9, 'Válčí-li dva sbory věřících proti sobě, smiřte je: činí-li pak jeden z nich bezpráví druhému, bojujte proti onomu, jenž činí bezpráví, dokud se nenavrátí k (zachování) přikázání božích: a vrátí-li se, smiřte obě strany spravedlivě a nestranně, neboť Bůh zajisté miluje nestranně jednající.'),
(4622, 49, 10, 'Neboť věřící jsou bratry si, pročež smiřujte dva (rozvaděné) bratry své a bojte se Boha, abyste snad slitování jeho dosáhli.'),
(4623, 49, 11, 'Vy, kteříž jste uvěřili, nechť neposmívají se muži mužům, kteří možná lepšími jsou jich: aniž ženy ženám, jež možná lepšími jsou jich. A nepomlouvejte se navzájem, aniž pokřikujte na sebe přezdívkami: jak ošklivé (jest užívati) prostopášných jmen poté, když stali jste se veřícími! Kdožkoli pak nebude káti se, patřiti bude k nepravostným.'),
(4624, 49, 12, 'Vy, kteříž jste uvěřili, vystříhejte se častých podezřívání, neboť někdy podezření jest hříchem: nebuďte vyzvědači, a nepomlouvejte se navzájem. Zdaž bylo by komu z vás příjemno jísti maso mrtvého bratra svého? Zajisté odporným by vám to bylo! Pročež bojte se Boha, neboť Bůh zajisté rád popřává sluchu kajícným a jest slitovný.'),
(4625, 49, 13, 'Ó lidé, zajisté stvořili jsme vás z muže a ženy a rozdělili jsme vás v národy a kmeny, abyste seznamovali se navzájem. Zajisté pak nejvzácnějším z vás před Bohem jest ten, jenž největším jest z vás v bázni boží. V pravdě Bůh vševědoucí jest a o všem zpraven.'),
(4626, 49, 14, 'Řkou Arabové z pouště: „Uvěřili jsme.“ Rci: „Neuvěřili jste, ale rcete spíše: Odevzdali jsme se do vůle boží — neboť víra nevešla dosud v srdce vaše. Však budete-li poslušni Boha a proroka jeho, nedá (Bůh) ničemu ztratiti se ze skutků vašich, neboť Bůh zajisté velkým jest v odpouštění, slitovným.'),
(4627, 49, 15, 'Věřícími jsou pouze ti, kdož uvěřili v Boha a proroka jeho a poté nechovají (nejmenších) pochybností, nýbrž bojují statky i osobami svými na stezce boží: tito jsou opravdovými.“'),
(4628, 49, 16, 'Rci: „Zdaž poučovati chcete Boha o náboženství svém, když Bůh ví vše, což na nebi jest i na zemi, neboť Bůh o všech věcech má vědomost?“'),
(4629, 49, 17, 'Vyčítají ti, že odevzdali se do vůle boží. Rci: „Nevyčítejte mi svého odevzdání se do vůle boží, spíše Bůh vyčítati může vám, že uvedl vás k víře: (doznejte to), jste-li pravdomluvnými.'),
(4630, 49, 18, 'Bůh zajisté zná věci skryté nebes i země: a Bůh (dobře) obzírá konání vaše.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'QÁF. Při Koránu vznešeném!'),
(4632, 50, 2, 'Naopak, diví se, že přišel jim varovatel ze středu jejich; i říkají nevěřící; „Totoť věc jest divná!'),
(4633, 50, 3, 'Že když zemřeme a budeme v prach obráceni? — To věru návrat jest z daleka!“'),
(4634, 50, 4, 'My (dobře) víme, kolik pohltila jich země, neb u nás jest Kniha, (vše) uschovávající.'),
(4635, 50, 5, 'Naopak, lží nazvali pravdu, když přišla jim: i v postavení jsou spleteném.'),
(4636, 50, 6, 'Což nepohlédli k nebesům nad sebou, aby viděli, jak vybudovali jsme je a zkrášlili tak, že není skulin v nich?'),
(4637, 50, 7, 'A zemi rozprostřeli jsme a rozházeli po ní temena hor; a dali vzrůsti na ní všeho druhu (rostlin) rozkošných:'),
(4638, 50, 8, 'k nahlédnutí a napomenuti všem služebníkům kajícným.'),
(4639, 50, 9, 'A seslali jsme s nebe vodu požehnanou, jíž vzrůsti dáváme zahradám a zrní obilnému;'),
(4640, 50, 10, 'a datlovníkům vysokým s vrcholy obtěžkanými,'),
(4641, 50, 11, 'k výživě služebníkům našim a vodou oživujeme zemi zmrtvělou. Takové bude i z mrtvých vzkříšení.'),
(4642, 50, 12, 'Před nimi již lháři zvali proroky: lid Noemův a obyvatelé Ar-Rassu a (kmen) Tsemúd;'),
(4643, 50, 13, 'a (kmen) \'Ád a Farao a bratří Lotovi;'),
(4644, 50, 14, 'a obyvatelé hvozdu a lid Tubba; ti všichni lháři nazvali proroky i uskutečnil se (nad nimi) trest slíbený.'),
(4645, 50, 15, 'Což znaveni jsme prvním stvořením? Naopak: ale oni přec jsou v pochybách ohledně stvoření nového!'),
(4646, 50, 16, 'My stvořili jsme člověka a víme, co našeptává mu duše jeho: a my bližšími jsme mu žíly hrdelné.'),
(4647, 50, 17, 'Když přijímati budou oba přijímači (výpovědi), jeden z prava, druhý z leva sedící:'),
(4648, 50, 18, 'nepronese slova, aniž by pozorovatel vedle něho nebyl pohotově (je zapsati).'),
(4649, 50, 19, 'A přijde (naň) mrákota smrti skutečné: „Toto jest, čemu chtěl ses vyhnouti!“'),
(4650, 50, 20, 'A zatroubeno bude v roh: totoť jest den trestu slíbený!'),
(4651, 50, 21, 'I přijde jedna každá duše: s každou pak honec a svědek její.'),
(4652, 50, 22, '„Žil\'s v lhostejnosti k tomuto dnu: nuž, sejmuli jsme ti clonu a zrak tvůj dnes ostrý jest!“'),
(4653, 50, 23, 'A řekne druh jeho věrný: „Toto jest co mám (pro tebe) připraveno.“'),
(4654, 50, 24, '„Uvrcte, vy dva, v peklo všechny nevěrce vzdorovité:'),
(4655, 50, 25, 'zabraňovače dobra, přestupníky, pochybovače;'),
(4656, 50, 26, 'kteří stavěli vedle Boha bohy druhé: uvrcte je v trest přísný!“'),
(4657, 50, 27, 'Řekne druh jeho věrný: „Pane náš, jáť nesváděl jsem jej, nýbrž on (sám) byl v bloudění dálném.“'),
(4658, 50, 28, '(Řekne Bůh): „Neveďte sporu přede mnou: dal jsem vám již před tím slib výhrůžný:'),
(4659, 50, 29, 'nemění se pak slovo mé: a nejsem utiskovatelem služebníků svých.“'),
(4660, 50, 30, 'V den ten řekneme peklu: „Zdaž naplněno jsi?“ a řekne: „Jest (ještě) více jich?“'),
(4661, 50, 31, 'Přiblížena pak bude k bohabojným zahrada (ráje) nedaleká:'),
(4662, 50, 32, '„Totoť jest, co slíbeno bylo vám, všem kajícným a (zákony) zachovávajícím:'),
(4663, 50, 33, 'těm, kdož báli se milosrdného v skrytosti a přicházejí se srdcem kajícným.'),
(4664, 50, 34, 'Vstupte do ní v pokoji: tentoť jest věčnosti den.“'),
(4665, 50, 35, 'V ní budou míti, cožkoliv chtíti budou a u nás bude (toho) rozhojnění.'),
(4666, 50, 36, 'A kolik zahladili jsme před nimi pokolení, jež mocnejší jich byla silou: a prohledejte zemi, je-li v ní před námi útočiště!'),
(4667, 50, 37, 'Zajisté v tomto jest napomenutí tomu, jenž má srdce a popřává sluchu a (sám) jest svědkem.'),
(4668, 50, 38, 'Stvořili jsme pak nebesa i zemi a vše, což mezi oběma jest v šesti dnech a nedotkla se nás únava.'),
(4669, 50, 39, 'Trpělivě snášej tedy, co říkají a vyvyšuj Pána svého ve chvále jeho před slunce východem a západem.'),
(4670, 50, 40, 'A za noci též chval jej a v posledních dvou na tvář padnutích.'),
(4671, 50, 41, 'A přislouchej ke dnu, kdy zavolá provolavatel z místa blízkého.'),
(4672, 50, 42, 'V den, kdy zaslechnou zavřesknutí v pravdě, toť den bude vystoupení (z hrobů):'),
(4673, 50, 43, 'Myť zajisté křísíme i umrtvujeme a k nám vede (cesta) návratu.'),
(4674, 50, 44, 'V den, kdy rozpoltí se země nad nimi — toť shromáždění bude pro nás snadné.'),
(4675, 50, 45, 'My nejlépe víme, co říkají a ty nejsi nad nimi násilníkem. Tudíž napomínej Koránem ty, kdož bojí se výhrůžných slibů mých.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Při ROZPTYLUJICÍCH rozptylováním'),
(4677, 51, 2, 'a břímě nesoucích;'),
(4678, 51, 3, 'a lehce plynoucích'),
(4679, 51, 4, 'a dle rozkazu rozdělujících,'),
(4680, 51, 5, '(přisahám, že) co slibováno jest vám, zajisté jest pravdivo:'),
(4681, 51, 6, 'a že (den) súčtování přijde nezvratně.'),
(4682, 51, 7, 'Při nebi plném (hvězdných) drah,'),
(4683, 51, 8, 'věru řeč vedete rozličnou:'),
(4684, 51, 9, 'odvrácen od ní bude ten, jenž byl odvrácen.'),
(4685, 51, 10, 'Nechť pobiti jsou lháři,'),
(4686, 51, 11, 'kdož v hloubi (nevědomosti) své jsou lhostejni,'),
(4687, 51, 12, 'a táží se: „Kdy přijde ten den súčtování?“'),
(4688, 51, 13, 'V den ten na ohni budou zkoušeni:'),
(4689, 51, 14, '„Okuste této zkoušky své, již uspíšiti chtěli jste!“'),
(4690, 51, 15, 'Však bohabojní v zahradách budou a u pramenů.'),
(4691, 51, 16, 'při-jímajíce to, co dal jim Pán jejich za to, že předtím dobře činili:'),
(4692, 51, 17, 'jen málo v noci spávali:'),
(4693, 51, 18, 'z jitra za odpuštění prosili:'),
(4694, 51, 19, 'ze statků svých patřičný podíl prosícím a odvrženým dávali.'),
(4695, 51, 20, 'Na zemi znamení jsou pevně věřícím,'),
(4696, 51, 21, 'i ve vás samých: což neprohlédnete?'),
(4697, 51, 22, 'A na nebi výživa jest vaše a to, co slíbeno jest vám;'),
(4698, 51, 23, 'a při Pánu nebes a země přisahám: pravda jest to, jako že mluvíte!'),
(4699, 51, 24, 'Zdaž došla tě pověst o hostech Abrahama poctěných,'),
(4700, 51, 25, 'když přišli k němu a řekli: „Pokoj tobě,“ a řekl: „Pokoj vám! — Jsou lidé to neznámí.“'),
(4701, 51, 26, 'I poodešel k rodině své a přinesl tele krmené:'),
(4702, 51, 27, 'a předložil jim je, řka: „Zdaž nepojíte snad?“'),
(4703, 51, 28, 'A zachvěl se před nimi bázní; oni pak řekli: „Neboj se“ a oznámili mu narození mládce moudrého.'),
(4704, 51, 29, 'I přišla žena jeho; vzkřikla a udeřivši se v tvář svou, řekla: „Stařena (jsem již) neplodná.“'),
(4705, 51, 30, 'Řekli, „Tak řekl Pán tvůj; on zajisté vševědoucí jest a moudrý.“'),
(4706, 51, 31, 'Řekl (Abraham): „A jaký jest cíl váš, ó poslové?“'),
(4707, 51, 32, 'Řekli: „Zajistéť posláni jsme k lidu provinilému,'),
(4708, 51, 33, 'abychom seslali naň kamení z hlíny (pálené),'),
(4709, 51, 34, 'u Pána tvého jmény výstředníků znamenané.“'),
(4710, 51, 35, 'A vyvedli jsme ven z města věřící:'),
(4711, 51, 36, 'však nalezli jsme v něm jen jeden dům, kde (lidé) odevzdáni byli do vůle boží;'),
(4712, 51, 37, 'a zanechali jsme v něm znamení pro ty, kdož bojí se trestu bolestného.'),
(4713, 51, 38, 'A (též) v Mojžíšovi, když poslali jsme jej k Faraonovi s mocí zjevnou:'),
(4714, 51, 39, 'však odvrátil se on i přední lidu jeho, řka: „Kouzelník to, neb (duchem) posedlý!“'),
(4715, 51, 40, 'I zachvátili jsme jej i vojska jeho a vmetli jsme je v moře; neb byl hoden pohany.'),
(4716, 51, 41, 'A též v (lidu) \'Ád, když poslali jsme naň vichr ničivý,'),
(4717, 51, 42, 'jenž neponechal ničeho v cestě své, aniž by nebyl v prach to obrátil.'),
(4718, 51, 43, 'A v (lidu) Tsemúd, když řečeno mu bylo: „Těšte se z požitků svých dočasně!“'),
(4719, 51, 44, 'Však protivili se rozkazu Pána svého a zachvátil je vichr bouřný, ani naň hleděli.'),
(4720, 51, 45, 'A nebyli s to, udržeti se v stoje, aniž si pomoci.'),
(4721, 51, 46, 'A v (lidu) Noemově, před nimi, neb lidem byl prostopášným.'),
(4722, 51, 47, 'A nebesa zbudovali jsme vlastnoručně a dali velký rozsah (jim):'),
(4723, 51, 48, 'a zemi (jak koberec) jsme rozprostřeli: jak krásně byla námi rozložena!'),
(4724, 51, 49, 'A všechny věci stvořili jsme v párech, abyste si toho byli pamětlivi.'),
(4725, 51, 50, 'Utíkejte se tedy k Bohu: jáť pak zajisté jsem vám od něho varovatelem zjevným!'),
(4726, 51, 51, 'A nečiňte (si) s Bohem bohů jiných: jáť zajisté jsem vám od něho varovatelem zjevným!'),
(4727, 51, 52, 'Stejně tak, kdykoli přišel k těm, již před nimi byli, prorok, řekli jen: „Kouzelník to, neb (duchem) posedlý.“'),
(4728, 51, 53, 'Dávají si to navzájem dědictvím? Ano, lid jest to svévolný.'),
(4729, 51, 54, 'I odvrať se od nich, nebude ti to zazlíváno:'),
(4730, 51, 55, 'však napomínej, neb zajisté napomenutí prospěje věřícím.'),
(4731, 51, 56, 'A stvořil jsem Džinny a lidi jen proto, aby mne uctívali.'),
(4732, 51, 57, 'Nežádám si od nich výživy, aniž žádám si, aby mně jídlo dávali.'),
(4733, 51, 58, 'Zajisté Bůh jest Živitel silný, pevný!'),
(4734, 51, 59, 'Nepravostným pak zajisté připadne stejný úděl, jaký připadl rovným jim předtím: nechť tedy na mne nepospíchají!'),
(4735, 51, 60, 'A běda těm, kdož neuvěřili, přede dnem soudu jejich, jenž slíben jest jim!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Při HOŘE,'),
(4737, 52, 2, 'při Knize napsané'),
(4738, 52, 3, 'na svitku rozbaleném;'),
(4739, 52, 4, 'při domu (četně) navštíveném;'),
(4740, 52, 5, 'při (klenbě) střechy pozdvižené,'),
(4741, 52, 6, 'při moři rozbouřeném;'),
(4742, 52, 7, '(přisahám, že) trest Pána tvého jest nezvratný:'),
(4743, 52, 8, 'a není, kdo by jej zadržel.'),
(4744, 52, 9, 'V den, kdy valiti se budou nebesa valem'),
(4745, 52, 10, 'a hýbati se budou hory hybem,'),
(4746, 52, 11, 'běda v den onen těm, kdož (proroka) lhářem zvali,'),
(4747, 52, 12, 'kdož si v planém žvastu hráli!'),
(4748, 52, 13, 'V den ten vrženi budou, v oheň pekla vrhem:'),
(4749, 52, 14, '„Totoť oheň jest, jejž vylhaným jste zvali!'),
(4750, 52, 15, 'Jest kouzlem to, či nevidíte?'),
(4751, 52, 16, 'Pecte se v něm: ať trpělivě snášeti jej budete, či nikoli, jedno bude to pro vás: tak od-měněni jen budete za skutky své.“'),
(4752, 52, 17, 'Však bohabojní budou v zahradách (ráje) a v rozkoš;'),
(4753, 52, 18, 'radujíce se z toho, co dal jim Pán jejich: a z toho, že spasil je od muk plamene pekelného.'),
(4754, 52, 19, '„Pojídejte a po-píjejte ve zdraví, v odměnu za skutky své!“'),
(4755, 52, 20, 'Spočívati budou na pohovkách seřazených, a za manželky dáme jim děvy velkých očí černých,'),
(4756, 52, 21, 'Ti, kdož uvěřili, a jež následovalo potomstvo jejich ve víře, spojíme s potomstvem jejich: a neošidíme je o nic ze skutků jejich. Každý člověk jest rukojmím za to, čeho dobyl si.'),
(4757, 52, 22, 'A v hojnosti dáme jim z ovoce i masa, cokoliv přáti si budou.'),
(4758, 52, 23, 'Podávati si tam budou pohár, z nějž nepojde řeč svévolná, ni k hříchu příčina.'),
(4759, 52, 24, 'Obcházeti budou je mládci-číšníci, jak perly skryté (krásní).'),
(4760, 52, 25, 'I obrátí se k sobě se vzájemnými dotazy,'),
(4761, 52, 26, 'řkouce: „Zajisté byli jsme kdys o rodiny své plni starostí;'),
(4762, 52, 27, 'však popřál nám Bůh dobrodiní svého a spasil nás od muk větru žárného:'),
(4763, 52, 28, 'neb zajisté jsme jej předtím vzývali: onť pak dobrodějný jest a slitovný.“'),
(4764, 52, 29, 'Pročež napomínej, neb nejsi, z milosti Pána svého, ni hadačem, ni (duchem) posedlým.'),
(4765, 52, 30, 'Či říkají: „Básník jest to; dávejme pozor naň, jak osud jeho změní se.“'),
(4766, 52, 31, 'Rci: „Dávejte jen pozor: i já s vámi pozor budu dávati!“'),
(4767, 52, 32, 'Či nabádají je k tomuto (jednání) sny jich, anebo lidem jsou svévolným?'),
(4768, 52, 33, 'Či říkají: „Podvrhl jej?“ Naopak, však oni nevěří.'),
(4769, 52, 34, 'Nechť tedy předvedou vyprávění jemu podobné, jsou-li pravdomluvnými!'),
(4770, 52, 35, 'Či stvořeni byli z ničeho — anebo sami jsou stvořiteli?'),
(4771, 52, 36, 'Či oni stvořili nebe a zemi? Naopak, ale pevné víry nemají.'),
(4772, 52, 37, 'Či mají snad (v moci) pokladnice Pána tvého, aneb vrchními jsou rozhodčími?'),
(4773, 52, 38, 'Či mají snad žebřík, pomocí jehož mohli by přislouchati? Nechť ten, jenž přislouchal tak, předvede moc zjevnou!'),
(4774, 52, 39, 'Či Bůh má dcery a vy syny?'),
(4775, 52, 40, 'Či žádáš si odměny, když oni dluhy jsou obtěžkáni?'),
(4776, 52, 41, 'Či znají věci skryté tak, aby je mohli spisovati?'),
(4777, 52, 42, 'Či chtějí lest ti strojiti? Však ti, kdož neuvěřili, sami budou obelstěni!'),
(4778, 52, 43, 'Či mají jiného boha, mimo Boha? Chválen budiž Bůh nad to, co s ním spolčují!'),
(4779, 52, 44, 'A kdyby viděli kus nebe padati na sebe, řekli by: „Toť mračno zhuštěné.“'),
(4780, 52, 45, 'I ponech je až do chvíle, kdy setkají se se dnem (soudu) svého, jímž postiženi budou:'),
(4781, 52, 46, 'dnem, kdy neprospěje jim lest jejich pranic a nebude jim pomoženo.'),
(4782, 52, 47, 'Zajisté těm, kdož nepravostni byli, připadnou ještě jiné tresty vedle toho: však většina jich není si toho vědoma.'),
(4783, 52, 48, 'Vyčkávej tedy trpělivě rozsudek Pána svého, neb tys pod dozorem naším; a vyvyšuj Pána svého ve chvále jeho, když vstáváš:'),
(4784, 52, 49, 'a za noci pěj chválu jeho a při odchodu hvězd!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Při HVĚZDĚ mizící!'),
(4786, 53, 2, 'Není druh váš v bludu, aniž byl zváben:'),
(4787, 53, 3, 'aniž mluví dle choutky své!'),
(4788, 53, 4, '(Korán) jest pouze vnuknutí mu vnuknuté,'),
(4789, 53, 5, 'jemuž naučil jej silou přemocný,'),
(4790, 53, 6, 'statnosti plný: onť v výši vznášel se,'),
(4791, 53, 7, 'v obzoru nejvyšším:'),
(4792, 53, 8, 'pak níže snesl se a přiblížil,'),
(4793, 53, 9, 'na dosah luků dvou, neb blíže snad:'),
(4794, 53, 10, 'a vnuknul služebníku Jeho, co vnuknul mu.'),
(4795, 53, 11, 'Mysl pak nelhala ohledně toho, co byl viděl.'),
(4796, 53, 12, 'Zdaž pochybnosti vyjádříte ohledně toho, co byl viděl?'),
(4797, 53, 13, 'Vždyť viděl jej již při sestoupení jiném,'),
(4798, 53, 14, 'u sidru, konec značícího,'),
(4799, 53, 15, 'ráje příbytku (věčného).'),
(4800, 53, 16, 'Strom když pak krylo to, co krylo jej,'),
(4801, 53, 17, 'nevzdálil se zrak, (od něho), aniž zbloudil:'),
(4802, 53, 18, 'a viděl tehdy ze znamení Pána svého to největší.'),
(4803, 53, 19, 'Co míníte o Allát a Al-\'Uzzá'),
(4804, 53, 20, 'a Manát, té třetí další?'),
(4805, 53, 21, 'Vy že syny měli byste, On pak dcery?'),
(4806, 53, 22, 'Rozdělení také by bylo nesprávné!'),
(4807, 53, 23, 'Ony jsou jména jen, jimiž nazvali jste (je) vy a otcové vaši: neseslal Bůh ohledně jich zmocnění! Následují pouze domněnky a vlastní choutky své, ač přišlo jim od Pána jejich (správné) Vedení.'),
(4808, 53, 24, 'Či má člověk míti (vše), co přeje si?'),
(4809, 53, 25, 'Bohu pak patří život budoucí i přítomný:'),
(4810, 53, 26, 'a kolik andělů jest v nebesích, jichž neprospěje pranic přímluva, leda poté, když dovolil byl Bůh, komu byl chtěl, a zlíbilo se mu!'),
(4811, 53, 27, 'Zajisté ti, kdož nevěří v život budoucí, jmenují anděly pojmenováními žen:'),
(4812, 53, 28, 'však nemají v tom nijakého vědění, nýbrž následují pouze domněnku: domněnka pak nemá vůči pravdě významu pražádného.'),
(4813, 53, 29, 'Vzdal se tudíž toho, jenž odvrací se od vzpomínání na nás a žádá si pouze života pozemského.'),
(4814, 53, 30, 'Takový jest dosah jejich vědění; však zajisté Pán tvůj nejlépe ví o tom, kdo zbloudil s cesty Jeho a nejlépe, ví o tom, kdo veden jest (cestou pravou).'),
(4815, 53, 31, 'Bohu pak přináleží vše, což na nebi jest a na zemi, aby odměniti mohl ty, kdož konají špatnosti, dle skutků jejich, a odměnil ty, kdož dobro konali (odměnou) krásnou;'),
(4816, 53, 32, 'ty, kdož straní se velkých hříchů i smilstev a dopouštějí se jen lehkých přestupků: zajisté Bůh bude (vůči nim) nesmírným v odpouštění. On nejlépe zná vás, když vytvořil vás ze země a když byli jste zárodky v lůnech matek svých; počež nesnažte se, abyste očistili se před ním: onť nejlépe ví, kdo bojí se jej.'),
(4817, 53, 33, 'Co míníš o tom, jenž odvrací se'),
(4818, 53, 34, 'a dává jen málo a skrblí?'),
(4819, 53, 35, 'Zdaž (ten) má vědomost o věcech skrytých, takže vidí (je)?'),
(4820, 53, 36, 'Či nebyl zpraven o tom, co na stránkách (knih) jest Mojžíše'),
(4821, 53, 37, 'i Abrahama, věrného (slovu svému)'),
(4822, 53, 38, 'Že nebude obtěžkána duše břemenem duše druhé;'),
(4823, 53, 39, 'a že nepřipadne člověku, leda (výsledek) práce (jeho):'),
(4824, 53, 40, 'a zajisté práce (jeho dobře) bude prohlédnuta;'),
(4825, 53, 41, 'pak odměněn bude odměnou nejvýš přesnou:'),
(4826, 53, 42, 'že u Pána tvého jest konec (věcí všechněch).'),
(4827, 53, 43, 'Onť zajisté dává lidem smáti se a plakati:'),
(4828, 53, 44, 'onť oživuje a umrtvuje:'),
(4829, 53, 45, 'onť zajisté stvořil dvé pohlaví, mužské a ženské,'),
(4830, 53, 46, 'ze (semene) kapky vystříknuté;'),
(4831, 53, 47, 'a že zajisté na něm jest stvoření druhé:'),
(4832, 53, 48, 'onť obohacuje a dává získati,'),
(4833, 53, 49, 'a zajisté jest Pánem Siria.'),
(4834, 53, 50, 'Onť zahubil jest kmen \'Ád bývalý;'),
(4835, 53, 51, 'a Tsemúd, nezanechav (žádného);'),
(4836, 53, 52, 'Noemův lid předtím, neb nejvýš nepravostným lidem byli, svévolným;'),
(4837, 53, 53, 'a (města) vyvrácená pobořil;'),
(4838, 53, 54, 'a pokrylo je to, co pokrylo je.'),
(4839, 53, 55, 'A o kterém dobrodiní Pána svého budeš pochybovati?'),
(4840, 53, 56, 'Totoť varovatel jest, podobný varovatelům dřívějším!'),
(4841, 53, 57, 'Přibližuje se (hodina), jež přibližuje se:'),
(4842, 53, 58, 'není nikoho, mimo Boha, jenž mohl by ji odvrátiti.'),
(4843, 53, 59, 'Zdaž tomuto rozhovoru divíte se?'),
(4844, 53, 60, 'A smějete se (jen) a nepláčete?'),
(4845, 53, 61, 'A pouze bavíte se?'),
(4846, 53, 62, 'Na tvář svou spíš padněte před Bohem a (jej) uctívejte!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Přiblížila se hodina a MĚSÍC rozpoltil se:'),
(4848, 54, 2, 'však kdykoli vidí znamení, odvrátí se a řeknou, „Totoť jest kouzelnictví ustavičné!“'),
(4849, 54, 3, 'A vylhanými nazvali (znamení toto) a následují vlastní choutky své: však všechno pevně předurčeno jest.'),
(4850, 54, 4, 'Však již přišly jim zprávy o dějích, z nichž možno míti výstrahu;'),
(4851, 54, 5, 'moudrost to dokonalá: však nic platno jim není varování.'),
(4852, 54, 6, 'Odvrať se tedy od nich! V den, kdy zváti bude Zvoucí k věci příšerné,'),
(4853, 54, 7, 'se sklopenými zraky vyjdou z hrobů svých jak kobylky rozptýlené,'),
(4854, 54, 8, 'spějíce ke Zvoucímu! I řeknou nevěřící: „Totoť den jest přetěžký.“'),
(4855, 54, 9, 'Před nimi již lid Noemův vylhanou nazval (pravdu) a lhárem nazval služebníka našeho; řekli: „Blázen!“ a byl zastrašen.'),
(4856, 54, 10, 'I volal k Pánu svému: „Přemožen jsem; ó pomoz (mi)!“'),
(4857, 54, 11, 'Tu otevřeli jsme brány nebes vodě proudící:'),
(4858, 54, 12, 'a dali vyvřít ze země (přehojným) pramenům; i setkaly se (tyto) vody dle rozkazu předurčeného.'),
(4859, 54, 13, 'Jej pak nesli jsme na (arše) z prken a hřebíků.'),
(4860, 54, 14, 'Před naším zrakem plula, odměnou, tomu, jenž s nevírou přijat byl.'),
(4861, 54, 15, 'A jako znamení jsme ji ponechali: což není, kdo pamětliv by toho byl?'),
(4862, 54, 16, 'A jaký byl trest můj a varování mé!'),
(4863, 54, 17, 'Snadným pak učinili jsme Korán (k pochopení) napomenutí: což není, kdo pamětliv by toho byl?'),
(4864, 54, 18, 'Lhářem nazval (kmen) \'Ád (proroka svého): a jaký byl trest můj a varování mé!'),
(4865, 54, 19, 'Poslali jsme na ně vichr bouřný v den strasti ustavičné.'),
(4866, 54, 20, 'Odnášel lidi jako pařezy datlovníků vyvrácené:'),
(4867, 54, 21, 'a jaký byl trest můj a varování mé!'),
(4868, 54, 22, 'Snadným pak učinili jsme Korán (k pochopení) napomenutí: což není kdo pamětliv by toho byl?'),
(4869, 54, 23, 'Lhářem nazval též (kmen) Tsemúd varovatele,'),
(4870, 54, 24, 'řkouce: „Zdaž následovati budeme jednoho muže z nás? Tehdy jistě byli bychom v bludu a třeštění!'),
(4871, 54, 25, 'Jemu že výhradně dáno bylo napomenutí mezi námi? Kde pak! Toť lhář jest drzounský!“'),
(4872, 54, 26, '„Z jitra však zvědí, kdo lhář a drzoun byl.'),
(4873, 54, 27, 'Neb pošleme jim velbloudici na vyzkoušení jich: dobře je pozopruj a měj strpení;'),
(4874, 54, 28, 'a oznam jim, že voda rozdělena bude. mezi nimi a jí; každé napití budiž střídavé.“'),
(4875, 54, 29, 'Však zavolali na druha svého: (nůž) vytasil, žíly přeřízl jí.'),
(4876, 54, 30, 'A jaký byl trest můj a varování mé!'),
(4877, 54, 31, 'Poslali jsme na ně jen jedno vzkřiknutí a byla z nich suchá stébla s hlínou smísená.'),
(4878, 54, 32, 'Snadným pak učinili jsme Korán (k pochopení) napomenutí: což není, kdo pamětliv by toho byl?'),
(4879, 54, 33, 'Lhářem též nazval lid Lotův varovatele.'),
(4880, 54, 34, 'I poslali jsme na ně vichr bouřlivý, vyjma rodiny Lotovy: je zachránili jsme za zory,'),
(4881, 54, 35, 'z milosti své: takto odměňujeme ty, kdož vděčni jsou.'),
(4882, 54, 36, 'On pak varoval je před urputností naší; však pochybovali o varování (tom).'),
(4883, 54, 37, 'Z vášně své Pak žádali na něm hosty jeho, i vytloukli jsme jim oči: „Okuste trestu mého a varování!“'),
(4884, 54, 38, 'Z jitra pak časně stihl je trest můj stanovený:'),
(4885, 54, 39, '„Okuste trestu mého a varování!“'),
(4886, 54, 40, 'Snadným pak učinili jsme Korán (k pochopení) napomenutí: což není, kdo pamětliv by toho byl?'),
(4887, 54, 41, 'Přišli kdys k rodu Faraonovu varovatelé.'),
(4888, 54, 42, 'Vylhanými pak nazývali znamení naše veškerá a zachvátili jsme je, jakž zachvacuje Mocný, Všemocný.'),
(4889, 54, 43, 'Zdaž nevěrci vaši lepsími jsou těchto, či zproštění jest pro vás v Písmech snad?'),
(4890, 54, 44, 'Či řeknou snad: „My (velké) spolčení jsme, (navzájem) si pomáhající?'),
(4891, 54, 45, 'Poražen bude spolek jich a na útěk se obrátí.'),
(4892, 54, 46, 'Vskutku: hodina (soudu) bude časem schůzky jejich; hodina trapná, přetrpká.'),
(4893, 54, 47, 'Provinilci pak v bludu jsou a třeštění.'),
(4894, 54, 48, 'V den, kdy vláčeni budou do ohně na tvářích svých, řečeno bude jim: „Okuste pekla doteku!“'),
(4895, 54, 49, 'Zajisté pak vše stvořili jsme dle určení.'),
(4896, 54, 50, 'A rozkaz náš jest pouze slovo jediné, jak oka mžiknutí!'),
(4897, 54, 51, 'Kdys zahladili jsme vám podobné: což není, kdo pamětliv by toho byl?'),
(4898, 54, 52, 'A vše, co oni činí, zapsáno v knihách jest:'),
(4899, 54, 53, '(čin) malý ať či velký, dobře jest zanesen.'),
(4900, 54, 54, 'Zajisté pak bohabojní budou v zahradách a poblíž řek:'),
(4901, 54, 55, 'v sídle (to) pravdy, u Krále všemocného.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Všemilosrdným (byl),'),
(4903, 55, 2, 'Koránu naučil,'),
(4904, 55, 3, 'člověka utvořil,'),
(4905, 55, 4, 'jasné mluvě jej naučil;'),
(4906, 55, 5, 'slunce i měsíc dle určených jdou chvil,'),
(4907, 55, 6, '(úctou) rostlina i strom k zemi se poklonil:'),
(4908, 55, 7, 'a nebe Bůh k výsotám pozdvihl a váhu stanovil,'),
(4909, 55, 8, 'by nikdo z vás svévolně ji neměnil.'),
(4910, 55, 9, 'Pročež spravedlivou dávejte váhu a nezkracujte ji.'),
(4911, 55, 10, 'A zemi obydlím různým národům připravil:'),
(4912, 55, 11, 'na ní ovoce a datle rostou, slupkou okryté'),
(4913, 55, 12, 'a obilí na stéblech svých a myrty vonné.'),
(4914, 55, 13, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4915, 55, 14, 'Člověka z hlíny, podobné hrnčířské, utvořil:'),
(4916, 55, 15, 'a Džinny z ohně čistého, bez kouře, utvořil.'),
(4917, 55, 16, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4918, 55, 17, 'Onť Pánem jest obou východů a Pánem obou západů!'),
(4919, 55, 18, 'Nuž, které z- dobrodiní Pána svého chcete zapírati?'),
(4920, 55, 19, 'Rozlil (po zemi) dvé moří, jež -navzájem se dotýkají,'),
(4921, 55, 20, 'však mezi nimi přehrada jest, již nepřetékají.'),
(4922, 55, 21, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4923, 55, 22, 'Z nich perly a korále vycházejí:'),
(4924, 55, 23, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4925, 55, 24, 'A jeho, jsou koráby, plující vztýčené mořem, horám podobné :'),
(4926, 55, 25, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4927, 55, 26, 'Všichni, kdož na zemi jsou, zajdou,'),
(4928, 55, 27, 'však tvář Pána tvého zůstane, ve velikosti a slávě své:'),
(4929, 55, 28, 'nuž, které z dobrodiní Pána svého chcete zapírati'),
(4930, 55, 29, 'Vše obrací se naň s prosbou, což na zemi jest i na nebi: a každého dne v novém jest díle:'),
(4931, 55, 30, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4932, 55, 31, 'Najdeme čas volný pro vás, obě skupiny!'),
(4933, 55, 32, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4934, 55, 33, 'Ó shromáždění Džinnů a lidí, jste-li s to, abyste přestoupili obvody nebes a země, přestupte je! Však nepřestoupíte jich, leda se zmocněním (Boha).'),
(4935, 55, 34, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4936, 55, 35, 'Seslán bude na vás déšť ohnivý a měď roztavená a ne-budete míti obrany.'),
(4937, 55, 36, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4938, 55, 37, 'Tehdy, když nebesa rozstoupnou se a zrůžoví, jak kůže rudě zbarvená:'),
(4939, 55, 38, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4940, 55, 39, 'V den onen nebudou tázáni ni lidé, ni Džinnové na hříchy své.'),
(4941, 55, 40, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4942, 55, 41, 'Po-znáni budou provinilci po známkách svých a uchopeni budou za kštice a nohy své.'),
(4943, 55, 42, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4944, 55, 43, '„Totoť jest peklo, jež vylhaným nazývají provinilci!“'),
(4945, 55, 44, 'Krou-žiti budou semo tamo, mezi plameny jeho, a vodou vroucí.'),
(4946, 55, 45, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4947, 55, 46, 'Však pro ty, již bojí se postavení Pána svého, bude zahrad dvé:'),
(4948, 55, 47, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4949, 55, 48, 'V obou větvoví (rozložité):'),
(4950, 55, 49, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4951, 55, 50, 'V obou pramenů dvé:'),
(4952, 55, 51, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4953, 55, 52, 'V obou ze všeho ovoce druhů dvé:'),
(4954, 55, 53, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4955, 55, 54, 'Tam odpočívati budou na lůžkách, zevnitř brokátem pod-šitých, a (zralé) plody obou zahrad budou v jich dosahu:'),
(4956, 55, 55, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4957, 55, 56, 'Tam panny budou, cudných pohledů, nikdy před tím ni mužem, ni Džinnem dotčené:'),
(4958, 55, 57, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4959, 55, 58, 'Hyacintu a korálům podobné:'),
(4960, 55, 59, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4961, 55, 60, 'Zdaž odměna dobra co jiného jest, než dobro?'),
(4962, 55, 61, 'Nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4963, 55, 62, 'A mimo těchto dvou, jest ještě zahrad dvé:'),
(4964, 55, 63, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4965, 55, 64, 'zelení pokryté,'),
(4966, 55, 65, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4967, 55, 66, 'v obou (z nich) pramenů vyvírajích dvé:'),
(4968, 55, 67, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4969, 55, 68, 'V nich ovoce bude a granátová jablka a palmy datlové:'),
(4970, 55, 69, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4971, 55, 70, 'V nich (dívky) budou krásné a rozkošné:'),
(4972, 55, 71, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4973, 55, 72, 'Děvy velkých, černých očí, ve stanech střežené:'),
(4974, 55, 73, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4975, 55, 74, 'nikdy mužem, ni Džinnem dotčené:'),
(4976, 55, 75, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4977, 55, 76, 'Spočívati budou na polštářích zelených a ložích krásných (jich manželé):'),
(4978, 55, 77, 'nuž, které z dobrodiní Pána svého chcete zapírati?'),
(4979, 55, 78, 'Požehnáno budiž jméno Pána tvého, jenž pln veleby jest a slávy (své)!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Když (nezvratná) udá se UDÁLOST,'),
(4981, 56, 2, 'nebude (duše), jež prohlašovala by udání se její za vylhané:'),
(4982, 56, 3, 'poníží, povýší;'),
(4983, 56, 4, 'když třásti se bude země třesením:'),
(4984, 56, 5, 'a řítiti se budou hory zřícením'),
(4985, 56, 6, 'a obráceny budou v mušky rozptýlené:'),
(4986, 56, 7, 'a rozděleni budete ve druhů tré.'),
(4987, 56, 8, 'Ti, kdož na pravé budou straně — jak sťastni budou (ti na pravé straně)!'),
(4988, 56, 9, 'a ti, již na levé budou straně — jak nešťastni budou (ti na levé straně)!'),
(4989, 56, 10, 'A první zůstanou prvními,'),
(4990, 56, 11, 'a tito nejblíže budou Bohu'),
(4991, 56, 12, 'v zahradách rozkoše:'),
(4992, 56, 13, 'zástup ze starých,'),
(4993, 56, 14, 'a málo z těch, po nich přislých.'),
(4994, 56, 15, 'bude na lůžkách vykládaných'),
(4995, 56, 16, 'spočívat, jedni proti druhým.'),
(4996, 56, 17, 'Obcházeti je budou jinoši věčně mladí,'),
(4997, 56, 18, 's poháry, konvicemi a číšemi jasného nápoje:'),
(4998, 56, 19, 'nerezbolí je hlava z něho, aniž opojeni budou.'),
(4999, 56, 20, 'A ovoce na výběr (dle chuti jich) :'),
(5000, 56, 21, 'a maso ptáků dle přání jich:'),
(5001, 56, 22, 'a děvy velkých zraků tmavých,'),
(5002, 56, 23, 'v podobě perel ukrytých:'),
(5003, 56, 24, 'odměnou to za skutky jich.'),
(5004, 56, 25, 'Nebudou slyšeti tam hovorů pustých, ani hříšných.'),
(5005, 56, 26, 'jen říkání: „Pokoj (vám), pokoj!“'),
(5006, 56, 27, 'A ti, kdož budou na pravé straně — jak šťastni budou (ti na pravé straně)!'),
(5007, 56, 28, 'Mezi sidry bezostými,'),
(5008, 56, 29, 'a talhy plody pokrytými,'),
(5009, 56, 30, 've stínu rozlehlém,'),
(5010, 56, 31, 'u vody tekoucí,'),
(5011, 56, 32, 'u plodů přečetných,'),
(5012, 56, 33, 'nevyčerpatelných, nezapovězených:'),
(5013, 56, 34, 'na ložích pozdvižených.'),
(5014, 56, 35, '(Děvy) ony stvořili jsme soustavou (zvláštní)'),
(5015, 56, 36, 'a učinili jsme je pannami,'),
(5016, 56, 37, 'milými, rovnověkými,'),
(5017, 56, 38, 'pro ty, kdož na pravé budou straně:'),
(5018, 56, 39, '(četný to) zástup ze starých'),
(5019, 56, 40, 'a málo z těch, po nich přišlých.'),
(5020, 56, 41, 'Však ti, kdož na levé budou straně — jak nešťastni budou ti na levé straně!'),
(5021, 56, 42, 'Ve vichru žhavém a vodě vroucí,'),
(5022, 56, 43, 'a ve stínu smolného kouře,'),
(5023, 56, 44, 'ni chladném, ni příjemném.'),
(5024, 56, 45, 'Předtím žili životem bohatým,'),
(5025, 56, 46, 'a setrvávali v odporu velikém,'),
(5026, 56, 47, 'a říkali: „Zdaž až zemřeme,'),
(5027, 56, 48, 'změněni v prach a kosti, vzkříšeni budeme?'),
(5028, 56, 49, 'Jakož i otcové naši předešlí?“ Rci: „Zajisté staří i po nich přišlí,'),
(5029, 56, 50, 'shromázděni budou k určené schůzce dne známého:'),
(5030, 56, 51, 'pak vy, zbloudilci a popíratelé,'),
(5031, 56, 52, 'jistotně jísti budete ze Zaqqúmu'),
(5032, 56, 53, 'a naplníte z něho břicha (svá);'),
(5033, 56, 54, 'pak na to pít budete vodu vařící,'),
(5034, 56, 55, 'a pít budete pitím žíznivce:'),
(5035, 56, 56, 'totoť hod jejich bude v den poslední.'),
(5036, 56, 57, 'My stvořili jsme vás: proč víry nepřikládáte?'),
(5037, 56, 58, 'Co míníte ? To, co vyměšujete,'),
(5038, 56, 59, 'zdaž vy jste stvořili, či my jsme toho Stvořitelem?'),
(5039, 56, 60, 'My ustanovili jsme mezi vámi smrt: i není, kdož předešel by nás'),
(5040, 56, 61, 'v tom, abychom zaměnili vás vám podobnými, neb sestavili vás v čem nevíte.'),
(5041, 56, 62, 'Přece znáte stvoření prvé, proč tedy nejste toho pamětlivi?'),
(5042, 56, 63, 'Co míníte? To, co obděláváte,'),
(5043, 56, 64, 'zdaž vy to oséváte, anebo my jsme osévatelem!'),
(5044, 56, 65, 'Kdybychom chtěli, mohli bychom učiniti z toho suchou slámu, takže nepřestali byste se diviti:'),
(5045, 56, 66, '„Zadlužili jsme se'),
(5046, 56, 67, 'a nyní zapovězeno jest nám (kliditi)?“'),
(5047, 56, 68, 'Co míníte o vodě, již pijete?'),
(5048, 56, 69, 'Zdaž vy seslali jste ji s mračen, anebo my ji sesíláme?'),
(5049, 56, 70, 'Kdybychom chtěli, učinili bychom ji hořkou: což nebudete vděčni?'),
(5050, 56, 71, 'Co míníte o ohni, jejž třením vzbuzujete?'),
(5051, 56, 72, 'Zdaž vy stvořili jste strom, (jenž dřevo vám k tomu dává), anebo my jej stvořili?'),
(5052, 56, 73, 'My učinili jsme jej připomenutím a užitečností poutníkům v poušti.'),
(5053, 56, 74, 'Pročež vyvyšuj v chvále jméno Pána svého mocného.'),
(5054, 56, 75, 'A nepřisáhnu při západech hvězd'),
(5055, 56, 76, '— a toť zajisté přísaha jest, kdybyste věděli, velká —'),
(5056, 56, 77, 'že Korán jest tento jistě ctný,'),
(5057, 56, 78, 've knize skryté (napsaný),'),
(5058, 56, 79, 'jejž pouze očištění nechť se dotýkají.'),
(5059, 56, 80, 'Seslání jest to od Pána světů:'),
(5060, 56, 81, 'zdaž tímto hovorem opovrhnete?'),
(5061, 56, 82, 'a učiníte svou potravou nazývání (jej) vylhaným?'),
(5062, 56, 83, 'Proč tedy, když hrdla dostoupí,'),
(5063, 56, 84, 'a vy v té chvíli hledíte,'),
(5064, 56, 85, 'však my bližší jsme vás, ač vy nás nevidíte: —'),
(5065, 56, 86, 'a netřeba-li vám účtu skládati, (jak tvrdíte);'),
(5066, 56, 87, 'navraťte duši jeho (do těla), jste-li pravdomluvnými!'),
(5067, 56, 88, 'Však byl-li by jedním z nejblíže k Bohu stojících,'),
(5068, 56, 89, 'odpočinek a radost bude míti a zahrady rozkoše.'),
(5069, 56, 90, 'A byl-li by z těch, kdož na pravé budou straně (uslyší):'),
(5070, 56, 91, '„Pokoj budiž s tebou“ od těch, kdož na pravé budou straně.'),
(5071, 56, 92, 'A byl-li by z těch, kdož proroky lháři nazývali, jedním ze zbloudilců:'),
(5072, 56, 93, 'hod jeho bude z vody vařící'),
(5073, 56, 94, 'a sžíhání ohněm pekelným.'),
(5074, 56, 95, 'Totoť zajisté jest pravda jistotná:'),
(5075, 56, 96, 'pročež vyvyšuj v chvále jméno Pána svého mocného!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Chvály pěje Bohu vše, což na nebi jest i na zemi a on mocný jest, moudrý.'),
(5077, 57, 2, 'Jemu přináleží panství nebes a země; on oživuje i umrtvuje; a on nade všemi věcmi moc má.'),
(5078, 57, 3, 'Onť počátek jest i konec, zevnějšek i vnitřek; a on o všech věcech má vědomost.'),
(5079, 57, 4, 'Onť jest to, jenž stvořil nebe i zemi v šesti dnech; pak povznesl se na trůn svůj: on ví, co vchází v zemi i co vychází z ní; co přichází dolů s nebe i co stoupá do nebe; on s vámi jest, kdekoliv byste byli: a Bůh dobře pozoruje to, co činíte!'),
(5080, 57, 5, 'Jemu přináleží panství nebes a země: a k Bohu navráceny budou všechny věci.'),
(5081, 57, 6, 'Dává noci vcházeti v den a dnu v noc a dobře ví, co nitra skrývají.'),
(5082, 57, 7, 'Věřte v Boha a v proroka jeho, a rozdávejte z toho, v čem učinil vás Bůh následníky: neb ti, kdož uvěřili z vás a dávali almužnu, obdrzí odměnu velkou.'),
(5083, 57, 8, 'Proč (vlastně) nechtěli byste uvěřiti v Boha a proroka, jenž zve vás, abyste uvěřili v Pána svého, když tento byl (již) přijal smlouvu s vámi — jste-li věřícími?'),
(5084, 57, 9, 'Onť jest to, jenž sesílá na služebníka svého znamení jasná, aby vyvedl vás z temnot na světlo: zajisté Bůh jest k vám laskavým a slitovným!'),
(5085, 57, 10, 'A proč (vlastně) nechtěli byste rozdávati ve stezce boží, když Bohu patří dědictví nebes a země? Nebudou rovnocenni ti z vás, kdož přispěli jměním před vítězstvím a bojovali: onino budou vyšší míti stupen těch, kdož přispěli jměním poté a bojovali. Však oběma slíbil Bůh (odměnu) krásnou a Bůh (dobře) zpraven jest o tom, co činíte.'),
(5086, 57, 11, 'Kdo jest ten, jenž půjčí Bohu půjčku štědrou, aby zdvojnásobil mu ji Bůh a dostalo se mu odměny hojném?'),
(5087, 57, 12, 'V den, kdy spatříš věřící muže a ženy, ano běží světlo jejich před nimi a zprava jich i zleva jich (řečeno bude jim): „Zvěstí radostnou jsou pro vás dnes zahrady, pod nimiž řeky tekou, v nichž přebývati budete věčně!“ Toť blaženost bude velká.'),
(5088, 57, 13, 'V den (ten) říkati budou pokrytci muži i ženy, k těm, kdož uvěřili: „Posečkejte na nás, abychom pochytili trochu ze světla vašeho.“ I řečeno bude (jim): „Vraťte se nazpěť a žádejte tam o světlo.“ I vztýčen bude mezi nimi val, s branou: uvnitř brány bude Milosrdenství, zevnitř pak jí, vpředu, bude Trest.'),
(5089, 57, 14, 'Budou\' volati na ně: „Což nebyli jsme s vámi?“ Řeknou: „Ano, však uvedli jste sebe samy pokušení; a vyčkávali jste (v nerozhodnosti); a byli jste v pochybnostech; a omámily vás žádosti (světa); až přišel ortel boží: a (tak) oklamal vás ohledně Boha klamatel.'),
(5090, 57, 15, 'Pročež v den tento nebude přijato od vás výkupné, aniž od těch, kdož neuvěřili: a obydlím vaším bude oheň (pekelný), jakož i vládcem. Špatný to bude cesty cíl!“'),
(5091, 57, 16, 'Což není již na čase pro ty, kdož uvěřili, aby se ponížila srdce jejich před Napomenutím božím a tím, co byl seslal z pravdy? A aby nebyli jako ti, jimž dána byla Kniha předtím, a jimž prodloužen byl život, ale tak zatvrdila se srdce jejich, a mnozí z nich jsou pohoršlivci.'),
(5092, 57, 17, 'Vězte, že Bůh oživuje zemi zmrtvělou: vždyť již vysvětlili jsme vám znamení (tato), abyste snad pochopili.'),
(5093, 57, 18, 'Zajisté ti, kdož dávali almužnu, muži i ženy, a půjčili Bohu půjčku štědrou, - — zdvojnásobena jim bude a obdrží odměnu hojnou.'),
(5094, 57, 19, 'A ti, kdož uvěřili v Boha a proroky jeho — tito jsou pravdomluvní a svědkové u Pána svého: jim dána bude odměna jejich a světlo jejich. Ti pak, kdož neuvěřili a vylhanými nazvali znamení naše — tito budou obyvateli plamene (pekelného).'),
(5095, 57, 20, 'Vezte, že život pozemský jest pouze hrou a kratochvílí a pozlátkem; předmětem vzájemného vychloubání mezi vámi: a rozmnožování statků a dětí podobno jest dešti, jehož (posílení vzrůstu) rostlin líbí se nevěrcům; pak nadnou a vidíš je sežloutlé a změněné ve strnisko. A v životě budoucím trest (čeká) přísný. aneb odpuštění Boha a zalíbení (jeho): život pak pozemský jest jen (dočasným) požitkem přeludovým.'),
(5096, 57, 21, 'Předstihujte se tudíž v dosažení odpuštění Pána svého a zahrady (ráje), jejíž šíře jest rovna rozsahem nebi a zemi, a jež schystána jest těm, kdož uvěřili v Boha a proroky jeho. Totoť jest milost boží, již dá, komu bude chtíti, neb Bůh jest nesmírným v milosti své.'),
(5097, 57, 22, 'A nijaké neštěstí nestihne zemi, aniž vaše osoby, jež nebylo by zaneseno v Knize, před tím již, než stvořili jsme je; totoť zajisté jest Bohu snadným:'),
(5098, 57, 23, 'abyste netruchlili nad tím, co minulo vás, a neradovali se příliš z toho, čeho dostalo se vám: neboť Bůh nemiluje domýšlivců chlubných,'),
(5099, 57, 24, 'kteří sami lakotí a nabádají lid k lakotě: kdožkoli pak odvrátí se od štědrosti — Bůh zajisté bohatý jest, chvályhodný!'),
(5100, 57, 25, 'Již přece poslali jsme proroky své s jasnými důkazy, a seslali jsme s nimi Knihu a váhu, aby zachovávali lidé poctivost. A seslali jsme ŽELEZO, — v něm sveřepost mocná jest i užitky (různé) pro lidstvo — aby též rozpoznal (Bůh), kdo pomůže jemu a prorokům jeho v skrytosti: Bůh zajisté silný jest a mocný.'),
(5101, 57, 26, 'Již poslali jsme Noema i Abrahama a dali jsme potomstvu obou dar prorocký a Knihu: někteří z nich (správnou) šli cestou, však mnozí z nich páchali pohoršení.'),
(5102, 57, 27, 'Poté dali jsme následovati ve šlépějích jich prorokům svým: a dali jsme (tak) následovati Ježíši, synu Mariinu; a dali jsme mu Evangelium a vložili jsme v srdce těch, kdož následovali ho, laskavost a slitovnost: mnišství pak začali (jako novotu) oni sami. Předepsali jsme jim jedině snahu po zalíbení božím, však oni nezachovali se tu, jak správně měli zachovati se: těm, kdož uvěřili z nich, dali jsme odměnu jejich, však mnozí z nich pohoršení páchali.'),
(5103, 57, 28, 'Vy, kteříž jste uvěřili: bojte se Boha a věřte v proroka jeho! Popřeje vám pak dvou podílů milosrdenství svého a dá vám světlo, v němž kráčeti budete moci a odpustí vám: neb Bůh velkým jest v odpouštění, slitovným:'),
(5104, 57, 29, 'aby věděl lid obdrževší Knihu, že nemají nijaké moci nad milostí boží a že milost svou dává Bůh, komu chce. Neb Bůh velkým jest v milosti své!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Již slyšel Bůh řeč oné, jež NALÉHÁ na tě ohledně manžela svého a žaluje si u Boha: Bůh pak slyšel vzájemný rozhovor váš, neb Bůh vše slyší a obzírá.'),
(5106, 58, 2, 'Ti z vás, kdož zapudí ženy své — ony nejsou matky jejich, neb matky jich jsou jen ty, jež porodily je! — zajisté mluví řeč špatnou a nepravdu: však Bůh promíjející jest, odpouštějící!'),
(5107, 58, 3, 'Těm pak, kdož takto zapuzují ženy své, a pak chtějí vzíti zpět své slovo, vykoupiti bude otroka, dříve než (opět) dotknou se sebe navzájem. Tímto napomenutí dáno jest vám: a Bůh dobře zpraven jest o tom, co činíte.'),
(5108, 58, 4, 'Kdo pak nenajde k tomu prostředků, nechť postí se dvé měsíců po sobě následujících, dříve než dotknou se oba manželé sebe navzájem. A komu nebude lze to, nechť dá najísti šedesáti nuzným. Toto (určeno jest), aby věřili jste v Boha a proroka jeho; tyto jsou meze boží; a nevěřícím (schystán) jest trest bolestný!'),
(5109, 58, 5, 'Zajisté ti, kdož příčí se Bohu a proroku jeho, povaleni budou, jakož povaleni byli ti, kdož byli před nimi; nyní pak již seslali jsme znamení jasná: a nevěřícím (schystán) jest trest potupný'),
(5110, 58, 6, 'v den, kdy vzkřísí Bůh všechny a oznámí jim, co činili: Bůh dobře má to spočteno, ač oni zapomněli to, neb Bůh věcí všechněch svědkem jest.'),
(5111, 58, 7, 'Což neviděl, že Bůh ví vše, co na nebi jest i na zemi? Není tajné rozmluvy mezi třemi, kde on nebyl by čtvrtým z nich; aniž mezi pěti, kde on nebyl by šestým z nich. Aniž jest méně (osob) nad to, neb více, aby On nebyl s nimi, kdekolivěk by nalézaly se. Onť oznámí jim pak, co byly činily, v den zmrtvýchvstání: neb zajisté Bůh o všech věcech má vědomost!'),
(5112, 58, 8, 'Což neviděl\'s ty, jimž zakázány byly tajné rozmluvy, však poté přece vracejí se k tomu, co zakázáno bylo jim a přetřásají mezi mezi sebou hřích, nepřátelství a neposlušnost vůči prorokovi? A když přijdou k tobě, zdraví tě jinak, než jak zdraví tě Bůh a říkají si v duchu: „Proč netresce nás Bůh za to, co říkáme?“ Údělem jejich bude peklo, v němž připékati se budou: a špatný bude to cesty cíl!'),
(5113, 58, 9, 'Vy, kteří jste uvěřili: přetrásáte-li co v hovoru mezi sebou, nehovořte o hříchu, nepřátelství, neposlušnosti vůči prorokovi, nýbrž hovořte o ctnosti a bázni boží: a bojte se Boha, k němuž budete shromážděni.'),
(5114, 58, 10, 'Zajisté pak tajné hovory přicházejí jen od Satana, aby zármutek přivedl na ty, kdož uvěřili: však nebude s to uškoditi vám pranic, leda z dovolení božího; na Boha pak nechť spoléhají věřící.'),
(5115, 58, 11, 'Vy, kteří jste uvěřili, když řečeno jest vám: „Udělejte místo ve shromážděních,“ tedy udělejte místo: a udělá Bůh místo vám! A když řečeno jest: „Rozejděte se,“ tedy se rozejděte. Bůh pak vyvýší ty, kdož uvěrili z vás, a ty, kdož obdrželi vědění, dle stupňů: a Bůh dobře zpraven jest o tom, co činíte!'),
(5116, 58, 12, 'Vy, kteří jste uvěřili, když tajně hovořiti chcete o něčem s prorokem, dejte před tím almužnu. Toť bude lepším pro vás a čistším. Však nenajdete-li (k tomu prostředků) — Bůh zajisté velkým jest v odpouštění, slitovným.'),
(5117, 58, 13, 'Jest vám snad za těžko dáti almužnu dříve, než máte tajný rozhovor s prorokem? Neučiníte-li tak — a Bůh promine vám to — tedy zachovávejte modlitbu a dávejte povinnou almužnu a buďte poslusni Boha a proroka jeho: Bůh pak zajisté dobře zpraven jest o tom, co činíte.'),
(5118, 58, 14, 'Zdaž neviděl\'s ty, kdož obrátili se (o pomoc) k lid, na které rozhněval se Bůh? Nepatří ani k vám, ani k nim, a přisahají lživě s plným toho vědomím.'),
(5119, 58, 15, 'Jim přichystal Bůh trest přísný, neb zajisté špatné jest to, co činí.'),
(5120, 58, 16, 'Vzali si víru svou za plášť a odvracejí jiné od stezky boží: pročes (schystán) jest jim trest potupný.'),
(5121, 58, 17, 'Neprospějí jim statky jejich, aniž děti jejich proti Bohu pranic: tito budou přebývati v ohni (pekelném) a přebývati v něm věčně.'),
(5122, 58, 18, 'Jednoho dne Bůh vzkřísí je všechny, i přisahati budou mu jako přisahají nyní vám, a domnívati budou se, že bude jim to něco platno. Ó zajisté jsou to prolhanci!'),
(5123, 58, 19, 'Opanoval je Satan a způsobil, aby zapomněli napomenutí boží: tito patří ke straně Satanově — ó zajisté strana Satanova záhubě propadne.'),
(5124, 58, 20, 'Ti, kdož protiví se Bohu a proroku jeho, z nejopovrženějších budou!'),
(5125, 58, 21, 'Napsalť Bůh: „Dojista zvítězím já a proroci moji!“ Bůh pak zajisté silný jest a mocný.'),
(5126, 58, 22, 'Nenajdeš nikoho z těch, kdož uvěřili v Boha a v den poslední, že by milovali ty, kdož protiví se Bohu a proroku jeho, i kdyby to byli otcové jejich, neb synové jejich, neb bratří jejich, neb nejbližší soukmenovci jejich. V srdcích těchto zapsal (Bůh) víru a posílil je duchem svým: a uvede je v zahrady. pod nimiž řeky tekou, v nichž přebývati budou věčně. Nalezl Bůh zalíbení v nich a oni nalezli zalíbení v něm. Tito patří ke straně Boha: ó zajisté těm, kdož na straně jsou Boha, dobře povede se!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Chvály pěje Bohu, cožkoliv na nebi jest i na zemi: a on mocný jest a moudrý'),
(5128, 59, 2, 'Onť jest to, jenž vypudil ty, kdož neuvěřili z lidu obdrževšího Knihu, z obydlí jejich k těm, kdož byli v prvním VYSTĚHOVÁNÍ. Neměli jste za to, že vyjdou (z nich) a oni měli za to, že ochrání je tvrze jejich před Bohem; však Bůh přisel na ně zkad nedomnívali se, a vrhl v srdce jejich bázeň, takže rozbíjena byla obydlí jejich rukama jejich i rukama věřících: vezměte si z toho výstrahu lidé prozíraví!'),
(5129, 59, 3, 'A nebýti toho, že Bůh určil jim vyhnanství, byl by potrestal je (jinak) ve světě tomto: a ve světě budoucím jim (schystán) jest trest ohně (pekelného).'),
(5130, 59, 4, 'To proto, že odštěpili se od Boha a proroka jeho: a kdokoliv odštěpí se od Boha — zajistéť Bůh přísným jest v trestání!'),
(5131, 59, 5, 'Cokoliv zutínali jste ze stromů palmových, neb ponechali státi na kořenech jejich, bylo jedině z dovolení božího a aby pohanou pokryti byli pohoršlivci.'),
(5132, 59, 6, 'A cokoliv popřál Bůh kořistí prorokovi z těchto lidí, nenaléhali jste v tom na ně ani koňstvem, ani velbloudy avšak Bůh dává moc prorokům svým nad kýmkoliv chce, neboť Bůh nade všemi věcmi moc má.'),
(5133, 59, 7, 'Cokoliv popřál Bůh kořistí prorokovi z obyvatelů měst, patří Bohu a proroku jeho, a příbuzným a sirotkům a nuzným a pocestným, tak aby nedostalo se to do oběhu mezi bohaté z vás: a cokoliv dal vám prorok, vezměte; a cokoliv odepřel vám, přestaňte s tím; a bojte se Boha, neb Bůh přísným jest v trestání.'),
(5134, 59, 8, '(Podíl připadniž) chudým z vystěhovavších se, kteří vypuzeni byli z obydlí svých a majetků svých, a snaží se dosíci milosti Boha a zalíbení jeho, a pomáhají Bohu a proroku jeho: tito zajisté jsou lidé opravdoví.'),
(5135, 59, 9, 'A ti, kdož přebývali ve svých domech, a přijali víru před nimi, milují ty, kdož vystěhovali se k nim a nenalézají v nitrech svých touhu po něčem, co těmto dáno bylo ba dávají jim přednost před sebou, ač jsou mezi nimi potřební. A tomu, kdo ochrání duši svou před lakotou, zajisté dobře povede se.'),
(5136, 59, 10, 'Ti pak, kdož přišli po nich, říkají: „Pane náš, odpusť nám a bratřím našim, kteří predešli nás ve víře: a nevkládej v srdce naše zlovůli vůči těm, kdož uvěřili, Pane náš neb ty zajisté laskavý jsi a slitovný.'),
(5137, 59, 11, 'Zdaž neviděl\'s ty, kdož pokrytci jsou, říkati bratřím svým, kdož neuvěřili z lidu, obdrževšího Knihu: „Budete-li vypuzeni, my dojista vyjdeme s vámi: a ohledně vás neuposlechneme nikdy nikoho; a bude-li válčeno proti vám, dojista pomůžeme vám?“ Bůh zajisté jest svědkem toho, že lháři jsou.'),
(5138, 59, 12, 'I kdyby byli vypuzeni, nevyjdou s nimi; a bude-li válčeno proti nim, nepomohou jim; a i kdyby vyšli na pomoc jim, obrátili by se na útěk: a tak oni zůstali by bez pomoci.'),
(5139, 59, 13, 'Vyť zajisté silnější působíte bázeň v nitrech jejich, než sám Bůh! To proto, že lid jest to nechápavý.'),
(5140, 59, 14, 'Nebudou bojovati proti vám v celku, leda v městech opevněnych, aneb zpoza náspů. Udatnost jich mezi sebou jest velká a měl bys je za sjednocené; však srdce jich rozdělena jsou: to proto, že lid jest to nerozumný.'),
(5141, 59, 15, 'Jako ti, kdož byli před nimi, nedávno okusili zlý výsledek stanoviska svého a (schystán) jest jim trest bolestný.'),
(5142, 59, 16, 'Jako Satan, když řekl člověku: „Nevěř,“ a když stal se nevěrcem, řekl: „Prost jsem viny na tobě: jáť zajisté bojím se Boha, Pána veškerenstva.“'),
(5143, 59, 17, 'A konec obou jich bude, že oba budou v ohni (pekelném), v němž přebývati budou věčně: takováto bude odměna nepravostných.'),
(5144, 59, 18, 'Vy, kteří jste uvěřili, bojte se Boha: a nechť dobře hledí každá duše, co kupředu si posílá na zítřek. A bojte se Boha, neb on zajisté (dobře) zpraven jest o tom, co činíte.'),
(5145, 59, 19, 'Nebuďte jako ti, kdož zapomněli Boha a jimž On dal zapomenouti sebe samy. Tito zajisté jsou pachatelé pohoršení.'),
(5146, 59, 20, 'Nebudou rovni si obyvatelé ohně (pekelného) a obyvatelé zahrady (ráje): obyvatelé ráje budou blaženi.'),
(5147, 59, 21, 'Kdybychom byli seslali tento Korán na horu, byl bys viděl ji poníženou a pukající ze strachu před Bohem. A tato podobenství činíme lidem, aby snad přemýšleli.'),
(5148, 59, 22, 'Onť jest Bůh, kromě něhož není boha jiného: zná věci světa skrytého i viditelného: onť Milosrdný jest, Slitovný.'),
(5149, 59, 23, 'Onť jest Bůh, kromě něhož není boha jiného: onť jest Král, Svatý, Pokojný, Věrný, Opatrovný, Mocný, Silný, Povýšený: chválen budiž Bůh nade vše, co spolčují s ním!'),
(5150, 59, 24, 'Onť Bůh jest Tvořitel, Tvůrce, Vypodobňovatel! Jemu patrí jména nejkrásnější a chvály pěje mu vše, což na nebi jest a na zemi: a on Mocný jest, Moudrý!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Vy, kteříž jste uvěřili, nebeřte si nepřátel mých aniž svých za přátele, ukazujíce jim lásku, neb přece neuvěřili v to, co přišlo vám z pravdy: (vždyť) vyhánějí proroka i vás proto, že věříte v Boha, Pána svého. Vyšli-li jste v boj na stezce mé a ve snaze dosíci zalíbení mého, jak můžete v tajnosti ukazovati jim lásku? Však já nejlépe vím o tom co skrýváte i o tom, co na venek přiznáváte: a ten, kdo tak činí z vás, zbloudil již s cesty rovné!'),
(5152, 60, 2, 'Potkají-li vás, budou vám nepřáteli a vztáhnou na vás ruce i jazyky své, aby vám uškodili; a rádi by vás opět viděli nevěrci.'),
(5153, 60, 3, 'Neprospějí vám rodní vaši, aniž děti vaše, v den zmrtvýchvstání: tehdy rozděleno bude mezi vámi! A Bůh dobře pozoruje skutky vaše.'),
(5154, 60, 4, 'Vždyť již měli jste krásný příklad v Abrahamovi a v těch, kdož byli s ním, když řekli lidu svému: „Prosti jsme viny za vás a za to, co uctíváte vedle Boha: zapíráme vás, a počíná mezi námi a vámi nepřátelství a nenávist navždy, dokud neuveříte v Boha jediného!“ Pouze (nenásledujte) rčení Abrahamova otci svému: „Já dojista budu prositi za odpuštění pro tebe, však nebudu s to, docíliti pro tebe od Boha praničeho.“ Pane náš, na tebe spoléháme a k tobě kajícně se obracíme a k tobě vede cesta (návratu).'),
(5155, 60, 5, 'Pane náš, nečiň nás pokušením pro ty, kdož neuvěřili, a odpusť nám, Pane náš, neb ty zajisté\'s mocný, moudrý.'),
(5156, 60, 6, 'Vždyť již měli jste v nich krásný příklad: každý, kdož doufá v (setkání s) Bohem a v den poslední. A kdo nazpět obrátí se — Bůh zajisté bohatý jest, chvályhodný!'),
(5157, 60, 7, 'Možná, že způsobí Bůh mezi vámi a těmi z nich, s nimiž znepřáteleni jste, vzájemnou lásku, neb Bůh všemocný jest, a Bůh velkým jest v odpouštění, slitovným.'),
(5158, 60, 8, 'Bůh nezabraňuje vám, abyste byli správnými a spravedlivými vůči těm, kdož neválčili proti vám v příčině náboženství, aneb nevyháněli vás z obydlí vašich: zajisté Bůh miluje správně jednající.'),
(5159, 60, 9, 'Bůh zabraňuje vám jen, aby přátelili jste se s těmi, kdož válčili proti vám v příčině náboženství a vyháněli vás z obydlí vašich a pomáhali ve vyhánění vás: a ten, kdo přátelí se s nimi, patřiti bude k nepravostným.'),
(5160, 60, 10, 'Vy, kteří jste uvěřili, když přijdou k vám ženy věřící jako vystěhovavší se, podrobte je ZKOUŠCE. Bůh nejlépe zná víru jejich; když pak seznali jste, že skutečně jsou věřícími, nevracejte je již k nevěrcům, neb nejsou doveleny jim, aniž oni jsou dovoleni jim; ale vraťte jim, což oni byli dali jim (věnem). Není pak viny vám, abyste brali si je za manželky, když byli dali jste jim věna jejich. Nepodržujte si nijakých nároků na nevěřící ženy, ale žádejte zpět, cožkoliv byli dali jste jim (věnem): nevěrci pak nechť žádají zpět, cožkoliv byli dali ženám svým. Takovýto jest rozsudek boží, kterým rozsuzuje mezi vámi: a Bůh vševědoucí jest a moudrý.'),
(5161, 60, 11, 'A když některá z manželek vašich přejde k nevěřícím a dostane se vám kořisti, tehdy dejte těm, jichž ženy byly odešly, stejný obnos jaký byli na ně vydali: a bojte se Boha, v nějž věříte.'),
(5162, 60, 12, 'Ó proroku, přijdou-li k tobě ženy věřící, zaručujíce se, že nebudou ničeho spolčovati s Bohem; že nebudou krásti, aniž smilniti, aniž zabíjeti dětí svých, aniž šířiti budou pomluvy, jež byly, samy vymyslily, aniž budou neposlušny tebe v dobru — tehdy přijmi záruku jejich a pros Boha za odpuštení pro ně, neboť Bůh velkým jest v odpouštění, slitovným.'),
(5163, 60, 13, 'Vy, kteříž jste uvěřili, nepřátelte se s lidmi, na něž rozhněval se Bůh tito v beznaději jsou ohledně života budoucího, stejně jako nevěřící v beznaději jsou ohledně těch, kdož v hrobech jsou.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Chvály pěje Bohu, cožkoliv na nebi jest i na zemi: a on mocný jest a moudrý.'),
(5165, 61, 2, 'Vy, kteříž jste uvěřili, proč říkáte to, co neděláte?'),
(5166, 61, 3, 'Velice protiví se Bohu, že říkáte to, co neděláte.'),
(5167, 61, 4, 'Bůh zajisté miluje ty, kdož bojují na stezce jeho v ŠIKU BITEVNÍM, jako by byli stavbou pevnou.'),
(5168, 61, 5, '(Vzpomeň), když řekl Mojžíš lidu svému: „Lide můj, proč zarmucujete mne, když víte, že jsem poslem božím k vám?“ A když pochybili, dal Bůh pochybiti srdcím jejich: neb Bůh nevede (cestou pravou) lid pohoršení páchající.'),
(5169, 61, 6, 'A (vzpomeň), když řekl Ježíš, syn Mariin: „Synové Israele, jáť zajisté jsem poslem božím k vám, potvrzujícím, což přede mnou dáno bylo vám ze Zákona a zvěstujícím vám příchod onoho proroka, jenž přijde po mně, jehož jméno bude Ahmed.“ A když předvedl jim jasné důkazy, řekli: „Totoť jest kouzelnictví zjevné.“'),
(5170, 61, 7, 'A kdo nepravostnějším jest toho, jenž vymýšlí o Bohu lež, když volán jest k Islámu? Bůh pak nevede (pravou cestou) lid nepravostný.'),
(5171, 61, 8, 'Rádi by chtěli sfouknouti světlo beží ústy svými, však Bůh dokonalým učiní světlo své, i kdyby protivno to bylo nevěrícím.'),
(5172, 61, 9, 'On jest to, který poslal proroka svého s (dobrým) Vedením a náboženstvím pravým, aby vyvýšil je nad náboženství veškerá, i kdyby protivno to bylo mnohobožcům.'),
(5173, 61, 10, 'Vy, kteříž jste uvěřili, chcete, abych ukázal vám obchod, jenž zachrání vás od trestu bolestného?'),
(5174, 61, 11, '(Když) věříte v Boha a proroka jeho, a bojujete na stezce boží statky svými i osobami svými: to bude nejlepším pro vás, kdybyste jen věděli.'),
(5175, 61, 12, '(Bůh) odpustí vám viny vaše a uvede vás v zahrady, pod nimiž řeky tekou — a v obydlí krásná v zahradách Edenu: to blaženost bude velká!'),
(5176, 61, 13, 'A jiného (něco), co rádi budete míti: pomoc od Boha a vítězství blízké. Radostnou oznamuj pak zvěst (tuto) věrícím!'),
(5177, 61, 14, 'Vy, kteříž jste uvěřili, buďte pomocníky Boha, jakož řekl Ježíš, syn Mariin, učedníkům svým: „Kdož jsou pomocníky mými ve věci bozí?“ Řekli učedníci: „My pomocníky jsme božími.“ I uvěřila část synů Israele a odepřela víru část jejich: a pomohli jsme těm kdož uvěřili, proti nepřátelům jejich a stali se vítěznými.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Chvály pěje Bohu, cožkoliv na nebi jest i na zemi: Králi, Posvátnému, Mocnému, Moudrému!'),
(5179, 62, 2, 'Onť jest to, jenž vzbudil mezi prostými proroka z nich, aby předčítal jim znamení jeho a očišťoval je a učil je Knize a moudrosti, ač předtím nalézali se v bloudění zjevném.'),
(5180, 62, 3, 'A jiní jsou mezi nimi, kdož dosud nepřilnuli k nim: Bůh pak mocný jest a moudrý.'),
(5181, 62, 4, 'Totoť milost jest od Boha, již dává, komu chce: a Bůh nesmírným jest v milosti své!'),
(5182, 62, 5, 'Podobni jsou ti, na které naložen byl Zákon a oni pak nenesli jej, oslu, jenž nese knihy: špatné jest to podobenství o lidu, který vylhanými nazval znamení boží! Ale Bůh nevede cestou pravou lid nepravostný.'),
(5183, 62, 6, 'Rci: „Ó Židé, máte-li za to, že vy jste přáteli Boha spíše než druzí lidé, tedy přejte si smrti, jste-li pravdomluvnými!“'),
(5184, 62, 7, 'Však nebudou přáti si jí nikdy, pro to, co byly poslaly před nimi ruce jejich: neb Bůh dobře zná nepravostné.'),
(5185, 62, 8, 'Rci: „Zajisté smrt, před níž prcháte, jistě potká vás: pak. zpět přivedeni budete ke Znateli věcí skrytých i viditelných a On poví vám, co činili jste.'),
(5186, 62, 9, 'Vy, kteříž jste uvěřili, když zavoláno jest k modlitbě v den SHROMÁŽDĚNÍ, spěšte ke vzpomínání Boha a zanechte obchodování: to bude nejlepším pro vás, kdybyste si to (jen) uvědomili.'),
(5187, 62, 10, 'A když skončena jest modlitba, rozejděte se po zemi, snažíce se dosíci (podílu) z milosti Boha a vzpomínejte Boha často, aby snad dobře vedlo se vám.'),
(5188, 62, 11, 'Však když vidí obchodování, neb kratochvíli, rozběhnou se za nimi a zanechají tě stojícího o samotě. Rci: „Co u Boha jest, lepším jest kratochvíle neb obchodování: a Bůh nejlepším, jest ze všech živitelů.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Když přijdou k tobě POKRYTCI, řkou: „Dosvědčujeme, žes zajisté prorokem Boha.“ Bůh pak nejlépe ví, žes zajisté prorokem jeho a Bůh dosvědčuje, že pokrytci jen lháři jsou.'),
(5190, 63, 2, 'Béřou si víru svoji za plášť a odvracejí (druhé) od stezky boží: špatné věru jest konání jejich.'),
(5191, 63, 3, 'To proto, že zprvu uvěřili, pak zapřeli (víru); pročež pečeť vložena byla na srdce jejich, takže nechápou.'),
(5192, 63, 4, 'A když vidíš je, líbí se ti osoby jejich a když mluví, posloucháš řeči jejich: jsou jako trámy o zeď opřené. Domnívají se, že každé vzkřiknutí jest proti nim. Oni jsou nepřáteli (ti), pročež měj se na pozoru před nimi. Bůh bojujž proti nim - jak prolhaní jsou!'),
(5193, 63, 5, 'A když řečeno jest jim: „Pojďte sem, prositi bude prorok za odpuštění pro vás Boha,“ odvracejí stranou hlavy své a vidíš je, ani vzdalují se pýchou naduti.'),
(5194, 63, 6, 'Jedno jest jim, prosíš-li za odpuštění pro ně, či neprosíš; Bůh neodpustí jim, neb zajisté Bůh nevede správnou cestou lid pohoršlivý.'),
(5195, 63, 7, 'Oni jsou to, kteří říkají: „Nevydávejte ničeho na ty, kdož jsou s prorokem božím, takže nuceni budou opustiti jej.“ Nicméně Bohu patří pokladnice nebes i země: však pokrytci toho nechápou.'),
(5196, 63, 8, 'Říkají: „Kdybychom vrátili se do města dojista vyhnal by silnější z ní slabšího.“ Nicméně Bohu patří síla veškerá a proroku jeho a věřícím: však pokrytci nejsou si toho vědomi.'),
(5197, 63, 9, 'Vy, kteříž jste uvěřili, nechť nezlákají vás statky vaše, ani děti vaše, od vzpomínání Boha: kdožkoli učiní tak, ten jistě záhubě propadne.'),
(5198, 63, 10, 'Ale rozdávejte z toho, čím obdařili jsme vás, dříve než přijde k některému z vás smrt a on řekne: „Pane můj, kdybys jen poshověl mi do lhůty kratičké, dal bych štědrou almužnu a byl bych ze spravedlivých.“'),
(5199, 63, 11, 'Však Bůh neposhoví žádné duši, když přišla lhůta její a Bůh (dobře) zpraven jest o tom, co činíte.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Chvály pěje Bohu, cožkoliv na nebi jest i na zemi: jemu přináleží panství a jemu přináleží chvála; a on nade všemi věcmi moc má!'),
(5201, 64, 2, 'On jest to, který stvořil vás; některý z vás jest nevěrcem, některý z vás jest věřícím: a Bůh (dobře) skutky vaše obzírá.'),
(5202, 64, 3, 'Stvořil nebesa i zemi v pravdě, a podobu dal vám a dal vám podobu krásnou: a k němu vede cesty cíl.'),
(5203, 64, 4, 'Ví, cožkoliv na nebi jest i na zemi a ví, co tajíte i co navenek přiznáváte: a Bůh dobře ví, co nitra skrývají.'),
(5204, 64, 5, 'Což nedošla vás zvěst o těch, kdož nevěrci byli v minulu i okusili bědnost postavení svého? A (schystán) jest jim trest bolestný.'),
(5205, 64, 6, 'To proto, že přišli k nim proroci naši s jasnými důkazy a on říkali: „Zdaž (pouzí) lidé mají vésti nás?“ a neuvěřili a odvrátili se zády. Však Bůh mohl obstáti bez nich, neb Bůh bohatý jest a chvályhodný.'),
(5206, 64, 7, 'Ti, kdož neuvěřili, mají za to, že nebudou vzkříšeni. Rci: „Zajisté, že ano; při Pánu mém, dojista vzkříšeni budete a pak dojista zpraveni budete o tom, co činili jste: a bude to Bohu snadným!“'),
(5207, 64, 8, 'Pročež věřte v Boha a proroka jeho a ve světlo, jež seslali jsme: a Bůh (dobře) zpraven jest o skutcích vašich.'),
(5208, 64, 9, 'V den, kdy shromáždí vás ke dni shromáždění, tento bude den VZÁJEMNÉHO OKLAMÁNÍ; a od toho, kdož věří v Boha a koná dobré skutky, odejme (Bůh) špatnosti jeho a uvede ho v zahrady, pod nimiž řeky tekou: v nich přebývati. budou věčně věkův. Toť blaženost bude velká!'),
(5209, 64, 10, 'Ale ti, kdož neuvěřili a vylhanými nazývali znamení naše - ti obyvateli budou ohně (pekelného): v něm přebývati budou věčně a špatný bude to cesty cíl!'),
(5210, 64, 11, 'Nepostihne nijaké neštěstí člověka, leda z dopuštění božího; a ten, jenž věří v Boha - srdce jeho povede (Bůh): a Bůh o všech věcech má vědomost.'),
(5211, 64, 12, 'Pročež poslušni buďte Boha a poslušni buďte proroka: a když obrátíte se zády - proroku našemu uloženo jest jen hlásání zjevné!'),
(5212, 64, 13, 'Bůh! Není Boha, kromě Něho, a na Boha nechť spoléhají věřící!'),
(5213, 64, 14, 'Vy, kteříž jste uvěřili, zajisté některé z manželek vašich a dětí vašich jsou nepřáteli vám: pročež mějte se na pozoru před nimi! Však prominete-li a přehlédnete (chyby) a odpustíte, vězte, že Bůh velkým jest v odpouštění, slitovným.'),
(5214, 64, 15, 'Statky vaše a děti vaše jsou jen pokušením vám, však u Boha věru odměna jest velká!'),
(5215, 64, 16, 'Pročež bojte se Boha, seč můžete, a naslouchejte, a buďte poslušni, a přispívejte almužnou, ve prospěch vlastních duší: a kdožkoliv ochráněn jest před vlastní skoupostí — těm zajisté dobře povede se.'),
(5216, 64, 17, 'Půjčíte-li Bohu půjčku štědrou, zdvojnásobí vám ji a odpustí vám: a Bůh velmi vděčný jest a laskavý:'),
(5217, 64, 18, 'Znatel věcí skrytých i viditelných: Mocný, Moudrý!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Ó proroku! - Zapuzujete-li ženy své, tedy zapuzujte je dle období jejich, a počítejte období ta přesně: a bojte se Boha, Pána svého. Nevyhánějte jich z domů jejich a nevyhánějte jich, ledaže by dopouštěly se cizoložství zjevného. Tyto jsou hranice (nařízení) božího a kdo přestupuje hranice boží, křivdí sobě samému: nevíš přece, zda snad Bůh vzbudí poté okolnost nějakou.'),
(5219, 65, 2, 'A když dosáhly určené lhůty své, buď vezměte je zpět laskavě, aneb rozejděte se s nimi laskavě: a přiveďte za svědky osoby spravedlivé z vašeho středu a svědectví (přímé) čiňte před Bohem. Tímto napomínán jest, kdožkoliv věří v Boha a v den poslední: a kdožkoliv bojí se Boha, tomu popřeje Bůh (dobrého) výsledku!'),
(5220, 65, 3, 'a obdaří jej, zkad (odkud ) nenaděje se. A tomu, kdo spoléhá na Boha, stačí Bůh; Bůh zajisté dosáhne cílů svých: daltě Bůh všem věcem předurčení.'),
(5221, 65, 4, 'Pokud se týče oněch žen vašich, jež nemají naděje na čmýru (menstruaci) - máte-li pochybnosti o tom - tehdy období jejich budiž tři měsíce, a stejně takové pro ty, jež dosud neměly čmýry: a pro ty, jež těhotné jsou, lhůta bude dokud neslehly. A tomu, kdo bojí se Boha, Bůh usnadní záležitost jeho.'),
(5222, 65, 5, 'Takovýto jest rozkaz boží, jejž seslal vám a tomu, jenž bojí se Boha, Bůh odejme špatnosti jeho a zvětší mu odměnu.'),
(5223, 65, 6, 'Dejte příbytek (zapuzeným) tam, kde sami bydlíte, dle prostředků svých; a nečiňte jim příkoří, tak abyste vháněli je v tíseň: a jsou-li těhotny, tedy dávejte na ně, dokud neslehnou; a když kojí vám (děcko), dávejte jim plat jejich a poraďte se mezi sebou v laskavosti; však naleznete-li v tom nějakých obtíží, teď nechť kojí (děcko) žena jiná.'),
(5224, 65, 7, 'Ten pak, kdo zámožný jest, nechť dává dle zámožnosti své: a ten, jehož živobytí odměřeno jest, nechť dává dle toho, co dal mu Bůh. Bůh neukládá duši většího břemena. než dle toho, co byl dal jí: a Bůh po těžkostech ulehčí.'),
(5225, 65, 8, 'A kolik měst odvrátilo se od rozkazu Pána svého a proroků jeho! A súčtovali jsme s nimi súčtováním přísným a ztrestali jsme je trestem odporným.'),
(5226, 65, 9, 'A okusila bědnost postavení svého a konec postavení jejich byla záhuba (plná).'),
(5227, 65, 10, 'Schystal jim Bůh trest přísný: pročež bojte se Boha, lidé (pronikavého) rozumu, kdož uvěřili jste, neb Bůh seslal vám již napomenutí:'),
(5228, 65, 11, 'proroka, jenž předčítá vám znamení boží jasná, aby vyvedl ty, kdož uvěřili a konají dobré skutky, z temnot na světlo; a toho, jenž věří v Boha a koná dobro, uvede (Bůh) v zahrady, pod nimiž řeky tekou - v nich přebývati budou věčně věkův a překrásné proň, Bůh schystal živobytí!'),
(5229, 65, 12, 'Bůh jest to, jenž stvořil sedmero nebes a zemí tolikéž: rozkaz (jeho) sestupuje mezi nimi, aby věděli jste, že Bůh zajisté nade všemi věcmi moc má a že Bůh zajisté všechny věci obklopuje věděním svým.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Ó proroku! Proč ZAPOVÍDÁŠ, co dovolil ti Bůh? Žádáš si uspokojení žen svých: a Bůh velkým jest v odpouštění, slitovným!'),
(5231, 66, 2, 'Bůh učinil vám povinným osvobození od přísah vašich; a Bůh pánem jest vaším: a on vševědoucí jest a moudrý.'),
(5232, 66, 3, 'Když (onehdy) sdělil prorok v tajnosti jistou událost jedné ze svých žen a ona oznámila ji druhé, tu zjevil se mu Bůh, a on sdělil s ní něco z toho a zamlčel něco. A když oznámil jí to, řekla: „Kdo pověděl ti o tom?“ řekl: „Pověděl mi o tom Vševědoucí, Zpravený!'),
(5233, 66, 4, 'Obrátíte-li se obě kajícně k Bohu - neb srdce vaše pochybila - však spojíte-li se proti němu, věztež, že Bůh jest ochráncem jeho a že Gabriel a každý spravedlivý mezi věřícími a andělé, budou poté spojenci jeho.'),
(5234, 66, 5, 'Možná dost, že Pán jeho, v případě, že (prorok) zapudí vás, nahradí mu vás manželkami lepšími vás, odevzdanými do vůle boží, věřícími, oddanými, kajícnými, sloužícími, postícími se; poznavšími již muže, neb pannami.“'),
(5235, 66, 6, 'Vy, kteříž jste uvěřili, chraňte sebe samy a rodiny své před ohněm, jehož, palivem jsou lidé a kamení; nad nimž vznášejí se andělé drsní a přísní, kteří nijak nejsou neposlušni Boha v rozkazech jeho, nýbrž vykonávají to, což nakázáno bylo jim.'),
(5236, 66, 7, 'Vy, kteří jste neuvěřili, neomlouvejte se nijak dnes: odměněni budete dle skutků svých!'),
(5237, 66, 8, 'Vy, kteří jste uvěřili, kajte se pokáním upřímným: možná, že Pán váš odejme od vás špatnosti vaše a uvede vás v zahrady, pod nimiž řeky tekou - v den, kdy nepokryje Bůh potupou proroka a ty; kdož uvěřili s ním; světlo jejich proběhne před nimi a po pravici jejich, a říkati budou: „Pane náš, dokonalým učiň světlo naše a odpust nám: tyť zajisté nade všemi věcmi moc máš.“'),
(5238, 66, 9, 'Ó proroku, bojuj proti nevěrcům a pokrytcům a buď drsným k nim; obydlím jejich bude peklo a špatný to bude cesty cíl!'),
(5239, 66, 10, 'Učinil Bůh těm, kdož neuvěřili, podobenstvím ženu Noemovu a ženu Lotovu: obě byly pod mocí dvou služebníků ze služebníků našich spravedlivých a obě zradily je: však neprospěli jim (manželé jejich) u Boha pranic a řečeno bylo jim: „Vstupte do ohně (pekelného) s těmi, kdož vstupují!“'),
(5240, 66, 11, 'Učinil pak Bůh těm, kdož uvěřili, podobenstvím ženu Faraonovu, když řekla: pane můj, postav mi u sebe dům v zahradě (ráje) a vysvoboď mne od Faraona a skutků jeho a vysvoboď mne od lidu nepravostného.“'),
(5241, 66, 12, 'A Marii, dceru Imránovu, jež ochránila si neporušenost lůna svého i vdechli jsme v ně ducha svého: i potvrdila slova Pána svého a byla jednou z pobožných.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Požehnán budiž ten, v jehož ruce jest PANSTVÍ a jenž nade všemi věcmi moc má;'),
(5243, 67, 2, 'jenž stvořil smrt i život, aby vyzkoušel vás a zvěděl kdo nejlepší bude konati (skutky): a on mocný jest, odpouštějící.'),
(5244, 67, 3, 'Jenž stvořil sedmero nebes v poschodích (nad sebou): neshlédneš ve stvoření Milosrdného pochybení! Pohlédni tedy znovu; zda uvidíš(nejmenší) skuliny!'),
(5245, 67, 4, 'Pak pohlédni znovu ještě dvakrát: navrátí se pohled tvůj k tobě zmátožen a znaven.'),
(5246, 67, 5, 'Ozdobili jsme pak nebe nejspodnější svítilnami a učinili jsme tyto kamenovadlem zlých duchů, pro něž schystali jsme trest plamene.'),
(5247, 67, 6, 'A těm, kdož neuvěřili v Pána svého (schystán) jest trest pekla a špatný to bude cesty cíl!'),
(5248, 67, 7, 'Když vrháni budou do něho, budou slyšet řev jeho, jak vříti bude,'),
(5249, 67, 8, 'a málem praskati bude rozlíceností. Pokaždé, když vržen do něho bude houf (nevěrců), tázati se jich budou strážcové jeho: „Což nepřišel vám varovatel?“'),
(5250, 67, 9, 'Řeknou: ,,Ano, přišel nám varovatel, však lhářem nazvali jsme (jej) a řekli jsme: ,Ničeho neseslal Bůh!‘ Vyť pouze jste v bludu velkém!“'),
(5251, 67, 10, 'Řeknou (dále): ,Kdybychom byli naslouchali, aneb pochopili, nebyli bychom mezi obyvateli plamene.“'),
(5252, 67, 11, 'I doznají vinu svou, však: „Pryč odtud, obyvatelé plamene!“'),
(5253, 67, 12, 'Zajisté pak těm, kdož bojí se Pána svého v skrytosti, (dáno) bude odpuštění a odměna velká.'),
(5254, 67, 13, 'Ať tajná jest řeč vaše, ať veřejná, zajisté Bůh ví, co skrývají nitra.'),
(5255, 67, 14, 'Ó zajisté, ví to On jenž stvořil (vše), neb On prozíravý jest, (o všem) zpraven.'),
(5256, 67, 15, 'Onť, jest to, jenž učinil vám zemi srovnanou: pročež choďte po (širých) bocích jejích a pojídejte z výživy (jím dané): k němu pak (povede) vzkříšení.'),
(5257, 67, 16, 'Zdaž jste si jisti, že ten jenž na nebi jest, nerozpoltí pod vámi zemi? A hle, již hýbe se.'),
(5258, 67, 17, 'Anebo jisti jste si, že ten, jenž na nebi jest, nepošle na vás vichr bouřný? Pak, zvíte jaké jest varování mé!'),
(5259, 67, 18, 'Však již ti kteří byli před nimi lháři nazývali (proroky): a jaký byl hněv můj!'),
(5260, 67, 19, 'Což neviděli ptactvo nad sebou, ano stře a stahuje křídla svá - a kdo jiný (tak) udržuje je, leda Milosrdný? Onť zajisté všechny věci obzírá.'),
(5261, 67, 20, 'Kdo jest ten jenž jako celé vojsko vám může pomoci, leda Milosrdný? Zajisté nevěřící jsou v klamu (bludném)!'),
(5262, 67, 21, 'Kdo jest ten, jenž může živiti vás, odejme-li On výživu svou? A přece setrvávají ve svém vzdoru a prchání!'),
(5263, 67, 22, 'Zdaž ten, jenž jde, plaze se na tváři své, lépe jest veden než ten, jenž jde zpříma na stezce přímé?'),
(5264, 67, 23, 'Rci: „Onť jest to, jenž dal vzniknouti vám a učinil vám sluch a zrak a srdce: jak málo vděčni jste!“'),
(5265, 67, 24, 'Rci: „On jest to, který rozsel vás po zemi a k němu budete shromážděni.“'),
(5266, 67, 25, 'A říkají: „Kdy naplní se onen slib? Rcete, jste-li pravdomluvnými.“'),
(5267, 67, 26, 'Rci: „Vědomost o tom jest jen u Boha: jáť pouze jsem varovatelem zjevným.“'),
(5268, 67, 27, 'A když spatří (trest) an blíží se, zasmuší se tváře těch, kdož neuvěřili, a bude řečeno jim: ,,Totoť jest, co na sebe jste svolávali.“'),
(5269, 67, 28, 'Rci: „Co míníte? Ať již zahladí Bůh mne a ty, kdož se mnou jsou, aneb slituje se nad námi - kdo chrání nevěřící před trestem bolestným?“'),
(5270, 67, 29, 'Rci: „Onť jest Milosrdný: uvěřili jsme v něho a na něho spoléháme: vy seznáte pak, kdo nalézal se v bludu zjevném.“'),
(5271, 67, 30, 'Rci: „Co míníte? Kdyby z jitra vody vaše vsákly se do země - kdo dá vám vodu pramenitou?“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nún. Při PÉRU a tom, co spisují!'),
(5273, 68, 2, 'Nejsi, z milosti Pána svého posedlý:'),
(5274, 68, 3, 'a zajisté schystána jest ti odměna bezvýhradná.'),
(5275, 68, 4, 'Tyt zajisté‘s povahy vznešené:'),
(5276, 68, 5, 'i spatříš, spatří oni (též),'),
(5277, 68, 6, 'kdo z vás (vskutku) jest šílený.'),
(5278, 68, 7, 'Zajisté Pán tvůj ví nejlépe, kdo zbloudil se stezky jeho a ví nejlépe, kdož (dobře) jsou vedeni.'),
(5279, 68, 8, 'Pročež nebuď poslušen těch, kdož lhářem zvou (tě).'),
(5280, 68, 9, 'Rádi by, kdybys hladce jednal s nimi, aby i oni hladce s tebou jednali.'),
(5281, 68, 10, 'A nebuď poslušen žádného z těch, kdož (slavnostně) zaklínají se, hanebných;'),
(5282, 68, 11, 'pomlouvačů, s klepy jen obcházejících;'),
(5283, 68, 12, '(velkých) zabraňovatelů dobra. přestupníků, zločinných:'),
(5284, 68, 13, 'hrubých, mimo to rodem špatných,'),
(5285, 68, 14, 'i kdyby mnoho statků měl a potomstva:'),
(5286, 68, 15, 'jenž, kdež čtena jsou mu znamení naše říká: „Báchorky jsou to starých.“'),
(5287, 68, 16, 'Poznamenáme jej na rypáku!'),
(5288, 68, 17, 'Postavili jsme je na zkoušku, jakož postavili jsme na zkoušku majitele zahrady. když přisáhli, že dojista sčesají datle její za jitra,'),
(5289, 68, 18, 'bez připojení výminky.'),
(5290, 68, 19, 'A obešlo ji zničení od Pána tvého mezitím, co spali:'),
(5291, 68, 20, 'a za jitra vypadala jak očesaná.'),
(5292, 68, 21, 'A zvolali na sebe časně za jitra:'),
(5293, 68, 22, '„Vyjděte časně na pole své, česat chcete-li!“'),
(5294, 68, 23, 'A vpřed se brali, šeptajíce si navzájem:'),
(5295, 68, 24, '„Dojista dnes v zahradu nevejde k vám ani jeden nuzný.“'),
(5296, 68, 25, 'Tak z jitra vyšli rozhodnuti.'),
(5297, 68, 26, 'A když spatřili (zahradu), řekli: „Zajisté v bludu byli jsme:'),
(5298, 68, 27, 'Naopak, (úrody její) jsme zbaveni.“'),
(5299, 68, 28, 'A nejsprávnější řekl nich: „Což neděl jsem vám: Nebudete-li chvály pěti Bohu?“'),
(5300, 68, 29, 'Řekli: ,,Chválen budiž Pán náš: myť zajisté nepravostni byli jsme.“'),
(5301, 68, 30, 'A počali si klásti vinu navzájem,'),
(5302, 68, 31, 'řkouce: ,,Běda nám, zajisté svévolni byli jsme.'),
(5303, 68, 32, 'Snad Pán náš nahradí nám ji lepší (zahradou): tak Pána svého prosíme.“'),
(5304, 68, 33, 'Takový byl trest náš: však trest života budoucího jistě větší bude. kdyby si toho jen byli vědomi!'),
(5305, 68, 34, 'Bohabojným pak zajisté schystány jsou u Pána jejich zahrady rozkoše.'),
(5306, 68, 35, 'Či učiníme stejnými ty, kdož do vůle boží jsou odevzdáni a provinilce?'),
(5307, 68, 36, 'Jak to přijde, že tak soudíte?'),
(5308, 68, 37, 'Aneb Knihu máte, z níž se učíte,'),
(5309, 68, 38, 'že dle ní dostanete cokoli si vybéřete?'),
(5310, 68, 39, 'Aneb dána vám, byla přísaha, pro nás závazná, sáhající až do dne zmrtvýchvstání, že dostanete, co sami usoudíte?'),
(5311, 68, 40, 'Taž se jich, kdo z nich jest toho ručitelem?'),
(5312, 68, 41, 'Anebo společníky mají? Nechť tedy předvedou společníky své, jsou-li pravdomluvnými!'),
(5313, 68, 42, 'V den, kdy obnaženy budou holeně a vyzváni budou k padnutí na tváře své - tu nebudou s to, (tak učiniti).'),
(5314, 68, 43, 'Sklopeni budou zrakové jejich a potupa je pokryje, neb vyzváni byli k padnutí na tváře své, když byli bezpečni.'),
(5315, 68, 44, 'I ponech mne a ty, kdož vylhaným nazývají toto vyprávění: postupně přivedeme na ně (zkázu), zkad jí netuší.'),
(5316, 68, 45, 'A dlouho poshovím jim, neb lest má jest jistá.'),
(5317, 68, 46, 'Či žádáš od nich odměnu, když přece dluhy přetíženi jsou?'),
(5318, 68, 47, 'Či znají věci skryté, jež (snad) spisují?'),
(5319, 68, 48, 'Pročež trpělivě vyčkávej rozsudek Pána svého a nebudiž jako onen v rybě, když volal k Bohu v tísni dusivé.'),
(5320, 68, 49, 'Nebýti toho, že dostihlo jej dobrodiní od Pána jeho, byl by býval vyvržen na (břeh) pustý, pohaněn.'),
(5321, 68, 50, 'Ale vyvolil si jej Pán jeho a učinil jej jedním ze spravedlivých.'),
(5322, 68, 51, 'Málem pak srážejí tě k zemi ti, kdož neuvěřili, pohledy svými, když slyší Napomenutí - a říkají: „On věru jest Džinny posedlý.“'),
(5323, 68, 52, 'Však jest to jen Napomenutí veškerenstvu.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'NEVYHNUTELNÁ!'),
(5325, 69, 2, 'Co jest nevyhnutelná?'),
(5326, 69, 3, 'A co objasní ti, co jest nevyhnutelná?'),
(5327, 69, 4, 'Vylhaným nazvaly (kmeny) Tsemmúd i ‘Ád úder (rozhodný).'),
(5328, 69, 5, 'A pokud se týče (kmene) Tsemúd, zahlazeni byli ranou hřmějící:'),
(5329, 69, 6, 'a pokud se týče kmene ‘Ád, zahlazeni byli vichrem bouřným, běsnícím.'),
(5330, 69, 7, '(Bůh) podřídil si jej proti nim po sedm nocí a osm dní po sobě následujících: tehdy byl bys viděl lid jeho k zemi sražený, jako by byli pni datlovníků vykotlamými.'),
(5331, 69, 8, 'Zdaž pak viděl\'s, že někdo z nich zbyl?'),
(5332, 69, 9, 'Farao, a kdož byli před ním a města vyvrácená - též hříchu se dopustili:'),
(5333, 69, 10, 'neb neposlušni byli proroka Pána svého i postihl je trestem zúročeným.'),
(5334, 69, 11, 'Zajisté, když voda vše zaplavila, nesli jsme vás v (lodi) plující,'),
(5335, 69, 12, 'abychom učinili vám ji upomínkou a aby chovalo ji (ucho) pozorné.'),
(5336, 69, 13, 'Až zatroubeno bude na roh jedním troubením,'),
(5337, 69, 14, 'a vznesena bude země i hory a (obé) zhrouceno jedním zhroucením:'),
(5338, 69, 15, 'v den onen pak udá se událost nezvratná:'),
(5339, 69, 16, 'i rozštěpí se nebe, neb den ten bude mizivým:'),
(5340, 69, 17, 'andělé budou po stranách jeho; a ponese osm jich trůn Pána tvého v den onen nad sebou.'),
(5341, 69, 18, 'V den onen předvedeni budete (před Boha) a nic skryto nebude z vašich (skutků) skrytých.'),
(5342, 69, 19, 'A pokud se týče toho; jemuž dána bude Kniha jeho do pravice, řekne: „Zde čtěte v Knize mé.'),
(5343, 69, 20, 'Jáť vždy jsem myslil, že (jednou) setkám se s účtem svým.“'),
(5344, 69, 21, 'Tenť nalezne se v žití příjemném,'),
(5345, 69, 22, 'v zahradě zvýšené,'),
(5346, 69, 23, 'jejíž plody blízky budou:'),
(5347, 69, 24, '„Jezte a pijte ve zdraví v odměnu za to, co předeslali jste ve dnech minulých!“'),
(5348, 69, 25, 'A pokud se týče toho, jemuž dána bude kniha jeho do levice, řekne: „Kéž by nikdy nebyla mi bývala dána kniha moje,'),
(5349, 69, 26, 'a nikdy nebyl bych zvěděl, jak stojí si účet můj!'),
(5350, 69, 27, 'Kéž (smrt) byla by ukončila (žití moje)!'),
(5351, 69, 28, 'Nic platno není mi jmění moje!'),
(5352, 69, 29, 'Odplata ode mne moc moje.“'),
(5353, 69, 30, '„Chopte se ho a řetězy svažte,'),
(5354, 69, 31, 'pak v ohni (pekelném) jej připékejte:'),
(5355, 69, 32, 'pak v řetězy sedmdesát loket zdélí jej navlékněte;'),
(5356, 69, 33, 'neb nebyl věřil v Boha mocného,'),
(5357, 69, 34, 'aniž krmiti snažil se nuzného.'),
(5358, 69, 35, 'Pročež nemá zde dnes přítele vřelého,'),
(5359, 69, 36, 'aniž pokrmu, vyjma hnisu smrdutého,'),
(5360, 69, 37, 'z nějž požívati budou pouze hříšníci.“'),
(5361, 69, 38, 'Nebudu přisahati při tom, co vidíte,'),
(5362, 69, 39, 'anebo tom, co nevidíte,'),
(5363, 69, 40, 'že toto zajisté jest řeč proroka vzácného:'),
(5364, 69, 41, 'a nikoli řeč básníka: jak málo věříte!'),
(5365, 69, 42, 'A není to řeč hadače: jak málo na to pamatujete!'),
(5366, 69, 43, 'Seslání (jest to) od Pána světů veškerých.'),
(5367, 69, 44, 'A kdyby byl sám býval vyřkl jako naše některé z těchto vyřčení:'),
(5368, 69, 45, 'byli bychom chopili se jeho pravice,'),
(5369, 69, 46, 'a jistě prořízli mu žílu hrdelní,'),
(5370, 69, 47, 'a proti nám nikdo z vás by jej nebyl uchránil.'),
(5371, 69, 48, 'Korán pak v pravdě jest upomínka bohabojným:'),
(5372, 69, 49, 'dobře pak víme, že mezi vámi jsou, kdož vylhanými (jej) zvou.'),
(5373, 69, 50, 'On zajisté jest smutkem pro nevěřící,'),
(5374, 69, 51, 'neb zajisté pravdou jest jistotnou.'),
(5375, 69, 52, 'Pročež pěj chválu jména Pána svého mocného!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Žádal žadatel, by náhlý trest'),
(5377, 70, 2, 'na nevěřící paď: nikdo (pak) nezbrání'),
(5378, 70, 3, '(seslání) jeho Bohem, Pánem STUPÁTEK:'),
(5379, 70, 4, '(po nichž) stoupají andělé a duch vzhůru k němu v průběhu dne, jehož délka jest padesát tisíc let.'),
(5380, 70, 5, 'Pročež vyčkej s trpělivostí krásnou;'),
(5381, 70, 6, 'oni vidí den onen daleký,'),
(5382, 70, 7, 'však my vidíme jej blízkým:'),
(5383, 70, 8, 'v den, kdy nebe bude jak mosaz roztavená,'),
(5384, 70, 9, 'a hory budou jak vločky vlny (rudé):'),
(5385, 70, 10, 'kdy nebude dotazovati se přítel vřelý přítele,'),
(5386, 70, 11, 'ač hledět budou jeden na druhého. Rád by provinilec v den onen vykoupil se z trestu dětmi svými,'),
(5387, 70, 12, 'a družkou svou, a bratrem svým,'),
(5388, 70, 13, 'a příbuznými, již chrání ho,'),
(5389, 70, 14, 'a obyvateli země všechněmi, kdyby pak spasilo jej to.'),
(5390, 70, 15, 'Však nikoliv! Neb výheň,'),
(5391, 70, 16, 'chopivši (je) za lebku,'),
(5392, 70, 17, 'zvát bude ty, kdož zády obrátili se a odcházeli,'),
(5393, 70, 18, 'a (statky) hromadili a schraňovali.'),
(5394, 70, 19, 'Zajisť člověk stvořen byl prchlým:'),
(5395, 70, 20, 'když zlo dotkne se jej, jest rozčilen;'),
(5396, 70, 21, 'a když dobro se jej dotkne, zpupným jest:'),
(5397, 70, 22, 'vyjma těch, kdož modlí se;'),
(5398, 70, 23, 'kdož v modlitbě své stálí jsou;'),
(5399, 70, 24, 'a v nichž jmění vždy jest podíl určený'),
(5400, 70, 25, 'pro prosícího a pro odvrženého;'),
(5401, 70, 26, 'a kdož věří v den poslední;'),
(5402, 70, 27, 'a kdož před trestem Pána svého bázní chvějí se;'),
(5403, 70, 28, '-neb není, kdo před trestem Pána jejich byl by bezpečen —'),
(5404, 70, 29, 'a kdož pohlavní údy své ostříhají,'),
(5405, 70, 30, 'vyjma s manželkami svými a otrokyněmi: (pak) zajisté viny nemají:'),
(5406, 70, 31, 'a kdo žádost svou nese dále toho, ti přestupníky jsou; —'),
(5407, 70, 32, 'a kdož věci svěřené jim, jakož i závazky své věrně zachovávají;'),
(5408, 70, 33, 'a kdož při svědectví svém pevně stojí;'),
(5409, 70, 34, 'a kdož (dobu) modliteb svých pevně ostříhají:'),
(5410, 70, 35, 'tito v zahradách (ráje) budou poctěni.'),
(5411, 70, 36, 'Co stalo se tedy oněm, kdož neuvěřili, že horempádem běží před tebou,'),
(5412, 70, 37, 'z prava i z leva shluknuti?'),
(5413, 70, 38, 'Snad přeje si jedenkaždý z nich, aby uveden byl do zahrad rozkoše?'),
(5414, 70, 39, 'Nikoliv. My stvořili jsme je, z čeho dobře vědí.'),
(5415, 70, 40, 'Není třeba mi přisáhnout při Pánu východů a západů, že máme moc,'),
(5416, 70, 41, 'abychom zaměnili je lepšími jich: v tom předejíti se nedáme.'),
(5417, 70, 42, 'Pročež nech je, ať baví se, a hrají si, až potkají den svůj, jenž jest jim slibován:'),
(5418, 70, 43, 'den, kdy vyběhnou z hrobů svých ve spěchu, jako by pod prapory se hrnuli;'),
(5419, 70, 44, 'se zrakem sklopeným, hanou pokryti. Toto jest den, jenž byl jim slibován!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Zajisté poslali jsme NOEMA k lidu jeho (řkouce): „Varuj lid svůj dříve než přijde na ně trest bolestný.“'),
(5421, 71, 2, 'Řekl: „Lide můj, jáť jsem vám varovatelem zjevným;'),
(5422, 71, 3, 'abyste uctívali Boha a báli se jej a byli poslušni mne.'),
(5423, 71, 4, 'Odpustí vám viny vaše a poshoví vám do lhůty předurčené; zajisté, když přijde lhůta boží, nebude odložena: kéž byste si toho byli vědomi!“'),
(5424, 71, 5, 'Řekl (pak): „Pane můj, zajistéť volal jsem lid svůj nocí i dnem:'),
(5425, 71, 6, 'však volání mé rozmnožilo v nich jen prchání.'),
(5426, 71, 7, 'A zajisté pokaždé, když volal jsem je, abys mohl odpustiti jim; zandali prsty uši své a zahalili se oděvem svým a setrvávali (v bludu svém) a vzpychnuli pýchou.'),
(5427, 71, 8, 'Pak volal jsem k nim hlasitě:'),
(5428, 71, 9, 'na to veřejně jsem k nim mluvil i tajně s nimi jednal v tajnosti,'),
(5429, 71, 10, 'a řekl jsem: „Proste za odpuštění Pána svého, neb on zajisté velkým jest v odpouštění.'),
(5430, 71, 11, 'Sešle na vás nebesa v dešti hojném:'),
(5431, 71, 12, 'a rozmnoží vám statky i děti vaše; dá vám zahrady a dá řeky vám;'),
(5432, 71, 13, 'proč tedy nechcete doufati v boží dobrotu?'),
(5433, 71, 14, 'Vždyť stvořil vás přec v tvarech postupných.'),
(5434, 71, 15, 'Což neviděli jste, jak stvořil Bůh nebes sedmero v poschodích,'),
(5435, 71, 16, 'a učinil měsíc, v nich světlem a slunce pochodní?'),
(5436, 71, 17, 'A Bůh dal vzrůsti vám ze země vzrůstáním:'),
(5437, 71, 18, 'pak dá vám navrátiti se v ni a ven vyvede vás vzkříšením.'),
(5438, 71, 19, 'Bůh pak jak koberec vám zemi rozprostřel,'),
(5439, 71, 20, 'abyste po ní chodili stezkami širokými.“'),
(5440, 71, 21, 'Řekl pak Noe: „Pane můj, zajisté oni neposlechli mne a následují ty, jichž majetky a děti jediné zkázou je rozmnožili.“'),
(5441, 71, 22, 'A lest vylstili velkou,'),
(5442, 71, 23, 'řkouce: „Neopouštějte božstev svých: dojista neopouštějte Wadda a Suwá\'a, aniž Jaghútse a Ja\'úqa a Nasra.“'),
(5443, 71, 24, 'Tak v blud zavedli mnohé; v nepravostných pak rozmnožiti můžeš jen zbloudění.'),
(5444, 71, 25, 'Za hříchy své potopeni byli a uvedeni v oheň pekelný: a nenalezli kromě Boha pomocníků.'),
(5445, 71, 26, 'Řekl pak Noe: „Pane můj, neponech na zemi z nevěřících rodiny jediné,'),
(5446, 71, 27, 'neb kdybys ponechal je, zavedli by v blud služebníky tvé a rodili by jen hříšníky, velké nevěrce.'),
(5447, 71, 28, 'Pane můj, odpusť mi a rodičům mým a těm, kdož vstoupí do domu mého, jsouce věřícími, jakož i věřícím mužům i ženám; a záhubou rozmnož nepravostné!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Rci: „Bylo vnuknuto mi, že skupina DŽINNŮ naslouchala, a řekla: ,Zajisté slyšeli jsme Korán údivný,'),
(5449, 72, 2, 'jenž vede k cestě správné: i uvěřili jsme veň a nikoho spolčovati nebudeme s Pánem svým.'),
(5450, 72, 3, 'Onť pak - povýšena budiž sláva Pána našeho! - nevzal si manželky aniž syna má.'),
(5451, 72, 4, 'Však jistý pošetilec mezi námi o Bohu tvrdil bláhovost.'),
(5452, 72, 5, 'Mínili jsme pak, že nikdo z lidí nebo Džinnů neříkal by lež o Bohu.'),
(5453, 72, 6, 'Někteří mužové mezi lidmi brali útočiště k mužům z džinnů, však ti rozmnožili pouze bláznivost jejich,'),
(5454, 72, 7, 'a pak mnili, jakož i vy mnili jste, že Bůh nevzkřísí nikoho.'),
(5455, 72, 8, 'Dostihlí jsme nebe, však shledali je plné strážců silných a šlehů plamenných.'),
(5456, 72, 9, 'A seděli jsme na sedadlech v něm, abychom slyšeli: však kdokoli přislouchávat chce teď, shledá, že číhati naň bude šleh plamenný.'),
(5457, 72, 10, 'A nevíme, zda zlo bylo míněno pro ty, kdož na zemi jsou, aneb žádal-li si Pán jejich pro ně cesty správné.'),
(5458, 72, 11, 'Mezi námi jsou pak spravedliví a jsou mezi námi, kdož nejsou takými: jsme cest rozdílných:'),
(5459, 72, 12, 'a mnili jsme, že nejsme s to, bychom znemožnili (zámyslů) Boha na zemi, aniž znemožníme mu to útěkem.'),
(5460, 72, 13, 'A když slyšeli jsme Vedení, uvěřili jsme v ně: a kdo věří v Pána svého, nechť nebojí se škody, ani potupy.'),
(5461, 72, 14, 'Jsou pak mezi námi ti, kdo odevzdali se do, vůle boží a ti, kdož se vzdalují: a kdožkoli odevzdal se do vůle boží, béře se cestou pravou.'),
(5462, 72, 15, 'A pokud se týče vzdalujících se, budou peklu palivem.“'),
(5463, 72, 16, 'Kdyby přidržovali ses přímé cesty, napojili bychom je vodou přehojnou,'),
(5464, 72, 17, 'abychom jí vyzkoušeli je: však kdožkoli vzdaluje se od Napomenutí Pána svého, tento podrobí jej trestu tvrdému.'),
(5465, 72, 18, 'Modlitebnice pak přináležejí Bohu (jedinému): pročež nevzývejte (současně) s Bohem nikoho.'),
(5466, 72, 19, 'A když povstal služebník boží, aby vzýval jej, málem by byli umačkali jej náporem.'),
(5467, 72, 20, 'Rci: „Jáť pouze vzývám Pána svého a nespolčuji s ním nikoho.“'),
(5468, 72, 21, 'Rci: „Neníť v moci mé přičiniti vám škody nijaké, aniž dobra.“'),
(5469, 72, 22, 'Rci: „Zajisté nikdo nemůže popřáti mi ochrany před Bohem, aniž najdu, kromě něho, útočiště;'),
(5470, 72, 23, 'vyjma hlásání, jež přichází od Boha a poselství jeho: a těm, kdož neposlušni jsou Boha proroka jeho, zajisté schystán jest oheň pekelný, v němž přebývati budou věčně.'),
(5471, 72, 24, 'Až když spatří to, co slibováno jest jim: pak zvědí, kdo nejslabším jest co do pomocníků a nejmenším co do počtu.“'),
(5472, 72, 25, 'Rci: „Nevím, zda blízkým jest to, co slibováno jest vám, aneb prodlouží-li Pán můj toho příchod:'),
(5473, 72, 26, 'on znatelem jest věcí skrytých a neodkrývá skrytých věcí svých nikomu,'),
(5474, 72, 27, 'vyjma oněch poslů svých, v nichž zalíbení nalezl: tehdy pak zajisté kráčí před ním i za ním,'),
(5475, 72, 28, 'číhaje, aby věděl, zda hlásali poselství Pána svého. On obklopuje věděním svým vše, co oni činí a přesný o všem počet vede.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ó ZAHALENÝ!'),
(5477, 73, 2, 'Stůj celou noc, vyjma něco málo:'),
(5478, 73, 3, 'půl noci, neb zmenši půl o něco málo:'),
(5479, 73, 4, 'aneb přidej k ní; a předzpěvuj Korán předzpěvem:'),
(5480, 73, 5, 'myť zajisté vložíme na tě rčení převážná.'),
(5481, 73, 6, 'Zajisté počátek noci budí silnější dojmy (zbožnosti) a přímější mluvení,'),
(5482, 73, 7, 'neb během dne máš práci zdlouhavou.'),
(5483, 73, 8, 'Pročež pomni jména Pána svého a oddej se mu plným oddáním:'),
(5484, 73, 9, 'Pánu východu i západu; není Boha, kromě něho; i vezmi si jej ochráncem:'),
(5485, 73, 10, 'a snášej trpělivě to, co říkají a vzdaluj se jich vzdalováním přístojným:'),
(5486, 73, 11, 'a ponech mne (samotna) s těmi, kdož lhářem nazývají tě, tonoucími v rozkoších (světa) - a posečkej jim ještě maličko.'),
(5487, 73, 12, 'Myť zajisté máme pro ně pouta a výheň plamennou;'),
(5488, 73, 13, 'potravu dusící a trest bolestný,'),
(5489, 73, 14, 'v den, kdy zatřese se země a hory, a budou hory jako hromady volného písku!'),
(5490, 73, 15, 'Zajisté poslali jsme k vám posla, aby svědčil proti vám, jakož k Faraonovi posla jsme poslali.'),
(5491, 73, 16, 'Však neuposlechl Farao posla i zachvátili jsme jej mocným zachvácením,'),
(5492, 73, 17, 'A jak ochráníte se, neuvěříte-li, přede dnem, kdy děti zšedivějí?'),
(5493, 73, 18, 'Kdy nebe samo pukne jím: neb slib o něm bude naplněn!'),
(5494, 73, 19, 'Totoť jest připomínka! Pročež nechť kdo chce, nastoupí k Pánu svému stezku!'),
(5495, 73, 20, 'Zajisté Pán tvůj dobře ví, že stojíš (v modlitbě) téměř dvě třetiny noci, neb půl, neb třetinu: a taktéž činí skupina z těch, kdož s tebou jsou. A Bůh měří noc a den; (dobře) ví, že to nepočítáte (přesně) i odpouští vám. Pročež předčítejte z Koránu, kolik snadno jest vám; (Bůh) ví, že budou mezi vámi nemocní, a jiní že tlouci se budou zemí, žádajíce si podílu z milosti boží; a jiní že bojovati budou na stezce boží. Pročež předčítejte z něho, kolik snadno jest vám: a zachovávejte modlitbu a dávejte povinnou almužnu a půjčujte Bohu půjčku štědrou; neboť cokoliv dobrého pošlete před sebou ve prospěch duší svých, shledáte u Boha; bude to lepším a větším co do odměny. A proste Boha za odpuštění; zajisté Bůh velkým jest v odpouštění, slitovným!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ó (PLÁŠTĚM) POKRYTÝ!'),
(5497, 74, 2, 'Vstaň a varuj!'),
(5498, 74, 3, 'A Pána svého vyvyšuj!'),
(5499, 74, 4, 'A oděv svůj očišťuj!'),
(5500, 74, 5, 'A pohoršení se vzdaluj!'),
(5501, 74, 6, 'A nebuď štědrým (proto), abys (jmění své) rozmnožil.'),
(5502, 74, 7, 'A na Pána svého trpělivě čekej!'),
(5503, 74, 8, 'A když zatroubeno bude v troubu,'),
(5504, 74, 9, 'ten den pak bude den přetěžký,'),
(5505, 74, 10, 'pro nevěřící (jistě) ne lehký.'),
(5506, 74, 11, 'Nech mne s tím, jejž stvořil jsem, samotna:'),
(5507, 74, 12, 'jemuž byl dal jsem statky rozsáhlé'),
(5508, 74, 13, 'a děti, jako svědky (mu):'),
(5509, 74, 14, 'vše na hladko mu uhladil.'),
(5510, 74, 15, 'Pak ještě víc si žádá ode mne!'),
(5511, 74, 16, 'Nikoliv! Zajisté vůči znamením našim byl odbojným:'),
(5512, 74, 17, 'i uvalím naň vzestup přetěžký.'),
(5513, 74, 18, 'Zajistéť vymýšlel a pořádal —'),
(5514, 74, 19, 'nechť zabit jest - jak pořádal!'),
(5515, 74, 20, 'Znovu, nechť zabit jest - jak pořádal!'),
(5516, 74, 21, 'Pak (kolem sebe) pohlížel,'),
(5517, 74, 22, 'obočí vraštil, mračil se,'),
(5518, 74, 23, 'zády se obrátil, s pýchou vzdmul,'),
(5519, 74, 24, 'a řekl: „Toť kouzelnictví jest jen strojené:'),
(5520, 74, 25, 'řeč jest to pouze člověka.“'),
(5521, 74, 26, 'Připékat v žáru pekla budu ho!'),
(5522, 74, 27, 'Kdo poví ti, co žár pekla jest?'),
(5523, 74, 28, 'Neponechává ničeho, aniž ušetří;'),
(5524, 74, 29, 'pokožku lidem spaluje;'),
(5525, 74, 30, 'nad ním devatenácte (strážců) jest:'),
(5526, 74, 31, 'jedině anděly učinili jsme strážci ohně: a počet jich učinili jsme jen pokušením pro ty, kdož neuvěřili, aby ujištěni byli ti, jimž dána byla Kniha, a aby rozmnoženi byli ti, kteří uvěřili, a ve víře své; a aby nebyli v pochybnostech ti, jimž dána byla Kniha, jakož i ti, kteří uvěřili; a aby říkali ti, v jichž srdcích jest choroba a nevěřící: „Co chce Bůh říci tímto podobenstvím?“ Takovýmto způsobem zavádí Bůh v blud, koho chce, a vede (správně) koho chce; nikdo neví (počet) vojsk Pána tvého, mimo něho: totoť pak jest pouze napomenutí lidstvu!'),
(5527, 74, 32, 'Nikoliv! při měsíci,'),
(5528, 74, 33, 'a noci, když odchází,'),
(5529, 74, 34, 'a jitru. když jasní se:'),
(5530, 74, 35, '(přisám, že peklo) zajisté jest jednou z (věcí) nejvážnějších,'),
(5531, 74, 36, 'varováním lidstvu,'),
(5532, 74, 37, 'pro každého z vás, jenž chce vpřed jít, neb opozditi se.'),
(5533, 74, 38, 'Každá duše svých vlastních činů jest zárukou;'),
(5534, 74, 39, 'jenže ti, kdož na pravici budou,'),
(5535, 74, 40, 'v zahradách (ráje) navzájem budou se dotazovati'),
(5536, 74, 41, 'ohledně provinilců,'),
(5537, 74, 42, '(řkouce jim:) „Co dovedlo vás z pekla žár?“'),
(5538, 74, 43, 'Řeknou: „Nebyli jsme z těch, kdož se modlili;'),
(5539, 74, 44, 'aniž jíst jsme nuzným dávali;'),
(5540, 74, 45, 'hádky jsme vedli s těmi, kdož se hádali:'),
(5541, 74, 46, 'vylhaným den poslední jsme nazývali,'),
(5542, 74, 47, 'až když přišla nám (nyní) jistota.“'),
(5543, 74, 48, 'Neprospěje jim pak přímluvčích přímluva —'),
(5544, 74, 49, 'proč od připomínky vzdalovali se,'),
(5545, 74, 50, 'jak by byli osli splašení,'),
(5546, 74, 51, 'přede lvem (tryskem) pádící?'),
(5547, 74, 52, 'Naopak, jedenkaždý z nich chce, aby mu dány byly listy (knih) zvlášť určené!'),
(5548, 74, 53, 'Nikoliv, ale oni nebojí se života budoucího.'),
(5549, 74, 54, 'Nikoliv! Korán zajisté jest připomínkou'),
(5550, 74, 55, 'a kdožkoli chce, nechť si ho připomíná.'),
(5551, 74, 56, 'Však nebudou připomínati si (ho) leda ti, jež Bůh bude chtíti: jemu přístojí, by báli se jej; jemu přístojí odpouštění.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Netřeba přisahati mi při ZMRTVÝCHVSTÁNÍ,'),
(5553, 75, 2, 'aniž přisahati při duši výčitky si činící:'),
(5554, 75, 3, 'zdaž domnívá se člověk, že neshromáždíme kosti jeho?'),
(5555, 75, 4, 'Ano; my s to jsme přesně sestaviti i špičky prstů jeho!'),
(5556, 75, 5, 'Však člověk zapírati chce to, co před sebou má:'),
(5557, 75, 6, 'táže se: „Kdy přijde ten den zmrtvýchvstání?“'),
(5558, 75, 7, 'Však když oslepen bude zrak;'),
(5559, 75, 8, 'a měsíc zatmí se;'),
(5560, 75, 9, 'a spojeno bude slunce s měsícem;'),
(5561, 75, 10, 'tehdy řekne člověk: „Kde útulek nalézti dnes?“'),
(5562, 75, 11, 'Nikoliv, nebude útulku:'),
(5563, 75, 12, 'u Pána tvého v den ten stálé bude stanoviště.'),
(5564, 75, 13, 'Zpraven bude v den onen člověk o tom, co poslal ku předu i o tom, co zadržel;'),
(5565, 75, 14, 'ano, člověk bude sám sobě (svědkem) očitým,'),
(5566, 75, 15, 'a i kdyby omluvy své přednesl —'),
(5567, 75, 16, '„Nehýbej příliš jazykem svým v něm, abys uspíšil jej!'),
(5568, 75, 17, 'Nám zajisté přísluší sebrání a čtení jeho:'),
(5569, 75, 18, 'a když čteme ti jej, následuj čtení jeho!'),
(5570, 75, 19, 'Pak též zajisté připadne nám vysvětlení jeho.“ —'),
(5571, 75, 20, 'Nikoliv! Ale vy milujete život rychle míjící'),
(5572, 75, 21, 'a (stranou) necháváte život budoucí.'),
(5573, 75, 22, 'V den ten tváře budou zářící'),
(5574, 75, 23, 'a k Pánu svému hledící:'),
(5575, 75, 24, 'a budou též tváře se temnící,'),
(5576, 75, 25, 'že stane se s nimi cos strašného, mnící.'),
(5577, 75, 26, 'Nikoli; když (duše) dostoupí hltanu'),
(5578, 75, 27, 'a bude řečeno: „Kdo kouzlo má?“'),
(5579, 75, 28, 'a myslí (člověk), že jest mu loučiti se,'),
(5580, 75, 29, 'a noha s nohou splete se,'),
(5581, 75, 30, 'v den ten k Pánu svému bude hnán:'),
(5582, 75, 31, 'neb nedával almužny, aniž modlil se,'),
(5583, 75, 32, 'nýbrž lhářem nazýval (proroka) a záda obracel,'),
(5584, 75, 33, 'pak ke svým (lidem) hrdě odešel.'),
(5585, 75, 34, 'Běda ti, běda!'),
(5586, 75, 35, 'A znovu běda ti, běda!'),
(5587, 75, 36, 'Zdaž domnívá se člověk, že ponechán bude pánem?'),
(5588, 75, 37, 'Což nebyl kapkou (semene jen) vystříknutého?'),
(5589, 75, 38, 'Pak krví sraženou byl, z níž (Bůh) stvořil jej a urovnal?'),
(5590, 75, 39, 'A pohlavím učinil jej dvojím, mužským, a ženským.'),
(5591, 75, 40, 'Není-liž pak (On) dost mocným též, aby vzkřísil mrtvé?');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Zdaž přišel kdy na člověka průběh ČASU, kdy věcí nebyl vzpomenutou?'),
(5593, 76, 2, 'Zajistéť stvořili jsme ČLOVĚKA z kapky (semene) smíseného, zkoušejíce jej; a učinili jsme jej slyšícím, vidoucím:'),
(5594, 76, 3, 'zajisté na (pravou) stezku uvedli jsme jej, ať již vděčným byl by; či nevděčným.'),
(5595, 76, 4, 'Zajisté pak schystali jsme pro nevěřící řetězy a pouta a žár plamenný:'),
(5596, 76, 5, 'ctnostní pak píti budou z číše, s příměskem kafrovým'),
(5597, 76, 6, 'z pramene, z nějž píti budou služebníci boží, kamkoli zavedše jej vyvřením:'),
(5598, 76, 7, 'ti, kdož plní sliby své a bojí se dne, jehož bědy (daleko) se rozlétnou,'),
(5599, 76, 8, 'a krmí pokrmem z lásky k Bohu, nuzného a sirotka a zajatce,'),
(5600, 76, 9, '(řkouce): „Zajisté krmíme vás z lásky k Bohu; nechceme od vás odměny ni velkých díků.'),
(5601, 76, 10, 'Myť bojíme se od Pána svého dne mračivého, hrůzyplného.“'),
(5602, 76, 11, 'I ochránil je Bůh od běd dne onoho a vložil na ně jas tváří a rozradování:'),
(5603, 76, 12, 'a odměnil je za trpělivost jejich zahradou (ráje) a (rouchem) hedvábným.'),
(5604, 76, 13, 'Spočívati v ní (budou) na lůžkách: viděti nebudou tam slunce žár, vanu ledového;'),
(5605, 76, 14, 'blízky nad nimi stíny (stromů budou) a velmi nízko (vyseti budou) plody jejich;'),
(5606, 76, 15, 'a obnášeni budou džbány stříbrnými a číšemi, jak konvicemi —'),
(5607, 76, 16, 'konvicemi ze stříbra, jež plniti budou mírou svou.'),
(5608, 76, 17, 'A napájeni budou z poháru, s příměskem zázvorným —'),
(5609, 76, 18, 'z pramene v ráji, zvaného Selsebíl.'),
(5610, 76, 19, 'Obcházeni pak budou mládci věcně mladými: když hledíš na ně, měl bys je za perly rozsypané.'),
(5611, 76, 20, 'A když (toto) uzříš, uzříš rozkoš a panství veliké.'),
(5612, 76, 21, 'Na nich budou roucha z hedvábí zeleného a brokátu; a ozdobeni budou náramky stříbrnými; a napájeti bude je Pán jejich nápojem přečistým.'),
(5613, 76, 22, 'Toto vše bude odměnou vám: a námaha vaše odvděčena bude.'),
(5614, 76, 23, 'Myť zajisté seslali jsme Korán tobě sesláním.'),
(5615, 76, 24, 'Pročež vyčkávej trpělivě rozsudek Pána svého a neposlouchej hříšných mezi nimi, aniž nevěrců.'),
(5616, 76, 25, 'Ale vzpomínej jména Pána svého za zory ranní i s večerem:'),
(5617, 76, 26, 'též za noci; i padej na tvář svou před ním a chval jej noci za dlouhé.'),
(5618, 76, 27, 'Zajisté pak tito milují (život) rychle míjící a nechávají za sebou den (soudu) přetěžký.'),
(5619, 76, 28, 'My stvořili jsme je a posílili klouby jejich; a kdybychom chtěli, zaměniti bychom mohli je jim podobnými (rychlou) záměnou.'),
(5620, 76, 29, 'Zajisté pak toto jest připomínka: pročež kdo chce, nechť k Pánu svému cestu nastoupí.'),
(5621, 76, 30, 'Však nebudete chtíti toho, ledaže by Bůh tomu chtěl: Bůh zajisté vševědoucí jest a moudrý.'),
(5622, 76, 31, 'Uvádí, koho chce, v milosrdenství své; a pro nepravostné přichystal trest bolestný!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Při pořadem POSLANÝCH,'),
(5624, 77, 2, 'a spěchem spějících,'),
(5625, 77, 3, 'a hodem rozhazujících, a předělem rozlišujících,'),
(5626, 77, 4, 'a napomenutí vrhajících'),
(5627, 77, 5, 'omluv či výstrahy,'),
(5628, 77, 6, '(přisám:) zajisté to, co slibováno jest vám, jistě udá se!'),
(5629, 77, 7, 'Když pak hvězdy budou zhasnuty,'),
(5630, 77, 8, 'a nebesa budou rozpoltěna,'),
(5631, 77, 9, 'a když hory budou rozprášeny,'),
(5632, 77, 10, 'a čas jistý určen pude prorokům —'),
(5633, 77, 11, 'ke kterému dni (určena) bude lhůta?'),
(5634, 77, 12, 'Ke dni to rozhodnutí!'),
(5635, 77, 13, 'A kdo poví ti, co den jest rozhodnutí?'),
(5636, 77, 14, 'Běda v den onen těm, kdož zvou to lží!'),
(5637, 77, 15, 'Což nezahladili jsme předešlé,'),
(5638, 77, 16, 'pak (jiným) dali po nich následovati?'),
(5639, 77, 17, 'Taktéž učiníme i s provinilými!'),
(5640, 77, 18, 'Běda v den onen těm, kdož zvou to lží!'),
(5641, 77, 19, 'Což nestvořili jsme vás z vody prabídné,'),
(5642, 77, 20, 'již vložili jsme v místo bezpečné,'),
(5643, 77, 21, 'až k době předem určené?'),
(5644, 77, 22, 'A mohli jsme to!'),
(5645, 77, 23, 'Jak mocní jsme!'),
(5646, 77, 24, 'Běda v den onen těm, kdož zvou to lží!'),
(5647, 77, 25, 'Což neučinili jsme zemi schránou jen,'),
(5648, 77, 26, 'živým i mrtvým?'),
(5649, 77, 27, 'A na ní učinili jsme hor temena vysoká, a pitím vodu čerstvou dali vám?'),
(5650, 77, 28, 'Běda v den onen těm, kdož zvou to lží!'),
(5651, 77, 29, 'Táhněte k tomu, co lží jste nazvali!'),
(5652, 77, 30, 'Táhněte k stínu trosloupovému,'),
(5653, 77, 31, 'jenž nestíní, aniž chrání proti plamenům:'),
(5654, 77, 32, 'jiskry metá, jak hrady veliké,'),
(5655, 77, 33, 'podobné velbloudům žlutavým.'),
(5656, 77, 34, 'Běda v den onen těm, kdož zvou to lží!'),
(5657, 77, 35, 'Toto jest den, kdy ani nehlesnou,'),
(5658, 77, 36, 'aniž dovoleno bude jim, by omlouvali se.'),
(5659, 77, 37, 'Běda v den onen těm, kdož zvou to lží!'),
(5660, 77, 38, 'Totoť den rozhodnutí jest: shromáždili jsme vás i předešlé;'),
(5661, 77, 39, 'máte-li pak lest nějakou, nuž kujte ji proti mně!'),
(5662, 77, 40, 'Běda v den onen těm, kdož zvou to lží!'),
(5663, 77, 41, 'Bohabojní zajisté ve stínech budou a mezi prameny'),
(5664, 77, 42, 'a ovocem, jež přáti budou si:'),
(5665, 77, 43, '„Jezte a pijte ve zdraví, v odměnu za skutky své;'),
(5666, 77, 44, 'zajisté takto odměňujeme ty, kdož dobře činí!“'),
(5667, 77, 45, 'Běda v den onen těm,. kdož zvou to lží!'),
(5668, 77, 46, '„Jezte a radujte se (ze světa) na krátko: zajistéť provinilci jste!“'),
(5669, 77, 47, 'Běda v den onen těm, kdož zvou to lží!'),
(5670, 77, 48, 'A když řečeno jest jim: „Klekněte!“, neklekají.'),
(5671, 77, 49, 'Běda v den onen těm, kdož zvou to lží!'),
(5672, 77, 50, 'Ve kterou rozpravu po této uvěří?');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'O čem že navzájem se dotazují?'),
(5674, 78, 2, 'Ohledně ZVĚSTI veliké,'),
(5675, 78, 3, 'níž (míněním) se rozcházejí.'),
(5676, 78, 4, 'Vskutku však zvědí!'),
(5677, 78, 5, 'Pak opět: vskutku však zvědí!'),
(5678, 78, 6, 'Což neučinili jsme zemi pohovkou'),
(5679, 78, 7, 'a hory (na ní) podpěrou?'),
(5680, 78, 8, 'A stvořili jsme v párech vás;'),
(5681, 78, 9, 'a spánek učinili jsme vám odpočinkem;'),
(5682, 78, 10, 'a noc učinili jsme vám pokrývkou;'),
(5683, 78, 11, 'a den učinili jsme vám pro (hledání) živobytí;'),
(5684, 78, 12, 'a nad vámi postavili jsme sedmero (nebes) mohutných;'),
(5685, 78, 13, 'a postavili tam svítilnu planoucí;'),
(5686, 78, 14, 'a s mračen seslali jsme vodu v hojnosti,'),
(5687, 78, 15, 'abychom dali ní vzrůsti obilí a bylinám'),
(5688, 78, 16, 'a hustě (vzrostlým) zahradám.'),
(5689, 78, 17, 'Zajisté den rozhodnutí má čas svůj určený:'),
(5690, 78, 18, 'den, kdy zatroubeno bude v roh a přijdete v zástupech;'),
(5691, 78, 19, 'a otevřeno bude nebe branami četnými;'),
(5692, 78, 20, 'a rozhýbány budou hory, přeludům pouště podobné;'),
(5693, 78, 21, 'peklo pak bude (lstivě) číhati,'),
(5694, 78, 22, 'pro nepravostné obydlí,'),
(5695, 78, 23, 'v němž dlíti budou po věky;'),
(5696, 78, 24, 'chládku v něm neokusí, aniž nápoje,'),
(5697, 78, 25, 'jen vodu vroucí a ran hnis,'),
(5698, 78, 26, 'odměnu (jistě) případnou!'),
(5699, 78, 27, 'Neb nedoufali v den účtů skládání'),
(5700, 78, 28, 'a odmítali znamení naše, lží zvouce je.'),
(5701, 78, 29, 'Však vše to spočetli jsme, sepsali:'),
(5702, 78, 30, '„Okuste trestu nyní, rozmnožíme vám jen muky (vaše)!“'),
(5703, 78, 31, 'Však bohabojným dán jest byt blažený:'),
(5704, 78, 32, 'zahrady a vinice;'),
(5705, 78, 33, 'a (děvy) prsou okrouhlých; jim rovné věkem;'),
(5706, 78, 34, 'a plné poháry:'),
(5707, 78, 35, 'tam nebudou slyšet řečí pustých, aniž lží:'),
(5708, 78, 36, 'odměnou od Pána tvého — dar to stačící!'),
(5709, 78, 37, 'Od Pána nebes a země a všeho, což mezi nimi jest, Milosrdného: však slova od něho nedostanou.'),
(5710, 78, 38, 'V den, kdy postaví se duch i andělé šikem, nebudou mluviti, vyjma toho, jemuž dovolí Milosrdný, a jenž mluviti bude jen co správno jest.'),
(5711, 78, 39, 'Tentoť jest den pravdivý: pročež kdo chce, nechť k Pánu svému cestu nastoupí.'),
(5712, 78, 40, 'Myť zajisté varovali jsme vás před trestem blízkým: v den, kdy pohlížeti bude člověk na skutky, kupředu poslané rukama jeho a kdy řekne nevěřící: „Kéž byl bych prachem (jen)!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Při silou VYTRHUJÍCÍCH,'),
(5714, 79, 2, 'při volně uvolňujících,'),
(5715, 79, 3, 'při lehce plujících,'),
(5716, 79, 4, 'při předem předcházejících,'),
(5717, 79, 5, 'při osudy všeho rovnajících (přisám):'),
(5718, 79, 6, 'v den, kdy zazvučí (roh) zvučící,'),
(5719, 79, 7, 'druhé přijde po něm (troubení):'),
(5720, 79, 8, 'v den ten srdce chvěti budou se (bázní);'),
(5721, 79, 9, 'zraky jich budou sklopeny.'),
(5722, 79, 10, 'Říkati budou (nevěřící): „Budeme jistě, když zakopáni jsme, nazpět vráceni?'),
(5723, 79, 11, 'Nyní, když kosti jsme (jen) zpuchřelé?“'),
(5724, 79, 12, 'Řeknou: „Toť návrat byl by záhubný.“'),
(5725, 79, 13, 'Však pouze jediné se ozve (varovné) zavřesknutí'),
(5726, 79, 14, 'a již budou na země povrchu!'),
(5727, 79, 15, 'Zdaž děj Mojžíšův tě došel?'),
(5728, 79, 16, 'Když Pán jeho v posvátném údolí Tuwá naň volal:'),
(5729, 79, 17, '„Jdi k Faraonovi, neb svévolným se stal“'),
(5730, 79, 18, 'a rci: „Zdaž očistiti bys se chtěl,'),
(5731, 79, 19, 'tak abych vedl tě k Pánu tvému a ty se (jej) bál!“'),
(5732, 79, 20, 'Pak znamení převelké mu ukázal —'),
(5733, 79, 21, 'však neposlechl, lhářem jej nazval:'),
(5734, 79, 22, 'poté pak odvrátil se, nemeškal:'),
(5735, 79, 23, '(lid) shromáždil a (takto) zavolal,'),
(5736, 79, 24, 'řka: „Jáť Pánem vaším jsem nejvyšším.“'),
(5737, 79, 25, 'I zachvátil jej Bůh trestem života budoucí ho i tohoto.'),
(5738, 79, 26, 'V tom zajisté naučení jest pro toho, kdož bojí se.'),
(5739, 79, 27, 'Zdaž těžším bylo stvořit vás neb nebe, jež Bůh zbudoval?'),
(5740, 79, 28, 'Do výše pozdvihl je, pak je urovnal:'),
(5741, 79, 29, 'a ztemnil jeho noc, a jeho půldne vznítil;'),
(5742, 79, 30, 'poté pak zemi rozprostřel'),
(5743, 79, 31, 'a vyvříti dal vodě z ní, a pastvám vzrůsti na ní dal:'),
(5744, 79, 32, 'a hory na ní upevnil:'),
(5745, 79, 33, 'k užitku vám a dobytčatům vašim.'),
(5746, 79, 34, 'Když pak přijde rozvrácení převelké;'),
(5747, 79, 35, 'v den, kdy rozpomene se člověk na to, co byl (usilovně) konal;'),
(5748, 79, 36, 'a předveden bude plam pekla všem vidícím —'),
(5749, 79, 37, 'tehdy ten, kdo svévolný byl,'),
(5750, 79, 38, 'a život pozemský radši měl,'),
(5751, 79, 39, 'plam pekla bytem dostane;'),
(5752, 79, 40, 'však ten, jenž Pána svého veleby se bál a duši svou před choutkami chránil:'),
(5753, 79, 41, 'zahradu ráje bytem dostane.'),
(5754, 79, 42, 'Dotazovati se budou tě na hodinu (soudu) : na kdy že určena jest?'),
(5755, 79, 43, 'Však jakou vědomost máš o ní?'),
(5756, 79, 44, 'Konečná lhůta její závisí na Pánu tvém;'),
(5757, 79, 45, 'tys varovatelem jen tomu; kdož bojí se jí.'),
(5758, 79, 46, 'V den, kdy uzří ji, (připadne jim) jako by byli v hrobě dleli jen večer neb rána dne onoho.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'ZACHMUŘIL SE a odvrátil,'),
(5760, 80, 2, 'že slepec k němu přišel:'),
(5761, 80, 3, 'však co mohlo ti říci, že by snad se nepolepšil,'),
(5762, 80, 4, 'aneb napomenutí, tak aby prospělo mu, si nerozvážil?'),
(5763, 80, 5, 'Pokud se týče bohatce,'),
(5764, 80, 6, 'k němu pozorně ses choval:'),
(5765, 80, 7, 'a nehledíš na to, že se nepolepšil.'),
(5766, 80, 8, 'Však pokud se týče toho, jenž přichází k tobě s úsilím,'),
(5767, 80, 9, 'a (Boha) bojí se,'),
(5768, 80, 10, 'jej zanedbáváš.'),
(5769, 80, 11, 'Ne tak! Korán zajisté jest připomínkou, -'),
(5770, 80, 12, 'a kdožkoli chce, nechť si jej připomíná —'),
(5771, 80, 13, '(psaný) na listech převzácných,'),
(5772, 80, 14, 'vznešených, přečistých,'),
(5773, 80, 15, 'rukama písařů,'),
(5774, 80, 16, 'převzácných, bez viny.'),
(5775, 80, 17, 'Zabit budiž člověk! Jak zatvrzelý jest!'),
(5776, 80, 18, 'Z čeho že Bůh jej stvořil?'),
(5777, 80, 19, 'Z kapky (semene) dle (určeného) rozměru ho stvořil;'),
(5778, 80, 20, 'pak cestu mu usnadnil;'),
(5779, 80, 21, 'pak zemřít dá mu i pohřbí ho;'),
(5780, 80, 22, 'pak, když chtíti bude, vzkřísí ho.'),
(5781, 80, 23, 'Však ne - on dosud nesplnil, co Bůh mu byl nakázal!'),
(5782, 80, 24, 'Nechť jen pohlédne na pokrm svůj:'),
(5783, 80, 25, 'my zajisté vodu v proudech dštíme mu;'),
(5784, 80, 26, 'pak zemi pukat dáváme v puklinách,'),
(5785, 80, 27, 'i vzrůsti dáváme v ní obilí;'),
(5786, 80, 28, 'a révě též a jeteli;'),
(5787, 80, 29, 'a olivovníku a datlovníku;'),
(5788, 80, 30, 'a zahradám hustě porostlým;'),
(5789, 80, 31, 'a ovoci a travinám:'),
(5790, 80, 32, 'k užitku vám a dobytčatům vašim.'),
(5791, 80, 33, 'Ale když přijde děsný (trouby) vřesk,'),
(5792, 80, 34, 'v den, kdy člověk bude prchati před bratrem svým;'),
(5793, 80, 35, 'a matkou svou, a otcem svým;'),
(5794, 80, 36, 'a ženou svou a dětmi svými:'),
(5795, 80, 37, '(v den ten) každý člověk bude sám (s sebou) míti práce dosti.'),
(5796, 80, 38, 'V den ten budou tváře zářící,'),
(5797, 80, 39, 'smějící se, radující se:'),
(5798, 80, 40, 'a budou v den onen tváře prachem pokryté —'),
(5799, 80, 41, 'halit je bude černota:'),
(5800, 80, 42, 'toť budou nevěrci, ti zavilí!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Když slunce bude SLOŽENO;'),
(5802, 81, 2, 'a hvězdy budou padati;'),
(5803, 81, 3, 'a pohoří se hýbati;'),
(5804, 81, 4, 'a velbloudice budou opuštěny;'),
(5805, 81, 5, 'a divá zvěř bude shromážděna;'),
(5806, 81, 6, 'a moře budou varem vřít;'),
(5807, 81, 7, 'a duše (ke svým tělům) budou pojeny;'),
(5808, 81, 8, 'a (dcerka) za živa zahrabaná bude tázána,'),
(5809, 81, 9, 'pro jakou vinu byla zabita;'),
(5810, 81, 10, 'když listy (knih) budou rozbaleny;'),
(5811, 81, 11, 'a nebesa stranou stažena;'),
(5812, 81, 12, 'a pekla plamen rozdmýchán;'),
(5813, 81, 13, 'zahrada ráje pak přiblížena:'),
(5814, 81, 14, 'tu (každá) duše zví, co vykonala.'),
(5815, 81, 15, 'Aniž přisáhnout (nutno mi) při hvězdách zpátečných'),
(5816, 81, 16, 'jež běží rychle a skrývají se;'),
(5817, 81, 17, 'aniž při noci, když temno nadchází,'),
(5818, 81, 18, 'anebo jitru, když dechu nabírá —'),
(5819, 81, 19, 'že (Korán) jest rčením, jež pronesl posel vzácný,'),
(5820, 81, 20, 'mocný před Pánem trůnu, pevný,'),
(5821, 81, 21, 'jenž poslouchán jest a věrný.'),
(5822, 81, 22, 'Neníť soudruh váš Džinny posedlý,'),
(5823, 81, 23, 'nýbrž viděl jej na obzoru jasném;'),
(5824, 81, 24, 'aniž pro sebe chce podržeti věci skryté,'),
(5825, 81, 25, 'aniž slova opakuje, jež vyřkl Satan kamenovaný.'),
(5826, 81, 26, 'Kam tedy odcházíte?'),
(5827, 81, 27, '(Korán) jest pouze Napomenutím veškerenstvu;'),
(5828, 81, 28, 'těm z vás, kdož přímou chtějí stezkou jít;'),
(5829, 81, 29, 'však nebudete toho chtít, leda by Bůh chtěl tomu, Pán světů veškerých.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Když nebesa se ROZPOLTÍ;'),
(5831, 82, 2, 'a hvězdy budou rozprášeny;'),
(5832, 82, 3, 'a moře splynou do sebe;'),
(5833, 82, 4, 'a hroby zdola navrch budou převráceny:'),
(5834, 82, 5, '(tehdy) zví duše, co byla učinila, i co byla zameškala.'),
(5835, 82, 6, 'Ó člověče! co zmámilo tě ohledně Pána tvého vzácného,'),
(5836, 82, 7, 'jenž stvořil tě a vyrovnal a správnou (podobu) ti dal;'),
(5837, 82, 8, 'v jakékoliv pak podobě chtěl, tě sestavil?'),
(5838, 82, 9, 'Nikoliv! naopak, vylhaným nazývají soud (poslední)!'),
(5839, 82, 10, 'Však zajisté nad vámi strážci stojí,'),
(5840, 82, 11, 'vzácní, (vše) píšící;'),
(5841, 82, 12, 'co konáte (dobře) vědí!'),
(5842, 82, 13, 'Ctnostní v rozkošném budou (bydlení),'),
(5843, 82, 14, 'zavilí pak v pekla plameni,'),
(5844, 82, 15, 'péci budou se v den soudu (poslední):'),
(5845, 82, 16, 'z něj nebudou se moci vzdáliti!'),
(5846, 82, 17, 'Kdo poví ti, co jest den soudu (poslední)?'),
(5847, 82, 18, 'A znovu: kdo poví ti, co jest den soudu (poslední)?'),
(5848, 82, 19, 'Den, kdy, nebude duše moci učiniti pro jinou duši praničeho: rozhodnutí v den ten patřiti bude Bohu !');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Běda UTRHÁVAJÍCÍM,'),
(5850, 83, 2, 'kteří, když měřeno jest jim, na lidech míru plnou žádají,'),
(5851, 83, 3, 'však měří-li jim sami aneb váží, je poškozují!'),
(5852, 83, 4, 'Což onino, že vzkříšeni budou se nedomnívají,'),
(5853, 83, 5, 'ke dni velkému,'),
(5854, 83, 6, 'ke dni, kdy lidé stanou před Pánem světů veškerých?'),
(5855, 83, 7, 'Nikoliv; zajisté v Sidždžínu jest seznam (všech) zavilých!'),
(5856, 83, 8, 'A kdo poví ti, co Sidždžín jest?'),
(5857, 83, 9, 'Kniha (zřetelně) popsaná:'),
(5858, 83, 10, 'běda v den onen těm, kdož zvou to lží,'),
(5859, 83, 11, 'kdož zovou vylhaným den soudu (poslední)!'),
(5860, 83, 12, 'Nikdo vylhaným jej nezve, vyjma přestupníků, hříšných,'),
(5861, 83, 13, 'kteří dí: „Báchorky starých!“ když předčítána Jsou jim naše znamení.'),
(5862, 83, 14, 'Nikoliv: však srdce zastřela jim jejich konání!'),
(5863, 83, 15, 'Nikoliv: zajisté v den onen clonou skryti budou před Pána svého spatřením.'),
(5864, 83, 16, 'Pak opékáni budou v pekla plameni.'),
(5865, 83, 17, 'Pak řečeno (jim) bude: „Toto jest, co zvali jste (kdys) vylhaným!“'),
(5866, 83, 18, 'Nikoliv: zajisté pak seznam ctnostných jest v \'Illijjúnu.'),
(5867, 83, 19, 'A kdo poví ti, co jest \'Illijjún?'),
(5868, 83, 20, 'Kniha (zřetelně) popsaná;'),
(5869, 83, 21, 'svědky psaní v ní jsou (andělé) nejbližší.'),
(5870, 83, 22, 'Ctnostní zajisté budou v rozkoši,'),
(5871, 83, 23, 'na pohovkách sedíce rozhlížeti budou se:'),
(5872, 83, 24, 'seznáš na tvářích jejich svěží zář rozkoše.'),
(5873, 83, 25, 'Píti budou víno vzácné, zapečetěné'),
(5874, 83, 26, 'pečetí pižmovou: o toto nechť soutěží ti, kdož soutěží!'),
(5875, 83, 27, 'Smísené bude s vodou Tasnímu,'),
(5876, 83, 28, 'pramene, z nějž pijí (andělé) nejbližší.'),
(5877, 83, 29, 'Zajisté provinilci těm, kdož uvěřili, se vysmívali;'),
(5878, 83, 30, 'když mimo nich šli, na sebe mrkali;'),
(5879, 83, 31, 'když vraceli se k lidu svému, s výsměchem se vraceli;'),
(5880, 83, 32, 'když viděli je, říkali: „Oninoť zajisté jsou pobloudilci.“'),
(5881, 83, 33, 'A přece, aby střehli je, posláni nebyli.'),
(5882, 83, 34, 'Však nevěrcům vysmějí se ti, kdož uvěřili,'),
(5883, 83, 35, '(když) na pohovkách sedíce, rozhlížeti budou se:'),
(5884, 83, 36, '„Zdaž nevěrci odměněni byli dle toho, co konali?“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Když ROZŠTĚPÍ se nebesa'),
(5886, 84, 2, 'a poslechnou Pána svého, povinnost k němu plníce:'),
(5887, 84, 3, 'a (na rovno) se země rozestře'),
(5888, 84, 4, 'a vyvrhne vše, co v ní jest a prázdnou zůstane:'),
(5889, 84, 5, 'a poslechne Pána svého, povinnost k němu plníc:'),
(5890, 84, 6, 'tehdy zajisté, ó člověče, jenž mocně přeješ si dosíci Pána svého, setkáš se s ním.'),
(5891, 84, 7, 'A pokud se týče toho, jemuž dána bude kniha jeho do pravice,'),
(5892, 84, 8, 's tím súčtováno bude súčtováním snadným'),
(5893, 84, 9, 'a vrátí se k rodině své pln radosti.'),
(5894, 84, 10, 'A pokud se týče toho, jemuž dána bude kniha jeho za záda,'),
(5895, 84, 11, 'ten volati bude zničení,'),
(5896, 84, 12, 'však péci se bude v plameni;'),
(5897, 84, 13, 'zajisté na zemi žil s rodinou svojí pln radosti'),
(5898, 84, 14, 'a zajisté mnil, že (k Bohu) se (nikdy) nevrátí.'),
(5899, 84, 15, 'Ale ano! Pán jeho jistě dobře jej obzíral.'),
(5900, 84, 16, 'I netřeba mi, bych přisáhl při, soumraku;'),
(5901, 84, 17, 'a noci a tom, co hromadí;'),
(5902, 84, 18, 'a luně, když jest v úplňku,'),
(5903, 84, 19, 'že dojista pojedete od stupně ke stupni.'),
(5904, 84, 20, 'Proč tedy vlastně nevěří?'),
(5905, 84, 21, 'A když čteno jest jim Čtení, na tváře své nepadají?'),
(5906, 84, 22, 'Naopak ti, kdož neuvěřili, zovou je vylhaným!'),
(5907, 84, 23, 'Však Bůh dobře ví a tom, co skrývají:'),
(5908, 84, 24, 'i zvěstuj jim trest bolestný,'),
(5909, 84, 25, 'vyjma těch, kdož uvěřili a konali dobré skutky: těm odměna dána bude bezvýhradná.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Při nebi, plném SOUHVĚZDÍ:'),
(5911, 85, 2, 'a při dnu slíbeném;'),
(5912, 85, 3, 'a při svědku a dosvědčeném;'),
(5913, 85, 4, '(přisám:) nechť zhynou vládci zákopu,'),
(5914, 85, 5, 'ohně, jenž (stále) živen, plál,'),
(5915, 85, 6, 'když kolem něho seděli,'),
(5916, 85, 7, 'aby sami svědky byli toho, co s věřícími dělali.'),
(5917, 85, 8, 'A mstili se jim tak jen proto, že v Boha, Mocného, Chvályhodného věřili,'),
(5918, 85, 9, 'jemuž přináleží panství nebes i země; a Bůh všech věcí svědkem jest.'),
(5919, 85, 10, 'Zajisté pak těm, kdož na tuto zkoušku postavili věřící muže a ženy a pak nekáli se, (schystán) jest trest pekla a trest sžíhání.'),
(5920, 85, 11, 'Však zajisté těm, kdož uvěřili a konali dobré skutky, (schystány) jsou zahrady, pod nimiž řeky tekou; toť blaho bude veliké!'),
(5921, 85, 12, 'Zajisté udeření Pána tvého přísné bude:'),
(5922, 85, 13, 'zajisté onť jest to, jenž (všemu) dává vznik a k sobě dává navraceti:'),
(5923, 85, 14, 'onť velkým v odpouštění jest a láskyplný:'),
(5924, 85, 15, 'Pán trůnu vznešeného,'),
(5925, 85, 16, 'Konatel toho, co chce.'),
(5926, 85, 17, 'Zdaž došla tě zpráva: o vojích'),
(5927, 85, 18, 'Faraona a (lidu) Tsemúd?'),
(5928, 85, 19, 'Však nevěřící to zovou lží —'),
(5929, 85, 20, 'co Bůh ze zadu jich je obklopuje.'),
(5930, 85, 21, 'Však toto jest Korán vznešený,'),
(5931, 85, 22, '(zapsaný) na desce uschované!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Při nebi a HVĚZDĚ NOČNÍ!'),
(5933, 86, 2, 'A co poví ti, co jest hvězda noční?'),
(5934, 86, 3, 'Hvězda pronikavě (zářivá).'),
(5935, 86, 4, 'Zajisté každá duše nad sebou strážce má.'),
(5936, 86, 5, 'Nechť pohlédne jen člověk na to, z čeho stvořen byl.'),
(5937, 86, 6, 'Stvořen byl z vody vystřiklé,'),
(5938, 86, 7, 'z prostřed ledví a kostí hrudních vyšlé.'),
(5939, 86, 8, 'Pročež jistě (Bůh) moc má; by vrátil jej (k životu),'),
(5940, 86, 9, 'v den, kdy zkoumána budou tajemství,'),
(5941, 86, 10, 'a kdy síly míti (člověk) nebude, aniž pomocníka.'),
(5942, 86, 11, 'Při nebi, jež se otáčí;'),
(5943, 86, 12, 'při zemi, jež se rozpukává,'),
(5944, 86, 13, '(přísám, že Korán) zajisté jest slovo rozhodné,'),
(5945, 86, 14, 'není Jen žertem (míněno).'),
(5946, 86, 15, 'Oni pak kují léčky své —'),
(5947, 86, 16, 'a já též kuji léčky své!'),
(5948, 86, 17, 'Pomalu jednej s nevěrci: poshov jim (ještě) chvilenku!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Chválu pěj jména Pána svého NEJVYŠŠÍHO,'),
(5950, 87, 2, 'jenž vše stvořil a srovnal,'),
(5951, 87, 3, 'vše předurčil a vede;'),
(5952, 87, 4, 'jenž vzrůsti dává pastvinám'),
(5953, 87, 5, 'a mění je pak v stébla vyschlá.'),
(5954, 87, 6, 'Naučíme tě čísti (Korán) a nezapomeneš,'),
(5955, 87, 7, 'leda co Bůh by chtěl: Bůh zajisté zná věci skryté i veřejné:'),
(5956, 87, 8, 'a usnadníme ti cestu k snadnému.'),
(5957, 87, 9, 'Pročež napomínej, neb napomenutí tvé jest prospěšné.'),
(5958, 87, 10, 'Pamětliv bude ho ten, kdo (Boha) bojí se:'),
(5959, 87, 11, 'a straniti bude se ho jen ten nejbídnější,'),
(5960, 87, 12, 'jenž péci se bude v ohni mohutném;'),
(5961, 87, 13, 'v něm pak nezemře, aniž bude žíti.'),
(5962, 87, 14, 'Však dobře povede se očištěnému,'),
(5963, 87, 15, 'jenž pomní jména Pána svého i modlí se.'),
(5964, 87, 16, 'Nicméně radši máte život pozemský;'),
(5965, 87, 17, 'ač budoucí lepší jest a stálejší.'),
(5966, 87, 18, 'To vše jest v listech (knih) předešlých,'),
(5967, 87, 19, 'v listech (knih) Abrahama i Mojžíše.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Zdaž došla tě zpráva o (dnu) ZAHALUJÍCÍM?'),
(5969, 88, 2, 'V den ten líce budou pokorná,'),
(5970, 88, 3, 'upracována, znavena;'),
(5971, 88, 4, 'páliti budou se v ohni horoucím,'),
(5972, 88, 5, 'píti budou z pramene vřelého.'),
(5973, 88, 6, 'Nebudou pokrmu míti, vyjma trnitého bodláčí,'),
(5974, 88, 7, 'jež nevykrmí (je) aniž ukojí (jich) hlad.'),
(5975, 88, 8, 'V den ten líce (též) budou veselá,'),
(5976, 88, 9, 'se snahou svou spokojená,'),
(5977, 88, 10, 'v zahradách vysokých:'),
(5978, 88, 11, 'v ní neslyšán bude hovor zlý;'),
(5979, 88, 12, 'v ní prameny budou zurčící,'),
(5980, 88, 13, 'pohovky vyvýšené,'),
(5981, 88, 14, 'a číše připravené,'),
(5982, 88, 15, 'polštáře urovnané,'),
(5983, 88, 16, 'koberce rozprostřené.'),
(5984, 88, 17, 'Zdaž nepatří na velblouda, jak (moudře) stvořen byl,'),
(5985, 88, 18, 'a na nebesa, jak byla zdvižena,'),
(5986, 88, 19, 'a na pohoří jak byla vztyčena,'),
(5987, 88, 20, 'a na zemi, jak byla prostřena?'),
(5988, 88, 21, 'Napomínej tedy, neb tys jen napominatel,'),
(5989, 88, 22, 'nemáš nad nimi práva (plného),'),
(5990, 88, 23, 'vyjma toho, jenž odvrací se a nevěří:'),
(5991, 88, 24, 'toho pak ztresce Bůh největším ztrestáním!'),
(5992, 88, 25, 'Zajistéť k nám jich návrat (povede):'),
(5993, 88, 26, 'pak zajisté na nás bude súčtování s nimi.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Při JITŘENCE'),
(5995, 89, 2, 'a nocích deseti;'),
(5996, 89, 3, 'při dvojitém a jednoduchém;'),
(5997, 89, 4, 'a při noci, když ubíhá!'),
(5998, 89, 5, 'Zdaž není v tomto přísaha pro muže rozumného?'),
(5999, 89, 6, 'Což neviděl‘s, jak naložil Pán tvůj s (kmenem) ‘Ád;'),
(6000, 89, 7, 'v Iramu, plném sloupoví,'),
(6001, 89, 8, 'jemuž rovného nebylo vytvořeno v (této) zemi?'),
(6002, 89, 9, 'A s (kmenem) Tsemúd, jenž tesal si (domy) ve skále v údolí?'),
(6003, 89, 10, 'A s Faraonem, jenž na kůl narážel?'),
(6004, 89, 11, 'Všichni svévoli v zemi páchali'),
(6005, 89, 12, 'a pohoršení (jen) v ní množili.'),
(6006, 89, 13, 'I snesl na ně Pán tvůj trestu bič,'),
(6007, 89, 14, 'neb Pán tvůj stále na číhané jest.'),
(6008, 89, 15, 'A pokud se týče člověka, kdykoli zkouší jej Pán jeho tím, že ctí zahrnuje jej a milostí, říká: „Poctil mne Pán můj.“'),
(6009, 89, 16, 'A když zkouší jej tím, že odměřuje mu dary své, říká: „Pohanil mne Pán můj!“'),
(6010, 89, 17, 'Nikoliv! Ale vy nectíte sirotka,'),
(6011, 89, 18, 'aniž vybízíte se navzájem k dávání krmě nuznému;'),
(6012, 89, 19, 'a hltáte dědictví (slabých) dravým hltáním;'),
(6013, 89, 20, 'a milujete bohatství láskou přílišnou.'),
(6014, 89, 21, 'Ale ne: když země roztlučena bude ranou za ranou:'),
(6015, 89, 22, 'a přijde Pán tvůj i andělé, v šiku za šikem:'),
(6016, 89, 23, 'a předvedeno bude v den ten peklo: - v den ten rozvažovati bude člověk! Však co plátno bude mu pak rozvažování?'),
(6017, 89, 24, 'Řekne pak: „Kéž byl bych pro svůj (nynější) život (dobré skutky) předeslal!“'),
(6018, 89, 25, 'V den onen pak nikdo takovým trestem neztresce,'),
(6019, 89, 26, 'aniž kdo do takových spoutá pout!'),
(6020, 89, 27, 'Ó duše uklidněná,'),
(6021, 89, 28, 'navratiž se k Pánu svému,uspokojená, uspokojující —'),
(6022, 89, 29, 'vstupiž pak mezi služebníky mé,'),
(6023, 89, 30, 'a vstup do (zahrad) ráje mého!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Přisahati mi netřeba při této KONČINĚ, -'),
(6025, 90, 2, 'neb ty přece bydlíš v této končině —'),
(6026, 90, 3, 'ni při otci a tom; co zplozuje!'),
(6027, 90, 4, 'Vždyť člověka my stvořili jsme v soužení;'),
(6028, 90, 5, 'což za to má, že přemoci jej nikdo není s to?'),
(6029, 90, 6, 'Říká: „Ztrávil jsem statky převelké.“'),
(6030, 90, 7, 'Což za to má, že nikdo jej neviděl?'),
(6031, 90, 8, 'Což nedali jsme mu očí dvé'),
(6032, 90, 9, 'a jazyk (též), i retů dvé?'),
(6033, 90, 10, 'A neuvedli jej na silnic dvé?'),
(6034, 90, 11, 'A přece ještě nepodjal se (vzestupu po) svahu.'),
(6035, 90, 12, 'A co poví ti, co jest (vzestup po) svahu?'),
(6036, 90, 13, 'Toť výkup zajatců,'),
(6037, 90, 14, 'neb jídla dávání v den hladový'),
(6038, 90, 15, 'sirotkovi, blízkému příbuznému;'),
(6039, 90, 16, 'neb chuďasovi v prachu úpícímu:'),
(6040, 90, 17, 'a pak býti z těch, kdož uvěřili a navzájem povzbuzují se k trpělivosti a navzájem povzbuzují se k soucitu.'),
(6041, 90, 18, 'Tito budou státi po pravici;'),
(6042, 90, 19, 'a ti, kdož neuvěřili ve znamení naše, státi budou po levici:'),
(6043, 90, 20, 'oheň (jak klenba) je obklopí.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Při SLUNCI a polední záři jeho,'),
(6045, 91, 2, 'a při měsíci, když je následuje;'),
(6046, 91, 3, 'a při dnu, když je odhaluje;'),
(6047, 91, 4, 'a při noci, když je zahaluje;'),
(6048, 91, 5, 'při nebi a tom, jenž zbudoval je;'),
(6049, 91, 6, 'a při zemi a tom, jenž rozprostřel ji'),
(6050, 91, 7, 'při duši a tom, jenž urovnal ji,'),
(6051, 91, 8, 'a hříšnost i bohabojnost vnukl jí:'),
(6052, 91, 9, 'dobře povede se tomu, jenž čistou udržuje si ji;'),
(6053, 91, 10, 'a ztroskotá ten, jenž kazí si ji.'),
(6054, 91, 11, '(Kmen) Tsemúd lhářem nazval (proroka) ve svévoli své;'),
(6055, 91, 12, 'když nejbídnější vyvstal z nich;'),
(6056, 91, 13, 'tu řekl prorok boží jim: „Velbloudice boží to; nechte ji píti!“'),
(6057, 91, 14, 'Však lhářem nazvali jej a žíly podřízli jí; i zahladil je Pán jejich za jejich vinu, rovným způsobem'),
(6058, 91, 15, 'aniž se bojí (toho) následků.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Při NOCI, když (temnem) halí;'),
(6060, 92, 2, 'a při dnu, když závoj odhalí;'),
(6061, 92, 3, 'a při tom, jenž stvořil mužské a ženské (pohlaví):'),
(6062, 92, 4, 'zajisté snaha vaše se různí!'),
(6063, 92, 5, 'Pročež, pokud se týče toho, jenž dává (almužnu) a (Boha) se bojí,'),
(6064, 92, 6, 'a víru dává tomu nejkraššímu:'),
(6065, 92, 7, 'tomu usnadníme cestu k snadnému.'),
(6066, 92, 8, 'Ale pokud se týče lakomce soběstačného,'),
(6067, 92, 9, 'jenž lží zove to nejkrašší:'),
(6068, 92, 10, 'tomu usnadníme cestu k obtížnému;'),
(6069, 92, 11, 'a co prospějí mu statky jeho, když (v peklo) vržen bude'),
(6070, 92, 12, 'Zajisté na nás jest (správné) vedení,'),
(6071, 92, 13, 'neb zajisté nám přináleží moc nad životem budoucím i pozemským.'),
(6072, 92, 14, 'Pročež varuji vás před ohněm planoucím'),
(6073, 92, 15, 'v němž bude péci se jen ten nejbídnější:'),
(6074, 92, 16, 'ten, jenž zval (to vše) lží a odvracel se:'),
(6075, 92, 17, 'však vzdálen od něho bude bohabojný,'),
(6076, 92, 18, 'jenž rozdává ze jmění svého, aby čistším stal se;'),
(6077, 92, 19, 'a nečiní nikomu dobrodiní proto, aby byl odměněn,'),
(6078, 92, 20, 'nýbrž jen z touhy po tváři Pána svého nejvyššího.'),
(6079, 92, 21, 'A zajisté, že bude spokojen!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Při ZÁŘI POLEDNÍ,'),
(6081, 93, 2, 'a noci, když se temní!'),
(6082, 93, 3, 'Neopustil tě Pán tvůj, aniž tě nenávidí;'),
(6083, 93, 4, 'zajisté pak život budoucí lepším bude pro tebe života pozemského,'),
(6084, 93, 5, 'zajisté obdaří té Pán tvůj a budeš spokojen.'),
(6085, 93, 6, 'Což neshledal tě sirotkem a dal ti útulek?'),
(6086, 93, 7, 'A shledal tě zbloudilým a uvedl tě (na cestu pravou);'),
(6087, 93, 8, 'a shledal tě potřebným a obohatil tě.'),
(6088, 93, 9, 'Pročež, pokud se týče sirotka. neubližuj mu;'),
(6089, 93, 10, 'a pokud se týče prosícího, neodháněj ho;'),
(6090, 93, 11, 'a pokud se týče dobrodiní Pána tvého, hovoř o něm.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Což NEROZEVŘELI jsme ti hruď tvou?'),
(6092, 94, 2, 'A sejmuli s tebe břímě tvé,'),
(6093, 94, 3, 'jež obtěžovalo záda tvá?'),
(6094, 94, 4, 'Nevyzdvihli jsme tobě pověst tvou?'),
(6095, 94, 5, 'Však zajisté, s nesnází přijde též usnadnění —'),
(6096, 94, 6, 'zajisté, s nesnází přijde též usnadnění!'),
(6097, 94, 7, 'A když jsi hotov dál se tuž'),
(6098, 94, 8, 'a Pána svého pilně pros!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Při FÍKOVNÍKU a stromu olivovém;'),
(6100, 95, 2, 'při hoře Sinai;'),
(6101, 95, 3, 'a při končině této bezpečné!'),
(6102, 95, 4, 'Vždyť stvořili jsme člověka v soustavě nejkrašší:'),
(6103, 95, 5, 'poté pak svrhli jej do hlubin nejnižších,'),
(6104, 95, 6, 'vyjma těch, kdož uvěřili a konali dobré skutky: těm dána bude odměna bezvýhradná.'),
(6105, 95, 7, 'Kdo může tě pak lhářem zváti ohledně dne soudu (posledního)?'),
(6106, 95, 8, 'Což není Bůh ze všech soudců nejlepší?');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Čti, ve jménu Pána svého, jenž stvořil:'),
(6108, 96, 2, 'stvořil člověka z KRVE SRAŽENÉ:'),
(6109, 96, 3, 'čti, neb Pán tvůj jest nejvýš šlechetný,'),
(6110, 96, 4, 'jenž naučil (psaní) pérem:'),
(6111, 96, 5, 'naučil člověka, co byl nevěděl.'),
(6112, 96, 6, 'Ale ne; člověk zajisté jest svévolný,'),
(6113, 96, 7, 'jakmile vidí se soběstačným.'),
(6114, 96, 8, 'Zajisté (však) k Pánu tvému (vede všeho) návrat!'),
(6115, 96, 9, 'Co míníš o tom, jenž zbraňuje'),
(6116, 96, 10, 'služebníku (božímu), když modlí se?'),
(6117, 96, 11, 'Co míníš, kdyby raději následoval (pravé) vedení,'),
(6118, 96, 12, 'neb přikazoval bázeň boží?'),
(6119, 96, 13, 'Co míníš, když lhářem zval (proroka) a odvrátil se?'),
(6120, 96, 14, 'Což nevěděl, že Bůh zajisté vidí?'),
(6121, 96, 15, 'Ale ne; nepřestane-li, dojista uchopíme jej za kštici,'),
(6122, 96, 16, 'kštici prolhanou, hřešící!'),
(6123, 96, 17, 'Nechť zavolá pak členy spolku svého:'),
(6124, 96, 18, 'my zavoláme strážce pekelné!'),
(6125, 96, 19, 'Ale ne: neposlouchej ho, však na tvář svou padej a (k Bohu) se přibližuj!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Zajisté seslali jsme jej tobě v noci URČENÍ:'),
(6127, 97, 2, 'A co poví ti, co jest noc určení?'),
(6128, 97, 3, 'Noc určení lepší jest tisíce měsíců.'),
(6129, 97, 4, 'V ní sestupují andělé a duch, z dovolení Pána svého, se všemi rozkazy:'),
(6130, 97, 5, 'pokoj v ní jest, až do vzchodu jitřenky.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Nezačali ti, kdož neuvěřili z lidu, obdrževšího Knihu a z mnohobožců, rozštěpovati se ve víře, až když přišlo jim ZNAMENÍ JASNÉ:'),
(6132, 98, 2, 'posel od Boha, čtoucí listy (knihy) očištěné,'),
(6133, 98, 3, 'na nichž písmo jestli pravé;'),
(6134, 98, 4, 'a nerozdělili se v různé strany ti, jimž dána byla Kniha, až poté, když bylo přišlo jim znamení jasné.'),
(6135, 98, 5, 'A přece není nakazováno jim, leda aby uctívali Boha, upřímnými jsouce mu ve víře pravé; a aby zachovali modlitbu a dávali povinnou almužnu: totoť pak náboženství pravé.'),
(6136, 98, 6, 'Zajisté pak ti, kdož neuvěřili z lidu, obdrževšího Knihu, z mnohobožců, uvrženi budou v oheň pekelný, v němž přebývati budou věčně: tito nejhoršími jsou ze všech stvoření!'),
(6137, 98, 7, 'Zajisté pak ti, kdož uvěřili a konali dobré skutky: tito nejlepšími. jsou ze všech stvoření!'),
(6138, 98, 8, 'Odměna jich jest u Pána jejich: zahrady Edenu, pod nimiž řeky tekou, v nichž přebývati budou věčně věkův; nalezlť Bůh zalíbení v nich a oni nalezli zalíbení v něm. Toto bude pro toho, kdož bojí se Pána svého!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Když otřásána bude země třesením svým,'),
(6140, 99, 2, 'a vyvrhne země břemena svá,'),
(6141, 99, 3, 'a řekne člověk: „Co děje se s ní?“'),
(6142, 99, 4, 'V den ten vyprávěti bude (země) noviny své,'),
(6143, 99, 5, 'neboť tak vnukne jí Pán její.'),
(6144, 99, 6, 'V den ten předstoupí lidé v oddílech, aby ukázány byly jim skutky jejich.'),
(6145, 99, 7, 'A kdožkoliv byl učinil zvíci zrnka dobra, spatří je:'),
(6146, 99, 8, 'a kdožkoliv byl učinil zvíci zrnka zla, spatří je.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Při OŘÍCH supících,'),
(6148, 100, 2, 'a jiskry sršících:'),
(6149, 100, 3, 'při z rána útočících'),
(6150, 100, 4, 'a prach tím vydeptávajících,'),
(6151, 100, 5, 'a střed voje tím prorážejících!'),
(6152, 100, 6, 'Zajisté člověk vůči Pánu svému jest nevděčný'),
(6153, 100, 7, 'a zajisté sám toho jest svědkem:'),
(6154, 100, 8, 'neb v pravdě statků (světa) velkým jest milovníkem.'),
(6155, 100, 9, 'Což neví, že když rozrušeno bude to, co v hrobech jest;'),
(6156, 100, 10, 'a kdy zjeveno bude, co nitra skrývají,'),
(6157, 100, 11, 'že Pán jejich v den onen bude dobře o nich zpraven?');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'ÚDER!.'),
(6159, 101, 2, 'Co jest úder?'),
(6160, 101, 3, 'Co poví ti; co jest úder?'),
(6161, 101, 4, 'Den kdy lidé podobni budou molům rozptýleným:'),
(6162, 101, 5, 'a hory podobny budou vločkám vlny poletujícím.'),
(6163, 101, 6, 'Tehdy ten, jehož váha těžká bude,'),
(6164, 101, 7, 'v životě (velmi) libém bude:'),
(6165, 101, 8, 'a ten, jehož váha lehká bude,'),
(6166, 101, 9, 'příbytkem jeho Jáma bude!'),
(6167, 101, 10, 'A co poví ti, co tato jest?'),
(6168, 101, 11, 'Oheň pálící!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Zalíbení máte v ROZMNOŽOVANÍ'),
(6170, 102, 2, 'až do návštěvy pohřebišť:'),
(6171, 102, 3, 'Nikoliv, zajisté zvíte!'),
(6172, 102, 4, 'A znovu nikoliv, zajisté zvíte!'),
(6173, 102, 5, 'Nikoliv, kéž uvědomili byste si to jistotně!'),
(6174, 102, 6, 'Dojista spatříte pekla plameny:'),
(6175, 102, 7, 'pak dojista spatříte jej okem ujištěným!'),
(6176, 102, 8, 'Pak, v den onen, tázáni budete ohledně (světa) rozkoší.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Při ODPOLEDNI!'),
(6178, 103, 2, 'Zajisté člověk jde v záhubu,'),
(6179, 103, 3, 'vyjma těch, kdož uvěřili a konali dobré skutky: a nabádají se navzájem k pravdě a nabádají se navzájem k trpělivosti.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Běda každému POMLOUVAČI utrhačnému,'),
(6181, 104, 2, 'jenž hromadí jmění a počítá je!'),
(6182, 104, 3, 'Má za to, že jmění jeho jej věčným učiní:'),
(6183, 104, 4, 'Nikoliv! dojista vržen bude v peklo drtící!'),
(6184, 104, 5, 'A co poví ti, co jest peklo drtící?'),
(6185, 104, 6, 'Toť oheň boží, vznícený,'),
(6186, 104, 7, 'jenž nad srdce (zavržených) vystoupí:'),
(6187, 104, 8, 'zajistéť (jak klenba) je obklopí,'),
(6188, 104, 9, 'na rozložitém sloupoví.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Což neviděl’s, jak naložil Pán tvůj s lidem SLONY přivedším?'),
(6190, 105, 2, 'Což nepopletl jejich lest?'),
(6191, 105, 3, 'A poslal na ně ptáky abábíl,'),
(6192, 105, 4, 'již metali na ně kamení z hlíny pálené:'),
(6193, 105, 5, 'a učinil je jak stébla okousaná!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Pro soudržnost KOREJŠOVCŮ :'),
(6195, 106, 2, 'soudržnost jich (ve vysílání) karavan zimou i létem!'),
(6196, 106, 3, 'Nechť tedy uctívají Pána této svatyně,'),
(6197, 106, 4, 'jenž nakrmil je (a ochránil) před hladem a zabezpečil je před strachem.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Co míníš o tom, jenž zve den soudu vylhaným?'),
(6199, 107, 2, 'Tenť jest, jenž sirotka odhání:'),
(6200, 107, 3, 'a lid nepovzbuzuje k jídla chudým dávání.'),
(6201, 107, 4, 'Běda modlícím se,'),
(6202, 107, 5, 'kteří modlitbu svou jen ledabyle konají;'),
(6203, 107, 6, 'a kteří se přetvařují:'),
(6204, 107, 7, 'a před ALMUŽNOU se zpěčují.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Zajistéť dali jsme ti KAUTSAR:'),
(6206, 108, 2, 'pročež modli se k Pánu svému a obětní čiň mu dar.'),
(6207, 108, 3, 'Zajisté kdož nenávidí tě, bude abtar.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Rci: „Ó vy nevěřící?'),
(6209, 109, 2, 'Neklaním se tomu, čemu vy se klaníte:'),
(6210, 109, 3, 'aniž vy klaníte se, čemu já klaním se.'),
(6211, 109, 4, 'Já klaněti se nebudu, čemu vy klaněli jste se,'),
(6212, 109, 5, 'aniž vy klaněti se budete, čemu já klaním se.'),
(6213, 109, 6, 'Vy svoje máte náboženství, já svoje mám.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Když přijde pomoc boží a vítězství,'),
(6215, 110, 2, 'a když spatříš lidi vstupovati do náboženství Boha v zástupech:'),
(6216, 110, 3, 'tehdy pěj chválu Pána svého a pros jej za odpuštění, neboť zajisté on jest promíjejícím!');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Zhyňtež obě ruce Abú Lahaba a zhyňž on sám:'),
(6218, 111, 2, 'nic neprospěje mu bohatství jeho, aniž co si vydobyl:'),
(6219, 111, 3, 'sžíhán bude ohněm, plným plamenů:'),
(6220, 111, 4, 'jakož i žena jeho, s smolnic nákladem:'),
(6221, 111, 5, 'kol hrdla majíc provaz z pletiva.');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Rci: „Onť Bůh jest jediný,'),
(6223, 112, 2, 'Bůh věčný!'),
(6224, 112, 3, 'Nezplozoval, aniž zplozen byl:'),
(6225, 112, 4, 'a nikdo roveň není mu!“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Rci: „Béřu útočiště u Pána JITŘENKY,'),
(6227, 113, 2, 'přede zlem jeho stvoření:'),
(6228, 113, 3, 'a před zlem temna nočního, když přikvačí:'),
(6229, 113, 4, 'a před zlem foukatelek do uzlů:'),
(6230, 113, 5, 'a před zlem závistníka, když závidí.“');
INSERT INTO `cs_nykl` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Rci: „Béřu útočiště u Pána LIDÍ,'),
(6232, 114, 2, 'krále lidí,'),
(6233, 114, 3, 'Boha lidí:'),
(6234, 114, 4, 'před zlem našeptávače plíživce,'),
(6235, 114, 5, 'jenž našeptává do niter lidí:'),
(6236, 114, 6, 'před Džinny a lidmi.“');
