INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خدا که رحمتش بی‌اندازه است و مهربانی‌اش همیشگی.'),
(2, 1, 2, 'همه ستایش ها، ویژه خدا، مالک و مربّی جهانیان است.'),
(3, 1, 3, 'رحمتش بی اندازه و مهربانی اش همیشگی است.'),
(4, 1, 4, 'مالک و فرمانروای روز پاداش و کیفر است.'),
(5, 1, 5, '[پروردگارا!] تنها تو را می پرستیم وتنها از تو کمک می خواهیم.'),
(6, 1, 6, 'ما را به راهِ راست راهنمایی کن.'),
(7, 1, 7, 'راه کسانی [چون پیامبران، صدّیقان، شهیدان و صالحان که به خاطر لیاقتشان] به آنان نعمتِ [ایمان، عمل شایسته و اخلاق حسنه] عطا کردی، هم آنان که نه مورد خشم تواند و نه گمراه اند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم'),
(9, 2, 2, 'در [وحی بودن و حقّانیّت] این کتابِ [با عظمت] هیچ شکی نیست؛ سراسرش برای پرهیزکاران هدایت است.'),
(10, 2, 3, 'آنان که به غیب ایمان دارند و نماز را بر پا می دارند و از آنچه به آنان روزی داده ایم، انفاق می کنند.'),
(11, 2, 4, 'و آنان که به آنچه به سوی تو و به آنچه پیش از تو نازل شده، مؤمن هستند و به آخرت یقین دارند.'),
(12, 2, 5, 'آنانند که از سوی پروردگارشان بر [راهِ] هدایت اند و آنانند که رستگارند.'),
(13, 2, 6, 'بی تردید برای کسانی که [به خدا و آیاتش] کافرند مساوی است چه [از عذاب] بیمشان دهی یا بیمشان ندهی، ایمان نمی آورند.'),
(14, 2, 7, 'خدا [به کیفر کفرشان] بر دل ها و گوش هایشان مُهرِ [تیره بختی] نهاده، و بر چشم هایشان پرده ای [از تاریکی است که فروغ هدایت را نمی بینند]، و برای آنان عذابی بزرگ است.'),
(15, 2, 8, 'و گروهی از مردم [که اهل نفاق اند] می گویند: ما به خدا و روز قیامت ایمان آوردیم، در حالی که آنان مؤمن نیستند.'),
(16, 2, 9, '[به گمان باطلشان] می خواهند خدا و اهل ایمان را فریب دهند، در حالی که جز خودشان را فریب نمی دهند، ولی [این حقیقت را] درک نمی کنند.'),
(17, 2, 10, 'در دلِ آنان بیماریِ [سختی از نفاق] است، پس خدا به کیفرِ نفاقشان بر بیماریشان افزود، و برای آنان در برابر آنچه همواره دروغ می گفتند، عذابی دردناک است.'),
(18, 2, 11, 'چون به آنان گویند: در زمین فساد نکنید، می گویند: فقط ما اصلاح گریم!'),
(19, 2, 12, 'آگاه باشید! یقیناً خود آنان فسادگرند، ولی درک نمی کنند.'),
(20, 2, 13, 'چون به آنان گویند: ایمان آورید چنان که دیگر مردم ایمان آوردند می گویند: آیا ما هم مانند سبک مغزان ایمان آوریم؟! آگاه باشید! قطعاً اینان خود سبک مغزند، ولی [از شدت کوردلی به این حقیقت] آگاه نیستند.'),
(21, 2, 14, 'و هنگامی که با اهل ایمان دیدار کنند، گویند: ما ایمان آوردیم و چون با شیطان هایشان [که سرانِ شرک و کفرند] خلوت گزینند، گویند: بدون شک ما با شماییم، جز این نیست که ما [با تظاهر به ایمان] آنان را مسخره می کنیم.'),
(22, 2, 15, 'خدا آنان را [به کیفر این کار منافقانه در دنیا و آخرت] عذاب خواهد کرد، و آنان را در سرکشی وتجاوزشان مهلت می دهد [تا در گمراهی شان] سرگردان وحیران بمانند.'),
(23, 2, 16, 'آنان کسانی هستند که گمراهی را به جای هدایت خریدند، پس تجارتشان سود نکرد و از راه یافتگان [به سوی حق] نبودند.'),
(24, 2, 17, 'سرگذشت آنان مانند کسانی است که [در شب بسیار تاریک بیابان] آتشی افروختند [تا در پرتو آن خود را از خطر نجات دهند]، چون آتش پیرامونشان را روشن ساخت، خدا [به وسیله توفانی سهمگین] نورشان را خاموش کرد و آنان را در تاریکی هایی که مطلقاً نمی دیدند واگذاشت.'),
(25, 2, 18, 'کر و لال و کورند، به این سبب آنان [از گمراهی و ضلالت به سوی هدایت و حقیقت] بازنمی گردند.'),
(26, 2, 19, 'یا [سرگذشت آنان] سرگذشتِ [دچار شدگان به] رگباری شدید از آسمان است که در آن تاریکی ها و رعد و برقی است، [آنان] انگشتانشان را از [صدایِ هولناکِ] صاعقه ها به خاطر بیم مرگ در گوش هایشان می گذارند [در حالی که راه گریزی از مرگ برای آنان نیست] و خدا به کافرانْ احاطه [همه جانبه] دارد.'),
(27, 2, 20, 'نزدیک است که آن برقِ [بسیار رخشنده، روشنیِ] چشم های آنان را برباید؛ زمانی که آنان را روشنی دهد، در آن روشنی راه می روند و چون محیط را بر آنان تاریک کند، می ایستند و اگر خدا می خواست [شنواییِ] گوش و [بیناییِ] چشم آنان را نابود می کرد؛ زیرا خدا بر هر کاری تواناست.'),
(28, 2, 21, 'ای مردم! پروردگارتان را که شما و پیشینیان شما را آفریده است، بپرستید تا [با پرستیدن او] پروا پیشه شوید.'),
(29, 2, 22, 'آن پروردگاری که زمین را برای شما بستری گسترده و آسمان را سقفی برافراشته قرار داد و از آسمان، آبی [مانند برف و باران] نازل کرد و به وسیله آن از میوه های گوناگون، رزق و روزی برای شما بیرون آورد؛ پس برای خدا شریکان و همتایانی قرار ندهید در حالی که می دانید [برای خدا در آفریدن و روزی دادن، شریک و همتایی وجود ندارد].'),
(30, 2, 23, 'و اگر در آنچه ما بر بنده خود] محمّد (صلی الله علیه وآله)] نازل کرده ایم، شک دارید [که وحی الهی است یا ساخته بشر] پس سوره ای مانند آن رابیاورید، و [برای این کار] غیر از خدا، شاهدان و گواهان خود را [از فُصحا و بُلغای بزرگ عرب به یاری] فرا خوانید، اگر [در گفتار خود که این قرآن ساخته بشر است نه وحی الهی [راستگویید.'),
(31, 2, 24, 'و اگر این کار را انجام ندادید ـ که هرگز نمی توانید انجام دهید ـ بنابراین از آتشی که هیزمش مردم و سنگ هایند، بپرهیزید؛ آتشی که برای کافران آماده شده است.'),
(32, 2, 25, 'و کسانی را که ایمان آورده اند و کارهای شایسته [چون عبادت حق و خدمت به خلق] انجام داده اند، مژده ده که بهشت هایی ویژه آنان است که از زیرِ [درختانِ] آن نهرها جاری است؛ هرگاه از آن بهشت ها میوه ای آماده به آنان دهند، گویند: این همان است که از پیشْ روزیِ ما نمودند، و از میوه های گوناگون که [در طعم و گوارایی و زیبایی] شبیهِ هم است، نزد آنان آورند؛ در آنجا برای ایشان همسرانی پاکیزه [از هر آلودگی] است؛ و در آن بهشت ها جاودانه اند.'),
(33, 2, 26, 'بی تردید خدا [برای فهماندن مطلبی به مردم] از اینکه به پشه و فراتر از آن [در کوچکی] مَثَل بزند، شرم نمی کند؛ اما اهل ایمان آگاهند که آن مثل از سوی پروردگارشان درست و حق است و اما کافران می گویند: خدا از این مثل چه اراده کرده است؟! خدا بسیاری را به آن مثل [به خاطر عدم دقت و مطالعه] گمراه می کند، و بسیاری را به آن مثل [به سبب درک صحیح] هدایت می نماید؛ و جز فاسقان را به آن گمراه نمی کند.'),
(34, 2, 27, 'آنان کسانی هستند که پیمان خدا را [که توحید و وحی و نبوت است] پس از استواری اش [با دلایل عقلی و علمی و براهین منطقی با عدم وفای به آن] می شکنند و آنچه را خدا پیوند خوردن به آن را فرمان داده است [مانند پیوند با پیامبران و کتابهای آسمانی و اهل بیت طاهرین و خویشان] قطع می نمایند و در زمین تباهی و فساد بر پا می کنند، آنانند که زیانکارند.'),
(35, 2, 28, 'چگونه به خدا کفر می ورزید در حالی که [پیش از دمیده شدن روح به کالبدتان ترکیبی از عناصر] مرده بودید، پس شما را حیات بخشید، سپس شما را می میراند، آن گاه دوباره زنده می کند، سپس به سوی او بازگردانده می شوید.'),
(36, 2, 29, 'اوست که همه آنچه را در زمین است برای شما آفرید، سپس آفرینش آسمان را [که به صورت ماده ای دود مانند بود] اراده کرد و آن را به شکل هفت آسمان [همراه با نظامی استوار] درست و نیکو قرار داد؛ و او [به قوانین و محاسباتِ] همه چیز داناست.'),
(37, 2, 30, 'و آن زمان را یاد آر که پروردگارت به فرشتگان گفت: به یقین جانشینی در زمین قرار می دهم. گفتند: آیا موجودی را در زمین قرار می دهی که در آن به فساد و تباهی برخیزد و به ناحق خون ریزی کند و حال آن که ما تو را همواره با ستایشت تسبیح می گوییم و تقدیس می کنیم. [پروردگار] فرمود: من [از این جانشین و قرار گرفتنش در زمین اسراری] می دانم که شما نمی دانید.'),
(38, 2, 31, 'و خدا همه نام ها [یِ موجودات] را به آدم آموخت؛ سپس [هویت و حقایق ذات موجودات را] به فرشتگان ارائه کرد و گفت: مرا از نام های ایشان خبر دهید، اگر [در ادعای سزاوار بودنتان به جانشینی] راستگویید.'),
(39, 2, 32, 'گفتند: تو از هر عیب و نقصی منزّهی، ما را دانشی جز آنچه خودت به ما آموخته ای نیست، یقیناً تویی که بسیار دانا و حکیمی.'),
(40, 2, 33, '[خدا] فرمود: ای آدم! فرشتگان را از نام های آنان خبر ده. پس هنگامی که نام هایشان را به فرشتگان خبر داد [خدا] فرمود: آیا به شما نگفتم که من یقیناً نهانِ آسمان ها و زمین را می دانم، و به آنچه شما آشکار می کنید و به آنچه پنهان می دارید، دانایم؟'),
(41, 2, 34, 'و [یاد کن] هنگامی که به فرشتگان گفتیم: به آدم سجده کنید، [پس] سجده کردند مگر ابلیس که سر پیچید و تکبّر ورزید و از کافران شد.'),
(42, 2, 35, 'و گفتیم: ای آدم! تو و همسرت در این بهشت سکونت گیرید و از هر جای آنکه خواستید فراوان و گوارا بخورید، و به این درخت نزدیک نشوید که [اگر نزدیک شوید] از ستمکاران خواهید شد.'),
(43, 2, 36, 'پس شیطان، هر دو را از [طریق] آن درخت لغزانید و آنان را از آنچه در آن بودند [چه مقام و مرتبه معنوی، و چه منزلت و جایگاه ظاهری] بیرون کرد. و ما گفتیم: [ای آدم و حوا و ای ابلیس!] در حالی که دشمن یکدیگرید [و تا ابد، بین شما آدمیان و ابلیسیان صلح و صفایی نخواهد بود، از این جایگاه] فرود آیید و برای شما در زمین، قرارگاهی [برای زندگی] و تا مدتی معین، وسیله بهرهوری اندکی خواهد بود.'),
(44, 2, 37, 'پس آدم کلماتی را] مانند کلمه استغفار و توسّل به اهل بیت (علیهم السلام) که مایه توبه و بازگشت بود] از سوی پروردگارش دریافت کرد و [پروردگار [توبه اش را پذیرفت؛ زیرا او بسیار توبه پذیر و مهربان است.'),
(45, 2, 38, 'گفتیم: همگی از آن [مرتبه و مقام] فرود آیید؛ چنانچه از سوی من هدایتی برای شما آمد، پس کسانی که از هدایتم پیروی کنند نه ترسی بر آنان است و نه اندوهگین شوند.'),
(46, 2, 39, 'و کسانی که کافر شدند و آیات ما را تکذیب کردند، اهل آتشند و در آن جاودانه اند.'),
(47, 2, 40, 'ای بنی اسرائیل! نعمت های مرا که به شما عطا کردم، یاد کنید و به پیمانم [که سفارش به عبادت و ایمان به همه انبیا به ویژه پیامبر اسلام است] وفا کنید تا من هم به پیمان شما [که توقع ثواب وپاداش در برابر عبادت وایمان است] وفا کنم، و [نسبت به پیمان شکنی] فقط از من بترسید.'),
(48, 2, 41, 'وبه آنچه [بر پیامبر اسلام] نازل کرده ام که تصدیق کننده [تورات وانجیلی] است که با شماست، ایمان آورید و نخستین کافر به آن نباشید [که نسل به نسل پس از شما به پیروی از شما به آن کافر شوند] و آیاتم را [در تورات که اوصاف محمّد و قرآن در آن است، با تغییر دادن و تحریف کردن] به بهایی ناچیز نفروشید، و فقط از من پروا کنید.'),
(49, 2, 42, 'و حق را با باطل مخلوط نکنید [تا تشخیص دادنشان بر مردمِ جویای حق دشوار نشود]، وحق را [که قرآن وپیامبر است] در حالی که می دانید [و می شناسید، از مردم] پنهان نکنید.'),
(50, 2, 43, 'و نماز را بر پا دارید، و زکات بپردازید، و همراه رکوع کنندگان رکوع کنید [که نماز خواندن با جماعت محبوب خداست].'),
(51, 2, 44, 'آیا مردم را به نیکی فرمان می دهید و خود را [در ارتباط با نیکی] فراموش می کنید؟ در حالی که کتاب [تورات را که با شدت به نیکی دعوتتان کرده] می خوانید. آیا [به وضع زیان بار و خطرناک خود] نمی اندیشید؟'),
(52, 2, 45, 'از صبر و نماز [برای حل مشکلات خود و پاک ماندن از آلودگی ها و رسیدن به رحمت حق] کمک بخواهید و بی تردید این کار جز بر کسانی که در برابر حق قلبی فروتن دارند دشوار و گران است.'),
(53, 2, 46, '[دارندگان قلب فروتن] کسانی هستند که یقین دارند دیدار کننده [قیامت و پاداش] پروردگارشان می باشد و قطعاً به سوی او باز می گردند.'),
(54, 2, 47, 'ای بنی اسرائیل! نعمت های مرا که به شما عطا کردم و اینکه شما را بر جهانیانِ [زمانِ خودتان] برتری دادم، یاد کنید،'),
(55, 2, 48, 'و از روزی پروا کنید که نه کسی از کسی عذابی را دفع می کند، و نه از کسی شفاعتی می پذیرند، و نه از کسی [در برابر گناهانش] فدیه و عوضی می گیرند، و نه [برای رهایی از آتش دوزخ] یاری می شوند.'),
(56, 2, 49, 'و [یادکنید] آن گاه که شما را از [سیطره حکومت ظالمانه] فرعونیان نجات دادیم؛ آنان که همواره شما را به سخت ترین صورت شکنجه می کردند، پسران شما را سر می بریدند و زنان شما را [برای بیگاری و کنیزی] زنده می گذاشتند. و در این سختی ها ومشکلاتْ آزمایشی بزرگ از سوی پروردگارتان بود.'),
(57, 2, 50, 'و [یاد کنید] هنگامی که دریا را برای شما شکافتیم، پس شما را نجات دادیم و فرعونیان را در حالی که می دیدید، غرق کردیم.'),
(58, 2, 51, 'و [یاد کنید] زمانی که [برای نازل کردن تورات] چهل شب با موسی وعده گذاشتیم، سپس شما بعد از [غایب شدن] او گوساله را معبود خود گرفتید، در حالی که [به سبب این کار بسیار زشت] ستمکار بودید.'),
(59, 2, 52, 'سپس بعد از آن [کار زشت] از [گناه] شما درگذشتیم، تا سپاس گزاری کنید.'),
(60, 2, 53, 'و [یاد کنید] هنگامی که به موسی، کتاب و میزان جداکننده [حق از باطل] عطا کردیم تا هدایت یابید.'),
(61, 2, 54, 'و [یاد کنید] زمانی که موسی به قومش گفت: ای قوم من! قطعاً شما به سبب معبود گرفتن گوساله به خودتان ستم ورزیدید، پس به سوی آفریننده خود بازگردید، و [افرادی از] خودتان را [که گوساله را به پرستش گرفتند] بکشید که این [عمل] برای شما در پیشگاه آفریدگارتان بهتر است. پس خدا [به دنبال اجرا کردن دستورش] توبه شما را پذیرفت؛ زیرا او بسیار توبه پذیر و مهربان است.'),
(62, 2, 55, 'و [یاد کنید] آن گاه که گفتید: ای موسی! هرگز به تو ایمان نمی آوریم تا خدا را آشکارا [با چشم خود] ببینیم. پس صاعقه مرگبار شما را گرفت، در حالی که می دیدید.'),
(63, 2, 56, 'سپس شما را پس از مرگتان برانگیختیم تا سپاس گزاری کنید.'),
(64, 2, 57, 'و [در صحرای سوزان سینا] ابر را بر سر شما سایبان قرار دادیم؛ و بر شما گزانگبین و بلدرچین نازل کردیم؛ [و گفتیم:] از خوراکی های پاک و پاکیزه ای که روزیِ شما قرار داده ایم بخورید. و آنان [در تجاوز و طغیانشان وناسپاسی وکفرانشان] بر ما ستم نکردند، بلکه همواره بر خود ستم می ورزیدند.'),
(65, 2, 58, 'و [یاد کنید] هنگامی را که گفتیم: به این شهر [بیت المقدس] وارد شوید، و از نعمت های آن هر چه خواستید، فراوان و گوارا بخورید و از دروازه [شهر یا درِ معبد] فروتنانه و سجده کنان درآیید و بگویید: [خدایا! خواسته ما] ریزش گناهان ماست، تا گناهانتان را بیامرزیم و به زودی [پاداش] نیکوکاران را بیفزایم.'),
(66, 2, 59, 'ولی ستمکاران، سخنی را که [بیرون دروازه شهر] به آنان گفته شده بود [پس از ورود به شهر] به سخنی دیگر تبدیل کردند [به جای درخواست ریزش گناهان، درخواست امور مادی کردند]. ما هم بر ستمکاران به سبب آنکه همواره نافرمانی می کردند، عذابی از آسمان فرود آوردیم.'),
(67, 2, 60, 'و [یاد کنید] آن گاه که موسی برای قومش درخواست آب کرد، پس گفتیم: عصایت را به این سنگ بزن. پس دوازده چشمه از آن جوشید به طوری که هر گروهی [از دوازده گروه بنی اسرائیل] چشمه ویژه خود را شناخت. [و گفتیم:] از روزیِ خدا بخورید و بیاشامید و تبهکارانه در زمین فتنه و آشوب بر پا نکنید.'),
(68, 2, 61, 'و [یاد کنید] هنگامی که گفتید: ای موسی! ما هرگز بر یک نوع غذا صبر نمی کنیم، پس از پروردگارت بخواه تا از آنچه زمین می رویاند از سبزی و خیار و سیر و عدس و پیازش را برای ما آماده کند. [موسی] گفت: آیا شما به جای غذای بهتر، غذای پست تر را می خواهید؟! [اکنون که چنین درخواست ناروایی دارید] به شهری فرود آیید که آنچه خواستید، برای شما آماده است. و [داغِ] خواری و بیچارگی و نیاز بر آنان زده شد و سزاوار خشم خدا شدند؛ این [خواری و خشم] به سبب آن بود که آنان همواره به آیات خدا کفر می ورزیدند و پیامبران را به ناحق می کشتند؛ این [کفرورزی و کشتن پیامبران] به علت آن بود که [از فرمانِ من] سرپیچی نمودند و پیوسته [از حدود حق] تجاوز می کردند.'),
(69, 2, 62, 'مسلماً کسانی که [به ظاهر] ایمان آوردند، و یهودی ها و نصرانی ها و صابئی ها هر کدامشان [از روی حقیقت] به خدا و روز قیامت ایمان آورند و کار شایسته انجام دهند، برای آنان نزد پروردگارشان پاداشی شایسته و مناسب است، و نه بیمی بر آنان است و نه اندوهگین شوند.'),
(70, 2, 63, 'و [یاد کنید] هنگامی که از شما [بر پیروی از حق] پیمان گرفتیم، و کوه طور را بالای سرتان برافراشتیم، [و گفتیم:] آنچه را [از آیات کتاب آسمانی] به شما داده ایم، با قدرت و قوّت دریافت کنید، و آنچه را در آن است [برای اجرا کردن] به یاد داشته باشید تا پرهیزکار شوید.'),
(71, 2, 64, 'آن گاه بعد از [پیمان گرفتن، از وفاکردن به آن] سرپیچی کردید، و اگر فضل و رحمت خدا بر شما نبود، قطعاً از زیانکاران بودید.'),
(72, 2, 65, 'بی تردید شما [سرگذشتِ کسانی از هم مسلکان خود را] که در روز شنبه [از فرمان خدا که صید ماهی را حرام کرده بود] عصیان ورزیدند دانستید که [به کیفر عصیانشان] به آنان نهیب زدیم که به صورت بوزینگانی خوار و رانده شده درآیید.'),
(73, 2, 66, 'در نتیجه، آن [مجازات] را عبرتی برای کسانی که شاهدِ حادثه بودند، وکسانی که بعد از آنان می آیند، و پندی برای پرواپیشگان قرار دادیم.'),
(74, 2, 67, 'و [یاد کنید] زمانی که موسی به قومش گفت: خدا به شما فرمان می دهد گاوی را ذبح کنید، گفتند: آیا ما را مسخره می کنی؟! گفت: به خدا پناه می برم از اینکه از نادانان باشم.'),
(75, 2, 68, 'گفتند: از پروردگارت بخواه برای ما بیان کند که آن گاو چگونه گاوی باشد؟ گفت: او می فرماید که آن گاوی است نه پیر از کارمانده، نه جوان نارسیده، [بلکه] گاوی میان این دو نوع گاو است. پس آنچه را به آن فرمان داده اند، انجام دهید.'),
(76, 2, 69, 'گفتند: از پروردگارت بخواه که برای ما توضیح دهد رنگش چگونه باشد؟ گفت: خدا می گوید: گاوی است زرد و رنگش روشن که بینندگان را شاد و مسرور می کند.'),
(77, 2, 70, 'گفتند: از پروردگارت بخواه برای ما بیان کند که [نهایتاً آن گاو] چه گاوی است؟ زیرا این گاو بر ما مُبهم و مُشتبه شده، و اگر خدا بخواهد [به شناخت آن] هدایت خواهیم شد.'),
(78, 2, 71, 'گفت: او می گوید: گاوی است که نه رام است تا زمین را شخم زند و نه زراعت را آبیاری نماید، [از هر عیب و نقصی] سالم است، و رنگی مخالف رنگ اصلی در آن نیست، گفتند: اکنون حق را برای ما آوردی. پس آن را ذبح کردند، در حالی که نزدیک بود فرمان خدا را اجرا نکنند!!'),
(79, 2, 72, 'و [یاد کنید] هنگامی که کسی را کشتید و درباره [قاتل] او به نزاع و ستیز برخاستید؛ و خدا آشکار کننده چیزی است که پنهان می داشتید.'),
(80, 2, 73, 'پس گفتیم: پاره ای از آن [گاو ذبح شده] را به مقتول بزنید [تا زنده شود و قاتل را معرفی کند]. خدا مردگان را این گونه زنده می کند و نشانه هایِ [قدرت و ربوبیّت] خود را به شما نشان می دهد، تا بیندیشید.'),
(81, 2, 74, 'سپس دل های شما بعد از آن [معجزه شگفت انگیز] سخت شد، مانند سنگ یا سخت تر؛ زیرا پاره ای از سنگ هاست که از آنها نهرها می جوشد، و پاره ای از آنها می شکافد و آب از آن بیرون می آید، و پاره ای از آنها از ترس خدا [از بلندی] سقوط می کند؛ و خدا از آنچه انجام می دهید بی خبر نیست.'),
(82, 2, 75, 'آیا [شما مردم مؤمن] امید دارید که [آن سخت دلان] به [دین] شما ایمان بیاورند؟! در حالی که گروهی از آنان کلام خدا را همواره می شنیدند، سپس بعد از آنکه [معنا و مفهومش را] درک می کردند، [به سبب دنیاطلبی و امور مادی] به دلخواه خود تغییرش می دادند، در صورتی که می دانستند [به کلام خدا و به مردم جویای حق خیانت می کنند].'),
(83, 2, 76, 'و هنگامی که با مؤمنان دیدار کنند، می گویند: ما ایمان آوردیم. و چون با هم خلوت می کنند [از روی اعتراض و ایراد] به یکدیگر می گویند: چرا حقایقی را که خدا [در تورات درباره پیامبر اسلام] برای شما بیان کرده به مؤمنان می گویید تا [روز قیامت با این حقایق] در پیشگاه پروردگارتان بر ضد شما استدلال کنند؟ آیا تعقّل نمی کنید [که نباید زمینه استدلال بر ضد خود را در اختیار مؤمنان گذارید؟!]'),
(84, 2, 77, 'آیا [آن سخت دلان] نمی دانند که خدا آنچه را پنهان می دارند و آنچه را آشکار می کنند، می داند؟!'),
(85, 2, 78, 'و گروهی از یهود، که جاهل و بی سوادند [غیر از تورات فعلی] که جز بافته های دروغین [عالمان خائن آنان نیست] نمی دانند، و حال آن که [در امر دین به سبب عدم تحقیق و دنبال نکردن علم و دانش] فقط در مسیر گمان و خیال واهی قدم برمی دارند.'),
(86, 2, 79, 'پس وای بر کسانی که با دست هاشان نوشته ای را می نویسند، سپس می گویند: این [نوشته] از سوی خداست. تا با این [کار زشت و خائنانه] بهایی ناچیز به دست آورند؛ پس وای بر آنان از آنچه دست هاشان نوشت، و وای بر آنان از آنچه به دست می آورند.'),
(87, 2, 80, 'و گفتند: آتش [دوزخ] جز چند روزی به ما نمی رسد. بگو: آیا [بر این عقیده خود] از نزد خدا پیمانی گرفته اید؟ که هرگز خدا از آن پیمان تخلّف نخواهد کرد، یا جاهلانه چیزی را به خدا نسبت می دهید؟'),
(88, 2, 81, '[نه چنین است که می گویید] بلکه کسانی که مرتکب گناه شدند وآثار گناه، سراسر وجودشان را فرا گرفت، آنان اهل آتشند و در آن جاودانه اند.'),
(89, 2, 82, 'و کسانی که ایمان آوردند و کارهای شایسته انجام دادند، اهل بهشت اند و در آن جاودانه اند.'),
(90, 2, 83, 'و [یاد کنید] زمانی که از بنی اسرائیل پیمان گرفتیم که جز خدا را نپرستید، و به پدر و مادر و خویشان ویتیمان ومستمندان نیکی کنید، و با مردم با خوش زبانی سخن گویید، و نماز را بر پا دارید، و زکات بپردازید، سپس همه شما جز اندکی [از پیمان خدا] روی گردانیدید؛ و شما [به طور عادت] روی گردان هستید.'),
(91, 2, 84, 'و [یاد کنید] هنگامی که از شما پیمان گرفتیم که خون همدیگر را نریزید، و یکدیگر را از خانه های خود آواره نکنید، سپس [به پیمانتان] اقرار کردید و بر آن هم گواهی می دهید.'),
(92, 2, 85, 'باز این شما هستید که یکدیگر را می کشید، و گروهی از خودتان را از خانه هایشان آواره می کنید، و از روی گناه و تجاوز یکدیگر را بر ضد آنان [که آواره کرده اید] یاری و کمک می دهید، و اگر آنان در حال اسارت نزد شما آیند، برای آزاد شدنشان فدیه می دهید، در صورتی که آواره کردنشان بر شما حرام بود. آیا به بخشی از کتاب [آسمانی] ایمان می آورید و به بخشی دیگر کفر می ورزید؟ [حرام بودن جنگ و آواره کردن را مردود می شمارید، و وجوب آزاد کردن هم کیشان را از اسارت قبول می کنید!] پس کیفر کسانی از شما که چنین تبعیضی را [در آیات خدا] روا می دارند، جز خواری و رسوایی در زندگی دنیا نیست، و روز قیامت به سوی سخت ترین عذاب بازگردانیده می شوند، و خدا از آنچه انجام می دهید، بی خبر نیست.'),
(93, 2, 86, 'اینان کسانی اند که زندگی [زودگذر] دنیا را به جای آخرت خریدند؛ پس نه عذاب از آنان سبک شود و نه یاری شوند.'),
(94, 2, 87, 'و یقیناً ما به موسی کتاب دادیم و پس از او پیامبرانی به دنبال هم فرستادیم، و به عیسی بن مریم دلایل روشن و آشکار عطا نمودیم، و او را به وسیله روح القدس توانایی بخشیدیم؛ پس چرا هرگاه پیامبری آیین و احکامی که مطابق هوا و هوستان نبود برای شما آورد، سرکشی کردید؟ پس [نبوّتِ] گروهی را تکذیب نمودید وگروهی را می کشتید.'),
(95, 2, 88, 'و گفتند: دل های ما در غلاف و پوشش است [به این علتْ کلام تو را نمی فهمیم، ولی چنین نیست که می گویند] بلکه خدا به سبب کفرشان آنان را از رحمتش دور کرده [در نتیجه از پذیرفتن اسلام خودداری می کنند] پس اندکی ایمان می آورند.'),
(96, 2, 89, 'و هنگامی که برای آنان از سوی خدا کتابی [چون قرآن] آمد که تصدیق کننده توراتی است که با آنان است، و همواره پیش از نزولش به خودشان [در سایه ایمان به آن] مژده پیروزی بر کافران می دادند، پس [با این وصف] زمانی که قرآن [که پیش از نزولش آن را با پیشگویی تورات می شناختند] نزد آنان آمد، به آن کافر شدند؛ پس لعنت خدا بر کافران باد.'),
(97, 2, 90, 'بد چیزی است آنچه خود را به آن فروختند که کفر ورزیدن از روی حسادت به کتابی است که خدا نازل کرده [معترضانه می گویند:] چرا خدا از فضل و احسانش به هر که از بندگانش بخواهد [کتاب آسمانی] نازل می کند، پس آنان به خشمی بر روی خشمی سزاوار شدند. و برای کافران عذابی خوار کننده است.'),
(98, 2, 91, 'هنگامی که به آنان گویند: به آنچه که خدا [بر پیامبر اسلام] نازل کرده ایمان آورید، گویند: به توراتی که بر خود ما یهودیان نازل شده ایمان می آوریم و به غیر آن در حالی که حق است و تصدیق کننده توراتی است که با آنان است، کفر می ورزند. بگو: اگر شما [از روی درستی و راستی به تورات] مؤمن بودید، پس چرا پیش از این پیامبران خدا را می کشتید؟'),
(99, 2, 92, 'و قطعاً موسی برای شما معجزات و دلایلی روشن آورد، سپس شما پس از [رفتن] او [به کوه طور] گوساله را معبود خود گرفتید، در حالی که [به خود و دلایل آشکار و روشن حق] ستمکار بودید.'),
(100, 2, 93, 'و [یاد کنید] زمانی که از شما [برای پیروی از موسی] پیمان گرفتیم، و کوه طور را بالای سرتان برافراشتیم [و گفتیم:] آنچه را [چون تورات] به شما دادیم با قدرت و قوّت دریافت کنید [و دستورهای ما و پیامبرتان را بشنوید، به ظاهر] گفتند: شنیدیم و [در باطن گفتند:] نافرمانی کردیم. و به سبب کفرشان دوستی گوساله با دل هایشان در آمیخت. بگو: اگر شما مؤمن هستید [و ایمانتان شما را به این همه ظلم و جنایت و فساد فرمان می دهد] پس بد چیزی است آنچه ایمانتان به آن فرمان می دهد.'),
(101, 2, 94, 'بگو: اگر [آن گونه که می پندارید] سرای آخرت [با همه نعمت هایش] نزد خدا ویژه شماست نه مردم دیگر، پس چنانچه راستگویید مرگ را آرزو کنید.'),
(102, 2, 95, 'و آنان هرگز مرگ را به سبب گناهانی که مرتکب شده اند، آرزو نمی کنند؛ و خدا به ستمکاران داناست.'),
(103, 2, 96, 'و یقیناً آنان را حریص ترین مردم به زندگی [دراز مدت] خواهی یافت و [حتی حریص تر] از مشرکان. هر یک از آنان آرزومند است که ای کاش هزار سال عمرش دهند، ولی آن عمر طولانی دور کننده او از عذاب نیست؛ و خدا به آنچه انجام می دهند، بیناست.'),
(104, 2, 97, '[آنان می گویند: چون جبرئیل، وحی را برای تو می آورد ما با او دشمنیم؛ بنابراین به تو ایمان نمی آوریم] بگو: هر که دشمن جیرئیل است [دشمن خداست] زیرا او قرآن را به فرمان خدا بر قلب تو نازل کرده است، در حالی که تصدیق کننده کتاب های پیش از خود و هدایت وبشارت برای مؤمنان است.'),
(105, 2, 98, 'هر که با خدا و فرشتگان و رسولانش و جبرئیل و میکائیل دشمن باشد [کافر است]، و بی تردید خدا دشمن کافران است.'),
(106, 2, 99, 'و یقیناً به سوی تو آیاتی روشن نازل کردیم، و به آنها جز فاسقان کافر نمی شوند.'),
(107, 2, 100, 'آیا چنین نیست که هرگاه یهود پیمانی [با خدا و رسولانش] بستند، گروهی از آنان، آن را شکستند؟ [آنان نه فقط عهد شکنند] بلکه بیشترشان ایمان نمی آورند.'),
(108, 2, 101, 'و زمانی که فرستاده ای از سوی خدا به سویشان آمد که تصدیق کننده کتابی است که با آنان است، گروهی از آنان که [دانش] کتاب به آنان داده شده بود [با کمال گستاخی] کتاب خدا را پشت سر انداختند [و با آن به مخالفت برخاستند]، گویی نمی دانند [که کلام خداست].'),
(109, 2, 102, 'و [یهودیان] از آنچه شیاطین در زمان پادشاهی سلیمان [از علم سحر بر مردم می خواندند] پیروی کردند. و [سلیمان دست به سحر نیالود تا کافر شود، بنابراین] سلیمان کافر نشد، ولی شیاطین که به مردم سحر می آموختند، کافر شدند. و [نیز یهودیان] از آنچه بر دو فرشته هاروت و ماروت در شهر بابل نازل شد [پیروی کردند]، و حال آنکه آن دو فرشته به هیچ کس نمی آموختند مگر آنکه می گفتند: ما فقط مایه آزمایشیم [و علم سحر را برای مبارزه با ساحران و باطل کردن سحرشان به تو تعلیم می دهیم]، پس [با به کار گرفتن آن در مواردی که ممنوع و حرام است] کافر مشو. اما آنان از آن دو فرشته مطالبی [از سحر] می آموختند که با آن میان مرد و همسرش جدایی می انداختند؛ در حالی که آنان به وسیله آن سحر جز به اذن خدا قدرت آسیب رساندن به کسی را نداشتند؛ و همواره چیزی را می آموختند که به آنان آسیب می رسانید و سودی نمی بخشید؛ و یقیناً [یهود] می دانستند که هر کس خریدار سحر باشد، در آخرت هیچ بهره ای ندارد. و همانا بدچیزی است آنچه خود را به آن فروختند اگر معرفت می داشتند.'),
(110, 2, 103, 'و اگر آنان [از روی حقیقت] ایمان می آوردند و [از سحر و جادو] پرهیز می کردند، قطعاً پاداشی که از جانب خداست [برای آنان] بهتر بود، اگر می دانستند.'),
(111, 2, 104, 'ای کسانی که ایمان آورده اید! [هنگام سخن گفتن با پیامبر] مگویید: راعنا [یعنی: در ارائه احکام، امیال و هوس های ما را رعایت کن] و بگویید: انظرنا [یعنی: مصلحت دنیا و آخرت ما را ملاحظه کن] و [فرمان های خدا و پیامبرش را] بشنوید. و برای کافران عذابی دردناک است.'),
(112, 2, 105, 'نه کافران از اهل کتاب و نه مشرکان، دوست ندارند که هیچ خیری از سوی پروردگارتان بر شما نازل شود، در حالی که خدا هرکه را بخواهد به رحمت خود اختصاص می دهد؛ و خدا دارای فضل بزرگی است.'),
(113, 2, 106, 'هر آیه ای را که [محتوی حکم یا احکامی است وقتی بر پایه مصلحت یا اقتضای زمان] از میان برداریم یا به تأخیر اندازیم، بهتر از آن یا مانندش را می آوریم. آیا ندانسته ای که خدا بر هر کاری تواناست؟!'),
(114, 2, 107, 'آیا ندانسته ای که فرمانروایی و حکومت آسمان ها و زمین فقط در سیطره اوست و شما را جز خدا هیچ سرپرست و یاوری نیست؟'),
(115, 2, 108, 'بلکه می خواهید از پیامبرتان [کارهای نامعقول و بیهوده] درخواست کنید، همان گونه که پیش از این از موسی درخواست شد؟ و کسی که کفر را به جای ایمان اختیار کند، تحقیقاً راه راست را گم کرده است.'),
(116, 2, 109, 'بسیاری از اهل کتاب پس از آنکه حق برای آنان روشن شد به سبب حسدی که از وجودشان شعله کشیده، دوست دارند که شما را پس از ایمانتان به کفر بازگردانند. هم اکنون [از ستیز و جدال با آنان] درگذرید، و [از آنان] روی بگردانید، تا خدا فرمانش را [به جنگ یا جزیه] اعلام کند؛ یقیناً خدا بر هر کاری تواناست.'),
(117, 2, 110, 'و نماز را برپا دارید، و زکات بپردازید، و آنچه از کار نیک برای خود پیش فرستید آن را نزد خدا خواهید یافت. مسلماً خدا به آنچه انجام می دهید، بیناست.'),
(118, 2, 111, 'و گفتند: هرگز کسی وارد بهشت نمی شود مگر آنکه یهودی یا نصرانی باشد، این دروغ ها و اباطیل آنان است؛ بگو: اگر راستگویید دلیل و برهان خود را بیاورید.'),
(119, 2, 112, 'آری، کسانی که همه وجود خود را تسلیم خدا کنند در حالی که نیکوکارند، برای آنان نزد پروردگارشان پاداشی شایسته و مناسب است، نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(120, 2, 113, 'ویهودیان گفتند: نصرانی ها بر آیین و روش درستی نیستند و نصرانی ها گفتند: یهودیان بر آیین و روش درستی نیستند، در حالی که همه آنان کتاب [آسمانی] را می خوانند [که هر دو آیین در زمان مخصوص به خود درست و بر حق بوده] کسانی که نادانند [هم چون مشرکان] سخنی مانند سخن آنان [درباره آیین مسلمانان] گفتند؛ پس خدا روز قیامت در میان آنان درباره آنچه با هم اختلاف می کردند، داوری می کند.'),
(121, 2, 114, 'و چه کسانی ستمکارتر از کسانی می باشند که از بردن نام خدا در مساجد خدا جلوگیری کردند، و در خرابی آنها کوشیدند؟ آنان را شایسته نیست که در آن [مراکز عبادت] درآیند، جز در حال ترس [از عدالت و انتقام خدا]. برای آنان در دنیا خواری و زبونی است و در آخرت عذابی بزرگ است.'),
(122, 2, 115, 'مالکیّتِ مشرق و مغرب فقط ویژه خداست؛ پس به هر کجا رو کنید آنجا روی خداست. یقیناً خدا بسیار عطا کننده و داناست.'),
(123, 2, 116, 'و [یهود و نصاری] گفتند: خدا [برای خود] فرزندی گرفته، منزّه است او [از اوصاف دروغ و باطلی که به حضرتش نسبت می دهند]، بلکه آنچه در آسمان ها و زمین است مخلوق و مملوک اویند [نه فرزند او؛ زیرا فرزند، هم جنس پدر و مادر است و چیزی در جهان هم جنس خدا نیست تا فرزند خدا باشد] و همه در برابر او مطیع و فرمانبردارند.'),
(124, 2, 117, '[بی سابقه ماده، مدت و نقشه] پدیدآورنده آسمان ها و زمین است. وهنگامی که فرمان به وجود آمدن چیزی را صادر کند فقط به آن می گوید: «باش». پس بی درنگ می باشد.'),
(125, 2, 118, 'و کسانی که نادان و ناآگاهند، گفتند: چرا خدا با ما سخن نمی گوید یا نشانه و معجزه ای برای ما نمی آید؟ گذشتگان آنان نیز مانند گفته ایشان را گفتند؛ دل هایشان [در تعصّب، لجاجت، عناد و نادانی] شبیه هم است. تحقیقاً ما [به اندازه لازم] نشانه ها را برای اهل باور بیان کرده ایم.'),
(126, 2, 119, 'قطعاً ما تو را به درستی و راستی، بشارت دهنده وبیم رسان فرستادیم، و تو [بعد از ابلاغ پیام حق] مسؤولِ [ایمان نیاوردن و وضع و حال] دوزخیان نیستی.'),
(127, 2, 120, 'یهود و نصاری هرگز از تو راضی نمی شوند تا آنکه از آیینشان پیروی کنی. بگو: مسلماً هدایت خدا فقط هدایت [واقعی] است. و اگر پس از دانشی که [چون قرآن] برایت آمده از هوا و هوس های آنان پیروی کنی، از سوی خدا هیچ سرپرست و یاوری برای تو نخواهد بود.'),
(128, 2, 121, 'کسانی که کتاب آسمانی به آنان عطا کرده ایم، آن را به طوری که شایسته آن است قرائت می کنند [و آن قرائت نمودن با تدبر و به قصد عمل است] اهل ایمان به آن [کتاب] هستند و کسانی که به آن کفر می ورزند فقط آنان زیانکارند.'),
(129, 2, 122, 'ای بنی اسرائیل! نعمت های مرا که به شما عطا کردم و اینکه شما را بر جهانیان [زمان خودتان] برتری دادم، یاد کنید.'),
(130, 2, 123, 'و از روزی پروا کنید که نه کسی از کسی عذابی را دفع می کند، و نه از کسی [در برابر گناهانش] فدیه و عوضی می گیرند، و نه کسی را شفاعتی سود دهد، و نه [برای رهایی از آتش دوزخ] یاری می شوند.'),
(131, 2, 124, 'و [یاد کنید] هنگامی که ابراهیم را پروردگارش به اموری [دشوار و سخت] آزمایش کرد، پس او همه را به طور کامل به انجام رسانید، پروردگارش [به خاطر شایستگی ولیاقت او] فرمود: من تو را برای همه مردم پیشوا و امام قرار دادم. ابراهیم گفت: و از دودمانم [نیز پیشوایانی برگزین]. [پروردگار] فرمود: پیمان من [که امامت و پیشوایی است] به ستمکاران نمی رسد.'),
(132, 2, 125, 'و [یاد کنید] هنگامی که ما این خانه [کعبه] را برای همه مردم محل گردهمایی و جای امن وامان قرار دادیم، و [فرمان دادیم:] از مقام ابراهیم جایگاهی برای نماز انتخاب کنید. و به ابراهیم و اسماعیل سفارش کردیم که خانه ام را برای طواف کنندگان و اعتکاف کنندگان و رکوع کنندگان وسجده گذاران [از هر آلودگی ظاهری و باطنی] پاکیزه کنید.'),
(133, 2, 126, 'و [یاد کنید] آن گاه که ابراهیم گفت: پروردگارا! این [مکان] را شهری امن قرار ده و اهلش را آنان که به خدا و روز قیامت ایمان آورده اند از هر نوع میوه و محصول روزی بخش. خدا فرمود: [دعایت را درباره مؤمنان اجابت کردم، ولی] هر که کفر ورزد بهره اندکی به او خواهم داد، سپس او را به عذاب آتش می کشانم و آن بد بازگشت گاهی است.'),
(134, 2, 127, 'و [یاد کنید] زمانی که ابراهیم و اسماعیل پایه های خانه کعبه را بالا می بردند [و به پیشگاه حق می گفتند:] پروردگارا! [این عمل را] از ما بپذیر که تو شنوا و دانایی،'),
(135, 2, 128, 'پروردگارا! ما را [با همه وجود] تسلیم خود قرار ده، و نیز از دودمان ما امتی که تسلیم تو باشند پدید آر، و راه و رسم عبادتمان را به ما نشان ده، و توبه ما را بپذیر، که تو بسیار توبه پذیر و مهربانی،'),
(136, 2, 129, 'پروردگارا! در میان آنان پیامبری از خودشان برانگیز، که آیات تو را بر آنان بخواند، و آنان را کتاب و حکمت بیاموزد، و [از آلودگی های ظاهری و باطنی] پاکشان کند؛ زیرا تو توانای شکست ناپذیر و حکیمی.'),
(137, 2, 130, 'وکیست که از آیین ابراهیم روی گردان شود، جز کسی که [خود را خوار و بی ارزش کند و] خویش را به نادانی و سبک مغزی زند؟ یقیناً ما ابراهیم را در دنیا [به امامت و رسالت] برگزیدیم، و قطعاً در آخرت از شایستگان است.'),
(138, 2, 131, '[و یاد کنید] هنگامی که پروردگارش به او فرمود: تسلیم باش. گفت: به پروردگار جهانیان تسلیم شدم.'),
(139, 2, 132, 'و ابراهیم و یعقوب پسرانشان را به آیین اسلام سفارش کردند که ای پسران من! یقیناً خدا این دین را برای شما برگزیده، پس شما باید جز در حالی که مسلمان باشید، نمیرید.'),
(140, 2, 133, 'آیا شما [یهودیان که ادعا می کنید یعقوب پسرانش را به آیین شما سفارش کرد] هنگامی که یعقوب را مرگ در رسید [کنار بستر او] حاضر بودید؟ [یقیناً حاضر نبودید] آن گاه که به پسران خود گفت: پس از من چه چیزی را می پرستید؟ گفتند: خدای تو و خدای پدرانت ابراهیم و اسماعیل و اسحاق را که خدای یگانه است می پرستیم، و ما تسلیم اوییم.'),
(141, 2, 134, 'آنان گروهی بودند که درگذشتند، آنچه [از طاعت و معصیت] به دست آوردند مربوط به خود آنان است، و آنچه شما به دست آوردید مربوط به خود شماست؛ و شما در برابر آنچه آنان انجام می دادند، مسؤول نیستید.'),
(142, 2, 135, 'و [اهل کتاب به مردم مؤمن] گفتند: یهودی یا نصرانی باشید تا هدایت یابید. بگو: بلکه از آیین ابراهیم یکتاپرست و حق گرا [پیروی می کنیم نه از آیین تحریف شده شما که عین گمراهی است] و او هرگز از مشرکان نبود.'),
(143, 2, 136, '[شما مردم مؤمن از روی حقیقت اقرار کنید و] بگویید: ما به خدا و آنچه به سوی ما نازل شده، و به آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگانِ [دارای مقام نبوّت] آنان فرود آمده، و به آنچه به موسی و عیسی و آنچه به پیامبران از ناحیه پروردگارشان داده شده ایمان آوردیم؛ میان هیچ یک از آنان [در اینکه از سوی خدا برای هدایت مردم مبعوث شده اند] فرقی نمی گذاریم، و ما در برابر او تسلیم هستیم.'),
(144, 2, 137, 'پس اگر آنان هم به آنچه شما به آن ایمان آورده اید، ایمان آورند [که ایمان به قرآن و به پیامبر است] مسلماً هدایت یافته اند، و اگر روی برتابند جز این نیست که در ستیز و دشمنی اند؛ پس به زودی خدا شرّ آنان را [به کشته شدن یا آوارگی از خانه و کاشانه] دفع خواهد کرد؛ و او شنوا و داناست.'),
(145, 2, 138, '[به یهود و نصاری بگویید:] رنگ خدا را [که اسلام است، انتخاب کنید] و چه کسی رنگش نیکوتر از رنگ خداست؟ و ما فقط پرستش کنندگان اوییم.'),
(146, 2, 139, 'بگو: آیا با ما در باره خدا گفتگوی بی منطق و احتجاج نادرست می کنید؟! در حالی که او پروردگار ما و شماست [و همه کارهایش بر وفق حکمت و مصلحت است، و جای گفتگوی بی منطق و احتجاج نادرست نیست]؛ و اعمال ما مربوط به خود ما و اعمال شما مربوط به خود شماست، و ما [در ایمان، اعتقاد، طاعت و عبادت] برای او اخلاص می ورزیم.'),
(147, 2, 140, 'آیا می گویید: ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگان [دارای مقام نبوّت] آنان یهودی یا نصرانی بودند؟! بگو: شما داناترید یا خدا؟ [شما به یقین می دانید که آنان یهودی و نصرانی نبودند، پس چرا واقعیت را پنهان می دارید؟!] وستمکارتر از کسی که شهادتی را که از خدا نزد اوست پنهان کند، کیست؟ وخدا از آنچه انجام می دهید، بی خبر نیست.'),
(148, 2, 141, 'آنان گروهی بودند که درگذشتند؛ آنچه [طاعت و معصیت] به دست آوردند مربوط به خود آنان است و آنچه شما به دست آوردید مربوط به خود شماست؛ و شما در برابر آنچه آنان انجام می دادند، مسؤول نیستید.'),
(149, 2, 142, 'به زودی مردم سبک مغز می گویند: چه چیزی مسلمانان را از قبله ای که بر آن بودند [یعنی بیت المقدس، به سوی کعبه] گردانید؟ بگو: مالکیّتِ مشرق و مغرب فقط ویژه خداست، هر که را بخواهد به راه راست هدایت می کند.'),
(150, 2, 143, 'و همان گونه [که شما را به راه راست هدایت کردیم] شما را امتی میانه [ومعتدل و پیراسته از افراط و تفریط] قرار دادیم تا [در ایمان، عمل، درستی و راستی] بر مردم گواه باشید و پیامبر هم گواه بر شما باشد. و ما قبله ای را که بر آن بودی فقط به خاطر این قرار دادیم تا کسانی که از پیامبر پیروی می کنند از کسانی که از اسلام و اطاعت پیامبر برمی گردند [و متعصبانه به قبله پیش از کعبه می مانند] معلوم و مشخص کنیم؛ گر چه این حکم جز بر کسانی که خدا هدایتشان کرده گران و دشوار بود. و خدا بر آن نیست که ایمان شما را تباه کند؛ زیرا خدا به همه مردم رؤوف و مهربان است.'),
(151, 2, 144, 'ما گردانیدن رویت را در [جهت] آسمان [چون کسی که به انتظار مطلبی باشد] می بینیم؛ پس یقیناً تو را به سوی قبله ای که آن را بپسندی برمی گردانیم؛ پس رویت را به سوی مسجد الحرام گردان؛ و [شما ای مسلمانان!] هر جا که باشید، روی خود را به سوی آن برگردانید. و مسلماً اهل کتاب می دانند که این تغییر قبله [از بیت المقدس به کعبه] از سوی پروردگارشان [کاری] درست و حق است؛ [زیرا در تورات و انجیلشان خوانده اند که پیامبر اسلام به دو قبله نماز خواهد خواند] و خدا از آنچه [بر ضد پیامبر و مؤمنان] انجام می دهند، بی خبر نیست.'),
(152, 2, 145, 'به خدا سوگند اگر برای اهل کتاب هر نشانه و دلیلی بیاوری، از قبله تو پیروی نمی کنند، و تو هم از قبله آنان پیروی نخواهی کرد و [نیز] برخی از آنان [که یهودی اند] از قبله دیگران [که نصرانی اند] پیروی نخواهند کرد. اگر پس از دانشی که [چون قرآن] برایت آمده از هوا و هوس های آنان پیروی کنی، مسلماً در آن صورت از ستمکاران خواهی بود.'),
(153, 2, 146, 'اهل کتاب، پیامبر اسلام را [بر اساس اوصافش که در تورات و انجیل خوانده اند] می شناسند، به گونه ای که پسران خود را می شناسند؛ و مسلماً گروهی از آنان حق را در حالی که می دانند، پنهان می دارند.'),
(154, 2, 147, '[ای امت این] حق [که تغییرِ قبله است] از سوی پروردگار توست؛ بنابراین از تردید کنندگان نسبت به حق مباش.'),
(155, 2, 148, 'برای هر گروهی قبله ای است که خدا گرداننده روی آن گروه به آن قبله است؛ پس [درباره قبله، نزاع و کشمکش نکنید و به جای بحث و گفتگو] به جانب نیکی ها و کارهای خیر پیشی جویید. خدا همه شما را هر جا که باشید [به صحرای محشر] می آورد؛ مسلماً خدا بر هر کاری تواناست.'),
(156, 2, 149, 'از هر جا [برای سفر یا کار دیگر] بیرون شدی، رویت را [در حال اقامه نماز] به سوی مسجد الحرام بگردان. بدون تردید این فرمان از نزد پروردگارت درست وحق است و خدا از آنچه انجام می دهید، بی خبر نیست.'),
(157, 2, 150, '[آری] و از هرجا بیرون شدی، رویت را [در حال اقامه نماز] به سوی مسجدالحرام بگردان؛ و [شما ای مؤمنان!] هر جا که باشید، رویتان را [در حال اقامه نماز] به سوی آن بگردانید، تا مردم را جز ستمکاران [لجوج، متکبر و یاوه گو] بر ضد شما دلیل و برهانی نباشد. [مشرکان نگویند: اگر آیین ابراهیم را ادعا دارد چرا به قبله او که کعبه است رو نمی کند؟ و اهل کتاب نگویند: پیامبری که تورات و انجیل وعده داده، قبله اش مسجدالحرام است، پس این شخص که به قبله ما نماز می خواند، پیامبر موعود نیست؛] بنابراین از آنان نترسید و از من بترسید، و [تغییر قبله] برای آن است که نعمتم را بر شما کامل کنم تا [به احکام و سنن الهی] هدایت شوید.'),
(158, 2, 151, 'همان گونه که [تغییر قبله، کامل کردن نعمت است، این حقیقت هم کامل کردن نعمت است که] در میان شما رسولی از خودتان فرستادیم که همواره آیات ما را بر شما می خواند، و شما را [از هر نوع آلودگی ظاهری و باطنی] پاک و پاکیزه می کند، و کتاب وحکمت به شما می آموزد، وآنچه را نمی دانستید به شما تعلیم می دهد.'),
(159, 2, 152, 'پس مرا یاد کنید تا شما را یاد کنم و مرا سپاس گزارید و کفران نعمت نکنید.'),
(160, 2, 153, 'ای اهل ایمان از صبر و نماز [برای حل مشکلات خود و پاک ماندن از آلودگی ها و رسیدن به رحمت حق] کمک بخواهید زیرا خدا با صابران است.'),
(161, 2, 154, 'و به آنان که در راه خدا کشته می شوند مرده نگویید، بلکه [در عالم برزخ] دارای حیات اند، ولی شما [کیفیت آن حیات را] درک نمی کنید.'),
(162, 2, 155, 'و بی تردید شما را به چیزی اندک از ترس و گرسنگی و کاهش بخشی از اموال و کسان و محصولات [نباتی یا ثمرات باغ زندگی از زن و فرزند] آزمایش می کنیم. و صبرکنندگان را بشارت ده.'),
(163, 2, 156, 'همان کسانی که چون بلا و آسیبی به آنان رسد گویند: ما مملوک خداییم و یقیناً به سوی او بازمی گردیم.'),
(164, 2, 157, 'آنانند که درودها و رحمتی از سوی پروردگارشان بر آنان است و آنانند که هدایت یافته اند.'),
(165, 2, 158, 'بی تردید صفا و مروه از نشانه های خداست؛ پس کسی که حج خانه کعبه کند، یا عمره انجام دهد، بر او گناهی نیست که بر آن دو طواف کند. و کسی که [به خواست خودش افزون بر واجبات] کار نیکی [چون طواف و سعی مستحب] انجام دهد، بدون تردید خدا [در برابر عمل او] پاداش دهنده و [به کار وحال او] داناست.'),
(166, 2, 159, 'یقیناً کسانی که آنچه را ما از دلایل آشکار و [وسیله] هدایت نازل کردیم، پس از آنکه همه آن را در کتاب [تورات و انجیل] برای مردم روشن ساختیم، پنهان می کنند [تا مردم به قرآن و پیامبر ایمان نیاورند] خدا لعنتشان می کند، و لعنت کنندگان هم لعنتشان می کنند.'),
(167, 2, 160, 'مگر کسانی که توبه کردند، و [مفاسد خود را] اصلاح نمودند، و [آنچه را پنهان کرده بودند] برای مردم روشن ساختند، پس توبه آنان را می پذیرم؛ زیرا من بسیار توبه پذیر و مهربانم.'),
(168, 2, 161, 'قطعاً کسانی که کافر شدند، و در حال کفر از دنیا رفتند، لعنت خدا و فرشتگان و همه مردم بر آنان است.'),
(169, 2, 162, 'در آن لعنت جاودانه اند، نه عذاب از آنان سبک شود و نه مهلتشان دهند [تا عذر خواهی کنند].'),
(170, 2, 163, 'و خدای شما خدای یگانه است، جز او خدایی نیست، رحمتش بی اندازه و مهربانی اش همیشگی است.'),
(171, 2, 164, 'بی تردید در آفرینش آسمان ها و زمین و رفت و آمد شب و روز و کشتی هایی که در دریاها [با جابجا کردن مسافر و کالا] به سود مردم روانند و بارانی که خدا از آسمان نازل کرده و به وسیله آن زمین را پس از مردگی اش زنده ساخته و در آن از هر نوع جنبنده ای پراکنده کرده و گرداندن بادها و ابرِ مسخّر میان آسمان و زمین، نشانه هایی است [از توحید، ربوبیّت و قدرت خدا] برای گروهی که می اندیشند.'),
(172, 2, 165, 'و برخی از مردم به جای خدا معبودهایی انتخاب می کنند که آنها را آن گونه که سزاوار است خدا را دوست داشته باشند، دوست می دارند؛ ولی آنان که ایمان آورده اند، محبت و عشقشان به خدا بیشتر و قوی تر است. و اگر کسانی که [با انتخاب معبودهای باطل] ستم روا داشتند، هنگامی که عذاب را ببینند، بی تردید بفهمند که همه قدرت ویژه خداست [و معبودهای باطل، هیچ و پوچ اند] و خدا سخت کیفر است.'),
(173, 2, 166, 'در آن هنگام پیشوایان [شرک و کفر] از پیروانشان بیزاری جویند، و عذاب را مشاهده کنند، و همه دست آویزها و پیوندها از آنان بریده شود.'),
(174, 2, 167, 'و آنان که [به جای خدا از پیشوایان شرک و کفر] پیروی کردند، گویند: کاش برای ما بازگشتی [به دنیا] بود تا ما هم از آنان بیزاری می جستیم، همان گونه که آنان از ما بیزاری جستند. خدا این گونه اعمالشان را که برای آنان مایه اندوه و دریغ است، به آنان نشان می دهد و اینان هرگز از آتش بیرون آمدنی نیستند.'),
(175, 2, 168, 'ای مردم! از آنچه [از انواع میوه ها و خوردنی ها] در زمین حلال و پاکیزه است، بخورید و از گام های شیطان پیروی نکنید؛ زیرا او نسبت به شما دشمنی آشکار است.'),
(176, 2, 169, 'فقط شما را به بدی و زشتی فرمان می دهد، و اینکه جاهلانه اموری را [به عنوان حلال و حرام] به خدا نسبت دهید.'),
(177, 2, 170, 'و هنگامی که به آنان [که مشرک و کافرند] گویند: از آنچه خدا نازل کرده پیروی کنید، می گویند: نه، بلکه از آیینی که پدرانمان را بر آن یافتیم، پیروی می کنیم. آیا هر چند پدرانشان چیزی نمی فهمیدند و راه [حق را به سبب کوردلی] نمی یافتند [باز هم کورکورانه از آنان پیروی خواهند کرد؟!]'),
(178, 2, 171, 'داستان [دعوت کننده] کافران [به ایمان]، مانند کسی است که به حیوانی [برای رهاندنش از خطر] بانگ می زند، ولی آن حیوان جز آوا و صدا [آن هم آوا و صدایی که مفهومش را درک نمی کند] نمی شنود. [کافران، در حقیقت] کر و لال و کورند، به همین سبب [درباره حقایق] اندیشه نمی کنند.'),
(179, 2, 172, 'ای اهل ایمان! از انواع میوه ها و خوردنی های پاکیزه ای که روزی شما کرده ایم، بخورید و خدا را سپاس گزارید، اگر فقط او را می پرستید.'),
(180, 2, 173, 'جز این نیست که خدا [برای مصون ماندن شما از زیان های جسمی و روحی] مردار و خون و گوشت خوک و حیوانی را که [هنگام ذبح] نام غیر خدا بر آن برده شده، بر شما حرام کرده. پس کسی که [برای نجات جانش از خطر] به خوردن آنها ناچار شود، در حالی که خواهان لذت نباشد و از حدّ لازم تجاوز نکند، گناهی بر او نیست؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(181, 2, 174, 'قطعاً کسانی که آنچه را خدا از کتاب [تورات و انجیل به عنوان حلال و حرام] نازل کرده [به سود خود، از بی سوادان یهود و نصاری] پنهان می کنند، و در برابر این پنهان کاری بهای اندکی به دست می آورند، جز آتش به شکم های خود نمی ریزند. و خدا روز قیامت با آنان سخن نمی گوید، و [از گناهان و زشتی ها] پاکشان نمی کند، و برای آنان عذابی دردناک است.'),
(182, 2, 175, 'اینانند که گمراهی را به جای هدایت، و عذاب را به عوض آمرزش خریده اند، شگفتا! چه شکیبایند بر آتش.'),
(183, 2, 176, 'آن عذاب به خاطر این است که خدا کتاب آسمانی را به درستی و راستی نازل کرد [ولی اینان، آن را واگذاشتند و در تکذیبش کوشیدند و آن را مایه اختلاف قرار دادند] و یقیناً آنان که در [مفهوم و محتوای] کتاب اختلاف کردند [تا حقایق از مردم پنهان بماند] در دشمنی دور و درازی قرار دارند.'),
(184, 2, 177, 'نیکی این نیست که روی خود را به سوی مشرق و مغرب کنید، بلکه نیکی [واقعی و کامل، که شایسته است در همه امور شما ملاک و میزان قرار گیرد، منش و رفتار و حرکات] کسانی است که به خدا و روز قیامت و فرشتگان و کتاب آسمانی و پیامبران ایمان آورده اند، و مال و ثروتشان را با آنکه دوست دارند به خویشان و یتیمان و درماندگان و در راه ماندگان و سائلان و [در راه آزادی] بردگان می دهند، و نماز را [با همه شرایطش] برپای می دارند، و زکات می پردازند، و چون پیمان بندند وفاداران به پیمان خویشند، و در تنگدستی و تهیدستی و رنج و بیماری و هنگام جنگ شکیبایند؛ اینانند که [در دین داری و پیروی از حق] راست گفتند، و اینانند که پرهیزکارند.'),
(185, 2, 178, 'ای اهل ایمان! در مورد کشته شدگان بر شما قصاص مقرّر و لازم شده: آزاد در برابر آزاد، برده در برابر برده، زن در برابر زن. پس کسی که [مرتکب قتل شده چنانچه] از سوی برادر [دینی] اش [که ولیِّ مقتول است] مورد چیزی از عفو قرار گرفت [که به جای قصاص، دیه و خون بها پرداخت شود] پس پیروی از روش شایسته و پسندیده [نسبت به وضع مادی قاتل بر عهده عفو کننده است]، و پرداخت دیه و خون بها با نیکویی و خوش رویی [بر عهده قاتل است]. این [حکم] تخفیف و رحمتی است از سوی پروردگارتان؛ پس هر که بعد از عفو، تجاوز کند [و به قصاص قاتل برخیزد] برای او عذابی دردناک است.'),
(186, 2, 179, 'ای صاحبان خرد! برای شما در قصاص مایه زندگی است، باشد که [از ریختن خون مردم بدون دلیل شرعی] بپرهیزید.'),
(187, 2, 180, 'بر شما مقرّر و لازم شده چون یکی از شما را مرگ در رسد، اگر مالی از خود به جا گذاشته است، برای پدر و مادر و خویشان به طور شایسته و پسندیده وصیت کند. این حقّی است بر عهده پرهیزکاران.'),
(188, 2, 181, 'پس کسانی که بعد از شنیدن وصیت، آن را تغییر دهند، گناهش فقط بر عهده آنانی است که تغییرش می دهند؛ یقیناً خدا شنوا و داناست.'),
(189, 2, 182, 'پس کسی که از انحراف وصیت کننده [در مورد حقوق ورثه] یا از گناه او [که به کار نامشروع و ناحقّی وصیت کند] بترسد، و میان ورثه [با تغییر دادن وصیت بر اساس احکام دین] اصلاح دهد، گناهی بر او نیست؛ یقیناً خدا بسیار آمرزنده و مهربان است.'),
(190, 2, 183, 'ای اهل ایمان! روزه بر شما مقرّر و لازم شده، همان گونه که بر پیشینیان شما مقرّر و لازم شد، تا پرهیزکار شوید.'),
(191, 2, 184, '[در] روزهایی چند [روزه بگیرید]؛ پس هر که از شما بیمار یا در سفر باشد [به تعداد روزه های فوت شده] از روزهای دیگر [را روزه بگیرد]؛ و بر آنان که روزه گرفتن طاقت فرساست، طعام دادن به یک نیازمند [به جای هر روز] کفاره آن است. و هر که به خواست خودش افزون بر کفاره واجب، بر طعام نیازمند بیفزاید، برایش بهتر است و روزه گرفتن [هر چند دشوار و سخت باشد] اگر [فضیلت و ثوابش را] بدانید برای شما بهتر است.'),
(192, 2, 185, '[این است] ماه رمضان که قرآن در آن نازل شده، قرآنی که سراسرش هدایتگر مردم است و دارای دلایلی روشن و آشکار از هدایت می باشد، و مایه جدایی [حق از باطل] است. پس کسی که در این ماه [در وطنش] حاضر باشد باید آن را روزه بدارد، و آنکه بیمار یا در سفر است، تعدادی از روزهای غیر ماه رمضان را [به تعداد روزه های فوت شده، روزه بدارد]. خدا آسانی و راحت شما را می خواهد نه دشواری و مشقت شما را. و [قضای روزه] برای این است: شما روزه هایی را [که به خاطر عذر شرعی افطار کرده اید] کامل کنید، و خدا را بر اینکه شما را هدایت فرموده بزرگ شمارید، و نیز برای این که سپاس گزاری کنید.'),
(193, 2, 186, 'هنگامی که بندگانم از تو درباره من بپرسند، [بگو:] یقیناً من نزدیکم، دعای دعا کننده را زمانی که مرا بخواند اجابت می کنم؛ پس باید دعوتم را بپذیرند و به من ایمان آورند، تا [به حقّ و حقیقت] راه یابند [و به مقصد اعلی برسند].'),
(194, 2, 187, 'در شبِ روزهایی که روزه دار هستید، آمیزش با زنانتان برای شما حلال شد. آنان برای شما لباسند و شما برای آنان لباسید. خدا می دانست که شما [پیش از حلال شدن این کار] به خود خیانت می کردید، پس توبه شما را پذیرفت و از شما در گذشت. اکنون [آزاد هستید که] با آنان آمیزش کنید و آنچه را خدا [در این کار] برای شما مقرّر داشته [از فضیلت، ثواب، پاکدامنی از حرام و فرزند شایسته] طلب کنید. و بخورید و بیاشامید تا رشته سپید صبح از رشته سیاه شب برای شما آشکار شود؛ سپس روزه را تا شب به پایان برید. و در حالی که در مساجد معتکف هستید با زنان آمیزش نکنید. اینها حدود خداست، به آنها نزدیک نشوید. خدا این گونه آیاتش را برای مردم بیان می کند تا [از مخالفت اوامر و نواهی او] بپرهیزند.'),
(195, 2, 188, 'و اموالتان را در میان خود به باطل و ناحق مخورید. و آن را به عنوان رشوه به سوی حاکمان و قاضیان سرازیر نکنید تا بخشی از اموال مردم را [با تکیه بر حکم ظالمانه آنان] به گناه و معصیت بخورید، در حالی که [زشتیِ کارتان را] می دانید.'),
(196, 2, 189, 'از تو درباره هلال های ماه می پرسند، بگو: آنها وسیله تعیین اوقات [برای امور دنیایی و نظام زندگی] مردم و [تعیین زمان مراسم] حج است. و نیکی آن نیست که به خانه ها از پشت آنها وارد شوید، [چنان که اعراب جاهلی در حال احرام حج از پشت دیوار خانه خود وارد می شدند نه از در ورودی] بلکه نیکی [روش و منشِ] کسی است که [از هر گناه و معصیتی] می پرهیزد. و به خانه ها از درهای آنها وارد شوید؛ و از خدا پروا کنید تا رستگار شوید.'),
(197, 2, 190, 'و در راه خدا با کسانی که با شما می جنگند بجنگید، و [هنگام جنگ از حدود الهی] تجاوز نکنید، که خدا تجاوزکاران را دوست ندارد.'),
(198, 2, 191, 'و آنان را [که از شرک و کفر و هیچ ستمی بازنمی ایستند] هر کجا یافتید، به قتل برسانید و از جایی که شما را بیرون کردند بیرونشان کنید و فتنه [که شرک، بت پرستی، بیرون کردن مردم از خانه و کاشانه و وطنشان باشد] از قتل وکشتار بدتر است. و کنار مسجدالحرام با آنان نجنگید مگر آنکه در آنجا با شما بجنگند؛ پس اگر با شما جنگیدند، آنان را به قتل برسانید که پاداش وکیفر کافران همین است.'),
(199, 2, 192, 'اگر از فتنه گری وجنگ بازایستند، یقیناً خدا بسیار آمرزنده و مهربان است.'),
(200, 2, 193, 'و با آنان بجنگید تا فتنه ای [چون شرک، بت پرستی و حاکمیّت کفّار] بر جای نماند و دین فقط ویژه خدا باشد. پس اگر بازایستند [به جنگ با آنان پایان دهید و از آن پس] تجاوزی جز بر ضد ستمکاران جایز نیست.'),
(201, 2, 194, 'ماه حرام در برابر ماه حرام است [اگر دشمن حرمت آن را رعایت نکرد و با شما در آن جنگید، شما هم برای حفظ کیان خود در همان ماه با او بجنگید.] و همه حرمت ها دارای قصاص اند. پس هر که بر شما تعدّی کرد، شما هم به مثل آن بر او تعدّی کنید، و از خدا پروا نمایید، و بدانید که خدا با پروا پیشگان است.'),
(202, 2, 195, 'و در راه خدا انفاق کنید و [با ترک این کار پسندیده، یا هزینه کردن مال در راه نامشروع] خود را به هلاکت نیندازید، ونیکی کنید که یقیناً خدا نیکوکاران را دوست دارد.'),
(203, 2, 196, 'حج وعمره را برای خدا به پایان برید، و اگر [به علتی] از انجام آن ممنوع شدید، آنچه را از قربانی برای شما میسر است [قربانی کنید و از احرام در آیید]؛ و سر خود را نتراشید تا قربانی به محلش برسد؛ و از شما اگر کسی بیمار بود یا در سرش ناراحتی و آسیب داشت [و ناچار بود سر بتراشد، جایز است سر بتراشد و] کفّاره ای چون روزه، یا صدقه یا قربانی بر عهده اوست. و هنگامی که [از علل ممنوعیّت] در امان بودید، پس هر که با پایان بردن عمره تمتّع به سوی حج تمتّع رود، آنچه از قربانی میسر است [قربانی کند]. و کسی که قربانی نیابد، سه روزْ روزه، در همان سفر حج و هفت روزْ روزه، هنگامی که بازگشتید بر عهده اوست؛ این ده روز کامل است [و قابل کم و زیاد شدن نیست]. این وظیفه کسی است که [خود و] خانواده اش ساکن و مقیم [منطقه] مسجد الحرام نباشد. و از خدا پروا کنید و بدانید که خدا سخت کیفر است.'),
(204, 2, 197, 'حج در ماه های معین و معلومی است [شوال، ذوالقعده، ذوالحجه] پس کسی که در این ماه ها حج را [با احرام بستن و تلبیه] بر خود واجب کرد، [بداند که] در حج، آمیزش با زنان و گناه و جدال [جایز] نیست. و آنچه از کار خیر انجام دهید خدا می داند. و به نفع خود توشه برگیرید که بهترین توشه، پرهیزکاری است و ای صاحبانِ خرد! از من پروا کنید.'),
(205, 2, 198, 'بر شما گناهی نیست که [در ایام حج] از پروردگارتان [به وسیله تجارت و داد و ستد] فضل و روزی و منافع مادی بطلبید. و هنگامی که از عرفات کوچ کردید، خدا را در مشعر الحرام یاد کنید. [آری] او را یاد کنید همان گونه که شما را هدایت کرد. و همانا شما پیش از این از گمراهان بودید.'),
(206, 2, 199, 'سپس از همان جایی که مردم روانه می شوند [به سوی منی،] روانه شوید، و از خدا آمرزش بخواهید؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(207, 2, 200, 'پس هنگامی که مناسک [حجّتان] را انجام دادید، پس خدا را آن گونه که پدرانتان را یاد می کنید یا بهتر و بیشتر از آن یاد کنید. پس گروهی از مردم [کوتاه فکر] می گویند: پروردگارا! به ما در دنیا [کالای زندگی] عطا کن. و آنان را در آخرت هیچ بهره ای نیست.'),
(208, 2, 201, 'و گروهی از آنان می گویند: پروردگارا! به ما در دنیا نیکی و در آخرت هم نیکی عطا کن، و ما را از عذاب آتش نگاه دار.'),
(209, 2, 202, 'اینانند که از آنچه به دست آورده اند، نصیب و بهره فراوانی دارند، و خدا حسابرسی سریع است.'),
(210, 2, 203, 'و خدا را در روزهای معلوم و معینی [که یازدهم، دوازدهم و سیزدهم ماه ذوالحجه ایام وقوف در منی است] یاد کنید. پس هر که در دو روز [واجبات منی را انجام دهد و به کوچ کردن از آن] شتاب ورزد، گناهی بر او نیست، و هر که [تا روز سوم] تأخیر کند، بر او هم گناهی نیست، [اختیار کردن وقوف در منی چه در دو روز، چه در سه روز] برای کسی است که [از محرمات احرام] پرهیز کرده است. و از خدا پروا کنید و بدانید که یقیناً همه شما به سوی او محشور خواهید شد.'),
(211, 2, 204, 'و از مردم کسی است که گفتارش در زندگی دنیا تو را خوش آید، و [برای اینکه چنین وانمود کند که زبانش با دلش یکی است] خدا را بر آنچه در دل دارد شاهد می گیرد، در حالی که سرسخت ترین دشمنان است.'),
(212, 2, 205, 'و هنگامی که [چنین دشمن سرسختی] قدرت و حکومتی یابد، می کوشد که در زمین فساد و تباهی به بار آورد، و زراعت و نسل را نابود کند؛ و خدا فساد و تباهی را دوست ندارد.'),
(213, 2, 206, 'و چون به او گویند: از خدا پروا کن، غرور و سرسختی و تعصّب و لجاجت، او را به گناه وا می دارد؛ پس دوزخ او را بس است، و یقیناً بد جایگاهی است.'),
(214, 2, 207, 'و از مردم کسی است که جانش را برای خشنودی خدا می فروشد [مانند امیرالمؤمنین (علیه السلام)] و خدا به بندگان مهربان است.'),
(215, 2, 208, 'ای اهل ایمان! همگی در عرصه تسلیم و فرمان بری [از خدا] در آیید، و از گام های شیطان پیروی نکنید، که او نسبت به شما دشمنی آشکار است.'),
(216, 2, 209, 'پس اگر بعد از آنکه دلایل و نشانه های روشن برای شما آمد، لغزیدید [و به شرک و کفر روی کردید] نهایتاً بدانید که خدا [در انتقام از شما] توانای شکست ناپذیر و حکیم است.'),
(217, 2, 210, 'آیا [اهل لغزش و پیروان شیطان] جز این را انتظار دارند که [عذاب] خدا و فرشتگان [مأمور عذاب] در سایبان هایی از ابر به سوی آنان آیند و کار نابودی آنان تمام شود؟ و همه کارها به سوی خدا بازگردانده می شود.'),
(218, 2, 211, 'از بنی اسرائیل بپرس: چه بسیار نشانه های روشن به آنان عطا کردیم، و هرکس نعمتِ [هدایت] خدا را پس از آنکه به او رسید، تبدیل [به کفر] کند، [بداند] که یقیناً خدا سخت کیفر است.'),
(219, 2, 212, 'زندگی [زودگذرِ] دنیا برای کافران آراسته شده، و [به این سبب] مؤمنان را مسخره می کنند، در حالی که پروا پیشگان در روز قیامت [از هر جهت] برتر از آنان هستند، و خدا هر که را بخواهد، بی حساب روزی می دهد.'),
(220, 2, 213, 'مردم [در ابتدای تشکیل اجتماع] گروهی واحد و یک دست بودند [و اختلاف و تضادی در امور زندگی نداشتند]، پس [از پدید آمدن اختلاف و تضاد] خدا پیامبرانی را مژده دهنده و بیم رسان برانگیخت، و با آنان به درستی و راستی کتاب را نازل کرد، تا میان مردم در آنچه با هم اختلاف داشتند، داوری کند. [آن گاه در خود کتاب اختلاف پدید شد] و اختلاف را در آن پدید نیاوردند مگر کسانی که به آنان کتاب داده شد، [این اختلاف] بعد از دلایل و برهان های روشن و آشکاری بود که برای آنان آمد، [و سبب آن] برتری جویی و حسد در میان خودشان بود. پس خدا اهل ایمان را به توفیق خود، به حقّی که در آن اختلاف کردند، راهنمایی کرد. و خدا هر که را بخواهد به راهی راست هدایت می کند.'),
(221, 2, 214, 'آیا پنداشته اید در حالی که هنوز حادثه هایی مانند حوادث گذشتگان شما را نیامده، وارد بهشت می شوید؟! به آنان سختی ها و آسیب هایی رسید وچنان متزلزل و مضطرب شدند تا جایی که پیامبر و کسانی که با او ایمان آورده بودند [در مقام دعا و درخواست یاری] می گفتند: یاری خدا چه زمانی است؟ [به آنان گفتیم:] آگاه باشید! یقیناً یاری خدا نزدیک است.'),
(222, 2, 215, 'از تو می پرسند: چه چیز انفاق کنند؟ بگو: هر مال و مایه سودمندی که انفاق می کنید باید برای پدر و مادر و نزدیکان و یتیمان و نیازمندان و در راه مانده گان باشد، و هر کار نیکی انجام دهید، خدا به آن داناست.'),
(223, 2, 216, 'جنگ [با دشمن] بر شما مقرّر و لازم شده، و حال آنکه برایتان ناخوشایند است. و بسا چیزی را خوش ندارید و آن برای شما خیر است، وبسا چیزی را دوست دارید و آن برای شما بد است؛ وخدا [مصلحت شما را در همه امور] می داند و شما نمی دانید.'),
(224, 2, 217, 'از تو درباره جنگ در ماه حرام می پرسند. بگو: هر جنگی در آن [گناهی] بزرگ است، ولی هر نوع بازداشتن [مردم] از راه خدا و کفرورزی به او و [بازداشتن مردم از] مسجد الحرام و بیرون راندن اهلش از آن، نزد خدا بزرگ تر [از جنگ در ماه حرام] است؛ و فتنه [شرک و بت پرستی] از کشتار بزرگ تر است. و مشرکان همواره با شما می جنگند تا شما را اگر بتوانند از دینتان برگردانند. و از شما کسانی که از دینشان برگردند و در حال کفر بمیرند، همه اعمال خوبشان در دنیا و آخرت تباه و بی اثر می شود، و آنان اهل آتش اند و در آن جاودانه اند.'),
(225, 2, 218, 'یقیناً کسانی که ایمان آورده، و آنان که هجرت کرده و در راه خدا به جهاد برخاستند، به رحمت خدا امید دارند؛ و خدا بسیار آمرزنده و مهربان است.'),
(226, 2, 219, 'درباره شراب و قمار از تو می پرسند، بگو: در آن دو، گناهی بزرگ و سودهایی برای مردم است، و گناه هر دو از سودشان بیشتر است. و از تو می پرسند: چه چیز انفاق کنند؟ بگو: از آنچه افزون بر نیاز است. خدا این گونه آیاتش را برای شما بیان می کند تا بیندیشید.'),
(227, 2, 220, '[آری، تا] درباره [آنچه به صلاح] دنیا و آخرت [است] بیندیشید. و از تو درباره یتیمان می پرسند، بگو: اصلاح امور آنان بهتر است، و اگر با آنان معاشرت کنید و امورتان را با امورشان بیامیزید [کاری خداپسندانه است] آنان برادر دینی شما هستند، و خدا افسادگر [در امور یتیم] را از اصلاح گر بازمی شناسد. و اگر خدا می خواست شما را [در تکلیف و مسؤولیت نسبت به مال و جان یتیم] به زحمت می انداخت؛ مسلماً خدا توانای شکست ناپذیر و حکیم است.'),
(228, 2, 221, 'و با زنان مشرک ازدواج نکنید تا زمانی که ایمان بیاورند. قطعاً کنیز با ایمان از زن آزاد مشرک بهتر است، هر چند [زیبایی، مال و موقعیت او] شما را خوش آید. و زنان با ایمان را به ازدواج مردان مشرک در نیاورید تا زمانی که ایمان بیاورند. مسلماً برده با ایمان از مرد آزاد مشرک بهتر است، هر چند [جمال، مال و منال او] شما را خوش آید. اینان [که مشرکند، نه تنها مردم، بلکه بی رحمانه زن و فرزند خود را] به سوی آتش می خوانند، و خدا به توفیق خود به سوی بهشت و آمرزش دعوت می کند، و آیاتش را برای مردم بیان می فرماید تا متذکّر شوند.'),
(229, 2, 222, 'از تو درباره حیض می پرسند، بگو: حیض، حالت ناملایم و زیان باری است؛ پس در زمان حیض از [آمیزش با] زنان کناره گیری کنید، و با آنان نزدیکی ننمایید تا پاک شوند؛ و هنگامی که پاک شدند از جایی که خدا به شما فرمان داده با آنان آمیزش کنید. یقیناً خدا کسانی را که بسیار توبه می کنند، و کسانی را که خود را [با پذیرش انواع پاکی ها از همه آلودگی ها] پاکیزه می کنند. دوست دارد.'),
(230, 2, 223, 'زنان شما کشتزار شمایند، هر زمان و هر کجا که خواستید به کشتزار خود در آیید. و [با رعایت حقوق یکدیگر، و حفظ عفت و پاکی، در سایه زناشویی و تولید نسلی پاک و شایسته و صالح، خیر و ثوابی] برای خودتان پیش فرستید، و از خدا پروا کنید، و بدانید که او را ملاقات خواهید کرد، و مؤمنان را بشارت ده.'),
(231, 2, 224, 'و خدا را در معرض سوگندهایی که می خورید قرار ندهید، [به اینکه سوگند بخورید] که نیکی نکنید، و تقوا پیشه نسازید، و میان مردم آشتی ندهید [خدا رضایت ندارد که نامش وسیله خودداری از کارهایی شود که خودش به انجام آنها فرمان داده است] و خدا [نسبت به همه گفته های شما] شنوا، [و نسبت به تمام کارهای شما] داناست.'),
(232, 2, 225, 'خدا شما را به خاطر سوگندهای لغوتان [که جدی و حقیقی نیست و عادتاً بدون قصد قلبی بر زبان جاری می شود] مؤاخذه نمی کند، ولی شما را به خاطر آنچه دل هایتان [از سوگند جدی و حقیقی] مرتکب شده مؤاخذه می کند؛ و خدا بسیار آمرزنده و بردبار است.'),
(233, 2, 226, 'برای کسانی که سوگند یاد می کنند [که از آمیزش] با زنان خود دوری گزینند، [شرعاً] چهار ماه مهلت است، چنانچه [در این مدت به آنچه مقتضای زناشویی است] بازگشتند [گناهی بر آنان نیست]؛ زیرا خدا بسیار آمرزنده ومهربان است.'),
(234, 2, 227, 'و اگر [برای پایان یافتن مشکلات] تصمیم به طلاق گرفتند، [در صورت رعایت شرایط مانعی ندارد] مسلماً خدا [به گفتار و کردارشان] شنوا و داناست.'),
(235, 2, 228, 'زنان طلاق داده شده باید به سر آمدن سه پاکی [از حیض را] انتظار برند [و پیش از به سر رسیدن آن، از شوهر کردن خودداری ورزند]. و اگر به خدا و روز قیامت ایمان دارند [به خاطر رعایت شرایط ایام عدّه] نباید آنچه را خدا در رحم هایشان [از حیض یا جنین] آفریده، پنهان دارند. و شوهرانشان در این مدت چنانچه خواهان صلح و سازش اند، به بازگرداندن آنان [به زندگی دوباره] سزاوارترند. و برای زنان، حقوق شایسته ای بر عهده مردان است، مانند حقوقی که برای مردان بر عهده آنان است، و مردان را بر آنان [به خاطر کارگزاری، و تدبیر امور زندگی در امر میراث و دیه، گواهی در دادگاه و غیر این امور حقوقی] افزون تر است؛ و خدا توانای شکست ناپذیر و حکیم است.'),
(236, 2, 229, 'طلاقِ [رجعی که برای شوهر در آن حقّ رجوع وبازگشت به همسر است] دوبار است. [شوهر در هر مرتبه از رجوع] باید [همسرش را] به طور شایسته ومتعارف نگه دارد، یا [با ترک رجوع، او را] به نیکی و خوشی رها کند. و برای شما حلال نیست از آنچه [به عنوان مهریه] به آنان پرداخته اید چیزی را بازستانید، مگر آنکه هر دو بترسند که حدود و مقرّرات خدا را [در روش همسرداری] برپا ندارند. پس شما [حاکمان شرع] اگر بترسید که آن دو نفر حدود خدا را برپا ندارند در آنچه زن برای رهایی خود [عوض طلاق] فدیه بپردازد، گناهی بر آنان نیست. اینها حدود خداست؛ پس از آنها تجاوز نکنید و کسانی که از حدود خدا تجاوز کنند، آنان بی تردید ستمکارند.'),
(237, 2, 230, 'پس اگر [بعد از دو طلاق و رجوع، بار سوم] او را طلاق داد، آن زن بر وی حلال نخواهد بود، مگر آنکه با همسری غیر از او ازدواج کند. و اگر [همسر دوم] او را طلاق داد بر وی و شوهر اول گناهی نیست که به یکدیگرباز گردند، در صورتی که امیدوار باشند حدود الهی را [در روش همسرداری] رعایت می کنند. و اینها حدود خداست که آن را برای گروهی که دانایند بیان می کند.'),
(238, 2, 231, 'و هنگامی که زنان را طلاق دادید و به پایان عدّه خود نزدیک شدند، آنان را [با رجوع کردن] به طور شایسته و متعارف، نگه دارید یا [با ترک رجوع] به نیکی و خوشی رها کنید. و آنان را برای آزار رساندن و زیان زدن نگه ندارید تا بر آنان تعدّی و ستم کنید. و هر که چنین کند قطعاً به خود ستم کرده است. و آیات خدا را به مسخره نگیرید و نعمت خدا را بر خود و آنچه از کتاب و حکمت بر شما نازل کرده که شما را به آن پند می دهد، به یاد آرید. و از خدا پروا کنید و بدانید که خدا به همه چیز داناست.'),
(239, 2, 232, 'و زمانی که زنان را طلاق دادید، و به پایان عدّه خود رسیدند، آنان را از ازدواج با شوهران سابق خود در صورتی که میان خودشان به روشی شایسته و متعارف توافق کنند باز مدارید. با این [حکم،] کسانی از شما که به خدا و روز قیامت ایمان دارند، پند داده می شوند. این برای شما سودمندتر وپاکیزه تر است؛ وخدا [مصالح شما را در همه امور] می داند وشما نمی دانید.'),
(240, 2, 233, 'و مادران باید فرزندانشان را دو سال کامل شیر دهند. [این حکم] برای کسی است که می خواهد دوران شیرخوارگی [کودک] را تکمیل کند. تأمین خوراک و پوشاک مادران شیردهنده به طور شایسته و متعارف بر عهده پدر فرزند است. هیچ کس جز به اندازه توانش تکلیف نمی شود. نباید مادری به خاطر فرزندش زیان بیند، و نه پدری برای فرزندش دچار ضرر شود [بر پدر است که مخارج مادر و کودک را در حدّ متعارف بپردازد، و بر مادر است که بیش از طاقت مالی شوهر از شوهر طلب خوراک و پوشاک نکند]. و [چنانچه پدر کودک از دنیا برود] هزینه مادر و کودک به اندازه متعارف [از میراث به جا مانده] بر عهده وارث است. و اگر پدر و مادر بر اساس توافق و مشورتشان بخواهند کودک را [قبل از دو سال] از شیر بگیرند، گناهی بر آنان نیست. و اگر بخواهید برای فرزندانتان دایه بگیرید، گناهی بر شما نیست، در صورتی که مزدی را که باید بدهید، به طور شایسته و متعارف بپردازید. و از خدا پروا کنید و بدانید یقیناً خدا به آنچه انجام می دهید، بیناست.'),
(241, 2, 234, 'و کسانی از شما که می میرند و همسرانی باقی می گذارند، باید همسرانْ چهار ماه و ده روز انتظار برند [و از شوهر کردن خودداری ورزند]؛ پس چون به پایان مدتشان رسیدند، در آنچه درباره خودشان به طور شایسته و متعارف [نسبت به ازدواج یا ترک آن] انجام دهند بر شما [حاکمان، وارثان متوفّی و اقوام زنان] گناهی نیست؛ و خدا از آنچه انجام می دهید، آگاه است.'),
(242, 2, 235, 'در سخنانی که هنگام خواستگاری زن ها [یی که در عدّه وفات اند] بدون صراحت و وضوح گویید [مانند اینکه من «خوش معاشرتم» و «زن دوستم» و به زنی که دارای چنین صفاتی باشد علاقه مندم] یا قصد ازدواج با آنان را در دل پنهان دارید، گناهی بر شما نیست. خدا می داند که شما به زودی آنان را [بر اساس میل فطری و خواسته طبیعی] یاد خواهید کرد؛ ولی با آنان در پنهانی و خلوت قرار ازدواج نگذارید، جز آنکه گفتاری پسندیده [و بدون صراحت و وضوح] بگویید. و هرگز تصمیم بستن عقد ازدواج نگیرید تا عدّه وفات به پایان رسد، و بدانید که خدا آنچه در دل دارید می داند؛ بنابراین از [مخالفت با] او بپرهیزید، و بدانید که خدا بسیار آمرزنده و بردبار است.'),
(243, 2, 236, 'اگر زنان را پیش از آمیزش جنسی یا تعیین مهریه طلاق دادید [در این طلاق] بر شما گناهی نیست؛ و [در چنین موقعیتی بر شما واجب است] آنان را [از مال خود] بهره ای شایسته و متعارف دهید؛ توانگر به اندازه خویش، و تنگدست به اندازه خویش که این حقّی لازم بر عهده نیکوکاران است.'),
(244, 2, 237, 'و اگر آنان را پیش از آمیزش جنسی طلاق دهید، در حالی که برای آنان مهری تعیین کرده اید، پس [بر شما واجب است] نصف آنچه تعیین کرده اید [به آنان بپردازید]، مگر آنکه خود آنان یا کسی که پیوند ازدواج به دست اوست [مانند ولی یا وکیل] آن را ببخشند. و گذشت و بخشش شما [که تمام مهریه را به زن بپردازید،] به پرهیزکاری نزدیک تر است. و فزون بخشی و نیکوکاری را در میان خودتان فراموش نکنید که خدا به آنچه انجام می دهید، بیناست.'),
(245, 2, 238, 'بر همه نمازها و به ویژه نماز میانه [که بر اساس پاره ای از روایات، نماز ظهر است] محافظت کنید، و [هنگام عبادت] فروتنانه برای خدا قیام کنید.'),
(246, 2, 239, 'پس اگر [از دشمن یا حیوانات درنده یا خطر دیگر] ترس داشتید، [نماز را] پیاده یا سواره بخوانید و هنگامی که امنیت یافتید، خدا را [با خواندن نماز] یاد کنید، نمازی که آن را در ضمن سایر برنامه های دینی که دانای به آنها نبودید، به شما آموخت.'),
(247, 2, 240, 'و کسانی از شما که مرگشان نزدیک می شود، و همسرانی به جا می گذارند، باید برای همسرانشان وصیت کنند که آنان را تا یک سال بدون بیرون کردن از خانه از هزینه زندگی بهره مند سازند. پس اگر [به اختیار خود] بیرون رفتند، در آنچه نسبت به خود [از ازدواج یا انتخاب شغل] به طور شایسته و متعارف انجام دهند، بر شما [وارثان میّت به سبب نپرداختن هزینه زندگی آنان] گناهی نیست؛ و خدا توانای شکست ناپذیر و حکیم است.'),
(248, 2, 241, 'و [سزاوار است از سوی شوهران] به طور شایسته و متعارف، کالا و وسایل زندگی به زنان طلاق داده شده پرداخت شود که این حقّی لازم بر عهده پرهیزکاران است.'),
(249, 2, 242, 'این گونه خدا آیاتش را برای شما بیان می کند، باشد که بیندیشید.'),
(250, 2, 243, 'آیا به کسانی که از ترس مرگ از خانه های خود در حالی که هزاران نفر بودند، بیرون آمدند [با دیده عبرت] ننگریستی؟ پس خدا به آنان فرمود: بمیرید [و آنان بدون فاصله مردند،] سپس آنان را زنده کرد [تا بدانند رهایی از چنگ مرگ ممکن نیست]. خدا نسبت به مردم دارای فضل و احسان است، ولی بیشتر مردم سپاس نمی گزارند.'),
(251, 2, 244, 'و در راه خدا پیکار کنید و بدانید که یقیناً خدا شنوا و داناست.'),
(252, 2, 245, 'کیست آنکه به خدا وام نیکو دهد، تا آن را برایش چندین برابر بیفزاید؟ و خداست که [روزی را] تنگ می گیرد و وسعت می دهد؛ و [همه شما برای دریافت پاداش] به سوی او بازگردانده می شوید.'),
(253, 2, 246, 'آیا [با دیده عبرت] آن گروه از سران و اشراف بنی اسرائیل را پس از موسی ندیدی که به پیامبرشان گفتند: برای ما زمامدار و فرمانروایی برانگیز تا در راه خدا جنگ کنیم؟ گفت: آیا احتمال نمی دهید، اگر جنگ بر شما مقرّر و لازم شود، جنگ نکنید [و سر به نافرمانی بزنید؟] گفتند: ما را چه هدف و مرادی است که در راه خدا جنگ نکنیم، در حالی که از میان خانه ها و فرزندانمان بیرون رانده شده ایم؟! پس چون جنگ بر آنان مقرّر و لازم شد، همه جز اندکی از آنان روی گرداندند؛ و خدا به ستمکاران داناست.'),
(254, 2, 247, 'و پیامبرشان به آنان گفت: بی تردید خدا طالوت را برای شما به زمامداری برانگیخت. گفتند: [شگفتا!] چگونه او را بر ما حکومت باشد و حال آنکه ما به فرمانروایی از او سزاوارتریم، و به او وسعت و فراخی مالی داده نشده [پس ما کجا و زمامداری انسانی تهیدست کجا!؟] [پیامبرشان] گفت: خدا او را بر شما برگزیده و وی را در دانش و نیروی جسمی فزونی داده؛ و خدا زمامداریش را به هر کس که بخواهد عطا می کند؛ و خدا بسیار عطا کننده و داناست.'),
(255, 2, 248, 'و پیامبرشان به آنان گفت: یقیناً نشانه فرمانروایی او این است که آن صندوق [که موسی را در آن گذاشتند و به دریا انداختند] نزد شما خواهد آمد، در آن آرامشی از سوی پروردگارتان است، و باقی مانده ای از آنچه خاندان موسی و هارون به جا گذاشته اند [چون الواح تورات، عصای موسی و عمامه هارون] و فرشتگان آن را حمل می کنند، البته در آن نشانه ای برای شماست اگر مؤمن باشید.'),
(256, 2, 249, 'پس زمانی که طالوت با سپاهیان [برای جنگ با دشمن از شهر] بیرون رفت، گفت: بی تردید خدا شما را به وسیله نهر آبی آزمایش می کند؛ پس هر که [به هنگام تشنگی] از آن [سیر] بنوشد، از من نیست و هر که از آن نخورد، از من است، مگر کسی که با دستش کفی آب برگیرد [که او نه از من است و نه مردود از سپاه]. پس جز اندکی از آنان همگی از آن نوشیدند. و زمانی که او و کسانی که با او ایمان آورده بودند از نهر گذشتند، [گروهی از آنان] گفتند: ما را امروز قدرت مقابله با جالوت و سپاهیانش نیست. ولی کسانی که یقین داشتند که دیدارکننده خدایند، گفتند: چه بسا گروه اندکی که به توفیق خدا بر گروه بسیاری پیروز شدند، و خدا باشکیبایان است.'),
(257, 2, 250, 'و چون [طالوت و اهل ایمان] برای جنگ با جالوت و سپاهیانش ظاهر شدند، گفتند: پروردگارا! بر ما صبر و شکیبایی فرو ریز، و گام هایمان را استوار ساز، و ما را بر گروه کافران پیروز گردان.'),
(258, 2, 251, 'پس آنان را به توفیق خدا شکست دادند. و داود [جوان مؤمن نیرومندی که در سپاه طالوت بود] جالوت را کشت، و خدا او را فرمانروایی و حکمت داد، و از آنچه می خواست به او آموخت. و اگر خدا [تجاوز و ستم] برخی از مردم را به وسیله برخی دیگر دفع نمی کرد، قطعاً زمین را فساد فرا می گرفت؛ ولی خدا نسبت به جهانیان دارای فضل و احسان است.'),
(259, 2, 252, 'این [داستان ها و حادثه های واقعی] نشانه های [توحید، ربوبیّت و قدرت] خداست که به حقّ و راستی بر تو می خوانیم و یقیناً تو از فرستادگانی.'),
(260, 2, 253, 'از آن فرستادگان برخی را بر برخی برتری بخشیدیم. از آنان کسی است که خدا با او سخن گفت، و برخی از آنان را درجات و مراتبی بالا برد. و عیسی بن مریم را دلایل و نشانه های روشن دادیم، و او را به وسیله روح القدس توانایی بخشیدیم. و اگر خدا می خواست کسانی که بعد از آنان [در طول قرون و اعصار] آمدند، پس از آنکه دلایل و براهین روشن به آنان رسید، با هم نمی جنگیدند، ولی [درباره ایمان و کفر با هم] اختلاف کردند، پس برخی از آنان ایمان آوردند و برخی کفر ورزیدند، قطعاً خدا اگر می خواست نمی جنگیدند، ولی خدا آنچه را می خواهد [از روی حکمت و مصلحت] انجام می دهد.'),
(261, 2, 254, 'ای اهل ایمان! از آنچه به شما روزی کرده ایم انفاق کنید، پیش از آنکه روزی بیاید که در آن نه داد و ستدی است و نه دوستی و نه شفاعتی؛ و تنها کافران ستمکارند.'),
(262, 2, 255, 'خدای یکتا که جز او هیچ معبودی نیست، زنده و قائم به ذات [و مدّبر و برپا دارنده و نگه دارنده همه مخلوقات] است، هیچ گاه خواب سبک و سنگین او را فرا نمی گیرد، آنچه در آسمان ها و آنچه در زمین است در سیطره مالکیّت و فرمانروایی اوست. کیست آنکه جز به اذن او در پیشگاهش شفاعت کند؟ آنچه را پیش روی مردم است [که نزد ایشان حاضر و مشهود است] و آنچه را پشت سر آنان است [که نسبت به آنان دور و پنهان است] می داند. و آنان به چیزی از دانش او احاطه ندارند مگر به آنچه او بخواهد. تخت [حکومت، قدرت و سلطنت] ش آسمان ها و زمین را فرا گرفته و نگهداری آنان بر او گران و مشقت آور نیست؛ و او بلند مرتبه و بزرگ است.'),
(263, 2, 256, 'در دین، هیچ اکراه و اجباری نیست [کسی حق ندارد کسی را از روی اجبار وادار به پذیرفتن دین کند، بلکه هر کسی باید آزادانه با به کارگیری عقل و با تکیه بر مطالعه و تحقیق، دین را بپذیرد]. مسلماً راه هدایت از گمراهی [به وسیله قرآن، پیامبر و امامان معصوم] روشن و آشکار شده است. پس هر که به طاغوت [که شیطان، بت و هر طغیان گری است] کفر ورزد و به خدا ایمان بیاورد، بی تردید به محکم ترین دستگیره که آن را گسستن نیست، چنگ زده است؛ و خدا شنوا و داناست.'),
(264, 2, 257, 'خدا سرپرست و یار کسانی است که ایمان آورده اند؛ آنان را از تاریکی ها [ی جهل، شرک، فسق وفجور] به سوی نورِ [ایمان، اخلاق حسنه و تقوا] بیرون می برد. و کسانی که کافر شدند، سرپرستان آنان طغیان گرانند که آنان را از نور به سوی تاریکی ها بیرون می برند؛ آنان اهل آتش اند و قطعاً در آنجا جاودانه اند.'),
(265, 2, 258, 'آیا [با دیده عبرت] ننگریستی به کسی که چون خدا او را پادشاهی داده بود [از روی کبر و غرور] با ابراهیم درباره پروردگارش به مجادله و ستیز و گفتگوی بی منطق پرداخت؟! هنگامی که ابراهیم گفت: پروردگارم کسی است که زنده می کند و می میراند [او] گفت: من هم زنده می کنم و می میرانم. [و برای مشتبه کردن کار بر مردم، دستور داد دو زندانی محکوم را حاضر کردند، یکی را آزاد و دیگری را کشت.] ابراهیم [برای بستن راه مغالطه و تزویر به روی دشمن] گفت: مسلماً خدا خورشید را از مشرق بیرون می آورد، تو آن را از مغرب برآور! پس آنکه کافر شده بود، متحیر و مبهوت شد. و خدا گروه ستم گر را [به خاطر تجاوز، ستم، پافشاری بر عناد و لجاجشان] هدایت نمی کند.'),
(266, 2, 259, 'یا چون آن کسی که به دهکده ای گذر کرد، در حالی که دیوارهای آن بر روی سقف هایش فرو ریخته بود [و اجساد ساکنانش پوسیده و متلاشی به نظر می آمد] گفت: خدا چگونه اینان را پس از مرگشان زنده می کند؟ پس خدا او را صد سال میراند، سپس وی را برانگیخت، به او فرمود: چه مقدار [در این منطقه] درنگ کرده ای؟ گفت: یک روز یا بخشی از یک روز درنگ کرده ام. [خدا] فرمود: بلکه صد سال درنگ کرده ای! به خوراکی و نوشیدنی خود بنگر که [پس از گذشت صد سال و رفت و آمد فصول چهارگانه] تغییری نکرده، و به دراز گوش خود نظر کن [که جسمش متلاشی شده، ما تو را زنده کردیم تا به پاسخ پرسشت برسی و به واقع شدن این حقیقت مطمئن شوی]، و تا تو را نشانه ای [از قدرت و ربوبیّت خود] برای مردم [در مورد زنده شدن مردگان] قرار دهیم، اکنون به استخوان ها [یِ دراز گوشت] بنگر که چگونه آنها را برمی داریم و به هم پیوند می دهیم، سپس بر آنها گوشت می پوشانیم. چون [کیفیتِ زنده شدنِ مردگان] بر او روشن شد، گفت: اکنون می دانم که یقیناً خدا بر هر کاری تواناست.'),
(267, 2, 260, 'و [یاد کنید] هنگامی که ابراهیم گفت: پروردگارا! به من نشان ده که مردگان را چگونه زنده می کنی؟ [خدا] فرمود: آیا [به قدرتم نسبت به زنده کردن مردگان] ایمان نیاورده ای؟! گفت: چرا، ولی [مشاهده این حقیقت را خواستم] تا قلبم آرامش یابد. [خدا] فرمود: پس چهار پرنده بگیر و آنها را [برای دقت در آفرینش هر یک] به خود نزدیک کن، و [بعد از کشتن، ریز ریز کردن و مخلوط کردنشان به هم] بر هر کوهی [در این منطقه] بخشی از آنها را قرار ده، سپس آنها را بخوان که شتابان به سویت می آیند؛ و بدان که یقیناً خدا توانای شکست ناپذیر و حکیم است.'),
(268, 2, 261, 'مَثَل آنان که اموالشان را در راه خدا انفاق می کنند، مانند دانه ای است که هفت خوشه برویاند، در هر خوشه صد دانه باشد؛ و خدا برای هر که بخواهد چند برابر می کند و خدا بسیار عطا کننده و داناست.'),
(269, 2, 262, 'کسانی که اموالشان را در راه خدا انفاق می کنند، سپس منت و آزاری به دنبال انفاقشان نمی آورند، برای آنان نزد پروردگارشان پاداشی شایسته و مناسب است، و نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(270, 2, 263, 'گفتاری پسندیده [در برابر تهیدستان] و عفو [ی کریمانه نسبت به خشم و بد زبانی مستمندان] بهتر از بخششی است که دنبالش آزاری باشد، و خدا بی نیاز و بردبار است.'),
(271, 2, 264, 'ای اهل ایمان! صدقه هایتان را با منت و آزار باطل نکنید، مانند کسی که مالش را به ریا به مردم انفاق می کند و به خدا و روز قیامت ایمان ندارد، که وصفش مانند سنگ سخت و خارایی است که بر آن [پوششی نازک از] خاک قرار دارد و رگباری تند و درشت به آن برسد و آن سنگ را صاف [و بدون خاک] واگذارد [صدقه ریایی مانند آن خاک است و این ریاکاران] به چیزی از آنچه کسب کرده اند، دست نمی یابند و خدا مردم کافر را هدایت نمی کند.'),
(272, 2, 265, 'ومثل کسانی که اموالشان را برای طلب خشنودی خدا و استوار کردن نفوسشان [بر حقایق ایمانی و فضایل اخلاقی] انفاق می کنند، مانند بوستانی است در جایی بلند که بارانی تند به آن برسد، در نتیجه میوه اش را دو چندان بدهد، و اگر باران تندی به آن نرسد باران ملایمی می رسد [و آن برای شادابی و محصول دادنش کافی است] و خدا به آنچه انجام می دهید، بیناست.'),
(273, 2, 266, 'آیا یکی از شما دوست دارد که او را بوستانی از درختان خرما و انگور باشد که از زیرِ [درختانِ] آن نهرها جاری است و برای وی در آن بوستان از هر گونه میوه و محصولی باشد، در حالی که پیری به او رسیده و دارای فرزندان ناتوان [و خردسال] است، پس گردبادی که در آن آتش سوزانی است به آن بوستان برسد و یک پارچه بسوزد؟ [ریا و منت و آزار به همین صورت انفاق را نابود می کند] این گونه خدا آیاتش را برای شما توضیح می دهد تا بیندیشید.'),
(274, 2, 267, 'ای اهل ایمان! از پاکیزه های آنچه [از راه داد و ستد] به دست آورده اید، و آنچه [از گیاهان و معادن] برای شما از زمین بیرون آورده ایم، انفاق کنید و برای انفاق کردن دنبال مال ناپاک و بی ارزش و معیوب نروید، در حالی که اگر آن را به عنوان حقّ شما به خود شما می دادند، جز [با] چشم پوشی [و دلسردی] نمی گرفتید و بدانید که خدا، بی نیاز و ستوده است.'),
(275, 2, 268, 'شیطان، شما را [به هنگام انفاق مال با ارزش] از تهیدستی و فقر می ترساند، و شما را به کار زشت [چون بخل وخودداری از زکات و صدقات] امر می کند، و خدا شما را از سوی خود وعده آمرزش و فزونی رزق می دهد؛ و خدا بسیار عطا کننده و داناست.'),
(276, 2, 269, 'حکمت را به هر کس بخواهد می دهد، و آنکه به او حکمت داده شود، بی تردید او را خیر فراوانی داده اند، و جز صاحبان خرد، کسی متذکّر نمی شود.'),
(277, 2, 270, 'و هر نفقه ای که انفاق کردید و هر نذری را که برعهده گرفتید، یقیناً خدا آن را می داند، [در نتیجه فرمانبر را پاداش می دهد و ستمکار به آیات خدا و حقوق مردم را عذاب می کند] و برای ستمکاران در قیامت یاوری نیست.'),
(278, 2, 271, 'اگر صدقه ها را آشکار کنید، کاری نیکوست، و اگر آنها را پنهان دارید و به تهیدستان دهید برای شما بهتر است؛ و خدا [به این سبب] بخشی از گناهانتان را محو می کند؛ و خدا به آنچه انجام می دهید، آگاه است.'),
(279, 2, 272, '[ای پیامبر!] هدایت آنان [به سوی اخلاص در انفاق و ترک منّت و آزار] بر عهده تو نیست، [وظیفه تو ابلاغ پیام و اتمام حجّت است] بلکه خداست که هر کس را بخواهد هدایت می کند. [ای اهل ایمان!] و هر مالی را که انفاق کنید به سود خود شماست، و این در صورتی است که انفاق نکنید جز برای طلب خشنودی خدا. و آنچه از مال [باارزش و بی عیب] انفاق کنید، پاداشش به طور کامل به شما داده می شود، و مورد ستم قرار نخواهید گرفت.'),
(280, 2, 273, '[صدقاتْ] حقِّ نیازمندانی است که در راه خدا [به سبب جنگ یا طلب دانش یا بیماری یا عوامل دیگر] در مضیقه و تنگنا افتاده اند [و برای فراهم کردن هزینه زندگی] نمی توانند در زمین سفر کنند؛ فرد ناآگاه آنان را از شدت پارسایی وعفّتی که دارند توانگر و بی نیاز می پندارد. [تو ای رسول من!] آنان را از سیمایشان می شناسی. [آنان] از مردم به اصرار چیزی نمی خواهند. و شما ای اهل ایمان! آنچه از مال [با ارزش و بی عیب] انفاقکنید، یقیناً خدا به آن داناست.'),
(281, 2, 274, 'کسانی که [چون علی بن أبی طالب (علیه السلام)] اموالشان را در شب و روز و پنهان و آشکار انفاق می کنند، برای آنان نزد پروردگارشان پاداشی شایسته و مناسب است؛ و نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(282, 2, 275, 'کسانی که ربا می خورند [در میان مردم و برای امر معیشت و زندگی] به پای نمی خیزند، مگر مانند به پای خاستن کسی که شیطان او را با تماس خود آشفته حال کرده [و تعادل روانی و عقلی اش را مختل ساخته] است، این بدان سبب است که آنان گفتند: خرید و فروش هم مانند رباست. در حالی که خدا خرید و فروش را حلال، و ربا را حرام کرده است. پس هر که از سوی پروردگارش پندی به او رسد و [از کار زشت خود] بازایستد، سودهایی که [پیش از تحریم آن] به دست آورده، مال خود اوست، و کارش [از جهت آثار گناه و کیفر آخرتی] با خداست. و کسانی که [به عمل زشت خود] بازگردند [و نهی خدا را احترام نکنند] پس آنان اهل آتش اند، و در آن جاودانه اند.'),
(283, 2, 276, 'خدا ربا را نابود می کند، و صدقات را فزونی می دهد؛ و خدا هیچ ناسپاس بزه کاری را دوست ندارد.'),
(284, 2, 277, 'مسلماً کسانی که ایمان آوردند و کارهای شایسته انجام دادند، و نماز را به پا داشتند، و زکات پرداختند، برای آنان نزد پروردگارشان پاداشی [شایسته و مناسب] است، و نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(285, 2, 278, 'ای اهل ایمان! از خدا پروا کنید، و اگر مؤمن [واقعی] هستید آنچه را از ربا [بر عهده مردم] باقی مانده رها کنید.'),
(286, 2, 279, 'واگر چنین نکردید [و به رباخواری اصرار ورزیدید] به جنگی بزرگ از سوی خدا و رسولش [بر ضد خود] یقین کنید؛ و اگر توبه کردید، اصل سرمایه های شما برای خود شماست [و سودهای گرفته شده را به مردم بازگردانید] که در این صورت نه ستم می کنید و نه مورد ستم قرار می گیرید.'),
(287, 2, 280, 'و اگر [بدهکار] تنگدست بود [بر شماست که] او را تا هنگام توانایی مهلت دهید؛ و بخشیدن همه وام [و چشم پوشی و گذشت از آن در صورتی که توانایی پرداختش را ندارد] اگر [فضیلت وثوابش را] بدانید برای شما بهتر است.'),
(288, 2, 281, 'و پروا کنید از روزی که در آن به سوی خدا بازگردانده می شوید، سپس به هر کس آنچه انجام داده، به طور کامل داده می شود؛ و آنان مورد ستم قرار نمی گیرند [زیرا هر چه را دریافت می کنند، تجسّمِ عینی اعمال خودشان است].'),
(289, 2, 282, 'ای اهل ایمان! چنانچه وامی به یکدیگر تا سر آمد معینی، دادید، لازم است آن را بنویسید. و باید نویسنده ای [سَنَدش را] در میان خودتان به عدالت بنویسد. و نباید هیچ نویسنده ای از نوشتن سند همان گونه که خدا [بر اساس قوانین شرعی] به او آموخته است، دریغ ورزد. او باید بنویسد، و کسی که حقْ به عهده اوست باید [کلامش را جهت تنظیم سند برای نویسنده] املا کند، و از خدا که پروردگار اوست پروا نماید و از حق چیزی را نکاهد. و اگر کسی که حق به عهده اوست، سفیه یا ناتوان باشد، یا [به علتی] نتواند املا کند، ولیّ و سرپرست او به عدالت املا کند. و دو شاهد از مردانتان را [بر این حق] شاهد بگیرید، و اگر دو مرد نبود، یک مرد و دو زن را از میان شاهدانی که می پسندید، شاهد بگیرید تا اگر یکی از آن دو زن [واقعیت را] فراموش کرد، آن دیگری او را یادآوری کند. و شاهدان هنگامی که [برای ادای شهادت] دعوت شوند [از پاسخ دعوت] امتناع نورزند، و از نوشتن [سندِ وام] تا سرآمد معیّنش، کوچک باشد یا بزرگ، ملول نشوید، این [کار] نزد خدا عادلانه تر و برای اقامه شهادت پابرجاتر، و به اینکه [در جنس وام، اندازه آن و زمان پرداختش] شک نکنید [و ستیز و نزاعی پیش نیاید،] نزدیک تر است. مگر آنکه داد و ستدی نقدی باشد که آن را میان خود دست به دست می کنید، در این صورت بر شما گناهی نیست که آن را ننویسید. و هرگاه داد و ستد کنید، شاهد بگیرید. و نباید به نویسنده و شاهد زیان برسد و اگر زیان برسانید، خروج از اطاعت خداست که گریبانگیر شما شده است و از خدا پروا کنید و خدا [احکامش را] به شما می آموزد و خدا به همه چیز داناست.'),
(290, 2, 283, 'و اگر در سفر بودید و نویسنده ای [برای ثبت سند] نیافتید، وثیقه های دریافت شده [جایگزین سند و شاهد] است. و اگر یکدیگر را امین دانستید [وثیقه لازم نیست] پس باید کسی که امینش دانسته اند، امانتش را ادا کند. و از خدا پروردگار خود، پروا نماید و [شما ای شاهدان!] شهادت را پنهان نکنید و هر که آن را پنهان کند، یقیناً دلش گناهکار است؛ و خدا به آنچه انجام می دهید داناست.'),
(291, 2, 284, 'آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست، و اگر آنچه [از نیّت های فاسد و افکار باطل] در دل دارید آشکار کنید یا پنهان سازید، خدا شما را به آن محاسبه می کند؛ پس هر که را بخواهد می آمرزد، و هر که را بخواهد عذاب می کند؛ و خدا بر هر کاری تواناست.'),
(292, 2, 285, 'پیامبر به آنچه از پروردگارش به او نازل شده، ایمان آورده، و مؤمنان همگی به خدا و فرشتگان و کتاب ها و پیامبرانش، ایمان آورده اند [و بر اساس ایمان استوارشان گفتند:] ما میان هیچ یک از پیامبران او فرق نمی گذاریم. و گفتند: شنیدیم و اطاعت کردیم، پروردگارا! آمرزشت را خواهانیم و بازگشت [همه] به سوی توست.'),
(293, 2, 286, 'خدا هیچ کس را جز به اندازه توانایی اش تکلیف نمی کند. هر کس عمل شایسته ای انجام داده، به سود اوست، و هر کس مرتکب کار زشتی شده، به زیان اوست. [مؤمنان گویند:] پروردگارا! اگر فراموش کردیم یا مرتکب اشتباه شدیم، ما را مؤاخذه مکن. پروردگارا! تکالیف سنگینی برعهده ما مگذار، چنان که بر عهده کسانی که پیش از ما بودند گذاشتی. پروردگارا! و آنچه را به آن تاب و توان نداریم بر ما تحمیل مکن؛ و از ما درگذر؛ و ما را بیامرز؛ و بر ما رحم کن؛ تو سرپرست مایی؛ پس ما را بر گروه کافران پیروز فرما.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الم'),
(295, 3, 2, 'خدای یکتا که جز او هیچ معبودی نیست، زنده و قائم به ذات [و مدبّر و برپا دارنده ونگه دارنده همه مخلوقات] است.'),
(296, 3, 3, 'این کتاب را تدریجاً به حق و راستی بر تو نازل کرد که تصدیق کننده کتاب های پیش از خود است؛ و تورات و انجیل را'),
(297, 3, 4, 'پیش از این برای هدایت مردم فرستاد، و فرقان را [که مایه جدایی حق از باطل است] نازل کرد. مسلماً کسانی که به آیات خدا کافر شدند، برای آنان [به کیفرِ کفرشان] عذابی سخت است؛ وخدا توانای شکست ناپذیر و صاحب انتقام است.'),
(298, 3, 5, 'یقیناً چیزی در زمین و در آسمان بر خدا پوشیده نیست.'),
(299, 3, 6, 'اوست که شما را در رحم ها [یِ مادران] به هر گونه که می خواهد تصویر می کند. جز او هیچ معبودی نیست؛ توانای شکست ناپذیر و حکیم است.'),
(300, 3, 7, 'اوست که این کتاب را بر تو نازل کرد که بخشی از آن کتاب، آیات محکم است [که دارای کلماتی صریح و مفاهیمی روشن است] آنها اصل و اساس کتاب اند، و بخشی دیگر آیات متشابه است [که کلماتش غیر صریح و معانی اش مختلف و گوناگون است و جز به وسیله آیات محکم و روایات استوار تفسیر نمی شود] ولی کسانی که در قلوبشان انحراف [از هدایت الهی] است برای فتنه انگیزی و طلب تفسیرِ [نادرست و به تردید انداختن مردم و گمراه کردن آنان] از آیات متشابهش پیروی می کنند، و حال آنکه تفسیر واقعی و حقیقی آنها را جز خدا نمی داند. و استواران در دانش [و چیره دستان در بینش] می گویند: ما به آن ایمان آوردیم، همه [چه محکم، چه متشابه] از سوی پروردگار ماست. و [این حقیقت را] جز صاحبان خرد متذکّر نمی شوند.'),
(301, 3, 8, '[و می گویند:] پروردگارا! دل هایمان را پس از آنکه هدایتمان فرمودی منحرف مکن، و از سوی خود رحمتی برما ببخش؛ زیرا تو بسیار بخشنده ای.'),
(302, 3, 9, 'پروردگارا! قطعاً تو در روزی که هیچ شکی در آن نیست، گردآورنده همه مردمی؛ مسلماً خدا خلف وعده نمی کند.'),
(303, 3, 10, 'یقیناً اموال و فرزندان کافران هرگز چیزی از [عذاب] خدا را از آنان برطرف نمی کند؛ و اینانند که آتشگیره آتش اند.'),
(304, 3, 11, '[روشِ آنان] مانند روش فرعونیان و کسانی است که پیش از آنان بودند که آیات ما را تکذیب کردند، پس خدا هم آنان را به گناهانشان مؤاخذه کرد؛ و خدا سخت کیفر است.'),
(305, 3, 12, 'به کافران بگو: به زودی شکست می خورید و به سوی دوزخ محشور می شوید، و آن بد آرامگاهی است.'),
(306, 3, 13, 'تحقیقاً برای شما در دو گروهی که [در جنگ بدر] با هم رو به رو شدند، نشانه ای [از قدرت خدا و حقّانیّت نبوّت پیامبر] بود، گروهی در راه خدا می جنگیدند، و گروه دیگر کافر بودند، که اهل ایمان را به چشم خویش دو برابر می دیدند [به همین خاطر شکست خوردند] و خدا هر که را بخواهد با یاری خود تأیید می کند؛ مسلماً در این [واقعیت] عبرتی برای دارندگان بصیرت است.'),
(307, 3, 14, 'محبت و عشق به خواستنی ها [که عبارت است] از زنان و فرزندان و اموال فراوان از طلا و نقره و اسبان نشاندار و چهارپایان و کشت و زراعت، برای مردم آراسته شده است؛ اینها کالای زندگیِ [زودگذرِ] دنیاست؛ و خداست که بازگشت نیکو نزد اوست.'),
(308, 3, 15, 'بگو: آیا شما را به بهتر از این [امور] خبر دهم؟ برای آنان که [در همه شؤون زندگی] پرهیزکاری پیشه کرده اند، در نزد پروردگارشان بهشت هایی است که از زیرِ [درختانِ] آن نهرها جاری است، در آنجا جاودانه اند و [نیز برای آنان] همسرانی پاکیزه و خشنودی و رضایتی از سوی خداست؛ و خدا به بندگان بیناست.'),
(309, 3, 16, 'آنان که می گویند: پروردگارا! قطعاً ما ایمان آوردیم، پس گناهانمان را بیامرز وما را از عذاب آتش نگاه دار.'),
(310, 3, 17, 'آنان که صبر کنندگان و راستگویان و فرمانبرداران و انفاق کنندگان و استغفار کنندگان در سحرهایند؛'),
(311, 3, 18, 'خدا در حالی که برپا دارنده عدل است [با منطق وحی، با نظام مُتقن آفرینش و با زبان همه موجودات] گواهی می دهد که هیچ معبودی جز او نیست؛ و فرشتگان و صاحبان دانش نیز گواهی می دهند که هیچ معبودی جز او نیست؛ معبودی که توانای شکست ناپذیر و حکیم است.'),
(312, 3, 19, 'مسلماً دینِ [واقعی که همه پیامبرانْ مُبلّغ آن بودند] نزد خدا، اسلام است. و اهل کتاب [درباره آن] اختلاف نکردند مگر پس از آنکه آنان را [نسبت به حقّانیّت آن] آگاهی و دانش آمد، این اختلاف به خاطر حسد و تجاوز میان خودشان بود؛ و هر کس به آیات خدا کافر شود [بداند که] خدا حسابرسی، سریع است.'),
(313, 3, 20, 'پس اگر با تو جدال و گفتگوی خصومت آمیز کردند، [فقط در پاسخشان] بگو: من و همه پیروانمْ وجود خود را تسلیم خدا کرده ایم. و به اهل کتاب و به بی سوادانِ [مشرک] بگو: آیا شما هم تسلیم شده اید؟ پس اگر تسلیم شوند، قطعاً هدایت یافته اند، و اگر روی گرداندند [برتو دشوار و سخت نیاید] که آنچه بر عهده توست فقط ابلاغ [پیام خدا] ست؛ و خدا به بندگان بیناست.'),
(314, 3, 21, 'کسانی که پیوسته به آیات خدا کفر می ورزند، و همواره پیامبران را به ناحق می کشند، و از مردم کسانی را که امر به عدالت می کنند به قتل می رسانند، پس آنان را به عذابی دردناک بشارت ده.'),
(315, 3, 22, 'آنان کسانی اند که اعمالشان در دنیا و آخرت تباه و بی اثر شده، و برای آنان هیچ یاوری نخواهد بود.'),
(316, 3, 23, 'آیا به کسانی که بهره ای اندک از کتاب [تورات و انجیل] به آنان داده شده ننگریستی که به سوی کتاب خدا خوانده می شوند تا در میان آنان [درباره احکام الهی و نبوّت پیامبر اسلام] داوری کنند؟ سپس گروهی از آنان در حالی که اعراض کننده [از حق و حقیقت] اند، روی می گردانند.'),
(317, 3, 24, 'این روی گردانی به خاطر آن است که گفتند: هرگز آتش [دوزخ در روز قیامت] جز چند روزی به ما نمی رسد و آنچه همواره افترا و دروغ [به خدا] می بستند، آنان را در دینشان مغرور کرده است.'),
(318, 3, 25, 'پس چه خواهند کرد، هنگامی که آنان را برای روزی که هیچ شکی در آن نیست گرد آوریم، و به هر کس آنچه [از نیک و بد] به دست آورده، به طور کامل داده شود، و آنان مورد ستم قرار نخواهند گرفت.'),
(319, 3, 26, 'بگو: خدایا! ای مالک همه موجودات! به هر که خواهی حکومت می دهی و از هر که خواهی حکومت را می ستانی، و هر که را خواهی عزت می بخشی و هر که را خواهی خوار و بی مقدار می کنی، هر خیری به دست توست، یقیناً تو بر هر کاری توانایی.'),
(320, 3, 27, 'شب را در روز در می آوری و روز را در شب در می آوری، و زنده را از مرده بیرون می آوری و مرده را از زنده بیرون می آوری؛ و هر که را بخواهی بی حساب روزی می دهی.'),
(321, 3, 28, 'مؤمنان نباید کافران را به جای اهل ایمان، سرپرست و دوست بگیرند؛ و هر کس چنین کند در هیچ پیوند و رابطه ای با خدا نیست، مگر آنکه بخواهید به سبب دفع خطری که متوجه شماست از آنان تقیّه کنید؛ خدا شما را از [عذاب] خود بر حذر می دارد، و بازگشت [همه] به سوی خداست.'),
(322, 3, 29, 'بگو: اگر آنچه [از نیّت های فاسد و افکار باطل] در سینه های شماست، پنهان دارید یا آشکار کنید، خدا آن را می داند. و نیز آنچه را در آسمان ها و آنچه را در زمین است، می داند و خدا بر هر چیز تواناست.'),
(323, 3, 30, 'روزی که هر کس آنچه را از کار نیک انجام داده و آنچه را از کار زشت مرتکب شده حاضر شده می یابد، و آرزو می کند که ای کاش میان او و کارهای زشتش زمان دور و درازی فاصله بود. و خدا شما را از [عذاب] خود برحذر می دارد؛ و خدا به بندگان مهربان است.'),
(324, 3, 31, 'بگو: اگر خدا را دوست دارید، پس مرا پیروی کنید تا خدا هم شما را دوست بدارد، و گناهانتان را بیامرزد؛ و خدا بسیار آمرزنده و مهربان است.'),
(325, 3, 32, 'بگو: از خدا و پیامبر اطاعت کنید. پس اگر روی گردانیدند [بدانند که] یقیناً خدا کافران را دوست ندارد.'),
(326, 3, 33, 'بی تردید خدا آدم و نوح و خاندان ابراهیم و خاندان عمران را [به خاطر شایستگی های ویژه ای که در آنان بود] بر جهانیان برگزید.'),
(327, 3, 34, 'فرزندانی [را برگزید] که [از نظر پاکی، تقوا، درستی و راستی] برخی از [آنان از نسل] برخی دیگرند؛ و خدا شنوا و داناست.'),
(328, 3, 35, '[یاد کنید] هنگامی که همسر عمران گفت: پروردگارا! برای تو نذر کردم که آنچه را در شکم خود دارم [برای خدمت خانه تو از ولایت و سرپرستی من] آزاد باشد، بنابراین از من بپذیر؛ یقیناً تو شنوا و دانایی.'),
(329, 3, 36, 'زمانی که او را زایید، گفت: پروردگارا! من او را دختر زاییدم. و خدا به آنچه او زایید داناتر بود؛ و آن پسر [که زاییدن او را آرزو داشت، در کرامت، عظمت، ارزش و شخصیت] مانند این دختر نیست؛ [پس در مقام نام گذاریش گفت:] البته من نامش را «مریم» نهادم، و او و فرزندانش را از خطرات مهلک و وسوسه های بنیان برانداز شیطان رانده شده به پناه تو می آورم.'),
(330, 3, 37, 'پس پرودگارش او را به صورت نیکویی پذیرفت، و به طرز نیکویی نشو و نما داد، و زکریا را کفیلِ [رشد و تربیت معنوی] او قرار داد. هر زمان که زکریا در محراب [عبادت] بر او وارد می شد، رزق ویژه ای نزدش می یافت. [روزی در کمال شگفتی] گفت: ای مریم! این رزق ویژه برای تو از کجاست؟! گفت: از سوی خداست، یقیناً خدا هر کس را بخواهد، رزق بی حساب می دهد.'),
(331, 3, 38, 'در آنجا بود که زکریا [با دیدن کرامت و عظمت مریم] پروردگار خود را خوانده، گفت: پروردگارا! مرا از سوی خود فرزندی پاک و پاکیزه عطا کن، یقیناً تو شنوای دعایی.'),
(332, 3, 39, 'پس فرشتگان، او را در حالی که در محراب عبادت به نماز ایستاده بود، ندا دادند که خدا تو را به یحیی بشارت می دهد که تصدیق کننده کلمه ای از سوی خدا [یعنی مسیح] است و سرور و پیشوا، و [از روی زهد و حیا] نگاهدار خود از مُشتهیات نفسانی، و پیامبری از شایستگان است.'),
(333, 3, 40, 'گفت: پروردگارا! چگونه برای من پسری خواهد بود، در حالی که پیری به من رسیده و همسرم نازاست؟ خدا فرمود: چنین است [که می گویی، ولی کار خدا مُقیّد به علل و اسباب نیست] خدا هر چه را بخواهد [با مشیّت مطلقه خود] انجام می دهد.'),
(334, 3, 41, 'گفت: پروردگارا! برای من نشانه ای [جهت الهی بودن این بشارت] قرار ده. گفت: نشانه تو این است که سه روز نتوانی با مردم جز با رمز و اشاره سخن گویی، و پروردگارت را بسیار یاد کن و [او را] شام گاه و بامداد تسبیح گوی.'),
(335, 3, 42, 'و [یاد کنید] هنگامی که فرشتگان گفتند: ای مریم! قطعاً خدا تو را برگزیده و [از همه آلودگی های ظاهری و باطنی] پاک ساخته، و بر زنان جهانیان برتری داده است.'),
(336, 3, 43, 'ای مریم! فروتنانه برای پروردگارت به طاعت برخیز و سجده به جای آور و با رکوع کنندگان رکوع کن.'),
(337, 3, 44, 'این [حقایق] از خبرهای غیبی است که به تو وحی می کنیم. و تو هنگامی که آنان قلم های خود را [به عنوان قرعه زدن در آب] می انداختند که کدامیک از آنان سرپرستی مریم را عهده دار شود، و نیز زمانی که [برای کفالت او] با یکدیگر جدال و ستیز می کردند، نزد آنان نبودی.'),
(338, 3, 45, '[یاد کنید] زمانی که فرشتگان گفتند: ای مریم! یقیناً خدا تو را به کلمه ای از سوی خود که نامش مسیح عیسی بن مریم است مژده می دهد که در دنیا و آخرت دارای مقبولیّت و آبرومندی و از مقربّان است.'),
(339, 3, 46, 'و با مردم در گهواره [به صورت کاری خارق العاده] و در میان سالی [با زبان وحی] سخن می گوید و از شایستگان است.'),
(340, 3, 47, '[مریم] گفت: پروردگارا! چگونه برای من فرزندی خواهد بود، در حالی که هیچ بشری با من تماس نگرفته؟! [خدا] فرمود: چنین است که می گویی [ولی کار خدا مُقیّد به علل و اسباب نیست،] خدا هر چه را بخواهد [با مشیّت مطلقه خود] می آفریند، چون چیزی را اراده کند جز این نیست که به آن می گوید: «باش» پس بی درنگ می باشد؛'),
(341, 3, 48, 'و به او کتاب و حکمت و تورات و انجیل می آموزد.'),
(342, 3, 49, 'و به رسالت و پیامبری به سوی بنی اسرائیل می فرستد که [به آنان گوید:] من از سوی پروردگارتان برای شما نشانه ای [بر صدق رسالتم] آورده ام؛ من از گِل برای شما چیزی به شکل پرنده می سازم و در آن می دَمم که به اراده و مشیّت خدا پرنده ای [زنده و قادر به پرواز] می شود؛ و کور مادرزاد و مبتلای به پیسی را بهبود می بخشم؛ و مردگان را به اِذن خدا زنده می کنم؛ و شما را از آنچه می خورید و آنچه در خانه هایتان ذخیره می کنید خبر می دهم؛ مسلماً اگر مؤمن باشید این [معجزات] برای شما نشانه ای [بر صدق رسالت من] است؛'),
(343, 3, 50, 'وآنچه را از تورات پیش از من بوده، تصدیق دارم و [آمده ام] تا برخی از چیزهایی را که [در گذشته به سزای ستمکاری وگناهانتان] بر شما حرام شده حلال کنم، وبرای شما نشانه ای [برصدق رسالتم] از سوی پروردگارتان آورده ام؛ پس [ای بنی اسرائیل!] از خدا پروا کنید و مرا فرمان برید؛'),
(344, 3, 51, 'یقیناً خدا پروردگار من و پرودگار شماست؛ پس او را بپرستید، این است راه راست.'),
(345, 3, 52, 'زمانی که عیسی از آنان احساس کفر کرد، گفت: [برای اقامه دین و سلوک و حرکت] کیانند یاران من به سوی خدا؟ حواریون گفتند: ما یاران خداییم، به خدا ایمان آوردیم؛ و گواه باش که ما در برابر خدا [و فرمان ها و احکام او] تسلیم هستیم.'),
(346, 3, 53, 'پروردگارا! به آنچه نازل کردی ایمان آوردیم و از این پیامبر پیروی کردیم؛ پس ما را در زمره گواهان [که پیامبران تواند] بنویس.'),
(347, 3, 54, 'و [کافران از بنی اسرائیل درباره عیسی و آیینش] نیرنگ زدند، و خدا هم جزای نیرنگشان را داد؛ و خدا بهترین جزا دهنده نیرنگ زنندگان است.'),
(348, 3, 55, '[یاد کنید] هنگامی که خدا فرمود: ای عیسی! من تو را [از روی زمین و از میان مردم] بر می گیرم، و به سوی خود بالا می برم، و از بودن در میان اجتماع آلوده کافران پاک می کنم، و آنان را که از تو پیروی کردند تا روز قیامت برتر از کسانی که کافر شدند قرار می دهم؛ سپس بازگشت شما به سوی من است، و در میان شما در آنچه با هم اختلاف داشتید [به حقّ و عدالت] داوری می کنم؛'),
(349, 3, 56, 'اما کسانی که کافر شدند، آنان را در دنیا و آخرت به عذابی سخت شکنجه می کنم و برای آنان هیچ یاوری نخواهد بود؛'),
(350, 3, 57, 'و اما کسانی که ایمان آوردند و کارهای شایسته انجام دادند، خدا پاداششان را به طور کامل می دهد؛ و خدا ستمکاران را [که به دین کافر شدند، یا از دین فقط به اسم آن قناعت کردند] دوست ندارد.'),
(351, 3, 58, 'این داستان هایی که بر تو می خوانیم از آیات [الهی] و پندهای حکیمانه است.'),
(352, 3, 59, 'قطعاً داستان عیسی نزد خدا [از نظر چگونگی آفرینش] مانند داستان آدم است که [پیکر] او را از خاک آفرید، سپس به او فرمود: [موجود زنده] باش؛ پس بی درنگ [موجود زنده] شد.'),
(353, 3, 60, 'حق از [ناحیه] پروردگار توست؛ بنابراین از تردیدکنندگان مباش.'),
(354, 3, 61, 'پس هر که با تو درباره او [عیسی] پس از آنکه بر تو [به واسطه وحی، نسبت به احوال وی] علم و آگاهی آمد، مجادله و ستیز کند، بگو: بیایید ما پسرانمان را و شما پسرانتان را، و ما زنانمان را و شما زنانتان را، و ما نفوسمان را و شما نفوستان را دعوت کنیم؛ سپس یکدیگر را نفرین نماییم، پس لعنت خدا را بر دروغگویان قرار دهیم'),
(355, 3, 62, 'یقیناً این [واقعیاتی که بیان شد،] همان داستان راست و درست [مسیح] است؛ [و الوهیّت او یا فرزند خدا بودنش از پندارهای واهی و ادعاهای باطل نصاری است] و هیچ معبودی جز خدا نیست؛ و یقیناً خداست که توانای شکست ناپذیر و حکیم است.'),
(356, 3, 63, 'چنانچه [با این همه دلایل روشن از اعتراف به توحید و پذیرفتن اسلام] روی گرداندند، [بدانند که] یقیناً خدا به [وضع و حال] مفسدان آگاه است.'),
(357, 3, 64, 'بگو: ای اهل کتاب! بیایید به سوی سخنی که میان ما و شما یکسان است [و همه کتاب های آسمانی و پیامبران آن را ابلاغ کردند] که جز خدای یگانه را نپرستیم، و چیزی را شریک او قرار ندهیم، و بعضی از ما بعضی را اربابانی به جای خدا نگیرد. پس اگر [از دعوتت به این حقایق] روی گرداندند [تو و پیروانت] بگویید: گواه باشید که ما [در برابر خدا و فرمان ها و احکام او] تسلیم هستیم.'),
(358, 3, 65, 'ای اهل کتاب! چرا درباره ابراهیم مجادله و ستیز می کنید [شما قوم یهود، او را یهودی می دانید، و شما گروه نصاری، او را نصرانی می شمارید] در حالی که تورات و انجیل بعد از او نازل شد؛ آیا نمی اندیشید؟!'),
(359, 3, 66, 'آگاه باشید! شما کسانی هستید که درباره آنچه به آن آگاهی داشتید مجادله و ستیز کردید، [ستیز شما یهودیان با نصاری این بود که مسیح، فرزند خدا نیست، و ستیز شما نصاری با یهودیان این بود که عیسی دارای مقام نبوّت است و ایمان به او واجب است،] پس چرا درباره آنچه به آن آگاهی ندارید [و آن آیین ابراهیم است] مجادله و ستیز می کنید؟! و خدا می داند و شما نمی دانید.'),
(360, 3, 67, 'ابراهیم نه یهودی بود و نه نصرانی، بلکه یکتاپرست و حق گرایی تسلیم بود، و از مشرکان نبود.'),
(361, 3, 68, 'مسلماً نزدیک ترین مردم به ابراهیم [از جهت پیوند وانتساب معنوی] کسانی اند که [از روی حقیقت] از او پیروی کردند، و این پیامبر و کسانی که [به او] ایمان آورده اند [از همه به او نزدیک ترند] و خدا یاور و سرپرست مؤمنان است.'),
(362, 3, 69, 'گروهی از اهل کتاب آرزو دارند که کاش شما را [از راه خدا] گمراه کنند، در حالی که جز خودشان را گمراه نمی کنند، و [این واقعیت را] درک نمی کنند.'),
(363, 3, 70, 'ای اهل کتاب! چرا به آیات خدا [در تورات و انجیل درباره اوصاف پیامبر اسلام] کفر می ورزید در حالی که خودتان گواهی می دهید [که این آیات از سوی خدا نازل شده است؟!]'),
(364, 3, 71, 'ای اهل کتاب! چرا در حالی که [به حقایق و واقعیات] آگاهید، حق را به باطل مُشتبه می کنید، و حق را پنهان می دارید [تا مردم گمراه بمانند؟!]'),
(365, 3, 72, 'و گروهی از [عالمانِ] اهل کتاب [به پیروان خود] گفتند: اول روز به آنچه [از آیات قرآن] بر مؤمنان نازل شده [به مکر و حیله] ایمان آورید، و در پایان روز کفر ورزید، شاید [مؤمنان با این ایمان و انکار شما به تردید افتند و] از دینشان بازگردند!!'),
(366, 3, 73, 'و [نیز گفتند: در توضیح حقایق] جز به کسی که از دینتان پیروی می کند، اطمینان نورزید [تا دیگران هدایت نیابند]. بگو: یقیناً هدایت، هدایت خداست. سپس گفتند: گمان نکنید آنچه به شما اهل کتاب [از نبوّت، معجزه، قبله مستقل و آیات آسمانی] داده شده به کسی [از عرب و غیر عرب] داده شود، یا اینکه مؤمنان می توانند نزد پروردگارشان با شما محاجّه و گفتگو کنند. [در پاسخ یاوه های آنان] بگو: فضل و رحمت [که از جلوه هایش نبوّت، کتاب، معجزه و قبله است،] به دست خداست، به هر کس بخواهد می دهد؛ و خدا بسیار عطا کننده و داناست.'),
(367, 3, 74, 'هر که را بخواهد، به رحمت خود اختصاص می دهد و خدا دارای فضل بزرگ است.'),
(368, 3, 75, 'و از اهل کتاب کسی است که اگر او را بر مال فراوانی امین شماری، آن را به تو بازمی گرداند؛ و از آنان کسی است که اگر او را به یک دینار امین شماری، آن را به تو بازنمی گرداند، مگر آنکه همواره بالای سرش بایستی [و مال خود را با سخت گیری از او بستانی]. این به خاطر آن است که آنان گفتند: [چون ما اهل کتابیم] رعایت کردن حقوق غیر اهل کتاب بر عهده ما نیست، [و در ضایع کردن حقوق دیگران گناه و عقوبتی نداریم] و اینان [در حالی که باطل بودن گفتار خود را] می دانند بر خدا دروغ می بندند.'),
(369, 3, 76, 'آری، هر که به پیمان خود [در تعهد به اجرای احکام دین] وفا کرد، و [در همه امور زندگی] تقوا پیشه ساخت، [بداند که] یقیناً خدا تقوا پیشگان را دوست دارد.'),
(370, 3, 77, 'قطعاً کسانی که پیمان خدا و سوگندهایشان را [برای رسیدن به مقاصد دنیایی] به بهای اندکی می فروشند، برای آنان در آخرت بهره ای نیست؛ و خدا با آنان سخن نمی گوید، و در قیامت به آنان نظر [لطف و رحمت] نمی نماید، و [از گناه و آلودگی] پاکشان نمی کند؛ و برای آنان عذاب دردناکی خواهد بود.'),
(371, 3, 78, 'و از یهود گروهی هستند که هنگام [خواندن] دست نوشته ها [ی دروغ و بر بافته های خود] زبان [و صدایشان] را چنان پیچ و خم می دهند تا شما گمان کنید که [آنچه می خوانند] از کتاب آسمانی است، در حالی که از کتاب [آسمانی] نیست؛ و [با بی شرمی] می گویند: آنچه می خوانیم از سوی خداست. در حالی که از سوی خدا نیست؛ و با آنکه می دانند [از سوی خدا نیست] به خدا دروغ می بندند.'),
(372, 3, 79, 'هیچ انسانی را نسزد که خدا او را کتاب و حکمت و نبوّت دهد، سپس به مردم بگوید: به جای خدا بندگان من باشید بلکه [تکلیف الهی و انسانی او اقتضا می کند به مردم بگوید:] به خاطر آنکه کتاب خدا را تعلیم می دادید، و به سبب آنکه آن را می خواندید، دانشمندانِ «الهی مسلک» [و کاملان در دین] باشید.'),
(373, 3, 80, 'و نیز نَسِزد که به شما فرمان دهد که فرشتگان و پیامبران را خدایان خود گیرید. آیا [چنین انسان والایی که دارای نبوّت و حکمت است] شما را پس از آنکه تسلیم [خدا و فرمان ها و احکام او] شده اید، امر به کفر می کند؟'),
(374, 3, 81, 'و [یاد کنید] هنگامی که خدا از همه پیامبران [و امت هایشان] پیمان گرفت که هرگاه کتاب و حکمت به شما دادم، سپس [در آینده] پیامبری برای شما آمد که آنچه را [از کتاب های آسمانی] نزد شماست تصدیق کرد، قطعاً باید به او ایمان آورید و وی را یاری دهید. [آن گاه خدا] فرمود: آیا اقرار کردید و بر این [حقیقت] پیمان محکم مرا [به صورتی که به آن وفا کنید] دریافت نمودید؟ گفتند: اقرار کردیم. فرمود: پس [بر این پیمان] گواه باشید و من هم با شما از گواهانم.'),
(375, 3, 82, 'پس کسانی که بعد از این [پیمان محکم و استوار از آن] روی برتافتند فقط آنان هستند که از دایره انسانیت بیرون شده اند.'),
(376, 3, 83, 'آیا [اهل کتاب پس از این همه دلایل روشن] غیر دین خدا را خواستارند؟ در حالی که هر که در آسمان ها وزمین است از روی رغبت یا کراهت در برابر او [و اراده و فرمانش] تسلیم است، وهمه به سوی او بازگردانده می شوند.'),
(377, 3, 84, '[ای پیامبر! از جانب خود و پیروانت به همه] بگو: ما به خدا و آنچه بر ما نازل شده و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگان [دارای مقام نبوتشان] فرود آمده، و آنچه به موسی و عیسی و پیامبران از سوی پروردگارشان داده شده ایمان آوردیم، و میان هیچ یک از آنان فرق نمی گذاریم، و ما در برابر او [و فرمان ها و احکام او] تسلیم هستیم.'),
(378, 3, 85, 'و هرکه جز اسلام، دینی طلب کند، هرگز از او پذیرفته نمی شود و او در آخرت از زیانکاران است.'),
(379, 3, 86, 'چگونه خدا گروهی را هدایت کند که بعد از آنکه ایمان آوردند، و به حقّانیّت رسول اسلام شهادت دادند، و دلایل روشن و آشکار برای آنان آمد، کافر شدند؟ و خدا گروه ستمکاران را هدایت نمی کند.'),
(380, 3, 87, 'اینان کیفرشان این است که لعنت خدا و فرشتگان و همه مردم بر آنان است.'),
(381, 3, 88, 'در آن لعنت جاودانه اند؛ نه عذاب از آنان سبک شود، و نه مهلتشان دهند.'),
(382, 3, 89, 'مگر کسانی که بعد از آن توبه کردند و [مفاسد خود را] اصلاح نمودند؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(383, 3, 90, 'مسلماً کسانی که پس از ایمانشان کافر شدند، سپس بر کفر [خود] افزودند، هرگز توبه آنان پذیرفته نخواهد شد؛ و اینانند که گمراهند.'),
(384, 3, 91, 'قطعاً کسانی که کافر شدند و در حال کفر از دنیا رفتند، هرگز از هیچ یک از آنان [برای رهایی از عذاب] هر چند به اندازه آکندگی زمین از طلا فدیه و عوض دهد، پذیرفته نمی شود؛ برای آنان عذابی دردناک خواهد بود، و هیچ یاوری نخواهند داشت.'),
(385, 3, 92, 'هرگز به [حقیقتِ] نیکی [به طور کامل] نمی رسید تا از آنچه دوست دارید انفاق کنید؛ و آنچه از هر چیزی انفاق می کنید [خوب یا بد، کم یا زیاد، به اخلاص یا ریا] یقیناً خدا به آن داناست.'),
(386, 3, 93, 'همه خوراکی ها بر بنی اسرائیل حلال بود، جز آنچه اسرائیل [یعنی حضرت یعقوب] پیش از آنکه تورات نازل شود [به علتی] بر خود حرام کرد؛ [بنابراین آنچه از خوراکی ها در توراتْ حرام شده به کیفر گناهان بنی اسرائیل است نه آنکه از آیین ابراهیم به جا مانده باشد] بگو: اگر راستگویید [و حقیقت غیر از این است] تورات را [که از نظرها پنهان کرده اید] بیاورید و آن را بخوانید، [و اگر از آوردن تورات و خواندنش خودداری ورزید، اعتراف کنید که به تورات دروغ می بندید.]'),
(387, 3, 94, 'پس کسانی که بعد از این [دلایل روشن] بر خدا دروغ بندند، [و بگویند: آنچه از خوراکی ها در تورات حرام شده از شریعت ابراهیم باقی مانده و نسخ آن محال است،] اینانند که یقیناً ستمکارند.'),
(388, 3, 95, 'بگو: خدا راست گفت [که خوراکی ها در آیین ابراهیم حرام نبوده]. بنابراین از آیین ابراهیم که یکتاپرست و حق گرا بود و از مشرکان نبود، پیروی کنید.'),
(389, 3, 96, 'یقیناً نخستین خانه ای که برای [نیایش و عبادت] مردم نهاده شد، همان است که در مکه است، که پر برکت و وسیله هدایت برای جهانیان است.'),
(390, 3, 97, 'در آن نشانه هایی روشن [از ربوبیّت، لطف، رحمت خدا و از جمله] مقام ابراهیم است؛ و هر که وارد آن شود در امان است؛ و خدا را حقّی ثابت و لازم بر عهده مردم است که [برای ادای مناسک حج] آهنگ آن خانه کنند، [البته] کسانی که [از جهت سلامت جسمی و توانمندی مالی و باز بودن مسیر] بتوانند به سوی آن راه یابند، و هر که ناسپاسی ورزد [و از رفتن به آنجا خودداری کند، به خود زیان زده]؛ زیرا خدا از جهانیان بی نیاز است.'),
(391, 3, 98, 'بگو: ای اهل کتاب! چرا به آیات خدا کفر می ورزید؟ در حالی که خدا بر آنچه انجام می دهید، گواه است.'),
(392, 3, 99, 'بگو: ای اهل کتاب! چرا کسانی را که ایمان آورده اند از راه خدا ـ در حالی که خواهان کج نشان دادن آن هستید ـ بازمی دارید؟ با اینکه شما [به مستقیم بودن و حقّانیّتش] گواهید، و خدا از آنچه انجام می دهید بی خبر نیست.'),
(393, 3, 100, 'ای اهل ایمان! اگر از گروهی از کسانی که به آنان کتاب داده شده اطاعت کنید، شما را پس از ایمانتان به کفر بازمی گردانند.'),
(394, 3, 101, 'و چگونه کفر می ورزید در حالی که آیات خدا بر شما خوانده می شود، و پیامبر او در میان شماست؟! و هر کس به خدا تمسّک جوید، قطعاً به راه راست هدایت شده است.'),
(395, 3, 102, 'ای اهل ایمان! از خدا آن گونه که شایسته پروای از اوست، پروا کنید، و نمیرید مگر در حالی که [در برابر او و فرمان ها و احکامش] تسلیم باشید.'),
(396, 3, 103, 'و همگی به ریسمان خدا [قرآن و اهل بیت (علیهم السلام)] چنگ زنید، و پراکنده و گروه گروه نشوید؛ و نعمت خدا را بر خود یاد کنید آن گاه که [پیش از بعثت پیامبر و نزول قرآن] با یکدیگر دشمن بودید، پس میان دل های شما پیوند و الفت برقرار کرد، در نتیجه به رحمت و لطف او با هم برادر شدید، و بر لب گودالی از آتش بودید، پس شما را از آن نجات داد؛ خدا این گونه، نشانه های [قدرت، لطف و رحمت] خود را برای شما روشن می سازد تا هدایت شوید.'),
(397, 3, 104, 'و باید از شما گروهی باشند که [همه مردم را] به سوی خیر [اتحاد، اتفاق، الفت، برادری، مواسات و درستی] دعوت نمایند، و به کار شایسته و پسندیده وادارند، و از کار ناپسند و زشت بازدارند؛ و اینانند که یقیناً رستگارند.'),
(398, 3, 105, 'و [شما ای اهل ایمان!] مانند کسانی نباشید که پس از آنکه دلایل روشن برای آنان آمد، پراکنده و گروه گروه شدند و [در دین] اختلاف پیدا کردند، و آنان را عذابی بزرگ است؛'),
(399, 3, 106, 'در روزی که چهره هایی سپید و چهره هایی سیاه شود، اما آنان که چهره هایشان سیاه شده [به آنان گویند:] آیا پس از ایمانتان کافر شدید؟ پس به کیفر آنکه کفر می ورزیدید، این عذاب را بچشید.'),
(400, 3, 107, 'و اما آنان که چهره هایشان سپید گشته، همواره در رحمت خدایند، و در آن جاودانه اند.'),
(401, 3, 108, 'اینها [که سرگذشت اهل کتاب، مژده ها، بیم ها و امور مربوط به آخرت است] آیات خداست که آن را به درستی و راستی بر تو می خوانیم؛ و خدا هیچ ستم و بیدادی بر جهانیان نمی خواهد.'),
(402, 3, 109, 'و آنچه در آسمان و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست، و همه امور به سوی او بازگردانده می شود.'),
(403, 3, 110, 'شما بهترین امتی هستید که [برای اصلاح جوامع انسانی] پدیدار شده اید، به کار شایسته و پسندیده فرمان می دهید و از کار ناپسند و زشت بازمی دارید، و [از روی تحقیق، معرفت، صدق و اخلاص] به خدا ایمان می آورید. و اگر اهل کتاب ایمان می آوردند قطعاً برای آنان بهتر بود؛ برخی از آنان مؤمن [به قرآن و پیامبرند] و بیشترشان فاسقند.'),
(404, 3, 111, 'هرگز به شما جز آزاری اندک نمی رسانند، و اگر با شما بجنگند [شکست می خورند و] به شما پشت کرده [فرار می کنند] آن گاه [از جانب دیگران] یاری نمی شوند.'),
(405, 3, 112, 'هر زمان و هر کجا یافت شوند، [داغِ] خواری و ذلت بر آنان زده شده، مگر [آنکه] به ریسمانی از جانب خدا [که ایمان به قرآن و نبوّت پیامبر است] و یا ریسمانی از سوی مردمِ [مؤمن که پذیرش ذمه و شرایط آن است، چنگ زنند] و به خشمی از سوی خدا سزاوار شده اند و [داغِ] بینوایی و بدبختی بر آنان زده شد. این بدان سبب است که آنان همواره به آیات خدا کفر می ورزیدند و پیامبران را به ناحق می کشتند، و این [کفرورزی و کشتن پیامبران] به سبب این است که [خدا را] نافرمانی نمودند و همواره [از حدود الهی] تجاوز می کردند.'),
(406, 3, 113, '[همه اهل کتاب] یکسان نیستند، از اهل کتاب گروهی درستکار [و رعایت کننده حقّ خدا و مردم] هستند، آیات خدا را در ساعاتی از شب می خوانند و [به پیشگاه حق از روی تواضع و فروتنی] سجده می کنند.'),
(407, 3, 114, '[در سایه قرآن و نبوّت پیامبر] به خدا و روز قیامت ایمان می آورند و به کار شایسته و پسندیده فرمان می دهند، و از کار ناپسند و زشت بازمی دارند و در کارهای خیر می شتابند؛ و اینان از شایستگانند.'),
(408, 3, 115, 'و هر کار خیری انجام دهند، هرگز درباره آن مورد ناسپاسی قرار نخواهند گرفت؛ و خدا به تقواپیشگان داناست.'),
(409, 3, 116, 'قطعاً کسانی که کافر شدند، هرگز اموال و اولادشان چیزی از [عذاب] خدا را از آنان دفع نخواهد کرد؛ و آنان اهل آتشند و در آن جاودانه اند.'),
(410, 3, 117, 'داستان آنچه [کافران] در این زندگی دنیا انفاق می کنند، مانند بادی آمیخته با سرمایی سخت است که [به عنوان مجازات] به کشتزار قومی که بر خود ستم کرده اند برسد و آن را نابود کند؛ و خدا به آنان ستم نکرده است، ولی آنان به خویشتن ستم می ورزند.'),
(411, 3, 118, 'ای اهل ایمان! از غیر خودتان برای خود محرم راز نگیرید؛ آنان از هیچ توطئه و فسادی درباره شما کوتاهی نمی کنند؛ شدت گرفتاری و رنج و زیان شما را دوست دارند؛ تحقیقاً دشمنی [با اسلام و مسلمانان] از لابلای سخنانشان پدیدار است و آنچه سینه هایشان [از کینه و نفرت] پنهان می دارد بزرگ تر است. ما نشانه ها [یِ دشمنی و کینه آنان] را اگر می اندیشید برای شما روشن ساختیم.'),
(412, 3, 119, 'آگاه باشید! این شمایید که آنان را دوست دارید، و آنان شما را دوست ندارند. و شما به همه کتاب ها [یِ آسمانی] ایمان دارید [ولی آنان ایمان ندارند]. و چون با شما دیدار کنند، می گویند: ایمان آوردیم. و زمانی که با یکدیگر خلوت نمایند از شدت خشمی که بر شما دارند، سر انگشتان خود را می گزند. بگو: به خشمتان بمیرید. یقیناً خدا به آنچه در سینه هاست، داناست.'),
(413, 3, 120, 'اگر به شما خیر و خوشی [و پیروزی و غنیمت] رسد، آنان را بدحال [و دلتنگ] می کند، و اگر بدی و ناخوشی [و حادثه تلخی] رسد به سبب آن خوشحال می شوند؛ و اگر شکیبایی ورزید و پرهیزکاری کنید، نیرنگشان هیچ زیانی به شما نمی رساند؛ مسلماً خدا به آنچه انجام می دهند، احاطه دارد.'),
(414, 3, 121, 'و [یاد کن] زمانی را که صبحگاهان [برای جنگ احد] از میان خانواده ات بیرون آمدی تا مؤمنان را برای جنگیدن در سنگرهای نظامی جای دهی؛ و خدا شنوا [یِ به گفته ها] و دانا [یِ بهنیّت ها] ست.'),
(415, 3, 122, '[یاد کنید که] در آن هنگام دو گروه از شما بر آن شدند که سستی و ناتوانی نشان دهند [و از جنگ منصرف شده، برگردند]، در حالی که خدا یار و یاورشان بود [لذا از این قصد شیطانی بازشان داشت] و مؤمنان باید فقط بر خدا توکل کنند.'),
(416, 3, 123, 'و بی تردید خدا در [جنگِ] بدر شما را یاری داد، در حالی که [از نظر ساز و برگ جنگی و شمار نفرات نسبت به دشمن] ناتوان بودید؛ بنابراین از خدا پروا کنید، باشد که سپاس گزاری نمایید.'),
(417, 3, 124, 'آن هنگام که به مؤمنان می گفتی: آیا شما را بس نیست که پروردگارتان به سه هزار فرشته نازل شده شما را یاری دهد؟'),
(418, 3, 125, 'آری، اگر شکیبایی ورزید و پرهیزکاری کنید و دشمنان در همین لحظه، جوشان و خروشان بر شما بتازند، پروردگارتان شما را با پنج هزار فرشته نشان دار یاری می دهد.'),
(419, 3, 126, 'و خدا [وعده یاری و پیروزی] را جز بشارتی برای شما و برای آنکه دل هایتان به آن آرامش یابد، قرار نداد؛ و یاری و نصرت جز از سوی خدای توانای شکست ناپذیر و حکیم نیست.'),
(420, 3, 127, 'تا برخی از کافران را [از ریشه و بن] نابود کند، یا آنان را خوار و ذلیل سازد، تا نومید بازگردند.'),
(421, 3, 128, 'زمام چیزی از امور [مشرکان و مؤمنانِ فراری از جنگ] در اختیار تو نیست، یا توبه آنان را [به شرط آنکه توبه کنند] می پذیرد یا عذابشان می کند؛ زیرا آنان ستمکارند.'),
(422, 3, 129, 'و آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست. هر که را بخواهد می آمرزد و هر که را بخواهد عذاب می کند؛ و خدا بسیار آمرزنده و مهربان است.'),
(423, 3, 130, 'ای اهل ایمان! ربا را که سودهای چند برابر است نخورید، و از خدا پروا کنید تا رستگار شوید.'),
(424, 3, 131, 'و از آتشی که برای کافران آماده شده است بپرهیزید.'),
(425, 3, 132, 'و از خدا و پیامبر فرمان برید تا مورد رحمت قرار گیرید.'),
(426, 3, 133, 'و به سوی آمرزشی از پروردگارتان و بهشتی که پهنایش [به وسعتِ] آسمان ها و زمین است بشتابید؛ بهشتی که برای پرهیزکاران آماده شده است؛'),
(427, 3, 134, 'آنان که در گشایش و تنگ دستی انفاق می کنند، و خشم خود را فرو می برند، و از [خطاهایِ] مردم در می گذرند؛ و خدا نیکوکاران را دوست دارد.'),
(428, 3, 135, 'و آنان که چون کار زشتی مرتکب شوند یا بر خود ستم ورزند، خدا را یاد کنند و برای گناهانشان آمرزش خواهند؛ و چه کسی جز خدا گناهان را می آمرزد؟ و دانسته و آگاهانه بر آنچه مرتکب شده اند، پا فشاری نمی کنند؛'),
(429, 3, 136, 'پاداش آنان آمرزشی است از سوی پروردگارشان، و بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، در آن جاودانه اند؛ و پاداش عمل کنندگان، نیکوست.'),
(430, 3, 137, 'قطعاً پیش از شما روش هایی [در میان ملل و جوامع بوده که از میان رفته است]، پس در زمین گردش کنید و با دقت و تامّل بنگرید که سرانجام تکذیب کنندگانِ [حقایق] چگونه بود.'),
(431, 3, 138, 'این [قرآن] برای مردم، بیانگر [حوادث و واقعیات] و برای پرهیزکاران، سراسر هدایت و اندرز است.'),
(432, 3, 139, 'و [در انجام فرمان هایِ حق و در جهاد با دشمن] سستی نکنید و [از پیش آمدها و حوادث و سختی هایی که به شما می رسد] اندوهگین مشوید که شما اگر مؤمن باشید، برترید.'),
(433, 3, 140, 'اگر [در جنگ احد] به شما آسیب و جراحتی رسید، آن گروه را نیز [در جنگ بدر] آسیب و جراحتی مانند آن رسید. و ما این روزها [یِ پیروزی و ناکامی] را [به عنوان امتحان] در میان مردم می گردانیم [تا عبرت گیرند] و خدا کسانی را که [از روی حقیقت و خلوص] ایمان آورده اند، مشخص کند و از میان شما گواهانی [نسبت به پیروزی ها که نتیجه طاعت و تقواست، و شکست ها که محصول نافرمانی و بی تقوایی است] بگیرد؛ و خدا ستمکاران را [که با سستی و بی تقوایی بر خود ستم می کنند] دوست ندارد.'),
(434, 3, 141, 'و [نیز این پیروزی ها و ناکامی ها] برای آن است که کسانی را که [از روی حقیقت] ایمان آورده اند [از عیوب و آلودگی ها] تصفیه و پاک کند، و کافران را نابود نماید.'),
(435, 3, 142, 'آیا پنداشته اید [با ایمانِ بدون عمل] وارد بهشت می شوید، در حالی که هنوز خدا کسانی از شما را که در راه خدا جهاد کرده اند وشکیبایان را [از دیگران] مشخص و معلوم نکرده است؟!'),
(436, 3, 143, 'و یقیناً شما [پس از آگاهی از درجات شُهدایِ بدر] مرگِ [در میدان جنگ] را پیش از رویارویی با آن سخت آرزو می کردید، و هنگامی که با آن روبرو شدید [به هراس افتادید و بدون هیچ اقدامی] به تماشای آن پرداختید!!'),
(437, 3, 144, 'و محمّد جز فرستاده ای از سوی خدا که پیش از او هم فرستادگانی [آمده و] گذشته اند، نیست. پس آیا اگر او بمیرد یا کشته شود، [ایمان و عمل صالح را ترک می کنید و] به روش گذشتگان و نیاکان خود برمی گردید؟! و هر کس به روش گذشتگان خود برگردد، هیچ زیانی به خدا نمی رساند؛ و یقیناً خدا سپاس گزاران را پاداش می دهد.'),
(438, 3, 145, 'و هیچ کس جز به مشیّت و فرمان خدا نمی میرد. سرنوشتی است مقرّر شده و هر که پاداش دنیا را بخواهد [اندکی از آن] به او می دهیم، و هر که خواستار پاداش آخرت باشد، او را از آن می بخشیم؛ و یقیناً سپاس گزاران را پاداش خواهیم داد.'),
(439, 3, 146, 'چه بسا پیامبرانی که انبوهی دانشمندانِ الهی مسلک [و کاملان در دینِ] به همراه او با دشمنان جنگیدند، پس در برابر آسیب هایی که در راه خدا به آنان رسید، سستی نکردند و ناتوان نشدند و [در برابر دشمن] سر تسلیم و فروتنی فرود نیاوردند؛ و خدا شکیبایان را دوست دارد.'),
(440, 3, 147, 'و سخن آنان [در گرما گرم و سختی جنگ] جز این نبود که گفتند: پروردگارا! گناهان ما و زیاده روی در کارمان را بر ما ببخش و قدم هایمان را استوار بدار و ما را بر گروه کافران یاری ده.'),
(441, 3, 148, 'پس خدا پاداش این دنیا و پاداش نیک آخرت را به آنان عطا فرمود؛ و خدا نیکوکاران را دوست دارد.'),
(442, 3, 149, 'ای اهل ایمان! اگر از کافران فرمان برید، شما را به [عقاید و روش های کافرانه] گذشتگانتان بازمی گردانند، در نتیجه زیانکار خواهید شد.'),
(443, 3, 150, '[آنان نه سرپرست شما و نه شایسته اطاعت هستند] بلکه خدا یار و سرپرست شماست؛ و او بهترین یاری دهندگان است.'),
(444, 3, 151, 'به زودی در دل های کافران ترس می اندازیم؛ زیرا چیزی را که خدا بر [حقّانیّت] آن دلیلی نازل نکرده، شریک خدا قرار داده اند، و جایگاهشان آتش است؛ و بد است جایگاه ستمکاران.'),
(445, 3, 152, 'و یقیناً خدا وعده اش را [در جنگ احد] برای شما تحقّق داد، آن گاه که دشمنان را به فرمان او تا مرز ریشه کن شدنشان می کشتید، تا زمانی که سست شدید و در کارِ [جنگ و غنیمت و حفظ سنگری که محل رخنه دشمن بود] به نزاع و ستیز برخاستید، و پس از آنکه [در شروع جنگ] آنچه را از پیروزی و غنیمت دوست داشتید، به شما نشان داد [از فرمان پیامبر در رابطه با حفظ سنگر] سر پیچی کردید، برخی از شما دنیا را می خواست و برخی از شما خواهان آخرت بود، سپس برای آنکه شما را امتحان کند از [پیروزی بر] آنان بازداشت و از شما درگذشت؛ و خدا بر مؤمنان دارای فضل است.'),
(446, 3, 153, '[یاد کنید] زمانی که [از میدان جنگ احد] تا مرز پنهان شدن از دیده ها دور می شدید و به هیچ کس توجه نمی کردید، در صورتی که پیامبر [که اجابت دعوتش واجب است] شما را از پشت سرتان فرا می خواند، پس خدا شما را به اندوهی روی اندوهی مجازات کرد تا بر آنچه [از پیروزی و غنیمت] از دستتان رفته و به آنچه [از آسیب و مصیبت] به شما رسیده، اندوهگین نشوید؛ و خدا به آنچه انجام می دهید، آگاه است.'),
(447, 3, 154, 'سپس بعد از آن اندوه و غم، خواب آرام بخشی بر شما فرود آمد که گروهی از شما را [که بر اثر پشیمانی، دست از فرار برداشته به سوی پیامبر آمدید] فرا گرفت، و گروهی که فکر حفظ جانشان آنان را [در آن میدان پر حادثه] پریشان خاطر و غمگین کرده بود، و درباره خدا گمان ناحق و ناروا هم چون گمان هایِ [زمانِ] جاهلیت می بردند [که چون خدا وعده پیروزی داده پس پیروزی بدون هر قید و شرطی حقّ مسلّم آنان است! اما وقتی شکست خوردند درباره وعده خدا دچار تردید شدند و] گفتند: آیا ما را در این امر [پیروزی] اختیاری هست؟ بگو: یقیناً اختیار همه امور به دست خداست. [این نیست که چون خدا وعده پیروزی به شما داده بدون قید و شرط برای شما حاصل شود، پیروزیِ وعده داده شده، محصول صبر و تقوا، و شکست معلول سستی و نافرمانی است]. آنان در دل هایشان چیزی را پنهان می کنند که برای تو آشکار نمی سازند، می گویند: اگر ما را در این امر [پیروزی] اختیاری بود [و وعده خدا و پیامبر حقیقت داشت] در اینجا کشته نمی شدیم. بگو: اگر شما در خانه های خود هم بودید کسانی که کشته شدن بر آنان لازم و مقرّر شده بود، یقیناً به سوی خوابگاه های خود [در معرکه جهاد و جنگ] بیرون می آمدند. و [تحقّق دادن این برنامه ها] به سبب این است که خدا آنچه را [از نیّت ها] در سینه های شماست [در مقام عمل] بیازماید، و آنچه را [از عیوب و آلودگی ها] در دل های شماست، خالص و پاک گرداند؛ و خدا به آنچه در سینه هاست، داناست.'),
(448, 3, 155, 'قطعاً کسانی از شما روزی که [در نبرد احد] که دو گروه [مؤمن و مشرک] با هم رویاروی شدند، به دشمن پشت کردند، جز این نیست که شیطان آنان را به سبب برخی از گناهانی که مرتکب شده بودند لغزانید، و یقیناً خدا از آنان در گذشت؛ زیرا خدا بسیار آمرزنده و بردبار است.'),
(449, 3, 156, 'ای اهل ایمان! مانند کسانی نباشید که کفر ورزیدند و درباره برادرانشان هنگامی که آنان به سفر رفتند [و در سفر مُردند] و یا جهادگر بودند [و شهید شدند]، گفتند: اگر نزد ما مانده بودند نمی مردند و شهید نمی شدند. [شما به کافران اعتنا نکنید] تا خدا این [اعتقاد و گفتار] را حسرتی در دل هایشان قرار دهد. و خداست که زنده می کند و می میراند؛ و خدا به آنچه انجام می دهید، بیناست.'),
(450, 3, 157, 'و اگر در راه خدا شهید شوید یا بمیرید، یقیناً آمرزش و رحمتی از سوی خدا، بهتر است از آنچه [آنان از مال و منال دنیا] جمع می کنند.'),
(451, 3, 158, 'و اگر بمیرید یا شهید شوید، به سوی خدا محشور خواهید شد.'),
(452, 3, 159, '[ای پیامبر!] پس به مهر و رحمتی از سوی خدا با آنان نرم خوی شدی، و اگر درشت خوی و سخت دل بودی از پیرامونت پراکنده می شدند؛ بنابراین از آنان گذشت کن، و برای آنان آمرزش بخواه، و در کارها با آنان مشورت کن، و چون تصمیم گرفتی بر خدا توکل کن؛ زیرا خدا توکل کنندگان را دوست دارد.'),
(453, 3, 160, 'اگر خدا شما را یاری کند، هیچ کس بر شما چیره و غالب نخواهد شد، و اگر شما را واگذارد، چه کسی بعد از او شما را یاری خواهد داد؟ و مؤمنان باید فقط بر خدا توکل کنند.'),
(454, 3, 161, 'هیچ پیامبری را نَسِزد که [در اموال، غنایم، سایر امور به امت خود] خیانت ورزد و هر که خیانت کند، روز قیامت با آنچه در آن خیانت کرده بیاید؛ سپس به هر کس آنچه را مرتکب شده به طور کامل می دهند، و آنان مورد ستم قرار نمی گیرند.'),
(455, 3, 162, 'پس آیا کسی که [با طاعت و عبادت] از خشنودی خدا پیروی کرده، همانند کسی است که [بر اثر گناه] به خشمی از سوی خدا سزاوار شده؟ و جایگاه او دوزخ است و آن بد بازگشت گاهی است.'),
(456, 3, 163, 'همه آنان را [چه مؤمن، چه کافر] نزد خدا درجات و منزلت هایی [متفاوت] است، و خدا به آنچه انجام می دهند، بیناست.'),
(457, 3, 164, 'یقیناً خدا بر مؤمنان منّت نهاد که در میان آنان پیامبری از خودشان برانگیخت که آیات او را بر آنان می خواند و [از آلودگی های فکری و روحی] پاکشان می کند، و کتاب و حکمت به آنان می آموزد، و به راستی که آنان پیش از آن در گمراهی آشکاری بودند.'),
(458, 3, 165, 'آیا زمانی که [در جنگ احد] آسیبی به شما رسید که بی تردید دو برابرش را [در جنگ بدر به دشمن] رساندید، [از روی بی صبری، جزع، ناراحتی و اضطراب] می گویید: این آسیب چگونه و از کجاست؟ بگو: از ناحیه خود شماست [که بر اثر سستی در جنگ، نافرمانی از پیامبر، نزاع و اختلاف، به شما رسید]. یقیناً خدا بر هر کاری تواناست.'),
(459, 3, 166, 'و آنچه [در جنگ احد] روزی که دو گروه [مؤمن و مشرک] با هم رویاروی شدند به شما رسید به اذن خدا بود، [تا شما را امتحان کند] و مؤمنان را معلوم و مشخص نماید.'),
(460, 3, 167, 'و نیز کسانی را که نفاق و دورویی ورزیدند، معلوم و مشخص کند. و به آنان گفته شد: بیایید در راه خدا بجنگید یا [از مدینه و کیان جامعه] دفاع کنید. گفتند: اگر جنگیدن می دانستیم، قطعاً از شما پیروی می کردیم. آنان در آن روز به کفر نزدیک تر بودند تا ایمان. به زبانشان چیزی را می گویند که در دل هایشان نیست؛ و خدا به آنچه پنهان می کنند، داناتر است.'),
(461, 3, 168, 'همان کسانی که [از جنگ کناره گرفتند]، و در خانه های خود نشستند، و درباره برادرانشان گفتند: اگر از ما فرمان می بردند کشته نمی شدند. بگو: [چنانچه اختیار مرگ در دست شماست] پس مرگ را از خود دفع کنید، اگر راستگویید.'),
(462, 3, 169, 'و هرگز گمان مبر آنان که در راه خدا کشته شدند مرده اند، بلکه زنده اند و نزد پروردگارشان روزی داده می شوند.'),
(463, 3, 170, 'در حالی که خدا به آنچه از بخشش و احسان خود به آنان عطا کرده شادمانند، و برای کسانی که از پی ایشانند و هنوز به آنان نپیوسته اند [و سرانجام به شرف شهادت نایل می شوند] شادی می کنند، که نه بیمی بر آنان است ونه اندوهگین می شوند.'),
(464, 3, 171, '[شهیدان] به نعمت و فضلی از سوی خدا و اینکه خدا پاداش مؤمنان را تباه نمی کند، شادمان و مسرورند.'),
(465, 3, 172, 'برای نیکوکاران و تقواپیشگان از کسانی که خدا و رسول را پس از آنکه زخم و جراحت به آنان رسیده بود [برای جنگی دیگر بعد از جنگ احد] اجابت کردند، پاداشی بزرگ است.'),
(466, 3, 173, 'همان کسانی که مردمِ [منافق و عوامل نفوذی دشمن] به آنان گفتند: لشکری انبوه از مردم [مکه] برای جنگ با شما گرد آمده اند، از آنان بترسید. ولی [این تهدید] بر ایمانشان افزود، و گفتند: خدا ما را بس است، و او نیکو وکیل و [نیکو کارگزاری] است.'),
(467, 3, 174, 'پس با نعمت و بخششی از سوی خدا [از میدان جنگ] بازگشتند، در حالی که هیچ گزند و آسیبی به آنان نرسیده بود، و از خشنودی خدا پیروی کردند؛ و خدا دارای فضلی بزرگ است.'),
(468, 3, 175, 'در حقیقت این شیطان است که دوستانش را [با شایعه پراکنی و گفتار وحشت زا، از رفتن به جهاد] می ترساند؛ پس اگر مؤمن هستید از آنان نترسید و از من بترسید.'),
(469, 3, 176, 'و مبادا آنان که در کفر می شتابند، تو را اندوهگین کنند، آنان هرگز به خدا هیچ زیانی نمی رسانند، خدا می خواهد [به سزای کفرشان] هیچ بهره ای در آخرت برای آنان قرار ندهد، و برای آنان عذابی بزرگ است.'),
(470, 3, 177, 'مسلماً کسانی که کفر را به بهای از دست دادن ایمان خریدند، هرگز به خدا هیچ زیانی نمی رسانند، و برای آنان عذابی دردناک است.'),
(471, 3, 178, 'و کسانی که کافر شدند، گمان نکنند مهلتی که به آنان می دهیم به سودشان خواهد بود، جز این نیست که مهلتشان می دهیم تا بر گناه خود بیفزایند، و برای آنان عذابی خوار کننده است.'),
(472, 3, 179, 'خدا بر آن نیست که مؤمنان را بر این [وضعی] که شما بر آن قرار دارید [که منافق از مؤمن، و خوب از بد مشخص و معلوم نیست] واگذارد، [بر آن است] تا پلید را از پاک [به سبب آزمایش های مختلف] جدا سازد. و خدا بر آن نیست که شما را بر غیب آگاه کند. ولی خدا از میان فرستادگانش هر کس را بخواهد [برای آگاه کردن به غیب] برمی گزیند، پس به خدا و فرستادگانش ایمان آورید. و اگر ایمان آورید و تقوا پیشه کنید، برای شما پاداشی بزرگ خواهد بود.'),
(473, 3, 180, 'و کسانی که خدا به آنچه از فضلش به آنان داده بخل می ورزند، گمان نکنند که آن بخل به سود آنان است، بلکه آن بخل به زیانشان خواهد بود. به زودی آنچه به آن بخل ورزیدند در روز قیامت طوق گردنشان می شود. و میراث آسمان ها و زمین فقط در سیطره مالکیّت خداست، و خدا به آنچه انجام می دهید، آگاه است.'),
(474, 3, 181, 'محققاً خدا سخن کسانی را که گفتند: «خدا نیازمند است و ما بی نیازیم»، شنید. بی تردید آنچه را گفتند، و [نیز] کشتن پیامبران را از روی ستم [در پرونده اعمالشان] می نویسیم، و [روز قیامت] می گوییم: عذاب سوزان را بچشید.'),
(475, 3, 182, 'این [عذاب] به خاطر فسق و فجور و گناهانی است که خود پیش فرستادید، و گرنه خدا به بندگان ستمکار نیست.'),
(476, 3, 183, 'کسانی که گفتند: خدا به ما سفارش کرده است که به هیچ پیامبری ایمان نیاوریم تا برای ما [به نشانه صدق نبوتش] یک قربانی بیاورد که آن را آتش [غیبی] بسوزاند، بگو: پیش از من پیامبرانی دلایل روشن و آنچه گفتید برای شما آوردند؛ اگر راستگویید، پس چرا آنان را کشتید؟'),
(477, 3, 184, 'اگر [این یهودیان بهانه جو] تو را تکذیب کردند، [غمگین مباش] مسلماً رسولانی هم که پیش از تو دلایل روشن، و نوشته ها [ی مشتمل بر پند و موعظه] و کتاب روشنگر آورده بودند، تکذیب شدند؟'),
(478, 3, 185, 'هر کسی مرگ را می چشد؛ و بدون تردید روز قیامت پاداش هایتان به طور کامل به شما داده می شود. پس هر که را از آتش دور دارند و به بهشت درآورند مسلماً کامیاب شده است؛ و زندگی این دنیا جز کالای فریبنده نیست.'),
(479, 3, 186, 'یقیناً در اموال و جان هایتان امتحان خواهید شد، و مسلماً از کسانی که پیش از شما کتاب آسمانی به آنان داده شده و [نیز] از کسانی که شرک آوردند، سخنان رنج آور بسیاری خواهید شنید، و اگر [در برابر آزار اینان] شکیبایی ورزید و [از تجاوز از حدود الهی] بپرهیزید [سزاوارتر است.] این اموری است که ملازمت بر آن از واجبات است.'),
(480, 3, 187, 'و [یاد کنید] هنگامی که خدا از کسانی که به آنان کتاب آسمانی داده شد، پیمان گرفت که حتماً باید [احکام و حقایق] آن را برای مردم بیان کنید و پنهانش ننمایید. پس آن [عهد و کتاب] را ترک کردند و به آن اعتنایی ننمودند، و در برابر ترک آن اندک بهایی به دست آوردند؛ و بد چیزی است آنچه به دست می آورند.'),
(481, 3, 188, 'البته مپندار کسانی که به آنچه [از پنهان داشتن حقایق و باز داشتن مردم از اسلام] انجام دادند، شادمانی می کنند، و دوست دارند به آنچه انجام نداده اند ستایش شوند [از عذاب خدا در امانند،] پس گمان مبر که برای آنان نجاتی از عذاب است، [بلکه] برای آنان عذابی دردناک خواهد بود.'),
(482, 3, 189, 'و مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره خداست، و خدا بر هر کاری تواناست.'),
(483, 3, 190, 'یقیناً در آفرینش آسمان ها و زمین، و آمد و رفت شب و روز، نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] برای خردمندان است.'),
(484, 3, 191, 'آنان که همواره خدا را ایستاده و نشسته و به پهلو آرمیده یاد می کنند، و پیوسته در آفرینش آسمان ها و زمین می اندیشند، [و از عمق قلب همراه با زبان می گویند:] پروردگارا! این [جهان با عظمت] را بیهوده نیافریدی، تو از هر عیب و نقصی منزّه و پاکی؛ پس ما را از عذاب آتش نگاهدار.'),
(485, 3, 192, 'پروردگارا! بی تردید هر که را تو در آتش در آوری، قطعاً خوار و رسوایش کرده ای، و برای ستمکاران هیج یاری وجود ندارد.'),
(486, 3, 193, 'پروردگارا! بی تردید ما [صدای] ندا دهنده ای را شنیدیم [که مردم را] به ایمان فرا می خواند که به پروردگارتان ایمان آورید. پس ما ایمان آوردیم. پروردگارا! گناهان ما را بیامرز، و بدی هایمان را از ما محو کن، و ما را در زمره نیکوکاران بمیران.'),
(487, 3, 194, 'پروردگارا! آنچه را که به وسیله فرستادگانت به ما وعده داده ای به ما عطا فرما و روز قیامت، ما را رسوا و خوار مکن؛ زیرا تو خلف وعده نمی کنی؟'),
(488, 3, 195, 'پس پروردگارشان دعای آنان را اجابت کرد [که] یقیناً من عمل هیچ عمل کننده ای از شما را از مرد یا زن که همه از یکدیگرند تباه نمی کنم؛ پس کسانی که [برای خدا] هجرت کردند، و از خانه هایشان رانده شدند، و در راه من آزار دیدند، و جنگیدند و کشته شدند، قطعاً بدی هایشان را محو خواهم کرد و آنان را به بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، وارد می کنم [که] پاداشی است از سوی خدا و خداست که پاداش نیکو نزد اوست.'),
(489, 3, 196, 'رفت و آمد کافران در شهرها [با وسایل و ابزار فراوان و شوکت ظاهری] تو را به طمع نیندازد.'),
(490, 3, 197, '[این] برخورداری اندک [و ناچیزی از زندگی زود گذر دنیا] است؛ سپس جایگاهشان دوزخ است، و آن بد آرامگاهی است.'),
(491, 3, 198, 'کسانی که از پروردگارشان پروا کردند [و فریفته زرق و برق زندگی مادی نشدند] برای آنان بهشت هایی است که از زیرِ [درختانِ] آن نهرها جاری است، در آنجا جاودانه اند. [این به عنوان آغاز] پذیرایی از سوی خداست، و آنچه [غیر از بهشت و نعمت هایش] نزد خداست، برای نیکوکاران بهتر است.'),
(492, 3, 199, 'بی تردید از اهل کتاب کسانی هستند که به خدا و آنچه به سوی شما نازل شده و آنچه به سوی خودشان فرود آمده ایمان می آورند، در حالی که در برابر خدا فروتن و خاکسار بوده، آیات خدا را به بهای اندک نمی فروشند. برای آنان نزد پروردگارشان پاداشی شایسته و مناسب است؛ یقیناً خدا حسابرسی سریع است.'),
(493, 3, 200, 'ای اهل ایمان! [در برابر حوادث] شکیبایی کنید، و دیگران را هم به شکیبایی وادارید، و با یکدیگر [چه در حال آسایش چه در بلا و گرفتاری] پیوند و ارتباط برقرار کنید و از خدا پروا نمایید تا رستگار شوید.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم! از [مخالفت با فرمان هایِ] پروردگارتان بپرهیزید، آنکه شما را از یک تن آفرید و جفتش را [نیز] از [جنس] او پدید آورد و از آن دو تن، مردان و زنان بسیاری را پراکنده و منتشر ساخت. و از خدایی که به نام او از یکدیگر درخواست می کنید، پروا کنید و از [قطع رابطه با] خویشاوندان بپرهیزید. یقیناً خدا همواره بر شما حافظ و نگهبان است.'),
(495, 4, 2, 'اموال یتیمان را [پس از رشدشان] به آنان بدهید، و [اموال] پست و بی ارزش [خود] را با [اموالِ] مرغوب و با ارزش [آنان] عوض نکنید؛ و اموالشان را با ضمیمه کردن به اموال خود نخورید؛ زیرا آن گناهی بزرگ است.'),
(496, 4, 3, 'واگر می ترسید که نتوانید در مورد دختران یتیم [در صورت ازدواج با آنان] عدالت ورزید، بنابراین از [دیگر] زنانی که شما را خوش آید دو دو و سه سه و چهار چهار به همسری بگیرید. پس اگر می ترسید که با آنان به عدالت رفتار نکنید، به یک زن یا به کنیزانی که مالک شده اید [اکتفا کنید]؛ این [اکتفای به یک همسر عقدی] به اینکه ستم نورزید و از راه عدالت منحرف نشوید، نزدیک تر است.'),
(497, 4, 4, 'ومهریه زنان را به عنوان هدیه ای الهی با میل و رغبت [و بدون چشم داشت و منّت گذاری] به خودشان بدهید؛ واگر چیزی از آن را با میل و رضایت خود به شما ببخشند، آن را حلال و گوارا بخورید.'),
(498, 4, 5, 'و اموالتان را که خدا مایه قوام و برپایی [زندگی] شما گردانیده به سبک مغزان ندهید، ولی آنان را از درآمد آن بخورانید و لباس بپوشانید و با آنان به صورتی شایسته و پسندیده سخن گویید.'),
(499, 4, 6, 'و یتیمان را [نسبت به امور زندگی] بیازمایید تا زمانی که به حدّ ازدواج برسند، پس اگر در آنان رشد لازم را یافتید اموالشان را به خودشان بدهید و آن را از [ترس] آنکه مبادا به سن رشد رسند [و از شما بگیرند] به اسراف وشتاب مخورید. و [از سرپرستان ایتام] آنکه توانگر است باید [از تصرّف در مال یتیم به عنوان حق الزحمه] خودداری کند؛ و هر که تهیدست است به اندازه متعارف مصرف نماید؛ وهنگامی که خواستید اموالشان را به خودشان بدهید [برای آنکه در آینده اختلاف و نزاعی پیش نیاید] بر آنان گواه بگیرید؛ و خدا برای محاسبه کافی است.'),
(500, 4, 7, 'برای مردان از آنچه پدر و مادر و نزدیکان [پس از مرگ خود] به جای می گذارند سهمی است؛ و برای زنان هم آنچه پدر و مادر و نزدیکان به جای می گذارند سهمی است، اندک باشد یا بسیار، سهمی است لازم وواجب.'),
(501, 4, 8, 'چون هنگام تقسیم کردن ارث، خویشاوندان و یتیمان و مستمندان حاضر شدند، چیزی از آن را به ایشان بدهید، و با آنان سخنی شایسته و پسندیده گویید.'),
(502, 4, 9, 'و کسانی که اگر فرزندانی ناتوان پس از خود به جای می گذارند، بر آنان [از ضایع شدن حقوقشان] بیم دارند، باید [از اینکه حقوق یتیمان دیگران را ضایع کنند] بترسند. پس لازم است [نسبت به شأن یتیمان] از خدا پروا کنند، و [درباره آنان] سخنی درست و استوار گویند.'),
(503, 4, 10, 'بی تردید کسانی که اموال یتیمان را به ستم می خورند، فقط در شکم های خود آتش می خورند، و به زودی در آتش فروزان در آیند.'),
(504, 4, 11, 'خدا شما را درباره [ارث] فرزندانتان سفارش می کند که سهم پسر مانند سهم دو دختر است. و اگر [میراث برانِ میّت] دخترند و بیش از دو دختر باشند، سهم آنان دو سوم میراث است؛ و اگر یک دختر باشد، نصف میراث سهم اوست. و برای هر یک از پدر و مادر میّت چنانچه میّت فرزندی داشته باشد، یک ششم میراث است؛ و اگر فرزندی نداشته باشد، و [تنها] پدر و مادرش ارث می برند، برای مادرش یک سوم است [و باقی مانده میراثْ سهم پدر اوست] و اگر میّت برادرانی داشته باشد، سهم مادرش یک ششم است. [همه این سهام] پس از وصیتی است که [نسبت به مال خود] می کند، یا [پس از] دَیْنی است [که باید از اصل مال پرداخت شود.] شما نمی دانید پدران و فرزندانتان کدام یک برای شما سودمندترند [بنابراین اختلاف سهم ها، شما را نگران نکند و موجب اعتراض نشود]. [این تقسیم بندی] فریضه ای از سوی خداست؛ زیرا خدا همواره دانا و حکیم است.'),
(505, 4, 12, 'نصف میراثی که همسرانتان به جای می گذارند، اگر فرزندی نداشته باشند برای شماست، و اگر فرزندی داشته باشند یک چهارم میراث حقّ شماست، پس از وصیتی که نسبت به مال خود می کنند یا [پس از] دَیْنی [که باید از اصل مال پرداخت شود]. و برای زنانتان چنانچه فرزندی نداشته باشید یک چهارم میراثی است که از شما به جای می ماند، و اگر فرزندی داشته باشید یک هشتم میراث حقّ آنان است، پس از وصیتی که کرده اید یا [پس از] دینی [که باید از اصل مال پرداخت شود]. و اگر مرد یا زنی که از او ارث می برند، کلاله [یعنی بی اولاد و بدون پدر و مادر] باشد و دارای برادر و خواهری است، پس برای هر یک از آن دو نفر یک ششم میراث است؛ و اگر بیش از این باشند در یک سوم میراث شریک اند، پس از وصیتی که [نسبت به مال] شده یا پس از دینی [که باید از اصل مال پرداخت شود]. [همه اینها در صورتی است که با وصیت و اقرار به دین] در وصیتش به وارثان زیان نزند. این سفارشی است از سوی خدا، و خدا دانا و بردبار است.'),
(506, 4, 13, 'اینها حدود خداست، و هر که از خدا و رسولش اطاعت کند، خدا او را به بهشت هایی که از زیر [درختان] آن نهرها جاری است درآورد؛ در آن جاودانه اند، و آن است کامیابی بزرگ.'),
(507, 4, 14, 'و هر که از خدا و رسولش نافرمانی کند و از حدود او تجاوز نماید، خدا او را در آتشی درآورد که در آن جاودانه است و برای او عذابی خوارکننده است.'),
(508, 4, 15, 'و از زنان شما [امتِ اسلام] کسانی که مرتکب زنا می شوند، چهار نفر مرد از خودتان را بر آنان به گواهی بخواهید؛ پس اگر [به ارتکاب آن] گواهی دادند، آن زشت کاران را در خانه ها [به عنوان حبس ابد] نگاه دارید تا مرگشان فرا رسد، یا خدا [برای نجات] آنان راهی قرار دهد.'),
(509, 4, 16, 'و از شما [مسلمانان] آن مرد و زنی که مرتکب آن [کار زشت] می شوند [در صورتی که بدون همسر باشند] آنان را [با اجرای حدود خدا] بیازارید؛ و اگر توبه کنند و [مفاسد خود را] اصلاح نمایند از آنان دست بردارید؛ زیرا خدا همواره بسیار توبه پذیر و مهربان است.'),
(510, 4, 17, 'بی تردید توبه نزد خدا فقط برای کسانی است که از روی نادانی مرتکب کار زشت می شوند، سپس به زودی توبه می کنند؛ اینانند که خدا توبه آنان را می پذیرد، و خدا همواره دانا و حکیم است.'),
(511, 4, 18, 'و برای کسانی که پیوسته کارهای زشت مرتکب می شوند، تا زمانی که مرگ یکی از آنان فرا رسد [و در آن لحظه که تمام فرصت ها از دست رفته] گوید: اکنون توبه کردم. و نیز برای آنان که در حال کفر از دنیا می روند، توبه نیست. اینانند که عذابی دردناک برای آنان آماده کرده ایم.'),
(512, 4, 19, 'ای اهل ایمان! برای شما ارث بردن از زنان در حالی که خوشایند شما نیستند [و فقط به طمع به دست آوردن اموالشان پس از مرگشان با آنان زندگی می کنید] حلال نیست. و آنان را در تنگنا و فشار مگذارید تا بخشی از آنچه را [به عنوان مهریه] به آنان داده اید پس بگیرید، مگر آنکه کار زشت آشکاری مرتکب شوند. و با آنان به صورتی شایسته و پسندیده رفتار کنید. و اگر [به علتی] از آنان نفرت داشتید [باز هم با آنان به صورتی شایسته و پسندیده رفتار کنید] چه بسا چیزی خوشایند شما نیست و خدا در آن خیر فراوانی قرار می دهد.'),
(513, 4, 20, 'و اگر خواستید همسری دیگر به جای همسر پیشین جایگزین کنید و به همسر پیشین مال فراوانی [به عنوان مهریه] پرداخته اید، چیزی از آن را پس مگیرید. آیا آن را با تکیه به تهمت و گناهی آشکار پس می گیرید؟!'),
(514, 4, 21, 'و چگونه آن را پس می گیرید در حالی که [با آمیزش جسمی، تماس روحی و معاشرت] به یکدیگر رسیده اید، و آنان از شما [به وسیله قرارداد ازدواج] پیمانی محکم گرفته اند.'),
(515, 4, 22, 'و با زنانی که پدرانتان با آنان ازدواج کرده اند ازدواج نکنید، مگر آنچه [پیش از اعلام این حکم] انجام گرفته باشد. یقیناً این عمل، عملی بسیار زشت و منفور و بد راهی است.'),
(516, 4, 23, '[ازدواج با این زنان] بر شما حرام شده است: مادرانتان، و دخترانتان، و خواهرانتان، و عمه هایتان، و خاله هایتان، و دختران برادر، و دختران خواهر، و مادرانی که شما را شیر داده اند، و خواهران رضاعی شما، و مادران همسرانتان، و دختران همسرانتان که در دامان شمایند [و تحت سرپرستی شما پرورش یافته اند، البته] از آن همسرانی که با آنان آمیزش داشته اید، و اگر آمیزش نداشته اید، بر شما گناهی نیست [که مادرشان را رها کرده با آنان ازدواج کنید]، و [نیز] همسران پسرانتان که از نسل شما هستند [بر شما حرام شده است]. و هم چنین جمع میان دو خواهر [در یک زمان ممنوع است]، جز آنچه [پیش از اعلام این حکم] انجام گرفته باشد؛ زیرا خدا همواره بسیار آمرزنده و مهربان است.'),
(517, 4, 24, 'و [ازدواج با] زنان شوهردار [بر شما حرام شده است] مگر زنانی که [به سبب جنگ با شوهران کافرشان از راه اسارت] مالک شده اید؛ [این احکام] مقرّر شده خدا بر شماست. و زنان دیگر غیر از این [زنانی که حرمت ازدواج با آنان بیان شد] برای شما حلال است، که آنان را با [هزینه کردن] اموالتان [به عنوان ازدواج] بخواهید در حالی که [قصد دارید با آن ازدواج] پاکدامن باشید نه زناکار. و از هر کدام از زنان بهره مند شدید، مهریه او را به عنوان واجب مالی بپردازید، و در آنچه پس از تعیین مهریه [نسبت به مدت عقد یا کم یا زیاد کردن مهریه] با یکدیگر توافق کردید بر شما گناهی نیست؛ یقیناً خدا همواره دانا و حکیم است.'),
(518, 4, 25, 'و از شما کسی که به سبب تنگدستی نتواند با زنان آزاد مؤمن ازدواج کند، با کنیزان جوان با ایمانتان [ازدواج کنید و اظهار ایمان از سوی کنیزان در جواز ازدواج با آنان کافی است]. و خدا به ایمان شما [که کدام ظاهری و کدام حقیقی و واقعی است] داناتر است. [آزاد و کنیز در اصل و نسب و در رابطه ایمانی] از یکدیگر، [و اعضای یک پیکرید]، پس با کنیزان با اجازه صاحبانشان ازدواج کنید، و مهریه آنان را به طور شایسته و پسندیده به خودشان بپردازید. [جواز این ازدواج مشروط به آن است که کنیزان] پاکدامن باشند نه زناکار و نه گیرندگان دوست پنهانی. پس هنگامی که به ازدواج درآمدند، اگر مرتکب کار زشت شوند، بر آنان نصف مجازاتی است که بر زنان آزاد است. این [جواز ازدواج با کنیز] برای کسی از شماست که از مشقت [غریزه جنسی و دچار شدن به زنا] بترسد، و شکیبایی کردن برای شما بهتر است؛ و خدا بسیار آمرزنده و مهربان است.'),
(519, 4, 26, 'خدا می خواهد [احکام و مقرّراتش را] برای شما بیان کند و شما را به روش های [پاک و صحیح] کسانی که پیش از شما بودند، راهنمایی نماید و رحمت و مغفرتش را بر شما فرو ریزد؛ و خدا دانا و حکیم است.'),
(520, 4, 27, 'و خدا می خواهد با رحمت و لطفش به شما توجه کند؛ و آنان که از شهوات پیروی می کنند می خواهند شما [در روابط جنسی از حدود و مقرّرات حق] به انحراف بزرگی دچار شوید.'),
(521, 4, 28, 'خدا می خواهد [با تشریع ازدواج با زنان مؤمن، و ازدواج با کنیزان مؤمن، و ازدواج موقت، بار مشکلات زندگی و مشقت های روابط نامشروع جنسی را] بر شما سبک کند؛ و انسان [در برابر مشکلات و شهوات جنسی] ناتوان آفریده شده است.'),
(522, 4, 29, 'ای اهل ایمان! اموال یکدیگر را در میان خود به باطل [و از راه حرام و نامشروع] مخورید، مگر آنکه تجارتی از روی خشنودی و رضایت میان خودتان انجام گرفته باشد. و خودکشی نکنید؛ زیرا خدا همواره به شما مهربان است.'),
(523, 4, 30, 'و هر که خوردن مال به باطل و قتل نفس را از روی تجاوز [از حدود خدا] و ستم [بر خود و دیگران] مرتکب شود، به زودی او را در آتشی [آزار دهنده و سوزان] درآوریم؛ واین کار بر خدا آسان است.'),
(524, 4, 31, 'اگر از گناهان بزرگی که از آنها نهی می شوید دوری کنید، گناهان کوچکتان را از شما محو می کنیم، و شما را به جایگاهی ارزشمند و نیکو وارد می کنیم.'),
(525, 4, 32, 'و نعمت ها و مال و ثروتی که خدا به سبب آن برخی از شما را بر برخی برتری داده آرزو مکنید [که آرزویش مایه حسد و فساد است. این تفاوت ها و برتری ها لازمه زندگی دنیا و بر اساس محاسبات حکیمانه است، اما در عین حال] برای مردان از آنچه کسب کرده اند بهره ای است، و برای زنان هم از آنچه کسب کرده اند بهره ای است. و [با کمک تقوا و عمل صالح] از بخشش خدا بخواهید، یقیناً خدا همواره به همه چیز داناست.'),
(526, 4, 33, 'و برای هر کسی از آنچه به جای گذاشته وارثانی قرار داده ایم، که از میراث پدر و مادر و خویشاوندان ارث ببرند و کسانی که با آنان پیمان [ازدواج یا پیمان های شرعی و عرفی] بسته اید؛ پس سهم ارث آنان را بدهید؛ یقیناً خدا بر همه چیز گواه است.'),
(527, 4, 34, 'مردان، کارگزاران و تدبیرکنندگانِ [زندگی] زنانند، به خاطر آنکه خدا مردان را [از جهت توان جسمی، تحمل مشکلات و قدرت روحی و فکری] بر زنان برتری داده، و به خاطر آنکه [مردان] از اموالشان هزینه زندگی زنان را [به عنوان حقّی واجب] می پردازند؛ پس زنان شایسته و درست کار [با رعایت قوانین حق] فرمانبردار [و مطیع شوهر] ند [و] در برابر حقوقی که خدا [به نفع آنان برعهده شوهرانشان] نهاده است، در غیاب شوهر [حقوق و اسرار و اموال او را] حفظ می کنند. و زنانی که از سرکشی و نافرمانی آنان بیم دارید [در مرحله اول] پندشان دهید، و [در مرحله بعد] در خوابگاه ها از آنان دوری کنید، و [اگر اثر نبخشید] آنان را [به گونه ای که احساس آزار به دنبال نداشته باشد] تنبیه کنید؛ پس اگر از شما اطاعت کردند برای آزار دادن آنان هیچ راهی مجویید؛ یقیناً خدا بلند مرتبه و بزرگ است.'),
(528, 4, 35, 'و [شما ای حاکمان شرع و عُقلا و بزرگان خانواده!] اگر از جدایی و ناسازگاری میان زن و شوهر بیم داشتید، پس داوری از خانواده مرد، و داوری از خانواده زن [برای رفع اختلاف] برانگیزید که اگر هر دو داور قصد اصلاح داشته باشند، خدا میان دو داور توافق و سازگاری ایجاد می کند [تا به صلاح زن و شوهر حکم کنند]؛ یقیناً خدا [به نیّات و اعمال شما] دانا و آگاه است.'),
(529, 4, 36, 'و خدا را بپرستید، و چیزی را شریک او قرار ندهید، و به پدر و مادر و خویشاوندان و یتیمان و مستمندان و همسایه نزدیک و همسایه دور و همنشینان و همراهان و در راه ماندگان و بردگان نیکی کنید؛ یقیناً خدا کسی را که متکبّر و خودستاست، دوست ندارد.'),
(530, 4, 37, 'کسانی که [از انفاق اموالشان در راه خدا] بخل می ورزند، و مردم را به بخل فرمان می دهند، و آنچه را خدا از فضل خود به آنان داده پنهان می کنند، [کافرند] و ما برای کافران عذابی خوارکننده آماده کرده ایم.'),
(531, 4, 38, 'و آنان که اموالشان را از روی ریا و خودنمایی به مردم انفاق می کنند، و به خدا و روز قیامت ایمان ندارند [شیطان همدم آنان است]. و هر کس شیطانْ همدم او باشد بی تردید بد همدمی است.'),
(532, 4, 39, 'و بر آنان چه زیانی بود اگر به خدا و روز قیامت ایمان می آوردند، و از آنچه خدا به آنان داده انفاق می کردند؟ و خدا همواره به آنان داناست.'),
(533, 4, 40, 'یقیناً خدا به اندازه وزن ذرّه ای [به اَحدی] ستم نمی کند، و اگر [هم وزن آن ذرّه] کار نیکی باشد، آن را دو چندان می کند، و از نزد خود پاداشی بزرگ می دهد.'),
(534, 4, 41, 'پس چگونه است [حال مردم] هنگامی که از هر امتی گواهی [که پیامبر آنان است بر اعمالشان] بیاوریم، و تو را بر آنان گواه آوریم؟!'),
(535, 4, 42, 'در آن روز کسانی که کفر ورزیده اند و از رسول نافرمانی کرده اند، آرزو می کنند که ای کاش با زمین یکسان می شدند. و از خدا هیچ سخنی را [که در دنیا درباره قرآن و پیامبر به باطل و ناروا می گفتند] نمی توانند بپوشانند.'),
(536, 4, 43, 'ای اهل ایمان! در حالی که مستید به نماز نزدیک نشوید تا زمانی که [مستیِ شما برطرف شود و از روی هوشیاری] بدانید [که در حال نماز] چه می گویید. و در حال جنابت هم به نماز نزدیک نشوید تا غسل کنید مگر در حال سفر [که آب نیابید، پس با تیمّم نماز بخوانید]. و اگر بیمارید، یا در سفرید، یا یکی از شما از قضای حاجت [دستشویی] آمده، یا با زنان آمیزش کرده اید و آبی [برای وضو یا غسل] نیافتید، به خاکی پاک، تیمم کنید و بخشی از صورت و دست هایتان را [با آن] مسح نمایید؛ یقیناً خدا همواره گذشت کننده و بسیار آمرزنده است.'),
(537, 4, 44, 'آیا به کسانی که بهره ای اندک از علم کتابِ [تورات و انجیل] به آنان داده شده ننگریستی [که با پنهان داشتن حقایق کتاب و تحریف آیاتش] گمراهی را می خرند، و [از روی حسادت و دشمنی] می خواهند شما هم از راه مستقیم گمراه شوید؟!'),
(538, 4, 45, 'و خدا به دشمنان شما داناتر است. و بس است که خدا سرپرست شما باشد، و کافی است که خدا یاور شما باشد.'),
(539, 4, 46, 'برخی از کسانی که یهودی اند، حقایق [کتاب آسمانی] را [با تفسیرهای نابجا و تحلیل های غلط و ناصواب] از جایگاه های اصلی و معانی حقیقی اش تغییر می دهند، و [به زبان ظاهر به پیامبر] می گویند: [دعوتت را] شنیدیم و [به باطن می گویند:] نافرمانی کردیم و [از روی توهین به پیامبر بر ضد او فریاد می زنند: سخنان ما را] بشنو که [ای کاش] ناشنوا شوی. و با پیچ و خم دادن زبان و آوازشان و به نیّت عیب جویی از دین [به آهنگی، کلمه] راعنا [را که در عربی به معنای «ما را رعایت کن» است، تلفظ می کنند که برای شنونده، راعنا که مفهومی خارج از ادب دارد، تداعی می شود]. و اگر آنان [به جای این همه اهانت از روی صدق و حقیقت] می گفتند: شنیدیم و اطاعت کردیم و [سخنان ما را] بشنو و ما را مهلت ده [تا معارف اسلام را درک کنیم] قطعاً برای آنان بهتر و درست تر بود، ولی خدا آنان را به سبب کفرشان لعنت کرده، پس جز عده اندکی ایمان نمی آورند.'),
(540, 4, 47, 'ای کسانی که به شما کتاب آسمانی داده شده! به آنچه [بر پیامبر اسلام] نازل کردیم که تصدیق کننده تورات و انجیلی است که با شماست ایمان آورید، پیش از آنکه چهره هایی را [از شکل و شخصیت انسانی] محو کنیم، و [به کیفر تکبّر در برابر پیامبر اسلام و قرآن] به کفر و گمراهی بازگردانیم، یا چنان که اصحاب سبت را لعنت کردیم، لعنت کنیم؛ و فرمان خدا همواره شدنی است.'),
(541, 4, 48, 'مسلماً خدا اینکه به او شرک ورزیده شود نمی آمرزد، و غیر آن را برای هر کس که بخواهد می آمرزد. و هر که به خدا شرک بیاورد، مسلماً گناه بزرگی را مرتکب شده است.'),
(542, 4, 49, 'آیا به کسانی که خود را به پاکی می ستایند، ننگریستی؟ [این خودستایی هیچ ارزشی و اعتباری ندارد] بلکه خداست که هر که را بخواهد [بر اساس ملاک های تعیین شده از سوی خود] می ستاید، و [گروهی که به ناحق، خود را می ستایند در کیفر و مجازات] به اندازه رشته میان هسته خرما مورد ستم قرار نمی گیرند.'),
(543, 4, 50, 'با تأمل بنگر چگونه [قوم یهود با ادعای پاکی خویش در پیشگاه حق] بر خدا دروغ می بندند، و همین گناه آشکار [برای خواری و عذاب آنان] کافی است.'),
(544, 4, 51, 'آیا به کسانی که بهره ای اندک از [دانش] کتاب [تورات و انجیل] به آنان داده شده ننگریستی که به هر معبودی غیر خدا، و هر طغیان گری ایمان می آورند؟! و درباره کسانی که کافر شده اند، می گویند: آنان از کسانی که ایمان [به قرآن و پیامبر] آورده اند، راه یافته ترند.'),
(545, 4, 52, 'اینانند که خدا لعنتشان کرده و هر که را خدا لعنت کند، هرگز برای او یاوری نخواهی یافت.'),
(546, 4, 53, 'آیا آنان را بهره ای از حکومت است [که بخواهند چیزی به مردم دهند و کارساز دنیا و آخرتشان باشند، اگر هم بهره ای باشد] در این صورت به اندازه گودی پشت هسته خرما به مردم نمی پردازند.'),
(547, 4, 54, 'بلکه آنان به مردم [که در حقیقتْ پیامبر و اهل بیت اویند] به خاطر آنچه خدا از فضلش به آنان عطا کرده، حسد می ورزند. تحقیقاً ما به خاندان ابراهیم کتاب و حکمت دادیم، و به آنان فرمانروایی بزرگی بخشیدیم.'),
(548, 4, 55, 'پس برخی از آنان [که اهل کتاب اند] به او [که پیامبر اسلام و والاترین فرد از خاندان ابراهیم است] ایمان آوردند، و گروهی از او روی گردانیده اند، و دوزخ که آتشی سوزان و برافروخته است، برای آنان کافی است.'),
(549, 4, 56, 'یقیناً کسانی که به آیات ما کافر شدند به زودی آنان را به آتشی [شکنجه آور و سوزان] درآوریم، هرگاه پوستشان بریان شود، پوست های دیگری جایگزین آن می کنیم تا عذاب را بچشند؛ یقیناً خدا توانای شکست ناپذیر و حکیم است.'),
(550, 4, 57, 'و کسانی که ایمان آورده اند و کارهای شایسته انجام داده اند، به زودی آنان را در بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، وارد کنیم، در آنجا جاودانه اند؛ آنان را در آنجا همسرانی پاکیزه است، و آنان را در زیر سایه ای پایدار [و دلپذیر، آرام بخش و خنک] درآوریم.'),
(551, 4, 58, 'خدا قاطعانه به شما فرمان می دهد که امانت ها را به صاحبانش بازگردانید و هنگامی که میان مردم داوری می کنید، به عدالت داوری کنید. یقیناً [فرمان بازگردانیدن امانت و عدالت در داوری] نیکو چیزی است که خدا شما را به آن موعظه می کند؛ بی تردید خدا همواره شنوا و بیناست.'),
(552, 4, 59, 'ای اهل ایمان! از خدا اطاعت کنید و [نیز] از پیامبر و صاحبان امر خودتان [که امامان از اهل بیت اند و چون پیامبر دارای مقام عصمت می باشند] اطاعت کنید. و اگر درباره چیزی [از احکام و امور مادی و معنوی و حکومت و جانشینی پس از پیامبر] نزاع داشتید، آن را [برای فیصله یافتنش] اگر به خدا و روز قیامت ایمان دارید، به خدا و پیامبر ارجاع دهید؛ این [ارجاع دادن] برای شما بهتر واز نظر عاقبت نیکوتر است.'),
(553, 4, 60, 'آیا به کسانی که گمان می کنند به آنچه بر تو و پیش از تو نازل شده ایمان آورده اند، ننگریستی، که می خواهند [در موارد نزاع و اختلافشان] داوری و محاکمه نزد طاغوت برند؟ [همان حاکمان ستمکار و قاضیان رشوه خواری که جز به باطل حکم نمی رانند،] در حالی که فرمان یافته اند به طاغوت کفر ورزند، و شیطان [با سوق دادنشان به محاکم طاغوت] می خواهد آنان را به گمراهی دوری [که هرگز به رحمت خدا دست نیابند] دچار کند.'),
(554, 4, 61, 'چون به آنان گویند: [برای داوری و محاکمه] به سوی آنچه خدا نازل کرده، و به سوی پیامبر آیید، منافقان را می بینی که از تو به شدت روی می گردانند!'),
(555, 4, 62, 'پس [وضع و حالشان] چگونه خواهد بود هنگامی که به سبب آنچه مرتکب شده اند، حادثه ناگواری به آنان برسد، آن گاه در حالی که به خدا سوگند می خورند، نزد تو می آیند که ما [از داوری بردن نزد طاغوت] نیّتی جز نیکی و برقراری صلح و سازش [میان طرفین نزاع] نداشتیم.'),
(556, 4, 63, 'اینان کسانی هستند که خدا آنچه را که [از نیّات شوم، کینه و نفاق] در دل های آنان است می داند؛ بنابراین از آنان روی برتاب، و پندشان ده، و به آنان سخنی رسا که در دلشان اثر کند بگوی.'),
(557, 4, 64, 'و هیچ پیامبری را نفرستادیم مگر آنکه به توفیق خدا از او اطاعت شود. و اگر آنان هنگامی که [با ارتکاب گناه] به خود ستم کردند، نزد تو می آمدند و از خدا آمرزش می خواستند، و پیامبر هم برای آنان طلب آمرزش می کرد، یقیناً خدا را بسیار توبه پذیر و مهربان می یافتند.'),
(558, 4, 65, 'به پروردگارت سوگند که آنان مؤمن حقیقی نخواهند بود، مگر آنکه تو را در آنچه میان خود نزاع واختلاف دارند به داوری بپذیرند؛ سپس از حکمی که کرده ای دروجودشان هیچ دل تنگی و ناخشنودی احساس نکنند، وبه طور کامل تسلیم شوند.'),
(559, 4, 66, 'و اگر بر آنان مقرّر می کردیم که خودکشی کنید، یا از دیار و کاشانه خود بیرون روید، جز اندکی از آنان انجام نمی دادند. و اگر آنچه را که به آن پند داده می شوند عمل می کردند، مسلماً برای آنان بهتر و در [جهت تثبیت ایمان و] استواری قدم، مؤثرتر و قوی تر بود.'),
(560, 4, 67, 'و ما نیز در آن صورت آنان را به طور یقین پاداشی بزرگ می دادیم.'),
(561, 4, 68, 'و بی تردید آنان را به راهی راست راهنمایی می کردیم.'),
(562, 4, 69, 'و کسانی که از خدا و پیامبر اطاعت کنند، در زمره کسانی از پیامبران و صدّیقان و شهیدان و شایستگان خواهند بود که خدا به آنان نعمت [ایمان، اخلاق و عمل صالح] داده؛ و اینان نیکو رفیقانی هستند.'),
(563, 4, 70, 'این [رفاقت با آنان] بخشش و فضلی از سوی خداست، و [در استحقاق این کرامت و فضل] کافی است که خدا [به نیّات و اعمال مطیعان] داناست.'),
(564, 4, 71, 'ای اهل ایمان! [در برابر دشمنْ مهیا و آماده باشید و] سلاح ها و ساز و برگ جنگی خود را برگیرید، پس گروه گروه یا دسته جمعی [به سوی جنگ با دشمن] کوچ کنید.'),
(565, 4, 72, 'یقیناً از شما کسی است که [از رفتن به سوی جنگ] درنگ و کُندی می کند، پس اگر به شما [رزمندگانی که به جنگ رفته اید] آسیبی رسد، گوید: خدا بر من محبت و لطف داشت که با آنان [در میدان جنگ] حاضر نبودم.'),
(566, 4, 73, 'و اگر بخشش و فضلی از سوی خدا به شما رسد چنان که گویی میان شما و او هرگز دوستی و مودّتی نبوده، [و از جنگ شما با دشمن آگاهی نداشته، و برای اینکه از پیروزی و غنیمت سهمی نبرده، آه سردی می کشد و] می گوید: ای کاش من هم با آنان بودم، پس به کامیابی بزرگی دست می یافتم.'),
(567, 4, 74, 'باید کسانی که زندگیِ زودگذرِ دنیا را با سرای جاویدان آخرت مبادله می کنند، در راه خدا بجنگند. و هر که در راه خدا بجنگد و کشته شود، یا بر دشمن پیروز گردد، پس پاداش بزرگی به او خواهیم داد.'),
(568, 4, 75, 'شما را چه شده که در راه خدا و [رهایی] مردان و زنان و کودکان مستضعف [ی که ستمکاران هر گونه راه چاره را بر آنان بسته اند] نمی جنگید؟ آن مستضعفانی که همواره می گویند: پروردگارا! ما را از این شهری که اهلش ستمکارند، بیرون ببر و از سوی خود، سرپرستی برای ما بگمار و از جانب خود برای ما یاوری قرار ده.'),
(569, 4, 76, 'آنان که ایمان آورده اند، در راه خدا می جنگند؛ و کسانی که کافر شده اند، در راه طاغوت می جنگند. پس شما با یاران شیطان بجنگید، که یقیناً نیرنگ و توطئه شیطان [در برابر اراده خدا و پایداری شما] سست و بی پایه است.'),
(570, 4, 77, 'آیا ندیدی کسانی را که [پیش از اعلام حکم جهاد] به آنان گفته شد: [چون زمینه جنگ فراهم نیست] دست از جنگ بازدارید و [در این شرایط] نماز را بر پا دارید، و زکات بپردازید. ولی هنگامی که جنگ بر آنان لازم و مقرّر شد، ناگاه گروهی از آنان مانند ترس از [عقوبت] خدا یا ترسی سخت تر از آن از مردمِ [مُشرک] ترسیدند و گفتند: پروردگارا! چرا جنگ را بر ما لازم و مقرّر کردی؟ و چرا ما را تا زمانی نزدیک [که زمان مرگ طبیعی است] مهلت ندادی؟ بگو: متاع دنیا اندک، و آخرت برای آنان که تقوا ورزیده اند بهتر است؛ و به اندازه رشته میان هسته خرما مورد ستم قرار نمی گیرند.'),
(571, 4, 78, 'هر کجا باشید هر چند در قلعه های مرتفع و استوار، مرگ شما را درمی یابد. و اگر خیری [چون پیروزی و غنیمت] به آنان [که سست ایمان و منافق اند] برسد، می گویند: این از سوی خداست. و اگر سختی وحادثه ای [چون بیماری، تنگدستی، شکست وناکامی] به آنان رسد [به پیامبر اسلام] می گویند: از ناحیه توست. بگو: همه اینها از سوی خداست. این گروه را چه شده که نمی خواهند [معارف الهیّه وحقایق را] بفهمند؟!'),
(572, 4, 79, '[ای انسان!] آنچه از نیکی به تو رسد، از سوی خداست و آنچه از بدی به تو رسد، از سوی خود توست. و [ای پیامبر! از نیکی هایی که از سوی ماست اینکه] تو را برای مردم به پیامبری فرستادیم، و گواه بودن خدا [بر پیامبریِ تو] کافی است.'),
(573, 4, 80, 'هر که از پیامبر اطاعت کند، در حقیقت از خدا اطاعت کرده و هر که روی برتابد [حسابش با ماست.] ما تو را بر آنان نگهبان [اعمالشان که به طور اجبار از فسق و فجور حفظشان کنی] نفرستادیم.'),
(574, 4, 81, 'و [آنان در محضر تو] می گویند: ما مطیع و فرمانبرداریم، ولی هنگامی که از نزد تو بیرون می روند، گروهی از آنان در شب نشینی های مخفیانه خود بر خلاف آنچه تو می گویی، [و غیر آنچه در حضورت می گفتند] تدبیر می کنند. خدا آنچه را که در شب نشینی های مخفیانه تدبیر می کنند [در پرونده اعمالشان] می نویسد. بنابراین از آنان روی برتاب، و بر خدا توکل کن؛ و کارساز بودنِ خدا [نسبت به امور بندگان] کافی است.'),
(575, 4, 82, 'آیا به قرآن [عمیقاً] نمی اندیشند؟ چنانچه از سوی غیر خدا بود، همانا در آن اختلاف و ناهمگونی بسیاری می یافتند.'),
(576, 4, 83, 'و هنگامی که خبری از ایمنی و ترس [چون پیروزی و شکست] به آنان [که مردمی سست ایمان اند] رسد، [بدون بررسی در درستی و نادرستی اش] آن را منتشر می کنند، و [در صورتی که] اگر آن خبر را به پیامبر و اولیای امورشان [که به سبب بینش و بصیرتْ دارای قدرت تشخیص و اهل تحقیق اند] ارجاع می دادند، درستی و نادرستی اش را در می یافتند؛ [چنانچه به مصلحت جامعه بود، انتشارش را اجازه می دادند و اگر نبود، از انتشارش منع می کردند] و اگر فضل و رحمت خدا بر شما نبود، یقیناً همه شما جز اندکی، از شیطان پیروی می کردید.'),
(577, 4, 84, 'پس در راه خدا به جنگ برخیز. تو فقط به [وظایف و اعمال] خودت مکلّف می باشی، و مؤمنان را [هم به جنگ با دشمن برانگیز و] تشویق کن؛ امید است خدا آسیب و گزند کافران را [از تو و مؤمنان] بازدارد؛ و خداست که صولت وقدرتش شدیدتر، وعذاب وکیفرش سخت تر است.'),
(578, 4, 85, 'هر کس وساطت پسندیده ای کند، [تا کار خیری چون جهاد، احسان به مردم و حلّ مشکلات جامعه انجام گیرد] بهره ای از آن برای او خواهد بود، و هر کس وساطت ناپسندی کند [تا شرّی و باطلی به وجود آید و زیانی به مردم رسد] سهمی از [گناه و وبال] آن برای او خواهد بود؛ و خدا همواره نگهبان بر هر چیزی است.'),
(579, 4, 86, 'و هنگامی که به شما درود گویند، شما درودی نیکوتر از آن، یا همانندش را پاسخ دهید؛ یقیناً خدا همواره بر همه چیز حسابرس است.'),
(580, 4, 87, 'خداست که هیچ معبودی جز او نیست. مسلماً شما را [از گورهایتان] به سوی [دادگاهِ حسابرسی] روز قیامت که هیچ شکی در آن نیست گرد می آورد؛ و راستگوتر از خدا در گفتار کیست؟'),
(581, 4, 88, 'شما را چه شده که درباره منافقین دو گروه شده اید؟ [گروهی شفیع و طرفدار، و گروهی مخالف و دشمن آنان] در صورتی که خدا آنان را به کیفر اعمال ناپسندی که مرتکب شده اند [در چاه کفر و گمراهی] نگونسار کرده است. آیا می خواهید کسانی را که خدا [به خاطر اعمال ناپسندشان] گمراه کرده هدایت کنید؟ [ای پیامبر!] در حالی که خدا هر که را گمراه کند هرگز برای او راهی [به سوی هدایت] نخواهی یافت.'),
(582, 4, 89, 'آنان دوست دارند همان گونه که خود کافر شده اند، شما هم کافر شوید، تا [در کفر و ضلالت] با هم برابر و یکسان باشید. بنابراین از آنان دوستانی انتخاب نکنید تا آنکه در راه خدا [برای پذیرش دین] هجرت کنند؛ پس اگر [از هجرت] روی برتافتند [و به خیانت و جنایتشان بر ضد شما ادامه دادند] آنان را هر کجایافتید بگیرید و بکشید، و از آنان یار و یاوری نگیرید.'),
(583, 4, 90, 'مگر آنان که به گروهی که میان شما و ایشان پیمانی [چون پیمان متارکه جنگ یا پیمان های دیگر] است بپیوندند، یا در حالی که سینه هایشان از جنگیدن با شما یا قوم خودشان به تنگ آمده باشد به نزد شما آیند. و اگر خدا می خواست قطعاً آنان را بر شما مسلط می کرد، در آن صورت به طور یقین با شما می جنگیدند. پس اگر از شما کناره گرفتند، و با شما به جنگ برنخاستند، و پیشنهاد صلح و آشتی دادند، [به آنان تعدّی نکنید که] خدا در این صورت برای تجاوز به آنان راهی برای شما قرار نداده است.'),
(584, 4, 91, 'به زودی گروهی دیگر را می یابید که می خواهند [با پیمان متارکه جنگ] از ناحیه شما و قوم [مُشرکِ] خود ایمن و آسوده باشند، [ولی اینان به خاطر خباثت باطنشان نسبت به پیمان هایشان مورد اطمینان نیستند، به همین سبب] هر بار به فتنه [و جنگ با مسلمانان] دعوت شدند، با سر در آن فرو می افتند. پس اگر [از جنگ با شما] کناره نگرفتند، و پیشنهاد صلح و آشتی نکردند، و بر ضد شما دست [از فتنه و آشوب] برنداشتند، آنان را هر جا یافتید بگیرید و بکشید؛ آنانند که ما برای شما نسبت به [گرفتن و کشتن] آنان دلیلی روشن و آشکار قرار دادیم.'),
(585, 4, 92, 'هیچ مؤمنی را نسزد که مؤمنی را بکشد، مگر آنکه این عمل از روی خطا و اشتباه اتفاق افتد. و کسی که مؤمنی را از روی خطا و اشتباه بکشد، باید یک برده مؤمن آزاد کند، و خون بهایی به وارثان مقتول پرداخت نماید؛ مگر آنکه آنان خون بها را ببخشند. و اگر مقتول از گروهی باشد که دشمن شمایند و خود او مؤمن است، فقط آزاد کردن یک برده مؤمن بر عهده قاتل است. و اگر مقتول مؤمن، از گروهی باشد که میان شما و آنان پیمانی برقرار است، باید خون بهایش را به وارثان او پرداخت کند، و نیز یک برده مؤمن آزاد نماید. و کسی که برده نیافت، باید دو ماه پیاپی روزه بگیرد. [این حکم] به سبب [پذیرش] توبه [قاتل] از سوی خداست؛ و خدا همواره دانا و حکیم است.'),
(586, 4, 93, 'و هر کس مؤمنی را از روی عمد بکشد، کیفرش دوزخ است که در آن جاودانه خواهد بود، و خدا بر او خشم گیرد، و وی را لعنت کند و عذابی بزرگ برایش آماده سازد.'),
(587, 4, 94, 'ای اهل ایمان! هنگامی که در راه خدا [برای نبرد با دشمن] سفر می کنید [در مسیر سفر نسبت به شناخت مؤمن از کافر] تحقیق و تفحّص کامل کنید. و به کسی که نزد شما اظهار اسلام می کند، نگویید: مؤمن نیستی. [تا] برای به دست آوردن کالای بی ارزش و ناپایدار زندگی دنیا [او را بکشید، اگر خواهان غنیمت هستید] پس [برای شما] نزد خدا غنایم فراوانی است. شما هم پیش از این ایمانتان ایمانی ظاهری بود؛ خدا بر شما منّت نهاد [تا به ایمان استوار و محکم رسیدید]. پس باید تحقیق و تفحّص کامل کنید [تا به دست شما کاری خلاف اوامر خدا انجام نگیرد] یقیناً خدا همواره به آنچه انجام می دهید، آگاه است.'),
(588, 4, 95, 'آن گروه از مؤمنانی که بدون بیماری جسمی [و نقص مالی، و عذر دیگر، از رفتن به جهاد خودداری کردند و] در خانه نشستند، با مجاهدانی که در راه خدا با اموال و جان هایشان به جهاد برخاستند، یکسان نیستند. خدا کسانی را که با اموال و جان هایشان جهاد می کنند به مقام و مرتبه ای بزرگ بر خانه نشینان برتری بخشیده است. و هر یک [از این دو گروه] را [به خاطر ایمان و عمل صالحشان] وعده پاداش نیک داده، و جهادکنندگان را بر خانه نشینانِ [بی عُذر] به پاداشی بزرگ برتری داده است.'),
(589, 4, 96, 'به درجات و رتبه هایی، و آمرزش و رحمتی ویژه از سوی خود؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(590, 4, 97, 'قطعاً کسانی که [با ترک هجرت از دیار کفر، و ماندن زیر سلطه کافران و مشرکان] بر خویش ستم کردند [هنگامی که] فرشتگانْ آنان را قبض روح می کنند، به آنان می گویند: [از نظر دین داری و زندگی] در چه حالی بودید؟ می گویند: ما در زمین، مستضعف بودیم. فرشتگان می گویند: آیا زمین خدا وسیع و پهناور نبود تا در آن [از محیط شرک به دیار ایمان] مهاجرت کنید؟! پس جایگاهشان دوزخ است و آن بد بازگشت گاهی است.'),
(591, 4, 98, 'مگر مردان و زنان و کودکان مستضعفی که [برای نجات خود از محیط کفر و شرک] هیچ چاره ای ندارند، و راهی [برای هجرت] نمی یابند.'),
(592, 4, 99, 'پس اینانند که امید است خدا از آنان درگذرد؛ و خدا همواره گذشت کننده و بسیار آمرزنده است.'),
(593, 4, 100, 'و هر کس در راه خدا هجرت کند، اقامت گاه های فراوان و فراخیِ معیشت خواهد یافت. و کسی که از خانه خود به قصد مهاجرت به سوی خدا و پیامبرش بیرون رود، سپس مرگ او را دریابد، مسلماً پاداشش بر خداست؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(594, 4, 101, 'و هنگامی که در روی زمین سفر می کنید، اگر بترسید که کافران به شما آسیب و آزار رسانند، بر شما گناهی نیست که از نمازها [یِ چهار رکعتی خود، دو رکعت] بکاهید؛ زیرا کافران همواره برای شما دشمنی آشکارند.'),
(595, 4, 102, 'و هنگامی که در [عرصه نبرد و خطر] میان آنان باشی، و برای آنان [به جماعت] اقامه نماز کنی، پس باید گروهی از رزمندگان در حالی که لازم است سلاحشان را برگیرند، همراهت به نماز ایستند، و چون سجده کردند [و رکعت دوم را بدون اتصال به جماعت به پایان بردند] باید [برای حفاظت از شما] پشت سرتان قرار گیرند. و آن گروه دیگر که [به خاطر مشغول بودن به حفاظت] نماز نخوانده اند بیایند و با تو نماز گزارند. و آنان باید [در حال نماز] از هوشیاری و احتیاط [نسبت به دشمن] غافل نباشند، و سلاحشان را با خود برگیرند؛ چون کافران دوست دارند شما از سلاح ها و ساز و برگ جنگی خود غفلت ورزید، تا یک باره به شما هجوم کنند. و اگر از باران در زحمت و مشقت هستید، یا بیمارید، بر شما گناهی نیست که سلاحتان بر زمین گذارید، ولی باید هوشیاری و احتیاط خود را حفظ کنید؛ یقیناً خدا برای کافران عذاب خوارکننده ای آماده کرده است.'),
(596, 4, 103, 'پس هنگامی که نماز را [در میدان جنگ] به پایان بردید، خدا را در حال ایستاده و نشسته و خوابیده یاد کنید. و همین که [از فتنه و آشوب دشمن] مطمئن شدید، نماز را [به صورت معمولش] اقامه کنید. مسلماً نماز همواره در اوقاتی مشخص و معین وظیفه ای مقرّر و لازم بر مؤمنان است.'),
(597, 4, 104, 'و در تعقیب و جستجوی دشمن سستی نکنید. اگر شما [در رویارویی با دشمن] درد و رنج می بینید، آنان نیز چون شما درد و رنج می بینند، و شما چیزی را [چون پیروزی و پاداش] از خدا امید دارید که آنان امید ندارند؛ و خدا همواره دانا و حکیم است.'),
(598, 4, 105, 'یقیناً این کتاب را به درستی و راستی بر تو نازل کردیم؛ تا میان مردم به آنچه خدا به تو آموخته داوری کنی، و حمایت گر خائنان مباش.'),
(599, 4, 106, 'و از خدا آمرزش بخواه؛ زیرا خدا همواره بسیار آمرزنده و مهربان است.'),
(600, 4, 107, 'و از کسانی که به خود خیانت می کنند دفاع مکن؛ زیرا خدا هر کس را که خیانت پیشه و گناه کار است دوست ندارد.'),
(601, 4, 108, '[خیانت پیشگانْ خیانت خود را] از مردم پنهان می دارند، و [نمی توانند] از خدا پنهان نمایند، و خدا با آنان بود آن گاه که در جلسه مخفی شبانه، درباره طرح و نقشه و سخنی که خدا به آن رضایت نداشت، چاره اندیشی می کردند و خدا همواره به آنچه انجام می دهند، احاطه دارد.'),
(602, 4, 109, 'این شما [قبیله و عشیره خائنین] هستید که در زندگی دنیا از آنان دفاع کردید، ولی چه کسی است که در روز قیامت در برابر خدا از آنان دفاع کند؟ یا چه کسی است که [برای رهایی آنان از عذاب] کارسازشان باشد؟'),
(603, 4, 110, 'و هر کس کار زشتی کند یا برخود ستم ورزد، سپس از خدا آمرزش بخواهد، خدا را بسیار آمرزنده و مهربان خواهد یافت.'),
(604, 4, 111, 'و هر کس مرتکب گناهی شود، فقط به زیان خود مرتکب می شود؛ و خدا همواره دانا و حکیم است.'),
(605, 4, 112, 'و هر کس مرتکب خطا یا گناهی شود، سپس آن را به بی گناهی نسبت دهد، بی تردید بهتان و گناهی آشکار بر دوش گرفته است.'),
(606, 4, 113, 'و اگر فضل و رحمت خدا بر تو نبود، گروهی از عشیره و قبیله خائنان تصمیم داشتند تو را [از داوری عادلانه] منحرف کنند، [این سبک مغزانِ خیالباف] جز خودشان را منحرف نمی کنند، و هیچ زیانی به تو نمی رسانند. و خدا کتاب و حکمت را بر تو نازل کرد، و آنچه را نمی دانستی به تو آموخت؛ و همواره فضل خدا بر تو بزرگ است.'),
(607, 4, 114, 'در بسیاری از رازگویی های آنان خیری نیست، جز کسی که [از این طریق] به صدقه، یا کار نیک، یا اصلاح در میان مردم فرمان دهد؛ و هر که برای طلب خشنودی خدا چنین کند، پس پاداش بزرگی به او خواهیم داد.'),
(608, 4, 115, 'و هر کس بعد از آنکه [راه] هدایت برایش روشن و آشکار شد، با پیامبر مخالفت و دشمنی کند و از راهی جز راه مؤمنان پیروی نماید، او را به همان سو که رو کرده واگذاریم، و به دوزخ درآوریم؛ و آن بد بازگشت گاهی است.'),
(609, 4, 116, 'مسلماً خدا این را که به او شرک آورده شود نمی آمرزد، و فروتر از آن را برای هر که بخواهد می آمرزد. و هر که به خدا شرک ورزد، یقیناً به گمراهی بسیار دوری دچار شده است.'),
(610, 4, 117, 'مشرکان به جای خدا جز جماداتی را [که هیچ اثری ندارند، و همیشه محکوم تأثیر عوامل دیگرند] نمی پرستند، و در حقیقت جز شیطانِ سرکشِ گمراه را اطاعت نمی کنند.'),
(611, 4, 118, 'خدا شیطان را از رحمتش دور کرد؛ و [او] گفت: سوگند می خورم که از میان بندگانت سهمی مُعیّن [برای پیروی از فرهنگ ویرانگرم] برخواهم گرفت.'),
(612, 4, 119, 'و یقیناً آنان را گمراه می کنم، و دچار آرزوهای دور و دراز [و واهی و پوچ] می سازم، و آنان را وادار می کنم که گوش های چهارپایان را [به نشانه حرام بودن بهره گیری از آنان] بشکافند، و فرمانشان می دهم که آفرینش خدایی را تغییر دهند [به این صورت که جنسیت مرد را به زن و زن را به مرد برگردانند، و فطرت پاکشان را به شرک بیالایند، و زیبایی های طبیعی، روحی، جسمی خود را به زشتی ها تبدیل کنند]. و هر کس شیطان را به جای خدا سرپرست ویار خود گیرد، مسلماً به زیان آشکاری دچار شده است.'),
(613, 4, 120, 'شیطان به آنان وعده [دروغ] می دهد، و در آرزوها [ی سرابوار] می اندازد، و جز وعده فریبنده به آنان نمی دهد.'),
(614, 4, 121, 'اینان [که پیروان شیطان اند] جایگاهشان دوزخ است، و از آن هیچ راه فراری نیابند.'),
(615, 4, 122, 'و کسانی که ایمان آورده اند و کارهای شایسته انجام داده اند، به زودی آنان را در بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، درآوریم، در آن جاودانه اند. وعده خدا حق است، و راستگوتر از خدا در گفتار کیست؟'),
(616, 4, 123, '[کیفر و پاداش] نه بر وفق آرزوهایِ [واهیِ] برخی از شما [مسلمانان] است، و نه بر وفق آرزوهایِ [پوچِ] اهل کتاب. هر کس مرتکب کار زشتی شود، به آن کیفر داده می شود؛ و جز خدا سرپرست و یاوری برای خود نخواهد یافت.'),
(617, 4, 124, 'و کسانی از مردان یا زنان که بخشی از کارهای شایسته را انجام دهند، در حالی که مؤمن باشند، پس اینان وارد بهشت می شوند، و به اندازه گودی پشت هسته خرما مورد ستم قرار نمی گیرند.'),
(618, 4, 125, 'و دین چه کسی بهتر است از آنکه همه وجودش را تسلیم خدا کرده و نیکوکار است، و از آیین ابراهیم یکتاپرست حق گرا پیروی کرده است؟ وخدا ابراهیم را [برای حق گرایی، خلوص، پاکی اخلاق واعمالش] دوست خود گرفت.'),
(619, 4, 126, 'و آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست؛ و خدا همواره بر هر چیزی احاطه دارد.'),
(620, 4, 127, 'درباره [حقوق ازدواج و نیز ارث] زنان از تو فتوا می خواهند. بگو: فقط خدا درباره آنان [مطابق با آیاتی که در اوایل همین سوره گذشت] به شما فتوا می دهد، و درباره دختران یتیمی که حقوق لازم و مقرّر آنان را نمی پردازید، و [به قصد خوردن اموالشان] میلی به ازدواج با آنان دارید، برابر احکامی که در این کتاب بر شما خوانده می شود به شما فتوا می دهد. و [نیز درباره] کودکان مستضعف [ی که حقوق مالی و انسانی آنان را پایمال می کنید، مطابق احکامی که در قرآن بر شما تلاوت می شود به شما فتوا می دهد]. و [نیز سفارش می کند] که با یتیمان به عدالت رفتار کنید؛ و آنچه را [درباره زنان، دختران یتیم و کودکان مستضعف] از هر خیری انجام می دهید، یقیناً خدا همواره به آن داناست.'),
(621, 4, 128, 'و اگر زنی از ناسازگاری شوهرش، یا روی گردانی اش [از حقوق همسرداری] بترسد، بر آن دو گناهی نیست که با یکدیگر به طور شایسته و پسندیده آشتی کنند، [گرچه به چشم پوشی بخشی از حقوقشان باشد.] و [در حقیقت] صلح و آشتی بهتر است. و بخل، نزد نفوس [آدمیان] حاضر است [به این خاطر هر یک از دو زوج در عفو و گذشت و سازش و ادای حقوق بخل می ورزند]. و اگر [شما شوهران] نیکی کنید و [از سرکشی و ناسازگاری نسبت به زنان] بپرهیزید [از ثواب و پاداش حق بهره مند می شوید]؛ یقیناً خدا همواره به آنچه انجام می دهید، آگاه است.'),
(622, 4, 129, 'و شما هر چند حریص [بر عدالت] باشید، هرگز نمی توانید [از نظر علاقه و میل قلبی] میان زن ها [یی که در عقد خود دارید] عدالت ورزید، پس تمایل خود را به طور کامل متوجه یک طرف ننمایید تا دیگری را به صورت زنی سرگردان و بلاتکلیف رها کنید. و اگر توافق و سازش نمایید و تقوا را [در محیط خانواده] رعایت کنید [مورد آمرزش قرار می گیرید]؛ زیرا خدا همواره بسیار آمرزنده و مهربان است.'),
(623, 4, 130, 'و اگر [زن و شوهر راهی برای صلح و سازش نیافتند و] از یکدیگر جدا شوند، خدا هر یک را از توانگری خود بی نیاز می کند؛ و خدا همواره بسیار عطا کننده و حکیم است.'),
(624, 4, 131, 'و آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست. و البته ما کسانی را که پیش از شما کتاب آسمانی به آنان داده شده است، و نیز شما را سفارش کردیم که [در همه امورتان] از خدا پروا کنید. و [هشدار دادیم] اگر کفر ورزید [زیانی به او نمی رسانید]؛ زیرا آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست، و خدا همواره بی نیاز و ستوده است.'),
(625, 4, 132, 'و آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست، و خدا نسبت به کارسازی [همه امور جهان آفرینش] کافی است.'),
(626, 4, 133, 'ای مردم! اگر خدا بخواهد همه شما را از میان برمی دارد، و گروه دیگری را [به جای شما] می آورد؛ و خدا همواره بر این کار تواناست.'),
(627, 4, 134, 'کسی که فقط پاداش دنیا را بخواهد [دچار اشتباه وکوته بینی شده است؛ زیرا] پاداش دنیا وآخرت که [محصول ایمان و تقواست] نزد خداست؛ و خدا همواره شنوا و بیناست.'),
(628, 4, 135, 'ای اهل ایمان! [همواره در همه امور زندگی] قیام کننده به عدل، و گواهی دهنده برای خدا باشید، هر چند به زیان خود یا پدر و مادر یا خویشانتان باشد. اگر [یکی از دو طرف نزاع] توانگر یا فقیر باشد [با لحاظ کردن وضع آنان، بر خلاف خدا گواهی ندهید؛ زیرا] خدا به حمایت از آنان و رعایت حالشان سزوارتر است. پس [در هنگام گواهی دادن] از هوای نفس پیروی نکنید تا از حق منحرف شوید. و اگر زبانتان را [به سوی گواهی ناحق و دروغ] بپیچانید، یا از گواهی دادن روی برتابید؛ یقیناً خدا همواره به آنچه انجام می دهید، آگاه است.'),
(629, 4, 136, 'ای اهل ایمان! به خدا و پیامبرش و کتابی که بر پیامبرش نازل کرده و به کتابی که پیش از این فرستاده [از روی صدق و حقیقت] ایمان بیاورید. و هر که به خدا و فرشتگان و کتاب های آسمانی و پیامبران او و روز قیامت کافر شود، یقیناً به گمراهیِ دور و درازی دچار شده است.'),
(630, 4, 137, 'کسانی که ایمان آوردند، سپس کفر ورزیدند، باز ایمان آوردند، و بار دیگر کافر شدند، آن گاه بر کفر خود افزودند، خدا بر آن نیست که آنان را بیامرزد، و به راهی هدایت کند.'),
(631, 4, 138, 'منافقان را بشارت ده که عذابی دردناک برای آنان است.'),
(632, 4, 139, 'همان کسانی که کافران را به جای مؤمنانْ سرپرست و دوست خود می گیرند؛ آیا عزت و قدرت را نزد آنان می طلبند؟ یقیناً همه عزت و قدرت فقط برای خداست.'),
(633, 4, 140, 'البته خدا [این فرمان را] در قرآن بر شما نازل کرده است که چون بشنوید گروهی آیات خدا را مورد انکار و استهزا قرار می دهند، با آنان ننشینید تا در سخنی دیگر درآیند، [اگر بنشینید] شما هم [در به دوش کشیدن بار گناه انکار و استهزا] مانند آنان خواهید بود؛ یقیناً خدا همه منافقان و کافران را در دوزخ گرد خواهد آورد.'),
(634, 4, 141, 'آنان که همواره حوادثی را برای شما انتظار می برند، اگر از سوی خدا برایتان فتح و پیروزی رسد، می گویند: مگر ما با شما [در میدان جنگ] نبودیم؟ [پس سهم ما را از غنایم جنگی بپردازید.] و اگر برای کافران بهره ای اندک [از غلبه و پیروزی] باشد، به آنان می گویند: آیا [ما که در میان ارتش اسلام بودیم] بر شما چیره و مسلّط نبودیم؟ [ولی دیدید که ازضربه زدن به شما خودداری کردیم] و شما را [ازآسیب و زیان مؤمنان] مانع می شدیم [پس سهم غنیمت ما را بدهید.] خدا روز قیامت میان شما داوری می کند؛ و خدا هرگز هیچ راه سلطه ای به سود کافران بر ضد مؤمنان قرار نداده است.'),
(635, 4, 142, 'منافقان همواره با خدا نیرنگ می کنند، و حال آنکه خدا کیفر دهنده نیرنگ آنان است. و هنگامی که به نماز می ایستند، با کسالت می ایستند، و همواره در برابر مردم ریاکاری می کنند؛ و خدا را جز اندکی به یاد نمی آورند.'),
(636, 4, 143, 'منافقان میان کفر و ایمان متحیّر و سرگردانند، نه [با تمام وجود] با مؤمنانند و نا با کافران. و هر که را خدا [به کیفر اعمال زشتش] گمراه کند، هرگز برای او راهی [به سوی هدایت] نخواهی یافت.'),
(637, 4, 144, 'ای اهل ایمان! کافران را به جای مؤمنان، سرپرست و یار خود مگیرید. آیا می خواهید برای خدا بر ضد خودتان دلیلی آشکار [نسبت به عذابتان در دنیا و آخرت] قرار دهید؟!'),
(638, 4, 145, 'بی تردید منافقان در پایین ترین طبقه از آتش اند، و هرگز برای آنان یاوری نخواهی یافت.'),
(639, 4, 146, 'مگر کسانی که [از گناه بزرگ نفاق] توبه کردند، و [مفاسد خود را] اصلاح نمودند، و به خدا تمسّک جستند، وعبادتشان را برای خدا خالص ساختند؛ پس آنان در زمره مؤمنان اند، وخدا به مؤمنان پاداشی بزرگ خواهد داد.'),
(640, 4, 147, 'اگر شما سپاس گزارِ [نعمت هایِ بی شمارِ حق] باشید و ایمان آورید، خدا را با عذاب شما چه کار؟ خدا همواره پاداش دهنده و داناست.'),
(641, 4, 148, 'خدا افشای بدی های دیگران را دوست ندارد، جز برای کسی که مورد ستم قرار گرفته است [که بر ستمدیده برای دفع ستم، افشای بدی های ستمکار جایز است]؛ و خدا شنوا و داناست.'),
(642, 4, 149, 'اگر کار خیری را آشکار کنید، یا آن راپنهان دارید، یا از بدیِ [دیگران] گذشت کنید، [کاری مورد پسند خدا انجام داده اید]؛ یقیناً خدا [با قدرت داشتن بر انتقام] همواره گذشت کننده و تواناست.'),
(643, 4, 150, 'بی تردید کسانی که به خدا و پیامبرانش کافر می شوند و می خواهند میان خدا و پیامبرانش [با ایمان آوردن به خدا و کفرورزی به پیامبران] جدایی اندازند، و می گویند: به بعضی ایمان می آوریم و به برخی کافر می شویم و می خواهند میان کفر و ایمان راهی [خاص] برگزینند، [سبک مغز و بی شعورند.]'),
(644, 4, 151, 'اینان در حقیقت کافرند؛ و ما برای کافران عذابی خوار کننده آماده کرده ایم.'),
(645, 4, 152, 'وکسانی که به خدا و پیامبرانش ایمان آوردند و میان هیچ یک از آنان جدایی نینداختند، خدا پاداششان را می دهد؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(646, 4, 153, 'اهل کتاب [بنا به خواستِ نامعقولشان] از تو می خواهند که از آسمان، کتابی برای آنان نازل کنی، البته [این بازیگران زشت باطن] از موسی بزرگ تر از آن را خواستند، و گفتند: خدا را آشکارا به ما نشان ده. پس آنان را به کیفر ستمشان صاعقه فرو گرفت. باز پس از آنکه [بر وحدانیّت حق و ضرورت خداپرستی] دلایل روشن برای آنان آمد، گوساله را به عنوان معبود گرفتند، و ما از این [گناه بزرگ و زشت هم] گذشتیم و به موسی برهانی روشن و قدرتی آشکار دادیم.'),
(647, 4, 154, 'و کوه طور را به سبب پیمانشان [به پذیرفتن تورات، عمل به آن و ایمان به موسی] برفرازشان برافراشتیم، و به آنان گفتیم: با تواضع و فروتنی و سجده کنان، از دروازه [شهر یا درِ معبد] درآیید. و [نیز] به آنان گفتیم: روز شنبه [از حکم حرمت صید ماهی] تجاوز نکنید. و از آنان [برای اجرای احکام تورات و فرمان های موسی] پیمانی محکم و استوار گرفتیم.'),
(648, 4, 155, 'پس به کیفر پیمان شکنی آنان و کفرشان به آیات خدا، و به ناحق کشتن پیامبران، و گفتار [بی پایه و باطل] شان که دل های ما در پوشش و حجاب است [از این رو سخن حق را درک نمی کنیم، لعنتشان کردیم؛] بلکه به سبب کفرشان بر دل هایشان مُهرِ [محرومیت از فهم معارف] زدیم؛ به همین سبب جز اندکی ایمان نمی آورند.'),
(649, 4, 156, 'و [نیز] به سبب کفرشان [به نبوّت عیسی] و گفتارشان بر مریمِ [پاکدامن و آراسته به عفت] که افترا و بهتان بزرگی بود [لعنتشان کردیم.]'),
(650, 4, 157, 'و به سبب گفتارِ [سراسر دروغ] شان که ما عیسی بن مریم فرستاده خدا را کشتیم. در صورتی که او رانکشتند و به دار نیاویختند، بلکه بر آنان مُشتبه شد [به این خاطر شخصی را به گمان اینکه عیسی است به دار آویختند و کشتند]؛ و کسانی که درباره او اختلاف کردند، نسبت به وضع وحال او در شک هستند، و جز پیروی از گمان و وهم، هیچ آگاهی و علمی به آن ندارند، و یقیناً او را نکشتند.'),
(651, 4, 158, 'بلکه خدا او را به سوی خود بالا برد؛ و خدا همواره توانای شکست ناپذیر و حکیم است.'),
(652, 4, 159, 'و هیچ یک از اهل کتاب نیست مگر آنکه پیش از مرگش [که لحظه روشن شدن حقایق است] به مسیح ایمان می آورد [که او فرستاده خدا، بنده او و مژده دهنده به ظهور پیامبر اسلام است، ولی ایمان در آن لحظه سودبخش نیست] و مسیح روز قیامت بر [عقاید و اعمال] آنان گواه است.'),
(653, 4, 160, 'پس به کیفر ستمی که یهودیان [به آیات خدا و بر خود و دیگران] روا داشتند، و به سزای آنکه بسیاری از مردم را از راه خدا بازداشتند، چیزهای پاکیزه ای را که بر آنان حلال شده بود [مانند صید ماهی و خوردنِ چربی و شیر و گوشت حیوانات] حرام کردیم.'),
(654, 4, 161, 'و [نیز به سبب] ربا گرفتنشان با آنکه از آن نهی شده بودند، و خوردن اموال مردم به باطل و نامشروع [پاکیزه های حلال شده را بر آنان حرام کردیم] و ما برای کافران عذابی دردناک آماده کرده ایم.'),
(655, 4, 162, 'ولی از میان آنان، ثابت قدمان در دانش، مؤمنانِ [به موسی و تورات] برپادارندگان نماز، پرداخت کنندگان زکات، مؤمنان به خدا و روز قیامت، به آنچه بر تو و [بر پیامبران] پیش از تو نازل شده ایمان واقعی می آورند آنان هستند که یقیناً پاداش بزرگی به ایشان عطا خواهیم کرد.'),
(656, 4, 163, 'ما به تو وحی کردیم، همان گونه که به نوح و پیامبران پس از او وحی کردیم؛ و به ابراهیم و اسماعیل و اسحاق و یعقوب و نوادگان [دارای مقام نبوتشان] و عیسی و ایوب و یونس و هارون و سلیمان نیز وحی نمودیم؛ و به داود، زبور دادیم.'),
(657, 4, 164, 'و به پیامبرانی [وحی کردیم] که سرگذشت آنان را پیش از این برای توگفتیم، و پیامبرانی [را برانگیخته ایم] که سرگذشتشان را برای تو حکایت نکرده ایم. و خدا با موسی به صورتی ویژه و بیواسطه سخن گفت.'),
(658, 4, 165, 'پیامبرانی که مژده رسان و بیم دهنده بودند تا مردم را [در دنیا و آخرت در برابر خدا] پس از فرستادن پیامبران، عذر و بهانه و حجتی نباشد؛ و خدا همواره توانای شکست ناپذیر و حکیم است.'),
(659, 4, 166, 'ولی [در برابر کبرورزی و لجاجت اهل کتاب، که به حقّانیّت قرآن و رسالت تو گواهی نمی دهند] خدا به وسیله آنچه بر تو نازل کرده گواهی می دهد که آن را به علم و دانش خود نازل کرده، و [نیز] فرشتگان هم گواهی می دهند، گرچه گواهی خدا کافی است.'),
(660, 4, 167, 'مسلماً کسانی که کافر شدند و [مردم را] از راه خدا بازداشتند، یقیناً به گمراهی دور و درازی دچار شده اند.'),
(661, 4, 168, 'بی تردید کسانی که کافر شدند و [به آیات الهی، به خود و به دیگران] ستم ورزیدند، خدا بر آن نیست که آنان را بیامرزد و به راهی [مستقیم و استوار] هدایت کند.'),
(662, 4, 169, 'مگر به راه دوزخ که در آن جاودانه اند؛ و این کار بر خدا آسان است.'),
(663, 4, 170, 'ای مردم! البته پیامبری از سوی پروردگارتان به درستی و راستی به سوی شما آمد؛ پس به او ایمان آورید که به خیر شماست. و اگر کفر ورزید [بدانید که خدا از شما و ایمانتان بی نیاز است] زیرا آنچه در آسمان ها و زمین است، در سیطره مالکیّت و فرمانروایی خداست، و خدا همواره دانا و حکیم است.'),
(664, 4, 171, 'ای اهل کتاب! در دین خود غلوّ [و افراط] نورزید، [و از چهارچوب آیات نازل شده از سوی خدا و آنچه پیامبران به شما آموخته اند، خارج نشوید] و نسبت به خدا جز حق مگویید. جز این نیست که مسیح عیسی بن مریم فرستاده خدا و کلمه اوست که به مریم القا کرد و [این بنده پاک و خالص] روحی از سوی اوست؛ پس به خدا و فرستادگانش ایمان آورید، و مگویید: [خدا] ترکیبی از سه حقیقت [اَب، اِبن و روح القدس] است. [از این عقیده باطل] خودداری کنید که به خیر شماست. جز این نیست که خدا معبودی یگانه است. منزّه و پاک است از اینکه فرزندی برای او باشد. آنچه در آسمان ها و آنچه در زمین است فقط در سیطره مالکیّت و فرمانروایی اوست، و خدا از نظر کارسازی [همه امور آفرینش] کافی است.'),
(665, 4, 172, 'مسیح از اینکه بنده خدا باشد، هرگز امتناع ندارد، و فرشتگان مقرّب هم امتناع ندارند؛ و کسانی که از بندگی او امتناع کنند و تکبّر ورزند، به زودی همه آنان را [برای حسابرسی] به سوی خود گردآوری خواهد کرد.'),
(666, 4, 173, 'اما کسانی که ایمان آوردند و کارهای شایسته انجام دادند، پاداششان را به طور کامل می دهد، و از فضل و احسانش بر آنان می افزاید؛ و اما کسانی که [از ایمان و عمل شایسته] امتناع کردند و تکبّر ورزیدند، به عذاب دردناکی عذاب می کند؛ و برای خود جز خدا سرپرست و یاوری نخواهند یافت.'),
(667, 4, 174, 'ای مردم! یقیناً از سوی پروردگارتان برای شما برهان [و دلیلی چون پیامبر و معجزاتش] آمد؛ و نور روشنگری [مانند قرآن] به سوی شما نازل کردیم.'),
(668, 4, 175, 'اما کسانی که به خدا ایمان آوردند و به او تمسّک جستند، به زودی آنان را در رحمت و فضلی از سوی خود درآورد، و به راهی راست به سوی خود راهنمایی می کند.'),
(669, 4, 176, 'از تو درباره کلاله [یعنی میراث برادران و خواهران] فتوا می خواهند، بگو: خدا درباره کلاله به شما فتوا می دهد: اگر مردی که فرزند ندارد، از دنیا برود وبرای او خواهری باشد، نصف میراثی را که از او به جا مانده خواهر به ارث می برد، و اگر خواهری از دنیا برود و وارثش فقط یک برادر باشد، همه میراث به جا مانده خواهر را برادرش به ارث می برد، و اگر خواهران میّت دو نفر باشند، دو سوم میراث به جا مانده را ارث می برند، و اگر خواهران و برادران متعدد باشند، سهم ارث برای مرد مانند دو زن است. خدا [احکامش را] برای شما بیان می کند تا گمراه نشوید؛ و خدا به همه چیز داناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای اهل ایمان! به همه قراردادها [یِ فردی، خانوادگی، اجتماعی، سیاسی، اقتصادی، نذر، عهد و سوگند] وفا کنید. [گوشتِ] چهارپایان جز آنچه [در آیات بعد، حُرمتش] بر شما خوانده می شود برایتان حلال است، [و توجه داشته باشید که] نباید شکار را در حالی که مُحرم [به احرام حج و عمره] هستید، حلال بشمارید؛ یقیناً خدا آنچه را بخواهد [بر پایه علم و حکمتش و بر اساس رعایت مصلحت شما] حکم می کند.'),
(671, 5, 2, 'ای اهل ایمان! [هتکِ حرمتِ] شعایر خدا [مانند مناسک حج] و ماه های حرام [رجب، ذوالقعده، ذوالحجه ومحرم] و قربانی بی نشان، و قربانی نشان دار و قاصدان [و راهیان به سوی] بیت الحرام را که فضل و خشنودی پروردگارشان را می طلبند، حلال مشمارید. و چون از احرام بیرون آمدید [اگر مایل باشید، می توانید] شکار کنید. و کینه توزی و دشمنی گروهی که شما را از [ورود به] مسجد الحرام بازداشتند وادارتان نکند که [به آنان] تعدّی و تجاوز کنید. و یکدیگر را بر انجام کارهای خیر و پرهیزکاری یاری نمایید، و یکدیگر را بر گناه و تجاوز یاری ندهید؛ و از خدا پروا کنید که خدا سخت کیفر است.'),
(672, 5, 3, '[خوردن مواد ناپاک که تناسبی با جسم و روح شما ندارند] بر شما حرام شده است [مانند] گوشت مردار و خون و گوشت خوک و آنچه به نام غیر خدا کشته اند و حیوان خفه شده و آنچه به ضرب چوب و سنگ مرده و آنچه به سبب پرت شدن از بلندی جان داده و حیوانی که به ضرب شاخ حیوان دیگر از بین رفته و حیوانی که درنده ای آن را کشته و از آن خورده، مگر آنچه را که در آستانه مرگ، قابل تذکیه بوده و شما به دستور شرع تذکیه کرده اید، و نیز آنچه برای بت ها قربانی شده، و آنچه به وسیله تیرهای قمار سهم بندی می کنید بر شما حرام گشته است. [همه] این [امور] فسق و نافرمانی [از احکام خدا] ست. امروز کفرپیشگان از [شکست] دین شما ناامید شده اند؛ بنابراین از آنان مترسید و از من بترسید. امروز [با نصبِ علی بن ابی طالب به ولایت، امامت، حکومت و فرمانروایی بر امت] دینتان را برای شما کامل، و نعمتم را بر شما تمام کردم، و اسلام را برایتان به عنوان دین پسندیدم. پس هر که در حال گرسنگی شدید بی آنکه مایل به گناه باشد [به خوردن مُحرّمات بیان شده] ناچار شود، [می تواند به اندازه ضرورت از آنها بهره گیرد]؛ یقیناً خدا بسیار آمرزنده و مهربان است.'),
(673, 5, 4, 'از تو می پرسند: چه چیزی بر آنان حلال شده؟ بگو: همه پاکیزه ها و شکاری که حیوانات شکاری که به آنها شکار کردن را تعلیم داده اید، در حالی که از احکام تذکیه ای که خدا به شما آموخته به آنها می آموزید، بر شما حلال شده است؛ بنابراین از آنچه آنان برای شما [گرفته اند و] نگاه داشته اند بخورید، و نام خدا را [هنگام فرستادن حیوان شکاری] بر آن بخوانید، و از خدا پروا کنید؛ زیرا خدا حسابرسی سریع است.'),
(674, 5, 5, 'امروز همه پاکیزه ها [ی از رزق خدا] برای شما حلال شد، و طعام اهل کتاب [که مخلوط با مواد حرام و نجس نیست] بر شما حلال و طعام شما هم بر آنان حلال است. و زنان پاکدامن مؤمن، و زنان پاکدامن از اهل کتاب [از طریق ازدواج شرعی] در صورتی که مهریه آنان را بپردازید بر شما حلال است، در حالی که مایل باشید پاکدامن بمانید نه زناکار، و نه انتخاب کننده معشوقه نامشروع در خلوت و پنهان. و هر کس در ایمان [به خدا و پیامبران وقیامت] کفر ورزد، قطعاً عملش تباه شده و فقط او در آخرت از زیانکاران است.'),
(675, 5, 6, 'ای اهل ایمان! هنگامی که به [قصدِ] نماز برخیزید، صورت و دست هایتان را تا آرنج بشویید و بخشی از سرتان و روی پاهایتان را تا برآمدگی پشت پا مسح کنید و اگر جُنُب بودید، غسل کنید و اگر بیمار هستید یا در سفر می باشید یا یکی از شما از قضای حاجت [دستشویی] آمده، یا با زنان آمیزش کرده اید و آبی [برای وضو یا غسل] نیافتید، به خاکی پاک، تیمم کنید و از آن بخشی از صورت و دست هایتان را مسح نمایید. خدا نمی خواهد [با احکامش] بر شما تنگی و مشقت قرار دهد، بلکه می خواهد شما را [از آلودگی ها] پاک کند و نعمتش را بر شما تمام نماید، تا سپاس گزارید.'),
(676, 5, 7, 'و نعمت خدا را بر خود و پیمانش را که [در لزوم اطاعت از او و پیامبرش] با شما محکم و استوار کرد، یاد کنید، هنگامی که گفتید: شنیدیم و اطاعت کردیم. و از خدا پروا کنید؛ زیرا خدا به آنچه در سینه هاست، داناست.'),
(677, 5, 8, 'ای اهل ایمان! همواره [در همه امور] قیام کننده برای خدا و گواهان به عدل و داد باشید. و نباید دشمنی با گروهی شما را بر آن دارد که عدالت نورزید؛ عدالت کنید که آن به پرهیزکاری نزدیک تر است. و از خدا پروا کنید؛ زیرا خدا به آنچه انجام می دهید آگاه است.'),
(678, 5, 9, 'خدا به کسانی که ایمان آورده اند و کارهای شایسته انجام داده اند، وعده داده است که برای آنان آمرزش و پاداشی بزرگ است.'),
(679, 5, 10, 'وکسانی که کافر شدند و آیات ما را تکذیب کردند، اهل دوزخ اند.'),
(680, 5, 11, 'ای اهل ایمان! نعمت خدا را بر خود یاد کنید زمانی که گروهی قصد کردند که [برای نابودی تان] به شما شبیخون زنند، ولی خدا توطئه آنان را از شما بازداشت. و از خدا پروا کنید و مؤمنان فقط باید بر خدا توکل کنند.'),
(681, 5, 12, 'خدا از بنی اسرائیل [بر لزوم اطاعت از آیینش و پیروی از موسی] پیمان گرفت، و از میان آنان دوازده سرپرست برانیگختیم [تا هر یک عهده دار امور قبیله ای از قبایل بنی اسرائیل باشد]، و خدا به آنان فرمود: یقیناً من با شمایم، اگر نماز را برپا دارید، و زکات بپردازید، و به پیامبرانم ایمان آورید، و آنان را تقویت و یاری کنید و به خدا وامی نیکو دهید، مسلماً گناهانتان را محو می کنم، و شما را در بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است در می آورم؛ پس هر که از شما بعد از این کافر شود، یقیناً راه راست را گم کرده است.'),
(682, 5, 13, 'پس آنان را به سبب پیمان شکستنشان لعنت کردیم، و دل هایشان را بسیار سخت گردانیدیم، [تا جایی که] کلمات خدا را از جایگاه اصلی اش و معنای حقیقی اش تغییر می دهند، و بخشی از آنچه را [از معارف و احکام تورات واقعی] که به وسیله آن پند داده شدند، از یاد بردند [و نادیده گرفتند]، و همواره از اعمال خائنانه آنان جز اندکی از ایشان [که وفادار به پیمان خدایند] آگاه می شوی؛ پس [تا نزول حکم جهاد] از آنان درگذر و [از مجازاتشان] روی گردان؛ زیرا خدا نیکوکاران را دوست دارد.'),
(683, 5, 14, 'و از کسانی که گفتند: ما نصرانی هستیم، [بر لزوم اطاعت از خود و پیروی از عیسی] پیمان گرفتیم، پس بخشی از آنچه را [از معارف و احکام انجیل واقعی] که به وسیله آن پند داده شدند، از یاد بردند [و نادیده گرفتند]، بر این اساس میان آنان تا روز قیامت کینه و دشمنی انداختیم، و خدا آنان را از آنچه همواره انجام می دادند، آگاه می کند.'),
(684, 5, 15, 'ای اهل کتاب! یقیناً پیامبر ما به سوی شما آمد که بسیاری از آنچه را که شما از کتاب [تورات و انجیل درباره نشانه های او و قرآن] همواره پنهان می داشتید برای شما بیان می کند، و از بسیاری [از پنهان کاری های ناروای شما هم] درمی گذرد. بی تردید از سوی خدا برای شما نور و کتابی روشنگر آمده است.'),
(685, 5, 16, 'خدا به وسیله آن [نور و کتاب] کسانی را که از خشنودی او پیروی کنند به راه های سلامت راهنمایی می کند، و آنان را به توفیق خود از تاریکی ها [یِ جهل، کفر، شرک و نفاق] به سوی روشناییِ [معرفت، ایمان و عمل صالح] بیرون می آورد، و به جانب راه راست هدایت می کند.'),
(686, 5, 17, 'مسلماً آنان که گفتند: خدا همان مسیح، فرزند مریم است. یقیناً کافر شده اند. بگو: اگر خدا بخواهد، مسیح و مادرش و تمام کسانی که در روی زمین اند، هلاک کند، چه کسی می تواند در برابر اراده و قدرت او بایستد؟ و مالکیّت و فرمانروایی آسمان ها و زمین و آنچه میان آن دو قرار دارد، فقط در سیطره خداست، هر چه را بخواهد می آفریند، و خدا بر هر کاری تواناست.'),
(687, 5, 18, 'و یهود و نصاری گفتند: ما پسران خدا و دوستان اوییم. بگو: [اگر گفتار شما درست است] پس چرا خدا شما را به گناهانتان عذاب می کند؟ بلکه شما هم بشری هستید از مخلوقاتی که خدا آفریده است. هر که را بخواهد می آمرزد، و هر که را بخواهد عذاب می کند. و مالکیّت و فرمانروایی آسمان ها و زمین و آنچه میان آن دو قرار دارد، فقط در سیطره خداست، و بازگشت به سوی اوست.'),
(688, 5, 19, 'ای اهل کتاب! بی تردید رسول ما پس از روزگار فترت و خلأ پیامبران به سوی شما آمد [و آنچه را مورد نیاز دنیا و آخرت شماست] برای شما بیان می کند که [روز قیامت در پیشگاه خدا] نگویید: برای ما هیچ مژده دهنده و بیم رسانی نیامد، یقیناً مژده دهنده و بیم رسان به سویتان آمد؛ وخدا بر هر کاری تواناست.'),
(689, 5, 20, 'و [یاد کنید] هنگامی که موسی به قومش گفت: ای قوم من! نعمت خدا را بر خود یاد کنید، آن گاه که در میان شما پیامبرانی قرار داد، و شما را حاکمان و فرمانروایان ساخت، و به شما نعمت های ویژه ای داد که به هیچ یک از جهانیان نداد.'),
(690, 5, 21, 'ای قوم من! به سرزمین مقدسی که خدا برایتان مقرّر فرموده در آیید و [به گناه، عصیان، سرپیچی از فرمان ها و احکام حق] بازنگردید که زیانکار می شوید.'),
(691, 5, 22, 'گفتند: ای موسی! مسلماً در آنجا مردمی زورگو و ستم گر قرار دارند، و ما هرگز وارد آنجا نمی شویم تا آنان از آنجا بیرون روند، پس اگر از آنجا بیرون روند البته ما وارد خواهیم شد.'),
(692, 5, 23, 'دو مرد از کسانی که [از خدا] می ترسیدند و خدا به هر دو نعمتِ [معرفت، ایمان و شهامت] داده بود، گفتند: از این دروازه به آنان یورش برید، چون به آنجا درآیید یقیناً پیروزید؛ و اگر مؤمن هستید بر خدا توکل کنید.'),
(693, 5, 24, 'گفتند: ای موسی! تا آنان در آنجایند، ما هرگز وارد آنجا نخواهیم شد، پس تو و پروردگارت بروید [با آنان] بجنگید که ما [تا پایان کار] در همین جا نشسته ایم.'),
(694, 5, 25, '[موسی] گفت: پروردگارا! من جز بر خود و برادرم تسلطی ندارم، پس میان ما و این گروه نافرمان و بدکار جدایی انداز.'),
(695, 5, 26, '[خدا] فرمود: این سرزمین مقدس [به کیفر نافرمانی از خواسته های حق] تا چهل سال بر آنان حرام شد، همواره در طول این مدت در زمینِ [سینا] سرگردان خواهند بود، پس بر این گروه نافرمان وبدکار غمگین مباش.'),
(696, 5, 27, 'وداستان دو پسر آدم را [که سراسر پند و عبرت است] به درستی و راستی بر آنان بخوان، هنگامی که هر دو نفر با انجام کار نیکی به پروردگار تقرّب جستند، پس از یکی پذیرفته شد، و از دیگری پذیرفته نشد. [برادری که عملش پذیرفته نشد از روی حسد و خودخواهی به برادرش] گفت: بی تردید تو را می کشم. [او] گفت: خدا فقط از پرهیزکاران می پذیرد.'),
(697, 5, 28, 'مسلماً اگر تو برای کشتن من دستت را دراز کنی، من برای کشتن تو دستم را دراز نمی کنم؛ زیرا از خدا پروردگار جهانیان می ترسم.'),
(698, 5, 29, 'من می خواهم به گناه کشتن من و گناه خودت [که سبب مردود شدن کار نیکت بود، به پیشگاه خدا] بازگردی و در نتیجه از دوزخیان باشی؛ و این است پاداش ستمکاران.'),
(699, 5, 30, 'نفس [طغیان گرش]، کشتن برادرش را در نظرش سهل و آسان جلوه داد، پس او را کشت و از زیانکاران شد.'),
(700, 5, 31, 'پس [در کنار جسد برادرش سرگردان بود که] خدا کلاغی را برانگیخت که زمین را می کاوید تا به او نشان دهد که چگونه جسد برادرش را پنهان کند. [با دیدن حرکات کلاغ] فریاد زد: وای بر من! آیا ناتوان و عاجز بودم از اینکه مانند این کلاغ باشم تا جسد برادرم را پنهان کنم؟! نهایتاً از پشیمانان شد.'),
(701, 5, 32, 'به این سبب بر بنی اسرائیل لازم و مقرّر کردیم که هر کس انسانی را جز برای حق، [قصاص] یا بدون آنکه فسادی در زمین کرده باشد، بکشد، چنان است که همه انسان ها را کشته، و هر کس انسانی را از مرگ برهاند و زنده بدارد، گویی همه انسان ها را زنده داشته است. و یقیناً پیامبران دلایل روشنی برای بنی اسرائیل آوردند، سپس بسیاری از آنان بعد از آن در روی زمین به [تجاوز از حدود حقّ و فساد و] زیاده روی برخاستند.'),
(702, 5, 33, 'کیفر آنان که با خدا و پیامبرش می جنگند، و در زمین به فساد و تباهی می کوشند، فقط این است که کشته شوند، یا به دارشان آویزند، یا دست راست و پای چپشان بریده شود، یا از وطن خود تبعیدشان کنند. این برای آنان رسوایی و خواری در دنیاست، و برای آنان در آخرت عذابی بزرگ است.'),
(703, 5, 34, 'مگر کسانی که پیش از دست یافتنتان بر آنان توبه کنند؛ پس بدانید که خدا بسیار آمرزنده و مهربان است.'),
(704, 5, 35, 'ای اهل ایمان! از خدا پروا کنید و دست آویز و وسیله ای [از ایمان، عمل صالح و آبروی مقرّبان درگاهش] برای تقرّب به سوی او بجویید؛ و در راه او جهاد کنید تا رستگار شوید.'),
(705, 5, 36, 'قطعاً کسانی که کافر شدند اگر همه آنچه در زمین است و همانند آن را نیز با آن همراه خود داشته باشند، تا آن را برای نجاتشان از عذاب روز قیامت فدیه و عوض دهند، از آنان پذیرفته نخواهد شد؛ و برای آنان عذابی دردناک است.'),
(706, 5, 37, 'همواره می خواهند از آتش بیرون آیند، ولی از آن بیرون شدنی نیستند؛ و برای آنان عذابی دائم و پایدار است.'),
(707, 5, 38, 'و دست مرد و زن دزد را به کیفر کار زشتی که مرتکب شده اند به عنوان مجازاتی از سوی خدا قطع کنید؛ و خدا توانای شکست ناپذیر است.'),
(708, 5, 39, 'پس کسی که بعد از ستم کردنش توبه کند و [مفاسد خود را] اصلاح نماید، یقیناً خدا توبه اش را می پذیرد؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(709, 5, 40, 'آیا ندانسته ای که فرمانروایی آسمان ها و زمین در سیطره خداست، هر کس را بخواهد عذاب می کند، و هر کس را بخواهد می آمرزد؛ و خدا بر هر کاری تواناست؟'),
(710, 5, 41, 'ای پیامبر! کسانی که در کفر می شتابند تو را غمگین نسازند، چه آنان که به زبانشان گفتند: ایمان آوردیم و دل هایشان ایمان نیاورده، و چه آنان که یهودی اند که به شدت شنونده دروغند [با آنکه می دانند دروغ است] و به شدت گوش به فرمان گروهی دیگرند که [از روی حسد و کبر] نزد تو نیامده اند. [آن گروهی که] کلمات [خدا] را پس از استواری در جایگاه هایش تحریف می کنند، [و معنای حقیقی اش را تغییر می دهند و به مقلّدان و مطیعان بی سواد خود] می گویند: اگر [از طرف پیامبر اسلام] احکام [و فرمان هایی مطابق میل ما] به شما ابلاغ کردند بپذیرید، و اگر آن را [مطابق میل ما] ابلاغ نکردند [از آن] بپرهیزید. و کسانی که خدا عذاب [و رسوایی و ذلت] شان را بخواهد، تو هرگز نمی توانی چیزی از عذاب خدا را از آنان برطرف کنی. اینانند کسانی که خدا نخواسته دل هایشان را [از آلودگی] پاک کند؛ برای آنان در دنیا خواری و رسوایی، و در آخرت عذابی بزرگ است.'),
(711, 5, 42, 'آنان فوق العاده شِنوای دروغ اند [با آنکه می دانند دروغ است و] بسیار خورنده مال حرام؛ پس اگر نزد تو آمدند میان آنان [در آنچه تو را داور قرار دادند] داوری کن، یا [اگر نخواستی داوری کنی] از آنان روی برتاب. و اگر روی برتابی هرگز هیچ زیانی به تو نمی رسانند. و اگر میانشان داوری کردی به عدالت داوری کن؛ زیرا خدا عدالت پیشگان را دوست دارد.'),
(712, 5, 43, 'چگونه تو را داور قرار می دهند در حالی که تورات که در آن حکم خداست نزد آنان است؟ سپس بعد از آنکه میانشان با حکمی که مطابق حکم تورات است داوری کردی روی برمی تابند، و [این روی برتافتن نشانه این است که] آنان [به تورات هم] ایمان ندارند.'),
(713, 5, 44, 'بی تردید ما تورات را که در آن هدایت و نور است نازل کردیم. پیامبرانی که [تا زمان عیسی] تسلیم تورات بودند بر اساس آن برای یهودیان داوری می کردند، و [نیز] دانشمندان الهی مَسلک [وکاملان در دین] و عالمان یهود به سبب آنکه حفظ و حراست کتاب خدا از آنان خواسته شده بود و بر [درستی و راستی] آن گواه بودند به وسیله آن داوری می کردند. پس [ای دانشمندان یهود!] از مردم نترسید و از من بترسید، و آیاتم را به بهایی اندک مفروشید. و کسانی که بر طبق آنچه خدا نازل کرده داوری نکنند، هم اینانند که کافرند.'),
(714, 5, 45, 'و ما در تورات بر بنی اسرائیل لازم و مقرّر داشتیم که [در قانون قصاص] جان در برابر جان، و چشم در مقابل چشم، و بینی به عوض بینی، و گوش به جای گوش، و دندان در برابر دندان، و زخم ها را قصاصی است. و هر که از آن قصاص گذشت کند، پس آن گذشت کفّاره ای برای [خطاها و معاصیِ] اوست. و آنان که بر طبق آنچه خدا نازل کرده داوری نکنند، هم اینانند که ستمکارند.'),
(715, 5, 46, 'و به دنبال پیامبران گذشته، عیسی بن مریم را درآوردیم در حالی که تصدیق کننده تورات پیش از خود بود، و انجیل را که در آن هدایت و نور است به او عطا کردیم و آن هم تصدیق کننده تورات پیش از خود، و برای پرهیزکاران هدایت و موعظه بود.'),
(716, 5, 47, 'و باید اهل انجیل بر طبق آنچه خدا در آن نازل کرده داوری کنند، و آنان که بر طبق آنچه خدا نازل کرده داوری نکنند، هم اینانند که فاسق اند.'),
(717, 5, 48, 'و ما این کتاب [قرآن] را به درستی و راستی به سوی تو نازل کردیم در حالی که تصدیق کننده کتاب های پیش از خود و نگهبان و گواه بر [حقّانیّت همه] آنان است؛ پس میان آنان بر طبق آنچه خدا نازل کرده است داوری کن، و [به هنگام داوری] سرپیچی از حقّی که به سوی تو آمده از هواهای نفسانی آنان پیروی مکن. برای هر یک از شما شریعت و راه روشنی قرار دادیم. و اگر خدا می خواست همه شما را امت واحدی قرار می داد، ولی می خواهد شما را در آنچه به شما داده امتحان کند؛ پس به سوی کارهای نیک بر یکدیگر پیشی گیرید. بازگشت همه شما به سوی خداست؛ پس شما را به آنچه همواره درباره آن اختلاف می کردید، آگاه می کند.'),
(718, 5, 49, 'و میان آنان بر طبق آنچه خدا نازل کرده است داوری کن، و از هواهای نفسانی آنان پیروی مکن، و از آنان برحذر باش که مبادا تو را از بخشی از آنچه خدا به سویت نازل کرده است، منحرف کنند. پس اگر [ازداوری حکیمانه ات] روی گردانند، بدان که خدا می خواهد آنان را به سبب پاره ای از گناهانشان مجازات کند؛ و مسلماً بسیاری از مردم فاسق اند.'),
(719, 5, 50, 'پس آیا [با وجودِ مشعل نبوّت و آیات روشن قرآن در میانشان] داوری جاهلیت را می طلبند؟! و برای قومی که یقین دارند، چه کسی در داوری بهتر از خدا است؟'),
(720, 5, 51, 'ای اهل ایمان! یهود و نصاری را سرپرستان و دوستان خود مگیرید، آنان سرپرستان و دوستان یکدیگرند [و تنها به روابط میان خود وفا دارند]. و هر کس از شما، یهود و نصاری را سرپرست و دوست خود گیرد از زمره آنان است؛ بی تردید خدا گروه ستمکار را هدایت نمی کند.'),
(721, 5, 52, 'کسانی که در دل هایشان بیماری [دورویی] است، می بینی که در دوستی با یهود و نصاری شتاب می ورزند و [بر پایه خیال باطلشان که مبادا اسلام و مسلمانان تکیه گاه استواری نباشند] می گویند: می ترسیم آسیب و گزند ناگواری به ما برسد [به این سبب باید برای دوستی به سوی یهود و نصاری بشتابیم]. امید است خدا از سوی خود پیروزی یا واقعیت دیگری [به نفع مسلمانان] پیش آرد تا این بیماردلان بر آنچه در دل هایشان پنهان می داشتند، پشیمان شوند.'),
(722, 5, 53, 'و کسانی که ایمان آورده اند [به مؤمنان دیگر] می گویند: آیا این بیماردلان آنانند که به سخت ترین سوگندهایشان به خدا سوگند می خوردند که با شما مؤمنانند؟! [ولی روشن شد که دروغ می گفتند. آنان یهود و نصاری را به سرپرستی و دوستی گرفتند و به این سبب] اعمالشان تباه و بی اثر شد، در نتیجه زیانکار شدند.'),
(723, 5, 54, 'ای اهل ایمان! هر کس از شما از دینش برگردد [زیانی به خدا نمی رساند] خدا به زودی گروهی را می آورد که آنان را دوست دارد، و آنان هم خدا را دوست دارند؛ در برابر مؤمنانْ فروتن اند، و در برابر کافرانْ سرسخت و قدرتمندند، همواره در راه خدا جهاد می کنند، و از سرزنش هیچ سرزنش کننده ای نمی ترسند. این فضل خداست که به هر کس بخواهد می دهد؛ و خدا بسیار عطاکننده و داناست.'),
(724, 5, 55, 'سرپرست و دوست شما فقط خدا و رسول اوست و مؤمنانی [مانند علی بن ابی طالب اند] که همواره نماز را برپا می دارند و در حالی که در رکوعند [به تهیدستان] زکات می دهند.'),
(725, 5, 56, 'و کسانی که خدا و رسولش و مؤمنانی [چون علی بن ابی طالب] را به سرپرستی و دوستی بپذیرند [حزب خدایند،] و یقیناً حزب خدا [در هر زمان و همه جا] پیروزند.'),
(726, 5, 57, 'ای مؤمنان! کسانی که دین شما را مسخره و بازیچه گرفته اند، چه از اهل کتاب و چه از کافران، سرپرستان و دوستان خود مگیرید؛ و اگر مؤمن هستید از خدا پروا کنید.'),
(727, 5, 58, 'و چون برای نمازْ ندا در می دهید، آن را به مسخره و بازی می گیرند؛ این کار زشتشان به سبب این است که آنان گروهی هستند که [در حقایق و معارف] اندیشه نمی کنند.'),
(728, 5, 59, 'بگو: ای اهل کتاب! آیا جز این را بر ما عیب می گیرید که ما به خدا و آنچه از سوی او بر ما نازل شده و آنچه پیش از ما [بر پیامبران] فرود آمده ایمان آورده ایم؟! و این [عیب جویی شما به سبب این است] که بیشتر شما فاسق هستید.'),
(729, 5, 60, 'بگو: آیا شما را از کسانی که کیفرشان [از مؤمنانی که به خیال خود از آنان عیب می گیرید و آنان را سزاوار عذاب می دانید] نزد خدا بدتر است، خبر دهم؟ [آنان] کسانی [از گذشتگان خود شما] هستند که خدا لعنتشان کرده، و بر آنان خشم گرفته، و برخی از آنان را به صورت بوزینه و خوک درآورده، و [نیز آنان که] طاغوت را پرستیدند؛ اینانند که جایگاه و منزلتشان بدتر و از راه راست گمراه ترند.'),
(730, 5, 61, 'و هنگامی که نزد شما آیند، گویند: ایمان آوردیم. در حالی که با کفر وارد می شوند، و با کفر بیرون می روند؛ و خدا به کفر و نفاقی که همواره پنهان می کنند، داناتر است.'),
(731, 5, 62, 'و بسیاری از آنان را می بینی که در گناه و تجاوز [از حدود خدا] و حرام خوری خود می شتابند؛ همانا بد است اعمالی که همواره انجام می دادند.'),
(732, 5, 63, 'چرا دانشمندان الهی مَسلک [و کاملان در دین] و عالمان یهود، آنان را از گفتار گناه آلود و حرام خواری بازنمی دارند؟ بسیار زشت است سکوتی که همواره پیش می گرفتند.'),
(733, 5, 64, 'و یهود گفتند: دست [قدرت] خدا [نسبت به تصرّف در امور آفرینش، تشریع قوانین و عطا کردنِ روزی] بسته است. دست هاشان بسته باد و به کیفر گفتار باطلشان بر آنان لعنت باد؛ بلکه هر دو دست خدا همواره گشوده و باز است [به هر چیز و به هر کس] هر گونه بخواهد، روزی می دهد. و مسلماً آنچه از جانب پروردگارت به سوی تو نازل شده است، بر سرکشی و کفر بسیاری از آنان می افزاید. و ما میان [یهود، نصاری و گروه های دیگر] آنان تا روز قیامت کینه و دشمنی انداختیم. هر زمان آتشی را برای جنگ [با اهل ایمان] افروختند خدا آن را خاموش کرد، و همواره در زمین برای فساد می کوشند، و خدا مفسدان را دوست ندارد.'),
(734, 5, 65, 'و اگر اهل کتاب [به آیین اسلام] ایمان می آوردند و پرهیزکاری می کردند، یقیناً گناهانشان را محو می کردیم، و آنان را در بهشت های پر نعمت در می آوردیم.'),
(735, 5, 66, 'و اگر آنان تورات و انجیل [واقعی] و آنچه را که از سوی پروردگارشان به آنان نازل شده برپا می داشتند، بی تردید از برکات آسمان و زمین بهره مند می شدند. از آنان گروهی میانه رو و معتدل اند، و بسیاری از آنان بد است آنچه را انجام می دهند.'),
(736, 5, 67, 'ای پیامبر! آنچه از سوی پروردگارت [درباره ولایت و رهبری علی بن أبی طالب امیرالمؤمنین (علیه السلام)] بر تو نازل شده ابلاغ کن؛ و اگر انجام ندهی پیام خدا را نرسانده ای. و خدا تو را از [آسیب و گزند] مردم نگه می دارد؛ قطعاً خدا گروه کافران را هدایت نمی کند.'),
(737, 5, 68, 'بگو: ای اهل کتاب! شما بر مسلک صحیح و درستی نیستید تا زمانی که تورات و انجیل و آنچه از جانب پروردگارتان به سوی شما نازل شده بر پا دارید، و بی تردید آنچه از جانب پروردگارت به سوی تو نازل شده، بر سرکشی و کفر بسیاری از آنان می افزاید، پس بر گروه کافران غمگین مباش.'),
(738, 5, 69, 'یقیناً کسانی که [از روی ظاهر] به اسلام گرویده اند و یهودی ها و صابئان و نصاری [با مُتّصف بودن به عنوان اسلام یا یهودیت یا صابئیت یا نصرانیّتْ، اهل سعادت و نجات نیستند،] هر کدام [از این گروه ها در زمان هر پیامبری از روی یقین و اخلاص] به خدا و روز قیامت ایمان آورده و کار شایسته انجام داده باشند، نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(739, 5, 70, 'البته ما از بنی اسرائیل [بر اطاعت از خدا و پیروی از موسی] پیمان گرفتیم و پیامبرانی به سوی ایشان فرستادیم؛ هرگاه پیامبری احکام و قوانینی که دلخواهشان نبود برای آنان می آورد، گروهی را تکذیب کرده و گروهی را می کشتند!!'),
(740, 5, 71, 'وپنداشتند که عذاب و مجازاتی وجود ندارد؛ بنابراین [از دیدن واقعیات] کور و [از شنیدن حقایق و معارف] کر شدند، سپس [بعد از آگاهی و توبه کردن] خدا توبه آنان را پذیرفت. باز بسیاری از آنان کور و کر شدند! و خدا به آنچه انجام می دهند، بیناست.'),
(741, 5, 72, 'آنان که گفتند: خدا همان مسیح، فرزند مریم است، بی تردید کافر شدند؛ و حال آنکه مسیح گفت: ای بنی اسرائیل! خدای یکتا را که پروردگار من و پروردگار شماست بپرستید، مسلماً هر کس به خدا شرک ورزد، بی تردید خدا بهشت را بر او حرام کند، و جایگاهش آتش است، و برای ستمکاران هیچ یاوری نیست.'),
(742, 5, 73, 'آنان که گفتند: خدا یکی از [این] سه تاست [پدر، پسر، روح] یقیناً کافر شدند، و حال آنکه هیچ معبودی جز خدای یکتا نیست. و اگر از آنچه می گویند بازنایستند، قطعاً به کسانی از آنان که [به سبب چنین اعتقادی] کافر شدند، عذابی دردناک خواهد رسید.'),
(743, 5, 74, 'آیا به سوی خدا بازنمی گردند، و از او درخواست آمرزش نمی کنند؟ و حال آنکه خدا بسیار آمرزنده ومهربان است.'),
(744, 5, 75, 'مسیح، فرزند مریم جز فرستاده ای [از سوی خدا] نیست که پیش از او هم فرستادگانی گذشته اند. و مادرش زن بسیار راستگو [و درست کاری] بود. هر دو [مانند انسان های دیگر] غذا می خوردند. با تأمل بنگر چگونه نشانه ها را برای آنان بیان می کنیم [تا بدانند متولد شدگان و نیازمندان به خوراک، خدا نیستند]؛ سپس با تأمل بنگر که آنان چگونه [از حق به باطل] منحرف می شوند؟!'),
(745, 5, 76, 'بگو: آیا به جای خدا چیزی را می پرستید که برای شما قدرت دفع هیچ زیان و جلب هیچ سودی را ندارد؟! و خداست که شنوا [یِ همه گفتارها] و دانا [یِ همه اسرار و رازها] ست.'),
(746, 5, 77, 'بگو: ای اهل کتاب! به ناحق در دینتان غلوّ نکنید، و از امیال و هوس های گروهی که یقیناً پیش از این گمراه شدند وبسیاری را گمراه کردند و از راه راست دور شدند، پیروی مکنید.'),
(747, 5, 78, 'از بنی اسرائیل آنان که کافر شدند به زبان داود و عیسی بن مریم لعنت شدند. لعنت شدنشان برای این بود که [نسبت به فرمان های خدا و انبیا] سرپیچی داشتند و همواره [از حدود الهی] تجاوز می کردند.'),
(748, 5, 79, 'آنان یکدیگر را ازکارهای زشتی که مرتکب می شدند بازنمی داشتند. مسلماً بد بود آنچه را انجام می دادند.'),
(749, 5, 80, 'بسیاری از آنان را می بینی که با کسانی که کفر ورزیدند، همواره دوستی می کنند. قطعاً بد است آنچه نفوسِ [طغیان گرشان برای قیامت] آنان پیش فرستاده است؛ [و نتیجه آن این است] که خدا بر آنان خشم گرفت و در عذاب جاودانه اند.'),
(750, 5, 81, 'اگر به خدا و پیامبر و آنچه بر او نازل شده ایمان می آوردند، [از برکت آن ایمان] کفّار و مشرکان را سرپرست و دوست خود نمی گرفتند؛ ولی بسیاری از آنان فاسق اند.'),
(751, 5, 82, 'یقیناً سرسخت ترین مردم را در کینه و دشمنی نسبت به مؤمنان، یهودیان و مشرکان خواهی یافت. و البته نزدیک ترینشان را در دوستی با مؤمنان، کسانی می یابی که گفتند: ما نصرانی هستیم. این واقعیت برای آن است که گروهی از آنان کشیشان دانشمند و عابدان خدا ترس اند، و آنان [در پیروی از حق] تکبّر نمی کنند.'),
(752, 5, 83, 'و چون آنچه را که بر پیامبر اسلام نازل شده بشنوند، دیدگانشان را می بینی به سبب آنچه از حق شناخته اند، لبریز از اشک می شود، می گویند: پروردگارا! ایمان آوردیم، پس ما را در زمره گواهان [به حقّانیّت پیامبر و قرآن] بنویس.'),
(753, 5, 84, 'و بر پایه چه عذر و بهانه ای به خدا و آنچه از حق برای ما آمده ایمان نیاوریم؟ و حال آنکه امید داریم که پروردگارمان ما را در زمره شایستگان درآورد.'),
(754, 5, 85, 'پس خدا به پاس این سخنان [و عقاید صادقانه] به آنان بهشت هایی پاداش داد که از زیرِ [درختانِ] آن نهرها جاری است، در آن جاودانه اند؛ و این است پاداش نیکوکاران.'),
(755, 5, 86, 'و آنان که کافر شدند و آیات ما را تکذیب کردند، اهل آتش اند.'),
(756, 5, 87, 'ای اهل ایمان! چیزهای پاکیزه ای را که خدا برای شما حلال کرده بر خود حرام نکنید، و [از حدود خدا] تجاوز ننمایید؛ یقیناً خدا متجاوزان را دوست ندارد.'),
(757, 5, 88, 'و از نعمت های حلال و پاکیزه ای که خدا روزی شما فرموده، بخورید؛ و از خدایی که به او ایمان دارید، پروا کنید.'),
(758, 5, 89, 'خدا شما را به خاطر سوگندهای لغو و بی هدفتان مجازات نمی کند، ولی به سبب [شکستنِ] سوگندهایی که به طور جدّی و با قصد و اراده خورده اید، مؤاخذه می کند؛ پس کفّاره [شکستن] این گونه سوگندها، طعام دادن به ده نفر مسکین از غذاهای متوسطی است که به خانواده خود می خورانید، یا لباس پوشاندن بر آن ده نفر، یا آزاد کردن یک برده. و کسی که هیچ یک [از این کفّاراتِ سه گانه] را نیابد کفّاره اش سه روزْ روزه است. این است کفّاره سوگندهایتان زمانی که سوگند خوردید [و آن را شکستید]. لازم است سوگندهای خود را حفظ کنید. این گونه خدا آیاتش را برای شما بیان می کند تا سپاس گزارید.'),
(759, 5, 90, 'ای اهل ایمان! جز این نیست که همه مایعات مست کننده و قمار و بت هایی که [برای پرستش] نصب شده وپاره چوب هایی که به آن تفأل زده می شود، پلید و از کارهای شیطان است؛ پس از آنها دوری کنید تا رستگار شوید.'),
(760, 5, 91, 'مسلماً شیطان می خواهد با شراب و قمار، میان شما دشمنی و کینه [سخت] اندازد، و از یاد خدا و نماز بازتان دارد؛ آیا شما [از این امور با همه زیان ها وخطراتی که دارد] خودداری خواهید کرد؟'),
(761, 5, 92, 'و از خدا اطاعت کنید و از پیامبر فرمان برید، و [از مخالفت با خدا و پیامبر] بپرهیزید، پس اگر روی گردانید [سزاوار کیفر می شوید]، و بدانید که بر عهده پیامبر ما فقط رساندن آشکارِ [پیام وحی] است.'),
(762, 5, 93, 'بر کسانی که ایمان آورده اند و کارهای شایسته انجام داده اند نسبت به آنچه [پیش از حکم تحریم، از مسکرات و منافع قمار و سایر مُحرّمات] خورده اند، گناهی نیست، هرگاه بپرهیزند و ایمان [واقعی] آورند و [خالصانه] کارهای شایسته انجام دهند؛ سپس پرهیزکاری را تداوم بخشند و ایمان خود را ادامه دهند، و بر پرهیزکاری پافشاری ورزند و کار نیک بجا آورند؛ و خدا نیکوکاران را دوست دارد.'),
(763, 5, 94, 'ای اهل ایمان! بی تردید خدا شما را به چیزی از شکار [حیوانات در حالی که مُحرم هستید] آزمایش می کند، چه شکاری که [به آسانی و بدون اسلحه] دست شما به آن برسد، و [چه شکاری که به سبب وحشی بودنش] نیزه های شما [آن را صید می کند؛] تا خدا کسی را که در نهان از او می ترسد، معلوم و مشخص نماید. و هر که پس از این [امتحان از حدود خدا] تجاوز کند [و در حال احرام به شکار برخیزد] برای او عذابی دردناک است.'),
(764, 5, 95, 'ای اهل ایمان! در حالی که در احرام [حج یا عمره] هستید، شکار را نکشید. و هر کس از شما عمداً آن را بکشد، کفّاره ای همانند آن از جنس چهارپایانِ [اهلی] بر عهده اوست؛ که [همانند بودن آن را] دو عادل از خودتان گواهی دهند، و به عنوان قربانی به [حریم] کعبه رسد، یا به کفّاره [آن شکار کشته شده معادل قیمت قربانی] به مستمندان طعام دهد، یا برابر تعداد مستمندی که طعام می دهد روزه بگیرد، تا کیفر کار خود را بچشد. خدا از [گناه] کشتن شکارهایی که پیش از این حکم انجام گرفته، درگذشت. و هر که [پس از کفّاره دادن] به شکار کردن بازگردد، خدا از او انتقام می گیرد؛ و خدا توانای شکست ناپذیر و صاحب انتقام است.'),
(765, 5, 96, 'برای بهره مند شدن شما و کاروانیان، شکار کردن از دریا و خوراکی آن [در حال احرام] بر شما حلال شد. و شکار صحرا و بیابان تا زمانی که مُحرم هستید، بر شما حرام است. و از خدایی که به سوی او گردآوری می شوید، پروا کنید.'),
(766, 5, 97, 'خدا، [زیارتِ] کعبه، آن خانه با حرمت، و ماه های حرام، و قربانی های بی نشان، و قربانی های نشان دار را وسیله قوام و برپایی [زندگی، معیشت و سامان دنیا و آخرت] مردم قرار داد. این [شرایع و احکام] برای آن است که بدانید: یقیناً خدا آنچه را در آسمان ها و آنچه را در زمین است می داند؛ و قطعاً خدا به همه چیز داناست.'),
(767, 5, 98, 'بدانید مسلماً خدا [نسبت به نافرمانی بندگان] سخت کیفر، و [نسبت به طاعت آنان] بسیار آمرزنده و مهربان است.'),
(768, 5, 99, 'بر عهده پیامبر جز رساندن [پیام خدا] نیست. و خدا آنچه را آشکار می کنید و آنچه را پنهان می دارید، می داند.'),
(769, 5, 100, 'بگو: ناپاک و پاک [مانند کافر و مؤمن، معصیت و طاعت، حرام و حلال، و معیوب و سالم] یکسان نیستند؛ هر چند فراوانی ناپاک ها تو را به شگفت آورد. پس ای صاحبان خرد! از خدا پروا کنید تا رستگار شوید.'),
(770, 5, 101, 'ای اهل ایمان! از احکام و معارفی [که شریعت اسلام از بیان آنها خودداری می کند و خدا هم به خاطر آسان گرفتن بر بندگانْ نهی و امری نسبت به آنها ندارد] مپرسید؛ که اگر برای شما آشکار شود، غمگینتان کند. و اگر هنگامی که قرآن نازل می شود از آنها بپرسید، برای شما روشن می شود. خدا از [اینکه شما را به] آن احکام و معارف [که باعث غم و اندوه شماست مُکلّف کند] گذشت کرد؛ و خدا بسیار آمرزنده و بردبار است.'),
(771, 5, 102, 'گروهی از پیشینیان شما از آن احکام و معارف پرسیدند، [چون برای آنان روشن شد، از اجرایش روی گرداندند] سپس به آن کافر شدند.'),
(772, 5, 103, 'خدا هیچ حیوانی را به عنوان بحیره [ماده شتری که پنجمین حمل مادر خود باشد] و سائبه [شتری که ده ماده زاییده] و وصیله [گوسفند ماده ای که همزاد با بره نر است] و حام [شتر نری که ده شتر از او پدید آمده]، مقدس و ممنوع از تصرّف قرار نداده است. ولی کسانی که کفر ورزیده اند [این امور را] بر خدا دروغ می بندند و بیشترشان اندیشه نمی کنند.'),
(773, 5, 104, 'و هنگامی که به آنان گویند: به سوی آنچه خدا نازل کرده و به سوی پیامبر آیید، گویند: روش و آیینی که پدرانمان را بر آن یافته ایم، ما را بس است. آیا هر چند پدرانشان چیزی نمی دانستند و هدایت نیافته باشند [باز هم این تقلید جاهلانه و ناروا را بر خود می پسندند؟!]'),
(774, 5, 105, 'ای اهل ایمان! مراقبِ [ایمان و ارزش های معنویِ] خود باشید؛ اگر شما هدایت یافتید، گمراهی کسی که گمراه شده به شما زیانی نمی رساند. بازگشت همه شما به سوی خداست؛ پس شما را از آنچه انجام می دادید، آگاه خواهد کرد.'),
(775, 5, 106, 'ای اهل ایمان! هنگامی که یکی از شما را [آثارِ] مرگ فرا می رسد، باید در حال وصیت، دو عادل از هم کیشان خود را بر وصیت شاهد بگیرید؛ یا اگر در سفر بودید و مرگِ شما فرا رسید [و شاهدی از مؤمنان نیافتید] دو نفر از غیر هم کیشان خود را شاهد وصیت بگیرید. و اگر [شما وارثان در صداقت و راستی آن دو شاهد غیر مسلمان] شک کردید، هر دو را بعد از نماز حبس کنید، پس به خدا سوگند یاد می کنند که ما این شهادت را به هیچ قیمتی نمی فروشیم هر چند [مورد شهادت] خویشاوندان ما باشد، و شهادت الهی را [که در حقیقت شهادت بر وصیت است] پنهان نمی کنیم، که اگر پنهان کنیم از گناهکاران خواهیم بود.'),
(776, 5, 107, 'پس اگر اطلاعی حاصل شود که آن دو شاهد [با شهادت ناحقّ خود] مرتکب گناه شده اند، دو شاهد دیگر از کسانی که به میّت نزدیک تر [و از کم و بیش وصیت آگاه تر] ند به جای آن دو شاهدِ [خائن] می ایستند، و به خدا سوگند می خورند که شهادتمان از شهادت آن دو نفر درست تر و به حق نزدیک تر است، و [در شهادتی که بر خلاف شهادت آنان می دهیم] بنای تجاوز از حق را نداریم، [که اگر داشته باشیم] قطعاً از ستمکاران خواهیم بود.'),
(777, 5, 108, 'این [حکم به ترتیبی که خدا برای شما مقرّر کرده] برای اینکه شاهدان شهادت را به صورتی درست و صحیح ادا کنند یا بترسند، [از اینکه مشتشان باز شود و سوگندشان مورد توجه قرار نگیرد] و حقّ سوگندشان به دیگران برگردانده شود، به حقیقت نزدیک تر است. و از خدا پروا کنید و [فرمان هایش را] بشنوید؛ و خدا گروه فاسقان را هدایت نمی کند.'),
(778, 5, 109, '[از] روزی [پروا کنید] که خدا پیامبران را جمع می کند، و [به آنان] می فرماید: [در برابر دعوتتان به اجرای دستورهای خدا] چه پاسخی به شما داده شد؟ گویند: ما را [در برابرِ دانشِ تو] هیچ دانشی نیست؛ یقیناً تویی که به نهان ها بسیار دانایی.'),
(779, 5, 110, '[یاد کنید] هنگامی که خدا فرمود: ای عیسی بن مریم! نعمتم را بر خود و بر مادرت یاد کن، آن گاه که تو را به وسیله روح القدس توانایی بخشیدم، که با مردم در گهواره [به اعجاز] و در میان سالی [به وحی] سخن گفتی، و آن گاه که تو را کتاب و حکمت و تورات و انجیل آموختم، و هنگامی که به اذن من از گِل، مجسمه ای به شکل پرنده می ساختی، پس در آن می دمیدی و به فرمان من پرنده ای زنده می شد، و کور مادر زاد و شخص پیس را به اذن من شفا می دادی، و زمانی که مردگان را به اجازه من [زنده از گور] بیرون می آوردی، و آن گاه که [شرّ و آسیب] بنی اسرائیل را هنگامی که برای آنان دلایل روشن آوردی از تو بازداشتم، پس کسانی که از آنان کافر شدند، گفتند: این [دلایل و معجزات] جز افسونی آشکار نیست.'),
(780, 5, 111, 'و [یاد کنید] هنگامی که به حواریون وحی کردم که به من و فرستاده من ایمان آورید. گفتند: ایمان آوردیم و شاهد باش که ما [در برابر خدا، فرمان ها و احکام او] تسلیم هستیم.'),
(781, 5, 112, '[و یاد کنید] زمانی که حواریون گفتند: ای عیسی بن مریم! آیا پروردگارت می تواند برای ما سفره ای که غذا در آن باشد از آسمان نازل کند؟! گفت: اگر ایمان دارید، از خدا پروا کنید.'),
(782, 5, 113, 'گفتند: می خواهیم از آن بخوریم و دل های ما آرامش یابد، و بدانیم که تو [در ادعای نبوتت] به ما راست گفته ای، و ما بر آن از گواهان باشیم.'),
(783, 5, 114, 'عیسی بن مریم گفت: خدایا! ای پروردگار ما! برای ما از آسمان سفره ای پر از غذا نازل کن تا عیدی باشد برای اهل زمان ما و نسل آینده ما، و نشانه ای از سوی تو؛ و ما را روزی بخش که تو بهترین روزی دهندگانی.'),
(784, 5, 115, 'خدا فرمود: من مسلماً آن را بر شما نازل می کنم، پس اگر کسی از شما بعد از آن کافر شود [و به انکار نبوّت عیسی و معجزه اش برخیزد] بی تردید او را عذابی خواهم کرد که هیچ یک از جهانیان را آن گونه عذاب نکنم.'),
(785, 5, 116, 'و [عرصه هول انگیز قیامت را یاد کنید] هنگامی که خدا به عیسی بن مریم می فرماید: آیا تو به مردم گفتی که مرا و مادرم را به عنوان دو معبود به جای خدا انتخاب کنید؟! می گوید: منزّه و پاکی، مرا چنین قدرتی نمی باشد که آنچه را حقّ من نیست بگویم، اگر آن را گفته باشم یقیناً تو آن را می دانی، تو از آنچه در جان و روح من است آگاهی، و من از آنچه در ذات توست نمی دانم؛ زیرا تو بر نهان ها بسیار دانایی.'),
(786, 5, 117, 'من به آنان جز آنچه را که به من دستور دادی نگفتم، [گفتم:] خدایی را بپرستید که پروردگار من و شماست. و تا زمانی که در میان آنان بودم گواهشان بودم، و چون مرا [از میان آنان به سوی خود] برگرفتی، خود مراقب و نگاهبانشان بودی؛ و تو بر همه چیز گواهی.'),
(787, 5, 118, 'اگر آنان را [به سبب شرک ورزیدنشان] عذاب کنی، بندگان تواند، و اگر آنان را بیامرزی، یقیناً تویی که توانای شکست ناپذیر و حکیمی.'),
(788, 5, 119, 'خدا فرمود: این روزی است که راستان را راستی و صدقشان سود دهد. برای آنان بهشت هایی است که از زیرِ [درختانِ] آن نهرها جاری است، همیشه در آن جاودانه اند، خدا از آنان خشنود و آنان هم از خدا خشنودند؛ این است رستگاری بزرگ.'),
(789, 5, 120, 'مالکیّت و فرمانروایی آسمان ها و زمین و آنچه در آنهاست، فقط در سیطره خداست، و او بر هر کاری تواناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'همه ستایش ها ویژه خداست که آسمان ها و زمین را آفرید و تاریکی ها و روشنی را پدید آورد. [با این همه نشانه ها که گواهی بر یکتایی و قدرت اوست] باز کافران برای پروردگارشان [بت ها و معبودهای باطل را] شریک و همتا قرار می دهند.'),
(791, 6, 2, 'اوست که شما را از گِلی مخصوص آفرید، سپس برای عمر شما مدتی مقرّر کرد، و اجل حتمی و ثابت نزد اوست، [شگفتا! که] باز شما [با این همه دلایل آشکار در یکتایی و ربوبیّت و خالقیّت او] شک می کنید.'),
(792, 6, 3, 'و او در آسمان ها و در زمین، خداست که نهان و آشکار شما را می داند، و نیز به آنچه [از خیر و شر] به دست می آورید، آگاه است.'),
(793, 6, 4, 'و هیچ نشانه ای از نشانه های پروردگارشان برای آنان نیامد مگر آنکه از آن روی گردانیدند.'),
(794, 6, 5, 'آنان حق را هنگامی که به سویشان آمد تکذیب کردند، پس [ظهور و تحقّق] خبرهای آنچه را همواره به آن استهزا می کردند، به آنان خواهد رسید.'),
(795, 6, 6, 'آیا ندانسته اند که پیش از ایشان چه بسیار از ملت ها و اقوام را هلاک کردیم؟ که به آنان در پهنه زمین، نعمت ها و امکاناتی دادیم که به شما ندادیم، و بر آنان باران های پی در پی و پر ریزش فرستادیم و نهرهایی از زیر پای آنان جاری ساختیم [ولی ناسپاسی کردند]؛ پس آنان را به کیفر گناهانشان هلاک کردیم، و بعد از آنان اقوامی دیگر را پدید آوردیم.'),
(796, 6, 7, 'و اگر ما نوشته ای روی صحفه ای بر تو نازل کنیم که آنان آن را با دست خود لمس کنند، باز کفر پیشگان می گویند: این جز جادویی آشکار نیست!!'),
(797, 6, 8, 'و گفتند: چرا فرشته ای [که در معرض دید ما قرار گیرد] بر او نازل نشده است؟ اگر فرشته ای نازل کنیم، کار هلاکت [این بهانه جویان] تمام می شود و لحظه ای مهلت نیابند.'),
(798, 6, 9, '[این مغالطه گران لجوج می گویند: چرا پیامبر از جنس فرشتگان نیست؟] اگر او را فرشته ای قرار می دادیم، یقیناً وی را به شکل مردی در می آوردیم، و قطعاً حقیقت را همانطور که آنان بر دیگران مُشتبه می کنند [به سزای لجاجتشان] بر خود آنان مُشتبه می ساختیم، [تا بر فرض پیامبر قرار دادن فرشته، باز بگویند: چرا پیامبری از جنس بشر برای ما قرار داده است؟]'),
(799, 6, 10, 'مسلماً پیامبران پیش از تو هم مسخره شدند؛ پس بر کسانی که پیامبران را مسخره می کردند، عذاب و کیفری که همواره به استهزا می گرفتند نازل شد و آنان را فرا گرفت.'),
(800, 6, 11, 'بگو: در زمین بگردید، سپس با تأمل بنگرید که فرجام تکذیب کنندگان چگونه بوده است؟'),
(801, 6, 12, 'بگو: مالکیّت و فرمانروایی آنچه در آسمان ها و زمین است در سیطره کیست؟ بگو: در سیطره خداست که رحمت را بر خود لازم و مقرّر کرده؛ یقیناً همه شما را در روز قیامت که تردیدی در آن نیست جمع خواهد کرد. فقط کسانی که سرمایه وجودشان را تباه کرده اند، ایمان نمی آورند.'),
(802, 6, 13, 'و همه موجوداتی که در [عرصه] شب و روز آرام دارند، در سیطره مالکیّت خدا هستند؛ و او شنوا و داناست.'),
(803, 6, 14, 'بگو: آیا جز خدا را که آفریننده آسمان ها و زمین است، سرپرست و یاور خود گیرم؟ و اوست که روزی می دهد و خود بی نیاز از روزی است. بگو: من مأمورم نخستین کسی باشم که فرمان خدا را گردن نهد، و فرمان یافته ام که هرگز از مشرکان مباش.'),
(804, 6, 15, 'بگو: مسلماً اگر پروردگارم را نافرمانی کنم، از عذاب روزی بزرگ می ترسم'),
(805, 6, 16, 'هر کس در آن روز، عذاب از او برگردانده شود، قطعاً خدا به او رحم کرده، و آن است کامیابی آشکار.'),
(806, 6, 17, 'و اگر خدا تو را آسیب و گزندی رساند، کسی جز او برطرف کننده آن نیست، و اگر تو را خیری رساند [حفظ و دوامش فقط به دست اوست]؛ پس او بر هر کاری تواناست.'),
(807, 6, 18, 'اوست که بر بندگانش چیره و غالب است، و او حکیم و آگاه است.'),
(808, 6, 19, '[به کافران و مشرکان که برای حق بودنِ نبوتت گواه می طلبند] بگو: گواهی چه کسی از همه گواهان بزرگ تر است [تا او را بر حقّانیّت نبوتم برای شما گواه آورم]؟ بگو: خدا میان من وشما گواه است [که گواهی اش بزرگ ترین ومطمئن ترین و صحیح ترین گواهی هاست]، و این قرآن [که گواهی خدا بر حقّانیّت نبوّت من است] به من وحی شده تا به وسیله آن شما وهر کس را که این قرآن به او می رسد بیم دهم. آیا شما [با دلیل استوار و محکم] گواهی می دهید که با خدا معبودهای دیگری به عنوان شریک هست؟ بگو: [چون دلیلی وجود ندارد] من گواهی نمی دهم. بگو: فقط معبود یگانه و یکتا اوست، و من از معبودهای باطلی که شریک او قرار می دهید، بیزارم.'),
(809, 6, 20, 'کسانی که به آنان کتاب آسمانی دادیم، همان گونه که پسرانشان را می شناسند [بر اساس اوصافی که در کتاب هایشان بیان شده] پیامبر را می شناسند؛ فقط کسانی که سرمایه وجودشان را تباه کردند، ایمان نمی آورند.'),
(810, 6, 21, 'ستمکارتر از کسی که بر خدا دروغ بسته، یا آیات او را تکذیب کرده، کیست؟ یقیناً ستمکاران، رستگار نخواهند بود.'),
(811, 6, 22, 'و [یاد کنید] روزی که همه آنان را [در عرصه قیامت] جمع می کنیم، سپس به کسانی که شرک ورزیده اند، می گوییم: شریکانتان که آنان را شریک خدا می پنداشتید، کجایند [تا شما را از عذاب امروز نجات دهند؟]'),
(812, 6, 23, 'پس [آن روز] بهانه و عذرشان [برای رهایی از عذاب] فقط این است که خواهند گفت: به خدا پروردگارمان سوگند، که ما مشرک نبودیم!!'),
(813, 6, 24, 'با تأمل بنگر که چگونه بر خود دروغ می بندند؛ وآنچه را به دروغ شریک خدا می دانستند از دستشان رفت وگم شد.'),
(814, 6, 25, 'گروهی از آنان به سخنانت گوش می دهند، و ما [به کیفر لجاجت وکفرشان] بر دل هایشان پوشش هایی قرار دادیم که آن را نفهمند، و در گوش هایشان سنگینی نهادیم [تا نشنوند]. و اگر همه نشانه های حق را ببینند باز هم به آن ایمان نمی آورند؛ تا آنجا که وقتی به محضرت می آیند با تو مجادله وستیزه می کنند، کافران [از روی لجاجت وعناد] می گویند: این [قرآن] جز افسانه های پیشینیان نیست.'),
(815, 6, 26, 'و آنان دیگران را [از توجه به قرآن] بازمی دارند و خود نیز از آن دور می شوند، اینان جز خود را به هلاکت نمی اندازند، و [این خطر را] درک نمی کنند.'),
(816, 6, 27, 'اگر هنگامی که آنان را بر آتش [دوزخ] نگه داشته اند ببینی [حالتی ذلت بار و وضعی هول انگیز می بینی] می گویند: ای کاش به دنیا بازگردانده می شدیم، و در آنجا آیات پروردگارمان را تکذیب نمی کردیم، و از مؤمنان می شدیم.'),
(817, 6, 28, '[ولی آرزوی آنان از روی صدق و راستی نیست] بلکه آنچه [از حقّانیّت توحید و نبوّت و معاد] پیش از این پنهان می داشتند برای آنان آشکار شده [که این گونه آرزو می کنند]، و اگر به دنیا بازگردانده شوند، یقیناً به کفر و شرکی که از آن نهی شده اند بازمی گردند؛ و مسلماً آنان دروغگویند.'),
(818, 6, 29, 'و [زمانی که در دنیا بودند] گفتند: جز این زندگی دنیای ما زندگی دیگری نیست و [پس از مرگ] برانگیخته نخواهیم شد.'),
(819, 6, 30, 'و اگر ببینی هنگامی که در پیشگاه پروردگارشان بازداشته شوند [وضعی شگفت انگیز می بینی]، خدا می فرماید: آیا این [برانگیخته شدن و زنده گشتن پس از مرگ] حق نیست؟ می گویند: سوگند به پروردگارمان حق است. [خدا] می فرماید: پس به کیفر آنکه کفر می ورزیدید، این عذاب را بچشید.'),
(820, 6, 31, 'کسانی که دیدار [پاداش و مقام قرب] خدا را تکذیب کردند، یقیناً دچار زیان شدند. تا هنگامی که قیامت به طور ناگهان و غافل گیرانه به آنان رسد، می گویند: ای بر ما دریغ و افسوس که نسبت به تکالیف و وظایف شرعی خود کوتاهی کردیم. و آنان بار سنگین گناهانشان را بر دوش می کشند؛ آگاه باشید! بد باری است که بر دوش خواهند کشید.'),
(821, 6, 32, 'و زندگی دنیا [بدون ایمان و عمل صالح] بازی و سرگرمی است، و یقیناً سرای آخرت برای آنان که همواره پرهیزکاری می کنند، بهتر است. آیا نمی اندیشید؟'),
(822, 6, 33, 'آنچه را کافران و مشرکان [بر ضد قرآن و تو] می گویند می دانیم که تو را غمگین می کند. پس آنان تو را تکذیب نمی کنند، بلکه ستمکاران آیات خدا را تکذیب می نمایند.'),
(823, 6, 34, 'مسلماً پیش از تو رسولانی تکذیب شدند [و مورد آزار قرار گرفتند] و بر تکذیب و آزاری که دیدند، شکیبایی ورزیدند تا یاری ما به آنان رسید، [تو نیز شکیبایی کن تا یاری ما به تو برسد. این سنّت خداست،] و سنّت های خدا را تغییر دهنده ای نیست. و قطعاً بخشی از سرگذشت پیامبران [در آیاتی که پیش از این نازل شده] به تو رسیده است.'),
(824, 6, 35, 'و اگر روی گرداندن آنان [از قرآن و نبوّت] بر تو سنگین و دشوار است، اگر بتوانی نقبی در زمین، یا نردبانی برای راه یافتن در آسمان بجویی تا [از عمق زمین و پهنه آسمان] معجزه ای دیگر [غیر قرآن] برای آنان بیاوری [این کار را انجام ده ولی آنها ایمان نمی آورند]؛ و اگر خدا می خواست همه آنان را [به جبر و زور] به راه هدایت گرد می آورد؛ [ولی هدایت اجباری فاقد ارزش است] پس هرگز [نسبت به روحیات و اوصاف کافران لجوج که خواستار هدایت نیستند] از بی خبران مباش.'),
(825, 6, 36, 'فقط کسانی [دعوتت را] پاسخ می دهند که [سخنانت را با دقّت، تدبّر، اندیشه و تفکّر] می شنوند، [مشرکان و کافران لجوج، مردگانند] و خدا مردگان را [در قیامت] برمی انگیزد، آنگه [برای حسابرسی] به سوی او بازگردانده می شوند.'),
(826, 6, 37, 'و [مرده دلان] گفتند: چرا معجزه ای از سوی پروردگارش [همانند معجزات پیامبران گذشته] بر او نازل نمی شود؟ بگو: یقیناً خدا قدرت دارد که معجزه ای [نظیر آنچه آنان درخواست دارند] نازل کند، ولی [این گونه درخواست های نابجا به سبب این است که] بیشترشان [شرایط و موقعیت ها را] نمی دانند.'),
(827, 6, 38, 'و هیچ جنبنده ای در زمین نیست، و نه هیچ پرنده ای که با دو بال خود پرواز می کند، مگر آنکه گروه هایی مانند شمایند؛ ما چیزی را در کتاب [تکوین از نظر ثبت جریانات هستی و برنامه های آفرینش] فروگذار نکرده ایم، سپس همگی به سوی پروردگارشان گردآوری می شوند.'),
(828, 6, 39, 'کسانی که آیات ما را تکذیب کردند، کرها و لال هایی فرو رفته در تاریکی های جهل و گمراهی اند؛ خدا هر که را بخواهد [به کیفر تکذیب آیاتش] گمراه می کند، و هر که را بخواهد [به پاداش تصدیق آیاتش] بر راه راست قرار می دهد.'),
(829, 6, 40, 'بگو: اگر راستگویید به من خبر دهید چنانچه عذاب خدا بر شما درآید یا قیامت به شما رسد، آیا کسی را جز خدا [برای یاری خویش] می خوانید؟'),
(830, 6, 41, '[قطعاً چنین نیست] بلکه فقط خدا را می خوانید، و او هم اگر بخواهد آسیب و گزندی که به سبب آن او را خوانده اید برطرف می کند، و [آن زمان است که] بت هایی که برای او شریک قرار می دهید، فراموش می کنید.'),
(831, 6, 42, 'و بی تردید ما به سوی امت هایی که پیش از تو بودند [پیامبرانی] فرستادیم؛ پس آنان را [هنگامی که با پیامبران به مخالفت و دشمنی برخاستند] به تهیدستی و سختی و رنج و بیماری دچار کردیم، باشد که [در پیشگاه ما] فروتنی و زاری کنند.'),
(832, 6, 43, 'پس چرا هنگامی که عذاب ما به آنان رسید، فروتنی و زاری نکردند؟ بلکه دل هایشان سخت شد و شیطان، اعمال ناپسندی که همواره مرتکب می شدند در نظرشان آراست.'),
(833, 6, 44, 'پس چون حقایقی را که [برای عبرت گرفتنشان] به آن یادآوری شده بودند، فراموش کردند، درهای همه نعمت ها را به روی آنان گشودیم، تا هنگامی که به آنچه داده شدند، مغرورانه خوشحال گشتند، به ناگاه آنان را [به عذاب] گرفتیم، پس یکباره [از نجات خود] درمانده و نومید شدند.'),
(834, 6, 45, 'در نتیجه ریشه کسانی که [به آیات ما و به مردم] ستم ورزیدند، بریده شد؛ و همه ستایش ها ویژه خدا مالک و مربی جهانیان است.'),
(835, 6, 46, '[به آنان که برای خدا به خیال باطلشان شریکانی قرار داده اند] بگو: به من خبر دهید اگر خدا گوش ها و چشم هایتان را بگیرد، و بر دل هایتان مُهر [تیره بختی] نِهد، کدام معبودی جز خداست که آنها را به شما دهد؟! با تأمل بنگر چگونه آیات خود را به صورت های گوناگون [برای هدایت اینان] بیان می کنیم، سپس آنان روی می گردانند.'),
(836, 6, 47, 'بگو: به من خبر دهید اگر عذاب خدا ناگهان [مانند صاعقه و صیحه آسمانی] یا آشکار [مانند بیماری های مهلک] به سوی شما آید، آیا جز گروه ستمکار هلاک می شوند؟'),
(837, 6, 48, 'و پیامبران را جز مژده دهنده و بیم رسان نمی فرستیم؛ پس کسانی که ایمان بیاورند و [مفاسد خود را] اصلاح کنند نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(838, 6, 49, 'و آنان که آیات ما را تکذیب کردند، به سزای نافرمانی هایشان عذاب به آنان خواهد رسید.'),
(839, 6, 50, 'بگو: من به شما نمی گویم که خزینه ها و گنجینه های خدا نزد من است، و نیز غیب هم نمی دانم، و نمی گویم که فرشته ام؛ فقط از آنچه به من وحی شده پیروی می کنم. بگو: آیا [اعراض کننده از وحی که] نابینا [ست] و [پیروِ وحی که] بینا [ست] یکسانند؟! پس چرا نمی اندیشید؟!'),
(840, 6, 51, 'و به وسیله این قرآن کسانی را که از محشور شدن به سوی پروردگارشان می ترسند، هشدار ده که آنان را [در آن روز هول انگیز] جز خدا سرپرست و شفیعی نخواهد بود؛ باشد که بپرهیزند.'),
(841, 6, 52, 'و آنان که بامدادان و شامگاهان پروردگارشان را می خوانند [و با این خواندن] خشنودی او را می خواهند، از خود مران. نه چیزی از حساب [عمل] آنان بر عهده توست، و نه چیزی از حساب [کار] تو بر عهده آنان است تا آنان را برانی و [به سبب راندنشان] از ستمکاران باشی.'),
(842, 6, 53, 'و ما این گونه توانگران را به مؤمنان تهیدستی که در کنار تو هستند آزمودیم، تا [توانگران با تعجب و استهزا] بگویند: آیا اینانند که خدا از میان ما بر آنان منّت نهاده [و نعمت ایمان و عمل صالح داده و بر ما برتری بخشیده؟!] آیا خدا به سپاس گزاران داناتر نیست؟'),
(843, 6, 54, 'و هرگاه کسانی که به آیات ما ایمان می آورند به نزد تو آیند، بگو: سلام بر شما، پروردگارتان رحمت را بر خود لازم و مقرّر کرده؛ بنابراین هر کس از شما به نادانی کار زشتی مرتکب شود، سپس بعد از آن توبه کند و [مفاسد خود را] اصلاح نماید [مشمول آمرزش و رحمت خدا شود]؛ زیرا او بسیار آمرزنده و مهربان است.'),
(844, 6, 55, 'و این گونه آیات را بیان می کنیم تا [حق روشن شود و] راه و رسم گناهکاران آشکار گردد.'),
(845, 6, 56, 'بگو: من از پرستیدن کسانی که شما به جای خدا می پرستید نهی شده ام. بگو: من از هواهای نفسانی شما پیروی نمی کنم، که در آن صورت گمراه شده ام و از راه یافتگان نخواهم بود.'),
(846, 6, 57, 'بگو: من بر دلیل روشنی [مانند قرآن] از سوی پروردگارم هستم و حال آنکه شما آن را تکذیب کردید، عذابی که به آن شتاب دارید در اختیار من نیست. فرمان و حکم فقط به دست خداست، همواره حق را بیان می کند و او بهترین داوران و جداکنندگان [حق از باطل] است.'),
(847, 6, 58, 'بگو: اگر عذابی که به آن شتاب دارید در اختیار من بود، یقیناً کار میان من و شما به سرعت تمام شده بود؛ و خدا به ستمکاران داناتر است [که چه زمانی آنان را عذاب کند.]'),
(848, 6, 59, 'و کلیدهای غیب فقط نزد اوست، و کسی آنها را جز او نمی داند. و به آنچه در خشکی و دریاست، آگاه است، و هیچ برگی نمی افتد مگر آنکه آن را می داند، و هیچ دانه ای در تاریکی های زمین، و هیچ تر و خشکی نیست مگر آنکه در کتابی روشن [ثبت] است.'),
(849, 6, 60, 'او کسی است که شبانگاهان روح شما را به وقت خواب می گیرد، و آنچه را در روز [از نیکی و بدی] به دست می آورید می داند؛ سپس شما را در روز [با برگرداندن روح به جسمتان از خواب] بر می انگیزد تا اجل مُعّین سر آید؛ آن گاه بازگشت شما به سوی اوست؛ سپس شما را به آنچه همواره انجام می دادید آگاه می کند.'),
(850, 6, 61, 'و اوست که بر بندگانش چیره و غالب است، و همواره نگهبانانی [از فرشتگان] برای [حفاظتِ] شما [از حوادث و بلاها] می فرستد تا هنگامی که یکی از شما را مرگ در رسد [در این وقت] فرستادگان ما جانش را می گیرند؛ و آنان [در مأموریت خود] کوتاهی نمی کنند.'),
(851, 6, 62, 'سپس [همه] به سوی خدا، سرپرست و مولای به حقّشان بازگردانده می شوند. آگاه باشید! [که در آن روز] فرمانروایی و حکومت مطلق فقط ویژه اوست؛ و او سریع ترین حساب گران است.'),
(852, 6, 63, 'بگو: چه کسی شما را از تاریکی هایِ خشکی و دریا نجات می دهد؟ در حالی که او را [برای نجات خود] از روی فروتنی و زاری و مخفیانه به کمک می طلبید؛ [و می گویید] که اگر ما را از این [تنگناها و مهلکه ها] نجات دهد، بی تردید از سپاس گزاران خواهیم بود.'),
(853, 6, 64, 'بگو: خدا شما را از آن [سختی ها] و از هر اندوهی نجات می دهد، باز شما به او شرک می ورزید [و به ناسپاسی می گرایید.]'),
(854, 6, 65, 'بگو: او تواناست که از بالای سرتان یا از زیر پاهایتان عذابی بر شما برانگیزد، یا شما را گروه گروه و حزب حزب به جان هم اندازد، [تا وضعتان به اختلافات سنگین و کینه های سخت بیانجامد،] و مزه تلخ جنگ و خونریزی را به هر گروه شما به وسیله گروه دیگر بچشاند؛ با تأمل بنگر چگونه آیات خود را به صورت های گوناگون بیان می کنیم تا بفهمند.'),
(855, 6, 66, 'و قوم تو [قرآن] را در حالی که حق است تکذیب کردند؛ بگو: من بر [هدایت، پاداش و کیفر] شما کارساز و صاحب اختیار نیستم.'),
(856, 6, 67, 'برای هر خبری [از مژده ها و بیم ها] قرارگاهی است، و به زودی [ظهور و تحقّق آنها را در قرارگاه هایشان] خواهید دانست.'),
(857, 6, 68, 'و هرگاه کسانی را دیدی که در آیات ما [به قصد شبهه اندازی] به یاوه گویی و سخن بی منطق می پردازند از آنان روی گردان [و مجلسشان را ترک کن] تا در سخنی دیگر در آیند. و اگر شیطان تو را [نسبت به ترک مجلسشان] به فراموشی اندازد، پس از یاد آوردن [به سرعت بیرون برو و] با گروه ستمکاران منشین.'),
(858, 6, 69, 'و چیزی از حساب ستمکاران [مغالطه گر] بر عهده کسانی که [به ناچار سخنان آنان را می شنوند ولی از نشستن با آنان] می پرهیزند نیست؛ بلکه [بر عهده اهل تقواست که یاوه گویان را] پند دهند، تا [از گفتار باطل و بی پایه خود] بپرهیزند.'),
(859, 6, 70, 'و کسانی که دینشان را بازی و سرگرمی گرفتند، و زندگی دنیا آنان را فریفت، و اگذار؛ و [مردم را] با قرآن اندرز ده؛ که مبادا کسی [در روز قیامت] به [کیفر] آنچه [از گناهان] مرتکب شده [از رحمت و ثواب] محروم ماند [و به هلاکت سپرده شود]؛ و او را جز خدا سرپرست و یاور و شفیعی نباشد؛ و اگر [برای رهایی اش از عذاب] هر گونه عوض و فدیه ای بدهد از او پذیرفته نشود. آنانند که به سبب آنچه [از اعمال زشت] مرتکب شده اند [از رحمت و ثواب] محروم مانده [و به هلاکت سپرده شده] اند. و به خاطر آنکه همواره [به آیات الهی] کفر می ورزیدند برای آنان نوشابه ای از آب جوشان و عذابی دردناک است.'),
(860, 6, 71, 'بگو: آیا به جای خدا معبودی را بپرستیم که نه سودی به ما رساند و نه زیانی؟ و پس از آنکه خدا ما را هدایت کرد [به سبب شرک و بت پرستی] به فرهنگ و فضای جاهلیت بازمان گردانند، مانند کسی که شیاطین عقلش را ربوده اند و در زمین سرگردان [و بی هدف و گمراه] است، در حالی که برای او یارانی است که وی را به سوی هدایت دعوت می کنند که به جانب ما بیا [ولی او به سبب سرگردانی و حیرت زدگی نمی پذیرد.] بگو: یقیناً هدایت خدا همان هدایت [واقعی] است، و ما مأموریم که تسلیم پروردگار جهانیان باشیم.'),
(861, 6, 72, 'و [نیز فرمان یافته ایم] به اینکه نماز را بر پا دارید و از خدا پروا کنید، و اوست که به سویش گردآوری می شوید.'),
(862, 6, 73, 'و اوست که آسمان ها و زمین را به درستی و راستی آفرید؛ و روزی که [اراده اش به آفریدن چیزی تعلق گیرد] می گوید: باش، پس بی درنگ می باشد؛ گفتارش حق است؛ و روزی که در صور دمیده شود فرمانروایی و حاکمیّت مطلق ویژه اوست، دانای نهان و آشکار است، و او حکیم و آگاه است.'),
(863, 6, 74, 'و [یاد کنید] هنگامی را که ابراهیم به پدرش آزر [شخصی که بنابر روایات اهل بیت، پدر خوانده یا عمویش بود] گفت: آیا بت هایی را به عنوان معبود خود انتخاب می کنی؟ قطعاً من تو را و قومت را در گمراهی آشکار می بینم.'),
(864, 6, 75, 'و این گونه فرمانروایی و مالکیّت و ربوبیّت خود را بر آسمان ها و زمین به ابراهیم نشان می دهیم تا از یقین کنندگان شود.'),
(865, 6, 76, 'پس چون [تاریکی] شب او را پوشانید، ستاره ای دید [برای محکوم کردن ستاره پرستان با تظاهر به ستاره پرستی] گفت: این پروردگار من است؛ هنگامی که ستاره غروب کرد، گفت: من غروب کنندگان را دوست ندارم.'),
(866, 6, 77, 'هنگامی که ماه را در حال طلوع دید [برای محکوم کردن ماه پرستان با تظاهر به ماه پرستی]، گفت: این پروردگار من است؛ چون ماه غروب کرد، گفت: یقیناً اگر پروردگارم مرا هدایت نکند بدون شک از گروه گمراهان خواهم بود.'),
(867, 6, 78, 'وقتی خورشید را در حال طلوع دید [برای محکوم کردن خورشیدپرستان با تظاهر به خورشید پرستی] گفت: این پروردگار من است، این بزرگ تر است؛ و هنگامی که غروب کرد، گفت: ای قوم من! بی تردید من [با همه وجود] از آنچه شریک خدا قرار می دهید، بیزارم.'),
(868, 6, 79, 'من به دور از انحراف و با قلبی حق گرا همه وجودم را به سوی کسی که آسمان ها و زمین را آفرید، متوجه کردم و از مشرکان نیستم.'),
(869, 6, 80, 'قومش با او به گفتگوی بی منطق و ستیز برخاستند، گفت: آیا درباره خدا با من گفتگوی بی منطق و ستیز می کنید و حال آنکه او مرا هدایت کرد؟ و من از آنچه که شما شریک او قرار می دهید نمی ترسم مگر آنکه پروردگارم چیزی را [درباره من] بخواهد، دانش پروردگارم همه چیز را فراگرفته است، آیا [برای فهم توحید] متذکّر نمی شوید؟'),
(870, 6, 81, 'چگونه از آنچه شما شریک او قرار داده اید بترسم؟! در حالی که خود شما از اینکه چیزی را شریک خدا گرفته اید که خدا هیچ دلیلی بر حقّانیّت آن بر شما نازل نکرده نمی ترسید، اگر حقایق را می دانید [بگویید] کدام یک از دو گروه [خداپرستان و مشرکان] به ایمنی [از عذاب خدا] سزاوارترند؟'),
(871, 6, 82, 'کسانی که ایمان آوردند و ایمانشان را به ستمی [چون شرک] نیامیختند، ایمنی [از عذاب] برای آنان است، و آنان راه یافتگانند.'),
(872, 6, 83, 'و آن [دلایل استوار و محکم] حجت و برهان ما بود که به ابراهیم در برابر قومش عطا کردیم؛ هر که را بخواهیم [به] درجاتی بالا می بریم؛ زیرا پروردگارت حکیم و داناست.'),
(873, 6, 84, 'و اسحاق و یعقوب را به ابراهیم بخشیدیم، [و] هر یک را هدایت کردیم، و [نیز] نوح را پیش از این هدایت نمودیم، و از فرزندان او داود و سلیمان و ایوب و یوسف و موسی و هارون را [هدایت کردیم؛] و این گونه نیکوکاران را پاداش می دهیم.'),
(874, 6, 85, 'و زکریا و یحیی و عیسی و الیاس را [نیز که از نسل اویند، هدایت نمودیم] همه از شایستگان بودند.'),
(875, 6, 86, 'و اسماعیل و یَسَع و یونس و لوط را [هدایت کردیم]، و همه را بر جهانیان برتری دادیم.'),
(876, 6, 87, 'و از پدران و فرزندان و برادرانشان برخی را [برتری عطا کردیم]، و آنان را برگزیدیم و به راهی راست هدایت نمودیم.'),
(877, 6, 88, 'این هدایت خداست که هر کس از بندگانش را بخواهد به آن هدایت می کند، و اگر [آنان با همه عظمت و مقامی که داشتند برای خدا] شریک قرار داده بودند، یقیناً آنچه عمل شایسته انجام می دادند، تباه و بی اثر می شد.'),
(878, 6, 89, 'آنان کسانی هستند که به ایشان کتاب ومنصب داوری ونبوّت دادیم؛ پس اگر اینان [که به سبب مشرک بودنشان دشمنی و مخالفت می کنند] به این حقایق [که پایه های آیین الهی اند] کفر ورزند، یقیناً گروهی را [برای نگهبانی و مراعات امر نبوّت و برنامه دین] می گماریم که نسبت به آنها کافر نیستند.'),
(879, 6, 90, 'آنان [که در آیات گذشته به عنوان پیامبران از ایشان یاد شد] کسانی هستند که خدا هدایتشان کرد، پس به هدایت آنان اقتدا کن، [و به قوم خود] بگو: در برابر رسالتم پاداشی از شما نمی خواهم، این [قرآن] جز تذکّر و پندی برای جهانیان نیست.'),
(880, 6, 91, '[یهودیان بر ضد پیامبر اسلام به سفسطه گری پرداختند چون] آنان خدا را آن گونه که سزاوار اوست نشناختند، چرا که گفتند: خدا بر هیچ بشری چیزی نازل نکرده. بگو: کتابی را که موسی آورده چه کسی نازل کرده؟ کتابی که برای مردمْ نور [و روشنگر حقایق] و [سرتاسر] هدایت است، شما آن را در طومارهای پراکنده قرار می دهید، بخشی از آن را [که دلخواه شماست برای مردم] آشکار می کنید و بسیاری از آن را [که گواه نبوّت پیامبر اسلام است] پنهان می دارید و حال آنکه حقایقی که نه شما می دانستید و نه پدرانتان [به وسیله تورات] به شما آموخته شد. بگو: خدا، سپس آنان را رها کن تا در باطل گویی و خرافاتشان بازی کنند.'),
(881, 6, 92, 'و این [قرآن] کتابی است پرفایده، که ما آن را نازل کردیم، تصدیق کننده کتاب های آسمانی پیش از خود است، و [نزولش] برای این است که مردم مکه و کسانی را که پیرامون آنند بیم دهی؛ و آنان که به آخرت ایمان دارند به آن ایمان می آورند، و آنان همواره بر نمازشان محافظت می کنند.'),
(882, 6, 93, 'چه کسی ستمکارتر از کسی است که به خدا دروغ می بندد، یا می گوید: به من وحی شده؛ در حالی که چیزی به او وحی نشده است، و نیز کسی که بگوید: من به زودی مانند آنچه خدا نازل کرده نازل می کنم؟! ای کاش ستمکاران را هنگامی که در سختی ها و شداید مرگ اند ببینی در حالی که فرشتگان دست های خود را [به سوی آنان] گشوده [و فریاد می زنند] جانتان را بیرون کنید، امروز [که روز وارد شدن به جهان دیگر است] به سبب سخنانی که به ناحق درباره خدا می گفتید واز پذیرفتن آیات او تکبّر می کردید، به عذاب خوارکننده ای مجازات می شوید.'),
(883, 6, 94, 'و [لحظه ورود به جهان دیگر به آنان خطاب می شود:] همان گونه که شما را نخستین بار [در رحم مادر تنها و دست خالی از همه چیز] آفریدیم، اکنون هم تنها به نزد ما آمدید، و آنچه را در دنیا به شما داده بودیم پشت سر گذاشته و همه را از دست دادید، و شفیعانتان را که [در ربوبیّت و عبادت ما] شریک می پنداشتید، همراه شما نمی بینیم، یقیناً پیوندهای شما [با همه چیز] بریده، و آنچه را شریکان خدا گمان می کردید از دستتان رفته و گم شده است.'),
(884, 6, 95, 'بی تردید خدا شکافنده دانه و هسته است؛ زنده را از مرده بیرون می آورد، و بیرون آورنده مرده از زنده است؛ این است خدا، پس چگونه [از حق] منصرفتان می کنند؟'),
(885, 6, 96, 'شکافنده صبح [از پرده تاریک شب است] و شب را برای آرامش قرار داد، و خورشید و ماه را وسیله ای برای محاسبه و اندازه گیری زمان مقرّر فرمود؛ این است اندازه گیری آن توانای شکست ناپذیر ودانا.'),
(886, 6, 97, 'و اوست که ستارگان را برای شما قرار داد تا به وسیله آنها در تاریکی های خشکی و دریا راه یابید؛ مسلماً ما آیات خود را برای قومی که دانایند بیان کردیم.'),
(887, 6, 98, 'و اوست که شما را از یک تن آفرید، برخی [از شما] استقرار یافته [در زمین] و برخی به ودیعت نهاده [در اصلاب و رحم هایند]؛ ما آیات خود را برای قومی که می فهمند، بیان کردیم.'),
(888, 6, 99, 'و اوست که از آسمان آبی فرستاد، و به وسیله آن گیاهان گوناگون را [از زمین] رویاندیم، و از آن ساقه ها و شاخه های سبز درآوردیم، و از آن دانه های متراکم را خارج می کنیم، و از شکوفه درخت خرما خوشه های سر فروهشته [به وجود می آوریم]، و باغ هایی از انگور و زیتون و انار شبیه به هم و بی شباهت به هم [بیرون می آوریم]؛ به میوه اش چون میوه دهد و به رسیدن و کامل شدنش با تأمل بنگرید، مسلماً در این [امور] برای قومی که ایمان می آورند، نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] ست.'),
(889, 6, 100, '[مشرکان] برای خدا شریکانی از جن قرار دادند، در حالی که همه آنها را خدا آفرید، و از روی جهل و بی دانشی برای خدا پسران و دخترانی انگاشتند؛ منزّه و برتر است از آنچه آنان توصیف می کنند.'),
(890, 6, 101, '[او] پدید آورنده آسمان ها و زمین است، چگونه او را فرزندی باشد؟! در صورتی که برای او همسری نبوده، و همه چیز را او آفریده؛ و او به همه چیز داناست.'),
(891, 6, 102, 'این است خدا پروردگار شما، جز او معبودی نیست، آفریننده همه چیز است؛ پس تنها او را بپرستید، و او کارساز هر چیز است.'),
(892, 6, 103, 'چشم ها او را در نمی یابند، ولی او چشم ها را درمی یابد، و او لطیف و آگاه است.'),
(893, 6, 104, 'بی تردید برای شما از سوی پروردگارتان دلایلی روشن آمده، پس هر که [به وسیله آن دلایل بینا شود و حقایق را با چشم دل] ببیند به سود خود اوست و هر که [با پشت کردن به دلایل] کوردل شود [و از دیدن حقایق محروم گردد] به زیان خود اوست، [وظیفه من ابلاغ پیام خداست] و بر شما حافظ و نگهبان نیستم.'),
(894, 6, 105, 'این چنین، آیات [خود] را به صورت های گوناگون بیان می کنیم [تا هدف هایی تحقق یابد]، یکی اینکه منکران با متهم کردن تو که این آیات را از دیگران فرا گرفته ای، شقاوت خود را کامل کنند و دیگر اینکه آن را برای گروهی که دانایند، روشن سازیم.'),
(895, 6, 106, 'از آنچه از سوی پروردگارت به تو وحی شده پیروی کن، هیچ معبودی جز او نیست، و از مشرکان روی بگردان.'),
(896, 6, 107, 'اگر خدا می خواست آنان [به طور اجبار] شرک نمی آوردند، [ولی خواست خدا آزادی انسان در انتخاب است] و ما تو را بر آنان نگهبان و کارساز قرار ندادیم [تا آنان را به اجبار به پذیرش دین وادار کنی.]'),
(897, 6, 108, 'و معبودانی را که کافران به جای خدا می پرستند، دشنام ندهید، که آنان هم از روی دشمنی و نادانی خدا را دشنام خواهند داد. این گونه برای هر امتی عملشان را آراستیم [تا به کیفر لجاجت و عنادشان گمان کنندکه آنچه انجام می دهند نیکوست،] سپس بازگشت همه آنان به سوی پروردگارشان خواهد بود، پس آنان را به اعمالی که همواره انجام می دادند، آگاه می کند.'),
(898, 6, 109, 'با سخت ترین سوگندهایشان به خدا سوگند خوردند که اگر معجزه دلخواهشان برای آنان آید، قطعاً به آن ایمان می آورند، بگو: معجزات فقط در اختیار خداست، و شما [ای مردم مؤمن!] چه می دانید [که حقیقت چیست؟] حقیقت این است که اگر آن معجزه هم بیاید، ایمان نمی آورند.'),
(899, 6, 110, 'و دل ها و دیدگانشان را [که وسیله فهم و بصیرت است، به سزای لجاجت و عنادشان] وارونه و دگرگون کردیم، [به این سبب با آمدن معجزه دلخواهشان هم ایمان نمی آورند] همان گونه که نخستین بار به آیات قرآن ایمان نیاوردند، آنان را در طغیانشان رها می کنیم تا پیوسته سرگردان باشند.'),
(900, 6, 111, 'و مسلماً اگر ما فرشتگان را به سوی آنان نازل می کردیم، و مردگان با آنان سخن می گفتند، و همه چیز را گروه گروه در برابرشان گرد می آوردیم، باز هم [از روی اراده و اختیار] ایمان نمی آوردند؛ مگر آنکه خدا [ایمان آوردنشان را به طور اجبار] بخواهد، ولی بیشترشان نادانی و جهالت می ورزند.'),
(901, 6, 112, 'و این گونه برای هر پیامبری دشمنانی از شیاطین انس و جن قرار دادیم، که برای فریب مردم همواره گفتاری باطل ولی به ظاهر آراسته و دلپسند به یکدیگر القاء می کنند، و اگر پروردگارت می خواست چنین نمی کردند، پس آنان و آنچه را به دروغ به هم می بافند واگذار.'),
(902, 6, 113, 'و [ما به عنوان آزمایش بندگان از القای گفتار باطل توسط شیاطین انس و جن به یکدیگر مانع نشدیم] تا دل های کسانی که به آخرت ایمان ندارند به آن القائات متمایل شود، و آن را بپسندد و [برای رسیدن به منتهای شقاوت که کیفر لجاجت و عناد آنان است] هر عمل زشتی را که بخواهند انجام دهند، نهایتاً انجام دهند.'),
(903, 6, 114, 'آیا جز خدا حاکم و داوری بطلبم؟! در حالی که اوست که این کتاب آسمانی را که [همه معارف و احکام در آن] به تفصیل بیان شده به سوی شما نازل کرد؛ و اهل کتاب [به ویژه دانشمندان آنان] می دانند که این کتاب [قرآن] از سوی پروردگارت به درستی و راستی نازل شده؛ پس [در اینکه معجزه خواهی منکران و مخالفت های اهل کتاب از روی حقیقت جویی نیست] از تردیدکنندگان مباش.'),
(904, 6, 115, 'و سخن پروردگارت از روی راستی و عدل کامل شد، سخنان او را تغییر دهنده ای نیست؛ و او شنوا و داناست.'),
(905, 6, 116, 'اگر از بیشتر مردم روی زمین پیروی کنی [و آرا و خواسته هایشان را گردن نهی] تو را از راه خدا گمراه می کنند؛ آنان فقط از گمان و پندار [که پایه علمی و منطقی ندارد] پیروی می کنند، و تنها به حدس و تخمین تکیه می زنند.'),
(906, 6, 117, 'یقیناً پروردگارت به کسی که از راه او گمراه می شود و نیز به راه یافتگان داناتر است.'),
(907, 6, 118, 'پس اگر به آیات خدا ایمان دارید، از آنچه [از گوشت حیوان حلال گوشتی] که [وقت ذبح شدن] نام خدا بر او برده شده بخورید [نه از ذبیحه گمراهان.]'),
(908, 6, 119, 'و شما را چه شده [که به دنبال یاوه گویی مشرکان] از آنچه [چون گوشت ذبیحه ای] که نام خدا بر آن برده شده نمی خورید؟! در صورتی که آنچه را خدا بر شما حرام نموده برای شما بیان کرده است، مگر چیزی که [برای حفظ جانتان] به خوردن آن ناچار شوید؛ و قطعاً بسیاری [از بداندیشان]، دیگران را از روی جهل و نادانی با هواهای نفسانی خود گمراه می کنند؛ یقیناً پروردگارت به تجاوزکاران [از حدود حق] داناتر است.'),
(909, 6, 120, 'گناه آشکار و پنهان را رها کنید؛ قطعاً کسانی که مرتکب گناه می شوند به زودی در برابر آنچه همواره مرتکب می شدند، مجازات خواهند شد.'),
(910, 6, 121, 'از آنچه [هنگام ذبح شدن] نام خدا بر آن برده نشده نخورید؛ مسلماً خوردن آن فسق [و بیرون رفتن از عرصه طاعت خدا] است؛ قطعاً شیاطین [شبهات ناروایی را به ضد احکام خدا] به دوستانشان القاء می کنند تا با شما [درباره احکام خدا] مجادله و ستیزه کنند، و اگر از آنان پیروی کنید، یقیناً شما هم مشرکید.'),
(911, 6, 122, 'آیا کسی که [از نظر عقلی و روحی] مرده بود و ما او را [به وسیله هدایت و ایمان] زنده کردیم، و برای وی نوری قرار دادیم تا در پرتو آن در میان مردم [به درستی و سلامت] حرکت کند، مانند کسی است که در تاریکی ها [یِ جهل و گمراهی] است و از آن بیرون شدنی نیست؟! این گونه برای کافران [به خاطر لجاجت و عنادشان] آنچه انجام می دادند، آراسته شد [تا گمان کنند اعمالی را که انجام می دهند نیکوست.]'),
(912, 6, 123, 'و این گونه [که در مکه می بینی] در هر آبادی و شهری رؤسا و سردمداران گنهکاری قرار دادیم تا در آن [برای گمراه کردن مردم] نیرنگ زنند، ولی جز به خودشان نیرنگ نمی زنند، و [این واقعیت را] درک نمی کنند.'),
(913, 6, 124, 'و چون آیه و معجزه ای [بخاطر هدایت یافتنشان] به سوی آنان آید، می گویند: هرگز ایمان نمی آوریم تا نظیر آنچه به پیامبران خدا داده شده به ما هم داده شود. خدا داناتر است که مقام رسالت را در کجا قرار دهد؛ به زودی آنان را که گناه کرده اند به کیفر نیرنگی که [همواره برای گمراه کردن مردم] می زدند، در پیشگاه خدا ذلت و خواری و عذابی سخت خواهد رسید.'),
(914, 6, 125, 'پس کسی را که خدا بخواهد هدایت کند، سینه اش را برای [پذیرفتن] اسلام می گشاید؛ و کسی را که [به خاطر لجاجت و عنادش] بخواهد گمراه نماید، سینه اش را چنان تنگ می کند که گویی به زحمت در آسمان بالا می رود؛ خدا این گونه پلیدی را بر کسانی که ایمان نمی آورند، قرار می دهد.'),
(915, 6, 126, 'و این [قرآن و اسلام] راه راست پروردگار توست؛ ما آیات را برای گروهی که متذکّر می شوند، بیان کردیم.'),
(916, 6, 127, 'برای آنان نزد پروردگارشان خانه سلامت و امن است؛ و به پاداش کارهای پسندیده ای که همواره انجام می دادند یار و سرپرست آنان است.'),
(917, 6, 128, 'و [یاد کنید] روزی که همه جن و انس را جمع می کنیم [و به طایفه جن می گوییم:] ای گروه جن! شما [با وسوسه و اغواگریِ خود] در بسیاری از انسان ها میل و رغبت کردید [تا آنان را گمراه کنید] و دوستانشان از میان انسان ها می گویند: پروردگارا! هر یک از ما دو گروه [پیشوایان و پیروان] از دیگری بهره برداری کردیم [ما پیروان به بهره های نامشروع رسیدیم، و پیشوایان از منحرف کردن ما بهره ناروا بردند] تا به آن مدت از عمری که برای ما مقرّر کرده بودی، رسیدیم. [خدا] می گوید: آتش، جایگاه شماست، در آن جاودانه اید، مگر آنکه خدا نجات شما را بخواهد؛ یقیناً پروردگارت حکیم و داناست.'),
(918, 6, 129, 'این گونه برخی ستمکاران را بر برخی به کیفر گناهانی که همواره مرتکب می شدند، مسلط و چیره می کنیم.'),
(919, 6, 130, '[در قیامت خطاب می رسد:] ای گروه جن و انس! آیا برای شما از جنس خودتان پیامبرانی نیامدند که همواره آیات مرا بر شما می خواندند و شما را از دیدار امروزتان هشدار می دادند؟ می گویند: ما به زیان خود گواهی می دهیم [که آمدند، ولی ما تکذیبشان کردیم] و [جلوه های پر زرق و برق] زندگی دنیا آنان را فریفت، و [اکنون در این عرصه هول انگیز] به زیان خود گواهی می دهند که [در دنیا] کافر بودند.'),
(920, 6, 131, 'این [فرستادن پیامبران] برای این است که پروردگارت مردم شهرها و آبادی ها را در حالی که اهلشان [پیش از آمدن پیامبران] بی خبر [از توحید و معاد و حقایق] باشند، از روی ستم هلاک نمی کند.'),
(921, 6, 132, 'و [در قیامت] برای هر کدام [از دو گروه جن و انس] به سزای آنچه انجام می دادند، درجاتی [از ثواب و عذاب] است؛ و پروردگارت از آنچه انجام می دهند، بی خبر نیست.'),
(922, 6, 133, 'و پروردگارت بی نیاز و صاحب رحمت است، اگر بخواهد همه شما را هلاک می نماید، و پس از شما هر نسلی که بخواهد جایگزین شما می کند، همان گونه که شما را از نسل گروهی دیگر پدید آورد.'),
(923, 6, 134, 'بی تردید آنچه [از ثواب و عقاب] به شما وعده می دهند، آمدنی است؛ و شما عاجز کننده خدا نیستید [تا بتوانید از دسترس قدرتش بیرون روید.]'),
(924, 6, 135, 'بگو: ای قوم! تا جایی که در قدرت شماست [برای مبارزه با من و آیینم] بکوشید، من هم [در انجام دادن وظایفم] می کوشم، سپس خواهید دانست که سرانجام خوش و نیکوی سرای آخرت برای کیست؟ قطعاً ستمکاران رستگار نمی شوند.'),
(925, 6, 136, 'و مشرکان از زراعت و چهارپایانی که خدا آفریده برای او سهمی قرار دادند، و به گمان بی اساس خود گفتند: این سهم خدا، و این سهم بُتان ما؛ [و عقیده داشتند] آنچه برای بتانشان باشد به خدا نمی رسد و آنچه برای خدا باشد [در صورتی که سهم بتان کمبودی داشته باشد] به آنها می رسد؛ بد است آنچه داوری می کنند.'),
(926, 6, 137, 'مانند [این سهم بندی بی اساس که هوای نفسشان در نظرشان آراست] بتان، کشتن فرزندانشان را در نظرشان آراستند تا سرگردان و هلاکشان سازند و دینشان را بر آنان مُشتبه [و آمیخته به بدعت ها و خرافات] کنند؛ اگر خدا می خواست [اجباراً] آن کار را نمی کردند [ولی اجبار به عمل و ترک آن مشمول خواست خدا نیست؛] پس آنان را با آنچه دروغ می سازند واگذار.'),
(927, 6, 138, 'و به گمان بی اساس خود گفتند: این چهار پایان و زراعتی که [سهم خدا و بتان است، خوردنش] ممنوع است، فقط کسی که ما بخواهیم باید از آنها بخورد، و [اینها] چهارپایانی است که سوار شدن بر آنان حرام است، و [نیز آنان را] چهارپایانی [بود] که [هنگام ذبح] نام خدا را بر آنها نمی بردند، در حالی که [این سنت ها و قوانین را به دروغ] به خدا نسبت می دادند؛ به زودی خدا آنان را در برابر آنچه دروغ می بستند، مجازات خواهد کرد.'),
(928, 6, 139, 'و گفتند: آنچه در شکم این چهارپایان است [در صورتی که زنده بیرون آید] ویژه مردان ماست، و بر همسرانمان حرام است، و اگر مرده باشد همگی در آن شریکند؛ به زودی خدا آنان را بر این توصیف بی پایه و باطلشان جزا خواهد داد، زیرا خدا حکیم و داناست.'),
(929, 6, 140, 'قطعاً کسانی که فرزندان خود را از روی سبک مغزی و جهالت کشته اند، و آنچه را خدا روزی آنان کرده بود بر پایه دروغ بستن به خدا حرام شمرده اند، زیان کردند؛ به راستی که گمراه شدند و هدایت یافته نبودند.'),
(930, 6, 141, 'و اوست که باغ هایی [که درختانش برافراشته] بر داربست و باغ هایی [که درختانش] بدون داربست [است]، و درخت خرما، و کشتزار با محصولات گوناگون، و زیتون، و انار شبیه به هم و بی شباهت به هم را پدید آورد. از میوه های آنها هنگامی که میوه داد بخورید، و حقِّ [الهیِ] آن را روز دروکردنش [که به تهیدستان اختصاص داده شده] بپردازید، و از اسراف [در خوردن وخرج کردن] بپرهیزید، که قطعاً خدا اسراف کنندگان را دوست ندارد.'),
(931, 6, 142, 'و از دام ها، حیوانات باربر و حیوانات کُرک دار و پشم دهنده را [آفرید]؛ از آنچه خدا روزی شما کرده بخورید، و از گام های شیطان پیروی نکنید، که بی تردید او برای شما دشمنی آشکار است.'),
(932, 6, 143, '[از حیوانات حلال گوشت و قابل استفاده شما] هشت نوع همراه با جفتش [را آفرید] از گوسفند یک نر و یک ماده، از بز یک نر و یک ماده، بگو: آیا خدا، نر گوسفند و بز، یا ماده گوسفند و بز را یا آنچه را که رحم آن دو ماده در برگرفته حرام کرده؟ اگر راستگویید از روی علم و دانش به من خبر دهید.'),
(933, 6, 144, 'و از شتر یک نر و یک ماده و از گاو یک نر و یک ماده [را آفرید] بگو: آیا خدا نرینه شتر و گاو یا ماده هر دو را یا آنچه را که رحم آن دو ماده دربرگرفته حرام کرده؟ یا زمانی که خدا شما را به این [حرام ساختگی] سفارش کرد، حاضر بودید، پس ستمکارتر از کسی که به خدا دروغ بندد تا مردم را از روی نادانی و جهالت گمراه کند، کیست؟! مسلماً خدا گروه ستمکاران را هدایت نمی کند.'),
(934, 6, 145, 'بگو: در احکامی که به من وحی شده [خوراک] حرامی را بر خورنده ای که میل دارد آن را بخورد نمی یابم، مگر آنکه مردار یا خون ریخته شده [از رگ های حیوان] یا گوشت خوک باشد که یقیناً همه نجس و پلیدند، یا حیوانی که از روی نافرمانی از دستور خدا [هنگام ذبح] نام غیر خدا بر آن برده شده؛ پس کسی که [برای نجات جانش از خطر] به خوردن آنها ناچار شود در حالی که خواهان لذت نباشد و از حدّ لازم تجاوز نکند [گناهی بر او نیست؛] یقیناً پروردگارت بسیار آمرزنده و مهربان است.'),
(935, 6, 146, 'و هر حیوان ناخن دار، و پیه گاو و گوسفند را بر یهودیان حرام کردیم مگر چربی هایی که بر پشت یا روده های آنان است یا آنچه آمیخته و مخلوط با استخوان است؛ این [تحریم] را به سبب ستم کردنشان به آنان کیفر دادیم، و یقیناً ما راستگوییم.'),
(936, 6, 147, 'اگر تو را تکذیب کردند، بگو: پروردگارمان صاحب رحمتی گسترده است، ولی عذاب او هم از گروه مجرمان بازگردانده نخواهد شد.'),
(937, 6, 148, 'کسانی که برای خدا شریک و همتا قرار دادند به زودی خواهند گفت: اگر خدا می خواست نه ما شرک می ورزیدیم و نه پدرانمان، و نه چیزی [از حلال خدا را خودسرانه] حرام می کردیم [پس شرک و تحریم ما جبری بوده نه اختیاری] کسانی که پیش از اینان بودند [بر اساس همین عقیده باطل پیامبران را] تکذیب کردند تا عذاب و انتقام ما را چشیدند؛ بگو: آیا نزد شما دانشی [استوار و منطقی مُستدل بر عقایدتان] هست که آن را برای ما آشکار کنید؟ شما فقط از پندارهای واهی و پوچ پیروی می کنید و جز به حدس و گمان تکیه نمی زنید.'),
(938, 6, 149, 'بگو: دلیل و برهان رسا [که قابل ردّ، ایراد، شک و تردید نیست] مخصوص خداست، و اگر خدا می خواست قطعاً همه شما را [به طور جبر] هدایت می کرد.'),
(939, 6, 150, '[به مدعیان تحریم های خودسرانه] بگو: گواهان خود را که گواهی می دهند بر اینکه خدا این امور را حرام کرده بیاورید. پس اگر گواهان هم گواهی دادند تو با آنان گواهی مده؛ [زیرا گواهی دادنشان ناحق و نارواست] و از هواهای نفسانی آنان که آیات ما را تکذیب کردند، و کسانی که به آخرت ایمان ندارند، و برای پروردگارشان شریک و همتا قرار می دهند، پیروی مکن.'),
(940, 6, 151, 'بگو: بیایید تا آنچه را پروردگارتان بر شما حرام کرده بخوانم: اینکه چیزی را شریک او قرار مدهید، و به پدر و مادر نیکی کنید، و فرزندانتان را از [ترسِ] تنگدستی نکشید، ما شما و آنان را روزی می دهیم، و به کارهای زشت چه آشکار و چه پنهانش نزدیک نشوید، و انسانی را که خدا محترم شمرده جز به حق نکشید؛ خدا این [گونه] به شما سفارش کرده تا بیندیشید.'),
(941, 6, 152, 'و به مال یتیم جز به روشی که نیکوتر است، نزدیک نشوید تا به حدّ بلوغِ [بدنی و عقلیِ] خود برسد، و پیمانه و ترازو را بر اساس عدالت و انصاف کامل و تمام بدهید؛ هیچ کس را جز به اندازه توانش تکلیف نمی کنیم؛ و هنگامی که سخن گویید، عدالت ورزید هر چند درباره خویشان باشد، و به پیمان خدا وفا کنید؛ خدا این [گونه] به شما سفارش کرده تا پند گیرید.'),
(942, 6, 153, 'و مسلماً این [برنامه های محکم و استوار و قوانین و مقرّرات حکیمانه] راه راست من است؛ بنابراین از آن پیروی کنید و از راه های دیگر پیروی مکنید که شما را از راه او پراکنده می کند؛ خدا این [گونه] به شما سفارش کرده تا پرهیزکار شوید.'),
(943, 6, 154, 'سپس به موسی کتاب دادیم برای اینکه [نعمت خود را] بر آنان که نیکی کردند کامل کنیم، و برای اینکه همه احکام و معارفی که مورد نیاز بنی اسرائیل بود، تفصیل و توضیح دهیم و برای اینکه هدایت و رحمت [بر آنان] باشد تا به دیدار [پاداش و مقام قرب] پروردگارشان ایمان آورند.'),
(944, 6, 155, 'و این [قرآن] کتابی پرفایده است که ما آن را نازل کردیم پس آن را پیروی کنید و [از مخالفت با آن] بپرهیزید تا مشمول رحمت شوید.'),
(945, 6, 156, '[آری، قرآن را نازل کردیم] تا [شما مشرکان] نگویید: کتاب آسمانی فقط بر دو گروه پیش از ما [یهود و نصاری] نازل شد و [چون به لغت ما نبود] از [یاد گرفتن] قرائت آنان و آموزششان بی خبر ماندیم.'),
(946, 6, 157, 'یا نگویید: اگر کتاب آسمانی بر ما نازل می شد، مسلماً از آنان راه یافته تر بودیم، اینک برهانی آشکار و هدایت و رحمتی از سوی پروردگارتان برای شما آمد، پس ستمکارتر از کسی که آیات خدا را تکذیب کند و از آن ها روی بگرداند کیست؟ به زودی کسانی [را] که از آیات ما روی می گردانند، به خاطرِ روی گرداندنشان به عذابی سخت مجازات خواهیم کرد.'),
(947, 6, 158, 'آیا جز این انتظار دارند که فرشتگانِ [عذاب] به سویشان آیند، یا [قیامت] پروردگارت [که روز جلوه کامل حق و ظهور وانکشاف تام توحید است] بیاید، یا برخی از نشانه های پروردگارت [مانند مرگ و آثار پس از آن] تحقق یابد؟ روزی که برخی از نشانه های پروردگارت به ظهور آید، ایمان آوردن کسی که پیش از این ایمان نیاورده یا در حال ایمانش عمل شایسته ای انجام نداده سودی نخواهد داشت. بگو: [شما با این وضع ناروایی که دارید] منتظر [عذاب] باشید. ما هم منتظریم [که عذاب خدا به شما برسد.]'),
(948, 6, 159, 'مسلماً کسانی که دینشان را بخش بخش کردند، وگروه گروه شدند، تو را هیچ پیوندی با آنان نیست، کار آنان فقط با خداست، سپس به اعمالی که همواره انجام می دادند، آگاهشان می کند.'),
(949, 6, 160, 'هر کس کار نیک بیاورد، پاداشش ده برابر آن است، و آنان که کار بد بیاورند، جز به مانند آن مجازات نیابند و ایشان مورد ستم قرار نمی گیرند.'),
(950, 6, 161, 'بگو: یقیناً پروردگارم مرا به راه راست هدایت کرد، به دینی پایدار و استوار، دین ابراهیم یکتاپرست حق گرا، و او از مشرکان نبود.'),
(951, 6, 162, 'بگو: مسلماً نماز و عبادتم و زندگی کردن و مرگم برای خدا پروردگار جهانیان است.'),
(952, 6, 163, 'او را شریکی نیست، و به این [یگانه پرستی] مأمورم، و نخستین کسی هستم که [در این آیین] تسلیم [فرمان ها و احکام] اویم.'),
(953, 6, 164, 'بگو: آیا جز خدا پروردگاری بجویم در حالی که او پروردگار هر چیزی است؟! و هیچ کس جز به زیان خود [عمل زشتی] مرتکب نمی شود، و هیچ سنگین باری بار گناه دیگری را بر نمی دارد؛ سپس بازگشت همه شما به سوی پروردگارتان خواهد بود، پس شما را به [حقّانیّت] آنچه درباره آن [با مردم مؤمن] اختلاف می کردید، آگاه می کند.'),
(954, 6, 165, 'اوست کسی که شما را در زمین، جانشینان [نسل های گذشته] قرار داد، و [پایه های مادی و معنوی] برخی از شما را بر برخی دیگر به درجاتی بالا برد تا شما را در آنچه به شما عطا کرده، بیازماید؛ قطعاً پروردگارت زود کیفر است، و یقیناً بسیار آمرزنده و مهربان است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المص'),
(956, 7, 2, 'این کتابی [با عظمت] است که به سوی تو نازل شده است؛ پس نباید در سینه ات از ناحیه [تبلیغِ] آن تنگی و فشار باشد، تا به وسیله آن مردم را بیم دهی، و برای مؤمنان [مایه] تذکّر و پند باشد.'),
(957, 7, 3, 'آنچه را از سوی پروردگارتان به جانب شما نازل شده پیروی کنید، و از معبودانی غیر او پیروی ننمایید، ولی بسیار اندک و کم، پند می گیرید.'),
(958, 7, 4, 'چه بسیار شهرهایی که اهلش را [به سبب گناهانشان] هلاک کردیم و عذاب ما در حالی که شب یا نیم روز در استراحت و آرامش بودند، به آنان رسید!!'),
(959, 7, 5, 'زمانی که عذاب ما به سویشان آمد، اعترافشان جز این نبود که گفتند: قطعاً ما ستمکار بوده ایم.'),
(960, 7, 6, '[روز قیامت] از کسانی که پیامبران به سویشان فرستاده شده [درباره پذیرفتن و نپذیرفتن رسالت پیامبران]، و از شخص پیامبران [درباره تبلیغ دین] به طور یقین پرسش خواهیم کرد.'),
(961, 7, 7, 'بی تردید برای آنان [در آن روز، عقاید، اعمال و اخلاقشان را] از روی دانشی [دقیق و فراگیر] بیان خواهیم کرد؛ [زیرا] ما هیچ گاه از آنان غایب نبوده ایم.'),
(962, 7, 8, 'میزان [سنجش اعمال] در آن روز حق است؛ پس کسانی که اعمال وزن شده آنان سنگین و با ارزش باشد، رستگارند.'),
(963, 7, 9, 'و کسانی که اعمال وزن شده آنان سبک و بی ارزش باشد، به سبب اینکه همواره به آیات ما ستم می ورزیدند، به خود زیان زده [و سرمایه وجودشان راتباه کرده] اند.'),
(964, 7, 10, 'و شما را در زمین جای دادیم، و در آن برای شما وسایل و ابزارزندگی قرار دادیم، ولی بسیار اندک و کم، سپاس می گزارید.'),
(965, 7, 11, 'و شما را آفریدیم، سپس شما را صورت گری کردیم، آن گاه به فرشتگان گفتیم: بر آدم سجده کنید؛ بی درنگ همه سجده کردند، جز ابلیس که از سجده کنندگان نبود.'),
(966, 7, 12, 'خدا فرمود: هنگامی که تو را امر کردم، چه چیز تو را مانع شد که سجده نکردی؟ گفت: من از او بهترم، مرا از آتش پدید آورده ای و او را از گِل آفریدی.'),
(967, 7, 13, 'خدا فرمود: از این جایگاه و منزلتت [که عرصه فروتنی و فرمانبرداری است] فرود آی؛ زیرا تو را نرسد که در این جایگاه بلند، بزرگ منشی کنی پس بیرون شو که قطعاً از خوارشدگانی.'),
(968, 7, 14, 'گفت: مرا تا روزی که [مردگان] برانگیخته شوند، مهلت ده.'),
(969, 7, 15, 'خدا فرمود: البته تو از مهلت یافتگانی.'),
(970, 7, 16, 'گفت: به سبب اینکه مرا به بیراهه و گمراهی انداختی، یقیناً بر سر راه راست تو [که رهروانش را به سعادت ابدی می رساند] در کمین آنان خواهم نشست.'),
(971, 7, 17, 'سپس از پیش رو و پشت سر و از طرف راست و از جانب چپشان بر آنان می تازم و [تا جایی آنان را دچار وسوسه و اغواگری می کنم که] بیشترشان را سپاس گزار نخواهی یافت.'),
(972, 7, 18, 'خدا فرمود: از این جایگاه و منزلتت نکوهیده و مطرود بیرون شو که قطعاً هر که از آنان از تو پیروی کند، بی تردید جهنم را از همه شما لبریز خواهم کرد.'),
(973, 7, 19, 'و [گفتیم:] ای آدم! تو و همسرت در این بهشت سکونت گیرید، و از هر جا [و هر نوع میوه ای] که خواستید بخورید، و به این درخت نزدیک مشوید که ازستمکاران [بر خود] خواهید شد.'),
(974, 7, 20, 'پس شیطان، آن دو را وسوسه کرد تا آنچه از شرمگاه بدنشان بر آنان پوشیده بود نمایان کند، [وسوسه اش این بود که به هر دو] گفت: پروردگارتان شما را از این درخت نهی نکرده مگر از این جهت که مبادا دو فرشته گردید، یا از جاودانان شوید.'),
(975, 7, 21, 'و برای هر دو سوگند سخت و استوار یاد کرد که یقیناً من برای شما از خیر خواهانم [و قصد فریب شما را ندارم.]'),
(976, 7, 22, 'پس آن دو را با مکر و فریب از مقام و منزلتشان فرود آورد [و به خوردن درخت ممنوعه نزدیک کرد]، هنگامی که از آن درخت چشیدند، شرمگاه بدنشان بر دو نفرشان نمایان شد و هر دو دست به کار چسبانیدن برگ [درختان] بهشت به خود شدند؛ و پروردگارشان بر آن دو بانگ زد: آیا من شما را از آن درخت نهی نکردم، وبه شما نگفتم: بی تردید شیطان برای شما دشمنی آشکار است؟!'),
(977, 7, 23, 'گفتند: پروردگارا! ما بر خود ستم ورزیدیم، و اگر ما را نیامرزی و به ما رحم نکنی مسلماً از زیانکاران خواهیم بود.'),
(978, 7, 24, 'خدا فرمود: از این جایگاه و مقام فرود آیید، در حالی که دشمن یکدیگرید، و برای شما در زمین تا مدتی قرارگاه و مایه برخورداری است.'),
(979, 7, 25, 'پروردگار فرمود: در آن زندگی می کنید، و در آن می میرید، و از آن بیرون می آیید.'),
(980, 7, 26, 'ای فرزندان آدم! ما لباسی که شرمگاهتان را می پوشاند، و لباسی فاخر و گران که مایه زینت و جمال است، برای شما نازل کردیم؛ و لباس تقوا [که انسان را از آلودگی های ظاهر و باطن بازمی دارد] بهتر است، و این [لباس که با استفاده از آن انسان به سعادت ابدی می رسد] از نشانه های خداست، باشد که متذکّر [این حقیقت] شوند.'),
(981, 7, 27, 'ای فرزندان آدم! شیطان، شما را نفریبد چنان که پدر و مادرتان را [با فریبکاریش] از بهشت بیرون کرد، لباسشان را از اندامشان بر می کشید تا شرمگاهشان را به آنان بنمایاند، او و دار و دسته اش شما را از آنجا که شما آنان را نمی بینید می بینند، ما شیاطین را سرپرست و یاران کسانی قرار دادیم که ایمان نمی آورند.'),
(982, 7, 28, 'چون کار زشتی مرتکب می شوند، می گویند: پدرانمان را بر آن [کار] یافتیم و خدا ما را به آن فرمان داده. بگو: یقیناً خدا به کار زشت فرمان نمی دهد، آیا چیزی را که نمی دانید [از روی جهل و نادانی] به خدا نسبت می دهید؟!'),
(983, 7, 29, 'بگو: پروردگارم به میانه روی [در همه امور و به اجتناب از افراط و تفریط] فرمان داده و [امر فرموده]: در هر مسجدی [به هنگام عبادت] روی [دل] خود را [آن گونه] متوجه خدا کنید [که از هر چیزی غیر او مُنقطع شود]، و او را در حالی که ایمان و عبادت را برای وی از هر گونه شرکی خالص می کنید بخوانید؛ همان گونه که شما را آفرید، [پس از مرگ به او] بازمی گردید.'),
(984, 7, 30, 'در حالی که گروهی را [به خاطر داشتن لیاقت] هدایت کرد، و گروهی [به سبب نداشتن شایستگی] گمراهی بر آنان ثابت و قطعی شد، چون اینان شیاطین را به جای خدا سرپرستان و دوستان خود گرفتند، وگمان می کنند که راه یافتگانند!!'),
(985, 7, 31, 'ای فرزندان آدم! [هنگام هر نماز و] در هر مسجدی، آرایش و زینتِ [مادی و معنوی خود را متناسب با آن عمل و مکان] همراه خود برگیرید، و بخورید و بیاشامید و اسراف نکنید؛ زیرا خدا اسراف کنندگان را دوست ندارد.'),
(986, 7, 32, 'بگو: زینت های خدا و روزی های پاکیزه ای را که برای بندگانش پدید آورد، چه کسی حرام کرده؟! بگو: این [زینتها و روزی های پاکیزه] در زندگی دنیا برای کسانی است که ایمان آورده اند [البته اگر چه غیر مؤمنان هم با آنان در بهرهوری شریکند، ولی] در قیامت فقط ویژه مؤمنان است؛ ما این گونه آیات خود را برای گروهی که می دانند [و اهل دانش و بصیرت اند] بیان می کنیم.'),
(987, 7, 33, 'بگو: پروردگارم فقط کارهای زشت را چه آشکارش باشد و چه پنهانش، و گناه و ستم ناحق را، و اینکه چیزی را که خدا بر حقّانیّت آن دلیلی نازل نکرده شریک او قرار دهید، و اینکه اموری را از روی نادانی و جهالت به خدا نسبت دهید، حرام کرده است.'),
(988, 7, 34, 'برای هر امتی زمانی [معین و اجلی محدود] است، هنگامی که اجلشان سرآید، نه ساعتی پس می مانند و نه ساعتی پیش می افتند.'),
(989, 7, 35, 'ای فرزندان آدم! چون پیامبرانی از جنس خودتان به سویتان آیند که آیاتم را بر شما بخوانند [به آنان ایمان آورید و آیاتم را عمل کنید]؛ پس کسانی که [از مخالفت با آنان] بپرهیزند و [مفاسد خود را] اصلاح کنند، نه بیمی بر آنان است و نه اندوهگین شوند.'),
(990, 7, 36, 'و آنان که آیات ما را تکذیب کردند، و از پذیرفتن آنها تکبّر ورزیدند اهلِ آتش و در آن جاودانه اند.'),
(991, 7, 37, 'پس ستمکارتر از کسانی که بر خدا دروغ می بندند، یا آیات او را تکذیب می کنند، کیست؟! آنانند که سهمشان از آنچه که [از نعمت ها] مقرّر و مقدّر شده به آنان می رسد. تا هنگامی که فرستادگان ما برای ستاندن جانشان به سویشان آیند، می گویند: بت ها و معبودهایی که به جای خدا می پرستیدید، کجایند؟ [تا شما را از سختی های مرگ و پس از آن نجات دهند] می گویند: از دست ما رفتند؛ و بر ضد خود گواهی دهند که کافر بوده اند.'),
(992, 7, 38, 'خدا می فرماید: شما هم در میان گروه هایی از جن و انس که پیش از شما بودند در آتش درآیید، هرگاه گروهی وارد شوند، هم مسلکان خود را لعنت کنند تا آنکه همه آنان با خفت و خواری در آتش جمع شوند، آن گاه پیروانشان درباره پیشوایانشان گویند: پروردگارا! اینان ما را به گمراهی کشیدند، پس عذابشان را از آتش، دو چندان گردان؛ خدا می فرماید: برای هر کدامتان دو چندان است، ولی نمی دانید.'),
(993, 7, 39, 'و پیشوایانشان به پیروانشان گویند: شما را بر ماهیچ برتری و امتیازی نیست [که عذابتان کمتر باشد] پس به کیفر آن اعمال زشتی که مرتکب می شدید، عذاب را بچشید.'),
(994, 7, 40, 'قطعاً کسانی که آیات ما را تکذیب کردند، و از پذیرفتن آنها تکبّر ورزیدند، درهای آسمان [برای نزول رحمت] بر آنان گشوده نخواهد شد، و در بهشت هم وارد نمی شوند مگر آنکه شتر در سوراخ سوزن درآید!! [پس هم چنانکه ورود شتر به سوراخ سوزن محال است، ورود آنان هم به بهشت محال است؛] این گونه گنهکاران را کیفر می دهیم.'),
(995, 7, 41, 'برای آنان بستری از دوزخ و برفرازشان پوشش هایی از آتش است، و ما این گونه ستمکاران را پاداش می دهیم.'),
(996, 7, 42, 'و کسانی که ایمان آوردند و [به اندازه طاقت خود] کارهای شایسته انجام دادند، اهل بهشت اند و در آن جاودانه اند، [چرا که] ما هیچ کس را جز به اندازه طاقتش تکلیف نمی کنیم.'),
(997, 7, 43, 'و آنچه از کینه و خشم در سینه های آنان است، بَر می کنیم [تا در بهشت با خوشی و سلامت کامل کنار هم زندگی کنند؛] از زیر [کاخ ها و عمارت های] شان نهرها جاری است و می گویند: و همه ستایش ها ویژه خداست که ما را به این [نعمت ها] هدایت کرد، و اگر خدا ما را هدایت نمی کرد هدایت نمی یافتیم، مسلماً پیامبران پروردگارمان حق را به سوی ما آوردند، و ندایشان می دهند به پاداش اعمال شایسته ای که همواره انجام می دادید، این بهشت را به ارث بردید.'),
(998, 7, 44, 'و بهشتیان، دوزخیان را آواز می دهند که ما آنچه را که پروردگارمان به ما وعده داده بود، حق یافتیم، آیا شما هم آنچه را که پروردگارتان وعده داده بود، حق یافتید؟ گویند: آری؛ پس آواز دهنده ای در میان آنان آواز دهد که لعنت خدا بر ستمکاران باد.'),
(999, 7, 45, 'هم آنان که مردم را همواره از راه خدا بازمی دارند و می خواهند آن را [با وسوسه و اغواگری] کج نشان دهند، و ایشان به روز قیامت کافرند.'),
(1000, 7, 46, 'و میان آن دو [گروه بهشتیان و دوزخیان] حائلی است، و بر اعراف، مردانی [با مقام و منزلت اند] که هر کدام از دو گروه را به نشانه هایشان می شناسند، و بهشتیان را که وارد بهشت نشده اند، ولی ورود به آن را امید دارند، آواز می دهند که درود بر شما.'),
(1001, 7, 47, 'و چون بهشتیان، چشمانشان ناخواسته به سوی دوزخیان گردانده شود، گویند: پروردگارا! ما را با گروه ستمکاران قرار مده.'),
(1002, 7, 48, 'و اهل اعراف مردانی را که از نشانه هایشان می شناسند، آواز می دهند [و به عنوان سرزنش] می گویند: [امکانات مادیو] جمعیت [انسانی] شما و تکبّری که می ورزیدید، عذابی را از شما دفع نکرد.'),
(1003, 7, 49, '[سپس با توجه دادن دوزخیان به سوی اهل ایمان می گویند:] آیا اینان کسانی نبودند که در دنیا سوگند یاد می کردید که خدا آنان را به رحمتی نمی رساند؟ [پس به مؤمنان می گویند:] به بهشت در آیید که نه بیمی برشماست و نه اندوهگین می شوید.'),
(1004, 7, 50, 'و دوزخیان بهشتیان را آواز می دهند که از آب [خوشگوار] یا از آنچه خدا روزی شما فرموده بر ما فرو ریزید. [بهشتیان] گویند: خدا آب و نعمت های بهشتی را بر کافران حرام کرده است.'),
(1005, 7, 51, 'آنان که دینشان را سرگرمی و بازی گرفتند و زندگی دنیا آنان را فریفت، پس ما امروز از یاد می بریمشان، همان گونه که آنان دیدار امروزشان را از یاد بردند وهمواره آیات ما را انکار می کردند.'),
(1006, 7, 52, 'و برای آنان کتابی [عظیم و با ارزش] آوردیم که آن را از روی دانشی [گسترده و دقیق] بیان کردیم، در حالی که برای گروهی که ایمان آوردند، سراسر هدایت و رحمت است.'),
(1007, 7, 53, 'آیا [منکران قرآن برای باور کردن آن] جز تحقّق وعده هایش را انتظار می برند؟! روزی که حقایق بیان شده در آن [به صورتی ظاهر و آشکار] بیاید، کسانی که از پیش، آن را فراموش کرده بودند، می گویند: یقیناً فرستادگان پروردگارمان حق را آوردند [ولی ما آن را نپذیرفتیم،] پس آیا [در چنین روزی] شفاعت کنندگانی برای ما هست که ما را شفاعت کنند یا [به دنیا] بازگردانده می شویم تا کاری غیر آنچه انجام می دادیم، انجام دهیم؟! به راستی آنان [سرمایه] وجودشان را تباه کردند و آنچه را به دروغ [به عنوان شریک خدا به خدا] نسبت می دادند [از دستشان رفت و] گم شد.'),
(1008, 7, 54, 'به یقین پروردگار شما خداست که آسمان ها و زمین را در شش روز آفرید، سپس بر تخت [فرمانروایی و تدبیر امور آفرینش] چیره و مسلط شد، شب را در حالی که همواره با شتابْ روز را می جوید بر روز می پوشاند، و خورشید و ماه و ستارگان را که مُسخّر فرمان اویند [پدید آورد]؛ آگاه باشید که آفریدن و فرمان [نافذ نسبت به همه موجودات] مخصوص اوست؛ همیشه سودمند و با برکت است، پروردگار عالمیان.'),
(1009, 7, 55, 'پروردگارتان را از روی فروتنی و زاری و مخفیانه بخوانید [و از آداب و شرایط دعا تجاوز نکنید]؛ یقیناً خدا متجاوزان را دوست ندارد.'),
(1010, 7, 56, 'و در زمین پس از اصلاح آن [به وسیله رسالت پیامبران] فساد مکنید، و خدا را از روی بیم و امید بخوانید، که یقیناً رحمت خدا به نیکوکاران نزدیک است.'),
(1011, 7, 57, 'و اوست که بادها را پیشاپیشِ [بارانِ] رحمتش به عنوان مژده دهنده می فرستد تا هنگامی که ابرهای سنگین بار را بردارند، آن را به سوی سرزمینی مرده می رانیم، پس به وسیله آن باران نازل می کنیم و به وسیله باران از هر نوع میوه [از زمین] بیرون می آوریم [و] مردگان را نیز [در روز قیامت] این گونه [از لابلای گورها] بیرون می آوریم، [باد، ابر، باران، زمین، روییدن گیاهان و انواع میوه ها را مثل زدیم] تا متذکّر و یادآور [اراده و قدرت بی نهایت خدا] شوید.'),
(1012, 7, 58, 'و زمین پاک است که گیاهش به اذن پروردگارش بیرون می آید، و زمینی که ناپاک است، جز گیاهی اندک و بی سود از آن بیرون نمی آید؛ این گونه نشانه ها را برای گروهی که سپاس گزارند [به صورت های گوناگون] بیان می کنیم.'),
(1013, 7, 59, 'به یقین، نوح را به سوی قومش فرستادیم، پس به آنان گفت: ای قوم من! خدا را بپرستید، که شما را جز او معبودی نیست، من قطعاً از عذاب روزی بزرگ بر شما می ترسم.'),
(1014, 7, 60, 'اشراف و سران قومش گفتند: مسلماً ما تو را در گمراهی آشکار می بینیم!'),
(1015, 7, 61, 'گفت: ای قوم من! هیچ گمراهی و انحرافی در من نیست، بلکه من فرستاده ای از سوی پروردگار جهانیانم.'),
(1016, 7, 62, 'پیام های پروردگارم را به شما می رسانم، و برای شما خیرخواهی می کنم و از سوی خدا حقایقی را می دانم که شما نمی دانید.'),
(1017, 7, 63, 'آیا تعجب کردید که بر مردی از جنس خودتان معارفی از سوی پروردگارتان آمده تا شما را [از عذاب دنیا و آخرت] بیم دهد، و تا شما پرهیزکاری کنید و برای اینکه مورد رحمت قرار گیرید؟!'),
(1018, 7, 64, 'پس او را تکذیب کردند، ما هم او و کسانی که در کشتی همراهش بودند، نجات دادیم و کسانی که آیات ما را تکذیب کردند، غرق نمودیم؛ زیرا آنان گروهی کوردل بودند.'),
(1019, 7, 65, 'و به سوی قوم عاد، برادرشان هود را فرستادیم، گفت: ای قوم من! خدا را بپرستید که شما را جز او معبودی نیست، آیا [از زشتی ها] نمی پرهیزید؟'),
(1020, 7, 66, 'اشراف و سران قومش که کافر بودند گفتند: ما تو را در سبک مغزی و نادانی می بینیم و تو را از دروغگویان می پنداریم!!'),
(1021, 7, 67, 'گفت: ای قوم من! در من هیچ سبک مغزی و نادانی نیست، بلکه من فرستاده ای از سوی پروردگار جهانیانم.'),
(1022, 7, 68, 'پیام های پروردگارم را به شما می رسانم و برای شما خیرخواهی اَمینم.'),
(1023, 7, 69, 'آیا تعجب کردید که بر مردی از جنس خودتان معارفی از سوی پروردگارتان آمده تا شما را [از عذاب دنیا و آخرت] بیم دهد؟! و به یاد آورید که شما را جانشینانی پس از قوم نوح قرار داد، و شما را در آفرینش [جسم و جان] نیرومندی و قدرت افزود، پس نعمت های خدا را به یاد آورید تا رستگار شوید.'),
(1024, 7, 70, 'گفتند: آیا به سوی ما آمده ای که ما فقط خدا را بپرستیم، و آنچه را پدرانمان می پرستیدند واگذاریم؟ اگر از راستگویانی آنچه را از عذاب و گزند به ما وعده می دهی برای ما بیاور.'),
(1025, 7, 71, 'گفت: یقیناً از سوی پروردگارتان بر شما عذاب و خشمی مقرّر شده، آیا درباره نام های [بی هویتی] که خود و پدرانتان بت ها را به آن نامیده اید، و خدا هیچ دلیل و برهانی بر [حقّانیّت] آنان نازل نکرده با من مجادله و ستیزه می کنید؟ پس منتظر [عذاب خدا] باشید و من هم با شما از منتظرانم.'),
(1026, 7, 72, 'پس [هنگام نزول عذاب] او و کسانی را که همراهش بودند به رحمتی از سوی خود نجات دادیم و بنیاد آنان که آیات ما را تکذیب کردند و مؤمن نبودند، برکندیم.'),
(1027, 7, 73, 'و به سوی قوم ثمود، برادرشان صالح را فرستادیم، گفت: ای قوم من! خدا را بپرستید که شما را جز او معبودی نیست، برای شما دلیلی روشن از جانب پروردگارتان آمده است، این ماده شتر [از سوی] خدا برای شما نشانه ای [بر صدق رسالت من] است، پس او را واگذارید تا در زمین خدا بخورد و آزار و گزندی به او نرسانید که عذابی دردناک شما را خواهد گرفت.'),
(1028, 7, 74, 'و به یاد آورید که خدا شما را جانشینانی پس از قوم عاد قرار داد، و در زمین، جای [مناسبی] به شما بخشید که از مکان های هموارش برای خود قصرها بنا می کنید، و از کوه ها خانه هایی می تراشید، پس نعمت های خدا را یاد کنید و در زمین تبهکارانه فتنه و آشوب برپا نکنید.'),
(1029, 7, 75, 'اشراف و سران قومش که تکبّر و سرکشی می ورزیدند به مستضعفانی که ایمان آورده بودند، گفتند: آیا شما یقین دارید که صالح از سوی پروردگارش فرستاده شده؟ گفتند: به طور یقین ما به آیینی که فرستاده شده مؤمنیم.'),
(1030, 7, 76, 'مستکبران گفتند: ما به آیینی که شما به آن ایمان آوردید، کافریم!'),
(1031, 7, 77, 'پس آن ماده شتر را پی کردند، و از فرمان پروردگارشان سرپیچی نمودند و به صالح گفتند: اگر از پیامبران هستی عذابی که همواره به ما وعده می دهی، بیاور.'),
(1032, 7, 78, 'پس زلزله ای سخت آنان را فرا گرفت، و در خانه هایشان [به رو درافتاده] جسمی بی جان شدند!'),
(1033, 7, 79, 'پس صالح از آنان روی گرداند و گفت: ای قوم من! قطعاً من پیام پروردگارم را به شما رساندم، و برایتان خیرخواهی کردم، ولی شما خیرخواهان را دوست ندارید.'),
(1034, 7, 80, 'و لوط را [به یاد آورید] هنگامی که به قومش گفت: آیا آن کار بسیار زشت و قبیح را که هیچ کس از جهانیان در آن بر شما پیشی نگرفته است، مرتکب می شوید؟!'),
(1035, 7, 81, 'شما [بدون توجه به حقوق همسران و غافل از اینکه نعمت غریزه جنسی برای بقای نسل است] از روی میل شدیدی که به آن [کار بسیار زشت] دارید به سوی مردان می آیید، [نه فقط در این کار متجاوز از حدود انسانیّت هستید] بلکه در امور دیگر هم گروهی زیاده طلب هستید.'),
(1036, 7, 82, 'وپاسخ قومش جز این نبود که به یکدیگر گفتند: اینان را از شهرتان بیرون کنید؛ زیرا مردمانی اند که همواره خود را پاک نشان می دهند.'),
(1037, 7, 83, 'پس او و اهلش را نجات دادیم مگر همسرش که از باقی ماندگان [در میان کافران] بود.'),
(1038, 7, 84, 'و بر آنان بارشی [بی نظیر از سنگ های آتشین] باراندیم، پس با تأمل بنگر که سرانجام گنهکاران چگونه بود؟!'),
(1039, 7, 85, 'و به سوی مردم مدین، برادرشان شعیب را [فرستادیم،] گفت: ای قوم من! خدا را بپرستید که شما را جز او معبودی نیست، یقیناً برهانی روشن از سوی پروردگارتان برای شما آمد، پس پیمانه و ترازو را تمام و کامل بدهید، و از اجناس و اموال و حقوق مردم مکاهید، و در زمین پس از اصلاح آن [به وسیله رسالت پیامبران] فساد مکنید، این [امور] برای شما بهتر است، اگر مؤمنید.'),
(1040, 7, 86, 'و بر سر هر راهی منشینید که کسانی را که به خدا ایمان آورده اند [به مصادره اموال، شکنجه و باج خواهی] بترسانید، و از راه خدا بازدارید، و بخواهید آن را [با وسوسه و اغواگری] کج نشان دهید؛ و به یاد آوردید زمانی که جمعیت اندکی بودید، ولی [پروردگارتان] شما را فزونی داد، و با تأمل بنگرید که سرانجام مفسدین و تبهکاران چگونه بود؟'),
(1041, 7, 87, 'و اگر گروهی از شما به آیینی که من به آن فرستاده شده ام، ایمان آوردند و گروهی ایمان نیاوردند، پس [شما مؤمنان] شکیبایی ورزید تا خدا میان ما داوری کند، که او بهترین داوران است.'),
(1042, 7, 88, 'اشراف و سران قومش که [از پذیرفتن حق] تکبّر ورزیدند، گفتند: ای شعیب! مسلماً تو و کسانی را که با تو ایمان آورده اند از شهرمان بیرون می کنیم یا اینکه بی چون و چرا به آیین ما بازگردید. گفت: آیا هر چند که نفرت و کراهت [از آن آیین] داشته باشیم؟!'),
(1043, 7, 89, 'اگر پس از آنکه خدا ما را از آن [آیین شرک آلود] نجات داده به آیین شما بازگردیم [و بگوییم: خدا دارای شریک و همتاست] یقیناً بر خدا دروغ بسته ایم؛ بنابراین امکان ندارد به آیین شما بازگردیم، مگر اینکه خدا که پروردگار ماست بخواهد [که او هم مشرک شدن و کافر شدن کسی را هرگز نمی خواهد] پروردگار ما از نظر دانش بر همه چیز احاطه دارد، فقط بر خدا توکل کردیم. پروردگارا! میان ما و قوم ما به حق داوری کن که تو بهترین داورانی.'),
(1044, 7, 90, 'و اشراف و سران کافر قومش [به مردم] گفتند: اگر از شعیب پیروی کنید، مسلماً شما هم از زیانکارانید.'),
(1045, 7, 91, 'پس زلزله ای سخت آنان را فراگرفت و در خانه هایشان [به رو درافتاده] جسمی بی جان شدند!'),
(1046, 7, 92, 'کسانی که شعیب را تکذیب کردند [چنان نابود شدند که] گویی در آن شهر سکونت نداشتند، [آری،] آنان که شعیب را تکذیب کردند، همان زیانکاران بودند.'),
(1047, 7, 93, 'پس شعیب از آنان روی گردانید و گفت: ای قوم من! یقیناً من پیام های پروردگارم را به شما رساندم، و برای شما خیرخواهی کردم، با این حال چگونه بر گروهی که کافرند دریغ و تأسف خورم؟'),
(1048, 7, 94, 'هیچ پیامبری را در شهری نفرستادیم مگر آنکه اهلش را [پس از تکذیب آن پیامبر] به تهیدستی و سختی و رنج و بیماری دچار کردیم، باشد که [به پیشگاه ما] فروتنی و زاری کنند.'),
(1049, 7, 95, 'سپس [هنگامی که این مصایب، مایه بیداری آنان نشد] به جای آن مصایب، رفاه وفراوانی نعمت قرار دادیم تا [ازنظر مال، ثروت، جمعیت و قدرت] فزونی یافتند، [بلکه بیدار شوند ولی رفاه و فراوانی نعمت در بیدار کردنشان مؤثّر نیفتاد] و گفتند: به پدران ما هم به طور طبیعی تهیدستی و سختی و رنج و بیماری رسید [و ربطی به رویارویی ما با پیامبران و خشم خدا نداشت]؛ پس به ناگاه آنان را در حالی که درک نمی کردند [به عذابی سخت و نابود کننده] گرفتیم.'),
(1050, 7, 96, 'و اگر اهل شهرها و آبادی ها ایمان می آوردند و پرهیزکاری پیشه می کردند، یقیناً [درهایِ] برکاتی از آسمان و زمین را بر آنان می گشودیم، ولی [آیات الهی و پیامبران را] تکذیب کردند، ما هم آنان را به کیفر اعمالی که همواره مرتکب می شدند [به عذابی سخت] گرفتیم.'),
(1051, 7, 97, 'آیا اهل شهرها ایمنند از اینکه هنگام شب، عذاب ما در حالی که خوابند بر آنان درآید؟!'),
(1052, 7, 98, 'آیا اهل شهرها ایمنند از اینکه هنگام روز، عذاب ما در حالی که سرگرم [امور دنیایی] اند بر آنان در آید؟!'),
(1053, 7, 99, 'آیا از عذاب و انتقام خدا ایمنند در حالی که جز گروه زیانکاران خود را از عذاب و انتقام خدا ایمن نمی دانند؟!'),
(1054, 7, 100, 'آیا [سرگذشت عبرت آموز پیشینیان] برای کسانی که زمین را پس از صاحبانش به ارث می برند، روشن نکرده که اگر ما بخواهیم آنان را به کیفر گناهانشان می رسانیم و بر دل هایشان مهر می زنیم، پس آنان [دعوت حق را] نشنوند؟'),
(1055, 7, 101, 'این شهرهاست که بخشی از داستان هایش را برای تو بیان می کنیم، و یقیناً پیامبرانشان برای آنان دلایل روشن آوردند، ولی آنان بر آن نبودند که به حقایقی که پیش از آمدن آن دلایل تکذیب کرده بودند ایمان بیاورند؛ این گونه خدا بر دل های کافران [به سزای لجاجت وعنادشان] مُهر می زند.'),
(1056, 7, 102, 'و برای بیشتر آنان هیچ گونه پای بندی وتعهدی [نسبت به وعده هایشان مبنی برایمان آوردن پس از دیدن معجزات ودلایل] نیافتیم، و در حقیقت بیشتر آنان را نافرمان [و پیمان شکن] یافتیم.'),
(1057, 7, 103, 'سپس بعد از پیامبران گذشته موسی را با آیات خود به سوی فرعون و اشراف و سران قومش فرستادیم؛ ولی آنان به آن آیات ستم ورزیدند. پس با تأمل بنگر که سرانجام مفسدین و تبهکاران چگونه بود؟'),
(1058, 7, 104, 'وموسی گفت: ای فرعون! یقیناً من فرستاده ای از سوی پروردگار جهانیانم.'),
(1059, 7, 105, 'سزوار است که درباره خدا سخنی جز حق نگویم. بی تردید من دلیلی روشن [بر صدق رسالتم] از سوی پروردگارتان برای شما آورده ام، [از حکومت ظالمانه ات دست بردار] و بنی اسرائیل را [برای کوچ کردن از این سرزمین] با من روانه کن.'),
(1060, 7, 106, '[فرعون] گفت: اگر [در ادعای پیامبری] از راستگویانی چنانچه معجزه ای آورده ای آن را ارائه کن.'),
(1061, 7, 107, 'پس موسی عصایش را انداخت، پس به ناگاه اژدهایی آشکار شد.'),
(1062, 7, 108, 'و دستش را از گریبانش بیرون کشید که ناگاه دست برای بینندگان سپید و درخشان گشت.'),
(1063, 7, 109, 'اشراف و سران قوم فرعون گفتند: قطعاً این جادوگری [زبردست و] داناست.'),
(1064, 7, 110, 'می خواهد شما را از سرزمینتان بیرون کند؛ اینک [درباره او] چه رأی و نظری می دهید؟'),
(1065, 7, 111, 'گفتند: [مجازات] او و برادرش را به تأخیر انداز و نیروهای گردآورنده را به شهرها روانه کن.'),
(1066, 7, 112, 'تا هر جادوگری دانا را به نزد تو آورند.'),
(1067, 7, 113, 'و جادوگران نزد فرعون آمدند [و] گفتند: آیا اگر پیروز شویم، حتماً برای ما پاداش و مزد قابل توجهی خواهد بود؟'),
(1068, 7, 114, 'گفت: آری، و یقیناً از مقرّبان خواهید بود.'),
(1069, 7, 115, 'جادوگران گفتند: ای موسی! یا تو [چوب دست خود را] بینداز، یا اینکه ما می اندازیم.'),
(1070, 7, 116, '[موسی] گفت: شما بیندازید. هنگامی که انداختند، چشم های مردم را جادو کردند و آنان را سخت ترساندند، و جادویی بزرگ و شگفت آور به میان آوردند.'),
(1071, 7, 117, 'و به موسی وحی کردیم: عصایت را بینداز. ناگهان آنچه را جادوگران به دروغ بافته بودند، به سرعت بلعید!'),
(1072, 7, 118, 'پس حق ثابت شد و آنچه را همواره جادوگران [به عنوان سِحر] انجام می دادند، باطل و پوچ گشت.'),
(1073, 7, 119, 'پس [فرعونیان] در آنجا مغلوب شدند و با ذلت و خواری بازگشتند.'),
(1074, 7, 120, 'و جادوگران [با دیدن آن معجزه عظیم و باطل شدن سِحر خویش] به سجده افتادند.'),
(1075, 7, 121, 'گفتند: [از روی حقیقت] به پروردگار جهانیان ایمان آوردیم،'),
(1076, 7, 122, 'پروردگار موسی و هارون.'),
(1077, 7, 123, 'فرعون گفت: آیا پیش از آنکه من به شما اجازه دهم به او ایمان آوردید؟! مسلماً این توطئه و نیرنگی است که [شما و موسی] در این شهر برپا کرده اید تا مردمش را از آن بیرون کنید، ولی به زودی خواهید دانست.'),
(1078, 7, 124, 'قطعاً دست ها و پاهایتان را یکی از چپ و یکی از راست جدا می کنم، سپس همه شما را به دار خواهم آویخت.'),
(1079, 7, 125, 'گفتند: ما به سوی پروردگارمان بازمی گردیم [بنابراین ترسی از مجازات تو نداریم.]'),
(1080, 7, 126, 'و تو ما را جز به این سبب به کیفر نمی رسانی که ما به آیات پروردگارمان هنگامی که به سوی ما آمد، ایمان آوردیم. [آن گاه به دعا روی آوردند و گفتند:] پروردگارا! صبر و شکیبایی بر ما فرو ریز و ما را در حالی که تسلیم [فرمان ها و احکامت] باشیم، بمیران.'),
(1081, 7, 127, 'اشراف و سران قوم فرعون گفتند: آیا موسی و قومش را رها می کنی تا در این سرزمین فساد و تباهی کنند و تو و معبودهایت را واگذارند؟ گفت: به زودی پسرانشان را به صورتی وسیع و گسترده به قتل می رسانیم و زنانشان را زنده می گذاریم و ما بر آنان چیره و مُسلّطیم.'),
(1082, 7, 128, 'موسی به قومش گفت: از خدا یاری بخواهید، و شکیبایی ورزید، یقیناً زمین در سیطره مالکیّت و فرمانروایی خداست، آن را به هر کس از بندگانش که بخواهد می بخشد، و سرانجام نیک، برای پرهیزکاران است.'),
(1083, 7, 129, 'گفتند: پیش از آنکه تو نزد ما بیایی شکنجه و آزار شدیم، و نیز پس از آنکه آمده ای [مورد شکنجه و آزاریم]. گفت: امید است که پروردگارتان دشمنانتان را نابود کند، و شما را در این سرزمین، جانشین [آنان] گرداند، پس بنگرد که شما [پس از فرعونیان] چگونه عمل می کنید؟'),
(1084, 7, 130, 'و فرعونیان را به قحطی و خشکسالی های متعدد و کمبود بسیار شدیدِ بخشی از محصولات دچار نمودیم تا متذکّر شوند.'),
(1085, 7, 131, 'پس هنگامی که رفاه و نعمت به آنان روی می کرد، می گفتند: این به سبب [شایستگیِ] خود ماست، و چون گزند و آسیبی به آنان می رسید، به موسی و همراهانش فال بد می زدند؛ آگاه باشید که [سررشته و] علت شومی و نحوست فرعونیان [که عکس العمل زشتی های خود آنان است] نزد خداست، ولی بیشترشان نمی دانند.'),
(1086, 7, 132, 'فرعونیان گفتند: [ای موسی! از دعوتت دست بردار که] هر چه را به عنوان معجزه برای ما بیاوری تا به وسیله آن ما را جادو کنی به تو ایمان نمی آوریم.'),
(1087, 7, 133, 'پس ما توفان و هجوم ملخ و شپش و قورباغه و آلوده شدن وسایل زندگی را به خون که عذاب های گوناگونی بود به سوی آنان فرستادیم، باز هم تکبّر و سرکشی کردند و گروهی مجرم و گناهکار بودند.'),
(1088, 7, 134, 'و هرگاه عذاب بر آنان فرود آمد، گفتند: ای موسی! پروردگارت را به پیمانی که با تو دارد [و آن مستجاب کردن دعای توست] برای ما بخوان که اگر این عذاب را از مابرطرف کنی یقیناً به تو ایمان می آوریم و بنی اسرائیل را با تو روانه می کنیم.'),
(1089, 7, 135, 'پس هنگامی که عذاب را تا مدتی که [می باید همه] آنان به پایان مهلتآن می رسیدند از ایشان برطرف کردیم، به دور از انتظار پیمانشان را می شکستند.'),
(1090, 7, 136, 'نهایتاً به سبب اینکه آیات ما را تکذیب کردند، و از آنها غافل و بی خبر بودند، از آنان انتقام گرفتیم و در دریا غرقشان کردیم.'),
(1091, 7, 137, 'و به آن گروهی که همواره ناتوان و زبونشان شمرده بودند، نواحی شرقی و غربی آن سرزمین را که در آن [از جهت فراوانی نعمت، ارزانی و حاصل خیزی] برکت قرار داده بودیم، بخشیدیم؛ و وعده نیکوتر و زیباتر پروردگارت بر بنی اسرائیل به [پاداش] صبری که [بر سختی ها و بلاها] کردند تحقّق یافت، و آنچه را که همواره فرعون وفرعونیان [از کاخ و قصرهای مجلّل] و سایه بان های خوش نشین می افراشتند، نابود کردیم.'),
(1092, 7, 138, 'و بنی اسرائیل را از دریا [یی که فرعونیان را در آن غرق کردیم] عبور دادیم؛ پس به گروهی گذر کردند که همواره بر پرستش بت های خود ملازمت داشتند، گفتند: ای موسی! همان گونه که برای آنان معبودانی است، تو هم برای ما معبودی قرار بده!! موسی گفت: قطعاً شما گروهی هستید که جهالت و نادانی می ورزید.'),
(1093, 7, 139, 'بی تردید آنچه اینان در آن قرار دارند [وآن عقاید شرک آلود و آیین بت پرستی است] نابود شده و فاسد است، و آنچه همواره انجام می دهند، باطل و بیهوده است.'),
(1094, 7, 140, '[موسی با یک دنیا شگفتی و تعجب] گفت: آیا غیر خدا را [که هیچ گونه شایستگی پرستش ندارد] به عنوان معبود برایتان طلب کنم؟ در حالی که اوست که شما را بر جهانیان [روزگارتان] برتری داد.'),
(1095, 7, 141, 'و [یاد کنید] هنگامی را که شما را از [چنگال ظالمانه] فرعونیان نجات دادیم، هم آنان که شما را به سخت ترین صورت شکنجه می کردند، [و به شکلی گسترده] پسرانتان را می کشتند، و زنانتان را [برای به عزا نشستن در مرگ پسران و بیگاری] زنده می گذاشتند، و برای شما در این [امور] آزمایشی بزرگ از سوی پروردگارتان بود.'),
(1096, 7, 142, 'و با موسی [برای عبادتی ویژه و دریافت تورات] سی شب وعده گذاشتیم و آن را با [افزودن] ده شب کامل کردیم، پس میعادگاه پروردگارش به چهل شب پایان گرفت، و موسی [هنگامی که به میعادگاه می رفت] به برادرش هارون گفت: در میان قومم جانشین من باش و به اصلاح برخیز و از راه و روش مفسدان پیروی مکن.'),
(1097, 7, 143, 'زمانی که موسی به میعادگاه ما آمد، و پروردگارش با وی سخن گفت، عرض کرد: پروردگارا! جمال با کمال ذات بی نهایتت را [به قلب من] بنمای تا تو را [به رؤیت ویژه باطنی] بنگرم. خدا فرمود: هرگز مرا نخواهی دید، ولی به این کوه بنگر اگر [پس از جلوه من] بر جای خود ثابت و برقرار ماند، تو هم مرا خواهی دید. چون پروردگارش بر کوه جلوه کرد، آن را متلاشی نمود و موسی بی هوش شد، پس هنگامی که به هوش آمد گفت: تو منزّهی [از اینکه مشاهده شوی،] به سویت بازگشتم، و من [در میان مردم این روزگار] نخستین باور کننده [این حقیقت که هرگز دیده نمی شوی] هستم.'),
(1098, 7, 144, '[خدا] فرمود: ای موسی! من تو را به [ابلاغ] پیام هایم و به سخن گفتنم با تو بر همه مردم برگزیدم؛ پس آنچه را [از پیام هایم] به تو دادم، دریافت کن، [وبه کار بند] و از سپاس گزاران باش.'),
(1099, 7, 145, 'و برای او در الواحِ [تورات] از هر چیزی [که در باب دین مورد نیاز مردم باشد] پندی و برای هر چیزی [که تأمین کننده هدایت انسان ها باشد] سخنی روشن نوشتیم؛ پس [به او گفتیم:] آن را با قدرت [ی تمام و عزمی استوار] دریافت کن، [و به کار بند] و قوم خود را فرمان ده که آن را به نیکوترین صورت دریافت کنند [و به کار بندند.] به زودی سرای فاسقان را [که در دنیا ویرانی کاخ ها و خانه های آنان، و در آخرت دوزخ است] به شما نشان می دهم.'),
(1100, 7, 146, 'به زودی کسانی را که در روی زمین به ناحق گردنکشی و تکبّر می کنند از [فهم] آیاتم بازمی دارم که اگر هر آیه ای را ببینند، به آن ایمان نمی آورند و چون راه هدایت را مشاهده کنند، آن را راه و رسم زندگی نگیرند و اگر راه گمراهی را ببینند، آن را راه و روش خود گیرند؛ این [بازداشتن از فهم آیاتم] به سبب آن است که آیات ما را تکذیب کردند و از آنها غافل و بی خبر بودند.'),
(1101, 7, 147, 'و آنان که آیات ما و دیدار آخرت را تکذیب کردند، اعمالشان تباه و بی اثر شد. آیا جز به آنچه همواره [بر طبق کفر و انحرافشان] انجام می دادند، کیفر داده می شوند؟!'),
(1102, 7, 148, 'و قوم موسی پس از [رفتن] او [به میعادگاه پروردگار]، از زیورهای خود مجسمه گوساله ای ساختند که صدای گاو داشت! آیا نمی دیدند که آن مجسمه با آنان سخن نمی گوید و آنان را به راهی هدایت نمی کند؟! [ولی با روشن بودن این حقیقت که مجسمه ای بیش نیست و هیچ کاری از دستش بر نمی آید] آن را [به عنوان معبودی برای پرستش] گرفتند و [در صورتی که در این انتخاب] از ستمکاران بودند.'),
(1103, 7, 149, 'و هنگامی که به شدت پشیمان شدند [و به باطل بودن گوساله پرستی آگاه گشتند] و دانستند که قطعاً گمراه شده اند، گفتند: اگر پروردگارمان به ما رحم نکند و ما را نیامرزد، یقیناً از زیانکاران خواهیم بود.'),
(1104, 7, 150, 'و هنگامی که موسی [پس از آگاهی از آن پیش آمد خطرناک] خشمگین و بسیار اندوهناک به سوی قومش بازگشت، گفت: پس از من بد جانشینانی برایم بودید، آیا [با پرستش گوساله] بر فرمان پروردگارتان [که در تورات آمده] پیشی گرفتید [و صبر نکردید تا من بیایم و فرمان خدا را به شما ابلاغ کنم؟] و الواح را افکند و سر برادرش را گرفت [وبا خشم] او را به سوی خود می کشید. [هارون] گفت: ای فرزند مادرم! این گروه مرا ناتوان وزبون شمردند، و نزدیک بود مرا به قتل برسانند، پس مرا با مؤاخذه کردنم دشمنْ شاد مکن، و [هم طراز] باگروه ستمکاران قرار مده.'),
(1105, 7, 151, '[موسی] گفت: پروردگارا! مرا و برادرم را بیامرز، و ما را در رحمتت درآور، که تو مهربان ترین مهربانانی.'),
(1106, 7, 152, 'بی تردید کسانی که گوساله را [به پرستش] گرفتند، به زودی خشمی سخت از پروردگارشان، و خواری و ذلتی در زندگی دنیا به آنان خواهد رسید؛ و این گونه دروغ بافان را کیفر می دهیم.'),
(1107, 7, 153, 'و آنان که مرتکب بدی ها شدند، سپس بعد از آن توبه کردند، وایمان آوردند [امیدوار باشند که] یقیناً پروردگارت پس از آن [توبه وایمان] بسیار آمرزنده و مهربان است.'),
(1108, 7, 154, 'وهنگامی که خشم موسی فرو نشست، الواح را برگرفت، و در نوشته هایش برای کسانی که از [مخالفت بافرمان های] پروردگارشان می ترسند، هدایت ورحمت بود.'),
(1109, 7, 155, 'موسی از میان قومش هفتاد مرد را برای میعادگاه ما بر گزید؛ پس هنگامی که [به سبب درخواست نابجایشان] آن زلزله شدید نابود کننده، آنان را فرا گرفت، گفت: پروردگارا! اگر می خواستی می توانستی همه آنان و مرا پیش از این هلاک کنی [ای کاش پیش از این هلاک می کردی تا بنی اسرائیل گمان نکنند که مرا در این حادثه توطئه و مکری بوده] آیا ما را به خاطر گناهی که سبک مغزانمان مرتکب شدند، هلاک می کنی؟ این [حادثه] چیزی جز آزمایش تو نیست، هر که را بخواهی [به آزمایشت] گمراه می کنی، و هر که را بخواهی هدایت می نمایی، تو سرپرست و یاور مایی، ما را بیامرز و به ما رحم کن که تو بهترین آمرزندگانی.'),
(1110, 7, 156, 'و برای ما در این دنیا و آخرت، نیکی مقرّر کن که ما به سوی تو بازگشته ایم. خدا فرمود: عذابم را به هر کس بخواهم می رسانم و رحمتم همه چیز را فرا گرفته است، پس به زودی آن را برای کسانی که [از شرک، کفر، بت پرستی و ارتداد] می پرهیزند و زکات می پردازند و به آیاتم ایمان می آورند، مقرّر و لازم می دارم.'),
(1111, 7, 157, 'همان کسانی که از این رسول و پیامبر «ناخوانده درس» که او را نزد خود [با همه نشانه ها و اوصافش] در تورات وانجیل نگاشته می یابند، پیروی می کنند؛ پیامبری که آنان را به کارهای شایسته فرمان می دهد، و از اعمال زشت بازمی دارد، و پاکیزه ها را بر آنان حلال می نماید، و ناپاک ها را بر آنان حرام می کند، و بارهای تکالیف سنگین و زنجیره ها [یِ جهل، بی خبری و بدعت را] که بر دوش عقل وجان آنان است برمی دارد؛ پس کسانی که به او ایمان آوردند و او را [در برابر دشمنان] حمایت کردند و یاریش دادند و از نوری که بر او نازل شده پیروی نمودند، فقط آنان رستگارانند.'),
(1112, 7, 158, 'بگو: ای مردم! یقیناً من فرستاده خدا به سوی همه شمایم؛ خدایی که مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره اوست، جز او معبودی نیست، زنده می کند و می میراند، پس به خدا و رسول او پیامبر ناخوانده درس که به خدا و تمام سخنان او ایمان دارد، ایمان بیاورید، و از او پیروی کنید تا هدایت یابید.'),
(1113, 7, 159, 'و از قوم موسی گروهی هستند که مردم را با [موازین و روش هایِ] حق هدایت می کنند و به درستی و راستی داوری می نمایند.'),
(1114, 7, 160, 'و ما بنی اسرائیل را به دوازده قبیله که هر یک امتی بودند، تقسیم کردیم، و به موسی هنگامی که قومش از او درخواست آب کردند، وحی نمودیم که عصایت را بر این سنگ بزن؛ در نتیجه دوازده چشمه از آن جوشید؛ هر گروهی چشمه ویژه خود را شناخت؛ و ابر را بر سر آنان سایبان قرار دادیم، و برای آنان منّ و سلوی [یعنی ترنجبین و مرغی مخصوص] نازل کردیم [و به آنان گفتیم:] از روزی های پاکیزه ای که به شما دادیم، بخورید. و [آنان با سرپیچی از فرمان های ما] بر ما ستم نکردند، ولی همواره بر خود ستم می ورزیدند.'),
(1115, 7, 161, 'و [یاد کنید] هنگامی را که به بنی اسرائیل گفته شد: در این شهر [بیت المقدس] سکونت گزینید، و از هر جا که خواستید [ازمیوه و محصولاتش] بخورید، و بگویید: [خدایا! خواسته ما] ریزش گناهان است. و از دروازه [شهر یا درِ معبد] فروتنانه و سجده کنان درآیید، تا گناهانتان را بیامرزم، قطعاً [پاداش] نیکوکاران را بیفزایم.'),
(1116, 7, 162, 'پس ستمکاران از آنان، سخنی را که [بیرون دروازه شهر] به آنان گفته شده بود [پس از ورود به شهر] به سخنی دیگر تبدیل کردند، [به جای درخواست ریزش گناهان درخواست امور مادی نمودند] نهایتاً به کیفر ستمی که همواره مرتکب می شدند، عذابی از آسمان بر آنان فرستادیم.'),
(1117, 7, 163, 'از آنان سرگذشت شهری را که در ساحل دریا بود بپرس، هنگامی که [اهلش] در [روز تعطیلیِ] شنبه [از حکم خدا] تجاوز می کردند؛ به این صورت که روز [تعطیلیِ] شنبه ماهی هایشان [به اراده خدا از اعماق آب به سوی ساحل می آمدند و] به روی آب آشکار می شدند و غیر شنبه ها نمی آمدند [وآنان بر خلاف حکم خدا در تعطیلی شنبه به صید می پرداختند؛] این گونه آنان را در برابر نافرمانی و فسقی که همواره داشتند، آزمایش می کردیم.'),
(1118, 7, 164, 'و گروهی از بنی اسرائیل [که در برابر بد کاری های دیگران ساکت بودند، به پند دهندگان خیرخواه و دلسوز] گفتند: چرا گروهی را که خداهلاک کننده آنان یا عذاب کننده آنان به عذابی سخت است، پند می دهید؟ [پند دادن شما کاری نابجاست.] گفتند: برای اینکه در پیشگاه پروردگارمان [نسبت به رفع مسؤولیت خود] حجت و عذر داشته باشیم و شاید آنان [از گناهانشان] بپرهیزند.'),
(1119, 7, 165, 'پس چون پندی را که به آنان داده شد، فراموش کردند [در لحظه نزول عذاب] پند دهندگانی که مردم را از بدی ها بازمی داشتند، نجات دادیم و آنان را که ستم کردند به کیفر آنکه همواره نافرمانی می کردند، به عذابی سخت گرفتیم.'),
(1120, 7, 166, 'و هنگامی که از آنچه نهی شدند سرکشی کردند، به آنان گفتیم: به شکل بوزینگانی رانده شده درآیید!'),
(1121, 7, 167, 'و [یاد کن] هنگامی را که پروردگارت اعلام کرد که بی تردید تا روز قیامت کسانی را بر ضد یهودیان [متجاوز و سرکش] برانگیزد و مسلط کند که همواره عذابی سخت به آنان بچشاند؛ مسلماً پروردگارت زود کیفر و یقیناً [نسبت به تائبان] بسیار آمرزنده و مهربان است.'),
(1122, 7, 168, 'و آنان را در زمین [به صورت] گروه گروه پراکنده کردیم، گروهی از آنان مردمی شایسته و گروهی از آنان غیر شایسته اند، و آنان را با خوشی ها و سختی ها آزمودیم، باشد که [غیر شایسته ها به صلاح، درستی، صواب و راستی] بازگردند.'),
(1123, 7, 169, 'پس بعد از آنان جانشینانی [ناشایسته وگناهکار] که کتاب [تورات] را به ارث بردند به جای ایشان قرار گرفتند، [آنان] متاع پست و از دست رفتنی این دنیا [یِ زودگذر] را [از هر راه نامشروعی] به چنگ می زنند و [به ناحق] می گویند: به زودی آمرزیده خواهیم شد. و [به سبب دنیاگرایی وغفلت از حقایق] اگر متاع نامشروعی دیگر همانند متاع اول به آنان برسد [باز هم] آن را به چنگ می زنند؛ آیا از آنان در کتاب [تورات] در حالی که آنچه را در آن است مکرر خوانده و فهمیده اند، پیمان [محکم و استوار] گرفته نشده است که نسبت به خدا جز حق نگویند؟! [پس چرا آمرزشی که بدون توبه از حرام خواری و زشت کاری به آنان تعلق نمی گیرد به خدا نسبت می دهند؟] و سرای آخرت برای کسانی که [همواره از مال نامشروع و نسبت ناروا] می پرهیزند، بهتر است؛ آیا نمی اندیشید؟'),
(1124, 7, 170, 'و آنان که به کتاب [آسمانی] چنگ می زنند [و عملاً به آیاتش پای بندند] و نماز را بر پا داشته اند [دارای پاداشند] یقیناً ما پاداش اصلاح گران را ضایع نمی کنیم.'),
(1125, 7, 171, 'و [یاد کنید] هنگامی [را] که کوه [طور] را برکندیم، [و] چنان که گویی سایبانی است بالای سرشان قرار دادیم و [به خاطرضعف ایمان] پنداشتند که بر سرشان سقوط می کند. [به آنان گفتیم:] آنچه [از کتاب، شریعت و احکام] به شما داده ایم با قدرت [ی تمام و عزمی استوار] بگیرید، و آنچه [از معارف و حقایق] در آن است متذکّر شوید [و فرا گیرید و همواره به خاطر داشته باشید] تا [با عمل به آن] پرهیزکار شوید.'),
(1126, 7, 172, 'و [به یاد آر] هنگامی را که پروردگارت از صلب بنی آدم نسلشان را پدید آورد، و آنان را [در ارتباط با پروردگاریش] بر خودشان گواه گرفت [و فرمود:] آیا من پروردگار شما نیستم؟ [انسان ها با توجه به وابستگی وجودشان و وجود همه موجودات به پروردگاری و ربوبیّت حق] گفتند: آری، گواهی دادیم. [پس اقرار به پروردگاری خود را در این دنیا از شما گرفتیم] تا روز قیامت نگویید: ما از این [حقیقت آشکار و روشن] بی خبر بودیم.'),
(1127, 7, 173, 'یا نگویید: پدرانمان پیش از ما مشرک بودند، و ما فرزندانی پس از آنان بودیم [و راهی جز تقلید از آنان نداشتیم] آیا ما را به خاطر آنچه باطل گرایان انجام دادند، هلاک می کنی؟'),
(1128, 7, 174, 'و این گونه آیات را [مُستدل و منطقی] تفصیل و توضیح می دهیم [تا تدبّر کنند] و برای اینکه [از شرک به توحید] بازگردند.'),
(1129, 7, 175, 'و سرگذشت کسی که آیات خود را به او عطا کردیم و او عملاً از آنان جدا شد، برای آنان بخوان؛ پس شیطان او را دنبال کرد [تا به دامش انداخت] و در نتیجه از گمراهان شد.'),
(1130, 7, 176, 'و اگر می خواستیم [درجات و مقاماتش را] به وسیله آن آیات بالا می بردیم، ولی او به امور ناچیز مادی و لذت هایِ زودگذرِ دنیایی تمایل پیدا کرد واز هوای نفسش پیروی نمود؛ پس داستانش چون داستان سگ است [که] اگر به او هجوم بری، زبان از کام بیرون می آورد، واگر به حال خودش واگذاری [باز هم] زبان از کام بیرون می آورد. این داستان گروهی است که آیات ما را تکذیب کردند؛ پس این داستان را [برای مردم] حکایت کن، شاید [نسبت به امور خویش] بیندیشند.'),
(1131, 7, 177, 'بد است داستان گروهی که آیات ما را تکذیب کردند وهمواره به خود ستم روا می داشتند.'),
(1132, 7, 178, 'هر که را خدا هدایت کند، پس او راه یافته واقعی است، و کسانی را که [به سبب عناد و لجاجتشان] گمراه نماید، پس فقط اینان زیانکارانند.'),
(1133, 7, 179, 'و مسلماً بسیاری از جنّیان و آدمیان را برای دوزخ آفریده ایم [زیرا] آنان را دل هایی است که به وسیله آن [معارف الهی را] در نمی یابند، و چشمانی است که توسط آن [حقایق و نشانه های حق را] نمی بینند، و گوش هایی است که به وسیله آن [سخن خدا و پیامبران را] نمی شنوند، آنان مانند چهارپایانند بلکه گمراه ترند؛ اینانند که بی خبر و غافل [ازمعارف و آیات خدای] اند.'),
(1134, 7, 180, 'و نیکوترین نام ها [به لحاظ معانی] ویژه خداست، پس او را با آن نام ها بخوانید؛ و آنان که در نام های خدا به انحراف می گرایند [و او را با نام هایی که نشان دهنده کاستی و نقص است، می خوانند] رها کنید؛ آنان به زودی به همان اعمالی که همواره انجام می دادند، جزا داده می شوند.'),
(1135, 7, 181, 'و از میان کسانی که آفریده ایم [یعنی جنّیان وآدمیان] گروهی [هستند که هم نوعان خود را] به حق هدایت می کنند و به درستی و راستی داوری می نمایند.'),
(1136, 7, 182, 'و کسانی که آیات ما را تکذیب کردند، به تدریج ازجایی که نمی دانند [به ورطه سقوط و هلاکت می کشانیم تا عاقبت به عذاب دنیا و آخرت دچار شوند.]'),
(1137, 7, 183, 'و به آنان مهلت می دهیم؛ [زیرا از سیطره قدرت ما بیرون رفتنی نیستند] یقیناً تدبیر ونقشه من استوار است.'),
(1138, 7, 184, 'آیا اندیشه نکردند که در همنشین آنان [یعنی پیامبر اسلام] هیچ نوع جنونی نیست؛ او فقط بیم دهنده ای آشکار [نسبت به سرانجام شوم بدکاران] است.'),
(1139, 7, 185, 'آیا در [فرمانروایی و] مالکیّت [و ربوبیّت] بر آسمان ها و زمین و هر چیزی که خدا آفریده و اینکه شاید پایان عمرشان نزدیک شده باشد با تأمل ننگریسته اند؟ [و اگر به قرآن مجید، این کتاب هدایتگر ایمان نیاورند] پس بعد ازآن به کدام سخن ایمان می آورند؟!'),
(1140, 7, 186, 'برای کسانی که خدا [به سبب لجاجت و عنادشان] گمراهشان کند، هدایت کننده ای نیست؛ و آنان را در سرکشی و تجاوزشان وا می گذارد تا در [گمراهی شان] سرگردان و حیران بمانند.'),
(1141, 7, 187, 'همواره درباره قیامت از تو می پرسند که وقوع آن چه وقت است؟ بگو: دانش آن فقط نزد پروردگار من است، غیر او آن را در وقت معینش آشکار نمی کند؛ [تحملِ این حادثه عظیم و هولناک،] بر آسمان ها و زمین سنگین و دشوار است، جز به طور ناگهانی بر شما نمی آید. آن گونه از تو می پرسند که گویا تو از وقت وقوعش به شدت کنجکاوی کرده ای [و کاملاً از آن آگاهی]، بگو: دانش آن فقط نزد خداست، ولی بیشتر مردم نمی دانند [که این دانش، مخصوص به خدا و فقط در اختیار اوست.]'),
(1142, 7, 188, 'بگو: من قدرتِ [جلبِ] سودی و [دفعِ] زیانی را از خود ندارم جز آنچه خدا خواهد، و [غیب هم نمی دانم] اگر غیب می دانستم، یقیناً برای خود ازهر خیری فراوان و بسیار فراهم می کردم وهیچ گزند وآسیبی به من نمی رسید؛ من فقط برای گروهی که ایمان می آورند، بیم دهنده و مژده رسانم.'),
(1143, 7, 189, 'او کسی است که شما را از یک تن آفرید، و همسرش را از او پدید آورد تا در کنارش آرامش یابد؛ پس هنگامی که مرد با زن آمیزش نمود، زن به حملی سبک حامله شد، پس با آن حمل سبک، زندگی را ادامه داد؛ پس چون سنگین بار شد، زن و شوهر پروردگارشان را خواندند که اگر به ما فرزندی تندرست و سالم عطا کنی، مسلماً از سپاس گزاران خواهیم بود.'),
(1144, 7, 190, 'پس هنگامی که به آن زن و شوهر فرزندی تندرست و سالم داد [غیر از خدا را در اعطای این نعمت مؤثر دانستند، از این جهت] برای خدا در کنار نعمتی که به آنان عطا کرده بود، شریکان وهمتایانی قرار دادند!! پس خدا برتر و والاتر از آن است که برایش شریکان و همتایان قرار دهند.'),
(1145, 7, 191, 'آیا موجوداتی را شریک او قرار می دهند که قدرت ندارند کمترین چیزی را بیافرینند و خودشان آفریده می شوند؟!'),
(1146, 7, 192, 'و [این شریکانی که برای خدا قرار می دهند] نه می توانند پرستش کنندگان خود را یاری دهند، ونه قدرت دارند خودشان را یاری کنند.'),
(1147, 7, 193, 'و اگر آن شریکان را به سوی هدایت بخوانید، از شما پیروی نمی کنند، برای شما یکسان است که آنان را بخوانید یا خاموش باشید [در هر صورت چیزی از آنان نصیب شما نمی شود.]'),
(1148, 7, 194, 'یقیناً کسانی را که به جای خدا می پرستید بندگان و مملوکانی ناتوانْ چون شما هستند؛ پس اگر راستگویید [که می توانند در زندگی شما مؤثر باشند] آنان را [در گرفتاری ها و نیازمندی های خود] بخوانید پس [هنگامی که بخوانید [باید شما را اجابت کنند] ولی خود می دانید که در هیچ موردی جواب شما را نمی دهند.]'),
(1149, 7, 195, 'آیا آنها پاهایی دارند که با آن راه روند، یا دست هایی دارند که با آن بگیرند، یا چشم هایی دارند که با آن ببینند، یا گوش هایی دارند که با آن بشنوند؟! بگو: شریکان خود را [به کمک خویش] بخوانید، سپس [بر ضدِّ من هر توطئه و] نیرنگی [دارید] به کار گیرید و [برای نابودی من لحظه ای مرا [مهلت ندهید] تا برای شما ثابت شود که هیچ کاری در هیچ زمینه ای از غیر خدا بر نمی آید.]'),
(1150, 7, 196, 'یقیناً سرپرست و یار من خدایی است که قرآن را نازل کرده و او همواره شایستگان را سرپرستی و یاری می کند.'),
(1151, 7, 197, 'و کسانی را که به جای خدا می خوانید، نه می توانند شما را یاری دهند و نه خود را یاری رسانند.'),
(1152, 7, 198, 'و اگر آنان را به سوی هدایت دعوت کنید، نمی شنوند و آنان را می بینی که به سوی تو می نگرند در حالی که نمی بینند.'),
(1153, 7, 199, 'عفو و گذشت را پیشه کن، و به کار پسندیده فرمان ده، و از نادانان روی بگردان.'),
(1154, 7, 200, 'و اگر [وسوسه ای از سوی] شیطان، تو را [به خشم بر مردم و ترکِ مهربانی و ملاطفت] تحریک کند، به خدا پناه جوی؛ زیرا خدا شنوا و داناست.'),
(1155, 7, 201, 'مسلماً کسانی که [نسبت به گناهان، معاصی و آلودگی های ظاهری وباطنی] تقوا ورزیده اند، هرگاه وسوسه هایی از سوی شیطان به آنان رسد [خدا و قیامت را] یاد کنند، پس بی درنگ بینا شوند [و از دام وسوسه هایش نجات یابند.]'),
(1156, 7, 202, 'و برادران بی تقوایان [که شیاطین هستند] همواره آنان را به عمق گمراهی می کشانند؛ سپس [در به گمراهی کشیدنشان] کوتاهی نمی ورزند.'),
(1157, 7, 203, 'و هرگاه برای مخالفان [به سبب تأخیر وحی] آیه ای نیاوری [بر پایه گمان پوچ و باطلشان که قرآن را از پیش خود می آوری] می گویند: چرا آیه ای از نزد خود انتخاب نکردی [تا برای ما بیاوری]؟ بگو: من فقط آنچه از سوی پروردگارم به من وحی می شود، پیروی می کنم. این قرآن دلایلی روشن از سوی پروردگار شماست و برای گروهی که ایمان می آورند، سراسر هدایت و رحمت است.'),
(1158, 7, 204, 'و هنگامی که قرآن خوانده شود، به آن گوش فرا دهید و سکوت کنید تا مشمول رحمت شوید.'),
(1159, 7, 205, 'و پروردگارت را در دل خود بامدادان و شامگاهان از روی فروتنی و زاری و بیم و ترس به صدایی آرام وآهسته یاد کن و [نسبت به ذکر خدا] از بی خبران مباش.'),
(1160, 7, 206, 'یقیناً مقرّبان و نزدیکان خدا هیچ گاه از عبادت و بندگی اش تکبّر نمی ورزند، وهمواره او را تسبیح می گویند، و پیوسته برای او سجده می کنند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'از تو درباره انفال] یعنی غنایم جنگی و هر گونه مالی که مالک معینی ندارد [می پرسند، [که مالک آنها کیست و چگونه باید تقسیم شود؟] بگو: [مالکیّتِ حقیقیِ] انفال ویژه خدا وپیامبر است، پس اگر مؤمن هستید [نسبت به انفال] از خدا پروا کنید و [اختلاف و نزاع] بین خود را اصلاح نمایید، و از خدا و پیامبرش اطاعت کنید.'),
(1162, 8, 2, 'مؤمنان، فقط کسانی هستند که چون یاد خدا شود، دل هایشان ترسان می شود، وهنگامی که آیات او بر آنان خوانده شود بر ایمانشان می افزاید، و بر پروردگارشان توکل می کنند.'),
(1163, 8, 3, 'هم آنان که نماز را برپا می دارند و ازآنچه به آنان روزی داده ایم، انفاق می کنند.'),
(1164, 8, 4, 'مؤمنان واقعی و حقیقی فقط آنانند، برای ایشان نزد پروردگارشان درجاتی بالا و آمرزش و رزق نیکو و فراوانی است.'),
(1165, 8, 5, 'همان گونه که پروردگارت تو را به درستی و راستی از خانه ات [به سوی جنگ بدر] بیرون آورد و گروهی ازمؤمنان [از رفتن به جنگ] ناخشنود بودند [همان گونه گروهی از آنان از کیفیتِ تقسیمِ غنایم جنگی ناخشنودند.]'),
(1166, 8, 6, 'پس از آنکه حق [بودنِ انگیزه های رفتنت به جنگ بدر] روشن شد با تو در این حقّ [روشن و آشکار] مجادله و ستیزه می کنند [تا رأی خود را در ترک جنگ به عنوان اینکه جنگی نابرابر است، تحمیل کنند؛ چنان از شرکت در جنگ ترسیده بودند] که گویا به سوی مرگ رانده می شوند و آن را با چشم خود می بینند!!'),
(1167, 8, 7, 'و [یاد کنید] هنگامی را که خدا پیروزی بر یکی ازدو گروه [سپاه دشمن یا کاروان تجارتی قریش] را به شما وعده داد، و شما دوست داشتید بر کاروان تجارتی قریش دست یابید، ولی خدا می خواست پیروزی در میدان جنگ را با فرمان نافذی [که دایر بر پیروزی مؤمنان و شکست دشمنان جاری ساخته بود] تحقّق دهد و ریشه کافران را قطع کند.'),
(1168, 8, 8, 'تا حق را ثابت [و پابرجا و استوار نماید] وباطل را نابود سازد، هر چند مجرمان خوش نداشته باشند.'),
(1169, 8, 9, '[یاد کنید] هنگامی را که [در حال مشاهده دشمنِ تا دندان مسلح با دعا و زاری] ازپروردگارتان یاری خواستید، و او درخواست شما را اجابت کرد که من مسلماً شما را با هزار فرشته که پی در پی نازل می شوند، یاری می دهم.'),
(1170, 8, 10, 'و خدا آن [وعده یاری] را فقط مژده و نویدی برای شما قرار داد و نیز برای آنکه دل هایتان به سبب آن آرامش یابد؛ و گرنه پیروزی فقط از سوی خداست؛ زیرا خدا توانای شکست ناپذیر و حکیم است.'),
(1171, 8, 11, 'و [یاد کنید] هنگامی را که به سبب امنیت و آرامشی که ازسوی خدا یافتید، خواب سبکی را به همه شما مسلط کرد و برایتان آبی از آسمان نازل کرد تا شما را به وسیله آن [از آلودگی ها] پاک کند، و وسوسه شیطان را [که از بدترین کثافات است] از شما برطرف نماید، و دل هایتان را استحکام دهد، و گام هایتان را به آن استوار و پا برجا کند.'),
(1172, 8, 12, '[یاد کن] هنگامی را که پروردگارت به فرشتگان وحی کرد که من با شمایم، پس مؤمنان را [برای نبرد با دشمن] ثابت قدم دارید، [و] به زودی در دل کافران ترس می اندازم، پس سرهایشان را در هم کوبید و همه انگشتانشان را قطع کنید.'),
(1173, 8, 13, 'این [کیفر سخت] به سبب این است که آنان با خدا و پیامبرش دشمنی و مخالفت کردند، و هر کس با خدا و پیامبرش دشمنی و مخالفت ورزد، پس [بداند که] یقیناً خدا سخت کیفر است.'),
(1174, 8, 14, 'این [مجازات شما در دنیاست]، پس آن رابچشید، و [بدانید] برای کافران در قیامت، عذاب آتش است.'),
(1175, 8, 15, 'ای اهل ایمان! هنگامی که با کافران در حالی که بر ضد شما لشکرکشی می کنند روبرو می شوید، به آنان پشت نکنید [و نگریزید.]'),
(1176, 8, 16, 'و هر کس در آن موقعیت به آنان پشت کند [و بگریزد] سزاوار خشمی از سوی خدا شود و جایگاهش دوزخ است ودوزخ بازگشت گاه بدی است مگر [اینکه گریزش برای انتخاب محلی دیگر] جهت ادامه نبرد بادشمن، یا پیوستن به گروهی [تازه نفس از مجاهدان برای حمله به دشمن] باشد.'),
(1177, 8, 17, '[به کشتن دشمنان بر خود مبالید] شما آنان را نکشتید، بلکه خدا آنان را کشت. [ای پیامبر!] هنگامی که به سوی دشمنان تیر پرتاب کردی، تو پرتاب نکردی، بلکه خدا پرتاب کرد [تا آنان را هلاک کند] و مؤمنان را از سوی خود به آزمایشی نیکو بیازماید؛ زیرا خدا شنوا و داناست.'),
(1178, 8, 18, '[سرنوشت مؤمنان و کافران از] این [قرار است که دیدید]، و قطعاً خدا سست کننده نیرنگ کافران است.'),
(1179, 8, 19, '[شما ای مشرکان!] اگر پیروزی [آیینِ حق] را می خواستید، اینک پیروزی برای آیینِ حق آمد [پس به آن ایمان آورید]، و اگر [از دشمنی و مخالفت با خدا و رسول] بازایستید، برای شما بهتر است، و اگر [به دشمنی و مخالفت] بازگردید، ما نیز [بر ضد شما] بازمی گردیم، و هرگز جمعیت شما هر چند زیاد باشد، چیزی [از عذاب خدا] را از شما دفع نمی کند، و یقیناً خدا با مؤمنان است.'),
(1180, 8, 20, 'ای اهل ایمان! از خدا و پیامبرش اطاعت کنید و در حالی که [سخنان بر حقّ او را] می شنوید، از او روی نگردانید.'),
(1181, 8, 21, 'و مانند کسانی نباشید که [از روی تظاهر] گفتند: شنیدیم در حالی که [از روی حقیقت] نمی شنوند.'),
(1182, 8, 22, 'قطعاً بدترین جُنبندگان نزد خدا، کرانِ [از شنیدن حق] و لالانِ [از گفتن حق] هستند که [کلام حق را] نمی اندیشند!'),
(1183, 8, 23, 'اگر خدا [نسبت به پذیرفتن هدایت، شایستگی و] خیری در آنان می دید، یقیناً ایشان را شنوا [ی حقایق و معارف] می کرد، و اگر [با لجبازی و عنادی که فعلاً دارند] آنان را شنوا کند، باز اعراض کنان، روی [از حق] می گردانند.'),
(1184, 8, 24, 'ای اهل ایمان! هنگامی که خدا و پیامبرش شما را به حقایقی که به شما [حیات معنوی و] زندگی [واقعی] می بخشد، دعوت می کنند اجابت کنید، و بدانید که خدا میان آدمی و دلش حایل و مانع می شود [تا حق را باطل و باطل را حق مپندارد] و مسلماً همه شما به سوی او گردآوری خواهید شد.'),
(1185, 8, 25, 'و از عذابی [که نتیجه گناه، فساد، نزاع، اختلاف و ترک امر به معروف و نهی از منکر است] بپرهیزید، [عذابی] که فقط به ستمکاران از شما نمی رسد [بلکه وقتی نازل شود، همه را فرا می گیرد، ستمکاران را به خاطر ستم و اهل ایمان را به سبب اختلاف و نزاع و ترک امر به معروف و نهی از منکر] و بدانید که خدا سخت کیفر است.'),
(1186, 8, 26, 'و یاد کنید هنگامی را که شما در زمین گروهی اندک بودید، و ناتوان و زبون شمرده می شدید، و همواره می ترسیدید که مردم [مشرک و کافر] شما را بربایند [تا شکنجه و آزار دهند ونابود کنند] پس خدا شما را [در شهر مدینه] جای داد و با یاریش نیرومندتان ساخت، و از [نعمت های] پاکیزه اش به شما روزی بخشید تا سپاس گزاری کنید.'),
(1187, 8, 27, 'ای اهل ایمان! [با فاش کردنِ اسرار و جاسوسی برای دشمن] به خدا و پیامبر خیانت نکنید، و به امانت های میان خود هم خیانت نورزید، در حالی که می دانید [خیانت، عملی بسیار زشت است.]'),
(1188, 8, 28, 'و بدانید که اموال و فرزندانتان فقط وسیله آزمایش شماست، وخداست که پاداشی بزرگ نزد اوست.'),
(1189, 8, 29, 'ای اهل ایمان! اگر [در همه امورتان] از خدا پروا کنید، برای شما [بینایی و بصیرتی ویژه] برای تشخیص حق از باطل قرار می دهد، و گناهانتان را محو می کند، و شما را می آمرزد؛ و خدا دارای فضل بزرگ است.'),
(1190, 8, 30, 'و [یاد کن] هنگامی را که کفرپیشگان درباره تو نیرنگ می زدند تا تو را به زندان دراندازند، یا به قتل برسانند یا [از وطن] بیرونت کنند، و [در آینده هم، همواره بر ضد تو] نیرنگ می زنند و خدا هم جزای نیرنگشان را می دهد و خدا بهترین جزا دهنده نیرنگ زنندگان است.'),
(1191, 8, 31, 'و هنگامی که آیات ما را بر آنان می خوانند، می گویند: البته شنیدیم [ولی این آیات، مطلب فوق العاده ای نیست] ما هم اگر بخواهیم مانند آن را می گوییم، این آیات جز افسانه پیشینیان نیست!!'),
(1192, 8, 32, 'و [یاد کن] هنگامی را که گفتند: خدایا! اگر این [قرآن] همان حقِّ [نازل شده] از نزد توست، پس بر ما سنگ هایی از آسمان ببار یا عذابی دردناک بر ما بیاور!!'),
(1193, 8, 33, 'و خدا بر آن نیست که آنان را در حالی که تو در میان آنان به سر می بری، عذاب کند و تا ایشان طلب آمرزش می کنند، خدا عذاب کننده آنان نخواهد بود.'),
(1194, 8, 34, 'و چه چیزی مانع آنان است که خدا عذابشان نکند، در حالی که [مردم مؤمن را] از ورود به مسجدالحرام [و عبادت در آن] با آنکه متولّیان آن نیستند، بازمی دارند؛ متولّیانِ [شایسته و واقعیِ] این [مکان مقدس] فقط پرهیزکارانند، ولی بیشتر مشرکان نمی دانند [که تولیت آنجا شایسته آنان نیست.]'),
(1195, 8, 35, 'و نماز و دعایشان در کنار خانه [خدا] چیزی جز سوت کشیدن و کف زدن نبود، پس به کیفر کفری که همواره می ورزیدید، عذاب [دنیا و آخرت را] بچشید.'),
(1196, 8, 36, 'مسلماً کسانی که کافرند، اموالشان را هزینه می کنند تا مردم را از راه خدا بازدارند، و به زودی همه اموالشان را [برای جلوگیری از گسترش اسلام] خرج می کنند؛ سپس [این عمل] برای آنان مایه حسرت و ندامت می شود [زیرا خواهند دید که ثروتشان را از دست داده اند و اسلام هم گسترش یافته] و آن گاه مغلوب می شوند، و آنان که کافرند به سوی دوزخ گردآوری خواهند شد.'),
(1197, 8, 37, 'تا خدا ناپاکان را از پاکان جدا کند، وناپاکان را روی یکدیگر گذارد، پس همه را متراکم وانباشته سازد و یکجا در جهنم قرار دهد؛ [به راستی] آنانند که زیانکار واقعی اند.'),
(1198, 8, 38, 'به کسانی که کافرند بگو: اگر از کفر خود بازایستند [و به اسلام روی آورند] گناهان [ی چون جنگ با پیامبر و بازداشتن مردم از راه خدا، ومنع کردن مؤمنان از ورود به مسجدالحرام] از آنان بخشیده خواهد شد، واگر [به گناهانشان] بازگردند، بی تردید همان سنت خدا که در رابطه با [عذاب وهلاک] پیشینیان جاری شد [بر آنان نیز جاری می شود.]'),
(1199, 8, 39, 'و با آنان بجنگید تا هیچ نوع فساد و فتنه [و خونریزی وناامنی] بر جا نماند، و دین [در سراسر گیتی] ویژه خدا شود؛ پس اگر از فتنه بازایستند [با آنان نجنگید] زیرا خدا به آنچه انجام می دهند، بیناست.'),
(1200, 8, 40, 'و اگر روی گرداندند [باک نداشته باشید] بدانید که خدا سرپرست و یار شماست؛ نیکو سرپرست و نیکو یاوری است.'),
(1201, 8, 41, 'و بدانید هر چیزی را که [از راه جهاد یا کسب یا هر طریق مشروعی] به عنوان غنیمت و فایده به دست آوردید [کم باشد یا زیاد] یک پنجم آن برای خدا و رسول خدا (صلی الله علیه وآله) و خویشان پیامبر، و یتیمان و مسکینان و در راه ماندگان است، اگر به خدا و آنچه بر بنده اش روز جدا کننده حق از باطل، روز رویارویی دو گروه [مؤمن وکافر در جنگ بدر] نازل کردیم، ایمان آورده اید [پس آن را به عنوان حقّی واجب به خدا و رسول و دیگر نامبردگان بپردازید]؛ و خدا بر هرکاری تواناست.'),
(1202, 8, 42, 'و [یاد کنید] هنگامی را که شما [در جنگ بدر] بر دامنه ای نزدیک تر به سطح زمین بودید [که برای جنگ جایی نامناسب بود] و دشمن در دامنه ای بالاتر [و مناسب برای جنگ] قرار داشت، و کاروان [تجاری قریش] در مکانی پایین تر ازشما بود [که توانست دور از دید شما بگریزد]؛ و اگر [برای رویارویی] با دشمن [زمانی معین و جایی مشخص] وعده می گذاشتید، نسبت به وعده گاه اختلاف می کردید [ظاهر امر نشان می داد که شکست حتمی با شما و پیروزی قطعی با دشمن است، ولی این پیروزی به صورتی غیر عادی نصیب شما شد] تا خدا پیروزی شما و شکست آنان را که [بر اساس اراده اش] انجام شدنی بود تحقق دهد، تا هر که هلاک می شود از روی دلیلی روشن هلاک شود، وهر که زندگی می کند ازروی برهانی آشکار زندگی کند؛ و یقیناً خدا شنوا و داناست.'),
(1203, 8, 43, 'و [یاد کن] هنگامی را که خدا دررؤیاهای مکرّرت [نفراتِ] آنان را [که بر ضد تو و اهل ایمان تصمیم به جنگ داشتند] به تو اندک و ناچیز نشان داد [تا آن را برای یارانت بیان کنی]؛ و اگر آنان را بسیار و زیاد نشان می داد، شما [ای یاران پیامبر!] قطعاً سست می شدید و در کار جنگ نزاع و اختلاف می کردید، ولی خدا شما را [با آگاه شدن از رؤیای پیامبر، از سستی و اختلاف] رهایی بخشید؛ زیرا او به آنچه در سینه هاست، داناست.'),
(1204, 8, 44, 'و [یاد کنید] هنگامی را که در جنگ بدر باهم برخورد کردید، دشمن را در چشم شما اندک نشان داد، و شما را نیز در چشم آنان اندک نشان داد [اگر چه در گرما گرم جنگ، شما را در نظر آنان بسیار و آنان را در نظر شما اندک نشان داد] تا خدا پیروزی شما و شکست آنان را که [بر اساس اراده اش] انجام شدنی بود، تحقّق دهد؛ و همه کارها به سوی خدا بازگردانده می شود.'),
(1205, 8, 45, 'ای اهل ایمان! هنگامی که [در میدان نبرد] با گروهی [از مشرکان و کافران] برخورد کردید، ایستادگی نمایید و خدا را بسیار یاد کنید تا رستگار شوید.'),
(1206, 8, 46, 'و از خدا و پیامبرش فرمان برید، و با یکدیگر نزاع و اختلاف مکنید، که سست و بد دل می شوید، و قدرت و شوکتتان از میان می رود؛ و شکیبایی ورزید؛ زیرا خدا با شکیبایان است.'),
(1207, 8, 47, 'و مانند کسانی مباشید که از خانه هایشان از روی مستی و غرور، و برای خودنمایی بیرون آمدند و مردم را از راه خدا بازمی داشتند [ولی سرانجام دچار شکست ذلت باری شدند]؛ و خدا به آنچه انجام می دهند، احاطه دارد.'),
(1208, 8, 48, 'و [یاد کنید] هنگامی را که شیطان اعمالشان را [که در راه دشمنی با پیامبر و لشکرکشی بر ضد اهل ایمان بود] در نظرشان آراست و گفت: امروز [به سبب جمعیت بسیار و آرایش جنگی شما] هیچ کس از مردمان [با ایمان] پیروز شونده بر شما نیست، و من پناه دهنده به شمایم، ولی زمانی که دو گروه [مؤمن و مشرک] با یکدیگر برخورد کردند، به عقب برگشت و پا به فرار گذاشت، و گفت: من از شما [یاران و پیروانم] بیزارم، من چیزی را [چون نزول فرشتگان] می بینم که شما نمی بینید، البته من از خدا می ترسم و خدا سخت کیفر است.'),
(1209, 8, 49, 'و [یاد کنید] هنگامی را که منافقان و آنان که در دل هایشان بیماری [شک و تردید نسبت به حقایق] است، می گفتند: مؤمنان را دینشان [با این جمعیت اندک و اسلحه ناچیز برای شرکت در میدان نبرد] فریفت، و [آنان باور نمی کردند که] هر کس بر خدا توکل کند [بی تردید پیروز می شود]؛ زیرا خدا توانای شکست ناپذیر و حکیم است.'),
(1210, 8, 50, 'و اگر آن زمانی را که فرشتگان جان کافران را می ستانند، مشاهده کنی [می بینی] که بر چهره و پشتشان ضربات سنگینی می کوبند و [به آنان نهیب می زنند که] عذاب سوزان را بچشید!!'),
(1211, 8, 51, 'این به سبب گناهانی است که مرتکب شده اند و گرنه خدا نسبت به بندگانش [حتی به کمترین چیزی از ستم] ستمکار نیست.'),
(1212, 8, 52, '[عادتِ اینان] مانند عادت فرعونیان و پیشینیان آنان است که به آیات خدا کافر شدند، پس خدا آنان را به کیفر گناهانشان [به عذابی سخت] گرفت؛ زیرا خدا نیرومند و سخت کیفر است.'),
(1213, 8, 53, 'این [کیفر سخت] به سبب این است که خدا بر آن نیست که نعمتی را که به قومی عطا کرده [به عذاب و نقمت] تغییر دهد تا زمانی که آنان آنچه را در خود [از عقاید حقّه، حالات پاک و اخلاق حسنه ای که] دارند [به کفر، شرک، عصیان و گناه] تغییر دهند؛ و یقیناً خدا شنوا و داناست.'),
(1214, 8, 54, '[عادت اینان] مانند عادت فرعونیان و پیشینیان آنان است که آیات پروردگارشان را تکذیب کردند، در نتیجه ایشان را به کیفر گناهانشان هلاک نمودیم، و فرعونیان را غرق کردیم و همه آنان ستمکار بودند.'),
(1215, 8, 55, 'یقیناً بدترین جنبندگان نزد خدا کسانی هستند که کافرند و [به سبب لجبازی و عنادشان] ایمان نمی آورند.'),
(1216, 8, 56, 'همان کسانی که با برخی از آنان [بارها] پیمان بستی، ولی هر بار پیمانشان را می شکنند و از خدا پروا نمی کنند.'),
(1217, 8, 57, 'پس اگر در میدان جنگ، بر آنان دست یافتی [با وارد کردن مجازات و عقوبت سنگین بر ایشان] دیگر کسانی را که دنبال آنانند [و قصد جنگ با تو را دارند] تار و مار کن تا متذکّر [قدرت شما] شوند [و از خیال جنگ با تو بازایستند.]'),
(1218, 8, 58, 'و اگر از خیانت و پیمان شکنی گروهی [که با آنان هم پیمانی] بیم داری، پس به آنان خبر ده که [پیمان] به صورتی مساوی [و طرفینی] گسسته است؛ زیرا خدا خائنان را دوست ندارد.'),
(1219, 8, 59, 'و آنان که کافرند گمان نکنند که با پیمان شکنی خود [بر قدرتِ حق] پیشی جسته اند، اینان نمی توانند [ما را] عاجزکنند [تا از دسترس قدرت ما بیرون روند.]'),
(1220, 8, 60, 'و در برابر آنان آنچه در قدرت و توان دارید از نیرو [و نفرات و ساز و برگ جنگی] و اسبان ورزیده [برای جنگ] آماده کنید تا به وسیله آنها دشمن خدا و دشمن خودتان ودشمنانی غیر ایشان را که نمی شناسید، ولی خدا آنان را می شناسد بترسانید. و هر چه در راه خدا هزینه کنید، پاداشش به طور کامل به شما داده می شود، و مورد ستم قرار نخواهید گرفت.'),
(1221, 8, 61, 'و اگر دشمنان به صلح گرایند، تو هم به صلح گرای، و بر خدا توکل کن، که یقیناً او شنوا و داناست.'),
(1222, 8, 62, 'و اگر بخواهند [در زمینه صلح و آشتی] تو را بفریبند، یقیناً خدا تو را بس است؛ اوست کسی که تو را با یاری خود و به وسیله مؤمنان نیرومند ساخت.'),
(1223, 8, 63, 'و میان دل هایشان الفت و پیوند برقرار کرد که اگر همه آنچه را در روی زمین است، هزینه می کردی نمی توانستی میان دل هایشان الفت اندازی، ولی خدا میان آنان ایجاد الفت کرد؛ زیرا خدا توانای شکست ناپذیر و حکیم است.'),
(1224, 8, 64, 'ای پیامبر! خدا و مؤمنانی که از تو پیروی می کنند [از نظر حمایت و پشتیبانی] برای تو بس است.'),
(1225, 8, 65, 'ای پیامبر! مؤمنان را به جنگ برانگیز که اگر از شما بیست نفر صابر باشند بر دویست نفر چیره می شوند، و اگر از شما صد نفر [صابر] باشند بر هزار نفر از کافران چیره می شوند؛ زیرا آنان گروهی هستند [که حقایق توحید و قدرت خدا را] نمی فهمند.'),
(1226, 8, 66, 'اکنون خدا به شما تخفیف داد و معلوم داشت که در شما ضعفی هست؛ بنابراین اگر از شما صد نفر صابر باشند بر دویست نفر چیره می شوند، و اگر هزار نفر [صابر] باشند، به فرمان خدا بر دو هزار نفر چیره می شوند؛ و خدا با صابران است.'),
(1227, 8, 67, 'هیچ پیامبری را نسزد و نشاید که [پیش از جنگ و پیروزی بر دشمن] اسیران جنگی بگیرد، تا زمانی که [دشمن را به طور کامل] در زمین از قدرت و توان بیندازد [و موقعیت دین و مؤمنان در جهان پابرجا و استوار گردد آن گاه اسیر بگیرد؛ شما ای یاران پیامبر!] متاع ناچیز و ناپایدار دنیا را می خواهید، و خدا [برای شما] آخرت را می خواهد؛ و خدا توانای شکست ناپذیر و حکیم است.'),
(1228, 8, 68, 'اگر فرمان و حکم لازمی که از سوی خدا گذشته است، نبود [که پیش از اتمام حجت کسی را عذاب نکند] مسلماً شما را به سبب اسیرانی که [بدون اجازه پیامبر و پیش از شروع جنگ] گرفتید، عذابی بزرگ می رسید.'),
(1229, 8, 69, 'آنچه از غنیمت [در میدان جنگ] گرفته اید، حلال و پاکیزه بخورید و از خدا پروا کنید؛ یقیناً خدا بسیار آمرزنده و مهربان است.'),
(1230, 8, 70, 'ای پیامبر! به اسیرانی که در اختیار شمایند، بگو: اگر خدا خیری در دل های شما بداند، بهتر از مالی که [در برابر آزادی شما] از شما گرفته اند به شما می دهد، و گناهانتان را می آمرزد و خدا بسیار آمرزنده و مهربان است.'),
(1231, 8, 71, 'و اگر اسیران آزاد شده بخواهند [پس از آزاد شدنشان] به تو خیانت ورزند، [کار جدید و تازه ای نیست] آنان پیش از این هم به خدا خیانت ورزیدند، پس او تو را بر آنان مسلط ساخت؛ و خدا دانا و حکیم است.'),
(1232, 8, 72, 'مسلماً کسانی که ایمان آوردند، و هجرت کردند، و با اموال و جان هایشان در راه خدا جهاد نمودند، و کسانی که مهاجران را پناه و جا دادند و یاری کردند، اینانند که یار و دوست یکدیگرند. و کسانی که ایمان آوردند و هجرت نکردند شما را از دوستی آنان هیچ سودی نیست تازمانی که هجرت کنند؛ و اگر در امور دین از شما یاری طلبند، بر شماست که آنان را یاری دهید مگر آنکه یاری دادن شما به زیان گروهی باشد که میان شما و آنان پیمان [ترک جنگ] برقرار است؛ و خدا به آنچه انجام می دهید، بیناست.'),
(1233, 8, 73, 'و آنان که کافرند، دوستان و یاران یکدیگرند [و] اگر شما فرمان پیوند [با اهل ایمان و قطع رابطه با کافران] را به اجرا نگذارید فتنه و فسادی بزرگ در زمین پیدا خواهد شد.'),
(1234, 8, 74, 'و کسانی که ایمان آوردند و هجرت کردند و در راه خدا جهاد نمودند و آنان که مهاجران را پناه دادند و یاری کردند، مؤمنان حقیقی فقط آنانند، برای آنان آمرزش و رزق نیکو و فراوانی است.'),
(1235, 8, 75, 'و آنان که بعد [از مؤمنان و نخستین مهاجران] ایمان آوردند و هجرت کردند و همراه شما با دشمنان جنگیدند، از شمایند؛ و خویشاوندان [بنابر آنچه نسبت به برنامه ارث] در کتاب خدا مقرّر شده از دیگران سزاوارترند؛ یقیناً خدا به همه چیز داناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'این [اعلامِ] بیزاری و لغو پیمان از سوی خدا و پیامبرش به کسانی از مشرکان است که با آنان پیمان بسته اید.'),
(1237, 9, 2, 'بنابراین، چهار ماه حرام را [در کمال آزادی و امنیت] در زمین گردش کنید و بدانید که شما عاجز کننده خدا نیستید [تا بتوانید از دسترس قدرت او بیرون روید] و خدا خوار کننده کافران است.'),
(1238, 9, 3, 'و این اعلامی است از سوی خدا و پیامبرش به همه مردم در روز حج اکبر که: یقیناً خدا و پیامبرش از مشرکان بیزارند [و هیچ تعهدی نسبت به آنان ندارند]؛ پس [ای مشرکان!] اگر [از پیمان شکنی و خیانت] توبه کنید [و مسلمان شوید] برای شما بهتر است و اگر روی [از وفای به پیمان و اسلام] بگردانید، بدانید که شما عاجز کننده خدا نیستید [تا بتوانید از دسترس قدرت او بیرون روید]؛ و کسانی را که کفر ورزیدند، به عذابی دردناک مژده ده.'),
(1239, 9, 4, 'مگر کسانی از مشرکان که با آنان پیمان بستید، سپس چیزی [از شرایط پیمان را] نسبت به شما نکاستند و احدی [از دشمنان را] بر ضد شما یاری نکردند، پس به پیمانشان تا پایان مدتشان وفادار باشید؛ زیرا خدا پرهیزکاران را دوست دارد.'),
(1240, 9, 5, 'پس هنگامی که ماه های حرام سپری شود، مشرکان را هر جا یافتید، بکشید و به اسیری بگیرید و محاصره کنید و در هر کمین گاهی به کمین آنان بنشینید؛ ولی اگر توبه کردند و نماز را بر پا داشتند و زکات پرداختند، پس آزادشان گذارید؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(1241, 9, 6, 'و اگر یکی از مشرکان از تو پناه خواست، پس پناهش بده تا سخن خدا را بشنود، آن گاه او را به جایگاه امنش برسان؛ این به سبب آن است که آنان گروهی هستند که [حقایق را] نمی دانند [باشد که در پناه تو و شنیدن سخن حق مسلمان شوند.]'),
(1242, 9, 7, 'چگونه مشرکان را نزد خدا و پیامبرش پیمانی [استوار] تواند بود [در صورتی که همواره پیمان شکنی می کنند] مگر کسانی که با آنان در کنار مسجد الحرام پیمان بسته اید، پس تا زمانی که [به پیمانشان] با شما پایداری کنند، شما هم به پیمانتان با آنان پایداری کنید؛ زیرا خدا پرهیزکاران را دوست دارد.'),
(1243, 9, 8, 'چگونه [مشرکان بر پیمان خود پای بندند؟] و در صورتی که اگر بر شما چیره شوند، نه [پیوندِ] خویشاوندی را در حقّ شما رعایت می کنند، نه پیمانی را!! شما را با زبانشان خشنود می کنند، ولی دل هایشان [از خشنود کردن شما] امتناع دارد و بیشترشان فاسقند.'),
(1244, 9, 9, 'آیات خدا را در برابر بهایی اندک فروختند و مردم را از راه خدا بازداشتند؛ راستی چه بد است آنچه را همواره انجام می دادند.'),
(1245, 9, 10, 'در حقّ هیچ مؤمنی رعایتِ [پیوندِ] خویشاوندی و پیمانی را نمی کنند؛ و آنان همان تجاوز کارانند.'),
(1246, 9, 11, 'پس اگر [از پیمان شکنی، قطع رحم، کفر و شرک] توبه کنند و نماز را بر پا دارند و زکات بپردازند، برادران دینی شمایند؛ و ما آیات خود را برای گروهی که [واقعیات را] می دانند، به صورت های گوناگون بیان می کنیم.'),
(1247, 9, 12, 'و اگر پیمان هایشان را پس از تعهدشان شکستند و در دین شما زبان به طعنه و عیب جویی گشودند، در این صورت با پیشوایان کفر بجنگید که آنان را [نسبت به پیمان هایشان] هیچ تعهدی نیست، باشد که [از طعنه زدن و پیمان شکنی] بازایستند.'),
(1248, 9, 13, 'چرا و برای چه نمی جنگید؟ آن هم با گروهی که پیمان های خود را شکستند، و عزمشان را بر بیرون کردن پیامبر از وطنش جزم کردند و هم آنان بودند که نخستین بار با شما جنگیدند، آیا از آنان می ترسید؟! در صورتی که اگر مؤمن هستید، خدا سزاوارتر است که از او بترسید.'),
(1249, 9, 14, 'با آنان بجنگید تا خدا آنان را به دست شما عذاب کند و رسوایشان نماید و شما را بر آنان پیروزی دهد و سینه های [پر سوز و غم] مردم مؤمن را شفا بخشد.'),
(1250, 9, 15, 'و خشم دل هایشان را از میان ببرد؛ و خدا توبه هر کس را بخواهد می پذیرد؛ و خدا دانا و حکیم است.'),
(1251, 9, 16, 'آیا گمان کرده اید که شما را به خود واگذارند [و به بوته آزمایش نیازمایند] در حالی که هنوز کسانی از شما را که جهاد کردند و غیر خدا و پیامبرش و مؤمنان را محرم اسرار خود نگرفتند، از دیگران معلوم و مشخص نکرده است [یقیناً باید آزمایش شوید تا مؤمن از غیر مؤمن معلوم و مشخص شود]؛ و خدا به آنچه انجام می دهید، آگاه است.'),
(1252, 9, 17, 'و مشرکان در حالی که بر ضد خود به کفر [و انکار حقایق] گواهی می دهند، صلاحیت آباد کردن مساجد خدا را ندارند؛ اینانند که اعمالشان تباه و بی اثر است و در آتش جاودانه اند.'),
(1253, 9, 18, 'آباد کردن مساجد خدا فقط در صلاحیت کسانی است که به خدا و روز قیامت ایمان آورده و نماز را بر پا داشته و زکات پرداخته و جز از خدا نترسیده اند؛ پس امید است که اینان از راه یافتگان باشند.'),
(1254, 9, 19, 'آیا آب دادن به حاجیان و آباد کردن مسجدالحرام را مانند [عمل] کسی قرار داده اید که به خدا و روز قیامت ایمان آورده و در راه خدا جهاد کرده است؟! [این دو] نزد خدا برابر و یکسان نیستند و خدا گروه ستمکاران را هدایت نمی کند.'),
(1255, 9, 20, 'آنان که ایمان آوردند و هجرت کردند و با اموال و جان هایشان در راه خدا به جهاد برخاستند، منزلتشان در پیشگاه خدا بزرگ تر و برتر است، و فقط اینانند که کامیابند.'),
(1256, 9, 21, 'پروردگارشان آنان را نزد خود به رحمت و خشنودی و بهشت هایی که برای آنان در آنها نعمت های پایدار است، مژده می دهد.'),
(1257, 9, 22, 'همواره در آنجا جاودانه اند؛ یقیناً نزد خدا پاداشی بزرگ است.'),
(1258, 9, 23, 'ای اهل ایمان! اگر پدرانتان و برادرانتان کفر را بر ایمان ترجیح دهند، آنان را دوستان و سرپرستان خود مگیرید؛ و کسانی از شما که آنان را دوست و سرپرست خود گیرند، هم اینانند که ستمکارند.'),
(1259, 9, 24, 'بگو: اگر پدرانتان و فرزندانتان و برادرانتان و همسرانتان و خویشانتان و اموالی که فراهم آورده اید و تجارتی که از بی رونقی و کسادی اش می ترسید و خانه هایی که به آنها دل خوش کرده اید، نزد شما از خدا و پیامبرش و جهاد در راهش محبوب ترند، پس منتظر بمانید تا خدا فرمان عذابش را بیاورد؛ و خدا گروه فاسقان را هدایت نمی کند.'),
(1260, 9, 25, 'بی تردید خدا شما را در جبهه های زیاد و عرصه های بسیار یاری کرد و [به ویژه] روز [نبرد] حنین، آن زمان که فزونی افرادتان شما را مغرور و شگفت زده کرد، ولی [فزونی عدد] چیزی از خطر را از شما برطرف نکرد، و زمین با همه وسعت و فراخی اش بر شما تنگ شد، سپس پشت به دشمن از عرصه نبرد گریختید.'),
(1261, 9, 26, 'آن گاه خدا آرامش خود را [که حالت طمأنینه قلبی است] بر پیامبرش و مؤمنان نازل کرد، و لشکریانی که آنان را نمی دیدید [برای یاری مؤمنان] فرود آورد، و کسانی را که کفر می ورزیدند، به عذاب سختی مجازات کرد؛ و این است کیفر کفرپیشگان.'),
(1262, 9, 27, 'سپس خدا بعد از این [گناه بزرگ که فرار از جنگ است] توبه هر کس را که بخواهد می پذیرد؛ و خدا بسیار آمرزنده و مهربان است.'),
(1263, 9, 28, 'ای اهل ایمان! جز این نیست که مشرکان پلیدند؛ پس نباید بعد از امسال به مسجدالحرام نزدیک شوند؛ و اگر [به سبب قطع رابطه با آنان و تعطیل شدن داد و ستد با ایشان] از بی نوایی و تنگدستی می ترسید، خدا اگر بخواهد شما را از فضل و احسانش بی نیاز می کند؛ یقیناً خدا دانا و حکیم است.'),
(1264, 9, 29, 'با کسانی از اهل کتاب که به خدا و روز قیامت ایمان نمی آورند، و آنچه را خدا و پیامبرش حرام کرده اند، حرام نمی شمارند، و دین حق را نمی پذیرند، بجنگید تا با دست خود در حالی که [نسبت به احکام دولت اسلامی] متواضع و فروتن اند، جزیه بپردازند.'),
(1265, 9, 30, 'و یهود گفتند: عُزیر، پسر خداست. و نصاری گفتند: مسیح، پسر خداست. این گفتاری [بی دلیل و برهان] است که به زبان می آورند، و به گفتار کسانی که پیش از این [به حقایق] کفر ورزیدند، شباهت دارد؛ خدا آنان را نابود کند، چگونه [از حق به باطل] منحرف می شوند.'),
(1266, 9, 31, 'آنان دانشمندان و راهبانشان و مسیح پسر مریم را به جای خدا به خدایی گرفتند؛ در حالی که مأمور نبودند مگر اینکه معبود یگانه را که هیچ معبودی جز او نیست بپرستند؛ منزّه و پاک است از آنچه شریک او قرار می دهند.'),
(1267, 9, 32, 'همواره می خواهند نور خدا را با سخنان باطل [و تبلیغاتِ بی پایه] خود خاموش کنند؛ ولی خدا جز اینکه نور خود را کامل کند، نمی خواهد، هر چند کافران خوش نداشته باشند.'),
(1268, 9, 33, 'اوست که پیامبرش را با هدایت و دین حق فرستاد، تا آن را بر همه ادیان پیروز گرداند، هر چند مشرکان خوش نداشته باشند.'),
(1269, 9, 34, 'ای اهل ایمان! یقیناً بسیاری از عالمان یهود و راهبان، اموال مردم را به باطل [و به صورتی نامشروع] می خورند و همواره [مردم را] از راه خدا بازمی دارند؛ و کسانی را که طلا و نقره می اندوزند و آن را در راه خدا هزینه نمی کنند، به عذاب دردناکی مژده ده.'),
(1270, 9, 35, 'روزی که آن اندوخته ها را در آتش دوزخ به شدّت گرما دهند و پیشانی و پهلو و پشتشان را به آن داغ کنند [و به آنان نهیب زنند] این است ثروتی که برای خود اندوختید، پس کیفر زراندوزی خود را بچشید.'),
(1271, 9, 36, 'یقیناً شماره ماه ها در پیشگاه خدا از روزی که آسمان ها و زمین را آفریده در کتاب [علم] خدا دوازده ماه است؛ از آنها چهار ماهش ماه حرام است؛ این است حساب استوار و پایدار؛ پس در این چهار ماه [با جنگ و فتنه و خونریزی] بر خود ستم روا مدارید و با همه مشرکان همان گونه که آنان با همه شما می جنگند، بجنگید و بدانید خدا با پرهیزکاران است.'),
(1272, 9, 37, 'بی تردید به تأخیر انداختن [حرمت ماهی به ماه دیگر] افزایشی در کفر است؛ [و این بدعتی است که سردمداران کفر] کافران را به سبب آن [نسبت به ماه های حرام واقعی] گمراه می کنند، یک سال ماه حرام را حلال می شمارند و در دیگر سال آن را حرام می دانند تا با شماره ماه هایی که خدا حرام کرده هماهنگ و مطابق سازند ولی [در نهایت] آنچه را خدا حرام کرده از پیش خود حلال می کنند؛ زشتی کارهایشان در نظرشان آراسته شده و خدا گروه کافران را هدایت نمی کند.'),
(1273, 9, 38, 'ای اهل ایمان! شما را چه عذر و بهانه ای است هنگامی که به شما گویند: برای نبرد در راه خدا باشتاب [از شهر و دیارتان] بیرون روید؛ به سستی و کاهلی می گرایید [و به دنیا و شهواتش میل می کنید؟!] آیا به زندگی دنیا به جای آخرت دل خوش شده اید؟ کالای زندگی دنیا در برابر آخرت جز کالایی اندک نیست.'),
(1274, 9, 39, 'اگر باشتاب بیرون نروید، خدا شما را به عذابی دردناک عذاب می کند و گروه دیگری را به جای شما می آورد؛ و شما [با نرفتن به میدان نبرد] هیچ زیانی به خدا نمی رسانید؛ و خدا بر هر کاری تواناست.'),
(1275, 9, 40, 'اگر پیامبر را یاری ندهید، یقیناً خدا او را یاری می دهد؛ چنان که او را یاری داد هنگامی که کافران از مکه بیرونش کردند در حالی که یکی از دو تن بود، آن زمان هر دو در غار [ثور نزدیک مکه] بودند، همان زمانی که به همراهش گفت: اندوه به خود راه مده خدا با ماست. پس خدا آرامش خود را [که حالت طمأنینه قلبی است] بر پیامبر نازل کرد، و او را با لشکریانی که شما ندیدید، نیرومند ساخت، و شعار کافران را پست تر قرار داد، و شعار خداست که شعار والاتر و برتر است؛ و خدا توانای شکست ناپذیر و حکیم است.'),
(1276, 9, 41, 'با شتاب با تجهیزات سبک و سنگین [و سواره، پیاده، پیر و جوان به سوی میدان نبرد] بیرون روید، و با اموال و جان هایتان در راه خدا جهاد کنید که اگر دانا [یِ به حقایق] باشید این برای شما بهتر است.'),
(1277, 9, 42, 'اگر [برای مسلمانان سست اراده و منافقانِ مسلمان نما] غنیمتی [بی رنج و مشقت] در دسترس بود و [راه] سفر [به سوی میدان نبرد] کوتاه و آسان بود، مسلماً به دنبال تو می آمدند، ولی پیمودن راه طولانی پر مشقت به نظرشان طاقت فرسا آمد، و به زودی به خدا سوگند می خورند که اگر توانایی داشتیم یقیناً با شما بیرون می آمدیم [آنان] خود را [به دروغ و تزویرشان] هلاک می کنند و خدا می داند که بی تردید آنان دروغگویند.'),
(1278, 9, 43, 'خدا تو را مورد بخشش و لطف قرار دهد، چرا پیش از آنکه [راستگویی] راستگویان بر تو روشن شود، و دروغگویان را بشناسی [از روی مهر و محبتی که به ایشان داری] به آنان اجازه [ترک جنگ] دادی؟'),
(1279, 9, 44, 'آنان که به خدا و روز قیامت ایمان دارند از تو برای بازایستادن از جهاد با اموال و جان هایشان اجازه نمی خواهند [بلکه در هر شرایطی مشتاقانه به سوی میدان نبرد بیرون می روند]؛ و خدا به پرواپیشگان داناست.'),
(1280, 9, 45, 'فقط کسانی از تو اجازه ترک جهاد می خواهند که به خدا و روز قیامت ایمان ندارند و دل هایشان [در انجام فرمان های حق] دچار تردید است و همواره در تردیدشان سرگردانند.'),
(1281, 9, 46, 'و اگر برای بیرون رفتن [به سوی میدان نبرد] تصمیم جدّی داشتند، مسلماً برای آن ساز و برگ آماده می کردند، ولی خدا [به سبب سستی اراده و نفاقشان] برانگیختن آنان را [به سوی میدان نبرد] خوش نداشت، پس آنان را از حرکت بازداشت، و [انگار به آنان] گفته شد: با نشستگان [در خانه ها که به علتی معذور از جنگند] بنشینید.'),
(1282, 9, 47, 'اگر [هم] با شما بیرون می آمدند، جز شرّ و فساد به شما نمی افزودند و مسلماً خود را برای سخن چینی [و نمّامی] در میان شما قرار می دادند تا [برای از هم گسستن شیرازه سپاه اسلام] فتنه جویی کنند و در میان شما جاسوسانی برای آنان هستند [که به نفعشان خبرچینی می کنند]؛ و خدا به ستمکاران داناست.'),
(1283, 9, 48, 'قطعاً پیش از این هم فتنه جویی می کردند و امور را بر تو وارونه و دگرگون می ساختند، تا آنکه [یاریِ] حق آمد و فرمان خدا [که تحقّق پیروزی و موفقیت شماست] آشکار شد، در حالی که آنان خوش نداشتند.'),
(1284, 9, 49, 'و از منافقان کسانی هستند که می گویند: ما را اجازه ترک نبرد ده و به فتنه و گناه دچار مکن. آگاه باش که [آنان با این درخواست ناهنجارشان] به فتنه و گناه افتاده اند؛ و یقیناً دوزخ بر کافران احاطه دارد.'),
(1285, 9, 50, 'اگر [در میدان نبرد] پیروزی و غنیمت به تو رسد، غمگینشان می کند، و اگر تو را آسیب و زیانی رسد، می گویند: ما پیش از این تصمیم خود را [بر شرکت نکردن در جنگ] گرفته بودیم [و احتیاط را از دست ندادیم] و در حالی که خوشحال و شادمانند [به خانه و زندگی و به سوی امور مادی] بازمی گردند.'),
(1286, 9, 51, 'بگو: هرگز به ما جز آنچه خدا لازم و مقرّر کرده، نخواهد رسید، او سرپرست و یار ماست و مؤمنان فقط باید بر خدا توکل کنند.'),
(1287, 9, 52, 'بگو: آیا درباره ما جز یکی از دو نیکی [پیروزی یا شهادت] را انتظار می برید؟ در صورتی که ما درباره شما انتظار می بریم که خدا از سوی خود یا به دست ما عذابی به شما برساند؛ پس انتظار برید که ما هم با شما منتظریم.'),
(1288, 9, 53, 'بگو: [ای منافقان!] چه از روی میل و رغبت یا بی میلی و اکراه انفاق کنید، هرگز از شما پذیرفته نشود؛ زیرا شما گروهی فاسق هستید.'),
(1289, 9, 54, 'هیچ چیز آنان را از پذیرفته شدن انفاقشان بازنداشت، مگر آنکه آنان به خدا و پیامبرش کافر شدند، و نماز را جز با کسالت وسستی به جا نمی آورند، و جز با بی میلی و ناخشنودی انفاق نمی کنند.'),
(1290, 9, 55, 'اموال و فرزندانشان تو را به شگفت نیاورد؛ خدا می خواهد آنان را در این زندگی دنیا به وسیله آنها عذاب کند، و جانشان در حالی که کافرند بیرون رود.'),
(1291, 9, 56, '[با کمال بی شرمی] به خدا سوگند یاد می کنند که حتماً از زمره شمایند؛ در صورتی که از شما نیستند، بلکه گروهی اند که از شما [به سبب ایمان استوارتان] در اضطراب و ترس به سر می برند.'),
(1292, 9, 57, 'اگر پناهگاهی یا غارهایی یا گریزگاهی می یافتند، شتابان به سوی آنها روی می آوردند.'),
(1293, 9, 58, 'برخی از آنان نسبت به [تقسیم] صدقات بر تو خرده می گیرند، پس اگر از صدقات به آنان داده شود خشنود می شوند، و اگر داده نشود، ناگاه خشمگین می شوند.'),
(1294, 9, 59, 'و اگر آنان به آنچه خدا و پیامبرش به ایشان عطا کرده اند، خشنود می شدند و می گفتند: خدا ما را بس است؛ خدا و پیامبرش به زودی از فضل و احسان خود به ما عطا می کنند [و] ما فقط به سوی خدا مایل و علاقمندیم [برای آنان بهتر بود.]'),
(1295, 9, 60, 'صدقات، فقط ویژه نیازمندان و تهیدستان [زمین گیر] و کارگزاران [جمع و پخش آن] و آنانکه باید [به خاطر تمایل به اسلام] قلوبشان را به دست آورد، و برای [آزادی] بردگان و [پرداخت بدهی] بدهکاران و [هزینه کردن] در راه خدا [که شامل هر کار خیر و عام المنفعه می باشد] و در راه ماندگان است؛ [این احکام] فریضه ای از سوی خداست، و خدا دانا و حکیم است.'),
(1296, 9, 61, 'و از منافقان کسانی هستند که همواره پیامبر را آزار می دهند، و می گویند: شخص زود باور و نسبت به سخن این و آن سراپا گوش است. بگو: او در جهت مصلحت شما سراپا گوش و زود باور خوبی است، به خدا ایمان دارد و فقط به مؤمنان اعتماد می ورزد، و برای کسانی از شما که ایمان آورده اند، رحمت است، و برای آنانکه همواره پیامبر خدا را آزار می دهند، عذابی دردناک است.'),
(1297, 9, 62, 'آنان برای شما [از روی حیله و تزویر برای معذور نشان دادن خود نسبت به کارهای ناهنجارشان] به خدا سوگند می خورند تا شما را راضی و خشنود سازند، در صورتی که اگر مؤمن بودند، شایسته تر آن بود که خدا و رسولش را خشنود کنند.'),
(1298, 9, 63, 'آیا ندانسته اند که هر کس با خدا و رسولش دشمنی و مخالفت کند، مسلماً آتش دوزخ برای اوست که در آن جاودانه است؛ این [همان] رسوایی بزرگ است.'),
(1299, 9, 64, 'منافقان از اینکه سوره ای بر ضدشان نازل شود که آنان را از اسراری که [در جهت دشمنی با خدا، پیامبر، مؤمنان و حکومت اسلامی] در دل هایشان وجود دارد، آگاه نماید اظهار ترس و نگرانی می کنند؛ بگو: مسخره کنید، خدا آنچه را از آن بیمناک و نگرانید، آشکار خواهد کرد.'),
(1300, 9, 65, 'و اگر [نسبت به اعمال ناهنجار و گفتار باطلشان] از آنان بازخواست کنی، قاطعانه می گویند: فقط شوخی وبازی کردیم! بگو: آیا خدا وآیات او و پیامبرش را مسخره می کردید؟!'),
(1301, 9, 66, '[بگو: نسبت به اعمال و گفتارتان] عذرخواهی نکنید که [عذرخواهی شما را پایه واساسی نیست] یقیناً شما پس از ایمانتان کافر شدید؛ اگر از گروهی از شما [که تابع و دنباله رو بودید] درگذریم، گروه دیگر را [که سردمداران برنامه های منافقانه بودند] به سبب آنکه همواره [در جامعه اسلامی] دست به جرم و خطا می زدند، قطعاً عذاب می کنیم.'),
(1302, 9, 67, 'مردان و زنان منافق همانند و مشابه یکدیگرند، به کار بد فرمان می دهند و از کار نیک باز می دارند و از انفاق در راه خدا امساک می ورزند، خدا را فراموش کردند و خدا هم آنان را [از لطف و رحمت خود] محروم کرد؛ یقیناً منافقانند که فاسق اند.'),
(1303, 9, 68, 'خدا آتش دوزخ را به مردان و زنان منافق و کافران وعده داده، در آن جاودانه اند، همان برای آنان بس است و خدا لعنتشان کرده و برای آنان عذابی پایدار است.'),
(1304, 9, 69, '[همه شما منافقان و کافران در نفاق و کفر] مانند کسانی هستید که پیش از شما بودند؛ آنان از شما نیرومندتر و اموال و فرزندانشان بیشتر بود؛ آنان از سهمشان [که در دنیا از نعمت های خدا داشتند در امور باطل] بهره گرفتند؛ پس شما نیز از سهمتان همان گونه که آنان از سهمشان بهره گرفتند بهره گرفتید، و به صورتی که [آنان در شهواتشان] فرو رفتند فرو رفتید؛ اینانند که اعمالشان در دنیا و آخرت تباه و بی اثر است و در حقیقت اینانند که زیانکارند.'),
(1305, 9, 70, 'آیا خبر کسانی که پیش از آنان بودند به اینان نرسیده؟ خبر قوم نوح و عاد و ثمود و قوم ابراهیم و اصحاب مدین و شهرهای زیر و رو شده [قوم لوط] که پیامبرانشان برای آنان دلایل روشن آوردند [ولی نپذیرفتند]؛ خدا بر آن نبود که به آنان ستم ورزد، ولی آنان بودند که همواره بر خود ستم می کردند.'),
(1306, 9, 71, 'مردان و زنان با ایمان دوست و یار یکدیگرند؛ همواره به کارهای نیک و شایسته فرمان می دهند و از کارهای زشت و ناپسند بازمی دارند، و نماز را برپا می کنند، و زکات می پردازند، و از خدا و پیامبرش اطاعت می نمایند؛ یقیناً خدا آنان را مورد رحمت قرار می دهد؛ زیرا خدا توانای شکست ناپذیر و حکیم است.'),
(1307, 9, 72, 'خدا به مردان و زنان با ایمان بهشت هایی را وعده داده که از زیرِ [درختانِ] آن نهرها جاری است؟ در آن جاودانه اند، و نیز سراهای پاکیزه ای را در بهشت های ابدی [وعده فرموده] و هم چنین خشنودی و رضایتی از سوی خدا [که از همه آن نعمت ها] بزرگ تر است؛ این همان کامیابی بزرگ است.'),
(1308, 9, 73, 'ای پیامبر! با کافران و منافقان به جهاد برخیز و نسبت به آنان سخت گیری کن [و درشت خو باش]؛ و جایگاهشان دوزخ است؛ و دوزخ بد بازگشت گاهی است.'),
(1309, 9, 74, 'همواره سوگند می خورند که [بر ضد پیامبر سخن ناروا و نادرست] نگفته اند، در صورتی که سخن کفرآمیز گفته اند و پس از اسلامشان کافر شده اند و به آنچه [از اهداف خائنانه ای که] دست نیافتند، اهتمام ورزیدند، و زبان به عیب جویی و انکار [نسبت به پیامبر] نگشودند مگر پس از آنکه خدا و پیامبرش آنان را از فضل و احسان خود توانگر ساختند؛ پس اگر توبه کنند برای آنان بهتر است؛ و اگر روی [از خدا و پیامبر] برگردانند، خدا آنان را در دنیا و آخرت به عذابی دردناک مجازات خواهد کرد و آنان را در زمین سرپرست و یاری [برای نجاتشان از چنگال عذاب] نخواهد بود.'),
(1310, 9, 75, 'از منافقان کسانی هستند که با خدا پیمان بستند، چنانچه خدا از فضل و احسانش به ما عطا کند، یقیناً صدقه خواهیم داد و از شایستگان خواهیم شد.'),
(1311, 9, 76, 'هنگامی که خدا از فضل و احسانش به آنان عطا کرد نسبت به [هزینه کردن] آن [در راه خدا] بخل ورزیدند و اعراض کنان [از پیمانشان] روی گرداندند.'),
(1312, 9, 77, 'پس برای آنکه به وعده های خود با خدا وفا نکردند، و همواره دروغ می گفتند، نفاقی [ثابت] در دل هایشان تا روزی که خدا را ملاقات کنند، باقی گذاشت.'),
(1313, 9, 78, 'آیا ندانستند که خدا اسرار و پنهان سخن گفتن آنان را می داند و قطعاً خدا دانای به همه نهان هاست؟!'),
(1314, 9, 79, 'آنان که در رابطه باصدقات از مؤمنانی که [افزون بر صدقه واجبشان از روی رضا ورغبت] صدقه [مستحبی] می پردازند، عیب جویی می کنند، و کسانی را که جز به اندازه قدرتشان [ثروت و مالی] نمی یابند [تا صدقه دهند] مسخره می کنند؛ خدا هم کیفر مسخره آنان را خواهدداد و برای آنان عذابی دردناک خواهدبود.'),
(1315, 9, 80, 'برای آنان [که عیب جویان مسخره کننده اند] چه آمرزش بخواهی چه نخواهی [یکسان است] اگر برای آنان هفتاد بار هم آمرزش بخواهی، خدا هرگز آنان را نخواهد آمرزید؛ زیرا آنان به خدا و پیامبرش کفر ورزیدند و خدا گروه فاسقان را هدایت نمی کند.'),
(1316, 9, 81, 'بر جای ماندگان [از جنگ تبوک] از خانه نشستن خود به سبب مخالفت با پیامبر خدا خوشحال شدند و خوش نداشتند که با اموال و جانهایشان در راه خدا جهاد کنند، و [به مؤمنان] گفتند: در این گرما [برای جهاد] بیرون نروید. بگو: آتش دوزخ در حرارت و گرمی، بسیار سخت تر است، اگر می فهمیدید.'),
(1317, 9, 82, 'پس به کیفر گناهانی که همواره مرتکب می شدند باید کمتر بخندند و بسیار بگریند.'),
(1318, 9, 83, 'چنانچه خدا تو را [از سفر جنگ تبوک] به سوی گروهی از آنان [که بدون عذر از جنگ بازماندند] بازگردانید و آنان برای بیرون آمدن [به سوی جنگی دیگر] از تو اجازه خواستند، پس بگو: هرگز با من بیرون نخواهید آمد، و هرگز همراه من با هیچ دشمنی نخواهید جنگید؛ زیرا شما نخستین بار به نشستن در خانه [و ترک جنگ] خوشحال شدید، اکنون هم با مخالفین بنشینید.'),
(1319, 9, 84, 'و هرگز به جنازه هیچ کدام از آنان نماز مخوان و بر گورش [برای دعا و طلب آمرزش] نایست؛ زیرا آنان به خدا و پیامبرش کافر شدند و در حالی که فاسق بودند، از دنیا رفتند.'),
(1320, 9, 85, 'اموال و فرزندانشان تو را به شگفت نیاورد [این ها برای آنان خوشبختی نیست] خدا فقط می خواهد آنان را در دنیا به اموال و فرزندانشان عذاب کند، و در حالی که کافرند جانشان درآید.'),
(1321, 9, 86, 'و چون سوره ای نازل شود که: [در آن سوره از آنان خواسته اند] به خدا ایمان آورید و همراه پیامبرش جهاد کنید؛ ثروتمندان و قدرتمندان [منافق] از تو اجازه می خواهند [که در جهاد شرکت نکنند] و می گویند: بگذار که ما با خانه نشینان باشیم.'),
(1322, 9, 87, 'آنان راضی شده اند که با خانه نشینان باشند!! بر دل هایشان مهر تیره بختی زده شده پس [به همین سبب] آنان [منافع جهاد در راه خدا و بهره های آخرتی آن را] نمی فهمند.'),
(1323, 9, 88, 'ولی پیامبر و کسانی که با او ایمان آوردند با اموال و جان هایشان جهاد کردند، اینانند که همه خیرات [دنیا و آخرت] برای آنان است و اینانند که رستگارند.'),
(1324, 9, 89, 'خدا برای آنان بهشت هایی را آماده کرده است که از زیر [درختانِ] آن نهرها جاری است، در آنجا جاودانه اند، این است کامیابی بزرگ.'),
(1325, 9, 90, 'عذر آورندگان از بادیه نشینان نزد تو آمدند تا به آنان اجازه [ترک جنگ] داده شود، و کسانی که به خدا و رسولش دروغ گفتند [بدون آمدن نزد تو و بی هیچ عذری در خانه] نشستند، به زودی به کسانی از آنان که کفر ورزیدند، عذابی دردناک خواهد رسید.'),
(1326, 9, 91, 'بر ناتوانان و بیماران و آنان که چیزی برای هزینه کردن [در راه جهاد] نمی یابند، گناهی نیست [که در جهاد شرکت نکنند] در صورتی که [در پشت جبهه با اعمال و گفتارشان] برای خدا و پیامبرش خیرخواهی کنند [و از این طریق به حمایت رزمندگان برخیزند]؛ آری، بر نیکوکاران [معذور] هیچ مؤاخذه و سرزنشی نیست، و خدا بسیار آمرزنده و مهربان است.'),
(1327, 9, 92, 'و نیز بر کسانی که هنگامی که نزد تو آمدند تا آنان را [برای رفتن به سوی نبرد] سوار مرکبی کنی، گفتی: [به سبب نبود امکانات] بر مرکبی دسترسی ندارم تا شما را به جهاد برم، هیچ مؤاخذه و سرزنشی نیست؛ [از نزد تو] بازگشتند در حالی که به خاطر غصه و اندوه از دیدگانشان اشک می ریخت که چرا چیزی نمی یابند تا [در نبرد با دشمنان] هزینه کنند.'),
(1328, 9, 93, 'راه مؤاخذه و سرزنش فقط بر ضد کسانی باز است که با آنکه توانگرند [باز هم برای ترک نبرد] از تو اجازه می خواهند؛ آنان راضی شدند که با خانه نشینان باشند، خدا بر دل هایشان مهر تیره بختی زد به همین سبب [حقایق را] نمی دانند.'),
(1329, 9, 94, 'هنگامی که به سوی آنان بازگردید، از شما [به سبب شرکت نکردن در جنگ] عذرخواهی می کنند، بگو: عذرخواهی نکنید، ما هرگز شما را باور نخواهیم کرد، خدا ما را از خیانت های شما آگاه کرد، و [دیگر بار هم] یقیناً خدا و پیامبرش کارهای شما را می بینند [و برای آنان روشن است که باز هم خیانت می ورزید] آنگاه [پس از پایان مهلت مقرر] به سوی دانای نهان و آشکار بازگردانده می شوید و شما را به خیانت هایی که همواره مرتکب می شدید، آگاه خواهد کرد.'),
(1330, 9, 95, 'هنگامی که به سوی آنان بازگردید، برای شما [در جهت معذور بودن خود] زود سوگند می خورند تا از آنان صرف نظر کنید؛ پس از آنان روی برگردانید؛ زیرا پلیدند و جایگاهشان به کیفر خیانت هایی که همواره مرتکب می شدند، دوزخ است.'),
(1331, 9, 96, 'برای شما [در جهت معذور بودن خود] سوگند می خورند تا از آنان راضی شوید، اگر شما هم از آنان راضی شوید، یقیناً خدا از گروه فاسقان راضی نخواهد شد.'),
(1332, 9, 97, 'بادیه نشینانِ [جزیرة العرب به سبب دوری از علم، دانش، فرهنگ و بینش] در کفر و نفاق [از دیگران] سخت تر و به جاهل بودن به احکام و حدود آنچه را خدا بر پیامبرش نازل کرده سزاوارترند، و خدا دانا و حکیم است.'),
(1333, 9, 98, 'و گروهی از بادیه نشینان کسانی هستند که آنچه را انفاق می کنند غرامت و تاوان می شمارند، و پیش آمدهای بدی را برای شما انتظار می برند، پیش آمدهای بد بر خودشان باد؛ و خدا شنوا و داناست.'),
(1334, 9, 99, 'و گروهی از بادیه نشینانِ [جزیرة العرب] کسانی هستند که به خدا و روز قیامت ایمان دارند و آنچه را انفاق می کنند، مایه قرب به خدا و دعاهای پیامبر می دانند؛ آگاه باشید! انفاقشان وسیله تقرّب برای آنان است، به زودی خدا آنان را در رحمتش در آورد؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(1335, 9, 100, 'پیشگامان نخستین از مهاجران و انصار و کسانی که به نیکی و درستی از آنان پیروی کردند، خدا از ایشان خشنود است و آنان هم از خدا راضی هستند؛ برای ایشان بهشت هایی آماده کرده که از زیرِ [درختانِ] آن نهرها جاری است، در آنجا برای ابد جاودانه اند؛ این است کامیابی بزرگ.'),
(1336, 9, 101, 'و گروهی از بادیه نشینانی که پیرامونتان هستند منافق اند؛ و نیز گروهی از اهل مدینه بر نفاق خو گرفته اند، تو آنان را نمی شناسی ما آنان را می شناسیم، به زودی آنان را دوبار عذاب می کنیم [عذابی در دنیا و عذابی در برزخ] سپس به سوی عذابی بزرگ بازگردانده می شوند.'),
(1337, 9, 102, 'و دیگرانی هستند که به گناهانشان اعتراف کردند، [و] اعمال شایسته را با اعمال بد درآمیختند، امید است خدا توبه آنان را بپذیرد زیرا خدا بسیار آمرزنده و مهربان است.'),
(1338, 9, 103, 'از اموالشان زکاتی دریافت کن که به سبب آن [نفوس و اموالشان را] پاک می کنی، و آنان را رشد و تکامل می دهی؛ و [به هنگام دریافت زکات] بر آنان دعا کن؛ زیرا دعای تو مایه آرامشی برای آنان است؛ و خدا شنوا و داناست.'),
(1339, 9, 104, 'آیا ندانسته اند که فقط خداست که از بندگانش توبه را می پذیرد و صدقات را دریافت می کند؟ و یقیناً خداست که بسیار توبه پذیر و مهربان است.'),
(1340, 9, 105, 'و بگو: عمل کنید یقیناً خدا و پیامبرش و مؤمنان اعمال شما را می بینند، و به زودی به سوی دانای نهان و آشکار بازگردانده می شوید، پس شما را به آنچه همواره انجام می دادید، آگاه می کند.'),
(1341, 9, 106, 'و گروهی دیگر کارشان موقوف به مشیّت خداست، یا آنان را عذاب می کند یا توبه آنان را می پذیرد؛ و خدا دانا و حکیم است.'),
(1342, 9, 107, 'و [از منافقان] کسانی هستند که بر پایه دورویی و نفاق، مسجدی ساختند برای آسیب رساندن و ترویج کفر و تفرقه افکنی میان مؤمنان و کمین گاهی برای [گردآمدن] کسانی که پیش از این با خدا و پیامبرش جنگیده بودند، سوگند سخت می خورند که ما با ساختن این مسجد جز خوبی [و خدمت] قصدی نداشتیم، ولی خدا گواهی می دهد که بی تردید آنان دروغگویند.'),
(1343, 9, 108, 'هرگز [برای عبادت و نماز] در آن مسجد نایست، قطعاً مسجدی که از نخستین روز بر پایه تقوا بنا شده شایسته تر است که در آن [به نماز و عبادت] بایستی، در آن مردانی هستند که خواهان پاکیزگی [و طهارت جسم و جان] هستند؛ و خدا پاکیزگان را دوست دارد.'),
(1344, 9, 109, 'آیا کسی که بنیاد [امورش] را بر پایه تقوای الهی و رضای او نهاده بهتر است یا کسی که بنیاد [امورش] را بر لب پرتگاهی سست و فروریختنی نهاده؟! و آن بنا با بناکننده اش در جهنم سقوط می کند؛ و خدا گروه ستمکاران را هدایت نمی کند.'),
(1345, 9, 110, 'همواره آن ساختمانی که بنا نهاده اند در دل هایشان مایه شک و تردید است تا دل هایشان [به سبب مرگ] پاره پاره شود، و خدا دانا و حکیم است.'),
(1346, 9, 111, 'یقیناً خدا از مؤمنان جان ها و اموالشان را به بهای آنکه بهشت برای آنان باشد خریده؛ همان کسانی که در راه خدا پیکار می کنند، پس [دشمن را] می کشند و [خود در راه خدا] کشته می شوند [خدا آنان را] بر عهده خود در تورات و انجیل و قرآن [وعده بهشت داده است] وعده ای حق؛ و چه کسی به عهد و پیمانش از خدا وفادارتر است؟ پس [ای مؤمنان!] به این داد و ستدی که انجام داده اید، خوشحال و شاد باشید؛ و این است کامیابی بزرگ.'),
(1347, 9, 112, '[آن مؤمنان، همان] توبه کنندگان، عبادت کنندگان، سپاس گزاران، روزه داران، رکوع کنندگان، سجده کنندگان، فرمان دهندگان به معروف و بازدارندگان از منکر و پاسداران حدود و مقرّرات خدایند؛ و مؤمنان را [به رحمت و رضوان خدا] مژده ده.'),
(1348, 9, 113, 'پیامبر و اهل ایمان را نسزد که برای مشرکان پس از آنکه روشن شد که آنان اهل دوزخند، درخواست آمرزش کنند، هر چند از خویشان باشند.'),
(1349, 9, 114, 'و آمرزش خواهی ابراهیم برای پدرش جز به سبب وعده ای که به او داده بود، نبود [که اگر از بت پرستی خودداری کند، برای او آمرزش بخواهد] چون برای او روشن شد که وی دشمن خداست از او بیزاری جست؛ یقیناً ابراهیم بسیار مهربان و بردبار بود.'),
(1350, 9, 115, 'و خدا بر آن نیست که قومی را پس از آنکه هدایت کرد، گمراه سازد، مگر آنکه اموری را که باید از آن بپرهیزند برای آنان بیان کند [و آنان مخالفت ورزند]؛ مسلماً خدا به همه چیز داناست.'),
(1351, 9, 116, 'یقیناً خداست که مالکیّت و فرمانروایی آسمان ها و زمین در سیطره اوست؛ زنده می کند و می میراند؛ و شما را هیچ سرپرست و یاری جز خدا نیست.'),
(1352, 9, 117, 'مسلماً خدا رحمت ویژه اش را بر پیامبر و مهاجران و انصار که در آن ساعت دشوار [جنگ تبوک] از او پیروی کردند، ارزانی داشت، پس از آنکه نزدیک بود دل های گروهی از آنان [به سبب سختی مسیر جنگ از حق] منحرف شود [و از ادامه مسیر بازایستند و به مدینه برگردند] سپس خدا توبه آنان را پذیرفت زیرا خدا نسبت به آنان بسیار رؤوف و مهربان است.'),
(1353, 9, 118, 'و [نیز رحمتش] شامل حال آن سه نفری [بود] که [با بهانه تراشیِ واهی از شرکت در جنگ] بازمانده بودند [و همه مسلمانان به دستور پیامبر با آنان قطع رابطه کردند] تا جایی که زمین با همه وسعت و فراخی اش بر آنان تنگ شد و [از شدت غصه، اندوه و عذاب وجدان] دل هایشان هم در تنگی و مضیقه قرار گرفت، و دانستند که هیچ پناهگاهی از خدا جز به سوی او نیست؛ پس خدا به رحمتش بر آنان توجه کرد تا توبه کنند؛ زیرا خدا بسیار توبه پذیر و مهربان است.'),
(1354, 9, 119, 'ای کسانی که ایمان آورده اید! از خدا پروا کنید و با صادقان باشید [صادقانی که کامل ترینشان پیامبران و اهل بیت رسول بزرگوار اسلام هستند.]'),
(1355, 9, 120, 'شایسته نیست که اهل مدینه و بادیه نشینانی که پیرامون آنانند، از رسول خدا تخلف کنند؛ و آنان را نسزد که به سبب پرداختن به خویش از حفظ جان او [در شداید و سختی ها] دریغ ورزند؛ زیرا هیچ تشنگی و رنج و گرسنگی در راه خدا به آنان نمی رسد، و در هیچ مکانی که کافران را به خشم می آورد، قدم نمی گذارند، و از هیچ دشمنی انتقام نمی گیرند [و با نبرد با او به کام دل نمی رسند] مگر آنکه به پاداش هر یک از آنان عمل شایسته ای در پرونده آنان ثبت می شود؛ چرا که خدا پاداش نیکوکاران را تباه نمی کند.'),
(1356, 9, 121, 'و هیچ مال کوچک و بزرگی را هزینه نمی کنند و هیچ سرزمینی را [برای نبرد با دشمن یا انجام کار خیری] نمی پیمایند مگر آنکه در پرونده اعمالشان ثبت می شود، تا خدا به آنان [نسبت به همه اعمالشان] با معیار گرفتن بهترین عملی که همواره انجام می دادند، پاداش دهد.'),
(1357, 9, 122, 'و مؤمنان را نسزد که همگی [به سوی جهاد] بیرون روند؛ چرا از هر جمیعتی گروهی [به سوی پیامبر] کوچ نمی کنند تا در دین آگاهی یابند و قوم خود را هنگامی که به سوی آنان بازگشتند، بیم دهند، باشد که [از مخالفت با خدا و عذاب او] بپرهیزند.'),
(1358, 9, 123, 'ای اهل ایمان! با کافرانی که هم جوار شما هستند، نبرد کنید؛ و آنان باید در شما سرسختی و شدت یابند؛ و بدانید که خدا با پرهیزکاران است.'),
(1359, 9, 124, 'و هنگامی که سوره ای نازل شود، برخی از منافقان به اهل ایمان گویند: این سوره، ایمان کدام یک از شما را افزود؟ ولی کسانی که ایمان آورده اند این سوره بر ایمانشان افزود، و آنان [از نزول این سوره] شادمان می شوند.'),
(1360, 9, 125, 'اما کسانی که در دل هایشان بیماری [نفاق] است، پس پلیدی بر پلیدیشان افزود و در حالی که کافر بودند از دنیا رفتند.'),
(1361, 9, 126, 'آیا نمی بینند که در هر سال یک بار یا دو بار [به وسیله جهاد یا پیش آمدهای دیگر] آزمایش می شوند، ولی نه توبه می کنند ونه پند می گیرند.'),
(1362, 9, 127, 'و هنگامی که سوره ای نازل شود برخی از منافقان به برخی دیگر، نگاهِ [مرموزانه] می کنند [و به سبب نگرانی از برملا شدن نفاقشان می گویند:] آیا کسی شما را می بیند؟ سپس [به صورت مخفیانه از محضر پیامبر خدا] بازمی گردند، خدا دل هایشان را [از حق] گردانیده است؛ زیرا آنان گروهی هستند که [حقایق را] نمی فهمند.'),
(1363, 9, 128, 'یقیناً پیامبری از جنس خودتان به سویتان آمد که به رنج و مشقت افتادنتان بر او دشوار است، اشتیاق شدیدی به [هدایتِ] شما دارد، و نسبت به مؤمنان رؤوف و مهربان است.'),
(1364, 9, 129, 'پس اگر [منافقان] از حق روی گرداندند، بگو: خدا مرا بس است، هیچ معبودی جز او نیست، فقط بر او توکل کردم، و او پروردگار عرش بزرگ است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الر ـ این آیات [با عظمت] کتاب محکم و استوار است.'),
(1366, 10, 2, 'آیا برای مردم شگفت آور است که بر مردی از خودشان وحی فرستادیم که مردم را [از عاقبت کفر، ناسپاسی، گناه و تجاوز] بیم ده، و به کسانی که ایمان آورده اند مژده ده که آنان را نزد پروردگارشان سابقه ای نیک [و مقام، مرتبه ای بلند و پاداشی شایسته] است. کافران [لجوج و بی منطق] گفتند: همانا این مرد، جادوگری آشکار است!!'),
(1367, 10, 3, 'یقیناً پروردگارتان خدای یکتاست که آسمان ها و زمین را در شش روز پدید آورد، آن گاه بر تخت فرمانروایی و حکومت بر آفرینش چیره شد، همواره کار جهان را [با قوانینی استوار و منظم] تدبیر می کند. [کار] هیچ واسطه ای [در همه جهان هستی برای جابجایی عناصر وترکیب اجزا وبه جریان انداختن امور] جز پس از اذن او نیست. این است خدا، پروردگار شما، پس او را بپرستید؛ آیا متذکّر [حقایق] نمی شوید؟'),
(1368, 10, 4, 'بازگشت همه شما فقط به سوی اوست. [خدا شما را وعده داد] وعده ای حق وثابت؛ بی تردید اوست که جهان آفرینش را می آفریند، سپس آن را [به قیامت] بازمی گرداند تا کسانی که ایمان آورده اند و کارهای شایسته انجام داده اند، به عدالت و انصاف پاداش دهد، و برای کسانی که کافر شدند به کیفر کفری که همواره می ورزیدند، شربتی از آب بسیار جوشان و عذابی دردناک است.'),
(1369, 10, 5, 'اوست که خورشید را فروزان و ماه را تابان قرار داد، و برای ماه منازلی [چون هلال، تربیع، بدر و محاق] مقدّر ساخت تا شمار سال و حساب [ماه، هفته، اوقات امور زندگی و تنظیم برنامه های معیشت] را بدانید. خدا آنها را جز به درستی و راستی نیافریده؛ او نشانه ها را برای گروهی که دانایند [بدون هر گونه ابهام] بیان می کند.'),
(1370, 10, 6, 'به یقین در رفت و آمد شب و روز و آنچه را خدا در آسمان ها و زمین پدید آورد، برای گروهی که همواره تقوا پیشه اند، نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] ست.'),
(1371, 10, 7, 'مسلماً کسانی که دیدارِ [قیامتِ] ما [و محاسبه شدن اعمالشان] را امید ندارند و به زندگی دنیا خشنود شده اند و به آن آرام یافته اند و آنانکه از آیات ما بی خبرند.'),
(1372, 10, 8, 'آنانند که به کیفر گناهانی که همواره مرتکب می شدند، جایگاهشان آتش است.'),
(1373, 10, 9, 'بی تردید کسانی که ایمان آورده اند و کارهای شایسته انجام داده اند، پروردگارشان آنان را به سبب ایمانشان به بهشت های پر نعمت که نهرها از زیر [قصرهایِ] آنها جاری است، راهنمایی می کند.'),
(1374, 10, 10, '[آغازِ] نیایششان در آنجا [این است که]: خدایا! از هر عیب و نقصی منزّهی، و درود خدا در آنجا به آنان عطا کردن سلامتی کامل است، و پایان بخشِ نیایششان این است که همه ستایش ها ویژه خدا مالک و مربی جهانیان است.'),
(1375, 10, 11, 'و اگر خدا برای مردم در عذاب و مجازات همان گونه که آنان در طلب خیر و خوشی شتاب دارند، شتاب می ورزید، قطعاً مدت عمرشان [با نزول عذاب و مجازات] پایان می یافت؛ پس کسانی که دیدارِ [قیامتِ] ما [و محاسبه شدن اعمالشان] را امید ندارند، وا می گذاریم تا در طغیانشان سرگردان بمانند.'),
(1376, 10, 12, 'و چون انسان را گزند و آسیبی رسد، ما را [در همه حالات] به پهلو خوابیده یا نشسته یا ایستاده [به یاری] می خواند، پس زمانی که گزند و آسیبش را برطرف کنیم، آن چنان به راه ناسپاسی و گناه می رود که گویی هرگز ما را برای برطرف کردن گزند و آسیبی که به او رسیده [به یاری] نخوانده است!! این گونه برای اسراف کاران اعمالی که همواره انجام می دادند، آراسته شده [تا جایی که زشتی اعمالشان را نمی فهمند.]'),
(1377, 10, 13, 'یقیناً اقوام پیش از شما را هنگامی که ستم ورزیدند، هلاک کردیم، و پیامبرانشان برای آنان دلایل روشن آوردند، ولی آنان بر آن نبودند که ایمان بیاورند؛ این گونه گروه گناهکار را کیفر می دهیم.'),
(1378, 10, 14, 'سپس شما را بعد از آنان در زمین جانشین قرار دادیم تا [بر پایه سنّت آزمایش] بنگریم، چگونه عمل می کنید؟'),
(1379, 10, 15, 'و هنگامی که آیات روشن ما بر آنان خوانده شود، کسانی که دیدار [قیامت] ما [و محاسبه شدن اعمالشان] را امید ندارند، می گویند: قرآنی غیر این بیاور یا آن را [به آیاتی دیگر که خوش آیند طبع ما باشد] تغییر ده!! بگو: مرا نرسد که آن را از نزد خود تغییر دهم؛ جز آنچه را به من وحی می شود، پیروی نمی کنم؛ من اگر پروردگارم را نافرمانی کنم، از عذاب روزی بزرگ می ترسم.'),
(1380, 10, 16, 'بگو: اگر خدا می خواست آن را بر شما نمی خواندم، و او هم شما را به آن آگاه نمی کرد؛ همانا مدت ها پیش از نزول قرآن در میان شما بودم، [و ادعای پیامبری نداشتم، اکنون صدق پیامبری خود را با این قرآن اثبات می کنم] آیا نمی اندیشید؟'),
(1381, 10, 17, 'پس ستمکارتر از کسی که بر خدا دروغ بندد یا آیاتش را تکذیب کند، کیست؟ یقیناً گنهکاران، رستگار نخواهند شد.'),
(1382, 10, 18, 'و آنان به جای خدا چیزهایی را می پرستند که نه زیانی به آنان می رسانند و نه سودی به آنان می دهند؛ و می گویند: اینان شفیعان ما نزد خدایند. بگو: آیا خدا را به شفیعانی خبر می دهید که آنها را در آسمان ها و زمین [به عنوان شفیع] نمی شناسد؟ او از آنچه که شریک او قرار می دهند، منزّه و برتر است.'),
(1383, 10, 19, 'و مردم [بر محور یکتاپرستی] جز امت واحدی نبودند؛ پس [چیزی نگذشت که درباره دین] دچار اختلاف شدند؛ و اگر از سوی پروردگارت، فرمانی مقرّر نشده بود [که عذاب اختلاف کنندگان تا قیامت به تأخیر افتد] یقیناً میان آنان در آنچه اختلاف می کنند، داوری می شد [و نتیجه داوری نابودی آنان و انقراض نسلشان بود.]'),
(1384, 10, 20, 'و می گویند: چرا از سوی پروردگارش معجزه ای [چون عصای موسی وناقه صالح وغیر آن] بر او نازل نشده؟ بگو: [آمدن و نیامدن معجزه از امور غیبی است و] غیب ویژه خداست؛ پس [به خاطر لجاجت و مخالفت با حق] به انتظار [عذاب] باشید که من هم با شما از منتظرانم.'),
(1385, 10, 21, 'و چون مردم را پس از رنج و آسیبی که به آنان رسیده، رفاه و آسایشی بچشانیم [به جای سپاس و ستایش] ناگاه در آیات ما به نیرنگ و بداندیشی برخیزند [و برای نپذیرفتن آن به بهانه ها و توجیهات بی پایه متوسّل شوند] بگو: خدا در کیفر و مجازات، سریع تر و کارآمدتر است. مسلماً فرستادگان ما [که فرشتگان و نویسندگانِ اعمالند] آنچه نیرنگ و بداندیشی می کنید [در نامه عمل شما] ثبت می کنند.'),
(1386, 10, 22, 'او کسی است که شما را در خشکی و دریا گردش می دهد، تا آن گاه که در کشتی ها باشید و کشتی ها مسافرانش را با بادی ملایم و آرام حرکت دهند، و کشتی نشینان به آن باد ملایم و آرام شادمان شوند، ناگاه بادی تند و سخت بر آن کشتی وَزَد و از هر طرف موجی سهمگین بر آنان تازد، و یقین کنند که در محاصره [امواج خطرناک] افتاده اند [و راهی برای نجات ندارند]، [در آن هنگامه هلاکت بار] خدا را در حالی که ایمان و عبادت را از هر گونه شرکی برای او خالص می کنند، می خوانند که اگر ما را از این [عرصه هلاکت بار] نجات دهی، مسلماً و قطعاً از سپاس گزاران خواهیم شد.'),
(1387, 10, 23, 'پس هنگامی که آنان را نجات بخشد، ناگهان در زمین به ناحق تجاوز و سرکشی می کنند. ای مردم! تجاوز و سرکشی شما فقط به زیان خود شماست؛ [چند روزی محدود از] کالای زندگی دنیا [بهره می برید]؛ آن گاه بازگشتتان به سوی ماست؛ پس شما را به اعمالی که همواره انجام می دادید، آگاه می کنیم.'),
(1388, 10, 24, 'در حقیقت، داستان زندگی دنیا [در زود گذر بودن] مانند آبی است که از آسمان نازل کردیم، پس گیاهان و روییدنی های زمین از آنچه مردم و چهارپایان از آن می خورند، با آن آب در آمیخت [و رشد و نمو یافت] تا آن گاه که زمین [سرسبزی و نهایت زیبایی و] زینتش را [از آن همه روییدنی های رنگارنگ هم چون عروس] بر خود گرفت، و اهل آن گمان کردند که قدرت [هر نوع بهره برداری را] از آن [چهره زیبا و آراسته] دارند، [که ناگهان] فرمان ما در شبی یا روزی [به صورت سرمایی سخت یا صاعقه ای آتش زا یا توفانی بنیان کن] به زمین رسید، پس همه گیاهان را به صورت گیاهان خشک درو شده درآوردیم که گویی دیروز [چنین زراعتی] وجود نداشته. این گونه نشانه ها [ی قدرت خود] را برای گروهی که می اندیشند، بیان می کنیم.'),
(1389, 10, 25, 'و خدا [مردم را] به سرای سلامت و امنیت [که بهشتِ عنبر سرشت است] دعوت می کند و هر که را بخواهد به راهی راست هدایت می نماید.'),
(1390, 10, 26, 'برای کسانی که نیکی کردند، [بهترین] پاداش و افزون [بر آن] است؛ و چهره آنان را سیاهی و خواری نمی پوشاند؛ آنان اهل بهشت اند [و] در آن جاودانه اند.'),
(1391, 10, 27, 'و کسانی که مرتکب بدی ها شدند، کیفر هر بدی مانند همان بدی است و خواری آنان را فرامی گیرد، برای آنان از [خشم و عذاب] خدا هیچ حافظ و نگه دارنده ای نخواهد بود؛ گویی چهره هایشان با پاره هایی از شب تاریک پوشیده شده؛ آنان اهل آتش اند و در آن جاودانه اند.'),
(1392, 10, 28, 'و [یاد کن] روزی را که همه آنان را [در قیامت] جمع می کنیم، آن گاه به مشرکان می گوییم: شما و معبودانتان در جایگاه خود بایستید؛ پس میان آنان و معبودشان جدایی می اندازیم، و معبودانشان به آنان خطاب می کنند: شما در دنیا ما را نمی پرستیدید [بلکه شیطان و هوای نفس خود را می پرستیدید.]'),
(1393, 10, 29, 'پس خدا میان ما و شما از جهت شهادت کافی است که ما یقیناً از پرستش شما نسبت به خود بی خبر بودیم.'),
(1394, 10, 30, 'آنجاست که هر کسی [به پاداش] هر عملی که از پیش فرستاده گرفتار آید، همه به سوی خدا ـ سرپرست حقیقی خود ـ بازگردانده می شوند، و آنچه را همواره به دروغ شریک خدا انگاشته بودند [گم شده] از دستشان می رود.'),
(1395, 10, 31, 'بگو: کیست که شما را از آسمان و زمین روزی می دهد؟ یا کیست که بر گوش ها و چشم ها مالکیّت و حکومت دارد؟ و کیست که زنده را از مرده، و مرده را از زنده بیرون می آورد؟ و کیست که همواره امور [جهان هستی] را تدبیر می کند؟ به زودی خواهند گفت: خدا! پس بگو: آیا [از پرستش غیر او] نمی پرهیزید؟!'),
(1396, 10, 32, 'این است خدا، پروردگار حقیقی شما، بنابراین بعد از حق چیزی جز گمراهی و ضلالت وجود دارد؟ پس چگونه شما را از حق بازمی گردانند؟'),
(1397, 10, 33, 'این گونه فرمان قاطعانه پروردگارت درباره فاسقان محقّق و ثابت شد که اینان [به سبب گناه و لجاجت] ایمان نمی آورند.'),
(1398, 10, 34, 'بگو: آیا از معبودهای شما کسی هست که جهان آفرینش را ایجاد نماید و سپس آن را [پس ازفانی شدن، به قیامت] بازگرداند؟ بگو: فقط خداست که جهان آفرینش را می آفریند سپس آن را [پس از فنا] بازمی گرداند، پس چگونه [از حق] منصرفتان می کنند؟'),
(1399, 10, 35, 'بگو: آیا از معبودان شما کسی هست که به سوی حق هدایت کند؟ بگو: فقط خداست که به سوی حق هدایت می کند؛ پس آیا کسی که به سوی حق هدایت می کند، برای پیروی شدن شایسته تر است یا کسی که هدایت نمی یابد مگر آنکه هدایتش کنند؟ شما را چه شده؟ چگونه [بدون بصیرت و دانش] داوری می کنید؟'),
(1400, 10, 36, 'و بیشتر آنان [در عقاید و آرایشان] جز از گمان و ظن پیروی نمی کنند، یقیناً گمان و ظن به هیچوجه انسان را از حق بی نیاز نمی کند، [و جای معرفت و دانش را نمی گیرد] مسلماً خدا به آنچه انجام می دهند، داناست.'),
(1401, 10, 37, 'و این قرآن را نسزد که دروغی ساختگی از سوی غیر خدا باشد، بلکه [با آیات محکم و استوارش] تصدیق کننده کتاب های پیش از خود و شرح و توضیحی بر هر کتاب [آسمانی] است، در آن هیچ تردیدی نیست که از سوی پروردگار جهانیان است.'),
(1402, 10, 38, 'ولی [این سبک مغزان بی منطق، در عین روشن بودن حقیقت] می گویند: [پیامبر] آن را به دروغ بافته است. بگو: پس اگر [در ادعای خود] راستگو هستید، سوره ای مانند آن بیاورید، و هر که را جز خدا می توانید [برای این کار] به یاری خود دعوت کنید.'),
(1403, 10, 39, 'آری، [عجولانه] حقیقتی را تکذیب کردند که به معارف و مفاهیمش احاطه نداشتند و هنوز تفسیر عینی و تحقّق و ظهور آیاتش [که در قیامت انجام می گیرد] برای آنان نیامده است، کسانی که پیش از آنان بودند [نیز آیات الهی و پیامبران را] این گونه تکذیب کردند؛ پس با تأمل بنگر که سرانجام ستمکاران چگونه بود؟'),
(1404, 10, 40, 'و گروهی از مردم به قرآن ایمان می آورند و گروهی ایمان نمی آورند؛ و پروردگارت به مفسدان، داناتر است.'),
(1405, 10, 41, 'و اگر تو را تکذیب کردند [تا آنجا که از ایمان آوردنشان ناامید شدی] بگو: عمل من برای من وعمل شما برای شماست، شما از آنچه من انجام می دهم، بیزارید، ومن از آنچه شما انجام می دهید، بیزارم.'),
(1406, 10, 42, 'و برخی از آنان به تو گوش می دهند [ولی گویا نمی شنوند] آیا تو می توانی کران را گرچه اندیشه نمی کنند، بشنوانی؟'),
(1407, 10, 43, 'و گروهی از آنان تو را می نگرند [ولی گویا از درک معجزاتت، فهم آیات قرآن، سلامت اخلاق و کردارت با چشم دل ناتوانند]. آیا تو می توانی نابینایان را گرچه از روی بصیرت و بینش نبینند، هدایت کنی؟'),
(1408, 10, 44, 'یقیناً خدا هیچ ستمی به مردم روا نمی دارد، ولی مردم [با روی گردانی از حق] بر خود ستم می ورزند.'),
(1409, 10, 45, 'و یاد کن روزی را که خدا آنان را [در قیامت] گرد می آورد، در حالی که گویا [در دنیا] جز ساعتی از روز درنگ نکرده اند، آنان میان خودشان یکدیگر را [به گونه ای که در دنیا می شناختند] می شناسند؛ یقیناً کسانی که دیدار [قیامت] خدا [و محاسبه شدن اعمالشان] را تکذیب کردند، سرمایه وجودشان را تباه نمودند و از راه یافتگان نبودند.'),
(1410, 10, 46, 'اگر پاره ای از عذاب هایی را که [به سبب کفرشان] به آنان وعده می دهیم، به تو نشان دهیم [می بینی که عذابی سخت و دردناک است] یا اگر [پیش از آنکه عذاب آنان را به تو نشان دهیم] تو را قبض روح کنیم [اندوه مخور که در قیامت، عذابشان را خواهی دید] پس بازگشتشان به سوی ماست؛ آن گاه خدا بر آنچه انجام می دهند، گواه است.'),
(1411, 10, 47, 'و برای هر امتی پیامبری است؛ پس هنگامی که پیامبرشان [در قیامت] به سویشان آید، در میانشان به عدالت وانصاف داوری شود و مورد ستم قرار نخواهند گرفت.'),
(1412, 10, 48, 'و [به صورتی مسخره آمیز] می گویند: این وعده [آمدن قیامت و محاسبه اعمال] چه زمانی است، اگر راستگویید؟'),
(1413, 10, 49, 'بگو: من برای خود قدرت دفع زیان و جلب سود ندارم، مگر آنچه را که خدا بخواهد؛ [وظیفه من فقط ابلاغ پیام خداست] برای هر امتی سرآمدی معین و اجلی محدود است، هنگامی که اجلشان سرآید، نه لحظه ای پس می مانند و نه پیش می افتند.'),
(1414, 10, 50, 'بگو: به من خبر دهید که اگر عذاب خدا شما را در شب یا در روز فرا رسد [چه قدرتی بر دفع آن دارید؟] گنهکاران چه چیزی از عذاب را به شتاب می خواهند؟'),
(1415, 10, 51, 'سپس آیا هنگامی که عذاب فرا می رسد به درستی و حق بودنش ایمان می آورید؟ [در لحظه فرا رسیدن عذاب به شما گفته می شود:] حالا و ایمان؟! [این همان عذابی است] که همواره به رسیدن آن شتاب ورزیدید!'),
(1416, 10, 52, 'آن گاه به کسانی که ستم کرده اند، گفته می شود: عذاب جاودانه را بچشید، آیا جز به کیفر آنچه همواره مرتکب می شدید، جزا داده می شوید؟'),
(1417, 10, 53, 'و از تو خبر می گیرند: آیا [عذاب جاودانه ای که به آن تهدید می شویم] حق است؟ بگو: آری، سوگند به پروردگارم یقیناً حق است، و شما عاجز کننده [خدا] نیستید [تا بتوانید از دسترس قدرت او بیرون روید.]'),
(1418, 10, 54, 'اگر [در قیامت] برای هر کسی که ستم کرده همه ثروتی که در زمین است فراهم باشد، یقیناً آن را برای بازخرید خود [از عذاب جاودانه] می پردازد؛ و زمانی که عذاب را ببینند [از شدت شرمساری] پشیمانی و اندوه خود را پنهان می کنند، ومیان آنان به عدالت و انصاف داوری می شود و مورد ستم قرار نخواهند گرفت.'),
(1419, 10, 55, 'آگاه باشید! مسلماً آنچه در آسمان ها وزمین است، فقط در سیطره مالکیّت و فرمانروایی خداست. بدانید که بی تردید وعده خدا حق است، ولی بیشترشان [این حقایق را] نمی دانند.'),
(1420, 10, 56, 'او زنده می کند ومی میراند وبه سوی او بازگردانده می شوید.'),
(1421, 10, 57, 'ای مردم! یقیناً از سوی پروردگارتان برای شما پند وموعظه ای آمده، و شفاست برای آنچه [ازبیماری های اعتقادی و اخلاقی] در سینه هاست، و سراسر هدایت و رحمتی است برای مؤمنان.'),
(1422, 10, 58, 'بگو: [این موعظه، دارو، هدایت و رحمت] به فضل و رحمت خداست، پس باید مؤمنان به آن شاد شوند که آن از همه ثروتی که جمع می کنند، بهتر است.'),
(1423, 10, 59, 'بگو: به من خبر دهید که آنچه خدا از رزق و روزی برای شما نازل کرده و شما بخشی از آن را حرام و بخشی را حلال کردید. بگو: آیا خدا این حرام و حلال کردن را به شما اجازه داده یا بر خدا دروغ می بندید؟!'),
(1424, 10, 60, 'و کسانی که بر خدا دروغ می بندند، گمانشان به روز قیامت چیست؟ بی تردید خدا نسبت به همه مردم، صاحب فضل و احسان است، ولی بیشترشان سپاس نمی گزارند.'),
(1425, 10, 61, '[ای پیامبر!] در هیچ شغل و کاری نمی باشی، و هیچ بخشی از قرآن را که از سوی خداست، تلاوت نمی کنی، و [شما ای مردم!] هیچ کاری انجام نمی دهید، مگر آنکه وقتی سرگرم به آن کار هستید، گواه و شاهد شماییم. و به اندازه وزن ذره ای در زمین و آسمان از پروردگارت پوشیده نیست و نه کوچک تر از آن ذره و نه بزرگ تر از آن نیست، مگر آنکه در کتابی روشن ثبت است.'),
(1426, 10, 62, 'آگاه باشید! یقیناً دوستان خدا نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(1427, 10, 63, 'آنان که ایمان آورده اند و همواره پرهیزکاری دارند.'),
(1428, 10, 64, 'آنان را در زندگی دنیا وآخرت مژده و بشارت است [در دنیا به وسیله وحی و در آخرت به خطاب خدا و گفتار فرشتگان] در کلمات خدا [که وعده ها و بشارت های اوست] هیچ دگرگونی نیست؛ این است کامیابی بزرگ.'),
(1429, 10, 65, 'و گفتار [بی اساس و تبلیغاتِ ناروایِ] مخالفان، تو را غمگین نکند؛ زیرا همه عزت و توانمندی برای خداست؛ او شنوا و داناست.'),
(1430, 10, 66, 'آگاه باشید! یقیناً هر که در آسمان ها و هر که در زمین است در سیطره مالکیّت و فرمانروایی خداست؛ و کسانی که به جای خدا معبودانی را می پرستند، از حق پیروی نمی کنند؛ آنان [در این پرستش] فقط از گمان و ظن پیروی می کنند و آنان فقط دروغ می بافند.'),
(1431, 10, 67, 'اوست کسی که شبِ [تاریک] را برای شما پدید آورد تا در آن بیارامید، و روز را نور افشان [قرار داد تا در آن به کار و کوشش بپردازید]؛ یقیناً در این امور برای گروهی که حقایق را بشنوند، نشانه هایی [از توحید و قدرت و ربوبیّت خدا] ست.'),
(1432, 10, 68, '[مشرکان بر پایه گمان واهی خود] گفتند: خدا برای خود فرزندی گرفته است!! او منزّه از هر عیب و نقصی است، او از هر چیزی بی نیاز است، آنچه در آسمان ها و زمین است، در سیطره مالکیّت و فرمانروایی اوست؛ نزد شما بر این ادعا [یِ پوچ] هیچ دلیل و برهانی نیست، آیا چیزی را از روی جهل و نادانی به خدا نسبت می دهید؟!'),
(1433, 10, 69, 'بگو: کسانی که بر خدا دروغ می بندند، یقیناً رستگار نمی شوند.'),
(1434, 10, 70, 'در دنیا [بهره آنان از دروغ بستن] بهره ای [اندک] است؛ آن گاه بازگشتشان به سوی ماست؛ سپس به آنان به کیفر آنکه کفر می ورزیدند، عذابی سخت می چشانیم.'),
(1435, 10, 71, 'و سرگذشت [پر فایده و عبرت آموز] نوح را برای آنان بگو، آن گاه که به قوم خود گفت: ای قوم من! اگر اقامت طولانی من میان شما و پند واندرزم به وسیله آیات خدا، بر شما گران و دشوار است [از هیچ کاری بر ضد من کوتاهی نکنید] من بر خدا توکل کردم، پس شما عزم و تصمیم خود و قدرت معبودانتان را [که گمان می کنید شما را یاری می دهند] روی هم بگذارید تا تلاشتان [بر ضد من] بر شما مبهم و پوشیده نباشد، آن گاه به زندگی من خاتمه دهید و مرا [چشم به هم زدنی] مهلت ندهید.'),
(1436, 10, 72, 'اگر [از پذیرش دعوتم] روی بگردانید [خود زیان کرده اید] من از شما [در برابر ابلاغ رسالتم] پاداشی نمی خواهم؛ پاداش من فقط بر عهده خداست و مأمورم که از تسلیم شدگان [در برابر فرمان های حق] باشم.'),
(1437, 10, 73, 'پس او را تکذیب کردند؛ ما هم او و کسانی را که در کشتی همراه او بودند [از آن توفانِ مُهلک] نجات دادیم، و آنان را جانشینان [غرق شدگان] نمودیم، و کسانی که آیات ما را تکذیب کردند، غرق کردیم؛ پس [با تأمل] بنگر که سرانجام کسانی که بیم داده شدند، چگونه بود؟!'),
(1438, 10, 74, 'سپس بعد از نوح، پیامبرانی را به سوی قومشان فرستادیم، پس برای آنان دلایل آشکار آوردند، ولی قومشان بر آن نبودند که به آنچه پیش از آن تکذیب کرده بودند ایمان آورند؛ این گونه دل های متجاوزان را مُهر [بدبختی] می زنیم.'),
(1439, 10, 75, 'آن گاه پس از آنان موسی و هارون را با آیات خود به سوی فرعون و اشراف و سران قومش فرستادیم، پس آنان تکبّر ورزیدند و آنان گروهی گنهکار بودند.'),
(1440, 10, 76, 'پس هنگامی که حق از نزد ما به سویشان آمد، گفتند: مسلماً این جادویی است آشکار.'),
(1441, 10, 77, 'موسی گفت: آیا درباره حق هنگامی که به سویتان آمد، می گویید: آیا این جادوست؟ [نه، این جادو نیست، اگر جادو بود پیروزی نداشت] زیرا جادوگران پیروز نمی شوند.'),
(1442, 10, 78, 'گفتند: آیا به سوی ما آمده ای تا ما را از آیینی که پدرانمان را بر آن یافته ایم، برگردانی و [با نابود کردن ما] قدرت و حکومت در این سرزمین برای شما دو نفر باشد؟ و ما به شما دو نفر ایمان نمی آوریم!'),
(1443, 10, 79, 'و فرعون گفت: [برای درهم کوبیدن این دو نفر] هر جادوگر دانا و زبردستی را نزد من آورید.'),
(1444, 10, 80, 'چون جادوگران آمدند، موسی به آنان گفت: آنچه را بر افکندن آن مصمم هستید، بیفکنید.'),
(1445, 10, 81, 'پس هنگامی که افکندند، موسی گفت: آنچه را در این صحنه آوردید، جادوست؛ مسلماً خدا آن را به زودی باطل می کند، قطعاً خدا، کار مفسدان را [که برای تقویت طاغیان انجام می دهند] به سامان نمی آورد.'),
(1446, 10, 82, 'و خدا حق را با دلایل و معجزاتش ثابت و پابرجا می کند، گرچه گنهکاران خوش نداشته باشند.'),
(1447, 10, 83, 'پس [در ابتدای کار] کسی به موسی ایمان نیاورد، مگر فرزندانی [انگشت شمار] از قومش آن هم با ترسی [شدید] از فرعون و اشراف و سران قوم خودشان که مبادا فرعونیان آنان را شکنجه و عذاب دهند؛ و مسلماً فرعون در سرزمین مصر برتری خواه، و [در گناه، معصیت و ستم] از اسراف کاران بود.'),
(1448, 10, 84, 'و موسی گفت: ای قوم من! اگر به خدا ایمان آورده اید، پس بر او توکل کنید اگر از تسلیم شدگان [در برابر فرمان های حق] هستید.'),
(1449, 10, 85, 'پس گفتند: ما فقط بر خدا توکل کردیم. پروردگارا! ما را دستخوش شکنجه و عذاب ستمکاران قرار مده.'),
(1450, 10, 86, 'و ما را به رحمتت از گروه کافران رهایی ده.'),
(1451, 10, 87, 'و به موسی و برادرش وحی کردیم که خانه هایی در سرزمین مصر برای قوم خود فراهم آورید، و خانه هایتان را روبروی هم قرار دهید، و نماز را برپا دارید، و مؤمنان را [به رهایی از چنگال فرعونیان] مژده ده.'),
(1452, 10, 88, 'و موسی گفت: پروردگارا! فرعون و اشراف و سرانش را در زندگی دنیا زیور و زینت [بسیار] و اموال [فراوان] داده ای که [نهایتاً مردم را] از راه تو گمراه کنند، پروردگارا! اموالشان را نابود کن و دل هایشان را سخت گردان که ایمان نیاورند تا آنکه عذاب دردناک را ببینند.'),
(1453, 10, 89, '[خدا] فرمود: دعای شما دو نفر پذیرفته شد؛ بنابراین [در ابلاغ پیام خدا] پابرجا واستوار باشید واز روش کسانی که جاهل و نادانند، پیروی نکنید.'),
(1454, 10, 90, 'وبنی اسرائیل را از دریا گذراندیم، پس فرعون و لشکریانش آنان را از روی ستم وتجاوز دنبال کردندتا هنگامی که بلای غرق شدن، او را فروگرفت، گفت: ایمان آوردم که هیچ معبودی جز همان که بنی اسرائیل به او ایمان آوردند، نیست و من از تسلیم شدگان [در برابر فرمان های حق] هستم.'),
(1455, 10, 91, '[به اوگفته شد:] اکنون [که به نابودی خود یقین داری و زمان بازگشت به خدا سپری شده ایمان می آوری؟!] در حالی که پیش از این عصیان می ورزیدی و از تبهکاران بودی؟'),
(1456, 10, 92, 'پس امروز تو را با بدن بی جانت نجات می دهم [وبر بلندایی از ساحل دریا می گذارم] تا برای آیندگانت نشانه ای [از قدرت ما و زبونی و خواری گردنکشان] باشی؛ و یقیناً بسیاری از مردم از نشانه های ما بی خبرند.'),
(1457, 10, 93, 'به راستی ما بنی اسرائیل را در جایگاهی [خوش آب و هوا و منطقه ای سرشار از نعمت ها] جای دادیم و به آنان از انواع روزی های پاکیزه، روزی بخشیدیم [ولی آنان در نبوّت موسی و در دین خدا روی به اختلاف آوردند]؛ و اختلاف نکردند مگر پس از آنکه دانش [به حقایق و معارف] برای آنان آمد؛ یقیناً پروردگارت روز قیامت میان آنان درباره آنچه در آن اختلاف می کردند، داوری خواهد کرد.'),
(1458, 10, 94, 'و [به فرض محال] اگر از آنچه بر تو نازل کردیم در شک و تردیدی، از آنان که پیش از تو کتاب [آسمانی] می خواندند بپرس [کتابی که نزول قرآن را از سوی خدا خبر داده] تا روشن شود که حق از سوی پروردگارت به سوی تو آمده؛ بنابراین از تردیدکنندگان مباش.'),
(1459, 10, 95, 'و هرگز از کسانی مباش که آیات ما را تکذیب کردند که از زیانکاران خواهی شد.'),
(1460, 10, 96, 'مسلماً کسانی که عذاب پروردگارت بر آنان محقق و ثابت شده، ایمان نمی آورند.'),
(1461, 10, 97, 'گرچه همه نشانه ها و معجزه ها برای آنان بیاید تا آن زمان که عذاب دردناک را ببینند [که آن هنگام ایمان آوردنشان هیچ سودی نخواهد داشت.]'),
(1462, 10, 98, 'پس چرا هیچ شهری نبوده است که [اهلش] ایمان بیاورد تا ایمانشان به آنان سود دهد؟ مگر قوم یونس که وقتی ایمان آوردند، عذاب رسوایی را در زندگی دنیا از آنان برطرف کردیم و آنان را تا پایان عمرشان [از الطاف و نعمت های خود] برخوردار نمودیم.'),
(1463, 10, 99, 'اگر پروردگارت می خواست یقیناً همه کسانی که روی زمین اند [اجباراً] ایمان می آوردند؛ [ای پیامبر! در حالی که خدا از بندگانش ایمان اجباری نخواسته] پس آیا تو مردم را وادار می کنی تا به اجبار مؤمن شوند؟!'),
(1464, 10, 100, 'هیچ کس را نسزد [و قدرت نباشد] که ایمان بیاورد مگر به اذن و توفیق خدا، و خدا پلیدی [ضلالت] را [پس از بیان حقایق] بر کسانی قرار می دهد که [در حقایق] نمی اندیشند.'),
(1465, 10, 101, 'بگو: با تأمل بنگرید که در آسمان ها و زمین [از شگفتی های آفرینش و عجایب خلقت] چه چیزها هست؟ ولی نشانه ها و هشدارها به حال گروهی که ایمان نمی آورند، سودی نمی دهد.'),
(1466, 10, 102, 'پس آیا جز مانند روزهای [سخت و شکننده] کسانی را که پیش از آنان درگذشتند، انتظار می برند؟ بگو: پس منتظر بمانید که من [هم] با شما از منتظرانم.'),
(1467, 10, 103, 'آن گاه پیامبران خود و کسانی را که ایمان آورده اند [روز نزول عذاب] می رهانیم، همین گونه بر ما فریضه و لازم است که مؤمنان را نجات دهیم.'),
(1468, 10, 104, 'بگو: ای مردم! اگر شما در [حقّانیّت] دین من تردید دارید [من در حقّانیّت دینم و بطلان آیین شما هیچ تردیدی ندارم] من آنهایی را که شما به جای خدا می پرستید، نمی پرستم، بلکه خدایی را می پرستم که جان همه شما را می ستاند، و من مأمورم که از مؤمنان باشم.'),
(1469, 10, 105, 'و [دستور یافته ام] که [حق گرایانه و بدون انحراف با همه وجود] به سوی [این] دین درست واستوار روی آور، و از مشرکان مباش.'),
(1470, 10, 106, 'و به جای خدا چیزی را که سودی به تو نمی رساند و زیانی نمی زند، مَپَرست؛ که اگر بپرستی قطعاً از زیانکاران خواهی بود.'),
(1471, 10, 107, 'و اگر خدا گزند و آسیبی به تو رساند، آن را جز او برطرف کننده ای نیست، و اگر برای تو خیری خواهد فضل و احسانش را دفع کننده ای نیست؛ خیرش را به هر کس از بندگانش بخواهد می رساند و او بسیار آمرزنده و مهربان است.'),
(1472, 10, 108, 'بگو: ای مردم! یقیناً حق از سوی پروردگارتان برای شما آمد؛ پس هر که هدایت یابد، فقط به سود خود هدایت می یابد و هر که گمراه گردد، فقط به زیان خود گمراه می شود، و من نگهبان شما نیستم.'),
(1473, 10, 109, 'و از آنچه به سویت وحی می شود، پیروی کن و [در برابر پیش آمدها و تکذیب منکران] شکیبا باش تا خدا [میان تو و مخالفانت] داوری کند؛ و او بهترین داوران است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الر ـ [این] کتابی [با عظمت] است که آیاتش [به صورت حقیقتی واحد] استواری واستحکام یافته، سپس از سوی حکیمی آگاه [در قالب سوره ها، آیه ها، حقایق اعتقادی، اخلاقی و عملی] تفصیل داده شده است.'),
(1475, 11, 2, 'که جز خدا را نپرستید، یقیناً من از سوی او برای شما بیم دهنده [از عذاب] و مژده دهنده [به بهشت و رضوان] هستم.'),
(1476, 11, 3, 'و اینکه از پروردگارتان آمرزش بخواهید، سپس به سوی او بازگردید تا آنکه شما را تا پایان زندگی از بهره نیک و خوشی برخوردار کند، و هر که را صفات پسندیده و اعمال شایسته او افزون تر است، پاداش زیادتری عطا کند، و اگر روی از حق برگردانید، من از عذاب روزی بزرگ بر شما بیمناکم.'),
(1477, 11, 4, 'بازگشت شما فقط به سوی خداست و او بر هر کاری تواناست.'),
(1478, 11, 5, 'آگاه باشید که مشرکان سر در گریبان فرو می برند تا [خود را] از شنیدن قرآن [به هنگام تلاوتش به وسیله پیامبر و مؤمنان] پنهان بدارند؛ آگاه باشید! چون جامه هایشان را بر سر و روی خود می پوشانند [تا دیده نشوند، این پنهانکاری سودی به حال آنان ندارد] خدا آنچه را پنهان می کنند وآنچه را آشکار می نمایند، می داند؛ یقیناً او به آنچه در سینه هاست، داناست.'),
(1479, 11, 6, 'و هیچ جنبده ای در زمین نیست مگر اینکه روزیِ او برخداست، و [او] قرارگاه واقعی و جایگاه موقت آنان را می داند؛ همه در کتابی روشن ثبت است.'),
(1480, 11, 7, 'و او کسی است که آسمان ها و زمین را در شش روز آفرید، در حالی که تخت فرمانروایی اش بر آب [که زیربنای حیات است] قرار داشت، تا شما را بیازماید که کدام یک نیکوکارترید؟ و اگر بگویی: [ای مردم!] شما یقیناً پس از مرگ برانگیخته می شوید، بی تردید کافران می گویند: این سخنان جز جادویی آشکار نیست.'),
(1481, 11, 8, 'و اگر عذاب را از آنان تا مدتی اندک به تأخیر اندازیم، مسلماً [از روی استهزا] خواهند گفت: چه عاملی آن را [از آمدن] باز می دارد؟! آگاه باشید! روزی که عذاب به آنان رسد، از آنان بازگشتنی و بازداشتنی نیست و عذابی را که همواره مسخره می کردند، آنان را فراخواهد گرفت.'),
(1482, 11, 9, 'و اگر از سوی خود رحمتی [چون سلامتی، ثروت، اولاد و امنیت] به انسان بچشانیم، سپس آن را [به علتی حکیمانه] از او سلب کنیم [نسبت به آینده زندگی] بسیار نومید شونده و [نسبت به نعمت هایی که دارا بود] بسیار کفران کننده است.'),
(1483, 11, 10, 'و اگر پس از گزند و آسیبی که به او رسیده خوشی و رفاهی به او بچشانیم، قطعاً خواهد گفت: همه [گزندها و] آسیب ها از فضای زندگی ام رفت [و دیگر آسیبی نمی بینم و] مسلماً در آن حال [که غافل از حوادث آینده است] بسیار شادمان و خودستا خواهد بود.'),
(1484, 11, 11, 'مگر کسانی که [در خوشی ها و آسیب ها] شکیبایی ورزیدند و کارهای شایسته انجام دادند، اینانند که برای آنان آمرزش و پاداشی بزرگ است.'),
(1485, 11, 12, 'ممکن است به سبب آنکه [دشمنان] می گویند: چرا گنجی بر او نازل نشده، یا فرشته ای همراهش نیامده؟ برخی از آنچه را به تو وحی می شود ترک کنی وسینه ات [از ابلاغ و اظهار آن] تنگ شود [به سبب موضع گیری های دشمنان از ابلاغ پیام حق خودداری مکن] تو فقط بیم دهنده ای و خدا بر هر چیز نگهبان است.'),
(1486, 11, 13, 'بلکه [در برابر همه قرآن می ایستند و] می گویند: او این قرآن را از نزد خود ساخته [و به خدا نسبت می دهد] بگو: اگر راستگویید، شما هم ده سوره مانند آن بیاورید و هر کس را غیر خدا می توانید، به یاری خود دعوت کنید.'),
(1487, 11, 14, 'پس اگر آنان دعوت شما را اجابت نکردند [که هرگز اجابت نمی کنند] بدانید که آنچه نازل شده به دانش خداست وهیچ معبودی جز او نیست، پس آیا تسلیم [حق] می شوید؟'),
(1488, 11, 15, 'کسانی که زندگی دنیا و زیور و زینتش را بخواهند، ثمره تلاششان را به طور کامل در [همین] دنیا به آنان می دهیم و در اینجا چیزی از آنان کاسته نخواهد شد.'),
(1489, 11, 16, 'اینان کسانی هستند که در آخرت، سهمی جز آتش برای آنان نیست و آنچه [در دنیا از کار خیر] کرده اند، در آخرت تباه و بی اثر می شود، و آنچه همواره [ریاکارانه] انجام می دادند، باطل است.'),
(1490, 11, 17, 'آیا کسانی که از سوی پروردگارشان بر دلیلی روشن [از بصیرت و بینش] متکی هستند و شاهدی از سوی او [چون قرآن برای تأیید آن دلیل روشن] از پی درآید و پیش از قرآن [هم] کتاب موسی در حالی که [برای مؤمنان] پیشوا و رحمت بود [بر حقّانیّت قرآن گواهی داده مانند کسانی می باشند که چنین نیستند] اینان [که متکی بر دلیل روشن اند] به قرآن ایمان می آورند، و هر کس از گروه ها [چه یهود، چه نصاری و چه مشرکان] به آن کفر ورزد، وعده گاهش آتش است؛ پس [ای انسان!] درباره قرآن در تردید مباش که آن از سوی پروردگارت حق است، ولی بیشتر مردم [به خاطر کبر باطنی، لجاجت و جهل] ایمان [به آن] نمی آورند.'),
(1491, 11, 18, 'و ستمکارتر از کسانی که به خدا دروغ بندند چه کسی است؟ اینان [در قیامت] بر پروردگارشان عرضه خواهند شد، وگواهان [اعمال] می گویند: اینان کسانی هستند که بر پروردگارشان دروغ بستند؛ آگاه باشید! لعنت خدا بر ستمکاران باد.'),
(1492, 11, 19, 'آنان که مردم را از راه خدا باز می دارند و می خواهند آن را [با وسوسه و اغواگری] کج نشان دهند و به آخرت کافرند.'),
(1493, 11, 20, 'اینان در زمین، عاجزکننده [خدا] نیستند تا [بتوانند از دسترس قدرت او بیرون روند]، و برای آنان سرپرستان و یاورانی جز خدا نیست، عذابشان [در روز قیامت] دو چندان خواهد شد؛ آنان [به سبب بسیاریِ گناه] تاب وتوانِ شنیدنِ [حق را] نداشتند و [با چشم بصیرت، حقیقت را] نمی دیدند.'),
(1494, 11, 21, 'اینان کسانی هستند که سرمایه وجودشان را تباه کردند، و آنچه را همواره به دروغ [به عنوان شریکان خدا به خدا] نسبت می دادند [گم شده] از دستشان می رود.'),
(1495, 11, 22, 'ثابت و یقینی است که اینانند که در آخرت از همه زیانکارترند.'),
(1496, 11, 23, 'قطعاً کسانی که ایمان آوردند و کارهای شایسته انجام دادند و به پروردگارشان آرامش و اطمینان یافتند، اهل بهشت اند و در آن جاودانه اند.'),
(1497, 11, 24, 'وصف این دو گروه [مشرک و مؤمن] همانند نابینا و کر و بینا و شنواست؛ آیا [این دو گروه] در صفت و حال یکسانند؟ پس آیا متذکّر نمی شوید؟'),
(1498, 11, 25, 'بی تردید نوح را به سوی قومش فرستادیم [که به آنان بگو:] من برای شما بیم دهنده ای آشکارم.'),
(1499, 11, 26, 'که جز خدا را نپرستید؛ من بر شما از عذاب روزی دردناک بیمناکم.'),
(1500, 11, 27, 'اشراف و سران کافر قومش گفتند: ما تو را جز بشری مانند خود نمی بینیم، و کسی را جز فرومایگان که نسنجیده و بدون اندیشه از تو پیروی کرده باشند مشاهده نمی کنیم، و برای شما هیچ برتری و فضیلتی بر خود نمی بینیم، بلکه شما را دروغگو می پنداریم.'),
(1501, 11, 28, 'نوح گفت: ای قوم من! مرا خبر دهید: اگر من بر دلیل روشنی از سوی پروردگارم متکی باشم و مرا از نزد خود رحمتی عطا کرده باشد که بر شما مخفی مانده [باز هم نبوّت مرا تکذیب می کنید؟] آیا [در صورتی که اجباری در پذیرش دین نیست] می توان شما را به پذیرش آن دلیل روشن در حالی که آن را خوش ندارید، وادار کنیم؟'),
(1502, 11, 29, 'و ای قوم من! من از شما [در برابر ابلاغ رسالتم] هیچ پاداشی نمی خواهم؛ پاداشم فقط بر عهده خداست، و من [برای به دست آوردن دل شما] طرد کننده کسانی که ایمان آورده اند [و شما آنان را فرومایه می دانید] نخواهم بود؛ زیرا اینان دیدارکنندگان [پاداش و مقام قرب] پروردگار خویش اند. ولی من شما را گروهی می بینم که جهالت می ورزید.'),
(1503, 11, 30, 'و ای قوم من! اگر آنان را از خود طرد کنم، چه کسی مرا در برابر [عذاب] خدا یاری می دهد؟ آیا متذکّر نمی شوید؟'),
(1504, 11, 31, 'و من به شما نمی گویم که گنجینه های [عنایات و الطاف] خدا نزد من است [تا به طور مستقل بتوانم حاجات شما را برآورم و هر تصرفی را که مایل باشم در آسمان ها و زمین بنمایم]، و ادعا نمی کنم که غیب هم می دانم، و نمی گویم که من فرشته ام و درباره آنان که چشمانتان فرومایه و خوارشان می نگرد، نمی گویم که خدا هرگز خیری به آنان نخواهد داد؛ خدا به آنچه در دل آنان است آگاه تر است؛ اگر چنین گویم از ستمکاران خواهم بود.'),
(1505, 11, 32, 'گفتند: ای نوح! با ما جدال و ستیزه کردی و بسیار هم جدال و ستیزه کردی، نهایتاً اگر از راستگویانی آنچه را [از عذاب] به ما وعده می دهی، برایمان بیاور.'),
(1506, 11, 33, 'گفت: جز این نیست که اگر خدا بخواهد آن را برای شما می آورد و شما عاجز کننده [خدا] نیستید [تا بتوانید از دسترس قدرت او بیرون روید.]'),
(1507, 11, 34, 'و اگر بخواهم برای شما خیرخواهی کنم در صورتی که خدا بخواهد شما را [به کیفر گناهانتان] گمراه کند، خیرخواهی من سودی به شما نخواهد داد، او پروردگار شماست و به سویش بازگردانده می شوید.'),
(1508, 11, 35, 'آیا مشرکان می گویند: پیامبر، این قرآن را از نزد خود به دروغ ساخته [و به خدا نسبت داده؟] بگو: اگر آن را به دروغ ساخته باشم، گناهم فقط بر عهده خود من است، و من از گناهانی که شما مرتکب می شوید، بیزارم.'),
(1509, 11, 36, 'و به نوح وحی شد که از قوم تو جز کسانی که [تاکنون] ایمان آورده اند، هرگز کسی ایمان نخواهد آورد؛ بنابراین از کارهایی که همواره [بر ضد حق] انجام می دادند، اندوهگین مباش.'),
(1510, 11, 37, 'و با نظارت ما و [بر اساس] وحی ما کِشتی را بساز، و با من درباره کسانی که ستم کرده اند، سخن مگو که یقیناً آنان غرق شدنی هستند.'),
(1511, 11, 38, 'و [نوح] کشتی را می ساخت و هرگاه گروهی از [اشراف و سرانِ] قومش بر او عبور می کردند، او را به مسخره می گرفتند. گفت: اگر شما ما را مسخره می کنید، مسلماً ما هم شما را [به هنگام پدید آمدن توفان] همان گونه که ما را مسخره می کنید، مسخره خواهیم کرد.'),
(1512, 11, 39, 'سپس خواهید دانست که چه کسی را [در دنیا] عذابی خوارکننده و [در آخرت عذابی] پایدار خواهد رسید.'),
(1513, 11, 40, '[رویاروییِ نوح و قومش هم چنان ادامه داشت] تا هنگامی که فرمان ما فرا رسید و تنور فوران کرد، گفتیم: از هر [نوع حیوانی] یک زوج دوتایی [یک نر و یک ماده] و نیز خاندانت و آنان را که ایمان آورده اند، در کشتی سوار کن مگر کسی که پیش تر فرمان غرق شدن را بر ضد او لازم کرده ایم. و جز اندکی همراه او ایمان نیاوردند.'),
(1514, 11, 41, 'و نوح گفت: در آن سوار شوید که حرکت کردنش و لنگر انداختنش فقط به نام خداست، یقیناً پروردگارم بسیار آمرزنده و مهربان است.'),
(1515, 11, 42, 'آن کشتی آنان را در میان موج هایی کوه آسا حرکت می داد، و نوح فرزندش را که در کناری بود، بانگ زد که ای پسرم! همراه ما سوار شو و با کافران مباش.'),
(1516, 11, 43, 'گفت: به زودی به کوهی که مرا از [این] آب نگه دارد، پناه می برم. نوح گفت: امروز در برابر عذاب خدا هیچ نگه دارنده ای نیست مگر کسی که [خدا بر او] رحم کند. و موج میان آن دو حایل شد و پسر از غرق شدگان گردید.'),
(1517, 11, 44, 'و [پس از هلاک شدن کافران] گفته شد: ای زمین! آب خود را فرو بر، و ای آسمان! [از ریختن باران] باز ایست، و آب کاستی گرفت و کار پایان یافت و کشتی بر [کوهِ] جودی قرار گرفت و گفته شد: دوری [از رحمت خدا] بر گروه ستمکاران باد.'),
(1518, 11, 45, 'و نوح [پیش از توفان] پروردگارش را ندا داد و گفت: پروردگارا! به راستی که پسرم از خاندان من است و یقیناً وعده ات [به نجات خاندانم] حق است و تو بهترین داورانی.'),
(1519, 11, 46, 'خدا فرمود: ای نوح! به یقین او از خاندان تو نیست، او [دارای] کرداری ناشایسته است، پس چیزی را که به آن علم نداری از من مخواه، همانا من تو را اندرز می دهم که مبادا از ناآگاهان باشی.'),
(1520, 11, 47, 'نوح گفت: پروردگارا! من از اینکه چیزی را که به آن علم ندارم از تو بخواهم به تو پناه می برم و اگر مرا نیامرزی و بر من رحم نکنی از زیانکاران خواهم بود.'),
(1521, 11, 48, 'گفته شد: ای نوح! با سلام و برکاتی از سوی ما بر تو و امت هایی که با تواند [از کشتی] فرود آی و امت هایی [از نسل همراهانت به وجود می آیند] که به زودی آنان را [از نعمت های خود] برخوردار می کنیم، سپس آنان را از سوی ما [به سبب کفران نعمت] عذابی دردناک خواهد رسید.'),
(1522, 11, 49, 'اینها از خبرهای غیبی است که آن را به تو وحی می کنیم، نه تو آنها را پیش از این می دانستی و نه قوم تو؛ پس [در ابلاغ پیام ما با بهره گرفتن از این خبرها] شکیبایی ورز؛ یقیناً فرجام [نیک] برای پرهیزکاران است.'),
(1523, 11, 50, 'و به سوی قوم عاد، برادرشان هود را [فرستادیم] گفت: ای قوم من! خدا را بپرستید که شما را جز او هیچ معبودی نیست، شما [که شریکانی برای خدا قرار داده اید] جز مردمی دروغ پرداز نیستید.'),
(1524, 11, 51, 'ای قوم من! از شما [در برابر ابلاغ رسالتم] هیچ پاداشی نمی خواهم، پاداشم فقط بر عهده کسی است که مرا آفریده؛ آیا نمی اندیشید؟'),
(1525, 11, 52, 'ای قوم من! از پروردگارتان آمرزش بخواهید، آن گاه به سوی او بازگردید، [تا] برای شما باران فراوان و پی در پی فرستد و نیرویی بر نیرویتان بیفزاید، و مجرمانه روی [از حق] برمگردانید.'),
(1526, 11, 53, 'گفتند: ای هود! دلیل روشنی [که پسند خاطر ما باشد] برای ما نیاوردی، و ما به گفته تو رهاکننده معبودهای خود نیستیم، و ما به تو ایماننمی آوریم.'),
(1527, 11, 54, '[ما درباره تو] جز این نمی گوییم که برخی از معبودهای ما به تو گزند و آسیب [روحی] رسانده اند [به همین سبب سخنان جنون آمیز و بی منطق می گویی]. هود گفت: من خدا را گواه می گیرم و شما هم گواه باشید که یقیناً من از آنچه شریک او قرار می دهید، بیزارم.'),
(1528, 11, 55, '[آری، از هر معبودی] به غیر او [بیزارم] پس همه شما بر ضد من نیرنگ بزنید، سپس مهلتم ندهید.'),
(1529, 11, 56, 'یقیناً من بر خدا که پروردگار من و پروردگار شماست توکل کردم؛ هیچ جنبنده ای نیست مگر اینکه او مهارش را به دست [قدرت و فرمانروایی خود] گرفته است، مسلماً پروردگارم بر راهی راست است.'),
(1530, 11, 57, 'پس اگر روی [از حق] برگردانید [زیانش متوجه خود شماست] یقیناً آنچه را که من برای آن به سوی شما فرستاده شده ام به شما ابلاغ کردم، و پروردگارم گروهی غیر از شما را جایگزینِ [شما] خواهد کرد، و نمی توانید هیچ زیانی به او رسانید؛ زیرا پروردگارم بر همه چیز نگهبان است.'),
(1531, 11, 58, 'و هنگامی که فرمان ما [بر عذاب آنان] فرا رسید، هود و آنان را که با او ایمان آورده بودند با رحمتی از سوی خود نجات دادیم و آنان را از عذابی سخت رهایی بخشیدیم.'),
(1532, 11, 59, 'و اینان قوم عاد بودند که آیات پروردگارشان را انکار کردند، و از پیامبران او نافرمانی نمودند، و فرمان هر سرکش ستیزه جویی را پیروی کردند.'),
(1533, 11, 60, 'و در این دنیا و روز قیامت با لعنتی بدرقه شدند. آگاه باشید که [قومِ] عاد به پروردگارشان کافر شدند، هان! دوری [از رحمت خدا] بر عاد، قوم هود باد.'),
(1534, 11, 61, 'و به سوی قوم ثمود، برادرشان صالح را [فرستادیم] گفت: ای قوم من! خدا را بپرستید؛ شما را جز او هیچ معبودی نیست، و او شما را از زمین به وجود آورد و از شما خواست که در آن آبادانی کنید؛ بنابراین از او آمرزش بخواهید، سپس به سوی او بازگردید؛ زیرا پروردگارم [به بندگانش] نزدیک و اجابت کنندهدعای آنان است.'),
(1535, 11, 62, 'گفتند: ای صالح! پیش از این در میان ما [به عنوان انسانی عاقل و خردمند] مورد امید بودی، آیا ما را از پرستیدن آنچه پدرانمان می پرستیدند، باز می داری؟ و ما بی تردید در [درستی و صحّت] تعالیمی که به آن دعوتمان می کنی در شکی شدید و سخت هستیم [چنان شکی که در خردمندی تو نیز دچار تردیدیم.]'),
(1536, 11, 63, 'گفت: ای قوم من! مرا خبر دهید اگر من بر دلیلی روشن از سوی پروردگارم متکی باشم و از سوی خود رحمتی به من عطا کرده باشد، چنانچه [در ابلاغ پیامش] از او نافرمانی کنم، چه کسی مرا [از عذابش] نجات می دهد؟ پس شما [در صورتی که خواسته های بی جایتان را بپذیرم] چیزی جز خسارت بر من نمی افزایید.'),
(1537, 11, 64, 'و ای قوم من! این ناقه خداست که برای شما [در اثبات صدق نبوّت من] نشانه ای [عظیم] است، پس بگذاریدش در زمین خدا بچرد، و هیچ آسیب و گزندی به او نرسانید که عذابی زود هنگام شما را خواهد گرفت.'),
(1538, 11, 65, 'پس آن را [از روی طغیان و سرکشی] پی کردند. پس صالح گفت: سه روز [فرصت دارید که] در خانه هایتان از زندگی برخوردار باشید. این وعده ای بی دروغ است.'),
(1539, 11, 66, 'پس هنگامی که فرمان ما [بر عذاب آنان] فرا رسید، صالح و آنان را که همراه او ایمان آورده بودند با رحمتی از سوی خود نجات دادیم، و از خواری و رسوایی آن روز [رهایی بخشیدیم]. مسلماً فقط پروردگارت نیرومند و توانای شکست ناپذیر است.'),
(1540, 11, 67, 'و کسانی را که ستم کردند، فریاد مرگبار فروگرفت، پس در خانه هایشان به رو درافتاده، جسمی بی جان شدند.'),
(1541, 11, 68, 'گویی در آنجا اقامت نداشتند. آگاه باشید! قطعاً قوم ثمود به پروردگارشان کافر شدند. هان! دوری [از رحمت خدا] بر قوم ثمود باد.'),
(1542, 11, 69, 'و به راستی فرستادگان ما ابراهیم را مژده آورده، سلام گفتند، او هم گفت: سلام [بر شما]. و درنگ نکرد تا گوساله ای بریان [برای آنان] آورد.'),
(1543, 11, 70, 'و چون دید دست هایشان به سوی غذا دراز نمی شود، آنان را ناشناس یافت و از آنان احساس ترس و دلهره کرد، گفتند: مترس که ما به سوی قوم لوط فرستاده شده ایم.'),
(1544, 11, 71, 'پس همسرش در حالی که ایستاده بود [از شنیدن گفتگوی فرشتگان با ابراهیم] خندید. پس او را به اسحاق و پس از اسحاق به یعقوب مژده دادیم.'),
(1545, 11, 72, '[همسر ابراهیم] گفت: ای وای بر من! آیا فرزند آورم در حالی که من پیرزنم و این شوهر من است که در سنّ سالخوردگی است؟ یقیناً این چیزی بسیار شگفت است!!'),
(1546, 11, 73, 'گفتند: آیا از کار خدا شگفتی می کنی؟ [در حالی که] رحمت خدا و برکاتش بر شما خانواده است. یقیناً او ستوده و بزرگوار است.'),
(1547, 11, 74, 'پس هنگامی که ترس و دلهره از ابراهیم برطرف شد و آن مژده به او رسید، با ما درباره قوم لوط [به قصد دفع عذاب از آنان] به گفتگو پرداخت.'),
(1548, 11, 75, 'به راستی که ابراهیم بسیار بردبار و دلسوز و روی آورنده [به سوی خدا] بود.'),
(1549, 11, 76, 'ای ابراهیم! از این [گفتگو] درگذر؛ زیرا فرمان پروردگارت [بر عذاب قوم لوط] فرا رسیده و یقیناً آنان را عذابی بدون بازگشت خواهد آمد.'),
(1550, 11, 77, 'و هنگامی که فرستادگان ما نزد لوط آمدند، به سبب آمدنشان تنگدل و اندوهگین شد؛ [زیرا خود را برای دفع خطر از مهمانانش در برابر قوم تبهکارش توانمند نیافت،] وگفت: این روز سخت و دشواری است.'),
(1551, 11, 78, 'و قومش شتابان به سویش آمدند [تا به خیال خود به مهمانانش دست درازی کنند، آن قومی] که پیش از آن کارهای زشت و ناپسند مرتکب می شدند، گفت: ای قوم من! اینان دختران من هستند، اینان [از نظر ازدواج] برای شما پاکیزه ترند، از خدا پروا کنید و مرا در میان مهمانانم رسوا نکنید؛ آیا از میان شما مردی راه یافته نیست؟'),
(1552, 11, 79, 'گفتند: مسلماً دانسته ای که ما را به دخترانت هیچ حاجت و نیازی نیست و خوب می دانی که ما چه می خواهیم!!'),
(1553, 11, 80, 'گفت: ای کاش برای مبارزه با شما قدرت و نیرویی داشتم یا به پناهگاهی استوار مأوی می گرفتم.'),
(1554, 11, 81, '[میهمانان] گفتند: ای لوط! ما فرستادگان پروردگار توایم، آنان هرگز به تو دسترسی پیدا نمی کنند، پس خانواده ات را در پاره ای از [همین] شب حرکت ده و نباید هیچ یک از شما به سوی شهر بازگردد، اما همسرت را با خود مبر زیرا عذابی که به تبهکاران می رسد به او هم خواهد رسید. یقیناً وعده گاهشان [برای دچار شدن به عذاب] صبح [فردا] است، آیا صبح نزدیک نیست؟'),
(1555, 11, 82, 'پس هنگامی که عذاب ما فرا رسید، بالاترین آن [سرزمین آلوده] را فروترینش نمودیم و بر آن سنگ هایی از نوع سنگِ گلِ لایه لایه فرو ریختیم.'),
(1556, 11, 83, '[سنگ هایی] که نزد پروردگارت نشانه دار بود و آن سنگ ها از ستمکاران دور نیست.'),
(1557, 11, 84, 'و به سوی [مردم] مَدین، برادرشان شعیب را [فرستادیم.] گفت: ای قوم من! خدا را بپرستید، شما را جز او هیچ معبودی نیست؛ و از پیمانه و ترازو مکاهید، همانا من شما را در توانگری و نعمت [ی که بی نیاز کننده از کم فروشی است] می بینم و بر شما از عذاب روزی فراگیر بیمناکم.'),
(1558, 11, 85, 'و ای قوم من، پیمانه و ترازو را عادلانه [و منصفانه] کامل و تمام بدهید، و اجناس مردم را [هنگام خریدن] کم شمارتر و کم ارزش تر [از آنچه که هست] به حساب نیاورید و در زمین تبهکارانه فتنه و آشوب برپا نکنید.'),
(1559, 11, 86, 'آنچه خدا [در کسب و کارتان از سود و بهره پس از پرداخت حق مردم] باقی می گذارد، برای شما بهتر است اگر مؤمن باشید، و من بر شما نگهبان نیستم.'),
(1560, 11, 87, 'گفتند: ای شعیب! آیا نمازت به تو فرمان می دهد که آنچه را پدرانمان می پرستیدند رها کنیم؟ یا از اینکه در اموالمان به هر کیفیتی که می خواهیم تصرف کنیم دست برداریم؟ به راستی که تو [انسانی] بردبار و راه یافته ای [پس چرا می خواهی در برابر آزادی ما نسبت به بت پرستی وهزینه کردن اموالمان به هر کیفیتی که بخواهیم بایستی؟!]'),
(1561, 11, 88, 'گفت: ای قوم من! مرا خبر دهید اگر من بر دلیل روشنی از سوی پروردگارم متکی باشم و از جانب او رزق نیکویی به من داده باشد [آیا رواست که خلاف خواسته او عمل کنم؟] و من نمی خواهم آنچه که شما را از آن باز می دارم خود مرتکب شوم؛ تا جایی که قدرت دارم جز اصلاح [شما را] نمی خواهم؛ و توفیقم فقط به [یاری] خداست؛ بر او توکل کردم و به سوی او باز می گردم.'),
(1562, 11, 89, 'ای قوم من! دشمنی و مخالفت با من، شما را به جایی نرساند، که [عذابی] مانند آنچه به قوم نوح یا قوم هود یا قوم صالح رسید به شما هم برسد، و قوم لوط از شما [چه از جهت زمان و چه از جهت مکان] چندان دور نیست.'),
(1563, 11, 90, 'از پروردگارتان آمرزش بطلبید، سپس به سوی او بازگردید؛ زیرا پروردگارم مهربان و بسیار دوستدار [توبه کنندگان] است.'),
(1564, 11, 91, 'گفتند: ای شعیب! بسیاری از مطالبی که می گویی نمی فهمیم، و به راستی تو را در [مقایسه با] خود، ناتوان و ضعیف می بینیم، و اگر عشیره ات نبودند، بی تردید سنگسارت می کردیم، وتو بر ما پیروز نیستی.'),
(1565, 11, 92, 'گفت: ای قوم من! آیا عشیره کوچکم نزد شما از خدا عزیزتر است که او را پشت سر قرار داده و فراموشش کرده اید؟! یقیناً پروردگارم به همه اعمالی که انجام می دهید، احاطه دارد.'),
(1566, 11, 93, 'و ای قوم من! به اندازه ای که در قدرت و توان شماست، عمل کنید، من [هم] بی تردید [به وظیفه الهی خود] عمل می کنم؛ به زودی خواهید دانست، چه کسی را عذاب رسوا کننده خواهد آمد و دروغگو کیست؟! و منتظر بمانید که من هم با شما منتظرم.'),
(1567, 11, 94, 'و هنگامی که عذاب ما رسید، شعیب و کسانی را که با او ایمان آورده بودند، با رحمتی از سوی خود نجات دادیم، و کسانی را که [به آیات ما] ستم کردند، فریاد مرگبار فرا گرفت، پس در خانه هایشان به رو در افتاده جسمی بی جان شدند.'),
(1568, 11, 95, 'گویی در آنجا اقامت نداشتند. آگاه باشید! مَدین [از رحمت خدا] دور باد، همان گونه که ثمود دور شد.'),
(1569, 11, 96, 'همانا موسی را با نشانه های خود و برهانی روشن فرستادیم،'),
(1570, 11, 97, 'به سوی فرعون و اشراف و سرانِ [قومِ] او، ولی [آنان برای حفظ مقام و مال خود] از فرمان فرعون پیروی کردند، در حالی که فرمان فرعون راهنمای به سوی سعادت و خوشبختی نبود.'),
(1571, 11, 98, '[فرعون] روز قیامت پیشاپیش قومش می رود، پس آنان را به آتش درمی آورد، و بد نصیب و سهمی است [آتشی] که در آن وارد می شود.'),
(1572, 11, 99, 'و در این دنیا و روز قیامت به لعنتی بدرقه شدند [و آن لعنت] بد عطیه ای است که به آنان می دهند.'),
(1573, 11, 100, 'این از گزارش های شهرها [و اهل آنها] است که آن را بر تو حکایت می کنیم، برخی از آن [شهر] ها هنوز برجایند، و برخی درو شده بر باد رفته اند!'),
(1574, 11, 101, 'و ما بر آنان ستم نکردیم، ولی آنان بر خویشتن ستم ورزیدند، پس هنگامی که عذاب پروردگارت فرا رسید، معبودانی که به جای خدا می پرستیدند، چیزی [از عذاب را] از آنان دفع نکردند، و به آنان جز خسارت و هلاکت نیفزودند.'),
(1575, 11, 102, 'و چنین است مؤاخذه کردن پروردگارت، هنگامی که [مردم] آبادی ها را در آن حال که ستمکارند مؤاخذه می کند. بی تردید مؤاخذه او دردناک و سخت است.'),
(1576, 11, 103, 'یقیناً در آن مؤاخذه ها برای کسی که از عذاب آخرت می ترسد، عبرت است، [و] آن روزی است که مردم را برای آن گرد می آورند، و آن روزی است که [همه صحنه های آن] مورد مشاهده است.'),
(1577, 11, 104, 'و ما آن روز را جز برای مدتی اندک به تأخیر نمی اندازیم.'),
(1578, 11, 105, 'روزی که چون فرا رسد، هیچ کس جز به اجازه او سخن نمی گوید؛ پس برخی تیره بخت و برخی نیک بخت اند.'),
(1579, 11, 106, 'اما تیره بختان [که خود سبب تیره بختی خود بوده اند] در آتش اند، برای آنان در آنجا ناله های حسرت بار و عربده و فریاد است.'),
(1580, 11, 107, 'در آن تا آسمان ها و زمین پابرجاست جاودانه اند، مگر آنچه را که مشیّت پروردگارت اقتضا کرده است؛ بی تردید پروردگارت هر چه را اراده می کند، انجام می دهد.'),
(1581, 11, 108, 'اما نیک بختان [که به توفیق و رحمت خدا سعادت یافته اند] تا آسمان ها و زمین پابرجاست، در بهشت جاودانه اند مگر آنچه را مشیّت پروردگارت اقتضا کرده، [بهشت] عطایی قطع ناشدنی و بی پایان است.'),
(1582, 11, 109, 'پس در باطل بودن معبودانی که مشرکان می پرستند، شک نداشته باش؛ اینان نمی پرستند مگر به همان صورت که پدرانشان پیش از این می پرستیدند [و آن پرستشی از روی جهل و بی خردی بود]. و ما سهمشان را [از عذاب] به طور کامل و بی کم و کاست خواهیم داد.'),
(1583, 11, 110, 'و به راستی به موسی کتاب دادیم، پس در آن اختلاف شد، و اگر از سوی پروردگارت [به سبب اتمام حجت] مهلتی برای انسان تا اجل معینش مقدر نشده بود، یقیناً میانشان [به عذاب] داوری شده بود [و هیچ کس از آنان پس از آن داوری زنده نمی ماند] و این یهودی ها [یِ عصر تو] درباره تورات [که بخشی از آیاتش تحریف شده و پاره ای از مطالبش آمیخته با خرافات و قسمتی از نوشته هایش دور از فطرت و غیر قابل اجراست] در تردیدی آمیخته با بدگمانی اند.'),
(1584, 11, 111, 'و یقیناً پروردگارت [پاداشِ] اعمال همه آنان را [در قیامت] کامل و تمام پرداخت خواهد کرد؛ زیرا او به آنچه انجام می دهند، آگاه است.'),
(1585, 11, 112, 'پس همان گونه که فرمان یافته ای ایستادگی کن؛ و نیز آنان که همراهت به سوی خدا روی آورده اند [ایستادگی کنند] و سرکشی مکنید که او به آنچه انجام می دهید، بیناست.'),
(1586, 11, 113, 'و به کسانی که [به آیات خدا، پیامبر و مردم مؤمن] ستم کرده اند، تمایل و اطمینان نداشته باشید و تکیه مکنید که آتش [دوزخ] به شما خواهد رسید ودر آن حال شما را جز خدا هیچ سرپرستی نیست، سپس یاری نمی شوید.'),
(1587, 11, 114, 'و نماز را در دو طرف روز و ساعات نخستین شب برپا دار، که یقیناً نیکی ها، بدی ها را از میان می برند، این برای یادکنندگان تذکّر و یاد آوری است.'),
(1588, 11, 115, 'و شکیبایی کن که یقیناً خدا پاداش نیکوکاران را تباه نمی کند.'),
(1589, 11, 116, 'پس چرا در میان امت هایی که پیش از شما بودند، مصلحانی دلسوز نبودند که [مردم را] از فساد در زمین بازدارند؟ مگر اندکی که [به سبب بازداشتن مردم از فساد] نجاتشان دادیم. و آنان که ستم کردند، دنباله رو [و دل بسته] چیزی از نعمت و ثروت شدند [که در آن به سرکشی و طغیان افتادند] و آنان گنهکار بودند.'),
(1590, 11, 117, 'و پروردگارت بر آن نبوده است که شهرهایی را در حالی که مردمش درست کارند، ظالمانه هلاک کند.'),
(1591, 11, 118, 'اگر پروردگارت می خواست یقیناً تمام مردم را [از روی اجبار، در مسیر هدایت] امت واحدی قرار می داد، [ولی نخواست به همین سبب] همواره [در امر دین] در اختلاف اند.'),
(1592, 11, 119, 'مگر کسانی که پروردگارت به آنان رحم کرده و به همین سبب آنان را آفریده است. و فرمان حتمی پروردگارت تحقق یافت که همانا دوزخ را از همه جن و انس [که راه کفر و عناد را برگزیدند] پر خواهم کرد.'),
(1593, 11, 120, 'و هر سرگذشتی از سرگذشت های [پندآموز] پیامبران را که برایت می خوانیم، حقیقتی است که دل تو را به آن پابرجا و استوار کنیم، و برای تو در این سرگذشت ها حق و برای بهره بردن مؤمنان پند و تذکّری آمده است.'),
(1594, 11, 121, 'و به کسانی که ایمان نمی آورند، بگو: به اندازه قدرتتان عمل کنید، و ما هم [بی تردید به وظایف الهی خود] عمل می کنیم.'),
(1595, 11, 122, 'و [به خاطر اعمال ناهنجارتان] به انتظار [عذاب] بمانید که ما هم [عذاب شما را] منتظریم.'),
(1596, 11, 123, 'نهان آسمان ها و زمین فقط در سیطره دانش خداست، همه کارها به او باز گردانده می شود؛ پس او را بندگی کن و بر او توکل داشته باش، و پروردگارت از آنچه انجام می دهید، بی خبر نیست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الر ـ این است آیات کتاب روشنگر.'),
(1598, 12, 2, 'ما آن را قرآنی به زبان عربی نازل کردیم تا شما [درباره حقایق، مفاهیم، اشارات و لطایفش] تعقّل کنید.'),
(1599, 12, 3, 'ما بهترین داستان را با وحی کردن این قرآن بر تو می خوانیم و تو یقیناً پیش از آن از بی خبران [نسبت به این بهترین داستان] بودی.'),
(1600, 12, 4, '[یاد کن] آن گاه که یوسف به پدرش گفت: پدرم! من در خواب دیدم یازده ستاره و خورشید و ماه برایم سجده کردند!'),
(1601, 12, 5, '[پدر] گفت: ای پسرک من! خواب خود را برای برادرانت مگو که نقشه ای خطرناک بر ضد تو به کار می بندند، بدون شک شیطان برای انسان دشمنی آشکار است.'),
(1602, 12, 6, 'و این چنین پروردگارت تو را برمی گزیند و از تفسیر خواب ها به تو می آموزد، و نعمتش را بر تو و بر آل یعقوب تمام می کند، چنانکه پیش از این بر پدرانت ابراهیم و اسحاق تمام کرد؛ یقیناً پروردگارت دانا و حکیم است.'),
(1603, 12, 7, 'بی تردید [در داستان] یوسف و برادرانش نشانه هایی [از ربوبیّت، رحمت و لطف خدا] برای مردم کنجکاو است.'),
(1604, 12, 8, '[یاد کن] هنگامی را که برادران گفتند: با اینکه ما گروهی نیرومندیم، یوسف و برادرش نزد پدرمان از ما محبوب ترند، و قطعاً پدرمان در اشتباه روشن و آشکاری است.'),
(1605, 12, 9, '[یکی گفت:] یوسف را بکشید و یا او را در سرزمین نامعلومی بیندازید، تا توجه و محبت پدرتان فقط معطوف به شما شود. و پس از این گناه [با بازگشت به خدا و عذرخواهی از پدر] مردمی شایسته خواهید شد.'),
(1606, 12, 10, 'یکی از آنان گفت: یوسف را نکشید، اگر می خواهید کاری بر ضد او انجام دهید، وی را در مخفی گاه آن چاه اندازید، که برخی رهگذران او را برگیرند [و با خود ببرند!!]'),
(1607, 12, 11, 'گفتند: ای پدر! تو را چه شده که ما را نسبت به یوسف امین نمی دانی با اینکه ما بدون تردید خیرخواه اوییم.'),
(1608, 12, 12, 'فردا او را با ما روانه کن تا [در دشت و صحرا] بگردد و بازی کند، قطعاً ما حافظ و نگهبان او خواهیم بود.'),
(1609, 12, 13, 'گفت: بردن او مرا سخت اندوهگین می کند، و می ترسم شما از او غفلت کنید و گرگ، او را بخورد.'),
(1610, 12, 14, 'گفتند: اگر با بودن ما که گروهی نیرومندیم، گرگ او را بخورد، یقیناً ما در این صورت زیانکار و بی مقداریم.'),
(1611, 12, 15, 'پس هنگامی که وی را بردند و تصمیم گرفتند که او را در مخفی گاه آن چاه قرارش دهند [تصمیم خود را به مرحله اجرا گذاشتند] و ما هم به او الهام کردیم که از این کار آگاهشان خواهی ساخت در حالی که آنان نمی فهمند [که تو همان یوسفی.]'),
(1612, 12, 16, 'و شبان گاه گریه کنان نزد پدر آمدند.'),
(1613, 12, 17, 'گفتند: ای پدر! ما یوسف را در کنار بار و کالای خود نهادیم و برای مسابقه رفتیم؛ پس گرگ، او را خورد و تو ما را تصدیق نخواهی کرد اگرچه راست بگوییم!'),
(1614, 12, 18, 'و خونی دروغین بر پیراهنش آوردند [تا یعقوب مرگ یوسف را باور کند]. گفت: چنین نیست که می گویید، بلکه نفس شما کاری [زشت را] در نظرتان آراست [تا انجامش بر شما آسان شود] در این حال صبری نیکو [مناسب تر است]؛ و خداست که بر آنچه شما [از وضع یوسف] شرح می دهید از او یاری خواسته می شود.'),
(1615, 12, 19, 'و کاروانی آمد، پس آب آورشان را فرستادند، او دلوش را به چاه انداخت، گفت: مژده! این پسری نورس است! و او را به عنوان کالا [ی تجارت] پنهان کردند؛ و خدا به آنچه می خواستند انجام دهند، دانا بود.'),
(1616, 12, 20, 'و او را به بهایی ناچیز، درهمی چند فروختند و نسبت به او بی رغبت بودند.'),
(1617, 12, 21, 'آن مرد مصری که یوسف را خرید، به همسرش گفت: جایگاهش را گرامی دار، امید است [در امور زندگی] به ما سودی دهد، یا او را به فرزندی انتخاب کنیم. این گونه یوسف را در سرزمین مصر مکانت بخشیدیم [تا زمینه فرمانروایی وحکومتش فراهم شود] و به او از تعبیر خواب ها بیاموزیم؛ و خدا بر کار خود چیره و غالب است، ولی بیشتر مردم نمی دانند.'),
(1618, 12, 22, 'و هنگامی که یوسف به سنّ کمال رسید، حکمت و دانش به او عطا کردیم، و ما نیکوکاران را این گونه پاداش می دهیم.'),
(1619, 12, 23, 'و آن [زنی] که یوسف در خانه اش بود، از یوسف با نرمی و مهربانی خواستار کام جویی شد، و [در فرصتی مناسب] همه درهای کاخ را بست و به او گفت: پیش بیا [که من در اختیار توام] یوسف گفت: پناه به خدا، او پروردگار من است، جایگاهم را نیکو داشت، [من هرگز به پروردگارم خیانت نمی کنم] به یقین ستمکاران رستگار نمی شوند.'),
(1620, 12, 24, 'بانوی کاخ [چون خود را در برابر یوسفِ پاکدامن، شکست خورده دید با حالتی خشم آلود] به یوسف حمله کرد و یوسف هم اگر برهان پروردگارش را [که جلوه ربوبیت و نور عصمت و بصیرت است] ندیده بود [به قصد دفاع از شرف و پاکی اش] به او حمله می کرد [و در آن حال زد و خورد سختی پیش می آمد و با مجروح شدن بانوی کاخ، راه اتهام بر ضد یوسف باز می شد، ولی دیدن برهان پروردگارش او را از حمله بازداشت و راه هر گونه اتهام از سوی بانوی کاخ بر او بسته شد]. [ما] این گونه [یوسف را یاری دادیم] تا زد و خورد [ی که سبب اتهام می شد] و [نیز] عمل خلاف عفت آن بانو را از او بگردانیم؛ زیرا او از بندگان خالص شده ما [از هر گونه آلودگی ظاهری و باطنی] بود.'),
(1621, 12, 25, 'و هر دو به سوی در کاخ پیشی گرفتند، و بانو پیراهن یوسف را از پشت پاره کرد و [در آن حال] در کنار [آستانه] در به شوهر وی برخوردند؛ بانو به شوهرش گفت: کسی که نسبت به خانواده ات قصد بدی داشته باشد، کیفرش جز زندان یا شکنجه دردناک [چه خواهد بود؟!]'),
(1622, 12, 26, 'یوسف گفت: او از من خواستار کام جویی شد. و گواهی از خاندان بانو چنین داوری کرد: اگر پیراهن یوسف از جلو پاره شده، بانو راست می گوید و یوسف دروغگوست.'),
(1623, 12, 27, 'و اگر پیراهنش از پشت پاره شده، بانو دروغ می گوید و یوسف راستگوست.'),
(1624, 12, 28, 'پس همسر بانو چون دید پیراهن یوسف از پشت پاره شده، گفت: این [فتنه و فساد] از نیرنگ شما [زنان] است، بی تردید نیرنگ شما بزرگ است.'),
(1625, 12, 29, 'یوسفا! این داستان را ندیده بگیر. و تو [ای بانو!] از گناهت استغفار کن؛ زیرا تو از خطاکارانی.'),
(1626, 12, 30, 'و گروهی از زنان در شهر شایع کردند که همسر عزیز [مصر] در حالی که عشق آن نوجوان در درون قلبش نفوذ کرده از او درخواست کام جویی می کند؛ یقیناً ما او را در گمراهی آشکاری می بینیم.'),
(1627, 12, 31, 'پس هنگامی که بانوی کاخ گفتارِ مکرآمیز آنان را شنید [برای آنکه به آنان ثابت کند که در این رابطه، سخنی نابجا دارند] به مهمانی دعوتشان کرد، و برای آنان تکیه گاه آماده نمود و به هر یک از آنان [برای خوردن میوه] کاردی داد و به یوسف گفت: به مجلس آنان در آی. هنگامی که او را دیدند به حقیقت در نظرشان بزرگ [و بسیار زیبا] یافتند و [از شدت شگفتی و حیرت به جای میوه] دست هایشان را بریدند و گفتند: حاشا که این بشر باشد! او جز فرشته ای بزرگوار نیست.'),
(1628, 12, 32, 'بانوی کاخ گفت: این همان کسی است که مرا درباره عشق او سرزنش کردید. به راستی من از او خواستار کام جویی شدم، ولی او در برابر خواست من به شدت خودداری کرد، و اگر فرمانم را اجرا نکند یقیناً خوار و حقیر به زندان خواهد رفت.'),
(1629, 12, 33, 'یوسف گفت: پروردگارا! زندان نزد من محبوب تر است از عملی که مرا به آن می خوانند، و اگر نیرنگشان را از من نگردانی به آنان رغبت می کنم و از نادانان می شوم.'),
(1630, 12, 34, 'پس پروردگارش خواسته اش را اجابت کرد و نیرنگ زنان را از او بگردانید؛ زیرا خدا شنوا و داناست.'),
(1631, 12, 35, 'آن گاه آنان پس از آنکه نشانه ها [یِ پاکی و پاکدامنی یوسف] را دیده بودند، عزمشان بر این جزم شد که تا مدتی او را به زندان اندازند.'),
(1632, 12, 36, 'و دو غلام [پادشاه مصر] با یوسف به زندان افتادند. یکی از آن دو نفر گفت: من پی در پی خواب می بینم که [برای] شراب، [انگور] می فشارم، و دیگری گفت: من خواب می بینم که بر سر خود نان حمل می کنم [و] پرندگان از آن می خورند، از تعبیر آن ما را خبر ده؛ زیرا ما تو را از نیکوکاران می دانیم.'),
(1633, 12, 37, 'گفت: هیچ جیره غذایی برای شما نمی آید مگر آنکه من شما را پیش از آمدنش از تعبیر آن خواب آگاه می کنم، این از حقایقی است که خدا به من آموخته است؛ زیرا من آیین مردمی را که به خدا ایمان ندارند و به سرای آخرت کافرند، رها کرده ام.'),
(1634, 12, 38, 'و [از ابتدا] از آیین پدرانم ابراهیم و اسحاق و یعقوب پیروی کرده ام، برای ما شایسته نیست که چیزی را شریک خدا قرار دهیم. این از فضل خدا بر ما و بر مردم است، ولی بیشتر مردم ناسپاسند.'),
(1635, 12, 39, 'ای دو یار زندان! آیا معبودان متعدد و متفرق بهتر است یا خدای یگانه مقتدر؟'),
(1636, 12, 40, 'شما به جای خدا جز [بت هایی] با نام هایی بی اثر و بی معنا که خود و پدرانتان آنها را نامگذاری کرده اید نمی پرستید، خدا [که صاحب اختیار همه هستی است] هیچ دلیلی بر [حقّانیّت آنها برای پرستش] نازل نکرده است. حکم فقط ویژه خداست، او فرمان داده که جز او را نپرستید. دین درست و راست و آیین پابرجا و حق همین است، ولی بیشتر مردم [حقایق را] نمی دانند.'),
(1637, 12, 41, 'ای دو یار زندان! اما یکی از شما [از زندان رهایی می یابد و] سرور خود را شراب می نوشاند، اما دیگری به دار آویخته می شود و پرندگان از سر او خواهند خورد. تعبیر خوابی که از من جویا شدید، تحققّش قطعی و انجامش حتمی شده است.'),
(1638, 12, 42, 'و به یکی از آن دو نفر که دانست آزاد می شود، گفت: مرا نزد سرور خود یاد کن. ولی شیطان یاد کردنِ از یوسف را نزد سرورش از یاد او برد؛ در نتیجه چند سالی در زندان ماند.'),
(1639, 12, 43, 'و پادشاه [مصر به بارگاه نشینانش] گفت: پی در پی در خواب می بینم که هفت گاو لاغر، هفت گاو فربه را می خورند، و نیز هفت خوشه سبز و هفت خوشه خشک را مشاهده می کنم؛ شما ای بزرگان! اگر تعبیر خواب می دانید درباره خوابم نظر دهید.'),
(1640, 12, 44, 'گفتند: [این] خواب هایی پریشان و آشفته است و ما به تعبیر خواب های پریشان و آشفته دانا نیستیم.'),
(1641, 12, 45, 'از آن دو زندانی آنکه آزاد شده بود و پس از مدتی [یوسف را] به یاد آورد گفت: من یقیناً شما را از تعبیر آن آگاه می کنم، پس [مرا به زندان] بفرستید.'),
(1642, 12, 46, '[سپس به زندان نزد یوسف رفت و گفت:] تو ای یوسف! ای راستگویِ [راست کردار!] درباره هفت گاو فربه که هفت [گاو] لاغر آنان را می خورند، و هفت خوشه سبز و [هفت خوشه] خشک دیگر، نظرت را برای ما بیان کن. امید است نزد مردم برگردم، باشد که [از تعبیر این خواب عجیب] آگاه شوند.'),
(1643, 12, 47, 'گفت: هفت سال با تلاش پی گیر زراعت کنید، پس آنچه را درو کردید جز اندکی که خوراک شماست در خوشه اش باقی گذارید.'),
(1644, 12, 48, 'سپس بعد از آن [هفت سال فراوانی و گشایش] هفت سال سخت و دشوار [پیش] می آید که آنچه را برای آن [سال ها] ذخیره کرده اید مگر اندکی که برای کاشتن نگهداری می کنید، می خورید.'),
(1645, 12, 49, 'آن گاه بعد از آن [دوره سخت و دشوار،] سالی می آید که مردم در آن بارانِ [فراوان] یابند و در آن [سال از محصولات کشاورزی] عصاره میوه می گیرند.'),
(1646, 12, 50, 'و پادشاه [مصر] گفت: یوسف را نزد من آورید. هنگامی که فرستاده [پادشاه] نزد یوسف آمد، یوسف گفت: نزد سرورت بازگرد و از او بپرس حال و داستان زنانی که دست های خود را بریدند، چه بود؟ یقیناً پروردگارم به نیرنگ آنان داناست.'),
(1647, 12, 51, '[پادشاه به زنان] گفت: داستان شما هنگامی که یوسف را به کام جویی دعوت کردید چیست؟ [زنان] گفتند: پاک و منزّه است خدا! ما هیچ بدی در او سراغ نداریم. همسر عزیز گفت: اکنون حق [پس از پنهان ماندنش] به خوبی آشکار شد، من [بودم که] از او درخواست کام جویی کردم، یقیناً یوسف از راستگویان است.'),
(1648, 12, 52, '[من به پاکی او و گناه خود اعتراف کردم] و این اعتراف برای این است که یوسف بداند من در غیاب او به وی خیانت نورزیدم و اینکه خدا نیرنگ خیانت کاران را به نتیجه نمی رساند.'),
(1649, 12, 53, 'من خود را از گناه تبرئه نمی کنم؛ زیرا نفس طغیان گر، بسیار به بدی فرمان می دهد مگر زمانی که پروردگارم رحم کند؛ زیرا پروردگارم بسیار آمرزنده و مهربان است.'),
(1650, 12, 54, 'و پادشاه گفت: یوسف را نزد من آورید تا او را برای کارهای خود برگزینم. پس هنگامی که با یوسف سخن گفت به او اعلام کرد: تو امروز نزد ما دارای منزلت ومقامی و [در همه امور] امینی.'),
(1651, 12, 55, 'یوسف گفت: مرا سرپرست خزانه های این سرزمین قرار ده؛ زیرا من نگهبان دانایی هستم.'),
(1652, 12, 56, 'این گونه یوسف را در [آن] سرزمین مکانت و قدرت دادیم که هر جای آن بخواهد اقامت نماید. رحمت خود را به هر کس که بخواهیم می رسانیم و پاداش نیکوکاران را تباه نمی کنیم.'),
(1653, 12, 57, 'و یقیناً پاداش آخرت برای کسانی که ایمان آورده اند وهمواره پرهیزکاری می کردند، بهتر است.'),
(1654, 12, 58, 'و برادران یوسف [با روی آوردن خشکسالی به کنعان، جهت تهیه آذوقه به مصر] آمدند وبر او وارد شدند. پس او آنان را شناخت وآنان او را نشناختند.'),
(1655, 12, 59, 'و هنگامی که زاد و توشه آنان را در اختیارشان قرار داد، گفت: برادر پدری خود را نزد من آورید، آیا نمی بینید که من پیمانه را کامل و تمام می پردازم و بهتر از هر کس مهمانداری می کنم؟'),
(1656, 12, 60, 'پس اگر او را نزد من نیاورید، هیچ پیمانه ای پیش من ندارید و نزدیک من نیایید.'),
(1657, 12, 61, 'گفتند: می کوشیم رضایت پدرش را به آوردن او جلب کنیم، و یقیناً این کار را انجام خواهیم داد.'),
(1658, 12, 62, 'و [یوسف] به کارگزاران و گماشتگانش گفت: اموالشان را [که در برابر دریافت آذوقه پرداختند] در بارهایشان بگذارید، امید است وقتی به خاندان خود برگشتند آن را بشناسند، باشد که دوباره برگردند.'),
(1659, 12, 63, 'پس هنگامی که به سوی پدرشان بازگشتند، گفتند: ای پدر! پیمانه از ما منع شد، پس برادرمان را با ما روانه کن تا پیمانه بگیریم، یقیناً ما او را حفظ خواهیم کرد.'),
(1660, 12, 64, 'گفت: آیا همان گونه که شما را پیش از این نسبت به برادرش امین پنداشتم، درباره او هم امین پندارم؟ [من به مراقبت و نگهبانی شما امید ندارم] پس خدا بهترین نگهبان است و او مهربان ترین مهربانان است.'),
(1661, 12, 65, 'و هنگامی که کالایشان را گشودند، دیدند اموالشان را به آنان بازگردانده اند، گفتند: ای پدر! [بهتر از این] چه می خواهیم؟ این اموال ماست که به ما بازگردانده و ما [دوباره با همین اموال] برای خانواده خود آذوقه می آوریم و برادرمان را حفظ می کنیم، و بار شتری اضافه می کنیم و آن [بار شتر از نظر عزیز که مردی کریم است] باری ناچیز است.'),
(1662, 12, 66, 'گفت: برادرتان را همراه شما [به مصر] نمی فرستم تا اینکه پیمان محکمی از خدا به من بسپارید که او را حتماً به من بازگردانید، مگر اینکه [به سبب بسته شدن همه راه ها به روی شما] نتوانید. پس هنگامی که پیمان استوارشان را به پدر سپردند، گفت: خدا بر آنچه می گوییم، وکیل است.'),
(1663, 12, 67, 'و گفت: ای پسرانم! [در این سفر] از یک در وارد نشوید بلکه از درهای متعدد وارد شوید، و البته من [با این تدبیر] نمی توانم هیچ حادثه ای را که از سوی خدا برای شما رقم خورده از شما برطرف کنم، حکم فقط ویژه خداست، [تنها] بر او توکل کرده ام، و [همه] توکل کنندگان باید به خدا توکل کنند.'),
(1664, 12, 68, 'هنگامی که فرزندان یعقوب از آنجایی که پدرشان دستور داده بود، وارد شدند، تدبیر یعقوب نمی توانست هیچ حادثه ای را که از سوی خدا رقم خورده بود، از آنان برطرف کند جز خواسته ای که در دل یعقوب بود [که فرزندانش به سلامت و دور از چشم زخم وارد شوند] که خدا آن را به انجام رساند، یعقوب به سبب آنکه تعلیمش داده بودیم از دانشی [ویژه] برخوردار بود ولی بیشتر مردم [که فقط چشمی ظاهربین دارند، این حقایق را] نمی دانند.'),
(1665, 12, 69, 'و هنگامی که بر یوسف وارد شدند، برادر [مادری] ش را کنار خود جای داد، گفت: بی تردید من برادر تو هستم، بنابراین بر آنچه آنان همواره انجام می دادند [و من برای تو فاش کردم] اندوهگین مباش.'),
(1666, 12, 70, 'پس هنگامی که بارشان را آماده کرد، آبخوری [پادشاه] را در بار برادرش گذاشت. سپس ندا دهنده ای بانگ زد: ای کاروان! یقیناً شما دزد هستید!'),
(1667, 12, 71, 'کاروانیان روی به گماشتگان کردند و گفتند: چه چیزی گم کرده اید؟'),
(1668, 12, 72, 'گفتند: آبخوری شاه را گم کرده ایم و هر کس آن را بیاورد، یک بار شتر مژدگانی اوست و من ضامن آن هستم.'),
(1669, 12, 73, 'گفتند: به خدا سوگند شما به خوبی دانستید که ما نیامده ایم در این سرزمین فساد کنیم، و هیچ گاه دزد نبوده ایم.'),
(1670, 12, 74, 'گماشتگان گفتند: اگر شما دروغگو باشید [و سارق در میان شما یافت شود] کیفرش چیست؟'),
(1671, 12, 75, 'گفتند: کسی که آبخوری در بار و بنه اش پیدا شود، کیفرش خود اوست [که به غلامی گرفته شود]. ما ستمکاران را [در کنعان] به همین صورت کیفر می دهیم.'),
(1672, 12, 76, 'پس [یوسف] پیش از [بررسی] بارِ برادرش، شروع به [بررسی] بارهای [دیگر] برادران کرد، آن گاه آبخوری پادشاه را از بار برادرش بیرون آورد. ما این گونه برای یوسف چاره اندیشی نمودیم؛ زیرا او نمی توانست بر پایه قوانین پادشاه [مصر] برادرش را بازداشت کند مگر اینکه خدا بخواهد [بازداشت برادرش از راهی دیگر عملی شود]. هر که را بخواهیم [به] درجاتی بالا می بریم و برتر از هر صاحب دانشی، دانشمندی است.'),
(1673, 12, 77, 'برادران گفتند: اگر این شخص دزدی می کند [خلاف انتظار نیست]؛ زیرا پیش تر برادر [ی داشت که] او هم دزدی کرد. یوسف [به مقتضای کرامت و جوانمردی] این تهمت را در دل خود پنهان داشت و نسبت به آن سخنی نگفت و این راز را فاش نساخت. در پاسخ آنان گفت: منزلت شما بدتر [و دامنتان آلوده تر از این] است [که ظاهرتان نشان می دهد] و خدا به آنچه بیان می کنید، داناتر است.'),
(1674, 12, 78, 'گفتند: ای عزیز! او را پدری سالخورده و بزرگوار است، پس یکی از ما را به جای او بازداشت کن، بی تردید ما تو را از نیکوکاران می بینیم.'),
(1675, 12, 79, 'گفت: پناه بر خدا از اینکه بازداشت کنیم مگر کسی را که متاع خود را نزد وی یافته ایم، که در این صورت ستمکار خواهیم بود.'),
(1676, 12, 80, 'پس هنگامی که از عزیز مأیوس شدند، در کناری [با یکدیگر] به گفتگوی پنهان پرداختند. بزرگشان گفت: آیا ندانستید که پدرتان از شما پیمان استوار خدایی گرفت و پیش تر هم درباره یوسف کوتاهی کردید، بنابراین من هرگز از این سرزمین بیرون نمی آیم تا پدرم به من اجازه دهد، یا خدا درباره من حکم کند؛ و او بهترین حکم کنندگان است.'),
(1677, 12, 81, 'شما به سوی پدرتان بازگردید، پس به او بگویید: ای پدر! بدون شک پسرت دزدی کرد، و ما جز به آنچه دانستیم گواهی ندادیم وحافظ ونگهبان نهان هم [که در آن چه اتفاقی افتاده] نبودیم.'),
(1678, 12, 82, 'حقیقت را از شهری که در آن بودیم [و در و دیوارش گواه است] و از کاروانی که با آن آمدیم بپرس؛ و یقیناً ما راستگوییم.'),
(1679, 12, 83, '[برادران پس از بازگشت به کنعان، ماجرا را برای پدر بیان کردند، یعقوب] گفت: [نه چنین است که می گویید] بلکه نفوس شما کاری [زشت] را در نظرتان آراست [تا انجامش بر شما آسان شود] پس من بدون جزع و بیتابی شکیبایی می ورزم، امید است خدا همه آنان را پیش من آرد؛ زیرا او بی تردید، دانا و حکیم است.'),
(1680, 12, 84, 'و از آنان کناره گرفت و گفت: دریغا بر یوسف! و در حالی که از غصّه لبریز بود دو چشمش از اندوه، سپید شد.'),
(1681, 12, 85, 'گفتند: به خدا آن قدر از یوسف یاد می کنی تا سخت ناتوان شوی یا جانت را از دست بدهی.'),
(1682, 12, 86, 'گفت: شکوه اندوه شدید و غم و غصه ام را فقط به خدا می برم و از خدا می دانم آنچه را که شما نمی دانید.'),
(1683, 12, 87, 'ای پسرانم! بروید آن گاه از یوسف و برادرش جستجو کنید و از رحمت خدا مأیوس نباشید؛ زیرا جز مردم کافر از رحمت خدا مأیوس نمی شوند.'),
(1684, 12, 88, 'پس هنگامی که بر یوسف وارد شدند، گفتند: عزیزا! از سختی [قحطی و خشکسالی] به ما و خانواده ما گزند و آسیب رسیده و [برای دریافت آذوقه] مال ناچیزی آورده ایم، پس پیمانه ما را کامل بده و بر ما صدقه بخش؛ زیرا خدا صدقه دهندگان را پاداش می دهد.'),
(1685, 12, 89, 'گفت: آیا زمانی که نادان بودید، دانستید با یوسف و برادرش چه کردید؟'),
(1686, 12, 90, 'گفتند: شگفتا! آیا تو خود یوسفی؟! گفت: من یوسفم و این برادر من است، همانا خدا بر ما منت نهاده است؛ بی تردید هر کس پرهیزکاری کند و شکیبایی ورزد، [پاداش شایسته می یابد]؛ زیرا خدا پاداش نیکوکاران را تباه نمی کند.'),
(1687, 12, 91, 'گفتند: به خدا سوگند یقیناً که خدا تو را بر ما برتری بخشید و به راستی که ما خطاکار بودیم.'),
(1688, 12, 92, 'گفت: امروز هیچ ملامت و سرزنشی بر شما نیست، خدا شما را می آمرزد و او مهربان ترین مهربانان است.'),
(1689, 12, 93, 'این پیراهنم را ببرید، و روی صورت پدرم بیندازید، او بینا می شود و همه خاندانتان را نزد من آورید.'),
(1690, 12, 94, 'و زمانی که کاروان [از مصر] رهسپار [کنعان] شد، پدرشان گفت: بی تردید، بوی یوسف را می یابم اگر مرا سبک عقل ندانید.'),
(1691, 12, 95, 'گفتند: به خدا سوگند تو در همان خطا و گمراهی دیرینت هستی.'),
(1692, 12, 96, 'پس هنگامی که مژده رسان آمد، پیراهن را بر صورت او افکند و او دوباره بینا شد، گفت: آیا به شما نگفتم که من از خدا حقایقی می دانم که شما نمی دانید؟'),
(1693, 12, 97, 'گفتند: ای پدر! آمرزش گناهانمان را بخواه، بی تردید ما خطاکار بوده ایم.'),
(1694, 12, 98, 'گفت: برای شما از پروردگارم درخواست آمرزش خواهم کرد؛ زیرا او بسیار آمرزنده و مهربان است.'),
(1695, 12, 99, 'پس زمانی که بر یوسف وارد شدند، پدر و مادرش را کنار خود جای داد و گفت: همگی با خواست خدا [آسوده خاطر و] در کمال امنیت وارد مصر شوید.'),
(1696, 12, 100, 'و پدر و مادرش را بر تخت بالا برد و همه برای او به سجده افتادند و گفت: ای پدر! این تعبیر خواب پیشین من است که پروردگارم آن را تحقق داد، و یقیناً به من احسان کرد که از زندان رهاییم بخشید، و شما را پس از آنکه شیطان میان من و برادرانم فتنه انداخت، از آن بیابان نزد من آورد، پروردگارم برای هر چه بخواهد با لطف برخورد می کند؛ زیرا او دانا و حکیم است.'),
(1697, 12, 101, 'پروردگارا! تو بخشی از فرمانروایی را به من عطا کردی و برخی از تعبیر خواب ها را به من آموختی. ای پدید آورنده آسمان ها و زمین! تو در دنیا و آخرت سرپرست و یار منی در حالی که تسلیم [فرمان های تو] باشم جانم را بگیر، و به شایستگان مُلحقم کن.'),
(1698, 12, 102, 'این از سرگذشت های پرفایده غیب است که به تو وحی می کنیم، و تو هنگامی که آنان در کارشان تصمیم گرفتند و [برای انجامش] نیرنگ می کردند، نزدشان نبودی.'),
(1699, 12, 103, 'بیشتر مردم هر چند رغبت شدید [به ایمان آوردنشان] داشته باشی، ایمان نمی آورند.'),
(1700, 12, 104, 'و در حالی که هیچ پاداشی [در برابر ابلاغ قرآن] از آنان نمی خواهی، این [قرآن] جز پندی برای جهانیان نیست.'),
(1701, 12, 105, 'و [برای هدایت مردم] در آسمان ها و زمین چه بسیار نشانه هاست که [در غفلت و بی خبری] بر آنها می گذرند در حالی که از آنها روی می گردانند.'),
(1702, 12, 106, 'و بیشترشان به خدا ایمان نمی آورند مگر آنکه [برای او] شریک قرار می دهند.'),
(1703, 12, 107, 'آیا ایمنند از اینکه فراگیرنده ای از عذاب خدا بیایدشان، یا ناگاه قیامت در حالی که نمی فهمند بر آنان فرا رسد؟'),
(1704, 12, 108, 'بگو: این طریقه و راه من است که من و هر کس پیرو من است بر پایه بصیرت و بینایی به سوی خدا دعوت می کنیم، و خدا از هر عیب و نقصی منزّه است و من از مشرکان نیستم.'),
(1705, 12, 109, 'و پیش از تو [بخاطر هدایت مردم] جز مردانی از اهل آبادی ها را که به آنان وحی می نمودیم نفرستادیم. آیا [مخالفان حق] به گردش و سفر در زمین نرفتند تا با تأمل بنگرند که عاقبت کسانی که پیش از آنان بودند [و از روی کبر و عناد به مخالفت با حق برخاستند] چگونه بود؟ و مسلماً سرای آخرت برای کسانی که پرهیزکاری کردند، بهتر است؛ آیا نمی اندیشید؟'),
(1706, 12, 110, '[پیامبران، مردم را به خدا خواندند و مردم هم حق را منکر شدند] تا زمانی که پیامبران [از ایمان آوردن اکثر مردم] مأیوس شدند و گمان کردند که به آنان [از سوی مردم در وعده یاری و حمایت] دروغ گفته شده است. [ناگهان] یاری ما به پیامبران رسید؛ پس کسانی را که خواستیم رهایی یافتند و عذاب ما از گروه مجرمان برگردانده نمی شود.'),
(1707, 12, 111, 'به راستی در سرگذشت آنان عبرتی برای خردمندان است. [قرآن] سخنی نیست که به دروغ بافته شده باشد، بلکه تصدیق کننده کتاب های آسمانی پیش از خود است و بیان گر هر چیز است و برای مردمی که ایمان دارند، سراسر هدایت و رحمت است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'المر ـ این آیاتِ [با عظمتِ] کتابِ [الهی] است، و آنچه از سوی پروردگارت بر تو نازل شده، سراسر حق و راستی است [چنان حقّی که هیچ باطلی در آن راه ندارد] ولی بیشتر مردم [به سبب عناد و لجاجت] ایمان نمی آورند.'),
(1709, 13, 2, 'خداست که آسمان ها را بدون پایه هایی که آنها را ببینید، برافراشت، آن گاه بر تخت فرمانروایی [و حکومت بر آفرینش] چیره و مسلط شد، و خورشید و ماه را رام و مسخّر ساخت، که هر کدام تا زمان معینی روانند، کار [جهان و جهانیان] را تدبیر می کند، نشانه ها [ی قدرت و حکمتش] را [در پهنه آفرینش] به روشنی بیان می کند تا شما به دیدار [قیامت و محاسبه شدن اعمال به وسیله] پروردگارتان یقین کنید.'),
(1710, 13, 3, 'و اوست که زمین را گسترانید، و در آن کوه هایی استوار و نهرهایی پدید آورد و در آن از همه محصولات و میوه ها جفت دوتایی [که نر و ماده است] قرار داد، شب را به روز می پوشاند، [تا ادامه حیات برای همه نباتات و موجودات زنده ممکن باشد]؛ یقیناً در این امور برای مردمی که می اندیشند نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] ست.'),
(1711, 13, 4, 'در زمین قطعه هایی مختلف و گوناگون، و باغ هایی از انگور و کشتزار و درختان خرمایی است که بر یک ریشه و غیر یک ریشه [می رویند]. ما برخی از آنان را در میوه و محصول با آنکه از یک آب سیراب می شوند بر برخی دیگر برتری می دهیم. بی تردید در این امور برای مردمی که تعقّل می کنند، نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] ست.'),
(1712, 13, 5, 'اگر بخواهی تعجب کنی، پس تعجب از گفتار منکران لجوج است که [بدون توجه به قدرت خدا که آنان را از خاک پدید آورد، می گویند]: آیا ما هنگامی که [پس از مردن] خاک شدیم، به راستی در آفرینش جدیدی خواهیم بود؟! اینانند که به پروردگارشان کافر شدند، و اینانند که در گردنشان بندها و زنجیرها [ی گمراهی در دنیا و عذاب در آخرت] است، و اینان اهل آتش اند و در آن جاودانه اند.'),
(1713, 13, 6, 'و از تو [بر پایه تمسخر، استهزا، جهل و نادانی] پیش از [درخواست] خیر و خوشی تقاضای شتاب در [فرود آمدن] عذاب می کنند، [اینان گمان می کنند که عذاب ها و مجازات های الهی دروغ است] با اینکه پیش از آنان عذاب ها و عقوبت های عبرت آموز [بر کافران و منکران] گذشته است و قطعاً پروردگارت نسبت به مردم با ظلم و ستمی که می کنند صاحب آمرزش است، و مسلماً پروردگارت سخت کیفر است.'),
(1714, 13, 7, 'و کافران می گویند: چرا از سوی پروردگارش معجزه ای [غیر قرآن] بر او نازل نشده؟ تو فقط بیم دهنده ای [نه اعجاز کننده ای که هر زمان هر کس و برای هر هدفی غیر منطقی بخواهد، دست به اعجاز بزنی] وبرای هر قومی هدایت کننده ای است.'),
(1715, 13, 8, 'خدا [حالات، صفات و زمان ولادت] جنین هایی را که هر انسان و حیوان ماده ای آبستن است، و آنچه را رحم ها [از زمان طبیعی حمل] می کاهند و آنچه را می افزایند، می داند؛ و هر چیزی نزد او اندازه معینی دارد.'),
(1716, 13, 9, 'دانای نهان و آشکار و بزرگ و بلندمرتبه است.'),
(1717, 13, 10, '[برای او] یکسان است که کسی از شما گفتارش را پنهان کند یا آشکار سازد، و کسی مخفیانه در دل شب حرکت نماید یا در روز راه بپیماید.'),
(1718, 13, 11, 'برای انسان از پیش رو و پشت سر، مأمورانی است که همواره او را به فرمان خدا [از آسیب ها و گزندها] حفظ می کنند. یقیناً خدا سرنوشت هیچ ملتی را [به سوی بلا، نکبت، شکست و شقاوت] تغییر نمی دهد تا آنکه آنان آنچه را [از صفات خوب و رفتارشایسته و پسندیده] در وجودشان قرار دارد به زشتی ها و گناه تغییر دهند. و هنگامی که خدا نسبت به ملتی آسیب و گزند بخواهد [برای آن آسیب و گزند] هیچ راه بازگشتی نیست؛ زیرا برای آنان جز خدا هیچ یاوری نخواهد بود.'),
(1719, 13, 12, 'اوست که برق [جهنده را از میان قطعه های ابر] که مایه ترس و امید است، به شما نشان می دهد، و ابرهای سنگین بار را پدید می آورد.'),
(1720, 13, 13, 'و رعد، همراه با ستایش خدا تسبیح می گوید و فرشتگان نیز از بیمش [تسبیح می گویند]، و صاعقه ها را می فرستد و به وسیله آن به هر کس بخواهد آسیب می رساند، در حالی که اینان [با مشاهده این همه آثار قدرت که صادر شده از خدای یکتاست، باز هم] درباره خدا مجادله و ستیز می کنند در صورتی که خدا [دارایِ قدرتی بی نهایت و] سخت کیفر است.'),
(1721, 13, 14, 'دعوت حق [که اجابتش مایه سعادت دنیا و آخرت است] فقط ویژه خداست. و کسانی را که مشرکان به جای خدا می خوانند، چیزی [از درخواست ها و نیازهایشان را] برای آنان برآورده نمی کنند [درخواست این بی خردان از بتان بی جان و جاندار نیست] مگر مانند کسی که دو دستش را به سوی آبی [که با آن فاصله زیادی دارد] می گشاید تا آب به دهانش برسد و حال آنکه هرگز نخواهد رسید، و دعا و درخواست کافران [از غیر خدا] جز در گمراهی و تباهی [که به نتیجه و هدف نمی رسد] نیست.'),
(1722, 13, 15, 'کسانی که در آسمان ها و زمین هستند، از روی میل و رغبت یا بی میلی و کراهت و نیز سایه هایشان بامداد و شام گاه برای خدا سجده می کنند.'),
(1723, 13, 16, '[به مشرکان] بگو: پروردگار آسمان ها و زمین کیست؟ [خود بی درنگ] بگو: خدای یکتاست. بگو: آیا سرپرستان و معبودانی به جای او انتخاب کرده اید که اختیار هیچ سود و زیانی را برای خودشان ندارند [چه رسد برای شما؟] بگو: آیا نابینا و بینا یکسانند یا تاریکی و نور برابرند؟ یا آنان شریکانی برای خدا قرار دادند [به خیال آنکه] مانند آفرینش خدا چیزی آفریده اند، آن گاه آفریده خدا و آفریده شریکان بر آنان مشتبه شده؟ بگو: آفریننده همه چیز فقط خداست و تنها او یکتای پیروز است.'),
(1724, 13, 17, 'خدا از آسمان آبی نازل کرد که در هر درّه و رودی به اندازه گنجایش و وسعتش [سیلابی] جاری شد، سپس سیلاب، کفی پُف کرده را به روی خود حمل کرد، و نیز از فلزاتی که برای به دست آوردن زینت و زیور یا کالا و متاع، آتش بر آن می افروزند، کفی پُف کرده مانند سیلاب بر می آید؛ این گونه خدا حق و باطل را [به امور محسوس] مَثَل می زند. اما آن کفِ [روی سیل و روی فلز گداخته در حالی که کناری رفته] به حالتی متلاشی شده از میان می رود، و اما آنچه [چون آب و فلز خالص] به مردم سود می رساند، در زمین می ماند. خدا مَثَل ها را این گونه بیان می کند [تا مردم در همه امور حق را از باطل بشناسند.]'),
(1725, 13, 18, 'برای آنان که دعوت پروردگارشان را اجابت کردند، بهترین سرانجام است؛ و آنان که دعوت او را پاسخ نگفتند، اگر مالک دو برابر همه آنچه روی زمین است باشند، بی تردید آن را برای رهایی خود از عذاب خواهند داد؛ آنان را حساب سختی است، و جایگاهشان دوزخ است، و دوزخ بد بستری است.'),
(1726, 13, 19, 'آیا کسی که می داند آنچه از سوی پروردگارت بر تو نازل شده حق است، مانند کسی است که [از نظر باطن] نابیناست؟! فقط خردمندان [بینادل] متذکّرِ [حق] می شوند.'),
(1727, 13, 20, 'همان کسانی که به عهد خدا [که همانا قرآن است] وفا می کنند و پیمان را نمی شکنند.'),
(1728, 13, 21, 'و آنچه را خدا به پیوند آن فرمان داده پیوند، می دهند و از [عظمت و جلال] پروردگارشان همواره در هراسند و از حساب سخت و دشوار بیم دارند.'),
(1729, 13, 22, 'و برای به دست آوردن خشنودی پروردگارشان [در برابر گناهان و انجام وظایف و حوادث] شکیبایی ورزیدند، و نماز را بر پا داشتند، و بخشی از آنچه را روزی آنان کردیم در نهان و آشکار انفاق نمودند، و همواره با نیکیِ [عبادت] زشتی و پلیدی [گناه] را دفع می کنند [و با خوبی های خود نسبت به مردم، بدی های آنان را نسبت به خود برطرف می نمایند]، اینانند که فرجام نیک آن سرای، ویژه آنان است.'),
(1730, 13, 23, 'بهشت های جاویدی که آنان و پدران و همسران و فرزندان شایسته و درست کارشان در آن وارد می شوند، و فرشتگان از هر دری بر آنان درآیند.'),
(1731, 13, 24, '[و به آنان گویند:] سلام بر شما به پاس استقامت و صبرتان [در برابر عبادت، معصیت و مصیبت] پس نیکوست فرجام این سرای.'),
(1732, 13, 25, 'و کسانی که عهد خدا را پس از استوار کردنش می شکنند، و پیوندهایی را که خدا به برقراری آن فرمان داده می گسلند، و در زمین فساد می کنند، لعنت و فرجام بد و دشوار آن سرای برای آنان است.'),
(1733, 13, 26, 'خدا روزی را برای هر کس که بخواهد، وسعت می دهد و برای هر کس که بخواهد، تنگ می گیرد. و [آنان که از حیات جاوید و پرنعمت آخرت بی خبرند] به زندگی زودگذر دنیا شادمان شدند، در حالی که زندگی دنیا در برابر آخرت جز متاعی اندک و ناچیز نیست.'),
(1734, 13, 27, 'کافران می گویند: چرا معجزه ای [غیر قرآن] از سوی پروردگارش بر او نازل نشده؟ بگو: مسلماً خدا هر کس را بخواهد [پس از اتمام حجت] گمراه می کند و هر کس را که به سوی او بازگردد، هدایت می نماید.'),
(1735, 13, 28, '[بازگشتگان به سوی خدا] کسانی [هستند] که ایمان آوردند و دل هایشان به یاد خدا آرام می گیرد، آگاه باشید! دل ها فقط به یاد خدا آرام می گیرد.'),
(1736, 13, 29, 'کسانی که ایمان آوردند، و کارهای شایسته انجام دادند، برای آنان زندگی خوش و با سعادت و بازگشتی نیک است.'),
(1737, 13, 30, 'آن گونه [که رسولان گذشته را ارسال کردیم] تو را در میان امتی که پیش از آن، امت هایی روزگار به پایان بردند، فرستادیم، تا آنچه را به تو وحی کردیم بر آنان بخوانی، در حالی که آنان [با روی گرداندن از وحی] به [خدایِ] رحمان کفر می ورزند. بگو: او پروردگار من است، معبودی جز او نیست، فقط بر او توکل کردم و بازگشتم فقط به سوی اوست.'),
(1738, 13, 31, 'و اگر قرآنی بود که به وسیله آن کوه ها به حرکت می آمدند، یا زمین پاره پاره می گشت، یا به وسیله آن با مردگان سخن گفته می شد [باز هم این کافران لجوج، ایمان نمی آورند، زیرا وقتی با مشاهده قرآن که بزرگ ترین معجزه است ایمان نیاورند با مشاهده هیچ معجزه ای ایمان نمی آورند! کار هدایت و ایمان اینان ارتباطی به معجزه ندارد] بلکه همه کارها فقط در اختیار خداست. پس آیا آنان که ایمان آورده اند، ندانسته اند که اگر خدا می خواست همه مردم را [از روی اجبار] هدایت می کرد؟! [ولی هدایت اجباری فاقد ارزش است] و همواره به کافران به کیفر آنچه که انجام دادند، حادثه ای کوبنده و بسیار سخت می رسد یا حادثه نزدیک خانه هایشان فرود می آید [که شهر و دیارشان و اطرافش را ناامن می کند] تا وعده [قطعی و نهاییِ] خدا [که شکست کامل کافران و عذاب آخرت است] فرا رسد؛ بی تردید خدا خُلف وعده نمی کند.'),
(1739, 13, 32, '[فقط تو را مسخره نمی کنند] قطعاً پیامبرانی که پیش از تو بودند، مسخره شدند. پس به کافران مهلت دادم، سپس آنان را [به کیفر سخت] گرفتم، پس [یافتی که] مجازات و عذاب [من] چگونه بود؟ ….'),
(1740, 13, 33, 'آیا کسی که بر همه نفوس [جهانیان] با آنچه به دست آورده اند، مسلط و حاکم و نگهبان است [و همه تحولات وجود آنان را تا رساندنشان به سر منزل مقصود در اختیار دارد، مانند کسی است که مطلقاً این صفات را ندارد؟] ولی آنان [از روی جهل و نادانی] برای خدا شریکانی قرار داده اند! بگو: [ویژگی های] آن شریکان را نام ببرید [تا روشن شود که آیا شایسته شریک قرار دادن برای خدا هستند؟!] آیا خدا را از چیزی در روی زمین که آنها را شریک خود نمی شناسد، خبر می دهید؟ یا سخنی ظاهر و پوچ و بی منطق است [که بر زبان خود می رانید؟!] بلکه برای کافران نیرنگ و دروغشان آراسته شده، و از راه خدا بازداشته شده اند [تا آنجا که موجودات بی ارزش و بی اثر را شریکان حق پنداشته اند]؛ و هر که را خدا [به سبب از دست دادن لیاقتش] گمراه کند، او را هیچ هدایت کننده ای نخواهد بود. ….'),
(1741, 13, 34, 'برای آنان در زندگی دنیا عذابی است، و مسلماً عذاب آخرت سخت تر و پر مشقت تر است، و آنان را هیچ حافظ و نگه دارنده ای [از عذاب خدا] نیست.'),
(1742, 13, 35, 'صفت بهشتی که به پرهیزکاران وعده داده شده [چنین است که] از زیر [درختانِ] آن نهرها جاری است، میوه ها و سایه اش همیشگی است. این عاقبت و فرجام کسانی است که [در همه امور زندگی] پرهیزکاری کردند و فرجام کافران آتش است.'),
(1743, 13, 36, 'و کسانی که کتاب آسمانی به آنان عطا کردیم از آنچه بر تو نازل شده است، خوشحالند. و برخی از گروه ها [یِ اهل کتاب] بخشی از آن را منکرند، بگو: من فرمان یافته ام که خدای یکتا را بپرستم و شریکی برای او قرار ندهم، تنها به سوی او دعوت می کنم و بازگشتم فقط به سوی اوست.'),
(1744, 13, 37, 'و همان گونه [که پیش از این کتاب های آسمانی نازل کردیم] قرآن را فرمانی [گویا و] روشن نازل نمودیم، و اگر از هواها و تمایلات آنان پس از دانشی که [مانند قرآن] برایت آمده پیروی کنی، برای تو در برابر [عذاب] خدا هیچ یاور و نگه دارنده ای نخواهد بود.'),
(1745, 13, 38, 'و همانا پیش از تو پیامبرانی را فرستادیم، و برای آنان همسران و فرزندانی قرار دادیم. و هیچ پیامبری را نسزد که معجزه ای بیاورد مگر به فرمان خدا. برای هر زمانی برنامه مقدّر شده ای [ثبت] است.'),
(1746, 13, 39, 'خدا هر چه را بخواهد محو می کند و هر چه را بخواهد ثابت و پابرجا می نماید، و "امّ الکتاب "نزد اوست'),
(1747, 13, 40, 'و اگر پاره ای از عذاب ها را که به منکران وعده می دهیم به تو نشان دهیم [می بینی که بسیار سخت و دردناک است] یا اگر تو را [پیش از فرا رسیدن آن عذاب ها] قبض روح کنیم [در قیامت عذابشان را خواهی دید؛ در هر صورت] تنها وظیفه تو ابلاغ [وحی] است و حسابرسی بر عهده ماست.'),
(1748, 13, 41, 'آیا نمی دانند که ما همواره به زمین می پردازیم و از اطراف [و جوانب] آن [که ملت ها، اقوام، تمدن ها و دانشمندانش هستند] می کاهیم؟ و خدا حکم می کند؛ و هیچ بازدارنده ای برای حکمش نیست، و او در حسابرسی سریع است.'),
(1749, 13, 42, 'کسانی که پیش از این کافران لجوج و منکران عنود بودند [در برابر حکومت و حکم خدا] مکر ونیرنگ کردند [ولی به هدفشان نرسیدند] چرا که همه تدبیرها و نقشه ها در اختیار خداست؛ زیرا او هر کاری را هر کس انجام می دهد، می داند [به همین سبب مکر و نیرنگ هر کس را با تدبیر و نقشه حکیمانه اش باطل و نابود می کند] و کافران و منکران به زودی خواهند دانست که فرجامِ [نیک و بدِ] سرایِ دیگر برای کیست؟'),
(1750, 13, 43, 'کافران می گویند: تو فرستاده [خدا] نیستی. بگو: کافی است که خدا [با آیات محکم و استوار قرآنش] و کسی [چون امیرالمؤمنین علی بن ابی طالب] که دانش کتاب نزد اوست، میان من و شما [نسبت به پیامبری ام] گواه باشند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الر ـ [این] کتابی است که آن را بر تو نازل کردیم تا مردم را به اجازه پروردگارشان از تاریکی ها [یِ جهل، گمراهی و طغیان] به سوی روشناییِ [معرفت، عدالت و ایمان و در حقیقت] به سوی راه [خدایِ] توانای شکست ناپذیر و ستوده بیرون آوری.'),
(1752, 14, 2, 'خدایی که آنچه در آسمان ها و زمین است، در سیطره مالکیّت و فرمانروایی اوست؛ و وای بر کافران از عذابی سخت.'),
(1753, 14, 3, 'همانان که زندگی دنیا را بر آخرت ترجیح می دهند و [مردم را] از راه خدا باز می دارند و می خواهند آن را [با وسوسه و اغواگری] کج نشان دهند؛ اینان در گمراهی دوری هستند.'),
(1754, 14, 4, 'و ما هیچ پیامبری را جز به زبان قومش نفرستادیم تا [بتواند به وسیله آن زبان، پیام وحی را به روشنی] برای آنان بیان کند. پس خدا هر کس را بخواهد [به کیفر لجاجت و عنادش] گمراه می کند، و هر کس را بخواهد، هدایت می نماید، و او توانای شکست ناپذیر و حکیم است.'),
(1755, 14, 5, 'و همانا موسی را با نشانه های خود فرستادیم [و به او وحی کردیم] که قوم خود را از تاریکی ها به سوی روشنایی بیرون آور و روزهای خدا را [که روزهای رحمت، عذاب، پیروزی و شکست است] به آنان یادآوری کن، بی تردید در این روزهای خدا برای هر شکیبای سپاس گزاری نشانه هایی [از توحید، ربوبیّت و قدرت خدا] است.'),
(1756, 14, 6, 'و [یاد کن] زمانی را که موسی به قومش گفت: نعمت خدا را بر خودتان به یاد آورید، آن گاه که شما را از [چنگال] فرعونیان رهایی بخشید، [همانان] که پیوسته شما را شکنجه سخت می دادند، و پسرانتان را سر می بریدند، و زنانتان را [برای بیگاری] زنده می گذاشتند، و در این [حوادث] آزمایش بزرگی از سوی پروردگارتان بود.'),
(1757, 14, 7, 'و [نیز یاد کنید] هنگامی را که پروردگارتان اعلام کرد که اگر سپاس گزاری کنید، قطعاً [نعمتِ] خود را بر شما می افزایم، و اگر ناسپاسی کنید، بی تردید عذابم سخت است.'),
(1758, 14, 8, 'و موسی [به بنی اسرائیل] گفت: اگر شما و همه مردم روی زمین کافر شوید [زیانی به خدا نمی رسد]؛ زیرا خدا بی نیاز و ستوده است.'),
(1759, 14, 9, 'آیا خبر [پندآموز] کسانی که پیش از شما بودند، به شما نرسیده؟ [خبرِ] قوم نوح و عاد و ثمود و آنان که پس از ایشان بودند که جز خدا از آنان آگاه نیست، [همه آنان اقوامی بودند که] پیامبرانشان برای آنان دلایل روشن آوردند، ولی آنان دست هایشان را [به عنوان اعتراض، استهزا، تحقیر کردن و پاسخ ندادن به دعوت پیامبران] در دهان هایشان بردند و گفتند: مسلماً ما به آن رسالتی که شما به آن فرستاده شده اید، کافریم و نسبت به آنچه ما را به آن دعوت می کنید، به شدت در شک و تردیدیم!!'),
(1760, 14, 10, 'پیامبرانشان گفتند: آیا در خدا که آفریننده آسمان ها و زمین است، شکی هست؟ او شما را دعوت [به ایمان] می کند تا همه گناهانتان را بیامرزد، و شما را تا زمان معینِ [عمرتان] مهلت می دهد. پاسخ دادند: شما بشرهایی مانند ما هستید که می خواهید ما را از معبودهایی که پدرانمان می پرستیدند باز دارید؛ پس [شما] دلیل روشنی [بر اثبات رسالت خود که مورد پسند ما باشد] بیاورید.'),
(1761, 14, 11, 'پیامبرانشان به آنان گفتند: یقینی است که ما بشری مانند شما هستیم، ولی خدا به هر کس از بندگانش که بخواهد [با عطا کردن مقام نبوّت] منّت می نهد و ما را نسزد که جز به اجازه خدا معجزه ای برای شما بیاوریم، و باید مؤمنان فقط بر خدا توکل کنند.'),
(1762, 14, 12, 'و ما را چه عذر و بهانه ای است که بر خدا توکل نکنیم، در حالی که ما را به راه هایِ [خوشبختی و سعادت] مان هدایت کرد، و قطعاً بر آزاری که [در راه دعوت به توحید] از ناحیه شما به ما می رسد، شکیبایی می ورزیم، پس باید توکل کنندگان فقط بر خدا توکل کنند.'),
(1763, 14, 13, 'ولی کفرپیشگان به پیامبرانشان گفتند: مسلماً ما شما را از سرزمین خود بیرون خواهیم کرد، مگر اینکه هم کیش ما شوید. پس پروردگارشان به آنان وحی کرد: ما قطعاً ستمکاران را نابود می کنیم.'),
(1764, 14, 14, 'و یقیناً شما را پس از آنان در آن سرزمین ساکن خواهیم کرد. این [لطف و رحمت] ویژه کسی است که از مقام من بترسد، و از تهدید [به عذابم] بیم داشته باشد.'),
(1765, 14, 15, 'و پیامبران [از خدا] درخواست گشایش و پیروزی کردند، و هر سرکش منحرفی [از رسیدن به هدفش] نومید شد.'),
(1766, 14, 16, '[سرانجام] پیش روی او دوزخ است، و او را از آبی چرکین و متعفّن می نوشانند!!'),
(1767, 14, 17, 'آن را [به سختی و مشقت] جرعه جرعه می نوشد، [و به خواست خود حاضر به نوشیدن نیست، بلکه به زور و جبر در گلویش می ریزند،] و [او] نمی تواند آن را به [آسانی] فرو برد، و مرگ از هر طرف به او رو می کند، ولی مردنی نیست، و عذابی سخت و انبوه به دنبال اوست.'),
(1768, 14, 18, 'وصف حال کسانی که به پروردگارشان کافر شدند [این گونه است] اعمالشان مانند خاکستری است که در یک روز توفانی، تند بادی بر آن بوزد [و آن را به صورتی که هرگز نتوان جمع کرد، پراکنده کند] آنان نمی توانند از اعمال خیری که انجام داده اند، چیزی [برای ارائه به بازار قیامت جهت کسب ثواب و پاداش] به دست آورند؛ این است آن گمراهی دور.'),
(1769, 14, 19, '[ای انسان!] آیا [به طور قطع و یقین] ندانسته ای که خدا آسمان ها و زمین را بر پایه درستی و راستی و نظم و حساب آفرید؟ اگر بخواهد شما را از میان می برد، و خلقی جدید می آورد.'),
(1770, 14, 20, 'و این [کار] بر خدا دشوار و گران نیست.'),
(1771, 14, 21, 'و [در عرصه قیامت] همگی در پیشگاه خدا ظاهر می شوند؛ پس ناتوانان [ی که بدون به کار گرفتن عقل، بلکه از روی تقلید کورکورانه پیرو مستکبران بودند] به مستکبران می گویند: ما [در دنیا بدون درخواست دلیل و برهان] پیروِ [مکتب] شما بودیم، آیا امروز چیزی از عذاب خدا را [به پاداش آنکه از شما پیروی کردیم] از ما برطرف می کنید؟ می گویند: اگر خدا ما را [در صورت داشتن لیاقت] هدایت کرده بود، همانا ما هم شما را هدایت می کردیم، [اکنون همه سرمایه های وجودی ما و تلاش و کوششمان بر باد رفته] چه بیتابی کنیم وچه شکیبایی ورزیم، برای ما یکسان است، ما را هیچ راه گریزی نیست.'),
(1772, 14, 22, 'و شیطان [در قیامت] هنگامی که کار [محاسبه بندگان] پایان یافته [به پیروانش] می گوید: یقیناً خدا [نسبت به برپایی قیامت، حساب بندگان، پاداش و عذاب] به شما وعده حق داد، و من به شما وعده دادم [که آنچه خدا وعده داده، دروغ است، ولی می بینید که وعده خدا تحقّق یافت] و [من] در وعده ام نسبت به شما وفا نکردم، مرا بر شما هیچ غلبه و تسلّطی نبود، فقط شما را دعوت کردم [به دعوتی دروغ و بی پایه] و شما هم [بدون اندیشه و دقت دعوتم را] پذیرفتید، پس سرزنشم نکنید، بلکه خود را سرزنش کنید، نه من فریادرس شمایم، و نه شما فریادرس من، بی تردید من نسبت به شرک‌ورزی شما که در دنیا درباره من داشتید [که اطاعت از من را هم چون اطاعت خدا قرار دادید] بیزار و منکرم؛ یقیناً برای ستمکاران عذابی دردناک است.'),
(1773, 14, 23, 'و کسانی را که ایمان آوردند و کارهای شایسته انجام دادند، به بهشت هایی درآورند، که از زیرِ [درختانِ] آن نهرها جاری است، در آنجا به اجازه پروردگارشان جاودانه اند، و در آنجا درود [خدا و فرشتگان] بر آنان، سلام است.'),
(1774, 14, 24, 'آیا ندانستی که خدا چگونه مثلی زده است؟ کلمه پاک [که اعتقاد واقعی به توحید است] مانند درخت پاک است، ریشه اش استوار و پابرجا و شاخه اش در آسمان است.'),
(1775, 14, 25, 'میوه اش را به اجازه پروردگارش در هر زمانی می دهد. و خدا مَثَل ها را برای مردم می زند تا متذکّر حقایق شوند.'),
(1776, 14, 26, 'و مَثَل کلمه ناپاک [که عقاید باطل و بی پایه است] مانند درخت ناپاک است که از زمین ریشه کن شده و هیچ قرار و ثباتی ندارد.'),
(1777, 14, 27, 'خدا مؤمنان را به سبب اعتقاد و ایمانشان در زندگی دنیا و آخرت ثابت قدم و پابرجا می دارد، و خدا ستمکاران [به آیاتش] را [به علت لجاجت و عنادشان] گمراه می کند، و خدا هر چه بخواهد [بر اساس حکمتش] انجام می دهد.'),
(1778, 14, 28, 'آیا کسانی را که [شکر] نعمت خدا را به کفران و ناسپاسی تبدیل کردند و قوم خود را به سرای نابودی و هلاکت درآوردند، ندیدی؟'),
(1779, 14, 29, '[سرای نابودی و هلاکت، همان] دوزخی است که در آن وارد می شوند، و بد قرارگاهی است.'),
(1780, 14, 30, 'و برای خدا همتایانی قرار دادند تا مردم را از راه او گمراه کنند؛ بگو: [چند روزی از زندگی زودگذر دنیا] برخوردار شوید، ولی یقیناً بازگشت شما به سوی آتش است.'),
(1781, 14, 31, 'به بندگان مؤمنم بگو: نماز را بر پا دارند، و از آنچه روزی آنان کرده ایم، پنهان و آشکار، انفاق کنند، پیش از آنکه روزی فرا رسد که در آن نه داد و ستدی است و نه پیوند دوستی و رفاقت.'),
(1782, 14, 32, 'خداست که آسمان ها و زمین را آفرید، و از آسمان آبی نازل کرد، و برای شما به وسیله آن از محصولات و میوه های گوناگون روزی بیرون آورد، و کِشتی ها را مسخّر شما قرار داد تا به فرمان او در دریا روان شوند، و نیز نهرها را مسخّر شما کرد.'),
(1783, 14, 33, 'و خورشید و ماه را که همواره با برنامه ای حساب شده در کارند، رام شما نمود و شب و روز را نیز مسخّر شما ساخت.'),
(1784, 14, 34, 'و از هر چیزی که [به سبب نیازتان به آن] از او خواستید، به شما عطا کرد. و اگر نعمت های خدا را شماره کنید، هرگز نمی توانید آنها را به شماره آورید. مسلماً انسان بسیار ستمکار و ناسپاس است.'),
(1785, 14, 35, 'و [یاد کن] هنگامی را که ابراهیم گفت: پروردگارا! این شهر [مکه] را منطقه ای امن قرار ده و من و فرزندانم را از پرستش بت ها دور بدار.'),
(1786, 14, 36, 'پروردگارا! آن [بت] ها بسیاری از مردم را گمراه کردند، پس هر کسی از من [که یکتاپرست و حق گرایم] پیروی کند، یقیناً از من است، و هر کس از من نافرمانی کند [شایسته شدنش برای آمرزش و رحمت بسته به عنایت توست] زیرا تو بسیار آمرزنده و مهربانی.'),
(1787, 14, 37, 'پروردگارا! من برخی از فرزندانم را در درّه ای بی کشت و زرع نزد خانه محترمت سکونت دادم؛ پروردگارا! برای اینکه نماز را بر پا دارند؛ پس دل های گروهی از مردم را به سوی آنان علاقمند و متمایل کن، و آنان را از انواع محصولات و میوه ها روزی بخش، باشد که سپاس گزاری کنند.'),
(1788, 14, 38, 'پروردگارا! یقیناً تو آنچه را ما پنهان می داریم و آنچه را آشکار می کنیم، می دانی و هیچ چیز در زمین و آسمان بر خدا پنهان نیست.'),
(1789, 14, 39, 'همه ستایش ها ویژه خدایی است که اسماعیل واسحاق را در سنّ پیری به من بخشید؛ یقیناً پروردگارم شنونده دعاست.'),
(1790, 14, 40, 'پروردگارا! مرا بر پادارنده نماز قرار ده، و نیز از فرزندانم [برپادارندگان نماز قرار ده]. و پروردگارا! دعایم را بپذیر.'),
(1791, 14, 41, 'پروردگارا! روزی که حساب برپا می شود، مرا و پدر و مادرم و مؤمنان را بیامرز.'),
(1792, 14, 42, 'و خدا را از آنچه ستمکاران انجام می دهند، بی خبر مپندار؛ مسلماً [کیفر] آنان را برای روزی که چشم ها در آن خیره می شود، به تأخیر می اندازد.'),
(1793, 14, 43, '[ترسان به سوی دادگاه قیامت] شتابانند، سرهایشان را بالا گرفته [و دیدگانشان ذلیلانه به برنامه های محشر دوخته شده] تا جایی که پلک هایشان به هم نمی خورد، و دل هایشان [از بیم عذاب فرو ریخته و از تدبیر و چاره جویی] تهی است.'),
(1794, 14, 44, 'و مردم را از روزی که عذاب به سویشان می آید، هشدار ده. پس کسانی که ستم ورزیده اند، می گویند: پروردگارا! ما را تا [سرآمدی نزدیک؟ و] مدتی کوتاه مهلت ده تا دعوتت را اجابت کنیم، و از پیامبرانت پیروی نماییم. [ولی به آنان گویند:] شما نبودید که پیش از این سوگند یاد می کردید که هرگز برای شما زوال و فنایی نیست؟!'),
(1795, 14, 45, 'و در مساکن کسانی که به خود ستم کردند، ساکن شدید، در صورتی که برای شما روشن و آشکار است که ما با آنان [به سبب ستم هایشان] چه کردیم و برای شما مثال ها [یِ پندآموزی از جامعه هایی که به وسیله عذاب نابود شدند] ذکر کردیم.'),
(1796, 14, 46, 'و آنان [نهایت] نیرنگشان را [بر ضدِّ خدا و پیامبران] به کار گرفتند، و [کیفر عقوبت دنیایی و آخرتی] نیرنگشان نزد خداست و هر چند که از نیرنگشان کوه ها از جا کنده شود.'),
(1797, 14, 47, 'پس مپندار که خدا در وعده اش با پیامبرانش وفا نمی کند؛ زیرا خدا توانای شکست ناپذیر و صاحب انتقام است.'),
(1798, 14, 48, '[در] روزی که زمین به غیر این زمین، و آسمان ها [به غیر این آسمان ها] تبدیل شود، و [همه] در پیشگاه خدای یگانه قهّار حاضر شوند.'),
(1799, 14, 49, 'و آن روز مجرمان را می بینی که در زنجیرها [به صورتی محکم و سخت] به هم بسته شده اند.'),
(1800, 14, 50, 'پیراهن هایشان از قطران [ماده ای متعفّن، قابل اشتعال و بدبو] است، و آتش چهره هایشان را می پوشاند.'),
(1801, 14, 51, 'تا [به این کیفیت] خدا هر کس را [به سبب] آنچه انجام داده کیفر دهد؛ یقیناً خدا حسابرسی سریع است.'),
(1802, 14, 52, 'این [قرآن یا آنچه در این سوره است] پیامی برای [همه] مردم است، برای آنکه به وسیله آن هشدار داده شوند، و [با تدبّر در آیاتش] بدانند که او معبودی یگانه و یکتاست، و تا خردمندان، متذکّر [حقایق و معارف الهیّه] شوند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الر ـ این [آیات بلندمرتبه] آیات کتاب الهی و آیات قرآنِ روشنگر [با عظمت] است.'),
(1804, 15, 2, 'کافران [هنگام روبرو شدن با عذاب] چه بسا آرزو می کنند که کاش تسلیم [فرمان های خدا] بودند.'),
(1805, 15, 3, 'بگذارشان تا بخورند و [با لذایذ مادی و زودگذر] کامرانی کنند، و آرزوها، سرگرمشان نماید؛ سپس [حقّانیّت اسلام و فرجام شوم خود را] خواهند فهمید.'),
(1806, 15, 4, 'و هیچ شهری را [به خاطر فساد فراگیرش] نابود نکردیم مگر اینکه برای آن سرنوشتی معین [وروزگار ودوره ای تغییرناپذیر] بود.'),
(1807, 15, 5, 'هیچ ملتی از اجل معین خود نه پیش می افتد و نه پس می ماند.'),
(1808, 15, 6, 'و گفتند: ای کسی که قرآن بر او نازل شده! قطعاً تو دیوانه ای!'),
(1809, 15, 7, 'اگر [درباره پیامبری ات] راست می گویی، چرا فرشتگان را نزد ما نمی آوری؟!'),
(1810, 15, 8, '[اینان بدانند که] ما فرشتگان را جز به درستی و راستی نازل نمی کنیم، و در آن هنگام [که نازل شوند، این منکران لجوج از دچار شدن به عذاب] مهلت نمی یابند.'),
(1811, 15, 9, 'همانا ما قرآن را نازل کردیم، و یقیناً ما نگهبان آن [از تحریف و زوال] هستیم.'),
(1812, 15, 10, 'و بی تردید ما پیش از تو هم پیامبرانی را در امت های پیشین فرستادیم.'),
(1813, 15, 11, 'و هیچ پیامبری به سوی آنان نمی آمد مگر آنکه او را مسخره می کردند.'),
(1814, 15, 12, 'ما این گونه [که قابل فهمیدن باشد] قرآن را وارد قلوب بدکاران می کنیم.'),
(1815, 15, 13, '[با این همه] به آن ایمان نمی آورند، و البته روش پیشینیان هم [در مسخره کردن آیات خدا و استهزای پیامبران] به همین صورت بوده است.'),
(1816, 15, 14, 'و اگر [برای دریافت حقایق ومعارف] دری از آسمان به روی آنان بگشاییم، که همواره از آن بالا روند.'),
(1817, 15, 15, 'باز خواهند گفت: یقیناً ما چشم بندی شده ایم، بلکه گروهی جادو شده هستیم.'),
(1818, 15, 16, 'به راستی که ما در آسمان، برج هایی قرار دادیم و آن را برای بینندگان [به شکل صورت های فلکی] آراستیم.'),
(1819, 15, 17, 'و آن را از هر شیطان رانده شده ای حفظ کردیم.'),
(1820, 15, 18, 'مگر آنکه دزدانه [خبرهای عالم بالا را] بشنود، که شهابی روشن او را دنبال می کند.'),
(1821, 15, 19, 'و زمین را گستراندیم و در آن کوه های استوار افکندیم، واز هر گیاه موزون وسنجیده ای در آن رویاندیم.'),
(1822, 15, 20, 'و در آن برای شما و کسانی که روزی دهنده آنان نیستید، انواع وسایل و ابزار معیشت قرار دادیم.'),
(1823, 15, 21, 'و هیچ چیزی نیست مگر آنکه خزانه هایش نزد ماست، و آن را جز به اندازه معین نازل نمی کنیم.'),
(1824, 15, 22, 'و بادها را باردار کننده فرستادیم، و از آسمان آبی نازل کردیم و شما را با آن سیراب ساختیم و شما ذخیره کننده آن نیستید.'),
(1825, 15, 23, 'و یقیناً ماییم که حیات می دهیم، و می میرانیم و ما وارث [جهان و جهانیان] هستیم.'),
(1826, 15, 24, 'و بی تردید [حالات، اعمال و شمار] پیشینیانِ شما و آیندگانتان را می دانیم.'),
(1827, 15, 25, 'و مسلماً پروردگار توست که محشورشان می کند؛ زیرا او حکیم و داناست.'),
(1828, 15, 26, 'و ما انسان را از گِلی خشک که برگرفته از لجنی متعفّن و تیره رنگ است، آفریدیم.'),
(1829, 15, 27, 'و جن را پیش از آن از آتشی سوزان و بی دود پدید آوردیم.'),
(1830, 15, 28, 'و [یاد کن] هنگامی را که پروردگارت به فرشتگان گفت: من بشری از گِل خشک که برگرفته از لجنی متعفّن و تیره رنگ است، می آفرینم.'),
(1831, 15, 29, 'پس چون او را درست و نیکو گردانم و از روح خود در او بدمم، برای او سجده کنان بیفتید.'),
(1832, 15, 30, 'پس همه فرشتگان بدون استثناء سجده کردند.'),
(1833, 15, 31, 'مگر ابلیس که از اینکه با سجده کنان باشد، امتناع کرد.'),
(1834, 15, 32, '[خدا] گفت: ای ابلیس! تو را چه شده که با سجده کنان نیستی؟'),
(1835, 15, 33, 'گفت: من آن نیستم که برای بشری که او را از گلی خشک و برگرفته از لجنی متعفّن و تیره رنگ آفریدی، سجده کنم!!'),
(1836, 15, 34, '[خدا] گفت: از این [جایگاه والا که مقام مقربان است] بیرون رو که رانده شده ای،'),
(1837, 15, 35, 'و بی تردید تا روز قیامت لعنت بر تو خواهد بود.'),
(1838, 15, 36, 'گفت: پروردگارا! پس مرا تا روزی که [همگان] برانگیخته می شوند، مهلت ده.'),
(1839, 15, 37, '[خدا] گفت: تو از مهلت یافتگانی،'),
(1840, 15, 38, 'تا روز [آن] وقت معین.'),
(1841, 15, 39, 'گفت: پروردگارا! به سبب اینکه مرا گمراه نمودی، من هم یقیناً [همه کارهای زشت را] در زمین برای آنان می آرایم [تا ارتکاب زشتی ها برای آنان آسان شود] و مسلماً همه را گمراه می کنم.'),
(1842, 15, 40, 'مگر [آن] بندگانت را که خالص شدگانِ [از هر نوع آلودگی ظاهری و باطنی] اند.'),
(1843, 15, 41, 'خدا فرمود: این [پیراسته شدن از هر ناخالصی] راهی است مستقیم [که تحقّقش در وجود بندگان مخلصم] برعهده من [است.]'),
(1844, 15, 42, 'قطعاً تو را بر بندگانم تسلّطی نیست، مگر بر گمراهانی که از تو پیروی می کنند.'),
(1845, 15, 43, 'و مسلماً دوزخ، وعده گاه همگی آنان است.'),
(1846, 15, 44, 'برای آن هفت در است، برای هر دری گروهی از پیروان شیطان تقسیم شده اند.'),
(1847, 15, 45, 'به یقین، پرهیزکاران در بهشت ها و چشمه سارها هستند.'),
(1848, 15, 46, '[به آنان گویند:] با سلامت و امنیت وارد آنجا شوید.'),
(1849, 15, 47, 'و آنچه از دشمنی و کینه در سینه هایشان بوده برکنده ایم که برادروار بر تخت هایی روبروی یکدیگرند.'),
(1850, 15, 48, 'در آنجا خستگی و رنجی به آنان نمی رسد و هیچ گاه از آنجا اخراج نمی شوند.'),
(1851, 15, 49, 'به بندگانم خبر ده که یقیناً من [نسبت به مؤمنان] بسیار آمرزنده و مهربانم.'),
(1852, 15, 50, 'و اینکه عذابم [برای مجرمان] همان عذاب دردناک است.'),
(1853, 15, 51, 'و نیز آنان را از مهمانان ابراهیم خبر ده.'),
(1854, 15, 52, 'هنگامی که بر او وارد شدند، پس سلام گفتند. [ابراهیم] گفت: ما از شما ترسانیم.'),
(1855, 15, 53, 'گفتند: نترس که ما تو را به پسری دانا مژده می دهیم.'),
(1856, 15, 54, 'گفت: آیا با آنکه پیری به من رسید، مژده ام می دهید؟ به چه مژده می دهید؟'),
(1857, 15, 55, 'گفتند: تو را به بشارتی درست و به حق [که واقع شدنی است] مژده دادیم؛ بنابراین از ناامیدان مباش.'),
(1858, 15, 56, 'گفت: چه کسی جز گمراهان از رحمت پروردگارش ناامید می شود؟!'),
(1859, 15, 57, '[سپس] گفت: ای فرستادگان! کار مهم شما چیست؟'),
(1860, 15, 58, 'گفتند: ما به سوی قومی گنهکار فرستاده شده ایم [تا نابودشان کنیم.]'),
(1861, 15, 59, 'مگر خاندان لوط را که قطعاً همه آنان را نجات می دهیم،'),
(1862, 15, 60, 'مگر همسرش را که [به سبب روی گردانی اش از حق] مقدّر کرده ایم از بازماندگان [در شهر و نابودشوندگان] باشد.'),
(1863, 15, 61, 'پس هنگامی که فرستادگان خدا نزد خاندان لوط آمدند،'),
(1864, 15, 62, 'لوط گفت: بی تردید شما گروهی ناشناخته اید.'),
(1865, 15, 63, 'گفتند: [نه، ما آشنا هستیم] با چیزی نزد تو آمده ایم که قومت همواره در آن تردید داشتند.'),
(1866, 15, 64, 'و ما واقعیتی قطعی و مسلّم را [که همان عذاب الهی است، جهت نابودی مردم] برای تو آورده ایم و یقیناً راستگوییم.'),
(1867, 15, 65, 'پس [چون] پاره ای از شب [گذشت] خاندانت را کوچ ده و خودت دنبال آنان برو و هیچ یک از شما [به شهر] بازنگردد، و آنجا که مأمور هستید، بروید.'),
(1868, 15, 66, 'ما [کیفیت] این حادثه [بزرگ] را به او وحی کردیم که هنگامی که مجرمان وارد صبح شوند، بنیادشان برکنده خواهد شد.'),
(1869, 15, 67, 'و اهل شهر [با آگاه شدن از مهمانان لوط] شادی کنان [به خانه لوط] آمدند.'),
(1870, 15, 68, 'لوط گفت: اینان مهمانان منند، آبروی مرا نبرید.'),
(1871, 15, 69, 'از خدا پروا کنید و مرا [نزد مهمانانم] خوار و بی مقدار نسازید.'),
(1872, 15, 70, 'گفتند: مگر ما تو را [از مهمان کردن] مردمان نهی نکردیم؟'),
(1873, 15, 71, '[لوط] گفت: اگر می خواهید [کار درست و معقولی] انجام دهید، اینان دختران منند [که برای ازدواج مناسبند.]'),
(1874, 15, 72, '[ای پیامبر!] به جان تو سوگند، آنان در مستی خود فرو رفته و سرگردان بودند.'),
(1875, 15, 73, 'پس به هنگام طلوع آفتاب، صدایی [مرگبار و وحشتناک و غرّشی سهمگین] آنان را فرا گرفت.'),
(1876, 15, 74, 'در نتیجه آن شهر را زیر و رو کردیم و بر آنان سنگ هایی از جنس سنگِ گل باراندیم.'),
(1877, 15, 75, 'قطعاً در این [سرگذشت اَسفبار و حادثه عبرت آموز] نشانه هایی [از قدرت خدا و خواری و رسوایی مجرمان] برای هوشمندان [که جستجوگر علل حوادث اند] وجود دارد.'),
(1878, 15, 76, 'و آن [آثار و بقایای شهر ویران شده قوم لوط] بر سر راهی است که پابرجاست.'),
(1879, 15, 77, 'مسلماً در این [شهر ویران شده] برای مؤمنان نشانه ای [پندآموز] است.'),
(1880, 15, 78, 'و بی تردید اهل ایکه [قوم شعیب] ستمکار بودند.'),
(1881, 15, 79, 'در نتیجه از آنان انتقام گرفتیم، و [آثار] دو شهر نابود شده [قوم لوط و شعیب] بر سر جاده ای آشکار قرار دارد.'),
(1882, 15, 80, 'و به راستی اهل [دیار] حجر [که سرزمینی میان مدینه و شام است] پیامبران را تکذیب کردند.'),
(1883, 15, 81, 'و ما آیات و نشانه های خود را به آنان نشان دادیم، ولی از آنان روی گردان شدند.'),
(1884, 15, 82, 'و همواره از کوه ها خانه ها می تراشیدند در حالی که [به خیال خود به سبب استحکام آن خانه ها] ایمن [از حوادث] بودند.'),
(1885, 15, 83, 'پس هنگامی که به صبح درآمدند، صدایی [مرگبار و وحشتناک و غرّشی سهمگین] آنان را فرا گرفت.'),
(1886, 15, 84, 'و آنچه [از بناهای محکم و استواری که] فراهم می آوردند، عذاب خدا را از آنان دفع نکرد.'),
(1887, 15, 85, 'و آسمان ها و زمین و آنچه را میان آن دوتاست، جز به حق نیافریدیم، و بی تردید قیامت آمدنی است؛ پس [در برابر ناهنجاری های مردم] گذشتی کریمانه داشته باش.'),
(1888, 15, 86, 'یقیناً پروردگارت همان آفریننده داناست.'),
(1889, 15, 87, 'و به راستی که هفت آیه از مثانی [یعنی سوره حمد] و قرآن بزرگ را به تو عطا کردیم.'),
(1890, 15, 88, 'بنابراین به امکانات مادی [و ثروت و اولادی] که برخی از گروه های آنان را از آن برخوردار کردیم، چشم مدوز، و بر آنان [به سبب اینکه پذیرای حق نیستند] اندوه مخور، وپر و بال [لطف و مهربانیِ] خود را برای مؤمنان فرو گیر.'),
(1891, 15, 89, 'و [به اخلال گران در امر دین] بگو: بی تردید من بیم دهنده آشکارم.'),
(1892, 15, 90, '[عذابی به سوی شما می فرستیم] همان گونه که بر تفرقه افکنان [در دین] فرستادیم.'),
(1893, 15, 91, 'همانان که قرآن را بخش بخش کردند [بخشی را پذیرفتند و از پذیرفتن بخشی دیگر روی گرداندند.]'),
(1894, 15, 92, 'به پروردگارت سوگند، قطعاً از همه آنان بازخواست می کنیم.'),
(1895, 15, 93, 'از اعمالی که همواره انجام می داده اند.'),
(1896, 15, 94, 'پس آنچه را به آن مأموری اظهار کن و از مشرکان روی بگردان.'),
(1897, 15, 95, 'که ما [شرّ] استهزا کنندگان را از تو بازداشته ایم.'),
(1898, 15, 96, 'همانان که با خدا معبود دیگری قرار می دهند، پس خواهند دانست [که مرتکب چه خطای بزرگی شده اند و مستحقّ چه عذابی هستند.]'),
(1899, 15, 97, 'ما می دانیم که تو از آنچه [مشرکان] می گویند، دلتنگ می شوی.'),
(1900, 15, 98, 'پس [برای دفع دلتنگی] پروردگارت را همراه با سپاس و ستایش تسبیح گوی و از سجده کنان باش.'),
(1901, 15, 99, 'و پروردگارت را تا هنگامی که تو را مرگ بیاید، بندگی کن.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'فرمان حتمی خدا [در مورد عذاب کافران ومشرکان] فرا رسیده است، پس خواستار شتاب در آن نباشید؛ او منزّه وبرتر است از آنچه شریک او قرار می دهند.'),
(1903, 16, 2, 'فرشتگان را به فرمان خود همراه با روح به هر کس از بندگانش که بخواهد [برای برگزیدنش به پیامبری] نازل می کند [و به آنان فرمان می دهد] که مردم را هشدار دهید که معبودی جز من نیست؛ بنابراین از من پروا کنید.'),
(1904, 16, 3, 'آسمان ها و زمین را به حق آفرید. از آنچه شریک او قرار می دهند، برتر است.'),
(1905, 16, 4, 'انسان را از آبی اندک و بی ارزش آفرید، پس آن گاه [و به دور از انتظار] ستیزه جویی آشکار است.'),
(1906, 16, 5, 'ودام ها را که برای شما در آنها [وسیله] گرمی و سودهاست آفرید، و از [محصولات لبنی و گوشتی] شان می خورید.'),
(1907, 16, 6, 'و برای شما در آنها زیبایی [و شکوه] است، آن گاه که [آنها را] شبان گاه از چراگاه بر می گردانید و هنگامی که صبح گاهان به چراگاه می برید.'),
(1908, 16, 7, 'و بارهای سنگین شما را تا شهری که جز با دشواری و مشقت به آن نمی رسید، حمل می کنند؛ یقیناً پروردگارتان رؤوف و بسیار مهربان است.'),
(1909, 16, 8, 'و اسب ها و استرها و الاغ ها را آفرید تا بر آنها سوار شوید و [برای شما] تجمل و زینت باشد، و چیزهایی [در آینده جز این وسایل نقلیه حیوانی] به وجود می آورد که شما نمی دانید.'),
(1910, 16, 9, 'و قرار دادن راه راست [برای هدایت بندگان به سوی سعادت ابدی] فقط برعهده خداست، و برخی از این راه ها کج و منحرف است [که حرکت در آن شما را از سعادت ابدی محروم می کند]، و اگر خدا می خواست همه شما را [به طور اجبار] به راه راست هدایت می کرد.'),
(1911, 16, 10, 'اوست که از آسمان، آبی برای شما نازل کرد که بخشی از آن نوشیدنی است و از بخشی از آن درخت و گیاه می روید که [دام هایتان] را در آن می چرانید.'),
(1912, 16, 11, 'برای شما به وسیله آن آب، زراعت و زیتون و خرما و انگور و از همه محصولات می رویاند؛ یقیناً در این [واقعیات شگفت انگیز طبیعی] نشانه ای است [بر توحید، و ربوبیّت وقدرت خدا] برای گروهی که می اندیشند.'),
(1913, 16, 12, 'و شب و روز و خورشید و ماه را نیز رام و مسخّر شما قرار داد، وستارگان هم به فرمانش رام و مسخّر شده اند؛ قطعاً در این [حقایق] نشانه هایی است [بر توحید، ربوبیّت و قدرت خدا] برای گروهی که تعقّل می کنند.'),
(1914, 16, 13, 'و نیز آنچه را که در زمین به رنگ های گوناگون برای شما آفرید [رام و مسخّرتان ساخت]؛ مسلماً در این [امور] نشانه ای است [بر توحید، ربوبیّت و قدرت خدا] برای گروهی که متذکّر می شوند.'),
(1915, 16, 14, 'و اوست که دریا را رام و مسخّر کرد تا از آن گوشت تازه بخورید، و زینتی را که می پوشید از آن بیرون آورید و در آن کشتی ها را می بینی که آب را می شکافند [تا شما را برای حمل کالا، تجارت و داد و ستد جابجا کنند] و برای اینکه از فضل واحسان خدا طلب کنید و [در نهایت] به سپاس گزاری خدا برخیزید.'),
(1916, 16, 15, 'و در زمین کوه هایی استوار افکند تا شما را [در حال گردش وضعی و انتقالی] نلرزاند، و نهرها و راه هایی را [پدید آورد] تا [برای رسیدن به اهداف و مقاصد خود] راه یابید.'),
(1917, 16, 16, 'و [برای اینکه در عبور از کوه ها و بیابان ها و کویرها سرگردان و گم نشوید] نشانه ها [قرار داد]، و [مردم به هنگام شب در دل بیابان ها و بر سطح دریاها] به وسیله ستارگان راهیابی می کنند.'),
(1918, 16, 17, 'بر این اساس آیا کسی که [همه چیز] می آفریند، مانند کسی است که [هیچ چیز] نمی آفریند؟ [پس کُرنش در برابر بتان بر پایه چه دلیلی است؟] آیا پند نمی گیرید؟'),
(1919, 16, 18, 'و اگر نعمت های خدا را شماره کنید، هرگز نمی توانید آنها را به شمار آورید؛ یقیناً خدا بسیار آمرزنده و مهربان است.'),
(1920, 16, 19, 'و خدا آنچه را پنهان می کنید و آنچه را آشکار می سازید، می داند.'),
(1921, 16, 20, 'و معبودهایی را که به جای خدا می پرستند [نه اینکه] چیزی را نمی آفرینند، بلکه خودشان آفریده می شوند.'),
(1922, 16, 21, 'مُردگانند نه زندگان، و نمی دانند چه زمانی برانگیخته می شوند.'),
(1923, 16, 22, 'معبود شما معبودی یگانه است؛ پس کسانی که به آخرت ایمان نمی آورند، دل هایشان [حق را] انکار می کند، و آنان مستکبرند.'),
(1924, 16, 23, 'ثابت و یقینی است که خدا آنچه را پنهان می کنند و آنچه را آشکار می نمایند، می داند؛ قطعاً او مستکبران را دوست ندارد.'),
(1925, 16, 24, 'و هنگامی که به آنان گویند: پروردگارتان چه چیزهایی نازل کرده؟ می گویند: [چیزی نازل نکرده، آنچه به عنوان قرآن در دسترس مردم است، همان] افسانه های [دروغین] پیشینیان است!!'),
(1926, 16, 25, 'سرانجام روز قیامت بار گناهانشان را به طور کامل و بخشی از بار گناهان کسانی که از روی بی دانشی گمراهشان می کنند، به دوش می کشند. آگاه باشید! بد باری است که به دوش می کشند.'),
(1927, 16, 26, 'همانا کسانی که پیش از اینان بودند [به همین صورت] نیرنگ زدند [تا حق را نابود کنند]، پس خدا بنیانشان را از پایه و اساس ویران کرد و سقف [خانه هایشان را] از بالای سرشان بر آنان فرو ریخت، و از جایی که پی نبردند، عذاب به سویشان آمد.'),
(1928, 16, 27, 'آن گاه روز قیامت رسوایشان می کند و می گوید: [معبودانی که گمان می بردید] شریکان من [در ربوبیّت هستند] که برای آنها [با پیامبران و اهل ایمان] دشمنی و ستیز می کردید، کجایند؟ [آنان پاسخی ندارند، ولی] کسانی که به آنان دانش و بصیرت داده شده می گویند: امروز رسوایی و عذاب بر کافران است.'),
(1929, 16, 28, 'همانان که فرشتگان جانشان را در حالی که ستمکار بر خود بوده اند، می گیرند؛ پس [در آن موقعیت] از در تسلیم در آیند [و گویند:] ما هیچ کار بدی انجام نمی دادیم. [به آنان گویند: یقیناً انجام می دادید] ومسلماً خدا به آنچه همواره انجام می دادید، داناست.'),
(1930, 16, 29, 'بنابراین از درهای دوزخ وارد شوید در حالی که در آن جاودانه اید؛ و بد جایی است جایگاه مستکبران.'),
(1931, 16, 30, 'و هنگامی که به پرهیزکاران گویند: پروردگارتان چه چیزی نازل کرد؟ گویند: خیر را [که قرآن هدایت گر است و سراسر آیاتش وحی الهی است، نازل کرد]. برای کسانی که نیکی کردند [پاداش] نیکویی در این دنیاست، وهمانا سرای آخرت بهتر است، و سرای پرهیزکاران چه نیکوست.'),
(1932, 16, 31, '[سرای پرهیزکاران] بهشت های جاویدانی [است] که وارد آنها می شوند، از زیرِ [درختانِ] آنها نهرها جاری است، در آنجا هر چه بخواهند برای آنان فراهم است؛ خدا پرهیزکاران را این گونه پاداش می دهد.'),
(1933, 16, 32, 'آنان در حالی که [از آلودگی های عملی و اخلاقی] پاک و پاکیزه اند، فرشتگانْ جانشان را می گیرند، به آنان می گویند: سلام بر شما، [اکنون] به پاداش آنچه همواره انجام می دادید، به بهشت درآیید.'),
(1934, 16, 33, 'آیا [کافران و مشرکان] جز اینکه فرشتگانِ [قبض کننده ارواح] به سویشان آیند، یا فرمان پروردگارت [در مورد عذابشان] در رسد، انتظار می برند؟ کسانی هم که پیش از آنان بودند [در برابر حق] چنین کردند، و خدا [در عذاب کردنشان] به آنان ستم نورزید، بلکه آنان همواره [با مرتکب شدن انواع گناهان] به خودشان ستم می کردند.'),
(1935, 16, 34, 'پس [کیفر] بدی هایی که انجام دادند به آنان رسید، و عذابی که همواره مسخره می کردند، بر آنان نازل شد.'),
(1936, 16, 35, 'و کسانی که [به خدا] شرک ورزیدند [از روی جهل، و بی خردی و بدون دلیل و برهان] گفتند: اگر خدا می خواست نه ما و نه پدرانمان هیچ چیزی را جز او نمی پرستیدیم، و هیچ چیزی را بدون [حکم و فرمان] او حرام نمی کردیم. کسانی هم پیش از اینان بودند [در برابر حق] چنین کردند، پس آیا بر عهده پیامبران جز رساندن آشکار [پیام وحی، وظیفه ای] هست؟'),
(1937, 16, 36, 'و همانا در هر امتی پیامبری فرستادیم که [اعلام کند که] خدا را بپرستید و از [پرستش] طاغوت بپرهیزید. پس خدا گروهی از آنان را [که لیاقت نشان دادند] هدایت کرد و گروهی [دیگر را به سبب تکبّر و عنادشان] گمراهی بر آنان مقرّر شد. پس در زمین بگردید و با تأمل بنگرید که سرانجام تکذیب کنندگان چگونه بود.'),
(1938, 16, 37, 'هر چند بر هدایتشان حریص باشی [هدایت نمی یابند]؛ زیرا خدا کسانی را که [به سبب تکبّر و عنادشان] گمراه می کند، هدایت نخواهد کرد، و برای آنان هیچ یاوری [که از گمراهی نجاتشان دهد] وجود ندارد.'),
(1939, 16, 38, 'با سخت ترین سوگندهایشان به خدا سوگند یاد کردند که خدا کسانی را که می میرند، برنمی انگیزد!! آری، [با قدرت بی نهایتش برمی انگیزد] این وعده حقّی بر عهده اوست، ولی بیشتر مردم نمی دانند [و به علت نادانی و جهلشان انکار می کنند.]'),
(1940, 16, 39, '[یقیناً مردگان را زنده می کند] تا حقایقی را که در آن اختلاف می کنند، برای آنان روشن سازد. و تا کافران بدانند که همواره [در انکارشان نسبت به زنده شدن مردگان] دروغ می گفتند.'),
(1941, 16, 40, '[زنده کردن مردگان برای ما دشوار نیست] فرمان ما درباره چیزی چون [به وجود آمدنش را] اراده کنیم، فقط این است که به آن می گوییم: باش، پس [بی درنگ] موجود می شود.'),
(1942, 16, 41, 'و آنان که پس از ستم دیدنشان برای به دست آوردن خشنودی خدا هجرت کردند، یقیناً آنان را در این دنیا در جایگاه و مکانی نیکو جای دهیم، و قطعاً پاداش آخرت بهتر و برتر است، اگر می دانستند [که دارای چه کمیّت و کیفیتی است.]'),
(1943, 16, 42, 'همانان که [در برابر مصایب، وحوادث و آزار دیدن از دیگران] صبر کردند و فقط بر پروردگارشان توکل می کنند.'),
(1944, 16, 43, 'و پیش از تو جز مردانی که به آنان وحی می کردیم، نفرستادیم. اگر [این حقیقت را] نمی دانید [که پیامبران از جنس بشر بودند] از اهل دانش و اطلاع بپرسید [تا دیگر نگویید که چرا فرشته ای به رسالت نازل نشده است؟!]'),
(1945, 16, 44, '[همه پیامبران را] با دلایل روشن و کتاب های آسمانی [فرستادیم]، و قرآن را [هم] به سوی تو نازل کردیم به خاطر اینکه برای مردم آنچه را که برای [هدایتشان] به سویشان نازل شده بیان کنی و برای اینکه [در پیامبری تو و آنچه را به حق نازل شده] بیندیشند.'),
(1946, 16, 45, 'پس آیا کسانی که [بر ضد قرآن و پیامبر] نیرنگ های زشت به کار گرفتند، ایمنند از اینکه خدا آنان را در زمین فرو برد یا از جایی که پی نبرند، عذاب به سویشان آید؟'),
(1947, 16, 46, 'یا [ایمنند از اینکه] آنان را [در حالی که برای داد و ستد، زراعت و تجارت] در رفت و آمدند، گرفتار [عذاب] سازد؟ اینان عاجز کننده [خدا] نیستند [تا بتوانند از دسترس قدرت او بیرون روند.]'),
(1948, 16, 47, 'یا [ایمنند از اینکه] آنان را بر [پایه] ترس و بیم [و هشدارهای پی در پی به عذاب] بگیردشان؟ یقیناً پروردگارتان رؤوف و بسیار مهربان است.'),
(1949, 16, 48, 'آیا به سوی آنچه خدا آفریده ننگریستند که سایه های آنان در حالی که فروتنانه برای خدا سجده می کنند، از راست و چپ برمی گردد؟'),
(1950, 16, 49, 'و آنچه در آسمان ها و زمین از جنبندگان و فرشتگان وجود دارد، فقط برای خدا سجده می کنند و تکبّر و سرکشی نمی ورزند.'),
(1951, 16, 50, 'از پروردگارشان که بر فراز آنان است، می ترسند، و آنچه را به آن مأمور می شوند، انجام می دهند.'),
(1952, 16, 51, 'و [فرمان قاطعانه] خدا [ست که] فرمود: دو معبود انتخاب نکنید [که این انتخابی خلاف حقیقت است]؛ جز این نیست که او معبودی یگانه است؛ پس تنها از من بترسید.'),
(1953, 16, 52, 'آنچه در آسمان ها و زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست، و نیز دین ثابت و پایدار مخصوص اوست؛ آیا از غیر او پروا می کنید؟!'),
(1954, 16, 53, 'و آنچه از نعمت ها در دسترس شماست از خداست، آن گاه چون آسیبی [مانند سلبِ نعمت] به شما رسد، ناله و فریادتان را به التماس و زاری به درگاه او بلند می کنید.'),
(1955, 16, 54, 'سپس هنگامی که آسیب را از شما برطرف کند [بر خلاف انتظار] گروهی از شما به پروردگارشان شرک می آورند!!'),
(1956, 16, 55, 'تا به نعمت هایی که به آنان عطا کرده ایم، ناسپاسی و کفران ورزند. پس [چند روزی از کالای زودگذر دنیا] برخوردار شوید که سپس [فرجام زشت خود را] خواهید دانست.'),
(1957, 16, 56, 'و سهمی از آنچه را که به آنان روزی داده ایم، برای معبودانی قرار می دهند که جاهل به حقیقت آنهایند [که به طور مستقل هیچ سود و زیانی برای کسی ندارند]؛ به خدا سوگند از آنچه همواره افترا می بستید [که خدا دارای شریکانی است] قطعاً بازخواست خواهید شد.'),
(1958, 16, 57, 'و دختران را [به عنوان فرزند] برای خدا قرار می دهند، او منزّه [از توالد و تناسل] است؛ ولی برای خودشان آنچه را که [از پسران] میل دارند [به عنوان فرزند قرار می دهند].'),
(1959, 16, 58, 'و چون یکی از آنان را به [ولادت] دختر مژده دهند [از شدت خشم] چهره اش سیاه گردد، ودرونش از غصه واندوه لبریز و آکنده شود!!'),
(1960, 16, 59, 'به سبب مژده بدی که به او داده اند، از قوم و قبیله اش [در جایی که او را نبینند] پنهان می شود [و نسبت به این پیش آمد در اندیشه ای سخت فرو می رود که] آیا آن دختر را با خواری نگه دارد یا به زور، زنده به گورش کند؟! آگاه باشید! [نسبت به دختران] بد داوری می کنند.'),
(1961, 16, 60, 'برای کسانی که به آخرت ایمان نمی آورند، صفات زشت [وسیرت بد] است، وصفات برتر و والا ویژه خداست، و او توانای شکست ناپذیر و حکیم است.'),
(1962, 16, 61, 'و اگر خدا مردم را به سبب ستمشان مؤاخذه کند، هیچ جنبنده ای را [که مستحقّ مؤاخذه است] بر روی زمین باقی نمی گذاشت، ولی آنان را تا مدتی معین مهلت می دهد، پس هنگامی که اجلشان سرآید، نه ساعتی پس می مانند و نه ساعتی پیش می افتند.'),
(1963, 16, 62, 'و آنچه را [چون دختر] خوش ندارند، برای خدا قرار می دهند، و زبانهایشان دروغ پردازی می کند که [سرانجامِ] نیکوتر ویژه آنان است. ثابت ویقینی است که آتش برای آنان است و یقیناً پیشگامان [به سوی] آتش اند.'),
(1964, 16, 63, 'به خدا سوگند پیامبرانی به سوی امت های پیش از تو فرستادیم، پس شیطان کارهای [زشت] شان را در نظرشان آراست [تا انجامش برای آنان آسان گردد]، او امروز ولیّ و سرپرست اینان است [که در عقاید و رفتار پیرو مشرکان پیش اند] و برای آنان [در قیامت] عذابی دردناک است.'),
(1965, 16, 64, 'ما این کتاب را بر تو نازل نکردیم مگر برای اینکه حقایقی را که در آن اختلاف کرده اند، برای آنان توضیح دهی [تا از آرا، نظریات وسلیقه های باطلشان نسبت به حقایق دست بردارند] و برای مردمی که ایمان دارند [مایه] هدایت و رحمت باشد.'),
(1966, 16, 65, 'و خدا از آسمان آبی نازل کرد، و زمین را به وسیله آن پس از مردگی اش زنده ساخت، یقیناً در این [فعل و انفعالات] نشانه ای [بر توحید، ربوبیّت و قدرت خدا] ست برای قومی که شنوای [حقایق] اند.'),
(1967, 16, 66, 'و بی تردید برای شما در دام ها عبرتی است، [عبرت در اینکه] از درون شکم آنان از میان علف های هضم شده وخون، شیری خالص و گوارا به شما می نوشانیم که برای نوشندگان گواراست.'),
(1968, 16, 67, 'و از محصولات درختان خرما و انگور [گاهی بر خلاف خواسته خدا] نوشابه ای مست کننده و [زمانی دیگر] رزقی پاکیزه و نیکو می گیرید؛ در این [حقیقت] نشانه ای [بر توحید، ربوبیّت و قدرت خدا] ست برای گروهی که تعقّل می کنند.'),
(1969, 16, 68, 'و پروردگارت به زنبور عسل الهام کرد که از کوه ها و درختان و آنچه [از دار بست هایی] که [مردم] بر می افرازند، برای خود خانه هایی برگیر.'),
(1970, 16, 69, 'آن گاه از همه محصولات و میوه ها بخور، پس در راه های پروردگارت که برای تو هموار شده [به سوی کندو] برو؛ از شکم آنها [شهدی] نوشیدنی با رنگ های گوناگون بیرون می آید که در آن درمانی برای مردم است. قطعاً در این [حقیقت] نشانه ای [بر قدرت، لطف و رحمت خدا] ست برای مردمی که می اندیشند.'),
(1971, 16, 70, 'و خدا شما را آفرید، آن گاه شما را می میراند، برخی از شما به سوی پست ترین مرحله عمر [که سالخوردگی و فرتوتی است] برگردانده می شود، تا پس از دانش اندکی که داشت چیزی را نداند؛ یقیناً خدا دانا و تواناست.'),
(1972, 16, 71, 'خدا برخی از شما را در رزق و روزی بر برخی فزونی داده است؛ پس آنانکه فزونی یافته اند، حاضر نیستند از روزی خود [به صورتی قابل توجه و به اندازه لازم] به بردگان خود بپردازند که در بهرهوری از رزق و روزی باهم برابر باشند؛ پس آیا [با این تبعیض ظالمانه ای که به بردگان و زیردستان خود روا می دارند] نعمت خدا را [که عطای اوست و خود اختیار و استقلالی در تأمینش ندارند] منکرند؟!'),
(1973, 16, 72, 'و خدا برای شما همسرانی از جنس خودتان قرار داد و از همسرانتان، فرزندان و نوادگانی پدید آورد، و از نعمت های پاکیزه و دلپذیر به شما روزی داد، [با این همه لطف و رحمت] آیا به باطل ایمان می آورند و نعمت های خدا را ناسپاسی می کنند؟!'),
(1974, 16, 73, 'و به جای خدا معبودانی را می پرستند که از آسمان ها و زمین، مالک اندک رزقی برای آنان نیستند و هیچ قدرتی هم [برای مالک شدن روزی و رساندنش به آنان] ندارند.'),
(1975, 16, 74, 'بنابراین برای خدا اوصافی [همانند اوصاف موجودات] مَثَل نزنید، یقیناً خدا [کُنه ذات و حقیقت صفات خود را] می داند و شما نمی دانید.'),
(1976, 16, 75, 'خدا مثلی زده است: برده زر خریدی که بر هیچ کاری قدرت ندارد، و کسی که ما از سوی خود رزق نیکویی به او داده ایم، و او در پنهان و آشکار از آن انفاق می کند، آیا [این دو نفر] یکسانند؟ [این مَثَل، بیان کننده جایگاه و منزلت خدا و مجموعه هستی است؛ خدا مالک و فرمانروا و رازق همه مخلوقات و تدبیر کننده امور آنهاست، و همه مخلوقات مملوک و محکوم قدرت اویند؛ پس چرا شما بدون اندیشه و تفکر و بی دلیل و برهان بت های ناتوان وعاجز را که مملوکی بی اراده اند، شریک ربوبیّت حق می دانید؟! آیا خدا وبت مساوی است؟!] همه ستایش ها ویژه خداست [که یگانه ویکتاست وهیچ شریکی ندارد]؛ ولی بیشتر آنان [این حقیقت را] نمی دانند.'),
(1977, 16, 76, 'و خدا مَثَلی [دیگر] زده است: دو مرد را که یکی از آنان لال مادرزاد است [و] بر هیچ کاری قدرت ندارد و سربار سرپرست خویش است؛ او را هر کجا روانه می کند، منفعت و سودی نمی آورد، آیا چنین لال بی قدرت و بی سودی با کسی که [گویا و شنوا است و] به عدالت و انصاف فرمان می دهد و بر راهی راست قرار دارد، یکسان و مساوی است؟ [پس چرا معبودهای لال بی قدرت و بی سود را با خدای توانا و شنوا که ذاتش خیر بی نهایت است، برابر می دانید؟!]'),
(1978, 16, 77, 'غیب آسمان ها و زمین ویژه خداست، و کار برپا کردن قیامت برای او جز مانند یک چشم بر هم زدن یا نزدیک تر از آن نیست، یقیناً خدا بر هر کاری تواناست.'),
(1979, 16, 78, 'و خدا شما را از شکم مادرانتان بیرون آورد در حالی که چیزی نمی دانستید، و برای شما گوش و چشم و قلب قرار داد تا سپاس گزاری کنید.'),
(1980, 16, 79, 'آیا به پرندگان که در فضای آسمان، رام و مسخّر [فرمان او] هستند، ننگریستید که آنها را [از سقوط به زمین] جز خدا نگه نمی دارد؛ یقیناً در آن [وصفی که پرندگان دارند] نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] ست برای گروهی که ایمان دارند.'),
(1981, 16, 80, 'و خدا خانه هایتان را برای شما مایه آرامش و راحت قرار داد، و برای شما از پوست دام ها سر پناه [مانند قبّه و خیمه] مقرّر کرد، که آنها را روز کوچ کردنتان و هنگام اقامتتان [در جابجا کردن] سبک می یابید و [برای شما] از پشم ها و کرک ها و موهای آنها تا زمانی معین، وسایل زندگی و کالای تجارت پدید آورد.'),
(1982, 16, 81, 'و خدا برای شما از آنچه آفریده سایه هایی قرار داد، از کوه ها پناه گاه هایی برایتان به وجود آورد، و تن پوش هایی برای شما قرار داد که شما را از گرما نگه می دارد و پیراهن هایی [مقاوم] که شما را از آسیب جنگ و نبردتان حفظ می کند؛ این گونه نعمت هایش را بر شما کامل می کند تا تسلیم فرمان های او شوید.'),
(1983, 16, 82, 'پس اگر [بعد از این همه لطف و رحمت] روی از حق گرداندند [اندوهگین مشو]؛ زیرا فقط رساندن آشکار [پیام وحی] بر عهده توست.'),
(1984, 16, 83, 'آنان نعمت خدا را می شناسند، سپس [از روی کبر و لجاجت] آن را انکار می کنند و بیشترشان کافرند.'),
(1985, 16, 84, 'و روزی را [یاد کن] که از هر امتی گواهی برمی انگیزیم، سپس به کافران نه اجازه [عذر خواهی] می دهند، و نه از آنان می خواهند که [برای به دست آوردن خشنودی خدا] عذرخواهی کنند!'),
(1986, 16, 85, 'و چون که ستمکاران عذاب را ببینند [می یابند که هیچ راه گریزی از آن ندارند] پس [به کیفر ستمشان] نه عذاب از آنان سبک می شود، و نه مهلتشان دهند.'),
(1987, 16, 86, 'و زمانی که مشرکان [در عرصه قیامت] بت هایشان را که شریکان خدا قرار داده بودند ببینند، می گویند: پروردگارا! اینان شریکانی هستند که ما آنها را به جای تو می پرستیدیم [و اینان سبب گمراهی ما شدند.] ولی شریکان به آنان پاسخ می دهند: یقیناً شما دروغگویید [؛ زیرا سبب گمراهی شما هوای نفس، کبر و لجاجت شما بود.]'),
(1988, 16, 87, 'و در آن روز همه مشرکان در پیشگاه خدا سر تسلیم فرود آورند و معبودهایی را که همواره به دروغ [به عنوان شریک خدا] بر خدا می بستند [گم شده] از دستشان می رود.'),
(1989, 16, 88, 'آنان که کافر شدند، و مردم را از راه خدا بازداشتند، به سبب فسادی که همواره مرتکب می شدند، عذابی بر عذابشان می افزاییم.'),
(1990, 16, 89, 'و [یاد کن] روزی را که در هر امتی گواهی از خودشان بر آنان برمی انگیزیم، و تو را [ای پیامبر اسلام!] بر اینان گواه می آوریم؛ و این کتاب را بر تو نازل کردیم که بیانگر هر چیزی و هدایت و رحمت ومژده ای برای تسلیم شدگان [به فرمان های خدا] ست.'),
(1991, 16, 90, 'به راستی خدا به عدالت و احسان و بخشش به خویشاوندان فرمان می دهد، و از فحشا و منکر و ستم گری نهی می کند. شما را اندرز می دهد تا متذکّر [این حقیقت] شوید [که فرمان های الهی، ضامن سعادت دنیا و آخرت شماست.]'),
(1992, 16, 91, 'و به پیمان خدا چون پیمان بستید، وفا کنید و سوگند را پس از محکم کردنش [به وسیله عزم و قصد قطعی] در حالی که خدا را بر خود ضامن و کفیل قرار داده اید، نشکنید؛ یقیناً خدا آنچه را انجام می دهید، می داند.'),
(1993, 16, 92, 'و مانند آن [زنی] که پشم های تابیده خود را پس از استحکام و استواری وا می تابانید، نباشید که سوگندهایتان را میان خود به بهانه اینکه گروهی [چون مشرکان] از گروهی [چون مؤمنان] از نظر نفرات و امکانات افزون ترند، مایه خیانت و فساد قرار دهید [به این صورت که از روی تظاهر، مؤمنان را با سوگندهایتان دلخوش کنید که با آنان هستید، و در پشت پرده سوگندهایتان را بشکنید، و به خیال اینکه از ضربه های دشمن در امان بمانید، به دشمن بپیوندید] جز این نیست که خدا شما را به وسیله آن [نفرات اندک وافزونی نفرات دشمن] آزمایش می کند، و روز قیامت آنچه را [از حق و باطل] همواره در آن اختلاف می کردید، برای شما روشن می سازد.'),
(1994, 16, 93, 'اگر خدا می خواست شما را [بر محور هدایت] یک امت قرار می داد [اماهدایت اجباری فاقد ارزش است]، ولی هر کس را بخواهد [به کیفر لجاجت وتکبّرش] گمراه می کند و هر کس را بخواهد هدایت می نماید؛ و مسلماً ازآنچه همواره انجام می دادید، بازپرسی خواهیدشد.'),
(1995, 16, 94, 'و سوگندهایتان را میان خود مایه خیانت و فساد قرار ندهید که در نتیجه، قدمی پس از استواریش بلغزد؛ و به سبب آنکه مردم را از راه خدا بازداشتید [در این دنیا] سختی و ناگواری بچشید، و برای شما [در آخرت] عذابی بزرگ باشد.'),
(1996, 16, 95, 'و پیمان خدا را به بهایی اندک نفروشید؛ زیرا آنچه نزد خداست، برای شما بهتر است، اگر بدانید [که ارزش وفای به پیمان خدا قابل مقایسه با همه دنیا نیست.]'),
(1997, 16, 96, 'آنچه [از ثروت و مال] نزد شماست، فانی می شود و آنچه [از پاداش و ثواب] نزد خداست، باقی می ماند، و قطعاً آنان که [برای دینشان] شکیبایی ورزیدند، پاداششان را بر پایه بهترین عملی که همواره انجام می داده اند، می دهیم.'),
(1998, 16, 97, 'از مرد و زن، هر کس کار شایسته انجام دهد در حالی که مؤمن است، مسلماً او را به زندگی پاک و پاکیزه ای زنده می داریم و پاداششان را بر پایه بهترین عملی که همواره انجام می داده اند، می دهیم.'),
(1999, 16, 98, 'پس هنگامی که قرآن می خوانی از شیطان رانده شده به خدا پناه ببر.'),
(2000, 16, 99, 'یقیناً او بر کسانی که ایمان آورده اند وهمواره بر پروردگارشان توکل می کنند، تسلّطی ندارد.'),
(2001, 16, 100, 'تسلّطش فقط بر کسانی است که او را سرپرست و دوست خود گرفته اند و بر کسانی است که [به وسیله اغواگری او] برای خدا شریک قرار داده اند.'),
(2002, 16, 101, 'و هنگامی که آیه ای را [که در بردارنده حکم جدیدی است] جایگزین آیه دیگر می کنیم، در حالی که خدا به آنچه نازل می کند داناتر است، مخالفان می گویند: تو فقط [بر خدا] دروغ می بندی!! [چنین نیست که می گویند] بلکه بیشترشان [اسرار تبدیلِ احکام را] نمی دانند.'),
(2003, 16, 102, 'بگو: آن [آیات] را روح القدس از سوی پروردگارت به درستی و راستی نازل کرده تا کسانی را که ایمان آورده اند، ثابت قدم بدارد و برای تسلیم شدگان [به فرمان های حق] هدایت و مژده باشد.'),
(2004, 16, 103, 'و ما می دانیم که آنان می گویند: یقیناً این آیات را بشری به او می آموزد!! [چنین نیست که می گویند، زیرا] زبان کسی که [آموختن قرآن را به پیامبر] به او نسبت می دهید، غیر عربی است و این قرآن به زبان عربی فصیح و روشن است.'),
(2005, 16, 104, 'قطعاً کسانی که به آیات خدا ایمان ندارند، خدا هدایتشان نمی کند، و برای آنان عذابی دردناک است.'),
(2006, 16, 105, 'فقط کسانی [به خدا و پیامبر] دروغ می بندند که به آیات خدا ایمان ندارند، و اینانند که دروغگوی واقعی اند.'),
(2007, 16, 106, 'هر کس پس از ایمان آوردنش به خدا کافر شود [به عذاب خدا گرفتار آید]؛ مگر کسی که به کفر مجبور شده [امّا] دلش مطمئن به ایمان است، ولی آنان که سینه برای پذیرفتن کفر گشاده اند، خشمی سخت از سوی خدا بر آنان است و آنان را عذابی بزرگ خواهد بود.'),
(2008, 16, 107, 'این [خشم و عذاب بزرگ] به سبب آن است که زندگی دنیا [یِ زودگذر] را بر آخرت ترجیح دادند، و مسلماً خدا مردم کفر پیشه را هدایت نمی کند.'),
(2009, 16, 108, 'اینان کسانی هستند که خدا بر دل و گوش و چشمشان مُهر [شقاوت] زده، و اینان بی خبران واقعی اند.'),
(2010, 16, 109, 'ثابت و یقینی است که آنان در آخرت زیانکارند.'),
(2011, 16, 110, 'آن گاه یقیناً پروردگارت نسبت به کسانی که پس از آزار دیدنِ [از مشرکان، سرزمین شرک را رها کرده] هجرت نمودند، سپس جهاد کردند، و [برای حفظ دینشان] شکیبایی ورزیدند، آری، پروردگارت [نسبت به آنان] پس از این [همه بلا] بسیار آمرزنده و مهربان است.'),
(2012, 16, 111, '[یاد کن] روزی را که هر کس [به پیشگاه حق] می آید [برای مصون ماندنش از عذاب] از خود دفاع می کند و هر کس هر عملی انجام داده است [همه آن] به طور کامل به او داده می شود و مورد ستم قرار نخواهند گرفت.'),
(2013, 16, 112, 'و خدا [برای پندآموزی به ناسپاسان] مَثَلی زده است: شهری را که امنیت و آسایش داشت و رزق و روزیِ [مردمش] به فراوانی از همه جا برایش می آمد، پس نعمت خدا را ناسپاسی کردند، در نتیجه خدا به کیفر اعمالی که همواره مرتکب می شدند، بلای گرسنگی و ترس فراگیر را به آنان چشانید.'),
(2014, 16, 113, 'و همانا پیامبری از خود آنان برای [هدایت] شان آمد، ولی او را تکذیب کردند، پس عذاب [خدا] آنان را در حالی که ستمکار بودند، فراگرفت.'),
(2015, 16, 114, 'از نعمت هایی که خدا روزی شما کرده است، حلال وپاکیزه بخورید، و نعمت خدا را سپاس گزارید، اگر تنها خدا را می پرستید.'),
(2016, 16, 115, 'خدا فقط مردار وخون و گوشت خوک و آنچه را با نام غیر خدا ذبح کرده اند، بر شما حرام کرده است؛ پس کسی که [برای نجات جانش از خطر، به خوردن آنها] ناچار شود در حالی که خواهان لذت نباشد و از حد لازم تجاوز نکند [گناهی بر او نیست] یقیناً خدا بسیار آمرزنده و مهربان است.'),
(2017, 16, 116, 'و به سبب دروغی که زبانتان گویای به آن است، نگویید: این حلال است و این حرام، تا به دروغ به خدا افترا بزنید [که این حلال و حرام حکم خداست]؛ مسلماً کسانی که به خدا دروغ می بندند، رستگار نخواهند شد.'),
(2018, 16, 117, '[با این کارهای زشت] بهره و برخورداری اندکی [در زندگی دنیا دارند] و برای آنان [در قیامت] عذابی دردناک است.'),
(2019, 16, 118, 'و آنچه را پیش از این برای تو شرح دادیم، بر یهود حرام کردیم، ما به آنان ستم نورزیدیم، بلکه آنان خودشان به خود ستم می کردند.'),
(2020, 16, 119, 'آن گاه پروردگارت به کسانی که از روی نادانی کار زشت مرتکب شدند، و بعد از آن توبه کردند و [مفاسد خود را] اصلاح نمودند، [لطف و عنایت دارد] زیرا پروردگارت پس از آن [توبه و اصلاح] بسیار آمرزنده و مهربان است.'),
(2021, 16, 120, 'بدون تردید، ابراهیم [به تنهایی] یک امت بود، برای خدا از روی فروتنی فرمانبردار و [یکتاپرستی] حق گرا بود واز مشرکان نبود.'),
(2022, 16, 121, 'سپاس گزار نعمت های او بود، خدا او را برگزید وبه راهی راست راهنمایی اش کرد.'),
(2023, 16, 122, 'و به او در دنیا [زندگی نیکویی] دادیم، و بی تردید در آخرت از شایستگان است.'),
(2024, 16, 123, 'آن گاه به تو وحی کردیم که از آیین [یکتاپرستیِ] ابراهیم حق گرا پیروی کن که از مشرکان نبود.'),
(2025, 16, 124, '[تحریم ماهی گیری در] روز شنبه فقط برای کسانی که در آن [از نظر آزادی و ممنوعیت کار] اختلاف کردند، قرار داده شده است، و بی تردید پروردگارت در روز قیامت درباره آنچه در آن اختلاف می کردند، داوری خواهد کرد.'),
(2026, 16, 125, '[مردم را] با حکمت و اندرز نیکو به راه پروردگارت دعوت کن، و با آنان به نیکوترین شیوه به بحث [و مجادله] بپرداز، یقیناً پروردگارت به کسانی که از راه او گمراه شده اند و نیز به راه یافتگان داناتر است.'),
(2027, 16, 126, 'و اگر [ستم گر را] مجازات کردید، پس فقط به مانند ستمی که به شما شده مجازات کنید، و اگر شکیبایی ورزید [و از مجازات کردن بگذرید] این کار برای شکیبایان بهتر است.'),
(2028, 16, 127, 'و شکیبایی کن، و شکیبایی تو جز به توفیق خدا نیست، و بر [گمراهی و ضلالت] آنان [و کارهایی که انجام می دهند] اندوهگین مباش، و از نیرنگی که همواره به کار می گیرند، دلتنگ مشو.'),
(2029, 16, 128, 'بی تردید خدا با کسانی که پرهیزکاری پیشه کردند و کسانی که [از هر جهت] نیکوکارند می باشد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزّه و پاک است آن [خدایی] که شبی بنده اش] محمّد (صلی الله علیه وآله وسلم)] را از مسجدالحرام به مسجد الاقصی که پیرامونش را برکت دادیم، سیر [و حرکت] داد، تا [بخشی] از نشانه هایِ [عظمت و قدرت [خود را به او نشان دهیم؛ یقیناً او شنوا و داناست.'),
(2031, 17, 2, 'و ما به موسی کتاب دادیم و آن را برای بنی اسرائیل وسیله هدایت قرار دادیم [و در آن کتاب، آنان را به این حقیقت راهنمایی کردیم] که جز مرا [که خدای یگانه ام] وکیل و کار ساز نگیرید.'),
(2032, 17, 3, '[ای] نسل کسانی که با نوح [در کشتی] سوار کردیم! مسلماً او بنده ای بسیار سپاس گزار بود [پس شما هم چون او با عمل به فرمان های حق سپاس گزار باشید.]'),
(2033, 17, 4, 'ما در تورات به بنی اسرائیل خبر دادیم که قطعاً دو بار در زمین فساد می کنید و [در برابر طاعت خدا] به سرکشی و طغیان [و نسبت به مردم به برتری جویی و ستمی] بزرگ دچار می شوید.'),
(2034, 17, 5, 'پس هنگامی که [زمان ظهور] وعده [عذاب و انتقام ما به کیفر] نخستین فسادانگیزی و طغیان شما فرا رسد، بندگان سخت پیکار ونیرومند خود را بر ضد شما برانگیزیم، آنان [برای کشتن، اسیر کردن و ربودن ثروت و اموالتان] لابه لای خانه ها را [به طور کامل و با دقت] جستجو می کنند؛ و یقیناً این وعده ای انجام شدنی است.'),
(2035, 17, 6, 'سپس پیروزی بر آنان را به شما باز می گردانیم و شما را به وسیله اموال و فرزندان تقویت می کنیم، و نفرات [رزمی] شما را بیشتر می گردانیم.'),
(2036, 17, 7, 'اگر نیکی کنید به خود نیکی کرده اید، و اگر بدی کنید به خود بدی کرده اید. پس هنگامی که [زمان ظهور] وعده دوم [برای عذاب و انتقام] فرا رسد، [پیکارگرانی بسیار سخت گیر بر ضد شما برمی انگیزیم] تا شما را [با دچار کردن به مصایب سنگین و گزند و آسیب فراوان] غصه دار و اندوهگین کنند و به مسجد [الاقصی] درآیند، آن گونه که بار اول درآمدند تا هر که و هر چه را دست یابند، به شدت در هم کوبند و نابود کنند.'),
(2037, 17, 8, 'امید است که پروردگارتان [در صورتی که توبه کنید] به شما رحم کند و اگر به طغیان و فساد برگردید، ما هم [به کیفر شدید و عذاب سخت] بازگردیم، و دوزخ را برای کافران، زندانی تنگ قرار دادیم.'),
(2038, 17, 9, 'بی تردید این قرآن به استوارترین آیین هدایت می کند، و به مؤمنانی که کارهای شایسته انجام می دهند، مژده می دهد که برای آنان پاداشی بزرگ است.'),
(2039, 17, 10, 'و برای آنان که به قیامت ایمان نمی آورند، عذابی دردناک آماده کرده ایم.'),
(2040, 17, 11, 'و انسان به همان صورت که نیکی ها را می طلبد [بدون توجه به عواقب امور و به سبب جهل به مصالح و مفاسد خویش، گزند و آسیب و] بدی ها را می طلبد و انسان بسیار شتاب زده و عجول است.'),
(2041, 17, 12, 'ما شب و روز را دو نشانه [قدرت و حکمت خود] قرار دادیم؛ پس شب را بی نور نمودیم و روز را روشن ساختیم، تا [در آن] روزی و رزقی را از پروردگارتان بطلبید و برای اینکه شماره سال ها و حساب [اوقات و زمان ها] را بدانید، و هر چیزی را [که نسبت به دنیا و دینتان و تربیت و کمالتان نیازمند به آن هستید] به روشنی و به طور کامل بیان کردیم.'),
(2042, 17, 13, 'و عمل هر انسانی را برای همیشه ملازم او نموده ایم، و روز قیامت نوشته ای را [که کتاب عمل اوست] برای او بیرون می آوریم که آن را پیش رویش گشوده می بیند.'),
(2043, 17, 14, '[به او می گویند:] کتاب خود را بخوان، کافی است که امروز خودت بر خود حسابگر باشی.'),
(2044, 17, 15, 'هر کس هدایت یافت، فقط به سود خودش هدایت می یابد و هر کس گمراه شد، فقط به زیان خودش گمراه می شود. و هیچ بردارنده بار گناهی بار گناه دیگری را به دوش خود بر نمی دارد؛ و ما بدون اینکه پیامبری را [برای هدایت واتمام حجت به سوی مردم] بفرستیم، عذاب کننده [آنان] نبودیم.'),
(2045, 17, 16, 'هنگامی که بخواهیم شهر و دیاری را نابود کنیم، مرفّهین و خوش گذران هایش را [به وسیله وحی به طاعت، بندگی و دوری از گناه] فرمان می دهیم، چون [سرپیچی کنند و] در آن شهر به فسق و فجور روی آورند، عذاب بر آنان لازم و حتم می شود، پس آنان را به شدت درهم می کوبیم [و بنیادشان را از ریشه بر کنیم.]'),
(2046, 17, 17, 'و بسیاری از ملت های پس از نوح را [بر پایه همین روش به سبب فسق و فجورشان] نابود کردیم، و [برای نابود کردن این گونه ملت ها] کافی است که پروردگارت به گناهان بندگانش آگاه و بینا باشد.'),
(2047, 17, 18, 'هر کس [همواره] دنیای زودگذر را بخواهد [چنین نیست که هر چه بخواهد بیابد بلکه] هر چه را ما برای هر که بخواهیم، به سرعت در همین دنیا به او عطا می کنیم، آن گاه دوزخ را در حالی که نکوهیده و رانده شده از رحمت خدا وارد آن می شود، برای او قرار می دهیم.'),
(2048, 17, 19, 'و کسانی که آخرت را در حالی که مؤمن هستند بخواهند و با تلاشی کامل [و خالصانه] برای [به دست آوردن] آن تلاش کنند، پس تلاششان به نیکی مقبول افتد [و به آن پاداششان دهند.]'),
(2049, 17, 20, 'هر یک از دو گروه دنیا طلب و آخرت خواه را [در این دنیا] از عطای پروردگارت یاری دهیم، وعطای پروردگارت [در این دنیا از کسی] ممنوع شدنی نیست.'),
(2050, 17, 21, 'با تأمل بنگر چگونه برخی از آنان را بر برخی [در امور مادی و بهره های دنیایی] فزونی بخشیدیم، و همانا آخرت از نظر درجات برتر، و از جهت فزونی بیشتر است.'),
(2051, 17, 22, 'با خدا معبودی دیگر قرار مده که [نزد شایستگان] نکوهیده و [در دنیا و آخرت] بی یار و یاور شوی.'),
(2052, 17, 23, 'و پروردگارت فرمان قاطع داده است که جز او را نپرستید، و به پدر و مادر نیکی کنید؛ هرگاه یکی از آنان یا دو نفرشان در کنارت به پیری رسند [چنانچه تو را به ستوه آورند] به آنان اُف مگوی و بر آنان [بانگ مزن و] پرخاش مکن، و به آنان سخنی نرم و شایسته [و بزرگوارانه] بگو.'),
(2053, 17, 24, 'و برای هر دو از روی مهر و محبت، بال فروتنی فرود آر و بگو: پروردگارا! آنان را به پاس آنکه مرا در کودکی تربیت کردند، مورد رحمت قرار ده.'),
(2054, 17, 25, 'پروردگارتان به نیّت ها و حالاتی که [نسبت به پدر و مادرتان] در دل های شماست [از خود شما] آگاه تر است، اگر مردم شایسته ای باشید [ولی نیّت شما درباره پدر و مادر ناپسند باشد، یا حقّی از آنان ضایع کنید، اما پشیمان شوید و به خدا بازگردید، شایسته است]؛ زیرا او نسبت به بازگشت کنندگان بسیار آمرزنده است.'),
(2055, 17, 26, 'و حقّ خویشاوندان و حقّ تهیدست و از راه مانده را بپرداز، و هیچ گونه اسراف و ولخرجی مکن.'),
(2056, 17, 27, 'بی تردید اسراف کنندگان و ولخرجان، برادران شیاطین اند، و شیطان همواره نسبت به پروردگارش بسیار ناسپاس است.'),
(2057, 17, 28, 'و اگر [به خاطر تهیدستی و فقر] باید از آنان [که به پرداخت حقّشان سفارش شدی] روی بگردانی [و این روی گردانی] برای طلب رزقی است که از سوی پروردگارت رسیدن به آن را امید داری؛ پس با آنان [تا رسیدن رزق خدا] سخنی نرم و امیدوار کننده بگو.'),
(2058, 17, 29, 'و دستت را بخیلانه بسته مدار [که از انفاق در راه خدا باز مانی] و به طور کامل هم [در انفاق] دست و دل باز مباش [که چیزی برای معاش خودت باقی نماند] که در نهایت [نزد شایستگان] نکوهیده [و در زندگی خود] درمانده گردی.'),
(2059, 17, 30, 'یقیناً پروردگارت رزق را برای هر که بخواهد وسعت می دهد، و [برای هر که بخواهد] تنگ می گیرد؛ زیرا او به [مصلحت] بندگانش آگاه و بیناست.'),
(2060, 17, 31, 'فرزندانتان را از بیم تنگدستی نکشید؛ ما به آنان و شما روزی می دهیم، یقیناً کشتن آنان گناهی بزرگ است.'),
(2061, 17, 32, 'و نزدیک زنا نشوید که کاری بسیار زشت و راهی بد است.'),
(2062, 17, 33, 'و کسی را که خدا کشتنش را حرام کرده [و به جانش حرمت نهاده] جز به حق نکشید، و هر کس مظلوم [و به ناحق] کشته شود، برای وارثش تسلّطی بر قاتل [جهت خونخواهی، دیه و عفو] قرار داده ایم، پس وارث مقتول نباید در کشتن زیاده روی کند؛ زیرا او [با قوانین صحیح و درستی چون دیه و قصاص] مورد حمایت قرار گرفته است.'),
(2063, 17, 34, 'و به مال یتیم تا زمانی که به سنّ بلوغ و رشدش برسد، جز به بهترین شیوه نزدیک نشوید؛ و به عهد و پیمان وفا کنید، زیرا [روز قیامت] درباره پیمان بازخواست خواهد شد.'),
(2064, 17, 35, 'و هنگامی که [جنسی را] پیمانه می کنید، پیمانه را کامل بدهید، و [اجناس قابل وزن را] با ترازوی درست و صحیح وزن کنید، این برای شما بهتر و فرجامش نیکوتر است.'),
(2065, 17, 36, 'و از چیزی که به آن علم نداری [بلکه برگرفته از شنیده ها، ساده نگری ها، خیالات و اوهام است] پیروی مکن؛ زیرا گوش و چشم و دل [که ابزار علم و شناخت واقعی اند] موردِ بازخواست اند.'),
(2066, 17, 37, 'و در زمین، با تکبّر و سرمستی راه مرو که تو هرگز نمی توانی [با قدم هایت] زمین را بشکافی، و هرگز در بلندی قامت نمی توانی به کوه ها برسی.'),
(2067, 17, 38, 'همه این دستورات و فرمان هایی که [در آیات گذشته بیان شد، سرپیچی از آنها] گناهش نزد پروردگارت ناپسند است.'),
(2068, 17, 39, 'این [احکام و مطالب] از حکمت هایی است که پروردگارت به تو وحی کرده است، و با خدا معبودی دیگر قرار مده که نکوهیده و رانده [شده از رحمت] در دوزخ افکنده شوی.'),
(2069, 17, 40, '[ای مشرکان!] آیا پروردگارتان [با ترجیح دادن شما بر خود] شما را به [دارا بودن] پسران، برگزیده و برای خود از فرشتگان، دخترانی برگرفته؟ یقیناً [از روی دروغ و اتهام] سخنی بزرگ [و ناروا] می گویید.'),
(2070, 17, 41, 'به راستی در این قرآن [داستان ها، حکمت ها و موعظه های] گوناگون بیان کردیم تا متذکّر و هوشیار شوند؛ ولی آنان را جز رمیدن نمی افزاید.'),
(2071, 17, 42, 'بگو: اگر آن گونه که می گویند، باخدا خدایانی بود، در آن هنگام به سوی صاحب عرش راهی می جستند [تا قدرتش را تصاحب کنند.]'),
(2072, 17, 43, 'او از آنچه می گویند، منزّه و بسیار برتر و والاتر است.'),
(2073, 17, 44, 'آسمان های هفتگانه و زمین و هر کس که در آنهاست، او را تسبیح می گویند، وهیچ چیزی نیست مگر اینکه همراه با ستایش، تسبیح او می گوید، ولی شما تسبیح آنها را نمی فهمید، یقیناً او بردبار و بسیار آمرزنده است.'),
(2074, 17, 45, 'و هنگامی که قرآن بخوانی، میان تو و آنان که به آخرت ایمان ندارند، پرده ای نامریی قرار می دهیم [که به سزای لجاجت و کفرشان از فهم آن محروم شوند.]'),
(2075, 17, 46, 'و بر دل هایشان پوشش هایی می گذاریم تا آن را نفهمند و در گوش هایشان سنگینی می نهیم [تا آن را از روی حقیقت نشنوند]؛ و چون پروردگارت را در قرآن به یگانگی یاد کنی با حالت رمیدگی از تو روی می گردانند.'),
(2076, 17, 47, 'هنگامی که [وقت قرآن خواندنت] به تو گوش می دهند، ما به هدفی که برای آن گوش می دهند داناتریم، و نیز هنگامی که رازگویی می کنند [به کارشان آگاه تریم] آن زمان که [آن] ستم گران [به یکدیگر] می گویند: شما [اگر از او پیروی کنید] جز از مردی جادو شده پیروی نمی کنید.'),
(2077, 17, 48, 'بنگر چگونه تو را به صفاتی [چون شاعر، کاهن، ساحر و مجنون] وصف می کنند در نتیجه گمراه شدند، بنابراین قدرت ندارند راهی [به سوی هدایت] یابند.'),
(2078, 17, 49, 'و گفتند؛ هنگامی که ما استخوان [هایی جدا از هم و ریز ریز] و پوسیده شدیم، آیا به طور قطع در آفرینشی جدید برانگیخته می شویم؟!'),
(2079, 17, 50, 'بگو: [اینکه سهل و آسان است] شما سنگ شوید یا آهن،'),
(2080, 17, 51, 'یا آفریده ای از آنچه در ذهنتان [حیات یافتنش] سخت و دشوار می آید [بدون تردید با آفرینشی جدید برانگیخته می شوید] بی درنگ خواهند گفت: چه کسی ما را بر می گرداند؟ بگو: همان کسی که اولین بار شما را آفرید. پس سرهای خود را [به عنوان ریشخند و استهزا] به سوی تو می جنبانند و می گویند: [این زنده شدن دوباره] چه زمانی خواهد بود؟ بگو: چه بسا نزدیک باشد.'),
(2081, 17, 52, 'روزی که شما را [از میان گورها برای ورود به عرصه قیامت] فرا می خواند، پس [فرا خواندنش را] در حالی که او را سپاس و ستایش می گویید، اجابت می کنید، وگمان می برید که [در دنیا یا در برزخ] جز اندکی درنگ نکرده اید.'),
(2082, 17, 53, 'و به بندگانم بگو: سخنی را که نیکوتر است، بگویند؛ زیرا شیطان میان آنان [به سبب سخنان زشت و بی منطق] دشمنی و نزاع می افکند، زیرا شیطان همواره برای انسان دشمنی آشکار است.'),
(2083, 17, 54, 'پروردگارتان به شما داناتر است، اگر بخواهد شما را مورد رحمت قرار می دهد و اگر بخواهد عذابتان می کند. و ما تو را بر آنان نگهبان و کارساز نفرستاده ایم.'),
(2084, 17, 55, 'و پروردگارت به هر که [و هر چه] در آسمان ها و زمین است، داناتر است. و به یقین برخی از پیامبران را بر برخی دیگر برتری دادیم وبه داود زبور عطا کردیم.'),
(2085, 17, 56, 'بگو: کسانی را که به جای خدا [معبودان خود] پنداشتید، [بخوانید تا بفهمید که] آنها نمی توانند آسیب و گزندی را از شما دفع کنند، و نه [آن را از شما به دیگری] انتقال دهند.'),
(2086, 17, 57, 'کسانی [از فرشتگان، پریان و ارواح را] که آنان [به عنوان معبود] می پرستند [خود آنان برای رفع نیازمندی هایشان] به سوی پروردگارشان وسیله می جویند، تا کدامشان نزدیک تر باشد، و به رحمت او امید دارند، و از عذابش می ترسند؛ زیرا عذاب پروردگارت شایسته پرهیز است.'),
(2087, 17, 58, 'هیچ شهری [از شهرهای یاغیان و مفسدان] نیست مگر آنکه آن را پیش از قیامت [به عذاب] نابود می کنیم یا به عذابی سخت دچار می سازیم. انجام این کار در کتاب [لوح محفوظ] نگاشته شده است.'),
(2088, 17, 59, 'و [ای پیامبر!] هیچ چیز، ما را از فرستادن معجزات [که همواره مشرکان و کافران خواهانِ آنند] باز نداشت جز اینکه پیشینیان، آن معجزات را تکذیب کردند [و به این سبب نابود شدند؛ واینان هم اگر به سویشان آید، تکذیب می کنند ونابود می شوند.] و به قوم ثمود [ازلابلای سنگ های کوه] ناقه دادیم که معجزه ای روشن بود، ولی به آن ستم کردند [و او را از پا درآوردند وبه عذاب مبتلا شدند] و ما معجزات را جز برای هشدار دادن به مردم نمی فرستیم.'),
(2089, 17, 60, 'و [یاد کن] هنگامی را که به تو گفتیم: یقیناً پروردگارت [از هر جهت] به مردم احاطه کامل دارد و آن خوابی را که به تو نشان دادیم و نیز درخت لعنت شده در قرآن را [که مصداقش درخت زقّوم، بنی امیه، طاغیان و یاغیانند] جز برای آزمایش مردم قرار ندادیم؛ و ما آنان را [از عاقبت شرک و کفر] هشدار می دهیم، ولی در آنان جز طغیانی بزرگ نمی افزاید!!'),
(2090, 17, 61, 'و [یاد کن] هنگامی را که به فرشتگان گفتیم: برای آدم سجده کنید. پس همه سجده کردند مگر ابلیس که گفت: آیا برای کسی که او را از گِل آفریدی، سجده کنم؟!'),
(2091, 17, 62, '[سپس] گفت: مرا خبر ده این کسی که او را بر من برتری دادی [سببش چه بود؟] اگر تا قیامت مهلتم بخشی، بی تردید فرزندانش را جز اندکی لجام می زنم [و به دنبال خود به عرصه هلاکت و نابودی می کشم.]'),
(2092, 17, 63, 'خدا فرمود: برو که از آنان هر کس تو را پیروی کند، قطعاً دوزخ کیفرتان خواهد بود، کیفری سخت و کامل.'),
(2093, 17, 64, 'از آنان هر که را توانستی با آوازت [به سوی باطل] برانگیز، و [برای راندنشان به سوی طغیان و گناه] با سواره ها و پیاده هایت بر آنان بانگ زن، و با آنان [از راه سوق دادنشان به حرام] در اموال و [از طریق وسوسه کردنشان به زنا] در اولاد شریک شو، و به آنان وعده [دروغ] بده [که از قیامت، حسابرسی، بهشت و دوزخ خبری نیست] و شیطان آنان را جز از روی باطل و دروغ وعده نمی دهد.'),
(2094, 17, 65, '[ولی آگاه باش که] یقیناً تو را بر بندگانم هیچ تسلّطی نیست، و کافی است که پروردگارت نگهبان و کارساز [آنان] باشد.'),
(2095, 17, 66, 'پروردگارتان کسی است که کشتی ها را در دریا برای شما روان می کند تا [با رفتن از اقلیمی به اقلیم دیگر] از رزقش بطلبید؛ زیرا او همواره به شما مهرباناست.'),
(2096, 17, 67, 'و زمانی که در دریا سختی و آسیبی به شما رسد، هر که را جز او می خوانید ناپدید و گم می شود، و هنگامی که شما را [با سوق دادنتان] به سوی خشکی نجات دهد [از خدا] روی می گردانید. و انسان [با اینکه الطاف خدا را همواره در زندگی خود لمس می کند] بسیار ناسپاس است.'),
(2097, 17, 68, 'آیا ایمن هستید از اینکه شما را در کنار خشکی [در زمین] فرو برد، یا بر شما توفانی از شن و سنگریزه فرستد، سپس برای خود حافظ و نگهبانی نیابید؟!'),
(2098, 17, 69, 'یا ایمن هستید از اینکه بار دیگر شما را به دریا برگرداند، پس توفانی سخت که با خود ریگ و سنگ می آورد بر شما فرستد و به سبب اینکه ناسپاسی کردید غرقتان کند؟ آن گاه در برابر کار ما برای خود پی گیر و دفاع کننده ای نخواهید یافت.'),
(2099, 17, 70, 'به یقین فرزندان آدم را کرامت دادیم، و آنان را در خشکی و دریا [بر مرکب هایی که در اختیارشان گذاشتیم] سوار کردیم، و به آنان از نعمت های پاکیزه روزی بخشیدیم، وآنان را بر بسیاری از آفریده های خود برتری کامل دادیم.'),
(2100, 17, 71, '[یاد کن] روزی را که هر گروهی از مردم را با پیشوایشان می خوانیم؛ پس کسانی که نامه اعمالشان را به دست راستشان دهند، پس آنان نامه خود را [با شادی و خوشحالی] می خوانند و به اندازه رشته میان هسته خرما مورد ستم قرار نمی گیرند.'),
(2101, 17, 72, 'و کسی که در این دنیا کوردل باشد، در آخرت هم کوردل و گمراه تر است.'),
(2102, 17, 73, 'و نزدیک بود که تو را از آنچه به تو وحی کردیم، بلغزانند تا غیر آن را از روی افترا به ما نسبت دهی و در این صورت، تو را دوست خود می گرفتند.'),
(2103, 17, 74, 'و اگر تو را ثابت قدم نمی داشتیم، همانا نزدیک بود اندکی به سوی آنان متمایل شوی.'),
(2104, 17, 75, 'در آن صورت دو برابر در دنیا و دو برابر در آخرت عذاب به تو می چشاندیم، آن گاه برای خود در برابر ما یاوری نمی یافتی.'),
(2105, 17, 76, 'و نزدیک بود [مشرکان] تو را از این سرزمین حرکت دهند تا از آن بیرونت کنند، و در آن صورت پس از تو جز مدتی اندک درنگ نمی کردند [و دچار عذاب خدا می شدند.]'),
(2106, 17, 77, '[مهلت نیافتن دشمنان جز مدتی اندک] شیوه ای است که همواره درباره [امت های] پیامبرانی که پیش از تو فرستادیم [جاری] بوده است، و برای شیوه ما هیچ دگرگونی و تغییری نخواهی یافت.'),
(2107, 17, 78, 'نماز را از ابتدای تمایل خورشید به جانب مغرب [که شروعِ ظهرِ شرعی است] تا نهایت تاریکی شب بر پا دار، و [نیز] نماز صبح را [اقامه کن] که نماز صبح مورد مشاهده [فرشتگان شب و فرشتگان روز] است.'),
(2108, 17, 79, 'و پاسی از شب را برای عبادت و بندگی بیدار باش که این افزون [بر واجب،] ویژه توست، امید است پروردگارت تو را [به سبب این عبادت ویژه] به جایگاهی ستوده برانگیزد.'),
(2109, 17, 80, 'و بگو: پروردگارا! مرا [در هر کار و شغلی] به نیکی وارد کن و به نیکی بیرون آور و برایم از نزد خود نیرویی یاری دهنده قرار ده.'),
(2110, 17, 81, 'و بگو: حق آمد و باطل نابود شد؛ بی تردید باطل نابود شدنی است.'),
(2111, 17, 82, 'و ما از قرآن آنچه را برای مؤمنان مایه درمان ورحمت است، نازل می کنیم وستمکاران را جز خسارت نمی افزاید.'),
(2112, 17, 83, 'و زمانی که به انسان نعمت می بخشیم [از ما] روی بگرداند و خود را مغرورانه از ما دور کند، و هنگامی که آسیبی به او برسد [به سبب بی ایمانی و غفلت از قدرت و رحمت ما] بسیار نومید و مأیوس شود.'),
(2113, 17, 84, 'بگو: هر کس بر پایه خلق و خوی و عادت های اکتسابی خود عمل می کند، پس پروردگارتان به کسی که راه یافته تر است، داناتر است.'),
(2114, 17, 85, 'و از تو درباره روح می پرسند، بگو: روح از امر پروردگار من است، و از دانش و علم جز اندکی به شما نداده اند.'),
(2115, 17, 86, 'و اگر بخواهیم قطعاً آنچه را به تو وحی کرده ایم [از یادها و نوشته ها] محو می کنیم، آن گاه در برابر ما [برای بازگرفتنش] کارسازی برای خود نخواهی یافت.'),
(2116, 17, 87, '[ولی محو نشدن قرآن از یادها و نوشته ها، نیست] مگر رحمتی از سوی پروردگارت، به یقین فضل او بر تو همواره بزرگ است.'),
(2117, 17, 88, 'بگو: قطعاً اگر جنّ و انس گرد آیند که مانند این قرآن را بیاورند، نمی توانند مانندش را بیاورند، و اگر چه پشتیبان یکدیگر باشند.'),
(2118, 17, 89, 'و همانا در این قرآن برای [هدایت] مردم از هر مَثَل و داستانی به صورت های گوناگون بیان کردیم، ولی بیشتر مردم جز انکار وکفران [آن نعمت عظیم را] نخواستند.'),
(2119, 17, 90, 'و گفتند: ما به تو ایمان نمی آوریم، مگر آنکه از این سرزمین [خشک و سوزان مکه] چشمه ای پر آب و جوشان روان سازی!!'),
(2120, 17, 91, 'یا برای تو باغی از درختان خرما و انگور باشد که از لابه لای آنها نهرها روان کنی.'),
(2121, 17, 92, 'یا آسمان را آن گونه که گمان کرده ای [قدرت داری] پاره پاره بر سر ما بیفکنی، یا خدا و فرشتگان را رو به روی ما آوری.'),
(2122, 17, 93, 'یا خانه ای از طلا برای تو باشد، یا در آسمان بالا روی، و بالا رفتنت را باور نمی کنیم تا آنکه نوشته ای بر ما نازل کنی که آن را بخوانیم!! بگو: پروردگارم منزّه است [از اینکه خواسته های سبک مغزان یاوه گو را پاسخ دهد] آیا من جز بشری فرستاده هستم؟ [که مرا برای هدایت شما فرستاده اند، نه برای اینکه درخواست های بی مورد شما را پاسخ دهم.]'),
(2123, 17, 94, 'و چیزی مردم را زمانی که هدایت به سویشان آمد از ایمان آوردن باز نداشت، جز اینکه گفتند: آیا خدا بشری را به پیامبری برانگیخته است؟'),
(2124, 17, 95, 'بگو: اگر در زمین [به جای بشر] فرشتگانی بودند که با آرامی راه می رفتند، یقیناً فرشته ای را از آسمان برای آنان به پیامبری نازل می کردیم [زیرا هدایتگر هر گروهی باید از جنس خودشان باشد.]'),
(2125, 17, 96, 'بگو: کافی است که خدا میان من و شما [در صدقِ نبوتم] شاهد باشد؛ چون او به بندگانش آگاه و بیناست.'),
(2126, 17, 97, 'و هر که را خدا هدایت کند، پس او هدایت یافته است، و آنان را که خدا [به کیفر لج بازی و عنادشان] گمراه سازد، هرگز در برابر خدا برای آنان یاورانی نخواهی یافت، و آنان را روز قیامت در حالی که به رو در افتاده اند، کور و لال و کر محشور می کنیم، جایگاهشان دوزخ است، هرگاه [شعله] آتشش فرو نشیند، شعله ای دیگر بر آنان می افزاییم.'),
(2127, 17, 98, 'این کیفر آنان است، به سبب اینکه به آیات ما کافر شدند و گفتند: آیا ما وقتی استخوان [جدا از هم و ریز ریز] و پوسیده شدیم، با آفرینش جدید برانگیخته خواهیم شد؟'),
(2128, 17, 99, 'آیا ندانستند خدایی که آسمان ها و زمین را آفرید، می تواند مانند آنان را [پس از پوسیده شدن] بیافریند؟ برای آنان مدتی معین قرار داده که هیچ تردیدی در آن نیست، ولی [این] ستمکاران جز انکار [قیامت و تکذیب محشور شدن در آن] را نخواستند.'),
(2129, 17, 100, '[به این کافران و مغروران به امور مادی دلفریب] بگو: اگر شما مالک خزینه های رحمت پروردگارم [مانند باران، اموال، ارزاق و...] بودید، در آن صورت از ترس [تهی دستی و کم شدنش] از انفاق کردن آن بخل می ورزیدید؛ و انسان همواره بسیار بخیل و تنگ نظر است.'),
(2130, 17, 101, 'و به راستی به موسی آن گاه که نزد بنی اسرائیل آمد، نه معجزه روشن دادیم، بنابراین از [آگاهان] بنی اسرائیل بپرس [که آن معجزه ها چه بود؟] پس فرعون [با دیدن معجزات] به او گفت: ای موسی! بی تردید من تو را جادوشده می پندارم!!'),
(2131, 17, 102, 'موسی گفت: مسلماً تو می دانی این معجزه ها را که بصیرت آور هستند، جز پروردگار آسمان ها و زمین نازل نکرده است، و ای فرعون! قطعاً من تو را هلاک شده می پندارم.'),
(2132, 17, 103, 'پس [به دنبال این گفتگو] فرعون تصمیم گرفت آنان را از آن سرزمین برانگیزد و بیرون کند، ولی ما او و همه کسانی را که با او بودند، غرق کردیم.'),
(2133, 17, 104, 'و پس از فرعون به بنی اسرائیل گفتیم: در این سرزمین ساکن شوید، زمانی که وعده آخرت برسد، همه شما را با هم [به صحرای قیامت] می آوریم.'),
(2134, 17, 105, 'و قرآن را به حق نازل کردیم و به حق نازل شد، و تو را جز مژده رسان و بیم دهنده نفرستادیم.'),
(2135, 17, 106, 'و قرآن را [جدا جدا و] بخش بخش قرار دادیم تا آن را با درنگ و تأمل بر مردم بخوانی و آن را به تدریج [برپایه حوادث، پیش آمدها و نیازها] نازل کردیم.'),
(2136, 17, 107, 'بگو: به آن ایمان بیاورید یا نیاورید [برای آن یکسان است]، همانا کسانی که پیش از [نزول] آن معرفت و دانش یافته اند، زمانی که بر آنان می خوانند، سجده کنان به رو در می افتند.'),
(2137, 17, 108, 'و می گویند: منزّه و پاک است پروردگارمان بی تردید وعده پروردگارمان [به پاداش مؤمنان و کیفر مجرمان] قطعاً انجام شدنی است.'),
(2138, 17, 109, 'و گریه کنان به رو در می افتند و [شنیدن قرآن] بر فروتنی و خشوعشان می افزاید.'),
(2139, 17, 110, 'بگو: خدا را بخوانید یا رحمان را بخوانید، هر کدام را بخوانید [ذات یکتای او را خوانده اید] نیکوترین نام ها [که این دو نام هم از آنهاست] فقط ویژه اوست. و نماز خود را با صدای بلند و نیز با صدای آهسته مخوان و میان این دو [صدا] راهی میانه بجوی.'),
(2140, 17, 111, 'و بگو: همه ستایش ها ویژه خداست که نه فرزندی گرفته و نه در فرمانروایی شریکی دارد و نه او را به سبب ناتوانی و ذلت یار و یاوری است. و او را بسیار بزرگ شمار.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'همه ستایش ها ویژه خداست که این کتاب را بر بنده اش نازل کرد و برای آن هیچ گونه انحراف و کژی قرار نداد.'),
(2142, 18, 2, '[کتابی] است درست و استوار [و برپادارنده مصالح حیات انسان] تا از سوی خود [ستمکاران را] به عذابی سخت بیم دهد، و مؤمنانی را که کارهای شایسته انجام می دهند، مژده دهد که برای آنان پاداشی نیکوست.'),
(2143, 18, 3, 'که در آن پاداش نیکو، جاودانه ماندگارند.'),
(2144, 18, 4, 'و [نیز] کسانی را که گفتند: خدا [برای خود] فرزندی گرفته است، [به آتش دوزخ] بیم دهد.'),
(2145, 18, 5, 'نه آنان به این [سخن خرافی و بی پایه] یقین و دانشی دارند و نه پدرانشان [بلکه گفتارشان از روی جهل و نادانی است] چه بزرگ سخنی است که [از روی افترا] از دهانشان بیرون می آید؛ آنان جز دروغ نمی گویند.'),
(2146, 18, 6, 'شاید تو می خواهی اگر [این معاندان لجوج] به این سخن [که قرآن کریم است] ایمان نیاورند، خود را از شدت اندوه هلاک کنی!!'),
(2147, 18, 7, 'مسلماً ما آنچه را [از درخت، نبات، حیوان، دریا و دیگر آثار] روی زمین است، زینت زمین قرار دادیم تا آنان را آزمایش کنیم که کدامشان از جهت عمل نیکوترند.'),
(2148, 18, 8, 'و بی تردید ما آنچه را روی زمین است، سرانجام، خاک بی گیاه خواهیم کرد.'),
(2149, 18, 9, 'آیا گمان کردی که اصحاب کهف و رقیم از نشانه های شگفت انگیز ما بودند؟ [چنین نیست؛ زیرا ما را در پهن دشت هستی نشانه هایی شگفت انگیزتر از اصحاب کهف است.]'),
(2150, 18, 10, '[یاد کن] هنگامی را که [آن] جوانان در غار پناه گرفتند و گفتند: پروردگارا! رحمتی از نزد خود به ما عطا کن، و برای ما در کارمان زمینه هدایتی فراهم آور.'),
(2151, 18, 11, 'پس سالیانی چند در آن غار، خواب را بر گوش هایشان چیره ساختیم.'),
(2152, 18, 12, 'سپس آنان را [از خواب] برانگیختیم تا مشخص کنیم کدام یک از آن دو گروه، مدت درنگشان را [در غار] به شمار می آورند؟'),
(2153, 18, 13, 'ما خبر [عبرت آموز] شان را به حق و درستی برای تو بیان می کنیم: آنان جوانمردانی بودند که به پروردگارشان ایمان آوردند، و ما بر هدایتشان افزودیم.'),
(2154, 18, 14, 'و دل هایشان را [با یقین به حقایق،] محکم و استوار ساختیم؛ آن گاه [که در برابر شرک و بت پرستی] به پا خاستند و گفتند: پروردگار ما پروردگار آسمان ها و زمین است، هرگز جز او معبودی را نمی پرستیم که اگر بپرستیم سخنی گزاف و دور از حق گفته ایم: [که با خدا معبودی دیگر وجود دارد.]'),
(2155, 18, 15, 'اینان قوم [نادان و بی منطق] ما هستند که به جای خدا معبودانی برگرفتند، چرا بر حقّانیّت معبودانشان دلیلی روشن نمی آورند؟ پس ستمکارتر از کسی که بر خدا دروغ بندد [که خدا دارای شریک است] کیست؟'),
(2156, 18, 16, 'و [پس از مشورت و گفتگو با یکدیگر چنین گفتند:] اکنون که از آنان و آنچه غیر خدا می پرستند، کناره گرفته اید، پس به این غار پناه گیرید تا پروردگارتان از رحمتش بر شما بگستراند و در کارتان آسایش و آسانی فراهم آورد.'),
(2157, 18, 17, 'و خورشید را می بینی که وقتی طلوع می کند، از سمت راست غارشان متمایل می شود، و وقتی غروب می کند، سمت چپشان را ترک می کند، و آنان در محل وسیعی از آن غارند [که نسیم مطبوعش آنان را فرا می گیرد]؛ این از نشانه هایِ [قدرتِ] خداست. خدا هر که را هدایت کند، راه یافته است و هر که را گمراه نماید، هرگز برای او یاور و دوست هدایت کننده ای نخواهی یافت.'),
(2158, 18, 18, 'و آنان را گمان می کنی که بیدارند، در حالی که خوابند، و آنان را به جانب راست و جانب چپ می گردانیم، و سگشان دو دستش را در آستانه غار گسترانیده است. اگر بر آنان آگاه می شدی از آنان روی برتافته و می گریختی و همه وجودت از [دیدن] آنان پر از ترس می شد.'),
(2159, 18, 19, 'و همان گونه [که با قدرت خود خوابشان کردیم، از خواب] بیدارشان نمودیم تا میان خود از یکدیگر [از حادثه اتفاق افتاده] بپرسند. گوینده ای از آنان گفت: چه مقدار [در خواب] مانده اید؟ [برخی] گفتند: یک روز یا پاره ای از روز را [در خواب] مانده ایم. [و برخی دیگر] گفتند: پروردگارتان به مقداری که [در خواب] مانده اید، داناتر است، پس یکی از خودتان را با این پولتان به شهر روانه کنید و او باید با تأمل بنگرد کدام یک [از مغازه داران شهر] غذایش پاکیزه تر است؟ پس غذایی از آن برایتان بیاورد، و او باید [در رفت، برگشت و داد و ستد] دقت و نرمی و لطف نشان دهد و احدی را از حال شما آگاه نکند.'),
(2160, 18, 20, 'زیرا اگر بر شما دست یابند، سنگسارتان می کنند یا شما را به آیین خود برمی گردانند، و در آن صورت هرگز رستگار نخواهید شد.'),
(2161, 18, 21, 'و این گونه [مردم آن شهر را به وسیله آن سکه قدیمی] از حال آنان آگاه کردیم تا بدانند که وعده خدا [در برانگیختن مردگان در روز قیامت] حق است و در برپا شدن قیامت هیچ تردیدی نیست. هنگامی که [کاشفانِ غار] میان خودشان در کار آنان نزاع و ستیز داشتند، پس [یک گروه] گفتند: ساختمانی به روی [جایگاه] آنان بنا کنید [تا از دیده ها پنهان بمانند] البته پروردگارشان به آنان داناتر است. ولی آنان که بر کارشان [نسبت به اصحاب کهف] پیروز شدند، گفتند: به یقین مسجدی بر روی [جایگاه] آنان بنا خواهیم کرد.'),
(2162, 18, 22, 'به زودی خواهند گفت: سه نفر بودند، چهارمین آنان سگشان بود. و می گویند: پنج نفر بودند، و ششمین آنان سگشان بود، در حالی که [این اظهار نظرهای بی دلیل] تیر به تاریکی انداختن است. و می گویند: هفت نفر بودند، هشتمین آنان سگشان بود. بگو: پروردگارم به شماره آنان آگاه تر است، جز اندکی کسی شماره آنان را نمی داند. پس درباره آنان بحث و مجادله مکن مگر بحث و مجادله ای ظاهر، و در مورد آنان از هیچ کس نظر مخواه.'),
(2163, 18, 23, 'و هرگز درباره چیزی مگو که من فردا آن را انجام می دهم،'),
(2164, 18, 24, 'مگر اینکه [بگویی: اگر] خدا بخواهد. و هرگاه [گفتن ان شاءالله را] از یاد بردی، پروردگات را یاد کن و بگو: امید است پروردگارم مرا به چیزی که از این به صواب و مصلحت نزدیک تر باشد، راهنمایی کند.'),
(2165, 18, 25, '[اصحاب کهف] سیصد سال در غارشان ماندند و نُه سال به آن افزودند.'),
(2166, 18, 26, 'بگو: خدا به مدتی که ماندند داناتر است، [برای اینکه] غیب آسمان ها و زمین ویژه اوست، چه بینا و چه شنواست، برای اهل آسمان ها و زمین سرپرست و یاوری جز او نیست و احدی را در فرمانروایی اش بر جهان هستی شریک نمی گیرد.'),
(2167, 18, 27, 'و آنچه از کتاب پروردگارت به تو وحی شده است، بخوان. برای کلماتش تبدیل کننده ای نیست، و هرگز جز او ملجأ و پناهی نخواهی یافت.'),
(2168, 18, 28, 'با کسانی که صبح و شام، پروردگارشان را می خوانند در حالی که همواره خشنودی او را می طلبند، خود را پایدار و شکیبا دار، و در طلب زینت و زیور زندگی دنیا دیدگانت [از التفات] به آنان [به سوی ثروتمندان] برنگردد، و از کسی که دلش را [به سبب کفر و طغیانش] از یاد خود غافل کرده ایم و از هوای نفسش پیروی کرده و کارش اسراف و زیاده روی است، اطاعت مکن.'),
(2169, 18, 29, 'و بگو: [سخن] حق [که قرآن است] فقط از سوی پروردگار شماست؛ پس هر که خواست ایمان بیاورد، و هر که خواست کافر شود، به یقین ما برای ستمکاران آتشی آماده کرده ایم که سراپرده هایش بر آنان احاطه دارد، و اگر [از شدت تشنگی] استغاثه کنند با آبی چون مس گداخته که چهره ها را بریان می کند [به استغاثه آنان] جواب گویند، بد آشامیدنی و بد جایگاهی است.'),
(2170, 18, 30, 'مسلماً کسانی که ایمان آوردند و کارهای شایسته انجام دادند [پاداششان داده خواهد شد] زیرا ما پاداش کسانی را که کار نیکو کرده اند، تباه نمی کنیم.'),
(2171, 18, 31, 'برای آنان بهشت های جاودانی است که از زیرِ درختانِ [آن] نهرها جاری است، در حالی که در آنجا بر تخت ها تکیه دارند با دستبندهایی از طلا آراسته می شوند، و جامه هایی سبز از دیبای نازک و سِتبر می پوشند، چه پاداش خوبی و چه آسایش گاه نیکویی.'),
(2172, 18, 32, 'و برای مشرکان [که از معبودان باطل دم می زنند و دل به دنیای فانی بسته اند] مَثَلی بزن: دو مرد را که به یکی از آنان دو باغ انگور دادیم و اطراف آن دو باغ را با درختان خرما پوشاندیم و فاصله میان آن دو باغ را کشتزاری قرار دادیم؛'),
(2173, 18, 33, 'هر یک از این دو باغ میوه اش را می داد و چیزی از آن میوه نمی کاست، و میان آن دو باغ، نهری [پر آب] روان کردیم.'),
(2174, 18, 34, 'و برای او میوه [فراوان] بود، پس [مغرورانه] به رفیقش در حالی که با او گفتگو می کرد، گفت: من مال و ثروتم از تو بیشتر است و از جهت نَفَرات نیرومندترم.'),
(2175, 18, 35, 'و در حالی که [به سبب غرور و عصیان] بر خویشتن ستمکار بود، به باغش وارد شد [و] گفت: گمان نمی برم که این باغ هرگز نابود شود؛'),
(2176, 18, 36, 'و گمان نمی کنم که قیامت برپا شود، و اگر هم [بر فرض] به پروردگارم بازگردانده شوم، یقیناً در بازگشت، جایگاهی بهتر از این را خواهم یافت.'),
(2177, 18, 37, 'رفیقش در حالی که با او گفتگو می کرد به او گفت: آیا به آنکه تو را از خاک سپس از نطفه آفرید، آن گاه تو را به صورت مردی درست و نیکو درآورد، کافر شده ای؟'),
(2178, 18, 38, 'من [اقرار قلبی دارم که] اوست خدا، پروردگار من و هیچ کس را با پروردگارم شریک نمی گیرم؛'),
(2179, 18, 39, 'و چرا وقتی به باغ خود، وارد شدی، نگفتی: آنچه خدا بخواهد [صورت می پذیرد] و هیچ نیرویی جز به وسیله خدا نیست؛ اگر مرا از جهت ثروت و فرزند کمتر از خودت می بینی.'),
(2180, 18, 40, 'امید است پروردگارم بهتر از باغ تو را به من عطا کند و از آسمان آسیبی دقیق و حساب شده بر باغ تو فرستد تا به صورت زمینی صاف و بی گیاه و لغزنده شود؛'),
(2181, 18, 41, 'یا آب آن در زمین فرو رود که هرگز نتوانی آن را به دست آوری.'),
(2182, 18, 42, '[عاقبت آن مغرور مشرک، به عذاب خدا دچار شد] و تمام میوه هایش از بین رفت، پس در حالی که همه داربست ها فرو ریخته [و تاک ها روی آن بود] و در [حسرت] هزینه های [فراوانی که] متحمل شده بود، دو دستش را زیر و رو می کرد، می گفت: ای کاش من احدی را با پروردگارم شریک نگرفته بودم.'),
(2183, 18, 43, 'و برایش گروهی نبود که او را در برابر خدا یاری دهند، و خودش هم قدرت نداشت که عذاب را از خود برطرف کند.'),
(2184, 18, 44, 'در آنجا [که گرفتاری از همه سو انسان را احاطه می کند] یاری دادن ویژه خدای به حقّ است. او از جهت پاداش بهتر و از نظر تأمین کردن عاقبت نیکوتر است.'),
(2185, 18, 45, 'و زندگی دنیا را برای آنان وصف کن که [در سرعت زوال و ناپایداری] مانند آبی است که آن را از آسمان نازل کنیم، پس گیاه زمین به وسیله آن [به طور انبوه و در هم پیچیده] بروید، [و طراوت و سرسبزی شگفت انگیزی پدید آورد] آن گاه [در مدتی کوتاه] خشک و ریز ریز شود که بادها آن را به هر سو پراکنده کنند؛ و خدا بر هر کاری تواناست.'),
(2186, 18, 46, 'مال و فرزندان، آرایش و زیور زندگی دنیا هستند، ولی اعمال شایسته پایدار نزد پروردگارت از جهت پاداش بهتر و از لحاظ امید داشتن به آنها نیکوتر است.'),
(2187, 18, 47, 'و [یاد کن] روزی را که کوه ها را [از محل استقرارشان] برانیم و نابود کنیم، و زمین را هموار و آشکار ببینی، و همه آنان را [برای ورود به عرصه قیامت] محشور می کنیم، و هیچ یک از آنان را وانمی گذاریم.'),
(2188, 18, 48, 'و صف کشیده بر پروردگارت عرضه می شوند [به آنان گویند:] همانا [تنها] نزد ما آمدید، همان گونه که نخستین بار شما را [تنها] آفریدیم [شما در توانمندی ما نسبت به زنده کردن مردگان تردید داشتید]، بلکه می پنداشتید برای حسابرسی اعمال شما موعدی قرار نخواهیم داد.'),
(2189, 18, 49, 'کتاب [اعمال] هر کسی [در برابر دیدگانش] نهاده می شود، پس مجرمان را می بینی که از آنچه در آن است هراسان و بیمناکند و می گویند: ای وای بر ما، این چه کتابی است که هیچ عمل کوچک و بزرگی را فرو نگذاشته است مگر آنکه آن را به حساب آورده؟! و هر عملی را انجام داده اند، حاضر می یابند، و پروردگارت به هیچ کس ستم نخواهدکرد.'),
(2190, 18, 50, 'و [یاد کن] هنگامی را که به فرشتگان گفتیم: برای آدم سجده کنید. پس همه سجده کردند، جز ابلیس که از گروه جن بود، پس او از دایره فرمان پروردگارش بیرون رفت. [با این حال] آیا او و نسلش را به جای من سرپرست و یاور خود می گیرید، در حالی که آنها دشمن شمایند؟! [ابلیس ونسلش] برای ستمکاران، بد جایگزینی [به جای خدا] هستند.'),
(2191, 18, 51, 'من ابلیس و نسلش را در آفرینش آسمان ها و [در پدید آوردن]، زمین و در آفرینش خودشان شاهد و گواه نگرفتم [تا یاریم دهند]؛ و من گمراه کنندگان را یار و مددکار خود نگرفته ام.'),
(2192, 18, 52, 'و [یاد کن] روزی را که [خدا به مشرکان] می گوید: کسانی [چون فرشتگان، جنّ و بت ها را] که می پنداشتید شریکان من [در قدرت و ربوبیّت] هستند، صدا بزنید [تا شما را از عذاب نجات دهند] پس آنان را صدا می زنند، ولی پاسخشان را نمی دهند و میان آنان و معبودهایشان هلاکت گاهی قرار می دهیم [که هر نوع رابطه ای را بین آنان ناممکن خواهد ساخت.]'),
(2193, 18, 53, 'مجرمان، آتش را می بینند ویقین می کنند که در آن خواهند افتاد و راه بازگشتی از آن نمی یابند.'),
(2194, 18, 54, 'بی تردید در این قرآن از هر گونه مثلی برای مردم بیان کردیم [ولی مردم به انکار آنها برخاستند]؛ و انسان از هر چیزی [با اصرار بر یک اعتقاد بی پایه] برخوردش [خصمانه تر و] ستیزه جو تر است.'),
(2195, 18, 55, 'و هنگامی که هدایت به سوی مردم آمد، چیزی مانع ایمان آوردن آنان و درخواست آمرزش از پروردگارشان نشد، مگر [کبر و دشمنی آنان با حق که در چنان فضایی آکنده از کبر و دشمنی گویی انتظار می بردند] که روش های [جاری ما در عذاب] پیشینیان [متکبّر کفر پیشه] به سراغشان آید، یا عذاب رویاروی به آنان رسد [تا آن زمان ایمان بیاورند و درخواست آمرزش کنند.]'),
(2196, 18, 56, 'و ما پیامبران را جز مژده رسان و بیم دهنده نمی فرستیم، و آنان که کافرند به وسیله سخن باطل، ستیزه جویی و نزاع می کنند تا به وسیله آن حق را تباه کنند! و آیات من و عذاب هایی که به آن بیمشان داده اند، به مسخره گرفتند.'),
(2197, 18, 57, 'و کیست ستمکارتر از کسی که به وسیله آیات پروردگارش پند داده شود، ولی از آنها روی برگرداند و دست آورد پیشین خود را [که کفر، جدال و ستیز با حق است] فراموش کند؟ به راستی ما بر دل های آنان پوشش هایی قرار داده ایم تا قرآن را نفهمند، و در گوش هایشان سنگینی نهادیم [تا آن را نشنوند]؛ و اگر آنان را به راه هدایت فراخوانی، هرگز و هیچ گاه هدایت نیابند.'),
(2198, 18, 58, 'و پروردگارت بسیار آمرزنده [و] صاحب رحمت است. اگر می خواست آنان را به کیفر گناهانی که مرتکب شده اند، مؤاخذه کند، قطعاً در عذابشان شتاب می نمود [اما چنین نمی کند]، بلکه آنان را وعده گاهی است که هرگز در برابر آن کمترین پناهگاهی نخواهند یافت.'),
(2199, 18, 59, 'و آن شهرها [یی که داستانشان را بر تو خواندیم] هنگامی که اهل آنها [با کفر و تکذیب آیات خدا] به خود ستم ورزیدند، هلاکشان کردیم، و برای هلاکتشان وقتی معلوم مقرّر نمودیم.'),
(2200, 18, 60, 'و [یاد کن] هنگامی را که موسی به جوان [خدمت گزار] خود گفت: همواره می روم تا به محل برخورد آن دو دریا برسم [چه اینکه زود برسم] یا روزگاری طولانی به سفرم ادامه دهم، [در هر حال می روم تا برای تحصیل دانش بیشتر، عبد صالح حق را بیابم.]'),
(2201, 18, 61, 'پس هنگامی که به محل برخورد دو دریا رسیدند، ماهی خود را [که برای خوردن فراهم کرده بودند] از یاد بردند، [آن] ماهی راه خود را [به طور سرازیر] در دریا پیش گرفت.'),
(2202, 18, 62, 'وقتی [از آنجا] گذشتند، موسی به خدمت گزارش گفت: غذای صبح گاهی ما را بیاور که از این سفرمان سختی و خستگیِ بسیار دیدیم.'),
(2203, 18, 63, 'خدمت گزارش گفت: آیا ندانستی چون کنار آن سنگ جای گرفتیم، من ماهی را از یاد بردم و آن با وضعی شگفت انگیز راه خود را در دریا گرفت و رفت، و جز شیطان از خاطرم نبرد که آن را به یاد داشته باشم.'),
(2204, 18, 64, 'موسی گفت: این [جای فراموش کردن ماهی] همان است که ما در طلبش بودیم. پس با پی گرفتن جای پای خود [از راهی که آمده بودند] برگشتند.'),
(2205, 18, 65, 'پس بنده ای از بندگان ما را یافتند که او را از نزد خود رحمتی داده و از پیشگاه خود دانشی ویژه به او آموخته بودیم.'),
(2206, 18, 66, 'موسی به او گفت: آیا [اذن می دهی که] من تو را [به این هدف] پیروی کنم که از آنچه به تو آموخته اند، مایه رشدی به من بیاموزی؟'),
(2207, 18, 67, 'گفت: [ای موسی!] تو هرگز نمی توانی بر همراهی من شکیبایی ورزی.'),
(2208, 18, 68, 'و چگونه می توانی بر چیزی که به آن احاطه [علمی] نداری شکیبایی ورزی؟'),
(2209, 18, 69, 'گفت: اگر خدا بخواهد، مرا شکیبا خواهی یافت، و هیچ فرمانی را از تو مخالفت نخواهم کرد.'),
(2210, 18, 70, 'گفت: [ای موسی!] اگر دنبال من آمدی، از هیچ چیز از من مپرس تا خودم درباره [حقیقتِ] آن با تو آغاز سخن کنم.'),
(2211, 18, 71, 'پس هر دو به راه افتادند تا وقتی که در کشتی سوار شدند، او شکافی در کشتی ایجاد کرد. موسی گفت: آیا آن را شکافتی تا سرنشینانش را غرق کنی؟ به راستی که کاری بسیار زشت کردی!'),
(2212, 18, 72, 'گفت: [ای موسی!] آیا نگفتم که تو هرگز نمی توانی بر همراهی من شکیبایی ورزی؟'),
(2213, 18, 73, 'گفت: مرا بر آنچه از یاد بردم، مؤاخذه مکن و در کارم به من سخت مگیر.'),
(2214, 18, 74, 'پس [هر دو] به راه افتادند تا [زمانی که] به نوجوانی برخوردند؛ پس [بنده ما] او را کشت. موسی گفت: آیا شخص بی گناهی را بدون آنکه کسی را کشته باشد، کُشتی؟ به راستی که کاری بسیار ناپسند مرتکب شدی!'),
(2215, 18, 75, 'گفت: [ای موسی!] آیا نگفتم که تو هرگز نمی توانی بر همراهی من شکیبایی کنی؟'),
(2216, 18, 76, 'گفت: بعد از این اگر چیزی از تو پرسیدم، دیگر با من مصاحبت مکن [برای آنکه] از جانب من به عذر قابل قبولی رسیده ای [و برای جدا شدن از من دلیل قاطعی داری.]'),
(2217, 18, 77, 'پس [هر دو] به راه افتادند تا هنگامی که به مردم شهری رسیدند، از مردمش خوراک خواستند و آنان از اینکه آن دو را مهمان کنند، خودداری کردند. پس در آن شهر، دیواری یافتند که می خواست فرو ریزد، پس او آن را [به گونه ای] استوار کرد [که فرو نریزد]. موسی گفت: اگر می خواستی برای تعمیر آن دیوار، مزدی می گرفتی [که برای خود خوراک فراهم آوریم.]'),
(2218, 18, 78, 'گفت: [ای موسی!] اکنون زمان جدایی میان من و توست؛ به زودی تو را به تفسیر و علت آنچه نتوانستی بر آن شکیبایی ورزی، آگاه می کنم.'),
(2219, 18, 79, 'اما آن کشتی، از بی نوایانی بود که [با آن] در دریا کار می کردند و در برابرشان پادشاهی بود که هر کشتی سالم و بی عیبی را غاصبانه تصرف می کرد، من خواستم معیوبش کنم [تا به دست آن ستم گر نیفتد.]'),
(2220, 18, 80, 'و اما [آن] نوجوان [که او را کُشتم]، پدر و مادرش مؤمن بودند، پس ترسیدم که آن دو را [در آینده] به طغیان و کفر بکشاند.'),
(2221, 18, 81, 'پس خواستیم پروردگارشان پاک تر و مهربان تر از او را به آنان عوض دهد.'),
(2222, 18, 82, 'و اما آن دیوار از دو نوجوان یتیم در این شهر بود، و زیرش گنجی متعلق به آن دو قرار داشت، و پدرشان مردی شایسته بود، پس پروردگارت خواست که آن دو یتیم به حد رشد رسند وگنجشان را به سبب مِهر [ی که] پروردگارت [به آن دو] داشت بیرون آورند؛ و من این را از پیش خود انجام ندادم. این است تفسیر و علت آنچه نتوانستی بر آن شکیبایی ورزی.'),
(2223, 18, 83, 'و از تو درباره ذوالقرنین می پرسند؛ بگو: به زودی بخشی از سرگذشت او را [به وسیله آیاتی از قرآن] برای شما می خوانم.'),
(2224, 18, 84, 'ما به او در زمین، قدرت و تمکّن دادیم و از هر چیزی [که برای رسیدن به هدف هایش نیازمند به آن بود] وسیله ای به او عطا کردیم.'),
(2225, 18, 85, 'پس [با توسل به وسیله،] راهی را [برای سفر به غرب] دنبال کرد.'),
(2226, 18, 86, 'تا زمانی که به محل غروب خورشید رسید [منظره غروب] خورشید را چنین یافت که در چشمه ای گرم و لجن آلود غروب می کند، و نزد آن قومی را یافت [که فساد و ستم می کردند]. گفتیم: ای ذوالقرنین! یا [این قوم را به کیفر فساد و ستمشان] عذاب می کنی و یا در میانشان شیوه ای نیک در پیش می گیری.'),
(2227, 18, 87, 'ذوالقرنین گفت: اما هر که [با کفر، فساد و گناه] ستم کرده، عذابش می کنیم، آن گاه به سوی پروردگارش بازگردانده می شود، پس او را عذابی سخت خواهد کرد.'),
(2228, 18, 88, 'و اما هر که ایمان آورده و کار شایسته انجام داده است، پس بهترین پاداش برای اوست، و ما هم از سوی خود تکلیفی آسان به او خواهیم داد.'),
(2229, 18, 89, 'پس [باز هم با توسل به وسیله] راهی را [برای سفر به شرق] دنبال کرد.'),
(2230, 18, 90, 'تا زمانی که به محل طلوع خورشید رسید، آن را یافت که بر قومی طلوع می کند که در برابر آن پوششی [از مسکن و لباس] برای آنان قرار نداده ایم.'),
(2231, 18, 91, '[سرگذشت ذوالقرنین و ملت ها] این گونه [بود]، و یقیناً ما به آنچه [از وسایل و امکانات مادی و معنوی] نزد او بود، احاطه [علمی] داشتیم.'),
(2232, 18, 92, 'سپس [با توسل به وسیله،] راهی را [برای سفر دیگر] دنبال کرد.'),
(2233, 18, 93, 'تا زمانی که میان دو کوه رسید، نزد آن دو کوه، قومی را یافت که هیچ سخنی را به آسانی نمی فهمیدند.'),
(2234, 18, 94, '[آنان با رمز، اشاره و با هر وسیله ای که ممکن بود] گفتند: ای ذوالقرنین! یأجوج و مأجوج [با کشتن و غارت و تخریب] در این سرزمین فساد می کنند؛ آیا می پذیری که ما مزدی برایت قرار دهیم تا میان ما و آنان سدّی بسازی؟'),
(2235, 18, 95, 'گفت: آنچه پروردگارم مرا در آن تمکّن و قدرت داده [از مزد شما] بهتر است؛ پس شما مرا با نیرویی یاری دهید [تا] میان شما و آنان سدّی سخت و استوار قرار دهم.'),
(2236, 18, 96, 'برای من قطعه های [بزرگ] آهن بیاورید [و در شکاف این دو کوه بریزید، پس آوردند و ریختند] تا زمانی که میان آن دو کوه را هم سطح و برابر کرد، گفت: [در کوره ها] بدمید تا وقتی که آن [قطعه های آهن] را چون آتش سرخ کرد. گفت: برایم مس گداخته شده بیاورید تا روی آن بریزم.'),
(2237, 18, 97, '[هنگامی که سد ساخته شد] یأجوج و مأجوج نتوانستند بر آن بالا روند، و نتوانستند در آن رخنه ای وارد کنند.'),
(2238, 18, 98, '[ذوالقرنین پس از پایان یافتن کار سد] گفت: این رحمتی است از پروردگار من، ولی زمانی که وعده پروردگارم فرا رسد، آن را درهم کوبد [و به صورت خاکی مساوی با زمین قرار دهد]، و وعده پروردگارم حق است.'),
(2239, 18, 99, 'و در آن روز که برخی با برخی دیگر درهم و مخلوط، موج می زنند، رهایشان می کنیم و در صور دمیده شود، پس همه آنان را [در عرصه قیامت] گرد می آوریم.'),
(2240, 18, 100, 'و دوزخ را در آن روز [به صورتی بسیار ترسناک] برای کافران آشکار می کنیم.'),
(2241, 18, 101, 'همان کسانی که دیده [بصیرت] شان از یاد من در پرده [غفلت] بود، و نمی توانستند [سخن خدا را] بشنوند.'),
(2242, 18, 102, 'آیا کسانی که کافرند، پنداشته اند که بندگانم را، به جای من سرپرستان خود می گیرند [برای آنان در دنیا و آخرت کاری انجام می دهند؟! چنین نیست قطعاً] ما دوزخ را برای پذیرایی کافران آماده کرده ایم.'),
(2243, 18, 103, 'بگو: آیا شما را از زیانکارترین مردم از جهت عمل آگاه کنم؟'),
(2244, 18, 104, '[آنان] کسانی هستند که کوششان در زندگی دنیا به هدر رفته [و گم شده است] در حالی که خود می پندارند، خوب عمل می کنند.'),
(2245, 18, 105, 'آنان کسانی هستند که آیات پروردگارشان و دیدار [قیامت و محاسبه اعمال] را به وسیله او منکر شدند، در نتیجه اعمالشان تباه و بی اثر شده است، پس روز قیامت میزانی برای [محاسبه اعمال] آنان برپا نمی کنیم.'),
(2246, 18, 106, 'این است [وضع و حال زیانکاران] به سبب آنکه کفر ورزیدند و آیات من و پیامبرانم را به مسخره گرفتند.'),
(2247, 18, 107, 'مسلماً کسانی که ایمان آورده و کارهای شایسته انجام داده اند، بهشت های فردوس جای پذیرایی آنان است.'),
(2248, 18, 108, 'در آن جاودانه اند و از آن درخواست انتقال به جای دیگر نمی کنند.'),
(2249, 18, 109, 'بگو: اگر دریا برای [نوشتن] کلمات پروردگارم [که مخلوقات او هستند] مرکب شود، پیش از آنکه کلمات پروردگارم پایان یابد، یقیناً دریا پایان می یابد، و اگرچه مانند آن دریا را به کمک بیاوریم.'),
(2250, 18, 110, 'بگو: جز این نیست که من هم بشری مانند شمایم که به من وحی می شود که معبود شما فقط خدای یکتاست؛ پس کسی که دیدار [پاداش و مقام قرب] پروردگارش را امید دارد، پس باید کاری شایسته انجام دهد و هیچ کس را در پرستش پروردگارش شریک نکند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کهیعص'),
(2252, 19, 2, '[آیاتی که بر تو خوانده می شود] یاد رحمت پروردگارت بر بنده اش زکریاست.'),
(2253, 19, 3, 'هنگامی که پروردگارش را با دعایی پنهان خواند،'),
(2254, 19, 4, 'گفت: پروردگارا! به راستی استخوانم سست شده و [مویِ] سرم از پیری سپید گشته، و پروردگارا! هیچ گاه درباره دعا به پیشگاهت [از اجابت] محروم و بی بهره نبودم.'),
(2255, 19, 5, 'و همانا من پس از خود از خویشاوندانم بیمناکم، و همسرم [از شروع زندگی] نازا بوده است، پس مرا از سوی خود فرزندی عطا کن.'),
(2256, 19, 6, 'که از من و خاندان یعقوب ارث ببرد، و او را پروردگارا! [از هر جهت] مورد رضایت [خود] قرار ده.'),
(2257, 19, 7, 'ای زکریا! ما تو را به پسری که نامش یحیی است، مژده می دهیم، [و] پیش از این همنامی برای او قرار نداده ایم.'),
(2258, 19, 8, 'گفت: پروردگارا! چگونه برایم پسری خواهد بود در حالی که همسرم نازا بوده است و خود نیز از پیری به فرتوتی رسیده ام؟!'),
(2259, 19, 9, '[فرشته وحی به او] گفت: چنین است [که گفتی، ولی] پروردگارت فرمود: این [کار] بر من آسان است، و همانا تو را پیش از این در حالی که چیزی نبودی آفریدم.'),
(2260, 19, 10, 'گفت: پروردگارا! برای من نشانه ای قرار ده. گفت: نشانه تو این است که سه شبانه روز در حالی که سالم هستی، قدرت سخن گفتن با مردم نخواهی داشت.'),
(2261, 19, 11, 'پس [به دنبال ناتوانی از سخن گفتن] از عبادتگاهش بر قومش درآمد و به آنان اشاره کرد که صبح و شام خدا را تسبیح گویید.'),
(2262, 19, 12, 'ای یحیی! کتاب را به قوت و نیرومندی بگیر؛ و به او در حالی که کودک بود، حکمت دادیم.'),
(2263, 19, 13, 'و از سوی خود مهرورزی [به همه مردم] و پاکی و شایستگی [به او عطا کردیم] و او همواره پرهیزکار بود.'),
(2264, 19, 14, 'و به پدر و مادرش نیکوکار بود و سرکش و نافرمان نبود.'),
(2265, 19, 15, 'و بر او سلام باد روزی که زاده شد، و روزی که می میرد، و روزی که زنده برانگیخته می شود.'),
(2266, 19, 16, 'و در این کتاب [سرگذشتِ] مریم را یاد کن هنگامی که از خانواده اش [در] مکان شرقی کناره گرفت.'),
(2267, 19, 17, 'و جدا از آنان پوشش و پرده ای برای خود قرار داد. و ما روح خود را به سوی او فرستادیم، پس برای او [به صورت] بشری خوش اندام و معتدل نمودار شد.'),
(2268, 19, 18, 'مریم گفت: همانا من از تو به [خدای] رحمان پناه می برم، اگر پرهیزکار هستی [از من فاصله بگیر.]'),
(2269, 19, 19, 'گفت: جز این نیست که من فرستاده پروردگار تو هستم برای آنکه پسری پاک و پاکیزه به تو ببخشم.'),
(2270, 19, 20, 'گفت: چگونه برای من پسری خواهد بود در حالی که نه هیچ بشری با من ازدواج کرده است و نه بدکار بوده ام.'),
(2271, 19, 21, 'گفت: چنین است [که گفتی، ولی] پروردگارت فرموده است: این کار بر من آسان است و [فرزندی بدون ازدواج به او بخشیدن] برای این [است] که او را نشانه ای [از قدرت خود] برای مردم و رحمتی از سوی خود قرار دهیم؛ و این کار شدنی است.'),
(2272, 19, 22, 'پس به عیسی حامله شد و به خاطر او در مکانی دور کناره گرفت.'),
(2273, 19, 23, 'آن گاه درد زاییدن، او را به ناچار به جانب درخت خرما کشانید؛ [در آن حال] گفت: ای کاش پیش از این میمردم و یکسره از خاطره ها فراموش شده بودم.'),
(2274, 19, 24, 'پس کودک از زیر [پای] او ندایش داد: غمگین مباش که پروردگارت از زیر [پای] تو نهر آبی پدید آورده است [تا بیاشامی و شستشو کنی.]'),
(2275, 19, 25, 'و تنه خرما را به سوی خود بجنبان تا برایت خرمای تازه و از بار چیده بریزد.'),
(2276, 19, 26, 'پس [از آن خرما] بخور و [از آن نهر] بیاشام و خاطرت را شاد و خوش دار، و اگر از مردم کسی را دیدی [که درباره نوزادت پرسید] بگو: من برای [خدای] رحمان روزه [سکوت] نذر کرده ام، پس هرگز امروز با هیچ انسانی سخن نخواهم گفت.'),
(2277, 19, 27, 'آن گاه نوزاد را در حالی که [در آغوشش] حمل می کرد، نزد قومش آورد. گفتند: ای مریم! به راستی که تو کاری شگفت [و بی سابقه و ناپسندی] مرتکب شده ای.'),
(2278, 19, 28, 'ای خواهر هارون! نه پدرت مرد بدی بود و نه مادرت بدکاره بود [این طفل را از کجا آورده ای؟]'),
(2279, 19, 29, 'پس مریم به نوزاد اشاره کرد [که از او بپرسید.] گفتند: چگونه با کودکی که در گهواره است، سخن بگوییم؟!'),
(2280, 19, 30, 'نوزاد [از میان گهواره] گفت: بی تردید من بنده خدایم، به من کتاب عطا کرده و مرا پیامبر قرار داده است.'),
(2281, 19, 31, 'و هر جا که باشم بسیار بابرکت و سودمندم قرار داده، و مرا تا زنده ام به نماز و زکات سفارش کرده است.'),
(2282, 19, 32, 'و مرا نسبت به مادرم نیکوکار [و خوش رفتار] گردانیده و گردنکش و تیره بختم قرار نداده است.'),
(2283, 19, 33, 'و سلام بر من روزی که زاده شدم، و روزی که می میرم، و روزی که زنده برانگیخته می شوم.'),
(2284, 19, 34, 'این است عیسی بن مریم، همان قول حق که [یهود و نصاری] درباره او در تردیدند.'),
(2285, 19, 35, 'خدا را نسزد که هیچ فرزندی [برای خود] برگیرد؛ او منزّه است؛ چون پدید آمدن چیزی را اراده کند، فقط به آن می گوید: باش، پس بی درنگ موجود می شود.'),
(2286, 19, 36, '[عیسی گفت:] بی تردید خدا [یِ یکتا] پروردگار من و شماست؛ پس او را بپرستید [که] این راهی راست است.'),
(2287, 19, 37, 'ولی گروه هایی از میان آنان [درباره عیسی] اختلاف کردند، پس وای بر کسانی که [با افراط در شأن عیسی] کافر شدند از حضور در روزی بزرگ!'),
(2288, 19, 38, 'آنان روزی که به سوی ما می آیند [نسبت به حقایق] چه شنوا و چه بینا می شوند، ولی امروز ستمکاران در گمراهی آشکارند.'),
(2289, 19, 39, 'و آنان را از روز حسرت ـ آن گاه که کار از کار بگذرد ـ بترسان، در حالی که آنان در بی خبری [شدیدی] هستند و ایمان نمی آورند.'),
(2290, 19, 40, 'یقیناً ماییم که زمین و همه کسانی را که روی آن قرار دارند به میراث می بریم و [همه] به سوی ما بازگردانده می شوند.'),
(2291, 19, 41, 'و در این کتاب، [سرگذشتِ] ابراهیم را یاد کن، یقیناً او بسیار راستگو و پیامبر بود.'),
(2292, 19, 42, 'آن گاه که به پدر [خوانده] اَش گفت: پدرم! چرا چیزی را که نمی شنود و نمی بیند و نمی تواند هیچ آسیب و گزندی را از تو برطرف کند، می پرستی!؟'),
(2293, 19, 43, 'پدرم! همانا برای من [از طریق وحی] دانشی آمده که تو را نیامده؛ بنابراین از من پیروی کن تا تو را به راهی راست راهنمایی کنم.'),
(2294, 19, 44, 'پدرم! شیطان را مپرست؛ زیرا شیطان همواره نسبت به خدا نافرمان است.'),
(2295, 19, 45, 'پدرم! به یقین می ترسم که عذابی از سوی [خدای] رحمان به تو برسد، و در نتیجه همنشین شیطان شوی.'),
(2296, 19, 46, 'گفت: ای ابراهیم! آیا تو از معبودهای من روی گردانی؟ اگر [از بت ستیزی] باز نایستی، قطعاً تو را سنگسار می کنم، و [تا از من آسیبی به تو نرسیده] زمانی طولانی از من دور شو.'),
(2297, 19, 47, 'ابراهیم گفت: سلام بر تو، به زودی از پروردگارم برای تو آمرزش می خواهم؛ زیرا او همواره نسبت به من بسیار نیکوکار و مهربان است.'),
(2298, 19, 48, 'و من از شما و معبودانی که به جای خدا می خوانید، کناره می گیرم و پروردگارم را می خوانم، امید است در خواندن پروردگارم [از اجابت او] محروم و بی بهره نباشم.'),
(2299, 19, 49, 'پس هنگامی که از آنان و آنچه جز خدا می پرستیدند، کناره گرفت، اسحاق ویعقوب را به او بخشیدیم وهر یک را پیامبر قرار دادیم.'),
(2300, 19, 50, 'و به آنان از رحمت خود بخشیدیم، وبرای آنان [در میان مردم] نامی نیک و ستایشی والامرتبه قرار دادیم.'),
(2301, 19, 51, 'و در این کتاب، [سرگذشتِ] موسی را یاد کن، بی تردید او انسانی خالص شده و فرستاده ای پیامبر بود.'),
(2302, 19, 52, 'و او را از جانب راست طور ندا کردیم، و او را در حالی که با وی راز گفتیم، مقرّب خود قرار دادیم.'),
(2303, 19, 53, 'و از رحمت خود برادرش هارون را که دارای مقام پیامبری بود، به او بخشیدیم.'),
(2304, 19, 54, 'و در این کتاب، [سرگذشتِ] اسماعیل را یاد کن، که او وفا کننده به عهد و فرستاده ای پیامبر بود.'),
(2305, 19, 55, 'و همواره خانواده اش را به نماز و زکات فرمان می داد، و نزد پروردگارش پسندیده بود.'),
(2306, 19, 56, 'و در این کتاب، [سرگذشتِ] ادریس را یاد کن، که او بسیار راستگو و پیامبر بود.'),
(2307, 19, 57, 'و او را به جایگاه و مقام بلندی ارتقا دادیم.'),
(2308, 19, 58, 'اینان کسانی از پیامبران بودند که خدا به آنان نعمت داد، از نسل آدم و از نسل کسانی که با نوح در کشتی سوار کردیم و از نسل ابراهیم و اسرائیل و از کسانی که آنان را هدایت کردیم و برگزیدیم؛ هنگامی که آیات [خدایِ] رحمان بر آنان خوانده می شد، سجده کنان و گریان به رو می افتادند.'),
(2309, 19, 59, 'سپس بعد از آنان نسلی جایگزین [آنان] شد که نماز را ضایع کردند و از شهوات پیروی نمودند؛ پس [کیفر] گمراهی خود را [که عذابی دردناک است] خواهند دید.'),
(2310, 19, 60, 'مگر آنان که توبه کرده و ایمان آورده و کار شایسته انجام داده اند، پس آنان به بهشت درآیند و ذرّه ای مورد ستم قرار نمی گیرند.'),
(2311, 19, 61, 'بهشت های جاویدی که [خدای] رحمان به بندگانش وعده داده در حالی که اکنون از نظرها پنهان است، یقیناً وعده خدا آمدنی است.'),
(2312, 19, 62, 'در آنجا کلام لغو و بیهوده ای نمی شنوند، بلکه آنچه می شنوند فقط سلام و درود است؛ و در آنجا صبح و شام، رزقشان برای آنان [آماده] است.'),
(2313, 19, 63, 'این است بهشتی که به هر کس از بندگان خود که پرهیزکار باشد، به میراث می دهیم.'),
(2314, 19, 64, 'و [ای رسول خدا! ما فرشتگان] جز به فرمان پرودگارت نازل نمی شویم. [آگاهی به] آنچه مربوط به آینده ماست و آنچه مربوط به گذشته ماست وآنچه میان گذشته و آینده است، ویژه اوست؛ و پروردگارت هیچ گاه فراموشکار نیست.'),
(2315, 19, 65, '[اوست] پروردگار آسمان ها و زمین و آنچه میان آن دو قرار دارد؛ پس او را بِپرَست و بر پرستیدنش شکیبا باش. آیا برای او همنامی می دانی [که او هم پروردگار آسمان ها و زمین باشد؟!]'),
(2316, 19, 66, 'و انسان می گوید: آیا زمانی که بمیرم به راستی زنده [از خاک] بیرونم می آورند؟'),
(2317, 19, 67, 'آیا انسان به یاد نمی آورد [و توجه ندارد] که ما او را پیش از این در حالی که چیزی نبود، آفریدیم.'),
(2318, 19, 68, 'پس به پروردگارت سوگند، آنان را [که بدون دلیل و از روی جهل و نادانی منکر زنده شدن مردگانند] با شیاطین محشور می کنیم، سپس آنان را در حالی که به زانو درآمده اند، گرداگرد دوزخ حاضر خواهیم کرد.'),
(2319, 19, 69, 'آن گاه از هر گروهی، آنان که بر خدای رحمان عاصی و سرکش تر بوده اند، بیرون می کشیم.'),
(2320, 19, 70, 'سپس ما به کسانی که برای سوختن در دوزخ سزاوارترند، داناتریم.'),
(2321, 19, 71, 'و هیچ کس از شما نیست مگر آنکه وارد دوزخ می شود، [ورود همگان به دوزخ] بر پروردگارت مسلّم و حتمی است.'),
(2322, 19, 72, 'سپس آنان را که [از کفر، شرک، فساد و گناه] پرهیز کردند، نجات می دهیم، و ستمکاران را که به زانو درافتاده اند، در دوزخ رها می کنیم.'),
(2323, 19, 73, 'و هنگامی که آیات روشن ما را بر آنان می خوانند، کافران به آنان که ایمان آورده اند، می گویند: کدام یک از ما دو گروه، جایگاهش [از جهت مادی] بهتر و مجلس اُنسش [از لحاظ اجتماعی و سیاسی] نیکوتر است؟'),
(2324, 19, 74, 'و چه بسیار اقوامی را پیش از آنان هلاک کردیم که لوازم زندگی بهتر [و منظر] و ظاهری [خوش نماتر و] نیکوتر داشتند.'),
(2325, 19, 75, 'بگو: آنان که در گمراهی قرار دارند، باید [خدای] رحمان [بر پایه سنت جاری خود] به آنان مهلتی معین دهد، تا زمانی که آنچه را به آنان وعده داده اند ببینند، یا عقوبت و شکنجه [در دنیا] را یا عذاب قیامت را؛ پس به زودی خواهند دانست چه کسی جایگاهش بدتر و سپاهش ناتوان تر است؟!'),
(2326, 19, 76, 'و آنان که هدایت یافته اند، خدا بر هدایتشان می افزاید، و اعمال شایسته پایدار نزد پروردگارت از جهت پاداش بهتر و از لحاظ بازدهی نیکوتر است.'),
(2327, 19, 77, 'آیا دیدی آن کسی را که به آیات ما کافر شد وگفت: [به خاطر انتخاب آیین شرک و کفر] به من مال و اولاد بسیار خواهند داد؟'),
(2328, 19, 78, 'آیا [او درباره رسیدن به مال و اولاد بسیار] بر غیب آگاهی یافته، یا پیمانی [بر این عطای فراوان] از سوی [خدای] رحمان دریافت نموده است؟'),
(2329, 19, 79, 'این چنین نیست [که او می گوید]، ما به زودی آنچه را می گوید [در پرونده عملش] خواهیم نوشت و عذابش را همواره ادامه خواهیم داد.'),
(2330, 19, 80, 'و آنچه را که [از مال و فرزند] می گوید، از او به میراث می بریم، و تنها نزد ما خواهد آمد.'),
(2331, 19, 81, 'و به جای خدا معبودانی گرفتند تا برای آنان مایه عزت و توانمندی باشد.'),
(2332, 19, 82, 'چنین نیست، به زودی [آن معبودان] پرستش آنان را انکار می کنند و دشمنشان خواهند شد.'),
(2333, 19, 83, 'آیا ندانسته ای که ما شیطان ها را بر کافران فرستادیم تا آنان را به شدت به سوی گناهان و شهوات برانگیزند.'),
(2334, 19, 84, 'بنابراین بر [شکست و هلاکت] آنان شتاب مکن، ما [همه اعمال و رفتار حتی لحظات عمرشان را] به دقت شماره می کنیم [تا مهلتشان تمام شود و به کیفر آنچه همواره مرتکب می شدند، برسند.]'),
(2335, 19, 85, '[یاد کن] روزی را که پرهیزکاران را به ضیافت و میهمانی [خدای] رحمان گرد می آوریم.'),
(2336, 19, 86, 'و مجرمان را تشنه به سوی دوزخ می رانیم.'),
(2337, 19, 87, '[شفیعان در آن روز] قدرت بر شفاعت ندارند مگر کسانی [مانند پیامبران، امامان، فرشتگان و اولیا] که از نزد [خدای] رحمان، پیمانی [بر اذن شفاعت] گرفته باشند.'),
(2338, 19, 88, 'و [مشرکان] گفتند: [خدای] رحمان برای خود فرزندی گرفته است.'),
(2339, 19, 89, 'به راستی که چیزی بسیار زشت و نسبتی بس ناروا [به میان] آورده اید.'),
(2340, 19, 90, 'نزدیک است آسمان ها از این [ادعای بی اصل و سخن ناروا] پاره پاره شوند، و زمین بشکافد، و کوه ها به شدت در هم بشکنند و فرو ریزند!!'),
(2341, 19, 91, 'به خاطر آنکه برای [خدای] رحمان فرزندی ادعا کرده اند.'),
(2342, 19, 92, 'و [خدای] رحمان را نسزد که فرزندی اختیار کند.'),
(2343, 19, 93, 'هیچ کس در آسمان ها و زمین نیست مگر اینکه [ذاتاً] بنده [و مملوک] به سوی [خدای] رحمان می آید.'),
(2344, 19, 94, 'به یقین همه آنان را [با قدرت و دانشش کنترل و] احاطه نموده و درست و دقیق شماره کرده است.'),
(2345, 19, 95, 'و روز قیامت همه آنان تنها به پیشگاه او می آیند.'),
(2346, 19, 96, 'قطعاً کسانی که ایمان آورده و کارهای شایسته انجام داده اند، به زودی [خدای] رحمان برای آنان [در دل ها] محبتی قرار خواهد داد.'),
(2347, 19, 97, 'جز این نیست که ما این [قرآن] را بر زبان تو آسان ساختیم تا به وسیله آن پرهیزکاران را مژده دهی و مردم ستیزه جو را بترسانی.'),
(2348, 19, 98, 'و چه بسیار ملت ها را پیش از آنان [به سبب طغیانشان] هلاک کردیم. آیا کسی از آنان را می یابی، یا هیچ زمزمه و صدایی آهسته از آنان می شنوی؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طه'),
(2350, 20, 2, 'ما قرآن را بر تو نازل نکردیم تا به مشقت و زحمت افتی؛'),
(2351, 20, 3, '[بلکه آن را نازل کردیم] تا برای کسی که [از خدا] می ترسد، زمینه توجه و یادآوری باشد.'),
(2352, 20, 4, 'در حالی که به تدریج از سوی کسی نازل شده که زمین و آسمان های بلند را آفریده است.'),
(2353, 20, 5, '[خدای] رحمان بر تخت فرمانروایی و تدبیر امور آفرینش چیره و مسلّط است.'),
(2354, 20, 6, 'آنچه در آسمان ها و زمین و آنچه میان آن دو و آنچه زیر زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست.'),
(2355, 20, 7, 'و اگر سخن خود را با صدای بلند آشکار کنی [یا پنهان بداری، برای خدا یکسان است]؛ زیرا او پنهان وپنهان تر را می داند.'),
(2356, 20, 8, 'خدای یکتاست که جز او هیچ معبودی نیست، نیکوترین نام ها فقط ویژه اوست.'),
(2357, 20, 9, 'و آیا سرگذشت موسی به تو رسیده است؟'),
(2358, 20, 10, 'هنگامی که آتشی دید، پس به خانواده اش گفت: درنگ کنید؛ بی تردید من آتشی دیدم [می روم] شاید شعله ای از آن را برایتان بیاورم یا نزد آتش [برای پیدا کردن راه] راهنمایی بیابم.'),
(2359, 20, 11, 'پس چون به آن آتش رسید، ندا داده شد: ای موسی!'),
(2360, 20, 12, 'به یقین این منم پروردگار تو، پس کفش خود را از پایت بیفکن؛ زیرا تو در وادی مقدس طوی هستی.'),
(2361, 20, 13, 'و من تو را [به پیامبری] برگزیدم، پس به آنچه وحی می شود، گوش فرا دار.'),
(2362, 20, 14, 'همانا! من خدایم که جز من معبودی نیست، پس مرا بپرست و نماز را برای یاد من برپا دار.'),
(2363, 20, 15, 'بی تردید قیامت که می خواهم زمان وقوعش را پنهان بدارم، آمدنی است، تا هر کس را برابر تلاش و کوششی که می کند، پاداش دهند.'),
(2364, 20, 16, 'پس مبادا آنکه از هوای نفسش پیروی کرده و [به این سبب] به قیامت ایمان ندارد، تو را [از توجه به آن] باز دارد که هلاک می شوی.'),
(2365, 20, 17, 'و ای موسی! این [قطعه چوب] در دست راستت چیست؟'),
(2366, 20, 18, 'گفت: این عصای من است، بر آن تکیه می زنم، و با آن برگ درختان را برای گوسفندانم فرو می ریزم و مرا در آن نیازهای دیگری هم هست.'),
(2367, 20, 19, 'خدا فرمود: ای موسی! آن را بیفکن.'),
(2368, 20, 20, 'پس آن را افکند، ناگهان ماری عظیم شد که به سرعت می شتافت.'),
(2369, 20, 21, 'خدا فرمود: آن را بگیر و مترس که بی درنگ آن را به کیفیت اولش باز می گردانیم.'),
(2370, 20, 22, 'و دستت را در گریبانت کن تا بدون هیچ عیبی [چون برص و پیسی] سفید و درخشان بیرون آید، [آن] معجزه ای دیگر است.'),
(2371, 20, 23, 'تا برخی از بزرگ ترین معجزات خود را به تو نشان دهیم.'),
(2372, 20, 24, 'به سوی فرعون برو؛ زیرا او [در برابر خدا] سرکشی کرده است.'),
(2373, 20, 25, 'گفت: پروردگارا! سینه ام را [برای تحمل این وظیفه سنگین] گشاده گردان،'),
(2374, 20, 26, 'و کارم را برایم آسان ساز،'),
(2375, 20, 27, 'و گِرِهی را [که مانع روان سخن گفتن من است] از زبانم بگشای،'),
(2376, 20, 28, '[تا] سخنم را بفهمند،'),
(2377, 20, 29, 'و از خانواده ام دستیاری برایم قرار ده،'),
(2378, 20, 30, 'هارون، برادرم را'),
(2379, 20, 31, 'پشتم را به او محکم کن،'),
(2380, 20, 32, 'واو را در کارم شریک گردان،'),
(2381, 20, 33, 'تا تو را [در میان مشرکان از داشتن شریک] بسیار و فراوان تنزیه کنیم،'),
(2382, 20, 34, 'وبسیار به یادت باشیم،'),
(2383, 20, 35, 'بی تردید تو به ما بینایی.'),
(2384, 20, 36, 'خدا فرمود: ای موسی! به یقین خواسته ات به تو عطا شد.'),
(2385, 20, 37, 'و بی تردید یک بار دیگر هم به تو احسان کردیم.'),
(2386, 20, 38, 'آن زمان که به مادرت آنچه را که باید الهام می شد، الهام کردیم؛'),
(2387, 20, 39, 'که او را در صندوق بگذار، پس او را به دریا بینداز تا دریا او را به ساحل اندازد، تا دشمن من و دشمن او، وی را برگیرد. و محبوبیّتی از سوی خود بر تو انداختیم تا [همگان به تو علاقه و محبت ورزند و آنچه را انجام دادم برای این بود که] با مراقبت کامل من پرورش یابی [و ساخته شوی.]'),
(2388, 20, 40, 'آن گاه که خواهرت به سوی کاخ فرعون رفت، و گفت: آیا شما را به کسی که از این نوزاد سرپرستی کند، راهنمایی کنم؟ پس تو را به مادرت برگرداندیم تا خوشحال و شاد شود و غم و غصه نخورد، و کسی [از فرعونیان] را کُشتی و ما تو را از اندوه [و نگرانی بر ارتکاب قتل آن مشرک] نجات دادیم، و چنان که باید امتحانت نمودیم، پس سالیانی در میان اهل مدین ماندی، سپس ای موسی! بر اساس تقدیر الهی [برای انتخاب شدن به مقام پیامبری اینجا] آمدی،'),
(2389, 20, 41, 'و تو را برای [اجرای اهداف] خود ساختم [و انتخاب کردم.]'),
(2390, 20, 42, 'تو و برادرت با معجزات من [برای هدایت گمراهان] بروید، و درباره ذکر من [که ابلاغ وحی است] سستی نورزید.'),
(2391, 20, 43, 'هر دو به سوی فرعون بروید؛ زیرا او [در برابر خدا] سرکشی کرده است.'),
(2392, 20, 44, 'پس با گفتاری نرم به او بگویید، امید است که هوشیار شود و [آیین حق را بپذیرد] یا بترسد [و از سرکشی باز ایستد.]'),
(2393, 20, 45, 'گفتند: پروردگارا! ما می ترسیم که [پیش از دعوت کردنش به حق] با سخت گیری و شکنجه بر [ضد] ما پیشی گیرد یا بر سرکشی خود بیفزاید.'),
(2394, 20, 46, 'خدا فرمود: نترسید که من بی تردید با شما هستم [سخن او و شما را] می شنوم و [اعمالتان را] می بینم.'),
(2395, 20, 47, 'بنابراین [بدون بیم و هراس] به سوی او بروید و بگویید: ما دو نفر فرستاده پروردگار تو هستیم، پس بنی اسرائیل را [از قید بندگی و بردگی خود آزاد کرده] با ما روانه کن و آنان را شکنجه مکن، به راستی ما از سوی پروردگارت معجزه ای آورده ایم؛ و درود بر کسی باد که از هدایت [حق] پیروی کند.'),
(2396, 20, 48, 'مسلماً و یقیناً به ما وحی شده است که عذاب بر کسی است که [آیات حق و پیامبران را] تکذیب کند و از آن رو برگرداند.'),
(2397, 20, 49, 'فرعون گفت: ای موسی! پروردگار شما دو نفر کیست؟'),
(2398, 20, 50, 'گفت: پروردگار ما کسی است که به هر موجودی، آفرینش [ویژه] او را [آن گونه که سزاوارش بود] به وی عطا کرده، سپس هدایت نمود.'),
(2399, 20, 51, 'فرعون [به جای دنبال کردن بحث خداشناسی، مطلب را منحرف کرد و] گفت: پس حال امت های پیشین چگونه است؟'),
(2400, 20, 52, 'موسی گفت: علم و دانش به [حال] آنان در کتابی [مانند لوح محفوظ] نزد پروردگار من است، که پروردگارم [بر پایه آن در پاداش دادن و کیفر نمودن] نه اشتباه می کند و نه از یاد می برد.'),
(2401, 20, 53, 'همان کسی که زمین را برای شما بستری گسترده قرار داد، و برای شما در آن [به خاطر آسان شدن رفت و آمدتان] راه هایی پدید آورد، و از آسمان آبی نازل کرد، پس به وسیله آن انواعی از روییدنی های گوناگون بیرون آوردیم.'),
(2402, 20, 54, '[خود از این نعمت های فراوان] بخورید و دام هایتان را بچرانید، یقیناً در این امور نشانه هایی [بر توحید، ربوبیّت و قدرت خدا] برای صاحبان خرد است.'),
(2403, 20, 55, 'شما را از زمین آفریدیم، و به آن باز می گردانیم، و بار دیگر از آن بیرونتان می آوریم.'),
(2404, 20, 56, 'و بی تردید ما همه معجزات خود را [که در ارتباط با نبوّت موسی و هدایت مردم بود] به فرعون نشان دادیم، ولی آنها را تکذیب کرد و به پذیرفتنش رضایت نداد.'),
(2405, 20, 57, 'گفت: ای موسی! آیا به سوی ما آمده ای تا ما را به وسیله جادویت از سرزمینمان بیرون کنی؟'),
(2406, 20, 58, 'قطعاً ما جادویی مانند آن برایت می آوریم، پس میان ما و خودت در جایی هموار و صاف که نه ما از آن تخلف کنیم و نه تو، وعده گاهی قرار ده.'),
(2407, 20, 59, 'موسی گفت: وعده گاه شما روز جشن است [روزی که] همه مردم را پیش از ظهر گرد هم می آورند.'),
(2408, 20, 60, 'پس فرعون [از مجلس مناظره با موسی] برگشت، پس نیرنگش را [که جادوگران و ابزار جادویشان بود] گرد آورد، سپس به وعده گاه باز آمد.'),
(2409, 20, 61, 'موسی به فرعون و فرعونیان گفت: وای بر شما، بر خدا دروغ نبندید [که بت ها شریک اویند واین معجزات افسون وجادو است] که با عذابی شدید ریشه کن ونابودتان می کند، و یقیناً کسی که دروغ بست، محروم و نومید شد.'),
(2410, 20, 62, 'پس فرعونیان میان خود در کارشان [نسبت به موسی] نزاع و ستیز کردند و این رازگویی را از دیگران پنهان داشتند.'),
(2411, 20, 63, 'گفتند: مسلماً این دو نفر جادوگرند، می خواهند شما را با جادویشان از سرزمینتان بیرون کنند و آیین برتر شما را از بین ببرند.'),
(2412, 20, 64, 'پس حیله و نیرنگتان را گرد آورید، آن گاه صف کشیده بیایید که قطعاً امروز کسی کامیاب است که برتری یابد.'),
(2413, 20, 65, '[ساحران] گفتند: ای موسی! یا تو [عصایت را] می افکنی یا ما نخستین کسی باشیم که [ابزار و وسایل جادویش را] می افکند؟'),
(2414, 20, 66, 'گفت: بلکه شما بیفکنید. پس [چون افکندند] ناگهان ریسمان ها و چوب دستی هایشان بر اثر جادویشان در خیال، چنان وانمود شد که با سرعت به راه افتادند.'),
(2415, 20, 67, 'پس موسی در دلش ترسی احساس کرد.'),
(2416, 20, 68, 'گفتیم: مترس که بی تردید تو برتری.'),
(2417, 20, 69, 'آنچه را در دست راست داری بیفکن تا همه ساخته هایشان را ببلعد؛ یقیناً آنچه را ساخته اند [واقعیت ندارد] فقط نیرنگ جادوگر است، و جادوگر هر جا درآید، پیروز نمی شود.'),
(2418, 20, 70, '[چون عصایش را انداخت و ساخته های جادوگران را بلعید، جادوگران از عظمت کار دریافتند که امری است خدایی نه جادویی] پس سجده کنان به رو درافتادند و گفتند: ما به پروردگار هارون و موسی ایمان آوردیم.'),
(2419, 20, 71, '[فرعون] گفت: آیا پیش از آنکه به شما اذن دهم، به او ایمان آوردید؟ یقیناً او بزرگ شماست که به شما جادو آموخته است، پس بی تردید دست ها و پاهایتان را یکی از راست و یکی از چپ قطع خواهم کرد، و شما را بر تنه های درخت خرما خواهم آویخت تا کاملاً بدانید که کدام یک از ما [من یا خدای موسی] عذابش سخت تر و پایدارتر است؟!'),
(2420, 20, 72, 'گفتند: ما هرگز تو را بر دلایل روشنی که به سوی ما آمده و بر آنکه ما را آفرید، ترجیح نمی دهیم؛ پس هر حکمی را که می توانی صادر کن، تو فقط در این زندگی دنیا می توانی حکم کنی.'),
(2421, 20, 73, 'بی تردید ما به پروردگارمان ایمان آوردیم تا خطاهایمان و آن جادویی که ما را به آن واداشتی بر ما بیامرزد؛ وخدا بهتر وپایدارتر است.'),
(2422, 20, 74, 'مسلماً کسی که گنهکار به پیشگاه پروردگارش بیاید، دوزخ برای اوست که در آن نه می میرد و نه زندگی می کند.'),
(2423, 20, 75, 'و کسانی که مؤمن بیایند در حالی که کارهای شایسته انجام داده اند، برای آنان برترین درجات است.'),
(2424, 20, 76, '[و آن] بهشت های پایداری است که از زیرِ [درختانِ] آن نهرها جاری است که در آن جاودانه اند؛ و این است پاداش کسانی که خود را [با ایمان و عمل صالح از آلودگی ها] پاک کرده اند.'),
(2425, 20, 77, 'و همانا به موسی وحی کردیم که بندگانم را شبانه [از مصر] حرکت بده، و برای آنان راهی خشک در دریا قرار ده که [در آن موقعیت] نه از رسیدن فرعونیان بترسی و نه از غرق شدن بهراسی.'),
(2426, 20, 78, 'پس فرعون با لشکریانش آنان را دنبال کرد، و [بخشی] از دریا آنان را فروگرفت، چه فروگرفتنی!'),
(2427, 20, 79, 'و فرعون، قومش را گمراه کرد و راهنمایی ننمود.'),
(2428, 20, 80, 'ای بنی اسرائیل! به راستی شما را از دشمنتان نجات دادیم، و با شما در جانب راست طور وعده گذاشتیم و ترنجبین و مرغ بریان را بر شما نازل کردیم،'),
(2429, 20, 81, '[و گفتیم:] از خوردنی های پاکیزه [و مطبوع] که روزی شما نمودیم، بخورید و در [مورد] آن از حدود الهی تجاوز نکنید که خشم من بر شما فرود آید، و کسی که خشم من بر او فرود آید، یقیناً هلاک شده است.'),
(2430, 20, 82, 'و مسلماً من آمرزنده کسی هستم که توبه کرد و ایمان آورد و کار شایسته انجام داد، سپس در راه مستقیم پایداری و استقامت ورزید.'),
(2431, 20, 83, 'ای موسی! چه چیز تو را شتابان کرد که قبل از قوم خود [در آمدن به کوه طور] پیشی گرفتی؟'),
(2432, 20, 84, 'گفت: پروردگارا! من به سوی تو شتافتم تا خشنود شوی. آنان گروهی هستند که اینک به دنبال من می آیند.'),
(2433, 20, 85, '[خدا] گفت: ای موسی! ما به راستی قوم تو را پس از آمدن تو امتحان کردیم و سامری آنان را گمراه کرد.'),
(2434, 20, 86, 'پس موسی خشمگین و اندوهناک به سوی قومش بازگشت، گفت: ای قوم من! آیا پروردگارتان به شما [درباره نزول تورات] وعده ای نیکو نداد؟ آیا زمان آن وعده بر شما طولانی آمد یا خواستید که خشمی از پروردگارتان بر شما فرود آید که با وعده من مخالفت کردید؟!'),
(2435, 20, 87, 'گفتند: ما با اراده خود با وعده تو مخالفت نکردیم، بلکه ما را وادار کردند که بارهایی سنگین از زیور و زینت این قوم را حمل کنیم، پس آنان را [در آتش] انداختیم و به همین صورت سامری هم [آنچه از زیور و زینت داشت] در آتش انداخت.'),
(2436, 20, 88, 'پس برای آنان مجسمه گوساله ای که صدای گاو داشت بیرون آورد، آن گاه [او و پیروانش] گفتند: این معبود شما و معبود موسی است که [موسی آن را] فراموش کرد [به این خاطر برای طلب معبود به طور رفت.]'),
(2437, 20, 89, 'پس آیا درک نمی کنند [و نمی فهمند] که [گوساله، هنگامی که آن را می خوانند] پاسخی به آنان نمی دهد و مالک و صاحب اختیار هیچ سود و زیانی برای آنان نیست؟!'),
(2438, 20, 90, 'و به راستی هارون پیش از این به آنان گفته بود: ای قوم من! شما به وسیله این گوساله مورد امتحان قرار گرفته اید، و بی تردید پروردگارتان [خدای] رحمان است، بنابراین از من [که پیامبر او هستم] پیروی کنید و فرمانم را اطاعت نمایید.'),
(2439, 20, 91, 'گفتند: ما همواره بر پرستیدن گوساله باقی می مانیم تا موسی به سوی ما باز گردد.'),
(2440, 20, 92, '[هنگامی که موسی بازگشت، به هارون] گفت: ای هارون! وقتی دیدی آنان گمراه شدند چه چیز تو را مانع شد،'),
(2441, 20, 93, 'از اینکه مرا [در برخورد شدید با گمراهان] پیروی کنی؟ آیا از فرمان من سرپیچی کردی؟'),
(2442, 20, 94, 'گفت: ای پسر مادرم! نه ریش مرا بگیر و نه سرم را، من ترسیدم که بگویی: میان بنی اسرائیل تفرقه و جدایی انداختی و سفارش مرا [در حفظ وحدت بنی اسرائیل] رعایت نکردی.'),
(2443, 20, 95, '[موسی] گفت: ای سامری! سبب کار [بسیار خطرناک] تو چه بود؟'),
(2444, 20, 96, 'گفت: من به حقایق و اسراری آگاه شدم که آنان آگاه نشدند، پس اندکی از دانش و اسرار رسول را گرفتم و [بر اثر هواپرستی] آن را دور انداختم [و به گوساله سازی پرداختم]؛ و این گونه نفس من [آن کار بسیار خطرناک را برای گمراه کردن بنی اسرائیل] در نظرم آراست.'),
(2445, 20, 97, '[موسی] گفت: پس [از میان مردم] برو، یقیناً کیفر تو [به خاطر دور انداختن آثار رسالت و ساختن گوساله] در زندگی این است که [دچار بیماری مُسری ویژه ای شوی تا هر کس نزدیکت آید بگویی:] به من دست نزنید؛ و تو را وعده گاهی [از عذاب بسیار سخت قیامت] است که هرگز نسبت به تو از آن تخلف نخواهد شد، و [اکنون] به معبودت که همواره ملازمش بودی نگاه کن که حتماً آن را در آتش بسوزانیم، سپس سوخته اش را در دریا می پاشیم.'),
(2446, 20, 98, 'معبود شما فقط خدای یکتاست که جز او معبودی نیست، دانش او همه چیز را فرا گرفته است.'),
(2447, 20, 99, 'این گونه بخشی از اخبار گذشته را برای تو بیان می کنیم و بی تردید ذکری [چون قرآن] از نزد خود به تو عطا کردیم.'),
(2448, 20, 100, 'کسانی که از آن روی برگردانند، مسلماً روز قیامت وزر و وبال سنگینی بر دوش خواهند کشید.'),
(2449, 20, 101, 'در آن [وزر و وبال،] جاودانه اند و چه بد باری روز قیامت برای آنان خواهد بود.'),
(2450, 20, 102, '[آن] روزی که در صور دمیده می شود و گنهکاران را در آن روز، کبودچشم و نابینا محشور می کنیم.'),
(2451, 20, 103, 'در میان خودشان پنهانی و بسیار آهسته می گویند: که [در برزخ] جز ده روز درنگ نکرده اید.'),
(2452, 20, 104, 'ما به آنچه می گویند داناتریم، آن گاه که مُنصف ترینشان می گوید: جز یک روز درنگ نکرده اید،'),
(2453, 20, 105, 'و از تو درباره کوه ها می پرسند، بگو: پروردگارم آنان را ریشه کن می کند و از هم می پاشد.'),
(2454, 20, 106, 'پس آنها را به صورت دشتی هموار و صاف وامی گذارد،'),
(2455, 20, 107, 'که در آن هیچ کژی و پستی و بلندی نمی بینی.'),
(2456, 20, 108, 'در آن روز همه انسان ها دعوت کننده را که هیچ انحرافی ندارد [برای ورود به محشر] پیروی می کنند و صداها در برابر [خدای] رحمان فرو می نشیند و جز صدایی آهسته [چیزی] نمی شنوی.'),
(2457, 20, 109, 'در آن روز شفاعت کسی سودی ندهد مگر آنکه [خدای] رحمان به او اذن دهد و گفتارش را [در مورد شفاعت از دیگران] بپسندد.'),
(2458, 20, 110, '[خدا] آنچه را [از احوال و اعمال بندگان] پیش روی آنهاست و آنچه را [از ثواب و کیفر] در آینده دارند، می داند و آنان از جهت دانش به او احاطه ندارند،'),
(2459, 20, 111, 'و همه چهره ها در برابر [خدای] زنده قائم به ذات خاضع و فروتن می شوند، و کسی که بار ظلم و ستمی با خود برداشت، مسلماً محروم و ناامید شود،'),
(2460, 20, 112, 'و هر که کارهای شایسته انجام دهد، در حالی که مؤمن باشد، نه از ستمی [درباره حقّش] بیم و هراس خواهد داشت و نه از کاستی و نقصانی [در اجر و مزدش.]'),
(2461, 20, 113, 'و این گونه آن را قرآنی به زبان عربی [فصیح و گویا] نازل کردیم، و در آن هشدارها و تهدیدهای گوناگون آوردیم، امید است [از عقاید و آرای بی پایه و گناه و طغیان] بپرهیزند، یا برای آنان بیداری و هشیاری پدید آورد.'),
(2462, 20, 114, 'برتر و بلند مرتبه است خدا [یِ یگانه] که فرمانروای هستی و حقّ محض است؛ و پیش از آنکه وحی کردن قرآن بر تو پایان گیرد در خواندنش شتاب مکن، و بگو: پروردگارا! دانش مرا بیفزای.'),
(2463, 20, 115, 'یقیناً پیش از این به آدم سفارش کردیم [که از میوه آن درخت نخورد] پس فراموش کرد و عزمی استوار برای او نیافتیم.'),
(2464, 20, 116, 'و [یاد کن] هنگامی را که به فرشتگان گفتیم: برای آدم سجده کنید. همه سجده کردند مگر ابلیس که روی برتافت.'),
(2465, 20, 117, 'پس گفتیم: ای آدم! مسلماً این [ابلیس] دشمنی است برای تو و همسرت، پس شما را از بهشت بیرون نکند که در مشقت و رنج افتی.'),
(2466, 20, 118, 'قطعاً برای تو [در این مکان موقعیتی است که] است که نه گرسنه شوی و نه برهنه گردی.'),
(2467, 20, 119, 'و نه در آن تشنه شوی و نه دچار آفتاب زدگی گردی.'),
(2468, 20, 120, 'پس شیطان او را وسوسه کرد، گفت: ای آدم! آیا تو را به درخت جاودان و سلطنتی که کهنه نمی شود، راهنمایی کنم؟'),
(2469, 20, 121, 'پس هر دو [به سبب وسوسه شیطان] از میوه آن [درخت] خوردند، در نتیجه شرمگاهشان بر آنان پدیدار شد و شروع به چسباندن برگ های درختان بهشت بر [شرمگاه] خود کردند. و آدم پروردگارش را نافرمانی کرد و [از رسیدن به آنچه شیطان به او القا کرده بود] ناکام ماند.'),
(2470, 20, 122, 'آن گاه پروردگارش او را برگزید و توبه اش را پذیرفت و او را راهنمایی کرد.'),
(2471, 20, 123, '[خدا] گفت: هر دو با هم از بهشت [به سوی زمین] فرود آیید که برخی از شما دشمن برخی دیگرند، پس اگر از سوی من هدایتی به شما رسید، هر کس از هدایتم پیروی کند، نه گمراه می شود و نه به مشقت و رنج می افتد.'),
(2472, 20, 124, 'و هر کس از هدایت من [که سبب یاد نمودن از من در همه امور است] روی بگرداند، برای او زندگی تنگ [و سختی] خواهد بود، و روز قیامت او را نابینا محشور می کنیم.'),
(2473, 20, 125, 'می گوید: پروردگارا! برای چه مرا نابینا محشور کردی، در حالی که [در دنیا] بینا بودم؟'),
(2474, 20, 126, '[خدا] می گوید: همین گونه که آیات ما برای تو آمد و آنها را فراموش کردی این چنین امروز فراموش می شوی.'),
(2475, 20, 127, 'و کسی را که [از هدایت حق] تجاوز کرده و به آیات پروردگارش ایمان نیاورده، این گونه کیفر می دهیم، و بی تردید عذاب آخرت سخت تر و پایدارتر است.'),
(2476, 20, 128, 'پس آیا [این حقیقت] سبب هدایتشان نشده است که چه بسیار امت ها را پیش از آنان هلاک کردیم که [اینان] در مساکن آنان رفت و آمد می کنند؟ مسلماً در این [امور] نشانه هایی [عبرت آموز] برای صاحبان خرد است.'),
(2477, 20, 129, 'و اگر سنت و روشی از پروردگارت [نسبت به تأخیر افتادن عذاب از آنان] نگذشته بود و نیز مدتی که [برای زندگی آنان] مشخص و معین شده است، قطعاً عذاب خدا بدون تأخیر بر آنان لازم و حتم می شد.'),
(2478, 20, 130, 'پس در برابر آنچه [مشرکان] می گویند، شکیبا باش، و پیش از طلوع خورشید و پیش از غروب آن پروردگارت را همراه با سپاس و ستایش تسبیح گوی، و [نیز] در بخشی از ساعات شب و اطراف روز تسبیح گوی تا [به سنت ها و تدبیرهای او] خشنود شوی.'),
(2479, 20, 131, 'دیدگانت را به آنچه برخی از اصناف آنان را بهره مند کردیم مدوز، [آنچه به آنان داده ایم] شکوفه [بی میوه و زیور و زینت] دنیاست تا آنان را در آن بیازماییم، و رزق پروردگارت بهتر و پایدارتر است.'),
(2480, 20, 132, 'و خانواده ات را به نماز فرمان بده و خود نیز بر آن شکیبایی ورز؛ از تو رزقی نمی طلبیم، ما به تو روزی می دهیم و عاقبت نیک برای پرهیزکاری است.'),
(2481, 20, 133, 'و [مشرکان] گفتند: چرا [پیامبر] معجزه ای از سوی پروردگارش برای ما نمی آورد؟ آیا [این قرآن که] شاهد و دلیلی روشن بر [معارف] کتاب های پیشین [آسمانی است] برای آنان نیامده؟!'),
(2482, 20, 134, 'و اگر آنان را پیش از نزول قرآن هلاک می کردیم، قطعاً می گفتند: پروردگارا! چرا رسولی به سوی ما نفرستادی تا پیش از آنکه خوار و رسوا شویم، آیات تو را پیروی کنیم.'),
(2483, 20, 135, 'بگو: هر یک [از ما و شما] منتظریم، پس منتظر باشید که به زودی خواهید دانست که رهروان راهِ راست و ره یافتگان چه کسانی هستند؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'مردم را [هنگام] حسابرسی [آنچه در مدت عمرشان انجام داده اند] نزدیک شده در حالی که آنان با [فرو افتادن] در غفلت [از دلایل اثبات کننده معاد] روی گردانند.'),
(2485, 21, 2, 'هیچ یادآوری و پند تازه ای از سوی پروردگارشان برای آنان نمی آید مگر آنکه آن را می شنوند و در حالی که سرگرم بازی هستند [آن را مسخره می کنند.]'),
(2486, 21, 3, 'دل هایشان [به امور مادی، خوشگذرانی و معصیت] مشغول است؛ و آنان که ستم پیشه اند رازگویی خود را پنهان داشتند [و گفتند:] آیا این پیامبر جز این است که بشری مانند شماست؟ آیا شما با چشم باز وشناخت وآگاهی به سوی سِحر می روید؟!'),
(2487, 21, 4, '[پیامبر به آنان] گفت: [رازگویی خود را پنهان نکنید، زیرا] پروردگارم هر سخنی را در آسمان و زمین می داند، و او شنوا و داناست.'),
(2488, 21, 5, '[مشرکان] گفتند: [نه، قرآنْ سحر نیست] بلکه خواب هایی آشفته و پریشان است، [نه] بلکه آن را به دروغ بربافته، [نه] بلکه او شاعرِ [خیال پردازی] است، [اگر فرستاده خداست] باید برای ما معجزه ای بیاورد مانند معجزه هایی که پیامبران گذشته را [با آنها] فرستادند.'),
(2489, 21, 6, 'پیش از آنان [اهل] هیچ شهری که آن را نابود کردیم [با دیدن معجزه] ایمان نیاوردند؛ پس آیا اینان ایمان می آورند؟!'),
(2490, 21, 7, 'و پیش از تو [برای هدایت مردم] نفرستادیم مگر مردانی را که به آنان وحی می نمودیم. اگر نمی دانید از دانایان [به کتاب های آسمانی و آگاهان به اخبار پیشینیان] بپرسید [که همه پیامبران از جنس خود بشر بودند، نه فرشته]'),
(2491, 21, 8, 'و آنان را جسدهایی که غذا نخورند قرار ندادیم، و جاویدان هم نبودند [که از دنیا نروند.]'),
(2492, 21, 9, 'سپس به وعده ای که به آنان داده بودیم [که شکست برای دشمنان لجوج و پیروزی برای آنان است] وفا کردیم، و آنان و هر که را می خواستیم، نجات دادیم و متجاوزان [از حدود حق] را هلاک کردیم.'),
(2493, 21, 10, 'بی تردید کتابی به سوی شما نازل کردیم که مایه [شرف، بزرگواری، رشد و سعادت] شما در آن است؛ آیا نمی اندیشید.'),
(2494, 21, 11, 'و چه بسیار از شهرهایی که [اهلش] ستمکار بودند، درهم شکستیم، و پس از آنان قومی دیگرپدید آوردیم.'),
(2495, 21, 12, 'پس هنگامی که عذاب ما را احساس کردند، ناگهان از آن می گریختند.'),
(2496, 21, 13, '[از روی استهزا و تحقیر به آنان گفتند:] فرار نکنید، و به سوی زندگی مرفّهی که در آن نازپرورده [و مغرور] بودید و خانه هایتان بازگردید تا [بار دیگر به وسیله تهیدستان و مستمندان] از شما درخواست کمک شود [و شما آنان را با کبر و نخوت برانید.]'),
(2497, 21, 14, '[با دیدن عذاب فریاد برداشتند و] گفتند: ای وای بر ما که ما قطعاً ستمکار بودیم!'),
(2498, 21, 15, 'پس همواره سخنشان همین بود تا آنکه آنان را ریشه کن و خاموش ساختیم.'),
(2499, 21, 16, 'و ما آسمان و زمین و آنچه را میان آن دو قرار دارد به بازی نیافریده ایم.'),
(2500, 21, 17, 'اگر می خواستیم بازیچه و سرگرمی انتخاب کنیم، چنان چه، [بر فرض محال] کننده [این کار] بودیم، آن را از نزدِ ذاتِ خود [برابر با شأنمان] انتخاب می کردیم [نه از آسمان و زمین که مملوکِ ما هستند.]'),
(2501, 21, 18, 'نه، بلکه [شأن ما این است که] با حق بر باطل می کوبیم تا آن را درهم شکند [و از هم بپاشد] پس ناگهان باطل نابود شود؛ و وای بر شما از آنچه [درباره خدا و مخلوقات او به ناحق] توصیف می کنید.'),
(2502, 21, 19, 'و هر که در آسمان ها و زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست، و کسانی که [از فرشتگان] در محضر اویند از بندگیش تکبّر نمی ورزند و خسته و درمانده نمی شوند.'),
(2503, 21, 20, 'شبانه روز او را بی آنکه سست شوند، تسبیح می گویند.'),
(2504, 21, 21, 'آیا معبودانی از زمین اختیار کرده اند که آنان مردگان را زنده می کنند؟!'),
(2505, 21, 22, 'اگر در آسمان و زمین معبودانی جز خدا بود بی تردید آن دو تباه می شد؛ پس منزّه است خدای صاحب عرش از آنچه [او را به ناحق به آن] وصف می کنند.'),
(2506, 21, 23, 'خدا از آنچه انجام می دهد، بازخواست نمی شود و آنان [در برابر خدا] بازخواست خواهند شد.'),
(2507, 21, 24, '[شگفتا!] آیا به جای خدا معبودانی اختیار کرده اند؟ بگو: [اگر انتخاب شما حق است] دلیل و برهانتان را [بر آن] بیاورید، این [قرآن] یادآور امت من [نسبت به توحید و نفی شرک] و یادآور امت های پیش از من [نسبت به معارف توحیدی دیگر کتاب های آسمانی] است؛ [حق نه این است که مشرکان می گویند] بلکه بیشترشان حق را نمی شناسند و به این سبب از آن روی گردانند.'),
(2508, 21, 25, 'و پیش از تو هیچ پیامبری نفرستادیم مگر آنکه به او وحی کردیم که معبودی جز من نیست، پس تنها مرا بپرستید.'),
(2509, 21, 26, 'و [مشرکان] گفتند: [خدایِ] رحمان فرشتگان را فرزند خود گرفته است. منزّه است او، [فرشتگان، فرزند خدا نیستند] بلکه بندگانی گرامی و ارجمندند.'),
(2510, 21, 27, 'در گفتار بر او پیشی نمی گیرند، و آنان فقط به فرمان او عمل می کنند.'),
(2511, 21, 28, 'خدا همه گذشته آنان و آینده شان را می داند، و جز برای کسی که خدا بپسندد شفاعت نمی کنند، و آنان از ترس [عظمت و جلال] او هراسان و بیمناکند.'),
(2512, 21, 29, 'و هر کس از آنان بگوید: من هم معبودی، غیر از اویم، دوزخ را به آن گوینده کیفر می دهیم، و ستمکاران را این گونه مجازات می کنیم.'),
(2513, 21, 30, 'آیا کافران ندانسته اند که آسمان ها و زمین به هم بسته و پیوسته بودند و ما آن دو را شکافته و از هم باز کردیم و هر چیز زنده ای را از آب آفریدیم؟ پس آیا ایمان نمی آورند؟'),
(2514, 21, 31, 'و در زمین کوه های استوار پدید آوردیم تا زمین آنان را نلرزاند، و در آن راه هایی فراخ و گشاده قرار دادیم تا [به سوی اهداف خود] راه یابند.'),
(2515, 21, 32, 'و آسمان را سقفی محفوظ قرار دادیم در حالی که آنان [از تأمل و دقت در] نشانه های آن [که گواه توحید، ربوبیّت و قدرت خداست] روی گردانند.'),
(2516, 21, 33, 'و اوست که شب و روز و خورشید و ماه را آفرید که هر یک در مداری شناور است.'),
(2517, 21, 34, 'و پیش از تو برای هیچ بشری جاودانگی قرار ندادیم؛ پس آیا اگر تو بمیری آنان جاویدان خواهند ماند؟!'),
(2518, 21, 35, 'هر کسی چشنده مرگ است و ما شما را [چنانکه سزاوار است] به نوعی خیر و شر [که تهیدستی، ثروت، سلامت، بیماری، امنیت و بلاست] آزمایش می کنیم، و به سوی ما بازگردانده می شوید.'),
(2519, 21, 36, 'کافران چون تو را ببینند جز به مسخره ات نمی گیرند [و می گویند:] آیا این است آن کسی که معبودان شما را [به موجوداتی بی اثر و بی اختیار] یاد می کند؟ در حالی که خود به ذکر [خدایِ] رحمان [که توحید و قرآن است] کافرند [و از این کار، باکی ندارند.]'),
(2520, 21, 37, 'انسان از شتاب و عجله آفریده شده است [که با نخوت و غرور، عذابم را به شتاب می خواهد]؛ به زودی عذاب هایم را به شما نشان خواهم داد، پس [آن را] به شتاب از من نخواهید.'),
(2521, 21, 38, '[به پیامبر و مؤمنان] می گویند: اگر راست می گویید، این وعده کی خواهد بود؟'),
(2522, 21, 39, 'اگر کافران به آن وقتی که نمی توانند آتش را از چهره ها و پشتشان باز دارند و یاری هم نمی شوند، آگاهی داشتند [عجولانه عذاب را نمی خواستند.]'),
(2523, 21, 40, 'بلکه ناگهان به آنان می رسد، پس چنان مبهوتشان می کند که نه قدرت دارند آن را بازگردانند، و نه [برای به تأخیر افتادنش] مهلت می یابند.'),
(2524, 21, 41, 'و به راستی پیش از تو پیامبرانی مورد استهزا قرار گرفتند، پس عذابی را که همواره مسخره می کردند، استهزا کنندگان را فرا گرفت.'),
(2525, 21, 42, 'بگو: چه کسی شما را در شب و روز از [عذاب خدای] رحمان محافظت می کند؟ بلکه [حقیقت این است که] آنان از یاد پروردگارشان روی گردانند.'),
(2526, 21, 43, 'آیا برای آنان به جای ما معبودانی هست که آنان را [از عذاب ما] باز دارند در حالی که [آن معبودان] نمی توانند خود را یاری دهند و از سوی ما هم پناه داده نمی شوند؟'),
(2527, 21, 44, 'بلکه اینان و پدرانشان را [از انواع نعمت ها] بهره مند ساختیم تا جایی که عمرشان طولانی شد [و گمان کردند که نعمت ها و عمرشان پایان نمی پذیرد]. آیا ندانسته اند که ما همواره به زمین می پردازیم و از اطراف [و جوانب] آن [که ملت ها، اقوام، تمدن ها و دانشمندانش هستند] می کاهیم؟ پس آیا باز هم آنان پیروزند؟'),
(2528, 21, 45, 'بگو: من فقط شما را به وسیله وحی بیم می دهم، ولی کران، بانگ دعوت را هنگامی که بیمشان دهند، نمی شنوند.'),
(2529, 21, 46, 'و اگر اندکی از عذاب پروردگارت به آنان برسد، خواهند گفت: ای وای بر ما که قطعاً ستمکار بودیم!'),
(2530, 21, 47, 'و ترازوهای عدالت را در روز قیامت می نهیم و به هیچ کس هیچ ستمی نمی شود؛ و اگر [عمل خوب یا بد] هم وزن دانه خردلی باشد آن را [برای وزن کردن] می آوریم، و کافی است که ما حسابگر باشیم.'),
(2531, 21, 48, 'یقیناً به موسی و هارون [کتابی که] جداکننده [حق از باطل] و نور و مایه یادآوری برای پرهیزکاران [است] عطا کردیم.'),
(2532, 21, 49, 'همانان که در پنهانی از پروردگارشان می ترسند و از قیامت هم بیمناکند.'),
(2533, 21, 50, 'و این [قرآنی] که آن را نازل کرده ایم، ذکر و پندی پرمنفعت است؛ آیا باز هم شما منکر آن هستید؟'),
(2534, 21, 51, 'و مسلماً پیش از این به ابراهیم، رشد و هدایتی [که سزاوارش بود] عطا کردیم؛ و ما به او دانا بودیم.'),
(2535, 21, 52, '[یاد کن] زمانی را که به پدرش و قومش گفت: این مجسمه هایی که شما ملازم پرستش آنها شده اید، چیست؟'),
(2536, 21, 53, 'گفتند: پدرانمان را پرستش کنندگان آنها یافتیم [لذا به پیروی از پدرانمان آنها را می پرستیم!!]'),
(2537, 21, 54, 'گفت: به یقین شما و پدرانتان در گمراهی آشکاری هستید.'),
(2538, 21, 55, 'گفتند: آیا حق را برای ما آورده ای یا شوخی می کنی؟!'),
(2539, 21, 56, 'گفت: [شوخی نمی کنم] بلکه پروردگارتان همان پروردگار آسمان ها و زمین است، همان که آنها را آفرید و من بر این [حقیقت] از گواهی دهندگانم.'),
(2540, 21, 57, 'سوگند به خدا پس از آنکه [به بتخانه] پشت کردید و رفتید، درباره بت هایتان تدبیری خواهم کرد.'),
(2541, 21, 58, 'پس [همه] بت ها را قطعه قطعه کرد و شکست مگر بت بزرگشان را که [برای درک ناتوانی بت ها] به آن مراجعه کنند.'),
(2542, 21, 59, '[چون به بتخانه آمدند، با شگفتی] گفتند: چه کسی این کار را با معبودانمان انجام داده است؟ به یقین او از ستمکاران است.'),
(2543, 21, 60, 'گفتند: از جوانی شنیدیم که از بتان ما [به عنوان عناصری بی اثر و بی اختیار] یاد می کرد که به او ابراهیم می گویند.'),
(2544, 21, 61, 'گفتند: پس او را در برابر دیدگان مردم بیاورید تا آنان [به این کار او] شهادت دهند.'),
(2545, 21, 62, 'گفتند: ای ابراهیم! آیا تو با معبودان ما چنین کرده ای؟'),
(2546, 21, 63, 'گفت: بلکه [سالم ماندن بزرگشان نشان می دهد که] بزرگشان این کار را انجام داده است؛ پس اگر سخن می گویند، از خودشان بپرسید.'),
(2547, 21, 64, 'پس آنان [با تفکر و تأمل] به خود آمدند و گفتند: شما خودتان [با پرستیدن این موجودات بی اثر و بی اختیار] ستمکارید [نه ابراهیم.]'),
(2548, 21, 65, 'آن گاه سرافکنده و شرمسار شدند [ولی از روی ستیزه جویی به ابراهیم گفتند:] مسلماً تو می دانی که اینان سخن نمی گویند.'),
(2549, 21, 66, 'گفت: [با توجه به این حقیقت] آیا به جای خدا چیزهایی را می پرستید که هیچ سود و زیانی به شما نمی رسانند؟!'),
(2550, 21, 67, 'اُف بر شما و بر آنچه به جای خدا می پرستید؛ پس آیا نمی اندیشید؟'),
(2551, 21, 68, 'گفتند: اگر می خواهید کاری انجام دهید [و مرد کار هستید] او را بسوزانید، و معبودانتان را یاری دهید.'),
(2552, 21, 69, '[پس او را در آتش افکندند] گفتیم: ای آتش! برابر ابراهیم سرد و بی آسیب باش!'),
(2553, 21, 70, 'و بر ضد او نیرنگی سنگین به کار گرفتند [که نابودش کنند] پس آنان را زیانکارترین [مردم] قرار دادیم.'),
(2554, 21, 71, 'و او و لوط را [از آن سرزمین پر از شرک و فساد] نجات داده و به سوی سرزمینی که در آن برای جهانیان برکت نهاده ایم، بردیم.'),
(2555, 21, 72, 'و اسحاق و یعقوب را به عنوان عطایی افزون، به او بخشیدیم و همه را افرادی شایسته قراردادیم.'),
(2556, 21, 73, 'و آنان را پیشوایانی قرار دادیم که به فرمان ما [مردم را] هدایت می کردند، و انجام دادن کارهای نیک و برپا داشتن نماز و پرداخت زکات را به آنان وحی کردیم، و آنان فقط پرستش کنندگان ما بودند.'),
(2557, 21, 74, 'و به لوط، حکمت و دانش دادیم و او را از آن شهری که [اهلش] کارهای زشت مرتکب می شدند، نجات دادیم؛ بی تردید آنان قومی بد و نافرمان بودند.'),
(2558, 21, 75, 'و او را در رحمت خود درآوردیم؛ چون او از شایستگان بود.'),
(2559, 21, 76, 'و نوح را [یاد کن] هنگامی که پیش از این [پیامبران یاد شده] ندا کرد: [پروردگارا! مرا از این قوم فاسد و تبهکار نجات بخش.] پس ندایش را اجابت کردیم، و او و خانواده اش را از آن اندوه بزرگ نجات دادیم.'),
(2560, 21, 77, 'و او را در برابر گروهی که آیات ما را تکذیب کردند، یاری دادیم؛ قطعاً آنان گروه بدی بودند، پس همه آنان را غرق کردیم.'),
(2561, 21, 78, 'و داود و سلیمان را [یاد کن] زمانی که درباره آن کشتزار که شبانه گوسفندان قوم در آن چریده بودند، داوری می کردند، و ما گواه داوری آنان بودیم.'),
(2562, 21, 79, 'پس [داوری] آن را به سلیمان فهماندیم، و هر یک را حکمت و دانش عطا کردیم، و کوه ها و پرندگان را رام و مسخّر کردیم که همواره با داود تسبیح می گفتند، و ما انجام دهنده [این کار] بودیم.'),
(2563, 21, 80, 'و به سود شما صنعتِ ساختنِ پوشش های دفاعی را به او آموختیم تا شما را از [آسیب] جنگ تان محافظت نماید، پس آیا شما شکرگزار حق هستید؟'),
(2564, 21, 81, 'و برای سلیمان، تندباد را رام و مسخّر کردیم که به فرمانش به سوی آن سرزمینی که در آن برکت نهادیم، حرکت می کرد و ما همواره به همه چیز داناییم.'),
(2565, 21, 82, 'و از شیطان ها کسانی را رام و مسخّر او کردیم که برایش غوّاصی و کارهایی غیر از آن انجام می دادند، و ما نگهبان آنان بودیم.'),
(2566, 21, 83, 'و ایوب را [یاد کن] هنگامی که پروردگارش را ندا داد که مرا آسیب و سختی رسیده و تو مهربان ترین مهربانانی.'),
(2567, 21, 84, 'پس ندایش را اجابت کردیم و آنچه از آسیب و سختی به او بود برطرف نمودیم، و خانواده اش را [که در حادثه ها از دستش رفته بودند] و مانندشان را همراه با آنان به او عطا کردیم که رحمتی از سوی ما و مایه پند و تذکری برای عبادت کنندگان بود.'),
(2568, 21, 85, 'و اسماعیل و ادریس و ذوالکفل را [یاد کن] که همه از شکیبایان بودند.'),
(2569, 21, 86, 'و آنان را در رحمت خود درآوردیم، چون از شایستگان بودند.'),
(2570, 21, 87, 'و صاحب ماهی [حضرت یونس] را [یاد کن] زمانی که خشمناک [از میان قومش] رفت و گمان کرد که ما [زندگی را] بر او تنگ نخواهیم گرفت، پس در تاریکی ها [ی شب، زیر آب، و دل ماهی] ندا داد که معبودی جز تو نیست تو از هر عیب و نقصی منزّهی، همانا من از ستمکارانم.'),
(2571, 21, 88, 'پس ندایش را اجابت کردیم و از اندوه نجاتش دادیم؛ و این گونه مؤمنان را نجات می دهیم.'),
(2572, 21, 89, 'و زکریا را [یاد کن] زمانی که پروردگارش را ندا داد: پروردگارا! مرا تنها [و بی فرزند] مگذار؛ و تو بهترین وارثانی.'),
(2573, 21, 90, 'پس [ندای] او را اجابت کردیم و یحیی را به او بخشیدیم و نازایی همسرش را برای وی اصلاح نمودیم، آنان همواره در کارهای خیر می شتافتند، و ما را از روی امید و بیم می خواندند، و پیوسته در برابر ما فروتن بودند.'),
(2574, 21, 91, 'و آن [زن را یاد کن] که دامن خود را پاک نگه داشت، پس ما از روح خود در او دمیدیم و او و پسرش را نشانه ای [بزرگ از قدرت خود] برای جهانیان قرار دادیم.'),
(2575, 21, 92, 'و بی تردید این [اسلام] آیین [حقیقی] شماست در حالی که آیینی یگانه است و منم پروردگار شما پس مرا بپرستید.'),
(2576, 21, 93, 'ولی [آنان] آیینشان را در میان خود قطعه قطعه کردند [و نسبت به دین گروه گروه شدند و به شدت با هم اختلاف پیدا کردند]؛ و همه آنان به سوی ما باز خواهند گشت [تا کیفر سخت این گروه گرایی را ببینند.]'),
(2577, 21, 94, 'پس کسی که برخی از کارهای شایسته را انجام دهد در حالی که مؤمن باشد، نسبت به تلاشش ناسپاسی نخواهد شد، و ما یقیناً [تلاشش را] برای او می نویسیم.'),
(2578, 21, 95, 'و بر [اهل] شهری که نابودشان کردیم، محال است که [در قیامت به سوی ما] باز نگردند.'),
(2579, 21, 96, 'تا زمانی که [سدّ] یأجوج و مأجوج گشوده شود و آنان از هر زمین بلندی سرازیر می شوند.'),
(2580, 21, 97, 'و آن وعده حق [که قیامت است] نزدیک شود، پس ناگهان چشم های کافران خیره شود [و گویند:] وای بر ما! که ما از این روز در بی خبری سنگینی قرار داشتیم، بلکه ما ستمکار بودیم.'),
(2581, 21, 98, '[به آنان گویند:] به یقین شما و معبودانی که به جای خدا می پرستیدید، هیزمِ دوزخید؛ [بی تردید] شما در آن وارد خواهید شد.'),
(2582, 21, 99, 'اگر اینان معبودان [برحق] بودند، وارد دوزخ نمی شدند در حالی که همگی [بتان و بت پرستان] در آن جاودانه اند.'),
(2583, 21, 100, 'آنان در دوزخ، نعره های دردناکی دارند و در آنجا [سخنی امیدوار کننده] نمی شنوند.'),
(2584, 21, 101, 'بی تردید کسانی که پیش تر از سوی ما وعده نیک به آنان داده اند، از دوزخ دورشان نگه می دارند.'),
(2585, 21, 102, 'کمترین صدای آن را [هم] نمی شنوند، و آنان در آنچه [از نعمت های الهی] دلشان بخواهد جاودانه اند.'),
(2586, 21, 103, '[در آن روز] آن بزرگ ترین ترس و واهمه آنان را اندوهگین نمی کند و فرشتگان [با درود و سلام] به استقبالشان آیند [و گویند:] این است روز شما که شما را به آن وعده می دادند.'),
(2587, 21, 104, 'روزی که آسمان را در هم می پیچیم، مانند در هم پیچیدن طومار، همان گونه که نخستین بار آفریده ها را آفریدیم، دوباره آنان را باز می گردانیم. وعده ای است [که تحقق دادنش] بر عهده ما [ست]، به یقین آن را انجام می دهیم.'),
(2588, 21, 105, 'و همانا ما پس از تورات در زبور نوشتیم که زمین را بندگان شایسته ما به میراث می برند.'),
(2589, 21, 106, 'بی تردید در این [حقایق] برای [رسانیدن] مردم عبادت پیشه [به نهایت مقصود و اوج مطلوب] کفایت است.'),
(2590, 21, 107, 'و تو را جز رحمتی برای جهانیان نفرستادیم.'),
(2591, 21, 108, 'بگو: به من فقط وحی می شود که معبود شما خدای یکتاست؛ پس آیا تسلیم [فرمان ها و احکام او] می شوید؟'),
(2592, 21, 109, 'پس اگر روی برگرداندند بگو: من به شما به طور یکسان آگاهی و هشدار دادم، و نمی دانم آنچه شما را [از عذاب] به آن وعده داده اند، آیا نزدیک است یا دور؟'),
(2593, 21, 110, 'بی تردید او سخن آشکار را و آنچه را پنهان می دارید، می داند.'),
(2594, 21, 111, 'و من نمی دانم شاید این [تأخیر عذاب] آزمایشی برای شما و بهره مندی اندکی [از نعمت ها] تا مدتی معین است.'),
(2595, 21, 112, '[پیامبر] گفت: پروردگارا! [میان ما و مشرکان] به حق داوری کن و پروردگار ما مهربان است و [مؤمنان] بر خلاف واقعیتی که [شما مشرکان درباره پیروزی خود وشکست ما] وصف می کنید، از او یاری می خواهند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ای مردم! از پروردگارتان پروا کنید، بی تردید زلزله قیامت، واقعه ای بزرگ است.'),
(2597, 22, 2, 'روزی که آن را ببینید [مشاهده خواهید کرد که] هر مادر شیر دهنده ای از کودکی که شیرش می دهد، بی خبر می شود، و هر ماده بارداری بار خود را سقط می کند، و مردم را مست می بینی در حالی که مست نیستند، بلکه عذاب خدا بسیار سخت است.'),
(2598, 22, 3, 'و برخی از مردم اند که [همواره] بدون هیچ دانشی [بلکه از روی جهل و نادانی] درباره خدا [با اصرار بر یک اعتقاد بی پایه] برخورد خصمانه و گفتگوی ستیزآمیز می کنند، و از هر شیطان سرکشی پیروی می نمایند.'),
(2599, 22, 4, 'بر آن شیطان، لازم و مقرّر شده است که هر کس که او را به سرپرستی و دوستی خود گیرد، حتماً او گمراهش می کند، و به آتش سوزنده راهنمایی اش می نماید.'),
(2600, 22, 5, 'ای مردم! اگر درباره برانگیخته شدن [پس از مرگ] در تردید هستید، پس [به این واقعیت توجه کنید که] ما شما را از خاک آفریدیم، سپس از نطفه، سپس از علقه، سپس از پاره گوشتی با آفرینشی کامل یا غیر کامل آفریدیم تا برای شما روشن کنیم [که ما به برانگیختن مردگان تواناییم]؛ و آنچه را می خواهیم تا مدتی معین در رحم ها مستقر می کنیم؛ آن گاه شما را به صورت کودک [از رحم مادر] بیرون می آوریم تا آنکه به قدرت فکری و نیرومندی جسمی خود برسید. و برخی از شما [پیش از فرتوتی] قبض روح می شود، و برخی از شما را به پست ترین دوره عمر [که ایام پیری است] برمی گردانند تا در نتیجه از دانشی که داشتند چیزی ندانند. و [از نشانه های دیگر قدرت ما اینکه] زمین را [در زمستان] خشک و افسرده می بینی، پس چون آب [باران] را بر آن نازل می کنیم، می جنبد و برمی آید و از هر نوع گیاه تر و تازه و بهجت انگیزی می رویاند.'),
(2601, 22, 6, '[همه] این [امور] برای این است که [بدانید] خدا همان حق است، و اینکه او مردگان را زنده می کند، و اینکه او بر هر کاری تواناست.'),
(2602, 22, 7, 'و اینکه قیامت آمدنی است، هیچ شکی در آن نیست، و اینکه خدا کسانی را که در گورهایند، برمی انگیزد.'),
(2603, 22, 8, 'و از مردمان کسی است که همواره بدون هیچ دانشی [بلکه از روی جهل و نادانی] و بدون هیچ هدایتی، و هیچ کتاب روشنی درباره خدا مجادله و ستیزه می کند.'),
(2604, 22, 9, '[آن هم] با حالتی متکبرانه و مغرورانه که سرانجام مردم را از راه خدا گمراه کند؛ برای او در دنیا رسوایی است و روز قیامت عذاب سوزان به وی می چشانیم.'),
(2605, 22, 10, 'این [رسوایی و عذاب] به کیفر اعمالی است که از پیش فرستاده ای و به آن خاطر است که خدا نسبت به بندگان ستمکار نیست.'),
(2606, 22, 11, 'و برخی از مردم اند که خدا را یک سویه [و بر پایه دست یابی به امور مادی] می پرستند، پس اگر خیری [چون ثروت، مقام و اولاد] به آنان برسد به آن آرامش یابند، و اگر بلایی [چون بیماری، تهیدستی و محرومیت از عناوین اجتماعی] به آنان برسد [از پرستش خدا] عقب گرد می کنند [و به بی دینی و ارتداد می گرایند]، دنیا و آخرت را از دست داده اند، و این است همان زیان آشکار.'),
(2607, 22, 12, 'چیزی را به جای خدا می پرستند که نه زیانی به آنان می رساند و نه سودی به آنان می دهد؛ این است همان گمراهی بسیار دور.'),
(2608, 22, 13, 'بلکه کسی را می پرستند که قطعاً زیانش از سودش نزدیک تر است، بد سرپرست و یاوری و بد معاشر و همدمی است!'),
(2609, 22, 14, 'یقیناً خدا کسانی را که ایمان آورده و کارهای شایسته انجام داده اند، در بهشت هایی در می آورد که از زیرِ [درختانِ] آن نهرها جاری است؛ مسلماً خدا هر چه را بخواهد انجام می دهد.'),
(2610, 22, 15, 'کسی که همواره گمان می کند که خدا هرگز پیامبرش را در دنیا و آخرت یاری نخواهد داد [و اکنون یاری خدا را می بیند و به این خاطر ناامید و خشمگین است] باید طنابی از سقف [خانه اش] بیاویزد، سپس خود را حلق آویز کند، پس ببیند آیا حیله و نیرنگش آنچه را سبب خشم او شده است از بین می برد؟!'),
(2611, 22, 16, 'و این گونه، قرآن را [به صورت] آیاتی روشن نازل کردیم؛ و قطعاً خدا هر که را بخواهد، هدایت می کند.'),
(2612, 22, 17, 'همانا آنان که ایمان آوردند و آنان که یهودی اند و صابئان و نصاری و مجوس و کسانی که شرک ورزیده اند، حتماً خدا روز قیامت میانشان داوری می کند [تا گرویدگان به حق از آلودگان به باطل جدا شوند و حق پیشگان به بهشت و باطل گرایان به دوزخ درآیند]؛ بی تردید خدا بر همه چیز گواه است.'),
(2613, 22, 18, 'آیا ندانسته ای که هر که در آسمان ها و هر که در زمین است و خورشید و ماه و ستارگان و کوه ها و درختان و جنبندگان و بسیاری از مردم برای او سجده می کنند؟ و بر بسیاری [که از سجده امتناع دارند] عذاب، لازم و مقرّر شده است. و کسی را که خدا خوار کند، گرامی دارنده ای برای او نیست؛ به یقین خدا هر چه را بخواهد انجام می دهد.'),
(2614, 22, 19, 'این دو [گروهِ حق پیشه و باطل گرا] دشمن یکدیگرند که درباره پروردگارشان در جدال و ستیزند [گروه حق پیشه، پروردگار را به صفاتی وصف می کنند که شایسته اوست و گروه باطل گرا او را به اموری می ستایند که سزاوار او نیست]، پس کسانی که کافر شدند برای آنان لباس هایی از آتش [به اندازه اندامشان] بریده شده و از بالای سرشان مایع جوشان [به روی آنان] ریخته می شود،'),
(2615, 22, 20, 'که آنچه در شکم های ایشان است و پوست بدنشان به وسیله آن گداخته می شود،'),
(2616, 22, 21, 'و برای آنان گرزهایی از آهن [مخصوص] است [که بر سرشان می کوبند.]'),
(2617, 22, 22, 'هرگاه بخواهند به سبب اندوه [فراوان و غصه گلوگیر] از دوزخ درآیند، به آن بازشان می گردانند؛ و [به آنان می گویند:] عذاب سوزان را بچشید.'),
(2618, 22, 23, 'بی تردید خدا کسانی را که ایمان آورده و کارهای شایسته انجام داده اند، در بهشت هایی درمی آورد که از زیرِ [درختانِ] آن نهرها جاری است، در آنجا با دستبندهایی از طلا و مروارید آرایش می شوند، و لباسشان در آنجا حریر است.'),
(2619, 22, 24, 'و به سوی گفتار پاک و پاکیزه [مانند سلام، الحمدلله و سبحان الله] راهنماییشان کنند، و به راه پسندیده [که راه بهشت است] هدایتشان نمایند.'),
(2620, 22, 25, 'مسلماً کسانی که کافرند و از راه خدا و [ورود مؤمنان به] مسجدالحرام که آن را برای همه مردم چه مقیم و حاضر و چه مسافر، یکسان قرار داده ایم جلوگیری می کنند، [کیفری بسیار سخت خواهند داشت]، و هر که بخواهد در آنجا با انحراف از حق روی به ستم آورد [و دست به شرک و هر گناهی بیالاید] او را عذابی دردناک می چشانیم.'),
(2621, 22, 26, 'و [یاد کن] هنگامی را که جای خانه [کعبه] را برای ابراهیم آماده کردیم [و از او پیمان گرفتیم] که هیچ چیز را شریک من قرار مده و خانه ام را برای طواف کنندگان و قیام کنندگان و رکوع کنندگان و سجده کنندگان [از پلیدی های ظاهری و باطنی] پاک و پاکیزه گردان.'),
(2622, 22, 27, 'و در میان مردم برای حج بانگ زن تا پیاده و سوار بر هر شتر باریک اندام [چابک و چالاک] که از هر راه دور می آیند، به سوی تو آیند.'),
(2623, 22, 28, 'تا منافع خود را [از برکت این سفر معنوی] مشاهده کنند، و نام خدا را در روزهایی معین [که برای قربانی اعلام شده] بر دام های زبان بسته ای که به آنان عطا کرده ذکر کنند، [چون قربانی کردید] از آن بخورید و تهیدست را نیز اطعام کنید.'),
(2624, 22, 29, 'سپس باید آلودگی های خود را [که در مدت مُحرم بودن و قربانی کردن و سر تراشیدن بر بدن هایشان قرار گرفته] برطرف کنند و نذرهایشان را وفا نمایند و بر گرد خانه کهن طواف کنند.'),
(2625, 22, 30, 'این است [آنچه به عنوان مناسک حج قرار داده ایم] و هر کس مقرّرات خدا را بزرگ شمارد، برای او نزد پروردگارش بهتر است. و چهارپایان مگر آنچه [در آیات دیگر، حُرمتش] بر شما خوانده می شود، برای شما حلال شده است؛ بنابراین از پلیدی بت ها و از گفتار باطل [چون دروغ، افترا، غیبت و شهادت ناحق] دوری گزینید.'),
(2626, 22, 31, 'در حالی که برای خدا حق گرا باشید [و در مناسک حج] به او شرک نورزید. و هر کس به خدا شرک ورزد، گویا چنان است که از آسمان سقوط کرده و پرندگان [شکاری] او را می ربایند، یا باد او را به جایی دور دست می اندازد.'),
(2627, 22, 32, 'این است [برنامه های حج و حدود و مقرّرات خدا] و هر کس شعایر خدا را بزرگ شمارد، بدون تردید این بزرگ شمردن ناشی از تقوای دل هاست.'),
(2628, 22, 33, 'برای شما در دام های قربانی تا زمانی معین سودهایی است [مانند سواری گرفتن و تغذیه از شیر و گوشت آنها] سپس جایگاه [فرود آمدنشان برای قربانی] به سوی خانه کهن [یعنی خانه کعبه] است.'),
(2629, 22, 34, 'و برای هر امتی عبادتی ویژه قرار داده ایم [که مشتمل بر قربانی است] تا نام خدا را بر آنچه که از دام های زبان بسته روزی آنان نموده ایم [به هنگام قربانی] ذکر کنند. پس [بدانید که] معبود شما خدای یکتاست؛ بنابراین فقط تسلیم او شوید. و فرمانبرداران فروتن را [به لطف و رحمت خدا] مژده ده.'),
(2630, 22, 35, 'همانان که وقتی خدا یاد شود، دل هایشان می هراسد، و بر آنچه [از بلا و حادثه] به آنان می رسد، شکیبایند و برپا دارندگان نمازند و از آنچه روزی آنان نمودیم، انفاق می کنند.'),
(2631, 22, 36, 'شتران قربانی را برای شما از شعایر خدا قرار دادیم، برای شما در آنها سودی [مانند سواری گرفتن و تغذیه از شیر و گوشت آنها] ست؛ در حالی که به نظم در خط مستقیم ایستاده اند، نام خدا را [هنگام نحر کردنشان] بر آنها ذکر کنید، و زمانی که [بی جان] به پهلو در افتاده اند، از آنها بخورید و به تهیدستانی که اهل درخواست کردن نیستند و فقیرانی که اهل درخواست کردن هستند، بخورانید. این گونه آنها را برای شما رام و مسخّر کردیم تا سپاس گزاری کنید.'),
(2632, 22, 37, 'هرگز گوشت هایشان وخون هایشان به خدا نمی رسد، بلکه تقوای شما به او می رسد. این گونه آنها را برای شما رام و مسخّر کرد تا خدا را به [شکرانه] اینکه هدایتتان کرد به بزرگی یاد کنید؛ و نیکوکاران را [به لطف و رحمت خدا] مژده ده.'),
(2633, 22, 38, 'مسلماً خدا از مؤمنان دفاع می کند، قطعاً خدا هیچ خیانت کار ناسپاسی را دوست ندارد.'),
(2634, 22, 39, 'به کسانی که [ستمکارانه] مورد جنگ و هجوم قرار می گیرند، چون به آنان ستم شده اذن جنگ داده شده، مسلماً خدا بر یاری دادن آنان تواناست.'),
(2635, 22, 40, 'همانان که به ناحق از خانه هایشان اخراج شدند [و گناه و جرمی نداشتند] جز اینکه می گفتند: پروردگار ما خداست و اگر خدا برخی از مردم را به وسیله برخی دیگر دفع نمی کرد، همانا صومعه ها و کلیساها و کنیسه ها و مسجدهایی که در آنها بسیار نام خدا ذکر می شود به شدت ویران می شدند؛ و قطعاً خدا به کسانی که [دین] او را یاری می دهند یاری می رساند؛ مسلماً خدا نیرومند و توانای شکست ناپذیر است.'),
(2636, 22, 41, 'همانان که اگر آنان را در زمین قدرت و تمکّن دهیم، نماز را برپا می دارند، و زکات می پردازند، و مردم را به کارهای پسندیده وا می دارند و از کارهای زشت بازمی دارند؛ و عاقبت همه کارها فقط در اختیار خداست.'),
(2637, 22, 42, 'و اگر تو را تکذیب می کنند [کار جدیدی نیست] پیش از اینان قوم نوح و عاد و ثمود هم [پیامبرانشان را] تکذیب کردند.'),
(2638, 22, 43, 'و [نیز] قوم ابراهیم وقوم لوط،'),
(2639, 22, 44, 'و [هم چنین] اهل مدین؛ و موسی هم تکذیب شد. پس کافران را مهلت دادم، سپس آنان را [به عذابی سخت] گرفتم، پس [بنگر که] کیفر و انتقام من [نسبت به آنان] چگونه بود؟'),
(2640, 22, 45, 'و چه بسیار شهرها را در حالی که اهلش ستمکار بودند، هلاک کردیم، پس [به سبب نزول عذاب سقف های خانه هایشان خراب شده و دیوارهایشان بر] سقف ها فرو ریخته است و [چه بسیار] چاه های پر آب [که به سبب نابود شدن مالکانش] متروک افتاده و کاخ ها و قصرهای برافراشته [ومحکمی که بی ساکن و بی صاحب مانده است.]'),
(2641, 22, 46, 'آیا در زمین گردش نکرده اند تا برای آنان دل هایی [بیدار و بینا] پیدا شود که با آن بیندیشند یا گوش هایی که با آن [اندرزها را] بشنوند؟ حقیقت این است که دیده ها کور نیست بلکه دل هایی که در سینه هاست، کور است!'),
(2642, 22, 47, 'و آنان از تو [از روی مسخره و ریشخند] شتاب در عذاب را درخواست می کنند، در حالی که خدا هرگز از وعده اش تخلف نمی کند؛ و همانا یک روز نزد پروردگارت مانند هزار سال از سال هایی است که شما می شمارید [برای او زمان نزدیک، زمان دور، امروز، دیروز، گذشته و آینده مفهومی ندارد؛ بنابراین فاصله زمانی شما با عذاب الهی شما را دچار این پندار نکند که تهدید به عذاب، تهدیدی طولانی و دروغ است.]'),
(2643, 22, 48, 'و چه بسا شهرها که به [اهل] آنها در حالی که ستمکار بودند، مهلت دادم، سپس آنان را [به عذابی سخت] گرفتم؛ وبازگشت همه به سوی اوست.'),
(2644, 22, 49, 'بگو: ای مردم! من برای شما فقط بیم دهنده ای آشکارم.'),
(2645, 22, 50, 'پس کسانی که ایمان آورده و کارهای شایسته انجام داده اند، آمرزش و رزقی نیکو برای آنهاست.'),
(2646, 22, 51, 'و کسانی که در [باطل کردن و بی اثر نمودن] آیات ما کوشیده اند، به گمان آنکه ما را عاجز کنند [تا از دسترس قدرت ما بیرون روند] اهل آتشِ افروخته اند.'),
(2647, 22, 52, 'و پیش از تو هیچ رسول و پیامبری را نفرستادیم مگر آنکه هرگاه آرزو می کرد [اهداف پاک و سعادت بخش خود را برای نجات مردم از کفر و شرک پیاده کند] شیطان [برای بازداشتن مردم از پذیرش حق] در برابر آرزویش شبهه و وسوسه می انداخت، ولی خدا آنچه را شیطان [از وسوسه ها و شبهه ها می اندازد] می زداید و محو می کند، سپس آیاتش را محکم و استوار می سازد؛ و خدا دانا و حکیم است.'),
(2648, 22, 53, '[آزاد گذاشتن شبهه اندازی شیطان] برای این [است] که خدا آنچه را شیطان می اندازد برای آنان که در دل هایشان بیماری است و برای سنگدلان وسیله آزمایش قرار دهد؛ و قطعاً ستمکاران در دشمنی و ستیزی بسیار دور [نسبت به حق و حقیقت] قرار دارند.'),
(2649, 22, 54, 'و [نیز برای این است] تا کسانی که دانش و آگاهی [دینی] به آنان عطا شده بدانند که [محو شدن و نابودی القائات شیطان و استوار شدن آیات] از سوی پروردگارت حق است، پس به آن ایمان آورند و دل هایشان برای آن رام و فروتن شود؛ و قطعاً خدا هدایت کننده اهل ایمان به سوی راهی راست است.'),
(2650, 22, 55, 'و کافران همواره نسبت به آیات خدا در تردیدی سخت قرار دارند تا آنکه ناگهان قیامت بر آنان در رسد، یا عذاب روزی که روز دیگری به دنبال ندارد [بلکه ابدی است] به سراغشان آید.'),
(2651, 22, 56, 'آن روز، حاکمیّت و فرمانروایی ویژه خداست. میان آنان داوری می کند؛ پس کسانی که ایمان آورده و کارهای شایسته انجام داده اند، در بهشت های پر نعمت اند.'),
(2652, 22, 57, 'و کسانی که کافر شده و آیات ما را تکذیب کرده اند، پس عذابی خوارکننده برای آنان است.'),
(2653, 22, 58, 'و کسانی که در راه خدا هجرت کرده سپس کشته شده یا مرده اند، به یقین خدا رزقی نیکو به آنان می دهد؛ و قطعاً خدا بهترین روزی دهندگان است.'),
(2654, 22, 59, 'مسلماً آنان را به جایگاهی که آن را می پسندند وارد می کند؛ و بی تردید خدا دانا و بردبار است.'),
(2655, 22, 60, '[مطلب درباره مؤمن و کافر] همان است [که گفتیم]، و هر کس به مانند آنچه به آن عقوبت شده [متجاوز را] عقوبت کند، آن گاه به وی ستم شود، یقیناً خدا او را یاری می دهد؛ زیرا خدا باگذشت و بسیار آمرزنده است.'),
(2656, 22, 61, 'این [یاری ستمدیده بر ضد ستمکار] به سبب این است که خدا [دارای قدرت بی نهایت است و گوشه ای از قدرتش اینکه] همواره شب را در روز و روز را در شب درمی آورد؛ و به یقین خدا شنوا و بیناست.'),
(2657, 22, 62, 'این [قدرت بی نهایت او نسبت به امور آفرینش و نسبت به وضع و حال مردم] برای این است که فقط خدا حق است، و اینکه آنچه را جز او می پرستید، باطل است، و اینکه فقط خدا بلند مرتبه و بزرگ است.'),
(2658, 22, 63, 'آیا ندانسته ای که خدا از آسمان آبی نازل می کند، در نتیجه زمین سرسبز و خرم می شود؛ به یقین خدا لطیف و آگاه است.'),
(2659, 22, 64, 'آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست؛ و یقیناً خداست که خود بی نیاز و ستوده است.'),
(2660, 22, 65, 'آیا ندانسته ای که خدا آنچه را در زمین است و [نیز] کشتی ها را که به فرمان او در دریا روانند، برای شما رام و مسخّر کرده است؟ و آسمان را نگه می دارد که بر زمین نیفتد مگر به اذن او؟ یقیناً خدا به همه مردم رؤوف و مهربان است.'),
(2661, 22, 66, 'و اوست که به شما حیات داد، سپس شما را می میراند، آن گاه شما را زنده می کند؛ به یقین که انسان بسیار ناسپاس است.'),
(2662, 22, 67, 'برای هر امتی عبادتی ویژه قرار داده ایم که آن را انجام می دهند؛ پس نباید در این امر با تو ستیز و نزاع کنند. و به سوی پروردگارت دعوت کن که بی تردید تو بر راهی راست قرار داری.'),
(2663, 22, 68, 'و اگر با تو [با اصرار بر یک اعتقاد بی پایه برخورد خصمانه کنند و] مجادله ستیزآمیز نمایند، بگو: خدا به آنچه انجام می دهید، داناتر است.'),
(2664, 22, 69, 'خدا روز قیامت درباره آنچه با یکدیگر در آن اختلاف می کردید، داوری خواهد کرد [تا مؤمنان با ورود به بهشت و ستیزه جویان با ورود به دوزخ از هم مشخص و جدا شوند.]'),
(2665, 22, 70, 'آیا ندانسته ای که خدا آنچه را در آسمان و زمین است، می داند؟ مسلماً [همه] این [اعمالی که انجام می دهید] در کتابی [چون لوح محفوظ، ثبت] است [و] بی تردید [ثبت در آن کتاب] بر خدا آسان است.'),
(2666, 22, 71, 'و [مشرکان] به جای خدا معبودانی را می پرستند که خدا هیچ دلیل و برهانی [نسبت] به [حق بودن] آنها نازل نکرده است؛ و معبودانی را می پرستند که به آن دانشی [برای استدلال بر ربوبیّت آنها] ندارند و ستمکاران را [در قیامت] هیچ یاوری نیست.'),
(2667, 22, 72, 'و هنگامی که آیات روشن ما را بر آنان می خوانند در چهره های کسانی که کافرند [اثرِ] انکار [و خشم] را می شناسی، آن چنان که نزدیک است [از شدت خشم] به کسانی که آیات ما را بر آنان می خوانند، بتازند، بگو: آیا شما را به بدتر از این [خشم و ناگواری که از شنیدن قرآن به شما دست می دهد] خبر دهم [آن] آتشِ [دوزخ] است که خدا آن را به کافران وعده داده است، و بد بازگشت گاهی است.'),
(2668, 22, 73, 'ای مردم! [برای شما و معبودانتان] مَثَلی زده شده است؛ پس به آن گوش فرا دهید، یقیناً کسانی که به جای خدا می پرستید، هرگز نمی توانند مگسی بیافرینند اگر چه برای آفریدن آن گرد آیند و اگر مگس، چیزی را از آنان برباید، نمی توانند آن را از او بازگیرند، هم پرستش کنندگان و هم معبودان ناتوانند.'),
(2669, 22, 74, 'خدا را آن گونه که سزاوار اوست نشناختند، بی تردید خدا نیرومند و توانای شکست ناپذیر است.'),
(2670, 22, 75, 'خدا از میان فرشتگان و از میان مردم رسولانی [برای هدایت مردم] برمی گزیند [تا فرشتگان وحی را دریافت کنند و به پیامبران برسانند و پیامبران هم وحی را پس از دریافت از فرشتگان به مردم ابلاغ کنند]؛ یقیناً خدا شنوا و بیناست.'),
(2671, 22, 76, 'همه اعمال و احوال گذشته [رسولان چه فرشته و چه بشر] و [همه اعمال و احوال] آینده آنان را می داند؛ و همه کارها به خدا بازگردانده می شود.'),
(2672, 22, 77, 'ای اهل ایمان! رکوع به جا آورید و سجده نمایید و پروردگارتان را عبادت کنید و کار نیک انجام دهید تا رستگار شوید.'),
(2673, 22, 78, 'و در راه خدا چنان که شایسته جهاد است، جهاد کنید؛ او شما را برگزید و بر شما در دین هیچ مشقت و سختی قرار نداد. [در دینتان گشایش و آسانی قرار داد مانند گشایش و آسانیِ] آیین پدرتان ابراهیم، او شما را پیش از این «مسلمان» نامید و در این [قرآن هم به همین عنوان نامگذاری شده اید] تا پیامبر گواه بر شما باشد و شما هم گواه بر مردم باشید؛ پس نماز را برپا دارید و زکات را بپردازید و به خدا تمسّک جویید. او سرپرست و یاور شماست؛ چه خوب سرپرست و یاوری و چه نیکو یاری دهنده ای است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'بی تردید مؤمنان رستگار شدند.'),
(2675, 23, 2, 'آنان که در نمازشان [به ظاهر] فروتن [و به باطن با حضور قلب] اند.'),
(2676, 23, 3, 'و آنان که از [هر گفتار و کردارِ] بیهوده و بی فایده روی گردانند،'),
(2677, 23, 4, 'و آنان که پرداخت کننده زکات اند،'),
(2678, 23, 5, 'و آنان که نگه دارنده دامنشان [از شهوت های حرام] اند،'),
(2679, 23, 6, 'مگر در [کام جویی از] همسران یا کنیزانشان، که آنان [در این زمینه] مورد سرزنش نیستند.'),
(2680, 23, 7, 'پس کسانی [که در بهره گیری جنسی، راهی] غیر از این جویند، تجاوزکار [از حدود حق] هستند.'),
(2681, 23, 8, 'و آنان که امانت ها و پیمان های خود را رعایت می کنند،'),
(2682, 23, 9, 'و آنان که همواره بر [اوقات و شرایط ظاهری و معنوی] نمازهایشان محافظت دارند.'),
(2683, 23, 10, 'اینانند که وارثان اند،'),
(2684, 23, 11, 'وارثانی که [از روی شایستگی] بهشت فردوس را به میراث می برند [و] در آن جاودانه اند.'),
(2685, 23, 12, 'و یقیناً ما انسان را از [عصاره و] چکیده ای از گِل آفریدیم،'),
(2686, 23, 13, 'سپس آن را نطفه ای در قرارگاهی استوار [چون رحم مادر] قرار دادیم.'),
(2687, 23, 14, 'آن گاه آن نطفه را علقه گرداندیم، پس آن علقه را به صورت پاره گوشتی درآوردیم، پس آن پاره گوشت را استخوان هایی ساختیم و بر استخوان ها گوشت پوشاندیم، سپس او را با آفرینشی دیگر پدید آوردیم؛ پس همیشه سودمند و بابرکت است خدا که نیکوترین آفرینندگان است.'),
(2688, 23, 15, 'سپس همه شما بعد از این می میرید.'),
(2689, 23, 16, 'آن گاه شما مسلماً روز قیامت برانگیخته می شوید.'),
(2690, 23, 17, 'و بی تردید بالای سرتان هفت آسمان [که هر یک بر فراز دیگری است] آفریدیم، و ما از آفریده ها بی خبر نیستیم.'),
(2691, 23, 18, 'و از آسمان، آبی به اندازه نازل کردیم و آن را در زمین جای دادیم؛ و بی تردید به از بین بردن آن کاملاً توانمندیم.'),
(2692, 23, 19, 'پس به وسیله آن برای شما باغ هایی از درختان خرما و انگور پدید آوردیم که برای شما در آنها میوه های فراوانی است [که از فروش آنها زندگی خود را اداره می کنید] و [نیز] از آنها می خورید.'),
(2693, 23, 20, 'و [به وسیله آن] درختی را که از طور سینا بیرون می آید [پدید آوردیم] که برای خورندگان، روغن و نان خورشی می رویاند.'),
(2694, 23, 21, 'و به یقین برای شما در دام ها عبرتی است، از شیری که در شکم آنهاست به شما می نوشانیم، و در آنها برای شما سودهای فراوانی است، و از [گوشت] آنها [نیز] می خورید.'),
(2695, 23, 22, 'و بر آنها و بر کشتی ها حمل می شوید؛'),
(2696, 23, 23, 'و همانا نوح را به سوی قومش فرستادیم. پس گفت: ای قوم من! خدا را بپرستید که معبودی جز او برای شما نیست، پس آیا [از پرستش معبودان باطل] نمی پرهیزید؟'),
(2697, 23, 24, 'پس سران و اشراف قومش که کافر بودند، گفتند: این جز بشری مانند شما نیست که می خواهد بر شما برتری جوید، و اگر خدا می خواست [پیامبری بفرستد] قطعاً فرشتگانی [را به پیامبری] می فرستاد، ما این [سخنانی] را [که نوح می گوید] میان پدران پیشین خود نشنیده ایم.'),
(2698, 23, 25, 'او نیست جز مردی که [نوعی] دیوانگی در اوست؛ بنابراین نسبت به او تا مدتی منتظر بمانید [که بمیرد یا از دیوانگی رهایی یابد.]'),
(2699, 23, 26, '[نوح] گفت: پروردگارا! مرا در برابر تکذیب آنان یاری ده.'),
(2700, 23, 27, 'پس به او وحی کردیم که زیر نظر ما و پیام ما کشتی بساز و هنگامی که فرمان ما به هلاکت آنان بیاید و آن تنور [از آب] فوران کند از هر گونه ای [از حیوان] دو عدد [یکی نر و دیگری ماده] و نیز خانواده ات را وارد کشتی کن، جز افرادی از آنان که فرمان [عذاب] بر او گذشته [و درباره او قطعی شده] است، و درباره کسانی که [به سبب شرک و کفر] ستم ورزیده اند، با من سخن مگوی، زیرا [همه] آنان بدون تردید غرق شدنی اند.'),
(2701, 23, 28, 'پس هنگامی که تو و آنان که با تو هستند، بر کشتی سوار شدید، به خاطر این نعمت بگو: همه ستایش ها ویژه خداست که ما را از این گروه ستم پیشه نجات داد.'),
(2702, 23, 29, 'و بگو: پروردگارا! مرا در جایگاهی پرخیر و برکت فرود آور، که تو بهترین فرودآورندگانی.'),
(2703, 23, 30, 'همانا در این [سرگذشت] نشانه هایی [از قدرت، رحمت و انتقام خدا] برای عبرت گیرندگان است؛ و یقیناً ما آزمایش کننده بندگانیم.'),
(2704, 23, 31, 'سپس بعد از آنان قومی دیگر را به وجود آوردیم.'),
(2705, 23, 32, 'و پیامبری از خودشان در میان آنان فرستادیم که خدا را بپرستید، شما را جز او معبودی نیست، آیا [از پرستش معبودان باطل] نمی پرهیزید؟'),
(2706, 23, 33, 'و از سران و اشراف قومش که کافر بودند و دیدار آخرت را تکذیب می کردند، و آنان را در زندگی دنیا از وسایل و ابزار مادی فراوانی برخوردار کرده بودیم، گفتند: این جز بشری مانند شما نیست که از آنچه شما می خورید می خورد، و از آنچه شما می آشامید می آشامد.'),
(2707, 23, 34, 'و بی تردید اگر بشری مانند خود را اطاعت کنید، یقیناً زیانکارید.'),
(2708, 23, 35, 'آیا به شما وعده می دهد هنگامی که از دنیا رفتید و خاک و استخوان شدید [زنده از گور] بیرونتان می آورند؟'),
(2709, 23, 36, '[از عقل ما] دور است و بسیار دور است آنچه به شما وعده می دهند،'),
(2710, 23, 37, 'جز این زندگی دنیای ما [زندگی دیگری] وجود ندارد، همواره [گروهی] می میریم و [گروهی دیگر] به دنیا می آییم، و ما پس از مرگ برانگیخته نخواهیم شد.'),
(2711, 23, 38, 'او نیست مگر مردی که بر خدا افترا بسته است؛ و ما باور کننده او نیستیم.'),
(2712, 23, 39, 'گفت: پروردگارا! مرا در برابر تکذیب آنان یاری ده.'),
(2713, 23, 40, 'خدا فرمود: همانا پس از اندک زمانی پشیمان می شوند.'),
(2714, 23, 41, 'پس فریاد مرگبار [عذاب] آنان را بر [پایه وعده به] حقِّ [ما] فرو گرفت، و آنان را چون خاشاک روی سیل قرار دادیم؛ پس قوم ستمکار [از رحمت خدا] دور باد.'),
(2715, 23, 42, 'سپس بعد از آنان قومی دیگر را به وجود آوردیم.'),
(2716, 23, 43, 'هیچ امتی از اجلِ [معینِ] خود نه پیش می افتد و نه پس می ماند.'),
(2717, 23, 44, 'آن گاه پیامبران را پی در پی فرستادیم؛ هر زمان برای امتی پیامبرش می آمد او را تکذیب می کردند، و ما این [امت] ها را به دنبال یکدیگر هلاک می کردیم و آنان را [به صورت] سرگذشت ها [برای عبرت دیگران] قرار دادیم؛ پس ملتی که ایمان نمی آورند از رحمت خدا دور باد.'),
(2718, 23, 45, 'سپس موسی و برادرش هارون را با معجزات و آیات خود و دلیلی روشن فرستادیم،'),
(2719, 23, 46, 'به سوی فرعون و سران و اشراف قومش، پس تکبّر ورزیدند؛ و آنان قومی برتری جو بودند.'),
(2720, 23, 47, 'و گفتند: آیا به دو بشری که مانند خودمان هستند ایمان بیاوریم در حالی که قوم [و قبیله] آن دو بردگان ما هستند؟'),
(2721, 23, 48, 'در نتیجه هر دو را تکذیب کردند و از هلاک شدگان شدند.'),
(2722, 23, 49, 'همانا ما به موسی کتاب دادیم، تا آنان هدایت شوند.'),
(2723, 23, 50, 'و پسر مریم و مادرش را نشانه [قدرت و رحمت خود] قرار دادیم و آن دو را در سرزمینی بلند که [دارای] جایگاهی مستقر [و امن] و آبی روان بود، جای دادیم.'),
(2724, 23, 51, 'ای پیامبران! از خوردنی های پاکیزه بخورید و کار شایسته انجام دهید؛ مسلماً من به آنچه انجام می دهید، دانایم.'),
(2725, 23, 52, 'و بی تردید این [اسلام] آیینِ [حقیقی] شماست در حالی که آیینی یگانه است، و من پروردگار شمایم؛ بنابراین از من پروا کنید.'),
(2726, 23, 53, 'پس کار [آیین] خود را میان خویش قطعه قطعه کردند [و گروه گروه شدند]، در حالی که هر گروهی به آن [آیینی] که نزد آنان است خوشحال و شادمانند [که آیینشان بر حق است.]'),
(2727, 23, 54, 'پس آنان را در غرقاب گمراهی و جهالتشان تا هنگامی [که مرگشان فرا رسد] واگذار.'),
(2728, 23, 55, 'آیا گمان می کنند افزونی و گسترشی که به سبب مال و اولاد به آنان می دهیم،'),
(2729, 23, 56, 'در حقیقت می خواهیم در عطا کردن خیرات به آنان شتاب ورزیم؟ [چنین نیست] بلکه [آنان] درک نمی کنند [که ما می خواهیم با افزونی مال و اولاد، در تفرقه، طغیان، گمراهی و تیره بختی بیشتری فرو روند.]'),
(2730, 23, 57, 'یقیناً آنان که از بیم پروردگارشان هراسان و نگرانند،'),
(2731, 23, 58, 'و آنان که به آیات پروردگارشان ایمان می آورند.'),
(2732, 23, 59, 'و آنان که به پروردگارشان شرک نمی ورزند.'),
(2733, 23, 60, 'و آنان که آنچه را [از زکات و دیگر انفاقات در راه خدا] می پردازند، و در حالی که دل هایشان از اینکه به سوی پروردگارشان [برای محاسبه دقیق] باز می گردند، ترسان است.'),
(2734, 23, 61, 'اینانند که در کارهای خیر می شتابند، و در [انجام دادن] آن [از دیگران] پیشی می گیرند.'),
(2735, 23, 62, 'و هیچ کس را جز به اندازه گنجایش و توانش تکلیف نمی کنیم، و نزد ما کتابی [چون لوح محفوظ] است که [درباره همه امور آفرینش و نسبت به اعمال بندگان] به درستی و راستی سخن می گوید، و آنان [در پاداش و کیفر] مورد ستم قرار نمی گیرند.'),
(2736, 23, 63, '[چنین نیست که کافران تکلیف را بیش از اندازه گنجایش و توان خود حس کنند] بلکه دل هایشان از این [حقیقت] در بی خبری عمیقی است، و برای آنان غیر از این [بی خبری] اعمالی [زشت و ناپسند] است که همواره آنها را انجام می دهند،'),
(2737, 23, 64, 'تا زمانی که عیّاشان و مرفّهان آنان را به عذاب بگیریم، ناگهان فریاد و ناله سر دهند و [به آوای بلند] استغاثه کنند. ….'),
(2738, 23, 65, '[به آنان گویند:] امروز فریاد و ناله سر ندهید؛ زیرا شما از سوی ما یاری نخواهید شد. ….'),
(2739, 23, 66, 'همانا آیات من را بر شما می خواندند وشما به پشت سرتان به قهقرا برمی گشتید [تا آن را نشنوید!]'),
(2740, 23, 67, 'در حالی که به آن تکبّر می ورزیدید [و در بزم] شبانه [خود درباره آن] هذیان می گفتید [و سخنان نامعقول به زبان می آوردید.] ….'),
(2741, 23, 68, 'آیا در این سخن [که وحی خداست] نیندیشیدند؟ یا مگر [برای اولین بار] کتابی برای آنان آمده است که برای پدران پیشین آنان نیامده بود؟ ….'),
(2742, 23, 69, 'یا مگر پیامبرشان را [از لحاظ حسب، نسب، درستی، امانت، اخلاق و کرامت] نشناخته اند که منکر او هستند؟ ….'),
(2743, 23, 70, 'یا می گویند: نوعی دیوانگی در اوست؟ [چنین نیست که این یاوه گویان به هم می بافند] بلکه او حق را برای آنان آورده است، و بیشترشان حق را خوش ندارند،'),
(2744, 23, 71, 'و اگر حق از هواهای نفس آنان پیروی می کرد، بی تردید آسمان ها و زمین و هر که در آنهاست از هم می پاشید [و تباه می شد؛ چنین نیست که حق از هواهای نفس آنان پیروی کند] بلکه کتاب [ی را که مایه شرف و سربلندی] آنان [است] به آنان داده ایم، ولی آنان از مایه شرف و سربلندی خود روی گردانند.،'),
(2745, 23, 72, 'یا [مگر برای ادای وظیفه ات] مزدی از آنان می خواهی [که پذیرش دعوتت برای آنان سنگین است؟] پس مزد پروردگارت بهتر است، و او بهترین روزی دهندگان است،'),
(2746, 23, 73, 'و مسلماً تو آنان را به راهی راست دعوت می کنی،'),
(2747, 23, 74, 'و بی تردید کسانی که به آخرت ایمان نمی آورند از [این] راه منحرف اند. ….'),
(2748, 23, 75, 'و اگر به آنان رحم کنیم و آسیب و گزندی را که دچار آن هستند [از آنان] برطرف سازیم، باز هم سرگردان و متحیر در سرکشی و طغیانشان لجاجت می ورزند.'),
(2749, 23, 76, 'همانا ما آنان را به بلاها و آسیب ها [ی دنیایی] گرفتیم، ولی نه برای پروردگارشان فروتنی کردند و نه به پیشگاهش زاری می کنند،'),
(2750, 23, 77, 'تا هنگامی که دری از عذابی سخت به روی آنان بگشاییم، ناگهان در آن حال از همه چیز مأیوس و نومید می شوند؛'),
(2751, 23, 78, 'و اوست که برای شما گوش و دیده و دل پدید آورد، ولی اندکی سپاس گزاری می کنید،'),
(2752, 23, 79, 'و اوست که شما را در زمین آفرید و عاقبت به سوی او گردآوری می شوید،'),
(2753, 23, 80, 'و اوست که حیات می دهد و می میراند و رفت و آمد شب و روز در سیطره خواست اوست، آیا نمی اندیشید؟'),
(2754, 23, 81, '[نه اینکه نمی اندیشند] بلکه مانند همان [سخنان یاوه و بی منطق] را گفتند که پیشینیان گفتند،'),
(2755, 23, 82, 'گفتند: [که] آیا زمانی که بمیریم و خاک و استخوان شویم حتماً برانگیخته می شویم؟'),
(2756, 23, 83, 'همانا این [برانگیخته شدن] را پیش از این به ما و پدرانمان وعده دادند [ولی] این جز افسانه خرافی پیشینیان نیست؛'),
(2757, 23, 84, 'بگو: اگر معرفت و شناخت دارید، بگویید: زمین و هر که در آن است از کیست؟'),
(2758, 23, 85, 'خواهند گفت: از خداست. بگو: با این حال آیا متذکّر نمی شوید؟'),
(2759, 23, 86, 'بگو: مالک آسمان های هفتگانه و پروردگار عرش بزرگ کیست؟'),
(2760, 23, 87, 'خواهند گفت: [آنها هم] در سیطره مالکیّت خداست. بگو: آیا [از پرستش بتان] نمی پرهیزید؟'),
(2761, 23, 88, 'بگو: اگر معرفت و شناخت دارید [بگویید:] کیست که [حاکمیّت مطلق و] فرمانروایی همه چیز به دست اوست و او پناه دهد و برخلاف خواسته اش به کسی [از عذاب] پناه داده نمی شود.'),
(2762, 23, 89, 'خواهند گفت: [این ویژگی ها] فقط برای خداست. بگو: پس چگونه [بازیچه] نیرنگ و افسون می شوید [و از راه خدا منحرفتان می کنند؟!]'),
(2763, 23, 90, 'چنین نیست [که آنان می گویند] بلکه ما حق را برای آنان آورده ایم، و بی تردید آنان دروغگویند.'),
(2764, 23, 91, 'خدا هیچ فرزندی برای خود نگرفته است، و هیچ معبودی با او نیست؛ [اگر جز خدا معبودی بود] در این صورت هر معبودی [برای آنکه به تنهایی و مستقل تدبیر امور کند] آفریده های خود را با خود می برد [و از مدار تصرف دیگر معبودان خارج می کرد] و بر یکدیگر برتری می جستند. منزّه و پاک است خدا از آنچه [او را به آن] وصف می کنند.'),
(2765, 23, 92, '[همان] دانای نهان و آشکار؛ پس او از آنچه برایش شریک می گیرند، برتر است.'),
(2766, 23, 93, 'بگو: پروردگارا! اگر آن [عذابی] را که به آنان وعده می دهند، به من بنمایانی؛'),
(2767, 23, 94, 'پس پروردگارا! مرا در میان گروه ستمکاران قرار مده [و با آنان قرین عذاب مکن.]'),
(2768, 23, 95, 'و به یقین ما تواناییم آن عذابی که به آنان وعده می دهیم به تو بنمایانیم.'),
(2769, 23, 96, 'بدی را با بهترین شیوه دفع کن؛ ما به آنچه که [مشرکان به ناحق ما را به آن] وصف می کنند، داناتریم.'),
(2770, 23, 97, 'و بگو: پروردگارا! از وسوسه های شیطان ها به تو پناه می آورم،'),
(2771, 23, 98, 'و پروردگارا! به تو پناه می آورم از اینکه [شیطان ها] نزد من حاضر شوند.'),
(2772, 23, 99, '[دشمنان حق از دشمنی خود باز نمی ایستند] تا زمانی که یکی از آنان را مرگ در رسد، می گوید: پروردگارا! مرا [برای جبران گناهان و تقصیرهایی که از من سر زده به دنیا] بازگردان؛'),
(2773, 23, 100, 'امید است در [برابر] آنچه [از عمر، مال و ثروت در دنیا] واگذاشته ام کار شایسته ای انجام دهم. [به او می گویند:] این چنین نیست [که می گویی] بدون تردید این سخنی بی فایده است که او گوینده آن است، و پیش رویشان برزخی است تا روزی که برانگیخته می شوند.'),
(2774, 23, 101, 'پس هنگامی که در صور دمیده شود، در آن روز نه میانشان خویشاوندی و نسبی وجود خواهد داشت و نه از اوضاع و احوال یکدیگر می پرسند؛'),
(2775, 23, 102, 'پس کسانی که اعمال وزن شده آنان سنگین [و باارزش] است، همانانند که رستگارند.'),
(2776, 23, 103, 'و کسانی که اعمال وزن شده آنان سبک [و بی ارزش] است، همانانند که سرمایه وجودشان را از دست داده و در دوزخ جاودانه اند.'),
(2777, 23, 104, 'آتش [همواره] صورت هایشان را می سوزاند، و آنان در آنجا [از شدت سوختگی] زشت رویانی بد منظرند!'),
(2778, 23, 105, '[خدا به آنان می گوید:] آیا آیات من بر شما خوانده نمی شد و شما [همواره] آنها را تکذیب می کردید؟'),
(2779, 23, 106, 'می گویند: پروردگارا! تیره بختی و شقاوت ما بر ما چیره شد، و ما گروهی گمراه بودیم.'),
(2780, 23, 107, 'پروردگارا! ما را از دوزخ بیرون آر، پس اگر [به بدی ها و گمراهی ها] بازگشتیم، بی تردید ستمکار خواهیم بود.'),
(2781, 23, 108, '[خدا] می گوید: [ای سگان!] در دوزخ گم شوید و با من سخن مگویید!'),
(2782, 23, 109, '[به یاد دارید که] گروهی از بندگان من بودند که می گفتند: پروردگارا! ما ایمان آوردیم، پس ما را بیامرز و به ما رحم کن که تو بهترین رحم کنندگانی.'),
(2783, 23, 110, 'ولی شما آنان را به مسخره گرفتید، تا [دل مشغولی شما به مسخره آنان] یاد مرا از خاطرتان برد، و شما همواره به [ایمان، عمل و دعاهای] آنان می خندیدید!!'),
(2784, 23, 111, 'من امروز آنان را در برابر صبری که [نسبت به مسخره و خنده شما] کردند پاداش دادم که همه آنان کامیابند.'),
(2785, 23, 112, '[خدا] می گوید: از جهت شمار سال ها چه مدت در زمین درنگ داشتید؟'),
(2786, 23, 113, 'می گویند: روزی یا بخشی از روزی، ولی [ای پرسنده!] از شمارندگان [پیشگاه خود] بپرس.'),
(2787, 23, 114, '[خدا] می گوید: اگر معرفت و شناخت می داشتید [می دانستید که] جز اندکی درنگ نکرده اید،'),
(2788, 23, 115, 'آیا پنداشته اید که شما را بیهوده و عبث آفریدیم، و اینکه به سوی ما بازگردانده نمی شوید؟'),
(2789, 23, 116, 'پس برتر است خدا آن فرمانروای حق [از آنکه کارش بیهوده و عبث باشد]، هیچ معبودی جز او نیست، [او] پروردگار عرش نیکو و باارزش است.'),
(2790, 23, 117, 'و هر کس با خدا معبود دیگری بپرستد [که] هیچ دلیلی بر حقّانیّت آن ندارد، حسابش فقط نزد پروردگار اوست؛ بدون تردید کافران، رستگار نمی شوند.'),
(2791, 23, 118, 'و بگو: پروردگارا! [مرا] بیامرز و [بر من] رحم کن و تو بهترین رحم کنندگانی.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '[این] سوره ای است که آن را [از اُفق دانش خود] فرود آوردیم و [اجرای احکام و معارف آن را] لازم و واجب نمودیم و در آن آیاتی روشن نازل کردیم، تا متذکّر و هوشیار شوید.'),
(2793, 24, 2, 'به زن زناکار و مرد زناکار صد تازیانه بزنید، و اگر به خدا و روز قیامت ایمان دارید، نباید شما را در [اجرای] دین خدا درباره آن دو نفر دلسوزی و مهربانی بگیرد، و باید گروهی از مؤمنان، شاهد مجازات آن دو نفر باشند.'),
(2794, 24, 3, 'مرد زناکار نباید جز با زن زناکار یا مشرک ازدواج کند، و زن زناکار نباید جز با مرد زناکار یا مشرک ازدواج نماید، و این [ازدواج] بر مؤمنان حرام شده است.'),
(2795, 24, 4, 'و کسانی که زنان عفیفه پاکدامن را به زنا متهم می کنند، سپس چهار شاهد نمی آورند، پس به آنان هشتاد تازیانه بزنید، و هرگز شهادتی را از آنان نپذیرید، و اینانند که در حقیقت فاسق اند.'),
(2796, 24, 5, 'مگر کسانی که بعد از آن توبه کنند و مفاسد خود را اصلاح نمایند که بدون تردید خدا [نسبت به آنان] بسیار آمرزنده و مهربان است.'),
(2797, 24, 6, 'و کسانی که همسران خود را متهم به زنا می کنند و بر آنان شاهدانی جز خودشان نباشد، پس هر کدام از آن شوهران [برای اثبات اتهامش] باید چهار بار شهادت دهد که سوگند به خدا، او [درباره همسرش] در این زمینه قطعاً راست می گوید،'),
(2798, 24, 7, 'و [شهادت] پنجم این است که [بگوید:] لعنت خدا بر او باد اگر [در این اتهام بستن] دروغگو باشد؛'),
(2799, 24, 8, 'و مجازات را از آن زنی که مورد اتهام قرار گرفته دفع می کند اینکه چهار بار شهادت دهد که سوگند به خدا، آن مرد دروغگوست،'),
(2800, 24, 9, 'و [شهادت] پنجم این است که [بگوید:] خشم خدا بر او باد اگر [آن مرد در این اتهام بستن] راستگو باشد.'),
(2801, 24, 10, 'و اگر فضل و رحمت خدا بر شما نبود، و اینکه خدا بسیار توبه پذیر و حکیم است [به کیفرهای بسیار سختی دچار می شدید.]'),
(2802, 24, 11, 'به یقین کسانی که آن تهمت [بزرگ] را [درباره یکی از همسران پیامبر به میان] آوردند، گروهی [هم دست و هم فکر] از [میان] خود شما بودند، آن را برای خود شرّی مپندارید، بلکه آن برای شما خیر است، برای هر مردی از آنان کیفری به میزان گناهی است که مرتکب شده، و آن کس که بخش عمده آن را بر عهده گرفته است، برایش عذابی بزرگ است.'),
(2803, 24, 12, 'چرا هنگامی که آن [تهمت بزرگ] را شنیدید، مردان و زنان مؤمن به خودشان گمان نیک نبردند [که این تهمت کار اهل ایمان نیست] و نگفتند: این تهمتی آشکار [از سوی منافقان] است؟! [که می خواهند در میان اهل ایمان فتنه و آشوب و بدبینی ایجاد کنند]'),
(2804, 24, 13, 'چرا بر آن تهمت، چهار شاهد نیاوردند؟ و چون شاهدان را نیاوردند، پس خود آنان نزد خدا محکوم به دروغگویی اند؛'),
(2805, 24, 14, 'و اگر فضل و رحمت خدا در دنیا و آخرت بر شما نبود، به یقین به خاطر آن تهمت بزرگی که در آن وارد شدید، عذابی بزرگ به شما می رسید.'),
(2806, 24, 15, 'چون [که آن تهمت بزرگ را] زبان به زبان از یکدیگر می گرفتید و با دهان هایتان چیزی می گفتید که هیچ معرفت و شناختی به آن نداشتید و آن را [عملی] ناچیز و سبک می پنداشتید و در حالی که نزد خدا بزرگ بود.'),
(2807, 24, 16, 'و چرا وقتی که آن را شنیدید نگفتید: ما را نسزد [و هیچ جایز نیست] که به این تهمت بزرگ زبان بگشاییم، شگفتا! این بهتانی بزرگ است.'),
(2808, 24, 17, 'خدا شما را اندرز می دهد که اگر ایمان دارید، هرگز مانند آن را [در حقّ کسی] تکرار نکنید.'),
(2809, 24, 18, 'و خدا آیات [خود] را برای شما بیان می کند، و خدا دانا و حکیم است.'),
(2810, 24, 19, 'کسانی که دوست دارند کارهای بسیار زشت [مانند آن تهمت بزرگ] در میان اهل ایمان شایع شود، در دنیا و آخرت عذابی دردناک خواهند داشت، و خدا [آنان را] می شناسد و شما نمی شناسید.'),
(2811, 24, 20, 'و اگر فضل و رحمت خدا بر شما نبود، و اینکه خدا رؤوف و مهربان است [به کیفرهای بسیار سختی دچار می شدید.]'),
(2812, 24, 21, 'ای مؤمنان! از گام های شیطان پیروی نکنید؛ و هر که از گام های شیطان پیروی کند [هلاک می شود] زیرا شیطان به کار بسیار زشت و عمل ناپسند فرمان می دهد؛ و اگر فضل و رحمت خدا بر شما نبود، هرگز احدی از شما [از عقاید باطل و اعمال و اخلاق ناپسند] پاک نمی شد، ولی خدا هر که را بخواهد پاک می کند؛ و خدا شنوا و داناست.'),
(2813, 24, 22, 'و از میان شما کسانی که دارندگان ثروت و گشایش مالی هستند، نباید سوگند یاد کنند که از انفاق مال به خویشاوندان و تهی دستان و مهاجرانِ در راه خدا دریغ ورزند، و باید [بدی آنان را که برای شما مؤمنان توانگر سبب خودداری از انفاق شده] عفو کنند و از مجازات درگذرند؛ آیا دوست نمی دارید خدا شما را بیامرزد؟ [اگر دوست دارید، پس شما هم دیگران را مورد عفو و گذشت قرار دهید]؛ و خدا بسیار آمرزنده و مهربان است.'),
(2814, 24, 23, 'بی تردید کسانی که زنان عفیفه پاکدامن باایمان را [که از شدت ایمان از بی عفتی و گناه بی خبرند] متهم به زنا کنند، در دنیا و آخرت لعنت می شوند، و برای آنان عذابی برزگ است.'),
(2815, 24, 24, '[در] روزی که زبان ها و دست ها و پاهایشان بر ضد آنان به گناهانی که همواره انجام می دادند، شهادت دهند.'),
(2816, 24, 25, 'در آن روز خدا کیفر به حق آنان را به طور کامل می دهد، و خواهند دانست که خدا همان حقّ آشکار است.'),
(2817, 24, 26, 'زنان پلید برای مردان پلید و مردان پلید برای زنان پلیدند، و زنان پاک برای مردان پاک و مردان پاک برای زنان پاک اند، این پاکان از سخنان ناروایی که [تهمت زنندگان] درباره آنان می گویند، مبرّا و پاک هستند، برای آنان آمرزش و رزق نیکویی است.'),
(2818, 24, 27, 'ای اهل ایمان! به خانه هایی غیر از خانه های خودتان وارد نشوید تا آنکه اجازه بگیرید، و بر اهل آنها سلام کنید، [رعایت] این [امور اخلاقی] برای شما بهتر است، باشد که متذکّر شوید.'),
(2819, 24, 28, 'نهایتاً اگر کسی را در آنها نیافتید، پس وارد آن نشوید تا به شما اجازه دهند، و اگر به شما گویند: برگردید، پس برگردید که این برای شما پاکیزه تر است، و خدا به آنچه انجام می دهید، داناست.'),
(2820, 24, 29, 'و بر شما گناهی نیست که به خانه های غیر مسکونی که در آنها کالا و متاع و سودی دارید وارد شوید، و خدا آنچه را آشکار می کنید و آنچه را پنهان می دارید، می داند.'),
(2821, 24, 30, 'به مردان مؤمن بگو: چشمان خود را [از آنچه حرام است مانند دیدن زنان نامحرم و عورت دیگران] فرو بندند، و شرمگاه خود را حفظ کنند، این برای آنان پاکیزه تر است، قطعاً خدا به کارهایی که انجام می دهند، آگاه است.'),
(2822, 24, 31, 'و به زنان باایمان بگو: چشمان خود را از آنچه حرام است فرو بندند، و شرمگاه خود را حفظ کنند، و زینت خود را [مانند لباس های زیبا، گوشواره و گردن بند] مگر مقداری که [طبیعتاً مانند انگشتر و حنا و سرمه، بر دست و صورت] پیداست [در برابر کسی] آشکار نکنند، و [برای پوشاندن گردن و سینه] مقنعه های خود را به روی گریبان هایشان بیندازند، و زینت خود را آشکار نکنند مگر برای شوهرانشان، یا پدرانشان، یا پدران شوهرانشان، یا پسرانشان، یا پسران شوهرانشان، یا برادرانشان، یا پسران برادرانشان، یا پسران خواهرانشان، یا زنان [هم کیش خود] شان، یا بردگان زر خریدشان، یا خدمتکارانشان از مردانی که ساده لوح و کم عقل اند و نیاز شهوانی حس نمی کنند، یا کودکانی که [به سنّ تمیز دادن خوب و بد نسبت به امیال جنسی] نرسیده اند. و زنان نباید پاهایشان را [هنگام راه رفتن آن گونه] به زمین بزنند تا آنچه از زینت هایشان پنهان می دارند [به وسیله نامحرمان] شناخته شود. و [شما] ای مؤمنان! همگی به سوی خدا بازگردید تا رستگار شوید.'),
(2823, 24, 32, '[مردان و زنان] بی همسرتان و غلامان و کنیزان شایسته خود را همسر دهید؛ اگر تهیدست اند، خدا آنان را از فضل خود بی نیاز می کند؛ و خدا بسیار عطا کننده و داناست.'),
(2824, 24, 33, 'و کسانی که [وسیله] ازدواجی نمی یابند باید پاکدامنی پیشه کنند تا خدا آنان را از فضل خود توانگرشان سازد. و کسانی از بردگانتان که درخواست نوشتن قرارداد [برای فعالیت اقتصادی و پرداخت مبلغی به مالک خود برای آزاد شدن] دارند، اگر در آنان [برای عقد این قرارداد] شایستگی سراغ دارید با آنان قرارداد ببندید، و چیزی از مال خدا را که به شما عطا کرده است به آنان بدهید، و کنیزان خود را که می خواهند پاکدامن باشند، برای به دست آوردن متاع ناچیز و زودگذر زندگی دنیا به زنا وادار مکنید، و هر که آنان را وادار [به زنا] کند، به یقین خدا پس از مجبور شدنشان [نسبت به آنان] بسیار آمرزنده و مهربان است.'),
(2825, 24, 34, 'و بی تردید آیاتی روشن و سرگذشتی از آنان که پیش از شما درگذشتند و پندی برای پرهیزکاران به سوی شما نازل کردیم.'),
(2826, 24, 35, 'خدا نور آسمان ها و زمین است؛ وصف نورش مانند چراغدانی است که در آن، چراغ پر فروغی است، و آن چراغ در میان قندیل بلورینی است، که آن قندیل بلورین گویی ستاره تابانی است، [و آن چراغ] از [روغن] درخت زیتونی پربرکت که نه شرقی است و نه غربی افروخته می شود، [و] روغن آن [از پاکی و صافی] نزدیک است روشنی بدهد گرچه آتشی به آن نرسیده باشد، نوری است بر فراز نوری؛ خدا هر کس را بخواهد به سوی نور خود هدایت می کند، و خدا برای مردم مثل ها می زند [تا حقایق را بفهمند] و خدا به همه چیز داناست.'),
(2827, 24, 36, '[این نور] در خانه هایی است که خدا اذن داده [شأن و منزلت و قدر و عظمت آنها] رفعت یابند و نامش در آنها ذکر شود، همواره در آن خانه ها صبح و شام او را تسبیح می گویند.'),
(2828, 24, 37, 'مردانی که تجارت و داد و ستد آنان را از یاد خدا و برپا داشتن نماز و پرداخت زکات باز نمی دارد، [و] پیوسته از روزی که دل ها و دیده ها در آن زیر و رو می شود، می ترسند.'),
(2829, 24, 38, '[این گونه عمل می کنند] تا خدا آنان را بر [پایه] نیکوترین عملی که انجام داده اند پاداش دهد، و از فضلش برای آنان بیفزاید، خدا به هر که بخواهد بی حساب روزی می دهد.'),
(2830, 24, 39, 'و کافران اعمالشان مانند سرابی در بیابانی مسطّح و صاف است که تشنه، آن را [از دور] آب می پندارد، تا وقتی که به آن رسد آن را چیزی نیابد، و خدا را نزد اعمالش می یابد که حسابش را کامل و تمام می دهد، و خدا حسابرسی سریع است.'),
(2831, 24, 40, 'یا [اعمالشان] مانند تاریکی هایی است در دریایی بسیار عمیق که همواره موجی آن را می پوشاند، و بالای آن موجی دیگر است، و بر فراز آن ابری است، تاریکی هایی است برخی بالای برخی دیگر؛ [مبتلای این امواج و تاریکی ها] هرگاه دستش را بیرون آورد، بعید است آن را ببیند. و کسی که خدا نوری برای او قرار نداده است، برای او هیچ نوری نیست.'),
(2832, 24, 41, 'آیا ندانسته ای که هرکه در آسمان ها و زمین است و پرندگان بال گشوده، خدا را تسبیح می گویند؟ به یقین هریک نماز و تسبیح خود را می داند؛ و خدا به آنچه انجام می دهند داناست.'),
(2833, 24, 42, 'و مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره خداست، و بازگشت همه به سوی خداست.'),
(2834, 24, 43, 'آیا ندانسته ای که خدا ابری را [به آرامی] می راند، آن گاه میان [اجزای] آن پیوند برقرار می کند، سپس آن را انبوه و متراکم می سازد، پس می بینی که باران از لابلای آن بیرون می آید، و از آسمان از کوه هایی که در آن ابر یخ زده است، تگرگی فرو می ریزد، پس آسیب آن را به هر که بخواهد می رساند، و از هر که بخواهد برطرف می کند، نزدیک است درخشندگی برقش دیده ها را کور کند.'),
(2835, 24, 44, 'خدا شب و روز را [دگرگون و] جابجا می کند؛ مسلماً در این دگرگونی و جابجایی عبرتی برای صاحبان بصیرت است.'),
(2836, 24, 45, 'و خدا هر جنبده ای را از آبی [که به صورت نطفه بود] آفرید، پس گروهی از آنها بر شکمشان راه می روند، و برخی از آنها به روی دو پا حرکت می کنند، و بعضی از آنها به روی چهار پا راه می روند. خدا آنچه را بخواهد می آفریند؛ مسلماً خدا بر هر کاری تواناست.'),
(2837, 24, 46, 'همانا آیاتی روشن نازل کردیم؛ و خدا هر که را بخواهد به راهی راست راهنمایی می کند.'),
(2838, 24, 47, 'و می گویند: به خدا و این پیامبر ایمان آوردیم و اطاعت کردیم، سپس گروهی از آنان پس از این [اعتراف] روی می گردانند، و اینان مؤمن [واقعی] نیستند.'),
(2839, 24, 48, 'و زمانی که آنان را به سوی خدا و پیامبرش می خوانند تا [نسبت به اختلافاتی که دارند] میانشان داوری کند، ناگهان گروهی از آنان روی گردان می شوند.'),
(2840, 24, 49, 'ولی اگر [بفهمند که] داوری حق به سود آنان می باشد، مطیعانه به سوی آن آیند.'),
(2841, 24, 50, 'آیا در دل هایشان بیماری [نفاق] است یا [در دین خدا] شک کرده اند یا می ترسند که خدا و پیامبرش بر آنان ستم کند؟ [چنین نیست] بلکه اینان خود ستمکارند.'),
(2842, 24, 51, 'گفتار مؤمنان هنگامی که آنان را به سوی خدا و پیامبرش می خوانند تا میانشان داوری کند، فقط این است که می گویند: شنیدیم و اطاعت کردیم و اینانند که رستگارند.'),
(2843, 24, 52, 'و کسانی که از خدا و پیامبرش اطاعت کنند و از خدا بترسند و از او پروا نمایند، پس آنان همان کامیابانند.'),
(2844, 24, 53, 'و با سخت ترین سوگندهایشان به خدا سوگند خوردند که اگر به آنان [برای بیرون رفتن به سوی جهاد] فرمان دهی حتماً بیرون می روند، بگو: سوگند نخورید، [آنچه بر شما واجب است] اطاعت پسندیده است [نه سوگند]، به یقین خدا به آنچه انجام می دهید، آگاه است.'),
(2845, 24, 54, 'بگو: خدا را [در همه امور] اطاعت کنید و این پیامبر را نیز اطاعت کنید؛ پس اگر روی بگردانید [زیانی متوجه پیامبر نمی شود، زیرا] بر او فقط آن [مسؤولیتی] است که بر عهده اش نهاده شده و بر شما هم آن [مسؤولیتی] است که بر عهده شما نهاده شده است. و اگر او را اطاعت کنید هدایت می یابید. و بر عهده این پیامبر جز رساندن آشکار [پیام وحی] نیست.'),
(2846, 24, 55, 'خدا به کسانی از شما که ایمان آورده و کارهای شایسته انجام داده اند، وعده داده است که حتماً آنان را در زمین جانشین [دیگران] کند، همان گونه که پیشینیان آنان را جانشین [دیگران] کرد، و قطعاً دینشان را که برای آنان پسندیده به سودشان استوار و محکم نماید، و یقیناً ترس و بیمشان را تبدیل به امنیت کند، [تا جایی که] فقط مرا بپرستند [و] هیچ چیزی را شریک من نگیرند. و آنان که پس از این نعمت های ویژه ناسپاسی ورزند [در حقیقت] فاسق اند.'),
(2847, 24, 56, 'و نماز را برپا دارید و زکات را بپردازید، و این پیامبر را اطاعت کنید تا مورد رحمت قرار گیرید.'),
(2848, 24, 57, 'گمان مکن کسانی که کافر شدند [برای آنان میسر است که] عاجز کننده [خدا] در زمین باشند [تا بتوانند از دسترس قدرت او بیرون روند]؛ و جایگاهشان آتش است، و بد جایگاهی است.'),
(2849, 24, 58, 'ای اهل ایمان! باید بردگانتان و کسانی از شما که به مرز بلوغ نرسیده اند [هنگام ورود به خلوت خانه شخصی شما] سه بار [در سه زمان] از شما اجازه بگیرند، پیش از نماز صبح، و هنگام [استراحت] نیم روز که جامه هایتان را کنار می نهید، و پس از نماز عشا؛ [این زمان ها] سه زمان خلوت شماست، بعد از این سه زمان بر شما و آنان گناهی نیست [که بدون اجازه وارد شوند؛ زیرا آنان] همواره نزد شما در رفت و آمدند و با یکدیگر نشست و برخاست دارید. این گونه خدا آیاتش را برای شما بیان می کند، و خدا دانا و حکیم است.'),
(2850, 24, 59, 'و هنگامی که کودکان شما به مرز بلوغ رسیدند، باید [برای ورود به خلوت خانه شخصی شما] اجازه بگیرند، همان گونه که کسانی که پیش از آنان [به مرز بلوغ رسیده بودند] اجازه می گرفتند. خدا این گونه آیاتش را برای شما بیان می کند؛ و خدا دانا و حکیم است.'),
(2851, 24, 60, 'و بر زنان از کار افتاده ای که امید ازدواجی ندارند، گناهی نیست که حجاب و روپوش خود را کنار بگذارند، در صورتی که با زیور و آرایش خویش قصد خودآرایی نداشته باشند. و پاکدامنی برای آنان بهتر است؛ و خدا شنوا و داناست.'),
(2852, 24, 61, 'بر نابینا و لنگ و بیمار و خود شما گناهی نیست که [بدون هیچ اجازه ای از خانه هایی که ذکر می شود، غذا] بخورید: از خانه های خودتان، یا خانه های پدرانتان، یا خانه های مادرانتان یا خانه های برادرانتان، یا خانه های خواهرانتان، یا خانه های عموهایتان، یا خانه های عمّه هایتان، یا خانه های دایی هایتان، یا خانه های خاله هایتان، یا خانه هایی که کلیدهایشان در اختیار شماست، یا خانه های دوستانتان؛ بر شما گناهی نیست که [با دیگر اعضای خانواده خود] دسته جمعی یا جدا جدا غذا بخورید. پس هرگاه به خانه هایی [که ذکر شد] وارد شدید، بر خودتان سلام کنید که درودی است از سوی خدا [درودی] پربرکت و پاکیزه، خدا این گونه آیات را برای شما بیان می کند تا بیندیشید.'),
(2853, 24, 62, 'مؤمنان فقط آنانند که به خدا و پیامبرش ایمان آورده اند، و هنگامی که بر سر کار مهمی [که طبیعتاً مردم را گرد هم می آورد] با پیامبر باشند تا از او اجازه نگیرند [از نزد او] نمی روند. به راستی کسانی که [برای رفتن] از تو اجازه می گیرند، آنانند که به خدا و پیامبرش ایمان دارند. پس هنگامی که برای برخی از کارهایشان از تو اجازه می خواهند، به هر کدام از آنان که خواستی اجازه بده و از خدا برای آنان آمرزش بخواه؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(2854, 24, 63, 'دعوت پیامبر را [به ایمان، عمل صالح و امور اجتماعی] در میان خود مانند دعوت بعضی از شما از بعضی دیگر قرار ندهید، خدا کسانی از شما را که برای [بی اعتنایی به دعوت پیامبر] با پنهان شدن پشت سر دیگران آهسته از نزد او بیرون می روند می شناسد. پس باید کسانی که از فرمانش سرپیچی می کنند، برحذر باشند از اینکه بلایی [در دنیا] یا عذابی دردناک [در آخرت] به ایشان رسد.'),
(2855, 24, 64, 'آگاه باشید! که آنچه در آسمان ها و زمین است فقط در سیطره مالکیّت و فرمانروایی خداست. به یقین آنچه را که شما [از حالات، اعمال و خواسته ها] بر آن هستید می داند، و روزی را که [برای حسابرسی] به سوی او باز گردانده می شوند، پس آنان را به آنچه انجام داده اند، آگاه می کند و خدا به همه چیز داناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'همیشه سودمند و با برکت است آنکه فرقان را [که قرآن جدا کننده حق از باطل است] به تدریج بر بنده اش نازل کرد، تا برای جهانیان بیم دهنده باشد.'),
(2857, 25, 2, 'همان که فرمانروایی آسمان ها و زمین فقط در سیطره اوست و فرزندی برای خود نگرفته است و در فرمانروایی شریکی ندارد و هر چیزی را آفریده و آن را به اندازه قرار داده، اندازه ای درست و دقیق.'),
(2858, 25, 3, 'و [مشرکان] به جای او معبودانی اختیار کرده اند که چیزی را نمی آفرینند و خود آفریده می شوند، و برای خودشان مالک زیان و سودی نیستند، و قدرت و تسلطی بر مرگ و حیات و برانگیختن پس از مرگ ندارند،'),
(2859, 25, 4, 'و کافران گفتند: این [قرآن] چیزی نیست جز دروغی که [پیامبر] آن را از پیش خود ساخته و دیگران او را بر ساختن آن یاری داده اند، بی تردید [با این نسبت ناروا] مرتکب ستمی سنگین و دروغی بزرگ [و تهمتی زشت] شده اند.'),
(2860, 25, 5, 'و گفتند: افسانه های مکتوب پیشینیان است که نوشتن [از روی] آنها را از [نویسندگان] درخواست کرده است و آن [نوشته] ها هر صبح و شام بر او خوانده می شود [تا حفظ کند و بر ما بخواند و بگوید: این وحی آسمانی است!!]'),
(2861, 25, 6, 'بگو: آن را کسی نازل کرده است که همه نهان ها را در آسمان ها و زمین می داند، همانا او همواره بسیار آمرزنده و مهربان است.'),
(2862, 25, 7, 'و گفتند: این چه پیامبری است که غذا می خورد و در بازارها راه می رود؟ چرا فرشته ای به سوی او نازل نشده که همراه او بیم دهنده باشد؟'),
(2863, 25, 8, 'یا [چرا] گنجی به سوی او افکنده نمی شود؟ یا باغی برای او نمی باشد که از [میوه های] آن بهره مند شود؟ و ستمکاران گفتند: [ای مردم!] شما جز مردی جادو شده را پیروی نمی کنید!!'),
(2864, 25, 9, 'بنگر که چگونه [و برپایه چه امور نامعقولی] اوصافی برای تو بیان کردند، پس [به سبب لجاجت، تکبّر، دشمنی و تعصّب] گمراه شدند و نمی توانند راهی [به سوی حق] بیابند.'),
(2865, 25, 10, 'همیشه سودمند و بابرکت است آنکه اگر بخواهد بهتر از آن را [در دنیا] برای تو قرار می دهد، بوستان هایی که از زیرِ [درختانِ] آن نهرها جاری باشد، و قصرهایی [استوار و مجلّل] برای تو مقرّر می نماید.'),
(2866, 25, 11, '[همه این مطالب و خواسته های نامعقول بهانه است] بلکه آنان قیامت را تکذیب کرده اند [و به این سبب نبوّت تو را باور نمی کنند] و ما برای آنان که قیامت را تکذیب کنند، آتشی سوزان آماده کرده ایم.'),
(2867, 25, 12, 'که وقتی [آن آتش سوزان] آنان را از مکانی دور ببیند، از آن [نعره] خشم و خروشی هولناک بشنوند،'),
(2868, 25, 13, 'و هنگامی که آنان را در حالی که با غل و زنجیر به هم بسته شده اند در مکانی تنگ از آن آتش سوزان بیفکنند، در آنجا فریاد مرگ خواهی سر دهند.'),
(2869, 25, 14, '[به آنان می گویند:] امروز یک بار درخواست مرگ نکنید، بلکه بسیار درخواست مرگ کنید.'),
(2870, 25, 15, 'بگو: آیا این [آتش سوزان] بهتر است یا بهشت جاودانی که به پرهیزکاران وعده داده اند که پاداش و بازگشت گاه آنان است؟'),
(2871, 25, 16, 'در آنجا هر چه بخواهند در حالی که جاودانه اند برای آنان فراهم است، این بر عهده پروردگارت وعده ای است درخواست شده [و مورد انتظار اهل ایمان از خدای بخشنده و کریم.]'),
(2872, 25, 17, 'و [یاد کن] روزی را که آنان را با معبودانی که به جای خدا می پرستیدند، محشور می کند، پس [به آن معبودان] می گوید: آیا شما این بندگان مرا گمراه کردید، یا خودشان راه را گم کردند؟'),
(2873, 25, 18, 'پاسخ می دهند: شگفتا! سزاوار ما نبود که در برابر تو دوستان و پیروانی برای [پرستش] خود بگیریم، ولی تو اینان و پدرانشان را [از نعمت ها] برخوردار کردی [و آنان به جای شکر نعمت ها چنان در شهوات غرق شدند] تا آنکه یاد تو را فراموش کردند و گروهی هلاکت یافته شدند.'),
(2874, 25, 19, '[خدا می گوید: این معبودان شما] گفته شما را که می گفتید [اینان به جای خدا معبودان ما هستند] تکذیب کردند، اکنون نه می توانید [عذاب را ازخود] دفع کنید، و نه می توانید [برای خود یاور و] یاری بیابید. و هر که از شما [در این دنیا] ستم کند، در قیامت عذابی بزرگ به او می چشانیم.'),
(2875, 25, 20, 'و ما پیش از تو هیچ یک از پیامبران را نفرستادیم مگر آنکه آنان هم غذا می خوردند و در بازارها راه می رفتند، و ما برخی از شما را [وسیله] آزمایش برخی دیگر قرار دادیم [توانگر را به تهیدست وتهیدست را به توانگر، بیمار را به تندرست و تندرست را به بیمار، پیامبر را به امت و امت را به پیامبر]. آیا [نسبت به اجرای احکام الهی و تکالیف و مسؤولیت ها] شکیبایی می ورزید؟ و پروردگارت همواره [به احوال و اعمال همه] بیناست.'),
(2876, 25, 21, 'و آنان که به دیدار [قیامت و محاسبه اعمال به وسیله] ما امید ندارند، گفتند: چرا بر ما فرشتگانی نازل نشد، یا [چرا] پروردگارمان را نمی بینیم؟ به راستی که خودشان را در درون خود بزرگ شمردند و دچار سرکشی بزرگی شدند.'),
(2877, 25, 22, 'روزی که آنان فرشتگان را می بینند، آن روز برای مجرمان بشارتی نیست؛ و آنان [به فرشتگان] می گویند: [از شما درخواست داریم که ما را] امان دهید [و آسیب وگزند عذاب را از ما] مانع شوید.'),
(2878, 25, 23, 'و ما به [بررسی و حسابرسی] هر عملی که [به عنوان عمل خیر] انجام داده اند، می پردازیم، پس همه آنها را غباری پراکنده می سازیم.'),
(2879, 25, 24, 'اهل بهشت در آن روز، قرارگاهشان بهتر و استراحت گاهشان نیکوتر است.'),
(2880, 25, 25, 'و [یاد کن] روزی را که آسمان به سبب ابری [که بر فراز آن است] می شکافد و فرشتگان به تدریج [به صورتی ویژه] فرستاده شوند.'),
(2881, 25, 26, 'در آن روز فرمانروایی مطلق برای [خدای] رحمان ثابت است، و بر کافران روزی بسیار دشوار است،'),
(2882, 25, 27, 'و روزی که ستمکار، دو دست خود را [از شدت اندوه و حسرت به دندان] می گزد [و] می گوید: ای کاش همراه این پیامبر راهی به سوی حق برمی گرفتم،'),
(2883, 25, 28, 'ای وای، کاش من فلانی را [که سبب بدبختی من شد] به دوستی نمی گرفتم،'),
(2884, 25, 29, 'بی تردید مرا از قرآن پس از آنکه برایم آمد گمراه کرد. و شیطان همواره انسان را [پس از گمراه کردنش تنها و غریب در وادی هلاکت] وامی گذارد؛'),
(2885, 25, 30, 'و پیامبر [درقیامت] می گوید: پروردگارا! همانا قوم من این قرآن را متروک گذاشتند!'),
(2886, 25, 31, 'و ما این گونه برای هر پیامبری دشمنانی از مجرمان قرار دادیم، و کافی است که پروردگارت [برای تو] راهنما و یاری دهنده باشد.'),
(2887, 25, 32, 'و کافران گفتند: چرا قرآن یک باره بر او نازل نشد؟ این گونه [قرآن را به تدریج نازل می کنیم] تا قلب تو را به آن استوار سازیم، و آن را بر تو با مهلت و آرامی خواندیم.'),
(2888, 25, 33, 'و [دشمنان] هیچ وصف و سخن باطلی بر ضد تو نمی آورند، مگر آنکه ما حق را و نیکوترین تفسیر را [برای در هم شکستن آن] برای تو می آوریم.'),
(2889, 25, 34, 'همانان که [در قیامت] به رو در افتاده به سوی دوزخ محشور می شوند، آنان بدترین جایگاه را دارند، و گمراه ترین مردم اند.'),
(2890, 25, 35, 'و به راستی به موسی کتاب دادیم، و برادرش هارون را همراه او دستیار و کمک قرار دادیم.'),
(2891, 25, 36, 'پس گفتیم: هر دو به سوی گروهی که آیات ما را تکذیب کردند، بروید. [آن گروه در برابر حق لجاجت و تکبّر ورزیدند] در نتیجه آنان را به شدت درهم کوبیده و هلاک کردیم.'),
(2892, 25, 37, 'و قوم نوح را هنگامی که پیامبران را تکذیب کردند، غرق کردیم، و آنان را برای مردم نشانه ای [عبرت آموز] قرار دادیم، و برای ستمکاران عذابی دردناک آماده کرده ایم.'),
(2893, 25, 38, 'و قوم عاد و ثمود و اهل رسّ و اقوام بسیاری را [نیز که] در [فاصله] میان آن [قوم نوح و اهل رسّ بودند، هلاک کردیم.]'),
(2894, 25, 39, 'و برای هر یک [به جهت هدایتشان] سرگذشت های عبرت آموز بیان کردیم، و [چون هدایت نیافتند] هر یک را به شدت در هم شکستیم وهلاک کردیم.'),
(2895, 25, 40, 'یقیناً مشرکان مکه بر شهری که [محل زندگی قوم لوط بود و] بر آن باران عذاب باریده شد، گذر کرده اند، پس آیا آنجا را ندیده اند؟ [چرا! دیده اند] ولی [عبرت نگرفته اند، چون] برانگیخته شدن [مردگان] را [برای رسیدن به پاداش اعمال] امید و انتظار ندارند.'),
(2896, 25, 41, 'و هنگامی که تو را می بینند جز به مسخره ات نمی گیرند [و می گویند:] آیا این است آنکه خدا او را به پیامبری برانگیخته است؟!'),
(2897, 25, 42, 'اگر ما بر پرستش بتهایمان ایستادگی نمی کردیم، نزدیک بود ما را از پرستش آنها منحرف کند. سپس وقتی که عذاب را می بینند خواهند دانست که چه کسی گمراه تر است.'),
(2898, 25, 43, 'آیا کسی که هوای [نفسش] را معبود خود گرفته دیدی؟ آیا تو می توانی کارساز ونگهبان او باشی [که او را به میل خود به راه راست هدایت کنی؟]'),
(2899, 25, 44, 'آیا گمان می کنی بیشتر آنان [سخن حق را] می شنوند، یا [در حقایق] می اندیشند؟ آنان جز مانند چهارپایان نیستند بلکه آنان گمراه ترند!'),
(2900, 25, 45, 'آیا به [قدرت و حکمت] پروردگارت ننگریستی که چگونه سایه را امتداد داد و گستراند؟ و اگر می خواست آن را ساکن و ثابت می کرد، آن گاه خورشید را برای [شناختن] آن سایه، راهنما [ی انسان ها] قرار دادیم.'),
(2901, 25, 46, 'سپس آن را [با بلند شدن آفتاب] اندک اندک به سوی خود باز می گیریم.'),
(2902, 25, 47, 'و اوست که شب را برای شما پوشش، و خواب را مایه استراحت و آرامش، و روز را [زمان] پراکنده شدن [جهت فعالیت و کوشش] قرار داد.'),
(2903, 25, 48, 'و اوست که بادها را پیشاپیش [باران] رحمتش به عنوان مژده دهنده باران فرستاد، و از آسمان آبی پاک و پاک کننده نازل کردیم.'),
(2904, 25, 49, 'تا به وسیله آن سرزمینی مرده را زنده کنیم و آن را به آفریده های خود از دام ها ومردمان بسیار بنوشانیم.'),
(2905, 25, 50, 'ما باران را میان آنان [از منطقه ای به منطقه دیگر] گرداندیم تا متذکّر [قدرت و رحمت من] شوند، ولی بیشتر مردم جز به ناسپاسی و کفران رضایت ندادند.'),
(2906, 25, 51, 'و اگر می خواستیم حتماً در هر شهری [پیامبری] بیم دهنده مبعوث می کردیم [ولی به سبب کمال و جامعیّت، پیامبری را به تو ختم کردیم.]'),
(2907, 25, 52, 'پس کافران را [که انتظار دارند از ابلاغ وحی باز ایستی،] فرمان مبر و به وسیله این [قرآن] با آنان جهاد [ی فرهنگی و تبلیغی] کن.'),
(2908, 25, 53, 'و اوست که دو دریا را به هم آمیخت، این خوش طعم و گوارا، و این شور و تلخ است، ومیان آن دو مانع و حایل و سدّی نفوذناپذیر واستوار قرار داد [تا به هم مخلوط نشوند.]'),
(2909, 25, 54, 'و اوست که از آب، بشری آفرید و او را دارای [دو نوع پیوند] نسبی و سببی کرد؛ و پروردگارت همواره تواناست.'),
(2910, 25, 55, 'و به جای خدا چیزهایی می پرستند که نه سودشان می دهد ونه زیانشان می رساند؛ و کافر همواره بر ضد پروردگارش پشتیبان و یار [شیطان] است.'),
(2911, 25, 56, 'تو را جز مژده رسان و بیم دهنده نفرستادیم.'),
(2912, 25, 57, 'بگو: من از شما [در برابر تبلیغ دین هیچ] پاداشی نمی خواهم، جز اینکه هر که بخواهد [می تواند از برکت هدایت من] راهی به سوی پروردگارش بگیرد.'),
(2913, 25, 58, 'و بر آن زنده ای که هرگز نمی میرد توکل کن، و او را همراه با ستایش تسبیح گوی، و کافی است که او به گناهان بندگانش آگاه باشد.'),
(2914, 25, 59, 'همان که آسمان ها و زمین و آنچه را میان آنهاست در شش روز آفرید، آن گاه بر تخت فرمانروایی و تدبیر امور آفرینش چیره و مسلط شد، او رحمان است پس [ای انسان! درباره خدا و کیفیت آفرینش جهان هستی] از خبیر آگاهی بپرس.'),
(2915, 25, 60, 'و هنگامی که به آنان گویند: برای رحمان سجده کنید، می گویند: رحمان چیست؟! آیا برای چیزی که تو فرمان می دهی، سجده کنیم؟ و [دعوت تو] بر رمیدگی و نفرتشان می افزاید.'),
(2916, 25, 61, 'همیشه سودمند و با برکت است آنکه در آسمان برج هایی قرار داد، و در آن چراغی روشن وماهی تابان پدید آورد.'),
(2917, 25, 62, 'و اوست که برای کسی که بخواهد متذکّر [هوشیار حقایق] شود یا بخواهد سپاس گزاری کند، شب و روز را جانشین یکدیگر قرار داد.'),
(2918, 25, 63, 'و بندگان رحمان کسانی اند که روی زمین با آرامش و فروتنی راه می روند، و هنگامی که نادانان آنان را طرف خطاب قرار می دهند [در پاسخشان] سخنانی مسالمت آمیز می گویند،'),
(2919, 25, 64, 'و آنان که شب را برای پروردگارشان با سجده و قیام به صبح می رسانند،'),
(2920, 25, 65, 'و آنان که می گویند: پروردگارا! عذاب [دوزخ] را از ما بگردان که مسلماً عذاب آن پایدار و همیشگی است.'),
(2921, 25, 66, 'قطعاً دوزخ بد قرارگاه و بد اقامت گاهی است.'),
(2922, 25, 67, 'و آنان که وقتی انفاق می کنند، نه از حدّ معمول [و متعارف] می گذرند و نه تنگ می گیرند، و [انفاقشان] همواره میان این دو در حدّ اعتدال است.'),
(2923, 25, 68, 'و آنان که معبود دیگری را با خدا نمی پرستند، و کسی را که خدا خونش را حرام کرده است، جز به حق نمی کشند، و زنا نمی کنند؛ و کسی که این اعمال را مرتکب شود به کیفر سختی برسد.'),
(2924, 25, 69, 'روز قیامت عذابش دو چندان شود، و در آن با خواری و سرشکستگی جاودانه ماند.'),
(2925, 25, 70, 'مگر آنان که توبه کنند و ایمان آورند و کار شایسته انجام دهند، که خدا بدی هایشان را به خوبی ها تبدیل می کند؛ و خدا بسیار آمرزنده و مهربان است.'),
(2926, 25, 71, 'و هر که توبه کند و کار شایسته انجام دهد قطعاً به صورتی پسندیده و نیکو به سوی خدا باز می گردد.'),
(2927, 25, 72, 'و آنان که در مجلس غنا و دروغ پردازی و امور باطل حاضر نمی شوند، و هنگامی که بر گفتار و کردار لغو می گذرند، با بزرگواری و متانت می گذرند،'),
(2928, 25, 73, 'و آنان که وقتی به آیات پروردگارشان پندشان دهند، در برابر آن با حالت کری و کوری نمی افتند، [بلکه با گوش شنوا و چشم بصیرت به آن دل می دهند.]'),
(2929, 25, 74, 'و آنان که می گویند: پروردگارا! ما را از سوی همسران و فرزندانمان خوشدلی و خوشحالی بخش، و ما را پیشوای پرهیزکاران قرار ده.'),
(2930, 25, 75, 'اینانند که به خاطر صبری که [در برابر حوادث و اجرای تکالیف الهی] از خود نشان دادند با [برترین] مکان ها [ی بهشت] پاداششان می دهند، و در آن با درود و سلامی [از سوی خدا و فرشتگان] روبرو می شوند.'),
(2931, 25, 76, 'در آنجا جاودانه اند. نیکو قرارگاه و خوب اقامت گاهی است.'),
(2932, 25, 77, 'بگو: اگر دعایتان نباشد پروردگارم به شما ارجی ننهد، پس شما که [قاطعانه آیات خدا و پیامبرش را] تکذیب کردید، [کیفر این تکذیب برای همیشه] ملازم [شما] خواهد بود.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسم'),
(2934, 26, 2, 'این است آیات [با عظمت این] کتاب روشنگر.'),
(2935, 26, 3, 'شاید تو می خواهی برای اینکه آنان ایمان نمی آورند، خود را از شدت اندوه هلاک کنی!'),
(2936, 26, 4, 'اگر بخواهیم، معجزه ای بزرگ از آسمان بر آنان نازل می کنیم که فروتنانه و بی اختیار در برابرش گردن نهند،'),
(2937, 26, 5, 'هیچ [مایه] تذکر جدیدی از سوی [خدای] رحمان برای آنان نمی آید، مگر آنکه از آن روی گردان می شوند.'),
(2938, 26, 6, 'همانا [آیات و وعده های ما را] تکذیب کردند [تا جایی که آنها را به مسخره گرفتند] پس به زودی خبرهای [مهم] آنچه که آن را مسخره می کردند [از حقّانیّت آیات، وعده های ما و تحقّق عذاب های دنیایی و آخرتی] به آنان می رسد.'),
(2939, 26, 7, 'آیا [با تأمل] به این زمین ننگریستند که در آن چه بسیار از هر نوع [گیاهان و درختان] نیکو رویاندیم؟'),
(2940, 26, 8, 'یقیناً در این [رویاندن انواع نباتات] نشانه ای است [بر قدرت، ربوبیّت خدا و اینکه مردگان را زنده می کند] ولی بیشترشان [به خاطر رسوخ کبر و لجاجت در باطنشان] ایمان آور نبوده و نیستند.'),
(2941, 26, 9, 'و یقیناً پروردگارت همان توانای شکست ناپذیر مهربان است.'),
(2942, 26, 10, '[یاد کن] هنگامی را که پروردگارت موسی را ندا داد که خود را به قوم ستمکار برسان.'),
(2943, 26, 11, '[به سوی] قوم فرعون [که به آنان بگویی:] آیا [از سرانجام شرک و طغیان که خشم و عذاب خداست] نمی پرهیزید؟!'),
(2944, 26, 12, 'گفت: پروردگارا! به راستی می ترسم مرا تکذیب کنند،'),
(2945, 26, 13, 'و سینه ام [از این وظیفه سنگین] تنگی می کند، و زبانم روان و گویا نمی شود، پس به سوی هارون هم [پیام نبوّت] بفرست [تا مرا در این وظیفه سنگین یاری دهد.]'),
(2946, 26, 14, 'و آنان را بر من ادعای جرمی است که می ترسم [به سبب آن] مرا بکشند.'),
(2947, 26, 15, '[خدا] گفت: این چنین نیست [که فرعون بر تو پیروز شود] پس شما دو نفر معجزات ما را ببرید که یقیناً ما همراه شما شنونده [گفتار هر دو طرف] هستیم [پس شما دو نفر را بر پایه دلیل و برهان و معجزه بر آنان پیروز می کنیم.]'),
(2948, 26, 16, 'بنابراین خود را به فرعون برسانید و بگویید: یقیناً ما فرستاده پروردگار جهانیانیم.'),
(2949, 26, 17, 'با این وصف، بنی اسرائیل را [آزاد کن و] با ما بفرست.'),
(2950, 26, 18, '[فرعون] گفت: آیا ما تو را در کودکی در میان خود نپروراندیم، و سالیانی چند از عمر خود در میان ما درنگ نکردی؟'),
(2951, 26, 19, 'و آن کارت را که کردی، کردی در حالی که از ناسپاسان [زحمات و نعمت های ما] بودی.'),
(2952, 26, 20, 'گفت: آن را در آن زمان در حالی انجام دادم که بی خبر از این واقعه بودم [که با مشت من در دفاع از مظلوم، آن مرد قبطی کشته می شود.]'),
(2953, 26, 21, 'چون از شما ترسیدم از دستتان گریختم، پس [با این گریز از عرصه زندگی ظالمانه شما این شایستگی را یافتم که] پروردگارم به من حکمت بخشید، و مرا از پیامبران قرار داد.'),
(2954, 26, 22, 'آیا اینکه بنی اسرائیل را به بردگی گرفته ای نعمتی است که منتش را بر من می نهی؟!'),
(2955, 26, 23, 'فرعون گفت: پروردگار جهانیان چیست؟'),
(2956, 26, 24, 'گفت: اگر باور می کنید همان پروردگار آسمان ها و زمین و آنچه میان آنهاست.'),
(2957, 26, 25, '[فرعون از روی مسخره] به افراد پیرامونش گفت: آیا نمی شنوید [که چه می گوید؟!]'),
(2958, 26, 26, '[موسی] گفت: [او] پروردگار شما وپروردگار پدران پیشین شماست.'),
(2959, 26, 27, '[فرعون] گفت: این پیامبرتان که به سوی شما فرستاده اند، حتماً مجنون است!'),
(2960, 26, 28, '[موسی] گفت: پروردگار مشرق و مغرب و آنچه میان آنهاست، اگر می اندیشید.'),
(2961, 26, 29, '[فرعون] گفت: اگر معبودی جز من بگیری، قطعاً تو را از زندانیان [که زیر سخت ترین شکنجه اند] قرار خواهم داد.'),
(2962, 26, 30, '[موسی] گفت: آیا اگر [بر صدق نبوتم] معجزه آشکاری برایت آورده باشم [باز هم مرا به زندان می افکنی؟]'),
(2963, 26, 31, 'گفت: اگر راستگویی آن را بیاور.'),
(2964, 26, 32, 'پس عصایش را انداخت و ناگهان اژدهایی حقیقی نمایان شد،'),
(2965, 26, 33, 'و دستش را [ازگریبانش] بیرون آورد و ناگهان برای بینندگان سپید و روشن بود.'),
(2966, 26, 34, '[فرعون] به سران و اشراف پیرامونش گفت: یقیناً این جادوگری بسیار ماهر و داناست!'),
(2967, 26, 35, 'می خواهد با جادویش شما را از سرزمینتان بیرون کند، پس شما چه نظر می دهید؟'),
(2968, 26, 36, 'گفتند: کار او و برادرش را به تأخیر انداز و گروهی گردآورنده را به شهرها روانه کن،'),
(2969, 26, 37, 'تا هر جادوگر کاردان بسیار دانایی را نزد تو آورند.'),
(2970, 26, 38, 'پس همه جادوگران را در وعده گاه روزی معین گرد آوردند،'),
(2971, 26, 39, 'و به همه مردم گفتند: آیا شما هم اجتماع خواهید کرد؟'),
(2972, 26, 40, 'به امید آنکه اگر جادوگران پیروز شدند، از آنان پیروی کنیم.'),
(2973, 26, 41, 'پس هنگامی که جادوگران آمدند، به فرعون گفتند: آیا اگر ما پیروز شویم، حتماً برای ما پاداشی شایسته خواهد بود؟'),
(2974, 26, 42, 'گفت: آری، و در آن صورت مسلماً از مقربان [درگاه] خواهید شد.'),
(2975, 26, 43, 'موسی به جادوگران گفت: بیفکنید آنچه را قصد دارید، بیفکنید.'),
(2976, 26, 44, 'پس ریسمان ها و عصاهایشان را افکندند، و گفتند: به عزت فرعون سوگند که به راستی ما به طور مسلّم پیروزیم.'),
(2977, 26, 45, 'پس موسی عصایش را افکند، ناگاه آنچه را جادوگران با نیرنگ [و به صورت غیر واقعی] ساخته بودند، بلعید.'),
(2978, 26, 46, 'پس جادوگران [از هیبت و عظمت این معجزه که یافتند کاری خدایی است نه جادویی] به سجده افتادند.'),
(2979, 26, 47, 'گفتند: ما به پروردگار جهانیان ایمان آوردیم،'),
(2980, 26, 48, 'پروردگار موسی و هارون.'),
(2981, 26, 49, 'فرعون گفت: پیش از آنکه به شما اجازه دهم به او ایمان آوردید، حتماً او بزرگ شماست که جادوگری را به شما آموخته است، پس به زودی خواهید دانست که مسلماً دست ها و پاهای شما را یکی از راست و یکی از چپ قطع خواهم کرد، و یقیناً همه شما را به دار خواهم آویخت.'),
(2982, 26, 50, 'گفتند: [در این شکنجه و عذاب] هیچ زیان و باکی [بر ما] نیست، یقیناً ما به سوی پروردگارمان باز می گردیم،'),
(2983, 26, 51, 'قطعاً ما امیدواریم که چون نخستین ایمان آورندگان [از این قوم] بودیم، پروردگارمان خطاهای ما را بیامرزد.'),
(2984, 26, 52, 'و به موسی وحی کردیم که بندگان مرا شبانه کوچ بده که حتماً دشمنان از پی شما خواهند آمد.'),
(2985, 26, 53, 'پس فرعون گروهی گردآورنده را [برای جمع کردن نیرو] به شهرها فرستاد'),
(2986, 26, 54, '[و گفت:] مسلماً اینان [که بر ضد من برخاسته اند] گروهی اندکند،'),
(2987, 26, 55, 'و همانا ما را همواره [با اعمالشان] به خشم می آورند،'),
(2988, 26, 56, 'و ما گروهی هوشیار و آماده باش و مهیای دفاعیم.'),
(2989, 26, 57, 'پس ما آنان را [به دنبال بنی اسرائیل] از باغ ها و چشمه سارها [که نعمت های ما در سرزمین مصر بود] بیرون کردیم.'),
(2990, 26, 58, 'واز گنج ها ومکان های نیکو [وقصرهای باشکوه بیرون کردیم].'),
(2991, 26, 59, 'این گونه [نعمت های خود را از چنگ آنان بیرون آوردیم] و آنها را به بنی اسرائیل به میراث دادیم.'),
(2992, 26, 60, 'فرعونیان هنگام طلوع آفتاب آنان را دنبال کردند.'),
(2993, 26, 61, 'چون آن دو گروه یکدیگر را دیدند، اصحاب موسی گفتند: حتماً ما به چنگ آنان خواهیم افتاد.'),
(2994, 26, 62, 'موسی گفت: این چنین نیست، بی تردید پروردگارم با من است، و به زودی مرا هدایت خواهد کرد.'),
(2995, 26, 63, 'پس به موسی وحی کردیم که عصایت را به این دریا بزن. [موسی عصایش را به دریا زد] پس [دریا] از هم شکافت و هر پاره اش چون کوهی بزرگ بود.'),
(2996, 26, 64, 'و آن گروه دیگر را [هم در آنجا] به دریا نزدیک کردیم،'),
(2997, 26, 65, 'و موسی و هر که با او بود، همه را نجات دادیم.'),
(2998, 26, 66, 'آن گاه آن گروه دیگر را غرق کردیم.'),
(2999, 26, 67, 'بی تردید در این سرگذشت عبرتی بزرگ وجود دارد، و [قوم قبطی] بیشترشان مؤمن نبودند.'),
(3000, 26, 68, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهربان است.'),
(3001, 26, 69, 'و سرگذشت مهم ابراهیم را بر آنان بخوان،'),
(3002, 26, 70, 'هنگامی که به پدرش وقومش گفت: چه چیز را می پرستید؟'),
(3003, 26, 71, 'گفتند: بت هایی را می پرستیم و همواره ملازم پرستش آنها هستیم.'),
(3004, 26, 72, 'گفت: آیا هنگامی که آنها را می خوانید، سخن شما را می شنوند؟'),
(3005, 26, 73, 'یا به شما سود و زیانی می رسانند؟'),
(3006, 26, 74, 'گفتند: نه، بلکه پدرانمان را یافتیم که به این صورت عبادت می کردند!'),
(3007, 26, 75, 'گفت: پس آیا می دانید که آنچه می پرستید،'),
(3008, 26, 76, 'هم شما و هم پدران پیشین شما'),
(3009, 26, 77, 'هم آنان قطعاً دشمن منند [چون اگر آنها را بپرستم، مرا دچار عذاب جاودانه خواهند کرد]، جز پروردگار جهانیان [که پرستیدنش مایه سعادت همیشگی و جاودانی است.]'),
(3010, 26, 78, 'همان کسی که مرا آفرید و هم او مرا هدایت می کند،'),
(3011, 26, 79, 'و آنکه او طعامم می دهد و سیرابم می کند'),
(3012, 26, 80, 'و هنگامی که بیمار می شوم، او شفایم می دهد'),
(3013, 26, 81, 'و آنکه مرا می میراند سپس زنده ام می کند'),
(3014, 26, 82, 'و آنکه امید دارم روز جزا خطایم را بر من بیامرزد.'),
(3015, 26, 83, 'پروردگارا! به من حکمت بخش، و مرا به شایستگان ملحق کن.'),
(3016, 26, 84, 'و برای من در آیندگان نامی نیک و ستایشی والا مرتبه قرار ده،'),
(3017, 26, 85, 'و مرا از وارثان بهشت پرنعمت گردان،'),
(3018, 26, 86, 'و پدرم را بیامرز که او از گمراهان است،'),
(3019, 26, 87, 'و روزی که [مردگان] برانگیخته می شوند، رسوایم مکن؛'),
(3020, 26, 88, 'روزی که هیچ مال و اولادی سود نمی دهد،'),
(3021, 26, 89, 'مگر کسی که دلی سالم [از رذایل وخبایث] به پیشگاه خدا بیاورد،'),
(3022, 26, 90, 'و آن روز بهشت را برای پرهیزکاران نزدیک آورند،'),
(3023, 26, 91, 'و آتش برافروخته را برای گمراهان آشکار کنند،'),
(3024, 26, 92, 'و به آنان گویند: آنچه را می پرستیدید، کجایند؟'),
(3025, 26, 93, '[معبودانی را] به جای خدا؟! آیا شما را [برای رهایی از آتش] یاری می دهند؟ یا از خود می توانند دفع عذاب کنند؟'),
(3026, 26, 94, 'پس آن بت ها و گمراهان [که آنها را می پرستیدند] به رو در آتش افروخته افکنده می شوند،'),
(3027, 26, 95, 'با همه سپاهیان ابلیس،'),
(3028, 26, 96, 'در حالی که در آن [آتش افروخته] با هم ستیز و نزاع می کنند [و به بتان] می گویند:'),
(3029, 26, 97, 'به خدا سوگند که ما در گمراهی آشکاری بودیم،'),
(3030, 26, 98, 'که شما را با پروردگار جهانیان برابر و مساوی قرار می دادیم،'),
(3031, 26, 99, 'و ما را جز مجرمان [خائن] گمراه نکردند،'),
(3032, 26, 100, 'در نتیجه برای ما نه شفیعانی است،'),
(3033, 26, 101, 'و نه یک دوست نزدیک و صمیمی،'),
(3034, 26, 102, 'پس ای کاش برای ما بازگشتی [به دنیا] بود تا از مؤمنان می شدیم.'),
(3035, 26, 103, 'بی تردید در این سرگذشت، عبرتی بزرگ وجود دارد، و [قوم ابراهیم] بیشترشان مؤمن نبودند،'),
(3036, 26, 104, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهربان است.'),
(3037, 26, 105, 'قوم نوح پیامبران را تکذیب کردند،'),
(3038, 26, 106, 'هنگامی که برادرشان نوح به آنان گفت: آیا [از سرانجام شرک و طغیان که خشم و عذاب خداست] نمی پرهیزید؟'),
(3039, 26, 107, 'بی تردید من برای شما فرستاده ای امینم،'),
(3040, 26, 108, 'بنابراین از خدا پروا کنید و از من فرمان ببرید،'),
(3041, 26, 109, 'و من از شما بر ابلاغ رسالتم هیچ پاداشی نمی خواهم، پاداش من فقط بر عهده پروردگار جهانیان است،'),
(3042, 26, 110, 'بنابراین از خدا پروا کنید و از من فرمان ببرید.'),
(3043, 26, 111, 'گفتند: آیا ما به تو ایمان بیاوریم در حالی که فرومایگان از تو پیروی کرده اند؟!'),
(3044, 26, 112, '[نوح] گفت: مرا از آنچه آنان انجام می داده اند، چه اطلاعی است؟'),
(3045, 26, 113, 'حسابشان اگر واقعاً درک می کنید، جز بر عهده پروردگارم نیست،'),
(3046, 26, 114, 'و من طرد کننده مؤمنان نخواهم بود،'),
(3047, 26, 115, 'من جز بیم دهنده ای آشکار نیستم.'),
(3048, 26, 116, 'گفتند: ای نوح! اگر [از ابلاغ دین] باز نایستی، یقیناً از سنگسار شدگان خواهی بود!'),
(3049, 26, 117, 'گفت: پروردگارا! همانا قوم من مرا تکذیب کردند،'),
(3050, 26, 118, 'پس میان من و آنان چنانکه سزاوار است داوری کن و من و کسانی که از مؤمنان با من هستند از چنگ آنان نجات ده.'),
(3051, 26, 119, 'پس او و کسانی را که با او در آن کشتی مملو [از سرنشینان، جنبندگان، متاع و ابزار] بود نجات دادیم،'),
(3052, 26, 120, 'آن گاه بعد از آن، همه باقی ماندگان را غرق کردیم.'),
(3053, 26, 121, 'بی تردید در این سرگذشت، عبرتی بزرگ وجود دارد، و [قوم نوح] بیشترشان مؤمن نبودند،'),
(3054, 26, 122, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهربان است.'),
(3055, 26, 123, 'قوم عاد پیامبران را تکذیب کردند،'),
(3056, 26, 124, 'هنگامی که برادرشان هود به آنان گفت: آیا [از سرانجام شرک و طغیان که خشم و عذاب خداست] نمی پرهیزید؟'),
(3057, 26, 125, 'بی تردید من برای شما فرستاده ای امینم،'),
(3058, 26, 126, 'بنابراین از خدا پروا کنید و از من فرمان ببرید،'),
(3059, 26, 127, 'و من از شما بر ابلاغ رسالتم هیچ پاداشی نمی خواهم، پاداش من فقط بر عهده پروردگار جهانیان است،'),
(3060, 26, 128, 'آیا شما بر روی هر مکان بلندی به بیهوده کاری و بدون نیاز، برجی عظیم و برافراشته بنا می کنید؟'),
(3061, 26, 129, 'و قلعه ها و کاخ های استوار و مجلل برمی گیرید، که شاید جاودانه بمانید؟'),
(3062, 26, 130, 'و چون کسی را با شدت و قهر می گیرید ظالمانه و زورمدارانه می گیرید [بدون اینکه در عاقبت کار بیندیشید.]'),
(3063, 26, 131, 'بنابراین از خدا پروا کنید واز من فرمان ببرید،'),
(3064, 26, 132, 'واز کسی که شما را به وسیله آنچه خود می دانید یاری داده، پروا کنید،'),
(3065, 26, 133, 'به وسیله چهارپایان و فرزندانی، به شما یاری داده است.'),
(3066, 26, 134, 'و [به وسیله] بوستان ها و چشمه سارها،'),
(3067, 26, 135, 'بی تردید من بر شما از عذاب روزی بزرگ می ترسم.'),
(3068, 26, 136, 'گفتند: تو پند و اندرز دهی یا ندهی برای ما یکسان است!!'),
(3069, 26, 137, 'این [پند و اندرزها و تقسیم امور به حلال و حرام] جز شیوه پیشینیان [که اهل واپس گرایی و خرافات بودند] نیست،'),
(3070, 26, 138, 'و ما گرفتار عذابی [که تو ما را از آن بیم می دهی] نخواهیم شد.'),
(3071, 26, 139, 'پس او را تکذیب کردند، و در نتیجه هلاکشان کردیم. بی تردید در این سرگذشت عبرتی بزرگ وجود دارد و [قوم هود] بیشترشان مؤمن نبودند،'),
(3072, 26, 140, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهربان است.'),
(3073, 26, 141, 'قوم ثمود پیامبران را تکذیب کردند،'),
(3074, 26, 142, 'هنگامی که برادرشان صالح به آنان گفت: آیا [از سرانجام شرک و طغیان که خشم و عذاب خداست] نمی پرهیزید؟'),
(3075, 26, 143, 'بی تردید من برای شما فرستاده ای امینم،'),
(3076, 26, 144, 'بنابراین از خدا پروا کنید و از من فرمان ببرید،'),
(3077, 26, 145, 'و من از شما بر ابلاغ رسالتم هیچ پاداشی نمی خواهم، پاداش من فقط بر عهده پروردگار جهانیان است،'),
(3078, 26, 146, 'آیا شما را در آنچه در این دنیا [از نعمت و رفاه هست] ایمن و آسوده [از تغییر و زوال] رهایتان خواهند کرد؟!'),
(3079, 26, 147, 'در بوستان ها و چشمه سارها،'),
(3080, 26, 148, 'و کشتزارها و درختان خرمایی که شکوفه هایشان انبوه و باطراوت و لطیف است؟'),
(3081, 26, 149, 'و ماهرانه و هنرمندانه از کوه ها خانه هایی می تراشید؟ [به امید آنکه از هر آسیبی حتی مرگ در امان بمانید!!]'),
(3082, 26, 150, 'بنابراین از خدا پروا کنید و مرا فرمان ببرید،'),
(3083, 26, 151, 'و از فرمان اسراف گران اطاعت نکنید،'),
(3084, 26, 152, 'همانان که در زمین فساد می کنند و به اصلاح گری نمی پردازند.'),
(3085, 26, 153, 'گفتند: جز این نیست که تو از جادوشدگانی،'),
(3086, 26, 154, 'تو جز بشری مانند ما نیستی، اگر راست می گویی معجزه ای بیاور.'),
(3087, 26, 155, 'گفت: این ماده شتری است [که به اذن خدا به عنوان معجزه من از دل کوه بیرون آمد] سهمی از آب [این چشمه] برای او، و سهم روز معینی برای شماست،'),
(3088, 26, 156, 'آسیبی به او نرسانید که عذاب روزی بزرگ شما را فرا می گیرد.'),
(3089, 26, 157, 'پس ناقه را پی کردند و [از کار خود] پشیمان شدند،'),
(3090, 26, 158, 'در نتیجه عذاب آنان را فرا گرفت. بی تردید در این سرگذشت، عبرتی بزرگ وجود دارد و [قوم صالح] بیشترشان مؤمن نبودند،'),
(3091, 26, 159, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهرباناست.'),
(3092, 26, 160, 'قوم لوط پیامبران را تکذیب کردند،'),
(3093, 26, 161, 'هنگامی که که برادرشان لوط به آنان گفت: آیا [از سرانجام شرک و طغیان که خشم و عذاب خداست] نمی پرهیزید؟'),
(3094, 26, 162, 'بی تردید من برای شما فرستاده ای امینم،'),
(3095, 26, 163, 'بنابراین از خدا پروا کنید و از من فرمان ببرید،'),
(3096, 26, 164, 'و من از شما بر ابلاغ رسالتم هیچ پاداشی نمی خواهم، پاداش من فقط بر عهده پروردگار جهانیان است،'),
(3097, 26, 165, 'آیا شما از میان جهانیان با مردان آمیزش می کنید؟!!'),
(3098, 26, 166, 'و آنچه را پروردگارتان برای شما از همسرانتان آفریده رها می کنید؟ [برای این کار زشت بی سابقه، دلیل و برهان استواری ندارید] بلکه شما گروهی تجاوزکار [از حدود خدا و مرزهای انسانیّت] هستید.'),
(3099, 26, 167, 'گفتند: ای لوط! اگر [از تبلیغ دین] باز نایستی، حتماً تبعید خواهی شد.'),
(3100, 26, 168, '[لوط] گفت: بی تردید من از کار زشت شما به شدت متنفرم.'),
(3101, 26, 169, 'پروردگارا! مرا و خانواده ام را [از آثار، وزر و وبال] آنچه انجام می دهند، نجات بده؛'),
(3102, 26, 170, 'پس ما او و خانواده اش همگی را نجات دادیم،'),
(3103, 26, 171, 'مگر پیرزنی در [میان] باقی ماندگان،'),
(3104, 26, 172, 'سپس دیگران را نابود کردیم،'),
(3105, 26, 173, 'و بارانی از سنگ گِل بر سر آنان باراندیم؛ پس بد بود باران بیم داده شدگان.'),
(3106, 26, 174, 'بی تردید در این سرگذشت عبرتی بزرگ وجود دارد و قوم لوط بیشترشان مؤمن نبودند،'),
(3107, 26, 175, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهربان است.'),
(3108, 26, 176, 'مردم اَیکه پیامبران را تکذیب کردند،'),
(3109, 26, 177, 'هنگامی که شعیب به آنان گفت: آیا [از سرانجام شرک و طغیان که خشم و عذاب خداست] نمی پرهیزید؟'),
(3110, 26, 178, 'بی تردید من برای شما فرستاده ای امینم،'),
(3111, 26, 179, 'بنابراین از خدا پروا کنید و از من فرمان ببرید،'),
(3112, 26, 180, 'من از شما بر ابلاغ رسالتم هیچ پاداشی نمی خواهم، پاداش من فقط بر عهده پروردگار جهانیان است،'),
(3113, 26, 181, 'پیمانه را کامل بدهید و از کم فروشان نباشید،'),
(3114, 26, 182, 'و [متاع و کالا را] با ترازوی درست وزن کنید،'),
(3115, 26, 183, 'و از قیمت اشیا و اجناس مردم به هنگام خرید نکاهید، و در زمین تباهکارانه فتنه و آشوب برپا نکنید،'),
(3116, 26, 184, 'و از آنکه شما و امت های پیشین را آفرید پروا کنید.'),
(3117, 26, 185, 'گفتند: جز این نیست که تو از جادوشدگانی،'),
(3118, 26, 186, 'و تو جز بشری مانند ما نیستی، و بی تردید ما تو را از دروغگویان می پنداریم،'),
(3119, 26, 187, 'پس اگر راست می گویی پاره هایی از آسمان را بر ما فرو ریز.'),
(3120, 26, 188, '[شعیب] گفت: پروردگارم به آنچه انجام می دهید، داناتر است.'),
(3121, 26, 189, 'پس او را تکذیب کردند؛ در نتیجه عذاب روز سایبان [یعنی روزی که ابری تیره و صاعقه زا سایه می اندازد] آنان را فرا گرفت، همانا آن عذاب روزی بزرگ بود،'),
(3122, 26, 190, 'بی تردید در این سرگذشت، عبرتی بزرگ وجود دارد؛ و قوم شعیب بیشترشان مؤمن نبودند،'),
(3123, 26, 191, 'و یقیناً پروردگارت همان توانای شکست ناپذیر و مهربان است.'),
(3124, 26, 192, 'و بی تردید این قرآن، نازل شده پروردگار جهانیان است،'),
(3125, 26, 193, 'که روح الامین آن را نازل کرده است،'),
(3126, 26, 194, 'بر قلب تو، تا از بیم دهندگان باشی،'),
(3127, 26, 195, 'به زبان عربی روشن و گویا'),
(3128, 26, 196, 'و بی تردید [خبر] این [قرآن] در کتاب های پیشینیان است.'),
(3129, 26, 197, 'آیا آگاهی دانشمندان بنی اسرائیل به خبر قرآن [در کتاب های پیشینیان] برای مشرکان نشانه ای [بر حقّانیّت قرآن و صدق نبوّت تو] نیست؟!'),
(3130, 26, 198, 'و اگر آن را بر برخی از غیر عرب ها نازل کرده بودیم،'),
(3131, 26, 199, 'و او آن را بر عرب ها می خواند، باز هم به آن ایمان نمی آوردند!!'),
(3132, 26, 200, 'این گونه [که آن را به زبان عربی روشن و گویا نازل کردیم] در دل های مجرمان درآوردیم [که آن را بفهمند و به آن ایمان آورند،]'),
(3133, 26, 201, '[ولی این بیمار دلان لجوج] به آن ایمان نمی آورند تا آن عذاب دردناک را ببینند،'),
(3134, 26, 202, 'که ناگهان در حالی که بی خبرند به سراغشان آید،'),
(3135, 26, 203, 'پس [در آن موقعیت بسیار سخت] گویند: آیا ما مهلت می یابیم؟'),
(3136, 26, 204, 'آیا به آمدن عذاب ما شتاب می کنند؟'),
(3137, 26, 205, 'پس خبر ده اگر ما آنان را سالیانی بهره مندی و برخورداری دهیم،'),
(3138, 26, 206, 'سپس آن عذابی که به آن تهدید می شدند، به سراغشان آید.'),
(3139, 26, 207, 'آنچه را [چند سال] همواره از آن برخورداری می یافتند [عذاب را] از آنان دفعنخواهد کرد،'),
(3140, 26, 208, 'و [مردم] هیچ شهری را نابود نکردیم مگر آنکه بیم دهندگانی برای آنان بود،'),
(3141, 26, 209, 'برای اندرز دادن و اتمام حجت؛ و ما هرگز ستمکار نبوده ایم [که مردمی را بدون فرستادن پیامبر نابود کنیم.]'),
(3142, 26, 210, 'قرآن را شیطان ها نازل نکرده اند،'),
(3143, 26, 211, 'و [نزول قرآن] سزاوار آنان نیست، و قدرت [وحی کردن چنین کتابی را] ندارند،'),
(3144, 26, 212, 'بی تردید آنان از شنیدن [وحی الهی و اخبار ملکوتی] محروم و برکنارند.'),
(3145, 26, 213, 'پس با خدا معبودی دیگر را مپرست، که از عذاب شدگان خواهی شد.'),
(3146, 26, 214, 'و خویشان نزدیکت را [از عاقبت اعمال زشت] هشدار ده،'),
(3147, 26, 215, 'و پر و بال [فروتنی و تواضع] خود را برای مؤمنانی که از تو پیروی می کنند بگستر،'),
(3148, 26, 216, 'پس اگر تو را نافرمانی کردند بگو: من از آنچه انجام می دهید، بیزارم؛'),
(3149, 26, 217, 'و بر توانای شکست ناپذیر مهربان توکل کن،'),
(3150, 26, 218, 'هم او که تو را هنگامی که [برای عبادت] می ایستی، می بیند،'),
(3151, 26, 219, 'و گردش و حرکت تو را در میان سجده کنندگان [مشاهده می کند،]'),
(3152, 26, 220, 'یقیناً او شنوا و داناست.'),
(3153, 26, 221, 'آیا شما را خبر دهم که شیطان ها بر چه کسانی نازل می شوند؟'),
(3154, 26, 222, 'بر هر دروغ پرداز گنه پیشه ای نازل می شوند،'),
(3155, 26, 223, 'که گوش [بر القائات و اغواگری های شیطان] می سپرند، و بیشتر شیطان ها دروغگویند،'),
(3156, 26, 224, 'و [پیامبر، شاعر و خیال پرداز نیست، چون] شاعران [خیال پرداز] را [که حقایق را هجو می کنند، و با مطالب بی اساس به واقعیات می تازند] گمراهان، پیروی می کنند.'),
(3157, 26, 225, 'آیا ندانسته ای که آنان در هر وادی [باطلی خیال پردازانه] حیران و سرگردانند؟'),
(3158, 26, 226, 'و مطالبی می گویند که خود عمل نمی کنند،'),
(3159, 26, 227, 'مگر آنان که ایمان آورده و کارهای شایسته انجام داده اند و خدا را بسیار یاد کردند و پس از آنکه [با شعرهای باطل دشمنان، هجویّات مشرکان و یاوه های کافران] مورد ستم قرار گرفتند [با شعرهای توحیدی و اصیل و استوار خود] به انتقام گرفتن [از دشمن و دفاع از خویش] برخاستند، و کسانی که ستم کرده اند، به زودی خواهند دانست که به چه بازگشت گاهی باز خواهند گشت؟!');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طس ـ این آیات [باعظمت] قرآن و کتابی روشنگر است،'),
(3161, 27, 2, '[که سراسر] هدایت کننده [انسان ها] و برای مؤمنان مژده دهنده است.'),
(3162, 27, 3, 'همانان که نماز برپا می دارند و زکات می پردازند، و قاطعانه به آخرت یقین دارند؛'),
(3163, 27, 4, 'به راستی کسانی که به آخرت ایمان ندارند، اعمال [زشتشان] را در نظرشان آراستیم، پس همواره در حیرت و سرگردانی اند،'),
(3164, 27, 5, 'اینان کسانی هستند که عذاب سختی برای آنان است و بی تردید آنان در آخرت زیانکارترین [مردم] اند.'),
(3165, 27, 6, 'یقیناً تو قرآن را از نزد حکیمی دانا فرا می گیری.'),
(3166, 27, 7, '[یاد کن] هنگامی را که موسی به خانواده اش گفت: به راستی من آتشی را از دور دیدم، به زودی خبری از آن برای شما می آورم، یا از آن شعله ای برگرفته به شما می رسانم تا خود را گرم کنید،'),
(3167, 27, 8, 'پس هنگامی که نزد آن آمد، ندا رسید که پربرکت باد آنکه در آتش است و آنکه پیرامون آن است، و منزّه و پاک است خدا که پروردگار جهانیان است.'),
(3168, 27, 9, 'ای موسی! یقیناً منم خدای توانای شکست ناپذیر و حکیم،'),
(3169, 27, 10, 'و عصایت را بیفکن. پس وقتی آن را دید که تند و شتابان حرکت می کند، گویا ماری باریک و تیزرو است، پشت کنان رو به فرار گذاشت و به پشت برنگشت. [ندا رسید:] ای موسی! نترس که پیامبران [به سبب دارا بودن مقام عصمت و پاکی از گناه،] نزد من نمی ترسند،'),
(3170, 27, 11, 'مگر کسی که ستم کند [که او باید بترسد]؛ ولی زمانی که پس از بدی نیکی را [که ایمان و کار شایسته است] جایگزین آن نماید [از عذاب من در امان است و نباید بترسد]؛ زیرا من بسیار آمرزنده و مهربانم،'),
(3171, 27, 12, 'و دستت را در گریبانت کن تا بدون هیچ عیبی، سفید و درخشان بیرون آید، [با این دو معجزه که در ضمن] نه معجزه است به سوی فرعون و قومش [برو] که قطعاً آنان گروهی نافرمانند؛'),
(3172, 27, 13, 'هنگامی که معجزات روشن ما به سویشان آمد، گفتند: این جادویی آشکار است.'),
(3173, 27, 14, 'و آنها را در حالی که باطنشان به الهی بودن آن معجزات یقین داشت، ستمکارانه وبرتری جویانه انکار کردند. پس با تأمل بنگر که سرانجام مفسدان چگونه بود؟!'),
(3174, 27, 15, 'و یقیناً ما به داود و سلیمان، دانش [ویژه] دادیم، و آن دو گفتند: همه ستایش ها ویژه خداست، همان که ما را بر بسیاری از بندگان مؤمن خود برتری عطا کرده است،'),
(3175, 27, 16, 'و سلیمان وارث داود شد و گفت: ای مردم! [معرفت و آگاهی به] زبان و منطق پرندگان را به ما آموخته اند، و از هر چیزی [که به پیامبران و پادشاهان داده اند] به ما عطا کرده اند، یقیناً این امتیاز و برتری آشکاری است.'),
(3176, 27, 17, 'و برای سلیمان، سپاهیانش از پریان و آدمیان و پرندگان گردآوری شدند، [و آنان را از حرکت باز می داشتند] تا گروه های دیگر به آنان ملحق شوند؛'),
(3177, 27, 18, '[پس حرکت کردند] تا به وادی مورچگان درآمدند. مورچه ای گفت: ای مورچگان! به خانه هایتان درآیید تا سلیمان و سپاهیانش ناآگاهانه شما را پایمال نکنند.'),
(3178, 27, 19, 'پس سلیمان از گفته اش با تبسم، حالت خنده به خود گرفت، و [به سبب نعمت فهم گفتار حیوانات] گفت: پروردگارا! به من الهام کن تا شکر نعمتی را که به من و پدر و مادرم عطا کرده ای به جای آورم، و اینکه کار شایسته ای که آن را بپسندی انجام دهم، و مرا به رحمتت در میان بندگان شایسته ات درآور.'),
(3179, 27, 20, 'و [سلیمان] جویای پرندگان شد [و هدهد را در میان پرندگان بارگاهش نیافت] پس گفت: مرا چه شده که هدهد را نمی بینم [آیا هست و او را نمی بینم] یا از غایبان است؟'),
(3180, 27, 21, 'قطعاً او را عذابی سخت خواهم کرد یا [برای عبرت دیگر پرندگان] او را سر می برم، یا باید [برای غایب بودنش] دلیلی روشن برایم بیاورد.'),
(3181, 27, 22, 'پس [هدهد] زمانی نه چندان دور درنگ کرد [و با شتاب بازگشت،] پس گفت: [ای سلیمان!] من به چیزی آگاهی یافته ام که تو به آن آگاهی نیافته ای، و [من] از [سرزمین] سبا خبری مهم و یقینی برایت آورده ام.'),
(3182, 27, 23, 'به راستی من زنی را یافتم که بر آنان حکومت می کند، و از هر چیزی [که از وسایل و لوازم حکومت و قدرت است] به او داده اند و تختی بزرگ دارد،'),
(3183, 27, 24, 'او و قومش را یافتم که به جای خدا برای خورشید سجده می کنند و شیطان، اعمال [زشتشان] را برای آنان آراسته و در نتیجه آنان را از راه [حق] بازداشته است به این سبب هدایت نمی یابند،'),
(3184, 27, 25, '[و شیطان آنان را این گونه فریب داده] تا برای خدا سجده نکنند، همان که نهان در آسمان ها و زمین را بیرون می آورد و آنچه را پنهان می دارید و آنچه را آشکار می کنید، می داند.'),
(3185, 27, 26, 'خدای یکتا که معبودی جز او نیست، پروردگار عرش بزرگ است.'),
(3186, 27, 27, '[سلیمان به هدهد] گفت: به زودی [درباره ادعایت] تأمل و بررسی می کنم که آیا راست گفته ای یا از دروغگویانی،'),
(3187, 27, 28, 'این نامه مرا ببر و به سوی آنان بیفکن، سپس به دور از دیدگان آنان خود را کناری بگیر و بنگر چه پاسخیمی دهند؟'),
(3188, 27, 29, '[ملکه سبا پس از آگاهی از مضمون نامه] گفت: ای سران و اشراف! همانا نامه ای نیکو و با ارزشی به سوی من افکنده اند،'),
(3189, 27, 30, 'یقیناً این نامه از سوی سلیمان است و سرآغازش به نام خدای رحمان و رحیم است،'),
(3190, 27, 31, 'و [مضمونش این است که] بر من برتری مجویید و همه با حالت تسلیم نزد من آیید.'),
(3191, 27, 32, 'گفت: ای سران و اشراف! در کارم به من نظر دهید تا شما نزد من حضور داشتید، من [بدون شما] فیصله دهنده کاری نبوده ام.'),
(3192, 27, 33, 'گفتند: ما دارای قدرت و مالک وسایل رزمی سختی هستیم، پس بنگر چه فرمان می دهی؟'),
(3193, 27, 34, 'گفت: همانا پادشاهان هنگامی که [با ابزار، ادوات جنگی و سپاهی رزمی] وارد شهری می شوند، آن را تباه می کنند و عزیزان اهلش را به ذلت و خواری می نشانند و [آنان] همواره چنین می کنند!'),
(3194, 27, 35, 'من به سوی آنان هدیه ای [قابل توجه] می فرستم، پس با تأمل می نگرم که فرستادگان با چه پاسخی بر می گردند؟'),
(3195, 27, 36, 'هنگامی که [فرستاده ملکه سبا] نزد سلیمان آمد، [سلیمان] گفت: آیا مرا با مالی [اندک و ناچیز] یاری می دهید؟ آنچه خدا [از نبوّت و حکومت و ثروت] به من عطا کرده، بهتر است از آنچه به شما داده [هدیه شما برای من شادی آور نیست]، این شمایید که با هدیه خود شادمانی می کنید،'),
(3196, 27, 37, 'به سوی آنان بازگرد که ما حتماً با سپاهیانی به سوی آنان خواهیم آمد که قدرت رویارویی با آن را ندارند، و آنان را در حالی که [در آن منطقه] حقیر و بی ارزش شده اند، با خواری و ذلت از آنجا بیرون می کنیم.'),
(3197, 27, 38, '[سلیمان] گفت: ای سران و اشراف! کدام یک از شما تخت او را پیش از آنکه همگی به حالت تسلیم نزد من آیند، برایم می آورد؟'),
(3198, 27, 39, 'یکی از جنّیان کاردان و تیزهوش گفت: من آن را پیش از آنکه از مسند خود برخیزی نزد تو می آورم، و من بر این [کار] توانا و امینم،'),
(3199, 27, 40, 'کسی که دانشی از کتاب [لوح محفوظ] نزد او بود گفت: من آن را پیش از آنکه پلک دیده ات به هم بخورد، نزد تو می آورم. [و آن را در همان لحظه آورد]. پس هنگامی که سلیمان تخت را نزد خود پابرجا دید، گفت: این از فضل و احسان پروردگار من است، تا مرا بیازماید که آیا سپاس گزارم یا بنده ای ناسپاسم؟ و هر کس که سپاس گزاری کند، به سود خود سپاس گزاری می کند و هر کس ناسپاسی ورزد، [زیانی به خدا نمی رساند]؛ زیرا پروردگارم بی نیاز و کریم است.'),
(3200, 27, 41, 'گفت: تختش را [با تغییر دادن در زینت و آرایش] برایش ناشناس کنید تا ببینیم آیا [به شناخت آن] راه می یابد، یا از کسانی است که راه نمی یابند؟'),
(3201, 27, 42, 'پس زمانی که [ملکه سبا] آمد، گفتند: آیا تخت تو نیز این گونه است؟ گفت: گویا این همان است!! و ما را پیش از این [به حقّانیّت و قدرت سلیمان] آگاه کردند و [همان زمان] تسلیم شدیم؛'),
(3202, 27, 43, 'چیزی را که [ملکه سبا] به جای خدا می پرستید او را [از تسلیم شدن به حق] بازداشته بود؛ زیرا او از گروه کافران بود [و در میان آنان رشد کرده بود و به روش و رفتار آنان خو گرفته بود.]'),
(3203, 27, 44, 'به او گفتند: به حیاط قصر درآی. هنگامی که آن را دید، پنداشت آبی فراوان [چون دریاچه] است، پس دامن جامه از دو ساق پایش بالا زد [که به خیال خود وارد آب شود]، سلیمان گفت: این محوطه ای است صاف و هموار از شیشه [نه از آب]. [ملکه سبا] گفت: پروردگارا! قطعاً من به خود ستم کردم، اینک همراه سلیمان، تسلیم [فرمان ها و احکام] خدا، پروردگار جهانیان شدم.'),
(3204, 27, 45, 'همانا به سوی قوم ثمود برادرشان صالح را فرستادیم که خدا را بپرستید. پس ناگهان آنان دو گروه [مؤمن و کافر] شدند که با یکدیگر نزاع و دشمنیمی کردند.'),
(3205, 27, 46, 'گفت: ای قوم من! چرا بر عذاب و هلاکت، پیش از توبه و ایمان شتاب می کنید؟! چرا از خدا درخواست آمرزش نمی کنید تا مورد رحمت قرار گیرید؟!'),
(3206, 27, 47, 'گفتند: ما به تو و همراهانت فال بد زده ایم [و شما را سبب بروز این همه مشکلات و بدبختی خود می دانیم]. گفت: سبب بروز مشکلات و بدبختی شما [طغیان و گناهان شماست که عکس العملش] نزد خداست، [ربطی به ما ندارد] بلکه شما گروهی هستید که [به مشکلات و مصایب] امتحان می شوید [شاید به این سبب توبه کنید و مؤمن شوید.]'),
(3207, 27, 48, 'در آن شهر نُه نفر از اشراف و سران بودند که در آن فساد می کردند و اصلاح گر نبودند،'),
(3208, 27, 49, '[آنان به یکدیگر] گفتند: هم قسم شوید که حتماً به صالح و خانواده اش شبیخون می زنیم [تا همه را نابود کنیم]، آن گاه به خونخواهش بگوییم: ما زمان هلاک شدن خانواده اش حضور نداشتیم و یقیناً راستگوییم.'),
(3209, 27, 50, 'آنان نیرنگ مهمی به کار گرفتند و ما هم در حالی که بی خبر بودند [با کیفر بسیار سختی] نیرنگ آنان را از بین بردیم.'),
(3210, 27, 51, 'پس با تأمل بنگر که سرانجام نیرنگشان چگونه بود؟ که ما آنان و قومشان را همگی درهم کوبیدیم و هلاک کردیم.'),
(3211, 27, 52, 'این خانه های آنان است که به سبب ستمشان [از سکنه] خالی مانده. بی تردید در این سرگذشت برای گروهی که معرفت و شناخت دارند، عبرتی بزرگ وجود دارد،'),
(3212, 27, 53, 'و آنان را که ایمان آوردند و همواره پرهیزکاری می کردند، نجات دادیم.'),
(3213, 27, 54, 'و لوط را [یاد کن] هنگامی که به قومش گفت: آیا این عمل بسیار زشت را در حالی که [وقت انجام دادنش با بی شرمی کامل به هم] نگاه می کنید، مرتکب می شوید؟!'),
(3214, 27, 55, 'آیا شما از روی میل و شهوت به جای زنان با مردان آمیزش می کنید؟ [شما برای این کار زشت دلیل و برهانی ندارید] بلکه شما گروهی نادان هستید.'),
(3215, 27, 56, 'پس جواب قومش جز این نبود که گفتند: خاندان لوط را از شهرتان بیرون کنید؛ زیرا آنان مردمانی اند که همواره خواهان پاکی اند.'),
(3216, 27, 57, 'پس او و خانواده اش را نجاتدادیم مگر همسرش را که مقدر کرده بودیم در باقی ماندگان [در شهر برای هلاکت بماند.]'),
(3217, 27, 58, 'و بارانی از [سنگ گِل] بر آنان باراندیم، پس بد بود باران بیم داده شدگان.'),
(3218, 27, 59, 'بگو: همه ستایش ها ویژه خداست، و درود بر آن بندگانش که آنان را برگزیده است. آیا خدا بهتر است یا آنچه شریک او قرار می دهند؟'),
(3219, 27, 60, '[آیا آن شریکان انتخابی شما بهترند] یا آنکه آسمان ها و زمین را آفرید، و برای شما از آسمان آبی نازل کرد که به وسیله آن باغ هایی خرم و باطراوت رویاندیم، که رویاندن درختانش در قدرت شما نیست؛ آیا با خدا معبودی دیگر هست [که شریک در قدرت و ربوبیت او باشد؟ نه، نیست]، بلکه آنان مردمی منحرف اند [که برای او شریک می گیرند.]'),
(3220, 27, 61, '[آیا آن شریکان انتخابی شما بهترند] یا آنکه زمین را [برای موجوداتش] آرام و قرارگاه ساخت و در شکاف هایش نهرهایی پدید آورد، و برای آن کوه هایی استوار قرار داد [تا زیر پای اهلش نلرزد]، و میان دو دریا [ی شیرین و شور] مانع و حایلی قرار داد [که با هم مخلوط نشوند]؛ آیا با خدا معبودی دیگر هست [که شریک در قدرت و ربوبیت او باشد]؟! [نه، نیست] بلکه بیشترشان اهل معرفت و دانش نیستند.'),
(3221, 27, 62, '[آیا آن شریکان انتخابی شما بهترند] یا آنکه وقتی درمانده ای او را بخواند اجابت می کند و آسیب و گرفتاریش را دفع می نماید، و شما را جانشینان [دیگران در روی] زمین قرار می دهد؟ آیا با خدا معبودی دیگر هست [که شریک در قدرت و ربوبیت او باشد؟!] اندکی متذکّر و هوشیار می شوند.'),
(3222, 27, 63, '[آیا آن شریکان انتخابی شما بهترند] یا آنکه شما را در تاریکی های خشکی و دریا [به وسیله ستارگان و دیگر نشانه ها] راهنمایی می کند؟! و کیست که پیشاپیش [باران] رحمتش بادها را مژده رسان می فرستد؟ آیا با خدا معبودی دیگر هست [که شریک در قدرت و ربوبیت او باشد؟!] خدا برتر است از آنچه برای او شریک قرار می دهند.'),
(3223, 27, 64, '[آیا آن شریکان انتخابی شما بهترند] یا آنکه مخلوقات را می آفریند، آن گاه آنان را [پس از مرگشان] بازمی گرداند؟! و کیست آنکه از آسمان و زمین شما را روزی می دهد؟ آیا با خدا معبودی دیگر هست [که شریک در قدرت و ربوبیت او باشد؟] بگو: اگر راستگویید دلیل خود را بیاورید.'),
(3224, 27, 65, 'بگو: در آسمان ها و زمین هیچ کس جز خدا غیب نمی داند، و آنان آگاهی ندارند چه زمانی برانگیخته می شوند؟'),
(3225, 27, 66, 'بلکه دانش و آگاهی آنان نسبت به آخرت [به خاطر هزینه کردن عمرشان در امور مادی] به پایان رسیده، بلکه درباره آخرت در تردیدند، بلکه اینان از [فهم] آن کوردل اند،'),
(3226, 27, 67, 'و کافران گفتند: آیا زمانی که ما و پدرانمان خاک شدیم به راستی ما را از آن [زنده] بیرون می آورند؟'),
(3227, 27, 68, 'همانا پیش از این [زنده بیرون آمدن از خاک را] به ما و پدرانمان وعده داده اند، ولی این مطلب جز افسانه خرافی پیشینیان نیست!'),
(3228, 27, 69, 'بگو: در زمین بگردید پس با تأمل بنگرید که سرانجام گنهکاران چگونه بود؟'),
(3229, 27, 70, 'و بر آنان [که با حق دشمنی می کنند] اندوه مخور و از نیرنگی که همواره به کار می گیرند، دلتنگ مباش.'),
(3230, 27, 71, 'و می گویند: اگر راستگویید این وعده [عذاب] کی خواهد بود؟'),
(3231, 27, 72, 'بگو: امید است بخشی از آن عذابی که رسیدنش را با شتاب می خواهید، دنبال شما باشد،'),
(3232, 27, 73, 'و یقیناً پروردگارت بر مردم دارای فضلی بزرگ است، ولی بیشترشان سپاس نمی گزارند،'),
(3233, 27, 74, 'و به راستی پروردگارت آنچه را سینه هایشان پنهان می دارد و آنچه را آشکار می کند، می داند،'),
(3234, 27, 75, 'هیچ پوشیده و پنهانی در آسمان و زمین وجود ندارد مگر آنکه در کتابی روشن [چون لوح محفوظ] است.'),
(3235, 27, 76, 'بی تردید این قرآن بیشتر آنچه را بنی اسرائیل [از روی جهالت] در آن اختلاف دارند، برای آنان بیان می کند،'),
(3236, 27, 77, 'و یقیناً قرآن برای مؤمنان، سراسر هدایت و رحمت است.'),
(3237, 27, 78, 'قطعاً پروردگارت [روز قیامت] به حکم خود میان بنی اسرائیل [در آنچه از امور دینی و معنوی اختلاف دارند] داوری می کند؛ و او توانای شکست ناپذیر و داناست.'),
(3238, 27, 79, 'پس بر خدا توکل کن؛ زیرا تو [متکی بر آیین] حقّ آشکاری.'),
(3239, 27, 80, 'بی تردید تو نمی توانی [دعوتت را] به مردگان بشنوانی، و نیز نمی توانی آن را به کران که پشت کنان روی برمی گردانند بشنوانی،'),
(3240, 27, 81, 'و تو هدایت کننده کوردلان از گمراهی شان نیستی، و نمی توانی [دعوتت را] جز به آنان که به آیات ما ایمان می آورند و تسلیم [فرمان ها و احکام] خدا هستند، بشنوانی.'),
(3241, 27, 82, 'و هنگامی که [در اوقات پایانی دنیا] وعده عذاب ما بر آنان حتمی و لازم شود، جنبنده ای را از زمین برای آنان بیرون می آوریم که با آنان سخن می گوید [تا به آیات ما وآخرت واصول آن یقین کنند]؛ زیرا مردم پیش از این به آیات ما یقین نداشتند.'),
(3242, 27, 83, 'و [یاد کن] روزی را که از هر امتی گروهی از آنان را که آیات ما را تکذیب می کنند، محشور می کنیم و آنان را [از حرکت] باز می دارند [تا گروه های دیگر به آنان ملحق شوند،]'),
(3243, 27, 84, 'تا وقتی که [به محل حساب] آیند، [خدا] می گوید: آیا آیات مرا تکذیب کردید در حالی که هیچ احاطه علمی به آنها نداشتید؟ یا شما [غیر از تکذیب آیات] چه کارها [ی دیگری] انجام می دادید؟'),
(3244, 27, 85, 'و به سبب ستمی که [به آیات ما] روا داشتند، عذاب ما بر آنان حتمی و لازم می شود، پس [برای معذور نشان دادن خود] سخن نمی گویند.'),
(3245, 27, 86, 'آیا ندانسته اند که ما شب را قرار داده ایم تا در آن بیارامند، و روز را روشن نموده ایم [تا در آن به تلاش اقتصادی برخیزند؟] به راستی در این امور برای مردمی که ایمان می آورند، نشانه هایی [بر توحید، ربوبیت و قدرت خدا] ست.'),
(3246, 27, 87, 'و [یاد کن] روزی را که در صور می دمند، پس هر که در آسمان ها و هر که در زمین است دچار هراس شود، مگر کسی که خدا بخواهد؛ و همه خوار و فروتن به پیشگاه او آیند،'),
(3247, 27, 88, 'و کوه ها را می بینی [و] آنها را [در جای خود] بی حرکت می پنداری، در حالی که آنها مانند ابر گذر می کنند. آفرینش خداست که [آفرینش] هر چیزی را محکم و استوار کرده است؛ یقیناً او به آنچه انجام می دهید، آگاه است.'),
(3248, 27, 89, 'آنان که کارهای خیر و نیک [به آخرت] بیاورند، پاداشی بهتر از آن دارند، و آنان در آن روز از هول و هراسی بزرگ ایمن اند،'),
(3249, 27, 90, 'و آنان که کارهای بد و زشت بیاورند به رو در آتش افکنده می شوند؛ [و به آنان گویند:] آیا جز آنچه انجام دادید پاداشتان داده اند؟'),
(3250, 27, 91, 'من فقط فرمان یافته ام که پروردگار این شهر را که آن را بسیار محترم شمرده و همه چیز در سیطره مالکیّت و فرمانروایی اوست، بپرستم و فرمان یافته ام که از تسلیم شدگان [فرمان ها و احکام او] باشم،'),
(3251, 27, 92, 'و اینکه قرآن را بخوانم؛ پس هر که راه یابد فقط به سود خود راه یافته است، و هر که گمراه شود [آثار شوم گمراهی فقط متوجه خود اوست] پس [به مردم] بگو: من فقط از بیم دهندگانم،'),
(3252, 27, 93, 'و بگو: همه ستایش ها ویژه خداست؛ به زودی نشانه های خود را [که شکست شما و گرفتاری های سخت دنیایی و مرگ و عذاب آخرت است] به شما [مشرکان و کافران] نشان خواهد داد، پس آنها را [به خوبی] خواهید شناخت. و پروردگارت از آنچه انجام می دهید، بی خبر نیست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طسم'),
(3254, 28, 2, 'این آیات [باعظمت] کتاب روشنگر است.'),
(3255, 28, 3, 'بخشی از سرگذشت [مهم] موسی و فرعون را برای [عبرت گرفتن] مردمی که ایمان می آورند به حق و راستی بر تو می خوانیم.'),
(3256, 28, 4, 'همانا فرعون [در سرزمین مصر] برتری جویی و سرکشی کرد و مردمش را گروه گروه ساخت، در حالی که گروهی از آنان را ناتوان و زبون گرفت، پسرانشان را سر می برید، و زنانشان را [برای بیگاری گرفتن] زنده می گذاشت؛ بی تردید او از مفسدان بود.'),
(3257, 28, 5, 'و ما می خواستیم به آنان که در آن سرزمین به ناتوانی و زبونی گرفته شده بودند، نعمت های باارزش دهیم، و آنان را پیشوایان مردم و وارثان [اموال، ثروت ها و سرزمین های فرعونیان] گردانیم.'),
(3258, 28, 6, 'و برای آنان در آن سرزمین، زمینه قدرت و حکومت آماده کنیم، و به فرعون و هامان و سپاهشان که از ایشان اند چیزی را که از آن هراسناک و در حذر بودند [و آن پیروزی بنی اسرائیل بر آنان بود] نشان دهیم.'),
(3259, 28, 7, 'و به مادر موسی الهام کردیم که او را شیر بده، پس هنگامی که [از سوی فرعونیان] بر او بترسی به دریایش انداز، و مترس و غمگین مباش که ما حتماً او را به تو باز می گردانیم، و او را از پیامبران قرار می دهیم.'),
(3260, 28, 8, '[مادرش به الهام خدا او را به دریا انداخت] پس خاندان فرعون او را [از آب] گرفتند تا سرانجام، دشمنشان و مایه اندوهشان شود. مسلماً فرعون و هامان و سپاهیان آن دو خطاکار بودند؛'),
(3261, 28, 9, 'و همسر فرعون گفت: [این نوزاد] برای من و تو مایه شادمانی و خوشحالی است، او را نکشید، امید است ما را سود دهد، یا وی را به فرزندی خود بگیریم. ولی آنان آگاه نبودند [که دشمنشان را به دست خود می پرورند.]'),
(3262, 28, 10, 'و قلب مادر موسی [از هر چیز جز یاد فرزندش] تهی شد [و در اضطراب و نگرانی فرو رفت]. اگر قلبش را [با لطف خود] محکم و استوار نکرده بودیم تا از باورکنندگان وعده ما باشد، به درستی که نزدیک بود آن [حادثه پنهانی] را فاش کند،'),
(3263, 28, 11, 'و به خواهر موسی گفت: [اوضاع و احوال] او را پی گیری کن. پس او موسی را از دور [در آغوش فرعونیان] دید در حالی که خاندان فرعون [پی گیری او را] درک نمی کردند.'),
(3264, 28, 12, 'و ما پیش از آن [خوردن شیر] همه زنان شیردهنده را بر او حرام کردیم [تا پستان هیچ زنی را نگیرد،] پس [خواهرش پیش آمد و] گفت: آیا می خواهید شما را به خانواده ای راهنمایی کنم که سرپرستی او را برای شما به عهده گیرند و خیرخواه او باشند؟'),
(3265, 28, 13, 'پس او را به مادرش برگردانیم تا خوشحال و شادمان شود و اندوه نخورد و بداند که حتماً وعده خدا حق است، ولی بیشتر مردم [که محروم از بصیرت اند این حقایق را] نمی دانند.'),
(3266, 28, 14, 'چون به توانایی [جسمی و عقلی] خود رسید و رشد و کمال یافت، به او حکمت و دانش دادیم؛ و این گونه نیکوکاران را پاداش می دهیم.'),
(3267, 28, 15, 'و [موسی] به شهر وارد شد در حالی که اهل آن [در خانه ها استراحت می کردند و از آنچه در شهر می گذشت] بی خبر بودند، پس دو مرد را در آنجا یافت که با هم [به قصد نابودی یکدیگر] زد و خورد می کردند، این یک از پیروانش، و آن دیگر از دشمنانش، آنکه از پیروانش بود از موسی بر ضد کسی که از دشمنانش بود درخواست یاری کرد، پس موسی مشتی به او زد و او را کشت، گفت: این [نزاع میان آن دو] از عمل شیطان است، قطعاً او گمراه کننده و دشمنی اش آشکار است.'),
(3268, 28, 16, 'گفت: پروردگارا! [از اینکه از جایگاهم درآمدم و وارد شهر شدم] به خود ستم کردم [مرا از شرّ دشمنانم حفظ کن] و آمرزشت را نصیب من فرما. پس خدا او را آمرزید؛ زیرا او بسیار آمرزنده و مهربان است.'),
(3269, 28, 17, 'گفت: پروردگارا! به خاطر قدرت و نیرویی که به من عطا کردی، هرگز پشتیبان مجرمان نخواهم شد.'),
(3270, 28, 18, 'پس [موسی آن شب را] در آن شهر با حالت بیم و نگرانی صبح کرد، در حالی که [آثار و عواقب حادثه اتفاق افتاده را] انتظار می کشید؛ پس ناگهان دید آنکه دیروز از او یاری خواسته بود، دوباره وی را به فریادرسی می خواند، [موسی به او] گفت: همانا تو گمراهی آشکار هستی، [و گمراهیت از اینکه بدون قدرت و نیرو با فرعونیان به زد و خورد می پردازی، پیداست.]'),
(3271, 28, 19, 'هنگامی که خواست با کسی که دشمن هر دوی آنان بود به سختی برخورد کند [و از آن بنی اسرائیلی مظلوم دفاع نماید، مرد ستمدیده با ارزیابی اشتباهش در حقّ موسی به تصور آنکه موسی قصد وی را دارد] گفت: ای موسی! آیا می خواهی مرا بکشی چنان که دیروز یک نفر را کشتی؟ تو می خواهی در این سرزمین فقط یاغی و سرکش باشی، ونمی خواهی از مصلحان به شمار آیی!'),
(3272, 28, 20, 'و مردی از دورترین [نقطه] شهر شتابان آمد [و] گفت: ای موسی! اشراف و سران [فرعونی] درباره تو مشورت می کنند که تو را بکشند! بنابراین [از این شهر] بیرون برو که یقیناً من از خیرخواهان توام.'),
(3273, 28, 21, 'پس موسی ترسان و نگران در حالی که [حوادث تلخی را] انتظار می کشید از شهر بیرون رفت، [در آن حال] گفت: پروردگارا! مرا از این مردم ستمکار نجات بده.'),
(3274, 28, 22, 'هنگامی که به سوی مدین روی آورد، گفت: امید است پروردگارم مرا به راه راست [که انسان را به نتیجه مطلوب می رساند] راهنمایی کند.'),
(3275, 28, 23, 'هنگامی که به آب مدین رسید، گروهی از مردم را بر آن یافت که دام هایشان را آب می دادند، و غیر آنان دو زن را دید که [دام هایشان را از رفتن به سوی آب] بازمی دارند؛ گفت: چه چیزی شما را بر بازداشتن [گوسفندان] وامی دارد؟ گفتند: ما [این دام هایمان را] آب نمی دهیم تا [این] شبانان [دام هایشان را] برگردانند و پدر ما پیری کهنسال است [به این علت از انجام این کار معذور است.]'),
(3276, 28, 24, 'پس [موسی] دام هایشان را [به جهت کمک کردن به آن دو] آب داد، سپس به سوی سایه برگشت و گفت: پروردگارا! به آنچه از خیر بر من نازل می کنی، نیازمندم.'),
(3277, 28, 25, 'پس یکی از آن دو [زن] در حالی که با حالت شرم و حیا گام برمی داشت، نزد او آمد [و] گفت: پدرم تو را می طلبد تا پاداش اینکه [دام های] ما را آب دادی به تو بدهد. چون نزد او آمد و داستانش را بیان کرد، گفت: دیگر نترس که از آن گروه ستمکار نجات یافتی.'),
(3278, 28, 26, 'یکی از آن دو زن گفت: ای پدر! او را استخدام کن؛ زیرا بهترین کسی که استخدام می کنی آن کسی است که نیرومند و امین باشد [و او دارای این صفات است.]'),
(3279, 28, 27, 'گفت: می خواهم یکی از این دو دخترم را به نکاح تو درآورم به شرط آنکه هشت سال اجیر من باشی، و اگر ده سال را تمام کردی، اختیارش با خود توست [و ربطی به اصل قرار داد ندارد]، و من نمی خواهم بر تو سخت گیرم، و به خواست خدا مرا از شایستگان خواهی یافت.'),
(3280, 28, 28, '[موسی] گفت: این قرارداد میان من و تو باشد، هر یک از این دو مدت را به پایان برم هیچ تعدّی و ستمی بر من نیست، و خدا بر آنچه می گوییم، نگهبان و وکیل است.'),
(3281, 28, 29, 'چون موسی آن مدت را به پایان برد و با خانواده اش رهسپار [مصر] شد، از جانب طور آتشی دید، به خانواده اش گفت: درنگ کنید که من آتشی دیدم، [می روم] شاید خبری از آن برای شما بیاورم یا پاره ای از آتش را می آورم تا گرم شوید.'),
(3282, 28, 30, 'چون نزد آن آمد، از جانب راست آن وادی در آن جایگاه مبارک از آن درخت ندا رسید که ای موسی! یقیناً منم خدا پروردگار جهانیان،'),
(3283, 28, 31, 'و عصایت را بیفکن. پس وقتی آن را دید که تند و سریع حرکت می کند، گویا ماری باریک و تیزرو است، پشت کنان گریخت و به پشت سر برنگشت. [ندا آمد:] ای موسی! پیش آی و مترس، بی تردید تو [از آسیب و گزند آن] در امانی،'),
(3284, 28, 32, 'دستت را در گریبانت ببر تا بدون هیچ عیبی سفید و درخشان بیرون آید، و برای [از بین رفتن] ترسی که دچارش شده ای دو دستت را بر سینه بگذار، پس این دو معجزه از ناحیه پروردگار توست به سوی فرعون و اشراف و سران او که همواره مردمی نافرمان هستند.'),
(3285, 28, 33, 'گفت: پروردگارا! من یک نفر از آنان را کشته ام، می ترسم مرا بکشند،'),
(3286, 28, 34, 'و برادرم هارون زبانش از من گویاتر است، پس او را همراه من بفرست که یاور و دستیارم باشد تا [در همه امور] مرا تصدیق کند؛ زیرا می ترسم [فرعون و فرعونیان] تکذیبم کنند.'),
(3287, 28, 35, '[خدا] گفت: به زودی قدرت و نیرویت را به وسیله برادرت افزون کنم، و برای هر دوی شما به خاطر معجزات ما [که در اختیارتان نهاده ام] قدرتی قرار می دهم که آنان به شما دست نیابند، شما و آنان که از شما پیروی کنند، پیروزید.'),
(3288, 28, 36, 'پس هنگامی که موسی آیات روشن ما را برای آنان آورد، گفتند: این جز جادویی ساختگی و دروغین نیست، و ما این [ادعای نبوّت و دعوت به توحید] را از پدران پیشین خود نشنیده ایم [که به ما گفته باشند کسی در میانشان به عنوان پیامبر و دعوت کننده به توحید آمده باشد!!]'),
(3289, 28, 37, 'و موسی گفت: پروردگارم به کسی که از نزد او هدایت آورده و به کسی که سرانجام نیک برای اوست داناتر است؛ بی تردید ستمکاران پیروز نمی شوند.'),
(3290, 28, 38, 'و فرعون گفت: ای اشراف و سران [مملکت]! من برای شما هیچ معبودی جز خود نمی شناسم، پس ای هامان! برایم بر گِل آتش بیفروز [تا آجر محکم و قوی به دست آید]، پس برایم برجی بلند بساز شاید بر معبود موسی آگاهی و اطلاع یابم، و البته من او را از دروغگویان می پندارم.'),
(3291, 28, 39, 'او و سپاهیانش به ناحق در زمین سرکشی و تکبّر کردند، و پنداشتند که آنان را به سوی ما بازنمی گردانند،'),
(3292, 28, 40, 'پس او و سپاهیانش را گرفتیم و در دریا افکندیم؛ پس با تأمل بنگر که سرانجام ستمکاران چگونه بود؟'),
(3293, 28, 41, 'و آنان را [به کیفر طغیانشان] پیشوایانی که دعوت به آتش می کنند قرار دادیم، و روز قیامت یاری نمی شوند،'),
(3294, 28, 42, 'و آنان را در این دنیا با لعنت بدرقه کردیم، و روز قیامت از زشت رویان [و مطرودان] از رحمت خدایند،'),
(3295, 28, 43, 'و پس از آنکه اقوام پیشین را [چون قوم نوح و هود و صالح و لوط] هلاک کردیم، به موسی کتاب دادیم که برای مردم وسیله بینایی و هدایت و رحمتی بود، تا متذکّر و هوشیار شوند.'),
(3296, 28, 44, 'تو در جانب غربی [وادی طور] حضور نداشتی هنگامی که ما [با نازل کردن تورات] امر نبوّت موسی را به انجام رساندیم، و از شاهدان [آن واقعه مهم هم] نبودی.'),
(3297, 28, 45, 'ولی ما ملت هایی را پدید آوردیم، پس روزگار درازی بر آنان گذشت [تا جایی که سرگذشتشان فراموش شد، اکنون ما سرگذشتشان را به تو وحی می کنیم تا برای مردم بیان کنی]. و تو در میان مردم مدین ساکن نبودی تا نشانه های [قدرت و خشم] ما را [که به آنان رسید] بر اینان [که در مکه، آلوده به طغیان و فسادند] بخوانی، ولی ما بودیم که تو را به پیامبری فرستادیم [و سرگذشت مردم مدین را به تو وحی کردیم تا بر آنان بخوانی.]'),
(3298, 28, 46, 'و زمانی که ما موسی را ندا دادیم، تو در ناحیه طور نبودی ولی به سبب رحمتی از پروردگارت [سرگذشت موسی را به تو وحی کردیم] تا به مردمی که پیش از تو [تا زمان عیسی] بیم دهنده ای برای آنان نیامده بود، بیم دهی تا متذکّر و هوشیار شوند.'),
(3299, 28, 47, 'و اگر نه آن بود که به سبب گناهانی که مرتکب شدند عذابی به آنان برسد، پس بگویند: پروردگارا! چرا پیامبری به سوی ما نفرستادی تا از آیاتت پیروی کنیم و از مؤمنان باشیم [بی تردید در عذاب کردن و هلاکتشان شتاب می نمودیم.]'),
(3300, 28, 48, 'پس هنگامی که حق [چون قرآن و پیامبر] از سوی ما برای آنان آمد [به جای آنکه بپذیرند] گفتند: چرا مانند آنچه به موسی دادند به او نداده اند؟ آیا پیش از این به آنچه به موسی داده شد، کفر نورزیدند؟! گفتند: [این تورات و قرآن] دو جادویند که پشتیبان یکدیگرند!! و گفتند: ما منکر هر دو هستیم!!'),
(3301, 28, 49, 'بگو: [بنا بر ادعای شما چنانچه تورات و قرآن از جانب خدا نیست] اگر راست می گویید کتابی از نزد خدا بیاورید که هدایت کننده تر از این دو باشد تا آن را پیروی کنم.'),
(3302, 28, 50, 'پس اگر پاسخ تو را ندادند بدان که فقط از هواهای نفسانی خود پیروی می کنند و گمراه تر از کسی که بدون هدایتی از سوی خدا از هواهای نفسانی خود پیروی کند، کیست؟ مسلماً خدا مردم ستمکار را هدایت نمی کند.'),
(3303, 28, 51, 'ما این قرآن را برای آنان پی در پی و به هم پیوسته [به صورت سوره ای پس از سوره ای و آیه ای بعد از آیه ای] آوردیم تا متذکّر و هوشیار [حقایق] شوند.'),
(3304, 28, 52, '[و برخی از] کسانی که پیش از قرآن کتابشان دادیم، به قرآن ایمان می آورند،'),
(3305, 28, 53, 'و هنگامی که بر آنان خوانند، می گویند: به آن ایمان آوردیم، یقیناً این قرآن از سوی پروردگارمان حق و درست است، البته ما پیش از نزول آن [به سبب راهنمایی های تورات و انجیل] تسلیم بودیم.'),
(3306, 28, 54, 'اینان به علت صبری که [بر ایمان و عمل به قرآن] کردند و بدی [مردم] را با نیکی و خوبی خود دفع می کنند و از آنچه به آنان روزی کرده ایم، انفاق می نمایند، دوبار پاداششان می دهند.'),
(3307, 28, 55, 'و هنگامی که سخن بیهوده ای بشنوند، از آن روی برمی گردانند و می گویند: اعمال ما برای ما و اعمال شما برای شما، سلام بر شما [سلام متارکه]، ما خواستار [همنشینی و معاشرت با] نادانان نیستیم.'),
(3308, 28, 56, 'قطعاً تو نمی توانی هر که را خود دوست داری هدایت کنی، بلکه خدا هر که را بخواهد هدایت می کند؛ و او به هدایت پذیران داناتر است،'),
(3309, 28, 57, 'و [مشرکان برای معذور بودن خود در نپذیرفتن هدایت] گفتند: اگر ما همراه تو از هدایت [قرآن] پیروی کنیم [به وسیله مشرکان قلدر عرب] از سرزمینمان [شهر مکه] ربوده خواهیم شد! آیا ما آنان را در حرم امنی جای ندادیم که همواره [در همه فصول سال] هر نوع میوه و محصولی که رزقی از سوی ماست به سوی آن گردآوری می شود؟ ولی بیشتر آنان معرفت و دانش [نسبت به واقعیات] ندارند.'),
(3310, 28, 58, 'چه بسیار [اهل] شهرهایی را که [به سبب فراوانی نعمت] در زندگی خود گرفتار سرمستی و طغیان شده بودند، هلاک کردیم، پس این ها خانه های ایشان است که پس از آنان [خراب و ویران شده و] جز اندکی مورد سکونت قرار نگرفته اند، و فقط ما وارث آنان هستیم!'),
(3311, 28, 59, 'و پروردگارت بر آن نبوده است که شهرها را نابود کند، تا آنکه [پیش از نابودی] در مرکز آنها پیامبری برانگیزد که آیات ما را بر آنان بخواند، و ما [در هیچ حالی از احوال] نابودکننده شهرها نبوده ایم مگر در حالی که اهلش ستمکار بوده اند.'),
(3312, 28, 60, 'آنچه به شما داده شده کالا و ابزار زندگی دنیا و زینت آن است، و آنچه نزد خداست بهتر و پایدارتر است؛ آیا نمی اندیشید؟'),
(3313, 28, 61, 'پس آیا کسی که وعده نیک به او داده ایم و حتماً به آن خواهد رسید، مانند کسی است که او را [فقط] از کالا و ابزار زندگی دنیا برخوردارش کرده ایم، سپس او در روز قیامت از احضار شوندگان [برای عذاب] است؟!'),
(3314, 28, 62, 'و [یاد کن] روزی را که خدا آنان را ندا می دهد و می گوید: کجایند معبودانی که می پنداشتید شریکان من [در ربوبیت] هستند؟'),
(3315, 28, 63, 'کسانی که عذاب بر آنان حتمی و لازم شده [از سردمداران شرک و کفر که مردم را به اطاعت و بندگی خود واداشتند] می گویند: پروردگارا! اینانند [مطیعان ما] که گمراهشان کردیم، همان گونه که ما [به اختیار خود] گمراه شدیم آنان را نیز [به اختیار و انتخاب خودشان] گمراه کردیم، [از آنان] به سوی تو بیزاری می جوییم، ما را نمی پرستیدند، [بلکه هوای نفسشان را می پرستیدند،]'),
(3316, 28, 64, 'و [به مطیعان] گفته شود: شریکان خود را [که می پرستیدید] بخوانید. پس آنها را می خوانند ولی آنها پاسخشان را نمی دهند، و عذاب را می بینند و آرزو می کنند که ای کاش هدایت می یافتند،'),
(3317, 28, 65, 'و روزی که خدا آنان را ندا می دهد و می گوید: چه پاسخی به پیامبران دادید؟'),
(3318, 28, 66, 'پس در آن روز [به خاطر ترس و هراس فراگیر و به سبب محجوب بودن دل هایشان و قطع رابطه آنان با علل و اسباب، همه] اخبار [واقعی] بر آنان پوشیده ماند [و راه نجات از همه طرف بر آنان مسدود است]، بنابراین از یکدیگر پرسش هم نمی کنند،'),
(3319, 28, 67, 'و اما کسی که [از شرک و عصیان] توبه کرده و ایمان آورده و کار شایسته انجام داده، امید است که از رستگاران باشد،'),
(3320, 28, 68, 'و پروردگارت آنچه را بخواهد می آفریند و [آنچه را بخواهد] بر می گزیند، برای آنان [در برابر اراده او در قلمرو تکوین و تشریع] اختیاری نیست؛ منزّه است خدا و برتر است از آنچه برای او شریک می گیرند،'),
(3321, 28, 69, 'و پروردگارت آنچه را سینه هایشان پنهان می نماید و آنچه را آشکار می کند، می داند'),
(3322, 28, 70, 'و اوست خدا که معبودی جز او نیست، همه ستایش ها در دنیا و آخرت ویژه اوست، و فرمانروایی فقط برای اوست، و تنها به سوی او بازگردانده می شوید.'),
(3323, 28, 71, 'بگو: به من خبر دهید، اگر خدا شب را بر شما تا روز قیامت پاینده و جاوید کند، کدام معبودی است جز خدا که برای شما روشنایی بیاورد؟ پس آیا [دعوت او را] نمی شنوید؟'),
(3324, 28, 72, 'بگو: به من خبر دهید اگر خدا روز را بر شما تا روز قیامت پاینده و جاوید کند، کدام معبودی است جز خدا که شبی را برای شما بیاورد تا در آن بیارامید؟ پس آیا [آثار قدرت او را] نمی بینید؟'),
(3325, 28, 73, 'و از رحمت خود شب و روز را برای شما قرار داد، تا در شب بیارامید و [در روز] رزق و روزی بجویید، باشد که سپاس گزاری کنید.'),
(3326, 28, 74, 'و [یاد کن] روزی را که خدا آنان را ندا می کند و می گوید: کجایند معبودانی که می پنداشتید شریکان من [در ربوبیت] هستند؟'),
(3327, 28, 75, 'و از هر امتی گواه مطلع و آگاهی [چون پیامبر یا امام معصوم یا شخصی عادل و صالح] بیرون می آوریم، و [در محضر او به امت] می گوییم: دلیل خود را [بر درستی آیین شرک] بیاورید. پس برای آنان یقینی شود که بی تردید حق [یعنی توحید] ویژه خداست، و آنچه را به دروغ [به عنوان شریک خدا] می ساختند از دستشان می رود و گم می شود.'),
(3328, 28, 76, 'همانا قارون از قوم موسی بود که بر آنان تعدی و تجاوز کرد، و از گنجینه های مال و ثروت آن اندازه به او دادیم که حمل کلیدهایش بر گروهی نیرومند گران و دشوار می آمد. [یاد کن] هنگامی که قومش به او گفتند: [متکبرانه و مغرورانه] شادی مکن، قطعاً خدا شادمانان [متکبر و مغرور] را دوست ندارد.'),
(3329, 28, 77, 'در آنچه خدا به تو عطا کرده است سرای آخرت را بجوی، و سهم خود را از دنیا فراموش مکن، و نیکی کن همان گونه که خدا به تو نیکی کرده است، و در زمین خواهان فساد مباش، بی تردید خدا مفسدان را دوست ندارد.'),
(3330, 28, 78, 'گفت: جز این نیست که این [ثروت و مال انبوه] را بر پایه دانشی که نزد من است به من داده اند. آیا نمی دانست که خدا پیش از او اقوامی را هلاک کرده است که از او نیرومندتر و ثروت اندوزتر بودند؟ و مجرمان [که جرمشان معلوم و مشهود است] از گناهانشان بازپرسی نمی شود.'),
(3331, 28, 79, '[قارون] در میان آرایش و زینت خود بر قومش درآمد؛ آنانکه خواهان زندگی دنیا بودند، گفتند: ای کاش مانند آنچه به قارون داده اند برای ما هم بود، واقعاً او دارای بهره بزرگی است.'),
(3332, 28, 80, 'و کسانی که معرفت و دانش به آنان عطا شده بود، گفتند: وای بر شما پاداش خدا برای کسانی که ایمان آورده و کار شایسته انجام داده اند بهتر است. و [این حقیقت الهیه را] جز شکیبایان در نمی یابند.'),
(3333, 28, 81, 'پس او و خانه اش را در زمین فرو بردیم، و هیچ گروهی غیر از خدا برای او نبود که وی را [برای رهایی از عذاب] یاری دهد، و خود نیز نتوانست از خود دفاع کند.'),
(3334, 28, 82, 'و بامدادان، آنانکه دیروز مقام و جایگاه او را آرزو داشتند [چنان حالی شدند که] می گفتند: وه! گویی خدا رزق را برای هر کس از بندگانش بخواهد وسعت می دهد، و [برای هر که بخواهد] تنگ می گیرد، اگر خدا بر ما منت نگذاشته بود، ما را نیز در زمین فرو برده بود، وه، گویی کافران رستگار نمی شوند.'),
(3335, 28, 83, 'آن سرای [پرارزش] آخرت را برای کسانی قرار می دهیم که در زمین هیچ برتری و تسلّط و هیچ فسادی را نمی خواهند؛ و سرانجام [نیک] برای پرهیزکاران است.'),
(3336, 28, 84, 'هر کس کار نیکی [به پیشگاه خدا] بیاورد، پاداشی بهتر از آن برای اوست، و هر کس کار بدی بیاورد [پس بداند آنان که کارهای ناشایسته انجام داده اند] جزا داده نمی شوند مگر آنچه را همواره انجام می داده اند.'),
(3337, 28, 85, 'یقیناً کسی که [ابلاغ و عمل کردن به] قرآن را بر تو واجب کرده است، حتماً تو را به بازگشت گاه [رفیع و بلند مرتبه ات، شهر مکه] بازمی گرداند. بگو: پروردگارم به کسی که هدایت را آورده است و به کسی که در گمراهی آشکاری است، داناتر است.'),
(3338, 28, 86, 'و تو امید و انتظار نداشتی که این کتاب بر تو القا شود، لکن رحمتی از سوی پروردگارت بود [که بر تو القا شد]؛ پس هرگز پشتیبان کافران مباش،'),
(3339, 28, 87, 'و مبادا [کفر پیشگان لجوج] تو را از [ابلاغ] آیات خدا پس از آنکه به سوی تو نازل شد باز دارند، و به جانب پروردگارت دعوت کن، و هرگز از مشرکان مباش،'),
(3340, 28, 88, 'و با خدا معبودی دیگر مخوان، جز او معبودی نیست، هر چیزی مگر ذات او هلاک شدنی است، فرمانروایی [بر همه جهان هستی] ویژه اوست، و فقط به سوی او بازگردانده می شوید.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الم'),
(3342, 29, 2, 'آیا مردم گمان کرده اند، همین که بگویند: ایمان آوردیم، رها می شوند و آنان [به وسیله جان، مال، اولاد و حوادث] مورد آزمایش قرار نمی گیرند؟'),
(3343, 29, 3, 'در حالی که یقیناً کسانی را که پیش از آنان بودند، آزمایش کرده ایم [پس اینان هم بی تردید آزمایش می شوند]، و بی تردید خدا کسانی را که [در ادعای ایمان] راست گفته اند می شناسد، و قطعاً دروغگویان را نیز می شناسد.'),
(3344, 29, 4, 'آیا آنان که مرتکب زشتی ها می شوند، پنداشته اند که می توانند بر ما پیشی گیرند [تا از مجازات و عذاب زشت کاری هایشان بگریزند؟!] چه بد داوری می کنند.'),
(3345, 29, 5, 'هر که امید دیدار [پاداش و مقام قرب] خدا را دارد [باید در عرصه طاعت و عبادت بکوشد]؛ زیرا زمان معین شده [از سوی] خدا [که روز قیامت است] حتماً آمدنی است؛ و او شنوا و داناست.'),
(3346, 29, 6, 'و کسی که [در راه خدا] بکوشد فقط به سود خودش می کوشد؛ زیرا خدا از جهانیان بی نیاز است.'),
(3347, 29, 7, 'و آنان که ایمان آورده و کارهای شایسته انجام داده اند، گناهانشان را قطعاً از آنان می پوشانیم و بر پایه بهترین عملی که همواره انجام می داده اند، پاداششان می دهیم.'),
(3348, 29, 8, 'انسان را درباره پدر و مادرش به نیکی کردن سفارش کرده ایم؛ و اگر آن دو نفر تلاش کنند تا بر پایه جهالت و نادانی [وبدون معرفت و دانش که روشنگر حقایق است] چیزی را شریک من قرار دهی، از آنان اطاعت مکن. بازگشت شما فقط به سوی من است، پس شما را به آنچه همواره انجام می داده اید، آگاه می کنم.'),
(3349, 29, 9, 'و کسانی که ایمان آورده و کارهای شایسته انجام داده اند، حتماً در زمره شایستگان درمی آوریم.'),
(3350, 29, 10, 'و از مردم کسانی هستند که می گویند: به خدا ایمان آوردیم. پس هنگامی که در راه خدا شکنجه و آزار شوند، شکنجه و آزار مردم را مانند عذاب خدا [غیر قابل تحمل] شمارند [و از بیم ادامه یافتنش از ایمان دست برمی دارند]، و اگر از سوی پروردگارت یاری و نصرتی [چون پیروزی و غنیمت] بیاید، [به مؤمنان] قاطعانه می گویند: ما هم با شما بودیم. آیا [حال آنان از دید خدا پنهان است] و خدا به آنچه در سینه های جهانیان است [از همه] آگاه تر نیست؟'),
(3351, 29, 11, 'و یقیناً خدا کسانی را که ایمان آورده اند، می شناسد و بی تردید منافقان را نیز می شناسد،'),
(3352, 29, 12, 'و کافران به مؤمنان گفتند: از راه ما پیروی کنید تا ما [اگر آخرتی و عذابی در کار باشد] گناهان شما را به عهده گیریم. و آنان [به اختیار خود] بر عهده گیرنده چیزی از گناهان اینان نیستند، آنان یقیناً [در پیشنهادی که به مؤمنان می دهند] دروغگویند،'),
(3353, 29, 13, 'و مسلماً بارهای [گناهان] خود را با بارهای دیگری [از گناهان کسانی که گمراهشان کردند] همراه با بارهای خود حمل خواهند کرد، و روز قیامت از آنچه [به خدا و پیامبر و قرآن] افترا می بستند، حتماً بازپرسی خواهند شد،'),
(3354, 29, 14, 'و همانا نوح را به سوی قومش فرستادیم پس نهصد و پنجاه سال در میانشان درنگ کرد [ولی بیشتر مردم به او ایمان نیاوردند]، سرانجام توفان آنان را در حالی که ستمکار بودند، فرا گرفت.'),
(3355, 29, 15, 'پس او و اهل کشتی را نجات بخشیدیم و آن [حادثه] را عبرتی بسیار مهم برای جهانیان قرار دادیم.'),
(3356, 29, 16, 'و ابراهیم را [نیز فرستادیم] آن گاه به قومش گفت: خدا را بپرستید و از او پروا کنید، این برای شما بهتر است، اگر بدانید؛'),
(3357, 29, 17, 'جز این نیست که شما به جای خدا بت هایی را می پرستید و [در نامگذاری آنها به آلهه و شفیع و مؤثر] دروغی را می سازید، بی تردید کسانی را که به جای خدا می پرستید، رزقی را برای شما مالک نیستند [تا آن را به شما برسانند]، پس رزق را نزد خدا بجویید و او را بندگی کنید و او را شکر کنید که [فقط] به سوی او بازگردانده می شوید،'),
(3358, 29, 18, 'و اگر [مرا] تکذیب کنید [چیز جدیدی نیست]؛ زیرا امت هایی پیش از شما نیز [پیامبران را] تکذیب کردند، و بر عهده پیامبر جز رساندن آشکار [پیام وحی] نیست.'),
(3359, 29, 19, 'آیا ندانسته اند که چگونه خدا مخلوقات را می آفریند سپس آنان را [پس از مرگشان] باز خواهد گرداند، یقیناً این [کار] بر خدا آسان است.'),
(3360, 29, 20, 'بگو: در زمین بگردید پس با تأمل بنگرید که چگونه مخلوقات را آفرید، سپس خدا جهان آخرت را ایجاد می کند؛ زیرا خدا بر هر کاری تواناست،'),
(3361, 29, 21, 'هر کس را بخواهد عذاب می کند، و هر کس را بخواهد مورد رحمت قرار می دهد، و فقط به سوی او بازگردانده می شوید.'),
(3362, 29, 22, 'و شما نه در زمین و نه در آسمان نمی توانید خدا را عاجز کنید [تا از دسترس قدرت او بیرون روید]، و شما را به جای خدا هیچ سرپرست و یاوری نیست،'),
(3363, 29, 23, 'وکسانی که به آیات خدا و دیدار [قیامت و محاسبه اعمال به وسیله] او کفر ورزیدند، آنان از رحمت من مأیوسند وبرای آنان عذابی دردناک است.'),
(3364, 29, 24, 'پس جواب قوم ابراهیم جز این نبود که گفتند: او را بکشید یا بسوزانیدش. پس خدا او را از آن آتش رهایی بخشید، مسلماً در این [حادثه] برای مردمی که ایمان دارند، عبرت هاست،'),
(3365, 29, 25, 'و [ابراهیم] گفت: جز این نیست که به جای خدا بتهایی را [برای خود به عنوان معبود] گرفته اید [که علت آن] دوستی میان خودتان در زندگی دنیاست، سپس روز قیامت برخی از شما برخی دیگر را انکار می کنند، و بعضی از شما بعضی دیگر را لعنت می نمایند، و جایگاه شما آتش است، و برای شما هیچ یاوری نخواهد بود.'),
(3366, 29, 26, 'پس لوط به ابراهیم ایمان آورد، و [ابراهیم] گفت: به درستی که من به سوی پروردگارم مهاجرت می کنم، که فقط او توانای شکست ناپذیر و حکیم است.'),
(3367, 29, 27, 'و اسحاق و یعقوب را به او بخشیدیم و پیامبری و کتاب را در نسلش قرار دادیم و پاداشش را در دنیا به او دادیم، و بی تردید او در آخرت از زمره شایستگان است.'),
(3368, 29, 28, 'و لوط را [نیز فرستادیم]، آن گاه به قومش گفت: شما کار بسیار زشتی مرتکب می شوید که پیش از شما هیچ یک از جهانیان آن را انجام نداده اند.'),
(3369, 29, 29, 'آیا شما با مردان آمیزش می کنید و راه [توالد و تناسل] را قطع می نمایید و در محفل عمومی خود [در انظار یکدیگر] این کار بسیار زشت را مرتکب می شوید. پس جواب قوم او جز این نبود که گفتند: اگر تو از راستگویانی عذاب خدا را برای ما بیاور.'),
(3370, 29, 30, 'گفت: پروردگارا! مرا بر این مردم فسادکار یاری ده.'),
(3371, 29, 31, 'و زمانی که فرستادگان ما با بشارت [ولادت اسحاق] نزد ابراهیم آمدند، گفتند: ما یقیناً اهل شهر را نابود می کنیم؛ زیرا اهل آن ستمکارند.'),
(3372, 29, 32, 'گفت: لوط در آنجاست. گفتند: ما به کسانی که در آنجا هستند، داناتریم، حتماً او و خانواده اش را نجات می دهیم، مگر همسرش را که از باقی ماندگان خواهد بود،'),
(3373, 29, 33, 'و چون فرستادگان ما نزد لوط آمدند به سبب [حضور] آنان [به صورت جوانانی آراسته] بدحال و بی طاقت شد، و گفتند: مترس و غمگین مباش، قطعاً ما نجات دهنده تو و خانواده ات هستیم، مگر همسرت را که از باقی ماندگان خواهد بود،'),
(3374, 29, 34, 'ما بر اهل این شهر به علت کار بسیار زشتی که مرتکب می شوند، عذاب سختی از آسمان نازل خواهیم کرد.'),
(3375, 29, 35, '[پس با عذاب آسمانی، شهر و اهلش را در هم کوبیدیم] و از آن شهر نشانه ای روشن [از سنگ هایی که بر سر اهلش فرو باریدیم] برای مردمی که می اندیشند، بر جای گذاشتیم.'),
(3376, 29, 36, 'و نیز به سوی مدین برادرشان شعیب را فرستادیم؛ پس گفت: ای قوم من! خدا را بپرستید و روز قیامت را انتظار برید و در زمین تبهکارانه فتنه و آشوب برپا نکنید،'),
(3377, 29, 37, 'پس او را تکذیب کردند؛ در نتیجه زلزله سختی آنان را فرا گرفت، پس در خانه هایشان به رو در افتاده جسمی بی جان شدند،'),
(3378, 29, 38, 'و [قوم] عاد و ثمود را [نیز نابود کردیم]، و از خانه های [خراب شده و خالی] آنان [سرنوشت شومشان] برای شما نمایان است، و شیطان عمل های [زشت] آنان را برایشان آراست، در نتیجه آنان را از راه [خدا] باز داشت در حالی که [برای تشخیص حق از باطل] بصیرت داشتند.'),
(3379, 29, 39, 'و قارون و فرعون و هامان را [نیز نابود کردیم]. و همانا موسی برای آنان دلایل روشن آورد، پس آنان در زمین تکبّر و سرکشی کردند، ولی پیشی گیرنده [بر اراده و قضا و قدر ما] نبودند [تا بتوانند از عذاب ما بگریزند.]'),
(3380, 29, 40, 'پس همه را به گناهانشان گرفتیم، بر برخی از آنان توفانی سخت [که با خود ریگ و سنگ می آورد] فرستادیم، و بعضی را فریاد مرگبار گرفت، و برخی را به زمین فرو بردیم، و بعضی را غرق کردیم؛ و خدا بر آن نبود که به آنان ستم کند، ولی آنان بودند که به خودشان ستم کردند.'),
(3381, 29, 41, 'داستان کسانی که به جای خدا سرپرستانی گرفته اند، مانند داستان عنکبوت است که خانه ای [بی دیوار، بی سقف و بی حفاظ] برای خود بنا کرده باشد، و بی تردید سست ترین خانه ها خانه عنکبوت است، اگر [به این واقعیت] معرفت و شناخت داشتند [بت ها را سرپرستان خود نمی گرفتند.]'),
(3382, 29, 42, 'یقیناً خدا می داند به جای او چه چیزهایی را می پرستند [چیزهایی که نه دارای ارزش و اعتبارند و نه قدرت تصرف در کاری را دارند!!] و فقط او توانای شکست ناپذیر و حکیم است،'),
(3383, 29, 43, 'و این مثل ها را برای مردم می زنیم، ولی جز اهل معرفت و دانش در آنها تعقّل نمی کنند.'),
(3384, 29, 44, 'خدا آسمان ها و زمین را به حق آفرید؛ یقیناً در این [آفرینش] نشانه ای [بر توحید، ربوبیت و قدرت خدا] برای مؤمنان است.'),
(3385, 29, 45, 'آنچه را از این کتاب به تو وحی شده است، بخوان و نماز را برپا دار، یقیناً نماز از کارهای زشت، و کارهای ناپسند باز می دارد؛ و همانا ذکر خدا بزرگ تر است، و خدا آنچه را انجام می دهید، می داند.'),
(3386, 29, 46, 'و با اهل کتاب جز با نیکوترین شیوه مجادله و گفتگو مکنید، مگر با کسانی از آنان که [در مجادله با شما] ظالمانه به میدان آیند، و بگویید: به آنچه به سوی ما و به سوی شما نازل شده است، ایمان داریم و معبود ما و معبود شما یکی است و ما تسلیم [فرمان ها و احکام] او هستیم.'),
(3387, 29, 47, 'و همان گونه [که بر پیامبران پیشین کتاب نازل کردیم] این کتاب را بر تو نازل نمودیم، پس [برخی از] کسانی که کتابشان دادیم به آن ایمان می آورند، و از اینان [هم که مشرک اند] اندکی به آن ایمان می آورند، و آیات ما را جز کافران انکار نمی کنند،'),
(3388, 29, 48, 'و پیش از این [قرآن]، تو هیچ نوشته ای را نمی خواندی و آن را با دست خود نمی نوشتی وگرنه باطل گرایان یاوه گو [در وحی بودن و حقّانیّت آن] شک می کردند،'),
(3389, 29, 49, 'بلکه این قرآن، آیات روشنی است در سینه کسانی که به آنان معرفت و دانش عطا شده است؛ و آیات ما را جز ستمکاران انکار نمی کنند.'),
(3390, 29, 50, 'گفتند: چرا از سوی پروردگارش معجزاتی [مانند معجزات پیامبران پیشین] بر او نازل نمی شود. بگو: این معجزات فقط در اختیار خداست و من فقط بیم دهنده ای آشکارم.'),
(3391, 29, 51, 'آیا [در قرآن عیب و نقصی می یابند؟ و] برای آنان کافی نبوده است که ما این کتاب را که [همواره] بر آنان خوانده می شود بر تو نازل کردیم؟! همانا در این کتاب رحمتی است [ویژه و مایه یادآوری] و پندی برای مردمی که ایمان می آورند.'),
(3392, 29, 52, 'بگو: کافی است که خدا [نسبت به حقّانیّت نبوّتم] میان من و شما شاهد باشد، [او] آنچه را در آسمان ها و زمین است می داند، و آنان که به باطل [چون بت و طاغوت] گرویده و به خدا کفر ورزیده اند همان زیانکاران [واقعی] اند.'),
(3393, 29, 53, 'آنان شتاب در آوردن عذاب را از تو می خواهند، اگر مدتی معین [برای آنان] مقرّر نبود، قطعاً آن عذاب را [که خواهانش هستند] بر آنان می آمد و حتماً ناگاه در حالی که بی خبرند، به سراغشان خواهد آمد.'),
(3394, 29, 54, '[آنان] شتاب در آوردن عذاب را از تو می خواهند، بدون تردید هم اکنون دوزخ بر کافران حاطه دارد.'),
(3395, 29, 55, 'آنان را متوجه روزی کن که عذاب از بالای سرشان و از زیر پایشان آنان را فرو پوشاند و [خدا به آنان] می گوید: آنچه را همواره انجام می دادید [اکنون به صورت سخت ترین عذاب] بچشید.'),
(3396, 29, 56, 'ای بندگان من که ایمان آورده اید! یقیناً زمین من وسیع و پهناور است؛ پس [با انتخاب سرزمینی مناسب و شایسته که ارزش ها در آن حفظ شود] فقط مرا بپرستید،'),
(3397, 29, 57, 'هر جانداری چشنده مرگ خواهد بود، سپس به سوی ما بازگردانده می شوید،'),
(3398, 29, 58, 'و کسانی که ایمان آورده و کارهای شایسته انجام داده اند حتماً آنان را در قصرهایی رفیع و با ارزش از بهشت که از زیرِ [درختانِ] آن نهرها جاری است، جای خواهیم داد، در آنجا جاودانه اند؛ چه نیکوست پاداش عمل کنندگان.'),
(3399, 29, 59, 'همانان که [برای حفظ ایمان، اخلاق و عمل صالح] شکیبایی ورزیدند و همواره بر پروردگارشان توکل می کنند.'),
(3400, 29, 60, 'چه بسیارند جنبندگانی که قدرت ندارند [به دست آوردن] روزی خود را بر عهده بگیرند، خداست که به آنان و شما روزی می دهد، و او شنوا و داناست.'),
(3401, 29, 61, 'اگر از آنان [که شرک می ورزند] بپرسی: چه کسی آسمان ها و زمین را آفریده و خورشید و ماه را رام و مسخّر کرده؟ بی تردید خواهند گفت: خدا. پس چگونه [از حق به باطل] منحرف می شوند؟!'),
(3402, 29, 62, 'خدا روزی را برای هرکس از بندگانش که بخواهد وسعت می دهد و یا تنگ می گیرد؛ یقیناً خدا بر هر کاری تواناست،'),
(3403, 29, 63, 'و اگر از آنان بپرسی: چه کسی از آسمان آبی نازل کرد و به وسیله آن زمین را پس از مردگی اش زنده ساخت؟ بی تردید خواهند گفت: خدا. بگو: همه ستایش ها ویژه خداست [و مشرکان دور از حقیقت اند]، بلکه بیشترشان تعقّل نمی کنند.'),
(3404, 29, 64, 'و این زندگی دنیا جز سرگرمی و بازی نیست و بی تردید سرای آخرت، همان زندگی [واقعی و ابدی] است؛ اگر اینان معرفت و دانش داشتند [دنیا را به قیمت از دست دادن آخرت برنمی گزیدند.]'),
(3405, 29, 65, '[مشرکان تا در امنیت و آسایشند، بر آیین شرک تعصّب دارند،] پس زمانی که در کشتی سوار می شوند [و در وسط دریا امواج خطرناک آنان را محاصره می کند] خدا را در حالی که ایمان و عبادت را برای او [از هرگونه شرکی] خالص می کنند، می خوانند، و چون به سوی خشکی نجاتشان می دهیم به ناگاه به آیین شرک روی می آورند،'),
(3406, 29, 66, 'سرانجام به آنچه [از نعمت] به آنان عطا کردیم، ناسپاسی ورزند، و [از زندگی شرک آلودشان] برخوردار شوند؛ سپس [نتیجه این ناسپاسی و زندگی شرک آلود را] خواهند دانست.'),
(3407, 29, 67, 'آیا ندانسته اند که ما [شهرشان مکه را] حرم امنی قرار داده ایم [که در آن با آسایش و آرامش و محفوظ از قتل و غارت زندگی می کنند] در حالی که مردم از اطراف آنان [به وسیله دزدان و غارتگران عرب] ربوده می شوند؟ پس آیا [با دارا بودن این نعمت باارزش الهی] به باطل می گروند و به نعمت خدا کفران می ورزند؟!'),
(3408, 29, 68, 'و کیست ستمکارتر از کسی که دروغی را بر خدا ببندد [که خدا بتان را شریک خود گرفته؟] یا حق را زمانی که به سویش آمد تکذیب کند؟ آیا در دوزخ جایگاهی برای کافران نیست؟'),
(3409, 29, 69, 'و کسانی که برای [به دست آوردن خشنودی] ما [با جان و مال] کوشیدند، بی تردید آنان را به راه های خود [راه رشد، سعادت، کمال، کرامت، بهشت و مقام قرب] راهنمایی می کنیم؛ و یقیناً خدا با نیکوکاران است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الم'),
(3411, 30, 2, 'رومیان مغلوب شدند،'),
(3412, 30, 3, 'در نزدیک ترین سرزمین [خود به حجاز که نواحی شام است]، و آنان پس از مغلوب شدنشان به زودی پیروز خواهند شد،'),
(3413, 30, 4, '[البته] در ظرف چند سال. عاقبت و نتیجه کار پیش [از مغلوب شدن رومیان] و پس [از پیروزی رومیان] فقط در سیطره اراده و فرمان خداست، و آن روز [که رومیان پیروز شوند] مؤمنان شادمان و خوشحال خواهند شد،'),
(3414, 30, 5, 'به سبب نصرت و یاری خدا، [آری او] هرکس را بخواهد یاری می دهد، و تنها او توانای شکست ناپذیر و مهربان است.'),
(3415, 30, 6, 'خدا این وعده را داده است؛ و خدا از وعده اش تخلف نمی کند، ولی بیشتر مردم معرفت و شناخت [نسبت به وفای قطعی خدا در مورد وعده اش] ندارند.'),
(3416, 30, 7, '[تنها] ظاهری [محسوس] از زندگی دنیا را می شناسند و آنان از آخرت [که سرای ابدی و دارای نعمت های جاودانی و حیات سرمدی است] بی خبرند.'),
(3417, 30, 8, 'آیا در [خلوت] درون خود نیندیشیده اند؟ [که] خدا آسمان ها و زمین و آنچه را میان آنهاست، جز به حقّ و راستی و برای مدتی معین نیافریده است؛ و همانا بسیاری از مردم به دیدار [قیامت و محاسبه اعمال به وسیله] پروردگارشان کافرند.'),
(3418, 30, 9, 'آیا [به غفلت نانی می خورند و بی خبر به سر می برند و] در زمین گردش نکرده اند تا با تأمل بنگرند سرانجام کسانی که پیش از اینان بودند چگونه بود؟ آنان قوی تر و نیرومندتر از اینان بودند، و زمین را [برای هدف های گوناگون] زیر و رو کردند و آن را بیش از آنچه اینان آبادش کردند، آباد نمودند، و پیامبرانشان دلایل روشن برای آنان آوردند [ولی به سبب تکذیب پیامبرانشان هلاک شدند]؛ پس خدا بر آن نبود که به آنان ستم کند، ولی آنان بودند که به خودشان ستم می کردند.'),
(3419, 30, 10, 'آن گاه بدترین سرانجام، سرانجام کسانی بود که مرتکب زشتی شدند به سبب اینکه آیات خدا را تکذیب کردند و همواره آنها را به مسخره می گرفتند.'),
(3420, 30, 11, 'خدا مخلوقات را می آفریند، سپس آنان را [بعد از مرگشان] باز می گرداند، آن گاه به سوی او باز گردانده می شوید،'),
(3421, 30, 12, 'و روزی که قیامت برپا شود گنهکاران [از شدت سختی و هولناکی آن اندوهگین گردند و به علت پذیرفته نشدن بهانه و عذرشان از رحمت خدا] مأیوس شوند،'),
(3422, 30, 13, 'و از معبودانشان [که آنها را کورکورانه می پرستیدند] برای آنان شفیعانی نخواهد بود، و آنان معبودانشان را [از روی واقعیت] انکار می کنند،'),
(3423, 30, 14, 'و روزی که قیامت برپا شود آن روز [همه] از هم جدا می شوند،'),
(3424, 30, 15, 'اما کسانی که ایمان آورده و کارهای شایسته انجام داده اند، آنان را در بوستانی [سرسبز و خرم] مسرور وشادمان می دارند.'),
(3425, 30, 16, 'و اما کسانی که کفر ورزیده و آیات ما و دیدار آخرت را تکذیب کرده اند، احضارشدگان در عذاب اند.'),
(3426, 30, 17, 'پس خدا را هنگامی که وارد شب می شوید و هنگامی که به صبح در می آیید، تسبیح گویید،'),
(3427, 30, 18, 'همه ستایش ها در آسمان ها و زمین ویژه اوست و شب و آن گاه که وارد ظهر می شوید [نیز خدا را تسبیح گویید.]'),
(3428, 30, 19, 'زنده را از مرده بیرون می آورد و مرده را از زنده بیرون می آورد، و زمین را پس از مردگی اش زنده می کند؛ و این گونه [از گورها] بیرون آورده می شوید.'),
(3429, 30, 20, 'و از نشانه های [قدرت و ربوبیت] او این است که شما را از خاکی [بی جان] آفرید؛ پس اکنون بشری هستید که [روی زمین] پراکنده و منتشرید،'),
(3430, 30, 21, 'و از نشانه های [قدرت و ربوبیت] او این است که برای شما از جنس خودتان همسرانی آفرید تا در کنارشان آرامش یابید و در میان شما دوستی و مهربانی قرار داد؛ یقیناً در این [کار شگفت انگیز] نشانه هایی است برای مردمی که می اندیشند،'),
(3431, 30, 22, 'و از نشانه های [قدرت و ربوبیت] او آفرینش آسمان ها وزمین و اختلاف و گوناگونی زبان ها و رنگ های شماست؛ بی تردید در این [واقعیات] نشانه هایی است برای دانایان،'),
(3432, 30, 23, 'و از نشانه های [قدرت و ربوبیت] او خواب شما وجستجویتان برای تأمین معاش در شب وروز است، یقیناً در این [امور] نشانه هایی است برای مردمی که گوش شنوا دارند،'),
(3433, 30, 24, 'و از نشانه های [قدرت و ربوبیت] اوست که برق را مایه ترس [از صاعقه] و امید [به باران] به شما می نمایاند، و از آسمان، آبی نازل می کند که زمین را پس از مردگی اش به وسیله آن زنده می کند؛ قطعاً در این [شگفتی های آفرینش] نشانه هایی است برای مردمی که تعقّل می کنند.'),
(3434, 30, 25, 'و از نشانه های [قدرت و ربوبیت] او این است که آسمان و زمین به فرمانش برپایند، سپس زمانی که شما را با یک دعوت از زمین بخواند، ناگاه [از گورها] بیرون می آیید،'),
(3435, 30, 26, 'و هر که در آسمان ها و زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست، و همه برای او فروتن و خاضع اند،'),
(3436, 30, 27, 'و اوست که مخلوقات را می آفریند سپس آنان را [پس از مرگشان] باز می گرداند؛ و این [کار] برای او آسان تر است. و برترین وصف ها در آسمان ها و زمین ویژه اوست؛ و او توانای شکست ناپذیر و حکیم است.'),
(3437, 30, 28, 'خدا از [وضع و حال] خودتان برای شما مثلی زده است، آیا از بردگانتان در آنچه [از نعمت ها و ثروت ها] به شما روزی داده ایم، شریکانی دارید که شما در آن [نعمت ها و ثروت ها] با هم برابر و یکسان باشید، و همان گونه که از یکدیگر می ترسید [که یکی از شما نعمت و ثروت مشترک را ویژه خود کند] از بردگانتان هم بترسید؟ [بی تردید در میان آزاد و برده و مولا و عبد و مالک و مملوک چنین شرکتی وجود ندارد، پس چگونه ممکن است مملوک خدا در خدایی، ربوبیت، خالقیت و مالکیّت شریک او باشد؟!] این گونه آیات خود را برای مردمی که تعقّل می کنند، بیان می کنیم.'),
(3438, 30, 29, '[اعتقاد و عمل مشرکان بر پایه تعقّل نیست] بلکه آنان که [با شرکورزی] ستم کرده اند از روی جهل و نادانی از هواهای نفسانی خود پیروی نموده اند؛ پس کسانی را که خدا [به کیفر پیروی از هواها] گمراه کرده است، چه کسی هدایت می کند؟ و آنان هیچ یاری کننده ای [که از گمراهی و عذاب نجاتشان دهد] نخواهند داشت.'),
(3439, 30, 30, 'پس [با توجه به بی پایه بودن شرک] حق گرایانه و بدون انحراف با همه وجودت به سوی این دین [توحیدی] روی آور، [پای بند و استوار بر] سرشت خدا که مردم را بر آن سرشته است باش برای آفرینش خدا هیچگونه تغییر و تبدیلی نیست؛ این است دین درست و استوار؛ ولی بیشتر مردم معرفت و دانش [به این حقیقت اصیل] ندارند.'),
(3440, 30, 31, '[پای بند به همان سرشت خدایی باشید] در حالی که روی آورندگان به سوی او هستید و از او پروا کنید و نماز را برپا دارید و از مشرکان نباشید.'),
(3441, 30, 32, 'مشرکانی که دینشان را بخش بخش کردند و [سرانجام] گروه گروه شدند، در حالی که هر گروهی به آنچه [از بخشی از دین] نزد آنان است [به تصور اینکه حق است] شادمانند!'),
(3442, 30, 33, 'هنگامی که به مردم آسیب و گزندی برسد، پروردگارشان را در حالی که به سوی او روی آورده اند، می خوانند، سپس زمانی که رحمتی از سوی خود [چون نعمت، ثروت، اولاد و امنیت] به آنان بچشاند، ناگهان گروهی از آنان به پروردگارشان شرک می ورزند.'),
(3443, 30, 34, 'بگذار تا به آنچه به آنان داده ایم، ناسپاسی کنند، بنابراین [از نعمت های اندک و زودگذر] برخوردار شوید که سپس خواهید دانست [سرنوشت و کیفر ناسپاسان چیست؟]'),
(3444, 30, 35, 'یا [مگر] دلیلی استوار بر آنان نازل کرده ایم که آن دلیل [بر حقّانیّت] معبودانی که به وسیله آن [به خدا] شرک می ورزند، سخن می گوید؟'),
(3445, 30, 36, 'و هرگاه به مردم رحمتی [چون نعمت، ثروت، اولاد و امنیت] بچشانیم به آن شادمان می شوند، و چون به سبب گناهانی که مرتکب شده اند آسیب و گزندی به آنان رسد، ناگهان ناامید می شوند.'),
(3446, 30, 37, 'آیا ندانسته اند که خدا رزق و روزی را برای هرکه بخواهد فراخ و گشاده قرار می دهد و [برای هر که بخواهد] تنگ می گیرد؟ یقیناً در این [برنامه] نشانه هایی است برای مردمی که ایمان دارند.'),
(3447, 30, 38, 'پس [با توجه به فراخ بخشی خدا در روزی و رزق] حقّ خویشاوند و مسکین و در راه مانده را بده. این انفاق برای آنان که خشنودی خدا را می خواهند بهتر است؛ و اینان [که حقوق مالی را می پردازند] همان رستگارانند.'),
(3448, 30, 39, 'اموال و اجناسی را که [به صورت وام] به ربا می دهید تا در میان اموال مردم فزونی یابد، نزد خدا فزونی نخواهد یافت؛ و آنچه از زکات می دهید که [به سبب پرداختنش] خشنودی خدا را می خواهید [مایه فزونی است]؛ پس این زکات دهندگانند که مال و ثوابشان دو چندان می شود.'),
(3449, 30, 40, 'خداست که شما را آفرید، آن گاه به شما روزی داد، سپس شما را می میراند، و پس از آن شما را زنده می کند؛ آیا از معبودان شما کسی هست که چیزی از این [کارها] را انجام دهد؟ او منزّه و برتر است از اینکه به او شرک ورزند.'),
(3450, 30, 41, 'در خشکی و دریا به سبب اعمال زشتی که مردم به دست خود مرتکب شدند، فساد و تباهی نمودار شده است تا [خدا کیفر] برخی از آنچه را انجام داده اند به آنان بچشاند، باشد که [از گناه و طغیان] برگردند.'),
(3451, 30, 42, 'بگو: در زمین بگردید پس با تأمل بنگرید که سرانجام کسانی که پیش تر بودند [و مرتکب کار زشت شدند] چگونه بود؟ بیشتر آنان مشرک بودند.'),
(3452, 30, 43, 'پس [با توجه به بی پایه بودن شرک] با همه وجودت به سوی این دین درست و استوار روی آور پیش از آنکه روزی فرا رسد که از سوی خدا هیچ بازگشتی برای آن نیست، آن روز [همه مردم] دسته دسته و گروه گروه شوند.'),
(3453, 30, 44, 'کسانی که کافر شوند کفرشان به زیان خود آنان است، و آنان که کار شایسته انجام دهند، [بستر امن و آسایشی و اقامت گاه جاویدی] برای خود آماده می کنند،'),
(3454, 30, 45, 'تا خدا به کسانی که ایمان آورده و کارهای شایسته انجام داده اند از فضل خود پاداش دهد [و کافران را از رحمتش محروم کند]؛ زیرا خدا کافران را دوست ندارد.'),
(3455, 30, 46, 'و از نشانه های [قدرت و ربوبیت] او این است که بادها را مژده دهنده می فرستد، و تا بخشی از [باران] رحمتش را به شما بچشاند، و تا کشتی ها [به وسیله بادها] به فرمان او حرکت کنند، و نیز برای اینکه از رزق و روزی او بجویید، و باشد که شما سپاس گزاری کنید.'),
(3456, 30, 47, 'و به راستی پیش از تو پیامبرانی را به سوی قومشان فرستادیم که برای آنان دلایل روشن آوردند، سپس ما از آنان که مرتکب گناه شدند، انتقام گرفتیم [و مؤمنان را یاری دادیم]؛ و یاری مؤمنان حقّی بر عهده ماست.'),
(3457, 30, 48, 'خداست که بادها را می فرستد تا ابری را برمی انگیزد، پس آن را در آسمان آن گونه که بخواهد می گستراند و به صورت بخش بخش و پاره های مختلف در می آورد، پس باران را می بینی که از لابه لای آن بیرون می آید و چون آن را به هرکس از بندگانش که بخواهد می رساند، آن گاه شادمان و خوشحال می شوند.'),
(3458, 30, 49, 'و همانا پیش از آنکه باران بر آنان نازل شود، [آری] پیش از آن مأیوس و ناامید بودند.'),
(3459, 30, 50, 'پس با تأمل به آثار رحمت خدا بنگر که چگونه زمین را پس از مردگی اش زنده می کند، بی تردید این [خدای قدرتمند] زنده کننده مردگان است و او بر هر کاری تواناست.'),
(3460, 30, 51, 'و اگر بادی [سوزان و زیانبار] بفرستیم، پس آن [کشت و زرع] را زرد شده ببینند، پس از آن [به جای جبران گناهانی که سبب این آسیب شده] ناسپاس می شوند.'),
(3461, 30, 52, '[اینان که کفر را بر ایمان ترجیح داده اند، مانند مردگان اند] پس تو نمی توانی [دعوتت را] به مردگان بشنوانی و نمی توانی دعوتت را به کران زمانی که پشت کنان روی می گردانند، بشنوانی.'),
(3462, 30, 53, 'و تو هدایت کننده کوردلان از گمراهی شان نیستی، و جز به آنان که به آیات ما ایمان می آورند و تسلیم [فرمان ها و احکام ما] هستند، نمی توانی [دعوتت] را بشنوانی.'),
(3463, 30, 54, 'خداست که شما را از ناتوانی آفرید، سپس بعد از ناتوانی قدرت و نیرو داد، آن گاه بعد از نیرومندی و توانایی، ناتوانی و پیری قرار داد؛ هرچه بخواهد می آفریند و او دانا و تواناست.'),
(3464, 30, 55, 'و روزی که قیامت برپا می شود، مجرمان سوگند می خورند که [فاصله مرگ تا قیامت را] جز ساعتی درنگ نکرده اند، این گونه [که در قیامت دروغ می گویند و منحرف از حقیقت هستند در دنیا نیز همواره از حق به باطل] منحرف می شدند.'),
(3465, 30, 56, 'و کسانی که دانش و ایمان به آنان داده شده است می گویند: بی تردید شما [بر طبق قضا و قدر ثبت شده] در کتاب خدا [لوح محفوظ] تا روز قیامت درنگ کرده اید و این روز قیامت است، ولی شما [به اینکه قیامت حق است] معرفت و دانش نداشتید.'),
(3466, 30, 57, 'پس در آن روز کسانی که [به آیات ما و پیامبران] ستم کرده اند، عذرخواهی شان به آنان سود نمی دهد، و نه از آنان می خواهند که [برای به دست آوردن خشنودی خدا] عذرخواهی کنند،'),
(3467, 30, 58, 'و به راستی در این قرآن برای مردم از هرگونه مثلی زدیم. و اگر برای آنان معجزه ای [که خود درخواست دارند] بیاوری آنان که کفر ورزیده اند، قطعاً خواهند گفت: شما جز باطل گرا نیستید [و این معجزه ای که آوردید، جز باطل نیست.]'),
(3468, 30, 59, 'خدا این گونه بر دل های کسانی که معرفت و دانش ندارند مُهر [تیره بختی] می نهد.'),
(3469, 30, 60, 'پس [بر آزار و یاوه گویی این تیره بختان] شکیبایی کن که یقیناً وعده خدا [در مورد یاری و پیروزی تو] حق است، و مبادا آنان که یقین [به وعده های حق و برپا شدن قیامت] ندارند تو را به ناشکیبایی و سبک سری وادارند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الم'),
(3471, 31, 2, 'این آیات [با عظمتِ] کتاب محکم و استوار است،'),
(3472, 31, 3, 'که سراسر هدایت و رحمتی است برای نیکوکاران.'),
(3473, 31, 4, 'همانان که نماز را برپا می دارند و زکات می دهند و به آخرت یقین دارند.'),
(3474, 31, 5, 'اینانند که از سوی پروردگارشان بر بلندای هدایتند، و اینانند که رستگارند،'),
(3475, 31, 6, 'و برخی از مردم اند که افسانه های بیهوده و سخنان یاوه و سرگرم کننده را می خرند تا از روی نادانی و بی دانشی [مردم را] از راه خدا گمراه کنند و آن را به مسخره بگیرند؛ اینانند که برای آنان عذابی خوارکننده خواهد بود.'),
(3476, 31, 7, 'چون آیات ما بر او خوانده شود متکبرانه روی برمی گرداند، گویی آنها را نشنیده است، انگار در دو گوشش سنگینی است، پس او را به عذابی دردناک مژده ده؛'),
(3477, 31, 8, 'و یقیناً کسانی که ایمان آورده و کارهای شایسته انجام داده اند، برای آنان بهشت های پرنعمت خواهد بود،'),
(3478, 31, 9, 'که در آنجا جاودانه اند؛ خدا وعده کرده است وعده ای به حقّ و درست، و او توانای شکست ناپذیر و حکیم است.'),
(3479, 31, 10, 'آسمان ها را بدون ستون هایی که آنها را ببینند، آفریده و در زمین کوه هایی استوار افکنده است تا شما را نلرزاند و به اضطراب نیندازد، و در آن از هر جنبنده ای پراکنده کرده است، و از آسمان آبی نازل کردیم پس [به وسیله آن] از هر نوع گیاه نیکو و با ارزش در آن رویاندیم.'),
(3480, 31, 11, 'این است آفرینش خدا. پس به من نشان دهید آنها که غیر اویند [و شما به عنوان معبود برگزیده اید] چه چیزی آفریده اند؟ [آنها چیزی نیافریده اند] بلکه ستمکاران در گمراهی آشکاری هستند.'),
(3481, 31, 12, 'و به راستی ما به لقمان حکمت عطا کردیم که نسبت به خدا سپاس گزار و شاکر باش و هرکه سپاس گزارد تنها به سود خود سپاس می گزارد، و هرکه ناسپاسی کند [به خدا زیان نمی زند، زیرا] خدا بی نیاز و ستوده است.'),
(3482, 31, 13, 'و [یاد کن] هنگامی که لقمان به پسرش در حالی که او را موعظه می کرد، گفت: پسرکم! به خدا شرک نورز، بی تردید شرک ستمی بزرگ است.'),
(3483, 31, 14, 'و انسان را درباره پدر و مادرش سفارش کردیم، مادرش به او حامله شد [در حالی که] سستی به روی سستی [به او دست می داد] و باز گرفتنش [از شیر] در دو سال است [و سفارش کردیم] که برای من و پدر و مادرت سپاس گزاری کن؛ بازگشت [همه] فقط به سوی من است.'),
(3484, 31, 15, 'و اگر آن دو نفر تلاش کنند تا بر پایه جهالت و نادانی [و بدون معرفت و دانش که روشنگر حقایق است] چیزی را شریک من قرار دهی، از آنان اطاعت مکن؛ ولی در دنیا با آن دو نفر به شیوه ای پسندیده معاشرت کن و راه کسی را پیروی کن که [با توبه و ایمان و اخلاص] به من بازگشته است؛ سپس بازگشت شما فقط به سوی من است، پس شما را از آنچه انجام می دادید، آگاه می کنم.'),
(3485, 31, 16, 'پسرکم! اگر عمل هموزن دانه خردلی و در درون سنگی یا در آسمان ها یا در دل زمین باشد، خدا آن را [در قیامت برای حسابرسی] می آورد؛ یقیناً خدا لطیف و آگاه است.'),
(3486, 31, 17, 'پسرکم! نماز را برپا دار و مردم را به کار پسندیده وادار و از کار زشت بازدار و بر آنچه [از مشکلات و سختی ها] به تو می رسد شکیبایی کن، که اینها از اموری است که ملازمت بر آن از واجبات است.'),
(3487, 31, 18, 'متکبرانه روی از مردم برمگردان، و در زمین با ناز و غرور راه مرو، همانا خدا هیچ خودپسند فخرفروش را دوست ندارد.'),
(3488, 31, 19, 'و در راه رفتنت میانه رو باش، و از صدایت بکاه که بی تردید ناپسندترین صداها صدای خران است.'),
(3489, 31, 20, 'آیا ندانسته اید که خدا آنچه را در آسمان ها و آنچه را در زمین است، مسخّر و رام شما کرده، و نعمت های آشکار و نهانش را بر شما فراوان و کامل ارزانی داشته، و برخی از مردم اند که همواره بدون هیچ دانشی [بلکه از روی جهل و نادانی] و بدون هیچ هدایتی و هیچ کتاب روشنی درباره خدا مجادله و ستیز می کنند.'),
(3490, 31, 21, 'و چون به آنان گویند: از آنچه خدا نازل کرده است، پیروی کنید، می گویند: [پیروی نمی کنیم] بلکه ما از آنچه پدرانمان را بر آن یافته ایم، پیروی می کنیم. آیا [کورکورانه از گذشتگان پیروی می کنند] و هرچند که شیطان آنان را با این پیروی به عذاب فروزان دعوت کند؟!'),
(3491, 31, 22, 'و هرکس همه وجود خود را به سوی خدا کند در حالی که نیکوکار باشد بی تردید به محکم ترین دستگیره چنگ زده است؛ و سرانجام و عاقبت همه کارها فقط به سوی خداست.'),
(3492, 31, 23, 'و هرکس کفر ورزد، کفرش تو را اندوهگین نسازد؛ بازگشت آنان فقط به سوی ماست، پس آنان را از آنچه انجام داده اند آگاه می کنیم؛ یقیناً خدا به نیّات و اسرار سینه ها داناست.'),
(3493, 31, 24, 'اندکی [از نعمت های مادی] برخوردارشان می کنیم، سپس آنان را [به وارد شدن] در عذابی سخت ناچار می کنیم،'),
(3494, 31, 25, 'و اگر از آنان بپرسی: چه کسی آسمان ها و زمین را آفریده است؟ قطعاً می گویند: خدا. بگو: همه ستایش ها ویژه خداست [آیا درک می کنند که همه امور به دست خداست؟ نه] بلکه بیشترشان معرفت و دانش ندارند؛'),
(3495, 31, 26, 'آنچه در آسمان ها و زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست؛ یقیناً خدا همان بی نیاز و ستوده است.'),
(3496, 31, 27, 'اگر [برای نوشتن و ثبت کردن کلمات خدا که در حقیقت مخلوقات او هستند] آنچه درخت در زمین است قلم باشد و دریا [مرکب] و هفت دریای دیگر آن را پس از پایان یافتنش مدد رسانند، کلمات خدا پایان نپذیرد؛ یقیناً خدا توانای شکست ناپذیر و حکیم است.'),
(3497, 31, 28, 'آفریدن شما و برانگیختنتان [برای ما] جز مانند [آفریدن و برانگیختن] یک تن نیست؛ یقیناً خدا شنوا و بیناست.'),
(3498, 31, 29, 'آیا ندانسته ای که خدا شب را در روز فرو می برد و روز را در شب فرو می برد، و خورشید و ماه را مسخّر و رام کرده است که هر کدام تا مدتی معین روانند؛ و یقیناً خدا به آنچه انجام می دهید، آگاه است.'),
(3499, 31, 30, 'این [آفریده های شگفت و این تغییرات و تحولات] دلیل بر این است که خدا فقط حق است و آنچه به جای او می پرستند باطل است، و بی تردید خدا همان والا مرتبه و بزرگ است.'),
(3500, 31, 31, 'آیا ندانسته ای که کشتی ها به سبب نعمت های خدا [چون باد، هوا و دیگر عوامل] در دریا روانند تا خدا برخی از نشانه های قدرت خود را به شما نشان دهد؛ بی تردید در این [واقعیت] برای هر شکیبای سپاس گزاری نشانه هایی [از توحید، قدرت و ربوبیت خدا] ست.'),
(3501, 31, 32, 'و چون آنان را [در دریا] موجی [به دنبال موجی] مانند ابرهای سایه انداز فرو پوشد، خدا را در حالی که ایمان و عبادت را برای او [از هرگونه شرکی] خالص می کنند، می خوانند، و زمانی که آنان را به خشکی می رساند برخی از آنان به راه میانه و معتدل [که راه توحید است] می روند [و برخی پیمان می شکنند] و آیات ما را جز هر پیمان شکن خائن و کفر پیشه ای انکار نمی کند.'),
(3502, 31, 33, 'ای مردم! از پروردگارتان پروا کنید، و بترسید از روزی که هیچ پدری چیزی [از عذاب دوزخ را] از فرزندش دفع نمی کند، و نه هیچ فرزندی دفع کننده چیزی از [عذاب] پدر خویش است. بی تردید وعده خدا حق است، پس زندگی دنیا شما را نفریبد، و مبادا شیطان شما را به [کرم و رحمت] خدا مغرور کند.'),
(3503, 31, 34, 'یقیناً خداست که دانش قیامت فقط نزد اوست، و [تنها او] باران را نازل می کند، و [اوضاع و احوال] آنچه را در رحم هاست می داند؛ و هیچ کس نمی داند فردا چه چیزی [از خیر و شر] به دست می آورد، و هیچ کس نمی داند در چه سرزمینی می میرد؛ بی تردید خدا دانا و آگاه است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الم'),
(3505, 32, 2, 'نازل کردن این کتاب که هیچ تردیدی در [وحی بودن] آن نیست، از سوی پروردگار جهانیان است.'),
(3506, 32, 3, 'آیا [مشرکان لجوج] می گویند: [پیامبر] آن را از نزد خود ساخته و پرداخته [و به خدا نسبت داده است؟ چنین نیست] بلکه آن [کتابی] حق و درست است [که] از سوی پروردگارت [نازل شده است]، تا به مردمی که پیش از تو هیچ بیم دهنده ای برای آنان نیامده است بیم دهی تا هدایت یابند.'),
(3507, 32, 4, 'خداست که آسمان ها و زمین را و آنچه را میان آنهاست در شش روز آفرید، سپس بر تخت فرمانروایی و تدبیر امور آفرینش چیره و مسلط شد. برای شما جز او هیچ یاور و شفاعت کننده ای نیست، [با این حال] آیا متذکّر و هوشیار نمی شوید؟'),
(3508, 32, 5, '[همه] امور را [همواره] از آسمان تا زمین تدبیر و تنظیم می کند، سپس در روزی که اندازه آن به شمارش شما هزار سال است به سوی او بالا می رود.'),
(3509, 32, 6, 'این است [پروردگار] دانای نهان و آشکار که توانای شکست ناپذیر و مهربان است،'),
(3510, 32, 7, 'همان کسی که آنچه را آفرید نیکو ساخت، و آفرینش انسان را از گِل آغاز کرد،'),
(3511, 32, 8, 'سپس نسل او را از چکیده ای از آب پست و بی مقدار قرار داد،'),
(3512, 32, 9, 'آن گاه او را [از جهت اندام] درست و نیکو نمود، و از روح خود در وی دمید و برای شما گوش و چشم و قلب آفرید در حالی که اندک سپاس گزاری می کنید.'),
(3513, 32, 10, 'و [منکران لجوج] گفتند: آیا زمانی که [پس از مردنمان بدن ما ریز ریز شد و پوسید و] در زمین گم شدیم، آیا به راستی ما در آفرینشی جدید خواهیم آمد؟ [نه اینکه این لجوجان متکبر فقط منکر قدرت حق باشند] بلکه آنان منکر دیدار [قیامت و محاسبه اعمال به وسیله] پروردگارشان هستند،'),
(3514, 32, 11, 'بگو: فرشته مرگ که بر شما گماشته شده است [روح] شما را می گیرد، سپس به سوی پروردگارتان باز گردانده می شوید.'),
(3515, 32, 12, 'و اگر ببینی زمانی که [این] مجرمان [منکر قیامت] نزد پروردگارشان سرهایشان را به زیر افکنده اند [می بینی که می گویند:] پروردگارا! [به آنچه ما را وعده داده بودی] بینا شدیم و [به دعوت حق] شنوا گشتیم، پس ما را به دنیا برگردان تا کار شایسته انجام دهیم، بی تردید ما باور کننده [همه حقایق] هستیم.'),
(3516, 32, 13, 'و اگر می خواستیم، هدایت هرکسی را [از روی اجبار] به او عطا می کردیم، [اما همه را آزاد و مختار آفریدیم تا راه هدایت یا گمراهی را خود انتخاب کنند] ولی فرمان من بر عذاب لازم و حتم شده است که بی تردید دوزخ را از همه جنّیان و آدمیان [که آیات من را تکذیب کردند] پر خواهم کرد.'),
(3517, 32, 14, 'پس [به آنان گویند:] به کیفر آنکه دیدار امروزتان را فراموش کردید [عذاب دوزخ را] بچشید، که ما [هم] شما را واگذاشته ایم، و به کیفر آنچه همواره انجام می دادید، عذاب جاودانه را بچشید.'),
(3518, 32, 15, 'فقط کسانی به آیات ما ایمان می آورند که وقتی به وسیله آن آیات به آنان تذکر داده شود، سجده کنان به رو در می افتند و همراه با سپاس، پروردگارشان را از هر عیب و نقصی تنزیه می کنند در حالی که [از سجده و سپاس و تنزیه] تکبّر و سرکشی نمی ورزند؛'),
(3519, 32, 16, '[ملازم بستر استراحت و خواب نیستند، بلکه] پهلوهایشان از خوابگاه هایشان دور می شود در حالی که همواره پروردگارشان را به علت بیم [از عذاب] و امید [به رحمت و پاداش] می خوانند و از آنچه آنان را روزی داده ایم، انفاق می کنند.'),
(3520, 32, 17, 'پس هیچ کس نمی داند چه چیزهایی که مایه شادمانی و خوشحالی آنان است به پاداش اعمالی که همواره انجام می داده اند، برای آنان پنهان داشته اند.'),
(3521, 32, 18, 'با این حال آیا کسانی که مؤمن اند مانند کسانی هستند که فاسق اند؟ [نه هرگز این دو گروه] مساوی و یکسان نیستند.'),
(3522, 32, 19, 'اما کسانی که ایمان آورده و کارهای شایسته انجام داده اند، پس برای آنان بهشت هایی که اقامت گاه دائمی است خواهد بود [که به آنان] به پاداش اعمالی که همواره انجام می دادند، پیشکش می شود.'),
(3523, 32, 20, 'اما کسانی که نافرمانی کرده اند اقامتگاهشان آتش است، هرگاه بخواهند از آن بیرون آیند در آن بازگردانده می شوند، و به آنان گویند: عذاب آتش را که همواره آن را تکذیب می کردید، بچشید.'),
(3524, 32, 21, 'و بی تردید آنان را غیر از عذاب بزرگ تر [در قیامت] از عذاب نزدیک تر [در دنیا] می چشانیم، باشد که [از فسق و فجور] برگردند.'),
(3525, 32, 22, 'ستمکارتر از کسی که به وسیله آیات پروردگارش تذکر داده شود، سپس از آنها روی برگرداند، کیست؟ بی تردید ما از مجرمان انتقام می گیریم.'),
(3526, 32, 23, 'و به راستی ما به موسی کتاب دادیم، پس تو در دریافت کتاب [به وسیله موسی] هیچ تردیدی نداشته باش، و آن را برای بنی اسرائیل هدایت قرار دادیم،'),
(3527, 32, 24, 'و برخی از آنان را چون [در برابر مشکلات، سختی ها و حادثه های تلخ و شیرین] صبر کردند و همواره به آیات ما یقین داشتند، پیشوایانی قرار دادیم که به فرمان ما [مردم را] هدایت می کردند.'),
(3528, 32, 25, 'یقیناً پروردگارت روز قیامت، خود میان آنان درباره آنچه همواره در آن اختلاف می کردند، داوری خواهد کرد.'),
(3529, 32, 26, 'آیا مشرکان را [داستان] بسیاری از ملت هایی که ما پیش از آنان هلاک کردیم که [اینان] در مسکن هایشان قدم می زنند هدایت نکرد [و برای آنان روشن نساخت که سرانجام کفرپیشگان هلاکت است؟] همانا در این [سرگذشت ها] عبرت هاست، آیا نمی شنوند؟!'),
(3530, 32, 27, 'آیا ندانسته اند که ما آب را به سوی زمین بی گیاه می رانیم و به وسیله آن زراعتی را بیرون می آوریم که دام هایشان و خودشان از آن می خورند؟ پس آیا [آثار رحمت خدا را] نمی بینند؟!'),
(3531, 32, 28, 'و [مشرکان از روی مسخره به مؤمنان] می گویند: اگر راست می گویید این فتح و پیروزی [که به خود وعده می دهید و به آن دلخوش هستید] کی خواهد بود؟'),
(3532, 32, 29, 'بگو: روز فتح و پیروزی [حتمی مؤمنان] کسانی که کفر ورزیده اند، نه ایمان آوردنشان سودشان دهد و نه به آنان مهلت داده می شود؛'),
(3533, 32, 30, 'بنابراین از آنان روی برگردان و منتظر [رسیدن پیروزی] باش که آنان هم [به گمان خود] منتظرند [که بر شما پیروز شوند.]');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ای پیامبر! بر تقوای الهی ثابت قدم و استوار باش، و از کافران و منافقان اطاعت مکن که خدا همواره دانا و حکیم است.'),
(3535, 33, 2, 'و آنچه را که از سوی پروردگارت به تو وحی می شود پیروی کن؛ قطعاً خدا به آنچه انجام می دهید، آگاه است.'),
(3536, 33, 3, 'و بر خدا توکل کن؛ و کافی است که خدا نگهبان و کارساز [همه امور بندگان] باشد.'),
(3537, 33, 4, 'خدا برای هیچ مردی در درونش دو قلب قرار نداده، و همسرانتان را که همواره با آنان «ظهار» می کنید [یعنی بر پایه فرهنگ جاهلی با خواندن صیغه ای خاص آنان را به منزله مادرانتان به شمار آورده بر خود حرام می نمایید] مادران شما نگردانیده، و پسر خواندگانتان را پسران [واقعی] شما ننموده است. این سخن [باطل و بی پایه] شماست که به زبان شما [جاری] است، و خدا همواره حق را می گوید، و همواره راه پسر [درست و راست که شما را به سعادت می رساند] می نمایاند.'),
(3538, 33, 5, 'پسر خواندگانتان را به نام پدرانشان بخوانید که این نزد خدا عادلانه تر است؛ و اگر پدرانشان را نمی شناسید، پس برادران و دوستان دینی شمایند. و بر شما در آنچه [از انتساب هایی] که به خطا انجام داده اید، گناهی نیست؛ ولی آنچه را که دل هایتان تعمد و قصد جدی داشته است [گناه است]؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(3539, 33, 6, 'پیامبر، نسبت به مؤمنان از خودشان اولی و سزاوارتر است، و همسرانش [حرمت ازدواج به منزله] مادران آنانند، و بر طبق کتاب خدا دارندگان قرابت نسبی [در میراث بردنِ] از یکدیگر از مؤمنان و مهاجران [که پیش از این بر پایه ایمان و هجرت میراث می بردند] سزاوارترند، مگر اینکه بخواهید [در وصیت کردن به پرداخت ارث] درباره دوستانتان احسان کنید که این امری ثبت شده در کتاب [خدا] ست.'),
(3540, 33, 7, 'و [یاد کن] زمانی را که از پیامبران [برای ابلاغ وحی] پیمان گرفتیم، و [نیز] از تو و از نوح و از ابراهیم و موسی و عیسی پسر مریم، و از همه آنان پیمانی محکم و استوار گرفتیم،'),
(3541, 33, 8, 'تا صادقان را از صدقشان بپرسد [و پاداش صدقشان را به آنان عطا کند] و برای کافران عذابی دردناک آماده کرده است.'),
(3542, 33, 9, 'ای اهل ایمان! نعمت خدا را بر خود یاد کنید، هنگامی که سپاهیانی [به قصد نابود کردنتان] به سوی شما آمدند، پس بادی [کوبنده] و لشکریانی که آنها را نمی دیدید بر ضد آنان فرستادیم [تا آنان را در هم کوبیدند]؛ و خدا به آنچه انجام می دهید، بیناست.'),
(3543, 33, 10, 'زمانی که از بالا و از پایین [لشکرگاه] تان به سویتان آمدند، و آن گاه که دیده ها [از شدت ترس] خیره شد و جان ها به گلو رسید، و به خدا آن گمان ها [ی ناروا] را [که خود می دانید] می بردید.'),
(3544, 33, 11, 'آنجا بود که مؤمنان مورد آزمایش قرار گرفتند و به تزلزل و اضطرابی سخت دچار شدند.'),
(3545, 33, 12, 'و آن گاه که منافقان و آنان که در دل هایشان بیماری [ضعف ایمان] بود، می گفتند: خدا و پیامبرش جز به فریب، ما را وعده [پیروزی] نداده اند!'),
(3546, 33, 13, 'و آن گاه که گروهی از آنان گفتند: ای اهل مدینه! [میدان نبرد] جای درنگ و ماندن شما نیست، پس برگردید. و گروهی از آنان از پیامبر اجازه [برگشتن] می خواستند، و می گفتند: خانه های ما بدون حفاظ است. در صورتی که بدون حفاظ نبود، و آنان جز فرار را قصد نداشتند!'),
(3547, 33, 14, 'و اگر از پیرامون خانه هایشان بر آنان حمله می شد و از آنان بازگشت [به شرک و جنگ با مؤمنان] درخواست می شد، آن را می پذیرفتند و برای آن جز مدت کوتاهی [به اندازه تجهیز خود بر ضد مؤمنان] درنگ نمی کردند!!'),
(3548, 33, 15, 'و همانا آنان از پیش با خدا پیمان بسته بودند که [به دشمن] پشت نکنند؛ و پیمان خدا همواره بازخواست شدنی است.'),
(3549, 33, 16, 'بگو: اگر از مرگ یا کشته شدن بگریزید، گریز شما هرگز سودتان نمی دهد، و در این صورت [اگر هم سودتان دهد، از این زندگی زودگذر فانی] جز اندکی برخوردار نخواهید شد.'),
(3550, 33, 17, 'بگو: اگر خدا برای شما آسیب و گزندی یا پیروزی و غنیمتی بخواهد، کیست که شما را در برابر [تقدیرات و قضای] خدا نگه دارد؟ در صورتی که غیر از خدا نه کارسازی برای خود می یابند، نه یاری دهنده ای.'),
(3551, 33, 18, 'یقیناً خدا بازدارندگان را از میان شما [که مجاهدان را با وسوسه و اغواگری از شرکت در جهاد باز می دارند] و کسانی را که به برادرانشان [آن براداران دینی که ایمانشان سست است] می گویند: [برای عیش و نوش] به سوی ما بیایید [شما را به شرکت در جهاد چه کار؟] می شناسد؛ و جز اندکی به جهاد نمی آیند.'),
(3552, 33, 19, 'در حالی که نسبت به شما [برای هزینه کردن هر نوع کمکی] بخیل اند؛ و چون [به سبب افروخته شدن آتش جنگ] ترس پیش آید آنان را می بینی به سوی تو می نگرند در حالی که چشمانشان [بی اختیار در حدقه] می گردد، مانند کسی که بیهوشی مرگ او را فرو می پوشد، پس هنگامی که ترس برطرف شود با زبان هایی تیز و تند به شما آزار می دهند، در حالی که بر [سخن] خیر [و زبان خوش و نرم] بخیل اند؛ اینان ایمان نیاورده اند، به این خاطر خدا اعمالشان را تباه و بی اثر کرده است؛ و این [کار] بر خدا آسان است.'),
(3553, 33, 20, '[این بزدلان منافق] می پندارند که گروه های دشمن نرفته اند و اگر بار دیگر گروه های دشمن بیایند، آنان دوست دارند که کاش در میان اعراب بادیه نشین بودند، و [همانجا] از خبرهای شما می پرسیدند، و اگر در میان شما بودند جز اندکی جنگ نمی کردند.'),
(3554, 33, 21, 'یقیناً برای شما در [روش و رفتار] پیامبر خدا الگوی نیکویی است برای کسی که همواره به خدا و روز قیامت امید دارد؛ و خدا را بسیار یاد می کند.'),
(3555, 33, 22, 'هنگامی که مؤمنان [در نبرد خندق] گروه های دشمن را دیدند، گفتند: این است آنچه خدا وپیامبرش به ما وعده داده اند [که ثواب و پاداش عظیم روز بازپسین بدون تحمل سختی ها و سرافراز بیرون آمدن از آزمایش ها، میسر نیست] و خدا و پیامبرش راست گفته اند، و [جنگ خندق] جز بر ایمان و تسلیم آنان نیفزود.'),
(3556, 33, 23, 'از مؤمنان مردانی هستند که به آنچه با خدا بر آن پیمان بستند [و آن ثبات قدم و دفاع از حق تا نثار جان بود] صادقانه وفا کردند، برخی از آنان پیمانشان را به انجام رساندند [و به شرف شهادت نایل شدند] و برخی از آنان [شهادت را] انتظار می برند و هیچ تغییر و تبدیلی [در پیمانشان] نداده اند،'),
(3557, 33, 24, 'تا سرانجام خدا صادقان را به سبب صدقشان پاداش دهد، و منافقان را اگر بخواهد عذاب کند یا [اگر توبه کنند] توبه آنان را بپذیرد؛ زیرا خدا همواره بسیار آمرزنده و مهربان است.'),
(3558, 33, 25, 'و خدا کافران [شرکت کننده در جنگ خندق] را در حالی که به پیروزی و غنیمت دست نیافتند با خشم و اندوهشان برگرداند، و خدا [سختی و مشقت] جنگ را از مؤمنان برداشت؛ و خدا همواره نیرومند و توانای شکست ناپذیر است.'),
(3559, 33, 26, 'و [خدا] کسانی را از اهل کتاب که گروه های دشمن را پشتیبانی کردند از قلعه های محکم و استوارشان پایین کشید و در دل هایشان ترس و بیم افکند، گروهی را می کشتید و گروهی را اسیر می کردید،'),
(3560, 33, 27, 'و سرزمینشان و دیار و اموالشان و سرزمینی که به آن قدم نگذاشته بودید به شما میراث داد؛ و خدا بر هر کاری تواناست.'),
(3561, 33, 28, 'ای پیامبر! به همسرانت بگو: اگر شما زندگی دنیا و زیور و زینتش را می خواهید بیایید تا برخوردارتان نمایم و با روشی پسندیده رهایتان کنم،'),
(3562, 33, 29, 'و اگر خدا و پیامبر او و سرای آخرت را می خواهید، پس خدا یقیناً برای نیکوکاران شما پاداشی بزرگ آماده کرده است.'),
(3563, 33, 30, 'ای همسران پیامبر! هرکس از شما کار بسیار زشت آشکاری مرتکب شود [قطعاً پیوند همسری با پیامبر به او مصونیّت نمی دهد، بلکه] عذاب برای او دو چندان خواهد شد، و این [کار] بر خدا آسان است.'),
(3564, 33, 31, 'و هرکس از شما برای خدا و پیامبرش اطاعت خاضعانه کند و کار شایسته انجام دهد، پاداشش را دو برابر به او می دهیم و برای او رزقی نیکو و با ارزش آماده کرده ایم.'),
(3565, 33, 32, 'ای همسران پیامبر! شما اگر پرهیزکاری پیشه کنید [از نظر منزلت و موقعیت] مانند هیچ یک از زنان نیستید، پس در گفتار خود، نرمی و طنّازی [چنانکه عادت بیشتر زنان است] نداشته باشید تا کسی که بیمار دل است طمع کند، و سخن پسندیده و شایسته گویید.'),
(3566, 33, 33, 'و در خانه هایتان قرار و آرام گیرید، و [در میان نامحرمان و کوچه و بازار] مانند زنان دوران جاهلیت پیشین [که برای خودنمایی با زینت و آرایش و بدون پوشش در همه جا ظاهر می شدند] ظاهر نشوید، و نماز را برپا دارید و زکات بدهید، و خدا و پیامبرش را اطاعت کنید، جز این نیست که همواره خدا می خواهد هرگونه پلیدی را از شما اهل بیت [که به روایت شیعه و سنی محمّد، علی، فاطمه، حسن و حسین علیهم السلام اند] برطرف نماید، و شما را چنان که شایسته است [از همه گناهان و معاصی] پاک و پاکیزه گرداند.'),
(3567, 33, 34, '[ای همسران پیامبر!] و آنچه را از آیات خدا و حکمت در خانه های شما می خوانند به یاد داشته باشید؛ یقیناً خدا لطیف و آگاه است.'),
(3568, 33, 35, 'مسلماً خدا برای مردان و زنان مسلمان و مردان و زنان با ایمان، و مردان و زنان عبادت پیشه، و مردان و زنان راستگو و مردان و زنان شکیبا، و مردان و زنان فروتن، و مردان و زنان صدقه دهنده، و مردان و زنان روزه دار، و مردان و زنان حفظ کننده خود از پلیدی های جنسی، و مردان و زنانی که بسیار یاد خدا می کنند، آمرزش و پاداشی بزرگ آماده کرده است.'),
(3569, 33, 36, 'و هیچ مرد و زن مؤمنی را نرسد هنگامی که خدا و پیامبرش کاری را حکم کنند برای آنان در کار خودشان اختیار باشد؛ و هرکس خدا و پیامبرش را نافرمانی کند یقیناً به صورتی آشکار گمراه شده است.'),
(3570, 33, 37, 'و [یاد کن] زمانی را که به آن شخص که خدا به او نعمت [اسلام] بخشیده بود، و تو هم [با آزاد کردنش از طوق بردگی] به او احسان کرده بودی، می گفتی: همسرت را برای خود نگه دار و از خدا پروا کن. و تو در باطن خود چیزی را [چون فرمان خدا به ازدواج با مطلقه او] پنهان می داشتی که خدا آشکار کننده آن بود [تا برای مردم در ازدواج مطلقه پسر خوانده هایشان ممنوعیت و مشکلی نباشد] و [برای بیان فرمان خدا نسبت به مشروعیت ازدواج مطلقه پسر خوانده] از مردم می ترسیدی و در حالی که خدا سزاوارتر بود که از او بترسی، پس هنگامی که «زید» نیاز خود را از همسرش به پایان برد [و او را طلاق داد] وی را به همسری تو درآوردیم تا برای مؤمنان نسبت به ازدواج با همسران پسر خوانده هایشان زمانی که نیازشان را از آنان به پایان برده باشند سختی و حرجی نباشد؛ و همواره فرمان خدا شدنی است.'),
(3571, 33, 38, 'بر پیامبر در آنچه خدا برای او مقرّر و لازم کرده منعی نیست، خدا این روش را درباره کسانی [از پیامبران] که پیش از این گذشته اند مقرّر داشته است و همواره فرمان خدا نافذ و اندازه گیری شده است.'),
(3572, 33, 39, '[آری این روش خداست درباره] آنان که همواره پیامهای خدا را به مردم می رسانند و از [عظمت و مقام] او می ترسند و از هیچ کس جز او واهمه ندارند و برای حسابرسی [کار با ارزش اینان] خدا کافی است.'),
(3573, 33, 40, 'محمّد، پدر هیچ یک از مردان شما نیست، ولی فرستاده خدا و خاتم پیامبران است؛ و خدا به هر چیزی داناست.'),
(3574, 33, 41, 'ای اهل ایمان! خدا را بسیار یاد کنید،'),
(3575, 33, 42, 'و صبح و شام او را تسبیح گویید.'),
(3576, 33, 43, 'اوست که با فرشتگان خود بر شما درود می فرستد تا شما را از تاریکی ها به سوی نور بیرون آورد، و او به مؤمنان مهربان است.'),
(3577, 33, 44, 'درود و تحیّت [خدا] بر آنان، روزی که با [پاداش و مقام قرب] او دیدار می کنند، سلام است، و [خدا] برای آنان پاداشی نیکو و باارزش آماده کرده است.'),
(3578, 33, 45, 'ای پیامبر! به راستی ما تو را شاهد [بر امت] و مژده رسان و بیم دهنده فرستادیم.'),
(3579, 33, 46, 'و تو را دعوت کننده به سوی خدا به فرمان او و چراغی فروزان [برای هدایت جهانیان] قرار دادیم،'),
(3580, 33, 47, 'و مؤمنان را مژده ده که برای آنان از سوی خدا فضل بزرگی خواهد بود،'),
(3581, 33, 48, 'و از کافران و منافقان اطاعت مکن و آزارشان را واگذار و بر خدا توکل کن، و کافی است که خدا نگهبان و کارساز [انسان] باشد،'),
(3582, 33, 49, 'ای اهل ایمان! هنگامی که زنان مؤمن را به همسری خود درآوردید، آن گاه پیش از آنکه با آنان آمیزش کنید طلاقشان دادید، برای شما بر عهده آنان عدّه ای نیست که آن را بشمارید، پس آنان را [به پرداخت نصف مهریه] بهره مندشان کنید و به صورت پسندیده ای رهایشان سازید.'),
(3583, 33, 50, 'ای پیامبر! برایت حلال کردیم آن همسرانت را که مهرشان را داده ای و کنیزانی که خدا غنیمت به تو داده است و دختر عموها و دختر عمه ها و دختر دایی ها و دختر خاله هایت را که با تو مهاجرت نموده اند و زن مؤمنی که خود را به پیامبر [مجانی و بدون مهر] هبه کند، اگر پیامبر او را به همسری بخواهد؛ این حکم ویژه توست نه مؤمنان. یقیناً ما آنچه را در مورد همسران و کنیزانشان بر آنان لازم و مقرّر داشته ایم، می دانیم؛ [این گشایش در ازدواج] برای آن است که بر تو سختی و حَرَجی نباشد؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(3584, 33, 51, 'نوبت هر یک از آنان را که بخواهی به تأخیر انداز و هر کدام را که میل داری نزد خود جای ده، و هر کدام از آنان را که از او کناره گرفته باشی [اگر] نزد خود بطلبی بر تو گناهی نیست. این [برنامه ریزی آزادانه،] به خوشحال نمودنشان و اینکه اندوهگین نشوند و همگی به آنچه به آنان داده ای خشنود شوند، نزدیک تر است؛ و خدا آنچه را در دل های شماست می داند؛ و خدا دانا و بردبار است.'),
(3585, 33, 52, '[غیر از زن هایی که گفته شد] از این پس زنان دیگر برای تو حلال نیست، و [نیز] جایز نیست که آنان را به همسرانی دیگر تبدیل کنی گرچه زیبایی آنان تو را خوش آید، مگر کنیزی که مالکش شوی؛ و خدا نگهبان و مراقب همه چیز است.'),
(3586, 33, 53, 'ای مؤمنان! به خانه های پیامبر جز آنکه برای خوردن غذایی به شما اجازه دهند وارد نشوید، چشم انتظار فرا رسیدن وقت خوردن آن هم نباشید [که پی در پی آوردنش را بخواهید و از این جهت اسائه ادب کنید]؛ ولی هنگامی که دعوت شدید وارد شوید، و چون غذا خوردید و بی آنکه [پس از صرف غذا] سرگرم سخن گردید، پراکنده شوید، این [کار که بنشینید و سرگرم سخن گردید] پیامبر را آزار می دهد و از شما حیا می کند [که بیرونتان کند] ولی خدا از حق حیا نمی کند. و زمانی که از همسرانش متاعی خواستید از پشت پرده و حجابی از آنان بخواهید، که این برای قلب شما و قلب های آنان پاکیزه تر است. و شما را نسزد [و جایز نباشد] که پیامبر خدا را آزار دهید. و بر شما هرگز جایز نیست که پس از او با همسرانش ازدواج کنید؛ که این [کار] نزد خدا بزرگ است.'),
(3587, 33, 54, 'اگر چیزی را آشکار کنید یا آن را پنهان نمایید [خدا می داند]؛ یقیناً خدا به هر چیزی داناست.'),
(3588, 33, 55, 'همسران پیامبر را نزد پدرانشان و پسرانشان و برادرانشان و پسران برادرانشان و پسران خواهرانشان و زنان هم کیششان و بردگانشان [در ترک پوشش و حجاب] گناهی نیست؛ و از خدا پروا کنید که خدا به هر چیزی گواه است.'),
(3589, 33, 56, 'همانا خدا و فرشتگانش بر پیامبر درود و رحمت می فرستند. ای اهل ایمان! بر او درود فرستید و آن گونه که شایسته است، تسلیم او باشید.'),
(3590, 33, 57, 'قطعاً آنان که خدا و پیامبرش را می آزارند، خدا در دنیا و آخرت لعنتشان می کند، و برای آنان عذابی خوارکننده آماده کرده است.'),
(3591, 33, 58, 'و کسانی که مردان و زنان مؤمن را [با متهم کردن] به اعمالی که انجام نداده اند، می آزارند، بی تردید بهتان و گناه بزرگی بر عهده گرفته اند.'),
(3592, 33, 59, 'ای پیامبر! به همسرانت و دخترانت و همسران کسانی که مؤمن هستند بگو: چادرهایشان را بر خود فرو پوشند [تا بدن و آرایش و زیورهایشان در برابر دید نامحرمان قرار نگیرد.] این [پوشش] به اینکه [به عفت و پاکدامنی] شناخته شوند نزدیک تر است، و در نتیجه [از سوی اهل فسق و فجور] مورد آزار قرار نخواهند گرفت؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(3593, 33, 60, 'اگر منافقان و آنان که در دل هایشان بیماری [ضعف ایمان] است و آنانکه در مدینه شایعه های دروغ و دلهره آور پخش می کنند [از رفتار زشتشان] باز نایستند، تو را بر ضد آنان برمی انگیزیم [که یا تبعیدشان کنی یا با آنان بجنگی]، آن گاه در این شهر جز اندکی [که خالص و پاک هستند] در کنار تو نخواهند ماند.'),
(3594, 33, 61, '[این چند گروه به علت اعمال ناهنجارشان] طردشدگان [از رحمت خدای] اند، هرجا که یافت شوند باید دستگیر شوند، و به سختی به قتل برسند.'),
(3595, 33, 62, 'خدا شیوه و روش خود را درباره کسانی [مانند این خائنان] که پیش تر درگذشتند، این گونه قرار داده است، و برای شیوه و روش خدا هرگز تغییر و دگرگونی نخواهی یافت.'),
(3596, 33, 63, 'مردم درباره [وقت] قیامت از تو می پرسند، بگو: دانش و آگاهی آن فقط نزد خداست. و تو چه می دانی؟ شاید قیامت نزدیک باشد.'),
(3597, 33, 64, 'همانا خدا کافران را لعنت کرده، و آتشی افروخته برای آنان آماده کرده است.'),
(3598, 33, 65, 'همیشه در آن جاودانه اند، و سرپرست و یاوری [که آنان را نجات دهد] نیابند.'),
(3599, 33, 66, 'روزی که چهره هایشان را در آتش از سویی به سویی می گردانند، [و] می گویند: ای کاش ما [در دنیا] از خدا و پیامبر اطاعت کرده بودیم،'),
(3600, 33, 67, 'و می گویند: پروردگارا! همانا ما از فرمانروایان و بزرگانمان اطاعت کردیم، در نتیجه گمراهمان کردند.'),
(3601, 33, 68, 'پروردگارا! آنان را از عذاب دو چندان ده، و کاملاً از رحمتت دورشان ساز.'),
(3602, 33, 69, 'ای اهل ایمان! [درباره پیامبرتان] مانند کسانی نباشید که موسی را [با تهمت و دروغ بستن به او] آزار دادند و خدا او را از آنچه درباره او می گفتند، پاک و مبرّا ساخت، و او نزد خدا آبرومند بود.'),
(3603, 33, 70, 'ای اهل ایمان! از خدا پروا کنید و سخن درست و استوار گویید.'),
(3604, 33, 71, 'تا [خدا] اعمالتان را برای شما اصلاح کند و گناهتان را بر شما بیامرزد. و هرکس خدا و پیامبرش را اطاعت کند، بی تردید رستگاری بزرگی یافته است.'),
(3605, 33, 72, 'یقیناً ما امانت را [که تکالیف شرعیه سعادت بخش است] بر آسمان ها و زمین و کوه ها عرضه کردیم و آنها از به عهده گرفتنش [به سبب اینکه استعدادش را نداشتند] امتناع ورزیدند و از آن ترسیدند، و انسان آن را پذیرفت بی تردید او [به علت ادا نکردن امانت] بسیار ستمکار، و [نسبت به سرانجام خیانت در امانت] بسیار نادان است.'),
(3606, 33, 73, 'تا نهایتاً خدا مردان و زنان منافق، و مردان و زنان مشرک را [به سبب خیانت در امانت] عذاب کند و توبه مردان و زنان مؤمن را [به علت لغزش در امانت] بپذیرد؛ و خدا همواره بسیار آمرزنده و مهربان است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'همه ستایش ها ویژه خداست که آنچه در آسمان ها و آنچه در زمین است در سیطره مالکیّت و فرمانروایی اوست، و همه ستایش ها در آخرت مخصوص اوست، و او حکیم و آگاه است.'),
(3608, 34, 2, '[خدا] آنچه در زمین فرو می رود و آنچه از آن بیرون می آید و آنچه از آسمان فرود می آید و آنچه در آن بالا می رود عالم و آگاه است؛ و او مهربان و بسیار آمرزنده است.'),
(3609, 34, 3, 'و کافران گفتند: قیامت بر ما نخواهد آمد. بگو: آری، سوگند به پروردگارم که دانای غیب است، حتماً بر شما خواهد آمد؛ در آسمان ها و زمین هم وزن ذرّه ای از او پوشیده نیست، و نه کوچک تر از آن و نه بزرگ تر از آن هست مگر اینکه در کتابی روشن [ثبت] است.'),
(3610, 34, 4, '[آری، قیامت حتماً می آید] تا خدا کسانی را که ایمان آورده و کارهای شایسته انجام داده اند، پاداش دهد، اینانند که برای آنان آمرزشی و رزق نیکو و ارزشمندی خواهد بود.'),
(3611, 34, 5, 'و کسانی که در [انکار و تکذیب] آیات ما کوشیدند، به گمان اینکه [می توانند] ما را عاجز کنند [تا از دسترس قدرت ما بیرون روند] برای آنان عذابی دردناک از سخت ترین عذاب هاست.'),
(3612, 34, 6, 'و کسانی که معرفت و دانش به آنان عطا شده، می دانند که آنچه از سوی پروردگارت بر تو نازل شده حق است، و به راه خدای توانای شکست ناپذیر و ستوده هدایت می کند.'),
(3613, 34, 7, 'و کافران گفتند: [ای مردم!] آیا شما را به مردی دلالت کنیم که به شما خبر [عجیبی] می دهد، هنگامی که [در خاک گور] به طور کامل متلاشی [و قطعه قطعه] شوید، در آفرینشی جدید و تازه خواهید آمد!'),
(3614, 34, 8, 'آیا [به نظر شما این خبر دهنده] بر خدا دروغ بسته، یا دچار نوعی جنون است؟! [نه، چنین نیست که می پندارند] بلکه کسانی که به آخرت ایمان ندارند، در عذاب و گمراهی دوری هستند.'),
(3615, 34, 9, 'آیا به آنچه از آسمان و زمین پیش رو و پشت سر آنان است، ننگریستند [که همان خدایی که جهان هستی را آفرید بر زنده کردن مردگان تواناست؟] ما اگر بخواهیم آنان را در زمین فرو می بریم یا پاره هایی از آسمان را بر سرشان می افکنیم؛ بی تردید در این [آفرینش] برای هر بنده بازگشت کننده، نشانه ای [بر قدرت خدا] ست.'),
(3616, 34, 10, 'همانا به داود از سوی خود فضلی بزرگ عطا کردیم، [و گفتیم:] ای کوه ها و ای پرندگان! [در تسبیح خدا] با او، هم صدا شوید. و آهن را برای او نرم کردیم.'),
(3617, 34, 11, '[و به او گفتیم] که زره های فراخ بساز، و حلقه ها [ی آن] را متناسب و هماهنگ اندازه گیری کن. و تو و خاندان و قومت کار شایسته انجام دهید؛ یقیناً من به آنچه انجام می دهید بینایم.'),
(3618, 34, 12, 'و باد را برای سلیمان [مسخّر و رام کردیم]، که رفتن صبح گاهش [به اندازه] یک ماه و رفتن شام گاهش [به اندازه] یک ماه بود؛ و چشمه مس را برای او روان ساختیم، و گروهی از جن به اذن پروردگارش نزد او کار می کردند، و هر کدام از آنان از فرمان ما سرپیچی می کرد از عذاب سوزان به او می چشاندیم.'),
(3619, 34, 13, '[گروه جن] برای او هرچه می خواست، می ساختند از معبدها، و مجسمه ها، و ظروف بزرگی مانند حوض ها، و دیگ هایی ثابت. ای خاندان داود! به خاطر سپاس گزاری [به فرمان ها حق] عمل کنید؛ و از بندگانم اندکی سپاس گزارند.'),
(3620, 34, 14, 'پس هنگامی که مرگ را بر او مقرّر کردیم، جنیان را از مرگش جز موریانه ای که عصایش را می خورد، آگاه نکرد؛ زمانی که به روی زمین در افتاد، جنّیان [که ادعای علم غیب داشتند] فهمیدند که اگر غیب می دانستند در آن عذاب خوارکننده [که کارهای بسیار پرزحمت و طاقت فرسا بود] درنگ نمی کردند.'),
(3621, 34, 15, 'یقیناً برای قوم سبا در جای اقامتشان نشانه ای [از قدرت و رحمت خدا] وجود داشت، دو باغ از طرف راست و چپ. [گفتیم:] از رزق و روزی پروردگارتان بخورید، و برای او سپاس گزاری کنید، سرزمینی خوش و دلپذیر [دارید]، و پروردگاری بسیار آمرزنده'),
(3622, 34, 16, 'ولی [آنان از سپاس گزاری در برابر نعمت، و از فرمان ها او و دعوت پیامبرشان] روی گرداندند، در نتیجه سیل [ویران گر] «عرم» را بر ضد آنان جاری کردیم [که دو باغ آباد راست و چپ منطقه را نابود کرد]، و ما آن دو باغ پر حاصلشتان را به دو باغستانی تبدیل کردیم که دارای میوه هایی تلخ و درخت شوره گز و چیزی اندک از درخت سدر بودند!!'),
(3623, 34, 17, 'این [سیل ویران گر] را در برابر کفرشان به آنان کیفر دادیم. آیا جز کفران کننده را کیفر می دهیم؟'),
(3624, 34, 18, 'و میان مردم سبا و شهرهایی که در آن ها برکت نهادیم، آبادی های به هم پیوسته و نمایان قرار دادیم، و سیر و سفر را در [میان] آنان متناسب و به اندازه مقرّر داشتیم، [و گفتیم:] شب ها و روزها با امنیت در آنها مسافرت کنید.'),
(3625, 34, 19, 'پس [این مغرورشدگان به رفاه و خوشی و ناسپاسان در برابر نعمت] گفتند: پروردگارا! میان سفرهای ما، دوری و فاصله انداز. [این را درخواست کردند تا تهیدستان و پا برهنگان نتوانند در کنار آنان سفر کنند]، و [این گونه آنان] بر خودشان ستم کردند، پس ما آنان را داستان هایی [برای عبرت آیندگان] قرار دادیم و جمعشان را به شدت متلاشی و تار و مار کردیم، همانا در این [سرگذشت ها] برای هر صبرکننده سپاس گزاری عبرت هاست'),
(3626, 34, 20, 'و همانا ابلیس، پندارش را [که گفته بود: نسل آدم را گمراه می کنم] درباره آنان تحقق یافت که همه جز گروهی از مؤمنان از او پیروی کردند.'),
(3627, 34, 21, 'و ابلیس را بر آنان [در اینکه فرمان بردارشان کند] هیچ تسلطی نبود [جز وسوسه کردن و باز بودن راه وسوسه بر او، هیچ سببی نداشت] مگر اینکه می خواستیم کسانی را که به آخرت ایمان دارند از آنان که درباره آن در تردیدند مشخص کنیم؛ و پروردگارت بر هر چیزی نگهبان است.'),
(3628, 34, 22, 'بگو: کسانی را که به جای خدا [سزاوار پرستیدن] پنداشته اید، بخوانید [تا خواسته هایتان را اجابت کنند، ولی آنها هیچ خواسته ای را از شما اجابت نمی کنند؛ زیرا] در آسمان ها و زمین، هموزن ذره ای را مالک نیستند و آنها را در آن دو هیچ سهم و شرکتی نیست، و از میان آنها هیچ پشتیبانی برای خدا وجود ندارد.'),
(3629, 34, 23, 'شفاعت در پیشگاه خدا جز برای کسانی که به آنان اذن دهد سودی ندارد، [آن روز شفیعان و امیدواران به شفاعت مضطربانه به انتظار اذن خدا برای شفاعت هستند] تا زمانی که با صدور اذن شفاعت دل هایشان آرام گیرد [در این هنگام مجرمان به شفیعان] گویند: پروردگارتان چه گفت؟ می گویند: حق گفت و او بلند مرتبه و بزرگ است.'),
(3630, 34, 24, '[به مشرکان] بگو: چه کسی از آسمان ها و زمین به شما روزی می دهد؟ بگو: خدا. و بی تردید ما یا شما بر هدایت یا در گمراهی آشکار هستیم.'),
(3631, 34, 25, 'بگو: شما از گناه ما بازخواست نخواهید شد، و ما هم از آنچه شما انجام می دهید، بازخواست نخواهیم شد.'),
(3632, 34, 26, 'بگو: پروردگارمان میان ما و شما را [روز قیامت] جمع می کند، سپس میان ما و شما به حق داوری خواهد کرد، و او داور دانایی است.'),
(3633, 34, 27, 'بگو: کسانی را که به عنوان شریکان به او ملحق کرده اید به من نشان دهید [تا بنگرم آیا صفاتی که واجب است در یک معبود باشد در آنها هست؟ یقیناً] این چنین [صفاتی در آنها] نیست، بلکه فقط خدا توانای شکست ناپذیر و حکیم است.'),
(3634, 34, 28, 'و ما تو را برای همه مردم جز مژده رسان و بیم دهنده نفرستادیم، ولی بیشتر مردم [به این واقعیت] معرفت و آگاهی ندارند.'),
(3635, 34, 29, 'و [از روی مسخره] می گویند: اگر راستگویید این وعده [قیامت و عذاب] کی خواهد بود؟'),
(3636, 34, 30, 'بگو: وعده گاه شما روزی است که نه ساعتی از آن تأخیر می کنید و نه بر آن پیشی می گیرید.'),
(3637, 34, 31, 'و کافران گفتند: ما به این قرآن و کتاب هایی که پیش از آن بوده است، هرگز ایمان نمی آوریم. و اگر ستمکاران را زمانی که در پیشگاه پروردگارشان [برای محاکمه] بازداشت شده اند، ببینی در حالی که هر کدام گناه و بدبختی خود را بر عهده دیگری می اندازد [امر عجیبی می بینی] مستضعفان به مستکبران می گویند: اگر شما سر راه ما نبودید یقیناً ما مؤمن بودیم.'),
(3638, 34, 32, 'مستکبران به مستضعفان می گویند: آیا ما شما را از هدایت الهی بعد از آنکه به شما رسید باز داشتیم؟ [نه، باور نداریم] بلکه شما خودتان مجرم بودید.'),
(3639, 34, 33, 'مستضعفان به مستکبران می گویند: بلکه نیرنگ های [پی گیر شما در] شب و روز که به ما فرمان می دادید به خدا کافر شویم و همتایانی برای او قرار دهیم [ما را گمراه کرد]. و هنگامی که عذاب را ببینند، اظهار پشیمانی کنند [شاید نجات یابند]. و ما در گردن کافران غل و زنجیر می نهیم؛ آیا جز آنچه را انجام می دادند جزا داده می شوند.'),
(3640, 34, 34, 'در هیچ شهری هیچ بیم دهنده ای نفرستادیم مگر آنکه خوش گذرانانِ مغرور و سرمستِ آنان گفتند: ما به آنچه شما را به آن فرستاده اند، کافریم!'),
(3641, 34, 35, 'و گفتند: اموال و فرزندان ما از شما بیشتر است و [این دلیل توجه ویژه خدا به ماست؛ بنابراین اگر عذابی هم باشد] ما را عذاب نخواهند کرد.'),
(3642, 34, 36, 'بگو: یقیناً پروردگارم روزی را برای هر کس بخواهد وسعت می دهد یا تنگ می گیرد، ولی بیشتر مردم [نسبت به مصلحت خدا] معرفت و آگاهی ندارند.'),
(3643, 34, 37, 'و اموال و فرزندانتان چیزهایی نیستند که شما را نزد ما مقرب کنند مگر آنان که ایمان آورده و کار شایسته انجام داده اند [که به سبب ایمان و کار شایسته، مقرب ما هستند]؛ پس اینانند که برای آنان در برابر آنچه انجام داده اند، پاداش مضاعف است، و آنان در غرفه ها [ی بهشتی از هر گزند و آسیبی] آسوده خاطرند.'),
(3644, 34, 38, 'و کسانی که همواره در [انکار و ابطال] آیات ما می کوشند، به گمان اینکه می توانند ما را عاجز کنند [تا از دسترس قدرت ما بیرون روند]، اینان احضار شدگان در عذاب خواهند بود.'),
(3645, 34, 39, 'بگو: یقیناً پروردگارم روزی را برای هر کس از بندگانش بخواهد وسعت می دهد و یا تنگ می گیرد، و هرچه را انفاق می کنید [چه کم و چه زیاد] خدا عوضی را جایگزین آن می کند؛ و او بهترین روزی دهندگان است.'),
(3646, 34, 40, 'و [یاد کن] روزی را که [خدا] همه آنان را محشور می کند، آن گاه به فرشتگان می گوید: آیا اینان شما را می پرستیدند؟'),
(3647, 34, 41, 'می گویند: تو منزهی [از اینکه در پرستیده شدن شریک و همتا داشته باشی]، تو سرپرست و یار مایی نه آنان، [آنان ما را نمی پرستیدند] بلکه جنّیان را می پرستیدند [و] بیشترشان به آنها ایمان داشتند.'),
(3648, 34, 42, 'امروز برای یکدیگر مالک سود و زیانی نیستید، و به آنان که ستم کردند می گوییم: عذاب آتشی را که انکار می کردید، بچشید.'),
(3649, 34, 43, 'و هنگامی که آیات روشن ما بر آنان خوانده شود، می گویند: این نیست مگر مردی که می خواهد شما را از آنچه پدرانتان می پرستیدند، باز دارد. و می گویند: این قرآن جز دروغی ساختگی نیست. و کافران درباره حق، هنگامی که به سویشان آمد گفتند: این جز جادویی آشکار نیست!!'),
(3650, 34, 44, 'و [این در حالی است که] هیچ کتابی به آنان نداده ایم که آن را بخوانند [تا به تکیه بر آن قرآن را دروغی ساختگی بدانند]، و پیش از تو هیچ پیامبری نفرستادیم تا [با اعتماد به گفتار او] نبوّت تو را انکار کنند،'),
(3651, 34, 45, 'و کسانی که پیش از اینان بودند [آیات ما و پیامبران را] تکذیب کردند، و اینان به یک دهم قدرت و ثروتی که به آنان داده بودیم، نرسیده اند، پس پیامبرانم را تکذیب کردند [در نتیجه هلاکشان کردیم]؛ پس [بنگر] عذاب من چگونه بود؟'),
(3652, 34, 46, 'بگو: من شما را فقط به یک حقیقت اندرز می دهم [و آن] اینکه دو دو و یک یک برای خدا قیام کنید، سپس درباره رفیقتان [محمّد که عمری با پاکی، امانت، صدق و درستی در میان شما زندگی کرده است] بیندیشید که هیچ گونه جنونی ندارد، او برای شما از عذاب سختی که پیش روست، جز بیم دهنده ای نیست.'),
(3653, 34, 47, 'بگو: هر گونه پاداشی که از شما خواستم، آن پاداش برای خودتان، پاداش من فقط بر عهده خداست، و او بر همه چیز گواه است.'),
(3654, 34, 48, 'بگو: همانا پروردگارم که دانای غیب است، حق را [بر قلوب پیامبرانش] القا می کند.'),
(3655, 34, 49, 'بگو: حق آمد و باطل [که آن را به جای خدا می پرستید] نه می تواند چیزی را به وجود آورد، و نه می تواند [چیزی که از بین رفته] برگرداند.'),
(3656, 34, 50, 'بگو: اگر من گمراه شده باشم، فقط به زیان خود گمراه شده ام، و اگر هدایت شده باشم به سبب آن است که پروردگارم به من وحی می کند؛ زیرا او شنوا و نزدیک است.'),
(3657, 34, 51, 'و اگر ببینی [مشرکان] زمانی که [با ظهور قیامت] ترسان و وحشت زده می شوند [امر عجیبی می بینی] و [آن اینکه برای آنان] توانایی [گریز از عذاب] نیست، و از جایی نزدیک [که موقف قیامت است] بازداشت می شوند.'),
(3658, 34, 52, 'و [در آن موقعیت بسیار سخت] می گویند: به حق ایمان آوردیم. کجا و چگونه می توانند از فاصله بسیار دور [که فاصله قیامت تا دنیاست] به آن ایمان دسترسی پیدا کنند! [زیرا آخرت جای تکلیف نیست و دنیایی که جای تکلیف است برای همیشه از دستشان رفته است.]'),
(3659, 34, 53, '[اینان] پیش از این [که در دنیا می زیستند] به حق کافر شدند، و ناآگاهانه و دور از معرفت و علم [درباره حق به عنوان اینکه شعر، جادو، افسانه خرافی و دورغ ساختگی است] سخن پراکنی می کردند.'),
(3660, 34, 54, 'و [در نتیجه] میان آنان و همه خواسته هایشان [به وسیله مرگ] جدایی انداخته شد، همان گونه که پیش از این با هم کیشانشان رفتار شد؛ زیرا آنان همواره [نسبت به حق] در تردیدی سخت بودند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'همه ستایش ها ویژه خداست که آفریننده آسمان ها و زمین است [و] فرشتگان را که دارای بالهایی دوگانه و سه گانه و چهارگانه اند رسولانی [برای انجام دادن فرمان های تکوینی و تشریعی اش] قرار داده است. هرچه بخواهد در آفرینش می افزاید؛ یقیناً خدا بر هر کاری تواناست.'),
(3662, 35, 2, 'چون خدا رحمتی را برای مردم بگشاید، بازدارنده ای برای آن نیست، و چون بازدارد، بعد از او فرستنده ای برایش وجود ندارد، و او توانای شکست ناپذیر و حکیماست.'),
(3663, 35, 3, 'ای مردم! نعمت خدا را بر خودتان یاد کنید. آیا جز خدا آفریننده ای هست که از آسمان و زمین شما را روزی دهد؟ هیچ معبودی جز او نیست، پس چگونه [از حق] منصرفتان می کنند؟'),
(3664, 35, 4, 'اگر تو را تکذیب می کنند [اندوهگین مباش] یقیناً پیش از تو هم پیامبرانی تکذیب شده اند. و همه امور به خدا بازگردانده می شود.'),
(3665, 35, 5, 'ای مردم! بی تردید وعده خدا [درباره قیامت] حق است، پس این زندگی دنیا [ی زودگذر،] شما را نفریبد و شیطان فریبنده، شما را [به کرم] خدا مغرور نکند.'),
(3666, 35, 6, 'بی تردید شیطان، دشمن شماست، پس او را دشمن خود بگیرید. [او] گروهش را فقط [به این سبب به فسق و فجور] دعوت می کند که اهل آتش سوزان گردند.'),
(3667, 35, 7, 'کسانی که کفر ورزیدند، عذاب سختی برای آنان خواهد بود، و کسانی که ایمان آورده و کارهای شایسته انجام داده اند، برای آنان آمرزش و پاداشی بزرگ است.'),
(3668, 35, 8, 'پس آیا کسی که کردار زشتش برای او زینت داده شده و [به این سبب] آن را خوب دیده [مانند کسی است که در پرتو ایمان، خوبو بد را تشخیص داده است؟] همانا خدا هر که را بخواهد [به کیفر تکبّرش در برابر حق] گمراه می کند و هر که را بخواهد هدایت می نماید. پس مبادا جانت به سبب حسرت هایی که بر آنان می خوری از بین برود؛ بی تردید خدا به آنچه انجام می دهند داناست.'),
(3669, 35, 9, 'خداست که بادها را فرستاد تا ابری را برانگیزند، پس ما آن را به سوی سرزمین مرده راندیم و زمین را پس از مردگی اش به وسیله آن زنده کردیم؛ زنده شدن مردگان هم این گونه است.'),
(3670, 35, 10, 'کسی که عزت می خواهد، پس [باید آن را از خدا بخواهد، زیرا] همه عزت ویژه خداست. حقایق پاک [چون عقاید و اندیشه های صحیح] به سوی او بالا می رود و عمل شایسته آن را بالا می برد. و کسانی که حیله های زشت به کار می گیرند، برای آنان عذابی سخت خواهد بود، و بی تردید حیله آنان نابود می شود.'),
(3671, 35, 11, 'و خدا شما را از خاک [مرده]، سپس از نطفه آفرید، آن گاه شما را زوج هایی [نر و ماده] قرار داد. هیچ ماده ای باردار نمی شود و بارش را نمی نهد مگر به علم او، و هیچ کس عمر طولانی نمی کند واز عمرش کاسته نمی شود مگر اینکه در کتابی [چون لوح محفوظ] ثبت است. بی تردید این [کارها] بر خدا آسان است.'),
(3672, 35, 12, 'دو دریا مساوی نیستند، این یکی شیرین و از بین برنده تشنگی و نوشیدنش گوارا و آن یکی شور و تلخ است؛ و از هر یک گوشتی تازه می خورید و زینتی که آن را می پوشید، استخراج می کنید. و کشتی ها را در آن می بینی که آب را می شکافند و می روند تا شما [با رسیدن به مقاصد و هدف هایتان] از فضل او بجویید و باشد که سپاس گزاری کنید.'),
(3673, 35, 13, 'شب را در روز فرو می برد و روز را در شب فرو می برد، و خورشید و ماه را مسخّر و رام کرده است که هر کدام تا سرآمدی معین روانند. این است خدا پروردگار شما، فرمانروایی، ویژه اوست و کسانی را که به جای او می پرستیدید، مالک پوست هسته خرمایی هم نیستند.'),
(3674, 35, 14, 'اگر آنها را بخوانید، خواندنتان را نمی شنوند و اگر [بر فرض محال] بشنوند، پاسختان را نمی دهند، و روز قیامت شرک شما را انکار می کنند؛ و هیچ کس مانند [خدای] آگاه تو را [از حقایق] خبردار نمی کند.'),
(3675, 35, 15, 'ای مردم! شمایید نیازمندان به خدا، و فقط خدا بی نیاز و ستوده است.'),
(3676, 35, 16, 'اگر بخواهید شما را از بین می برد و آفریده تازه ای می آورد.'),
(3677, 35, 17, 'و این کار بر خدا دشوار نیست،'),
(3678, 35, 18, 'و هیچ سنگین باری بار گناه دیگری را برنمی دارد، و اگر سنگین باری دعوت کند که از بارش بردارند، چیزی از بار گناهش برداشته نمی شود، گرچه [دعوت شونده] از نزدیکان و خویشان باشد. تو فقط کسانی را بیم می دهی که در نهان از پروردگارشان می ترسند و نماز را برپا می دارند؛ و هر کس [از آلودگی ها] پاک شود به سود خود پاک می شود، و بازگشت فقط به سوی خداست.'),
(3679, 35, 19, 'نابینا و بینا [کافر و مؤمن،] یکسان نیستند،'),
(3680, 35, 20, 'و نه تاریکی ها و نه روشنایی،'),
(3681, 35, 21, 'و نه سایه و نه باد گرم سوزان،'),
(3682, 35, 22, 'و زندگان و مردگان هم یکسان نیستند. بی تردید خدا [دعوت حق را] به هر کس بخواهد می شنواند؛ و تو نمی توانی [دعوت حق را] به کسانی که در قبرهایند بشنوانی.'),
(3683, 35, 23, 'تو فقط بیم دهنده ای،'),
(3684, 35, 24, 'یقیناً ما تو را در حالی که مژده دهنده و هشدار دهنده ای، به حق و راستی فرستادیم، و هیچ امتی نبوده مگر آنکه در میان آنان بیم دهنده ای گذاشته است.'),
(3685, 35, 25, 'اگر تو را تکذیب می کنند [اندوهگین مباش] همانا کسانی هم که پیش از اینان بودند [پیامبران را] تکذیب می کردند، در حالی که پیامبرانشان معجزه های آشکار، و نوشته ها [یی مشتمل بر پند و موعظه و مناجات] و کتاب روشن [که حاوی احکام و قوانین بود] برای آنان آوردند.'),
(3686, 35, 26, 'سپس کافران را [به سبب کفرشان به عذابی سخت] گرفتیم؛ پس کیفر من چگونه بود؟!'),
(3687, 35, 27, 'آیا ندانسته ای که خدا از آسمان آبی نازل کرده است، پس به وسیله آن میوه هایی که رنگ هایش گوناگون است، پدید آورده ایم، و در برخی از کوه ها راه هایی است به رنگ های مختلف: سپید و سرخ و سیاه پر رنگ،'),
(3688, 35, 28, 'و نیز از انسان ها و جنبدگان و چهارپایان [مانند میوه ها و راه های کوهستانی] رنگ های گوناگون وجود دارد. از بندگان خدا فقط دانشمندان از او می ترسند؛ یقیناً خدا توانای شکست ناپذیر و بسیار آمرزنده است.'),
(3689, 35, 29, 'قطعاً کسانی [مانند علی بن ابی طالب (علیه السلام)] که همواره کتاب خدا را می خوانند و نماز را برپا می دارند و از آنچه روزی آنان کرده ایم در نهان و آشکار انفاق می کنند، تجارتی را امید دارند که هرگز کساد و نابود نمی شود.'),
(3690, 35, 30, '[این همه را انجام می دهند] تا [خدا] پاداششان را کامل عطا کند و از فضلش بر آنان بیفزاید؛ یقیناً او بسیار آمرزنده و عطا کننده پاداش فراوان در برابر عمل اندک است.'),
(3691, 35, 31, 'و آنچه از کتاب به تو وحی کرده ایم، همان حق است، [و] تصدیق کننده [کتاب هایی که] پیش از آن بوده است؛ یقیناً خدا به بندگانش آگاه و بیناست.'),
(3692, 35, 32, 'سپس این کتاب را به کسانی از بندگانمان که برگزیدیم به میراث دادیم؛ پس برخی از آنان [در ترک عمل به کتاب] ستمکار بر خویش اند، و برخی از آنان میانه رو، و برخی از آنان به اذن خدا در کارهای خیر [بر دیگران] پیشی می گیرند، این است آن فضل بزرگ.'),
(3693, 35, 33, '[پاداششان] بهشت های جاودانه ای [است] که در آنها درآیند، در آنجا با دستبندهایی از طلا و مروارید آرایش می شوند، و لباسشان در آنجا حریر است.'),
(3694, 35, 34, 'و می گویند: همه ستایش ها ویژه خداست که اندوه را از ما برطرف کرد؛ بی تردید پروردگارمان بسیارآمرزنده و عطا کننده پاداش فراوان در برابر عمل اندک است.'),
(3695, 35, 35, 'همان که از فضلش ما را در این سرای جاودان جای داد، که در آن هیچ رنجی و هیچ سستی و افسردگی به ما نمی رسد.'),
(3696, 35, 36, 'و برای کافران آتش دوزخ است، نه فرمان مرگشان صادر می شود که بمیرند، و نه عذاب از آنان سبک می شود؛ این گونه هر کفران کننده ای را کیفر می دهیم.'),
(3697, 35, 37, 'و آنان در آنجا شیون و فریاد می زنند: پروردگارا! ما را بیرون بیاور تا کار شایسته انجام دهیم غیر آنچه انجام می دادیم. [می گویم:] آیا شما را چندان عمر ندادیم که هر کس می خواست در آن مقدار عمر متذکّر شود، متذکّر می شد؛ و [آیا] بیم دهنده ای به سوی شما نیامد؟ پس بچشید که برای ستمکاران هیچ یاوری نیست.'),
(3698, 35, 38, 'یقیناً خدا دانای غیب آسمان ها و زمین است و بی تردید به نیّات و اسرار سینه ها آگاه است.'),
(3699, 35, 39, 'او کسی است که شما را در زمین جانشینان [دیگران] قرار داد. پس کسی که کافر شود کفرش به زیان خود اوست، و کافران را کفرشان نزد پروردگارشان جز دشمنی و خشم نمی افزاید، و کافران را کفرشان جز خسارت اضافه نمی کند.'),
(3700, 35, 40, 'بگو: از [قدرت و تدبیر] معبودانتان که به جای خدا می پرستید، مرا خبر دهید، به من نشان دهید که چه چیزی از زمین را آفریده اند یا اینکه شرکتی [با خدا] در آفرینش آسمان ها دارند، یا به آنان کتابی داده ایم که از آن [بر حقّانیّت اعتقاد و ادعای خود] دلیل و برهانی دارند؟ [چنین نیست] بلکه ستمکاران یکدیگر را جز از روی فریب وعده نمی دهند.'),
(3701, 35, 41, 'یقیناً خدا آسمان ها و زمین را از اینکه از جای خود منحرف شوند [و فرو ریزند] نگه می دارد. و اگر منحرف شوند هیچ کس بعد از او نمی تواند نگاهشان دارد؛ مسلماً خدا همواره بردبار و بسیار آمرزنده است.'),
(3702, 35, 42, 'و [مشرکان] با سخت ترین سوگندهایشان به خدا سوگند خوردند که اگر بیم دهنده ای به سوی آنان آید هدایت یافته ترین ها خواهند بود، پس چون [پیامبری] بیم دهنده برای آنان آمد، آنان را [دعوت او] جز نفرت و دوری از هدایت نیفزود.'),
(3703, 35, 43, 'این نفرت به سبب گردن کشی و تکبّر آنان در زمین و نیرنگ زشتشان بود؛ و نیرنگ زشت جز اهلش را احاطه نمی کند. پس آیا جز سنت [جاری شده در] پیشینیان را [که هلاکتشان به سبب تکبّرشان بود] انتظار دارند؟ پس هرگز برای سنت خدا تبدیلی نمی یابی، و هرگز برای سنت خدا دگرگونی نخواهی یافت.'),
(3704, 35, 44, 'آیا در زمین گردش نکردند تا بنگرند سرانجام کسانی که پیش از اینان زندگی می کردند و از اینان نیرومندتر بودند چگونه بود؟ و هیچ چیز در آسمان ها و زمین نیست که بتواند خدا را عاجز کند [تا از دسترس قدرت او بیرون رود]؛ زیرا او همواره دانا و تواناست.'),
(3705, 35, 45, 'اگر خدا مردم را به کیفر گناهانشان مجازات می کرد، هیچ جنبنده ای را بر پشت زمین وا نمی گذاشت؛ ولی آنان را تا سرآمدی معین مهلت می دهد، پس هنگامی که اجلشان فرا رسد [آنان را در قیامت مجازات خواهد کرد]؛ بی تردید خدا [به احوال] بندگانش بیناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یس'),
(3707, 36, 2, 'سوگند به قرآن کریم،'),
(3708, 36, 3, 'که بی تردید تو از فرستادگانی،'),
(3709, 36, 4, 'بر راهی راست [قرار داری.]'),
(3710, 36, 5, '[قرآن] نازل شده توانای شکست ناپذیر و مهربان است.'),
(3711, 36, 6, 'تا مردمی را بیم دهی که پدرانشان را بیم نداده اند و به این علت [از حقایق] بی خبرند.'),
(3712, 36, 7, 'یقیناً فرمان عذاب بر بیشترشان محقق و ثابت شده است، به این سبب ایمان نمی آورند.'),
(3713, 36, 8, 'مسلماً ما غل هایی بر گردنشان نهاده ایم که تا چانه هایشان قرار دارد به طوری که سرهایشان بالا مانده است،'),
(3714, 36, 9, 'و از پیش رویشان حایلی و از پشت سرشان [نیز] حایلی قرار داده ایم، و به صورت فراگیر دیدگانشان را فرو پوشانده ایم، به این خاطر حقایق را نمی بینند،'),
(3715, 36, 10, 'و برای آنان یکسان است چه بیمشان دهی یا بیمشان ندهی، ایمان نمی آورند،'),
(3716, 36, 11, 'بیم دادنت فقط برای کسی ثمربخش است که از قرآن پیروی کند و در نهان از خدای رحمان بترسد، پس او را به آمرزش و پاداشی نیکو و باارزش مژده ده.'),
(3717, 36, 12, 'بی تردید ما مردگان را زنده می کنیم و آنچه را پیش فرستاده اند و [خوبی ها و بدی های] بر جا مانده از ایشان را ثبت می کنیم و همه چیز را در کتابی روشن [که اصل همه کتاب هاست و آن لوح محفوظ است] برشمرده ایم.'),
(3718, 36, 13, 'و برای آنان [که با تو برخورد خصمانه دارند] اهل آن شهر را مثل بزن، هنگامی که پیامبران به آنجا آمدند،'),
(3719, 36, 14, 'زمانی که دو پیامبر را به سوی آنان فرستادیم، پس آن دو را انکار کردند و با پیامبر سومی آنان را تقویت کردیم، پس [همگی] گفتند: ما را به سوی شما فرستاده اند.'),
(3720, 36, 15, '[اهل شهر] گفتند: شما جز بشرهایی مانند ما نیستید و [خدای] رحمان هیچ چیزی نازل نکرده است، شما فقط دروغ می گویید.'),
(3721, 36, 16, 'گفتند: پروردگارمان می داند که یقیناً ما به سوی شما فرستاده شده ایم،'),
(3722, 36, 17, 'و بر عهده ما جز رساندن آشکار [پیام وحی] نیست.'),
(3723, 36, 18, 'گفتند: همانا ما شما را به شومی و فال بد گرفته ایم، اگر [از دعوت خود] باز نایستید، قطعاً شما را سنگسار می کنیم، و از سوی ما شکنجه دردناکی به شما خواهد رسید.'),
(3724, 36, 19, 'گفتند: شومی شما از خود شماست [و این شومی نتیجه کفر و طغیان و گناه است، شگفتا!] آیا اگر به شما تذکر دهند [آن تذکر سعادت بخش را به شومی و فال بد می گیرید؟ نه، شومی از ما نیست] بلکه شما مردمی تجاوزکارید.'),
(3725, 36, 20, 'و از دورترین نقطه شهر مردی شتابان آمد، گفت: ای قوم من! از این فرستادگان [خدا] پیروی کنید،'),
(3726, 36, 21, 'از کسانی پیروی کنید که پاداشی از شما نمی خواهند و آنان راه یافته اند،'),
(3727, 36, 22, 'و مرا چیست که [از روی یقین] کسی را نپرستم که مرا آفریده و به سوی او بازگردانده می شوید؟'),
(3728, 36, 23, 'آیا به جای او معبودانی را برگزینم که اگر [خدای] رحمان برای من آسیب وگزندی بخواهد نه شفاعتشان چیزی را از من دفع می کند و نه می توانند نجاتم دهند؟!'),
(3729, 36, 24, 'اگر چنین کنم، بی تردید در گمراهی آشکار خواهم بود.'),
(3730, 36, 25, '[ای رسولان!] بی تردید به پروردگار شما ایمان آورده ام؛ بنابراین از من بشنوید.'),
(3731, 36, 26, '[سرانجام به دست آن مشرکان شهید شد، و به او] گفته شد: به بهشت درآی. گفت: ای کاش قوم من معرفت و آگاهی داشتند،'),
(3732, 36, 27, 'به اینکه پروردگارم مرا آمرزید و از کرامت یافتگان قرارم داد.'),
(3733, 36, 28, 'و بعد از شهادت او هیچ سپاهی از آسمان بر [هلاکت] قومش نازل نکردیم، و بر آن نبوده ایم که نازل کنیم.'),
(3734, 36, 29, '[کیفرشان] جز یک فریاد مرگبار نبود که ناگهان [پس از آن فریاد] همه بی حرکت و خاموش شدند!'),
(3735, 36, 30, 'ای دریغ و افسوس بر این بندگان که هیچ پیامبری برای هدایتشان نمی آمد مگر اینکه او را مسخره می کردند!'),
(3736, 36, 31, 'آیا [مشرکان مکه] ندانسته اند چه بسیار از اقوام پیش از آنان را [به سبب کفر و طغیانشان] هلاک کردیم که آنان هرگز نزد اینان برنمی گردند، [و در دنیا زندگی دوباره نمی یابند،]'),
(3737, 36, 32, 'و همه آنان [بدون استثنا گردآوری خواهند شد و] در قیامت نزد ما احضار می شوند.'),
(3738, 36, 33, 'و این زمین مرده برای آنان نشانه ای [آشکار بر اینکه ما مردگان را در قیامت زنده می کنیم] می باشد که آن را زنده کردیم و از آن دانه بیرون می آوریم که از آن می خورند،'),
(3739, 36, 34, 'و در آن بوستان هایی از درختان خرما و انگور قرار دادیم، و در آن از چشمه های گوناگون روان ساختیم،'),
(3740, 36, 35, 'تا از میوه آن و آنچه دست هایشان به عمل می آورد [مانند شیره، کشمش، شربت و...] بخورند، آیا سپاس گزاری نمی کنند؟'),
(3741, 36, 36, 'منزّه [از هر عیب و نقصی] است آنکه همه زوج ها را آفرید از آنچه زمین می رویاند و از وجود خودشان و از آنچه نمی دانند،'),
(3742, 36, 37, 'و نشانه ای [از نشانه های قدرت و حکمت ما] برای آنها شب است که [پوشش] روز را از آن برمی کنیم، پس ناگاه آنان به تاریکی درآیند،'),
(3743, 36, 38, 'و خورشید [نیز برای آنان نشانه ای از قدرت ماست] که همواره به سوی قرارگاهش حرکت می کند. این اندازه گیری توانای شکست ناپذیر و داناست،'),
(3744, 36, 39, 'و برای ماه منزل هایی قرار دادیم تا اینکه به صورت شاخه کهنه هلالی شکل و زرد رنگ خرما برگردد [و باز به تدریج بدر کامل شود،]'),
(3745, 36, 40, 'نه برای خورشید این توان هست که به ماه برسد، و نه شب از روز پیشی می گیرد، و هر کدام در مداری شناورند.'),
(3746, 36, 41, 'و برای آنان نشانه ای دیگر [از قدرت و رحمت ما] این [است] که فرزندانشان را در کشتی هایی پر [از اجناس و وسایل] حمل کردیم،'),
(3747, 36, 42, 'و برای آنان چیزهایی مانند کشتی [چون اسب، قاطر و دیگر وسایل نقلیه] آفریدیم که بر آن سوار می شوند،'),
(3748, 36, 43, 'و اگر بخواهیم آنان را غرق می کنیم در این صورت هیچ فریادرسی برای آنان نخواهد بود، و نجات هم نیابند،'),
(3749, 36, 44, 'مگر [اینکه] رحمتی از سوی ما [نجاتشان دهد] و تا مدتی [از زندگی دنیا] بهره مندشان کنیم،'),
(3750, 36, 45, 'و هنگامی که به آنان گویند: از آنچه [عذاب های دنیا و آخرت] پیش رو و پشت سر شماست بپرهیزید تا مورد رحمت قرار گیرید [به این هشدار قطعی و یقینی توجه نمی کنند،]'),
(3751, 36, 46, 'و هیچ آیه ای از آیات پروردگارشان برای آنان نمی آید مگر اینکه از آن روی می گردانند.'),
(3752, 36, 47, 'و هنگامی که به آنان گویند: از آنچه خدا روزی شما کرده، انفاق کنید، کافران به مؤمنان گویند: آیا کسانی را اطعام کنیم که اگر خدا می خواست آنان را اطعام می کرد؟ [پس گرسنگی را خدا بر آنان خواسته است] ولی شما [ای کفرپیشگان!] جز در گمراهی آشکاری نیستند.'),
(3753, 36, 48, 'و می گویند: اگر راستگویید، این وعده [قیامت و عذاب] کی خواهد بود؟'),
(3754, 36, 49, 'اینان جز یک فریاد مرگبار [آسمانی] را انتظار نمی کشند که آنان را در حالی که سرگرم مجادله و ستیز [در امور دنیایی] اند فراگیرد.'),
(3755, 36, 50, '[فریادی که وقتی بر سر آنان زده شود] نه می توانند وصیتی کنند و نه [اگر بیرون خانه باشند] می توانند به خانواده خود برگردند،'),
(3756, 36, 51, 'و در صور دمیده شود، ناگاه همه آنان از قبرها به سوی پروردگارشان می شتابند.'),
(3757, 36, 52, 'می گویند: ای وای بر ما، چه کسی ما را از خواب گاهمان برانگیخت؟ این واقعیتی است که [خدای] رحمان وعده داده بود و پیامبران راست گفته بودند!!'),
(3758, 36, 53, '[آن صحنه عظیم] جز یک فریاد نیست، پس به ناگاه همه آنان [گردآوری شده و] نزد ما احضار می شوند.'),
(3759, 36, 54, 'در این روز به هیچ کس ذره ای ستم نمی شود، و جز آنچه را انجام می دادید پاداش داده نمی شوید.'),
(3760, 36, 55, 'همانا بهشتیان در چنین روزی در سرگرمی وصف ناپذیری شیرین کام و خوش اند.'),
(3761, 36, 56, 'آنان و همسرانشان در زیر سایه هایی [آرام بخش] بر تخت هایی [آراسته چون حجله عروس] تکیه می زنند.'),
(3762, 36, 57, 'برای آنان در آنجا میوه ها [ی عالی و مطبوع] و آنچه دلشان بخواهد فراهم است.'),
(3763, 36, 58, 'با سلام [ی پرارزش و سلامت بخش] که گفتاری از پروردگاری مهربان است.'),
(3764, 36, 59, 'و [ندا آید:] ای گناهکاران! امروز [از صف نیکان] جدا شوید.'),
(3765, 36, 60, 'ای فرزندان آدم! آیا به شما سفارش نکردم که شیطان را مپرستید که او بی تردید دشمن آشکاری برای شماست؟'),
(3766, 36, 61, 'و اینکه مرا بپرستید که این راهی است مستقیم،'),
(3767, 36, 62, 'و همانا شیطان گروه بسیاری از شما را گمراه کرد، آیا تعقّل نمی کردید [که پیروانش به چه سرنوشت شومی دچار شدند؟]'),
(3768, 36, 63, 'این است دوزخی که به شما وعده می دادند.'),
(3769, 36, 64, 'امروز به کیفر کفری که همواره می ورزیدید، در آن درآیید.'),
(3770, 36, 65, 'امروز بر دهان هایشان مُهر خاموشی نهیم و دست هایشان با ما سخن می گویند و پاهایشان به اعمالی که همواره مرتکب می شدند، گواهی می دهند!'),
(3771, 36, 66, 'و اگر بخواهیم [در همین دنیا] دیدگانشان را محو می کنیم، پس به [سوی همان] راه [گمراهی] بر یکدیگر پیشی می گیرند؛ نهایتاً چگونه و کجا می توانند [صراط مستقیم را] ببینند؟'),
(3772, 36, 67, 'و [نیز] اگر بخواهیم آنان را در جای خودشان مسخ می کنیم، [و به صورت جمادی خشک درمی آوریم] که نه بتوانند بروند و نه بازگردند،'),
(3773, 36, 68, 'و به هر کس عمر طولانی دهیم، او را در عرصه آفرینش [به سوی حالت ضعف، سستی، نقصان و فراموشی] واژگونش می کنیم؛ پس آیا تعقّل نمی کنند؟'),
(3774, 36, 69, 'و به پیامبر، شعر نیاموختیم و [شعرگویی] شایسته او نیست. کتاب [او] جز مایه یادآوری و قرآنی روشنگر [حقایق] نیست،'),
(3775, 36, 70, 'تا کسانی را که [به عقل و هوش و استعداد] زنده اند هشدار دهد، و فرمان عذاب بر کافران محقق و ثابت شود.'),
(3776, 36, 71, 'آیا ندیده اند که ما از آنچه به قدرت خود انجام داده ایم برای آنان چهارپایانی آفریده ایم که آنان مالکشان هستند.'),
(3777, 36, 72, 'و چهارپایان را برای آنان رام کردیم که برخی از آنها مرکب سواری آنان هستند و از [گوشت] برخی از آنها می خورند،'),
(3778, 36, 73, 'و برای آنان در آن چهارپایان سودهایی [چون پشم، کُرک و پوست] و نوشیدنی هایی [چون شیر و فرآورده های آن] هست؛ آیا سپاس گزاری نمی کنند؟'),
(3779, 36, 74, 'و به جای خدا معبودانی انتخاب کرده اند به امید اینکه [به وسیله آنها] یاری شوند،'),
(3780, 36, 75, 'در حالی که [معبودان،] قدرت یاری دادن به آنان را ندارند و آنان [که سبک مغزان روی برتافته از حق اند] برای آن معبودان [از سوی شیطان] سپاهی احضار شده اند [تا عمر و مالشان را فدای این معبودان باطل کنند.]'),
(3781, 36, 76, 'پس گفتارشان تو را اندوهگین نکند، بی تردید ما آنچه را پنهان می دارند و آنچه را آشکار می کنند، می دانیم.'),
(3782, 36, 77, 'آیا انسان ندانسته که ما او را از نطفه ای [پست و ناچیز] آفریده ایم و اینک ستیزه گری آشکار است؟'),
(3783, 36, 78, 'در حالی که آفرینش نخستین خود را از یاد برده برای ما مثلی زد [و] گفت: چه کسی این استخوان ها را در حالی که پوسیده اند، زنده می کند؟'),
(3784, 36, 79, 'بگو: همان کسی که نخستین بار آن را پدید آورد. زنده اش می کند، و او به هر چیزی داناست'),
(3785, 36, 80, 'همان کسی که برای شما از درخت سبز، «آتش زنه» آفرید، که اکنون شما از آن آتش می افروزید.'),
(3786, 36, 81, 'آیا کسی که آسمان ها و زمین را آفریده است، قدرت ندارد که [پس از مرگشان] همانند آنان را بیافریند؟ چرا قدرت دارد؛ زیرا اوست که آفریننده بسیار داناست.'),
(3787, 36, 82, 'شأن او این است که چون پدید آمدن چیزی را اراده کند، فقط به آن می گوید: باش، پس بی درنگ موجود می شود.'),
(3788, 36, 83, 'بنابراین [از هر عیب و نقصی] منزّه است خدایی که مالکیّت و فرمانروایی همه چیز به دست اوست، و به سوی او بازگردانده می شوید.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به صف بستگان [مانند فرشتگان، نمازگزاران و جهادگران] که صفی [منظم و استوار] بسته اند،'),
(3790, 37, 2, 'و به بازدارندگان که [انسان را از گناهان] به شدت باز می دارند،'),
(3791, 37, 3, 'و به تلاوت کنندگان وحی،'),
(3792, 37, 4, 'که بی تردید معبود شما یگانه است،'),
(3793, 37, 5, 'پروردگار آسمان ها وزمین وآنچه میان آنهاست، و پروردگار مشرق ها،'),
(3794, 37, 6, 'همانا ما آسمان دنیا را به زیور ستارگان آراستیم،'),
(3795, 37, 7, 'و آن را از هر شیطان سرکشی حفظ کردیم،'),
(3796, 37, 8, 'آنان نمی توانند به سخنان فرشتگان بسیار مکرم و شریف گوش فرا دهند، و [هرگاه به گوش دادن برخیزند] از هر سو [شهاب] به سویشان پرتاب می شود،'),
(3797, 37, 9, 'تا با خفت وخواری رانده شوند، وبرای آنان عذابی همیشگی است،'),
(3798, 37, 10, 'مگر آنکه شیطانی خبری را [دزدانه و] با سرعت برباید [و فرار کند] که در این صورت گلوله ای آتشین و شکافنده او را دنبال می کند.'),
(3799, 37, 11, 'پس از منکران معاد بپرس: آیا آفرینش آنان سخت تر و دشوارتر است یا آنچه [مانند فرشتگان، آسمان ها، زمین، کوه ها و...] آفریده ایم؟ [به یقین آفرینش آنان کاری ناچیز است چون] ما آنان را از گِلی چسبنده آفریده ایم،'),
(3800, 37, 12, 'بلکه [تو از انکارشان] تعجب می کنی و آنان مسخره می کنند،'),
(3801, 37, 13, 'و هنگامی که به آنان تذکر، می دهند متذکّر نمی شوند؛'),
(3802, 37, 14, 'و چون معجزه ای را می بینند به شدت مسخره می کنند [و دیگران را هم به مسخره وامی دارند،]'),
(3803, 37, 15, 'و می گویند: این جز جادویی آشکار نیست.'),
(3804, 37, 16, '[می گویند:] آیا زمانی که ما مُردیم و خاک و استخوان شدیم، حتماً برانگیخته می شویم؟'),
(3805, 37, 17, 'و آیا پدران پیشین ما [هم برانگیخته می شوند؟]'),
(3806, 37, 18, 'بگو: آری، [برانگیخته می شوید] در حالی که خوار و ناچیز هستید.'),
(3807, 37, 19, 'جز این نیست که آن یک فریاد عظیم است که [وقتی واقع شود] ناگاه [همه زنده می شوند و حیرت زده منظره قیامت را] می نگرند،'),
(3808, 37, 20, 'و می گویند: ای وای بر ما! این روز جزاست!'),
(3809, 37, 21, '[آری] این همان روز جدایی [میان حق و باطل] است که همواره آن را انکار می کردید.'),
(3810, 37, 22, '[آن گاه ندا رسد:] ستمکاران و هم ردیفان آنان و معبودهایی را که همواره به جای خدا می پرستیدند، گرد آورید.'),
(3811, 37, 23, 'پس همه را به سوی راه دوزخ راهنمایی کنید.'),
(3812, 37, 24, 'آنان را نگه دارید که حتماً مورد بازپرسی قرار می گیرند.'),
(3813, 37, 25, '[به آنان گویند:] شما را چه شده که یکدیگر را [برای رهایی از عذاب] یاری نمی دهید؟'),
(3814, 37, 26, '[نه اینکه نمی توانند یکدیگر را یاری دهند] بلکه آنان امروز فروتنانه تسلیم [قدرت حق] هستند،'),
(3815, 37, 27, 'به یکدیگر رو کرده از هم می پرسند: [این چه وضعی است؟]'),
(3816, 37, 28, '[پس پیروان گمراه به پیشوایان گمراه کننده] می گویند: همانا شما از راه خیرخواهی به سوی ما می آمدید، [ولی کارتان جز فریب و نیرنگ نبود!]'),
(3817, 37, 29, 'می گویند: [ما را تقصیری نیست] بلکه خود شما اهل ایمان نبودید،'),
(3818, 37, 30, 'و ما را بر شما هیچ تسلطی نبود، بلکه شما مردمی طغیان گر و سرکش بودید.'),
(3819, 37, 31, 'اکنون فرمان عذاب پروردگارمان بر ما محقق و ثابت شد که همه ما چشندگان عذاب خواهیم بود.'),
(3820, 37, 32, '[سبک مغزی و تعصّب شما زمینه گمراهی شما شد] پس ما شما را گمراه کردیم؛ زیرا خودمان گمراه بودیم،'),
(3821, 37, 33, 'پس بی تردید همه 2آنان در آن روز در عذاب شریک اند،'),
(3822, 37, 34, 'ما با مجرمان این گونه رفتار می کنیم؛'),
(3823, 37, 35, 'زیرا آنان چنین بودند که هر زمان به آنان می گفتند: معبودی جز خدا نیست، تکبّر می کردند،'),
(3824, 37, 36, 'و [همواره] می گفتند: آیا باید به خاطر شاعری دیوانه معبودان خود را رها کنیم؟!'),
(3825, 37, 37, '[چنین نیست که می پندارید] بلکه او حق را آورده و پیامبران را تصدیق کرده است.'),
(3826, 37, 38, 'بی تردید شما [کوردلان] عذاب دردناک را خواهید چشید،'),
(3827, 37, 39, 'و جز آنچه که همواره انجام می دادید، پاداش داده نمی شوید،'),
(3828, 37, 40, 'جز بندگان خالص شده خدا [که از هر کیفری در امانند،]'),
(3829, 37, 41, 'برای آنان رزق و روزی معین و ویژه ای است،'),
(3830, 37, 42, 'میوه هایی [گوناگون] در حالی که مورد اکرام خواهند بود،'),
(3831, 37, 43, 'در بهشت های پر نعمت،'),
(3832, 37, 44, 'در حالی که بر تخت هایی روبه روی یکدیگر [تکیه زده اند،]'),
(3833, 37, 45, 'جامی از نوشیدنی زلال و پاک گرداگردشان می گردانند،'),
(3834, 37, 46, '[نوشیدنی] سپید [و درخشنده] و لذت بخش برای نوشندگان،'),
(3835, 37, 47, 'نه در آن مایه فساد [جسم و عقل] است، و نه از آن مست و بیهوش می شوند،'),
(3836, 37, 48, 'و در کنارشان زنانی هستند که فقط به شوهرانشان عشق می ورزند.'),
(3837, 37, 49, 'گویا آنان [از سپیدی] تخم شترمرغی هستند که [زیر پر و بال] پوشیده شده اند [و هرگز دست کسی به آنان نرسیده است.]'),
(3838, 37, 50, 'پس برخی از آنان به برخی دیگر رو کرده از [حال] یکدیگر می پرسند.'),
(3839, 37, 51, 'گوینده ای از آنان می گوید: همانا من [در دنیا] هم نشینی داشتم.'),
(3840, 37, 52, '[که همواره از روی تعجب به من] می گفت: آیا تو از باور دارندگان [رستاخیز و زنده شدن مردگان] هستی؟'),
(3841, 37, 53, 'آیا زمانی که ما مردیم و خاک استخوان شدیم، حتماً [زنده می شویم و] پاداشمان می دهند؟'),
(3842, 37, 54, '[سپس به دوستان بهشتی خود] می گوید: آیا شما با من به دوزخ سر می کشید [تا از هم نشینم خبری بگیرید که در کجا و در چه حالی است؟]'),
(3843, 37, 55, 'پس خود او به دوزخ سر می کشد و هم نشینش را وسط دوزخ می بیند.'),
(3844, 37, 56, '[به او] می گوید: به خدا سوگند نزدیک بود، مرا به هلاکت بیندازی.'),
(3845, 37, 57, 'و اگر توفیق و رحمت پروردگارم نبود، حتماً از احضارشدگان [در دوزخ] بودم.'),
(3846, 37, 58, '[آن گاه به دوستان بهشتی خود می گوید:] آیا ما [برای همیشه در بهشتیم و] هرگز نمی میریم؟'),
(3847, 37, 59, '[و] جز همان مرگ نخستین [که در دنیا سراغمان آمد مرگی دیگر به سراغمان نخواهد آمد] و ما هرگز عذاب نخواهیم شد؟ [شگفتا! چه لطف خاصی از سوی خدا به ما شده است!]'),
(3848, 37, 60, 'بی تردید این همان کامیابی بزرگ است.'),
(3849, 37, 61, 'پس باید برای چنین پاداشی عمل کنندگان عمل کنند.'),
(3850, 37, 62, 'آیا این [بهشت جاودان پر نعمت] برای پذیرایی بهتر است یا درخت زقّوم؟'),
(3851, 37, 63, 'ما آن را برای ستمکاران مایه شکنجه و عذاب قرار داده ایم.'),
(3852, 37, 64, 'آن درختی است که در قعر دوزخ می روید،'),
(3853, 37, 65, 'شکوفه هایش مانند سرهای شیاطین [بسیار بدنما و زشت] است.'),
(3854, 37, 66, 'پس این منکران لجوج حتماً از آن می خورند و شکم ها را از آن پر می کنند.'),
(3855, 37, 67, 'آن گاه به ناچار روی آن [به عنوان نوشیدنی] مخلوطی از آب بسیار داغ و متعفّن برای آنان خواهد بود!'),
(3856, 37, 68, 'سپس بازگشتشان حتماً به سوی دوزخ است.'),
(3857, 37, 69, '[سزاوار بودنشان در قیامت به این همه عذاب به سبب این است که] آنان پدرانشان را گمراه یافتند،'),
(3858, 37, 70, 'و [با اینکه می دانستند گمراهند بدون اندیشه و تأمل] عجولانه از پی آنان می رفتند!'),
(3859, 37, 71, 'و به راستی پیش از اینان بیشتر پیشینیان گمراه شدند،'),
(3860, 37, 72, 'و بی تردید ما در میان آنان بیم دهندگانی فرستادیم.'),
(3861, 37, 73, 'پس با تأمل بنگر سرانجام بیم داده شدگان چگونه بود؟'),
(3862, 37, 74, '[همه هلاک شدند] جز بندگان خالص شده خدا،'),
(3863, 37, 75, 'و نوح ما را ندا کرد [و ما ندایش را اجابت کردیم] پس ما به راستی نیکو اجابت کننده ای هستیم،'),
(3864, 37, 76, 'و او و خاندانش را از آن اندوه بزرگ نجادت دادیم.'),
(3865, 37, 77, 'و تنها ذریه او را [در زمین] باقی گذاشتیم،'),
(3866, 37, 78, 'و در میان آیندگان برای او نام نیک به جا گذاشتیم؛'),
(3867, 37, 79, 'سلام بر نوح در میان جهانیان.'),
(3868, 37, 80, 'به راستی ما نیکوکاران را این گونه پاداش می دهیم.'),
(3869, 37, 81, 'بی تردید او از بندگان مؤمن ما بود.'),
(3870, 37, 82, 'سپس دیگران را غرق کردیم؛'),
(3871, 37, 83, 'و به راستی ابراهیم از پیروان نوح بود،'),
(3872, 37, 84, 'هنگامی که با دلی پاک به سوی پروردگارش آمد.'),
(3873, 37, 85, '[یاد کن] هنگامی را که به پدر و قومش گفت: چیست آنچه می پرستید؟'),
(3874, 37, 86, 'آیا به جای خدا معبودان دروغین را می خواهید.'),
(3875, 37, 87, 'پس گمانتان به پروردگار جهانیان چیست؟ [که غیر او را می پرستید،]'),
(3876, 37, 88, '[چون از او دعوت کردند که شبانه به مراسم عیدشان برود] نگاهی به ستارگان انداخت،'),
(3877, 37, 89, 'و گفت: به راستی من بیمارم.'),
(3878, 37, 90, 'پس پشت کنان از او روی گرداندند.'),
(3879, 37, 91, 'او هم مخفیانه به سوی بت هایشان رفت و [از روی ریشخند] گفت: آیا غذا نمی خورید؟'),
(3880, 37, 92, 'شما را چه شده که سخن نمی گویید؟'),
(3881, 37, 93, 'پس [به آنها روی آورد و] با دست راست ضربه ای کاری بر آنها کوبید [و خردشان کرد.]'),
(3882, 37, 94, 'مردم با شتاب به سوی او آمدند.'),
(3883, 37, 95, '[به آنان] گفت: آیا آنچه را [با دست خود] می تراشید، می پرستید؟!'),
(3884, 37, 96, 'در حالی که خدا شما را و آنچه را می سازید، آفریده است.'),
(3885, 37, 97, 'گفتند: برای او بنایی بسازید [که گنجایش آتش فراوانی داشته باشد] پس او را در آتش شعلهور بیندازید.'),
(3886, 37, 98, 'پس خواستند به او نیرنگی زنند، ولی ما آنان را پست و شکست خورده کردیم.'),
(3887, 37, 99, 'و [وقتی از این مهلکه جان سالم به در برد] گفت: به راستی من به سوی پروردگارم می روم، و [او] به زودی مرا راهنمایی خواهد کرد.'),
(3888, 37, 100, 'پروردگارا! مرا فرزندی که از صالحان باشد عطا کن.'),
(3889, 37, 101, 'پس ما او را به پسری بردبار مژده دادیم.'),
(3890, 37, 102, 'هنگامی که با او به [مقام] سعی رسید، گفت: پسرکم! همانا من در خواب می بینم که تو را ذبح می کنم، پس با تأمل بنگر رأی تو چیست؟ گفت: پدرم آنچه به آن مأمور شده ای انجام ده اگر خدا بخواهد مرا از شکیبایان خواهی یافت.'),
(3891, 37, 103, 'پس هنگامی که آن دو تسلیم [خواسته خدا] شدند و ابراهیم، جبین او را به زمین نهاد [تا ذبحش کند]'),
(3892, 37, 104, 'و او را ندا دادیم که ای ابراهیم!'),
(3893, 37, 105, 'خوابت را تحقق دادی [و فرمان پروردگارت را اجرا کردی]، به راستی ما نیکوکاران را این گونه پاداش می دهیم [که نیّت پاک و خالصشان را به جای عمل می پذیریم.]'),
(3894, 37, 106, 'به یقین این همان آزمایش روشن بود.'),
(3895, 37, 107, 'و ما اسماعیل را در برابر قربانی بزرگی [از ذبح شدن] رهانیدیم،'),
(3896, 37, 108, 'و در میان آیندگان برای او [نام نیک] به جا گذاشتیم.'),
(3897, 37, 109, 'سلام بر ابراهیم.'),
(3898, 37, 110, '[ما] نیکوکاران را این گونه پاداش می دهیم.'),
(3899, 37, 111, 'بی تردید او از بندگان مؤمن ما بود،'),
(3900, 37, 112, 'و ما او را به اسحاق که پیامبری از شایستگان بود مژده دادیم،'),
(3901, 37, 113, 'و بر او و بر اسحاق برکت دادیم، و از دودمان آن دو برخی نیکوکارند و برخی آشکارا ستمکار بر خویشند،'),
(3902, 37, 114, 'و به راستی ما به موسی و هارون نعمت دادیم،'),
(3903, 37, 115, 'و آن دو نفر و قومشان را از اندوه بزرگ نجات بخشیدیم،'),
(3904, 37, 116, 'و آنان را یاری دادیم در نتیجه پیروز شدند،'),
(3905, 37, 117, 'و هر دو را کتاب بسیار روشنگر عطا کردیم،'),
(3906, 37, 118, 'وبه راه راست هدایتشان نمودیم،'),
(3907, 37, 119, 'و در میان آیندگان برای هر دو نفر نام نیک به جا گذاشتیم.'),
(3908, 37, 120, 'سلام بر موسی و هارون.'),
(3909, 37, 121, 'ما نیکوکاران را این گونه پاداش می دهیم.'),
(3910, 37, 122, 'بی تردید هر دو از بندگان مؤمن ما بودند،'),
(3911, 37, 123, 'و بی تردید الیاس از پیامبران بود.'),
(3912, 37, 124, '[یاد کن] هنگامی را که به قومش گفت: آیا [از شرک وطغیان] نمی پرهیزید؟'),
(3913, 37, 125, 'آیا بت «بعل» را می پرستید و بهترین آفرینندگان را رها می کنید؟!'),
(3914, 37, 126, 'خدا را که پروردگار شما و پروردگار پدران پیشین شماست.'),
(3915, 37, 127, 'پس او را انکار کردند، یقیناً آنان از احضار شدگان [در عذاب] خواهند بود،'),
(3916, 37, 128, 'جز بندگان خالص شده خدا [که از هر کیفری در امانند،]'),
(3917, 37, 129, 'و در میان آیندگان برای او نام نیک به جا گذاشتیم.'),
(3918, 37, 130, 'سلام بر آل یاسین.'),
(3919, 37, 131, 'ما نیکوکاران را این گونه پاداش می دهیم.'),
(3920, 37, 132, 'بی تردید او از بندگان مؤمن ما بود.'),
(3921, 37, 133, 'و بی تردید لوط از پیامبران بود.'),
(3922, 37, 134, '[یاد کن] هنگامی را که او و همه اهلش را نجات دادیم،'),
(3923, 37, 135, 'مگر پیرزنی را که در میان باقی ماندگان [در شهر] بود.'),
(3924, 37, 136, 'سپس دیگران را هلاک کردیم،'),
(3925, 37, 137, 'و شما همواره صبحگاهان [در مسیر سفرهایتان از کنار ویرانه های شهر] آنان گذر می کنید،'),
(3926, 37, 138, 'و نیز شبانگاهان، آیا تعقّل نمی کنید؟'),
(3927, 37, 139, 'و یونس از پیامبران بود.'),
(3928, 37, 140, '[یاد کن] هنگامی را که به سوی آن کشتی پر [از جمعیت و بار] گریخت،'),
(3929, 37, 141, 'و با سرنشینان کشتی قرعه انداخت [و قرعه به نامش افتاد] و از مغلوب شدگان شد [و او را به دریا انداختند.]'),
(3930, 37, 142, 'پس آن ماهی بزرگ او را بلعید، در حالی که سزاوار سرزنش بود.'),
(3931, 37, 143, '[و در شکم ماهی به تسبیح خدا مشغول شد که] اگر او از تسبیح کنندگان نبود،'),
(3932, 37, 144, 'بی تردید تا روزی که مردم برانگیخته می شوند در شکم ماهی می ماند.'),
(3933, 37, 145, 'پس او را در حالی که بیمار بود به زمینی خشک و بی گیاه افکندیم.'),
(3934, 37, 146, 'و بر او گیاهی از نوعی کدو رویاندیم،'),
(3935, 37, 147, 'و او را به سوی [قومی] یکصد هزار نفر [ی] یا بیشتر فرستادیم.'),
(3936, 37, 148, 'پس ایمان آوردند در نتیجه آنان را تا پایان عمرشان [از نعمت ها و مواهب خود] بهره مند کردیم.'),
(3937, 37, 149, '[مشرکان سبک مغز می گویند: فرشتگان، دختران خدایند] پس از آنان بپرس که آیا دختران برای پروردگار تو هستند و پسران برای ایشان؟!'),
(3938, 37, 150, 'یا اینکه ما فرشتگان را دختر آفریدیم و آنان شاهد بودند؟!'),
(3939, 37, 151, 'آگاه باش! که آنان از بافته های دروغ خود می گویند'),
(3940, 37, 152, 'که خدا فرزند آورده! و بی تردید آنان دروغگویند.'),
(3941, 37, 153, 'آیا دختران را بر پسران ترجیح داده است؟'),
(3942, 37, 154, 'شما را چه شده، چگونه حکم می کنید؟'),
(3943, 37, 155, 'پس آیا متذکّر [حقایق] نمی شوید؟'),
(3944, 37, 156, 'یا شما [بر این ادعای خود] دلیل روشنی دارید؟'),
(3945, 37, 157, 'پس اگر راستگویید، کتابتان را [که این سخنان را با تکیه بر آن می گویید، به میان] آورید.'),
(3946, 37, 158, 'میان خدا و جن، نسب و خویشی قرار دادند، در صورتی که جنیان به خوبی می دانند که [روز قیامت برای حساب و پاداش] احضار خواهند شد.'),
(3947, 37, 159, 'خدا از آنچه او را به آن توصیف می کنند، منزّه است.'),
(3948, 37, 160, 'مگر بندگان خالص شده خدا [که او را به آنچه توصیف می کنند شایسته مقام قدس اوست.]'),
(3949, 37, 161, 'و بی تردید شما و آنچه را می پرستید،'),
(3950, 37, 162, 'نمی توانید [مردم را] بر ضد خدا گمراه کنید.'),
(3951, 37, 163, 'مگر کسانی را که [به اختیار خود به خاطر پذیرفتن وسوسه و اغواگری شما] به دوزخ درآیند،'),
(3952, 37, 164, 'و هیچ یک از ما فرشتگان نیست مگر اینکه برای او مقامی معین است.'),
(3953, 37, 165, 'و همانا ما [برای اجرای فرمان خدا] صف بستگانیم.'),
(3954, 37, 166, 'و ما خود تسبیح کنندگانیم.'),
(3955, 37, 167, 'و مشرکان [پیش از بعثت پیامبر] قاطعانه می گفتند:'),
(3956, 37, 168, 'اگر نزد ما کتابی چون کتاب های آسمانی پیامبران پیشین بود،'),
(3957, 37, 169, 'بی تردید از بندگان خالص شده خدا می شدیم.'),
(3958, 37, 170, 'ولی [هنگامی که قرآن را برای هدایت آنان نازل کردیم] به آن کافر شدند و به زودی [و زر و وبال کفرشان را] خواهند دانست.'),
(3959, 37, 171, 'و وعده قطعی ما درباره بندگان به رسالت فرستاده شده، از پیش محقق و ثابت گشته است،'),
(3960, 37, 172, 'که بی تردید آنان [در همه زمینه ها] یاری شدگانند،'),
(3961, 37, 173, 'و مسلماً سپاه ما پیروزند.'),
(3962, 37, 174, 'پس تا مدتی از آنان روی بگردان.'),
(3963, 37, 175, 'و آنان را بنگر که به زودی [و زر و وبال گناهانشان را] خواهند دید.'),
(3964, 37, 176, 'آیا شتاب در آمدن عذاب ما را می خواهند؟!'),
(3965, 37, 177, 'پس هنگامی که [عذاب ما] به آستانه خانه هایشان نازل شود، بیم شدگان روزگار بدی خواهند داشت،'),
(3966, 37, 178, 'و تا مدتی از آنان روی بگردان،'),
(3967, 37, 179, 'و [آنان را] بنگر که به زودی [و زر و وبال گناهانشان را] خواهند دید.'),
(3968, 37, 180, 'پروردگارت که دارای عزت است از آنچه او را به آن توصیف می کنند، منزّه است.'),
(3969, 37, 181, 'و سلام بر پیامبران'),
(3970, 37, 182, 'و همه ستایش ها ویژه خداست که پروردگار جهانیان است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص، سوگند به قرآن که مشتمل بر ذکر [حقایق، معارف، مطالب اخلاقی و اجتماعی و احکام حلال و حرام] است.'),
(3972, 38, 2, '[اینکه قرآن را نمی پذیرند، برای این نیست که زمینه ای برای پذیرش آن وجود ندارد] بلکه کافران [غرق] در تکبّر [و سرکشی و عداوت] و دشمنی اند.'),
(3973, 38, 3, 'چه بسیار اقوامی که پیش از آنان هلاک کردیم که [وقت نزول عذاب] فریاد کمک خواهی سر دادند، در حالی که وقت گریز و یافتن پناه گاه نبود؛'),
(3974, 38, 4, 'و از اینکه بیم دهنده ای از جنس خودشان به سوی آنان آمده است تعجب کردند، و کافران گفتند: این جادوگری بسیار دروغگوست.'),
(3975, 38, 5, 'آیا [محمّد] معبودان [گوناگون] را معبودی یگانه قرار داده است؟ به راستی [که منحصر کردن معبودان در یک معبود] چیزی بسیار شگفت است.'),
(3976, 38, 6, 'سران و اشرافشان روان شدند [و فریاد برداشتند] که بروید و بر پرستش و نگهداری معبودانتان ایستادگی کنید زیرا از این دعوت [به سوی خدای یگانه] ریاست و آقایی بر اراده شده است.'),
(3977, 38, 7, 'ما این [پرستش معبود یگانه] را در آخرین آیین [که پدرانمان بر آن بودند] نشنیده ایم؛ این جز دروغی ساخته شده نیست.'),
(3978, 38, 8, 'آیا از میان ما [که دارای ثروت فراوان و مقام و قدرتیم] قرآن، فقط بر او [که فقیری یتیم بیش نیست] نازل شده است؟! [نه، قرآن دروغی ساخته شده نیست] بلکه اینان درباره قرآن من در شک هستند، بلکه هنوز عذاب را نچشیده اند [تا از بیماری شک درآیند و به حقّانیّت آن اقرار کنند و زبان از گستاخی و جسارت ببندند.]'),
(3979, 38, 9, 'مگر خزانه های رحمت پروردگار توانای شکست ناپذیر و بخشنده ات نزد آنان است [که منصب نبوّت را به هر کس که دلشان خواست ببخشند؟]'),
(3980, 38, 10, 'یا مگر مالکیّت و فرمانروایی آسمان ها و زمین و آنچه میان آنهاست، در اختیار آنان است؟ [اگر چنین است] پس [بیایند] از نردبان ها [ی رساننده به این مالکیّت و فرمانروایی] بالا روند [و امور را به دست گیرند و مانع نزول وحی بر محمّد شوند و خود به هر کس که بخواهند وحی کنند.]'),
(3981, 38, 11, 'اینان لشکری ناچیز و اندک [از احزاب کفر و شرک] اند که در آنجا [که میدان جنگ بدر است] شکست خوردنی هستند.'),
(3982, 38, 12, 'پیش از اینان نیز قوم نوح و عاد و فرعون که از تمام ابزار حاکمیت برخوردار بودند [پیامبران را] انکار کردند؛'),
(3983, 38, 13, 'و نیز قوم ثمود و قوم لوط و اصحاب ایکه همان احزاب کفر و شرک اند،'),
(3984, 38, 14, 'که هر یک از اینان پیامبران را انکار کردند، پس عقوبت [من بر آنان] محقق و ثابت شد،'),
(3985, 38, 15, 'و اینان [که تو را انکار می کنند] جز یک فریاد مرگبار را که هیچ تأخیری در آن نیست، انتظار نمی کشند،'),
(3986, 38, 16, 'و [اینان از روی مسخره] گفتند: پروردگارا! پیش از روز حساب، هر چه زودتر سهم ما را از عذاب به ما بده.'),
(3987, 38, 17, 'بر آنچه می گویند شکیبا باش، و بنده ما داود را که دارای نیرومندی [در دانش و حکومت] بود یاد کن. او بسیار رجوع کننده [به سوی خدا] بود.'),
(3988, 38, 18, 'همانا ما کوه ها را مسخّر و رام کردیم که با او در شبان گاه و هنگام برآمدن آفتاب تسبیح می گفتند،'),
(3989, 38, 19, 'و پرندگان را [نیز] به طور دسته جمعی [مسخّر و رام کردیم که با او تسبیح می گفتند]، و همه رجوع کننده به سوی خدا بودند،'),
(3990, 38, 20, 'و حکومتش را محکم و استوار ساختیم و به او حکمت و منصب داوری عطا کردیم،'),
(3991, 38, 21, 'و آیا خبر مهم آن دادخواهان هنگامی که از دیوار بلند نمازخانه او بالا رفتند به تو رسیده است؟'),
(3992, 38, 22, 'زمانی که [به طور ناگهانی] بر داود وارد شدند، و او از آنان هراسان شد، گفتند: نترس [ما] دو گروه دادخواه و شاکی هستیم که یکی از ما بر دیگری ستم کرده است؛ بنابراین میان ما به حق داوری کن و [در داوریت] ستم روا مدار، و ما را به راه راست راهنمایی کن.'),
(3993, 38, 23, 'این برادر من است، نود و نه میش دارد و من یک میش دارم، گفته است: این یکی را هم به من واگذار. و در گفتگو مرا مغلوب ساخت.'),
(3994, 38, 24, 'گفت: یقیناً او با درخواست افزودن میش تو به میش های خود بر تو ستم روا داشته است، و قطعاً بسیاری از معاشران و شریکان به یکدیگر ستم می کنند، به جز کسانی که ایمان آورده و کارهای شایسته انجام داده اند و اینان اندک اند. و داود دانست که ما او را [در این حادثه] آزموده ایم، در نتیجه از پروردگارش درخواست آمرزش کرد و بی درنگ به حالت خضوع به رو در افتاد و به خدا بازگشت.'),
(3995, 38, 25, 'و ما او را در این [داوری] آمرزیدیم، بی تردید او نزد ما تقرب و منزلتی بلند و سرانجامی نیکو دارد.'),
(3996, 38, 26, '[و گفتیم:] ای داود! همانا تو را در زمین جانشین [و نماینده خود] قرار دادیم؛ پس میان مردم به حق داوری کن و از هوای نفس پیروی مکن که تو را از راه خدا منحرف می کند. بی تردید کسانی که از راه خدا منحرف می شوند، چون روز حساب را فراموش کرده اند، عذابی سخت دارند.'),
(3997, 38, 27, 'و ما آسمان و زمین و آنچه را که میان آنهاست، بیهوده نیافریده ایم، این پندار کافران است، پس وای بر آنان که کافرند از آتش دوزخ.'),
(3998, 38, 28, 'آیا کسانی که ایمان آورده و کارهای شایسته انجام داده اند مانند مفسدان در زمین قرار می دهیم یا پرهیزکاران را چون بدکاران؟!'),
(3999, 38, 29, 'این کتاب پربرکتی است که آن را بر تو نازل کرده ایم تا آیاتش را تدبّر کنند و خردمندان متذکّر شوند.'),
(4000, 38, 30, 'و سلیمان را به داود بخشیدیم، چه نیکو بنده ای بود به راستی او بسیار رجوع کننده [به خدا] بود.'),
(4001, 38, 31, '[یاد کن] هنگامی را که در پایان روز اسب های چابک و تیزرو بر او عرضه کردند.'),
(4002, 38, 32, 'پس گفت: من دوستی اسبان را بر یاد پروردگارم [که نماز مستحب پایان روز است] اختیار کردم [زیرا می خواهم از آنان در جهاد با دشمن استفاده کنم و همواره به آنها نظر می کرد] تا [خورشید] پشت پرده افق پنهان شد.'),
(4003, 38, 33, '[اسب ها چنان توجه او را جلب کرده بودند که گفت:] آنها را به من بازگردانید. پس [برای نوازش آنها] به دست کشیدن به ساق ها و گردن های آنها پرداخت؛'),
(4004, 38, 34, 'و به راستی سلیمان را [درباره فرزندش] آزمودیم، و [آزمون این بود که] بر تختش جسدی [بی جان از فرزندش] افکندیم [فرزندی که سلیمان به زنده بودن او بسیار امید داشت]، آن گاه به درگاه خدا رجوع کرد [و همه امورش را به خدا واگذاشت.]'),
(4005, 38, 35, 'گفت: پروردگارا! مرا بیامرز و حکومتی به من ببخش که بعد از من سزاوار هیچ کس نباشد؛ یقیناً تو بسیار بخشنده ای.'),
(4006, 38, 36, 'پس باد را برای او مسخّر و رام کردیم که به فرمان او هر جا که می خواست نرم و آرام روان می شد.'),
(4007, 38, 37, 'و هر بنّا و غواصی از شیاطین را [مسخّر و رام او نمودیم،]'),
(4008, 38, 38, 'و دیگر شیاطین را که با غل و زنجیر به هم بسته بودند [در سلطه او درآوردیم تا نتوانند در حکومت او فتنه و آشوب برپا کنند.]'),
(4009, 38, 39, '[و به او گفتیم:] این عطای بی حساب ماست، [به هر کس خواهی] بی حساب ببخش و [از هر کس خواهی] دریغ کن.'),
(4010, 38, 40, 'بی تردید او نزد ما تقرب و منزلتی بلند و سرانجامی نیکو دارد.'),
(4011, 38, 41, 'و بنده ما ایوب را یاد کن، هنگامی که پروردگارش را ندا داد که شیطان [به سبب رنج و شکنجه ای که دچارش هستم] مرا سرزنش و شماتت می کند [تا از رحمت تو دلسردم کند.]'),
(4012, 38, 42, '[به او گفتیم:] با پایت به زمین بکوب، این آبی است برای شسشتو، آبی سرد و آشامیدنی.'),
(4013, 38, 43, 'و خانواده اش را [که در حادثه ها از دستش رفته بودند] و مانندشان را همراه با آنان به او بخشیدیم تا رحمتی از سوی ما و تذکری برای خردمندان باشد.'),
(4014, 38, 44, 'و [به او گفتیم: چون سوگند خورده ای که همسرت را برای اینکه تو را در امور معنوی ناراحت کرده بود، صد تازیانه بزنی] با دستت بسته ای ترکه خشک برگیر و همسرت را با آن بزن، و سوگندت را مشکن. بی تردید ما او را شکیبا یافتیم. چه نیکو بنده ای! یقیناً بسیار رجوع کننده به سوی ما بود.'),
(4015, 38, 45, 'و بندگان ما ابراهیم و اسحاق و یعقوب را یاد کن که دارای قدرت و بصیرت بودند.'),
(4016, 38, 46, 'ما آنان را با [صفت بسیار پرارزش] یاد کردن سرای آخرت با اخلاصی ویژه خالص ساختیم.'),
(4017, 38, 47, 'به یقین آنان در پیشگاه ما از برگزیدگان و نیکان اند.'),
(4018, 38, 48, 'و اسماعیل والیسع وذوالکفل را یاد کن و همه از نیکان اند.'),
(4019, 38, 49, 'این [سرگذشت های سازنده] یادآوری و پند است؛ و بی تردید برای پرهیزکاران بازگشت گاه نیکویی خواهد بود.'),
(4020, 38, 50, '[آن بازگشت گاه] بهشت های جاویدانی است که درهایش را به روی آنان گشوده اند،'),
(4021, 38, 51, 'در حالی که در آنجا بر تخت ها تکیه می زنند و میوه های فراوان و نوشیدنی مورد دلخواهشان را در آنجا می طلبند،'),
(4022, 38, 52, 'و نزد آنان زنانی است که فقط به شوهرانشان عشق می ورزند، و با شوهرانشان هم سن و سال اند.'),
(4023, 38, 53, '[به آنان گویند:] این است آنچه شما را برای روز حساب وعده می دادند.'),
(4024, 38, 54, 'این بی تردید عطای ماست که برای آن پایانی نیست.'),
(4025, 38, 55, 'این [همه برای پرهیزکاران است]، و مسلماً برای سرکشان، بدترین بازگشتگاه خواهد بود.'),
(4026, 38, 56, 'دوزخ که در آن وارد می شوند و چه بد آرامگاهی است!'),
(4027, 38, 57, 'این آب جوشان و مایع چرکین متعفّن است که باید آن را بچشند،'),
(4028, 38, 58, 'و [جز اینها] عذاب های دیگری مانند آن دارند.'),
(4029, 38, 59, '[چون پیشوایان کفر به دوزخ درآیند، و پیروانشان را نیز راهی دوزخ کنند ندا رسد:] این گروهی [از پیروان شما] هستند که با فشار و زور با شما وارد دوزخ می شوند. [پیشوایان کفر در پاسخ ندا دهنده گویند:] خوش آمد و گشایشی بر آنان [که پیروان ما بودند] مباد، بی تردید آنان به آتش خواهند سوخت.'),
(4030, 38, 60, '[پیروان به پیشوایان] گویند: بلکه بر شما خوش آمد و گشایشی مباد، شما این عذاب را از پیش برای ما فراهم کردید، و چه بد قرارگاهی است.'),
(4031, 38, 61, 'می گویند: پروردگارا! هر کس این عذاب را از پیش برای ما فراهم آورده است، برای او در آتش عذابی دو چندان بیفزا.'),
(4032, 38, 62, 'و می گویند: ما را چه شده که [مردان مؤمن و شایسته] ای که از اشرارشان می شمردیم، نمی بینیم.'),
(4033, 38, 63, 'آیا ما آنان را به ناحق به مسخره گرفتیم [و اکنون در بهشت جای دارند] یا [در دوزخ اند و] دیدگان ما به آنان نمی افتد؟!'),
(4034, 38, 64, 'این گفتگو و مجادله اهل آتش حتمی و واقع شدنی است.'),
(4035, 38, 65, 'بگو: من فقط بیم دهنده ام، و هیچ معبودی جز خدای یگانه قهّار نیست.'),
(4036, 38, 66, 'پروردگار آسمان ها و زمین و آنچه میان آنهاست، توانای شکست ناپذیر و بسیار آمرزنده است.'),
(4037, 38, 67, 'بگو: این [قرآن من] خبری بزرگ است،'),
(4038, 38, 68, 'که شما از آن روی می گردانید.'),
(4039, 38, 69, 'من از ملأ اعلی هنگامی که [درباره آفرینش آدم] مجادله می کردند، هیچ خبری ندارم.'),
(4040, 38, 70, 'به من وحی نمی شود جز برای اینکه بیم دهنده ای آشکارم.'),
(4041, 38, 71, '[یاد کن] هنگامی را که پروردگارت به فرشتگان گفت: همانا من بشری از گل خواهم آفرید.'),
(4042, 38, 72, 'پس زمانی که اندامش را درست و نیکو نمودم و از روح خود در او دمیدم، برای او سجده کنید.'),
(4043, 38, 73, 'پس فرشتگان همه با هم سجده کردند؛'),
(4044, 38, 74, 'مگر ابلیس که تکبّر ورزید و از کافران شد.'),
(4045, 38, 75, '[خدا] فرمود: ای ابلیس! تو را چه چیزی از سجده کردن بر آنچه که با دستان قدرت خود آفریدم، بازداشت؟ آیا تکبّر کردی یا از بلند مرتبه گانی؟'),
(4046, 38, 76, 'گفت: من از او بهترم، مرا از آتش آفریدی و او را از گل ساختی.'),
(4047, 38, 77, '[خدا] گفت: از آن [جایگاه] بیرون رو که بی تردید تو رانده شده ای؛'),
(4048, 38, 78, 'و حتماً لعنت من تا روز قیامت بر تو باد.'),
(4049, 38, 79, 'گفت: پروردگارا! مرا تا روزی که مردم برانگیخته می شوند، مهلت ده.'),
(4050, 38, 80, '[خدا] گفت: تو از مهلت یافتگانی،'),
(4051, 38, 81, 'تا زمانی معین و معلوم.'),
(4052, 38, 82, 'گفت: به عزتت سوگند همه آنان را گمراه می کنم،'),
(4053, 38, 83, 'مگر بندگان خالص شده ات را.'),
(4054, 38, 84, '[خدا] گفت: سوگند به حق و فقط حق را می گویم'),
(4055, 38, 85, 'که بی تردید دوزخ را از تو و آنان که از تو پیروی کنند، از همگی پر خواهم کرد'),
(4056, 38, 86, 'بگو: من برای ابلاغ دین هیچ پاداشی از شما نمی خواهم و از کسانی که چیزی را از نزد خود می سازند [و ادعای باطل می کنند] نیستم.'),
(4057, 38, 87, 'آن [قرآن را که بر شما می خوانم] جز مایه تذکر و پندی برای جهانیان نیست.'),
(4058, 38, 88, 'و بی تردید پس از مدتی خبر [صدق حقّانیّت و ظهور عینی آیات و وعده های] آن را خواهید دانست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, '[این] کتاب نازل شده از سوی خدای توانای شکست ناپذیر و حکیم است.'),
(4060, 39, 2, 'بی تردید ما این کتاب را به حقّ و درستی به سوی تو نازل کردیم، پس خدا را در حالی که اعتقاد و ایمان را برای او [از هرگونه شرکی] خالص می کنی، بپرست.'),
(4061, 39, 3, 'آگاه باشید! که دین خالص ویژه خداست، و آنان که به جای خدا سرپرستان و معبودانی برگزیده اند [و می گویند:] ما آنان را جز برای اینکه ما را هر چه بیشتر به خدا نزدیک کنند، نمی پرستیم. بی تردید خدا میان آنان [و مؤمنان] درباره آنچه که اختلاف می کنند [و آن آیین توحید و شرک است] داوری خواهد کرد؛ قطعاً خدا آن کس را که دروغگو و بسیار ناسپاس است، هدایت نمی کند.'),
(4062, 39, 4, 'اگر [بر فرض محال] خدا می خواست برای خود فرزندی بگیرد، همانا از میان آفریده هایش آنچه را خودش می خواست برمی گزید [نه اینکه شما برای او انتخاب کنید]؛ منزّه است او [از آنکه برای او فرزندی باشد] اوست خدای یگانه و قهّار.'),
(4063, 39, 5, 'آسمان ها و زمین را به حق آفرید. شب را به روز درمی پیچید، و روز را به شب درمی پیچید، و خورشید و ماه را [برای بندگانش] مسخّر و رام کرد. هر کدام تا سرآمدی معین روانند. آگاه باشید! او توانای شکست ناپذیر و بسیار آمرزنده است.'),
(4064, 39, 6, 'شما را از یک تن آفرید، سپس همسرش را از او پدید آورد، و از چهارپایان [شتر و گاو و گوسفند و بز به اعتبار یک نر و یک ماده] هشت زوج آفرید، شما را در شکم های مادرانتان آفرینشی پس از آفرینشی دیگر در میان تاریکی های سه گانه [شکم و رحم و مشیمه] به وجود آورد. این است خدا پروردگار شما که فرمانروایی [مطلق بر همه هستی] ویژه اوست، معبودی جز او نیست، پس چگونه شما را از حق بازمی گردانند؟'),
(4065, 39, 7, 'اگر کافر شوید [زیانی به خدا نمی رسانید؛ زیرا] خدا از شما بی نیاز است، و کفر را برای بندگانش نمی پسندد؛ و اگر سپاس گزاری کنید، آن را برای شما می پسندد. و هیچ سنگین باری، بار [گناه] دیگری را برنمی دارد. سپس بازگشت شما به سوی پروردگارتان خواهد بود، و شما را به آنچه انجام می دادید، آگاه خواهد کرد؛ زیرا او به نیّات و اسرار سینه ها داناست.'),
(4066, 39, 8, 'و چون به انسان آسیبی رسد، پروردگارش را در حال بازگشت به سوی او [برای برطرف شدن آسیبش] می خواند، سپس چون او را [برای رهایی از آن آسیب] نعمتی از جانب خود عطا کند، آن آسیب را که پیش تر برای برطرف شدنش دعا می کرد، فراموش می کند و دوباره برای خدا همتایانی قرار می دهد تا [مردم را] از راه خدا گمراه کند. بگو: اندک زمانی از کفر خود برخوردار باش، بی تردید تو از اهل آتشی.'),
(4067, 39, 9, '[آیا چنین انسان کفران کننده ای بهتر است] یا کسی که در ساعات شب به سجده و قیام و عبادتی خالصانه مشغول است، از آخرت می ترسد و به رحمت پروردگارش امید دارد؟ بگو: آیا کسانی که معرفت و دانش دارند و کسانی که بی بهره از معرفت و دانش اند، یکسانند؟ فقط خردمندان متذکّر می شوند.'),
(4068, 39, 10, 'بگو: ای بندگان مؤمنم! از پروردگارتان پروا کنید. برای کسانی که در این دنیا اعمال شایسته انجام داده اند، پاداش نیکی است و زمین خدا گسترده است [بر شماست از سرزمینی که دچار مضیقه دینی هستید به سرزمینی دیگر مهاجرت کنید]. فقط شکیبایان پاداششان را کامل و بدون حساب دریافت خواهند کرد.'),
(4069, 39, 11, 'بگو: من مأمورم که خدا را در حالی که ایمان و عبادت را برای او [از هر گونه شرکی] خالص می کنم، بپرستم.'),
(4070, 39, 12, 'و مأمورم که [در این آیین از] نخستین تسلیم شدگان [به فرمان ها و احکام] او باشم.'),
(4071, 39, 13, 'بگو: اگر پروردگارم را نافرمانی کنم از عذاب روزی بزرگ می ترسم.'),
(4072, 39, 14, 'بگو: فقط خدا را در حالی که ایمان و عبادتم را برای او [از هر گونه شرکی] خالص می کنم، می پرستم.'),
(4073, 39, 15, 'پس شما هم آنچه را می خواهید، به جای او بپرستید. بگو: بی تردید زیانکاران [واقعی] کسانی هستند که روز قیامت سرمایه وجودشان و کسانشان را به تباهی داده باشند؛ آگاه باشید! که آن همان زیان آشکار است.'),
(4074, 39, 16, 'برای آنان از بالای سرشان و از زیر پایشان سایبان هایی از آتش است، این عذابی است که خدا بندگانش را به آن بیم می دهد. ای بندگانم! از من پروا کنید'),
(4075, 39, 17, 'و کسانی که از پرستیدن طاغوت [یعنی بت ها، اربابان کفر و شیطان های سرکش] دوری کردند و به سوی خدا بازگشتند، بر آنان مژده باد؛ پس به بندگانم مژده ده.'),
(4076, 39, 18, 'آنان که سخن را می شنوند و از بهترینش پیروی می کنند، اینانند کسانی که خدا هدایتشان کرده، و اینان همان خردمندانند.'),
(4077, 39, 19, 'آیا کسی که فرمان عذاب بر او محقق و ثابت شده [راه گریزی از آن دارد؟] آیا کسی را که در آتش است، تو نجات می دهی؟'),
(4078, 39, 20, 'ولی کسانی که از پروردگارشان پروا داشتند، برای آنان غرفه هایی است که از فراز آنها غرفه هایی دیگر بنا شده و از زیر آنها نهرها جاری است. خدا این وعده را داده است؛ خدا خلف وعده نمی کند.'),
(4079, 39, 21, 'آیا ندانسته ای که خدا از آسمان آبی نازل کرد، پس آن را به صورت چشمه هایی در زمین درآورد، آن گاه به وسیله آن زراعتی را که رنگ هایش گوناگون است، بیرون می آورد، سپس [آن زراعت] خشک می شود، در نتیجه آن را به رنگ زرد بینی، پس آن را ریز ریز و در هم شکسته می کند؛ بی تردید در این [دگرگونی ها] برای خردمندانْ هشدار و عبرتی است.'),
(4080, 39, 22, 'آیا کسی که خدا سینه اش را برای [پذیرفتن] اسلام گشاده است، و بهره مند از نوری از سوی پروردگار خویش است [مانند کسی است که سینه اش از پذیرفتن اسلام تنگ است؟] پس وای بر آنان که دل هایشان از یاد کردن خدا سخت است، اینان در گمراهی آشکار هستند.'),
(4081, 39, 23, 'خدا نیکوترین سخن را نازل کرد، کتابی که [آیاتش در نظم، زیبایی، فصاحت، بلاغت و عمق محتوا] شبیه یکدیگر است، مشتمل بر داستان های پندآموز [و امر، نهی، وعده، وعید، حلال و حرام] است؛ از شنیدن آیاتش پوست کسانی که از پروردگارشان می هراسند به هم جمع می شود، آن گاه پوستشان و دلشان به یاد خدا نرم می گردد و آرامش می یابد. این هدایت خداست که هر که را بخواهد به آن راهنمایی می کند؛ و هر که را خدا [به کیفر کفر و عنادش] گمراه کند، او را هیچ هدایت کننده ای نخواهد بود.'),
(4082, 39, 24, 'آیا کسی که روز قیامت [به خاطر بسته بودن دست هایش می کوشد که] با صورت خود گزند عذاب را دفع کند [مانند کسی است که ایمن از عذاب است؟] به ستمکاران گویند: آنچه را همواره مرتکب می شدید، بچشید.'),
(4083, 39, 25, 'کسانی [هم] که پیش از آنان بودند [آیات خدا را] تکذیب کردند، پس از آنجا که گمان نمی کردند، عذاب به سویشان آمد.'),
(4084, 39, 26, 'پس خدا در زندگی دنیا خواری و رسوایی را به آنان چشانید؛ و اگر معرفت و آگاهی داشتند [توجه می کردند که] عذاب آخرت بزرگ تر است.'),
(4085, 39, 27, 'و ما برای مردم در این قرآن از هر گونه مثل و سرگذشت عبرت آموزی بیان کرده ایم، باشد که متذکّر شوند.'),
(4086, 39, 28, 'قرآنی است گویا و روشن بدون هر گونه انحراف و کژی، برای اینکه [در سایه تعالیمش از شرک، عصیان و فساد] بپرهیزند.'),
(4087, 39, 29, 'خدا [برای فهماندن حقیقت توحید و شرک] مثلی زده است: مردی را که اربابانی ناسازگار و بداخلاق در [مالکیّت] او شریک اند، و مردی که فقط برده یک مالک است، آیا این دو برده از جهت فرمان گرفتن و فرمان بردن با هم یکسانند؟ [موحدان که بنده خدای یکتا هستند، دارای زندگی منظم و پاک و آخرتی آبادند، و مشرکان که فرمان بران اربابان گوناگونند، دارای زندگی پریشان و آخرتی خراب اند.] همه ستایش ها ویژه خداست. بلکه بیشترشان [به آثار و منافع بندگی خدا] معرفت و آگاهی ندارند.'),
(4088, 39, 30, 'بی تردید تو می میری و قطعاً آنان هم می میرند.'),
(4089, 39, 31, 'سپس شما روز قیامت نزد پروردگارتان [در امر دین] مجادله خواهید کرد [که ما موحدان در دنیا حقّانیّت توحید را ثابت کردیم و بطلان بت پرستی را روشن نمودیم، ولی کبرِ شما مانع از هدایت شما شد.]'),
(4090, 39, 32, 'پس چه کسی ستمکارتر است از آنکه بر خدا دروغ بندد و سخن راست و درست را چون به سوی او آمد، تکذیب کند؟ آیا دوزخ جایگاهی برای کافران نیست؟'),
(4091, 39, 33, 'و آنکه سخن راست و درست آورد و کسانی که آن را باور کردند، اینانند که پرهیزکارانند.'),
(4092, 39, 34, 'برای آنان نزد پروردگارشان آنچه بخواهند فراهم است؛ این است پاداش نیکوکاران؛'),
(4093, 39, 35, 'تا خدا [به رحمتش] زشت ترین اعمالی که انجام دادند از آنان محو کند، و آنان را بر پایه بهترین عملی که همواره انجام می دادند پاداش دهد.'),
(4094, 39, 36, 'آیا خدا برای بنده اش [در همه امور] کافی نیست؟ و مشرکان [به پندار خود] تو را از کسانی که غیر خدا هستند، می ترسانند و هر که را خدا به کیفر [کفر و عناد] ش گمراه کند، او را هیچ هدایت کننده ای نخواهد بود؛'),
(4095, 39, 37, 'و هر که را خدا هدایت کند، او را هیچ گمراه کننده ای نخواهد بود. آیا خدا توانای شکست ناپذیر و صاحب انتقام نیست؟'),
(4096, 39, 38, 'اگر از آنان بپرسی: چه کسی آسمان ها و زمین را آفرید؟ بی تردید می گویند: خدا. بگو: پس مرا درباره معبودانی که به جای خدا می پرستید خبر دهید، که اگر خدا آسیبی را برای من بخواهد، آیا آنها می توانند آسیب او را از من برطرف کنند؟ یا اگر رحمتی را برای من بخواهد، آیا می توانند رحمتش را باز دارند؟ بگو: خدا مرا کافی است. توکل کنندگان فقط بر او توکل می کنند.'),
(4097, 39, 39, 'بگو: ای قوم من! شما بر همین حالت کفر و عنادی که هستید عمل کنید، من نیز [بر پایه ایمان و اخلاصم] عمل می کنم، سپس خواهید دانست،'),
(4098, 39, 40, 'که چه کسی را [در دنیا] عذابی خوار کننده بیاید، و [در آخرت هم] عذابی جاودانه بر او فرود آید.'),
(4099, 39, 41, 'ما این کتاب را برای [هدایت] مردم به حقّ و راستی بر تو نازل کرده ایم؛ پس هر که هدایت یافت، به سود خود هدایت یافته و هر که گمراه شد، فقط به زیان خود گمراه می شود؛ و تو بر آنان نگهبان و کارساز نیستی،'),
(4100, 39, 42, 'و خداست که روح [مردم] را هنگام مرگشان به طور کامل می گیرد، و روحی را که [صاحبش] نمرده است نیز به هنگام خوابش [می گیرد]، پس روح کسی که مرگ را بر او حکم کرده نگه می دارد، [و به بدن باز نمی گرداند] و دیگر روح را تا سرآمدی معین باز می فرستد؛ مسلماً در این [واقعیت] برای مردمی که می اندیشند، نشانه هایی [بر قدرت خدا] ست.'),
(4101, 39, 43, '[نه اینکه بی خبران، درباره قدرت خدا نمی اندیشند] بلکه به جای خدا [از بتان] شفیعانی برای خود گرفته اند. بگو: آیا [از آنها شفاعت می خواهید] هر چند مالک چیزی [و اختیاردار شفاعتی] نباشند و علم و عقلی نداشته باشند [و پرستندگان خود را نشناسند؟]'),
(4102, 39, 44, 'بگو: شفاعت، یکسره ویژه خداست، فرمانروایی آسمان ها و زمین در سیطره اوست، سپس به سوی او بازگردانده می شوید؛'),
(4103, 39, 45, 'و هنگامی که خدا به یگانگی یاد می شود [و نامی از معبودانشان به میان نمی آید] دل های کسانی که به آخرت ایمان ندارند، نفرت پیدا می کند و چون یادی از معبودان دیگر به میان می آید، ناگاه مسرور و شاد می شوند!!'),
(4104, 39, 46, '[وقتی از ایمان آوردنشان ناامید شدی] بگو: خدایا! ای آفریننده آسمان ها و زمین! [ای] دانای نهان و آشکار! تو خود در میان بندگانت بر سر آنچه [از عقاید و عبادات] اختلاف می کردند، داوری خواهی کرد؛'),
(4105, 39, 47, 'و اگر برای کسانی که [به آیات خدا] ستم ورزیده اند، همه آنچه در زمین است و مانندش با آن باشد، بی تردید حاضرند آن را برای رهایی خود از عذاب سخت روز قیامت عوض دهند، و از سوی خدا آنچه را که [از عذاب های گوناگون] نمی پنداشتند، آشکار می شود.'),
(4106, 39, 48, 'و زشتی های آنچه را که مرتکب شده اند برای آنان پدیدار می گردد، و آنچه [از عذاب هایی] که مسخره می کردند، آنان را احاطه خواهد کرد.'),
(4107, 39, 49, 'پس چون انسان را آسیبی رسد، ما را [برای برطرف کردنش] بخواند، سپس هنگامی که [برای برطرف شدن آسیب] از سوی خود نعمتی به او می دهیم، می گوید: فقط آن را بر پایه دانش و کاردانی خود یافته ام! [چنین نیست که می پندارد] بلکه آن نعمت آزمایشی است [که آیا به خاطر آن سپاس گزاری می کند یا به طغیان برمی خیزد؟] ولی بیشتر آنان [به این واقعیت] معرفت و آگاهی ندارند.'),
(4108, 39, 50, 'همین [سخن بیهوده و باطل] را کسانی که پیش از آنان بودند، گفتند، ولی آنچه را [از مقام و ثروت همواره] به دست می آوردند، عذاب و هلاکت را از آنان برطرف نکرد.'),
(4109, 39, 51, 'پس عذاب های آنچه را مرتکب شدند به آنان رسید، و کسانی از این [مردم مکه] که [به آیات ما] ستم ورزیدند، به زودی عذاب های آنچه را که مرتکب شدند به آنان خواهد رسید؛ و آنان عاجزکننده ما نیستند [تا بتوانند از دسترس قدرت ما بیرون روند.]'),
(4110, 39, 52, 'آیا ندانسته اند که خدا رزق و روزی را برای هر که بخواهد وسعت می دهد یا تنگ می گیرد؟ بی تردید در این [اندازه گیری] برای مردمی که ایمان دارند، نشانه هایی [از حکمت و مصلحت خدا] ست.'),
(4111, 39, 53, 'بگو: ای بندگان من که [با ارتکاب گناه] بر خود زیاده روی کردید! از رحمت خدا نومید نشوید، یقیناً خدا همه گناهان را می آمرزد؛ زیرا او بسیار آمرزنده و مهربان است؛'),
(4112, 39, 54, 'و به سوی پروردگارتان بازگردید و تسلیم [فرمان ها و احکام] او شوید، پیش از آنکه شما را عذاب آید آن گاه یاری نشوید.'),
(4113, 39, 55, 'و از نیکوترین چیزی که از طرف پروردگارتان به سوی شما نازل شده است پیروی کنید، پیش از آنکه ناگهان و در حالی که بی خبرید، عذاب به شما رسد؛'),
(4114, 39, 56, 'تا مبادا آنکه کسی بگوید: دریغ و افسوس بر اهمال کاری و تقصیری که درباره خدا کردم، و بی تردید [نسبت به احکام الهی و آیات ربّانی] از مسخره کنندگان بودم.'),
(4115, 39, 57, 'یا بگوید: اگر خدا هدایتم می کرد، بی تردید از پرهیزکاران بودم،'),
(4116, 39, 58, 'یا چون عذاب را ببیند، بگوید: ای کاش مرا [به دنیا] بازگشتی بود تا از نیکوکاران می شدم.'),
(4117, 39, 59, '[ولی به او گویم:] آری، آیاتم به سوی تو آمد، پس تو آنها را انکار کردی و [در پذیرفتنش] تکبّر ورزیدی، و [نسبت به آن] از کافران بودی،'),
(4118, 39, 60, 'و روز قیامت کسانی را که بر خدا دروغ بستند می بینی که صورت هایشان سیاه است؛ آیا در دوزخ جایگاهی برای متکبران نیست؟'),
(4119, 39, 61, 'و خدا کسانی را که پرهیزکاری پیشه کردند، به سبب اعمالی که مایه رستگاری شان بود نجات می دهد، عذاب به آنان نمی رسد، و اندوهگین هم نمی شوند.'),
(4120, 39, 62, 'خدا آفریننده هر چیزی است و او بر هر چیزی نگهبان و کارساز است.'),
(4121, 39, 63, 'کلیدهای آسمان ها و زمیندر مالکیّت اوست، و کسانی که به آیات خدا کفر ورزیدند، هم آنان زیانکارند.'),
(4122, 39, 64, 'بگو: ای نادانان! آیا به من فرمان می دهید که غیر خدا را بپرستم؟!'),
(4123, 39, 65, 'بی تردید به تو و به کسانی که پیش از تو بوده اند، وحی شده است که اگر مشرک شوی، همه اعمالت تباه و بی اثر می شود و از زیانکاران خواهی بود.'),
(4124, 39, 66, 'بلکه فقط خدا را بپرست و از سپاس گزاران باش.'),
(4125, 39, 67, 'و خدا را آن گونه که سزاوار اوست نشناختند، در حالی که زمین در روز قیامت یکسره در قبضه قدرت اوست، و آسمان ها هم درهم پیچیده به دست اوست؛ منزّه و برتر است از آنچه با او شریک می گیرند.'),
(4126, 39, 68, 'و در صور دمیده می شود، پس هر که در آسمان ها و زمین است می میرد، مگر کسی را که خدا بخواهد، آن گاه بار دیگر در صور دمیده می شود، ناگاه همه آنان بر پای ایستاده [مات و مبهوت به هر سو] می نگرند [که سرانجام کارشان چه خواهد شد!]'),
(4127, 39, 69, 'و زمین به نور پروردگارش روشن می شود، و کتاب [اعمال] را می نهند، و پیامبران و گواهان را بیاورند ومیانشان به حقّ وراستی داوری شود، وآنان مورد ستم قرار نمی گیرند؛'),
(4128, 39, 70, 'و به هر کسی آنچه را انجام داده است، به طور کامل داده شود و او به کارهایی که انجام می دهند، داناتر است.'),
(4129, 39, 71, 'و کافران گروه گروه به دوزخ رانده می شوند، چون به دوزخ رسند درهایش گشوده شود و نگهبانانش به آنان گویند: آیا پیامبرانی از جنس خودتان به سویتان نیامدند که آیات پروردگارتان را بر شما بخوانند و شما را به دیدار این روزتان بیم دهند؟ گویند: آری، [آمدند] ولی فرمان عذاب بر کافران محقق و ثابت شدهاست.'),
(4130, 39, 72, '[به آنان] گویند: از درهای دوزخ درآیید در حالی که در آن جاودانه اید؛ پس جایگاه متکبران چه بد جایگاهی است؛'),
(4131, 39, 73, 'و کسانی که از پروردگارشان پروا کردند گروه گروه به بهشت رانده می شوند، چون به آن رسند در حالی که درهایش از پیش گشوده شده است، نگهبانانش به آنان گویند: سلام بر شما، پاکیزه و نیکو شدید، پس وارد آن شوید که [در آن] جاودانه اید.'),
(4132, 39, 74, 'می گویند: همه ستایش ها ویژه خداست که درباره ما به وعده اش وفا کرد، و زمین [بهشت] را به ما میراث داد که هر جای از بهشت را بخواهیم، جای خود قرار می دهیم. چه نیکوست پاداش عمل کنندگان.'),
(4133, 39, 75, 'و فرشتگان را می بینی که پیرامون عرش حلقه زده اند، پروردگارشان را همراه ستایش تسبیح می گویند، و در میان اهل محشر به حق داوری شود، و [پس از پایان کار قیامت] گویند: همه ستایش ها ویژه خداست که پروردگار جهانیان است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حم'),
(4135, 40, 2, 'این کتاب نازل شده از سوی خدای توانای شکست ناپذیر وداناست؛'),
(4136, 40, 3, 'که آمرزنده گناه و پذیرنده توبه، سخت کیفر و صاحب نعمت فراوان است. هیچ معبودی جز او نیست. بازگشت فقط به سوی اوست.'),
(4137, 40, 4, 'تنها کافران در آیات خدا مجادله و ستیزه می کنند [اینان را هیچ شأن و اعتباری نیست]؛ پس مبادا رفت و آمدشان در شهرها [به خاطر قدرت نمایی، تجارت و ثروتمند شدنشان] تو را بفریبد.'),
(4138, 40, 5, 'پیش از آنان قوم نوح و اقوامی که بعد از آنان بودند [پیامبرانشان را] تکذیب کردند، و هر امتی آهنگ پیامبر خود کردند تا او را دستگیر کنند [و از ابلاغ دین بازش دارند، یا از وطن بیرونش نمایند، یا او را بکشند] و با توسل به باطل برای نابود کردن حق و از میان بردن آن مجادله و ستیزه کردند، پس من هم آنان را [به عذاب] گرفتم، پس [بنگر که] عذاب من چگونه بود.'),
(4139, 40, 6, 'این گونه فرمان پروردگارت بر کافران محقق و ثابت شد که آنان اهل آتش اند.'),
(4140, 40, 7, 'فرشتگانی که عرش را حمل می کنند و آنان که پیرامون آن هستند، همراه سپاس و ستایش، پروردگارشان را تسبیح می گویند و به او ایمان دارند و برای اهل ایمان آمرزش می طلبند، [و می گویند:] پروردگارا! از روی رحمت و دانش همه چیز را فرا گرفته ای، پس آنان را که توبه کرده اند و راه تو را پیروی نموده اند بیامرز، و آنان را از عذاب دوزخ نگه دار.'),
(4141, 40, 8, 'پروردگارا! و آنان را در بهشت های جاودانی که به آنان و به شایستگان از پدران و همسران و فرزندانشان وعده داده ای در آور؛ یقیناً تو توانای شکست ناپذیر و حکیمی؛'),
(4142, 40, 9, 'و آنان را از عقوبت ها نگه دار، و هر که را در آن روز از عقوبت ها نگه داری بی تردید او را مورد رحمت قرار داده ای؛ و این همان کامیابی بزرگ است.'),
(4143, 40, 10, 'کافران را [پس از ورود به دوزخ] ندا می دهند: مسلماً دشمنی و خشم خدا نسبت به شما از دشمنی و خشم خودتان درباره خودتان بیشتر است؛ زیرا دعوت به ایمان می شدید پس کفر می ورزیدید.'),
(4144, 40, 11, 'می گویند: پروردگارا! دوبار ما را میراندی و دوبار زنده کردی، اکنون به گناهانمان معترفیم، پس آیا راهی برای بیرون آمدن [از دوزخ] هست؟'),
(4145, 40, 12, 'این [سختیِ عذاب] به سبب این است که چون خدا به یگانگی [و بدون معبودانتان] خوانده می شد [یگانگی اش] را انکار می کردید، و اگر برای او شریک و همتایی قرار داده می شد، باور می کردید؛ پس [اکنون] داوری، ویژه خدای والامرتبه و بزرگ است.'),
(4146, 40, 13, 'اوست که نشانه های خود را به شما نشان می دهد، و از آسمان برای شما روزی نازل می کند؛ و فقط کسانی متذکّر می شوند که به سوی خدا باز می گردند.'),
(4147, 40, 14, 'پس خدا را در حالی که ایمان و عبادت را [از هر گونه شرکی] برای او خالص می کنید، بپرستید گرچه کافران [از روش شما] ناخشنود باشند.'),
(4148, 40, 15, 'بالا برنده درجات و صاحب عرش است، روح را [که فرشته وحی است] به فرمانش به هر کس از بندگانش که بخواهد القا می کند تا مردم را از روز ملاقات [که روز رستاخیز است] بیم دهد.'),
(4149, 40, 16, 'روزی که همه آنان آشکار می شوند، [و] چیزی از آنان بر خدا پوشیده نمی ماند. [و ندا آید:] امروز فرمانروایی ویژه کیست؟ ویژه خدای یکتای قهّار است.'),
(4150, 40, 17, 'امروز هر کس را در برابر آنچه انجام داده است، پاداش می دهند. امروز هیچ ستمی وجود ندارد؛ یقیناً خدا در حسابرسی سریع است.'),
(4151, 40, 18, 'و آنان را از روز نزدیک بیم ده، آن گاه که [از شدت ترس] جان ها به گلوگاه رسد، در حالی که همه وجودشان پر از غم و اندوه است. برای ستمکاران هیچ دوست مهربانی و شفیعی که شفاعتش پذیرفته شود، وجود ندارد!'),
(4152, 40, 19, '[او] چشم هایی را که به خیانت [به نامحرمان] می نگرد و آنچه را سینه ها پنهان می دارند، می داند.'),
(4153, 40, 20, 'و خدا به حق داوری می کند، و معبودانی را که [مشرکان] به جای او می پرستند [چون از هر جهت ناتوانند] هیچ گونه داوری نمی کنند؛ یقیناً خدا شنوا و بیناست.'),
(4154, 40, 21, 'آیا در زمین گردش نکردند تا با تأمل بنگرند که سرانجام کسانی که پیش از آنان بودند، چگونه بود؟ آنان از ایشان نیرومندتر بودند، و در زمین آثاری پایدارتر [چون قلعه ها، خانه های استوار و کاخ های بسیار محکم] داشتند، پس خدا آنان را به کیفر گناهانشان [به عذاب] گرفت، و در برابر خدا مدافع و حمایت گری نداشتند.'),
(4155, 40, 22, 'این [کیفر دردناک] برای این بود که پیامبرانشان همواره دلایل روشن برای آنان می آوردند و آنان [از روی تکبّر و عناد] کفر می ورزیدند، پس خدا هم همه آنان را [به عذابی سخت] گرفت؛ زیرا او توانا و سخت کیفر است؛'),
(4156, 40, 23, 'و به راستی موسی را با معجزات خود و برهانی آشکار فرستادیم،'),
(4157, 40, 24, 'به سوی فرعون و هامان و قارون، ولی [آنان] گفتند: جادوگری بسیار دروغگوست!'),
(4158, 40, 25, 'هنگامی که حق را از سوی ما برای آنان آورد، گفتند: پسران کسانی را که با موسی ایمان آورده اند، بکشید، و زنانشان را [برای بیگاری و خدمت] زنده بگذارید. ولی نیرنگ و نقشه کافران جز در بیراهه و گمراهی نیست.'),
(4159, 40, 26, 'فرعون گفت: مرا بگذارید تا موسی را بکشم و او پروردگارش را [برای نجات خود] بخواند، چون من می ترسم دین شما را تغییر دهد، یا در این سرزمین فساد و تباهی به بار آورد!'),
(4160, 40, 27, 'و موسی گفت: من از هر متکبر سرکشی که به روز حساب ایمان ندارد به پروردگارم و پروردگار شما پناه می برم.'),
(4161, 40, 28, 'و مرد مؤمنی از خاندان فرعون که ایمانش را پنهان می داشت، گفت: آیا مردی را می کشید که می گوید: پروردگار من خداست؟ و بی تردید از سوی پروردگارتان برای شما دلایل روشنی آورده است، و اگر دروغگو باشد دروغش به زیان خود اوست، و اگر راستگو باشد برخی از عذاب هایی که به شما وعده می دهد به شما خواهد رسید؛ زیرا خدا کسی را که اسراف کار و بسیار دروغگوست، هدایت نمی کند.'),
(4162, 40, 29, 'ای قوم من! امروز فرمانروایی و حکومت برای شماست که در این سرزمین پیروز هستید، ولی اگر عذاب خدا به سوی ما آید، چه کسی ما را یاری خواهد داد؟ فرعون گفت: من جز آنچه را [صواب] می بینم [و به آن یقین دارم و آن انکار موسی و کشتن او و تقویت حکومت من است] به شما ارائه نمی کنم، و شما را جز به راه راست هدایت نمی کنم.'),
(4163, 40, 30, 'و آن مرد مؤمن گفت: ای قوم من! بی تردید من بر شما از روزی مانند روز [عذاب] گروه ها [یی که پیامبران را تکذیب کردند] می ترسم'),
(4164, 40, 31, 'چون سرنوشت قوم نوح و عاد و ثمود و کسانی که پس از آنان بودند؛ و خدا ستمی بر بندگان نمی خواهد [این بندگانند که بر خود ستم روا می دارند.]'),
(4165, 40, 32, 'و ای قوم من! بی تردید من از روزی که مردم یکدیگر را [برای نجات خود از عذاب] ندا می دهند بر شما می ترسم؛'),
(4166, 40, 33, 'روزی که [به علت شدت عذاب] پشت کنان از این سو به آن سو فرار می کنید [ولی از هر سو که می روید، برگردانده می شوید و] شما را [در برابر عذاب خدا] هیچ نگه دارنده ای نیست؛ و هر که را خدا [به سبب کبر و عنادش] گمراه کند، او را هیچ هدایت کننده ای نخواهد بود.'),
(4167, 40, 34, 'بی تردید یوسف پیش از این دلایل روشنی برای شما آورد، ولی شما همواره نسبت به آنچه که آورده بود در تردید بودید، تا زمانی که از دنیا رفت، [پس از مرگ او] گفتید: خدا هرگز بعد از او پیامبری مبعوث نخواهد کرد، این گونه خدا هر اسراف کار تردید کننده ای را گمراه می کند.'),
(4168, 40, 35, 'کسانی که در آیات خدا بی آنکه دلیلی برای آنان آمده باشد، مجادله و ستیزه می کنند [این عمل زشتشان] نزد خدا و نزد اهل ایمان مایه دشمنی بزرگ است؛ این گونه خدا بر دل هر گردنکش زورگویی، مُهر [تیره بختی] می نهد'),
(4169, 40, 36, 'و فرعون گفت: ای هامان! برای من بنایی بسیار بلند بساز شاید به وسایلی برسم،'),
(4170, 40, 37, 'وسایل [بالا رفتن به] آسمان ها تا از خدای موسی آگاه شوم [که کیست و کجاست؟!] هر چند موسی را [در گفته هایش درباره خدا] دروغگو می پندارم. و این گونه برای فرعون زشتی کردارش آراسته شد و از راه خدا باز ماند، و نیرنگ و نقشه فرعون جز در زیانکاری و نابودی نبود.'),
(4171, 40, 38, 'و آن مرد مؤمن گفت: ای قوم من! از من پیروی کنید تا شما را به راه راست هدایت کنم.'),
(4172, 40, 39, 'ای قوم من! این زندگی دنیا فقط کالایی بی ارزش و زودگذر است، و بی تردید آخرت سرای همیشگی و پایدار است.'),
(4173, 40, 40, 'هر که بدی کند جز به مانند آن کیفر نیابد، و از مردان یا زنان کسانی که کار شایسته انجام دهند در حالی که مؤمن باشند، آنان در بهشت درآیند و در آن بی حسابْ روزی یابند.'),
(4174, 40, 41, 'ای قوم من! چرا من شما را به سوی رهایی [از خسران دنیا و آخرت] می خوانم، و شما مرا به آتش می خوانید؟!'),
(4175, 40, 42, 'مرا می خوانید که به خدای یگانه کافر شوم و بر پایه جهالت و نادانی، و بدون دانش و معرفت که روشنگر حقایق است چیزی را شریک او قرار دهم! و من شما را به توانای شکست ناپذیر و بسیار آمرزنده می خوانم.'),
(4176, 40, 43, 'ثابت و یقینی است اینکه آنچه مرا به سویش می خوانید برای او در دنیا و آخرت حقّ ادعا [ی الوهیت و ربوبیت] نیست، و اینکه بازگشت ما به سوی خداست، و اینکه اسراف کاران اهل آتش اند.'),
(4177, 40, 44, 'پس به زودی [درستیِ] آنچه را [که امروز درباره عذاب اسراف کاران] می گویم [و شما باور نمی کنید] متوجّه خواهید شد، و من کارم را به خدا وامی گذارم؛ زیرا خدا به بندگان بیناست.'),
(4178, 40, 45, 'پس خدا او را از آسیب های آنچه بر ضد او نیرنگ می زدند، نگه داشت و عذاب سختی فرعونیان را احاطه کرد.'),
(4179, 40, 46, '[عذابشان] آتش است که صبح و شام بر آن عرضه می شوند، و روزی که قیامت برپا شود [ندا رسد:] فرعونیان را در سخت ترین عذاب در آورید.'),
(4180, 40, 47, 'و [یاد کن] هنگامی را که در آتش با یکدیگر نزاع و کشمکش می کنند، پس ضعیفان به مستکبران می گویند: ما [در دنیا] پیرو شما بودیم، آیا می توانید بخشی از این آتش را از ما دفع کنید؟'),
(4181, 40, 48, 'مستکبران می گویند: هم اکنون همه ما در آتش هستیم، بی تردید خدا [به عدالت و انصاف] میان بندگان داوری کرده است.'),
(4182, 40, 49, 'و آنان که در آتش اند، به نگهبانان دوزخ می گویند: از پروردگارتان بخواهید که یک روز بخشی از عذاب را از ما سبک کند.'),
(4183, 40, 50, '[نگهبانان] می گویند: آیا پیامبرانتان دلایل روشن برای شما نیاوردند؟ می گویند: چرا آوردند. می گویند: پس [هر اندازه که می خواهید خدا را] بخوانید، ولی دعای کافران جز در بیراهه و گمراهی نیست.'),
(4184, 40, 51, 'بی تردید ما پیامبران خود و مؤمنان را در زندگی دنیا و روزی که گواهان [برای گواهی دادن] به پا ایستند، یاری می کنیم.'),
(4185, 40, 52, 'همان روزی که عذرخواهی ستمکاران سودشان ندهد و برای آنان لعنت خدا و سرای سخت و بدی است.'),
(4186, 40, 53, 'محققاً ما به موسی هدایت عطا کردیم و کتاب [تورات] را به بنی اسرائیل به میراث دادیم؛'),
(4187, 40, 54, 'کتابی که [سراسر] برای خردمندان مایه هدایت و تذکر است.'),
(4188, 40, 55, 'پس [چنان که موسی بر آزار بنی اسرائیل شکیبایی ورزید، تو هم بر آزار دشمنان] شکیبا باش، بی تردید وعده خدا حق است، و از میان رفتن [توطئه ها و موانعی که به وسیله دشمنان در راه پیشرفت دعوتت به اسلام برای تو ایجاد شده]، از خدا بخواه، و پروردگارت را شب و صبح همراه با سپاس و ستایش تسبیح گوی.'),
(4189, 40, 56, 'بی تردید آنان که در آیات خدا بی آنکه دلیلی برای آنان آمده باشد، مجادله و ستیزه می کنند و در سینه هایشان جز کبر و بزرگ نمایی نیست به آن [بزرگی و عزت و سلطنتی که در آرزویش هستند] نمی رسند؛ پس به خدا پناه ببر؛ زیرا او شنوا و بیناست.'),
(4190, 40, 57, 'همانا آفرینش آسمان ها و زمین از آفرینش انسان مهم تر است، ولی بیشتر مردم [به حقایق] معرفت و آگاهی ندارند.'),
(4191, 40, 58, 'و بینا و نابینا، یکسان نیستند و [نیز] آنان که ایمان آورده و کارهای شایسته انجام داده اند با [مردم] بدکار برابر نمی باشند. اندکی متذکّر [این حقایق] می شوید.'),
(4192, 40, 59, 'یقیناً قیامت آمدنی است، هیچ تردیدی در آن نیست، ولی بیشتر مردم [به سبب تکبّر و غرور] ایمان نمی آورند.'),
(4193, 40, 60, 'و پروردگارتان گفت: مرا بخوانید تا شما را اجابت کنم، آنان که از عبادت من تکبّر ورزند، به زودی خوار و رسوا به دوزخ درآیند.'),
(4194, 40, 61, 'خداست که شب را برای شما پدید آورد تا در آن بیارامید، و روز را روشن و بینش بخش قرار داد [تا در آن به کارهای لازم بپردازید]؛ بی تردید خدا نسبت به مردم دارای فضل و احسان است، ولی بیشتر مردم سپاس گزاری نمی کنند.'),
(4195, 40, 62, 'این است خدا پروردگار شما که آفریننده هر چیزی است، هیچ معبودی جز او نیست، پس چگونه [از حق] منصرفتان می کنند؟'),
(4196, 40, 63, 'این گونه [که شما منحرف می شوید] آنان که همواره آیات خدا را انکار می کردند، منحرف می شوند.'),
(4197, 40, 64, 'خداست که زمین را برای شما جایگاه امن و آسایش آفرید، و آسمان را بنایی [محکم و استوار] ساخت، و شما را صورتگری کرد و صورت هایتان را نیکو گردانید، و از چیزهای پاکیزه [ای که با طبیعت شما هماهنگ است] به شما روزی داد. این است خدا پروردگار شما، پربرکت است خدا پروردگار جهانیان.'),
(4198, 40, 65, 'اوست زنده [بی زوال]، هیچ معبودی جز او نیست، پس او را در حالی که ایمان و عبادت را برای او [از هر گونه شرکی] خالص می کنید، بپرستید. همه ستایش ها ویژه خدا پروردگار جهانیان است.'),
(4199, 40, 66, 'بگو: من نهی شده ام از اینکه معبودانی که شما به جای خدا می پرستید، بپرستم؛ زیرا از جانب پروردگارم برای من دلایل روشن [بر حقّانیّت توحید] آمده است و مأمورم که فقط تسلیم [فرمان ها و احکام] پروردگار جهانیان باشم.'),
(4200, 40, 67, 'اوست که شما را از خاک آفرید، سپس از نطفه، آن گاه از علقه، سپس به صورت طفلی [از رحم مادران] بیرون می فرستد، تا به کمال نیرومندی و قوت خود برسید، آن گاه پیر و سالخورده شوید، و برخی از شما پیش از رسیدن به این مراحل قبض روح می شوید، و [برخی زنده می مانید] تا به آن مدتی که معین و مقرّر است برسید، و برای اینکه شما [درباره حق] تعقّل کنید.'),
(4201, 40, 68, 'اوست که زنده می کند و می میراند، و چون پدید آمدن چیزی را اراده کند، فقط به آن می گوید: باش. پس بی درنگ موجود می شود.'),
(4202, 40, 69, 'آیا کسانی را که در آیات خدا مجادله و ستیزه می کنند، ننگریستی که چگونه [از حق به باطل] برگردانده می شوند؟'),
(4203, 40, 70, 'همانان که قرآن و دینی را که پیامبران خود را به آن فرستادیم، انکار کردند، پس به زودی [نتیجه انکار خود را] خواهند دانست؛'),
(4204, 40, 71, 'زمانی که غل ها و زنجیرها [ی آتشین] در گردن هایشان باشد در حالی که به وسیله آنها کشیده شوند،'),
(4205, 40, 72, 'در میان آب جوشان، سپس آنان را در آتش افروخته دوزخ می سوزانند.'),
(4206, 40, 73, 'آن گاه به آنان گویند: معبودانی که [در الوهیت و ربوبیت] شریکان خدا می گرفتید کجایند؟'),
(4207, 40, 74, '[همان معبودانی که] به جای خدا [می پرستیدید]، می گویند: از نظر ما گم و ناپدید شدند، بلکه ما پیش از این چیزی را نمی پرستیدیم. این گونه خدا کافران را گمراه می کند.'),
(4208, 40, 75, 'این [عذاب ها] برای آن است که به ناحق در زمین شادی و سرمستی می کردید و به سبب آن است که مغرورانه به پایکوبی و خوشحالی می پرداختید.'),
(4209, 40, 76, 'از درهای دوزخ وارد شوید که در آن جاو دانه اید؛ پس چه بد است اقامت گاه متکبران.'),
(4210, 40, 77, 'نهایتاً [بر آزار اینان] شکیبا باش، بی تردید وعده خدا حق است. پس اگر بخشی از عذاب هایی را که به آنان وعده می دهیم به تو نشان دهیم، یا تو را پیش از آن از دنیا ببریم [تفاوتی ندارد، در هر صورت] به سوی ما بازگردانده می شوند [و کیفرشان را خواهند دید.]'),
(4211, 40, 78, 'قطعاً پیش از تو پیامبرانی فرستادیم؛ سرگذشت گروهی از آنان را برای تو حکایت کرده ایم، و سرگذشت برخی را بیان نکرده ایم. هیچ پیامبری را نسزد که جز به اذن خدا معجزه ای بیاورد؛ بنابراین هنگامی که فرمان خدا [به عذاب دشمنان لجوج] برسد، بر پایه حقّ و درستی داوری خواهد شد و آنجاست که اهل باطل زیان خواهند کرد.'),
(4212, 40, 79, 'خداست که چهارپایان را برای شما پدید آورد تا بر بعضی از آنها سوار شوید، و از [گوشت] بعضی از آنها بخورید؛'),
(4213, 40, 80, 'و برای شما در آنها سودهایی است، [آری آنها را آفرید] تا با [سوار شدن بر آنها و حمل بار و بنه خود] به مقصدی که در دل های شماست برسید، و بر آنها و بر کشتی ها حمل می شوید.'),
(4214, 40, 81, 'و همواره نشانه های [ربوبیت و قدرت] خود را به شما نشان می دهد، پس کدام یک از نشانه های خدا را انکار می کنید؟!'),
(4215, 40, 82, 'آیا در زمین گردش نکردند تا با تأمل بنگرند که سرانجام کسانی که پیش از آنان بودند [و سرکشی و تکبّر می کردند] چگونه بود؟ همانان که نفراتشان از اینان بیشتر و از ایشان نیرومندتر بودند، و در زمین آثاری پایدارتر [چون قلعه ها و خانه های استوار و کاخ های بسیار محکم] داشتند، ولی نفرات و قدرت و آثاری که همواره به دست می آوردند [چیزی از عذاب خدا را] از آنان دفع نکرد.'),
(4216, 40, 83, 'هنگامی که پیامبرانشان دلایل روشن برای آنان آوردند، به اندک دانشی که نزد خود داشتند خوشحال بودند [و غیر آن را چیزی به حساب نمی آوردند] ولی عذابی که همواره آن را به مسخره می گرفتند، آنان را احاطه کرد.'),
(4217, 40, 84, 'پس هنگامی که عذاب سخت ما را دیدند، گفتند: بی تردید به خدا ایمان آوردیم و به معبودانی که با او شریک قرار می دادیم، کافریم.'),
(4218, 40, 85, 'ولی زمانی که عذاب سخت ما را دیدند، ایمانشان سودی به آنان نداد. سنت خداست که از دیرباز نسبت به بندگانش جاری شده است [که ایمانشان را پس از دیدن عذاب نمی پذیرد] و آنجاست که کافران زیانکار شدند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حم'),
(4220, 41, 2, 'این کتابی است نازل شده از سوی خدا که رحمتش بی اندازه و مهربانی اش همیشگی است.'),
(4221, 41, 3, 'کتابی است که آیاتش در نهایت روشنی بیان شده است، [به زبانی] فصیح و گویا برای مردمی که اهل معرفت و آگاهی اند؛'),
(4222, 41, 4, 'مژده دهنده و بیم دهنده است. ولی بیشتر آنان [از این دریای معارف] روی گرداندند و به گوشِ [جان] نمی شنوند.'),
(4223, 41, 5, 'و گفتند: دل های ما از [درک] حقایقی که ما را به آن می خوانی در پوشش های سختی است، و در گوش های ما سنگینی است، و میان ما و تو پرده ای وجود دارد، بنابراین تو کار خود را انجام بده و ما هم کار خود را انجام می دهیم.'),
(4224, 41, 6, 'بگو: من بشری مانند شما هستم، [نه از جنسی دیگر که سخنم را نفهمید] به من وحی می شود که معبود شما فقط معبود یکتاست، پس [با دوری از افراط و تفریط] به سوی او رو کنید، و از او آمرزش بخواهید، و وای بر مشرکان؛'),
(4225, 41, 7, 'همانان که زکات نمی دهند و آخرت را انکار می کنند.'),
(4226, 41, 8, 'بی تردید کسانی که ایمان آورده و کارهای شایسته انجام داده اند، برای آنان پاداشی همیشگی است.'),
(4227, 41, 9, 'بگو: آیا شما به کسی که زمین را در دو روز آفرید، کفر می ورزید، و برای او همتایانی قرار می دهید؟! [ساحت مقدسش از این پیرایه ها پاک است] آن [توانمندی که زمین را آفرید] پروردگار جهانیان است.'),
(4228, 41, 10, 'و در روی زمین کوه های استواری پدید آورد، و در آن منافع فراوانی آفرید و رزق [روزی خوارانش] را در آن به مدت چهار دوره [بهار، تابستان، پاییز و زمستان] تقدیر کرد [آن هم] یکسان و به اندازه برای خواهندگان.'),
(4229, 41, 11, 'آن گاه آهنگ آفرینش آسمان کرد، در حالی که به صورت دود بود، پس به آن و به زمین گفت: خواه یا ناخواه بیایید. آن دو گفتند: فرمانبردار آمدیم.'),
(4230, 41, 12, 'پس آنها را در دو روز به صورت هفت آسمان به انجام رسانید [و محکم و استوار ساخت]، و در هر آسمانی کار آن را وحی کرد، و آسمان دنیا را با چراغ هایی آرایش دادیم و [از استراق سمع شیطان ها] حفظ کردیم، این است اندازه گیری توانای شکست ناپذیر و دانا.'),
(4231, 41, 13, 'اگر روی گرداندند، بگو: من شما را از صاعقه ای [مرگبار] چون صاعقه عاد و ثمود بیم می دهم.'),
(4232, 41, 14, 'هنگامی که پیامبران از پیش رو و پشت سرشان به سوی آنان آمدند [و گفتند:] که جز خدا را نپرستید. گفتند: اگر پروردگار ما می خواست [که ما مؤمن شویم و او را بپرستیم] فرشتگانی نازل می کرد [تا دعوتش را به ما ابلاغ کنند]؛ بنابراین ما به آنچه شما به آن فرستاده شده اید، کافریم.'),
(4233, 41, 15, 'اما قوم عاد به ناحق در زمین تکبّر کردند و گفتند: نیرومندتر از ما کیست؟ آیا ندانستند خدایی که آنان را آفرید از آنان نیرومندتر است؟ و [به گمان اینکه نیرومندتر از آنان وجود ندارد] همواره آیات ما را انکار می کردند.'),
(4234, 41, 16, 'سرانجام تندبادی سخت و سرد در روزهایی شوم بر آنان فرستادیم تا در زندگی دنیا عذاب خواری و رسوایی را به آنان بچشانیم، و بی تردید عذاب آخرت رسوا کننده تر است و آنان [در آن روز] یاری نخواهند شد.'),
(4235, 41, 17, 'و اما ثمودیان، پس آنان را هدایت کردیم، ولی آنان کوردلی را بر هدایت ترجیح دادند، پس به کیفر اعمالی که همواره مرتکب می شدند، بانگ عذاب خوارکننده آنان را فراگرفت؛'),
(4236, 41, 18, 'و کسانی را که ایمان آورده و همواره پرهیزکاری می کردند، نجات دادیم.'),
(4237, 41, 19, 'و [یاد کن] روزی را که دشمنان خدا به سوی آتش گرد آورده می شوند، پس آنان را از حرکت باز می دارند [تا گروه های دیگر به آنان ملحق شوند.]'),
(4238, 41, 20, 'وقتی به آتش می رسند، گوش و چشم و پوستشان به اعمالی که همواره انجام می دادند، گواهی می دهند.'),
(4239, 41, 21, 'و آنان به پوستشان می گویند: چرا بر ضد ما گواهی دادید؟ می گویند: همان خدایی که هر موجودی را به سخن آورد، ما را گویا ساخت، و او شما را نخستین بار آفرید و به سوی او بازگردانده می شوید.'),
(4240, 41, 22, 'و شما [هنگام ارتکاب گناه در دنیا] از اینکه مبادا گوش و چشم و پوستتان بر ضد شما گواهی دهند، پنهان نمی شدید، بلکه گمان کردید که خدا بسیاری از آنچه را که [در خلوت] مرتکب می شدید، نمی داند!!'),
(4241, 41, 23, 'و این گمانتان بود که به پروردگارتان بردید [و همان] شما را هلاک کرد، در نتیجه از زیانکاران شدید.'),
(4242, 41, 24, 'پس اگر [بر همین حال] ایستادگی کنند، جایگاهشان دوزخ است، و اگر [برای به دست آوردن خشنودی خدا] عذرخواهی کنند، عذرشان مورد پذیرش قرار نخواهد گرفت.'),
(4243, 41, 25, '[به خاطر کفر و طغیانشان] برای آنان همنشینانی [مفسد و تبهکار] گماشتیم که لذایذ مادی و شهوانی حال و آینده را در نظرشان زیبا و دلربا جلوه دادند، و فرمان عذاب بر آنان در [زمره] گروه هایی از جن و انس که پیش از آنان گذشتند، محقق و ثابت شد، آنان بی تردید زیانکارند.'),
(4244, 41, 26, 'کافران گفتند: به این قرآن گوش ندهید و [هنگام قرائتش] سخنان لهو و بیهوده [و سر و صداهای بی معنا] در آن افکنید [تا کسی نشنود!] شاید که پیروز شوید.'),
(4245, 41, 27, 'به یقین کافران را عذابی سخت می چشانیم، و بی تردید آنان را بر پایه بدترین اعمالی که همواره مرتکب می شدند، کیفر می دهیم.'),
(4246, 41, 28, 'این است کیفر دشمنان خدا که آتش است، برای آنان در آتش دوزخ سرایی جاودانه و همیشگی است، کیفری است [ویژه و سخت]، برای اینکه همواره آیات ما را انکار می کردند.'),
(4247, 41, 29, 'و کافران می گویند: پروردگارا! آنان که از گروه جنّ و انس ما را گمراه کردند، به ما نشان بده تا زیر پای خود گذاریم، برای اینکه از پست ترینان شوند.'),
(4248, 41, 30, 'بی تردید کسانی که گفتند: پروردگار ما خداست؛ سپس [در میدان عمل بر این حقیقت] استقامت ورزیدند، فرشتگان بر آنان نازل می شوند [و می گویند:] مترسید و اندوهگین نباشید و شما را به بهشتی که وعده می دادند، بشارت باد.'),
(4249, 41, 31, 'ما در زندگی دنیا و آخرت، یاران و دوستان شما هستیم، آنچه دلتان بخواهد، در بهشت برای شما فراهم است، و در آن هر چه را بخواهید، برای شما موجود است.'),
(4250, 41, 32, 'رزق آماده ای از سوی آمرزنده مهربان است.'),
(4251, 41, 33, 'و خوش گفتارتر از کسی که به سوی خدا دعوت کند و کار شایسته انجام دهد و گوید: من از تسلیم شدگان [در برابر فرمان ها و احکام خدا] هستم، کیست؟'),
(4252, 41, 34, 'نیکی و بدی یکسان نیست. [بدی را] با بهترین شیوه دفع کن؛ [با این برخورد متین و نیک] ناگاه کسی که میان تو و او دشمنی است [چنان شود] که گویی دوستی نزدیک و صمیمی است.'),
(4253, 41, 35, 'این بهترین شیوه را جز کسانی که [در زمینه خودسازی و تزکیه] پایداری کردند، نمی یابند، و جز کسانی که بهره بزرگی [از ایمان و تقوا] دارند به آن نمی رسند؛'),
(4254, 41, 36, 'و اگر وسوسه ای از سوی شیطان تو را تحریک کند [که از این بهترین شیوه دست برداری] به خدا پناه ببر؛ بی تردید او شنوا و داناست.'),
(4255, 41, 37, 'از نشانه های [ربوبیت و قدرت] او شب و روز و خورشید و ماه است؛ نه برای خورشید سجده کنید و نه برای ماه، برای خدایی که آنان را آفرید، سجده کنید، اگر می خواهید فقط او را بپرستید؛'),
(4256, 41, 38, 'و اگر [از پرستش خدای یکتا] تکبّر ورزند، پس [باید بدانند چنین نیست که پرستش کننده ای برای او وجود ندارد،] کسانی که نزد پروردگار تواند [از فرشتگانی که شمار آنان را جز خدا نمی داند] شب و روز برای او تسبیح می گویند و [از تسبیح گویی] ملول و خسته نمی شوند.'),
(4257, 41, 39, 'و از [دیگر] نشانه های او این است که تو زمین را خشک و بی گیاه می بینی، پس هنگامی که باران بر آن نازل می کنیم، به شدت به جنبش درآید و برآید. بی تردید کسی که زمین مرده را زنده کرد، یقیناً مردگان را زنده می کند؛ زیرا او بر هر کاری تواناست.'),
(4258, 41, 40, 'مسلماً کسانی که معانی و مفاهیم آیات ما را از جایگاه واقعی اش تغییر می دهند [و به تفسیر و تأویلی نادرست متوسل می شوند] بر ما پوشیده نیستند. آیا کسی را که در آتش می افکنند، بهتر است یا کسی که روز قیامت در حال ایمنی می آید؟ هر چه می خواهید انجام دهید، بی تردید او به آنچه انجام می دهید، بیناست.'),
(4259, 41, 41, 'کسانی که به این قرآن هنگامی که به سویشان آمد کافر شدند [به عذابی سخت دچار می شوند] بی تردید قرآن کتابی است شکست ناپذیر،'),
(4260, 41, 42, 'که هیچ باطلی از پیش رو و پشت سرش به سویش نمی آید، نازل شده از سوی حکیم و ستوده است.'),
(4261, 41, 43, 'جز آنچه به پیامبران پیش از تو گفته شده است، به تو گفته نمی شود؛ مسلماً پروردگارت صاحب آمرزش ودارای عذابی دردناک است؛'),
(4262, 41, 44, 'و اگر آن را قرآنی غیر عربی قرار داده بودیم قطعاً می گفتند: چرا آیاتش در نهایت روشنی بیان نشده است، آیا [قرآنی] غیر عربی [و نامفهوم] برای [مردمی] عرب زبان [و فصیح؟!] بگو: این کتاب برای کسانی که ایمان آورده اند، سراسر هدایت و درمان است، و کسانی که ایمان نمی آورند در گوششان سنگینی است، و آن [با همه روشنی و آشکاری اش] بر آنان پوشیده و نامفهوم است؛ اینانند که [گویی] از جایی دور ندایشان می دهند.'),
(4263, 41, 45, 'به یقین ما به موسی کتاب عطا کردیم پس در آن اختلاف شد [که از پیشگاه حق آمده یا ساختگی است]، و اگر از سوی پروردگارت فرمانی [بر مهلت یافتنشان] پیشی نگرفته بود، بی تردید میانشان [به نابودی و هلاکت] حکم شده بود؛ و اینان هم [چون قوم موسی] نسبت به قرآن در تردیدی سخت هستند.'),
(4264, 41, 46, 'کسی که کار شایسته انجام دهد، به سود خود اوست، و کسی که مرتکب زشتی شود به زیان خود اوست، و پروردگارت ستمکار به بندگان نیست.'),
(4265, 41, 47, 'آگاهی و دانش به قیامت [و اینکه چه زمانی برپا می شود و چه خواهد شد] فقط ویژه اوست؛ و میوه ها از غلاف هایشان بیرون نمی آیند، و هیچ ماده ای حامله نمی شود و وضع حمل نمی کند مگر به دانش او. و روزی که [خدا] مشرکان را ندا می دهد: شریکانی که برای من می پنداشتید، کجایند؟ می گویند: [پس از روشن شدن حقایق بر ما] قاطعانه به تو اعلام می کنیم که هیچ گواهی از میان ما [بر اینکه تو را شریکی هست] وجود ندارد؛'),
(4266, 41, 48, 'و آنچه را از پیش [به عنوان شریکان خدا] می پرستیدند از نظرشان گم و ناپدید می شود و یقین می کنند که آنان را هیچ راه گریزی [از عذاب] نیست.'),
(4267, 41, 49, 'انسان از درخواست خوشی و رفاه خسته نمی شود، واگر آسیبی به او رسد به شدت مأیوس [و] ناامید می شود؛'),
(4268, 41, 50, 'و اگر او را از سوی خود پس از آسیبی که به او رسیده خوشی و رفاه بچشانیم قاطعانه می گوید: این [خوشی و رفاه] ویژه من است [و به خاطر لیاقتم به من رسیده است] و گمان نمی کنم که قیامت برپا شود، و [به فرض برپا شدن] اگر به سوی پروردگارم باز گردانده شوم، برای من نزد او پاداشی نیکوتر خواهد بود! ما یقیناً کسانی را که کافر شدند به اعمالی که انجام داده اند، آگاه خواهیم کرد، و قطعاً از عذابی سخت به آنان می چشانیم.'),
(4269, 41, 51, 'و هنگامی که به انسان نعمت عطا می کنیم [از طاعت و عبادت] روی بر می گرداند و [با کبر و نخوت] از ما دور می شود، و چون آسیبی به او رسد [برای برطرف شدنش] به دعای فراوان و طولانی روی می آورد؛'),
(4270, 41, 52, 'بگو: به من خبر دهید: اگر [قرآن] از سوی خدا باشد و سپس شما به آن کافر شوید، چه کسی گمراه تر از آن خواهد بود که در مخالفتی دور [از منطق و صواب] قرار دارد؟'),
(4271, 41, 53, 'به زودی نشانه های خود را در کرانه ها و اطراف جهان و در نفوس خودشان به آنان نشان خواهیم داد تا برای آنان روشن شود که بی تردید او حق است. آیا کافی نیست که پروردگارت [با ظاهر کردن حقایق و دلایل] بر همه چیز گواه است [که تنها او آفریننده و بی نیاز است و غیر او مخلوق و از هر جهت نیازمند به اوست.]'),
(4272, 41, 54, 'آگاه باش! که آنان نسبت به دیدار [قیامت و محاسبه اعمال به وسیله] پروردگارشان در تردیدند. [و] آگاه باش! که یقیناً او به همه چیز [با قدرت و دانش بی نهایتش] احاطه دارد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حم'),
(4274, 42, 2, 'عسق'),
(4275, 42, 3, 'این گونه خدای توانای شکست ناپذیر و حکیم به سوی تو و کسانی [از پیامبران] که پیش از تو بودند، وحی می کند.'),
(4276, 42, 4, 'آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست، و او بلند مرتبه و بزرگ است.'),
(4277, 42, 5, 'نزدیک است آسمان ها از فرازشان [به سبب عظمت وحی] بشکافند و فرشتگان، پروردگارشان را همواره همراه با سپاس و ستایش تسبیح می گویند، و برای کسانی که در زمین هستند، درخواست آمرزش می کنند؛ آگاه باشید! بی تردید خدا بسیار آمرزنده و مهربان است.'),
(4278, 42, 6, 'و کسانی که غیر خدا را سرپرستان و یاران خود گرفته اند، خدا نگهبان و مراقب بر [اعمال و گفتار] آنان است و تو کارساز و وکیل بر آنان نیستی.'),
(4279, 42, 7, 'و این گونه قرآنی [به زبان] عربی [فصیح و گویا] به تو وحی کردیم تا [مردمِ] ام القری [شهر مکه] و کسانی را که پیرامون آن هستند، بیم دهی، و آنان را از روز جمع شدن [یعنی روز قیامت] که تردیدی در آن نیست بترسانی، [روزی که] گروهی در بهشت اند و گروهی در آتش سوزان.'),
(4280, 42, 8, 'اگر خدا می خواست آنان را [از روی جبر بر محور هدایت] امت واحدی قرار می داد، ولی [هدایت اجباری فاقد ارزش است] هر که را بخواهد [در صورتی که ستمکار به آیات خدا نباشد] در رحمت خود در آورد؛ و ستمکاران را هیچ دوست و یاوری [که از عذاب نجاتشان دهد] نیست.'),
(4281, 42, 9, 'آیا به جای او سرپرستان و معبودانی برای خود گرفته اند؟ در حالی که سرپرست و معبود واقعی خداست، و تنها اوست که مردگان را زنده می کند و فقط اوست که بر هر کاری تواناست؛'),
(4282, 42, 10, 'و آنچه را [از امور دین، عقاید و احکام] در آن اختلاف دارید، داوری اش با خداست. این است خدا پروردگار من، بر او توکل کردم و به او باز می گردم.'),
(4283, 42, 11, 'آفریننده آسمان ها و زمین است. از خودتان برایتان جفت هایی قرار داد، و از چهارپایان هم جفت هایی آفرید. شما را با این [تدبیر حکیمانه] زیاد می کند. هیچ چیزی مانند او نیست و او شنوا و بیناست.'),
(4284, 42, 12, 'کلیدهای آسمان ها و زمین فقط در سیطره مالکیّت اوست. رزق و روزی را برای هر کس که بخواهد وسعت می دهد و یا تنگ می گیرد. یقیناً او به همه چیز داناست.'),
(4285, 42, 13, 'از دین آنچه را به نوح سفارش کرده بود، برای شما تشریع کرد و آنچه را به تو وحی کردیم؛ و آنچه ابراهیم و موسی و عیسی را به آن توصیه نمودیم [این است] که دین را برپا دارید و در آن فرقه فرقه و گروه گروه نشوید. بر مشرکان دینی که آنان را به آن می خوانی گران است. خدا هر کس را بخواهد به سوی [این] دین جلب می کند، و هر کس را که به سوی او بازگردد هدایت می کند،'),
(4286, 42, 14, 'و [جامعه های دینی در طول تاریخ] فرقه فرقه و گروه گروه نشدند، مگر پس از آنکه [نسبت به حقّانیّت دین به وسیله کتاب های آسمانی] دانش و آگاهی به سویشان آمد، [این پراکندگی و تفرقه به سبب] حسد و دشمنی میان خودشان بود، و اگر از سوی پروردگارت فرمانی [بر مهلت یافتنشان] تا زمان معین پیشی نگرفته بود، بی تردید میانشان [به نابودی و هلاکت] حکم شده بود. و یقیناً کسانی که پس از آنان کتاب آسمانی [قرآن] را به میراث یافتند، نسبت به آن در تردیدی سخت هستند.'),
(4287, 42, 15, 'پس آنان را [به سوی همان آیینی که به تو وحی شده] دعوت کن، و همان گونه که مأموری [بر این دعوت] استقامت کن، و از هواهای نفسانی آنان پیروی مکن و بگو: به هر کتابی که خدا نازل کرده، ایمان آوردم، و مأمورم که در میان شما به عدالت رفتار کنم؛ خدا پروردگار ما و شماست؛ اعمال ما برای خود ما و اعمال شما برای خود شماست؛ دیگر میان ما و شما [پس از روشن شدن حقایق] هیچ حجت و برهانی نیست؛ خدا ما و شما را [در عرصه قیامت] جمع می کند، و بازگشت [همه] به سوی اوست.'),
(4288, 42, 16, 'کسانی که درباره خدا پس از آنکه دعوتش [از سوی مردم منصف با تکیه بر عقل و فطرت] اجابت شده مجادله و ستیزه می کنند، دلیلشان [بر ضد حقایق] نزد پروردگارشان باطل است و از سوی خدا خشمی بر آنان است و برای آنان عذابی سخت خواهد بود.'),
(4289, 42, 17, 'خداست که به حقّ و راستی کتاب و میزان [سنجش حق از باطل] را نازل کرد. و تو چه می دانی، شاید قیامت نزدیک باشد.'),
(4290, 42, 18, 'کسانی که به آن ایمان ندارند [از روی ریشخند] به آمدنش شتاب دارند، و کسانی که ایمان دارند از آن بیمناکند و می دانند بی تردید قیامت حق است. آگاه باشید! یقیناً کسانی که درباره قیامت همواره تردید می کنند، در گمراهی دور و درازی هستند.'),
(4291, 42, 19, 'خدا نسبت به بندگانش بسیار مهربان و نیکوکار است، هر که را بخواهد روزی می بخشد و او نیرومند و توانای شکست ناپذیر است.'),
(4292, 42, 20, 'کسی که زراعت آخرت را بخواهد، بر زراعتش می افزاییم و کسی که زراعت دنیا را بخواهد، اندکی از آن را به او می دهیم، ولی او را در آخرت هیچ بهره و نصیبی نیست.'),
(4293, 42, 21, 'آیا مشرکان و کافران معبودانی دارند که بی اذن خدا آیینی را برای آنان پایه گذاری کرده اند؟ [در صورتی که پایه گذاری آیین، حق ویژه خداست و کسی را نرسد که از نزد خود آیینی بسازد] اگر فرمان قاطعانه خدا بر مهلت یافتنشان نبود، مسلماً میانشان [به نابودی و هلاکت] حکم می شد؛ و بی تردید برای ستمکاران عذابی دردناک خواهد بود.'),
(4294, 42, 22, 'ستمکاران را [در قیامت] می بینی که از اعمالی که انجام داده اند، بسیار بیمناکند وهمان را که مرتکب شده اند، بر آنان فرود می آید و کسانی که ایمان آورده و کارهای شایسته انجام داده اند، در باغ های سرسبز بهشت اند، برای آنان هر چه را که بخواهند نزد پروردگارشان فراهم است؛ این همان فضل بزرگ است.'),
(4295, 42, 23, 'این است چیزی که خدا آن را به بندگانش که ایمان آورده و کارهای شایسته انجام داده اند، مژده می دهد. بگو: از شما [در برابر ابلاغ رسالتم] هیچ پاداشی جز مودّت نزدیکان را [که بنابر روایات بسیار اهل بیت ـ علیهم السلام ـ هستند] را نمی خواهم. و هر کس کار نیکی کند، بر نیکی اش می افزاییم؛ یقیناً خدا بسیار آمرزنده و عطاکننده پاداش فراوان در برابر عمل اندک است.'),
(4296, 42, 24, 'بلکه [منافقان سبک مغز] می گویند: [در نزول آیه مودت] بر خدا دروغ بسته است! پس اگر خدا بخواهد، بر دل تو مهر می نهد [تا از دروغ بستن باز ایستی، ولی تو از دروغ بستن بر خدا منزهی، این تهمت بزرگ است که این بیماردلان بر تو می بندند]. و خدا باطل را محو می کند و حق را با کلمات استوارش [و سخنان منطقی و مستدلش] پابرجا می سازد؛ یقیناً او به نیّات و اسرار سینه ها داناست.'),
(4297, 42, 25, 'و اوست که توبه را از بندگانش می پذیرد و از گناهان درمی گذرد و آنچه را انجام می دهید، می داند.'),
(4298, 42, 26, 'و درخواست کسانی را که ایمان آورده و کارهای شایسته انجام داده اند، اجابت می کند و از فضل و احسانش بر آنان می افزاید؛ و برای کافران عذابی سخت خواهد بود.'),
(4299, 42, 27, 'و اگر خدا روزی را بر بندگانش وسعت دهد، در زمین سرکشی و ستم کنند، ولی آنچه را بخواهد به اندازه نازل می کند؛ یقیناً او به بندگانش آگاه و بیناست.'),
(4300, 42, 28, 'و اوست که باران را پس از اینکه [مردم از آمدنش] نومید شدند، نازل می کند و رحمتش را می گستراند، و او سرپرست و یار [واقعی] و ستوده است.'),
(4301, 42, 29, 'و از نشانه های [ربوبیت و قدرت] او آفرینش آسمان ها و زمین است و [نیز] آنچه از جنبنده میان آن دو پراکنده است، و او هرگاه بخواهد بر جمع کردنشان تواناست.'),
(4302, 42, 30, 'و هر آسیبی به شما رسد به سبب اعمالی است که مرتکب شده اید، و از بسیاری [از همان اعمال هم] درمی گذرد.'),
(4303, 42, 31, 'و شما در زمین عاجزکننده [خدا] نیستید [تا بتوانید از دسترس قدرت او بیرون روید] و جز خدا هیچ سرپرست و یاوری برای شما نیست.'),
(4304, 42, 32, 'از نشانه های او کشتی های کوه آسا در میان دریاست [که به کمک باد در حرکت اند.]'),
(4305, 42, 33, 'اگر بخواهد باد را فرومی نشاند، در نتیجه کشتی ها به روی آب ساکن و بی حرکت می مانند؛ یقیناً در این واقعیت برای هر شکیبای سپاس گزاری نشانه هایی [بر قدرت و ربوبیت خدا] ست.'),
(4306, 42, 34, 'یا اگر بخواهد کشتی ها را [با سرنشینانش] به سبب گناهانی که مرتکب شده اند، نابود می کند و از بسیاری [از همان گناهان] می گذرد.'),
(4307, 42, 35, 'و تا کسانی که درآیات ما مجادله و ستیزه می کنند، بدانند که آنان را [از عرصه قدرت ما] هیچ گریزگاهی نیست.'),
(4308, 42, 36, 'پس آنچه [از کالا، وسایل و ابزار مادی] به شما داده اند، متاع [اندک و زودگذر] زندگی دنیاست، و آنچه [از بهره و پاداش] نزد خداست، برای کسانی که ایمان آورده اند و بر پروردگارشان توکل می کنند، بهتر و پایدارتر است.'),
(4309, 42, 37, 'و [همان] کسانی که از گناهان بزرگ و از کارهای زشت دوری می کنند و هنگامی که [به مردم] خشم می گیرند، راه چشم پوشی و گذشت را برمی گزینند؛'),
(4310, 42, 38, 'و آنان که دعوت پروردگارشان را اجابت کردند و نماز را برپا داشتند و کارشان در میان خودشان بر پایه مشورت است و از آنچه روزی آنان کرده ایم، انفاق می کنند؛'),
(4311, 42, 39, 'و آنان که هرگاه ستمی به آنان رسد [تسلیم ستم و ستمکار نمی شوند، بلکه از دشمن به حکم حق و برابر با قوانین اسلام] انتقام می گیرند؛'),
(4312, 42, 40, 'پاداش بدی [چون قتل و زخم زدن و اتلاف مال] مانند همان بدی است؛ ولی هر که بگذرد و [میان خود و طرف مقابلش را] اصلاح نماید. پاداشش بر عهده خداست؛ یقیناً خدا ستمکاران را دوست ندارد.'),
(4313, 42, 41, 'و کسانی که پس از ستم دیدنشان [به حکم حق و برابر قوانین اسلام] در مقام انتقام برآیند، ایرادی بر آنان نیست [و در شرع مقدس مجوزی وجود ندارد که حق آنان را باطل کنند.]'),
(4314, 42, 42, 'ایراد و محکومیت فقط متوجه کسانی است که به مردم ستم روا می دارند و به ناحق در زمین سرکشی می کنند، اینانند که برای آنان عذابی دردناک خواهد بود،'),
(4315, 42, 43, 'و کسی که [با قدرت داشتن بر انتقام به اختیار خود] شکیبایی ورزد و [از انتقام] گذشت کند، بی تردید این از اموری است که ملازمت بر آن از واجبات است،'),
(4316, 42, 44, 'و هر که را خدا [به کیفر کبر و عنادش] گمراه کند، او را پس از خدا هیچ سرپرست و یاوری نخواهد بود؛ و ستمکاران را [روز قیامت] می بینی چون عذاب را ببینند، می گویند: آیا راهی به سوی بازگشت [به دنیا] وجود دارد؟!'),
(4317, 42, 45, 'و آنان را می بینی که بر آتش عرضه می شوند، [در حالی که از شدت رسوایی سرافکنده اند] زیر چشمی به آن می نگرند. و اهل ایمان می گویند: یقیناً زیانکاران کسانی هستند که روز قیامت سرمایه وجود خود و کسانشان را تباه کرده اند. آگاه باشید! مسلماً ستمکاران در عذابی پایدار و جاودانه اند.'),
(4318, 42, 46, 'و در برابر خدا برای آنان سرپرستان و یاورانی که یاریشان دهد نخواهد بود؛ و هر که را خدا گمراه کند، او را هیچ راهی [به سوی نجات و سعادت] نیست.'),
(4319, 42, 47, 'پیش از آنکه روزی فرا رسد که از سوی خدا هیچ بازگشتی برای آن نیست، پروردگارتان را اجابت کنید، آن روز برای شما هیچ پناه گاهی و هیچ راه انکاری [نسبت به اعمالی که مرتکب شده اید] وجود ندارد؛'),
(4320, 42, 48, 'پس اگر [از دعوتت] روی برگردانند [اندوهگین مباش] ما تو را بر آنان نگهبان و مراقب نفرستاده ایم [تا آنان را به اجبار در دایره هدایت قرار دهی]، جز رساندن [پیام وحی] بر عهده تو نیست، و هنگامی که ما از سوی خود رحمتی [چون سلامت، امنیت و ثروت] به انسان بچشانیم، به آن سرمست و مغرور می شود، و اگر به سبب گناهانی که مرتکب شده اند آسیبی به آنان رسد [رحمت حق را فراموش می کنند]، بی تردید انسان بسیار ناسپاس است.'),
(4321, 42, 49, 'مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره خداست، هر چه را بخواهد می آفریند، به هر کس بخواهد دختر عطا می کند و به هر کس بخواهد پسر می بخشد؛'),
(4322, 42, 50, 'یا پسران و دختران را با هم به آنان می دهد و هر که را بخواهد نازا می کند؛ یقیناً او دانا و تواناست.'),
(4323, 42, 51, 'هیچ بشری را نسزد که خدا با او سخن گوید، مگر از راه وحی یا از پشت حجاب غیب یا رسولی [چون فرشته] می فرستد؛ پس فرشته به اذن او آنچه را بخواهد وحی می کند؛ یقیناً او بلند مرتبه و حکیم است.'),
(4324, 42, 52, 'و همان گونه [که بر پیامبران پیشین وحی کردیم] روحی را [چون قرآن] از امر خود به تو وحی کردیم. تو [پیش از این] نمی دانستی کتاب و ایمان چیست؟ ولی آن [کتاب] را نوری قرار دادیم که هر کس از بندگانمان را بخواهیم به وسیله آن هدایت می کنیم؛ بی تردید تو [مردم را] به راهی راست هدایت می نمایی.'),
(4325, 42, 53, 'راه آن خدایی که آنچه در آسمان ها و آنچه در زمین است، فقط در سیطره مالکیّت و فرمانروایی اوست. آگاه باشید! که همه امور به سوی خدا بازمی گردد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حم'),
(4327, 43, 2, 'سوگند به [این] کتاب روشنگر'),
(4328, 43, 3, 'که ما آن را [به زبان] عربی [فصیح و گویا] قرار دادیم تا [در آن] تعقّل کنند؛'),
(4329, 43, 4, 'و همانا که آن در امِّ الکتاب [که لوح محفوظ است] نزد ما بلند مرتبه و حکیم است.'),
(4330, 43, 5, 'آیا برای اینکه شما قومی اسراف کارید، قرآن را از شما دور کنیم و بازگردانیم؟! [نه این کار را نمی کنیم تا بندگانمان از هدایت محروم نشوند.]'),
(4331, 43, 6, 'و چه بسیار پیامبرانی که در میان پیشینیان [که مردمی اسراف کار بودند] فرستادیم [و به سبب اسرافشان از فرستادن پیامبر دریغ نکردیم؛]'),
(4332, 43, 7, 'ولی هیچ پیامبری به سوی آنان نیامد مگر اینکه او را همواره مسخره می کردند.'),
(4333, 43, 8, 'پس ما نیرومندتر از این اسراف کاران [زمان تو] را هلاک کردیم و سرگذشت پیشینیان [هلاک شده، در سوره های دیگر قرآن] گذشت.'),
(4334, 43, 9, 'و اگر از آنان بپرسی آسمان ها و زمین را چه کسی آفریده است؟ قطعاً خواهند گفت: آنها را همان توانای شکست ناپذیر و دانا آفرید.'),
(4335, 43, 10, 'همان که این زمین را محل آرامش شما قرار داد و برای شما در آن، راه ها پدید آورد تا [به اهداف و مقاصدتان] راه یابید.'),
(4336, 43, 11, 'و آنکه از آسمان آبی به اندازه نازل کرد، پس به وسیله آن سرزمینی مرده را زنده کردیم، [و] همین گونه [درقیامت از گورها] بیرون آورده می شوید؛'),
(4337, 43, 12, 'و آنکه همه جفت ها را آفرید، و برای شما از کشتی ها و چهارپایان و آنچه که سوارش می شوید پدید آورد،'),
(4338, 43, 13, 'تا بر پشت آنها قرار گیرید، سپس هنگامی که بر آنها سوار می شوید نعمت پروردگارتان را به یاد آورید و بگویید: منزّه [از هر عیب و نقصی] است کسی که این [وسایل سواری] را برای ما مسخّر و رام کرد، در حالی که ما را قدرت مسخّر کردن آنها نبود؛'),
(4339, 43, 14, 'و یقیناً ما به سوی پروردگارمان باز می گردیم؛'),
(4340, 43, 15, 'و [مشرکان سبک مغز] برای خدا از میان بندگانش قطعه ای و جزیی [به عنوان فرزند که از او جدا شده] قرار دادند؛ قطعاً انسان کفران کننده آشکاری است.'),
(4341, 43, 16, 'آیا از آنچه خدا می آفریند، دخترانی برای خود برگرفته و شما را به داشتن پسران برگزیده است؟'),
(4342, 43, 17, 'و [در صورتی که] چون یکی از آنان را به [ولادت] دختری که شبیه و هم جنس خدا قرار داده مژده دهند در حالی که [دلش] پر از اندوه و خشم است، صورتش سیاه می شود! [چون علاوه بر اینکه دخترداری را دوست ندارد از آن ننگ دارد، اما همین دختر را به عنوان هم جنس و فرزند خدا قلمداد می کند!!]'),
(4343, 43, 18, 'آیا کسی را [شریک خدا قرار داده اند] که در زر و زیور پرورش یافته است و [هنگام] مجادله و بحث بیانش روشن نیست؟'),
(4344, 43, 19, 'و فرشتگان را که بندگان [خدای] رحمان اند، به پندار خود جنس ماده قرارداده اند، آیا زمان آفرینش آنان حضور داشتند [که بر ماده بودنشان گواهی می دهند؟] به زودی گواهی آنان [در نامه اعمالشان] نوشته می شود و [درباره آن] مورد بازپرسی قرار خواهند گرفت.'),
(4345, 43, 20, 'و گفتند: اگر خدا می خواست ما آنان را نمی پرستیدیم. ادعایشان متکی بر هیچ [دلیل] و دانشی نیست [و مایه ای جز جهل و نادانی ندارد]، فقط حدس می زنند و دروغ می بافند؛'),
(4346, 43, 21, 'یا مگر ما پیش از قرآن کتابی به آنان داده ایم که [برای اثبات ادعایشان] به آن تمسک می جویند؟'),
(4347, 43, 22, '[نه] بلکه [هیچ دلیلی بر حقّانیّت بت پرستی و شرک خود ندارند، بلکه دلگرمی آنان به آیینشان این است که] گفتند: ما پدرانمان را بر آیینی یافتیم و مسلماً ما هم با پیروی از آثارشان ره یافته ایم.'),
(4348, 43, 23, '[پیروی کورکورانه و جاهلانه مخصوص اینان نیست] و به همین گونه پیش از تو در هیچ شهری بیم دهنده ای نفرستادیم، مگر آنکه سران خوشگذران و مست و مغرورش گفتند: ما پدرانمان را بر آیینی یافتیم و ما هم حتماً به آثارشان اقتدا می کنیم.'),
(4349, 43, 24, '[بیم دهنده] گفت: آیا هر چند من آیینی هدایت کننده تر از آیینی که پدرانتان را بر آن یافته اید، برایتان بیاورم؟ گفتند: ما به آیینی که به آن فرستاده شده اید، کافریم!'),
(4350, 43, 25, 'در نتیجه از آنان انتقام گرفتیم. پس با تأمل بنگر سرانجام تکذیب کنندگان چگونه بود.'),
(4351, 43, 26, 'و [یاد کن] هنگامی را که ابراهیم به پدر و قومش گفت: من بی تردید از آنچه می پرستید، بیزارم.'),
(4352, 43, 27, 'مگر کسی که مرا آفریده، که یقیناً او مرا هدایت می کند،'),
(4353, 43, 28, 'و توحید و یکتاپرستی را در نسل های بعد از خود حقیقتی پایدار قرار داد، باشد که آنان [به سوی توحید] بازگردند؛'),
(4354, 43, 29, '[نه اینکه بازنگشتند و من هم از آنان انتقام نگرفتم] بلکه اینان [که در مکه آلوده به بت پرستی هستند] و پدرانشان را [از انواع نعمت ها] برخوردار کردم تا آنکه حق و فرستاده ای روشنگر [چون قرآن و محمّد] به سویشان آمد؛'),
(4355, 43, 30, 'و هنگامی که حق به سویشان آمد، گفتند: این جادوست و ما به آن کافریم،'),
(4356, 43, 31, 'و گفتند: این قرآن چرا بر مردمی بزرگ از میان یکی از این دو شهر [مکه و طایف] نازل نشد؟!'),
(4357, 43, 32, 'آیا آنان هستند که رحمت پروردگارت را تقسیم می کنند؟ ما در زندگی دنیا معیشت آنان را میانشان تقسیم کرده ایم، و برخی را از جهت درجات [فکری و مادی] بر برخی برتری داده ایم تا برخی از آنان برخی دیگر را [در امر معیشت و سایر امور] به خدمت گیرند؛ و رحمت پروردگارت از آنچه آنان جمع می کنند، بهتر است.'),
(4358, 43, 33, 'و اگر [بهره مندی کفار از انواع نعمت های مادی و تهیدستی مؤمنان، سبب] این نبود که همه مردم [به خاطر ضعف ایمان و ناآگاه بودن به حقایق] بر محور کفر امتی واحد شوند، ما برای خانه های کافران به [خدای] رحمان سقف هایی از نقره قرار می دادیم و نردبان هایی که با آن بر بالای خانه ها برآیند.'),
(4359, 43, 34, 'و برای خانه هایشان [نیز] درها و تخت هایی [از نقره می ساختیم] که بر آن تکیه زنند،'),
(4360, 43, 35, 'و [برای خانه ها و زندگی آنان] زر و زیور [قرار می دادیم]، ولی همه این ها جز کالای اندک زندگی دنیا نیست، و آخرت نزد پروردگارت برای پرهیزکاران است؛'),
(4361, 43, 36, 'و هر کس خود را از یاد [خدای] رحمان به کوردلی و حجاب باطن بزند، شیطانی بر او می گماریم که آن شیطان ملازم و دمسازش باشد.'),
(4362, 43, 37, 'و بی تردید شیطان ها چنین کسانی را از راه خدا باز می دارند، در حالی که [با این گمراهی سخت] گمان می کنند راه یافتگان واقعی آنانند؛'),
(4363, 43, 38, 'تا زمانی که [در قیامت] نزد ما آیند [به شیطانش] گوید: ای کاش میان من و تو فاصله و دوری مشرق و مغرب بود، پس بد همنشینی بودی!'),
(4364, 43, 39, 'و [به آنان گویند: امروز این گفتگو و آرزوی دوری از شیطان] هرگز به شما سودی نمی دهد؛ زیرا [در دنیا] ستم ورزیدید [و] اکنون همه با هم در عذاب مشترک خواهید بود؛'),
(4365, 43, 40, 'پس آیا تو می توانی [دعوتت را] به کران بشنوانی، یا کوران و کسانی را که در گمراهی آشکارند هدایت کنی؟!'),
(4366, 43, 41, 'پس اگر تو را از دنیا ببریم، یقیناً از اینان انتقام خواهیم گرفت،'),
(4367, 43, 42, 'یا [اگر از دنیا نبریم] آنچه را از عذاب به آنان وعده کرده ایم به تو نشان خواهیم داد [در هر صورت ما آنان را عذاب می کنیم]، و بی تردید بر آنان چیره و مسلطیم؛'),
(4368, 43, 43, 'پس به آنچه بر تو وحی شده تمسک بجوی، یقیناً تو بر راهی راست قرار داری؛'),
(4369, 43, 44, 'و مسلماً [این] قرآن برای تو و قوم تو مایه تذکر [و شرف و عزت] است، و سپس [درباره آن] بازخواست خواهید شد.'),
(4370, 43, 45, 'و از پیامبرانی که پیش از تو فرستاده ایم بپرس که آیا به جای [خدایِ] رحمان معبودانی که پرستش شوند، قرار داده ایم؟!'),
(4371, 43, 46, 'و به راستی ما موسی را با نشانه های خود به سوی فرعون و سران و اشراف قومش فرستادیم، پس گفت: من فرستاده پروردگار جهانیانم؛'),
(4372, 43, 47, 'نهایتاً چون معجزه های ما را برای آنان آورد ناگاه آنان [از روی ریشخند] به آنان می خندیدند.'),
(4373, 43, 48, 'ما هیچ معجزه ای را به آنان نشان نمی دادیم مگر آنکه از مشابهش بزرگ تر بود، و آنان را به عذاب دنیایی گرفتار کردیم، باشد که بازگردند.'),
(4374, 43, 49, 'و [وقتی گرفتار شدند] گفتند: ای جادوگر! پروردگارت را بر پایه عهدی که با تو کرده [که اگر ایمان آوریم عذاب را از ما بردارد] برای ما بخوان، که بی تردید ما هدایت خواهیم یافت.'),
(4375, 43, 50, 'هنگامی که عذاب را از آنان برطرف می کردیم، ناگاه پیمان می شکستند.'),
(4376, 43, 51, 'و فرعون در میان قومش ندا داد: گفت: ای قوم من! آیا حکومت و پادشاهی مصر ویژه من نیست و این نهرها از زیر [کاخ های] من به فرمان من روان نیستند؟ آیا [عظمت و حشمت من و فقر و تهیدستی موسی را] نمی بینید؟!'),
(4377, 43, 52, 'مگر نه این است که من از این کسی که خوار و بی مقدار است و نمی تواند روشن و گویا سخن گوید، بهترم؟'),
(4378, 43, 53, '[اگر موسی، عزت، سربلندی، کرامت و شخصیت داشت] پس چرا دستبندهای زرین و طلا بر او آویخته نشده است؟ یا چرا فرشتگانی [برای اثبات نبوتش] همراه او نیامده اند؟'),
(4379, 43, 54, 'پس او قومش را سبک مغز شمرد [و آنان را با وسوسه و اغواگری فریفت و خوارشان کرد] در نتیجه از او اطاعت کردند؛ زیرا آنان مردمی فاسق و نافرمان بودند.'),
(4380, 43, 55, 'چون ما را به خشم آوردند از آنان انتقام گرفتیم، پس همه را غرق کردیم.'),
(4381, 43, 56, 'در نتیجه آنان را پیشگامان [دوزخیان] و عبرتی برای آیندگان قرار دادیم.'),
(4382, 43, 57, 'و چون فرزند مریم [در آیات قرآن به نداشتن پدر به عنوان روح خدا و کلمة الله] مثل زده شد، ناگهان قوم [مشرک و بت پرست] تو نسبت به آن به مسخره و خنده فریاد برداشتند،'),
(4383, 43, 58, 'و گفتند: آیا معبودان ما بهترند یا او. [ولی] آنان این مقایسه را به عنوان مثل برای تو نزدند مگر از روی جدال بی منطق و ستیزه جویی [تو معبود بودن عیسی را نپذیرفته ای]، بلکه اینان گروهی ستیزه جو [و در جدال باطل بسیار سرسخت] هستند.'),
(4384, 43, 59, 'او نبود جز بنده ای که به او نعمت [نبوّت و معجزات را] عطا کردیم و وی را برای بنی اسرائیل نشانه ای بزرگ [از قدرت خود] قرار دادیم [تا به وسیله او حق برای آنان روشن شود.]'),
(4385, 43, 60, '[ولادت، نبوّت و معجزات عیسی از قدرت ما عجیب نیست] و ما اگر بخواهیم برخی از شما را در زمین فرشتگانی قرار می دهیم که جانشین [صاحب کمالاتی چون خود] باشند.'),
(4386, 43, 61, 'و عیسی [با ولادتی که ویژه او بود، و با معجزاتی چون شفا دادن کور مادرزاد، علاج بیماری برص، زنده کردن مردگان و دمیدن روح در مجسمه گلی] سبب یقین به قیامت است، پس هرگز در وقوع آن تردید مکنید، و از من پیروی نمایید که این است راه راست.'),
(4387, 43, 62, 'و شیطان شما را [از راه راست] باز ندارد، بی تردید او برای شما دشمنی آشکار است.'),
(4388, 43, 63, 'و هنگامی که عیسی دلایل روشن آورد، گفت: به راستی من برای شما حکمت آوردم، و تا برای شما [حکم] برخی [از عقاید و احکام] را که در آن اختلاف می کنید بیان کنم؛ بنابراین از خدا پروا کنید و از من اطاعت نمایید.'),
(4389, 43, 64, 'بی تردید خدا پروردگار من و پروردگار شماست، پس او را بپرستید که این است راه راست.'),
(4390, 43, 65, 'از میان امت عیسی گروه هایی [درباره او و دینش] اختلاف کردند [و هر یک چون نسطوریه، ملکانیه، مرقوسیه و شمعونیه به عقیده ای خلاف حق پای بند شدند، و آیینی غیر آیین مسیح را به عنوان آیین مسیح برگزیدند] پس وای بر کسانی که [به آیات خدا] ستم ورزیدند از عذاب روزی دردناک.'),
(4391, 43, 66, 'آیا جز این انتظاری دارند که ناگاه قیامت در رسد در حالی که نمی فهمند؟'),
(4392, 43, 67, 'در آن روز دوستان دشمن یکدیگرند مگر پرهیزکاران.'),
(4393, 43, 68, '[خدا آن روز به آنان گوید:] ای بندگان من! امروز نه بیمی بر شماست و نه اندوهگین می شوید؛'),
(4394, 43, 69, 'همانان که به آیات ما ایمان آوردند و همواره تسلیم [فرمان ها و احکام] ما بودند.'),
(4395, 43, 70, '[ندا آید:] شما و همسرانتان در نهایت خوشحالی و شادمانی به بهشت درآیید؛'),
(4396, 43, 71, 'ظرف هایی از طلا [که پر از طعام است] و جام هایی [زرین که پر از شراب طهور است] گرداگرد آنان می گردانند، و در آنجا آنچه دل ها می خواهد و چشم ها از آن لذّت می برد، آماده است، و شما [ای پرهیزکاران!] در آن جاودانه اید.'),
(4397, 43, 72, 'این بهشتی است که شما به پاداش اعمالی که همواره انجام می دادید، میراث یافتید.'),
(4398, 43, 73, 'شما را در آنجا میوه هایی فراوان است که از آنها می خورید.'),
(4399, 43, 74, 'بی تردید گناهکاران در عذاب دوزخ جاودانه اند.'),
(4400, 43, 75, '[عذاب را] از آنان سبک نگردانند و آنان در آنجا [از نجات خود] نومیدند،'),
(4401, 43, 76, 'و ما بر آنان ستم نکردیم، ولی آنان خود ستمکار بودند،'),
(4402, 43, 77, 'و فریاد می زنند: ای مالکِ [دوزخ! بگو:] پروردگارت ما را بمیراند [تا از این عذاب نجات یابیم]. [مالک] می گوید: یقیناً شما ماندنی هستید.'),
(4403, 43, 78, 'به راستی ما حق را برای شما آوردیم، ولی بیشتر شما حق را خوش نداشتید،'),
(4404, 43, 79, 'بلکه آنان کار [توطئه و نیرنگ بر ضد حق] را محکم کردند، پس ما هم کار [عذاب] را بر آنان محکم می کنیم.'),
(4405, 43, 80, 'آیا آنان گمان می کنند که ما رازشان و سخنان در گوشی آنان را نمی شنویم؟ چرا می شنویم و فرستادگان ما [که فرشتگان نویسنده اعمال اند] نزد آنان حاضرند [و اعمالشان و رازهایشان را] می نویسند.'),
(4406, 43, 81, '[به مشرکان سبک مغز] بگو: اگر برای [خدای] رحمان فرزندی بود، من [در این امت] نخستین پرستنده او بودم.'),
(4407, 43, 82, 'منزّه است پروردگار آسمان ها و زمین، پروردگار عرش از آنچه [درباره او] توصیف می کنند.'),
(4408, 43, 83, 'آنان را واگذار تا [در گفتار و کردار باطل] فرو روند و [با کالای بی ارزش دنیا] سرگرم بازی شوند تا آن روزشان را که وعده داده می شوند دیدار کنند.'),
(4409, 43, 84, 'و اوست که در آسمان ها معبود [واقعی] است و در زمین هم معبود [واقعی] است و او حکیم و داناست.'),
(4410, 43, 85, 'همیشه سودمند و با برکت است کسی که مالکیّت و فرمانروایی آسمان ها و زمین و آنچه میان آنهاست، فقط در سیطره اوست و آگاهی [به همه شؤون] قیامت نزد اوست و به او بازگردانده می شوید.'),
(4411, 43, 86, 'و کسانی را که به جای خدا می پرستند، اختیار شفاعت ندارند، اختیار شفاعت فقط با کسانی است که [از روی بصیرت] شهادت به حق داده اند و آنان [حقیقت حال کسانی را که می خواهند برای آنان شفاعت کنند] می دانند.'),
(4412, 43, 87, 'و اگر از آنان بپرسی: چه کسی آنان را آفریده است؟ یقیناً می گویند: خدا. پس چگونه [از حق به باطل] منحرف می شوند؟'),
(4413, 43, 88, 'و [چگونه از شکایت پیامبر بی خبرند که] می گوید: پروردگارا! اینان گروهی هستند که ایمان نمی آورند؛'),
(4414, 43, 89, 'بنابراین از آنان روی بگردان و سلام جدایی [را به آنان] بگو، پس [سرانجام شقاوت بار خود و نتیجه کفر و عنادشان را] خواهند دانست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حم'),
(4416, 44, 2, 'سوگند به [این] کتاب روشنگر؛'),
(4417, 44, 3, 'به راستی ما آن را در شبی پربرکت نازل کردیم؛ زیرا که همواره بیم دهنده بوده ایم؛'),
(4418, 44, 4, 'در آن شب هر کار استواری [به اراده خدا] فیصله می یابد.'),
(4419, 44, 5, '[نزول قرآن] کاری است [که] از نزد ما [صورت پذیرفته است؛] زیرا ما همواره فرستنده [وحی و پیامبران] بوده ایم.'),
(4420, 44, 6, '[همه این واقعیات] به سبب رحمتی از سوی پروردگار توست؛ بی تردید او شنوا وداناست.'),
(4421, 44, 7, '[همان] پروردگار آسمان ها و زمین و آنچه میان آنهاست، اگر یقین دارید.'),
(4422, 44, 8, 'هیچ معبودی جز او نیست، زنده می کند و می میراند، پروردگار شما وپروردگار پدران پیشین شماست.'),
(4423, 44, 9, '[نه اینکه یقین ندارند] بلکه آنان در شک اند [و با گفتار و کردارشان با حقایق] بازی می کنند.'),
(4424, 44, 10, 'پس به انتظار روزی باش که آسمان دودی آشکار بیاورد،'),
(4425, 44, 11, 'که همه مردم را فرا می گیرد، این عذابی دردناک است.'),
(4426, 44, 12, '[همه می گویند:] پروردگارا! این عذاب را از ما برطرف کن که ما ایمان می آوریم'),
(4427, 44, 13, '[در آن موقعیت سخت] چگونه برای آنان جای پند گرفتن و هوشیاری است و حال آنکه [پیش از این عذاب] پیامبری روشنگر برای آنان آمد [و پند نگرفتند.]'),
(4428, 44, 14, 'آن گاه از او روی گرداندند و گفتند: تعلیم یافته ای دیوانه است!!'),
(4429, 44, 15, 'مدتی اندک عذاب را برطرف می کنیم، ولی باز شما [به همان عقاید بی پایه و اعمال زشت] برمی گردید!'),
(4430, 44, 16, '[ما از آنان انتقام خواهیم گرفت آن] روزی که آنان را با قدرتی بسیار سخت بگیریم؛ زیرا که ما انتقام گیرنده ایم.'),
(4431, 44, 17, 'و همانا پیش از آنان قوم فرعون را آزمودیم، و پیامبری بزرگوار برای آنان آمد،'),
(4432, 44, 18, 'که [به آنان گفت:] بندگان خدا را [که به آزار و شکنجه شما گرفتارند] به من واگذارید؛ زیرا من برای شما فرستاده ای امینم.'),
(4433, 44, 19, 'و در برابر خدا تکبّر نکنید که من برای شما دلیلی روشن آورده ام.'),
(4434, 44, 20, 'و من به پروردگارم و پروردگار شما از اینکه مرا سنگسار کنید [یا برانید یا متهم کنید] پناه می برم؛'),
(4435, 44, 21, 'و اگر به من ایمان نمی آورید [از آزردن من و باز داشتن مردم از ایمان آوردن] کناره گیری کنید.'),
(4436, 44, 22, '[فرعونیان دست از آزردنش برنداشتند و مردم را از ایمان آوردن مانع شدند] پس پروردگارش را خواند که اینان قومی گناهکارند.'),
(4437, 44, 23, '[گفتم:] بندگانم را شبانه حرکت ده؛ زیرا شما مورد تعقیب هستید،'),
(4438, 44, 24, 'و دریا را با [همان] راه های گشاده [که برای عبور شما باز کردیم] پشت سر بگذار [تا فرعونیان هم در آن درآیند]؛ زیرا آنان سپاهی محکوم به غرق شدن هستند.'),
(4439, 44, 25, 'چه بسیار بوستان ها و چشمه سارانی که آنان [پس از خود] بر جای گذاشتند،'),
(4440, 44, 26, 'و کشتزار و جایگاه های نیکو و باارزش،'),
(4441, 44, 27, 'و نعمت هایی که با آسایش و خوشی از آن برخوردار بودند.'),
(4442, 44, 28, '[آری، سرانجام کارشان] چنین بود و ما همه آنها را به قومی دیگر به میراث دادیم.'),
(4443, 44, 29, 'پس نه آسمان برآنان گریست و نه زمین و [هنگام نزول عذاب هم] مهلت نیافتند.'),
(4444, 44, 30, 'همانا ما بنی اسرائیل را از آن عذاب خوارکننده نجات دادیم.'),
(4445, 44, 31, 'از فرعون که متکبری سرکش از زمره اسراف کاران بود،'),
(4446, 44, 32, 'و آنان را از روی آگاهی بر جهانیان [زمان خودشان] برگزیدیم؛'),
(4447, 44, 33, 'و به آنان از آیات و معجزات آنچه را که در آن آزمایشی آشکار بود، عطا کردیم.'),
(4448, 44, 34, 'اینان [که شرک و کفر سراپای وجودشان را فرا گرفته است] با اصرار [به اهل ایمان] می گویند:'),
(4449, 44, 35, 'پایان زندگی جز همین مرگ نخستین نیست و ما [پس از این مرگ] برانگیخته نخواهیم شد.'),
(4450, 44, 36, 'اگر شما [در زمینه زنده شدن مردگان] راستگویید [با درخواست از خدا] پدران ما را [زنده کنید و نزد ما] بیاورید [تا ما به زنده شدن مردگان یقین پیدا کنیم!!]'),
(4451, 44, 37, 'آیا اینان [در قدرت و شوکت] برترند یا قوم تُبَّع، و کسانی که پیش از آنان بودند؟ ما همه آنان را هلاک کردیم؛ زیرا مجرم بودند؛'),
(4452, 44, 38, 'و ما آسمان ها و زمین و آنچه را میان آن دو است، به بازی نیافریده ایم؛'),
(4453, 44, 39, 'ما آن دو را جز به درستی و راستی به وجود نیاورده ایم، ولی بیشترشان [به حقایق] معرفت و آگاهی ندارند.'),
(4454, 44, 40, 'بی تردید روز جدایی [حق از باطل، مؤمن از کافر و پاک از ناپاک] وعده گاه همه آنهاست؛'),
(4455, 44, 41, 'همان روزی که هیچ دوستی چیزی از عذاب را از دوستش دفع نمی کند، و چون [آلوده به شرک و کفرند] یاری نمی شوند؛'),
(4456, 44, 42, 'مگر کسی که خدا او را مورد رحمت قرار داده است؛ زیرا او توانای شکست ناپذیر و مهربان است'),
(4457, 44, 43, 'همانا درخت زقّوم،'),
(4458, 44, 44, 'خوراک گنهکار است،'),
(4459, 44, 45, 'مانند مس گداخته شده در شکم ها می جوشد،'),
(4460, 44, 46, 'چون جوشیدن آب جوشان'),
(4461, 44, 47, '[گفته می شود:] این گنهکار را بگیرید و او را به زور به وسط دوزخ بکشانید.'),
(4462, 44, 48, 'آن گاه از عذاب آب جوشان بر سرش فرو ریزید؛'),
(4463, 44, 49, '[و بگویید:] بچش که تو همان ارجمند و بزرگواری!!'),
(4464, 44, 50, 'به یقین این همان چیزی است که همواره درباره آن تردید می کردید.'),
(4465, 44, 51, 'مسلماً پرهیزکاران در جایگاه امنی خواهند بود.'),
(4466, 44, 52, 'در میان بوستان ها و چشمه سارها؛'),
(4467, 44, 53, 'لباس هایی از حریر نازک و دیبای ضخیم می پوشند در حالی که برابر هم می نشینند.'),
(4468, 44, 54, '[آری سرانجام کار پرهیزکاران] چنین است، و حور العین را به همسری آنان درآوریم،'),
(4469, 44, 55, 'در آنجا هر گونه میوه ای را که بخواهند می طلبند و می خورند، در حالی که [از هر جهت] ایمن و آسوده خاطرند؛'),
(4470, 44, 56, 'در آنجا مرگ را نمی چشند، مرگ آنان همان مرگی بود که در دنیا چشیدند، و خدا آنان را از عذاب دوزخ مصون می دارد.'),
(4471, 44, 57, '[این] فضل و احسانی است از سوی پروردگار تو، [و] این همان کامیابی بزرگ است.'),
(4472, 44, 58, 'پس جز این نیست که ما [فهم] قرآن را با زبان تو [که زبانی فصیح و گویاست] آسان ساختیم تا آنان متذکّر و هوشیار شوند.'),
(4473, 44, 59, '[ولی اگر متذکّر و هوشیار نشدند] پس به انتظار باش که مسلماً آنان هم منتظرند [که سرانجام کار چه خواهد شد؟ سرانجام نصرت و پیروزی خدا برای تو و عذاب دنیا و آخرت برای آنان است.]');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حم'),
(4475, 45, 2, 'این کتاب نازل شده از سوی خدای توانای شکست ناپذیر وحکیم است.'),
(4476, 45, 3, 'به یقین در آسمان ها و زمین برای مؤمنان نشانه هایی [بر ربوبیت، حکمت و قدرت خدا] ست؛'),
(4477, 45, 4, 'و در آفرینش شما و آنچه از جنبندگان منتشر و پراکنده می کند، برای اهل یقین نشانه هایی [بر ربوبیت، حکمت و قدرت خدا] ست؛'),
(4478, 45, 5, 'و در رفت و آمد شب و روز و آنچه را از رزق و روزی [چون باران و برف] از آسمان نازل کرده و به وسیله آن زمین را پس از مردگی اش زنده کرده است، و در گرداندن بادها [از سویی به سویی] برای مردمی که تعقّل می کنند، نشانه هایی است.'),
(4479, 45, 6, 'این ها آیات خداست که به حقّ و راستی بر تو می خوانیم؛ پس [اگر به این آیات ایمان نیاوردند] به کدام سخن بعد از [سخن] خدا و نشانه هایش ایمان می آورند؟'),
(4480, 45, 7, 'وای بر هر دروغگوی گنهکار،'),
(4481, 45, 8, 'که آیات خدا را در حالی که بر او می خوانند می شنود، آن گاه از روی سرکشی و تکبّر چنان که گویی آن را نشنیده است [بر کفر، عناد، دشمنی و مخالفتش] پافشاری می کند؛ پس او را به عذابی دردناک بشارت ده.'),
(4482, 45, 9, 'و هنگامی که به چیزی از آیات ما آگاه می شود، آن را به مسخره گیرد؛ اینانند که برای آنان عذاب خوارکننده ای خواهد بود.'),
(4483, 45, 10, 'پیش رویشان دوزخ است، و آنچه [از ثروت، مقام، یار و یاور] به دست آورده اند و آنچه را به جای خدا سرپرستان و معبودان خود گرفته اند، چیزی از عذاب را از آنان دفع نمی کند، و برای آنان عذابی بزرگ است.'),
(4484, 45, 11, 'این [قرآن] سراسر هدایت است؛ و کسانی که به آیات پروردگارشان کافر شدند، آنان را عذابی است از نوع عذابی بسیار سخت و دردناک.'),
(4485, 45, 12, 'خداست که دریا را برای شما مسخّر و رام کرد تا کشتی ها به فرمانش در آن روان شوند و تا شما [با سفرهای دریایی] از فضل و رزق و روزی اش بهره جویید و تا شما [بر نعمت هایش] سپاس گزاری کنید.'),
(4486, 45, 13, 'و همه آنچه را در آسمان هاست و آنچه را در زمین است از سوی خود برای شما مسخّر و رام کرد؛ بی تردید در این امور برای مردمی که می اندیشند، نشانه هایی [بر ربوبیت، حکمت و قدرت خدا] ست.'),
(4487, 45, 14, 'به مؤمنان بگو: از کسانی که به روزهای خدا [چون روز آزمایش بندگان، روز پیروزی اهل ایمان، شکست دشمنان و روز قیامت] امید ندارند، گذشت کنند، تا [خدا] مردمی را [که تهیدست از عقاید پاک و کردار شایسته هستند، در روزی از روزهایش] به خاطر گناهانی که همواره مرتکب می شوند، کیفر دهد.'),
(4488, 45, 15, 'هرکس کار شایسته ای انجام دهد، به سود خود اوست و هرکس مرتکب بدی شود به زیان خود اوست، سپس به سوی پروردگارتان باز گردانده می شوید.'),
(4489, 45, 16, 'و همانا ما به بنی اسرائیل کتاب و حکومت و نبوّت عطا کردیم، و از پاکیزه ها به آنان روزی دادیم و آنان را بر جهانیان [روزگار خودشان] برتری بخشیدیم.'),
(4490, 45, 17, 'و دلایل روشنی [در امر نبوّت و دین] در اختیار آنان قرار دادیم، پس آنان [در امر نبوّت و دین] اختلاف نکردند مگر پس از آنکه [به وسیله وحی] دانش و آگاهی برای آنان آمد، اختلافشان از روی حسادت و برتری جویی در میان خودشان بود؛ بی تردید پروردگارت روز قیامت درباره آنچه همواره در آن اختلاف می کردند میانشان داوری خواهد کرد.'),
(4491, 45, 18, 'سپس تو را [در امر نبوّت و دین] بر آیینی ویژه و کامل قرار دادیم؛ بنابراین از آن پیروی کن و از هواهای نفسانی کسانی که [به حقایق] معرفت و آگاهی ندارند، پیروی مکن.'),
(4492, 45, 19, 'آنان در برابر خدا نمی توانند چیزی [از گرفتاری ها و بلاها] را از تو دفع کنند؛ و همانا ستمکاران یار و یاور یکدیگرند و خدا یار و یاور پرهیزکاران است.'),
(4493, 45, 20, 'این [قرآن و آیین] وسایل بینایی و بصیرت برای مردم و سراسر هدایت و رحمت برای گروهی است که [حقایق را] باور دارند.'),
(4494, 45, 21, 'آیا کسانی که مرتکب گناهان شدند، گمان دارند آنان را مانند کسانی که ایمان آورده و کارهای شایسته انجام داده اند، قرار می دهیم که زندگی و مرگشان یکسان باشد؟ چه بد داوری می کنند.'),
(4495, 45, 22, 'خدا آسمان ها و زمین را به حق آفرید، و تا هر کس در برابر اعمالی که انجام داده است، پاداش یابد و آنان مورد ستم قرار نمی گیرند.'),
(4496, 45, 23, 'پس آیا کسی که معبودش را هوای نفسش قرار داده دیدی؟ و خدا او را از روی علم و آگاهی خود [بر اینکه شایسته هدایت نیست] گمراه کرد، و بر گوش و دلش مُهرِ [تیره بختی] نهاد، و بر چشم [دلش] پرده ای قرار داده است، پس چه کسی است که بعد از خدا او را هدایت کند؟ آیا متذکّر [حقایق] نمی شوید؟'),
(4497, 45, 24, 'گفتند: زندگی و حیاتی جز همین زندگی و حیات دنیای ما نیست که [همواره گروهی از ما] می میریم و [گروهی] زندگی می کنیم، و ما را فقط روزگار هلاک می کند. آنان را نسبت به آنچه می گویند یقینی در کار نیست، آنان فقط حدس و گمان می زنند.'),
(4498, 45, 25, 'چون آیات روشن ما [درباره معاد و زنده شدن مردگان] بر آنان خوانده شود، برهان و دلیلی جز این ندارند که می گویند: اگر شما [در زمینه زنده شدن مردگان] راستگویید [با درخواست از خدا] پدران ما را [زنده کنید و] نزد ما بیاورید [تا به زنده شدن مردگان یقین پیدا کنیم.]'),
(4499, 45, 26, 'بگو: خدا شما را [ابتدا از لابه لای عناصر مرده برای قرار گرفتن در دنیا] حیات می بخشد، سپس می میراند، آن گاه همه شما را به روز قیامت که هیچ شکی در آن نیست گرد می آورد، ولی بیشتر مردم [به این حقایق] معرفت و آگاهی ندارند.'),
(4500, 45, 27, 'و مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره خداست، و روزی که قیامت برپا شود، آن روز است که اهل باطل زیان خواهند دید،'),
(4501, 45, 28, 'و هر امتی را در آن روز به زانو درافتاده می بینی، [آن روز] هر امتی به سوی نامه اعمالش خوانده می شود [و به آنان می گویند:] امروز همان اعمالی که همواره انجام می دادید به شما پاداش می دهند.'),
(4502, 45, 29, 'این نوشته ماست که بر پایه حقّ و درستی بر ضد شما سخن می گوید؛ زیرا ما آنچه را همواره انجام می دادید، می نوشتیم.'),
(4503, 45, 30, 'و اما کسانی که ایمان آورده و کارهای شایسته انجام داده اند، پس پروردگارشان آنان را در رحمت خود درآورد؛ این همان کامیابی آشکار است،'),
(4504, 45, 31, 'و اما کسانی که کفر ورزیدند [به آنان گفته می شود:] مگر آیات من همواره بر شما خوانده نمی شد؟ ولی شما [نسبت به پذیرش آن] تکبّر ورزیدید و قومی گنهکار بودید.'),
(4505, 45, 32, 'وچون می گفتند: یقیناً وعده خدا حق است و در وقوع قیامت هیچ شکی نیست، می گفتید: ما نمی دانیم قیامت چیست [و برپا شدنش را گمان نمی بریم] مگر گمانی ضعیف و غیر قابل اعتماد، و نمی توانیم به آن یقین پیدا کنیم!'),
(4506, 45, 33, 'در آن روز همه اعمال بدی که مرتکب شده اند، برای آنان آشکار می شود و عذابی که همواره آن را مسخره می کردند، آنان را احاطه می کند.'),
(4507, 45, 34, 'و [به آنان] گویند: امروز شما را از یاد می بریم، همان گونه که شما [در دنیا] دیدار امروزتان را از یاد بردید، و جایگاهتان آتش است و شما را یار و یاوری نخواهد بود.'),
(4508, 45, 35, 'این [عذاب] برای این است که شما آیات خدا را به مسخره گرفته اید و زندگی دنیا، شما را فریفت؛ پس امروز نه آنان را از آتش بیرون آورند و نه از آنان می خواهند که [برای به دست آوردن خشنودی خدا] عذرخواهی کنند.'),
(4509, 45, 36, 'نهایتاً همه ستایشها مخصوص خداست که پروردگار آسمان ها و پروردگار زمین و پروردگار جهانیان است؛'),
(4510, 45, 37, 'و کبریایی و عظمت در آسمان ها و زمین ویژه اوست، و او توانای شکست ناپذیر و حکیم است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حم'),
(4512, 46, 2, 'این کتاب نازل شده از سوی خدای توانای شکست ناپذیر حکیم است.'),
(4513, 46, 3, 'ما آسمان ها و زمین و آنچه را میان آن دوست، جز به حقّ و تا سرآمدی معین نیافریدیم، و کافران از آنچه بیمشان داده اند، روی گردانند.'),
(4514, 46, 4, '[به مشرکان] بگو: به من خبر دهید [و نشانم دهید] معبودانی که به جای خدا می پرستید چه چیزی از زمین را آفریده اند یا مگر آنان را در آفرینش آسمان ها مشارکتی است؟ اگر راستگو هستید کتابی پیش از این قرآن یا بازمانده ای از دانش استواری [که ادعای شما را تصدیق کند] برای من بیاورید؛'),
(4515, 46, 5, 'و چه کسانی گمراه ترند از کسانی که به جای خدا معبودانی را می خوانند که تا روز قیامت پاسخشان را نمی دهند و آنان از خواندنشان بی خبرند؟'),
(4516, 46, 6, 'و هنگامی که مردم را [در قیامت] گرد آورند [معبودانشان] با آنان دشمن باشند و پرستش آنان را انکار کنند،'),
(4517, 46, 7, 'و چون آیات روشن ما را بر آنان بخوانند، کافران درباره حقّی که به سویشان آمده می گویند: این جادویی آشکار است.'),
(4518, 46, 8, 'بلکه می گویند: این قرآن را از نزد خود ساخته و به خدا نسبت داده است. بگو: اگر آن را از نزد خود ساخته و به خدا نسبت داده باشم [و خدا بخواهد برای این کار مرا به عذابی سخت گرفتار کند] شما نمی توانید برای من چیزی از عذاب خدا را دفع کنید، خدا به باطلی که در آن فرو می روید [و آن طعنه زدن به قرآن و جادو خواندن آن است] داناتر است، و کافی است که خدا میان من و شما گواه باشد، و او بسیار آمرزنده و مهربان است.'),
(4519, 46, 9, 'بگو: از میان فرستادگان خدا فرستاده ای نوظهور نیستم [که گفتار و کردارم مخالف گفتار و کردار آنان باشد، بلکه به من هم چون آنان وحی می شود] و نمی دانم با من و شما [در دنیا و آخرت] چه خواهد شد، من فقط از آنچه به من وحی شده پیروی می کنم، و من جز بیم دهنده ای آشکار نیستم.'),
(4520, 46, 10, 'بگو: به من خبر دهید که اگر این قرآن از ناحیه خدا باشد و شما به آن کفر ورزیده باشید و شاهدی هم از بنی اسرائیل به نظیر آن [که تورات واقعی و تصدیق کننده قرآن است] شهادت داده باشد [که همان طوری که تورات وحی خداست، قرآن هم وحی خداست و] پس [از شهادتش به قرآن] ایمان آورده باشد و شما از ایمان به آن تکبّر ورزیده باشید [آیا گمراه نبوده اید؟] بی تردید خدا گروه ستمکار را هدایت نمی کند.'),
(4521, 46, 11, 'و کافران درباره مؤمنان گفتند: اگر [آیین محمّد] بهتر [از آیین ما] بود [آنان در پذیرفتنش] بر ما نسبت به آن پیشی نمی گرفتند [بلکه خود ما زودتر از آنان به این کتاب ایمان می آوردیم]. و چون [خودشان به خاطر کبر و عنادشان] به وسیله آن هدایت نیافتند، به زودی خواهند گفت: این یک دروغ قدیمی است.'),
(4522, 46, 12, '[چگونه یک دروغ قدیمی است] در حالی که پیش از آن کتاب موسی پیشوا و رحمت بود [و از نزول آن بر پیامبر اسلام خبر داده]، و این قرآن تصدیق کننده [آن] است در حالی که به زبان عربی [فصیح و گویا] ست تا ستمکاران را بیم دهد، و برای نیکوکاران بشارتی باشد.'),
(4523, 46, 13, 'بی تردید کسانی که گفتند: پروردگار ما الله است، سپس [در میدان عمل بر این حقیقت] استقامت ورزیدند، نه بیمی بر آنان است و نه اندوهگین می شوند.'),
(4524, 46, 14, 'اینان اهل بهشت اند که به پاداش اعمال شایسته ای که همواره انجام می دادند، در آن جاودانه اند. ….'),
(4525, 46, 15, 'و انسان را درباره پدر و مادرش به نیکی سفارش کردیم. مادرش او را با تحمل رنج و زحمت باردار شد و با رنج و زحمت او را زایید. و دوران بارداری و باز گرفتنش از شیر سی ماه است، تا زمانی که به رشد و نیرومندی خود و به چهل سالگی برسد، گوید: پروردگارا! به من الهام کن تا نعمتت را که بر من و پدر و مادرم عطا کرده ای سپاس گزارم، و کار شایسته ای که آن را می پسندی انجام دهم و ذریه و نسل مرا برای من صالح و شایسته گردان که من به سوی تو بازگشتم و به یقین از تسلیم شدگان [به فرمان ها و احکام] توام.'),
(4526, 46, 16, 'اینانند کسانی که ما از آنان بهترین اعمالی که انجام داده اند می پذیریم، و از گناهانشان در زمره اهل بهشت در می گذریم؛ [خدا وعده داد] وعده درست و راست که همواره به آن وعده داده می شدند؛'),
(4527, 46, 17, 'و کسی که به پدر و مادرش گفت: اُف بر شما! [من از شما متنفر و دلتنگم]، آیا به من وعده می دهید که [پس از مرگ] زنده خواهم شد، در حالی که پیش از من اقوام بسیاری در گذشتند، [و هیچ کدام از آنان زنده نشدند]. و آن دو همواره [برای بازگشت فرزندشان] از خدا یاری می خواهند [و به فرزندشان می گویند:] وای بر تو ایمان بیاور، بی تردید وعده خدا [درباره سعادت و رستگاری مؤمنان در قیامت و شقاوت و بدبختی کافران] حق است. [ولی او در پاسخشان] می گوید: این [وعده ها] جز افسانه های گذشتگان نیست!!'),
(4528, 46, 18, 'اینانند کسانی که [به خاطر انکار وعده های حق] فرمان عذاب درباره آنان در زمره گروه هایی از جن و انس که پیش از آنان در گذشتند، محقق و ثابت شده است؛ زیرا آنان زیانکار بودند.'),
(4529, 46, 19, 'برای هر کدام [از گروه مؤمنان و کافران] بر پایه اعمالی که انجام داده اند درجاتی است [که در آن قرار می گیرند]، و تا خدا اعمالشان را به طور کامل به آنان بدهد؛ و آنان مورد ستم قرار نمی گیرند.'),
(4530, 46, 20, 'و روزی که کافران را بر آتش عرضه کنند [به آنان گویند:] نعمت های پاکیزه خود را [که می توانست وسیله آبادی آخرتتان باشد] در زندگی دنیایتان مصرف کردید و [همانجا] از آن برخوردار شدید؛ بنابراین امروز به سبب اینکه به ناحق در زمین تکبّر می ورزیدید و در برابر اینکه همواره نافرمانی می کردید با عذاب خفّت بار کیفر می یابید.'),
(4531, 46, 21, 'و [سرگذشت هود] برادر قوم عاد را یاد کن، هنگامی که قومش را در سرزمین احقاف بیم داد، و بی تردید بیم دهندگانی پیش از او و پس از او [در آن سرزمین] گذشته بودند که فقط خدا را بپرستید؛ زیرا من بر شما از عذاب روزی بزرگ می ترسم.'),
(4532, 46, 22, 'گفتند: آیا آمده ای تا ما را از [پرستش] معبودانمان بر گردانی، پس اگر راستگویی عذابی که ما را تهدید می کنی بیاور!'),
(4533, 46, 23, 'گفت: آگاهی [به کیفیت عذاب و زمان آمدنش] فقط نزد خداست، و من آیینی را که به آن فرستاده شده ام به شما ابلاغ می کنم، ولی من شما را گروهی می دانم که همواره نادانی می کنید.'),
(4534, 46, 24, 'پس هنگامی که عذاب را [در پهنه فضا] به صورت ابری گسترده دیدند که به سوی درّه ها و آبگیرهایشان روی آورده، گفتند: این ابری باران زا برای ماست. [هود گفت: نه] بلکه این همان چیزی است که آن را به شتاب خواستید، بادی است که در آن عذابی دردناک است؛'),
(4535, 46, 25, '[چنان بادی است که] همه چیز را به فرمان پروردگارش در هم می کوبد و نابود می کند. پس آنان [به سبب آن عذاب] چنان شدند که جز خانه هایشان دیده نمی شد. این گونه مردم گناهکار را کیفر می دهیم.'),
(4536, 46, 26, 'همانا ما به آنان در اموری قدرت و تمکن داده بودیم که شما را در آن امور چنان قدرت و نیرویی نداده ایم، و برای آنان گوش و چشم و دل قرار داده بودیم، ولی گوش و چشم و دلشان چیزی از عذاب را از آنان برطرف نکرد؛ زیرا همواره [با داشتن آن ابزار تشخیص] آیات ما را انکار می کردند، و سرانجام عذابی که همواره آن را مسخره می کردند، آنان را احاطه کرد.'),
(4537, 46, 27, 'و به راستی ما [اهل] آبادی هایی را که پیرامون شما [اهل مکه] بودند [به کیفر طغیانشان] هلاک کردیم و آیات خود را به صورت های گوناگون [برای آنان] بیان داشتیم، باشد که برگردند.'),
(4538, 46, 28, 'پس چرا معبودانی که به جای خدا انتخاب کردند به گمان اینکه آنان را به خدا نزدیک کند، آنان را [هنگام نزول عذاب] یاری نکردند بلکه [گم شده] از دستشان رفتند، و این بود [سرانجام و نتیجه] دروغشان و آنچه را همواره افترا می زدند.'),
(4539, 46, 29, 'و [یاد کن] هنگامی که گروهی از جن را به سوی تو متوجه کردیم که قرآن را بشنوند، پس وقتی که نزد قرائت آن حاضر شدند، گفتند: [برای شنیدن قرآن] خاموش باشید. پس چون قرائت پایان یافت به سوی قومشان در حالی که گفتند: بیم دهنده بودند، بازگشتند. شنیدیم که'),
(4540, 46, 30, 'ای قوم ما! به راستی ما کتابی را شنیدیم که پس از موسی نازل شده است، تصدیق کننده همه کتاب های پیش از خود است، به سوی حق و به سوی راه راست هدایت می کند.'),
(4541, 46, 31, 'ای قوم ما! دعوت کننده [به سوی] خدا [پیامبر اسلام] را اجابت کنید و به او ایمان آورید تا خدا برخی از گناهانتان را بیامرزد و از عذابی دردناک پناهتان دهد؛'),
(4542, 46, 32, 'و آنان که دعوت کننده خدا را اجابت نکنند نمی توانند [خدا را] در زمین عاجز کنند [تا از دسترس قدرت او بیرون روند] و آنان را در برابر خدا یاورانی نیست [که عذاب را از آنان دفع کند]، اینان در گمراهی آشکاری هستند.'),
(4543, 46, 33, 'آیا ندانسته اند خدایی که آسمان ها و زمین را آفرید و از آفرینش آنها درمانده و خسته نشد، قدرت دارد که مردگان را زنده کند؟ آری، او بر هر کاری تواناست؛'),
(4544, 46, 34, 'و روزی که کافران را بر آتش عرضه می کنند [از آنان می پرسند:] آیا این حق نیست؟ می گویند: آری! سوگند به پروردگارمان حق است، [پرسش کننده] می گوید: پس این عذاب را برای آنکه همواره کفر می ورزیدید، بچشید.'),
(4545, 46, 35, 'پس صبر کن همان گونه که پیامبران اولوالعزم صبر کردند، و برای آنان در [نزول عذاب] شتاب مکن، روزی که آنان آنچه را که وعده داده می شوند ببینند، گویی فقط ساعتی از یک روز [در دنیا] درنگ داشتند؛ [این قرآن] ابلاغی است برای همه؛ پس آیا جز مردم نافرمان هلاک می شوند؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'آنان که کفر ورزیدند و [مردم را] از راه خدا باز داشتند خدا اعمالشان را باطل و تباهساخت؛'),
(4547, 47, 2, 'و کسانی که ایمان آورده و کارهای شایسته انجام داده اند و به آنچه بر محمّد نازل شده که از سوی پروردگارشان حق است گرویدند، خدا گناهانشان را از آنان محو کرد و حالشان را اصلاح نمود.'),
(4548, 47, 3, 'این به سبب آن است که کافران از باطل پیروی کردند، و مؤمنان از حق که از سوی پروردگار آنان است، پیروی نمودند. این گونه خدا برای مردم اوصافشان را بیان می کند.'),
(4549, 47, 4, 'پس هنگامی که [در میدان جنگ] با کافران روبرو شدید، گردن هایشان را به شدت بزنید تا آن گاه که بسیاری از آنان را با سختی و غلظت از پای درآورید، در این هنگام [از دشمن اسیر بگیرید و] آنان را محکم ببندید، [پس از اسیر گرفتن] یا بر آنان منت نهید [و آزادشان کنید]، یا از آنان [در برابر آزاد کردنشان] فدیه و عوض بگیرید تا آنجا که جنگ بارهای سنگینش را بر زمین نهد. این است [فرمان خدا]؛ و اگر خدا می خواست [خود بدون فرمان جنگ] از آنان انتقام می گرفت [ولی به جنگ فرمان داد] تا برخی از شما را به وسیله برخی دیگر بیازماید، و کسانی که در راه خدا به شهادت رسیده اند، خدا هرگز اعمالشان را باطل و تباه نمی کند.'),
(4550, 47, 5, '[خدا] آنان را به زودی [به سرمنزل خوشبختی و کرامت] هدایت می کند و حالشان را اصلاح می نماید؛'),
(4551, 47, 6, 'و آنان را در بهشتی که در دنیا به آنان شناسانده در می آورد.'),
(4552, 47, 7, 'ای مؤمنان! اگر خدا را یاری کنید، خدا هم شما را یاری می کند و گام هایتان را محکم و استوار می سازد؛'),
(4553, 47, 8, 'و بر کافران هلاکت و نابودی باد و [خدا] اعمالشان را باطل و تباه ساخت.'),
(4554, 47, 9, 'این برای آن است که آنان آنچه را خدا نازل کرده است خوش نداشتند، پس خدا هم اعمالشان را تباه و بی اثر کرد.'),
(4555, 47, 10, 'آیا در زمین گردش نکردند تا با تأمل بنگرند که سرانجام کسانی که پیش از آنان بودند، چگونه بود؟ خدا آنان را درهم کوبید و نابود کرد و نیز برای این کافران همانند آنان [عذابی قطعی و ثابت] است.'),
(4556, 47, 11, 'این [پیروزی مؤمنان و نابودی کافران] به سبب این است که خدا سرپرست و یاور کسانی است که ایمان آورده اند، و کافران را سرپرست و یاری نیست.'),
(4557, 47, 12, 'بی تردید خدا کسانی را که ایمان آورده و کارهای شایسته انجام داده اند، در بهشت هایی کهاز زیرِ [درختانِ] آن نهرها جاری است در می آورد، و در حالی که کافران همواره سرگرم بهره گیری از [کالا و لذت های زودگذر] دنیایند و می خورند، همان گونه که چهارپایان می خورند و جایگاهشان آتش است.'),
(4558, 47, 13, 'چه بسیار آبادی هایی که [اهلش] از [اهل] آبادی تو که تو را بیرون کرده اند نیرومندتر بودند، ما آنان را هلاک کردیم و یاوری [در برابر عذاب] برای آنان نبود.'),
(4559, 47, 14, 'آیا کسانی که بر دلیل روشنی از پروردگار خویش اند، مانند کسانی هستند که عمل زشتشان برای آنان آراسته شده و از هواهای نفسانی خود پیروی کرده اند؟'),
(4560, 47, 15, 'صفت بهشتی که به پرهیزکاران وعده داده شده [چنین است:] در آن نهرهایی است از آبی که مزه و رنگش برنگشته و نهرهایی از شیر که طعمش دگرگون نشده و نهرهایی از شراب که برای نوشندگان مایه لذت است و نهرهایی از عسل خالص است، و در آن از هر گونه میوه ای برای آنان فراهم است، و نیز آمرزشی از سوی پروردگارشان [ویژه آنان است. آیا اینان] مانند کسانی هستند که در آتش همیشگی و جاودانه اند، واز آبی جوشان به آنان می نوشانند که روده هایشان را متلاشی می کند؟!'),
(4561, 47, 16, 'گروهی از آنان [که منافق اند در ظاهر به گفتارت] گوش می دهند، ولی هنگامی که از نزد تو بیرون روند به کسانی که آگاهی و معرفت به آنان داده شده [از روی مسخره] می گویند: هم اکنون چه گفت؟ [ما که گفته او را نفهمیدیم.] آنان کسانی هستند که خدا بر دل هایشان مهر [تیره بختی] نهاده واز هواهای نفسانی خود پیروی کرده اند،'),
(4562, 47, 17, 'و کسانی که هدایت یافته اند خدا بر هدایتشان افزوده و پرهیزکاری و تقوایشان را به آنان عطا کرده است؛'),
(4563, 47, 18, 'پس آیا [منافقان و منکران] جز این را انتظار می برند که ناگاه قیامت بر آنان فرا رسد؟ [در حالی که هم اکنون نشانه هایش آمده است]، پس هنگامی که قیامت بر آنان فرا رسد، متذکّر شدن و هوشیاری شان از کجا و چگونه به آنان سود بخشد؟'),
(4564, 47, 19, 'پس بدان که هیچ معبودی جز خدا نیست [بنابراین بر معرفت و آگاهیت نسبتِ به حق استوار باش]؛ و از میان رفتن [توطئه ها و موانعی که به وسیله دشمنان در راه پیشرفت دعوتت به اسلام] برای تو و مردان و زنان با ایمان ایجاد شده از خدا بخواه، و برای زنان و مردان مؤمن طلب آمرزش کن و خدا محل رفت و بازگشت شماو قرارگاهتان را می داند.'),
(4565, 47, 20, 'مؤمنان می گویند: چرا سوره ای [در رابطه با فرمان جهاد] نازل نمی شود؟ ولی هنگامی که سوره ای محکم و بی شبهه نازل می شود و در آن از جهاد سخن به میان آمده است، آنان را که در دل هایشان بیماری است، می بینی مانند کسی که در بیهوشی مرگ افتاده، به تو می نگرند و سزاوارشان هم [به سبب بیماردلی] همین حالت است.'),
(4566, 47, 21, '[ولی] فرمانبرداری [از خدا و پیامبر] و سخن پسندیده و نیک [نه اظهار کراهت و ناخشنودی از جهاد] برای آنان شایسته تر است. پس اگر هنگامی که فرمان جهاد قطعی و مسلم شود با خدا از سر صدق و راستی درآیند، بی تردید برای آنان بهتر است.'),
(4567, 47, 22, 'پس اگر [از خدا و پیامبر] روی گردان شوید آیا از شما جز این انتظار می رود که در زمین فساد کنید و [بر سر مال و منال دنیا] قطع رحم نمایید؟'),
(4568, 47, 23, 'آنان [که روی گردانند] کسانی هستند که خدا لعنتشان کرده و گوش [دل] شان را کر و چشم [بصیرت] شان را کور کرده است.'),
(4569, 47, 24, 'آیا در قرآن نمی اندیشند [تاحقایق را بفهمند] یا بر دل هایشان قفل هایی قرار دارد؟'),
(4570, 47, 25, 'بی تردید کسانی که پس از روشن شدن هدایت برای آنان [به همان عقاید باطل و کردار ناپسندشان] برگشتند [و دست از قرآن و پیامبر برداشتند] شیطان زشتی هایشان را در نظرشان آراست و آنان را در آرزوهای دور و دراز انداخت.'),
(4571, 47, 26, 'این به سبب آن است که آنان به کسانی [چون مشرکان و کافران] که نازل شدن وحی را خوش نداشتند، گفتند: [ما] در بعضی از امور [که بر ضد مؤمنان است] از شما اطاعت خواهیم کرد. در حالی که خدا اسرارشان را می داند.'),
(4572, 47, 27, 'پس حال و وضعشان زمانی که فرشتگان جانشان را می گیرند در حالی که بر صورت و پشتشان [تازیانه عذاب] می زنند، چگونه خواهد بود؟!'),
(4573, 47, 28, 'این عذاب برای این است که آنان از آنچه خدا را به خشم آورده پیروی کردند، و خشنودی او را خوش نداشتند در نتیجه اعمالشان را تباه و بی اثر کرد.'),
(4574, 47, 29, 'بلکه کسانی که در دل هایشان بیماری است، گمان کردند که خدا کینه هایشان را آشکار نخواهد کرد.'),
(4575, 47, 30, 'و اگر بخواهیم یقیناً آنان را به تو نشان می دهیم تا آنان را از روی نشانه هایشان بشناسی، و بی تردید آنان را از شیوه سخن گفتن خواهی شناخت؛ و خدا کارهای شما را می داند.'),
(4576, 47, 31, 'به یقین ما شما را امتحان می کنیم تا مجاهدان از شما و صابران را مشخص نماییم، و اخبار شما را نیز [که اعمال و اسرار شماست] می آزماییم [تا صدق و کذب شما را در همه امور معلوم بداریم.]'),
(4577, 47, 32, 'مسلماً آنان که کفر ورزیدند و [مردم را] از راه خدا باز داشتند و پس از آنکه هدایت برای آنان روشن شد با پیامبر به شدت دشمنی نمودند، هرگز هیچ زیانی به خدا نمی رسانند و خدا به زودی اعمالشان را تباه و بی اثر می کند.'),
(4578, 47, 33, 'ای اهل ایمان! خدا را اطاعت کنید و پیامبر را اطاعت کنید و اعمالتان را [با تخلف از دستورها خدا و پیامبر، شرک، نفاق، منت گذاری و ریا] باطل مکنید.'),
(4579, 47, 34, 'مسلماً کسانی که کافر شدند و [مردم را] از راه خدا باز داشتند، سپس در حال کفر از دنیا رفتند، هرگز خدا آنان را نمی آمرزد.'),
(4580, 47, 35, 'پس [با توجه به یاری و کارسازی خدا] سست نشوید و [کافران و مشرکان را] به آشتی و صلحی [که برای شما خفّت بار است] دعوت نکنید، در حالی که شما برتر هستید و خدا با شماست و هرگز از [ثواب و پاداش] اعمالتان نمی کاهد.'),
(4581, 47, 36, 'زندگی دنیا فقط بازی و سرگرمی است، اگر ایمان بیاورید و پرهیزکاری کنید، خدا پاداش هایتان را می دهد و از شما نمی خواهد [که] اموالتان را [در راه خدا انفاق کنید؛]'),
(4582, 47, 37, 'اگر همه اموال شما را بخواهد و بر شما اصرار ورزد، بخل می کنید و [سبب می شود که] کینه های درونی شما را بیرون بریزد [و آن زمان است که گمراه می شوید.]'),
(4583, 47, 38, 'آگاه باشید! شما همان مردمی هستید که برای انفاق در راه خدا دعوت می شوید، پس برخی از شما بخل می ورزند، و هر که بخل پیشه کند، فقط نسبت به [سعادت و خوشبختی] خود بخیل است، و خدا [از انفاق و صدقات شما] بی نیاز است، و شمایید که نیازمند هستید؛ و اگر [از فرمان های او] روی بگردانید به جای شما گروه دیگری را می آورد که مانند شما [روی گردان، سست عقیده و بخیل] نخواهند بود.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'به راستی ما برای تو پیروزی آشکاری فراهم آوردیم؛'),
(4585, 48, 2, 'تا خدا [با این پیروزی آشکار] آنچه را [به وسیله دشمنان از توطئه ها و موانع و مشکلات در راه پیشرفت دعوتت به اسلام] در گذشته پیش آمده و آینده پیش خواهد آمد از میان بردارد، و نعمتش را بر تو کامل کند، و به راهی راست راهنمایی ات نماید؛'),
(4586, 48, 3, 'و خدا تو را به نصرتی توانمندانه و شکست ناپذیر یاری دهد.'),
(4587, 48, 4, 'اوست که آرامش را در دل های مؤمنان نازل کرد، تا ایمانی بر ایمانشان بیفزاید. و سپاهیان آسمان و زمین فقط در سیطره مالکیّت و فرمانروایی خداست؛ و خدا همواره دانا و حکیم است.'),
(4588, 48, 5, 'تا مردان و زنان مؤمن را در بهشت هایی درآورد که از زیرِ [درختانِ] آن نهرها جاری است در حالی که در آن جاودانه اند، و گناهانشان را از آنان محو کند، و این [سرانجام نیک] نزد خدا کامیابی بزرگ است.'),
(4589, 48, 6, 'و مردان و زنان منافق و مردان و زنان مشرک را که به خدا گمان بد می برند عذاب کند؛ پیشامد بد زمانه فقط بر خودشان باد. خدا بر آنان خشم گرفته، و لعنتشان کرده است و دوزخ را برای آنان آماده نموده و بد بازگشت گاهی است.'),
(4590, 48, 7, 'سپاهیان آسمان ها و زمین فقط در سیطره مالکیّت و فرمانروایی خداست؛ و خدا همواره توانای شکست ناپذیر و حکیم است.'),
(4591, 48, 8, 'ما تو را گواه [بر اعمال امت] و مژده رسان و بیم دهنده فرستادیم؛'),
(4592, 48, 9, 'تا [همه شما مردم] به خدا و پیامبرش ایمان آورید و او را یاری دهید و بزرگش بشمارید و بامداد و شام گاه تسبیحش گویید.'),
(4593, 48, 10, 'به یقین کسانی که با تو بیعت می کنند، جز این نیست که با خدا بیعت می کنند؛ قدرت خدا بالاتر از همه قدرتهاست. پس کسی که پیمان می شکند فقط به زیان خود می شکند، و کسی که به پیمانی که با خدا بسته است وفا کند، خدا به زودی پاداشی بزرگ به او می دهد.'),
(4594, 48, 11, 'کسانی از بادیه نشینان که از جنگ [با دشمن] تخلف کردند به زودی [از روی بهانه جویی] به تو می گویند: اموال و خانواده مان ما را به خود مشغول کردند [به این سبب نتوانستیم به جنگ آییم]؛ پس برای ما آمرزش بخواه. با زبان هایشان چیزی می گویند که در دل هایشان نیست. بگو: اگر خدا زیانی یا سودی را برای شما بخواهد چه کسی می تواند در برابر خدا از شما دفاع کند؟ بلکه خدا همواره به آنچه انجام می دهید، آگاه است.'),
(4595, 48, 12, '[تخلف شما از جنگ، ربطی به اموال و خانواده تان نداشت] بلکه پنداشتید پیامبر و مؤمنان هرگز به کسان خود باز نمی گردند، و این پندار باطل در دل هایتان آراسته شد، و گمان بد بردید [که خدا پیامبر و مؤمنان را یاری نخواهد کرد] و شما [به سبب این پندار باطل و گمان بد] قومی نابودشدنی هستید.'),
(4596, 48, 13, 'و هر کس به خدا و پیامبرش ایمان نیاورد [زیانی به خدا و پیامبر نمی رساند]، ما برای کافران آتشی سوزان آماده کرده ایم.'),
(4597, 48, 14, 'و مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره خداست، هر کس را بخواهد می آمرزد و هر کس را بخواهد عذاب می کند؛ و خدا همواره بسیار آمرزنده و مهربان است.'),
(4598, 48, 15, 'هنگامی که شما برای به دست آوردن غنایمی روانه [میدان جنگی] می شوید، متخلفان [از جنگ حدیبیه] به زودی خواهند گفت: بگذارید ما هم به دنبال شما بیاییم. [این سبک مغزان سودجو] می خواهند وعده خدا را [که به مؤمنان وعده پیروزی و غنیمت داد و به منافقان وعده محرومیت از غنیمت و رحمت داده است] تغییر دهند. بگو: هرگز دنبال ما نخواهید آمد؛ این گونه خدا پیش از این [درباره شما] فرموده است. اما [این گروه بی شرم] به زودی خواهند گفت: شما نسبت به ما حسد می ورزید، [چنین نیست] بلکه آنان جز اندکی نمی فهمند.'),
(4599, 48, 16, 'به متخلفانِ بادیه نشین بگو: به زودی به سوی گروهی سرسخت و نیرومند دعوت خواهید شد که با آنان بجنگید، یا آنکه اسلام آورند؛ پس اگر اطاعت کنید خدا پاداش نیکی به شما خواهد داد، و اگر روی برگردانید، همان گونه که پیش از این روی گرداندید، شما را به عذابی دردناک عذاب خواهد کرد.'),
(4600, 48, 17, 'بر نابینا و لنگ و بیمار [اگر در جنگ شرکت نکنند] گناهی نیست. و کسی که از خدا و پیامبرش اطاعت کند، او را در بهشت هایی درآورد که از زیرِ [درختانِ] آن نهرها جاری است، و هر کس روی بگرداند او را به عذابی دردناک عذاب می کند.'),
(4601, 48, 18, 'یقیناً خدا از مؤمنان هنگامی که زیر آن درخت [که در منطقه حدیبیه بود] با تو بیعت می کردند خشنود شد، و خدا آنچه را [از خلوص نیّت و پاکی قصد] در دل هایشان بود می دانست، در نتیجه آرامش را بر آنان نازل کرد، و پیروزی نزدیکی را [که پیروزی در خیبر بود] به آنان پاداش داد.'),
(4602, 48, 19, '[و پاداش دیگر] غنیمت های فراوانی است که آن را به دست می آورید؛ و خدا همواره توانای شکست ناپذیر و حکیم است.'),
(4603, 48, 20, 'خدا غنیمت های فراوانی را در آینده به شما وعده داده است که به دست می آورید، و این یک غنیمت را [که فتح خیبر است] برای شما پیش انداخت، و دست های [تجاوز و ستم] مردم را از شما باز داشت تا این [پیروزی ها] برای مؤمنان نشانه ای [از حقّانیّت دین و یاری خدا] باشد و تا شما را به راهی راست هدایت کند.'),
(4604, 48, 21, 'و [غنیمت ها و پیروزی های] دیگر [ی هست] که شما تا کنون بر آنها دست نیافته اید، بی تردید خدا بر آنها احاطه دارد [و به آسانی می تواند در دسترس شما قرار دهد]؛ و خدا همواره بر هر کاری تواناست.'),
(4605, 48, 22, 'اگر کافران با شما بجنگند حتماً پشت کنان می گریزند، آن گاه سرپرست و یاوری [که از آنان دفاع کند] نمی یابند.'),
(4606, 48, 23, 'خدا روشی ثابت و قطعی مقرّر کرده است که از پیش [بر پیروزی مؤمنان و شکست کافران] جاری بوده است، و هرگز برای روش خدا تغییر و تبدیلی نخواهی یافت.'),
(4607, 48, 24, 'و اوست که پس از آنکه شما را [بی جنگ و خونریزی] بر دشمن پیروز و مسلط کرد، دست های آنان را از [جنگ با] شما و دست های شما را از [جنگ با] آنان در دل مکه [که منطقه حدیبیه است] باز داشت؛ و خدا همواره به آنچه انجام می دهید بیناست.'),
(4608, 48, 25, 'آنان کسانی هستند که کفر ورزیدند و شما را از [ورود به] مسجدالحرام باز داشتند و نیز نگذاشتند قربانی هایی که با خود آورده بودید به محل قربانی اش برسد، و اگر مردان و زنان مؤمن ناشناسی در میان مکه نبودند، تا جنگ شما سبب کشته شدن آن بی گناهان شود و در نتیجه امر ناملایم و مکروهی [چون دیه] گریبان شما را بگیرد [شما را از جنگ باز نمی داشتیم، ولی بازداشتیم] تا خدا هرکه را [مانند مردان و زنان مومنی که برای شما ناشناخته بودند] بخواهد در رحمتش درآورد. [و] اگر مؤمنان از کافران جدا بودند یقیناً کافران [از اهل مکه] را به عذابی دردناک عذاب می کردیم'),
(4609, 48, 26, '[یاد کن] هنگامی را که کافران [پس از قرارداد صلح حدیبیه] دل هایشان را از تعصّب و خشم شدید جاهلی آکنده کردند، پس خدا هم آرامشش را بر پیامبرش و مؤمنان نازل کرد و روح تقوا را [که حقیقت و جان توحید است] ملازم آنان نمود، و آنان به [روح تقوا] سزاوارتر و شایسته آن بودند؛ و خدا همواره به هر چیز داناست.'),
(4610, 48, 27, 'بی تردید خدا رؤیای پیامبرش را به حق و درستی تحقق داد [که در رؤیا وعده داده بود] شما قطعاً در حال امن و امنیت در حالی که سرهایتان را تراشیده و موی [یا ناخن] کوتاه کرده اید و بیمی ندارید، وارد مسجدالحرام خواهید شد. خدا آنچه را که شما نمی دانستید [که وعده ورود به مسجدالحرام در سال آینده است] دانست و پیش از آن پیروزی نزدیکی [که صلح حدیبیه بود برای شما] قرارداد.'),
(4611, 48, 28, 'اوست که پیامبرش را با هدایت و دین حق فرستاد تا آن را بر همه ادیان پیروز کند، و کافی است که خدا [بر وقوع این حقیقت] گواه باشد.'),
(4612, 48, 29, 'محمّد، فرستاده خداست و کسانی که با او هستند بر کافران سرسخت و در میان خودشان با یکدیگر مهربانند، همواره آنان را در رکوع و سجود می بینی که پیوسته فضل و خشنودی خدا را می طلبند؛ نشانه آنان در چهره شان از اثر سجود پیداست، این است توصیف آنان در تورات، و اما توصیفشان در انجیل این است که وجودشان چون زراعتی است که جوانه های خود را رویانده پس تقویتش کرده تا ستبر و ضخیم شده، و در نتیجه بر ساقه هایش [محکم و استوار] ایستاده است، به طوری که دهقانان را [از رشد و انبوهی خود] به تعجب می آورد تا خدا به وسیله [انبوهی و نیرومندی] مؤمنان، کافران را به خشم آورد. [و] خدا به کسانی از آنان که ایمان آورده و کارهای شایسته انجام داده اند، آمرزش و پاداشی بزرگ وعده داده است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ای اهل ایمان! بر خدا و پیامبرش [در هیچ امری از امور دین و دنیا و آخرت] پیشی مگیرید و از خدا پروا کنید که خدا شنوا و داناست.'),
(4614, 49, 2, 'ای مؤمنان! صدایتان را بلندتر از صدای پیامبر نکنید، و آن گونه که با یکدیگر بلند سخن می گویید با او بلند سخن نگویید، مبادا آنکه اعمالتان تباه و بی اثر شود و شما تباهی آن را درک نکنید.'),
(4615, 49, 3, 'بی تردید کسانی که صدایشان را نزد رسول خدا پایین می آورند، همانان هستند که خدا دل هایشان را برای پرهیزکاری امتحان کرده، آنان را آمرزش و پاداشی بزرگ است.'),
(4616, 49, 4, 'یقیناً کسانی که تو را از پشت اطاق ها صدا می کنند، بیشترشان معرفت و آگاهی [به حرمت و عظمت تو] ندارند.'),
(4617, 49, 5, 'و اگر آنان صبر می کردند تا به سویشان بیرون آیی، یقیناً برای آنان بهتر بود، و خدا بسیار آمرزنده و مهربان است.'),
(4618, 49, 6, 'ای اهل ایمان! اگر فاسقی خبری برایتان آورد، خبرش را بررسی و تحقیق کنید تا مبادا از روی ناآگاهی، گروهی را آسیب و گزند رسانید و بر کرده خود پشیمان شوید.'),
(4619, 49, 7, 'و بدانید که پیامبر خدا در میان شماست [و بر شما واجب است در همه امور از او پیروی کنید]، اگر او در بسیاری از کارها از شما پیروی کند، قطعاً دچار گرفتاری و زحمت می شوید، ولی خدا ایمان را محبوب شما قرار داد و آن را در دل هایتان بیاراست، و کفر و بدکاری و نافرمانی را ناخوشایند شما ساخت، اینان [که دارای این ویژگی ها هستند] هدایت یافته اند.'),
(4620, 49, 8, '[این محبت و ناخوشایندی نسبت به حق و باطل] عطیه و نعمتی از سوی خداست؛ و خدا دانا و حکیم است.'),
(4621, 49, 9, 'و اگر دو گروه از مؤمنان با یکدیگر بجنگند میان آنها صلح و آشتی برقرار کنید، و اگر یکی از آن دو گروه بر دیگری تجاوز کند، با آن گروهی که تجاوز می کند، بجنگید تا به حکم خدا باز گردد. پس اگر بازگشت، میانشان به عدالت و انصاف، صلح و آشتی برقرار کنید؛ و همواره دادگری را پیشه سازید که خدا دادگران را دوست دارد.'),
(4622, 49, 10, 'جز این نیست که همه مؤمنان با هم برادرند؛ بنابراین [در همه نزاع ها و اختلافات] میان برادرانتان صلح و آشتی برقرار کنید، و از خدا پروا نمایید که مورد رحمت قرار گیرید.'),
(4623, 49, 11, 'ای اهل ایمان! نباید گروهی گروه دیگر را مسخره کنند، شاید مسخره شده ها از مسخره کنندگان بهتر باشند، ونباید زنانی زنان دیگر را [مسخره کنند] شاید مسخره شده ها از مسخره کنندگان بهتر باشند، و از یکدیگر عیب جویی نکنید و با لقب های زشت و ناپسند یکدیگر را صدا نزنید؛ بد نشانه و علامتی است اینکه انسانی را پس از ایمان آوردنش به لقب زشت علامت گذاری کنند. و کسانی که [از این امور ناهنجار و زشت] توبه نکنند، خود ستمکارند.'),
(4624, 49, 12, 'ای اهل ایمان! از بسیاری از گمان ها [در حقّ مردم] بپرهیزید؛ زیرا برخی از گمان ها گناه است، و [در اموری که مردم پنهان ماندنش را خواهانند] تفحص و پی جویی نکنید، و از یکدیگر غیبت ننمایید، آیا یکی از شما دوست دارد که گوشت برادر مرده اش را بخورد؟ بی تردید [از این کار] نفرت دارید، و از خدا پروا کنید که خدا بسیار توبه پذیر و مهربان است.'),
(4625, 49, 13, 'ای مردم! ما شما را از یک مرد و زن آفریدیم و ملت ها و قبیله ها قرار دادیم تا یکدیگر را بشناسید. بی تردید گرامی ترین شما نزد خدا پرهیزکارترین شماست. یقیناً خدا دانا و آگاه است.'),
(4626, 49, 14, 'بادیه نشینان گفتند: ما [از عمق قلب] ایمان آوردیم. بگو: ایمان نیاورده اید، بلکه بگویید: اسلام آورده ایم؛ زیرا هنوز ایمان در دل هایتان وارد نشده است. و اگر خدا و پیامبرش را اطاعت کنید، چیزی از اعمالتان را نمی کاهد؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(4627, 49, 15, 'مؤمنان فقط کسانی اند که به خدا و پیامبرش ایمان آورده اند، آن گاه [در حقّانیّت آنچه به آن ایمان آورده اند] شک ننموده و با اموال و جان هایشان در راه خدا جهاد کرده اند؛ اینان [در گفتار و کردار] اهل صدق و راستی اند.'),
(4628, 49, 16, 'بگو: آیا خدا را از دین خود [به خیال اینکه آگاه نیست] خبر می دهید [که از عمق قلب ایمان آورده اید؟!] در حالی که خدا آنچه را در آسمان ها و آنچه را در زمین است می داند، و خدا به همه چیز داناست.'),
(4629, 49, 17, 'از اینکه اسلام آورده اند بر تو منت می گذارند؛ بگو: اگر [در ادعای مؤمن بودن] راستگویید، بر من از اسلام آوردن خود منت نگذارید، بلکه خداست که با هدایت شما به ایمان بر شما منت دارد.'),
(4630, 49, 18, 'یقیناً خدا نهان آسمان ها و زمین را می داند و خدا به آنچه انجام می دهید، بیناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'ق، سوگند به قرآن مجید [که محمّد، فرستاده ماست و وقوع قیامت حق است.]'),
(4632, 50, 2, '[کفر پیشگان نه تنها ایمان نیاوردند] بلکه از اینکه بیم دهنده ای از خودشان به سوی آنان آمد تعجب کردند و در نتیجه کافران گفتند: این چیزی عجیب است.'),
(4633, 50, 3, 'آیا هنگامی که مردیم و خاک شدیم [زنده می شویم و به حیات دوباره باز می گردیم؟] این بازگشتی دور [از عقل] است.'),
(4634, 50, 4, 'بی تردید ما آنچه را که زمین از اجسادشان می کاهد، می دانیم و نزد ما کتابی ضبط کننده [همه امور چون لوح محفوظ] است.'),
(4635, 50, 5, '[اینان نه اینکه زنده شدن پس از مرگ را باور نکردند] بلکه حق را هم هنگامی که به سویشان آمد، تکذیب کردند پس در حالتی آشفته و سردرگم اند.'),
(4636, 50, 6, 'آیا با تأمل به آسمان بالای سرشان ننگریستند که چگونه آن را بنا کرده و بیاراستیم و آن را هیچ شکاف [و ناموزونی] نیست؟'),
(4637, 50, 7, 'و زمین را گستردیم و کوه هایی استوار در آن افکندیم و در آن از هر نوع گیاه خوش منظر و دل انگیزی رویاندیم،'),
(4638, 50, 8, 'تا برای هر بنده ای که [با اندیشیدن در نظام هستی] به سوی خدا باز می گردد، مایه بینایی و یادآوری باشد؛'),
(4639, 50, 9, 'و از آسمان آبی بسیار پربرکت و سودمند نازل کردیم، پس به وسیله آن باغ ها و دانه های دروکردنی را رویاندیم.'),
(4640, 50, 10, 'و [نیز] درختان بلندقامت خرما را که خوشه های متراکم و روی هم چیده دارند [رویاندیم.]'),
(4641, 50, 11, 'برای آنکه رزق و روزی بندگان باشد، و نیز به وسیله آن آب سرزمین مرده را زنده کردیم؛ و بیرون آمدنشان [پس از مرگ از خاک گور برای ورود به قیامت] این گونه است.'),
(4642, 50, 12, 'پیش از اینان [که تو را تکذیب می کنند] قوم نوح و اصحاب رس و [قوم] ثمود [پیامبرانشان] را تکذیب کردند.'),
(4643, 50, 13, 'و نیز قوم عاد و فرعون و برادران لوط،'),
(4644, 50, 14, 'و اصحاب ایکه و قوم تُبّع همگی پیامبران را تکذیب کردند، پس تهدیدم [به نزول عذاب] بر آنان محقق و ثابت شد.'),
(4645, 50, 15, 'آیا ما از آفرینش نخستین عاجز و درمانده شدیم [تا از دوباره آفریدن عاجز و درمانده شویم؟ چنین نیست] بلکه آنان [با این همه دلایل روشن و استوار] باز در آفرینش جدید در تردیدند.'),
(4646, 50, 16, 'همانا انسان را آفریدیم و همواره آنچه را که باطنش [نسبت به معاد و دیگر حقایق] به او وسوسه می کند، می دانیم، و ما به او از رگ گردن نزدیک تریم.'),
(4647, 50, 17, '[یاد کن] دو فرشته ای را که همواره از ناحیه خیر و از ناحیه شر، ملازم انسان هستند و همه اعمالش را دریافت کرده و ضبط می کنند.'),
(4648, 50, 18, 'هیچ سخنی را به زبان نمی گوید جز اینکه نزد آن [برای نوشتن و حفظش] نگهبانی آماده است؛'),
(4649, 50, 19, 'و سکرات و بیهوشی مرگ، حق را [که همه واقعیات جهان پس از مرگ است] می آورد [و به محتضر می گویند:] این همان چیزی است که از آن می گریختی؛'),
(4650, 50, 20, 'و در صور می دمند؛ آن است روز [تحقق و ظهور] وعده های تهدیدآمیز و وحشتناک.'),
(4651, 50, 21, 'هر کسی در آن روز می آید در حالی که سوق دهنده ای و شاهدی با اوست، [که سوق دهنده او را به محشر می راند و شاهد بر اعمالش گواهی می دهد.]'),
(4652, 50, 22, '[به او می گویند:] تو از این روز بزرگ در بی خبری و غفلت بودی، پس ما پرده بی خبری را از دیده [بصیرت] ات کنار زدیم در نتیجه دیده ات امروز بسیار تیزبین است.'),
(4653, 50, 23, 'و [فرشته] همراهش می گوید: این است نامه اعمالش که همراه من آماده است.'),
(4654, 50, 24, '[به دو فرشته نگهبان و حافظ اعمال فرمان می دهند] شما دو نفر هر کافر سرسخت و معاندی را در دوزخ اندازید.'),
(4655, 50, 25, '[همان که به شدت] مانع خیر و تجاوزکار [از حدود خدا] و تردید کننده در حقایق [و تردید انداز در دل های مردم بود.]'),
(4656, 50, 26, 'که با خدا معبودی دیگر قرار داد، پس او را در عذاب سخت اندازید،'),
(4657, 50, 27, 'همنشینش [از میان شیطان ها] می گوید: پروردگارا! من او را به سرکشی و طغیان وا نداشتم، او خودش [به میل خود] در گمراهی دور و درازی بود. ….'),
(4658, 50, 28, '[خدا] می گوید: در پیشگاه من با یکدیگر ستیزه مکنید، بی تردید من تهدید [به عذاب] را پیش از این [از طریق وحی و پیامبران] به شما اعلام کرده بودم.'),
(4659, 50, 29, 'نزد من فرمان [تهدیدآمیز به اینکه هر کس با حال کفر و شرک وارد آخرت شود حتماً دوزخی است] تغییر نمی یابد و من نسبت به بندگان ستمکار نیستم.'),
(4660, 50, 30, '[یاد کن] روزی را که به دوزخ می گوییم: آیا پر شدی؟ می گوید: آیا زیادتر از این هم هست؟'),
(4661, 50, 31, 'و بهشت را به پرهیزکاران نزدیک می کنند بی آنکه فاصله ای از آنان داشته باشد.'),
(4662, 50, 32, '[به آنان می گویند:] این همان است که [در دنیا] وعده داده می شدید [این جایگاه رفیع] برای هر کسی است که [در دنیا به سوی خدا] باز می گشته [و] حافظ و نگهبان [عهد، پیمان الهی و حقوق خدا و مردم] بوده است.'),
(4663, 50, 33, 'همان کسی که در نهان از [خدای] رحمان می ترسید و دلی رجوع کننده [به سوی خدا] آورده است؛'),
(4664, 50, 34, 'به سلامت در آن وارد شوید، امروز روز جاودانگی است'),
(4665, 50, 35, 'در آنجا هرچه بخواهند برای آنان فراهم است، و نزد ما [نعمت های] بیشتری است.'),
(4666, 50, 36, 'و چه بسیار اقوامی را که پیش از آنان هلاک کردیم که از آنان نیرومندتر بودند، و [با نیرویشان] به سرزمین ها رفتند [و آنها را فتح کردند]، آیا توانستند [از عذاب و هلاکت] گریزگاهی بیابند؟'),
(4667, 50, 37, 'بی تردید در سرگذشت پیشینیان مایه پند و عبرتی است برای کسی که نیروی تعقّل دارد، یا با تأمل و دقت [به سرگذشت ها] گوش فرا می دهد در حالی که حاضر به شنیدن و فراگیری شنیده های خود باشد.'),
(4668, 50, 38, 'همانا ما آسمان ها و زمین و آنچه را میان آنهاست در شش روز آفریدیم، و هیچ رنج و درماندگی به ما نرسید.'),
(4669, 50, 39, 'پس [بر آزار مشرکان] شکیبا باش، و پیش از طلوع خورشید و پیش از غروب آن پروردگارت را همراه با سپاس و ستایش تسبیح گوی،'),
(4670, 50, 40, 'و در پاره ای از شب و نیز پس از سجده ها خدا را تسبیح گوی،'),
(4671, 50, 41, 'و گوش فرا ده روزی که ندا دهنده از جایی نزدیک ندا می دهد.'),
(4672, 50, 42, 'روزی که [همگان] صیحه و فریاد را به حق و درستی می شنوند [و] آن روز، روز بیرون آمدن [از قبرها] ست.'),
(4673, 50, 43, 'بی تردید ماییم که زنده می کنیم و می میرانیم، و بازگشت به سوی ماست.'),
(4674, 50, 44, 'روزی که زمین از [روی اجساد] آنان در حالی که [از قبرها بیرون آمده] شتابان [به سوی ندا کننده خود بروند] بشکافد؛ این گردآوردنشان از گورها [وحاضر کردنشان در قیامت] بر ما آسان است.'),
(4675, 50, 45, 'ما به آنچه [دشمنان لجوج بر ضد حقایق] می گویند، داناتریم و تو را بر آنان تسلطی نیست [که به قبول حقایق وادارشان کنی]؛ پس به وسیله قرآن کسانی را که از تهدید من می ترسند، بیم ده.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهای افشان کننده [که ابرهای باران زا و گرده های نرینه و مادینه گیاهان را می افشانند.]'),
(4677, 51, 2, 'سوگند به ابرهایی که بار سنگین باران را با خود [به نواحی مختلف] حمل می کنند.'),
(4678, 51, 3, 'و سوگند به کشتی هایی که به آسانی روی آب دریاها روانند.'),
(4679, 51, 4, 'و سوگند به فرشتگانی که [به امر خدا] کارها را [برای تدبیر امور هستی میان خود] تقسیم می کنند،'),
(4680, 51, 5, 'که بی تردید آنچه را [از اوضاع و احوال روز جزا] به شما وعده می دهند، راست ویقینی است.'),
(4681, 51, 6, 'وقطعاً [روز] جزا واقع خواهد شد.'),
(4682, 51, 7, 'و سوگند به آسمان که داری اعتدال و زیبایی و آراستگی است؛'),
(4683, 51, 8, 'که شما [در رابطه با قرآن و حقایقی که قرآن با دلیل و برهان اثبات می کند] در گفتاری متناقض و گوناگون هستید.'),
(4684, 51, 9, 'از قرآن [به باطل] منحرف می شود کسی که [از خیر و سعادت] منحرف شده است.'),
(4685, 51, 10, 'مرگ بر دروغ پردازان [که بدون دلیل و به ناحق درباره قرآن و حقایقش سخن پراکنی می کنند.]'),
(4686, 51, 11, 'همانان که در جهالتی عمیق و فراگیر در بی خبری و غفلتی سنگین فرو رفته اند!'),
(4687, 51, 12, '[همواره] می پرسند: روز جزا چه زمانی خواهد بود؟'),
(4688, 51, 13, 'همان روزی است که آنان را در آتش می سوزانند.'),
(4689, 51, 14, '[و به آنان گویند:] عذابتان را بچشید، این همان عذابی است که [از روی ریشخند] شتاب در آمدنش را می خواستید.'),
(4690, 51, 15, 'بی تردید پرهیزکاران در بهشت ها و چشمه سارهایند.'),
(4691, 51, 16, 'آنچه را پروردگارشان به آنان عطا کرده دریافت می کنند؛ زیرا که آنان پیش از این همواره نیکوکار بودند.'),
(4692, 51, 17, 'آنان اندکی از شب را می خوابیدند [و بیشتر آن را به عبادت و بندگی می گذراندند.]'),
(4693, 51, 18, 'و سحرگاهان از خدا درخواست آمرزش می کردند.'),
(4694, 51, 19, 'و در اموالشان حقّی برای سائل تهیدست و محروم از معیشت بود.'),
(4695, 51, 20, 'در زمین برای اهل یقین نشانه هایی [بر توحید، ربوبیت و قدرت خدا] ست.'),
(4696, 51, 21, 'و [نیز] در وجود شما [نشانه هایی است] آیا نمی بینید؟'),
(4697, 51, 22, 'و رزق شما و آنچه به آن وعده داده می شوید، در آسمان است'),
(4698, 51, 23, 'پس سوگند به پروردگار آسمان و زمین که آنچه را که وعده داده می شوید، حقّ و یقینی است، همان گونه که شما [وقت سخن گفتن یقین دارید] سخن می گویید.'),
(4699, 51, 24, 'آیا خبر مهمانان ارجمند و بزرگوار ابراهیم به تو رسیده است؟'),
(4700, 51, 25, 'زمانی که بر او وارد شدند، پس سلام گفتند. گفت: سلام، [شما] مردمی ناشناسید!'),
(4701, 51, 26, 'پس به سوی خانواده اش بازگشت و گوساله ای فربه [و بریان شده] آورد.'),
(4702, 51, 27, 'پس آن را نزدیکشان برد [ولی دید نمی خورند]؛ گفت: مگر نمی خورید؟'),
(4703, 51, 28, 'و [در دلش] از آنان احساس ترسی کرد. گفتند: مترس. و او را به پسری دانا مژده دادند.'),
(4704, 51, 29, 'در این میان همسرش [که مژده را شنیده بود] با فریادی [از روی تعجب] در حالی که به چهره اش زد سر رسید و گفت: پیرزنی نازا [و فرزند؟!]'),
(4705, 51, 30, 'گفتند: پروردگارت چنین گفته است؛ یقیناً او حکیم و داناست.'),
(4706, 51, 31, '[ابراهیم] گفت: ای فرستادگان! دنبال چه کار مهمی هستید؟'),
(4707, 51, 32, 'گفتند: ما را به سوی مردمی گنهکار فرستاده اند؛'),
(4708, 51, 33, 'تا [بارانی] از گِل سنگ بر آنان بفرستیم؛'),
(4709, 51, 34, '[سنگی که] نزد پروردگارت برای تجاوزکاران نشانه گذاری شده است؛'),
(4710, 51, 35, 'پس هرکه را از مؤمنان در آن شهر بود [پیش از آمدن عذاب] بیرون بردیم؛'),
(4711, 51, 36, 'در آنجا جز یک خانه از مسلمانان [که اهلش مطیع خدا بودند، نیافتیم.]'),
(4712, 51, 37, 'در آنجا [پس از زیر و رو شدن شهر و هلاکت مردم] نشانه و عبرتی [از قدرت، غضب خود و محکومیت مجرمان] برای کسانی که از عذاب دردناک می ترسند به جا گذاشتیم.'),
(4713, 51, 38, 'و [نیز] در [سرگذشت] موسی، چون او را با دلیلی روشن به سوی فرعون فرستادیم [عبرتی است.]'),
(4714, 51, 39, 'پس فرعون با سپاهیانش روی [از حق] گرداندند و گفت: [این مرد] یا جادوگر است یا دیوانه!'),
(4715, 51, 40, 'در نتیجه او و سپاهیانش را گرفتیم و به دریا افکندیم، در حالی که [به سبب لجبازی و دشمنی] مستحقّ سرزنش بود.'),
(4716, 51, 41, 'و [نیز] در [سرگذشت قوم] عاد هنگامی که بر آنان باد هلاک کننده ای فرستادیم [عبرتی است.]'),
(4717, 51, 42, '[آن باد] هیچ چیزی را که بر آن می وزید، رها نمی کرد مگر آنکه آن را به صورت استخوان پوسیده ای می ساخت.'),
(4718, 51, 43, 'و [نیز] در قوم ثمود [عبرتی است] هنگامی که [پس از پی کردن ناقه] به آنان گفته شد: زمانی کوتاه [که بیش از سه روز نیست، از زندگی] برخوردار باشید [که عذاب خواهد رسید.]'),
(4719, 51, 44, 'ولی [آنان در آن سه روز هم که مهلت داشتند باز] از فرمان پروردگارشان سرپیچی کردند، پس صاعقه مرگبار آنان را فراگرفت در حالی که آنان با ترس و حیرت به آن می نگریستند.'),
(4720, 51, 45, 'پس [با آمدن عذاب و به سبب اینکه هیچ مهلتی نیافتند] نه قدرت برخاستن از جای خود را داشتند و نه دادخواهی کردند.'),
(4721, 51, 46, 'و قوم نوح را پیش از این [هلاک کردیم]؛ زیرا آنان همواره مردمی نافرمان بودند.'),
(4722, 51, 47, 'و آسمان را به قدرت ونیرو بنا کردیم و ما [همواره] وسعت دهنده ایم.'),
(4723, 51, 48, 'و زمین را گستردیم و چه نیکو گستراننده ای هستیم.'),
(4724, 51, 49, 'واز هر چیزی جفت آفریدیم باشد که متذکّر [این حقایق که نشانه هایی بر قدرت، حکمت و ربوبیت خداست] شوید.'),
(4725, 51, 50, 'پس به سوی خدا بگریزید [که] بی تردید من از سوی او بیم دهنده ای روشنگرم.'),
(4726, 51, 51, 'و با خدا معبودی دیگر قرار ندهید، یقیناً من از سوی او بیم دهنده ای آشکارم.'),
(4727, 51, 52, 'همچنین هیچ پیامبری بر کسانی که پیش از اینان بودند نیامد مگر اینکه گفتند: جادوگر یا دیوانه است!'),
(4728, 51, 53, 'آیا [این اقوام] یکدیگر را به این گونه داوری [ناحق درباره پیامبرشان] سفارش کرده بودند؟! [نه] بلکه همه آنان گروهی یاغی و سرکش بودند [و این یاوه گویی ها، محصول سرکشی و یاغی گری آنان بود.]'),
(4729, 51, 54, 'بنابراین از این سبک مغزان باطل گو، روی برگردان [که تو اتمام حجت کردی] و شایسته سرزنش نیستی؛'),
(4730, 51, 55, 'و پند ده؛ زیرا پند به مؤمنان سود می دهد؛'),
(4731, 51, 56, 'و جن و انس را جز برای اینکه مرا بپرستند نیافریدیم؛'),
(4732, 51, 57, 'از آنان هیچ رزقی نمی خواهم، و نمی خواهم که مرا طعام دهند.'),
(4733, 51, 58, 'بی تردید خدا خود روزی دهنده و صاحب قدرت استوار است.'),
(4734, 51, 59, 'پس برای کسانی [از قوم تو] که ستم ورزیدند، سهمی از عذاب است مانند سهم هم مسلکانشان [از امت های پیشین]، بنابراین شتاب در آمدن آن را نخواهند.'),
(4735, 51, 60, 'پس وای بر کسانی که کافر شدند از آن روزشان که به آن وعده داده می شوند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به [کوهِ] طور،'),
(4737, 52, 2, 'و به کتابی که نوشته شده،'),
(4738, 52, 3, 'در صفحه ای باز و گسترده،'),
(4739, 52, 4, 'و به آن خانه آباد،'),
(4740, 52, 5, 'و به آن سقف برافراشته،'),
(4741, 52, 6, 'و به آن دریای مملو و برافروخته،'),
(4742, 52, 7, 'که بی تردید عذاب پروردگارت واقع شدنی است؛'),
(4743, 52, 8, 'و آن را هیچ مانع و بازدارنده ای نیست.'),
(4744, 52, 9, '[و آن در] روزی [است] که آسمان به حرکت و لرزه ای سخت در آید؛'),
(4745, 52, 10, 'و کوه ها [چون گرد و غبار] سریع و تند، روان شوند.'),
(4746, 52, 11, 'پس در آن روز وای بر تکذیب کنندگان!'),
(4747, 52, 12, 'همانان که با فرو رفتن [در گفتار و کردار باطل با حقایق] بازی می کنند.'),
(4748, 52, 13, 'روزی که آنان را با خشونت و زور به سوی آتش می رانند.'),
(4749, 52, 14, '[و به آنان می گویند:] این همان آتشی است که همواره آن را تکذیب می کردید.'),
(4750, 52, 15, 'آیا این آتش هم جادوست [چنانکه در دنیا وحی را جادو می پنداشتید] یا شما [این واقعیت آشکار را] نمی بینید [چنانکه در دنیا حقایق را نمی دیدید؟]'),
(4751, 52, 16, 'در آن در آیید [و بسوزید]؛ پس صبر کنید یا نکنید برای شما یکسان است. فقط اعمالی را که همواره انجام می دادید، جزا داده می شوید.'),
(4752, 52, 17, 'بی تردید پرهیزکاران در بهشت ها و نعمتی فراوان اند.'),
(4753, 52, 18, 'به آنچه پروردگارشان به آنان عطا کرده و [برای آنکه] پروردگارشان آنان را از عذاب دوزخ مصون داشته، شادمان و مسرورند.'),
(4754, 52, 19, '[به آنان گویند:] به پاداش اعمالی که همواره انجام می دادید [از این نعمت ها] بخورید و بیاشامید، گوارایتان باد.'),
(4755, 52, 20, '[این] در حالی [است] که بر تخت هایی ردیف و به هم پیوسته تکیه می زنند و حورالعین را به همسری آنان در می آوریم.'),
(4756, 52, 21, 'و کسانی که ایمان آوردند و فرزندانشان [به نوعی] در ایمان از آنان پیروی کردند، فرزندانشان را [در بهشت] به آنان ملحق می کنیم و هیچ چیز از اعمالشان را نمی کاهیم؛ هر انسانی در گرو اعمال خویش است.'),
(4757, 52, 22, 'و [همواره] به آنان انواع میوه ها و گوشتهایی که دلخواه آنان است، می رسانیم.'),
(4758, 52, 23, 'آنان در بهشت، جام های [پر از شراب طهور را] که نه در آن [پس از نوشیدنش، زمینه] بیهوده گویی و نه [خوردنش] گناه است، از دست یکدیگر می گیرند.'),
(4759, 52, 24, 'و [همواره برای پذیرایی از آنان] نوجوانانی پیرامونشان می گردند که گویا مرواریدی نهفته در صدف هستند.'),
(4760, 52, 25, 'و به یکدیگر روی می کنند [و از احوالات گذشته] از هم می پرسند.'),
(4761, 52, 26, 'می گویند: ما پیش تر [در دنیا] در میان کسان خود [از عذاب امروز] ترسان بودیم؛'),
(4762, 52, 27, 'ولی خدا بر ما منت نهاد و ما را از عذاب مرگبار حفظ کرد.'),
(4763, 52, 28, 'از پیش او را [برای نجات از عذاب] می خواندیم؛ زیرا که او نیکوکار و مهربان است.'),
(4764, 52, 29, 'پس [مشرکان را] هشدار ده که تو به لطف و رحمت پروردگارت نه کاهنی نه دیوانه؛'),
(4765, 52, 30, 'بلکه این سبک مغزانند که می گویند: او شاعری است که برای او حوادث تلخ روزگار و آمدن مرگش را انتظار می بردیم!'),
(4766, 52, 31, 'بگو: [هلاکت خود را] انتظار برید که من هم با شما از منتظرانم.'),
(4767, 52, 32, 'آیا عقل هایشان آنان را به این گفتارهای باطل و یاوه [بر ضد تو و قرآن] وا می دارد، یا [نه] آنان مردمی طغیان گر و سرکش اند؟'),
(4768, 52, 33, 'یا می گویند: قرآن را از پیش خود ساخته [و به خدا نسبت داده است؟ چنین نیست که می پندارند]، بلکه [قرآن وحی الهی است، ولی اینان] ایمان نمی آورند.'),
(4769, 52, 34, 'پس اگر [در ادعای خود] راستگویید، سخنی مانند آن بیاورید.'),
(4770, 52, 35, 'آیا آنان از غیر [آن] چیزی [که دیگران را از آن آفریده اند،] آفریده شده اند.'),
(4771, 52, 36, 'یاآنان آسمان ها و زمین را آفریده اند؟ نه، بلکه یقین ندارند.'),
(4772, 52, 37, 'آیا خزانه های [علم و قدرت] پروردگارت نزد آنان است [که هر کس را اینان به پیامبری برگزینند، پیامبر است و چون تو را پیامبر نمی دانند، پس پیامبر نیستی؟] یا بر تدبیر امور هستی و بر خدا مسلط اند [که اگر بخواهند این مقام را از تو سلب کنند، بتوانند؟]'),
(4773, 52, 38, 'یا برای آنان نردبانی است که [بر آن برشوند و] در آن خبرهای غیبی را بشنوند؟ پس باید شنونده اش دلیلی آشکار [بر ادعایش] بیاورد؟'),
(4774, 52, 39, 'یا سهم خدا دختران است و سهم شما پسران؟'),
(4775, 52, 40, 'یا از آنان [در برابرابلاغ رسالت] پاداشی می خواهی که از خسارت آن سنگین بارند؟'),
(4776, 52, 41, 'یا علم غیب نزد آنان است که آنان [ادعای خود را از روی آن] می نویسند؟'),
(4777, 52, 42, 'یا می خواهند [بر ضد تو] نیرنگ و فریبی به کار گیرند؟ ولی کافران [بدانند که] خود اسیر و محکوم نیرنگ شده اند. ….'),
(4778, 52, 43, 'آیا آنان را معبودی جز خداست؟ خدا از آنچه شریک او قرار می دهند، منزّه است. ….'),
(4779, 52, 44, 'و اگر پاره سنگی در حال سقوط از آسمان ببینند [فکر نمی کنند عذاب است، بلکه چنان بر انکار حق اصرار دارند که] می گویند: ابری متراکم است!'),
(4780, 52, 45, '[اکنون که با هیچ دلیلی هدایت نمی شوند] پس آنان را واگذار تا آن روزشان را که در آن هلاک می شوند، ببینند.'),
(4781, 52, 46, 'روزی که نیرنگشان چیزی از عذاب را از آنان دفع نمی کند و یاری هم نخواهند شد.'),
(4782, 52, 47, 'برای ستمکاران جز این عذاب آخرتی عذابی دیگر [در دنیا] خواهد بود [و آن درهم کوبیده شدنشان در جنگ بدر است]، ولی بیشترشان معرفت و آگاهی [به وضع خود در آینده] ندارند.'),
(4783, 52, 48, 'و در برابر حکم پروردگارت شکیبایی کن که تو زیر نظر و مراقبت ما هستی، و هنگامی که [از خواب] برمی خیزی پروردگارت را همراه با سپاس و ستایش تسبیح گوی.'),
(4784, 52, 49, 'و [نیز] پاره ای از شب و هنگام ناپدید شدن ستارگان [به سبب روشنایی صبح] خدا را تسبیح گوی.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به ستاره هنگامی که [برای غروب کردن در کرانه افق] افتد؛'),
(4786, 53, 2, 'که هرگز دوست شما از راه راست منحرف نشده، و [در ایمان و اعتقادش از راه راست] خطا نرفته؛ ….'),
(4787, 53, 3, 'و از روی هوا و هوس سخن نمی گوید.'),
(4788, 53, 4, 'گفتار او چیزی جز وحی که به او نازل می شود، نیست.'),
(4789, 53, 5, '[فرشته] بسیار نیرومند به او تعلیم داده است.'),
(4790, 53, 6, '[همان که] دارای درایت و توانمندی شگفتی است، پس [به آنچه که مأمور انجامش می باشد] مسلط و چیره است.'),
(4791, 53, 7, 'در حالی که در افق اعلا بود.'),
(4792, 53, 8, 'سپس نزدیک رفت و نزدیک تر شد'),
(4793, 53, 9, 'پس [فاصله اش با پیامبر] به اندازه فاصله دو کمان گشت یا نزدیک تر شد.'),
(4794, 53, 10, 'آن گاه به بنده اش آنچه را باید وحی می کرد، وحی کرد.'),
(4795, 53, 11, 'آنچه را دل [پیامبر] دید [به پیامبر] دروغ نگفت [تا او را درباره حقیقت فرشته وحی به وهم و خیال اندازد، بلکه به حضور و شهودش یقین کامل داشت.]'),
(4796, 53, 12, 'آیا در آنچه [به حقیقت] می بینید با او به سختی مجادله و ستیزه می کنید؟'),
(4797, 53, 13, 'و بی تردید یک بار دیگر هم او را دیده است'),
(4798, 53, 14, 'نزد سدرة المنتهی،'),
(4799, 53, 15, 'در آنجا که جنت الماوی است.'),
(4800, 53, 16, 'آن گاه که سدره را احاطه کرده بود آنچه [از فرشتگان، نور و زیبایی] احاطه کرده بود.'),
(4801, 53, 17, 'دیده [پیامبر آنچه را دید] بر غیر حقیقت و به خطا ندید و از مرز دیدن حقیقت هم درنگذشت.'),
(4802, 53, 18, 'به راستی که بخشی از نشانه های بسیار بزرگ پروردگارش را دید.'),
(4803, 53, 19, 'پس به من از لات و عزّی [دو بت خویش] خبر دهید'),
(4804, 53, 20, 'و منات، سومین [بت] دیگرتان [که شما آنها را تمثال فرشتگانی به عنوان دختران خدا می پندارید،]'),
(4805, 53, 21, 'آیا [به پندار شما] ویژه شما پسر و ویژه او دختر است؟!'),
(4806, 53, 22, 'در این صورت این تقسیمی ظالمانه است.'),
(4807, 53, 23, 'این بتان [که شما آنها را به عنوان شریک خدا گرفته اید] چیزی جز نام ها [ی بی معنا و بی مفهوم] که شما و پدرانتان [بر اساس حدس و گمان] نامگذاری کرده اید نیستند، خدا بر [حقّانیّت] آنها هیچ دلیلی نازل نکرده است. اینان فقط از پندار و گمان [بی پایه] و هواهای نفسانی پیروی می کنند، در حالی که مسلماً از سوی پروردگارشان برای آنان هدایت آمده است.'),
(4808, 53, 24, 'مگر برای انسان آنچه را [چون حاجت بخشی بتان و شفاعت آنان] آرزو می کند، فراهم است؟ ….'),
(4809, 53, 25, 'آخرت و دنیا فقط در سیطره مالکیّت و فرمانروایی خداست.'),
(4810, 53, 26, 'و چه بسیار فرشتگانی که در آسمان ها هستند که شفاعتشان هیچ سودی نمی بخشد مگر پس از آنکه خدا برای هر که بخواهد و بپسندد، اجازه دهد.'),
(4811, 53, 27, 'مسلماً کسانی که به آخرت ایمان ندارند، فرشتگان را در نامگذاری به نام زن نامگذاری می کنند؛'),
(4812, 53, 28, 'و آنان را به این کار هیچ آگاهی و معرفت نیست. [آنان] فقط از گمان پیروی می کنند، و بی تردید گمان [انسان را] برای دریافت حق، هیچ سودی نمی دهد.'),
(4813, 53, 29, 'بنابراین از کسانی که از یاد ما روی گردانده اند و جز زندگی دنیا را نخواسته اند، روی بگردان.'),
(4814, 53, 30, 'این [دنیا خواهی] آخرین مرز دانش و معرفت آنان است؛ یقیناً پروردگارت به کسی که از راه او منحرف شده، داناتر است، و او به کسی که هدایت یافته، آگاه تر است.'),
(4815, 53, 31, 'و آنچه را در آسمان ها و آنچه را در زمین است، فقط در سیطره مالکیّت و فرمانروایی خداست، تا کسانی را که مرتکب گناه شده اند، همان گناهانشان را به آنان کیفر دهد، و کسانی را که کار نیک کرده اند، همان کار نیکشان را به آنان پاداش دهد.'),
(4816, 53, 32, 'کسانی که از گناهان بزرگ و زشت کاری ها جز لغزش های کوچک دوری می کنند [مورد آمرزش اند] یقیناً آمرزش پروردگارت گسترده و وسیع است. او به شما از هنگامی که شما را از زمین به وجود آورد و از هنگامی که در شکم مادرانتان جنین بودید، داناتر است؛ پس خودستایی نکنید. او به کسی که پرهیزکاری پیشه کرده است، آگاه تر است.'),
(4817, 53, 33, 'آیا کسی را که [از حق] روی گردانید، دیدی؟'),
(4818, 53, 34, 'و اندکی [از مال خود] بخشید و [از باقی مانده آن] امساک ورزید.'),
(4819, 53, 35, 'آیا علم غیب نزد اوست و او می بیند [که بارگناهانش رادر قیامت دیگری برمی دارد؟]'),
(4820, 53, 36, 'یا او را به آنچه در صحیفه های موسی است خبر نداده اند؟'),
(4821, 53, 37, 'و [یا به آنچه در صحیفه های] ابراهیم [است] همان که به طور کامل [به پیمانش با خدا] وفا کرد [آگاهش نکرده اند؟]'),
(4822, 53, 38, 'که هیچ سنگین باری بار گناه دیگری را بر نمی دارد،'),
(4823, 53, 39, 'و اینکه برای انسان جز آنچه تلاش کرده [هیچ نصیب و بهره ای] نیست،'),
(4824, 53, 40, 'و اینکه تلاش او به زودی دیده خواهد شد؛'),
(4825, 53, 41, 'سپس به تلاشش پاداش کامل خواهند داد؛'),
(4826, 53, 42, 'و اینکه پایان [همه امور] به سوی پروردگارتوست؛'),
(4827, 53, 43, 'و این اوست که می خنداند و می گریاند؛'),
(4828, 53, 44, 'و هم اوست که می میراند و زنده می کند.'),
(4829, 53, 45, 'واوست که دو زوج نر وماده آفرید،'),
(4830, 53, 46, 'از نطفه هنگامی که در رحم ریخته شود.'),
(4831, 53, 47, 'و پدید آوردن جهان دیگر بر عهده اوست،'),
(4832, 53, 48, 'و اوست که شما را توانگر کرد و سرمایه قابل ذخیره بخشید،'),
(4833, 53, 49, 'و او پروردگار [ستاره] شِعری است،'),
(4834, 53, 50, 'و اوست که قوم عاد نخستین را هلاک کرد،'),
(4835, 53, 51, 'و [نیز] قوم ثمود را به طوری که [کسی از آنان را] باقی نگذاشت'),
(4836, 53, 52, 'وقوم نوح را پیش تر [هلاک کرد]؛ زیرا آنان ستمکارتر و سرکش تر بودند'),
(4837, 53, 53, 'و شهرها [ی قوم لوط] را زیر و رو کرد و به زمین کوبید.'),
(4838, 53, 54, 'پس [عذاب خدا] آنان را احاطه کرد آن مقدار که احاطه کرد.'),
(4839, 53, 55, 'پس [ای انسان!] در کدام یک از نعمت های پروردگارت تردید می کنی [که آیا از سوی خدا هست یا نیست؟!]'),
(4840, 53, 56, 'این پیامبر [نیز] بیم دهنده ای از [زمره] بیم دهندگان پیشین است.'),
(4841, 53, 57, 'قیامت نزدیک شد.'),
(4842, 53, 58, 'کسی جز خدا برطرف کننده [سختی ها و هول و هراسش] نیست.'),
(4843, 53, 59, 'آیا از این سخن تعجب می کنید؟'),
(4844, 53, 60, 'و [با چنین وضعی که دارید هنوز] می خندید و نمی گریید؟!'),
(4845, 53, 61, 'و همواره سرکشی می کنید و غافلانه به خوشی و خوشگذرانی مشغول هستید؟!'),
(4846, 53, 62, 'پس [با این وصف که قیامتی سنگین در پی دارید، بیایید] خدا را سجده کنید و بپرستید.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت بسیار نزدیک شد، و ماه از هم شکافت.'),
(4848, 54, 2, 'و اگر معجزه ای را ببینند، روی بگردانند و گویند: [این] جادویی همیشگی است!'),
(4849, 54, 3, 'و [قرآن، رسالت پیامبر و معجزه را] انکار کردند و هواهای نفسانی خود را پیروی نمودند، در حالی که هر کاری [چه خیر و چه شر، چه حق و چه باطل در قرارگاه ویژه خود] قرار می گیرد [و اینان به زودی خواهند دانست که قرآن و پیامبر بر حق است یا معبودانشان؟]'),
(4850, 54, 4, 'بی تردید آنچه از خبرها [ی گذشتگان] که [در آن] مایه بازداشتن [از کفر و شرک و گناهان] است برای آنان آمده است.'),
(4851, 54, 5, '[این خبرها] پندی رساست، ولی هشدارها [به این ستمکاران لجوج] سودی نمی بخشد.'),
(4852, 54, 6, 'بنابراین از آنان روی بگردان [و] روزی را [یاد کن] که آن دعوت کننده [یعنی اسرافیل] آنان را به امری بس دشوار و هولناک دعوت می کند.'),
(4853, 54, 7, 'در حالی که دیدگانشان [از شدت ترس] فرو افتاده، هم چون ملخ های پراکنده از گورها بیرون آیند.'),
(4854, 54, 8, 'شتابان به سوی آن دعوت کننده می روند و کافران می گویند: امروز روز بسیار سختی است.'),
(4855, 54, 9, 'پیش از آنان قوم نوح [پیامبران] را تکذیب کردند، پس بنده ما [نوح] را هم تکذیب کردند و گفتند: دیوانه است، و جن زده ای است [که آثاری از گزند جن در او می باشد.]'),
(4856, 54, 10, 'پس پروردگارش را خواند که من مغلوب شده ام [و از ستم و زورگویی آنان دلتنگم] بنابراین انتقام [مرا از اینان] بگیر.'),
(4857, 54, 11, 'پس [در پاسخ درخواستش] درهای آسمان را به آبی بسیار فراوان و ریزان گشودیم،'),
(4858, 54, 12, 'و زمین را به [جوشیدن] چشمه هایی [پر آب] شکافتیم؛ پس آبِ آسمان و زمین برای کاری که مقدّر شده بود به هم پیوستند،'),
(4859, 54, 13, 'و او را بر کشتی که دارای تخته ها و میخ ها بود سوار کردیم،'),
(4860, 54, 14, 'که زیر نظر ما روان بود. [و این] پاداشی بود برای کسی که مورد تکذیب قرار گرفته بود.'),
(4861, 54, 15, 'و بی تردید آن سرگذشت را [برای آیندگان] مایه پند و عبرت باقی گذاشتیم، پس آیا پند گیرنده ای هست؟'),
(4862, 54, 16, 'پس عذاب و هشدارهایم چگونه بود؟'),
(4863, 54, 17, 'و یقیناً ما قرآن را برای پند گرفتن آسان کردیم، پس آیا پند گیرنده ای هست؟'),
(4864, 54, 18, 'قوم عاد [پیامبرشان را] تکذیب کردند، پس عذاب و هشدارهایم چگونه بود؟'),
(4865, 54, 19, 'ما بر آنان در روزی شوم که شومی اش استمرار داشت، تندبادی سخت و بسیار سرد [به عنوان عذاب] فرستادیم،'),
(4866, 54, 20, 'که مردم را [از زمین] برمی کند [و بعد از آن عذاب،] گویی تنه های نخلی بودند که ریشه کن شده اند.'),
(4867, 54, 21, 'پس عذاب و هشدارهایم چگونه بود؟'),
(4868, 54, 22, 'و یقیناً ما قرآن را برای پند گرفتن آسان کردیم، پس آیا پند گیرنده ای هست؟'),
(4869, 54, 23, 'قوم ثمود بیم دهندگان را تکذیب کردند.'),
(4870, 54, 24, 'پس گفتند: آیا ما بشری از جنس خود را که [تک و] تنهاست [و جمعیت و نیرویی با خود ندارد] پیروی کنیم؟! در این صورت در گمراهی و دیوانگی خواهیم بود.'),
(4871, 54, 25, 'آیا از میان ما فقط بر او وحی نازل شده است؟! [نه، چنین نیست] بلکه او بسیار دروغگو و پر افاده و متکبر است [که می خواهد بر ما بزرگی کند.]'),
(4872, 54, 26, 'در فردای نزدیک خواهند دانست که بسیار دروغگو و پرافاده و متکبر کیست؟'),
(4873, 54, 27, '[به صالح گفتیم:] ما برای آزمایش آنان یقیناً آن ماده شتر [درخواست شده] را خواهیم فرستاد؛ پس در انتظار سرانجام آنان باش و شکیبایی پیشه کن.'),
(4874, 54, 28, 'و آنان را خبر ده که آب آشامیدنی میان آنان و ماده شتر تقسیم شده است؛ هریک در زمان نوبت خود بر سر آب حاضر شوند.'),
(4875, 54, 29, 'پس آنان یارشان را [که برای پی کردن ناقه آماده کرده بودند] فرا خواندند و [او] دست به کار شد و [ماده شتر را] پی کرد.'),
(4876, 54, 30, 'پس عذاب و هشدارهایم چگونه بود؟'),
(4877, 54, 31, 'ما بر آنان یک فریاد مرگبار فرستادیم، پس همه آنان به صورت گیاه خشکی که در آغل چهارپایان جمع می کنند، درآمدند.'),
(4878, 54, 32, 'و یقیناً ما قرآن را برای پند گرفتن آسان کردیم، پس آیا پند گیرنده ای هست؟'),
(4879, 54, 33, 'قوم لوط بیم دهندگان را تکذیب کردند.'),
(4880, 54, 34, 'ما بر آنان توفانی سخت که با خود ریگ و سنگ می آورد فرستادیم [در نتیجه همه را هلاک کرد]، مگر خاندان لوط را که سحرگاهان نجاتشان دادیم.'),
(4881, 54, 35, '[این] نعمت و رحمتی از سوی ما بود، این گونه کسی را که سپاس گزار است، پاداش می دهیم.'),
(4882, 54, 36, 'به راستی لوط آنان را به مؤاخذه سخت ما بیم داده بود، ولی [آنان با وی] در بیمها و هشدارها [یش] سرسختانه مجادله و ستیزه کردند.'),
(4883, 54, 37, 'و از میهمانانش [که در حقیقت فرشتگان ما بودند] کام جویی خواستند، در نتیجه دیدگانشان را محو و نابینا کردیم؛ پس [گفتیم: طعمِ] عذاب و هشدارهایم را بچشید.'),
(4884, 54, 38, 'به یقین در سپیده دم عذابی پیگیر و پایدار به آنان رسید.'),
(4885, 54, 39, 'پس [گفتیم: طعمِ] عذاب و هشدارهایم را بچشید.'),
(4886, 54, 40, 'و یقیناً ما قرآن را برای پند گرفتن آسان کردیم، پس آیا پند گیرنده ای هست؟'),
(4887, 54, 41, 'و همانا برای فرعونیان بیم دهندگان آمدند.'),
(4888, 54, 42, '[آنان] همه معجزات و آیات ما را تکذیب کردند؛ در نتیجه آنان را [به عذابی سخت] گرفتیم، گرفتن توانایی شکست ناپذیر و نیرومند.'),
(4889, 54, 43, 'آیا کافران [شهر] شما از اقوامی که برشمردیم، بهتر و برترند [تا به سبب بهتری و برتری مصون از عذاب باشند؟] یا برای شما در کتاب های آسمانی امان نامه ای هست [که هر جرمی را مرتکب شوید عذابی نخواهید داشت؟!]'),
(4890, 54, 44, 'یا [آنکه] می گویند: ما گروهی متحد و پشتیبان یکدیگریم [که هرکس بخواهد آسیبی به ما رساند، با قدرت هرچه تمام تر از او انتقام می گیریم؟]'),
(4891, 54, 45, 'به زودی [این] گروه [متحد در جنگی که خود بر ضد مؤمنان تدارک می بینند] شکست می خورند و پشت کنان می گریزند.'),
(4892, 54, 46, 'بلکه وعده گاهشان قیامت است، و قیامت هولناک تر و تلخ تر است.'),
(4893, 54, 47, 'بی تردید گنهکاران در گمراهی و انحراف و در آتش افروخته اند.'),
(4894, 54, 48, 'روزی که با صورت در آتش کشیده می شوند [و به آنان می گویند:] سوزندگی و عذاب دردناک دوزخ را بچشید.'),
(4895, 54, 49, 'ما هر چیزی را به اندازه آفریدیم.'),
(4896, 54, 50, 'و فرمان ما جز فرمان واحدی نیست که مانند یک چشم بر هم زدن است.'),
(4897, 54, 51, 'بی تردید ما هم مسلکان شما را [که در گذشته به سر می بردند، به خاطر طغیانشان] هلاک کردیم؛ پس آیا پند گیرنده ای هست؟'),
(4898, 54, 52, 'و هر عملی را که انجام دادند در نامه های اعمالشان ثبت است.'),
(4899, 54, 53, 'و هر کوچک و بزرگی [از اعمالشان در آن] نوشته شده است.'),
(4900, 54, 54, 'یقیناً پرهیزکاران در بهشت ها و [کنار] نهرها هستند؛'),
(4901, 54, 55, 'در جایگاهی حق و پسندیده نزد پادشاهی توانا.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '[خدای] رحمان'),
(4903, 55, 2, 'قرآن را تعلیم داد.'),
(4904, 55, 3, 'انسان را آفرید؛'),
(4905, 55, 4, 'به او بیان آموخت؛'),
(4906, 55, 5, 'خورشید و ماه با حسابی [منظم و دقیق] روانند؛'),
(4907, 55, 6, 'و گیاه و درخت همواره [برای او] سجده می کنند؛'),
(4908, 55, 7, 'و آسمان را برافراشت و [برای سنجش هر امر معنوی و مادی] ترازو نهاد؛'),
(4909, 55, 8, 'تا در [سنجیدن با] ترازو طغیان روا مدارید [و از مرز عدالت و انصاف مگذرید.]'),
(4910, 55, 9, 'و ترازو را به عدالت برپا دارید و از ترازو مکاهید؛'),
(4911, 55, 10, 'و زمین را برای [زندگی] مردم نهاد؛'),
(4912, 55, 11, 'در آن میوه ها و نخل ها با خوشه های غلاف دار است؛'),
(4913, 55, 12, 'و دانه های با سبوس و کاه و گیاهان خوشبوست،'),
(4914, 55, 13, 'پس [ای انس و جن!] کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4915, 55, 14, 'انسان را از گلی خشکیده مانند سفال آفرید.'),
(4916, 55, 15, 'و جن را از آمیزه ای [از شعله های مختلف] آتش به وجود آورد.'),
(4917, 55, 16, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4918, 55, 17, 'پروردگار دو مشرق و پروردگار دو مغرب است.'),
(4919, 55, 18, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4920, 55, 19, 'دو دریای [شیرین و شور] را روان ساخت در حالی که همواره باهم تلاقی و برخورد دارند؛'),
(4921, 55, 20, '[ولی] میان آن دو حایلی است که به هم تجاوز نمی کنند [درنتیجه باهم مخلوط نمی شوند!]'),
(4922, 55, 21, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4923, 55, 22, 'از آن دو دریا لؤلؤ و مرجان بیرون می آید.'),
(4924, 55, 23, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4925, 55, 24, 'و او را در دریا کشتی های بادبان برافراشته چون کوه هاست.'),
(4926, 55, 25, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4927, 55, 26, 'همه آنان که روی این زمین هستند، فانی می شوند.'),
(4928, 55, 27, 'و تنها ذات باشکوه و ارجمند پروردگارت باقی می ماند.'),
(4929, 55, 28, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4930, 55, 29, 'هر که در آسمان ها و زمین است از او درخواست [حاجت] می کند، او هر روز در کاری است.'),
(4931, 55, 30, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4932, 55, 31, 'ای انس و جن! به زودی به [حساب] شما می پردازیم.'),
(4933, 55, 32, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4934, 55, 33, 'ای گروه جن و انس! اگر می توانید از کرانه ها و نواحی آسمان ها و زمین بیرون روید، پس بیرون روید؛ نمی توانید بیرون روید مگر با نوعی توانایی و قدرت.'),
(4935, 55, 34, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4936, 55, 35, 'بر شما شعله هایی از آتش بی دود و دود آتش آلود فرستاده خواهد شد، در نتیجه نمی توانید یکدیگر را [برای دفع عذاب] یاری دهید!'),
(4937, 55, 36, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4938, 55, 37, 'و ناگهان آسمان بشکافد و چون چرمی سرخ رنگ و گلگون شود.'),
(4939, 55, 38, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4940, 55, 39, 'پس در آن روز [به علت سرعت فوق العاده حسابرسی و روشن بودن همه امور] هیچ انس و جنّی را از گناهش نپرسند.'),
(4941, 55, 40, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4942, 55, 41, 'گناهکاران به نشانه هایشان شناخته می شوند، پس آنان را به موهای پیش سرو به پاهایشان می گیرند [و به آتش می اندازند.]'),
(4943, 55, 42, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4944, 55, 43, 'این همان دوزخی است که گنهکاران آن را دروغ می پنداشتند.'),
(4945, 55, 44, 'آنان در میان آتش و آب جوشان رفت و آمد می کنند'),
(4946, 55, 45, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4947, 55, 46, 'و برای کسی که از مقام پروردگارش [که احاطه و تسلّط او بر ظاهر و باطن هستی است] بترسد دو بهشت است.'),
(4948, 55, 47, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4949, 55, 48, '[دو بهشتی که] دارای درختان و شاخسارهای باطراوت و انبوه است.'),
(4950, 55, 49, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4951, 55, 50, 'در آن دو بهشت دو چشمه ای است که همواره جاری است.'),
(4952, 55, 51, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4953, 55, 52, 'در آن دو بهشت از هر میوه ای دو نوع فراهم است.'),
(4954, 55, 53, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4955, 55, 54, '[بهشتیان] بر بسترهایی که آستر آنها از حریر درشت بافت است، تکیه می زنند، و میوه های رسیده آن دو بهشت [به آسانی] در دسترس است.'),
(4956, 55, 55, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4957, 55, 56, 'در آن بهشت ها زنانی هستند که فقط به همسرانشان عشق می ورزند، و پیش از آنان دست هیچ انس و جنی به آنان نرسیده است.'),
(4958, 55, 57, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4959, 55, 58, 'گویی آن زنان بهشتی یاقوت و مرجان اند.'),
(4960, 55, 59, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4961, 55, 60, 'آیا پاداش نیکی جز نیکی است؟'),
(4962, 55, 61, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4963, 55, 62, 'و جز آن دو بهشت، دو بهشت دیگر [هم] هست.'),
(4964, 55, 63, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4965, 55, 64, 'آن [دو بهشت دیگر] در نهایت سرسبزی اند.'),
(4966, 55, 65, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4967, 55, 66, 'در آن دو بهشت دو چشمه همواره جوشان و در حال فوران است.'),
(4968, 55, 67, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4969, 55, 68, 'در آنها میوه های فراوان و درخت خرما و انار است.'),
(4970, 55, 69, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4971, 55, 70, 'در آنها زنانی نیکو سیرت و زیبا صورت اند.'),
(4972, 55, 71, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4973, 55, 72, 'حوریانی که در سراپرده ها مستورند.'),
(4974, 55, 73, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4975, 55, 74, 'پیش از همسران بهشتی شان دست هیچ انس و جنّی به آنان نرسیده است.'),
(4976, 55, 75, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4977, 55, 76, 'بر بالش های سبز و فرش های زیبا تکیه می زنند.'),
(4978, 55, 77, 'پس کدامیک از نعمت های پروردگارتان را انکار می کنید؟'),
(4979, 55, 78, 'همیشه سودمند و با برکت است نام پروردگار با شکوه و ارجمندت.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'هنگامی که واقعه [بسیار عظیم قیامت] واقع شود،'),
(4981, 56, 2, 'که در واقع شدنش دروغی [در کار] نیست،'),
(4982, 56, 3, 'پست کننده [کافران] و رفعت دهنده [مؤمنان] است.'),
(4983, 56, 4, 'هنگامی که زمین به شدت لرزانده شود،'),
(4984, 56, 5, 'و کوه ها درهم کوبیده وریز ریز شوند.'),
(4985, 56, 6, 'در نتیجه غباری پراکنده گردد،'),
(4986, 56, 7, 'وشما سه گروه شوید:'),
(4987, 56, 8, 'سعادتمندان، چه بلند مرتبه اند سعادتمندان!'),
(4988, 56, 9, 'و شقاوتمندان، چه دون پایه اند شقاوتمندان!'),
(4989, 56, 10, 'و پیشی گیرندگان [به اعمال نیک] که پیشی گیرندگان [به رحمت و آمرزش] اند،'),
(4990, 56, 11, 'اینان مقربان اند،'),
(4991, 56, 12, 'در بهشت های پر نعمت اند.'),
(4992, 56, 13, 'گروهی بسیار از پیشینیان،'),
(4993, 56, 14, 'و اندکی از پسینیان،'),
(4994, 56, 15, 'بر تخت هایی زربافت،'),
(4995, 56, 16, 'در حالی که روبروی یکدیگر بر آنها تکیه دارند.'),
(4996, 56, 17, 'نوجوانانی همیشه نو جوان همواره [برای خدمت] پیرامونشان می گردند،'),
(4997, 56, 18, 'با قدح ها و کوزه ها و جام هایی از باده ناب و پاک،'),
(4998, 56, 19, 'که از نوشیدنش نه سردرد گیرند، و نه مست و بی خرد شوند،'),
(4999, 56, 20, 'و میوه ها از هر نوعی که اختیار کنند،'),
(5000, 56, 21, 'و گوشت پرنده از هر گونه ای که بخواهند،'),
(5001, 56, 22, 'و حوریانی چشم درشت،'),
(5002, 56, 23, 'هم چون مروارید پنهان شده در صدف؛'),
(5003, 56, 24, 'پاداشی است در برابر اعمالی که همواره انجام می دادند.'),
(5004, 56, 25, 'در آنجا نه سخن بیهوده ای می شنوند، نه کلام گناه آلودی،'),
(5005, 56, 26, 'مگر سخنی که سلام است و سلام،'),
(5006, 56, 27, 'و سعادتمندان چه بلند مرتبه اند سعادتمندان!'),
(5007, 56, 28, 'در سایه درخت سدر بی خارند،'),
(5008, 56, 29, 'و درختان موزی که میوه هایش خوشه خوشه روی هم چیده شده است،'),
(5009, 56, 30, 'و سایه ای گسترده و پایدار،'),
(5010, 56, 31, 'و آبی ریزان،'),
(5011, 56, 32, 'و میوه ای فراوان،'),
(5012, 56, 33, 'که پایان نپذیرد و ممنوع نشود،'),
(5013, 56, 34, 'و همسرانی بلند مرتبه،'),
(5014, 56, 35, 'که ما آنان را با آفرینشی ویژه آفریدیم؛'),
(5015, 56, 36, 'پس آنان را همواره دوشیزه قرار داده ایم'),
(5016, 56, 37, 'عشقورز به شوهران، و هم سن و سال با همسران.'),
(5017, 56, 38, '[همه این نعمت ها] برای سعادتمندان [است.]'),
(5018, 56, 39, 'گروهی بسیار از پیشینیان،'),
(5019, 56, 40, 'و گروهی بسیار از پسینیان،'),
(5020, 56, 41, 'و شقاوتمندان، چه دون پایه اند شقاوتمندان!'),
(5021, 56, 42, 'در میان بادی سوزان و آبی جوشان [قرار دارند،]'),
(5022, 56, 43, 'و سایه ای از دودهای بسیار غلیظ و سیاه،'),
(5023, 56, 44, 'نه خنک است و نه آرام بخش،'),
(5024, 56, 45, 'اینان پیش از این از نازپروردگان خود کامه و سرکش بودند،'),
(5025, 56, 46, 'و همواره بر گناهان بزرگ پافشاری داشتند،'),
(5026, 56, 47, 'و پیوسته می گفتند: آیا هنگامی که مردیم و خاک و استخوان شدیم، آیا به راستی برانگیخته می شویم؟!'),
(5027, 56, 48, 'و آیا پدران گذشته ما نیز برانگیخته می شوند؟!'),
(5028, 56, 49, 'بگو: بی تردید همه پیشینیان وهمه پسینیان،'),
(5029, 56, 50, 'برای وعده گاه روزی معین گرد آورده خواهند شد.'),
(5030, 56, 51, 'آن گاه شما ای گمراهان انکار کننده!'),
(5031, 56, 52, 'قطعاً از درختی که از زقّوم است [و دارای مایعی جوشان و بسیار بدمزه و بدبوست] خواهید خورد؛'),
(5032, 56, 53, 'و شکم ها را از آن پر خواهید کرد،'),
(5033, 56, 54, 'و روی آن از آب جوشان خواهید نوشید،'),
(5034, 56, 55, 'مانند نوشیدن شترانی که به شدت تشنه اند؛'),
(5035, 56, 56, 'این است پذیرایی از آنان در روز جزا.'),
(5036, 56, 57, 'ما شما را آفریدیم، پس چرا [آفرینش دوباره خود را پس از مرگ] باورنمی کنید؟'),
(5037, 56, 58, 'آیا از [حالات و دگرگونی های] نطفه ای که در رحم می ریزید آگاه هستید؟'),
(5038, 56, 59, 'آیا شما آن را [تا انسانی معتدل و آراسته شود] می آفرینید یا ما آفریننده ایم؟'),
(5039, 56, 60, 'ماییم که مرگ را میان شما مقدّر کردیم، و هیچ چیز ما را [در جاری کردن مرگ بر شما] مغلوب نمی کند.'),
(5040, 56, 61, '[آری، مرگ را مقدّر کردیم] تا امثال شما را جایگزین شما کنیم و شما را به صورتی که نمی دانید آفرینشی تازه و جدید بخشیم،'),
(5041, 56, 62, 'و به راستی پیدایش نخستین را [که جهان فعلی است] شناختید، پس چرا متذکّر [پدید شدن جهان دیگر] نمی شوید؟!'),
(5042, 56, 63, 'مرا خبر دهید آنچه را می کارید،'),
(5043, 56, 64, 'آیا شما آن را می رویانید، یا ما می رویانیم؟'),
(5044, 56, 65, 'به یقین اگر بخواهیم، آن را ریز ریز کرده و خاشاک می کنیم که متأسف و شگفت زده می شوید،'),
(5045, 56, 66, '[و می گویید:] مسلماً ما خسارت زده ایم،'),
(5046, 56, 67, 'بلکه ناکام و محرومیم'),
(5047, 56, 68, 'به من خبر دهید آبی که می نوشید،'),
(5048, 56, 69, 'آیا شما آن را از ابر باران زا فرود آورده اید یا ما فرود آورنده ایم؟'),
(5049, 56, 70, 'اگر بخواهیم آن را تلخ می گردانیم، پس چرا سپاس گزاری نمی کنید؟'),
(5050, 56, 71, 'به من خبر دهید آتشی که می افروزید،'),
(5051, 56, 72, 'آیا شما درختش را به وجود آورده اید یا ما به وجود آوردنده ایم؟'),
(5052, 56, 73, 'ما آن را وسیله تذکر و مایه استفاده برای صحرانشینان و بیابانگردان قرار داده ایم.'),
(5053, 56, 74, 'پس به نام پروردگار بزرگت تسبیح گوی.'),
(5054, 56, 75, 'پس به جایگاه ستارگان سوگند می خورم،'),
(5055, 56, 76, 'و اگر بدانید بی تردید این سوگندی بس بزرگ است.'),
(5056, 56, 77, 'که یقیناً این قرآن، قرآنی است ارجمند و باارزش؛'),
(5057, 56, 78, '[که] در کتابی مصون از هر گونه تحریف و دگرگونی [به نام لوح محفوظ جای دارد.]'),
(5058, 56, 79, 'جز پاک شدگان [از هر نوع آلودگی] به [حقایق و اسرار و لطایف] آن دسترسی ندارند.'),
(5059, 56, 80, 'نازل شده از سوی پروردگار جهانیان است.'),
(5060, 56, 81, 'آیا شما نسبت به این گفتار سهل انگاری می کنید [و آن را قابل اعتنا نمی دانید؟!]'),
(5061, 56, 82, 'و فقط نصیب خود را این قرار می دهید که آن را انکار کنید؟!'),
(5062, 56, 83, 'پس چرا هنگامی که روح به گلوگاه می رسد،'),
(5063, 56, 84, 'و شما در آن وقت نظاره گر هستید [و هیچ کاری از شما ساخته نیست!]'),
(5064, 56, 85, 'و ما به او از شما نزدیک تریم، ولی نمی بینید.'),
(5065, 56, 86, '[آری] پس چرا اگر شما پاداش داده نمی شوید [و به گمان خود قیامتی در کار نیست و شما را قدرتی بزرگ و فراتر است؟]'),
(5066, 56, 87, 'آن [روح به گلوگاه رسیده] را [به بدن محتضر] برنمی گردانید، اگر [در ادعای خود] راستگویید؟'),
(5067, 56, 88, 'پس اگر [جان به گلو رسیده] از مقربان باشد،'),
(5068, 56, 89, '[در] راحت و آسایش و بهشت پرنعمت [خواهد بود.]'),
(5069, 56, 90, 'و اگر از سعادتمندان باشد،'),
(5070, 56, 91, '[به او گفته می شود:] از سوی سعادتمندان بر تو سلام باد.'),
(5071, 56, 92, 'و اما اگر از انکار کنندگان [حقایق و] گمراه باشد،'),
(5072, 56, 93, 'پذیرایی از او با آب جوشان است،'),
(5073, 56, 94, 'و وارد شدن به دوزخ است.'),
(5074, 56, 95, '[آنچه درباره این سه طایفه بیان شد،] بی تردید این است همان حقّ یقینی.'),
(5075, 56, 96, 'پس به نام پروردگار بزرگت تسبیح گوی.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمان ها و زمین است، خدا را [به پاک بودن از هر عیب و نقصی] می ستایند، و او توانای شکست ناپذیر و حکیم است.'),
(5077, 57, 2, 'مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره اوست، زنده می کند و می میراند، و او بر هر کاری تواناست.'),
(5078, 57, 3, 'اوست اول و آخر و ظاهر و باطن، و او به همه چیز داناست.'),
(5079, 57, 4, 'اوست که آسمان ها و زمین را در شش روز آفرید، سپس بر تخت فرمانروایی و تدبیر امور آفرینش چیره و مسلط شد. آنچه در زمین فرو می رود و آنچه از آن بیرون می آید و آنچه از آسمان نازل می شود، و آنچه در آن بالا می رود می داند. و او با شماست هرجا که باشید، و خدا به آنچه انجام می دهید، بیناست.'),
(5080, 57, 5, 'مالکیّت و فرمانروایی آسمان ها و زمین فقط در سیطره اوست، و همه امور به خدا باز گردانده می شود.'),
(5081, 57, 6, 'شب را در روز در می آورد و روز را در شب در می آورد، و او به نیّات و اسرار سینه ها داناست.'),
(5082, 57, 7, 'به خدا و پیامبرش ایمان آورید، و از اموالی که خدا شما را در آن جانشین خود قرار داده، انفاق کنید؛ پس برای کسانی از شما که ایمان آورده و انفاق کرده اند، پاداش بزرگی خواهد بود.'),
(5083, 57, 8, 'و شما را چه شده که به خدا ایمان نمی آورید؟ در حالی که پیامبر، شما را دعوت می کند تا به پروردگارتان ایمان آورید، و بی تردید خدا [از طریق عقل و فطرت بر ایمان آوردنتان] پیمان گرفته است، اگر باور دارید.'),
(5084, 57, 9, 'اوست که بر بنده اش آیات روشنی نازل می کند تا شما را از تاریکی ها به سوی نور بیرون آورد. و یقیناً خدا نسبت به شما رؤوف و مهربان است.'),
(5085, 57, 10, 'و شما را چه شده که در راه خدا انفاق نمی کنید؟ در حالی که میراث آسمان ها و زمین مخصوص خداست [و کسی مالک حقیقی چیزی نیست]. کسانی از شما که پیش از فتح [مکه] انفاق کردند و جهاد نمودند [با دیگران] یکسان نیستند، آنان از جهت درجه از کسانی که پس از فتح [مکه] انفاق کردند و جهاد نمودند، بلندپایه ترند. و خدا به هریک وعده نیکو داده است و خدا به آنچه انجام می دهید، آگاه است.'),
(5086, 57, 11, 'کیست که به خدا وامی نیکو دهد تا خدا آن را برای او دو چندان کند و او را پاداشی نیکو و باارزش باشد؟'),
(5087, 57, 12, '[این پاداش نیکو و باارزش در] روزی [است] که مردان و زنان باایمان را می بینی که نورشان پیش رو و از جانب راستشان شتابان حرکت می کند، [به آنان می گویند:] امروز شما را مژده باد به بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، در آنها جاودانه اید، این است آن کامیابی بزرگ.'),
(5088, 57, 13, 'روزی که مردان و زنان منافق به کسانی که ایمان آورده اند، می گویند: ما را مهلت دهید تا [پرتویی] از نور شما بر گیریم. به آنان گویند: به پشت سرتان [دنیا] برگردید و [از آنجا برای خود] نوری بجویید. سپس میان آنان دیواری زده می شود که دارای دری است، درونش [که مؤمنان در آن درآیند] رحمت است و بیرونش که پیش روی منافقان قرار دارد عذاب است.'),
(5089, 57, 14, 'مؤمنان را ندا می دهند: آیا ما [در دنیا] با شما نبودیم؟ می گویند: چرا، ولی شما خود را [به سبب نفاق و دشمنی با خدا و رسول] در بلا و هلاکت افکندید و به انتظار [خاموش شدن چراغ اسلام و نابودی پیامبر] نشستید و [نسبت به حقایق] در تردید بودید و آرزوها [ی دور و دراز و بی پایه،] شما را فریفت، تا فرمان خدا [به نابودی شما] در رسید و [شیطان] فریبنده، شما را فریب داد.'),
(5090, 57, 15, 'پس امروز از شما و از کافران فدیه و عوضی [در برابر نجات از عذاب] دریافت نخواهد شد، جایگاهتان آتش است، [و] همان سرپرست و یار شماست و بد بازگشت گاهی است.'),
(5091, 57, 16, 'آیا برای اهل ایمان وقت آن نرسیده که دل هایشان برای یاد خدا و قرآنی که نازل شده نرم و فروتن شود؟ و مانند کسانی نباشند که پیش از این کتاب آسمانی به آنان داده شده بود، آن گاه روزگار [سرگرمی در امور دنیا و مشغول بودن به آرزوهای دور و دراز] بر آنان طولانی گشت، در نتیجه دل هایشان سخت و غیر قابل انعطاف شد، و بسیاری از آنان نافرمان بودند.'),
(5092, 57, 17, 'بدانید که خدا زمین را پس از مردگی اش زنده می کند. همانا ما نشانه ها [ی ربوبیت و قدرت خود] را برای شما بیان کردیم تا بیندیشید.'),
(5093, 57, 18, 'بی تردید مردان و زنان انفاق کننده و آنان که به خدا وام نیکو داده اند، [وامشان در پیشگاه خدا] برای آنان دو چندان شود، و برای آنان پاداش باارزشی است.'),
(5094, 57, 19, 'و کسانی که به خدا و پیامبرش ایمان آوردند، آنانند که صدّیقان و گواهان [اعمال] نزد پروردگارشان هستند، [و] برای آنان است پاداش [اعمال] شان و نور [ایمان] شان و کسانی که کفر ورزیدند و آیات ما را انکار کردند، آنان اهل دوزخ اند.'),
(5095, 57, 20, 'بدانید که زندگی دنیا [یی که دارنده اش از ایمان و عمل تهی است،] فقط بازی و سرگرمی و آرایش و فخرفروشیتان به یکدیگر، و افزون خواهی در اموال و اولاد است، [چنین دنیایی] مانند بارانی است که محصول [سبز و خوش منظره اش] کشاورزان را به شگفتی آورد، سپس پژمرده شود و آن را زرد بینی، سپس ریز ریز و خاشاک شود!! [که برای دنیاپرستان بی ایمان] در آخرت عذاب سختی است و [برای مؤمنان که دنیای خود را در راه اطاعت حق و خدمت به خلق به کار گرفتند] از سوی خدا آمرزش و خشنودی است، و زندگی دنیا جز کالای فریبنده نیست.'),
(5096, 57, 21, 'سبقت جویید به آمرزشی از سوی پروردگارتان و بهشتی که پهنایش چون پهنای آسمان و زمین است، برای کسانی که به خدا و پیامبرانش ایمان آورده اند، آماده شده است؛ این فضل خداست که آن را به هرکس بخواهد عطا می کند و خدا دارای فضل بزرگ است.'),
(5097, 57, 22, 'هیچ گزند و آسیبی در زمین [چون زلزله، سیل، توفان و قحطی] و در وجود خودتان [چون بیماری، رنج، تعب و از دست رفتن مال و ثروت،] روی نمی دهد مگر پیش از آنکه آن را به وجود آوردیم در کتابی [چون لوح محفوظ] ثبت است، بی تردید این [تقدیر حوادث و فرمان قطعی به پدید آمدنش] بر خدا آسان است.'),
(5098, 57, 23, 'تا [با یقین به اینکه هر گزند و آسیبی و هر عطا و منعی فقط به اراده خداست و شما را در آن اختیاری نیست] بر آنچه از دست شما رفت، تأسف نخورید، و بر آنچه به شما عطا کرده است، شادمان و دلخوش نشوید، و خدا هیچ گردنکش خودستا را [که به نعمت ها مغرور شده است] دوست ندارد.'),
(5099, 57, 24, 'همانان که همواره بخل می ورزند و مردم را به بخل وامی دارند، و هرکه [از انفاق] روی بگرداند [زیانی به خدا نمی رساند]؛ زیرا خدا بی نیاز و ستوده است.'),
(5100, 57, 25, 'همانا ما پیامبران خود را با دلایل روشن فرستادیم و با آنان کتاب و ترازو [ی تشخیص حق از باطل] نازل کردیم تا مردم به عدالت بر خیزند، و آهن را که در آن برای مردم قوت و نیرویی سخت و سودهایی است، فرود آوردیم و تا خدا مشخص بدارد چه کسانی او و پیامبرانش را در غیاب پیامبران یاری می دهند؛ یقیناً خدا نیرومند و توانای شکست ناپذیر است.'),
(5101, 57, 26, 'به یقین ما نوح و ابراهیم را فرستادیم و در میان فرزندان آن دو نفر، نبوّت و کتاب قرار دادیم، پس برخی از آنان هدایت یافتند و بسیاری از آنان نافرمان بودند.'),
(5102, 57, 27, 'سپس به دنبال آنان پیامبران خود را پی در پی آوردیم و پس از آنان عیسی پسر مریم را [به رسالت] فرستادیم و به او انجیل عطا کردیم و در قلب کسانی که از او پیروی می کردند رأفت و رحمت قرار دادیم، و رهبانیتی که از نزد خود ساخته بودند ما بر آنان مقرّر نکرده بودیم، ولی [خود آنان آن را] برای طلب خشنودی خدا [بر خود واجب کرده بودند]، اما آنگونه که باید حدود خودساخته را رعایت نکردند؛ پس پاداش کسانی از آنان را که ایمان آورده اند، عطا کردیم و بسیاری از آنان نافرمانند.'),
(5103, 57, 28, 'ای مؤمنان! از خدا پروا کنید و به پیامبرش ایمان آورید تا دو سهم از رحمتش را به شما عطا کند، و برای شما نوری قرار دهد که به وسیله آن [در میان مردم] راه سپارید و شما را بیامرزد، و خدا بسیار آمرزنده و مهربان است.'),
(5104, 57, 29, 'تا اهل کتاب بدانند [که اگر به پیامبر اسلام ایمان نیاورند] بر چیزی از فضل خدا دسترسی نخواهند داشت؛ بی تردید همه فضل به دست خداست، آن را به هرکس بخواهد عطا می کند و خدا دارای فضل بزرگ است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'به راستی خدا گفتار [زنی] را که درباره همسرش با تو گفتگو داشت و به خدا شکایت می کرد، شنید و خدا گفتگوی شما را می شنود؛ زیرا خدا شنوا و بیناست.'),
(5106, 58, 2, 'کسانی از شما که با زنان خود «ظهار» می کنند [یعنی بر پایه فرهنگ جاهلی می گویند: تو نسبت به من به منزله مادرم هستی، پس آمیزش با تو بر من حرام ابدی است] زنانشان مادرانشان نیستند، مادرانشان فقط زنانی هستند که آنان را زاده اند، و آنان بی تردید سخنی ناپسند و دروغ می گویند و مسلماً خدا بسیار باگذشت و بسیار آمرزنده است.'),
(5107, 58, 3, 'و کسانی که با زنانشان ظهار می کنند، سپس از آنچه گفته اند برمی گردند، باید پیش از آمیزش با هم برده ای [در راه خدا] آزاد کنند. این [حکمی] است که به آن اندرز داده می شوید، و خدا بر آنچه انجام می دهید، آگاه است.'),
(5108, 58, 4, 'کسی که توانایی [آزاد کردن برده را] ندارد، باید پیش از آنکه با هم آمیزش کنند، دو ماه پی در پی روزه بگیرد، و هر که نتواند باید شصت مسکین را طعام دهد؛ این حکم برای این است که به خدا و پیامبرش ایمان آورید [و از فرهنگ جاهلی دوری گزینید]؛ و اینها حدود خداست. و برای کافران عذابی دردناک است.'),
(5109, 58, 5, 'بی تردید کسانی که با خدا و پیامبرش دشمنی و مخالفت می ورزند، خوار و سرنگون می شوند، چنان که کسانی که پیش از آنان بودند، خوار و سرنگون شدند، و همانا ما آیات روشنی نازل کردیم، و برای کافران عذابی خوارکننده است.'),
(5110, 58, 6, 'در روزی که خدا همه آنان را برمی انگیزد، آن گاه به اعمالی که انجام داده اند، آگاهشان می کند، اعمالی که خدا حساب همه آنها را برشمرده است در حالی که [آنان] آن را فراموش کرده اند، و خدا بر هر چیز گواه است.'),
(5111, 58, 7, 'آیا ندانسته ای که خدا آنچه را در آسمان ها و آنچه را در زمین است، می داند؟ هیچ گفتگوی محرمانه ای میان سه نفر نیست مگر اینکه او چهارمی آنان است، و نه میان پنج نفر مگر اینکه او ششمی آنان است، و نه کمتر از آن و نه بیشتر مگر اینکه هر کجا باشند او با آنان است، سپس روز قیامت آنان را به اعمالی که انجام داده اند آگاه می کند؛ زیرا خدا به همه چیز داناست.'),
(5112, 58, 8, 'آیا کسانی را که از گفتگوی محرمانه منع شده بودند، ندیدی؟ که باز به کاری که از آن منع شده بودند باز می گردند، و با یکدیگر از روی گناه و دشمنی و سرپیچی از پیامبر، محرمانه گفتگو می کنند، و هنگامی که نزد تو آیند به صورتی تو را درود می گویند که خدا به تو درود نگفته است، و در دل خود می گویند: چرا خدا ما را به سبب آنچه می گوییم عذاب نمی کند؟! دوزخ برای آنان کافی است، در آن وارد می شوند، و بد بازگشت گاهی است.'),
(5113, 58, 9, 'ای اهل ایمان! هنگامی که محرمانه با یکدیگر گفتگو می کنید از روی گناه و دشمنی و سرپیچی از پیامبر با هم گفتگوی محرمانه نکنید، و بر پایه [تشویق یکدیگر به] نیکی و تقوا گفتگوی محرمانه کنید، و از خدایی که به سوی او محشور می شوید، پروا کنید.'),
(5114, 58, 10, 'جز این نیست که گفتگوی محرمانه [بی منطق و رازگویی بی دلیل] از [ناحیه] شیطان است تا مؤمنان را اندوهگین کند، ولی نمی تواند هیچ گزندی به آنان برساند مگر به فرمان خدا. و مؤمنان فقط باید بر خدا توکل کنند [که توکل کننده به خدا از گزند شیطان مصون است.]'),
(5115, 58, 11, 'ای مؤمنان! هنگامی که گویند: در مجالس [برای نشستن دیگر برادرانتان] جا باز کنید، پس جا باز کنید، تا خدا برای شما [در بهشت] جا باز کند. و چون گویند: برخیزید، بی درنگ برخیزید تا خدا مؤمنان از شما را به درجه ای و دانشمندانتان را به درجاتی [عظیم و باارزش] بلند گرداند، و خدا به آنچه انجام می دهید، آگاه است.'),
(5116, 58, 12, 'ای مؤمنان! زمانی که می خواهید با پیامبر گفتگوی محرمانه کنید، پیش از گفتگوی محرمانه خود صدقه دهید، این برای شما بهتر و پاکیزه تر است، و اگر چیزی برای صدقه نیافتید [اجازه دارید محرمانه گفتگو کنید] زیرا خدا بسیار آمرزنده و مهربان است.'),
(5117, 58, 13, 'آیا ترسیدید از اینکه پیش از گفتگوی محرمانه خود صدقاتی بپردازید؟ پس چون نپرداختید و خدا هم توبه شما را پذیرفت، پس نماز را برپا دارید و زکات بدهید و از خدا و پیامبرش اطاعت کنید، و خدا به اعمالی که انجام می دهید، آگاه است.'),
(5118, 58, 14, 'آیا به کسانی که قوم مورد خشم خدا را به دوستی گرفتند، ننگریستی؟ اینان نه از شمایند و نه از آنان، و به دروغ سوگند می خورند [که از شمایند] و خود آگاهند [که دروغ می گویند.]'),
(5119, 58, 15, 'خدا برای آنان عذاب سختی آماده کرده است؛ زیرا آنچه را که همواره انجام می دادند، بسیار بد است.'),
(5120, 58, 16, 'آنان سوگندهایشان را [برای تبرئه خود از خیانت به پیامبر و مؤمنان] سپر قرار داده اند، در نتیجه [مردم را] از راه خدا بازداشتند پس برای آنان عذابی خوارکننده است.'),
(5121, 58, 17, 'هرگز اموال و اولادشان چیزی از عذاب خدا را از آنان دفع نمی کند، آنان اهل آتش اند و در آن جاودانه اند.'),
(5122, 58, 18, 'روزی که خدا همه آنان را برمی انگیزد، پس همان گونه که برای شما سوگند دروغ می خوردند، برای خدا هم سوگند دروغ می خورند، و گمان می کنند که با سوگند دروغ می توانند در عرصه محشر، سودی را برای خود جلب کنند و زیانی را از خویش بگردانند، آگاه باش که آنان همان دورغگویانند!'),
(5123, 58, 19, 'شیطان بر آنان چیره و مسلط شده و یاد خدا را از خاطرشان برده است، آنان حزب شیطانند، آگاه باش که حزب شیطان یقیناً همان زیان کارانند!'),
(5124, 58, 20, 'بی تردید کسانی که با خدا و پیامبرش دشمنی و مخالفت می کنند، در زمره خوارترینان هستند.'),
(5125, 58, 21, 'خدا لازم و مقرّر کرده است که بی تردید من و پیامبرانم پیروزیم. همانا خدا نیرومند و توانای شکست ناپذیر است.'),
(5126, 58, 22, 'گروهی را که به خدا و روز قیامت ایمان دارند، نمی یابی که با کسانی که با خدا و پیامبرش دشمنی و مخالفت دارند، دوستی برقرار کنند، گرچه پدرانشان یا فرزاندانشان یا برادرانشان یا خویشانشان باشند. اینانند که خدا ایمان را در دل هایشان ثابت و پایدار کرده، و به روحی از جانب خود نیرومندشان ساخته، و آنان را به بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است درمی آورد، در آنجا جاودانه اند، خدا از آنان خشنود است و آنان هم از خدا خشنودند. اینان حزب خدا هستند، آگاه باش که بی تردید حزب خدا همان رستگارانند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمان ها و آنچه در زمین است، خدا را به پاک بودن از هر عیب و نقصی می ستایند، و او توانای شکست ناپذیر و حکیم است.'),
(5128, 59, 2, 'اوست که کافرانِ از اهل کتاب را در نخستین بیرون راندن دسته جمعی از خانه هایشان بیرون راند. [شما اهل ایمان] رفتنشان را گمان نمی بردید، و خودشان پنداشتند که حصارها و دژهای استوارشان در برابر خدا [از تبعید و در به دری آنان] جلوگیری خواهد کرد ولی [اراده کوبنده] خدا از آنجا که گمان نمی کردند به سراغشان آمد و در دل هایشان رعب و ترس افکند به گونه ای که خانه هایشان را به دست خود و به دست مؤمنان ویران کردند. پس ای صاحبان بینش و بصیرت! عبرت گیرید.'),
(5129, 59, 3, 'اگر خدا فرمان ترک وطن را بر آنان لازم و مقرّر نکرده بود، قطعاً در همین دنیا عذابشان می کرد و برای آنان در آخرت عذاب آتش است.'),
(5130, 59, 4, 'این [در به دری و تبعید] برای این است که آنان با خدا و پیامبرش دشمنی و مخالفت کردند، و هر کس با خدا دشمنی و مخالفت کند [بداند که] خدا سخت کیفر است.'),
(5131, 59, 5, 'آنچه از درخت خرما [که در مالکیّت این دشمنان غدّار بود] بریدید یا آنها را بر ریشه ها و تنه هایش رها کردید، به فرمان خدا بود، تا نافرمانان را زبون و رسوا کند.'),
(5132, 59, 6, 'اموال و زمین هایی را که خدا به عنوان غنیمت به پیامبرش بازگرداند، شما برای به دست آوردنش اسب و شتری نتازاندید [و در نتیجه به زحمت نیفتادید]، ولی خدا پیامبرانش را بر هر که بخواهد مسلط و چیره می کند، و خدا بر هر کاری تواناست.'),
(5133, 59, 7, 'آنچه خدا از [اموال و زمین های] اهل آن آبادی ها به پیامبرش بازگرداند اختصاص به خدا و پیامبر و اهل بیت پیامبر و یتیمان و مستمندان و در راه ماندگان دارد، تا میان ثرومندان شما دست به دست نگردد. و [از اموال و احکام و معارف دینی] آنچه را پیامبر به شما عطا کرد بگیرید و از آنچه شما را نهی کرد، باز ایستید و از خدا پروا کنید؛ زیرا خدا سخت کیفر است.'),
(5134, 59, 8, '[بخشی از غنایم] برای فقیران مهاجری است که از دیار و اموالشان بیرون رانده شده اند، در حالی که فضل و خشنودی خدا را می جویند وخدا وپیامبرش را یاری می کنند، اینان همان راستگویانند.'),
(5135, 59, 9, 'و [برای] کسانی [از انصار است] که پیش از مهاجران در سرای هجرت و ایمان [یعنی مدینه] جای گرفتند، [و] کسانی را که به سوی آنان هجرت کرده اند دوست دارند، و در سینه های خود نیاز و چشم داشتی به آنچه به مهاجران داده شده است نمی یابند، و آنان را بر خود ترجیح می دهند، گرچه خودشان را نیاز شدیدی [به مال و متاع] باشد. و کسانی را که از بخل و حرصشان بازداشته اند، اینان همان رستگارانند.'),
(5136, 59, 10, 'و نیز کسانی که بعد از آنان [انصار و مهاجرین] آمدند در حالی که می گویند: پروردگارا! ما و برادرانمان را که به ایمان بر ما پیشی گرفتند بیامرز، و در دل هایمان نسبت به مؤمنان، خیانت و کینه قرار مده. پروردگارا! یقیناً تو رؤوف و مهربانی.'),
(5137, 59, 11, 'آیا کسانی را که نفاق ورزیدند، ندیدی؟ که به برادران کافرشان از اهل کتاب می گویند: اگر شما را [از خانه و دیارتان] بیرون کردند، ما هم قطعاً با شما بیرون می آییم، و هرگز فرمان کسی را بر ضد شما اطاعت نمی کنیم، و اگر با شما جنگیدند، همانا شما را یاری می کنیم. و خدا گواهی می دهد که آنان دروغگویند.'),
(5138, 59, 12, 'اگر [کافران از اهل کتاب را] بیرون کنند با آنان بیرون نمی روند، و اگر با آنان بجنگند آنان را یاری نمی دهند، و اگر یاری دهند در گرماگرم جنگ پشت کنان می گریزند، سپس [کافران اهل کتاب] یاری نمی شوند.'),
(5139, 59, 13, '[آری منافقان، کافران از اهل کتاب را در گرماگرم جنگ به هنگام خطر رها می کنند و می گریزند؛ زیرا] ترس آنان از شما در دل هایشان بیش از ترس از خداست؛ چون آنان قومی هستند که [حقایق را به خاطر کوردلی] نمی فهمند.'),
(5140, 59, 14, 'همه آنان [به صورت متحد و یک پارچه] با شما نمی جنگند مگر در آبادی هایی که دارای حصار و قلعه و دژ هستند، یا از پشت دیوارها، دلاوری آنان میان خودشان شدید است [ولی از رویارویی با شما می ترسند]، آنان را متحد و هم دست می پنداری در حالی که دل هایشان پراکنده است؛ زیرا آنان گروهی هستند که تعقّل نمی کنند.'),
(5141, 59, 15, '[داستان این یهودی های نابکار بنی نظیر] مانند کسانی است که اندکی پیش از اینان [در پیرامون مدینه] بودند که سرانجام وخیم کارشان را چشیدند، و برای آنان عذابی دردناک است.'),
(5142, 59, 16, '[داستان منافقان که کافران از اهل کتاب را با وعده های دروغ فریفتند] چون داستان شیطان است که به انسان گفت: کافر شو. هنگامی که کافر شد، گفت: من از تو بیزارم، من از خدا که پروردگار جهانیان است، می ترسم.'),
(5143, 59, 17, 'سرانجامشان این شد که هر دو در آتش اند؛ و در آن جاودانه اند و این است کیفر ستمکاران.'),
(5144, 59, 18, 'ای اهل ایمان! از خدا پروا کنید؛ و هر کسی باید با تأمل بنگرد که برای فردای خود چه چیزی پیش فرستاده است، و از خدا پروا کنید؛ یقیناً خدا به آنچه انجام می دهید، آگاه است.'),
(5145, 59, 19, 'و مانند کسانی مباشید که خدا را فراموش کردند، پس خدا هم آنان را دچار خودفراموشی کرد؛ اینان همان فاسقانند.'),
(5146, 59, 20, 'دوزخیان و بهشتیان یکسان نیستند، بهشتیان همان رستگارانند.'),
(5147, 59, 21, 'اگر این قرآن را بر کوهی نازل می کردیم، قطعاً آن را از ترس خدا فروتن و از هم پاشیده می دیدی. و این مثل ها را برای مردم می زنیم تا بیندیشند.'),
(5148, 59, 22, 'اوست خدایی که جز او هیچ معبودی نیست، دانای نهان و آشکار است، او رحمان و رحیم است.'),
(5149, 59, 23, 'اوست خدایی که جز او هیچ معبودی نیست، همان فرمانروای پاک، سالم از هر عیب و نقص، ایمنی بخش، چیره و مسلط، شکست ناپذیر، جبران کننده، شایسته بزرگی و عظمت است. خدا از آنچه شریک او قرار می دهند، منزّه است.'),
(5150, 59, 24, 'اوست خدا، آفریننده، نوساز، صورتگر، همه نام های نیکو ویژه اوست. آنچه در آسمان ها و زمین است همواره برای او تسبیح می گویند، و او توانای شکست ناپذیر و حکیم است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ای اهل ایمان! دشمنان من و دشمنان خودتان را دوستان خود مگیرید، شما با آنان اظهار دوستی می کنید، در حالی که آنان به طور یقین به آنچه از حق برای شما آمده کافرند، و پیامبر و شما را به خاطر ایمانتان به خدا که پروردگار شماست [از وطن] بیرون می کنند، [پس آنان را دوستان خود مگیرید] اگر برای جهاد در راه من و طلب خشنودیم بیرون آمده اید [چرا] مخفیانه به آنان پیام می دهید که دوستشان دارید؟ در حالی که من به آنچه پنهان می داشتید و آنچه آشکار کردید داناترم، و هر کس از شما با دشمنان من رابطه دوستی برقرار کند، مسلماً از راه راست منحرف شده است.'),
(5152, 60, 2, 'اگر بر شما چیره شوند، دشمنانتان خواهند بود، و دست هایشان را [به اسارت، آزار و کشتن] و زبان هایشان را به [بدگویی، تحقیر و ناسزا] بر ضد شما می گشایند، و آرزو دارند که ای کاش شما هم کافر شوید.'),
(5153, 60, 3, 'روز قیامت [که کیفر دوستی با دشمنان داده شود] خویشان و فرزندانتان سودی به حال شما ندارند، خدا میان شما و آنان جدایی می اندازد، و خدا به آنچه انجام می دهید، بیناست.'),
(5154, 60, 4, 'مسلماً برای شما در ابراهیم و کسانی که با اویند سرمشقی نیکوست، آن گاه که به قوم خود گفتند: ما از شما و آنچه به جای خدا می پرستید بیزاریم، ما به شما کافریم و میان ما و شما دشمنی و کینه همیشگی پدیدار شده است، تا آن زمان که به خدای یگانه ایمان آورید. [آری، ابراهیم و مؤمنان جز اعلام بیزاری سخنی با بت پرستان نداشتند] مگر سخن ابراهیم به پدرش که گفت: من برای تو [در صورتی که دست از دشمنی و کینه با حق برداری] آمرزش خواهم خواست و در برابر خدا به سود تو اختیار چیزی را ندارم. پروردگارا! بر تو توکل کردیم، و به سوی تو بازگشتیم، و بازگشت به سوی توست.'),
(5155, 60, 5, 'پروردگارا! ما را وسیله آزمایش [و هدف آزار، چیرگی و سلطه] برای کافران قرار مده، و پروردگارا! ما را بیامرز که همانا تو توانای شکست ناپذیر و حکیمی.'),
(5156, 60, 6, 'بی تردید برای شما در [روش و شیوه] آنان سرمشقی نیکوست، برای کسی که به خدا و روز قیامت امید دارد. و هر کس [از اقتدای به آن روش و شیوه] روی برگرداند [زیانی به خدا نمی رساند]؛ زیرا خدا همان بی نیاز و ستوده است.'),
(5157, 60, 7, 'امید است خدا میان شما و کسانی از کافران که با آنان دشمنی داشتید [به وسیله اسلام آوردنشان] دوستی قرار دهد، و خدا تواناست، و خدا بسیار آمرزنده و مهربان است.'),
(5158, 60, 8, 'خدا شما را از نیکی کردن و عدالت نسبت به کسانی که درکار دین با شما نجنگیدند و شما را از دیارتان بیرون نکردند باز نمی دارد؛ زیرا خدا عدالت پیشگان را دوست دارد.'),
(5159, 60, 9, 'خدا فقط شما را از دوستی با کسانی نهی می کند که در کار دین با شما جنگیدند، و از دیارتان بیرون راندند، و در بیرون راندنتان به یکدیگر کمک کردند تا [به خاطر این سختگیری] با آنان دوستی کنید. و تنها کسانی که با آنان دوستی کنند، ستمکارانند.'),
(5160, 60, 10, 'ای مؤمنان! هنگامی که زنان باایمان [با جدا شدن از همسرانشان] هجرت کنان [از دیار کفر] به سوی شما می آیند، آنان را [از جهت ایمان] بیازمایید، البته خدا خود به ایمان آنان داناتر است. پس اگر آنان را باایمان تشخیص دادید، آنان را به سوی کافران [که همسرانشان هستند] باز مگردانید، نه این زنان بر کافران حلال اند، و نه آن کافران بر این زنان حلال اند، و مهریه ای که همسران کافر به زنان مؤمن خود داده اند به آنان بپردازید، و بر شما گناهی نیست در صورتی که مهریه شان را به آنان بدهید، با آنان ازدواج کنید، و به عقد و پیوندهای زنان کافر [تان که به کفار پیوسته اند] پای بند نباشید [که آن عقد و پیوند باطل شده و وظیفه شما رها کردن آنان است] و آنچه را شما [برای همسران مرتد و فراری خود که به کافران پناهنده شده اند] هزینه کرده اید [از کافران] بخواهید، و آنان هم باید آنچه را هزینه کرده اند از شما درخواست کنند؛ این حکم خداست که میان شما حکم می کند، و خدا دانا و حکیم است.'),
(5161, 60, 11, 'اگر برخی از همسران شما با پیوستن به کفار از دست شما رفتند [و کافران مهریه مورد مطالبه شما را ندادند] و شما غنیمتی یافتید، پس به کسانی که همسرانشان را [به خاطر پیوستن به کفار] از دست داده اند، معادل مهریه ای که داده اند از غنایم بپردازید، و از خدایی که به او ایمان دارید پروا کنید.'),
(5162, 60, 12, 'ای پیامبر! هنگامی که زنان باایمان نزد تو آیند تا [بر پایه این شرایط] با تو بیعت کنند که چیزی را با خدا شریک نگیرند، و دزدی نکنند، و مرتکب زنا نشوند، و فرزندان خود را نکشند، و طفل حرام زاده خود را به دروغ و افترا به شوهرانشان نبندند، و در هیچ کار پسندیده ای از تو سرپیچی نکنند، با آنان بیعت کن و از خدا برای آنان آمرزش بخواه؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(5163, 60, 13, 'ای مؤمنان! با قومی که خدا بر آنان خشم گرفته، دوستی نکنید. آنان به یقین از آخرت مأیوسند، همان گونه که کافران مدفون در قبرها [که به سرانجام شوم خود رسیده اند، از نجات خویش] مأیوسند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمان ها و آنچه در زمین است، خدا را [به پاک بودن از هر عیب و نقصی] می ستایند، و او توانای شکست ناپذیر و حکیم است.'),
(5165, 61, 2, 'ای مؤمنان! چرا چیزی را می گویید که خود عمل نمی کنید؟'),
(5166, 61, 3, 'نزد خدا به شدت موجب خشم است که چیزی را بگویید که خود عمل نمی کنید.'),
(5167, 61, 4, 'خدا کسانی را دوست دارد که صف زده در راه او جهاد می کنند [و از ثابت قدمی] گویی بنایی پولادین و استوارند.'),
(5168, 61, 5, 'و [یاد کن] هنگامی را که موسی به قومش گفت: ای قوم من! چرا مرا می آزارید در حالی که می دانید من فرستاده خدا به سوی شما هستم؟ پس هنگامی که [از حق] منحرف شدند، خدا هم دل هایشان را [از پذیرفتن هدایت] منحرف کرد، و خدا مردم فاسق را هدایت نمی کند.'),
(5169, 61, 6, 'و [یاد کن] هنگامی را که عیسی پسر مریم گفت: ای بنی اسرائیل! به یقین من فرستاده خدا به سوی شمایم، تورات را که پیش از من بوده، تصدیق می کنم، و به پیامبری که بعد از من می آید و نامش «احمد» است، مژده می دهم. پس هنگامی که [احمد] دلایل روشن برای آنان آورد، گفتند: این جادویی است آشکار!!'),
(5170, 61, 7, 'و ستمکارتر از کسی که بر خدا دروغ می بندد، در حالی که به اسلام دعوت می شود کیست؟ و خدا مردم ستمکار را هدایت نمی کند.'),
(5171, 61, 8, 'می خواهند نور خدا را با دهان هایشان خاموش کنند در حالی که خدا کامل کننده نور خود است، گرچه کافران خوش نداشته باشند.'),
(5172, 61, 9, 'اوست که پیامبرش را با هدایت و دین حق فرستاد تا آن را بر همه ادیان پیروز کند، گرچه مشرکان خوش نداشته باشند.'),
(5173, 61, 10, 'ای اهل ایمان! آیا شما را به تجارتی راهنمایی کنم که شما را از عذابی دردناک نجات می دهد؟'),
(5174, 61, 11, 'به خدا و پیامبرش ایمان آورید، و با اموال و جان هایتان در راه خدا جهاد کنید؛ این [ایمان و جهاد] اگر [به منافع فراگیر و همیشگی آن] معرفت و آگاهی داشتید، برای شما [از هر چیزی] بهتر است؛'),
(5175, 61, 12, 'تا گناهتان را بیامرزد، و شما را در بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است و خانه های پاکیزه در بهشت های جاویدان، درآورد؛ این است کامیابی بزرگ.'),
(5176, 61, 13, 'و نعمت های دیگری که آن را دوست دارید [و به شما عطا می کند] یاری و پیروزی نزدیک از سوی خداست. و مؤمنان را مژده ده.'),
(5177, 61, 14, 'ای اهل ایمان! یاران خدا باشید، همان گونه که عیسی پسر مریم به حواریون گفت: یاران من در راه خدا چه کسانی اند؟ حواریون گفتند: ما یاران خداییم. پس طایفه ای از بنی اسرائیل ایمان آوردند و گروهی کافر شدند؛ در نتیجه کسانی را که ایمان آوردند، بر ضد دشمنانشان یاری دادیم تا پیروز شدند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمان ها و آنچه در زمین است، خدا را [به پاک بودن از هر عیب و نقصی] می ستایند، خدایی که فرمانروای هستی و بی نهایت پاکیزه و توانای شکست ناپذیر و حکیم است.'),
(5179, 62, 2, 'اوست که در میان مردم بی سواد، پیامبری از خودشان برانگیخت تا آیات او را بر آنان بخواند و آنان را [از آلودگی های فکری و روحی] پاکشان کند و به آنان کتاب و حکمت بیاموزد، و آنان به یقین پیش از این در گمراهی آشکاری بودند.'),
(5180, 62, 3, 'و [نیز پیامبر را] بر مردمی دیگر [از عرب و غیر عرب] که هنوز به آنان نپیوسته اند [برانگیخت]. و او توانای شکست ناپذیر و حکیم است.'),
(5181, 62, 4, 'این [برانگیختن به پیامبری و مسؤولیت عظیم تعلیم و تربیت،] فضل خداست که آن را به هر کس بخواهد عطا می کند، و خدا صاحب فضل بزرگ است.'),
(5182, 62, 5, 'وصف کسانی که عمل کردن به تورات به آنان تکلیف شده است، آن گاه به آن عمل نکردند، مانند درازگوشی است که کتاب هایی را [که هیچ آگاهی به محتویات آنها ندارد] حمل می کند. چه بد است سرگذشت مردمی که آیات خدا را تکذیب کردند. و خدا مردم ستمکار را هدایت نمی کند.'),
(5183, 62, 6, 'بگو: ای یهودیان! اگر گمان می کنید که فقط شما دوستان خدایید نه مردم دیگر، پس آرزوی مرگ کنید اگر راستگویید [چون دوستان خدا برای رسیدن به لقاء او مشتاق مرگ هستند.]'),
(5184, 62, 7, 'ولی آنان به سبب گناهانی که مرتکب شده اند هرگز آروزی مرگ نمی کنند، و خدا به ستمکاران داناست.'),
(5185, 62, 8, 'بگو: بی تردید مرگی را که از آن می گریزید با شما دیدار خواهد کرد، سپس به سوی دانای نهان و آشکار بازگردانده می شوید، پس شما را به اعمالی که همواره انجام می دادید، آگاه خواهد کرد.'),
(5186, 62, 9, 'ای مؤمنان! چون برای نماز روز جمعه ندا دهند، به سوی ذکر خدا بشتابید، و خرید و فروش را رها کنید. که این [اقامه نماز جمعه و ترک خرید و فروش] برای شما بهتر است اگر [به پاداشش] معرفت و آگاهی داشتید.'),
(5187, 62, 10, 'و چون نماز پایان گیرد، در زمین پراکنده شوید و از فضل و رزق خدا جویا شوید و خدا را بسیار یاد کنید تا رستگار شوید.'),
(5188, 62, 11, 'و [برخی از مردم] چون تجارت یا مایه سرگرمی ببینند [از صف یک پارچه نماز] به سوی آن پراکنده شوند و تو را در حالی که [بر خطبه نماز] ایستاده ای، رها کنند. بگو: پاداش و ثوابی که نزد خداست از سرگرمی و تجارت بهتر است، و خدا بهترین روزی دهندگان است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'چون منافقان نزد تو آیند، می گویند: گواهی می دهیم که تو بی تردید فرستاده خدایی. و خدا می داند که تو بی تردید فرستاده اویی، و خدا گواهی می دهد که یقیناً منافقان دروغگویند.'),
(5190, 63, 2, 'سوگندهایشان را [برای شناخته نشدن نفاقشان] سپر گرفته در نتیجه [مردم را] از راه خدا بازداشته اند. چه بد است آنچه را اینان همواره انجام می دهند!'),
(5191, 63, 3, 'این [صفت زشت نفاق و بدی اعمال] به سبب آن است که آنان [نخست] ایمان آوردند، سپس کافر شدند در نتیجه بر دل هایشان مهر [تیره بختی] زده شد، به این علّت [حقایق را] نمی فهمند.'),
(5192, 63, 4, 'چون آنان را ببینی جسم و ظاهرشان [از آراستگی و وقار] تو را به شگفت آورد، و اگر سخن گویند [به علت شیرینی و جذابیّت کلام] به سخنانشان گوش فرا می دهی [اما از پوچی باطن، سبک مغزی و دورویی] گویی چوب های خشکی هستند که به دیواری تکیه دارند [و در حقیقت اجسادی بی روح اند که در هیچ برنامه ای اطمینانی به آنان نیست، از شدت بزدلی] هر فریادی را به زیان خود می پندارند. اینان دشمن واقعی اند؛ از آنان بپرهیز؛ خدا آنان را بکشد؛ چگونه [با دیدن این همه دلایل روشن، از حق به باطل] منحرف می شوند.'),
(5193, 63, 5, 'و چون به آنان گویند: بیایید تا پیامبر خدا برای شما آمرزش بخواهد [از روی کبر و غرور] سرهای خود را بر می گردانند، و آنان را می بینی که متکبرانه [از حق،] روی می گردانند.'),
(5194, 63, 6, 'برای آنان یکسان است چه برای آنان آمرزش بخواهی چه نخواهی، خدا هرگز آنان را نمی آمرزد. مسلماً خدا مردم فاسق را هدایت نمی کند.'),
(5195, 63, 7, 'فقط آنانند که می گویند: به کسانی که نزد پیامبر خدایند، انفاق مکنید تا [از پیرامون او] پراکنده شوند. در حالی که خزانه های آسمان ها و زمین در سیطره خداست، ولی منافقان نمی فهمند.'),
(5196, 63, 8, 'می گویند: اگر به مدینه بازگردیم، مسلماً آنکه عزیزتر است [یعنی عبدالله بن ابی سلول، رئیس منافقان] آن را که خوارتر است [یعنی پیامبر که هنوز به پندار آنان تمکن لازم را به دست نیاورده] از آنجا بیرون می کند. در حالی که عزت و اقتدار برای خدا و پیامبر او و مؤمنان است، ولی منافقان [به این حقیقت] معرفت و آگاهی ندارند.'),
(5197, 63, 9, 'ای مؤمنان! مبادا اموال و فرزندانتان شما را از یاد خدا غافل کنند، و آنان که [به خاطر مال و فرزند از یاد خدا] غافل می شوند، زیانکارند.'),
(5198, 63, 10, 'و از آنچه روزی شما کرده ایم انفاق کنید، پیش از آنکه یکی از شما را مرگ در رسد و بگوید: چرا مرا تا مدتی نزدیک مهلت ندادی تا صدقه دهم و از شایستگان باشم؟!'),
(5199, 63, 11, 'و هرگز خدا کسی را چون اجلش فرا رسد، مهلت نمی دهد؛ و خدا به اعمالی که انجام می دهید، آگاه است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'آنچه در آسمان ها و آنچه در زمین است، خدا را [به پاک بودن از هر عیب و نقصی] می ستایند. فرمانروایی ویژه اوست، همه ستایش ها مخصوص اوست، و او بر هر کاری تواناست.'),
(5201, 64, 2, 'اوست که شما را آفرید؛ گروهی از شما کافرند، و برخی مؤمن، و خدا به آنچه انجام می دهید، بیناست.'),
(5202, 64, 3, 'آسمان ها و زمین را به حق آفرید، و شما را صورت گری کرد و صورت هایتان را نیکو آراست، و بازگشت به سوی اوست.'),
(5203, 64, 4, 'آنچه را که در آسمان ها و زمین است می داند، و [نیز] آنچه را پنهان می کنید و آنچه را آشکار می نمایید، می داند و خدا به نیّات و اسرار سینه ها داناست.'),
(5204, 64, 5, 'آیا خبر کسانی که پیش از این کافر شدند، پس عقوبت کفرشان را چشیدند و برای آنان عذابی دردناک است، به شما نرسیده است؟'),
(5205, 64, 6, 'آن [عقوبت و عذاب دردناک] به سبب آن است که پیامبرانشان همواره دلایل روشن برای آنان آوردند، ولی گفتند: آیا بشر [ی مانند خودمان] ما را راهنمایی می کنند؟! پس کافر شدند و [از حق] روی گرداندند و خدا [از ایمان و طاعتشان] اظهار بی نیازی کرد، و خدا بی نیاز و ستوده است.'),
(5206, 64, 7, 'کافران پنداشتند [که پس از مرگ] هرگز برانگیخته نخواهند شد. بگو: آری سوگند به پروردگارم مسلماً برانگیخته خواهید شد، سپس شما را به آنچه انجام داده اید، خبر خواهند داد، و این بر خدا آسان است.'),
(5207, 64, 8, 'پس به خدا و پیامبرش و نوری که نازل کردیم، ایمان آورید و خدا به آنچه انجام می دهید، آگاه است.'),
(5208, 64, 9, '[بی تردید برانگیخته می شوید در] روزی که شما را در روز اجتماع [که روز قیامت است] جمع می کند، آن روز، روز غبن و خسارت است؛ و هرکس به خدا ایمان بیاورد و کار شایسته انجام دهد، خدا گناهانش را از او محو می کند، و او را به بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، درمی آورد، در آنها جاودانه و همیشگی اند. این است کامیابی بزرگ.'),
(5209, 64, 10, 'و آنان که کافر شدند و آیات ما را انکار کردند، اهل آتش اند و در آن جاودانه اند، و بد بازگشت گاهی است!'),
(5210, 64, 11, 'هیچ مصیبتی جز به فرمان خدا نرسد. و هر کس به خدا ایمان بیاورد، خدا قلبش را [به حقایق] راهنمایی می کند؛ و خدا به همه چیز داناست.'),
(5211, 64, 12, 'و از خدا اطاعت کنید و از پیامبر فرمان برید، و اگر روی برگردانید [بدانید که] بر عهده پیامبر فقط رساندن آشکار [پیام وحی] است.'),
(5212, 64, 13, 'خداست که هیچ معبودی جز او نیست، پس مؤمنان فقط باید بر او توکّل کنند.'),
(5213, 64, 14, 'ای اهل ایمان! به راستی برخی از همسران و فرزندانتان [به علت بازداشتن شما از اجرای فرمان های خدا و پیامبر] دشمن شمایند؛ بنابراین از [عمل به خواسته های بی جای] آنان [که مخالف احکام خداست] بپرهیزید، و اگر [از آزار و رنجی که به شما می دهند] چشم پوشی کنید و سرزنش کردن آنان را ترک نمایید و از آنان بگذرید [خدا هم شما را مورد الطاف بی کرانش قرار می دهد]؛ زیرا خدا بسیار آمرزنده و مهربان است.'),
(5214, 64, 15, 'اموال و فرزندانتان فقط وسیله آزمایش اند، و خداست که پاداشی بزرگ نزد اوست.'),
(5215, 64, 16, 'بنابراین به اندازه استطاعتی که دارید از خدا پروا کنید و [دعوت حق را] بشنوید و اطاعت نمایید و انفاق کنید که برای شما بهتر است؛ و کسانی که خود را از بخل و حرص بازدارند آنان رستگارند.'),
(5216, 64, 17, 'اگر به خدا وام نیکو دهید، آن را برای شما دو چندان می کند و شما را می آمرزد و خدا عطا کننده پاداش فراوان در برابر عمل اندک می باشد و بردبار است.'),
(5217, 64, 18, 'دانای نهان و آشکار و توانای شکست ناپذیر و حکیم است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'ای پیامبر! زمانی که خواستید زنان را طلاق دهید، آنان را در زمان عدّه [آن زمانی که از عادت پاک شده و با همسر آمیزش نکرده باشند] طلاق دهید و حساب عدّه را نگه دارید، و از خدا پروردگارتان پروا کنید، و آنان را [در مدتی که عدّه خود را می گذرانند] از خانه هایشان بیرون مکنید، و آنان هم [در مدت عدّه] بیرون نروند مگر اینکه مرتکب عمل زشت آشکاری شوند [که در آن صورت بیرون کردنشان از خانه بی مانع است]. این حدود خداست؛ و هر که از حدود تجاوز کند، یقیناً به خودش ستم ورزیده است. تو نمی دانی شاید خدا پس از این [در عده طلاق،] کاری پدید آورد [تا وسیله صلح و سازشی میان آنان شود.]'),
(5219, 65, 2, 'و چون به پایان زمان [عدّه] نزدیک شدند، آنان را [با رجوع به زوجیت] به صورتی شایسته [که رعایت همه حقوق همسرداری است] نگه دارید یا از آنان به طرزی شایسته [که پرداخت همه حقوق شرعیه اوست] جدا شوید، و [هنگام جدایی،] دو عادل از خودتان را گواه [طلاق] گیرید. [و شما ای گواهان!] گواهی را برای خدا اقامه کنید. به وسیله این [حقایق] به کسی که همواره به خدا و روز قیامت ایمان دارد، اندرز داده می شود و هر که از خدا پروا کند، خدا برای او راه بیرون شدن [از مشکلات و تنگناها را] قرار می دهد.'),
(5220, 65, 3, 'و او را از جایی که گمان نمی برد روزی می دهد، و کسی که بر خدا توکل کند، خدا برایش کافی است، [و] خدا فرمان و خواسته اش را [به هر کس که بخواهد] می رساند؛ یقیناً برای هر چیزی اندازه ای قرار داده است.'),
(5221, 65, 4, 'و از زنان شما آنانی که از عادت ماهیانه ناامیدند، اگر شک دارید [که به سبب رسیدن به سن یائسگی یا عاملی دیگر است] عدّه آنان [پس از طلاق] سه ماه است. و [هم چنین عدّه] زنانی که [با وجود سن معمولی] عادت نشده اند [سه ماه است] و [پایان] عدّه زنان باردار، روزی است که وضع حمل می کنند. و هر که از خدا پروا کند برای او در کارش آسانی قرار می دهد.'),
(5222, 65, 5, 'این حقایق بیان شده احکام خداست که آنها را به سوی شما نازل کرده است؛ و هر کس از خدا پروا کند، گناهانش را از او محو می کند و پاداش او را بزرگ می دارد.'),
(5223, 65, 6, 'زنان طلاق داده را [تا پایان عدّه] به اندازه توانگری خود آنجا سکونت دهید که خود سکونت دارید و به آنان [در نفقه و مسکن] آسیب و زیان نرسانید تا زندگی را بر آنان سخت و دشوار کنید [و آنان مجبور به ترک خانه شوند]، و اگر باردار باشند، هزینه آنان را تا زمانی که وضع حمل کنند، بپردازید و اگر [پس از جدایی،] کودک شما را شیر دادند، اجرتشان را بدهید، و در میان خود [درباره فرزند] به نیکی و شایستگی مشورت کنید [تا به توافقی عادلانه برسید و در نتیجه به حقّ زن و شوهر و فرزند زیانی نرسد] و اگر [کارتان در این زمینه] با یکدیگر به سختی کشید [و نهایتاً به توافق نرسیدید،] زنی دیگر کودک را [با هزینه پدر] شیر دهد.'),
(5224, 65, 7, 'بر توانگر است که از توانگری اش [همسر بچه دارش را در ایام عدّه] هزینه دهد، و هر که رزق و روزی اش تنگ باشد از همان که خدا به او عطا کرده هزینه دهد. خدا هیچ کس را جز به اندازه رزقی که به او عطا کرده است، تکلیف نمی کند. خدا به زودی پس از سختی و تنگنا، فراخی و گشایش قرار می دهد.'),
(5225, 65, 8, 'چه بسیار آبادی ها که [اهلش] از فرمان پروردگارشان و فرستادگانش روی برگرداندند، پس ما آنان را به حساب سختی محاسبه کردیم و به عذاب بسیار شدیدی عذاب نمودیم؛'),
(5226, 65, 9, 'و آنان کیفر سنگین کفر و کبرشان را چشیدند و سرانجام کارشان خسارت بود.'),
(5227, 65, 10, 'خدا برای آنان [در آخرت] عذابی سخت آماده کرده است، پس ای خردمندان مؤمن! از خدا پروا کنید. به راستی خدا به سوی شما وسیله تذکر نازل کرده است.'),
(5228, 65, 11, '[و آن] پیامبری [است] که آیات روشن خدا را بر شما می خواند، تا کسانی را که ایمان آورده و کارهای شایسته انجام داده اند از تاریکی ها به سوی نور بیرون آورد. و هر کس به خدا ایمان بیاورد و کار شایسته انجام دهد، او را در بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، درآورد، در حالی که در آنها جاودانه اند. همانا رزق و روزی را برای او نیکو قرار داده است.'),
(5229, 65, 12, 'خداست که هفت آسمان و نیز مثل آنها هفت زمین را آفرید. همواره فرمان او در میان آنها نازل می شود تا بدانید که خدا بر هر کاری تواناست و اینکه یقیناً علم خدا به همه چیز احاطه دارد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ای پیامبر! چرا آنچه را که خدا بر تو حلال کرده برای به دست آوردن خشنودی همسرانت بر خود حرام می کنی؟ و خدا بسیار آمرزنده و مهربان است.'),
(5231, 66, 2, 'به یقین خدا راه گشودن [و شکستن] سوگندهایتان را [در مواردی] برای شما مقرّر کرده است، [و آن اینکه سوگندها را با کفاره دادن بشکنید تا از محدودیت و تنگنا درآیید] و خدا سرپرست و یار شماست، و او دانا و حکیم است.'),
(5232, 66, 3, 'و هنگامی که پیامبر، رازی را به یکی از همسرانش گفت و او آن راز را [نزد زن دیگر] فاش کرد، و خدا پیامبر را از افشای آن آگاه نمود، پیامبر بخشی از آن [راز افشا شده] را به آن زن گوشزد کرد و از گفتن بخشی دیگر [برای آنکه فاش کننده بیشتر شرمنده نشود] خودداری نمود. آن زن گفت: چه کسی فاش کردن راز را [به وسیله من] به تو خبر داده است؟ پیامبر گفت: دانای آگاه، به من خبر داد.'),
(5233, 66, 4, 'اگر شما دو زن از کار خود به پیشگاه خدا توبه کنید [خدا توبه شما را می پذیرد]، چون دل شما دو نفر از حق و درستی منحرف شده است، و اگر بر ضدِّ پیامبر به یکدیگر کمک دهید [راه به جایی نخواهید برد]؛ زیرا خدا و جبرئیل و صالح مؤمنان [که علی بن ابی طالب است] یار اویند، و فرشتگان نیز بعد از آنان پشتیبان او خواهند بود.'),
(5234, 66, 5, 'اگر پیامبر، شما را طلاق دهد امید است که پروردگارش همسرانی بهتر از شما که مسلمان، مؤمن، فرمانبر، توبه کار، خداپرست، روزه دار، غیر باکره و باکره باشند، به او عوض دهد.'),
(5235, 66, 6, 'ای مؤمنان! خود و خانواده خود را از آتشی که هیزم آن انسان ها و سنگ ها است، حفظ کنید. بر آن فرشتگانی خشن و سخت گیر گمارده شده اند که از آنچه خدا به آنان دستور داده، سرپیچی نمی کنند، و آنچه را به آن مأمورند، همواره انجام می دهند.'),
(5236, 66, 7, '[آن روز گویند:] ای کافران! امروز عذرخواهی مکنید، فقط آنچه را همواره انجام می دادید، جزا داده می شوید.'),
(5237, 66, 8, 'ای مؤمنان! به پیشگاه خدا توبه کنید، توبه ای خالص [که شما را از بازگشت به گناه بازدارد]؛ امید است پروردگارتان گناهانتان را از شما محو کند و شما را به بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، درآورد، در آن روزی که خدا پیامبر و کسانی که با او ایمان آورده اند، خوار نمی کند، نورشان پیشاپیش آنان و از سوی راستشان شتابان حرکت می کند، می گویند: پروردگارا! نور ما را برای ما کامل کن و ما را بیامرز که تو بر هر کاری توانایی.'),
(5238, 66, 9, 'ای پیامبر! با کافران و منافقان جنگ کن و بر آنان سخت بگیر و جایگاهشان دوزخ است و بد بازگشت گاهی است.'),
(5239, 66, 10, 'خدا برای کافران، زن نوح و زن لوط را مثل زده که تحت سرپرستی و زوجیت دو بنده شایسته از بندگان ما بودند، ولی [در امر دین و دین داری] به آن دو [بنده شایسته ما] خیانت ورزیدند، و آن دو [پیامبر] چیزی از عذاب خدا را از آن دو زن دفع نکردند و [هنگام مرگ] به آن دو گفته شد: با وارد شوندگان وارد آتش شوید.'),
(5240, 66, 11, 'و خدا برای مؤمنان همسر فرعون را مثل زده است هنگامی که گفت: پروردگارا! برای من نزد خودت خانه ای در بهشت بنا کن ومرا از فرعون وکردارش رهایی بخش ومرا از مردم ستمکار نجات ده.'),
(5241, 66, 12, 'و [نیز] مریم دختر عمران [را مثل زده است] که دامان خود را پاک نگه داشت و در نتیجه از روح خود در او دمیدیم و کلمات پروردگارش و کتاب های او را تصدیق کرد و از اطاعت کنندگان [فرمان های خدا] بود.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'همیشه سودمند و با برکت است آنکه فرمانروایی [همه هستی] به دست اوست و او بر هر کاری تواناست.'),
(5243, 67, 2, 'آنکه مرگ و زندگی را آفرید تا شما را بیازماید که کدامتان نیکوکارترید، و او توانای شکست ناپذیر و بسیار آمرزنده است.'),
(5244, 67, 3, 'آنکه هفت آسمان را بر فراز یکدیگر آفرید. در آفرینش [خدای] رحمان، خلل و نابسامانی و ناهمگونی نمی بینی، پس بار دیگر بنگر آیا هیچ خلل و نابسامانی و ناهمگونی می بینی؟'),
(5245, 67, 4, 'سپس بار دیگر بنگر تا دیده ات در حالی که خسته و کم سو شده [و از یافتن خلل، نابسامانی و ناهمگونی فرو مانده] و درمانده گشته است، به سویت باز گردد.'),
(5246, 67, 5, 'همانا ما آسمان دنیا را با چراغ هایی آراستیم و آنها را تیرهایی برای راندن شیطان ها قرار دادیم، و برای آنان [در آخرت] آتشی افروخته، آماده کرده ایم؛'),
(5247, 67, 6, 'و برای کسانی که به پروردگارشان کافر شدند، عذاب دوزخ است و بد بازگشت گاهی است.'),
(5248, 67, 7, 'هنگامی که در آن افکنده شوند از آن در حالی که در جوش و فوران است، صدایی هولناک و دلخراش می شنوند.'),
(5249, 67, 8, 'نزدیک است که از شدت خشم متلاشی و پاره پاره شود. هرگاه گروهی در آن افکنده شوند، نگهبانانش از آنان می پرسند: آیا شما را بیم دهنده ای نیامد؟'),
(5250, 67, 9, 'می گویند: چرا، بیم دهنده آمد، ولی او را انکار کردیم و گفتیم: خدا هیچ چیز نازل نکرده است؛ شما بیم دهندگان جز در گمراهی بزرگی نیستید؛'),
(5251, 67, 10, 'و می گویند: اگر ما [دعوت سعادت بخش آنان را] شنیده بودیم، یا [در حقایقی که برای ما آوردند] تعقّل کرده بودیم، در میان [آتش] اهل آتش سوزان نبودیم.'),
(5252, 67, 11, 'پس به گناه خود اعتراف می کنند. و مرگ و دوری از رحمت بر اهل آتش سوزان باد.'),
(5253, 67, 12, 'بی تردید کسانی که در نهان از پروردگارشان می ترسند، برای آنان آمرزش و پاداشی بزرگ است.'),
(5254, 67, 13, 'و گفتارتان را پنهان کنید یا آن را آشکار سازید، مسلماً او به نیّات و اسرار سینه ها داناست.'),
(5255, 67, 14, 'آیا کسی که [همه موجودات را] آفریده است، نمی داند؟ و حال آنکه او لطیف و آگاه است.'),
(5256, 67, 15, 'اوست که زمین را برای شما رام قرار داد، بنابراین بر اطراف و جوانب آن راه روید و از روزی خدا بخورید، و برانگیختن مردگان و رستاخیز به سوی اوست.'),
(5257, 67, 16, 'آیا از کسی که [حاکمیت و فرمانروایی اش] در آسمان قطعی است [چه رسد در زمین] خود را در امان دیده اید، از اینکه [با شکافتن زمین] شما را در آن فرو برد در حالی که زلزله و جنبش موج آسایش را ادامه دهد؟'),
(5258, 67, 17, 'آیا از کسی که حاکمیت و فرمانروایی اش در آسمان قطعی است [چه رسد در زمین] خود را در امان دیده اید از اینکه توفانی سخت [که با خود ریگ و سنگ می آورد] بر شما فرستد؟ پس به زودی خواهید دانست که بیم دادن من چگونه است!'),
(5259, 67, 18, 'و مسلماً کسانی که پیش از آنان بودند [آیات خدا و پیامبران را انکار کردند]، پس [بنگر] عذاب من چگونه بود!'),
(5260, 67, 19, 'آیا ندانسته اند که پرندگان بالای سرشان را در حالی که بال می گشایند و می بندند، فقط [خدای] رحمان در فضا نگه می دارد؟ یقیناً او بر همه چیز بیناست.'),
(5261, 67, 20, 'آیا آن کیست [از] سپاهتان که شما را به هنگام نزول عذاب در برابر خدای رحمان یاری دهد؟ کافران جز دچار فریب [شیطان] نیستند.'),
(5262, 67, 21, 'یا کیست آنکه به شما روزی دهد، اگر خدا روزی اش را از شما باز دارد؟ [نه اینکه حقیقت را نمی دانند] بلکه در سرکشی و نفرتِ [از حق سرسختی و] پافشاری می کنند.'),
(5263, 67, 22, 'آیا کسی که نگونسار و به صورت افتاده حرکت می کند، هدایت یافته تر است یا آنکه راست قامت بر راه راست می رود؟'),
(5264, 67, 23, 'بگو: اوست که شما را آفرید و برای شما گوش و دیده و دل قرار داد، ولی اندکی سپاس می گزارید.'),
(5265, 67, 24, 'بگو: اوست که شما را در زمین آفرید و به سوی او محشور می شوید.'),
(5266, 67, 25, 'و می گویند: اگر راستگویید این وعده وقوع [رستاخیز] کی خواهد بود؟'),
(5267, 67, 26, 'بگو: دانش و آگاهی [این حقیقت] فقط نزد خداست و من تنها بیم دهنده ای آشکارم.'),
(5268, 67, 27, 'پس زمانی که آن [وعده داده شده] را از نزدیک ببینند، چهره کافران درهم و زشت گردد [و به آنان گویند:] این است همان چیزی که آن را [از روی مسخره] می خواستید.'),
(5269, 67, 28, 'بگو: به من خبر دهید اگر خدا من را و هر که را با من است هلاک کند، یا مورد رحمت قرار دهد، پس چه کسی کافران را از عذاب دردناک پناه خواهد داد؟'),
(5270, 67, 29, 'بگو: اوست رحمان، به او ایمان آوردیم، و بر او توکل کردیم، پس به زودی خواهید دانست چه کسی در گمراهی آشکار است.'),
(5271, 67, 30, 'بگو: به من خبر دهید اگر آب مورد بهره برداری شما [چون آب رودها، چشمه ها، سدها و چاه ها] در زمین فرو رود [تا آنجا که از دسترس شما خارج گردد] پس کیست که برایتان آب روان و گوارا بیاورد؟!');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'ن، سوگند به قلم و آنچه می نویسند،'),
(5273, 68, 2, 'که به سبب نعمت و رحمت پروردگارت [که نبوّت، دانش و بصیرت است] تو مجنون نیستی؛'),
(5274, 68, 3, 'و بی تردید برای تو پاداشی دایم و همیشگی است؛'),
(5275, 68, 4, 'و یقیناً تو بر بلندای سجایای اخلاقی عظیمی قرار داری.'),
(5276, 68, 5, 'پس به زودی می بینی و [منکران هم] می بینند،'),
(5277, 68, 6, 'که کدام یک از شما دچار جنون اند؛'),
(5278, 68, 7, 'بی تردید پروردگارت بهتر می داند چه کسی از راه او گمراه شده، و او به راه یافتگان داناتر است. ….'),
(5279, 68, 8, 'از تکذیب کنندگان [قرآن و نبوّت] فرمان مبر.'),
(5280, 68, 9, 'دوست دارند نرمی کنی تا نرمی کنند،'),
(5281, 68, 10, 'و از هر سوگند خورده زبون و فرومایه فرمان مبر،'),
(5282, 68, 11, 'آنکه بسیار عیب جوست، و برای سخن چینی در رفت و آمد است،'),
(5283, 68, 12, 'به شدت بازدارنده [مردم] از کار خیر و متجاوز و گنهکار است.'),
(5284, 68, 13, 'گذشته از این ها کینه توز و بی اصل و نسب است،'),
(5285, 68, 14, '[سرکشی و یاغی گری اش] برای آن [است] که دارای ثروت و فرزندان فراوان است.'),
(5286, 68, 15, 'هنگامی که آیات ما را بر او می خوانند، گوید: افسانه های پیشینیان است!'),
(5287, 68, 16, 'به زودی بر بینیِ [پر باد و خرطوم مانند] ش داغ رسوایی و خواری می نهیم.'),
(5288, 68, 17, 'بی تردید ما آنان را [که در مکه بودند] آزمودیم همان گونه که صاحبان آن باغ را [در منطقه یمن] آزمودیم، هنگامی که سوگند خوردند که صبحگاهان حتماً میوه های باغ را بچینند،'),
(5289, 68, 18, 'و چیزی از آن را [برای تهیدستان و نیازمندان] استثنا نکردند.'),
(5290, 68, 19, 'پس در حالی که صاحبان باغ در خواب بودند، بلایی فراگیر از سوی پروردگارت آن باغ را فرا گرفت.'),
(5291, 68, 20, 'پس [آن باغ] به صورت شبی تاریک درآمد [و جز خاکستر چیزی در آن دیده نمی شد!]'),
(5292, 68, 21, 'وهنگام صبح یکدیگر را آوازدادند،'),
(5293, 68, 22, 'که اگر قصد چیدن میوه دارید بامدادان به سوی کشتزار و باغتان حرکت کنید؛'),
(5294, 68, 23, 'پس به راه افتادند در حالی که آهسته به هم می گفتند:'),
(5295, 68, 24, 'امروز نباید نیازمندی در این باغ بر شما وارد شود'),
(5296, 68, 25, 'و بامدادان به قصد اینکه تهیدستان را محروم گذارند به سوی باغ روان شدند.'),
(5297, 68, 26, 'پس چون [به باغ رسیدند و آن را نابود] دیدند، گفتند: یقیناً ما گمراه بوده ایم [که چنان تصمیم خلاف حقّی درباره مستمندان و تهیدستان گرفتیم.]'),
(5298, 68, 27, 'بلکه ما [از لطف خدا هم] محرومیم.'),
(5299, 68, 28, 'عاقل ترینشان گفت: آیا به شما نگفتم که چرا خدا را [به پاک بودن از هر عیب و نقصی] یاد نمی کنید [و چرا او را از انتقام گرفتن درمانده می دانید؟!]'),
(5300, 68, 29, 'گفتند: پروردگارا! تو را به پاکی می ستاییم، مسلماً ما ستمکار بوده ایم.'),
(5301, 68, 30, 'پس به یکدیگر رو کرده به سرزنش و ملامت هم پرداختند.'),
(5302, 68, 31, 'گفتند: وای بر ما که طغیان گر بوده ایم.'),
(5303, 68, 32, 'امید است پروردگارمان بهتر از آن را به ما عوض دهد چون ما [از هر چیزی دل بریدیم و] به پروردگارمان راغب و علاقه مندیم.'),
(5304, 68, 33, 'چنین است عذاب [دنیا] و عذاب آخرت اگر معرفت و آگاهی داشتند، بزرگ تر است.'),
(5305, 68, 34, 'بی تردید برای پرهیزکاران نزد پروردگارشان بهشت های پرنعمت است.'),
(5306, 68, 35, 'آیا ما تسلیم شدگان [به فرمان ها و احکام خود] را چون مجرمان قرار می دهیم؟'),
(5307, 68, 36, 'شما را چه شده؟ چگونه داوری می کنید؟!'),
(5308, 68, 37, 'آیا شما را کتابی [آسمانی از نزد خدا] ست که در آن می خوانید'),
(5309, 68, 38, 'که در آن جهان هر چه را شما بخواهید و انتخاب کنید برای شما خواهد بود؟!'),
(5310, 68, 39, 'یا شما را بر ما تا روز قیامت پیمان و سوگند استواری است که هر چه را به سود خود حکم کنید ویژه شماست؟!'),
(5311, 68, 40, 'از آنان بپرس کدامشان ضامن آن ادعاست [که مسلمان و مجرم یکسانند؟!!]'),
(5312, 68, 41, 'یا شریکانی [در ربوبیت خدا] دارند [که از آنان نزد خدا شفاعت کنند که در اجر و ثواب با مسلمانان یکسان شوند؟] پس اگر راستگویند، شریکانشان را [به میدان] آورند.'),
(5313, 68, 42, '[یاد کن] روزی را که کار بر آنان به شدت سخت و دشوار شود، [و آن روز که جای هیچ تکلیف و عبادتی نیست به عنوان سرزنش و ملامت] به سجده کردن دعوت شوند، ولی در خود قدرت و استطاعت [سجده کردن] نیابند!'),
(5314, 68, 43, 'دیدگانشان از شرم و حیا، فرو افتاده، خواری و ذلت آنان را فرا گیرد و اینان [در دنیا] به سجده [بر خدا] دعوت می شدند در حالی که تندرست بودند [ولی از فرمان خدا متکبرانه روی می گرداندند.]'),
(5315, 68, 44, 'پس مرا با کسانی که این قرآن را انکار می کنند واگذار، به زودی ما آنان را به تدریج از آن جا که نمی دانند [به سوی عذاب] می کشانیم؛'),
(5316, 68, 45, 'و [البته] آنان را مهلت می دهیم [تا گناهشان را در حال بی خبری به نهایت برسانند]، بی تردید نقشه و تدبیر من استوار است.'),
(5317, 68, 46, '[اینکه دعوتت را نمی پذیرند] مگر از آنان در برابر ابلاغ رسالت پاداشی می طلبی که از خسارت و زیانش سنگین بارند؟'),
(5318, 68, 47, 'یا غیب نزد آنان است و آنان از روی آن] می نویسند [و خود با تکیه بر آن به ادعاهای خود یقین می کنند و به دیگران هم خبر می دهند؟'),
(5319, 68, 48, 'پس در برابر حکم و قضای پروردگارت [که هلاک کردن تدریجی این طاغیان است] شکیبا باش و مانند صاحب ماهی [یونس] مباش [که شتاب در آمدن عذاب را برای قومش خواست و به این علت در شکم ماهی محبوس شد] و در آن حال با دلی مالامال از اندوه، خدا را ندا داد.'),
(5320, 68, 49, 'اگر رحمت و لطفی از سوی پروردگارش او را در نیافته بود، یقیناً نکوهش شده به صحرایی بی آب و گیاه افکنده می شد.'),
(5321, 68, 50, 'پس پروردگارش او را برگزید و از شایستگان قرار داد'),
(5322, 68, 51, 'و کافران چون قرآن را شنیدند، نزدیک بود تو را با چشمانشان بلغزانند [و از پای درآورند] و می گویند: بی تردید او دیوانه است!'),
(5323, 68, 52, 'در حالی که قرآن جز مایه تذکر و پند برای جهانیان نیست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن روز ثابت و حق [که وقوعش حتمی و تردیدناپذیر است،]'),
(5325, 69, 2, 'آن روز ثابت و حق چیست؟'),
(5326, 69, 3, 'و تو چه می دانی که آن روز ثابت و حق چیست؟'),
(5327, 69, 4, '[قوم] ثمود و عاد آن روز کوبنده را انکار کردند؛'),
(5328, 69, 5, 'اما قوم ثمود با عذابی سرکش هلاک شدند،'),
(5329, 69, 6, 'و اما قوم عاد با تندبادی بسیار سرد و طغیان گر نابود شدند،'),
(5330, 69, 7, 'که خدا آن را هفت شب و هشت روز پی در پی بر آنان مسلط کرد و [اگر آنجا بودی] می دیدی که آنان مانند تنه های پوسیده و پوک درختان خرما روی زمین افتاده [و هلاک شده اند.]'),
(5331, 69, 8, 'آیا از آنان هیچ باقی مانده ای می بینی؟'),
(5332, 69, 9, 'و فرعون و کسانی که پیش از او بودند و مردم شهرهای زیر و رو شده [قوم لوط] مرتکب گناهان بزرگ شدند،'),
(5333, 69, 10, 'و فرستاده پروردگارشان را نافرمانی کردند و خدا هم آنان را به عذابی سخت گرفت.'),
(5334, 69, 11, 'هنگامی که آب طغیان کرد، ما شما را در کشتی سوار کردیم،'),
(5335, 69, 12, 'تا آن را برای شما مایه تذکر و بیداری قرار دهیم و گوش شنوا آن را [به عنوان مایه عبرت و تذکر] حفظ کند.'),
(5336, 69, 13, 'پس چون در صور یک بار دمیده شود،'),
(5337, 69, 14, 'و زمین و کوه ها از جای خود برداشته شوند و هر دوی آنها یک باره درهم کوبیده و ریز ریز گردند!'),
(5338, 69, 15, 'پس آن روز است که آن واقعه بزرگ واقع می شود؛'),
(5339, 69, 16, 'و آسمان بشکافد و در آن روز است که از هم گسسته و متلاشی گردد؛'),
(5340, 69, 17, 'و فرشتگان [برای اجرای دستورها حق] بر کناره ها و اطراف آسمان قرار می گیرند، ودر آن روز هشت فرشته، عرش پروردگارت را بر فراز همه آنها حمل می کنند.'),
(5341, 69, 18, 'آن روز [همه شما برای حسابرسی به پیشگاه خدا] عرضه می شوید در حالی که هیچ [عمل و نیّت] پوشیده ای از شما پنهان نمی ماند.'),
(5342, 69, 19, 'اما کسی که پرونده اش را به دست راستش دهند، می گوید: [ای مردم!] پرونده مرا بگیرید و بخوانید.'),
(5343, 69, 20, 'من یقین داشتم که حساب اعمالم را می بینم [به این سبب همه اعمالم را هماهنگ با احکام خدا انجام دادم و کردار بدم را اصلاح کردم.]'),
(5344, 69, 21, 'پس او در یک زندگی خوش و پسندیده ای است.'),
(5345, 69, 22, 'در بهشتی برین'),
(5346, 69, 23, 'که میوه هایش در دسترس است.'),
(5347, 69, 24, '[به آنان گویند:] بخورید و بیاشامید، گوارایتان باد به سبب اعمالی که در ایام گذشته انجام دادید؛'),
(5348, 69, 25, 'و اما کسی که پرونده اعمالش را به دست چپش دهند، می گوید: ای کاش پرونده ام را دریافت نمی کردم،'),
(5349, 69, 26, 'و نمی دانستم حساب من چیست؟'),
(5350, 69, 27, 'ای کاش همان مرگ اول [که مرا از دنیا به آخرت انتقال داد] کارم را یکسره می کرد [و در نیستی ابد قرارم می داد،]'),
(5351, 69, 28, 'ثروتم عذاب را از من دفع نکرد،'),
(5352, 69, 29, 'قدرت و توانم از دست رفت.'),
(5353, 69, 30, '[فرمان آید] او را بگیرید و در غل و زنجیرش کشید،'),
(5354, 69, 31, 'آن گاه به دوزخش دراندازید،'),
(5355, 69, 32, 'سپس او را در زنجیری که طولش هفتاد ذرع است به بند کشید،'),
(5356, 69, 33, 'زیرا او به خدای بزرگ ایمان نمی آورده'),
(5357, 69, 34, 'و مردم را به اطعام نیازمندان تشویق نمی کرده.'),
(5358, 69, 35, 'پس امروز او را در اینجا دوست مهربان و حمایت گری نیست؛'),
(5359, 69, 36, 'و نه غذایی مگر چرکاب و کثافات [ی از بدن اهل دوزخ!]'),
(5360, 69, 37, 'که آن را جز خطاکاران نمی خورند.'),
(5361, 69, 38, 'پس سوگند یاد می کنم به آنچه [از محسوسات] می بینید،'),
(5362, 69, 39, 'و آنچه [از غیر محسوسات] نمی بینید،'),
(5363, 69, 40, 'بی تردید این قرآن، گفتار فرستاده ای بزرگوار است،'),
(5364, 69, 41, 'و آن گفتار یک شاعر نیست، ولی جز اندکی ایمان نمی آورید،'),
(5365, 69, 42, 'و گفتار کاهن هم نیست، ولی جز اندکی متذکّر نمی شوید.'),
(5366, 69, 43, 'نازل شده از سوی پروردگار جهانیان است.'),
(5367, 69, 44, 'و اگر [او] پاره ای از گفته ها را به دروغ بر ما می بست،'),
(5368, 69, 45, 'ما او را به شدت می گرفتیم،'),
(5369, 69, 46, 'سپس رگ قلبش را پاره می کردیم؛'),
(5370, 69, 47, 'در آن صورت هیچ کدام از شما مانع از عذاب او نبود،'),
(5371, 69, 48, 'بی تردید این قرآن، وسیله پند و تذکری برای پرهیزکاران است.'),
(5372, 69, 49, 'و ما به یقین می دانیم که از میان شما انکارکنندگانی هست.'),
(5373, 69, 50, 'و این انکار قطعاً مایه حسرت کافران است،'),
(5374, 69, 51, 'و بی تردید این قرآن، حقّی یقینی است.'),
(5375, 69, 52, 'پس به نام پروردگارت تسبیح گوی.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'درخواست کننده ای عذابی را که واقع شدنی است درخواست کرد،'),
(5377, 70, 2, '[عذابی که] ویژه کافران است، [و] آن را بازدارنده ای نیست.'),
(5378, 70, 3, '[این عذاب] از سوی خدای صاحب درجات است.'),
(5379, 70, 4, 'فرشتگان و روح در روزی که مقدارش پنجاه هزار سال است به سوی او بالا می روند.'),
(5380, 70, 5, 'پس صبر کن صبری نیکو [صبری که در کنارش جزع و ناخشنودی نباشد.]'),
(5381, 70, 6, 'دشمنان و مخالفان، آن [عذاب] را دور می بینند'),
(5382, 70, 7, 'و ما آن را نزدیک می بینیم.'),
(5383, 70, 8, 'روزی که آسمان چون فلز گداخته گردد'),
(5384, 70, 9, 'و کوه ها مانند پشم رنگینِ حلاجی شده شود'),
(5385, 70, 10, 'و هیچ خویشاوند و دوست صمیمی از [اوضاع و احوال] خویشاوند و دوست صمیمی اش نپرسد!'),
(5386, 70, 11, 'آنان را نشانشان می دهند [ولی به خاطر دل مشغولی خود هرگز به آنان توجه نکنند!] گنهکار آرزو می کند که ای کاش می توانست فرزندانش را در برابر عذاب آن روز فدیه و عوض دهد!'),
(5387, 70, 12, 'و نیز همسر و برادرش را'),
(5388, 70, 13, 'و قبیله و قومش را که [در دنیا] به او پناه می دادند،'),
(5389, 70, 14, 'و نیز همه کسانی را که در روی زمین اند تا [این فدیه و عوض] او را [از عذاب آن روز] نجات دهد!'),
(5390, 70, 15, 'این چنین نیست [که برایش راه نجاتی باشد] همانا آتش زبانه می کشد،'),
(5391, 70, 16, 'در حالی که دست و پا و پوست سر را بر می کند!!'),
(5392, 70, 17, 'هر که را به حق پشت کرده واز دعوت حق روی گردانده، می طلبد،'),
(5393, 70, 18, 'و آن را که ثروت جمع کرده و به ذخیره سازی و انباشتن پرداخته، می خواند؛'),
(5394, 70, 19, 'همانا انسان حریص و بی تاب آفریده شده است؛'),
(5395, 70, 20, 'چون آسیبی به او رسد، بی تاب است،'),
(5396, 70, 21, 'و هنگامی که خیر و خوشی [و مال و رفاهی] به او رسد، بسیار بخیل و بازدارنده است،'),
(5397, 70, 22, 'مگر نماز گزاران،'),
(5398, 70, 23, 'آنان که همواره بر نمازشان مداوم و پایدارند،'),
(5399, 70, 24, 'و آنان که در اموالشان حقّی معلوم است'),
(5400, 70, 25, 'برای درخواست کننده [تهیدست] و محروم [از معیشت و ثروت،]'),
(5401, 70, 26, 'و آنان که همواره روز پاداش را باور دارند،'),
(5402, 70, 27, 'و آنان که از عذاب پروردگارشان بیمناکند،'),
(5403, 70, 28, 'زیرا که از عذاب پروردگارشان ایمنی نیست،'),
(5404, 70, 29, 'و آنان که دامنشان را [از آلوده شدن به شهوات حرام] حفظ می کنند،'),
(5405, 70, 30, 'مگر در کام جویی از همسران و کنیزانشان که آنان در این زمینه مورد سرزنش نیستند.'),
(5406, 70, 31, 'پس کسانی که در بهره گیری جنسی راهی غیر از این جویند، تجاوزکار از حدود حق اند،'),
(5407, 70, 32, 'و آنان که امانت ها و پیمان های خود را رعایت می کنند،'),
(5408, 70, 33, 'و آنان که بر ادای گواهی های خود پای بند و متعهدند،'),
(5409, 70, 34, 'و آنان که همواره بر [اوقات و شرایط ظاهری و معنوی] نمازهایشان محافظت دارند.'),
(5410, 70, 35, 'اینان در بهشت ها، مکرّم و محترم اند'),
(5411, 70, 36, 'کافران را چه شده که به تو چشم دوخته به سویت شتابانند؟'),
(5412, 70, 37, 'از راست و چپ، گروه گروه،'),
(5413, 70, 38, 'آیا هر یک از آنان طمع دارد که او را در بهشت پرنعمت درآورند؟!'),
(5414, 70, 39, 'این چنین نیست، ما آنان را از آنچه خود می دانند [آبی گندیده و بی مقدار] آفریدیم.'),
(5415, 70, 40, 'به پروردگار مشرق ها و مغرب ها سوگند که ما تواناییم،'),
(5416, 70, 41, 'بر اینکه به جای آنان بهتر از آنان را بیاوریم؛ و هیچ چیز ما را مغلوب نمی کند.'),
(5417, 70, 42, 'پس آنان را واگذار تا [در گفتار باطل] فرو روند و مشغول بازی باشند، تا روزشان را که به آنان وعده داده اند دیدار کنند،'),
(5418, 70, 43, 'روزی که شتابان از خاک بیرون آیند، گویی به سوی نشانه های نصب شده می دوند،'),
(5419, 70, 44, 'درحالی که دیدگانشان [از شدت ترس] فرو افتاده، خواری و ذلت آنان را می پوشاند. این همان روزی است که همواره وعده داده می شدند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به سوی قومش فرستادیم که قوم خود را پیش از آنکه عذابی دردناکبه آنان رسد، بیم ده.'),
(5421, 71, 2, 'گفت: ای قوم من! همانا من شما را بیم دهنده ایآشکارم،'),
(5422, 71, 3, 'که خدا را بپرستید و از او پروا کنید و از من اطاعت نمایید،'),
(5423, 71, 4, 'تا خدا گناهانتان را بیامرزد و شما را تا زمانی معین مهلت دهد. اگر آگاهی داشته باشید [توجه خواهید کرد که] بی تردید هنگامی که اجل خدایی فرا رسد، تأخیر نخواهد داشت.'),
(5424, 71, 5, 'گفت: پروردگارا! همانا قوم خود را شب و روز [به آیین توحید] دعوت کردم،'),
(5425, 71, 6, 'ولی دعوت من جز بر فرارشان نیفزود،'),
(5426, 71, 7, 'و من هرگاه آنان را دعوت کردم تا آنان را بیامرزی، انگشتان خود را در گوش هایشان کردند و جامه هایشان را به سر کشیدند و بر انکار خود پافشاری ورزیدند و به شدت تکبّر کردند،'),
(5427, 71, 8, 'آن گاه آنان را آشکارا دعوت کردم،'),
(5428, 71, 9, 'سپس آشکار و پنهان آنان را خواندم.'),
(5429, 71, 10, 'پس [به آنان] گفتم: از پروردگارتان آمرزش بخواهید که او همواره بسیار آمرزنده است.'),
(5430, 71, 11, 'تا بر شما از آسمان باران پی در پی و با برکت فرستد،'),
(5431, 71, 12, 'و شما را با اموال و فرزندان یاری کند، و برایتان باغ ها و نهرها قرار دهد،'),
(5432, 71, 13, 'شما را چه شده که [ربوبیت خدا را نفی کرده و در نتیجه از بندگی اش دست برداشته و] به عظمت و بزرگی خدا امید ندارید؟'),
(5433, 71, 14, 'در حالی که شما را مرحله به مرحله [خاک، نطفه، علقه، مضغه، گوشت، استخوان و...] آفریده است'),
(5434, 71, 15, 'آیا ندانسته اید که خدا هفت آسمان را چگونه بر فراز یکدیگر آفرید؟'),
(5435, 71, 16, 'و ماه را در میان آنها روشنی بخش، و خورشید را چراغ فروزان قرار داد،'),
(5436, 71, 17, 'و خدا شما را از زمین [مانند] گیاهی رویانید،'),
(5437, 71, 18, 'سپس شما را در آن باز می گرداند و باز به صورتی ویژه بیرون می آورد،'),
(5438, 71, 19, 'و خدا زمین را برای شما فرشی گسترده قرار داد،'),
(5439, 71, 20, 'تا از راه ها و جاده های وسیع آن [هر جا که خواستید] بروید؟'),
(5440, 71, 21, 'نوح گفت: پروردگارا! آنان از من نافرمانی کردند و از کسانی [چون سردمداران کفر و متولیان بتخانه] پیروی نمودند که اموال و فرزندانشان جز خسارت و زیانی بر آنان نیفزود!'),
(5441, 71, 22, 'و [این پیشوایان گمراهی، برای گمراه نگاه داشتن این مردم] نیرنگی بزرگ به کار گرفتند،'),
(5442, 71, 23, 'و گفتند: دست از معبودانتان بر ندارید، و هرگز [بت های] وَدّ و سواع و یغوث و یعوق و نسر را رها نکنید؛'),
(5443, 71, 24, 'همانا بسیاری را گمراه کردند. و [پروردگارا!] ستمکاران را جز گمراهی میفزا.'),
(5444, 71, 25, '[همه آنان] به سبب گناهانشان غرق شدند و بی درنگ در آتشی درآورده شدند که [از آن] در برابر خدا برای خود یاورانی نیافتند،'),
(5445, 71, 26, 'و نوح گفت: پروردگارا! هیچ یک از کافران را بر روی زمین باقی مگذار'),
(5446, 71, 27, 'که اگر آنان را باقی گذاری، بندگانت را گمراه می کنند و جز نسلی بدکار و ناسپاس زاد و ولد نمی کنند.'),
(5447, 71, 28, 'پروردگارا! مرا و پدر و مادرم را و هر کس که با ایمان به خانه ام درآید و همه مردان و زنان با ایمان را بیامرز، و ستمکاران را جز هلاکت میفزای.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: به من وحی شده که گروهی از جنّ [به قرآن] گوش دادند و گفتند: همانا ما قرآن شگفت آوری شنیدیم،'),
(5449, 72, 2, 'که به سوی راه راست هدایت می کند، پس به آن ایمان آوردیم و هرگز احدی را شریک پروردگارمان قرار نخواهیم داد،'),
(5450, 72, 3, 'و [این حقایق را اعتراف و اقرار می کنیم:] اینکه برتر و بلند است عظمت پروردگارمان، و او هرگز برای خود همسری و فرزندی نگرفته است،'),
(5451, 72, 4, 'و اینکه سبک مغز و نادان ما همواره درباره خدا سخنی دور از حق می گفت [که او دارای همسر و فرزند است!]'),
(5452, 72, 5, 'و اینکه گمان می کردیم، هرگز انس و جن بر خدا دروغ نمی بندند،'),
(5453, 72, 6, 'و اینکه مردانی از آدمیان به مردانی از جن پناه می بردند، پس [مردان جن] بر طغیان و تباهی آنان افزودند،'),
(5454, 72, 7, 'و مردانی از انس پنداشتند چنان که شما [جنیان] پنداشتید که خدا هرگز کسی را [به نبوّت] برنمی انگیزد!'),
(5455, 72, 8, 'و اینکه [برای کسب خبر] به آسمان نزدیک شدیم، پس آن را پر از نگهبانان نیرومند و تیرهای شهاب یافتیم،'),
(5456, 72, 9, 'و اینکه ما در آسمان برای شنیدن [خبرهای ملکوتی و سخنان فرشتگان] در نشست گاه هایی می نشستیم، ولی اکنون هر که بخواهد استراق سمع کند، شهابی را در کمین خود می یابد!'),
(5457, 72, 10, 'و اینکه ما نمی دانیم آیا درباره کسانی که در زمین هستند گزند و شری خواسته شده یا پروردگارشان برای آنان هدایت خواسته است؟'),
(5458, 72, 11, 'واینکه از ما برخی شایسته وبرخی غیر شایسته اند، [و] ما بر مذاهب گوناگون ومتفاوتی هستیم،'),
(5459, 72, 12, 'و اینکه ما یقین داریم که هرگز نمی توانیم خدا را در زمین عاجز و درمانده کنیم و هرگز نمی توانیم [با گریختن و فرار] از دسترس قدرت او بیرون رویم،'),
(5460, 72, 13, 'و اینکه چون هدایت قرآن را شنیدیم به آن ایمان آوردیم؛ زیرا کسی که به پروردگارش ایمان آورد نه از کاسته شدن ثواب می ترسد و نه از اینکه عذاب و سختی و مشقت و رنج او را احاطه کند،'),
(5461, 72, 14, 'و اینکه گروهی از ما تسلیم [فرمان ها و احکام] خدایند، و برخی منحرف اند، پس کسانی که تسلیم شدند در صدد یافتن حقیقت و راه مستقیم اند،'),
(5462, 72, 15, 'ولی منحرفان هیزم دوزخ اند،'),
(5463, 72, 16, 'و اگر [انس و جن] بر طریقه حق پایداری کنند حتماً آنان را از آب فراوانی سیراب خواهیم کرد،'),
(5464, 72, 17, 'تا آنان را در زمینه فراوانی نعمت بیازماییم و کسی که از یاد پروردگارش روی برگرداند، او را در عذابی بسیار سخت و روز افزون درخواهد آورد،'),
(5465, 72, 18, 'و مساجد ویژه خداست، پس هیچ کس را با خدا مپرستید،'),
(5466, 72, 19, 'و اینکه هنگامی که بنده خدا [محمّد] برمی خاست تا خدا را عبادت کند [از جنیان به اندازه ای به سویش هجوم می بردند که] نزدیک بود جمعیت انبوه و متراکمی بر سر او بریزد،'),
(5467, 72, 20, 'بگو: من فقط پروردگارم را می پرستم، و هیچ کس را شریک او قرار نمی دهم.'),
(5468, 72, 21, 'بگو: من اختیار زیان و هدایتی را برای شما ندارم.'),
(5469, 72, 22, 'بگو: [اگر از او نافرمانی کنم] هرگز کسی در برابر [عذاب] خدا مرا پناه نمی دهد، و هرگز پناهگاهی غیر او نمی یابم.'),
(5470, 72, 23, '[وظیفه من] فقط رساندن [برنامه های سعادت بخش] از سوی خدا و [رساندن] پیام های اوست. و آنان که از خدا و پیامبرش نافرمانی کنند، مسلماً آتش دوزخ برای آنان است و در آن جاودانه و همیشگی اند.'),
(5471, 72, 24, '[اینان همواره شما را ضعیف و ناتوان و بی یار می شمارند] تا زمانی که آنچه را [از عذاب] وعده داده می شوند ببینند، پس به زودی آگاه می شوند چه کسی یاورش ناتوان تر و نفراتش کمتر است؟'),
(5472, 72, 25, 'بگو: نمی دانم آیا آنچه را وعده داده می شوید نزدیک است یا پروردگارم برای آن زمانی طولانی قرار خواهد داد؟'),
(5473, 72, 26, '[او] دانای غیب است و هیچ کس را بر غیب خود آگاه نمی کند؛'),
(5474, 72, 27, 'مگر پیامبرانی را که [برای آگاه شدن از غیب] برگزیده است، پس نگهبانانی [برای محافظت از آنان] از پیش رو و پشت سرشان می گمارد'),
(5475, 72, 28, 'تا مشخص کند که پیام های پروردگارشان را [به طور کامل به مردم] رسانده اند و او به آنچه نزد آنان است احاطه دارد، و همه چیز را از جهت عدد، شماره و احصا کرده است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ای جامه بر خود پیچیده!'),
(5477, 73, 2, 'شب را جز اندکی [که ویژه استراحت است، برای عبادت] برخیز؛'),
(5478, 73, 3, 'نصف [همه ساعات] شب [را به عبادت اختصاص ده] یا اندکی ازنصف بکاه،'),
(5479, 73, 4, 'یا [مقداری] بر نصف بیفزا، و قرآن را شمرده و روشن و با تأمل و دقت بخوان.'),
(5480, 73, 5, 'به یقین ما به زودی گفتاری سنگین [چون آیات قرآن] به تو القا خواهیمکرد.'),
(5481, 73, 6, 'بی تردید عبادت [ی که در دل] شب [انجام می گیرد] محکم تر و پایدارتر و گفتار در آن استوارتر و درست تر است.'),
(5482, 73, 7, 'تو را در روز [برای مشاغل فراوان معنوی و هدایت مردم و حل مشکلات نیازمندان،] رفت و آمدی طولانی است [پس ساعات شب برای عبادت فرصتی بهتر است.]'),
(5483, 73, 8, 'نام پروردگارت را [به زبان حال و قال] یاد کن [و از غیر او قطع امید نما] و فقط دل بر او بند.'),
(5484, 73, 9, '[همان که] پروردگار مشرق و مغرب [است]، هیچ معبودی جز او نیست، پس او را وکیل و کارساز خود انتخاب کن،'),
(5485, 73, 10, 'و بر گفتار [و آزار] مشرکان شکیبا باش و از آنان به شیوه ای پسندیده دوری کن،'),
(5486, 73, 11, 'و مرا با تکذیب کنندگان توانگر و نازپرورده واگذار و آنان را اندکی مهلت ده،'),
(5487, 73, 12, 'که حتماً [برای آنان] نزد ما [به کیفر اعمالشان] زنجیرهایی گران و آتشی به شدت شعلهور است،'),
(5488, 73, 13, 'و غذایی گلوگیر و عذابی دردناک است،'),
(5489, 73, 14, 'در روزی که زمین و کوه ها به لرزه درآیند، و کوه ها به صورت توده ای شن روان گردند!'),
(5490, 73, 15, 'ما پیامبری که گواه بر [اعمال] شماست، به سویتان فرستادیم، همان گونه که به سوی فرعون، رسولی فرستادیم؛'),
(5491, 73, 16, 'پس فرعون از آن رسول نافرمانی کرد، پس او را به عذابی سخت گرفتیم.'),
(5492, 73, 17, 'اگر امروز کفر بورزید، پس چگونه خود را [از عذاب] روزی که کودکان را پیر می کند، حفظ می نمایید؟!'),
(5493, 73, 18, 'آسمان به سبب شدت و کوبندگی آن روز، درهم شکافته می شود و قطعاً وعده خدا شدنی است.'),
(5494, 73, 19, 'بی تردید این [قرآن] مایه تذکر و پند است، پس هر کس بخواهد راهی به سوی پروردگارش [باتکیه براین قرآن] برگزیند.'),
(5495, 73, 20, 'پروردگارت آگاه است که تو و گروهی از کسانی که با تواند، نزدیک به دو سوم شب و گاهی نیمی از آن و زمانی یک سومش را [برای عبادت و خواندن قرآن] برمی خیزد، و خدا شب و روز را [دقیق و منظم] اندازه گیری می کند، و برای او مشخص است که شما هرگز نمی توانید [به علت کوتاهی و بلندی شب در طول سال، دو سوم و نصف و یک سوم را دقیقاً] اندازه گیری کنید، پس [اندازه گیری دقیق را] بر شما بخشید؛ بنابراین آنچه را از قرآن برای شما میسر است بخوانید. او می داند که به زودی برخی از شما بیمار می شوند، و گروهی برای به دست آوردن رزق و روزی خدا در زمین سفر می کنند، و بعضی در راه خدا می جنگند؛ پس آنچه را از آن میسر است بخوانید و نماز را برپا دارید و زکات بپردازید و وام نیکو به خدا بدهید؛ و آنچه را از عمل خیر برای خود پیش می فرستید، آن را نزد خدا به بهترین صورت و بزرگ ترین پاداش خواهید یافت؛ و از خدا آمرزش بخواهید که خدا بسیار آمرزنده و مهربان است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ای جامه برخود پوشیده!'),
(5497, 74, 2, 'برخیز و بیم ده،'),
(5498, 74, 3, 'و پروردگارت را بزرگ دار،'),
(5499, 74, 4, 'و جامه ات را پاک کن،'),
(5500, 74, 5, 'و از پلیدی دور شو'),
(5501, 74, 6, 'و [احسانت را بر دیگران] در حالی که [آن را] بزرگ و فراوان بینی، منت مگذار،'),
(5502, 74, 7, 'و [بر آزار دشمنان] برای پروردگارت شکیبایی ورز.'),
(5503, 74, 8, 'پس زمانی که در صور دمیده شود،'),
(5504, 74, 9, 'آن روز، روز بسیار سختی است،'),
(5505, 74, 10, 'بر کافران آسان نیست،'),
(5506, 74, 11, 'مرا با کسی که او را به تنهایی آفریدم واگذار،'),
(5507, 74, 12, 'و برایش ثروت گسترده و فراوان قرار دادم،'),
(5508, 74, 13, 'و فرزندانی که نزدش حاضرند'),
(5509, 74, 14, 'و نعمت ها [ی مادی و وسایل زندگی] را به طور کامل برای او فراهم آوردم،'),
(5510, 74, 15, 'باز هم طمع دارد که بیفزایم.'),
(5511, 74, 16, 'این چنین نیست؛ زیرا او همواره به آیات ما دشمنی می ورزد،'),
(5512, 74, 17, 'به زودی او را به بالا رفتن از گردنه عذابی بسیار سخت مجبور می کنم.'),
(5513, 74, 18, 'او [برای باطل کردن دعوت قرآن] اندیشید و [آن را] سنجید؛'),
(5514, 74, 19, 'پس مرگ بر او باد، چگونه سنجید؟'),
(5515, 74, 20, 'باز هم مرگ بر او باد، چگونه سنجید؟'),
(5516, 74, 21, 'آن گاه نظر کرد،'),
(5517, 74, 22, 'سپس چهره درهم کشید و قیافه نفرت آمیز به خود گرفت،'),
(5518, 74, 23, 'آن گاه [از قرآن] روی گردانید و تکبّر ورزید؛'),
(5519, 74, 24, 'پس گفت: این قرآن جز جادویی که از جادوگران پیشین حکایت شده چیزی نیست،'),
(5520, 74, 25, 'این جز سخن بشر نمی باشد.'),
(5521, 74, 26, 'به زودی او را در سقر درآورم'),
(5522, 74, 27, 'و تو چه می دانی سقر چیست؟'),
(5523, 74, 28, '[آتشی است که] نه چیزی را باقی می گذارد و نه رها می کند؛'),
(5524, 74, 29, 'پوست بدن را [همه جانبه] دگرگون می کند؛'),
(5525, 74, 30, 'بر آن نوزده نفر [موکّل] گمارده اند'),
(5526, 74, 31, 'و ما موکّلان آتش را جز فرشتگان قرار نداده ایم، و شماره آنان را جز آزمایشی برای کافران مقرّر نکرده ایم تا اهل کتاب [به خاطر هماهنگی قرآن با کتابشان] یقین پیدا کنند و بر ایمان مؤمنان بیفزاید، و اهل کتاب و مؤمنان [در حقّانیّت قرآن] تردید نکنند، و تا بیماردلان و کافران بگویند: خدا از توصیف گماشتگان آتش به این [نوزده نفر] چه هدفی دارد؟ این گونه خدا هر که را بخواهد [پس از بیان حقیقت] گمراه می کند و هر که را بخواهد هدایت می نماید، و سپاهیان پروردگارت را جز خود او کسی نمی داند، و این قرآن فقط مایه تذکر و پند برای بشر است.'),
(5527, 74, 32, 'این چنین نیست [که کافران می پندارند که این حقایق افسانه است،] سوگند به ماه'),
(5528, 74, 33, '[و] سوگند به شب هنگامی که پشت می کند'),
(5529, 74, 34, 'و سوگند به صبح زمانی که از پرده سیاه شب درآید و رخ نماید'),
(5530, 74, 35, 'که بی تردید این [قرآن] از بزرگ ترین پدیده هاست؛'),
(5531, 74, 36, 'هشدار دهنده به بشر است،'),
(5532, 74, 37, 'برای هر کس از شما که بخواهد [به وسیله ایمان، طاعت و انجام کار خیر] پیشی جوید یا [به سبب کفر، طغیان و گناه] پس ماند؛'),
(5533, 74, 38, 'هر کسی در گرو دست آورده های خویش است،'),
(5534, 74, 39, 'مگر سعادتمندان'),
(5535, 74, 40, 'که در بهشت ها از یکدیگر می پرسند'),
(5536, 74, 41, 'از روز و حال گنهکاران،'),
(5537, 74, 42, '[به آنان رو کرده] می گویند: چه چیز شما را به دوزخ وارد کرد؟'),
(5538, 74, 43, 'می گویند: ما از نمازگزاران نبودیم'),
(5539, 74, 44, 'و به تهیدستان و نیازمندان طعام نمی دادیم'),
(5540, 74, 45, 'و با فرورفتگان [در گفتار و کردار باطل] فرومی رفتیم،'),
(5541, 74, 46, 'و همواره روز جزا را انکار می کردیم'),
(5542, 74, 47, 'تا آنکه مرگ ما فرا رسید.'),
(5543, 74, 48, 'پس آنان را شفاعت شفیعان سودی نمی دهد؛'),
(5544, 74, 49, 'آنان را چه شده که از تذکر و هشدار روی گردانند؟'),
(5545, 74, 50, 'گویی گورخرانی وحشی و رمیده اند'),
(5546, 74, 51, 'که از [چنگال] شیری گریخته اند؛'),
(5547, 74, 52, 'بلکه هر کدام از آنان توقع دارد نامه هایی سرگشاده [از سوی خدا] دریافت دارد!'),
(5548, 74, 53, 'این چنین نیست [که علت فرارشان از دین برای دریافت نکردن نامه باشد] بلکه [فرارشان برای این است که] از آخرت نمی ترسند؛'),
(5549, 74, 54, 'این چنین نیست [که نامه سرگشاده ای از سوی ما به آنان رسد]، بلکه بی تردید این قرآن تذکر و هشداری است [که برای جهانیان کافی است.]'),
(5550, 74, 55, 'پس هرکه بخواهد از آن پند گیرد،'),
(5551, 74, 56, 'و از آن پند نمی گیرند مگر آنکه خدا بخواهد، او سزاوار است که از وی پروا کنند، و سزاوار است که به آمرزشش امید بندند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'به روز قیامت سوگند می خورم،'),
(5553, 75, 2, 'و به نفس سرزنش گر قسم می خورم.'),
(5554, 75, 3, 'آیا انسان گمان می کند که ما هرگز استخوان هایش را جمع نخواهیم کرد؟'),
(5555, 75, 4, 'چرا در حالی که تواناییم که [خطوط] سر انگشتانش را درست و نیکو بازسازی کنیم،'),
(5556, 75, 5, '[نه اینکه به گمان او قیامتی در کار نباشد] بلکه انسان می خواهد [با دست و پا زدن در شک و تردید] فرارویش را [از اعتقاد به قیامت که بازدارنده ای قوی است] باز کند [تا برای ارتکاب هر گناهی آزاد باشد!]'),
(5557, 75, 6, '[با حالتی آمیخته با تردید] می پرسد: روز قیامت چه وقت است؟'),
(5558, 75, 7, 'پس هنگامی [است] که چشم [از سختی و هولناکی آن] خیره شود،'),
(5559, 75, 8, 'و ماه تاریک و بی نور گردد،'),
(5560, 75, 9, 'و خورشید و ماه به هم جمع شوند.'),
(5561, 75, 10, 'آن روز انسان گوید: گریزگاه کجاست؟'),
(5562, 75, 11, 'این چنین نیست، هرگز پناهگاهی وجود ندارد.'),
(5563, 75, 12, 'آن روزقرارگاه [نهایی] فقط به سوی پروردگار توست.'),
(5564, 75, 13, 'آن روز است که انسان را به اعمالی که از دیرباز یا پس از آن انجام داده، آگاه می کنند.'),
(5565, 75, 14, 'بلکه انسان خود به وضع خویش بیناست.'),
(5566, 75, 15, 'و هر چند [برای توجیه گناهانش] بهانه ها بتراشد'),
(5567, 75, 16, '[پیش از پایان یافتن وحی به وسیله جبرئیل] زبانت را به حرکت در نیاور تا در خواندن آن شتاب ورزی.'),
(5568, 75, 17, 'بی تردید گردآوردن و [به هم پیوند دادن آیات که بر تو وحی می شود و چگونگی] قرائتش بر عهده ماست،'),
(5569, 75, 18, 'پس هنگامی که آن را [به طور کامل] خواندیم، [به همان صورت] خواندنش را دنبال کن.'),
(5570, 75, 19, 'سپس توضیح و بیانش نیز بر عهده ماست.'),
(5571, 75, 20, '[اینکه می پندارید قیامتی در کار نیست] این چنین نیست، بلکه شما عاشق ایندنیای زودگذر هستید،'),
(5572, 75, 21, 'و همواره آخرت را [برای این عشق بی پایه] رها می کنید.'),
(5573, 75, 22, 'در آن روز چهره هایی شاداب است؛'),
(5574, 75, 23, '[با دیده دل] به پروردگارش نظر می کند'),
(5575, 75, 24, 'و چهره هایی عبوس و درهم کشیده است؛'),
(5576, 75, 25, 'چون یقین دارند که در معرض عذابی کمرشکن قرار خواهند گرفت.'),
(5577, 75, 26, 'این چنین نیست [که می پندارد]، هنگامی که جان به گلوگاه رسد،'),
(5578, 75, 27, 'و [کسان بیمار] گویند: درمان کننده این بیمار کیست؟'),
(5579, 75, 28, 'و [بیمار] یقین می کند [که با رسیدن جان به گلوگاه] زمان جدایی [از دنیا، ثروت، زن و فرزند] فرا رسیده است!'),
(5580, 75, 29, 'و [از سختی جان کندن] ساق به ساق به هم پیچد؛'),
(5581, 75, 30, 'آن روز، روز سوق و مسیر به سوی پروردگار توست.'),
(5582, 75, 31, '[در آن حال فرشتگان می گویند: این به کام مرگ افتاده] نه [دعوت پیامبر را] باور کرد، و نه نماز خواند؛'),
(5583, 75, 32, 'بلکه [در میان اجتماعات] تکذیب کرد و روی گرداند؛'),
(5584, 75, 33, 'سپس متکبرانه و خرامان به سوی کسانش رفت.'),
(5585, 75, 34, '[و گویند: با این وضعی که داری، عذاب دوزخ] برای تو شایسته تر است، شایسته تر!'),
(5586, 75, 35, 'باز هم شایسته تر است شایسته تر.'),
(5587, 75, 36, 'آیا انسان گمان می کند بیهوده و مهمل [و بدون تکلیف و مسؤولیت] رها می شود؟!'),
(5588, 75, 37, 'آیا نطفه ای از منی که در رحم ریخته می شود نبود؟'),
(5589, 75, 38, 'سپس علقه شد و خدا او را آفرید و اندامش را درست و نیکو ساخت،'),
(5590, 75, 39, 'و از او دو زوج به وجود آورد یکی نر و دیگر ماده،'),
(5591, 75, 40, 'آیا چنین نیرومند آگاهی توانا نیست که مردگان را زنده کند؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آیا بر انسان زمانی از روزگار گذشت که چیزی در خور ذکر نبود؟'),
(5593, 76, 2, 'ما انسان را از نطفه آمیخته و مختلطی [از مواد و عناصر] آفریدیم و او را از حالتی به حالتی و شکلی به شکلی [از نطفه به علقه، از علقه به مضغه، از مضغه به استخوان تا طفلی کامل] درآوردیم، پس او را شنوا و بینا قرار دادیم.'),
(5594, 76, 3, 'ما راه را به او نشان دادیم یا سپاس گزار خواهد بود یا ناسپاس.'),
(5595, 76, 4, 'ما برای کافران زنجیرها و بندها و آتش فروزان آماده کرده ایم.'),
(5596, 76, 5, 'همانا نیکان همواره از جامی می نوشند که نوشیدنی اش آمیخته به کافور [آن ماده سرد، سپید و معطر] است.'),
(5597, 76, 6, 'آن جام از چشمه ای است که همواره بندگان خدا از آن می نوشند و آن را به دلخواهشان هرگونه که بخواهند جاری می نمایند.'),
(5598, 76, 7, '[همانان که] همواره نذرشان را وفا می کنند، و از روزی که آسیب و گزندش گسترده است، می ترسند،'),
(5599, 76, 8, 'و غذا را در عین دوست داشتنش، به مسکین و یتیم و اسیر انفاق می کنند.'),
(5600, 76, 9, '[و می گویند:] ما شما را فقط برای خشنودی خدا اطعام می کنیم و انتظار هیچ پاداش و سپاسی را از شما نداریم.'),
(5601, 76, 10, 'ما از پروردگارمان در روزی که روز عبوس و بسیار هولناکی است می ترسیم.'),
(5602, 76, 11, 'پس خدا نگه دار آنان از آسیب و گزند آن روز است و شادابی و شادمانی به آنان عطا می کند.'),
(5603, 76, 12, 'و آنان را برای اینکه [در برابر تکالیف دینی و حوادث روزگار] شکیبایی ورزیدند، بهشتی [عنبرسرشت] و لباسی ابریشمین پاداش می دهد.'),
(5604, 76, 13, 'در آنجا بر تخت ها تکیه می زنند، در حالی که آفتابی [که از گرمایش ناراحت شوند] و سرمایی [که از سختی اش به زحمت افتند] نمی بینند،'),
(5605, 76, 14, 'و سایه های درختان به آنان نزدیک و میوه هایش به آسانی در دسترس است،'),
(5606, 76, 15, 'و ظرف هایی از نقره فام، و قدح هایی بلورین [که پر از غذا و نوشیدنی است] پیرامون آنان می گردانند؛'),
(5607, 76, 16, 'جام هایی از نقره که آنها را به اندازه مناسب اندازه گیری کرده اند،'),
(5608, 76, 17, 'و در آنجا جامی که نوشیدنی اش آمیخته به زنجبیل است، به آنان می نوشانند،'),
(5609, 76, 18, 'از چشمه ای در آنجا که «سلسبیل» نامیده شود،'),
(5610, 76, 19, 'و پیرامونشان نوجوانانی جاودانی [برای پذیرایی از آنان] می گردند، که چون آنان را ببینی پنداری مرواریدی پراکنده اند،'),
(5611, 76, 20, 'و چون آنجا را ببینی، نعمتی فراوان و کشوری بزرگ بینی،'),
(5612, 76, 21, 'بر اندامشان جامه هایی از حریر نازک و سبز رنگ و دیبای ستبر است و با دستبندهایی از نقره آراسته شده اند، و پروردگارشان باده طهور به آنان می نوشاند.'),
(5613, 76, 22, '[به آنان گوید:] یقیناً این [نعمت ها] پاداش شماست و تلاشتان مقبول افتاده است؛'),
(5614, 76, 23, 'به راستی ما قرآن را به تدریج بر تو نازل کردیم؛'),
(5615, 76, 24, 'پس در برابر فرمان پروردگارت شکیبا باش، و از هیچ گنهکار یا کافری از آنان اطاعت مکن،'),
(5616, 76, 25, 'و صبح و شام نام پروردگارت را یاد کن.'),
(5617, 76, 26, 'و پاره ای از شب را سر به سجده گذار، و او را در شب طولانی تسبیح گوی.'),
(5618, 76, 27, 'بی تردید اینان [که گنهکار و ناسپاس اند] دنیای زودگذر را دوست دارند [و فقط برای به دست آوردن آن می کوشند] و روز هولناک و سختی را که پیش رو دارند وامی گذارند.'),
(5619, 76, 28, 'ما آنان را آفریدیم و مفاصلشان را [با رشته های اعصاب و عضلات] محکم و استوار کردیم، و هرگاه بخواهیم [آنان را نابود می کنیم و] امثال و نظایرشان را [که مطیع و فرمانبردارند] به جای آنان قرار می دهیم.'),
(5620, 76, 29, 'همانا این [آیات قرآن] تذکر و هشداری است؛ پس هر کس بخواهد راهی به سوی پروردگارش [با تکیه بر این آیات] برگزیند،'),
(5621, 76, 30, 'و تا خدا نخواهد، نخواهید خواست؛ یقیناً خدا همواره دانا و حکیم است.'),
(5622, 76, 31, 'هر کس را بخواهد در رحمتش درآورد و برای ستمکاران عذابی دردناک آماده کرده است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به آن فرشتگانی که پی در پی فرستاده می شوند،'),
(5624, 77, 2, 'و سوگند به آن فرشتگانی که [برای آوردن وحی در سرعت حرکت] چون تندبادند.'),
(5625, 77, 3, 'و سوگند به آن فرشتگانی که گشاینده صحیفه های وحی اند،'),
(5626, 77, 4, 'و سوگند به آن فرشتگانی که جدا کننده حق از باطل اند،'),
(5627, 77, 5, 'و سوگند به آن فرشتگانی که القاکننده آیات آسمانی به پیامبران [اند،]'),
(5628, 77, 6, 'تا حجت [باشد برای اهل ایمان] و بیم و هشدار باشد [برای کافران]'),
(5629, 77, 7, '[به همه این حقایق سوگند] که آنچه [به عنوان روز قیامت] وعده داده می شوید بی تردید واقع شدنی است.'),
(5630, 77, 8, 'در آن زمان که ستارگان محو و تاریک شوند،'),
(5631, 77, 9, 'و آن زمانی که آسمان بشکافد.'),
(5632, 77, 10, 'و آن زمان که کوه ها از بیخ و بن کنده شوند.'),
(5633, 77, 11, 'و آن زمان که وقت حضور پیامبران [برای گواهی بر امت ها] معین شود.'),
(5634, 77, 12, 'این امور برای چه روزی به تأخیر افتاده؟'),
(5635, 77, 13, 'برای روز داوری'),
(5636, 77, 14, 'و تو چه می دانی روز داوری چیست؟'),
(5637, 77, 15, 'وای در آن روز بر تکذیب کنندگان؛'),
(5638, 77, 16, 'آیا پیشینیان را [به سبب تکذیبشان] هلاک نکردیم؟'),
(5639, 77, 17, 'سپس به دنبال آنان دیگران را هم [به سبب تکذیبشان] هلاک می کنیم.'),
(5640, 77, 18, 'با گنهکاران این گونه رفتار می کنیم.'),
(5641, 77, 19, 'وای در آن روز بر تکذیب کنندگان!'),
(5642, 77, 20, 'آیا شما را از آبی پست و بی مقدار نیافریدیم؟'),
(5643, 77, 21, 'پس آن را در جایگاهی استوار قرار دادیم'),
(5644, 77, 22, 'تا زمانی معین؛'),
(5645, 77, 23, 'پس توانا بودیم و چه نیکو تواناییم.'),
(5646, 77, 24, 'وای در آن روز بر تکذیب کنندگان!'),
(5647, 77, 25, 'آیا زمین را فراهم آورنده [انسان ها] قرار ندادیم؟'),
(5648, 77, 26, 'هم در حال حیاتشان و هم زمان مرگشان'),
(5649, 77, 27, 'و کوه های استوار و بلند در آن قرار دادیم و شما را آبی گوارا نوشاندیم.'),
(5650, 77, 28, 'وای در آن روز بر تکذیب کنندگان!'),
(5651, 77, 29, '[آن روز به آنان گویند:] به سوی آتشی که همواره آن را تکذیب می کردید، بروید؛'),
(5652, 77, 30, 'و [نیز] به سوی سایه ای [از دود متراکم و آتش زا] که دارای سه شاخه است، بروید.'),
(5653, 77, 31, '[سایه ای که] نه مانع از حرارت است، و نه از شعله های آتش جلوگیری می کند.'),
(5654, 77, 32, 'آن آتش، شراره هایی چون ساختمان بلند پرتاب می کند.'),
(5655, 77, 33, 'گویی آن شراره ها هم چون شتران زرد رنگ هستند.'),
(5656, 77, 34, 'وای در آن روز بر تکذیب کنندگان!'),
(5657, 77, 35, 'این روزی است که [انسان ها چون موقعیتی نمی بینند برای دفاع از خود] سخن نمی گویند،'),
(5658, 77, 36, 'و به آنان اجازه داده نمی شود که عذرخواهی کنند.'),
(5659, 77, 37, 'وای در آن روز بر تکذیب کنندگان!'),
(5660, 77, 38, 'امروز همان روز داوری است که شما و پیشینیان را در آن جمع کرده ایم.'),
(5661, 77, 39, 'پس اگر [برای فرار از عذاب] چاره و تدبیری دارید، آن را به کار گیرید.'),
(5662, 77, 40, 'وای در آن روز بر تکذیب کنندگان!'),
(5663, 77, 41, 'به یقین پرهیزکاران در زیر سایه ها و کنار چشمه سارهایند،'),
(5664, 77, 42, 'و نزد میوه هایی از آنچه همواره بخواهند.'),
(5665, 77, 43, '[به آنان گویند:] به پاداش اعمالی که همواره انجام می دادید، بخورید و بیاشامید گوارایتان باد.'),
(5666, 77, 44, 'ما نیکوکاران را این گونه پاداش می دهیم.'),
(5667, 77, 45, 'وای در آن روز بر تکذیب کنندگان!'),
(5668, 77, 46, '[شما ای کافران و مشرکان! در این دنیا] بخورید و اندک زمانی برخوردار شوید که شما گنهکارید [و بی تردید به کیفر اعمالتان گرفتار خواهید شد.]'),
(5669, 77, 47, 'وای در آن روز بر تکذیب کنندگان!'),
(5670, 77, 48, 'و هنگامی که به آنان گویند: [در برابر خدا] رکوع کنید، رکوع نمی کنند.'),
(5671, 77, 49, 'وای در آن روز بر تکذیب کنندگان!'),
(5672, 77, 50, '[اگر به قرآن ایمان نیاورند] پس به کدام سخن بعد از آن ایمان می آورند؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'درباره چه چیز از یکدیگر می پرسند؟'),
(5674, 78, 2, 'از آن خبر بزرگ [قیامت کبری]'),
(5675, 78, 3, 'که همواره درباره آن با یکدیگر اختلاف دارند [که واقع می شود یا نه؟]'),
(5676, 78, 4, 'نه چنین است [که می پندارند] به زودی [به حتمی بودن وقوع آن] آگاه خواهند شد.'),
(5677, 78, 5, 'باز هم نه چنین است [که می پندارند] به زودی [به حتمی بودن وقوع آن] آگاه خواهند شد.'),
(5678, 78, 6, 'آیا زمین را بستر آرامش قرار ندادیم؟'),
(5679, 78, 7, 'و کوه ها را میخ هایی [برای استواری آن؟]'),
(5680, 78, 8, 'و شما را جفت هایی [به صورت نر و ماده] آفریدیم،'),
(5681, 78, 9, 'و خوابتان را مایه استراحت و آرامش [و تمدّد اعصاب] قرار دادیم،'),
(5682, 78, 10, 'و شب راپوششی'),
(5683, 78, 11, 'و روز را وسیله معاش مقرّر کردیم؛'),
(5684, 78, 12, 'و بر فرازتان هفت آسماناستوار بنا نهادیم،'),
(5685, 78, 13, 'و چراغی روشن و حرارت زا پدید آوردیم،'),
(5686, 78, 14, 'و از ابرهای متراکم و باران زا آبی ریزان نازل کردیم'),
(5687, 78, 15, 'تا به وسیله آن دانه و گیاه برویانیم،'),
(5688, 78, 16, 'و باغ هایی از درختان به هم پیچیده و انبوه بیرون آوریم.'),
(5689, 78, 17, 'بی تردید روز داوری وعده گاه است.'),
(5690, 78, 18, 'روزی که در صور می دمند و شما گروه گروه به عرصه محشر می آیید،'),
(5691, 78, 19, 'و آسمان گشوده می شود، پس به صورت درهایی درمی آید.'),
(5692, 78, 20, 'و کوه ها را [از جای خود] روان کنند و سرابی شوند!'),
(5693, 78, 21, 'بی تردید دوزخ کمین گاه است.'),
(5694, 78, 22, 'جایگاه بازگشت برای سرکشان و طاغیان است.'),
(5695, 78, 23, 'روزگاری دراز در آن بمانند.'),
(5696, 78, 24, 'در آنجا نه [آب] خنکی می چشند و نه آشامیدنی [باب طبع]'),
(5697, 78, 25, 'مگر آب جوشان و چرکاب و خونابه ای [از بدن دوزخیان]'),
(5698, 78, 26, 'پاداشی است مناسب [اعمالشان.]'),
(5699, 78, 27, 'اینان بودند که به [روز] حساب امیدی نداشتند،'),
(5700, 78, 28, 'و آیات ما را به شدت و با همه وجود انکار می کردند'),
(5701, 78, 29, 'و [ما] همه چیز را [از خوبی و بدی آنان] برشمرده و در نامه اعمالشان ثبت کرده ایم.'),
(5702, 78, 30, '[در قیامت به آنان می گوییم:] پس بچشید که هرگز جز عذاب بر شما نیفزاییم.'),
(5703, 78, 31, 'بی تردید برای پرهیزکاران پیروزی و کامیابی است.'),
(5704, 78, 32, 'باغ هایی [سرسبز و خرم] و انواع انگورها'),
(5705, 78, 33, 'و دخترانی نو رسیده و هم سن'),
(5706, 78, 34, 'و جام هایی لبریز [از باده طهور]'),
(5707, 78, 35, 'در آنجا نه سخن بیهوده ای می شنوند و نه دروغ و تکذیبی.'),
(5708, 78, 36, '[این] پاداشی کافی و به اندازه از سوی پروردگار توست.'),
(5709, 78, 37, 'همان پروردگار آسمان ها و زمین و آنچه میان آنهاست، آن مهربانی که هیچ کس را اختیار چون و چرای با او نیست.'),
(5710, 78, 38, 'روزی که روح و فرشتگان در یک صف می ایستند و سخن نمی گویند مگر کسی که [خدای] رحمان به او اجازه دهد و سخن حق و درست گوید.'),
(5711, 78, 39, 'آن [روز] روز حق است، پس هرکه بخواهد راه بازگشتی به سوی پروردگارش برگزیند؛'),
(5712, 78, 40, 'ما شما را از عذابی نزدیک هشدار دادیم، روزی که آدم آنچه را [از خیر و شر] پیش فرستاده است بنگرد و کافر گوید: ای کاش من خاک بودم [و موجودی مکلّف آفریده نمی شدم تا چنین روز سختی را ببینم!]');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به فرشتگانی که [روح بدکاران را به شدت از بدن هایشان] بر می کنند،'),
(5714, 79, 2, 'و سوگند به فرشتگانی که [روح نیکوکاران را به نرمی و ملایمت از بدن هایشان] بیرون می آورند؛'),
(5715, 79, 3, 'و سوگند به فرشتگانی که [برای اجرای فرمان های حق] به سرعت نازل می شوند،'),
(5716, 79, 4, 'و سوگند به فرشتگانی که [در ایمان، عبادت، پرستش و اطاعت] بر یکدیگر [به صورتی ویژه] سبقت می گیرند،'),
(5717, 79, 5, 'و سوگند به فرشتگانی که [به اذن خدا امور آفرینش را] تدبیر می کنند؛'),
(5718, 79, 6, '[که همه برای رسیدن به پاداش و کیفر برانگیخته می شوند در] روزی که لرزاننده همه چیز را به شدت بلرزاند؛'),
(5719, 79, 7, 'در حالی که لرزاننده دیگری از پی آن در آید.'),
(5720, 79, 8, 'دل هایی در آن روز مضطرب و هراسان اند'),
(5721, 79, 9, 'دیدگانشان [از ترس و هول قیامت] فرو افتاده است.'),
(5722, 79, 10, 'می گویند: آیا ما [پس از مرگ] به همان حالت اول [که در دنیا بودیم] باز گردانده می شویم؟'),
(5723, 79, 11, '[شگفتا!] آیا زمانی که ما استخوان های پوسیده و ریز ریز شدیم [بازمی گردیم؟!]'),
(5724, 79, 12, '[و] گویند: [اگر قیامتی در کار باشد] بازگشت به حیات دوباره در آن زمان بازگشتی زیانبار است.'),
(5725, 79, 13, 'جز این نیست آن بازگشت فقط با یک بانگ عظیم است [و بس]'),
(5726, 79, 14, 'که ناگاه همه بر یک زمین صاف و هموار حاضر شوند.'),
(5727, 79, 15, 'آیا سرگذشت موسی به تو رسیده است؟'),
(5728, 79, 16, 'هنگامی که پروردگارش او را در وادی مقدس طوی ندا داد'),
(5729, 79, 17, 'که به سوی فرعون برو؛ زیرا که طغیان کرده است.'),
(5730, 79, 18, 'پس به او بگو: می خواهی [از آلودگی شرک و طغیان] پاک و پاکیزه شوی؟'),
(5731, 79, 19, 'من تو را به سوی پروردگارت راهنمایی می کنم تا از او بترسی [و از طغیان دست برداری.]'),
(5732, 79, 20, 'پس آن معجزه بزرگ تر را به او نشان داد.'),
(5733, 79, 21, 'ولی [او آن را] تکذیب کرد و سرپیچی نمود.'),
(5734, 79, 22, 'سپس [به حق] پشت کرد [و برای نابودی موسی] به تلاش برخاست!'),
(5735, 79, 23, 'پس [قومش را] گرد آورد، و ندا داد'),
(5736, 79, 24, 'و گفت: من پروردگار بزرگ تر شما هستم.'),
(5737, 79, 25, 'پس خدا هم [به کیفر این ادعای نابجا] او را به عذاب آخرت و دنیا دچار ساخت.'),
(5738, 79, 26, 'بی تردید در این سرگذشت برای کسی که [از عذاب خدا] بترسد عبرتی است.'),
(5739, 79, 27, 'آیا آفریدن شما [پس از مرگ] دشوارتر است یا آسمان که او آن را بنا کرد؟'),
(5740, 79, 28, 'سقفش را برافراشت، پس آن را درست و نیکو قرار داد،'),
(5741, 79, 29, 'و شبش را تاریکو روزش را روشن ساخت،'),
(5742, 79, 30, 'و زمین را پس از آن گسترانید،'),
(5743, 79, 31, 'و از آن آبو چراگاهش را بیرون آورد،'),
(5744, 79, 32, 'و کوه ها را محکم و استوار نمود'),
(5745, 79, 33, 'تا مایه بهرهوری شما و دام هایتان باشد.'),
(5746, 79, 34, 'پس زمانی که آن حادثه بزرگ تر [و غیر قابل دفع] در رسد،'),
(5747, 79, 35, 'در آن روز انسان آنچه تلاش و کوشش کرده به یاد آورد'),
(5748, 79, 36, 'و دوزخ رابرای هر بیننده ای آشکار سازند،'),
(5749, 79, 37, 'و اما کسی که طغیان و سرکشی کرده'),
(5750, 79, 38, 'و زندگی دنیا را [بر زندگی ابد و جاوید آخرت] ترجیح داده،'),
(5751, 79, 39, 'پس بی تردید جایگاهش دوزخ است؛'),
(5752, 79, 40, 'و اما کسی که از مقام و منزلت پروردگارش ترسیده و نفس را از هوا و هوس بازداشته است؛'),
(5753, 79, 41, 'پس بی تردید جایگاهش بهشت است.'),
(5754, 79, 42, 'همواره ازتو درباره قیامت می پرسند که در چه زمانی واقع می شود؟'),
(5755, 79, 43, 'تو از بسیار یاد کردنش [و سخن گفتن درباره آن] چه به دست می آوری؟'),
(5756, 79, 44, 'نهایتش به سوی پروردگار توست [کیفیت برپا شدن و زمان وقوع و اوصافش را کسی جز خدا نمی داند.]'),
(5757, 79, 45, 'وظیفه تو فقط بیم دادن کسانی است که همواره از آن می ترسند.'),
(5758, 79, 46, 'گویی آنان روزی که قیامت را می بینند چنین می پندارند که در برزخ جز شامگاهی یا صبح گاه آن درنگ نکرده اند!');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'چهره در هم کشید و روی گردانید،'),
(5760, 80, 2, 'از اینکه آن مرد نابینا نزد او آمد!'),
(5761, 80, 3, 'تو چه می دانی شاید او [در پرتو تعالیم اسلام از آلودگی] پاک و پاکیزه شود'),
(5762, 80, 4, 'یا متذکّر [حقایق] گردد و آن تذکر او را سود دهد؛'),
(5763, 80, 5, 'اما کسی که خود را ثروتمند نشان می دهد'),
(5764, 80, 6, 'تو به او روی می آوری [و نسبت به وی اهتمام می ورزی]'),
(5765, 80, 7, 'در حالی که اگر او نخواهد خود را [از آلودگی های باطنی و عملی] پاک کند تکلیفی بر عهده تو [نسبت به او] نیست؛'),
(5766, 80, 8, 'و اما آنکه شتابان نزد تو آمد'),
(5767, 80, 9, 'در حالی که [از پروردگارش] می ترسد،'),
(5768, 80, 10, 'تو [با روی گردانی] از او به دیگران می پردازی.'),
(5769, 80, 11, 'این چنین [برخوردی شایسته] نیست، بی تردید این آیات قرآن مایه پند است.'),
(5770, 80, 12, 'پس هرکه خواست از آن پند گیرد،'),
(5771, 80, 13, 'در صحیفه هایی است ارزشمند'),
(5772, 80, 14, 'بلند مرتبه و پاکیزه'),
(5773, 80, 15, 'در دست سفیرانی'),
(5774, 80, 16, 'بزرگوار و نیکوکار.'),
(5775, 80, 17, 'مرگ بر انسان، چه کافر و ناسپاس است!'),
(5776, 80, 18, '[خدا] او را از چه چیز آفریده؟'),
(5777, 80, 19, 'از نطفه ای [ناچیز و بی مقدار] آفریده است، پس او را [در ذات، صفات و اندام] اندازه لازم عطا کرد.'),
(5778, 80, 20, 'آن گاه راه [هدایت، سعادت، خیر و طاعت] را برایش آسان ساخت.'),
(5779, 80, 21, 'سپس او را میراند و در گور نهاد،'),
(5780, 80, 22, 'و سپس چون بخواهد او را زنده می کند.'),
(5781, 80, 23, 'این چنین نیست [که وظیفه اش را انجام داده باشد] هنوز آنچه را به او دستور داده به جا نیاورده است.'),
(5782, 80, 24, 'پس انسان باید به خوراکش با تأمل بنگرد'),
(5783, 80, 25, 'که ما [از آسمان] آب فراوانی فرو ریختیم.'),
(5784, 80, 26, 'سپس زمین را [به صورتی سودمند] از هم شکافتیم.'),
(5785, 80, 27, 'پس در آن دانه های فراوانی رویاندیم،'),
(5786, 80, 28, 'و انگور و سبزیجات'),
(5787, 80, 29, 'و زیتون و درخت خرما….'),
(5788, 80, 30, 'و بوستان های پر از درخت تناور و بزرگ'),
(5789, 80, 31, 'و میوه و چراگاه'),
(5790, 80, 32, 'تا مایه برخورداری شما و دام هایتان باشد. ….'),
(5791, 80, 33, 'پس زمانی که آن بانگ هولناک و مهیب در رسد،'),
(5792, 80, 34, 'روزی که آدمی فرار می کند، از برادرش….'),
(5793, 80, 35, 'و از مادر و پدرش….'),
(5794, 80, 36, 'و از همسر و فرزندانش'),
(5795, 80, 37, 'در آن روز هرکسی از آنان را کاری است که او را به خود مشغول می کند [تا جایی که نمی گذارد به چیز دیگری بپردازد.]'),
(5796, 80, 38, 'در آن روز چهره هایی درخشان و نورانی است'),
(5797, 80, 39, 'خندان و خوشحال….'),
(5798, 80, 40, 'و در آن روز چهره هایی است که بر آنان غبار نشسته'),
(5799, 80, 41, '[و] سیاهی و تاریکی آنان را فرا گرفته است؛ ….'),
(5800, 80, 42, 'آنان همان کافران بد کارند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'هنگامی که خورشید را به هم درپیچند'),
(5802, 81, 2, 'و هنگامی که ستارگان تیره و بی نور شوند'),
(5803, 81, 3, 'و هنگامی که کوه ها را به حرکت آرند و از جا برکنند'),
(5804, 81, 4, 'و هنگامی که اموال نفیس و با ارزش رها و بی صاحب شود'),
(5805, 81, 5, 'و هنگامی که همه حیوانات وحشی محشور شوند'),
(5806, 81, 6, 'و هنگامی که دریاها مشتعل و برافروخته گردند'),
(5807, 81, 7, 'و هنگامی که هرکس [با همسان خود] قرین و جفت شوند [خوبان با خوبان و بدان با بدان]'),
(5808, 81, 8, 'و هنگامی که از [دختر] زنده به گور بپرسند'),
(5809, 81, 9, 'به کدام گناه کشته شده؟'),
(5810, 81, 10, 'و هنگامی که نامه های اعمال را بگشایند….'),
(5811, 81, 11, 'و هنگامی که آسمان را به شدت برکنند و برچینند'),
(5812, 81, 12, 'و هنگامی که دوزخ افروخته شود….'),
(5813, 81, 13, 'و هنگامی که بهشت را نزدیک آرند'),
(5814, 81, 14, '[در چنین موقعیت و زمانی است که] هر کس هر عملی را [از خیر و شر] حاضر کرده است، بداند.'),
(5815, 81, 15, 'پس سوگند به ستارگانی که باز می گردند،'),
(5816, 81, 16, 'ستارگانی که به سرعت می روند و پنهان می شوند'),
(5817, 81, 17, 'و سوگند به شب هنگامی که [روی می آورد، و زمانی که] می رود'),
(5818, 81, 18, 'و سوگند به صبح هنگامی که می دمد [و با گستردن نورش تاریکی را می زداید]'),
(5819, 81, 19, 'که قرآن کلام فرستاده ای ارجمند و بزرگوار است.'),
(5820, 81, 20, 'نیرومندی که نزد صاحب عرش دارای مقام و منزلت است؛ ….'),
(5821, 81, 21, 'آنجا مورد اطاعت [فرشتگان] و امین است….'),
(5822, 81, 22, 'و رفیق و یار شما دیوانه نیست.'),
(5823, 81, 23, 'بی تردید امین وحی را در افق روشن دیده است،'),
(5824, 81, 24, 'و او نسبت به [ابلاغ و تعلیم] آنچه به او وحی می شود، بخیل نیست'),
(5825, 81, 25, 'و قرآن، کلام شیطان رانده شده نیست؛'),
(5826, 81, 26, 'پس [با انکار قرآن و روی گرداندن از آن] کجا می روید؟'),
(5827, 81, 27, 'قرآن فقط وسیله یادآوری و پند برای جهانیان است؛'),
(5828, 81, 28, 'برای هرکس از شما که بخواهد [در همه شؤون زندگی مادی و معنوی] راه مستقیم بپیماید'),
(5829, 81, 29, 'و شما [طیِ راهِ مستقیم را] نخواهید خواست مگر آنکه خدا پروردگار جهانیان بخواهد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'هنگامی که آسمان بشکافد'),
(5831, 82, 2, 'و هنگامی که ستارگان پراکنده شوند'),
(5832, 82, 3, 'و هنگامی که دریاها شکافته شوند [و به هم بپیوندند]'),
(5833, 82, 4, 'و هنگامی که قبرها زیر و رو شوند،'),
(5834, 82, 5, 'هرکسی آنچه را [از خیر و شر] پیش فرستاده و باز پس نهاده بداند.'),
(5835, 82, 6, 'ای انسان! چه چیزی تو را به پروردگار بزرگوارت مغرور کرده است؟'),
(5836, 82, 7, 'همان کسی که تو را آفرید و اندامت را درست و نیکو ساخت و متعادل و متناسب قرار داد،'),
(5837, 82, 8, 'و تو را در هر نقش و صورتی که خواست ترکیب کرد.'),
(5838, 82, 9, 'این چنین نیست [که سبب ارتکاب گناهانتان مغرور بودنتان به بزرگواری پروردگار باشد]، بلکه روز جزا را تکذیب می کنید [و با این تکذیب، راه گناه را به روی خود باز می نمایید،]'),
(5839, 82, 10, 'و بی تردید بر شما نگهبانانی گماشته اند'),
(5840, 82, 11, 'بزرگوارانی نویسنده'),
(5841, 82, 12, 'که آنچه را [از خیر و شر] انجام می دهید، می دانند [و ضبط می کنند.]'),
(5842, 82, 13, 'به یقین نیکان در نعمت فراوانی قرار دارند'),
(5843, 82, 14, 'و مسلماً گناهکاران در دوزخ اند'),
(5844, 82, 15, '[که] روز پاداش در آن درآیند،'),
(5845, 82, 16, 'و آنان از دوزخ غایب نمی شوند'),
(5846, 82, 17, 'و تو چه می دانی روز جزا چیست؟'),
(5847, 82, 18, 'باز چه می دانی روز جزا چیست؟'),
(5848, 82, 19, 'روزی که کسی از کسی چیزی از عذاب را دفع نمی کند، و در آن روز، فرمان و حکم ویژه خداست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای بر کم فروشان!'),
(5850, 83, 2, 'آنان که چون از مردم کالایی را با پیمانه و وزنمی ستانند، تمام و کامل می ستانند،'),
(5851, 83, 3, 'و چون برای آنان پیمانه و وزن کنند، کم می دهند.'),
(5852, 83, 4, 'آیا اینان یقین ندارند که حتماً بر انگیخته می شوند؟'),
(5853, 83, 5, 'برای روزی بزرگ،'),
(5854, 83, 6, 'روزی که مردم در برابر پروردگار جهانیان به پا می ایستند.'),
(5855, 83, 7, 'این چنین نیست که می پندارند [در آن روز] یقیناً پرونده بدکاران در سجّین است.'),
(5856, 83, 8, 'و تو چه می دانی که سجّین چیست؟'),
(5857, 83, 9, 'قضا و سرنوشتی حتمی [برای خائنان] است.'),
(5858, 83, 10, 'وای در آن روز بر تکذیب کنندگان.'),
(5859, 83, 11, 'آنان که همواره روز جزا را تکذیب می کنند.'),
(5860, 83, 12, 'و آن را جز هر متجاوز گناه پیشه تکذیب نکند،'),
(5861, 83, 13, '[که] هرگاه آیات ما را بر او خوانند می گوید: افسانه های پیشینیان است.'),
(5862, 83, 14, 'این چنین نیست که می گویند، بلکه گناهانی که همواره مرتکب شده اند بر دل هایشان چرک و زنگار بسته است [که حقایق را افسانه می پندارند.]'),
(5863, 83, 15, 'این چنین نیست که آنان می پندارند، بلکه اینان در آن روز از پروردگارشان محجوب اند.'),
(5864, 83, 16, 'سپس آنان بی تردید وارد دوزخ می شوند.'),
(5865, 83, 17, '[به آنان] گویند: این است آن دوزخی که همواره تکذیبش می کردید.'),
(5866, 83, 18, 'این چنین نیست [که این سبک مغزان درباره نیکان می پندارند] بلکه پرونده نیکان در علیّین است'),
(5867, 83, 19, 'تو چه می دانی علیّین چیست؟'),
(5868, 83, 20, 'قضا و سرنوشتی حتمی [برای نیکان] است.'),
(5869, 83, 21, 'مقربان آن را مشاهده می کنند.'),
(5870, 83, 22, 'بی تردید نیکان در نعمتی فراوانند'),
(5871, 83, 23, 'بر تخت ها [ی آراسته و پرارزش تکیه زده و مناظر زیبای بهشت را با چشم سر و جمال محبوب را با چشم دل] می نگرند.'),
(5872, 83, 24, 'در چهره هایشان شادابی و طراوت نعمت را می یابی.'),
(5873, 83, 25, 'آنان را از باده ناب و طهوری که سربسته و مُهر و موم شده است، می نوشانند.'),
(5874, 83, 26, 'مُهر و مومش مشک است، و رقابت کنندگان و مسابقه گران باید به سوی این نعمت ها بر یکدیگر پیشی گیرند،'),
(5875, 83, 27, 'و [این باده ناب مُهر و موم شده،] آمیخته ای از «تسنیم» است'),
(5876, 83, 28, '[آن] چشمه ای که همواره مقربان از آن می نوشند.'),
(5877, 83, 29, 'بدکاران همواره [در دنیا از روی ریشخند و استهزا] به مؤمنان می خندیدند'),
(5878, 83, 30, 'و هنگامی که بر آنان می گذشتند آنان را با اشاره چشم وابرو به مسخره می گرفتند،'),
(5879, 83, 31, 'و چون به خانواده خود بازمی گشتند [به سبب تمسخر مؤمنان] خوشحال و شادمان باز می گشتند،'),
(5880, 83, 32, 'و هنگامی که مؤمنان را می دیدند، می گفتند: بی تردید اینان گمراه اند؛'),
(5881, 83, 33, 'و حال آنکه کافران را بر مؤمنان نگهبان و مراقب نفرستاده بودند [که مراقب هدایت و گمراهی آنان باشند.]'),
(5882, 83, 34, 'پس امروز همواره مؤمنان به کافران می خندند.'),
(5883, 83, 35, 'بر تخت ها [ی آراسته و پرارزش تکیه زده] می نگرند'),
(5884, 83, 36, 'که آیا کافران را در برابر آنچه همواره انجام می دادند کیفر داده اند؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'هنگامی که آسمان بشکافد'),
(5886, 84, 2, 'و فرمان پروردگارش را اطاعت کند، و [به این اطاعت از فرمان،] سزاوارتر گردد'),
(5887, 84, 3, 'و هنگامی که زمین گسترده شود'),
(5888, 84, 4, 'و آنچه را در درون دارد بیرون افکند، و تهی گردد'),
(5889, 84, 5, 'و فرمان پروردگارش را اطاعت کند، و [به این اطاعتِ از فرمان،] سزاوارتر گردد،'),
(5890, 84, 6, 'ای انسان! یقیناً تو با کوشش و تلاشی سخت به سوی پروردگارت در حرکتی، پس او را [در حالی که مقام فرمانروایی مطلق و حکومت بر همه چیز ویژه اوست و هیچ حکومتی در برابرش وجود ندارد،] دیدار می کنی،'),
(5891, 84, 7, 'اما کسی که نامه اعمالش را به دست راستش دهند،'),
(5892, 84, 8, 'به زودی با حسابی آسان به حسابش رسیدگیشود،'),
(5893, 84, 9, 'و خوشحال و شادمان به سوی خانواده اش [در بهشت] باز گردد،'),
(5894, 84, 10, 'و اما کسی که نامه اعمالش را از پشت سرش دهند،'),
(5895, 84, 11, 'به زودی با فریادی حسرت بار، هلاکت و نابودی خود را خواهد،'),
(5896, 84, 12, 'و در آتش افروخته در آید.'),
(5897, 84, 13, 'او همواره در میان خانواده اش [به ثروت و مال و مقام] خوشحال و شادمان بود.'),
(5898, 84, 14, 'او می پنداشت که هرگز پس از مرگ [به حیات دوباره] باز نخواهد گشت.'),
(5899, 84, 15, 'آری، بی تردید پروردگارش نسبت به [همه امور] او بینا بود [از این جهت تمام اعمال و دست آوردش را برای پاداش و کیفر ثبت کرد.]'),
(5900, 84, 16, 'سوگند به سرخی کنار افق به هنگام غروب خورشید'),
(5901, 84, 17, 'و سوگند به شب و آنچه را [از موجودات پراکنده شده در روز، در خانه ها و لانه ها] جمع می کند'),
(5902, 84, 18, 'و سوگند به ماه هنگامی که بَدْر کامل می شود'),
(5903, 84, 19, 'که شما حالی را پس از حالی [که مراحل مختلف زندگی است و در سختی و زحمت مطابق یکدیگرند] طی می کنید [تا به دیدار پروردگارتان نایل آیید.]'),
(5904, 84, 20, 'پس اینان را چه می شود که ایمان نمی آورند؟!'),
(5905, 84, 21, 'و هرگاه قرآن را بر آنان خوانند، خاضع و تسلیم نمی شوند؟!'),
(5906, 84, 22, 'بلکه کافران همواره انکار هم می کنند!'),
(5907, 84, 23, 'و خدا به آنچه [از انکار، کفر، نفاق، حسد و...] در دل خود جمع می کنند، داناتر است.'),
(5908, 84, 24, 'پس آنان را به عذابی دردناک بشارت ده. ….'),
(5909, 84, 25, 'مگر آنان را که ایمان آورده و کارهای شایسته انجام داده اند، که آنان را پاداشی جاودان و بی منت است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمان که دارای برج هاست'),
(5911, 85, 2, 'و سوگند به روزی که [برپا شدنش را برای داوری میان مردم] وعده داده اند'),
(5912, 85, 3, 'و سوگند به شاهد [که پیامبر هر امت است] و مورد مشاهده [که اعمال هر امت است؛]'),
(5913, 85, 4, 'مرده باد صاحبان آن خندق [که مؤمنان را در آن سوزاندند.]'),
(5914, 85, 5, 'آن آتشی که آتش گیرانه اش فراوان و بسیار بود،'),
(5915, 85, 6, 'هنگامی که آنان پیرامونش [به تماشا] نشسته بودند'),
(5916, 85, 7, 'و آنچه را از شکنجه و آسیب درباره مؤمنان انجام می دادند تماشاگر و ناظر بودند'),
(5917, 85, 8, 'و از مؤمنان چیزی را منفور و ناپسند نمی داشتند مگر ایمانشان را به خدای توانای شکست ناپذیر و ستوده؛'),
(5918, 85, 9, 'خدایی که مالکیّت و فرمانروایی آسمان ها و زمین در سیطره اوست و خدا بر همه چیز گواه است.'),
(5919, 85, 10, 'کسانی که مردان و زنان مؤمن را مورد شکنجه و آزار قرار دادند، سپس توبه نکردند، نهایتاً عذاب دوزخ و عذاب سوزان برای آنان است.'),
(5920, 85, 11, 'بی تردید کسانی که ایمان آورده و کارهای شایسته انجام داده اند بهشت هایی که از زیرِ [درختانِ] آن نهرها جاری است، ویژه آنان است، این است کامیابی بزرگ.'),
(5921, 85, 12, 'بی تردید به قهر گرفتن و مجازات پروردگارت بسیار سخت است. ….'),
(5922, 85, 13, 'اوست که [مخلوقات را] می آفریند، و [پس از مرگ] باز می گرداند….'),
(5923, 85, 14, 'و او بسیار آمرزنده و دوستدار [مؤمنان] است.'),
(5924, 85, 15, 'صاحب عرش و ارجمند [و عالی صفات] است. ….'),
(5925, 85, 16, 'آنچه را بخواهد [بی آنکه بازدارنده ای در برابرش باشد] انجام می دهد. ….'),
(5926, 85, 17, 'آیا خبر آن سپاهیان به تو رسیده است؟'),
(5927, 85, 18, '[سپاهیان] فرعون و ثمود [که به سبب تکذیبشان به عذابی دردناک دچار شدند؛]'),
(5928, 85, 19, '[فقط آنان تکذیب گر نبودند] بلکه کافران [قوم تو] در تکذیبی سخت ترند….'),
(5929, 85, 20, 'و خدا از همه سو بر آنان احاطه دارد.'),
(5930, 85, 21, '[چنین نیست که درباره قرآن می پندارند] بلکه آن، قرآنی باعظمت و بلند مرتبه است'),
(5931, 85, 22, 'که در لوح محفوظ است [به همین خاطر از دستبرد هر تحریف گری مصون است.]');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و به چیزی که در شب پدیدار می شود؛'),
(5933, 86, 2, 'و تو چه می دانی چیزی که در شب پدیدار می شود، چیست؟'),
(5934, 86, 3, 'همان ستاره درخشانی است که پرده ظلمت را می شکافد.'),
(5935, 86, 4, 'هیچ کس نیست مگر اینکه بر او نگهبانی است.'),
(5936, 86, 5, 'پس انسان باید با تأمل بنگرد که از چه چیز آفریده شده است؟'),
(5937, 86, 6, 'از آبی جهنده آفریده شده است؛'),
(5938, 86, 7, '[آبی که] از صلب مرد و از میان استخوان های سینه زن بیرون می آید.'),
(5939, 86, 8, 'بی تردید خدا بر بازگرداندن انسان [پس از مرگش] تواناست،'),
(5940, 86, 9, 'روزی که رازها فاش می شود.'),
(5941, 86, 10, 'پس انسان را [در آن روز در برابر عذاب] نه نیرویی است و نه یاوری.'),
(5942, 86, 11, 'سوگند به آسمان که دارای باران فراوان و پرریزش است.'),
(5943, 86, 12, 'و سوگند به زمین که [برای جوشیدن چشمه ها و کشت و زرع و روییدن نباتات] دارای شکاف است.'),
(5944, 86, 13, 'بی تردید که این قرآن، سخنی جداکننده [میان حق و باطل] است'),
(5945, 86, 14, 'و آن هرگز شوخی نیست.'),
(5946, 86, 15, 'آنان همواره [برای خاموش کردن نور حق] حیله می کنند'),
(5947, 86, 16, 'و من [هم در برابر آنان] چاره و تدبیری [مناسب] می کنم.'),
(5948, 86, 17, 'پس کافران را مهلت ده و مدت اندکی آنان را در این حالی که هستند، واگذار.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار برتر و بلند مرتبه ات را [از هرچه رنگ شرک خفی و جلی دارد] منزّه و پاک بدار.'),
(5950, 87, 2, 'آنکه آفرید، پس درست و نیکو گردانید.'),
(5951, 87, 3, 'و آنکه اندازه قرار داد و هدایت کرد،'),
(5952, 87, 4, 'و آنکه چراگاه را رویانید،'),
(5953, 87, 5, 'و آن را خاشاکی سیاه گردانید،'),
(5954, 87, 6, 'به زودی قرآن را بر تو می خوانیم، پس هرگز فراموش نخواهی کرد،'),
(5955, 87, 7, 'جز آنچه را خدا بخواهد، که او آشکار و آنچه را پنهان است، می داند.'),
(5956, 87, 8, 'و تو را برای آسان ترین راه [که شریعت سمحه و سهله است] آماده می کنیم.'),
(5957, 87, 9, 'پس [مردم را] اندرز ده، اگر اندرز سودمند افتد.'),
(5958, 87, 10, 'کسی که از خدا می ترسد، به زودی متذکّر می شود.'),
(5959, 87, 11, 'و کسی که از همه بدبخت تر است از آن کناره می گیرد.'),
(5960, 87, 12, 'همان کسی که در آتش بزرگ تر در می آید.'),
(5961, 87, 13, 'آن گاه در آنجا نه می میرد و نه به خوشی زندگی می کند.'),
(5962, 87, 14, 'بی تردید کسی که خود را [از زشتی های باطن و ظاهر] پاک کرد، رستگار شد.'),
(5963, 87, 15, 'و نام پروردگارش را یاد کرد، پس نماز خواند.'),
(5964, 87, 16, '[ولی شما منکران لجوج به سوی راه رستگاری نمی روید] بلکه زندگی دنیا را [بر آن] ترجیح می دهید.'),
(5965, 87, 17, 'در حالی که آخرت بهتر و پایدارتر است.'),
(5966, 87, 18, 'همانا این [حقایق] در کتاب های آسمانی پیشین هست.'),
(5967, 87, 19, '[در] کتاب های ابراهیم وموسی.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آیا خبر حادثه هولناکی که [همه انسان ها را از هر سو] فرا می گیرد، به تو رسیده است؟'),
(5969, 88, 2, 'در آن روز چهره هایی زبون و شرمسارند؛'),
(5970, 88, 3, '[آنان که همواره در دنیا] کوشیده اند و خسته شده اند [و سرانجام سودی نیافته اند]'),
(5971, 88, 4, 'در آتشی سوزان درآیند.'),
(5972, 88, 5, 'آنان را از چشمه ای بسیار داغ می نوشانند؛'),
(5973, 88, 6, 'برای آنان طعامی جز خار خشک و زهرآگین وجود ندارد'),
(5974, 88, 7, 'که نه فربه می کند و نه از گرسنگی بی نیاز می نماید.'),
(5975, 88, 8, 'در آن روز چهره هایی شاداب و باطراوت اند'),
(5976, 88, 9, 'از تلاش و کوشش خود خشنودند'),
(5977, 88, 10, 'در بهشتی برین اند'),
(5978, 88, 11, 'در آنجا سخن لغو وبیهوده نشنوند'),
(5979, 88, 12, 'در آن چشمه ای روان است'),
(5980, 88, 13, '[و] در آنجا تخت هایی بلند و با ارزش قرار دارد'),
(5981, 88, 14, 'و [در کنار چشمه هایش] قدح ها نهاده شده است'),
(5982, 88, 15, 'و [دیگر از نعمت هایش] بالش هایی پهلوی هم چیده [برای تکیه زدن بهشتی ها] ست'),
(5983, 88, 16, 'و فرش هایی زربافت و گسترده [که روی آن می نشینند.]'),
(5984, 88, 17, 'آیا با تأمل به شتر نمی نگرند که چگونه آفریده شده؟'),
(5985, 88, 18, 'و به آسمان که چگونه بر افراشته شده؟'),
(5986, 88, 19, 'و به کوه ها که چگونه در جای خود نصب شده؟'),
(5987, 88, 20, 'و به زمین که چگونه گسترده شده؟'),
(5988, 88, 21, 'پس تذکر ده که تو فقط تذکر دهنده ای؛'),
(5989, 88, 22, 'تو بر آنان مسلط نیستی [که به قبول ایمان مجبورشان کنی،]'),
(5990, 88, 23, 'ولی کسی که [به دنبال تذکر پی در پی] روی گردانید و کفر ورزید.'),
(5991, 88, 24, 'پس خدا او را به عذاب بزرگ تر عذاب خواهد کرد.'),
(5992, 88, 25, 'قطعاً بازگشت آنان به سوی ماست.'),
(5993, 88, 26, 'آن گاه بی تردید حسابشان بر عهده ماست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپیده دم'),
(5995, 89, 2, 'و به شب های ده گانه'),
(5996, 89, 3, 'و به زوج و فرد'),
(5997, 89, 4, 'و به شب هنگامی که می گذرد.'),
(5998, 89, 5, 'آیا در آنچه گفته شد، سوگندی برای خردمند هست؟'),
(5999, 89, 6, 'آیا ندانسته ای که پروردگارت با قوم عاد چه کرد؟'),
(6000, 89, 7, 'و [با آن شهر] اِرم که دارای کاخ های باعظمت و ساختمان های بلند بود؟'),
(6001, 89, 8, 'همان که مانندش در شهرها ساخته نشده بود؟'),
(6002, 89, 9, 'و با قوم ثمود آنان که در آن وادی [برای ساختن بناهای استوار و محکم] تخته سنگ ها را می بریدند؟'),
(6003, 89, 10, 'و با فرعون نیرومند که دارای میخ های شکنجه بود؟'),
(6004, 89, 11, 'همانان که در شهرها، طغیان وسرکشی کردند؟'),
(6005, 89, 12, 'و در آنها فساد وتباه کاری فراوانی به بار آوردند؟'),
(6006, 89, 13, 'پس پروردگارت تازیانه عذاب های گوناگون را بر آنان فرو ریخت.'),
(6007, 89, 14, 'بی تردید پروردگارت در کمین گاه است؛'),
(6008, 89, 15, 'اما انسان، هنگامی که پروردگارش او را بیازماید و گرامی داردش و نعمتش بخشد، می گوید: پروردگارم [چون شایسته و سزاوار بودم] مرا گرامی داشت،'),
(6009, 89, 16, 'و اما چون او را بیازماید، پس روزی اش را بر او تنگ گیرد، گوید: پروردگارم مرا خوار و زبون کرد.'),
(6010, 89, 17, 'این چنین نیست که می پندارید، بلکه [زبونی، خواری و دور شدن شما از رحمت خدا برای این است که] یتیم را گرامی نمی دارید'),
(6011, 89, 18, 'و یکدیگر را بر طعام دادن به مستمند تشویق نمی کنید'),
(6012, 89, 19, 'و میراث خود را [با میراث دیگران بی توجه به حلال وحرام بودنش] یک جا و کامل می خورید'),
(6013, 89, 20, 'وثروت را بسیار دوست دارید.'),
(6014, 89, 21, 'این چنین نیست که می پندارید، هنگامی که زمین را به شدت درهم کوبند'),
(6015, 89, 22, 'و [فرمان] پروردگارت برسد، وفرشتگان صف اندر صف حاضر شوند،'),
(6016, 89, 23, 'در آن روز دوزخ را بیاورند، در آن روز انسان متذکّر شود و کجا این تذکر برای او سودمند افتد؟!'),
(6017, 89, 24, 'می گوید: ای کاش برای این زندگی ام [عبادت خالصانه و کار نیک] پیش فرستاده بودم.'),
(6018, 89, 25, 'پس در آن روز هیچ کس چون عذاب کردن او عذاب نکند،'),
(6019, 89, 26, 'و هیچ کس چون به بند کشیدن او به بند نکشد.'),
(6020, 89, 27, 'ای جان آرام گرفته و اطمینان یافته!'),
(6021, 89, 28, 'به سوی پروردگارت در حالی که از او خشنودی و او هم از تو خشنود است، باز گرد.'),
(6022, 89, 29, 'پس در میان بندگانم درآی'),
(6023, 89, 30, 'و در بهشتم وارد شو.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'قسم می خورم به این شهر،'),
(6025, 90, 2, 'در حالی که تو در آن جای داری'),
(6026, 90, 3, 'و سوگند به پدر و آنچه زاده است؛'),
(6027, 90, 4, 'همانا ما انسان را در رنج و زحمت آفریدیم.'),
(6028, 90, 5, 'آیا گمان می کند که هرگز کسی بر او دست نیابد؟!'),
(6029, 90, 6, 'می گوید: ثروت فراوانی را [برای مبارزه با پیامبر و اسلام] تباه کرده ام!'),
(6030, 90, 7, 'آیا گمان می کند که هیچ کس او را ندیده است [که همه اعمال و کردارش را ثبت کند؟]'),
(6031, 90, 8, 'آیا برای او دو چشم قرار ندادیم؟'),
(6032, 90, 9, 'و یک زبان و دو لب؟'),
(6033, 90, 10, 'و او را به راه خیر و شر هدایت نکردیم [تا راه خیر را بگزیند و راه شر را واگذارد؟]'),
(6034, 90, 11, 'پس شتابان و با شدت به آن گردنه سخت وارد نشد؛'),
(6035, 90, 12, 'و تو چه می دانی آن گردنه سخت چیست؟'),
(6036, 90, 13, 'آزاد کردن برده،'),
(6037, 90, 14, 'یا طعام دادن در روز گرسنگی و قحطی،'),
(6038, 90, 15, 'به یتیمی خویشاوند،'),
(6039, 90, 16, 'یا مستمندی خاک نشین؛'),
(6040, 90, 17, 'علاوه بر [انجام] این [تکالیف]، از کسانی باشد که ایمان آورده اند و یکدیگر را به صبر و مهربانی سفارش کرده اند؛'),
(6041, 90, 18, 'اینان سعادتمندان اند؛'),
(6042, 90, 19, 'و کسانی که به آیات ما کفر ورزیدند، آنان شقاوتمندان اند'),
(6043, 90, 20, 'بر آنان آتشی سرپوشیده احاطه دارد [که هیچ راه گریزی از آن ندارند.]');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشید و گسترش روشنی اش'),
(6045, 91, 2, 'و به ماه هنگامی که از پی آن برآید'),
(6046, 91, 3, 'و به روزچون خورشید را به خوبی آشکار کند'),
(6047, 91, 4, 'و به شب هنگامی که خورشید را فرو پوشد'),
(6048, 91, 5, 'و به آسمان و آنکه آن را بنا کرد'),
(6049, 91, 6, 'و به زمین و آنکه آن را گستراند'),
(6050, 91, 7, 'و به نفس و آنکه آن را درست و نیکو نمود،'),
(6051, 91, 8, 'پس بزه کاری و پرهیزکاری اش را به او الهام کرد.'),
(6052, 91, 9, 'بی تردید کسی که نفس را [از آلودگی پاک کرد و] رشد داد، رستگار شد.'),
(6053, 91, 10, 'و کسی که آن را [به آلودگی ها و امور بازدارنده از رشد] بیالود [از رحمت حق] نومید شد.'),
(6054, 91, 11, 'قوم ثمود به سبب سرکشی و طغیانش [پیامبرشان را] تکذیب کرد،'),
(6055, 91, 12, 'زمانی که بدبخت ترینشان به پا خاست.'),
(6056, 91, 13, 'پس فرستاده خدا به آنان گفت: ناقه خدا و آبشخورش را [واگذارید]'),
(6057, 91, 14, 'ولی به تکذیب او برخاستند و ناقه را پی کردند، و در نتیجه پروردگارشان به خاطر گناهشان عذاب سختی بر آنان فرو ریخت و همه قوم را با خاک یکسان و برابر ساخت؛'),
(6058, 91, 15, 'و خدا از سرانجام آن کار [که نابودی ستمکاران است] پروایی ندارد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب هنگامی که فرو پوشد'),
(6060, 92, 2, 'و به روز هنگامی که آشکار شود'),
(6061, 92, 3, 'و به آنکه نر و ماده آفرید؛'),
(6062, 92, 4, 'همانا تلاشتان گوناگون و پراکنده است؛'),
(6063, 92, 5, 'اما کسی که [ثروتش را در راه خدا] انفاق کرد و پرهیزکاری پیشه ساخت'),
(6064, 92, 6, 'و وعده نیکوتر را [که وعده خدا نسبت به پاداش انفاق و پرهیزکاری است] باور کرد؛'),
(6065, 92, 7, 'پس به زودی او را برای راه آسانی [که انجام همه اعمال نیک به توفیق خداست] آماده می کنیم'),
(6066, 92, 8, 'و اما کسی که [از انفاق ثروت] بخل ورزید و خود را بی نیاز نشان داد'),
(6067, 92, 9, 'و وعده نیکوتر را تکذیب کرد،'),
(6068, 92, 10, 'پس او را برای راه سخت و دشواری [که سلب هرگونه توفیق از اوست] آماده می کنیم،'),
(6069, 92, 11, 'و هنگامی که [به چاه هلاکت و گودال گور] سقوط کند ثروتش [چیزی از عذاب خدا] را از او دفع نمی کند.'),
(6070, 92, 12, 'بی تردید هدایت کردن بر عهده ماست.'),
(6071, 92, 13, 'و به یقین دنیا و آخرت در سیطره مالکیّت ماست.'),
(6072, 92, 14, 'پس شما را از آتشی که زبانه می کشد بیم می دهم.'),
(6073, 92, 15, '[که] جز بدبخت ترین مردم در آن در نیایند.'),
(6074, 92, 16, 'همان که [دین را] تکذیب کرد و از آن روی گردانید.'),
(6075, 92, 17, 'و به زودی پرهیزکارترین [مردم] را از آن دور می دارند.'),
(6076, 92, 18, 'همان که مال خود را انفاق می کند تا [مال] رشد و نمو کند.'),
(6077, 92, 19, 'و هیچ کس را نزد خدا حق نعمتی نیست تا این مالی که انفاق کرده به عنوان پاداش آن نعمت به حساب او گذارد.'),
(6078, 92, 20, 'بلکه [انفاق کننده با تقوا مال خود را در راه خدا انفاق می کند] تا خشنودی پروردگار والایش را به دست آورد.'),
(6079, 92, 21, 'و [بی تردید] به زودی [در قیامت با دریافت لطف، رحمت حق و پاداش بی پایان او] خشنود خواهد شد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به ابتدای روز [وقتی که خورشید پرتو افشانی می کند]'),
(6081, 93, 2, 'و سوگند به شب آن گاه که آرام گیرد،'),
(6082, 93, 3, 'که پروردگارت تو را رها نکرده و مورد خشم و کینه قرار نداده است.'),
(6083, 93, 4, 'و بی تردید آخرت برای تو از دنیا بهتر است،'),
(6084, 93, 5, 'و به زودی پروردگارت بخششی به تو خواهد کرد تا خشنود شوی.'),
(6085, 93, 6, 'آیا تو را یتیم نیافت، پس پناه داد؟'),
(6086, 93, 7, 'و تو را بدون شریعت نیافت، پس به شریعت هدایت کرد؟'),
(6087, 93, 8, 'و تو را تهیدست نیافت، پس بی نیاز ساخت؟'),
(6088, 93, 9, 'و اما [به شکرانه این همه نعمت] یتیم را خوار و رانده مکن'),
(6089, 93, 10, 'و تهیدست حاجت خواه را [به بانگ زدن] از خود مران'),
(6090, 93, 11, 'و نعمت های پروردگارت را بازگو کن.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آیا سینه ات را [به نوری از سوی خود] گشاده نکردیم؟'),
(6092, 94, 2, 'و بار گرانت را فرو ننهادیم؟'),
(6093, 94, 3, 'همان بار گرانی که پشتت را شکست.'),
(6094, 94, 4, 'و آوازه ات را برایت بلند نکردیم؟'),
(6095, 94, 5, 'پس بی تردید با دشواری آسانی است.'),
(6096, 94, 6, '[آری] بی تردید با دشواری آسانی است.'),
(6097, 94, 7, 'پس هنگامی که [از کار بسیار مهم تبلیغ] فراغت می یابی، به عبادت و دعا بکوش'),
(6098, 94, 8, 'و مشتاقانه به سوی پروردگارت رو آور.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به انجیر و زیتون'),
(6100, 95, 2, 'و سوگند به طور سینا'),
(6101, 95, 3, 'و سوگند به این [مکه] شهر امن'),
(6102, 95, 4, 'که ما انسان را در نیکوترین نظم و اعتدال و ارزش آفریدیم.'),
(6103, 95, 5, 'آن گاه او را [به سبب گناهکاری] به [مرحله] پست ترینِ پَستان بازگرداندیم.'),
(6104, 95, 6, 'مگر آنان که ایمان آورده و کارهای شایسته انجام داده اند، پس آنان را پاداشی بی منت و همیشگی است.'),
(6105, 95, 7, '[ای انسان!] پس چه چیزی تو را بعد [از این همه دلایل] به تکذیب دین و [روز قیامت]، وامی دارد؟'),
(6106, 95, 8, 'آیا خدا بهترین داوران نیست؟');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت که [همه آفریده ها را] آفریده؛'),
(6108, 96, 2, '[همان که] انسان را از علق به وجود آورد.'),
(6109, 96, 3, 'بخوان در حالی که پروردگارت کریم ترین [کریمان] است.'),
(6110, 96, 4, 'همان که به وسیله قلم آموخت،'),
(6111, 96, 5, '[و] به انسان آنچه را نمی دانست تعلیم داد.'),
(6112, 96, 6, 'این چنین نیست [که انسان سپاس گزار باشد] مسلماً انسان سرکشی می کند.'),
(6113, 96, 7, 'برای اینکه خود را بی نیاز می پندارد.'),
(6114, 96, 8, 'بی تردید بازگشت به سوی پروردگار توست.'),
(6115, 96, 9, 'مرا خبر ده، آیا آن کسی که باز می دارد،'),
(6116, 96, 10, 'بنده ای را هنگامی که نماز می خواند؟'),
(6117, 96, 11, 'مرا خبر ده، اگر این بنده نمازگزار بر راه راست باشد'),
(6118, 96, 12, 'یا [دیگران را] به پرهیزکاری وادارد؟ [آیا آن بازدارنده سزاوار کیفر سخت نیست؟]'),
(6119, 96, 13, 'مرا خبر ده، اگر این بازدارنده [دین را] تکذیب کند و [از فرمان حق] روی برگرداند [مستحق مجازات سخت نیست؟!]'),
(6120, 96, 14, 'مگر ندانسته که قطعاً خدا [همه کارهایش را] می بیند؟'),
(6121, 96, 15, 'این چنین نیست که می پندارد [که ما کارهایش را زیرنظر نداریم،] اگر [از کارهایش] باز نایستد، به شدت موی جلوی سرش را می گیریم [و به سوی دوزخ می کشانیم.]'),
(6122, 96, 16, '[آری] موی جلوی سر دروغگوی خطاپیشه را'),
(6123, 96, 17, 'پس [اگر بخواهد] اهل مجلس و انجمنش را [برای یاری دادنش] فرا خواند،'),
(6124, 96, 18, 'ما هم به زودی مأموران آتش دوزخ را فرا می خوانیم.'),
(6125, 96, 19, 'این چنین نیست [که بتواند برای نجات خود کاری کند]، هرگز از او اطاعت مکن، و سجده کن و به خدا تقرب جوی.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما قرآن را در شب قدر نازل کردیم.'),
(6127, 97, 2, 'تو چه می دانی شب قدر چیست؟'),
(6128, 97, 3, 'شب قدر، بهتر از هزار ماه است.'),
(6129, 97, 4, 'فرشتگان و روح در آن شب به اذن پروردگارشان برای [تقدیر و تنظیم] هر کاری نازل می شوند.'),
(6130, 97, 5, 'این شب تا برآمدن سپیده دم [سراسر] سلام و رحمت است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کافران از اهل کتاب و مشرکان [از آیین خود] جدا نمی شدند تا آن دلیل روشن برای آنان بیاید.'),
(6132, 98, 2, '[آن دلیل روشن] فرستاده ای [چون پیامبر اسلام] از سوی خداست که صحیفه هایی پاک را می خواند.'),
(6133, 98, 3, 'در آنها نوشته هایی استوار و باارزش است؛'),
(6134, 98, 4, 'و اهل کتاب درباره دین، [گروه گروه و] پراکنده نشدند مگر پس از آنکه آن دلیل روشن برای آنان آمد [و به طور کامل نسبت به آنان اتمام حجت شد.]'),
(6135, 98, 5, 'در حالی که فرمان نیافته بودند جز آنکه خدا را بپرستند، و ایمان و عبادت را برای او از هرگونه شرکی خالص کنند، و حق گرا باشند، و نماز را برپا دارند، و زکات بپردازند؛ و این است آیین استوار و ثابت.'),
(6136, 98, 6, 'به یقین کافرانِ از اهل کتاب و مشرکان در آتش دوزخ اند و در آن جاودانه اند؛ اینانند که بدترین مخلوقاتند.'),
(6137, 98, 7, 'مسلماً کسانی که ایمان آورده و کارهای شایسته انجام داده اند، اینانند که بهترین مخلوقاتند.'),
(6138, 98, 8, 'پاداششان نزد پروردگارشان بهشت های پاینده ای است که از زیرِ [درختانِ] آن نهرها جاری است، در آنها جاودانه اند، خدا از آنان خشنود است و آنان هم از خدا خشنودند؛ این [پاداش] برای کسی است که از پروردگارش بترسد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'هنگامی که زمین را با [شدیدترین] لرزشش بلرزانند،'),
(6140, 99, 2, 'و زمین بارهای گرانش را بیرون اندازد،'),
(6141, 99, 3, 'و انسان بگوید: زمین را چه شده است؟'),
(6142, 99, 4, 'آن روز است که زمین اخبار خود را می گوید؛'),
(6143, 99, 5, 'زیرا که پروردگارت به او وحی کرده است.'),
(6144, 99, 6, 'آن روز مردم [پس از پایان حساب] به صورت گروه های پراکنده [به سوی منزل های ابدی خود بهشت یا دوزخ] باز می گردند، تا اعمالشان را [به صورت تجسم یافته] به آنان نشان دهند.'),
(6145, 99, 7, 'پس هرکس هموزن ذره ای نیکی کند، آن نیکی را ببیند.'),
(6146, 99, 8, 'و هرکس هموزن ذره ای بدی کند، آن بدی را ببیند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به اسبان دونده ای که همهمه کنان [به سوی جنگ] می تازند'),
(6148, 100, 2, 'و سوگند به اسبانی که با کوبیدن سمشان از سنگ ها جرقه می جهانند'),
(6149, 100, 3, 'و سوگند به سوارانی که هنگام صبح غافل گیرانه به دشمن هجوم می برند'),
(6150, 100, 4, 'و به وسیله آن هجوم، گرد و غبار فراوانی بر انگیزند'),
(6151, 100, 5, 'و ناگاه در آن وقت در میان گروهی دشمن در آیند؛'),
(6152, 100, 6, '[که] قطعاً انسان نسبت به پروردگارش بسیار ناسپاس است،'),
(6153, 100, 7, 'و بی تردید خود او بر این ناسپاسی گواه است،'),
(6154, 100, 8, 'و همانا او نسبت به ثروت و مال سخت علاقه مند است [و به این سبب بخل می ورزد.]'),
(6155, 100, 9, 'آیا نمی داند هنگامی که آنچه در گورهاست برانگیخته شود.'),
(6156, 100, 10, 'و آنچه در سینه هاست پدیدار گردد،'),
(6157, 100, 11, 'بی تردید در آن روز پروردگارشان نسبت به آنان آگاه است [و بر پایه اعمال نیک و بدشان به آنان پاداش و کیفر دهد.]');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'آن کوبنده'),
(6159, 101, 2, 'چیست آن کوبنده؟'),
(6160, 101, 3, 'و تو چه می دانی آن کوبنده چیست؟'),
(6161, 101, 4, 'روزی که مردم [در سراسیمگی] چون پروانه های پراکنده اند'),
(6162, 101, 5, 'و کوه ها مانند پشم رنگین حلاجی شده گردد!'),
(6163, 101, 6, 'اما هرکس اعمال وزن شده اش سنگین و باارزش است؛'),
(6164, 101, 7, 'پس او در یک زندگی خوش و پسندیده ای است،'),
(6165, 101, 8, 'و اما هرکساعمال وزن شده اش سبک و بی ارزش است،'),
(6166, 101, 9, 'پس جایگاه و پناهگاهش هاویه است،'),
(6167, 101, 10, 'و تو چه می دانی هاویه چیست؟'),
(6168, 101, 11, 'آتشی بسیار داغ و سوزاناست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'مباهات و افتخار بر یکدیگر [به ثروت و کثرت نفرات] شما را [از پرداختن به تکالیف دینی و یاد آخرت] بازداشت؛'),
(6170, 102, 2, 'تا جایی که گورها را دیدار کردید [و به تعداد مردگان هم به یکدیگر مباهات و افتخار نمودید!!]'),
(6171, 102, 3, 'این چنین نیست [که شما می پندارید]، به زودی [به آثار شوم این اوضاع و احوالی که دارید] آگاه خواهید شد.'),
(6172, 102, 4, 'باز هم این چنین نیست، به زودی آگاه خواهید شد.'),
(6173, 102, 5, '[زندگی واقعی] این چنین نیست [که می پندارید]، اگر به علم الیقین [که علم استوار و غیر قابل تردید است به آخرت] آگاه بودید [از تکالیف دینی و توجه به آباد کردن آخرت باز نمی ماندید.]'),
(6174, 102, 6, 'بی تردید دوزخ را خواهید دید.'),
(6175, 102, 7, 'سپس [با وارد شدن در آن،] قطعاً آن را به دیده یقین و باور خواهید دید.'),
(6176, 102, 8, 'آن گاه شما در آن روز از نعمت ها بازپرسی خواهید شد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به عصر [ظهور پیامبر اسلام]'),
(6178, 103, 2, '[که] بی تردید انسان در زیان کاری بزرگی است؛'),
(6179, 103, 3, 'مگر کسانی که ایمان آورده و کارهای شایسته انجام داده اند و یکدیگر را به حق توصیه نموده و به شکیبایی سفارش کرده اند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر عیب جوی بدگوی!'),
(6181, 104, 2, 'همان که ثروتی فراهم آورده و [پی در پی] آن را شمرد [و ذخیره کرد.]'),
(6182, 104, 3, 'گمان می کند که ثروتش او را جاودانه خواهد کرد.'),
(6183, 104, 4, 'این چنین نیست، بی تردید او را در آن شکننده، اندازند؛'),
(6184, 104, 5, 'و تو چه می دانی آن شکننده چیست؟'),
(6185, 104, 6, 'آتش برافروخته خداست'),
(6186, 104, 7, '[آتشی] که بر دل ها برآید و چیره شود.'),
(6187, 104, 8, 'آن آتش بر آنان سربسته است [که هیچ راه گریزی از آن ندارند؛]'),
(6188, 104, 9, '[آتشی] در ستون هایی بلند و کشیده.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آیا ندانسته ای که پروردگارت با فیل سواران چه کرد؟'),
(6190, 105, 2, 'آیا نیرنگشان را در تباهی قرار نداد [ونقشه آنان را نقش بر آب نساخت؟!]'),
(6191, 105, 3, 'و بر ضد آنان گروه گروه پرندگانی فرستاد؛'),
(6192, 105, 4, '[که] بر آنان سنگ هایی از نوع سنگ گِل می افکندند.'),
(6193, 105, 5, 'سرانجام همه آنان را چون کاه جویده شده قرار داد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, '[خدا فیل سواران را هلاک کرد] تا قریش را [با یکدیگر و با مردم و با حرم امن] الفت دهد؛'),
(6195, 106, 2, '[و نیز] به سفرهای [تجارتی] زمستانی و [سفرهای تجارتی] تابستانی پیوند و انس دهد [تا در آرامش و امنیت، امر معاششان را تأمین کنند.]'),
(6196, 106, 3, 'پس باید پروردگار این خانه را بپرستند؛'),
(6197, 106, 4, 'پروردگاری که آنان را از گرسنگی نجات داد، و از بیمی [که از دشمن داشتند] ایمنی شان بخشید.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا کسی که همواره روز جزا را انکار می کند، دیدی؟'),
(6199, 107, 2, 'همان که یتیم را به خشونت و جفا از خود می راند،'),
(6200, 107, 3, 'و [کسی را] به طعام دادن به مستمند تشویق نمی کند.'),
(6201, 107, 4, 'پس وای بر نمازگزاران'),
(6202, 107, 5, 'که از نمازشان غافل و نسبت به آن سهل انگارند.'),
(6203, 107, 6, 'همانان که همواره ریا می کنند'),
(6204, 107, 7, 'و از [دادن] وسایل و ابزار ضروری زندگی [و زکات، هدیه و صدقه به نیازمندان] دریغ می ورزند.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'بی تردید ما به تو خیر فراوان [که برکت در نسل است و از فاطمه ریشه می گیرد] عطا کردیم.'),
(6206, 108, 2, 'پس [به شکرانه آن] برای پروردگارت نماز بخوان و شتر قربانی کن.'),
(6207, 108, 3, 'یقیناً دشمن [که به] تو [زخم زبان می زند] خود بدون تبار و نسل [و بریده از همه خیرات و برکات] است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو: ای کافران!'),
(6209, 109, 2, 'آنچه را شما می پرستید، من نمی پرستم،'),
(6210, 109, 3, 'و نه شما آنچه را من می پرستم، می پرستید،'),
(6211, 109, 4, 'و نه من آنچه را شما پرستیده اید، می پرستم،'),
(6212, 109, 5, 'و نه شما آنچه را که من می پرستم، می پرستید.'),
(6213, 109, 6, 'دین شما برای خودتان، و دین من برای خودم.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'هنگامی که یاری خدا و [آن] پیروزی فرا رسد،'),
(6215, 110, 2, 'و مردم را ببینی که گروه گروه در دین خدا درآیند،'),
(6216, 110, 3, 'پس پروردگارت را همراه با سپاس و ستایش تسبیح گوی، و از او آمرزش بخواه، که او همواره توبه پذیر است.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'نابود باد قدرت ابولهب، و نابود باد خودش؛'),
(6218, 111, 2, 'ثروتش و آنچه از امکانات به دست آورد چیزی [از عذاب خدا را که در دنیا عذاب استیصال است] از او دفع نکرد.'),
(6219, 111, 3, 'به زودی در آتشی زبانه دار درآید؛'),
(6220, 111, 4, 'و [نیز] همسرش که هیزم کش است [در آتش زبانه دار در آید.]'),
(6221, 111, 5, '[همان که] بر گردنش طنابی تابیده از لیف خرماست.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: او خدای یکتاست؛'),
(6223, 112, 2, 'خدای بی نیاز [همه موجودات هستی که نیازمند و عین فقرند برای رفع نیازشان روی به او کنند و از حضرتش گدایی نمایند.]'),
(6224, 112, 3, 'نزاده، و زاییده نشده است،'),
(6225, 112, 4, 'و هیچ کس [در ذات و صفات] همانند و همتا و شبیه او نمی باشد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: به پروردگار سپیده دم پناه می برم،'),
(6227, 113, 2, 'از زیان مخلوقاتی که [با انحرافشان از قوانین الهی به انسان] ضرر می رسانند،'),
(6228, 113, 3, 'و از زیان شب هنگامی که با تاریکی اش درآید [که در آن تاریکی انواع حیوانات موذی و انسان های فاسق و فاجر برای ضربه زدن به انسان در کمین اند]'),
(6229, 113, 4, 'و از زیان زنان دمنده [که افسون و جادو] در گره ها [می دمند و با این کار شیطانی در زندگی فرد، خانواده و جامعه خسارت به بار می آورند]'),
(6230, 113, 5, 'و از زیان حسود، زمانی که حسد می ورزد.');
INSERT INTO `fa_ansarian` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: پناه می برم به پروردگار مردم'),
(6232, 114, 2, '[به] پادشاه مردم'),
(6233, 114, 3, '[به] معبود مردم'),
(6234, 114, 4, 'از زیان وسوسه گر کمین گرفته و پنهان،'),
(6235, 114, 5, 'آنکه همواره در سینه های مردم وسوسه می کند'),
(6236, 114, 6, 'از جنّیان و آدمیان.');
