INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداى بخشاينده مهربان‌'),
(2, 1, 2, 'ستايش خدا را كه پروردگار جهانيان است.'),
(3, 1, 3, 'آن بخشاينده مهربان،'),
(4, 1, 4, 'آن فرمانرواى روز جزا.'),
(5, 1, 5, 'تنها تو را مى پرستيم و تنها از تو يارى مى‌جوييم.'),
(6, 1, 6, 'ما را به راه راست هدايت كن:'),
(7, 1, 7, 'راه كسانى كه ايشان را نعمت داده‌اى، نه خشم گرفتگانِ بر آنها و نه گمراهان.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف. لام. ميم.'),
(9, 2, 2, 'اين است همان كتابى كه در آن هيچ شكى نيست. پرهيزگاران را راهنماست:'),
(10, 2, 3, 'آنان كه به غيب ايمان مى‌آورند و نماز مى گزارند، و از آنچه روزيشان داده‌ايم انفاق مى كنند،'),
(11, 2, 4, 'و آنان كه به آنچه بر تو و بر پيامبران پيش از تو نازل شده است ايمان مى‌آورند و به آخرت يقين دارند.'),
(12, 2, 5, 'ايشان از سوى پروردگارشان قرين هدايتند، و خود رستگارانند.'),
(13, 2, 6, 'كافران را خواه بترسانى يا نترسانى تفاوتشان نكند، ايمان نمى آورند.'),
(14, 2, 7, 'خدا بر دلهايشان و بر گوششان مهر نهاده و بر روى چشمانشان پرده‌اى است، و برايشان عذابى است بزرگ.'),
(15, 2, 8, 'پاره‌اى از مردم مى‌گويند: به خدا و روز قيامت ايمان آورده‌ايم. حال آنكه ايمان نياورده‌اند.'),
(16, 2, 9, 'اينان خدا و مؤمنان را مى‌فريبند، و نمى‌دانند كه تنها خود را فريب مى‌دهند.'),
(17, 2, 10, 'در دلهاشان مرضى است و خدا نيز بر مرضشان بيفزوده است و به كيفر دروغى كه گفته‌اند برايشان عذابى است دردآور.'),
(18, 2, 11, 'چون به آنان گفته شود كه در زمين فساد مكنيد، مى‌گويند: ما مصلحانيم.'),
(19, 2, 12, 'آگاه باشيد كه اينان خود تبهكارانند و نمى‌دانند.'),
(20, 2, 13, 'و چون به آنان گفته شود كه شما نيز همانند ديگر مردمان ايمان بياوريد، مى‌گويند: آيا ما نيز همانند بيخردان ايمان بياوريم؟ آگاه باشيد، كه آنان خود بيخردانند و نمى‌دانند.'),
(21, 2, 14, 'و چون به مؤمنان مى‌رسند، مى‌گويند: ايمان آورده‌ايم. و چون با شيطانهاى خويش خلوت مى‌كنند، مى‌گويند: ما با شما هستيم، ما استهزايشان مى‌كنيم.'),
(22, 2, 15, 'خداست كه آنها را استهزا مى‌كند و وامى‌گذاردشان تا همچنان در طغيان خويش سرگردان بمانند.'),
(23, 2, 16, 'اينان گمراهى را به هدايت خريدند، پس تجارتشان سود نكرد و در شمار هدايت يافتگان درنيامدند.'),
(24, 2, 17, 'مثلشان، مثل آن كسى است كه آتشى افروخت، چون پيرامونش را روشن ساخت، خدا روشنايى از آنان بازگرفت و نابينا در تاريكى رهايشان كرد.'),
(25, 2, 18, 'كرانند، لالانند، كورانند، و بازنمى‌گردند.'),
(26, 2, 19, 'يا چون بارانى سخت در ظلمت همراه با رعد و برق از آسمان فرود آيد، تا مباد كه از بانگ صاعقه بميرند، انگشتان خويش در گوشها كنند. و خدا بر كافران احاطه دارد.'),
(27, 2, 20, 'نزديك باشد كه برق ديدگانشان را نابينا سازد، هرگاه كه بردَمد چند گامى برمى‌دارند، و چون خاموش شود، از رفتن بازايستند. اگر خدا مى‌خواست، گوشهاشان را كر و چشمانشان را كور مى‌ساخت، كه او بر هر كارى تواناست.'),
(28, 2, 21, 'اى مردم، پروردگارتان را كه شما و پيشينيانتان را بيافريده است بپرستيد. باشد كه پرهيزگار شويد.'),
(29, 2, 22, 'آن خداوندى كه زمين را چون فراشى بگسترد، و آسمان را چون بنايى بيفراشت، و از آسمان آبى فرستاد، و بدان آب براى روزى شما از زمين هر گونه ثمره‌اى برويانيد، و خود مى‌دانيد كه نبايد براى خدا همتايانى قرار دهيد.'),
(30, 2, 23, 'و اگر در آنچه بر بنده خويش نازل كرده‌ايم در ترديد هستيد، سوره‌اى همانند آن بياوريد و جز خداى همه حاضرانتان را فراخوانيد اگر راست مى‌گوييد.'),
(31, 2, 24, 'و هرگاه چنين نكرده‌ايد -كه هرگز نتوانيد كرد- پس بترسيد از آتشى كه براى كافران مهيّا شده و هيزم آن مردمان و سنگها هستند.'),
(32, 2, 25, 'به آنان كه ايمان آورده‌اند، و كارهاى شايسته كرده‌اند، بشارت ده كه برايشان بهشتهايى است كه در آن نهرها جارى است. و هرگاه كه از ميوه‌هاى آن برخوردار شوند گويند: پيش از اين، در دنيا، از چنين ميوه‌هايى برخوردار شده بوديم، كه اين ميوه‌ها شبيه به يكديگرند. و نيز در آنجا همسرانى پاكيزه دارند و در آنجا جاودانه باشند.'),
(33, 2, 26, 'خدا ابايى ندارد كه به پشه و كمتر از آن مثل بزند. آنان كه ايمان آورده‌اند مى‌دانند كه آن مثل درست و از جانب پروردگار آنهاست. و امّا كافران مى‌گويند كه خدا از اين مثل چه مى‌خواسته است؟ بسيارى را بدان گمراه مى‌كند و بسيارى را هدايت. امّا تنها فاسقان را گمراه مى كند.'),
(34, 2, 27, 'كسانى كه پيمان خدا را پس از بستن آن مى‌شكنند و آنچه را كه خدا به پيوستن آن فرمان داده مى‌گسلند و در زمين فساد مى‌كنند، زيانكارانند.'),
(35, 2, 28, 'چگونه خدا را انكار مى‌كنيد، در حالى كه مُرده بوديد و او شما را زنده ساخت، باز مى‌ميراند و زنده مى‌كند و آنگاه به نزد او بازگردانده مى شويد؟'),
(36, 2, 29, 'اوست كه همه چيزهايى را كه در روى زمين است برايتان بيافريد، آنگاه به آسمان پرداخت و هر هفت آسمان را برافراشت و خود از هر چيزى آگاه است.'),
(37, 2, 30, 'و چون پروردگارت به فرشتگان گفت: من در زمين خليفه‌اى مى آفرينم، گفتند: آيا كسى را مى‌آفرينى كه در آنجا فساد كند و خونها بريزد، و حال آنكه ما به ستايش تو تسبيح مى‌گوييم و تو را تقديس مى كنيم؟ گفت: من آن دانم كه شما نمى‌دانيد.'),
(38, 2, 31, 'و نامها را به تمامى به آدم بياموخت. سپس آنها را به فرشتگان عرضه كرد. و گفت: اگر راست مى‌گوييد مرا به نامهاى اينها خبر دهيد.'),
(39, 2, 32, 'گفتند: منّزهى تو. ما را جز آنچه خود به ما آموخته‌اى دانشى نيست. تويى داناى حكيم.'),
(40, 2, 33, 'گفت: اى آدم، آنها را از نامهايشان آگاه كن. چون از آن نامها آگاهشان كرد، خدا گفت: آيا به شما نگفتم كه من نهان آسمانها و زمين را مى‌دانم، و بر آنچه آشكار مى‌كنيد و پنهان مى‌داشتيد آگاهم؟'),
(41, 2, 34, 'و به فرشتگان گفتيم: آدم را سجده كنيد. همه سجده كردند جز ابليس، كه سرباز زد و برترى جست. و او از كافران بود.'),
(42, 2, 35, 'و گفتيم: اى آدم، خود و زنت در بهشت جاى گيريد. و هر چه خواهيد، و هر جا كه خواهيد، از ثمرات آن به خوشى بخوريد. و به اين درخت نزديك مشويد، كه به زمره ستمكاران درآييد.'),
(43, 2, 36, 'پس شيطان آن دو را به خطا واداشت، و از بهشتى كه در آن بودند بيرون راند. گفتيم: پايين رويد، برخى دشمن برخى ديگر، و قرارگاه و جاى برخوردارى شما تا روز قيامت در زمين باشد.'),
(44, 2, 37, 'آدم از پروردگارش كلمه‌اى چند فرا گرفت. پس خدا توبه او را بپذيرفت، زيرا توبه‌پذير و مهربان است.'),
(45, 2, 38, 'گفتيم: همه از بهشت فرو شويد؛ پس اگر از جانب من راهنمايى برايتان آمد، بر آنها كه از راهنمايى من پيروى كنند بيمى نخواهد بود و خود اندوهناك نمى‌شوند.'),
(46, 2, 39, 'كسانى كه كافر شوند، و آيات خدا را تكذيب كنند خود اهل جهنّمند و جاودانه در آنجا خواهند بود.'),
(47, 2, 40, 'اى بنى اسرائيل، نعمتى را كه بر شما ارزانى داشتم به ياد بياوريد. و به عهد من وفا كنيد تا به عهدتان وفا كنم. و از من بترسيد.'),
(48, 2, 41, 'و به آنچه نازل كرده‌ام و كتاب شما را تصديق مى‌كند ايمان بياوريد، و از نخستين كسانى كه انكارش مى‌كنند مباشيد. و آيات مرا به بهاى اندك مفروشيد و از من بيمناك باشيد.'),
(49, 2, 42, 'حق را به باطل مياميزيد و با آنكه حقيقت را مى‌دانيد، كتمانش مكنيد.'),
(50, 2, 43, 'و نماز را برپاى داريد و زكات بدهيد و با ركوع‌كنندگان ركوع كنيد.'),
(51, 2, 44, 'آيا در حالى كه كتاب را مى‌خوانيد، مردم را به نيكى فرمان مى‌دهيد و خود را فراموش مى‌كنيد؟ آيا به عقل درنمى‌يابيد؟'),
(52, 2, 45, 'از شكيبايى و نماز يارى جوييد. و اين دو، كارى دشوارند، جز براى اهل خشوع:'),
(53, 2, 46, 'آنان كه بى‌گمان مى‌دانند كه با پروردگار خود ديدار خواهند كرد و نزد او باز مى‌گردند.'),
(54, 2, 47, 'اى بنى اسرائيل، نعمتى را كه بر شما ارزانى داشتم و شما را بر جهانيان برترى دادم به ياد بياوريد.'),
(55, 2, 48, 'و بترسيد از روزى كه هيچ كس ديگرى را به كار نيايد و هيچ شفاعتى از كسى پذيرفته نگردد و از كسى عوضى نستانند و كسى را يارى نكنند.'),
(56, 2, 49, 'و به ياد آريد آنگاه كه شما را از فرعونيان رهانيديم: شما را شكنجه‌هاى سخت مى‌كردند، پسرانتان را مى‌كشتند و زنانتان را زنده مى گذاشتند. و در اين آزمونى بزرگ از سوى پروردگارتان بود.'),
(57, 2, 50, 'و آن هنگام را كه دريا را برايتان شكافتيم و شما را رهانيديم و فرعونيان را در برابر چشمانتان غرقه ساختيم.'),
(58, 2, 51, 'و آن هنگام را كه چهل شب با موسى وعده نهاديم و شما كه ستمكاران بوديد، بعد از او گوساله را پرستيديد.'),
(59, 2, 52, 'پس گناهانتان را عفو كرديم، باشد كه سپاسگزار باشيد.'),
(60, 2, 53, 'و به ياد آريد آن هنگام را كه به موسى كتاب و فرقان داديم، باشد كه هدايت شويد.'),
(61, 2, 54, 'و آن هنگام را كه موسى به قوم خود گفت: اى قوم من، شما بدان سبب كه گوساله را پرستيديد بر خود ستم روا داشتيد؛ اينك به درگاه آفريدگارتان توبه كنيد و يكديگر را بكشيد، كه چنين كارى در نزد آفريدگارتان ستوده‌تر است. پس خدا توبه شما را بپذيرفت، زيرا توبه‌پذير و مهربان است.'),
(62, 2, 55, 'و آن هنگام را كه گفتيد: اى موسى، ما تا خدا را به آشكارا نبينيم به تو ايمان نمى‌آوريم. و همچنان كه مى‌نگريستيد صاعقه شما را فرو گرفت.'),
(63, 2, 56, 'و شما را پس از مردن زنده ساختيم، شايد سپاسگزار شويد.'),
(64, 2, 57, 'و ابر را سايبانتان گردانيديم و برايتان مَنّ و سَلوى فرستاديم: بخوريد از اين چيزهاى پاكيزه كه شما را روزى داده‌ايم. و آنان بر ما ستم نكردند، بلكه بر خود ستم مى‌كردند.'),
(65, 2, 58, 'و به ياد آريد آن زمان را كه به شما گفتيم: به اين قريه درآييد و از نعمتهاى آن هر چه و هر جا كه خواسته باشيد به فراوانى بخوريد. ولى سجده‌كنان از دروازه داخل شويد و بگوييد: بار گناه از ما فرو نه. تا خطاهاى شما را بيامرزيم و به پاداش نيكوكاران بيفزاييم.'),
(66, 2, 59, 'امّا ستمكاران آن سخن را ديگر كردند و بر آنان به جزاى عصيانى كه كرده بودند عذابى آسمانى فرود آورديم.'),
(67, 2, 60, 'و به ياد آريد آنگاه را كه موسى براى قوم خود آب خواست. گفتيم: عصايت را بر آن سنگ بزن. پس دوازده چشمه از آن بگشاد. هر گروهى آبشخور خود را بدانست. از روزى خدا بخوريد و بياشاميد و در روى زمين به فساد سركشى مكنيد.'),
(68, 2, 61, 'و آنگاه را كه گفتيد: اى موسى، ما بر يك نوع طعام نتوانيم ساخت، از پروردگارت بخواه تا براى ما از آنچه از زمين مى‌رويد چون سبزى و خيار و سير و عدس و پياز بروياند. موسى گفت: آيا مى‌خواهيد آنچه را كه برتر است به آنچه فروتر است بدل كنيد؟ به شهرى باز گرديد كه در آنجا هر چه خواهيد به شما بدهند. مقرر شد بر آنها خوارى و بيچارگى و با خشم خدا قرين شدند. و اين بدان سبب بود كه به آيات خدا كافر شدند و پيامبران را به ناحق كشتند و نافرمانى كردند و تجاوز ورزيدند.'),
(69, 2, 62, 'كسانى كه ايمان آوردند و كسانى كه آيين يهودان و ترسايان و صابئان را برگزيدند، اگر به خدا و روز بازپسين ايمان داشته باشند و كارى شايسته كنند، خدا به آنها پاداش نيك مى‌دهد و نه بيمناك مى شوند و نه محزون.'),
(70, 2, 63, 'و به ياد آريد آن زمان را كه با شما پيمان بستيم و كوه طور را بر فراز سرتان بداشتيم. آنچه را كه به شما داده‌ايم با اطمينان بگيريد و آنچه را كه در آن است به خاطر بسپاريد. باشد كه پرهيزگار شويد.'),
(71, 2, 64, 'ولى زان پس از فرمان سرباز زديد و اگر فضل و رحمت خدا نبود از زيانكاران مى‌بوديد.'),
(72, 2, 65, 'و شناخته‌ايد آن گروه را كه در آن روز شنبه از حد خود تجاوز كردند، پس به آنها خطاب كرديم: بوزينگانى خوار و خاموش گرديد.'),
(73, 2, 66, 'و آنها را عبرت معاصران و آيندگان و اندرزى براى پرهيزگاران گردانيديم.'),
(74, 2, 67, 'و به ياد آريد آن هنگام را كه موسى به قوم خود گفت: خدا فرمان مى دهد كه گاوى را بكشيد. گفتند: آيا ما را به ريشخند مى‌گيرى؟ گفت: به خدا پناه مى‌برم اگر از نادانان باشم.'),
(75, 2, 68, 'گفتند: براى ما پروردگارت را بخوان تا بيان كند كه آن چگونه گاوى است؟ گفت: مى‌گويد: گاوى است نه سخت پير و از كارافتاده، نه جوان و كارناكرده، ميانسال. اكنون بكنيد آنچه شما را مى‌فرمايند.'),
(76, 2, 69, 'گفتند: براى ما پروردگارت را بخوان تا بگويد كه رنگ آن چيست؟ گفت: مى‌گويد: گاوى است به زرد تند كه رنگش بينندگان را شادمان مى سازد.'),
(77, 2, 70, 'گفتند: براى ما پروردگارت را بخوان تا بگويد آن چگونه گاوى است؟ كه آن گاو بر ما مشتبه شده است، و اگر خدا بخواهد ما بدان راه مى يابيم.'),
(78, 2, 71, 'گفت: مى‌گويد: از آن گاوان نيست كه رام باشد و زمين را شخم زند و كِشته را آب دهد. بى‌عيب است و يكرنگ. گفتند: اكنون حقيقت را گفتى. پس آن را كشتند، هر چند كه نزديك بود كه از آن كار سرباز زنند.'),
(79, 2, 72, 'و به ياد آريد آن هنگام را كه كسى را كشتيد و بر يكديگر بهتان زديد و پيكار درگرفتيد و خدا آنچه را كه پنهان مى‌كرديد آشكار ساخت.'),
(80, 2, 73, 'سپس گفتيم: پاره‌اى از آن را بر آن كشته بزنيد. خدا مردگان را اينچنين زنده مى‌سازد، و نشانه‌هاى قدرت خويش را اينچنين به شما مى نماياند، باشد كه به عقل دريابيد.'),
(81, 2, 74, 'پس از آن دلهاى شما چون سنگ، سخت گرديد، حتى سخت‌تر از سنگ كه از سنگ گاه جويها روان شود، و چون شكافته شود آب از آن بيرون جهد، و گاه از ترس خدا از فراز به نشيب فرو غلتد، و خدا از آنچه مى‌كنيد غافل نيست.'),
(82, 2, 75, 'آيا طمع مى‌داريد كه به شما ايمان بياورند، و حال آنكه گروهى از ايشان كلام خدا را مى‌شنيدند و با آنكه حقيقت آن را مى‌يافتند تحريفش مى‌كردند و از كار خويش آگاه بودند؟'),
(83, 2, 76, 'و چون با مؤمنان ديدار كنند، گويند: ما هم ايمان آورده‌ايم. و چون با يكديگر خلوت كنند، گويند: آيا با آنان از دانشى كه خدا به شما ارزانى داشته سخن مى‌گوييد، تا به يارى آن در نزد پروردگارتان بر شما حجت آرند؟ آيا به عقل در نمى‌يابيد؟'),
(84, 2, 77, 'آيا نمى‌دانند كه هر چه را كه پنهان مى‌دارند و هر چه را كه آشكار مى سازند، خدا مى‌داند؟'),
(85, 2, 78, 'برخى از ايشان بيسوادانى هستند كه نمى‌دانند در آن كتاب چيست، جز سخنانى كه شنيده‌اند و مى‌خوانند. اينان تنها پايبند گمان خويشند.'),
(86, 2, 79, 'پس واى بر آنهايى كه كتاب را خود به دست خود مى‌نويسند، و تا سودى اندك برند، مى‌گويند كه از جانب خدا نازل شده. پس واى بر آنها بدانچه نوشتند و واى بر آنها از سودى كه مى‌برند.'),
(87, 2, 80, 'و گفتند: آتش جز چند روزى ما را نسوزاند. بگو: آيا با خدا چنين پيمانى بسته‌ايد، تا او خلاف پيمان خود نكند؟ يا آنكه از روى نادانى چنين نسبتى به خدا مى‌دهيد؟'),
(88, 2, 81, 'آرى. آنان كه مرتكب كارى زشت شدند و گناهشان گرد بر گردشان بگرفت، اهل جهنمند و جاودانه در آن.'),
(89, 2, 82, 'و آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، اهل بهشتند و در آن جاويدان.'),
(90, 2, 83, 'به ياد آريد آن هنگام را كه از بنى اسرائيل پيمان گرفتيم كه جز خدا را نپرستيد و به پدر و مادر و خويشاوندان آن و يتيمان و درويشان نيكى كنيد و به مردمان سخن نيك گوييد و نماز بخوانيد و زكات بدهيد. ولى جز اندكى از شما، پشت كرديد و شماييد روى‌گردانندگان.'),
(91, 2, 84, 'و آن هنگام را كه با شما پيمان نهاديم كه خون هم مريزيد و يكديگر را از خان‌ومان آواره مسازيد و شما به پيمان گردن نهاديد و خود بر آن گواه هستيد.'),
(92, 2, 85, 'پس شما چنين جماعتى هستيد كه يكديگر را مى‌كشيد و گروهى از خود را از خان‌ومانشان آواره مى‌كنيد و بر ضد آنها به گناه و بيداد به همدستى يكديگر برمى خيزيد و اگر به اسارت شما درآيند در برابر آزاديشان فديه مى‌گيريد و حال آنكه بيرون راندنشان بر شما حرام بود. آيا به بعضى از كتاب ايمان مى‌آوريد و بعض ديگر را انكار مى كنيد؟ پاداش كسى كه چنين كند در دنيا جز خوارى نيست و در روز قيامت به سخت‌ترين وجهى شكنجه مى‌شود و خدا از آنچه مى‌كنيد غافل نيست.'),
(93, 2, 86, 'اينان همان كسانند كه آخرت را دادند و زندگى دنيا را خريدند. از عذابشان كاسته نگردد و كس ياريشان نكند.'),
(94, 2, 87, 'به تحقيق موسى را كتاب داديم و از پى او پيامبران فرستاديم. و به عيسى بن مريم دليلهاى روشن عنايت كرديم و او را به روح‌القدس تأييد نموديم. و هرگاه پيامبرى آمد و چيزهايى آورد كه پسند نفس شما نبود سركشى كرديد، و گروهى را دروغگو خوانديد و گروهى را كشتيد.'),
(95, 2, 88, 'گفتند: دلهاى ما در حجاب است. نه، خدا آنان را به سبب كفرى كه مى ورزند مطرود ساخته و چه اندك ايمان مى‌آورند.'),
(96, 2, 89, 'و چون ايشان را از جانب خدا كتابى آمد، و او را شناختند، هر چند كتابشان را هم تصديق كرده بود، و با آنكه زان پيش خواستار پيروزى بر كافران بودند، به او ايمان نياوردند، كه لعنت خدا بر كافران باد.'),
(97, 2, 90, 'با خود، بد معامله‌اى كردند آنگاه كه از حسد به كتاب خدا كافر شدند و از اينكه خدا فضل و كرم خويش را به هر كس از بندگان خود كه بخواهد ارزانى مى‌دارد حسد بردند و قرين خشمى افزون بر خشم ديگر شدند، و كافران را عذابى است خواركننده.'),
(98, 2, 91, 'و چون به آنها گفته شود كه به آنچه خدا نازل كرده است ايمان بياوريد، مى‌گويند: ما به آنچه بر خودمان نازل شده است ايمان مى آوريم. و به غير آن هر چند با حقيقت همراه باشد و كتابشان را هم تصديق كند، ايمان نمى‌آورند. بگو: اگر شما ايمان آورده بوديد، از چه روى پيامبران خدا را پيش از اين مى‌كشتيد؟'),
(99, 2, 92, 'موسى با دليلهاى روشن خويش به هدايت شما آمد و شما ستمكاران، پس از او به گوساله گرويديد.'),
(100, 2, 93, 'و با شما پيمان بستيم و كوه طور را بر فراز سرتان بداشتيم. اكنون آنچه را كه برايتان فرستاده‌ايم با ايمان استوار بگيريد و كلام خدا را بشنويد. گفتند: شنيديم و به كار نخواهيم بست. بر اثر كفرشان عشق گوساله در دلشان جاى گرفت. بگو: اگر بدانچه مى‌گوييد باور داريد، باورتان شما را به بدكارى وا مى‌دارد.'),
(101, 2, 94, 'بگو: اگر راست مى‌گوييد كه سراى آخرت نزد خدا ويژه شماست نه مردم ديگر، پس آرزوى مرگ كنيد.'),
(102, 2, 95, 'ولى به سبب اعمالى كه مرتكب شده‌اند، هرگز آرزوى مرگ نخواهند كرد. خدا ستمكاران را مى‌شناسد.'),
(103, 2, 96, 'آنان را از مردم ديگر، حتى مشركان، به زندگى اينجهانى حريص‌تر خواهى يافت و بعضى از كافران دوست دارند كه هزار سال در اين دنيا زيست كنند؛ و اين عمر دراز عذاب خدا را از آنان دور نخواهد ساخت، كه خدا به اعمالشان بيناست.'),
(104, 2, 97, 'به آنان كه با جبريل دشمنى مى‌ورزند، بگو: اوست كه اين آيات را به فرمان خدا بر دل تو نازل كرده است تا كتابهاى ديگر آسمانى را تصديق كند و براى مؤمنان رهنمون و بشارت باشد.'),
(105, 2, 98, 'هر كه دشمن خدا و فرشتگان او و پيامبرانش و جبريل و ميكال باشد، خدا هم دشمن كافران است.'),
(106, 2, 99, 'هر آينه كه بر تو آياتى روشن نازل كرديم. و جز فاسقان كسى منكر آنها نخواهد شد.'),
(107, 2, 100, 'آيا هر بار كه با خدا پيمانى بستند گروهى از ايشان‌پيمان شكنى كردند؟ آرى بيشترشان ايمان نخواهند آورد.'),
(108, 2, 101, 'و گروهى از اهل كتاب چون پيامبرى از جانب خدا بر آنان مبعوث شد كه به كتابشان هم گواهى مى‌داد، كتاب خدا را چنان كه گويى از آن بى‌خبرند، پس پشت افكندند.'),
(109, 2, 102, 'و از افسونى كه ديوها به روزگار پادشاهى سليمان مى‌خواندند پيروى كردند، و سليمان كافر نبود، ولى ديوها كه مردم را جادوگرى مى‌آموختند كافر بودند. و نيز آن افسون كه بر آن دو فرشته، هاروت و ماروت، در بابل نازل شد، در حالى كه آن دو به هر كس كه جادوگرى مى آموختند مى‌گفتند: كار ما فتنه است، مباد كافر شوى. و مردم از آن دو جادوهايى مى‌آموختند كه مى‌توانستند ميان زن و شوى جدايى افكنند و آنان جز به فرمان خدا به كسى زيانى نمى‌رسانيدند و آنچه مردم مى‌آموختند به آنها زيان مى‌رسانيد، نه سود. و خود مى دانستند كه خريداران آن جادو را در آخرت بهره‌اى نيست. خود را به بد چيزى فروختند، اگر مى‌دانستند.'),
(110, 2, 103, 'اگر ايمان آورده و پرهيزگار شده بودند، پاداشى كه از جانب خدا به آنها داده مى‌شد، از هر چيز ديگرى نيكوتر مى‌بود، اگر مى‌دانستند.'),
(111, 2, 104, 'اى كسانى كه ايمان آورده‌ايد، مگوييد «راعنا»، بگوييد «انظرنا». و گوش فرا داريد كه براى كافران عذابى است دردآور.'),
(112, 2, 105, 'از ميان اهل كتاب آنان كه كافر شدند، و نيز مشركان، دوست نمى‌دارند كه از جانب پروردگار به شما خيرى برسد. و حال آنكه خدا هر كس را كه بخواهد به بخشايش خويش مخصوص مى‌دارد. خدا صاحب فضلى بزرگ است.'),
(113, 2, 106, 'هيچ آيه‌اى را منسوخ يا ترك نمى‌كنيم مگر آنكه بهتر از آن يا همانند آن را مى‌آوريم. آيا نمى‌دانى كه خدا بر هر كارى تواناست؟'),
(114, 2, 107, 'آيا نمى‌دانى كه خدا فرمانرواى آسمانها و زمين است و شما را جز او يارى و ياورى نيست؟'),
(115, 2, 108, 'آيا مى‌خواهيد از پيامبر خود چيزى بپرسيد، همچنان كه قوم موسى پيش از اين از موسى پرسيده بودند؟ آن كس كه كفر را به جاى ايمان برگزيند، چون كسى است كه راه راست را گم كرده باشد.'),
(116, 2, 109, 'بسيارى از اهل كتاب با آنكه حقيقت بر آنها آشكار شده، از روى حسد، دوست دارند شما را پس از ايمان آوردنتان به كفر بازگردانند. عفو كنيد و گذشت كنيد، تا خدا فرمانش را بياورد، كه او بر هر كارى تواناست.'),
(117, 2, 110, 'نماز بگزاريد و زكات بدهيد. هر نيكى را كه پيشاپيش براى خود روانه مى‌داريد نزد خدايش خواهيد يافت. هر آينه خدا به كارهايى كه مى كنيد بيناست.'),
(118, 2, 111, 'گفتند: جز يهودان و ترسايان كسى به بهشت نمى‌رود. اين آرزوى آنهاست. بگو: اگر راست مى‌گوييد حجّت خويش بياوريد.'),
(119, 2, 112, 'آرى، هر كس كه از روى اخلاص رو به خدا كند و نيكوكار بود، پاداشش را از پروردگارش خواهد گرفت و دستخوش بيم و اندوه نمى‌شود.'),
(120, 2, 113, 'با آنكه كتاب خدا را مى‌خوانند، يهودان گفتند كه ترسايان بر حق نيند و ترسايان گفتند كه يهودان بر حق نيند. همچنين آنها كه ناآگاهند سخنى چون سخن آنان گويند. خدا در روز قيامت درباره آنچه در آن اختلاف مى‌كنند، ميانشان حكم خواهد كرد.'),
(121, 2, 114, 'كيست ستمكارتر از آنكه نگذاشت كه نام خدا در مسجدهاى خدا برده شود، و در ويران ساختن آنها كوشيد؟ روا نيست در آن مسجدها جز بيمناك و ترسان داخل شوند و نصيبشان در دنيا خوارى و در آخرت عذابى بزرگ است.'),
(122, 2, 115, 'مشرق و مغرب از آن خداست. پس به هر جاى كه رو كنيد، همان جا رو به خداست. خدا فراخ‌رحمت و داناست.'),
(123, 2, 116, 'گفتند كه خدا فرزندى گرفت. منزّه است او. بلكه هر چه در آسمانها و زمين است از آن اوست و همه فرمانبردار اويند.'),
(124, 2, 117, 'آفريننده آسمانها و زمين است. چون اراده چيزى كند، مى‌گويد: موجود شو. و آن چيز موجود مى‌شود.'),
(125, 2, 118, 'نادانان گفتند: چرا خدا با ما سخن نمى‌گويد؟ يا معجزه‌اى بر ما نمى‌آيد؟ پيشينيانشان نيز چنين سخنانى مى‌گفتند. دلهايشان همانند يكديگر است. ما براى آنان كه به يقين رسيده‌اند، آيات را بيان كرده‌ايم.'),
(126, 2, 119, 'ما تو را كه سزاوار هستى، به رسالت فرستاديم، تا مژده دهى و بيم دهى. تو مسئول دوزخيان نيستى.'),
(127, 2, 120, 'يهودان و ترسايان از تو خشنود نمى‌شوند، تا به آيينشان گردن نهى. بگو: هدايت، هدايتى است كه از جانب خدا باشد. اگر از آن پس كه خدا تو را آگاه كرده است از خواسته آنها پيروى كنى، هيچ ياور و مددكارى از جانب او نخواهى داشت.'),
(128, 2, 121, 'كسانى كه كتابشان داده‌ايم و آنچنان كه سزاوار است آن را مى خوانند، مؤمنان به آن هستند، و آنان كه به آن ايمان ندارند، زيانكاران هستند.'),
(129, 2, 122, 'اى بنى اسرائيل، از آن نعمتى كه بر شما ارزانى داشتم و شما را بر جهانيان برترى دادم، ياد كنيد.'),
(130, 2, 123, 'و بترسيد از روزى كه هيچ كس ديگرى را به كار نيايد و فديه پذيرفته نشود و شفاعت سودشان نكند و كسى به ياريشان برنخيزد.'),
(131, 2, 124, 'و پروردگار ابراهيم او را به كارى چند بيازمود و ابراهيم آن كارها را به تمامى به انجام رسانيد. خدا گفت: من تو را پيشواى مردم گردانيدم. گفت: فرزندانم را هم؟ گفت: پيمان من ستمكاران را در بر نگيرد.'),
(132, 2, 125, 'و كعبه را جايگاه اجتماع و مكان امن مردم ساختيم. مقام ابراهيم را نمازگاه خويش گيريد. ما ابراهيم و اسماعيل را فرمان داديم: خانه مرا براى طواف‌كنندگان و مقيمان و راكعان و ساجدان پاكيزه داريد.'),
(133, 2, 126, 'و ابراهيم گفت: اى پروردگار من، اين شهر را جاى امنى گردان و از مردمش آنان را كه به خدا و روز قيامت ايمان دارند، از هر ثمره روزى ساز. گفت: هر كس كه كافر شد او را اندك برخوردارى دهم، سپس به عذاب آتش دچارش گردانم، كه بد سرانجامى است.'),
(134, 2, 127, 'و چون ابراهيم و اسماعيل پايه‌هاى خانه را بالا بردند، گفتند: اى پروردگار ما، از ما بپذير، كه تو شنوا و دانا هستى.'),
(135, 2, 128, 'اى پروردگار ما، ما را فرمانبردار خويش ساز و نيز فرزندان ما را فرمانبردار خويش گردان، و مناسكمان را به ما بياموز، و توبه ما بپذير كه تو توبه‌پذيرنده و مهربان هستى.'),
(136, 2, 129, 'اى پروردگار ما، از ميانشان پيامبرى بر آنها مبعوث گردان تا آيات تو را برايشان بخواند و به آنها كتاب و حكمت بياموزد و آنها را پاكيزه سازد و تو پيروزمند و حكيم هستى.'),
(137, 2, 130, 'چه كسى از كيش ابراهيم روى برمى‌تابد جز آنكه خود را بى‌خرد ساخته باشد؟ ابراهيم را در دنيا برگزيديم و او در آخرت نيز از شايستگان است.'),
(138, 2, 131, 'و پروردگارش به او گفت: تسليم شو. گفت: من در برابر پروردگار جهانيان تسليمم.'),
(139, 2, 132, 'ابراهيم به فرزندان خود وصيّت كرد كه در برابر خدا تسليم شوند. و يعقوب به فرزندان خود گفت: اى فرزندان من، خدا براى شما اين دين را برگزيده است، مباد بميريد بى آنكه بدان گردن نهاده باشيد.'),
(140, 2, 133, 'آيا شما حاضر بوديد، آنگاه كه مرگ يعقوب فرا رسيد و به فرزندانش گفت: پس از من چه چيز را مى‌پرستيد؟ گفتند: خداى تو و خداى نياكان تو ابراهيم و اسماعيل و اسحاق را به يكتايى خواهيم پرستيد و در برابر او تسليم هستيم.'),
(141, 2, 134, 'آنها امّتهايى بوده‌اند كه اكنون درگذشته‌اند. آنچه كرده بودند از آنِ آنهاست و آنچه شما كنيد از آنِ شماست و شما را از اعمالى كه آنها مى كرده‌اند نمى‌پرسند.'),
(142, 2, 135, 'گفتند: يهودى يا نصرانى شويد تا به راه راست افتيد. بگو: ما كيش يكتاپرستى ابراهيم را برگزيديم و او مشرك نبود.'),
(143, 2, 136, 'بگوييد: ما به خدا و آياتى كه بر ما نازل شده و نيز آنچه بر ابراهيم و اسماعيل و اسحاق و يعقوب و سبطها نازل آمده و نيز آنچه به موسى و عيسى فرستاده شده و آنچه بر پيامبران ديگر از جانب پروردگارشان آمده است، ايمان آورده‌ايم. ميان هيچ يك از پيامبران فرقى نمى‌نهيم و همه در برابر خدا تسليم هستيم.'),
(144, 2, 137, 'اگر به آنچه شما ايمان آورده‌ايد، آنان نيز ايمان بياورند، هدايت يافته‌اند. امّا اگر روى برتافتند، پس با تو سرِ خلاف دارند و در برابر آنها خدا تو را كافى است كه او شنوا و داناست.'),
(145, 2, 138, 'اين رنگ خداست و رنگ چه كسى از رنگ خدا بهتر است. ما پرستندگان او هستيم.'),
(146, 2, 139, 'بگو: آيا درباره خدا با ما مجادله مى‌كنيد؟ او پروردگار ما و شماست. اعمال ما از آنِ ما و اعمال شما از آنِ شماست و ما او را به پاكدلى مى‌پرستيم.'),
(147, 2, 140, 'آيا مى‌گوييد كه ابراهيم و اسماعيل و اسحاق و يعقوب و سبطها يهودى يا نصرانى بودند؟ بگو: آيا شما آگاه تريد يا خدا؟ ستمكارتر از كسى كه گواهى خود را از خدا پنهان مى‌كند كيست؟ و خداوند از كارهايى كه مى‌كنيد غافل نيست.'),
(148, 2, 141, 'آنان امّتهايى بوده‌اند كه اكنون درگذشته‌اند. آنچه آنها كردند از آنِ آنهاست و آنچه شما مى‌كنيد از آنِ شماست و شما را از اعمال آنها نمى پرسند.'),
(149, 2, 142, 'از مردم، آنان كه كم خردند، خواهند گفت: چه چيز آنها را از قبله‌اى كه رو به روى آن مى‌ايستادند برگردانيد؟ بگو: مشرق و مغرب از آن خداست و خدا هر كس را كه بخواهد به راه راست هدايت مى كند.'),
(150, 2, 143, 'آرى چنين است كه شما را بهترين امّتها گردانيديم تا بر مردمان گواه باشيد و پيامبر بر شما گواه باشد. و آن قبله‌اى را كه رو به روى آن مى‌ايستادى دگرگون نكرديم، جز بدان سبب كه آنان را كه از پيامبر پيروى مى‌كنند از آنان كه مخالفت مى‌ورزند بازشناسيم. هر چند كه اين امر جز بر هدايت‌يافتگان دشوار مى‌نمود. خدا ايمان شما را تباه نمى‌كند، او بر مردمان مهربان و بخشاينده است.'),
(151, 2, 144, 'نگريستنت را به اطراف آسمان مى‌بينيم. تو را به سوى قبله‌اى كه مى‌پسندى مى‌گردانيم. پس روى به جانب مسجدالحرام كن. و هر جا كه باشيد روى بدان جانب كنيد. اهل كتاب مى‌دانند كه اين دگرگونى به حق و از جانب پروردگارشان بوده است. و خدا از آنچه مى‌كنيد غافل نيست.'),
(152, 2, 145, 'براى اهل كتاب هر برهان و نشانه‌اى كه بياورى از قبله تو پيروى نخواهند كرد، و تو نيز از قبله آنها پيروى نمى‌كنى، و آنها هم خود پيرو قبله يكديگر نخواهند بود. هرگاه پس از آگاهى، پى خواهشهاى ايشان بروى، از ستمكاران خواهى بود.'),
(153, 2, 146, 'اهل كتاب همچنان كه فرزندان خود را مى‌شناسند او را مى‌شناسند، ولى گروهى از ايشان در عين آگاهى حقيقت را پنهان مى‌دارند.'),
(154, 2, 147, 'آنچه از جانب خدا بر تو نازل شده، حقّ همان است؛ ترديد مكن.'),
(155, 2, 148, 'هر كسى را جانبى است كه بدان روى مى‌آورد. پس در نيكى كردن بر يكديگر سبقت گيريد. هر جا كه باشيد خدا شما را حاضر مى‌آورد، كه او بر هر كارى تواناست.'),
(156, 2, 149, 'از هر جاى كه بيرون شوى روى خود به جانب مسجدالحرام كن. حق همان است كه پروردگارت بدان امر فرموده. و خدا از كارهايتان ناآگاه نيست.'),
(157, 2, 150, 'از هر جاى كه بيرون شوى روى خويش به جانب مسجدالحرام كن. و هر جا كه باشيد روى بدان سوى كنيد تا هيچ كس را، جز ستمكاران، با شما مجادله‌اى نباشد. از ايشان مترسيد، از من بترسيد تا نعمت خويش بر شما تمام كنم، باشد كه هدايت شويد.'),
(158, 2, 151, 'همچنان كه پيامبرى از خود شما را بر شما فرستاديم تا آيات ما را برايتان بخواند و شما را پاكيزه گرداند و كتاب و حكمت آموزد و آنچه را كه نمى‌دانستيد به شما ياد دهد.'),
(159, 2, 152, 'پس مرا ياد كنيد تا شما را ياد كنم. مرا سپاس گوييد و ناسپاسى من مكنيد.'),
(160, 2, 153, 'اى كسانى كه ايمان آورده‌ايد، از شكيبايى و نماز مدد جوييد كه خدا با شكيبايان است.'),
(161, 2, 154, 'آنان را كه در راه خدا كشته مى‌شوند، مرده مخوانيد. آنها زنده‌اند و شما در نمى‌يابيد.'),
(162, 2, 155, 'البته شما را به اندكى ترس و گرسنگى و بينوايى و بيمارى و نقصان در محصول مى‌آزماييم. و شكيبايان را بشارت ده.'),
(163, 2, 156, 'كسانى كه چون مصيبتى به آنها رسيد گفتند: ما از آن خدا هستيم و به او باز مى‌گرديم.'),
(164, 2, 157, 'صلوات و رحمت پروردگارشان بر آنان باد، كه هدايت يافتگانند.'),
(165, 2, 158, 'صفا و مروه از شعاير خداست. پس كسانى كه حجّ خانه را به جاى مى‌آورند يا عُمره مى‌گزارند، اگر بر آن دو كوه طواف كنند مرتكب گناهى نشده‌اند. پس هر كه كار نيكى را به رغبت انجام دهد بداند كه خدا شكر گزارنده‌اى داناست.'),
(166, 2, 159, 'كسانى را كه دلايل روشن و هدايت‌كننده ما را، پس از آنكه در كتاب براى مردم بيانشان كرده‌ايم، كتمان مى‌كنند هم خدا لعنت مى كند و هم ديگر لعنت‌كنندگان.'),
(167, 2, 160, 'مگر آنها كه توبه كردند و به صلاح آمدند و آنچه پنهان داشته بودند آشكار ساختند، كه توبه‌شان را مى‌پذيرم و من توبه‌پذير و مهربانم.'),
(168, 2, 161, 'بر آنان كه كافر بودند و در كافرى مُردند لعنت خدا و فرشتگان و همه مردم باد.'),
(169, 2, 162, 'جاودانه در لعنتند و در عذابشان تخفيف داده نشود و لحظه‌اى مهلتشان ندهند.'),
(170, 2, 163, 'خداى شما خدايى است يكتا. خدايى جز او نيست بخشاينده و مهربان.'),
(171, 2, 164, 'در آفرينش آسمانها و زمين، و در آمد و شد شب و روز، و در كشتيهايى كه در دريا مى‌روند و مايه سود مردمند، و در بارانى كه خدا از آسمان فرو مى‌فرستد تا زمين مرده را بدان زنده سازد و جنبندگان را در آن پراكنده كند، و در حركت بادها، و ابرهاى مسخّر ميان زمين و آسمان؛ براى خردمندانى كه در مى‌يابند نشانه هاست.'),
(172, 2, 165, 'بعضى از مردم خدا را همتايانى اختيار مى‌كنند و آنها را چنان دوست مى‌دارند كه خدا را. ولى آنان كه ايمان آورده‌اند خدا را بيشتر دوست مى‌دارند. و آنگاه كه اين ستم پيشگان عذاب را ببينند دريابند كه همه قدرت از آن خداست. هر آينه خدا به سختى عقوبت مى‌كند.'),
(173, 2, 166, 'آنگاه كه پيشوايان، عذاب را بنگرند و از فرمانبران خويش بيزارى جويند و پيوند ميان ايشان گسسته گردد.'),
(174, 2, 167, 'و آن پيروان گويند: كاش بار ديگر بازمى‌گشتيم، تا آنچنان كه از ما بيزارى جسته‌اند از آنها بيزارى مى‌جستيم. خداوند كردارهايشان را اينچنين مايه حسرتشان سازد و آنان از آتش رهايى نيابند.'),
(175, 2, 168, 'اى مردم، از آن چيزهاى حلال و پاكيزه كه در زمين است بخوريد و پاى به جاى پاى شيطان مگذاريد كه دشمن آشكار شماست.'),
(176, 2, 169, 'او شما را به بدى و زشتى فرمان مى‌دهد و مى‌خواهد كه درباره خدا چيزهايى بگوييد كه بدان آگاه نيستيد.'),
(177, 2, 170, 'چون به ايشان گفته شود كه از آنچه خدا نازل كرده است پيروى كنيد، گويند: نه، ما به همان راهى مى‌رويم كه پدرانمان مى‌رفتند. حتى اگر پدرانشان بيخرد و گمراه بوده‌اند.'),
(178, 2, 171, 'مَثَل كافران، مثَل حيوانى است كه كسى در گوش او آواز كند، و او جز بانگى و آوازى نشنود. اينان كرانند، لالانند، كورانند و هيچ درنمى يابند.'),
(179, 2, 172, 'اى كسانى كه ايمان آورده‌ايد، از آن چيزهاى پاكيزه‌اى كه روزى شما كرده‌ايم، بخوريد و اگر خدا را مى‌پرستيد، سپاسش را به جاى آوريد.'),
(180, 2, 173, 'جز اين نيست كه مردار را و خون را و گوشت خوك را و آنچه را كه به هنگام ذبح نام غير خدا بر آن بخوانند، بر شما حرام كرد. اما كسى كه ناچار شود هرگاه كه بى‌ميلى جويد و از حد نگذراند، گناهى مرتكب نشده است، كه خدا آمرزنده و بخشاينده است.'),
(181, 2, 174, 'آنان كه كتابى را كه خدا نازل كرده است پنهان مى‌دارند، تا بهاى اندكى بستانند، شكمهاى خود را جز از آتش انباشته نمى‌سازند. و خدا در روز قيامت با آنها سخن نگويد و پاكشان نسازد و بهره آنها عذابى دردآور است.'),
(182, 2, 175, 'اينان گمراهى را به جاى هدايت برگزيدند و عذاب را به جاى آمرزش. چه چيز بر آتش شكيبايشان ساخته؟'),
(183, 2, 176, 'زيرا كه خدا كتاب را به حق نازل كرد و كسانى كه در كتاب خدا اختلاف مى‌كنند، در مخالفتى دور از صوابند.'),
(184, 2, 177, 'نيكى آن نيست كه روى خود به جانب مشرق و مغرب كنيد، بلكه نيكوكار كسى است كه به خدا و روز بازپسين و فرشتگان و كتاب خدا و پيامبران ايمان آورد. و مال خود را، با آنكه دوستش دارد، به خويشاوندان و يتيمان و درماندگان و مسافران و گدايان و دربندماندگان ببخشد. و نماز بگزارد و زكات بدهد. و نيز كسانى هستند كه چون عهدى مى‌بندند بدان وفا مى‌كنند. و آنان كه در بينوايى و بيمارى و به هنگام جنگ صبر مى‌كنند، اينان راستگويان و پرهيزگارانند.'),
(185, 2, 178, 'اى كسانى كه ايمان آورده‌ايد، درباره كشتگان بر شما قصاص مقرر شد: آزاد در برابر آزاد و بنده در برابر بنده و زن در برابر زن. پس هر كس كه از جانب برادر خود عفو گردد بايد كه با خشنودى از پى اداى خونبها رود و آن را به وجهى نيكو بدو پردازد. اين حكم، تخفيف و رحمتى است از جانب پروردگارتان و هر كه از آن سرباز زند، بهره او عذابى است دردآور.'),
(186, 2, 179, 'اى خردمندان، شما را در قصاص كردن زندگى است. باشد كه پروا كنيد.'),
(187, 2, 180, 'هرگاه يكى از شما را مرگ فرا رسد و مالى بر جاى گذارد، مقرر شد كه درباره پدر و مادر و خويشاوندان، از روى انصاف وصيت كند. و اين شايسته پرهيزگاران است.'),
(188, 2, 181, 'پس هر كس كه آن وصيت را بشنود و آنگاه دگرگونش سازد، گناهش بر آن كسى است كه آن را دگرگون ساخته است. خدا شنوا و داناست.'),
(189, 2, 182, 'هرگاه بيم آن رود كه وصيت‌كننده‌اى در وصيت خويش دستخوش ستم يا گناهى شده باشد، اگر كسى به اصلاح ميان آنان پردازد، مرتكب گناهى نشده است، كه خدا آمرزنده و مهربان است.'),
(190, 2, 183, 'اى كسانى كه ايمان آورده‌ايد، روزه داشتن بر شما مقرر شد، همچنان كه بر كسانى كه پيش از شما بوده‌اند مقرر شده بود، تا پرهيزگار شويد.'),
(191, 2, 184, 'روزهايى معدود. هر كس كه از شما بيمار يا در سفر باشد، به همان تعداد از روزهاى ديگر روزه بدارد. و آنان كه توانايى آن ندارند، هر روز را به اطعام مسكينى باز خرند. و هر كه به رغبت در آن بيفزايد، برايش بهتر است و اگر مى‌خواهيد بدانيد، بهتر آن است كه خود روزه بداريد.'),
(192, 2, 185, 'ماه رمضان، كه در آن براى راهنمايى مردم و بيان راه روشن هدايت و جدا ساختن حق از باطل، قرآن نازل شده است. پس هر كس كه اين ماه را دريابد، بايد كه در آن روزه بدارد. و هر كس كه بيمار يا در سفر باشد، به همان تعداد از روزهاى ديگر. خدا براى شما خواستار آسايش است نه سختى و تا آن شمار را كامل سازيد. و خدا را بدان سبب كه راهنماييتان كرده است به بزرگى ياد كنيد و سپاس گوييد.'),
(193, 2, 186, 'چون بندگان من درباره من از تو بپرسند، بگو كه من نزديكم و به نداى كسى كه مرا بخواند پاسخ مى‌دهم. پس به نداى من پاسخ دهند و به من ايمان آورند تا راه راست يابند.'),
(194, 2, 187, 'همبسترى با زنانتان در شب ماه روزه بر شما حلال شد. آنها پوشش شمايند و شما پوشش آنهاييد. خدا مى‌دانست كه شما به خويشتن خيانت مى‌ورزيد، پس توبه شما را بپذيرفت و شما را عفو كرد. اكنون با آنها همبستر شويد و آنچه را كه خدا بر شما مقرر گردانيده است انجام دهيد، و بخوريد و بياشاميد تا رشته روشن صبحدم در تاريكى شب آشكار شود. و روزه را به شب برسانيد. و چون در مسجد اعتكاف كنيد، با زنان همبستر مشويد. اينها حدود فرمان خداست، بدان نزديك مشويد. خدا آيات خود را اينچنين بيان مى‌كند، باشد كه به پرهيزگارى برسند.'),
(195, 2, 188, 'اموال يكديگر را به ناشايست مخوريد و آن را به رشوت به حاكمان مدهيد تا بدان سبب اموال گروهى ديگر را به ناحق بخوريد. و شما خود مى‌دانيد.'),
(196, 2, 189, 'از تو درباره هلالهاى ماه مى‌پرسند، بگو: براى آن است كه مردم وقت كارهاى خويش و زمان حجّ را بشناسند. و پسنديده نيست كه از پشت خانه‌ها به آنها داخل شويد، ولى پسنديده راه كسانى است كه پروا مى‌كنند و از درها به خانه‌ها درآييد و از خدا بترسيد تا رستگار شويد.'),
(197, 2, 190, 'با كسانى كه با شما جنگ مى‌كنند، در راه خدا بجنگيد و تعدى مكنيد. زيرا خدا تعدى‌كنندگان را دوست ندارد.'),
(198, 2, 191, 'هر جا كه آنها را بيابيد بكشيد و از آنجا كه شما را رانده‌اند، برانيدشان، كه فتنه از قتل بدتر است. و در مسجدالحرام با آنها مجنگيد، مگر آنكه با شما بجنگند. و چون با شما جنگيدند بكشيدشان، كه اين است پاداش كافران.'),
(199, 2, 192, 'و اگر باز ايستادند، خدا آمرزنده و مهربان است.'),
(200, 2, 193, 'با آنها بجنگيد تا ديگر فتنه‌اى نباشد و دين تنها دين خدا شود. ولى اگر از آيين خويش دست برداشتند، تجاوز جز بر ستمكاران روا نيست.'),
(201, 2, 194, 'اين ماه حرام در مقابل آن ماه حرام و شكستن ماه‌هاى حرام را قصاص است. پس هر كس بر شما تعدى كند. به همان اندازه تعدى‌اش بر او تعدى كنيد و از خدا بترسيد و بدانيد كه او با پرهيزگاران است.'),
(202, 2, 195, 'در راه خدا انفاق كنيد و خويشتن را به دست خويش به هلاكت ميندازيد و نيكى كنيد كه خدا نيكوكاران را دوست دارد.'),
(203, 2, 196, 'حج و عمره را براى خدا كامل به جاى آريد. و اگر شما را از حج بازداشتند، آن قدر كه ميسّر است قربانى كنيد و سر متراشيد تا قربانى شما به قربانگاهش برسد. اگر يكى از شما بيمار يا در سرش آزارى بود به عنوان فديه روزه بدارد يا صدقه دهد يا قربانى كند. و چون ايمن شويد، هر كه از عمره تمتّع به حج باز آيد، آن قدر كه او را ميسّر است قربانى كند. و هر كه را قربانى ميسّر نشد سه روز در حج روزه بدارد و هفت روز چون از حج باز گردد، تا ده روز كامل شود. و اين حكم براى كسى است كه از مردم مكه نباشد. از خدا بترسيد و بدانيد كه خدا به سختى عقوبت مى‌كند.'),
(204, 2, 197, 'حج در ماه‌هاى معيّنى است. هر كه در آن ماه‌ها اين فريضه را ادا كند بايد كه در اثناى آن جماع نكند و فسقى از او سر نزند و مجادله نكند. هر كار نيكى كه انجام مى‌دهيد خدا از آن آگاه است. و توشه برداريد، كه بهترين توشه‌ها پرهيزگارى است. اى خردمندان، از من بترسيد.'),
(205, 2, 198, 'اگر به هنگام حج طالب روزى خدا باشيد، مرتكب گناهى نشده‌ايد، و چون از عرفات بازگشتيد خدا را در مشعرالحرام ياد كنيد، از آن رو كه شما را هدايت كرده، در حالى كه پيش از آن گمراه بوده‌ايد.'),
(206, 2, 199, 'سپس از آنجا كه ديگر مردم بازمى‌گردند، شما نيز بازگرديد و از خدا آمرزش بخواهيد كه آمرزنده و مهربان است.'),
(207, 2, 200, 'چون مناسكتان را به جاى آورديد، همچنان كه پدران خويش را ياد مى‌كرديد -حتى بيشتر از آن- خداى را ياد كنيد. برخى از مردم مى‌گويند: اى پروردگار ما، ما را در دنيا چيزى بخش. اينان را در آخرت نصيبى نيست.'),
(208, 2, 201, 'و برخى از مردم مى‌گويند: اى پروردگار ما، ما را، هم در دنيا خيرى بخش و هم در آخرت، و ما را از عذاب آتش نگه دار.'),
(209, 2, 202, 'اينان از آنچه خواسته‌اند، بهره‌مند مى‌شوند و خدا به سرعت به حساب هر كس مى‌رسد.'),
(210, 2, 203, 'خداوند را در روزهايى چند ياد كنيد، و هر كه در دو روز شتاب كند، مرتكب گناهى نشده است. هر پرهيزگارى هم كه تأخير كند گناهى نكرده است. از خدا بترسيد و بدانيد كه همگان به پيشگاه وى گِرد مى‌آييد.'),
(211, 2, 204, 'در اين دنيا كسى است از مردم كه خدا را به درستى اعتقاد خويش گواه مى‌گيرد و تو را سخنش درباره زندگى اين دنيا به شگفت مى‌دارد، در حالى كه كينه‌توزترين دشمنان است.'),
(212, 2, 205, 'چون از نزد تو بازگردد، در زمين فساد كند و كشتزارها و دامها را نابود سازد، و خدا فساد را دوست ندارد.'),
(213, 2, 206, 'و چون به او گويند كه از خدا بترس، خودخواهى‌اش او را به گناه كشاند. جهنم، آن آرامگاه بد، او را بس باشد.'),
(214, 2, 207, 'كسى ديگر از مردم براى جستن خشنودى خدا جان خويش را فدا كند. خدا بر اين بندگان مهربان است.'),
(215, 2, 208, 'اى كسانى كه ايمان آورده‌ايد، همگان به اطاعت درآييد و پاى به جاى پاى شيطان مگذاريد كه او دشمن آشكار شماست.'),
(216, 2, 209, 'هرگاه پس از آن كه اين آيات روشن خدا به شما رسيد، در ايمان خويش لغزشى يافتيد، بدانيد كه خدا پيروزمند و حكيم است.'),
(217, 2, 210, 'آيا اينان منتظر آن هستند كه خدا با فرشتگان در زير سايبانى از ابر نزدشان بيايد و كار يكسره شود؟ حال آنكه خدا مرجع همه كارهاست.'),
(218, 2, 211, 'از بنى اسرائيل بپرس كه ايشان را چه آيات روشنى داديم. هر كس كه نعمتى را كه خدا به او عنايت كرده است دگرگون سازد، بداند كه عقوبت او سخت است.'),
(219, 2, 212, 'براى كافران زندگى اينجهانى آراسته شده است و مؤمنان را به ريشخند مى‌گيرند. آنان كه از خدا مى‌ترسند در روز قيامت بر فراز كافران هستند، و خدا هركس را كه بخواهد بى‌حساب روزى مى‌دهد.'),
(220, 2, 213, 'مردم يك امّت بودند، پس خدا پيامبران بشارت‌دهنده و ترساننده را بفرستاد، و بر آنها كتاب بر حق نازل كرد تا آن كتاب در آنچه مردم اختلاف دارند ميانشان حكم كند، ولى جز كسانى كه كتاب بر آنها نازل شده و حجّتها آشكار گشته بود از روى حسدى كه نسبت به هم مى ورزيدند در آن اختلاف نكردند. و خدا مؤمنان را به اراده خود در آن حقيقتى كه اختلاف مى‌كردند راه نمود، كه خدا هر كس را كه بخواهد به راه راست هدايت مى‌كند.'),
(221, 2, 214, 'مى‌پنداريد كه به بهشت خواهيد رفت؟ و هنوز آنچه بر سر پيشينيان شما آمده، بر سر شما نيامده؟ به ايشان سختى و رنج رسيد و متزلزل شدند، تا آنجا كه پيامبر و مؤمنانى كه با او بودند، گفتند: پس يارى خدا كى خواهد رسيد؟ بدان كه يارى خدا نزديك است.'),
(222, 2, 215, 'از تو مى‌پرسند كه چه انفاق كنند؟ بگو آنچه از مال خود انفاق مى‌كنيد، براى پدر و مادر و خويشاوندان و يتيمان و مسكينان و رهگذران باشد، و هر كار نيكى كه كنيد خدا به آن آگاه است.'),
(223, 2, 216, 'جنگ بر شما مقرر شد، در حالى كه آن را ناخوش داريد. شايد چيزى را ناخوش بداريد و در آن خير شما باشد و شايد چيزى را دوست داشته باشيد و برايتان ناپسند افتد. خدا مى‌داند و شما نمى‌دانيد.'),
(224, 2, 217, 'تو را از جنگ‌كردن در ماه حرام مى‌پرسند. بگو: جنگ‌كردن در آن ماه گناهى بزرگ است. اما بازداشتن مردم از راه حق و كافر شدن به او و مسجدالحرام و بيرون راندن مردمش از آنجا در نزد خداوند گناهى بزرگتر است، و شرك از قتل بزرگ‌تر است. آنها با شما مى‌جنگند تا اگر بتوانند شما را از دينتان بازگردانند. از ميان شما آنها كه از دين خود بازگردند و كافر بميرند، اعمالشان در دنيا و آخرت تباه شده و جاودانه در جهنّم باشند.'),
(225, 2, 218, 'كسانى كه ايمان آورده‌اند و آنان كه مهاجرت كرده‌اند و در راه خدا جهاد نموده‌اند، به رحمت خدا اميد مى‌دارند و خدا آمرزنده و مهربان است.'),
(226, 2, 219, 'تو را از شراب و قمار مى‌پرسند. بگو: در آن دو گناهى بزرگ و سودهايى است براى مردم. و گناهشان از سودشان بيشتر است. و از تو مى‌پرسند، چه چيز انفاق كنند؟ بگو: آنچه افزون آيد. خدا آيات را اينچنين براى شما بيان مى‌كند، باشد كه در كار دنيا و آخرت بينديشيد.'),
(227, 2, 220, 'تو را از يتيمان مى‌پرسند. بگو: اصلاح حالشان بهتر است. و اگر با آنها آميزش مى‌كنيد چون برادران شما باشند. خداوند تبهكار را از نيكوكار بازمى‌شناسد و اگر خواهد بر شما سخت مى‌گيرد، كه پيروزمند و حكيم است.'),
(228, 2, 221, 'زنان مشركه را تا ايمان نياورده‌اند به زنى مگيريد و كنيز مؤمنه بهتر از آزادزن مشركه است، هر چند شما را از او خوش آيد. و به مردان مشرك تا ايمان نياورده‌اند زن مؤمنه مدهيد. و بنده مؤمن بهتر از مشرك است، هر چند شما را از او خوش آيد. اينان به سوى آتش دعوت مى‌كنند و خدا به جانب بهشت و آمرزش. و آيات خود را آشكار بيان مى‌كند، باشد كه بينديشند.'),
(229, 2, 222, 'تو را از حيض زنان مى‌پرسند. بگو: حيض رنجى است. پس در ايّام حيض از آنان دورى گزينيد و به آنها نزديك مشويد تا پاك گردند. و چون پاك شدند از آنجا كه خدا فرمان داده است با ايشان نزديكى كنيد. هر آينه خدا توبه‌كنندگان و پاكيزگان را دوست دارد.'),
(230, 2, 223, 'زنانتان كشتزار شما هستند. هر جا كه خواهيد به كشتزار خود درآييد. و براى خويش از پيش چيزى فرستيد و از خدا بترسيد و بدانيد كه به نزد او خواهيد شد. و مؤمنان را بشارت ده.'),
(231, 2, 224, 'خدا را وسيله سوگندهاى خويش قرار مدهيد تا از نيكوكارى و تقوى و اصلاح در ميان مردم بازايستيد، كه خدا شنوا و داناست.'),
(232, 2, 225, 'خدا شما را به سبب سوگندهاى لغوتان بازخواست نمى‌كند، بلكه به خاطر نيّتى كه در دل نهان مى‌داريد بازخواست مى‌كند. خدا آمرزنده و بردبار است.'),
(233, 2, 226, 'براى كسانى كه سوگند مى‌خورند كه با زنان خويش نياميزند چهار ماه مهلت است. پس اگر بازآيند، خدا آمرزنده و مهربان است.'),
(234, 2, 227, 'و اگر عزم طلاق كردند، خداوند شنوا و داناست.'),
(235, 2, 228, 'بايد كه زنان مطلقه تا سه بار پاك شدن از شوهر كردن بازايستند. و اگر به خدا و روز قيامت ايمان دارند، روا نيست كه آنچه را كه خدا در رحم آنان آفريده است پنهان دارند. و در آن ايام اگر شوهرانشان قصد اصلاح داشته باشند به باز گرداندنشان سزاوارترند. و براى آنان حقوقى شايسته است همانند وظيفه‌اى كه بر عهده آنهاست ولى مردان را بر زنان مرتبتى است. و خدا پيروزمند و حكيم است.'),
(236, 2, 229, 'اين طلاق دو بار است و از آن پس يا به نيكو وجهى نگه داشتن اوست يا به نيكو وجهى رهاساختنش. و حلال نيست كه از آنچه به زنان داده‌ايد چيزى بازستانيد مگر آنكه بدانند كه حدود خدا را رعايت نمى كنند. اما هرگاه دانستيد كه آن دو حدود خدا را رعايت نمى‌كنند اگر آن، خود را از شوى باز خرد، گناهى بر آن دو نيست. اينها حدود خدا است. از آن تجاوز مكنيد كه ستمكاران از حدود خدا تجاوز مى كنند.'),
(237, 2, 230, 'پس اگر باز زن را طلاق داد ديگر بر او حلال نيست، مگر آنكه به نكاح مردى ديگر درآيد؛ و هرگاه آن مرد آن را طلاق دهد، اگر مى‌دانند كه حدود خدا را رعايت مى‌كنند رجوعشان را گناهى نيست. اينها حدود خدا است كه براى مردمى دانا بيان مى‌كند.'),
(238, 2, 231, 'هرگاه زنان را طلاق داديد و مهلتشان سرآمد، يا آنان را به نيكو وجهى نگه داريد يا به نيكو وجهى رها سازيد. و، تا بر آنها زيان برسانيد يا ستم بكنيد نگاهشان مداريد. و هر كس كه چنين كند به خويشتن ستم كرده است. و آيات خدا را به ريشخند مگيريد و از نعمتى كه خدا به شما داده است و از آيات و حكمتى كه براى موعظه شما فرستاده است ياد كنيد و از خدا بترسيد و بدانيد كه او به همه چيز آگاه است.'),
(239, 2, 232, 'و چون زنان را طلاق داديد و مهلتشان سرآمد، مانع مشويد كه به نكاح همسران خود -هرگاه كه ميانشان رضايتى حاصل شده باشد- درآيند. كسى كه از شما به خداى و روز قيامت ايمان آورده باشد اينچنين پند گيرد. و اين شما را بهتر و به پاكى نزديكتر است. خدا مى داند و شما نمى‌دانيد.'),
(240, 2, 233, 'مادرانى كه مى‌خواهند شيردادن را به فرزندان خود كامل سازند، دو سال تمام شيرشان بدهند. خوراك و لباس آنان، به وجهى نيكو، بر عهده صاحب فرزند است و هيچ كس بيش از قدرتش مكلّف نمى شود. نبايد هيچ مادرى به خاطر فرزندش زيانى ببيند و هيچ پدرى به خاطر فرزندش. و قيّم نيز چنين بر عهده دارد. و اگر پدر و مادر بخواهند با رضايت و مشاورت يكديگر فرزندشان را از شير بازگيرند مرتكب گناهى نشده‌اند. و هرگاه بخواهيد كسى ديگر را به شيردادن فرزندتان برگماريد، اگر مزدى نيكو و در خورش بپردازيد گناهى نيست. از خدا بترسيد و بدانيد كه او به كارى كه مى‌كنيد بصير و بيناست.'),
(241, 2, 234, 'كسانى كه از شما بميرند و زنانى بر جاى گذارند، آن زنان بايد كه چهار ماه و ده روز انتظار كشند؛ و چون مدّتشان به سرآمد، اگر درباره خويش كارى شايسته و در خور كنند، بر شما گناهى نيست، كه خدا به كارهايى كه مى‌كنيد آگاه است.'),
(242, 2, 235, 'و اگر به كنايت از آن زنان خواستگارى كنيد يا انديشه خود در دل نهان داريد، گناهى بر شما نيست. زيرا خدا مى‌داند كه از آنها به زودى ياد خواهيد كرد. ولى در نهان با آنان وعده منهيد، مگر آنكه به وجهى نيكو سخن گوييد؛ و آهنگ بستن نكاح مكنيد تا آن مدت به سر آيد، و بدانيد كه خدا به آنچه در دل داريد آگاه است. از او بترسيد و بدانيد كه او آمرزنده و بردبار است.'),
(243, 2, 236, 'اگر زنانى را كه با آنها نزديكى نكرده‌ايد و مهرى برايشان مقرر نداشته‌ايد طلاق گوييد، گناهى نكرده‌ايد. ولى آنها را به چيزى در خور بهره‌مند سازيد: توانگر به قدر توانش و درويش به قدر توانش. اين كارى است شايسته نيكوكاران.'),
(244, 2, 237, 'اگر برايشان مهرى معين كرده‌ايد و پيش از نزديكى طلاقشان مى گوييد، نصف آنچه مقرر كرده‌ايد بپردازيد؛ مگر آنكه ايشان خود، يا كسى كه عقد نكاح به دست اوست، آن را ببخشد. و بخشيدن شما به پرهيزگارى نزديكتر است. و فضيلت را ميان خود فراموش مكنيد، كه خدا به كارهايى كه مى‌كنيد بيناست.'),
(245, 2, 238, 'نمازها و نماز ميانين را پاس داريد و مطيعانه براى خدا قيام كنيد.'),
(246, 2, 239, 'و اگر از دشمن بيمناك بوديد، پياده يا سواره نماز كنيد. و چون ايمن شديد خدا را ياد كنيد، زيرا به شما چيزهايى آموخت كه نمى‌دانستيد.'),
(247, 2, 240, 'مردانى از شما كه مى‌ميرند و زنانى برجاى مى‌گذارند بايد كه درباره زنان خود وصيت كنند كه هزينه آنها را به مدت يك سال بدهند و از خانه اخراجشان نكنند. پس اگر خود خارج شوند، در مورد تصميمى كه به نحو شايسته‌اى براى خود مى‌گيرند گناهى بر شما نيست. و خدا غالب و حكيم است.'),
(248, 2, 241, 'براى زنان مطلّقه بهره‌اى است شايسته، چنان كه در خور مردان پرهيزگار باشد.'),
(249, 2, 242, 'خدا آياتش را براى شما اينچنين بيان مى‌كند، باشد كه تعقل كنيد.'),
(250, 2, 243, 'آيا آن هزاران تن را نديده‌اى كه از بيم مرگ، از خانه‌هاى خويش بيرون رفتند؟ سپس خدا به آنها گفت: بميريد. آنگاه همه را زنده ساخت. خدا به مردم نعمت مى‌دهد ولى بيشتر مردم شكر نعمت به جاى نمى‌آورند.'),
(251, 2, 244, 'در راه خدا جنگ كنيد و بدانيد كه خدا شنوا و داناست.'),
(252, 2, 245, 'كيست كه به خدا قرض الحسنه دهد، تا خدا بر آن چند برابر بيفزايد؟ خدا تنگدستى دهد و توانگرى بخشد و شما به سوى او باز گردانده مى‌شويد.'),
(253, 2, 246, 'آيا آن گروه از بنى اسرائيل را پس از موسى نديدى كه به يكى از پيامبران خود گفتند: براى ما پادشاهى نصب كن تا در راه خدا بجنگيم. گفت: نپنداريد كه اگر قتال برشما مقرر شود از آن سرباز خواهيد زد؟ گفتند: چرا درراه خدا نجنگيم درحالى كه ما از سرزمينمان بيرون رانده شده‌ايم و از فرزندانمان جدا افتاده‌ايم؟ و چون قتال بر آنها مقرر شد، جز اندكى، از آن روى برتافتند. خدا به ستمكاران آگاه است.'),
(254, 2, 247, 'پيغمبرشان به آنها گفت: خدا طالوت را پادشاه شما كرد. گفتند: چگونه او را بر ما پادشاهى باشد؟ ما سزاوارتر از او به پادشاهى هستيم و او را دارايى چندانى نداده‌اند. گفت: خدا او را بر شما برگزيده است و به دانش و توان او بيفزوده است، و خدا پادشاهيش را به هر كه خواهد دهد كه خدا دربرگيرنده و داناست.'),
(255, 2, 248, 'پيغمبرشان گفت كه نشان پادشاهى او اين است كه تابوتى كه سكينه پروردگارتان و باقى ميراث خاندان موسى و خاندان هارون در آن است و فرشتگانش حمل مى‌كنند، نزد شما آيد. اگر مؤمن باشيد اين براى شما عبرتى است.'),
(256, 2, 249, 'چون طالوت سپاهش را به راه انداخت، گفت: خدا شما را به جوى آبى مى‌آزمايد: هر كه از آن بخورد از من نيست، و هر كه از آن نخورد يا تنها كفى بياشامد از من است. همه جز اندكى از آن نوشيدند. چون او و مؤمنانى كه همراهش بودند از نهر گذشتند، گفتند: امروز ما را توان جالوت و سپاهش نيست. آنانى كه مى‌دانستند كه با خدا ديدار خواهند كرد، گفتند: به خواست خدا چه بسا گروه اندكى كه بر گروه بسيارى غلبه كند، كه خدا با كسانى است كه پاى مى‌فشرند.'),
(257, 2, 250, 'چون با جالوت و سپاهش رو به رو شدند، گفتند: اى پروردگار ما، بر ما شكيبايى ببار و ما را ثابت‌قدم گردان و بر كافران پيروز ساز.'),
(258, 2, 251, 'پس به خواست خدا ايشان را بشكستند و داود جالوت را بكشت و خدا به او پادشاهى و حكمت داد، و آنچه مى‌خواست به او بياموخت. و اگر خدا بعضى از مردم را به وسيله بعضى ديگر دفع نمى‌كرد، زمين تباه مى شد، ولى خدا بر جهانيان فضل و كرم خويش را ارزانى مى‌دارد.'),
(259, 2, 252, 'اين است آيات خدا كه به راستى بر تو مى‌خوانيم و تو از پيامبران هستى.'),
(260, 2, 253, 'بعضى از اين پيامبران را بر بعضى ديگر برترى داديم. خدا با بعضى سخن گفت و بعضى را به درجاتى برافراشت. و به عيسى بن مريم معجزه‌ها داديم و او را به روح القدس يارى كرديم. و اگر خدا مى خواست، مردمى كه بعد از آنها بودند، پس از آنكه حجتها بر آنان آشكار شده بود، با يكديگر قتال نمى‌كردند. ولى آنان اختلاف كردند: پاره‌اى مؤمن بودند و پاره‌اى كافر شدند. و اگر خدا مى‌خواست با هم قتال نمى‌كردند، ولى خدا هر چه خواهد مى‌كند.'),
(261, 2, 254, 'اى كسانى كه ايمان آورده‌ايد، پيش از آنكه آن روز فرا رسد، كه نه در آن خريد و فروشى باشد و نه دوستى و شفاعتى، از آنچه به شما روزى داده‌ايم انفاق كنيد. و كافران خود ستمكارانند.'),
(262, 2, 255, 'اللّه خدايى است كه هيچ خدايى جز او نيست. زنده و پاينده است. نه خواب سبك او را فرا مى‌گيرد و نه خواب سنگين. از آنِ اوست هر چه در آسمانها و زمين است. چه كسى جز به اذن او، در نزد او شفاعت كند؟ آنچه را كه پيش رو و آنچه را كه پشت سرشان است مى‌داند و به علم او جز آنچه خود خواهد، احاطه نتوانند يافت. كرسى او آسمانها و زمين را در بر دارد. نگهدارى آنها، بر او دشوار نيست. او بلندپايه و بزرگ است.'),
(263, 2, 256, 'در دين هيچ اجبارى نيست. هدايت از گمراهى مشخص شده است. پس هر كس كه به طاغوت كفر ورزد و به خداى ايمان آورد، به چنان رشته استوارى چنگ زده كه گسستنش نباشد. خدا شنوا و داناست.'),
(264, 2, 257, 'خدا ياور مؤمنان است. ايشان را از تاريكيها به روشنى مى‌برد. ولى آنان كه كافر شده‌اند طاغوت ياور آنهاست، كه آنها را از روشنى به تاريكيها مى‌كشد. اينان جهنميانند و همواره در آن خواهند بود.'),
(265, 2, 258, 'آن كسى را كه خدا به او پادشاهى ارزانى كرده بود نديدى كه با ابراهيم درباره پروردگارش محاجّه مى‌كرد؟ آنگاه كه ابراهيم گفت: پروردگار من زنده مى‌كند و مى‌ميراند. او گفت: من نيز زنده مى‌كنم و مى ميرانم. ابراهيم گفت: خدا خورشيد را از مشرق برمى‌آورد تو آن را از مغرب برآور. آن كافر حيران شد. زيرا خدا ستمكاران را هدايت نمى كند.'),
(266, 2, 259, 'يا مانند آن كس كه به دهى رسيد. دهى كه سقفهاى بناهايش فروريخته بود. گفت: از كجا خدا اين مردگان را زنده كند؟ خدا او را به مدت صد سال ميراند. آنگاه زنده‌اش كرد. و گفت: چه مدت در اينجا بوده‌اى؟ گفت: يك روز يا قسمتى از روز. گفت: نه، صد سال است كه در اينجا بوده‌اى. به طعام و آبت بنگر كه تغيير نكرده است، و به خرت بنگر، مى‌خواهيم تو را براى مردمان عبرتى گردانيم، بنگر كه استخوانها را چگونه به هم مى‌پيونديم و گوشت بر آن مى‌پوشانيم. چون قدرت خدا بر او آشكار شد، گفت: مى‌دانم كه خدا بر هر كارى تواناست.'),
(267, 2, 260, 'ابراهيم گفت: اى پروردگار من، به من بنماى كه مردگان را چگونه زنده مى‌سازى. گفت: آيا هنوز ايمان نياورده‌اى؟ گفت: بلى، و لكن مى خواهم كه دلم آرام يابد. گفت: چهار پرنده برگير و گوشت آنها را به هم بياميز، و هر جزئى از آنها را بر كوهى بنه. پس آنها را فراخوان. شتابان نزد تو مى‌آيند، و بدان كه خدا پيروزمند و حكيم است.'),
(268, 2, 261, 'مثَل آنان كه مال خود را در راه خدا انفاق مى‌كنند، مثَل دانه‌اى است كه هفت خوشه برآورد و در هر خوشه‌اى صد دانه باشد. خدا پاداش هر كه را كه بخواهد، چند برابر مى‌كند. خدا گشايش‌دهنده و داناست.'),
(269, 2, 262, 'مزد كسانى كه اموال خود را در راه خدا انفاق مى‌كنند و پس از انفاق منّتى نمى‌نهند و آزارى نمى‌رسانند با پروردگارشان است. نه بيمناك مى‌شوند و نه اندوهگين.'),
(270, 2, 263, 'گفتار نيكو و بخشايش، بهتر از صدقه‌اى است كه آزارى به دنبال داشته باشد. خدا بى‌نياز و بردبار است.'),
(271, 2, 264, 'اى كسانى كه ايمان آورده‌ايد، همانند آن كس كه اموال خود را از روى ريا و خودنمايى انفاق مى‌كند و به خدا و روز قيامت ايمان ندارد، صدقه‌هاى خويش را به منّت‌نهادن و آزار رسانيدن باطل مكنيد. مثَل او مثَل سنگ صافى است كه بر روى آن خاك نشسته باشد. به ناگاه بارانى تند فرو بارد و آن سنگ را همچنان كشت ناپذير باقى گذارد. چنين كسان از آنچه كرده‌اند سودى نمى‌برند، كه خدا كافران را هدايت نمى‌كند.'),
(272, 2, 265, 'و مثَل كسانى كه اموال خويش را براى طلب رضاى خدا از روى يقين و اعتقاد انفاق مى‌كنند، مثَل بوستانى است بر تپه‌اى، كه بارانى تند، بر آن بارد و دو چندان ميوه دهد، و اگر نه بارانى تند كه نرم بارانى بر آن بارد. خدا به كارهاى شما بيناست.'),
(273, 2, 266, 'آيا از ميان شما كسى هست كه دوست داشته باشد كه او را بوستانى از خرما و انگور بوده باشد، و جويها در پاى درختانش جارى باشد، و هر گونه ميوه‌اى دهد، و خود پير شده و فرزندانى ناتوان داشته باشد، به ناگاه گرد بادى آتشناك در آن بوستان افتد و بسوزد؟ خدا آيات خود را براى شما اينچنين بيان مى‌كند، باشد كه بينديشيد.'),
(274, 2, 267, 'اى كسانى كه ايمان آورده‌ايد، از دستاوردهاى نيكوى خويش و از آنچه برايتان از زمين رويانيده‌ايم انفاق كنيد، نه از چيزهاى ناپاك و بد، كه خود آنها را جز از روى اغماض نمى‌ستانيد. و بدانيد كه خدا بى‌نياز و ستودنى است.'),
(275, 2, 268, 'شيطان شما را از بينوايى مى‌ترساند و به كارهاى زشت وا مى‌دارد، در حالى كه خدا شما را به آمرزش خويش و افزونى وعده مى‌دهد. خدا گشايش‌دهنده و داناست.'),
(276, 2, 269, 'به هر كه خواهد حكمت عطا كند. و به هر كه حكمت عطا شده نيكى فراوان داده شده. و جز خردمندان پند نپذيرند.'),
(277, 2, 270, 'هر چه را انفاق يا نذر كرده‌ايد، خدا به آن آگاه است. و ستمكاران را هيچ يارى و مددكارى نيست.'),
(278, 2, 271, 'اگر آشكارا صدقه دهيد كارى نيكوست و اگر در نهان به بينوايان صدقه دهيد نيكوتر است و گناهان شما را مى‌زدايد. و خدا به كارهايى كه مى‌كنيد آگاه است.'),
(279, 2, 272, 'هدايت يافتن آنان بر عهده تو نيست، بلكه خداست كه هر كه را كه بخواهد هدايت مى‌كند. و هر مالى كه انفاق مى‌كنيد ثوابش از آن خود شماست و جز براى خشنودى خدا چيزى انفاق مكنيد و هر چه انفاق كنيد پاداش آن به شما مى‌رسد و بر شما ستم نخواهد شد.'),
(280, 2, 273, 'اين صدقات از آن بينوايانى است كه خود را در طاعت حق محصور كرده‌اند و در طلب قوت ناتوانند و آنچنان در پرده عفافند كه هر كه حال ايشان نداند پندارد كه از توانگرانند. آنان را از سيمايشان مى‌شناسى كه به اصرار از كسى چيزى نخواهند. و هر مالى كه انفاق مى كنيد خدا به آن آگاه است.'),
(281, 2, 274, 'آنان كه اموال خويش را در شب و روز، در پنهان و آشكار انفاق مى‌كنند، اجرشان با پروردگارشان است، نه بيمناك مى‌شوند و نه غمگين.'),
(282, 2, 275, 'آنان كه ربا مى‌خورند، در قيامت چون كسانى از قبر برمى‌خيزند كه به افسون شيطان ديوانه شده باشند. و اين به كيفر آن است كه گفتند: ربا نيز چون معامله است. در حالى كه خدا معامله را حلال و ربا را حرام كرده است. هر كس كه موعظه خدا به او رسيد و از رباخوارى باز ايستاد، خدا از گناهان پيشين او درگذرد و كارش به خدا واگذار مى‌شود. و آنان كه بدان كار بازگردند، اهل جهنّمند و جاودانه در آن خواهند بود.'),
(283, 2, 276, 'خداوند، ربا را ناچيز مى‌گرداند و صدقات را افزونى مى‌دهد و هيچ كفران‌كننده گنهكار را دوست ندارد.'),
(284, 2, 277, 'آنان كه ايمان آورده‌اند و كارهاى نيكو كرده‌اند، و نماز خوانده‌اند و زكات داده‌اند، مزدشان با پروردگارشان است. نه بيمناك مى‌شوند و نه غمگين.'),
(285, 2, 278, 'اى كسانى كه ايمان آورده‌ايد، از خدا بترسيد، و اگر ايمان آورده‌ايد، از ربا هر چه باقى مانده است رها كنيد.'),
(286, 2, 279, 'و هرگاه چنين نكنيد، پس جنگ با خدا و رسول او را اعلام كنيد. و اگر توبه كنيد، اصل سرمايه از آن شماست. در اين حال نه ستم كرده‌ايد و نه تن به ستم داده‌ايد.'),
(287, 2, 280, 'و اگر وامدار تنگدست بود، مهلتى بايد تا توانگر گردد. و اگر دانا باشيد، دانيد كه چون بر او ببخشاييد برايتان بهتر است.'),
(288, 2, 281, 'از آن روز كه به سوى خدا بازمى‌گرديد و به هر كس پاداش عملش به تمامى داده مى‌شود و ستمى نمى‌بيند، بيمناك باشيد.'),
(289, 2, 282, 'اى كسانى كه ايمان آورده‌ايد، چون وامى تا مدتى معيّن به يكديگر دهيد، آن را بنويسيد. و بايد در بين شما كاتبى باشد كه آن را به درستى بنويسد. و كاتب نبايد كه در نوشتن از آنچه خدا به او آموخته است سرپيچى كند. و مديون بايد كه بر كاتب املاء كند و از اللّه، پروردگار خود بترسد و از آن هيچ نكاهد. اگر مديون سفيه يا صغير بود يا خود املاء كردن نمى‌توانست، ولىّ او از روى عدالت املاء كند. و دو شاهد مرد به شهادت گيريد. اگر دو مرد نبود، يك مرد و دو زن كه به آنها رضايت دهيد شهادت بدهند، تا اگر يكى فراموش كرد ديگرى به يادش بياورد. و شاهدان چون به شهادت دعوت شوند، نبايد كه از شهادت خوددارى كنند. و از نوشتن مدّت دين خود، چه كوچك و چه بزرگ، ملول مشويد. اين روش در نزد خدا عادلانه‌تر است، و شهادت را استواردارنده‌تر و شك و ترديد را زايل‌كننده‌تر. و هرگاه معامله نقدى باشد اگر براى آن سندى ننويسيد مرتكب گناهى نشده‌ايد. و چون معامله‌اى كنيد، شاهدى گيريد. و نبايد به كاتب و شاهد زيانى برسد، كه اگر چنين كنيد نافرمانى كرده‌ايد. از خداى بترسيد. خدا شما را تعليم مى دهد و او بر هر چيزى آگاه است.'),
(290, 2, 283, 'هرگاه در سفر بوديد و كاتبى نيافتيد، بايد چيزى به گرو گرفته شود، و اگر كسى از شما ديگرى را امين دانست، آن كس كه امين دانسته شده امانت را بازدهد و بايد از اللّه، پروردگارش، بترسد. و شهادت را كتمان مكنيد. هر كس كه شهادت را كتمان كند، به دل گناهكار است و خدا از كارى كه مى‌كنيد آگاه است.'),
(291, 2, 284, 'از آن خداست هر چه در آسمانها و زمين است. آنچه را كه در دل داريد خواه آشكارش سازيد يا پوشيده‌اش داريد، خدا شما را بدان بازخواست خواهد كرد. پس هر كه را كه بخواهد مى‌آمرزد و هر كه را بخواهد عذاب مى‌كند. و خدا بر هر كارى تواناست.'),
(292, 2, 285, 'پيامبر، خود به آنچه از جانب پروردگارش به او نازل شده ايمان دارد. و همه مؤمنان، به خدا و فرشتگانش و كتابهايش و پيامبرانش ايمان دارند. ميان هيچ يك از پيامبرانش فرقى نمى‌نهيم. گفتند: شنيديم و اطاعت كرديم، اى پروردگار ما، آمرزش تو را خواستاريم كه سرانجام همه به سوى توست.'),
(293, 2, 286, 'خدا هيچ كس را جز به اندازه طاقتش مكلّف نمى‌كند. نيكيهاى هر كس از آن خود اوست و بديهايش از آن خود اوست. اى پروردگار ما، اگر فراموش كرده‌ايم يا خطايى كرده‌ايم، ما را بازخواست مكن. اى پروردگار ما، آن گونه كه بر امّتهاى پيش از ما تكليف گران نهادى، تكليف گران بر ما منه و آنچه را كه طاقت آن نداريم، بر ما تكليف مكن. گناه ما ببخش و ما را بيامرز و بر ما رحمت آور. تو مولاى ما هستى. پس ما را بر گروه كافران پيروز گردان.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف. لام. ميم.'),
(295, 3, 2, 'اللّه است كه هيچ خدايى جز او نيست، زنده است و پاينده است.'),
(296, 3, 3, 'اين كتاب را كه تصديق‌كننده كتابهاى پيش از آن است به حق بر تو نازل كرد، و قبل از آن تورات را و انجيل را'),
(297, 3, 4, 'براى هدايت مردم فرستاد و فرقان را نازل كرد. براى آنان كه به آيات خدا ايمان نمى‌آورند عذابى سخت مهياست، و خدا پيروزمند و انتقام گيرنده است.'),
(298, 3, 5, 'هيچ چيز در زمين و آسمان بر خدا پوشيده نيست.'),
(299, 3, 6, 'اوست كه شما را در رحم مادران به هر سان كه خواسته باشد مى‌نگارد. نيست خدايى جز او كه پيروزمند و حكيم است.'),
(300, 3, 7, 'اوست كه اين كتاب را بر تو نازل كرد. بعضى از آيه‌ها محكماتند، اين آيه‌ها ام‌الكتابند، و بعضى آيه‌ها متشابهاتند. امّا آنها كه در دلشان ميل به باطل است، به سبب فتنه‌جويى و ميل به تأويل از متشابهات پيروى مى‌كنند، در حالى كه تأويل آن را جز خداى نمى‌داند. و آنان كه قدم در دانش استوار كرده‌اند مى‌گويند: ما بدان ايمان آورديم، همه از جانب پروردگار ماست. و جز خردمندان پند نمى‌گيرند.'),
(301, 3, 8, 'اى پروردگار ما، از آن پس كه ما را هدايت كرده‌اى، دلهاى ما را به باطل متمايل مساز، و رحمت خود را بر ما ارزانى دار، كه تو بخشاينده‌اى.'),
(302, 3, 9, 'اى پروردگار ما، تو مردم را در آن روزى كه هيچ شكى در آن نيست، گرد مى‌زورى. به يقين كه خدا از وعده تخلّف نكند.'),
(303, 3, 10, 'كافران را داراييها و فرزندانشان هرگز از عذاب خدا نرهاند. آنها خود هيزم آتش جهنمند.'),
(304, 3, 11, 'به شيوه آل فرعون و پيشينيانشان آيات ما را تكذيب كردند. پس خدا آنان را به كيفر گناهانشان بازخواست كرد. و عقوبت خدا شديد است.'),
(305, 3, 12, 'به كافران بگوى: به زودى مغلوب خواهيد شد و در جهنّم، آن آرامگاه بد، گرد خواهيد آمد.'),
(306, 3, 13, 'در آن دو گروه كه به هم رسيدند، براى شما عبرتى بود: گروهى در راه خدا مى‌جنگيدند و گروهى ديگر كافر بودند. آنان را به چشم خود دو چندان خويش مى‌ديدند. خدا هر كس را كه بخواهد يارى دهد. و صاحبنظران را در اين عبرتى است.'),
(307, 3, 14, 'در چشم مردم آرايش يافته است، عشق به اميال نفسانى و دوست داشتن زنان و فرزندان و هميانهاى زر و سيم و اسبان داغ برنهاده و چارپايان و زراعت. همه اينها متاع زندگى اينجهانى هستند، در حالى كه بازگشتنگاه خوب نزد خدا است.'),
(308, 3, 15, 'بگو: آيا شما را به چيزهايى بهتر از اينها آگاه كنم؟ براى آنان كه پرهيزگارى پيشه كنند، در نزد پروردگارشان بهشتهايى است كه نهرها در آن روان است. اينان با زنان پاكيزه، در عين خشنودى خدا، جاودانه در آنجا خواهند بود. و خدا از حال بندگان آگاه است:'),
(309, 3, 16, 'كسانى كه مى‌گويند: اى پروردگار ما، ايمان آورديم. گناهان ما را بيامرز و ما را از عذاب آتش حفظ كن،'),
(310, 3, 17, 'شكيبايان و راستگويان و فرمانبرداران و انفاق‌كنندگان و آنان كه در سحرگاهان آمرزش مى‌طلبند.'),
(311, 3, 18, 'اللّه حكم كرد -و فرشتگان و دانشمندان نيز- كه هيچ خدايى بر پاى دارنده عدل جز او نيست. خدايى جز او نيست كه پيروزمند و حكيم است.'),
(312, 3, 19, 'هر آينه دين در نزد خدا دين اسلام است. و اهل كتاب راه خلاف نرفتند، مگر از آن پس كه به حقّانيّت آن دين آگاه شدند، و نيز از روى حسد. آنان كه به آيات خدا كافر شوند، بدانند كه او به زودى به حسابها خواهد رسيد.'),
(313, 3, 20, 'اگر با تو به داورى برخيزند بگوى: من و پيروانم در دين خويش به خدا اخلاص ورزيديم. به اهل كتاب و مشركان بگو: آيا شما هم به خدا اخلاص ورزيده‌ايد؟ اگر اخلاص ورزيده‌اند پس هدايت يافته‌اند و اگر رويگردان شده‌اند، بر تو تبليغ است و بس، و خدا بندگان را مى‌بيند.'),
(314, 3, 21, 'كسانى را كه به آيات خدا ايمان نمى‌آورند، و پيامبران را به ناحق مى كشند و مردمى را كه از روى عدل فرمان مى‌دهند مى‌كشند، به عذابى دردآور بشارت ده.'),
(315, 3, 22, 'اعمال اينان در دنيا و آخرت تباه شده است و هيچ ياورى ندارند.'),
(316, 3, 23, 'آيا آنان را نديدى كه از كتاب بهره‌اى يافته بودند، چون دعوت شدند تا كتاب خدا درباره آنها حكم كند گروهى از ايشان بازگشتند و اعراض كردند؟'),
(317, 3, 24, 'و اين بدان سبب بود كه مى‌گفتند: جز چند روزى آتش دوزخ به ما نرسد. و اين دروغ كه بر خود بسته بودند در دين خود فريبشان داد.'),
(318, 3, 25, 'حالشان چگونه خواهد بود در آن روز بى‌ترديد، وقتى كه همه را گرد آوريم، تا پاداش عمل هر كس داده شود، بى‌آنكه بر كسى ستمى رود؟'),
(319, 3, 26, 'بگو: بار خدايا، تويى دارنده مُلك. به هر كه بخواهى مُلك مى‌دهى و از هر كه بخواهى مُلك مى‌ستانى. هر كس را كه بخواهى عزت مى‌دهى و هر كس را كه بخواهى ذلت مى‌دهى. همه نيكيها به دست توست و تو بر هر كارى توانايى.'),
(320, 3, 27, 'از شب مى‌كاهى و به روز مى‌افزايى و از روز مى‌كاهى و به شب مى‌افزايى. زنده را از مرده بيرون مى‌آورى و مرده را از زنده. و به هر كه بخواهى بى‌حساب روزى مى‌دهى.'),
(321, 3, 28, 'نبايد مؤمنان، كافران را به جاى مؤمنان به دوستى برگزينند. پس هر كه چنين كند او را با خدا رابطه‌اى نيست. مگر اينكه از آنها بيمناك باشيد. و خدا شما را از خودش مى‌ترساند كه بازگشت به سوى اوست.'),
(322, 3, 29, 'بگو: هر چه در دل داريد، چه پنهانش كنيد و چه آشكارش سازيد، خدا به آن آگاه است. او هر چه را كه در آسمانها و زمين است مى‌داند و بر هر كارى تواناست.'),
(323, 3, 30, 'روزى كه هر كس كارهاى نيك و كارهاى بد خود را در برابر خود حاضر بيند، آرزو كند كه‌اى كاش ميان او و كردار بدش فاصله‌اى بزرگ بود. خداوند شما را از خودش مى‌ترساند. و خدا به بندگانش مهربان است.'),
(324, 3, 31, 'بگو: اگر خدا را دوست مى‌داريد از من پيروى كنيد تا او نيز شما را دوست بدارد و گناهانتان را بيامرزد، كه آمرزنده و مهربان است.'),
(325, 3, 32, 'بگو: از خدا و رسولش فرمان ببريد. پس اگر رويگردان شدند، بدانند كه خدا كافران را دوست ندارد.'),
(326, 3, 33, 'خدا آدم و نوح و خاندان ابراهيم و خاندان عمران را بر جهانيان برترى داد:'),
(327, 3, 34, 'فرزندانى بودند، برخى از نسل برخى ديگر پديد آمده. و خدا شنوا و داناست.'),
(328, 3, 35, 'و زن عمران گفت: اى پروردگار من، نذر كردم كه آنچه در شكم دارم از كار اينجهانى آزاد و تنها در خدمت تو باشد. اين نذر را از من بپذير كه تو شنوا و دانايى.'),
(329, 3, 36, 'چون فرزند خويش بزاد، گفت: اى پروردگار من، اين كه زاييده‌ام دختر است -و خدا به آنچه زاييده بود داناتر است- و پسر چون دختر نيست. او را مريم نام نهادم. او و فرزندانش را از شيطان رجيم در پناه تو مى آورم.'),
(330, 3, 37, 'پس پروردگارش آن دختر را به نيكى از او بپذيرفت. و به وجهى پسنديده پرورشش داد و زكريا را به سرپرستى او گماشت. و هر وقت كه زكريا به محراب نزد او مى‌رفت، پيش او خوردنى مى‌يافت. مى گفت: اى مريم، اينها براى تو از كجا مى‌رسد؟ مريم مى‌گفت: از جانب خدا؛ زيرا او هر كس را كه بخواهد بى‌حساب روزى مى‌دهد.'),
(331, 3, 38, 'در آنجا زكريا پروردگارش را ندا داد و گفت: اى پروردگار من، مرا از جانب خود فرزندى پاكيزه عطا كن، همانا كه تو دعا را مى‌شنوى.'),
(332, 3, 39, 'پس همچنان كه در محراب به نماز ايستاده بود، فرشتگان ندايش دادند: خدا تو را به يحيى بشارت مى‌دهد. او كلمه خدا را تصديق مى كند و خود مهترى است بيزار از زنان و پيامبرى است از شايستگان.'),
(333, 3, 40, 'گفت: اى پروردگار من، چگونه مرا پسرى باشد، در حالى كه به پيرى رسيده‌ام و زنم نازاست؟ گفت: بدان سان كه خدا هر چه بخواهد مى كند.'),
(334, 3, 41, 'گفت: اى پروردگار من، براى من نشانه‌اى پديدار كن. گفت: نشان تو اين است كه سه روز با مردم سخن نگويى مگر به اشاره؛ و پروردگارت را فراوان ياد كن و در شبانگاه و بامداد او را بستاى.'),
(335, 3, 42, 'و فرشتگان گفتند: اى مريم، خدا تو را برگزيد و پاكيزه ساخت و بر زنان جهان برترى داد؛'),
(336, 3, 43, 'اى مريم، از پروردگارت اطاعت كن و سجده كن و با نمازگزاران نماز بخوان.'),
(337, 3, 44, 'اينها از خبرهاى غيب است كه به تو وحى مى‌كنيم. وگرنه آنگاه كه قرعه زدند تا چه كسى از ميانشان عهده‌دار نگهدارى مريم شود، و آنگاه كه كارشان به نزاع كشيد، تو در نزدشان نبودى.'),
(338, 3, 45, 'فرشتگان گفتند: اى مريم، خدا تو را به كلمه خود بشارت مى‌دهد؛ نام او مسيح، عيسى پسر مريم است، در دنيا و آخرت آبرومند و از مقرّبان است.'),
(339, 3, 46, 'با مردم همچنان كه در بزرگى، در گهواره سخن مى‌گويد، و از شايستگان است.'),
(340, 3, 47, 'مريم گفت: اى پروردگار من، چگونه مرا فرزندى باشد، در حالى كه بشرى به من دست نزده است؟ گفت: بدين سان كه خدا هر چه بخواهد مى‌آفريند. چون اراده چيزى كند به او گويد موجود شو، پس موجود مى‌شود.'),
(341, 3, 48, 'خدا به او كتاب و حكمت و تورات و انجيل مى‌آموزد.'),
(342, 3, 49, 'و به رسالت بر بنى اسرائيلش مى‌فرستد كه: من با معجزه‌اى از پروردگارتان نزد شما آمده‌ام. برايتان از گل چيزى چون پرنده مى سازم و در آن مى‌دمم، به اذن خدا پرنده‌اى شود، و كور مادرزاد را و برص‌گرفته را شفا مى‌دهم. و به فرمان خدا مرده را زنده مى‌كنم. و به شما مى‌گويم كه چه خورده‌ايد و در خانه‌هاى خود چه ذخيره كرده‌ايد. اگر از مؤمنان باشيد، اينها براى شما نشانه‌هاى حقانيت من است.'),
(343, 3, 50, 'تورات حاضر را تصديق مى‌كنم و پاره‌اى از چيزهايى را كه بر شما حرام شده حلال مى‌كنم. با نشانى از پروردگارتان نزد شما آمده‌ام. از خداى بترسيد و از من اطاعت كنيد.'),
(344, 3, 51, 'اللّه پروردگار من و شماست. بپرستيدش كه راه راست اين است.'),
(345, 3, 52, 'چون عيسى دريافت كه به او ايمان نمى‌آورند، گفت: چه كسانى در راه خدا ياران منند؟ حواريان گفتند: ما ياران خداييم. به خدا ايمان آورديم. شهادت ده كه ما تسليم هستيم:'),
(346, 3, 53, 'اى پروردگار ما، به آنچه نازل كرده‌اى ايمان آورديم و از رسول پيروى كرديم، ما را در شمار گواهى‌دهندگان بنويس.'),
(347, 3, 54, 'آنان مكر كردند، و خدا هم مكر كرد، و خدا بهترين مكركنندگان است.'),
(348, 3, 55, 'آنگاه خدا گفت: اى عيسى، من تو را مى‌ميرانم، و به سوى خود برمى آورم، و از كافران دور مى‌سازم، و تا روز قيامت آنان را كه از تو پيروى كنند فراز كافران قرار خواهم داد. سپس بازگشت همه شما به سوى من است و من در آنچه اختلاف مى‌كرديد ميانتان حكم مى‌كنم.'),
(349, 3, 56, 'اما آنان را كه كافر شدند، در دنيا و آخرت به سختى عذاب خواهم كرد و آنها را مددكارانى نيست.'),
(350, 3, 57, 'اما آنان كه ايمان آوردند و كارهاى نيك كردند، خدا مزدشان را به تمامى خواهد داد. خدا ستمكاران را دوست ندارد.'),
(351, 3, 58, 'اينها كه بر تو مى‌خوانيم از آيات و اندرزهاى حكمت‌آميز است.'),
(352, 3, 59, 'مثَل عيسى در نزد خدا، چون مثَل آدم است كه او را از خاك بيافريد و به او گفت: موجود شو. پس موجود شد.'),
(353, 3, 60, 'اين سخن حق از جانب پروردگار تو است. از ترديدكنندگان مباش.'),
(354, 3, 61, 'از آن پس كه به آگاهى رسيده‌اى، هر كس كه درباره او با تو مجادله كند، بگو: بياييد تا حاضر آوريم، ما فرزندان خود را و شما فرزندان خود را، ما زنان خود را و شما زنان خود را، ما برادران خود را و شما برادران خود را. آنگاه دعا و تضرع كنيم و لعنت خدا را بر دروغگويان بفرستيم.'),
(355, 3, 62, 'همانا اين داستانى است راست و درست. جز اللّه هيچ خدايى نيست. او خدايى پيروزمند و حكيم است.'),
(356, 3, 63, 'اگر رويگردان شوند، خداوند مفسدان را مى‌شناسد.'),
(357, 3, 64, 'بگو: اى اهل كتاب، بياييد از آن كلمه‌اى كه پذيرفته ما و شماست پيروى كنيم: آنكه جز خداى را نپرستيم و هيچ چيز را شريك او نسازيم و بعضى از ما بعضى ديگر را سواى خدا به پرستش نگيرد. اگر آنان رويگردان شدند بگو: شاهد باشيد كه ما مسلمان هستيم.'),
(358, 3, 65, 'اى اهل كتاب، چرا درباره ابراهيم مجادله مى‌كنيد، در حالى كه تورات و انجيل بعد از او نازل شده است؟ مگر نمى‌انديشيد؟'),
(359, 3, 66, 'هان اى اهل كتاب، گرفتم كه در آنچه بدان علم داريد مجادله‌تان روا باشد، چرا در آنچه بدان علم نداريد مجادله مى‌كنيد؟ در حالى كه خدا مى‌داند و شما نمى‌دانيد.'),
(360, 3, 67, 'ابراهيم نه يهودى بود نه نصرانى، بلكه حنيفى مسلمان بود. و از مشركان نبود.'),
(361, 3, 68, 'نزديك‌ترين كسان به ابراهيم همانا پيروان او و اين پيامبر و مؤمنان هستند. و خدا ياور مؤمنان است.'),
(362, 3, 69, 'طايفه‌اى از اهل كتاب دوست دارند كه شما را گمراه كنند و حال آنكه نمى‌دانند كه جز خود را گمراه نمى‌كنند.'),
(363, 3, 70, 'اى اهل كتاب، با آنكه خود به آيات خدا شهادت مى‌دهيد، چرا انكارشان مى‌كنيد؟'),
(364, 3, 71, 'اى اهل كتاب، با آنكه از حقيقت آگاهيد، چرا حق را به باطل مى آميزيد و حقيقت را كتمان مى‌كنيد؟'),
(365, 3, 72, 'طايفه‌اى از اهل كتاب گفتند: در اول روز به آنچه بر مؤمنان نازل شده است ايمان بياوريد و در آخر روز انكارش كنيد، تا مگر از اعتقاد خويش باز گردند.'),
(366, 3, 73, 'و گفتند: جز پيروان دين خود را تصديق مكنيد. بگو: هدايت، هدايت خدايى است. و اگر گويند كه به ديگران همان چيزهايى ارزانى شود كه به شما ارزانى شده است، يا اگر گويند كه فردا در نزد پروردگارتان با شما به حجت مى‌ايستند، بگو: فضيلت به دست خداست، به هر كه خواهد آن را عطا مى‌كند، كه او بخشاينده و داناست.'),
(367, 3, 74, 'هر كه را بخواهد خاص رحمت خود مى‌كند و خدا صاحب كرمى است بس بزرگ.'),
(368, 3, 75, 'از ميان اهل كتاب كسى است كه اگر او را امين شمرى و قنطارى به او بسپارى آن را به تو باز مى‌گرداند، و از ايشان كسى است كه اگر امينش شمرى و دينارى به او بسپارى جز به تقاضا و مطالبت آن را بازنگرداند. زيرا مى‌گويد: راه اعتراض مردم مكه بر ما بسته است و كس ما را ملامت نكند. اينان خود مى‌دانند كه به خدا دروغ مى‌بندند.'),
(369, 3, 76, 'آرى هر كس كه به عهد خويش وفا كند و پرهيزگار باشد، خدا پرهيزگاران را دوست مى‌دارد.'),
(370, 3, 77, 'كسانى كه عهد خدا و سوگندهاى خود را به بهايى اندك مى فروشند در آخرت نصيبى ندارند و خدا در روز قيامت نه با آنان سخن مى‌گويد و نه به آنان مى‌نگرد و نه آنان را پاكيزه مى‌سازد. و برايشان عذابى دردآور است.'),
(371, 3, 78, 'و از ميان ايشان گروهى هستند كه به شيوه كتاب خدا سخن مى گويند، تا پنداريد كه آنچه مى‌گويند از كتاب خداست، در حالى كه از كتاب خدا نيست. و مى‌گويند كه از جانب خدا آمده و حال آنكه از جانب خدا نيامده است. و خود مى‌دانند كه بر خدا دروغ مى‌بندند.'),
(372, 3, 79, 'نسزد هيچ بشرى را كه خدا به او كتاب و حكمت و نبوت داده باشد، آنگاه به مردم بگويد كه بندگان من باشيد نه بندگان خدا. و حال آنكه پيامبران مى‌گويند: همچنان كه از كتاب خدا مى‌آموزيد و در آن مى خوانيد، پرستندگان خدا باشيد.'),
(373, 3, 80, 'و نيز به شما نمى‌فرمايد كه فرشتگان و پيامبران را خدايان پنداريد. آيا پس از آنكه اسلام آورده‌ايد شما را به كفر فرمان مى‌دهد؟'),
(374, 3, 81, 'و خدا از پيامبران پيمان گرفت كه شما را كتاب و حكمت داده‌ام؛ به پيامبرى كه آيين شما را تصديق مى‌كند و به رسالت نزد شما مى‌آيد بگرويد و يارى‌اش كنيد. گفت: آيا اقرار كرديد و عهد مرا پذيرفتيد؟ گفتند: اقرار كرديم. گفت: پس شهادت دهيد و من نيز با شما از شاهدانم.'),
(375, 3, 82, 'و هر كه از آن پس سرپيچى كند از فاسقان است.'),
(376, 3, 83, 'آيا دينى جز دين خدا مى‌جويند، حال آنكه آنچه در آسمانها و زمين است خواه و ناخواه تسليم فرمان او هستند و به نزد او بازگردانده مى شويد.'),
(377, 3, 84, 'بگو: به خدا و آنچه بر ما و بر ابراهيم و اسماعيل و اسحاق و يعقوب و فرزندان او و نيز آنچه بر موسى و عيسى و پيامبران ديگر از جانب پروردگارشان نازل شده است، ايمان آورديم. ميان هيچ يك از ايشان فرقى نمى‌نهيم و همه تسليم اراده او هستيم.'),
(378, 3, 85, 'و هر كس كه دينى جز اسلام اختيار كند از او پذيرفته نخواهد شد و در آخرت از زيانديدگان خواهد بود.'),
(379, 3, 86, 'چگونه خدا هدايت كند قومى را كه نخست ايمان آوردند و به حقانيت رسول شهادت دادند و دلايل و آيات روشن را مشاهده كردند، سپس كافر شدند؟ خدا ستمكاران را هدايت نمى‌كند.'),
(380, 3, 87, 'جزايشان اين است كه لعنت خدا و فرشتگان و همه مردم بر آنان است.'),
(381, 3, 88, 'جاويد در لعنت بمانند و از عذابشان كاسته نشود و مهلتشان ندهند.'),
(382, 3, 89, 'مگر آنان كه از آن پس توبه كنند و به صلاح آيند، زيرا خدا آمرزنده و مهربان است.'),
(383, 3, 90, 'توبه كسانى كه پس از ايمان آوردن كافر شدند و بر كفر خود افزودند، پذيرفته نخواهد شد. اينان گمراهانند.'),
(384, 3, 91, 'آنان كه كافر شدند و كافر مردند، اگر بخواهند به اندازه همه زمين طلا دهند و خويشتن را از عذاب باز خرند، از آنها پذيرفته نخواهد شد. برايشان عذابى دردناك است و ايشان را هيچ ياورى نيست.'),
(385, 3, 92, 'نيكى را در نخواهيد يافت تا آنگاه كه از آنچه دوست مى‌داريد انفاق كنيد. و هر چه انفاق مى‌كنيد خدا بدان آگاه است.'),
(386, 3, 93, 'همه طعامها بر بنى اسرائيل حلال بود، مگر آنها كه پيش از نزول تورات، اسرائيل بر خود حرام كرده بود. بگو: اگر راست مى‌گوييد تورات را بياوريد و بخوانيد.'),
(387, 3, 94, 'از آن پس هر كه بر خدا دروغ بندد از ستمكاران است.'),
(388, 3, 95, 'بگو: خدا راست مى‌گويد. آيين حنيف ابراهيم را پيروى كنيد و او از مشركان نبود.'),
(389, 3, 96, 'نخستين خانه‌اى كه براى مردم بنا شده همان است كه در مكه است. خانه‌اى كه جهانيان را سبب بركت و هدايت است.'),
(390, 3, 97, 'در آنجاست آيات روشن و مقام ابراهيم. و هر كه بدان داخل شود ايمن است. براى خدا، حج آن خانه بر كسانى كه قدرت رفتن به آن را داشته باشند واجب است. و هر كه راه كفر پيش گيرد، بداند كه خدا از جهانيان بى‌نياز است.'),
(391, 3, 98, 'بگو: اى اهل كتاب، چرا آيات خدا را انكار مى‌كنيد؟ خدا ناظر بر اعمال شماست.'),
(392, 3, 99, 'بگو: اى اهل كتاب، به چه سبب آنها را كه ايمان آورده‌اند از راه خدا بازمى‌داريد و مى‌خواهيد كه به راه كج روند؟ و شما خود به زشتى كار خويش آگاهيد و خدا نيز از آنچه مى‌كنيد غافل نيست.'),
(393, 3, 100, 'اى كسانى كه ايمان آورده‌ايد، اگر از گروهى از اهل كتاب اطاعت كنيد شما را از ايمانتان به كفر باز مى‌گردانند.'),
(394, 3, 101, 'چگونه كافر مى‌شويد در حالى كه آيات خدا بر شما خوانده مى شود و رسول او در ميان شماست؟ و هر كه به خدا تمسك جويد به راه راست هدايت شده است.'),
(395, 3, 102, 'اى كسانى كه ايمان آورده‌ايد، آنچنان كه شايسته ترس از خداست از او بترسيد و جز در مسلمانى نميريد.'),
(396, 3, 103, 'و همگان دست در ريسمان خدا زنيد و پراكنده مشويد و از نعمتى كه خدا بر شما ارزانى داشته است ياد كنيد: آن هنگام كه دشمن يكديگر بوديد و او دلهايتان را به هم مهربان ساخت و به لطف او برادر شديد. و بر لبه پرتگاهى از آتش بوديد، خدا شما را از آن برهانيد. خدا آيات خود را براى شما اينچنين بيان مى‌كند، شايد هدايت يابيد.'),
(397, 3, 104, 'بايد كه از ميان شما گروهى باشند كه به خير دعوت كنند و امر به معروف و نهى از منكر كنند. اينان رستگارانند.'),
(398, 3, 105, 'همانند آن كسان مباشيد كه پس از آنكه آيات روشن خدا بر آنها آشكار شد، پراكنده گشتند و با يكديگر اختلاف ورزيدند، البته براى اينان عذابى بزرگ خواهد بود.'),
(399, 3, 106, 'آن روز كه گروهى سپيدروى و گروهى سيه‌روى شوند به آنان كه سيه‌روى شده‌اند مى‌گويند: آيا شما پس از ايمان آوردنتان كافر شديد؟ به سبب كافر شدنتان بچشيد عذاب خدا را.'),
(400, 3, 107, 'اما آنان كه سپيدروى شده‌اند، همواره غرق در رحمت پروردگار باشند.'),
(401, 3, 108, 'اينها آيات خداست كه به حق بر تو فرو مى‌خوانيم. و خدا به مردم جهان ستم روا نمى‌دارد.'),
(402, 3, 109, 'از آن خداست هر چه در آسمانها و زمين است و كارها بدو بازگردانيده شود.'),
(403, 3, 110, 'شما بهترين امتى هستيد از ميان مردم پديد آمده، كه امر به معروف و نهى از منكر مى‌كنيد و به خدا ايمان داريد. اگر اهل كتاب نيز ايمان بياورند برايشان بهتر است. بعضى از ايشان مؤمنند ولى بيشترين تبهكارانند.'),
(404, 3, 111, 'به شما جز اندك آزار، ديگر آسيبى نرسانند. اگر با شما كارزار كنند پشت كنند و به هزيمت روند و پيروز نگردند.'),
(405, 3, 112, 'هر جا كه باشند مهر خوارى بر آنها زده شده است، مگر آنكه در امان خدا و در امان مردم باشند. و با خشم خدا قرين شده‌اند و مهر بدبختى بر آنها نهاده‌اند، زيرا به آيات خدا كافر شدند و پيامبران را به ناحق كشتند. و اين بدان سبب بود كه عصيان ورزيدند و تجاوز كردند.'),
(406, 3, 113, 'اهل كتاب همه يكسان نيستند. گروهى به طاعت خدا ايستاده‌اند و آيات خدا را در دل شب تلاوت مى‌كنند و سجده به جاى مى‌آورند.'),
(407, 3, 114, 'و به خدا و روز رستاخيز ايمان دارند و امر به معروف و نهى از منكر مى‌كنند و در كارهاى نيك شتاب مى‌ورزند و از جمله صالحانند.'),
(408, 3, 115, 'و هر كار نيك كه كنند ناديده انگاشته نشود، زيرا خدا به پرهيزگاران آگاه است.'),
(409, 3, 116, 'كافران را اموال و اولادشان هيچ از عذاب خدا نرهاند و آنان اهل آتشند جاودانه در جهنم باشند.'),
(410, 3, 117, 'آنچه اينان در اين جهان انفاق مى‌كنند، همانند آن است كه تندبادى سرد بر كشتزار قومى بر خويش ستم‌كرده بوزد، و آن كشته را نابود سازد. خدا بر آنها ستم روانداشت. آنان خود بر خود ستم كردند.'),
(411, 3, 118, 'اى كسانى كه ايمان آورده‌ايد، دوست همرازى جز از همكيشان خود مگيريد، كه ديگران از هيچ فسادى در حق شما كوتاهى نمى‌كنند و خواستار رنج و مشقت شمايند. و كينه‌توزى از گفتارشان آشكار است و آن كينه كه در دل دارند بيشتر است از آنچه به زبان مى‌آورند. آيات را برايتان آشكار ساختيم، اگر به عقل دريابيد.'),
(412, 3, 119, 'آگاه باشيد كه شما آنان را دوست مى‌داريد و حال آنكه آنها شما را دوست ندارند. شما به همه اين كتاب ايمان آورده‌ايد. چون شما را ببينند گويند: ما هم ايمان آورده‌ايم. و چون خلوت كنند، از غايت كينه‌اى كه به شما دارند سرانگشت خويش به دندان گزند. بگو: در كينه خويش بميريد؛ هرآينه خدا به آنچه در دلهاست آگاه است.'),
(413, 3, 120, 'اگر خيرى به شما رسد اندوهگين شوند و اگر به مصيبتى گرفتار آييد شادمان گردند. اگر شكيبايى ورزيد و پرهيزگارى كنيد از مكرشان به شما زيانى نرسد، كه خدا بر هر كارى كه مى‌كنند احاطه دارد.'),
(414, 3, 121, 'و بامدادان از ميان كسان خويش بيرون آمدى، تا مؤمنان را در آن جايها كه مى‌بايست بجنگند بگمارى، و خدا شنوا و داناست.'),
(415, 3, 122, 'دو گروه از شما آهنگ آن كردند كه در جنگ سستى ورزند و خدا ياورشان بود، پس مؤمنان بايد كه بر خداى توكل كنند.'),
(416, 3, 123, 'هر آينه خدا شما را در بدر يارى كرد و حال آنكه ناتوان بوديد. پس، از خداى بترسيد، باشد كه سپاسگزار شويد.'),
(417, 3, 124, 'آنگاه كه به مؤمنان مى‌گفتى كه اگر خدا سه هزار فرشته به ياريتان فروفرستد، آيا شما را كافى نخواهد بود؟'),
(418, 3, 125, 'بلى، اگر پايدارى كنيد و پرهيزگار باشيد چون دشمنان تاخت آورند، خدا با پنج هزار از فرشتگان صاحب علامت شما را يارى كند.'),
(419, 3, 126, 'و خداوند اين كار را جز براى شادمانى و دلگرمى شما نكرد. و نيست ياريى مگر از سوى خداى پيروزمند و دانا،'),
(420, 3, 127, 'تا گروهى از كافران را هلاك كند، يا خوار گرداند. آنگاه نوميد بازگردند.'),
(421, 3, 128, 'يا ايشان را به توبه وادارد يا آن ستمكاران را عذاب كند و تو را در اين كارها دستى نيست.'),
(422, 3, 129, 'از آن خداست هر چه در آسمانها و زمين است. هر كه را بخواهد مى‌آمرزد و هر كه را بخواهد عذاب مى‌كند، و خدا آمرزنده و مهربان است.'),
(423, 3, 130, 'اى كسانى كه ايمان آورده‌ايد، ربا مخوريد به افزودنهاى پى در پى. و از خداى بترسيد تا رستگار شويد.'),
(424, 3, 131, 'و بترسيد از آتشى كه براى كافران مهيا شده است.'),
(425, 3, 132, 'و از خدا و رسول اطاعت كنيد تا مگر مشمول رحمت شويد.'),
(426, 3, 133, 'بر يكديگر پيشى گيريد براى آمرزش پروردگار خويش و رسيدن به آن بهشت كه پهنايش به قدر همه آسمانها و زمين است و براى پرهيزگاران مهيا شده است.'),
(427, 3, 134, 'آن كسان كه در توانگرى و تنگدستى انفاق مى‌كنند و خشم خويش فرومى‌خورند و از خطاى مردم درمى‌گذرند. خدا نيكوكاران را دوست دارد.'),
(428, 3, 135, 'و آن كسان كه چون مرتكب كارى زشت شوند يا به خود ستمى كنند، خدا را ياد مى‌كنند و براى گناهان خويش آمرزش مى‌خواهند و كيست جز خدا كه گناهان را بيامرزد؟ و چون به زشتى گناه آگاهند در آنچه مى‌كردند پاى نفشرند.'),
(429, 3, 136, 'پاداش اينان آمرزش پروردگارشان است و نيز بهشتهايى كه در آن نهرها جارى است. در آنجا جاويدانند و چه نيكو است پاداش نيكوكاران.'),
(430, 3, 137, 'پيش از شما سنتهايى بوده است، پس بر روى زمين بگرديد و بنگريد كه پايان كار آنها كه پيامبران را به دروغگويى نسبت مى‌دادند چه بوده است.'),
(431, 3, 138, 'اين براى مردم دليلى روشن و براى پرهيزگاران راهنما و اندرزى است.'),
(432, 3, 139, 'سستى مكنيد و اندوهگين مباشيد، زيرا اگر ايمان آورده باشيد شما برترى خواهيد جست.'),
(433, 3, 140, 'اگر بر شما زخمى رسيد، به آن قوم نيز همچنان زخمى رسيده است. و اين روزگار است كه هر دم آن را به مراد كسى مى‌گردانيم، تا خدا كسانى را كه ايمان آورده‌اند بشناسد و از شما گواهان گيرد. و خدا ستمكاران را دوست ندارد،'),
(434, 3, 141, 'و تا مؤمنان را پاكيزه گرداند و كافران را نابود سازد.'),
(435, 3, 142, 'آيا مى‌پنداريد كه به بهشت خواهيد رفت و حال آن كه هنوز خدا معلوم نداشته است كه از ميان شما چه كسانى جهاد مى‌كنند و چه كسانى پايدارى مى‌ورزند؟'),
(436, 3, 143, 'پيش از آنكه مرگتان فرا رسد تمناى مرگ مى‌كرديد، اينك مرگ را ديديد و در آن مى‌نگريد.'),
(437, 3, 144, 'جز اين نيست كه محمد پيامبرى است كه پيش از او پيامبرانى ديگر بوده‌اند. آيا اگر بميرد يا كشته شود، شما به آيين پيشين خود باز مى‌گرديد؟ هر كس كه بازگردد هيچ زيانى به خدا نخواهد رسانيد. خدا سپاسگزاران را پاداش خواهد داد.'),
(438, 3, 145, 'هيچ كس جز به فرمان خدا نمى‌ميرد. مدت مكتوب است. هر كس خواهان ثواب اينجهانى باشد به او مى‌دهيم و هر كس خواهان ثواب آنجهانى باشد به او مى‌دهيم و شاكران را پاداش خواهيم داد.'),
(439, 3, 146, 'چه بسا پيامبرانى كه خدا دوستان بسيار همراه آنان به جنگ رفتند. و در راه خدا، هر چه به آنها رسيد، سستى نكردند و ناتوان نشدند و سر فرود نياوردند و خدا شكيبايان را دوست دارد.'),
(440, 3, 147, 'سخنشان جز اين نبود كه مى‌گفتند: اى پروردگار ما، گناهان ما را و زياده‌رويهاى ما را در كارها بيامرز و ما را ثابت‌قدم گردان و در برابر كافران يارى كن.'),
(441, 3, 148, 'خدا پاداش اينجهانى و پاداش نيك آنجهانى را به ايشان ارزانى داشت و خدا نيكوكاران را دوست دارد.'),
(442, 3, 149, 'اى كسانى كه ايمان آورده‌ايد، اگر از كافران پيروى كنيد، شما را به آيين پيشين برمى‌گردانند، پس زيانديده بازمى‌گرديد.'),
(443, 3, 150, 'نه، يارى‌كننده شما خداوند است كه بهترين يارى‌كنندگان است.'),
(444, 3, 151, 'در دل كافران هراسى خواهيم افكند. زيرا چيزى را كه از آسمان براى آن حجتى نفرستاده است، شريك خدا گرفتند. جاى آنان جهنم است كه براى ستمكاران مكان بدى است.'),
(445, 3, 152, 'خدا به وعده‌اى كه با شما نهاده بود وفا كرد، آنگاه كه به اذن او دشمن را مى‌كشتيد. و چون غنيمتى را كه هواى آن را در سر داشتيد به شما نشان داد، سستى كرديد و در آن امر به منازعه پرداختيد و عصيان ورزيديد. بعضى خواستار دنيا شديد و بعضى خواستار آخرت. سپس تا شما را به بلايى مبتلا كند به هزيمت واداشت. اينك شما را ببخشود كه او را به مؤمنان بخشايشى است.'),
(446, 3, 153, 'آنگاه كه مى‌گريختيد و به كس نمى‌پرداختيد و پيامبر شما را از پشت سر فرامى‌خواند. پس به پاداش، غمى بر غم شما افزود. اكنون اندوه آنچه را كه از دست داده‌ايد، يا رنجى را كه به شما رسيده است، مخوريد. خدا به هر كارى كه مى‌كنيد آگاه است.'),
(447, 3, 154, 'آنگاه، پس از آن اندوه، خدا به شما ايمنى ارزانى داشت، چنان كه گروهى را خواب آرام فرو گرفت. اما گروهى ديگر كه چون مردم عصر جاهلى به خدا گمانى باطل داشتند، هنوز دستخوش اندوه خويش بودند و مى‌گفتند: آيا هرگز كار به دست ما خواهد افتاد؟ بگو: همه كارها به دست خداست. آنان در دل خود چيزى را پنهان مى‌دارند كه نمى‌خواهند براى تو آشكارش سازند. مى‌گويند: اگر ما را اختيارى بود اينجا كشته نمى‌شديم. بگو: اگر در خانه‌هاى خود هم مى بوديد، كسانى كه كشته‌شدن بر آنها مقرر شده است از خانه به قتلگاهشان بيرون مى‌رفتند. خدا آنچه را كه در سينه داريد مى‌آزمايد و دلهايتان را پاك مى‌گرداند و خدا به آنچه در دلهاست آگاه است.'),
(448, 3, 155, 'از ميان شما آنان كه در روز مقابله آن دو گروه بگريختند، به سبب پاره‌اى از اعمالشان شيطان آنها را به خطا افكنده بود. اينك خداوند عفوشان كرد كه او آمرزنده و بردبار است.'),
(449, 3, 156, 'اى كسانى كه ايمان آورده‌ايد، همانند آن كافران مباشيد كه درباره برادران خود كه به سفر يا به جنگ رفته بودند، مى‌گفتند: اگر نزد ما مانده بودند نمى‌مردند يا كشته نمى‌شدند. خدا اين پندار را چون حسرتى در دل آنها نهاد. و خداست كه زنده مى‌كند و مى‌ميراند و اوست كه اعمال شما را مى‌بيند.'),
(450, 3, 157, 'اگر در راه خدا كشته شويد يا بميريد، آمرزش و رحمت خدا از آنچه در اين جهان گرد مى‌آوريد بهتر است.'),
(451, 3, 158, 'و اگر بميريد يا كشته شويد هر آينه در پيشگاه خداوند محشور مى‌شويد.'),
(452, 3, 159, 'به سبب رحمت خداست كه تو با آنها اينچنين خوشخوى و مهربان هستى. اگر تندخو و سخت‌دل مى‌بودى از گرد تو پراكنده مى‌شدند. پس بر آنها ببخشاى و برايشان آمرزش بخواه و در كارها با ايشان مشورت كن و چون قصد كارى كنى بر خداى توكل كن، كه خدا توكل‌كنندگان را دوست دارد.'),
(453, 3, 160, 'اگر خدا شما را يارى كند، هيچ كس بر شما غلبه نخواهد كرد. و اگر شما را خوار دارد، چه كسى از آن پس ياريتان خواهد كرد؟ پس مؤمنان بايد كه بر خداى توكل كنند.'),
(454, 3, 161, 'هيچ پيامبرى خيانت نكند و هر كه به چيزى خيانت كند، آن را در روز قيامت با خود آورد. سپس جزاى عمل هر كس به تمامى داده خواهد شد و بر كسى ستمى نرود.'),
(455, 3, 162, 'آيا آن كس كه به راه خشنودى خدا مى‌رود، همانند كسى است كه موجب خشم او مى‌شود و مكان او جهنم، آن سرانجام بد است؟'),
(456, 3, 163, 'اين دو گروه را نزد خدا درجاتى است گوناگون و او به كارهايشان آگاه است.'),
(457, 3, 164, 'خدا بر مؤمنان انعام فرمود، آنگاه كه از خودشان به ميان خودشان پيامبرى مبعوث كرد تا آياتش را بر آنها بخواند و پاكشان سازد و كتاب و حكمتشان بياموزد، هر چند از آن پيش در گمراهى آشكارى بودند.'),
(458, 3, 165, 'و آيا هنگامى كه آسيبى به شما رسيد كه شما خود دوچند آن رسانده بوديد، گفتيد: اين آسيب از كجا رسيد؟ بگو: از جانب خودتان. هر آينه خدا بر هر چيزى تواناست.'),
(459, 3, 166, 'آنچه در روز برخورد آن دو گروه به شما رسيد، به اذن خدا بود، تا مؤمنان را معلوم دارد،'),
(460, 3, 167, 'و آنان را نيز كه نفاق ورزيدند، معلوم دارد. به آنها گفته مى‌شد: بياييد در راه خدا كارزار كنيد يا به دفاع پردازيد. مى‌گفتند: اگر يقين داشتيم كه جنگى درمى‌گيرد، با شما مى‌آمديم. آنان به كفر نزديكترند تا به ايمان. به زبان چيزهايى مى‌گويند كه به دل اعتقاد ندارند و خدا به آنچه در دل نهفته مى‌دارند آگاهتر است.'),
(461, 3, 168, 'به آنان كه از جنگ باز ايستادند و درباره برادران خود گفتند كه اگر سخن ما را شنيده بودند كشته نمى‌شدند، بگو: اگر راست مى‌گوييد مرگ را از خود برانيد.'),
(462, 3, 169, 'كسانى را كه در راه خدا كشته شده‌اند مرده مپندار، بلكه زنده‌اند و نزد پروردگارشان روزى داده مى‌شوند.'),
(463, 3, 170, 'از فضيلتى كه خدا نصيبشان كرده است شادمانند. و براى آنها كه در پى‌شان هستند و هنوز به آنها نپيوسته‌اند خوشدلند كه بيمى بر آنها نيست و اندوهگين نمى‌شوند.'),
(464, 3, 171, 'آنان را مژده نعمت و فضل خدا مى‌دهند و خدا پاداش مؤمنان را تباه نمى‌كند.'),
(465, 3, 172, 'از ميان آن كسان كه پس از زخم‌خوردن باز هم فرمان خدا و رسولش را اجابت كردند، آنان كه نيكوكار باشند و از خداى بترسند مزدى بزرگ دارند.'),
(466, 3, 173, 'كسانى كه مردم گفتندشان كه مردم براى جنگ با شما گرد آمده‌اند، از آنها بترسيد، و اين سخن بر ايمانشان بيفزود و گفتند: خدا ما را بسنده است و چه نيكو ياورى است.'),
(467, 3, 174, 'پس از جنگ بازگشتند، در حالى كه نعمت و فضل خدا را به همراه داشتند و هيچ آسيبى به آنها نرسيده بود. اينان به راه خشنودى خدا رفتند و خدا را بخشايشى عظيم است.'),
(468, 3, 175, 'آن شيطان است كه در دل دوستان خود بيم مى‌افكند. اگر ايمان آورده‌ايد از آنها مترسيد، از من بترسيد.'),
(469, 3, 176, 'آنان كه به كفر مى‌شتابند تو را غمگين نسازند. اينان هيچ زيانى به خدا نمى‌رسانند. خدا مى‌خواهد آنها را در آخرت بى‌بهره گرداند، و برايشان عذابى است بزرگ.'),
(470, 3, 177, 'هر آينه آنان كه ايمان دادند و كفر خريدند، هيچ زيانى به خدا نمى‌رسانند و برايشان عذابى دردناك است.'),
(471, 3, 178, 'كافران مپندارند كه در مهلتى كه به آنها مى‌دهيم خير آنهاست. به آنها مهلت مى‌دهيم تا بيشتر به گناهانشان بيفزايند، و براى آنهاست عذابى خواركننده.'),
(472, 3, 179, 'خدا بر آن نيست كه شما مؤمنان را بدين حال كه اكنون هستيد رها كند. مى‌آزمايد تا ناپاك را از پاك جدا سازد. و خدا بر آن نيست كه شما را از غيب بياگاهاند، ولى برخى از پيامبرانش را كه خود بخواهد برمى‌گزيند. پس به خدا و پيامبرانش ايمان بياوريد. و اگر ايمان بياوريد و پرهيزگارى كنيد، اجرى عظيم يابيد.'),
(473, 3, 180, 'آنان كه در نعمتى كه خدا به آنها عطا كرده است بخل مى‌ورزند، مپندارند كه در بخل ورزيدن برايشان خير است. نه، شر است. در روز قيامت آنچه را كه در بخشيدنش بخل مى‌ورزيدند چون طوقى به گردنشان خواهند آويخت. و از آن خداست ميراث آسمانها و زمين و او به هر كارى كه مى‌كنيد آگاه است.'),
(474, 3, 181, 'هر آينه خدا شنيد سخن آن كسان را كه مى‌گفتند: خدا بينواست و ما توانگريم. گفتارشان را و نيز اينكه پيامبران را به ناحق مى‌كشتند، خواهيم نوشت، و گوييم: بچشيد عذاب آتش سوزان را.'),
(475, 3, 182, 'اين پاداش اعمالى است كه پيشاپيش به جاى آورده‌ايد وگرنه خداوند به بندگانش ستم روا نمى‌دارد.'),
(476, 3, 183, 'كسانى گفتند: ما را با خدا عهدى است كه به هيچ پيامبرى ايمان نياوريم، مگر براى ما قربانيى بياورد كه آتش آن را بخورد. بگو: پيش از من پيامبرانى با معجزه‌ها و آنچه اكنون مى‌خواهيد آمده‌اند، اگر راست مى‌گوييد، چرا آنها را كشتيد؟'),
(477, 3, 184, 'اگر تو را تكذيب كردند، پيامبرانى هم كه پيش از تو با معجزه‌ها و نوشته‌ها و كتاب روشنگر آمده بودند تكذيب شده‌اند.'),
(478, 3, 185, 'همه كس مرگ را مى‌چشد، و به تحقيق در روز قيامت مزد اعمال شما را به كمال خواهند داد. و هر كس را از آتش دور سازند و به بهشت درآورند به پيروزى رسيده است. و اين زندگى دنيا جز متاعى فريبنده نيست.'),
(479, 3, 186, 'شما را به مال و جان آزمايش خواهند كرد. و از زبان اهل كتاب و مشركان آزار فراوان خواهيد شنيد. اگر شكيبايى كنيد و پرهيزگار باشيد نشان قدرت اراده شماست.'),
(480, 3, 187, 'خدا از اهل كتاب پيمان گرفت كه كتاب خدا را براى مردم آشكار سازند و پنهانش مكنند؛ ولى آنها پس پشتش افكندند و در مقابل، بهاى اندكى گرفتند. چه بد معامله‌اى كردند.'),
(481, 3, 188, 'آنان را كه از كارهايى كه كرده‌اند شادمان شده‌اند، و دوست دارند به سبب كارهاى ناكرده خويش هم مورد ستايش قرار گيرند، مپندار كه در پناهگاهى دور از عذاب خدا باشند. برايشان عذابى دردآور مهياست.'),
(482, 3, 189, 'از آن خداست فرمانروايى آسمانها و زمين و خدا بر هر چيزى تواناست.'),
(483, 3, 190, 'هر آينه در آفرينش آسمانها و زمين و آمد و شد شب و روز، خردمندان را عبرتهاست:'),
(484, 3, 191, 'آنان كه خدا را ايستاده و نشسته و به پهلو خفته، ياد مى‌كنند و در آفرينش آسمانها و زمين مى‌انديشند: اى پروردگار ما، اين جهان را به بيهوده نيافريده‌اى، تو منزهى، ما را از عذاب آتش بازدار.'),
(485, 3, 192, 'اى پروردگار ما، هر كس را كه به آتش افكنى رسوايش كرده‌اى و ظالمان را هيچ ياورى نيست.'),
(486, 3, 193, 'اى پروردگار ما، شنيديم كه مناديى به ايمان فرا مى‌خواند كه به پروردگارتان ايمان بياوريد. و ما ايمان آورديم. پس، اى پروردگار ما، گناهان ما را بيامرز و بديهاى ما را از ما بزداى و ما را با نيكان بميران.'),
(487, 3, 194, 'اى پروردگار ما، عطا كن به ما آنچه را كه به زبان پيامبرانت به ما وعده داده‌اى و ما را در روز قيامت رسوا مكن كه تو وعده خويش خلاف نمى‌كنى.'),
(488, 3, 195, 'پروردگارشان دعايشان را اجابت فرمود كه: من كار هيچ كارگزارى را از شما، چه زن و چه مرد -همه از يكديگريد- ناچيز نمى‌سازم. پس گناهان كسانى را كه مهاجرت كرده‌اند و از خانه‌هايشان رانده شده‌اند و در راه من آزار ديده‌اند و جنگيده‌اند و كشته شده‌اند، مى‌زدايم و آنان را در بهشتهايى كه در آن نهرها جارى است داخل مى‌كنم. اين پاداشى است از جانب خدا و پاداش نيكو نزد خداست.'),
(489, 3, 196, 'جولان كافران در شهرها تو را نفريبد.'),
(490, 3, 197, 'اين برخوردارى اندكى است. پس از آن جايگاهشان جهنم است و جهنم بد آرامگاهى است.'),
(491, 3, 198, 'اما براى آنان كه از پروردگارشان مى‌ترسند، بهشتهايى است كه در آن نهرها جارى است. همواره در آنجا مهمان خدا هستند. و آنچه نزد خداست براى نيكان بهتر است.'),
(492, 3, 199, 'بعضى از اهل كتاب به خدا و كتابى كه بر شما نازل شده و كتابى كه بر خودشان نازل شده است ايمان دارند. مطيع فرمان خدايند. آيات خدا را به بهاى اندك نمى‌فروشند. مزد ايشان نزد پروردگارشان است. هر آينه خدا حسابها را زود خواهد رسيد.'),
(493, 3, 200, 'اى كسانى كه ايمان آورده‌ايد، شكيبا باشيد و ديگران را به شكيبايى فراخوانيد و در جنگها پايدارى كنيد و از خدا بترسيد، باشد كه رستگار شويد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اى مردم، بترسيد از پروردگارتان، آن كه شما را از يك تن بيافريد و از آن يك تن همسر او را و از آن دو، مردان و زنان بسيار پديد آورد. و بترسيد از آن خدايى كه با سوگند به نام او از يكديگر چيزى مى‌خواهيد و زنهار از خويشاوندان مبريد. هر آينه خدا مراقب شماست.'),
(495, 4, 2, 'مال يتيمان را به يتيمان دهيد و حرام را با حلال مبادله مكنيد. و اموال آنها را همراه با اموال خويش مخوريد، كه اين گناهى بزرگ است.'),
(496, 4, 3, 'اگر شما را بيم آن است كه در كار يتيمان عدالت نورزيد، از زنان هر چه شما را پسند افتد، دو دو، و سه سه و چهار چهار به نكاح درآوريد. و اگر بيم آن داريد كه به عدالت رفتار نكنيد تنها يك زن بگيريد يا هر چه مالك آن شويد. اين راهى بهتر است تا مرتكب ستم نگرديد.'),
(497, 4, 4, 'مهر زنان را به طيب خاطر به آنها بدهيد. و اگر پاره‌اى از آن را به رضايت به شما بخشيدند بگيريد كه خوش و گوارايتان خواهد بود.'),
(498, 4, 5, 'اموالتان را كه خدا قوام زندگى شما ساخته است به دست سفيهان مدهيد، ولى از آن هزينه و لباسشان دهيد و با آنان سخن به نيكى گوييد.'),
(499, 4, 6, 'يتيمان را بيازماييد تا آنگاه كه به سن زناشويى رسند، پس اگر در آنان رشدى يافتيد اموالشان را به خودشان واگذاريد. و از بيم آنكه مباد به سن رشد رسند اموالشان را به ناحق و شتاب مخوريد. هر كه توانگر است عفت ورزد و هر كه بينواست به آن اندازه كه عرف تصديق كند بخورد. و چون اموالشان را تسليمشان كرديد كسانى را بر آنان به شهادت گيريد و خدا براى حساب كشيدن كافى است.'),
(500, 4, 7, 'از هر چه پدر و مادر و خويشاوندان به ارث مى‌گذارند، مردان را نصيبى است. و از آنچه پدر و مادر و خويشاوندان به ارث مى‌گذارند چه اندك و چه بسيار زنان را نيز نصيبى است، نصيبى معيّن.'),
(501, 4, 8, 'و چون به هنگام تقسيم، خويشاوندان و يتيمان و مسكينان حاضر آمدند به آنان نيز چيزى ارزانى داريد و با آنان به نيكويى سخن گوييد.'),
(502, 4, 9, 'بايد از خداى بترسند كسانى كه اگر پس از خويش فرزندانى ناتوان بر جاى مى‌گذارند، از سرنوشت آنان بيمناكند. بايد كه از خداى بترسند و سخن عادلانه و به صواب گويند.'),
(503, 4, 10, 'آنان كه اموال يتيمان را به ستم مى‌خورند شكم خويش پر از آتش مى‌كنند و به آتشى فروزان خواهند افتاد.'),
(504, 4, 11, 'خدا درباره فرزندانتان به شما سفارش مى‌كند كه سهم پسر برابر سهم دو دختر است. و اگر دختر باشند و بيش از دو تن، دو سوم ميراث از آنهاست. و اگر يك دختر بود نصف برد و اگر مرده را فرزندى باشد هر يك از پدر و مادر يك ششم ميراث را برد. و اگر فرزندى نداشته باشد و ميراث‌بران تنها پدر و مادر باشند، مادر يك سوم دارايى را برد. اما اگر برادران داشته باشد سهم مادر، پس از انجام وصيتى كه كرده و پرداخت وام او يك ششم باشد. و شما نمى‌دانيد كه از پدران و پسرانتان كدام يك شما را سودمندتر است. اينها حكم خداست، كه خدا دانا و حكيم است.'),
(505, 4, 12, 'اگر زنانتان فرزندى نداشتند، پس از انجام دادن وصيتى كه كرده‌اند و پس از پرداخت دين آنها، نصف ميراثشان از آن شماست. و اگر فرزندى داشتند يك چهارم آن. و اگر شما را فرزندى نبود پس از انجام دادن وصيتى كه كرده‌ايد و پس از پرداخت وامهايتان يك چهارم ميراثتان از آن زنانتان است. و اگر داراى فرزندى بوديد يك هشتم آن. و اگر مردى يا زنى بميرد و ميراث‌بر وى نه پدر باشد و نه فرزند او اگر او را برادر يا خواهرى باشد هر يك از آن دو يك ششم برد. و اگر بيش از يكى بودند همه در يك سوم مال -پس از انجام دادن وصيتى كه كرده است بى‌آنكه براى وارثتان زيانمند باشد و نيز پس از اداى دينش- شريك هستند. اين اندرزى است از خدا به شما و خدا دانا و بردبار است.'),
(506, 4, 13, 'اينها احكام خداست. هر كس از خدا و پيامبرش فرمان برد، او را به بهشتهايى كه در آن نهرها جارى است درآورد و همواره در آنجا خواهد بود و اين كاميابى بزرگى است.'),
(507, 4, 14, 'و هر كه از خدا و رسولش فرمان نبرد و از احكام او تجاوز كند، او را داخل در آتش كند و همواره در آنجا خواهد بود و براى اوست عذابى خواركننده.'),
(508, 4, 15, 'و از زنان شما آنان كه مرتكب فحشا مى‌شوند، از چهار تن از خودتان بر ضد آنها شهادت بخواهيد. اگر شهادت دادند زنان را در خانه محبوس داريد تا مرگشان فرا رسد يا خدا راهى پيش پايشان نهد.'),
(509, 4, 16, 'و آن دو تن را كه مرتكب آن عمل شده‌اند، بيازاريد. و چون توبه كنند و به صلاح آيند از آزارشان دست برداريد زيرا خدا توبه‌پذير و مهربان است.'),
(510, 4, 17, 'جز اين نيست كه توبه از آن كسانى است كه به نادانى مرتكب كارى زشت مى‌شوند و زود توبه مى‌كنند. خدا توبه اينان را مى‌پذيرد و خدا دانا و حكيم است.'),
(511, 4, 18, 'توبه كسانى كه كارهاى زشت مى‌كنند و چون مرگشان فرا مى‌رسد مى‌گويند كه اكنون توبه كرديم، و نيز آنان كه كافر بميرند، پذيرفته نخواهد شد. براى اينان عذابى دردآور مهيا كرده‌ايم.'),
(512, 4, 19, 'اى كسانى كه ايمان آورده‌ايد، شما را حلال نيست كه زنان را بر خلاف ميلشان به ارث ببريد. و تا قسمتى از آنچه را كه به آنها داده‌ايد بازپس ستانيد بر آنها سخت مگيريد، مگر آنكه مرتكب فحشايى به ثبوت رسيده شده باشند. و با آنان به نيكويى رفتار كنيد. و اگر شما را از زنان خوش نيامد، چه بسا چيزها كه شما را از آن خوش نمى‌آيد در حالى كه خدا خير كثيرى در آن نهاده باشد.'),
(513, 4, 20, 'اگر خواستيد زنى به جاى زنى ديگر بگيريد و او را قنطارى مال داده‌ايد، نبايد چيزى از او بازستانيد. آيا به آنان تهمت مى‌زنيد تا مهرشان را باز پس گيريد؟ اين گناهى آشكار است.'),
(514, 4, 21, 'و چگونه آن مال را باز پس مى‌گيريد و حال آنكه هر يك از شما از ديگرى بهره‌مند شده است و زنان از شما پيمانى استوار گرفته‌اند.'),
(515, 4, 22, 'با زنانى كه پدرانتان به عقد خويش درآورده‌اند زناشويى مكنيد، مگر آنكه پيش از اين چنان كرده باشيد. زيرا اين كار، زنا و مورد خشم خدا است و شيوه‌اى است ناپسند.'),
(516, 4, 23, 'مادرانتان و دخترانتان و خواهرانتان و عمه‌هايتان و خاله‌هايتان و دختران برادران و دختران خواهرانتان و زنانى كه شما را شير داده‌اند و خواهران شيريتان و مادران زنانتان بر شما حرام شده‌اند. و دختران زنانتان كه در كنار شما هستند، هرگاه با آن زنان همبستر شده‌ايد بر شما حرام شده‌اند. ولى اگر همبستر نشده‌ايد، گناهى مرتكب نشده‌ايد. و نيز زنان پسرانى كه از پشت شما هستند بر شما حرام شده‌اند. و نبايد دو خواهر را در يك زمان به زنى گيريد، مگر آنكه پيش از اين چنين كرده باشيد. هر آينه خدا آمرزنده و مهربان است.'),
(517, 4, 24, 'و نيز زنان شوهردار بر شما حرام شده‌اند، مگر آنها كه به تصرف شما درآمده باشند. از كتاب خدا پيروى كنيد. و جز اينها، زنان ديگر هرگاه در طلب آنان از مال خويش مهرى بپردازيد و آنها را به نكاح درآوريد نه به زنا، بر شما حلال شده‌اند. و زنانى را كه از آنها تمتع مى‌گيريد واجب است كه مهرشان را بدهيد. و پس از مهر معيّن، در قبول هر چه هر دو بدان رضا بدهيد گناهى نيست. هر آينه خدا دانا و حكيم است.'),
(518, 4, 25, 'هر كس را كه توانگرى نباشد تا آزادزنان مؤمنه را به نكاح خود درآورد از كنيزان مؤمنه‌اى كه مالك آنها هستيد به زنى گيرد. و خدا به ايمان شما آگاه‌تر است. همه از جنس يكديگريد. پس بندگان را به اذن صاحبانشان نكاح كنيد و مهرشان را به نحو شايسته‌اى بدهيد. و بايد كه پاكدامن باشند نه زناكار و نه از آنها كه به پنهان دوست مى‌گيرند. و چون شوهر كردند، هرگاه مرتكب فحشا شوند شكنجه آنان نصف شكنجه آزادزنان است. و اين براى كسانى است از شما كه بيم دارند كه به رنج افتند. با اين همه، اگر صبر كنيد برايتان بهتر است و خدا آمرزنده و مهربان است.'),
(519, 4, 26, 'خدا مى‌خواهد براى شما همه چيز را آشكار كند و به سنتهاى پيشينيانتان راه بنمايد و توبه شما را بپذيرد، كه خدا دانا و حكيم است.'),
(520, 4, 27, 'خدا مى‌خواهد توبه شما را بپذيرد. ولى آنان كه از پى شهوات مى‌روند، مى‌خواهند كه شما به كجروى عظيمى افتيد.'),
(521, 4, 28, 'خدا مى‌خواهد بار شما را سبك كند، زيرا آدمى ناتوان آفريده شده است.'),
(522, 4, 29, 'اى كسانى كه ايمان آورده‌ايد، اموال يكديگر را به ناحق مخوريد، مگر آنكه تجارتى باشد كه هر دو طرف بدان رضايت داده باشيد. و خودتان را مكشيد. هر آينه خدا با شما مهربان است.'),
(523, 4, 30, 'و هر كه اين كارها از روى تجاوز و ستم كند، او را در آتش خواهيم افكند و اين بر خدا آسان است.'),
(524, 4, 31, 'اگر از گناهان بزرگى كه شما را از آن نهى كرده‌اند اجتناب كنيد، از ديگر گناهانتان درمى‌گذريم و شما را به مكانى نيكو درمى‌آوريم.'),
(525, 4, 32, 'آرزو مكنيد آن چيزهايى را كه بدانها خدا بعضى از شما را بر بعضى ديگر برترى داده است. مردان را از آنچه كنند نصيبى است و زنان را از آنچه كنند نصيبى. و روزى از خدا خواهيد كه خدا بر هر چيزى آگاه است.'),
(526, 4, 33, 'براى همه، در آنچه پدر و مادر و خويشاوندان نزديك به ميراث مى‌گذارند، ميراث‌برانى قرار داده‌ايم. و بهره هر كس را كه با او قرارى نهاده‌ايد بپردازيد كه خدا بر هر چيزى گواه است.'),
(527, 4, 34, 'مردان، از آن جهت كه خدا بعضى را بر بعضى برترى داده است، و از آن جهت كه از مال خود نفقه مى‌دهند، بر زنان تسلط دارند. پس زنان شايسته، فرمانبردارند و در غيبت شوى عفيفند و فرمان خداى را نگاه مى‌دارند. و آن زنان را كه از نافرمانيشان بيم داريد، اندرز دهيد و از خوابگاهشان دورى كنيد و بزنيدشان. اگر فرمانبردارى كردند، از آن پس ديگر راه بيداد پيش مگيريد. و خدا بلندپايه و بزرگ است.'),
(528, 4, 35, 'اگر از اختلاف ميان زن و شوى آگاه شديد، داورى از كسان مرد و داورى از كسان زن برگزينيد. اگر آن دو را قصد اصلاح باشد خدا ميانشان موافقت پديد مى‌آورد، كه خدا دانا و آگاه است.'),
(529, 4, 36, 'خداى را بپرستيد و هيچ چيز شريك او مسازيد و با پدر و مادر و خويشاوندان و يتيمان و بينوايان و همسايه خويشاوند و همسايه بيگانه و يار مصاحب و مسافر رهگذر و بندگان خود نيكى كنيد. هرآينه خدا متكبران و فخرفروشان را دوست ندارد.'),
(530, 4, 37, 'آنان كه بخل مى‌ورزند و مردم را به بخل وا مى‌دارند و مالى را كه خدا به آنها داده است پنهان مى‌كنند. و ما براى كافران عذابى خواركننده مهيا ساخته‌ايم.'),
(531, 4, 38, 'و نيز كسانى را كه اموال خويش براى خودنمايى انفاق مى‌كنند و به خدا و روز قيامت ايمان نمى‌آورند. و هر كه شيطان قرين او باشد، قرينى بد دارد.'),
(532, 4, 39, 'چه زيان داردشان اگر به خدا و روز قيامت ايمان آورند و از آنچه خدا به آنها روزى داده است انفاق كنند؟ خدا به كارشان داناست.'),
(533, 4, 40, 'خدا ذره‌اى هم ستم نمى‌كند. اگر نيكيى باشد آن را دو برابر مى‌كند و از جانب خود مزدى كرامند مى‌دهد.'),
(534, 4, 41, 'چگونه خواهد بود آن روز كه از هر امتى گواهى بياوريم و تو را بر اين امت به گواهى فرا خوانيم؟'),
(535, 4, 42, 'در آن روز كافران و آنان كه بر رسول عصيان ورزيده‌اند آرزو كنند كه با خاك زمين يكسان مى‌بودند. و هيچ سخنى را از خدا پنهان نتوانند كرد.'),
(536, 4, 43, 'اى كسانى كه ايمان آورده‌ايد، آنگاه كه مست هستيد گرد نماز مگرديد تا بدانيد كه چه مى‌گوييد. و نيز در حال جنابت، تا غسل كنيد، مگر آنكه راهگذر باشيد. و اگر بيمار يا در سفر بوديد يا از مكان قضاى حاجت بازگشته‌ايد يا با زنان جماع كرده‌ايد و آب نيافتيد با خاك پاك تيمم كنيد و روى و دستهايتان را با آن خاك مسح كنيد. هرآينه خدا عفو كننده و آمرزنده است.'),
(537, 4, 44, 'آيا آن كسانى را كه از كتاب بهره‌اى داده شده‌اند، نديده‌اى كه گمراهى مى‌خرند و مى‌خواهند كه شما نيز گمراه شويد؟'),
(538, 4, 45, 'خدا دشمنان شما را بهتر مى‌شناسد و دوستى او شما را كفايت خواهد كرد و يارى او شما را بسنده است.'),
(539, 4, 46, 'بعضى از جهودان كلمات خدا را به معنى دگرگون مى‌كنند و مى‌گويند: شنيديم و عصيان مى‌ورزيم، و بشنو و كاش ناشنوا گردى و «راعنا». به لغت خويش زبان مى‌گردانند و به دين اسلام طعنه مى‌زنند. اگر مى‌گفتند كه شنيديم و اطاعت كرديم و «اُنظُرنا»، برايشان بهتر و به صواب‌تر بود. خدا آنان را به سبب كفرشان لعنت كرده و جز اندكى ايمان نياورند.'),
(540, 4, 47, 'اى كسانى كه شما را كتاب داده شده، به كتابى كه نازل كرده‌ايم و كتاب شما را نيز تصديق مى‌كند ايمان بياوريد؛ پيش از آنكه نقش چهره‌هايى را محو كنيم و رويها را به قفا برگردانيم. يا همچنان كه اصحاب سبت را لعنت كرديم شما را هم لعنت كنيم. و فرمان خدا شدنى است.'),
(541, 4, 48, 'هرآينه خدا گناه كسانى را كه به او شرك آورند نمى‌آمرزد؛ و گناهان ديگر را براى هر كه بخواهد مى‌آمرزد. و هر كه به خدا شرك آورد، دروغى ساخته و گناهى بزرگ مرتكب شده است.'),
(542, 4, 49, 'آيا آنان را نديده‌اى كه خويشتن را پاك و بى‌عيب جلوه مى‌دهند؟ آرى خداست كه هر كه را كه خواهد از عيب پاك گرداند. و به هيچ كس حتى به قدر رشته‌اى كه در شكاف هسته خرماست ستم نشود.'),
(543, 4, 50, 'بنگر كه چگونه به خدا دروغ مى‌بندند و همين دروغ گناهى آشكار را بسنده است.'),
(544, 4, 51, 'آيا كسانى را كه نصيبى از كتاب داده شده‌اند نديده‌اى كه به جبت و طاغوت ايمان مى‌آورند و در باره كافران مى‌گويند كه راه اينان از راه مؤمنان به هدايت نزديك‌تر است؟'),
(545, 4, 52, 'اينان آن كسانند كه خدا لعنتشان كرده است؛ و هر كس را كه خدا لعنت كند براى او هيچ ياورى نيابى.'),
(546, 4, 53, 'يا از پادشاهى نصيبى برده‌اند؟ كه در اين صورت به قدر آن گودى كه بر پشت هسته خرماست به مردم سودى نمى‌رسانند.'),
(547, 4, 54, 'يا بر مردم به خاطر نعمتى كه خدا از فضل خويش به آنان ارزانى داشته حسد مى‌برند؟ در حالى كه ما به خاندان ابراهيم كتاب و حكمت داديم و فرمانروايى بزرگ ارزانى داشتيم.'),
(548, 4, 55, 'بعضى بدان ايمان آوردند و بعضى از آن اعراض كردند. دوزخ، آن آتش افروخته، ايشان را بس.'),
(549, 4, 56, 'آنان را كه به آيات ما كافر شدند به آتش خواهيم افكند. هرگاه پوست تنشان بپزد پوستى ديگرشان دهيم، تا عذاب خدا را بچشند. خدا پيروزمند و حكيم است.'),
(550, 4, 57, 'و آنان را كه ايمان آورده و كارهاى نيكو كرده‌اند به بهشتهايى كه در آن نهرها جارى است درآوريم، تا ابد در آنجا خواهند بود. و در آنجا صاحب زنان پاك و بى‌عيب شوند و در سايه‌هاى پيوسته و خنك جايشان مى‌دهيم.'),
(551, 4, 58, 'خدا به شما فرمان مى‌دهد كه امانتها را به صاحبانشان بازگردانيد. و چون در ميان مردم به داورى نشينيد به عدل داورى كنيد. خدا شما را چه نيكو پند مى‌دهد. هر آينه او شنوا و بيناست.'),
(552, 4, 59, 'اى كسانى كه ايمان آورده‌ايد، از خدا اطاعت كنيد و از رسول و اولوالامر خويش فرمان بريد. و چون در امرى اختلاف كرديد اگر -به خدا و روز قيامت ايمان داريد- به خدا و پيامبر رجوع كنيد. در اين خير شماست و سرانجامى بهتر دارد.'),
(553, 4, 60, 'آيا آنان را نمى‌بينى كه مى‌پندارند كه به آنچه بر تو نازل شده و آنچه پيش از تو نازل شده است ايمان آورده‌اند، ولى مى‌خواهند كه بت را حَكَم قراردهند، در حالى كه به آنان گفته‌اند كه بت را انكار كنند. شيطان مى‌خواهد گمراهشان سازد و از حق دور گرداند.'),
(554, 4, 61, 'و چون ايشان را گويند كه به آنچه خدا نازل كرده و به پيامبرش روى آريد، منافقان را مى‌بينى كه سخت از تو رويگردان مى‌شوند.'),
(555, 4, 62, 'پس چگونه است كه چون به پاداش كارهايى كه مرتكب شده‌اند مصيبتى به آنها رسد، نزد تو مى‌آيند و به خدا سوگند مى‌خورند كه ما جز احسان و موافقت قصد ديگرى نداشته‌ايم؟'),
(556, 4, 63, 'خداوند از دلهايشان آگاه است. از آنان اعراض كن و اندرزشان بده به چنان سخنى كه در وجودشان كارگر افتد.'),
(557, 4, 64, 'هيچ پيامبرى را نفرستاديم جز آنكه ديگران به امر خدا بايد مطيع فرمان او شوند. و اگر به هنگامى كه مرتكب گناهى شدند نزد تو آمده بودند و از خدا آمرزش خواسته بودند و پيامبر برايشان آمرزش خواسته بود، خدا را توبه‌پذير و مهربان مى‌يافتند.'),
(558, 4, 65, 'نه، سوگند به پروردگارت كه ايمان نياورند، مگر آنكه در نزاعى كه ميان آنهاست تو را داور قرار دهند و از حكمى كه تو مى‌دهى هيچ ناخشنود نشوند و سراسر تسليم آن گردند.'),
(559, 4, 66, 'و اگر به آنان فرمان داده بوديم كه خود را بكشيد يا از خانه‌هايتان بيرون رويد، جز اندكى از آنان فرمان نمى‌بردند. و حال آنكه اگر اندرزى را كه به آنان داده شده است كار مى‌بستند برايشان بهتر و بر اساسى استوارتر بود.'),
(560, 4, 67, 'آنگاه از جانب خود به آنان مزدى بزرگ مى‌داديم.'),
(561, 4, 68, 'و آنان را به راه راست هدايت مى‌كرديم.'),
(562, 4, 69, 'و هر كه از خدا و پيامبرش اطاعت كند، همراه با كسانى خواهد بود كه خدا نعمتشان داده است، چون انبياء و صديقان و شهيدان و صالحان. و اينان چه نيكو رفيقانند.'),
(563, 4, 70, 'اين فضيلتى است از جانب خدا و بسنده است خداى دانا.'),
(564, 4, 71, 'اى كسانى كه ايمان آورده‌ايد، سلاح بگيريد و آنگاه هوشيارانه، فوج فوج يا يكباره، به جنگ رويد.'),
(565, 4, 72, 'و از ميان شما كسى است كه در كارزار درنگ مى‌كند. و چون به شما بلايى رسد، مى‌گويد: خدا در حق من چه انعامى كرد كه در آن روز همراهشان نبودم.'),
(566, 4, 73, 'و چون مالى از جانب خدا نصيبتان شود -چنان كه گويى ميانتان هيچ گونه مودتى نبوده است- گويد: اى كاش من نيز با آنها مى‌بودم و به كاميابيى بزرگ دست مى‌يافتم.'),
(567, 4, 74, 'پس آنان كه زندگى دنيا را داده‌اند و آخرت را خريده‌اند بايد كه در راه خدا بجنگند. و هر كه در راه خدا بجنگد، چه كشته شود چه پيروز گردد، مزدى بزرگ به او خواهيم داد.'),
(568, 4, 75, 'چرا در راه خدا و به خاطر مردان و زنان و كودكان ناتوانى كه مى‌گويند: اى پروردگار ما، ما را از اين قريه ستمكاران بيرون آر و از جانب خود يار و مددكارى قرار ده، نمى‌جنگيد؟'),
(569, 4, 76, 'آنان كه ايمان آورده‌اند، در راه خدا مى‌جنگند، و آنان كه كافر شده‌اند در راه شيطان. پس با هواداران شيطان قتال كنيد كه مكر شيطان ناچيز است.'),
(570, 4, 77, 'آيا نديدى كسانى را كه به آنها گفته شد كه اكنون از جنگ بازايستيد و نماز بخوانيد و زكات بدهيد، كه چون جنگيدن بر آنان مقرر شد، گروهى چنان از مردم ترسيدند كه بايد از خدا مى‌ترسيدند؟ حتى ترسى بيشتر از ترس خدا. و گفتند: اى پروردگار ما، چرا جنگ را بر ما واجب كرده‌اى و ما را مهلت نمى‌دهى تا به مرگ خود -كه نزديك است- بميريم؟ بگو: متاع اينجهانى اندك است و آخرت از آن پرهيزگاران است و به شما حتى به قدر رشته‌اى كه در ميان هسته خرماست ستم نمى شود.'),
(571, 4, 78, 'هر جا كه باشيد ولو در حصارهاى سخت استوار، مرگ شما را درمى يابد. و اگر خيرى به آنها رسد مى‌گويند كه از جانب خدا بود و اگر شرى به آنها رسد مى‌گويند كه از جانب تو بود. بگو: همه از جانب خداست. چه بر سر اين قوم آمده است كه هيچ سخنى را نمى‌فهمند؟'),
(572, 4, 79, 'هر خيرى كه به تو رسد از جانب خداست و هر شرى كه به تو رسد از جانب خود تو است. تو را به رسالت به سوى مردم فرستاديم و خدا به شهادت كافى است.'),
(573, 4, 80, 'هر كه از پيامبر اطاعت كند، از خدا اطاعت كرده است. و آنان كه سرباز زنند، پس ما تو را به نگهبانى آنها نفرستاده‌ايم.'),
(574, 4, 81, 'مى‌گويند: فرمانبرداريم. و چون از نزد تو بيرون شوند گروهى از ايشان به شب خلاف آنچه تو مى‌گويى انديشه‌اى در دل مى‌پرورند. و خدا آنچه را شب در خاطر گرفته‌اند مى‌نويسد. پس، از ايشان اعراض كن و بر خداى توكل كن كه او كارسازى را كافى است.'),
(575, 4, 82, 'آيا در قرآن نمى‌انديشند؟ هرگاه از سوى ديگرى جز خدا مى‌بود در آن اختلافى بسيار مى‌يافتند.'),
(576, 4, 83, 'و چون خبرى، چه ايمنى و چه ترس به آنها رسد، آن را در همه جا فاش مى‌كنند. و حال آنكه اگر در آن به پيامبر و اولوالامرشان رجوع مى‌كردند، حقيقت امر را از آنان درمى‌يافتند. و اگر فضل و رحمت خدا نبود، جز اندكى، همگان از شيطان پيروى مى‌كرديد.'),
(577, 4, 84, 'پس در راه خدا نبرد كن كه جز بر نفس خويش مكلف نيستى، و مؤمنان را به جنگ برانگيز. شايد خدا آسيب كافران را از شما باز دارد و خشم و عذاب خدا از هر خشم و عذاب ديگرى سخت‌تر است.'),
(578, 4, 85, 'هر كس در كار نيكى ميانجى شود او را از آن نصيبى است و هر كس در كار بدى ميانجى شود او را از آن بهره‌اى است. و خدا نگهبان بر هر چيزى است.'),
(579, 4, 86, 'چون شما را به درودى نواختند به درودى بهتر از آن يا همانند آن پاسخ گوييد. هرآينه خدا حسابگر هر چيزى است.'),
(580, 4, 87, 'الله خدايى است كه هيچ خدايى جز او نيست. به تحقيق همه شما را در روز قيامت -كه هيچ شكى در آن نيست- گرد مى‌آورد و چه كسى از خدا به گفتار راستگوى‌تر است؟'),
(581, 4, 88, 'چيست شما را كه درباره منافقان دو گروه شده‌ايد، و حال آنكه خدا آنان را به سبب كردارشان مردود ساخته است؟ آيا مى‌خواهيد كسى را كه خدا گمراه كرده است هدايت كنيد؟ و تو راهى پيش پاى كسى كه خداوند گمراهش كرده است نتوانى نهاد.'),
(582, 4, 89, 'دوست دارند همچنان كه خود به راه كفر مى‌روند شما نيز كافر شويد تا برابر گرديد. پس با هيچ يك از آنان دوستى مكنيد تا آنگاه كه در راه خدا مهاجرت كنند. و اگر سرباز زدند در هر جا كه آنها را بيابيد بگيريد و بكشيد و هيچ يك از آنها را به دوستى و يارى برمگزينيد.'),
(583, 4, 90, 'مگر كسانى كه به قومى كه ميان شما و ايشان پيمانى است مى‌پيوندند، يا خود نزد شما مى‌آيند در حالى كه از جنگيدن با شما يا جنگيدن با قوم خود ملول شده باشند. و اگر خدا مى‌خواست بر شما پيروزشان مى‌ساخت و با شما به جنگ برمى‌خاستند. پس هرگاه كناره گرفتند و با شما نجنگيدند و به شما پيشنهاد صلح كردند، خدا هيچ راهى براى شما بر ضد آنان نگشوده است.'),
(584, 4, 91, 'گروه ديگرى را خواهيد يافت كه مى‌خواهند از شما و قوم خود در امان باشند، اينان هرگاه كه به كفر دعوت شوند بدان بازگردند. پس اگر خود را به كنارى نكشند و صلح نكنند و از اعمال خويش باز نايستند آنان را هر جا كه يافتيد بگيريد و بكشيد كه شما را بر آنان تسلطى آشكار داده‌ايم.'),
(585, 4, 92, 'هيچ مؤمنى را نرسد كه مؤمن ديگر را جز به خطا بكشد. و هر كس كه مؤمنى را به خطا بكشد بايد كه بنده‌اى مؤمن را آزاد كند و خونبهايش را به خانواده‌اش تسليم كند، مگر آنكه خونبها را ببخشند. و اگر مقتول، مؤمن و از قومى است كه دشمن شماست فقط بنده مؤمنى را آزاد كند و اگر از قومى است كه با شما پيمان بسته‌اند، خونبها به خانواده‌اش پرداخت شود و بنده مؤمنى را آزاد كند و هر كس كه بنده‌اى نيابد براى توبه دو ماه پى‌درپى روزه بگيرد. و خدا دانا و حكيم است.'),
(586, 4, 93, 'و هر كس مؤمنى را به عمد بكشد، كيفر او جهنم است كه در آن همواره خواهد بود و خدا بر او خشم گيرد و لعنتش كند و برايش عذابى بزرگ آماده سازد.'),
(587, 4, 94, 'اى كسانى كه ايمان آورده‌ايد، چون براى جهاد رهسپر شويد نيك تفحص كنيد. و به آن كس كه بر شما سلام گويد، مگوييد كه مؤمن نيستى. شما برخوردارى از زندگى دنيا را مى‌جوييد و حال آنكه غنيمتهاى بسيار نزد خداست. شما پيش از اين چنان بوديد ولى خدا بر شما منت نهاد. پس تفحص كنيد كه خدا بر اعمالتان آگاه است.'),
(588, 4, 95, 'مؤمنانى كه بى‌هيچ رنج و آسيبى از جنگ سر مى‌تابند با كسانى كه به مال و جان خويش در راه خدا جهاد مى‌كنند برابر نيستند. خدا كسانى را كه به مال و جان خويش جهاد مى‌كنند بر آنان كه از جنگ سر مى‌تابند به درجتى برترى داده است. و خدا همه را وعده‌هاى نيكو داده است. و جهادكنندگان را بر آنها كه از جهاد سر مى‌تابند به مزدى بزرگ برترى نهاده است.'),
(589, 4, 96, 'درجاتى از جانب خدا و آمرزش و رحمتى؛ كه او آمرزنده و مهربان است.'),
(590, 4, 97, 'كسانى هستند كه فرشتگان جانشان را مى‌ستانند در حالى كه بر خويشتن ستم كرده بودند. از آنها مى‌پرسند: در چه كارى بوديد؟ گويند: ما در روى زمين مردمى بوديم زبون گشته. فرشتگان گويند: آيا زمين خدا پهناور نبود كه در آن مهاجرت كنيد؟ مكان اينان جهنم است و سرانجامشان بد.'),
(591, 4, 98, 'مگر مردان و زنان و كودكان ناتوانى كه هيچ چاره‌اى نيابند و به هيچ جا راه نبرند.'),
(592, 4, 99, 'باشد كه خداشان عفو كند كه خدا عفوكننده و آمرزنده است.'),
(593, 4, 100, 'آن كس كه در راه خدا مهاجرت كند در روى زمين برخورداريهاى بسيار و گشايشها خواهد يافت. و هر كس كه از خانه خويش بيرون آيد تا به سوى خدا و رسولش مهاجرت كند و آنگاه مرگ او را دريابد، مزدش بر عهده خداست، و خدا آمرزنده و مهربان است.'),
(594, 4, 101, 'و چون در زمين سفر كنيد، گناهى نيست كه اگر بيم آن داشتيد كه كافران به شما زيان رسانند، نماز خويش كوتاه كنيد. زيرا كافران دشمن آشكار شما هستند.'),
(595, 4, 102, 'و چون تو در ميانشان باشى و برايشان اقامه نماز كنى، بايد كه گروهى از آنها با تو به نماز بايستند و سلاحهاى خويش بردارند. و چون سجده به پايان بردند برابر دشمن شوند تا گروه ديگر كه نماز نخوانده‌اند بيايند و با تو نماز بخوانند. آنان نيز هوشيار باشند و سلاحهاى خويش بگيرند. زيرا كافران دوست دارند كه شما از سلاحها و متاع خود غافل شويد تا يكباره بر شما بتازند. و گناهى مرتكب نشده‌ايد هرگاه از باران در رنج بوديد يا بيمار بوديد سلاحهاى خويش بگذاريد، ولى هوشيارانه مواظب دشمن باشيد. هرآينه خدا براى كافران عذابى خواركننده آماده ساخته است.'),
(596, 4, 103, 'و چون نماز را به پايان برديد خدا را ايستاده يا نشسته و يا به پهلو خوابيده ياد كنيد. و چون از دشمن ايمن گشتيد نماز را تمام ادا كنيد كه نماز بر مؤمنان در وقتهاى معين واجب گشته است.'),
(597, 4, 104, 'و در دست يافتن به آن قوم سستى مكنيد. اگر شما آزار مى‌بينيد، آنان نيز چون شما آزار مى‌بينند، ولى شما از خدا چيزى را اميد داريد كه آنان اميد ندارند. و خدا دانا و حكيم است.'),
(598, 4, 105, 'ما اين كتاب را به راستى بر تو نازل كرديم تا بدان سان كه خدا به تو آموخته است ميان مردم داورى كنى و به نفع خائنان به مخاصمت برمخيز.'),
(599, 4, 106, 'و از خدا آمرزش بخواه كه او آمرزنده و مهربان است.'),
(600, 4, 107, 'و به خاطر كسانى كه به خود خيانت مى‌ورزند مجادله مكن، كه خدا خائنان گناهكار را دوست ندارد.'),
(601, 4, 108, 'از مردم پنهان مى‌دارند و از خدا پنهان نمى‌دارند، زيرا آنگاه كه شب‌هنگام سخنانى كه خدا از آن ناخشنود بود مى‌گفتند، خدا با آنها بود و خدا به هر چه مى‌كردند احاطه داشت.'),
(602, 4, 109, 'هان، اين شماييد كه در اين جهان از آنان سخت جانبدارى كرديد، كيست كه در روز قيامت از آنان در برابر خدا جانبدارى كند يا چه كسى وكيل آنها خواهد بود؟'),
(603, 4, 110, 'و هر كه كارى ناپسند كند يا به خود ستم روا دارد، آنگاه از خدا آمرزش خواهد، خدا را آمرزنده و مهربان خواهد يافت.'),
(604, 4, 111, 'و هر كه گناهى كند، آن گناه به زيان خود كرده است، و خدا دانا و حكيم است.'),
(605, 4, 112, 'و هر كه خود خطا يا گناهى كند آنگاه بى‌گناهى را بدان متهم سازد، هر آينه بار تهمت و گناهى آشكار را بر دوش خود كشيده است.'),
(606, 4, 113, 'اگر فضل و رحمت خدا شامل حال تو نبود، گروهى از كافران قصد آن داشتند كه تو را گمراه كنند، ولى آنان جز خود را گمراه نكنند و هيچ زيانى به تو نرسانند. و خدا بر تو كتاب و حكمت نازل كرد و چيزهايى به تو آموخت كه از اين پيش نمى‌دانسته‌اى و خدا لطف بزرگ خود را بر تو ارزانى داشت.'),
(607, 4, 114, 'در بسيارى از نجواهايشان فايده‌اى نيست، مگر در سخن آنان كه به صدقه‌دادن يا نيكى‌كردن و يا آشتى‌جويى فرمان مى‌دهند. و آن كس را كه براى خشنودى خداى چنين كند مزد بزرگى خواهيم داد.'),
(608, 4, 115, 'هر كه پس از آشكار شدن راه هدايت با پيامبر مخالفت ورزد و از شيوه‌اى جز شيوه مؤمنان پيروى كند، بدان سوى كه پسند اوست بگردانيمش و به جهنمش افكنيم، و جهنم سرانجام بدى است.'),
(609, 4, 116, 'خدا كسى را كه براى او شريكى قرار دهد نمى‌آمرزد و جز آن هر گناهى را براى هر كه خواهد، مى‌آمرزد. و هر كس كه براى خدا شريكى قرار دهد سخت به گمراهى افتاده است.'),
(610, 4, 117, 'نمى‌خوانند سواى الّله مگر الاهه‌هايى را و نمى‌خوانند سواى اللّه مگر شيطانى سركش را.'),
(611, 4, 118, 'خدايش لعنت كرد. و شيطان گفت: گروهى معيّن از بندگانت را به فرمان خويش مى‌گيرم.'),
(612, 4, 119, 'و البته گمراهشان مى‌كنم و آرزوهاى باطل در دلشان مى‌افكنم و به آنان فرمان مى‌دهم تا گوشهاى چارپايان را بشكافند. و به آنان فرمان مى‌دهم تا خلقت خدا را دگرگون سازند. و هر كس كه به جاى خدا شيطان را به دوستى برگزيند زيانى آشكار كرده است.'),
(613, 4, 120, 'به آنها وعده مى‌دهد و به آرزوشان مى‌افكند و شيطان آنان را جز به فريب وعده ندهد.'),
(614, 4, 121, 'مكانشان در جهنم است و در آنجا راه گريزى نخواهند يافت.'),
(615, 4, 122, 'و كسانى را كه ايمان آورده‌اند و كارهاى نيكو كرده‌اند به بهشتهايى درمى‌آوريم كه در آن نهرها روان است و در آنجا جاويدانند. وعده بر حق خداوند است، و چه كسى از او راستگوى‌تر است؟'),
(616, 4, 123, 'نه بر وفق مراد شماست و نه بر وفق مراد اهل كتاب، كه هر كس كه مرتكب كار بدى شود جزايش را ببيند، و جز خدا براى خويش دوست و ياورى نيابد.'),
(617, 4, 124, 'و هر كس كه كارى شايسته كند چه زن و چه مرد اگر مؤمن باشد به بهشت مى‌رود و به قدر آن گودى كه بر پشت هسته خرماست به كس ستم نمى‌شود.'),
(618, 4, 125, 'دين چه كسى بهتر از دين كسى است كه به اخلاص روى به جانب خدا كرد و نيكوكار بود و از دين حنيف ابراهيم پيروى كرد؟ و خدا ابراهيم را به دوستى خود برگزيد.'),
(619, 4, 126, 'از آن خداست هر چه در آسمانها و زمين است و خدا بر هر چيزى احاطه دارد.'),
(620, 4, 127, 'از تو درباره زنان فتوى مى‌خواهند، بگو: خدا درباره آنان به آنچه در اين كتاب بر شما خوانده مى‌شود، فتوى داده است. اين فتوى در باب آنها و زنان پدرمرده‌اى است كه حق مقررشان را نمى‌پردازيد و مى‌خواهيد ايشان را به نكاح خود درآوريد و نيز در باب كودكان ناتوان است. و بايد كه درباره يتيمان به عدالت رفتار كنيد، و هر كار نيكى كه انجام مى‌دهيد خدا به آن آگاه است.'),
(621, 4, 128, 'اگر زنى دريافت كه شوهرش با او بى‌مهر و از او بيزار شده است، باكى نيست كه هر دو در ميان خود طرح آشتى افكنند، كه آشتى بهتر است. و بخل و فرومايگى بر نفوس مردم غلبه دارد. و اگر نيكى و پرهيزگارى كنيد خدا به هر چه مى‌كنيد آگاه است.'),
(622, 4, 129, 'هر چند بكوشيد هرگز نتوانيد كه در ميان زنان به عدالت رفتار كنيد. لكن يكباره به سوى يكى ميل نكنيد تا ديگرى را سرگشته رها كرده باشيد. اگر از درآشتى درآييد و پرهيزگارى كنيد خدا آمرزنده و مهربان است.'),
(623, 4, 130, 'و اگر آن دو از يكديگر جدا شوند خدا هر دو را به كمال فضل خويش بى‌نياز سازد كه خدا گشايش‌دهنده و حكيم است.'),
(624, 4, 131, 'از آن خداست آنچه در آسمانها و زمين است. و هرآينه اهل كتاب را كه پيش از شما بودند و نيز شما را سفارش كرديم كه از خدا بترسيد، اگر هم كفر ورزيد باز هم آنچه در آسمانها و آنچه در زمين است متعلق به خداست و اوست بى‌نياز و درخور ستايش.'),
(625, 4, 132, 'و از آن خداست آنچه در آسمانها و زمين است، و خدا كارسازى را كافى است.'),
(626, 4, 133, 'اى مردم، اگر او بخواهد همه شما را از ميان مى‌برد و مردمى ديگر را مى‌آورد، كه خدا بر اين كار قادر است.'),
(627, 4, 134, 'هر كس كه پاداش اينجهانى را مى‌طلبد بداند كه پاداش اينجهانى و آنجهانى در نزد خداست. و او شنوا و بيناست.'),
(628, 4, 135, 'اى كسانى كه ايمان آورده‌ايد، به عدالت فرمانروا باشيد و براى خدا شهادت دهيد، هر چند به زيان خود يا پدر و مادر يا خويشاوندان شما -چه توانگر و چه درويش- بوده باشد. زيرا خدا به آن دو سزاوارتر است. پس، از هوا نفس پيروى مكنيد تا از شهادت حق عدول كنيد. چه زبان‌بازى كنيد يا از آن اعراض كنيد، خدا به هر چه مى‌كنيد آگاه است.'),
(629, 4, 136, 'اى كسانى كه ايمان آورده‌ايد، به خدا و پيامبرش و اين كتاب كه بر پيامبرش نازل كرده و آن كتاب كه پيش از آن نازل كرده، به حقيقت ايمان بياوريد. و هر كه به خدا و فرشتگانش و كتابهايش و پيامبرانش و به روز قيامت كافر شود سخت در گمراهى افتاده است.'),
(630, 4, 137, 'هر آينه خداوند آنان را كه ايمان آوردند سپس كافر شدند و باز ايمان آوردند، سپس كافر شدند و به كفر خويش افزودند نخواهد آمرزيد و به راه راست هدايت نخواهد كرد.'),
(631, 4, 138, 'منافقان را بشارت ده كه عذابى دردآور بر ايشان آماده شده است.'),
(632, 4, 139, 'كسانى كه به جاى مؤمنان كافران را به دوستى برمى‌گزينند، آيا عزت و توانايى را نزد آنان مى‌جويند، در حالى كه عزت به تمامى از آن خداست؟'),
(633, 4, 140, 'و از اين پيش، در اين كتاب بر شما نازل كرده‌ايم كه چون شنيديد كسانى آيات خدا را انكار مى‌كنند و آن را به ريشخند مى‌گيرند با آنان منشينيد تا آنگاه كه به سخنى ديگر پردازند. وگرنه شما نيز همانند آنها خواهيد بود. و خدا همه منافقان و كافران را در جهنم گرد مى‌آورد.'),
(634, 4, 141, 'كسانى كه همواره مواظب شما هستند. پس اگر از جانب خدا فتحى نصيبتان شود، مى‌گويند: مگر ما همراه شما نبوديم؟ و اگر پيروزى نصيب كافران شود، مى‌گويند: آيا نه چنان بود كه بر شما غلبه يافته بوديم و مؤمنان را از آسيب رساندن به شما بازداشتيم؟ در روز قيامت خدا ميان شما حكم مى‌كند و او هرگز براى كافران به زيان مسلمانان راهى نگشوده است.'),
(635, 4, 142, 'منافقان خدا را فريب مى‌دهند، و حال آنكه خدا آنها را فريب مى‌دهد. و چون به نماز برخيزند با اكراه و كاهلى برخيزند و براى خودنمايى نماز كنند و در نماز -جز اندكى- خداى را ياد نكنند.'),
(636, 4, 143, 'سرگشتگان ميان كفر و ايمانند: نه با اينان و نه با آنان. آن كه خدا گمراهش كند هيچ راهى براى او نخواهى يافت.'),
(637, 4, 144, 'اى كسانى كه ايمان آورده‌ايد، به جاى مؤمنان كافران را به دوستى مگيريد. آيا كارى مى‌كنيد كه براى خدا به زيان خود حجتى آشكار پديد آريد؟'),
(638, 4, 145, 'هر آينه منافقان در فروترين طبقات آتش هستند و هرگز بر ايشان ياورى نمى‌يابى.'),
(639, 4, 146, 'مگر آنان كه توبه كرده‌اند و از تباهى بازآمده‌اند و به خدا توسل جسته‌اند و براى خدا از روى اخلاص به دين گرويده‌اند. اينان در زمره مؤمنانند و خدا به مؤمنان اجرى عظيم خواهد داد.'),
(640, 4, 147, 'چرا خدا شما را عذاب كند اگر سپاسگزار باشيد و ايمان آوريد؟ در حالى كه شكرپذير و داناست.'),
(641, 4, 148, 'خدا بلند كردن صدا را به بدگويى دوست ندارد، مگر از آن كس كه به او ستمى شده باشد، و خدا شنوا و داناست.'),
(642, 4, 149, 'اگر كار نيكى را به آشكار انجام دهيد يا به پنهان يا كردارى ناپسند را درگذريد، خدا عفوكننده و تواناست.'),
(643, 4, 150, 'كسانى هستند كه به خدا و پيامبرانش كافر مى‌شوند و مى‌خواهند ميان خدا و پيامبرانش جدايى افكنند و مى‌گويند كه بعضى را مى‌پذيريم و بعضى را نمى‌پذيريم و مى‌خواهند در اين ميانه راهى برگزينند.'),
(644, 4, 151, 'اينان در حقيقت كافرانند و ما براى كافران عذابى خواركننده آماده ساخته‌ايم.'),
(645, 4, 152, 'و كسانى كه به خدا و پيامبرانش ايمان آورده‌اند و ميان پيامبرانش جدايى نيفكنده‌اند پاداششان را خدا خواهد داد، و خدا آمرزنده و مهربان است.'),
(646, 4, 153, 'اهل كتاب از تو مى‌خواهند كه برايشان كتابى از آسمان نازل كنى. اينان بزرگتر از اين را از موسى طلب كردند و گفتند: خدا را به آشكار به ما بنماى. به سبب اين سخن كفرآميزشان صاعقه آنان را فرو گرفت. و پس از آنكه معجزه‌هايى برايشان آمده بود گوساله‌اى را به خدايى گرفتند و ما آنان را بخشيديم و موسى را حجتى آشكار ارزانى داشتيم.'),
(647, 4, 154, 'و به خاطر پيمانى كه با آنها بسته بوديم كوه طور را بر فراز سرشان بداشتيم و گفتيم: سجده‌كنان از آن در داخل شويد و در روز شنبه تجاوز مكنيد. و از ايشان پيمانى سخت گرفتيم.'),
(648, 4, 155, 'پس به سبب پيمان شكستنشان و كافر شدنشان به آيات خدا و به ناحق كشتن پيامبران و اينكه گفتند: دلهاى ما فرو بسته است، خدا بر دلهايشان مهر نهاده است و جز اندكى ايمان نمى‌آورند.'),
(649, 4, 156, 'و نيز به سبب كفرشان و آن تهمت بزرگ كه به مريم زدند.'),
(650, 4, 157, 'و نيز بدان سبب كه گفتند: ما مسيح پسر مريم پيامبر خدا را كشتيم. و حال آنكه آنان مسيح را نكشتند و بر دار نكردند بلكه امر برايشان مشتبه شد. هر آينه آنان كه درباره او اختلاف مى‌كردند خود در ترديد بودند و به آن يقين نداشتند. تنها پيرو گمان خود بودند و عيسى را به يقين نكشته بودند.'),
(651, 4, 158, 'بلكه خداوند او را به نزد خود فرا برد، كه خدا پيروزمند و حكيم است.'),
(652, 4, 159, 'و هيچ يك از اهل كتاب نيست مگر آنكه پيش از مرگش به او ايمان آورد و عيسى در روز قيامت به ايمانشان گواهى خواهد داد.'),
(653, 4, 160, 'و به كيفر ستمى كه يهودان روا داشتند و منع‌كردن بسيارشان از راه خدا، آن چيزهاى پاكيزه را كه بر آنان حلال بود، حرام كرديم.'),
(654, 4, 161, 'و نيز به سبب رباخواريشان و حال آنكه از آن منع شده بودند، و خوردن اموال مردم را به باطل. و ما براى كافرانشان عذابى دردآور مهيا كرده‌ايم.'),
(655, 4, 162, 'ولى دانشمندانشان و آن مؤمنانى را كه به آنچه بر تو و به آنچه پيش از تو نازل شده ايمان دارند، و نمازگزاران و زكات‌دهندگان و مؤمنان به خدا و روز قيامت را اجر بزرگى خواهيم داد.'),
(656, 4, 163, 'ما به تو وحى كرديم همچنان كه به نوح و پيامبران بعد از او وحى كرده‌ايم و به ابراهيم و اسماعيل و اسحاق و يعقوب و سبطها و عيسى و ايوب و يونس و هارون و سليمان وحى كرده‌ايم و به داود زبور را ارزانى داشتيم.'),
(657, 4, 164, 'و پيامبرانى كه پيش از اين داستانهاشان را براى تو گفته‌ايم و آنان كه داستانهاشان را براى تو نگفته‌ايم. و خدا با موسى سخن گفت، چه سخن گفتنى بى‌ميانجى.'),
(658, 4, 165, 'پيامبرانى مژده‌دهنده و بيم‌دهنده تا از آن پس مردم را بر خدا حجتى نباشد، و خدا پيروزمند و حكيم است.'),
(659, 4, 166, 'ولى خدا به آنچه بر تو نازل كرد شهادت مى‌دهد كه به علم خود نازل كرده است، و فرشتگان نيز شهادت مى‌دهند، و خدا شهادت را بسنده است.'),
(660, 4, 167, 'هر آينه آنان كه كافر شده‌اند و از راه خدا روى گردانيده‌اند سخت به گمراهى افتاده‌اند.'),
(661, 4, 168, 'كسانى را كه كافر شده‌اند و ستم كرده‌اند خداوند نمى‌آمرزد و به هيچ راهى هدايت نمى‌كند.'),
(662, 4, 169, 'مگر به راه جهنم كه همواره در آن باشند. و اين كار بر خداى آسان است.'),
(663, 4, 170, 'اى مردم، پيامبرى به حق از جانب خدا بر شما مبعوث شد، پس به او ايمان بياوريد كه خير شما در آن است. و اگر هم كافر شويد، از آن خداست هر چه در آسمانها و زمين است. و او دانا و حكيم است.'),
(664, 4, 171, 'اى اهل كتاب، در دين خويش غلوّ مكنيد و درباره خدا جز سخن حق مگوييد. هر آينه عيسى پسر مريم پيامبر خدا و كلمه او بود كه به مريمش افكند و روحى از او بود. پس به خدا و پيامبرانش ايمان بياوريد و مگوييد كه سه است. از اين انديشه‌ها بازايستيد كه خير شما در آن خواهد بود. جز اين نيست كه اللّه خدايى است يكتا. منزه است از اينكه صاحب فرزندى باشد. از آن اوست آنچه در آسمانها و زمين است، و خدا كارسازى را كافى است.'),
(665, 4, 172, 'مسيح اِبايى نداشت كه يكى از بندگان خدا باشد و ملائكه مقرب نيز اِبايى ندارند. هر كه از پرستش خداوند سرباز زند و سركشى كند، بداند كه خدا همه را در نزد خود محشور خواهد ساخت.'),
(666, 4, 173, 'اما آنان كه ايمان آورده‌اند و كارهاى نيك كرده‌اند اجرشان را به تمامى خواهد داد، و از فضل خويش بر آن خواهد افزود. اما كسانى را كه اِبا و سركشى كرده‌اند به عذابى دردآور معذب خواهد داشت و براى خود جز خدا هيچ دوست و ياورى نخواهند يافت.'),
(667, 4, 174, 'اى مردم، از جانب پروردگارتان بر شما حجتى آمد و براى شما نورى آشكار نازل كرده‌ايم.'),
(668, 4, 175, 'اما آنان را كه به خدا ايمان آورده‌اند و به او توسل جسته‌اند به آستان فضل و رحمت خويش درآورد و به راهى راست هدايت كند.'),
(669, 4, 176, 'از تو فتوى مى‌خواهند، بگوى كه خدا درباره كلاله برايتان فتوى مى‌دهد: هرگاه مردى كه فرزندى نداشته باشد بميرد و او را خواهرى باشد، به آن خواهر نصف ميراث او مى‌رسد. اگر خواهر را نيز فرزندى نباشد، برادر از او ارث مى‌برد. اگر آن خواهران دو تن بودند، دو ثلث دارايى را به ارث مى‌برند. و اگر چند برادر و خواهر بودند، هر مرد برابر دو زن مى‌برد. خدا براى شما بيان مى‌كند تا گمراه نشويد، و او از هر چيزى آگاه است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اى كسانى كه ايمان آورده‌ايد، به پيمانها وفا كنيد. حيوانات چهارپا مگر آنهايى كه از اين پس برايتان گفته مى‌شود، بر شما حلال شده‌اند و آنچه را كه در حال احرام صيد مى‌كنيد حلال مشماريد. خدا به هر چه مى‌خواهد حكم مى‌كند.'),
(671, 5, 2, 'اى كسانى كه ايمان آورده‌ايد، شعاير خدا و ماه حرام و قربانى را چه بدون قلاده و چه با قلاده حرمت مشكنيد و آزار آنان را كه به طلب روزى و خشنودى پروردگارشان آهنگ بيت‌الحرام كرده‌اند، روا مداريد. و چون از احرام به در آمديد صيد كنيد و دشمنى با قومى كه شما را از مسجدالحرام بازداشتند وادارتان نسازد كه از حد خويش تجاوز كنيد، و در نيكوكارى و پرهيز همكارى كنيد نه در گناه و تجاوز. و از خداى بترسيد كه او به سختى عقوبت مى‌كند.'),
(672, 5, 3, 'حرام شد بر شما مردار و خون و گوشت خوك و هر حيوانى كه به هنگام كشتنش نام ديگرى جز الّله را بر او بگويند، و آنچه خفه شده باشد يا به سنگ زده باشند يا از بالا درافتاده باشد يا به شاخ حيوانى ديگر بميرد يا درندگان از آن خورده باشند، مگر آنكه ذبحش كنيد. و نيز هر چه بر آستان بتان ذبح شود و آنچه به وسيله تيرهاى قمار قسمت كنيد كه اين كار خود نافرمانى است. امروز كافران از بازگشت شما از دين خويش نوميد شده‌اند. از آنان مترسيد از من بترسيد. امروز دين شما را به كمال رسانيدم و نعمت خود بر شما تمام كردم و اسلام را دين شما برگزيدم. پس هر كه در گرسنگى بى‌چاره ماند بى آنكه قصد گناه داشته باشد، بداند كه خدا آمرزنده و مهربان است.'),
(673, 5, 4, 'از تو مى‌پرسند كه چه چيزهايى بر آنها حلال شده است. بگو: چيزهاى پاكيزه بر شما حلال شده و نيز خوردن صيد آن حيوان كه به آن صيدكردن آموخته‌ايد، چون پرندگان شكارى و سگان شكارى، هرگاه آنها را بدان سان كه خدايتان آموخته است تعليم داده باشيد. از آن صيد كه برايتان مى‌گيرند و نگه مى‌دارند بخوريد و نام خدا را بر آن بخوانيد و از خدا بترسيد كه او سريع‌الحساب است.'),
(674, 5, 5, 'امروز چيزهاى پاكيزه بر شما حلال شده است. طعام اهل كتاب بر شما حلال است و طعام شما نيز بر آنها حلال است. و نيز زنان پارساى مؤمنه و زنان پارساى اهل كتاب، هرگاه مهرشان را بپردازيد، به طور زناشويى نه زناكارى و دوست‌گيرى، بر شما حلالند. و هر كس كه به اسلام كافر شود عملش ناچيز شود و در آخرت از زيانكاران خواهد بود.'),
(675, 5, 6, 'اى كسانى كه ايمان آورده‌ايد، چون به نماز برخاستيد، صورت و دستهايتان را با آرنج بشوييد و سر و پاهايتان را تا قوزك مسح كنيد. و اگر جُنب بوديد خود را پاك سازيد. و اگر بيمار يا در سفر بوديد يا از جاى قضاى حاجت آمده بوديد يا با زنان نزديكى كرده بوديد و آب نيافتيد با خاكى پاك تيمم كنيد و صورت و دستهايتان را با آن مسح كنيد. خدا نمى‌خواهد شما در رنج افتيد، بلكه مى‌خواهد كه شما را پاكيزه سازد و نعمتش را بر شما تمام كند، باشد كه سپاس گزاريد.'),
(676, 5, 7, 'نعمتى را كه خدا به شما داده است، و پيمانى را كه با شما بسته است، بدان هنگام كه گفتيد شنيديم و فرمانبردارى كرديم، ياد آوريد. و از خداى بترسيد كه خدا به آنچه در دلها مى‌گذرد آگاه است.'),
(677, 5, 8, 'اى كسانى كه ايمان آورده‌ايد، براى خدا، حق گفتن را بر پاى خيزيد و به عدل گواهى دهيد. دشمنى با گروهى ديگر وادارتان نكند كه عدالت نورزيد. عدالت ورزيد كه به تقوى نزديكتر است و از خدا بترسيد كه او به هر كارى كه مى‌كنيد آگاه است.'),
(678, 5, 9, 'خدا به كسانى كه ايمان آورده‌اند و كارهاى نيكو كرده‌اند، وعده آمرزش و مزدى بزرگ داده است.'),
(679, 5, 10, 'و آنان كه كافر شده‌اند و آيات ما را تكذيب كرده‌اند اهل جهنمند.'),
(680, 5, 11, 'اى كسانى كه ايمان آورده‌ايد، از نعمتى كه خدا به شما ارزانى داشته است ياد كنيد: آنگاه كه گروهى قصد آن كردند تا بر شما دست يازند و خدا دست آنان را از شما كوتاه كرد. از خدا بترسيد. و مؤمنانى بر خدا توكل مى‌كنند.'),
(681, 5, 12, 'خداوند از بنى اسرائيل پيمان گرفت و از ميان آنان دوازده نقيب برانگيختيم. و خدا گفت: اگر نماز بخوانيد و زكات بدهيد و به پيامبران من ايمان بياوريد و ياريشان كنيد و به خدا قرض‌الحسنه بدهيد، من با شمايم. بديهايتان را مى‌زدايم و شما را به بهشتهايى داخل مى‌كنم كه در آن نهرها روان باشد. و هر كس از شما كه از آن پس كافر شود راه راست را گم كرده است.'),
(682, 5, 13, 'و اهل كتاب -جز اندكى از آنها- چون پيمانشان را شكستند، لعنتشان كرديم و دلهايشان را سخت گردانيديم. كلمات را از معنى خود منحرف مى‌سازند و از آن پند كه به ايشان داده شده بود بهره خويش فراموش كرده‌اند. و همواره از كارهاى خائنانه‌شان آگاه مى‌شوى. عفوشان كن و از گناهشان درگذر كه خدا نيكوكاران را دوست مى‌دارد.'),
(683, 5, 14, 'و از كسانى كه گفتند كه ما نصرانى هستيم پيمان گرفتيم. پس قسمتى از اندرزهايى را كه به آنها داده بوديم فراموش كردند و ما نيز ميان آنها تا روز قيامت كينه و دشمنى افكنديم. به زودى خدا آنان را از كارهايى كه مى‌كنند آگاه خواهد ساخت.'),
(684, 5, 15, 'اى اهل كتاب، پيامبر ما نزد شما آمد تا بسيارى از كتاب خدا را كه پنهان مى‌داشتيد برايتان بيان كند و از بسيارى درگذرد، و از جانب خدا نورى و كتابى صريح و آشكار بر شما نازل شده است،'),
(685, 5, 16, 'تا خدا بَدان هر كس را كه در پى خشنودى اوست به راه‌هاى سلامت هدايت كند و به فرمان خود از تاريكى به روشناييشان ببرد و آنان را به راه راست هدايت كند.'),
(686, 5, 17, 'آنان كه گفتند كه خدا همان مسيح پسر مريم است، كافر شدند. بگوى: چه كسى مى‌تواند عذاب خدا را دفع كند اگر اراده كند مسيح پسر مريم و مادرش و همه اهل زمين را به هلاكت رساند؟ از آنِ خداست فرمانروايى آسمانها و زمين و هر چه ما بين آنهاست. آنچه مى‌خواهد مى‌آفريند و بر هر چيز تواناست.'),
(687, 5, 18, 'يهوديان و مسيحيان گفتند كه ما فرزندان و دوستان خدا هستيم. بگوى: پس چرا شما را به پاداش گناهانتان عذاب مى‌كند؟ بلكه شما انسانهايى از جمله آفريدگان هستيد. هر كس را كه بخواهد مى‌آمرزد و هر كس را كه بخواهد عذاب مى‌كند و از آن خداست فرمانروايى آسمانها و زمين و آنچه در ميان آنهاست و بازگشت همه به اوست.'),
(688, 5, 19, 'اى اهل كتاب فرستاده ما در دورانى كه پيامبرانى نبودند مبعوث شد تا حق را بر شما آشكار كند و نگوييد كه مژده‌دهنده و بيم‌دهنده‌اى بر ما مبعوث نشده است. اينك آن مژده‌دهنده و بيم‌دهنده آمده است و خدا بر هر چيز تواناست.'),
(689, 5, 20, 'و موسى به قوم خود گفت: اى قوم من، نعمتى را كه خدا بر شما ارزانى داشته است ياد كنيد، كه از ميان شما پيامبران پديد آورد و شما را صاحبان اختيار خويش گردانيد و به شما چيزهايى عنايت كرد كه به هيچ يك از مردم جهان عنايت نكرده است.'),
(690, 5, 21, 'اى قوم من، به زمين مقدسى كه خدا برايتان مقرر كرده است داخل شويد و بازپس‌مگرديد كه زيان‌ديده باز مى‌گرديد.'),
(691, 5, 22, 'گفتند: اى موسى، در آنجا مردمى جبارند و ما به آن سرزمين درنياييم تا آنگاه كه آن جباران بيرون شوند. اگر آنان از آن سرزمين بيرون شوند، بدان داخل شويم.'),
(692, 5, 23, 'دو مرد از آنان كه پرهيزگارى پيشه داشتند و خدا نعمتشان عطا كرده بود گفتند: از اين دروازه بر آنان داخل شويد؛ و چون به شهر درآمديد شما پيروز خواهيد شد. و بر خدا توكل كنيد اگر از مؤمنان هستيد.'),
(693, 5, 24, 'گفتند: اى موسى، تا وقتى كه جباران در آنجايند هرگز بدان شهر داخل نخواهيم شد. ما اينجا مى‌نشينيم، تو و پروردگارت برويد و نبرد كنيد.'),
(694, 5, 25, 'گفت: اى پروردگار من، من تنها مالك نفس خويش و برادرم هستم. ميان من و اين مردم نافرمان جدايى بينداز.'),
(695, 5, 26, 'خدا گفت: ورود به آن سرزمين به مدت چهل سال برايشان حرام شد و در آن بيابان سرگردان خواهند ماند. پس براى اين نافرمانان اندوهگين مباش.'),
(696, 5, 27, 'و داستان راستين دو پسر آدم را بر ايشان بخوان، آنگاه كه قربانيى كردند. از يكيشان پذيرفته آمد و از ديگرى پذيرفته نشد. گفت: تو را مى‌كشم. گفت: خدا قربانى پرهيزگاران را مى‌پذيرد.'),
(697, 5, 28, 'اگر تو بر من دست گشايى و مرا بكشى، من بر تو دست نگشايم كه تو را بكشم. من از خدا كه پروردگار جهانيان است مى‌ترسم.'),
(698, 5, 29, 'مى‌خواهم كه هم گناه مرا به گردن گيرى و هم گناه خود را تا از دوزخيان گردى كه اين است پاداش ستمكاران.'),
(699, 5, 30, 'نفسش او را به كشتن برادر ترغيب كرد، و او را كشت و از زيانكاران گرديد.'),
(700, 5, 31, 'خدا كلاغى را واداشت تا زمين را بكاود و به او بياموزد كه چگونه جسد برادر خود پنهان سازد. گفت: واى بر من، نتوانم همانند اين كلاغ باشم و پيكر برادرم را دفن كنم. و در زمره پشيمانان درآمد.'),
(701, 5, 32, 'از اين رو بر بنى اسرائيل مقرر داشتيم كه هر كس كس ديگر را نه به قصاص قتل كسى يا ارتكاب فسادى بر روى زمين بكشد، چنان است كه همه مردم را كشته باشد. و هر كس كه به او حيات بخشد چون كسى است كه همه مردم را حيات بخشيده باشد. و به تحقيق پيامبران ما همراه با دلايل روشن بر آنها مبعوث شدند، باز هم بسيارى از آنها همچنان بر روى زمين از حد خويش تجاوز مى‌كردند.'),
(702, 5, 33, 'جزاى كسانى كه با خدا و پيامبرش جنگ مى‌كنند و در زمين به فساد مى‌كوشند، آن است كه كشته شوند، يا بردار گردند يا دستها و پاهايشان يكى از چپ و يكى از راست بريده شود يا از سرزمين خود تبعيد شوند. اينها رسواييشان در اين جهان است و در آخرت نيز به عذابى بزرگ گرفتار آيند.'),
(703, 5, 34, 'مگر كسانى كه پيش از آنكه شما بر آنها دست يابيد توبه كنند. پس بدانيد كه خدا آمرزنده و مهربان است.'),
(704, 5, 35, 'اى كسانى كه ايمان آورده‌ايد، از خدا بترسيد و به او تقرب جوييد و در راهش جهاد كنيد. باشد كه رستگار گرديد.'),
(705, 5, 36, 'آنان كه كافر شده‌اند اگر همه آنچه در روى زمين است و همانند آن متعلق به آنها باشد و بخواهند خود را با آن از عذاب روز قيامت باز خرند، از ايشان پذيرفته نيايد و به عذاب دردآور گرفتار آيند.'),
(706, 5, 37, 'مى‌خواهند كه از آتش بيرون آيند در حالى كه بيرون‌آمدنى نباشند و عذابشان پاينده است.'),
(707, 5, 38, 'دست مرد دزد و زن دزد را به كيفر كارى كه كرده‌اند ببريد. اين عقوبتى است از جانب خدا، كه او پيروزمند و حكيم است.'),
(708, 5, 39, 'هر كس پس از كردار ناپسندش توبه كند و به صلاح آيد، خدا توبه او را مى‌پذيرد، كه او آمرزنده و مهربان است.'),
(709, 5, 40, 'آيا ندانسته‌اى كه فرمانروايى آسمانها و زمين از آن خداست، هر كه را بخواهد عذاب مى‌كند و هر كه را بخواهد مى‌آمرزد و بر هر كارى تواناست؟'),
(710, 5, 41, 'اى پيامبر، غمگين نكند تو را كردار آنان كه به كفر مى‌شتابند. چه آنهايى كه به زبان گفتند كه ايمان آورديم و به دل ايمان نياورده‌اند و چه آن يهودان كه گوش مى‌سپارند تا دروغ بندند و براى گروهى ديگر كه خود نزد تو نمى‌آيند سخن‌چينى مى‌كنند، و سخن خدا را دگرگون مى‌سازند، و مى‌گويند: اگر شما را اينچنين گفت بپذيريد و گرنه از وى دورى گزينيد. و هر كس را كه خدا عذاب او بخواهد، تواش از قهر خدا رهايى نخواهى داد. اينان كسانى هستند كه خدا نخواسته است كه دلهايشان را پاك گرداند. آنان را در دنيا خوارى و در آخرت عذابى بزرگ است.'),
(711, 5, 42, 'گوش‌نهادگان بر دروغند، خورندگان حرامند. پس اگر نزد تو آمدند ميانشان حكم كن يا از ايشان رويگردان شو؛ و اگر رويگردان شوى هيچ به تو زيانى نرسانند. و اگر ميانشان حكم كنى به عدالت حكم كن كه خدا عدالت‌پيشگان را دوست دارد.'),
(712, 5, 43, 'چگونه تو را داور قرار مى‌دهند، در حالى كه تورات كه حاوى حكم خداست در نزد آنهاست؟ سپس از حكم تو رويگردان مى‌شوند و اينان ايمان نياورده‌اند.'),
(713, 5, 44, 'ما تورات را كه در آن هدايت و روشنايى است، نازل كرديم. پيامبرانى كه تسليم فرمان بودند بنابر آن براى يهود حكم كردند و نيز خداشناسان و دانشمندان كه به حفظ كتاب خدا مأمور بودند و بر آن گواهى دادند، پس، از مردم مترسيد، از من بترسيد و آيات مرا به بهاى اندك مفروشيد. و هر كه بر وفق آياتى كه خدا نازل كرده است حكم نكند، كافر است.'),
(714, 5, 45, 'و در تورات بر آنان مقرر داشتيم كه نفس در برابر نفس و چشم در برابر چشم و بينى در برابر بينى و گوش در برابر گوش و دندان در برابر دندان و هر زخمى را قصاصى است. و هر كه از قصاص درگذرد، گناهش را كفاره‌اى خواهد بود. و هر كه به آنچه خدا نازل كرده است حكم نكند، از ستمكاران است.'),
(715, 5, 46, 'و از پى آنها عيسى پسر مريم را فرستاديم كه تصديق‌كننده توراتى بود كه پيش از او فرستاده بوديم و انجيل را كه تصديق‌كننده تورات پيش از او بود به او داديم كه در آن هدايت و روشنايى بود و براى پرهيزگاران هدايت و موعظه‌اى.'),
(716, 5, 47, 'و بايد كه اهل انجيل بر وفق آنچه خدا در آن كتاب نازل كرده است داورى كنند. زيرا هر كس به آنچه خدا نازل كرده است داورى نكند، از نافرمانان است.'),
(717, 5, 48, 'و اين كتاب را به راستى بر تو نازل كرديم؛ تصديق‌كننده و حاكم بر كتابهايى است كه پيش از آن بوده‌اند. پس بر وفق آنچه خدا نازل كرده است در ميانشان حكم كن و از پى خواهشهاشان مرو تا آنچه را از حق بر تو نازل شده است واگذارى. براى هر گروهى از شما شريعت و روشى نهاديم. و اگر خدا مى‌خواست همه شما را يك امت مى‌ساخت. ولى خواست در آنچه به شما ارزانى داشته است بيازمايدتان. پس در خيرات بر يكديگر پيشى گيريد. همگى بازگشتتان به خداست تا از آنچه در آن اختلاف مى‌كرديد آگاهتان سازد.'),
(718, 5, 49, 'ميانشان بر وفق آنچه خدا نازل كرده است حكم كن و از خواهشهاشان پيروى مكن و از ايشان بپرهيز كه مبادا بفريبندت تا از بعضى از چيزهايى كه خدا بر تو نازل كرده است سرباز زنى. و اگر رويگردان شدند بدان كه خدا مى‌خواهد آنان را به پاداش برخى گناهانشان عقوبت كند، و هر آينه بسيارى از مردم نافرمانند.'),
(719, 5, 50, 'آيا حكم جاهليت را مى‌جويند؟ براى آن مردمى كه اهل يقين هستند چه حكمى از حكم خدا بهتر است؟'),
(720, 5, 51, 'اى كسانى كه ايمان آورده‌ايد، يهود و نصارا را به دوستى برمگزينيد. آنان خود دوستان يكديگرند. هر كس از شما كه ايشان را به دوستى گزيند در زمره آنهاست. و خدا ستمكاران را هدايت نمى كند.'),
(721, 5, 52, 'آنهايى را كه در دل مرضى دارند مى‌بينى كه به صحبتشان مى‌شتابند، مى‌گويند: مى‌ترسيم كه به ما آسيبى رسد. اما باشد كه خدا فتحى پديد آرد يا كارى كند، آنگاه از آنچه در دل نهان داشته بودند پشيمان شوند.'),
(722, 5, 53, 'آنها كه ايمان آورده‌اند مى‌گويند: آيا اينان همان كسانند كه به خدا سوگندهاى سخت مى‌خوردند كه با شما خواهند بود؟ اعمالشان باطل گرديد و خود در زمره زيانكاران درآمدند.'),
(723, 5, 54, 'اى كسانى كه ايمان آورده‌ايد، هر كه از شما از دينش بازگردد چه باك؛ زودا كه خدا مردمى را بياورد كه دوستشان بدارد و دوستش بدارند. در برابر مؤمنان فروتنند و در برابر كافران سركش؛ در راه خدا جهاد مى‌كنند و از ملامت هيچ ملامتگرى نمى‌هراسند. اين فضل خداست كه به هر كس كه خواهد ارزانى دارد، و خداوند بخشاينده و داناست.'),
(724, 5, 55, 'جز اين نيست كه ولىّ شما خداست و رسول او و مؤمنانى كه نماز مى‌خوانند و همچنان كه در ركوعند انفاق مى‌كنند.'),
(725, 5, 56, 'و هر كه خدا و پيامبر او و مؤمنان را ولىّ خود گزيند، بداند كه پيروزمندان گروه خداوندند.'),
(726, 5, 57, 'اى كسانى كه ايمان آورده‌ايد، اهل كتاب را كه دين شما را به مسخره و بازى مى‌گيرند، و نيز كافران را به دوستى برمگزينيد. و اگر ايمان آورده‌ايد از خدا بترسيد.'),
(727, 5, 58, 'و چون بانگ نماز كنيد آن را به مسخره و بازيچه گيرند، زيرا مردمى هستند كه نمى‌انديشند.'),
(728, 5, 59, 'بگو: اى اهل كتاب، آيا ما را سرزنش مى‌كنيد؟ جز اين است كه ما به خدا و آنچه بر ما نازل شده و آنچه پيش از اين نازل شده است ايمان آورده‌ايم و شما بيشترين نافرمان هستيد؟'),
(729, 5, 60, 'بگو: آيا شما را از كسانى كه در نزد خدا كيفرى بدتر از اين دارند خبر بدهم: كسانى كه خدايشان لعنت كرده و بر آنها خشم گرفته و بعضى را بوزينه و خوك گردانيده است و خود بت پرستيده‌اند؟ اينان را بدترين جايگاه است و از راه راست گمگشته‌ترند.'),
(730, 5, 61, 'و چون نزد شما آمدند، گفتند كه ايمان آورديم؛ و حال آنكه با كفر داخل شدند و با كفر خارج گشتند، و خدا به آنچه پنهان مى‌دارند آگاه‌تر است.'),
(731, 5, 62, 'مى‌بينى بسيارى از ايشان را كه به گناه و تجاوز و حرامخوارگى مى‌شتابند. چه بد كارهايى مى‌كردند.'),
(732, 5, 63, 'از چه روى خداپرستان و دانشمندان، آنان را از گفتار بد و حرامخوارگى باز نمى‌دارند. چه بد كارهايى مرتكب مى‌شدند.'),
(733, 5, 64, 'يهود گفتند كه دست خدا بسته است. دستهاى خودشان بسته باد. و بدين سخن كه گفتند ملعون گشتند. دستهاى خدا گشاده است. به هر سان كه بخواهد روزى مى‌دهد. و آنچه بر تو از جانب پروردگارت نازل شده است، به طغيان و كفر بيشترشان خواهد افزود. ما تا روز قيامت ميانشان دشمنى و كينه افكنده‌ايم. هرگاه كه آتش جنگ را افروختند خدا خاموشش ساخت. و آنان در روى زمين به فساد مى‌كوشند، و خدا مفسدان را دوست ندارد.'),
(734, 5, 65, 'اگر اهل كتاب ايمان بياورند و پرهيزگارى كنند، گناهانشان را خواهيم زدود و آنها را به بهشتهاى پرنعمت داخل خواهيم كرد.'),
(735, 5, 66, 'و اگر تورات و انجيل و آنچه را كه از جانب خدا بر آنها نازل شده بر پاى دارند، از فراز سر و زير پايشان روزى بخورند. بعضى از ايشان مردمى ميانه‌رو هستند و بسياريشان بدكردارند.'),
(736, 5, 67, 'اى پيامبر، آنچه را از پروردگارت بر تو نازل شده است به مردم برسان. اگر چنين نكنى امر رسالت او را ادا نكرده‌اى. خدا تو را از مردم حفظ مى‌كند، كه خدا مردم كافر را هدايت نمى‌كند.'),
(737, 5, 68, 'بگو: اى اهل كتاب، شما هيچ نيستيد، تا آنگاه كه تورات و انجيل و آنچه را از جانب پروردگارتان بر شما نازل شده است بر پاى داريد. آنچه از جانب پروردگارت بر تو نازل شده است بر طغيان و كفر بيشترينشان بيفزايد. پس بر اين مردم كافر غمگين مباش.'),
(738, 5, 69, 'هرآينه از ميان آنان كه ايمان آورده‌اند و يهود و صابئان و نصارا هر كه به خدا و روز قيامت ايمان داشته باشد و كار شايسته كند بيمى بر او نيست و محزون نمى‌شود.'),
(739, 5, 70, 'ما از بنى اسرائيل پيمان گرفتيم و پيامبرانى برايشان فرستاديم. هرگاه كه پيامبرى چيزى مى‌گفت كه با خواهش دلشان موافق نبود، گروهى را تكذيب مى‌كردند و گروهى را مى‌كشتند.'),
(740, 5, 71, 'و پنداشتند كه عقوبتى نخواهد بود. پس كور و كر شدند. آنگاه خدا توبه‌شان بپذيرفت. باز بسيارى از آنها كور و كر شدند. هر چه مى‌كنند خدا مى‌بيند.'),
(741, 5, 72, 'به تحقيق آنان كه گفتند كه خدا همان مسيح پسر مريم است، كافر شدند. مسيح گفت: اى بنى اسرائيل، اللّه پروردگار من و پروردگار خود را بپرستيد. زيرا هر كس كه براى خداوند شريكى قرار دهد خدا بهشت را بر او حرام كند، و جايگاه او آتش است و ستمكاران را ياورى نيست.'),
(742, 5, 73, 'آنان كه گفتند: اللّه سومين سه است، كافر شدند. در حالى كه هيچ خدايى جز اللّه نيست. اگر از آنچه مى‌گويند باز نايستند به كافرانشان عذابى دردآور خواهد رسيد.'),
(743, 5, 74, 'آيا به درگاه خدا توبه نمى‌كنند و از او آمرزش نمى‌خواهند؟ خدا آمرزنده و مهربان است.'),
(744, 5, 75, 'مسيح پسر مريم جز پيامبرى نبود، كه پيامبرانى پيش از او بوده‌اند، و مادرش زنى راستگوى بود كه هر دو غذا مى‌خوردند. بنگر كه چگونه آيات را برايشان بيان مى‌كنيم. سپس بنگر كه چگونه از حق روى مى‌گردانند.'),
(745, 5, 76, 'بگو: آيا جز اللّه خداى ديگرى را كه براى شما مالك هيچ سود و زيانى نيست مى‌پرستيد و حال آنكه اللّه شنوا و داناست؟'),
(746, 5, 77, 'بگو: اى اهل كتاب، به ناحق در دين خويش غُلوّ مكنيد؛ و از خواهشهاى آن مردمى كه از پيش گمراه شده بودند و بسيارى را گمراه كردند و خود از راه راست منحرف شدند پيروى مكنيد.'),
(747, 5, 78, 'از بنى اسرائيل آنان كه كافر شدند به زبان داود و عيسى بن مريم لعنت شدند، و اين لعنت پاداش عصيان و تجاوزشان بود.'),
(748, 5, 79, 'از كار زشتى كه مى‌كردند يكديگر را نهى نمى‌كردند و هرآينه بدكارى مى‌كردند.'),
(749, 5, 80, 'بسيارى از ايشان را مى‌بينى كه با كافران دوستى مى‌ورزند. بد است آنچه پيشاپيش براى خود فرستادند. خشم خدا بر آنهاست و در عذاب جاودانه‌اند.'),
(750, 5, 81, 'اگر به خدا و پيامبر و آنچه بر او نازل شده ايمان آورده بودند كافران را به دوستى نمى‌گرفتند، ولى بيشترشان فاسقانند.'),
(751, 5, 82, 'دشمن‌ترين مردم نسبت به كسانى كه ايمان آورده‌اند يهود و مشركان را مى‌يابى، و مهربان‌ترين كسان نسبت به آنان كه ايمان آورده‌اند كسانى را مى‌يابى كه مى‌گويند: ما نصرانى هستيم. زيرا بعضى از ايشان كشيشان و راهبان هستند و آنها سرورى نمى‌جويند.'),
(752, 5, 83, 'چون آنچه را كه بر پيامبر نازل شده بشنوند، و حقيقت را دريابند، چشمانشان پر از اشك مى‌شود. مى‌گويند: اى پروردگار ما، ايمان آورديم، ما را نيز در زمره شهادت‌دهندگان بنويس.'),
(753, 5, 84, 'چرا به خدا و اين آيين حق كه بر ما نازل شده است ايمان نياوريم و طمع نورزيم در اينكه پروردگار ما ما را در شمار صالحان آورد؟'),
(754, 5, 85, 'به پاداش اين سخن كه گفتند، خدا آنان را به بهشتهايى كه در آن نهرها روان است پاداش داد. در آن جاودانند و اين است پاداش نيكوكاران.'),
(755, 5, 86, 'و آنان كه كفر ورزيدند و آيات ما را تكذيب كردند اهل جهنمند.'),
(756, 5, 87, 'اى كسانى كه ايمان آورده‌ايد، چيزهاى پاكيزه‌اى را كه خدا بر شما حلال كرده است حرام مكنيد و از حد درمگذريد كه خدا تجاوزكنندگان از حد را دوست ندارد.'),
(757, 5, 88, 'از چيزهاى حلال و پاكيزه‌اى كه خدا به شما روزى داده است بخوريد و از خدايى كه به او ايمان آورده‌ايد بترسيد.'),
(758, 5, 89, 'خداوند شما را به سبب سوگندهاى لغوتان بازخواست نخواهد كرد. ولى به سبب شكستن سوگندهايى كه به قصد مى‌خوريد، بازخواست مى‌كند و كفاره آن اطعام ده مسكين است از غذاى متوسطى كه به خانواده خويش مى‌خورانيد يا پوشيدن آنها يا آزاد كردن يك بنده، و هر كه نيابد سه روز روزه داشتن. اين كفاره قسم است، هرگاه كه قسم خورديد. به قسمهاى خود وفا كنيد. خدا آيات خود را براى شما اينچنين بيان مى‌كند، باشد كه سپاسگزار باشيد.'),
(759, 5, 90, 'اى كسانى كه ايمان آورده‌ايد، شراب و قمار و بتها و گروبندى با تيرها پليدى و كار شيطان است، از آن اجتناب كنيد تا رستگار شويد.'),
(760, 5, 91, 'شيطان مى‌خواهد با شراب و قمار ميان شما كينه و دشمنى افكند و شما را از ياد خدا و نماز باز دارد، آيا بس مى‌كنيد؟'),
(761, 5, 92, 'خدا و پيامبرش را اطاعت كنيد و پروا كنيد. اگر رويگردان شويد بدانيد كه وظيفه پيامبر ما رسانيدن پيام روشن خداوند است.'),
(762, 5, 93, 'بر آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند در آنچه خورده‌اند گناهى نيست، هرگاه پرهيزگارى كنند و ايمان بياورند و به كارهاى شايسته پردازند، باز هم پرهيزگارى كنند و ايمان بياورند، باز هم پرهيزگارى كنند و نيكى، كه خدا نيكوكاران را دوست دارد.'),
(763, 5, 94, 'اى كسانى كه ايمان آورده‌ايد، خدا شما را به صيدى كه به دست مى‌گيريد يا به نيزه شكار مى‌كنيد، مى‌آزمايد تا بداند چه كسى در نهان از او مى‌ترسد. و هر كه از اين پس از حد تجاوز كند او راست عذابى دردآور.'),
(764, 5, 95, 'اى كسانى كه ايمان آورده‌ايد، هرگاه كه در احرام باشيد شكار را مكشيد. هر كه صيد را به عمد بكشد جزاى او قربانى كردن حيوانى است همانند آنچه كشته است به شرط آنكه دو عادل از شما گواهى دهند و قربانى را به كعبه رساند، يا به كفاره درويشان را طعام دهد، يا برابر آن روزه بگيرد، تا عقوبت كار خود بچشد. از آنچه در گذشته كرده‌ايد خدا عفو كرده است، ولى هر كه بدان باز گردد خدا از او انتقام مى‌گيرد، كه خدا پيروزمند و انتقام‌گيرنده است.'),
(765, 5, 96, 'شكار دريايى و خوردن آن به جهت بهره‌مند شدنتان از آن، بر شما و مسافران حلال شده است. و شكار صحرايى تا هنگامى كه در احرام هستيد بر شما حرام شده. از خداوندى كه به نزد او گرد آورده مى‌شويد بترسيد.'),
(766, 5, 97, 'خدا كعبه، بيت الحرام، را با ماه حرام و قربانى بى‌قلاده و قربانى با قلاده قوام كار مردم گردانيد، تا بدانيد كه خدا هر چه را كه در آسمانها و زمين است مى‌داند و او بر هر چيزى آگاه است.'),
(767, 5, 98, 'بدانيد كه عقوبت خدا سخت است و هم او آمرزنده و مهربان است.'),
(768, 5, 99, 'بر پيامبر جز رسانيدن پيام وظيفه‌اى نيست. و آنچه را كه آشكار مى‌سازيد يا پنهان مى‌داريد خدا مى‌داند.'),
(769, 5, 100, 'بگو: ناپاك و پاك برابر نيستند، هر چند فراوانى ناپاك تو را به اعجاب افكند. پس اى خردمندان، از خداى بترسيد، باشد كه رستگار گرديد.'),
(770, 5, 101, 'اى كسانى كه ايمان آورده‌ايد، از چيزهايى كه چون براى شما آشكار شوند اندوهگينتان مى‌كنند، مپرسيد. و اگر سؤال از آنها را واگذاريد تا به هنگام نزول قرآن، برايتان آشكار خواهد شد. خدا از آنها عفو كرده است كه آمرزنده و بردبار است.'),
(771, 5, 102, 'مردمى كه پيش از شما بودند از آن چيزها سؤال كردند و بدان سبب كافر شدند.'),
(772, 5, 103, 'خداوند درباره بَحيره و سائبه و وصيله و حامى حكمى نكرده است؛ ولى كافران بر خدا دروغ مى‌بندند و بيشترينشان بى‌خردند.'),
(773, 5, 104, 'و چون به ايشان گويند كه به آنچه خدا نازل كرده است و به پيامبر روى آوريد، گويند: آن آيينى كه پدران خود را بدان معتقد يافته‌ايم ما را بس است. حتى اگر پدرانشان هيچ نمى‌دانسته‌اند و راه هدايت نيافته بوده‌اند؟'),
(774, 5, 105, 'اى كسانى كه ايمان آورده‌ايد، به خود پردازيد. اگر شما هدايت يافته‌ايد، آنان كه گمراه مانده‌اند به شما زيانى نرسانند. بازگشت همه شما نزد خداست، تا شما را به آن كارها كه مى‌كرده‌ايد آگاه گرداند.'),
(775, 5, 106, 'اى كسانى كه ايمان آورده‌ايد، چون مرگتان فرا رسد به هنگام وصيت دو عادل را از ميان خودتان به شهادت گيريد، يا از غير خودتان، هرگاه كه در سفر بوديد و مرگتان فرارسيد. اگر از آن دو در شك بوديد نگاهشان داريد تا بعد از نماز، آنگاه به خدا سوگند خورند كه اين شهادت را به هيچ قيمتى دگرگون نكنيم هرچند به سود خويشاوندمان باشد و آن را كتمان نكنيم، اگر جز اين باشد از گناهكارانيم.'),
(776, 5, 107, 'و هرگاه معلوم شود كه آن دو شاهد مرتكب گناه خيانت شده‌اند، دو شاهد ديگر كه اولى‌تر از آن دو باشند جاى ايشان را بگيرند. آن دو به خدا قسم خورند كه شهادت ما از شهادت آن دو درست‌تر است و ما از حق تجاوز نكنيم، هرگاه چنين كنيم از ستمكاران باشيم.'),
(777, 5, 108, 'اين شيوه نزديك‌تر به آن است كه هم شهادت را بر وجه خود ادا كنند يا پس از سوگند خوردن از ردّ سوگندهايشان بترسند. از خدا بترسيد. و گوش فرا داريد. خدا مردم نافرمان را هدايت نمى‌كند.'),
(778, 5, 109, 'روزى كه خدا پيامبران را گرد آورد و بپرسد كه دعوت شما را چگونه پاسخ دادند؟ گويند: ما را هيچ دانشى نيست، كه دانا به غيب تو هستى.'),
(779, 5, 110, 'خدا به عيسى بن مريم گفت: نعمتى را كه به تو و مادرت ارزانى داشته‌ام ياد كن، آن زمان كه به روح‌القدس ياريت كردم تا تو چه در گهواره و چه در بزرگسالى سخن گويى، و به تو كتاب و حكمت و تورات و انجيل آموختم. و آنگاه كه به امر من از گِل چيزى چون پرنده ساختى و در آن دميدى و به امر من پرنده‌اى شد و كور مادرزاد و پيسى‌گرفته را به فرمان من شفا دادى و مردگان را به فرمان من زنده بيرون آوردى و چون با اين دلايل روشن نزد بنى اسرائيل آمدى، من آنان را از آسيب رساندن به تو بازداشتم و از ميانشان كسانى كه كافر بودند گفتند كه اين جز جادويى آشكار نيست.'),
(780, 5, 111, 'و به حواريان وحى كردم: به من و به پيامبر من ايمان بياوريد. گفتند: ايمان آورديم، گواه باش كه ما تسليم هستيم.'),
(781, 5, 112, 'و حواريان پرسيدند: اى عيسى بن مريم، آيا پروردگار تو مى‌تواند كه براى ما از آسمان مائده‌اى فرستد؟ گفت: اگر ايمان آورده‌ايد، از خدا بترسيد.'),
(782, 5, 113, 'گفتند: مى‌خواهيم كه از آن مائده بخوريم تا دلهايمان آرام گيرد و بدانيم كه تو به ما راست گفته‌اى و بر آن شهادت دهيم.'),
(783, 5, 114, 'عيسى بن مريم گفت: بار خدايا، اى پروردگار ما، براى ما مائده‌اى از آسمان بفرست، تا ما را و آنان را كه بعد از ما مى‌آيند عيدى و نشانى از تو باشد، و ما را روزى ده كه تو بهترين روزى‌دهندگان هستى.'),
(784, 5, 115, 'خدا گفت: من آن مائده را براى شما مى‌فرستم؛ ولى هر كه از شما از آن پس كافر شود چنان عذابش مى‌كنم كه هيچ يك از مردم جهان را آن چنان عذاب نكرده باشم.'),
(785, 5, 116, 'و آنگاه كه خدا به عيسى بن مريم گفت: آيا تو به مردم گفتى كه مرا و مادرم را سواى اللّه به خدايى گيريد؟ گفت: به پاكى ياد مى‌كنم تو را. نسزد مرا كه چيزى گويم كه نه شايسته آن باشم. اگر من چنين گفته بودم تو خود مى‌دانستى زيرا به آنچه در ضمير من مى‌گذرد دانايى و من از آنچه در ذات تو است بى‌خبرم. زيرا تو داناترين كسان به غيب هستى.'),
(786, 5, 117, 'من به آنان جز آنچه تو فرمانم داده بودى نگفتم. گفتم كه اللّه پروردگار مرا و پروردگار خود را بپرستيد. و من تا در ميانشان بودم نگهبان عقيدتشان بودم و چون مرا ميرانيدى تو خود نگهبان عقيدتشان گشتى. و تو بر هر چيزى آگاهى.'),
(787, 5, 118, 'اگر آنان را عذاب كنى، بندگان تو هستند و اگر آنان را بيامرزى، تو پيروزمند و حكيمى.'),
(788, 5, 119, 'خدا گفت: اين روزى است كه راستگويان را راستى گفتارشان سود دهد. از آن آنهاست بهشتهايى كه در آن نهرها جارى است. همواره در آن جاويدان خواهند بود. خدا از آنان خشنود است و آنان نيز از خدا خشنودند. و اين كاميابى بزرگى است.'),
(789, 5, 120, 'از آن خداست فرمانروايى آسمانها و زمين و آنچه ميان آنهاست. و او بر هر كارى تواناست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ستايش از آن خداوندى است كه آسمانها و زمين را بيافريد و تاريكيها و روشنايى را پديدار كرد، با اين همه، كافران با پروردگار خويش ديگرى را برابر مى‌دارند.'),
(791, 6, 2, 'اوست كه شما را از گِل بيافريد و عمرى مقرر كرد: مدتى در نزد او معين. با اين همه، ترديد مى‌ورزيد.'),
(792, 6, 3, 'در آسمانها و زمين، اوست كه خداوند است. نهان و آشكارتان را مى‌داند و از كردارتان آگاه است.'),
(793, 6, 4, 'و هيچ آيه‌اى از آيات پروردگارشان بر ايشان نازل نشد، جز آنكه از آن رويگردان شدند.'),
(794, 6, 5, 'چون سخن حق بر آنها عرضه شد، تكذيبش كردند. پس به زودى، خبر چيزهايى كه به ريشخندشان مى‌گرفتند به آنها خواهد رسيد.'),
(795, 6, 6, 'آيا نديده‌اند كه پيش از آنها چه مردمى را هلاك كرده‌ايم؟ مردمى كه در زمين مكانتشان داده بوديم. آنچنان مكانتى كه به شما نداده‌ايم. و برايشان از آسمان بارانهاى پى در پى فرستاديم و رودها از زير پايشان روان ساختيم. آنگاه به كيفر گناهانشان هلاكشان كرديم و پس از آنها مردمى ديگر پديد آورديم.'),
(796, 6, 7, 'حتى اگر كتابى نوشته بر روى كاغذ بر تو نازل مى‌كرديم و آن را با دست خويش لمس مى‌كردند، باز هم آن كافران مى‌گفتند كه اين جز جادويى آشكار نيست.'),
(797, 6, 8, 'و گفتند: چرا فرشته‌اى بر او نازل نشده است؟ اگر فرشته‌اى مى‌فرستاديم كه كار به پايان مى‌رسيد و به آنها هيچ مهلتى داده نمى‌شد.'),
(798, 6, 9, 'و اگر آن پيامبر را از ميان فرشتگان بر مى‌گزيديم باز هم او را به صورت مردى مى‌فرستاديم و اين خلط و اشتباه كه پديد آورده‌اند بر جاى مى‌نهاديم.'),
(799, 6, 10, 'پيامبرانى را هم كه پيش از تو بودند مسخره مى‌كردند. پس مسخره‌كنندگان را عذابى كه به ريشخندش مى‌گرفتند فروگرفت.'),
(800, 6, 11, 'بگو: در روى زمين بگرديد و بنگريد كه پايان كار تكذيب‌كنندگان چگونه بوده است.'),
(801, 6, 12, 'بگو: از آن كيست آنچه در آسمانها و زمين است؟ بگو: از آن خداست. بخشايش را بر خود مقرر داشته، همه شما را در روز قيامت -كه در آن ترديدى نيست- گرد مى‌آورد. آنان كه به زيان خويش كار كرده‌اند، ايمان نمى‌آورند.'),
(802, 6, 13, 'از آن اوست هر چه در شب و روز جاى دارد و اوست شنوا و دانا.'),
(803, 6, 14, 'بگو: آيا ديگرى جز خدا را به دوستى گيرم كه آفريننده آسمانها و زمين است و مى‌خوراند و به طعامش نياز نيست؟ بگو: هر آينه من مأمور شده‌ام كه نخستين كسى باشم كه تسليم امر خدا شده باشد. پس، از مشركان مباش.'),
(804, 6, 15, 'بگو: از عذاب آن روز بزرگ مى‌ترسم اگر از پروردگارم فرمان نبرم.'),
(805, 6, 16, 'در آن روز، عذاب را از هر كه بگردانند مورد رحمت خدا واقع شده است، و اين كاميابى آشكارى است.'),
(806, 6, 17, 'اگر خدا به تو محنتى برساند، هيچ كس جز او دفعش نتواند كرد، و اگر به تو خيرى برساند بر هر كارى تواناست.'),
(807, 6, 18, 'و اوست قاهرى بالاتر از همه بندگان خويش و دانا و آگاه است.'),
(808, 6, 19, 'بگو: شهادت چه كسى از هر شهادتى بزرگ‌تر است؟ بگو: خدا ميان من و شما گواه است و اين قرآن بر من وحى شده است تا شما را و هر كس را كه به او برسد بيم دهم. آيا شهادت مى‌دهيد كه با اللّه خدايان ديگرى هم هستند؟ بگو: من شهادت نمى‌دهم. بگو: جز اين نيست كه او خدايى است يكتا و از آنچه با او شريك مى‌سازيد بيزارم.'),
(809, 6, 20, 'اهل كتاب او را چنان مى‌شناسند كه فرزندان خود را، اينان كه به خود زيان مى‌رسانيدند ايمان نمى‌آورند.'),
(810, 6, 21, 'چه كسى است ستمكارتر از آنكه به خدا دروغ مى‌بندد يا آيات او را دروغ مى‌انگارد؟ هر آينه ستمكاران را رستگارى نيست.'),
(811, 6, 22, 'روزى همه را گرد آوريم، سپس به آنها كه شرك آورده‌اند بگوييم: آن كسان كه مى‌پنداشتيد كه شريكان خدايند اكنون كجايند؟'),
(812, 6, 23, 'عذرى كه مى‌آورند جز اين نيست كه مى‌گويند: سوگند به خدا پروردگار ما كه ما مشرك نبوده‌ايم.'),
(813, 6, 24, 'بنگر كه چگونه بر خود دروغ بستند و آن دروغها كه ساخته بودند ناچيز گرديد.'),
(814, 6, 25, 'بعضى از آنها به سخن تو گوش مى‌دهند ولى ما بر دلهايشان پرده‌ها افكنده‌ايم تا آن را درنيابند و گوشهايشان را سنگين كرده‌ايم. و هر معجزه‌اى را كه بنگرند بدان ايمان نمى‌آورند. و چون نزد تو آيند، با تو به مجادله پردازند. كافران مى‌گويند كه اينها چيزى جز اساطير پيشينيان نيست.'),
(815, 6, 26, 'اينان مردم را از پيامبر بازمى‌دارند و خود از او كناره مى‌جويند و حال آنكه نمى‌دانند كه تنها خويشتن را به هلاكت مى‌رسانند.'),
(816, 6, 27, 'اگر آنها را در آن روز كه در برابر آتش نگاهشان داشته‌اند بنگرى، مى‌گويند: اى كاش ما را باز گردانند تا آيات پروردگارمان را تكذيب نكنيم و از مؤمنان باشيم.'),
(817, 6, 28, 'نه، آنچه را كه از اين پيش پوشيده مى‌داشتند اكنون برايشان آشكار شده، اگر آنها را به دنيا بازگردانند، باز هم به همان كارها كه منعشان كرده بودند باز مى‌گردند. اينان دروغگويانند.'),
(818, 6, 29, 'و گفتند: جز اين زندگى دنيوى ما هيچ نيست و ما ديگر بار زنده نخواهيم شد.'),
(819, 6, 30, 'و اگر ببينى آن هنگام را كه در برابر پروردگارشان ايستاده‌اند، خدا مى‌گويد: آيا اين به حق نبود؟ گويند: آرى، سوگند به پروردگارمان. گويد: به كيفر آنكه كافر بوده‌ايد عذاب خدا را بچشيد.'),
(820, 6, 31, 'زيان كردند آنهايى كه ديدار با خدا را دروغ پنداشتند. و چون قيامت به ناگهان فرا رسد، گويند: اى حسرتا بر ما به خاطر تقصيرى كه كرديم. اينان بار گناهانشان را بر پشت مى‌كشند. هان، چه بد بارى را بر دوش مى‌كشند.'),
(821, 6, 32, 'و زندگى دنيا چيزى جز بازيچه و لهو نيست و پرهيزگاران را سراى آخرت بهتر است. آيا به عقل نمى‌يابيد؟'),
(822, 6, 33, 'مى‌دانيم كه سخنشان تو را اندوهگين مى‌سازد، ولى اينان تنها تو را تكذيب نمى‌كنند؛ بلكه اين ستمكاران سخن خدا را انكار مى‌كنند.'),
(823, 6, 34, 'پيامبرانى را هم كه پيش از تو بودند تكذيب كردند ولى آنها بر آن تكذيب و آزار صبر كردند تا يارى ما فرا رسيدشان. و سخنان خدا را تغييردهنده‌اى نيست. و هر آينه پاره‌اى از اخبار پيامبران بر تو نازل شده است.'),
(824, 6, 35, 'اگر اعراض كردن آنها بر تو گران است، خواهى نقبى در زمين بجوى يا نردبانى بر آسمان بنه تا معجزه‌اى برايشان بياورى. اگر خدا بخواهد، همه را به راه راست برد. پس، از نادانان مباش.'),
(825, 6, 36, 'هرآينه تنها آنان كه مى‌شنوند مى‌پذيرند. و مردگان را خدا زنده مى‌كند و سپس همه به نزد او بازگردانده مى‌شوند.'),
(826, 6, 37, 'و گفتند: چرا معجزه‌اى از پروردگارش بر او نازل نشده؟ بگو: خدا قادر است كه معجزه‌اى فرو فرستد. ولى بيشترينشان نمى‌دانند.'),
(827, 6, 38, 'هيچ جنبنده‌اى در روى زمين نيست و هيچ پرنده‌اى با بالهاى خود در هوا نمى‌پرد مگر آنكه چون شما امتهايى هستند -ما در اين كتاب هيچ چيزى را فروگذار نكرده‌ايم- و سپس همه را در نزد پروردگارشان گرد مى‌آورند.'),
(828, 6, 39, 'آنان كه آيات ما را تكذيب كرده‌اند كرانند و لالانند و در تاريكيهايند. خدا هر كه را خواهد گمراه كند و هر كه را خواهد به راه راست اندازد.'),
(829, 6, 40, 'بگو: چه مى‌بينيد كه اگر بر شما عذاب خدا فرود آيد يا قيامت در رسد -اگر راست مى‌گوييد- باز هم جز خدا را مى‌خوانيد؟'),
(830, 6, 41, 'نه، تنها او را مى‌خوانيد و اگر بخواهد آن رنجى را كه خدا را به خاطر آن مى‌خوانيد از ميان مى‌برد و شما شريكى را كه براى او ساخته‌ايد از ياد مى‌بريد.'),
(831, 6, 42, 'هر آينه بر امتهايى كه پيش از تو بودند پيامبرانى فرستاديم و آنان را به سختيها و آفتها دچار كرديم تا مگر زارى كنند.'),
(832, 6, 43, 'پس چرا هنگامى كه عذاب ما به آنها رسيد زارى نكردند؟ زيرا دلهايشان را قساوت فراگرفته و شيطان اعمالشان را در نظرشان آراسته بود.'),
(833, 6, 44, 'چون همه اندرزهايى را كه به آنها داده شده بود فراموش كردند، همه درها را به رويشان گشوديم تا از آنچه يافته بودند شادمان گشتند، پس به ناگاه فروگرفتيمشان و همگان نوميد گرديدند.'),
(834, 6, 45, 'پس سپاس پروردگار جهانيان را كه ريشه ستمكاران بركنده شد.'),
(835, 6, 46, 'بگو: آيا مى‌دانيد كه اگر اللّه گوش و چشمان شما را باز ستاند و بر دلهايتان مهر نهد چه خدايى جز اللّه آنها را به شما باز مى‌گرداند؟ بنگر كه آيات خدا را چگونه به شيوه‌هاى گوناگون بيان مى‌كنيم. باز هم روى برمى‌تابند.'),
(836, 6, 47, 'بگو: چه مى‌بينيد، اگر عذاب خدا به ناگاه يا آشكارا بر شما فرود آيد، آيا جز ستمكاران هلاك مى‌شوند؟'),
(837, 6, 48, 'ما پيامبران را جز براى مژده‌دادن يا بيم‌كردن نمى‌فرستيم. پس هر كس كه ايمان آورد و كار شايسته كرد، بيمناك و محزون نمى‌شود.'),
(838, 6, 49, 'و به كسانى كه آيات ما را تكذيب كرده‌اند، به كيفر نافرمانيشان، عذاب خواهد رسيد.'),
(839, 6, 50, 'بگو: به شما نمى‌گويم كه خزاين خدا نزد من است. و علم غيب هم نمى‌دانم. و نمى‌گويم كه فرشته‌اى هستم. تنها از چيزى پيروى مى‌كنم كه بر من وحى شده است. بگو: آيا نابينا و بينا يكسانند؟ چرا نمى‌انديشيد؟'),
(840, 6, 51, 'آگاه ساز به اين كتاب، كسانى را كه از گردآمدن بر آستان پروردگارشان هراسناكند، كه ايشان را جز او هيچ ياورى و شفيعى نيست. باشد كه پرهيزگارى پيشه كنند.'),
(841, 6, 52, 'كسانى را كه هر بامداد و شبانگاه پروردگار خويش را مى‌خوانند و خواستار خشنودى او هستند، طرد مكن. نه چيزى از حساب آنها بر عهده تو است و نه چيزى از حساب تو بر عهده ايشان. اگر آنها را طرد كنى، در زمره ستمكاران درآيى.'),
(842, 6, 53, 'همچنين بعضى را به بعضى آزموديم، تا بگويند: آيا از ميان ما اينان بودند كه خدا به آنها نعمت داد؟ آيا خدا به سپاسگزاران داناتر نيست؟'),
(843, 6, 54, 'چون ايمان آوردگان به آيات ما نزد تو آمدند، بگو: سلام بر شما، خدا بر خويش مقرر كرده كه شما را رحمت كند، زيرا هر كس از شما كه از روى نادانى كارى بد كند، آنگاه توبه كند و نيكوكار شود، بداند كه خدا آمرزنده و مهربان است.'),
(844, 6, 55, 'و اين چنين آيات را به تفصيل بيان مى‌كنيم، تا راه و رسم مجرمان آشكار گردد.'),
(845, 6, 56, 'بگو: مرا منع كرده‌اند كه آن خدايانى را كه جز اللّه مى‌خوانيد پرستش كنم. بگو: از هوسهاى شما متابعت نمى‌كنم تا مبادا گمراه شوم و از هدايت يافتگان نباشم.'),
(846, 6, 57, 'بگو: من از پروردگارم دليلى روشن دارم و شما آن دليل را دروغ مى‌خوانيد. آنچه بدين شتاب مى‌طلبيد به دست من نيست. حكم تنها حكم خداست. حقيقت را بيان مى‌كند و او بهترين داوران است.'),
(847, 6, 58, 'بگو: اگر آنچه را كه بدين شتاب مى‌طلبيد به دست من بود، ميان من و شما كار به پايان مى‌رسيد، زيرا خدا به ستمكاران داناتر است.'),
(848, 6, 59, 'كليدهاى غيب نزد اوست. جز او كسى را از غيب آگاهى نيست. هر چه را كه در خشكى و درياست مى‌داند. هيچ برگى از درختى نمى‌اُفتد مگر آنكه از آن آگاه است. و هيچ دانه‌اى در تاريكيهاى زمين و هيچ ترى و خشكى نيست جز آنكه در كتاب مبين آمده است.'),
(849, 6, 60, 'و اوست كه شما را شب‌هنگام مى‌ميراند و هر چه در روز كرده‌ايد مى‌داند، آنگاه بامدادان شما را زنده مى‌سازد تا آن هنگام كه مدت معين عمرتان به پايان رسد. سپس بازگشتتان به نزد اوست و شما را از آنچه كرده‌ايد آگاه مى‌كند.'),
(850, 6, 61, 'اوست قاهرى فراتر از بندگانش. نگهبانانى بر شما مى‌گمارد، تا چون يكى از شما را مرگ فرا رسد فرستادگان ما بى هيچ كوتاهى و گذشتى جان او بگيرند.'),
(851, 6, 62, 'سپس به نزد خدا، مولاى حقيقى خويش بازگردانيده شوند. بدان كه حكم حكم اوست و او سريع‌ترين حسابگران است.'),
(852, 6, 63, 'بگو: چه كسى شما را از وحشتهاى خشكى و دريا مى‌رهاند؟ او را به زارى و در نهان مى‌خوانيد كه اگر از اين مهلكه ما را برهاند، ما نيز از سپاسگزاران خواهيم بود.'),
(853, 6, 64, 'بگو: خداست كه شما را از آن مهلكه و از هر اندوهى مى‌رهاند، باز هم بدو شرك مى‌آوريد.'),
(854, 6, 65, 'بگو: او قادر بر آن هست كه از فراز سرتان يا از زير پاهايتان عذابى بر شما بفرستد، يا شما را گروه گروه درهم افكند و خشم و كين گروهى را به گروه ديگر بچشاند. بنگر كه آيات را چگونه گوناگون بيان مى‌كنيم. باشد كه به فهم دريابند.'),
(855, 6, 66, 'قوم تو قرآن را دروغ مى‌شمردند در حالى كه سخنى است بر حق. بگو: من كارساز شما نيستم.'),
(856, 6, 67, 'به زودى خواهيد دانست كه براى هر خبرى زمانى معين است.'),
(857, 6, 68, 'و چون ببينى كه در آيات ما از روى عناد گفت و گو مى‌كنند از آنها رويگردان شو تا به سخنى جز آن پردازند. و اگر شيطان تو را به فراموشى افكند، چون به يادت آمد با آن مردم ستمكاره منشين.'),
(858, 6, 69, 'كسانى كه پرهيزگارى پيشه كرده‌اند به گناه كافران بازخواست نخواهند شد، ولى بايد آنان را پند دهند. باشد كه پرهيزگار شوند.'),
(859, 6, 70, 'و واگذار آن كسانى را كه دين خويش بازيچه و لهو گرفته‌اند و زندگانى دنيا فريبشان داد. و به قرآن پندشان ده مبادا بر كيفر اعمال خويش گرفتار آيند. جز خدا دادرس و شفيعى ندارند. و اگر براى رهايى خويش هر گونه فديه دهند پذيرفته نخواهد شد. اينان به عقوبت اعمال خود مأخوذند و به كيفر آنكه كافر شده‌اند برايشان شرابى از آب جوشان و عذابى دردآور مهيا شده است.'),
(860, 6, 71, 'بگو: آيا سواى اللّه كسى را بخوانيم كه نه ما را سود مى‌دهد و نه زيان مى‌رساند؟ و آيا پس از آنكه خدا ما را هدايت كرده است، همانند آن كس كه شيطان گمراهش ساخته و حيران بر روى زمين رهايش كرده، از دين بازگرديم؟ او را يارانى است كه به هدايت ندايش مى‌دهند كه نزد ما بازگرد. بگو: هدايتى كه از سوى خدا باشد، هدايت واقعى است. و به ما فرمان رسيده كه در برابر پروردگار جهانيان تسليم شويم.'),
(861, 6, 72, 'و نماز بخوانيد و از او بترسيد. اوست آنكه همگان نزدش محشور مى‌شويد.'),
(862, 6, 73, 'و اوست آنكه آسمانها و زمين را به حق بيافريد. و روزى كه بگويد: موجود شو، پس موجود مى‌شود. گفتار او حق است. و در آن روز كه در صور دميده شود فرمانروايى از آن اوست. داناى نهان و آشكار است و او حكيم و آگاه است.'),
(863, 6, 74, 'و ابراهيم پدرش آزر را گفت: آيا بتان را به خدايى مى‌گيرى؟ تو و قومت را به آشكارا در گمراهى مى‌بينم.'),
(864, 6, 75, 'بدين سان به ابراهيم ملكوت آسمانها و زمين را نشان داديم تا از اهل يقين گردد.'),
(865, 6, 76, 'چون شب او را فروگرفت، ستاره‌اى ديد. گفت: اين است پروردگار من. چون فرو شد، گفت: فرو شوندگان را دوست ندارم.'),
(866, 6, 77, 'آنگاه ماه را ديد كه طلوع مى‌كند. گفت: اين است پروردگار من. چون فروشد، گفت: اگر پروردگار من مرا راه ننمايد، از گمراهان خواهم بود.'),
(867, 6, 78, 'و چون خورشيد را ديد كه طلوع مى‌كند، گفت: اين است پروردگار من، اين بزرگتر است. و چون فروشد، گفت: اى قوم من، من از آنچه شريك خدايش مى‌دانيد بيزارم،'),
(868, 6, 79, 'من از روى اخلاص روى به سوى كسى آوردم كه آسمانها و زمين را آفريده است، و من از مشركان نيستم.'),
(869, 6, 80, 'و قومش با او به ستيزه برخاستند. گفت: آيا درباره اللّه با من ستيزه مى‌كنيد، و حال آنكه او مرا هدايت كرده است؟ من از آن چيزى كه شريك او مى‌انگاريد، نمى‌ترسم. مگر آنكه پروردگار من چيزى را بخواهد. علم پروردگار من همه چيز را در برگرفته است. آيا پند نمى گيريد؟'),
(870, 6, 81, 'چرا از آن چيزى كه شريك خدايش ساخته‌ايد بترسم در حالى كه شما چيزهايى را كه هيچ دليلى درباره آنها نازل نكرده است مى‌پرستيد و بيمى به دل راه نمى‌دهيد؟ اگر مى‌دانيد بگوييد كه كدام يك از اين دو گروه به ايمنى سزاوارترند؟'),
(871, 6, 82, 'كسانى كه ايمان آورده‌اند و ايمان خود را به شرك نمى‌آلايند، ايمنى از آن ايشان است و ايشان هدايت يافتگانند.'),
(872, 6, 83, 'اين برهان ما بود، كه آن را به ابراهيم تلقين كرديم در برابر قومش. هر كه را بخواهيم به درجاتى بالا مى‌بريم. هر آينه پروردگار تو حكيم و داناست.'),
(873, 6, 84, 'و به او اسحاق و يعقوب را بخشيديم و همگى را هدايت كرديم. و نوح را پيش از اين هدايت كرده بوديم و از فرزندان ابراهيم داود و سليمان و ايوب و يوسف و موسى و هارون را هدايت كرديم. و نيكوكاران را اين گونه جزا دهيم.'),
(874, 6, 85, 'و زكريا و يحيى و عيسى و الياس، كه همه از صالحان بودند.'),
(875, 6, 86, 'و اسماعيل و اليسع و يونس و لوط، كه همه را بر جهانيان برترى نهاديم.'),
(876, 6, 87, 'و از پدرانشان و فرزندانشان و برادرانشان بعضى را هدايت كرديم، و ايشان را برگزيديم و به راه راست راه نموديم.'),
(877, 6, 88, 'اين است هدايت خدا. هر كه را از بندگانش خواهد بدان هدايت مى‌كند و اگر شرك آورده بودند اعمالى كه انجام داده بودند نابود مى‌گرديد.'),
(878, 6, 89, 'اينان كسانى هستند كه به آنها كتاب و فرمان و نبوت داده‌ايم. اگر اين قوم بدان ايمان نياورند قوم ديگرى را بر آن گمارده‌ايم كه انكارش نمى‌كنند.'),
(879, 6, 90, 'اينان كسانى هستند كه خدا هدايتشان كرده است، پس به روش ايشان اقتدا كن. بگو: در برابر آن هيچ پاداشى از شما نمى‌طلبم، اين كتاب جز اندرزى براى مردم جهان نيست.'),
(880, 6, 91, 'وقتى كه مى‌گويند كه خدا بر هيچ انسانى چيزى نازل نكرده است، خدا را آنچنان كه درخور اوست نشناخته‌اند. بگو: كتابى را كه موسى براى روشنايى و هدايت مردم آورد، چه كسى بر او نازل كرده بود؟ آن را بر كاغذها نوشتيد، پاره‌اى از آن را آشكار مى‌سازيد ولى بيشترين را پنهان مى‌داريد. به شما چيزها آموختند كه از اين پيش نه شما مى‌دانستيد و نه پدرانتان مى‌دانستند. بگو: آن اللّه است. آنگاه رهايشان ساز تا همچنان به انكار خويش دلخوش باشند.'),
(881, 6, 92, 'اين است كتابى مبارك كه نازل كرده‌ايم، تصديق كننده چيزى است كه پيش از آن نازل شده است. تا با آن مردم اُمّ القُرى و مردم اطرافش را بيم دهى. كسانى كه به روز قيامت ايمان دارند به آن نيز ايمان دارند. اينان مراقب نمازهاى خويشند.'),
(882, 6, 93, 'كيست ستمكارتر از آن كس كه به خدا دروغ بست يا گفت كه به من وحى شده و حال آنكه به او هيچ چيز وحى نشده بود و آن كس كه گفت: من نيز همانند آياتى كه خدا نازل كرده است، نازل خواهم كرد؟ اگر ببينى آنگاه كه اين ستمكاران در سكرات مرگ گرفتارند و ملائكه بر آنها دست گشوده‌اند كه: جان خويش بيرون كنيد، امروز شما را به عذابى خواركننده عذاب مى‌كنند، و اين به كيفر آن است كه درباره خدا به ناحق سخن مى‌گفتيد و از آيات او سرپيچى مى‌كرديد.'),
(883, 6, 94, 'هرآينه، تنها تنها، آن سان كه در آغاز شما را بيافريديم، نزد ما آمده‌ايد در حالى كه هر چه را كه ارزانيتان داشته بوديم پشت سر نهاده‌ايد و هيچ يك از شفيعانتان را كه مى‌پنداشتيد با شما شريكند همراهتان نمى‌بينيم. از هم بريده شده‌ايد و پندار خود را گمگشته يافته‌ايد.'),
(884, 6, 95, 'خداست كه دانه و هسته را مى‌شكافد، و زنده را از مرده بيرون مى‌آورد و مرده را از زنده بيرون مى‌آورد. اين است خداى يكتا. پس، چگونه از حق منحرفتان مى‌كنند؟'),
(885, 6, 96, 'شكوفنده صبحگاهان است و شب را براى آرامش قرار داد و خورشيد و ماه را براى حساب‌كردن اوقات. اين است تقدير خداى پيروزمند دانا.'),
(886, 6, 97, 'اوست خدايى كه ستارگان را پديد آورد تا به آنها در تاريكيهاى خشكى و دريا، راه خويش را بيابيد. آيات را براى آنان كه مى‌دانند به تفصيل بيان كرده‌ايم.'),
(887, 6, 98, 'و اوست خداوندى كه شما را از يك تن بيافريد. سپس شما را قرارگاهى است و وديعت‌جايى است. آيات را براى آنان كه مى‌فهمند، به تفصيل بيان كرده‌ايم.'),
(888, 6, 99, 'اوست خدايى كه از آسمان باران فرستاد و بدان باران هرگونه نباتى را رويانيديم، و از آن نبات ساقه‌اى سبز و از آن دانه‌هايى بر يكديگر چيده و نيز از جوانه‌هاى نخل خوشه‌هايى سر فرو هشته پديد آورديم، و نيز بستانهايى از تاكها و زيتون و انار، همانند و ناهمانند. به ميوه‌هايش آنگاه كه پديد مى‌آيند و آنگاه كه مى‌رسند بنگريد كه در آنها عبرتهاست براى آنان كه ايمان مى‌آورند.'),
(889, 6, 100, 'براى خدا شريكانى از جن قرار دادند و حال آنكه جن را خدا آفريده است. و بى‌هيچ دانشى، به دروغ دخترانى و پسرانى براى او تصور كردند. او منزه است و فراتر است از آنچه وصفش مى‌كنند.'),
(890, 6, 101, 'پديد آورنده آسمانها و زمين است. چگونه او را فرزندى باشد، و حال آنكه او را همسرى نيست. هر چيزى را او آفريده است و به هر چيزى داناست.'),
(891, 6, 102, 'اين است خداى يكتا كه پروردگار شماست. خدايى جز او نيست. آفريننده هر چيزى. پس او را بپرستيد كه نگهبان هر چيزى است.'),
(892, 6, 103, 'چشمها او را نمى‌بينند و او بينندگان را مى‌بيند. دقيق و آگاه است.'),
(893, 6, 104, 'از سوى پروردگارتان براى شما نشانه‌هاى روشن آمد. هر كه از روى بصيرت مى‌نگرد به سود اوست و هر كه چشم بصيرت برهم نهد به زيان اوست. و من نگهدارنده شما نيستم.'),
(894, 6, 105, 'آيات خدا را اينچنين گونه‌گون بيان مى‌كنيم، تا مباد كه بگويند كه از كس درس گرفته‌اى و ما اين آيات را براى اهل دانش بيان مى‌كنيم.'),
(895, 6, 106, 'از هر چه از جانب پروردگارت بر تو وحى شده است پيروى كن. هيچ خدايى جز او نيست. و از مشركان روى برتاب.'),
(896, 6, 107, 'اگر خدا مى‌خواست، آنان شرك نمى‌آوردند، و ما تو را نگهبانشان نساخته‌ايم و تو كارسازشان نيستى.'),
(897, 6, 108, 'چيزهايى را كه آنان به جاى اللّه مى‌خوانند، دشنام مدهيد كه آنان نيز بى‌هيچ دانشى، از روى كينه‌توزى به اللّه دشنام دهند. اينچنين عمل هر قومى را در چشمشان آراسته‌ايم. پس بازگشت همگان به پروردگارشان است و او همه را از كارهايى كه كرده‌اند آگاه مى‌سازد.'),
(898, 6, 109, 'تا آنجا كه توانستند، به سخت‌ترين قسمها، به خدا سوگند ياد كردند كه اگر معجزه‌اى برايشان نازل شود بدان ايمان آورند. بگو: همه معجزه‌ها نزد خداست و شما از كجا مى‌دانيد كه اگر معجزه‌اى نازل شود ايمان نمى‌آورند؟'),
(899, 6, 110, 'و همچنان كه در آغاز به آن ايمان نياوردند، اين بار نيز در دلها و ديدگانشان تصرف مى‌كنيم و آنان را سرگردان در طغيانشان رها مى‌سازيم.'),
(900, 6, 111, 'و اگر ما فرشتگان را بر آنها نازل كرده بوديم و مردگان با ايشان سخن مى‌گفتند و هر چيزى را دسته‌دسته نزد آنان گرد مى‌آورديم، باز هم ايمان نمى‌آوردند، مگر اينكه خدا بخواهد. و ليك بيشترشان جاهلند.'),
(901, 6, 112, 'و همچنين براى هر پيامبرى دشمنانى از شياطين انس و جن قرار داديم. براى فريب يكديگر، سخنان آراسته القا مى‌كنند. اگر پروردگارت مى‌خواست، چنين نمى‌كردند. پس با افترايى كه مى‌زنند رهايشان ساز؛'),
(902, 6, 113, 'تا آنان كه به قيامت ايمان ندارند گوش دل را بدان سپارند و پسندشان افتد و هر چه در خورشان هست انجام دهند.'),
(903, 6, 114, 'آيا داور ديگرى جز خدا طلب كنم و حال آنكه اوست كه اين كتاب روشن را بر شما نازل كرده است؟ و اهل كتاب مى‌دانند كه به حق از جانب پروردگارت نازل شده است. پس، از شك‌آورندگان مباش.'),
(904, 6, 115, 'و كلام پروردگار تو در راستى و عدالت به حد كمال است. هيچ كس نيست كه ياراى دگرگون‌كردن سخن او را داشته باشد و اوست شنوا و دانا.'),
(905, 6, 116, 'اگر از اكثريتى كه در اين سرزمينند پيروى كنى، تو را از راه خدا گمراه سازند. زيرا جز از پى گمان نمى‌روند و جز به دروغ سخن نمى‌گويند.'),
(906, 6, 117, 'پروردگار تو به كسانى كه از راه وى دور مى‌گردند آگاه‌تر است و هدايت يافتگان را بهتر مى‌شناسد.'),
(907, 6, 118, 'اگر به آيات خدا ايمان داريد از ذبحى كه نام خدا بر آن ياد شده است بخوريد.'),
(908, 6, 119, 'چرا از آنچه نام خدا بر آن ياد شده است نمى‌خوريد و خدا چيزهايى را كه بر شما حرام شده است به تفصيل بيان كرده است، مگر آنگاه كه ناچار گرديد؟ بسيارى بى‌هيچ دانشى ديگران را گمراه پندارهاى خود كنند. هرآينه پروردگار تو به متجاوزان از حد داناتر است.'),
(909, 6, 120, 'و گناه را، چه آشكار باشد و چه پنهان، ترك گوييد. آنان كه مرتكب گناه مى‌شوند، به سزاى اعمال خود خواهند رسيد.'),
(910, 6, 121, 'از ذبحى كه نام خدا بر آن ياد نشده است مخوريد كه خود نافرمانى است. و شياطين به دوستان خود القا مى‌كنند كه با شما مجادله كنند؛ اگر از ايشان پيروى كنيد از مشركانيد.'),
(911, 6, 122, 'آيا آن كس كه مرده بود و ما زنده‌اش ساختيم و نورى فراراهش داشتيم تا بدان در ميان مردم راه خود را بيابد، همانند كسى است كه به تاريكى گرفتار است و راه بيرون‌شدن را نمى‌داند؟ اعمال كافران، در نظرشان اينچنين آراسته گرديده است.'),
(912, 6, 123, 'و بدين سان در هر قريه‌اى مجرمانشان را بزرگانشان قرار داديم تا در آنجا مكر كنند، ولى نمى‌دانند كه جز با خود مكر نمى‌ورزند.'),
(913, 6, 124, 'چون آيه‌اى بر آنها نازل شد، گفتند كه ما ايمان نمى‌آوريم تا آنگاه كه هر چه به پيامبران خدا داده شده به ما نيز داده شود. بگو: خدا داناتر است كه رسالت خود را در كجا قرار دهد. به زودى به مجرمان به كيفر مكرى كه مى‌ورزيده‌اند از جانب خدا خوارى و عذابى شديد خواهد رسيد.'),
(914, 6, 125, 'هر كس را كه خدا خواهد كه هدايت كند دلش را براى اسلام مى‌گشايد، و هر كس را كه خواهد گمراه كند قلبش را چنان فرو مى‌بندد كه گويى مى‌خواهد كه به آسمان فرا رود. بدين سان خدا به آنهايى كه ايمان نمى‌آورند پليدى مى‌نهد.'),
(915, 6, 126, 'اين است راه راست پروردگارت. آيات را براى مردمى كه پند مى‌گيرند به تفصيل بيان كرده‌ايم.'),
(916, 6, 127, 'براى آنها در نزد پروردگارشان، خانه آرامش است. و به پاداش كارهايى كه مى‌كنند، خدا دوستدار آنهاست.'),
(917, 6, 128, 'و روزى كه همگان را گرد آورد و گويد: اى گروه جنيان، شما بسيارى از آدميان را پيرو خويش ساختيد. يارانشان از ميان آدميان گويند: اى پروردگار ما، ما از يكديگر بهره‌مند مى‌شديم و به پايان زمانى كه براى زيستن ما قرار داده بودى رسيديم. گويد: جايگاه شما آتش است، جاودانه در آنجا خواهيد بود، مگر آنچه خدا بخواهد. هرآينه پروردگار تو حكيم و داناست.'),
(918, 6, 129, 'و بدين سان ستمكاران را به كيفر كارهايى كه مى‌كردند به يكديگر وامى‌گذاريم.'),
(919, 6, 130, 'اى گروه جنيان و آدميان، آيا بر شما پيامبرانى از خودتان فرستاده نشده تا آيات مرا برايتان بخوانند و شما را از ديدار چنين روزى بترسانند؟ گويند: ما به زيان خويش گواهى مى‌دهيم. زندگى دنيايى آنان را بفريفت و به زيان خود گواهى دادند، كه از كافران بودند.'),
(920, 6, 131, 'و اين بدان سبب است كه پروردگار تو مردم هيچ قريه‌اى را كه بى‌خبر بودند از روى ستم هلاك نمى‌كرد.'),
(921, 6, 132, 'براى هر يك برابر اعمالى كه انجام داده‌اند درجاتى است، كه پروردگار تو از آنچه مى‌كنند غافل نيست.'),
(922, 6, 133, 'و پروردگار تو بى‌نياز و مهربان است. و همچنان كه شما را از نسل مردمان ديگر بيافريده است، اگر بخواهد، شما را مى‌برد و پس از شما، هر چه را كه بخواهد، جانشينتان مى‌سازد.'),
(923, 6, 134, 'هر چه به شما وعده داده‌اند خواهد آمد و شما را راه گريزى نيست.'),
(924, 6, 135, 'بگو: اى قوم من، هر چه از دستتان برمى‌آيد بكنيد كه من نيز مى‌كنم؛ به زودى خواهيد دانست كه پايان اين زندگى به سود كه خواهد بود، هر آينه ستمكاران رستگار نمى‌شوند.'),
(925, 6, 136, 'براى خدا از كشته‌ها و چارپايانى كه آفريده است نصيبى معين كردند و به خيال خود گفتند كه اين از آن خداست و اين از آن بتان ماست. پس آنچه از آن بتانشان بود به خدا نمى‌رسيد، و آنچه از آن خدا بود به بتانشان مى‌رسيد. به گونه‌اى بد داورى مى‌كردند.'),
(926, 6, 137, 'همچنين، كاهنان، كشتن فرزند را در نظر بسيارى از مشركان بياراستند تا هلاكشان كنند و درباره دينشان به شك و اشتباهشان افكنند. اگر خدا مى‌خواست چنين نمى‌كردند. پس با دروغى كه مى‌بافند رهايشان ساز.'),
(927, 6, 138, 'از روى پندار گفتند: اينها چارپايان و كشتزاران ممنوع است. هيچ كس جز آنكه ما بخواهيم، نبايد از آنها بخورد. و اينها چارپايانى است كه سوارشدنشان حرام است و اينها چارپايانى است كه نام خدا را بر آنها ياد نكنند به خدا افترا مى‌بندند و به زودى به كيفر افترايى كه مى‌بسته‌اند جزايشان را خواهد داد.'),
(928, 6, 139, 'و گفتند: آنچه در شكم اين چارپايان است براى مردان ما حلال و براى زنانمان حرام است، و اگر مردار باشد زن و مرد در آن شريكند. خدا به سبب اين گفتار مجازاتشان خواهد كرد. هر آينه او حكيم و داناست.'),
(929, 6, 140, 'زيان كردند كسانى كه به سفاهت بى هيچ حجتى فرزندان خود را كشتند و به خدا دروغ بستند و آنچه را به ايشان روزى داده بود حرام كردند. اينان گمراه شده‌اند و راه هدايت را نيافته‌اند.'),
(930, 6, 141, 'و اوست كه باغهايى آفريد نيازمند به داربست و بى‌نياز از داربست، و درخت خرما و كشتزار، با طعمهاى گوناگون، و زيتون و انار، همانند، در عين حال ناهمانند. چون ثمره آوردند از آنها بخوريد و در روز درو حق آن را نيز بپردازيد و اسراف مكنيد كه خدا اسرافكاران را دوست ندارد.'),
(931, 6, 142, 'و نيز، چارپايانى كه بار مى‌برند يا در خور بار برداشتن نيستند. از آنچه خدا به شما روزى داده است بخوريد و از شيطان پيروى مكنيد كه او دشمن آشكار شماست.'),
(932, 6, 143, 'هشت لنگه: از گوسفند، نر و ماده و از بز، نر و ماده. بگو: آيا آن دو نر را حرام كرده است يا آن دو ماده را يا آنچه را در شكم مادگان است؟ اگر راست مى‌گوييد از روى علم به من خبر دهيد.'),
(933, 6, 144, 'و از شتر نر و ماده و از گاو نر و ماده، بگو: آيا آن دو نر را حرام كرده است يا آن دو ماده را يا آنچه را كه در شكم مادگان است؟ آيا آن هنگام كه خدا چنين فرمان مى‌داد شما آنجا بوديد؟ پس چه كسى ستمكارتر از آن كسى است كه به خدا دروغ مى‌بندد تا از روى بى‌خبرى مردم را گمراه كند؟ هر آينه خدا ستمكاران را هدايت نمى‌كند.'),
(934, 6, 145, 'بگو: در ميان آنچه بر من وحى شده است چيزى را كه خوردن آن حرام باشد نمى‌يابم، جز مردار يا خون ريخته يا گوشت خوك كه پليد است يا حيوانى كه در كشتنش مرتكب نافرمانى شوند و جز با گفتن نام اللّه ذبحش كنند. اگر كسى ناچار به خوردن گردد هرگاه بى‌ميلى جويد و از حد نگذراند بداند كه خدا آمرزنده و مهربان است.'),
(935, 6, 146, 'و بر يهود حرام كرديم هر حيوان صاحب ناخنى را و از گاو و گوسفند پيه آن دو را جز آنچه بر پشت آنهاست يا بر چرب‌روده آنهاست يا به استخوانشان چسبيده باشد. به سبب ستمكاريشان اينچنين كيفرشان داديم، و ما راستگويانيم.'),
(936, 6, 147, 'اگر تو را تكذيب كردند، بگو: پروردگار شما صاحب رحمتى گسترده است، و خشم و عذابش از مجرمان بازداشته نخواهد شد.'),
(937, 6, 148, 'مشركان خواهند گفت: اگر خدا مى‌خواست، ما و پدرانمان مشرك نمى‌شديم و چيزى را حرام نمى‌كرديم. همچنين كسانى كه پيش از آنها بودند پيامبران را تكذيب كردند و خشم و عذاب ما را چشيدند. بگو: اگر شما را دانشى هست آن را براى ما آشكار سازيد. ولى شما، تنها، از گمان خويش پيروى مى‌كنيد و گزافه‌گويانى بيش نيستند.'),
(938, 6, 149, 'بگو: خاص خداست دليل محكم و رسا، اگر مى‌خواست همه شما را هدايت مى‌كرد.'),
(939, 6, 150, 'بگو: گواهانتان را كه گواهى مى‌دهند كه خدا اين يا آن را حرام كرده است بياوريد. پس اگر گواهى دادند تو با آنان گواهى مده، و از خواسته‌هاى آنان كه آيات ما را تكذيب كرده‌اند و به آخرت ايمان ندارند و كسانى را با پروردگارشان برابر مى‌دارند، پيروى مكن.'),
(940, 6, 151, 'بگو: بياييد تا آنچه را كه پروردگارتان بر شما حرام كرده است برايتان بخوانم. اينكه به خدا شرك مياوريد. و به پدر و مادر نيكى كنيد. و از بيم درويشى فرزندان خود را مكشيد. ما به شما و ايشان روزى مى‌دهيم. و به كارهاى زشت چه پنهان و چه آشكارا نزديك مشويد. و كسى را كه خدا كشتنش را حرام كرده است -مگر به حق- مكشيد. اينهاست آنچه خدا شما را بدان سفارش مى‌كند، باشد كه به عقل دريابيد.'),
(941, 6, 152, 'به مال يتيم نزديك مشويد مگر به نيكوترين وجهى كه به صلاح او باشد تا به سن بلوغ رسد. و پيمانه و وزن را از روى عدل تمام كنيد. ما به كسى جز به اندازه توانش تكليف نمى‌كنيم. و هرگاه سخن گوييد عادلانه گوييد هر چند به زيان خويشاوندان باشد. و به عهد خدا وفا كنيد. اينهاست آنچه خدا شما را بدان سفارش مى‌كند، باشد كه پند گيريد.'),
(942, 6, 153, 'و اين است راه راست من. از آن پيروى كنيد و به راه‌هاى گوناگون مرويد كه شما را از راه خدا پراكنده مى‌سازد. اينهاست آنچه خدا شما را بدان سفارش مى‌كند، شايد پرهيزگار شويد.'),
(943, 6, 154, 'سپس به موسى كتاب داديم تا بر كسى كه نيكوكار بوده است نعمت را تمام كنيم و براى بيان هر چيزى و نيز براى راهنمايى و رحمت. باشد كه به ديدار پروردگارشان ايمان بياورند.'),
(944, 6, 155, 'اين كتابى است مبارك. آن را نازل كرده‌ايم. پس، از آن پيروى كنيد و پرهيزگار باشيد، باشد كه مورد رحمت قرار گيريد.'),
(945, 6, 156, 'تا نگوييد كه تنها بر دو طايفه‌اى كه پيش از ما بودند كتاب نازل شده و ما از آموختن آنها غافل بوده‌ايم.'),
(946, 6, 157, 'يا نگوييد كه اگر بر ما نيز كتاب نازل مى‌شد بهتر از آنان به راه هدايت مى‌رفتيم. بر شما نيز از جانب پروردگارتان دليل روشن و هدايت و رحمت فرا رسيد. پس چه كسى ستمكارتر از آن كس است كه آيات خدا را دروغ پنداشت و از آنها رويگردان شد؟ به زودى كسانى را كه از آيات ما رويگردان شده‌اند به سبب اين اعراضشان به عذابى بد كيفر خواهيم داد.'),
(947, 6, 158, 'آيا انتظارى جز آن دارند كه فرشتگان نزدشان بيايند؟ يا پروردگارت؟ يا نشانه‌اى از نشانه‌هاى خدا بر آنها ظاهر شود؟ روزى كه برخى نشانه‌هاى خدا آشكار شود، ايمان كسى كه پيش از آن ايمان نياورده يا به هنگام ايمان كار نيكى انجام نداده است، براى او سودى نخواهد داشت. بگو: چشم به راه باشيد، ما نيز چشم به راهيم.'),
(948, 6, 159, 'تو را با آنها كه دين خويش فرقه‌فرقه كردند و دسته‌دسته شدند، كارى نيست. كار آنها با خداست. و خدا آنان را به كارهايى كه مى‌كردند آگاه مى‌سازد.'),
(949, 6, 160, 'هر كس كار نيكى انجام دهد ده برابر به او پاداش دهند، و هر كه كار بدى انجام دهد تنها همانند آن كيفر بيند، تا ستمى بر آنها نرفته باشد.'),
(950, 6, 161, 'بگو: پروردگار من مرا به راه راست هدايت كرده است؛ به دينى همواره استوار، دين حنيف ابراهيم. و او از مشركان نبود.'),
(951, 6, 162, 'بگو: نماز من و قربانى من و زندگى من و مرگ من براى خدا آن پروردگار جهانيان است.'),
(952, 6, 163, 'او را شريكى نيست. به من چنين امر شده است، و من از نخستين مسلمانانم.'),
(953, 6, 164, 'بگو: آيا جز اللّه، پروردگارى جويم؟ او پروردگار هر چيزى است و هر كس تنها كيفر كار خويش را مى‌بيند. و كسى بار گناه ديگرى را بر دوش نمى‌كشد. سپس بازگشت همه شما به نزد پروردگارتان است و او شما را به آن چيزهايى كه در آن اختلاف مى‌ورزيديد آگاه مى‌كند.'),
(954, 6, 165, 'اوست خدايى كه شما را خليفگان زمين كرد، و بعضى را بر بعض ديگر به درجاتى برترى داد، تا شما را در چيزى كه عطايتان كرده است بيازمايد. هر آينه پروردگارت زود كيفر مى‌دهد و او آمرزنده و مهربان است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف، لام، ميم، صاد.'),
(956, 7, 2, 'كتابى است كه بر تو نازل شده، در دل تو از آن ترديدى نباشد، تا به آن بيم دهى و مؤمنان را پندى باشد.'),
(957, 7, 3, 'از آنچه از جانب پروردگارتان برايتان نازل شده است پيروى كنيد و سواى او، از خدايان ديگر متابعت مكنيد. شما چه اندك پند مى‌پذيريد.'),
(958, 7, 4, 'چه بسا قريه‌هايى كه مردمش را به هلاكت رسانيديم، و عذاب ما شب هنگام يا آنگاه كه به خواب نيمروزى فرورفته بودند، به آنان دررسيد.'),
(959, 7, 5, 'چون عذاب ما به آنان دررسيد سخنشان جز اين نبود كه گفتند: ما ستمكار بوديم.'),
(960, 7, 6, 'البته از مردمى كه برايشان پيامبرانى فرستاده شده و نيز از پيامبرانى كه فرستاده شده‌اند سؤال خواهيم كرد.'),
(961, 7, 7, 'و از هر چه كرده‌اند با آگاهى تمام برايشان حكايت خواهيم كرد، زيرا ما هرگز غايب نبوده‌ايم.'),
(962, 7, 8, 'در آن روز، وزن‌كردن به حق خواهد بود. آنها كه ترازويشان سنگين است رستگارانند.'),
(963, 7, 9, 'آنان كه ترازوى اعمالشان سبك گشته است كسانى هستند كه به آيات ما ايمان نياورده بودند و از اين رو به خود زيان رسانيده‌اند.'),
(964, 7, 10, 'در زمين جايگاهتان داديم و راه‌هاى معيشتتان را در آن قرار داديم. و چه اندك سپاس مى‌گزاريد.'),
(965, 7, 11, 'و شما را بيافريديم، و صورت بخشيديم، آنگاه به فرشتگان گفتيم: آدم را سجده كنيد. همه، جز ابليس، سجده كردند و ابليس در شمار سجده‌كنندگان نبود.'),
(966, 7, 12, 'خدا گفت: وقتى تو را به سجده فرمان دادم، چه چيز تو را از آن بازداشت؟ گفت: من از او بهترم، مرا از آتش آفريده‌اى و او را از گِل.'),
(967, 7, 13, 'گفت: از اين مقام فرو شو. تو را چه رسد كه در آن گردنكشى كنى؟ بيرون رو كه تو از خوارشدگانى.'),
(968, 7, 14, 'گفت: مرا تا روز قيامت كه زنده مى‌شوند مهلت ده.'),
(969, 7, 15, 'گفت: تو از مهلت‌يافتگانى.'),
(970, 7, 16, 'گفت: حال كه مرا نوميد ساخته‌اى، من هم ايشان را از راه راست تو منحرف مى‌كنم.'),
(971, 7, 17, 'آنگاه از پيش و از پس و از چپ و راست بر آنها مى‌تازم. و بيشترينشان را شكرگزار نخواهى يافت.'),
(972, 7, 18, 'گفت: از اينجا بيرون شو، منفور مطرود. از كسانى كه پيروى تو گزينند و از همه شما جهنم را خواهم انباشت.'),
(973, 7, 19, 'اى آدم، تو و همسرت در بهشت مكان گيريد. از هر جا كه خواهيد بخوريد ولى به اين درخت نزديك مشويد كه در شمار بر خويش ستم‌كنندگان خواهيد شد.'),
(974, 7, 20, 'پس شيطان آن دو را وسوسه كرد، تا شرمگاهشان را كه از آنها پوشيده بود در نظرشان آشكار كند. و گفت: پروردگارتان شما را از اين درخت منع كرد تا مباد از فرشتگان يا جاويدانان شويد.'),
(975, 7, 21, 'و برايشان سوگند خورد كه نيكخواه شمايم.'),
(976, 7, 22, 'و آن دو را بفريفت و به پستى افكند. چون از آن درخت خوردند شرمگاه‌هايشان آشكار شد و به پوشيدن خويش از برگهاى بهشت پرداختند. پروردگارشان ندا داد: آيا شما را از آن درخت منع نكرده بودم و نگفته بودم كه شيطان به آشكارا دشمن شماست؟'),
(977, 7, 23, 'گفتند: اى پروردگار ما، به خود ستم كرديم و اگر ما را نيامرزى و بر ما رحمت نياورى از زيان‌ديدگان خواهيم بود.'),
(978, 7, 24, 'گفت: فرو شويد، برخى دشمن برخى ديگر، و تا روز قيامت زمين قرارگاه و جاى تمتع شما خواهد بود.'),
(979, 7, 25, 'گفت: در آنجا زندگى خواهيد كرد و در آنجا خواهيد مرد و از آن بيرون آورده شويد.'),
(980, 7, 26, 'اى فرزندان آدم، براى شما جامه‌اى فرستاديم تا شرمگاهتان را بپوشد و نيز جامه زينت. و جامه پرهيزگارى از هر جامه‌اى بهتر است. و اين يكى از آيات خداست، باشد كه پند گيرند.'),
(981, 7, 27, 'اى فرزندان آدم، شيطان شما را نفريبد، همچنان كه پدر و مادرتان را از بهشت بيرون راند، لباس از تنشان كند تا شرمگاهشان را به ايشان بنماياند. او و قبيله‌اش از جايى كه آنها را نمى‌بينيد شما را مى‌بينند. ما شيطانها را دوستان كسانى قرار داديم كه ايمان نمى‌آورند.'),
(982, 7, 28, 'چون كار زشتى كنند، گويند: پدران خود را نيز چنين يافته‌ايم و خدا ما را بدان فرمان داده است. بگو: خدا به زشتكارى فرمان نمى‌دهد. چرا چيزهايى به خدا نسبت مى‌دهيد كه نمى‌دانيد؟'),
(983, 7, 29, 'بگو: پروردگار من به عدل فرمان داده است، و به هنگام هر نماز روى به جانب او داريد و او را با ايمان خالص بخوانيد. و همچنان كه شما را آفريده است باز مى‌گرديد.'),
(984, 7, 30, 'فرقه‌اى را هدايت كرده و فرقه‌اى گمراهى را در خورند. اينان شيطانها را به جاى خدا به دوستى گرفتند و مى‌پندارند كه هدايت‌يافته‌اند.'),
(985, 7, 31, 'اى فرزندان آدم، به هنگام هر عبادت لباس خود بپوشيد. و نيز بخوريد و بياشاميد ولى اسراف مكنيد، كه خدا اسرافكاران را دوست نمى‌دارد.'),
(986, 7, 32, 'بگو: چه كسى لباسهايى را كه خدا براى بندگانش پديد آورده، و خوردنيهاى خوش‌طعم را حرام كرده است؟ بگو: اين چيزها در اين دنيا براى كسانى است كه ايمان آورده‌اند و در روز قيامت نيز خاص آنها باشد. آيات خدا را براى دانايان اينچنين به تفصيل بيان مى‌كنيم.'),
(987, 7, 33, 'بگو: پروردگار من زشتكاريها را چه آشكار باشند و چه پنهان و نيز گناهان و افزونى‌جستن به ناحق را، حرام كرده است، و نيز حرام است چيزى را شريك خدا سازيد كه هيچ دليلى بر وجود آن نازل نشده است، يا در باره خدا چيزهايى بگوييد كه نمى‌دانيد.'),
(988, 7, 34, 'هر امتى را مدت عمرى است. چون اجلشان فراز آيد، يك ساعت پيش و پس نشوند.'),
(989, 7, 35, 'اى فرزندان آدم، هرگاه پيامبرانى از خود شما بيايند و آيات مرا بر شما بخوانند، كسانى كه پرهيزگارى كنند و به صلاح آيند بيمى بر آنها نيست و خود غمگين نمى‌شوند.'),
(990, 7, 36, 'و آنها كه آيات ما را به دروغ نسبت داده و از آن سر برتافته‌اند اهل جهنمند و جاودانه در آن خواهند بود.'),
(991, 7, 37, 'كيست ستمكارتر از آن كه به خدا دروغ بندد يا آيات او را دروغ انگارد؟ نصيبى كه برايشان مقرر شده به آنها خواهد رسيد. آنگاه كه فرستادگان ما بيايند تا جانشان را بگيرند، مى‌پرسند: آن چيزهايى كه به جاى خدا پرستش مى‌كرديد، اكنون كجايند؟ گويند: تباه شدند و از دست ما رفتند. و در اين حال به زيان خود شهادت دهند، كه كافر بوده‌اند.'),
(992, 7, 38, 'گويد: به ميان امتهايى كه پيش از شما بوده‌اند، از جن و انس، در آتش داخل شويد. هر امتى كه به آتش داخل شود امت همكيش خود را لعنت كند. تا چون همگى در آنجا گرد آيند، گروه‌هايى كه پيرو بوده‌اند در باره گروه‌هايى كه پيشوا بوده‌اند گويند: پروردگارا، اينان ما را گمراه كردند، دو چندان در آتش عذابشان كن. گويد: عذاب همه دوچندان است ولى شما نمى‌دانيد.'),
(993, 7, 39, 'پيشوايان به پيروان گويند: شما را بر ما هيچ برترى نيست، اينك به كيفر كارهايى كه كرده بوديد عذاب را بچشيد.'),
(994, 7, 40, 'درهاى آسمان بر روى كسانى كه آيات ما را تكذيب كرده‌اند و از آنها سر برتافته‌اند، گشوده نخواهد شد و به بهشت در نخواهند آمد تا آنگاه كه شتر از سوراخ سوزن بگذرد. و مجرمان را اينچنين كيفر مى‌دهيم.'),
(995, 7, 41, 'بسترى از آتش جهنم در زير و پوششى از آتش جهنم بر روى دارند. و ستمكاران را اينچنين كيفر مى‌دهيم.'),
(996, 7, 42, 'به هيچ كس جز اندازه توانش تكليف نمى‌كنيم. آنان كه ايمان آورده‌اند و كارهاى نيكو كرده‌اند اهل بهشتند و در آنجا جاويدانند.'),
(997, 7, 43, 'و هرگونه كينه‌اى را از دلشان برمى‌كنيم. نهرها در زير پايشان جارى است. گويند: سپاس خدايى را كه ما را بدين راه رهبرى كرده و اگر ما را رهبرى نكرده بود، راه خويش نمى‌يافتيم. رسولان پروردگار ما به حق آمدند. و آنگاه ايشان را ندا دهند كه به پاداش كارهايى كه مى‌كرديد اين بهشت را به شما داده‌اند.'),
(998, 7, 44, 'بهشتيان دوزخيان را آواز دهند كه ما به حقيقت يافتيم آنچه را كه پروردگارمان وعده داده بود، آيا شما نيز به حقيقت يافته‌ايد آنچه را كه پروردگارتان وعده داده بود؟ گويند: آرى. آنگاه آوازدهنده‌اى در آن ميان آواز دهد كه لعنت خدا بر كافران باد؛'),
(999, 7, 45, 'آنان كه از راه خدا روى برمى‌گردانند و آن را كجروى مى‌پندارند و به قيامت ايمان ندارند.'),
(1000, 7, 46, 'و ميانشان حايلى است، و بر اعراف مردانى هستند كه همه را به نشانيشان مى‌شناسند و اهل بهشت را آواز مى‌دهند كه سلام بر شما باد. اينان هر چند طمع بهشت دارند ولى هنوز بدان داخل نشده‌اند.'),
(1001, 7, 47, 'چون چشم به جانب دوزخيان گردانند گويند: اى پروردگار ما، ما را در شمار ستمكاران قرار مده.'),
(1002, 7, 48, 'ساكنان اعراف مردانى را كه از نشانيشان مى‌شناسند آواز دهند و گويند: آن خواسته كه گرد آورده بوديد و آن همه سركشى كه داشتيد شما را فايده‌اى نبخشيد.'),
(1003, 7, 49, 'آيا اينان همان كسانند كه شما سوگند خورده بوديد كه رحمت خداوند نصيبشان نمى‌شود؟ داخل در بهشت شويد، نه بيمى بر شماست و نه غمگين مى‌شويد.'),
(1004, 7, 50, 'دوزخيان بهشتيان را آواز دهند كه اندكى آب يا از چيزهايى كه خدا به شما ارزانى كرده است بر ما فرو ريزيد. گويند: خدا آنها را بر كافران حرام كرده است.'),
(1005, 7, 51, 'امروز آنان را كه دين خويش لهو و بازيچه پنداشتند و زندگى دنيا فريبشان داده بود فراموش مى‌كنيم، همچنان كه آنها نيز رسيدن به اين روز را از ياد برده بودند و آيات ما را تكذيب مى‌كردند.'),
(1006, 7, 52, 'براى هدايت مؤمنان و بخشايش به ايشان كتابى آورديم كه در آن هر چيز را از روى دانش به تفصيل بيان كرده‌ايم.'),
(1007, 7, 53, 'آيا جز نتيجه اعمال انتظارى دارند؟ روزى كه نتيجه اعمال پديدار شود، كسانى كه آن روز را از ياد برده مى‌گويند: پيامبران پروردگار ما به حق آمدند. آيا شفيعانى هستند كه ما را شفاعت كنند؟ يا آنكه كسى هست كه ما را باز گرداند تا كارهاى ديگرى جز آن كارها كه مى‌كرديم، انجام دهيم؟ به خود زيان رسانيدند و آن بتان را كه به دروغ ساخته بودند اينك از دست داده‌اند.'),
(1008, 7, 54, 'پروردگار شما اللّه است كه آسمانها و زمين را در شش روز آفريد. پس به عرش پرداخت. شب را در روز مى‌پوشاند و روز شتابان آن را مى‌طلبد. و آفتاب و ماه و ستارگان مسخّر فرمان او هستند. آگاه باشيد كه او راست آفرينش و فرمانروايى. خدا آن پروردگار جهانيان به غايت بزرگ است.'),
(1009, 7, 55, 'پروردگارتان را با تضرع و در نهان بخوانيد، زيرا او متجاوزان سركش را دوست ندارد.'),
(1010, 7, 56, 'در زمين آنگاه كه به صلاح آمده است فساد مكنيد. و خدا را از روى بيم و اميد بخوانيد. و رحمت خدا به نيكوكاران نزديك است.'),
(1011, 7, 57, 'و اوست كه پيشاپيش رحمت خود بادها را به بشارت مى‌فرستد. چون بادها ابرهاى گرانبار را بردارند، ما آن را به سرزمينهاى مرده روان سازيم و از آن باران مى‌فرستيم و به باران هر گونه ثمره‌اى را مى‌رويانيم. مردگان را نيز اينچنين زنده مى‌گردانيم شايد پند گيريد.'),
(1012, 7, 58, 'و سرزمين خوب گياه آن به فرمان پروردگارش مى‌رويد، و زمين بد جز اندك گياهى از آن پديد نمى‌آيد. براى مردمى كه سپاس مى‌گويند آيات خدا را اينچنين گونه‌گون بيان مى‌كنيم.'),
(1013, 7, 59, 'نوح را بر قومش به رسالت فرستاديم. گفت: اى قوم من، اللّه را بپرستيد، شما را خدايى جز او نيست، من از عذاب روزى بزرگ بر شما بيمناكم.'),
(1014, 7, 60, 'مهتران قومش گفتند: تو را به آشكارا در گمراهى مى‌بينيم.'),
(1015, 7, 61, 'گفت: اى قوم من، گمراهى را در من راهى نيست، من پيامبرى از جانب پروردگار جهانيانم.'),
(1016, 7, 62, 'پيامهاى پروردگارم را به شما مى‌رسانم و شما را اندرز مى‌دهم و از خدا، آن مى‌دانم كه شما نمى‌دانيد.'),
(1017, 7, 63, 'آيا از اينكه بر مردى از خودتان از جانب پروردگارتان وحى نازل شده است تا شما را بترساند و پرهيزگارى كنيد و كارى كند كه مورد رحمت قرار گيريد، به شگفت آمده‌ايد؟'),
(1018, 7, 64, 'پس تكذيبش كردند و ما او و كسانى را كه با او در كشتى بودند رهانيديم و آنان را كه آيات ما را دروغ مى‌انگاشتند غرقه ساختيم، كه مردمى بى‌بصيرت بودند.'),
(1019, 7, 65, 'و بر قوم عاد، برادرشان هود را فرستاديم. گفت: اى قوم من، اللّه را بپرستيد كه شما را جز او خدايى نيست، و چرا نمى‌پرهيزيد؟'),
(1020, 7, 66, 'مهتران قوم او كه كافر شده بودند، گفتند: مى‌بينيم كه به بيخردى گرفتار شده‌اى و پنداريم كه از دروغگويان باشى.'),
(1021, 7, 67, 'گفت: اى قوم من، در من نشانى از بيخردى نيست، من پيامبر پروردگار جهانيانم.'),
(1022, 7, 68, 'پيامهاى پروردگارم را به شما مى‌رسانم و شما را اندرزگويى امينم.'),
(1023, 7, 69, 'آيا از اينكه بر مردى از خودتان از جانب پروردگارتان وحى نازل شده است تا شما را بترساند، تعجب مى‌كنيد؟ به ياد آريد آن زمان را كه شما را جانشين قوم نوح ساخت و به جسم فزونى داد. پس نعمتهاى خدا را به ياد آوريد، باشد كه رستگار گرديد.'),
(1024, 7, 70, 'گفتند: آيا نزد ما آمده‌اى تا تنها اللّه را بپرستيم و آنچه را كه پدرانمان مى‌پرستيدند رها كنيم؟ اگر راست مى‌گويى آنچه را كه به ما وعده مى‌دهى بياور.'),
(1025, 7, 71, 'گفت: عذاب و خشم پروردگارتان حتماً بر شما نازل خواهد شد. آيا در باره اين بتهايى كه خود و پدرانتان بدين نامها ناميده‌ايد و خدا هيچ دليلى بر آنها نازل نساخته است، با من ستيزه مى‌كنيد؟ به انتظار بمانيد من هم با شما به انتظار مى‌مانم.'),
(1026, 7, 72, 'هود و همراهانش را به پايمردى رحمت خويش رهانيديم و كسانى را كه آيات ما را تكذيب كردند و ايمان نياورده بودند از ريشه بركنديم.'),
(1027, 7, 73, 'بر قوم ثمود، برادرشان صالح را فرستاديم. گفت: اى قوم من، اللّه را بپرستيد، شما را هيچ خدايى جز او نيست، از جانب خدا براى شما نشانه‌اى آشكار آمد. اين ماده‌شتر خدا، برايتان نشانه‌اى است. رهايش كنيد تا در زمين خدا بچرد و هيچ آسيبى به او نرسانيد كه عذابى دردآور شما را فرا خواهد گرفت.'),
(1028, 7, 74, 'به ياد آريد آن زمان را كه شما را جانشينان قوم عاد كرد و در اين زمين جاى داد تا بر روى خاكش قصرها برافرازيد و در كوهستانهايش خانه‌هايى بكنيد. نعمتهاى خدا را ياد كنيد و در زمين تبهكارى و فساد مكنيد.'),
(1029, 7, 75, 'مهتران قومش كه گردنكشى مى‌كردند، به زبون‌شدگان قوم كه ايمان آورده بودند گفتند: آيا مى‌دانيد كه صالح از جانب پروردگارش آمده است؟ گفتند: ما به آيينى كه بدان مأمور شده ايمان داريم.'),
(1030, 7, 76, 'گردنكشان گفتند: ما به كسى كه شما ايمان آورده‌ايد ايمان نمى‌آوريم.'),
(1031, 7, 77, 'پس ماده‌شتر را پى كردند و از فرمان پروردگارشان سرباز زدند و گفتند: اى صالح، اگر پيامبر هستى آنچه را به ما وعده مى‌دهى بياور.'),
(1032, 7, 78, 'پس زلزله‌اى سخت آنان را فروگرفت و در خانه‌هاى خود بر جاى مردند.'),
(1033, 7, 79, 'صالح از آنان روى برگردانيد و گفت: اى قوم من، رسالت پروردگارم را به شما رسانيدم و اندرزتان دادم ولى شما نيكخواهان را دوست نداريد.'),
(1034, 7, 80, 'و لوط را فرستاديم. آنگاه به قوم خود گفت: چرا كارى زشت مى‌كنيد، كه هيچ كس از مردم جهان پيش از شما نكرده است؟'),
(1035, 7, 81, 'شما به جاى زنان با مردان شهوت مى‌رانيد. شما مردمى تجاوزكار هستيد.'),
(1036, 7, 82, 'جواب قوم او جز اين نبود كه گفتند: آنها را از قريه خود برانيد كه آنان مردمى هستند كه از كار ما بيزارى مى‌جويند.'),
(1037, 7, 83, 'لوط و خاندانش را نجات داديم. جز زنش كه با ديگران در شهر ماند.'),
(1038, 7, 84, 'بر آنها بارانى باريديم، بنگر كه عاقبت مجرمان چگونه بود.'),
(1039, 7, 85, 'و بر مردم مدين برادرشان شعيب را فرستاديم. گفت: اى قوم من، اللّه را بپرستيد، شما را خدايى جز او نيست، از جانب پروردگارتان نشانه‌اى روشن آمده است. پيمانه و ترازو را تمام ادا كنيد و به مردم كم مفروشيد و از آن پس كه زمين به صلاح آمده است در آن فساد مكنيد، كه اگر ايمان آورده‌ايد، اين برايتان بهتر است.'),
(1040, 7, 86, 'و بر سر راه‌ها منشينيد تا مؤمنان به خدا را بترسانيد و از راه خدا بازداريد و به كجروى واداريد. و به ياد آريد آنگاه كه اندك بوديد، خدا بر شمار شما افزود. و بنگريد كه عاقبت مفسدان چگونه بوده است.'),
(1041, 7, 87, 'اگر گروهى از شما به آنچه من از جانب خدا بدان مبعوث شده‌ام، ايمان آورده‌اند و گروهى هنوز ايمان نياورده‌اند، صبر كنيد تا خدا ميان ما حكم كند كه او بهترين داوران است.'),
(1042, 7, 88, 'مهتران قومش كه سركشى پيشه كرده بودند گفتند: اى شعيب، تو و كسانى را كه به تو ايمان آورده‌اند از قريه خويش مى‌رانيم مگر آنكه به آيين ما برگرديد. گفت: و هر چند از آن كراهت داشته باشيم؟'),
(1043, 7, 89, 'پس از آنكه خدا ما را از كيش شما رهانيده است اگر بدان بازگرديم، بر خدا دروغ بسته باشيم، و ما ديگر بار بدان كيش بازنمى‌گرديم، مگر آنكه خدا آن پروردگار ما خواسته باشد. زيرا علم پروردگار ما بر همه چيز احاطه دارد. ما بر خدا توكل مى‌كنيم. اى پروردگار ما، ميان ما و قوم ما به حق، راهى بگشا كه تو بهترين راهگشايان هستى.'),
(1044, 7, 90, 'مهتران قومش كه كافر بودند، گفتند: اگر از شعيب پيروى كنيد سخت زيان كرده‌ايد.'),
(1045, 7, 91, 'پس زلزله‌اى سخت آنان را فرو گرفت و در خانه‌هاى خود بر جاى مُردند.'),
(1046, 7, 92, 'آنان كه شعيب را به دروغگويى نسبت دادند، گويى كه هرگز در آن ديار نبوده‌اند. آنان كه شعيب را به دروغگويى نسبت دادند خود زيان كردند.'),
(1047, 7, 93, 'پس، از آنها رويگردان شد و گفت: اى قوم من، هرآينه پيامهاى پروردگارم را به شما رسانيدم و اندرزتان دادم. چگونه بر مردمى كافر اندوهگين شوم؟'),
(1048, 7, 94, 'و ما هيچ پيامبرى را به هيچ قريه‌اى نفرستاديم مگر آنكه ساكنانش را به سختى و بيمارى گرفتار كرديم، باشد كه تضرع كنند.'),
(1049, 7, 95, 'آنگاه جاى بلا و محنت را به خوشى و خوبى سپرديم، تا شمارشان افزون شد و گفتند: آن پدران ما بودند با سختى و بهروزى. ناگهان آنان را بى‌خبر فرو گرفتيم.'),
(1050, 7, 96, 'اگر مردم قريه‌ها ايمان آورده و پرهيزگارى پيشه كرده بودند بركات آسمان و زمين را به رويشان مى‌گشوديم، ولى پيامبران را به دروغگويى نسبت دادند. ما نيز به كيفر كردارشان مؤاخذه‌شان كرديم.'),
(1051, 7, 97, 'آيا مردم قريه‌ها پنداشتند از اينكه عذاب ما شب‌هنگام كه به خواب رفته‌اند بر سر آنها بيايد، در امانند؟'),
(1052, 7, 98, 'و آيا مردم قريه‌ها پنداشتند از اينكه عذاب ما به هنگام چاشت كه به بازيچه مشغولند بر سر آنها بيايد، در امانند؟'),
(1053, 7, 99, 'آيا پنداشتند كه از مكر خدا در امانند؟ از مكر خدا جز زيانكاران ايمن ننشينند.'),
(1054, 7, 100, 'آيا براى آنان كه زمين را از پيشينيان به ارث برده‌اند، باز ننموده است كه اگر بخواهيم آنها را نيز به كيفر گناهانشان به عقوبت مى‌رسانيم و بر دلهايشان مُهر مى‌نهيم تا شنيدن نتوانند؟'),
(1055, 7, 101, 'اينها قريه‌هايى است كه ما اخبارشان را بر تو حكايت كنيم. پيامبرانشان با دلايل روشن آمدند. و به آن چيزها كه از آن پيش دروغ خوانده بودند، ايمان نياوردند. و خدا بر دلهاى كافران اينچنين مُهر مى‌نهد.'),
(1056, 7, 102, 'در بيشترينشان وفاى به عهد نيافتيم و بيشترين را جز نافرمانان نديديم.'),
(1057, 7, 103, 'بعد از آنها موسى را با آياتمان بر فرعون و قومش مبعوث داشتيم. به خلافش برخاستند. اينك بنگر كه عاقبت مفسدان چگونه بوده است.'),
(1058, 7, 104, 'موسى گفت: اى فرعون، من پيامبرى از جانب پروردگار عالميانم.'),
(1059, 7, 105, 'شايسته است كه در باره خدا جز به راستى سخن نگويم. من همراه با معجزه‌اى از جانب پروردگارتان آمده‌ام. بنى‌اسرائيل را با من بفرست.'),
(1060, 7, 106, 'گفت: اگر راست مى‌گويى و معجزه‌اى به همراه دارى، آن را بياور.'),
(1061, 7, 107, 'عصايش را انداخت، اژدهايى راستين شد.'),
(1062, 7, 108, 'و دستش را بيرون آورد، در نظر آنان كه مى‌ديدند سفيد و درخشان بود.'),
(1063, 7, 109, 'مهتران قوم فرعون گفتند: اين جادوگرى است دانا.'),
(1064, 7, 110, 'مى‌خواهد شما را از سرزمينتان بيرون كند، چه مى‌فرماييد؟'),
(1065, 7, 111, 'گفتند: او و برادرش را نگه دار و كسان به شهرها بفرست.'),
(1066, 7, 112, 'تا همه جادوگران دانا را نزد تو بياورند.'),
(1067, 7, 113, 'جادوگران نزد فرعون آمدند و گفتند: اگر غلبه يابيم، ما را پاداشى هست؟'),
(1068, 7, 114, 'گفت: آرى، و شما از مقربان خواهيد بود.'),
(1069, 7, 115, 'گفتند: اى موسى، آيا نخست تو مى‌افكنى، يا ما بيفكنيم؟'),
(1070, 7, 116, 'گفت: شما بيفكنيد. چون افكندند، ديدگان مردم را جادو كردند و آنان را ترسانيدند و جادويى عظيم آوردند.'),
(1071, 7, 117, 'و به موسى وحى كرديم كه عصاى خود را بيفكن. به ناگاه ديدند كه همه جادوهايشان را مى‌بلعد.'),
(1072, 7, 118, 'پس حق به ثبوت رسيد و كارهاى آنان باطل شد.'),
(1073, 7, 119, 'در همان جا مغلوب شدند، و خوار و زبون بازگشتند.'),
(1074, 7, 120, 'جادوگران به سجده وادار شدند.'),
(1075, 7, 121, 'گفتند: به پروردگار جهانيان ايمان آورديم؛'),
(1076, 7, 122, 'پروردگار موسى و هارون.'),
(1077, 7, 123, 'فرعون گفت: آيا پيش از آنكه من به شما رخصت دهم به او ايمان آورديد؟ اين حيله‌اى است كه درباره اين شهر انديشيده‌ايد تا مردمش را بيرون كنيد. به زودى خواهيد دانست.'),
(1078, 7, 124, 'دستها و پايهايتان را به خلاف يكديگر خواهم بريد و همگيتان را بر دار خواهم كرد.'),
(1079, 7, 125, 'گفتند: ما به نزد پروردگارمان باز مى‌گرديم.'),
(1080, 7, 126, 'خشم بر ما نمى‌گيرى، جز آنكه چون نشانه‌هاى پروردگارمان بر ما آشكار شد به آنها ايمان آورديم. اى پروردگار ما، بر ما شكيبايى ببار و ما را مسلمان بميران.'),
(1081, 7, 127, 'مهتران قوم فرعون گفتند: آيا موسى و قومش را مى‌گذارى تا در زمين فساد كنند و تو و خدايانت را ترك گويد؟ گفت: پسرانشان را خواهم كشت و زنانشان را زنده خواهم گذاشت. ما بالاتر از ايشانيم و بر آنها غلبه مى‌يابيم.'),
(1082, 7, 128, 'موسى به قومش گفت: از خدا مدد جوييد و صبر پيشه سازيد كه اين زمين از آن خداست و به هر كس از بندگانش كه بخواهد آن را به ميراث مى‌دهد. و سرانجام نيك از آن پرهيزگاران است.'),
(1083, 7, 129, 'گفتند: پيش از آنكه تو بيايى در رنج بوديم و پس از آنكه آمدى باز در رنجيم. گفت: اميد است كه پروردگارتان دشمنتان را هلاك كند و شما را در روى زمين جانشين او گرداند. آنگاه بنگرد كه چه مى‌كنيد.'),
(1084, 7, 130, 'قوم فرعون را به قحط و نقصان محصول مبتلا كرديم، شايد پند گيرند.'),
(1085, 7, 131, 'چون نيكيى نصيبشان مى‌شد مى‌گفتند: حق ماست. و چون بديى به آنها مى‌رسيد، موسى و همراهانش را بدشگون مى‌دانستند. آگاه باشيد، آن نيك و بد كه به ايشان رسد از خداست، ولى بيشترينشان نمى دانند.'),
(1086, 7, 132, 'و گفتند: هر گونه نشانه‌اى براى ما بياورى كه ما را بدان مسحور كنى به تو ايمان نخواهيم آورد.'),
(1087, 7, 133, 'ما نيز بر آنها نشانه‌هايى آشكار و گوناگون چون طوفان و ملخ و شپش و قورباغه و خون فرستاديم. باز سركشى كردند، كه مردمى مجرم بودند.'),
(1088, 7, 134, 'و چون عذاب بر آنها فرود آمد، گفتند: اى موسى، بدان عهدى كه خدا را با تو هست او را بخوان، كه اگر اين عذاب از ما دور كنى به تو ايمان مى‌آوريم و بنى‌اسرائيل را با تو مى‌فرستيم.'),
(1089, 7, 135, 'چون تا آن زمان كه قرار نهاده بودند عذاب را از آنها دور كرديم، پيمان خود را شكستند.'),
(1090, 7, 136, 'پس، از ايشان انتقام گرفتيم و در دريا غرقشان كرديم. زيرا آيات ما را دروغ مى‌انگاشتند و از آنها غفلت مى‌ورزيدند.'),
(1091, 7, 137, 'و به آن مردمى كه به ناتوانى افتاده بودند، شرق و غرب آن سرزمين را كه بركت داده بوديم به ميراث داديم، و وعده نيكويى كه پروردگار تو به بنى‌اسرائيل داده بود بدان سبب كه شكيبايى ورزيده بودند، به كمال رسيد و هر چه را فرعون و قومش مى‌ساختند و كاخهايى را كه برمى‌افراشتند ويران كرديم.'),
(1092, 7, 138, 'و بنى‌اسرائيل را از دريا گذرانيديم. و بر قومى گذشتند كه به پرستش بتهاى خود دل بسته بودند. گفتند: اى موسى، همان طور كه آنها را خدايانى است براى ما هم خدايى بساز. گفت: شما مردمى بى‌خرد هستيد.'),
(1093, 7, 139, 'آنچه اينان در آنند، نابودشونده و كارى كه مى‌كنند باطل است.'),
(1094, 7, 140, 'گفت: آيا جز الله، برايتان خدايى بجويم، و حال آنكه اوست كه شما را بر جهانيان برترى بخشيد؟'),
(1095, 7, 141, 'و شما را از آل فرعون رهانيديم. به عذابهاى سختتان مى‌آزردند، پسرانتان را مى‌كشتند و زنانتان را زنده مى‌گذاشتند و در اين از جانب پروردگارتان آزمايشى بزرگ بود.'),
(1096, 7, 142, 'سى شب با موسى وعده نهاديم و ده شب ديگر بر آن افزوديم تا وعده پروردگارش چهل شب كامل شد. و موسى به برادرش هارون گفت: بر قوم من جانشين من باش و راه صلاح پيش گير و به طريق مفسدان مرو.'),
(1097, 7, 143, 'چون موسى به ميعادگاه ما آمد و پروردگارش با او سخن گفت، گفت: اى پروردگار من، بنماى، تا در تو نظر كنم. گفت: هرگز مرا نخواهى ديد. به آن كوه بنگر، اگر بر جاى خود قرار يافت، تو نيز مرا خواهى ديد. چون پروردگارش بر كوه تجلى كرد، كوه را خرد كرد و موسى بيهوش بيفتاد. چون به هوش آمد گفت: تو منزهى، به تو بازگشتم و من نخستين مؤمنانم.'),
(1098, 7, 144, 'گفت: اى موسى، من تو را به پيامهايم و سخن گفتنم از ميان مردم برگزيدم، پس آنچه را به تو داده‌ام فرا گير و از سپاسگزاران باش.'),
(1099, 7, 145, 'براى او در آن الواح هر گونه اندرز و تفصيل هر چيز را نوشتيم. پس گفتيم: آن را به نيرومندى بگير، و قومت را بفرماى تا به بهترين آن عمل كنند، به زودى جايگاه نافرمانان را به شما نشان دهم.'),
(1100, 7, 146, 'آن كسانى را كه به ناحق در زمين سركشى مى‌كنند، زودا كه از آيات خويش رويگردان سازم، چنان كه هر آيتى را كه ببينند ايمان نياورند و اگر طريق هدايت ببينند از آن نروند و اگر طريق گمراهى ببينند از آن بروند. زيرا اينان آيات را دروغ انگاشتند و از آن غفلت ورزيدند.'),
(1101, 7, 147, 'و اعمال آنان كه آيات ما و ديدار قيامت را دروغ انگاشتند، ناچيز شد. آيا جز در مقابل كردارشان كيفر خواهند ديد؟'),
(1102, 7, 148, 'قوم موسى بعد از او از زيورهايشان تنديس گوساله‌اى ساختند كه بانگ مى‌كرد. آيا نمى‌بينند كه آن گوساله با آنها سخن نمى‌گويد و ايشان را به هيچ راهى هدايت نمى‌كند؟ آن را به خدايى گرفتند و بر خود ستم كردند.'),
(1103, 7, 149, 'و چون از آن كار پشيمان شدند و ديدند كه در گمراهى افتاده‌اند، گفتند: اگر پروردگارمان به ما رحم نكند و ما را نيامرزد، در زمره زيان‌كردگان خواهيم بود.'),
(1104, 7, 150, 'چون موسى خشمگين و اندوهناك نزد قوم خود بازگشت، گفت: در غيبت من چه بد جانشينانى بوديد. چرا بر فرمان پروردگار خود پيشى گرفتيد؟ و الواح را بر زمين افكند، و موى برادرش را گرفت و به سوى خود كشيد. هارون گفت: اى پسر مادرم، اين قوم مرا زبون يافتند و نزديك بود كه مرا بكشند، مرا دشمنكام مكن و در شمار ستمكاران مياور.'),
(1105, 7, 151, 'گفت: اى پروردگار من، مرا و برادرم را بيامرز و ما را در رحمت خويش داخل كن كه تو مهربان‌ترين مهربانانى.'),
(1106, 7, 152, 'آنان كه گوساله را برگزيدند به زودى به غضب پروردگارشان گرفتار خواهند شد و در زندگانى اينجهانى به خوارى خواهند افتاد. دروغسازان را اينچنين كيفر مى‌دهيم.'),
(1107, 7, 153, 'آنان كه مرتكب كارهاى بد شدند، آنگاه توبه كردند و ايمان آوردند، بدانند كه پروردگار تو پس از توبه، آمرزنده و مهربان است.'),
(1108, 7, 154, 'چون خشم موسى فرو نشست، الواح را برگرفت. و در نوشته‌هاى آن، براى آنهايى كه از پروردگارشان بيمناكند، هدايت و رحمت بود.'),
(1109, 7, 155, 'موسى براى وعده گاه ما از ميان قومش هفتاد مرد را برگزيد. چون زلزله آنها را فرو گرفت، گفت: اى پروردگار من، اگر مى‌خواستى ايشان را و مرا پيش از اين هلاك مى‌كردى. آيا به خاطر اعمالى كه بيخردان ما انجام داده‌اند ما را به هلاكت مى‌رسانى؟ واين جز امتحان تو نيست. هر كس را بخواهى بدان گمراه مى‌كنى و هر كس را بخواهى هدايت. تو ياور ما هستى، ما را بيامرز و بر ما ببخشاى كه تو بهترين آمرزندگانى.'),
(1110, 7, 156, 'براى ما در دنيا و آخرت نيكى بنويس. ما به سوى تو بازگشته‌ايم گفت: عذاب خود را به هر كس كه بخواهم مى‌رسانم و رحمت من همه چيز را دربرمى‌گيرد. آن را براى كسانى كه پرهيزگارى مى‌كنند و زكات مى‌دهند و به آيات ما ايمان مى‌آورند مقرر خواهم داشت.'),
(1111, 7, 157, 'آنان كه از اين رسول، اين پيامبر امى كه نامش را در تورات و انجيل خود نوشته مى‌يابند، پيروى مى‌كنند-آن كه به نيكى فرمانشان مى‌دهد و از ناشايست بازشان مى‌دارد و چيزهاى پاكيزه را بر آنها حلال مى‌كند و چيزهاى ناپاك را حرام و بار گرانشان را از دوششان برمى دارد و بند و زنجيرشان را مى‌گشايد. پس كسانى كه به او ايمان آوردند و حرمتش را نگاه داشتند و ياريش كردند و از آن كتاب كه بر او نازل كرده‌ايم پيروى كردند، رستگارانند.'),
(1112, 7, 158, 'بگو: اى مردم، من فرستاده خدا بر همه شما هستم. آن خدايى كه فرمانروايى آسمانها و زمين از آن اوست و هيچ خدايى جز او نيست. زنده مى‌كند و مى‌ميراند. پس به خدا و رسول او، آن پيامبر امى كه به خدا و كلمات او ايمان دارد، ايمان بياوريد و از او پيروى كنيد، باشد كه هدايت شويد.'),
(1113, 7, 159, 'گروهى از قوم موسى هستند كه مردم را به حق راه مى‌نمايند و به عدالت رفتار مى‌كنند.'),
(1114, 7, 160, 'بنى‌اسرائيل را به دوازده سبط تقسيم كرديم. و چون قوم موسى از او آب خواستند به او وحى كرديم كه عصايت را بر سنگ بزن. از آن سنگ دوازده چشمه روان شد. و هر گروه آبشخور خويش را بشناخت. و ابر را سايبانشان ساختيم و برايشان من و سلوى نازل كرديم. بخوريد از اين چيزهاى پاكيزه كه بر شما روزى داده‌ايم. و آنان به ما ستم نكردند بلكه به خودشان ستم مى‌كردند.'),
(1115, 7, 161, 'و به آنان گفته شد كه در اين قريه سكونت كنيد و هر جا هر چه خواهيد بخوريد و بگوييد كه گناهان ما را بريز و سجده‌كنان از دروازه داخل شويد، تا گناهانتان را بيامرزيم. به پاداش نيكوكاران خواهيم افزود.'),
(1116, 7, 162, 'از ميان آنان، آن گروه كه بر خود ستم كرده بودند سخنى را كه به آنها گفته شده بود ديگر كردند. پس به كيفر ستمى كه مى‌كردند برايشان از آسمان عذاب فرستاديم.'),
(1117, 7, 163, 'درباره آن قريه نزديك به دريا از ايشان بپرس. آنگاه كه در روز شنبه سنت مى‌شكستند. زيرا در روزى كه شنبه مى‌كردند ماهيان آشكار بر روى آب مى‌آمدند و روزى كه شنبه نمى‌كردند نمى‌آمدند. اينان را كه مردمى نافرمان بودند اينچنين مى‌آزموديم.'),
(1118, 7, 164, 'و آنگاه كه گروهى از ايشان گفتند: چرا قومى را پند مى‌دهيد كه خدا هلاكشان خواهد كرد و به عذابى دردناك مبتلا خواهد ساخت؟ گفتند: تا ما را نزد پروردگارتان عذرى باشد. و باشد كه پرهيزگار شوند.'),
(1119, 7, 165, 'چون اندرزى را كه به آنها داده شده بود فراموش كردند، آنان را كه از بدى پرهيز مى‌كردند نجات داديم و گنهكاران را به سبب گناهشان به عذابى سخت فرو گرفتيم.'),
(1120, 7, 166, 'و چون از ترك چيزى كه از آن منعشان كرده بودند سرپيچى كردند، گفتيم: بوزينگانى مطرود شويد.'),
(1121, 7, 167, 'و پروردگار تو اعلام كرد كه كسى را بر آنان بگمارد كه تا به روز قيامت به عذابى ناگوار معذبشان گرداند. هرآينه پروردگار تو زود عقوبت مى‌كند و نيز آمرزنده و مهربان است.'),
(1122, 7, 168, 'آنان را گروه گروه در زمين تقسيم كرديم، بعضى نيكوكار و بعضى جز آن. و به نيكيها و بديها آزموديم، شايد بازگردند.'),
(1123, 7, 169, 'بعد از اينان گروهى به جايشان نشستند و وارث آن كتاب شدند كه به متاع دنيوى دل بستند و گفتند كه به زودى آمرزيده مى‌شويم. و اگر همانند آن باز هم متاعى بيابند برگيرند. آيا از ايشان پيمان نگرفته‌اند كه درباره خدا جز به راستى سخن نگويند، حال آنكه آنچه در آن كتاب آمده بود خوانده بودند؟ سراى آخرت براى كسانى كه مى‌پرهيزند بهتر است. آيا تعقل نمى‌كنيد؟'),
(1124, 7, 170, 'آنان كه به كتاب خدا تمسك مى‌جويند و نماز مى‌گزارند بدانند كه پاداش نيكوكاران را تباه نمى‌سازيم.'),
(1125, 7, 171, 'و كوه را بر فراز سرشان چون سايبانى نگه داشتيم و مى‌پنداشتند كه اكنون بر سرشان خواهد افتاد. كتابى را كه به شما داده‌ايم با نيرومندى بگيريد و هر چه را كه در آن آمده است به ياد داريد، باشد كه پرهيزگار شويد.'),
(1126, 7, 172, 'و پروردگار تو از پشت بنى‌آدم فرزندانشان را بيرون آورد. و آنان را بر خودشان گواه گرفت و پرسيد: آيا من پروردگارتان نيستم؟ گفتند: آرى، گواهى مى‌دهيم. تا در روز قيامت نگوييد كه ما از آن بى‌خبر بوديم.'),
(1127, 7, 173, 'يا نگوييد كه پدران ما پيش از اين مشرك بودند و ما نسلى بوديم بعد از آنها و آيا به سبب كارى كه گمراهان كرده بودند ما را به هلاكت مى‌رسانى؟'),
(1128, 7, 174, 'آيات را اينچنين به روشنى بيان مى‌كنيم، شايد بازگردند.'),
(1129, 7, 175, 'خبر آن مرد را بر ايشان بخوان كه آيات خويش را به او عطا كرده بوديم و او از آن علم عارى گشت و شيطان در پى‌اش افتاد و در زمره گمراهان درآمد.'),
(1130, 7, 176, 'اگر خواسته بوديم به سبب آن علم كه به او داده بوديم رفعتش مى‌بخشيديم، ولى او در زمين بماند و از پى هواى خويش رفت. مثَل او چون مثَل آن سگ است كه اگر به او حمله كنى زبان از دهان بيرون آرد و اگر رهايش كنى بازهم زبان از دهان بيرون آرد. مثَل آنان كه آيات را دروغ انگاشتند نيز چنين است. قصه را بگوى، شايد به انديشه فرو روند.'),
(1131, 7, 177, 'بد است مثَل مردمى كه آيات ما را دروغ انگاشتند و به خود ستم روا مى‌داشتند.'),
(1132, 7, 178, 'هر كس را كه خداى راه نمايد، راه خويش بيابد. و آنان را كه گمراه سازد زيانكار شوند.'),
(1133, 7, 179, 'براى جهنم بسيارى از جن و انس را بيافريديم. ايشان را دلهايى است كه بدان نمى‌فهمند و چشمهايى است كه بدان نمى‌بينند و گوشهايى است كه بدان نمى‌شنوند. اينان همانند چارپايانند حتى گمراه‌تر از آنهايند. اينان خود غافلانند.'),
(1134, 7, 180, 'از آن خداوند است نيكوترين نامها. بدان نامهايش بخوانيد. آنها را كه به نامهاى خدا الحاد مى‌ورزند واگذاريد. اينان به كيفر اعمال خود خواهند رسيد.'),
(1135, 7, 181, 'از آفريدگان ما گروهى هستند كه به حق راه مى‌نمايند و به عدالت رفتار مى‌كنند.'),
(1136, 7, 182, 'و آنان را كه آيات ما را دروغ انگاشتند، از راهى كه خود نمى‌دانند به تدريج خوارشان مى‌سازيم.'),
(1137, 7, 183, 'و به آنها مهلت دهم، كه تدبير من استوار است.'),
(1138, 7, 184, 'آيا فكر نكرده‌اند كه در يارشان نشانى از ديوانگى نيست و او به آشكارا بيم‌دهنده است؟'),
(1139, 7, 185, 'آيا در ملكوت آسمانها و زمين و چيزهايى كه خدا آفريده است نمى‌انديشند؟ و شايد كه مرگشان نزديك باشد. و بعد از قرآن كدام سخن را باور دارند.'),
(1140, 7, 186, 'هر كس را كه خدا گمراه كند هيچ راهنمايى برايش نيست. و آنان را وامى‌گذارد تا همچنان در سركشى خويش سرگردان بمانند.'),
(1141, 7, 187, 'درباره قيامت از تو مى‌پرسند كه چه وقت فرا مى‌رسد. بگو: علم آن نزد پروردگار من است. تنها اوست كه چون زمانش فرا رسد آشكارش مى‌سازد. فرارسيدن آن بر آسمانيان و زمينيان پوشيده است، جز به ناگهان بر شما نيايد. چنان از تو مى‌پرسند كه گويى تو از آن آگاهى. بگو: علم آن نزد خداست ولى بيشتر مردم نمى‌دانند.'),
(1142, 7, 188, 'بگو: من مالك سود و زيان خود نيستم، مگر آنچه خدا بخواهد. و اگر علم غيب مى‌دانستم بر خير خود بسى مى‌افزودم و هيچ شرى به من نمى‌رسيد. من كسى جز بيم‌دهنده و مژده‌دهنده‌اى براى مؤمنان نيستم.'),
(1143, 7, 189, 'اوست كه همه شما را از يك تن بيافريد. و از آن يك تن زنش را نيز بيافريد تا به او آرامش يابد. چون با او درآميخت، به بارى سبك بارور شد و مدتى با آن سر كرد. و چون بار سنگين گرديد، آن دو، اللّه پروردگار خويش را بخواندند كه اگر ما را فرزندى صالح دهى از سپاسگزاران خواهيم بود.'),
(1144, 7, 190, 'چون خدا آن دو را فرزندى صالح داد، براى او در آنچه به آنها عطا كرده بود شريكانى انگاشتند، حال آنكه خدا از هر چه با او شريك مى‌سازند برتر است.'),
(1145, 7, 191, 'آيا شريك خدا مى‌سازند چيزهايى را كه نمى‌توانند هيچ چيز بيافرينند و خود مخلوق هستند؟'),
(1146, 7, 192, 'نه مى‌توانند ياريشان كنند و نه مى‌توانند به يارى خود برخيزند.'),
(1147, 7, 193, 'اگر آنها را به راه هدايت بخوانى، از شما پيروى نخواهند كرد. برايتان يكسان است چه دعوتشان كنيد و چه خاموشى پيشه سازيد.'),
(1148, 7, 194, 'آنهايى كه جز اللّه به خدايى مى‌خوانيد، بندگانى چون شمايند. اگر راست مى‌گوييد، آنها را بخوانيد، بايد شما را اجابت كنند.'),
(1149, 7, 195, 'آيا آنها را پاهايى هست كه با آن راه بروند يا آنها را دستهايى هست كه با آن حمله كنند يا چشمهايى هست كه با آن ببينند يا گوشهايى هست كه با آن بشنوند؟ بگو: شريكانتان را بخوانيد و بر ضد من تدبير كنيد و مرا مهلت مدهيد.'),
(1150, 7, 196, 'ياور من اللّه است كه اين كتاب را نازل كرده و او دوست شايستگان است.'),
(1151, 7, 197, 'آنان را كه به جاى اللّه به خدايى مى‌خوانيد، نه شما را مى‌توانند يارى كنند و نه خود را.'),
(1152, 7, 198, 'و اگر آنها را به راه هدايت بخوانى نمى‌شنوند و مى‌بينى كه به تو مى‌نگرند ولى گويى كه نمى‌بينند.'),
(1153, 7, 199, 'عفو را پيشه كن و به نيكى فرمان ده و از جاهلان اعراض كن.'),
(1154, 7, 200, 'و اگر از جانب شيطان در تو وسوسه‌اى پديد آمد به خدا پناه ببر، زيرا او شنوا و داناست.'),
(1155, 7, 201, 'كسانى كه پرهيزگارى مى‌كنند چون از شيطان وسوسه‌اى به آنها برسد، خدا را ياد مى‌كنند، و در دم بصيرت يابند.'),
(1156, 7, 202, 'و برادرانشان ايشان را به ضلالت مى‌كشند و از عمل خويش باز نمى‌ايستند.'),
(1157, 7, 203, 'چون آيه‌اى برايشان نياورى، گويند: چرا از خود چيزى نمى‌گويى؟ بگو: من پيرو چيزى هستم كه از پروردگارم به من وحى مى‌شود. و اين حجتهايى است از جانب پروردگارتان و رهنمود و رحمت است براى مردمى كه ايمان مى‌آورند.'),
(1158, 7, 204, 'چون قرآن خوانده شود به آن گوش فرا دهيد و خاموش باشيد، شايد مشمول رحمت خدا شويد.'),
(1159, 7, 205, 'پروردگارت را در دل خود به تضرع و ترس، بى‌آنكه صداى خود بلند كنى، هر صبح و شام ياد كن و از غافلان مباش.'),
(1160, 7, 206, 'هرآينه آنان كه در نزد پروردگار تو هستند از پرستش او سر نمى‌تابند و تسبيحش مى‌گويند و برايش سجده مى‌كنند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'تو را از غنايم جنگى مى‌پرسند، بگو: غنايم جنگى متعلق به خدا و پيامبر است. پس اگر از مؤمنان هستيد، از خداى بترسيد و با يكديگر به آشتى زيست كنيد و از خدا و پيامبرش فرمان بريد.'),
(1162, 8, 2, 'مؤمنان كسانى هستند كه چون نام خدا برده شود خوف بر دلهاشان چيره گردد و چون آيات خدا بر آنان خوانده شود ايمانشان افزون گردد و بر پروردگارشان توكل مى‌كنند؛'),
(1163, 8, 3, 'همان كسان كه نماز مى‌گزارند و از آنچه روزيشان داده‌ايم انفاق مى‌كنند'),
(1164, 8, 4, 'اينان مؤمنان حقيقى هستند. در نزد پروردگارشان صاحب درجاتند و مغفرت و رزقى نيكو.'),
(1165, 8, 5, 'آنچنان بود كه پروردگارت تو را از خانه‌ات به حق بيرون آورد، حال آنكه گروهى از مؤمنان ناخشنود بودند.'),
(1166, 8, 6, 'با آنكه حقيقت بر آنها آشكار شده درباره آن با تو مجادله مى‌كنند، چنان قدم برمى‌دارند كه گويى مى‌بينند كه آنها را به سوى مرگ مى‌برند.'),
(1167, 8, 7, 'و به ياد آر آنگاه را كه خدا به شما وعده داد كه يكى از آن دو گروه به دست شما افتد و دوست داشتيد كه آن گروه كه عارى از قدرت است، به دست شما افتد، حال آنكه خدا مى‌خواست با سخنان خويش حق را بر جاى خود نشاند و ريشه كافران را قطع كند.'),
(1168, 8, 8, 'تا حق را ثابت و باطل را ناچيز گرداند، هر چند گناهكاران ناخشنود باشند.'),
(1169, 8, 9, 'و آنگاه كه از پروردگارتان يارى خواستيد و خدا بپذيرفت كه من با هزار فرشته كه از پى يكديگر مى‌آيند ياريتان مى‌كنم.'),
(1170, 8, 10, 'و آن كار را خدا جز براى شادمانى شما نكرد و تا دلهايتان بدان آرام گيرد. و يارى تنها از سوى خداست كه او پيروزمند و حكيم است.'),
(1171, 8, 11, 'و به ياد آر آنگاه را كه خدا چنان ايمنيتان داده بود كه خوابى سبك شما را فروگرفت و از آسمان برايتان بارانى باريد تا شست‌وشويتان دهد و وسوسه شيطان را از شما دور كند و دلهاتان را قوى گرداند و قدمهاتان را استوار سازد.'),
(1172, 8, 12, 'و آنگاه را كه پروردگارت به فرشتگان وحى كرد: من با شمايم. شما مؤمنان را به پايدارى واداريد. من در دلهاى كافران بيم خواهم افكند. بر گردنهايشان بزنيد و انگشتانشان را قطع كنيد.'),
(1173, 8, 13, 'زيرا با خدا و پيامبرش به مخالفت برخاستند. و هر كه با خدا و پيامبرش مخالفت ورزد، بداند كه عقوبت خدا سخت است.'),
(1174, 8, 14, 'عقوبت را بچشيد، و عذاب آتش از آن كافران است.'),
(1175, 8, 15, 'اى كسانى كه ايمان آورده‌ايد، چون كافران را حمله‌ور ديديد به هزيمت پشت مكنيد؛'),
(1176, 8, 16, 'جز آنها كه براى ساز و برگ نبرد باز مى‌گردند يا آنها كه به يارى گروهى ديگر مى‌روند، هر كس كه پشت به دشمن كند مورد خشم خدا قرار مى‌گيرد و جايگاه او جهنم است، و جهنم بد جايگاهى است.'),
(1177, 8, 17, 'شما آنان را نمى‌كشتيد، خدا بود كه آنها را مى‌كشت. و آنگاه كه تير مى‌انداختى، تو تير نمى‌انداختى، خدا بود كه تير مى‌انداخت، تا به مؤمنان نعمتى كرامند ارزانى دارد. هرآينه خدا شنوا و داناست.'),
(1178, 8, 18, 'اينچنين بود. و خدا سست‌كننده حيله كافران است.'),
(1179, 8, 19, 'اگر پيروزى خواهيد پيروزيتان فراز آمد، و اگر از كفر باز ايستيد برايتان بهتر است، و اگر بازگرديد بازمى‌گرديم، و سپاه شما هر چند فراوان باشد برايتان سودى نخواهد داشت كه خدا با مؤمنان است.'),
(1180, 8, 20, 'اى كسانى كه ايمان آورده‌ايد، از خدا و پيامبرش اطاعت كنيد و در حالى كه سخن او را مى‌شنويد از او روى برمگردانيد.'),
(1181, 8, 21, 'و از آنان مباشيد كه گفتند كه شنيديم، در حالى كه نمى‌شنيدند.'),
(1182, 8, 22, 'بدترين جانوران در نزد خدا اين كران و لالان هستند كه در نمى‌يابند.'),
(1183, 8, 23, 'و اگر خدا خيرى در آنان مى‌يافت شنوايشان مى‌ساخت، و اگر هم آنان را شنوا ساخته بود باز هم برمى گشتند و رويگردان مى‌شدند.'),
(1184, 8, 24, 'اى كسانى كه ايمان آورده‌ايد، چون خدا و پيامبرش شما را به چيزى فرا خوانند كه زندگيتان مى‌بخشد دعوتشان را اجابت كنيد و بدانيد كه خدا ميان آدمى و قلبش حايل است و همه به پيشگاه او گرد آورده شويد.'),
(1185, 8, 25, 'و بترسيد از فتنه‌اى كه تنها ستمكارانتان را در بر نخواهد گرفت و بدانيد كه خدا به سختى عقوبت مى‌كند.'),
(1186, 8, 26, 'و به ياد آوريد آن هنگام را كه اندك بوديد و در شمار زبون‌شدگان اين سرزمين، بيم آن داشتيد كه مردم شما را از ميان بردارند و خدا پناهتان داد و يارى كرد و پيروز گردانيد و از چيزهاى پاكيزه روزى داد، باشد كه سپاس گوييد.'),
(1187, 8, 27, 'اى كسانى كه ايمان آورده‌ايد، مى‌دانيد كه نبايد به خدا و پيامبر خيانت كنيد و در امانت خيانت ورزيد.'),
(1188, 8, 28, 'بدانيد كه داراييها و فرزندان وسيله آزمايش شمايند و پاداش بزرگ در نزد خداست.'),
(1189, 8, 29, 'اى كسانى كه ايمان آورده‌ايد، اگر از خدا بترسيد شما را بصيرت شناخت حق از باطل دهد و گناهانتان را بزدايد و شما را بيامرزد، كه صاحب فضل و كرمى بزرگ است.'),
(1190, 8, 30, 'و آن هنگام را به يادآور كه كافران درباره تو مكرى كردند تا در بندت افكنند يا بكشندت يا از شهر بيرون سازند. آنان مكر كردند و خدا نيز مكر كرد و خدا بهترين مكركنندگان است.'),
(1191, 8, 31, 'چون آيات ما بر آنها خوانده شد، گفتند: شنيديم. و اگر بخواهيم همانند آن مى‌گوييم، اين چيزى جز افسانه‌هاى پيشينيان نيست.'),
(1192, 8, 32, 'و آن هنگام را كه گفتند: بارخدايا، اگر اين كه از جانب تو آمده حق است، بر ما از آسمان بارانى از سنگ ببار يا عذاب دردآورى بر ما بفرست.'),
(1193, 8, 33, 'تا آنگاه كه تو در ميانشان هستى خدا عذابشان نكند و تا آنگاه كه از خدا آمرزش مى‌طلبند، نيز خدا عذابشان نخواهد كرد.'),
(1194, 8, 34, 'چرا خدا عذابشان نكند، حال آنكه مردم را از مسجدالحرام باز مى‌دارند و صاحبان آن نيستند؟ صاحبان آن تنها پرهيزگارانند ولى بيشترينشان نمى‌دانند.'),
(1195, 8, 35, 'و دعايشان در نزد خانه كعبه جز صفير كشيدن و دست‌زدن هيچ نبود. پس به پاداش انكارتان عذاب را بچشيد.'),
(1196, 8, 36, 'كافران اموالشان را خرج مى‌كنند تا مردم را از راه خدا بازدارند. اموالشان را خرج خواهند كرد و حسرت خواهند برد، سپس مغلوب مى‌شوند. و كافران را در جهنم گرد مى‌آورند.'),
(1197, 8, 37, 'تا خدا ناپاك را از پاك بازنماياند و ناپاكان را برهم نهد. آنگاه همه را گرد كند و به جهنم افكند. اينان زيانكارانند.'),
(1198, 8, 38, 'به كافران بگوى كه اگر دست بردارند گناهان گذشته آنها آمرزيده شود و اگر بازگردند، دانند كه با پيشينيان چه رفتارى شده است.'),
(1199, 8, 39, 'با آنان نبرد كنيد تا ديگر فتنه‌اى نباشد و دين همه دين خدا گردد. پس اگر باز ايستادند، خدا كردارشان را مى‌بيند.'),
(1200, 8, 40, 'و اگر سر باز زدند، بدانيد كه خدا مولاى شماست، او مولا و يارى‌دهنده‌اى نيكو است.'),
(1201, 8, 41, 'و اگر به خدا و آنچه بر بنده خود در روز فرقان كه دو گروه به هم رسيدند نازل كرده‌ايم ايمان آورده‌ايد، بدانيد كه هرگاه چيزى به غنيمت گرفتيد خمس آن از آن خدا و پيامبر و خويشاوندان و يتيمان و مسكينان و در راه ماندگان است. و خدا به هر چيز تواناست.'),
(1202, 8, 42, 'شما در كرانه نزديك‌تر بيابان بوديد و آنها در كرانه دورتر بودند و آن قافله در مكانى فروتر از شما بود. اگر شما با يكديگر زمان جنگ را تعيين مى‌كرديد باز هم از آن تخلف مى‌ورزيديد تا كارى كه خدا مقرر كرده است واقع شود، تا هر كه هلاك مى‌شود به دليلى هلاك شود و هر كه زنده مى‌ماند به دليلى زنده ماند. هرآينه خدا شنوا و داناست.'),
(1203, 8, 43, 'در خواب، خدا شمارشان را به تو اندك نشان داد. اگر شمار آنها را بسيار نشان داده بود، از ترس ناتوان مى‌شديد و در تصميم به جنگ به مناقشه برمى‌خاستيد، ولى خداوند شما را از دشمنان در امان داشت، كه او به آنچه در دلهاست آگاه است.'),
(1204, 8, 44, 'و آنگاه كه چون به هم رسيديد آنان را در چشم شما اندك نمود و شما را نيز در چشم آنان اندك تا آن كار كه مقرر داشته بود واقع گردد. و همه كارها به خدا برمى‌گردد.'),
(1205, 8, 45, 'اى كسانى كه ايمان آورده‌ايد، اگر به فوجى از دشمن برخورديد پايدارى كنيد و خدا را فراوان ياد كنيد، باشد كه پيروز شويد.'),
(1206, 8, 46, 'از خدا و پيامبرش اطاعت كنيد و با يكديگر به نزاع برمخيزيد كه ناتوان شويد و مهابت و قوت شما برود. صبر پيشه گيريد كه خدا همراه صابران است.'),
(1207, 8, 47, 'همانند آن كسان مباشيد كه سرمست غرور و براى خودنمايى از ديار خويش بيرون آمدند و ديگران را از راه خدا بازداشتند. و خدا به هركارى كه مى‌كنند احاطه دارد.'),
(1208, 8, 48, 'شيطان كردارشان را در نظرشان بياراست و گفت: امروز از مردم كسى بر شما پيروز نمى‌شود و من پناه شمايم. ولى چون دو فوج روبه‌رو شدند او باز گشت و گفت: من از شما بيزارم، كه چيزهايى مى‌بينم كه شما نمى‌بينيد، من از خدا مى‌ترسم كه او به سختى عقوبت مى‌كند.'),
(1209, 8, 49, 'منافقان و آن كسان كه در دل بيماريى دارند گفتند: اينان را دينشان بفريفته است. و هر كس كه به خدا توكل كند او را پيروزمند و حكيم خواهد يافت.'),
(1210, 8, 50, 'اگر ببينى آن زمان را كه فرشتگان جان كافران مى‌ستانند، و به صورت و پشتشان مى‌زنند و مى‌گويند: عذاب سوزان را بچشيد.'),
(1211, 8, 51, 'اين به كيفر اعمالى بود كه پيش از اين كرده بوديد و خدا به بندگانش ستم روا نمى‌دارد.'),
(1212, 8, 52, 'و اين به شيوه خاندان فرعون بود و كسانى كه پيش از آنها بودند. آنها به آيات خدا كافر شدند. پس خدا آنها را به كيفر گناهانشان مؤاخذه كرد، كه خدا نيرومند و سخت‌عقوبت است.'),
(1213, 8, 53, 'زيرا خدا نعمتى را كه به قومى ارزانى داشته است، دگرگون نسازد، تا آن قوم خود دگرگون شوند. و خدا شنوا و داناست.'),
(1214, 8, 54, 'و اين به شيوه خاندان فرعون بود و كسانى كه پيش از آنها بودند. آنها آيات پروردگارشان را تكذيب كردند و ما به كيفر گناهانشان هلاكشان كرديم و خاندان فرعون را غرقه ساختيم، زيرا همه ستمكار بودند.'),
(1215, 8, 55, 'هرآينه بدترين جنبندگان در نزد خدا آنهايند كه كافر شده‌اند و ايمان نمى‌آورند:'),
(1216, 8, 56, 'كسانى كه تو از آنان پيمان گرفتى، سپس پياپى پيمان خويش را مى‌شكنند و هيچ پروا نمى‌كنند.'),
(1217, 8, 57, 'پس اگر آنها را در جنگ بيابى پراكنده‌شان ساز تا پيروانشان نيز پراكنده شوند، باشد كه عبرت گيرند.'),
(1218, 8, 58, 'اگر مى‌دانى كه گروهى در پيمان خيانت مى‌ورزند، به آنان اعلام كن كه همانند خودشان عمل خواهى كرد. زيرا خدا خائنان را دوست ندارد.'),
(1219, 8, 59, 'آنان كه كافر شده‌اند نپندارند كه جسته‌اند، زيرا خدا را ناتوان نمى‌سازند.'),
(1220, 8, 60, 'و در برابر آنها تا مى‌توانيد نيرو و اسبان سوارى آماده كنيد تا دشمنان خدا و دشمنان خود -و جز آنها كه شما نمى‌شناسيد و خدا مى‌شناسد- را بترسانيد. و آنچه را كه در راه خدا هزينه مى‌كنيد به‌تمامى به شما بازگردانده شود و به شما ستم نشود.'),
(1221, 8, 61, 'و اگر به صلح گرايند، تو نيز به صلح گراى. و بر خدا توكل كن كه اوست شنوا و دانا.'),
(1222, 8, 62, 'و اگر خواستند كه تو را بفريبند، خدا براى تو كافى است. اوست كه تو را به يارى خويش و يارى مؤمنان تأييد كرده است.'),
(1223, 8, 63, 'دلهايشان را به يكديگر مهربان ساخت. اگر تو همه آنچه را كه در روى زمين است انفاق مى‌كردى، دلهاى ايشان را به يكديگر مهربان نمى‌ساختى. ولى خدا دلهايشان را به يكديگر مهربان ساخت كه او پيروزمند و حكيم است.'),
(1224, 8, 64, 'اى پيامبر، خدا و مؤمنانى كه از تو پيروى مى‌كنند تو را بسند.'),
(1225, 8, 65, 'اى پيامبر، مؤمنان را به جنگ برانگيز. اگر از شما بيست تن باشند و در جنگ پايدارى كنند، بر دويست تن غلبه خواهند يافت. و اگر صد تن باشند، بر هزار تن از كافران پيروز مى‌شوند. زيرا آنان مردمى عارى از فهمند.'),
(1226, 8, 66, 'اكنون خدا بار از دوشتان برداشت و از ناتوانيتان آگاه شد. اگر از شما صد تن باشند و در جنگ پاى فشرند، بر دويست تن پيروز مى‌شوند. و اگر از شما هزار تن باشند، به يارى خدا بر دو هزار تن پيروز مى‌شوند. و خدا با آنهاست كه پايدارى مى‌كنند.'),
(1227, 8, 67, 'براى هيچ پيامبرى نسزد كه اسيران داشته باشد تا كه در روى زمين كشتار بسيار كند. شما متاع اينجهانى را مى‌خواهيد و خدا آخرت را مى‌خواهد. و او پيروزمند و حكيم است.'),
(1228, 8, 68, 'اگر پيش از اين از جانب خدا حكمى نشده بود، به سبب آنچه گرفته بوديد عذابى بزرگ به شما مى‌رسيد.'),
(1229, 8, 69, 'از آنچه به غنيمت گرفته‌ايد كه حلال است و پاكيزه، بخوريد. و از خدا بترسيد. هرآينه خدا آمرزنده و مهربان است.'),
(1230, 8, 70, 'اى پيامبر، به اسيرانى كه در دست شما هستند بگو: اگر خدا در دلهايتان نشان ايمان ببيند، بهتر از آنچه از شما گرفته شده ارزانيتان خواهد داشت و مى‌آمرزدتان، و خدا آمرزنده و مهربان است.'),
(1231, 8, 71, 'اگر قصد خيانت به تو را داشته باشند، پيش از اين به خدا خيانت ورزيده‌اند و خدا تو را بر آنها نصرت داده است كه دانا و حكيم است.'),
(1232, 8, 72, 'آنان كه ايمان آورده‌اند و مهاجرت كرده‌اند و با مال و جان خويش در راه خدا جهاد كرده‌اند و آنان كه به مهاجران جاى داده و ياريشان كرده‌اند، خويشاوندان يكديگرند. و آنان كه ايمان آورده‌اند و مهاجرت نكرده‌اند خويشاوندان شما نيستند تا آنگاه كه مهاجرت كنند. ولى اگر شما را به يارى طلبيدند بايد به ياريشان برخيزيد مگر آنكه بر ضد آن گروهى باشد كه ميان شما و ايشان پيمانى بسته شده باشد. و خدا به كارهايى كه مى‌كنيد بيناست.'),
(1233, 8, 73, 'كافران نيز خويشاوندان يكديگرند. اگر مراعات آن نكنيد فتنه و فسادى بزرگ در اين سرزمين پديد خواهد آمد.'),
(1234, 8, 74, 'آنان كه ايمان آورده‌اند و مهاجرت كرده‌اند و در راه خدا جهاد كرده‌اند و آنان كه جايشان داده‌اند و ياريشان كرده‌اند، به حقيقت مؤمنانند، آمرزش و روزى نيكو از آن آنهاست.'),
(1235, 8, 75, 'و كسانى كه بعداً ايمان آورده‌اند و مهاجرت كرده‌اند و همراه شما جهاد كرده‌اند، از شما هستند. به حكم كتاب خدا، خويشاوندان به يكديگر سزاوارترند. و خدا بر هر چيزى داناست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'خدا و پيامبرش بيزارند از مشركانى كه با آنها پيمان بسته‌ايد.'),
(1237, 9, 2, 'پس چهار ماه به شما مهلت داده شد كه در اين سرزمين سير كنيد، و بدانيد كه از خدا نتوانيد گريخت، و اوست كه كافران را رسوا مى‌سازد.'),
(1238, 9, 3, 'در روز حج بزرگ از جانب خدا و پيامبرش به مردم اعلام مى‌شود كه خدا و پيامبرش از مشركان بيزارند. پس اگر توبه كنيد برايتان بهتر است، ولى اگر سرپيچى كنيد بدانيد كه از خدا نتوانيد گريخت. و كافران را به عذابى دردآور بشارت ده.'),
(1239, 9, 4, 'مگر آن گروه از مشركان كه با ايشان پيمان بسته‌ايد و در پيمان خود كاستى نياورده‌اند و با هيچ كس بر ضد شما همدست نشده‌اند. با اينان به پيمان خويش تا پايان مدتش وفا كنيد، زيرا خدا پرهيزگاران را دوست دارد.'),
(1240, 9, 5, 'و چون ماه‌هاى حرام به پايان رسيد، هر جا كه مشركان را يافتيد بكشيد و بگيريد و به حبس افكنيد و در همه جا به كمينشان نشينيد. اما اگر توبه كردند و نماز خواندند و زكات دادند، از آنها دست برداريد، زيرا خدا آمرزنده و مهربان است.'),
(1241, 9, 6, 'و هرگاه يكى از مشركان به تو پناه آورد، پناهش ده تا كلام خدا را بشنود، سپس به مكان امنش برسان، زيرا اينان مردمى نادانند.'),
(1242, 9, 7, 'چگونه مشركان را با خدا و پيامبر او پيمانى باشد؟ مگر آنهايى كه نزد مسجد الحرام با ايشان پيمان بستيد. اگر بر سر پيمانشان ايستادند، بر سر پيمانتان بايستيد. خدا پرهيزگاران را دوست دارد.'),
(1243, 9, 8, 'چگونه پيمانى باشد كه اگر بر شما پيروز شوند به هيچ عهد و سوگند و خويشاوندى وفا نكنند؟ به زبان خشنودتان مى‌سازند و در دل سر مى‌پيچند و بيشترين عصيانگرانند.'),
(1244, 9, 9, 'آيات خدا را به بهاى اندك فروختند و مردم را از راه خدا باز داشتند و بد كارى كردند.'),
(1245, 9, 10, 'عهد و سوگند و خويشاوندى هيچ مؤمنى را رعايت نمى‌كنند و مردمى تجاوزكارند.'),
(1246, 9, 11, 'پس اگر توبه كردند و نماز به جاى آوردند و زكات دادند، برادران دينى شما هستند. ما آيات خدا را براى مردمى كه از دانايى برخوردارند تفصيل مى‌دهيم.'),
(1247, 9, 12, 'اگر پس از بستن پيمان، سوگند خود شكستند و در دين شما طعن زدند، با پيشوايان كفر قتال كنيد كه ايشان را رسم سوگند نگه‌داشتن نيست، باشد كه از كردار خود باز ايستند.'),
(1248, 9, 13, 'آيا با مردمى كه سوگند خود را شكستند و آهنگ اخراج پيامبر كردند و آنها بر ضد شما دشمنى آغاز كردند، نمى‌جنگيد؟ آيا از آنها مى‌ترسيد، و حال آنكه اگر ايمان آورده باشيد سزاوارتر است كه از خدا بترسيد و بس؟'),
(1249, 9, 14, 'با آنها بجنگيد. خدا به دست شما عذابشان مى‌كند و خوارشان مى‌سازد و شما را پيروزى مى‌دهد و دلهاى مؤمنان را خنك مى‌گرداند.'),
(1250, 9, 15, 'و كينه از دلهايشان بزدايد و خدا توبه هر كه را كه بخواهد مى‌پذيرد. و خداست دانا و حكيم.'),
(1251, 9, 16, 'آيا پنداشته‌ايد كه شما را به حال خود وامى‌گذارند بى‌آنكه خدا كسانى از شما را كه جهاد مى‌كنند و جز خدا و پيامبرش و مؤمنان را به همرازى نمى‌گزينند، معلوم دارد؟ و خدا به هر كارى كه مى‌كنيد آگاه است.'),
(1252, 9, 17, 'مشركان را نرسد كه در حالى كه به كفر خود اقرار مى‌كنند مسجدهاى خدا را عمارت كنند. اعمال آنها ناچيز شده و در آتش جاويدانند.'),
(1253, 9, 18, 'مسجدهاى خدا را كسانى عمارت مى‌كنند كه به خدا و روز قيامت ايمان آورده‌اند و نماز مى‌گزارند و زكات مى‌دهند و جز از خدا نمى ترسند. اميد است كه اينان از هدايت‌يافتگان باشند.'),
(1254, 9, 19, 'آيا آب‌دادن به حاجيان و عمارت مسجد الحرام را با كرده كسى كه به خدا و روز قيامت ايمان آورده و در راه خدا جهاد كرده، برابر مى‌دانيد؟ نه، نزد خدا برابر نيستند و خدا ستمكاران را هدايت نمى‌كند.'),
(1255, 9, 20, 'آنان كه ايمان آوردند و مهاجرت كردند و در راه خدا به مال و جان خويش جهاد كردند، در نزد خدا درجتى عظيم‌تر دارند و كاميافتگانند.'),
(1256, 9, 21, 'پروردگارشان به رحمت و خشنودى خود و به بهشتى كه در آن نعمتهاى جاويد باشد، بشارتشان مى‌دهد.'),
(1257, 9, 22, 'در آن بهشت جاويدان بمانند، زيرا اجر و مزد بزرگ در نزد خداوند است.'),
(1258, 9, 23, 'اى كسانى كه ايمان آورده‌ايد، اگر پدران و برادرانتان دوست دارند كه كفر را به جاى ايمان برگزينند، آنها را به دوستى مگيريد و هر كس از شما دوستشان بدارد از ستمكاران خواهد بود.'),
(1259, 9, 24, 'بگو: اگر پدرانتان و فرزندانتان و برادرانتان و زنانتان و خويشاوندانتان و اموالى كه اندوخته‌ايد و تجارتى كه از كساد آن بيم داريد، و خانه‌هايى كه بدان دلخوش هستيد براى شما از خدا و پيامبرش و جهادكردن در راه او دوست داشتنى‌تر است، منتظر باشيد تا خدا فرمان خويش بياورد. و خدا نافرمانان را هدايت نخواهد كرد.'),
(1260, 9, 25, 'خدا شما را در بسيارى از جايها يارى كرد. و نيز در روز حنين، آنگاه كه انبوهى لشكرتان شما را به شگفت آورده بود ولى براى شما سودى نداشت و زمين با همه فراخيش بر شما تنگ شد و بازگشتيد و به دشمن پشت كرديد.'),
(1261, 9, 26, 'آنگاه خدا آرامش خويش را بر پيامبرش و بر مؤمنان نازل كرد و لشكريانى كه آنها را نمى‌ديديد فرو فرستاد و كافران را عذاب كرد، و اين است كيفر كافران.'),
(1262, 9, 27, 'از آن پس خدا توبه هر كس را كه بخواهد مى‌پذيرد، كه آمرزنده و مهربان است.'),
(1263, 9, 28, 'اى كسانى كه ايمان آورده‌ايد، مشركان نجسند و از سال بعد نبايد به مسجد الحرام نزديك شوند. و اگر از بينوايى مى‌ترسيد، خدا اگر بخواهد به فضل خويش بى‌نيازتان خواهد كرد. زيرا خدا دانا و حكيم است.'),
(1264, 9, 29, 'با كسانى از اهل كتاب كه به خدا و روز قيامت ايمان نمى‌آورند و چيزهايى را كه خدا و پيامبرش حرام كرده است بر خود حرام نمى‌كنند و دين حق را نمى‌پذيرند جنگ كنيد، تا آنگاه كه به دست خود در عين مذلت جزيه بدهند.'),
(1265, 9, 30, 'يهود گفتند كه عزير پسر خداست، و نصارى گفتند كه عيسى پسر خداست. اين سخن كه مى‌گويند همانند گفتار كسانى است كه پيش از اين كافر بودند. خدا بكشدشان. چگونه از حق منحرفشان مى‌كنند؟'),
(1266, 9, 31, 'حبرها و راهبان خويش و مسيح پسر مريم را به جاى اللّه به خدايى گرفتند و حال آنكه مأمور بودند كه تنها يك خدا را بپرستند، كه هيچ خدايى جز او نيست. منزه است از آنچه شريكش مى‌سازند.'),
(1267, 9, 32, 'مى‌خواهند نور خدا را با دهان خود خاموش كنند، و خدا جز به كمال‌رساندن نور خود نمى‌خواهد. هر چند كافران را خوش نيايد.'),
(1268, 9, 33, 'او كسى است كه پيامبر خود را براى هدايت مردم فرستاد، با دينى درست و بر حق، تا او را بر همه دينها پيروز گرداند، هر چند مشركان را خوش نيايد.'),
(1269, 9, 34, 'اى كسانى كه ايمان آورده‌ايد، بسيارى از حبرها و راهبان اموال مردم را به ناشايست مى‌خورند و ديگران را از راه خدا باز مى‌دارند. و كسانى را كه زر و سيم مى‌اندوزند و در راه خدا انفاقش نمى‌كنند، به عذابى دردآور بشارت ده؛'),
(1270, 9, 35, 'روزى كه در آتش جهنم گداخته شود و پيشانى و پهلو و پشتشان را با آن داغ كنند. اين است آن چيزى كه براى خود اندوخته بوديد. حال طعم اندوخته خويش را بچشيد.'),
(1271, 9, 36, 'شمار ماه‌ها در نزد خدا، در كتاب خدا از آن روز كه آسمانها و زمين را بيافريده، دوازده است. چهار ماه، ماه‌هاى حرامند. اين است شيوه درست. در آن ماه‌ها بر خويشتن ستم مكنيد. و همچنان كه مشركان همگى به جنگ شما برخاستند، همگى به جنگ ايشان برخيزيد. و بدانيد كه خدا با پرهيزگاران است.'),
(1272, 9, 37, 'به تأخيرافكندن ماه‌هاى حرام، افزونى در كفر است و موجب گمراهى كافران. آنان يك سال آن ماه را حلال مى‌شمردند و يك سال حرام، تا با آن شمار كه خدا حرام كرده است توافق يابند. پس آنچه را كه خدا حرام كرده حلال مى‌شمارند. كردار ناپسندشان در نظرشان آراسته گرديده و خدا كافران را هدايت نمى‌كند.'),
(1273, 9, 38, 'اى كسانى كه ايمان آورده‌ايد، چيست كه چون به شما گويند كه براى جنگ در راه خدا بسيج شويد، گويى به زمين مى‌چسبيد؟ آيا به جاى زندگى اخروى به زندگى دنيا راضى شده‌ايد؟ متاع اين دنيا در برابر متاع آخرت جز اندكى هيچ نيست.'),
(1274, 9, 39, 'اگر به جنگ بسيج نشويد خدا شما را به شكنجه‌اى دردناك عذاب مى‌كند و قوم ديگرى را به جاى شما برمى گزيند و به خدا نيز زيانى نمى رسانيد كه خدا بر هر كارى تواناست.'),
(1275, 9, 40, 'اگر شما ياريش نكنيد، آنگاه كه كافران بيرونش كردند، خدا ياريش كرد. يكى از آن دو به هنگامى كه در غار بودند به رفيقش مى‌گفت: اندوهگين مباش، خدا با ماست. خدا به دلش آرامش بخشيد و با لشكرهايى كه شما آن را نمى‌ديديد تأييدش كرد. و كلام كافران را پست گردانيد، زيرا كلام خدا بالاست و خدا پيروزمند و حكيم است.'),
(1276, 9, 41, 'به جنگ برويد خواه بر شما آسان باشد خواه دشوار، و با مال و جان خويش در راه خدا جهاد كنيد. اگر بدانيد خير شما در اين است.'),
(1277, 9, 42, 'اگر متاعى دست‌يافتنى بود يا سفرى بود نه چندان دراز، از پى تو مى‌آمدند، ولى اكنون كه راه دراز شده است پيمودنش را دشوار مى‌انگارند. و به خدا قسم مى‌خورند كه اگر مى‌توانستيم همراه شما بيرون مى‌آمديم. اينان تن به هلاكت مى‌سپارند. خدا مى‌داند كه دروغ مى‌گويند.'),
(1278, 9, 43, 'خدايت عفو كند. چرا به آنان اذن ماندن دادى؟ مى‌بايست آنها كه راست مى‌گفتند آشكار شوند و تو دروغگويان را هم بشناسى.'),
(1279, 9, 44, 'آنان كه به خدا و روز قيامت ايمان دارند هرگز براى جهادكردن با مال و جانشان از تو رخصت نمى‌خواهند و خدا پرهيزگاران را مى‌شناسد.'),
(1280, 9, 45, 'كسانى كه به خدا و روز قيامت ايمان ندارند و دلهايشان را شك فرا گرفته است و اين شك آنها را به ترديد افكنده است، از تو رخصت مى‌خواهند.'),
(1281, 9, 46, 'اگر مى‌خواستند كه به جنگ بيرون شوند براى خويش ساز و برگى آماده كرده بودند، ولى خدا بسيج آنان را ناخوش داشت و از جنگشان باز داشت و به ايشان گفته شد كه همراه كسانى كه بايد در خانه بنشينند در خانه بنشينيد.'),
(1282, 9, 47, 'اگر با شما به جنگ بيرون آمده بودند، چيزى جز اضطراب به شما نمى‌افزودند، و تا فتنه‌انگيزى كنند و براى دشمنان به جاسوسى پردازند در ميان شما رخنه مى‌كردند، و خدا به ستمكاران آگاه است.'),
(1283, 9, 48, 'پيش از اين هم فتنه‌گرى مى‌كردند و كارها را براى تو واژگونه جلوه مى‌دادند تا آنگاه كه حق فراز آمد و فرمان خداوند آشكار شد، در حالى كه از آن كراهت داشتند.'),
(1284, 9, 49, 'بعضى از آنان مى‌گويند: مرا رخصت ده و به گناه مينداز. آگاه باش كه اينان خود در گناه افتاده‌اند و جهنم بر كافران احاطه دارد.'),
(1285, 9, 50, 'اگر خيرى به تو رسد اندوهگينشان سازد، و اگر به تو مصيبتى رسد مى‌گويند: ما چاره كار خويش پيش از اين انديشيده‌ايم. آنگاه شادمان بازمى‌گردند.'),
(1286, 9, 51, 'بگو: مصيبتى جز آنچه خدا براى ما مقرر كرده است به ما نخواهد رسيد. او مولاى ماست و مؤمنان بر خدا توكل كنند.'),
(1287, 9, 52, 'بگو: آيا جز يكى از آن دو نيكى، انتظار چيز ديگرى را براى ما داريد؟ ولى ما منتظريم كه عذاب خدا، يا از جانب او يا به دست ما به شما برسد. پس شما منتظر باشيد ما نيز با شما منتظر مى‌مانيم.'),
(1288, 9, 53, 'بگو: خدا از شما نمى‌پذيرد، چه از روى رضا انفاق كنيد چه از روى كراهت، زيرا مردمى نافرمان هستيد.'),
(1289, 9, 54, 'هيچ چيز مانع قبول انفاقهايشان نشده مگر آنكه به خدا و پيامبرش ايمان نياورده‌اند و جز با بى‌ميلى به نماز حاضر نمى‌شوند و جز به كراهت انفاق نمى‌كنند.'),
(1290, 9, 55, 'دارايى و فرزندانشان تو را به اعجاب نيفكند. جز اين نيست كه خدا مى‌خواهد به آنها در اين دنيا عذابشان كند و در حالى كه هنوز كافرند جانشان برآيد.'),
(1291, 9, 56, 'به خدا سوگند مى‌خورند كه از شمايند و حال آنكه از شما نيستند، اينان از شما بيمناكند.'),
(1292, 9, 57, 'اگر پناهگاه يا غار يا نهانگاهى مى‌يافتند، شتابان بدان جا روى مى‌آوردند.'),
(1293, 9, 58, 'بعضى از ايشان تو را در تقسيم صدقات به بيداد متهم مى‌كنند، اگر به آنها عطا كنند خشنود مى‌شوند و اگر عطا نكنند خشم مى‌گيرند.'),
(1294, 9, 59, 'چه مى‌شود اگر به آنچه خدا و پيامبرش به آنان عطا مى‌كند خشنود باشند و بگويند: خدا ما را بس است و خدا و پيامبرش ما را از فضل خويش بى‌نصيب نخواهند گذاشت و ما به خدا رغبت مى‌ورزيم؟'),
(1295, 9, 60, 'صدقات براى فقيران است و مسكينان و كارگزاران جمع‌آورى آن. و نيز براى به دست آوردن دل مخالفان و آزاد كردن بندگان و قرضداران و انفاق در راه خدا و مسافران نيازمند و آن فريضه‌اى است از جانب خدا. و خدا دانا و حكيم است.'),
(1296, 9, 61, 'بعضى از ايشان پيامبر را مى‌آزارند و مى‌گويند كه او به سخن هر كس گوش مى‌دهد. بگو: او براى شما شنونده سخن خير است. به خدا ايمان دارد و مؤمنان را باور دارد، و رحمتى است براى آنهايى كه ايمان آورده‌اند. و آنان كه رسول خدا را بيازارند به شكنجه‌اى دردآور گرفتار خواهند شد.'),
(1297, 9, 62, 'در برابر شما به خدا سوگند مى‌خورند تا خشنودتان سازند و حال آنكه اگر ايمان آورده‌اند شايسته‌تر است كه خدا و رسولش را خشنود سازند.'),
(1298, 9, 63, 'آيا نمى‌دانند كه هر كس كه با خدا و پيامبرش مخالفت مى‌ورزد جاودانه در آتش جهنم خواهد بود و اين رسوايى بزرگى است؟'),
(1299, 9, 64, 'منافقان مى‌ترسند كه مباد از آسمان در باره آنها سوره‌اى نازل شود و از آنچه در دل نهفته‌اند با خبرشان سازد. بگو: مسخره كنيد، كه خدا آنچه را كه از آن مى‌ترسيد آشكار خواهد ساخت.'),
(1300, 9, 65, 'اگر از آنها بپرسى كه چه مى‌كرديد؟ مى‌گويند: ما با هم حرف مى‌زديم و بازى مى‌كرديم. بگو: آيا خدا و آيات او و پيامبرش را مسخره مى‌كرديد؟'),
(1301, 9, 66, 'عذر مياوريد. پس از ايمان، كافر شده‌ايد. اگر از تقصير گروهى از شما بگذريم گروه ديگر را كه مجرم بوده‌اند عذاب خواهيم كرد.'),
(1302, 9, 67, 'مردان منافق و زنان منافق، همه همانند يكديگرند. به كارهاى زشت فرمان مى‌دهند و از كارهاى نيك جلو مى‌گيرند و مشت خود را از انفاق در راه خدا مى‌بندند. خدا را فراموش كرده‌اند. خدا نيز ايشان را فراموش كرده است، زيرا منافقان نافرمانانند.'),
(1303, 9, 68, 'خدا به مردان منافق و زنان منافق و كافران وعده آتش جهنم داده است. در آن جاودانه‌اند. همين برايشان بس است. لعنت خدا بر آنها باد و به عذابى پايدار گرفتار خواهند شد.'),
(1304, 9, 69, 'همانند كسانى كه پيش از شما بودند، با نيروى بيشتر و دارايى و فرزندان بيشتر. ايشان از نصيب خويش بهره‌مند شدند. شما نيز از نصيب خويش بهره‌مند شده‌ايد، همچنان كه كسانى كه پيش از شما بوده‌اند، از نصيب خويش بهره‌مند شده بودند. شما سخنان ناپسند گفتيد، همچنان كه ايشان سخن ناپسند مى‌گفتند. اعمال آنان در دنيا و آخرت ناچيز گرديد و زيانمند شدند.'),
(1305, 9, 70, 'آيا خبر كسانى كه پيش از آنها بوده‌اند چون قوم نوح و عاد و ثمود و قوم ابراهيم و اصحاب مدين و مؤتفكه را نشنيده‌اند كه پيامبرانشان با نشانه‌هاى آشكار بر آنها مبعوث شدند؟ خدا به آنها ستم نمى‌كرد، آنان خود بر خود ستم مى‌كردند.'),
(1306, 9, 71, 'مردان مؤمن و زنان مؤمن دوستان يكديگرند. به نيكى فرمان مى‌دهند و از ناشايست باز مى‌دارند، و نماز مى‌گزارند و زكات مى‌دهند و از خدا و پيامبرش فرمانبردارى مى‌كنند. خدا اينان را رحمت خواهد كرد، خدا پيروزمند و حكيم است.'),
(1307, 9, 72, 'خدا به مردان مؤمن و زنان مؤمن بهشتهايى را وعده داده است كه جويها در آن جارى است، و بهشتيان همواره در آنجايند و نيز خانه‌هايى نيكو در بهشت جاويد. ولى خشنودى خدا از همه برتر است كه پيروزى بزرگ، خشنودى خداوند است.'),
(1308, 9, 73, 'اى پيامبر، با كافران و منافقان بجنگ و با آنان به شدت رفتار كن، جايگاهشان جهنم است كه بد سرانجامى است.'),
(1309, 9, 74, 'به خدا سوگند مى‌خورند كه نگفته‌اند، ولى كلمه كفر را بر زبان رانده‌اند. و پس از آنكه اسلام آورده بودند كافر شده‌اند. و قصد كارى كردند، اما بدان نايل نشدند. عيبجوييشان از آن روست كه خدا و پيامبرش از غنايم بى‌نيازشان كردند. پس اگر توبه كنند خيرشان در آن است، و اگر رويگردان شوند خدا به عذاب دردناكى در دنيا و آخرت معذبشان خواهد كرد و آنها را در روى زمين نه دوستدارى خواهد بود و نه مددكارى.'),
(1310, 9, 75, 'بعضى از آنها با خدا پيمان بستند كه اگر از فضل خود مالى نصيبمان كند، زكات مى‌دهيم و در زمره صالحان درمى‌آييم.'),
(1311, 9, 76, 'چون خدا از فضل خود مالى نصيبشان كرد، بخل ورزيدند و به اعراض بازگشتند.'),
(1312, 9, 77, 'و از آن پس تا روزى كه همراه با آن خلف وعده با خدا و آن دروغها كه مى‌گفتند با او ملاقات كنند، دلهايشان را جاى نفاق ساخت.'),
(1313, 9, 78, 'آيا نمى‌دانند كه خدا راز دل ايشان را مى‌داند و از پچ‌پچ كردنشان آگاه است؟ زيرا اوست كه داناى نهانهاست.'),
(1314, 9, 79, 'خداوند به استهزا مى‌گيرد كسانى را كه بر مؤمنانى كه به رغبت صدقه مى‌دهند و بيش از استطاعت خويش چيزى نمى‌يابند، عيب مى‌گيرند و مسخره‌شان مى‌كنند. و ايشان را عذابى دردآور است.'),
(1315, 9, 80, 'مى‌خواهى برايشان آمرزش بخواه مى‌خواهى آمرزش نخواه، اگر هفتاد بار هم برايشان آمرزش بخواهى خدايشان نخواهد آمرزيد، زيرا به خدا و پيامبرش ايمان ندارند و خدا مردم نافرمان را هدايت نمى‌كند.'),
(1316, 9, 81, 'آنان كه در خانه نشسته‌اند و از همراهى با رسول خدا تخلف ورزيده‌اند خوشحالند. جهاد با مال و جان خويش را، در راه خدا، ناخوش شمردند و گفتند: در هواى گرم به جنگ نرويد. اگر مى‌فهمند بگو: گرماى آتش جهنم بيشتر است.'),
(1317, 9, 82, 'به سزاى اعمالى كه انجام داده‌اند بايد كه اندك بخندند و فراوان بگريند.'),
(1318, 9, 83, 'اگر خدا تو را از جنگ بازگردانيد و با گروهى از ايشان ديدار كردى و از تو خواستند كه براى جنگ ديگر بيرون آيند، بگو: شما هرگز با من به جنگ بيرون نخواهيد شد و همراه من با هيچ دشمنى نبرد نخواهيد كرد، زيرا شما از نخست به نشستن در خانه خشنود بوده‌ايد. پس اكنون هم با آنان كه از فرمان تخلف كرده‌اند در خانه بمانيد.'),
(1319, 9, 84, 'چون بميرند، بر هيچ يك از آنان نماز مكن و بر قبرشان مايست. اينان به خدا و رسولش كافر شده‌اند و نافرمان مُرده‌اند.'),
(1320, 9, 85, 'اموال و اولادشان به اعجابت نيفكند، خدا مى‌خواهد به سبب آنها در دنيا عذابشان كند و در عين كفر جان بسپارند.'),
(1321, 9, 86, 'چون سوره‌اى نازل شد كه به خدا ايمان بياوريد و با پيامبرش به جنگ برويد، توانگرانشان از تو رخصت خواستند و گفتند: ما را بگذار، تا با آنهايى كه بايد در خانه نشينند در خانه بنشينيم.'),
(1322, 9, 87, 'بدان راضى شده‌اند كه قرين خانه‌نشينان باشند، بر دلهايشان مُهر نهاده شده و نمى‌فهمند.'),
(1323, 9, 88, 'ولى پيامبر و كسانى كه با او ايمان آورده‌اند با مال و جان خود در راه خدا جهاد كردند. نيكيها از آن آنهاست و آنهايند كه رستگارانند.'),
(1324, 9, 89, 'خدا برايشان بهشتهايى كه جويها در آن روان است و در آنجا جاويدانند، آماده كرده است. اين است رستگارى بزرگ.'),
(1325, 9, 90, 'گروهى از عربهاى باديه‌نشين آمدند و عذر آوردند تا آنها را رخصت دهند كه به جنگ نروند و آنهايى كه به خدا و پيامبرش دروغ گفته بودند، در خانه نشستند. زودا كه به كافرانشان عذابى دردآور خواهد رسيد.'),
(1326, 9, 91, 'بر ناتوانان و بيماران و آنان كه هزينه خويش نمى‌يابند، هرگاه در عمل براى خدا و پيامبرش اخلاص ورزند گناهى نيست اگر به جنگ نيايند؛ كه بر نيكوكارن هيچ گونه عتابى نيست و خدا آمرزنده و مهربان است.'),
(1327, 9, 92, 'و نيز بر آنان كه نزد تو آمدند تا ساز و برگ نبردشان دهى و تو گفتى كه ساز و برگى ندارم و آنها براى خرج كردن هيچ نيافتند و اشك‌ريزان و محزون بازگشتند، گناهى نيست.'),
(1328, 9, 93, 'خشم و عذاب خدا بر كسانى است كه در عين توانگرى از تو رخصت مى‌خواهند و بدان خشنودند كه با خانه‌نشينان در خانه بمانند. خدا دلهايشان را مُهر برنهاده است كه نمى‌دانند.'),
(1329, 9, 94, 'چون شما به نزدشان بازگرديد، مى‌آيند و عذر مى‌آورند. بگو: عذر مياوريد؛ گفتارتان را باور نداريم كه خدا ما را از اخبار شما آگاه كرده است. زودا كه خدا و پيامبرش به اعمال شما خواهند رسيد. آنگاه شما را نزد آن خداوندى كه داناى نهان و آشكار است مى‌برند تا از نتيجه اعمالتان آگاهتان سازد.'),
(1330, 9, 95, 'چون به نزدشان باز گرديد، برايتان سوگند مى‌خورند تا از خطايشان درگذريد. از ايشان اعراض كنيد كه مردمى پليدند و به خاطر اعمالشان جاى در جهنم دارند.'),
(1331, 9, 96, 'برايتان سوگند مى‌خورند تا از آنان خشنود شويد. اگر شما هم خشنود شويد، خدا از اين مردم نافرمان خشنود نخواهد شد.'),
(1332, 9, 97, 'عربهاى باديه‌نشين كافرتر و منافق‌تر از ديگرانند و به بى‌خبرى از احكامى كه خدا بر پيامبرش نازل كرده است سزاوارترند. و خدا دانا و حكيم است.'),
(1333, 9, 98, 'پاره‌اى از عربهاى باديه‌نشين آنچه را كه خرج مى‌كنند چون غرامتى مى‌پندارند و منتظرند تا به شما حوادثى برسد. حوادث بد بر خودشان باد و خدا شنوا و داناست.'),
(1334, 9, 99, 'و پاره‌اى از عربهاى باديه‌نشين به خدا و روز قيامت ايمان دارند و آنچه را كه خرج مى‌كنند براى نزديك شدن به خدا و به خاطر دعاى پيامبر خرج مى‌كنند. بدانيد كه همين سبب نزديكيشان به خدا خواهد شد و خدا به رحمت خويش داخلشان خواهد كرد، زيرا آمرزنده و مهربان است.'),
(1335, 9, 100, 'از آن گروه نخستين از مهاجرين و انصار كه پيشقدم شدند و آنان كه به نيكى از پى‌شان رفتند، خدا خشنود است و ايشان نيز از خدا خشنودند. برايشان بهشتهايى كه در آنها نهرها جارى است و هميشه در آنجا خواهند بود، آماده كرده است. اين است كاميابى بزرگ.'),
(1336, 9, 101, 'گروهى از عربهاى باديه‌نشين كه گرد شما را گرفته‌اند منافقند و گروهى از مردم مدينه نيز در نفاق اصرار مى‌ورزند. تو آنها را نمى‌شناسى، ما مى‌شناسيمشان و دو بار عذابشان خواهيم كرد و به عذاب بزرگ گرفتار مى‌شوند.'),
(1337, 9, 102, 'و گروهى ديگر به گناه خود اعتراف كردند كه اعمال نيكو را با كارهاى زشت آميخته‌اند. شايد خدا توبه‌شان را بپذيرد، زيرا خدا آمرزنده و مهربان است.'),
(1338, 9, 103, 'از داراييهايشان صدقه بستان تا آنان را پاك و منزه سازى و برايشان دعا كن، زيرا دعاى تو مايه آرامش آنهاست، و خدا شنوا و داناست.'),
(1339, 9, 104, 'آيا هنوز ندانسته‌اند كه خداست كه توبه بندگانش را مى‌پذيرد و صدقات را مى‌ستاند، و خداست كه توبه‌پذير و مهربان است؟'),
(1340, 9, 105, 'بگو: عمل كنيد، خدا و پيامبرش و مؤمنان اعمال شما را خواهند ديد و شما به نزد داناى نهان و آشكارا بازگردانده مى‌شويد و او از اعمالتان آگاهتان خواهد كرد.'),
(1341, 9, 106, 'و گروهى ديگر به مشيت خداوند واگذاشته شده‌اند، كه يا عذابشان مى‌كند يا توبه‌شان را مى‌پذيرد. و خدا دانا و حكيم است.'),
(1342, 9, 107, 'خدا شهادت مى‌دهد: آنهايى كه مسجدى مى‌سازند تا به مؤمنان زيان رسانند و ميانشان كفر و تفرقه اندازند و تا براى كسانى كه مى‌خواهند با خدا و پيامبرش جنگ كنند كمينگاهى باشد، آنگاه سوگند مى‌خورند كه ما را قصدى جز نيكوكارى نبوده است؛ دروغ مى‌گويند.'),
(1343, 9, 108, 'هرگز در آن مسجد نمازمگزار. مسجدى كه از روز نخست بر پرهيزگارى بنيان شده شايسته‌تر است كه در آنجا نماز كنى. در آنجا مردانى هستند كه دوست دارند پاكيزه باشند، زيرا خدا پاكيزگان را دوست دارد.'),
(1344, 9, 109, 'آيا كسى كه بنيان مسجد را بر ترس از خدا و خشنودى او نهاده بهتر است، يا آن كسى كه بنيان مسجد را بر كناره سيلگاهى كه آب زير آن را شسته باشد نهاده است تا با او در آتش جهنم سرنگون گردد؟ و خدا مردم ستمگر را هدايت نمى‌كند.'),
(1345, 9, 110, 'آن بنا كه برآورده‌اند همواره مايه تشويش در دلشان خواهد بود تا آن هنگام كه دلشان پاره پاره گردد. و خداوند دانا و حكيم است.'),
(1346, 9, 111, 'خدا از مؤمنان جانها و مالهايشان را خريد، تا بهشت از آنان باشد. در راه خدا جنگ مى‌كنند، چه بكشند يا كشته شوند وعده‌اى كه خدا در تورات و انجيل و قرآن داده است به حق بر عهده اوست. و چه كسى بهتر از خدا به عهد خود وفا خواهد كرد؟ بدين خريد و فروخت كه كرده‌ايد شاد باشيد كه كاميابى بزرگى است.'),
(1347, 9, 112, 'توبه‌كنندگانند، پرستندگانند، ستايندگانند، روزه‌دارانند، ركوع كنندگانند، سجده‌كنندگانند، امر كنندگان به معروف و نهى كنندگان از منكرند و حافظان حدود خدايند. و مؤمنان را بشارت ده.'),
(1348, 9, 113, 'نبايد پيامبر و كسانى كه ايمان آورده‌اند براى مشركان هر چند از خويشاوندان باشند -پس از آنكه دانستند كه به جهنم مى‌روند- طلب آمرزش كنند.'),
(1349, 9, 114, 'آمرزش خواستن ابراهيم براى پدرش، نبود مگر به خاطر وعده‌اى كه به او داده بود. و چون براى او آشكار شد كه پدرش دشمن خداست، از او بيزارى جست. زيرا ابراهيم بسيار خداى‌ترس و بردبار بود.'),
(1350, 9, 115, 'خدا قومى را كه هدايت كرده است گمراه نمى‌خواهد، تا برايشان روشن كند كه از چه چيز بايد بپرهيزند، كه خدا بر هر چيزى داناست.'),
(1351, 9, 116, 'فرمانروايى آسمانها و زمين از آن خداست. زنده مى‌كند و مى‌ميراند و شما را جز خدا دوستدار و ياورى نيست.'),
(1352, 9, 117, 'خدا، توبه پيامبر و مهاجرين و انصار را كه در آن ساعت عسرت همراه او بودند، از آن پس كه نزديك بود كه گروهى را دل از جنگ بگردد، پذيرفت. توبه‌شان بپذيرفت، زيرا به ايشان رئوف و مهربان است.'),
(1353, 9, 118, 'و نيز خدا پذيرفت توبه آن سه تن را كه از جنگ تخلف كرده بودند، تا آنگاه كه زمين با همه گشادگيش بر آنها تنگ شد و جان در تنشان نمى‌گنجيد و خود دانستند كه جز خداوند هيچ پناهگاهى كه بدان روى آورند ندارند. پس خداوند توبه آنان بپذيرفت تا به او بازآيند، كه توبه‌پذير و مهربان است.'),
(1354, 9, 119, 'اى كسانى كه ايمان آورده‌ايد، از خدا بترسيد و با راستگويان باشيد.'),
(1355, 9, 120, 'اهل مدينه و عربهاى باديه‌نشين اطراف آن را نرسد كه از همراهى با پيامبر خدا تخلف ورزند و نبايد كه از او به خود پردازند. زيرا در راه خدا هيچ تشنگى به آنها چيره نشود يا به رنج نيفتند يا به گرسنگى دچار نگردند يا قدمى كه كافران را خشمگين سازد برندارند يا به دشمن دستبردى نزنند، مگر آنكه عمل صالحى برايشان نوشته شود، كه خدا پاداش نيكوكاران را تباه نمى‌سازد.'),
(1356, 9, 121, 'و هيچ مالى چه اندك و چه بسيار خرج نكنند و از هيچ واديى نگذرند، مگر آنكه به حسابشان نوشته شود، تا خدا پاداششان دهد، چون پاداش نيكوترين كارى كه مى‌كرده‌اند.'),
(1357, 9, 122, 'و نتوانند مؤمنان كه همگى به سفر روند. چرا از هر گروهى دسته‌اى به سفر نروند تا دانش دين خويش را بياموزند و چون بازگشتند مردم خود را هشدار دهند، باشد كه از زشتكارى حذر كنند؟'),
(1358, 9, 123, 'اى كسانى كه ايمان آورده‌ايد، با كافرانى كه نزديك شمايند جنگ كنيد تا در شما شدت و درشتى يابند، و بدانيد كه خدا با پرهيزگاران است.'),
(1359, 9, 124, 'و چون سوره‌اى نازل شود، بعضى مى‌پرسند: اين سوره به ايمان كدام يك از شما درافزود؟ آنان كه ايمان آورده‌اند به ايمانشان افزوده شود، و خود شادمانى مى‌كنند.'),
(1360, 9, 125, 'اما آنان كه در دلهايشان مرضى است، جز انكارى بر انكارشان نيفزود و همچنان كافر بمُردند.'),
(1361, 9, 126, 'آيا نمى‌بينند كه در هر سال يك يا دو بار مورد آزمايش واقع مى‌شوند؟ ولى نه توبه مى‌كنند و نه پند مى‌گيرند.'),
(1362, 9, 127, 'و چون سوره‌اى نازل شود، بعضى به بعضى ديگر نگاه مى‌كنند: آيا كسى شما را مى‌بيند؟ و باز مى‌گردند. خدا دلهايشان را از ايمان منصرف ساخته، زيرا مردمى نافهمند.'),
(1363, 9, 128, 'هر آينه پيامبرى از خود شما بر شما مبعوث شد، هر آنچه شما را رنج مى‌دهد بر او گران مى‌آيد. سخت به شما دلبسته است و با مؤمنان رئوف و مهربان است.'),
(1364, 9, 129, 'اگر باز گردند بگو: خدا براى من كافى است، خدايى جز او نيست، بر او توكل كردم و اوست پروردگار عرش بزرگ.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، را. اين است آيات كتاب به حكمت آميخته.'),
(1366, 10, 2, 'آيا مردم در شگفتند از اينكه به مردى از خودشان وحى كرديم كه مردم را بترسان و مؤمنان را بشارت ده كه در نزد پروردگارشان پايگاهى رفيع دارند؟ كافران گفتند كه اين مرد آشكارا جادوگرى است.'),
(1367, 10, 3, 'پروردگار شما اللّه است كه آسمانها و زمين را در شش روز بيافريد؛ سپس به عرش پرداخت؛ ترتيب كارها را از روى تدبير بداد. جز به رخصت او شفاعت‌كننده‌اى نباشد. اين است اللّه پروردگار شما. او را بپرستيد. چرا پند نمى‌گيريد؟'),
(1368, 10, 4, 'بازگشت همه شما به اوست. اين به يقين وعده خداست. او مخلوقات را بيافريند. آنگاه آنان را به سوى خود بازگرداند، تا كسانى را كه ايمان آورده و كار شايسته كرده‌اند از روى عدل پاداش دهد. و كافران را به جزاى كفرشان شرابى است از آب جوشان و عذابى است دردآور.'),
(1369, 10, 5, 'اوست آن كه خورشيد را فروغ بخشيد و ماه را منور ساخت و برايش منازلى معين كرد تا از شمار سالها و حساب آگاه شويد. خدا همه اينها را جز به حق نيافريد و آيات را براى مردمى كه مى‌دانند به تفصيل بيان مى‌كند.'),
(1370, 10, 6, 'درآمد و شد شب و روز و آنچه خدا در آسمانها و زمين آفريده است براى پرهيزگاران عبرتهاست.'),
(1371, 10, 7, 'كسانى كه به ديدار ما اميد ندارند و به زندگى دنيوى خشنود شده و بدان آرامش يافته‌اند، و آنان كه از آيات ما بى‌خبرند،'),
(1372, 10, 8, 'به كيفر كارهايى كه مى‌كرده‌اند، جايگاهشان جهنم است.'),
(1373, 10, 9, 'آنان را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند پروردگارشان به سبب ايمانشان به بهشتهايى پر نعمت كه نهرهاى آب در زير پايشان جارى است هدايت مى‌كند.'),
(1374, 10, 10, 'در آنجا دعايشان اين است: بار خدايا تو منزهى. و به هنگام درود، سلام مى‌گويند. و پايان دعايشان اين است: سپاس خداى را، آن پروردگار جهانيان.'),
(1375, 10, 11, 'اگر با همان شتاب كه مردم براى خود خير مى‌طلبند خدا برايشان شر مى‌طلبيد، مرگشان فرا رسيده بود. پس آنان را كه به ديدار ما اميد ندارند، وامى‌گذاريم تا در گمراهى خويش سرگردان بمانند.'),
(1376, 10, 12, 'چون به آدمى گزندى رسد، چه بر پهلو خفته باشد و چه نشسته يا ايستاده ما را به دعا مى‌خواند. و چون آن گزند را از او دور سازيم، چنان مى‌گذرد كه گويى ما را براى دفع آن گزندى كه به او رسيده بود هرگز نخوانده است. اعمال اسرافكاران اينچنين در نظرشان آراسته شده است.'),
(1377, 10, 13, 'و ما مردمى را كه پيش از شما بودند چون ستم كردند و به پيامبرانشان كه با دلايل روشن سوى ايشان آمده بودند ايمان نمى‌آوردند، هلاك كرديم. مردم تبهكار را اينچنين كيفر مى‌دهيم.'),
(1378, 10, 14, 'آنگاه شما را در روى زمين جانشين آنها گردانيديم تا بنگريم كه چگونه عمل مى‌كنيد.'),
(1379, 10, 15, 'چون آيات روشن ما بر آنان تلاوت شد، آنها كه به ديدار ما اميد ندارند گفتند: قرآنى جز اين قرآن بياور يا دگرگونش كن. بگو: مرا نرسد كه آن را از سوى خود دگرگون كنم. هر چه به من وحى مى‌شود پيرو همان هستم. مى‌ترسم كه اگر به پروردگارم عاصى شوم به عذاب آن روز بزرگ گرفتار آيم.'),
(1380, 10, 16, 'بگو: اگر خدا مى‌خواست من آن را بر شما تلاوت نمى‌كردم و شما را از آن آگاه نمى‌ساختم. و پيش از اين در ميان شما عمرى زيسته‌ام. چرا به عقل در نمى‌يابيد؟'),
(1381, 10, 17, 'كيست ستمكارتر از آن كه بر خدا دروغ مى‌بندد، يا آيات او را دروغ مى‌انگارد؟ هر آينه مجرمان رستگار نشوند.'),
(1382, 10, 18, 'سواى خدا چيزهايى را مى‌پرستند كه نه سودشان مى‌رساند نه زيان. و مى‌گويند: اينها شفيعان ما در نزد خدايند. بگو: آيا به خدا از چيزى خبر مى‌دهيد كه در زمين و آسمان از آن سراغى ندارد؟ خدا منزه است و از آنچه با او شريك مى‌سازيد، فراتر است.'),
(1383, 10, 19, 'مردم جز يك امت نبودند، ميانشان اختلاف افتاد. و اگر نه آن سخنى بود كه پيش از اين از پروردگارت صادر گشته بود، در آنچه اختلاف مى‌كنند داورى شده بود.'),
(1384, 10, 20, 'و مى‌گويند: چرا از جانب پروردگارش معجزه‌اى بر او نازل نمى‌شود؟ بگو: علم غيب از آن خداست. انتظار بكشيد. من نيز با شما منتظر مى‌مانم.'),
(1385, 10, 21, 'هرگاه پس از گزندى كه به مردم رسيده است رحمتى به آنها بچشانيم، بينى كه در آيات ما بدانديشى مى‌كنند. بگو: مكر خدا سريع‌تر است؛ رسولان ما نيز، بدانديشيهاى شما را مى‌نويسند.'),
(1386, 10, 22, 'اوست كه شما را در خشكى و در دريا سير مى‌دهد. تا آنگاه كه در كشتيها هستند و باد موافق به حركتشان مى‌آورد، شادمانند. چون توفان فرا رسد و موج از هر سو بر آنها ريزد، چنان كه پندارند كه در محاصره موج قرار گرفته‌اند، خدا را از روى اخلاص عقيدت بخوانند، كه اگر ما را از اين خطر برهانى از سپاسگزاران خواهيم بود.'),
(1387, 10, 23, 'چون خدا آنها را برهاند، بينى كه در زمين به ناحق سركشى كنند. اى مردم، اين سركشى به زيان خودتان است. تمتعى است در اين زندگى دنيوى. آنگاه همه به نزد ما بازمى‌گرديد تا از كارهايى كه مى‌كرده‌ايد آگاهتان سازيم.'),
(1388, 10, 24, 'مثَل اين زندگى دنيا مثَل بارانى است كه از آسمان نازل كنيم، تا بدان هر گونه رستنيها از زمين برويد، چه آنها كه آدميان مى‌خورند و چه آنها كه چارپايان مى‌چرند. چون زمين پيرايه خويش برگرفت و آراسته شد و مردمش پنداشتند كه خود قادر بر آن همه بوده‌اند، فرمان ما شب هنگام يا به روز دررسد و چنان از بيخش بركنيم كه گويى ديروز در آن مكان هيچ چيز نبوده است. آيات را براى مردمى كه مى‌انديشند اينچنين تفصيل مى‌دهيم.'),
(1389, 10, 25, 'خداوند به سراى سلامت فرا مى‌خواند و هر كه را بخواهد به راه راست هدايت مى‌كند.'),
(1390, 10, 26, 'پاداش آنان كه نيكى مى‌كنند نيكى است و چيزى افزون بر آن. نه سيه‌روى شوند و نه خوار. اينان اهل بهشتند و در آن جاويدانند.'),
(1391, 10, 27, 'و براى آنان كه مرتكب بديها شوند، پاداش هر بدى همانند آن است و خوارى بر آنها چيره مى‌شود. كسى آنها را از خشم خدا نگه نمى‌دارد، چنان شوند كه گويى صورتشان در پاره‌اى از شب تاريك پوشيده شده است. اينان اهل جهنمند و جاودانه در آن هستند.'),
(1392, 10, 28, 'و روزى همه آنها را در محشر گرد آوريم. سپس مشركان را گوييم: شما و بتهايتان در مكان خود قرار گيريد. سپس آنها را از يكديگر جدا مى‌كنيم و بتهايشان مى‌گويند: شما هرگز ما را نمى‌پرستيده‌ايد.'),
(1393, 10, 29, 'خدا به شهادت ميان ما و شما كافى است كه ما از پرستش شما هرگز آگاه نبوده‌ايم.'),
(1394, 10, 30, 'در آنجا هر كس هر چه كرده است پاداشش را خواهد ديد و همه را به سوى خدا مولاى حقيقيشان بازمى‌گردانند و همه آن بتان كه به باطل مى‌پرستيدند نابود مى‌شوند.'),
(1395, 10, 31, 'بگو: چه كسى از آسمان و زمين به شما روزى مى‌دهد؟ كيست كه شنوايى و بينايى مى‌بخشد؟ زنده را از مرده پديد مى‌آرد و مرده را از زنده و كارها را به سامان مى‌آورد؟ خواهند گفت: اللّه. بگو: آيا پروا نمى‌كنيد؟'),
(1396, 10, 32, 'اين اللّه پروردگار حقيقى شماست، بعد از حقيقت جز گمراهى چيست؟ پس به كجا روى مى‌آوريد؟'),
(1397, 10, 33, 'پس سخن پروردگار تو درباره عصيانگران كه گفت: اينان ايمان نمى‌آورند، به حقيقت پيوست.'),
(1398, 10, 34, 'بگو: آيا از اين بتان شما كسى هست كه چيزى را بيافريند، و باز زنده كند؟ بگو: خداى يكتا مخلوقات را مى‌آفريند و باز زنده مى‌كند. پس چگونه از حق منحرفتان مى‌كنند؟'),
(1399, 10, 35, 'بگو: آيا از اين بتان شما كسى هست كه به حق راه نمايد؟ بگو: خدا به حق راه مى‌نمايد. آيا آن كه به حق راه مى‌نمايد به متابعت سزاوارتر است يا آن كه به حق راه نمى‌نمايد و خود نيز نيازمند هدايت است؟ شما را چه مى‌شود؟ چگونه حكم مى‌كنيد؟'),
(1400, 10, 36, 'بيشترشان فقط تابع گمانند، و گمان نمى‌تواند جاى حق را بگيرد. هر آينه خدا به كارى كه مى‌كنند آگاه است.'),
(1401, 10, 37, 'نشايد كه اين قرآن را جز به خدا نسبت كنند، بلكه تصديق چيزى است كه پيش از آن آمده است و توضيح آن كتاب است. شكى نيست كه از جانب پروردگار جهانيان است.'),
(1402, 10, 38, 'مى‌گويند كه آن را به دروغ به خدا نسبت مى‌كند. بگو: اگر راست مى‌گوييد، جز خدا هر كه را كه مى‌توانيد به يارى بخوانيد و سوره‌اى همانند آن بياوريد.'),
(1403, 10, 39, 'چيزى را دروغ شمردند كه به علم آن احاطه نيافته بودند و هنوز از تأويل آن بى‌خبرند. كسانى كه پيش از آنان بودند نيز پيامبران را چنين به دروغ نسبت دادند. پس بنگر كه عاقبت كار ستمكاران چگونه بوده است.'),
(1404, 10, 40, 'برخى از ايشان بدان ايمان مى‌آورند و برخى ايمان نمى‌آورند. و پروردگار تو به مفسدان آگاه‌تر است.'),
(1405, 10, 41, 'اگر تو را به دروغ نسبت كردند، بگو: عمل من از آن من است و عمل شما از آن شما. شما از كار من بيزاريد و من از كار شما بيزارم.'),
(1406, 10, 42, 'برخى از ايشان به تو گوش مى‌دهند. آيا اگر درنيابند تو مى‌توانى كران را شنوا سازى؟'),
(1407, 10, 43, 'برخى از آنها به تو مى‌نگرند. آيا اگر نبينند، تو مى‌توانى كوران بى بصيرت را راه بنمايى؟'),
(1408, 10, 44, 'خداوند به مردم هيچ ستم نمى‌كند ولى مردم خود به خود ستم مى‌كنند.'),
(1409, 10, 45, 'و روزى كه خداوند آنان را در محشر گرد آورد، چنان كه پندارى تنها ساعتى از روز درنگ كرده‌اند، يكديگر را بشناسند. آنها كه ديدار با خدا را دروغ مى‌انگاشتند زيان ديده‌اند و هدايت نيافته‌اند.'),
(1410, 10, 46, 'يا پاره‌اى از چيزهايى را كه به آنها وعده داده‌ايم به تو مى‌نمايانيم يا تو را مى‌ميرانيم. بازگشت همه‌شان به نزد ماست و خدا به هر كارى كه مى‌كنند ناظر است.'),
(1411, 10, 47, 'هر امتى را پيامبرى بود و چون پيامبرشان بيامد كارها ميانشان به عدالت فيصله يافت و مورد ستم واقع نشدند.'),
(1412, 10, 48, 'مى‌گويند: اگر راست مى‌گوييد، پس اين وعده چه وقت خواهد بود؟'),
(1413, 10, 49, 'بگو: من درباره خود -جز آنچه خدا بخواهد- مالك هيچ سود و زيانى نيستم. مرگ هر امتى را زمانى معين است. چون زمانشان فرارسد، نه يك ساعت تأخير كنند و نه يك ساعت پيش افتند.'),
(1414, 10, 50, 'بگو: چه مى‌كنيد اگر عذاب او به ناگاه شب‌هنگام يا به روز فرا رسد؟ گناهكاران چه چيز را اينچنين به شتاب مى‌طلبند؟'),
(1415, 10, 51, 'آيا چون عذاب به وقوع پيوندد بدان ايمان مى‌آوريد؟ اكنون اين همان چيزى است كه فرا رسيدنش را به شتاب مى‌خواستيد.'),
(1416, 10, 52, 'آنگاه به ستمكاران گويند: عذاب جاويد را بچشيد. آيا نه چنين است كه در برابر اعمالتان كيفر مى‌بينيد؟'),
(1417, 10, 53, 'از تو خبر مى‌گيرند كه آيا حق است؟ بگو: آرى؛ سوگند به پروردگارم كه حق است و شما را ياراى فرار از آن نيست.'),
(1418, 10, 54, 'اگر هر گنهكارى صاحب همه روى زمين باشد، خواهد كه خود را بدان از عذاب بازخرد. و چون عذاب را بنگرند پشيمانى خويش در دل نهان دارند. ميانشان به عدالت حكم شود و مورد ستم واقع نگردند.'),
(1419, 10, 55, 'بدانيد كه هر چه در آسمانها و زمين است از آن خداست. و آگاه باشيد كه وعده خدا حق است، ولى بيشترشان نمى‌دانند.'),
(1420, 10, 56, 'اوست كه زنده مى‌كند و مى‌ميراند و همه به سوى او بازمى‌گرديد.'),
(1421, 10, 57, 'اى مردم، براى شما از جانب پروردگارتان موعظه‌اى آمد و شفايى براى آن بيماريى كه در دل داريد و راهنمايى و رحمتى براى مؤمنان.'),
(1422, 10, 58, 'بگو: به فضل خدا و رحمت او شادمان شوند، زيرا اين دو از هر چه مى‌اندوزند بهتر است.'),
(1423, 10, 59, 'بگو: آيا به رزقى كه خدا برايتان نازل كرده است نگريسته‌ايد؟ بعضى را حرام شمرديد و بعضى را حلال. بگو: خدا به شما اجازه داده است يا به او دروغ مى‌بنديد؟'),
(1424, 10, 60, 'آنان كه به خدا دروغ مى‌بندند در باره روز قيامت چه گمان كرده‌اند؟ خدا فضل خود را به مردمان ارزانى مى‌دارد، ولى بيشترينشان شكر نمى‌گويند.'),
(1425, 10, 61, 'در هر كارى كه باشى، و هر چه از قرآن بخوانى و دست به هر عملى كه بزنيد هنگامى كه بدان مى‌پردازيد ما ناظر بر شما هستيم. بر پروردگار تو حتى به مقدار ذره‌اى در زمين و آسمانها پوشيده نيست. و هر چه كوچكتر از آن يا بزرگ تر از آن باشد، در كتاب مبين نوشته شده است.'),
(1426, 10, 62, 'آگاه باشيد كه بر دوستان خدا بيمى نيست و غمگين نمى‌شوند.'),
(1427, 10, 63, 'كسانى را كه ايمان آوردند و پرهيزگارى مى‌كردند،'),
(1428, 10, 64, 'بشارت است ايشان را در دنيا و آخرت. سخن خدا دگرگون نمى‌شود. اين است كاميابى بزرگ.'),
(1429, 10, 65, 'سخن آنان تو را محزون نسازد. عزت به تمامى از آن خداوند است. اوست كه شنوا و داناست.'),
(1430, 10, 66, 'آگاه باشيد كه از آن خداست هر چه در آسمانها و زمين است و آنان كه سواى خدا را شريكان مى‌خوانند، جز از گمان خويش پيروى نمى‌كنند و جز دروغ نمى‌گويند.'),
(1431, 10, 67, 'اوست كه شب را برايتان پديد آورد تا در آن بياراميد و روز را پديد آورد تا در آن ببينيد. در اينها براى مردمى كه مى‌شنوند عبرتهاست.'),
(1432, 10, 68, 'گفتند: خدا فرزندى برگزيده است. منزه است. بى‌نياز است. از آن اوست هر چه در آسمانها و زمين است. شما را بر آن سخن دليلى نيست. چرا در باره خدا چيزهايى مى‌گوييد كه به آن آگاه نيستيد؟'),
(1433, 10, 69, 'بگو: آنها كه به خدا دروغ مى‌بندند رستگار نمى‌شوند.'),
(1434, 10, 70, 'برخوردارى از دنيا، آنگاه بازگشتشان به سوى ما. سپس به كيفر كفرورزيدنشان عذابى سختشان مى‌چشانيم.'),
(1435, 10, 71, 'برايشان داستان نوح را بخوان، آنگاه كه به قوم خود گفت: اى قوم من، اگر درنگ‌كردن من در ميان شما و يادآورى آيات خدا بر شما گران مى‌آيد، من بر خدا توكل مى‌كنم، ساز و برگ خويش و بتانتان را گرد آوريد چنان كه هيچ چيز از كارى كه مى‌كنيد بر شما پوشيده نباشد و به دشمنى من قدم به پيش نهيد و مرا مهلت مدهيد.'),
(1436, 10, 72, 'و اگر پشت كرديد، من از شما هيچ پاداشى نخواسته‌ام، كه پاداش من با خداست و من مأمور شده‌ام كه از تسليم‌شدگان باشم.'),
(1437, 10, 73, 'تكذيبش كردند و ما او و همراهانش را در كشتى برهانيديم و آنان را جانشين پيشينيان ساختيم و كسانى را كه آيات ما را دروغ مى‌انگاشتند غرقه كرديم. پس بنگر كه عاقبت بيم‌داده‌شدگان چگونه بود.'),
(1438, 10, 74, 'آنگاه پس از او پيامبرانى بر قومشان مبعوث كرديم و آنان دليلهاى روشن آوردند ولى مردم به آنچه پيش از آن تكذيبش كرده بودند، ايمان‌آورنده نبودند. بر دلهاى تجاوزكاران اينچنين مهر مى‌نهيم.'),
(1439, 10, 75, 'و پس از آنها موسى و هارون را همراه آياتمان بر فرعون و مهتران قومش مبعوث كرديم. و آنان سركشى كردند و مردمى تبهكار بودند.'),
(1440, 10, 76, 'و چون حق از نزد ما به سوى ايشان آمد، گفتند كه اين جادويى آشكار است.'),
(1441, 10, 77, 'موسى گفت: آيا سخن حق را كه اينك بر شما نازل شده است جادو مى‌خوانيد، و حال آنكه جادوگران رستگار نمى‌شوند؟'),
(1442, 10, 78, 'گفتند: آيا آمده‌اى تا ما را از آن آيين كه پدرانمان را بر آن يافته‌ايم منصرف سازى تا در زمين سرورى يابيد؟ ولى ما به شما ايمان نمى‌آوريم.'),
(1443, 10, 79, 'فرعون گفت: همه جادوگران دانا را نزد من بياوريد.'),
(1444, 10, 80, 'چون جادوگران آمدند، موسى گفت: بيفكنيد هر چه را كه بايد بيفكنيد.'),
(1445, 10, 81, 'چون بيفكندند، موسى گفت: آنچه شما آورده‌ايد جادوست و خدا آن را باطل خواهد ساخت، زيرا خدا كار مفسدان را به صلاح نمى‌آورد.'),
(1446, 10, 82, 'و خداوند با سخنان خود حق را به اثبات مى‌رساند، اگر چه مجرمان را ناخوش آيد.'),
(1447, 10, 83, 'از بيم آزار فرعون و مهتران قوم او، جز گروهى از آنها به موسى ايمان نياوردند. و فرعون در زمين برترى مى‌جست و او از گزافكاران بود.'),
(1448, 10, 84, 'موسى گفت: اى قوم من، اگر به خدا ايمان آورده‌ايد و تسليم فرمان هستيد به او توكل كنيد.'),
(1449, 10, 85, 'گفتند: بر خدا توكل كرديم، اى پروردگار ما، ما را مغلوب اين مردم ستمكار مكن.'),
(1450, 10, 86, 'و به رحمت خود ما را از اين كافران رهايى بخش.'),
(1451, 10, 87, 'به موسى و برادرش وحى كرديم كه براى قوم خود در مصر خانه‌هايى مهيا كنيد و خانه‌هاى خود را عبادتگاه سازيد و نمازگزاريد و مؤمنان را بشارت ده.'),
(1452, 10, 88, 'موسى گفت: اى پروردگار ما، به فرعون و مهتران قومش در اين جهان زينت و اموال داده‌اى، اى پروردگار ما، تا ديگران را از طريق تو گمراه كنند. اى پروردگار ما، اموالشان را تباه ساز و دلشان را سخت كن. ايمان نمى‌آورند تا آنگاه كه عذاب دردآور را بنگرند.'),
(1453, 10, 89, 'گفت: خواست شما اجابت شد، ثابت‌قدم باشيد و از طريقه نادانان پيروى مكنيد.'),
(1454, 10, 90, 'ما بنى‌اسرائيل را از دريا گذرانيديم. فرعون و لشكريانش به قصد ستم و تعدى به تعقيبشان پرداختند. چون فرعون غرق مى‌شد گفت: ايمان آوردم كه هيچ خداوندى جز آن كه بنى‌اسرائيل بدان ايمان آورده‌اند نيست، و من از تسليم‌شدگانم.'),
(1455, 10, 91, 'آيا اكنون؟ و تو پيش از اين عصيان مى‌كردى و از مفسدان بودى.'),
(1456, 10, 92, 'امروز جسم تو را به بلندى مى‌افكنيم تا براى آنان كه پس از تو مى‌مانند عبرتى باشى، و حال آنكه بسيارى از مردم از آيات ما غافلند.'),
(1457, 10, 93, 'هرآينه بنى‌اسرائيل را در مكانى نيكو جاى داديم و از طيّبات روزيشان داديم و تا آن هنگام كه صاحب دانش نشده بودند اختلافى نداشتند. پروردگار تو در روز قيامت در آنچه اختلاف مى‌كردند ميانشان قضاوت خواهد كرد.'),
(1458, 10, 94, 'اگر در آنچه بر تو نازل كرده‌ايم در ترديد هستى، از آنهايى كه كتاب آسمانى پيش از تو را مى‌خوانند بپرس. هرآينه حق است آنچه از جانب پروردگارت بر تو نازل شده است. پس نبايد در شمار ترديدكنندگان باشى.'),
(1459, 10, 95, 'و از آنان كه آيات خدا را تكذيب مى‌كنند مباش، كه در زمره زيان‌كنندگان باشى.'),
(1460, 10, 96, 'كسانى كه سخن پروردگار تو درباره آنان تحقق يافته، ايمان نمى‌آورند،'),
(1461, 10, 97, '-هر چند هر گونه معجزه‌اى بر آنان آشكار شود- تا آنگاه كه عذاب دردآور را بنگرند.'),
(1462, 10, 98, 'چرا مردم هيچ قريه‌اى به هنگامى كه ايمانشان سودشان مى‌داد ايمان نياوردند مگر قوم يونس كه چون ايمان آوردند عذاب ذلت در دنيا را از آنان برداشتيم و تا هنگامى كه اجلشان فرا رسيد از زندگى برخوردارشان كرديم.'),
(1463, 10, 99, 'اگر پروردگار تو بخواهد، همه كسانى كه در روى زمينند ايمان مى‌آورند. آيا تو مردم را به اجبار وامى‌دارى كه ايمان بياورند؟'),
(1464, 10, 100, 'جز به اذن خدا هيچ كس را نرسد كه ايمان بياورد. و او پليدى را بر كسانى كه خرد خويش به كار نمى‌بندند مقرر مى‌كند.'),
(1465, 10, 101, 'بگو: بنگريد كه چه چيزهايى در آسمانها و زمين است. و اين آيات و هشدارها قومى را كه ايمان نمى‌آورند سود نمى‌كند.'),
(1466, 10, 102, 'آيا جز اين است كه انتظار روزى را مى‌كشند همانند روزهايى كه پيشينيانشان منتظرش بودند؟ بگو: انتظار بكشيد، كه من نيز با شما منتظرم.'),
(1467, 10, 103, 'آنگاه پيامبرانمان و كسانى را كه ايمان آورده‌اند مى‌رهانيم. زيرا بر ما فريضه است كه مؤمنان را برهانيم.'),
(1468, 10, 104, 'بگو: اى مردم، اگر شما در دين من ترديد داريد، من چيزهايى را كه به جاى اللّه مى‌پرستيد نمى‌پرستم. من اللّه را مى‌پرستم كه شما را مى‌ميراند و مأمور شده‌ام كه از مؤمنان باشم.'),
(1469, 10, 105, 'و به دين حنيف روى آور و از مشركان مباش.'),
(1470, 10, 106, 'به جاى اللّه، خدايانى را كه نه به تو سود مى‌رسانند و نه زيان، مخوان. اگر چنين كنى، از ستمكاران خواهى بود.'),
(1471, 10, 107, 'و اگر خدا به تو زيانى برساند، جز او كس دفع آن نتواند كرد. و اگر براى تو خيرى بخواهد، هيچ كس فضل او را باز نتواند داشت. فضل خود را به هر كس از بندگانش كه بخواهد مى‌رساند و او آمرزنده و مهربان است.'),
(1472, 10, 108, 'بگو: اى مردم، پيام راستين از جانب پروردگارتان برايتان فرا رسيد. پس هر كس كه به راه راست هدايت يابد، هدايت به سود اوست. و هر كه گمراه گردد، به زيان خويش به گمراهى افتاده است. و من عهده‌دار شما نيستم.'),
(1473, 10, 109, 'چيزى را كه به تو وحى مى‌شود پيروى كن و صبر كن تا خدا داورى كند كه او بهترين داوران است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف، لام، را. كتابى است با آياتى استوار و روشن از جانب حكيمى آگاه.'),
(1475, 11, 2, 'كه جز خداى يكتا را نپرستيد، من از جانب او بيم‌دهنده شما و نيز مژده‌دهنده‌اى براى شما هستم.'),
(1476, 11, 3, 'و نيز از پروردگارتان آمرزش بخواهيد و به درگاهش توبه كنيد، تا شما را از رزقى نيكو -تا آنگاه كه مقرر است- برخوردارى دهد. و هر شايسته انعامى را نعمت دهد. و اگر رويگردان شويد، بر شما از عذاب روز بزرگ بيمناكم.'),
(1477, 11, 4, 'بازگشتتان به خداست و او بر هر كارى تواناست.'),
(1478, 11, 5, 'آگاه باش كه اينان صورت برمى گردانند تا راز دل خويش پنهان دارند، حال آنكه بدان هنگام كه جامه‌هاى خود در سر مى‌كشند خدا آشكار و نهانشان را مى‌داند، زيرا او به راز دلها آگاه است.'),
(1479, 11, 6, 'هيچ جنبنده‌اى در روى زمين نيست، جز آنكه روزى او بر عهده خداست، و موضع و مكانش را مى‌داند، زيرا همه در كتاب مبين آمده است.'),
(1480, 11, 7, 'اوست كه آسمانها و زمين را در شش روز آفريد و عرش او بر روى آب بود. تا بيازمايد كدام يك از شما به عمل نيكوتر است. و اگر بگويى كه بعد از مرگ زنده مى‌شويد، كافران گويند كه اين جز جادويى آشكار نيست.'),
(1481, 11, 8, 'و اگر چندگاهى عذابشان را به تأخير بيفكنيم مى‌پرسند: چه چيز مانع آن شده است؟ آگاه باشيد چون عذابشان فرا رسد، آن را باز نگردانند، و آنچه مسخره‌اش مى‌كردند آنان را در بر خواهد گرفت.'),
(1482, 11, 9, 'اگر به انسان رحمتى بچشانيم، آنگاه از او بازش گيريم، مأيوس مى‌شود و كفران مى‌ورزد.'),
(1483, 11, 10, 'و اگر پس از سختى و رنج، نعمت و آسايشى به او بچشانيم، مى‌گويد: ناگواريها از من دور شده است. و در اين حال، شادمان است و فخر مى‌فروشد،'),
(1484, 11, 11, 'مگر كسانى كه شكيبايى ورزيدند و كارهاى نيكو كردند، كه آمرزش و مزد بزرگ از آن آنهاست.'),
(1485, 11, 12, 'مباد كه برخى از چيزهايى را كه بر تو وحى كرده‌ايم واگذارى و بدان دلتنگ باشى كه مى‌گويند: چرا گنجى بر او افكنده نمى‌شود؟ و چرا فرشته‌اى همراه او نمى‌آيد؟ جز اين نيست كه تو بيم‌دهنده‌اى بيش نيستى و خداست كه كارساز هر چيزى است.'),
(1486, 11, 13, 'يا آنكه مى‌گويند كه از خود برمى‌بافد و به دروغ به خدا نسبتش مى‌كند. بگو: اگر راست مى‌گوييد، جز خدا هر كه را كه توانيد به يارى بطلبيد و ده سوره مانند آن به هم بربافته، بياوريد.'),
(1487, 11, 14, 'پس اگر شما را اجابت نكردند، بدانيد كه قرآن به علم خدا نازل شده و نيز هيچ خدايى جز او نيست. آيا تسليم مى‌شويد؟'),
(1488, 11, 15, 'آنان كه زندگى و زينت اين دنيا را بخواهند، پس همه مزد كردارشان را در اين جهان مى‌دهيم و در آن نقصانى نمى‌بينند.'),
(1489, 11, 16, 'اينان كسانى هستند كه در آخرت جز آتش نصيبى ندارند و هر چه كرده‌اند ناچيز شود و هر چه به جاى آورده‌اند باطل است.'),
(1490, 11, 17, 'آيا آن كس كه از جانب پروردگار خويش دليلى روشن دارد و زبانش بدان گوياست و پيش از اين كتاب موسى كه خود پيشوا و رحمتى بوده است بدان شهادت داده، با آن كس كه دليلى ندارد برابر است؟ ايشان به آن كتاب روشن ايمان مى‌آورند. و هر گروه ديگرى كه به او كافر شود، جايگاهش در آتش است. در آن شك مكن، كه حق است و از جانب پروردگارت آمده است. ولى بيشتر مردم ايمان نمى‌آورند.'),
(1491, 11, 18, 'چه كس ستمكارتر از آن كسى است كه به خدا دروغ مى‌بندد؟ اينان را به پروردگارشان عرضه خواهند داشت و شاهدان گواهى خواهند داد كه اينانند كه بر پروردگارشان دروغ مى‌بسته‌اند. هان، لعنت خدا بر ستمكاران باد؛'),
(1492, 11, 19, 'آنان كه مردم را از راه خدا بازمى‌دارند و راه خدا را منحرف مى‌خواهند و به آخرت ايمان ندارند؛'),
(1493, 11, 20, 'اينان نمى‌توانند در روى زمين از خداى بگريزند و جز او هيچ ياورى ندارند، عذابشان مضاعف مى‌شود. نه توان شنيدن داشته‌اند و نه توان ديدن.'),
(1494, 11, 21, 'اينان به خويشتن زيان رسانيدند و آنچه را كه به دروغ خدا مى‌خواندند از دست داده‌اند.'),
(1495, 11, 22, 'به ناچار در آخرت زيانكارترند.'),
(1496, 11, 23, 'كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند و در برابر پروردگارشان فروتنى نموده‌اند، اهل بهشتند و در آن جاويدانند.'),
(1497, 11, 24, 'مثَل اين دو گروه مثَل كور و كر و بينا و شنواست. آيا اين دو به مثَل با هم برابرند؟ چرا پند نمى‌گيريد؟'),
(1498, 11, 25, 'و نوح را بر مردمش به پيامبرى فرستاديم. گفت: من براى شما بيم‌دهنده‌اى آشكارم،'),
(1499, 11, 26, 'كه جز خداى يكتا را نپرستيد. زيرا از عذاب روز سخت قيامت بر شما بيمناكم.'),
(1500, 11, 27, 'مهتران قومش كه كافر بودند گفتند: ما تو را جز انسانى همانند خويش نمى‌بينيم. و نمى‌بينيم كه جز اراذل قوم از تو متابعت كنند. و نمى‌بينيم كه شما را بر ما فضيلتى باشد، بلكه پنداريم كه دروغ مى‌گوييد.'),
(1501, 11, 28, 'گفت: اى قوم من، چه مى‌گوييد اگر از پروردگارم حجتى به همراه داشته باشم و او مرا رحمت خويش ارزانى كرده باشد و شما از ديدن آن ناتوان باشيد. آيا در حالى كه خود نمى‌خواهيد، شما را به اكراه به قبول آن واداريم؟'),
(1502, 11, 29, 'اى قوم من، در برابر تبليغ رسالت خويش مالى از شما نمى‌طلبم. مزد من تنها با خداست. آنهايى را كه ايمان آورده‌اند از خود نمى‌رانم؛ آنان با پروردگار خويش ديدار خواهند كرد. ولى مى‌بينم كه شما مردمى نادان هستيد.'),
(1503, 11, 30, 'اى قوم من، اگر آنها را از خود برانم، چه كسى در قبال خدا مرا يارى خواهد كرد؟ آيا حقيقت را درنمى‌يابيد؟'),
(1504, 11, 31, 'به شما نمى‌گويم كه خزاين خدا در نزد من است. و علم غيب هم نمى‌دانم. و نمى‌گويم كه فرشته هستم. و نمى‌گويم كه خدا به آنان كه شما به حقارت در آنها مى‌نگريد خير خود را عطا نكند. خدا به آنچه در دلهاى آنهاست آگاه‌تر است. اگر چنين كنم، از ستمكاران خواهم بود.'),
(1505, 11, 32, 'گفتند: اى نوح، با ما جدال كردى و بسيار هم جدال كردى. اگر راست مى‌گويى، هر وعده‌اى كه به ما داده‌اى بياور.'),
(1506, 11, 33, 'گفت: خداست كه اگر بخواهد آن وعده را آشكار مى‌كند و شما از آن نتوانيد گريخت.'),
(1507, 11, 34, 'و اگر خدا خواسته باشد كه گمراهتان سازد، اگر من بخواهم شما را اندرز دهم، اندرزم سود نخواهد كرد. اوست پروردگار شما و همه بدو باز مى‌گرديد.'),
(1508, 11, 35, 'يا مى‌گويند كه آن را به خدا دروغ بسته است. بگو: اگر آن را به خدا دروغ بسته باشم، گناهش بر من است و من از گناهى كه مى‌كنيد مُبرا هستم.'),
(1509, 11, 36, 'و به نوح وحى رسيد كه از قوم تو جز آن گروه كه ايمان آورده‌اند، ديگر ايمان نخواهند آورد. از كردار آنان اندوهگين مباش.'),
(1510, 11, 37, 'كشتى را زير نظر و الهام ما بساز و در باره اين ستمكاران با من سخن مگوى، كه همه غرقه‌اند.'),
(1511, 11, 38, 'نوح كشتى مى‌ساخت و هر بار كه مهتران قومش بر او مى‌گذشتند مسخره‌اش مى‌كردند. مى‌گفت: اگر شما ما را مسخره مى‌كنيد، زودا كه ما هم همانند شما مسخره‌تان خواهيم كرد.'),
(1512, 11, 39, 'به زودى خواهيد دانست كه عذاب بر كه رسد و خوارش سازد و عذاب جاويد بر كه فرود آيد.'),
(1513, 11, 40, 'چون فرمان ما فراز آمد و تنور جوشيد، گفتيم: از هر نر و ماده دو تا و نيز خاندان خود را در كشتى بنشان -مگر آن كس را كه حكم در باره‌اش از پيش صادر شده باشد- و نيز آنهايى را كه به تو ايمان آورده‌اند. و جز اندكى به او ايمان نياورده بودند.'),
(1514, 11, 41, 'گفت: بر آن سوار شويد، كه به نام خدا به راه افتد و به نام خدا بايستد. زيرا پروردگار من، آمرزنده و مهربان است.'),
(1515, 11, 42, 'كشتى آنان را در ميان امواجى چون كوه مى‌برد. نوح پسرش را كه در گوشه‌اى ايستاده بود ندا داد: اى پسر، با ما سوار شو و با كافران مباش.'),
(1516, 11, 43, 'گفت: من بر سر كوهى كه مرا از آب نگه دارد، جا خواهم گرفت. گفت: امروز هيچ نگهدارنده‌اى از فرمان خدا نيست مگر كسى را كه بر او رحم آورد. ناگهان موج ميان آن دو حايل گشت و او از غرق‌شدگان بود.'),
(1517, 11, 44, 'و گفته شد: اى زمين آب خود فروبر و اى آسمان بازايست. آب فروشد و كار به پايان آمد و كشتى بر كوه جودى قرار گرفت و ندا آمد كه اى لعنت باد بر مردم ستمكاره.'),
(1518, 11, 45, 'نوح پروردگارش را ندا داد: اى پروردگار من، پسرم از خاندان من بود و وعده تو حق است و نيرومندترين حكم‌كنندگان تو هستى.'),
(1519, 11, 46, 'گفت: اى نوح، او از خاندان تو نيست، او عملى است ناصالح. از سر ناآگاهى از من چيزى مخواه. برحذر مى‌دارم تو را كه از مردم نادان باشى.'),
(1520, 11, 47, 'گفت: اى پروردگار من، پناه مى‌برم به تو اگر از سر ناآگاهى چيزى بخواهم، و اگر مرا نيامرزى و به من رحمت نياورى از زيان‌كردگان خواهم بود.'),
(1521, 11, 48, 'گفته شد: اى نوح، به سلامت و بركاتى كه بر تو و آنها كه همراه تواند ارزانى داشته‌ايم، فرود آى. و امتهايى هستند كه آنها را برخوردار مى‌سازيم، آنگاه دستخوش عذاب دردآور ما مى‌شوند.'),
(1522, 11, 49, 'اينها از خبرهاى غيب است كه بر تو وحى مى‌كنيم. پيش از اين نه تو آنها را مى‌دانستى و نه قوم تو. پس صبر كن، زيرا عاقبت نيك از آن پرهيزگاران است.'),
(1523, 11, 50, 'و بر قوم عاد برادرشان هود را فرستاديم. گفت: اى قوم من، خداى يكتا را بپرستيد، شما را هيچ خدايى جز او نيست و شما دروغ‌سازانى بيش نيستيد.'),
(1524, 11, 51, 'اى قوم من، در برابر رسالتم از شما مزدى نمى‌طلبم. مزد من تنها با آن كسى است كه مرا آفريده است. چرا از روى خرد نمى‌انديشيد؟'),
(1525, 11, 52, 'و اى قوم من، از پروردگارتان آمرزش بخواهيد، آنگاه بر آستان او توبه كنيد تا باران را پى در پى بر شما فروريزد و بر نيرويتان بيفزايد. و چون گنهكاران رخ برمتابيد.'),
(1526, 11, 53, 'گفتند: اى هود، تو براى ما دليل روشنى نياورده‌اى و ما به گفتار تو خدايان خويش را ترك نمى‌كنيم و به تو ايمان نمى‌آوريم.'),
(1527, 11, 54, 'جز اين نگوييم كه بعضى از خدايان ما به تو آزارى رسانده‌اند. گفت: خدا را گواه مى‌گيرم و شما نيز گواه باشيد كه من از آنچه جز خداى يكتا به شرك مى‌پرستيد بيزارم.'),
(1528, 11, 55, 'همگى به حيله‌گرى بر ضد من برخيزيد و مرا مهلت مدهيد.'),
(1529, 11, 56, 'من بر خداى يكتا كه پروردگار من و پروردگار شماست توكل كردم. هيچ جنبنده‌اى نيست مگر آنكه زمام اختيارش را او گرفته است. هر آينه پروردگار من بر صراط مستقيم است.'),
(1530, 11, 57, 'اگر هم رويگردان شويد، من رسالت خويش را به شما رسانيدم و پروردگار من مردم ديگرى را جانشين شما خواهد ساخت و هيچ به او زيانى نمى‌رسانيد. زيرا پروردگار من، نگهبان همه چيزهاست.'),
(1531, 11, 58, 'چون فرمان ما دررسيد، به بخشايش خويش هود و كسانى را كه به او ايمان آورده بودند نجات داديم و آنها را از عذاب سخت رهانيديم.'),
(1532, 11, 59, 'اينان قوم عاد بودند كه آيات پروردگارشان را انكار كردند و پيامبرانش را نافرمانى كردند و به فرمان هر جبار كينه‌توزى گردن نهادند.'),
(1533, 11, 60, 'و در اين دنيا و در روز قيامت گرفتار لعنت شدند. آگاه باشيد كه قوم عاد به پروردگارشان كافر شدند كه لعنت باد بر عاد، قوم هود.'),
(1534, 11, 61, 'و بر قوم ثمود برادرشان صالح را فرستاديم. گفت: اى قوم من، خداى يكتا را بپرستيد. شما را جز او خدايى نيست. اوست كه شما را از زمين پديد آورده است و خواست كه آبادانش داريد. پس آمرزش خواهيد و به درگاهش توبه كنيد. هر آينه پروردگار من نزديك است و دعاها را اجابت مى‌كند.'),
(1535, 11, 62, 'گفتند: اى صالح، پيش از اين به تو اميد مى‌داشتيم. آيا ما را از پرستش آنچه پدرانمان مى‌پرستيدند بازمى‌دارى؟ ما از آنچه ما را بدان مى‌خوانى در شكّيم.'),
(1536, 11, 63, 'گفت: اى قوم من، چه گوييد اگر از پروردگارم حجتى به همراه داشته باشم و او مرا رحمت خويش ارزانى كرده باشد، چه كسى مرا يارى مى‌كند اگر از فرمانش سرپيچى كنم؟ اگر از شما فرمان برم جز به زيان من نخواهيد افزود.'),
(1537, 11, 64, 'اى قوم من، اين ماده‌شترِ خداوند است و نشانه‌اى است براى شما. بگذاريدش تا در زمين خدا بچرد و به بدى ميازاريدش كه به زودى عذاب شما را فروگيرد.'),
(1538, 11, 65, 'پس ماده‌شتر را پى كردند. گفت: سه روز در خانه‌هاى خود از زندگى برخوردار شويد و اين وعده‌اى است عارى از دروغ.'),
(1539, 11, 66, 'چون امر ما فرا رسيد، صالح را با كسانى كه به او ايمان آورده بودند به رحمت خويش از خوارى آن روز نجات بخشيديم. زيرا پروردگار تو توانا و پيروزمند است.'),
(1540, 11, 67, 'و ستمكاران را صيحه‌اى فروگرفت و در خانه‌هاى خود بر جاى مُردند.'),
(1541, 11, 68, 'چنان كه گويى هرگز در آن ديار نبوده‌اند. آگاه باشيد كه قوم ثمود به پروردگارشان كافر شدند، هان بر قوم ثمود لعنت باد.'),
(1542, 11, 69, 'به تحقيق رسولان ما براى ابراهيم مژده آوردند. گفتند: سلام. گفت: سلام. و لحظه‌اى بعد گوساله‌اى بريان حاضر آورد.'),
(1543, 11, 70, 'و چون ديد كه بدان دست نمى‌يازند، آنان را ناخوش داشت و در دل از آنها بيمناك شد. گفتند: مترس، ما بر قوم لوط فرستاده شده‌ايم.'),
(1544, 11, 71, 'زنش كه ايستاده بود، خنديد. او را به اسحاق بشارت داديم و پس از اسحاق به يعقوب.'),
(1545, 11, 72, 'زن گفت: واى بر من، آيا در اين پيرزالى مى‌زايم و اين شوهر من نيز پير است؟ اين چيز عجيبى است.'),
(1546, 11, 73, 'گفتند: آيا از فرمان خدا تعجب مى‌كنى؟ رحمت و بركات خدا بر شما اهل اين خانه ارزانى باد. او ستودنى و بزرگوار است.'),
(1547, 11, 74, 'چون وحشت از ابراهيم برفت و او را نويد آمد، با ما در باره قوم لوط به مجادله برخاست.'),
(1548, 11, 75, 'ابراهيم بردبار است و رئوف است و فرمانبردار است.'),
(1549, 11, 76, 'اى ابراهيم، از اين سخن اعراض كن. فرمان پروردگارت فراز آمده است و بر آنها عذابى كه هيچ برگشتى ندارد فرود خواهد آمد.'),
(1550, 11, 77, 'چون رسولان ما نزد لوط آمدند، لوط اندوهگين و دلتنگ شد و گفت: امروز روز سختى است.'),
(1551, 11, 78, 'و قومش شتابان نزد او آمدند و آنان پيش از اين مرتكب كارهاى زشت مى‌شدند. لوط گفت: اى قوم من، اينها دختران من هستند. براى شما پاكيزه‌ترند. از خدا بترسيد و مرا در برابر مهمانانم خجل مكنيد. آيامرد خردمندى در ميان شما نيست؟'),
(1552, 11, 79, 'گفتند: تو خود مى‌دانى كه ما را به دختران تو نيازى نيست و نيز مى‌دانى كه چه مى‌خواهيم.'),
(1553, 11, 80, 'لوط گفت: كاش در برابر شما قدرتى مى‌داشتم، يا مى‌توانستم به تكيه‌گاهى استوار پناه ببرم.'),
(1554, 11, 81, 'گفتند: اى لوط، ما رسولان پروردگار تو هستيم. اينان، هرگز به تو دست نخواهند يافت. چون پاسى از شب بگذرد، خاندان خويش را بيرون ببر. و هيچ يك از شما روى برنگرداند جز زنت كه به او نيز آنچه به آنها رسد خواهد رسيد. وعده آنها صبحگاه است. آيا صبح نزديك نيست؟'),
(1555, 11, 82, 'چون فرمان ما فرازآمد، آنجا را زيروزبر كرديم و بر آن شهر بارانى از سنگهايى از سجّيل، پى‌درپى، فرو باريديم،'),
(1556, 11, 83, 'كه بر آنها نشان پروردگارت بود و چنين عذابى از ستمكاران دور نيست.'),
(1557, 11, 84, 'و بر مَديَن برادرشان شعيب را فرستاديم. گفت: اى قوم من، خداى يكتا را بپرستيد؛ شما را هيچ خدايى جز او نيست. و در پيمانه و ترازو نقصان مكنيد. اينك شما را در نعمت مى‌بينم. و از روزى كه عذابش شما را فروگيرد بيمناكم.'),
(1558, 11, 85, 'اى قوم من، پيمانه و ترازو را از روى عدل، كامل ادا كنيد و به مردم چيزهايشان را كم مدهيد و چون تبه‌كاران در زمين فساد مكنيد.'),
(1559, 11, 86, 'اگر ايمان آورده‌ايد، آنچه خدا باقى مى‌گذارد برايتان بهتر است. و من نگهبان شما نيستم.'),
(1560, 11, 87, 'گفتند: اى شُعيب، آيا نمازت به تو فرمان مى‌دهد كه ما آنچه را پدرانمان مى‌پرستيدند ترك گوييم، يا در اموال خود آنچنان كه خود مى‌خواهيم تصرف نكنيم؟ به راستى تو مردى بردبار و خردمند هستى.'),
(1561, 11, 88, 'گفت: اى قوم من، چه مى‌گوييد اگر با من از جانب پروردگارم حجتى باشد و او مرا رزقى نيكو عطا كرده باشد؟ اگر شما را نهى مى‌كنم براى آن نيست كه خود سودى ببرم. تا آنجا كه بتوانم قصدى جز به صلاح‌آوردنتان ندارم. توفيق من تنها با خداست. به او توكل كرده‌ام و به درگاه او روى مى‌آورم.'),
(1562, 11, 89, 'اى قوم من، مخالفت با من شما را به كارى وا ندارد تا آنچه بر قوم نوح يا قوم هود يا قوم صالح يا در همين نزديكى به قوم لوط رسيد، به شما نيز برسد.'),
(1563, 11, 90, 'از پروردگارتان آمرزش بخواهيد. به درگاهش توبه كنيد، كه پروردگار من مهربان و دوست‌دارنده است.'),
(1564, 11, 91, 'گفتند: اى شعيب، بسيارى از چيزهايى را كه مى‌گويى نمى‌فهميم، تو را در ميان خود ناتوان مى‌بينيم، اگر به خاطر قبيله‌ات نبود، سنگسارت مى‌كرديم و تو بر ما پيروزى نيابى.'),
(1565, 11, 92, 'گفت: اى قوم من، آيا قبيله من در نزد شما از خدا پيروزمندتر است؟ آيا خدا را پس پشت خويش افكنديد؟ و حال آنكه پروردگار من به هر كارى كه مى‌كنيد احاطه دارد.'),
(1566, 11, 93, 'اى قوم من، شما همچنان كه هستيد به كار خويش مشغول باشيد و من هم به كار خويش مشغول مى‌شوم. به زودى خواهيد دانست كه آن عذاب خواركننده بر چه كسى فرود مى‌آيد و چه كسى دروغگوست. منتظر بمانيد، من نيز با شما منتظر مى‌مانم.'),
(1567, 11, 94, 'چون امر ما فرازآمد، شعيب و كسانى را كه به او ايمان آورده بودند به رحمت خويش رهانيديم. و ستمكاران را صيحه‌اى فرو گرفت و در خانه‌هاى خويش بر جاى مُردند؛'),
(1568, 11, 95, 'چنان كه گويى هرگز در آن ديار نبوده‌اند. هان لعنت بر مردم مدين باد، همچنان كه لعنت بر قوم ثمود.'),
(1569, 11, 96, 'و ما موسى را همراه با آيات و حجت آشكار خويش فرستاديم؛'),
(1570, 11, 97, 'به نزد فرعون و مهتران قومش. اما آنان پيرو فرمان فرعون شدند. و فرمان فرعون به راه صواب راه نمى‌نمود.'),
(1571, 11, 98, 'در روز قيامت پيشاپيش قوم خود بيايد و همه را به آتش درآورد كه واردشدن را بد جايگاهى است.'),
(1572, 11, 99, 'لعنت اينجهانى و لعنت روز قيامت را از پى دارند و چه بد عطايى به آنان داده شده است.'),
(1573, 11, 100, 'اينها اخبار قريه‌هايى است كه براى تو حكايت مى‌كنيم؛ قريه‌هايى كه بعضى هنوز برپايند و بعضى ويران.'),
(1574, 11, 101, 'ما به آنها ستم نكرديم بلكه خود به خود ستم مى‌كردند. و چون امر پروردگار تو فراز آمد، خدايانى كه به جاى خداى يكتا مى‌پرستيدند هيچ به كارشان نيامدند و جز زيانكارى چيزى بر آنان نيفزودند.'),
(1575, 11, 102, 'اينچنين بود مؤاخذه پروردگار تو وقتى كه بخواهد قريه‌اى ستمكار را به مؤاخذه كشد. مؤاخذه او عذابى سخت دردآور است.'),
(1576, 11, 103, 'در اينها براى كسانى كه از عذاب آخرت بيمناكند عبرتى است، در آن روز كه مردم گرد آورده شوند و آن روز كه مردم را در آن حاضر آورند.'),
(1577, 11, 104, 'و جز تا اندك مدتى به تأخيرش نمى‌اندازيم.'),
(1578, 11, 105, 'روزى كه چون بيايد هيچ كس جز به فرمان او سخن نگويد و مردمان بعضى بدبخت باشند و بعضى نيكبخت.'),
(1579, 11, 106, 'اما بدبختان در آتشند و مردمان را در آنجا ناله‌اى زار و خروشى سخت بود.'),
(1580, 11, 107, 'و تا آسمانها و زمين باقى هستند در آنجا جاودانه بمانند؛ مگر آنچه پروردگارت بخواهد، زيرا پروردگار تو هر چه خواهد همان كند.'),
(1581, 11, 108, 'اما نيكبختان تا آسمانها و زمين باقى هستند در بهشت جاويدان بمانند؛ مگر آنچه پروردگارت بخواهد. عطاى او هيچ منقطع نمى‌شود.'),
(1582, 11, 109, 'از آنچه اينان مى‌پرستند به ترديد مباش. جز بدان گونه كه پدرانشان پيش از اين مى‌پرستيدند، نمى‌پرستند. ما نصيب آنان را بى هيچ كم و كاست ادا خواهيم كرد.'),
(1583, 11, 110, 'به موسى كتاب داديم. در آن كتاب اختلاف شد. اگر نه حكمى بود كه از پيش از جانب پروردگارت صادر شده بود، ميانشان داورى شده بود؛ كه ايشان در آن كتاب سخت در ترديدند.'),
(1584, 11, 111, 'و پروردگار تو پاداش اعمال همه را به تمامى خواهد داد و خدا به كارهايى كه مى‌كنند آگاه است.'),
(1585, 11, 112, 'همراه با آنان كه با تو رو به خدا كرده‌اند، همچنان كه مأمور شده‌اى ثابت‌قدم باش. و طغيان مكنيد كه او به هر كارى كه مى‌كنيد بيناست.'),
(1586, 11, 113, 'به ستمكاران ميل مكنيد، كه آتش بسوزاندتان. شما را جز خدا هيچ دوستى نيست و كس ياريتان نكند.'),
(1587, 11, 114, 'نماز بگزار در آغاز و انجام روز و ساعاتى از شب. زيرا نيكيها، بديها را از ميان مى‌برند. اين اندرزى است براى اندرزپذيران.'),
(1588, 11, 115, 'صبر كن، زيرا خداوند مزد نيكوكاران را تباه نمى‌سازد.'),
(1589, 11, 116, 'چرا در ميان مردمانى كه پيش از شما بودند -جز اندكى كه از آن ميان نجاتشان داديم- خردمندانى نبودند تا مردمان را از فسادكردن در زمين باز دارند؟ ظالمان از پى آسودگى و لذات دنيوى رفتند و گنهكار بودند.'),
(1590, 11, 117, 'پروردگار تو هيچ قريه‌اى را كه مردمش نيكوكار باشند به ستم هلاك نخواهد ساخت.'),
(1591, 11, 118, 'و اگر پروردگار تو خواسته بود، همه مردم را يك امت كرده بود، ولى همواره گونه‌گون خواهند بود؛'),
(1592, 11, 119, 'مگر آنهايى كه پروردگارت بر آنها رحمت آورده و آنها را براى همين بيافريده است. و سخن پروردگار تو بر اين مقرر شده كه: جهنم را از همه جن و انس انباشته مى‌كنم.'),
(1593, 11, 120, 'هر خبرى از اخبار پيامبران را برايت حكايت مى‌كنيم تا تو را قويدل گردانيم. و در اين كتاب بر تو سخن حق، و براى مؤمنان موعظه و اندرز نازل شده است.'),
(1594, 11, 121, 'به كسانى كه ايمان نمى‌آورند بگو: شما به هرسان كه خواهيد عمل كنيد، ما نيز عمل مى‌كنيم.'),
(1595, 11, 122, 'شما انتظار بكشيد ما نيز منتظر مى‌مانيم.'),
(1596, 11, 123, 'و از آن خداست نهان آسمانها و زمين و به او بازگردانده مى‌شود همه كارها. او را بپرست و بر او توكل كن كه پروردگارت از آنچه به جاى مى‌آوريد غافل نيست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، را. اينهاست آيات كتاب روشنگر.'),
(1598, 12, 2, 'ما قرآنى عربى نازلش كرده‌ايم، باشد كه شما دريابيد.'),
(1599, 12, 3, 'با اين قرآن كه به تو وحى كرده‌ايم، بهترين داستان را برايت حكايت مى‌كنيم، كه تو از اين پيش از بى‌خبران بوده‌اى.'),
(1600, 12, 4, 'آنگاه كه يوسف به پدر خود گفت: اى پدر، من در خواب يازده ستاره و خورشيد و ماه ديدم، ديدم كه سجده‌ام مى‌كنند،'),
(1601, 12, 5, 'گفت: اى پسر كوچكم، خوابت را براى برادرانت حكايت مكن، كه تو را حيله‌اى مى‌انديشند. زيرا شيطان آدميان را دشمنى آشكار است.'),
(1602, 12, 6, 'و بدين سان پروردگارت تو را برمى‌گزيند و تعبير خواب مى‌آموزد و همچنان كه نعمت خود را پيش از اين بر پدران تو ابراهيم و اسحاق تمام كرده بود بر تو و خاندان يعقوب هم تمام مى‌كند، كه پروردگارت دانا و حكيم است.'),
(1603, 12, 7, 'در داستان يوسف و برادرانش براى آنان كه از آن پرسيده‌اند عبرتهاست.'),
(1604, 12, 8, 'آنگاه كه گفتند: يوسف و برادرش نزد پدرمان محبوب‌تر از ما هستند، حال آنكه ما خود گروهى نيرومنديم. پدرمان در گمراهى آشكارى است.'),
(1605, 12, 9, 'يوسف را بكشيد، يا در سرزمينى ديگرش بيندازيد تا پدر خاص شما گردد و از آن پس مردمى شايسته به شمار آييد.'),
(1606, 12, 10, 'يكى از ايشان گفت: اگر مى‌خواهيد كارى كنيد، يوسف را مكشيد؛ در عمق تاريك چاهش بيفكنيد تا كاروانى او را برگيرد.'),
(1607, 12, 11, 'گفتند: اى پدر، چيست كه ما را بر يوسف امين نمى‌شمارى، حال آنكه ما خيرخواه او هستيم؟'),
(1608, 12, 12, 'فردا او را با ما بفرست تا بگردد و بازى كند و ما نگهدارش هستيم.'),
(1609, 12, 13, 'گفت: اگر او را ببريد، غمگين مى‌شوم و مى‌ترسم كه از او غافل شويد و گرگ او را بخورد.'),
(1610, 12, 14, 'گفتند: با اين گروه نيرومند كه ما هستيم اگر گرگ او را بخورد، از زيانكاران خواهيم بود.'),
(1611, 12, 15, 'چون او را بردند و هماهنگ شدند كه در عمق تاريك چاهش بيفكنند، به او وحى كرديم كه ايشان را از اين كارشان آگاه خواهى ساخت و خود ندانند.'),
(1612, 12, 16, 'شب‌هنگام، گريان نزد پدرشان بازآمدند.'),
(1613, 12, 17, 'گفتند: اى پدر، ما به اسب‌تاختن رفته بوديم و يوسف را نزد كالاى خود گذاشته بوديم، گرگ او را خورد. و هر چند هم كه راست بگوييم تو سخن ما را باور ندارى.'),
(1614, 12, 18, 'جامه‌اش را كه به خون دروغين آغشته بود آوردند. گفت: نفس شما، كارى را در نظرتان بياراسته است. اكنون براى من صبر جميل بهتر است و خداست كه در اين باره از او يارى بايد خواست.'),
(1615, 12, 19, 'كاروانى آمد. آب‌آورشان را فرستادند. دلو فرو كرد. گفت: مژدگانى، اين پسرى است. او را چون متاعى پنهان ساختند و خدا به كارى كه مى‌كردند آگاه بود.'),
(1616, 12, 20, 'او را به بهاى اندك، به چند درهم فروختند، كه هيچ رغبتى به او نداشتند.'),
(1617, 12, 21, 'كسى از مردم مصر كه او را خريده بود به زنش گفت: تا در اينجاست گراميش بدار، شايد به ما سودى برساند، يا او را به فرزندى بپذيريم. و بدين سان يوسف را در زمين مكانت داديم تا به او تعبير خواب آموزيم. و خدا بر كار خويش غالب است، ولى بيشتر مردم نمى‌دانند.'),
(1618, 12, 22, 'و چون باليدن يافت، حكمت و دانشش ارزانى داشتيم و نيكوكاران را بدين سان پاداش مى‌دهيم.'),
(1619, 12, 23, 'و آن زن كه يوسف در خانه‌اش بود، در پى كامجويى از او مى‌بود. و درها را بست و گفت: بشتاب. گفت: پناه مى‌برم به خدا. او پروراننده من است و مرا منزلتى نيكو داده و ستمكاران رستگار نمى‌شوند.'),
(1620, 12, 24, 'آن زن آهنگ او كرد. و اگر نه برهان پروردگارش را ديده بود، او نيز آهنگ آن زن مى‌كرد. چنين كرديم تا بدى و زشتكارى را از وى بازگردانيم. زيرا او از بندگان پاكدل ما بود.'),
(1621, 12, 25, 'هر دو به جانب در دويدند و زن جامه او را از پس بدريد. و شوى آن زن را نزديك در ديدند. زن گفت: جزاى كسى كه با زن تو قصد بدى داشته باشد چيست، جز اينكه به زندان افتد يا به عذابى دردآور گرفتار آيد؟'),
(1622, 12, 26, 'يوسف گفت: او در پى كامجويى از من بود و مرا به خود خواند. و يكى از كسان زن گواهى داد كه اگر جامه‌اش از پيش دريده است زن راست مى‌گويد و او دروغگوست.'),
(1623, 12, 27, 'و اگر جامه‌اش از پس دريده است، زن دروغ مى‌گويد و او راستگوست.'),
(1624, 12, 28, 'چون ديد جامه‌اش از پس دريده است، گفت: اين از مكر شما زنان است، كه مكر شما زنان مكرى بزرگ است.'),
(1625, 12, 29, 'اى يوسف، زبان خويش نگه دار، و اى زن، از گناه خود آمرزش بخواه كه تو از خطا كارانى.'),
(1626, 12, 30, 'زنان شهر گفتند: زن عزيز در پى كامجويى از غلام خود شده است و شيفته او گشته است. ما وى را در گمراهى آشكارا مى‌بينيم.'),
(1627, 12, 31, 'چون افسونشان را شنيد، نزدشان كس فرستاد و براى هر يك تا تكيه دهد متكايى ترتيب داد و به هر يك كاردى داد، و گفت: بيرون آى تا تو را بنگرند. چون او را ديدند، بزرگش شمردند و دست خويش ببريدند و گفتند: معاذ اللّه، اين آدمى نيست، اين جز فرشته‌اى بزرگوار نيست.'),
(1628, 12, 32, 'گفت: اين همان است كه مرا در باب او ملامت مى‌كرديد. من در پى كامجويى از او بودم و او خويشتن نگه داشت. اگر آنچه فرمانش مى‌دهم نكند، به زندان خواهد افتاد و خوار خواهد شد.'),
(1629, 12, 33, 'گفت: اى پروردگار من، براى من زندان دوست داشتنى‌تر است از آنچه مرا بدان مى‌خوانند؛ و اگر مكر اين زنان را از من نگردانى، به آنها ميل مى‌كنم و در شمار نادانان درمى‌آيم.'),
(1630, 12, 34, 'پروردگارش دعايش را اجابت كرد و مكر زنان از او دور كرد. زيرا خدا شنوا و داناست.'),
(1631, 12, 35, 'پس با آن نشانها كه ديده بودند، تصميم كردند كه چندى به زندانش بيفكنند.'),
(1632, 12, 36, 'دو جوان نيز با او به زندان افتادند. يكى از آن دو گفت: در خواب، خود را ديدم كه انگور مى‌فشارم. ديگرى گفت: خود را ديدم كه نان بر سر نهاده مى‌برم و پرندگان از آن مى‌خورند. ما را از تعبير آن آگاه كن، كه از نيكوكارانت مى‌بينيم.'),
(1633, 12, 37, 'گفت: طعام روزانه شما هنوز نيامده باشد كه پيش از آن شما را از تعبير آن خوابها چنان كه پروردگارم به من آموخته است خبر مى‌دهم. من كيش مردمى را كه به خداى يكتا ايمان ندارند و به روز قيامت كافرند، ترك كرده‌ام.'),
(1634, 12, 38, 'من پيرو كيش پدرانم ابراهيم و اسحاق و يعقوب هستم و ما را نسزد كه هيچ چيز را شريك خدا قرار دهيم. اين فضيلتى است كه خدا بر ما و بر مردم ديگر ارزانى داشته است ولى بيشتر مردم ناسپاسند.'),
(1635, 12, 39, 'اى دو زندانى، آيا خدايان متعدد بهتر است يا اللّه آن خداوند يكتاى غالب بر همگان؟'),
(1636, 12, 40, 'نمى‌پرستيد سواى خداى يكتا، مگر بتانى را كه خود و پدرانتان آنها را به نامهايى خوانده‌ايد و خدا حجتى بر اثبات آنها نازل نكرده است. حكم جز حكم خدا نيست. فرمان داده است كه جز او را نپرستيد. اين است دين راست و استوار، ولى بيشتر مردم نمى‌دانند.'),
(1637, 12, 41, 'اى دو زندانى، اما يكى از شما براى مولاى خويش شراب ريزد، اما ديگرى را بر دار كنند و پرندگان سر او بخورند. كارى كه در باره آن نظر مى‌خواستيد به پايان آمده است.'),
(1638, 12, 42, 'به يكى از آن دو كه مى‌دانست رها مى‌شود، گفت: مرا نزد مولاى خود ياد كن. اما شيطان از خاطرش زدود كه پيش مولايش از او ياد كند، و چند سال در زندان بماند.'),
(1639, 12, 43, 'پادشاه گفت: در خواب هفت گاو فربه را ديده‌ام كه آنها را هفت گاو لاغر مى‌خورند و هفت خوشه سبز ديدم و هفت خوشه خشك. اى خاصگان من، خواب مرا تعبير كنيد، اگر تعبير خواب مى‌دانيد.'),
(1640, 12, 44, 'گفتند: اينها خوابهاى آشفته است و ما را به تعبير اين خوابها آگاهى نيست.'),
(1641, 12, 45, 'يكى از آن دو كه رها شده بود و پس از مدتى به يادش آمده بود، گفت: من شما را از تعبير آن آگاه مى‌كنم. مرا نزد او بفرستيد.'),
(1642, 12, 46, 'اى يوسف، اى مرد راستگوى، براى ما تعبير كن كه هفت گاو فربه را هفت گاو لاغر مى‌خورند، و هفت خوشه سبز و هفت خوشه خشك. باشد كه من نزد مردم بازگردم و آنان آگاه گردند.'),
(1643, 12, 47, 'گفت: هفت سال پى در پى بكاريد و هر چه مى‌درويد، جز اندكى كه مى‌خوريد، با خوشه انبار كنيد.'),
(1644, 12, 48, 'از آن پس هفت سال سخت مى‌آيد، و در آن هفت سال آنچه برايشان اندوخته‌ايد بخورند مگر اندكى كه نگه مى‌داريد.'),
(1645, 12, 49, 'پس از آن سالى آيد كه مردمان را باران دهند و در آن سال افشردنيها را مى‌فشرند.'),
(1646, 12, 50, 'پادشاه گفت: نزد منش بياوريد. چون فرستاده نزد او آمد، يوسف گفت: نزد مولايت بازگرد و بپرس حكايت آن زنان كه دستهاى خود را بريدند چه بود؟ كه پروردگار من به مكرشان آگاه است.'),
(1647, 12, 51, 'گفت: اى زنان، آنگاه كه خواستار تن يوسف مى‌بوديد، حكايت شما چه بود؟ گفتند: پناه بر خدا. او را هيچ گناهكار نمى‌دانيم. زن عزيز گفت: اكنون حق آشكار شد. من در پى كامجويى از او مى‌بودم و او در زمره راستگويان است.'),
(1648, 12, 52, 'چنين شد تا بداند كه من در غيبتش به او خيانت نكرده‌ام و خدا حيله خائنان را به هدف نمى‌رساند.'),
(1649, 12, 53, 'من خويشتن را بى‌گناه نمى‌دانم، زيرا نفس، آدمى را به بدى فرمان مى‌دهد. مگر پروردگار من ببخشايد، زيرا پروردگار من آمرزنده و مهربان است.'),
(1650, 12, 54, 'پادشاه گفت: او را نزد من بياوريد تا همنشين خاص خود گردانم. و چون با او سخن گفت، گفت: تو از امروز نزد ما صاحب‌مكانت و امين هستى.'),
(1651, 12, 55, 'گفت: مرا بر خزاين اين سرزمين بگمار كه من نگهبانى دانايم.'),
(1652, 12, 56, 'اينچنين يوسف را در آن سرزمين مكانت داديم. هر جا كه مى‌خواست جاى مى‌گرفت. رحمت خود را به هر كس كه بخواهيم ارزانى مى‌داريم و پاداش نيكوكاران را ضايع نمى‌كنيم.'),
(1653, 12, 57, 'و هر آينه پاداش آنجهانى براى كسانى كه ايمان آورده‌اند و پرهيزگارى مى‌كنند، بهتر است.'),
(1654, 12, 58, 'برادران يوسف آمدند و بر او داخل شدند، آنها را شناخت و آنها نشناختندش.'),
(1655, 12, 59, 'چون بارهايشان را مهيا ساخت، گفت: برادر پدريتان را نيز نزد من بياوريد، آيا نمى‌بينيد كه پيمانه را كامل ادا مى‌كنم و بهترين ميزبانم؟'),
(1656, 12, 60, 'اگر او را نزد من نياوريد، پيمانه‌اى نزد من نخواهيد داشت و به من نزديك مشويد.'),
(1657, 12, 61, 'گفتند: ما او را به اصرار از پدر خواهيم خواست و اين كار را خواهيم كرد.'),
(1658, 12, 62, 'و به مردان خود گفت: سرمايه‌شان را در بارهايشان بنهيد، باشد كه چون نزد كسانشان بازگردند و آن را بيابند، بازآيند.'),
(1659, 12, 63, 'چون نزد پدر بازگشتند، گفتند: اى پدر، پيمانه از ما برگرفته‌اند. برادرمان را با ما بفرست تا پيمانه بازگيريم. ما نگهدار او هستيم.'),
(1660, 12, 64, 'گفت: آيا او را به شما بسپارم، همچنان كه برادرش را پيش از اين به شما سپردم؟ خدا بهترين نگهدار است و اوست مهربان‌ترين مهربانان.'),
(1661, 12, 65, 'چون بار خود گشودند، ديدند كه سرمايه‌شان را پس داده‌اند. گفتند: اى پدر، در طلب چه هستيم؟ اين سرمايه ماست كه به ما پس داده‌اند. براى كسان خود غله بياوريم و برادرمان را حفظ كنيم و بار شترى افزون گيريم، كه آنچه داريم اندك است.'),
(1662, 12, 66, 'گفت: هرگز او را با شما نمى‌فرستم، تا با من به نام خدا پيمانى ببنديد كه نزد منش بازمى‌آوريد. مگر آنكه همه گرفتار شويد. چون با او عهد كردند، گفت: خدا بر آنچه مى‌گوييم گواه است.'),
(1663, 12, 67, 'گفت: اى پسران من، از يك دروازه داخل مشويد؛ از دروازه‌هاى مختلف داخل شويد. و من قضاى خدا را از سر شما دفع نتوانم كرد و هيچ فرمانى جز فرمان خدا نيست. بر او توكل كردم و توكل‌كنندگان بر او توكل كنند.'),
(1664, 12, 68, 'چون از جايى كه پدر فرمان داده بود داخل شدند، اين كار در برابر اراده خدا سودشان نبخشيد. تنها نيازى در ضمير يعقوب بود كه آن را آشكار ساخت، زيرا او را علمى بود كه خود به او آموخته بوديم ولى بيشتر مردم نمى‌دانند.'),
(1665, 12, 69, 'چون بر يوسف داخل شدند، برادرش را نزد خود جاى داد. گفت: من برادر تو هستم. از كارى كه اينان كرده‌اند اندوهگين مباش.'),
(1666, 12, 70, 'چون بارهايشان را مهيا كرد، جام را در بار برادر نهاد. آنگاه منادى ندا داد: اى كاروانيان، شما دزدانيد.'),
(1667, 12, 71, 'كاروانيان نزد آنها بازگشتند و گفتند: چه گم كرده‌ايد؟'),
(1668, 12, 72, 'گفتند: جام پادشاه را. و هر كه بياوردش او را بار شترى است و من ضمانت مى‌كنم.'),
(1669, 12, 73, 'گفتند: خدا را، شما خود مى‌دانيد كه ما فسادكردن را در اين سرزمين نيامده‌ايم و دزد نبوده‌ايم.'),
(1670, 12, 74, 'گفتند: اگر دروغ گفته باشيد جزاى دزد چيست؟'),
(1671, 12, 75, 'گفتند: جزايش همان كسى است كه در بار او يافته شود. پس او خود جزاى عمل خود است و ما گنهكاران را چنين جزا دهيم.'),
(1672, 12, 76, 'پيش از بار برادر به بار آنها پرداخت، آنگاه از بار برادرش بيرونش آورد. حيله‌اى اين سان به يوسف آموختيم. در آيين آن پادشاه، گرفتن برادر حق او نبود، چيزى بود كه خدا مى‌خواست. هر كس را كه بخواهيم به درجاتى بالا مى‌بريم و فراز هر دانايى داناترى است.'),
(1673, 12, 77, 'گفتند: اگر او دزدى كرده، برادرش نيز پيش از اين دزدى كرده بود. يوسف جواب آن سخن در دل پنهان داشت و هيچ اظهار نكرد و گفت: شما در وضعى بدتر هستيد و خدا به بهتانى كه مى‌زنيد آگاه‌تر است.'),
(1674, 12, 78, 'گفتند: اى عزيز، او را پدرى است سالخورده. يكى از ما را به جاى او بگير، كه از نيكوكارانت مى‌بينيم.'),
(1675, 12, 79, 'گفت: معاذالّله كه جز آن كس را كه كالاى خويش نزد او يافته‌ايم بگيريم. اگر چنين كنيم از ستمكاران خواهيم بود.'),
(1676, 12, 80, 'چون از او نوميد شدند، مشاورت را به كنارى رفتند و بزرگ ترينشان گفت: آيا نمى‌دانيد كه پدرتان از شما به نام خدا پيمان گرفته و پيش از اين نيز در حق يوسف تقصير كرده‌ايد؟ من از اين سرزمين بيرون نمى‌آيم تا پدر مرا رخصت دهد، يا خدا درباره من داورى كند، كه او بهترين داوران است.'),
(1677, 12, 81, 'نزد پدر باز گرديد و بگوييد: اى پدر، پسرت دزدى كرد و ما جز به آنچه مى‌دانستيم شهادت نداديم و از غيب نيز آگاه نيستيم.'),
(1678, 12, 82, 'از شهرى كه در آن بوده‌ايم و از كاروانى كه همراهش آمده‌ايم بپرس، كه ما راست مى‌گوييم.'),
(1679, 12, 83, 'گفت: نه، نفس شما كارى را در نظرتان بياراست و مرا صبر جميل بهتر است. شايد خدا همه را به من بازگرداند كه او دانا و حكيم است.'),
(1680, 12, 84, 'روى خود از آنها بگردانيد و گفت: اى اندوها بر يوسف. و چشمانش از غم سپيدى گرفت و همچنان اندوه خود فرو مى‌خورد.'),
(1681, 12, 85, 'گفتند: به خدا سوگند پيوسته يوسف را ياد مى‌كنى تا بيمار گردى يا بميرى.'),
(1682, 12, 86, 'گفت: جز اين نيست كه شرح اندوه خويش تنها با خدا مى‌گويم. زيرا آنچه من از خدا مى‌دانم شما نمى‌دانيد.'),
(1683, 12, 87, 'اى پسران من، برويد و يوسف و برادرش را بجوييد و از رحمت خدا مأيوس مشويد، زيرا تنها كافران از رحمت خدا مأيوس مى‌شوند.'),
(1684, 12, 88, 'چون بر يوسف داخل شدند، گفتند: اى عزيز، ما و كسانمان به گرسنگى افتاده‌ايم و با سرمايه‌اى اندك آمده‌ايم؛ پيمانه ما را تمام ادا كن و بر ما صدقه بده، زيرا خدا صدقه‌دهندگان را دوست دارد.'),
(1685, 12, 89, 'گفت: مى‌دانيد كه از روى نادانى با يوسف و برادرش چه كرديد؟'),
(1686, 12, 90, 'گفتند: آيا به حقيقت تو يوسفى؟ گفت: من يوسفم، و اين برادر من است و خدا به ما نعمت داد. زيرا هر كس كه پرهيزگارى كند و شكيبايى ورزد، خدا مزدش را تباه نمى‌سازد.'),
(1687, 12, 91, 'گفتند: به خدا سوگند كه خدا تو را بر ما فضيلت داد و ما خطاكار بوديم.'),
(1688, 12, 92, 'گفت: امروز شما را سرزنش نبايد كرد؛ خدا شما را مى‌بخشايد كه او مهربان‌ترين مهربانان است.'),
(1689, 12, 93, 'اين جامه مرا ببريد و بر روى پدرم اندازيد تا بينا گردد. و همه كسان خود را نزد من بياوريد.'),
(1690, 12, 94, 'چون كاروان به راه افتاد، پدرشان گفت: اگر مرا ديوانه نخوانيد بوى يوسف مى‌شنوم.'),
(1691, 12, 95, 'گفتند: به خدا سوگند كه تو در همان ضلالت ديرينه خويش هستى.'),
(1692, 12, 96, 'چون مژده‌دهنده آمد و جامه بر روى او انداخت، بينا گشت. گفت: آيا نگفتمتان كه آنچه من از خدا مى‌دانم شما نمى‌دانيد؟'),
(1693, 12, 97, 'گفتند: اى پدر براى گناهان ما آمرزش بخواه كه ما خطاكار بوده‌ايم.'),
(1694, 12, 98, 'گفت: از پروردگارم براى شما آمرزش خواهم خواست، او آمرزنده و مهربان است.'),
(1695, 12, 99, 'چون بر يوسف داخل شدند، پدر و مادر را به آغوش كشيد و گفت: به مصر درآييد كه اگر خدا بخواهد در امان خواهيد بود.'),
(1696, 12, 100, 'پدر و مادر را بر تخت فرا برد و همه در برابر او به سجده درآمدند. گفت: اى پدر، اين است تعبير آن خواب من كه اينك پروردگارم آن را تحقق بخشيده است. و چقدر به من نيكى كرده است آنگاه كه مرا از زندان برهانيد و پس از آنكه شيطان ميان من و برادرانم فساد كرده بود، شما را از باديه به اينجا آورد. پروردگار من به هر چه اراده كند دقيق است، كه او دانا و حكيم است.'),
(1697, 12, 101, 'اى پروردگار من، مرا فرمانروايى دادى و مرا علم تعبير خواب آموختى. اى آفريننده آسمانها و زمين، تو در دنيا و آخرت كارساز منى. مرا مسلمان بميران و قرين شايستگان ساز.'),
(1698, 12, 102, 'اينها خبرهاى غيب است كه به تواش وحى مى‌كنيم. و آن هنگام كه با يكديگر گرد آمده بودند و مشورت مى‌كردند و حيلت مى‌ساختند، تو نزد آنها نبودى.'),
(1699, 12, 103, 'هر چند تو به ايمانشان حريص باشى، بيشتر مردم ايمان نمى‌آورند.'),
(1700, 12, 104, 'و تو در مقابل پيامبريت از آنها مزدى نمى‌طلبى و اين كتاب جز اندرزى براى مردم جهان نيست.'),
(1701, 12, 105, 'چه بسيار نشانه‌هايى در آسمانها و زمين است كه بر آن مى‌گذرند و از آن رخ برمى‌تابند.'),
(1702, 12, 106, 'و بيشترشان به خدا ايمان نياورند بلكه همچنان مشركند.'),
(1703, 12, 107, 'آيا پندارند كه ايمنى يافته‌اند از اينكه عقوبتى عام از عذاب خدا آنها را فروگيرد يا قيامت به ناگاه فرا رسد، بى‌آنكه خبردار شوند؟'),
(1704, 12, 108, 'بگو: اين راه من است. من و پيروانم، همگان را در عين بصيرت به سوى خدا مى‌خوانيم. منزه است خدا و من از مشركان نيستم.'),
(1705, 12, 109, 'و ما پيش از تو به رسالت نفرستاديم مگر مردانى را از مردم قريه‌ها كه به آنها وحى مى‌كرديم. آيا در روى زمين نمى‌گردند تا بنگرند كه پايان كار پيشينيانشان چه بوده است؟ و سراى آخرت پرهيزگاران را بهتر است، چرا نمى‌انديشيد؟'),
(1706, 12, 110, 'چون پيامبران نوميد شدند و چنان دانستند كه آنها را تكذيب مى‌كنند، ياريشان كرديم و هر كه را كه خواستيم نجات داديم و عذاب ما از مردم گنهكار بازگردانيده نشود.'),
(1707, 12, 111, 'در داستانهايشان خردمندان را عبرتى است. اين داستانى برساخته نيست، بلكه تصديق سخن پيشينيان و تفصيل هر چيزى است و براى آنها كه ايمان آورده‌اند هدايت است و رحمت.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، ميم، را. اينها آيات اين كتاب است و آنچه از پروردگارت بر تو نازل شده است حق است ولى بيشتر مردم ايمان نمى‌آورند.'),
(1709, 13, 2, 'اللّه، همان خداوندى است كه آسمانها را بى‌هيچ ستونى كه آن را ببينيد برافراشت. سپس به عرش پرداخت و آفتاب و ماه را كه هر يك تا زمانى معين در سيرند رام كرد. كارها را مى‌گرداند و آيات را بيان مى‌كند، باشد كه به ديدار پروردگارتان يقين كنيد.'),
(1710, 13, 3, 'اوست كه زمين را بگسترد، و در آن كوه‌ها و رودها قرار داد و از هر ميوه جفت جفت پديد آورد و شب را در روز مى‌پوشاند. در اينها عبرتهاست براى مردمى كه مى‌انديشند.'),
(1711, 13, 4, 'و بر روى زمين قطعه‌هايى است در كنار يكديگر و باغهاى انگور و كشتزارها و نخلهايى كه دو تنه از يك ريشه رسته است يا يك تنه از يك ريشه و همه به يك آب سيراب مى‌شوند و در ثمره، بعضى را بر بعض ديگر برترى نهاده‌ايم. هرآينه در اينها براى خردمندان عبرتهاست.'),
(1712, 13, 5, 'اگر تو در شگفت مى‌آيى، شگفت سخن ايشان است كه آيا آن هنگام كه خاك شويم از نو ما را بيافرينند؟ اينان به پروردگارشان ايمان ندارند، بر گردنهاشان غُلها باشد و اهل جهنمند و در آن جاودانه‌اند.'),
(1713, 13, 6, 'به شتاب از تو پيش از خير و آمرزش، عذاب مى‌طلبند. پيش از آنها عذابهايى بوده است و پروردگار تو مردم را با آنكه ستم كرده‌اند مى‌آمرزد و نيز پروردگار تو به سختى عقوبت مى‌كند.'),
(1714, 13, 7, 'كافران مى‌گويند: چرا از جانب پروردگارش معجزه‌اى بر او نازل نمى‌شود؟ جز اين نيست كه تو بيم‌دهنده‌اى هستى و هر قومى را رهبرى است.'),
(1715, 13, 8, 'خدا مى‌داند كه هر ماده‌اى چه در رحم دارد و آنچه رحمها كاهد و آنچه رحمها افزايد. و هر چيز را در نزد او مقدارى معين است.'),
(1716, 13, 9, 'داناى نهان و آشكار، آن خداى بزرگ متعال.'),
(1717, 13, 10, 'براى او يكسان است از شما هر كه سخن به راز گويد يا به آشكارا. و آنكه در پرده شب پنهان مى‌شود و آنكه در روز به آشكارا مى‌رود.'),
(1718, 13, 11, 'آدمى را فرشتگانى است كه پياپى به امر خدا از روبه‌رو و پشت سرش مى‌آيند و نگهبانيش مى‌كنند. خدا چيزى را كه از آن مردمى است دگرگون نكند تا آن مردم خود دگرگون شوند. چون خدا براى مردمى بدى خواهد، هيچ چيز مانع او نتواند شد و ايشان را جز خدا هيچ كارسازى نيست.'),
(1719, 13, 12, 'اوست كه برق را گاه براى ترسانيدن و گاه براى اميدبخشيدن به شما مى‌نماياند و ابرهاى گرانبار را پديد مى‌آورد.'),
(1720, 13, 13, 'رعد به ستايش او و فرشتگان از بيم او تسبيح مى‌كنند. و صاعقه‌ها را مى‌فرستد و هر كه را بخواهد بدان آسيب مى‌رساند. باز هم درباره خدا مجادله مى‌كنند. و او به سختى عقوبت مى‌كند.'),
(1721, 13, 14, 'خاص اوست خواندن راستين. آنان كه جز او را مى‌خوانند هيچ پاسخشان نمى‌گويند. همانند كسى كه دو دست به سوى آب برد تا آب به دهان رساند و نتواند كه آب به دهان رساند. و دعاى كافران جز به گمراهى نيست.'),
(1722, 13, 15, 'خواه و ناخواه براى خداوند سجده مى‌كنند همه آنها كه در آسمانها و زمينند و سايه‌هايشان نيز بامدادان و شامگاهان به سجده مى‌آيند.'),
(1723, 13, 16, 'بگو: كيست پروردگار آسمانها و زمين؟ بگو: اللّه. بگو: آيا سواى او خدايانى برگزيده‌ايد كه قادر به سود و زيان خويش نيستند؟ بگو: آيا نابينا و بينا برابرند؟ يا تاريكى و روشنى يكسانند؟ يا شريكانى كه براى خدا قائل شده‌اند چيزهايى آفريده‌اند، همانند آنچه خدا آفريده است و آنان درباره آفرينش به اشتباه افتاده‌اند؟ بگو: اللّه آفريننده هر چيزى است و او يگانه و قهار است.'),
(1724, 13, 17, 'از آسمان آب فرستاد و هر رودخانه به اندازه خويش جارى شد، و آب روان كف بر سر آورد. و از آنچه بر آتش مى‌گدازند تا زيور و متاعى سازند نيز كفى بر سر آيد. خدا براى حق و باطل چنين مثَل زند. اما كف به كنارى افتد و نابود شود و آنچه براى مردم سودمند است در زمين پايدار بماند. خدا اينچنين مثَل مى‌زند.'),
(1725, 13, 18, 'براى آنان كه دعوت پروردگارشان را پذيرفتند پاداش نيكويى است. و كسانى كه دعوت او را نپذيرفته‌اند، اگر هر آنچه را كه بر روى زمين است و همانند آن را داشته باشند، آن را فدا دهند. آنان به سختى بازخواست خواهند شد و مكانشان جهنّم است و بد جايگاهى است.'),
(1726, 13, 19, 'آيا كسى كه مى‌داند آنچه از جانب پروردگارت بر تو نازل شده حق است، همانند كسى است كه نابيناست؟ تنها خردمندان پند مى‌گيرند:'),
(1727, 13, 20, 'كسانى كه به عهد خدا وفا مى‌كنند و خود پيمان نمى‌شكنند.'),
(1728, 13, 21, 'آنان كه آنچه را خدا به پيوستن آن فرمان داده پيوند مى‌دهند و از پروردگارشان مى‌ترسند و از سختىِ بازخواست خداوند بيمناكند.'),
(1729, 13, 22, 'آنان كه به طلب ثواب پروردگار خويش صبر پيشه كردند، و نماز گزاردند و در نهان و آشكار از آنچه به آنها روزى داده‌ايم انفاق كردند و بدى را به نيكى دفع مى‌كنند. سراى آخرت خاص آنان است.'),
(1730, 13, 23, 'بهشتهاى جاويدان؛ آنها و هر كه نيكوكار بوده است، از پدران و همسران و فرزندانشان، بدان داخل شوند و فرشتگان از هر در به نزدشان آيند.'),
(1731, 13, 24, 'سلام بر شما به خاطر آن همه شكيبايى كه ورزيده‌ايد. سراى آخرت چه سرايى نيكوست.'),
(1732, 13, 25, 'و آنان كه پيمان خدا را پس از استواركردنش مى‌شكنند و آنچه را كه خدا به پيوستن آن فرمان داده مى‌گسلند و در زمين فساد مى‌كنند، لعنت بر آنهاست و بديهاى آن جهان نصيبشان.'),
(1733, 13, 26, 'خدا هر كه را خواهد روزى بسيار دهد يا روزى اندك. و مردم به زندگى دنيا خشنودند، حال آنكه زندگى دنيا در برابر زندگى آخرت جز اندك متاعى نيست.'),
(1734, 13, 27, 'كافران مى‌گويند: چرا از جانب پروردگارش معجزه‌اى بر او نازل نمى‌شود؟ بگو: خدا هر كه را بخواهد گمراه مى‌كند. و هر كه را به درگاه او روى كند، هدايت مى‌كند:'),
(1735, 13, 28, 'آنان كه ايمان آورده‌اند و دلهايشان به ياد خدا آرامش مى‌يابد. آگاه باشيد كه دلها به ياد خدا آرامش مى‌يابد.'),
(1736, 13, 29, 'زندگى خوش و بازگشتنگاه نيكو از آن كسانى است كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند.'),
(1737, 13, 30, 'همچنين تو را به ميان امتى كه پيش از آنها امتهاى ديگر بوده‌اند، به رسالت فرستاده‌ايم تا آنچه را كه بر تو وحى كرده‌ايم بر ايشان تلاوت كنى و آنها به رحمان كافر مى‌شوند. بگو: او پروردگار من است. خدايى جز او نيست. بر او توكل كرده‌ام و توبه من به درگاه اوست.'),
(1738, 13, 31, 'اگر قرآنى توانست بود كه كوه‌ها بدان به جنبش آيند يا زمين پاره‌پاره شود يا مردگان را به سخن آرد، جز اين قرآن نمى‌بود، كه همه كارها از آن خداست. آيا مؤمنان هنوز ندانسته‌اند كه اگر خدا مى‌خواست همه مردم را هدايت مى‌كرد؟ و كافران را پيوسته به سبب اعمالشان حادثه‌اى رسد يا آن حادثه در نزديكى خانه‌هايشان فرود آيد تا آنگاه كه وعده خدا فراز آيد، زيرا خدا خلف وعده نمى‌كند.'),
(1739, 13, 32, 'پيامبرانى هم كه پيش از تو بودند به استهزا گرفته شدند. من به كافران مهلت دادم، آنگاه همه را فرو گرفتم و آن عقوبت من چه سخت بود.'),
(1740, 13, 33, 'آيا آن كه بر همه مردم و اعمالشان ناظر است، همانند بتان است؟ براى خدا شريكانى قائل شدند. بگو: آنها را نام ببريد. شايد مى‌خواهيد او را از چيزى در روى زمين آگاه كنيد كه نمى‌داند، يا سخنان بيهوده مى‌گويند؟ در نظر كافران مكرشان آراسته آمد و از راه راست به در شدند. و هر كه را خدا گمراه كند هيچ راهنمايى ندارد.'),
(1741, 13, 34, 'در اين زندگى دنيا به عذابى گرفتار آيند و عذاب آخرت سخت‌تراست و آنها را هيچ نگهدارنده‌اى از عذاب خدا نيست.'),
(1742, 13, 35, 'صفت بهشتى كه به پرهيزگاران وعده شده: آب از زير درختانش روان است و ميوه‌ها و سايه‌اش هميشگى است. اين سرانجام كسانى است كه پرهيزگارند و سرانجام كافران آتش است.'),
(1743, 13, 36, 'اهل كتاب از آنچه بر تو نازل شده شادمانند. و از آن جماعات كسانى هستند كه پاره‌اى از آن را انكار مى‌كنند. بگو: من مأمور شده‌ام كه خداى يكتا را بپرستم و به او شرك نياورم. به سوى او دعوت مى‌كنم و بازگشت من به سوى اوست.'),
(1744, 13, 37, 'همچنين قرآن را به زبان عربى نازل كرديم. اگر پس از دانشى كه به تو رسيده، از پى خواهشهاى آنان بروى، در برابر خدا كارساز و نگهدارنده‌اى نخواهى داشت.'),
(1745, 13, 38, 'به تحقيق پيش از تو پيامبرانى فرستاده‌ايم و برايشان همسران و فرزندان قرار داده‌ايم. و هيچ پيامبرى را حق آن نبود كه جز به فرمان خدا آيتى بياورد و هر امرى را زمانى مكتوب است.'),
(1746, 13, 39, 'خدا هر چه را بخواهد محو يا اثبات مى‌كند و ام‌الكتاب نزد اوست.'),
(1747, 13, 40, 'اگر پاره‌اى از آنچه را كه به آنها وعده كرده‌ايم به تو بنمايانيم، يا تو را پيش از وقت بميرانيم، در هر حال آنچه بر عهده توست تبليغ است و آنچه بر عهده ماست حساب‌كشيدن.'),
(1748, 13, 41, 'آيا هنوز ندانسته‌اند كه ما از اطراف اين سرزمين مى‌كاهيم، و خدا حكم مى‌كند و هيچ چيز حكم او را فسخ نمى‌كند و او سريعاً به حساب همه مى‌رسد؟'),
(1749, 13, 42, 'كسانى كه پيش از اينان بودند مكرها كردند، ولى همه مكرها نزد خداوند است. مى‌داند كه هر كسى چه مى‌كند. و كافران به زودى خواهند دانست كه سراى آخرت از آن كيست.'),
(1750, 13, 43, 'كافران مى‌گويند كه تو پيامبر نيستى. بگو: خدا و هر كس كه از كتاب آگاهى داشته باشد، به شهادت ميان من و شما كافى است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف، لام، را. كتابى است كه بر تو نازل كرديم تا مردم را به فرمان پروردگارشان از تاريكى به روشنايى ببرى و به راه خداى پيروزمند ستودنى راه نمايى-'),
(1752, 14, 2, 'خدايى كه هر چه در آسمانها و زمين است از آن اوست. پس واى بر كافران از عذابى سخت.'),
(1753, 14, 3, 'آنان كه زندگى دنيا را از آخرت دوست‌تردارند و ديگران را از راه خدا باز مى‌دارند و آن را منحرف مى‌خواهند، سخت در گمراهى هستند.'),
(1754, 14, 4, 'هيچ پيامبرى را جز به زبان مردمش نفرستاديم، تا بتواند پيام خدا را برايشان بيان كند. پس خدا هر كه را بخواهد گمراه مى‌كند و هر كه را بخواهد هدايت مى‌كند و اوست پيروزمند و حكيم.'),
(1755, 14, 5, 'موسى را با آيات خود فرستاديم كه: قوم خود را از تاريكى به روشنايى بيرون آر، و روزهاى خدا را به يادشان بياور. و در اين براى هر شكيباى شكرگزار، عبرتهايى است.'),
(1756, 14, 6, 'و موسى به قوم خود گفت: نعمتى را كه خدا بر شما ارزانى داشته است به ياد آوريد، آنگاه كه شما را از فرعونيان رهانيد. به سختى آزارتان مى‌كردند و پسرانتان را مى‌كشتند و زنانتان را زنده مى‌گذاشتند و در اين از جانب پروردگارتان براى شما آزمايشى بزرگ بود.'),
(1757, 14, 7, 'و پروردگارتان اعلام كرد كه اگر مرا سپاس گوييد، بر نعمت شما مى‌افزايم و اگر كفران كنيد، بدانيد كه عذاب من سخت است.'),
(1758, 14, 8, 'و موسى گفت: اگر شما و همه مردم روى زمين كافر شويد، خدا بى‌نياز و درخور ستايش است.'),
(1759, 14, 9, 'آيا خبر كسانى كه پيش از شما بوده‌اند، چون قوم نوح و عاد و ثمود و نيز كسانى كه بعد از آنها بوده‌اند، به شما نرسيده است؟ جز خدا كسى را از آنان آگاهى نيست. پيامبرانشان همراه با دلايل روشن آمدند و آنها دست بر دهان بردند و گفتند: ما به آنچه بدان مأمور شده‌ايد ايمان نمى‌آوريم و در چيزى كه ما را بدان مى‌خوانيد سخت در ترديد هستيم.'),
(1760, 14, 10, 'پيامبرانشان گفتند: آيا در خدا -آن آفريننده آسمانها و زمين- شكى هست؟ شما را فرامى‌خواند تا گناهانتان را بيامرزد و تا مدتى معين شما را زنده گذارد. گفتند: شما جز مردمانى همانند ما نيستيد. مى‌خواهيد ما را از آنچه پدرانمان مى‌پرستيدند باز داريد. براى ما دليلى روشن بياوريد.'),
(1761, 14, 11, 'پيامبرانشان گفتند: ما جز مردمانى همانند شما نيستيم ولى خدا بر هر يك از بندگانش كه بخواهد منت نهد. ما را نسزد كه براى شما دليلى، جز به فرمان خدا، بياوريم و مؤمنان بر خدا توكّل كنند.'),
(1762, 14, 12, 'چرا بر خدا توكل نكنيم، و حال آنكه او راه ما را به ما بنمود؟ و ما بر آزارى كه به ما مى‌رسانيد صبر خواهيم كرد و توكل‌كنندگان بر خدا توكل كنند.'),
(1763, 14, 13, 'كافران به پيامبرانشان گفتند: يا شما را از سرزمين خود مى‌رانيم يا به كيش ما بازگرديد. پس پروردگارشان به پيامبران وحى كرد كه: ستمكاران را هلاك خواهيم كرد.'),
(1764, 14, 14, 'و شما را پس از ايشان در آن سرزمين جاى خواهيم داد. اين از آن كسى است كه از ايستادن در پيشگاه من و از وعده عذاب من بترسد.'),
(1765, 14, 15, 'پيروزى خواستند و هر جبار كينه‌توزى نوميد شد.'),
(1766, 14, 16, 'پشت سرش جهنم است تا در آنجا از آب چرك و خونش بخورانند.'),
(1767, 14, 17, 'جرعه‌جرعه آن را مى‌نوشد و هيچ گواراى او نيست و مرگ از هر سو بر او مى‌تازد اما نمى‌ميرد، كه عذابى سخت در انتظار اوست.'),
(1768, 14, 18, 'مثَل اعمال كسانى كه به خدا كافر شده‌اند چون خاكسترى است كه در روزى طوفانى بادى سخت بر آن بوزد. توان نگه‌داشتن آنچه را كه به دست آورده‌اند ندارند. اين است گمراهى بى‌انتها.'),
(1769, 14, 19, 'آيا نديده‌اى كه خدا آسمانها و زمين را به حق آفريده است؟ اگر بخواهد شما را مى‌برد و مخلوقى تازه مى‌آورد.'),
(1770, 14, 20, 'و اين بر خدا دشوار نيست.'),
(1771, 14, 21, 'همه در پيشگاه خدا حاضر آيند. ناتوانان به آنان كه گردنكشى مى‌كردند گويند: ما پيرو شما بوديم. آيا اكنون مى‌توانيد ما را به كار آييد و اندكى از عذاب خدا را از ما دفع كنيد؟ گويند: اگر خدا ما را هدايت كرده بود، ما نيز شما را هدايت مى‌كرديم. حال ما را راه خلاصى نيست، براى ما يكسان است چه بيتابى كنيم، چه شكيبايى ورزيم.'),
(1772, 14, 22, 'چون كار به پايان آيد، شيطان گويد: خدا به شما وعده داد و وعده او درست بود و من نيز به شما وعده دادم ولى وعده خود خلاف كردم. و برايتان هيچ دليل و برهانى نياوردم جز آنكه دعوتتان كردم شما نيز دعوت من اجابت كرديد؛ پس مرا ملامت مكنيد، خود را ملامت كنيد. نه من فريادرس شمايم، نه شما فريادرس من. از اينكه مرا پيش از اين شريك خدا كرده بوديد بيزارم. زيرا براى ستمكاران عذابى است دردآور.'),
(1773, 14, 23, 'كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، به بهشتهايى كه نهرها در آن جارى است درآورند؛ به فرمان پروردگارشان در آنجا جاودانه بمانند و به سلام، يكديگر را تحيت گويند.'),
(1774, 14, 24, 'آيا نديده‌اى كه خدا چگونه مثل زد؟ سخن پاك چون درختى پاك است كه ريشه‌اش در زمين استوار و شاخه‌هايش در آسمان است.'),
(1775, 14, 25, 'به فرمان خدا هر زمان ميوه خود را مى‌دهد. خدا براى مردم مثلها مى‌آورد، باشد كه پند گيرند.'),
(1776, 14, 26, 'و مثل سخن ناپاك چون درختى ناپاك است، كه ريشه در زمين ندارد و برپا نتواند ماند.'),
(1777, 14, 27, 'خدا مؤمنان را به سبب اعتقاد استوارشان در دنيا و آخرت پايدار مى‌دارد. و ظالمان را گمراه مى‌سازد و هر چه خواهد همان مى‌كند.'),
(1778, 14, 28, 'آيا نديده‌اى آن كسان را كه نعمت خدا را به كفر بدل ساختند و مردم خود را به ديار هلاك بردند؟'),
(1779, 14, 29, 'به جهنم، آن قرارگاه بد، داخل مى‌شوند.'),
(1780, 14, 30, 'براى خدا همتايانى قرار دادند تا مردم را از راه او گمراه سازند. بگو: اكنون بهره‌مند گرديد كه سرانجامتان آتش است.'),
(1781, 14, 31, 'به بندگان من كه ايمان آورده‌اند بگو تا نماز بگزارند و از آنچه روزيشان داده‌ايم نهان و آشكارا انفاق كنند، پيش از آنكه روزى فرا رسد كه در آن نه خريد و فروختى باشد و نه هيچ دوستيى به كار آيد.'),
(1782, 14, 32, 'خداست كه آسمانها و زمين را آفريد و از آسمان باران نازل كرد و بدان باران براى روزى شما ميوه‌ها رويانيد و كشتيها را رام شما كرد تا به فرمان او در دريا روان شوند و رودها را مسخر شما ساخت.'),
(1783, 14, 33, 'و آفتاب و ماه را كه همواره در حركتند رام شما كرد و شب و روز را مسخر شما گردانيد.'),
(1784, 14, 34, 'و هر چه از او خواسته‌ايد به شما ارزانى داشته است و اگر خواهيد كه نعمتهايش را شمار كنيد، نتوانيد، كه آدمى ستمكار و كافر نعمت است.'),
(1785, 14, 35, 'و ابراهيم گفت: اى پروردگار من، اين سرزمين را ايمن گردان و مرا و فرزندانم را از پرستش بتان دور بدار.'),
(1786, 14, 36, 'اى پروردگار من، اينها بسيارى از مردم را گمراه كرده‌اند، پس هر كس كه از من پيروى كند از من است و هر كس فرمان من نبرد، تو آمرزنده و مهربانى.'),
(1787, 14, 37, 'اى پروردگار ما، برخى از فرزندانم را به واديى بى‌هيچ كشته‌اى، نزديك خانه گرامى تو جاى دادم، اى پروردگار ما، تا نماز بگزارند. دلهاى مردمان چنان كن كه هواى آنها كند و از هر ثمره‌اى روزيشان ده، باشد كه سپاس گزارند.'),
(1788, 14, 38, 'اى پروردگار ما، هر چه را پنهان مى‌داريم يا آشكار مى‌سازيم تو بدان آگاهى. و بر خدا هيچ چيز در زمين و آسمان پوشيده نيست.'),
(1789, 14, 39, 'سپاس خدايى را كه در اين پيرى اسماعيل و اسحاق را به من عطا كرد. پروردگار من دعاها را مى‌شنود.'),
(1790, 14, 40, 'اى پروردگار من، مرا و فرزندان مرا برپاى‌دارندگان نماز گردان. اى پروردگار ما، دعاى مرا بپذير.'),
(1791, 14, 41, 'اى پروردگار ما، مرا و پدر و مادرم و همه مؤمنان را در روز شمار بيامرز.'),
(1792, 14, 42, 'و مپندار كه خدا از كردار ستمكاران غافل است. عذابشان را تا آن روز كه چشمها در آن خيره مى‌ماند، به تاخير مى‌افكند.'),
(1793, 14, 43, 'مى‌شتابند، سرها را بالا گرفته‌اند. چشم بر هم نمى‌زنند، و دلهايشان خالى از خرد است.'),
(1794, 14, 44, 'مردم را از آن روز كه عذاب فرا مى‌رسد بترسان. ستمكاران مى‌گويند: بار خدايا، ما را اندكى مهلت ده تا دعوت تو را اجابت كنيم و از پيامبران تو پيروى كنيم. آيا شما پيش از اين سوگند نمى‌خورديد كه هرگز زوال نمى‌يابيد؟'),
(1795, 14, 45, 'در خانه‌هاى كسانى كه خود بر خويشتن ستم مى‌كردند، جاى گرفتيد و دانستيد با آنان چگونه رفتار كرديم، و برايتان مثلها زديم.'),
(1796, 14, 46, 'آنان نيرنگهاى خود نمودند و خدا از نيرنگهايشان آگاه بود. هر چند كه از نيرنگهايشان كوه از پاى درمى‌آمد.'),
(1797, 14, 47, 'مپندار كه خدا وعده‌اى را كه به پيامبرانش داده است خلاف مى‌كند. خداوند پيروزمند و انتقام‌گيرنده است.'),
(1798, 14, 48, 'آن روز كه زمين به زمينى جز اين بدل شود و آسمانها به آسمانى ديگر، و همه در پيشگاه خداى واحد قهار حاضر آيند.'),
(1799, 14, 49, 'مجرمان را در آن روز به غلهايى كه دست را به گردن بندد، بسته بينى.'),
(1800, 14, 50, 'جامه‌هاشان از قطران است و آتش صورتهاشان را فروپوشيده است.'),
(1801, 14, 51, 'تا خدا هر كس را برابر عملش كيفر دهد، هرآينه خدا سريع الحساب است.'),
(1802, 14, 52, 'اين پيامى است براى مردم، تا بدان بيمناك شوند و بدانند كه اوست خداى يكتا، و تا خردمندان پند گيرند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف، لام، را. اين است آيات كتاب و قرآن روشنگر.'),
(1804, 15, 2, 'بسا كه كافران آرزو كنند كه اى‌كاش مسلمان مى‌بودند.'),
(1805, 15, 3, 'واگذارشان تا بخورند و بهره‌ور شوند و آرزو به خود مشغولشان دارد، زودا كه خواهند دانست.'),
(1806, 15, 4, 'هيچ قريه‌اى را هلاك نكرديم، مگر آنكه زمانى معلوم داشت.'),
(1807, 15, 5, 'هيچ امتى از اجل خويش نه پيش مى‌افتد و نه پس مى‌ماند.'),
(1808, 15, 6, 'و گفتند: اى مردى كه قرآن بر تو نازل شده، حقا كه تو ديوانه‌اى.'),
(1809, 15, 7, 'اگر راست مى‌گويى، چرا فرشتگان را براى ما نمى‌آورى؟'),
(1810, 15, 8, 'ما فرشتگان را جز به حق نازل نمى‌كنيم و در آن هنگام ديگر مهلتشان ندهند.'),
(1811, 15, 9, 'ما قرآن را خود نازل كرده‌ايم و خود نگهبانش هستيم.'),
(1812, 15, 10, 'و ما رسولان خود را پيش از تو به ميان اقوام پيشين فرستاده‌ايم.'),
(1813, 15, 11, 'هيچ پيامبرى بر آنها مبعوث نشد، جز آنكه مسخره‌اش كردند.'),
(1814, 15, 12, 'راهش را در دل مجرمان اينچنين مى‌گشاييم.'),
(1815, 15, 13, 'به آن ايمان نمى‌آورند و سنت پيشينيان چنين بوده است.'),
(1816, 15, 14, 'اگر بر ايشان از آسمان درى بگشاييم كه از آن بالا روند،'),
(1817, 15, 15, 'گويند: چشمان ما را جادو كرده‌اند، بلكه ما مردمى جادوزده هستيم.'),
(1818, 15, 16, 'و هرآينه در آسمان برجهايى آفريديم و براى بينندگانشان بياراستيم.'),
(1819, 15, 17, 'و از هر شيطان رجيمى حفظشان كرديم.'),
(1820, 15, 18, 'مگر آنكه دزدانه گوش مى‌داد و شهابى روشن تعقيبش كرد.'),
(1821, 15, 19, 'و زمين را گسترديم و در آن كوه‌هاى عظيم افكنديم. و از هر چيز به شيوه‌اى سنجيده در آن رويانيديم.'),
(1822, 15, 20, 'و معيشت شما و كسانى را كه شما روزى‌دهشان نيستيد، در آنجا قرار داديم.'),
(1823, 15, 21, 'هر چه هست خزاين آن نزد ماست و ما جز به اندازه‌اى معين آن را فرو نمى‌فرستيم.'),
(1824, 15, 22, 'و بادهاى آبستن‌كننده را فرستاديم، و از آسمان آبى نازل كرديم و شما را بدان سيراب ساختيم و شما را نرسد كه خازنان آن باشيد.'),
(1825, 15, 23, 'هرآينه ما هستيم كه زنده مى‌كنيم و مى‌ميرانيم و بعد از همه باقى مى‌مانيم.'),
(1826, 15, 24, 'و مى‌دانيم چه كسانى از شما از اين پيش رفته‌اند و چه كسانى واپس مانده‌اند.'),
(1827, 15, 25, 'و پروردگار تو همه را محشور مى‌گرداند، زيرا اوست كه حكيم و داناست.'),
(1828, 15, 26, 'ما آدمى را از گل خشك، از لجن بويناك آفريديم.'),
(1829, 15, 27, 'و جن را پيش از آن از آتش سوزنده بى‌دود آفريده بوديم.'),
(1830, 15, 28, 'و پروردگارت به فرشتگان گفت: مى‌خواهم بشرى از گل خشك، از لجن بويناك بيافرينم.'),
(1831, 15, 29, 'چون آفرينشش را به پايان بردم و از روح خود در آن دميدم، در برابر او به سجده بيفتيد.'),
(1832, 15, 30, 'فرشتگان همگى سجده كردند،'),
(1833, 15, 31, 'مگر ابليس كه سرباز زد كه با سجده‌كنندگان باشد.'),
(1834, 15, 32, 'گفت: اى ابليس، چرا تو از سجده‌كنندگان نبودى؟'),
(1835, 15, 33, 'گفت: من براى بشرى كه از گل خشك، از لجن بويناك آفريده‌اى سجده نمى‌كنم.'),
(1836, 15, 34, 'گفت: از آنجا بيرون شو كه مطرود هستى.'),
(1837, 15, 35, 'تا روز قيامت بر تو لعنت است.'),
(1838, 15, 36, 'گفت: اى پروردگار من، مرا تا روزى كه دوباره زنده مى‌شوند مهلت ده.'),
(1839, 15, 37, 'گفت: تو در شمار مهلت‌يافتگانى.'),
(1840, 15, 38, 'تا آن روزى كه وقتش معلوم است.'),
(1841, 15, 39, 'گفت: اى پروردگار من، چون مرا نوميد كردى، در روى زمين بديها را در نظرشان بيارايم و همگان را گمراه كنم،'),
(1842, 15, 40, 'مگر آنها كه بندگان با اخلاص تو باشند.'),
(1843, 15, 41, 'گفت: راه اخلاص راه راستى است كه به من مى‌رسد.'),
(1844, 15, 42, 'تو را بر بندگان من تسلطى نيست، مگر بر آن گمراهانى كه تو را پيروى كنند.'),
(1845, 15, 43, 'و جهنم ميعادگاه همه است.'),
(1846, 15, 44, 'هفت در دارد و براى هر در گروهى از آنان معين شده‌اند.'),
(1847, 15, 45, 'پرهيزگاران در بهشتها، كنار چشمه‌سارانند.'),
(1848, 15, 46, 'به سلامت و ايمنى داخل شويد.'),
(1849, 15, 47, 'هر كينه‌اى را از دلشان بركنده‌ايم، همه برادرند، بر تختها روبه‌روى همند.'),
(1850, 15, 48, 'هيچ رنجى به آنها نمى‌رسد و از آنجا بيرونشان نرانند.'),
(1851, 15, 49, 'به بندگانم خبر ده كه من آمرزنده و مهربانم،'),
(1852, 15, 50, 'و عذاب من عذابى دردآور است.'),
(1853, 15, 51, 'و از مهمانان ابراهيم خبر دارشان كن.'),
(1854, 15, 52, 'آنگاه كه بر او داخل شدند و گفتند: سلام. ابراهيم گفت: ما از شما مى‌ترسيم.'),
(1855, 15, 53, 'گفتند: مترس، ما تو را به پسرى دانا بشارت مى‌دهيم.'),
(1856, 15, 54, 'گفت: آيا مرا بشارت مى‌دهيد با آنكه پير شده‌ام؟ به چه چيز بشارتم مى‌دهيد؟'),
(1857, 15, 55, 'گفتند: به حق بشارتت داديم، از نوميدان مباش.'),
(1858, 15, 56, 'گفت: جز گمراهان چه كسى از رحمت پروردگارش نوميد مى‌شود؟'),
(1859, 15, 57, 'گفت: اى رسولان، كار شما چيست؟'),
(1860, 15, 58, 'گفتند: ما بر سر مردمى گنهكار فرستاده شده‌ايم.'),
(1861, 15, 59, 'مگر خاندان لوط، كه همه را نجات مى‌دهيم،'),
(1862, 15, 60, 'جز زنش را كه مقرر كرده‌ايم كه از باقى‌ماندگان باشد.'),
(1863, 15, 61, 'چون رسولان نزد خاندان لوط آمدند،'),
(1864, 15, 62, 'لوط گفت: شما بيگانه‌ايد.'),
(1865, 15, 63, 'گفتند: نه، چيزى را كه در آن شك مى‌كردند آورده‌ايم.'),
(1866, 15, 64, 'ما تو را خبر راست آورده‌ايم و ما راستگويانيم.'),
(1867, 15, 65, 'چون پاسى از شب بگذرد، خاندان خود را بيرون ببر. و خود از پى آنها رو و نبايد هيچ يك از شما به عقب بنگرد. به هر جا كه فرمانتان داده‌اند برويد.'),
(1868, 15, 66, 'و براى او حادثه را حكايت كرديم كه چون صبح فرارسد ريشه آنها بركنده شود.'),
(1869, 15, 67, 'اهل شهر شادى‌كنان آمدند.'),
(1870, 15, 68, 'گفت: اينان مهمانان منند، مرا رسوا مكنيد.'),
(1871, 15, 69, 'از خداوند بترسيد و مرا شرمسار مسازيد.'),
(1872, 15, 70, 'گفتند: مگر تو را از مردم منع نكرده بوديم؟'),
(1873, 15, 71, 'گفت: اگر قصدى داريد، اينك دختران من هستند.'),
(1874, 15, 72, 'به جان تو سوگند كه آنها در مستى خويش سرگشته بودند.'),
(1875, 15, 73, 'چون صبح طالع شد آنان را صيحه فروگرفت.'),
(1876, 15, 74, 'شهر را زيروزبر كرديم و بارانى از سجيل بر آنان بارانيديم.'),
(1877, 15, 75, 'در اين عبرتهاست براى پژوهندگان.'),
(1878, 15, 76, 'و آن شهر اكنون بر سر راه كاروانيان است.'),
(1879, 15, 77, 'و مؤمنان را در آن عبرتى است.'),
(1880, 15, 78, 'مردم ايكه نيز ستمكار بودند.'),
(1881, 15, 79, 'از آنان انتقام گرفتيم و شهرهاى آن دو قوم آشكارا بر سر راهند.'),
(1882, 15, 80, 'مردم حجر نيز پيامبران را به دروغ نسبت دادند.'),
(1883, 15, 81, 'آيات خويش را بر آنان رسانيديم ولى از آن اعراض مى‌كردند.'),
(1884, 15, 82, 'خانه‌هاى خود را تا ايمن باشند در كوه‌ها مى‌كندند.'),
(1885, 15, 83, 'صبحگاهان آنان را صيحه فروگرفت.'),
(1886, 15, 84, 'كردارشان از آنان دفع بلا نكرد.'),
(1887, 15, 85, 'آسمانها و زمين و آنچه را كه ميان آنهاست جز به حق نيافريده‌ايم. و بى‌ترديد قيامت فرا مى‌رسد. پس گذشت كن، گذشتى نيكو.'),
(1888, 15, 86, 'هرآينه پروردگار تو آفريننده‌اى داناست.'),
(1889, 15, 87, 'ما سبع‌المثانى و قرآن بزرگ را به تو داديم.'),
(1890, 15, 88, 'اگر بعضى از مردان و زنانشان را به چيزى بهره‌ور ساخته‌ايم تو بدان نگاه مكن. و غم آنان را مخور. و در برابر مؤمنان فروتن باش.'),
(1891, 15, 89, 'و بگو: من بيم‌دهنده‌اى روشنگرم.'),
(1892, 15, 90, 'همانند عذابى كه بر تقسيم‌كنندگان نازل كرديم:'),
(1893, 15, 91, 'آنان كه قرآن را به اقسام تقسيم كرده بودند.'),
(1894, 15, 92, 'به پروردگارت سوگند كه همه را بازخواست كنيم،'),
(1895, 15, 93, 'به خاطر كارهايى كه مى‌كرده‌اند.'),
(1896, 15, 94, 'به هر چه مأمور شده‌اى صريح و بلند بگو و از مشركان رويگردان باش.'),
(1897, 15, 95, 'ما مسخره‌كنندگان را از تو باز مى‌داريم؛'),
(1898, 15, 96, 'آنان كه با الله خداى ديگر قائل مى‌شوند. پس به زودى خواهند دانست.'),
(1899, 15, 97, 'و مى‌دانيم كه تو از گفتارشان دلتنگ مى‌شوى.'),
(1900, 15, 98, 'به ستايش پروردگارت تسبيح كن و از سجده‌كنندگان باش.'),
(1901, 15, 99, 'و پروردگارت را بپرست، تا لحظه مرگت فرا رسد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'فرمان خداوند در رسيد، به شتابش مخواهيد. او منزه است و از هر چه شريك او مى‌سازند برتر.'),
(1903, 16, 2, 'فرشتگان را همراه وحى -كه فرمان اوست- بر هر يك از بندگانش كه بخواهد فرو مى‌فرستد تا بيم دهند كه: جز من خدايى نيست، پس از من بترسيد.'),
(1904, 16, 3, 'آسمانها و زمين را به حق بيافريد، از هر چه با او شريك مى‌سازند، برتر است.'),
(1905, 16, 4, 'آدمى را از نطفه بيافريد. و اكنون اوست كه به آشكارا دشمنى مى‌ورزد.'),
(1906, 16, 5, 'چهارپايان را برايتان بيافريد، شما را از آنها حرارت و ديگر سودهاست. و از آنها مى‌خوريد.'),
(1907, 16, 6, 'و چون شب‌هنگام باز مى‌گردانيد و بامدادان بيرون مى‌فرستيد، نشان تجمل شمايند؛'),
(1908, 16, 7, 'بارهايتان را به شهرهايى كه جز به رنج تن بدانها نتوانيد رسيد، حمل مى‌كنند، زيرا پروردگارتان رئوف و مهربان است.'),
(1909, 16, 8, 'و اسبان و استران و خران را براى آن آفريده است كه سوارشان شويد و نيز زينت شما باشند. و خدا چيزهايى آفريده كه شما نمى‌دانيد.'),
(1910, 16, 9, 'بر خداست كه راه راست را بنمايد. از ميان راه‌ها نيز راهى است منحرف. اگر خدا مى‌خواست، همه شما را هدايت مى‌كرد.'),
(1911, 16, 10, 'اوست كه از آسمان برايتان باران نازل كرد. از آن مى‌نوشيد و بدان گياه مى‌رويد و چارپايان را مى‌چرانيد.'),
(1912, 16, 11, 'و با آن برايتان كشتزار و زيتون و نخلها و تاكستانها و هر نوع ميوه بروياند، در اين عبرتى است براى مردمى كه مى‌انديشند.'),
(1913, 16, 12, 'و مسخر شما كرد شب و روز را و خورشيد و ماه را و ستارگان همه فرمانبردار امر او هستند. در اين براى آنها كه به عقل درمى‌يابند عبرتهاست.'),
(1914, 16, 13, 'در زمين چيزهايى با رنگهاى گوناگون آفريد، در اين عبرتى است براى مردمى كه پند مى‌گيرند.'),
(1915, 16, 14, 'اوست كه دريا را رام كرد تا از آن گوشت تازه بخوريد و زيورهايى بيرون آريد و خويشتن بدان بياراييد، و كشتيها را بينى كه دريا را مى‌شكافند و پيش مى‌روند تا از فضل خدا روزى بطلبيد، باشد كه سپاس گوييد.'),
(1916, 16, 15, 'و بر زمين كوه‌هاى بزرگ افكند تا شما را نلرزاند. و رودها و راه‌ها پديد آورد. شايد هدايت شويد.'),
(1917, 16, 16, 'و نشانه‌ها نهاد، و به ستارگان راه مى‌يابند.'),
(1918, 16, 17, 'آيا آن كه مى‌آفريند همانند كسى است كه نمى‌آفريند؟ چرا درنمى‌يابيد؟'),
(1919, 16, 18, 'اگر بخواهيد نعمتهاى خدا را شمار كنيد، شمار كردن نتوانيد. خدا آمرزنده و مهربان است.'),
(1920, 16, 19, 'آنچه را كه پنهان مى‌كنيد يا آشكار مى‌سازيد خدا به آن آگاه است.'),
(1921, 16, 20, 'آنهايى را كه به جاى الله به خدايى مى‌خوانند، نمى‌توانند چيزى بيافرينند، و خود مخلوقند.'),
(1922, 16, 21, 'مردگانند، نه زندگان، و ندانند كه چه وقت آنها را دوباره زنده مى‌كنند.'),
(1923, 16, 22, 'خداى شما خدايى است يكتا. و آنان كه به آخرت ايمان ندارند دلهايشان انكار كند و خود كبرفروشند.'),
(1924, 16, 23, 'به راستى كه خدا مى‌داند كه چه در دل پنهان مى‌دارند و چه چيز را آشكار مى‌سازند و او متكبران را دوست ندارد.'),
(1925, 16, 24, 'چون به آنها گفته شود: پروردگارتان چه چيز نازل كرده است؟ گويند: افسانه‌هاى گذشتگان.'),
(1926, 16, 25, 'تا در روز قيامت همه بار گناه خويش و بار گناه كسانى را كه به نادانى گمراهشان كرده بودند، بردارند. آگاه باش كه بار بدى بر مى‌دارند.'),
(1927, 16, 26, 'پيشينيانشان حيلت ساختند. فرمان خدا در رسيد و آن بنا را از پايه ويران ساخت و سقف بر سرشان فرود آمد و از سويى كه نفهميدند، عذاب آنان را فروگرفت.'),
(1928, 16, 27, 'آنگاه در روز قيامت رسوايشان سازد و گويد: بتانى كه شريك من مى‌خوانديد و بر سر آنها با يكديگر اختلاف مى‌كرديد اكنون كجايند؟ دانشمندان گويند: امروز نصيب كافران رسوايى و رنج است.'),
(1929, 16, 28, 'كسانى هستند كه بر خود ستم روا داشته‌اند؛ چون فرشتگان جانشان را بستانند، سر تسليم فرود آرند و گويند: ما هيچ كار بدى نمى‌كرديم. آرى، خدا از كارهايى كه مى‌كرديد آگاه است.'),
(1930, 16, 29, 'از درهاى جهنم داخل شويد و تا ابد در آنجا بمانيد. بد جايگاهى است جايگاه گردنكشان.'),
(1931, 16, 30, 'از پرهيزگاران پرسند: پروردگار شما چه چيز نازل كرده است؟ گويند: بهترين را. به آنان كه در اين دنيا نيكى كنند، نيكى پاداش دهند و سراى آخرت نيكوتر از آن است و جايگاه پرهيزگاران چه جايگاه خوبى است.'),
(1932, 16, 31, 'به بهشتهاى جاويدان داخل مى‌شوند. در آن جويها روان است. هر چه بخواهند برايشان مهياست. خدا پرهيزگاران را اينچنين پاداش مى‌دهد؛'),
(1933, 16, 32, 'آنان كه چون فرشتگانشان پاك سيرت بميرانند، مى‌گويند: سلام بر شما. به پاداش كارهايى كه مى‌كرده‌ايد به بهشت درآييد.'),
(1934, 16, 33, 'آيا چشم به راهند كه فرشتگان نزدشان بيايند، يا فرمان پروردگارت فرا رسد؟ گروهى نيز كه پيش از ايشان بودند چنين مى‌كردند و خدا به آنان ستم نكرد بلكه آنان خود به خود ستم مى‌كردند.'),
(1935, 16, 34, 'به كيفر كردار بدشان رسيدند و همان چيزهايى كه به ريشخند مى‌گرفتند بر سرشان تاختن آورد.'),
(1936, 16, 35, 'مشركان گفتند: اگر خدا مى‌خواست، ما و پدرانمان هيچ چيز جز او را نمى‌پرستيديم و آنچه را حرام كرده‌ايم، حرام نمى‌كرديم. مردمى هم كه پيش از ايشان بودند چنين مى‌گفتند. آيا پيامبران را جز تبليغ روشنگر وظيفه ديگرى است؟'),
(1937, 16, 36, 'به ميان هر ملتى پيامبرى مبعوث كرديم، كه خدا را بپرستيد و از بت دورى جوييد. بعضى را خدا هدايت كرد و بر بعضى گمراهى مقرر گشت. پس در زمين بگرديد و بنگريد كه عاقبت كار كسانى كه پيامبران را به دروغ نسبت مى‌دادند، چگونه بوده است.'),
(1938, 16, 37, 'و اگر تو به هدايت آنها حريص باشى، خدا آن را كه گمراه كرده است، هدايت نمى‌كند و اين گمراهان را هيچ يارى‌دهنده‌اى نيست.'),
(1939, 16, 38, 'تا آنجا كه مى‌توانستند به خدا قسم خوردند، كه خدا كسانى را كه مى‌ميرند به رستاخيز زنده نمى‌كند. آرى اين وعده‌اى است كه انجام دادن آن بر عهده اوست، ولى بيشتر مردم نمى‌دانند.'),
(1940, 16, 39, 'تا آنجا را در آن اختلاف مى‌كردند برايشان آشكار كند و كافران بدانند كه دروغ مى‌گفته‌اند.'),
(1941, 16, 40, 'فرمان ما به هر چيزى كه اراده‌اش را بكنيم، اين است كه مى‌گوييم؛ موجود شو؛ و موجود مى‌شود.'),
(1942, 16, 41, 'به آنان كه مورد ستم واقع شدند و در راه خدا مهاجرت كردند در اين جهان جايگاهى نيكو مى‌دهيم. و اگر بدانند اجر آخرت بزرگ‌تر است؛'),
(1943, 16, 42, 'آنان كه صبر پيشه كردند و بر پروردگارشان توكل مى‌كنند.'),
(1944, 16, 43, 'اگر خود نمى‌دانيد، از اهل كتاب بپرسيد كه ما، پيش از تو، به رسالت نفرستاديم مگر مردانى را كه به آنها وحى مى‌فرستاديم؛'),
(1945, 16, 44, 'همراه با دلايل روشن و كتابها و بر تو نيز قرآن را نازل كرديم تا آنچه را براى مردم نازل شده است برايشان بيان كنى و باشد كه بينديشند.'),
(1946, 16, 45, 'آيا آنان كه مرتكب بديها مى‌شوند، مگر ايمنند از اينكه زمين به فرمان خدا آنها را فرو برد يا عذاب از جايى كه نمى‌دانند بر سرشان فرود آيد؟'),
(1947, 16, 46, 'يا به هنگام آمدوشد فروگيردشان، چنان كه نتوانند بگريزند؟'),
(1948, 16, 47, 'يا از آنان يكان‌يكان بكاهد؟ هرآينه پروردگارتان رئوف و مهربان است.'),
(1949, 16, 48, 'آيا به چيزهايى كه خدا آفريده است نمى‌نگرند كه براى سجده به درگاه او سايه‌هايشان از راست و چپ حركت دارند و در برابر او خاشعند؟'),
(1950, 16, 49, 'هر چه در آسمانها و زمين است از جنبندگان و فرشتگان خدا را سجده مى‌كنند و تكبر نمى‌ورزند.'),
(1951, 16, 50, 'از پروردگارشان كه فراز آنهاست مى‌ترسند و به هر چه مأمور شده‌اند همان مى‌كنند.'),
(1952, 16, 51, 'خدا گفت: دو خدا را مپرستيد و جز اين نيست كه اوست خداى يكتا. پس، از من بترسيد.'),
(1953, 16, 52, 'هر چه در آسمانها و زمين است از آن اوست و پرستش خاص اوست. آيا از غير خدا مى‌ترسيد؟'),
(1954, 16, 53, 'هر نعمتى كه شما راست از جانب خداوند است و چون محنتى به شما رسد به پيشگاه او زارى مى‌كنيد.'),
(1955, 16, 54, 'و باز چون آن محنت را به پايان آرد گروهى از شما به پروردگارشان مشرك مى‌شوند،'),
(1956, 16, 55, 'تا نعمتى را كه به آنها عنايت كرده‌ايم ناسپاسى كنند. اكنون متمتع شويد، زودا كه خواهيد دانست.'),
(1957, 16, 56, 'آنگاه از آنچه روزيشان داده‌ايم نصيبى براى بتانى كه هيچ نمى‌دانند معين مى‌كنند. به خدا سوگند به سبب دروغى كه مى‌بنديد بازخواست مى‌شويد.'),
(1958, 16, 57, 'براى خدا دختران قائل مى‌شوند -او منزه است- و براى خود هر چه دوست دارند.'),
(1959, 16, 58, 'و چون به يكيشان مژده دختر دهند، سيه‌روى شود و خشمگين گردد.'),
(1960, 16, 59, 'از شرم اين مژده، از مردم پنهان مى‌شود. آيا با خوارى نگاهش دارد يا در خاك نهانش‌كند؟ آگاه باشيد كه بد داورى مى‌كنند.'),
(1961, 16, 60, 'صفت بد از آن كسانى است كه به قيامت ايمان نمى‌آورند و صفت برتر از آن خداوند است. و اوست پيروزمند و حكيم.'),
(1962, 16, 61, 'اگر خداوند بخواهد كه مردم را به گناهشان هلاك كند، بر روى زمين هيچ جنبنده‌اى باقى نگذارد، ولى عذابشان را تا مدتى معين به تأخير مى‌افكند. و چون اجلشان فرارسد، يك ساعت پس و پيش نشوند.'),
(1963, 16, 62, 'آنچه را نمى‌پسندند به خدا نسبت كنند و زبانشان به دروغ گويد كه خوبيها از آن آنهاست، نه، بى‌ترديد نصيب آنها آتش است و ايشان را به شتاب به سوى آن برند.'),
(1964, 16, 63, 'به خدا سوگند كه براى مردمى هم كه پيش از تو بوده‌اند پيامبرانى فرستاده‌ايم. ولى شيطان اعمالشان را در چشمشان بياراست. و در آن روز شيطان دوستشان خواهد بود و آنها راست عذابى دردآور.'),
(1965, 16, 64, 'ما اين كتاب را بر تو نازل نكرده‌ايم جز براى آنكه هر چه را در آن اختلاف مى‌كنند برايشان بيان كنى. و نيز راهنما و رحمتى براى مؤمنان باشد.'),
(1966, 16, 65, 'و خدا از آسمان باران فرستاد و زمين مرده را با آن زنده كرد. براى مردمى كه گوش شنوا دارند در اين عبرتى است.'),
(1967, 16, 66, 'براى شما در چارپايان پندى است. از شير خالصى كه از شكمشان از ميان سرگين و خون بيرون مى‌آيد سيرابتان مى‌كنيم. شيرى كه به كام نوشندگانش گواراست.'),
(1968, 16, 67, 'و از ميوه‌هاى نخلها و تاكها شرابى مستى‌آور و رزقى نيكو به دست مى‌آوريد، و خردمندان را در اين عبرتى است.'),
(1969, 16, 68, 'پروردگار تو به زنبور عسل وحى كرد كه: از كوه‌ها و درختان و در بناهايى كه مى‌سازند خانه‌هايى برگزين.'),
(1970, 16, 69, 'آنگاه از هر ثمره‌اى بخور و فرمانبردار به راه پروردگارت برو. از شكم او شرابى رنگارنگ بيرون مى‌آيد كه شفاى مردم در آن است. و صاحبان انديشه را در اين عبرتى است.'),
(1971, 16, 70, 'خدا شما را بيافريد، آنگاه مى‌ميراند و از ميان شما كسى را به فرتوتى مى‌رساند تا هر چه را كه آموخته است از ياد ببرد، زيرا خدا دانا و تواناست.'),
(1972, 16, 71, 'خدا روزى بعضى از شما را از بعضى ديگر افزون كرده است. پس آنان كه فزونى يافته‌اند از روزى خود به بندگان خويش نمى‌دهند تا همه در روزى يكسان شوند. آيا نعمت خدا را انكار مى‌كنيد؟'),
(1973, 16, 72, 'خدا براى شما از ميان خودتان همسرانى قرار داد و از همسرانتان فرزندان و فرزندزادگان پديد آورد و از چيزهاى خوش و پاك روزيتان داد. آيا هنوز به باطل ايمان مى‌آورند و نعمت خدا را كفران مى‌كنند؟'),
(1974, 16, 73, 'سواى خدا چيزهايى را مى‌پرستند كه ناتوانند و ياراى آن ندارند كه از آسمانها و زمين روزيشان دهند.'),
(1975, 16, 74, 'براى خدا مثَل مزنيد، خدا مى‌داند و شما نمى‌دانيد.'),
(1976, 16, 75, 'خدا برده زرخريدى را مثَل مى‌زند كه هيچ قدرتى ندارد، و كسى را كه از جانب خويش رزق نيكويش داده‌ايم و در نهان و آشكارا انفاق مى‌كند. آيا اين دو برابرند؟ سپاس خاص خداست، ولى بيشترشان نادانند.'),
(1977, 16, 76, 'و خدا مثَل دو مرد را بيان مى‌كند كه يكى لال است و توان هيچ چيز ندارد و بار دوش مولاى خود است، هر جا كه او را بفرستد هيچ فايده‌اى حاصل نمى‌كند. آيا اين مرد با آن كس كه مردم را به عدل فرمان مى‌دهد و خود بر راه راست مى‌رود برابر است؟'),
(1978, 16, 77, 'از آن خداست نهان آسمانها و زمين. و فرارسيدن قيامت تنها يك چشم بر هم زدن يا نزديك‌تر از آن است. زيرا خدا بر هر كارى تواناست.'),
(1979, 16, 78, 'خدا شما را از بطن مادرانتان بيرون آورد و هيچ نمى‌دانستيد. و برايتان چشم و گوش و دل بيافريد. شايد سپاس گوييد.'),
(1980, 16, 79, 'آيا آن پرندگان رام‌شده در جو آسمان را نمى‌بينند؟ هيچ كس جز خدا آنها را در هوا نگاه نتواند داشت. و در اين براى مردمى كه ايمان مى‌آورند عبرتهاست.'),
(1981, 16, 80, 'خدا خانه‌هايتان را جاى آرامشتان قرار داد و از پوست چهارپايان برايتان خيمه‌ها ساخت تا به هنگام سفر و به هنگام اقامت از حمل آنها در رنج نيفتيد. و از پشم و كرك و مويشان، تا روز قيامت برايتان اثاث خانه و اسباب زندگى ساخت.'),
(1982, 16, 81, 'خدا براى شما از چيزهايى كه آفريده است سايه‌ها پديد آورد. و در كوه‌ها برايتان غارها ساخت. و جامه‌هايى كه شما را از گرما حفظ مى‌كند و جامه‌هايى كه در جنگ نگهدار شماست. خدا نعمتهاى خود را اينچنين بر شما تمام مى‌كند. باشد كه تسليم فرمان او شويد.'),
(1983, 16, 82, 'اگر روى‌گردان شدند، جز اين نيست كه وظيفه تو تبليغى روشنگر است.'),
(1984, 16, 83, 'نعمتهاى خدا را مى‌شناسند، باز هم منكر آن مى‌شوند، زيرا بيشترشان كافرانند.'),
(1985, 16, 84, 'روزى كه از هر امتى گواهى برانگيزيم و به كافران رخصت سخن‌گفتن داده نشود و عذرشان پذيرفته نيايد.'),
(1986, 16, 85, 'و ستمگران عذاب را بنگرند، عذابى كه هيچ تخفيف نيابد و هيچ مهلتشان ندهند.'),
(1987, 16, 86, 'و چون مشركان آنان را كه شريك خدا مى‌ساختند، ببينند، مى‌گويند: اى پروردگار ما، اينان شريكانى هستند كه ما به جاى تو آنها را مى‌خوانديم. آنان پاسخ مى‌گويند كه شما دروغ مى‌گوييد.'),
(1988, 16, 87, 'و در اين روز به بندگى، خويشتن را تسليم خدا كنند و آن دروغها كه مى‌بافته‌اند نابود شود.'),
(1989, 16, 88, 'آنان كه كافر شدند و ديگران را از راه خدا بازداشتند، به كيفر فسادى كه مى‌كردند عذابى بر عذابشان خواهيم افزود.'),
(1990, 16, 89, 'و روزى باشد كه از هر امتى شاهدى از خودشان بر خودشان برانگيزيم و تو را بياوريم تا بر آنان شهادت دهى. و ما قرآن را كه بيان‌كننده هر چيزى است و هدايت و رحمت و بشارت براى مسلمانان است بر تو نازل كرده‌ايم.'),
(1991, 16, 90, 'خدا به عدل و احسان و بخشش به خويشاوندان فرمان مى‌دهد. و از فحشا و زشتكارى و ستم نهى مى‌كند. شما را پند مى‌دهد، باشد كه پذيراى پند شويد.'),
(1992, 16, 91, 'چون با خدا پيمان بستيد بدان وفا كنيد و چون سوگند اكيد خورديد آن را مشكنيد، كه خدا را ضامن خويش كرده‌ايد و او مى‌داند كه چه مى‌كنيد.'),
(1993, 16, 92, 'و همانند آن زن كه رشته‌اى را كه محكم تافته بود از هم گشود و قطعه قطعه كرد مباشيد، تا سوگندهاى خود را وسيله فريب يكديگر سازيد بدين بهانه كه گروهى بيشتر از گروه ديگر است. خدا شما را بدان مى‌آزمايد، و در روز قيامت چيزى را كه در آن اختلاف مى‌كرديد برايتان بيان مى‌كند.'),
(1994, 16, 93, 'اگر خدا مى‌خواست، همه شما را يك امت كرده بود، ولى هر كه را بخواهد گمراه مى‌سازد و هر كه را بخواهد هدايت مى‌كند و از هر كارى كه مى‌كنيد بازخواست مى‌شويد.'),
(1995, 16, 94, 'سوگندهاى خود را وسيله فريب يكديگر مسازيد تا قدمى را كه استوار ساخته‌ايد بلغزد و به سبب اعراض از راه خدا گرفتار بدى شويد و به عذابى بزرگ گرفتار آييد.'),
(1996, 16, 95, 'پيمان خدا را به بهايى اندك مفروشيد زيرا، اگر بدانيد، آنچه در نزد خداست برايتان بهتر است.'),
(1997, 16, 96, 'آنچه نزد شماست فنا مى‌شود و آنچه نزد خداست باقى مى‌ماند. و آنان را كه شكيبايى ورزيدند پاداشى بهتر از كردارشان خواهيم داد.'),
(1998, 16, 97, 'هر زن و مردى كه كارى نيكو انجام دهد، اگر ايمان آورده باشد زندگى خوش و پاكيزه‌اى بدو خواهيم داد و پاداشى بهتر از كردارشان عطا خواهيم كرد.'),
(1999, 16, 98, 'و چون قرآن بخوانى، از شيطان رجيم به خدا پناه ببر.'),
(2000, 16, 99, 'شيطان را بر كسانى كه ايمان آورده‌اند و بر خدا توكل مى‌كنند تسلطى نيست.'),
(2001, 16, 100, 'تسلط او تنها بر كسانى است كه دوستش مى‌دارند و به خدا شرك مى‌آورند.'),
(2002, 16, 101, 'چون آيه‌اى را جانشين آيه ديگر كنيم، خدا بهتر مى‌داند كه چه چيز نازل كند. گفتند كه تو دروغ مى‌بافى، نه، بيشترينشان نادانند.'),
(2003, 16, 102, 'بگو: آن را روح القدس به حق از جانب پروردگارت نازل كرده است تا مؤمنان را استوارى ارزانى دارد و مسلمانان را هدايت و بشارت باشد.'),
(2004, 16, 103, 'مى‌دانيم كه مى‌گويند: اين قرآن را بشرى به او مى‌آموزد. زبان كسى كه به او نسبت مى‌كنند عجمى است، حال آنكه اين زبان عربى روشنى است.'),
(2005, 16, 104, 'خدا كسانى را كه به آياتش ايمان نمى‌آورند هدايت نمى‌كند و برايشان عذابى دردآور مهياست.'),
(2006, 16, 105, 'كسانى دروغ مى‌بافند كه به آيات خدا ايمان ندارند. اينان خود دروغگويند.'),
(2007, 16, 106, 'كسى كه پس از ايمان به خدا كافر مى‌شود نه آنكه او را به زور واداشته‌اند تا اظهار كفر كند و حال آنكه دلش به ايمان خويش مطمئن است بل آنان كه درِ دل را به روى كفر مى‌گشايند، مورد خشم خدايند و عذابى بزرگ برايشان مهياست.'),
(2008, 16, 107, 'و اين بدان سبب است كه اينان زندگى دنيا را بيشتر از زندگى آخرت دوست دارند و خدا مردم كافر را هدايت نمى‌كند.'),
(2009, 16, 108, 'خدا بر دل و گوشها و چشمانشان مهر برنهاده است و خود بى‌خبرانند.'),
(2010, 16, 109, 'به ناچار در آخرت هم از زيان‌ديدگان باشند.'),
(2011, 16, 110, 'سپس پروردگار تو براى كسانى كه پس از آن رنجها كه ديدند، مهاجرت كردند و به جهاد رفتند و پاى فشردند، آمرزنده و مهربان است.'),
(2012, 16, 111, 'روز قيامت هر كس به دفاع از خويش به مجادله مى‌پردازد و جزاى هر كس چنان كه حق اوست داده مى‌شود و به كسى ستم روا ندارند.'),
(2013, 16, 112, 'خدا قريه‌اى را مثَل مى‌زند كه امن و آرام بود، روزى مردمش به فراوانى از هر جاى مى‌رسيد، اما كفران نعمت خدا كردند و خدا به كيفر اعمالشان به گرسنگى و وحشت مبتلايشان ساخت.'),
(2014, 16, 113, 'پيامبرى از خودشان به نزدشان آمد، تكذيبش كردند؛ و عذاب، آن ستمكاران را فروگرفت.'),
(2015, 16, 114, 'از اين چيزهاى حلال و پاكيزه كه خدا به شما روزى داده است بخوريد و اگر خدا را مى‌پرستيد شكر نعمتش را به جاى آوريد.'),
(2016, 16, 115, 'خدا حرام كرده است بر شما مردار و خون و گوشت خوك و هر چه را جز به نام خدا ذبح كرده باشند. اما كسى كه ناچار شود هرگاه بى‌ميلى جويد و از حد نگذراند، خدا آمرزنده و مهربان است.'),
(2017, 16, 116, 'تا بر خدا افترا بنديد، براى هر دروغ كه بر زبانتان مى‌آيد مگوييد كه اين حلال است و اين حرام. كسانى كه به خداوند دروغ مى‌بندند رستگار نمى‌شوند؛'),
(2018, 16, 117, 'اندك بهره‌اى مى‌برند و در عوض به عذابى دردآور گرفتار مى‌آيند.'),
(2019, 16, 118, 'و بر يهوديان آنچه را كه پيش از اين براى تو حكايت كرديم حرام كرده بوديم. ما به آنها ستم نكرده‌ايم، آنان خود به خويشتن ستم كرده‌اند.'),
(2020, 16, 119, 'پروردگار تو براى كسانى كه از روى نادانى مرتكب كارى زشت شوند، سپس توبه كنند و به صلاح آيند، آمرزنده و مهربان است.'),
(2021, 16, 120, 'ابراهيم بزرگوار مردى بود. به فرمان خدا بر پاى ايستاد و صاحب دين حنيف بود. و از مشركان نبود،'),
(2022, 16, 121, 'و سپاسگزار نعمتهاى او بود، خداوندش برگزيد و به راه راست هدايت كرد.'),
(2023, 16, 122, 'در دنيا به او نيكى عنايت كرديم و در آخرت از صالحان است.'),
(2024, 16, 123, 'به تو نيز وحى كرديم كه: از آيين حنيف ابراهيم پيروى كن كه او از مشركان نبود.'),
(2025, 16, 124, 'شنبه گرفتن را براى كسانى كه در آن اختلاف كرده بودند قرار داده‌اند، و پروردگار تو در روز قيامت در باره آنچه اختلاف مى‌كردند حكم خواهد كرد.'),
(2026, 16, 125, 'مردم را با حكمت و اندرز نيكو به راه پروردگارت بخوان و با بهترين شيوه با آنان مجادله كن. زيرا پروردگار تو به كسانى كه از راه او منحرف شده‌اند آگاه‌تر است و هدايت‌يافتگان را بهتر مى‌شناسد.'),
(2027, 16, 126, 'اگر عقوبت مى‌كنيد، چنان عقوبت كنيد كه شما را عقوبت كرده‌اند. و اگر صبر كنيد، صابران را صبر نيكوتر است.'),
(2028, 16, 127, 'صبر كن، كه صبر تو جز به توفيق خدا نيست و بر ايشان محزون مباش و از مكرشان دلتنگى منماى،'),
(2029, 16, 128, 'زيرا خدا با كسانى است كه مى‌پرهيزند و نيكى مى‌كنند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزه است آن خدايى كه بنده خود را شبى از مسجدالحرام به مسجدالاقصى كه گرداگردش را بركت داده‌ايم سير داد، تا بعضى از آيات خود را به او بنماييم، هر آينه او شنوا و بيناست.'),
(2031, 17, 2, 'و آن كتاب را به موسى داديم و آن را راهنماى بنى‌اسرائيل گردانيديم كه: جز من كارسازى برمگزينيد.'),
(2032, 17, 3, 'اى فرزندان مردمى كه با نوح در كشتيشان نشانديم، بدانيد كه او بنده‌اى سپاسگزار بود.'),
(2033, 17, 4, 'و بنى اسرائيل را در آن كتاب خبر داديم كه: دو بار در زمين فساد خواهيد كرد و نيز سركشى خواهيد كرد، سركشى‌كردنى بزرگ.'),
(2034, 17, 5, 'چون از آن دو بار وعده نخستين در رسيد، گروهى از بندگان خويش را كه جنگاورانى زورمند بودند، بر سر شما فرستاديم. آنان حتى در درون خانه‌ها هم كشتار كردند و اين وعده به انجام رسيد.'),
(2035, 17, 6, 'بار ديگر شما را بر آنها غلبه داديم و به مال و فرزند مدد كرديم و بر شمارتان افزوديم.'),
(2036, 17, 7, 'اگر نيكى كنيد به خود مى‌كنيد، و اگر بدى كنيد به خود مى‌كنيد. و چون وعده دوم فرا رسيد، كسانى بر سرتان فرستاديم تا شما را غمگين سازند و چون بار اول كه به مسجد درآمده بودند به مسجد درآيند و به هر چه دست يابند نابود سازند.'),
(2037, 17, 8, 'شايد پروردگارتان بر شما رحمت آورد. و اگر بازگرديد، بازمى‌گرديم و جهنم را زندان كافران ساخته‌ايم.'),
(2038, 17, 9, 'اين قرآن به درست‌ترين آيينها راه مى‌نمايد و مؤمنانى را كه كارهاى شايسته به جاى مى‌آورند بشارت مى‌دهد كه از مزدى كرامند برخوردار خواهند شد.'),
(2039, 17, 10, 'و براى كسانى كه به آخرت ايمان ندارند عذابى دردآور مهيا كرده‌ايم.'),
(2040, 17, 11, 'و آدمى به دعا شرى را مى‌طلبد چنان كه گويى به دعا خيرى را مى‌جويد. و آدمى تا بوده شتابزده بوده است.'),
(2041, 17, 12, 'شب و روز را دو آيت از آيات خدا قرار داديم. آيت شب را تاريك گردانيديم و آيت روز را روشن تا به طلب رزقى كه پروردگارتان مقرر داشته است برخيزيد و شمار سالها و حساب را بدانيد، و ما هر چيزى را به تفصيل بيان كرده‌ايم.'),
(2042, 17, 13, 'كردار نيك و بد هر انسانى را چون طوقى به گردنش آويخته‌ايم. و در روز قيامت براى او نامه‌اى گشاده بيرون آوريم تا در آن بنگرد.'),
(2043, 17, 14, 'بخوان نامه‌ات را. امروز تو خود براى حساب كشيدن از خود بسنده‌اى.'),
(2044, 17, 15, 'هر كه هدايت شود به سود خود هدايت شده و هر كه گمراه گردد به زيان خود گمراه شده است و هيچ كس بار ديگرى را بر ندارد و ما هيچ قومى را عذاب نمى‌كنيم تا آنگاه كه پيامبرى بفرستيم.'),
(2045, 17, 16, 'چون بخواهيم قريه‌اى را هلاك كنيم، خداوندان نعمتش را بيفزاييم، تا در آنجا تبهكارى كنند، آنگاه عذاب بر آنها واجب گردد و آن را درهم فرو كوبيم.'),
(2046, 17, 17, 'بعد از نوح چه بسيار مردمى را هلاك كرده‌ايم. و پروردگار تو آگاهى يافتن و ديدن گناهان بندگانش را كافى است.'),
(2047, 17, 18, 'هر كس كه خواهان اين جهان باشد هر چه بخواهيم زودش ارزانى داريم، آنگاه جهنم را جايگاه او سازيم تا نكوهيده و مردود بدان درافتد.'),
(2048, 17, 19, 'و هر كه خواهان آخرت باشد و در طلب آن سعى كند و مؤمن باشد، جزاى سعيش داده خواهد شد.'),
(2049, 17, 20, 'همه را، چه آن گروه را و چه اين گروه را، از عطاى پروردگارت پى در پى خواهيم داد، زيرا عطاى پروردگارت را از كسى باز ندارند.'),
(2050, 17, 21, 'بنگر كه چگونه بعضيشان را بر بعضى ديگر برترى نهاده‌ايم و در آخرت درجات و برتريها، برتر و بالاتر است.'),
(2051, 17, 22, 'با خداى يكتا، خداى ديگرى به خدايى مگير، كه نكوهيده و خوار خواهى ماند.'),
(2052, 17, 23, 'پروردگارت مقرر داشت كه جز او را نپرستيد و به پدر و مادر نيكى كنيد. هرگاه تا تو زنده هستى هر دو يا يكى از آن دو سالخورده شوند، آنان را ميازار و به درشتى خطاب مكن و با آنان به اكرام سخن بگوى.'),
(2053, 17, 24, 'در برابرشان از روى مهربانى سر تواضع فرود آور و بگو: اى پروردگار من، همچنان كه مرا در خُردى پرورش دادند، بر آنها رحمت آور.'),
(2054, 17, 25, 'پروردگارتان از هر كس ديگر به آنچه در دلهايتان مى‌گذرد داناتر است؛ و اگر از صالحان باشيد او توبه‌كنندگان را مى‌آمرزد.'),
(2055, 17, 26, 'حق خويشاوند و مسكين و در راه‌مانده را ادا كن و هيچ اسرافكارى مكن.'),
(2056, 17, 27, 'اسرافكاران با شياطين برادرند و شيطان نسبت به پروردگارش ناسپاس بود.'),
(2057, 17, 28, 'و اگر به انتظار گشايشى كه از جانب پروردگارت اميد مى‌دارى از آنها اعراض مى‌كنى، پس با آنها به نرمى سخن بگوى.'),
(2058, 17, 29, 'نه دست خويش از روى خست به گردن ببند و نه به سخاوت يكباره بگشاى كه در هر دو حال ملامت‌زده و حسرت‌خورده بنشينى.'),
(2059, 17, 30, 'پروردگار تو در رزق هر كس كه بخواهد گشايش مى‌دهد، يا تنگ مى‌گيرد، زيرا او به بندگانش آگاه و بيناست.'),
(2060, 17, 31, 'فرزندان خود را از بيم درويشى مكشيد. ما، هم شما را روزى مى‌دهيم و هم ايشان را. كشتنشان خطاى بزرگى است.'),
(2061, 17, 32, 'و به زنا نزديك مشويد. زنا، كارى زشت و شيوه‌اى ناپسند است.'),
(2062, 17, 33, 'كسى را كه خدا كشتنش را حرام كرده است مكشيد مگر به حق. و هر كس كه به ستم كشته شود، به طلب‌كننده خون او قدرتى داده‌ايم. ولى در انتقام از حد نگذرد، كه او پيروزمند است.'),
(2063, 17, 34, 'جز به وجهى نيكوتر، به مال يتيم نزديك مشويد تا آنگاه كه به حد بلوغش برسد. و به عهد خويش وفا كنيد كه بازخواست خواهيد شد.'),
(2064, 17, 35, 'چون چيزى را پيمانه كنيد، پيمانه را كامل گردانيد و با ترازويى درست وزن كنيد كه اين بهتر و سرانجامش نيكوتر است.'),
(2065, 17, 36, 'از پى آنچه ندانى كه چيست مرو، زيرا گوش و چشم و دل، همه را، بدان بازخواست كنند.'),
(2066, 17, 37, 'به خودپسندى بر زمين راه مرو، كه زمين را نخواهى شكافت و به بلندى كوه‌ها نخواهى رسيد.'),
(2067, 17, 38, 'همه اين كارها ناپسند است و پروردگار تو آنها را ناخوش دارد.'),
(2068, 17, 39, 'اين حكمتى است كه پروردگار تو به تو وحى كرده است. با خداى يكتا خداى ديگرى مپندار، كه ملامت‌شده و مطرود به جهنمت اندازند.'),
(2069, 17, 40, 'آيا پروردگارتان براى شما پسران برگزيد و خود فرشتگان را به دخترى اختيار كرد؟ حقا كه سخنى است بزرگ كه بر زبان مى‌آوريد.'),
(2070, 17, 41, 'ما سخنان گونه‌گون در اين قرآن آورديم، باشد كه پند گيرند، ولى جز به رميدنشان نيفزايد.'),
(2071, 17, 42, 'بگو: همچنان كه مى‌گويند، اگر با او خدايان ديگرى هم بودند پس به سوى صاحب عرش راهى جسته بودند.'),
(2072, 17, 43, 'او منزه است، و از آنچه در باره‌اش مى‌گويند برتر و بالاتر است.'),
(2073, 17, 44, 'هفت آسمان و زمين و هر چه در آنهاست تسبيحش مى‌كنند و هيچ موجودى نيست جز آنكه او را به پاكى مى‌ستايد، ولى شما ذكر تسبيحشان را نمى‌فهميد. او بردبار و آمرزنده است.'),
(2074, 17, 45, 'چون تو قرآن بخوانى، ميان تو و آنان كه به قيامت ايمان نمى‌آورند پرده‌اى ستبر قرار مى‌دهيم.'),
(2075, 17, 46, 'و بر دلهاشان پرده افكنيم تا آن را درنيابند، و گوشهاشان سنگين كنيم. و چون پروردگارت را در قرآن به يكتايى ياد كنى، بازمى‌گردند و مى‌رمند.'),
(2076, 17, 47, 'ما بهتر مى‌دانيم كه چون به تو گوش مى‌دهند، چرا گوش مى‌دهند، يا وقتى كه با هم نجوا مى‌كنند، چه مى‌گويند. كافران مى‌گويند: شما در پى مرد جادو شده‌اى به راه افتاده‌ايد.'),
(2077, 17, 48, 'بنگر كه چگونه براى تو مثَلها زده‌اند. گمراه شده‌اند و راه به جايى نمى‌برند.'),
(2078, 17, 49, 'گفتند: آيا اگر از ما استخوانى بماند و خاكى، باز هم با آفرينشى نو از قبر برمى‌خيزيم؟'),
(2079, 17, 50, 'بگو: سنگ باشيد يا آهن.'),
(2080, 17, 51, 'يا مخلوقى كه در خاطرتان بزرگ مى‌نمايد. خواهند گفت: چه كسى ما را بازمى‌گرداند؟ بگو: آن كس كه بار نخست شما را آفريد. آنگاه در برابر تو سر مى‌جنبانند و مى‌گويند: چه وقت؟ بگو: ممكن است كه در همين نزديكى؛'),
(2081, 17, 52, 'روزى كه شما را فرا مى‌خواند و شما ستايش‌گويان پاسخ مى‌دهيد و مى‌پنداريد كه اندكى آرميده‌ايد.'),
(2082, 17, 53, 'و به بندگان من بگو كه با يكديگر به بهترين وجه سخن بگويند، كه شيطان در ميان آنها به فتنه‌گرى است، زيرا شيطان آدمى را دشمنى آشكار است.'),
(2083, 17, 54, 'پروردگارتان به شما آگاه‌تر است. اگر بخواهد بر شما رحمت مى‌آورد و اگر بخواهد عذابتان مى‌كند، و ما تو را به كارگزاريشان نفرستاده‌ايم.'),
(2084, 17, 55, 'و پروردگار تو به آنچه در آسمانها و زمين است آگاه‌تر است. بعضى از پيامبران را بر بعضى ديگر برترى نهاديم و به داود زبور را داديم.'),
(2085, 17, 56, 'بگو: آنهايى را كه جز او خدا مى‌پنداريد، بخوانيد. نمى‌توانند بلا را از شما دور سازند يا آن را نصيب ديگران كنند.'),
(2086, 17, 57, 'آنهايى كه كافران به خدايى مى‌خوانند، در صدد آنند كه خود به درگاه پروردگارشان وسيله‌اى بيابند و مقرّب‌ترين شوند، و به رحمت او اميد مى‌بندند و از عذاب او مى‌ترسند، زيرا عذاب پروردگارت ترسناك است.'),
(2087, 17, 58, 'هيچ قريه‌اى نباشد مگر اينكه پيش از فرا رسيدن روز قيامت هلاكش مى‌كنيم يا به عذابى سخت گرفتارش مى‌سازيم. و اين در آن كتاب نوشته شده است.'),
(2088, 17, 59, 'ما را از نزول معجزات باز نداشت، مگر اينكه پيشينيان تكذيبش مى‌كردند. به قوم ثمود به عنوان معجزه‌اى روشنگر ماده‌شتر را داديم. بر آن ستم كردند. و ما اين معجزات را جز براى ترسانيدن نمى‌فرستيم.'),
(2089, 17, 60, 'و آنگاه كه تو را گفتيم: پروردگارت بر همه مردم احاطه دارد. و آنچه در خواب به تو نشان داديم و داستان درخت ملعون كه در قرآن آمده است چيزى جز آزمايش مردم نبود. ما مردم را مى‌ترسانيم ولى تنها به كفر و سركشيشان افزوده مى‌شود.'),
(2090, 17, 61, 'و به فرشتگان گفتيم: آدم را سجده كنيد. همگان جز ابليس سجده كردند. گفت: آيا براى كسى كه از گِل آفريده‌اى سجده كنم؟'),
(2091, 17, 62, 'و گفت: با من بگوى چرا اين را بر من برترى نهاده‌اى؟ اگر مرا تا روز قيامت مهلت دهى، بر فرزندان او، جز اندكى، مهار زنم.'),
(2092, 17, 63, 'گفت: برو، جزاى تو و هر كس كه پيرو تو گردد جهنم است، كه كيفرى تمام است.'),
(2093, 17, 64, 'با فرياد خويش هر كه را توانى از جاى برانگيز و به يارى سواران و پيادگانت بر آنان بتاز و در مال و فرزند با آنان شركت جوى و به آنها وعده بده. و حال آنكه شيطان جز به فريبى وعده‌شان ندهد.'),
(2094, 17, 65, 'تو را بر بندگان من هيچ تسلطى نباشد و پروردگار تو براى نگهبانيشان كافى است.'),
(2095, 17, 66, 'پروردگار شماست كه كشتى را در دريا مى‌راند تا از فضل او روزى خود به چنگ آريد، زيرا با شما مهربان است.'),
(2096, 17, 67, 'چون در دريا شما را گزند رسد، همه آنهايى كه مى‌پرستيد از نظرتان محو شوند، مگر او. و چون شما را به ساحل نجات برساند از او رويگردان شويد، زيرا آدمى ناسپاس است.'),
(2097, 17, 68, 'آيا ايمنى داريد از اينكه ناگهان در ساحل دريا شما را در زمين فرو برد، يا تندبادى ريگ‌بار بر شما بينگيزد و براى خود هيچ نگهبانى نيابيد؟'),
(2098, 17, 69, 'يا ايمنى داريد از اينكه بار ديگر شما را به دريا باز گرداند و طوفانى سهمناك و كشتى‌شكن بفرستد و به خاطر كفرانى كه ورزيده‌ايد غرقتان سازد و كسى كه ما را از كارمان بازخواست كند نيابيد؟'),
(2099, 17, 70, 'ما فرزندان آدم را كرامت بخشيديم و بر دريا و خشكى سوار كرديم و از چيزهاى خوش و پاكيزه روزى داديم و بر بسيارى از مخلوقات خويش برتريشان نهاديم.'),
(2100, 17, 71, 'روزى كه هر گروه از مردم را به پيشوايانشان بخوانيم، نامه هر كه به دست راستش داده شود، چون بخواند بيند كه به اندازه رشته باريكى كه درون هسته خرماست، به او ستم نشده است.'),
(2101, 17, 72, 'و هر كه در اين دنيا نابينا باشد، در آخرت نيز نابينا و گمراه‌تر است.'),
(2102, 17, 73, 'نزديك بود تو را از آنچه بر تو وحى كرده بوديم منحرف سازند تا چيز ديگرى جز آن را به دروغ به ما نسبت كنى، آنگاه با تو دوستى كنند.'),
(2103, 17, 74, 'و اگر نه آن بود كه پايداريت داده بوديم، نزديك بود كه اندكى به آنان ميل كنى.'),
(2104, 17, 75, 'آنگاه تو را دو چندان در دنيا و دو چندان در آخرت عذاب مى‌كرديم و براى خود در برابر ما ياورى نمى‌يافتى.'),
(2105, 17, 76, 'و نزديك بود كه تو را از اين سرزمين بركنند تا از آنجا آواره‌ات سازند. و خود پس از تو جز اندك زمانى نمى‌ماندند.'),
(2106, 17, 77, 'سنت خدا درباره پيامبرانى كه پيش از تو فرستاده‌ايم چنين است. و سنت ما را تغييرپذير نيابى.'),
(2107, 17, 78, 'از هنگام زوال خورشيد تا آنگاه كه تاريكى شب فرا مى‌رسد نماز را برپاى دار، و نيز نماز صبحگاه را. و نماز صبحگاه را همگان حاضر شوند.'),
(2108, 17, 79, 'پاره‌اى از شب را به نمازخواندن زنده بدار. اين نافله خاص تو است. باشد كه پروردگارت، تو را به مقامى پسنديده برساند.'),
(2109, 17, 80, 'بگو: اى پروردگار من، مرا به راستى و نيكويى داخل كن و به راستى و نيكويى بيرون بر، و مرا از جانب خود پيروزى و يارى عطا كن.'),
(2110, 17, 81, 'بگو: حق آمد و باطل نابود شد. حقا كه باطل نابودشدنى بود.'),
(2111, 17, 82, 'و اين قرآن را كه براى مؤمنان شفا و رحمت است، نازل مى‌كنيم، ولى كافران را جز زيان نيفزايد.'),
(2112, 17, 83, 'چون به انسان نعمت داديم اعراض كرد و خويشتن به يك سو كشيد، و چون گزندى به او رسيد نوميد گرديد.'),
(2113, 17, 84, 'بگو: هر كس به طريقه خويش عمل مى‌كند و پروردگار تو بهتر مى‌داند كه كدام يك به هدايت نزديك‌تر است.'),
(2114, 17, 85, 'تو را از روح مى‌پرسند. بگو: روح جزئى از فرمان پروردگار من است و شما را جز اندك دانشى نداده‌اند.'),
(2115, 17, 86, 'اگر بخواهيم همه آنچه را كه بر تو وحى كرده‌ايم، باز مى‌ستانيم و تو براى خود در برابر ما مددكارى نمى‌يابى.'),
(2116, 17, 87, 'مگر پروردگارت رحمت آورد كه انعام او در باره تو بسيار است.'),
(2117, 17, 88, 'بگو: اگر جن و انس گرد آيند تا همانند اين قرآن را بياورند نمى‌توانند همانند آن را بياورند، هر چند كه يكديگر را يارى دهند.'),
(2118, 17, 89, 'ما در اين قرآن براى مردم هر مثَلى را گوناگون بيان كرديم، ولى بيشتر مردم جز ناسپاسى هيچ كارى نكردند.'),
(2119, 17, 90, 'گفتند: به تو ايمان نمى‌آوريم تا براى ما از زمين چشمه‌اى روان سازى،'),
(2120, 17, 91, 'يا تو را بستانى باشد از درختان خرما و انگور كه در خلالش نهرها جارى گردانى،'),
(2121, 17, 92, 'يا چنان كه گفته‌اى آسمان را پاره پاره بر سر ما افكنى يا خدا و فرشتگان را پيش ما حاضر آورى،'),
(2122, 17, 93, 'يا تو را خانه‌اى از طلا باشد، يا به آسمان بالا روى. و ما به آسمان رفتنت را باور نكنيم تا براى ما كتابى كه از آن بخوانيم بياورى. بگو: پروردگار من منزه است. آيا جز اين است كه من انسانى هستم كه به رسالت آمده‌ام؟'),
(2123, 17, 94, 'هيچ چيز مردم را از ايمان آوردن آنگاه كه هدايتشان مى‌كردند، باز نداشت مگر اينكه مى‌گفتند: آيا خدا انسانى را به رسالت مى‌فرستد؟'),
(2124, 17, 95, 'بگو: اگر در روى زمين فرشتگان به آرامى راه مى‌رفتند، برايشان به پيامبرى فرشته‌اى از آسمان نازل مى‌كرديم.'),
(2125, 17, 96, 'بگو خدا ميان من و شما شهادت را كافى است، زيرا او به بندگانش آگاه و بيناست.'),
(2126, 17, 97, 'و هر كه را خدا هدايت كند، هدايت شده است. و هر كه را گمراه سازد جز خداى براى او سرپرستى نيابى و در روز قيامت در حالى كه چهره‌هايشان رو به زمين است، كور و لال و كر محشورشان مى‌كنيم و جهنم جايگاه آنهاست كه هر چه شعله آن فرو نشيند بيشترش مى‌افروزيم.'),
(2127, 17, 98, 'اين كيفر آنهاست، زيرا به آيات ما ايمان نياوردند و گفتند: آيا چون ما استخوان شديم و خاكى، ما را به صورت تازه‌اى زنده مى‌كنند؟'),
(2128, 17, 99, 'آيا نمى‌دانند كه خدايى كه آسمانها و زمين را آفريده است قادر است كه همانند آنها را بيافريند و برايشان مدت عمرى نهاده كه در آن ترديدى نيست؟ اما ظالمان جز انكار نكنند.'),
(2129, 17, 100, 'بگو: اگر شما مالك همه خزاين پروردگار من مى‌بوديد، از بيم درويشى امساك پيشه مى‌كرديد، زيرا آدمى بخيل است.'),
(2130, 17, 101, 'از بنى‌اسرائيل بپرس، كه چون موسى نزد آنان آمد، به او نُه نشانه آشكار داده بوديم. و فرعون به او گفت: اى موسى، پندارم كه تو را جادو كرده باشند.'),
(2131, 17, 102, 'گفت: به حقيقت مى‌دانى اين نشانه‌هاى آشكار را جز پروردگار آسمانها و زمين نفرستاده است. و من اى فرعون، تو را به يقين هلاك شده مى‌بينم.'),
(2132, 17, 103, 'فرعون قصد آن داشت كه ايشان را از آن سرزمين بركند، ولى ما او و همه همراهانش را غرقه ساختيم.'),
(2133, 17, 104, 'و پس از او به بنى‌اسرائيل گفتيم: در آن سرزمين ساكن شويد و چون وعده آخرت رسد همه شما را با هم محشور مى‌كنيم.'),
(2134, 17, 105, 'اين قرآن را به حق نازل كرده‌ايم و به حق نازل شده است و تو را نفرستاديم مگر آنكه بشارت دهى و بيم دهى.'),
(2135, 17, 106, 'و قرآن را به تفاريق نازل كرديم تا تو آن را با تأنى بر مردم بخوانى و نازلش كرديم، نازل‌كردنى به كمال.'),
(2136, 17, 107, 'بگو: خواه بدان ايمان بياوريد يا ايمان نياوريد، آنان كه از اين پيش دانش آموخته‌اند، چون قرآن برايشان تلاوت شود سجده‌كنان بر روى درمى‌افتند.'),
(2137, 17, 108, 'و مى‌گويند: منزه است پروردگار ما. وعده پروردگار ما انجام‌يافتنى است.'),
(2138, 17, 109, 'و به رو درمى‌افتند و مى‌گريند و بر خشوعشان افزوده مى‌شود.'),
(2139, 17, 110, 'بگو: چه اللّه را بخوانيد چه رحمان را بخوانيد، هر كدام را كه بخوانيد، نامهاى نيكو از آن اوست. صدايت را به نماز بلند مكن و نيز صدايت را بدان آهسته مكن و ميان اين دو راهى برگزين.'),
(2140, 17, 111, 'بگو: سپاس خدايى را كه فرزندى ندارد و او را شريكى در مُلك نيست و به مذلت نيفتد كه به يارى محتاج شود. پس او را تكبير گوى، تكبيرى شايسته.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سپاس خداوندى را كه بر بنده خود اين كتاب را نازل كرد و هيچ كجى و انحراف در آن ننهاد.'),
(2142, 18, 2, 'كتابى عارى از انحراف. تا مردم را از خشم شديد خود بترساند و مؤمنان را كه كارهاى شايسته مى‌كنند، بشارت دهد كه پاداشى نيكو دارند،'),
(2143, 18, 3, 'در حالى كه تا ابد در آن جاودانه‌اند.'),
(2144, 18, 4, 'و كسانى را كه گفتند: خدا فرزندى اختيار كرد، بترساند.'),
(2145, 18, 5, 'نه خود به آن دانشى دارند و نه پدرانشان داشته‌اند، چه بزرگ است سخنى كه از دهانشان بيرون مى‌آيد و جز دروغى نمى‌گويند.'),
(2146, 18, 6, 'شايد اگر به اين سخن ايمان نياورند، خويشتن را به خاطرشان از اندوه هلاك سازى.'),
(2147, 18, 7, 'ما هر چه در روى زمين است زينت آن قرار داديم، تا امتحانشان كنيم كه كدامشان به عمل بهترند.'),
(2148, 18, 8, 'و نيز ما هستيم كه روى زمين را چون بيابانى خشك خواهيم كرد.'),
(2149, 18, 9, 'آيا پنداشته‌اى كه اصحاب كهف و رقيم از نشانه‌هاى شگفت‌انگيز ما بوده‌اند؟'),
(2150, 18, 10, 'آنگاه كه آن جوانمردان به غارها پناه بردند و گفتند: اى پروردگار ما، ما را از سوى خود رحمت عنايت كن و كار ما را به راه رستگارى انداز.'),
(2151, 18, 11, 'سالى چند در آن غار به خوابشان كرديم.'),
(2152, 18, 12, 'سپس بيدارشان كرديم تا بدانيم كدام يك از آن دو گروه حساب مدت آرميدنشان را داشته‌اند.'),
(2153, 18, 13, 'ما خبرشان را به راستى براى تو حكايت مى‌كنيم: آنها جوانمردانى بودند كه به پروردگارشان ايمان آورده بودند و ما نيز بر هدايتشان افزوديم.'),
(2154, 18, 14, 'بر دلهايشان نيرو بخشيديم، آنگاه كه برخاستند و گفتند: پروردگار ما پروردگار آسمانها و زمين است. جز او كسى را خدا نخوانيم كه هر گاه چنين كنيم، سخنى سخت كفرآميز گفته باشيم.'),
(2155, 18, 15, 'اينان كه قوم ما هستند، به جز او خدايانى اختيار كرده‌اند. چرا براى اثبات خدايى آنها دليل روشنى نمى‌آورند؟ كيست ستمكارتر از كسى كه به خدا دروغ مى‌بندد؟'),
(2156, 18, 16, 'اگر از قوم خود كناره جسته‌ايد و جز خداى يكتا خداى ديگرى را نمى‌پرستيد، به غار پناه بريد و خدا رحمت خويش بر شما ارزانى دارد و نعمتتان را در آن مهيا دارد.'),
(2157, 18, 17, 'و خورشيد را مى‌بينى كه چون برمى‌آيد، از غارشان به جانب راست ميل مى‌كند و چون غروب كند ايشان را واگذارد و به چپ گردد. و آنان در صحنه غارند. و اين از آيات خداست. هر كه را خدا هدايت كند هدايت يافته است و هر كه را گمراه سازد هرگز كارسازى راهنما براى او نخواهى يافت.'),
(2158, 18, 18, 'مى‌پنداشتى كه بيدارند حال آنكه در خواب بودند و ما آنان را به دست راست و دست چپ مى‌گردانيديم، و سگشان بر درگاه غار دو دست خويش دراز كرده بود. اگر به سروقتشان مى‌رفتى گريزان بازمى‌گشتى و از آنها سخت مى‌ترسيدى.'),
(2159, 18, 19, 'همچنين بيدارشان كرديم تا با يكديگر گفت و شنود كنند. يكى از آنها پرسيد: چند وقت است كه آرميده‌ايد؟ گفتند: يك روز يا پاره‌اى از روز را آرميده‌ايم. گفتند: پروردگارتان بهتر داند كه چند وقت آرميده‌ايد. يكى را از خود با اين پولتان به شهر بفرستيد تا بنگرد كه غذاى پاكيزه كدام است و برايتان از آن روزيتان را بياورد. و بايد كه به مهربانى رفتار كند تا كسى به شما آگاهى نيابد.'),
(2160, 18, 20, 'زيرا اگر شما را بيابند سنگسار خواهند كرد، يا به كيش خويش درآورند. و اگر چنين شود تا ابد رستگار نخواهيد شد.'),
(2161, 18, 21, 'بدين سان مردم را به حالشان آگاه كرديم تا بدانند كه وعده خدا راست است و در قيامت ترديدى نيست. آنگاه در باره آنها با يكديگر به گفت و گو پرداختند و گفتند: بر روى آنها بنايى برآوريد -پروردگارشان به كارشان آگاه‌تر است- و آنان كه بر حالشان آگاه‌تر شده بودند، گفتند: نه، اينجا را مسجدى مى‌كنيم.'),
(2162, 18, 22, 'خواهند گفت: سه تن بودند و چهارميشان سگشان بود و مى‌گويند: پنج تن بودند و ششميشان سگشان بود -تير به تاريكى مى‌افكنند- و مى‌گويند: هفت تن بودند و هشتميشان سگشان بود. بگو: پروردگار من به عدد آنها داناتر است و شمار ايشان را جز اندك‌كسان نمى‌دانند. و تو در باره آنها جز به ظاهر مجادله مكن و از كس نظر مخواه.'),
(2163, 18, 23, 'هرگز مگوى: فردا چنين مى‌كنم،'),
(2164, 18, 24, 'مگر خداوند بخواهد. و چون فراموش كنى، پروردگارت را به يادآر و بگو: شايد پروردگار من مرا از نزديك‌ترين راه هدايت كند.'),
(2165, 18, 25, 'و آنان در غار خود سيصد سال آرميدند و نُه سال بدان افزوده‌اند.'),
(2166, 18, 26, 'بگو: خداوند داناتر است كه چند سال آرميدند. غيب آسمانها و زمين از آنِ اوست. چه بيناست و چه شنواست. جز او دوستى ندارند و كس را در فرمان خود شريك نسازد.'),
(2167, 18, 27, 'از كتاب پروردگارت هر چه بر تو وحى شده است تلاوت كن. سخنان او را تغييردهنده‌اى نيست. و تو جز او پناهگاهى نمى‌يابى.'),
(2168, 18, 28, 'و همراه با كسانى كه هر صبح و شام پروردگارشان را مى‌خوانند و خشنودى او را مى‌جويند، خود را به صبر وادار. و نبايد چشمان تو براى يافتن پيرايه‌هاى اين زندگى دنيوى از اينان منصرف گردد. و از آن كه دلش را از ذكر خود بى‌خبر ساخته‌ايم، و از پى هواى نفس خود مى‌رود و در كارهايش اسراف مى‌ورزد، پيروى مكن.'),
(2169, 18, 29, 'بگو: اين سخن حق از جانب پروردگار شماست. هر كه بخواهد ايمان بياورد و هر كه بخواهد كافر شود. ما براى كافران آتشى كه لهيب آن همه را دربرمى‌گيرد، آماده كرده‌ايم و چون به استغاثه آب خواهند از آبى چون مس گداخته كه از حرارتش چهره‌ها كباب مى‌شود بخورانندشان، چه آب بدى و چه آرامگاهى بد.'),
(2170, 18, 30, 'كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، بدانند كه ما پاداش نيكوكاران را ضايع نمى‌كنيم.'),
(2171, 18, 31, 'برايشان بهشتهاى جاويد است. از زير پاهاشان نهرها جارى است. بهشتيان را به دستبندهاى زر مى‌آرايند و جامه‌هايى سبز از ديباى نازك و ديباى ستبر مى‌پوشند و در آنجا بر تختها تكيه مى‌زنند. چه پاداش نيكويى و چه آرامگاه نيكويى.'),
(2172, 18, 32, 'برايشان داستان دو مرد را بيان كن كه به يكى دو تاكستان داده بوديم و بر گرد آنها نخلها و در ميانشان كشتزار كرده بوديم.'),
(2173, 18, 33, 'آن دو تاكستان، ميوه خود را مى‌دادند بى هيچ كم و كاست. و نهرى بين آن دو جارى گردانيديم.'),
(2174, 18, 34, 'حاصل از آنِ او بود. به دوستش كه با او گفت و گو مى‌كرد گفت: من به مال از تو بيشتر و به افراد پيروزترم.'),
(2175, 18, 35, 'و آن مرد ستم بر خويشتن كرده به باغ خود درآمد و گفت: نپندارم كه اين باغ هرگز از ميان برود.'),
(2176, 18, 36, 'و نپندارم كه قيامت هم بيايد. و اگر هم مرا نزد خدا برند، جايگاهى بهتر از اين باغ خواهم يافت.'),
(2177, 18, 37, 'دوستش كه با او گفت و گو مى‌كرد گفت: آيا بر آن كس كه تو را از خاك و سپس از نطفه بيافريد و مردى راست بالا كرد، كافر شده‌اى؟'),
(2178, 18, 38, 'ولى او خداى يكتا پروردگار من است و من هيچ كس را شريك پروردگارم نمى‌سازم.'),
(2179, 18, 39, 'چرا آنگاه كه به باغ خود درآمدى نگفتى: هر چه خداوند خواهد، و هيچ نيرويى جز نيروى خدا نيست؟ اگر مى‌بينى كه دارايى و فرزند من كمتر از تو است،'),
(2180, 18, 40, 'شايد پروردگار من مرا چيزى بهتر از باغ تو دهد. شايد بر آن باغ صاعقه‌اى بفرستد و آن را به زمينى صاف و لغزنده بدل سازد.'),
(2181, 18, 41, 'يا آب آن بر زمين فرو رود و هرگز به يافتن آن قدرت نيابى.'),
(2182, 18, 42, 'به ثمره‌اش آفت رسيد و بامدادان دست حسرت بر دست مى‌ساييد كه چه هزينه‌اى كرده بود و اكنون همه بناهايش فرو ريخته است. و مى‌گويد: اى كاش كسى را شريك پروردگارم نساخته بودم.'),
(2183, 18, 43, 'جز خدا گروهى كه به ياريش برخيزند نبود و خود قدرت نداشت.'),
(2184, 18, 44, 'آنجا يارى كردن خداى حق را سزد. پاداش او بهتر و سرانجامش نيكوتر است.'),
(2185, 18, 45, 'برايشان زندگى دنيا را مثَل بزن كه چون بارانى است كه از آسمان ببارد و با آن گياهان گوناگون به فراوانى برويد. ناگاه خشك شود و باد بر هر سو پراكنده‌اش سازد. و خدا بر هر كارى تواناست.'),
(2186, 18, 46, 'دارايى و فرزند پيرايه‌هاى اين زندگانى دنياست و كردارهاى نيك كه همواره بر جاى مى‌مانند نزد پروردگارت بهتر و اميد بستن به آنها نيكوتر است.'),
(2187, 18, 47, 'روزى كه كوه‌ها را به راه مى‌اندازيم و زمين را بينى كه هر چه در اندرون دارد بيرون افكنده است و همه را براى حساب گرد مى‌آوريم و يك تن از ايشان را هم رها نمى‌كنيم.'),
(2188, 18, 48, 'همه در يك صف به پروردگارت عرضه مى‌شوند. اكنون نزد ما آمديد همچنان كه نخستين بار شما را بيافريديم. آيا مى‌پنداشتيد كه هرگز برايتان وعده‌گاهى قرار نخواهيم داد؟'),
(2189, 18, 49, 'دفتر اعمال گشوده شود. مجرمان را بينى كه از آنچه در آن آمده است بيمناكند و مى‌گويند: واى بر ما، اين چه دفترى است كه هيچ گناه كوچك و بزرگى را حساب ناشده رها نكرده است. آنگاه اعمال خود را در مقابل خود بيابند و پروردگار تو، به كسى ستم نمى‌كند.'),
(2190, 18, 50, 'و آنگاه كه به فرشتگان گفتيم كه آدم را سجده كنيد، همه جز ابليس كه از جن بود و از فرمان پروردگارش سر بتافت سجده كردند. آيا شيطان و فرزندانش را به جاى من به دوستى مى‌گيريد، حال آنكه دشمن شمايند؟ ظالمان بد چيزى را به جاى خدا برگزيدند.'),
(2191, 18, 51, 'آنگاه كه آسمانها و زمين و خودشان را مى‌آفريدم، از آنها يارى نخواستم. زيرا من گمراه‌كنندگان را به يارى نمى‌گيرم.'),
(2192, 18, 52, 'و روزى كه مى‌گويد: آنهايى را كه مى‌پنداشتيد شريكان منند ندا دهيد. ندا دهند و شريكان پاسخ ندهند. آنگاه هلاكتگاه را ميانشان قرار دهيم.'),
(2193, 18, 53, 'چون مجرمان آتش را ببينند بدانند كه در آن خواهند افتاد و راه رهايى از آن نيست.'),
(2194, 18, 54, 'در اين قرآن براى مردم هر گونه داستانى بيان كرده‌ايم، ولى آدمى بيش از همه به جدل برمى‌خيزد.'),
(2195, 18, 55, 'مردمان را چون به راه هدايت فرا خواندند، هيچ چيز از ايمان آوردن و آمرزش خواستن، بازنداشت مگر آنكه مى‌بايست به شيوه پيشينيان گرفتار عذاب شوند يا آنكه عذاب روياروى و آشكارا بر آنان فرود آيد.'),
(2196, 18, 56, 'و ما پيامبران را نفرستاديم جز آنكه به مردم مژده دهند يا بيم. و كافرانى كه مى‌خواهند به نيروى باطل، حق را از ميان ببرند جدال مى‌كنند و آيات و هشدارهاى مرا به ريشخند مى‌گيرند.'),
(2197, 18, 57, 'كيست ستمكارتر از آن كه آيات پروردگارش را برايش بخوانند و او اعراض كند و كارهايى را كه از پيش مرتكب شده فراموش كند؟ بر دل ايشان پرده افكنديم تا آيات را درنيابند و گوشهايشان را كر ساختيم كه اگر به راه هدايتشان فراخوانى، هرگز راه نيابند.'),
(2198, 18, 58, 'پروردگارت آمرزنده و مهربان است. اگر مى‌خواست به سبب كردارشان در اين جهان بازخواستشان كند، بى‌درنگ به عذابشان مى‌سپرد. اما عذاب را زمانى معين است كه چون فرازآيد، پناهگاهى نيابند.'),
(2199, 18, 59, 'و آن قريه‌ها را چون كافر شدند به هلاكت رسانيديم و براى هلاكتشان وعده‌اى نهاديم.'),
(2200, 18, 60, 'و موسى به شاگرد خود گفت: من همچنان خواهم رفت تا آنجا كه دو دريا به هم رسيده‌اند. يا مى‌رسم، يا عمرم به سر مى‌آيد.'),
(2201, 18, 61, 'چون آن دو به آنجا كه دو دريا به هم رسيده بودند رسيدند، ماهيشان را فراموش كردند و ماهى راه دريا گرفت و در آب شد.'),
(2202, 18, 62, 'چون از آنجا گذشتند، به شاگرد خود گفت: چاشتمان را بياور كه در اين سفرمان رنج فراوان ديده‌ايم.'),
(2203, 18, 63, 'گفت: آيا به ياد دارى آنگاه را كه در كنار آن صخره مكان گرفته بوديم؟ من ماهى را فراموش كرده‌ام. و اين شيطان بود كه سبب شد فراموشش كنم و ماهى به شيوه‌اى شگفت‌انگيز به دريا رفت.'),
(2204, 18, 64, 'گفت: آنجا همان جايى است كه در طلبش بوده‌ايم. و به نشان قدمهاى خود جست‌وجوكنان بازگشتند.'),
(2205, 18, 65, 'در آنجا بنده‌اى از بندگان ما را كه رحمت خويش بر او ارزانى داشته بوديم و خود بدو دانش آموخته بوديم، بيافتند.'),
(2206, 18, 66, 'موسى گفتش: آيا با تو بيايم تا از آنچه به تو آموخته‌اند به من كمالى بياموزى؟'),
(2207, 18, 67, 'گفت: تو را شكيب همراهى با من نيست.'),
(2208, 18, 68, 'و چگونه در برابر چيزى كه بدان آگاهى نيافته‌اى صبر خواهى كرد؟'),
(2209, 18, 69, 'گفت: اگر خدا بخواهد، مرا صابر خواهى يافت آنچنان كه در هيچ كارى تو را فرمانى نكنم.'),
(2210, 18, 70, 'گفت: اگر از پى من مى‌آيى، نبايد كه از من چيزى بپرسى تا من خود تو را از آن آگاه كنم.'),
(2211, 18, 71, 'پس به راه افتادند تا به كشتى سوار شدند. كشتى را سوراخ كرد. گفت: كشتى را سوراخ مى‌كنى تا مردمش را غرقه سازى؟ كارى كه مى‌كنى كارى سخت بزرگ و زشت است.'),
(2212, 18, 72, 'گفت: نگفتم كه تو را شكيب همراهى با من نيست؟'),
(2213, 18, 73, 'گفت: اگر فراموش كرده‌ام مرا بازخواست مكن و بدين اندازه بر من سخت مگير.'),
(2214, 18, 74, 'و رفتند تا به پسرى رسيدند، او را كشت، موسى گفت: آيا جان پاكى را بى‌آنكه مرتكب قتلى شده باشد مى‌كشى؟ مرتكب كارى زشت گرديدى.'),
(2215, 18, 75, 'گفت: نگفتم كه تو را شكيب همراهى با من نيست؟'),
(2216, 18, 76, 'گفت: اگر از اين پس از تو چيزى پرسم با من همراهى مكن، كه از جانب من معذور باشى.'),
(2217, 18, 77, 'پس برفتند تا به دهى رسيدند. از مردم آن ده طعامى خواستند. از ميزبانيشان سر برتافتند. آنجا ديوارى ديدند كه نزديك بود فرو ريزد. ديوار را راست كرد. موسى گفت: كاش در برابر اين كار مزدى مى‌خواستى.'),
(2218, 18, 78, 'گفت: اكنون زمان جدايى ميان من و توست و تو را از راز آن كارها كه تحملشان را نداشتى آگاه مى‌كنم:'),
(2219, 18, 79, 'اما آن كشتى از آنِ بينوايانى بود كه در دريا كار مى‌كردند. خواستم معيوبش كنم، زيرا در آن سوترشان پادشاهى بود كه كشتيها را به غصب مى‌گرفت.'),
(2220, 18, 80, 'اما آن پسر، پدر و مادرش مؤمن بودند. ترسيديم كه آن دو را به عصيان و كفر دراندازد.'),
(2221, 18, 81, 'خواستيم تا در عوض او پروردگارشان چيزى نصيبشان سازد به پاكى بهتر از او و به مهربانى نزديك‌تر از او.'),
(2222, 18, 82, 'اما ديوار از آنِ دو پسر يتيم از مردم اين شهر بود. در زيرش گنجى بود، از آنِ پسران، پدرشان مردى صالح بود. پروردگار تو مى‌خواست آن دو به حد رشد رسند و گنج خود را بيرون آرند. و من اين كار را به ميل خود نكردم. رحمت پروردگارت بود. اين است راز آن سخن كه گفتم: تو را شكيب آن نيست.'),
(2223, 18, 83, 'و از تو در باره ذوالقرنين مى‌پرسند. بگو: براى شما از او چيزى مى‌خوانم.'),
(2224, 18, 84, 'ما او را در زمين مكانت داديم و راه رسيدن به هر چيزى را به او نشان داديم.'),
(2225, 18, 85, 'او نيز راه را پى گرفت.'),
(2226, 18, 86, 'تا به غروبگاه خورشيد رسيد. ديد كه در چشمه‌اى گِل‌آلود و سياه غروب مى‌كند و در آنجا مردمى يافت. گفتيم: اى ذوالقرنين، مى‌خواهى عقوبتشان كن و مى‌خواهى با آنها به نيكى رفتار كن.'),
(2227, 18, 87, 'گفت: اما هر كس كه ستم كند ما عقوبتش خواهيم كرد. آنگاه او را نزد پروردگارش مى‌برند تا او نيز به سختى عذابش كند.'),
(2228, 18, 88, 'و اما هر كس كه ايمان آورد و كارهاى شايسته كند، اجرى نيكو دارد. و در باره او فرمانهاى آسان خواهيم راند.'),
(2229, 18, 89, 'باز هم راه را پى گرفت.'),
(2230, 18, 90, 'تا به مكان برآمدن آفتاب رسيد. ديد بر قومى طلوع مى‌كند كه غير از پرتو آن برايشان هيچ پوششى قرار نداده‌ايم.'),
(2231, 18, 91, 'چنين بود. و ما بر احوال او احاطه داريم.'),
(2232, 18, 92, 'باز هم راه را پى گرفت.'),
(2233, 18, 93, 'تا به ميان دو كوه رسيد. در پس آن دو كوه مردمى را ديد كه گويى هيچ سخنى را نمى‌فهمند.'),
(2234, 18, 94, 'گفتند: اى ذوالقرنين، يأجوج و مأجوج در زمين فساد مى‌كنند. مى‌خواهى خراجى بر خود مقرر كنيم تا تو ميان ما و آنها سدى برآورى؟'),
(2235, 18, 95, 'گفت: آنچه پروردگار من مرا بدان توانايى داده است بهتر است. مرا به نيروى خويش مدد كنيد، تا ميان شما و آنها سدى برآورم.'),
(2236, 18, 96, 'براى من تكه‌هاى آهن بياوريد. چون ميان آن دو كوه انباشته شد، گفت: بدميد. تا آن آهن را بگداخت. و گفت: مس گداخته بياوريد تا بر آن ريزم.'),
(2237, 18, 97, 'نه توانستند از آن بالا روند و نه در آن سوراخ كنند.'),
(2238, 18, 98, 'گفت: اين رحمتى بود از جانب پروردگار من؛ و چون وعده پروردگار من در رسد، آن را زير و زبر كند و وعده پروردگار من راست است.'),
(2239, 18, 99, 'و در آن روز واگذاريم تا چون موج روان گردند، و چون در صور دميده شود همه را يكجا گرد آوريم.'),
(2240, 18, 100, 'و در آن روز جهنم را به كافران، چنان كه بايد، نشان خواهيم داد:'),
(2241, 18, 101, 'آن كسان كه ديدگانشان از ياد من در حجاب بوده و توان شنيدن نداشته‌اند.'),
(2242, 18, 102, 'آيا كافران پندارند كه به جاى من بندگان مرا به خدايى گيرند؟ ما جهنم را آماده ساخته‌ايم تا منزلگاه كافران باشد.'),
(2243, 18, 103, 'بگو: آيا شما را آگاه كنيم كه كردار چه كسانى بيش از همه به زيانشان بود؟'),
(2244, 18, 104, 'آنهايى كه كوششان در زندگى دنيا تباه شد و مى‌پنداشتند كارى نيكو مى‌كنند.'),
(2245, 18, 105, 'آنان به آيات پروردگارشان و به ملاقات با او ايمان نياوردند، پس اعمالشان ناچيز شد و ما در روز قيامت برايشان منزلتى قائل نيستيم.'),
(2246, 18, 106, 'همچنين كيفر آنان به سبب كفرشان و نيز بدان سبب كه آيات و پيامبران مرا مسخره مى‌كردند، جهنم است.'),
(2247, 18, 107, 'كسانى كه ايمان آوردند و كارهاى شايسته كردند مهمانسرايشان باغهاى فردوس است.'),
(2248, 18, 108, 'در آنجا جاويدانند و هرگز هواى انتقال نكنند.'),
(2249, 18, 109, 'بگو: اگر دريا براى نوشتن كلمات پروردگار من مركب شود، دريا به پايان مى‌رسد و كلمات پروردگار من به پايان نمى‌رسد، هر چند درياى ديگرى به مدد آن بياوريم.'),
(2250, 18, 110, 'بگو: من انسانى هستم همانند شما. به من وحى مى‌شود. هرآينه خداى شما خدايى است يكتا. هر كس ديدار پروردگار خويش را اميد مى‌بندد، بايد كردارى شايسته داشته باشد و در پرستش پروردگارش هيچ كس را شريك نسازد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'كاف، ها، يا، عين، صاد.'),
(2252, 19, 2, 'بيان بخشايش پروردگارت بر بنده خود زكريّا است.'),
(2253, 19, 3, 'آنگاه كه پروردگارش را در نهان ندا داد.'),
(2254, 19, 4, 'گفت: اى پروردگار من، استخوان من سست گشته و سرم از پيرى سفيد شده است و هرگز در دعا به درگاه تو، اى پروردگار من، بى‌بهره نبوده‌ام.'),
(2255, 19, 5, 'من پس از مرگ خويش، از خويشاوندانم بيمناكم و زنم نازاينده است. مرا از جانب خود فرزندى عطا كن،'),
(2256, 19, 6, 'كه ميراثبر من و ميراثبر خاندان يعقوب باشد و او را، اى پروردگار من، شايسته و پسنديده گردان.'),
(2257, 19, 7, 'اى زكريّا، ما تو را به پسرى به نام يحيى مژده مى‌دهيم. از اين پيش كسى را همنام او نساخته‌ايم.'),
(2258, 19, 8, 'گفت: اى پروردگار من، مرا از كجا پسرى باشد، حال آنكه زنم نازاينده است. و من خود در پيرى به فرتوتى رسيده‌ام.'),
(2259, 19, 9, 'گفت: پروردگارت اينچنين گفته است: اين براى من آسان است و تو را كه پيش از اين چيزى نبوده‌اى بيافريده‌ام.'),
(2260, 19, 10, 'گفت: اى پروردگار من، به من نشانى بنماى. گفت: نشان تو اين است كه سه شب و سه روز با مردم سخن نگويى بى‌هيچ آسيبى.'),
(2261, 19, 11, 'از محراب به ميان قوم خود بيرون آمد و به آنان اعلام كرد كه: صبح و شام تسبيح گوييد.'),
(2262, 19, 12, 'اى يحيى، كتاب را به نيرومندى بگير. و در كودكى به او دانايى عطا كرديم.'),
(2263, 19, 13, 'و به او شفقت كرديم و پاكيزه‌اش ساختيم و او پرهيزگار بود.'),
(2264, 19, 14, 'به پدر و مادر نيكى مى‌كرد و جبار و گردنكش نبود.'),
(2265, 19, 15, 'سلام بر او؛ روزى كه زاده شد و روزى كه مى‌ميرد و روزى كه ديگر بار زنده برانگيخته مى‌شود.'),
(2266, 19, 16, 'در اين كتاب مريم را ياد كن، آنگاه كه از خاندان خويش به مكانى رو به سوى برآمدن آفتاب، دورى گزيد.'),
(2267, 19, 17, 'ميان خود و آنان پرده‌اى كشيد و ما روح خود را نزدش فرستاديم و چون انسانى تمام بر او نمودار شد.'),
(2268, 19, 18, 'مريم گفت: از تو به خداى رحمان پناه مى‌برم، كه پرهيزگار باشى.'),
(2269, 19, 19, 'گفت: من فرستاده پروردگار تو هستم، تا تو را پسرى پاكيزه ببخشم.'),
(2270, 19, 20, 'گفت: از كجا مرا فرزندى باشد، حال آنكه هيچ بشرى به من دست نزده است و من بدكاره هم نبوده‌ام.'),
(2271, 19, 21, 'گفت: پروردگار تو اينچنين گفته است: اين براى من آسان است. ما آن پسر را براى مردم آيتى و بخشايشى كنيم و اين كارى است حتمى و پايان يافته.'),
(2272, 19, 22, 'پس به او آبستن شد و او را با خود به مكانى دورافتاده برد.'),
(2273, 19, 23, 'درد زاييدن او را به سوى تنه درخت خرمايى كشانيد. گفت: اى كاش پيش از اين مرده بودم و از يادها فراموش شده بودم.'),
(2274, 19, 24, 'كودك از زير او ندا داد: محزون مباش، پروردگارت از زير پاى تو جوى آبى روان ساخت.'),
(2275, 19, 25, 'نخل را بجنبان تا خرماى تازه چيده برايت فرو ريزد.'),
(2276, 19, 26, 'پس اى زن، بخور و بياشام و شادمان باش و اگر از آدميان كسى را ديدى بگوى: براى خداى رحمان روزه نذر كرده‌ام و امروز با هيچ بشرى سخن نمى‌گويم.'),
(2277, 19, 27, 'كودك را برداشت و نزد قوم خود آورد. گفتند: اى مريم، كارى قبيح كرده‌اى.'),
(2278, 19, 28, 'اى خواهر هارون، نه پدرت مرد بدى بود و نه مادرت زنى بدكاره.'),
(2279, 19, 29, 'به فرزند اشاره كرد. گفتند: چگونه با كودكى كه در گهواره است سخن بگوييم.'),
(2280, 19, 30, 'كودك گفت: من بنده خدايم، به من كتاب داده و مرا پيامبر گردانيده است.'),
(2281, 19, 31, 'و هر جا كه باشم مرا بركت داده و تا زنده‌ام به نماز و زكات وصيت كرده است.'),
(2282, 19, 32, 'و نيز نيكى كردن به مادرم. و مرا جبار و شقى نساخته است.'),
(2283, 19, 33, 'سلام بر من؛ روزى كه زاده شدم و روزى كه مى‌ميرم و روزى كه ديگر بار زنده برانگيخته مى‌شوم.'),
(2284, 19, 34, 'اين است عيسى بن مريم -به سخن حق- همان كه در باره او در ترديد بودند.'),
(2285, 19, 35, 'نسزد خداوند را كه فرزندى برگيرد. منزه است. چون اراده كارى كند، مى‌گويد: موجود شو. پس موجود مى‌شود.'),
(2286, 19, 36, 'و اللّه پروردگار من و پروردگار شماست، پس او را بپرستيد، كه راه راست اين است.'),
(2287, 19, 37, 'گروه‌ها با هم اختلاف كردند، پس واى بر كافران به هنگام حضور در آن روز بزرگ.'),
(2288, 19, 38, 'چه خوب مى‌شنوند و چه خوب مى‌بينند آن روز كه نزد ما مى‌آيند. ولى ستمكاران امروز در گمراهى آشكارى هستند.'),
(2289, 19, 39, 'آنان را از روز حسرت كه كار به پايان آمده و آنان همچنان در حال غفلت و بى‌ايمانى هستند بترسان.'),
(2290, 19, 40, 'هر آينه ما زمين و هر كه را بر روى آن است به ارث مى‌بريم و همه به نزد ما بازگردانده مى‌شوند.'),
(2291, 19, 41, 'و در اين كتاب ابراهيم را ياد كن كه او پيامبرى راستگو بود.'),
(2292, 19, 42, 'آنگاه كه پدرش را گفت: اى پدر، چرا چيزى را كه نه مى‌شنود و نه مى‌بيند و نه هيچ نيازى از تو برمى‌آورد، مى‌پرستى؟'),
(2293, 19, 43, 'اى پدر، مرا دانشى داده‌اند كه به تو نداده‌اند. پس، از من پيروى كن تا تو را به راه راست هدايت كنم.'),
(2294, 19, 44, 'اى پدر، شيطان را مپرست، زيرا شيطان خداى رحمان را نافرمان بود.'),
(2295, 19, 45, 'اى پدر، مى‌ترسم عذابى از جانب خداى رحمان به تو رسد و تو دوستدار شيطان باشى.'),
(2296, 19, 46, 'گفت: اى ابراهيم، آيا از خدايان من بيزار هستى؟ اگر بازنايستى سنگسارت مى‌كنم. اكنون زمانى دراز از من دور باش.'),
(2297, 19, 47, 'گفت: تو را سلامت باد. از پروردگارم برايت آمرزش خواهم خواست. زيرا او بر من مهربان است.'),
(2298, 19, 48, 'از شما و از آن چيزهايى كه به جاى خداى يكتا مى‌خوانيد كناره مى‌گيرم و پروردگار خود را مى‌خوانم، باشد كه چون پروردگارم را بخوانم محروم نشوم.'),
(2299, 19, 49, 'چون از آنها و آنچه جز خداى يكتا مى‌پرستيدند كناره گرفت، اسحاق و يعقوب را به او بخشيديم و همه را پيامبرى داديم،'),
(2300, 19, 50, 'و رحمت خويش را به آنها ارزانى داشتيم و سخن نيكو و آوازه بلند داديم.'),
(2301, 19, 51, 'و در اين كتاب، موسى را ياد كن. او بنده‌اى مخلص و فرستاده‌اى پيامبر بود.'),
(2302, 19, 52, 'و او را از جانب راست كوه طور ندا داديم و نزديكش ساختيم تا با او راز گوييم.'),
(2303, 19, 53, 'و از رحمت خود برادرش هارون پيامبر را به او بخشيديم.'),
(2304, 19, 54, 'و در اين كتاب اسماعيل را ياد كن. او درست‌قول و فرستاده‌اى پيامبر بود،'),
(2305, 19, 55, 'و خاندان خود را به نماز و زكات امر مى‌كرد و نزد پروردگارش شايسته و پسنديده بود.'),
(2306, 19, 56, 'و در اين كتاب ادريس را ياد كن. او راست‌گفتارى پيامبر بود.'),
(2307, 19, 57, 'او را به مكانى بلند فرا برديم.'),
(2308, 19, 58, 'اينان گروهى از پيامبران بودند كه خدا به آنان انعام كرده بود، از فرزندان آدم و فرزندان آنان كه با نوح در كشتى نشانديم و فرزندان ابراهيم و اسرائيل و آنها كه هدايتشان كرديم و برگزيديمشان. و چون آيات خداى رحمان بر آنان تلاوت مى‌شد، گريان به سجده مى‌افتادند.'),
(2309, 19, 59, 'سپس كسانى جانشين اينان شدند كه نماز را ضايع گذاشتند و پيرو شهوات گرديدند و به زودى به غى خواهند افتاد،'),
(2310, 19, 60, 'مگر آن كس كه توبه كرد و ايمان آورد و كارهاى شايسته كرد. اينان به بهشت داخل مى‌شوند و هيچ مورد ستم قرار نمى‌گيرند.'),
(2311, 19, 61, 'بهشتهاى جاويد كه خداى رحمان در غيب به بندگان خويش وعده كرده است و وعده او فرا خواهد رسيد.'),
(2312, 19, 62, 'در آنجا سخن لغو نمى‌شنوند، گفتارشان سلام است. و روزيشان هر صبح و شام آماده است.'),
(2313, 19, 63, 'اين همان بهشتى است كه به بندگانمان، آنها كه پرهيزگارى كرده‌اند، عطا مى‌كنيم.'),
(2314, 19, 64, 'و ما جز به فرمان پروردگار تو فرود نمى‌آييم. آنچه در پيش روى ما و پشت سر ما و ميان اين دو قرار دارد از آنِ اوست. و پروردگار تو فراموشكار نيست.'),
(2315, 19, 65, 'پروردگار آسمانها و زمين و آنچه ميان آن دوست. او را بپرست و در پرستش شكيبا باش. آيا همنامى براى او مى‌شناسى؟'),
(2316, 19, 66, 'آدمى مى‌گويد: آيا زمانى كه بميرم، زنده از گور بيرون آورده خواهم شد؟'),
(2317, 19, 67, 'و آدمى به ياد ندارد كه او را پيش از اين آفريده‌ايم و او خود چيزى نبوده است؟'),
(2318, 19, 68, 'به پروردگارت سوگند، آنها را با شيطانها گردمى‌آوريم؛ آنگاه همه را در اطراف جهنم به زانو مى‌نشانيم.'),
(2319, 19, 69, 'آنگاه از هر گروه كسانى را كه در برابر خداى رحمان بيشتر سركشى كرده‌اند جدا مى‌كنيم.'),
(2320, 19, 70, 'و ما آنهايى را كه سزاوارتر به داخل شدن در آتش و سوختن در آن باشند، بهتر مى‌شناسيم.'),
(2321, 19, 71, 'و هيچ يك از شما نيست كه وارد جهنم نشود، و اين حكمى است حتمى از جانب پروردگار تو.'),
(2322, 19, 72, 'آنگاه پرهيزگاران را نجات مى‌دهيم و ستمكاران را همچنان به زانونشسته در آنجا وامى‌گذاريم.'),
(2323, 19, 73, 'چون آيات روشنگر ما را بر آنان بخوانند، كافران به مؤمنان مى‌گويند: كدام يك از دو فرقه را جايگاه بهتر و محفل نيكوتر است؟'),
(2324, 19, 74, 'پيش از آنها چه بسا مردمى را به هلاكت رسانيده‌ايم كه از حيث اثاث و منظر بهتر از آنها بودند.'),
(2325, 19, 75, 'بگو: هر كس كه در گمراهى باشد، خداى رحمان او را به فزونى مدد مى‌رساند، تا آنگاه آنچه را به او وعده داده شده است بنگرد: يا عذاب و يا قيامت. آنگاه خواهند دانست كه چه كسى را جايگاه بدتر و سپاه ناتوان‌تر است.'),
(2326, 19, 76, 'و خدا بر هدايت آنان كه هدايت يافته‌اند خواهد افزود و نزد پروردگار تو پاداش و نتيجه كردارهاى شايسته‌اى كه باقى‌ماندنى‌اند بهتر است.'),
(2327, 19, 77, 'آيا آن كسى را كه به آيات ما كافر بود ديدى كه مى‌گفت: البته به من مال و فرزند داده خواهد شد.'),
(2328, 19, 78, 'آيا از غيب آگاهى داشت، يا از خداى رحمان پيمان گرفته بود؟'),
(2329, 19, 79, 'نه چنين است. آنچه مى‌گويد خواهيم نوشت و البته بر عذابش خواهيم افزود.'),
(2330, 19, 80, 'آنچه را كه مى‌گويد از او مى‌ستانيم تا تنها نزد ما بيايد.'),
(2331, 19, 81, 'و به غير از اللّه خدايانى اختيار كردند تا سبب عزتشان گردند.'),
(2332, 19, 82, 'نه چنين است. به زودى عبادتشان را انكار كنند و به مخالفتشان برخيزند.'),
(2333, 19, 83, 'آيا نديده‌اى كه ما شياطين را به سوى كافران فرستاديم تا آنها را برانگيزند؟'),
(2334, 19, 84, 'پس تو بر آنان شتاب مكن كه ما روزهايشان را به دقت مى‌شماريم.'),
(2335, 19, 85, 'روزى كه پرهيزگاران را نزد خداى رحمان سواره گرد آوريم.'),
(2336, 19, 86, 'و مجرمان را تشنه‌كام به جهنم برانيم.'),
(2337, 19, 87, 'از شفاعت بى‌نصيبند، مگر آن كس كه با خداى رحمان پيمانى بسته باشد.'),
(2338, 19, 88, 'گفتند: خداى رحمان صاحب فرزندى است.'),
(2339, 19, 89, 'هر آينه سخنى زشت آورده‌ايد.'),
(2340, 19, 90, 'نزديك است كه از اين سخن آسمانها بشكافند و زمين شكافته شود و كوه‌ها فروافتند و در هم ريزند،'),
(2341, 19, 91, 'زيرا براى خداى رحمان فرزندى قائل شده‌اند.'),
(2342, 19, 92, 'خداى رحمان را سزاوار نيست كه صاحب فرزندى باشد.'),
(2343, 19, 93, 'هيچ چيز در آسمانها و زمين نيست مگر آنكه به بندگى سوى خداى رحمان بيايد.'),
(2344, 19, 94, 'او همه را حساب كرده و يك‌يك شمرده است.'),
(2345, 19, 95, 'و همگيشان در روز قيامت تنها نزد او مى‌آيند.'),
(2346, 19, 96, 'خداى رحمان كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، محبوب همه گرداند.'),
(2347, 19, 97, 'اين قرآن را بر زبان تو آسان كرديم تا پرهيزگاران را مژده دهى و ستيزه‌گران را بترسانى.'),
(2348, 19, 98, 'و چه بسيار مردمى را پيش از آنها هلاك كرده‌ايم. آيا هيچ يك از آنها را مى‌يابى يا حتى اندك آوازى از آنها مى‌شنوى؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طا، ها.'),
(2350, 20, 2, 'قرآن را بر تو نازل نكرده‌ايم كه در رنج افتى.'),
(2351, 20, 3, 'تنها هشدارى است براى آن كه مى‌ترسد.'),
(2352, 20, 4, 'از جانب كسى كه زمين و آسمانهاى بلند را آفريده است نازل شده.'),
(2353, 20, 5, 'خداى رحمان بر عرش استيلا دارد.'),
(2354, 20, 6, 'از آنِ اوست آنچه در آسمانها و زمين و ميان آنهاست و آنچه در زير زمين است.'),
(2355, 20, 7, 'و اگر سخن بلند گويى، او به راز نهان و نهان‌تر آگاه است.'),
(2356, 20, 8, 'اللّه، آن كه هيچ خدايى جز او نيست، نامهاى خوب از آن اوست.'),
(2357, 20, 9, 'آيا خبر موسى به تو رسيده است؟'),
(2358, 20, 10, 'آنگاه كه آتشى ديد و به خانواده خود گفت: درنگ كنيد، كه من از دور آتشى مى‌بينم، شايد برايتان قبسى بياورم يا در روشنايى آن راهى بيابم.'),
(2359, 20, 11, 'چون نزد آتش آمد، ندا داده شد: اى موسى،'),
(2360, 20, 12, 'من پروردگار تو هستم. پاى‌افزارت را بيرون كن كه اينك در وادى مقدس طوى هستى.'),
(2361, 20, 13, 'و من تو را برگزيده‌ام. پس به آنچه وحى مى‌شود گوش فرا دار.'),
(2362, 20, 14, 'خداى يكتا من هستم. هيچ خدايى جز من نيست. پس مرا بپرست و تا مرا ياد كنى نماز بگزار.'),
(2363, 20, 15, 'قيامت آمدنى است. مى‌خواهم زمان آن را پنهان دارم تا هر كس در مقابل كارى كه كرده است سزا ببيند.'),
(2364, 20, 16, 'آن كس كه به آن ايمان ندارد و پيرو هواى خويش است، تو را از آن رويگردان نكند، تا به هلاكت افتى.'),
(2365, 20, 17, 'اى موسى، آن چيست به دست راستت؟'),
(2366, 20, 18, 'گفت: اين عصاى من است. بر آن تكيه مى‌كنم و براى گوسفندانم با آن برگ مى‌ريزم. و مرا با آن كارهاى ديگر است.'),
(2367, 20, 19, 'گفت: اى موسى، آن را بيفكن.'),
(2368, 20, 20, 'بيفكندش. به ناگهان مارى شد كه مى‌دويد.'),
(2369, 20, 21, 'گفت: بگيرش، و مترس. بار ديگر آن را به صورت نخستينش بازمى‌گردانيم.'),
(2370, 20, 22, 'دست خويش در بغل كن، بى‌هيچ عيبى، سفيد بيرون آيد. اين هم آيتى ديگر،'),
(2371, 20, 23, 'تا آيتهاى بزرگ‌تر خود را به تو بنمايانيم.'),
(2372, 20, 24, 'نزد فرعون برو كه سركشى مى‌كند.'),
(2373, 20, 25, 'گفت: اى پروردگار من، سينه مرا براى من گشاده گردان.'),
(2374, 20, 26, 'و كار مرا آسان ساز.'),
(2375, 20, 27, 'و گره از زبان من بگشاى.'),
(2376, 20, 28, 'تا گفتار مرا بفهمند.'),
(2377, 20, 29, 'و ياورى از خاندان من براى من قرار ده؛'),
(2378, 20, 30, 'برادرم هارون را.'),
(2379, 20, 31, 'پشت مرا بدو محكم كن.'),
(2380, 20, 32, 'و در كار من شريكش گردان.'),
(2381, 20, 33, 'تا تو را فراوان ستايش كنيم.'),
(2382, 20, 34, 'و تو را فراوان ياد كنيم.'),
(2383, 20, 35, 'كه تو بر حال ما آگاه بوده‌اى.'),
(2384, 20, 36, 'گفت: اى موسى، هر چه خواستى به تو داده شد.'),
(2385, 20, 37, 'و ما بار ديگر به تو نعمت فراوان داده‌ايم،'),
(2386, 20, 38, 'آنگاه كه بر مادرت آنچه وحى كردنى بود وحى كرديم:'),
(2387, 20, 39, 'كه او را در صندوقى بيفكن، صندوق را به دريا افكن، تا دريا به ساحلش اندازد و يكى از دشمنان من و دشمنان او صندوق را برگيرد. محبت خويش بر تو ارزانى داشتم تا زير نظر من پرورش يابى.'),
(2388, 20, 40, 'آنگاه كه خواهرت مى‌رفت و مى‌گفت: مى‌خواهيد شما را به كسى كه نگهداريش كند راه بنمايم؟ ما تو را نزد مادرت باز گردانيديم تا چشمانش روشن گردد و غم نخورد. و تو يكى را بكشتى و ما از غم آزادت كرديم و بارها تو را بيازموديم. و سالى چند ميان مردم مدين زيستى. و اكنون، اى موسى، در آن هنگام كه مقدّر كرده بوديم آمده‌اى.'),
(2389, 20, 41, 'تو را خاص خود كردم.'),
(2390, 20, 42, 'تو و برادرت آيات مرا ببريد و در رسالت من سستى مكنيد.'),
(2391, 20, 43, 'به سوى فرعون برويد كه او طغيان كرده است.'),
(2392, 20, 44, 'با او به نرمى سخن گوييد، شايد پند گيرد، يا بترسد.'),
(2393, 20, 45, 'گفتند: اى پروردگار ما، بيم داريم كه بر ما تعدى كند يا طغيان از حد بگذراند.'),
(2394, 20, 46, 'گفت: مترسيد. من با شما هستم. مى‌شنوم و مى‌بينم.'),
(2395, 20, 47, 'پس نزد او رويد و گوييد: ما رسولان پروردگار توايم. بنى‌اسرائيل را با ما بفرست و آزارشان مده. ما نشانه‌اى از پروردگارت برايت آورده‌ايم. و سلام بر آن كس كه از پى هدايت قدم نهد.'),
(2396, 20, 48, 'هرآينه به ما وحى شده است كه عذاب براى آن كسى است كه راست را دروغ شمارد و از آن رخ برتابد.'),
(2397, 20, 49, 'گفت: اى موسى، پروردگار شما كيست؟'),
(2398, 20, 50, 'گفت: پروردگار ما همان كسى است كه آفرينش هر چيزى را به او ارزانى داشته، سپس هدايتش كرده است.'),
(2399, 20, 51, 'گفت: حال اقوامى كه از اين پيش مى‌زيسته‌اند چيست؟'),
(2400, 20, 52, 'گفت: علم آن در كتابى است نزد پروردگار من. پروردگار من نه خطا مى‌كند و نه فراموش.'),
(2401, 20, 53, 'كسى است كه زمين را آرامگاه شما ساخت و برايتان در آن راه‌هايى پديد آورد و از آسمان باران فرستاد، تا بدان انواعى گوناگون از نباتات برويانيم.'),
(2402, 20, 54, 'بخوريد و چارپايانتان را بچرانيد. در اين براى خردمندان عبرتهاست.'),
(2403, 20, 55, 'شما را از زمين آفريديم و به آن باز مى‌گردانيم و بار ديگر از آن بيرون مى‌آوريم.'),
(2404, 20, 56, 'هرآينه همه آيات خود را به او نشان داديم ولى تكذيبش كرد و سر باز زد.'),
(2405, 20, 57, 'گفت: اى موسى، آيا نزد ما آمده‌اى تا ما را به جادويى از سرزمينمان بيرون كنى؟'),
(2406, 20, 58, 'ما نيز در برابر تو جادويى چون جادوى تو مى‌آوريم. بين ما و خود وعده گاهى در زمينى هموار بگذار كه نه ما وعده را خلاف كنيم و نه تو.'),
(2407, 20, 59, 'گفت: موعد شما روز زينت است و همه مردم بدان هنگام كه آفتاب بلند گردد گرد آيند.'),
(2408, 20, 60, 'فرعون بازگشت و ياران حيله‌گر خويش را گرد كرد و باز آمد.'),
(2409, 20, 61, 'موسى گفتشان: واى بر شما، بر خدا دروغ مبنديد كه همه شما را به عذابى هلاك كند و هر كه دروغ بندد نوميد شود.'),
(2410, 20, 62, 'پس با يكديگر در كار خود به مشورت پرداختند. و پنهان رازها گفتند.'),
(2411, 20, 63, 'گفتند: اين دو جادوگرانى هستند كه مى‌خواهند به جادوى خويش شما را از سرزمينتان برانند و آيين برتر شما را براندازند.'),
(2412, 20, 64, 'شما تدبيرهاى خود گرد كنيد و در يك صف بياييد. كسى كه امروز پيروز شود، رستگارى يابد.'),
(2413, 20, 65, 'گفتند: اى موسى، آيا تو مى‌افكنى يا ما نخست بيفكنيم؟'),
(2414, 20, 66, 'گفت: شما بيفكنيد. ناگهان از جادويى كه كردند چنان در نظرش آمد كه آن رسنها و عصاها به هر سو مى‌دوند.'),
(2415, 20, 67, 'موسى خويشتن را ترسان يافت.'),
(2416, 20, 68, 'گفتيم: مترس كه تو برتر هستى.'),
(2417, 20, 69, 'آنچه در دست راست دارى بيفكن تا هر چه را كه ساخته‌اند ببلعد. آنان حيله جادوان ساخته‌اند و جادوگر هيچ گاه پيروز نمى‌شود.'),
(2418, 20, 70, 'ساحران به سجده وادار شدند. گفتند: به پروردگار هارون و موسى ايمان آورديم.'),
(2419, 20, 71, 'فرعون گفت: آيا پيش از آنكه شما را رخصت دهم به او ايمان آورديد؟ او بزرگ شماست كه به شما جادوگرى آموخته است. دستها و پاهاتان را از چپ و راست مى‌برم و بر تنه درخت خرما به دارتان مى‌آويزم. تا بدانيد كه عذاب كدام يك از ما سخت‌تر و پاينده‌تر است.'),
(2420, 20, 72, 'گفتند: سوگند به آن كه ما را آفريده است كه تو را بر آن نشانه‌هاى روشن كه ديده‌ايم ترجيح نمى‌دهيم؛ به هر چه خواهى حكم كن، كه رواج حكم تو در زندگى اينجهانى است.'),
(2421, 20, 73, 'اينك به پروردگار خويش ايمان آورديم تا از خطاهاى ما و آن جادوگرى كه ما را بدان واداشتى درگذرد، كه خدا بهتر و پايدارتر است.'),
(2422, 20, 74, 'هرآينه هر كس كه گنهكار نزد پروردگارش بيايد، جهنم جايگاه اوست كه در آنجا نه مى‌ميرد و نه زندگى مى‌كند.'),
(2423, 20, 75, 'و آنان كه با ايمان نزد او آيند و كارهاى شايسته كنند، صاحب درجاتى بلند باشند.'),
(2424, 20, 76, 'بهشتهاى جاويدان كه در آن نهرها روان است، و همواره در آنجا باشند. و اين است پاداش پاكان.'),
(2425, 20, 77, 'به موسى وحى كرديم كه بندگان ما را شب‌هنگام بيرون ببر و برايشان در دريا گذرگاهى خشك بجوى، و مترس كه بر تو دست يابند و بيم به دل راه مده.'),
(2426, 20, 78, 'فرعون با لشكرهايش از پيشان روان شد. و دريا چنان كه بايد آنان را در خود فرو پوشيد.'),
(2427, 20, 79, 'فرعون قومش را گمراه كرد، نه راهنمايى.'),
(2428, 20, 80, 'اى بنى‌اسرائيل، شما را از دشمنتان رهانيديم و با شما در جانب راست كوه طور وعده نهاديم و برايتان مَنّ و سَلوى نازل كرديم.'),
(2429, 20, 81, 'از چيزهاى پاكيزه كه شما را روزى داده‌ايم بخوريد و از حد مگذرانيدش تا مباد خشم من به شما رسد، كه هر كس كه خشم من به او برسد در آتش افتد.'),
(2430, 20, 82, 'هر كس كه توبه كند و ايمان آورد و كار شايسته كند و به راه هدايت بيفتد، مى‌آمرزمش.'),
(2431, 20, 83, 'اى موسى، چه چيز تو را واداشت تا بر قومت پيشى گيرى؟'),
(2432, 20, 84, 'گفت: آنها همانهايند كه از پى من روانند. اى پروردگار من، من به سوى تو شتافتم تا خشنود گردى.'),
(2433, 20, 85, 'گفت: ما قوم تو را پس از تو آزمايش كرديم و سامرى گمراهشان ساخت.'),
(2434, 20, 86, 'موسى خشمگين و پُر اندوه نزد قومش بازگشت و گفت: اى قوم من، آيا پروردگارتان شما را وعده‌هاى نيكو نداده بود؟ آيا درنگ من به درازا كشيد يا خواستيد كه خشم پروردگارتان به شما فرود آيد كه وعده مرا خلاف كرديد؟'),
(2435, 20, 87, 'گفتند: ما به اختيار خويش وعده تو خلاف نكرديم. بارهايى از زينت آن قوم بر دوش داشتيم. آنها را در آتش بيفكنديم. و سامرى نيز بيفكند.'),
(2436, 20, 88, 'و برايشان تنديس گوساله‌اى كه نعره گاوان را داشت بساخت و گفتند: اين خداى شما و خداى موسى است. و موسى فراموش كرده بود.'),
(2437, 20, 89, 'آيا نمى‌ديدند كه هيچ پاسخى به سخنشان نمى‌دهد، و هيچ سود و زيانى برايشان ندارد؟'),
(2438, 20, 90, 'هارون نيز پيش از اين به آنها گفته بود: اى قوم من، شما را به اين گوساله آزموده‌اند. پروردگار شما خداى رحمان است. از پى من بياييد و فرمانبردار من باشيد.'),
(2439, 20, 91, 'گفتند: ما همواره به پرستش او مى‌نشينيم تا موسى به نزد ما باز گردد.'),
(2440, 20, 92, 'گفت: اى هارون، هنگامى كه ديدى گمراه مى‌شوند،'),
(2441, 20, 93, 'چرا از پى من نيامدى؟ آيا تو نيز از فرمان من سرپيچى كرده بودى؟'),
(2442, 20, 94, 'گفت: اى پسر مادرم، چنگ به ريش و سر من مينداز. من ترسيدم كه بگويى: تو ميان بنى‌اسرائيل جدايى افكندى، و گفتار مرا رعايت نكردى.'),
(2443, 20, 95, 'گفت: و تو اى سامرى، اين چه كارى بود كه كردى؟'),
(2444, 20, 96, 'گفت: من چيزى ديدم كه آنها نمى‌ديدند. مشتى از خاكى كه نقش پاى آن رسول بر آن بود برگرفتم و در آن پيكر افكندم و نفس من اين كار را در چشم من بياراست.'),
(2445, 20, 97, 'گفت: برو، در زندگى اين دنيا چنان شوى كه پيوسته بگويى: «به من نزديك مشو.» و نيز تو را وعده‌اى است كه از آن رها نشوى و اينك به خدايت كه پيوسته عبادتش مى‌كردى بنگر كه مى‌سوزانيمش و به دريايش مى‌افشانيم.'),
(2446, 20, 98, 'جز اين نيست كه خداى شما اللّه است كه هيچ خدايى جز او نيست و علمش همه چيز را در برگرفته است.'),
(2447, 20, 99, 'اينچنين خبرهاى گذشته را براى تو حكايت مى‌كنيم. و به تو از جانب خود قرآن را عطا كرديم.'),
(2448, 20, 100, 'هر كس كه از آن رخ برتابد روز قيامت بار گناه بر دوش مى‌كشد.'),
(2449, 20, 101, 'در آن كار همواره بمانند. و بار روز قيامت برايشان بار بدى است.'),
(2450, 20, 102, 'روزى كه در صور دميده شود و مجرمان را در آن روز، كبودچشم گرد مى‌آوريم.'),
(2451, 20, 103, 'آهسته با هم سخن مى‌گويند كه ده روز بيش نيارميده‌ايد.'),
(2452, 20, 104, 'ما به گفتار آنان آگاه‌تريم آنگاه كه نيكوروشترين آنها مى‌گويد: جز يك روز نيارميده‌ايد.'),
(2453, 20, 105, 'تو را از كوه‌ها مى‌پرسند. بگو: پروردگار من همه را پراكنده مى‌سازد.'),
(2454, 20, 106, 'و آنها را به زمينى هموار بدل مى‌كند؛'),
(2455, 20, 107, 'در آن هيچ كجى و پستى و بلندى نمى‌بينى.'),
(2456, 20, 108, 'در آن روز از پى آن داعى كه هيچ كژى را در او راه نيست روان گردند. و صداها در برابر خداى رحمان به خشوع مى‌گرايد و از هيچ كس جز صداى پايى نخواهى شنيد.'),
(2457, 20, 109, 'در آن روز شفاعت سود ندهد، مگر آن را كه خداى رحمان اجازت دهد و سخنش را بپسندد.'),
(2458, 20, 110, 'هر چه را در پيش روى آنهاست و هر چه را در پشت سرشان است مى‌داند و علم آنان او را در بَر نگيرد.'),
(2459, 20, 111, 'و چهره‌ها در برابر خداى زنده پاينده خاضع مى‌شود و هر كه بار كفر بر دوش مى‌كشد نوميد مى‌گردد.'),
(2460, 20, 112, 'و هر كس كه كارهاى شايسته كند و مؤمن باشد، نبايد از هيچ ستمى و شكستى بهراسد.'),
(2461, 20, 113, 'اينچنين آن را قرآنى عربى نازل كرديم و در آن گونه‌گون هشدار داديم، شايد بترسند يا پندى تازه گيرند.'),
(2462, 20, 114, 'پس برتر است خداى يكتا آن پادشاه راستين. و پيش از آنكه وحى به پايان رسد در خواندن قرآن شتاب مكن. و بگو: اى پروردگار من، به علم من بيفزاى.'),
(2463, 20, 115, 'و ما پيش از اين با آدم پيمان بستيم ولى فراموش كرد، و شكيبايش نيافتيم.'),
(2464, 20, 116, 'و آنگاه كه به فرشتگان گفتيم: آدم را سجده كنيد؛ همه جز ابليس -كه سرپيچى كرد- سجده كردند.'),
(2465, 20, 117, 'گفتيم: اى آدم، اين دشمن تو و همسر توست، شما را از بهشت بيرون نكند، كه نگونبخت شوى.'),
(2466, 20, 118, 'كه تو در بهشت نه گرسنه مى‌شوى و نه برهنه مى‌مانى.'),
(2467, 20, 119, 'و نه تشنه مى‌شوى و نه دچار تابش آفتاب.'),
(2468, 20, 120, 'شيطان وسوسه‌اش كرد و گفت: اى آدم، آيا تو را به درخت جاويدانى و مُلكى زوال ناپذير راه بنمايم؟'),
(2469, 20, 121, 'از آن درخت خوردند و شرمگاهشان در نظرشان پديدار شد. و همچنان برگ درختان بهشت بر آنها مى‌چسبانيدند. آدم در پروردگار خويش عاصى شد و راه گم كرد.'),
(2470, 20, 122, 'سپس پروردگارش او را برگزيد و توبه‌اش را بپذيرفت و هدايتش كرد.'),
(2471, 20, 123, 'گفت: همگى از آنجا پايين رويد؛ دشمنان يكديگر. اگر از جانب من شما را راهنمايى آمد، هر كس از آن راهنماى من متابعت كند نه گمراه مى‌شود و نه تيره‌بخت.'),
(2472, 20, 124, 'و هر كس كه از ياد من اعراض كند، زندگيش تنگ شود و در روز قيامت نابينا محشورش سازيم.'),
(2473, 20, 125, 'گويد: اى پروردگار من، چرا مرا نابينا محشور كردى و حال آنكه من بينا بودم؟'),
(2474, 20, 126, 'گويد: همچنان كه تو آيات ما را فراموش مى‌كردى، امروز خود فراموش گشته‌اى.'),
(2475, 20, 127, 'اينچنين اسرافكاران و كسانى را كه به آيات پروردگارشان ايمان نمى‌آورند جزا مى‌دهيم. هر آينه عذاب آخرت سخت‌تر و پايدارتر است.'),
(2476, 20, 128, 'آيا آن همه مردمى كه پيش از اين در مساكن خويش راه مى‌رفتند و ما همه را هلاك كرديم، سبب هدايت اينان نشده‌اند؟ اينها نشانه‌هايى است براى خردمندان.'),
(2477, 20, 129, 'اگر نه سخنى بود كه پروردگارت پيش از اين گفته و زمان را معين كرده بود، عذابشان در اين جهان حتمى مى‌بود.'),
(2478, 20, 130, 'بر آنچه مى‌گويند شكيبا باش و پروردگارت را پيش از طلوع خورشيد و پيش از غروب آن به پاكى بستاى. و در ساعات شب و اول و آخر روز تسبيح گوى. شايد خشنود گردى.'),
(2479, 20, 131, 'اگر زنان و مردانى از آنها را از يك زندگى خوش بهره‌مند ساخته‌ايم، تو به آنها منگر. اين براى آن است كه امتحانشان كنيم. رزق پروردگارت بهتر و پايدارتر است.'),
(2480, 20, 132, 'كسان خود را به نماز فرمان ده و خود در آن كار پاى بيفشر. از تو روزى نمى‌خواهيم. ما به تو روزى مى‌دهيم. و عاقبت خير از آنِ پرهيزگاران است.'),
(2481, 20, 133, 'و گفتند: چرا معجزه‌اى از پروردگارش براى ما نمى‌آورد؟ آيا دليلهاى روشنى كه در صحيفه‌هاى پيشين آمده، به آنها نرسيده است؟'),
(2482, 20, 134, 'اگر پيش از آمدن پيامبرى عذابشان مى‌كرديم، مى‌گفتند: اى پروردگار ما، چرا رسولى بر ما نفرستادى تا پيش از آنكه به خوارى و رسوايى افتيم، از آيات تو پيروى كنيم؟'),
(2483, 20, 135, 'بگو: همه منتظرند، شما نيز منتظر بمانيد. به زودى خواهيد دانست آنها كه به راه راست مى‌روند و آنها كه هدايت يافته‌اند، چه كسانى هستند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'روز حساب مردم نزديك شد و آنان همچنان به غفلت رويگردانند.'),
(2485, 21, 2, 'از سوى پروردگارشان برايشان هيچ اندرز تازه‌اى نيامد، جز آنكه آن را شنيدند و سرگرم بازيچه بودند.'),
(2486, 21, 3, 'دلهايشان به لهو خو گرفته است. و آن ستمگران سر در گوش يكديگر نهادند و گفتند: آيا اين مرد جز اين است كه انسانى همانند شماست؟ آيا با آنكه به چشم خود مى‌بينيد، همچنان از پى جادو مى‌رويد؟'),
(2487, 21, 4, 'گفت: پروردگار من از هر سخنى در آسمان و زمين آگاه است و او شنوا و داناست.'),
(2488, 21, 5, 'گفتند: نه، خوابهاى پريشان است، يا دروغى است كه مى‌بندد يا شاعرى است. پس براى ما از آن گونه كه به پيامبران پيشين داده شده بود معجزه‌اى بياورد.'),
(2489, 21, 6, 'پيش از آنها مردم قريه‌اى را كه هلاك كرديم ايمان نياورده بودند. آيا اينان ايمان مى‌آورند؟'),
(2490, 21, 7, 'ما پيش از تو به رسالت نفرستاديم مگر مردانى را كه به آنها وحى مى‌كرديم و اگر خود نمى‌دانيد از اهل كتاب بپرسيد.'),
(2491, 21, 8, 'آنها را كالبدهايى نكرديم كه به طعام نيازمند نباشند و عمر جاويدان هم نداشتند.'),
(2492, 21, 9, 'سپس هر وعده‌اى كه با ايشان نهاديم برآورديم، و آنها و هر كس را كه خواستيم رهانيديم و گزافكاران را هلاك كرديم.'),
(2493, 21, 10, 'كتابى بر شما نازل كرده‌ايم كه در آن آوازه بزرگى شماست. آيا به عقل در نمى‌يابيد؟'),
(2494, 21, 11, 'و چه بسيار ستمكاره مردمى را كه هلاك كرديم و به جايشان مردمى ديگر بيافريديم.'),
(2495, 21, 12, 'چون عذاب ما را حس مى‌كردند به ناگاه از آنجا مى‌گريختند.'),
(2496, 21, 13, 'مگريزيد. به ناز و تنعم و خانه‌هاى خويش بازگرديد، تا بازخواست گرديد.'),
(2497, 21, 14, 'گفتند: واى بر ما، ما ستمكاره بوده‌ايم.'),
(2498, 21, 15, 'و همواره سخنشان اين بود، تا همه را چون كِشته درويده و آتش خاموش گشته گردانيديم.'),
(2499, 21, 16, 'ما اين آسمان و زمين و آنچه را ميان آن دوست به بازيچه نيافريده‌ايم.'),
(2500, 21, 17, 'اگر خواستار بازيچه‌اى مى‌بوديم خود آن را مى‌آفريديم، اگر خواسته بوديم.'),
(2501, 21, 18, 'بلكه حق را بر سر باطل مى‌زنيم، تا آن را درهم كوبد و باطل نابود شونده است. و واى بر شما از آنچه به خدا نسبت مى‌دهيد.'),
(2502, 21, 19, 'از آنِ اوست هر كه در آسمانها و زمين است. و آنان كه در نزد او هستند، از عبادتش به تكبر سر نمى‌تابند و خسته نمى‌شوند.'),
(2503, 21, 20, 'شب و روز، بى آنكه فتورى در آنان پديد آيد، تسبيح مى‌گويند.'),
(2504, 21, 21, 'آيا در زمين خدايانى برگزيده‌اند كه مردگان را از گور برمى‌انگيزند؟'),
(2505, 21, 22, 'اگر در زمين و آسمان خدايانى جز اللّه مى‌بود، هر دو تباه مى‌شدند. پس اللّه، پروردگار عرش، از هر چه به وصفش مى‌گويند منزه است.'),
(2506, 21, 23, 'او در برابر هيچ يك از كارهايى كه مى‌كند، بازخواست نمى‌شود ولى مردم بازخواست مى‌شوند.'),
(2507, 21, 24, 'آيا به جز او خدايانى را برگزيده‌اند؟ بگو: حجت خويش بياوريد. در اين كتاب سخن كسانى است كه با من هستند و سخن كسانى كه پيش از من بوده‌اند. نه، بيشترينشان چيزى از حق نمى‌دانند و از آن اعراض مى‌كنند.'),
(2508, 21, 25, 'ما پيش از تو هيچ پيامبرى نفرستاديم، جز آنكه به او وحى كرديم كه جز من خدايى نيست. پس مرا بپرستيد.'),
(2509, 21, 26, 'و گفتند كه خداى رحمان صاحب فرزند است. منزه است او. بلكه آنان بندگانى گرامى هستند،'),
(2510, 21, 27, 'در سخن بر او پيشى نمى‌گيرند و به فرمان او كار مى‌كنند.'),
(2511, 21, 28, 'مى‌داند، هر چه در برابر آنهاست و هر چه پشت سر آنهاست. و آنان جز كسانى را كه خدا از آنها خشنود است شفاعت نمى‌كنند و از بيم او لرزانند.'),
(2512, 21, 29, 'هر كس از آنها كه بگويد: من سواى او خدايم، جزاى چنين كسى را جهنم مى‌دهيم و ظالمان را چنين كيفر مى‌دهيم.'),
(2513, 21, 30, 'آيا كافران نمى‌دانند كه آسمانها و زمين بسته بودند، ما آنها را گشوديم و هر چيز زنده‌اى را از آب پديد آورديم؟ چرا ايمان نمى‌آورند؟'),
(2514, 21, 31, 'و بر زمين كوه‌ها بيافريديم تا نلرزاندشان. و در آن راه‌هاى فراخ ساختيم، باشد كه راه خويش بيابند.'),
(2515, 21, 32, 'و آسمان را سقفى مصون از تعرض كرديم و باز هم از عبرتهاى آن اعراض مى‌كنند.'),
(2516, 21, 33, 'اوست كسى كه شب و روز و آفتاب و ماه را كه هر يك در فلكى شناورند، بيافريد.'),
(2517, 21, 34, 'ما پيش از تو هيچ بشرى را جاويدان نساخته‌ايم. چسان اگر تو نيز بميرى آنها جاويدان بمانند؟'),
(2518, 21, 35, 'هر كسى طعم مرگ را مى‌چشد. و شما را به خير و شر مى‌آزماييم. و همه به نزد ما بازمى‌گرديد.'),
(2519, 21, 36, 'كافران چون تو را ببينند بى شك مسخره‌ات خواهند كرد، كه: آيا اين همان مردى است كه از خدايانتان به بدى ياد مى‌كند؟ و اينان خود ياد خداى رحمان را منكرند.'),
(2520, 21, 37, 'آدمى شتابكار آفريده شده. آيات خود را به شما نشان خواهم داد، شتاب مكنيد.'),
(2521, 21, 38, 'مى‌گويند: اگر راست مى‌گوييد، پس آن وعده كجاست؟'),
(2522, 21, 39, 'كافران، آنگاه كه نتوانند آتش را از چهره و پشت خويش بازدارند و كسى به ياريشان برنخيزد، خواهند دانست.'),
(2523, 21, 40, 'اما نه، قيامت به ناگهان فراز آيد و حيرانشان سازد و از عهده بازگرداندن آن برنيايند و مهلتشان ندهند.'),
(2524, 21, 41, 'پيامبرانى را كه پيش از تو بودند نيز به مسخره گرفته بودند ولى عذابى كه مسخره‌اش مى‌كردند بر سر مسخره‌كنندگان فرود آمد.'),
(2525, 21, 42, 'بگو: كيست آن كه شما را شب و روز از قهر خداى رحمان حفظ مى‌كند؟ با اين همه از يادكردن پروردگارشان اعراض مى‌كنند.'),
(2526, 21, 43, 'يا آنكه خدايانى دارند كه به جاى ما آنان را از حوادث نگه مى‌دارند؟ نه، آن خدايان يارى خويشتن نتوانند و نه در برابر ما پناه داده شوند.'),
(2527, 21, 44, 'بلكه ما آنها و پدرانشان را بهره‌مند كرديم تا عمرشان به درازا كشيد. آيا نمى‌بينند كه قصد اين سرزمين مى‌كنيم و از اطراف آن مى‌كاهيم؟ آيا باز هم آنها پيروزند؟'),
(2528, 21, 45, 'بگو: من شما را به وحى بيم مى‌دهم. ولى كران را چون بيم دهند، نمى‌شنوند.'),
(2529, 21, 46, 'و اگر شمه‌اى از عذاب پروردگارت به آنها برسد، خواهند گفت: واى بر ما كه ستمكار بوده‌ايم.'),
(2530, 21, 47, 'روز قيامت ترازوهاى عدل را تعبيه مى‌كنيم، و به هيچ كس ستم نمى‌شود. اگر عملى به سنگينى يك خردل هم باشد به حسابش مى‌آوريم، كه ما حساب‌كردن را بسنده‌ايم.'),
(2531, 21, 48, 'به موسى و هارون كتابى داديم كه حق و باطل را از يكديگر تميز مى‌دهد و روشنى و اندرز است براى پرهيزگاران.'),
(2532, 21, 49, 'آنان كه از خداى خويش در نهان مى‌ترسند و از روز قيامت هراسناكند.'),
(2533, 21, 50, 'اين مبارك قرآنى است كه نازل كرده‌ايم. آيا منكر آن هستيد؟'),
(2534, 21, 51, 'پيش از اين به ابراهيم نيروى شناختش را عطا كرديم و به او آگاه بوديم.'),
(2535, 21, 52, 'آنگاه كه به پدرش و قومش گفت: اين تنديسها كه به پرستش آنها دل نهاده‌ايد چيستند؟'),
(2536, 21, 53, 'گفتند: پدرانمان را ديديم كه آنها را مى‌پرستيدند.'),
(2537, 21, 54, 'گفت: هر آينه شما و پدرانتان در گمراهى آشكارى بوده‌ايد.'),
(2538, 21, 55, 'گفتند: براى ما سخن حقى آورده‌اى، يا ما را به بازى گرفته‌اى؟'),
(2539, 21, 56, 'گفت: نه، پروردگار شما، پروردگار آسمانها و زمين است. آن كه آنها را بيافريده است. و من بر اين سخن كه مى‌گويم گواهى مى‌دهم.'),
(2540, 21, 57, 'و به خدا سوگند كه چون شما برويد، چاره اين بتانتان را خواهم كرد.'),
(2541, 21, 58, 'آنها را خرد كرد. مگر بزرگ ترينشان را، تا مگر به آن رجوع كنند.'),
(2542, 21, 59, 'گفتند: چه كس به خدايان ما چنين كرده است؟ هر آينه او از ستمكاران است.'),
(2543, 21, 60, 'گفتند: شنيده‌ايم كه جوانى به نام ابراهيم، از آنها سخن مى‌گفته است.'),
(2544, 21, 61, 'گفتند: او را به محضر مردم بياوريد، تا شهادت دهند.'),
(2545, 21, 62, 'گفتند: اى ابراهيم، تو با خدايان ما چنين كرده‌اى؟'),
(2546, 21, 63, 'گفت: بلكه بزرگ‌ترينشان چنين كرده است. اگر سخن مى‌گويند از آنها بپرسيد.'),
(2547, 21, 64, 'با خودشان گفت‌وگو كردند و گفتند: شما خود ستمكار هستيد.'),
(2548, 21, 65, 'آنگاه به حيرت سر فرو داشتند و گفتند: تو خود مى‌دانى كه اينان سخن نمى‌گويند.'),
(2549, 21, 66, 'گفت: آيا سواى اللّه چيزى را مى‌پرستيد كه نه شما را سود مى‌رساند نه زيان.'),
(2550, 21, 67, 'بيزارم از شما و از آن چيزهايى كه سواى اللّه مى‌پرستيد. آيا به عقل درنمى‌يابيد؟'),
(2551, 21, 68, 'گفتند: اگر مى‌خواهيد كارى بكنيد، بسوزانيدش و خدايان خود را نصرت دهيد.'),
(2552, 21, 69, 'گفتيم: اى آتش، بر ابراهيم خنك و سلامت باش.'),
(2553, 21, 70, 'مى‌خواستند براى ابراهيم مكرى بينديشند، ولى ما زيانكارشان گردانيديم.'),
(2554, 21, 71, 'او و لوط را رهانيديم و به سرزمينى كه آن را بركت جهانيان قرار داده‌ايم، برديم.'),
(2555, 21, 72, 'و به او اسحاق و فرزندزاده‌اى چون يعقوب را بخشيديم. و همه را از شايستگان گردانيديم.'),
(2556, 21, 73, 'و همه را پيشوايانى ساختيم كه به امر ما هدايت مى‌كردند. و انجام دادن كارهاى نيك و برپاى داشتن نماز و دادن زكات را به آنها وحى كرديم و همه پرستنده ما بودند.'),
(2557, 21, 74, 'و به لوط حكم و علم داديم و از آن قريه كه مردمش مرتكب پليديها مى‌شدند نجاتش بخشيديم. آنها كه مردمى بد و فاسق بودند.'),
(2558, 21, 75, 'او را در رحمت خويش داخل كرديم. كه او از شايستگان بود.'),
(2559, 21, 76, 'و نوح را ياد كن كه پيش از آن ما را ندا داد و ما به او پاسخ داديم و او و خاندانش را از محنتى بزرگ رهانيديم.'),
(2560, 21, 77, 'و او را بر مردمى كه آيات ما را تكذيب مى‌كردند پيروزى داديم. آنان بد مردمى بودند و ما همه را غرقه ساختيم.'),
(2561, 21, 78, 'و داود و سليمان را ياد كن آنگاه كه درباره كشتزارى داورى كردند كه گوسفندان آن قوم، شبانه در آنجا مى‌چريدند. و ما شاهد داورى آنها بوديم.'),
(2562, 21, 79, 'و اين شيوه داورى را به سليمان آموختيم و همه را حكم و علم داديم و كوه‌ها را مسخّر داود گردانيديم كه آنها و پرندگان با او تسبيح مى‌گفتند و اين همه ما كرديم.'),
(2563, 21, 80, 'و به او آموختيم تا برايتان زره بسازد. تا شما را به هنگام جنگيدنتان حفظ كند. آيا سپاسگزارى مى‌كنيد؟'),
(2564, 21, 81, 'و تندباد را مسخّر سليمان كرديم كه به امر او در آن سرزمين كه بركتش داده بوديم حركت مى‌كرد. و ما بر هر چيزى آگاهيم.'),
(2565, 21, 82, 'و گروهى از ديوان برايش در دريا غواصى مى‌كردند و جز آن به كارهاى ديگر مشغول بودند و ما حافظ آنها بوديم.'),
(2566, 21, 83, 'و ايّوب را ياد كن آنگاه كه پروردگارش را ندا داد: به من بيمارى و رنج رسيده است و تو مهربان‌ترين مهربانانى.'),
(2567, 21, 84, 'دعايش را اجابت كرديم. و آزار از او دور كرديم و خاندانش را و همانند آنها را با آنها به او بازگردانيديم. و اين رحمتى بود از جانب ما، تا خداپرستان همواره به ياد داشته باشند.'),
(2568, 21, 85, 'و اسماعيل و ادريس و ذوالكفل را ياد كن كه همه از صابران بودند.'),
(2569, 21, 86, 'آنان را مشمول بخشايش خويش گردانيديم و همه از شايستگان بودند.'),
(2570, 21, 87, 'و ذوالنّون را، آنگاه كه خشمناك برفت و پنداشت كه هرگز بر او تنگ نمى‌گيريم. و در تاريكى ندا داد: هيچ خدايى جز تو نيست، تو منزه هستى و من از ستمكاران هستم.'),
(2571, 21, 88, 'دعايش را مستجاب كرديم و او را از اندوه رهانيديم و مؤمنان را اينچنين مى‌رهانيم.'),
(2572, 21, 89, 'و زكريّا را ياد كن، آنگاه كه پروردگارش را ندا داد: اى پروردگار من، مرا تنها وامگذار. و تو بهترين وارثانى.'),
(2573, 21, 90, 'دعايش را مستجاب كرديم و به او يحيى را بخشيديم و زنش را برايش شايسته گردانيديم. اينان در كارهاى نيك شتاب مى‌كردند و با بيم و اميد ما را مى‌خواندند و در برابر ما خاشع بودند.'),
(2574, 21, 91, 'و آن زن را ياد كن كه شرمگاه خود را نگاه داشت و ما از روح خود در او دميديم و او و فرزندش را براى جهانيان عبرتى گردانيديم.'),
(2575, 21, 92, 'اين شريعت شماست، شريعتى يگانه. و من پروردگار شمايم، مرا بپرستيد.'),
(2576, 21, 93, 'و در دين خويش فرقه فرقه شدند. همه به نزد ما بازمى‌گردند.'),
(2577, 21, 94, 'هر كس كه كارى شايسته كند و ايمان آورده باشد، كوشش او را ناسپاسى نيست و ما اعمالش را مى‌نويسيم.'),
(2578, 21, 95, 'و قريه‌اى را كه به هلاكت رسانده‌ايم محال است كه بازگشتى داشته باشند.'),
(2579, 21, 96, 'تا آنگاه كه يأجوج و مأجوج گشوده شوند و آنان را از بلنديها به شتاب سرازير گردند.'),
(2580, 21, 97, 'و آن وعده راستين نزديك گردد و چشمان كافران همچنان خيره ماند: واى بر ما، ما از اين حال غافل بوديم. نه، كه ستمكار مى‌بوديم.'),
(2581, 21, 98, 'شما و آن چيزهايى كه سواى اللّه مى‌پرستيديد هيزمهاى جهنميد. شما به جهنم خواهيد رفت.'),
(2582, 21, 99, 'اگر اينان خدايان مى‌بودند به جهنم نمى‌رفتند و حال آنكه همه در جهنم جاويدانند.'),
(2583, 21, 100, 'آنان در جهنم فرياد مى‌كشند و در آنجا هيچ نمى‌شنوند.'),
(2584, 21, 101, 'كسانى كه پيش از اين مقرر كرده‌ايم كه به آنها نيكويى كنيم، از جهنم بركنارند.'),
(2585, 21, 102, 'اينان صداى دوزخ را نمى‌شنوند و در بهشت كه هر چه خواهند مهياست، جاودانه‌اند.'),
(2586, 21, 103, 'آن وحشت بزرگ غمگينشان نكند. و فرشتگان به ديدارشان آيند كه اين همان روزى است كه به شما وعده داده بودند.'),
(2587, 21, 104, 'روزى كه آسمان را چون طومارى نوشته درهم نورديم. و چنان كه نخستين‌بار بيافريديم آفرينش از سر گيريم. اين وعده‌اى است كه برآوردنش بر عهده ماست و ما چنان خواهيم كرد.'),
(2588, 21, 105, 'و ما در زبور -پس از تورات- نوشته‌ايم كه اين زمين را بندگان صالح من به ميراث خواهند برد.'),
(2589, 21, 106, 'و در اين كتاب تبليغى است براى مردم خداپرست.'),
(2590, 21, 107, 'و نفرستاديم تو را، جز آنكه مى‌خواستيم به مردم جهان رحمتى ارزانى داريم.'),
(2591, 21, 108, 'بگو: به من وحى شده كه خداى شما خدايى است يكتا، آيا بدان گردن مى‌نهيد؟'),
(2592, 21, 109, 'پس اگر رويگردان شدند، بگو: شما را خبر دادم تا همه در آگاهى يكسان باشيد. و من نمى‌دانم آنچه شما را وعده كرده‌اند نزديك است يا دور.'),
(2593, 21, 110, 'اوست كه مى‌داند هر سخنى را كه به آواز بلند گوييد يا در دل پنهان داريد.'),
(2594, 21, 111, 'و نمى‌دانم شايد اين آزمايشى براى شما و بهره‌مندى تا به هنگام مرگ باشد.'),
(2595, 21, 112, 'گفت: اى پروردگار من، به حق داورى كن. و پروردگار ما بخشاينده است و به رغم آنچه مى‌گوييد همه از او يارى مى‌جويند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اى مردم، از پروردگارتان بترسيد، كه زلزله قيامت حادثه بزرگى است.'),
(2597, 22, 2, 'آن روز كه ببينيدش، هر شيردهنده‌اى شيرخواره‌اش را از ياد ببرد و هر آبستنى بار خود بر زمين گذارد و مردم را چون مستان بينى، حال آنكه مست نيستند بلكه عذاب خدا شديد است.'),
(2598, 22, 3, 'بعضى از مردم، بى هيچ دانشى در باره خدا مجادله مى‌كنند و از هر شيطان سركشى پيروى مى‌كنند.'),
(2599, 22, 4, 'بر شيطان چنين مقرر شده كه هر كس را كه دوستش بدارد گمراه كند و به عذاب آتش سوزانش كشاند.'),
(2600, 22, 5, 'اى مردم، اگر از روز رستاخيز در ترديد هستيد، ما شما را از خاك و سپس از نطفه، آنگاه از لخته‌خونى و سپس از پاره‌گوشتى گاه تمام آفريده گاه ناتمام، بيافريده‌ايم، تا قدرت خود را برايتان آشكار كنيم. و تا زمانى معين هر چه را خواهيم در رحمها نگه مى‌داريم. آنگاه شما را كه كودكى هستيد بيرون مى‌آوريم تا به حد زورمندى خود رسيد. بعضى از شما مى‌ميرند و بعضى به سالخوردگى برده مى‌شوند تا آنگاه كه هر چه آموخته‌اند فراموش كنند. و تو زمين را فسرده مى‌بينى. چون باران بر آن بفرستيم، در اهتزاز آيد و نمو كند و از هر گونه گياه بهجت‌انگيز بروياند.'),
(2601, 22, 6, 'و اينها دليل بر آن است كه خدا حق است، مردگان را زنده مى‌سازد و بر هر كارى تواناست.'),
(2602, 22, 7, 'و نيز قيامت خواهد آمد، شكى در آن نيست. و خدا همه كسانى را كه در گورها هستند زنده مى‌كند.'),
(2603, 22, 8, 'از مردم كسى است كه بى هيچ دانشى و هيچ راهنمايى و هيچ كتاب روشنى بخشى در باره خدا مجادله مى‌كند.'),
(2604, 22, 9, 'گردن را به تكبر به يكسو مى‌پيچاند تا مردم را از راه خدا گمراه سازد. نصيب او در دنيا خوارى است و در روز قيامت عذاب آتش را به او مى‌چشانيم:'),
(2605, 22, 10, 'اين كيفر كارهايى است كه پيش از اين كرده‌اى، و خدا به بندگانش ستم نمى‌كند.'),
(2606, 22, 11, 'و از ميان مردم كسى است كه خدا را با ترديد مى‌پرستد. اگر خيرى به او رسد دلش بدان آرام گيرد، و اگر آزمايشى پيش آيد رخ برتابد. در دنيا و آخرت زيان بيند و آن زيانى آشكار است.'),
(2607, 22, 12, 'سواى خدا كسى را مى‌خواند كه نه زيانى به او مى‌رساند و نه سودى، و اين گمراهيى است بى‌انتها.'),
(2608, 22, 13, 'كسى را مى‌خواند كه زيانش نزديك‌تر از سود اوست. چه بد دوستدارى است و چه بد مصاحبى است.'),
(2609, 22, 14, 'خدا كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند به بهشتهايى كه در آن نهرها جارى است داخل مى‌سازد، و خدا هر چه خواهد همان مى‌كند.'),
(2610, 22, 15, 'كسى كه مى‌پندارد كه خدا در دنيا و آخرت ياريش نخواهد كرد، خويشتن را با ريسمانى از سقف بياويزد تا خود را خفه كند و بنگرد كه آيا اين حيله خشمش را از ميان خواهد برد.'),
(2611, 22, 16, 'بدين‌سان قرآن را نازل كرديم، با آياتى روشنگر. و خدا هر كس را كه بخواهد هدايت مى‌كند.'),
(2612, 22, 17, 'خدا ميان آنان كه ايمان آورده‌اند و آنان كه كيش يهود يا صابئان يا نصارى يا مجوس برگزيده‌اند و آنان كه مشرك شده‌اند، در روز قيامت حكم مى‌كند. زيرا او بر هر كارى ناظر است.'),
(2613, 22, 18, 'آيا نديده‌اى كه هر كس در آسمانها و هر كس كه در زمين است و آفتاب و ماه و ستارگان و كوهها و درختان و جنبندگان و بسيارى از مردم خدا را سجده مى‌كنند؟ و بر بسيارى عذاب محقق شده و هر كه را خدا خوار سازد، هيچ كس گراميش نمى‌دارد. زيرا خدا هر چه بخواهد همان مى‌كند.'),
(2614, 22, 19, 'اين دو گروه در باره پروردگارشان به خصومت برخاسته‌اند. براى آنان كه كافرند جامه‌هايى از آتش بريده‌اند و از بالا بر سرشان آب جوشان مى‌ريزد.'),
(2615, 22, 20, 'بدان آب جوشان هر چه در درون شكم دارند و نيز پوستهايشان گداخته مى‌شود.'),
(2616, 22, 21, 'و نيز براى آنهاست گرزهايى آهنين.'),
(2617, 22, 22, 'هرگاه كه خواهند از آن عذاب، از آن اندوه بيرون آيند، بار ديگر آنان را بدان بازگردانند كه: بچشيد عذاب آتش سوزنده را.'),
(2618, 22, 23, 'خدا كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند به بهشتهايى كه در آن نهرها جارى است داخل مى‌سازد. در آنجا با دستبندهايى از طلا و مرواريد آراسته شوند و لباسشان از حرير است.'),
(2619, 22, 24, 'آنان به آن سخن پاك و به راه خداوند ستودنى راهنمايى شده‌اند.'),
(2620, 22, 25, 'آنهايى را كه كافر شدند و مردم را از راه خدا و مسجدالحرام -كه براى مردم چه مقيم و چه غريب يكسان است- بازمى‌دارند و نيز هر كه را در آنجا قصد كجروى يا ستمكارى داشته باشد، عذاب دردآور مى‌چشانيم.'),
(2621, 22, 26, 'و مكان خانه را براى ابراهيم آشكار كرديم و گفتيم: هيچ چيز را شريك من مساز و خانه مرا براى طواف‌كنندگان و به نمازايستادگان و راكعان و ساجدان پاكيزه بدار.'),
(2622, 22, 27, 'و مردم را به حج فرا خوان تا پياده يا سوار بر شتران تكيده از راه‌هاى دور نزد تو بيايند.'),
(2623, 22, 28, 'تا سودهايى را كه از آنِ آنهاست ببينند و نام خدا را در روزهايى معين به هنگام ذبح چارپايانى كه خدا رزق آنها ساخته، ياد كنند. پس از آنها بخوريد و بينوايان فقير را نيز اطعام كنيد.'),
(2624, 22, 29, 'سپس چرك از خود دور كنند و نذرهاى خويش را ادا كنند و بر آن خانه كهنسال طواف كنند.'),
(2625, 22, 30, 'همچنين هر كس دست از حرام بدارد، در نزد پروردگارش برايش بهتر است. و چارپايان بر شما حلالند مگر آنهايى كه برايتان خوانده شده. پس، از بتهاى پليد و سخن باطل و دروغ اجتناب ورزيد.'),
(2626, 22, 31, 'روى آوردگان به خدا باشيد و شرك نياوردگان به او؛ و هر كس كه به خدا شرك آورد، چونان كسى است كه از آسمان فروافتد و مرغ او را بربايد يا بادش به مكانى دور اندازد.'),
(2627, 22, 32, 'آرى، كسانى كه شعاير خدا را بزرگ مى‌شمارند كارشان نشان پرهيزگارى دلهايشان باشد.'),
(2628, 22, 33, 'از آن شتران قربانى تا زمانى معين برايتان سودهاست، سپس جاى قربانيشان در آن خانه كهنسال است.'),
(2629, 22, 34, 'براى هر امتى رسم قربانى كردنى نهاديم تا بدان سبب كه خدا از چارپايان روزيشان داده است، نام او را بر زبان رانند. پس خداى شما خدايى يكتاست، در برابر او تسليم شويد. و تواضع‌كنندگان را بشارت ده،'),
(2630, 22, 35, 'آنان كه چون نام خدا برده شود در دل بترسند و بدان هنگام كه به آنها مصيبتى رسد شكيبايانند و نمازگزارانند و از آنچه روزيشان داده‌ايم انفاق مى‌كنند.'),
(2631, 22, 36, 'شتران قربانى را براى شما از شعاير خدا قرار داديم. شما را در آن خيرى است. و همچنان كه بر پاى ايستاده‌اند نام خدا را بر آنها بخوانيد و چون پهلويشان بر زمين رسيد از آنها بخوريد و فقيران قانع و گدايان را اطعام كنيد. اينها را براى شما رام كرديم. باشد كه سپاسگزارى كنيد.'),
(2632, 22, 37, 'گوشت و خون اين شتران به خدا نمى‌رسد. آنچه به او مى‌رسد پرهيزگارى شماست. همچنين آنها را رام شما ساخت تا خدا را به شكرانه آنكه هدايتتان كرده است، به بزرگى ياد كنيد. و نيكوكاران را بشارت ده.'),
(2633, 22, 38, 'خدا از كسانى كه ايمان آورده‌اند دفاع مى‌كند، و خدا خيانتكاران ناسپاس را دوست ندارد.'),
(2634, 22, 39, 'به كسانى كه به جنگ بر سرشان تاخت آورده‌اند و مورد ستم قرار گرفته‌اند، رخصت داده شد و خدا بر پيروز گردانيدنشان تواناست.'),
(2635, 22, 40, 'آنهايى كه به ناحق از ديارشان رانده شده‌اند جز آن بود كه مى‌گفتند: پروردگار ما خداى يكتاست؟ و اگر خدا بعضى را به وسيله بعضى ديگر دفع نكرده بود، ديرها و كليساها و كنشتها و مسجدهايى كه نام خدا به فراوانى در آن برده مى‌شود ويران مى‌گرديد. و خدا هر كس را كه ياريش كند، يارى مى‌كند و خدا توانا و پيروزمند است.'),
(2636, 22, 41, 'همان كسان كه اگر در زمين مكانتشان دهيم نماز مى‌گزارند و زكات مى‌دهند و امر به معروف و نهى از منكر مى‌كنند. و سرانجام همه كارها با خداست.'),
(2637, 22, 42, 'اگر اينان تو را تكذيب كرده‌اند، پيش از آنها قوم نوح و عاد و ثمود نيز تكذيب كرده‌اند،'),
(2638, 22, 43, 'و نيز قوم ابراهيم و قوم لوط،'),
(2639, 22, 44, 'و مردم مَديَن. و موسى نيز تكذيب گرديد. من به كافران مهلت دادم، آنگاه آنها را فروگرفتم. و عقوبت من چگونه بود؟'),
(2640, 22, 45, 'چه بسيار قريه‌هايى ستم پيشه را هلاك كرديم و سقفهايشان فروريخت و چه بسيار چاه‌ها كه بيكاره ماند و قصرهاى رفيع گچكارى شده، بى‌صاحب.'),
(2641, 22, 46, 'آيا در زمين سير نمى‌كنند تا صاحب دلهايى گردند كه بدان تعقل كنند و گوشهايى كه بدان بشنوند؟ زيرا چشمها نيستند كه كور مى‌شوند، بلكه دلهايى كه در سينه‌ها جاى دارند كور باشند.'),
(2642, 22, 47, 'از تو به شتاب عذاب مى‌طلبند و خدا هرگز وعده خود را خلاف نمى‌كند. و يك روز از روزهاى پروردگار تو برابر با هزار سال است از آن سان كه مى‌شمريد.'),
(2643, 22, 48, 'چه بسيار قريه‌هايى را كه ستم‌پيشه بودند و من مهلتشان دادم، آنگاه آنها را فروگرفتم. و بازگشت همه نزد من است.'),
(2644, 22, 49, 'بگو: اى مردم، من براى شما بيم‌دهنده‌اى آشكارم.'),
(2645, 22, 50, 'پس آنان را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، آمرزش و رزقى كرامند است.'),
(2646, 22, 51, 'و آنان كه در رد آيات ما مى‌كوشند و مى‌خواهند ما را به عجز آورند، اهل جهنمند.'),
(2647, 22, 52, 'ما پيش از تو هيچ رسول يا نبيى را نفرستاديم مگر آنكه چون به خواندن آيات مشغول شد شيطان در سخن او چيزى افكند. و خدا آنچه را كه شيطان افكنده بود نسخ كرد، سپس آيات خويش را استوارى بخشيد. و خدا دانا و حكيم است.'),
(2648, 22, 53, 'چنين شود، تا آنچه شيطان در سخن او افكنده براى كسانى كه در قلبهايشان بيمارى است و نيز سخت‌دلان، آزمايشى باشد، و ستمكاران در اختلافى بزرگ گرفتارند.'),
(2649, 22, 54, 'و تا دانش‌يافتگان بدانند كه قرآن به راستى از جانب پروردگار توست و بدان ايمان بياورند و دلهايشان بدان آرام گيرد. و خدا كسانى را كه ايمان آورده‌اند به راه راست هدايت مى‌كند.'),
(2650, 22, 55, 'همواره كافران در آن شك مى‌كنند تا آنگاه كه قيامت ناگهان بر سرشان فرارسد يا به عذاب روز بى‌شادمانى گرفتار آيند.'),
(2651, 22, 56, 'فرمانروايى در آن روز از آنِ خداست. ميانشان حكم مى‌كند. پس كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، در بهشتهاى پرنعمتند.'),
(2652, 22, 57, 'و كسانى كه كفر ورزيده‌اند و آيات ما را دروغ شمرده‌اند، برايشان عذابى است خواركننده.'),
(2653, 22, 58, 'و كسانى را كه در راه خدا مهاجرت كردند، سپس كشته شدند يا خود مردند، خدا به رزقى نيكو روزى مى‌دهد. زيرا خدا بهترين روزى‌دهندگان است.'),
(2654, 22, 59, 'آنان را به جايى درآورد كه از آن خشنود باشند. و هر آينه خدا دانا و شكيباست.'),
(2655, 22, 60, 'هر كس عقوبت كند همچنان كه او را عقوبت كرده‌اند، آنگاه بر او ستم كنند، خدا ياريش خواهد كرد. زيرا خدا عفوكننده و آمرزنده است.'),
(2656, 22, 61, 'اين بدان سبب است كه خدا از شب مى‌كاهد و به روز مى‌افزايد و از روز مى‌كاهد و به شب مى‌افزايد. و خدا شنوا و بيناست.'),
(2657, 22, 62, 'اين بدان سبب است كه خدا حق است و آنچه جز او به خدايى مى‌خوانند باطل است و او بلندمرتبه و بزرگ است.'),
(2658, 22, 63, 'آيا نديده‌اى كه خدا از آسمان باران فرستاد و زمين پر سبزه گرديد؟ هر آينه خدا باريك بين و آگاه است.'),
(2659, 22, 64, 'از آن اوست آنچه در آسمانها و آنچه در زمين است. اوست بى‌نياز و در خور ستايش.'),
(2660, 22, 65, 'آيا نديده‌اى كه خدا هر چه را در روى زمين است مسخّر شما كرده است و كشتيها را كه در دريا به فرمان او مى‌روند؟ و آسمان را نگه داشته كه جز به فرمان او بر زمين نيفتد. زيرا خدا را بر مردم رأفت و مهربانى است.'),
(2661, 22, 66, 'اوست آن كه شما را زندگى بخشيد و سپس بميراند و باز زنده مى‌سازد. و آدمى ناسپاس است.'),
(2662, 22, 67, 'براى هر امتى آيينى نهاديم تا بر آن آيين باشد. پس در اين كار با تو مجادله نكنند و مردم را به پروردگار خود دعوت كن، زيرا تو به راستى بر راه راست گام مى‌نهى.'),
(2663, 22, 68, 'اگر با تو مجادله كردند، بگو: خدا به هر كارى كه مى‌كنيد آگاهتر است.'),
(2664, 22, 69, 'در آنچه مورد اختلاف شماست، خدا در روز قيامت حكم خواهد كرد.'),
(2665, 22, 70, 'آيا ندانسته‌اى كه خدا هر چه را كه در آسمانها و زمين است مى‌داند؟ و اين در كتابى نوشته است، و كارى است بر خدا آسان.'),
(2666, 22, 71, 'و سواى اللّه چيزى را مى‌پرستند كه هيچ دليلى بر وجودش نازل نشده است و هيچ دانشى بدان ندارند. و ستمكاران ياورى ندارند.'),
(2667, 22, 72, 'چون آيات روشنگر ما بر آنان خوانده شود، نشان انكار را بر چهره كافران مى‌شناسى؛ چنان كه نزديك باشد بر آنان كه آيات را مى‌خوانند، حمله‌ور شوند. بگو: آيا شما را به چيزى بدتر از اين آگاه كنم؟ آتش. خدا آن را به كسانى كه ايمان نياورده‌اند وعده داده است و آتش بد سرانجامى است.'),
(2668, 22, 73, 'اى مردم، مثَلى زده شد. بدان گوش دهيد. كسانى كه آنها را به جاى اللّه به خدايى مى‌خوانيد، اگر همه گرد آيند مگسى را نخواهند آفريد؛ و اگر مگسى چيزى از آنها بربايد بازستاندنش را نتوانند. طالب و مطلوب هر دو ناتوانند.'),
(2669, 22, 74, 'خدا را چنان كه سزاى اوست نشناختند. هر آينه خدا نيرومند و پيروز است.'),
(2670, 22, 75, 'خدا از ميان فرشتگان و مردمان رسولانى برمى‌گزيند. هر آينه خدا شنوا و بيناست.'),
(2671, 22, 76, 'آنچه را كه در پيش رويشان است و آنچه را كه پشت سرشان است مى‌داند. و همه كارها بدو بازمى‌گردد.'),
(2672, 22, 77, 'اى كسانى كه ايمان آورده‌ايد، ركوع كنيد و سجده كنيد و پروردگارتان را بپرستيد و كارهاى نيك به جاى آوريد، باشد كه رستگار گرديد.'),
(2673, 22, 78, 'در راه خداوند چنان كه بايد جهاد كنيد. او شما را برگزيد. و برايتان در دين هيچ تنگنايى پديد نياورد. كيش پدرتان ابراهيم است. او پيش از اين و در اين شما را مسلمان ناميد. تا پيامبر بر شما گواه باشد و شما بر ديگر مردم گواه باشيد. پس نماز بگزاريد و زكات بدهيد و به خدا توسل جوييد. اوست مولاى شما. چه مولايى نيكو و چه ياورى نيكو.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'به تحقيق رستگار شدند مؤمنان:'),
(2675, 23, 2, 'آنان كه در نمازشان خشوع مى‌ورزند،'),
(2676, 23, 3, 'و آنان كه از بيهوده اعراض مى‌كنند،'),
(2677, 23, 4, 'و آنان كه زكات را مى‌پردازند،'),
(2678, 23, 5, 'و آنان كه شرمگاه خود را نگه مى‌دارند،'),
(2679, 23, 6, 'جز بر همسران يا كنيزان خويش، كه در نزديكى با آنان مورد ملامت قرار نمى‌گيرند؛'),
(2680, 23, 7, 'و كسانى كه غير از اين دو بجويند از حد خويش تجاوز كرده‌اند.'),
(2681, 23, 8, 'و آنان كه امانتها و پيمانهاى خود مراعات مى‌كنند،'),
(2682, 23, 9, 'و آنان كه بر نمازهاى خود مواظبند،'),
(2683, 23, 10, 'اينان ميراث‌بران هستند:'),
(2684, 23, 11, 'كه فردوس را كه همواره در آن جاويدانند به ميراث مى‌برند.'),
(2685, 23, 12, 'هر آينه ما انسان را از گل خالص آفريديم.'),
(2686, 23, 13, 'سپس او را نطفه‌اى در جايگاهى استوار قرار داديم.'),
(2687, 23, 14, 'آنگاه از آن نطفه، لخته خونى آفريديم و از آن لخته خون، پاره‌گوشتى و از آن پاره‌گوشت، استخوانها آفريديم و استخوانها را به گوشت پوشانيديم؛ بار ديگر او را آفرينشى ديگر داديم. در خور تعظيم است خداوند، آن بهترين آفرينندگان.'),
(2688, 23, 15, 'و بعد از اين همه شما خواهيد مُرد.'),
(2689, 23, 16, 'باز در روز قيامت زنده مى‌گرديد.'),
(2690, 23, 17, 'و بر فراز سرتان هفت آسمان بيافريديم، حال آنكه از اين مخلوق غافل نبوده‌ايم.'),
(2691, 23, 18, 'و از آسمان به اندازه نياز آب فرستاديم و آن را در زمين جاى داديم و ما بر نابودكردنش توانا هستيم.'),
(2692, 23, 19, 'با آن آب برايتان بوستانهايى از خرما و انگور پديد آورديم. شما را در آن باغها ميوه‌هاى بسيارى است كه از آنها مى‌خوريد.'),
(2693, 23, 20, 'و درختى است كه در طور سينا مى‌رويد. روغن مى‌دهد و آن روغن براى خورندگان، نان خورشى است.'),
(2694, 23, 21, 'شما را در چارپايان عبرتى است. از شيرى كه در شكمشان هست سيرابتان مى‌كنيم و از آنها سودهاى بسيار مى‌بريد و از آنها مى‌خوريد.'),
(2695, 23, 22, 'و بر آنها و بر كشتيها سوار مى‌شويد.'),
(2696, 23, 23, 'هر آينه نوح را بر قومش فرستاديم. گفت: اى قوم من، خداى يكتا را بپرستيد. شما را خداوندى جز او نيست. آيا پروا نمى‌كنيد؟'),
(2697, 23, 24, 'مهتران قومش كه كافر بودند، گفتند: اين مرد انسانى است همانند شما، مى‌خواهد بر شما برترى جويد. اگر خدا مى‌خواست فرشتگانى را مى‌فرستاد. ما هرگز چنين چيزى در روزگار نياكانمان نشنيده‌ايم.'),
(2698, 23, 25, 'او مرد ديوانه‌اى بيش نيست. يكچند بر او صبر كنيد.'),
(2699, 23, 26, 'گفت: اى پروردگار من، اكنون كه مرا تكذيب مى‌كنند، ياريم كن.'),
(2700, 23, 27, 'به او وحى كرديم كه كشتى را در حضور ما و به الهام ما بساز. و چون فرمان ما در رسيد و آب از تنور بيرون زد از هر جنسى دو تا و نيز كسان خود را به آن ببر. مگر آن كس كه پيش از اين در باره او سخن رفته است. و در باره ستمكاران با من سخن مگوى كه آنها همه غرقه‌شدگانند.'),
(2701, 23, 28, 'چون خود و همراهانت به كشتى نشستيد، بگو: سپاس خدايى را كه ما را از مردم ستمكاره رهايى بخشيد.'),
(2702, 23, 29, 'و بگو: اى پروردگار من، مرا فرود آور در جايگاهى مبارك، كه تو بهترين راهبرانى.'),
(2703, 23, 30, 'در اين داستان عبرتها و پندهاست، و ما تنها آزمايندگانى بوديم.'),
(2704, 23, 31, 'و بعد از آنها مردمى ديگر بيافريديم.'),
(2705, 23, 32, 'و از خودشان به ميانشان پيامبرى فرستاديم كه: خداى يكتا را بپرستيد، شما را خدايى جز او نيست. آيا پروا نمى‌كنيد؟'),
(2706, 23, 33, 'گروهى از مهتران قومش كه كافر بودند و ديدار آخرت را دروغ مى‌شمردند و در اين دنيايشان عيش و نعمت داده بوديم، گفتند: اين مرد انسانى است همانند شما؛ از آنچه مى‌خوريد مى‌خورد و از آنچه مى‌آشاميد مى‌آشامد.'),
(2707, 23, 34, 'و اگر از انسانى همانند خود اطاعت كنيد، زيان كرده‌ايد.'),
(2708, 23, 35, 'آيا به شما وعده مى‌دهد كه چون مرديد و خاك و استخوان شديد، شما را از گور بيرون مى‌آورند؟'),
(2709, 23, 36, 'اين وعده‌اى كه به شما داده شده بعيد است، بعيد.'),
(2710, 23, 37, 'جز همين زندگانى دنيوى ما هيچ نيست، به دنيا مى‌آييم و مى‌ميريم و ديگربار زنده نمى‌شويم.'),
(2711, 23, 38, 'اين مردى است كه به خداى يكتا دروغ مى‌بندد و ما به او ايمان نمى‌آوريم.'),
(2712, 23, 39, 'گفت: اى پروردگار من، اكنون كه تكذيبم مى‌كنند، ياريم كن.'),
(2713, 23, 40, 'گفت: به همين زودى از كرده پشيمان مى‌شوند.'),
(2714, 23, 41, 'پس به حق، بانگى سخت آنان را فرو گرفت. مانند گياه خشكشان كرديم. اى، نصيب مردم ستمكاره دورى از رحمت خدا باد.'),
(2715, 23, 42, 'و بعد از آنها مردمى ديگر بيافريديم.'),
(2716, 23, 43, 'هيچ ملتى از اجل خود نه پيش مى‌افتد و نه تأخير مى‌كند.'),
(2717, 23, 44, 'سپس پى‌درپى پيامبران خود را فرستاديم. هر بار كه پيامبرى بر ملتى آمد تكذيبش كردند و ما نيز آنها را از پس يكديگر به هلاكت رسانيديم و آنان را داستانها كرديم. اى، نصيب مردمى كه ايمان نمى‌آورند دورى از رحمت خدا باد.'),
(2718, 23, 45, 'آنگاه موسى و برادرش هارون را با آيات خود و دلايل روشن فرستاديم؛'),
(2719, 23, 46, 'نزد فرعون و مهتران قومش. پس گردنكشى كردند كه مردمى برترى جوى بودند،'),
(2720, 23, 47, 'و گفتند: آيا به دو انسان كه همانند ما هستند و قومشان بندگان ما بودند، ايمان بياوريم؟'),
(2721, 23, 48, 'آنها را تكذيب كردند و خويشتن به هلاكت افكندند.'),
(2722, 23, 49, 'هر آينه ما به موسى كتاب داديم، باشد كه هدايت شوند.'),
(2723, 23, 50, 'و پسر مريم و مادرش را آيتى ساختيم و آن دو را بر بلند جايى كه قرار گاهى و آب روان داشت جاى داديم.'),
(2724, 23, 51, 'اى پيامبران، از چيزهاى پاكيزه و خوش بخوريد و كارهاى شايسته كنيد كه من به كارهايى كه مى‌كنيد آگاهم.'),
(2725, 23, 52, 'هر آينه اين دين شما دينى است واحد، و من پروردگار شمايم؛ از من بترسيد.'),
(2726, 23, 53, 'پس دين خود را فرقه‌فرقه كردند و هر فرقه‌اى به روشى كه برگزيده بود دلخوش بود.'),
(2727, 23, 54, 'آنان را تا چندى در گمراهيشان رها كن.'),
(2728, 23, 55, 'آيا مى‌پندارند كه آن مال و فرزند كه ارزانيشان مى‌داريم،'),
(2729, 23, 56, 'براى آن است كه مى‌كوشيم خيرى به آنها برسانيم؟ نه، كه آنان در نمى‌يابند.'),
(2730, 23, 57, 'آنهايى كه از خوف پروردگارشان لرزانند،'),
(2731, 23, 58, 'و آنهايى كه به آيات پروردگارشان ايمان مى‌آورند،'),
(2732, 23, 59, 'و آنهايى كه به پروردگارشان شرك نمى‌آورند،'),
(2733, 23, 60, 'و آنهايى كه همه آنچه را بايد ادا كنند، ادا مى‌كنند و باز هم دلهايشان ترسان است كه بايد نزد پروردگارشان باز گردند،'),
(2734, 23, 61, 'اينان هستند كه به كارهاى نيك مى‌شتابند و در آن بر يكديگر سبقت مى‌جويند.'),
(2735, 23, 62, 'بر هيچ كس جز به اندازه توانش تكليف نمى‌كنيم. و نزد ما كتابى است كه به حق سخن مى‌گويد. و بر آنها ستمى نمى‌رود.'),
(2736, 23, 63, 'نه، دلهايشان از اين سخن در پرده غفلت است. و آنها را كارهايى است جز اين كارها، كه مى‌كنند.'),
(2737, 23, 64, 'تا آنگاه كه ناز پروردگانشان را به عذاب گرفتار كنيم و ناله سر دهند.'),
(2738, 23, 65, 'امروز ناله سر مدهيد كه شما را از عذاب ما كس نرهاند.'),
(2739, 23, 66, 'آيات من برايتان خوانده مى‌شد و شما نمى‌پذيرفتيد و پس‌پس مى‌رفتيد.'),
(2740, 23, 67, 'بر آن نخوت مى‌فروختيد و شب هنگام در افسانه‌سرايى ناسزا مى‌گفتيد.'),
(2741, 23, 68, 'آيا در اين سخن نمى‌انديشند يا براى آنها چيزى نازل شده كه براى نياكانشان نازل نشده بود؟'),
(2742, 23, 69, 'يا آنكه پيامبرشان را نشناخته‌اند كه انكارش مى‌كنند؟'),
(2743, 23, 70, 'يا مى‌گويند كه ديوانه است؟ نه، پيامبرشان با دين حق بر آنها مبعوث شد، ولى بيشترينشان از حق كراهت دارند.'),
(2744, 23, 71, 'اگر حق از پى هوسهايشان مى‌رفت، آسمانها و زمين و هر كه در آنهاست تباه مى‌شد. ولى ما اندرزشان فرستاديم و آنها از اندرزشان رويگردان شدند.'),
(2745, 23, 72, 'يا تو از آنها مزدى مى‌طلبى؟ مزد پروردگارت بهتر است، كه او بهترين روزى‌دهندگان است.'),
(2746, 23, 73, 'هر آينه تو به راه راستشان مى‌خوانى.'),
(2747, 23, 74, 'و كسانى كه به آخرت ايمان ندارند از راه راست منحرفند.'),
(2748, 23, 75, 'اگر بر آنها رحمت مى‌آورديم و آزارى را كه بدان گرفتارند از آنها دور مى‌ساختيم، باز هم همچنان با سرسختى در طغيان خويش سرگشته مى‌ماندند.'),
(2749, 23, 76, 'به عذاب گرفتارشان كرديم. پس در مقابل پروردگارشان نه فروتنى كردند و نه تضرع.'),
(2750, 23, 77, 'تا آنگاه كه درى از عذاب سخت بر رويشان گشوديم، چنان كه از همه جا نوميد گشتند.'),
(2751, 23, 78, 'و اوست آن خدايى كه برايتان گوش و چشم و دل بيافريد. چه اندك سپاس مى‌گزاريد.'),
(2752, 23, 79, 'اوست آن خدايى كه شما را در روى زمين پديد آورد و همه نزد او گرد آورده مى‌شويد.'),
(2753, 23, 80, 'و اوست آن خدايى كه زنده مى‌كند و مى‌ميراند و آمدوشد شب و روز از آن اوست. چرا تعقل نمى‌كنيد؟'),
(2754, 23, 81, 'نه، آنها نيز همان سخنان گفتند كه پيشينيان مى‌گفتند:'),
(2755, 23, 82, 'گفتند: آيا اگر ما بميريم و خاك و استخوان شويم باز هم زنده مى‌شويم؟'),
(2756, 23, 83, 'به ما و پدرانمان نيز پيش از اين چنين وعده‌هايى داده شده بود. اينها چيزى جز افسانه‌هاى پيشينيان نيست.'),
(2757, 23, 84, 'بگو: اگر مى‌دانيد، اين زمين و هر كه در آن است از آن كيست؟'),
(2758, 23, 85, 'خواهند گفت: از آن خدا. بگو: آيا پند نمى‌گيريد؟'),
(2759, 23, 86, 'بگو: كيست پروردگار آسمانهاى هفتگانه و پروردگار عرش بزرگ؟'),
(2760, 23, 87, 'خواهند گفت: خدا. بگو: آيا پروا نمى‌كنيد؟'),
(2761, 23, 88, 'بگو: اگر مى‌دانيد، ملكوت همه چيزها به دست كيست؟ كيست آن كه به همه كس پناه دهد و كسى را از او پناه نيست؟'),
(2762, 23, 89, 'خواهند گفت: خدا. بگو: پس چرا فريب مى‌خوريد؟'),
(2763, 23, 90, 'نه، ما برايشان سخن راست فرستاديم و آنان دروغ مى‌گويند.'),
(2764, 23, 91, 'خدا هيچ فرزندى ندارد و هيچ خدايى با او نيست. اگر چنين مى‌بود، هر خدايى با آفريدگان خود به يك سو مى‌كشيد و بر يكديگر برترى مى‌جستند. خدا از آن گونه كه او را وصف مى‌كنند، منزه است.'),
(2765, 23, 92, 'داناى نهان و آشكارا، از هر چه شريك او مى‌سازند برتر است.'),
(2766, 23, 93, 'بگو: اى پروردگار من، كاش چيزى را كه به آنان وعده داده شده به من مى‌نماياندى.'),
(2767, 23, 94, 'پس اى پروردگار من، مرا در زمره ستمكاران قرار مده.'),
(2768, 23, 95, 'و ما اگر بخواهيم كه آنچه را به آنان وعده داده‌ايم، تو را بنمايانيم، مى توانيم.'),
(2769, 23, 96, 'سخن بد آنان را به هر نيكوترت مى‌آيد پاسخ گوى. ما به سخن آنان آگاهتر هستيم.'),
(2770, 23, 97, 'بگو: اى پروردگار من، از وسوسه‌هاى شيطان به تو پناه مى‌آورم.'),
(2771, 23, 98, 'و به تو پناه مى‌آورم، اى پروردگار من، اگر نزد من حاضر آيند.'),
(2772, 23, 99, 'چون يكيشان را مرگ فرارسد، گويد: اى پروردگار من، مرا بازگردان.'),
(2773, 23, 100, 'شايد، كارهاى شايسته‌اى را كه ترك كرده بودم به جاى آورم. هرگز. اين سخنى است كه او مى‌گويد و پشت سرشان تا روز قيامت مانعى است كه بازگشت نتوانند.'),
(2774, 23, 101, 'چون در صور دميده شود، هيچ خويشاونديى ميانشان نماند و هيچ از حال يكديگر نپرسند.'),
(2775, 23, 102, 'آنان كه ترازويشان سنگين باشد، خود، رستگارانند.'),
(2776, 23, 103, 'و آنان كه ترازويشان سبك باشد، به خود زيان رسانيده‌اند و در جهنم، جاويد ماندگانند.'),
(2777, 23, 104, 'آتش چهره‌هايشان را مى‌سوزاند و لبانشان آماس كرده و برگشته است.'),
(2778, 23, 105, 'آيا آيات من برايتان خوانده نمى‌شد و آنها را دروغ مى‌انگاشتيد؟'),
(2779, 23, 106, 'گويند: اى پروردگار ما، شوربختيمان بر ما غلبه كرد و ما مردمى گمراه بوديم.'),
(2780, 23, 107, 'اى پروردگار ما، ما را از اين آتش بيرون آور. اگر ديگر بار چنان كرديم، از ستمكاران باشيم.'),
(2781, 23, 108, 'گويد: در آتش گم شويد و با من سخن مگوييد.'),
(2782, 23, 109, 'آرى، گروهى از بندگان من مى‌گفتند: اى پروردگار ما، ايمان آورديم؛ ما را بيامرز، و بر ما رحمت آور كه تو بهترين رحمت آورندگانى.'),
(2783, 23, 110, 'و شما ريشخندشان مى‌كرديد، تا ياد مرا از خاطرتان بزدودند. و شما همچنان به آنها مى‌خنديديد.'),
(2784, 23, 111, 'امروز آنها را به خاطر صبرى كه مى‌كردند پاداش مى‌دهم. آنها به مراد خود رسيده‌اند.'),
(2785, 23, 112, 'گويد: به شمار سالها، چه مدت در زمين زيسته‌ايد؟'),
(2786, 23, 113, 'گويند: يك روز يا قسمتى از يك روز. از آنها كه مى‌شمردند بپرس.'),
(2787, 23, 114, 'گويد: اگر آگاهى داشتيد، مى‌دانستيد كه جز اندكى نزيسته‌ايد.'),
(2788, 23, 115, 'آيا پنداريد كه شما را بيهوده آفريده‌ايم و شما به نزد ما بازگردانده نمى‌شويد؟'),
(2789, 23, 116, 'پس فراتر است خداى يكتا، آن فرمانرواى راستين. هيچ خدايى جز او نيست. پروردگار عرش بزرگوار است.'),
(2790, 23, 117, 'آن كس كه جز خداى يكتا خداى ديگرى را مى‌خواند كه به حقانيتش هيچ برهانى ندارد، جز اين نيست كه حسابش نزد پروردگارش خواهد بود. و كافران رستگار نمى‌شوند.'),
(2791, 23, 118, 'و بگو: اى پروردگار من، بيامرز و رحمت آور و تو بهترين رحمت‌آورندگان هستى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'سوره‌اى است كه آن را نازل كرده‌ايم و واجبش ساخته‌ايم و در آن آياتى روشن فرستاده‌ايم، باشد كه پند گيريد.'),
(2793, 24, 2, 'زن و مرد زناكار را هر يك صد ضربه بزنيد. و اگر به خدا و روز قيامت ايمان داريد، مباد كه در حكم خدا نسبت به آن دو دستخوش ترحم گرديد. و بايد كه به هنگام شكنجه كردنشان گروهى از مؤمنان حاضر باشند.'),
(2794, 24, 3, 'مرد زناكار، جز زن زناكار يا مشرك را نمى‌گيرد، و زن زناكار را جز مرد زناكار يا مشرك نمى‌گيرد. و اين بر مؤمنان حرام شده است.'),
(2795, 24, 4, 'كسانى را كه زنان عفيف را به زنا متهم مى‌كنند و چهار شاهد نمى‌آورند، هشتاد ضربه بزنيد، و از آن پس هرگز شهادتشان را نپذيريد كه مردمى فاسقند.'),
(2796, 24, 5, 'مگر كسانى كه بعد از آن توبه كنند و به صلاح آيند. زيرا خدا آمرزنده و مهربان است.'),
(2797, 24, 6, 'و كسانى كه زنان خود را به زنا متهم مى‌كنند و شاهدى جز خود ندارند، هر يك از آنها را چهار بار شهادت است به نام خدا كه از راستگويان است.'),
(2798, 24, 7, 'و بار پنجم بگويد كه لعنت خدا بر او باد اگر از دروغگويان باشد.'),
(2799, 24, 8, 'و اگر آن زن چهار بار به خدا سوگند خورد كه آن مرد دروغ مى‌گويد، حد از او برداشته مى‌شود.'),
(2800, 24, 9, 'و بار پنجم بگويد كه خشم خدا بر او باد اگر مرد از راستگويان باشد.'),
(2801, 24, 10, 'چه مى‌شد اگر فضل و رحمتى كه خدا بر شما ارزانى داشته است نمى‌بود؟ و اگر نه اين بود كه خدا توبه‌پذير و حكيم است؟'),
(2802, 24, 11, 'كسانى كه آن دروغ بزرگ را ساخته‌اند گروهى از شمايند. مپنداريد كه شما را در آن شرى بود. نه، خير شما در آن بود. هر مردى از آنها بدان اندازه از گناه كه مرتكب شده است به كيفر رسد، و از ميان آنها آن كه بيشترين اين بهتان را به عهده دارد به عذابى بزرگ گرفتار مى‌آيد.'),
(2803, 24, 12, 'چرا هنگامى كه آن بهتان را شنيديد مردان و زنان مؤمن به خود گمان نيك نبردند و نگفتند كه اين تهمتى آشكار است؟'),
(2804, 24, 13, 'چرا چهار شاهد بر ادعاى خود نياوردند؟ پس اگر شاهدانى نياورده‌اند، در نزد خدا در زمره دروغگويانند.'),
(2805, 24, 14, 'اگر فضل و رحمت خدا در دنيا و آخرت ارزانيتان نمى‌بود، به سزاى آن سخنان كه مى‌گفتيد شما را عذابى بزرگ درمى‌رسيد.'),
(2806, 24, 15, 'آنگاه كه آن سخن را از دهان يكديگر مى‌گرفتيد و چيزى بر زبان مى‌رانديد كه در باره آن هيچ نمى‌دانستيد و مى‌پنداشتيد كه كارى خرد است، و حال آنكه در نزد خدا كارى بزرگ بود.'),
(2807, 24, 16, 'چرا آنگاه كه اين سخن شنيديد نگفتيد: ما را نشايد كه آن را بازگوييم، پروردگارا تو منزهى، اين تهمتى بزرگ است؟'),
(2808, 24, 17, 'خدا شما را اندرز مى‌دهد كه اگر از مؤمنان هستيد، بار ديگر گرد چنان كارى مگرديد.'),
(2809, 24, 18, 'خدا آيات را براى شما بيان مى‌كند. و خدا دانا و حكيم است.'),
(2810, 24, 19, 'براى كسانى كه دوست دارند در باره مؤمنان تهمت زنا شايع شود، در دنيا و آخرت عذابى دردآور مهياست. خدا مى‌داند و شما نمى‌دانيد.'),
(2811, 24, 20, 'چه مى‌شد اگر فضل و رحمتى كه خدا به شما ارزانى داشته است نمى‌بود؟ و اگر نه اين بود كه خدا مهربان و بخشاينده است؟'),
(2812, 24, 21, 'اى كسانى كه ايمان آورده‌ايد، پاى به جاى پاى شيطان مگذاريد. و هر كه پاى به جاى پاى شيطان گذارد، بداند كه او به فحشا و منكر فرمان مى‌دهد. و اگر فضل و رحمتى كه خدا بر شما ارزانى داشته است نمى‌بود، هيچ يك از شما هرگز روى پاكى نمى‌ديد. ولى خدا هر كس را كه بخواهد پاكيزه مى‌سازد. و خدا شنوا و داناست.'),
(2813, 24, 22, 'توانگران و آنان كه گشايشى در كار آنهاست، نبايد سوگند بخورند كه به خويشاوندان و مسكينان و مهاجران در راه خدا چيزى ندهند. بايد ببخشند و ببخشايند. آيا نمى‌خواهيد كه خدا شما را بيامرزد؟ و خداست آمرزنده مهربان.'),
(2814, 24, 23, 'كسانى كه بر آنان پاكدامن و بى‌خبر از فحشا و مؤمن تهمت زنا مى‌زنند، در دنيا و آخرت لعنت شده‌اند و برايشان عذابى است بزرگ،'),
(2815, 24, 24, 'روزى كه زبانشان و دستهايشان و پاهايشان به زيانشان بر كارهايى كه مى‌كرده‌اند شهادت دهند.'),
(2816, 24, 25, 'آن روز كه خدا جزايشان را به تمامى بدهد و بدانند كه خدا حقيقت آشكار است.'),
(2817, 24, 26, 'زنان ناپاك براى مردان ناپاك و مردان ناپاك براى زنان ناپاك و زنان پاك براى مردان پاك و مردان پاك براى زنان پاك. آنها از آنچه در باره‌شان مى‌گويند منزهند. آمرزش و رزق نيكو براى آنهاست.'),
(2818, 24, 27, 'اى كسانى كه ايمان آورده‌ايد، به خانه‌اى غير از خانه خود، بى آنكه اجازت طلبيده و بر ساكنانش سلام كرده باشيد، داخل مشويد. اين براى شما بهتر است، باشد كه پند گيريد.'),
(2819, 24, 28, 'و اگر در خانه، كسى را نيافتيد، داخل مشويد تا شما را رخصت دهند. و اگر گويند: بازگرديد؛ بازگرديد. اين برايتان پاكيزه‌تر است. و خدا به هر كارى كه مى‌كنيد آگاه است.'),
(2820, 24, 29, 'بر شما گناهى نيست اگر به خانه‌هاى غير مسكون كه متاعى در آن داريد داخل شويد. هر چه را آشكار سازيد يا پنهان داريد خدا به آن آگاه است.'),
(2821, 24, 30, 'به مردان مؤمن بگو كه چشمان خويش فروگيرند و شرمگاه خود نگه دارند. اين برايشان پاكيزه‌تر است. زيرا خدا به كارهايى كه مى‌كنند آگاه است.'),
(2822, 24, 31, 'و به زنان مؤمن بگو كه چشمان خويش فروگيرند و شرمگاه خود نگه دارند و زينتهاى خود را جز آن مقدار كه پيداست آشكار نكنند و مقنعه‌هاى خود را تا گريبان فروگذارند و زينتهاى خود را آشكار نكنند، جز براى شوهر خود يا پدر خود يا پدر شوهر خود يا پسر خود يا پسر شوهر خود يا برادر خود يا پسر برادر خود، يا پسر خواهر خود يا زنان همكيش خود، يا بندگان خود، يا مردان خدمتگزار خود كه رغبت به آن ندارند، يا كودكانى كه از شرمگاه زنان بى‌خبرند. و نيز چنان پاى بر زمين نزنند تا آن زينت كه پنهان كرده‌اند دانسته شود. اى مؤمنان، همگان به درگاه خدا توبه كنيد، باشد كه رستگار گرديد.'),
(2823, 24, 32, 'عَزبهايتان را و غلامان و كنيزان خود را كه شايسته باشند همسر دهيد. اگر بينوا باشند خدا به كرم خود توانگرشان خواهد ساخت، كه خدا گشايش‌دهنده و داناست.'),
(2824, 24, 33, 'آنان كه استطاعت زناشويى ندارند، بايد پاكدامنى پيشه كنند تا خدا از كرم خويش توانگرشان گرداند. و از بندگانتان آنان كه خواهان بازخريدن خويشند، اگر در آنها خيرى يافتيد، بازخريدنشان را بپذيريد. و از آن مال كه خدا به شما ارزانى داشته است به آنان بدهيد. و كنيزان خود را اگر خواهند كه پرهيزگار باشند به خاطر ثروت دنيوى به زنا وادار مكنيد. هر كس كه آنان را به زنا وادارد، خدا براى آن كنيزان كه به اكراه بدان كار وادار گشته‌اند آمرزنده و مهربان است.'),
(2825, 24, 34, 'به تحقيق براى شما آياتى روشن و داستانهايى از كسانى كه پيش از شما بوده‌اند و نيز اندرزهايى براى پرهيزگاران نازل كرديم.'),
(2826, 24, 35, 'خدا نور آسمانها و زمين است. مثَل نور او چون چراغدانى است كه در آن چراغى باشد، آن چراغ درون آبگينه‌اى و آن آبگينه چون ستاره‌اى درخشنده. از روغن درخت پربركت زيتون كه نه خاورى است و نه باخترى افروخته باشد. روغنش روشنى بخشد هر چند آتش بدان نرسيده باشد. نورى افزون بر نور ديگر. خدا هر كس را كه بخواهد بدان نور راه مى‌نمايد و براى مردم مثَلها مى‌آورد، زيرا بر هر چيزى آگاه است.'),
(2827, 24, 36, 'آن نور در خانه‌هايى است كه خدا رخصت داد ارجمندش دارند و نامش در آنجا ياد شود و او را هر بامداد و شبانگاه تسبيح گويند:'),
(2828, 24, 37, 'مردانى كه هيچ تجارت و خريد و فروختى از ياد خدا و نمازگزاردن و زكات دادن بازشان ندارد، از روزى كه دلها و ديدگان دگرگون مى‌شوند هراسناكند.'),
(2829, 24, 38, 'تا خدا به نيكوتر از آنچه كرده‌اند جزايشان دهد، و از فضل خود بر آن بيفزايد، و خدا هر كه را خواهد بى‌حساب روزى دهد.'),
(2830, 24, 39, 'اعمال كافران چون سرابى است در بيابانى. تشنه، آبش پندارد و چون بدان نزديك شود هيچ نيابد و خدا را نزد خود يابد كه جزاى او را به تمام بدهد. و خدا زود به حسابها مى‌رسد.'),
(2831, 24, 40, 'يا همانند تاريكيهايى است در دريايى ژرف، كه موجش فرو پوشد و بر فراز آن موجى ديگر و بر فرازش ابرى است تيره، تاريكيهايى بر فراز يكديگر، آن سان كه اگر دست خود بيرون آرد آن را نتواند ديد. و آن كه خدا راهش را به هيچ نورى روشن نكرده باشد، هيچ نورى فرا راه خويش نيابد.'),
(2832, 24, 41, 'آيا نديده‌اى كه هر چه در آسمانها و زمين است و نيز مرغانى كه در پروازند تسبيح گوى خدا هستند؟ همه نماز و تسبيح او را مى‌دانند. و خدا به هر كارى كه مى‌كنند آگاه است.'),
(2833, 24, 42, 'از آن خداست فرمانروايى آسمانها و زمين و بازگشت همگان نزد اوست.'),
(2834, 24, 43, 'آيا نديده‌اى كه خدا ابرهايى را به آهستگى مى‌راند، آنگاه آنها را به هم مى‌پيوندد و ابرى انبوه پديد مى‌آورد و باران را بينى كه از خلال آن بيرون مى‌آيد. و از آسمان، از آن كوه‌ها كه در آنجاست تگرگ مى‌فرستد و هر كه را خواهد با آن مى‌زند و از هر كه مى‌خواهد بازش مى‌دارد. روشنايى برقش نزديك باشد كه ديدگان را كور سازد.'),
(2835, 24, 44, 'خدا شب و روز را مى‌گرداند. خردمندان را در اين عبرتى است.'),
(2836, 24, 45, 'و خدا هر جنبنده‌اى را از آب بيافريد. بعضى از آنها بر شكم مى‌روند و بعضى بر دوپا مى‌روند و بعضى بر چهارپا مى‌روند. خدا هر چه بخواهد مى‌آفريند. زيرا خدا بر هر كارى تواناست.'),
(2837, 24, 46, 'به تحقيق آياتى روشنگر نازل كرديم و خدا هر كس را كه بخواهد به راه راست هدايت مى‌كند.'),
(2838, 24, 47, 'و مى‌گويند: به خدا و پيامبرش ايمان آورده‌ايم و اطاعت مى‌كنيم. پس از آن گروهى از آنان باز مى‌گردند. و اينان ايمان نياورده‌اند.'),
(2839, 24, 48, 'چون آنها را به خدا و پيامبرش خوانند تا ميانشان حكم كند، بينى كه گروهى اعراض مى‌كنند.'),
(2840, 24, 49, 'و اگر حق با آنها باشد مى‌آيند و به حكم گردن مى‌نهند.'),
(2841, 24, 50, 'آيا در دلهايشان مرضى است يا در ترديد هستند يا بيم آن دارند كه خدا و پيامبرش بر آنها ستم كنند؟ نه، آنان خود ستم پيشگانند.'),
(2842, 24, 51, 'چون مؤمنان را به خدا و پيامبرش فرا خوانند تاميانشان حكم كند، سخنشان جز اين نيست كه مى‌گويند: شنيديم و اطاعت كرديم. اينان رستگارانند.'),
(2843, 24, 52, 'و كسانى كه از خدا وپيامبرش فرمان مى‌برند و از خدا مى‌ترسند و پرهيزگارى مى‌كنند، رستگارانند.'),
(2844, 24, 53, 'به خدا قسم خوردند، قسمهاى سخت، كه اگر به آنها فرمان دهى از ديار خود بيرون روند. بگو: قسم مخوريد، طاعتى در خور بياوريد. خدا به كارهايى كه مى‌كنيد آگاه است.'),
(2845, 24, 54, 'بگو: از خدا اطاعت كنيد و از پيامبر اطاعت كنيد. پس اگر رويگردان شويد، بر پيامبر است آنچه بدو تكليف كرده‌اند، و بر شماست آنچه بر شما تكليف كرده‌اند. و اگر از او اطاعت كنيد هدايت يابيد. و بر پيامبر جز تبليغ آشكار هيچ نيست.'),
(2846, 24, 55, 'خدا به كسانى از شما كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند وعده داد كه در روى زمين جانشين ديگرانشان كند، همچنان كه مردمى را كه پيش از آنها بودند جانشين ديگران كرد. و دينشان را -كه خود برايشان پسنديده است- استوار سازد. و وحشتشان را به ايمنى بدل كند. مرا مى‌پرستند و هيچ چيزى را با من شريك نمى‌كنند. و آنها كه از اين پس ناسپاسى كنند، نافرمانند.'),
(2847, 24, 56, 'و نماز بگزاريد و زكات بدهيد و از پيامبر اطاعت كنيد، باشد كه بر شما رحمت آورند.'),
(2848, 24, 57, 'مپندار كه كافران مى‌توانند در اين سرزمين به جايى بگريزند. جايگاهشان جهنم است، و چه بد سرانجامى است.'),
(2849, 24, 58, 'اى كسانى كه ايمان آورده‌ايد، بايد بندگان شما و آنها كه هنوز به حد بلوغ نرسيده‌اند، در سه هنگام از شما براى وارد شدن به خانه رخصت طلبند: پيش از نماز صبح و هنگام ظهر كه لباس از تن بيرون مى‌كنيد و بعد از نماز عشا. اين سه وقت، وقت خلوت شماست. در غير آن سه هنگام، شما و آنها گناهى مرتكب نشده‌ايد اگر بر يكديگر بگذريد. خدا آيات را اينچنين براى شما بيان مى‌كند، و خدا دانا و حكيم است.'),
(2850, 24, 59, 'و چون اطفال شما به حد بلوغ رسيدند، بايد همانند كسانى كه ذكرشان گذشت رخصت طلبند. خدا آيات را اينچنين براى شما بيان مى‌كند. و خدا دانا و حكيم است.'),
(2851, 24, 60, 'پيرزنان كه ديگر اميد شوى‌كردنشان نيست، بى‌آنكه زينتهاى خود را آشكار كنند، اگر چادر خويش بنهند مرتكب گناهى نشده‌اند. و خوددارى‌كردن برايشان بهتر است و خدا شنوا و داناست.'),
(2852, 24, 61, 'بر نابينا حرجى نيست و بر لنگ حرجى نيست و بر بيمار حرجى نيست. و بر شما حرجى نيست اگر از خانه خود يا خانه پدرتان يا خانه مادرتان يا خانه برادرتان يا خانه خواهرتان يا خانه عمويتان يا خانه عمه‌تان يا خانه داييتان يا خانه خاله‌تان يا خانه‌اى كه كليدهاى آن نزد شماست يا خانه دوستتان چيزى بخوريد. و گناهى مرتكب نشده‌ايد اگر با هم بخوريد يا جداجدا. و چون به خانه‌اى داخل شويد بر يكديگر سلام كنيد. اين تحيتى است مبارك و پاكيزه از جانب خدا. خدا آيات را براى شما اينچنين بيان مى‌كند، باشد كه تعقل كنيد.'),
(2853, 24, 62, 'مؤمنان كسانى هستند كه به خدا و پيامبرش ايمان آورده‌اند. و چون با پيامبر در كارى همگانى باشند، تا از او رخصت نطلبيده‌اند نبايد بروند. آنان كه از تو رخصت مى‌طلبند، كسانى هستند كه به خدا و پيامبرش ايمان آورده‌اند. پس هنگامى كه از تو براى پاره‌اى از كارهايشان رخصت خواستند، به هر يك از آنان كه خواهى رخصت بده و برايشان از خدا آمرزش بخواه، كه خدا آمرزنده و مهربان است.'),
(2854, 24, 63, 'آنچنان كه يكديگر را صدا مى‌زنيد، پيامبر را صدا مزنيد. خدا مى‌داند چه كسانى از شما در پناه ديگرى خود را پنهان مى‌سازند و آهسته آهسته بيرون مى‌روند. آنان كه از فرمان او سرپيچى مى‌كنند بايد بترسند كه مباد به آنها فتنه‌اى يا عذابى دردآور برسد.'),
(2855, 24, 64, 'از آن خداست هر چه در آسمانها و زمين است. مى‌داند كه شما بر چه كاريد. و آن روز كه به او بازگردانده شوند آنان را از كارهايى كه كرده‌اند آگاه سازد. و خدا بر هر چيزى داناست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بزرگ است و بزرگوار آن كس كه اين فرقان را بر بنده خود نازل كرد، تا جهانيان را بيم‌دهنده‌اى باشد.'),
(2857, 25, 2, 'آن كس كه از آن اوست فرمانروايى آسمانها و زمين، و فرزندى نگرفته است، و او را شريكى در فرمانروايى نيست، و هر چيز را بيافريده است، و آن را به اندازه آفريده است.'),
(2858, 25, 3, 'سواى او خدايانى ديگر گرفتند كه هيچ چيز نمى‌آفرينند و خود مخلوقند. نه مالك زيان خود هستند و نه سود خود، و مالك مرگ و زندگى و رستاخيز نيستند.'),
(2859, 25, 4, 'و كافران گفتند كه اين جز دروغى كه خود بافته است و گروهى ديگر او را بر آن يارى داده‌اند، هيچ نيست. حقا آنچه مى‌گويند ستم و باطل است.'),
(2860, 25, 5, 'و گفتند: اين اساطير پيشينيان است كه هر صبح و شام بر او املا مى‌شود و او مى‌نويسدش.'),
(2861, 25, 6, 'بگو: اين كتاب را كسى نازل كرده است كه نهان آسمانها و زمين را مى‌داند و آمرزنده و مهربان است.'),
(2862, 25, 7, 'گفتند: چيست اين پيامبر را كه غذا مى‌خورد و در بازارها راه مى‌رود؟ چرا فرشته‌اى بر او فرود نمى‌آيد تا با او بيم‌دهنده باشد؟'),
(2863, 25, 8, 'چرا از آسمان گنجى برايش افكنده نشود؟ چرا او را باغى نيست كه از آن بخورد؟ و ستمكاران گفتند: شما از مرد جادوشده‌اى پيروى مى‌كنيد.'),
(2864, 25, 9, 'بنگر كه چگونه برايت داستانها مى‌آورند. گمراه شده‌اند و توان راه‌يافتنشان نيست.'),
(2865, 25, 10, 'بزرگ و بزرگوار است آن كس كه اگر خواهد بهتر از آن به تو ارزانى دارد: باغهايى كه در آنها نهرها جارى باشد و برايت قصرها پديد آورد.'),
(2866, 25, 11, 'بلكه اينان قيامت را دروغ انگارند. و ما براى كسانى كه قيامت را دروغ انگارند آتش سوزان آماده كرده‌ايم؛'),
(2867, 25, 12, 'كه چون از راه دور ببيندشان جوشش و خروشش را بشنوند؛'),
(2868, 25, 13, 'و چون دستها بر گردن بسته، در تنگنايى از آن افتند، به دعا مرگ خويش مى‌خواهند.'),
(2869, 25, 14, 'امروز نه يك بار مرگ خويش خواهيد، مرگ خويش فراوان خواهيد.'),
(2870, 25, 15, 'بگو: آيا اين بهتر است يا آن بهشت جاويدان كه به پرهيزگاران وعده شده است، كه پاداش و سرانجام آنان خواهد بود.'),
(2871, 25, 16, 'تا ابد هر چه بخواهند در آنجا هست. وعده‌اى است كه انجام دادن آن از پروردگارت خواسته آمده است.'),
(2872, 25, 17, 'روزى كه آنان را با چيزهايى كه سواى خداى يكتا مى‌پرستيدند به محشر گرد آورد و سپس پرسد: آيا شما اين بندگان مرا گمراه مى‌كرديد، يا آنها خود راه را گم كرده بودند؟'),
(2873, 25, 18, 'گويند: منزهى تو. ما را سزاوار نبوده است كه جز تو كسى را به يارى گيريم. تو خود آنها و پدرانشان را برخوردار ساختى، چنان كه ياد تو را فراموش كردند و مردمى شدند به هلاكت افتاده.'),
(2874, 25, 19, 'اينان آنچه را مى‌گفتيد دروغ مى‌خواندند و اكنون نتوانيد عذابى را از خود دور سازيد يا خويشتن را يارى دهيد. و هر كس از شما كه ستم كند عذابى بزرگش مى‌چشانيم.'),
(2875, 25, 20, 'پيش از تو پيامبرانى نفرستاده‌ايم جز آنكه طعام مى‌خوردند و در بازارها راه مى‌رفتند. و شما را وسيله آزمايش يكديگر قرار داديم. آيا صبر توانيد كرد؟ و پروردگار تو بيناست.'),
(2876, 25, 21, 'كسانى كه به ديدار ما اميد ندارند، گفتند: چرا فرشتگان بر ما نازل نمى‌شوند؟ يا، چرا پروردگار خود را نمى‌بينيم؟ به راستى كه خود را بزرگ شمردند و طغيان كردند، طغيانى بزرگ.'),
(2877, 25, 22, 'روزى كه فرشتگان را ببينند، در آن روز مجرمان را هيچ مژده‌اى ندهند و به آنها گويند: مژده بر شما حرام است.'),
(2878, 25, 23, 'و به اعمالى كه كرده‌اند پردازيم و همه را چون ذرّات خاك بر باد دهيم.'),
(2879, 25, 24, 'اهل بهشت در اين روز در بهترين جايگاه و بهترين مكان براى آرامش هستند.'),
(2880, 25, 25, 'روزى كه آسمان با ابرها مى‌شكافد و فرشتگان بر زمين فرو فرستاده شوند.'),
(2881, 25, 26, 'فرمانروايى در آن روز -به راستى- از آنِ خداى رحمان است. و براى كافران روزى دشوار خواهد بود.'),
(2882, 25, 27, 'روزى كه كافر دستان خود را به دندان گزد و گويد: اى كاش راهى را كه رسول در پيش گرفته بود، در پيش گرفته بودم؛'),
(2883, 25, 28, 'واى بر من، كاش فلان را دوست نمى‌گرفتم؛'),
(2884, 25, 29, 'با آنكه قرآن براى من نازل شده بود، مرا از پيرويش بازمى‌داشت. و اين شيطان همواره آدمى را تنها مى‌گذارد.'),
(2885, 25, 30, 'پيامبر گفت: اى پروردگار من، قوم من ترك قرآن گفتند.'),
(2886, 25, 31, 'اينچنين هر پيامبرى را از ميان مجرمان دشمنى پديد آورديم. و پروردگار تو براى راهنمايى و يارى تو كافى است.'),
(2887, 25, 32, 'كافران گفتند: چرا اين قرآن به يكباره بر او نازل نمى‌شود؟ براى آن است كه دل تو را بدان نيرومندى دهيم و آن را به آهستگى و ترتيب فرو خوانيم.'),
(2888, 25, 33, 'هيچ مثَلى براى تو نياورند مگر آنكه پاسخش را به راستى و در نيكوترين بيان بياوريم.'),
(2889, 25, 34, 'كسانى را كه بر روى مى‌كشند و در جهنم گرد مى‌آورند، به جايگاه بدتر و به راه گمگشته‌ترند.'),
(2890, 25, 35, 'به موسى كتاب داديم و برادرش هارون را مددكارش ساختيم.'),
(2891, 25, 36, 'و گفتيم: نزد مردمى كه آيات ما را تكذيب كرده‌اند، برويد. و آن قوم را به سختى هلاك كرديم.'),
(2892, 25, 37, 'قوم نوح را چون پيامبران را تكذيب كردند غرقه كرديم و آنها را براى مردم عبرتى ساختيم. و براى ستمكاران عذابى دردآور آماده كرده‌ايم.'),
(2893, 25, 38, 'و عاد را و ثمود را و اصحاب رسّ را و نسلهاى بسيارى را كه ميان آنها بودند.'),
(2894, 25, 39, 'و براى همه مثَلهايى آورديم و همه را نيست و نابود ساختيم.'),
(2895, 25, 40, 'و بر قريه‌اى كه بر آن باران عذاب باريده بوديم گذر مى‌كردند. آيا آن را نمى‌ديدند؟ آرى، به قيامت اميد نداشتند.'),
(2896, 25, 41, 'چون تو را ديدند مسخره‌ات كردند كه آيا اين است آن پيامبرى كه خدا بر ما مبعوث كرده است؟'),
(2897, 25, 42, 'اگر به خاطر خدايانمان سرسختى نمى‌كرديم نزديك بود كه ما را از پرستششان گمراه كند. چون عذاب را ببينند خواهند دانست چه كسى گمراه‌تر بوده است.'),
(2898, 25, 43, 'آيا آن كس را كه هواى نفس را به خدايى گرفته بود ديدى؟ آيا تو ضامن او هستى؟'),
(2899, 25, 44, 'يا گمان كرده‌اى كه بيشترينشان مى‌شنوند و مى‌فهمند؟ اينان چون چارپايانى بيش نيستند، بلكه از چارپايان هم گمراه‌ترند.'),
(2900, 25, 45, 'نديده‌اى كه پروردگار تو چگونه سايه را مى‌كشد؟ اگر مى‌خواست در يك جا ساكنش مى‌گرداند. آنگاه آفتاب را بر او دليل گردانيديم.'),
(2901, 25, 46, 'سپس بر گرفتيمش، گرفتنى اندك‌اندك.'),
(2902, 25, 47, 'اوست كه شب را پوشش شما كرد و خواب را آرامشتان و روز را زمان برخاستنتان.'),
(2903, 25, 48, 'اوست كه پيشاپيش رحمت خود بادها را به مژده‌دادن فرستادن. و از آسمان آبى پاك نازل كرديم،'),
(2904, 25, 49, 'تا سرزمين مرده را بدان زندگى بخشيم و چارپايان و مردم بسيارى را كه آفريده‌ايم بدان سيراب كنيم.'),
(2905, 25, 50, 'و تا به تفكر پردازند، باران را ميانشان تقسيم كرديم. ولى بيشتر مردم جز ناسپاسى نكردند.'),
(2906, 25, 51, 'اگر مى‌خواستيم، به هر قريه‌اى بيم‌دهنده‌اى مى‌فرستاديم.'),
(2907, 25, 52, 'از كافران اطاعت مكن، و به حكم خدا با آنها جهاد كن، جهادى بزرگ.'),
(2908, 25, 53, 'اوست كه دو دريا به هم بياميخت: يكى شيرين و گوارا و ديگرى شورابى سخت گزنده. و ميان آن دو مانعى و سدى استوار قرار داد.'),
(2909, 25, 54, 'اوست كه آدمى را از آب بيافريد و او را نسب و پيوند ساخت. و پروردگار تو به اين كارها تواناست.'),
(2910, 25, 55, 'سواى خداى يكتا چيزهايى را مى‌پرستند كه به آنها نه سود مى‌دهد و نه زيان. و كافر همواره پشتيبان مخالفان پروردگار خويش است.'),
(2911, 25, 56, 'ما تو را نفرستاديم مگر براى آنكه مژده دهى و بترسانى.'),
(2912, 25, 57, 'بگو: من از شما هيچ مزدى نمى‌طلبم و اين رسالت بدان مى‌گزارم تا هر كه خواهد به سوى پروردگارش راهى بيابد.'),
(2913, 25, 58, 'و بر آن زنده‌اى كه نمى‌ميرد توكل كن و به ستايش او تسبيح گوى و او خود براى آگاهى از گناهان بندگانش كافى است.'),
(2914, 25, 59, 'آن كه آسمانها و زمين و هر چه را در ميان آنهاست به شش روز بيافريد، آنگاه به عرش پرداخت. اوست خداى رحمان و در باره او از كسى بپرس كه آگاه باشد.'),
(2915, 25, 60, 'و چون به آنان گفته شد كه رحمان را سجده كنيد، گفتند: رحمان چيست؟ آيا بر هر كس كه تو فرمان مى‌دهى سجده كنيم؟ و بر نفرتشان افزوده شد.'),
(2916, 25, 61, 'بزرگ و بزرگوار است آن كسى كه در آسمان برجها بيافريد و در آنها چراغى و ماهى تابان پديد آورد.'),
(2917, 25, 62, 'و اوست كه شب و روز را براى كسانى كه مى‌خواهند عبرت گيرند يا شكرگزارى كنند از پى هم قرار داد.'),
(2918, 25, 63, 'بندگان خداى رحمان كسانى هستند كه در روى زمين به فروتنى راه مى‌روند. و چون جاهلان آنان را مخاطب سازند، به ملايمت سخن گويند.'),
(2919, 25, 64, 'و آنان كه شب را در سجده يا در قيام براى پروردگارشان به صبح مى‌آورند.'),
(2920, 25, 65, 'و آنان كه مى‌گويند: اى پروردگار ما، عذاب جهنم را از ما بگردان، زيرا عذاب جهنم عذابى است دايم،'),
(2921, 25, 66, 'جهنم بد قرارگاه و بد مكانى است.'),
(2922, 25, 67, 'و آنان كه چون هزينه مى‌كنند اسراف نمى‌كنند و خِسّت نمى‌ورزند بلكه ميان اين دو، راه اعتدال را مى‌گيرند.'),
(2923, 25, 68, 'و آنان كه با خداى يكتا خداى ديگرى نمى‌گيرند و كسى را كه خدا كشتنش را حرام كرده -مگر به حق- نمى‌كشند و زنا نمى‌كنند. و هر كه اين كارها كند، عقوبت گناه خود را مى‌بيند.'),
(2924, 25, 69, 'عذابش در روز قيامت مضاعف مى‌شود و تا ابد به خوارى در آن عذاب خواهد بود.'),
(2925, 25, 70, 'مگر آن كسان كه توبه كنند و ايمان آورند و كارهاى شايسته كنند. خدا گناهانشان را به نيكيها بدل مى‌كند و خدا آمرزنده و مهربان است.'),
(2926, 25, 71, 'و هر كه توبه كند و كار شايسته كند، به شايستگى نزد خدا بازگردد.'),
(2927, 25, 72, 'و آنان كه به دروغ شهادت نمى‌دهند و چون بر ناپسندى بگذرند به شتاب از آن دورى مى‌جويند.'),
(2928, 25, 73, 'و آنان كه چون به آيات پروردگارشان اندرزشان دهند، در برابر آن چون كران و كوران نباشند.'),
(2929, 25, 74, 'و آنان كه مى‌گويند: اى پروردگار ما، از همسران و فرزندانمان دلهاى ما را شاد دار، و ما را پيشواى پرهيزگاران گردان.'),
(2930, 25, 75, 'اينان همان كسانند كه به خاطر صبرى كه تحمل كرده‌اند غرفه‌هاى بهشت را پاداش يابند و در آنجا به درود و سلامشان بنوازند.'),
(2931, 25, 76, 'جاودانه در آنجا باشند. چه نيكو قرارگاه و مكانى است.'),
(2932, 25, 77, 'بگو: اگر پروردگار من شما را به طاعت خويش نخوانده بود به شما نمى‌پرداخت، كه شما تكذيب كرده‌ايد و كيفرتان همراهتان خواهد بود.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سين، ميم.'),
(2934, 26, 2, 'اينهاست آيات اين كتاب روشنگر.'),
(2935, 26, 3, 'شايد، از اينكه ايمان نمى‌آورند، خود را هلاك سازى.'),
(2936, 26, 4, 'اگر بخواهيم، از آسمان برايشان آيتى نازل مى‌كنيم كه در برابر آن به خضوع سر فرود آورند.'),
(2937, 26, 5, 'برايشان هيچ سخن تازه‌اى از خداى رحمان نازل نمى‌شود، جز آنكه از اعراض مى‌كنند.'),
(2938, 26, 6, 'پس تكذيبش كردند. زودا كه خبر آن چيزهايى كه مسخره‌اش مى‌كردند به ايشان برسد.'),
(2939, 26, 7, 'آيا به زمين ننگريسته‌اند كه چه قدر از هر گونه گياهان نيكو در آن رويانيده‌ايم.'),
(2940, 26, 8, 'در اين عبرتى است ولى بيشترشان مؤمن نبوده‌اند.'),
(2941, 26, 9, 'به راستى پروردگار تو پيروزمند و مهربان است.'),
(2942, 26, 10, 'و پروردگارت موسى را ندا داد كه: اى موسى، به سوى آن مردم ستمكار برو:'),
(2943, 26, 11, 'قوم فرعون. آيا نمى‌خواهند پرهيزگار شوند؟'),
(2944, 26, 12, 'گفت: اى پروردگار من، مى‌ترسم كه دروغگويم خوانند.'),
(2945, 26, 13, 'و دل من تنگ گردد و زبانم گشاده نشود. هارون را رسالت ده.'),
(2946, 26, 14, 'و بر من به گناهى ادعايى دارند، مى‌ترسم كه مرا بكشند.'),
(2947, 26, 15, 'گفت: هرگز، آيات مرا هر دو نزد آنها ببريد، ما نيز با شما هستيم و گوش فرا مى‌دهيم.'),
(2948, 26, 16, 'پس نزد فرعون رويد و بگوييد: ما رسول پروردگار جهانيان هستيم،'),
(2949, 26, 17, 'كه بنى اسرائيل را با ما بفرستى.'),
(2950, 26, 18, 'گفت: آيا به هنگام كودكى نزد خود پرورشت نداديم و تو چند سال از عمرت را در ميان ما نگذارنيدى؟'),
(2951, 26, 19, 'و آن كار را كه از تو سرزد مرتكب نشدى؟ پس تو كافر نعمتى.'),
(2952, 26, 20, 'گفت: آن وقت كه چنان كردم از خطاكاران بودم.'),
(2953, 26, 21, 'و چون از شما ترسيدم گريختم. ولى پروردگار من به من نبوت داد و مرا در شمار پيامبران آورد.'),
(2954, 26, 22, 'و منّت اين نعمت را بر من مى‌نهى كه بنى‌اسرائيل را برده ساخته‌اى؟'),
(2955, 26, 23, 'فرعون گفت: پروردگار جهانيان چيست؟'),
(2956, 26, 24, 'گفت: اگر به يقين مى‌پذيريد، پروردگار آسمانها و زمين و هر چه ميان آنهاست.'),
(2957, 26, 25, 'به آنان كه در اطرافش بودند، گفت: آيا نشنيديد؟'),
(2958, 26, 26, 'گفت: پروردگار شما و پروردگار نياكان شما.'),
(2959, 26, 27, 'فرعون گفت: اين پيامبرى كه بر شما فرستاده شده، ديوانه است.'),
(2960, 26, 28, 'گفت: اوست پروردگار مشرق و مغرب و هر چه ميان آن دوست، اگر تعقل كنيد.'),
(2961, 26, 29, 'فرعون گفت: اگر جز من كس ديگرى را به خدايى گيرى به زندانت مى‌افكنم.'),
(2962, 26, 30, 'گفت: حتى اگر معجزه‌اى روشن براى تو آورده باشم؟'),
(2963, 26, 31, 'گفت: اگر راست مى‌گويى، بياورش.'),
(2964, 26, 32, 'عصايش را انداخت. به آشكارا اژدهايى شد.'),
(2965, 26, 33, 'و دستش را بيرون آورد. در نظر بينندگان سپيد مى‌نمود.'),
(2966, 26, 34, 'به مهتران قومش كه كنارش بودند گفت: اين مرد جادوگرى داناست.'),
(2967, 26, 35, 'مى‌خواهد به جادوى خود شما را از سرزمينتان بيرون كند. چه رأى مى‌دهيد؟'),
(2968, 26, 36, 'گفتند: از او و برادرش مهلت بخواه و كسان به شهرها بفرست.'),
(2969, 26, 37, 'تا هر جادوگر دانايى را كه هست نزد تو بياورند.'),
(2970, 26, 38, 'جادوگران را در روزى معين به وعده‌گاه آوردند.'),
(2971, 26, 39, 'و مردم را گفتند: آيا شما نيز گرد مى‌آييد؟'),
(2972, 26, 40, 'تا اگر جادوگران پيروز آمدند همه از آنها پيروى كنيم.'),
(2973, 26, 41, 'چون جادوگران آمدند، به فرعون گفتند: آيا اگر ما پيروز شويم ما را مزدى خواهد بود.'),
(2974, 26, 42, 'گفت: آرى، همه از مقربان خواهيد بود.'),
(2975, 26, 43, 'موسى به آنها گفت: هر چه مى‌خواهيد بيفكنيد، بيفكنيد.'),
(2976, 26, 44, 'آنان ريسمانها و چوبهاى خود بيفكندند و گفتند: به عزت فرعون كه ما پيروز شديم.'),
(2977, 26, 45, 'و موسى عصايش را افكند. ناگاه همه آن دروغهايى را كه ساخته بودند، بلعيد.'),
(2978, 26, 46, 'جادوگران به سجده افتادند.'),
(2979, 26, 47, 'گفتند: به پروردگار جهانيان ايمان آورديم:'),
(2980, 26, 48, 'پروردگار موسى و هارون.'),
(2981, 26, 49, 'گفت: آيا پيش از آنكه شما را رخصت دهم ايمان آورديد؟ هرآينه آن مرد، بزرگ شماست كه شما را جادو آموخته است. خواهيد ديد. اكنون دستها و پاهايتان را از چپ و راست خواهم بريد و همه تان را بر دار خواهم كرد.'),
(2982, 26, 50, 'گفتند: باكى نيست، ما نزد پروردگارمان باز مى‌گرديم.'),
(2983, 26, 51, 'ما اميد مى‌داريم كه پروردگارمان خطاهاى ما را ببخشد كه ما نخستين كسانى هستيم كه ايمان آورديم.'),
(2984, 26, 52, 'و به موسى وحى كرديم كه: شب‌هنگام بندگان مرا بيرون ببر كه از پى شما بيايند.'),
(2985, 26, 53, 'و فرعون گردآورندگان سپاه را به شهرها فرستاد.'),
(2986, 26, 54, 'كه اينان گروهى اندكند،'),
(2987, 26, 55, 'و ما را به خشم آورده‌اند،'),
(2988, 26, 56, 'و ما همگى آماده پيكاريم.'),
(2989, 26, 57, 'پس ايشان را از باغها و چشمه‌سارها بيرون كرديم،'),
(2990, 26, 58, 'و از گنجها و خانه‌هاى نيكو،'),
(2991, 26, 59, 'بدين سان همه را به بنى اسرائيل وا گذاشتيم.'),
(2992, 26, 60, 'فرعونيان به هنگام برآمدن آفتاب از پى آنها رفتند.'),
(2993, 26, 61, 'چون آن دو گروه يكديگر را ديدند، ياران موسى گفتند: گرفتار آمديم.'),
(2994, 26, 62, 'گفت: هرگز. پروردگار من با من است و مرا راه خواهد نمود.'),
(2995, 26, 63, 'پس به موسى وحى كرديم كه: عصايت را بر دريا بزن. دريا بشكافت و هر پاره چون كوهى عظيم گشت.'),
(2996, 26, 64, 'و آن گروه ديگر را نيز به دريا رسانديم.'),
(2997, 26, 65, 'موسى و همه همراهانش را رهانيديم.'),
(2998, 26, 66, 'و آن ديگران را غرقه ساختيم.'),
(2999, 26, 67, 'هرآينه در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3000, 26, 68, 'هرآينه پروردگار تو پيروزمند و بخشنده است.'),
(3001, 26, 69, 'و داستان ابراهيم را برايشان تلاوت كن.'),
(3002, 26, 70, 'آنگاه كه به پدر و قوم خود گفت: چه مى‌پرستيد؟'),
(3003, 26, 71, 'گفتند: بتانى را مى‌پرستيم و معتكف آستانشان هستيم.'),
(3004, 26, 72, 'گفت: آيا وقتى آنها را مى‌خوانيد صدايتان را مى‌شنوند؟'),
(3005, 26, 73, 'يا براى شما سود و زيانى دارند؟'),
(3006, 26, 74, 'گفتند: نه، پدرانمان را ديده‌ايم كه چنين مى‌كرده‌اند.'),
(3007, 26, 75, 'گفت: آيا مى‌دانيد كه چه مى‌پرستيده‌ايد،'),
(3008, 26, 76, 'شما و نياكانتان؟'),
(3009, 26, 77, 'آنها دشمنان منند، ولى پروردگار جهانيان دوست من است:'),
(3010, 26, 78, 'آن كه مرا بيافريده سپس راهنماييم مى‌كند،'),
(3011, 26, 79, 'و آن كه به من طعام مى‌دهد و مرا سيراب مى‌سازد،'),
(3012, 26, 80, 'و چون بيمار شوم شفايم مى‌بخشد،'),
(3013, 26, 81, 'و آن كه مرا مى‌ميراند و سپس زنده مى‌كند،'),
(3014, 26, 82, 'و آن كه اميد مى‌دارم كه در روز قيامت خطايم را ببخشايد.'),
(3015, 26, 83, 'اى پروردگار من، مرا حكمت بخش و مرا به شايستگان بپيوند.'),
(3016, 26, 84, 'و ذكر جميل مرا در دهان آيندگان انداز.'),
(3017, 26, 85, 'و مرا از وارثان بهشت پرنعمت قرار ده.'),
(3018, 26, 86, 'و پدرم را بيامرز كه از گمراهان است.'),
(3019, 26, 87, 'و در روز قيامت مرا رسوا مساز:'),
(3020, 26, 88, 'روزى كه نه مال سود مى‌دهد و نه فرزندان.'),
(3021, 26, 89, 'مگر آن كس كه با قلبى رسته از شرك به نزد خدا بيايد.'),
(3022, 26, 90, 'و بهشت را براى پرهيزگاران نزديك آرند.'),
(3023, 26, 91, 'و جهنم را در نظر كافران آشكار كنند.'),
(3024, 26, 92, 'به آنها گويند: چيزهايى كه سواى خداى يكتا مى‌پرستيديد، كجايند؟'),
(3025, 26, 93, 'آيا ياريتان مى‌كنند، يا خود يارى مى‌جويند؟'),
(3026, 26, 94, 'آنها و كافران را سرنگون در جهنم اندازند،'),
(3027, 26, 95, 'و همه سپاهيان ابليس را.'),
(3028, 26, 96, 'و در حالى كه در جهنم با يكديگر به نزاع پرداخته‌اند، مى‌گويند:'),
(3029, 26, 97, 'به خدا سوگند كه ما در گمراهى آشكار بوديم،'),
(3030, 26, 98, 'آنگاه كه شما را با پروردگار جهانيان برابر مى‌دانستيم.'),
(3031, 26, 99, 'و ما را جز مجرمان گمراه نساختند.'),
(3032, 26, 100, 'و اكنون ما را شفيعانى نيست.'),
(3033, 26, 101, 'و ما را دوست مهربانى نيست.'),
(3034, 26, 102, 'كاش بار ديگر به دنيا باز گرديم تا از مؤمنان گرديم.'),
(3035, 26, 103, 'در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3036, 26, 104, 'هر آينه پروردگار تو پيروزمند و مهربان است.'),
(3037, 26, 105, 'قوم نوح پيامبران را تكذيب كردند.'),
(3038, 26, 106, 'آنگاه كه برادرشان نوح به آنها گفت: آيا پروا نمى‌كنيد؟'),
(3039, 26, 107, 'من براى شما پيامبرى امين هستم.'),
(3040, 26, 108, 'از خدا بترسيد و از من اطاعت كنيد.'),
(3041, 26, 109, 'من از شما در برابر هدايت خود مزدى نمى‌طلبم. مزد من تنها بر عهده پروردگار جهانيان است.'),
(3042, 26, 110, 'پس، از خدا بترسيد و از من اطاعت كنيد.'),
(3043, 26, 111, 'گفتند: آيا به تو ايمان بياوريم و حال آنكه فرومايگان پيرو تو هستند؟'),
(3044, 26, 112, 'گفت: دانش من به كارهايى كه مى‌كنند نمى‌رسد.'),
(3045, 26, 113, 'اگر مى‌فهميد، حسابشان تنها با پروردگار من است.'),
(3046, 26, 114, 'و من مؤمنان را طرد نمى‌كنم.'),
(3047, 26, 115, 'جز بيم‌دهنده‌اى آشكار نيستم.'),
(3048, 26, 116, 'گفتند: اى نوح، اگر بس نكنى، سنگسار مى‌شوى.'),
(3049, 26, 117, 'گفت: اى پروردگار من، قوم من مرا تكذيب مى‌كنند.'),
(3050, 26, 118, 'ميان من و آنها راهى برگشاى و مرا و مؤمنان همراه مرا رهايى بخش.'),
(3051, 26, 119, 'او و همراهانش را در آن كشتى انباشته، نجات داديم.'),
(3052, 26, 120, 'و باقى را غرقه كرديم.'),
(3053, 26, 121, 'هرآينه در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3054, 26, 122, 'هرآينه پروردگار تو پيروزمند و مهربان است.'),
(3055, 26, 123, 'قوم عاد پيامبران را تكذيب كردند.'),
(3056, 26, 124, 'آنگاه كه برادرشان هود گفت: آيا پروا نمى‌كنيد؟'),
(3057, 26, 125, 'من براى شما پيامبرى امين هستم.'),
(3058, 26, 126, 'از خدا بترسيد و از من اطاعت كنيد.'),
(3059, 26, 127, 'من از شما در برابر هدايت خود مزدى نمى‌طلبم. مزد من تنها بر عهده پروردگار جهانيان است.'),
(3060, 26, 128, 'آيا بر فراز هر بلندى به بيهودگى برجى برمى‌آوريد؟'),
(3061, 26, 129, 'و بدين پندار كه همواره زنده‌ايد كوشكهايى بنا مى‌كنيد؟'),
(3062, 26, 130, 'و چون انتقام گيريد چون جباران انتقام مى‌گيريد؟'),
(3063, 26, 131, 'پس، از خدا بترسيد و از من اطاعت كنيد.'),
(3064, 26, 132, 'و بترسيد از آن خدايى كه آنچه را كه مى‌دانيد، به شما عطا كرده است.'),
(3065, 26, 133, 'و به شما چارپايان و فرزندان ارزانى داشته است،'),
(3066, 26, 134, 'و باغها و چشمه‌ساران.'),
(3067, 26, 135, 'من از عذاب روزى بزرگ بر شما بيمناكم.'),
(3068, 26, 136, 'گفتند: براى ما يكسان است كه ما را اندرز دهى يا اندرز ندهى.'),
(3069, 26, 137, 'اينها جز همان دروغ و نيرنگ پيشينيان نيست.'),
(3070, 26, 138, 'و ما عذاب نخواهيم شد.'),
(3071, 26, 139, 'پس تكذيبش كردند و ما آنان را هلاك كرديم. هرآينه در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3072, 26, 140, 'هرآينه پروردگار تو پيروزمند و مهربان است.'),
(3073, 26, 141, 'قوم ثمود پيامبران را تكذيب كردند.'),
(3074, 26, 142, 'آنگاه كه برادرشان صالح گفت: آيا پروا نمى‌كنيد؟'),
(3075, 26, 143, 'من براى شما پيامبرى امين هستم.'),
(3076, 26, 144, 'از خدا بترسيد و از من اطاعت كنيد.'),
(3077, 26, 145, 'من از شما در برابر هدايت خود مزدى نمى‌طلبم. مزد من تنها بر عهده پروردگار جهانيان است.'),
(3078, 26, 146, 'آيا پنداريد كه شما را در اين نعمتها، ايمن رها مى‌كنند؟'),
(3079, 26, 147, 'در باغها و چشمه‌سارها؟'),
(3080, 26, 148, 'و كشتزارها و نخلها با آن شكوفه‌هاى نرم و لطيف؟'),
(3081, 26, 149, 'و شادمانه در كوه‌ها خانه‌هايى مى‌تراشيد؟'),
(3082, 26, 150, 'پس، از خدا بترسيد و از من اطاعت كنيد.'),
(3083, 26, 151, 'و فرمان اين اسرافكاران را مپذيريد:'),
(3084, 26, 152, 'اينان كه در زمين فساد مى‌كنند و اصلاح نمى‌كنند.'),
(3085, 26, 153, 'گفتند: جز اين نيست كه تو را جادو كرده‌اند.'),
(3086, 26, 154, 'تو نيز بشرى همانند ما هستى. اگر راست مى‌گويى نشانه‌اى بياور.'),
(3087, 26, 155, 'گفت: اين ماده‌شترِ من است. يك روز آب خوردن حق او باشد و يك روز حق شما.'),
(3088, 26, 156, 'به آن آسيبى مرسانيد كه عذابِ روزى بزرگ شما را فرومى‌گيرد.'),
(3089, 26, 157, 'آن را كشتند و پشيمان شدند.'),
(3090, 26, 158, 'پس عذاب آنها فروگرفت. هرآينه در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3091, 26, 159, 'هرآينه پروردگار تو پيروزمند و مهربان است.'),
(3092, 26, 160, 'قوم لوط پيامبران را تكذيب كردند.'),
(3093, 26, 161, 'آنگاه كه برادرشان لوط گفت: آيا پروا نمى‌كنيد؟'),
(3094, 26, 162, 'من براى شما پيامبرى امين هستم.'),
(3095, 26, 163, 'از خدا بترسيد و از من اطاعت كنيد.'),
(3096, 26, 164, 'من از شما در برابر هدايت خود مزدى نمى‌طلبم. مزد من تنها بر عهده پروردگار جهانيان است.'),
(3097, 26, 165, 'چرا از ميان مردم جهان با نران مى‌آميزيد؟'),
(3098, 26, 166, 'و همسرانى را كه پروردگارتان برايتان آفريده است ترك مى‌گوييد؟ نه. شما مردمى متجاوز هستيد.'),
(3099, 26, 167, 'گفتند: اى لوط، اگر بس نكنى، از شهر بيرونت مى‌كنيم.'),
(3100, 26, 168, 'گفت: من با كار شما دشمنم.'),
(3101, 26, 169, 'اى پروردگار من، مرا و كسانم را از عاقبت عملى كه مى‌كنند رهايى بخش.'),
(3102, 26, 170, 'او و همه كسانش را رهانيديم.'),
(3103, 26, 171, 'مگر پيرزنى كه خواست بماند.'),
(3104, 26, 172, 'سپس ديگران را هلاك كرديم.'),
(3105, 26, 173, 'بر آنها بارانى بارانيديم و چه بد بود باران تهديدشدگان.'),
(3106, 26, 174, 'هرآينه در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3107, 26, 175, 'هرآينه پروردگار تو پيروزمند و مهربان است.'),
(3108, 26, 176, 'مردم اَيكه پيامبران را تكذيب كردند.'),
(3109, 26, 177, 'آنگاه كه شعيب به آنها گفت: آيا پروا نمى‌كنيد؟'),
(3110, 26, 178, 'من براى شما پيامبرى امين هستم.'),
(3111, 26, 179, 'از خدا بترسيد و از من اطاعت كنيد.'),
(3112, 26, 180, 'من از شما در برابر هدايت خود مزدى نمى‌طلبم. مزد من تنها بر عهده پروردگار جهانيان است.'),
(3113, 26, 181, 'پيمانه را تمام بپردازيد و كم‌فروشى مكنيد.'),
(3114, 26, 182, 'و با ترازوى درست وزن كنيد.'),
(3115, 26, 183, 'به مردم كم مدهيد و بى‌باكانه در زمين فساد مكنيد.'),
(3116, 26, 184, 'از آن كس كه شما و آفريدگان پيش از شما را آفريده است بترسيد.'),
(3117, 26, 185, 'گفتند: جز اين نيست كه تو را جادو كرده‌اند.'),
(3118, 26, 186, 'تو نيز بشرى همانند ما هستى و مى‌پنداريم كه دروغ مى‌گويى.'),
(3119, 26, 187, 'اگر راست مى‌گويى، پاره‌اى از آسمان را بر سر ما انداز.'),
(3120, 26, 188, 'گفت: پروردگار من به كارى كه مى‌كنيد داناتر است.'),
(3121, 26, 189, 'پس تكذيبش كردند و در آن روز ابرى، عذاب آنان را فرو گرفت. و آن عذاب روزى بزرگ بود.'),
(3122, 26, 190, 'هرآينه در اين عبرتى است، و بيشترينشان ايمان نياوردند.'),
(3123, 26, 191, 'هرآينه پروردگار تو پيروزمند و مهربان است.'),
(3124, 26, 192, 'و اين كتاب، نازل شده از جانب پروردگار جهانيان است.'),
(3125, 26, 193, 'آن را روح‌الامين نازل كرده است،'),
(3126, 26, 194, 'بر دل تو، تا از بيم‌دهندگان باشى.'),
(3127, 26, 195, 'به زبان عربى روشن.'),
(3128, 26, 196, 'و آن در نوشته‌هاى پيشينيان نيز هست.'),
(3129, 26, 197, 'آيا اين نشانه برايشان بسنده نيست كه علماى بنى‌اسرائيل از آن آگاهند؟'),
(3130, 26, 198, 'اگر آن را بر يكى از عجمان نازل كرده بوديم،'),
(3131, 26, 199, 'و بر ايشان مى‌خواندش، بدان ايمان نمى‌آوردند.'),
(3132, 26, 200, 'بدين‌سان قرآن را در دلهاى مجرمان راه داديم.'),
(3133, 26, 201, 'بدان ايمان نمى‌آورند تا عذاب دردآور را بنگرند.'),
(3134, 26, 202, 'و آن عذاب ناگهان و بى‌خبر بر آنان فرود مى‌آيد.'),
(3135, 26, 203, 'مى‌گويند: آيا ما را مهلتى خواهند داد؟'),
(3136, 26, 204, 'آيا به عذاب ما مى‌شتابند؟'),
(3137, 26, 205, 'ديدى كه هر چند سالها برخوردارشان ساختيم،'),
(3138, 26, 206, 'باز هم عذابى كه به آنها وعده شده بود بر سرشان آمد،'),
(3139, 26, 207, 'آن برخورداريها به حالشان سود نكرد؟'),
(3140, 26, 208, 'ما هيچ قريه‌اى را هلاك نكرديم جز آنكه برايشان بيم‌دهندگانى بودند.'),
(3141, 26, 209, 'تا پندشان دهند. زيرا ما ستمكار نيستيم.'),
(3142, 26, 210, 'و اين قرآن را شياطين نازل نكرده‌اند.'),
(3143, 26, 211, 'آنان نه درخور اين كارند و نه توان آن دارند.'),
(3144, 26, 212, 'شياطين را از شنيدن وحى معزول داشته‌اند.'),
(3145, 26, 213, 'پس با خداى يكتا خداى ديگرى را مخوان تا مباد در شمار مستحقان عذاب درآيى.'),
(3146, 26, 214, 'خويشاوندان نزديكت را بترسان.'),
(3147, 26, 215, 'در برابر هر يك از مؤمنان كه از تو پيروى مى‌كند بال فروتنى فرودآر.'),
(3148, 26, 216, 'و اگر بر تو عصيان ورزيدند بگو: من از كارهاى شما بيزارم.'),
(3149, 26, 217, 'و بر خداى پيروزمند مهربان توكل كن.'),
(3150, 26, 218, 'آن كه تو را مى‌بيند آنگاه كه برمى‌خيزى.'),
(3151, 26, 219, 'و نماز خواندنت را با ديگر نمازگزاران مى‌بيند.'),
(3152, 26, 220, 'هرآينه اوست شنواى دانا.'),
(3153, 26, 221, 'آيا شما را آگاه كنم كه شياطين بر چه كسى نازل مى‌شوند؟'),
(3154, 26, 222, 'بر هر بسيار دروغگوى گنهكار نازل مى‌شوند.'),
(3155, 26, 223, 'گوش فرا مى‌دهند و بيشترينشان دروغگويانند.'),
(3156, 26, 224, 'و گمراهان از پى شاعران مى‌روند.'),
(3157, 26, 225, 'آيا نديده‌اى كه شاعران در هر واديى سرگشته‌اند؟'),
(3158, 26, 226, 'و چيزها مى‌گويند كه خود عمل نمى‌كنند؟'),
(3159, 26, 227, 'مگر آنان كه ايمان آوردند و كارهاى شايسته كردند و خدا را فراوان ياد كردند و چون مورد ستم واقع شدند انتقام گرفتند. و ستمكاران به زودى خواهند دانست كه به چه مكانى باز مى‌گردند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سين. اين است آيات قرآن و كتاب روشنگر.'),
(3161, 27, 2, 'رهنمون و مژده‌اى است براى مؤمنان:'),
(3162, 27, 3, 'آنان كه نماز مى‌گزارند و زكات مى‌دهند و به روز قيامت يقين دارند.'),
(3163, 27, 4, 'اعمال آنهايى را كه به آخرت ايمان ندارند در نظرشان بياراستيم. از اين روى سرگشته مانده‌اند.'),
(3164, 27, 5, 'ايشان همان كسانند كه عذاب سخت از آن آنهاست و در آخرت زيانكارترند.'),
(3165, 27, 6, 'تو كسى هستى كه قرآن از جانب خداى حكيم و دانا به تو تلقين مى‌شود.'),
(3166, 27, 7, 'موسى به خانواده خود گفت: من از دور آتشى ديدم، زودا كه از آن برايتان خبرى بياورم يا پاره آتشى. شايد گرم شويد.'),
(3167, 27, 8, 'چون به آتش رسيد، ندايش دادند كه بركت داده شده، آن كه درون آتش است و آن كه در كنار آن است. و منزه است خداى يكتا، آن پروردگار جهانيان.'),
(3168, 27, 9, 'اى موسى، من خداى پيروزمند حكيم هستم.'),
(3169, 27, 10, 'عصايت را بيفكن. چون ديدش كه همانند مارى مى‌جنبد، گريزان بازگشت و به عقب ننگريست. اى موسى، مترس. پيامبران نبايد كه در نزد من بترسند.'),
(3170, 27, 11, 'مگر كسى كه مرتكب گناهى شده باشد و پس از بدكارى، نيكوكار شود. زيرا من آمرزنده و مهربانم.'),
(3171, 27, 12, 'دستت را در گريبان ببر تا بى‌هيچ آسيبى سفيد بيرون آيد. با نه نشانه نزد فرعون و قومش برو كه مردمى عصيانگرند.'),
(3172, 27, 13, 'چون نشانه‌هاى روشنگر ما را ديدند گفتند: اين جادويى آشكار است.'),
(3173, 27, 14, 'با آنكه در دل به آن يقين آورده بودند، ولى از روى ستم و برترى‌جويى انكارش كردند. پس بنگر كه عاقبت تبهكاران چگونه بود.'),
(3174, 27, 15, 'ما به داود و سليمان دانش داديم. گفتند: سپاس از آن خدايى است كه ما را بر بسيارى از بندگان مؤمن خود برترى داد.'),
(3175, 27, 16, 'و سليمان وارث داود شد و گفت: اى مردم، به ما زبان مرغان آموختند و از هر نعمتى ارزانى داشتند. و اين عنايتى است آشكار.'),
(3176, 27, 17, 'سپاهيان سليمان از جن و آدمى و پرنده گرد آمدند و آنها به صف مى‌رفتند.'),
(3177, 27, 18, 'تا به وادى مورچگان رسيدند. مورچه‌اى گفت: اى مورچگان، به لانه‌هاى خود برويد تا سليمان و لشكريانش شما را بى‌خبر در هم نكوبند.'),
(3178, 27, 19, 'سليمان از سخن او لبخند زد و گفت: اى پروردگار من، مرا وادار تا سپاس نعمت تو را كه بر من و پدر و مادر من ارزانى داشته‌اى به جاى آورم و كارهاى شايسته‌اى كنم كه تو خشنود شوى، و مرا به رحمت خود در شمار بندگان شايسته‌ات درآور.'),
(3179, 27, 20, 'در ميان مرغان جستجو كرد و گفت: چرا هدهد را نمى‌بينم، آيا از غايب‌شدگان است؟'),
(3180, 27, 21, 'به سخت‌ترين وجهى عذابش مى‌كنم يا سرش را مى‌برم، مگر آنكه براى من دليلى روشن بياورد.'),
(3181, 27, 22, 'درنگش به درازا نكشيد. بيامد و گفت: به چيزى دست يافته‌ام كه تو دست نيافته بودى و از سبا برايت خبرى درست آورده‌ام.'),
(3182, 27, 23, 'زنى را يافتم كه بر آنها پادشاهى مى‌كند. از هر نعمتى برخوردار است و تختى بزرگ دارد.'),
(3183, 27, 24, 'ديدم كه خود و مردمش به جاى خداى يكتا آفتاب را سجده مى‌كنند. و شيطان اعمالشان را در نظرشان بياراسته است و از راه خدا منحرفشان كرده است، چنان كه روى هدايت نخواهند ديد.'),
(3184, 27, 25, 'چرا خدايى را كه نهان آسمانها و زمين را آشكار مى‌كند و هر چه را پنهان مى‌داريد يا آشكار مى‌سازيد مى‌داند، سجده نكنند؟'),
(3185, 27, 26, 'خداى يكتا كه هيچ خدايى جز او نيست. پروردگار عرش عظيم.'),
(3186, 27, 27, 'گفت: اكنون بنگريم كه راست گفته‌اى يا در شمار دروغگويانى.'),
(3187, 27, 28, 'اين نامه مرا ببر و بر آنها افكن، سپس به يكسو شو و بنگر كه چه جواب مى‌دهند.'),
(3188, 27, 29, 'آن گفت: اى بزرگان، نامه‌اى گرامى به سوى من افكنده شد.'),
(3189, 27, 30, 'نامه از سليمان است و اين است: «به نام خداى بخشاينده مهربان.»'),
(3190, 27, 31, '«بر من برترى مجوييد و به تسليم نزد من بياييد.»'),
(3191, 27, 32, 'زن گفت: اى بزرگان، در كار من رأى بدهيد، كه تا شما حاضر نباشيد من هيچ كارى را فيصل نتوانم داد.'),
(3192, 27, 33, 'گفتند: ما قدرتمندان و خداوندان نبرد سخت هستيم. كارها به دست توست. بنگر كه چه فرمان مى‌دهى.'),
(3193, 27, 34, 'زن گفت: پادشاهان چون به قريه‌اى درآيند، تباهش مى‌كنند و عزيزانش را ذليل مى‌سازند. آرى چنين كنند.'),
(3194, 27, 35, 'من هديه‌اى نزدشان مى‌فرستم و مى‌نگرم كه قاصدان چه جواب مى‌آورند.'),
(3195, 27, 36, 'چون قاصد نزد سليمان آمد، سليمان گفت: آيا مى‌خواهيد به مال مرا يارى كنيد؟ آنچه خدا به من داده از آنچه به شما داده بهتر است. نه، شما به هديه خويش شادمان هستيد.'),
(3196, 27, 37, 'اكنون به نزدشان بازگرد. سپاهى بر سرشان مى‌كشيم كه هرگز طاقت آن را نداشته باشند. و به خوارى و خفت از آنجا بيرونشان مى‌كنيم.'),
(3197, 27, 38, 'گفت: اى بزرگان، كدام يك از شما تخت او را -پيش از آنكه به تسليم نزد من آيند- برايم مى‌آورد؟'),
(3198, 27, 39, 'عفريتى از ميان جنها گفت: من، قبل از آنكه از جايت برخيزى، آن را نزد تو حاضر مى‌كنم، كه من بر اين كار هم توانايم و هم امين.'),
(3199, 27, 40, 'و آن كس كه از علم كتاب بهره‌اى داشت گفت: من، پيش از آنكه چشم بر هم زنى، آن را نزد تو مى‌آورم. چون آن را نزد خود ديد، گفت: اين بخشش پروردگار من است، تا مرا بيازمايد كه سپاسگزارم يا كافر نعمت. پس هر كه سپاس گويد براى خود گفته است و هر كه كفران ورزد پروردگار من بى‌نياز و كريم است.'),
(3200, 27, 41, 'گفت: تختش را ديگرگون كنيد، ببينيم آن را باز مى‌شناسد يا از آنهاست كه باز نتوانند شناخت.'),
(3201, 27, 42, 'چون آمد گفتندش: آيا تخت تو چنين بود؟ گفت: گويى همان است. و ما پيش از اين آگاه شده بوديم و تسليم بوده‌ايم.'),
(3202, 27, 43, 'و او را از هر چه به جز خداى يكتا مى‌پرستيد، بازداشت. زيرا او در زمره كافران بود.'),
(3203, 27, 44, 'گفتندش: به صحن قصر درآى. چون بديدش پنداشت كه آبگيرى ژرف است. دامن از ساقهايش برگرفت. سليمان گفت: اين صحنى است صاف از آبگينه. گفت: اى پروردگار من، من بر خويشتن ستم كرده‌ام و اينك با سليمان در برابر پروردگار جهانيان تسليم شدم.'),
(3204, 27, 45, 'و بر قوم ثمود برادرشان صالح را فرستاديم كه: خداى يكتا را بپرستيد. ناگهان دو گروه شدند و با يكديگر به خصومت برخاستند.'),
(3205, 27, 46, 'گفت: اى قوم من، چرا پيش از نيكى بر بدى مى‌شتابيد؟ چرا از خدا آمرزش نمى‌خواهيد؟ شايد بر شما رحمت آورد.'),
(3206, 27, 47, 'گفتند: ما تو را و يارانت را به فال بد گرفته‌ايم. گفت: جزاى شما نزد خداست. اينك مردمى فريب‌خورده هستيد.'),
(3207, 27, 48, 'در شهر نه مرد ناصالح بودند كه در آن سرزمين فساد مى‌كردند نه اصلاح.'),
(3208, 27, 49, 'گفتند: به خدا سوگند خوريد كه بر او و كسانش شبيخون زنيم. و چون كسى به طلب خونش برخيزد، بگوييم: ما به هنگام هلاكت كسان او آنجا نبوده‌ايم، و ما راست گفتاريم.'),
(3209, 27, 50, 'و غافل بودند كه اگر آنها حيله‌اى انديشيده‌اند ما نيز حيله‌اى انديشيده‌ايم.'),
(3210, 27, 51, 'بنگر كه عاقبت مكرشان چه شد: ما آنها و قومشان را به تمامى هلاك كرديم.'),
(3211, 27, 52, 'آن خانه‌هاى آنهاست كه به كيفر ظلمى كه مى‌كردند خالى افتاده است. در آن دانايان را عبرتى است.'),
(3212, 27, 53, 'آنهايى را كه ايمان آورده بودند و پرهيزگار بودند، نجات داديم.'),
(3213, 27, 54, 'و لوط را به يادآر. آنگاه كه به قوم خود گفت: كارهاى زشت مرتكب مى‌شويد، در حالى كه خود به زشتى آن آگاهيد؟'),
(3214, 27, 55, 'چرا از روى شهوت به جاى زنان به مردان مى‌گراييد؟ شما مردمى نادان هستيد.'),
(3215, 27, 56, 'جواب قوم او جز اين نبود كه گفتند: خاندان لوط را از قريه خود بيرون كنيد. آنان دعوى پاكى مى‌كنند.'),
(3216, 27, 57, 'او و كسانش، جز زنش، را نجات داديم. چنان خواستيم كه آن زن از بازماندگان باشد.'),
(3217, 27, 58, 'بارانى بر آنها بارانيديم و باران تهديدشدگان چه بد بارانى است.'),
(3218, 27, 59, 'بگو: سپاس اللّه را و سلام بر بندگان برگزيده او. آيا اللّه بهتر است يا آن چيزها كه شريك او قرار مى‌دهند؟'),
(3219, 27, 60, 'يا آن كسى كه آسمانها و زمين را آفريد و از آسمان برايتان آب فرستاد، و بدان بوستانهايى خوش‌منظر رويانيديم كه شما را توان رويانيدن درختى از آن نيست. آيا با وجود اللّه خداى ديگرى هست؟ نه، آنها مردمى هستند از حق منحرف.'),
(3220, 27, 61, 'يا آن كه زمين را آرامگاه ساخت و در آن رودها پديد آورد و كوه‌ها، و ميان دو دريا مانعى قرار داد. آيا با وجود اللّه خداى ديگرى هست؟ نه، بيشترينشان نمى‌دانند.'),
(3221, 27, 62, 'يا آن كه درمانده را چون بخواندش پاسخ مى‌دهد و رنج از او دور مى‌كند و شما را در زمين جانشين پيشينيان مى‌سازد. آيا با وجود اللّه خداى ديگرى هست؟ چه اندك پند مى‌گيريد.'),
(3222, 27, 63, 'يا آن كه شما را در تاريكيهاى بيابان و دريا راه مى‌نمايد، و آن كه پيش از باران رحمتش بادها را به مژده مى‌فرستد. آيا با وجود اللّه خداى ديگرى هست؟ اللّه برتر است از هر چه شريك او مى‌سازند.'),
(3223, 27, 64, 'يا آن كه مخلوقات را مى‌آفريند سپس آنها را باز مى‌گرداند، و آن كه از آسمان و زمين به شما روزى مى‌دهد. آيا با وجود اللّه خداى ديگرى هست؟ بگو: اگر راست مى‌گوييد، برهان خويش بياوريد.'),
(3224, 27, 65, 'بگو: هيچ كس در آسمانها و زمين غيب نمى‌داند مگر اللّه. و نيز نمى‌دانند چه وقت زنده مى‌شوند.'),
(3225, 27, 66, 'در باب آخرت به يقين رسيدند؟ نه، همچنان در شك هستند، كه مردمى نابينايند.'),
(3226, 27, 67, 'كافران گفتند: چگونه اگر ما و پدرانمان خاك شديم ما را از قبر بيرون مى‌آورند؟'),
(3227, 27, 68, 'به ما و پدرانمان هم پيش از اين چنين وعده‌اى داده بودند. اينها چيزى جز افسانه‌هاى پيشينيان نيست.'),
(3228, 27, 69, 'بگو: در زمين سير كنيد و بنگريد كه پايان كار مجرمان چگونه بوده است.'),
(3229, 27, 70, 'بر ايشان اندوهگين مباش و از حيله‌اى كه مى‌انديشند تنگدل مشو.'),
(3230, 27, 71, 'مى‌گويند: اگر راست مى‌گوييد، آن وعده چه وقت خواهد بود؟'),
(3231, 27, 72, 'بگو: شايد پاره‌اى از آنچه به شتاب مى‌طلبيد نزديك شما باشد.'),
(3232, 27, 73, 'و پروردگار تو فضل خود را بر مردم ارزانى مى‌دارد، ولى بيشترينشان شكر نمى‌گويند.'),
(3233, 27, 74, 'و پروردگار تو مى‌داند كه چه چيز را در دل نهان كرده‌اند و چه چيز را آشكار مى‌سازند.'),
(3234, 27, 75, 'و در آسمانها و زمين هيچ رازى نيست، مگر آنكه در كتاب مبين آمده است.'),
(3235, 27, 76, 'اين قرآن بسيارى از چيزهايى را كه بنى‌اسرائيل در آن اختلاف دارند، بر ايشان حكايت مى‌كند.'),
(3236, 27, 77, 'و براى مؤمنان هدايت و رحمت است.'),
(3237, 27, 78, 'هرآينه پروردگار تو به رأى خويش ميانشان قضاوت خواهد كرد، كه او پيروزمند و داناست.'),
(3238, 27, 79, 'پس بر خدا توكل كن كه تو همراه با حقيقتى روشن هستى.'),
(3239, 27, 80, 'تو نمى‌توانى مردگان را شنوا سازى و آواز خود را به گوش كرانى كه از تو روى مى‌گردانند برسانى.'),
(3240, 27, 81, 'تو نمى‌توانى كوران را از گمراهيشان راه نمايى. آواز خود را تنها به گوش كسانى توانى رساند كه به آيات ما ايمان آورده‌اند و مسلمان هستند.'),
(3241, 27, 82, 'چون فرمان قيامت مقرّر گردد، بر ايشان جنبنده‌اى از زمين بيرون مى‌آوريم كه با آنان سخن بگويد كه اين مردم به آيات ما يقين نمى‌آوردند.'),
(3242, 27, 83, 'روزى كه از هر ملتى فوجى -از آنان كه آيات ما را تكذيب مى‌كردند- گرد مى‌آوريم و آنان را به صف برانند؛'),
(3243, 27, 84, 'تا چون به محشر برسند، گويد: آيا شما آيات مرا تكذيب مى‌كرديد و حال آنكه دانش شما بدان احاطه نيافته بود؟ آن چه كارى بود كه مى‌كرديد؟'),
(3244, 27, 85, 'به كيفر ستمى كه مى‌كردند حكم عذاب بر آنها مقرر شود. بى‌آنكه هيچ سخنى بگويند.'),
(3245, 27, 86, 'آيا نمى‌بينند كه شب را پديد آورديم تا در آن بيارامند و روز را روشنى بخشيديم. هرآينه در اين براى مؤمنان عبرتهاست.'),
(3246, 27, 87, 'روزى كه در صور دميده شود و همه كسانى كه در آسمانها و زمين هستند -مگر آن كه خدا بخواهد- بترسند و همه خوار و ذليل به سوى او روان شوند.'),
(3247, 27, 88, 'و كوه‌ها را بينى، پندارى كه جامدند، حال آنكه به سرعت ابر مى‌روند. كار خداوند است كه هر چيزى را به كمال پديد آورده است. هرآينه او به هر چه مى‌كنيد آگاه است.'),
(3248, 27, 89, 'هر كس كه كار نيكى كند بهتر از آن را پاداش گيرد و نيكوكاران از وحشت آن روز در امان باشند.'),
(3249, 27, 90, 'و آنان را كه كارهاى بد مى‌كنند سرنگون در آتش اندازند، آيا جز بر وفق كارهايى كه كرده‌ايد كيفر يابيد؟'),
(3250, 27, 91, 'به من فرمان شده كه پروردگار اين شهر را بپرستم. شهرى كه خداوندى كه همه چيز از آن اوست حرمتش نهاده، و مأمورم كه از مسلمانان باشم.'),
(3251, 27, 92, 'و قرآن را تلاوت كنم. پس هر كه هدايت يافت به سود خود هدايت يافته است و هر كه گمراه شد، بگو: جز اين نيست كه من بيم‌دهنده‌اى هستم.'),
(3252, 27, 93, 'و بگو: سپاس خدا راست. آيات خود را به شما خواهد نمود تا آنها را بشناسيد. و پروردگار تو از هيچ كارى كه مى‌كنيد بى‌خبر نيست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا. سين. ميم.'),
(3254, 28, 2, 'اين است آيات اين كتاب روشنگر.'),
(3255, 28, 3, 'براى آنها كه ايمان مى‌آورند داستان راستين موسى و فرعون را بر تو مى‌خوانيم.'),
(3256, 28, 4, 'فرعون در آن سرزمين برترى جست و مردمش را فرقه‌فرقه ساخت. فرقه‌اى را زبون مى‌داشت و پسرانشان را مى‌كشت و زنانشان را زنده مى‌گذاشت كه او از تبهكاران بود.'),
(3257, 28, 5, 'و ما بر آن هستيم كه بر مستضعفان روى زمين نعمت دهيم و آنان را پيشوايان سازيم و وارثان گردانيم.'),
(3258, 28, 6, 'و آنها را در آن سرزمين مكانت بخشيم و به فرعون و هامان و لشكريانشان چيزى را كه از آن مى‌ترسيدند نشان دهيم.'),
(3259, 28, 7, 'و به مادر موسى وحى كرديم كه: شيرش بده و اگر بر او بيمناك شدى به دريايش بينداز و مترس و غمگين مشو، او را به تو باز مى‌گردانيم و در شمار پيامبرانش مى‌آوريم.'),
(3260, 28, 8, 'خاندان فرعون يافتندش تا دشمنشان و سبب اندوهشان گردد. فرعون و هامان و لشكرهاشان خطا مى‌كردند.'),
(3261, 28, 9, 'زن فرعون گفت: اين مايه شادمانى من و توست. او را مكشيد، شايد به ما سودى برساند يا او را به فرزندى گيريم. و نمى‌دانستند كه چه مى‌كنند.'),
(3262, 28, 10, 'مادر موسى را دل تهى شد. و اگر دلش را قوى نكرده بوديم كه از مؤمنان باشد، نزديك بود كه آن راز را فاش سازد.'),
(3263, 28, 11, 'و به خواهر او گفت: از پى او برو. و زن بى‌آنكه آنان دريابند از دور در او مى‌نگريست.'),
(3264, 28, 12, 'پستان همه دايگان را از پيش بر او حرام كرده بوديم. آن زن گفت: آيا مى‌خواهيد شما را به خانواده‌اى راهنمايى كنم كه او را برايتان نگه دارند و نيكخواهش باشند؟'),
(3265, 28, 13, 'پس او را نزد مادرش برگردانيديم تا چشمان آن زن روشن گردد و غمگين نباشد و بداند كه وعده خدا حق است، ولى بيشترينشان نمى‌دانند.'),
(3266, 28, 14, 'چون به حد بلوغ رسيد و برومند شد، او را فرزانگى و دانش داديم و نيكوكاران را چنين پاداش مى‌دهيم.'),
(3267, 28, 15, 'بى‌خبر از مردم شهر به شهر داخل شد. دو تن را ديد كه با هم نزاع مى‌كنند. اين يك از پيروانش بود و آن يك از دشمنانش. آن كه از پيروانش بود بر ضد آن ديگر كه از دشمنانش بود از او يارى خواست. موسى مشتى بر او نواخت و او را كشت. گفت: اين كار شيطان بود. او به آشكارا دشمنى گمراه‌كننده است.'),
(3268, 28, 16, 'گفت: اى پروردگار من، من به خود ستم كردم، مرا بيامرز. و خدايش بيامرزيد. زيرا آمرزنده و مهربان است.'),
(3269, 28, 17, 'گفت: اى پروردگار من، به پاس نعمتى كه بر من عطا كردى هرگز پشتيبان گنهكاران نخواهم شد.'),
(3270, 28, 18, 'ديگر روز در شهر ترسان و چشم بر راه حادثه مى‌گرديد. مردى كه ديروز از او مدد خواسته بود باز هم از او مدد خواست. موسى به او گفت: تو به آشكارا گمراه هستى.'),
(3271, 28, 19, 'چون خواست مردى را كه دشمن هر دو آنها بود بزند، گفت: اى موسى، آيا مى‌خواهى همچنان كه ديروز يكى را كشتى مرا نيز بكشى؟ تو مى‌خواهى كه در اين سرزمين جبارى باشى و نمى‌خواهى كه از مصلحان باشى.'),
(3272, 28, 20, 'مردى از دور دست شهر دوان آمد و گفت: اى موسى، مهتران شهر در باره تو رأى مى‌زنند تا بكشندت. بيرون برو. من خيرخواه تو هستم.'),
(3273, 28, 21, 'ترسان و نگران از شهر بيرون شد. گفت: اى پروردگار من، مرا از ستمكاران رهايى بخش.'),
(3274, 28, 22, 'چون به جانب مدين روان شد، گفت: شايد پروردگار من مرا به راه راست رهبرى كند.'),
(3275, 28, 23, 'چون به آب مدين رسيد، گروهى از مردم را ديد كه چارپايان خود را آب مى‌دهند و پشت سرشان دو زن را ديد كه گوسفندان خود را باز مى‌رانند. گفت: شما چه مى‌كنيد؟ گفتند: ما آب نمى‌دهيم تا آنگاه كه چوپانان باز گردند، كه پدر ما پيرى بزرگوار است.'),
(3276, 28, 24, 'گوسفندانشان را آب داد. سپس به سايه بازگشت و گفت: اى پروردگار من، من به آن نعمتى كه برايم مى‌فرستى نيازمندم.'),
(3277, 28, 25, 'يكى از آن دو زن كه به آزرم راه مى‌رفت نزد او آمد و گفت: پدرم تو را مى‌خواند تا مزد آب‌دادنت را بدهد. چون نزد او آمد و سرگذشت خويش بگفت، گفت: مترس، كه تو از مردم ستمكاره نجات يافته‌اى.'),
(3278, 28, 26, 'يكى از آن دو گفت: اى پدر، اجيرش كن، كه اگر چنين كنى بهترين مرد نيرومند امينى است كه اجير كرده‌اى.'),
(3279, 28, 27, 'گفت: مى‌خواهم يكى از اين دو دخترم را زن تو كنم به شرط آنكه هشت سال مزدور من باشى. و اگر ده سال را تمام كنى، خود خدمتى است و من نمى‌خواهم كه تو را به مشقت افكنم. ان شاء اللّه مرا از صالحان خواهى يافت.'),
(3280, 28, 28, 'گفت: اين است پيمان ميان من و تو. هر يك از دو مدت را كه سپرى سازم بر من ستمى نخواهد رفت و خدا بر آنچه مى‌گوييم وكيل است.'),
(3281, 28, 29, 'چون موسى مدت را به سر آورد و با زنش روان شد، از سوى طور آتشى ديد. به كسان خود گفت: درنگ كنيد. آتشى ديدم. شايد از آن خبرى يا پاره آتشى بياورم تا گرم شويد.'),
(3282, 28, 30, 'چون نزد آتش آمد، از كناره راست وادى در آن سرزمين مبارك، از آن درخت ندا داده شد كه: اى موسى، من خداى يكتا پروردگار جهانيانم.'),
(3283, 28, 31, 'عصايت را بينداز. چون ديدش كه همانند مارى مى‌جنبد، گريزان بازگشت و به عقب ننگريست. اى موسى، پيش آى و مترس. تو در امان هستى.'),
(3284, 28, 32, 'دست خود در گريبان ببر تا بيرون آيد سفيد بى‌هيچ آسيبى. و تا از وحشت بيارامى دست خود در بغل كن. اين دو از جانب پروردگارت حجتهاى تو براى فرعون و مهتران اوست، كه آنان مردمى نافرمانند.'),
(3285, 28, 33, 'گفت: اى پروردگار من، يكى از ايشان را كشته‌ام، و مى‌ترسم مرا بكشند.'),
(3286, 28, 34, 'و برادرم هارون به زبان از من فصيح‌تر است، او را به مدد من بفرست تا مرا تصديق كند، كه بيم آن دارم كه دروغگويم شمارند.'),
(3287, 28, 35, 'گفت: تو را به برادرت قويدست خواهيم كرد و برايتان حجتى قرار مى دهيم. به سبب نشانه‌هايى كه شما را داده‌ايم، به شما دست نخواهند يافت. شما و پيروانتان پيروز خواهيد بود.'),
(3288, 28, 36, 'چون موسى آيات روشن ما را نزدشان آورد گفتند: اين جادويى است برساخته، ما از نياكانمان چنين چيزهايى نشنيده‌ايم.'),
(3289, 28, 37, 'و موسى گفت: پروردگار من به آن كسى كه از جانب او به هدايت آمده است و عاقبت پسنديده در آن سراى از آن اوست، آگاه‌تر است. زيرا ستمكاران رستگار نمى‌شوند.'),
(3290, 28, 38, 'فرعون گفت: اى مهتران، من براى شما خدايى جز خود نمى‌شناسم. اى هامان، براى من آجر بپز و طارمى بلند بساز، مگر خداى موسى را ببينم، كه دروغگويش پندارم.'),
(3291, 28, 39, 'او و لشكرهايش به ناحق در زمين سركشى كردند و پنداشتند كه به نزد ما بازگردانيده نمى‌شوند.'),
(3292, 28, 40, 'پس او و لشكرهايش را گرفتيم و به دريا افكنديم. بنگر كه عاقبت كار ستمكاران چگونه بود.'),
(3293, 28, 41, 'و آنان را از آن گونه پيشوايانى ساختيم كه مردم را به آتش دعوت مى‌كنند و در روز قيامت كسى ياريشان نكند.'),
(3294, 28, 42, 'در اين دنيا از پى‌شان لعنت روانه ساختيم و در روز قيامت از زشت‌رويانند.'),
(3295, 28, 43, 'پس از آنكه مردم روزگار پيشين را هلاك ساختيم، به موسى كتاب داديم تا مردم را بصيرت و هدايت و رحمت باشد. شايد پند گيرند.'),
(3296, 28, 44, 'آنگاه كه به موسى فرمان امر نبوت را وحى كرديم تو نه در جانب غربى طور بودى و نه از حاضران.'),
(3297, 28, 45, 'ولى ما از آن پس نسلهايى را بيافريديم كه عمرشان به درازا كشيد. و تو در ميان مردم مدين مقيم نبودى كه آيات ما را بر آنها بخوانى. ولى ما بوديم كه پيامبرانى مى‌فرستاديم.'),
(3298, 28, 46, 'تو در كنار طور نبودى آنگاه كه موسى را ندا در داديم. ولى اين رحمتى است از جانب پروردگارت تا مردمى را كه پيش از تو بيم‌دهنده‌اى نداشتند، بيم دهى. باشد كه پندپذير شوند.'),
(3299, 28, 47, 'تا چون به سبب اعمالى كه مرتكب شده‌اند مصيبتى به آنها رسد نگويند: اى پروردگار ما، چرا پيامبرى بر ما نفرستادى تا از آيات تو پيروى كنيم و از مؤمنان باشيم؟'),
(3300, 28, 48, 'چون پيامبر راستگوى از جانب ما بر آنان مبعوث شد، گفتند: چرا آنچه به موسى داده شده به او داده نشده؟ آيا اينان پيش از اين به آنچه به موسى داده شده بود كافر نشده بودند؟ و گفتند كه اين هر دو، دو جادوست همانند هم و ما به هيچ يك ايمان نمى‌آوريم.'),
(3301, 28, 49, 'بگو: اگر راست مى‌گوييد، از جانب خداوند كتابى بياوريد كه از اين دو بهتر راه بنمايد تا من هم از آن پيروى كنم.'),
(3302, 28, 50, 'پس اگر اجابتت نكردند، بدان كه از پى هواى نفس خويش مى‌روند و كيست گمراه‌تر از آن كس كه بى‌آنكه راهنمايى از خدا خواهد، از پى هواى نفس خويش رود؟ زيرا خدا مردم ستمكاره را هدايت نمى‌كند.'),
(3303, 28, 51, 'برايشان سخن در سخن پيوستيم، باشد كه پندپذير گردند.'),
(3304, 28, 52, 'كسانى كه پيش از اين كتاب، كتابشان داده بوديم بدان ايمان مى‌آورند.'),
(3305, 28, 53, 'و چون بر آنان تلاوت شد گفتند: بدان ايمان آورديم. حقى است از جانب پروردگار ما و ما پيش از آن تسليم بوده‌ايم.'),
(3306, 28, 54, 'اينان به سبب صبرى كه كرده‌اند دو بار پاداش داده شوند. اينان بدى را به نيكى مى‌زدايند و نيز از آنچه روزيشان داده‌ايم انفاق مى‌كنند.'),
(3307, 28, 55, 'و چون سخن لغوى بشنوند، از آن اعراض كنند و گويند: كردارهاى ما از آن ما و كردارهاى شما از آن شما. به سلامت بمانيد. ما خواستار جاهلان نيستيم.'),
(3308, 28, 56, 'تو هر كس را كه بخواهى هدايت نمى‌كنى. خداست كه هر كه را بخواهد هدايت مى‌كند و او هدايت يافتگان را بهتر مى‌شناسد.'),
(3309, 28, 57, 'گفتند: اگر از كيش تو پيروى كنيم، ما را از سرزمينمان بر مى‌كنند. آيا آنها را در حرمى امن جاى نداده‌ايم كه همه گونه ثمرات در آن فراهم مى‌شود، و اين رزقى است از جانب ما؟ ولى بيشترينشان نمى‌دانند.'),
(3310, 28, 58, 'چه بسا مردم قريه‌اى را هلاك ساختيم كه از زندگى خويش دچار سرمستى شده بودند و اين خانه‌هاى آنهاست كه بعد از آنها، جز اندك زمانى كس در آنها سكنى نگرفت و ما وارث آنان بوديم.'),
(3311, 28, 59, 'پروردگار تو مردم قريه‌ها را هلاك نكرد تا آنگاه كه از خودشان پيامبرى بر آنها مبعوث داشت و او آيات ما را بر آنها بخواند. و ما قريه‌ها را نابود نكرده‌ايم مگر آنكه مردمش ستمكار بوده‌اند.'),
(3312, 28, 60, 'چيزهايى كه به شما داده شده است، برخوردارى و آرايش اين زندگى دنيوى است. حال آنكه آنچه نزد خداست، بهتر و ماندنى‌تر است. چرا در نمى‌يابيد؟'),
(3313, 28, 61, 'آيا آن كس كه او را وعده‌هاى نيك داده‌ايم و آن وعده‌ها را خواهد ديد، همانند كسى است كه او را از متاع اينجهانى برخوردار كرده‌ايم و در روز قيامت هم به بازخواستش فرا مى‌خوانيم؟'),
(3314, 28, 62, 'و روزى كه آنان را ندا مى‌دهد و مى‌گويد: شريكانى را كه براى من مى‌پنداشته‌ايد كجايند؟'),
(3315, 28, 63, 'آنان كه حكم درباره‌شان محقق شده مى‌گويند: اى پروردگار ما، اينان كسانى هستند كه ما گمراهشان كرديم. از آن رو گمراهشان كرديم كه خود گمراه بوديم. از آنها بيزارى مى‌جوييم و به تو مى‌گرويم. اينان ما را نمى‌پرستيده‌اند.'),
(3316, 28, 64, 'و گفته شود: اكنون بتانتان را بخوانيد. مى‌خوانندشان، ولى به آنها پاسخ نمى‌دهند. و چون عذاب را ببينند آرزو كنند كه كاش از هدايت‌يافتگان مى‌بودند.'),
(3317, 28, 65, 'و روزى كه خدا ندايشان دهد و گويد: پيامبران را چگونه اجابت كرديد؟'),
(3318, 28, 66, 'در اين روز خبرها بر آنان پوشيده شود و از يكديگر نيز نتوانند پرسيد.'),
(3319, 28, 67, 'اما آن كس كه توبه كرده و ايمان آورده و عمل صالح به جاى آورده، شايد كه از رستگاران باشد.'),
(3320, 28, 68, 'پروردگار تو هر چه را كه بخواهد مى‌آفريند و برمى‌گزيند. ولى ايشان را توان اختيار نيست. منزه است خدا و از هر چه برايش شريك مى‌سازند برتر است.'),
(3321, 28, 69, 'و پروردگار تو هر چه را كه در دل پنهان داشته‌اند يا آشكار كرده‌اند مى‌داند.'),
(3322, 28, 70, 'اوست خداى يكتا. هيچ خدايى به جز او نيست. ستايش خاص اوست، چه در اين جهان و چه در جهان ديگر. و فرمان فرمان اوست. و همگان به او بازگردانده مى‌شويد.'),
(3323, 28, 71, 'بگو: چه تصور مى‌كنيد اگر الله شبتان را تا روز قيامت طولانى سازد؟ جز او كدام خداست كه شما را روشنى ارزانى دارد؟ مگر نمى‌شنويد؟'),
(3324, 28, 72, 'بگو: چه تصور مى‌كنيد اگر الله روزتان را تا روز قيامت طولانى سازد؟ جز او كدام خداست كه شما را شب ارزانى دارد كه در آن بياساييد؟ مگر نمى‌بينيد؟'),
(3325, 28, 73, 'و از رحمت او آنكه براى شما شب و روز را پديد آورد تا در آن يك بياساييد و در اين يك به طلب روزى برخيزيد، باشد كه سپاس گوييد.'),
(3326, 28, 74, 'و روزى كه خدا ندايشان دهد و گويد: شريكانى را كه براى من تصور مى‌كرديد، كجايند؟'),
(3327, 28, 75, 'و از هر ملتى شاهدى بياوريم و گوييم: برهان خويش بياوريد. آنگاه بدانند كه حق از آن الله است و آن بتان كه به دروغ خدا مى‌خواندند تباه شوند.'),
(3328, 28, 76, 'قارون از قوم موسى بود كه بر آنها افزونى جست. و به او چنان گنجهايى داديم كه حمل كليدهايش بر گروهى از مردم نيرومند دشوار مى‌نمود. آنگاه كه قومش به او گفتند: سرمست مباش، زيرا خدا سرمستان را دوست ندارد،'),
(3329, 28, 77, 'در آنچه خدايت ارزانى داشته، سراى آخرت را بجوى و بهره خويش را از دنيا فراموش مكن. و همچنان كه خدا به تو نيكى كرده نيكى كن و در زمين از پى فساد مرو كه خدا فسادكنندگان را دوست ندارد.'),
(3330, 28, 78, 'گفت: آنچه به من داده شده، در خور دانش من بوده است. آيا ندانسته است كه خدا پيش از او نسلهايى را هلاك كرده كه قوتشان از او افزون‌تر و شمارشان بيشتر بوده است؟ و اين مجرمان را از گناهشان نمى‌پرسند.'),
(3331, 28, 79, 'آراسته به زيورهاى خود به ميان مردمش آمد. آنان كه خواستار زندگى دنيوى بودند گفتند: اى كاش آنچه به قارون داده شده ما را نيز مى‌بود، كه او سخت برخوردار است.'),
(3332, 28, 80, 'اما دانش‌يافتگان گفتند: واى بر شما. براى آنها كه ايمان مى‌آورند و كارهاى شايسته مى‌كنند ثواب خدا بهتر است. و بدين ثواب جز صابران نرسند.'),
(3333, 28, 81, 'پس او و خانه‌اش را در زمين فرو برديم و در برابر خدا هيچ گروهى نداشت كه ياريش كند و خود يارى كردن خويش نمى‌توانست.'),
(3334, 28, 82, 'روز ديگر آن كسان كه ديروز آرزو مى‌كردند كه به جاى او مى‌بودند، مى‌گفتند: شگفتا كه خدا روزى هر كس را كه خواهد فراوان كند يا تنگ سازد. اگر خدا به ما نيز نعمت فراوان داده بود، ما را نيز در زمين فرو مى‌برد. نمى‌بينى كه كافران رستگار نمى‌شوند؟'),
(3335, 28, 83, 'اين سراى آخرت را از آن كسانى ساخته‌ايم كه در اين جهان نه خواهان برترى‌جويى هستند و نه خواهان فساد. و سرانجام نيك از آن پرهيزگاران است.'),
(3336, 28, 84, 'هر كس كه كار نيكى به جاى آرد، بهتر از آن را پاداش يابد و هر كه مرتكب گناهى شود، پس آنان كه مرتكب گناه مى‌شوند جز به اندازه عملشان مجازات نشوند.'),
(3337, 28, 85, 'آن كس كه قرآن را بر تو فرض كرده است تو را به وعده‌گاهت باز مى‌گرداند. بگو: پروردگار من بهتر مى‌داند كه چه كسى بر راه راست است و چه كسى در گمراهى آشكار است.'),
(3338, 28, 86, 'اگر رحمت پروردگارت نبود، اميد آن را نداشتى كه اين كتاب بر تو القا شود. پس نبايد پشتيبان كافران باشى.'),
(3339, 28, 87, 'پس از آنكه آيات خدا بر تو نازل شد، تو را از آن منحرف نكنند. مردم را به سوى پروردگارت بخوان و از مشركان مباش.'),
(3340, 28, 88, 'با خداى يكتا خداى ديگرى را مخوان. هيچ خدايى جز او نيست. هر چيزى نابودشدنى است مگر ذات او. فرمان، فرمان اوست و همه به او بازگردانيده شويد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف، لام، ميم.'),
(3342, 29, 2, 'آيا مردم پنداشته‌اند كه چون بگويند: ايمان آورديم، رها شوند و ديگر آزمايش نشوند؟'),
(3343, 29, 3, 'هر آينه مردمى را كه پيش از آنها بودند آزموديم، تا خدا كسانى را كه راست گفته‌اند معلوم دارد و دروغگويان را متمايز گرداند.'),
(3344, 29, 4, 'آيا آنان كه مرتكب گناه مى‌شوند پنداشته‌اند كه از ما مى‌گريزند؟ چه بد داورى مى‌كنند.'),
(3345, 29, 5, 'هر كس كه به ديدار خدا اميد مى‌دارد بداند كه وعده خدا آمدنى است و او شنوا و داناست.'),
(3346, 29, 6, 'هر كه جهاد كند به سود خود كرده است، زيرا خدا از همه جهانيان بى‌نياز است.'),
(3347, 29, 7, 'گناهان آنان را كه ايمان آوردند و كارهاى شايسته كردند، مى‌زداييم و بهتر از آنچه عمل كرده‌اند پاداششان مى‌دهيم.'),
(3348, 29, 8, 'به آدمى سفارش كرديم كه به پدر و مادر خود نيكى كند. و اگر آن دو بكوشند كه تو چيزى را كه بدان آگاه نيستى شريك من قرار دهى، اطاعتشان مكن. بازگشت همه شما به سوى من است و شما را به كارهايى كه مى‌كرده‌ايد آگاه مى‌كنم.'),
(3349, 29, 9, 'كسانى را كه ايمان آوردند و كردارهاى شايسته كردند در زمره صالحان در آوريم.'),
(3350, 29, 10, 'بعضى از مردم مى‌گويند: به خدا ايمان آورده‌ايم. و چون در راه خدا آزارى ببينند، آن آزار را چون عذاب خدا به شمار مى‌آورند. و چون از سوى پروردگار تو مددى رسد، مى‌گويند: ما نيز با شما بوده‌ايم. آيا خدا به آنچه در دلهاى مردم جهان مى‌گذرد آگاه‌تر نيست؟'),
(3351, 29, 11, 'البته خدا مى‌داند كه مؤمنان چه كسانند و منافقان چه كسان.'),
(3352, 29, 12, 'كافران به مؤمنان گفتند: به راه ما بياييد، بار گناهتان بر گردن ما. حال آنكه اينان بار گناه كسى را بر گردن نخواهند گرفت و دروغ مى‌گويند.'),
(3353, 29, 13, 'بلكه بار گناه خود و بارهايى با بار خود بر گردن خواهند گذاشت و در روز قيامت به سبب دروغهايى كه بر هم مى‌بافته‌اند بازخواست خواهند شد.'),
(3354, 29, 14, 'ما نوح را بر مردمش به پيامبرى فرستاديم. او هزار سال و پنجاه سال كم در ميان آنان بزيست. و چون مردمى ستم پيشه بودند، طوفانشان فروگرفت.'),
(3355, 29, 15, 'او و كسانى را كه در كشتى بودند نجات داديم و آن طوفان را نشان عبرتى براى جهانيان گردانيديم.'),
(3356, 29, 16, 'و نيز ابراهيم را به پيامبرى فرستاديم، آنگاه كه به مردمش گفت: خداى يكتا را بپرستيد و از او بترسيد. اين برايتان بهتر است اگر مردمى دانا باشيد.'),
(3357, 29, 17, 'شما، سواى خداى يكتا بتانى را مى‌پرستيد و دروغهاى بزرگ مى‌پردازيد. آنهايى كه سواى خداى يكتا مى‌پرستيد، نمى‌توانند شما را روزى دهند. از خداى يكتا روزى بجوييد و او را عبادت كنيد و سپاس گوييد، زيرا به سوى او بازگردانيده مى‌شويد.'),
(3358, 29, 18, 'اگر شما تكذيب مى‌كنيد، مردمانى هم كه پيش از شما بودند تكذيب مى‌كردند. و بر عهده پيامبر جز تبليغى روشنگر هيچ نيست.'),
(3359, 29, 19, 'آيا نمى‌بينند كه خدا چگونه مخلوق را مى‌آفريند و پس از نيستى بازش مى‌گرداند؟ اين كار بر خدا آسان است.'),
(3360, 29, 20, 'بگو: در زمين سير كنيد و بنگريد كه چگونه خدا موجودات را آفريده. سپس آفرينش باز پسين را پديد مى‌آورد. زيرا خداوند بر هر چيزى تواناست.'),
(3361, 29, 21, 'هر كه را بخواهد عذاب مى‌كند و بر هر كه خواهد رحمت مى‌آورد، و همه بدو بازگردانيده مى‌شويد.'),
(3362, 29, 22, 'شما نمى‌توانيد از خدا بگريزيد، نه در زمين و نه در آسمان؛ و شما را جز او هيچ كارساز و ياورى نيست.'),
(3363, 29, 23, 'و آنان كه به آيات خدا و ديدار او ايمان ندارند، از رحمت من نوميدند و آنها راست عذابى دردآور.'),
(3364, 29, 24, 'پاسخ قوم او جز اين نبود كه گفتند: بكشيدش يا بسوزانيدش. و خدايش از آتش برهانيد. و در اين براى مؤمنان عبرتهاست.'),
(3365, 29, 25, 'گفت: شما بتانى را به جاى خداى يكتا به خدايى گرفته‌ايد تا در اين زندگانى دنيا ميانتان دوستى باشد، ولى در روز قيامت منكر يكديگر مى‌شويد و يكديگر را لعنت مى‌كنيد و جايتان در آتش است و هيچ ياورى نداريد.'),
(3366, 29, 26, 'لوط به او ايمان آورد و گفت: من به سوى پروردگارم مهاجرت مى‌كنم، زيرا او پيروزمند و حكيم است.'),
(3367, 29, 27, 'و اسحاق و يعقوب را به او بخشيديم و در فرزندان او پيامبرى و كتاب نهاديم. و پاداشش را در دنيا داديم، و او در آخرت از صالحان است.'),
(3368, 29, 28, 'و لوط را پيامبرى داديم. به قوم خود گفت: شما كارى زشت را پيشه كرده‌ايد كه هيچ يك از مردم جهان پيش از شما چنين نمى‌كرده است.'),
(3369, 29, 29, 'آيا با مردان مى‌آميزيد و راه‌ها را مى‌بريد و در محفل خود مرتكب كارهاى ناپسند مى‌شويد؟ جواب قومش جز اين نبود كه گفتند: اگر راست مى‌گويى عذاب خدا را بر سر ما بياور.'),
(3370, 29, 30, 'گفت: اى پروردگار من، مرا بر مردم تبهكار نصرت ده.'),
(3371, 29, 31, 'چون فرستادگان ما ابراهيم را مژده آوردند، گفتند: ما مردم اين قريه را هلاك خواهيم كرد، كه مردمى ستمكاره هستند.'),
(3372, 29, 32, 'گفت: لوط در آنجاست. گفتند: ما بهتر مى‌دانيم چه كسى در آنجاست. او و خاندانش را -جز زنش را كه در همان جا خواهد ماند- نجات مى‌دهيم.'),
(3373, 29, 33, 'چون فرستادگان ما نزد لوط آمدند، غمگين شد و در كارشان بماند. گفتند: مترس و غمگين مباش، ما تو و خاندانت را -جز زنت را كه در همان جا خواهد ماند- نجات مى‌دهيم.'),
(3374, 29, 34, 'ما بر مردم اين قريه به سبب فسقى كه مرتكب مى‌شوند، از آسمان عذاب نازل مى‌كنيم.'),
(3375, 29, 35, 'هر آينه از آن قريه براى خردمندان عبرتى روشن بر جاى گذاشتيم.'),
(3376, 29, 36, 'و برادرشان شعيب را به مديَن فرستاديم. گفت: اى قوم من، خدا را بپرستيد و به روز قيامت اميدوار باشيد و در زمين به تبهكارى مكوشيد.'),
(3377, 29, 37, 'پس تكذيبش كردند و زلزله آنان را فروگرفت و در خانه‌هاى خود بر جاى مُردند.'),
(3378, 29, 38, 'و عاد و ثمود را هلاك كرديم. و اين از مسكنهايشان برايتان آشكار است. شيطان كارهايشان را در نظرشان بياراست و آنان را از راه باز داشت. و حال آنكه مردمى صاحب بصيرت بودند.'),
(3379, 29, 39, 'و نيز قارون و فرعون و هامان را كه موسى با دليلهاى روشن خود نزد آنها آمد. آنها در زمين سركشى مى‌كردند ولى نتوانستند از خدا بگريزند.'),
(3380, 29, 40, 'همه را به گناهشان فروگرفتيم: بر بعضى بادهاى ريگ‌بار فرستاديم، بعضى را فرياد سهمناك فروگرفت، بعضى را در زمين فرو برديم، بعضى را غرقه ساختيم. و خدا به آنها ستم نمى‌كرد، آنها خود به خويشتن ستم كرده بودند.'),
(3381, 29, 41, 'مثَل آنان كه سواى خدا را اوليا گرفتند، مثَل عنكبوت است كه خانه‌اى بساخت. و كاش مى‌دانستند، هر آينه سست‌ترين خانه‌ها خانه عنكبوت است.'),
(3382, 29, 42, 'خداى يكتا مى‌داند كه چه چيزهايى را سواى او به خدايى مى‌خوانند، و اوست پيروزمند و حكيم.'),
(3383, 29, 43, 'اين مثَلها را براى مردم مى‌زنيم و آنها را جز دانايان درنمى‌يابند.'),
(3384, 29, 44, 'خدا آسمانها و زمين را به راستى بيافريد و در اين عبرتى است براى مؤمنان.'),
(3385, 29, 45, 'هر چه را از اين كتاب بر تو وحى شده است تلاوت كن. و نماز بگزار، كه نماز آدمى را از فحشا و منكر باز مى‌دارد و ذكر خدا بزرگ‌تر است و خدا به كارهايى كه مى‌كنيد آگاه است.'),
(3386, 29, 46, 'با اهل كتاب، جز به نيكوترين شيوه‌اى مجادله مكنيد. مگر با آنها كه ستم پيشه كردند. و بگوييد: به آنچه بر ما نازل شده و آنچه بر شما نازل شده است ايمان آورده‌ايم. و خداى ما و خداى شما يكى است و ما در برابر او گردن نهاده‌ايم.'),
(3387, 29, 47, 'همچنان اين كتاب را بر تو نازل كرديم و اهل كتاب به آن ايمان مى‌آورند و از اينان نيز كسانى به آن ايمان مى‌آورند و جز كافران كسى آيات ما را انكار نمى‌كند.'),
(3388, 29, 48, 'تو پيش از قرآن هيچ كتابى را نمى‌خواندى و به دست خويش كتابى نمى‌نوشتى. اگر چنان بود، اهل باطل به شك افتاده بودند.'),
(3389, 29, 49, 'بلكه قرآن آياتى است روشن، كه در سينه اهل دانش جاى دارد. و آيات ما را جز ستمكاران انكار نمى‌كنند.'),
(3390, 29, 50, 'و گفتند: چرا از جانب پروردگارش آياتى بر او نازل نمى‌شود؟ بگو: جز اين نيست كه آيات در نزد خداست و جز اين نيست كه من بيم‌دهنده‌اى آشكار هستم.'),
(3391, 29, 51, 'آيا آنان را بسنده نيست كه بر تو كتاب فرستاده‌ايم و بر آنها خوانده مى‌شود. در اين كتاب براى مؤمنان رحمت و اندرز است.'),
(3392, 29, 52, 'بگو: خدا به گواهى ميان من و شما كافى است. آنچه را كه در آسمانها و زمين است مى‌داند. كسانى كه به باطل گرويدند و به خدا كافر شدند، زيانكارانند.'),
(3393, 29, 53, 'از تو به شتاب عذاب را مى‌طلبند. اگر آن را زمانى معين نبود، بر آنها نازل شده بود. و ناگهان و بى‌آنكه خبر شوند بر آنها فرود خواهد آمد.'),
(3394, 29, 54, 'از تو به شتاب عذاب را مى‌طلبند، حال آنكه جهنم بر كافران احاطه دارد.'),
(3395, 29, 55, 'روزى كه عذاب از فراز سر و زير پاى، آنها را در بر گيرد و خدا بگويد: به خاطر اعمالى كه مى‌كرده‌ايد اينك عذاب را بچشيد.'),
(3396, 29, 56, 'اى بندگان من كه به من ايمان آورده‌ايد، زمين من فراخ است، پس تنها مرا بپرستيد.'),
(3397, 29, 57, 'هر كسى چشنده طعم مرگ است. و به سوى ما بازگردانده شويد.'),
(3398, 29, 58, 'كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند در غرفه‌هاى بهشت جاى دهيم. از زير آن جويهاى آب روان است. در آنجا جاودانه بمانند. عمل‌كنندگان را چه نيكو مزدى است؛'),
(3399, 29, 59, 'آنها كه شكيبايى ورزيدند و بر پروردگارشان توكل مى‌كنند.'),
(3400, 29, 60, 'چه بسيار جنبندگانى كه ياراى تحصيل روزى خويش ندارند و خدا آنها را و شما را روزى مى‌دهد. و او شنوا و داناست.'),
(3401, 29, 61, 'اگر از آنها بپرسى: چه كسى آسمانها و زمين را آفريده و آفتاب و ماه را رام كرده است؟ خواهند گفت: خداى يكتا. پس، از چه روى عقيدت ديگرگون مى‌كنند؟'),
(3402, 29, 62, 'خدا روزى هر يك از بندگانش را كه بخواهد فراوان مى‌كند يا بر او تنگ مى‌گيرد. زيرا خدا به همه چيز داناست.'),
(3403, 29, 63, 'اگر از آنها بپرسى: چه كسى از آسمان باران فرستاد و زمين مرده را بدان زنده ساخت؟ خواهند گفت: خداى يكتا. بگو: سپاس خداى راست. ولى بيشترينشان درنمى‌يابند.'),
(3404, 29, 64, 'زندگانى اين دنيا چيزى جز لهو و لعب نيست. اگر بدانند، سراى آخرت سراى زندگانى است.'),
(3405, 29, 65, 'چون به كشتى نشستند خدا را با اخلاص در دين او خواندند و چون نجاتشان داد و به خشكى آورد، شرك آوردند؛'),
(3406, 29, 66, 'تا نعمتى را كه به آنان عطا كرده بوديم كفران كنند و برخوردار شوند. زودا كه خواهند دانست.'),
(3407, 29, 67, 'آيا ندانسته‌اند كه حرم را جاى امن مردم قرار داديم، حال آنكه مردم در اطرافشان به اسارت ربوده مى‌شوند؟ آيا به باطل ايمان مى‌آورند و نعمت خدا را كفران مى‌كنند؟'),
(3408, 29, 68, 'پس كيست ستمكارتر از آن كه بر خدا دروغ مى‌بندد يا به هنگامى كه حق به سوى او آيد تكذيبش مى‌كند؟ آيا در جهنم جايگاهى براى كافران نيست؟'),
(3409, 29, 69, 'كسانى را كه در راه ما مجاهدت كنند، به راه‌هاى خويش هدايتشان مى‌كنيم، و خدا با نيكوكاران است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف، لام، ميم.'),
(3411, 30, 2, 'روميان مغلوب شدند،'),
(3412, 30, 3, 'در نزديك اين سرزمين. و پس از مغلوب شدن بار ديگر غالب خواهند شد،'),
(3413, 30, 4, 'در مدت چند سال. فرمان، فرمان خداست؛ چه پيش از پيروزى و چه بعد از آن. و در آن روز مؤمنان شادمان مى‌شوند،'),
(3414, 30, 5, 'به يارى خدا. خدا هر كس را كه بخواهد يارى مى‌كند، زيرا پيروزمند و مهربان است.'),
(3415, 30, 6, 'خدا وعده داده است و وعده خود خلاف نكند، ولى بيشتر مردم نمى‌دانند.'),
(3416, 30, 7, 'آنان به ظاهر زندگى دنيا آگاهند و از آخرت بى‌خبرند.'),
(3417, 30, 8, 'آيا با خود نمى‌انديشند كه خدا آسمانها و زمين را و هر چه را ميان آنهاست، جز به حق و تا مدتى محدود، نيافريده است؟ و بسيارى از مردم به ديدار پروردگارشان ايمان ندارند.'),
(3418, 30, 9, 'آيا در زمين نمى‌گردند، تا بنگرند كه چگونه بوده است عاقبت كسانى كه پيش از آنها مى‌زيسته‌اند؟ كسانى كه توانشان بيشتر بوده است و زمين را به شخم زدن زير و رو كرده و بيشتر از ايشان آبادش ساخته بودند و پيامبرانى با معجزه‌ها بر آنها مبعوث شده بود. خدا به ايشان ستم نمى‌كرد، آنان خود به خويشتن ستم مى‌كردند.'),
(3419, 30, 10, 'سپس عاقبت آن كسان كه مرتكب كارهاى بد شدند ناگوارتر بود. زيرا اينان آيات خدا را دروغ انگاشتند و آنها را به مسخره گرفتند.'),
(3420, 30, 11, 'خدا موجودات را مى‌آفريند، آنگاه ديگر بارشان بازمى‌گرداند، آنگاه همه به سوى او بازگردانده مى‌شويد.'),
(3421, 30, 12, 'روزى كه قيامت برپا شود، گناهكاران حيرت‌زده بمانند.'),
(3422, 30, 13, 'ايشان را از ميان بتانشان شفيعانى نيست. و ديگر به بتان خود باور ندارند.'),
(3423, 30, 14, 'و چون قيامت برپا شود، در آن روز از يكديگر ببرند.'),
(3424, 30, 15, 'اما آنها كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، در باغى به شادمانى پردازند.'),
(3425, 30, 16, 'و اما آنهايى كه كافر شده‌اند و آيات ما را تكذيب مى‌كنند و ديدار آخرت را دروغ مى‌انگارند، همه را در عذاب حاضر آرند.'),
(3426, 30, 17, 'خدا را بستاييد بدان هنگام كه به شب درمى‌آييد و بدان هنگام كه به صبح درمى‌آييد.'),
(3427, 30, 18, 'سپاس او راست در آسمانها و زمين، به هنگام شب و به هنگامى كه به نيمروز مى‌رسيد.'),
(3428, 30, 19, 'زنده را از مرده بيرون آرد و مرده را از زنده. و زمين را پس از مُردنش زنده مى‌سازد و شما نيز اين چنين از گورها بيرون شويد.'),
(3429, 30, 20, 'از نشانه‌هاى قدرت اوست كه شما را از خاك بيافريد تا انسان شديد و به هر سو پراكنده گشتيد.'),
(3430, 30, 21, 'و از نشانه‌هاى قدرت اوست كه برايتان از جنس خودتان همسرانى آفريد. تا به ايشان آرامش يابيد، و ميان شما دوستى و مهربانى نهاد. در اين عبرتهايى است براى مردمى كه تفكر مى‌كنند.'),
(3431, 30, 22, 'و از نشانه‌هاى قدرت اوست آفرينش آسمانها و زمين، و اختلاف زبانها و رنگهايتان. در اين عبرتهايى است براى دانايان.'),
(3432, 30, 23, 'و از نشانه‌هاى قدرت او خوابيدن شماست در شب و روز و به طلب روزى برخاستن شماست. در اين عبرتهايى است براى مردمى كه مى‌شنوند.'),
(3433, 30, 24, 'و از نشانه‌هاى قدرت اوست كه برق را به قصد بيم و اميد، به شما مى‌نماياند. و از آسمان باران مى‌فرستد تا زمين مرده را بدان زنده سازد. در اين عبرتهايى است براى كسانى كه به عقل درمى‌يابند.'),
(3434, 30, 25, 'و از نشانه‌هاى قدرت اوست كه آسمان و زمين به فرمان او برپاى ايستاده‌اند. سپس شما را از زمين فرا مى‌خواند و شما از زمين بيرون مى‌آييد.'),
(3435, 30, 26, 'از آنِ اوست هر چه در آسمانها و زمين است، و همه فرمانبردار او هستند.'),
(3436, 30, 27, 'اوست كه موجودات را مى‌آفريند، سپس آنها را بازمى‌گرداند. و اين كار بر او آسانتر است. او راست صفت برترى در آسمانها و زمين و اوست پيروزمند و حكيم.'),
(3437, 30, 28, 'براى شما از خودتان مثَلى مى‌آورد: مگر بندگانتان در آنچه به شما روزى داده‌ايم با شما شريك هستند تا در مال با شما برابر باشند و همچنان كه شما آزادان از يكديگر مى‌ترسيد از آنها هم بيمناك باشيد؟ آيات را براى مردمى كه تعقل مى‌كنند اين سان تفصيل مى‌دهيم.'),
(3438, 30, 29, 'آرى، ستمكاران بى هيچ دانشى از هواى نفس خود پيروى كرده‌اند. آن را كه خدا گمراه كرده است چه كسى هدايت مى‌كند؟ آنها را هيچ ياورى نيست.'),
(3439, 30, 30, 'به يكتاپرستى روى به دين آور. فطرتى است كه خدا همه را بدان فطرت بيافريده است و در آفرينش خدا تغييرى نيست. دين پاك و پايدار اين است. ولى بيشتر مردم نمى‌دانند.'),
(3440, 30, 31, 'به او بازگرديد، و از او بترسيد، و نماز بگزاريد و از مشركان مباشيد.'),
(3441, 30, 32, 'از آنان مباشيد كه دين خود را پاره‌پاره كردند و فرقه‌فرقه شدند و هر فرقه‌اى به هر چه داشت دلخوش بود.'),
(3442, 30, 33, 'چون بر آدميان زيانى رسد، پروردگارشان را بخوانند و به درگاه او توبه كنند؛ و چون رحمت خويش به آنها بچشاند، گروهى را بينى كه به پروردگارشان شرك مى‌آورند.'),
(3443, 30, 34, 'پس نعمتى را كه به آنها داده‌ايم كفران كنند. اكنون متمتع شويد، زودا كه خواهيد دانست.'),
(3444, 30, 35, 'آيا برايشان دليلى نازل كرديم تا از چيزى كه با خدا شريك مى‌سازند سخن گفته باشد؟'),
(3445, 30, 36, 'چون به مردم رحمتى بچشانيم، شادمان مى‌شوند؛ و چون به سبب كارهايى كه كرده‌اند رنجى به آنها رسد، ناگهان نوميد مى‌گردند.'),
(3446, 30, 37, 'آيا نمى‌بينند كه خدا روزى هر كس را كه بخواهد فراوان مى‌كند يا او را تنگ روزى مى‌سازد؟ در اين عبرتهايى است براى مردمى كه ايمان مى‌آورند.'),
(3447, 30, 38, 'حق خويشاوند و مسكين و در راه مانده را ادا كن. اين بهتر است براى كسانى كه خشنودى خدا را مى‌جويند و ايشان رستگارانند.'),
(3448, 30, 39, 'مالى كه به ربا مى‌دهيد تا در اموال مردم افزون شود، نزد خدا هيچ افزون نمى‌شود؛ و مالى كه براى خشنودى خدا از بابت زكات مى‌پردازيد، كسانى كه چنين كنند پاداش مضاعف دارند.'),
(3449, 30, 40, 'خداست آن كه شما را بيافريد، سپس روزى داد، سپس مى‌ميراند، سپس زنده مى‌كند. آيا كسانى كه شريك خدا مى‌سازيد، هيچ از اين كارها توانند؟ منزه است او و از هر چه برايش شريك مى‌آورند برتر است.'),
(3450, 30, 41, 'به سبب اعمال مردم، فساد در خشكى و دريا آشكار شد تا به آنان جزاى بعضى از كارهايشان را بچشاند، باشد كه بازگردند.'),
(3451, 30, 42, 'بگو: در زمين بگرديد و بنگريد كه عاقبت پيشينيان كه بيشترينشان از مشركان بودند چگونه بوده است.'),
(3452, 30, 43, 'به دين پاك پايدار روى بياور، پيش از آنكه روزى فرا رسد كه از امر خدا بازگشتنش نباشد. در آن روز مردم دو گروه شوند:'),
(3453, 30, 44, 'كسانى كه كافر باشند كفرشان به زيانشان باشد و آنها كه كارى شايسته كرده باشند براى خود پاداشى نيكو آماده كرده‌اند.'),
(3454, 30, 45, 'تا كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند از فضل خود پاداش دهد. زيرا خدا كافران را دوست ندارد.'),
(3455, 30, 46, 'و از نشانه‌هاى قدرت او آنكه بادهاى مژده‌دهنده را مى‌فرستد، تا رحمت خود را به شما بچشاند و تا كشتيها به فرمان او روان باشند و از فضل او روزى بجوييد. باشد كه سپاس گوييد.'),
(3456, 30, 47, 'و پيش از تو پيامبرانى را بر قومشان فرستاديم. آنان با دليلهاى روشن خود نزدشان آمدند. و ما از كسانى كه گناه كردند انتقام گرفتيم، و يارى دادن مؤمنان بر عهده ما بود.'),
(3457, 30, 48, 'خداست كه بادها را مى‌فرستد تا ابرها را برانگيزد و چنان كه خواهد بر آسمان بگسترد، و آن را پاره‌پاره كند و بينى كه باران از خلال ابرها بيرون مى‌آيد. و چون باران را به هر كه خواهد از بندگانش برساند شادمان شوند،'),
(3458, 30, 49, 'اگر چه پيش از آنكه باران بر آنها ببارد نوميد بوده‌اند.'),
(3459, 30, 50, 'پس به آثار رحمت خدا بنگر كه چگونه زمين را پس از مُردنش زنده مى‌كند. چنين خدايى زنده‌كننده مردگان است و بر هر كارى تواناست.'),
(3460, 30, 51, 'و اگر بادى بفرستيم كه كشته‌ها را زرد ببينند، از آن پس همه كافر شوند.'),
(3461, 30, 52, 'تو نمى‌توانى مردگان را شنوا سازى و اگر اينان نيز از تو بازگردند، آواز خود را به گوش آن كران نرسانى،'),
(3462, 30, 53, 'تو هدايت‌كننده نابينايان از گمراهيشان نيستى. تو صدايت را تنها به گوش كسانى مى‌رسانى كه به آيات ما ايمان آورده‌اند و تسليم شده‌اند.'),
(3463, 30, 54, 'خداست كه شما را ناتوان بيافريد، و پس از ناتوانى نيرومند ساخت، آنگاه پس از نيرومندى ناتوانى و پيرى آورد. هر چه بخواهد مى‌آفريند و او دانا و تواناست.'),
(3464, 30, 55, 'روزى كه قيامت برپا شود مجرمان سوگند خورند كه جز ساعتى در گور نيارميده‌اند. آرى اينچنين از حق منحرف مى‌شدند.'),
(3465, 30, 56, 'آنان كه دانش و ايمان داده شده‌اند، گويند: شما بر وفق كتاب خدا تا روز قيامت در گور آرميده‌ايد و اين روز قيامت است و شما نمى‌دانسته‌ايد.'),
(3466, 30, 57, 'در اين روز پوزش ستمكاران به حالشان سود ندهد و از آنها نخواهند كه توبه كنند.'),
(3467, 30, 58, 'ما در اين قرآن براى مردم هر گونه مثَلى آورده‌ايم. و چون آيه‌اى برايشان بياورى كافران خواهند گفت: شما ياوه گويانى بيش نيستيد.'),
(3468, 30, 59, 'اينچنين خدا بر دلهاى كسانى كه از دانايى بى‌بهره‌اند، مهر مى‌نهد.'),
(3469, 30, 60, 'پس صبر كن كه وعده خدا حق است. مباد آنان كه به مرحله يقين نرسيده‌اند، تو را بى‌ثبات و سبكسر گردانند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف. لام. ميم.'),
(3471, 31, 2, 'اينها آيه‌هاى كتاب حكمت‌آميز است.'),
(3472, 31, 3, 'نيكوكاران را هدايت و رحمتى است:'),
(3473, 31, 4, 'آنان كه نماز مى‌گزارند و زكات مى‌دهند و به آخرت يقين دارند.'),
(3474, 31, 5, 'اينان از جانب پروردگارشان هدايت يافته‌اند و اينان رستگارانند.'),
(3475, 31, 6, 'بعضى از مردم خريدار سخنان بيهوده‌اند، تا به نادانى مردم را از راه خدا گمراه كنند و قرآن را به مسخره مى‌گيرند. نصيب اينان عذابى است خوار كننده.'),
(3476, 31, 7, 'و چون آيات ما بر آنها خوانده شود، با خودپسندى روى برگردانند، چنان كه گويى نشنيده‌اند. يا همانند كسى كه گوشهايش سنگين شده باشد. او را به عذابى دردآور بشارت ده.'),
(3477, 31, 8, 'نصيب آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، باغهاى پر نعمت بهشت است.'),
(3478, 31, 9, 'در آنجا جاويدانند، وعده خدا راست است. او پيروزمند و حكيم است.'),
(3479, 31, 10, 'آسمانها را بى‌هيچ ستونى كه ببينيد بيافريد و بر روى زمين كوه‌ها را بيفكند تا نلرزاندتان و از هرگونه جنبنده‌اى در آن بپراكند. و از آسمان آب فرستاديم و در زمين هرگونه گياه نيكويى رويانيديم.'),
(3480, 31, 11, 'اين آفرينش خداست. پس به من بنمايانيد آنها كه غير او هستند چه آفريده‌اند. بلكه ظالمان در گمراهى آشكارى هستند.'),
(3481, 31, 12, 'هر آينه به لقمان حكمت داديم و گفتيم: خدا را سپاس گوى، زيرا هر كه سپاس گويد به سود خود سپاس گفته، و هر كه ناسپاسى كند خدا بى‌نياز و ستودنى است.'),
(3482, 31, 13, 'و لقمان به پسرش گفت -و او را پند مى‌داد- كه: اى پسرك من، به خدا شرك مياور، زيرا شرك ستمى است بزرگ.'),
(3483, 31, 14, 'آدمى را درباره پدر و مادرش سفارش كرديم. مادرش به او حامله شد و هر روز ناتوانتر مى‌شد و پس از دو سال از شيرش باز گرفت. و سفارش كرديم كه: مرا و پدر و مادرت را شكر گوى كه سرانجام تو نزد من است،'),
(3484, 31, 15, 'اگر آن دو به كوشش از تو بخواهند تا چيزى را كه نمى‌دانى چيست با من شريك گردانى اطاعتشان مكن. در آنها با آنها به وجهى پسنديده زندگى كن و خود، راه كسانى را كه به درگاه من باز مى‌گردند در پيش گير. بازگشت همه شما به سوى من است و من از كارهايى كه مى‌كرده‌ايد آگاهتان مى‌كنم.'),
(3485, 31, 16, 'اى پسرك من، اگر عمل به قدر يك دانه خردل در درون صخره‌اى يا در آسمانها يا در زمين باشد، خدا به حسابش مى‌آورد، كه او ريزبين و آگاه است.'),
(3486, 31, 17, 'اى پسرك من، نماز بگزار، و امر به معروف و نهى از منكر كن و بر هر چه بر تو رسد صبر كن كه اين از كارهايى است كه نبايد سهلش انگاشت.'),
(3487, 31, 18, 'به تكبر از مردم روى مگردان و به خودپسندى بر زمين راه مرو، زيرا خدا هيچ به نازخرامنده فخرفروشى را دوست ندارد.'),
(3488, 31, 19, 'در رفتارت راه ميانه را برگزين و آوازت را فرود آر، زيرا ناخوش‌ترين بانگها بانگ خران است.'),
(3489, 31, 20, 'آيا نديده‌ايد كه خدا هر چه را كه در آسمانها و زمين است رام شما كرده است و نعمتهاى خود را چه آشكار و چه پنهان به تمامى بر شما ارزانى داشته؟ و پاره‌اى از مردم بى‌هيچ دانشى يا راهنمايى و كتاب روشنى، درباره خدا جدال مى‌كنند.'),
(3490, 31, 21, 'و چون به آنها بگويند: از آنچه خدا نازل كرده است متابعت كنيد؛ گويند: نه، ما از آيينى كه پدران خود را بر آن يافته‌ايم متابعت مى‌كنيم. اگر چه شيطان به عذاب آتششان فراخوانده باشد.'),
(3491, 31, 22, 'هر كه روى خويش به خدا كند و نيكوكار باشد هرآينه به دستگيره استوارى چنگ زده است، و پايان همه كارها به سوى خداست.'),
(3492, 31, 23, 'آن كس كه كافر شده است كفرش تو را غمگين نسازد. بازگشتشان نزد ماست. پس به كارهايى كه كرده‌اند آگاهشان مى‌كنيم. زيرا خدا به آنچه در دلها مى‌گذرد آگاه است.'),
(3493, 31, 24, 'اندكى كاميابشان مى‌سازيم، سپس به بيچارگى به عذاب سختشان مى‌كشانيم.'),
(3494, 31, 25, 'اگر از آنها بپرسى: چه كسى آسمانها و زمين را آفريده است؟ خواهند گفت: خدا. بگو: سپاس خدا را. بلكه بيشترشان نادانند.'),
(3495, 31, 26, 'از آن خداست آنچه در آسمانها و زمين است و خدا بى‌نياز و ستودنى است.'),
(3496, 31, 27, 'و اگر همه درختان روى زمين قلم شوند و دريا مركب و هفت درياى ديگر به مددش بيايد، سخنان خدا پايان نمى‌يابد. و خدا پيروزمند و حكيم است.'),
(3497, 31, 28, 'آفرينش همه شما و باز زنده كردنتان تنها همانند زنده كردن يك تن است. هرآينه خدا شنوا و بيناست.'),
(3498, 31, 29, 'آيا نديده‌اى كه خدا از شب مى‌كاهد و به روز مى‌افزايد و از روز مى‌كاهد و به شب مى‌افزايد و آفتاب و ماه را رام خود ساخت كه هر يك تا زمانى معين در حركت است؟ و خدا به كارهايى كه مى‌كنيد آگاه است.'),
(3499, 31, 30, 'اين بدان سبب است كه خداى يكتا حق است و هر چه به جز او مى‌خوانند باطل است و خداست بلندمرتبه و بزرگوار.'),
(3500, 31, 31, 'آيا نديده‌اى كه كشتى به نعمت خدا در دريا روان مى‌شود تا خدا پاره‌اى از آيات قدرت خود را به شما بنماياند؟ در اين، براى مردم شكيباى سپاسگزار عبرتهاست.'),
(3501, 31, 32, 'و چون موجهاى عظيم همانند ابرها آنها را فرو پوشد، خدا را از روى اخلاص در دين بخوانند. و چون نجاتشان دهد و به خشكى برد، برخى به عهد خويش وفا كنند. و جز غداران كافرنعمت، آيات ما را انكار نكنند.'),
(3502, 31, 33, 'اى مردم، از پروردگارتان بترسيد و از روزى كه هيچ پدرى كيفر فرزند را به عهده نگيرد و هيچ فرزندى كيفر پدر را عهده‌دار نشود پروا كنيد. وعده خدا حق است. زندگى دنيا فريبتان ندهد و نيز شيطان فريبكار به كرم خدا مغرورتان نسازد.'),
(3503, 31, 34, 'خداست كه مى‌داند كه قيامت چه وقت مى‌آيد. اوست كه باران مى‌باراند و از آنچه در رحمهاست آگاه است. و هيچ كس نمى‌داند كه فردا چه چيز به دست خواهد آورد و كسى نمى‌داند كه در كدام زمين خواهد مرد. خدا دانا و آگاه است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف. لام. ميم.'),
(3505, 32, 2, 'نازل شدن اين كتاب، كه در آن هيچ شكى نيست، از جانب پروردگار جهانيان است.'),
(3506, 32, 3, 'آيا مى‌گويند دروغى است كه خود به هم بافته؟ نه، سخنى است بر حق از جانب پروردگارت، تا مردمى را كه پيش از تو بيم‌دهنده‌اى نداشته‌اند بيم دهى. شايد به راه هدايت افتند.'),
(3507, 32, 4, 'خداست كه آسمانها و زمين را و آنچه ميان آنهاست در شش روز بيافريد و آنگاه به عرش پرداخت. شما را جز او كارساز و شفيعى نيست. آيا پند نمى‌گيريد؟'),
(3508, 32, 5, 'كار را از آسمان تا زمين سامان مى‌دهد. سپس در روزى كه مقدار آن هزار سال است -چنان كه مى‌شماريد- به سوى او بالا مى‌رود.'),
(3509, 32, 6, 'اوست كه داناى نهان و آشكار است. پيروزمند و مهربان است.'),
(3510, 32, 7, 'آن كه هر چه را آفريد به نيكوترين وجه آفريد و خلقت انسان را از گل آغاز كرد.'),
(3511, 32, 8, 'سپس نسل او را از عصاره آبى بى‌مقدار پديد آورد.'),
(3512, 32, 9, 'آنگاه بالاى او راست كرد و از روح خود در آن بدميد. و برايتان گوش و چشمها و دلها آفريد. چه اندك شكر مى‌گوييد.'),
(3513, 32, 10, 'و گفتند: آيا وقتى كه در زمين ناپديد شويم، آفرينش تازه‌اى خواهيم يافت؟ آرى، ايشان به ديدار با پروردگارشان ايمان ندارند.'),
(3514, 32, 11, 'بگو: فرشته مرگ كه موكل بر شماست، شما را مى‌ميراند. سپس به سوى پروردگارتان باز گردانيده مى‌شويد.'),
(3515, 32, 12, 'تو را عبرت است آنگاه كه مجرمان را در نزد پروردگارشان سرافكنده بينى. گويند: اى پروردگار ما، ديديم و شنيديم. اكنون ما را بازگردان تا كارى شايسته كنيم، كه اينك به يقين رسيده‌ايم.'),
(3516, 32, 13, 'اگر مى‌خواستيم، هدايت هر كس را به او ارزانى مى‌داشتيم. ولى وعده من كه جهنم را از همه جنيان و آدميان پر مى‌كنم، حق است.'),
(3517, 32, 14, 'به كيفر آنكه ديدار چنين روزى را فراموش كرده بوديد، اكنون بچشيد. ما نيز شما را از ياد برده‌ايم. به سزاى كارهايى كه مى‌كرده‌ايد عذاب جاويد را بچشيد.'),
(3518, 32, 15, 'تنها كسانى به آيات ما ايمان آورده‌اند كه چون آيات ما را بشنوند به سجده بيفتند و پروردگارشان را به پاكى بستايند و سركشى نكنند.'),
(3519, 32, 16, 'از بستر خواب پهلو تهى مى‌كنند، پروردگارشان را با بيم و اميد مى‌خوانند و از آنچه به آنها داده‌ايم انفاق مى‌كنند.'),
(3520, 32, 17, 'و هيچ كس از آن مايه شادمانى خبر ندارد كه به پاداش كارهايى كه مى‌كرده برايش اندوخته شده است.'),
(3521, 32, 18, 'آيا آن كس كه ايمان آورده همانند كسى است كه عصيان مى‌ورزد؟ نه، برابر نيستند.'),
(3522, 32, 19, 'اما آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، به پاداش اعمالى كه مى‌كرده‌اند منزلگاهى در باغهاى بهشت خواهند يافت.'),
(3523, 32, 20, 'و اما عصيان‌پيشگان، منزلگاهشان آتش است. هرگاه كه بخواهند از آن بيرون آيند، بار ديگر آنها را به درون آتش باز گردانند و بگويندشان: بچشيد عذاب آتشى را كه دروغش مى‌پنداشتيد.'),
(3524, 32, 21, 'و عذاب دنيا را پيش از آن عذاب بزرگ‌تر به ايشان بچشانيم، باشد كه باز گردند.'),
(3525, 32, 22, 'كيست ستمكارتر از آن كسى كه او را به آيات پروردگارش پند دهند ولى اعراض كند؟ ما از مجرمان انتقام مى‌گيريم.'),
(3526, 32, 23, 'هرآينه به موسى كتاب داديم. از ديدار او در ترديد مباش. و آن را راهنماى بنى‌اسرائيل قرار داديم.'),
(3527, 32, 24, 'از ميان آن قوم پيشوايانى پديد آورديم كه چون صبورى پيشه كردند و به آيات ما يقين داشتند، به فرمان ما به هدايت مردم پرداختند.'),
(3528, 32, 25, 'هرآينه پروردگار تو در روز قيامت در آنچه اختلاف مى‌كردند ميانشان داورى خواهد كرد.'),
(3529, 32, 26, 'آيا آن همه مردمى را كه پيش از ايشان هلاك كرديم و اكنون اينان در خانه‌هايشان راه مى‌روند سبب هدايتشان نشد؟ در اين عبرتهاست، چرا نمى‌شنوند؟'),
(3530, 32, 27, 'آيا نمى‌بينند كه آب را به زمين خشك و بى‌گياه روانه مى‌سازيم تا كشتزارها برويانيم و چارپايان و خودشان از آن بخورند؟ چرا نمى‌بينند؟'),
(3531, 32, 28, 'مى‌گويند: اگر راست مى‌گوييد، پيروزى چه وقت خواهد بود؟'),
(3532, 32, 29, 'بگو: در روز پيروزى، ايمان آوردن كافران سودشان ندهد، و مهلتشان ندهند.'),
(3533, 32, 30, 'پس، از ايشان اعراض كن و منتظر باش، كه آنها نيز در انتظارند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اى پيامبر، از خدا بترس و از كافران و منافقان اطاعت مكن. زيرا خدا دانا و حكيم است.'),
(3535, 33, 2, 'از هر چه از پروردگارت به تو وحى مى‌شود اطاعت كن. زيرا خدا بدانچه مى‌كنيد آگاه است.'),
(3536, 33, 3, 'و بر خدا توكل كن، زيرا خدا كارسازى را بسنده است.'),
(3537, 33, 4, 'خدا در درون هيچ مردى دو قلب ننهاده است. و زنانتان را كه مادر خود مى‌خوانيد مادرتان قرار نداد و فرزندخواندگانتان را فرزندانتان نساخت. اينها چيزهايى است كه به زبان مى‌گوييد و سخن حق از آن خداست و اوست كه راه مى‌نمايد.'),
(3538, 33, 5, 'پسرخواندگان را به نام پدرشان بخوانيد كه در نزد خدا منصفانه‌تر است. اگر پدرشان را نمى‌شناسيد، برادران دينى و موالى شما باشند. اگر پيش از اين خطايى كرده‌ايد باكى نيست، مگر آنكه به قصد دل كنيد. و خدا آمرزنده و مهربان است.'),
(3539, 33, 6, 'پيامبر به مؤمنان از خودشان سزاتر است و زنانش مادران مؤمنان هستند و در كتاب خدا خويشاوندان نسبى از مؤمنان و مهاجران به يكديگر سزاوارترند، مگر آنكه بخواهيد به يكى از دوستان خود نيكى كنيد. و اين حكم در كتاب خدا مكتوب است.'),
(3540, 33, 7, 'و آن هنگام كه از پيامبران پيمان گرفتيم و از تو و از نوح و ابراهيم و موسى و عيسى بن مريم و از همه آنها پيمانى سخت گرفتيم،'),
(3541, 33, 8, 'تا راستگويان را از صدقشان بپرسد، و براى كافران عذابى دردآور مهيا كرده است.'),
(3542, 33, 9, 'اى كسانى كه ايمان آورده‌ايد، از نعمتى كه خدا به شما داده است ياد كنيد، بدان هنگام كه لشكرها بر سر شما تاخت آوردند و ما باد را و لشكرهايى را كه نمى‌ديديد بر سرشان فرستاديم و خدا بدانچه مى‌كرديد بينا بود.'),
(3543, 33, 10, 'آنگاه كه از سمت بالا و از سمت پايين بر شما تاختند، چشمها خيره شد و دلها به گلوگاه رسيده بود و به خدا گمانهاى گوناگون مى‌برديد.'),
(3544, 33, 11, 'در آنجا مؤمنان در معرض امتحان درآمدند و سخت متزلزل شدند.'),
(3545, 33, 12, 'زيرا منافقان و آنهايى كه در دلهايشان بيمارى است، مى‌گفتند: خدا و پيامبرش جز فريب به ما وعده‌اى نداده‌اند.'),
(3546, 33, 13, 'و گروهى از آنها گفتند: اى مردم يثرب، اينجا جاى ماندنتان نيست. بازگرديد. و گروهى از آنها از پيامبر رخصت طلبيدند. مى‌گفتند: خانه‌هاى ما را حفاظى نيست. خانه‌هايشان بى‌حفاظ نبود، مى‌خواستند بگريزند.'),
(3547, 33, 14, 'و اگر از اطراف، خانه‌هايشان را محاصره كنند و از آنها بخواهند كه مرتد شوند، مرتد خواهند شد و جز اندكى درنگ روانخواهند داشت.'),
(3548, 33, 15, 'اينان پيش از اين با خدا پيمان بسته بودند كه در جنگ به دشمن پشت نكنند. خدا از پيمان خود بازخواست خواهد كرد.'),
(3549, 33, 16, 'بگو: اگر از مرگ يا كشته شدن بگريزيد، هرگز گريختن سودتان ندهد. و آنگاه از زندگى جز اندكى بهره‌مند نخواهيد شد.'),
(3550, 33, 17, 'بگو: اگر خدا برايتان قصد بدى داشته باشد يا بخواهد به شما رحمتى ارزانى دارد، كيست كه شما را از اراده او نگه دارد. ايشان جز خدا براى خود دوست و مددكارى نخواهند يافت.'),
(3551, 33, 18, 'خدا مى‌داند چه كسانى از شما مردم را از جنگ بازمى‌دارند. و نيز مى‌شناسد كسانى را كه به برادران خود مى‌گويند: به نزد ما بياييد. و جز اندكى به جنگ نمى‌آيند.'),
(3552, 33, 19, 'هر خيرى را از شما دريغ مى‌دارند، و چون وحشت فراز آيد بينى كه به تو مى‌نگرند و چشمانشان در حدقه مى‌گردد مثل كسى كه از مرگ بيهوش شده باشد. و چون وحشت از ميان برود، از حرص غنايم با زبان تيز خود برنجانندتان. اينان ايمان نياورده‌اند. و خدا اعمالشان را تباه كرده است و اين كار بر خدا آسان بوده است.'),
(3553, 33, 20, 'مى‌پندارند كه سپاه احزاب نرفته است. و اگر آن لشكرها بازمى‌آمدند، آرزو مى‌كردند كه كاش در ميان اعراب باديه‌نشين مى‌بودند و همواره از اخبار شما مى‌پرسيدند. اگر هم در ميان شما مى‌بودند، جز اندكى قتال نمى‌كردند.'),
(3554, 33, 21, 'براى شما اگر به خدا و روز قيامت اميد مى‌داريد و خدا را فراوان ياد مى‌كنيد، شخص رسول اللّه مقتداى پسنديده‌اى است.'),
(3555, 33, 22, 'و چون مؤمنان آن گروه‌ها را ديدند، گفتند: اين همان چيزى است كه خدا و پيامبرش به ما وعده داده‌اند و خدا و پيامبرش راست گفته‌اند. و جز به ايمان و تسليمشان نيفزود.'),
(3556, 33, 23, 'از مؤمنان مردانى هستند كه به پيمانى كه با خدا بسته بودند وفا كردند. بعضى بر سر پيمان خويش جان باختند و بعضى چشم به راهند و هيچ پيمان خود دگرگون نكرده‌اند.'),
(3557, 33, 24, 'تا خدا راستگويان را به سبب راستى گفتارشان پاداش دهد و منافقان را اگر خواهد عذاب كند يا توبه آنها را بپذيرد، كه خدا آمرزنده و مهربان است.'),
(3558, 33, 25, 'خدا كافران كينه‌توز را بازپس زد. اينان به هيچ غنيمتى دست نيافتند. و در كارزار مؤمنان را خدا بسنده است. زيرا خدا پرتوان و پيروزمند است.'),
(3559, 33, 26, 'از اهل كتاب آن گروه را كه به ياريشان برخاسته بودند از قلعه‌هايشان فرود آورد و در دلهايشان بيم افكند. گروهى را كشتيد و گروهى را به اسارت گرفتيد.'),
(3560, 33, 27, 'خدا زمين و خانه‌ها و اموالشان و زمينهايى را كه بر آنها پاى ننهاده‌ايد به شما واگذاشت. و خدا بر هر كارى تواناست.'),
(3561, 33, 28, 'اى پيامبر، به زنانت بگو: اگر خواهان زندگى دنيا و زينتهاى آن هستيد، بياييد تا شما را بهره‌مند سازم و به وجهى نيكو رهايتان كنم؛'),
(3562, 33, 29, 'و اگر خواهان خدا و پيامبر او و سراى آخرت هستيد، خدا به نيكوكارانتان پاداشى بزرگ خواهد داد.'),
(3563, 33, 30, 'اى زنان پيامبر، هر كس از شما مرتكب كار زشت در خور عقوبت شود، خدا عذاب او را دو برابر مى‌كند. و اين بر خدا آسان است.'),
(3564, 33, 31, 'و هر كس از شما كه به فرمانبردارى خدا و پيامبرش مداومت ورزد و كارى شايسته كند، دو بار به او پاداش دهيم. و براى او رزقى كرامند آماده كرده‌ايم.'),
(3565, 33, 32, 'اى زنان پيامبر، شما همانند ديگر زنان نيستيد، اگر از خدا بترسيد. پس به نرمى سخن مگوييد تا آن مردى كه در قلب او مرضى هست به طمع افتد. و سخن پسنديده بگوييد.'),
(3566, 33, 33, 'و در خانه‌هاى خود بمانيد. و چنان كه در زمان پيشين جاهليت مى‌كردند، زينتهاى خود را آشكار مكنيد. و نماز بگزاريد و زكات بدهيد و از خدا و پيامبرش اطاعت كنيد. اى اهل بيت، خدا مى‌خواهد پليدى را از شما دور كند و شما را پاك دارد.'),
(3567, 33, 34, 'آنچه را در خانه‌هايتان از آيات خدا و حكمت تلاوت مى‌شود، ياد كنيد، كه خدا باريك‌بين و آگاه است.'),
(3568, 33, 35, 'خدا براى مردان مسلمان و زنان مسلمان و مردان مؤمن و زنان مؤمن و مردان اهل طاعت و زنان اهل طاعت و مردان راستگوى و زنان راستگوى و مردان شكيبا و زنان شكيبا و مردان خداى ترس و زنان خداى‌ترس و مردان صدقه‌دهنده و زنان صدقه‌دهنده و مردان روزه‌دار و زنان روزه‌دار و مردانى كه شرمگاه خود حفظ مى‌كنند و زنانى كه شرمگاه خود حفظ مى‌كنند و مردانى كه خدا را فراوان ياد مى‌كنند و زنانى كه خدا را فراوان ياد مى‌كنند، آمرزش و مزدى بزرگ آماده كرده است.'),
(3569, 33, 36, 'هيچ مرد مؤمن و زن مؤمنى را نرسد كه چون خدا و پيامبرش در كارى حكمى كردند آنها را در آن كارشان اختيارى باشد. هر كه از خدا و پيامبرش نافرمانى كند سخت در گمراهى افتاده است.'),
(3570, 33, 37, 'و تو، به آن مرد كه خدا نعمتش داده بود و تو نيز نعمتش داده بودى، گفتى: زنت را براى خود نگه‌دار و از خداى بترس. در حالى كه در دل خود آنچه را خدا آشكار ساخت مخفى داشته بودى و از مردم مى‌ترسيدى، حال آنكه خدا از هر كس ديگر سزاوارتر بود كه از او بترسى. پس چون زيد از او حاجت خويش بگزارد، به همسرى تواش درآورديم تا مؤمنان را در زناشويى با زنان فرزند خواندگان خود، اگر حاجت خويش از او بگزارده باشند، منعى نباشد. و حكم خداوند شدنى است.'),
(3571, 33, 38, 'بر پيامبر در انجام دادن آنچه خدا بر او مقرر كرده است حرجى نيست، همچنان كه خدا براى پيامبران پيشين نيز چنين سنتى نهاده بود و فرمان خدا فرمانى است بى‌هيچ زياده و نقصان.'),
(3572, 33, 39, 'كسانى كه پيامهاى خدا را مى‌رسانند و از او مى‌ترسند و از هيچ كس جز او نمى‌ترسند، خدا براى حساب‌كردن اعمالشان كافى است.'),
(3573, 33, 40, 'محمّد پدر هيچ يك از مردان شما نيست. او رسول خدا و خاتم پيامبران است. و خدا به هر چيزى داناست.'),
(3574, 33, 41, 'اى كسانى كه ايمان آورده‌ايد، خدا را فراوان ياد كنيد.'),
(3575, 33, 42, 'و هر بامداد و شبانگاه تسبيحش گوييد.'),
(3576, 33, 43, 'اوست كه خود و فرشتگانش بر شما درود مى‌فرستند تا شما را از تاريكى به روشنى برد، زيرا خداوند با مؤمنان مهربان است.'),
(3577, 33, 44, 'روزى كه با او ديدار كنند درودشان اين است: «سلام». و خدا برايشان پاداشى كرامند آماده كرده است.'),
(3578, 33, 45, 'اى پيامبر، ما تو را فرستاديم تا شاهد و مژده‌دهنده و بيم‌دهنده باشى.'),
(3579, 33, 46, 'و مردم را به فرمان خدا به سوى او بخوانى، و چراغى تابناك باشى.'),
(3580, 33, 47, 'و مؤمنان را بشارت ده كه از سوى خدا برايشان فضيلتى بزرگ آمده است.'),
(3581, 33, 48, 'از كافران و منافقان اطاعت مكن و آزارشان را واگذار و بر خدا توكل كن، كه خدا كارسازى را بسنده است.'),
(3582, 33, 49, 'اى كسانى كه ايمان آورده‌ايد، چون زنان مؤمن را نكاح كرديد و پيش از آنكه با آنها نزديكى كنيد طلاقشان گفتيد، شما را بر آنها عده‌اى نيست كه به سرآرند. پس آنان را برخوردار كنيد و به نيكوترين وجه رهايشان كنيد.'),
(3583, 33, 50, 'اى پيامبر، ما زنانى را كه مهرشان را داده‌اى و آنان را كه به عنوان غنايم جنگى كه خدا به تو ارزانى داشته است مالك شده‌اى و دختر عموها و دختر عمه‌ها و دختر داييها و دختر خاله‌هاى تو را كه با تو مهاجرت كرده‌اند بر تو حلال كرديم؛ و نيز زن مؤمنى را كه خود را به پيامبر بخشيده باشد، هرگاه پيامبر بخواهد او را به زنى گيرد. اين حكم ويژه توست نه ديگر مؤمنان. ما مى‌دانيم در باره زنانشان و كنيزانشان چه حكمى كرده‌ايم، تا براى تو مشكلى پيش نيايد. و خدا آمرزنده و مهربان است.'),
(3584, 33, 51, 'از زنان خود هر كه را خواهى به نوبت مؤخردار و هر كه را خواهى با خود نگه دار. و اگر از آنها كه دور داشته‌اى يكى را بطلبى بر تو گناهى نيست. در اين گزينش و اختيار بايد كه شادمان باشند و غمگين نشوند و از آنچه همگيشان را ارزانى مى‌دارى بايد كه خشنود گردند و خدا مى‌داند كه در دلهاى شما چيست. و خداست كه دانا و بردبار است.'),
(3585, 33, 52, 'بعد از اين زنان، هيچ زنى بر تو حلال نيست و نيز زنى به جاى ايشان اختيار كردن، هر چند تو را از زيبايى او خوش آيد؛ مگر آنچه به غنيمت به دست تو افتد. و خدا مراقب هر چيزى است.'),
(3586, 33, 53, 'اى كسانى كه ايمان آورده‌ايد، به خانه‌هاى پيامبر داخل مشويد مگر شما را به خوردن طعامى فراخوانند، بى‌آنكه منتظر بنشينيد تا طعام حاضر شود. اگر شما را فراخواندند داخل شويد و چون طعام خورديد پراكنده گرديد. نه آنكه براى سرگرمى سخن آغاز كنيد. هر آينه اين كارها پيامبر را آزار مى‌دهد و او از شما شرم مى‌دارد. ولى خدا از گفتن حق شرم نمى‌دارد. و اگر از زنان پيامبر چيزى خواستيد، از پشت پرده بخواهيد. اين كار، هم براى دلهاى شما و هم براى دلهاى آنها پاك دارنده‌تر است. شما را نرسد كه پيامبر خدا را بيازاريد، و نه آنكه زنهايش را بعد از وى هرگز به زنى گيريد. اين كارها در نزد خدا گناهى بزرگ است.'),
(3587, 33, 54, 'اگر چيزى را آشكار كنيد يا مخفى داريد، در هر حال خدا به هر چيزى آگاه است.'),
(3588, 33, 55, 'زنان را گناهى نيست اگر در نزد پدر و پسر و برادر و برادرزاده و خواهرزاده و زنان همدين و يا كنيزان خود بى‌حجاب باشند. و بايد از خدا بترسيد كه خدا بر هر چيزى ناظر است.'),
(3589, 33, 56, 'خدا و فرشتگانش بر پيامبر صلوات مى‌فرستند. اى كسانى كه ايمان آورده‌ايد، بر او صلوات فرستيد و سلام كنيد؛ سلامى نيكو.'),
(3590, 33, 57, 'هر آينه كسانى را كه خدا و پيامبرش را آزار مى‌دهند، خدا در دنيا و آخرت لعنت كرده و برايشان عذابى خواركننده مهيا كرده است.'),
(3591, 33, 58, 'و كسانى كه مردان مؤمن و زنان مؤمن را بى‌هيچ گناهى كه كرده باشند مى‌آزارند، تهمت و گناه آشكارى را بر دوش مى‌كشند.'),
(3592, 33, 59, 'اى پيامبر، به زنان و دختران خود و زنان مؤمنان بگو كه چادر خود را بر خود فرو پوشند. اين مناسب‌تر است، تا شناخته شوند و مورد آزار واقع نگردند. و خدا آمرزنده و مهربان است.'),
(3593, 33, 60, 'اگر منافقان و كسانى كه در دلهايشان مرضى است و آنها كه در مدينه شايعه مى‌پراكنند از كار خود باز نايستند، تو را بر آنها مسلط مى‌گردانيم تا از آن پس جز اندكى با تو در شهر همسايه نباشند.'),
(3594, 33, 61, 'اينان لعنت‌شدگانند. هر جا يافته شوند بايد دستگير گردند و به سختى كشته شوند.'),
(3595, 33, 62, 'اين سنت خداوندى است كه در ميان پيشينيان نيز بود و در سنت خدا تغييرى نخواهى يافت.'),
(3596, 33, 63, 'مردم تو را از قيامت مى‌پرسند، بگو: علم آن نزد خداست. و تو چه مى‌دانى، شايد قيامت نزديك باشد.'),
(3597, 33, 64, 'خدا كافران را لعنت كرده و برايشان آتشى سوزان مهيا كرده است،'),
(3598, 33, 65, 'كه در آن جاودانه‌اند و هيچ دوست و ياورى نخواهند يافت.'),
(3599, 33, 66, 'روزى كه صورتهايشان را در آتش بگردانند، مى‌گويند: اى كاش خدا را اطاعت كرده بوديم و رسول را اطاعت كرده بوديم.'),
(3600, 33, 67, 'و گفتند: اى پروردگار ما، از سروران و بزرگان خود اطاعت كرديم و آنان ما را گمراه كردند.'),
(3601, 33, 68, 'اى پروردگار ما، عذابشان را دو چندان كن و به لعنت بزرگى گرفتارشان ساز.'),
(3602, 33, 69, 'اى كسانى كه ايمان آورده‌ايد، مباشيد مانند آن كسان كه موسى را آزرده كردند. و خدايش از آنچه گفته بودند مبرايش ساخت و نزد خدا آبرومند بود.'),
(3603, 33, 70, 'اى كسانى كه ايمان آورده‌ايد، از خدا بترسيد و سخن درست بگوييد.'),
(3604, 33, 71, 'خدا كارهاى شما را به صلاح آورد و گناهانتان را بيامرزد. و هر كه از خدا و پيامبرش اطاعت كند به كاميابى بزرگى دست يافته است.'),
(3605, 33, 72, 'ما اين امانت را بر آسمانها و زمين و كوه‌ها عرضه داشتيم، از تحمل آن سرباز زدند و از آن ترسيدند. انسان آن امانت بر دوش گرفت، كه او ستمكار و نادان بود.'),
(3606, 33, 73, 'تا خدا مردان منافق و زنان منافق و مردان مشرك و زنان مشرك را عذاب كند و توبه مردان مؤمن و زنان مؤمن را بپذيرد، كه خدا آمرزنده و مهربان است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ستايش از آن خدايى است كه هر چه در آسمانها و هر چه در زمين است از آن اوست. و در آن جهان نيز ستايش از آن اوست و او حكيم و آگاه است.'),
(3608, 34, 2, 'هر چه را كه در زمين فرو شود و هر چه را كه از زمين بيرون آيد و هر چه را كه از آسمان فرود آيد و هر چه را كه بر آسمان بالا رود، مى‌داند. و او مهربان و آمرزنده است.'),
(3609, 34, 3, 'كافران گفتند: ما را قيامت نخواهد آمد. بگو: آرى، به پروردگارم آن داناى غيب سوگند كه شما را خواهد آمد. به قدر ذره‌اى يا كوچكتر از آن و يا بزرگ‌تر از آن در آسمانها و زمين از خدا پنهان نيست، و همه در كتاب مبين آمده است.'),
(3610, 34, 4, 'تا كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند پاداش دهد. براى آنهاست آمرزش و رزقى كرامند.'),
(3611, 34, 5, 'و آنان كه با آيات ما درفتادند تا ما را به عجز آرند، برايشان عذابى است سخت دردآور.'),
(3612, 34, 6, 'آنان كه از دانش برخوردارى يافته‌اند مى‌دانند كه آنچه از جانب پروردگارت بر تو نازل شده است حق است و به راه خداى پيروزمند ستودنى راه مى‌نمايد.'),
(3613, 34, 7, 'كافران گفتند: آيا مى‌خواهيد به مردى دلالتتان كنيم كه شما را مى‌گويد: آنگاه كه پاره پاره شويد و ريز ريز، از نو آفريده خواهيد شد؟'),
(3614, 34, 8, 'آيا بر خدا دروغ مى‌بندد، يا ديوانه است؟ نه، آنان كه به آخرت ايمان ندارند، در عذابند و سخت از راه دورند.'),
(3615, 34, 9, 'آيا به پيش روى يا پشت سر خود از آسمانها و زمين نمى‌نگرند؟ اگر بخواهيم، آنها را در زمين فرو مى‌بريم يا قطعه‌اى از آسمان را بر سرشان مى‌افكنيم. و در اين براى هر بنده‌اى كه به خدا باز مى‌گردد عبرتى است.'),
(3616, 34, 10, 'داود را از سوى خود فضيلتى داديم كه: اى كوه‌ها و اى پرندگان، با او هماواز شويد. و آهن را برايش نرم كرديم،'),
(3617, 34, 11, 'كه زرههاى بلند بساز و در بافتن زره اندازه‌ها را نگه دار. و كارهاى شايسته كنيد، كه من به كارهايتان بصيرم.'),
(3618, 34, 12, 'و باد را مسخّر سليمان كرديم. بامدادان يك ماهه راه مى‌رفت و شبانگاه يك ماهه راه. و چشمه مس را برايش جارى ساختيم و گروهى از ديوها به فرمان پروردگارش برايش كار مى‌كردند و هر كه از آنان سر از فرمان ما مى‌پيچيد به او عذاب آتش سوزان را مى‌چشانيديم.'),
(3619, 34, 13, 'براى وى هر چه مى‌خواست از بناهاى بلند و تنديسها و كاسه‌هايى چون حوض و ديگهاى محكم بر جاى، مى‌ساختند. اى خاندان داود، براى سپاسگزارى كارى كنيد و اندكى از بندگان من سپاسگزارند.'),
(3620, 34, 14, 'چون حكم مرگ را بر او رانديم حشره‌اى از حشرات زمين مردم را بر مرگش آگاه كرد: عصايش را جويد. چون فرو افتاد، ديوها دريافتند كه اگر علم غيب مى‌دانستند، در آن عذاب خواركننده نمى‌ماندند.'),
(3621, 34, 15, 'مردم سبا را در مساكنشان عبرتى بود: دو بوستان داشتند، يكى از جانب راست و يكى از جانب چپ. از آنچه پروردگارتان به شما روزى داده است بخوريد و شكر او به جاى آوريد. شهرى خوش و پاكيزه و پروردگارى آمرزنده.'),
(3622, 34, 16, 'اعراض كردند. ما نيز سيل ويرانگر را بر آنها فرستاديم و دو بوستانشان را به دو بوستان بدل كرديم با ميوه‌اى تلخ و شوره‌گز و اندكى سدر.'),
(3623, 34, 17, 'آنها را كه ناسپاس بودند اينچنين جزا داديم. آيا ما جز ناسپاسان را مجازات مى‌كنيم؟'),
(3624, 34, 18, 'ميان آنان و قريه‌هايى كه بركت داده بوديم، قريه‌هايى آبادان و بر سر راه پديد آورديم. و منزلهاى برابر معين كرديم. در آن راه‌ها ايمن از گزند، شبها و روزها سفر كنيد.'),
(3625, 34, 19, 'بر خويشتن ستم كردند و گفتند: اى پروردگار ما، منزلگاه‌هاى ما را از هم دور گردان. ما نيز افسانه روزگارشان گردانيديم و سخت پراكنده‌شان ساختيم و در اين عبرتهاست براى شكيبايان سپاسگزار.'),
(3626, 34, 20, 'شيطان گمان خود را در باره آنها درست يافت. و جز گروهى از مؤمنان، ديگران از او پيروى كردند.'),
(3627, 34, 21, 'و شيطان را بر آنان تسلطى نبود، مگر آنكه مى‌خواستيم معلوم داريم كه چه كسانى به قيامت ايمان دارند و چه كسانى از آن در شك هستند. و پروردگار تو نگهبان هر چيزى است.'),
(3628, 34, 22, 'بگو: بخوانيد كسانى را كه جز خداى يكتا خدا مى‌پنداريد. مالك ذره‌اى در آسمانها و زمين نيستند و در آفرينش آن دو شركتى نداشته‌اند و خدا را از ميان آنها ياريگرى نبوده است.'),
(3629, 34, 23, 'شفاعت نزد خدا سود نكند، مگر در باره كسى كه او خود اجازت دهد. و چون بيم از دلهايشان برود، گويند: پروردگارتان چه گفت؟ گويند: سخن حق گفت. و او بلندمرتبه و بزرگ است.'),
(3630, 34, 24, 'بگو: از آسمانها و زمين چه كسى به شما روزى مى‌دهد؟ بگو: خداى يكتا. اينك ما و شما يا در طريق هدايت هستيم يا در گمراهى آشكار.'),
(3631, 34, 25, 'بگو: اگر ما مرتكب جرمى شويم، شما را بازخواست نمى‌كنند و اگر شما مرتكب كارى زشت گرديد ما را بازخواست نخواهند كرد.'),
(3632, 34, 26, 'بگو: پروردگار ما، ما و شما را گرد مى‌آورد، سپس ميان ما به حق داورى مى‌كند. زيرا اوست حكم كننده و دانا.'),
(3633, 34, 27, 'بگو: آنهايى را كه شريك خدا پنداشتيد به من نشان بدهيد. هرگز، كه اوست خداى پيروزمند و حكيم.'),
(3634, 34, 28, 'تو را به پيامبرى نفرستاديم، مگر بر همه مردم؛ مژده‌دهنده و بيم‌دهنده. ولى بيشتر مردم نمى‌دانند.'),
(3635, 34, 29, 'و مى‌گويند: اگر راست مى‌گوييد، اين وعده چه وقت فرا رسد؟'),
(3636, 34, 30, 'بگو: آن روز كه ميعاد شماست، نه ساعتى تأخير كنيد و نه ساعتى پيش افتيد.'),
(3637, 34, 31, 'و كافران گفتند: ما نه به اين قرآن ايمان مى‌آوريم و نه به كتابهاى پيش از آن. اگر ببينى، آن روز كه ستمكاران را به پيشگاه پروردگارشان نگه دارند، هر كس گناه خود به گردن ديگرى اندازد. زبون‌شدگان به قدرتمندان گويند: اگر شما نبوديد، ما ايمان آورده بوديم.'),
(3638, 34, 32, 'قدرتمندان به زبون‌شدگان گويند: آيا از آن پس كه شما را به راه هدايت فراخواندند، ما شما را بازداشتيم؟ نه، شما خود گناهكار بوديد.'),
(3639, 34, 33, 'زبون‌شدگان به قدرتمندان گويند: نه، شما شب و روز حيلت مى‌كرديد، آنگاه كه ما را فرمان مى‌داديد كه به خداى يكتا كافر شويم و براى او همتايانى قرار دهيم. و چون عذاب را ببينند در دل پشيمانى كنند. و ما غلها را بر گردن كافران بگذاريم. آيا نه چنين است كه در برابر اعمالشان مجازات مى‌شوند؟'),
(3640, 34, 34, 'ما هيچ بيم‌دهنده‌اى به قريه‌اى نفرستاديم، جز آنكه توانگران عيّاشش گفتند: ما به آنچه شما را بدان فرستاده‌اند ايمان نمى‌آوريم.'),
(3641, 34, 35, 'و گفتند: اموال و اولاد ما از همه بيشتر است و كس ما را عذاب نكند.'),
(3642, 34, 36, 'بگو: پروردگار من است كه روزى هر كس را كه بخواهد فراوان مى‌كند و بر هر كه بخواهد تنگ مى‌گيرد. ولى بيشتر مردم نمى‌دانند.'),
(3643, 34, 37, 'اموال و اولادتان چيزى نيست كه شما را به ما نزديك سازد. مگر آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، كه پاداش اينان به سبب اعمالشان دو برابر است و ايمن در غرفه‌هاى بهشت هستند.'),
(3644, 34, 38, 'آنان كه به آيات ما مى‌تازند و مى‌پندارند كه از ما مى‌گريزند، طعمه عذابند.'),
(3645, 34, 39, 'بگو: پروردگار من است كه روزى هر كه از بندگانش را كه بخواهد فراوان مى‌كند يا او را به تنگى مى‌افكند. و اگر چيزى انفاق كنيد، عوضش را خواهد داد و او بهترين روزى‌دهندگان است.'),
(3646, 34, 40, 'روزى كه همه را گردآورد، آنگاه ملائكه را گويد: آيا اينان بودند كه شما را مى‌پرستيدند؟'),
(3647, 34, 41, 'مى‌گويند: تو منزهى. تويى ولىّ ما، نه آنها. اينان جنها را مى‌پرستيدند و بيشترين به آنها ايمان داشتند.'),
(3648, 34, 42, 'آن روز به يكديگر هيچ سود و زيانى نتوانيد رساند. و به ستمكاران گوييم: بچشيد عذاب آتشى را كه دروغش مى‌انگاشتيد.'),
(3649, 34, 43, 'چون آيات روشن ما بر آنان خوانده شد، گفتند: جز اين نيست كه اين مردى است كه مى‌خواهد شما را از آنچه پدرانتان مى‌پرستيدند بازدارد. و گفتند: اين جز دروغى به هم بافته، چيز ديگرى نيست. و كسانى كه به خدا كافر شده بودند، چون سخن حق بر آنها نازل شد گفتند: اين چيزى جز جادويى آشكار نيست.'),
(3650, 34, 44, 'پيش از اين، كتابى كه آن را بخوانند به آنها نداده‌ايم و پيش از تو بيم‌دهنده‌اى بر آنان نفرستاده‌ايم.'),
(3651, 34, 45, 'و كسانى كه پيش از آنها بودند پيامبران را تكذيب كردند و اينان خود به ده يك آنچه به آنها داده بوديم نرسيده‌اند، و با اين حال پيامبران مرا تكذيب كردند. پس عقوبت من چه سخت بود.'),
(3652, 34, 46, 'بگو: شما را به يك چيز اندرز مى‌دهم: دودو و يك‌يك براى خدا قيام كنيد. سپس بينديشيد، تا بدانيد كه در يار شما ديوانگيى نيست. اوست كه شما را از آمدن عذابى شديد مى‌ترساند.'),
(3653, 34, 47, 'بگو: هر مزدى كه از شما طلبيده‌ام، از آن خودتان باد. مزد من تنها بر عهده خداست. اوست كه بر هر كارى ناظر است.'),
(3654, 34, 48, 'بگو: پروردگار من الهامبخش سخن حق است. داناى غيبهاست.'),
(3655, 34, 49, 'بگو: حق فراز آمد و باطل باز نيايد و ياراى بازگشتنش نيست.'),
(3656, 34, 50, 'بگو: اگر من گمراه شوم زيانش بر من است، و اگر به راه هدايت روم بدان سبب است كه پروردگار من به من وحى مى‌كند. او شنوا و نزديك است.'),
(3657, 34, 51, 'اگر ببينى، آنگاه كه سخت بترسند و رهاييشان نباشد و از مكانى نزديك گرفتارشان سازند،'),
(3658, 34, 52, 'گويند: اينك به رسول ايمان آورديم. اما از آن جاى دور چسان به آن دست يابند؟'),
(3659, 34, 53, 'پيش از اين به او كافر شده بودند و به گمان خويش به او تهمت مى‌زدند.'),
(3660, 34, 54, 'ميان آنها و آن آرزو كه دارند جدايى افتاد. همچنان كه با ديگران كه چنين مى‌انديشيدند و سخت در ترديد بودند، نيز چنين شد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'ستايش از آن خداوند است، آفريننده آسمانها و زمين، آن كه فرشتگان را رسولان گردانيد. فرشتگانى كه بالهايى دارند، دودو و سه‌سه و چهارچهار. در آفرينش هر چه بخواهد مى‌افزايد، زيرا خدا بر هر كارى تواناست.'),
(3662, 35, 2, 'رحمتى كه خدا بر مردم بگشايد كس نتواند كه بازش دارد. و چون چيزى را دريغ دارد كس نتواند جز او كه روانش دارد. و اوست پيروزمند و حكيم.'),
(3663, 35, 3, 'اى مردم، نعمتى را كه خدا بر شما ارزانى داشته است ياد كنيد. آيا جز خدا آفريننده ديگرى هست كه شما را از آسمان و زمين روزى دهد؟ خدايى جز او نيست، پس چگونه از حق منحرفتان مى‌كنند؟'),
(3664, 35, 4, 'اگر تو را تكذيب كرده‌اند، پيامبرانى را هم كه پيش از تو بوده‌اند تكذيب كرده‌اند و همه كارها به خدا بازگردانده شود.'),
(3665, 35, 5, 'اى مردم، وعده خدا حق است. زندگى دنيا شما را نفريبد، و آن شيطان فريبنده به خدا مغرورتان نگرداند.'),
(3666, 35, 6, 'شيطان دشمن شماست. او را دشمن گيريد. او فرمانبران خويش را فرا مى‌خواند تا همه از دوزخيان باشند.'),
(3667, 35, 7, 'و كافران را عذابى است سخت؛ و براى كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند آمرزش و اجر بزرگى است.'),
(3668, 35, 8, 'آيا آن كه كردار بدش در نظرش آراسته شده، چنانكه نيكويش پنداشت، همانند كسى است كه چنين نيست؟ پس خدا هر كه را خواهد گمراه مى‌كند و هر كه را خواهد هدايت مى‌كند. نبايد كه جان تو به خاطر آنها دچار اندوه شود. زيرا خدا به كارهايى كه مى‌كنند آگاه است.'),
(3669, 35, 9, 'خداست كه بادها را فرستاد تا ابرها را برانگيزند. و ما آنها را به سرزمينهاى مرده مى‌رانيم و زمين مرده را به آن زنده مى‌كنيم. زنده‌گشتن در روز قيامت نيز چنين است.'),
(3670, 35, 10, 'هر كه خواهان عزت است بداند كه عزت، همگى از آن خداست. سخن خوش و پاك به سوى او بالا مى‌رود و كردار نيك است كه آن را بالا مى‌برد. و براى آنان كه از روى مكر به تبهكارى مى‌پردازند عذابى است سخت و مكرشان نيز از ميان برود.'),
(3671, 35, 11, 'خدا شما را از خاك و سپس از نطفه بيافريد. آنگاه جفتهاى يكديگرتان قرار داد. هيچ زنى آبستن نمى‌شود و نمى‌زايد مگر به علم او. و عمر هيچ سالخورده‌اى به درازا نكشد و از عمر كسى كاسته نگردد، جز آنكه همه در كتابى نوشته شده است. و اين كارها بر خدا آسان است.'),
(3672, 35, 12, 'اين دو دريا يكسان نيستند: يكى آبش شيرين و گواراست و يكى شور و تلخ. از هر دو گوشت تازه مى‌خوريد، و از آنها چيزهايى براى آرايش تن خويش بيرون مى‌كشيد. و مى‌بينى كشتيها را كه آب را مى‌شكافند تا شما از فضل خدا روزى طلبيد و باشد كه سپاسگزار باشيد.'),
(3673, 35, 13, 'از شب مى‌كاهد و به روز مى‌افزايد و از روز مى‌كاهد و به شب مى‌افزايد و آفتاب و ماه را رام كرد. هر يك تا زمانى معين در حركتند. اين است خدا پروردگار شما. فرمانروايى از آن اوست. چيزهايى را كه سواى او به خدايى مى‌خوانيد مالك پوست ميان هسته خرمايى هم نيستند.'),
(3674, 35, 14, 'و اگر آنها را بخوانيد، صدايتان را نمى‌شنوند و اگر بشنوند، پاسختان نگويند. و در روز قيامت شرك‌آوردنتان را انكار كنند. و كس همانند خداى آگاه خبردارت نسازد.'),
(3675, 35, 15, 'اى مردم، همه شما به خدا نيازمنديد. اوست بى‌نياز و ستودنى.'),
(3676, 35, 16, 'اگر بخواهد، شما را از ميان مى‌برد و مردمى تازه مى‌آورد.'),
(3677, 35, 17, 'و اين كار بر خدا دشوار نيست.'),
(3678, 35, 18, 'هيچ كس بار گناه ديگرى را بر دوش نكشد. و اگر گرانبارى كسى را به حمل‌كردن بار خود فرا خواند، هر چند خويشاوند او باشد، از حمل آن سرباز زند. تو فقط كسانى را مى‌ترسانى كه از پروردگارشان، ناديده، بيمناكند و نماز مى‌گزارند. و هر كه پاك شود براى خود پاك شده. و سرانجام همه به سوى خداست.'),
(3679, 35, 19, 'نابينا و بينا برابر نيستند،'),
(3680, 35, 20, 'و نه تاريكى و روشنى،'),
(3681, 35, 21, 'و نه سايه و حرارت آفتاب.'),
(3682, 35, 22, 'و زندگان و مردگان برابر نيستند. خدا هر كه را خواهد مى‌شنواند. و تو نمى‌توانى سخن خود را به مردگانى كه در گور خفته‌اند بشنوانى.'),
(3683, 35, 23, 'تو جز بيم‌دهنده‌اى نيستى.'),
(3684, 35, 24, 'ما تو را به حق به رسالت فرستاديم تا مژده دهى و بيم دهى و هيچ ملتى نيست مگر آنكه به ميانشان بيم‌دهنده‌اى بوده است.'),
(3685, 35, 25, 'اگر تو را تكذيب مى‌كنند، كسانى هم كه پيش از اينان بوده‌اند پيامبرانشان را كه با معجزات و با نوشته‌ها و كتابى روشنايى‌بخش به ميانشان آمده بودند، تكذيب كرده‌اند.'),
(3686, 35, 26, 'سپس آنهايى را كه كفر مى‌ورزيدند فروگرفتم. عقوبت من چگونه بود؟'),
(3687, 35, 27, 'آيا نديده‌اى كه خدا از آسمان باران فرستاد و بدان ميوه‌هاى گوناگون رويانيديم؟ و از كوه‌ها راه‌ها پديد آورديم: سفيد و سرخ و رنگارنگ و به غايت سياه.'),
(3688, 35, 28, 'همچنين از مردم و جنبندگان و چهارپايان گوناگون. هر آينه از ميان بندگان خدا تنها دانشمندان از او مى‌ترسند. و خدا پيروزمند و آمرزنده است.'),
(3689, 35, 29, 'آنان كه كتاب خدا را مى‌خوانند و نماز مى‌گزارند و از آنچه به آنها داده‌ايم پنهانى و آشكارا انفاق مى‌كنند، اميدوار به تجارتى هستند كه هرگز زيان نمى‌كند.'),
(3690, 35, 30, 'زيرا خدا پاداششان را به تمامى مى‌دهد و از فضل خود چيزى بر آن مى‌افزايد. زيرا آمرزنده و پذيراى سپاس است.'),
(3691, 35, 31, 'هر چه از اين كتاب به تو وحى كرده‌ايم حق است، كه كتابهاى پيش از خود را تصديق مى‌كند و خدا بر بندگانش آگاه و بيناست.'),
(3692, 35, 32, 'سپس كتاب را به كسانى از بندگانمان كه برگزيده بوديم به ميراث داديم. بعضى بر خود ستم كردند و بعضى راه ميانه را برگزيدند و بعضى به فرمان خدا در كارهاى نيك پيشى گرفتند. و اين است بخشايش بزرگ.'),
(3693, 35, 33, 'به بهشتهايى كه جايگاه جاودانه آنهاست داخل مى‌شوند. در آنجا به دستبندهاى زر و مرواريدشان مى‌آرايند و در آنجا جامه‌هاشان از حرير است.'),
(3694, 35, 34, 'و مى‌گويند: سپاس خدايى را كه اندوه از ما دور كرد، زيرا پروردگار ما آمرزنده و شكرپذير است.'),
(3695, 35, 35, 'آن خدايى كه ما را از فضل خويش بدين سراى جاويدان درآورد، كه در آنجا نه رنجى به ما مى‌رسد و نه خستگى.'),
(3696, 35, 36, 'و كافران را آتش جهنم است، نه بميرانندشان و نه از عذابشان كاسته گردد. ناسپاسان را چنين كيفر مى‌دهيم.'),
(3697, 35, 37, 'و از درون آتش فرياد زنند: اى پروردگار ما، ما را بيرون آر تا كارهايى شايسته كنيم، غير از آنچه مى‌كرديم. آيا آن قدر شما را عمر نداده بوديم كه پندگيرندگان پند گيرند؟ و شما را بيم‌دهنده آمد. پس بچشيد، كه گناهكاران را ياورى نيست.'),
(3698, 35, 38, 'خدا، داناى نهان آسمانها و زمين است و او به آنچه در دلهاست آگاه است.'),
(3699, 35, 39, 'اوست كه شما را در روى زمين جانشين پيشينيان كرد. پس هر كس كه ناسپاسى كند، ناسپاسى‌اش بر زيان اوست، و كفر كافران جز بر دشمنى پروردگارشان با آنها، نيفزايد. نيز كفر كافران جز به خسرانشان نيفزايد.'),
(3700, 35, 40, 'بگو: آن شريكانى را كه به جاى خداى يكتا مى‌خوانديد، ديديد؟ به من بنماييد كه از اين زمين چه چيز را آفريده‌اند؟ يا در آفرينش آسمان چه مشاركتى داشته‌اند؟ آيا بر آنها كتابى فرستاده‌ايم كه آن را حجت خود سازند؟ نه، ستمكاران به يكديگر جز فريب وعده‌اى نمى‌دهند.'),
(3701, 35, 41, 'خدا آسمانها و زمين را نگه مى‌دارد تا زوال نيابند؛ و اگر به زوال گرايند، هيچ يك از شما -جز او- نمى‌تواند آنها را نگه دارد. هر آينه خدا بردبار و آمرزنده است.'),
(3702, 35, 42, 'به خدا قسمهاى سخت خوردند كه اگر بيم‌دهنده‌اى بيايد بهتر از هر امت ديگر هدايت يابند. ولى چون بيم‌دهنده‌اى آمد، جز نفرتشان نيفزود؛'),
(3703, 35, 43, 'به سركشى در زمين و نيرنگهاى بد. و اين نيرنگهاى بد جز نيرنگبازان را در بر نگيرد. آيا جز سنتى كه بر گذشتگان رفته است منتظر چيز ديگرى هستند؟ در سنت خدا هيچ تبديلى نمى‌يابى و در سنت خدا هيچ تغييرى نمى‌يابى.'),
(3704, 35, 44, 'آيا در زمين نمى‌گردند تا ببينند كه عاقبت مردمى كه پيش از آنها بوده‌اند و نيرويى بيشتر داشته‌اند به كجا كشيد؟ هيچ چيز در آسمانها و زمين نيست كه خدا را ناتوان سازد. زيرا او دانا و تواناست.'),
(3705, 35, 45, 'و اگر خدا بخواهد مردم را به سبب كارهايى كه كرده‌اند بازخواست كند، بر روى زمين هيچ جنبنده‌اى باقى نگذارد. ولى آنها را تا زمانى معين مهلت مى‌دهد و چون مدتشان به سر آمد، به اعمال بندگان خويش آگاه است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'يا، سين.'),
(3707, 36, 2, 'سوگند به قرآن حكمت‌آميز،'),
(3708, 36, 3, 'كه تو از پيامبران هستى،'),
(3709, 36, 4, 'بر راهى راست.'),
(3710, 36, 5, 'قرآن از جانب آن پيروزمند مهربان، نازل شده‌'),
(3711, 36, 6, 'تا مردمى را بيم دهى كه پدرانشان بيم داده نشدند و در بى خبرى بودند.'),
(3712, 36, 7, 'وعده خدا در باره بيشترشان تحقق يافته و ايمان نمى‌آورند.'),
(3713, 36, 8, 'و ما بر گردنهايشان تا زنخها غُلها نهاديم، چنان كه سرهايشان به بالاست و پايين‌آوردن نتوانند.'),
(3714, 36, 9, 'در برابرشان ديوارى كشيديم و در پشت سرشان ديوارى. و بر چشمانشان نيز پرده‌اى افكنديم تا نتوانند ديد.'),
(3715, 36, 10, 'تفاوتشان نكند، چه آنها را بترسانى و چه نترسانى، ايمان نمى‌آورند.'),
(3716, 36, 11, 'تنها، تو كسى را بيم مى‌دهى كه از قرآن پيروى كند و از خداى رحمان در نهان بترسد. چنين كس را به آمرزش و مزد كرامند مژده بده.'),
(3717, 36, 12, 'ما مُردگان را زنده مى‌كنيم. و هر كارى را كه پيش از اين كرده‌اند و هر اثرى را كه پديد آورده‌اند، مى‌نويسيم و هر چيزى را در كتاب مبين شمار كرده‌ايم.'),
(3718, 36, 13, 'داستان مردم آن قريه را برايشان بياور، آنگاه كه رسولان بدان جا آمدند.'),
(3719, 36, 14, 'نخست دو تن را به نزدشان فرستاديم و تكذيبشان كردند؛ پس با سومى نيروشان داديم و گفتند: ما به سوى شما فرستاده شده‌ايم.'),
(3720, 36, 15, 'گفتند: شما انسانهايى همانند ما هستيد و خداى رحمان هيچ چيز نفرستاده است و شما جز دروغ نمى‌گوييد.'),
(3721, 36, 16, 'گفتند: پروردگار ما مى‌داند كه ما به سوى شما فرستاده شده‌ايم.'),
(3722, 36, 17, 'و بر عهده ما جز پيام‌رسانيدن آشكارا هيچ نيست.'),
(3723, 36, 18, 'گفتند: ما شما را به فال بد گرفته‌ايم. اگر بس نكنيد سنگسارتان خواهيم كرد و شما را از ما شكنجه‌اى سخت خواهد رسيد.'),
(3724, 36, 19, 'گفتند: شومى شما، با خود شماست. آيا اگر اندرزتان دهند چنين مى‌گوييد؟ نه، مردمى گزافكار هستيد.'),
(3725, 36, 20, 'مردى از دوردست شهر دوان‌دوان آمد و گفت: اى قوم من، از اين رسولان پيروى كنيد.'),
(3726, 36, 21, 'از كسانى كه از شما هيچ مزدى نمى‌طلبند، و خود مردمى هدايت‌يافته‌اند، پيروى كنيد.'),
(3727, 36, 22, 'چرا خدايى را كه مرا آفريده و به نزد او بازگردانده مى‌شويد، نپرستم؟'),
(3728, 36, 23, 'آيا سواى او خدايانى را اختيار كنم، كه اگر خداى رحمان بخواهد به من زيانى برساند، شفاعتشان مرا هيچ سود نكند و مرا رهايى نبخشند؟'),
(3729, 36, 24, 'و در اين هنگام من در گمراهى آشكار باشم.'),
(3730, 36, 25, 'من به پروردگارتان ايمان آوردم. سخن مرا بشنويد.'),
(3731, 36, 26, 'گفته شد: به بهشت درآى. گفت: اى كاش قوم من مى‌دانستند'),
(3732, 36, 27, 'كه پروردگار من مرا بيامرزيد و در زمره گرامى‌شدگان درآورد.'),
(3733, 36, 28, 'و از آن پس بر سر قوم او هيچ لشكرى از آسمان فرو نفرستاديم. و ما فروفرستنده نبوديم.'),
(3734, 36, 29, 'جز يك بانگ سهمناك نبود كه ناگاه همه بر جاى سرد شدند.'),
(3735, 36, 30, 'اى دريغ بر اين بندگان. هيچ پيامبرى بر آنها مبعوث نشد مگر آنكه مسخره‌اش كردند.'),
(3736, 36, 31, 'آيا نديده‌اند كه چه مردمى را پيش از آنها هلاك كرده‌ايم كه ديگر به نزدشان بازنمى‌گردند.'),
(3737, 36, 32, 'و كس نماند مگر آنكه نزد ما حاضرش آرند.'),
(3738, 36, 33, 'نشانه عبرتى است برايشان زمين مرده كه زنده‌اش ساختيم و از آن دانه‌اى كه از آن مى‌خورند بيرون آورديم.'),
(3739, 36, 34, 'و در آن باغهايى از نخلها و تاكها پديد آورديم و چشمه‌ها روان ساختيم،'),
(3740, 36, 35, 'تا از ثمرات آن و دسترنج خويش بخورند. چرا سپاس نمى‌گويند؟'),
(3741, 36, 36, 'منزه است آن خدايى كه همه جفتها را بيافريد، چه از آنچه زمين مى‌روياند و چه از نفسهايشان و چه آن چيزهايى كه نمى‌شناسند.'),
(3742, 36, 37, 'شب نيز برايشان عبرتى ديگر است كه روز را از آن برمى‌كشيم و همه در تاريكى فرو مى‌روند.'),
(3743, 36, 38, 'و آفتاب به سوى قرارگاه خويش روان است. اين فرمان خداى پيروزمند و داناست.'),
(3744, 36, 39, 'و براى ماه منزلهايى مقدر كرديم تا همانند شاخه خشك خرما باريك شود.'),
(3745, 36, 40, 'آفتاب را نسزد كه به ماه رسد و شب را نسزد كه بر روز پيشى گيرد. و همه در فلكى شناورند.'),
(3746, 36, 41, 'عبرتى ديگر براى آنها آنكه نياكانشان را در آن كشتى انباشته شده سوار كرديم.'),
(3747, 36, 42, 'و برايشان همانند كشتى چيزى آفريديم كه بر آن سوار شوند.'),
(3748, 36, 43, 'و اگر بخواهيم همه را غرقه مى‌سازيم و آنها را هيچ فريادرسى نباشد و رهايى نيابند،'),
(3749, 36, 44, 'جز به رحمت ما و برخوردارى تا هنگام مرگ.'),
(3750, 36, 45, 'و آنگاه كه به ايشان گفته شود كه از آنچه در پيش روى داريد يا پشت سر مى‌گذاريد بترسيد، شايد بر شما رحمت آرند؛ روى بگردانند.'),
(3751, 36, 46, 'و هيچ آيه‌اى از آيات پروردگارشان بر ايشان نازل نشود جز آنكه از آن اعراض كنند.'),
(3752, 36, 47, 'و چون گفته شود كه از آنچه خدا روزيتان كرده است انفاق كنيد، كافران به مؤمنان گويند: آيا كسانى را طعام دهيم كه اگر خدا مى‌خواست خود آنها را طعام مى‌داد؟ شما در گمراهى آشكار هستيد.'),
(3753, 36, 48, 'و مى‌گويند: اگر راست مى‌گوييد، اين وعده كى خواهد بود؟'),
(3754, 36, 49, 'اينان انتظار يك بانگ سهمناك را مى‌برند، تا بدان هنگام كه سرگرم ستيزه هستند فروگيردشان؛'),
(3755, 36, 50, 'آنچنان كه ياراى وصيتى نداشته باشند و نتوانند نزد كسان خويش بازگردند.'),
(3756, 36, 51, 'و در صور دميده شود و آنان از قبرها بيرون آيند و شتابان به سوى پروردگارشان روند.'),
(3757, 36, 52, 'مى‌گويند: واى بر ما، چه كسى ما را از خوابگاه‌هايمان برانگيخت؟ اين همان وعده خداى رحمان است و پيامبران راست گفته بودند.'),
(3758, 36, 53, 'جز يك بانگ سهمناك نخواهد بود، كه همه نزد ما حاضر مى‌آيند.'),
(3759, 36, 54, 'آن روز به كس ستم نمى‌شود. و جز همانند كارى كه كرده‌ايد پاداش نمى‌بينيد.'),
(3760, 36, 55, 'بهشتيان آن روز به شادمانى مشغول باشند.'),
(3761, 36, 56, 'آنها و همسرانشان در سايه‌ها بر تختها تكيه زده‌اند.'),
(3762, 36, 57, 'در آنجا هر ميوه و هر چيز ديگر كه بخواهند فراهم است.'),
(3763, 36, 58, 'و سلامى كه سخن پروردگار مهربان است.'),
(3764, 36, 59, 'اى گناهكاران، امروز كنارى گيريد.'),
(3765, 36, 60, 'اى فرزندان آدم، آيا با شما پيمان نبستم كه شيطان را نپرستيد زيرا دشمن آشكار شماست؟'),
(3766, 36, 61, 'و مرا بپرستيد، كه راه راست اين است؟'),
(3767, 36, 62, 'بسيارى از شما را گمراه كرد. مگر به عقل درنمى‌يافتيد؟'),
(3768, 36, 63, 'اين است آن جهنمى كه به شما وعده داده شده بود.'),
(3769, 36, 64, 'به سزاى كفرتان اينك در آن داخل شويد.'),
(3770, 36, 65, 'امروز بر دهانهايشان مهر مى‌نهيم. و دستهايشان با ما سخن خواهند گفت و پاهايشان شهادت خواهند داد كه چه مى‌كرده‌اند.'),
(3771, 36, 66, 'اگر بخواهيم، چشمانشان را محو مى‌كنيم. پس شتابان آهنگ راه كنند. اما كجا را توانند ديد؟'),
(3772, 36, 67, 'و اگر بخواهيم، آنها را بر جايشان مسخ كنيم، كه نه توان آن داشته باشند كه به پيش قدم بردارند و نه بازپس گردند.'),
(3773, 36, 68, 'هر كه را عمر دراز دهيم، در آفرينش دگرگونش كنيم. چرا تعقل نمى‌كنند؟'),
(3774, 36, 69, 'به او شعر نياموخته‌ايم و شعر در خور او نيست. آنچه به او آموخته‌ايم جز اندرز و قرآنى روشنگر نيست.'),
(3775, 36, 70, 'تا مؤمنان را بيم دهد و سخن حق بر كافران ثابت شود.'),
(3776, 36, 71, 'آيا نديده‌اند كه به يد قدرت خويش برايشان چارپايان را آفريديم و اكنون مالكشان هستند؟'),
(3777, 36, 72, 'و آنها را رامشان كرديم. بر بعضى سوار مى‌شوند و از گوشت بعضى مى‌خورند.'),
(3778, 36, 73, 'و ايشان را در آنها سودهاست و آشاميدنيها. چرا سپاس نمى‌گويند؟'),
(3779, 36, 74, 'و به جاى اللّه خدايانى اختيار كردند، بدان اميد كه ياريشان كنند.'),
(3780, 36, 75, 'آنها را ياراى آن نيست كه به ياريشان برخيزند، ولى اينان همانند سپاهى به خدمتشان آماده‌اند.'),
(3781, 36, 76, 'سخنشان تو را اندوهگين نسازد. ما هر چه را پنهان مى‌دارند يا آشكار مى‌سازند مى‌دانيم.'),
(3782, 36, 77, 'آيا آدمى كه اكنون خصمى آشكار است، نمى‌داند كه او را از نطفه‌اى آفريده‌ايم؟'),
(3783, 36, 78, 'در حالى كه آفرينش خود را از ياد برده است، براى ما مثَل مى‌زند كه چه كسى اين استخوانهاى پوسيده را زنده مى‌كند؟'),
(3784, 36, 79, 'بگو: كسى آنها را زنده مى‌كند كه در آغاز بيافريده است، و او به هر آفرينشى داناست.'),
(3785, 36, 80, 'آن خدايى كه از درخت سبز برايتان آتش پديد آورد و شما از آن آتش مى‌افروزيد.'),
(3786, 36, 81, 'آيا كسى كه آسمانها و زمين را آفريده است نمى‌تواند همانندشان را بيافريند؟ آرى مى‌تواند، كه او آفريننده‌اى داناست.'),
(3787, 36, 82, 'چون بخواهد چيزى را بيافريند، فرمانش اين است كه مى‌گويد: موجود شو، پس موجود مى‌شود.'),
(3788, 36, 83, 'منزه است آن خدايى كه ملكوت هر چيزى به دست اوست و همه به سوى او باز گردانده مى‌شويد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به آن فرشتگان كه براى نيايش صف بسته‌اند،'),
(3790, 37, 2, 'سوگند به آن فرشتگان كه ابرها را مى‌رانند،'),
(3791, 37, 3, 'سوگند به آن فرشتگان كه ذكر خدا را مى‌خوانند،'),
(3792, 37, 4, 'كه خداى شما خدايى يكتاست.'),
(3793, 37, 5, 'پروردگار آسمانها و زمين است و هر چه در ميان آنهاست. و پروردگار مشرقهاست.'),
(3794, 37, 6, 'ما آسمان فرودين را به زينت ستارگان بياراستيم.'),
(3795, 37, 7, 'و از هر شيطان نافرمان نگه داشتيم.'),
(3796, 37, 8, 'تا سخن ساكنان عالم بالا را نشنوند و از هر سوى رانده شوند.'),
(3797, 37, 9, 'تا دور گردند و براى آنهاست عذابى دايم.'),
(3798, 37, 10, 'مگر آن شيطان كه ناگهان چيزى بربايد و ناگهان شهابى ثاقب دنبالش كند.'),
(3799, 37, 11, 'پس، از كافران بپرس كه آيا آفرينش آنها دشوارتر است يا آنچه ما آفريده‌ايم. ما آنها را از گِلى چسبنده آفريده‌ايم.'),
(3800, 37, 12, 'تو در شگفت شدى و آنها مسخره مى‌كنند.'),
(3801, 37, 13, 'و چون به آنها پند داده شود، پند نمى‌پذيرند.'),
(3802, 37, 14, 'و چون آيه‌اى ببينند، از يكديگر مى‌خواهند تا به ريشخندش گيرند.'),
(3803, 37, 15, 'و مى‌گويند: اين چيزى جز جادويى آشكار نيست،'),
(3804, 37, 16, 'آيا وقتى كه ما مُرديم و خاك و استخوان شديم ما را زنده مى‌كنند؟'),
(3805, 37, 17, 'يا نياكان ما را؟'),
(3806, 37, 18, 'بگو: بلى، و شما خوار و بيچاره مى‌شويد.'),
(3807, 37, 19, 'و آنان خود خواهند ديد كه به يك فرمان همه را زنده مى‌كنند.'),
(3808, 37, 20, 'مى‌گويند: واى بر ما، اين همان روز جزاست.'),
(3809, 37, 21, 'اين همان روز داورى است كه دروغش مى‌پنداشتيد.'),
(3810, 37, 22, 'آنان را كه ستم مى‌كردند و همپايگانشان و آنهايى را كه به جاى خداى يكتا مى‌پرستيدند گرد آوريد.'),
(3811, 37, 23, 'همه را به راه جهنم راه بنماييد.'),
(3812, 37, 24, 'و نگه داريدشان، بايد بازخواست شوند.'),
(3813, 37, 25, 'چرا يكديگر را يارى نمى‌كنيد؟'),
(3814, 37, 26, 'نه، امروز همه تسليم‌شوندگانند.'),
(3815, 37, 27, 'و آنگاه روى به يكديگر كنند و بپرسند.'),
(3816, 37, 28, 'گويند: شما بوديد كه از در نيكخواهى بر ما درمى‌آمديد.'),
(3817, 37, 29, 'گويند: نه، شما ايمان نياورده بوديد.'),
(3818, 37, 30, 'ما را بر شما هيچ تسلطى نبود. شما خود مردمانى نافرمان بوديد.'),
(3819, 37, 31, 'گفته پروردگارمان بر ما ثابت شد. اكنون بايد بچشيم.'),
(3820, 37, 32, 'ما شما را گمراه كرديم و خود نيز گمراه بوديم.'),
(3821, 37, 33, 'در آن روز، همه در عذاب شريكند.'),
(3822, 37, 34, 'ما با مجرمان چنين مى‌كنيم.'),
(3823, 37, 35, 'چون به آنان گفته مى‌شد كه جز خداى يكتا خدايى نيست تكبر مى‌كردند،'),
(3824, 37, 36, 'و مى‌گفتند: آيا به خاطر شاعر ديوانه‌اى خدايانمان را ترك گوييم؟'),
(3825, 37, 37, 'نه، آن پيامبر حق را آورد و پيامبران پيشين را تصديق كرد.'),
(3826, 37, 38, 'شما عذاب دردآور را خواهيد چشيد.'),
(3827, 37, 39, 'جز در برابر اعمالتان كيفر نخواهيد ديد.'),
(3828, 37, 40, 'مگر بندگان مخلص خدا،'),
(3829, 37, 41, 'كه آنها راست رزقى معين،'),
(3830, 37, 42, 'از ميوه‌ها، و گرامى داشتگانند؛'),
(3831, 37, 43, 'در بهشتهاى پرنعمت،'),
(3832, 37, 44, 'بر تختهايى كه رو به روى هم‌اند.'),
(3833, 37, 45, 'و جامى از چشمه خوشگوار ميانشان به گردش درآيد،'),
(3834, 37, 46, 'سفيد است و نوشندگانش را لذت‌بخش،'),
(3835, 37, 47, 'نه عقل از آن تباهى گيرد و نه نوشنده مست شود.'),
(3836, 37, 48, 'زنانى درشت چشم كه تنها به شوهران خود نظر دارند، همدم آنهايند.'),
(3837, 37, 49, 'همانند تخم مرغهايى دور از دسترس.'),
(3838, 37, 50, 'رو به يكديگر كنند و به گفت و گو پردازند.'),
(3839, 37, 51, 'يكى از آنها گويد: مرا همنشينى بود،'),
(3840, 37, 52, 'كه مى‌گفت: آيا تو از آنها هستى كه تصديق مى‌كنند؟'),
(3841, 37, 53, 'آيا آنگاه كه مُرديم و خاك و استخوان شديم، كيفرمان مى‌دهند؟'),
(3842, 37, 54, 'گويد: مى‌توانيد از بالا بنگريد؟'),
(3843, 37, 55, 'از بالا بنگرد و او را در ميان آتش دوزخ بيند.'),
(3844, 37, 56, 'گويدش: به خدا سوگند، نزديك بود كه مرا به هلاكت افكنى.'),
(3845, 37, 57, 'و اگر موهبت پروردگارم نبود، من نيز از احضارشدگان مى‌بودم.'),
(3846, 37, 58, 'آيا ما را مرگى نيست،'),
(3847, 37, 59, 'جز آن مرگ نخستين؟ و ديگر عذابمان نمى‌كنند؟'),
(3848, 37, 60, 'هر آينه اين است كاميابى بزرگ.'),
(3849, 37, 61, 'عمل‌كنندگان بايد براى چنين پاداشى عمل كنند.'),
(3850, 37, 62, 'آيا خورش را اين بهتر يا درخت زقوم؟'),
(3851, 37, 63, 'ما آن درخت را براى ابتلاى ستمكاران پديد آورده‌ايم.'),
(3852, 37, 64, 'درختى است كه از اعماق جهنم مى‌رويد،'),
(3853, 37, 65, 'ميوه‌اش همانند سر شياطين است،'),
(3854, 37, 66, 'دوزخيان از آن مى‌خورند و شكم انباشته مى‌سازند،'),
(3855, 37, 67, 'و بر سر آن آميزه‌اى از آب سوزان مى‌نوشند.'),
(3856, 37, 68, 'سپس بازگشتشان به همان جهنم است.'),
(3857, 37, 69, 'آنها پدران خود را پيش از خود گمراه يافتند،'),
(3858, 37, 70, 'و بر پى ايشان شتابان مى‌روند.'),
(3859, 37, 71, 'و پيش از آنها بيشتر پيشينيان گمراه شدند.'),
(3860, 37, 72, 'و ما بيم‌دهندگانى به ميانشان فرستاديم.'),
(3861, 37, 73, 'پس بنگر كه سرانجام آن بيم‌يافتگان چگونه بود.'),
(3862, 37, 74, 'جز بندگان مخلص خدا.'),
(3863, 37, 75, 'نوح ما را ندا داد و ما چه نيك پاسخ‌دهنده‌اى بوديم.'),
(3864, 37, 76, 'او و كسانش را از اندوه بزرگ رهانيديم.'),
(3865, 37, 77, 'و فرزندانش را باقى گذاشتيم.'),
(3866, 37, 78, 'و نام نيك او را در نسلهاى بعد باقى نهاديم.'),
(3867, 37, 79, 'سلام بر نوح باد، در سراسر جهان.'),
(3868, 37, 80, 'ما نيكوكاران را چنين پاداش مى‌دهيم.'),
(3869, 37, 81, 'او از بندگان مؤمن ما بود.'),
(3870, 37, 82, 'سپس ديگران را غرقه ساختيم.'),
(3871, 37, 83, 'ابراهيم از پيروان او بود.'),
(3872, 37, 84, 'آنگاه كه با دلى رسته از ترديد روى به پروردگارش آورد.'),
(3873, 37, 85, 'به پدرش و قومش گفت: چه مى‌پرستيد؟'),
(3874, 37, 86, 'آيا به جاى خداى يكتا، خدايان دروغين را مى‌خواهيد؟'),
(3875, 37, 87, 'به پروردگار جهانيان چه گمان داريد؟'),
(3876, 37, 88, 'نگاهى به ستارگان كرد،'),
(3877, 37, 89, 'و گفت: من بيمارم.'),
(3878, 37, 90, 'از او رويگردان شدند و بازگشتند.'),
(3879, 37, 91, 'پنهانى نزد خدايانشان آمد و گفت: چيزى نمى‌خوريد؟'),
(3880, 37, 92, 'چرا سخن نمى‌گوييد؟'),
(3881, 37, 93, 'و در نهان، دستى به قوت بر آنها زد.'),
(3882, 37, 94, 'قومش شتابان نزدش آمدند.'),
(3883, 37, 95, 'گفت: آيا چيزهايى را كه خود مى‌تراشيد مى‌پرستيد؟'),
(3884, 37, 96, 'خداى يكتاست كه شما و هر چه مى‌سازيد، آفريده است.'),
(3885, 37, 97, 'گفتند: برايش بنايى برآوريد و در آتشش اندازيد.'),
(3886, 37, 98, 'خواستند تا بدانديشى كنند، ما نيز آنها را زيردست گردانيديم.'),
(3887, 37, 99, 'گفت: من به سوى پروردگارم مى‌روم، او مرا راهنمايى خواهد كرد؛'),
(3888, 37, 100, 'اى پروردگار من، مرا فرزندى صالح عطا كن.'),
(3889, 37, 101, 'پس او را به پسرى بردبار مژده داديم.'),
(3890, 37, 102, 'چون با پدر به جايى رسيد كه بايد به كار بپردازند، گفت: اى پسركم، در خواب ديده‌ام كه تو را ذبح مى‌كنم. بنگر كه چه مى‌انديشى. گفت: اى پدر، به هر چه مأمور شده‌اى عمل كن، كه اگر خدا بخواهد مرا از صابران خواهى يافت.'),
(3891, 37, 103, 'چون هر دو تسليم شدند و او را به پيشانى افكند،'),
(3892, 37, 104, 'ما ندايش داديم: اى ابراهيم،'),
(3893, 37, 105, 'خوابت را به حقيقت پيوستى. و ما نيكوكاران را چنين پاداش مى‌دهيم.'),
(3894, 37, 106, 'اين آزمايشى آشكارا بود.'),
(3895, 37, 107, 'و او را به ذبحى بزرگ بازخريديم.'),
(3896, 37, 108, 'و نام نيك او را در نسلهاى بعد باقى گذاشتيم.'),
(3897, 37, 109, 'سلام بر ابراهيم.'),
(3898, 37, 110, 'ما نيكوكاران را اينچنين پاداش مى‌دهيم.'),
(3899, 37, 111, 'او از بندگان مؤمن ما بود.'),
(3900, 37, 112, 'او را به اسحاق، پيامبرى شايسته، مژده داديم.'),
(3901, 37, 113, 'او و اسحاق را بركت داديم. و از فرزندانشان بعضى نيكوكار هستند و بعضى به آشكارا بر خود ستمكار.'),
(3902, 37, 114, 'ما به موسى و هارون نعمت داديم.'),
(3903, 37, 115, 'آن دو و مردمشان را از رنجى بزرگ نجات بخشيديم.'),
(3904, 37, 116, 'آنان را يارى داديم تا پيروز شدند.'),
(3905, 37, 117, 'و كتابى روشنگرشان داديم.'),
(3906, 37, 118, 'و به راه راست هدايتشان كرديم.'),
(3907, 37, 119, 'و نام نيكشان را در نسلهاى بعد باقى گذاشتيم.'),
(3908, 37, 120, 'سلام بر موسى و هارون.'),
(3909, 37, 121, 'ما نيكوكاران را اينچنين پاداش مى‌دهيم.'),
(3910, 37, 122, 'آن دو از بندگان مؤمن ما بودند.'),
(3911, 37, 123, 'و الياس از پيامبران بود.'),
(3912, 37, 124, 'به مردم خود گفت: آيا پروا نمى‌كنيد؟'),
(3913, 37, 125, 'آيا بَعل را به خدايى مى‌خوانيد، و آن بهترين آفرينندگان را وامى‌گذاريد؟'),
(3914, 37, 126, 'پروردگار شما و پروردگار نياكانتان، خداى يكتاست.'),
(3915, 37, 127, 'پس تكذيبش كردند و آنان از احضارشدگانند.'),
(3916, 37, 128, 'مگر بندگان مخلص خدا.'),
(3917, 37, 129, 'و نام نيك او را در نسلهاى بعد باقى گذاشتيم.'),
(3918, 37, 130, 'سلام بر خاندان الياس.'),
(3919, 37, 131, 'ما نيكوكاران را اينچنين پاداش مى‌دهيم.'),
(3920, 37, 132, 'او از بندگان مؤمن ما بود.'),
(3921, 37, 133, 'و لوط از پيامبران بود.'),
(3922, 37, 134, 'او و همه كسانش را نجات داديم.'),
(3923, 37, 135, 'مگر پيرزنى كه با مردم شهر بماند.'),
(3924, 37, 136, 'سپس ديگران را هلاك كرديم.'),
(3925, 37, 137, 'شما بر آنها مى‌گذريد، بامدادان،'),
(3926, 37, 138, 'و شامگاهان. آيا تعقل نمى‌كنيد؟'),
(3927, 37, 139, 'و يونس از پيامبران بود.'),
(3928, 37, 140, 'چون به آن كشتى پر از مردم گريخت،'),
(3929, 37, 141, 'قرعه زدند و او در قرعه مغلوب شد.'),
(3930, 37, 142, 'ماهى ببلعيدش و او در خور سرزنش بود.'),
(3931, 37, 143, 'پس اگر نه از تسبيح‌گويان مى‌بود،'),
(3932, 37, 144, 'تا روز قيامت در شكم ماهى مى‌ماند.'),
(3933, 37, 145, 'پس او را كه بيمار بود به خشكى افكنديم،'),
(3934, 37, 146, 'و بر فراز سرش بوته كدويى رويانيديم.'),
(3935, 37, 147, 'و او را به رسالت بر صد هزار كس و بيشتر فرستاديم.'),
(3936, 37, 148, 'آنها ايمان آوردند و تا زنده بودند، برخورداريشان داديم.'),
(3937, 37, 149, 'از ايشان بپرس: آيا دختران از آن پروردگار تو باشند و پسران از آن ايشان؟'),
(3938, 37, 150, 'آيا وقتى كه ما ملائكه را زن مى‌آفريديم آنها مى‌ديدند؟'),
(3939, 37, 151, 'آگاه باش كه از دروغگوييشان است كه مى‌گويند:'),
(3940, 37, 152, 'خدا صاحب فرزند است. دروغ مى‌گويند.'),
(3941, 37, 153, 'آيا خدا دختران را بر پسران برترى داد؟'),
(3942, 37, 154, 'شما را چه مى‌شود؟ چگونه قضاوت مى‌كنيد؟'),
(3943, 37, 155, 'آيا نمى‌انديشيد؟'),
(3944, 37, 156, 'يا بر ادعاى خود دليل روشنى داريد؟'),
(3945, 37, 157, 'اگر راست مى‌گوييد كتابتان را بياوريد.'),
(3946, 37, 158, 'و ميان خدا و جنيان خويشاوندى قائل شدند و جنيان مى‌دانند كه احضارشدگانند'),
(3947, 37, 159, 'از آن وصف كه مى‌آورند خدا منزه است.'),
(3948, 37, 160, 'مگر بندگان مخلص خدا.'),
(3949, 37, 161, 'شما و بتانى كه مى‌پرستيد،'),
(3950, 37, 162, 'نتوانستيد مفتون بتان كنيد،'),
(3951, 37, 163, 'مگر آن كس را كه راهى جهنم باشد.'),
(3952, 37, 164, 'و هيچ كس از ما نيست مگر آنكه جايى معين دارد.'),
(3953, 37, 165, 'هر آينه ما صف زدگانيم.'),
(3954, 37, 166, 'و هر آينه ما تسبيح‌گويندگانيم.'),
(3955, 37, 167, 'اگر چه مى‌گفتند:'),
(3956, 37, 168, 'اگر از پيشينيان نزد ما كتابى مانده بود،'),
(3957, 37, 169, 'ما از بندگان مخلص خدا مى‌بوديم.'),
(3958, 37, 170, 'آنها به آن كتاب كافر شدند، و زودا كه خواهند فهميد.'),
(3959, 37, 171, 'ما در باره بندگانمان كه به رسالت مى‌فرستيم از پيش تصميم گرفته‌ايم،'),
(3960, 37, 172, 'كه هر آينه آنان يارى مى‌شوند.'),
(3961, 37, 173, 'و لشكر ما خود غالبند.'),
(3962, 37, 174, 'پس تا مدتى از آنها روى بگردان.'),
(3963, 37, 175, 'عاقبتشان را ببين، آنها نيز خواهند ديد.'),
(3964, 37, 176, 'آيا عذاب ما را به شتاب مى‌طلبند؟'),
(3965, 37, 177, 'چون عذاب به ساحتشان فراز آيد، اين بيم‌داده‌شدگان چه بامداد بدى خواهند داشت.'),
(3966, 37, 178, 'از آنها تا چندى روى بگردان.'),
(3967, 37, 179, 'پس عاقبتشان را ببين، آنها نيز خواهند ديد.'),
(3968, 37, 180, 'منزه است پروردگار تو -آن پروردگار پيروزمند- از هر چه به وصف او مى‌گويند.'),
(3969, 37, 181, 'سلام بر پيامبران،'),
(3970, 37, 182, 'و حمد از آن پروردگار جهانيان است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صاد. سوگند به قرآن شريف صاحب اندرز.'),
(3972, 38, 2, 'كه كافران همچنان در سركشى و خلافند.'),
(3973, 38, 3, 'چه بسا مردمى را كه پيش از آنها به هلاكت رسانيديم. آنان فرياد برمى‌آوردند ولى گريزگاهى نبود.'),
(3974, 38, 4, 'در شگفت شدند از اينكه بيم‌دهنده‌اى از ميان خودشان برخاست. و كافران گفتند: اين جادوگرى دروغگوست.'),
(3975, 38, 5, 'آيا همه خدايان را يك خدا گردانيده است؟ و اين چيزى شگفت است.'),
(3976, 38, 6, 'مهترانشان به راه افتادند و گفتند: برويد و بر پرستش خدايان خويش پايدارى ورزيد كه اين است چيزى كه از شما خواسته شده.'),
(3977, 38, 7, 'ما در اين آخرين آيين، چنين سخنى نشنيده‌ايم و اين جز دروغ هيچ نيست.'),
(3978, 38, 8, 'آيا از ميان همه ما وحى بر او نازل شده است؟ بلكه آنها از وحى من در ترديدند، و هنوز عذاب مرا نچشيده‌اند.'),
(3979, 38, 9, 'يا آنكه خزاين رحمت پروردگار پيروزمند بخشنده‌ات در نزد آنهاست؟'),
(3980, 38, 10, 'يا فرمانروايى آسمانها و زمين و هر چه ميان آن دوست، از آن آنهاست؟ پس با نردبامهايى خود را به آسمان كشند.'),
(3981, 38, 11, 'آنجا لشكرى است ناچيز از چند گروه شكست‌خورده به هزيمت رفته.'),
(3982, 38, 12, 'پيش از آنها قوم نوح و عاد و فرعون، كه مردم را به چهار ميخ مى‌كشيد، پيامبران را تكذيب مى‌كردند.'),
(3983, 38, 13, 'و نيز قوم ثمود و قوم لوط و مردم اَيكه از آن جماعتها بودند.'),
(3984, 38, 14, 'از اينان كس نبود مگر آنكه پيامبران را تكذيب كرد، و عقوبت من واجب آمد.'),
(3985, 38, 15, 'اينان نيز جز بانگى سهمناك انتظارى ندارند، چنان كه آدمى را ديگر بازگشت نباشد.'),
(3986, 38, 16, 'و گفتند: اى پروردگار ما، نامه اعمال ما را پيش از فرارسيدن روز حساب به دست ما بده.'),
(3987, 38, 17, 'بر هر چه مى‌گويند صبر كن. و از بنده ما داود قدرتمند، كه همواره به درگاه ما توبه مى‌كرد، يادآور.'),
(3988, 38, 18, 'ما كوه‌ها را رام كرديم و كوه‌ها هر شامگاه و بامدادان با او تسبيح مى‌كردند.'),
(3989, 38, 19, 'و پرندگان بر او گرد مى‌آمدند، همه فرمانبر او بودند.'),
(3990, 38, 20, 'فرمانرواييش را استوارى بخشيديم و او را حكمت و فصاحت در سخن عطا كرديم.'),
(3991, 38, 21, 'آيا خبر آن مدعيان را شنيده‌اى آنگاه كه از ديوار قصر بالا رفتند؟'),
(3992, 38, 22, 'بر داود داخل شدند. داود از آنها ترسيد. گفتند: مترس، ما دو مدعى هستيم كه يكى بر ديگرى ستم كرده است. ميان ما به حق داورى كن و پاى از عدالت بيرون منه و ما را به راه راست هدايت كن.'),
(3993, 38, 23, 'اين برادر من است. او را نَوَد و نُه ميش است و مرا يك ميش. مى‌گويد: آن را هم به من واگذار، و در دعوى بر من غلبه يافته است.'),
(3994, 38, 24, 'داود گفت: او كه ميش تو را از تو مى‌خواهد تا به ميشهاى خويش بيفزايد بر تو ستم مى‌كند. و بسيارى از شريكان جز كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند -و اينان نيز اندك هستند- بر يكديگر ستم مى‌كنند. و داود دانست كه او را آزموده‌ايم. پس از پروردگارش آمرزش خواست و به ركوع درافتاد و توبه كرد.'),
(3995, 38, 25, 'ما اين خطايش را بخشيديم. او را به درگاه ما تقرب است و بازگشتى نيكو.'),
(3996, 38, 26, 'اى داود، ما تو را خليفه روى زمين گردانيديم. در ميان مردم به حق داورى كن و از پى هواى نفس مرو كه تو را از راه خدا منحرف سازد. آنان كه از راه خدا منحرف شوند، بدان سبب كه روز حساب را از ياد برده‌اند، به عذابى شديد گرفتار مى‌شوند.'),
(3997, 38, 27, 'ما اين آسمان و زمين و آنچه را كه ميان آنهاست به باطل نيافريده‌ايم. اين گمان كسانى است كه كافر شدند. پس واى بر كافران از آتش.'),
(3998, 38, 28, 'آيا كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، همانند فسادكنندگان در زمين قرار خواهيم داد؟ يا پرهيزگاران را چون گناهكاران؟'),
(3999, 38, 29, 'كتابى مبارك است كه آن را بر تو نازل كرده‌ايم، تا در آياتش بينديشند و خردمندان از آن پند گيرند.'),
(4000, 38, 30, 'سليمان را به داود عطا كرديم. چه بنده نيكويى بود و روى به خدا داشت.'),
(4001, 38, 31, 'آنگاه كه به هنگام عصر اسبان تيزرو را كه ايستاده بودند به او عرضه كردند،'),
(4002, 38, 32, 'گفت: من دوستى اين اسبان را بر ياد پروردگارم بگزيدم تا آفتاب در پرده غروب پوشيده شد،'),
(4003, 38, 33, 'آن اسبان را نزد من بازگردانيد. پس به بريدن ساقها و گردنشان آغاز كرد.'),
(4004, 38, 34, 'ما سليمان را آزموديم و بر تخت او جسدى را افكنديم و او روى به خدا آورد.'),
(4005, 38, 35, 'گفت: اى پروردگار من، مرا بيامرز و مرا مُلكى عطا كن كه پس از من كسى سزاوار آن نباشد، كه تو بخشاينده‌اى.'),
(4006, 38, 36, 'پس باد را رام او كرديم كه به نرمى هر جا كه آهنگ مى‌كرد، به فرمان او مى‌رفت.'),
(4007, 38, 37, 'و ديوان را، كه هم بنّا بودند و هم غوّاص.'),
(4008, 38, 38, 'و گروهى ديگر را، كه همه بسته در زنجير او بودند.'),
(4009, 38, 39, 'اين عطاى بى‌حساب ماست خواهى آن را ببخش و خواهى نگه دار.'),
(4010, 38, 40, 'او راست نزد ما تقرب و بازگشتى نيكو.'),
(4011, 38, 41, 'و از بنده ما ايّوب ياد كن آنگاه كه پروردگارش را ندا داد كه: مرا شيطان به رنج و عذاب افكنده است.'),
(4012, 38, 42, 'پايت را بر زمين بكوب: اين آبى است براى شستشو و سرد براى آشاميدن.'),
(4013, 38, 43, 'و به او خانواده‌اش و همچند آن از ديگر ياران را عطا كرديم و اين خود رحمتى از ما بود و براى خردمندان اندرزى.'),
(4014, 38, 44, 'دسته‌اى از چوبهاى باريك به دست گير و با آن بزن و سوگند خويش را مشكن. او را بنده‌اى صابر يافتيم. او كه همواره روى به درگاه ما داشت چه نيكو بنده‌اى بود.'),
(4015, 38, 45, 'بندگان ما ابراهيم و اسحاق و يعقوب آن مردان قدرتمند و بابصيرت را ياد كن.'),
(4016, 38, 46, 'آنان را خصلت پاكدلى بخشيديم تا ياد قيامت كنند.'),
(4017, 38, 47, 'آنها در نزد ما برگزيدگان و نيكانند.'),
(4018, 38, 48, 'و اسماعيل و اَليَسع و ذوالكِفل را ياد كن كه همه از نيكانند.'),
(4019, 38, 49, 'اين قرآن پندى است، و پرهيزكاران را بازگشتى نيكوست؛'),
(4020, 38, 50, 'بهشتهاى جاويدان كه درِ آن به رويشان گشاده است.'),
(4021, 38, 51, 'در آنجا تكيه زده‌اند و هرگونه ميوه و نوشيدنى كه بخواهند مى‌طلبند.'),
(4022, 38, 52, 'زنانى همسال از آن گونه كه جز به شوى خويش نظر ندارند نزد آنهايند.'),
(4023, 38, 53, 'اين است آن چيزهايى كه براى روز حساب به شما وعده داده‌اند.'),
(4024, 38, 54, 'اين رزق ماست كه پايان نيافتنى است،'),
(4025, 38, 55, 'چنين است. و سركشان را بدترين بازگشتهاست.'),
(4026, 38, 56, 'به جهنم مى‌روند كه بد جايگاهى است.'),
(4027, 38, 57, 'بايد آب جوشان و چرك و خونابه دوزخيان را بنوشند.'),
(4028, 38, 58, 'و شكنجه‌هايى ديگر، از هر نوع.'),
(4029, 38, 59, 'اين گروه با شما به آتش در مى‌آيند. خوش آمدشان مباد كه به آتش مى‌افتند.'),
(4030, 38, 60, 'گويند: نه، خوش آمد شما را مباد. شما اين عذاب را پيشاپيش براى ما فرستاده بوديد، چه بد جايگاهى است.'),
(4031, 38, 61, 'گويند: اى پروردگار ما، هر كس كه اين عذاب را پيشاپيش براى ما آماده كرده است عذابش را در آتش دو چندان افزون كن.'),
(4032, 38, 62, 'و گويند: چرا مردانى را كه از اشرار مى‌شمرديم اكنون نمى‌بينيم؟'),
(4033, 38, 63, 'آنان را به مسخره مى‌گرفتيم. آيا از نظرها دور مانده‌اند؟'),
(4034, 38, 64, 'اين جدال اهل جهنم با يكديگر چيزى است كه به حقيقت واقع شود.'),
(4035, 38, 65, 'بگو: جز اين نيست كه من بيم‌دهنده‌اى هستم و هيچ خدايى جز خداى يكتاى قهار نيست.'),
(4036, 38, 66, 'آن پيروزمند و آمرزنده، پروردگار آسمانها و زمين و هر چه ميان آنهاست.'),
(4037, 38, 67, 'بگو: اين خبرى بزرگ است،'),
(4038, 38, 68, 'كه شما از آن اعراض مى‌كنيد.'),
(4039, 38, 69, 'هنگامى كه با يكديگر جدال مى‌كردند من خبرى از ساكنان عالم بالا نداشتم.'),
(4040, 38, 70, 'تنها از آن روى به من وحى مى‌شود كه بيم‌دهنده‌اى روشنگر هستم.'),
(4041, 38, 71, 'پروردگارت به فرشتگان گفت: من بشرى را از گل مى‌آفرينم.'),
(4042, 38, 72, 'چون تمامش كردم و در آن از روح خود دميدم، همه سجده‌اش كنيد.'),
(4043, 38, 73, 'همه فرشتگان سجده كردند.'),
(4044, 38, 74, 'مگر ابليس كه بزرگى فروخت و از كافران شد.'),
(4045, 38, 75, 'گفت: اى ابليس، چه چيز تو را از سجده كردن در برابر آنچه من با دو دست خود آفريده‌ام منع كرد؟ آيا بزرگى فروختى يا مقامى ارجمند داشتى؟'),
(4046, 38, 76, 'گفت: من از او بهترم. مرا از آتش آفريده‌اى و او را از گل.'),
(4047, 38, 77, 'گفت: از اينجا بيرون شو كه تو مطرودى.'),
(4048, 38, 78, 'و تا روز قيامت لعنت من بر توست.'),
(4049, 38, 79, 'گفت: اى پروردگار من، مرا تا روزى كه از نو زنده شوند مهلت ده.'),
(4050, 38, 80, 'گفت: تو از مهلت يافتگانى،'),
(4051, 38, 81, 'تا آن روز معين معلوم.'),
(4052, 38, 82, 'گفت: به عزت تو سوگند كه همگان را گمراه كنم،'),
(4053, 38, 83, 'مگر آنها كه از بندگان مخلص تو باشند.'),
(4054, 38, 84, 'گفت: حق است و آنچه مى‌گويم راست است.'),
(4055, 38, 85, 'كه جهنم را از تو و از همه پيروانت پر كنم.'),
(4056, 38, 86, 'بگو: من از شما مزدى نمى‌طلبم و نيستم از آنان كه به دروغ چيزى بر خود مى‌بندند.'),
(4057, 38, 87, 'و اين جز اندرزى براى مردم جهان نيست.'),
(4058, 38, 88, 'و تو بعد از اين از خبر آن آگاه خواهى شد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'نازل شدن اين كتاب از جانب خداى پيروزمند حكيم است.'),
(4060, 39, 2, 'ما اين كتاب را به راستى بر تو نازل كرديم. پس خدا را بپرست و دين خود براى او خالص گردان.'),
(4061, 39, 3, 'آگاه باش كه دين خالص از آن خداست و آنان كه سواى او ديگرى را به خدايى گرفتند، گفتند: اينان را از آن رو مى‌پرستيم تا وسيله نزديكى ما به خداى يكتا شوند. و خدا در آنچه اختلاف مى‌كنند ميانشان حكم خواهد كرد. خدا آن را كه دروغگو و ناسپاس باشد هدايت نمى‌كند.'),
(4062, 39, 4, 'اگر خدا مى‌خواست كه براى خود فرزندى برگيرد، از ميان مخلوقات خود هر چه را كه مى‌خواست برمى‌گزيد. منزه است. اوست خداى يكتاى قهار.'),
(4063, 39, 5, 'آسمانها و زمين را به حق بيافريد. شب را بر روز داخل مى‌كند و روز را بر شب. و آفتاب و ماه را رام گردانيد و هر يك تا زمانى معين در حركتند. آگاه باش كه اوست پيروزمند و آمرزنده.'),
(4064, 39, 6, 'شما را از يك تن بيافريد. و از آن يك تن زنش را آفريد. و برايتان از چارپايان هشت جفت بيافريد. شما را در شكم مادرتان در چند مرحله در درون تاريكيهاى سه‌گانه آفرينش بخشيد. اين است خداى يكتا پروردگار شما. فرمانروايى از آن اوست. خدايى جز او نيست. پس چگونه رويگردانتان مى‌سازند؟'),
(4065, 39, 7, 'اگر ناسپاسى كنيد، خدا از شما بى‌نياز است. و ناسپاسى را براى بندگانش نمى‌پسندد. مى‌پسندد كه سپاسگزار باشيد. و هيچ كس بار گناه ديگرى را بر دوش نمى‌كشد. بازگشت همه شما نزد پروردگارتان است. او شما را از كارهايى كه مى‌كرده‌ايد آگاه مى‌كند. او به آنچه در دلها مى‌گذرد آگاه است.'),
(4066, 39, 8, 'چون به آدمى گزندى برسد، به پروردگارش روى مى‌آورد و او را مى‌خواند. آنگاه چون به او نعمتى بخشد، همه آن دعاها را كه پيش از اين كرده بود از ياد مى‌برد و براى خدا همتايانى قرار مى‌دهد تا مردم را از طريق او گمراه كنند. بگو: اندكى از كفرت بهره‌مند شو، كه تو از دوزخيان خواهى بود.'),
(4067, 39, 9, 'آيا آن كس كه در همه ساعات شب به عبادت پرداخته، يا در سجود است يا در قيام، و از آخرت بيمناك است و به رحمت پروردگارش اميدوار است، با آن كه چنين نيست يكسان است؟ بگو: آيا آنهايى كه مى‌دانند با آنهايى كه نمى‌دانند برابرند؟ تنها خردمندان پند مى‌پذيرند.'),
(4068, 39, 10, 'بگو: اى بندگان من كه ايمان آورده‌ايد، از پروردگارتان بترسيد. براى آنان كه در حيات اينجهانى نيكى كرده‌اند، پاداش نيك است. و زمين خدا پهناور است. مزد صابران بى‌حساب و كامل ادا مى‌شود.'),
(4069, 39, 11, 'بگو: من مأمور شده‌ام كه خدا را بپرستم و براى او در دين اخلاص ورزم.'),
(4070, 39, 12, 'و مرا فرموده‌اند كه نخستين مسلمانان باشم.'),
(4071, 39, 13, 'بگو: اگر پروردگارم را نافرمانى كنم، از عذاب آن روز بزرگ مى‌ترسم.'),
(4072, 39, 14, 'بگو: خدا را مى‌پرستم و براى او در دين خود اخلاص مى‌ورزم.'),
(4073, 39, 15, 'بپرستيد هر چيز ديگرى را جز او. بگو: زيان‌كنندگان كسانى هستند كه در روز قيامت خود و خاندانشان را از دست بدهند. بهوش باشيد كه اين زيانى آشكار است.'),
(4074, 39, 16, 'بالاى سرشان طبقات آتش است و در زير پايشان طبقات آتش. اين چيزى است كه خدا بندگان خود را بدان مى‌ترساند. پس اى بندگان من، از من بترسيد.'),
(4075, 39, 17, 'و كسانى را كه از پرستش بتان پرهيز كرده‌اند و به خدا روى آورده‌اند بشارت است. پس بندگان مرا بشارت ده:'),
(4076, 39, 18, 'آن كسانى كه به سخن گوش مى‌دهند و از بهترين آن پيروى مى‌كنند، ايشانند كسانى كه خدا هدايتشان كرده و اينان خردمندانند.'),
(4077, 39, 19, 'آيا كسى را كه حكم عذاب بر او محقق شده، تو مى‌توانى او را كه در آتش است برهانى؟'),
(4078, 39, 20, 'اما براى آنان كه از خدا مى‌ترسند غرفه‌هايى است بر فراز هم ساخته، كه از زيرشان جويباران روان است. اين وعده خداست و خدا وعده خود خلاف نخواهد كرد.'),
(4079, 39, 21, 'آيا نديده‌اى كه خدا از آسمان باران فرستاد و آن را چون چشمه‌سارهايى در زمين روان گردانيد، آنگاه به آن كشته‌هاى رنگارنگ برويانيد، سپس همه خشك مى‌شوند و مى‌بينى كه زرد شده‌اند، آنگاه خردشان مى‌سازد؟ هر آينه خردمندان را در آن اندرزى است.'),
(4080, 39, 22, 'آيا كسى كه خدا دلش را بر روى اسلام گشود و او در پرتو نور پروردگارش جاى دارد، همانند كسى است كه ايمان ندارد؟ پس واى بر سخت‌دلانى كه ياد خدا در دلهاشان راه ندارد، كه در گمراهى آشكار هستند.'),
(4081, 39, 23, 'خدا بهترين سخن را نازل كرده است. كتابى متشابه و دوتا دوتا، كه از تلاوت آن كسانى را كه از پروردگارشان مى‌ترسند از خوف تن بلرزد. سپس تن و جانشان به ياد خدا بيارامد. اين راه خداست كه هر كه را بخواهد بدان راهنمايى مى‌كند، و هر كه را خدا گمراه كند او را هيچ راهنمايى نخواهد بود.'),
(4082, 39, 24, 'آيا آن كس كه در روز قيامت با چهره خويش عذاب سخت قيامت را از خود بازمى‌دارد همانند كسى است كه بهشتى است؟ به ستمكاران گفته مى‌شود: به كيفر كارهايى كه مى‌كرده‌ايد عذاب را بچشيد.'),
(4083, 39, 25, 'پيشينيانشان تكذيب كردند و عذاب از جايى كه نمى‌دانستند بر سرشان رسيد.'),
(4084, 39, 26, 'خدا در اين جهان رسوايشان ساخت؛ ولى اگر بدانند، عذاب آخرت بزرگتر است.'),
(4085, 39, 27, 'ما در اين قرآن براى مردم هر گونه مثَلى آورديم، شايد پند گيرند.'),
(4086, 39, 28, 'قرآنى به زبان عربى بى هيچ انحراف و كجى. شايد پروا كنند.'),
(4087, 39, 29, 'خدا مثَلى مى‌زند: مردى را كه چند تن در او شريكند و بر سر او اختلاف دارند. و مردى كه تنها از آنِ يكى باشد. آيا اين دو با هم برابرند؟ سپاس خدا را. نه، بيشترشان نمى‌دانند.'),
(4088, 39, 30, 'تو مى‌ميرى و آنها نيز مى‌ميرند.'),
(4089, 39, 31, 'سپس همه در روز قيامت نزد پروردگارتان با يكديگر به خصومت خواهيد پرداخت.'),
(4090, 39, 32, 'پس كيست ستمكارتر از آن كه بر خدا دروغ مى‌بندد و سخن راستى را كه بر او آمده است تكذيب مى‌كند. آيا كافران را در جهنم جايگاهى نيست؟'),
(4091, 39, 33, 'و كسى كه سخن راست آورد و تصديقش كرد، آنان پرهيزگارانند.'),
(4092, 39, 34, 'برايشان هر چه بخواهند در نزد پروردگارشان مهياست. اين است پاداش نيكوكاران.'),
(4093, 39, 35, 'تا خدا بدترين اعمالى را كه مرتكب شده‌اند از آنان بزدايد، و به بهتر از آنچه مى‌كرده‌اند پاداششان دهد.'),
(4094, 39, 36, 'تو را به كسانى كه سواى خدا هستند، مى‌ترسانند. آيا خدا براى نگهدارى بنده‌اش كافى نيست؟ و هر كس را كه خدا گمراه سازد هيچ راهنمايى نخواهد بود.'),
(4095, 39, 37, 'هر كس را كه خدا راهنمايى كند، گمراه‌كننده‌اى نيست. آيا خدا پيروزمند و انتقام‌گيرنده نيست؟'),
(4096, 39, 38, 'اگر از آنها بپرسى: چه كسى آسمانها و زمين را آفريده است؟ خواهند گفت: خداى يكتا. بگو: پس اينهايى را كه سواى او مى‌پرستيد چگونه مى‌بينيد؟ اگر خداى يكتا بخواهد به من رنجى برساند آيا اينان مى‌توانند آن رنج را دفع كنند؟ يا اگر بخواهد به من رحمتى ارزانى دارد، مى‌توانند آن رحمت را از من بازدارند؟ بگو: خدا براى من بس است. توكل‌كنندگان به او توكل مى‌كنند.'),
(4097, 39, 39, 'بگو: اى قوم من، بر وفق امكان خويش عمل كنيد. من نيز عمل مى‌كنم. و به زودى خواهيد دانست كه‌'),
(4098, 39, 40, 'چه كسى به عذابى كه خوارش مى‌سازد گرفتار مى‌شود، يا عذاب جاويد بر سر او فرود مى‌آيد.'),
(4099, 39, 41, 'ما اين كتاب را به حق براى هدايت مردم بر تو نازل كرديم. پس هر كس كه هدايت يافت به سود خود اوست، و هر كه گمراه شد به زيان خود به گمراهى افتاده و تو وكيل آنها نيستى.'),
(4100, 39, 42, 'خدا جانها را به هنگام مردنشان مى‌گيرد، و نيز جان كسانى را كه در خواب خود نمرده‌اند. جانهايى را كه حكم مرگ بر آنها رانده شده نگه مى‌دارد و ديگران را تا زمانى كه معين است بازمى‌فرستد. در اين عبرتهاست براى آنهايى كه مى‌انديشند.'),
(4101, 39, 43, 'آيا سواى خدا شفيعانى اختيار كردند؟ بگو: حتى اگر آن شفيعان قدرت به كارى نداشته باشند و چيزى را درنيابند؟'),
(4102, 39, 44, 'بگو: شفاعت سراسر از آن خداست. متعلق به اوست فرمانروايى آسمانها و زمين. سپس همه به نزد او بازگردانده مى‌شويد.'),
(4103, 39, 45, 'چون خدا را به يكتايى ياد كنند دلهاى آن كسان كه به قيامت ايمان نياورده‌اند نفرت گيرد، و چون نام ديگرى جز او برده شود شادمان شوند.'),
(4104, 39, 46, 'بگو: بار خدايا، تويى آفريننده آسمانها و زمين، داناى نهان و آشكار، تو ميان بندگانت در هر چه در آن اختلاف مى‌كرده‌اند داورى خواهى كرد.'),
(4105, 39, 47, 'اگر هر چه در روى زمين است و همانند آن، از آن ستمكاران مى‌بود، در روز قيامت خود را بدان از عذاب دردناك بازمى‌خريدند. و از خدا برايشان چيزهايى آشكار مى‌شد كه هرگز حسابش را نمى‌كردند.'),
(4106, 39, 48, 'پاداش بد اعمالى كه مى‌كردند برايشان آشكار شد و آنچه مسخره‌اش مى‌كردند گرداگردشان را بگرفت.'),
(4107, 39, 49, 'چون آدمى را گزندى رسد ما را مى‌خواند، و چون از جانب خويش نعمتى ارزانيش داريم گويد: به سبب داناييم اين نعمت را به من داده‌اند. اين آزمايشى باشد ولى بيشترينشان نمى‌دانند.'),
(4108, 39, 50, 'اين سخنى بود كه پيشينيانشان هم مى‌گفتند، ولى هر چه گرد آورده بودند به حالشان سود نكرد.'),
(4109, 39, 51, 'عقوبت اعمالشان دامنگيرشان شد. و از اين ميان به آنهايى كه راه كفر پيش گرفته‌اند عقوبت اعمالشان خواهد رسيد و نمى‌توانند از ما بگريزند.'),
(4110, 39, 52, 'آيا هنوز ندانسته‌اند كه خداست كه روزى هر كس را كه بخواهد افزون مى‌سازد يا تنگ روزى‌اش مى‌كند؟ و در اين خود عبرتهاست براى مردمى كه ايمان مى‌آورند.'),
(4111, 39, 53, 'بگو: اى بندگان من كه بر زيان خويش اسراف كرده‌ايد، از رحمت خدا مأيوس مشويد. زيرا خدا همه گناهان را مى‌آمرزد. اوست آمرزنده و مهربان.'),
(4112, 39, 54, 'پيش از آنكه عذاب فرا رسد و كسى به ياريتان برنخيزد، به پروردگارتان روى آوريد و به او تسليم شويد.'),
(4113, 39, 55, 'و پيش از آنكه به ناگاه و بى‌خبر عذاب بر شما فرود آيد، از بهترين چيزى كه از جانب پروردگارتان نازل شده است پيروى كنيد.'),
(4114, 39, 56, 'تا كسى نگويد: اى حسرتا بر من كه در كار خدا كوتاهى كردم، و از مسخره‌كنندگان بودم.'),
(4115, 39, 57, 'يا بگويد: اگر خدا مرا هدايت كرده بود، من از پرهيزگاران مى‌بودم.'),
(4116, 39, 58, 'يا چون عذاب را ببيند، بگويد: اگر بار ديگر به دنيا باز مى‌گرديدم، از نيكوكاران مى‌شدم.'),
(4117, 39, 59, 'آرى، آيات من براى تو نازل شد و تو گردنكشى كردى و از كافران بودى.'),
(4118, 39, 60, 'در روز قيامت، كسانى را كه به خدا دروغ بسته‌اند مى‌بينى كه رويشان سياه شده است. آيا متكبران را در جهنم جايگاهى نيست؟'),
(4119, 39, 61, 'خدا پرهيزكاران را به سبب راه رستگارى كه در پيش گرفته بودند مى‌رهاند. به آنها هيچ بدى نرسد و از اندوه بدور باشند.'),
(4120, 39, 62, 'خداست كه آفريدگار هر چيزى است و او نگهبان هر چيزى.'),
(4121, 39, 63, 'كليدهاى آسمانها و زمين نزد اوست و آنها كه به آيات خدا كافر شده‌اند زيان‌ديدگان هستند.'),
(4122, 39, 64, 'بگو: اى نادانان، آيا مرا فرمان مى‌دهيد كه غير از خدا را بپرستم؟'),
(4123, 39, 65, 'به تو و پيامبران پيش از تو وحى شده است كه اگر شرك بياوريد اعمالتان ناچيز گردد و خود از زيان‌كنندگان خواهيد بود.'),
(4124, 39, 66, 'بلكه خدا را بپرست و سپاسگزار او باش.'),
(4125, 39, 67, 'خدا را نشناختند آنچنان كه شايان شناخت اوست. و در روز قيامت، زمين يكجا در قبضه اوست و آسمانها در هم پيچيده، در يد قدرت او. منزه است و برتر از هر چه شريك او مى‌پندارند.'),
(4126, 39, 68, 'و در صور دميده شود. پس هر كه در آسمانها و هر كه در زمين است -جز آنها كه او بخواهد- بيهوش مى‌شوند. و بار ديگر در آن دميده شود، ناگهان از جاى برمى‌خيزند و مى‌نگرند.'),
(4127, 39, 69, 'و زمين به نور پروردگارش روشن شود و نامه‌هاى اعمال را بنهند و پيامبران و گواهان را بياورند و در ميان مردم به حق داورى شود و بر كسى ستمى نرود.'),
(4128, 39, 70, 'پاداش هر كس برابر كردارش به تمامى ادا شود، در حالى كه خدا به كارهايى كه مى‌كرده‌اند آگاه‌تر است.'),
(4129, 39, 71, 'و كافران را گروه‌گروه به جهنم برانند. چون به جهنم رسند درهايش گشوده شود و نگهبانان آتش گويند: آيا پيامبرانى از خود شما بر شما مبعوث نشدند تا آيات پروردگارتان را برايتان بخوانند و شما را از ديدار با چنين روزى بترسانند؟ مى‌گويند: بلى. ولى، بر كافران عذاب محقق شده بود.'),
(4130, 39, 72, 'گفته شود: از درهاى جهنم داخل شويد، همواره در آنجا خواهيد بود. جايگاه سركشان چه بد جايگاهى است.'),
(4131, 39, 73, 'و آنان را كه از پروردگارشان ترسيده‌اند گروه‌گروه به بهشت مى‌برند. چون به بهشت برسند درهايش گشوده شود. و خازنان بهشت گويندشان: سلام بر شما، بهشتتان خوش باد، به درون بياييد، همواره در اينجا خواهيد بود.'),
(4132, 39, 74, 'مى‌گويند: سپاس خدايى را كه هر وعده كه به ما داد راست بود. آن زمين را به ميراث به ما داد. و اكنون در هر جاى بهشت كه بخواهيم مكان مى‌گيريم. عمل‌كنندگان را چه مزد نيكويى است.'),
(4133, 39, 75, 'و فرشتگان را مى‌بينى كه گرد عرش خدا حلقه زده‌اند و به ستايش پروردگارشان تسبيح مى‌گويند. ميان آنها نيز به حق داورى گردد و گفته شود كه ستايش از آن خدايى است كه پروردگار جهانيان است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حا، ميم.'),
(4135, 40, 2, 'نزول اين كتاب از جانب خداى پيروزمند داناست؛'),
(4136, 40, 3, 'آمرزنده گناه، پذيرنده توبه، سخت عقوبت، صاحب نعم. هيچ خدايى جز او نيست. بازگشت همه به سوى اوست.'),
(4137, 40, 4, 'جز كافران در آيات خدا جدال نمى‌كنند. پس جولانشان در شهرها تو را نفريبد.'),
(4138, 40, 5, 'پيش از ايشان قوم نوح و گروه‌هايى كه بعد از ايشان بودند پيامبرشان را تكذيب كردند. و هر امتى آهنگ آن كرد كه پيامبرش را دستگير كند، و به باطل به ستيزه برخاست تا حق را از ميان بردارد. اما من آنها را به عقوبت فروگرفتم و عقوبت من چه سخت بود.'),
(4139, 40, 6, 'بدين سان حكم پروردگار تو بر كافران محقق شد كه اهل جهنمند.'),
(4140, 40, 7, 'آنان كه عرش را حمل مى‌كنند و آنان كه بر گرد آن هستند به ستايش پروردگارشان تسبيح مى‌گويند و به او ايمان دارند و از او براى مؤمنان آمرزش مى‌خواهند: اى پروردگار ما، رحمت و علم تو همه چيز را فراگرفته است. پس آنان را كه توبه كرده‌اند و به راه تو آمده‌اند بيامرز و از عذاب جهنم نگه دار.'),
(4141, 40, 8, 'و اى پروردگار ما، آنان را و هر كه صالح باشد از پدران و همسران و فرزندانشان به بهشتهاى جاويدانى كه به آنها وعده داده‌اى داخل كن، كه تو پيروزمند و حكيمى.'),
(4142, 40, 9, 'آنان را از عقوبات حفظ كن، كه هر كه را در آن روز از عقوبات حفظ كنى بر او رحمت آورده‌اى و آن كاميابى بزرگى است.'),
(4143, 40, 10, 'كافران را ندا مى‌دهند كه دشمنى خدا با شما از دشمنى شما با خودتان، آنگاه كه به ايمانتان فرا مى‌خواندند و راه كفر پيش مى‌گرفتيد، بزرگ‌تر است.'),
(4144, 40, 11, 'مى‌گويند: اى پروردگار ما، ما را دوبار ميرانيدى و دوبار زنده ساختى و ما به گناهانمان اعتراف كرده‌ايم. آيا بيرون‌شدن را راهى هست؟'),
(4145, 40, 12, 'اين عذاب بدان سبب است كه چون خدا را به يكتايى مى‌خواندند شما انكار مى‌كرديد، و اگر براى او شريكى قرار مى‌دادند شما به آن شريك ايمان مى‌آورديد. پس، فرمان، فرمان خداى بلندمرتبه بزرگ است.'),
(4146, 40, 13, 'اوست آن كه آيات خويش را به شما نشان داد و برايتان از آسمان روزى فرستاد. تنها كسى پند مى‌گيرد كه روى به خدا آورد.'),
(4147, 40, 14, 'پس خدا را بخوانيد، در حالى كه تنها براى او در دين اخلاص مى‌ورزيد، اگر چه كافران را ناخوش آيد.'),
(4148, 40, 15, 'فرابرنده درجات، صاحب عرش كه بر هر يك از بندگانش كه بخواهد به فرمان خود وحى مى‌فرستد تا مردم را از روز قيامت بترساند.'),
(4149, 40, 16, 'آن روز كه همگان آشكار شوند. هيچ چيز از آنها بر خدا پوشيده نماند. در آن روز فرمانروايى از آن كيست؟ از آن خداى يكتاى قهار.'),
(4150, 40, 17, 'آن روز هر كس را همانند عملش جزا مى‌دهند و به كس ستمى نمى‌رود. و خدا زود به حسابها مى‌رسد.'),
(4151, 40, 18, 'از روز قيامت بترسانشان. آنگاه كه دلها لبريز از اندوه، نزديك حنجره‌ها رسد. ستمكاران را در آن روز نه خويشاوندى باشد و نه شفيعى كه سخنش را بشنوند.'),
(4152, 40, 19, 'نظرهاى دزديده را و هر چه دلها نهان داشته‌اند، مى‌داند.'),
(4153, 40, 20, 'خدا به حق داورى مى‌كند. ولى كسانى كه سواى او به خدايى مى‌خوانند هيچ داورى نتوانند، زيرا خدا مى‌شنود و مى‌بيند.'),
(4154, 40, 21, 'آيا در زمين سير نمى‌كنند تا بنگرند كه عاقبت پيشينيانشان چگونه بوده است؟ توانايى آنها و آثارى كه در روى زمين پديد آورده بودند از اينان بيش بود. و خدا آنها را به كيفر كفرشان فرو گرفت و از قهر خداوندشان نگهدارنده‌اى نبود.'),
(4155, 40, 22, 'اين بدان سبب بود كه پيامبرانشان با دلايل روشن نزدشان آمدند، ولى انكار كردند، و خدا هم آنان را فرو گرفت. و خدا نيرومند است و به سختى عقوبت مى‌كند.'),
(4156, 40, 23, 'ما موسى را با آيات خود و حجتى آشكار فرستاديم،'),
(4157, 40, 24, 'به سوى فرعون و هامان و قارون. و گفتند كه او جادوگرى دروغگوست.'),
(4158, 40, 25, 'چون دين حق را از جانب ما بر آنها عرضه داشت، گفتند: پسران كسانى را كه به او ايمان آورده‌اند بكشيد و زنانشان را زنده بگذاريد. و حيله‌سازى كافران جز در طريق تباهى نباشد.'),
(4159, 40, 26, 'فرعون گفت: بگذاريد موسى را بكشم و او خداى خود را به يارى طلبد. مى‌ترسم دينتان را ديگرگون كند يا در اين سرزمين فسادى برانگيزد.'),
(4160, 40, 27, 'موسى گفت: من به پروردگار خود و پروردگار شما از هر متكبرى كه به روز حساب باور ندارد، پناه مى‌برم.'),
(4161, 40, 28, 'و مردى مؤمن از خاندان فرعون كه ايمانش را پنهان داشته بود، گفت: آيا مردى را كه مى‌گويد كه پروردگار من خداى يكتاست و با دليلهايى روشن از جانب پروردگارتان آمده است، مى‌كشيد؟ اگر دروغ مى‌گويد، گناه دروغش بر گردن خود اوست؛ و اگر راست مى‌گويد، پاره‌اى از وعده‌هايى كه داده است به شما خواهد رسيد. هر آينه خدا هيچ گزافكار دروغگويى را هدايت نمى‌كند.'),
(4162, 40, 29, 'اى قوم من، امروز فرمانروايى از آن شماست. بر اين سرزمين غلبه داريد. ولى اگر عذاب خدا بر سر ما آيد چه كسى ياريمان خواهد كرد؟ فرعون گفت: شما را جز آنچه خود مصلحت ديده‌ام راهى ننمايم و جز به راه صواب راهنمايى نكنم.'),
(4163, 40, 30, 'آن كه ايمان آورده بود، گفت: اى قوم من، بر شما از آنچه بر سر آن اقوام ديگر آمده است بيمناكم.'),
(4164, 40, 31, 'همانند قوم نوح و عاد و ثمود و كسانى كه از آن پس آمدند. حال آنكه خدا براى بندگانش خواستار ستم نيست.'),
(4165, 40, 32, 'اى قوم من، از آن روز كه يكديگر را به فرياد بخوانيد بر شما بيمناكم.'),
(4166, 40, 33, 'آن روز كه همگى پشت كرده بازمى‌گرديد و هيچ كس شما را از عذاب خدا نگاه نمى‌دارد. و هر كس كه خدا گمراهش كند، هيچ راهنمايى ندارد.'),
(4167, 40, 34, 'يوسف پيش از اين با دلايل روشن بر شما بر شما مبعوث شد و شما از آنچه آورده بود همچنان در شك مى‌بوديد. چون يوسف بمرد گفتيد: خدا پس از او ديگر پيامبرى نخواهد فرستاد. خدا گزافكار شك‌آورنده را اين گونه گمراه مى‌سازد؛'),
(4168, 40, 35, 'كسانى كه بى‌هيچ حجتى كه در دست داشته باشند در آيات خدا جدال مى‌كنند. كار آنان نزد خدا و مؤمنان سخت ناپسند است. خدا بر دل هر متكبر جبارى اينچنين مهر مى‌نهد.'),
(4169, 40, 36, 'فرعون گفت: اى هامان، براى من كوشك بلندى بساز، شايد به آن درها دست يابم:'),
(4170, 40, 37, 'درهاى آسمانها. و خداى موسى را ببينم، زيرا پندارم كه دروغ مى‌گويد. بدين سان كردار زشت فرعون در نظرش آراسته شد و او از راه باز ماند و چاره‌جويى او جز زيان هيچ نبود.'),
(4171, 40, 38, 'آن مردى كه ايمان آورده بود گفت: اى قوم من، از پى من بياييد تا به راه صواب هدايتتان كنم.'),
(4172, 40, 39, 'اى قوم من، اين زندگى دنيا اندك مايه تعيشى است. و آخرت سراى بقاست.'),
(4173, 40, 40, 'هر كس كار بدى بكند جز همانند عملش كيفر نيابد. و هر كس از مرد و زن كه مؤمن باشد و عمل صالحى به جاى آرد به بهشت داخل شود و بى‌حساب روزى‌اش دهند.'),
(4174, 40, 41, 'اى قوم من، چيست كه من شما را به رهايى مى‌خوانم و شما مرا به آتش فرا مى‌خوانيد؟'),
(4175, 40, 42, 'مرا دعوت مى‌كنيد كه به خدا كافر شوم و چيزى را كه نمى‌شناسم شريك او قرار دهم، حال آنكه من شما را به خداى پيروزمند آمرزنده دعوت مى‌كنم.'),
(4176, 40, 43, 'بى‌شك آنچه شما مرا به آن دعوت مى‌كنيد ياراى آنش نيست كه در دنيا و آخرت كسى را به سوى خود خواند، حال آنكه بازگشت ما به سوى خداى يكتاست، و گزافكاران در جهنم باشند.'),
(4177, 40, 44, 'زودا كه آنچه را كه اكنون مى‌گويم به ياد خواهيد آورد. و من كار خويش به خدا واگذار كردم، زيرا او بندگانش را مى‌بيند.'),
(4178, 40, 45, 'خدا او را از آسيب مكرى كه برايش انديشيده بودند، نگه داشت و آن عذاب ناگوار خاندان فرعون را در ميان گرفت.'),
(4179, 40, 46, 'آتش. هر صبح و شام بر آن عرضه شوند و روزى كه قيامت برپا شود ندا دهند كه خاندان فرعون را به سخت‌ترين عذابها درآوريد.'),
(4180, 40, 47, 'و آنگاه در درون آتش با يكديگر به مجادله برخيزند و ناتوانان به گردنكشان گويند كه ما پيروان شما بوديم، آيا مى‌توانيد اندكى از اين آتشى كه نصيب ما شده است بكاهيد؟'),
(4181, 40, 48, 'آنان كه سركشى كرده بودند مى‌گويند: همه ما در آتشيم. و خداست كه ميان بندگانش داورى كرده است.'),
(4182, 40, 49, 'و آنان كه در آتشند به نگهبانان جهنم مى‌گويند: از پروردگارتان بخواهيد تا يك روز از عذاب ما بكاهد.'),
(4183, 40, 50, 'مى‌گويند: آيا پيامبرانتان با دلايل روشن نزد شما نيامده بودند؟ مى گويند: بلى. مى‌گويند: پس دعا كنيد. و دعاى كافران جز آنكه تباه شود هيچ نخواهد بود.'),
(4184, 40, 51, 'ما پيامبرانمان و مؤمنان را در زندگى دنيا و در روز قيامت كه شاهدان براى شهادت بر پاى مى‌ايستند، يارى مى‌كنيم.'),
(4185, 40, 52, 'در روزى كه ستمكاران را پوزش خواستن سود ندهد و نصيب آنها لعنت است و آن سراى بد.'),
(4186, 40, 53, 'ما به موسى مرتبه هدايت داديم و آن كتاب را به بنى‌اسرائيل ميراث داديم.'),
(4187, 40, 54, 'كتابى كه خود هدايت است و براى خردمندان اندرز.'),
(4188, 40, 55, 'صبر كن كه وعده خدا حق است. براى گناهت آمرزش بخواه و هر شامگاه و بامداد، به ستايش پروردگارت تسبيح بگوى.'),
(4189, 40, 56, 'آنان كه بى‌هيچ حجتى كه از آسمان آمده باشد درباره آيات خدا مجادله مى‌كنند، در دلشان جز هواى بزرگى نيست؛ ولى به آن نخواهند رسيد. پس به خدا پناه ببر كه او شنوا و بيناست.'),
(4190, 40, 57, 'آفرينش آسمان و زمين از آفرينش مردم بزرگتر است، ولى بيشتر مردم نمى‌دانند.'),
(4191, 40, 58, 'نابينا و بينا برابر نيستند. نيز آنهايى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند با زشتكاران يكسان نباشند. چه اندك پند مى‌پذيريد.'),
(4192, 40, 59, 'قيامت مى‌آيد و در آن ترديدى نيست، ولى بيشتر مردم ايمان نمى‌آورند.'),
(4193, 40, 60, 'پروردگارتان گفت: بخوانيد مرا تا شما را پاسخ گويم. آنهايى كه از پرستش من سركشى مى‌كنند زودا كه در عين خوارى به جهنم درآيند.'),
(4194, 40, 61, 'خداست آن كه شب را برايتان پديد آورد تا در آن بياراميد و روز را روشنايى بخشيد. خدا فضل خويش به مردم ارزانى مى‌دارد. ولى بيشتر مردم سپاسگزار نيستند.'),
(4195, 40, 62, 'اين است خداى يكتا پروردگار شما، آفريدگار هر چيزى. خدايى جز او نيست. پس چگونه از حق منحرفتان مى‌كنند؟'),
(4196, 40, 63, 'همچنين، آنان كه آيات خدا را انكار مى‌كنند، از ايمان روى برمى‌گردانند.'),
(4197, 40, 64, 'خداست كه زمين را قرارگاه شما ساخت و آسمان را چون بنايى بيفراشت. و شما را صورت بخشيد، و صورتهايتان را نيكو ساخت. و از چيزهاى پاكيزه و خوش روزيتان داد. اين است خداى يكتا پروردگار شما. برتر و بزرگوار است خدا آن پروردگار جهانيان.'),
(4198, 40, 65, 'او زنده است. خدايى جز او نيست. او را بخوانيد در حالى كه دين او را به اخلاص پذيرفته باشيد، كه ستايش از آن خدايى است كه پروردگار جهانيان است.'),
(4199, 40, 66, 'بگو: مرا نهى كرده‌اند كه چيزهايى را كه سواى خدا مى‌پرستيد پرستش كنم، در حالى كه از جانب پروردگارم براى من دلايلى روشن آمده است. و به من فرمان داده‌اند كه در برابر پروردگار جهانيان تسليم باشم.'),
(4200, 40, 67, 'اوست آن خدايى كه شما را از خاك، سپس از نطفه، سپس از لخته خونى بيافريده است. آنگاه شما را كه كودكى بوديد از رحم مادر آورد تا به سن جوانى برسيد و پير شويد. بعضى از شما پيش از پيرى بميريد و بعضى به آن زمان معين مى‌رسيد و شايد به عقل دريابيد.'),
(4201, 40, 68, 'اوست كه زنده مى‌كند و مى‌ميراند. و چون اراده چيزى كند مى‌گويدش: موجود شو. پس موجود مى‌شود.'),
(4202, 40, 69, 'آيا نديده‌اى آنهايى كه در آيات خدا مجادله مى‌كنند چگونه منحرف مى‌شوند؟'),
(4203, 40, 70, 'كسانى كه اين كتاب را دروغ مى‌انگارند و آنچه را كه به وسيله پيامبران فرستاده‌ايم تكذيب مى‌كنند، به زودى خواهند دانست.'),
(4204, 40, 71, 'آنگاه كه غُلها را به گردنشان اندازند و با زنجيرها بكشندشان،'),
(4205, 40, 72, 'در آب جوشان، سپس در آتش، افروخته شوند.'),
(4206, 40, 73, 'آنگاه به آنها گفته شود: آن شريكان كه براى خدا مى‌پنداشتيد كجا هستيد؟'),
(4207, 40, 74, 'مى‌گويند: آنها را از دست داده‌ايم، بلكه پيش از اين چيزى را به خدايى نمى‌خوانده‌ايم. خدا كافران را بدين سان گمراه مى‌كند.'),
(4208, 40, 75, 'اين بدان سبب است كه به ناحق در زمين شادمانى مى‌كرديد و به ناز مى‌خراميديد.'),
(4209, 40, 76, 'از درهاى جهنم داخل شويد. همواره در آنجا بمانيد. و جايگاه سركشان چه بد جايگاهى است.'),
(4210, 40, 77, 'پس صبر كن، البته وعده خدا راست است. يا بعضى از آن چيزها را كه به آنها وعده داده‌ايم به تو نشان مى‌دهيم يا تو را بميرانيم و آنها به نزد ما بازگردانيده شوند.'),
(4211, 40, 78, 'پيش از تو پيامبرانى فرستاده‌ايم. داستان بعضى را برايت گفته‌ايم و داستانى بعضى را نگفته‌ايم. و هيچ پيامبرى را نرسد كه آيه‌اى بياورد مگر به فرمان خدا. و چون فرمان خدا در رسد به حق داورى گردد و آنان كه بر باطل بوده‌اند -آنجا- زيان خواهند ديد.'),
(4212, 40, 79, 'خداست كه چارپايان را برايتان آفريد تا بر بعضى سوار شويد و از بعضى بخوريد.'),
(4213, 40, 80, 'و شما را در آن منافعى است و مى‌توانيد با آنها به حاجاتى كه در نظر داريد برسيد. و بر آنها و بر كشتيها سوار شويد.'),
(4214, 40, 81, 'آيات خويش را به شما مى‌نماياند. پس كدام يك از آيات خدا را انكار مى‌كنيد؟'),
(4215, 40, 82, 'آيا در زمين سير نكرده‌اند تا بنگرند كه عاقبت كسانى كه پيش از آنها مى‌آيسته‌اند چگونه بوده است؟ مردمى كه نيرويشان بيشتر و آثارشان در روى زمين فراوان‌تر بود. پس آن چيزها كه به دست مى آوردند سودشان نبخشيد.'),
(4216, 40, 83, 'چون پيامبرانشان با دلايل روشن به سويشان آمدند، به دانش خود دلخوش بودند تا آن چيزى كه مسخره‌اش مى‌كردند آنها را در ميان گرفت.'),
(4217, 40, 84, 'و چون عذاب ما را ديدند، گفتند: به خداى يكتا ايمان آورديم و به آن چيزهايى كه شريك خدا قرار داده بوديم كافر شديم.'),
(4218, 40, 85, 'اما بدان هنگام كه عذاب ما را ديدند ديگر ايمانشان برايشان سودى نبخشيد. اين سنت خداست در رفتار با بندگانش. و كافران در آن روز زيان كردند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حا، ميم.'),
(4220, 41, 2, 'كتابى است كه از جانب آن بخشاينده مهربان نازل شده است.'),
(4221, 41, 3, 'كتابى است كه آيه‌هايش به وضوح بيان شده، قرآنى است به زبان عربى براى مردمى كه مى‌دانند.'),
(4222, 41, 4, 'هم مژده‌دهنده است و هم بيم‌دهنده. بيشترشان از آن اعراض كرده‌اند و سخن نمى‌شنوند.'),
(4223, 41, 5, 'گفتند: دلهاى ما از آنچه ما را بدان دعوت مى‌كنى در پرده است و گوشهامان سنگين است و ميان ما و تو حجابى است. تو به كار خود پرداز و ما نيز به كار خود مى‌پردازيم.'),
(4224, 41, 6, 'بگو: من انسانى هستم همانند شما. به من وحى شده كه خدايتان خدايى است يكتا. پس بدو روى آوريد و از او آمرزش بخواهيد. و واى بر مشركان:'),
(4225, 41, 7, 'آنهايى كه زكات نمى‌دهند و به آخرت ايمان ندارند.'),
(4226, 41, 8, 'آنان را كه ايمان آورده‌اند و كارهاى شايسته مى‌كنند، پاداشى است تمام ناشدنى.'),
(4227, 41, 9, 'بگو: آيا به كسى كه زمين را در دو روز آفريده است كافر مى‌شويد و براى او همتايان قرار مى‌دهيد؟ اوست پروردگار جهانيان.'),
(4228, 41, 10, 'بر روى زمين كوه‌ها پديد آورد و آن را پر بركت ساخت و رزق همه را معين كرد، در چهار روز يكسان براى همه سائلان.'),
(4229, 41, 11, 'سپس به آسمان پرداخت و آن دودى بود. پس به آسمان و زمين گفت: خواه يا ناخواه بياييد. گفتند: فرمانبردار آمديم.'),
(4230, 41, 12, 'آنگاه هفت آسمان را در دو روز پديد آورد. و در هر آسمانى كارش را به آن وحى كرد. و آسمان فرودين را به چراغهايى بياراستيم و محفوظش داشتيم. اين است تدبير آن پيروزمند دانا.'),
(4231, 41, 13, 'پس اگر اعراض كردند، بگو: شما را از صاعقه‌اى همانند صاعقه‌اى كه بر عاد و ثمود فرود آمد مى‌ترسانم.'),
(4232, 41, 14, 'آنگاه كه رسولان پيش و بعد آنها نزدشان آمدند و گفتند كه جز خداى يكتا را مپرستيد، گفتند: اگر پروردگار ما مى‌خواست فرشتگان را از آسمان نازل مى‌كرد. ما به آنچه شما بدان مبعوث شده‌ايد ايمان نمى‌آوريم.'),
(4233, 41, 15, 'اما قوم عاد، به ناحق در روى زمين گردنكشى كردند و گفتند: چه كسى از ما نيرومندتر است؟ آيا نمى‌ديدند كه خدايى كه آنها را آفريده است از آنها نيرومندتر است كه آيات ما را انكار مى‌كردند؟'),
(4234, 41, 16, 'ما نيز بادى سخت و غران در روزهايى شوم بر سرشان فرستاديم تا در دنيا عذاب خوارى را به آنها بچشانيم. و عذاب آخرت خواركننده‌تر است و كسى به ياريشان برنخيزد.'),
(4235, 41, 17, 'اما قوم ثمود، هدايتشان كرديم. و آنها كورى را از هدايت بيشتر دوست مى‌داشتند. تا آنكه به خاطر اعمالى كه مى‌كردند صاعقه عذاب خواركننده آنها را فروگرفت.'),
(4236, 41, 18, 'و ما كسانى را كه ايمان آورده بودند و پرهيزگار بودند نجات داديم.'),
(4237, 41, 19, 'و روزى كه دشمنان خدا را گرد آورند و به صف برانندشان،'),
(4238, 41, 20, 'چون به كنار آتش آيند، گوش و چشمها و پوستهاشان به اعمالى كه مرتكب شده‌اند بر ضدشان شهادت دهند.'),
(4239, 41, 21, 'به پوستهاى خود گويند: چرا بر ضد ما شهادت داديد؟ گويند: آن خدايى كه هر چيزى را به سخن مى‌آورد و شما را نخستين بار بيافريد و به او بازگشت مى‌يابيد، ما را به سخن آورده است.'),
(4240, 41, 22, 'از اينكه گوش و چشمها و پوستهايتان به زيان شما شهادت دهند چيزى نهان نمى‌داشتيد، بلكه مى‌پنداشتيد كه خدا بر بسيارى از كارهايى كه مى‌كنيد آگاه نيست.'),
(4241, 41, 23, 'و اين بود گمانى كه به پروردگارتان داشتيد. هلاكتان كرد و در شمار زيان‌كردگان درآمديد.'),
(4242, 41, 24, 'پس اگر شكيبايى ورزند، جايگاهشان در آتش است، و اگر هم طالب عفو شوند كسى آنها را عفو نكند.'),
(4243, 41, 25, 'و برايشان همدمانى مقدر كرديم و آنان حال و آينده را در نظرشان بياراستند. و بر آنها نيز همانند پيشينيانشان از جن و انس، عذاب مقرر شد. زيرا زيانكار بودند.'),
(4244, 41, 26, 'كافران گفتند: به اين قرآن گوش مدهيد و سخن بيهوده بدان بياميزيد، شايد پيروز گرديد.'),
(4245, 41, 27, 'كافران را عذابى سخت مى‌چشانيم و بدتر از آنچه مى‌كرده‌اند پاداش مى‌دهيم.'),
(4246, 41, 28, 'اين است كيفر دشمنان خدا: آتش. در درون آن خانه‌اى هميشگى دارند. اين كيفر آنهاست، زيرا آيات ما را انكار مى‌كردند.'),
(4247, 41, 29, 'كافران گويند: اى پروردگار ما، آن دو تن را از جن و انس كه ما را گمراه كردند به ما بنمايان، تا پاى بر سر آنها نهيم تا از ما فروتر روند.'),
(4248, 41, 30, 'بر آنان كه گفتند: پروردگار ما اللّه است و پايدارى ورزيدند، فرشتگان فرود مى‌آيند كه مترسيد و غمگين مباشيد، شما را به بهشتى كه به شما وعده داده شده بشارت است.'),
(4249, 41, 31, 'ما در دنيا دوستدار شما بوديم و نيز در آخرت دوستدار شماييم. در بهشت هر چه دلتان بخواهد و هر چه طلب كنيد برايتان فراهم است.'),
(4250, 41, 32, 'سفره‌اى است از جانب خداى آمرزنده مهربان.'),
(4251, 41, 33, 'چه كسى را سخن نيكوتر از سخن آن كه به سوى خدا دعوت مى‌كند و كارهاى شايسته مى‌كند و مى‌گويد البته كه من از مسلمانانم؟'),
(4252, 41, 34, 'خوبى و بدى برابر نيستند. همواره به نيكوترين وجهى پاسخ ده، تا كسى كه ميان تو و او دشمنى است چون دوست مهربان تو گردد.'),
(4253, 41, 35, 'برخوردار نشوند از اين مگر كسانى كه شكيبا باشند و كسانى كه از ايمان بهره‌اى بزرگ داشته باشند.'),
(4254, 41, 36, 'و اگر از جانب شيطان دستخوش وسوسه‌اى گردى، به خدا پناه ببر، كه او شنوا و داناست.'),
(4255, 41, 37, 'و از نشانه‌هاى قدرت او شب و روز و آفتاب و ماه است. به آفتاب و ماه سجده مكنيد. به خداى يكتا كه آنها را آفريده است سجده كنيد، اگر او را مى‌پرستيد.'),
(4256, 41, 38, 'اگر آنان تكبر مى‌ورزند، آنها كه در نزد پروردگار تو هستند بى‌آنكه ملول شوند شب و روز تسبيح او مى‌گويند.'),
(4257, 41, 39, 'و از آيات قدرت او آنكه تو زمين را خشك مى‌بينى. چون آب بر آن بفرستيم به جنبش آيد و گياه بروياند. آن كس كه آن را زنده مى‌كند زنده‌كننده مردگان است، كه او بر هر چيزى تواناست.'),
(4258, 41, 40, 'كسانى كه در آيات ما راه باطل پيش مى‌گيرند، بر ما پوشيده نيستند. آيا آن كه به آتش افكنده مى‌شود بهتر است يا آن كه روز قيامت بى‌هيچ وحشتى مى‌آيد؟ هر چه مى‌خواهيد بكنيد، او به كارهايتان بيناست.'),
(4259, 41, 41, 'به كيفر خويش رسند آنان كه به قرآن كه براى هدايتشان آمده است ايمان نمى‌آورند، حال آنكه كتابى ارجمند است.'),
(4260, 41, 42, 'نه از پيش روى باطل بدو راه يابد و نه از پس. نازل شده از جانب خداوندى حكيم و ستودنى است.'),
(4261, 41, 43, 'هر چه درباره تو مى‌گويند درباره پيامبران پيش از تو نيز گفته‌اند. هر آينه پروردگار تو هم آمرزنده است و هم صاحب عقوبتى است دردآور.'),
(4262, 41, 44, 'اگر قرآن را به زبان عجم مى‌فرستاديم، مى‌گفتند: چرا آياتش به روشنى بيان نشده است؟ كتابى به زبان عجم و پيامبرى عرب؟ بگو: اين كتاب براى آنها كه ايمان آورده‌اند هدايت و شفاست و آنها كه ايمان نياورده‌اند گوشهاشان سنگين است، و چشمانشان كور است، چنانند كه گويى آنها را از جايى دور ندا مى‌دهند.'),
(4263, 41, 45, 'موسى را كتاب داديم، اما در آن اختلاف كردند. و اگر نبود آن سخنى كه پروردگارت از پيش گفته بود، ميانشان كار به پايان مى‌آمد. و البته هنوز به سختى در ترديدند.'),
(4264, 41, 46, 'هر كس كه كارى شايسته كند، به سود خود اوست و هر كه بد كند به زيان اوست. و پروردگار تو به بندگان ستم روا نمى‌دارد.'),
(4265, 41, 47, 'علم به روز قيامت نزد اوست. و هيچ ثمره‌اى از غلاف خويش بيرون نمى‌آيد، و هيچ ماده‌اى آبستن نمى‌شود و نمى‌زايد مگر آنكه او بدان آگاه است. و آن روز كه ايشان را ندا دهد كه شريكان من كجايند؟ گويند: آگاهت كرديم كه كسى از ما به شرك گواهى نمى‌دهد.'),
(4266, 41, 48, 'و آنچه را پيش از اين به خدايى مى‌خواندند از دست دادند و دانستند كه راه گريزى ندارند.'),
(4267, 41, 49, 'آدمى هر چه طلب خير كند خسته نمى‌شود، اما چون بدى به او برسد بدانديش و نوميد مى‌گردد.'),
(4268, 41, 50, 'اگر پس از رنجى كه به او رسيده رحمتى به او بچشانيم مى‌گويد: اين حق من است و نپندارم كه قيامتى برپا شود، و اگر هم مرا نزد پروردگارم برگردانند البته كه نزد او حالتى خوشتر باشد. پس كافران را به اعمالى كه كرده‌اند آگاه مى‌كنيم و به آنها عذابى سخت مى‌چشانيم.'),
(4269, 41, 51, 'چون به آدمى نعمتى ارزانى داريم، رويگردان مى‌شود و به تكبر گردن مى‌افرازد. و اگر بدو شرى برسد، بسيار فرياد و فغان مى‌كند.'),
(4270, 41, 52, 'بگو: چه مى‌بينيد؟ اگر اين كتاب از جانب خداست و شما بدان ايمان نمى‌آوريد، گمراه‌تر از كسى كه همچنان راه مخالفت در پيش گرفته است كيست؟'),
(4271, 41, 53, 'زودا كه آيات قدرت خود را در آفاق و در وجود خودشان به آنها نشان خواهيم داد تا برايشان آشكار شود كه او حق است. آيا اينكه پروردگار تو در همه جا حاضر است كافى نيست؟'),
(4272, 41, 54, 'بهوش باش كه آنها از ديدار پروردگارشان در ترديدند. بهوش باش كه او بر هر چيزى احاطه دارد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حا، ميم.'),
(4274, 42, 2, 'عين، سين، قاف.'),
(4275, 42, 3, 'خداوند پيروزمند حكيم، به تو و كسانى كه پيش از تو بودند اينچنين وحى مى‌فرستد:'),
(4276, 42, 4, 'از آن اوست هر چه در آسمانها و زمين است. و اوست آن بلند مرتبه بزرگوار.'),
(4277, 42, 5, 'نزديك است كه آسمانها بر فراز يكديگر در هم شكنند و فرشتگان به ستايش پروردگارشان تسبيح مى‌گويند و براى ساكنان زمين آمرزش مى‌طلبند. آگاه باشيد كه خدا آمرزنده و مهربان است.'),
(4278, 42, 6, 'خدا مراقب اعمال كسانى است كه جز او را به دوستى گرفتند، و تو وكيل آنها نيستى.'),
(4279, 42, 7, 'و نيز اين قرآن را به زبان عربى بر تو وحى كرديم تا ام‌القرى و ساكنان اطرافش را بيم دهى. همچنين آنان را از روز قيامت -كه در آن ترديدى نيست- بترسانى كه گروهى در بهشتند و گروهى در آتش سوزان.'),
(4280, 42, 8, 'اگر خدا مى‌خواست همه را يك امت كرده بود. ولى او هر كه را كه بخواهد به رحمت خويش درآورد، و ستمكاران را هيچ دوست و ياورى نيست.'),
(4281, 42, 9, 'آيا جز خدا را به دوستى گرفتند؟ دوست حقيقى خداست. و اوست كه مردگان را زنده مى‌كند، و اوست كه بر هر كارى تواناست.'),
(4282, 42, 10, 'در هر چه اختلاف مى‌كنيد حكمش با خداست. اين خداى يكتا پروردگار من است. بر او توكل كردم و به او روى مى‌آورم.'),
(4283, 42, 11, 'آفريدگار آسمانها و زمين است. براى شما، هم از شما، همسرانى بيافريد. و نيز براى چارپايان جفتهايى پديد آورد. با آفرينش همسران بر شمارتان مى‌افزايد. هيچ چيز همانند او نيست و اوست كه شنوا و بيناست.'),
(4284, 42, 12, 'كليدهاى آسمانها و زمين از آن اوست. در روزى هر كه بخواهد گشايش مى‌دهد يا تنگ مى‌گيرد، و او به هر چيزى داناست.'),
(4285, 42, 13, 'براى شما آيينى مقرر كرد، از همان گونه كه به نوح توصيه كرده بود و از آنچه بر تو وحى كرده‌ايم و به ابراهيم و موسى و عيسى توصيه كرده‌ايم كه دين را بر پاى نگه داريد و در آن فرقه فرقه مشويد. تحمل آنچه بدان دعوت مى‌كنيد بر مشركان دشوار است. خدا هر كه را خواهد براى رسالت خود بر مى‌گزيند و هر كه را بدو باز گردد به خود راه مى‌نمايد.'),
(4286, 42, 14, 'از روى حسد و عداوت فرقه فرقه نشدند، مگر از آن پس كه به دانش دست يافتند. و اگر پروردگار تو از پيش مقرر نكرده بود كه آنها را تا زمانى معين مهلت است، بر آنها حكم عذاب مى‌رفت. و كسانى كه بعد از ايشان وارث كتاب خدا شده‌اند درباره آن سخت به ترديد افتاده‌اند.'),
(4287, 42, 15, 'براى آن دعوت كن و چنان كه فرمان يافته‌اى پايدارى ورز و از پى خواهشهايشان مرو و بگو: به كتابى كه خدا نازل كرده است ايمان دارم و به من فرمان داده‌اند كه در ميان شما به عدالت رفتار كنم. خداى يكتا پروردگار ما و پروردگار شماست. اعمال ما از آن ما و اعمال شما از آن شما. ميان ما و شما هيچ محاجه‌اى نيست. خدا ما را در يك جا گرد مى‌آورد و سرانجام به سوى اوست.'),
(4288, 42, 16, 'و كسانى كه درباره خدا جدال مى‌كنند، پس از آنكه دعوت او را اجابت كرده‌اند نزد پروردگارشان حجتشان ناچيز است. بر آنهاست خشم خدا و بر آنهاست عذابى سخت.'),
(4289, 42, 17, 'خداست كه اين كتاب بر حق و ترازو را نازل كرده است. و تو چه مى‌دانى؟ شايد قيامت نزديك باشد.'),
(4290, 42, 18, 'آنان كه باورش ندارند آن را به شتاب مى‌طلبند، و آنان كه ايمان آورده‌اند از آن بيمناكند و مى‌دانند كه حق است. آگاه باش، كسانى كه درباره قيامت جدال مى‌كنند سخت در گمراهى هستند.'),
(4291, 42, 19, 'خدا با بندگانش مهربان است. هر كه را بخواهد روزى مى‌دهد. و او توانا و پيروزمند است.'),
(4292, 42, 20, 'هر كس كشت آخرت را بخواهد به كشته‌اش مى‌افزاييم و هر كس كشت دنيا را بخواهد به او عطا مى‌كنيم، ولى ديگر در كشت آخرتش نصيبى نيست.'),
(4293, 42, 21, 'آيا مشركان را بتانى است كه آيينى برايشان آورده‌اند كه خدا رخصت آن را نداده است؟ و اگر قصد تاخير عذابشان نبود، كارشان به پايان آمده بود و ستمكاران را عذابى است دردآور.'),
(4294, 42, 22, 'ستمكاران را بينى كه از حاصل اعمالشان بيمناكند. و البته به كيفر خود خواهند رسيد. ولى آنها كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، در باغهاى بهشتند. هر چه بخواهند نزد پروردگارشان هست. و اين فضل و بخشايش بزرگى است.'),
(4295, 42, 23, 'اين است آن چيزى كه خدا آن گروه از بندگانش را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند، بدان مژده مى‌دهد. بگو: بر اين رسالت مزدى از شما، جز دوست داشتن خويشاوندان، نمى‌خواهم. و هر كه كار نيكى كند به نيكويى‌اش مى‌افزاييم، زيرا خدا آمرزنده و شكرپذير است.'),
(4296, 42, 24, 'يا مى‌گويند كه بر خدا دروغ مى‌بندد. اگر خدا بخواهد بر دل تو مهر مى‌نهد. و خدا به كلمات خود باطل را محو مى‌كند و حق را ثابت مى‌گرداند. او به هر چه در دلها مى‌گذرد داناست.'),
(4297, 42, 25, 'و اوست كه توبه بندگانش را مى‌پذيرد و از گناهان عفوشان مى‌كند و هر چه مى‌كنيد مى‌داند.'),
(4298, 42, 26, 'و دعاى كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند اجابت مى‌كند، و از فضل خويش آنان را افزون مى‌دهد. و كافران را عذابى سخت است.'),
(4299, 42, 27, 'اگر خدا روزى بندگانش را افزون كند در زمين فساد مى‌كنند؛ ولى به اندازه‌اى كه بخواهد روزى مى‌فرستد. زيرا بر بندگان خود آگاه و بيناست.'),
(4300, 42, 28, 'و اوست آن خدايى كه بعد از نوميديشان باران مى‌فرستد و رحمت خود را به همه جا منتشر مى‌كند و اوست كارساز و ستودنى.'),
(4301, 42, 29, 'و از نشانه‌هاى قدرت او آفرينش آسمانها و زمين و پراكندن جنبندگان در آن دوست. و هرگاه بخواهد، بر گردآوردنشان تواناست.'),
(4302, 42, 30, 'اگر شما را مصيبتى رسد، به خاطر كارهايى است كه كرده‌ايد. و خدا بسيارى از گناهان را عفو مى‌كند.'),
(4303, 42, 31, 'شما نتوانيد در روى زمين از او بگريزيد و شما را جز او كارساز و ياورى نيست.'),
(4304, 42, 32, 'از نشانه‌هاى قدرت او كشتيهايى است چون كوه كه در درياها روانند.'),
(4305, 42, 33, 'اگر بخواهد، باد را نگه مى‌دارد تا كشتيها بر روى دريا از رفتن بازمانند. هر آينه در اين براى صابران شكرگزار عبرتهايى است.'),
(4306, 42, 34, 'يا آنها را به خاطر اعمالشان غرقه مى‌كند و بسيارى را نيز مى‌بخشايد.'),
(4307, 42, 35, 'و تا بدانند كسانى كه در آيات ما جدال مى‌كنند. آنان را هيچ گريزگاهى نيست.'),
(4308, 42, 36, 'آنچه شما را داده‌اند بهره اين زندگى دنيوى است. و آنچه در نزد خداست براى آنها كه ايمان آورده‌اند و به پروردگارشان توكل مى‌كنند، بهتر و پاينده‌تر است.'),
(4309, 42, 37, 'و آن كسان كه از گناهان بزرگ و زشتيها اجتناب مى‌كنند و چون در خشم شوند خطاها را مى‌بخشايند؛'),
(4310, 42, 38, 'و آنان كه دعوت پروردگارشان را پاسخ مى‌گويند و نماز مى‌گزارند و كارشان بر پايه مشورت با يكديگر است و از آنچه به آنها روزى داده‌ايم انفاق مى‌كنند؛'),
(4311, 42, 39, 'و آنان كه چون ستمى به آنها رسد انتقام مى‌گيرند.'),
(4312, 42, 40, 'جزاى هر بدى بديى است همانند آن. پس كسى كه عفو كند و آشتى ورزد مزدش با خداست، زيرا او ستمكاران را دوست ندارد.'),
(4313, 42, 41, 'بر كسانى كه پس از ستمى كه بر آنها رفته باشد انتقام مى‌گيرند، ملامتى نيست.'),
(4314, 42, 42, 'ملامت در خور كسانى است كه به مردم ستم مى‌كنند و به ناحق در روى زمين سركشى مى‌كنند. براى آنهاست عذابى دردآور.'),
(4315, 42, 43, 'و آن كه صبر كند و از خطا درگذرد، اين از كارهاى پسنديده است.'),
(4316, 42, 44, 'هر كس را كه خدا گمراه كند از آن پس هيچ دوستى نخواهد داشت. و ظالمان را مى‌بينى كه چون عذاب را بنگرند، مى‌گويند: آيا ما را راه بازگشتى هست؟'),
(4317, 42, 45, 'آنها را مى‌بينى كه به جهنم مى‌برند. ترسان و ذليل از گوشه چشم نگاهى دزديده مى‌كنند. كسانى كه ايمان آورده بودند مى‌گويند: اينان خود و كسانشان را در روز قيامت بر باد دادند. آگاه باش كه ستمكاران در عذاب دايم خواهند بود.'),
(4318, 42, 46, 'جز خدا يار و مددكارى ندارند و هر كس را كه خدا گمراه كند هيچ راهى برايش نيست.'),
(4319, 42, 47, 'پيش از آنكه روزى بيايد كه از جانب خدا بازگشتى ندارد، به پروردگارتان پاسخ قبول دهيد. در آن روز نه پناهى خواهيد داشت و نه كسى از شما دفاعى تواند كرد.'),
(4320, 42, 48, 'اگر اعراض كنند، تو را نفرستاده‌ايم كه نگهبانشان باشى. بر تو جز تبليغ رسالت هيچ نيست. و ما چون به انسان از رحمت خود بچشانيم شادمان مى‌گردد، و اگر به خاطر كارهايى كه كرده است ناروايى بدو رسد ناسپاسى مى‌كند.'),
(4321, 42, 49, 'از آن خداست فرمانروايى آسمانها و زمين. هر چه بخواهد مى‌آفريند. به هر كه بخواهد دختر مى‌بخشد و به هر كه بخواهد پسر مى‌بخشد.'),
(4322, 42, 50, 'و يا هم پسر دهد و هم دختر. و هر كس را بخواهد عقيم مى‌گرداند، زيرا او دانا و تواناست.'),
(4323, 42, 51, 'هيچ بشرى را نرسد كه خدا جز به وحى يا از آن سوى پرده، با او سخن گويد. يا فرشته‌اى مى‌فرستد تا به فرمان او هر چه بخواهد به او وحى كند. او بلند پايه و حكيم است.'),
(4324, 42, 52, 'همچنين كلام خود را به فرمان خود به تو وحى كرديم. تو نمى‌دانستى كتاب و ايمان چيست. ولى ما آن را نورى ساختيم تا هر يك از بندگانمان را كه بخواهيم بدان هدايت كنيم و تو به راه راست راه مى‌نمايى،'),
(4325, 42, 53, 'راه آن خدايى كه از آن اوست هر چه در آسمانها و زمين است. آگاه باشيد كه همه كارها به خدا باز مى‌گردد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حا، ميم.'),
(4327, 43, 2, 'سوگند به اين كتاب روشنگر.'),
(4328, 43, 3, 'ما اين كتاب را قرآنى عربى قرار داديم، باشد كه به عقل دريابيد.'),
(4329, 43, 4, 'و آن در ام‌الكتاب است در نزد ما؛ كتابى ارجمند و حكمت‌آميز.'),
(4330, 43, 5, 'آيا بدان سبب كه مردمى گزافكار هستيد، از شما اعراض كنيم و قرآن را از شما دريغ داريم؟'),
(4331, 43, 6, 'به ميان پيشينيان پيامبران بسيارى فرستاديم.'),
(4332, 43, 7, 'و هيچ پيامبرى بر آنها مبعوث نمى‌شد مگر آنكه مسخره‌اش مى‌كردند.'),
(4333, 43, 8, 'آنها را كه از ايشان نيرومندتر بودند هلاك كرديم. و داستان پيشينيان گذشت.'),
(4334, 43, 9, 'اگر از آنها بپرسى: چه كسى آسمانها و زمين را آفريده است؟ مى‌گويند: آنها را آن پيروزمند دانا آفريده است.'),
(4335, 43, 10, 'آن كه زمين را بستر شما ساخت و برايتان راه‌ها در آن پديد آورد، باشد كه هدايت شويد.'),
(4336, 43, 11, 'و آن كه از آسمان آب فرستاد، به اندازه. و بدان سرزمين مرده را زنده كرديم. شما نيز اينچنين از گورها بيرون آورده شويد.'),
(4337, 43, 12, 'و آن كه همه جفتها را بيافريد. و برايتان از كشتيها و چارپايان مركبها ساخت كه سوار شويد.'),
(4338, 43, 13, 'و چون بر پشت آنها قرار گرفتيد، نعمت پروردگارتان را ياد كنيد و بگوييد: منزه است آن كس كه اينها را رام ما كرد و گرنه ما را توان آن نبود.'),
(4339, 43, 14, 'و ما به سوى پروردگارمان باز مى‌گرديم.'),
(4340, 43, 15, 'و براى او از ميان بندگانش فرزندى قائل شدند. آدمى به آشكارا ناسپاس است.'),
(4341, 43, 16, 'آيا او از ميان مخلوقاتش براى خود دختران برگرفته و پسران را خاص شما كرده است؟'),
(4342, 43, 17, 'و چون به يكى از آنها مژده تولد همان چيزى را دهند كه به خداى رحمان نسبت داده رويش سياه گردد و آكنده از خشم شود.'),
(4343, 43, 18, 'آيا آن كه به آرايش پرورش يافته و در هنگام جدال آشكار نمى‌گردد از آن خداست؟'),
(4344, 43, 19, 'و فرشتگان را كه بندگان خدايند زن پنداشتند. آيا به هنگام خلقتشان آنجا حاضر بوده‌اند؟ زودا كه اين شهادتشان را مى‌نويسند و از آنها بازخواست مى‌شود.'),
(4345, 43, 20, 'گفتند: اگر خداى رحمان مى‌خواست ما فرشتگان را نمى‌پرستيديم. آنچه مى‌گويند از روى نادانى است و جز به دروغ سخن نمى‌گويند.'),
(4346, 43, 21, 'آيا پيش از قرآن كتابى برايشان نازل كرده‌ايم كه اكنون بدان تمسك مى‌جويند؟'),
(4347, 43, 22, 'نه، مى‌گويند: پدرانمان را بر آيينى يافتيم و ما از پى آنها مى‌رويم.'),
(4348, 43, 23, 'بدين سان، پيش از تو، به هيچ قريه‌اى بيم‌دهنده‌اى نفرستاديم مگر آنكه متنعمانش گفتند: پدرانمان را بر آيينى يافتيم و ما به اعمال آنها اقتدا مى‌كنيم.'),
(4349, 43, 24, 'گفت: حتى اگر براى شما چيزى بياورم كه از آنچه پدرانتان را بر آن يافته بوديد هدايت‌كننده‌تر باشد؟ گفتند: ما به آيينى كه شما را بدان فرستاده‌اند بى‌ايمانيم.'),
(4350, 43, 25, 'پس، از آنها انتقام گرفتيم. و بنگر كه عاقبت تكذيب‌كنندگان چگونه بوده است؟'),
(4351, 43, 26, 'و ابراهيم به پدر و قومش گفت: من از آنچه شما مى‌پرستيد بيزارم،'),
(4352, 43, 27, 'مگر آن كه مرا آفريده، كه او راهنماييم خواهد كرد.'),
(4353, 43, 28, 'و اين سخن را در فرزندان خود سخنى پاينده كرد، باشد كه به خدا باز گردند.'),
(4354, 43, 29, 'و من اينان و پدرانشان را از زندگى بهره‌مند كردم تا آنگاه كه حق و پيامبرى روشنگر به سويشان آمد.'),
(4355, 43, 30, 'چون حق بر آنها آشكار شد گفتند: اين جادوست و ما بدان ايمان نمى‌آوريم.'),
(4356, 43, 31, 'گفتند: چرا اين قرآن بر مردى از بزرگمردان آن دو قريه نازل نشده است؟'),
(4357, 43, 32, 'آيا آنان از رحمت پروردگارت را تقسيم مى‌كنند؟ حال آنكه ما روزى آنها را در زندگى دنيا ميانشان تقسيم مى‌كنيم. و بعضى را به مرتبت، بالاتر از بعضى ديگر قرار داده‌ايم تا بعضى بعض ديگر را به خدمت گيرند. و رحمت پروردگارت از آنچه آنها گرد مى‌آورند بهتر است.'),
(4358, 43, 33, 'و اگر نه آن بود كه همه مردم يك امت مى‌شدند، سقفهاى خانه‌هاى كسانى را كه خداى رحمان را باور ندارند از نقره مى‌كرديم و بر آنها نردبامهايى مى‌نهاديم تا بر آن بالا روند.'),
(4359, 43, 34, 'و براى خانه‌هايشان نيز درهايى از نقره مى‌كرديم و تختهايى كه بر آن تكيه زنند.'),
(4360, 43, 35, 'و از هر گونه زينت. و همه اينها برخوردارى ناچيز دنيوى است، حال آنكه آخرت در نزد پروردگار تو براى پرهيزگاران است.'),
(4361, 43, 36, 'هر كس كه از ياد خداى رحمان روى گرداند، شيطانى بر او مى‌گماريم كه همواره همراهش باشد.'),
(4362, 43, 37, 'و آن شيطانها آنان را از راه خدا باز مى‌گردانند، ولى پندارند كه هدايت‌يافتگانند.'),
(4363, 43, 38, 'تا آنگاه كه نزد ما آيد، مى‌گويد: اى كاش دورى من و تو دورى مشرق و مغرب بود. و تو چه همراه بدى بودى.'),
(4364, 43, 39, 'چون ستم كرديد آن روز پشيمانى سود نكند و هر دو در عذاب شريك باشيد.'),
(4365, 43, 40, 'آيا تو مى‌خواهى به كران سخن بشنوانى، يا كوران و آنهايى را كه در گمراهى آشكار هستند راه بنمايى؟'),
(4366, 43, 41, 'و اگر تو را ببريم، از آنها انتقام مى‌گيريم،'),
(4367, 43, 42, 'يا آن عذابى را كه به آنها وعده داده‌ايم به تو نشان مى‌دهيم، كه ما بر ايشان توانا هستيم.'),
(4368, 43, 43, 'پس در آنچه به تو وحى شده است چنگ بزن كه تو بر راه راست هستى.'),
(4369, 43, 44, 'و قرآن سبب بلندآوازه گشتن تو و قوم توست، و زودا كه بازخواست شويد.'),
(4370, 43, 45, 'از پيامبران ما كه پيش از تو فرستاده‌ايم بپرس: آيا جز خداى رحمان، ديگرى را براى پرستش آنها قرار داده بوديم؟'),
(4371, 43, 46, 'و هر آينه موسى را همراه با آيات خود بر فرعون و مهتران قومش فرستاديم و گفت: من فرستاده پروردگار جهانيانم.'),
(4372, 43, 47, 'چون آيات ما را بر آنان عرضه داشت، به ناگاه همه از آن به خنده افتادند.'),
(4373, 43, 48, 'و هر معجزه‌اى كه به آنها نشان داديم از معجزه ديگر عظيم‌تر بود. آنگاه همه را به عذاب گرفتار كرديم، باشد كه بازگردند.'),
(4374, 43, 49, 'گفتند: اى جادوگر، پروردگارت را با آن عهدى كه با تو نهاده است براى ما بخوان كه ما هدايت‌شدگانيم.'),
(4375, 43, 50, 'چون عذاب را از آنها برداشتيم، پيمان خود را شكستند.'),
(4376, 43, 51, 'فرعون در ميان مردمش ندا داد كه: اى قوم من، آيا پادشاهى مصر و اين جويباران كه از زير پاى من جارى هستند از آن من نيستند؟ آيا نمى‌بينيد؟'),
(4377, 43, 52, 'آيا من بهترم يا اين مرد خوار ذليل كه درست سخن‌گفتن نتواند؟'),
(4378, 43, 53, 'چرا دستهايش را به دستبندهاى طلا نياراسته‌اند؟ و چرا گروهى از فرشتگان همراهش نيامده‌اند؟'),
(4379, 43, 54, 'پس قوم خود را گمراه ساخت تا از او اطاعت كردند، كه مردمى تبهكار بودند.'),
(4380, 43, 55, 'چون ما را به خشم آوردند، از آنها انتقام گرفتيم و همگان را غرقه ساختيم.'),
(4381, 43, 56, 'آنان را در شمار گذشتگان و داستان براى آيندگان كرديم.'),
(4382, 43, 57, 'و چون داستان پسر مريم آورده شد، قوم تو به شادمانى فرياد زدند،'),
(4383, 43, 58, 'و گفتند: آيا خدايان ما بهترند يا او؟ و اين سخن را جز براى جدال با تو نگفتند، كه مردمى ستيزه‌جويند.'),
(4384, 43, 59, 'او جز بنده‌اى نبود كه ما نعمتش ارزانى داشتيم و مايه عبرت بنى‌اسرائيلش گردانيديم.'),
(4385, 43, 60, 'اگر مى‌خواستيم به جاى شما فرشتگانى پديد مى‌آورديم تا در روى زمين جانشين شما گردند.'),
(4386, 43, 61, 'و هر آينه او نشانه فرارسيدن قيامت است. در آن شك مكنيد، و از من متابعت كنيد. اين است راه راست.'),
(4387, 43, 62, 'شيطان شما را از راه باز نگرداند، زيرا او دشمن آشكار شماست.'),
(4388, 43, 63, 'و چون عيسى با دليلهاى روشن خود آمد، گفت: برايتان حكمت آورده‌ام و آمده‌ام تا چيزهايى را كه در آن اختلاف مى‌كنيد بيان كنم. پس، از خدا بترسيد و از من اطاعت كنيد.'),
(4389, 43, 64, 'خداى يكتا پروردگار من و پروردگار شماست. او را بپرستيد. راه راست اين است.'),
(4390, 43, 65, 'گروه‌ها با هم اختلاف كردند. پس واى بر ستمكاران از عذاب دردآور قيامت.'),
(4391, 43, 66, 'آيا چشم به راه چيزى جز قيامتند كه ناگاه و بى‌خبرشان بيايد؟'),
(4392, 43, 67, 'در آن روز دوستان -غير از پرهيزگاران- دشمن يكديگرند.'),
(4393, 43, 68, 'اى بندگان من، در آن روز بيمى بر شما نيست و شما غمگين نمى‌شويد.'),
(4394, 43, 69, 'آن كسان كه به آيات ما ايمان آورده‌اند و تسليم امر ما شده‌اند،'),
(4395, 43, 70, 'شما و جفتهايتان با شادكامى به بهشت داخل شويد.'),
(4396, 43, 71, 'قدحهاى زرين و سبوها را در ميانشان به گردش مى‌آورند. در آنجاست هر چه نفس آرزو كند و ديده از آن لذت ببرد. و در آنجا جاودانه خواهيد بود.'),
(4397, 43, 72, 'اين بهشتى است كه به پاداش كارهايى كه كرده‌ايد به ميراثش مى‌بريد.'),
(4398, 43, 73, 'در آنجا برايتان ميوه‌هاى بسيار هست كه از آنها مى‌خوريد.'),
(4399, 43, 74, 'گناهكاران در عذاب جهنم جاويدانند.'),
(4400, 43, 75, 'عذابشان كاهش نمى‌يابد و آنها از نوميدى خاموش باشند.'),
(4401, 43, 76, 'ما به آنها ستمى نكرده‌ايم؛ آنها خود به خويشتن ستم كرده‌اند.'),
(4402, 43, 77, 'فرياد برآورند كه: اى مالك، كاش پروردگار تو ما را بميراند. مى‌گويد: نه، شما در اينجا ماندنى هستيد.'),
(4403, 43, 78, 'ما شما را با حق آشنا كرديم ولى بيشترتان از حق كراهت داشتيد.'),
(4404, 43, 79, 'آيا آنها در اعتقاد خويش پاى فشرده‌اند؟ ما هم پاى فشرده‌ايم.'),
(4405, 43, 80, 'آيا مى‌پندارند ما راز و نجوايشان را نمى‌شنويم؟ آرى، رسولان ما نزد آنها هستند و مى‌نويسند.'),
(4406, 43, 81, 'بگو: اگر خداى رحمان را فرزندى مى‌بود، من از نخستين پرستندگان مى‌بودم.'),
(4407, 43, 82, 'منزه است پروردگار آسمانها و زمين و پروردگار عرش از آن نسبتها كه به او مى‌دهند.'),
(4408, 43, 83, 'پس رهايشان كن تا به همان سخنان باطل مشغول باشند و به بازيچه سرگرم، تا آن روزى كه آنها را وعده داده‌اند برسد.'),
(4409, 43, 84, 'اوست كه هم در آسمان خداست و هم در زمين خداست و حكيم و داناست.'),
(4410, 43, 85, 'برتر و بزرگوارتر است آن كس كه فرمانروايى آسمانها و زمين و هر چه در ميان آنهاست از آن اوست و علم فرارسيدن قيامت نزد اوست و به او بازگردانده مى‌شويد.'),
(4411, 43, 86, 'كسانى سواى او را كه به خدايى مى‌خوانند قادر به شفاعت كسى نيستند. مگر كسانى كه از روى علم، به حق شهادت داده باشند.'),
(4412, 43, 87, 'اگر از ايشان بپرسى كه چه كسى آنها را آفريده است، مى‌گويند: الله. پس چرا از حق روى مى‌گردانند؟'),
(4413, 43, 88, 'گفتارش اين بود: اى پروردگار من، اينان مردمى هستند كه ايمان نمى‌آورند.'),
(4414, 43, 89, 'پس، از آنها درگذر و بگو: ايمنى است شما را. آرى به زودى خواهند دانست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حا، ميم.'),
(4416, 44, 2, 'سوگند به اين كتاب روشنگر.'),
(4417, 44, 3, 'ما آن را در مبارك شبى نازل كرديم. ما بيم‌دهنده بوده‌ايم.'),
(4418, 44, 4, 'در آن شب هر فرمانى بر حسب حكمت صادر مى‌شود،'),
(4419, 44, 5, 'فرمانى از جانب ما. و ما همواره فرستنده آن بوده‌ايم.'),
(4420, 44, 6, 'رحمتى است از جانب پروردگارت، و هر آينه او شنوا و داناست.'),
(4421, 44, 7, 'اگر به يقين دريابيد، اوست پروردگار آسمانها و زمين و هر چه ميان آنهاست.'),
(4422, 44, 8, 'نيست خدايى به جز او. زنده مى‌كند و مى‌ميراند. پروردگار شما و پروردگار نياكان شماست.'),
(4423, 44, 9, 'نه، آنها همچنان به شك خويش دلخوشند.'),
(4424, 44, 10, 'چشم انتظار روزى باش كه آسمان به آشكارا دود بياورد.'),
(4425, 44, 11, 'و آن دود همه مردم را در خود فروپوشد. و اين عذابى است دردآور.'),
(4426, 44, 12, 'اى پروردگار ما، اين عذاب را از ما دور گردان كه ما ايمان آورده‌ايم.'),
(4427, 44, 13, 'كجا پند مى‌پذيرند؟ حال آنكه چون پيامبر روشنگر بر آنها مبعوث شد،'),
(4428, 44, 14, 'از او رويگردان شدند و گفتند: ديوانه‌اى است تعليم يافته.'),
(4429, 44, 15, 'عذاب را اندكى بر مى‌داريم و شما باز به آيين خويش باز مى‌گرديد.'),
(4430, 44, 16, 'روزى آنها را به صولتى سخت فروگيريم، كه ما انتقام‌گيرنده‌ايم.'),
(4431, 44, 17, 'پيش از آنها قوم فرعون را آزموديم و پيامبرى بزرگوار نزدشان آمد،'),
(4432, 44, 18, 'كه بندگان خدا را به من تسليم كنيد كه من پيامبرى امينم،'),
(4433, 44, 19, 'و نيز بر خدا برترى مجوييد، كه من با حجتى روشن نزد شما آمده‌ام،'),
(4434, 44, 20, 'و اگر بخواهيد بر من سنگ بزنيد، من به پروردگار خود و پروردگار شما پناه مى‌برم،'),
(4435, 44, 21, 'و اگر به من ايمان نمى‌آوريد، از من كنار گيريد.'),
(4436, 44, 22, 'پس پروردگارش را خواند كه: اينان مردمى مجرمند.'),
(4437, 44, 23, 'بندگان مرا شب‌هنگام روانه كن تا از پى شما بيايند.'),
(4438, 44, 24, 'دريا را آرام پشت سر گذار، كه آن سپاه غرق‌شدگانند.'),
(4439, 44, 25, 'بعد از خود چه باغها و چشمه‌سارها بر جاى گذاشتند،'),
(4440, 44, 26, 'و كشتزارها و خانه‌هاى نيكو،'),
(4441, 44, 27, 'و نعمتى كه در آن غرق شادمانى بودند.'),
(4442, 44, 28, 'بدين‌سان بودند و ما آن نعمتها را به مردمى ديگر واگذاشتيم.'),
(4443, 44, 29, 'نه آسمان بر آنها گريست و نه زمين و نه به آنها مهلت داده شد.'),
(4444, 44, 30, 'و ما بنى‌اسرائيل را از آن عذاب خواركننده رهانيديم:'),
(4445, 44, 31, 'از فرعون برترى‌جوى گزافكار،'),
(4446, 44, 32, 'و از روى علم بر اهل جهانشان برگزيديم.'),
(4447, 44, 33, 'و آياتى به آنها عطا كرديم كه در آن امتحانى آشكار بود.'),
(4448, 44, 34, 'هر آينه اينان مى‌گويند:'),
(4449, 44, 35, 'پايان كار جز همين مرگ نخستين نيست و ما ديگربار زنده نمى‌شويم؛'),
(4450, 44, 36, 'و اگر راست مى‌گوييد، پدران ما را به جهان باز آوريد.'),
(4451, 44, 37, 'آيا اينان بهترند يا قوم تُبّع و كسانى كه پيش از آن قوم بودند؟ همه را هلاك كرديم كه مجرمان بودند.'),
(4452, 44, 38, 'ما اين آسمانها و زمين و آنچه را ميان آنهاست به بازيچه نيافريده‌ايم.'),
(4453, 44, 39, 'آنها را به حق آفريده‌ايم، ولى بيشترين نمى‌دانند.'),
(4454, 44, 40, 'وعده‌گاه همه در روز داورى، قيامت است.'),
(4455, 44, 41, 'روزى كه هيچ دوستى براى دوست خود سودمند نباشد و از سوى كسى يارى نشوند.'),
(4456, 44, 42, 'مگر كسى كه خدا بر او ببخشايد، زيرا اوست پيروزمند و مهربان.'),
(4457, 44, 43, 'هر آينه درخت زقّوم،'),
(4458, 44, 44, 'طعام گناهكاران است.'),
(4459, 44, 45, 'همانند مس گداخته در شكمها مى‌جوشد.'),
(4460, 44, 46, 'همانند جوشيدن آب جوشان.'),
(4461, 44, 47, 'بگيريدش و به سختى به ميان جهنمش بكشيد.'),
(4462, 44, 48, 'و بر سرش آب جوشان بريزيد تا شكنجه شود.'),
(4463, 44, 49, 'بچش، كه تو پيروزمند و بزرگوارى.'),
(4464, 44, 50, 'اين همان چيزى است كه در آن شك مى‌كرديد.'),
(4465, 44, 51, 'پرهيزگاران در جاى امنى هستند،'),
(4466, 44, 52, 'در باغها و چشمه‌سارها،'),
(4467, 44, 53, 'لباسهايى از سندس و استبرق مى‌پوشند و روبه‌روى هم مى‌نشينند.'),
(4468, 44, 54, 'همچنين حورالعين را به همسريشان درآوريم.'),
(4469, 44, 55, 'در ايمنى هر ميوه‌اى را كه بخواهند مى‌طلبند.'),
(4470, 44, 56, 'در آنجا طعم مرگ را نمى‌چشند، مگر همان مرگ نخستين. و آنها را خدا از عذاب جهنم نگه داشته است.'),
(4471, 44, 57, 'اين بخششى است از جانب پروردگارت، و اين پيروزى بزرگى است.'),
(4472, 44, 58, 'ما اداى سخن خويش بر زبان تو آسان كرديم، باشد كه پند گيرند،'),
(4473, 44, 59, 'تو منتظر باش كه ايشان نيز منتظرند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حا، ميم.'),
(4475, 45, 2, 'نازل شدن اين كتاب از جانب خداى پيروزمند و حكيم است.'),
(4476, 45, 3, 'هر آينه در آسمانها و زمين نشانه‌هاى عبرتى است براى مؤمنان.'),
(4477, 45, 4, 'و در آفرينش شما و پراكنده‌شدن جنبندگان عبرتهاست براى اهل يقين.'),
(4478, 45, 5, 'آمد و شد شب و روز و رزقى كه خدا از آسمان مى‌فرستد و زمين مرده را بدان زنده مى‌كند و نيز در وزش بادها عبرتهاست براى عاقلان.'),
(4479, 45, 6, 'اينها آيات خداست كه به راستى بر تو تلاوت مى‌كنيم. جز خدا و آياتش به كدام سخن ايمان مى‌آورند؟'),
(4480, 45, 7, 'واى بر هر دروغپرداز گناهكارى!'),
(4481, 45, 8, 'آيات خدا را كه بر او خوانده مى‌شود مى‌شنود. آنگاه به گردنكشى پاى مى‌فشرد، چنان كه گويى هيچ نشنيده است. پس به عذابى دردآورش بشارت ده.'),
(4482, 45, 9, 'چون چيزى از آيات ما را فراگيرد، به مسخره‌اش مى‌گيرد. اينان در خور عذابى خواركننده‌اند.'),
(4483, 45, 10, 'پيش رويشان جهنم است. و مالى كه به دست آورده‌اند و كسانى كه سواى خداى يكتا به خدايى گرفته‌اند به حالشان سود نكند. ايشان راست عذابى بزرگ.'),
(4484, 45, 11, 'اين رهنمودى است. و آنان كه به آيات پروردگارشان ايمان نمى‌آورند، برايشان عذابى است از سخت‌ترين عذابهاى دردآور.'),
(4485, 45, 12, 'خداست كه دريا را رام كرد تا در آن به فرمان او كشتيها روان باشند، و طلب معيشت كنيد، باشد كه سپاسگزار باشيد.'),
(4486, 45, 13, 'رام شما ساخت آنچه در آسمانهاست و آنچه در زمين است. همه از آن اوست. در اين براى متفكران عبرتهاست.'),
(4487, 45, 14, 'به كسانى كه ايمان آورده‌اند، بگو: از خطاى كسانى كه به روزهاى خدا باور ندارند درگذريد، تا خود، آن مردم را به جزاى اعمالى كه مرتكب شده‌اند پاداش دهد.'),
(4488, 45, 15, 'هر كس كار شايسته‌اى كند، به سود خود اوست؛ و هر كس مرتكب كار بدى شود، به زيان اوست. سپس همه به سوى پروردگارتان بازگردانده مى‌شويد.'),
(4489, 45, 16, 'ما به بنى‌اسرائيل كتاب و علم داورى و نبوت عطا كرديم و از چيزهاى پاكيزه و خوش روزيشان كرديم. و بر جهانيان برتريشان داديم.'),
(4490, 45, 17, 'و آنها را در باره آن امر دلايلى روشن داديم. و در آن از روى حسد و كينه اختلاف نكردند، مگر آنگاه كه دانش يافتند. خدا در روز قيامت در آنچه اختلاف مى‌كردند داورى خواهد كرد.'),
(4491, 45, 18, 'پس تو را به راه دين انداختيم. از آن راه برو، و از پى خواهش نادانان مرو.'),
(4492, 45, 19, 'اينان تو را هيچ از خدا بى‌نياز نمى‌كنند و ظالمان دوستداران يكديگرند، و خدا دوستدار پرهيزگاران است.'),
(4493, 45, 20, 'اين قرآن مايه بصيرت مردم و هدايت و رحمتى است براى اهل يقين.'),
(4494, 45, 21, 'آيا آنان كه مرتكب بديها مى‌شوند مى‌پندارند كه در شمار كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند قرارشان مى‌دهيم؟ و آيا زندگى و مرگشان يكسان است؟ چه بد داورى مى‌كنند.'),
(4495, 45, 22, 'و خدا آسمانها و زمين را به حق بيافريد تا هر كسى را برابر كارى كه كرده است پاداش دهد و به آنها ستم نشود.'),
(4496, 45, 23, 'آيا آن كس را كه هوسش را چون خداى خود گرفت و خدا از روى علم گمراهش كرد و بر گوش و دلش مُهر نهاد و بر ديدگانش پرده افكند، ديده‌اى؟ اگر خدا هدايت نكند چه كسى او را هدايت خواهد كرد؟ چرا پند نمى‌گيريد؟'),
(4497, 45, 24, 'و گفتند: جز زندگى دنيوى ما هيچ نيست. مى‌ميريم و زنده مى‌شويم و ما را جز دهر هلاك نكند. آنان را بدان دانشى نيست و جز در پندارى نيستند.'),
(4498, 45, 25, 'و چون آيات روشنگر ما بر آنها تلاوت شود، حجتشان جز اين نيست كه مى‌گويند: اگر راست مى‌گوييد، پدران ما را زنده كنيد.'),
(4499, 45, 26, 'بگو: خداست كه شما را زنده مى‌كند، پس مى‌ميراند و سپس همه را در روز قيامت -كه شكى در آن نيست- گردمى‌آورد. ولى بيشتر مردم نمى‌دانند.'),
(4500, 45, 27, 'از آنِ خداوند است فرمانروايى آسمان و زمين. و آن روز كه قيامت بر پا شود، اهل باطل زيان خواهند كرد.'),
(4501, 45, 28, 'هر امتى را ببينى كه به زانو درآمده است، و هر امتى را براى گرفتن نامه اعمالش بخوانند. در چنين روزى در برابر اعمالى كه كرده‌ايد پاداش مى‌بينيد.'),
(4502, 45, 29, 'اين نوشته ماست كه به حق سخن مى‌گويد، زيرا ما كارهايى را كه مى‌كرده‌ايد مى‌نوشته‌ايم.'),
(4503, 45, 30, 'اما كسانى را كه ايمان آورده‌اند و كارهاى شايسته مى‌كنند، پروردگارشان داخل در رحمت خويش مى‌كند، و اين كاميابى آشكار است.'),
(4504, 45, 31, 'اما اى آنان كه ايمان نياورده‌ايد، مگر نه آنكه آيات ما بر شما خوانده مى‌شد و شما تكبر مى‌ورزيديد و مردمى گناهكار بوديد؟'),
(4505, 45, 32, 'چون گفته مى‌شد كه وعده خدا حق است و در قيامت ترديدى نيست، مى‌گفتيد: ما نمى‌دانيم قيامت چيست؟ جز گمانى نمى‌بريم و به يقين نرسيده‌ايم.'),
(4506, 45, 33, 'اعمال زشتشان در برابرشان آشكار شد و آن چيزى كه مسخره‌اش مى‌كردند گرد بر گردشان را بگرفت.'),
(4507, 45, 34, 'به آنها گفته شود: امروز فراموشتان مى‌كنيم، همچنان كه شما ديدار چنين روزتان را فراموش كرده بوديد. جايگاهتان در آتش است و شما را هيچ ياورى نيست.'),
(4508, 45, 35, 'و اين به كيفر آن است كه آيات خدا را به مسخره مى‌گرفتيد و زندگى دنيوى شما را بفريفت. پس امروز از اين آتش كسى بيرونشان نبرد و كسى عذرشان را نپذيرد.'),
(4509, 45, 36, 'ستايش از آنِ خداست، پروردگار آسمانها و پروردگار زمين و پروردگار جهانيان.'),
(4510, 45, 37, 'بزرگوارى در آسمانها و زمين خاص اوست، و اوست پيروزمند و حكيم.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حا، ميم.'),
(4512, 46, 2, 'نازل شدن اين كتاب از جانب خداى پيروزمند و حكيم است.'),
(4513, 46, 3, 'ما آسمانها و زمين و آنچه را كه در ميان آن دوست جز به حق و در مدتى معين نيافريده‌ايم. و كافران از آنچه بيمشان مى‌دهند اعراض مى‌كنند.'),
(4514, 46, 4, 'بگو: از آنهايى كه جُز اللّه به خدايى مى‌خوانيد خبر دهيد. به من نشان دهيد كه از اين زمين چه چيز را آفريده‌اند؟ يا در خلقت آسمانها شركت داشته‌اند؟ اگر راست مى‌گوييد، براى من كتابى كه پيش از اين قرآن آمده باشد يا اگر دانشى از پيشينيان مانده است، بياوريد.'),
(4515, 46, 5, 'و كيست گمراه‌تر از آن كه به جز اللّه چيزى را به خدايى مى‌خواند كه تا روز قيامت به او جواب نمى‌دهد، كه بتان از دعاى بت پرستان بى‌خبرند.'),
(4516, 46, 6, 'و چون در قيامت مردم را گردآرند، بتان با پرستندگان خويش دشمن باشند و عبادتشان را انكار كنند.'),
(4517, 46, 7, 'چون آيات ما به روشنى بر آنان خوانده شود، كافران حقيقتى را كه بر ايشان نازل شده است، گويند: جادويى آشكار است.'),
(4518, 46, 8, 'يا مى‌گويند: اين كتاب، دروغى است كه خود بافته است. بگو: اگر من آن را چون دروغى به هم بافته باشم، شما نمى‌توانيد خشم خدا را از من بازداريد. خدا از آن طعنها كه بدان مى‌زنيد آگاه‌تر است. و شهادت او ميان من و شما كافى است. و اوست آمرزنده مهربان.'),
(4519, 46, 9, 'بگو: من از ميان ديگر پيامبران بدعتى تازه نيستم و نمى‌دانم كه بر من، يا بر شما چه خواهد رفت. من از چيزى جز آنچه به من وحى مى‌شود، پيروى نمى‌كنم و من جز بيم‌دهنده‌اى آشكار نيستم.'),
(4520, 46, 10, 'بگو: چه مى‌كنيد اگر قرآن از جانب خدا باشد و شما بدان ايمان نياوريد؟ يكى از بنى‌اسرائيل بدان شهادت داد و ايمان آورد. ولى شما گردنكشى مى‌كنيد. خدا مردم ستمكار را هدايت نمى‌كند.'),
(4521, 46, 11, 'كافران مؤمنان را گفتند: اگر در آن خيرى مى‌بود، اينان در پذيرفتنش بر ما سبقت نمى‌گرفتند. و چون بدان راه نيافته‌اند خواهند گفت كه اين دروغى ديرينه است.'),
(4522, 46, 12, 'و پيش از آن كتاب موسى راهنما و رحمت بود. و اين، كتابى است تصديق‌كننده آن، به زبان عربى، تا ستمكاران را بيمى و نيكوكاران را مژده‌اى باشد.'),
(4523, 46, 13, 'هر آينه آنان كه گفتند كه پروردگار ما اللّه است و سپس پايدارى ورزيدند، بيمى بر آنها نيست و اندوهگين نمى‌شوند.'),
(4524, 46, 14, 'اينان به پاداش اعمالشان اهل بهشتند و در آنجا جاودانه‌اند.'),
(4525, 46, 15, 'آدمى را به نيكى كردن با پدر و مادر خود سفارش كرديم. مادرش بار او را به دشوارى برداشت و به دشوارى بر زمين نهاد. و مدت حمل تا از شير بازگرفتنش سى ماه است تا چون به سن جوانى رسد و به چهل سالگى درآيد، گويد: اى پروردگار من، به من بياموز تا شكر نعمتى كه بر من و بر پدر و مادرم ارزانى داشته‌اى به جاى آرم. كارى شايسته بكنم كه تو از آن خشنود شوى و فرزندان مرا به صلاح آور. من به تو بازگشتم و از تسليم‌شدگانم.'),
(4526, 46, 16, 'اينان كسانى هستند كه كارهاى نيكشان را مى‌پذيريم و از گناهشان درمى‌گذريم. در زمره اهل بهشتند. هر وعده‌اى كه به آنها داده شده راست است.'),
(4527, 46, 17, 'و آن كه به پدر و مادرش گفت: اف بر شما، آيا به من وعده مى‌دهيد كه از گورم برخيزانند و حال آنكه مردمى پيش از من بوده‌اند كه برنخاسته‌اند؟ و آن دو به درگاه خدا استغاثه مى‌كنند و گويند: واى بر تو ايمان بياور كه وعده خدا حق است. مى‌گويد: اينها چيزى جز همان افسانه پيشينيان نيست.'),
(4528, 46, 18, 'در باره اينان همان سخن كه در باره امتهاى پيشين از جن و انس گفته شده بود به حقيقت مى‌پيوندد. اينان زيان‌كنندگانند.'),
(4529, 46, 19, 'و هر يك را نسبت به كارى كه كرده است درجتى است تا خدا پاداش كارهايشان را به تمامى بدهد و به آنان ستم نمى‌شود.'),
(4530, 46, 20, 'و روزى كه كافران را بر آتش عرضه كنند: در زندگى دنيوى از چيزهاى پاكيزه و خوش بهره‌مند شديد، امروز به عذاب خوارى پاداشتان مى‌دهند. و اين بدان سبب است كه در زمين بى هيچ حقى گردنكشى مى‌كرديد و عصيانگرى پيش گرفته بوديد.'),
(4531, 46, 21, 'برادر قوم عاد را به ياد بياور كه چون قوم خود را در احقاف بيم داد -و پيش از او پيامبرانى بودند و رفتند و پس از او پيامبرانى آمدند- كه جز خداى يكتا را نپرستيد كه من از عذاب روزى بزرگ بر شما بيمناكم.'),
(4532, 46, 22, 'گفتند: آيا آمده‌اى تا ما را از خدايانمان رويگردان سازى؟ اگر راست مى‌گويى، هر چه به ما وعده داده‌اى بياور.'),
(4533, 46, 23, 'گفت: اين را خدا مى‌داند و من آنچه را بدان مبعوث شده‌ام به شما مى‌رسانم. ولى مى‌بينم كه مردمى نادان هستيد.'),
(4534, 46, 24, 'چون ابرى ديدند كه از جانب رودخانه‌هاشان مى‌آيد، گفتند: اين ابرى باران‌زاست. نه، اين همان چيزى است كه آن را به شتاب مى‌طلبيديد. باد است و در آن باد عذابى دردآور،'),
(4535, 46, 25, 'به فرمان پروردگارش همه چيز را هلاك مى‌كند. چنان شدند كه اكنون جز خانه‌هاشان را نبينى. و ما مجرمان را اين سان پاداش مى‌دهيم.'),
(4536, 46, 26, 'به آنها چنان مكانتى داده بوديم كه به شما نداده‌ايم. برايشان گوش و چشم و دل قرار داديم. ولى گوش و چشم و دلشان به حالشان هيچ سود نكرد، زيرا آيات خدا را انكار مى‌كردند تا آنچه به مسخره‌اش مى‌گرفتند آنها را فروگرفت.'),
(4537, 46, 27, 'ما همه قريه‌هايى را كه اطراف شما بوده‌اند هلاك كرده‌ايم و آيات را گونه‌گون بيان كرديم، باشد كه بازگردند.'),
(4538, 46, 28, 'از چه روى آن خدايانى كه سواى اللّه براى تقرب، به خدايى گرفته بودند ياريشان نكردند، بلكه از نظرشان گم شدند؟ اين است دروغ و افترايشان.'),
(4539, 46, 29, 'و گروهى از جن را نزد تو روانه كرديم تا قرآن را بشنوند. چون به حضرتش رسيدند گفتند: گوش فرادهيد. چون به پايان آمد، همانند بيم‌دهندگانى نزد قوم خود بازگشتند.'),
(4540, 46, 30, 'گفتند: اى قوم ما، ما كتابى شنيديم كه بعد از موسى نازل شده، كتابهاى پيشين را تصديق مى‌كند و به حق و راه راست راه مى‌نمايد.'),
(4541, 46, 31, 'اى قوم ما، اين دعوت‌كننده به خدا را پاسخ گوييد و به او ايمان بياوريد تا خدا گناهانتان را بيامرزد و شما را از عذابى دردآور در امان دارد.'),
(4542, 46, 32, 'و هر كس كه به اين دعوت‌كننده جواب نگويد نمى‌تواند در روى زمين از خداى بگريزد و او را جز خدا هيچ ياورى نيست و در گمراهى آشكارى است.'),
(4543, 46, 33, 'آيا نمى‌بينند كه خداى يكتاست آن كه آسمانها و زمين را بيافريد و در آفريدن آنها در نماند. او مى‌تواند مردگان را زنده كند. بلى او بر هر كارى تواناست.'),
(4544, 46, 34, 'روزى كه كافران به آتش عرضه شوند: آيا اين حقيقت نيست؟ گويند: بلى، به پروردگارمان سوگند. گويد: به خاطر كفرى كه مى‌ورزيده‌ايد، اينك عذاب را بچشيد.'),
(4545, 46, 35, 'پس پايدارى كن، همچنان كه پيامبران اُولوالعزم پايدارى كرده بودند. و در عقوبتشان شتاب مكن. آن روز كه آن وعده‌اى را كه به آنها داده شده بنگرند، پندارند كه جز به قدر ساعتى از روز در گور درنگ نكرده‌اند. اين تبليغ رسالت است. آيا جز نافرمانان به هلاكت مى‌رسند؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'خدا اعمال كسانى را كه كافر شدند و مردم را از راه خدا باز داشتند باطل ساخته است.'),
(4547, 47, 2, 'خدا گناه كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند و بدانچه بر محمد نازل شده -كه آن حق است و از جانب پروردگارشان- ايمان آورده‌اند، از آنها زدود و كارهايشان را به صلاح آورد.'),
(4548, 47, 3, 'و اين بدان سبب است كه كافران از باطل پيروى كردند و آنان كه ايمان آوردند از حقى كه از جانب پروردگارشان آمده بود، پيروى كردند. خدا براى مردم اينچنين مثَل مى‌زند.'),
(4549, 47, 4, 'چون با كافران روبرو شديد، گردنشان را بزنيد. و چون آنها را سخت فرو كوفتيد، اسيرشان كنيد و سخت ببنديد. آنگاه يا به منت آزاد كنيد يا به فديه. تا آنگاه كه جنگ به پايان آيد. و اين است حكم خدا. و اگر خدا مى‌خواست، از آنان انتقام مى‌گرفت، ولى خواست تا شما را به يكديگر بيازمايد. و آنان كه در راه خدا كشته شده‌اند اعمالشان را باطل نمى‌كند.'),
(4550, 47, 5, 'زودا كه هدايتشان كند و كارهايشان را به صلاح آورد.'),
(4551, 47, 6, 'و به بهشتى كه برايشان وصف كرده است داخلشان سازد.'),
(4552, 47, 7, 'اى كسانى كه ايمان آورده‌ايد، اگر خدا را يارى كنيد، شما را يارى خواهد كرد و پايدارى خواهد بخشيد.'),
(4553, 47, 8, 'هلاكت و شوربختى باد بر كافران. خدا اعمالشان را باطل كرده است.'),
(4554, 47, 9, 'زيرا آنان چيزى را كه خدا نازل كرده است ناخوش دارند. خدا نيز اعمالشان را نابود كرد.'),
(4555, 47, 10, 'آيا در زمين سير نكرده‌اند تا بنگرند كه عاقبت كسانى كه پيش از آنها بوده‌اند چگونه بوده است؟ خدا هلاكشان كرد و كافران نيز عاقبتى آنچنان خواهند داشت.'),
(4556, 47, 11, 'اين بدان سبب است كه خدا ياور كسانى است كه ايمان آورده‌اند. و كافران را هيچ ياورى نيست.'),
(4557, 47, 12, 'خدا كسانى را كه ايمان آورده‌اند و كارهاى شايسته مى‌كنند، به بهشتهايى كه نهرها در آن جارى است داخل خواهد كرد. ولى كافران از اين جهان متمتع مى‌شوند و چون چارپايان مى‌خورند و جايگاهشان آتش است.'),
(4558, 47, 13, 'چه قريه‌هايى كه مردمش از مردم قريه تو، كه از آن بيرونت كردند، بسى نيرومندتر بودند كه هلاكشان كرديم و هيچ يارى‌كننده‌اى نداشتند.'),
(4559, 47, 14, 'آيا كسى كه از جانب پرردگارش دليل روشنى دارد، همانند كسانى است كه كردار بدشان در نظرشان آراسته شده و از پى هواهاى خود مى‌روند؟'),
(4560, 47, 15, 'وصف بهشتى كه به پرهيزگاران وعده داده شده اين است كه در آن نهرهايى است از آبهايى تغييرناپذير و نهرهايى از شيرى كه طعمش دگرگون نمى‌شود و نهرهايى از شراب كه آشامندگان از آن لذت مى‌برند و نهرهايى از عسل مصفى. و در آنجا هرگونه ميوه كه بخواهند هست و نيز آمرزش پروردگارشان. آيا بهشتيان همانند كسانى هستند كه در آتش جاودانه‌اند و آنان را از آبى جوشان مى‌آشامانند چنان كه روده‌هايشان تكه‌تكه مى‌شود؟'),
(4561, 47, 16, 'بعضى به تو گوش مى‌دهند، تا آنگاه كه از نزد تو بيرون روند از دانشمندان مى‌پرسند: اين چه سخنانى بود كه مى‌گفت؟ خدا بر دلهايشان مهر نهاده است و از پى هواهاى خود رفته‌اند.'),
(4562, 47, 17, 'آنان كه هدايت يافته‌اند، خدا به هدايتشان مى‌افزايد و پرهيزگاريشان ارزانى مى‌دارد.'),
(4563, 47, 18, 'آيا تنها منتظر آنند كه به ناگاه قيامت فرا رسد؟ هرآينه نشانهاى قيامت آشكار شده است. و چون فرا رسد پند گرفتنشان را چه فايده؟'),
(4564, 47, 19, 'پس بدان كه هيچ خدايى جز اللّه نيست. از گناه خود و از گناه مردان و زنان مؤمن آمرزش بخواه. خدا مى‌داند كه روز به كجا مى‌رويد و شب به كجا مى‌آراميد.'),
(4565, 47, 20, 'كسانى كه ايمان آورده‌اند مى‌گويند: چرا از جانب خدا سوره‌اى نازل نمى‌شود؟ چون سوره‌اى از محكمات نازل شود كه در آن سخن از جنگ رفته باشد، آنان را كه در دلشان مرضى هست بينى كه چون كسى كه بيهوشى مرگ بر او چيره شده به تو مى‌نگرند. پس برايشان شايسته‌تر'),
(4566, 47, 21, 'فرمانبردارى است و سخن نيكو گفتن. و چون تصميم به جنگ گرفته شد، اگر با خدا صداقت ورزند برايشان بهتر است.'),
(4567, 47, 22, 'آيا اگر به حكومت رسيديد، مى‌خواهيد در زمين فساد كنيد و پيوند خويشاونديتان را ببريد؟'),
(4568, 47, 23, 'اينانند كه خدا لعنتشان كرده است و گوشهايشان را كر و چشمانشان را كور ساخته است.'),
(4569, 47, 24, 'آيا در قرآن نمى‌انديشند يا بر دلهايشان قفلهاست؟'),
(4570, 47, 25, 'شيطان اعمال كسانى را كه بعد از آشكارشدن راه هدايت مرتد شدند و بازگشتند، در نظرشان بياراست و آرزويشان دراز كرد.'),
(4571, 47, 26, 'و اين بدان سبب است كه به آن گروه كه آيات خدا را ناخوش مى‌داشتند، مى‌گفتند: ما در پاره‌اى از كارها فرمانبردار شما هستيم. و خدا از رازشان آگاه است.'),
(4572, 47, 27, 'چگونه‌اند آنگاه كه فرشتگان آنها را مى‌ميرانند و بر صورت و پشتهايشان مى‌زنند؟'),
(4573, 47, 28, 'اين به كيفر آن است كه از آنچه خدا را به خشم مى‌آورد پيروى مى‌كرده‌اند و از آنچه خشنودش مى‌ساخته كراهت داشته‌اند. خدا نيز اعمالشان را نابود كرد.'),
(4574, 47, 29, 'آيا آنان كه در دلشان مرضى است مى‌پندارند كه خدا كينه‌اى را كه در دل نهفته دارند آشكار نخواهد كرد؟'),
(4575, 47, 30, 'اگر بخواهيم، آنها را به تو مى‌نمايانيم و تو آنها را به سيمايشان يا از شيوه سخنشان خواهى شناخت، و خدا از اعمالتان آگاه است.'),
(4576, 47, 31, 'و شما را مى‌آزماييم تا مجاهدان و صابرانتان را معلوم داريم و حديثتان را آشكار كنيم.'),
(4577, 47, 32, 'كسانى كه ايمان نياوردند و از راه خدا رويگردان شدند و با آنكه راه هدايت برايشان آشكار شده بود با پيامبر مخالفت ورزيدند، هيچ زيانى به خدا نخواهند رسانيد و خدا اعمالشان را نابود خواهد كرد.'),
(4578, 47, 33, 'اى كسانى كه ايمان آورده‌ايد، از خدا اطاعت كنيد و از پيامبر اطاعت كنيد و اعمال خويش را باطل مسازيد.'),
(4579, 47, 34, 'خدا كسانى را كه كفر ورزيدند و مردم را از راه خدا باز داشتند و در كفر مردند، هرگز نخواهد آمرزيد.'),
(4580, 47, 35, 'سستى نورزيد تا دعوت به مصالحه كنيد. شما برتر هستيد و خدا با شماست و از پاداشهايتان نخواهد كاست.'),
(4581, 47, 36, 'جز اين نيست كه زندگى اينجهانى بازيچه و بيهودگى است. و اگر ايمان بياوريد و پرهيزگارى كنيد خدا پاداشهايتان را خواهد داد، و از شما اموالتان را نمى‌طلبد.'),
(4582, 47, 37, 'اگر از شما مالى طلبد و به اصرار هم طلبد، آنگاه بخل مى‌ورزيد و كينه‌هاى نهفته‌تان را آشكار مى‌سازد.'),
(4583, 47, 38, 'آگاه باشيد كه شما را دعوت مى‌كنند تا در راه خدا انفاق كنيد. بعضى از شما بخل مى‌ورزند؛ و هر كس كه بخل ورزد، در حق خود بخل ورزيده است. زيرا خدا بى‌نياز است و شما نيازمندانيد. و اگر روى برتابيد، به جاى شما مردمى ديگر آرد كه هرگز همسان شما نباشند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'ما براى تو پيروزى نمايانى را مقدر كرده‌ايم.'),
(4585, 48, 2, 'تا خدا گناه تو را آنچه پيش از اين بوده و آنچه پس از اين باشد براى تو بيامرزد و نعمت خود را بر تو تمام كند و تو را به صراط مستقيم راه نمايد.'),
(4586, 48, 3, 'و خدا ياريت كند يارى‌كردنى پيروزمندانه.'),
(4587, 48, 4, 'اوست كه بر دلهاى مؤمنان آرامش فرستاد تا بر ايمانشان پيوسته بيفزايد. و از آن خداست لشكرهاى آسمانها و زمين، و خدا دانا و حكيم است.'),
(4588, 48, 5, 'و تا مردان و زنان مؤمن را به بهشتهايى داخل كند كه در آن نهرها جارى است و در آنجا جاويدانند و گناهانشان را بزدايد. و اين در نزد خدا كاميابى بزرگى است.'),
(4589, 48, 6, 'و مردان و زنان منافق و مردان و زنان مشرك را كه بر خدا بدگمانند عذاب كند. بر گردشان بدى حلقه زند و خدا بر آنها خشم گرفت و لعنتشان كرد و جهنم را برايشان آماده كرده است و جهنم بد سرانجامى است.'),
(4590, 48, 7, 'از آن خداست لشكرهاى آسمانها و زمين، و خدا پيروزمند و حكيم است.'),
(4591, 48, 8, 'تو را گواه و مژده‌دهنده و بيم‌دهنده فرستاده‌ايم.'),
(4592, 48, 9, 'تا به خدا و پيامبرش ايمان بياوريد و ياريش كنيد و بزرگش داريد و خدا را صبحگاه و شامگاه تسبيح گوييد.'),
(4593, 48, 10, 'آنان كه با تو بيعت مى‌كنند جز اين نيست كه با خدا بيعت مى‌كنند. دست خدا بالاى دستهايشان است. و هر كه بيعت را بشكند، به زيان خود شكسته است. و هر كه بدان بيعت كه با خدا بسته است وفا كند، او را مزدى كرامند دهد.'),
(4594, 48, 11, 'از اعراب باديه نشين، آنان كه از جنگ تخلف كرده‌اند به تو خواهند گفت: دارايى و كسان ما، ما را از جنگ باز داشتند؛ پس براى ما آمرزش بخواه. به زبان چيزى مى‌گويند كه در دلشان نيست. بگو: اگر خدا برايتان زيانى بخواهد يا سودى بخواهد، چه كسى مى‌تواند در برابر خدا آن را ديگرگون كند؟ بلكه اوست كه به كارهايتان آگاه است.'),
(4595, 48, 12, 'يا مى‌پنداشتيد كه پيامبر و مؤمنان هرگز نزد كسانشان باز نخواهند گشت و دل به اين خوش كرده بوديد. پندار بدى داشته‌ايد و مردمى سزاوار هلاكت بوده‌ايد.'),
(4596, 48, 13, 'و هر كس به خدا و پيامبرش ايمان نياورده است، بداند كه براى كافران آتشى سوزان آماده كرده‌ايم.'),
(4597, 48, 14, 'از آنِ خداست فرمانروايى آسمانها و زمين. هر كه را بخواهد مى‌آمرزد و هر كه را بخواهد عذاب مى‌كند، و خدا آمرزنده و مهربان است.'),
(4598, 48, 15, 'چون براى گرفتن غنايم به راه بيفتيد، آنان كه از جنگ تخلف ورزيده‌اند خواهند گفت: بگذاريد تا ما هم از پى شما بياييم. مى‌خواهند سخن خدا را ديگرگون كنند. بگو: شما هرگز از پى ما نخواهيد آمد. خدا از پيش چنين گفته است. سپس خواهند گفت: بلكه بر ما حسد مى‌بريد؟ نه، اينان جز اندكى نمى‌فهمند.'),
(4599, 48, 16, 'به اعراب باديه‌نشين كه از جنگ تخلف ورزيده‌اند، بگو: به زودى براى جنگ با مردمى سخت نيرومند فراخوانده مى‌شويد كه با آنها بجنگيد يا مسلمان شوند. اگر اطاعت كنيد خدايتان پاداشى نيكو خواهد داد و اگر، همچنان كه پيش از اين سر برتافته‌ايد، سر برتابيد شما را به عذابى دردآور عذاب مى‌كند.'),
(4600, 48, 17, 'بر كور حرجى نيست، و بر لنگ حرجى نيست و بر بيمار حرجى نيست. و هر كه از خدا و پيامبرش اطاعت كند، او را به بهشتهايى داخل مى‌كند كه در آن نهرها روان است. و هر كه سر برتابد به عذابى دردآورش عذاب مى‌كند.'),
(4601, 48, 18, 'خدا از مؤمنان آن هنگام كه در زير درخت با تو بيعت كردند، خشنود گشت و دانست كه در دلشان چه مى‌گذرد. پس آرامش بر آنها نازل كرد و به فتحى نزديك پاداششان داد،'),
(4602, 48, 19, 'و به غنيمتهاى بسيار كه به دست مى‌آورند. و خدا پيروزمند و حكيم است.'),
(4603, 48, 20, 'خدا به شما وعده غنايم بسيار داده است كه به چنگ مى‌آوريد؛ و اين غنيمت را زودتر ارزانى داشت و شما را از آسيب مردمان امان بخشيد، تا براى مؤمنان عبرتى باشد و به راه راست هدايتتان كند.'),
(4604, 48, 21, 'و غنايم ديگرى كه هنوز به آنها دست نيافته‌ايد. هر آينه خدا به آن احاطه دارد و او بر هر كارى تواناست.'),
(4605, 48, 22, 'و اگر كافران با شما به جنگ برخيزند، پشت كرده بگريزند و ديگر هيچ دوست و ياريگرى نمى‌يابند.'),
(4606, 48, 23, 'اين سنت خداست كه از پيش چنين بوده است و تو در سنت خدا دگرگونى نخواهى يافت.'),
(4607, 48, 24, 'اوست كه چون در بطن مكه بر آنها پيروزيتان داد، دست آنها را از شما و دست شما را از آنها بازداشت. و خدا به كارهايى كه مى‌كرديد آگاه و بينا بود.'),
(4608, 48, 25, 'ايشان همانهايند كه كفر ورزيدند و شما را از مسجد الحرام بازداشتند و نگذاشتند كه قربانى به قربانگاهش برسد. اگر مردان مسلمان و زنان مسلمانى كه آنها را نمى‌شناسيد در ميان آنها نبودند و بيم آن نبود كه آنها را زير پاى درنورديد و نادانسته مرتكب گناه شويد، خدا دست شما را از آنها بازنمى‌داشت. و خدا هر كه را بخواهد مشمول رحمت خود گرداند. اگر از يكديگر جدا مى‌بودند، كافرانشان را به عذابى دردآور عذاب مى‌كرديم.'),
(4609, 48, 26, 'آنگاه كه كافران تصميم گرفتند كه دل به تعصب، تعصب جاهلى سپارند، خدا نيز آرامش خود را بر دل پيامبرش و مؤمنان فروفرستاد و به تقوى الزامشان كرد كه آنان به تقوى سزاوارتر و شايسته‌تر بودند و خدا بر هر چيزى داناست.'),
(4610, 48, 27, 'خدا رؤياى پيامبرش را به صدق پيوست كه گفته بود: اگر خدا بخواهد، ايمن، گروهى سرتراشيده و گروهى موى كوتاه كرده، بى هيچ بيمى به مسجدالحرام داخل مى‌شويد. او چيزها مى‌دانست كه شما نمى‌دانستيد. و جز آن در همين نزديكى فتحى نصيب شما كرده بود.'),
(4611, 48, 28, 'اوست كه پيامبرش را به هدايت و دين حق فرستاد تا آن دين را بر همه اديان پيروز گرداند. و خدا شهادت را كافى است.'),
(4612, 48, 29, 'محمد پيامبر خدا، و كسانى كه با او هستند بر كافران سختگيرند و با يكديگر مهربان. آنان را بينى كه ركوع مى‌كنند، به سجده مى‌آيند و جوياى فضل و خشنودى خدا هستند. نشانشان اثر سجده‌اى است كه بر چهره آنهاست. اين است وصفشان در تورات و در انجيل، كه چون كشته‌اى هستند كه جوانه بزند و آن جوانه محكم شود و بر پاهاى خود بايستد و كشاورزان را به شگفتى وادارد، تا آنجا كه كافران را به خشم آورد. خدا از ميان آنها كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند به آمرزش و پاداشى بزرگ وعده داده است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اى كسانى كه ايمان آورده‌ايد، بر خدا و پيامبر او پيشى مگيريد و از خدا بترسيد، زيرا خدا شنوا و داناست.'),
(4614, 49, 2, 'اى كسانى كه ايمان آورده‌ايد، صداى خود را از صداى پيامبر بلندتر مكنيد و همچنان كه با يكديگر بلند سخن مى‌گوييد با او به آواز بلند سخن مگوييد، كه اعمالتان ناچيز شود و آگاه نشويد.'),
(4615, 49, 3, 'كسانى كه در نزد پيامبر خدا صدايشان را پايين مى‌آورند همانهايند كه خدا دلهايشان را به تقوى آزموده است. آنها راست آمرزش و مزد بسيار.'),
(4616, 49, 4, 'آنهايى كه از آن سوى حجره‌ها ندايت مى‌دهند بيشتر بى‌خردانند.'),
(4617, 49, 5, 'اگر صبر مى‌كردند تا تو خود بيرون مى‌آمدى و نزد آنها مى‌رفتى برايشان بهتر مى‌بود، و خدا آمرزنده مهربان است.'),
(4618, 49, 6, 'اى كسانى كه ايمان آورده‌ايد، اگر فاسقى برايتان خبرى آورد تحقيق كنيد، مباد از روى نادانى به مردمى آسيب برسانيد، آنگاه از كارى كه كرده‌ايد پشيمان شويد.'),
(4619, 49, 7, 'و بدانيد كه پيامبر خدا در ميان شماست. اگر در بسيارى از امور از شما فرمان ببرد به رنج مى‌افتيد. ولى خدا ايمان را محبوب شما ساخت و آن را در دلتان بياراست و كفر و فسق و عصيان را در نظرتان مكروه گردانيد. اينان خود راه‌يافتگانند.'),
(4620, 49, 8, 'بخشش و نعمتى است از جانب خدا، و خدا دانا و حكيم است.'),
(4621, 49, 9, 'و اگر دو گروه از مؤمنان با يكديگر به جنگ برخاستند، ميانشان آشتى افكنيد. و اگر يك گروه بر ديگرى تعدى كرد، با آن كه تعدى كرده است بجنگيد تا به فرمان خدا باز گردد. پس اگر بازگشت، ميانشان صلحى عادلانه برقرار كنيد و عدالت ورزيد كه خدا عادلان را دوست دارد.'),
(4622, 49, 10, 'هر آينه مؤمنان برادرانند. ميان برادرانتان آشتى بيفكنيد و از خدا بترسيد، باشد كه بر شما رحمت آرد.'),
(4623, 49, 11, 'اى كسانى كه ايمان آورده‌ايد، مباد كه گروهى از مردان گروه ديگر را مسخره كند، شايد آن مسخره‌شدگان بهتر از آنها باشند. و مباد كه گروهى از زنان گروه ديگر را مسخره كند، شايد آن مسخره‌شدگان بهتر از آنها باشند. و از هم عيبجويى مكنيد و يكديگر را به القاب زشت مخوانيد. بد است عنوان فسق پس از ايمان‌آوردن. و كسانى كه توبه نمى‌كنند خود ستمكارانند.'),
(4624, 49, 12, 'اى كسانى كه ايمان آورده‌ايد، از گمان فراوان بپرهيزيد. زيرا پاره‌اى از گمانها در حد گناه است. و در كارهاى پنهانى يكديگر جست و جو مكنيد. و از يكديگر غيبت مكنيد. آيا هيچ يك از شما دوست دارد كه گوشت برادر مرده خود را بخورد؟ پس آن را ناخوش خواهيد داشت. و از خدا بترسيد، زيرا خدا توبه‌پذير و مهربان است.'),
(4625, 49, 13, 'اى مردم، ما شما را از نرى و ماده‌اى بيافريديم. و شما را جماعتها و قبيله‌ها كرديم تا يكديگر را بشناسيد. هر آينه گرامى‌ترين شما نزد خدا، پرهيزگارترين شماست. خدا دانا و كاردان است.'),
(4626, 49, 14, 'اعراب باديه‌نشين گفتند: ايمان آورديم. بگو: ايمان نياورده‌ايد، بگوييد كه تسليم شده‌ايم، و هنوز ايمان در دلهايتان داخل نشده است. و اگر خدا و پيامبرش را اطاعت كنيد از ثواب اعمال شما كاسته نمى‌شود، زيرا خدا آمرزنده و مهربان است.'),
(4627, 49, 15, 'مؤمنان كسانى هستند كه به خدا و پيامبر او ايمان آورده‌اند و ديگر شك نكرده‌اند، و با مال و جان خويش در راه خدا جهاد كرده‌اند. اينان راستگويانند.'),
(4628, 49, 16, 'بگو: آيا مى‌خواهيد خدا را از ديندارى خويش آگاه كنيد؟ حال آنكه خدا از هر چه در آسمانها و هر چه در زمين است آگاه است و او به هر چيزى عالم است.'),
(4629, 49, 17, 'از اينكه اسلام آورده‌اند بر تو منت مى‌گذارند. بگو: به خاطر اسلامتان بر من منت مگذاريد، بلكه خدا بدان سبب كه شما را به ايمان راه نموده است بر شما منت مى‌نهد، اگر راست مى‌گوييد.'),
(4630, 49, 18, 'هر آينه خدا نهان آسمانها و زمين را مى‌داند و به هر كارى كه مى‌كنيد بيناست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف. قسم به اين قرآن ارجمند'),
(4632, 50, 2, 'كه در شگفت شدند از اينكه از ميان خودشان بيم‌دهنده‌اى سويشان آمد و كافران گفتند: اين چيزى عجيب است،'),
(4633, 50, 3, 'آيا زمانى كه مرديم و خاك شديم ديگر بار زنده مى‌شويم؟ اين بازگشتى محال است.'),
(4634, 50, 4, 'ما مى‌دانيم كه خاك چگونه از آنها خواهد كاست. و كتابى كه همه چيز در آن نگهدارى شده، نزد ماست.'),
(4635, 50, 5, 'آرى، ايشان سخن راستى را كه بر آنها آمده بود دروغ شمردند. پس در كارى شوريده افتادند.'),
(4636, 50, 6, 'آيا به اين آسمان بر فراز سرشان نظر نمى‌كنند كه چگونه آن را بنا كرده‌ايم و آراسته‌ايم و هيچ شكافى در آن نيست؟'),
(4637, 50, 7, 'و زمين را گسترديم و در آن كوه‌هاى بلند افكنديم و از هر گونه نباتات خوش‌منظر در آن رويانيديم.'),
(4638, 50, 8, 'بينشى و پندى است براى هر بنده‌اى كه به خدا بازگردد.'),
(4639, 50, 9, 'و از آسمان، آبى پربركت فرستاديم و بدان باغها و دانه‌هاى دروشدنى رويانيديم.'),
(4640, 50, 10, 'و نخلهاى بلند با خوشه‌هاى بر هم چيده.'),
(4641, 50, 11, 'تا رزق بندگان باشد و بدان آب، سرزمين مرده را زنده كرديم. برآمدن از گور نيز چنين است.'),
(4642, 50, 12, 'پيش از آنها قوم نوح و اصحاب رَس و ثمود تكذيب كرده بودند،'),
(4643, 50, 13, 'و قوم عاد و فرعون و قوم لوط،'),
(4644, 50, 14, 'و اصحاب اَيكه و قوم تُبَّع، همه پيامبران را دروغ شمردند و مستحق وعده عذاب من شدند.'),
(4645, 50, 15, 'آيا از آفرينش نخستين عاجز شده بوديم؟ نه، آنها از آفرينش تازه در شكند.'),
(4646, 50, 16, 'ما آدمى را آفريده‌ايم و از وسوسه‌هاى نفس او آگاه هستيم، زيرا از رگ گردنش به او نزديك‌تريم.'),
(4647, 50, 17, 'هنگامى كه آن دو فرشته فراگيرنده در جانب راست و جانب چپ او نشسته‌اند هر چيز را فرامى‌گيرند.'),
(4648, 50, 18, 'هيچ كلامى نمى‌گويد مگر آنكه در كنار او مراقبى حاضر است.'),
(4649, 50, 19, 'به راستى كه بيهوشى مرگ فرامى‌رسد. اين است آنچه از آن مى‌گريختى.'),
(4650, 50, 20, 'و در صور دميده مى‌شود. اين است روز وعده‌هاى عذاب.'),
(4651, 50, 21, 'هر كسى مى‌آيد و با او كسى است كه مى‌راندش و كسى است كه برايش شهادت مى‌دهد.'),
(4652, 50, 22, 'تو از اين غافل بودى. ما پرده از برابرت برداشتيم و امروز چشمانت تيزبين شده است.'),
(4653, 50, 23, 'موكل او گويد: اين است آنچه من آماده كرده‌ام.'),
(4654, 50, 24, 'هر ناسپاس كينه‌توزى را به جهنم بيندازيد،'),
(4655, 50, 25, 'آن كه از بخشش امساك ورزيد و تجاوزكار بود و شك مى‌آورد،'),
(4656, 50, 26, 'آن كه با خداى يكتا خداى ديگرى قرار داد. پس به عذاب سختش بيفكنيد.'),
(4657, 50, 27, 'همنشين او گويد: اى پروردگار ما، من او را به سركشى وادار نكردم، بلكه او خود سخت در گمراهى بود.'),
(4658, 50, 28, 'مى‌گويد: نزد من مجادله مكنيد. من پيش از اين با شما سخن از عذاب گفته بودم.'),
(4659, 50, 29, 'وعده من ديگرگون نمى‌شود و من به بندگان ستم نمى‌كنم.'),
(4660, 50, 30, 'روزى كه جهنم را مى‌گوييم: آيا پر شده‌اى؟ مى‌گويد: آيا هيچ زيادتى هست؟'),
(4661, 50, 31, 'و بهشت را براى پرهيزگاران نزديك بياورند، تا از آن دور نباشند.'),
(4662, 50, 32, 'اين همان چيزى است كه به هر توبه‌كننده پرهيزگارى وعده‌اش داده بودند.'),
(4663, 50, 33, 'آنهايى را كه در نهان از خداى رحمان مى‌ترسند و با دلى توبه‌كار آمده‌اند،'),
(4664, 50, 34, 'گويند: در امان از هر عذابى به بهشت داخل شويد. اين روز، روز جاودانى است.'),
(4665, 50, 35, 'در آنجا هر چه بخواهند هست و بيشتر از آن نزد ما هست.'),
(4666, 50, 36, 'پيش از آنها چه مردمى را كه نيرومندتر بودند و در شهرها سير و جستجو مى‌كردند، به هلاكت آورده‌ايم. آيا راه گريزى هست؟'),
(4667, 50, 37, 'در اين سخن براى صاحبدلان يا آنان كه با حضور گوش فرا مى‌دارند، اندرزى است.'),
(4668, 50, 38, 'ما آسمانها و زمين و آنچه را ميان آنهاست در شش روز آفريديم و هيچ خستگى بما نرسيد.'),
(4669, 50, 39, 'در برابر آنچه مى‌گويند، پايدارى كن. و در ستايش پروردگارت پيش از برآمدن آفتاب و پيش از غروب آن تسبيح گوى.'),
(4670, 50, 40, 'و نيز در پاره‌اى از شب و بعد از هر سجده او را تسبيح گوى.'),
(4671, 50, 41, 'و آنگاه كه منادى از مكانى نزديك ندا مى‌دهد گوش فرادار.'),
(4672, 50, 42, 'روزى كه آن آواز سهمناك را به حق مى‌شنوند، آن روز، روز بيرون‌شدن از گور است.'),
(4673, 50, 43, 'ما زنده مى‌كنيم و مى‌ميرانيم و بازگشت نزد ماست.'),
(4674, 50, 44, 'روزى كه زمين بشكافد و آنها به شتاب بيرون آيند. و اين گرد آورى براى ما آسان است.'),
(4675, 50, 45, 'ما به آنچه مى‌گويند داناتريم و تو به آنها زور نمى‌گويى. پس هر كه را از وعده عذاب من مى‌ترسد به قرآن اندرز ده.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهايى كه خاك مى‌پراكنند.'),
(4677, 51, 2, 'سوگند به ابرهاى گرانبار.'),
(4678, 51, 3, 'سوگند به كشتيهايى كه به آسانى روانند،'),
(4679, 51, 4, 'و سوگند به فرشتگانى كه تقسيم‌كننده كارهايند.'),
(4680, 51, 5, 'كه آنچه شما را وعده مى‌دهند راست است،'),
(4681, 51, 6, 'و روز جزا آمدنى است.'),
(4682, 51, 7, 'و سوگند به آسمان كه آراسته به ستارگان است،'),
(4683, 51, 8, 'كه شما سخن گونه‌گون مى‌گوييد.'),
(4684, 51, 9, 'از حق منصرف گردد آن كه منصرفش خواسته‌اند.'),
(4685, 51, 10, 'مرگ باد بر آن دروغگويان:'),
(4686, 51, 11, 'آنان كه به غفلت در جهل فرومانده‌اند.'),
(4687, 51, 12, 'مى‌پرسند روز جزا كى خواهد بود؟'),
(4688, 51, 13, 'روزى است كه بر آتش، عذابشان مى‌كنند.'),
(4689, 51, 14, 'عذاب خود را بچشيد: اين است آن چيزى كه به شتاب مى‌طلبيديد.'),
(4690, 51, 15, 'پرهيزگاران در باغها و كنار چشمه‌ساران باشند.'),
(4691, 51, 16, 'آنچه را خدا ارزانيشان داشته است گرفته‌اند. زيرا پيش از آن نيكوكار بودند،'),
(4692, 51, 17, 'اندكى از شب را مى‌خوابيدند،'),
(4693, 51, 18, 'و به هنگام سحر استغفار مى‌كردند،'),
(4694, 51, 19, 'و در اموالشان براى سائل و محروم حقى بود.'),
(4695, 51, 20, 'و در زمين براى اهل يقين عبرتهايى است،'),
(4696, 51, 21, 'و نيز در وجود خودتان. آيا نمى‌بينيد؟'),
(4697, 51, 22, 'و رزق شما و هر چه به شما وعده شده در آسمان است.'),
(4698, 51, 23, 'پس سوگند به پروردگار آسمانها و زمين كه اين سخن، آنچنان كه سخن مى‌گوييد، حتمى است.'),
(4699, 51, 24, 'آيا داستان مهمانان گرامى ابراهيم به تو رسيده است؟'),
(4700, 51, 25, 'آنگاه كه نزد او آمدند و گفتند: سلام. گفت: سلام، شما مردمى ناشناخته‌ايد.'),
(4701, 51, 26, 'در نهان و شتابان نزد كسان خود رفت و گوساله فربهى آورد.'),
(4702, 51, 27, 'طعام را به نزدشان گذاشت و گفت: چرا نمى‌خوريد؟'),
(4703, 51, 28, 'و از آنها بيمناك شد. گفتند: مترس. و او را به فرزندى دانا مژده دادند.'),
(4704, 51, 29, 'و زنش فريادزنان آمد و بر روى زد و گفت: من پير زالى نازايم.'),
(4705, 51, 30, 'گفتند: پروردگار تو اينچنين گفته است. و او حكيم و داناست.'),
(4706, 51, 31, 'گفت: اى رسولان، به چه كار آمده‌ايد؟'),
(4707, 51, 32, 'گفتند: ما را بر مردمى تبهكار فرستاده‌اند،'),
(4708, 51, 33, 'تا تكه‌هاى كلوخ بر سرشان بباريم،'),
(4709, 51, 34, 'كه بر آنها از جانب پروردگارت براى متجاوزان نشان گذاشته‌اند.'),
(4710, 51, 35, 'پس همه كسانى را كه ايمان آورده بودند بيرون برديم.'),
(4711, 51, 36, 'و در آن شهر جز يك خانه از فرمانبرداران نيافتيم.'),
(4712, 51, 37, 'و در آن سرزمين براى كسانى كه از عذاب دردآور مى‌ترسند نشانى باقى گذاشتيم.'),
(4713, 51, 38, 'و عبرتى است در موسى آنگاه كه او را با برهانى آشكار نزد فرعون فرستاديم.'),
(4714, 51, 39, 'و او با همه نيرويش اعراض كرد و گفت: جادوگرى است يا ديوانه‌اى.'),
(4715, 51, 40, 'او و لشكرهايش را فروگرفتيم و به دريا افكنديم. و او مستوجب ملامت بود.'),
(4716, 51, 41, 'و نيز عبرتى است در قوم عاد آنگاه كه باد عقيم را بر آنها فرستاديم.'),
(4717, 51, 42, 'بر هيچ چيز نوزيد مگر آنكه چون استخوانى پوسيده‌اش بر جاى نهاد.'),
(4718, 51, 43, 'و نيز عبرتى است در قوم ثمود آنگاه كه به آنها گفته شد: تا زمانى چند برخوردار شويد.'),
(4719, 51, 44, 'آنان از فرمان پروردگارشان سرباز زدند و همچنان كه مى‌نگريستند صاعقه فروگرفتشان.'),
(4720, 51, 45, 'ايستادن نتوانستند و ياراى انتقام نداشتند.'),
(4721, 51, 46, 'و از پيش قوم نوح را فروگرفتيم، كه قومى نافرمان بودند.'),
(4722, 51, 47, 'و آسمان را به نيرو برافراشتيم و حقا كه ما تواناييم.'),
(4723, 51, 48, 'و زمين را گسترديم، و چه نيكو گسترندگانيم.'),
(4724, 51, 49, 'و از هر چيز جفتى بيافريده‌ايم، باشد كه عبرت گيريد.'),
(4725, 51, 50, 'پس به سوى خداوند بگريزيد. من شما را از جانب او بيم‌دهنده‌اى آشكارم.'),
(4726, 51, 51, 'و با خداى يكتا خداى ديگرى را مپرستيد. من شما را از جانب او بيم‌دهنده‌اى آشكارم.'),
(4727, 51, 52, 'بدين سان بر آنهايى كه از اين پيش بودند پيامبرى مبعوث نشد جز آنكه گفتند: جادوگرى است، يا ديوانه‌اى است.'),
(4728, 51, 53, 'آيا بدين كار يكديگر را وصيت كرده بودند؟ نه، خود مردمى طاغى بودند.'),
(4729, 51, 54, 'پس، از آنها رويگردان شو. كس تو را ملامت نخواهد كرد.'),
(4730, 51, 55, 'اندرز بده كه اندرز، مؤمنان را سودمند افتد.'),
(4731, 51, 56, 'جن و انس را جز براى پرستش خود نيافريده‌ام.'),
(4732, 51, 57, 'از آنها رزقى نمى‌خواهم و نمى‌خواهم كه مرا اطعام كنند.'),
(4733, 51, 58, 'خداست روزى‌دهنده. و اوست صاحب نيرويى سخت استوار.'),
(4734, 51, 59, 'اين ستمكاران را از عذاب بهره‌اى است همانند بهره‌اى كه يارانشان داشتند. پس به شتاب چيزى از من نخواهند.'),
(4735, 51, 60, 'واى بر كافران، از آن روز كه آنها را وعده داده‌اند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'قسم به كوه طور،'),
(4737, 52, 2, 'و قسم به كتاب نوشته شده،'),
(4738, 52, 3, 'در صفحه‌اى گشاده،'),
(4739, 52, 4, 'و قسم به بيت المعمور،'),
(4740, 52, 5, 'و قسم به اين سقف برافراشته،'),
(4741, 52, 6, 'و قسم به درياى مالامال،'),
(4742, 52, 7, 'كه عذاب پروردگارت واقع شدنى است.'),
(4743, 52, 8, 'و آن را دفع‌كننده‌اى نيست.'),
(4744, 52, 9, 'روزى كه آسمان سخت بچرخد،'),
(4745, 52, 10, 'و كوه‌ها به شتاب روان شوند،'),
(4746, 52, 11, 'پس در آن روز، واى بر تكذيب‌كنندگان.'),
(4747, 52, 12, 'آنان كه سرگرم كارهاى باطل خويشند.'),
(4748, 52, 13, 'روزى كه آنها را به قهر به جانب جهنم كشند.'),
(4749, 52, 14, 'اين است آن آتشى كه دروغش مى‌انگاشتيد.'),
(4750, 52, 15, 'آيا اين جادوست يا شما نمى‌بينيد؟'),
(4751, 52, 16, 'به آتش درآييد. خواه بر آن صبر كنيد يا صبر نكنيد، تفاوتى نكند. شما را در برابر كارهايى كه مى‌كرده‌ايد، كيفر مى‌دهند.'),
(4752, 52, 17, 'پرهيزگاران در بهشتها و نعمتند.'),
(4753, 52, 18, 'از آنچه پروردگارشان به آنها داده است شادمانند، و خدا آنها را از عذاب جهنم نگه داشته است.'),
(4754, 52, 19, 'به پاداش كارهايى كه كرده‌ايد بخوريد و بياشاميد. گوارايتان باد.'),
(4755, 52, 20, 'بر آن تختهاى كنار هم چيده، تكيه مى‌زنند. حوريان را همسرشان مى‌گردانيم.'),
(4756, 52, 21, 'كسانى كه خود ايمان آوردند و فرزندانشان در ايمان، پيرويشان كردند. فرزندانشان را به آنها ملحق مى‌كنيم و از پاداش عملشان هيچ نمى‌كاهيم كه هر كسى در گرو كار خويشتن است.'),
(4757, 52, 22, 'و پيوسته از هر گونه كه بخواهند، ميوه و گوشت، عطايشان مى‌كنيم.'),
(4758, 52, 23, 'در آنجا جام مى‌دهند و جام مى‌ستانند، كه در آن نه سخن بيهوده باشد و نه ارتكاب گناه.'),
(4759, 52, 24, 'غلامانشان چون مرواريد پنهان در صدف، به گردشان مى‌چرخند.'),
(4760, 52, 25, 'پرسش‌كنان روى به يكديگر مى‌كنند؛'),
(4761, 52, 26, 'مى‌گويند: پيش از اين در ميان كسانمان، تنها ما از خدا مى‌ترسيديم.'),
(4762, 52, 27, 'پس خدا بر ما منت نهاد و ما را از عذاب سموم نگه داشت.'),
(4763, 52, 28, 'ما پيش از اين او را مى‌خوانديم، كه او احسان‌كننده و مهربان است.'),
(4764, 52, 29, 'پندشان ده، كه تو به بركت نعمت پروردگارت نه كاهن هستى و نه مجنون.'),
(4765, 52, 30, 'يا مى‌گويند: شاعرى است و ما براى وى منتظر حوادث روزگاريم.'),
(4766, 52, 31, 'بگو: شما منتظر بمانيد كه من نيز با شما انتظار مى‌كشم.'),
(4767, 52, 32, 'آيا آنان را رؤياهايشان به اين پندارها كشانده يا خود مردمى طاغى هستند.'),
(4768, 52, 33, 'يا مى‌گويند: قرآن را خود ساخته است. نه، آنها ايمان نمى‌آورند.'),
(4769, 52, 34, 'اگر راست مى‌گويند، سخنى مانند آن بياورند.'),
(4770, 52, 35, 'آيا بى‌هيچ خالقى پديد آمده‌اند يا خود خالق خويشند؟'),
(4771, 52, 36, 'يا آسمانها و زمين را خلق كرده‌اند؟ نه، به يقين نرسيده‌اند.'),
(4772, 52, 37, 'يا خزاين پروردگارت نزد آنهاست؟ يا بر چيزى تسلط يافته‌اند،'),
(4773, 52, 38, 'يا نردبامى دارند كه از آن بالا مى‌روند و گوش فرامى‌دهند؟ پس آن كه گوش فراداده حجتى آشكار بياورد.'),
(4774, 52, 39, 'يا خداوند را دختران است و شما را پسران؟'),
(4775, 52, 40, 'يا از آنها مزدى مى‌طلبى و پرداخت غرامت بر آنها دشوار است؟'),
(4776, 52, 41, 'يا علم غيب مى‌دانند و مى‌نويسند؟'),
(4777, 52, 42, 'يا مى‌خواهند حيلتى كنند؟ اما كافران خود به حيلت گرفتارند.'),
(4778, 52, 43, 'يا آنها را خدايى است جز خداى يكتا؟ منزه است خداى يكتا از هر چه شريكش مى‌سازند.'),
(4779, 52, 44, 'اگر بنگرند كه قطعه‌اى از آسمان فرومى‌افتد، مى‌گويند: ابرى است متراكم.'),
(4780, 52, 45, 'پس آنها را واگذار تا روزى را كه در آن به هلاكت مى‌رسند بنگرند؛'),
(4781, 52, 46, 'روزى كه مكرشان هيچ به حالشان سود نكند و كس به ياريشان برنخيزد.'),
(4782, 52, 47, 'براى كسانى كه ستم مى‌كنند، باز هم عذابى است جز اين عذاب، ولى بيشترينشان نمى‌دانند.'),
(4783, 52, 48, 'در برابر فرمان پروردگارت شكيبا باش كه تو تحت نظر مايى. و هنگامى كه برخاستى، به ستايش پرردگارت تسبيح گوى.'),
(4784, 52, 49, 'و تسبيح گوى در پاره‌اى از شب و به هنگام ناپديد شدن ستارگان.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'قسم به آن ستاره چون پنهان شد،'),
(4786, 53, 2, 'كه يار شما نه گمراه شده و نه به راه كج رفته است.'),
(4787, 53, 3, 'و سخن از روى هوى نمى‌گويد.'),
(4788, 53, 4, 'نيست اين سخن جز آنچه بدو وحى مى‌شود.'),
(4789, 53, 5, 'او را آن فرشته بس نيرومند تعليم داده است:'),
(4790, 53, 6, 'صاحب نيرويى كه استيلا يافت،'),
(4791, 53, 7, 'و او به كناره بلند آسمان بود.'),
(4792, 53, 8, 'سپس نزديك شد و بسيار نزديك شد،'),
(4793, 53, 9, 'تا به قدر دو كمان، يا نزديك‌تر.'),
(4794, 53, 10, 'و خدا به بنده خود هر چه بايد وحى كند وحى كرد.'),
(4795, 53, 11, 'دل آنچه را كه ديد دروغ نشمرد.'),
(4796, 53, 12, 'آيا در آنچه مى‌بيند با او جدال مى‌كنيد؟'),
(4797, 53, 13, 'او را ديگر بار هم بديد،'),
(4798, 53, 14, 'نزد سدرةالمنتهى،'),
(4799, 53, 15, 'كه آرامگاه بهشت نزد آن درخت است.'),
(4800, 53, 16, 'وقتى كه سدره را چيزى در خود مى‌پوشيد،'),
(4801, 53, 17, 'چشم خطا نكرد و از حد درنگذشت.'),
(4802, 53, 18, 'هر آينه پاره‌اى از آيات بزرگ پروردگارش را بديد.'),
(4803, 53, 19, 'آيا لات و عُزا را ديده‌ايد؟'),
(4804, 53, 20, 'و مَنات آن بت سومى ديگر را؟'),
(4805, 53, 21, 'آيا شما را پسر باشد و او را دختر؟'),
(4806, 53, 22, 'اين تقسيمى است خلاف عدالت.'),
(4807, 53, 23, 'اينها چيزى نيستند جز نامهايى كه خود و پدرانتان به آنها داده‌ايد. و خداوند هيچ دليلى بر آنها نفرستاده است. تنها از پى گمان و هواى نفس خويش مى‌روند و حال آنكه از جانب خدا راهنماييشان كرده‌اند.'),
(4808, 53, 24, 'آيا هر چه آدمى آرزو كند برايش حاصل است؟'),
(4809, 53, 25, 'هم آخرت و هم دنيا از آن خداست.'),
(4810, 53, 26, 'و چه بسيار فرشتگان در آسمانند كه شفاعتشان هيچ سود ندهد، مگر از آن پس كه خدا براى هر كه خواهد رخصت دهد و خشنود باشد.'),
(4811, 53, 27, 'آنان كه به آخرت ايمان ندارند فرشتگان را به نامهاى زنان مى‌نامند.'),
(4812, 53, 28, 'اينان را بدانچه مى‌گويند هيچ دانشى نيست. تنها از پندار خود پيروى مى‌كنند و پندار براى شناخت حقيقت كافى نيست.'),
(4813, 53, 29, 'پس تو نيز از كسى كه از سخن ما رويگردان مى‌شود و جز زندگى دنيوى را نمى‌جويد اعراض كن.'),
(4814, 53, 30, 'منتهاى دانششان همين است. پروردگار تو به آن كه از طريق او گمراه مى‌شود يا به راه هدايت مى‌افتد داناتر است.'),
(4815, 53, 31, 'از آنِ خداست هر چه در آسمانها و هر چه در زمين است تا بدكاران را در برابر اعمالشان كيفر دهد و نيكوكاران را به كردار نيكشان پاداش.'),
(4816, 53, 32, 'كسانى كه از گناهان بزرگ و زشتيها اجتناب مى‌كنند -جز آنكه گناهى كوچك از آنها سرزند- بدانند كه آمرزش پروردگار تو وسيع است و او به شما، آنگاه كه از زمين بيافريدتان و آنگاه كه در شكم مادرتان پنهان بوديد، آگاه‌تر است. خويشتن را بى‌گناه مدانيد. اوست كه پرهيزگار را بهتر مى‌شناسد.'),
(4817, 53, 33, 'آيا آن را كه از تو رويگردان شد ديدى؟'),
(4818, 53, 34, 'اندك مى‌بخشيد و در انفاق بخل مى‌ورزيد.'),
(4819, 53, 35, 'آيا علم غيب دارد و مى‌بيند؟'),
(4820, 53, 36, 'يا از آنچه در صحيفه‌هاى موسى آمده بى‌خبر مانده است؟'),
(4821, 53, 37, 'يا از ابراهيم، كه حق رسالت را ادا كرد؟'),
(4822, 53, 38, 'كه هيچ كس بار گناه ديگرى را برندارد؟'),
(4823, 53, 39, 'و اينكه: براى مردم پاداشى جز آنچه خود كرده‌اند نيست؟'),
(4824, 53, 40, 'و زودا كه كوشش او در نظر آيد.'),
(4825, 53, 41, 'سپس به او پاداشى تمام دهند.'),
(4826, 53, 42, 'و پايان راه همه، پروردگار توست.'),
(4827, 53, 43, 'و اوست كه مى‌خنداند و مى‌گرياند.'),
(4828, 53, 44, 'و اوست كه مى‌ميراند و زنده مى‌كند.'),
(4829, 53, 45, 'و اوست كه جفتهاى نر و ماده را آفريده است،'),
(4830, 53, 46, 'از نطفه، آنگاه كه در رحم ريخته مى‌شود.'),
(4831, 53, 47, 'و بر خداوند است كه آن را بار ديگر زنده كند.'),
(4832, 53, 48, 'و اوست كه بى‌نياز كند و روزى دهد.'),
(4833, 53, 49, 'و اوست پرردگار ستاره شِعرى.'),
(4834, 53, 50, 'و اوست كه عاد، آن قوم پيشين را هلاك كرد،'),
(4835, 53, 51, 'و از ثمود هيچ باقى نگذاشت.'),
(4836, 53, 52, 'و پيش از آنها قوم نوح را، كه ستمكارتر و سركش‌تر بودند،'),
(4837, 53, 53, 'و نيز مؤتفكه را نابود كرد.'),
(4838, 53, 54, 'و فروپوشيد آن قوم را، هر چه بايد فروپوشد.'),
(4839, 53, 55, 'پس به كدام يك از نعمتهاى پروردگارت شك مى‌كنى؟'),
(4840, 53, 56, 'اين بيم‌دهنده‌اى است همانند بيم‌دهندگان پيشين.'),
(4841, 53, 57, 'قيامت فرا رسيد.'),
(4842, 53, 58, 'كس جز خداوند آشكارش نكند.'),
(4843, 53, 59, 'آيا از اين سخن در شگفت افتاده‌ايد؟'),
(4844, 53, 60, 'و مى‌خنديد و نمى‌گرييد؟'),
(4845, 53, 61, 'و شما غافل شده‌ايد.'),
(4846, 53, 62, 'پس خدا را سجده كنيد و او را بپرستيد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قيامت نزديك شد و ماه دو پاره گرديد.'),
(4848, 54, 2, 'و اگر معجزه‌اى ببينند، روى بگردانند و گويند: جادويى بزرگ است.'),
(4849, 54, 3, 'و تكذيب مى‌كنند و از پى هواهاى خويش مى‌روند. و هر كارى را هدفى است.'),
(4850, 54, 4, 'و برايشان خبرهايى آمده است كه از گناهشان بازمى‌دارد.'),
(4851, 54, 5, 'حكمتى است تمام. ولى بيم‌دهندگان سودشان ندهند.'),
(4852, 54, 6, 'پس در آن روز كه آن دعوت‌كننده، آنان را به چيزى ناخوش فرامى‌خواند، از ايشان رويگردان شو.'),
(4853, 54, 7, 'نشان ذلت در چشمانشان آشكار است. چون ملخهاى پراكنده از قبرها بيرون مى‌آيند.'),
(4854, 54, 8, 'سرها را بالا گرفته به سوى آن دعوت‌كننده مى‌شتابند. كافران مى‌گويند: اين روز دشوارى است.'),
(4855, 54, 9, 'پيش از اينها قوم نوح تكذيب كرده بودند. بنده ما را تكذيب كردند و گفتند: ديوانه است. و به دشنامش راندند.'),
(4856, 54, 10, 'و پروردگارش را خواند: من مغلوب شده‌ام، انتقام بكش.'),
(4857, 54, 11, 'و ما نيز درهاى آسمان را به روى آبى كه به شدت مى‌ريخت گشوديم.'),
(4858, 54, 12, 'و از زمين چشمه‌ها شكافتيم تا آب به آن مقدار كه مقدر شده بود گرد آمد.'),
(4859, 54, 13, 'و او را بر آن كشتى كه تخته‌ها و ميخها داشت سوار كرديم.'),
(4860, 54, 14, 'زير نظر ما روان شد. اين بود جزاى كسانى كه كفر ورزيدند.'),
(4861, 54, 15, 'و هر آينه آن كشتى را نشانه‌اى ساختيم. آيا هيچ پندگيرنده‌اى هست؟'),
(4862, 54, 16, 'پس عذاب و بيم‌دادنهاى من چگونه بود؟'),
(4863, 54, 17, 'و اين قرآن را آسان ادا كرديم تا از آن پند گيرند. آيا پندگيرنده‌اى هست؟'),
(4864, 54, 18, 'قوم عاد تكذيب كردند. پس عذاب و بيم‌دادنهاى من چگونه بود؟'),
(4865, 54, 19, 'ما بر آنها در روزى نحس و طولانى بادى سخت فرستاديم،'),
(4866, 54, 20, 'كه مردمان را از زمين، همانند ريشه‌هاى از جاى‌كنده نخل، برمى‌كند.'),
(4867, 54, 21, 'عذاب و بيم‌دادنهاى من چگونه بود؟'),
(4868, 54, 22, 'و اين قرآن را آسان ادا كرديم تا از آن پند گيرند. آيا پندگيرنده‌اى هست؟'),
(4869, 54, 23, 'قوم ثمود بيم‌دهندگان را تكذيب كردند.'),
(4870, 54, 24, 'گفتند: اگر از انسانى همانند خود پيروى كنيم گمراه و ديوانه باشيم؛'),
(4871, 54, 25, 'آيا از ميان همه ما كلام خدا به او القا شده است؟ نه، او دروغگويى خودخواه است.'),
(4872, 54, 26, 'فردا خواهند دانست كه دروغگوى خودخواه كيست.'),
(4873, 54, 27, 'ما آن ماده‌شتر را براى آزمايششان مى‌فرستيم. پس مراقبشان باش و صبر كن.'),
(4874, 54, 28, 'و به آنها بگوى كه آب ميانشان تقسيم شده. نوبت هر كه باشد او به سر آب مى‌رود.'),
(4875, 54, 29, 'يارشان را ندا دادند و او شمشير برگرفت و آن را پى كرد.'),
(4876, 54, 30, 'عذاب و بيم‌دادنهاى من چگونه بود؟'),
(4877, 54, 31, 'ما بر آنها يك آواز سهمناك فرستاديم. پس همانند آن علفهاى خشك آغل گوسفند شدند.'),
(4878, 54, 32, 'و اين قرآن را آسان ادا كرديم تا از آن پند گيرند. آيا پندگيرنده‌اى هست؟'),
(4879, 54, 33, 'قوم لوط بيم‌دهندگان را تكذيب كردند.'),
(4880, 54, 34, 'ما بر آنها بادى ريگ‌بار فرستاديم، مگر بر خاندان لوط كه آنها را سحرگاه رهانيديم.'),
(4881, 54, 35, 'نعمتى بود از جانب ما و آنان را كه سپاس گويند چنين پاداش دهيم.'),
(4882, 54, 36, 'از انتقام سخت ما ترسانيدشان ولى با بيم‌دهندگان به جدال برخاستند.'),
(4883, 54, 37, 'از مهمان او كارى زشت خواستند. ما نيز چشمانشان را كور گردانيديم. پس بچشيد عذاب و بيم‌دادنهاى مرا.'),
(4884, 54, 38, 'هر آينه بامدادان عذابى پايدار به سر وقتشان آمد.'),
(4885, 54, 39, 'پس عذاب من و بيم‌دادنهاى مرا بچشيد.'),
(4886, 54, 40, 'و اين قرآن را آسان ادا كرديم تا از آن پند گيرند. آيا پندگيرنده‌اى هست؟'),
(4887, 54, 41, 'بيم‌دهندگان نزد خاندان فرعون آمدند.'),
(4888, 54, 42, 'همه آيات ما را تكذيب كردند. ما نيز آنها را فرو گرفتيم چون فروگرفتن پيروزمندى مقتدر.'),
(4889, 54, 43, 'آيا كافران شما از ايشان نيرومندترند يا در كتابها آمده است كه در امان هستيد؟'),
(4890, 54, 44, 'يا مى‌گويند كه ما همگى به انتقام برمى‌خيزيم؟'),
(4891, 54, 45, 'زودا كه آن جمع منهزم شود و پشت كرده بازگردند.'),
(4892, 54, 46, 'بلكه وعده‌گاه آنها قيامت است و قيامت بلاخيزتر و تلختر است.'),
(4893, 54, 47, 'مجرمان در گمراهى و جنونند.'),
(4894, 54, 48, 'روزى كه آنها را، به صورت، در جهنم كشند كه: بچشيد عذاب سَقر را.'),
(4895, 54, 49, 'ما هر چيز را به اندازه آفريده‌ايم.'),
(4896, 54, 50, 'فرمان ما تنها يك فرمان است، آن هم چشم بر هم زدنى است.'),
(4897, 54, 51, 'كسانى را كه همانند شما بودند، هلاك كرديم. آيا پندگيرنده‌اى هست؟'),
(4898, 54, 52, 'هر كارى كه كرده‌اند در دفترهاست.'),
(4899, 54, 53, 'هر كار بزرگ و كوچكى مكتوب است.'),
(4900, 54, 54, 'پرهيزگاران در باغها و كنار جويبارانند.'),
(4901, 54, 55, 'در جايگاهى پسنديده، نزد فرمانروايى توانا.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'خداى رحمان،'),
(4903, 55, 2, 'قرآن را تعليم داد.'),
(4904, 55, 3, 'انسان را بيافريد.'),
(4905, 55, 4, 'به او گفتن آموخت.'),
(4906, 55, 5, 'آفتاب و ماه به حسابى مقرر در حركتند.'),
(4907, 55, 6, 'و گياه و درخت سجده‌اش مى‌كنند.'),
(4908, 55, 7, 'آسمان را برافراخت و ترازو را برنهاد.'),
(4909, 55, 8, 'تا در ترازو تجاوز مكنيد.'),
(4910, 55, 9, 'وزن‌كردن را به عدالت رعايت كنيد و كم‌فروشى مكنيد.'),
(4911, 55, 10, 'زمين را براى مردم قرار داد.'),
(4912, 55, 11, 'در آن ميوه‌هاست و نخلها، با خوشه‌هايى كه در غلافند.'),
(4913, 55, 12, 'و دانه‌هايى كه همراه با كاهند و نيز گياهان خوشبو.'),
(4914, 55, 13, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4915, 55, 14, 'آدمى را از گل خشك‌شده‌اى چون سفال بيافريد.'),
(4916, 55, 15, 'و جن را از شعله‌اى بى‌دود.'),
(4917, 55, 16, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4918, 55, 17, 'پروردگار دو مشرق و پروردگار دو مغرب.'),
(4919, 55, 18, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4920, 55, 19, 'دو دريا را پيش راند تا به هم رسيدند،'),
(4921, 55, 20, 'ميانشان حجابى است تا به هم در نشوند.'),
(4922, 55, 21, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4923, 55, 22, 'از آن دو، مرواريد و مرجان بيرون مى‌آيد.'),
(4924, 55, 23, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4925, 55, 24, 'و او راست كشتيهايى همانند كوه كه در دريا مى‌روند.'),
(4926, 55, 25, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4927, 55, 26, 'هر چه بر روى زمين است دستخوش فناست،'),
(4928, 55, 27, 'و ذات پروردگار صاحب جلالت و اكرام توست كه باقى مى‌ماند.'),
(4929, 55, 28, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4930, 55, 29, 'هر كس كه در آسمانها و زمين است سائل درگاه اوست، و او هر روز در كارى است.'),
(4931, 55, 30, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4932, 55, 31, 'اى جنيان و آدميان، به حساب شما خواهيم رسيد.'),
(4933, 55, 32, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4934, 55, 33, 'اى گروه جنيان و آدميان، اگر مى‌توانيد كه از كناره‌هاى آسمانها و زمين بيرون رويد، بيرون رويد. ولى بيرون نتوانيد رفت مگر با داشتن قدرتى.'),
(4935, 55, 34, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4936, 55, 35, 'بر شما شعله‌اى بى‌دود فرستاده شود، يا دودى بى‌شعله، پس با او مقابله نتوانيد كرد.'),
(4937, 55, 36, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4938, 55, 37, 'آنگاه كه آسمان شكافته شود، رنگى سرخ چون رنگ چرم خواهد داشت.'),
(4939, 55, 38, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4940, 55, 39, 'پس در آن روز از گناه هيچ جنى و آدميى نپرسند.'),
(4941, 55, 40, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4942, 55, 41, 'كافران را به نشان صورتشان مى‌شناسند و از موى جلو سر و پاهايشان مى‌گيرند.'),
(4943, 55, 42, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4944, 55, 43, 'اين همان جهنمى است كه مجرمان دروغش مى‌پنداشتند.'),
(4945, 55, 44, 'و اكنون در ميان آن و آب جوشان مى‌گردند.'),
(4946, 55, 45, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4947, 55, 46, 'هر كس را كه از ايستادن به پيشگاه پروردگارش ترسيده باشد دو بهشت است.'),
(4948, 55, 47, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4949, 55, 48, 'آن دو پر از درختانند.'),
(4950, 55, 49, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4951, 55, 50, 'در آن دو، دو چشمه جارى است.'),
(4952, 55, 51, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4953, 55, 52, 'در آنجا از هر گونه ميوه‌اى دو قسم هست.'),
(4954, 55, 53, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4955, 55, 54, 'بر بسترهايى كه آسترشان از استبرق است تكيه زده‌اند و ميوه‌هاى آن دو بهشت در دسترسشان باشد.'),
(4956, 55, 55, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4957, 55, 56, 'در آنجاست زنانى كه جز به شوهر خويش ننگرند، و پيش از بهشتيان هيچ آدمى و جنى به آنها دست نزده است.'),
(4958, 55, 57, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4959, 55, 58, 'همانند ياقوت و مرجان.'),
(4960, 55, 59, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4961, 55, 60, 'آيا پاداش نيكى جز نيكى است؟'),
(4962, 55, 61, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4963, 55, 62, 'و جز آنها دو بهشت ديگر است.'),
(4964, 55, 63, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4965, 55, 64, 'از شدت سبزى متمايل به سياهى.'),
(4966, 55, 65, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4967, 55, 66, 'در آن دو، دو چشمه همواره جوشنده هست.'),
(4968, 55, 67, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4969, 55, 68, 'در آن دو، ميوه هست و نخل هست و انار هست.'),
(4970, 55, 69, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4971, 55, 70, 'در آنجا زنانى است نيك‌سيرت و زيباروى.'),
(4972, 55, 71, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4973, 55, 72, 'حورانى مستور در خيمه‌ها.'),
(4974, 55, 73, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4975, 55, 74, 'پيش از بهشتيان هيچ آدمى و جنى به آنها دست نزده است.'),
(4976, 55, 75, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4977, 55, 76, 'بر بالشهاى سبز و فرشهاى نيكو تكيه مى‌زنند.'),
(4978, 55, 77, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4979, 55, 78, 'بزرگ است نام پروردگار تو آن صاحب جلالت و اكرام.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'چون قيامت واقع شود،'),
(4981, 56, 2, 'كه در واقع‌شدنش هيچ دروغ نيست،'),
(4982, 56, 3, 'گروهى را خواركننده است و گروهى را برافرازنده.'),
(4983, 56, 4, 'آنگاه كه زمين به سختى بلرزد،'),
(4984, 56, 5, 'و كوه‌ها به تمامى متلاشى شوند،'),
(4985, 56, 6, 'و چون غبارى پراكنده گردند،'),
(4986, 56, 7, 'شما سه گروه باشيد:'),
(4987, 56, 8, 'يكى اهل سعادت. اهل سعادت چه حال دارند؟'),
(4988, 56, 9, 'ديگر، اهل شقاوت. اهل شقاوت چه حال دارند؟'),
(4989, 56, 10, 'سه ديگر، آنها كه سبقت جسته بودند و اينك پيش افتاده‌اند.'),
(4990, 56, 11, 'اينان مقرّبانند،'),
(4991, 56, 12, 'در بهشتهاى پرنعمت.'),
(4992, 56, 13, 'گروهى از پيشينيان،'),
(4993, 56, 14, 'و اندكى از آنها كه از پى آمده‌اند،'),
(4994, 56, 15, 'بر تختهايى مرصّع،'),
(4995, 56, 16, 'روبروى هم بر آنها تكيه زده‌اند.'),
(4996, 56, 17, 'پسرانى همواره جوان گردشان مى‌چرخند،'),
(4997, 56, 18, 'با قدحها و ابريقها و جامهايى از شرابى كه در جويها جارى است.'),
(4998, 56, 19, 'از نوشيدنش نه سردرد گيرند و نه بيهوش شوند.'),
(4999, 56, 20, 'و ميوه‌هايى كه خود برمى‌گزينند.'),
(5000, 56, 21, 'و گوشت پرنده هر چه بخواهند.'),
(5001, 56, 22, 'و حوران درشت چشم،'),
(5002, 56, 23, 'همانند مرواريدهايى در صدف.'),
(5003, 56, 24, 'همه به پاداش كارهايى كه مى‌كرده‌اند.'),
(5004, 56, 25, 'در آنجا نه سخن لغو شنوند و نه گناه‌آلود.'),
(5005, 56, 26, 'جز يك سخن: سلام، سلام.'),
(5006, 56, 27, 'اما اصحاب سعادت، اصحاب سعادت چه حال دارند؟'),
(5007, 56, 28, 'در زير درخت سدر بى‌خار،'),
(5008, 56, 29, 'و درخت موزى كه ميوه‌اش بر يكديگر چيده‌شده،'),
(5009, 56, 30, 'و سايه‌اى دايم،'),
(5010, 56, 31, 'و آبى همواره جارى،'),
(5011, 56, 32, 'و ميوه‌اى بسيار،'),
(5012, 56, 33, 'كه نه منقطع مى‌گردد و نه كس را از آن باز دارند.'),
(5013, 56, 34, 'و زنانى ارجمند.'),
(5014, 56, 35, 'آن زنان را ما بيافريديم، آفريدنى.'),
(5015, 56, 36, 'و دوشيزگان ساختيم.'),
(5016, 56, 37, 'معشوق همسران خويشند،'),
(5017, 56, 38, 'براى اصحاب سعادت.'),
(5018, 56, 39, 'گروهى از پيشينيان،'),
(5019, 56, 40, 'و گروهى كه از پى آمده‌اند.'),
(5020, 56, 41, 'اما اصحاب شقاوت، اصحاب شقاوت چه حال دارند؟'),
(5021, 56, 42, 'در باد سموم و آب جوشانند.'),
(5022, 56, 43, 'در سايه‌اى از دود سياه،'),
(5023, 56, 44, 'نه سرد و نه خوش،'),
(5024, 56, 45, 'اينان پيش از اين در ناز و نعمت بودند.'),
(5025, 56, 46, 'و بر گناهان بزرگ اصرار مى‌ورزيدند.'),
(5026, 56, 47, 'و مى‌گفتند: آيا زمانى كه ما مُرديم و خاك و استخوان شديم باز هم ما را زنده مى‌كنند،'),
(5027, 56, 48, 'يا نياكان ما را؟'),
(5028, 56, 49, 'بگو: همه را، آنان كه از پيش بوده‌اند و آنها كه از پى‌شان آمده بودند،'),
(5029, 56, 50, 'همه در وعده‌گاه آن روز معين خواهند بود.'),
(5030, 56, 51, 'آنگاه شما اى گمراهان تكذيب‌كننده،'),
(5031, 56, 52, 'از درختان زقّوم خواهيد خورد.'),
(5032, 56, 53, 'و شكمهاى خود را از آن پر خواهيد كرد.'),
(5033, 56, 54, 'و بر سر آن آب جوشان خواهيد نوشيد.'),
(5034, 56, 55, 'چنان مى‌نوشيد كه شتر تشنه آب مى‌نوشد.'),
(5035, 56, 56, 'اين است غذايشان در روز جزا.'),
(5036, 56, 57, 'ما شما را آفريده‌ايم؛ پس چرا تصديق نمى‌كنيد؟'),
(5037, 56, 58, 'آيا آن منى را كه برون مى‌ريزيد ديده‌ايد؟'),
(5038, 56, 59, 'آيا شما او را مى‌آفرينيد يا ما آفريننده‌ايم؟'),
(5039, 56, 60, 'ما مرگ را بر شما مقدر ساختيم و ناتوان از آن نيستيم كه‌'),
(5040, 56, 61, 'به جاى شما قومى همانند شما بياوريم و شما را به صورتى كه از آن بى‌خبريد از نو بيافرينيم.'),
(5041, 56, 62, 'شما از آفرينش نخست آگاهيد؛ چرا به يادش نياوريد؟'),
(5042, 56, 63, 'آيا چيزى را كه مى‌كاريد ديده‌ايد؟'),
(5043, 56, 64, 'آيا شما مى‌رويانيدش يا ما روياننده‌ايم؟'),
(5044, 56, 65, 'اگر مى‌خواستيم خاشاكش مى‌ساختيم تا در شگفت بمانيد.'),
(5045, 56, 66, 'گويند: ما ثروت بر باد دادگانيم؛'),
(5046, 56, 67, 'ما بى‌نصيب ماندگانيم.'),
(5047, 56, 68, 'آيا آبى را كه مى‌نوشيد ديده‌ايد؟'),
(5048, 56, 69, 'آيا شما آن را از ابر فرو مى‌فرستيد يا ما فرو فرستنده‌ايم؟'),
(5049, 56, 70, 'اگر مى‌خواستيم آن را تلخ مى‌گردانيديم. پس چرا سپاس نمى‌گوييد؟'),
(5050, 56, 71, 'آيا آن آتشى را كه مى‌افروزيد ديده‌ايد؟'),
(5051, 56, 72, 'آيا درختش را شما آفريده‌ايد يا ما آفريننده‌ايم؟'),
(5052, 56, 73, 'ما آن را هشدارى و براى مسافران رهنورد، متاعى ساختيم.'),
(5053, 56, 74, 'به نام پروردگار بزرگ خود تسبيح گوى.'),
(5054, 56, 75, 'پس سوگند به غروبگاه ستارگان.'),
(5055, 56, 76, 'و اين سوگندى است -اگر بدانيد- بزرگ.'),
(5056, 56, 77, 'هر آينه، اين قرآنى است گرامى‌قدر،'),
(5057, 56, 78, 'در كتابى مكنون.'),
(5058, 56, 79, 'كه جز پاكان دست بر آن نزنند.'),
(5059, 56, 80, 'نازل شده از جانب پروردگار جهانيان است.'),
(5060, 56, 81, 'آيا اين سخن را دروغ مى‌انگاريد؟'),
(5061, 56, 82, 'و نصيب خود را در دروغ انگاشتن آن قرار مى‌دهيد؟'),
(5062, 56, 83, 'پس چرا آنگاه كه جان به گلوگاه رسد،'),
(5063, 56, 84, 'و شما در اين هنگام مى‌نگريد، تكذيب نمى‌كنيد؟'),
(5064, 56, 85, 'ما از شما به او نزديك‌تريم ولى شما نمى‌بينيد.'),
(5065, 56, 86, 'اگر قيامت را باور نداريد،'),
(5066, 56, 87, 'اگر راست مى‌گوييد، بازش گردانيد.'),
(5067, 56, 88, 'اما اگر از مقربان باشد،'),
(5068, 56, 89, 'براى اوست آسايش و روزى و بهشت پرنعمت.'),
(5069, 56, 90, 'و اما اگر از اصحاب سعادت باشد:'),
(5070, 56, 91, 'پس تو را از اصحاب سعادت سلام است.'),
(5071, 56, 92, 'و اما اگر از تكذيب‌كنندگان گمراه باشد،'),
(5072, 56, 93, 'به آب جوشان مهمانش كنند،'),
(5073, 56, 94, 'و به دوزخش درآورند.'),
(5074, 56, 95, 'اين سخن سخنى راست و يقين است.'),
(5075, 56, 96, 'پس به نام پروردگار بزرگت تسبيح گوى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'خداوند را تسبيح مى‌گويند، هر چه در آسمانها و زمين است، و او پيروزمند و حكيم است.'),
(5077, 57, 2, 'فرمانروايى آسمانها و زمين از آن اوست. زنده مى‌كند و مى‌ميراند و بر هر چيز تواناست.'),
(5078, 57, 3, 'اوست اوّل و آخر و ظاهر و باطن، و او به هر چيزى داناست.'),
(5079, 57, 4, 'اوست كه آسمانها و زمين را در شش روز آفريد. سپس به عرش پرداخت. هر چه را در زمين فرو رود و هر چه را از زمين بيرون آيد و هر چه را از آسمان فرو آيد و هر چه را در آسمان بالا رود، مى‌داند. و هر جا كه باشيد همراه شماست و به هر كارى كه مى‌كنيد بيناست.'),
(5080, 57, 5, 'فرمانروايى آسمانها و زمين از آن اوست، و همه كارها به خدا بازمى‌گردد.'),
(5081, 57, 6, 'از شب مى‌كاهد و به روز مى‌افزايد و از روز مى‌كاهد و به شب مى‌افزايد. و به هر چه در دلها مى‌گذرد آگاه است.'),
(5082, 57, 7, 'به خدا و پيامبرش ايمان بياوريد و از آن مال كه به وراثت به شما رسانده است انفاق كنيد. از ميان شما هر كه را ايمان آورده و انفاق كرده، مزدى فراوانش دهند.'),
(5083, 57, 8, 'چيست شما را كه به خدا ايمان نمى‌آوريد و حال آنكه پيامبر شما را دعوت مى‌كند كه به پروردگارتان ايمان بياوريد، و اگر باور داريد از شما پيمان گرفته است؟'),
(5084, 57, 9, 'اوست آن خدايى كه بر بنده خود آيات روشن را نازل مى‌كند تا شما را از تاريكى به روشنايى آورد. زيرا خدا به شما مُشفق و مهربان است.'),
(5085, 57, 10, 'و چرا در راه خدا انفاق نمى‌كنيد و حال آنكه از آنِ خداست ميراث آسمانها و زمين؟ از ميان شما آنان كه پيش از فتح انفاق كرده و به جنگ رفته با آنان كه بعد از فتح انفاق كرده‌اند و به جنگ رفته‌اند برابر نيستند. درَجت آنان فراتر است. و خدا به همه وعده نيك مى‌دهد. و به هر كارى كه مى‌كنيد آگاه است.'),
(5086, 57, 11, 'كيست كه خدا را قرض‌الحسنه دهد تا براى او دو چندانش كند، و او را پاداشى نيكو باشد.'),
(5087, 57, 12, 'روزى كه مردان مؤمن و زنان مؤمن را ببينى كه نورشان پيشاپيش و در سمت راستشان مى‌رود. در آن روز بشارتتان به بهشتهايى است كه در آن نهرها روان است و در آن جاويد خواهيد ماند، و اين كاميابى بزرگى است.'),
(5088, 57, 13, 'روزى كه مردان منافق و زنان منافق به كسانى كه ايمان آورده‌اند مى‌گويند: درنگى كنيد تا از نورتان فروغى گيريم. گويند: به دنيا بازگرديد و از آنجا نور بطلبيد. ميانشان ديوارى برآورند كه بر آن ديوار درى باشد؛ درون آن رحمت باشد و بيرون آن عذاب.'),
(5089, 57, 14, 'و آنها را ندا دهند كه آيا ما با شما همراه نبوديم؟ مى‌گويند: بلى، اما شما خويشتن را در بلا افكنديد و به انتظار نشستيد و در شك بوديد و آرزوها شما را بفريفت تا آنگاه كه فرمان خدا دررسيد و شيطان به خدا مغرورتان كرد.'),
(5090, 57, 15, 'و امروز نه از شما فديه‌اى پذيرند و نه از كافران. جايگاهتان آتش است. آتش سزاوار شماست. و بد سرانجامى است.'),
(5091, 57, 16, 'آيا مؤمنان را وقت آن نرسيده است كه دلهايشان در برابر ياد خدا و آن سخن حق كه نازل شده است، خاشع شود؟ همانند آن مردمى نباشند كه پيش از اين كتابشان داديم و چون مدتى برآمد دلهايشان سخت شد و بسيارى نافرمان شدند.'),
(5092, 57, 17, 'بدانيد كه خدا زمين را پس از مردنش زنده مى‌كند. ما آيات را برايتان به روشنى بيان كرديم، باشد كه به عقل دريابيد.'),
(5093, 57, 18, 'خدا به مردان صدقه‌دهنده و زنان صدقه‌دهنده‌اى كه به خدا قرض‌الحسنه مى‌دهند، دو چندان پاداش مى‌دهد؛ و نيز آنها را اجرى نيكوست.'),
(5094, 57, 19, 'كسانى كه به خدا و پيامبرانش ايمان آورده‌اند، راستگويان و راستكارانند. و شهيدان در نزد پروردگارشان هستند. صاحب پاداش و نور خويشند. و آنها كه كافرند و آيات ما را تكذيب مى‌كنند، در جهنمند.'),
(5095, 57, 20, 'بدانيد كه زندگى اينجهانى بازيچه است و بيهودگى و آرايش و فخرفروشى و افزون‌جويى در اموال و اولاد. همانند بارانى به وقت است كه روييدنيهايش كافران را به شگفت افكند. سپس پژمرده مى‌شود و بينى كه زرد گشته است و خاشاك شده است. و در آخرت نصيب گروهى عذاب سخت است و نصيب گروهى آمرزش خدا و خشنودى او. و زندگى دنيا جز متاعى فريبنده نيست.'),
(5096, 57, 21, 'براى رسيدن به آمرزش پروردگارتان و بهشتى كه پهناى آن همسان پهناى آسمانها و زمين است، بر يكديگر پيشى گيريد. اين بهشت براى كسانى كه به خدا و پيامبرانش ايمان آورده‌اند، مهيا شده است. اين بخشايشى است از جانب خدا كه به هر كه مى‌خواهد ارزانيش مى‌دارد، كه خدا صاحب بخشايشى بزرگ است.'),
(5097, 57, 22, 'هيچ مصيبتى به مال يا به جانتان نرسد مگر پيش از آنكه بيافرينيمش، در كتابى نوشته شده است. و اين بر خدا آسان است؛'),
(5098, 57, 23, 'تا بر آنچه از دستتان مى‌رود اندوهگين نباشيد و بدانچه به دستتان مى‌آيد شادمانى نكنيد. و خدا هيچ متكبر خودستاينده‌اى را دوست ندارد:'),
(5099, 57, 24, 'آنان كه بخل مى‌ورزند و مردم را به بخل وامى‌دارند. و هر كه رويگردان مى‌شود، بداند كه خدا بى‌نياز و ستودنى است.'),
(5100, 57, 25, 'ما پيامبرانمان را با دليلهاى روشن فرستاديم و با آنها كتاب و ترازو را نيز نازل كرديم تا مردم به عدالت عمل كنند. و آهن را كه در آن نيرويى سخت و منافعى براى مردم است فروفرستاديم، تا خدا بداند چه كسى به ناديده، او و پيامبرانش را يارى مى‌كند. زيرا خدا توانا و پيروزمند است.'),
(5101, 57, 26, 'ما نوح و ابراهيم را به رسالت فرستاديم و در ميان فرزندانشان نبوت و كتاب نهاديم. بعضيشان هدايت‌يافتگان بودند ولى بيشترينشان نافرمانان.'),
(5102, 57, 27, 'سپس پيامبرانمان را پس از ايشان فرستاديم. و عيسى بن مريم را از پى آنها روانه كرديم و به او انجيل را داديم، و در دل پيروانش رأفت و رحمت نهاديم. و رهبانيتى كه به بدعت آورده‌اند ما بر آنها مقرر نكرده‌ايم، اما در آن خشنودى خدا مى‌جستند، ولى حق آن را بسزا نگزاردند. ما از آن ميان به كسانى كه ايمان آورده بودند، پاداش داديم ولى بيشترينشان نافرمان بودند.'),
(5103, 57, 28, 'اى كسانى كه ايمان آورده‌ايد، از خدا بترسيد و به پيامبرش ايمان بياوريد تا شما را از رحمت خويش دو بار مزد بدهد. و شما را نورى عطا كند كه در روشنايى آن راه بجوييد و بيامرزدتان كه خدا آمرزنده مهربان است.'),
(5104, 57, 29, 'تا اهل كتاب بدانند كه به بخشايش خداوند هيچ دسترس ندارند و اين بخشايش به دست اوست كه به هر كه خواهد ارزانى مى‌دارد، و خدا را بخشايشى عظيم است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'خدا سخن زنى را كه در باره شوهرش با تو به مجادله آمده است و به خدا شِكوه مى‌كند، شنيد. و خدا گفت و گوى شما را مى‌شنود، زيرا شنوا و بيناست.'),
(5106, 58, 2, 'از ميان شما كسانى كه زنانشان را ظِهار مى‌كنند بدانند كه زنانشان مادرانشان نشوند؛ مادرانشان فقط زنانى هستند كه آنها را زاييده‌اند، و سخنى ناپسند و دروغ است كه مى‌گويند. و خدا از گناهان درگذرنده است و آمرزنده است.'),
(5107, 58, 3, 'آنهايى كه زنانشان را ظهار مى‌كنند، آنگاه از آنچه گفته‌اند پشيمان مى‌شوند، پيش از آنكه با يكديگر تماس يابند، بايد بنده‌اى آزاد كنند. اين پندى است كه به شما مى‌دهند و خدا به كارهايى كه مى‌كنيد آگاه است.'),
(5108, 58, 4, 'اما كسانى كه بنده‌اى نيابند، پيش از آنكه با يكديگر تماس يابند، بايد دو ماه پى در پى روزه بدارند. و آن كه نتواند، شصت مسكين را طعام دهد. و اين بدان سبب است كه به خدا و پيامبرش ايمان بياوريد. اينها حدود خداست. و براى كافران عذابى است دردآور.'),
(5109, 58, 5, 'كسانى كه با خدا و پيامبرش مخالفت مى‌ورزند ذليل و خوار مى‌شوند، همچنان كه پيشينيانشان خوار شده‌اند. ما آياتى روشن نازل كرديم و كافران را عذابى است خواركننده.'),
(5110, 58, 6, 'روزى كه خدا همگان را زنده مى‌كند، آنان را از كارى كه كرده‌اند آگاه مى‌سازد. خدا اعمال آنها را شماره كرده است، هر چند خود از ياد برده‌اند، و خدا ناظر بر هر چيزى است.'),
(5111, 58, 7, 'آيا ندانسته‌اى كه خدا هر چه را در آسمانها و زمين است مى‌داند؟ هيچ سه كس با هم نجوا نكنند مگر آنكه خداوند چهارمين آنهاست و هيچ پنج كس نباشند مگر آنكه خداوند ششمين آنهاست. و نه كمتر از اين -هر جا كه باشند- و نه بيشتر از اين، مگر آنكه خدا با آنهاست. سپس همه را در روز قيامت به كارهايى كه كرده‌اند، آگاه مى‌كند. زيرا خدا بر همه چيز آگاه است.'),
(5112, 58, 8, 'آيا آنها را كه از نجوا منع شده بودند نديدى كه كارى را كه از آن منع شده بودند از سر گرفتند و باز هم براى گناه و دشمنى و نافرمانى از پيامبر با هم نجوا مى‌كنند؟ و چون نزد تو مى‌آيند، به گونه‌اى تو را سلام مى‌گويند كه خدا تو را بدان گونه سلام نگفته است، و در دل مى‌گويند: چرا خدا ما را بدانچه مى‌گوييم عذاب نمى‌كند؟ جهنم برايشان كافى است. بدان داخل مى‌شوند، و اين بد سرانجامى است.'),
(5113, 58, 9, 'اى كسانى كه ايمان آورده‌ايد، اگر با يكديگر نجوا مى‌كنيد، در باب گناه و دشمنى و نافرمانى از پيامبر نجوا مكنيد، بلكه در باب نيكى و پرهيزگارى نجوا كنيد. از آن خدايى كه همگان نزد او گرد مى‌آييد بترسيد.'),
(5114, 58, 10, 'هر آينه نجواكردن كار شيطان است كه مى‌خواهد مؤمنان را محزون كند و حال آنكه هيچ زيانى، جز به فرمان خداوند، به آنها نمى‌رساند. و مؤمنان بايد كه بر خدا توكل كنند.'),
(5115, 58, 11, 'اى كسانى كه ايمان آورده‌ايد، چون شما را گويند در مجالس جاى باز كنيد، جاى باز كنيد تا خدا در كار شما گشايش دهد. و چون گويند كه برخيزيد، برخيزيد. خدا آنهايى را كه ايمان آورده‌اند و كسانى را كه دانش يافته‌اند به درجاتى برافرازد، و خدا به كارهايى كه مى‌كنيد آگاه است.'),
(5116, 58, 12, 'اى كسانى كه ايمان آورده‌ايد، چون خواهيد كه با پيامبر نجوا كنيد، پيش از نجواكردنتان صدقه بدهيد. اين براى شما بهتر و پاكيزه‌تر است. و اگر براى صدقه چيزى نيافتيد، خدا آمرزنده و مهربان است.'),
(5117, 58, 13, 'آيا ترسيديد پيش از نجوا كردن صدقه‌ها بدهيد؟ حال كه صدقه نداده‌ايد و خدا هم توبه شما را پذيرفته است، پس نماز بگزاريد و زكات بدهيد و از خدا و پيامبرش اطاعت كنيد، كه خدا به كارهايى كه مى‌كنيد آگاه است.'),
(5118, 58, 14, 'آيا نديده‌اى آن كسان را كه با مردمى كه خدا بر آنها خشم گرفته بود، دوستى ورزيدند؟ اينان نه از شمايند و نه از ايشان. و خود مى‌دانند كه بر دروغ سوگند مى‌خوردند.'),
(5119, 58, 15, 'خدا بر ايشان عذابى سخت آماده كرده است. زيرا كارهايى كه مى‌كنند ناپسند است.'),
(5120, 58, 16, 'از سوگندهايشان سپرى ساخته‌اند. و از راه خدا رويگردان شده‌اند. پس براى آنهاست عذابى خواركننده.'),
(5121, 58, 17, 'اموال و اولادشان برايشان در برابر خشم خدا سود نكند. اهل جهنمند و در آن جاودان.'),
(5122, 58, 18, 'روزى كه خدا همه آنها را زنده مى‌كند، همچنان كه براى شما قسم مى‌خوردند براى او هم قسم خواهند خورد. و مى‌پندارند كه سودى خواهند برد. آگاه باشيد كه دروغگويانند.'),
(5123, 58, 19, 'شيطان بر آنها چيره شده است و نام خدا را از يادشان برده است. ايشان حزب شيطانند. آگاه باش كه حزب شيطان زيان‌كنندگانند.'),
(5124, 58, 20, 'كسانى كه با خدا و پيامبرش مخالفت مى‌ورزند در زمره ذليل‌شدگانند.'),
(5125, 58, 21, 'خدا مقرر داشته كه: البته من و پيامبرانم پيروز مى‌شويم. زيرا خدا توانا و پيروزمند است.'),
(5126, 58, 22, 'نمى‌يابى مردمى را كه به خدا و روز قيامت ايمان آورده باشند، ولى با كسانى كه با خدا و پيامبرش مخالفت مى‌ورزند دوستى كنند، هر چند آن مخالفان، پدران يا فرزندان يا برادران و يا قبيله آنها باشند. خدا بر دلشان رقم ايمان زده و به روحى از خود ياريشان كرده است و آنها را به بهشتهايى كه در آن نهرها جارى است درآورد. در آنجا جاودانه باشند. خدا از آنها خشنود است و آنان نيز از خدا خشنودند. اينان حزب خدايند، آگاه باش كه حزب خدا رستگارانند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'خداوند را تسبيح گويند هر چه در آسمانها و هر چه در زمين است. و اوست پيروزمند و حكيم.'),
(5128, 59, 2, 'اوست آن خدايى كه نخستين بار كسانى از اهل كتاب را كه كافر بودند، از خانه‌هايشان بيرون راند و شما نمى‌پنداشتيد كه بيرون روند. آنها نيز مى‌پنداشتند حصارهاشان را توان آن هست كه در برابر خدا نگهدارشان باشد. خدا از سويى كه گمانش را نمى‌كردند بر آنها تاخت آورد و در دلشان وحشت افكند، چنان كه خانه‌هاى خود را به دست خود و به دست مؤمنان خراب مى‌كردند. پس اى اهل بصيرت، عبرت بگيريد.'),
(5129, 59, 3, 'اگر نه آن بود كه خدا ترك ديار را بر آنها مقرر كرده بود، در دنيا به عذاب گرفتارشان مى‌كرد و در آخرتشان عذاب آتش است.'),
(5130, 59, 4, 'و اين به كيفر آن بود كه با خدا و پيامبرش مخالفت ورزيدند؛ و هر كه با خدا مخالفت مى‌ورزد بداند كه خدا به سختى عقوبت مى‌كند.'),
(5131, 59, 5, 'هر درخت خرمايى را كه بريديد، يا آن را بر ريشه‌اش باقى گذارديد، به فرمان خدا بود تا نافرمانان خوار گردند.'),
(5132, 59, 6, 'و آنچه خدا از داراييشان به پيامبر خود غنيمت داد، آن نبود كه شما با اسب يا شترى بر آن تاخته بوديد؛ بلكه خدا پيامبرانش را بر هر كه بخواهد مسلط مى‌سازد، و خدا بر هر چيزى قادر است.'),
(5133, 59, 7, 'آن غنيمتى كه خدا از مردم قريه‌ها نصيب پيامبرش كرده است از آن خداست و پيامبر و خويشاوندان و يتيمان و مسكينان و مسافران در راه مانده، تا ميان توانگرانتان دست به دست نشود. هر چه پيامبر به شما داد بستانيد، و از هر چه شما را منع كرد اجتناب كنيد. و از خدا بترسيد كه خدا سخت عقوبت است.'),
(5134, 59, 8, 'نيز غنايم از آن مهاجران فقيرى است كه از سرزمينشان رانده شده‌اند و آنها در طلب فضل و خشنودى خدايند و خدا و پيامبرش را يارى مى‌كنند، اينان راستگويانند.'),
(5135, 59, 9, 'و كسانى كه پيش از آمدن مهاجران در ديار خود بوده‌اند و ايمان آورده‌اند، آنهايى را كه به سويشان مهاجرت كرده‌اند دوست مى‌دارند. و از آنچه مهاجران را داده مى‌شود در دل احساس حسد نمى‌كنند، و ديگران را بر خويش ترجيح مى‌دهند هر چند خود نيازمند باشند. و آنان كه از بخل خويش در امان مانده باشند رستگارانند.'),
(5136, 59, 10, 'كسانى كه از پس ايشان آمده‌اند، مى‌گويند: اى پروردگار ما، ما و برادران ما را كه پيش از ما ايمان آورده‌اند بيامرز و كينه كسانى را كه ايمان آورده‌اند، در دل ما جاى مده. اى پروردگار ما، تو مشفق و مهربان هستى.'),
(5137, 59, 11, 'آيا منافقان را نديده‌اى كه به ياران خود از اهل كتاب كه كافر بودند مى‌گفتند: اگر شما را اخراج كنند، ما نيز با شما بيرون مى‌آييم و به زيان شما از هيچ كس اطاعت نخواهيم كرد و اگر با شما جنگيدند ياريتان مى‌كنيم؟ و حال آنكه خدا مى‌داند كه دروغ مى‌گويند.'),
(5138, 59, 12, 'اگر آنها را اخراج كنند، با آنها بيرون نشوند. و اگر به جنگشان بيايند ياريشان نمى‌كنند، و اگر هم به ياريشان برخيزند به دشمن پشت مى‌كنند. پس روى يارى نبينند.'),
(5139, 59, 13, 'وحشت شما در دل آنها بيشتر از بيم خداست. زيرا مردمى هستند كه به فهم در نمى‌يابند.'),
(5140, 59, 14, 'آنان همگى با شما جنگ نپيوندند مگر در قريه‌هايى كه بارويشان باشد، يا از پس ديوار. آنها سخت با يكديگر دشمنند. تو يكدل مى‌پنداريشان و حال آنكه دلهاشان از هم جداست، كه مردمى بى‌خردند.'),
(5141, 59, 15, 'همانند آنهايى هستند كه چندى پيش وبال گناه خويش را چشيدند. و به عذابى دردآور نيز گرفتار خواهند شد.'),
(5142, 59, 16, 'همانند شيطان كه به آدمى گفت: كافر شو. چون كافر شد، گفت: من از تو بيزارم. من از خدا، آن پروردگار جهانيان مى‌ترسم.'),
(5143, 59, 17, 'سرانجامشان آن شد كه هر دو به آتش افتند و جاودانه در آن باشند. اين است كيفر ستمكاران.'),
(5144, 59, 18, 'اى كسانى كه ايمان آورده‌ايد، از خدا بترسيد. و هر كس بايد بنگرد كه براى فردايش چه فرستاده است. از خدا بترسيد كه خدا به كارهايى كه مى‌كنيد آگاه است.'),
(5145, 59, 19, 'از آن كسان مباشيد كه خدا را فراموش كردند و خدا نيز چنان كرد تا خود را فراموش كنند. ايشان نافرمانند.'),
(5146, 59, 20, 'اهل آتش و اهل بهشت با هم برابر نيستند. اهل بهشت خود كاميافتگانند.'),
(5147, 59, 21, 'اگر اين قرآن را بر كوه نازل مى‌كرديم، از خوف خدا آن را ترسيده و شكاف خورده مى‌ديدى. و اين مثالهايى است كه براى مردم مى‌آوريم، شايد به فكر فرو روند.'),
(5148, 59, 22, 'اوست خدايى يگانه. هيچ خدايى جز او نيست. داناى نهان و آشكار و بخشاينده و مهربان است.'),
(5149, 59, 23, 'اوست خداى يگانه كه هيچ خداى ديگرى جز او نيست، فرمانروا است، پاك است، عارى از هر عيب است، ايمنى‌بخش است، نگهبان است، پيروزمند است، باجبروت است و بزرگوار است. و از هر چه براى او شريك قرار مى‌دهند منزه است.'),
(5150, 59, 24, 'اوست خدايى كه آفريدگار است، موجد و صورت‌بخش است، اسمهاى نيكو از آن اوست. هر چه در آسمانها و زمين است تسبيحگوى او هستند و او پيروزمند و حكيم است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اى كسانى كه ايمان آورده‌ايد، دشمن من و دشمن خود را به دوستى اختيار مكنيد. شما با آنان طرح دوستى مى‌افكنيد و حال آنكه ايشان به سخن حقى كه بر شما آمده است ايمان ندارند. و بدان سبب كه به خدا، پروردگار خويش ايمان آورده بوديد، پيامبر و شما را بيرون راندند. اگر براى جهاد در راه من و طلب رضاى من بيرون آمده‌ايد، در نهان با آنها دوستى مكنيد؛ و من به هر چه پنهان مى‌داريد يا آشكار مى‌سازيد آگاهترم. و هر كه چنين كند، از راه راست منحرف گشته است.'),
(5152, 60, 2, 'اگر بر شما دست يابند، دشمنى مى‌كنند و به آزارتان دست و زبان مى‌گشايند و دوست دارند كه شما نيز كافر گرديد.'),
(5153, 60, 3, 'در روز قيامت نه خويشاوندان برايتان سود كنند، نه فرزندانتان. خدا ميانتان جدايى مى‌افكند و اعمالتان را مى‌بيند.'),
(5154, 60, 4, 'ابراهيم و كسانى كه با وى بودند، آنگاه كه به قوم خود گفتند كه ما از شما و از آنچه جز خداى يكتا مى‌پرستيد بيزاريم و شما را كافر مى‌شمريم و ميان ما و شما هميشه دشمنى و كينه‌توزى خواهد بود تا وقتى كه به خداى يكتا ايمان بياوريد، برايتان نيكو مقتدايى بودند. مگر آنگاه كه ابراهيم پدرش را گفت كه براى تو آمرزش مى‌طلبم، زيرا نمى‌توانم عذاب خدا را از تو دفع كنم. اى پروردگار ما، بر تو توكل كرديم و به تو روى آورديم و سرانجام تو هستى.'),
(5155, 60, 5, 'اى پروردگار ما، ما را چنان مكن كه كافران خوار دارندمان. و ما را بيامرز كه تو پيروزمند و حكيمى.'),
(5156, 60, 6, 'آنها، نيكو مقتدايى هستند براى شما: براى كسانى كه به خدا و روز قيامت اميد دارند. و هر كه رويگردان شود، خدا بى‌نياز و ستودنى است.'),
(5157, 60, 7, 'شايد خدا ميان شما و كسانى كه با آنها دشمنى مى‌ورزيد دوستى پديد آورد. خدا قادر است و خدا آمرزنده و مهربان است.'),
(5158, 60, 8, 'خدا شما را از نيكى‌كردن و عدالت‌ورزيدن با آنان كه با شما در دين نجنگيده‌اند و از سرزمينتان بيرون نرانده‌اند، باز نمى‌دارد. خدا كسانى را كه به عدالت رفتار مى‌كنند دوست دارد.'),
(5159, 60, 9, 'جز اين نيست كه خدا از دوستى‌ورزيدن با كسانى كه با شما در دين جنگيده‌اند و از سرزمين خود بيرونتان رانده‌اند يا در بيرون‌راندنتان همدستى كرده‌اند شما را باز دارد. و هر كه با آنها دوستى ورزد از ستمكاران خواهد بود.'),
(5160, 60, 10, 'اى كسانى كه ايمان آورده‌ايد، چون زنان مؤمنى كه مهاجرت كرده‌اند به نزدتان آيند، بيازماييدشان. خدا به ايمانشان داناتر است. پس، اگر دانستيد كه ايمان آورده‌اند، نزد كافران بازشان مگردانيد. زيرا اينان بر مردان كافر حلال نيستند و مردان كافر نيز بر آنها حلال نيستند. و هر چه آن كافران براى اين گونه زنان هزينه كرده‌اند بپردازيد. و اگر آنها را نكاح كنيد و مهرشان را بدهيد مرتكب گناهى نشده‌ايد. و زنان كافر خود را نگه مداريد. و هر چه هزينه كرده‌ايد از مردان كافر بخواهيد و آنها نيز هر چه هزينه كرده‌اند از شما بخواهند. اين حكم خداست. خدا ميان شما حكم مى‌كند و او دانا و حكيم است.'),
(5161, 60, 11, 'و اگر يكى از زنان شما به كفار پيوست و آنگاه به انتقام از كافران برخاستيد، به كسانى كه زنانشان رفته‌اند به ميزان هزينه‌اى كه كرده‌اند بپردازيد و از خدايى كه به او ايمان آورده‌ايد بترسيد.'),
(5162, 60, 12, 'اى پيامبر، اگر زنان مؤمن نزد تو آمدند تا بيعت كنند، بدين شرط كه هيچ كس را با خدا شريك نكنند و دزدى نكنند و زنا نكنند و فرزندان خود را نكشند و فرزندى را كه از آن شوهرشان نيست به دروغ به او نسبت ندهند و در كارهاى نيك نافرمانى تو نكنند، با آنها بيعت كن و برايشان از خدا آمرزش بخواه، كه خدا آمرزنده و مهربان است.'),
(5163, 60, 13, 'اى كسانى كه ايمان آورده‌ايد، با مردمى كه خدا بر آنها خشم گرفته است دوستى مكنيد. اينان از آخرت نوميدند، همچنان كه آن كافرانى كه اينك در گورند از آخرت نوميدند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'خدا را تسبيح گويند هر چه در آسمانها و در زمين است، و اوست پيروزمند و حكيم.'),
(5165, 61, 2, 'اى كسانى كه ايمان آورده‌ايد، چرا سخنانى مى‌گوييد كه به كارشان نمى‌بنديد؟'),
(5166, 61, 3, 'خداوند سخت به خشم مى‌آيد كه چيزى بگوييد و به جاى نياوريد.'),
(5167, 61, 4, 'خدا دوست دارد كسانى را كه در راه او در صفى، همانند ديوارى كه اجزايش را با سُرب به هم پيوند داده باشند، مى‌جنگند.'),
(5168, 61, 5, 'و موسى به قوم خود گفت: اى قوم من، چرا مرا مى‌آزاريد، حال آنكه مى‌دانيد كه من پيامبر خدا بر شما هستم؟ چون از حق رويگردان شدند خدا نيز دلهاشان را از حق بگردانيد. و خدا مردم نافرمان را هدايت نمى‌كند.'),
(5169, 61, 6, 'و عيسى‌بن‌مريم گفت: اى بنى اسرائيل، من پيامبر خدا بر شما هستم. توراتى را كه پيش از من بوده است تصديق مى‌كنم، و به پيامبرى كه بعد از من مى‌آيد و نامش احمد است، بشارتتان مى‌دهم. چون آن پيامبر با آيات روشن خود آمد، گفتند: اين جادويى است آشكار.'),
(5170, 61, 7, 'كيست ستمكارتر از آن كس كه به خدا دروغ مى‌بندد، در حالى كه او را به اسلام دعوت مى‌كنند؟ و خدا ستمكاران را هدايت نمى‌كند.'),
(5171, 61, 8, 'مى‌خواهند نور خدا را به دهانهايشان خاموش كنند ولى خدا كامل‌كننده نور خويش است، اگر چه كافران را ناخوش آيد.'),
(5172, 61, 9, 'اوست آن خدايى كه پيامبر خود را همراه با هدايت و دين راستين بفرستاد تا او را بر همه اديان پيروز گرداند، هر چند مشركان را ناخوش آيد.'),
(5173, 61, 10, 'اى كسانى كه ايمان آورده‌ايد، آيا شما را به تجارتى كه از عذاب دردآور رهاييتان دهد راه بنمايم؟'),
(5174, 61, 11, 'به خدا و پيامبرش ايمان بياوريد، و در راه خدا با مال و جان خويش جهاد كنيد. و اين براى شما بهتر است، اگر دانا باشيد.'),
(5175, 61, 12, 'گناهان شما را مى‌آمرزد و به بهشتهايى كه درونش نهرها جارى است و نيز خانه هايى خوش و پاكيزه در بهشتهاى جاويد داخل مى‌كند، و اين پيروزى بزرگى است.'),
(5176, 61, 13, 'و نعمتى ديگر كه دوستش داريد: نُصرتى است از جانب خداوند و فتحى نزديك، و مؤمنان را بشارت ده.'),
(5177, 61, 14, 'اى كسانى كه ايمان آورده‌ايد، ياوران خدا باشيد، همچنان كه عيسى بن مريم به حواريان گفت: چه كسانى ياوران من در راه خدايند؟ حواريان گفتند: ما ياوران خدا هستيم. پس گروهى از بنى‌اسرائيل ايمان آوردند و گروهى كافر شدند. ما كسانى را كه ايمان آورده بودند بر ضد دشمنانشان مدد كرديم تا پيروز شدند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'خدا را تسبيح مى‌گويند هر چه در آسمانها و هر چه در زمين است، آن فرمانرواى پاك از عيب را، آن پيروزمند حكيم را.'),
(5179, 62, 2, 'اوست خدايى كه به ميان مردمى بى‌كتاب پيامبرى از خودشان مبعوث داشت تا آياتش را بر آنها بخواند و آنها را پاكيزه سازد و كتاب و حكمتشان بياموزد. اگر چه پيش از آن در گمراهى آشكار بودند،'),
(5180, 62, 3, 'و بر گروهى ديگر از ايشان كه هنوز به آنها نپيوسته‌اند. و اوست پيروزمند و حكيم.'),
(5181, 62, 4, 'اين بخشايش خداست كه به هر كه خواهد ارزانيش دارد و خدا را بخشايشى بزرگ است.'),
(5182, 62, 5, 'مثَل كسانى كه تورات به آنها داده شده و بدان عمل نمى‌كنند مثَل آن خر است كه كتابهايى را حمل مى‌كند. بد مثَلى است مثَل مردمى كه آيات خدا را دروغ مى‌شمرده‌اند. و خدا ستمكاران را هدايت نمى‌كند.'),
(5183, 62, 6, 'بگو: اى قوم يهود، هرگاه مى‌پنداريد كه شما دوستان خدا هستيد، نه مردم ديگر، پس تمناى مرگ كنيد اگر راست مى‌گوييد.'),
(5184, 62, 7, 'و آنان به سبب اعمالى كه پيش از اين مرتكب شده‌اند، هرگز تمناى مرگ نخواهند كرد. و خدا به ستمكاران داناست.'),
(5185, 62, 8, 'بگو: آن مرگى كه از آن مى‌گريزيد شما را درخواهد يافت و سپس نزد آن داناى نهان و آشكارا برگردانده مى‌شويد تا به كارهايى كه كرده‌ايد آگاهتان سازد.'),
(5186, 62, 9, 'اى كسانى كه ايمان آورده‌ايد، چون نداى نماز روز جمعه دردهند، به نماز بشتابيد و داد و ستد را رها كنيد. اگر دانا باشيد، اين كار برايتان بهتر است.'),
(5187, 62, 10, 'و چون نماز پايان يافت، در زمين پراكنده شويد و رزق خدا را طلب كنيد و فراوانش ياد كنيد. باشد كه رستگار شويد.'),
(5188, 62, 11, 'و چون تجارتى يا بازيچه‌اى بينند پراكنده مى‌شوند و به جانب آن مى‌روند و تو را همچنان ايستاده رها مى‌كنند. بگو: آنچه در نزد خداست از بازيچه و تجارت بهتر است. و خدا بهترين روزى‌دهندگان است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'چون منافقان نزد تو آيند، گويند: شهادت مى‌دهيم كه تو پيامبر خدا هستى. خدا مى‌داند كه تو پيامبرش هستى و خدا شهادت مى‌دهد كه منافقان دروغگويند.'),
(5190, 63, 2, 'از سوگندهاى خود سپرى ساختند و از راه خدا بازداشتند و به حقيقت، آنچه مى‌كنند ناپسند است.'),
(5191, 63, 3, 'اين بدان سبب است كه ايمان آوردند، سپس كافر شدند. خدا نيز بر دلهاشان مُهر نهاد و آنان درنمى‌يابند.'),
(5192, 63, 4, 'چون آنها را ببينى تو را از ظاهرشان خوش مى‌آيد، و چون سخن بگويند به سخنشان گوش مى‌دهى. گويى چوبهايى هستند به ديوار تكيه داده. هر آوازى را بر زيان خود مى‌پندارند. ايشان دشمنانند. از آنها حذر كن. خدايشان بكشد. به كجا منحرف مى‌شوند؟'),
(5193, 63, 5, 'چون به آنها گفته شود كه بياييد تا پيامبر خدا برايتان آمرزش بخواهد، سرمى‌پيچند. مى‌بينى كه اعراض و گردنكشى مى‌كنند.'),
(5194, 63, 6, 'تفاوتى نكند، چه برايشان آمرزش بخواهى چه آمرزش نخواهى. خدايشان نخواهد آمرزيد. و خدا مردم نافرمان را هدايت نمى‌كند.'),
(5195, 63, 7, 'اينان همانهايند كه مى‌گويند: بر آنها كه گرد پيامبر خدايند، چيزى مدهيد تا از گردش پراكنده شوند. و حال آنكه خزاين آسمانها و زمين از آنِ خداست ولى منافقان نمى‌فهمند.'),
(5196, 63, 8, 'مى‌گويند: چون به مدينه بازگرديم، صاحبان عزت، ذليلان را از آنجا بيرون خواهند كرد. عزت از آنِ خدا و پيامبرش و مؤمنان است. ولى منافقان نمى‌دانند.'),
(5197, 63, 9, 'اى كسانى كه ايمان آورده‌ايد، اموال و اولادتان شما را از ذكر خدا به خود مشغول ندارد، كه هر كه چنين كند زيانكار است.'),
(5198, 63, 10, 'از آنچه روزيتان داده‌ايم در راه خدا انفاق كنيد، پيش از آنكه يكى از شما را مرگ فرا رسد و بگويد: اى پروردگار من، چرا مرگ مرا اندكى به تأخير نينداختى تا صدقه دهم و از شايستگان باشم؟'),
(5199, 63, 11, 'چون كسى اَجلش فرا رسد، خدا مرگ او را به تأخير نمى‌افكند. و خدا به كارهايى كه مى‌كنيد آگاه است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'خدا را تسبيح مى‌گويند هر چه در آسمانها و هر چه در زمين است. فرمانروايى از آنِ اوست و حمد سزاوار اوست و او بر هر چيزى تواناست.'),
(5201, 64, 2, 'اوست كه شما را بيافريد. بعضى از شما كافر، و بعضى مؤمنند. و كارهايى را كه مى‌كنيد مى‌بينيد.'),
(5202, 64, 3, 'آسمانها و زمين را به حق بيافريد و شما را تصوير كرد و نيكو تصوير كرد. و سرانجام همه به سوى اوست.'),
(5203, 64, 4, 'هر چه را در آسمانها و زمين است مى‌داند و به آنچه پنهان مى‌داريد يا آشكار مى‌سازيد آگاه است، و خدا به هر چه در دلها مى‌گذرد عالم است.'),
(5204, 64, 5, 'آيا شما را خبر كسانى كه از اين پيش كافر شده بودند نرسيده است؟ عقوبت عملشان را چشيدند و ايشان راست عذابى دردآور.'),
(5205, 64, 6, 'اين به كيفر آن بود كه پيامبرانشان با دليلهاى روشن بر آنان مبعوث شدند. و آنها گفتند: آيا آدميان ما را هدايت مى‌كنند؟ پس انكار كردند و رويگردان گشتند و خدا بى‌نياز است، كه توانگر و در خور ستايش است.'),
(5206, 64, 7, 'كافران پنداشتند كه آنها را زنده نمى‌كنند. بگو: آرى، به پروردگارم سوگند كه شما را زنده مى‌كنند تا به كارهايى كه كرده‌ايد آگاه سازند. و اين بر خدا آسان است.'),
(5207, 64, 8, 'پس به خدا و پيامبرش و اين نور كه نازل كرده‌ايم ايمان بياوريد. و خدا به كارهايى كه مى‌كنيد آگاه است.'),
(5208, 64, 9, 'روزى كه همه شما را در محشر گرد آورد، آن روز روز مغبونى است. و هر كه به خدا ايمان بياورد و كار شايسته كند، گناهانش را مى‌ريزد و به بهشتهايى كه در آن نهرها جارى است داخل كند. آنجا جاودانه خواهند بود. اين كاميابى بزرگى است.'),
(5209, 64, 10, 'و آنان كه كفر ورزيدند و آيات ما را تكذيب كردند اهل آتشند. در آن جاودانه‌اند و جهنم بد سرانجامى است.'),
(5210, 64, 11, 'هيچ مصيبتى جز به فرمان خدا به كسى نمى‌رسد. و هر كه به خدا ايمان بياورد، خدا قلبش را هدايت مى‌كند. و خدا به هر چيزى داناست.'),
(5211, 64, 12, 'خدا را اطاعت كنيد، و پيامبر را اطاعت كنيد. پس اگر شما اعراض كنيد، بر فرستاده ما وظيفه‌اى جز رسانيدن پيام آشكار نيست.'),
(5212, 64, 13, 'خداى يكتاست كه هيچ خدايى جز او نيست. مؤمنان البته بر خداى يكتا توكل كنند.'),
(5213, 64, 14, 'اى كسانى كه ايمان آورده‌ايد، بعضى از زنان و فرزندانتان دشمن شما هستند. از آنها حذر كنيد. و اگر عفو كنيد و چشم بپوشيد و گناهشان پوشيده داريد، خدا آمرزنده و مهربان است.'),
(5214, 64, 15, 'جز اين نيست كه اموال و اولاد شما آزمايشى هستند. و حال آنكه مزد فراوان نزد خداست.'),
(5215, 64, 16, 'تا توانيد از خدا بترسيد و گوش فرا داريد و اطاعت كنيد و به سود خود از مالتان انفاق كنيد. و آنان كه از حرص نفس خويش در امان مانده‌اند رستگارند.'),
(5216, 64, 17, 'اگر به خدا قرض‌الحسنه دهيد برايتان دو برابرش خواهد كرد. و شما را مى‌آمرزد كه او شكرپذيرنده و بردبار است.'),
(5217, 64, 18, 'داناى نهان و آشكار است، پيروزمند و حكيم است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اى پيامبر، اگر زنان را طلاق مى‌دهيد به وقت عده طلاقشان دهيد. و شمار عده را نگه داريد. و از خداى يكتا پروردگارتان بترسيد. و آنان را از خانه‌هايشان بيرون مكنيد. و از خانه بيرون نروند مگر آنكه به آشكارا مرتكب كارى زشت شوند. اينها احكام خداوند است، و هر كه از آن تجاوز كند به خود ستم كرده است. تو چه دانى، شايد خدا از اين پس امرى تازه پديد آورد.'),
(5219, 65, 2, 'و چون به پايان مدت رسيدند، يا به وجهى نيكو نگاهشان داريد يا به وجهى نيكو از آنها جدا شويد و دو تن عادل از خودتان را به شهادت گيريد. و براى خدا شهادت را به راستى ادا كنيد. هر كه را به خدا و روز قيامت ايمان دارد، اينچنين اندرز مى‌دهند. و هر كه از خدا بترسد، براى او راهى براى بيرون‌شدن قرار خواهد داد،'),
(5220, 65, 3, 'و از جايى كه گمانش را ندارد روزى‌اش مى‌دهد. و هر كه بر خدا توكل كند، خدا او را كافى است. خدا كار خود را به اجرا مى‌رساند و هر چيز را اندازه‌اى قرار داده است.'),
(5221, 65, 4, 'اگر در ترديد هستيد، از ميان زنانتان آنهايى كه از حيض مأيوس شده‌اند و آنهايى كه هنوز حيض نشده‌اند عِدّه‌شان سه ماه است. و عِدّه زنان آبستن همان وضع حمل است. و هر كه از خدا بترسد، خدا كارش را آسان خواهد كرد.'),
(5222, 65, 5, 'اين فرمان خداست كه بر شما نازل كرده است. و هر كه از خدا بترسد، گناهانش را از او مى‌زدايد و او را پاداشى بزرگ مى‌دهد.'),
(5223, 65, 6, 'هر جا كه خود سكونت گزينيد آنها را نيز به قدر توانايى خود مسكن دهيد. و تا بر آنها تنگ گيريد ميازاريدشان. و اگر آبستن بودند، نفقه‌شان را بدهيد تا وضع حمل كنند. و اگر فرزند شما را شير مى‌دهند، مزدشان را بدهيد و به وجهى نيكو با يكديگر توافق كنيد. و اگر به توافق نرسيديد، از زنى ديگر بخواهيد كه كودك را شير دهد.'),
(5224, 65, 7, 'هر مالدارى از مال خود نفقه دهد، و كسى كه تنگدست باشد، از هر چه خدا به او داده است نفقه دهد. خدا هيچ كسى را مگر به آن اندازه كه به او داده است مكلف نمى‌سازد، و زودا كه خدا پس از سختى آسانى پيش آورد.'),
(5225, 65, 8, 'چه بسا مردم قريه‌اى كه از فرمان پرردگارشان و پيامبرانش سرباز زدند. آنگاه ما سخت از آنها حساب كشيديم و به عذابى سهمناك عذابشان كرديم.'),
(5226, 65, 9, 'پس عقوبت عمل خود را چشيدند. عاقبت كارشان زيانكارى بود.'),
(5227, 65, 10, 'خداوند برايشان عذابى سخت آماده كرد. پس اى خردمندانى كه ايمان آورده‌ايد، از خدا بترسيد. خدا بر شما قرآن را نازل كرده است.'),
(5228, 65, 11, 'و پيامبرى كه آيات روشن خدا را برايتان مى‌خواند تا كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند از تاريكى به روشنايى آورد. و هر كه را به خدا ايمان آورد و كار شايسته كند به بهشتهايى در آورد كه در آن نهرها جارى است، همواره در آنجا بمانند و خدا روزيشان را كرامند گرداند.'),
(5229, 65, 12, 'خداست آن كه هفت آسمان و همانند آنها زمين بيافريد. فرمان او ميان آسمانها و زمين جارى است تا بدانيد كه خدا بر هر چيز قادر است و به علم بر همه چيز احاطه دارد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اى پيامبر، چرا چيزى را كه خدا بر تو حلال كرده است، به خاطر خشنودساختن زنانت بر خود حرام مى‌كنى؟ و خدا آمرزنده و مهربان است.'),
(5231, 66, 2, 'خدا براى شما گشودن سوگندهايتان را مقرر داشته است. خداست ياور شما، و اوست دانا و حكيم.'),
(5232, 66, 3, 'آنگاه كه پيامبر با يكى از زنان خود رازى در ميان نهاد، چون آن زن آن راز با ديگرى باز گفت، خدا پيامبر را از آن آگاه ساخت و او پاره‌اى از آن راز را بر آن زن آشكار كرد و از افشاى پاره‌اى ديگر سرباز آد. چون او را از آن خبر داد، گفت: چه كسى تو را از اين ماجرا آگاه كرده است؟ گفت: آن خداى داناى آگاه به من خبر داده است.'),
(5233, 66, 4, 'اگر شما دو زن توبه كنيد بهتر است، زيرا دلهايتان از حق باز گشته است. و اگر براى آزارش همدست شويد، خدا ياور اوست و نيز جبرئيل و مؤمنان شايسته و فرشتگان از آن پس ياور او خواهند بود.'),
(5234, 66, 5, 'شايد اگر شما را طلاق گويد پروردگارش به جاى شما زنانى بهتر از شمايش بدهد. زنانى مسلمان، مؤمن، فرمانبردار، توبه‌كننده، اهل عبادت و روزه‌گرفتن، خواه شوهركرده، خواه باكره.'),
(5235, 66, 6, 'اى كسانى كه ايمان آورده‌ايد، خود و خانواده خود را از آتشى كه هيزم آن مردم و سنگها هستند نگه داريد. فرشتگانى درشت‌گفتار و سختگير بر آن آتش موكّلند. هر چه خدا بگويد نافرمانى نمى‌كنند و همان مى‌كنند كه به آن مأمور شده‌اند.'),
(5236, 66, 7, 'اى كافران، در آن روز پوزش مخواهيد. جز اين نيست كه برابر كارى كه كرده‌ايد كيفر داده شويد.'),
(5237, 66, 8, 'اى كسانى كه ايمان آورده‌ايد، به درگاه خدا توبه كنيد؛ توبه‌اى از روى اخلاص. باشد كه پروردگارتان گناهانتان را محو كند و شما را به بهشتهايى داخل كند كه در آن نهرها جارى است. در آن روز، خدا پيامبر و كسانى را كه با او ايمان آورده‌اند فرو نگذارد، و نورشان پيشاپيش و سمت راستشان در حركت باشد. مى‌گويند: اى پروردگار ما، نور ما را براى ما به كمال رسان و ما را بيامرز، كه تو بر هر كارى توانا هستى.'),
(5238, 66, 9, 'اى پيامبر، با كفار و منافقان جهاد كن و بر آنها سخت بگير. جايگاهشان جهنم است كه بد سرانجامى است.'),
(5239, 66, 10, 'خدا براى كافران مثَل زن نوح و زن لوط را مى‌آورد كه هر دو در نكاح دو تن از بندگان صالح ما بودند و به آن دو خيانت ورزيدند. و آنها نتوانستند از زنان خود دفع عذاب كنند و گفته شد: با ديگران به آتش درآييد.'),
(5240, 66, 11, 'و خدا براى كسانى كه ايمان آورده‌اند، آن فرعون را مثَل مى‌زند آنگاه كه گفت: اى پروردگار من، براى من در بهشت نزد خود خانه‌اى بنا كن و مرا از فرعون و عملش نجات ده و مرا از مردم ستمكاره برهان.'),
(5241, 66, 12, 'و مريم دختر عمران را كه شرمگاه خويش را از زنا نگه داشت و ما از روح خود در آن دميديم. و او كلمات پروردگار خود و كتابهايش را تصديق كرد و او از فرمانبرداران بود.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بزرگوار و متعالى است خداوندى كه فرمانروايى به دست اوست و او بر هر چيزى تواناست.'),
(5243, 67, 2, 'آن كه مرگ و زندگى را بيافريد، تا بيازمايدتان كه كدام يك از شما به عمل نيكوتر است و اوست پيروزمند و آمرزنده.'),
(5244, 67, 3, 'آن كه هفت آسمان طبقه طبقه را بيافريد. در آفرينش خداى رحمان هيچ خلل و بى‌نظمى نمى‌بينى. پس بار ديگر نظركن، آيا در آسمان شكافى مى‌بينى؟'),
(5245, 67, 4, 'بار ديگر نيز چشم باز كن و بنگر. نگاه تو خسته و درمانده به نزد تو باز خواهد گشت.'),
(5246, 67, 5, 'ما آسمان فرودين را به چراغهايى بياراستيم و آن چراغها را وسيله راندن شياطين گردانيديم و برايشان شكنجه آتش سوزان آماده كرده‌ايم.'),
(5247, 67, 6, 'و براى كسانى كه به پروردگارشان كافر شده‌اند عذاب جهنم باشد و جهنم بد سرانجامى است.'),
(5248, 67, 7, 'چون در جهنم افكنده شوند، به جوش آيد و بانگ زشتش را بشنوند،'),
(5249, 67, 8, 'نزديك است كه از خشم پاره‌پاره شود. و چون فوجى را در آن افكنند، خازنانش گويندشان: آيا شما را بيم‌دهنده‌اى نيامد؟'),
(5250, 67, 9, 'گويند: چرا، بيم‌دهنده آمد ولى تكذيبش كرديم و گفتيم: خدا هيچ چيز نازل نكرده است؛ شما در گمراهى بزرگى هستيد.'),
(5251, 67, 10, 'و مى‌گويند: اگر ما مى‌شنيديم يا تعقل مى‌كرديم، اهل اين آتش سوزنده نمى‌بوديم.'),
(5252, 67, 11, 'پس به گناه خود اعتراف مى‌كنند. اى لعنت باد بر آنها كه اهل آتش سوزنده‌اند.'),
(5253, 67, 12, 'براى كسانى كه ناديده از پروردگارشان مى‌ترسند، آمرزش و مزد فراوان است.'),
(5254, 67, 13, 'چه به راز سخن گوييد و چه آشكارا، او به هر چه در دلها مى‌گذرد داناست.'),
(5255, 67, 14, 'آيا آن كه آفريده نمى‌داند؟ حال آنكه او باريك‌بين و آگاه است.'),
(5256, 67, 15, 'اوست كه زمين را رام شما گردانيد. پس بر روى آن سير كنيد، و از رزق خدا بخوريد. چون از قبر بيرون آييد به سوى او مى‌رويد.'),
(5257, 67, 16, 'آيا از آن كه در آسمان است ايمن نشسته‌ايد كه ناگاه زمين را به لرزش درآورد و زمين شما را در خود فرو برد؟'),
(5258, 67, 17, 'يا از آن كه در آسمان است ايمن نشسته‌ايد كه ناگاه بادى همراه با سنگريزه بر سر شما فرستد؟ به زودى خواهيد دانست كه بيم‌دادن من چگونه است.'),
(5259, 67, 18, 'هر آينه كسانى كه پيش از آنها بودند، پيامبران را تكذيب كردند. پس عذاب من چگونه بود.'),
(5260, 67, 19, 'آيا پرندگانى را كه بال گشوده يا بال فراهم‌كشيده بر فراز سرشان در پروازند، نديده‌اند؟ آنها را جز خداى رحمان كسى در هوا نگاه نتواند داشت. اوست كه به همه چيز بيناست.'),
(5261, 67, 20, 'آيا آنان كه ياران شمايند توانند در برابر خدا ياريتان كنند؟ كافران در فريبى بيش نيستند.'),
(5262, 67, 21, 'آيا كيست آن كه به شما روزى دهد اگر او روزى خويش باز دارد؟ نه، در سركشى و دورى از حق لجاج مى‌ورزند.'),
(5263, 67, 22, 'آيا آن كس كه نگونسار بر روى افتاده راه مى‌رود، هدايت يافته‌تر است يا آن كه بر پاى ايستاده و بر راه راست مى‌رود؟'),
(5264, 67, 23, 'بگو: اوست كه شما را آفريده است و گوش و چشم و دل داده است. چه اندك سپاس مى‌گزاريد.'),
(5265, 67, 24, 'بگو: اوست كه شما را در زمين آفريد و در قيامت نزد او گرد آورده مى‌شويد.'),
(5266, 67, 25, 'مى‌گويند: اگر راست مى‌گوييد، اين وعده چه وقت فرا مى‌رسد؟'),
(5267, 67, 26, 'بگو: علم آن نزد خداست و من بيم‌دهنده‌اى آشكارم.'),
(5268, 67, 27, 'كافران چون نزديكش بنگرند، چهره‌شان گرفته شود. و به آنها گفته مى‌شود: اين است آنچه باطلش مى‌خوانديد.'),
(5269, 67, 28, 'بگو: گيرم كه خدا مرا و همراهانم را هلاك كند يا بر ما رحمت آورد، چه كسى كافران را از عذاب دردآور مى‌رهاند؟'),
(5270, 67, 29, 'بگو: او خداى رحمان است. به او ايمان آورديم و بر او توكل كرديم. و زودا كه خواهيد دانست چه كسى در گمراهى آشكار است.'),
(5271, 67, 30, 'بگو: اگر آبتان در زمين فرو رود، چه كسى شما را آب روان خواهد داد؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون، سوگند به قلم و آنچه مى‌نويسند.'),
(5273, 68, 2, 'كه تو، به فضل پروردگارت، ديوانه نيستى.'),
(5274, 68, 3, 'و تو راست پاداشى پايان‌ناپذير.'),
(5275, 68, 4, 'و تو راست خُلقى عظيم.'),
(5276, 68, 5, 'زودا كه تو ببينى و آنها نيز ببينند،'),
(5277, 68, 6, 'كه ديوانگى در كدام يك از شماست.'),
(5278, 68, 7, 'هر آينه پروردگار تو بهتر مى‌داند چه كسى از طريق او گمراه گشته، كه او به ره‌يافتگان داناتر است.'),
(5279, 68, 8, 'پس، از تكذيب كنندگان، اطاعت مكن.'),
(5280, 68, 9, 'دوست دارند كه نرمى كنى تا نرمى كنند.'),
(5281, 68, 10, 'از هر فرومايه‌اى كه بسيار سوگند مى‌خورد، پيروى مكن:'),
(5282, 68, 11, 'عيبجويى كه براى سخن‌چينى اينجا و آنجا مى‌رود،'),
(5283, 68, 12, 'بازدارنده از خير، متجاوز گناهكار،'),
(5284, 68, 13, 'خشن‌مردى، ناشناخته نسب،'),
(5285, 68, 14, 'بدان جهت كه صاحب مال و فرزند است،'),
(5286, 68, 15, 'چون آيات ما بر او خوانده شود، گويد: اساطير پيشينيان است.'),
(5287, 68, 16, 'زودا كه بر بينى‌اش داغ گذاريم.'),
(5288, 68, 17, 'ما آنها را آزموديم، چنان كه صاحبان آن بستانها را آزموديم. آنگاه كه قسم خوردند كه فردا بامداد ميوه‌ها را خواهند چيد.'),
(5289, 68, 18, 'و ان شاء اللّه نگفتند.'),
(5290, 68, 19, 'پس شب‌هنگام كه به خواب بودند آفتى از آسمان آمد،'),
(5291, 68, 20, 'و بستانها سياه شد.'),
(5292, 68, 21, 'و سحرگاهان يكديگر را ندا دادند:'),
(5293, 68, 22, 'اگر مى‌خواهيد ميوه بچينيد، بامدادان به كشتزار خود برويد.'),
(5294, 68, 23, 'به راه افتادند و آهسته مى‌گفتند:'),
(5295, 68, 24, 'كه امروز نبايد بينوايى به بستان شما درآيد.'),
(5296, 68, 25, 'صبحگاهان بدين آهنگ كه مى‌توانند بينوا را منع كنند بيرون شدند.'),
(5297, 68, 26, 'چون بستانهاى خود را ديدند گفتند: راه را گم كرده‌ايم.'),
(5298, 68, 27, 'نه، ما از حاصل محروم شده‌ايم.'),
(5299, 68, 28, 'نيكمردشان گفت: شما را نگفتم؛ چرا خدا را تسبيح نمى‌گوييد؟'),
(5300, 68, 29, 'گفتند: منزه است پروردگار ما، ما ستمكار بوديم.'),
(5301, 68, 30, 'پس زبان به ملامت يكديگر گشودند.'),
(5302, 68, 31, 'گفتند: واى بر ما، ما مردمى سركش بوده‌ايم،'),
(5303, 68, 32, 'باشد كه پروردگار ما در عوض، چيزى بهتر از آن ما را ارزانى دارد. ما به پروردگار خود روى آورده‌ايم.'),
(5304, 68, 33, 'اينچنين است عذاب. و اگر بدانند، عذاب آخرت بزرگ‌تر است.'),
(5305, 68, 34, 'براى پرهيزگاران در نزد پروردگارشان بستانهايى است پرنعمت.'),
(5306, 68, 35, 'آيا با تسليم‌شدگان چون مجرمان رفتار مى‌كنيم؟'),
(5307, 68, 36, 'شما را چه مى‌شود؟ چسان داورى مى‌كنيد؟'),
(5308, 68, 37, 'آيا شما را كتابى است كه از آن مى‌خوانيد؟'),
(5309, 68, 38, 'و هر چه اختيار كنيد در آن هست؟'),
(5310, 68, 39, 'يا با ما پيمانى كه به روز قيامت كشد بسته‌ايد؛ كه به هر چه حكم كنيد حق شما باشد؟'),
(5311, 68, 40, 'از ايشان بپرس كدام يكشان ضامن چنين پيمانى است؟'),
(5312, 68, 41, 'يا آنها را بتانى است؟ اگر راست مى‌گويند بتان خود را بياورند.'),
(5313, 68, 42, 'روزى كه آن واقعه عظيم پديدار شود و آنها را به سجود فرا خوانند، ولى نتوانند.'),
(5314, 68, 43, 'وحشت در چشمانشان پيداست، ذلت بر آنها چيره شده است. پيش از اين نيز آنها را در عين تندرستى به سجده فراخوانده بودند.'),
(5315, 68, 44, 'پس مرا با آنها كه اين سخن را تكذيب مى‌كنند واگذار تا اندك‌اندك، چنان كه درنيابند، فروگيريمشان.'),
(5316, 68, 45, 'و به آنها مهلت دهم. هر آينه مكر من مكرى استوار است.'),
(5317, 68, 46, 'يا از آنان مزدى طلبيده‌اى و اكنون از اداى آن در رنجند؟'),
(5318, 68, 47, 'يا علم غيب مى‌دانند و آنهايند كه مى‌نويسند؟'),
(5319, 68, 48, 'در برابر فرمان پروردگارت صابر باش و چون صاحب ماهى مباش كه با دلى پراندوه ندا در داد.'),
(5320, 68, 49, 'اگر نعمت پروردگارش نبود، در عين بد حالى به صحرايى بى‌آب و گياه مى‌افتاد.'),
(5321, 68, 50, 'پس پروردگارش او را برگزيد و در زمره صالحانش آورد.'),
(5322, 68, 51, 'و كافران چون قرآن را شنيدند نزديك بود كه تو را با چشمان خود به سر درآورند و مى‌گويند كه او ديوانه است،'),
(5323, 68, 52, 'و حال آنكه قرآن براى جهانيان جز اندرزى نيست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن روز بر حق.'),
(5325, 69, 2, 'چيست آن روز بر حق؟'),
(5326, 69, 3, 'و چه دانى كه آن روز بر حق چيست؟'),
(5327, 69, 4, 'قوم ثمود و عاد روز رستاخيز را دروغ انگاشتند.'),
(5328, 69, 5, 'اما قوم ثمود به آن بانگ سهمگين هلاك شدند.'),
(5329, 69, 6, 'و اما قوم عاد با وزش باد صُرصر به هلاكت رسيدند.'),
(5330, 69, 7, 'آن عذاب را هفت شب و هشت روز پى در پى بر آنان بگماشت. آن قوم را چون تنه‌هاى پوسيده خرما مى‌ديدى كه افتاده‌اند و مُرده‌اند.'),
(5331, 69, 8, 'آيا كسى را مى‌بينى كه از آنها بر جاى مانده باشد؟'),
(5332, 69, 9, 'و فرعون و مردمى كه پيش از او بودند و نيز مردم مُؤتفكَه گناهكار آمدند.'),
(5333, 69, 10, 'فرستاده پروردگارشان را نافرمانى كردند و خدا نيز آنان را به سختى فرو گرفت.'),
(5334, 69, 11, 'چون آب طغيان كرد، شما را به كشتى سوار كرديم.'),
(5335, 69, 12, 'تا آن را مايه اندرزتان گردانيم و گوش نگه‌دارنده اندرز آن را فرا گيرد.'),
(5336, 69, 13, 'چون يك بار در صور دميده شود،'),
(5337, 69, 14, 'و زمين و كوه‌ها را برگيرند و يكباره در هم كوبند،'),
(5338, 69, 15, 'آن روز آن حادثه به وقوع پيوسته باشد.'),
(5339, 69, 16, 'و آسمان كه در آن روز سست شده است، بشكافد.'),
(5340, 69, 17, 'و فرشتگان در اطراف آسمان باشند. و در آن روز هشت تن از آنها عرش پروردگارت را بر فراز سرشان حمل مى‌كنند.'),
(5341, 69, 18, 'آن روز شما را پيش آورند و هيچ چيز شما نهان نماند.'),
(5342, 69, 19, 'اما هر كس كه نامه اعمالش را به دست راستش دهند، مى‌گويد: نامه مرا بگيريد و بخوانيد.'),
(5343, 69, 20, 'من يقين داشتم كه حساب خود را خواهم ديد.'),
(5344, 69, 21, 'پس او در يك زندگى پسنديده و خوش خواهد بود،'),
(5345, 69, 22, 'در بهشتى برين،'),
(5346, 69, 23, 'كه ميوه‌هايش در دسترس باشد.'),
(5347, 69, 24, 'بخوريد و بياشاميد، گوارا باد شما را. اينها پاداش اعمالى است كه در ايام گذشته به جاى مى‌آورده‌ايد.'),
(5348, 69, 25, 'اما آن كس كه نامه اعمالش را به دست چپش دهند مى‌گويد: اى كاش نامه مرا به دست من نداده بودند،'),
(5349, 69, 26, 'و ندانسته بودم كه حساب من چيست،'),
(5350, 69, 27, 'اى كاش همان مرگ مى‌بود و بس،'),
(5351, 69, 28, 'دارايى من مرا سود نبخشيد،'),
(5352, 69, 29, 'قدرت من از دست من برفت.'),
(5353, 69, 30, 'بگيريدش، زنجيرش كنيد.'),
(5354, 69, 31, 'و به جهنمش بكشيد.'),
(5355, 69, 32, 'و او را با زنجيرى به درازاى هفتاد ذراع بكشيد.'),
(5356, 69, 33, 'زيرا به خداى بزرگ ايمان نداشت.'),
(5357, 69, 34, 'و به طعام‌دادن به مسكينان كسى را ترغيب نمى‌كرد.'),
(5358, 69, 35, 'در آن روز، در آنجا هيچ دوستى نخواهد داشت.'),
(5359, 69, 36, 'طعامش چيزى جز چرك و خون اهل دوزخ نيست.'),
(5360, 69, 37, 'تنها خطاكاران از آن طعام مى‌خورند.'),
(5361, 69, 38, 'پس سوگند به آنچه مى‌بينيد،'),
(5362, 69, 39, 'و آنچه نمى‌بينيد،'),
(5363, 69, 40, 'كه اين سخن فرستاده‌اى بزرگوار است،'),
(5364, 69, 41, 'نه سخن شاعرى. چه اندك ايمان مى‌آوريد.'),
(5365, 69, 42, 'و نيز سخن كاهنى نيست. چه اندك پند مى‌گيريد.'),
(5366, 69, 43, 'از جانب پروردگار جهانيان نازل شده است.'),
(5367, 69, 44, 'اگر پيامبر پاره‌اى سخنان را به افترا بر ما مى‌بست،'),
(5368, 69, 45, 'با قدرت او را فرومى‌گرفتيم،'),
(5369, 69, 46, 'سپس رگ دلش را پاره مى‌كرديم،'),
(5370, 69, 47, 'و هيچ يك از شما را توان آن نبود كه مانع شود.'),
(5371, 69, 48, 'و قرآن براى پرهيزگاران پندى است.'),
(5372, 69, 49, 'ما مى‌دانيم كه از ميان شما كسانى هستند كه تكذيب مى‌كنند.'),
(5373, 69, 50, 'و قرآن كافران را مايه حسرت است.'),
(5374, 69, 51, 'و آن سخن حق و يقين است.'),
(5375, 69, 52, 'پس به نام پروردگار بزرگت تسبيح بگوى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'پرسنده‌اى از عذابى پرسيد كه‌'),
(5377, 70, 2, 'بر كافران فرود خواهد آمد و كس آن را دفع نتواند كرد،'),
(5378, 70, 3, 'از جانب خدا كه صاحب آسمانها.'),
(5379, 70, 4, 'در روزى كه مقدارش پنجاه هزار سال است، فرشتگان و روح بدان جا فراروند.'),
(5380, 70, 5, 'پس صبر كن صبرى نيكو.'),
(5381, 70, 6, 'ايشان آن روز را دور مى‌بينند.'),
(5382, 70, 7, 'و ما نزديكش مى‌بينيم.'),
(5383, 70, 8, 'روزى كه آسمان چون فلز گداخته گردد،'),
(5384, 70, 9, 'و كوه‌ها چون پشم،'),
(5385, 70, 10, 'و هيچ خويشاوندى از حال خويشاوند خود نپرسد،'),
(5386, 70, 11, 'يكديگر را برابر چشمشان آرند. گناهكار دوست دارد كه خويشتن را بازخرد به فرزندانش،'),
(5387, 70, 12, 'و زنش و برادرش،'),
(5388, 70, 13, 'و عشيره‌اش كه او را مكان داده است،'),
(5389, 70, 14, 'و همه آنها كه در روى زمينند، و نجات يابد.'),
(5390, 70, 15, 'هرگز نه، كه آن شعله آتش است،'),
(5391, 70, 16, 'پوست سر را مى‌كند،'),
(5392, 70, 17, 'هر كه را كه به حق پشت كرد و از فرمان سرپيچيد به خود مى‌خواند،'),
(5393, 70, 18, 'و آن را كه گردمى‌آورد و مى‌اندوخت.'),
(5394, 70, 19, 'هر آينه آدمى را حريص و ناشكيبا آفريده‌اند.'),
(5395, 70, 20, 'چون شرى بدو رسد بيقرارى كند.'),
(5396, 70, 21, 'و چون مالى به دستش افتد بخل مى‌ورزد.'),
(5397, 70, 22, 'مگر نماز گزارندگان:'),
(5398, 70, 23, 'آنان كه به نماز مداومت مى‌ورزند،'),
(5399, 70, 24, 'و آنان كه در اموالشان حقى است معين،'),
(5400, 70, 25, 'براى گدا و محروم.'),
(5401, 70, 26, 'و كسانى كه روز قيامت را تصديق مى‌كنند،'),
(5402, 70, 27, 'و كسانى كه از عذاب پروردگارشان ترسانند،'),
(5403, 70, 28, 'كه از عذاب پروردگارشان در امان نتوانند بود،'),
(5404, 70, 29, 'و كسانى كه شرمگاه خويش نگه مى‌دارند،'),
(5405, 70, 30, 'مگر براى همسرانشان يا كنيزانشان، كه در اين حال ملامتى بر آنها نيست.'),
(5406, 70, 31, 'و آنان كه جز اين را بطلبند، متجاوزان هستند.'),
(5407, 70, 32, 'و كسانى كه امانتها و عهدهاى خود را رعايت مى‌كنند،'),
(5408, 70, 33, 'و كسانى كه شهادت خويش به حق ادا مى‌كنند،'),
(5409, 70, 34, 'و كسانى كه بر نمازهاشان مواظبت دارند،'),
(5410, 70, 35, 'اينان در بهشت، گرامى هستند.'),
(5411, 70, 36, 'پس چيست كه كافران به سوى تو مى‌شتابند؟'),
(5412, 70, 37, 'دسته‌دسته از جانب چپ و از جانب راست.'),
(5413, 70, 38, 'آيا هر يك از ايشان طمع مى‌ورزد كه به بهشت پرنعمت داخل شود؟'),
(5414, 70, 39, 'هرگز نه، آنها خود مى‌دانند كه از چه چيز آنها را آفريده‌ايم.'),
(5415, 70, 40, 'پس سوگند به پروردگار مشرقها و مغربها كه ما تواناييم‌'),
(5416, 70, 41, 'كه به جاى آنها مردمى بهتر بياوريم و در اين كار ناتوان نيستيم.'),
(5417, 70, 42, 'پس بگذارشان تا به بيهودگى درآيند و به بازيچه، تا به آن روزى كه وعده‌شان داده‌ايم برسند.'),
(5418, 70, 43, 'روزى كه شتابان از قبرها به درآيند، چنان كه گويى نزد بتان مى‌شتابند.'),
(5419, 70, 44, 'وحشت بر چشمانشان مستولى شده و به خوارى افتاده‌اند: اين است همان روزى كه آنها را وعده داده بودند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به سوى قومش فرستاديم كه: قوم خود را پيش از آنكه عذابى دردآور بر سرشان فرودآيد، بيم ده.'),
(5421, 71, 2, 'گفت: اى قوم من، من بيم‌دهنده‌اى روشنگرم،'),
(5422, 71, 3, 'خدا را بپرستيد، و از او بترسيد و از من اطاعت كنيد،'),
(5423, 71, 4, 'تا گناهانتان را بيامرزد، و شما را تا مدتى معين مهلت دهد؛ زيرا آن مدت خدايى چون به سر آيد -اگر بدانيد- ديگر به تأخير نيفتد.'),
(5424, 71, 5, 'گفت: اى پروردگار من، من قوم خود را شب و روز دعوت كردم،'),
(5425, 71, 6, 'و دعوت من جز به فرارشان نيفزود.'),
(5426, 71, 7, 'و من هر بار كه دعوتشان كردم تا تو آنها را بيامرزى، انگشتها در گوشهاى خود كردند و جامه در سر كشيدند و پاى فشردند و هر چه بيشتر سركشى كردند.'),
(5427, 71, 8, 'پس به آواز بلند دعوتشان كردم.'),
(5428, 71, 9, 'باز به آشكار گفتم و در نهان گفتم.'),
(5429, 71, 10, 'سپس گفتم: از پروردگارتان آمرزش بخواهيد كه او آمرزنده است،'),
(5430, 71, 11, 'تا از آسمان برايتان پى‌درپى باران فرستد،'),
(5431, 71, 12, 'و شما را به اموال و فرزندان مدد كند و برايتان بستانها و نهرها بيافريند.'),
(5432, 71, 13, 'شما را چه مى‌شود كه از شكوه خداوند نمى‌ترسيد،'),
(5433, 71, 14, 'حال آنكه شما را به گونه‌هاى مختلف بيافريد.'),
(5434, 71, 15, 'آيا نمى‌بينيد چگونه خدا هفت آسمان طبقه طبقه را بيافريد؟'),
(5435, 71, 16, 'و ماه را روشنى آنها، و خورشيد را چراغشان گردانيد.'),
(5436, 71, 17, 'و خدا شما را چون نباتى از زمين برويانيد.'),
(5437, 71, 18, 'باز شما را بدان باز مى‌گرداند و باز از آن بيرون مى‌آورد.'),
(5438, 71, 19, 'و خدا زمين را چون فرشى برايتان بگسترد،'),
(5439, 71, 20, 'تا بر راه‌هاى پهناور آن برويد.'),
(5440, 71, 21, 'نوح گفت: اى پروردگار من، آنها فرمان من نبردند، و از كسى پيروى كردند كه مال و فرزندش جز به زيانش نيفزود.'),
(5441, 71, 22, 'مكر كردند - مكرى بزرگ.'),
(5442, 71, 23, 'و گفتند: خدايانتان را وامگذاريد. وَدّ و سُواع و يَغوث و يَعوق و نَسر را ترك مكنيد.'),
(5443, 71, 24, 'بسيارى را گمراه كردند و تو جز بر گمراهى ستمكاران ميفزاى.'),
(5444, 71, 25, 'به كيفر گناهانشان غرقه شدند و به آتش رفتند و جز خدا براى خود يارى نيافتند.'),
(5445, 71, 26, 'و نوح گفت: اى پروردگار من، بر روى زمين هيچ يك از كافران را مگذار،'),
(5446, 71, 27, 'كه اگر بگذاريشان، بندگانت را گمراه مى‌كنند و جز فرزندانى فاجر و كافر نياورند.'),
(5447, 71, 28, 'اى پروردگار من، مرا و پدر و مادرم را و هر كه را با ايمان به خانه من وارد شود و نيز مردان مؤمن و زنان مؤمن را بيامرز و ستمكاران را جز به هلاكتشان ميفزاى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: به من وحى شده است كه گروهى از جن گوش فرا دادند، و گفتند كه ما قرآنى شگفت شنيديم؛'),
(5449, 72, 2, 'به راه راست هدايت مى‌كند. پس ما بدان ايمان آورديم و هرگز كسى را شريك پروردگارمان نمى‌سازيم.'),
(5450, 72, 3, 'عظمت پروردگار ما متعالى است. نه همسرى گيرد و نه فرزندى دارد.'),
(5451, 72, 4, 'و سفيه ما در باره خدا سخنانى به ناحق مى‌گفت.'),
(5452, 72, 5, 'و ما مى‌پنداشتيم كه آدمى و جن در باره خدا دروغ نمى‌گويند.'),
(5453, 72, 6, 'و نيز مردانى بودند از آدميان كه به مردانى از جن پناه مى‌بردند و بر طغيانشان مى‌افزودند.'),
(5454, 72, 7, 'همچنان كه شما مى‌پنداشتيد آنها هم مى‌پنداشتند كه خدا هيچ كس را دوباره زنده نمى‌سازد.'),
(5455, 72, 8, 'ما به آسمان رسيديم و آن را پر از نگهبانان قدرتمند و شهابها يافتيم.'),
(5456, 72, 9, 'ما در آنجايها كه مى‌توان گوش فرا داد مى‌نشستيم. اما هر كه اكنون گوش نشيند، شهابى را در كمين خود يابد.'),
(5457, 72, 10, 'از اين روى نمى‌دانيم كه آيا براى كسانى كه در روى زمين هستند بدى مقدر مى‌شود، يا پروردگارشان مى‌خواهد به راه راستشان ببرد.'),
(5458, 72, 11, 'برخى از ما صالحند و برخى جز آن، و ما فرقه‌هايى گوناگونيم.'),
(5459, 72, 12, 'ما مى‌دانيم كه خدا را در زمين ناتوان نمى‌كنيم و با گريختن نيز ناتوانش نسازيم.'),
(5460, 72, 13, 'و چون سخن هدايت شنيديم، بدان ايمان آورديم. و هر كه به پروردگارش ايمان آورد نه از نقصان مى‌ترسد و نه از ستم.'),
(5461, 72, 14, 'و بعضى از ما مسلمانند و بعضى از حق دور. و آنان كه اسلام آورده‌اند در جست و جوى راه راست بوده‌اند.'),
(5462, 72, 15, 'اما آنان كه از حق دورند هيزم جهنم خواهند بود.'),
(5463, 72, 16, 'و اگر بر طريقه راست پايدارى كنند، از آبى فراوان سيرابشان كنيم.'),
(5464, 72, 17, 'تا آنها را بيازماييم. و هر كه از ذكر پروردگارش اعراض كند او را به عذابى دشوار دراندازد.'),
(5465, 72, 18, 'و مسجدها از آنِ خداست. و با وجود خداى يكتا كسى را به خدايى مخوانيد.'),
(5466, 72, 19, 'چون بنده خدا براى پرستش او بر پاى ايستاد، گرد او را گرفتند.'),
(5467, 72, 20, 'بگو: تنها پروردگارم را مى‌خوانم و هيچ كس را شريك او نمى‌سازم.'),
(5468, 72, 21, 'بگو: من نمى‌توانم به شما زيانى برسانم يا شما را به صلاح آورم.'),
(5469, 72, 22, 'بگو: هيچ كس مرا از عقوبت خدا پناه ندهد و من جز او پناهگاهى نمى‌يابم.'),
(5470, 72, 23, 'آنچه مى‌توانم جز رساندن پيام او و انجام دادن رسالتهاى او نيست. و هر كه خدا و پيامبرش را نافرمانى كند، نصيب او آتش جهنم است كه همواره در آن خواهند بود،'),
(5471, 72, 24, 'تا آنگاه كه آنچه را به آنها وعده داده بودند بنگرند. پس خواهند دانست چه كسى را ياران ناتوان‌تر و شمار كمتر بوده است.'),
(5472, 72, 25, 'بگو: من نمى‌دانم كه آيا آن وعده‌اى كه به شما داده‌اند نزديك است، يا پروردگار من براى آن زمانى نهاده است؟'),
(5473, 72, 26, 'او داناى غيب است و غيب خود را بر هيچ كس آشكار نمى‌سازد،'),
(5474, 72, 27, 'مگر بر آن پيامبرى كه از او خشنود باشد كه براى نگهبانى از او پيش روى و پشت سرش نگهبانى مى‌گمارد،'),
(5475, 72, 28, 'تا بداند كه آنها پيامهاى پروردگارشان را رسانيده‌اند و خدا به آنچه در نزد آنهاست احاطه دارد و همه چيز را به عدد شمار كرده است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اى جامه بر خود پيچيده،'),
(5477, 73, 2, 'شب را زنده بدار، مگر اندكى را،'),
(5478, 73, 3, 'نيمه‌اى از آن را، يا اندكى از نيمه كم كن.'),
(5479, 73, 4, 'يا اندكى بر نيمه بيفزاى و قرآن را شمرده و روشن بخوان.'),
(5480, 73, 5, 'ما به تو سخنى دشوار را القا خواهيم كرد.'),
(5481, 73, 6, 'هر آينه شب‌هنگام از بستر برخاستن، موافقت زبان و دل را افزاينده‌تر است و بيان سخن را استواردارنده‌تر.'),
(5482, 73, 7, 'كه كارهاى تو در روز بسيار است.'),
(5483, 73, 8, 'و نام پروردگارت را ياد كن و از همه ببر و به او بپيوند:'),
(5484, 73, 9, 'پروردگار مشرق و مغرب. هيچ خدايى جز او نيست. او را كارساز خويش برگزين.'),
(5485, 73, 10, 'و بر آنچه مى‌گويند صبر كن و به وجهى پسنديده از ايشان دورى جوى.'),
(5486, 73, 11, 'تكذيب كنندگان صاحب نعمت را به من واگذار. و اندكى مهلتشان ده.'),
(5487, 73, 12, 'نزد ماست بندهاى گران و دوزخ،'),
(5488, 73, 13, 'و طعامى گلوگير و عذابى دردآور.'),
(5489, 73, 14, 'روزى كه زمين و كوه‌ها به لرزه آيند و كوه‌ها تپه‌اى از ريگ روان گردند.'),
(5490, 73, 15, 'ما بر شما پيامبرى فرستاديم كه بر اعمالتان گواه است، بدان سان كه بر فرعون هم پيامبرى فرستاديم.'),
(5491, 73, 16, 'و فرعون از پيامبر فرمان نبرد. ما نيز او را به سختى فروگرفتيم.'),
(5492, 73, 17, 'اگر كافر باشيد، در روزى كه كودكان را پير مى‌گرداند چگونه در امان مانيد؟'),
(5493, 73, 18, 'در آن روز آسمان بشكافد و وعده خدا به وقوع پيوندد.'),
(5494, 73, 19, 'اين تذكارى است. پس هر كه بخواهد، راهى به سوى پروردگارش آغاز كند.'),
(5495, 73, 20, 'پروردگار تو مى‌داند كه تو و گروهى از آنان كه با تو هستند نزديك به دو ثلث شب و نيم شب و ثلث شب را به نماز مى‌ايستيد. و خداست كه اندازه شب و روز را معين مى‌كند. و مى‌داند كه شما هرگز حساب آن را نتوانيد داشت. پس توبه شما را بپذيرفت. و هر چه ميّسر شود از قرآن بخوانيد. مى‌داند چه كسانى از شما بيمار خواهند شد، و گروهى ديگر به طلب روزى خدا به سفر مى‌روند و گروه ديگر در راه خدا به جنگ مى‌روند. پس هر چه ميسر شود از آن بخوانيد. نماز بگزاريد و زكات بدهيد و به خدا قرض الحسنه دهيد. و هر خيرى را كه براى خود پيشاپيش بفرستيد، آن را نزد خدا خواهيد يافت. و آن پاداش بهتر است و پاداشى بزرگ‌تر است. و از خدا آمرزش بخواهيد، زيرا خدا آمرزنده و مهربان است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اى جامه در سر كشيده،'),
(5497, 74, 2, 'برخيز و بيم ده.'),
(5498, 74, 3, 'و پروردگارت را تكبير گوى.'),
(5499, 74, 4, 'و جامه‌ات را پاكيزه دار.'),
(5500, 74, 5, 'و از پليدى دورى گزين.'),
(5501, 74, 6, 'و چيزى مده كه بيش از آن چشم داشته باشى.'),
(5502, 74, 7, 'براى پروردگارت صابر باش.'),
(5503, 74, 8, 'و آنگاه كه در صور دميده شود،'),
(5504, 74, 9, 'آن روز روزى سخت خواهد بود.'),
(5505, 74, 10, 'و براى كافران ناآسان.'),
(5506, 74, 11, 'مرا با آن كه تنهايش آفريده‌ام واگذار.'),
(5507, 74, 12, 'او را مالى بسيار دادم.'),
(5508, 74, 13, 'و پسرانى همه در نزد او حاضر.'),
(5509, 74, 14, 'و كارهاى او را به نيكوتر وجهى برساختم.'),
(5510, 74, 15, 'آنگاه طمع مى‌بندد كه زيادت كنم.'),
(5511, 74, 16, 'آرى، كه او در برابر آيات ما ستيزه‌جوست.'),
(5512, 74, 17, 'او را به مشقتى مى‌اندازم.'),
(5513, 74, 18, 'او انديشيد و طرحى افكند.'),
(5514, 74, 19, 'مرگ بر او باد، چگونه طرحى افكند؟'),
(5515, 74, 20, 'باز هم مرگ بر او باد، چگونه طرحى افكند؟'),
(5516, 74, 21, 'آنگاه نگريست.'),
(5517, 74, 22, 'سپس روى ترش كرد و پيشانى در هم كشيد.'),
(5518, 74, 23, 'سپس روى گردانيد و گردنكشى كرد.'),
(5519, 74, 24, 'گفت: اين، جز جادويى كه ديگرانش آموخته‌اند، هيچ نيست.'),
(5520, 74, 25, 'اين، جز سخن آدمى هيچ نيست.'),
(5521, 74, 26, 'زودا كه او را به سَقَر بيفكنيم.'),
(5522, 74, 27, 'چه چيز آگاهت ساخت كه سَقَر چيست؟'),
(5523, 74, 28, 'نه هيچ باقى مى‌گذارد و نه چيزى را وا مى‌گذارد.'),
(5524, 74, 29, 'سوزاننده پوست است.'),
(5525, 74, 30, 'نوزده فرشته بر آن گماشته‌اند.'),
(5526, 74, 31, 'موكلان دوزخ را جز از فرشتگان قرارنداديم. و شمار آنها جز براى امتحان كافران نيست. تا اهل كتاب يقين كنند و بر ايمان مؤمنان بيفزايد و اهل كتاب و مؤمنان شك نكنند. و تا آنان كه در دلهاشان مرضى است نگويند: خدا از اين مثَل چه مى‌خواسته است؟ خدا اينچنين هر كس را كه بخواهد گمراه مى‌كند و هر كس را كه بخواهد راه مى‌نمايد. و شمار سپاهيان پروردگارت را جز او نداند. و اين سخن جز اندرزى از براى مردم نيست.'),
(5527, 74, 32, 'آرى، سوگند به ماه.'),
(5528, 74, 33, 'و سوگند به شب، چون روى در رفتن آرد،'),
(5529, 74, 34, 'و سوگند به صبح، چون پرده برافكند،'),
(5530, 74, 35, 'كه اين يكى از حادثه‌هاى بزرگ است.'),
(5531, 74, 36, 'ترساننده آدميان است.'),
(5532, 74, 37, 'براى هر كس از شما كه خواهد پيش افتد يا از پى رود.'),
(5533, 74, 38, 'هر كس در گرو كارى است كه كرده است.'),
(5534, 74, 39, 'مگر اهل سعادت،'),
(5535, 74, 40, 'كه در بهشتها نشسته‌اند و مى‌پرسند'),
(5536, 74, 41, 'از گناهكاران:'),
(5537, 74, 42, 'كه چه چيز شما را به جهنم كشانيد؟'),
(5538, 74, 43, 'مى‌گويند: ما از نمازگزاران نبوديم.'),
(5539, 74, 44, 'و به درويشان طعام نمى‌داديم.'),
(5540, 74, 45, 'و با آنان كه سخن باطل مى‌گفتند هماواز مى‌شديم،'),
(5541, 74, 46, 'و روز قيامت را دروغ مى‌انگاشتيم.'),
(5542, 74, 47, 'تا مرگ ما فرا رسيد.'),
(5543, 74, 48, 'پس شفاعت شفاعت‌كنندگان سودشان نبخشيد.'),
(5544, 74, 49, 'چه شده است كه از اين پند اعراض مى‌كنند؟'),
(5545, 74, 50, 'مانند گورخران رميده‌'),
(5546, 74, 51, 'كه از شير مى‌گريزند.'),
(5547, 74, 52, 'بلكه هر يك از ايشان مى‌خواهد كه نامه‌هايى گشاده به او داده شود.'),
(5548, 74, 53, 'آرى، كه از آخرت نمى‌ترسند.'),
(5549, 74, 54, 'آرى، كه اين قرآن اندرزى است.'),
(5550, 74, 55, 'هر كه خواهد، آن را بخواند.'),
(5551, 74, 56, 'و پند نمى‌گيرند، مگر اينكه خدا خواهد. او شايان آن است كه از او بترسند. و او شايان آمرزيدن است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'قسم مى‌خورم به روز قيامت.'),
(5553, 75, 2, 'و قسم مى‌خورم به نفس ملامتگر.'),
(5554, 75, 3, 'آيا آدمى مى‌پندارد كه ما استخوانهايش را گرد نخواهيم آورد؟'),
(5555, 75, 4, 'آرى، ما قادر هستيم كه سر انگشتهايش را برابر كنيم.'),
(5556, 75, 5, 'بلكه آدمى مى‌خواهد كه در آينده نيز به كارهاى ناشايست پردازد.'),
(5557, 75, 6, 'مى‌پرسد: روز قيامت چه وقت خواهد بود؟'),
(5558, 75, 7, 'روزى كه چشمها خيره شود،'),
(5559, 75, 8, 'و ماه تيره شود،'),
(5560, 75, 9, 'و آفتاب و ماه در يك جاى گرد آيند.'),
(5561, 75, 10, 'انسان در آن روز مى‌گويد: راه گريز كجاست؟'),
(5562, 75, 11, 'هرگز، پناهگاهى نيست.'),
(5563, 75, 12, 'قرارگاه همه در اين روز نزد پروردگار توست.'),
(5564, 75, 13, 'در اين روز آدمى را از هر چه پيشاپيش فرستاده و بعد از خويش گذاشته است خبر مى‌دهند.'),
(5565, 75, 14, 'بلكه آدمى خويشتن خويش را نيك مى‌شناسد،'),
(5566, 75, 15, 'هر چند به زبان عذرها آورد.'),
(5567, 75, 16, 'به تعجيل زبان به خواندن قرآن مجنبان،'),
(5568, 75, 17, 'كه گردآوردن و خواندنش بر عهده ماست.'),
(5569, 75, 18, 'چون خوانديمش، تو آن خواندن را پيروى كن.'),
(5570, 75, 19, 'سپس بيان آن بر عهده ماست.'),
(5571, 75, 20, 'آرى، شما اين جهان زودگذر را دوست مى‌داريد،'),
(5572, 75, 21, 'و آخرت را فرو مى‌گذاريد.'),
(5573, 75, 22, 'در آن روز چهره‌هايى هست زيبا و درخشان،'),
(5574, 75, 23, 'كه سوى پروردگارشان نظر مى‌كنند؛'),
(5575, 75, 24, 'و چهره‌هايى هست عبوس و ترش،'),
(5576, 75, 25, 'كه مى‌داند آن عذاب كمرشكن بر او فرود آيد.'),
(5577, 75, 26, 'آرى، چون جان به گلوگاه رسد،'),
(5578, 75, 27, 'و گفته شود كه چه كسى است كه افسون بخواند؟'),
(5579, 75, 28, 'و يقين كند كه زمان جدايى فرا رسيده،'),
(5580, 75, 29, 'و ساقهاى پا در هم پيچيده شوند،'),
(5581, 75, 30, 'آن روز روز راندنش به سوى پروردگار توست.'),
(5582, 75, 31, 'نه تصديق كرده است و نه نماز گزارده است.'),
(5583, 75, 32, 'اما تكذيب كرده و اعراض كرده است.'),
(5584, 75, 33, 'آنگاه خرامان نزد كسانش رفته است.'),
(5585, 75, 34, 'واى بر تو، پس واى بر تو.'),
(5586, 75, 35, 'باز هم واى بر تو، پس واى بر تو.'),
(5587, 75, 36, 'آيا انسان مى‌پندارد كه او را به حال خود واگذاشته‌اند؟'),
(5588, 75, 37, 'آيا او نطفه‌اى از منى كه در رحمى ريخته شده، نبوده است؟'),
(5589, 75, 38, 'سپس لخته‌اى خون؟ آنگاه به اندام درستش بيافريد.'),
(5590, 75, 39, 'و آنها را دو صنف كرد: نر و ماده.'),
(5591, 75, 40, 'آيا خداوند قادر نيست كه مردگان را زنده سازد؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'هرآينه بر انسان مدتى از زمان گذشت و او چيزى در خور ذكر نبود.'),
(5593, 76, 2, 'ما آدمى را از نطفه‌اى آميخته بيافريده‌ايم، تا او را امتحان كنيم. و شنوا و بينايش ساخته‌ايم.'),
(5594, 76, 3, 'راه را به او نشان داده‌ايم. يا سپاسگزار باشد يا ناسپاس.'),
(5595, 76, 4, 'ما براى كافران زنجيرها و غلها و آتش افروخته آماده كرده‌ايم.'),
(5596, 76, 5, 'نيكان از جامهايى مى‌نوشند كه آميخته به كافور است:'),
(5597, 76, 6, 'چشمه‌اى كه بندگان خدا از آن مى‌نوشند و آن را به هر جاى كه خواهند روان مى‌سازند.'),
(5598, 76, 7, 'به نذر وفا مى‌كنند و از روزى كه شر آن همه جا را گرفته است مى‌ترسند.'),
(5599, 76, 8, 'و طعام را در حالى كه خود دوستش دارند به مسكين و يتيم و اسير مى‌خورانند:'),
(5600, 76, 9, 'جز اين نيست كه شما را براى خدا اطعام مى‌كنيم و از شما نه پاداشى مى‌خواهيم نه سپاسى.'),
(5601, 76, 10, 'ما از پروردگار خود مى‌ترسيم، در روزى كه آن روز عبوس و سخت و هولناك است.'),
(5602, 76, 11, 'خدا ايشان را از شر آن روز نگه داشت و آنان را طراوت و شادمانى بخشيد.'),
(5603, 76, 12, 'به پاداش صبرى كه كرده‌اند پاداششان را بهشت و حرير داد.'),
(5604, 76, 13, 'در آنجا بر تختها تكيه زده‌اند نه هيچ آفتابى مى‌بينند و نه سرمايى.'),
(5605, 76, 14, 'سايه‌هاى درختانش بر سرشان افتاده و ميوه‌هايش به فرمانشان باشد.'),
(5606, 76, 15, 'كاسه‌هاى سيمين و كوزه‌هاى شراب ميانشان به گردش در مى‌آيد.'),
(5607, 76, 16, 'كوزه‌هايى از سيم كه آنها را به اندازه پُر كرده‌اند.'),
(5608, 76, 17, 'در آنجا جامى بنوشانندشان كه آميخته با زنجبيل باشد،'),
(5609, 76, 18, 'از چشمه‌اى كه آن را سلسبيل مى‌گويند.'),
(5610, 76, 19, 'همواره پسرانى به گردشان مى‌چرخند كه چون آنها را ببينى، پندارى مرواريد پراكنده‌اند.'),
(5611, 76, 20, 'چون بنگرى، هر چه بنگرى نعمت فراوان است و فرمانروايى بزرگ.'),
(5612, 76, 21, 'بر تنشان جامه‌هايى است از سُندس سبز و استبرق. و به دستبندهايى از سيم زينت شده‌اند. و پروردگارشان از شرابى پاكيزه سيرابشان سازد.'),
(5613, 76, 22, 'اين پاداش شماست و از كوششتان سپاسگزارى شده است.'),
(5614, 76, 23, 'ما قرآن را بر تو نازل كرديم، نازل‌كردنى نيكو.'),
(5615, 76, 24, 'در برابر فرمان پروردگارت صابر باش و از هيچ گناهكار يا ناسپاسى اطاعت مكن.'),
(5616, 76, 25, 'و بامدادان و شامگاهان نام پروردگارت را بر زبان بياور.'),
(5617, 76, 26, 'در پاره‌اى از شب سجده‌اش كن و در همه درازناى شب تسبيحش گوى.'),
(5618, 76, 27, 'اينان اين دنياى زودگذر را دوست دارند و آن روز دشوار را پس پشت مى‌افكنند.'),
(5619, 76, 28, 'ما آدميان را آفريديم و اعضا و مفاصلشان را نيرومند گردانيديم و اگر خواهيم، به جاى آنها قومى همانندشان بياوريم.'),
(5620, 76, 29, 'اين اندرزى است. پس هر كه خواهد، راهى به سوى پروردگار خويش برگزيند.'),
(5621, 76, 30, 'و شما جز آن نمى‌خواهيد كه خدا خواسته باشد، زيرا خدا دانا و حكيم است.'),
(5622, 76, 31, 'هر كس را كه بخواهد مشمول رحمت خود سازد، و براى ستمكاران عذابى دردآور آماده كرده است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به بادهايى كه از پى هم مى‌آيند،'),
(5624, 77, 2, 'و سوگند به بادهاى سخت‌وزنده،'),
(5625, 77, 3, 'و سوگند به بادهاى باران‌آور،'),
(5626, 77, 4, 'و سوگند به آنها كه حق را از باطل جدا مى‌كنند،'),
(5627, 77, 5, 'و سوگند به آنها كه ياد خدا را مى آموزند:'),
(5628, 77, 6, 'تمام‌كردن حجت را، يا بيم‌كردن را،'),
(5629, 77, 7, 'كه آنچه به شما وعده داده شود واقع خواهد شد.'),
(5630, 77, 8, 'پس آنگاه كه ستارگان خاموش شوند،'),
(5631, 77, 9, 'و آنگاه كه آسمان بشكافد،'),
(5632, 77, 10, 'و آنگاه كه كوه‌ها پراكنده گردند،'),
(5633, 77, 11, 'و آنگاه كه پيامبران را وقتى معين شود.'),
(5634, 77, 12, 'براى چه روزى معين شود؟'),
(5635, 77, 13, 'براى روز فصل.'),
(5636, 77, 14, 'و چه دانى كه روز فصل چيست؟'),
(5637, 77, 15, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5638, 77, 16, 'آيا اوّليان را هلاك نكرده‌ايم.'),
(5639, 77, 17, 'و آخريان را نيز از پى آنها نمى بريم؟'),
(5640, 77, 18, 'با مجرمان چنين رفتار مى‌كنيم.'),
(5641, 77, 19, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5642, 77, 20, 'آيا شما را از آبى پست و بى‌مقدار نيافريده‌ايم.'),
(5643, 77, 21, 'و آن را در جايگاهى استوار قرار نداديم،'),
(5644, 77, 22, 'تا زمانى معين؟'),
(5645, 77, 23, 'پس ما توانا بوديم و نيك توانا بوديم.'),
(5646, 77, 24, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5647, 77, 25, 'آيا زمين را جايگاهى نساختيم،'),
(5648, 77, 26, 'براى زندگان و مردگان؟'),
(5649, 77, 27, 'در آن كوه‌هاى بلند پديد آورديم و از آبى گوارا سيرابتان ساختيم.'),
(5650, 77, 28, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5651, 77, 29, 'به سوى آنچه دروغش مى‌شمرديد به راه افتيد.'),
(5652, 77, 30, 'به سوى آن دود سه شعبه به راه افتيد،'),
(5653, 77, 31, 'كه نه از آسيبتان نگه دارد و نه دفع آن شعله كند:'),
(5654, 77, 32, 'و جهنم شراره‌هايى مى‌افكند هر يك به بلندى كاخى عظيم،'),
(5655, 77, 33, 'گويى كه اُشترانى زردگونند.'),
(5656, 77, 34, 'در آن روز واى بر تكذيب‌كنندگان،'),
(5657, 77, 35, 'اين روزى است كه كس سخن نگويد.'),
(5658, 77, 36, 'آنها را رخصت ندهند تا پوزش خواهند.'),
(5659, 77, 37, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5660, 77, 38, 'اين روز روز فصل است كه شما و پيشينيانتان را گرد مى‌زوريم.'),
(5661, 77, 39, 'پس اگر حيلتى داريد به كار بريد.'),
(5662, 77, 40, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5663, 77, 41, 'پرهيزگاران در سايه‌ها و كنار چشمه سارانند،'),
(5664, 77, 42, 'با هر ميوه‌اى كه آرزو كنند.'),
(5665, 77, 43, 'به پاداش كارهايى كه مى‌كرده‌ايد بخوريد و بياشاميد، شما را گوارا باد.'),
(5666, 77, 44, 'ما نيكوكاران را اينچنين پاداش مى‌دهيم.'),
(5667, 77, 45, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5668, 77, 46, 'بخوريد و اندك بهره‌اى برگيريد، كه شما مجرمانيد.'),
(5669, 77, 47, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5670, 77, 48, 'و چون گويندشان كه ركوع كنيد، ركوع نمى‌كنند،'),
(5671, 77, 49, 'در آن روز واى بر تكذيب‌كنندگان.'),
(5672, 77, 50, 'پس از قرآن به كدام سخن ايمان مى‌آورند؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'از چه چيز مى‌پرسند؟'),
(5674, 78, 2, 'از آن خبر بزرگ،'),
(5675, 78, 3, 'كه در آن اختلاف مى كنند.'),
(5676, 78, 4, 'آرى، به زودى خواهند دانست.'),
(5677, 78, 5, 'باز هم آرى، به زودى خواهند دانست.'),
(5678, 78, 6, 'آيا ما زمين را بسترى نساختيم؟'),
(5679, 78, 7, 'و كوهها را ميخهايى؟'),
(5680, 78, 8, 'و شما را جفت جفت آفريديم.'),
(5681, 78, 9, 'و خوابتان را آسايشتان گردانيديم.'),
(5682, 78, 10, 'و شب را پوششتان قرار داديم.'),
(5683, 78, 11, 'و روز را گاه طلب معيشت.'),
(5684, 78, 12, 'و بر فراز سرتان هفت آسمان استوار بنا كرديم.'),
(5685, 78, 13, 'و چراغى روشن آفريديم.'),
(5686, 78, 14, 'از ابرهاى باران‌زاى آبى فراوان نازل كرديم،'),
(5687, 78, 15, 'تا بدان دانه و نبات برويانيم،'),
(5688, 78, 16, 'و بستانهاى انبوه.'),
(5689, 78, 17, 'هر آينه روز داورى روزى است معين.'),
(5690, 78, 18, 'روزى كه در صور دميده شود و شما فوج‌فوج بياييد.'),
(5691, 78, 19, 'آسمان شكافته شود و هر شكاف درى باشد.'),
(5692, 78, 20, 'و كوه‌ها روان شوند، و سراب گردند.'),
(5693, 78, 21, 'جهنم در انتظار باشد.'),
(5694, 78, 22, 'طاغيان را بازگشتنگاهى است.'),
(5695, 78, 23, 'زمانى دراز در آنجا درنگ كنند.'),
(5696, 78, 24, 'نه خُنكى چشند و نه آب؛'),
(5697, 78, 25, 'جز آب جوشان و خون و چرك.'),
(5698, 78, 26, 'اين كيفرى است برابر كردار.'),
(5699, 78, 27, 'زيرا آنان به روز حساب اميد نداشتند.'),
(5700, 78, 28, 'و آيات ما را به سختى تكذيب مى‌كردند.'),
(5701, 78, 29, 'و ما همه چيز را در كتابى شماره كرده‌ايم.'),
(5702, 78, 30, 'پس بچشيد كه جز به شكنجه شما نخواهيم افزود.'),
(5703, 78, 31, 'پرهيزگاران را جايى است در امان از هر آسيب؛'),
(5704, 78, 32, 'بستانها و تاكستانها،'),
(5705, 78, 33, 'و دخترانى همسال، با پستانهاى برآمده،'),
(5706, 78, 34, 'و جامهاى پر.'),
(5707, 78, 35, 'نه سخن بيهوده شنوند و نه دروغ.'),
(5708, 78, 36, 'و اين پاداشى است كافى، از جانب پروردگارت.'),
(5709, 78, 37, 'پروردگار آسمانها و زمين و آنچه ميان آنهاست. آن خداى رحمان، كه كس را به او ياراى خطاب نباشد.'),
(5710, 78, 38, 'روزى كه روح و فرشتگان به صف مى‌ايستند، و كس سخن نمى‌گويد مگر آنكه خداى رحمان به او رخصت دهد و او سخن به صواب گويد.'),
(5711, 78, 39, 'آن روز روزى است آمدنى. پس هر كه خواهد به سوى پروردگارش بازگردد.'),
(5712, 78, 40, 'ما شما را از عذابى نزديك مى‌ترسانيم: روزى كه آدمى هر چه را پيشاپيش فرستاده است مى‌نگرد و كافر مى‌گويد: اى كاش من خاك مى‌بودم.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به فرشتگانى كه جانها را به قوت مى‌گيرند،'),
(5714, 79, 2, 'و سوگند به فرشتگانى كه جانها را به آسانى مى‌گيرند،'),
(5715, 79, 3, 'و سوگند به فرشتگانى كه شناورند،'),
(5716, 79, 4, 'و سوگند به فرشتگانى كه بر ديوان پيشى مى‌گيرند،'),
(5717, 79, 5, 'و سوگند به آنها كه تدبير كارها مى‌كنند،'),
(5718, 79, 6, 'كه آن روز كه نخستين نفخه قيامت زمين را بلرزاند،'),
(5719, 79, 7, 'و نفخه دوم از پس آن بيايد،'),
(5720, 79, 8, 'در آن روز دلهايى در هراس باشند،'),
(5721, 79, 9, 'و نشان خشوع در ديدگان نمايان.'),
(5722, 79, 10, 'مى‌گويند: آيا ما به حالت نخستين بازمى‌گرديم،'),
(5723, 79, 11, 'آنگاه كه استخوانهايى پوسيده بوديم؟'),
(5724, 79, 12, 'گويند: اين بازگشت ما بازگشتى است زيان‌آور.'),
(5725, 79, 13, 'جز اين نيست كه تنها يك بانگ برمى‌آيد،'),
(5726, 79, 14, 'و آنها خود را در آن صحرا خواهند يافت.'),
(5727, 79, 15, 'آيا داستان موسى به تو رسيده است؟'),
(5728, 79, 16, 'آنگاه كه پروردگارش او را در وادى مقدس طوى ندا داد:'),
(5729, 79, 17, 'به سوى فرعون برو كه طغيان كرده است.'),
(5730, 79, 18, 'بگو: آيا تو را رغبتى هست كه پاكيزه شوى،'),
(5731, 79, 19, 'و من تو را به پروردگارت راه بنمايم و تو پروا كنى؟'),
(5732, 79, 20, 'پس آن آيت بزرگ را به او نشان داد.'),
(5733, 79, 21, 'و او تكذيب كرد و عصيان ورزيد.'),
(5734, 79, 22, 'سپس پشت كرد و مى‌شتافت.'),
(5735, 79, 23, 'پس همه را گرد كرد و ندا داد،'),
(5736, 79, 24, 'و گفت: من پروردگار برتر شما هستم.'),
(5737, 79, 25, 'خدايش به عذاب آخرت و دنيا گرفتار كرد.'),
(5738, 79, 26, 'در اين عبرتى است براى كسى كه مى‌ترسد.'),
(5739, 79, 27, 'آيا شما به خلقت سخت‌تريد يا اين آسمانى كه او بنا نهاده؟'),
(5740, 79, 28, 'سقفش را برافراشت و بپرداختش،'),
(5741, 79, 29, 'شبش را تاريك كرد و روشنايى‌اش را آشكار ساخت،'),
(5742, 79, 30, 'و پس از آن زمين را بگسترد،'),
(5743, 79, 31, 'و از آن آب بيرون آورد و چراگاه‌ها پديد كرد،'),
(5744, 79, 32, 'و كوه‌ها را استوار گردانيد،'),
(5745, 79, 33, 'براى بهره‌گيرى شما و چارپايانتان.'),
(5746, 79, 34, 'چون آن حادثه بزرگ دررسد،'),
(5747, 79, 35, 'روزى كه آدمى همه اعمال خود را به ياد آورد،'),
(5748, 79, 36, 'و جهنم را به هر كه مى‌بيند نشان دهند؛'),
(5749, 79, 37, 'پس هر كه طغيان كرده،'),
(5750, 79, 38, 'و زندگى اينجهانى را برگزيده،'),
(5751, 79, 39, 'جهنم جايگاه اوست.'),
(5752, 79, 40, 'اما هر كس كه از ايستادن در برابر پروردگارش ترسيده و نفس را از هوى بازداشته،'),
(5753, 79, 41, 'بهشت جايگاه اوست.'),
(5754, 79, 42, 'تو را از قيامت مى‌پرسند كه چه وقت واقع شود.'),
(5755, 79, 43, 'از يادكردن آن تو را چه كار است؟'),
(5756, 79, 44, 'منتهاى آن نزد پروردگار توست.'),
(5757, 79, 45, 'جز اين نيست كه تو كسانى را كه از آن روز بترسند بيم مى‌دهى.'),
(5758, 79, 46, 'چون آن را ببينند، پندارند در اين جهان جز يك شامگاه يا چاشت درنگ نكرده‌اند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'روى را ترش كرد و سر برگردانيد.'),
(5760, 80, 2, 'چون آن نابينا به نزدش آمد.'),
(5761, 80, 3, 'و تو چه دانى، شايد كه او پاكيزه شود،'),
(5762, 80, 4, 'يا پند گيرد و پند تو سودمندش افتد.'),
(5763, 80, 5, 'اما آن كه او توانگر است،'),
(5764, 80, 6, 'تو روى خود بدو مى‌كنى.'),
(5765, 80, 7, 'و اگر هم پاك نگردد چيزى بر عهده تو نيست.'),
(5766, 80, 8, 'و اما آن كه دوان‌دوان به نزد تو مى‌آيد،'),
(5767, 80, 9, 'و مى‌ترسد،'),
(5768, 80, 10, 'تو از او به ديگرى مى‌پردازى.'),
(5769, 80, 11, 'آرى، اين قرآن اندرزى است،'),
(5770, 80, 12, 'پس هر كه خواهد از آن پند گيرد.'),
(5771, 80, 13, 'در صحيفه‌هايى گرامى،'),
(5772, 80, 14, 'بلندقدر و پاكيزه،'),
(5773, 80, 15, 'به دست كاتبانى،'),
(5774, 80, 16, 'بزرگوار و نيكوكار.'),
(5775, 80, 17, 'مرگ بر آدمى باد كه چه ناسپاس است.'),
(5776, 80, 18, 'او را از چه آفريده است؟'),
(5777, 80, 19, 'از نطفه‌اى آفريد و به اندازه پديد آورد.'),
(5778, 80, 20, 'سپس راهش را آسان ساخت.'),
(5779, 80, 21, 'آنگاه بميراندش و در گور كرد.'),
(5780, 80, 22, 'و آنگاه كه خواهد زنده‌اش سازد.'),
(5781, 80, 23, 'نه، كه هنوز آنچه را به او فرمان داده بود به جاى نياورده است.'),
(5782, 80, 24, 'پس آدمى به طعام خود بنگرد.'),
(5783, 80, 25, 'ما باران را فرو باريديم، باريدنى.'),
(5784, 80, 26, 'و زمين را شكافتيم، شكافتنى.'),
(5785, 80, 27, 'و در آن دانه‌ها رويانيديم،'),
(5786, 80, 28, 'و تاك و سبزيهاى خوردنى،'),
(5787, 80, 29, 'و زيتون و نخل،'),
(5788, 80, 30, 'و باغهاى پردرخت،'),
(5789, 80, 31, 'و ميوه و علف،'),
(5790, 80, 32, 'تا شما و چارپايانتان بهره بريد.'),
(5791, 80, 33, 'چون بانگ قيامت برآيد،'),
(5792, 80, 34, 'روزى كه آدمى از برادرش مى‌گريزد،'),
(5793, 80, 35, 'و از مادرش و پدرش،'),
(5794, 80, 36, 'و از زنش و فرزندانش.'),
(5795, 80, 37, 'هر كس را در آن روز كارى است كه به خود مشغولش دارد.'),
(5796, 80, 38, 'چهره‌هايى در آن روز درخشانند،'),
(5797, 80, 39, 'خندانند و شادانند.'),
(5798, 80, 40, 'و چهره‌هايى در آن روز غبارآلودند.'),
(5799, 80, 41, 'در سياهى فرو رفته‌اند.'),
(5800, 80, 42, 'اينان كافران و فاجرانند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'چون خورشيد بى‌فروغ شود،'),
(5802, 81, 2, 'و چون ستارگان فرو ريزند،'),
(5803, 81, 3, 'و چون كوه‌ها از جاى خود بشوند،'),
(5804, 81, 4, 'و چون شتران آبستن به حال خود رها شوند،'),
(5805, 81, 5, 'و چون وحوش گرد آورده شوند،'),
(5806, 81, 6, 'و چون درياها آتش گيرند،'),
(5807, 81, 7, 'و چون روحها با تنها قرين گردند،'),
(5808, 81, 8, 'و چون از دختر زنده به گور شده پرسيده شود كه‌'),
(5809, 81, 9, 'به چه گناهى كشته شده است،'),
(5810, 81, 10, 'و چون صحيفه‌ها باز شوند،'),
(5811, 81, 11, 'و چون آسمان از جاى خود كنده شود،'),
(5812, 81, 12, 'و چون جهنم افروخته گردد،'),
(5813, 81, 13, 'و چون بهشت را پيش آورند،'),
(5814, 81, 14, 'هر كس بداند كه چه حاضر آورده است.'),
(5815, 81, 15, 'سوگند به ستارگان بازگردنده،'),
(5816, 81, 16, 'سيركننده غايب شونده،'),
(5817, 81, 17, 'و سوگند به شب چون تاريك شود،'),
(5818, 81, 18, 'و سوگند به صبح چون بدمد،'),
(5819, 81, 19, 'كه اين سخن رسولى بزرگوار است.'),
(5820, 81, 20, 'نيرومند، در نزد خداوند عرش، صاحب مكانت،'),
(5821, 81, 21, 'آنجا مطاع امين.'),
(5822, 81, 22, 'و همصحبت شما ديوانه نيست.'),
(5823, 81, 23, 'هر آينه او را در افق روشن ديده است.'),
(5824, 81, 24, 'و آنچه را از غيب به او گويند دريغ نمى‌دارد.'),
(5825, 81, 25, 'و آن سخن، سخن شيطان رجيم نيست.'),
(5826, 81, 26, 'پس به كجا مى‌رويد؟'),
(5827, 81, 27, 'اين كتاب پندى است براى جهانيان.'),
(5828, 81, 28, 'براى هر كس از شما كه بخواهد به راه راست افتد.'),
(5829, 81, 29, 'و شما نمى‌خواهيد مگر آنچه را كه پروردگار جهانيان خواسته باشد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'آنگاه كه آسمان بشكافد،'),
(5831, 82, 2, 'و آنگاه كه ستارگان پراكنده شوند،'),
(5832, 82, 3, 'و آنگاه كه درياها به هم بپيوندند،'),
(5833, 82, 4, 'و آنگاه كه قبرها زير و زبر گردند،'),
(5834, 82, 5, 'هر كس مى‌داند چه چيز پيشاپيش فرستاده و چه چيز بر جاى گذاشته است.'),
(5835, 82, 6, 'اى آدمى، چه چيز تو را به پروردگار كريمت مغرور كرده است؟'),
(5836, 82, 7, 'آن كه تو را بيافريد و اعضايت تمام كرد و اعتدالت بخشيد.'),
(5837, 82, 8, 'و به هر صورتى كه خواست اعضاى تو را به هم انداخت.'),
(5838, 82, 9, 'آگاه باشيد كه شما روز جزا را دروغ مى‌انگاريد.'),
(5839, 82, 10, 'حال آنكه بر شما محافظانى گمارده شده‌اند:'),
(5840, 82, 11, 'كاتبانى بزرگوار،'),
(5841, 82, 12, 'مى‌دانند كه شما چه مى‌كنيد.'),
(5842, 82, 13, 'هر آينه نيكوكاران در نعمتند،'),
(5843, 82, 14, 'و گناهكاران در جهنم.'),
(5844, 82, 15, 'در روز شمار به جهنم درآيند،'),
(5845, 82, 16, 'و از آن غايب نشوند.'),
(5846, 82, 17, 'تو چه مى‌دانى كه روز شمار چيست؟'),
(5847, 82, 18, 'باز هم تو چه مى‌دانى كه روز شمار چيست؟'),
(5848, 82, 19, 'روزى است كه كسى براى كسى ديگر هيچ كارى نتواند كرد و در آن روز فرمان فرمان خدا باشد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'واى بر كم‌فروشان.'),
(5850, 83, 2, 'آنان كه چون از مردم كيل مى‌ستانند آن را پُر مى‌كنند؛'),
(5851, 83, 3, 'و چون براى مردم مى‌پيمايند يا مى‌كشند از آن مى‌كاهند.'),
(5852, 83, 4, 'آيا اينان نمى‌دانند كه زنده مى‌شوند.'),
(5853, 83, 5, 'در آن روز بزرگ؟'),
(5854, 83, 6, 'روزى كه مردم به پيشگاه پروردگار جهانيان مى‌ايستند.'),
(5855, 83, 7, 'آگاه باشيد كه اعمال بدكاران مكتوب در سِجّين است.'),
(5856, 83, 8, 'تو چه مى‌دانى كه سجين چيست؟'),
(5857, 83, 9, 'كتابى است نوشته.'),
(5858, 83, 10, 'در آن روز واى بر تكذيب‌كنندگان:'),
(5859, 83, 11, 'آنان كه روز جزا را دروغ انگاشتند.'),
(5860, 83, 12, 'و آن روز را جز متجاوزى گناهكار دروغ نينگارد.'),
(5861, 83, 13, 'چون آيات ما بر او خوانده شد، گفت: افسانه‌هاى پيشينيان است.'),
(5862, 83, 14, 'حقا، كه كارهايى كه كرده بودند بر دلهاشان مسلط شده است.'),
(5863, 83, 15, 'حقا، كه در آن روز از پروردگارشان محجوب باشند.'),
(5864, 83, 16, 'پس ايشان به جهنم درآيند.'),
(5865, 83, 17, 'و آنها را گويند: اين است آنچه دروغش مى‌شمرديد.'),
(5866, 83, 18, 'حقا، كه اعمال نيكان در عِلّيّين مكتوب است.'),
(5867, 83, 19, 'و تو چه مى‌دانى كه عِلّيّين چيست؟'),
(5868, 83, 20, 'كتابى است نوشته،'),
(5869, 83, 21, 'كه مقربان خدا در آن مى‌نگرند.'),
(5870, 83, 22, 'هر آينه نيكان در نعمتند،'),
(5871, 83, 23, 'بر تختها نشسته و نظاره مى‌كنند.'),
(5872, 83, 24, 'بر چهره‌هاشان طراوت نعمت را بشناسى.'),
(5873, 83, 25, 'از شرابى خالص كه بر سر آن مُهر نهاده‌اند سيراب مى‌شوند.'),
(5874, 83, 26, 'مُهر آن از مشك است و پيشدستى‌كنندگان در آن بر يكديگر پيشدستى مى‌كنند.'),
(5875, 83, 27, 'آميزه آن تَسنيم است.'),
(5876, 83, 28, 'چشمه‌اى است كه مقربان خدا از آن مى‌آشامند.'),
(5877, 83, 29, 'گناهكاران به مؤمنان مى‌خنديدند.'),
(5878, 83, 30, 'و چون بر آنها مى‌گذشتند، به چشم و ابرو اشاره مى‌كردند.'),
(5879, 83, 31, 'و چون نزد كسانشان بازمى‌گشتند، شادمانه بازمى‌گشتند.'),
(5880, 83, 32, 'و چون آنان را مى‌ديدند، مى‌گفتند كه اينان گمراهند.'),
(5881, 83, 33, 'و حال آنكه آنان را به مواظبت اعمال مؤمنان نفرستاده بودند.'),
(5882, 83, 34, 'و امروز مؤمنان به كافران مى‌خندند.'),
(5883, 83, 35, 'بر تختها تكيه زده‌اند و مى‌نگرند.'),
(5884, 83, 36, 'آيا كافران برابر اعمالشان پاداش يافته‌اند؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'چون آسمان شكافته شود،'),
(5886, 84, 2, 'و به فرمان پروردگارش گوش دهد و حق بود كه چنين كند.'),
(5887, 84, 3, 'و چون زمين منبسط شود،'),
(5888, 84, 4, 'و هر چه را كه در درون دارد بيرون افكند و تهى گردد،'),
(5889, 84, 5, 'و به فرمان پروردگارش گوش دهد و حق بود كه چنين كند.'),
(5890, 84, 6, 'اى انسان، تو در راه پروردگارت رنج فراوان مى‌كشى؛ پس پاداش آن را خواهى ديد.'),
(5891, 84, 7, 'هر كس كه نامه‌اش را به دست راستش دهند،'),
(5892, 84, 8, 'زودا كه آسان از او حساب كشند،'),
(5893, 84, 9, 'و شادمان نزد كسانش باز گردد.'),
(5894, 84, 10, 'اما هر كس كه نامه‌اش از پشت سر داده شود،'),
(5895, 84, 11, 'زودا كه بگويد: واى بر من كه هلاك شدم.'),
(5896, 84, 12, 'و به آتش افروخته درآيد.'),
(5897, 84, 13, 'او در دنيا نزد كسانش شادمان زيسته بود.'),
(5898, 84, 14, 'و مى‌پنداشته است كه هرگز باز نخواهد گشت.'),
(5899, 84, 15, 'آرى پروردگارش او را مى‌ديده است.'),
(5900, 84, 16, 'پس به شفق سوگند مى‌خورم،'),
(5901, 84, 17, 'و سوگند به شب و هر چه را فرو پوشد،'),
(5902, 84, 18, 'و سوگند به ماه چون فروغش كامل شود،'),
(5903, 84, 19, 'كه به حالى بعد از حال ديگر تحول خواهيد يافت.'),
(5904, 84, 20, 'چه مى‌شودشان كه ايمان نمى‌آورند؟'),
(5905, 84, 21, 'و چون قرآن بر آنها خوانده شود سجده نمى‌كنند؟'),
(5906, 84, 22, 'بلكه، كافران تكذيب مى‌كنند.'),
(5907, 84, 23, 'و خدا به آنچه در دل دارند آگاه است.'),
(5908, 84, 24, 'پس آنان را به عذابى دردآور بشارت ده؛'),
(5909, 84, 25, 'مگر آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند كه پاداششان را پايانى نيست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'قسم به آسمان كه دارنده برجهاست،'),
(5911, 85, 2, 'و قسم به روز موعود،'),
(5912, 85, 3, 'و قسم به شهادت‌دهنده و آنچه بدان شهادت دهند.'),
(5913, 85, 4, 'كه اصحاب اُخدود به هلاكت رسيدند.'),
(5914, 85, 5, 'آتشى افروخته از هيزمها،'),
(5915, 85, 6, 'آنگاه كه بر كنار آن آتش نشسته بودند،'),
(5916, 85, 7, 'و بر آنچه بر سر مؤمنان مى‌آوردند، شاهد بودند.'),
(5917, 85, 8, 'و هيچ عيبى در آنها نيافتند جز آنكه به خداى پيروزمند در خور ستايش ايمان آورده بودند.'),
(5918, 85, 9, 'آن خدايى كه فرمانروايى آسمانها و زمين از آن اوست، و بر هر چيزى ناظر است.'),
(5919, 85, 10, 'هر آينه آنان كه مردان و زنان مؤمن را شكنجه كردند و توبه نكردند، عذاب جهنم و عذاب آتش براى آنهاست.'),
(5920, 85, 11, 'براى كسانى كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند بهشتهايى است كه در آن نهرها جارى است و آن كاميابى بزرگى است.'),
(5921, 85, 12, 'فروگرفتن پروردگار تو سخت است.'),
(5922, 85, 13, 'اوست كه نخست مى‌آفريند و پس از مرگ زنده مى‌سازد.'),
(5923, 85, 14, 'او آمرزنده و دوست‌دارنده است.'),
(5924, 85, 15, 'اوست صاحب عرش ارجمند.'),
(5925, 85, 16, 'هر چه را اراده كند به انجام مى‌رساند.'),
(5926, 85, 17, 'آيا داستان لشكرها را شنيده‌اى؟'),
(5927, 85, 18, 'لشكرهاى فرعون و قوم ثمود؟'),
(5928, 85, 19, 'آرى، آنان كه راه كفر پيش گرفته‌اند همچنان در تكذيب هستند.'),
(5929, 85, 20, 'و خدا از همه سو بر آنها احاطه دارد.'),
(5930, 85, 21, 'بلى اين قرآن مجيد است،'),
(5931, 85, 22, 'در لوح محفوظ.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و به آنچه در شب آيد.'),
(5933, 86, 2, 'و تو چه دانى كه آنچه در شب آيد چيست؟'),
(5934, 86, 3, 'ستاره‌اى است درخشنده.'),
(5935, 86, 4, 'هيچ كس نيست مگر آنكه بر او نگهبانى است.'),
(5936, 86, 5, 'پس آدمى بنگرد كه از چه چيز آفريده شده است،'),
(5937, 86, 6, 'از آبى جهنده آفريده شده است،'),
(5938, 86, 7, 'كه از ميان پشت و سينه بيرون مى‌آيد.'),
(5939, 86, 8, 'خدا به باز گردانيدن او تواناست.'),
(5940, 86, 9, 'روزى كه رازها آشكار مى‌شود،'),
(5941, 86, 10, 'او را نه نيرويى است و نه ياورى.'),
(5942, 86, 11, 'قسم به آسمان بازبارنده،'),
(5943, 86, 12, 'قسم به زمين رويان شكاف خورده،'),
(5944, 86, 13, 'كه اين كتاب، حق را از باطل جدا كند،'),
(5945, 86, 14, 'و سخن هزل نيست.'),
(5946, 86, 15, 'آنان حيله‌اى مى‌انديشند،'),
(5947, 86, 16, 'و من هم حيله‌اى مى‌انديشم.'),
(5948, 86, 17, 'پس كافران را مهلت ده، اندك مهلتشان ده.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار بزرگ خويش را به پاكى ياد كن:'),
(5950, 87, 2, 'آن كه آفريد و درست‌اندام آفريد.'),
(5951, 87, 3, 'و آن كه اندازه معين كرد. سپس راه نمود.'),
(5952, 87, 4, 'و آن كه چراگاه‌ها را رويانيد،'),
(5953, 87, 5, 'سپس خشك و سياه گردانيد.'),
(5954, 87, 6, 'زودا كه براى تو بخوانيم، مباد كه فراموش كنى.'),
(5955, 87, 7, 'مگر آنچه خدا بخواهد. اوست كه آشكارا و نهان را مى‌داند.'),
(5956, 87, 8, 'و تو را به كيش آسان توفيق دهيم.'),
(5957, 87, 9, 'اگر پند دادنت سود كند، پند ده.'),
(5958, 87, 10, 'آن كه مى‌ترسد، پند مى‌پذيرد.'),
(5959, 87, 11, 'و بدبخت از آن دورى مى‌گزيند:'),
(5960, 87, 12, 'آن كه به آتش بزرگ جهنم درافتد،'),
(5961, 87, 13, 'و در آنجا نه بميرد و نه زنده باشد.'),
(5962, 87, 14, 'هر آينه پاكان رستگار شدند؛'),
(5963, 87, 15, 'آنان كه نام پروردگار خود را بر زبان آوردند و نماز گزاردند.'),
(5964, 87, 16, 'آرى، شما زندگى اين جهان را بر مى‌گزينيد،'),
(5965, 87, 17, 'حال آنكه آخرت بهتر و پاينده‌تر است.'),
(5966, 87, 18, 'اين سخن در صحيفه‌هاى نخستين است:'),
(5967, 87, 19, 'صحيفه‌هاى ابراهيم و موسى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آيا داستان غاشيه به تو رسيده است؟'),
(5969, 88, 2, 'در آن روز وحشت در چهره‌ها پديدار است،'),
(5970, 88, 3, 'تلاش كرده و رنج ديده،'),
(5971, 88, 4, 'در آتش سوزان در افتند.'),
(5972, 88, 5, 'از آن چشمه بسيار گرم آبشان دهند،'),
(5973, 88, 6, 'طعامى جز خار ندارند،'),
(5974, 88, 7, 'كه نه فربه مى‌كند و نه دفع گرسنگى.'),
(5975, 88, 8, 'و در آن روز چهره‌هايى تازه باشند:'),
(5976, 88, 9, 'از كار خويشتن خشنود،'),
(5977, 88, 10, 'در بهشتى برين،'),
(5978, 88, 11, 'كه در آن سخن لغو نشنوى،'),
(5979, 88, 12, 'و در آن چشمه‌سارها روان باشد،'),
(5980, 88, 13, 'و تختهايى بلندزده،'),
(5981, 88, 14, 'و سبوهايى نهاده،'),
(5982, 88, 15, 'و بالشهايى در كنار هم چيده،'),
(5983, 88, 16, 'و فرشهايى گسترده.'),
(5984, 88, 17, 'آيا به شتر نمى‌نگرند كه چگونه آفريده شده؟'),
(5985, 88, 18, 'و به آسمان كه چسانش برافراشته‌اند؟'),
(5986, 88, 19, 'و به كوه‌ها كه چگونه بركشيده‌اند؟'),
(5987, 88, 20, 'و به زمين كه چسان گسترده شده؟'),
(5988, 88, 21, 'پس پند ده، كه تو پند دهنده‌اى هستى.'),
(5989, 88, 22, 'تو بر آنان فرمانروا نيستى.'),
(5990, 88, 23, 'مگر آن كس كه رويگردان شد و كفر ورزيد،'),
(5991, 88, 24, 'پس خدايش به عذاب بزرگ‌تر عذاب مى‌كند.'),
(5992, 88, 25, 'هر آينه بازگشتشان به سوى ماست.'),
(5993, 88, 26, 'سپس حسابشان با ماست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپيده صبح،'),
(5995, 89, 2, 'و سوگند به شبهاى دهگانه،'),
(5996, 89, 3, 'و سوگند به جفت و طاق،'),
(5997, 89, 4, 'و سوگند به شب چون روى به رفتن نهد.'),
(5998, 89, 5, 'آيا خردمند را اين سوگندها كافى است؟'),
(5999, 89, 6, 'آيا نديده‌اى كه پروردگار تو با قوم عاد چه كرد؟'),
(6000, 89, 7, 'با اِرَم كه ستونها داشت؟'),
(6001, 89, 8, 'و همانند آن در هيچ شهرى آفريده نشده بود.'),
(6002, 89, 9, 'و قوم ثمود كه در آن وادى سنگ را مى‌بريدند،'),
(6003, 89, 10, 'و قوم فرعون آن دارنده ميخها،'),
(6004, 89, 11, 'آنها در بلاد طغيان كردند،'),
(6005, 89, 12, 'و در آنها به فساد در افزودند.'),
(6006, 89, 13, 'و پروردگار تو تازيانه عذاب را بر سرشان فرود آورد.'),
(6007, 89, 14, 'زيرا پروردگارت به كمينگاه است.'),
(6008, 89, 15, 'اما آدمى، چون پروردگارش بيازمايد و گرامى‌اش دارد و نعمتش دهد، مى‌گويد: پروردگار من مرا گرامى داشت.'),
(6009, 89, 16, 'و چون بيازمايدش و رزق بر او تنگ گيرد، مى‌گويد: پروردگار من مرا خوار ساخت.'),
(6010, 89, 17, 'نه چنان است. شما يتيم را گرامى نمى‌داريد،'),
(6011, 89, 18, 'و يكديگر را به اطعام مسكين ترغيب نمى‌كنيد،'),
(6012, 89, 19, 'و ميراث را حريصانه مى‌خوريد،'),
(6013, 89, 20, 'و مال را فراوان دوست داريد.'),
(6014, 89, 21, 'آرى، چون زمين شكسته شود و شكسته شود،'),
(6015, 89, 22, 'و امر پروردگار تو فرا رسد و فرشتگان صف در صف،'),
(6016, 89, 23, 'و در آن روز جهنم را حاضر آرند، آدمى پند گيرد. و چه جاى پند گرفتن باشد؟'),
(6017, 89, 24, 'مى‌گويد: اى كاش زنده‌گشتن خويش را پيشاپيش چيزى مى‌فرستادم.'),
(6018, 89, 25, 'در آن روز كسى چون عذاب او عذاب نكند،'),
(6019, 89, 26, 'و همانند زنجير او به زنجير نكشد.'),
(6020, 89, 27, 'اى روح آرامش يافته،'),
(6021, 89, 28, 'خشنود و پسنديده به سوى پروردگارت باز گرد،'),
(6022, 89, 29, 'و در زمره بندگان من داخل شو،'),
(6023, 89, 30, 'و به بهشت من درآى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'قسم به اين شهر.'),
(6025, 90, 2, 'و تو در اين شهر سكنا گرفته‌اى.'),
(6026, 90, 3, 'و قسم به پدر و فرزندانى كه پديد آورد،'),
(6027, 90, 4, 'كه آدمى را در رنج و محنت بيافريده‌ايم،'),
(6028, 90, 5, 'آيا مى‌پندارد كه كس بر او چيره نگردد؟'),
(6029, 90, 6, 'مى‌گويد: مالى فراوان را تباه كردم.'),
(6030, 90, 7, 'آيا مى‌پندارد كه كسى او را نديده است؟'),
(6031, 90, 8, 'آيا براى او دو چشم نيافريده‌ايم؟'),
(6032, 90, 9, 'و يك زبان و دو لب؟'),
(6033, 90, 10, 'و دو راه پيش پايش ننهاديم؟'),
(6034, 90, 11, 'و او در آن گذرگاه سخت قدم ننهاد.'),
(6035, 90, 12, 'و تو چه دانى كه گذرگاه سخت چيست؟'),
(6036, 90, 13, 'آزاد كردن بنده است،'),
(6037, 90, 14, 'يا طعام دادن در روز قحطى،'),
(6038, 90, 15, 'خاصه به يتيمى كه خويشاوند باشد،'),
(6039, 90, 16, 'يا به مسكينى خاك‌نشين.'),
(6040, 90, 17, 'تا از كسانى باشد كه ايمان آورده‌اند و يكديگر را به صبر سفارش كرده‌اند و به بخشايش.'),
(6041, 90, 18, 'اينان اهل سعادتند.'),
(6042, 90, 19, 'و كسانى كه به آيات ما كافرند اهل شقاوتند.'),
(6043, 90, 20, 'نصيب آنهاست آتشى كه از هر سو سرش پوشيده است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به آفتاب و روشنى‌اش به هنگام چاشت،'),
(6045, 91, 2, 'و سوگند به ماه چون از پى آن برآيد،'),
(6046, 91, 3, 'و سوگند به روز چون گيتى را روشن كند،'),
(6047, 91, 4, 'و سوگند به شب چون فرو پوشدش،'),
(6048, 91, 5, 'و سوگند به آسمان و آن كه آن را بر آورده،'),
(6049, 91, 6, 'و سوگند به زمين و آن كه آن را بگسترده،'),
(6050, 91, 7, 'و سوگند به نفس و آن كه نيكويش بيافريده -'),
(6051, 91, 8, 'سپس بديها و پرهيزگاريهايش را به او الهام كرده -'),
(6052, 91, 9, 'كه: هر كه در پاكى آن كوشيد رستگار شد،'),
(6053, 91, 10, 'و هر كه در پليدى‌اش فرو پوشيد نوميد گرديد.'),
(6054, 91, 11, 'قوم ثمود از روى سركشى تكذيب كردند.'),
(6055, 91, 12, 'آنگاه كه شقى ترينشان برخاست.'),
(6056, 91, 13, 'پيامبر خدا به آنها گفت كه ماده‌شتر خدا را با آبشخورش واگذاريد.'),
(6057, 91, 14, 'تكذيبش كردند و شتر را پى كردند. پس پروردگارشان به سبب گناهشان بر سرشان عذاب آورد و با خاك يكسان ساخت.'),
(6058, 91, 15, 'و او از سرانجام آن بيمناك نشد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب آنگاه كه جهان را در خود فرو پوشد،'),
(6060, 92, 2, 'و سوگند به روز آنگاه كه آشكار شود،'),
(6061, 92, 3, 'و سوگند به آن كه نر و ماده را بيافريد،'),
(6062, 92, 4, 'كه: حاصل كوششهاى شما متفاوت است.'),
(6063, 92, 5, 'اما كسى كه بخشايش و پرهيزگارى كرد،'),
(6064, 92, 6, 'و آن بهترين را تصديق كرد،'),
(6065, 92, 7, 'پس براى بهشت آماده‌اش مى‌كنيم.'),
(6066, 92, 8, 'اما آن كس كه بخل و بى نيازى ورزيد،'),
(6067, 92, 9, 'و آن بهترين را تكذيب كرد،'),
(6068, 92, 10, 'او را براى دوزخ آماده مى‌سازيم.'),
(6069, 92, 11, 'و چون هلاكش دررسد، دارايى‌اش به حالش سود نبخشد.'),
(6070, 92, 12, 'و آنچه بر ماست راهنمايى است.'),
(6071, 92, 13, 'و از آنِ ماست آن جهان و اين جهان.'),
(6072, 92, 14, 'پس شما را از آتشى كه زبانه مى‌كشد مى‌ترسانم.'),
(6073, 92, 15, 'جز اهل شقاوت بدان درنيفتد:'),
(6074, 92, 16, 'آن كه تكذيب كرد و رويگردان شد.'),
(6075, 92, 17, 'و پرهيزگارترين مردم را از آن دور دارند:'),
(6076, 92, 18, 'آن كه مال خويش مى‌بخشايد و پاكى مى‌جويد.'),
(6077, 92, 19, 'و هيچ كس را بر او حقى نباشد كه اينك پاداش خواهد،'),
(6078, 92, 20, 'مگر جستن خشنودى پروردگار والاى خويش.'),
(6079, 92, 21, 'و زودا كه خشنود شود.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به آغاز روز،'),
(6081, 93, 2, 'و سوگند به شب چون آرام و در خود شود،'),
(6082, 93, 3, 'كه پروردگارت تو را ترك نكرده و بر تو خشم نگرفته است.'),
(6083, 93, 4, 'هر آينه آخرت براى تو بهتر از دنياست.'),
(6084, 93, 5, 'به زودى پروردگارت تو را عطا خواهد داد تا خشنود شوى.'),
(6085, 93, 6, 'آيا تو را يتيم نيافت و پناهت داد؟'),
(6086, 93, 7, 'آيا تو را گمگشته نيافت و هدايتت كرد؟'),
(6087, 93, 8, 'آيا تو را درويش نيافت و توانگرت گردانيد؟'),
(6088, 93, 9, 'پس يتيم را ميازار،'),
(6089, 93, 10, 'و گدا را مَران،'),
(6090, 93, 11, 'و از نعمت پروردگارت سخن بگوى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آيا سينه‌ات را برايت نگشوديم؟'),
(6092, 94, 2, 'و بار گرانت را از پشتت برنداشتيم؟'),
(6093, 94, 3, 'بارى كه بر پشت تو سنگينى مى‌كرد؟'),
(6094, 94, 4, 'آيا تو را بلندآوازه نساختيم؟'),
(6095, 94, 5, 'پس، از پى دشوارى آسانى است.'),
(6096, 94, 6, 'هر آينه از پى دشوارى آسانى است.'),
(6097, 94, 7, 'چون از كار فارغ شوى، به عبادت كوش.'),
(6098, 94, 8, 'و به پروردگارت مشتاق شو.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به انجير و زيتون،'),
(6100, 95, 2, 'سوگند به طور مبارك،'),
(6101, 95, 3, 'سوگند به اين شهر ايمن،'),
(6102, 95, 4, 'كه ما آدمى را در نيكوتر اعتدالى بيافريديم.'),
(6103, 95, 5, 'آنگاه او را فروتر از همه فروتران گردانيديم.'),
(6104, 95, 6, 'مگر آنان كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند كه پاداشى بى‌پايان دارند.'),
(6105, 95, 7, 'پس چيست كه با اين حال تو را به تكذيب قيامت وامى‌دارد؟'),
(6106, 95, 8, 'آيا خدا داورترين داوران نيست؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت كه بيافريد.'),
(6108, 96, 2, 'آدمى را از لخته‌خونى بيافريد.'),
(6109, 96, 3, 'بخوان، و پروردگار تو ارجمندترين است.'),
(6110, 96, 4, 'خدايى كه به وسيله قلم آموزش داد،'),
(6111, 96, 5, 'به آدمى آنچه را كه نمى‌دانست بياموخت.'),
(6112, 96, 6, 'حقا كه آدمى نافرمانى مى‌كند،'),
(6113, 96, 7, 'هرگاه كه خويشتن را بى‌نياز بيند.'),
(6114, 96, 8, 'هر آينه بازگشت به سوى پروردگار توست.'),
(6115, 96, 9, 'آيا ديدى آن كس را كه منع مى‌كند'),
(6116, 96, 10, 'بنده‌اى را كه نماز مى‌خواند؟'),
(6117, 96, 11, 'چه مى‌بينى اگر آن مرد بر طريق هدايت باشد؟'),
(6118, 96, 12, 'يا به پرهيزگارى فرمان دهد؟'),
(6119, 96, 13, 'چه مى‌بينى اگر تكذيب كند و رويگردان شود؟'),
(6120, 96, 14, 'آيا ندانسته است كه خدا مى‌بيند؟'),
(6121, 96, 15, 'حقا، كه اگر بازنايستد موى پيش سرش را مى‌گيريم و مى‌كشيم.'),
(6122, 96, 16, 'موى پيش سر دروغگوى خطاكار را.'),
(6123, 96, 17, 'پس همدمان خود را بخواند.'),
(6124, 96, 18, 'ما نيز كارگزاران دوزخ را فرا مى‌خوانيم.'),
(6125, 96, 19, 'نه، هرگز، از او پيروى مكن و سجده كن و به خدا نزديك شو.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما در شب قدرش نازل كرديم.'),
(6127, 97, 2, 'و تو چه دانى كه شب قدر چيست؟'),
(6128, 97, 3, 'شب قدر بهتر از هزار ماه است.'),
(6129, 97, 4, 'در آن شب فرشتگان و روح به فرمان پروردگارشان براى انجام دادن كارها نازل مى‌شوند.'),
(6130, 97, 5, 'آن شب تا طلوع بامداد همه سلام و درود است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'كافران اهل كتاب و مشركان دست برندارند تا برايشان برهانى روشن بيايد:'),
(6132, 98, 2, 'پيامبرى از جانب خدا كه صحيفه‌هاى پاك را مى‌خواند.'),
(6133, 98, 3, 'در آنها نوشته‌هايى است راست و درست.'),
(6134, 98, 4, 'و اهل كتاب پراكنده نشدند مگر از آن پس كه برايشان برهانى روشن آمد.'),
(6135, 98, 5, 'و آنان را جز اين فرمان ندادند كه خدا را بپرستند در حالى كه در دين او اخلاص مى‌ورزند. و نماز گزارند و زكات دهند. اين است دين درست و راست.'),
(6136, 98, 6, 'از اهل كتاب، آنها كه كافرند و نيز مشركان، در آتش جهنمند و در آن همواره خواهند بود. اينان بدترين آفريدگانند.'),
(6137, 98, 7, 'كسانى كه ايمان آورده‌اند و كارهاى شايسته مى‌كنند بهترين آفريدگانند.'),
(6138, 98, 8, 'پاداششان در نزد پروردگارشان بهشتهايى است جاويد كه در آن نهرها جارى است. تا ابد در آنجايند. خدا از آنها خشنود است و آنها از خدا خشنودند. و اين خاص كسى است كه از پروردگارش بترسد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'آنگاه كه زمين لرزانده شود به سخت‌ترين لرزه‌هايش،'),
(6140, 99, 2, 'و زمين بارهاى سنگينش را بيرون ريزد،'),
(6141, 99, 3, 'و آدمى بگويد كه زمين را چه رسيده است؟'),
(6142, 99, 4, 'در اين روز زمين خبرهاى خويش را حكايت مى‌كند:'),
(6143, 99, 5, 'از آنچه پروردگارت به او وحى كرده است.'),
(6144, 99, 6, 'در آن روز مردم پراكنده از قبرها بيرون مى‌آيند تا اعمالشان را به آنها بنمايانند.'),
(6145, 99, 7, 'پس هر كس به وزن ذره‌اى نيكى كرده باشد آن را مى‌بيند.'),
(6146, 99, 8, 'و هر كس به وزن ذره‌اى بدى كرده باشد آن را مى‌بيند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به اسبان دونده‌اى كه نفس‌نفس مى‌زنند،'),
(6148, 100, 2, 'سوگند به اسبانى كه به سُم از سنگ آتش مى‌جهانند،'),
(6149, 100, 3, 'و سوگند به اسبانى كه بامدادان هجوم آورند،'),
(6150, 100, 4, 'و در آنجا غبار برانگيزند،'),
(6151, 100, 5, 'و در آنجا همه را در ميان گيرند،'),
(6152, 100, 6, 'كه: آدمى پروردگار خود را سپاس نمى‌گويد،'),
(6153, 100, 7, 'و او خود بر اين گواه است،'),
(6154, 100, 8, 'او مال را فراوان دوست دارد.'),
(6155, 100, 9, 'آيا نمى‌داند كه چون آنچه در گورهاست زنده گردد،'),
(6156, 100, 10, 'و آنچه در دلها نهان است آشكار شود،'),
(6157, 100, 11, 'پروردگارشان در آن روز از حالشان آگاه است؟');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'قارعه.'),
(6159, 101, 2, 'چيست قارعه؟'),
(6160, 101, 3, 'و تو چه دانى كه قارعه چيست؟'),
(6161, 101, 4, 'روزى است كه مردم چون پروانگان پراكنده باشند،'),
(6162, 101, 5, 'و كوه‌ها چون پشم‌زده شده.'),
(6163, 101, 6, 'اما هر كه كفّه ترازويش سنگين باشد،'),
(6164, 101, 7, 'در يك زندگى پسنديده است.'),
(6165, 101, 8, 'و اما هر كه كفه ترازويش سبك باشد،'),
(6166, 101, 9, 'جايگاهش در هاويه است.'),
(6167, 101, 10, 'و تو چه دانى كه هاويه چيست؟'),
(6168, 101, 11, 'آتشى است در نهايت گرمى.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'به غفلت كشيد شما را نازشِ به بسيارى مال و فرزند،'),
(6170, 102, 2, 'تا به گورها رسيديد.'),
(6171, 102, 3, 'حقا كه به زودى خواهيد دانست.'),
(6172, 102, 4, 'باز هم حقا كه به زودى خواهيد دانست.'),
(6173, 102, 5, 'حقا، اگر از روى يقين بدانيد،'),
(6174, 102, 6, 'البته جهنم را خواهيد ديد.'),
(6175, 102, 7, 'سپس به چشم يقينش خواهيد ديد،'),
(6176, 102, 8, 'كه در آن روز شما را از نعمتهاى دنيوى بازخواست مى‌كنند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به اين زمان،'),
(6178, 103, 2, 'كه آدمى در خسران است.'),
(6179, 103, 3, 'مگر آنها كه ايمان آوردند و كارهاى شايسته كردند و يكديگر را به حق سفارش كردند و يكديگر را به صبر سفارش كردند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'واى بر هر غيبت‌كننده عيبجويى؛'),
(6181, 104, 2, 'آن كه مالى گرد كرد و حساب آن نگه داشت.'),
(6182, 104, 3, 'مى‌پندارد كه دارايى‌اش جاويدانش گرداند.'),
(6183, 104, 4, 'نه چنين است. كه او را در حُطَمه اندازند.'),
(6184, 104, 5, 'و تو چه دانى كه حطمه چيست؟'),
(6185, 104, 6, 'آتش افروخته خداست،'),
(6186, 104, 7, 'كه بر دلها غلبه مى‌يابد.'),
(6187, 104, 8, 'و از هر سو در ميانشان گرفته است،'),
(6188, 104, 9, 'در شعله‌هايى برافراشته.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آيا نديده‌اى كه پروردگارت با اصحاب فيل چه كرد؟'),
(6190, 105, 2, 'آيا مكرشان را باطل نساخت؟'),
(6191, 105, 3, 'و بر سر آنها پرندگانى فوج‌فوج فرستاد،'),
(6192, 105, 4, 'تا آنها را با سِجّيل سنگباران كردند.'),
(6193, 105, 5, 'و آنان را چون كاه پس‌مانده در آخور ساخت.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'براى ائتلاف قريش؛'),
(6195, 106, 2, 'ائتلافشان در سفر زمستانى و تابستانى.'),
(6196, 106, 3, 'پس بايد پروردگار اين خانه را بپرستند:'),
(6197, 106, 4, 'آن كه به هنگام گرسنگى طعامشان داد و از بيم در امانشان داشت.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آيا آن را كه روز جزا را دروغ مى‌شمرد ديدى؟'),
(6199, 107, 2, 'او همان كسى است كه يتيم را به اهانت مى‌راند،'),
(6200, 107, 3, 'و مردم را به طعام‌دادن به بينوا وانمى‌دارد.'),
(6201, 107, 4, 'پس واى بر آن نمازگزارانى‌'),
(6202, 107, 5, 'كه در نماز خود سهل انگارند؛'),
(6203, 107, 6, 'آنان كه ريا مى‌كنند،'),
(6204, 107, 7, 'و از دادن زكات دريغ مى‌ورزند.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'ما كوثر را به تو عطا كرديم.'),
(6206, 108, 2, 'پس براى پروردگارت نماز بخوان و قربانى كن‌'),
(6207, 108, 3, 'كه بدخواه تو خود اَبتر است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو: اى كافران،'),
(6209, 109, 2, 'من چيزى را كه شما مى‌پرستيد نمى‌پرستم.'),
(6210, 109, 3, 'و شما نيز چيزى را كه من مى‌پرستم نمى‌پرستيد.'),
(6211, 109, 4, 'و من پرستنده چيزى كه شما مى‌پرستيد نيستم.'),
(6212, 109, 5, 'و شما پرستنده چيزى كه من مى‌پرستم نيستيد.'),
(6213, 109, 6, 'شما را دين خود، و مرا دين خود.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'چون يارى خدا و پيروزى فراز آيد،'),
(6215, 110, 2, 'و مردم را ببينى كه فوج‌فوج به دين خدا درمى‌آيند،'),
(6216, 110, 3, 'پس به ستايش پروردگارت تسبيح گوى و از او آمرزش بخواه، كه او توبه‌پذير است.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'دستهاى ابولهب بريده باد و هلاك بر او.'),
(6218, 111, 2, 'دارايى او و آنچه به دست آورده بود به حالش سود نكرد.'),
(6219, 111, 3, 'زودا كه به آتشى شعله‌ور درافتد.'),
(6220, 111, 4, 'و زنش هيزم‌كش است.'),
(6221, 111, 5, 'و بر گردن ريسمانى از ليف خرما دارد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: اوست خداى يكتا،'),
(6223, 112, 2, 'خداى كه در حاجت به او رو كنند،'),
(6224, 112, 3, 'نه زاده است و نه زاده شده،'),
(6225, 112, 4, 'و نه هيچ كس همتاى اوست.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: به پروردگار صبحگاه پناه مى‌برم،'),
(6227, 113, 2, 'از شر آنچه بيافريده است،'),
(6228, 113, 3, 'و از شر شب چون درآيد،'),
(6229, 113, 4, 'و از شر جادوگرانى كه در گره‌ها افسون مى‌دمند،'),
(6230, 113, 5, 'از شر حسود چون رشك مى‌ورزد.');
INSERT INTO `fa_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: به پروردگار مردم پناه مى‌برم،'),
(6232, 114, 2, 'فرمانرواى مردم،'),
(6233, 114, 3, 'خداى مردم،'),
(6234, 114, 4, 'از شر وسوسه وسوسه‌گر نهانى،'),
(6235, 114, 5, 'آن كه در دلهاى مردم وسوسه مى‌كند،'),
(6236, 114, 6, 'خواه از جنيان باشد يا از آدميان.');
