INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداوند هستى بخش مهربان'),
(2, 1, 2, 'ستايش خداى را كه پروردگار جهانيان است'),
(3, 1, 3, 'آن هستى بخش مهربان'),
(4, 1, 4, 'صاحب روز جزا'),
(5, 1, 5, '[بار الها] تنها تو را مى‌پرستيم و تنها از تو يارى مى‌جوييم'),
(6, 1, 6, 'ما را به راه راست هدايت فرما'),
(7, 1, 7, 'راه آنان كه موهبتشان دادى، نه راه غضب شدگان و نه گمراهان');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف، لام، ميم'),
(9, 2, 2, 'آن كتاب [بلند مرتبه‌] هيچ ترديدى در آن نيست، راهنماى پرهيزگاران است'),
(10, 2, 3, 'آنان كه به غيب ايمان مى‌آورند و نماز را برپا مى‌دارند و از آنچه روزيشان كرده‌ايم، انفاق مى‌كنند'),
(11, 2, 4, 'و آنان كه به آنچه به تو نازل گرديده و آنچه پيش از تو نازل شده، ايمان مى‌آورند و اينانند كه به آخرت يقين دارند'),
(12, 2, 5, 'آنان بر هدايتى از پروردگار خويشند و هم ايشان رستگارانند'),
(13, 2, 6, 'به راستى آنان كه كفر ورزيدند تفاوتشان نكند چه انذارشان كنى يا انذارشان نكنى ايمان نمى‌آورند'),
(14, 2, 7, 'خدا بر دل‌ها و شنوايى‌شان مهر نهاده و بر چشمانشان پرده‌اى است و آنها را عذابى بزرگ است'),
(15, 2, 8, 'و از مردم كسانى هستند كه مى‌گويند: ما به خدا و روز واپسين ايمان آورده‌ايم. ولى آنان مؤمن نيستند'),
(16, 2, 9, 'مى‌خواهند خدا و مؤمنان را فريب دهند، در حالى كه جز خودشان را فريب نمى‌دهند، ولى نمى‌فهمند'),
(17, 2, 10, 'در دل‌هايشان بيمارى است، پس خدا ايشان را بيمارى افزود، و آنان را به سزاى اين كه دروغ مى‌گفتند عذابى دردناك است'),
(18, 2, 11, 'و چون به آنان گفته شود: در زمين فساد نكنيد، گويند: جز اين نيست كه ما اصلاحگرانيم'),
(19, 2, 12, 'به هوش باش كه همين‌ها تبهكارانند ولى نمى‌فهمند'),
(20, 2, 13, 'و چون به آنها گفته شود: شما نيز مانند [ساير] مردم ايمان بياوريد، گويند: آيا ما هم مانند كم‌خردان ايمان بياوريم؟ بدان كه آنان خود كم خردانند امّا نمى‌دانند'),
(21, 2, 14, 'و چون با مؤمنان رو به رو شوند گويند: ايمان آورده‌ايم، و چون با ياران شيطانى خود تنها شوند، گويند: ما بى‌ترديد با شما هستيم، جز اين نيست كه ما [آنها را] به مسخره گرفته‌ايم'),
(22, 2, 15, 'خداست كه آنها را به مسخره مى‌گيرد و فرصت مى‌دهد تا در طغيانشان سرگردان بمانند'),
(23, 2, 16, 'همين كسانند كه گمراهى را به بهاى هدايت خريدند پس تجارتشان سود نكرد و هدايت‌پذير نبودند'),
(24, 2, 17, 'مثل آنان مثل كسى است كه [به زحمت‌] آتشى بيفروخت و همين كه اطراف وى را روشن ساخت، خدا نورشان را برد و در تاريكى‌ها رهايشان كرد كه نبينند'),
(25, 2, 18, 'كرانند و گنگانند و كورانند، پس [از گمراهى‌] باز نمى‌گردند'),
(26, 2, 19, 'يا مانند مثل رگبار تندى از آسمان است كه در آن تاريكى‌ها و رعد و برق است كه از [غرّش‌] صاعقه‌ها و بيم مرگ، انگشتان خويش در گوش‌هايشان كنند، و خدا بر كافران احاطه دارد'),
(27, 2, 20, 'نزديك است برق چشمانشان را بزند، هر بار كه براى آنان روشن كند، در آن راه روند و چون بر آنها تاريك نمايد باز ايستند، و اگر خدا مى‌خواست، شنوايى و چشمانشان را [يكجا] مى‌گرفت. بى‌گمان، خدا بر هر چيزى تواناست'),
(28, 2, 21, 'اى مردم! پروردگارتان را كه شما و كسان قبل از شما را آفريد بپرستيد، باشد كه پرهيزگار شويد'),
(29, 2, 22, 'خدايى كه براى شما زمين را بسترى هموار و آسمان را سرپناهى قرار داد و از آسمان آبى نازل كرد كه بدان از ميوه‌ها رزقى براى شما برآورد. پس براى خدا همتايان قرار ندهيد در حالى كه خود مى‌دانيد'),
(30, 2, 23, 'و اگر در آنچه بر بنده‌ى خويش نازل كرده‌ايم شك داريد، سوره‌اى مانند آن بياوريد و شاهدان خود را غير از خدا فراخوانيد اگر راست مى‌گوييد'),
(31, 2, 24, 'پس اگر نكرديد و هرگز نخواهيد كرد، از آتشى كه هيمه آن مردمان و سنگ‌هاست برحذر باشيد كه براى كفار مهيّا شده است'),
(32, 2, 25, 'و كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند نويد ده كه برايشان باغ‌هايى است كه از پاى درختانش نهرها جارى است، هر بار كه ميوه‌اى از آن روزيشان گردد، گويند: اين همان است كه قبلا روزيمان شده و براى آنان همانند آن آورده شود و آن جا آنها را همسران'),
(33, 2, 26, 'به راستى خدا ابايى ندارد از اين كه به پشه‌اى يا فوق آن مثل زند، امّا آنان كه ايمان آوردند مى‌دانند كه آن [مثل‌] حق بوده و از جانب پروردگارشان است ولى كافران [به قصد تحقير] گويند: خدا از اين مثل چه خواسته است؟ [آرى‌] بسيارى را با آن گمراه و بسيارى را'),
(34, 2, 27, 'همانان كه پيمان خدا را پس از تأكيدش مى‌شكنند و آنچه را كه خدا به پيوستن آن امر كرده مى‌گسلند و در زمين فتنه به پا مى‌كنند. همين‌ها زيانكارانند'),
(35, 2, 28, 'چگونه منكر خدا مى‌شويد با آن كه بى‌جان بوديد پس شما را جان بخشيد، و ديگر بار شما را مى‌ميراند و باز جانتان مى‌دهد و آن گاه به سوى او بازگردانده مى‌شويد'),
(36, 2, 29, 'اوست كسى كه آنچه در زمين است، همه را براى شما آفريد، سپس به آسمان پرداخت و آنها را به صورت هفت آسمان تنظيم كرد و او به هر چيزى داناست'),
(37, 2, 30, 'و آن گاه كه پروردگارت به فرشتگان گفت: من در زمين جانشينى خواهم گماشت. گفتند: آيا در آن مخلوقى قرار مى‌دهى كه تباهى كند و خون‌ها بريزد، حال آن كه ما تو را به پاكى مى‌ستاييم و تقديست مى‌كنيم؟ گفت: من آن مى‌دانم كه شما نمى‌دانيد'),
(38, 2, 31, 'و همه نام‌ها را به آدم آموخت، سپس آنها را بر فرشتگان عرضه داشت و گفت: اگر راست مى‌گوييد مرا از نام‌هاى اينان خبر دهيد'),
(39, 2, 32, 'گفتند: پاكى تو راست، ما را دانشى جز آنچه به ما آموخته‌اى نيست، حقا داناى حكيم تنها تويى'),
(40, 2, 33, 'گفت: اى آدم! آنان (فرشتگان) را از نام‌هاى آنها آگاه ساز. پس همين كه از نام آنها آگاهشان نمود، فرمود: آيا به شما نگفتم كه من نهان آسمان‌ها و زمين را مى‌دانم، و مى‌دانم آنچه را آشكار مى‌كنيد و آنچه را پنهان مى‌داشتيد'),
(41, 2, 34, 'و آن گاه كه به فرشتگان گفتيم: براى آدم سجده كنيد، پس همه سجده كردند، جز ابليس كه ابا كرد و برترى جست و از كافران گرديد'),
(42, 2, 35, 'و گفتيم: اى آدم! تو و همسرت در اين باغ منزل كنيد و به فراوانى از هر جا [و هر چه‌] مى‌خواهيد بخوريد ولى نزديك اين درخت نشويد كه از ظالمان خواهيد شد'),
(43, 2, 36, 'پس شيطان آن دو را در باره‌ى آن [درخت‌] منحرف نمود و از آنچه در آن بودند بيرونشان كرد، و گفتيم: پايين رويد، شما دشمن يكديگريد و تا مدتى در زمين قرارگاه و معيشتى خواهيد داشت'),
(44, 2, 37, 'پس آدم از پروردگار خويش سخنانى دريافت كرد [و توبه نمود] پس خدا توبه‌اش را پذيرفت، حقا كه او توبه‌پذير مهربان است'),
(45, 2, 38, 'گفتيم: همه از آن پايين رويد، پس اگر از جانب من هدايتى برايتان آمد، هر كس از هدايت من پيروى كند، نه خوفى بر آنهاست و نه اندوهگين شوند'),
(46, 2, 39, 'و آنان كه كافر شدند و آيات ما را تكذيب كردند، آنها اهل آتشند و در آن ماندگار'),
(47, 2, 40, 'اى بنى اسرائيل! نعمت مرا كه بر شما ارزانى داشتم به ياد آريد و پيمان مرا وفا كنيد تا به پيمانتان وفا كنم، و تنها از من بيم داريد'),
(48, 2, 41, 'و به آنچه نازل كردم [قرآن‌] بگرويد كه آنچه را با شماست تصديق مى‌كند، و نخستين منكر آن نباشيد و آيات مرا به بهاى ناچيز مفروشيد و تنها از من بترسيد'),
(49, 2, 42, 'و حق را به باطل نياميزيد و حقيقت را پنهان نكنيد در حالى كه مى‌دانيد'),
(50, 2, 43, 'و نماز به پا داريد و زكات بدهيد و با راكعان ركوع كنيد'),
(51, 2, 44, 'آيا مردم را به خير و نيكى فرمان مى‌دهيد و خود را فراموش مى‌كنيد با اين كه شما كتاب آسمانى را تلاوت مى‌كنيد؟ پس چرا نمى‌انديشيد'),
(52, 2, 45, 'و از صبر و نماز يارى بگيريد و البتّه آن جز بر خاشعان بسى گران است'),
(53, 2, 46, 'آنهايى كه مى‌دانند حتما به لقاى پروردگارشان مى‌رسند و به سوى او باز مى‌گردند'),
(54, 2, 47, 'اى بنى اسرائيل! نعمتم را كه بر شما ارزانى داشتم و شما را بر جهانيان (مردم زمانه) برترى دادم به ياد آريد'),
(55, 2, 48, 'و بترسيد از روزى كه كسى از ديگرى چيزى دفع نكند و از كسى شفاعتى نپذيرند و از او فديه و غرامت نگيرند و نه ايشان يارى شوند'),
(56, 2, 49, 'و [ياد كنيد] آن گاه كه شما را از فرعونيان نجات داديم، كه شما را به سختى آزار مى‌دادند، پسرانتان را سر مى‌بريدند و زنانتان را زنده مى‌گذاشتند و در آن [مصايب‌] آزمونى بزرگ از سوى پروردگارتان بود'),
(57, 2, 50, 'و [ياد كنيد] آن گاه كه دريا را برايتان بشكافتيم و شما را نجات داديم و فرعونيان را غرق نموديم و شما نظاره مى‌كرديد'),
(58, 2, 51, 'و [ياد كنيد] آن گاه كه با موسى چهل شب وعده گذاشتيم، آن گاه شما در غياب وى گوساله را به پرستش گرفتيد و شما [واقعا] ستمكاريد'),
(59, 2, 52, 'پس از آن بر شما بخشوديم تا مگر سپاسگزارى كنيد'),
(60, 2, 53, 'و آن گاه كه به موسى كتاب و فرقان داديم به اين اميد كه هدايت يابيد'),
(61, 2, 54, 'و آن گاه كه موسى به قوم خود گفت: اى قوم من! شما با گوساله پرستى به خود ستم كرديد، اينك به سوى آفريدگار خود بازگرديد و [توبه نماييد و خطاكاران‌] خود را [به كيفر ارتداد] بكشيد كه اين كار نزد آفريدگارتان براى شما بهتر است، پس بر شما بخشود كه او توبه‌پذي'),
(62, 2, 55, 'و چون گفتيد: اى موسى! هرگز براى تو ايمان نياوريم مگر اين كه خدا را آشكارا ببينيم. پس صاعقه شما را در حالى كه نگاه مى‌كرديد بگرفت'),
(63, 2, 56, 'سپس شما را پس از مرگتان برانگيختيم، باشد كه شكرگزارى كنيد'),
(64, 2, 57, 'و ابر را سايبان شما كرديم و منّ و سلوى بر شما نازل كرديم [و گفتيم:] از پاكيزه‌هاى آنچه روزيتان كرده‌ايم بخوريد، و [با اين كارها] بر ما ستم نكردند، بلكه بر خويشتن ستم مى‌كردند'),
(65, 2, 58, 'و آن زمان كه گفتيم: به اين شهر [بيت المقدس‌] درآييد و از [نعمت‌هاى‌] آن هر جا خواستيد فراوان بخوريد و از اين دروازه خاضعانه وارد شويد و بگوييد: خدايا! گناهان ما را فرو ريز. تا خطاهاى شما را ببخشاييم، و [البته پاداش‌] نيكوكاران را خواهيم افزود'),
(66, 2, 59, 'پس ستمكاران سخن را به غير از صورتى كه دستور داشتند تغيير دادند. پس بر آنان كه ستم كردند، به سزاى نافرمانى‌شان بلايى از آسمان نازل كرديم'),
(67, 2, 60, 'و هنگامى كه موسى براى قومش آب خواست به او گفتيم: با عصايت به اين سنگ بزن. آن‌گاه دوازده چشمه از آن جوشيد [به گونه‌اى كه‌] هر قبيله‌اى آبگير خود را مى‌شناخت. [گفتيم:] از رزق خدا بخوريد و بنوشيد و در زمين سر به فساد بر مداريد'),
(68, 2, 61, 'و آن‌گاه كه گفتيد: اى موسى! ما ديگر بر يك خوراك صبر نكنيم، پروردگار خويش را بخوان تا از آنچه زمين مى‌روياند، از سبزيجات و خيار و سير و عدس و پياز براى ما برآورد. گفت: آيا پست‌تر را جانشين بهتر مى‌كنيد؟ [اگر چنين مى‌خواهيد] به شهرى درآييد تا اين چيزه'),
(69, 2, 62, 'محققا كسانى كه [صورى‌] ايمان آوردند و آنها كه يهودى شدند و مسيحيان و صابئيان، هر كدام [از روى حقيقت‌] به خدا و روز قيامت ايمان آورد و عمل صالح كند، پاداش آنها نزد پروردگارشان [محفوظ] است و نه خوفى بر آنهاست و نه غمگين شوند'),
(70, 2, 63, 'و [ياد كنيد] وقتى كه از شما پيمان گرفتيم و كوه طور را [به نشانه قدرت‌] بالاى سرتان بلند كرديم و [گفتيم:] كتابى را كه به شما داده‌ايم، محكم بگيريد و محتواى آن را به خاطر بسپاريد، باشد كه پرهيزكار شويد'),
(71, 2, 64, 'سپس شما بعد از آن [عهد و ميثاق‌] رو برتافتيد، و اگر فضل و رحمت خدا در حق شما نبود، قطعا از زيانكاران مى‌شديد'),
(72, 2, 65, 'شما به خوبى كسانى از خودتان را كه در روز شنبه [از حكم خدا] تجاوز كردند شناختيد كه به آنها گفتيم: بوزينگانى خوار و مطرود باشيد'),
(73, 2, 66, 'و اين [عقوبت‌] را مايه عبرت حاضران و آيندگان و اندرزى براى پارسايان قرار داديم'),
(74, 2, 67, 'و آن وقت كه موسى به قوم خود گفت: خدا به شما فرمان مى‌دهد كه گاوى را سر ببريد. گفتند: آيا ما را به ريشخند مى‌گيرى؟ گفت: پناه مى‌برم به خدا كه از جاهلان باشم'),
(75, 2, 68, 'گفتند: براى ما پروردگارت را بخوان تا معلوم كند كه آن چگونه گاوى است؟ گفت: او مى‌فرمايد آن گاوى است نه پير و نه جوان، بلكه ميانسالى است بين اين دو، پس آنچه دستور يافته‌ايد انجام دهيد'),
(76, 2, 69, '[بار ديگر] گفتند: از خدايت بخواه براى ما روشن سازد كه رنگ آن چيست؟ گفت: او مى‌فرمايد كه آن گاوى است زرد يكدست كه بينندگان را سرور مى‌بخشد'),
(77, 2, 70, 'گفتند: از خدايت بخواه تا براى ما روشن كند كه آن چگونه گاوى است؟ زيرا آن گاو [هنوز] بر ما مشتبه است و اگر خدا بخواهد ما قطعا بدان راه خواهيم يافت'),
(78, 2, 71, 'گفت: او مى‌فرمايد آن گاوى است كه رام نيست تا زمين را شخم زند و به كشت آب دهد، گاوى تندرست است، هيچ لكه‌اى در آن نيست. گفتند: اينك حق را ادا كردى. پس همان را سر بريدند، هر چند نزديك بود اين كار را نكنند'),
(79, 2, 72, 'و [ياد كنيد] آن گاه كه شخصى را كشتيد، پس يكديگر را در آن [قتل‌] متّهم كرديد و خدا آنچه را پنهان مى‌داشتيد [با ذبح گاو] آشكار ساخت'),
(80, 2, 73, 'پس گفتيم: پاره‌اى از گاو را به مقتول زنيد [تا زنده گردد]. آرى خداوند مردگان را اين گونه زنده مى‌كند و [بدين سان‌] نشانه‌هاى قدرت خود را به شما نشان مى‌دهد، باشد كه بينديشيد'),
(81, 2, 74, 'پس از آن [همه معجزات‌] دل‌هاى شما سخت شد، به سختى سنگ‌ها يا سخت‌تر از آن، در حالى كه از پاره‌اى سنگ‌ها جوى‌ها مى‌جوشد و پاره‌اى از آنها مى‌شكافد و آب از آن خارج مى‌شود و برخى از آنها از هيبت خدا فرو مى‌ريزد، و خداوند از آنچه مى‌كنيد غافل نيست'),
(82, 2, 75, 'پس آيا انتظار داريد [چنين مردمى‌] به [دين‌] شما ايمان آورند، با آن كه گروهى از آنان سخن خدا را مى‌شنيدند و بعد از درك آن، تحريفش مى‌كردند در حالى كه خود مى‌دانستند'),
(83, 2, 76, 'و چون با مؤمنان روبه رو شوند گويند: ايمان آورده‌ايم، و چون با يكديگر خلوت كنند گويند: چرا با مسلمانان از آنچه خدا براى شما [از اوصاف محمد صلّى الله عليه وآله‌] آشكار نموده، گفت و گو مى‌كنيد تا با آن نزد پروردگارتان با شما احتجاج كنند؟ آيا نمى‌انديشيد'),
(84, 2, 77, 'آيا نمى‌دانند كه خدا آنچه را نهان مى‌كنند يا آشكار مى‌سازند مى‌داند'),
(85, 2, 78, 'و گروهى از آنان افراد عامى هستند كه كتاب خدا را جز يك مشت اوهام و آرزوها چيزى نمى‌دانند، و اينان پاى‌بند پندارند و بس'),
(86, 2, 79, 'پس واى بر آنها كه كتاب را با دست‌هاى خود مى‌نويسند و سپس مى‌گويند: اين از جانب خداست، تا آن را به بهاى ناچيزى بفروشند. واى بر آنها از دست نوشته‌هاشان و واى بر آنها از آنچه كسب مى‌كنند'),
(87, 2, 80, 'و گفتند: هرگز آتش جز چند روزى ما را نخواهد رسيد. بگو: مگر پيمانى از خدا گرفته‌ايد كه خدا خلاف عهد خويش نكند؟ يا بر خدا سخنانى مى‌بنديد كه نمى‌دانيد'),
(88, 2, 81, 'آرى، هر كس كار بدى كند و گناه سراسر وجودش را فراگيرد، چنين كسانى دوزخى‌اند و هم ايشان در آن ماندگارند'),
(89, 2, 82, 'و آنان كه ايمان آوردند و عمل صالح كردند، اهل بهشتند و ايشان در آن جاودانند'),
(90, 2, 83, 'و [ياد كن‌] آن‌گاه كه از بنى اسرائيل پيمان گرفتيم كه جز خدا را نپرستيد و به پدر و مادر و يتيمان و بينوايان نيكى كنيد و با مردم، نيكو سخن گوييد و نماز به پا داريد و زكات بدهيد، آن‌گاه جز تعدادى اندك از شما با حالت اعراض به آن پشت كرديد'),
(91, 2, 84, 'و چون از شما پيمان گرفتيم كه خون يكديگر را مريزيد و يكديگر را از خانه و ديار خويش بيرون نكنيد، سپس شما [به اين پيمان‌] اقرار كرديد و شما گواهيد'),
(92, 2, 85, 'سپس همين شما يكديگر را مى‌كشيد و جمعى از خودتان را از ديارشان آواره مى‌سازيد و بر ضدّ آنها به گناه و دشمنى همدست مى‌شويد، و اگر به اسيرى نزدتان آيند، با دادن فديه آزادشان مى‌كنيد، در صورتى كه بيرون كردنشان [از ابتدا] بر شما حرام بوده است. آيا به بخش'),
(93, 2, 86, 'همين كسانند كه زندگى دنيا را به بهاى آخرت خريدند، پس نه عذابشان تخفيف داده شود و نه يارى شوند'),
(94, 2, 87, 'و البتّه به موسى كتاب داديم و پيامبرانى را از پى او فرستاديم و به عيسى بن مريم حجت‌هاى روشن عطا كرديم و او را با روح القدس تأييد نموديم. پس آيا هر بار پيامبرى چيزى برايتان آورد كه دلخواه شما نبود تكبّر ورزيديد پس گروهى را تكذيب كرديد و گروهى را مى‌كش'),
(95, 2, 88, 'و گفتند: دل‌هاى ما در نيام است. [اين طور نيست‌] بلكه خدا به سبب كفرشان لعن [و طرد] شان كرده، پس خيلى كم ايمان مى‌آورند'),
(96, 2, 89, 'و هنگامى كه از جانب خدا كتابى برايشان آمد كه آنچه را با خود داشتند تأييد مى‌كرد، و از پيش [با آن‌] بر كفّار پيروزى مى‌جستند، ولى همين كه آنچه را مى‌شناختند برايشان آمد، انكارش كردند. پس لعنت خدا بر كافران'),
(97, 2, 90, 'چه بد معامله‌اى است كه خود را به بهاى كافر شدن به آيات الهى فروختند، از روى حسد به اين كه خدا از فضل خويش به هر كس از بندگانش كه بخواهد [وحى‌] نازل مى‌كند. از اين رو به خشمى بر خشم ديگر گرفتار آمدند، و براى كافران عذابى خفّت بار است'),
(98, 2, 91, 'و چون به آنها گفته شود: بدانچه خدا نازل كرده ايمان آوريد، گويند: ما به آنچه بر خودمان نازل شده ايمان داريم، و غير آن را منكر مى‌شوند در حالى كه حق همان است و آنچه را كه نزدشان است تصديق مى‌كند. بگو: اگر [به تورات‌] ايمان داريد، پس چرا پيش از اين پيام'),
(99, 2, 92, 'و محققا موسى حجّت‌هاى روشن را براى شما آورد، ولى شما در غياب او گوساله را [معبود] گرفتيد و شما ستمكار بوديد'),
(100, 2, 93, 'و آن‌گاه كه از شما پيمان گرفتيم و كوه طور را [به علامت جدّيت موضوع‌] بر فرازتان بداشتيم [و گفتيم:] آنچه به شما داده‌ايم محكم بگيريد و گوش فرا دهيد. گفتند: شنيديم و [به دل گفتند] نافرمانى كرديم! آرى، در اثر كفرشان دل به گوساله باختند. بگو: اگر دعوى اي'),
(101, 2, 94, 'بگو: اگر سراى آخرت در پيشگاه خدا تنها به شما اختصاص دارد نه ساير مردم، پس آرزوى مرگ كنيد اگر راست مى‌گوييد'),
(102, 2, 95, 'ولى به خاطر اعمالى كه از پيش فرستاده‌اند هرگز آرزوى مرگ نخواهند كرد، و خدا به [حال‌] ستمگران آگاه است'),
(103, 2, 96, 'و مسلما آنها را حريص‌ترين مردم به زندگى [دنيا] خواهى يافت و [حتّى‌] حريص‌تر از كسانى كه شرك ورزيدند. هر يك از آنها خوش دارد كه كاش هزار سال عمر داده مى‌شد ولى آن (عمر دراز هم) مانع عقوبت او نخواهد شد، و خدا به آنچه مى‌كنند بيناست'),
(104, 2, 97, 'بگو: هر كه دشمن جبرئيل باشد [در واقع دشمن خداست، چرا كه‌] او قرآن را به امر خدا بر قلب تو نازل كرد كه كتب آسمانى پيشين را تصديق مى‌كند و هدايت و بشارتى براى مؤمنان است'),
(105, 2, 98, 'و هر كه دشمن خدا و فرشتگان و فرستادگان او و جبرئيل و ميكائيل باشد [كافر است و] بى‌ترديد خدا دشمن كافران است'),
(106, 2, 99, 'البته ما آيات روشنگرى به سوى تو نازل كرديم و جز فاسقان منكر آن نمى‌شوند'),
(107, 2, 100, 'آيا هر بار كه [يهود] پيمانى بستند، گروهى از ايشان آن را دور افكندند؟ آرى، بيشترشان ايمان نمى‌آورند'),
(108, 2, 101, 'و زمانى كه فرستاده‌اى از طرف خدا براى آنها آمد كه تصديق كننده‌ى چيزى است كه با آنها بود، گروهى از همين كسانى كه اهل كتاب بودند، كتاب خدا را پشت سر افكندند چنان كه گويى [آن را] نمى‌دانند'),
(109, 2, 102, 'و [يهود به جاى تورات‌] از آنچه شياطين در باره‌ى سلطنت سليمان مى‌خواندند، پيروى كردند، در صورتى كه سليمان [سحر نكرد] و كافر نشد ولى شياطين كافر شدند كه به مردم سحر و جادو مى‌آموختند، و نيز از آنچه كه بر دو فرشته، هاروت و ماروت، در بابل نازل شده بود پ'),
(110, 2, 103, 'و اگر آنان ايمان مى‌آوردند و پارسايى مى‌كردند، قطعا پاداشى كه از جانب خدا مى‌يافتند بهتر بود اگر مى‌دانستند'),
(111, 2, 104, 'اى كسانى كه ايمان آورده‌ايد! نگوييد «راعنا» و بگوييد «انظرنا»، و [اين دستور را] گوش دهيد و براى كافران عذابى دردناك است'),
(112, 2, 105, 'كفّار اهل كتاب و مشركان خوش ندارند كه از پروردگارتان خيرى بر شما نازل شود، امّا خدا هر كه را خواهد، خاصّ رحمت خويش مى‌كند كه خداوند صاحب فضل و كرم بزرگ است'),
(113, 2, 106, 'هر حكمى را نسخ يا متروك كنيم، بهتر از آن يا مانند آن را مى‌آوريم. مگر نمى‌دانى كه خدا بر هر كارى تواناست'),
(114, 2, 107, 'آيا نمى‌دانى كه فرمانروايى آسمان‌ها و زمين از آن خداست و شما را جز خدا سرپرست و ياورى نيست'),
(115, 2, 108, 'آيا مى‌خواهيد از پيامبر خود همان را بخواهيد كه پيش‌تر از موسى خواسته شد؟ هر كه كفر را جانشين ايمان سازد بى شك راه راست را گم كرده است'),
(116, 2, 109, 'بسيارى از اهل كتاب- با آن كه حق بر ايشان روشن شده بود- به انگيزه‌ى حسدى كه در نهادشان است آرزو داشتند شما را بعد از ايمانتان به كفر بازگردانند. پس [فعلا] بگذريد و مدارا كنيد تا خدا فرمان خويش را صادر كند كه خدا بر همه چيز تواناست'),
(117, 2, 110, 'و نماز به پا داريد و زكات دهيد و هر خيرى را كه براى خود پيش فرستيد، همان را نزد خدا خواهيد يافت. آرى خدا به آنچه مى‌كنيد بيناست'),
(118, 2, 111, 'و گفتند: هيچ كس داخل بهشت نمى‌شود مگر آن كه يهودى يا مسيحى باشد. اينها خيالبافى‌هاى آنهاست. بگو: اگر راست مى‌گوييد، دليل خود را بياوريد'),
(119, 2, 112, 'آرى، هر كس خود را [با تمام وجود] تسليم خدا كند و نيكوكار باشد، پاداش او نزد پروردگارش [محفوظ] است و نه خوفى بر آنهاست و نه اندوهگين شوند'),
(120, 2, 113, 'و يهود گفتند: نصارا بر چيز [حقى‌] نيستند، و نصارا گفتند: يهود بر چيز [حقى‌] نيستند، با آن كه همه آنها كتاب آسمانى را مى‌خوانند. همچنين نادان‌ها نيز مانند سخن آنها گفتند [كه مسلمين بر حق نيستند]. پس خدا روز قيامت ميان آنها در باره‌ى اختلافشان داورى خ'),
(121, 2, 114, 'كيست ستمكارتر از آن كه نگذارد نام خداوند در مساجد ياد شود و در ويرانى آن بكوشد. آنان را نشايد كه وارد اين مكان‌ها شوند جز با ترس و هراس. براى آنها در اين دنيا ننگ و خوارى و در آخرت عذابى بزرگ است'),
(122, 2, 115, 'و مشرق و مغرب از آن خداست، پس به هر سو رو كنيد، همان جا وجه خداست. همانا خداوند وسعت بخش داناست'),
(123, 2, 116, 'و گفتند: خدا فرزندى گرفته، [در حالى كه‌] او منزّه است، بلكه هر چه در آسمان‌ها و زمين است از آن اوست و همه فرمانبر اويند'),
(124, 2, 117, 'پديد آورنده‌ى آسمان‌ها و زمين است و چون چيزى را اراده كند همين كه بگويد: باش، مى‌شود'),
(125, 2, 118, 'و نادان‌ها گفتند: چرا خدا با ما [بى واسطه‌] سخن نمى‌گويد يا معجزه‌اى سوى ما نمى‌آيد؟ گذشتگان آنها نيز چنين مى‌گفتند، دل‌هايشان به هم مى‌ماند. البته ما آيات را براى اهل يقين بيان كرده‌ايم'),
(126, 2, 119, 'ما تو را به حق فرستاديم تا بشارت دهنده و ترساننده باشى و تو مسئول [گمراهى‌] دوزخيان نيستى'),
(127, 2, 120, 'و يهود و نصارا هرگز از تو راضى نخواهند شد مگر آن كه از آيين آنها پيروى كنى. بگو: بى‌ترديد هدايت واقعى همان هدايت خداست. و اگر پس از دانشى كه تو را حاصل شده از هوس‌هاى آنها پيروى كنى در برابر خدا هيچ سرپرست و ياورى نخواهى داشت'),
(128, 2, 121, 'كسانى كه كتابشان داده‌ايم، آن را چنان كه بايد مى‌خوانند. همين‌ها به آن ايمان مى‌آورند، و كسانى كه بدان كافر شوند آنها واقعا زيانكارند'),
(129, 2, 122, 'اى بنى اسرائيل! نعمتى را كه بر شما ارزانى داشتم و شما را بر جهانيان [مردم زمانه‌] برترى دادم ياد كنيد'),
(130, 2, 123, 'و بترسيد از روزى كه هيچ كس چيزى را از ديگرى دفع نكند و از او عوض نپذيرند و هيچ شفاعتى به كارش نيايد و نه ايشان يارى شوند'),
(131, 2, 124, 'و هنگامى كه خدا ابراهيم را با كلماتى بيازمود و او همه را به اتمام رسانيد، گفت: من تو را امام و پيشواى مردم قرار دادم. [ابراهيم‌] گفت: از خاندانم چطور؟ فرمود: عهد من به ستمكاران نمى‌رسد'),
(132, 2, 125, 'و آن‌گاه كه كعبه را محل اجتماع و جايگاه امن مردم قرار داديم و [گفتيم‌]: مقام ابراهيم را محل نماز قرار دهيد، و به ابراهيم و اسماعيل سفارش كرديم كه خانه مرا براى كسانى كه براى طواف و اعتكاف و نماز مى‌آيند پاكيزه كنيد'),
(133, 2, 126, 'و آن‌گاه كه ابراهيم عرضه داشت: پروردگارا! اين مكان را شهر امنى قرار ده و مردمش را از نعمت‌هاى فراوان روزى بخش، مردمى كه به خدا و روز واپسين ايمان آرند. فرمود: امّا آن كه كافر شود، اندكى برخوردارش مى‌كنم، سپس او را به سوى عذاب آتش مى‌كشانم و چه بد سر'),
(134, 2, 127, 'و آن‌گاه كه ابراهيم و اسماعيل پايه‌هاى خانه كعبه را بالا مى‌بردند [گفتند:] پروردگارا! از ما بپذير، كه تنها تو شنوا و دانايى'),
(135, 2, 128, 'پروردگارا! ما را تسليم فرمان خود گردان و از نسل ما امّتى فرمانبردار خويش پديد آور، و آداب دينى ما را به ما نشان ده و بر ما ببخشاى بى‌ترديد تويى توبه پذير مهربان'),
(136, 2, 129, 'پروردگارا! در ميان آنها پيامبرى از خودشان برانگيز تا آيات تو را بر آنان بخواند و كتاب و حكمتشان بياموزد و آنها را تزكيه كند حقا كه تنها تو عزيز و حكيمى'),
(137, 2, 130, 'و چه كسى از آيين ابراهيم روى مى‌گرداند جز كسى كه سبك مغزى كند؟ در حالى كه ما او را در دنيا برگزيديم و او در آخرت مسلما از شايستگان است'),
(138, 2, 131, 'آن‌گاه كه پروردگارش به او گفت: سر به فرمان خدا نه. گفت: تسليم پروردگار جهانيان شدم'),
(139, 2, 132, 'و ابراهيم و يعقوب آيين توحيد را به پسران خود سفارش نمودند [و گفتند:] اى پسران من! خدا اين آيين پاك را براى شما برگزيده است، پس زنهار كه بميريد و مسلمان نباشيد'),
(140, 2, 133, 'آيا آن دم كه مرگ يعقوب فرا رسيد حاضر بوديد؟ آن‌گاه كه به فرزندان خود گفت: پس از من، چه مى‌پرستيد؟ گفتند: معبود تو و معبود پدرانت، ابراهيم و اسماعيل و اسحاق را به عنوان معبود يكتا مى‌پرستيم و ما فرمانبردار اوييم'),
(141, 2, 134, '[به هر حال‌] آنها امتى بودند كه گذشتند براى آنهاست آنچه كردند و براى شماست آنچه كرديد، و شما را از آنچه آنها مى‌كردند نپرسند'),
(142, 2, 135, 'و گفتند: يهودى يا نصرانى شويد تا هدايت يابيد. بگو: بلكه آيين ابراهيم را [پيروى مى‌كنيم‌] كه ملازم حق است و او از مشركان نبود'),
(143, 2, 136, 'بگوييد: ايمان آورديم به خداوند و آنچه بر ما نازل شده و آنچه بر ابراهيم و اسماعيل و اسحاق و يعقوب و نوادگانش نازل شده و آنچه به موسى و عيسى داده شد و به آنچه پيامبران ديگر از جانب پروردگارشان داده شدند. ما ميان هيچ يك از آنها فرق نگذاريم و سر به فرما'),
(144, 2, 137, 'پس اگر آنان نيز به مانند آنچه شما ايمان آورده‌ايد ايمان آوردند، قطعا هدايت يافته‌اند، و اگر روى برتافتند، جز اين نيست كه سر ناسازگارى دارند و خداوند، تو را از شر آنان مصون مى‌دارد كه او شنواى داناست'),
(145, 2, 138, 'رنگ الهى [توحيدى را بگيريد] و چه رنگى از رنگ الهى خوش‌تر است؟ و ما پرستندگان اوييم'),
(146, 2, 139, 'بگو: آيا با ما در باره‌ى خداى يكتا مجادله مى‌كنيد؟ در حالى كه او پروردگار ما و شماست و براى ماست اعمال ما و براى شماست اعمال شما و ما او را خالصانه مى‌پرستيم'),
(147, 2, 140, 'يا [هنوز] مى‌گوييد ابراهيم و اسماعيل و اسحاق و يعقوب و اسباط، يهودى يا نصرانى بودند؟ بگو: آيا شما بهتر مى‌دانيد يا خدا؟ و راستى چه كسى ظالم‌تر از آن كس است كه شهادتى را كه از خدا [در باره‌ى اين پيامبران‌] نزد اوست كتمان كند؟ و خدا از آنچه مى‌كنيد غا'),
(148, 2, 141, '[به هر حال‌] آنها امّتى بودند كه گذشتند براى آنهاست آنچه كردند و براى شماست آنچه كرديد، و شما را از آنچه آنها مى‌كردند نپرسند'),
(149, 2, 142, 'به زودى بى‌خردان از مردم خواهند گفت: چه چيز آنان را از قبله‌اى كه بر آن بودند برگرداند؟ بگو: مشرق و مغرب از آن خداست، هر كه را خواهد به راه راست هدايت مى‌كند'),
(150, 2, 143, 'و بدين‌سان شما را امّتى ميانه قرار داديم تا بر مردم [جهان‌] گواه باشيد و پيامبر بر شما گواه باشد. و ما قبله‌اى را كه بر آن بودى (بيت المقدس) قرار نداديم مگر براى آن كه كسى را كه از پيامبر پيروى مى‌كند از كسى كه به عقب برمى‌گردد باز شناسيم، و البته ا'),
(151, 2, 144, 'ما گردش [جستجوگرانه‌] رويت را به آسمان [براى تغيير قبله‌] مى‌بينيم، پس قطعا تو را به جانب قبله‌اى كه بدان خشنود شوى برمى‌گردانيم. پس [حالا] روى خود را جانب مسجد الحرام بگردان و شما [مسلمين‌] نيز هر جا هستيد روى خود بدان سو كنيد. و البته اهل كتاب به'),
(152, 2, 145, 'و اگر هر گونه حجّت و دليلى براى اهل كتاب بياورى قبله تو را پيروى نخواهند كرد و تو نيز پيرو قبله آنها نيستى و هيچ يك از آنها هم پيرو قبله ديگرى نيستند. و اگر پس از اين آگاهى كه براى تو حاصل شده از هوس‌هاى آنها پيروى كنى قطعا از ستمگران خواهى بود'),
(153, 2, 146, 'آنها كه كتابشان داده‌ايم، او (پيامبر) را همان گونه مى‌شناسند كه پسران خود را، ولى گروهى از آنان با اين كه حق را مى‌دانند آن را كتمان مى‌كنند'),
(154, 2, 147, '[تغيير قبله‌] فرمان حقى از جانب پروردگار توست، پس مبادا كه از ترديد كنندگان باشى'),
(155, 2, 148, 'و براى هر قومى جهت و قبله‌اى است كه رو به سوى آن مى‌كند بنابر اين [به جاى مجادله در باره‌ى قبله‌] در اعمال خير از هم سبقت گيريد. هر جا باشيد خدا همه شما را [گرد] مى‌آورد كه او بر هر چيزى قادر است'),
(156, 2, 149, 'و [هنگام نماز] از هر جا بيرون شدى، روى خود را جانب مسجد الحرام كن كه قطعا اين [قبله‌] حق [و] به فرمان پروردگار توست، و خدا از آنچه مى‌كنيد غافل نيست'),
(157, 2, 150, 'از هر جا بيرون شدى [براى نماز] روى خود جانب مسجد الحرام بگردان، و شما مردم نيز هر جا بوديد روى خود را سوى آن كنيد تا مردم را بر ضد شما دستاويزى نباشد، مگر كسانى از آنها كه ظلم كردند [كه در هر صورت خرده مى‌گيرند]. پس از آنها نترسيد و از من بترسيد، و'),
(158, 2, 151, 'چنان كه در ميان شما پيامبرى از خودتان فرستاديم تا آيات ما را بر شما تلاوت كند و [از زشتى‌ها] پاكتان سازد و كتاب و حكمتتان بياموزد و آنچه را نمى‌دانستيد تعليمتان دهد'),
(159, 2, 152, 'پس مرا ياد كنيد تا شما را ياد كنم، و براى من سپاسگزارى كنيد و ناسپاسى‌ام نكنيد'),
(160, 2, 153, 'اى كسانى كه ايمان آورده‌ايد، از صبر و نماز كمك بگيريد كه خداوند با صابران است'),
(161, 2, 154, 'و به كشتگان راه خدا مرده نگوييد، بلكه زنده‌اند ولى شما درك نمى‌كنيد'),
(162, 2, 155, 'و مسلما شما را به چيزى از ترس و گرسنگى و زيان مالى و جانى و كاهش محصولات مى‌آزماييم، و صابران را نويد ده'),
(163, 2, 156, 'آنها كه هرگاه مصيبتى بدانها رسد [صبورى كنند] و گويند: ما از آن خداييم و به سوى او باز مى‌گرديم'),
(164, 2, 157, 'آنانند كه درودها و رحمتى از پروردگارشان بر آنهاست و [تنها] همين‌ها هدايت يافتگانند'),
(165, 2, 158, 'همانا صفا و مروه از شعائر خداست. پس هر كه حجّ خانه خدا كند يا عمره به جا آورد، مانعى ندارد ميان آن دو طواف كند. و هر كه با ميل و رغبت كار نيكى به جاى آورد، [بداند كه‌] مسلما خدا سپاسگزار داناست'),
(166, 2, 159, 'كسانى كه دلايل روشنگر و هدايتى را كه نازل كرده‌ايم، بعد از آنكه آن را در كتاب آسمانى براى مردم روشن ساخته‌ايم، كتمان مى‌كنند خدا آنها را لعنت مى‌كند، و لعنت كنندگان نيز لعنتشان مى‌كنند'),
(167, 2, 160, 'مگر آنها كه توبه كردند و اصلاح نمودند و [حقيقت را] آشكار كردند، آنانند كه توبه‌شان را مى‌پذيرم و من توبه پذير مهربانم'),
(168, 2, 161, 'آنها كه كافر شدند و با كفر از دنيا رفتند، لعنت خدا و فرشتگان و همه مردم بر آنهاست'),
(169, 2, 162, 'كه در آن [لعنت‌] جاويدانند، نه [در عذابشان‌] تخفيف داده مى‌شود و نه مهلت مى‌يابند'),
(170, 2, 163, 'و معبود شما معبودى يگانه است و جز او معبودى نيست كه هستى بخش مهربان است'),
(171, 2, 164, 'بى‌گمان در آفرينش آسمان‌ها و زمين و آمد و شد شب و روز و كشتى‌ها كه براى كسب منافع مردم به دريا روانند و آبى كه خدا از آسمان فرو فرستاد پس زمين مرده را به آن زنده نمود و همه گونه جنبنده در آن منتشر ساخت و گردش بادها و ابرهاى مهار شده‌ى ميان آسمان و ز'),
(172, 2, 165, 'و برخى از مردم همتايانى غير از خدا مى‌گيرند و آنها را چون خدا دوست مى‌دارند ولى آنها كه ايمان آورده‌اند خدا را بيش از هر چيزى دوست دارند. و اگر ظالمان آن گاه كه عذاب را ببينند، مى‌ديدند كه تمام قدرت يكسره به دست خداست و خدا سخت كيفر است، [از گناه خو'),
(173, 2, 166, 'آن‌گاه كه پيشوايان از پيروان خود بيزارى جويند و عذاب را به روشنى ببينند و [تمام‌] روابط و سبب‌ها بريده گردد'),
(174, 2, 167, 'و پيروان [كفّار] گويند: اى كاش ما را [به دنيا] بازگشتى بود تا از آنها بيزارى مى‌جستيم، همان طور كه آنها از ما بيزارى جستند. بدين‌سان خدا اعمالشان را [به صورت‌] حسرت‌هايى بر آنها مى‌نمايد و آنها از آتش بيرون شدنى نيستند'),
(175, 2, 168, 'اى مردم! از آنچه در زمين حلال و پاكيزه است بخوريد و از گام‌هاى شيطان پيروى نكنيد كه او دشمن آشكار شماست'),
(176, 2, 169, 'او فقط شما را به بدى‌ها و فحشا فرمان مى‌دهد و اين كه آنچه را نمى‌دانيد به خدا نسبت دهيد'),
(177, 2, 170, 'و چون به آنها گفته شود: از چيزى كه خدا نازل كرده پيروى كنيد، مى‌گويند: [نه‌] بلكه از آنچه پدران خويش را بر آن يافته‌ايم پيروى مى‌كنيم. آيا هر چند پدرانشان چيزى نمى‌فهميدند و هدايت نيافته بودند [باز پيروى مى‌كنند]'),
(178, 2, 171, 'و مثل [تو در دعوت‌] كافران مثل كسى است كه بر حيوان زبان بسته‌اى كه جز صدا و ندايى نشنود بانگ زند. [اينها نيز چنين‌اند] كرانند و گنگانند و كورانند، پس نمى‌انديشند'),
(179, 2, 172, 'اى كسانى كه ايمان آورده‌ايد! از نعمت‌هاى پاكيزه كه روزيتان كرده‌ايم بخوريد و شكر خدا را به جاى آوريد اگر تنها او را مى‌پرستيد'),
(180, 2, 173, 'او تنها [گوشت‌] مردار و خون و گوشت خوك و آنچه را كه [هنگام ذبحش‌] نام غير خدا بر آن برده شده بر شما حرام كرده است، و هر كه [به خوردن آنها] ناچار شد، بى‌آن كه قصد نافرمانى يا تجاوز داشته باشد گناهى بر او نيست [كه‌] خدا بخشنده‌ى مهربان است'),
(181, 2, 174, 'كسانى كه آيات كتاب آسمانى را كتمان مى‌كنند و آن را به بهاى ناچيزى مى‌فروشند، جز آتش به شكم‌هاى خود فرو نمى‌برند و خداوند روز قيامت با آنها سخن نگويد و پاكشان نسازد و ايشان را عذابى دردناك است'),
(182, 2, 175, 'همين كسانند كه ضلالت را به بهاى هدايت و عذاب را در ازاى آمرزش خريدند، پس چه صبورند بر آتش'),
(183, 2, 176, 'اين بدان سبب است كه خدا كتاب را به حق فرستاد و آنان كه در باره‌ى كتاب به اختلاف پرداختند، بى‌گمان در ستيزه‌ى دورى هستند'),
(184, 2, 177, 'نيكى [تنها] آن نيست كه روى خود را به جانب مشرق و مغرب كنيد، بلكه نيكى آن است كه انسان به خدا و روز واپسين و فرشتگان و كتاب [هاى آسمانى‌] و پيامبران ايمان آورد، و مال [خود] را با آن كه دوستش دارد به خويشاوندان [نيازمند] و يتيمان و درماندگان و به راه'),
(185, 2, 178, 'اى كسانى كه ايمان آورده‌ايد! در باره‌ى كشتگان بر شما قصاص مقرر شد، آزاد در برابر آزاد، بنده در برابر بنده و زن در مقابل زن. پس هر كه از سوى برادرش چيزى [از قصاص‌] برايش بخشوده شد بايد [از اين گذشت‌] به شايستگى پيروى كند و [خونبها را] با نيكى ادا كند'),
(186, 2, 179, 'اى خردمندان شما را در قصاص زندگانى است، باشد كه پرهيزكار شويد'),
(187, 2, 180, 'بر شما مقرر شده است كه هرگاه مرگ كسى از شما فرا رسيد اگر مالى بر جاى گذاشت، براى پدر و مادر و نزديكان، مطابق عرف [مقدارى را] وصيت كند كه اين شايسته پرهيزكاران است'),
(188, 2, 181, 'پس هر كه آن وصيّت را بشنود و تغيير دهد، تنها گناهش بر كسانى است كه آن را تغيير مى‌دهند همانا خدا شنوا و آگاه است'),
(189, 2, 182, 'پس هر كه از انحراف يا گناه وصيّت كننده بيم داشت، و ميانشان سازش داد، گناهى بر او نيست. همانا خدا آمرزنده‌ى مهربان است'),
(190, 2, 183, 'اى اهل ايمان! روزه بر شما واجب شد چنان كه بر امّت‌هاى گذشته واجب شده بود، باشد كه پرهيزكار شويد'),
(191, 2, 184, 'روزهاى شمرده شده‌اى را [روزه بداريد] و هر كه از شما بيمار يا در سفر باشد، روزهاى ديگرى [روزه بگيرد]، و بر كسانى كه روزه طاقت فرساست در عوض [هر روز] تهيدستى را طعام دهند، و هر كه با ميل كار خيرى كند آن برايش بهتر است، و روزه گرفتن براى شما بهتر است،'),
(192, 2, 185, 'ماه رمضان ماهى است كه قرآن به عنوان راهنماى مردم و حجّت‌هاى روشنگر و فارق ميان حق و باطل در آن نازل شده است پس هر كه از شما اين ماه را دريافت، روزه بگيرد و هر كه بيمار يا در سفر بود، به همان تعداد در روزهاى ديگر [قضا كند]. خدا براى شما آسانى مى‌خواه'),
(193, 2, 186, 'و چون بندگان من از تو در باره‌ى من بپرسند، همانا من [به آنها] نزديكم و دعاى دعا كننده را وقتى كه مرا بخواند پاسخ مى‌دهم، پس آنها نيز دعوت مرا اجابت كنند و به من ايمان بياورند، باشد كه راه يابند'),
(194, 2, 187, 'در شب‌هاى روزه براى شما نزديكى با همسرانتان حلال شد. آنها براى شما لباسند و شما براى آنها لباسيد. خدا مى‌دانست كه شما به خود خيانت مى‌كنيد، پس از شما درگذشت و شما را بخشيد. پس حالا با آنها مباشرت كنيد و آنچه را خدا بر شما مقرر كرده است طلب نماييد،'),
(195, 2, 188, 'و در روابط خود اموال يكديگر را به ناحق مخوريد و براى خوردن بخشى از اموال مردم به گناه، [قضيّه‌] آن را به حاكمان مكشانيد در حالى كه شما [حقيقت را] مى‌دانيد'),
(196, 2, 189, 'از تو در باره‌ى هلال‌ها مى‌پرسند، بگو: آن تقويمى براى مردم و موسم حجّ است، و نيكى آن نيست كه از پشت خانه‌ها وارد شويد، بلكه نيكى آن است كه شخص تقوا پيشه كند. و از در خانه‌ها وارد شويد و از خدا بترسيد تا شايد رستگار شويد'),
(197, 2, 190, 'و در راه خدا با كسانى كه با شما مى‌جنگند نبرد كنيد ولى تجاوز نكنيد همانا خدا تجاوزگران را دوست ندارد'),
(198, 2, 191, 'و آنها را هر جا يافتيد بكشيد، و از جايى كه شما را بيرون كردند بيرون كنيد، و فتنه از قتل بدتر است، و با آنها در كنار مسجد الحرام جنگ نكنيد مگر اين كه آنها به جنگ پيشدستى كنند، پس اگر با شما جنگيدند آنها را بكشيد كه سزاى كافران همين است'),
(199, 2, 192, 'پس اگر دست برداشتند، البته خدا آمرزنده‌ى مهربان است'),
(200, 2, 193, 'و با آنها بجنگيد تا ديگر فتنه‌اى نباشد و دين ويژه‌ى خداى يكتا گردد، و اگر دست برداشتند، دشمنى جز بر ضد ستمگران [روا] نيست'),
(201, 2, 194, 'ماه حرام در برابر ماه حرام است و حرمت [شكنى‌] ها قصاص دارد. پس هر كه به شما تعدّى كرد با او مقابله به مثل كنيد و از خدا پروا داريد و بدانيد كه خدا با پرهيزكاران است'),
(202, 2, 195, 'و در راه خدا انفاق كنيد، و خود را به دست خود به هلاكت نيفكنيد، و نيكى كنيد كه خدا نيكوكاران را دوست دارد'),
(203, 2, 196, 'و حجّ و عمره را براى خدا به پايان بريد، و اگر محاصره [يا ممنوع‌] شديد [و اتمام حجّ ممكن نشد] هر قربانى كه ميسّر است فراهم كنيد و سرهاى خود را نتراشيد تا قربانى به قربانگاه برسد، پس هر كه از شما بيمار است يا رنجى به سر دارد [و به ناچار سر بتراشد] پس'),
(204, 2, 197, 'حجّ در ماه‌هاى معينى است، پس هر كه در آن ماه‌ها ملتزم به حجّ شد، همبسترى و نافرمانى و مجادله در اثناى حجّ ممنوع است، و هر چه نيكى كنيد خدا آن را مى‌داند، و توشه بگيريد كه بهترين توشه تقواست، و اى خردمندان از من پروا كنيد'),
(205, 2, 198, 'مانعى نيست كه [در سفر حجّ‌] از فضل پروردگار خويش روزى بجوييد، و چون از عرفات كوچ كرديد، در مشعر الحرام خدا را ياد كنيد و او را ياد كنيد همان‌گونه كه شما را هدايت كرد، و محققا شما پيش‌تر، از گمراهان بوديد. [و چيزى نمى‌دانستيد]'),
(206, 2, 199, 'آن‌گاه از همان جا كه مردم روانه شدند روانه شويد و از خدا آمرزش بطلبيد كه خدا آمرزنده‌ى مهربان است'),
(207, 2, 200, 'و چون اعمال خود را به انجام رسانديد، خدا را آن‌سان كه پدران خود را ذكر مى‌كنيد ياد كنيد بلكه بالاتر از آن. پس بعضى از مردم [دنيا طلب‌] مى‌گويند: پروردگارا! به ما در همين دنيا عطا كن، و او را در آخرت نصيبى نيست'),
(208, 2, 201, 'و برخى از آنها گويند: پروردگارا! ما را در دنيا نيكى و در آخرت نيكى عطا كن و ما را از آتش نگاه دار'),
(209, 2, 202, 'آنها هستند كه از دستاوردشان نصيبى خواهند داشت و خدا در حسابرسى، سريع است'),
(210, 2, 203, 'و خدا را در روزهاى معينى ياد كنيد پس هر كه شتاب كند [و اعمال را] در دو روز انجام دهد گناهى بر او نيست، و نيز هر كه [تا روز سوم‌] تأخير كند گناهى بر او نيست، [اين‌] براى كسى است كه تقوا پيشه كند. و از خدا پروا كنيد و بدانيد كه بى‌ترديد شما به سوى ا'),
(211, 2, 204, 'و از مردم كسى است كه گفتار او در باره‌ى زندگى دنيا [و پارسايى‌] مايه اعجاب توست و خدا را بدانچه كه در دل دارد گواه مى‌گيرد، و حال آن كه او لجوج‌ترين دشمنان است'),
(212, 2, 205, 'و چون بازگردد [يا به مقامى رسد] بكوشد تا در زمين فساد كند و كشت و نسل را تباه نمايد، و خدا فساد را دوست ندارد'),
(213, 2, 206, 'و چون به او گفته شود: از خدا پروا كن! نخوت او را به گناه كشاند، پس جهنّم او را بس است و چه بد بسترى است'),
(214, 2, 207, 'و از مردم كسى است كه در طلب رضاى خدا از سر جان مى‌گذرد، [چون على در بستر پيامبر] و خدا به بندگان مهربان است'),
(215, 2, 208, 'اى كسانى كه ايمان داريد! همگى به طاعت خدا درآييد و گام‌هاى شيطان را دنبال نكنيد كه او براى شما دشمنى آشكار است'),
(216, 2, 209, 'و اگر پس از روشنگرى‌هايى كه سوى شما آمد كجروى كنيد بدانيد كه خدا شكست‌ناپذير و حكيم است'),
(217, 2, 210, 'آيا منتظرند كه [عذاب‌] خدا در سايه‌هايى از ابر و فرشتگان به سراغ آنها بيايد و كار يكسره شود؟ و همه كارها به خدا باز گردانده مى‌شود'),
(218, 2, 211, 'از بنى اسرائيل بپرس چه بسيار شواهد روشنى براى آنها فرستاديم. و هر كه نعمت خدا را بعد از آن كه سوى او آمد تغيير دهد [بداند كه‌] بى‌ترديد خدا سخت كيفر است'),
(219, 2, 212, 'زندگى دنيا براى كافران زينت يافته و [لذا] مؤمنان را مسخره مى‌كنند، و كسانى كه تقوا پيشه كردند، روز رستاخيز فوق آنها هستند و خدا هر كه را خواهد بى‌حساب روزى مى‌دهد'),
(220, 2, 213, 'مردم [ابتدا] يك امّت بودند، [كه به مرور دچار اختلاف شدند] پس خدا پيامبران را [به عنوان‌] مژده‌رسانان و بيم دهندگان فرستاد و همراه آنان كتابى به حق نازل كرد تا ميان مردم در آنچه بر سرش اختلاف داشتند داورى كند، و در دين اختلاف نكردند مگر كسانى كه به آ'),
(221, 2, 214, 'آيا پنداشته‌ايد كه داخل بهشت مى‌شويد در حالى كه هنوز سرگذشت آنها كه پيش از شما گذشتند به سراغ شما نيامده است؟ آنها تنگدستى و ناخوشى ديدند و به تزلزل افتادند تا آن جا كه پيامبر و مؤمنان همراهش گفتند: يارى خدا كى مى‌رسد؟ [مژده آمد]، آگاه باشيد كه يار'),
(222, 2, 215, 'از تو مى‌پرسند چه انفاق كنند؟ بگو: هر مالى كه مى‌بخشيد [بهتر است‌] به پدر و مادر و خويشان و يتيمان و تنگدستان و به راه مانده انفاق كنيد، و هر كار خيرى كنيد بى‌شك خدا به آن آگاه است'),
(223, 2, 216, 'جهاد بر شما مقرر شد و آن براى شما ناگوار است، و بسا چيزى را ناخوش داشته باشيد كه آن به سود شماست و بسا چيزى را دوست داشته باشيد كه به زيان شماست، و خدا مى‌داند و شما نمى‌دانيد'),
(224, 2, 217, 'از تو در باره‌ى جنگ در ماه حرام مى‌پرسند، بگو: جنگ در آن گناه بزرگى است، امّا بازداشتن از راه خدا و كافر شدن به او و [بازداشتن از] مسجد الحرام و آواره كردن ساكنانش نزد خدا بسى بزرگ‌تر است، و فتنه [و كفر] از كشتار بدتر است. و آنها پيوسته با شما مى‌جن'),
(225, 2, 218, 'البته كسانى كه ايمان آوردند و كسانى كه هجرت كردند و در راه خدا جهاد نمودند آنان به رحمت خدا اميدوارند و خدا آمرزنده‌ى مهربان است'),
(226, 2, 219, 'از تو در باره‌ى شراب و قمار مى‌پرسند، بگو: در آنها گناهى بزرگ و سودهايى براى مردم است، ولى زيان آنها از سودشان بزرگ‌تر است. و از تو مى‌پرسند چه چيز انفاق كنند؟ بگو: آنچه افزون بر نياز است. خدا بدين‌سان آيات خود را براى شما روشن مى‌سازد تا بينديشيد'),
(227, 2, 220, 'در [اسرار] دنيا و آخرت. و از تو در باره‌ى يتيمان مى‌پرسند، بگو: اصلاح كارشان نيكوتر است، پس اگر با آنها همزيستى كنيد برادران شمايند، و خدا خرابكار را از اصلاحگر باز مى‌شناسد، و اگر خدا مى‌خواست بر شما سخت مى‌گرفت همانا خدا شكست ناپذير حكيم است'),
(228, 2, 221, 'و با زنان مشرك ازدواج نكنيد تا وقتى كه ايمان بياورند و البته كنيز با ايمان از زن مشرك بهتر است هر چند [زيبايى‌] او شما را مفتون سازد، و به مردان مشرك تا ايمان نياورده‌اند زن مدهيد كه تحقيقا برده‌ى با ايمان از مرد مشرك بهتر است هر چند توجه شما را جل'),
(229, 2, 222, 'و از تو در باره‌ى حيض مى‌پرسند، بگو: آن رنج و ناراحتى است، پس در ايّام حيض از زنان كناره بگيريد و با آنها نزديكى نكنيد تا پاك شوند، و چون پاك شدند، از آن جا كه خدا فرمانتان داده به آنها نزديك شويد كه خدا توبه كاران و پاكى طلبان را دوست دارد'),
(230, 2, 223, 'زنان شما كشت شمايند، پس هرگاه خواستيد، به كشت خود درآييد و براى خويش [خير] پيش فرستيد و از خدا بترسيد و بدانيد كه او را ملاقات خواهيد كرد و مؤمنان را بشارت ده'),
(231, 2, 224, 'و خدا را دستاويز سوگندهاى خود قرار ندهيد كه از نيكى كردن و پرهيزكارى و اصلاح ميان مردم باز ايستيد، و خدا شنواى داناست'),
(232, 2, 225, 'خدا شما را به سوگندهاى بيهوده‌تان مؤاخذه نمى‌كند، ولى به آنچه دل‌هايتان [از روى عمد] كسب كرد شما را مؤاخذه مى‌كند، و خدا آمرزنده‌ى بردبار است'),
(233, 2, 226, 'براى آنها كه سوگند مى‌خورند كه با زنان خود آميزش نكنند، چهار ماه فرصت است، پس اگر بازگشتند خدا آمرزنده‌ى مهربان است'),
(234, 2, 227, 'و اگر عزم طلاق كردند خدا شنواى آگاه است'),
(235, 2, 228, 'و زنان طلاق داده شده تا سه پاكى منتظر بمانند، و اگر به خدا و روز واپسين ايمان دارند حلال نيست آنچه را كه خدا در رحم‌هايشان آفريده پنهان دارند، و شوهرانشان اگر سر آشتى دارند به بازگرداندنشان در اين [مدت‌] سزاوارترند، و براى اين زنان به طور شايسته [حق'),
(236, 2, 229, 'طلاق [رجعى‌] دو بار است، پس از آن به نيكى نگاه داشتن يا به نيكى رها ساختن است، و شما را حلال نيست كه از آنچه به ايشان داده‌ايد چيزى باز ستانيد مگر آن كه بترسند كه حدود الهى را [در كنار هم‌] رعايت نكنند، پس اگر [شما اوليا] ترسيديد كه آن دو، حدود اله'),
(237, 2, 230, 'پس اگر [بار سوم‌] زن را طلاق داد، ديگر بر او حلال نيست تا زمانى كه زن با مردى غير او ازدواج كند، پس اگر [شوهر دوّم‌] طلاقش داد و دانستند كه مقررات الهى را به پا خواهند داشت مانعى بر آنها نيست كه به يكديگر رجوع كنند، و اينها احكام الهى است كه خدا برا'),
(238, 2, 231, 'و چون زنان را طلاق داديد و به پايان عدّه‌ى خود رسيدند يا به شايستگى نگاهشان داريد، يا به شايستگى رهاشان سازيد، و به قصد آسيب رساندن نگاهشان مداريد تا ستم كنيد. و هر كه چنين كند، پس به خود ستم كرده است، و آيات الهى را به مسخره نگيريد، و نعمت خدا و اي'),
(239, 2, 232, 'و چون زنان را طلاق داديد و عدّه‌ى آنان سر آمد، آنان را از ازدواج با شوهران سابق خود در صورتى كه ميان آنها به خوبى توافق حاصل شده باشد جلوگيرى نكنيد. هر كه از شما به خدا و روز واپسين ايمان دارد با اين دستور پند داده مى‌شود. اين براى شما پربارتر و پاك'),
(240, 2, 233, 'مادرها فرزندان خود را دو سال تمام شير دهند. [اين‌] براى كسى است كه بخواهد دوره‌ى شير دادن را كامل كند، و خوراك و پوشاك آنها به طور شايسته بر عهده‌ى پدر فرزند است. هيچ كس جز به قدر توانش مكلّف نمى‌شود. هيچ مادرى نبايد به خاطر [اختلافات خود] به كودكش'),
(241, 2, 234, 'و كسانى از شما كه مى‌ميرند و همسرانى به جا مى‌گذارند [آنها] بايد چهار ماه و ده روز عدّه نگه دارند، و چون عدّه‌ى آنها سر آمد، در آنچه به شايستگى در باره‌ى خود انجام دهند گناهى بر شما نيست، و خدا به آنچه مى‌كنيد آگاه است'),
(242, 2, 235, 'و گناهى بر شما نيست كه به كنايه و سر بسته از [اين‌] زنان خواستگارى كنيد يا [ازدواج با آنها را] در دل نهان داريد. خدا آگاه است، كه شما آنها را ياد خواهيد نمود، ولى با آنها قرار پنهانى مگذاريد، مگر سخنى شايسته [و كنايى‌] بگوييد. و تا عدّه‌شان سر نيامد'),
(243, 2, 236, 'اگر زنان را قبل از نزديكى و قبل از تعيين مهر طلاق داديد گناهى بر شما نيست ولى هديه شايسته‌اى تقديمشان كنيد، توانگر به قدر توان خويش و تنگدست به قدر توان خويش، و اين وظيفه‌اى بر نيكوكاران است'),
(244, 2, 237, 'و اگر زنان را پيش از نزديكى طلاق داديد و مهرى براى آنان تعيين كرده بوديد، نصف آنچه مقرر داشته‌ايد بر عهده‌ى شماست، مگر اين كه آنان خود ببخشند يا كسى كه امر ازدواج به دست اوست ببخشد. و گذشت شما به تقوا نزديك‌تر است، و بزرگوارى را در ميان خودتان فرامو'),
(245, 2, 238, 'همه نمازها به ويژه نماز ميانه را پاس داريد و مطيعانه براى خدا به [نماز] ايستيد'),
(246, 2, 239, 'پس اگر [از دشمن‌] بيم داشتيد، پياده يا سواره نماز كنيد، و چون ايمن شديد خدا را ياد كنيد همان طور كه به شما چيزهايى آموخت كه نمى‌دانستيد'),
(247, 2, 240, 'و كسانى از شما كه در آستانه مرگ قرار مى‌گيرند و همسرانى به جا مى‌گذارند، بايد در باره‌ى آنها وصيّت كنند كه تا يك سال مخارج آنها داده شود و از خانه بيرونشان نكنند، ولى اگر آنها خود خارج شوند، در باره‌ى تصميم مناسبى كه براى خود مى‌گيرند گناهى بر شما ن'),
(248, 2, 241, 'و براى زنان طلاق داده شده هديه مناسبى بر عهده‌ى پرهيزكاران است'),
(249, 2, 242, 'بدين‌سان خدا آيات خود را براى شما بيان مى‌كند تا بينديشيد'),
(250, 2, 243, 'آيا كسانى را كه هزاران تن بودند نديدى آن‌گاه كه از بيم مرگ [و فرار از جهاد] از ديار خود بيرون شدند، پس خدا به آنها گفت: بميريد [و آنها مردند] سپس زنده‌شان كرد؟ به راستى خدا نسبت به مردم، صاحب كرم است ولى بيشتر مردم سپاس نمى‌گزارند'),
(251, 2, 244, 'و در راه خدا جهاد كنيد و بدانيد كه خدا شنواى داناست'),
(252, 2, 245, 'كيست كه به خدا وام نيكو دهد تا چندين برابرش افزون كند؟ خداست كه تنگدستى دهد و توانگرى بخشد، و به سوى او بازگردانده مى‌شويد'),
(253, 2, 246, 'آيا آن سران بنى اسرائيل را پس از موسى نديدى آن‌گاه كه به پيامبر خويش گفتند: براى ما اميرى بگمار تا در راه خدا جهاد كنيم؟ گفت: آيا احتمال نمى‌دهيد كه اگر جهاد بر شما مقرر شد جهاد نكنيد؟ گفتند: چگونه ممكن است كه در راه خدا جهاد نكنيم در حالى كه از ديا'),
(254, 2, 247, 'و پيامبرشان به آنها گفت: همانا خدا طالوت را به اميرى شما برگماشت. گفتند: از كجا او بر ما حكومت داشته باشد، حال آن كه ما براى اين امر شايسته‌تريم و او از مال دنيا چيزى ندارد؟ گفت: خدا وى را بر شما برگزيده و او را در دانش و جسم فزونى داده است، و خدا ف'),
(255, 2, 248, 'پيامبرشان به آنها گفت: نشانه اميرى او اين است كه آن صندوق [عهد] سوى شما مى‌آيد كه در آن تسكين خاطرى از جانب پروردگارتان نهفته، و يادگارى از ميراث خاندان موسى و هارون در آن است كه فرشتگان، آن را حمل مى‌كنند. مسلما اگر مؤمن باشيد، در اين [رويداد] برا'),
(256, 2, 249, 'پس همين كه طالوت با سپاهيان به راه افتاد گفت: خدا شما را به نهر آبى امتحان مى‌كند، پس هر كه از آن بنوشد از من نيست و هر كه از آن نخورد از من است، مگر كسى كه به اندازه‌ى كف دستى بياشامد. ولى همه از آن نوشيدند جز شمار اندكى از ايشان. و زمانى كه او و م'),
(257, 2, 250, 'و چون با جالوت و سپاه او رو در رو شدند گفتند: پروردگارا! بر ما شكيبايى ببار، و قدم‌هايمان را استوار ساز، و بر قوم كافر پيروزمان گردان'),
(258, 2, 251, 'پس به اذن خدا آنها را شكست دادند و داود جالوت را بكشت و خداوند او را پادشاهى و حكمت داد و از آنچه مى‌خواست به وى آموخت. و اگر خدا بعضى از مردم را به وسيله بعضى ديگر نمى‌راند، حتما زمين تباه مى‌شد ولى خدا به جهانيان نظر كرم دارد'),
(259, 2, 252, 'اينها آيات خداست كه به درستى آنها را بر تو مى‌خوانيم، و بى‌شك تو از پيامبرانى'),
(260, 2, 253, 'اين پيامبران، پاره‌اى از ايشان را بر پاره‌اى برترى داديم. از آنان كسى بود كه خدا با او سخن گفت و بعضى را درجاتى [بلند] ارزانى داشت و به عيسى بن مريم حجّت‌ها داديم و او را به روح القدس تقويت كرديم، و اگر خدا مى‌خواست كسانى كه بعد از اين پيامبران بودن'),
(261, 2, 254, 'اى كسانى كه ايمان آورده‌ايد! پيش از آن كه روزى فرا رسد كه در آن نه داد و ستدى هست و نه دوستى و شفاعتى، از آنچه روزيتان كرده‌ايم انفاق كنيد و [بدانيد كه‌] كافران، ستمگرانند'),
(262, 2, 255, 'خدا يكتاست و جز او معبودى نيست. زنده و پاينده است. نه چرت مى‌گيردش نه خواب. هر چه در آسمان‌ها و زمين است از آن اوست. كيست كه در پيشگاه او جز به اذن او شفاعت كند؟ گذشته و آينده‌ى آنها [شفيع و مشفوع‌] را مى‌داند، و به چيزى از علم او راه نمى‌يابند مگر'),
(263, 2, 256, 'در دين اجبارى نيست چرا كه راه از بيراهه آشكار شده است. پس هر كه به طاغوت كفر ورزد و به خدا ايمان آورد، يقينا به محكم‌ترين دستاويز دست آويخته است كه هرگز نخواهد گسست، و خداوند شنواى داناست'),
(264, 2, 257, 'خدا سرپرست مؤمنان است و آنان را از تاريكى‌ها به سوى روشنايى بيرون مى‌آورد، و كسانى كه كافر شدند سرپرستانشان طغيانگرانند كه از نور به تاريكى‌هايشان مى‌برند. آنها دوزخى‌اند و در آن جاودانند'),
(265, 2, 258, 'آيا نديدى آن كس را كه چون خدا به او ملك و مكنت بخشيده بود با ابراهيم در باره‌ى پروردگارش مجادله مى‌كرد؟ آن‌گاه كه ابراهيم گفت: پروردگار من كسى است كه زنده مى‌كند و مى‌ميراند. او گفت: من نيز زنده مى‌كنم و مى‌ميرانم. ابراهيم گفت: خداوند خورشيد را از م'),
(266, 2, 259, 'يا [داستان‌] كسى كه بر شهرى گذشت كه بر سقف‌هايش فرو ريخته بود. [در دل‌] گفت: خدا چگونه اهل اين ديار را پس از مرگشان زنده مى‌كند؟ پس خدا او را صد سال بميراند، سپس زنده كرد و گفت: چه مدّت [در اين حال‌] بوده‌اى؟ گفت: يك روز يا بخشى از يك روز بوده‌ام. ف'),
(267, 2, 260, 'و آن‌گاه كه ابراهيم گفت: پروردگارا! به من بنما كه مردگان را چگونه زنده مى‌كنى؟ فرمود: مگر باور ندارى؟ گفت: چرا، ولى از آن جهت كه دلم مطمئن شود. فرمود: چهار پرنده بگير [و بكش‌] و پاره پاره كن [و درهم بياميز]، سپس بر هر كوهى پاره‌اى از آنها را بگذار،'),
(268, 2, 261, 'داستان كسانى كه مالشان را در راه خدا مى‌بخشند چون داستان دانه‌اى است كه هفت خوشه بروياند كه در هر خوشه‌اى صد دانه [باشد]، و خدا براى هر كس بخواهد [پاداشى‌] چند برابر مى‌دهد و خدا وسعت بخش داناست'),
(269, 2, 262, 'كسانى كه مالشان را در راه خدا مى‌بخشند و به دنبال بخشش خود منّت و آزارى نمى‌آورند، اجرشان نزد پروردگارشان [محفوظ] است و نه بيمى بر آنهاست و نه اندوهگين شوند'),
(270, 2, 263, 'سخن خوش [با نيازمندان‌] و گذشت، از صدقه‌اى كه آزارى در پى دارد بهتر است، و خدا بى‌نياز و بردبار است'),
(271, 2, 264, 'اى مؤمنان! صدقات خود را با منّت و آزار باطل نكنيد، مانند كسى كه براى ريا و نماياندن به مردم مالش را انفاق مى‌كند و به خدا و رستاخيز ايمان ندارد. پس حكايت آن چون تخته سنگى است كه بر آن خاكى باشد و رگبارى بر آن ببارد و خاك آن را بشويد. آنان به چيزى ا'),
(272, 2, 265, 'و حكايت كسانى كه مالشان را در راه خشنودى خدا و براى تحكيم ايمان خويش انفاق مى‌كنند همچون بوستانى بر تپه‌اى است كه رگبارى بر آن ببارد و ميوه‌هايش را دو چندان بار آورد، و اگر رگبارى هم به آن نرسد باران سبك [آن را بس است‌]. و خدا به آنچه مى‌كنيد بيناست'),
(273, 2, 266, 'آيا كسى از شما دوست دارد كه باغى از درختان خرما و انگور داشته باشد، كه نهرها از پاى درختانش جارى است و هر گونه ميوه‌اى برايش به بار آورد و پيريش فرا رسيده و فرزندانى خرد و ناتوان داشته باشد، ناگاه گرد بادى آتشين بر آن بزند و [باغ يكسر] بسوزد؟ خدا بد'),
(274, 2, 267, 'اى مؤمنان! از بهترين آنچه به دست آورده‌ايد و از آنچه از زمين برايتان برآورده‌ايم انفاق كنيد، و [چيزهاى‌] پست را كه خودتان جز با چشم‌پوشى نمى‌پذيريد براى انفاق منظور نكنيد و بدانيد كه خدا بى‌نياز ستوده است'),
(275, 2, 268, 'شيطان شما را از تهيدستى مى‌ترساند و به [تنگ نظرى و] زشتى وا مى‌دارد، و خدا به شما آمرزش و بخشش خود را وعده مى‌دهد و او وسعت بخش داناست'),
(276, 2, 269, 'حكمت را به هر كس كه بخواهد مى‌بخشد، و هر كه را حكمت داده شود يقينا خير بسيارى داده شده است و جز خردمندان پند نگيرند'),
(277, 2, 270, 'و هر چه انفاق كرديد يا هر نذرى را ملتزم شديد قطعا خدا آن را مى‌داند و ستمكاران را هيچ ياورى نيست'),
(278, 2, 271, 'اگر صدقه‌ها را آشكار كنيد اين كار خوبى است و اگر پنهانش بداريد و به تهيدستان [آبرومند] بدهيد اين براى شما بهتر است و بخشى از گناهان شما را مى‌زدايد و خدا از آنچه مى‌كنيد آگاه است'),
(279, 2, 272, 'هدايت آنان [به طور اجبار] بر عهده‌ى تو نيست، بلكه خدا هر كه را بخواهد هدايت مى‌كند. و هر مالى كه انفاق كنيد به سود خود شماست، و جز براى طلب رضاى خدا انفاق نكنيد. و هر مالى كه انفاق كنيد [پاداش آن‌] به تمامى به شما داده خواهد شد و بر شما ستمى نخواهد'),
(280, 2, 273, '[اين صدقات‌] از آن تهيدستانى است كه در راه خدا از كار مانده‌اند و نمى‌توانند در زمين [براى معاش‌] سفر كنند و به خاطر مناعتى كه دارند، ناآگاه آنها را توانگر مى‌پندارد. آنان را به سيمايشان مى‌شناسى. از مردم به اصرار چيزى نمى‌خواهند. و هر مالى [به آنان'),
(281, 2, 274, 'كسانى كه مالشان را شب و روز، [و] نهان و آشكار، انفاق مى‌كنند پاداش آنها نزد پروردگارشان است و نه بيمى بر آنهاست و نه محزون شوند'),
(282, 2, 275, 'آنان كه ربا مى‌خورند، برنخيزند مگر مانند كسى كه شيطان به جنون آشفته‌اش كرده است. اين از آن روست كه گفتند: داد و ستد صرفا مانند رياست در صورتى كه خدا معامله را حلال و ربا را حرام كرده است. پس اينك كسى كه از پروردگارش پندى بدو رسد و باز ايستد، آنچه گذ'),
(283, 2, 276, 'خدا ربا را تباه و صدقات را افزون مى‌كند و او هيچ كفران پيشه گناهكارى را دوست نمى‌دارد'),
(284, 2, 277, 'همانا كسانى كه ايمان آورده و كارهاى شايسته كرده و نماز به پا داشته و زكات دادند، پاداش آنها نزد پروردگارشان است و نه بيمى بر آنهاست و نه غمگين شوند'),
(285, 2, 278, 'اى كسانى كه ايمان آورده‌ايد! اگر [واقعا] مؤمنيد از خدا بترسيد و باقى مانده‌ى ربا را واگذاريد'),
(286, 2, 279, 'و اگر [اين كار را] نكنيد، پس به جنگى از جانب خدا و رسولش آگاه باشيد، و اگر توبه كرديد اصل مال از آن شماست [كه بدين ترتيب‌] نه ستم مى‌كنيد و نه ستم مى‌بينيد'),
(287, 2, 280, 'و اگر [بدهكار]، تنگدست بود پس مهلتى بايد تا گشايشى يابد، و اينكه صدقه بدهيد برايتان بهتر است اگر مى‌دانستيد'),
(288, 2, 281, 'و از روزى بترسيد كه در آن به سوى خدا بازگردانده مى‌شويد، سپس به هر كس [پاداش‌] آنچه كرده است به تمامى داده مى‌شود و بر آنان ستم نمى‌رود'),
(289, 2, 282, 'اى مؤمنان! هنگامى كه [خواستيد] به قرض و نسيه معامله كنيد، آن را تا سر رسيدى معين بنويسيد و نويسنده‌اى ميان شما [سند معامله را] به درستى بنويسد، و نويسنده از نوشتن آن- چنان كه خدايش آموخته- دريغ نكند، پس بايد بنويسد و بدهكار املا كند و از خدا، پروردگ'),
(290, 2, 283, 'و اگر در سفر بوديد و نويسنده‌اى نيافتيد گرويى بستانيد و اگر كسى از شما ديگرى را امين دانست، آن كه امين شمرده شده، امانت او را باز پس دهد و بايد از خداوند، پروردگارش بترسد. و شهادت را كتمان نكنيد، و هر كه آن را كتمان كند همانا دلش گناهكار است، و خدا'),
(291, 2, 284, 'آنچه در آسمان‌ها و زمين است از آن خداوند است. و اگر آنچه در دل داريد عيان كنيد يا نهان داريد، خداوند شما را بدان محاسبه مى‌كند، پس هر كه را خواهد مى‌آمرزد و هر كه را خواهد عذاب مى‌كند، و خدا بر هر كارى تواناست'),
(292, 2, 285, 'پيامبر به آنچه از سوى پروردگارش بر او نازل شده ايمان دارد و مؤمنان [نيز]، همگى به خدا و فرشتگان و كتاب‌ها و پيامبران او ايمان دارند [و گويند:] ميان هيچ يك از پيامبران او فرق ننهيم، و گويند: شنيديم و اطاعت كرديم. بار الها! آمرزش تو را خواهانيم و باز'),
(293, 2, 286, 'خدا هيچ كس را جز به اندازه‌ى توانش تكليف نمى‌كند. هر چه نيكى كرده به سود او و هر چه بدى كرده و بال اوست. پروردگارا! اگر فراموش كرديم يا به خطا رفتيم بر ما مگير. بار الها! و تكليف سخت بر ما بار مكن، چنان كه بر پشينيان ما بار كردى. بار الها! و آنچه تا');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف، لام، ميم'),
(295, 3, 2, 'خداست كه هيچ معبودى جز او نيست و زنده‌ى پاينده است'),
(296, 3, 3, 'اين كتاب را به حق بر تو نازل كرد كه تصديق كننده كتابهاى پيشين است و تورات و انجيل را پيش‌تر نازل كرد'),
(297, 3, 4, 'كه راهنماى مردم باشد، و فرقان (قرآن) را نازل كرد. همانا كسانى كه آيات خدا را انكار كردند براى آنان عذابى سخت است و خدا مقتدر دادستان است'),
(298, 3, 5, 'مسلما هيچ چيز نه در زمين و نه در آسمان‌ها بر خدا پوشيده نيست'),
(299, 3, 6, 'اوست كه شما را در رحم‌ها بدان گونه كه خواهد صورتگرى مى‌كند خدايى جز او نيست كه شكست‌ناپذير حكيم است'),
(300, 3, 7, 'اوست كه اين كتاب را بر تو نازل كرد كه بخشى از آن آيات محكم [صريح و روشن‌] است كه اساس كتابند و برخى متشابه [و تأويل پذير] اند، اما كج دلان از اين كتاب، متشابهات را كه تأويل آن را جز خدا نداند، براى فتنه جويى و به قصد تأويل دنبال مى‌كنند. و آنها كه در'),
(301, 3, 8, '[گويند:] بار الها! دل‌هاى ما را پس از آن كه هدايتمان كردى ملغزان، و ما را از نزد خويش رحمتى بخشاى كه همانا تو بخشايشگرى'),
(302, 3, 9, 'پروردگارا! به يقين، تويى گرد آورنده‌ى مردمان براى روزى كه ترديدى در آن نيست، [چرا كه‌] خدا خلف وعده نمى‌كند'),
(303, 3, 10, 'كسانى كه كافر شدند، اموال و فرزندانشان به هيچ روى آنها را از [عذاب‌] خدا باز ندارد و آنان خود هيمه دوزخند'),
(304, 3, 11, 'همچون شيوه‌ى فرعونيان و كفّار پيشين آيات ما را دروغ انگاشتند، پس خدا آنان را به گناهانشان بگرفت كه خدا سخت كيفر است'),
(305, 3, 12, 'به كافران بگو: زودا كه به زانو در آييد و به سوى جهنّم گرد آورده شويد و چه بد جايگاهى است'),
(306, 3, 13, 'قطعا در [داستان‌] دو گروهى كه با هم رو در رو شدند براى شما درس عبرتى بود گروهى در راه خدا مى‌جنگيد و ديگرى كافر بود كه مؤمنان را به چشم سر دو برابر خود مى‌ديد و خدا هر كه را خواهد به نصرت خويش يارى مى‌دهد. يقينا در اين امر اهل بصيرت را عبرتى است'),
(307, 3, 14, 'دوست داشتن خواستنى‌ها از زنان و اولاد و اموال فراوان از زر و سيم و اسبان نشاندار و دام‌ها و كشتزاران در چشم مردم آراسته شده است، ولى اينها متاع زود گذر زندگانى دنياست و سر انجام نيكو [و زندگى عالى‌تر] نزد خداست'),
(308, 3, 15, 'بگو: آيا شما را به بهتر از اينها خبر دهم؟ براى پرهيزگاران نزد پروردگارشان باغ‌هايى است كه از پاى درختانش نهرها جارى است و در آن جاودانه بمانند و همسرانى پاكيزه و [از همه خوش‌تر] رضا و خرسندى خدا را دارند، و خداوند بر [احوال‌] بندگان بصير است'),
(309, 3, 16, 'كسانى كه مى‌گويند: پروردگارا! ايمان آورديم پس گناهان ما را بيامرز و ما را از عذاب آتش در امان دار'),
(310, 3, 17, '[اينان‌] صابران و راستگويان و فرمانبران و انفاق كنندگان و استغفارگران در سحرگاهانند'),
(311, 3, 18, 'خدا كه همواره نگاهبان عدل [و درستى‌] است گواهى مى‌دهد كه جز او معبودى نيست و فرشتگان و دانشوران [نيز گواهى مى‌دهند] جز او معبودى نيست كه شكست ناپذير حكيم است'),
(312, 3, 19, 'در حقيقت، دين در نزد خدا همان اسلام است، و اهل كتاب در آن اختلاف نكردند مگر پس از آن كه به حقّانيت آن پى بردند، آن هم به خاطر حسد و رقابت ميان خويش. و هر كه به آيات خدا كافر شود [بداند كه‌] خدا سريع الحساب است'),
(313, 3, 20, 'پس اگر با تو به محاجّه برخاستند بگو: من و هر كه پيرو من است روى دل به خدا سپرديم. و به كسانى كه كتاب داده شدند و به بى‌سوادان [مشرك كه كتاب ندارند،] بگو: آيا اسلام آورديد؟ پس اگر مسلمان شدند قطعا هدايت يافته‌اند، و اگر روى برتافتند تنها رساندن پيام ب'),
(314, 3, 21, 'كسانى كه آيات الهى را انكار مى‌كنند و به ناحق پيامبران را مى‌كشند و مردمى را كه به عدالت فرمان مى‌دهند به قتل مى‌رسانند، آنها را به عذابى دردناك بشارت ده'),
(315, 3, 22, 'آنها كسانى هستند كه در دنيا و آخرت اعمالشان به هدر رفته و براى آنان هيچ ياورى نخواهد بود'),
(316, 3, 23, 'آيا كسانى را كه بهره‌اى از كتاب يافته‌اند نديدى كه چون به كتاب خدا فراخوانده مى‌شوند تا ميانشان حكم كند، گروهى از آنان به حال اعراض پشت مى‌كنند'),
(317, 3, 24, 'اين بدان سبب است كه آنها گفتند: هرگز آتش جز چند روزى به ما نخواهد رسيد، و دروغ‌ها كه مى‌ساختند آنها را در دينشان فريب داد'),
(318, 3, 25, 'پس چگونه خواهد بود آن‌گاه كه آنان را در روزى كه در آن ترديدى نيست گرد آوريم و به هر كس نتيجه دستاوردش به تمامى داده شود و بر آنها ستمى نرود'),
(319, 3, 26, 'بگو: بار الها! [اى‌] صاحب فرمانروايى! به هر كه خواهى حكومت مى‌دهى و از هر كه خواهى باز مى‌ستانى، و هر كه را خواهى عزّت مى‌بخشى و هر كه را خواهى خوار مى‌كنى، [سر رشته‌] همه خيرات به دست توست و تو بر هر كارى توانايى'),
(320, 3, 27, 'شب را در روز و روز را در شب داخل مى‌كنى، و زنده را از مرده و مرده را از زنده، بيرون مى‌آورى، و هر كه را خواهى بى‌حساب روزى مى‌دهى'),
(321, 3, 28, 'مؤمنان نبايد كافران را به جاى مؤمنان دوست بگيرند، و هر كه چنين كند او را با خدا كارى نباشد [و از او بريده است‌] مگر اين كه از آنان به نوعى تقيّه كنيد. و خدا شما را از خود بر حذر مى‌دارد و [بدانيد كه‌] بازگشت [نهايى‌] به سوى خداوند است'),
(322, 3, 29, 'بگو: اگر آنچه را كه در سينه‌هاى شماست نهان كنيد، يا عيان كنيد خدا آن را مى‌داند، و هر آنچه را در آسمان‌ها و زمين است مى‌داند، و خدا بر هر چيزى تواناست. بازگشت، سرنوشت. ان تخفوا (خفى): اگر مخفى كنيد. ما فى صدوركم: آنچه در دل‌هاى شماست. او تبدوا (بدو)'),
(323, 3, 30, 'روزى كه هر كس هر آنچه از نيك و بد كرده است حاضر مى‌يابد و آرزو مى‌كند كاش ميان او و كار بدش فاصله‌اى دور بود، و خدا شما را از [كيفر] خود برحذر مى‌دارد، و خدا به بندگان رئوف است'),
(324, 3, 31, 'بگو: اگر خدا را دوست مى‌داريد از من پيروى كنيد تا او نيز دوستتان بدارد و گناهانتان را بيامرزد، و خدا آمرزنده‌ى مهربان است'),
(325, 3, 32, 'بگو: خدا و رسول را اطاعت كنيد پس اگر رويگردان شدند [بدانند كه‌] خدا كافران را دوست ندارد'),
(326, 3, 33, 'همانا خدا آدم و نوح و خاندان ابراهيم و خاندان عمران را بر مردم جهان برگزيد'),
(327, 3, 34, 'فرزندانى كه برخى از نسل برخى ديگرند، و خدا شنواى داناست'),
(328, 3, 35, 'آن‌گاه كه همسر عمران گفت: پروردگار! آنچه در شكم دارم نذر تو كردم، آزاد براى [خدمت خانه‌] تو، پس از من بپذير كه تويى شنواى دانا'),
(329, 3, 36, 'پس چون فرزند را بزاد، [با حسرت‌] گفت: پروردگارا! من آن را دختر زاييدم- و خدا بهتر مى‌داند كه او چه زاده و پسر همچون دختر نيست- و من او را مريم ناميدم و او و خاندانش را از شرّ شيطان مطرود به تو مى‌سپارم'),
(330, 3, 37, 'پس پروردگارش مريم را به نيكى پذيرا شد و او را نيكو بار آورد و زكريّا را سرپرست وى قرار داد. هر بار كه زكريّا در محراب بر او وارد مى‌شد نزد او رزق و روزى خاصّى مى‌يافت، [يك روز] گفت: اى مريم! اين روزى از كجا براى تو مى‌رسد؟ پاسخ داد: اين از جانب خداون'),
(331, 3, 38, 'اين جا بود كه زكريّا پروردگارش را بخواند و عرضه داشت: بار الها! از جانب خود فرزندى پاك عطايم كن كه تو شنواى دعايى'),
(332, 3, 39, 'پس در حالى كه در محراب به نماز ايستاده بود، فرشتگان او را ندا دادند كه خداوند تو را به يحيى نويد مى‌دهد كه تصديق كننده‌ى كلمه خدا (عيسى) است و بزرگوار و پارسا و پيامبرى از صالحان است'),
(333, 3, 40, 'گفت: پروردگارا! چگونه مرا فرزندى خواهد بود در حالى كه پير شده‌ام و همسرم نازا است؟ [فرشته‌] گفت: اين چنين [است كه‌] خدا هر چه خواهد مى‌كند'),
(334, 3, 41, 'گفت: خدايا! براى من نشانه‌اى بگذار. فرمود: نشانه تو اين است كه سه روز با مردم جز به اشاره سخن نتوانى گفت و پروردگارت را بسيار ياد كن و شبانگاه و بامداد او را تسبيح گوى'),
(335, 3, 42, 'و هنگامى كه فرشتگان گفتند: اى مريم! خداوند تو را برگزيد و پاكيزه داشت و تو را بر زنان جهان برترى داد'),
(336, 3, 43, 'اى مريم! مطيع پروردگار خود باش و سجده كن و همراه راكعان ركوع نما'),
(337, 3, 44, 'اين از اخبار غيب است كه به تو وحى مى‌كنيم، و آن دم كه آنها قلم‌هاى قرعه خود را مى‌افكندند تا كدامشان سرپرستى مريم را به عهده گيرد، تو پيش آنها نبودى، و آن دم كه با يكديگر مشاجره مى‌كردند نزدشان نبودى'),
(338, 3, 45, 'آن‌گاه كه فرشتگان گفتند: اى مريم! خدا تو را به كلمه‌اى از جانب خود كه نامش مسيح عيسى بن مريم است مژده مى‌دهد كه در دنيا و آخرت آبرومند و از مقرّبان است'),
(339, 3, 46, 'و در گهواره [به اعجاز] و در ميانسالى [به وحى‌] با مردم سخن مى‌گويد و از صالحان است'),
(340, 3, 47, 'مريم گفت: پروردگارا! چگونه مرا فرزندى باشد حال آن كه دست بشرى به من نرسيده است؟ فرمود: اين چنين [است كه‌] خدا آنچه بخواهد مى‌آفريند، و چون كارى را اراده كند، فقط به آن مى‌گويد: باش، پس موجود مى‌شود'),
(341, 3, 48, 'و به او كتاب و حكمت و تورات و انجيل مى‌آموزد'),
(342, 3, 49, 'و او را به عنوان پيامبرى به سوى بنى اسرائيل مى‌فرستد كه [مى‌گويد:] با معجزه از پروردگارتان سوى شما آمده‌ام من از گل براى شما چيزى به شكل پرنده مى‌سازم و در آن مى‌دمم و آن به اذن خدا پرنده‌اى مى‌شود، و به اذن خداوند كور مادرزاد و پيس را شفا مى‌دهم و م'),
(343, 3, 50, 'و تورات را كه پيش از من نازل شده تصديق مى‌كنم و [آمده‌ام‌] تا برخى چيزها را كه بر شما حرام شده برايتان حلال كنم، و از سوى پروردگارتان براى شما معجزه‌اى آورده‌ام، پس از خدا بترسيد و از من فرمان بريد'),
(344, 3, 51, 'به راستى خداى يكتا پروردگار من و پروردگار شماست، پس او را بپرستيد كه راه درست همين است'),
(345, 3, 52, 'پس وقتى عيسى به كفر آنها پى برد، گفت: ياوران من در راه خدا چه كسانند؟ حواريّون گفتند: ما ياوران خداييم، به خداوند ايمان آورده‌ايم و گواه باش كه ما فرمانبرداريم'),
(346, 3, 53, 'پروردگارا! به آنچه نازل كرده‌اى ايمان آورديم و از پيامبر پيروى كرديم پس نام ما را در زمره‌ى شاهدان بنويس'),
(347, 3, 54, 'آنها مكر كردند و خدا هم [در پاسخ آنها] مكر در ميان آورد و خدا ماهرترين مكر كنندگان است'),
(348, 3, 55, 'آن‌گاه كه خدا فرمود: اى عيسى! من تو را برگرفته و به سوى خود بالا مى‌برم و تو را از [آلودگى‌] كافران پاك مى‌سازم و پيروان تو را تا روز قيامت فوق كافران قرار مى‌دهم، سپس بازگشت شما به سوى من است، پس در آنچه بر سر آن اختلاف داشتيد در ميانتان داورى مى‌كن'),
(349, 3, 56, 'اما كسانى كه كافر شدند، آنها را در دنيا و آخرت به عذابى سخت دچار مى‌سازم و هيچ ياورى نخواهند داشت'),
(350, 3, 57, 'ولى كسانى كه ايمان آورده و عمل صالح انجام دادند اجرشان را به تمامى بدهد و خدا ستمكاران را دوست نمى‌دارد'),
(351, 3, 58, 'اينها كه بر تو مى‌خوانيم از آيه‌ها و اندرز و حكمت‌آميز است'),
(352, 3, 59, 'حكايت عيسى نزد خدا چون حكايت آدم است كه او را از خاك آفريد سپس به او گفت: باش، پس وجود يافت'),
(353, 3, 60, 'حق [همين است كه‌] از جانب پروردگار توست. پس، از ترديد كنندگان مباش'),
(354, 3, 61, 'پس هر كه با وجود دانشى كه [در باره‌ى عيسى‌] سوى تو آمد با تو محاجّه كرد بگو: بياييد پسرانمان و پسرانتان و زنانمان و زنانتان و كسان نزديكمان و كسان نزديكتان را فرا خوانيم، سپس [به درگاه خدا] تضرّع كنيم و لعنت خدا را بر دروغگويان قرار دهيم'),
(355, 3, 62, 'به راستى حكايت واقعى [مسيح‌] همين است و معبودى جز خدا نيست، و خداوند است كه يقينا، شكست ناپذير حكيم است'),
(356, 3, 63, 'پس اگر رويگردان شدند، [بدان كه‌] بى‌ترديد خدا به [حال‌] مفسدان آگاه است'),
(357, 3, 64, 'بگو: اى اهل كتاب! بياييد بر سر سخنى كه ميان ما و شما مشترك است بايستيم كه جز خدا را نپرستيم و كسى را با او شريك نكنيم و هيچ كس از ما ديگرى را به جاى خداوند صاحب اختيار نگيرد. پس اگر [از اين پيشنهاد] اعراض كردند بگوييد: شاهد باشيد كه ما سر به فرمان خد'),
(358, 3, 65, 'اى اهل كتاب! چرا در باره‌ى ابراهيم محاجّه مى‌كنيد در حالى كه تورات و انجيل نازل نشد مگر بعد از او؟ آيا تعقل نمى‌كنيد'),
(359, 3, 66, 'هان! شما همان كسانى هستيد كه در آنچه به آن علم داريد، محاجّه كرديد، [ولى‌] چرا در باره‌ى موضوعى كه نسبت به آن آگاهى نداريد محاجّه مى‌كنيد؟ و خدا مى‌داند و شما نمى‌دانيد'),
(360, 3, 67, 'ابراهيم نه يهودى بود و نه نصرانى، بلكه موحّدى فرمانبردار بود و از مشركان نبود'),
(361, 3, 68, 'در حقيقت نزديك‌ترين مردم به ابراهيم كسانى هستند كه از او پيروى كردند و نيز اين پيامبر و كسانى كه به او گرويده‌اند. و خدا سرپرست مؤمنان است'),
(362, 3, 69, 'گروهى از اهل كتاب آرزو دارند كه شما را گمراه كنند، ولى جز خودشان را گمراه نمى‌كنند و نمى‌فهمند'),
(363, 3, 70, 'اى اهل كتاب! چرا آيات الهى را انكار مى‌كنيد با اين كه شما [به حقانيت آن‌] گواهيد'),
(364, 3, 71, 'اى اهل كتاب! چرا حق را به باطل در مى‌آميزيد و حقيقت را با اين كه مى‌دانيد پنهان مى‌كنيد'),
(365, 3, 72, 'و دسته‌اى از اهل كتاب گفتند: آغاز روز به آنچه بر مؤمنان نازل شده بگرويد و پايان آن انكارش كنيد، شايد آنها [از اسلام‌] بازگردند'),
(366, 3, 73, 'و جز به كسى كه از آيين شما پيروى كند، ايمان نياوريد [و اعتماد نكنيد] بگو: هدايت، هدايت خداست- [و گفتند: باور نكنيد] كه به كسى نظير آنچه به شما داده شده است داده شود يا بتوانند در پيشگاه پروردگارتان با شما محاجّه كنند. بگو: تفضّل به دست خداست، آن را ب'),
(367, 3, 74, 'هر كه را خواهد خاصّ رحمت خويش مى‌كند، و خداوند صاحب فضل و كرم عظيم است'),
(368, 3, 75, 'و از اهل كتاب كسى است [كه در دستكارى چنان است‌] كه اگر او را بر مال فراوانى امين شمارى، آن را به تو باز مى‌گرداند و از آنها كسى [هم چنان‌] است كه اگر او را بر دينارى امين شمارى آن را به تو باز نخواهد گرداند مگر آن كه پيوسته بالاى سرش ايستاده باشى. اي'),
(369, 3, 76, 'آرى، هر كه به پيمانش وفا كند و پارسايى نمايد، بى‌ترديد خداوند، پرهيزگاران را دوست دارد'),
(370, 3, 77, 'بى‌ترديد، كسانى كه پيمان خدا و سوگندهاى خود را به بهاى ناچيز مى‌فروشند آنان را در آخرت بهره‌اى نيست و خدا در روز قيامت با آنها سخن نگويد و به سويشان ننگرد و پاكشان نگرداند و آنها را عذابى دردناك است'),
(371, 3, 78, 'و همانا گروهى از آنها هستند كه به هنگام تلاوت كتاب، زبان خود را چنان مى‌پيچانند كه گمان كنيد [آنچه مى‌خوانند] از كتاب خداست در حالى كه آن از كتاب خدا نيست، و ادّعا مى‌كنند كه آن از جانب خداست در حالى كه از جانب خدا نيست، و بر خدا دروغ مى‌بندند و خودش'),
(372, 3, 79, 'هيچ بشرى را نرسد كه خدا به او كتاب و حكم و پيامبرى بدهد، آن‌گاه به مردم بگويد: به جاى خدا مرا بپرستيد. بلكه [مى‌گويد:] شما كه كتاب آسمانى تعليم مى‌داديد و به درس و بحث آن مى‌پرداختيد، مردان خدايى [و موحد] باشيد'),
(373, 3, 80, 'نه اين كه شما را دستور دهد كه فرشتگان و پيامبران را به خدايى بگيريد. آيا او شما را پس از آن كه مسلمان شده‌ايد به كفر مى‌خواند'),
(374, 3, 81, 'و هنگامى كه خدا از پيامبران [و امّت‌هاى آنان‌] پيمان گرفت كه چون به شما كتاب و حكمت بخشيدم، آن‌گاه پيامبرى سويتان آمد كه گواهى دهنده بر كتاب آسمانى شما گرديد، بر شماست كه به او ايمان آوريد و حتما يارى‌اش دهيد آن‌گاه گفت: آيا گردن نهاديد و پيمانم را پ'),
(375, 3, 82, 'پس هر كه بعد از آن [پيمان‌] پشت كند، پس آنان از فرمان خدا خارج شده‌اند'),
(376, 3, 83, 'پس آيا آنها دينى جز دين الهى مى‌جويند؟ حال آن كه هر كه در آسمان‌ها و زمين است خواه ناخواه فرمانبردار اوست و همه به سوى او بازگردانده مى‌شوند. هم الفاسقون: پس آنان واقعا نافرمانند. تولّى (ولى): پشت كرد، رو گردانيد. فاسق: نافرمان، كسى كه از حوزه اطاعت'),
(377, 3, 84, 'بگو: به خدا و آنچه بر ما و آنچه بر ابراهيم و اسماعيل و اسحاق و يعقوب و نوادگان [او] نازل شد و آنچه به موسى و عيسى و پيامبران از سوى پروردگارشان داده شد ايمان آورده‌ايم ميان هيچ يك از آنها فرق ننهيم و ما فرمانبردار اوييم'),
(378, 3, 85, 'و هر كه دينى غير از اسلام برگزيند، هرگز از او پذيرفته نشود و او در آخرت از زيانكاران است'),
(379, 3, 86, 'چگونه خدا قومى را هدايت كند كه بعد از ايمان و گواهى به حقانيت رسول و آمدن نشانه‌هاى روشن براى آنها، كافر شدند؟ و خداوند قوم ستمكار را هدايت نمى‌كند'),
(380, 3, 87, 'آنان سزايشان اين است كه لعنت خدا و فرشتگان و مردم، همگى بر آنهاست'),
(381, 3, 88, 'جاودانه در آن بمانند و نه عذاب از آنها كاسته شود و نه مهلت يابند'),
(382, 3, 89, 'مگر كسانى كه پس از آن توبه كردند و به اصلاح و جبران پرداختند كه بى‌ترديد خدا آمرزگار مهربان است'),
(383, 3, 90, 'همانا كسانى كه از پى ايمانشان كافر شدند سپس بر كفر خود افزودند، هيچ‌گاه توبه آنها پذيرفته نمى‌شود و آنان گمراهند'),
(384, 3, 91, 'به راستى كسانى كه [از پى ايمانشان‌] كافر شدند و در حال كفر مردند، حتى اگر زمين را پر از طلا كنند و آن را فديه دهند هرگز از هيچ يك از ايشان پذيرفته نمى‌شود. آنان عذابى دردناك در پيش دارند و هيچ ياورى برايشان نخواهد بود'),
(385, 3, 92, 'هرگز به نيكى دست نيابيد مگر اين كه از آنچه دوست داريد انفاق كنيد، و هر چه انفاق كنيد، قطعا خدا از آن آگاه است'),
(386, 3, 93, 'همه خوردنى‌ها براى بنى اسرائيل حلال بود، مگر آنچه يعقوب پيش از نزول تورات بر خود تحريم كرده بود. بگو: اگر شما راست مى‌گوييد، تورات را بياوريد و بخوانيد'),
(387, 3, 94, 'پس هر كه بعد از اين بر خدا دروغ بندد، آنان به يقين ستمكارند'),
(388, 3, 95, 'بگو: خداوند راست گفت. پس از آيين ابراهيم پيروى كنيد. كه حق گرا بود و از مشركان نبود'),
(389, 3, 96, 'همانا نخستين خانه‌اى كه براى مردم بنا شد همان است كه در مكه است و مايه بركت و هدايت جهانيان است'),
(390, 3, 97, 'در آن نشانه‌هاى روشنى است [از جمله‌] مقام ابراهيم، و هر كه وارد آن شود ايمن است. و حق خدا بر مردم است كه قصد زيارت اين خانه كنند، كسى كه به آن راه تواند يافت. و هر كه كافر شود [بداند كه‌] قطعا خدا از جهانيان بى‌نياز است'),
(391, 3, 98, 'بگو: اى اهل كتاب! چرا آيات الهى را انكار مى‌كنيد در حالى كه خدا شاهد كردار شماست'),
(392, 3, 99, 'بگو: اى اهل كتاب! چرا كسى را كه ايمان آورده از راه خدا باز مى‌داريد و آن را منحرف مى‌خواهيد، در صورتى كه شما گواهيد؟ و خدا از آنچه مى‌كنيد غافل نيست'),
(393, 3, 100, 'اى مؤمنان! اگر فرقه‌اى از اهل كتاب را فرمان بريد، شما را بعد از ايمانتان به كفر باز مى‌گردانند'),
(394, 3, 101, 'و چگونه كافر مى‌شويد با آن كه آيات الهى بر شما خوانده مى‌شود و فرستاده‌ى او در ميان شماست؟ و هر كه به خدا تمسك جويد، قطعا به راه راست رهبرى شده است'),
(395, 3, 102, 'اى كسانى كه ايمان آورده‌ايد! چنان كه سزاوار خداست تقوا پيشه كنيد، و زينهار كه بميريد و مسلمان نباشيد'),
(396, 3, 103, 'و همگى به رشته الهى در آويزيد و پراكنده نشويد، و نعمت خدا را بر خود ياد كنيد كه دشمن يكديگر بوديد پس او ميان دل‌هاى شما الفت داد و به موهبت او با هم برادر شديد و بر لبه پرتگاه آتش بوديد كه شما را از آن رهانيد. خداوند اين گونه آيات خود را براى شما رو'),
(397, 3, 104, 'و بايد در ميان شما گروهى باشند كه به نيكى فرمان دهند و از كار ناشايسته باز دارند و هم آنان رستگارانند'),
(398, 3, 105, 'و همانند كسانى نباشيد كه پس از آمدن دلايل روشنگر براى ايشان، پراكنده شدند و اختلاف كردند، و اينانند كه عذابى بزرگ خواهند داشت'),
(399, 3, 106, 'روزى كه چهره‌هايى سفيد و چهره‌هايى سياه شود. اما رو سياهان [به آنها گفته شود:] آيا پس از ايمانتان كافر شديد؟ پس به سزاى كفرتان اين عذاب را بچشيد'),
(400, 3, 107, 'و اما رو سفيدان در رحمت خدا (بهشت) قرار مى‌گيرند و در آن جاودانند'),
(401, 3, 108, 'اينها آيات الهى است كه به درستى بر تو مى‌خوانيم، و خدا هيچ ستمى بر جهانيان نمى‌خواهد'),
(402, 3, 109, 'و آنچه در آسمان‌ها و زمين است از آن خداست و كارها به سوى او بازگردانده مى‌شود'),
(403, 3, 110, 'شما بهترين امتى هستيد كه براى مردم پديدار شده‌ايد كه به كار پسنديده فرمان مى‌دهيد و از كار ناپسند باز مى‌داريد و به خدا ايمان داريد. و اگر اهل كتاب ايمان آورده بودند البته برايشان بهتر بود. برخى از آنان مؤمنند و بيشترشان فاسقند'),
(404, 3, 111, 'هرگز به شما زيان نمى‌رسانند جز آزارى، و اگر با شما درگير شوند به شما پشت خواهند كرد، آن‌گاه يارى [و پيروزى‌] نخواهند يافت'),
(405, 3, 112, 'هر جا يافته شوند مهر خوارى بر آنها خورده، مگر به ريسمانى از جانب خدا [پذيرش اسلام‌] و يا به ريسمانى از جانب مردم [پناهندگى به مسلمانان تمسك جويند]. آنها به خشم خدا دچار شدند و مهر بيچارگى بر آنها زده شد. اين [عقوبت‌] از آن روست كه آنها همواره آيات ا'),
(406, 3, 113, 'آنان يكسان نيستند برخى از اهل كتاب امتى هستند كه [به طاعت خدا] ايستاده‌اند، آيات الهى را در دل شب تلاوت مى‌كنند و سر به سجده دارند'),
(407, 3, 114, 'به خدا و روز بازپسين ايمان دارند و به نيكى فرمان مى‌دهند و از كار ناپسند باز مى‌دارند و در كارهاى خير پيشتازند و آنان از صالحانند'),
(408, 3, 115, 'و هر كار خيرى انجام دهند هرگز ناسپاسى نبينند، و خدا به [حال‌] پرهيزكاران آگاه است'),
(409, 3, 116, 'به يقين، كافران را اموال و فرزندانشان به هيچ روى از [عذاب‌] خدا باز ندارد و آنان دوزخى‌اند و در آن جاويدان'),
(410, 3, 117, 'حكايت آنچه در اين زندگى دنيا [براى تخريب دين‌] هزينه مى‌كنند، همچون حكايت بادى است كه سرماى سوزانى در بر دارد كه به كشتزار كسانى كه در حق خويش ستم كرده‌اند بزند و آن را تباه سازد، و خدا به آنان ستم نكرده بلكه خود بر خويشتن ستم مى‌كنند'),
(411, 3, 118, 'اى مؤمنان! بيگانگان را همراز خود مگيريد، چه آنها از هيچ گونه آسيب‌رسانى در حق شما كوتاهى نمى‌كنند. دوست دارند شما در رنج باشيد. همانا دشمنى از گفتارشان پيداست، و كينه‌اى كه دلشان نهان مى‌دارد بزرگ‌تر است. به راستى ما آيات را براى شما بيان كرديم اگر'),
(412, 3, 119, 'هان! اين شماييد كه آنها را دوست مى‌داريد، ولى آنها شما را دوست نمى‌دارند، و شما به همه كتاب‌هاى آسمانى ايمان داريد، و چون با شما رو به رو شوند، گويند: ايمان آورده‌ايم. و چون تنها شوند، به دشمنى شما انگشتان خود را از خشم به دندان مى‌گزند. بگو: به خش'),
(413, 3, 120, 'اگر نيكى به شما رسد آنها را بدحال مى‌كند، و اگر گزندى به شما رسد بدان شاد مى‌شوند. و اگر استقامت و پارسايى كنيد، حيله آنان هيچ زيانى به شما نمى‌رساند، همانا خداوند به آنچه مى‌كنند احاطه دارد'),
(414, 3, 121, 'و [ياد آر] آن‌گاه كه [در جنگ احد] بامدادان از نزد كسان خود بيرون شدى و مؤمنان را براى كارزار در مواضع خود جاى مى‌دادى، و خدا شنواى داناست'),
(415, 3, 122, 'آن‌گاه كه دو گروه از شما خواستند سستى كنند، در حالى كه خدا يارشان بود. و مؤمنان بايد تنها بر خدا توكل كنند'),
(416, 3, 123, 'و به راستى خدا شما را در بدر يارى داد در حالى كه ناتوان بوديد. پس از خدا بترسيد، باشد كه شكر او را به جا آوريد'),
(417, 3, 124, 'آن‌گاه كه به مؤمنان مى‌گفتى: آيا شما را بس نيست كه پروردگارتان با سه هزار فرشته فرستاده شده ياريتان دهد'),
(418, 3, 125, 'آرى، اگر شكيبايى و پارسايى كنيد و دشمنان در اين هيجان و شتاب خويش بر شما بتازند، پروردگارتان شما را با پنج هزار فرشته نشاندار يارى خواهد كرد'),
(419, 3, 126, 'و خدا آن را جز بشارتى براى شما قرار نداد، تا دل‌هايتان به آن آرام گيرد، و پيروزى تنها از جانب خداوند شكست ناپذير حكيم است'),
(420, 3, 127, 'تا بخشى از كافران را براندازد يا سركوبشان كند تا نوميد بازگردند'),
(421, 3, 128, 'از اين كار چيزى به دست تو نيست، يا خدا آنها را مى‌بخشد و يا عذابشان مى‌كند، چرا كه آنان ستمكارند'),
(422, 3, 129, 'و آنچه در آسمان‌ها و زمين است از آن خداست، هر كه را خواهد مى‌آمرزد و هر كه را خواهد عذاب مى‌كند و خدا آمرزنده‌ى مهربان است'),
(423, 3, 130, 'اى مؤمنان! ربا را چند برابر مخوريد، و از خدا بترسيد تا رستگار شويد'),
(424, 3, 131, 'و از آتشى كه براى كافران آماده شده پروا كنيد'),
(425, 3, 132, 'و خدا و پيامبر را فرمان بريد تا مورد رحمت قرار گيريد'),
(426, 3, 133, 'و بشتابيد به آمرزشى از پروردگارتان و بهشتى كه پهنه آن آسمان‌ها و زمين است كه براى پرهيزكاران آماده شده است'),
(427, 3, 134, 'همان‌ها كه در راحت و رنج انفاق مى‌كنند و خشم خود را فرو مى‌خورند و از مردم در مى‌گذرند، و خدا نيكوكاران را دوست دارد'),
(428, 3, 135, 'و كسانى كه چون كار زشتى كردند يا بر خود ستم نمودند خدا را ياد كنند و براى گناهان خويش آمرزش طلبند، و جز خدا چه كسى است كه گناهان را بيامرزد؟ و دانسته بر خلاف‌هايى كه كرده‌اند پافشارى نكنند'),
(429, 3, 136, 'پاداش آنان آمرزش پروردگارشان و بوستان‌هايى است كه از پاى درختانش نهرها جارى است و جاودانه در آنند، و پاداش اهل عمل چه نيكوست'),
(430, 3, 137, 'بى‌ترديد، پيش از شما ماجراها گذشت (سنّت هلاكت اقوام)، پس در زمين بگرديد و ببينيد كه سر انجام تكذيب كنندگان چه سان بود'),
(431, 3, 138, 'اين بيانى براى مردم، و هدايت و پندى براى پرهيزكاران است'),
(432, 3, 139, 'و سست نگرديد و غمگين نشويد، كه اگر مؤمن باشيد، شما برتريد'),
(433, 3, 140, 'اگر به شما زخمى رسيد، آن گروه را نيز زخمى همانند آن رسيده است. و ما اين روزها [ى شكست و پيروزى‌] را ميان مردم مى‌گردانيم تا خدا مؤمنان [واقعى‌] را معلوم دارد و از شما گواهانى بگيرد، و خدا ستمكاران را دوست نمى‌دارد. آن روزگاران. نداول (دول): به گردش'),
(434, 3, 141, 'و تا خداوند مؤمنان را ناب و خالص گرداند و كافران را به نيستى كشاند'),
(435, 3, 142, 'آيا پنداشتيد كه به بهشت در آييد، در حالى كه هنوز خدا كسانى از شما را كه جهاد كردند و استقامت نمودند معلوم نداشته است'),
(436, 3, 143, 'و همانا شما بوديد كه مرگ [در ميدان جنگ‌] را پيش از آن كه با آن رو به رو شويد، آرزو مى‌كرديد، ولى آن را ديديد و تماشاگر شديد [و تن به جنگ نداديد]'),
(437, 3, 144, 'و محمد صلّى الله عليه وآله نيست جز فرستاده‌اى كه پيش از او هم پيامبران گذشتند. آيا اگر او بميرد يا كشته شود عقبگرد مى‌كنيد؟ و هر كه عقبگرد كند، هرگز به خدا زيانى نمى‌رساند، و خدا سپاسگزاران را پاداش خواهد داد'),
(438, 3, 145, 'و هيچ كس را نرسد كه بى‌اذن خدا بميرد، كه اين سرنوشتى مدّت دار و معين است. و هر كه پاداش دنيا خواهد از آن به او مى‌دهيم، و هر كه پاداش آخرت خواهد [نيز] از آنش مى‌دهيم و زودا كه شاكران را پاداش دهيم'),
(439, 3, 146, 'و بسى پيامبران كه خداپرستان بسيارى در كنارشان نبرد كردند و از هر رنجى كه در راه خدا ديدند سست و زبون و درمانده نشدند، و خدا صابران را دوست مى‌دارد'),
(440, 3, 147, 'و آنان سخنى جز اين نداشتند كه گفتند: پروردگارا! گناهانمان را بيامرز، و از زياده‌روى ما در كارمان در گذر، و قدم‌هايمان را استوار دار، و ما را بر گروه كافران پيروز گردان'),
(441, 3, 148, 'پس خدا به آنها پاداش دنيوى و پاداش نيكوى آخرت عطا كرد، و خدا نيكوكاران را دوست مى‌دارد'),
(442, 3, 149, 'اى مؤمنان! اگر از كافران پيروى كنيد، شما را به عقب باز مى‌گردانند و آن‌گاه خسارت زده باز مى‌گرديد'),
(443, 3, 150, 'آرى، [تنها] خداوند مولاى شماست و همو بهترين ياوران است'),
(444, 3, 151, 'به زودى در دل كافران رعب و هراس خواهيم افكند، زيرا آنها چيزى را كه خدا دليلى بر آن نفرستاده است با او شريك نمودند، و جايشان آتش است و بد است جايگاه ستمكاران'),
(445, 3, 152, 'و [در جنگ احد] حقيقتا خدا به وعده‌ى خويش با شما وفا كرد، آن‌گاه كه به اذن خدا آنها را تار و مار مى‌كرديد، تا وقتى كه سست شديد و در كار جنگ اختلاف نموديد و نافرمان شديد بعد از آن كه خداوند چيزى را كه دوست مى‌داشتيد (پيروزى) به شما نشان داد. گروهى از'),
(446, 3, 153, '[ياد آريد] آن‌گاه كه مى‌گريختيد و به كسى توجه نمى‌كرديد و پيامبر صلّى الله عليه وآله از پشت سرتان شما را فرا مى‌خواند. پس خدا نيز شما را غمى روى غم سزا داد تا بر آنچه از دست داده‌ايد و آنچه بر سرتان آمده غم نخوريد، و خدا بدانچه مى‌كنيد آگاه است'),
(447, 3, 154, 'سپس از پى آن غم، آرامشى بر شما نازل كرد، خوابى كه جمعى از شما را فرا گرفت و گروهى كه تنها در فكر جان خويش بودند گمان‌هاى نادرستى، در باره‌ى خدا چون گمان دوره‌ى جاهليّت داشتند، مى‌گفتند: آيا ما را از اين كار چيزى هست؟ بگو: سر رشته همه كارها به دست خد'),
(448, 3, 155, 'همانا كسانى از شما كه روز برخورد دو گروه، به جنگ پشت كردند شيطان، آنها را بر اثر پاره‌اى گناهان كه مرتكب شده بودند منحرف ساخت و البته خدا از آنان در گذشت، چرا كه خدا آمرزنده‌ى بردبار است'),
(449, 3, 156, 'اى مؤمنان! مانند كسانى نباشيد كه كافر شدند و در مورد برادرانشان كه به سفر يا جنگ رفتند [و مردند يا شهيد شدند] گفتند: اگر نزد ما بودند نمى‌مردند و كشته نمى‌شدند، تا خدا اين [پندار] را مايه حسرت در دلشان قرار دهد، و [بدانيد اين‌] خداست كه زنده مى‌كند'),
(450, 3, 157, 'و اگر در راه خدا كشته شديد يا مرديد، البته آمرزش و رحمت خدا از هر آنچه [ديگران‌] جمع مى‌كنند بهتر است'),
(451, 3, 158, 'و اگر بميريد يا كشته شويد، البته به سوى خدا محشور مى‌گرديد'),
(452, 3, 159, 'پس تو به لطف و رحمت الهى با آنان نرمخو شدى، و اگر درشتخوى و سختدل بودى بى‌شك از گرد تو پراكنده مى‌شدند. پس از ايشان درگذر و برايشان آمرزش بخواه و با آنان در كار [جنگ‌] مشورت كن، و چون تصميم گرفتى بر خدا توكّل كن، همانا خدا توكّل كنندگان را دوست مى‌د'),
(453, 3, 160, 'اگر خدا شما را يارى كند كسى بر شما پيروز نخواهد شد، و اگر رهايتان كند غير از او كيست كه شما را يارى دهد؟ پس مؤمنان بايد بر خدا توكّل كنند'),
(454, 3, 161, 'و هيچ پيامبرى را نسزد كه خيانت ورزد، و هر كه خيانت كند روز قيامت آنچه خيانت كرده با خود بياورد، آن‌گاه به هر كسى پاداش هر چه كرده به تمامى داده شود و بر آنان ستم نرود'),
(455, 3, 162, 'پس آيا كسى كه در طلب رضاى خداست همانند كسى است كه دچار خشم خدا شده و جاى او جهنّم است؟ و بد سر انجامى است'),
(456, 3, 163, 'آنها [خود] در پيشگاه خدا منزلت‌ها و مقاماتى هستند و خدا به آنچه مى‌كنند بيناست'),
(457, 3, 164, 'به راستى خدا بر مؤمنان منت نهاد آن‌گاه كه در ميانشان پيامبرى از خودشان برانگيخت تا آيات او را بر آنان بخواند و پاكشان كند و كتاب و حكمتشان بياموزد، در حالى كه بى‌ترديد از پيش در گمراهى آشكارى بودند'),
(458, 3, 165, 'آيا هنگامى كه به شما مصيبتى رسيد كه قطعا دو چندانش را [در جنگ بدر به دشمن‌] وارد ساخته بوديد گفتيد: اين [مصيبت‌] از كجاست؟ بگو: آن از ناحيه خود شماست، [كه سنگر را خالى كرديد] همانا خدا بر هر كارى تواناست'),
(459, 3, 166, 'و آنچه روز برخورد آن دو گروه [در جنگ احد] به شما رسيد، به اذن خدا بود و تا مؤمنان [واقعى‌] را باز شناسد'),
(460, 3, 167, 'و تا معلوم دارد كسانى را كه دورويى كردند و [چون‌] به آنان گفته شد: بياييد در راه خدا بجنگيد يا دفاع كنيد. گفتند: اگر مى‌دانستيم جنگى خواهد بود حتما شما را پيروى مى‌كرديم. آنها در آن روز به كفر نزديك‌تر بودند تا به ايمان. به زبان چيزى مى‌گويند كه در'),
(461, 3, 168, '[همان‌] كسانى كه [در خانه‌] نشستند و به برادرانشان گفتند: اگر از ما پيروى مى‌كردند كشته نمى‌شدند. بگو: پس مرگ را از خود برانيد اگر راست مى‌گوييد'),
(462, 3, 169, 'و كسانى را كه در راه خدا كشته شده‌اند مرده مپندار، بلكه آنها زنده‌اند و نزد پروردگارشان روزى داده مى‌شوند'),
(463, 3, 170, 'آنان به نعمتى كه خدا ارزانى‌شان داشته خوشحالند، و از [سرنوشت‌] همرزمانى كه هنوز به آنها ملحق نشده‌اند شادمانند [مى‌دانند] كه نه بيمى بر آنهاست و نه اندوهگين مى‌شوند'),
(464, 3, 171, 'آنها به نعمت و كرم خدا و اين كه خدا پاداش مؤمنان را تباه نمى‌كند شادمانند'),
(465, 3, 172, 'كسانى كه دعوت خدا و پيامبر را بعد از اصابت زخم [در احد] گردن نهادند، براى نيكوكاران و پرهيزكارانشان پاداشى بزرگ است'),
(466, 3, 173, 'همان كسانى كه وقتى مردم به آنها گفتند: مشركين در برابر شما گرد آمده‌اند، پس از آنان بترسيد، [به جاى ترس‌] بر ايمانشان افزود و گفتند: خداوند ما را بس است و نيكو حمايتگرى است'),
(467, 3, 174, 'در نتيجه با نعمت و تفضل خدا بازگشتند، بى‌آن كه زيانى به آنها برسد، و در پى رضاى خدا بودند، و خدا صاحب بخشش بى‌كران است'),
(468, 3, 175, 'جز اين نيست كه اين شيطان است كه ياران خود را مى‌ترساند، پس اگر مؤمنيد از آنان نترسيد و از من بترسيد'),
(469, 3, 176, 'و كسانى كه به كفر مى‌شتابند تو را اندوهگين نكنند، كه آنها هرگز ضررى به خدا نمى‌رسانند. خدا مى‌خواهد براى ايشان در آخرت بهره‌اى قرار ندهد و براى آنها عذاب بزرگى است'),
(470, 3, 177, 'به يقين كسانى كه كفر را به ايمان خريدند هرگز به خدا ضررى نمى‌رسانند و براى آنها عذابى دردناك است'),
(471, 3, 178, 'و كافران هرگز مپندارند اين مهلت كه به ايشان مى‌دهيم به سود آنهاست، فقط مهلتشان مى‌دهيم تا بار گناه خود را افزون كنند و براى ايشان عذاب خفت بارى است'),
(472, 3, 179, 'خدا بر آن نيست كه مؤمنان را بر اين حال كه شما هستيد [و مؤمن و منافق باهمند] رها سازد، تا اين كه پليد را از پاك جداكند. و خدا بر آن نيست كه شما را بر غيب آگاه سازد، و ليكن خدا از فرستادگان خود هر كه را خواهد بر مى‌گزيند. بنابر اين به خدا و رسولانش اي'),
(473, 3, 180, 'و كسانى كه در بخشيدن آنچه خدا از فضل خود به آنها داده بخل مى‌ورزند، مپندارند كه اين كار به سودشان است، بلكه براى آنان شرّ است زودا كه آنچه بخل ورزيده‌اند، در روز رستاخيز طوق گردنشان شود. و ميراث آسمان‌ها و زمين از آن خداست، و خدا به آنچه مى‌كنيد آگا'),
(474, 3, 181, 'البته خدا سخن كسانى (يهوديان) را كه گفتند: خدا فقير است و ما توانگريم، شنيد. آنچه را كه گفتند و پيامبرانى را كه به ناحق كشتند [به حسابشان‌] خواهيم نوشت و خواهيم گفت: بچشيد عذاب سوزان را'),
(475, 3, 182, 'اين به سبب چيزى است كه دستان شما پيش فرستاده و [گر نه‌] خدا در حق بندگان هرگز ستمكار نيست'),
(476, 3, 183, 'همانان كه گفتند: خدا به ما سفارش كرده است كه به هيچ فرستاده‌اى ايمان نياوريم مگر آن كه يك قربانى براى ما بياورد كه آتش، آن را [به نشانه قبولى‌] بخورد. [و بسوزاند] بگو: پيش از من پيامبرانى با معجزه‌ها و با آنچه گفتيد سوى شما آمدند، پس چرا آنها را كشت'),
(477, 3, 184, 'پس اگر اينها تو را تكذيب كردند [غم مخور]، همانا پيامبرانى قبل از تو تكذيب شدند كه معجزات و نوشته‌ها و كتاب‌هاى روشنگر آورده بودند'),
(478, 3, 185, 'هر كسى مرگ را مى‌چشد، و جز اين نيست كه روز قيامت پاداشتان به تمامى به شما داده مى‌شود. پس هر كه از آتش به دور داشته و در بهشت در آورده شود، به راستى رستگار شده است. و زندگى دنيا جز مايه فريب نيست'),
(479, 3, 186, 'به طور قطع با مال و جانتان آزمايش خواهيد شد، و حتما از كسانى كه پيش از شما كتاب داده شدند و نيز از مشركان [سخنان دل‌] آزار بسيار خواهيد شنيد، و اگر صبورى كنيد و پرهيزكار باشيد، همانا اين [مقاومت‌] از استوارترين كارهاست'),
(480, 3, 187, 'و آن‌گاه كه خداوند از اهل كتاب پيمان محكم گرفت كه آن را حتما براى مردم بيان كنيد و كتمانش نسازيد. آنها اين را پشت سر انداختند و آن را به بهاى ناچيزى فروختند، پس بد معامله‌اى كردند'),
(481, 3, 188, '[اى پيامبر!] مپندار آنان كه به كردار زشت خود شادمانند و دوست دارند براى آنچه نكرده‌اند ستوده شوند، از عذاب [الهى‌] بركنارند، و برايشان عذابى دردناك است'),
(482, 3, 189, 'و فرمانروايى آسمان‌ها و زمين از آن خداست و خدا بر هر چيزى تواناست'),
(483, 3, 190, 'به راستى در آفرينش آسمان‌ها و زمين و آمد و شد شب و روز براى خردمندان نشانه‌هايى است'),
(484, 3, 191, 'آنان كه خدا را ايستاده و نشسته و خوابيده ياد مى‌كنند و در آفرينش آسمان‌ها و زمين مى‌انديشند كه پروردگارا! اين [جهان‌] را بيهوده نيافريده‌اى، پاكى براى توست، پس ما را از عذاب آتش نگاه دار'),
(485, 3, 192, 'پروردگارا! به يقين هر كه را تو به آتش وارد كنى خوارش كرده‌اى، و ستمگران را هيچ ياورى نخواهد بود'),
(486, 3, 193, 'پروردگارا! همانا ما صداى منادى [توحيد] را شنيديم كه به ايمان دعوت مى‌كرد [و مى‌گفت‌] به پروردگارتان ايمان بياوريد، پس ايمان آورديم. بار الها! پس گناهان ما را ببخش و بدى‌هايمان را پوشيده دار و ما را با نيكان بميران'),
(487, 3, 194, 'پروردگارا! و آنچه را بر [زبان‌] پيامبرانت به ما وعده كرده‌اى به ما ارزانى دار، و ما را در روز رستاخيز رسوا مگردان، مسلما تو خلف وعده نمى‌كنى'),
(488, 3, 195, 'پس پروردگارشان [دعاى ايشان را] اجابت كرد كه من عمل هيچ عاملى از شما را، اعم از مرد يا زن تباه نمى‌كنم. شما همگى از يكديگريد. بنابر اين كسانى كه مهاجرت كردند و از خانه‌هاى خود بيرون رانده شدند و در راه من آزار ديدند و جنگيدند و كشته شدند، بى‌ترديد گن'),
(489, 3, 196, 'گشت و گذار كافران در شهرها تو را نفريبد'),
(490, 3, 197, 'اين بهره‌ى ناچيزى است و سپس جايگاهشان جهنّم است كه بد جايگاهى است'),
(491, 3, 198, 'ولى آنان كه تقواى الهى پيش گرفتند برايشان باغ‌هايى است كه از پاى درختانش نهرها جارى است و جاودانه در آن مى‌مانند، كه [اين‌] پذيرايى از جانب خداست و آنچه نزد خداست براى نيكان بهتر است'),
(492, 3, 199, 'و البته از ميان اهل كتاب كسانى هستند كه خاشعانه در برابر خدا به آنچه بر شما نازل شده و آنچه بر خودشان نازل گرديده ايمان مى‌آورند و آيات خدا را به بهاى ناچيز نمى‌فروشند. اينانند كه پاداششان نزد پروردگارشان [برايشان محفوظ] است و مسلما خدا حسابرس سريع'),
(493, 3, 200, 'اى كسانى كه ايمان آورده‌ايد! صبورى كنيد و استقامت ورزيد و از مرزها مراقبت نماييد و از خدا پروا كنيد، باشد كه رستگار شويد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اى مردم! از پروردگارتان، كه شما را از يك تن آفريد و همسرش را از او آفريد و از آن دو، مردان و زنان بسيارى را منتشر ساخت پروا كنيد و از خدايى كه به [نام‌] او از يكديگر درخواست مى‌كنيد و در مورد ارحام از خدا پروا كنيد. همانا خداوند مراقب شماست'),
(495, 4, 2, 'و اموال يتيمان را به آنان بدهيد و مال مرغوب [آنها] را با مال نامرغوب [خود] عوض نكنيد و اموال آنها را با اموال خودتان مخوريد كه اين گناه بزرگى است'),
(496, 4, 3, 'و اگر بيم آن داريد كه در باره‌ى [ازدواج‌] با دختران يتيم انصاف نكنيد، از ديگر زنان هر چه خوش داريد، دو يا سه و يا چهار زن را به همسرى درآوريد. پس اگر بيم آن داريد كه عدالت را رعايت نكنيد، به يك همسر و يا به آنچه مالك آنيد (كنيز) بسنده كنيد كه اين راه'),
(497, 4, 4, 'و كابين بانوان را به رغبت تقديمشان كنيد، و اگر آنها با رضايت خاطر چيزى از آن را به شما بخشيدند آن را مباح و گوارا بخوريد'),
(498, 4, 5, 'و اموالى كه خداوند آن را وسيله قوام زندگى شما قرار داده است در اختيار سفيهان قرار ندهيد، و از آن به آنها خوراك و پوشاك بدهيد و با آنها نيكو سخن بگوييد'),
(499, 4, 6, 'و يتيمان را بيازماييد تا آن‌گاه كه به سنّ ازدواج رسيدند، پس اگر در آنها رشد يافتيد اموالشان را به آنها بدهيد، و آن را از بيم آن كه مبادا بزرگ شوند [و اموالشان را پس بگيرند] به اسراف و شتاب نخوريد. هر كه توانگر بود خويشتن‌دارى كند و هر كه تهيدست بود د'),
(500, 4, 7, 'مردان را از آنچه پدر و مادر و خويشان به جا مى‌گذارند سهمى است، و براى زنان نيز از آنچه پدر و مادر و خويشان به جا مى‌گذارند سهمى است، چه كم باشد چه زياد، [و] اين سهمى تعيين شده است'),
(501, 4, 8, 'و چون به هنگام تقسيم ارث، خويشان [غير وارث‌] و يتيمان و تنگدستان حضور يافتند، از آن برخوردارشان سازيد و با آنها به نيكى سخن گوييد'),
(502, 4, 9, 'و كسانى كه اگر بعد از خود فرزندانى ناتوان بر جاى گذارند بر [آينده‌ى‌] آنان بيم دارند، بايد [در باره‌ى يتيمان مردم نيز] بترسند، پس بايد از خدا پروا كنند و سنجيده سخن بگويند'),
(503, 4, 10, 'بى‌گمان، كسانى كه اموال يتيمان را به ستم مى‌خورند جز آتشى در شكم‌هاى خود فرو نمى‌برند، و به زودى در آتشى افروخته وارد خواهند شد'),
(504, 4, 11, 'خدا شما را در باره‌ى فرزندانتان سفارش مى‌كند كه سهم پسر [در ميراث‌] چون سهم دو دختر است پس اگر [وارثان،] دخترانى بيش از دو تن باشند، دو سوم ميراث از آنهاست و اگر يك دختر باشد نيمى از ميراث از آن اوست. و اگر ميت را فرزندى باشد، پدر و مادر او هر يك وار'),
(505, 4, 12, 'و نيمى از ميراث زنانتان از آن شماست اگر فرزندى نداشته باشند، و اگر فرزندى داشته باشند يك چهارم ميراث آنها پس از عمل به وصيت يا پرداخت بدهى كه داشته‌اند از آن شماست. و [همچنين‌] اگر شما فرزندى نداشته باشيد يك چهارم و اگر فرزندى داشته باشيد يك هشتم مير'),
(506, 4, 13, 'اينها مقررات الهى است، و هر كه از خدا و رسولش اطاعت كند، او را داخل بهشت‌هايى مى‌كند كه از پاى درختانش نهرها جارى است كه در آن جاودانه‌اند و اين همان كاميابى بزرگ است'),
(507, 4, 14, 'و هر كه خدا و رسول او را نافرمانى كند و از حدود او تجاوز نمايد، وى را به آتشى در آورد كه همواره در آن بماند و براى او عذاب خفت بارى خواهد بود'),
(508, 4, 15, 'و آن كسان از زنان شما كه مرتكب فحشا مى‌شوند، بر آنها از ميان خودتان چهار گواه بگيريد. پس اگر گواهى دادند، آنها را در خانه‌ها بازداشت كنيد تا مرگشان برسد يا خدا راهى براى آنها پديد آورد'),
(509, 4, 16, 'و از شما، آن زن و مردى را كه مرتكب فحشا مى‌شوند آزار [و مجازات‌] كنيد. پس اگر توبه كردند و درستى را در پيش گرفتند، از آنها دست برداريد كه بى‌شك خدا توبه‌پذير مهربان است'),
(510, 4, 17, 'جز اين نيست كه پذيرش توبه از جانب خدا فقط براى كسانى است كه از روى نادانى بدى مى‌كنند سپس بى‌درنگ توبه مى‌كنند اينانند كه خداوند توبه‌شان را مى‌پذيرد و خدا داناى حكيم است'),
(511, 4, 18, 'و توبه كسانى كه پيوسته گناه مى‌كنند تا چون مرگ يكيشان فرا رسد، گويد: اينك توبه كردم، پذيرفته نيست، و نه توبه كسانى كه در حال كفر مى‌ميرند. براى اينان عذابى دردناك فراهم ساخته‌ايم'),
(512, 4, 19, 'اى كسانى كه ايمان آورده‌ايد! براى شما حلال نيست كه از زنان به اكراه ارث ببريد، و بر آنها سخت مگيريد تا بخشى از آنچه را كه به ايشان داده‌ايد ببريد، مگر آن كه مرتكب فحشايى آشكار شوند. و با آنها خوشرفتارى كنيد، و اگر آنها را خوش نداريد [بدانيد] چه بسا چ'),
(513, 4, 20, 'و اگر خواستيد به جاى زنى زن ديگرى بگيريد، و به يكى از آنها مال فراوانى داده‌ايد، چيزى از او پس نگيريد. آيا آن را به بهتان و با گناه آشكار پس مى‌گيريد؟ [كه با اتهام زدن از مهرشان دست بردارند]'),
(514, 4, 21, 'و چگونه آن را باز مى‌ستانيد با اين كه از يكديگر كام گرفته‌ايد، و آنان [با عقد ازدواج‌] از شما پيمان محكمى گرفته‌اند'),
(515, 4, 22, 'و زنانى را كه پدرانتان به زنى گرفته‌اند به همسرى نگيريد، مگر آنچه پيش از اين [در جاهليت‌] گذشت. بى‌ترديد اين كارى زشت و منفور و راه و رسم بدى است'),
(516, 4, 23, 'و بر شما حرام است ازدواج با مادران و دختران و خواهران و عمه‌ها و خاله‌هايتان و دختران برادر و دختران خواهر و دايگانى كه شما را شير داده‌اند و خواهران شيرى‌تان و مادر زنانتان و دختران زنانتان كه در دامان شما پرورش يافته‌اند از آن زنانى كه با آنان همبس'),
(517, 4, 24, 'و زنان شوهردار [نيز بر شما حرام است‌] مگر زنانى كه مالك آنها شده‌ايد. [اين‌] حكم خدا بر شماست. و آنچه غير اين باشد براى شما حلال است كه با صرف اموال خويش طلب كنيد [ولى‌] پاكدامنانه، نه زشتكارانه. و زنانى را كه متعه كرده‌ايد، مهرشان را به عنوان فريضه'),
(518, 4, 25, 'و هر كه از شما توان مالى ندارد كه با زنان آزاد مؤمن ازدواج كند، از كنيزان با ايمان كه در اختيار داريد [ازدواج كند] و خدا به ايمان شما داناتر است، همه از يكديگريد. پس آنها را با اجازه‌ى كسانشان به همسرى خود در آوريد و مهرشان را به شيوه‌اى نيكو بدهيد ب'),
(519, 4, 26, 'خدا مى‌خواهد [حكم خويش را] براى شما بيان كند و راه و رسم پيشينيان را به شما ارائه دهد و بر شما ببخشايد، و خدا داناى حكيم است'),
(520, 4, 27, 'و خدا مى‌خواهد بر شما ببخشايد، و كسانى كه پيرو شهواتند مى‌خواهند شما دستخوش انحرافى بزرگ شويد'),
(521, 4, 28, 'خدا مى‌خواهد بر شما آسان گيرد، و آدمى ناتوان آفريده شده است'),
(522, 4, 29, 'اى كسانى كه ايمان آورده‌ايد! اموال خود را ميان خودتان به ناروا مخوريد، مگر آن كه داد و ستدى با رضايت يكديگر باشد. و يكديگر را مكشيد، همانا خدا با شما مهربان است'),
(523, 4, 30, 'و هر كه به تجاوز و ستم چنين كند، زودا كه او را به آتش دوزخ در آوريم، و اين بر خدا آسان است'),
(524, 4, 31, 'اگر از گناهان بزرگى كه از آن نهى شده‌ايد دورى كنيد، بدى‌هاى [كوچك‌] شما را مى‌پوشانيم و شما را به جايگاهى ارجمند وارد مى‌كنيم'),
(525, 4, 32, 'و چيزى را كه خدا بدان برخى از شما را بر برخى ديگر برترى داده آرزو نكنيد. مردان را از آنچه كسب كرده‌اند بهره‌اى است و زنان را از آنچه به دست آورده‌اند بهره‌اى است. و [هر چه خواهيد] از فضل خدا بخواهيد كه بى‌گمان خدا به هر چيزى آگاه است'),
(526, 4, 33, 'و براى هر كسى از آنچه پدر و مادر و نزديكان به جا گذاشته‌اند وارثانى قرار داده‌ايم، و نيز كسانى كه با آنان پيمان بسته‌ايد بهره‌شان را بدهيد، بى‌شك خدا بر هر چيزى گواه است'),
(527, 4, 34, 'مردان سرپرست زنانند به سبب برترى‌هايى كه خدا به برخى از آنان در برابر برخى ديگر داده است و به سبب آن كه از اموالشان [براى آنها] خرج مى‌كنند. [در اين رابطه‌] پس زنان صالح فرمانبردارند و در غياب [همسر] اسرار او و حدود الهى را حفظ مى‌كنند. و [اما] آن دس'),
(528, 4, 35, 'و اگر از ناسازگارى ميان زن و مرد بيم داريد، داورى از كسان مرد و داورى از كسان زن تعيين كنيد اگر آنها سر سازگارى داشته باشند خدا ميانشان آشتى خواهد داد. بى‌شك خداوند دانا و آگاه است'),
(529, 4, 36, 'و خدا را بندگى كنيد و چيزى را با او شريك نگردانيد و به پدر و مادر احسان كنيد، و به خويشان، يتيمان، درماندگان، همسايه نزديك، همسايه دور، دوست همنشين [همكار و غيره‌]، در راه مانده و بردگان خود نيكى كنيد. همانا خداوند كسى را كه خودبين و فخر فروش باشد دو'),
(530, 4, 37, 'همان كسانى كه بخل مى‌ورزند و مردم را به بخل وا مى‌دارند، و آنچه را خدا از فضل خويش بدانها ارزانى داشته پنهان مى‌دارند. و براى كافران عذابى خفت بار آماده كرده‌ايم'),
(531, 4, 38, 'و كسانى كه اموال خود را براى نشان دادن به مردم انفاق مى‌كنند و به خدا و قيامت اعتقاد ندارند [همنشين شيطانند]. و كسى كه شيطان همدم وى باشد بد همدمى است'),
(532, 4, 39, 'و اگر به خدا و روز واپسين ايمان مى‌آوردند و از آنچه خدا روزيشان كرده انفاق مى‌كردند چه زيانى برايشان داشت؟ و خداوند به كارشان آگاه است'),
(533, 4, 40, 'خدا به اندازه‌ى سنگينى ذره‌اى ستم نكند، و اگر كار نيكى باشد دو چندانش كند و از نزد خود پاداش بزرگى دهد'),
(534, 4, 41, 'پس چگونه است آن‌گاه كه از هر امّتى گواهى بياوريم و تو را بر اينان گواه آوريم'),
(535, 4, 42, 'آن روز كسانى كه كافر شدند و پيامبر را نافرمانى كردند آرزو كنند كه كاش با خاك يكسان مى‌شدند و هيچ سخنى را نمى‌توانند از خدا پوشيده دارند'),
(536, 4, 43, 'اى كسانى كه ايمان آورده‌ايد! در حال مستى به نماز نزديك نشويد تا بدانيد چه مى‌گوييد، و نه در حال جنابت [به نماز و مسجد نزديك نشويد] مگر راهگذر باشيد، تا زمانى كه غسل كنيد. و اگر بيمار يا مسافر بوديد يا كسى از شما از قضاى حاجت آمد و يا با زنان تماس داش'),
(537, 4, 44, 'آيا كسانى را كه از كتاب بهره‌اى داده شده‌اند نديدى كه خريدار گمراهى‌اند و مى‌خواهند شما نيز راه گم كنيد'),
(538, 4, 45, 'و خدا به دشمنان شما آگاه‌تر است، و خدا براى سرپرستى كافى و براى ياورى بس است'),
(539, 4, 46, 'از يهود كسانى هستند كه كلمات را از جاهاى خود تغيير مى‌دهند و از راه زبان بازى و براى طعنه زدن به دين مى‌گويند: شنيديم و نافرمانى كرديم، و بشنو كه هرگز نشنوى، و [با تمسخر مى‌گويند:] راعنا، حال آن كه اگر مى‌گفتند: شنيديم و فرمان برديم و سخن ما بشنو و ب'),
(540, 4, 47, 'اى كسانى كه كتاب داده شده‌ايد! به آنچه نازل كرديم كه تصديق كننده‌ى چيزى است كه با شماست، ايمان بياوريد، پيش از آن كه چهره‌هايى را محو كنيم و به پشت سرشان بگردانيم يا لعنتشان كنيم چنان كه اهل شنبه را لعنت كرديم، و امر خدا انجام پذير است'),
(541, 4, 48, 'مسلما خداوند اين را كه براى او شريك قائل شوند نمى‌بخشد و آنچه را كه فروتر از آن باشد براى هر كه بخواهد مى‌بخشد، و هر كه براى خدا شريك بگيرد، بدون شك گناهى بزرگ ساخته است'),
(542, 4, 49, 'آيا نمى‌بينى كسانى را كه خود را به پاكى مى‌ستايند؟ در صورتى كه خداست كه هر كس را بخواهد به پاكى مى‌ستايد و ذرّه‌اى به آنها ستم نمى‌رود'),
(543, 4, 50, 'بنگر چگونه بر خدا دروغ مى‌بندند، و همين به تنهايى گناهى آشكار است'),
(544, 4, 51, 'آيا كسانى را كه از كتاب بهره‌اى يافته‌اند نديدى كه به هر بتى و طغيانگرى مى‌گروند و در باره‌ى كافران مى‌گويند: اينان از مؤمنان رهيافته‌ترند'),
(545, 4, 52, 'آنها هستند كه خدا لعنتشان كرده و هر كه را خدا لعنت كند هرگز براى او ياورى نخواهى يافت'),
(546, 4, 53, 'يا براى آنها بهره‌اى از حكومت است؟ كه در اين صورت به مردم سر سوزنى نمى‌دادند'),
(547, 4, 54, 'يا اين كه بر مردم نسبت به آنچه خدا از كرم خويش به ايشان داده رشك مى‌برند؟ همانا كه ما خاندان ابراهيم را كتاب و حكمت داديم و بديشان فرمانروايى بزرگى بخشيديم'),
(548, 4, 55, 'پس برخى از آنان به وى ايمان آوردند و برخى روى برتافتند و آنها را جهنم افروخته بس است'),
(549, 4, 56, 'كسانى كه به آيات ما كافر شدند، به زودى آنها را به آتشى درآوريم كه هر بار پوستشان پخته شود، پوستى ديگر بر جايش نهيم تا عذاب را [به طور كامل‌] بچشند. بى‌شك خداوند شكست‌ناپذير حكيم است'),
(550, 4, 57, 'و كسانى را كه ايمان آوردند و كارهاى شايسته كردند به بهشت‌هايى وارد سازيم كه از پاى درختانش نهرها جارى است كه براى هميشه در آن بمانند، و در آن جا برايشان همسرانى پاكيزه است و آنها را به سايه‌اى پايدار در آوريم'),
(551, 4, 58, 'همانا خدا به شما فرمان مى‌دهد كه سپرده‌ها را به صاحبانش پس دهيد و چون ميان مردم حكم كرديد به عدالت حكم كنيد، به راستى چه نيك است اين چيزها كه خدا شما را بدان پند مى‌دهد. همانا خدا شنوا و بيناست'),
(552, 4, 59, 'اى كسانى كه ايمان آورده‌ايد! خدا را اطاعت كنيد و از پيامبر و كاردارانى كه از شما هستند فرمان بريد. پس هرگاه در چيزى اختلاف كرديد آن را به خدا و پيامبر ارجاع دهيد اگر به خدا و روز واپسين ايمان داريد، كه اين بهتر و نيكو فرجام‌تر است'),
(553, 4, 60, 'آيا نديدى كسانى را كه مى‌پندارند به آنچه به سوى تو نازل شده و آنچه پيش از تو نازل گشته ايمان دارند؟ [ولى‌] مى‌خواهند داورى را پيش طاغوت برند، حال آن كه دستور يافته‌اند كه به آن كافر شوند، ولى شيطان مى‌خواهد آنها را به گمراهى دورى در اندازد'),
(554, 4, 61, 'و چون به آنها گفته شود: به سوى آنچه خدا فرستاده و به سوى پيامبر بياييد، منافقان را مى‌بينى كه به كلى از تو روى مى‌گردانند'),
(555, 4, 62, 'پس چرا وقتى به سبب كارهايى كه به دست خود كرده‌اند مصيبتى به آنها رسيد، نزد تو مى‌آيند [و] به خدا سوگند مى‌خورند كه ما [از داورى طاغوت‌] جز نيكى و سازش دادن قصدى نداشتيم'),
(556, 4, 63, 'آنها كسانى هستند كه خدا از آنچه در دل‌هاى آنهاست آگاه است. پس، از آنان روى گردان و پندشان ده و با آنان سخنى رسا و مؤثر در جانشان بگو'),
(557, 4, 64, 'و هيچ پيامبرى را نفرستاديم مگر آن كه به اذن خدا فرمانش برند. و اگر آنها هنگامى كه به خود ستم كردند، پيش تو مى‌آمدند و از خدا آمرزش مى‌خواستند و پيامبر [نيز] براى آنان طلب آمرزش مى‌كرد، قطعا خدا را توبه‌پذير و مهربان مى‌يافتند'),
(558, 4, 65, 'پس نه، به پروردگارت سوگند، ايمان نمى‌آورند مگر اين كه در اختلافى كه دارند تو را داور كنند و آن‌گاه در دلشان از حكمى كه كرده‌اى ملالى نيابند و بى‌چون و چرا تسليم شوند'),
(559, 4, 66, 'و اگر بر آنان مقرر مى‌كرديم كه تن به كشتن دهيد يا از خانه‌هاى خود بيرون شويد، جز اندكى از آنان عمل نمى‌كردند و اگر پندى را كه به آنان داده مى‌شود به كار مى‌بستند، قطعا برايشان بهتر و در ثبات قدم مؤثرتر بود'),
(560, 4, 67, 'و در آن صورت [ما هم‌] از پيش خود اجر بزرگى به آنها مى‌داديم'),
(561, 4, 68, 'و حتما به راهى راست هدايتشان مى‌كرديم'),
(562, 4, 69, 'و هر كه اطاعت خدا و رسول كند، آنها با كسانى خواهند بود كه خدا موهبتشان داده [كه عبارتند] از: پيامبران و راستى پيشگان و شاهدان و صالحان، و آنان نيكو همدمانى هستند'),
(563, 4, 70, 'اين تفضّل از جانب خداست و آگاهى خدا كفايت مى‌كند'),
(564, 4, 71, 'اى كسانى كه ايمان آورده‌ايد! سلاح خويش برگيريد و گروه گروه يا همه با هم [به سوى جهاد] رهسپار شويد'),
(565, 4, 72, 'و البته از شما كسى است كه سستى مى‌كند و چون حادثه‌اى به شما رسد گويد: به راستى خدا به من رحم كرد كه با آنها حاضر نبودم'),
(566, 4, 73, 'و اگر شما را خير [و پيروزى‌] از خدا برسد حتما گويد: كاش من با آنها مى‌بودم و به نواى بزرگى مى‌رسيدم! گويى كه ميان شما و او هيچ دوستى و آشنايى نبوده است'),
(567, 4, 74, 'پس كسانى كه زندگى دنيا را به آخرت مى‌فروشند، بايد در راه خدا جنگ كنند و هر كه در راه خدا بجنگد و كشته يا پيروز شود، به زودى به او پاداش بزرگى خواهيم داد'),
(568, 4, 75, 'و چرا در راه خدا و [نجات‌] مردان و زنان و كودكانى كه زبون شمرده شده‌اند نمى‌جنگيد؟ همانان كه مى‌گويند: پروردگارا! ما را از اين شهرى كه اهلش ستمكارند بيرون ببر و از جانب خود براى ما سرپرستى بگمار و از جانب خود براى ما ياورى قرار ده'),
(569, 4, 76, 'آنها كه ايمان آوردند در راه خدا پيكار مى‌كنند و آنها كه كافر شدند در راه طاغوت مى‌جنگند پس با ياران شيطان بجنگيد كه قطعا نيرنگ شيطان سست و ضعيف است'),
(570, 4, 77, 'آيا نديدى كسانى را كه به آنان گفته شد: [فعلا] دست از جنگ بداريد و نماز برپا داريد و زكات بدهيد، ولى وقتى كه كارزار بر آنها مقرر شد، به ناگاه گروهى از آنها از مردم (مشرك) مى‌ترسيدند مانند ترسيدن از خدا يا بالاتر از آن، و گفتند: پروردگارا! چرا بر ما جن'),
(571, 4, 78, 'هر كجا باشيد مرگ شما را در خواهد يافت، هر چند در برج‌هاى مستحكم باشيد. و اگر خيرى به آنها رسد گويند: اين از جانب خداست، و اگر شكست و بدى به آنها رسد گويند: اين از جانب توست. بگو: همه از جانب خداوند است. [آخر] اين قوم را چه شده است كه نمى‌خواهند سخنى'),
(572, 4, 79, 'هر خيرى به تو رسد از جانب خداست و هر بدى به تو رسد از خود توست، و تو را به عنوان پيامبر براى مردم فرستاديم، و اين بس كه خداوند گواه است'),
(573, 4, 80, 'هر كه پيامبر را اطاعت كند در حقيقت خدا را اطاعت كرده، و هر كه پشت كند [بدان كه‌] ما تو را بر آنان نگهبان نفرستاده‌ايم'),
(574, 4, 81, 'و [در حضور تو] مى‌گويند: فرمانبرداريم! ولى چون از نزد تو بيرون روند، جمعى از آنها شبانه، غير از آنچه تو مى‌گويى در سر مى‌پرورند، و خداوند آنچه را شبانه در سر مى‌پرورند مى‌نويسد. پس، از آنها روى بگردان و بر خدا توكل كن كه پشتيبانى خدا كافى است'),
(575, 4, 82, 'آيا در قرآن تدبر نمى‌كنند؟ اگر از جانب غير خدا بود قطعا در آن اختلاف فراوان مى‌يافتند'),
(576, 4, 83, 'و چون به آنان خبرى از امن يا ناامنى برسد آن را پخش مى‌كنند، و حال آن كه اگر آن را به پيامبر و اولياى امر خود ارجاع مى‌دادند قطعا كسانى از آنان كه مى‌توانند آن را استنباط كنند [صدق و كذب‌] آن را مى‌فهميدند، و اگر فضل و رحمت خدا بر شما نبود مسلما جز شم'),
(577, 4, 84, 'پس در راه خدا پيكار كن كه جز بر نفس خويش مكلف نيستى، و مؤمنان را [به پيكار] برانگيز، شايد خدا گزند آنان را كه كافر شدند از شما باز دارد، كه قدرت خدا بيشتر و كيفرش سخت‌تر است'),
(578, 4, 85, 'هر كه شفاعتى نيكو كند، او را از آن بهره‌اى باشد و كسى را كه شفاعتى بد كند وى را از آن سهمى باشد و مسلما خداوند بر هر چيزى مراقب و تواناست'),
(579, 4, 86, 'و زمانى كه به شما درود گفته شد، شما به صورتى بهتر از آن يا همانند آن را پاسخ دهيد همانا خداوند حسابرس همه چيز است'),
(580, 4, 87, 'خداوند جز او معبودى نيست. قطعا شما را به روز رستاخيز كه شكى در [وقوع‌] آن نيست گرد خواهد آورد، و چه كسى در سخن راستگوتر از خداست'),
(581, 4, 88, 'چرا در مورد منافقان دچار دو دستگى شده‌ايد؟ حال آن كه خدا آنها را به سبب آنچه كردند نگونسار كرده است. آيا مى‌خواهيد كسى را كه خدا گمراه كرده به راه آوريد؟ و هر كه را خدا گمراه كند هرگز راهى برايش نخواهى يافت'),
(582, 4, 89, 'آنها دوست دارند شما هم مانند آنها كافر شويد تا با آنها يكسان گرديد. پس، از آنها دوست مگيريد تا اين كه در راه خدا هجرت كنند. پس اگر رو برتافتند، هر جا آنها را يافتيد بگيريد و بكشيد و از آنها دوست و ياور نگيريد'),
(583, 4, 90, 'مگر آنها كه به گروهى پيوندند كه ميان شما و آنها پيمانى هست، يا در حالى نزد شما بيايند كه دل‌هايشان از پيكار با شما يا با قوم خود به تنگ آمده باشد. و اگر خدا مى‌خواست قطعا آنها را بر شما چيره مى‌ساخت و با شما مى‌جنگيدند. پس اگر آنها از شما كناره گرفتن'),
(584, 4, 91, 'گروهى ديگر را خواهيد يافت كه مى‌خواهند از ناحيه شما و قوم خود در امان باشند، [ولى‌] هرگاه به فتنه دعوت شوند با سر در آن فرو مى‌روند. پس اگر [از آزار] شما كنار نكشيدند و صلح نكردند و از شما دست برنداشتند آنها را بگيريد و هر كجا يافتيدشان بكشيد. اينانن'),
(585, 4, 92, 'و بر هيچ مؤمنى نسزد كه مؤمنى را بكشد مگر به خطا، و هر كه مؤمنى را به خطا بكشد بايد يك بنده‌ى مؤمن را آزاد كند و به كسان او خونبها بپردازد، مگر اين كه آنها گذشت كنند. و اگر [مقتول‌] از قومى باشد كه در حال جنگ با شماست و خود وى مؤمن است، آزاد كردن بنده'),
(586, 4, 93, 'و هر كه مؤمنى را به عمد بكشد، سزايش جاودانه دوزخ است و خدا بر او خشم گيرد و لعنتش كند و براى او عذابى بزرگ مهيا كرده است'),
(587, 4, 94, 'اى كسانى كه ايمان آورده‌ايد! هنگامى كه در راه خدا گام بر مى‌داريد پس خوب وارسى كنيد و كسى را كه به شما اظهار اسلام مى‌كند نگوييد تو مؤمن نيستى، كه بهره‌ى ناپايدار زندگى دنيا را بجوييد چرا كه غنيمت‌هاى فراوان نزد خداست. پيش‌تر خودتان نيز همين گونه بود'),
(588, 4, 95, 'مؤمنان وانشسته [از جهاد] كه زيانمند نيستند [و سلامت كافى دارند]، با آن مجاهدانى كه با جان و مال خود در راه خدا جهاد مى‌كنند يكسان نيستند. خدا جهادگران با مال و جان خويش را مرتبتى بر نشستگان برترى داده است و [البته‌] خدا همه را وعده‌ى نيكو داده [اما]'),
(589, 4, 96, 'منزلت‌ها و آمرزش و رحمتى از جانب خدا [براى آنهاست‌] و خداوند آمرزنده‌ى مهربان است'),
(590, 4, 97, 'كسانى كه فرشتگان جان آنها را- كه ستمگر خويشند- مى‌ستانند، به آنها گويند: [در امر دين‌] بر چه بوديد؟ گويند: ما در زمين مستضعفان بوديم. [فرشتگان‌] گويند: مگر زمين خدا وسيع نبود كه هجرت كنيد؟ اينانند كه جايگاهشان دوزخ است و بد سر انجامى است'),
(591, 4, 98, 'مگر مردان و زنان و كودكان مستضعفى كه نمى‌توانند چاره‌اى بينديشند و نه راه به جايى برند'),
(592, 4, 99, 'پس اينها را اميد است كه خدا ببخشايد و خداوند خطابخش آمرزنده است'),
(593, 4, 100, 'و هر كه در راه خدا هجرت كند، در زمين سرپناه بسيار و گشايشى [در كار] مى‌يابد و هر كه از خانه‌اش به منظور مهاجرت به سوى خدا و رسول او خارج شود و آن‌گاه مرگ او را دريابد، قطعا پاداش او بر عهده‌ى خداست و خداوند آمرزنده‌ى مهربان است'),
(594, 4, 101, 'و چون سفر كرديد، اگر بيم داشتيد كه كفّار به شما آسيب رسانند، مانعى نيست كه نماز را كوتاه كنيد. همانا كافران همواره دشمن آشكار شمايند'),
(595, 4, 102, 'و چون [در هنگام خطر] در ميان ايشان بودى و برايشان نماز بر پا كردى، بايد يك گروه از آنان با تو [ركعت اول را] به نماز ايستند و سلاح‌هاى خود را برگيرند، و چون به سجده رفتند [و ركعت دوم را فرادى تمام كردند] بايد پشت سر شما قرار گيرند و گروه ديگرى كه نما'),
(596, 4, 103, 'و چون نماز را به پايان برديد، خدا را [در همه حال‌] ايستاده و نشسته و خوابيده ياد كنيد، و همين كه ايمن شديد، نماز را [تمام‌] بخوانيد، چرا كه نماز بر مؤمنان فريضه‌اى زماندار است'),
(597, 4, 104, 'و در تعقيب دشمن سستى نكنيد. اگر شما [از زخم‌هاى جنگ‌] درد مى‌كشيد، بى‌گمان آنها نيز مثل شما درد مى‌كشند [با اين تفاوت كه‌] شما اميدى به خدا داريد كه آنها ندارند، و خداوند دانا و حكيم است'),
(598, 4, 105, 'همانا ما اين كتاب را به حق بر تو نازل كرديم تا ميان مردم طبق آنچه خدا به تو نشان داده است حكم كنى، و زنهار كه مدافع خيانتكاران باشى'),
(599, 4, 106, 'و از خدا آمرزش بخواه كه خداوند آمرزنده‌ى مهربان است'),
(600, 4, 107, 'و در باره‌ى كسانى كه به خود خيانت مى‌كنند بحث [و دفاع‌] نكن، زيرا خدا خيانتگر گناه پيشه را دوست ندارد'),
(601, 4, 108, '[خيانت خود را] از مردم پنهان مى‌دارند و از خدا پنهان نمى‌كنند [و شرم نمى‌دارند] در صورتى كه خدا در آن لحظه كه شبانه به سخنان خلاف رضاى او مى‌پردازند با آنهاست، و خداوند بدانچه مى‌كنند محيط است'),
(602, 4, 109, 'هان! اينك شما در زندگى دنيا از ايشان دفاع كرديد، پس چه كسى روز رستاخيز [در دفاع‌] از آنان با خداوند مجادله خواهد كرد يا چه كسى حامى و مدافع آنها خواهد بود'),
(603, 4, 110, 'و هر كه كار بدى كند يا به خود ستم كند، سپس از خداوند آمرزش بخواهد خدا را آمرزنده و مهربان خواهد يافت'),
(604, 4, 111, 'و هر كه گناهى مرتكب شود، تنها به زيان خود كار مى‌كند و خداوند آگاه و حكيم است'),
(605, 4, 112, 'و هر كه خطا يا گناهى كند، سپس آن را به بى‌گناهى نسبت دهد، زير بار بهتان و گناه آشكارى رفته است'),
(606, 4, 113, 'و اگر فضل و مهر خدا بر تو نبود، گروهى از آنها قصد داشتند تو را [در داورى‌] از راه به در كنند، ولى آنها جز خود را گمراه نكنند و هيچ زيانى به تو نرسانند، و خدا كتاب و حكمت بر تو نازل كرد و آنچه را نمى‌دانستى به تو آموخت، و تفضل خداوند بر تو بزرگ است'),
(607, 4, 114, 'در بسيارى از رازگويى [هاى‌] آنها خيرى نيست، مگر كسى كه به صدقه يا كار نيك يا اصلاح ميان مردم امر كند. و هر كه براى كسب رضاى خدا چنين كند، او را پاداش بزرگى خواهيم داد'),
(608, 4, 115, 'و هر كه پس از روشن شدن راه هدايت، با پيامبر ستيزه كند و راهى غير از راه مؤمنان در پيش گيرد، وى را به همان سو كه رو كرده برگردانيم و به دوزخش كشانيم كه بد سر انجامى است'),
(609, 4, 116, 'قطعا خدا اين گناه را كه براى او همتا گرفته شود نمى‌بخشد، ولى پايين‌تر از آن را براى هر كه خواهد مى‌آمرزد. و هر كه براى خدا شريك بگيرد، قطعا دچار گمراهى دورى شده است'),
(610, 4, 117, '[مشركان‌] به جاى او جز [جمادات‌] مؤنث (لات و عزّى) را نمى‌خوانند و جز شيطان سركش را نمى‌خوانند [و بندگى نمى‌كنند]'),
(611, 4, 118, 'خدا او را لعنت كرد و او گفت: حتما از ميان بندگانت سهم معينى خواهم گرفت'),
(612, 4, 119, 'و به يقين گمراهشان مى‌كنم و به دام آرزوها مى‌اندازم و وادارشان مى‌كنم تا گوش چهارپايان را [به عنوان تحريم‌] بشكافند، و فرمان مى‌دهم تا خلقت خدا را [به عنوان بدعت‌] تغيير دهند. و هر كه شيطان را به جاى خدا سرپرست خود گيرد، قطعا دستخوش زيان آشكارى شده'),
(613, 4, 120, '[آرى‌] شيطان وعده‌شان مى‌دهد و آنها را آرزومند مى‌كند، لكن شيطان به آنها جز فريب وعده نمى‌دهد'),
(614, 4, 121, 'آنها جايگاهشان جهنم است و هيچ راه گريزى از آن نيابند'),
(615, 4, 122, 'و كسانى كه ايمان آورده و كارهاى شايسته كردند، آنها را به بهشت‌هايى كه از پاى درختانش نهرها جارى است، در مى‌آوريم كه هميشه در آن باشند. وعده‌ى خدا راست است و چه كسى از خدا در سخن راستگوتر است'),
(616, 4, 123, '[اجر و كيفر الهى‌] به دلخواه شما و يا اهل كتاب نيست هر كه بدى كند بدان كيفر شود، و براى خود غير از خدا يار و ياورى نمى‌يابد'),
(617, 4, 124, 'و هر كه از مرد و زن كارهاى شايسته كند در حالى كه مؤمن باشد آنها داخل بهشت مى‌شوند و ذره‌اى به آنها ستم نمى‌رود'),
(618, 4, 125, 'چه كسى در ديندارى بهتر از آن كسى است كه خود را تسليم خدا كرده و نيكوكار بود و از آيين ابراهيم حق‌گرا پيروى نمود؟ ابراهيمى كه خداوند او را دوست خود گرفت'),
(619, 4, 126, 'و هر چه در آسمان‌ها و زمين است از آن خداست و خدا به همه چيز احاطه دارد'),
(620, 4, 127, 'و نظر تو را در باره‌ى زنان مى‌پرسند، بگو: خدا در باره‌ى آنها به شما فتوا مى‌دهد، و نيز آنچه در كتاب بر شما خوانده مى‌شود [پاسخى است‌] در مورد دختران يتيمى كه حقوق مقرر آنها را نمى‌پردازيد و ميل به ازدواج با آنها نداريد و نيز [در باره‌ى‌] كودكان ناتو'),
(621, 4, 128, 'و اگر زنى از بى‌ميلى يا رويگردانى شوهر خويش بيم دارد، رواست كه ميان خود به گونه‌اى، صلح كنند، كه صلح بهتر است، و حرص و آز در جان‌ها لانه كرده، و اگر نيكى كنيد و پرهيزكار باشيد، بدون شك خدا به آنچه مى‌كنيد آگاه است'),
(622, 4, 129, 'و شما هرگز نمى‌توانيد [به لحاظ محبت قلبى‌] ميان زنان عدالت كنيد هر چند هم بكوشيد پس [به يك طرف‌] يكسره تمايل نورزيد و آن ديگر را بلا تكليف رها كنيد. و اگر سازش و تقوا را پيشه كنيد، بى‌شك خدا آمرزنده و مهربان است'),
(623, 4, 130, 'و اگر جدا شوند، خداوند هر دو را از وسعت خويش بى‌نياز مى‌كند و او وسعت بخش حكيم است'),
(624, 4, 131, 'و آنچه در آسمان‌ها و زمين است از آن خداست، و ما به كسانى كه پيش از شما كتاب داده شدند و نيز به شما سفارش كرديم كه از خدا بترسيد. و اگر كافر شويد [چه باك‌]، آنچه در آسمان‌ها و زمين است از آن خداست و خداوند بى‌نياز و ستوده است'),
(625, 4, 132, 'و از آن خداست آنچه در آسمان‌ها و زمين است، و خداوند در كارسازى كافى است'),
(626, 4, 133, 'اى مردم! اگر خدا بخواهد، شما را از ميان مى‌برد و ديگران را مى‌آورد، كه خداوند بر اين كار تواناست'),
(627, 4, 134, 'هر كه پاداش دنيا بخواهد، [بداند كه‌] پاداش دنيا و آخرت نزد خداست، و خداوند شنوا و بيناست'),
(628, 4, 135, 'اى كسانى كه ايمان آورده‌ايد! برپا كنندگان عدل و داد باشيد و براى خدا گواهى دهيد، هر چند به زيان شما يا پدر و مادر يا نزديكان شما باشد. اگر [طرف گواهى‌] توانگر يا مستمند باشد، خدا به [رعايت‌] آنها سزاوارتر است. پس از هواى نفس پيروى نكنيد كه از حق عدو'),
(629, 4, 136, 'اى كسانى كه ايمان آورده‌ايد! به خدا و رسول او و كتابى كه بر پيامبرش نازل كرده و كتابى كه قبلا نازل نموده ايمان آوريد. و هر كه به خدا و فرشتگان و كتاب‌هاى آسمانى و فرستادگان او و به روز واپسين كافر شود، بى‌گمان به گمراهى دورى افتاده است'),
(630, 4, 137, 'همانا كسانى كه ايمان آوردند سپس كافر شدند و پس از آن ايمان آوردند و باز كافر شدند و آن‌گاه بر كفر خويش افزودند، قطعا خداوند آنها را نخواهد بخشيد و راهى به آنها نخواهد نمود'),
(631, 4, 138, '[و] به منافقان مژده ده كه عذابى دردناك براى آنهاست'),
(632, 4, 139, 'آنان كه كافران را به جاى مؤمنان به دوستى مى‌گيرند آيا عزّت را نزد آنان مى‌جويند؟ ولى به يقين هر چه عزت است از آن خداست'),
(633, 4, 140, 'البته خدا [اين حكم را] در اين كتاب بر شما نازل كرده كه چون بشنويد آيات الهى مورد انكار و ريشخند قرار مى‌گيرد با آنها منشينيد تا سخنى ديگر آغاز كنند، چرا كه در اين صورت شما نيز مثل آنها خواهيد شد، و مسلما خدا منافقان و كافران را همگى در دوزخ گرد خواه'),
(634, 4, 141, 'همانان كه در انتظار گرفتارى شمايند پس اگر شما را از خدا نصرتى بود، گويند: مگر ما با شما نبوديم؟ و اگر كافران را بهره‌اى بود، گويند: مگر ما مراقب شما نبوديم و از [گزند] مؤمنان محفوظتان نداشتيم؟ پس خداوند روز قيامت ميان شما داورى مى‌كند، و خدا هرگز بر'),
(635, 4, 142, 'همانا منافقان با خدا فريبكارى مى‌كنند، و حال آن كه خدا فريب دهنده‌ى آنهاست، و چون به نماز برخيزند با كاهلى برخيزند، با مردم رياكارى مى‌كنند و خدا را ياد نمى‌كنند مگر اندكى'),
(636, 4, 143, 'آنها ميان اين و آن سرگشته‌اند، نه با آن گروهند نه با اين گروه، و خدا هر كه را گمراه كند، هرگز براى او راهى نخواهى يافت'),
(637, 4, 144, 'اى كسانى كه ايمان آورده‌ايد! كافران را به جاى مؤمنان دوست مگيريد. آيا مى‌خواهيد در پيشگاه خدا بر ضدّ خود حجت روشنى فراهم آوريد'),
(638, 4, 145, 'بى‌ترديد منافقان در پست‌ترين طبقه از آتش‌اند و هرگز براى آنها ياورى نخواهى يافت'),
(639, 4, 146, 'مگر كسانى كه توبه كردند و راه صلاح را در پيش گرفتند و به خدا تمسك جستند و دين خود را براى خدا خالص كردند، پس آنها با مؤمنانند و خدا مؤمنان را اجرى بزرگ خواهد داد'),
(640, 4, 147, 'اگر شكرگزارى كنيد و ايمان آوريد، خدا به عذاب شما اقدام نمى‌كند كه خداوند حق‌شناس آگاه است'),
(641, 4, 148, 'خداوند علنى كردن سخن در باره‌ى بدى [ديگران‌] را دوست ندارد مگر كسى كه بر او ستم رفته باشد، و خداوند شنواى داناست'),
(642, 4, 149, 'اگر نيكى را عيان كنيد يا نهان داريد، يا از بدى [ديگران‌] در گذريد، البته خداوند [نيز] بخشاينده‌ى تواناست'),
(643, 4, 150, 'همانا كسانى كه خدا و پيامبرانش را انكار مى‌كنند، و مى‌خواهند ميان خدا و رسولانش جدايى قائل شوند، و مى‌گويند: ما به برخى ايمان داريم و به برخى نداريم، و مى‌خواهند بين اين دو، راهى [جدا] بگيرند'),
(644, 4, 151, 'آنان در حقيقت كافرند، و ما براى كافران عذابى خفّت‌بار آماده كرده‌ايم'),
(645, 4, 152, 'و كسانى كه به خدا و فرستادگان او ايمان آوردند و ميان احدى از آنها فرق نگذاشتند، خدا پاداش آنها را خواهد داد كه خداوند آمرزنده‌ى مهربان است'),
(646, 4, 153, 'اهل كتاب از تو مى‌خواهند نوشته‌اى از آسمان بر آنها نازل كنى، البته از موسى، بزرگتر از اين را خواستند و گفتند: خدا را آشكارا به ما بنماى! پس به سزاى ظلمشان صاعقه آنها را گرفت. آن‌گاه پس از آن همه معجزات روشنگر كه براى آنها آمد، گوساله پرستيدند، و ما'),
(647, 4, 154, 'و كوه طور را به اعتبار پيمانشان بالاى سرشان برافراشتيم و به ايشان گفتيم: فروتنانه از اين دروازه وارد شويد، و گفتيم: حرمت شنبه را نشكنيد [و صيد نكنيد] و از ايشان پيمانى محكم گرفتيم'),
(648, 4, 155, 'پس به سزاى پيمان شكنى‌شان، و انكار آيات خدا، و كشتن پيامبران به ناحق، و به خاطر گفتارشان كه: دل‌هاى ما در غلاف است [لعنتشان كرديم‌]، بلكه خدا به خاطر كفرشان بر دل‌هاى آنها مهر زد و جز اندكى ايمان نمى‌آورند'),
(649, 4, 156, 'و [نيز] به سزاى كفرشان و آن تهمت بزرگى كه به مريم زدند'),
(650, 4, 157, 'و ادعايشان كه: ما مسيح عيسى بن مريم پيامبر خدا را كشته‌ايم، در حالى كه نه او را كشتند و نه بر دار كردند، بلكه [حقيقت امر] بر آنها مشتبه شد و كسانى كه در باره‌ى [قتل‌] او اختلاف كردند، قطعا در مورد آن شك دارند و به آن آگاهى ندارند و تنها از حدس و گما'),
(651, 4, 158, 'بلكه خدا او را به سوى خود بالا برد، كه خداوند توانا و حكيم است'),
(652, 4, 159, 'و هيچ كس از اهل كتاب نيست مگر آن كه پيش از مرگش حتما به او [عيسى‌] ايمان مى‌آورد و روز قيامت [نيز] بر آنان گواه خواهد بود'),
(653, 4, 160, 'پس به خاطر ظلمى كه از يهود صادر شد، و به سبب بازداشتن‌شان بسيارى [از مردم‌] را از راه خدا، بخشى از چيزهاى پاكيزه را كه بر آنان حلال بود حرام كرديم'),
(654, 4, 161, 'و به خاطر ربا گرفتنشان كه از آن نهى شده بودند و خوردن مال مردم به ناروا و ما براى كافرانشان عذابى دردناك آماده كرده‌ايم'),
(655, 4, 162, 'ولى دانشوران آنها و مؤمنان، بدانچه بر تو نازل شده و آنچه پيش از تو نازل گرديده ايمان مى‌آورند، و [به ويژه‌] نمازگزاران، و زكات دهندگان و مؤمنان به خدا و روز بازپسين همه آنها را پاداشى بزرگ خواهيم داد'),
(656, 4, 163, 'همانا ما به تو وحى كرديم، چنان كه به نوح و پيامبران پس از او وحى نموديم، و به ابراهيم و اسماعيل و اسحاق و يعقوب و نوادگان [او] و عيسى و ايوب و يونس و هارون و سليمان [نيز] وحى فرستاديم، و داود را زبور داديم'),
(657, 4, 164, 'و پيامبرانى كه سرگذشت آنها را پيش‌تر با تو گفته‌ايم و پيامبرانى كه حكايتشان را با تو نگفته‌ايم و خدا با موسى به طرز خاصى سخن گفت'),
(658, 4, 165, 'پيامبرانى كه بشارتگر و هشدار دهنده بودند، تا مردم را پس از [فرستادن‌] پيامبران، در مقابل خدا دستاويزى نباشد [و حجت تمام شود] و خداوند شكست‌ناپذير حكيم است'),
(659, 4, 166, 'آرى، خدا به [حقانيت‌] آنچه به تو نازل كرده گواهى مى‌دهد كه آن را به علم خويش نازل كرده و فرشتگان [نيز] گواهى مى‌دهند، و گواهى خداوند كافى است'),
(660, 4, 167, 'بى‌ترديد، كسانى كه كافر شدند و [مردم‌] را از راه خدا بازداشتند، به گمراهى دورى افتاده‌اند'),
(661, 4, 168, 'همانا كسانى كه كافر شدند و ستم كردند، خدا هرگز آنها را نخواهد بخشيد و به هيچ راهى هدايتشان نخواهد كرد'),
(662, 4, 169, 'مگر به راه دوزخ كه جاودانه در آن خواهند ماند و اين بر خدا آسان است'),
(663, 4, 170, 'اى مردم! پيامبر [موعود]، حق را از سوى پروردگارتان براى شما آورده است، پس ايمان بياوريد كه براى شما بهتر است. و اگر كافر شويد [بدانيد] آنچه در آسمان‌ها و زمين است از آن خداست، و خدا داناى حكيم است'),
(664, 4, 171, 'اى اهل كتاب! در دين خود غلو نكنيد و در باره‌ى خدا جز حق نگوييد. مسيح، عيسى ابن مريم فقط فرستاده‌ى خدا و كلمه او بود كه آن را به مريم القا نمود و روحى از طرف او بود. پس به خدا و پيامبران او ايمان بياوريد و نگوييد: [خداوند] سه تا است. بس كنيد كه براى'),
(665, 4, 172, 'هرگز مسيح و فرشتگان مقرب از اين ابا ندارند كه بنده‌ى خدا باشند و هر كه از عبوديت و بندگى خدا روى برتابد و بزرگى فروشد، همه را [در قيامت‌] به حضور خود جمع خواهد كرد'),
(666, 4, 173, 'اما آنها كه ايمان آوردند و اعمال صالح انجام دادند، پاداششان را به تمامى خواهد داد و از كرم خويش براى آنان خواهد افزود، و اما آنها را كه ابا كردند و گردنكشى نمودند، مجازات دردناكى خواهد كرد و براى خود غير از خدا سرپرست و ياورى نخواهند يافت'),
(667, 4, 174, 'اى مردم! شما را از پروردگارتان برهانى آمده، و نورى روشنگر به سوى شما نازل كرده‌ايم'),
(668, 4, 175, 'اما آنها كه به خدا ايمان آوردند و به او تمسك جستند، همه را در جوار رحمت و فضل خود در خواهد آورد و آنان را به سوى خودش به راهى راست هدايت خواهد نمود'),
(669, 4, 176, 'از تو فتوا مى‌خواهند، بگو: خدا در باره‌ى كلاله [ارث خواهر و برادر] به شما پاسخ مى‌دهد: اگر مردى بميرد و فرزندى نداشته باشد و خواهرى دارد، نصف آنچه وانهاده از آن اوست، و او نيز از خواهر خويش اگر فرزندى نداشته باشد ارث مى‌برد. پس اگر [ورثه‌] دو خواهر');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اى كسانى كه ايمان آورده‌ايد! به قراردادهاى [خود] وفا كنيد. گوشت چهارپايان براى شما حلال شده است، مگر آنچه [حكمش‌] بر شما خوانده مى‌شود، در حالى كه نبايد شكار را در حال احرام جايز شماريد. خدا هر چه بخواهد [و مصلحت بداند] حكم مى‌كند'),
(671, 5, 2, 'اى كسانى كه ايمان آورده‌ايد! حرمت شعائر الهى، و ماه حرام و قربانى‌هاى بى‌نشان و نشاندار، و زائران بيت الحرام را كه از پروردگارشان جوياى وسعت معاش و خشنودى او هستند، نشكنيد. و چون از احرام بيرون آمديد [مى‌توانيد] شكار كنيد، و البته نبايد دشمنى جماعتى ك'),
(672, 5, 3, 'گوشت مردار، خون، گوشت خوك، آنچه به غير نام خدا ذبح شده، حيوان خفه شده، حيوان به كتك مرده، آنچه در اثر سقوط از بلندى مرده و آنچه با شاخ حيوان ديگرى مرده، نيم خورده‌ى درنده مگر آنچه را كه به موقع سر بريديد، آنچه براى بت‌ها ذبح شده و نيز تعيين قسمت سهم گ'),
(673, 5, 4, 'از تو مى‌پرسند: چه چيز براى آنها حلال شده است؟ بگو: پاكيزه‌ها و نيز صيد حيوانات شكارى [و سگ‌هاى تربيت يافته‌] كه از آنچه خدا به شما تعليم داده به آنها آموخته‌ايد، براى شما حلال شده است. پس از آنچه اين حيوانات براى شما گرفته و نگاه داشته‌اند بخوريد و ['),
(674, 5, 5, 'امروز چيزهاى پاكيزه براى شما حلال شد و طعام اهل كتاب براى شما حلال و طعام شما براى آنها حلال است، و [نيز ازدواج با] زنان پاكدامن با ايمان و زنان پاكدامن از كسانى كه قبل از شما به آنها كتاب داده شده بر شما حلال است، به شرط آن كه مهر آنها را بپردازيد، د'),
(675, 5, 6, 'اى كسانى كه ايمان آورده‌ايد! چون به نماز برخاستيد، صورت و دست‌هاى خود را تا آرنج بشوييد و سر و پاهاى خود را تا برآمدگى روى پا مسح كنيد و اگر جنب بوديد غسل كنيد و اگر بيمار يا مسافر بوديد، يا يكى از شما از قضاى حاجت آمد، يا با زنان نزديكى كرديد و آب ني'),
(676, 5, 7, 'و نعمت خدا را كه بر شما ارزانى داشته و [نيز] پيمانى را كه با شما بسته است به ياد آوريد كه گفتيد: شنيديم و فرمان برديم، و از خدا پروا كنيد كه خداوند به راز دل‌ها آگاه است'),
(677, 5, 8, 'اى كسانى كه ايمان آورده‌ايد! قيام كننده براى خدا و شاهدان به عدل و داد باشيد. و البته نبايد دشمنى عده‌اى شما را بر آن دارد كه عدالت نكنيد. دادگرى كنيد كه آن به تقوا نزديك‌تر است، و از خدا بترسيد كه خدا به آنچه انجام مى‌دهيد آگاه است'),
(678, 5, 9, 'خدا كسانى را كه ايمان آوردند و كارهاى شايسته كردند، و عده‌ى مغفرت و پاداشى بزرگ داده است'),
(679, 5, 10, 'و كسانى كه كافر شدند و آيات ما را تكذيب نمودند، آنها اهل دوزخند'),
(680, 5, 11, 'اى كسانى كه ايمان آورده‌ايد! نعمت خدا را بر خود ياد كنيد آن‌گاه كه قومى كوشيدند بر شما دست درازى كنند و خدا دستشان را از شما كوتاه كرد. و از خدا پروا داريد، و مؤمنان بايد فقط بر خدا توكل كنند'),
(681, 5, 12, 'و همانا خدا از بنى اسرائيل پيمان گرفت و از آنها دوازده سرپرست برانگيختيم، و خدا فرمود: همانا من با شما هستم، اگر نماز برپا داريد و زكات بدهيد و به فرستادگان من ايمان بياوريد و ياريشان كنيد و به خدا قرض الحسنه بدهيد، قطعا بدى‌هايتان را از شما مى‌پوشان'),
(682, 5, 13, 'پس به خاطر پيمان‌شكنى‌شان، آنها را از رحمت خويش دور ساختيم و دل‌هايشان را سخت گردانيديم كلمات را از جايگاه‌هاى خود تحريف مى‌كردند و بخشى از آنچه را به آنها گوشزد شده بود فراموش كردند، و تو همواره به خيانت [تازه‌اى‌] از ايشان آگاه مى‌شوى، مگر عده‌ى كم'),
(683, 5, 14, 'و از كسانى كه گفتند: ما نصرانى هستيم، از آنها نيز پيمان گرفتيم، ولى آنان بخش مهمى از آنچه را تذكر داده شده بودند، به فراموشى سپردند، پس ما تا روز قيامت ميانشان دشمنى و كينه افكنديم، و زودا كه خداوند آنها را از آنچه مى‌كردند آگاه كند'),
(684, 5, 15, 'اى اهل كتاب! همانا فرستاده‌ى ما به سوى شما آمده است كه بسيارى از حقايق كتاب آسمانى را كه نهان مى‌كرديد برايتان روشن سازد و [البته‌] از بسيارى مى‌گذرد. آرى، از جانب خدا نور و كتاب روشنگرى براى شما آمده است'),
(685, 5, 16, 'خدا هر كس را كه در پى جلب رضاى اوست، به وسيله آن [كتاب‌] به راه‌هاى امن و سلامت رهنمون مى‌شود، و به خواست خود، آنها را از تاريكى‌ها به نور مى‌برد و به راهى راست هدايتشان مى‌كند'),
(686, 5, 17, 'آنها كه گفتند: خدا همان مسيح، پسر مريم است، مسلما كافر شدند. بگو: اگر خدا اراده كند كه مسيح، پسر مريم و مادرش و هر كس را كه در زمين است، جملگى به هلاكت رساند، چه كسى مى‌تواند دفاع كند؟ فرمانروايى آسمان‌ها و زمين و آنچه ميان آنهاست خاص خداوند است و ا'),
(687, 5, 18, 'و يهود و نصارا گفتند: ما پسران خدا و دوستان [ويژه‌ى‌] او هستيم. بگو: پس چرا او شما را به كيفر گناهانتان عذاب مى‌كند؟ بلكه شما هم بشرى از آنهايى هستيد كه آفريده است. او هر كه را بخواهد مى‌بخشد و هر كه را بخواهد عذاب مى‌كند و حكومت آسمان‌ها و زمين و آن'),
(688, 5, 19, 'اى اهل كتاب! رسول ما پس از فاصله زمانى او با پيامبران به سوى شما آمد كه حق را براى شما بيان كند، تا نگوييد براى ما مژده‌رسان و هشدار دهنده‌اى نيامد پس [اينك‌] بشير و نذير به سوى شما آمد، و خدا بر هر چيزى تواناست'),
(689, 5, 20, 'و آن‌گاه كه موسى به قوم خود گفت: اى قوم من! نعمت خدا را بر خود ياد كنيد، آن‌گاه كه در ميان شما پيامبرانى قرار داد و شما را حاكم و صاحب اختيار خود كرد و به شما چيزهايى داد كه به هيچ كس از جهانيان نداده بود'),
(690, 5, 21, 'اى قوم من! به سرزمين مقدسى كه خدا براى شما مقرر داشته وارد شويد و به عقب باز نگرديد كه زيانكار مى‌گرديد'),
(691, 5, 22, 'گفتند: اى موسى! همانا در آن جا قومى زورمند و ستمگرند و تا آنها از آن جا بيرون نروند ما هرگز داخل آن نمى‌شويم اگر از آن جا بيرون روند ما وارد خواهيم شد'),
(692, 5, 23, 'دو نفر از مردانى كه از خدا مى‌ترسيدند و خدايشان به آنها نعمت [عقل و ايمان‌] داده بود، گفتند: از آن دروازه بر آنها وارد شويد، كه اگر از آن جا وارد شديد، قطعا پيروز خواهيد شد، و بر خدا توكل كنيد اگر ايمان داريد'),
(693, 5, 24, '[بنى اسرائيل‌] گفتند: اى موسى! تا وقتى كه آنها در آن [سرزمين‌] هستند ما هرگز پاى در آن ننهيم، پس تو و پروردگارت برويد و بجنگيد كه ما همين جا نشسته‌ايم'),
(694, 5, 25, '[موسى‌] گفت: پروردگارا! من جز اختيار خود و برادر خويش را ندارم پس ميان ما و اين قوم نافرمان جدايى افكن'),
(695, 5, 26, '[خدا] فرمود: اين سرزمين [مقدس‌] تا چهل سال بر آنها ممنوع شد. آنها در اين بيابان‌ها سرگردان خواهند ماند پس تو بر اين قوم نافرمان اندوه مخور'),
(696, 5, 27, 'و داستان دو فرزند آدم را به حق [به عنوان يك واقعه‌] بر آنها بخوان، آن‌گاه كه قربانى پيش بردند. پس، از يكى پذيرفته شد و از ديگرى پذيرفته نشد. [قابيل‌] گفت: حتما تو را خواهم كشت. [هابيل‌] گفت: خدا فقط از پرهيزكاران مى‌پذيرد'),
(697, 5, 28, 'اگر تو براى كشتن من دست دراز كنى، من هرگز به قتل تو دست نمى‌گشايم، همانا من از پروردگار عالميان مى‌ترسم'),
(698, 5, 29, 'من مى‌خواهم تو بار گناه من و گناه خودت را بر دوش‌كشى تا از دوزخيان گردى، و سزاى ستمكاران همين است'),
(699, 5, 30, 'پس نفس [سركش او كم‌كم‌] وى را به قتل برادرش ترغيب كرد و او را كشت و از زيانكاران گرديد'),
(700, 5, 31, 'سپس خداوند زاغى را برانگيخت كه زمين را [به جستجو] مى‌كاويد، تا به او نشان دهد كه چگونه جسد برادرش را پنهان كند. [قابيل‌] گفت: واى بر من! آيا عاجز بودم از اين كه مثل اين زاغ باشم تا جسد برادرم را پنهان كنم؟! آن‌گاه از پشيمانان گشت'),
(701, 5, 32, 'به همين جهت بر فرزندان اسرائيل مقرر داشتيم كه هر كسى ديگرى را جز به انتقام قتل يا فساد در روى زمين بكشد، مانند آن است كه همه مردم را كشته باشد، و هر كه شخصى را از مرگ نجات دهد گويى همه مردم را زنده كرده است. و البته فرستادگان ما حجت‌هاى روشن براى آنه'),
(702, 5, 33, 'سزاى كسانى كه با خدا و رسولش به جنگ بر مى‌خيزند و در زمين به فتنه و فساد مى‌كوشند جز اين نيست كه كشته شوند يا بردار زده شوند و يا دست و پايشان بر خلاف يكديگر بريده شود يا تبعيد شوند. اين، رسوايى آنان در دنياست و براى ايشان در آخرت عذابى بزرگ خواهد بو'),
(703, 5, 34, 'مگر كسانى كه پيش از آن كه آنان را دستگير كنيد توبه كنند، پس بدانيد كه خداوند بخشنده‌ى مهربان است'),
(704, 5, 35, 'اى كسانى كه ايمان آورده‌ايد! از خدا بترسيد و [براى تقرب‌] به سوى او وسيله بجوييد و در راه او جهاد كنيد، باشد كه رستگار شويد'),
(705, 5, 36, 'در حقيقت، كسانى كه كافر شدند اگر تمام آنچه روى زمين است و همانند آن مال آنها باشد و همه آن را براى نجات از كيفر روز قيامت بدهند، از آنها پذيرفته نخواهد شد، و عذاب دردناكى خواهند داشت'),
(706, 5, 37, '[همواره‌] مى‌خواهند از آتش بيرون بروند، ولى از آن خارج شدنى نيستند، و برايشان عذابى پايدار هست'),
(707, 5, 38, 'دست مرد و زن دزد را به سزاى كارى كه كرده‌اند، به عنوان يك مجازات الهى قطع كنيد، و خدا توانا و حكيم است'),
(708, 5, 39, 'پس هر كه بعد از ستم كردنش توبه آرد و اصلاح نمايد، خدا توبه او را مى‌پذيرد كه خدا آمرزنده‌ى مهربان است'),
(709, 5, 40, 'آيا نمى‌دانى كه فرمانروايى آسمان‌ها و زمين از آن خداست؟ هر كه را خواهد عذاب مى‌كند و هر كه را خواهد مى‌بخشد و خداوند بر هر چيزى تواناست'),
(710, 5, 41, 'اى پيامبر! كسانى كه در كفر شتاب مى‌كنند تو را غمگين نسازند، چه از آنان كه به زبان گفتند: ايمان آورديم ولى قلب آنها ايمان نياورده و چه از يهوديانى كه به سخنان كذب گوش مى‌سپارند [تا دستاويزى براى تكذيب تو بيابند]، آنها جاسوسان قومى ديگرند كه خودشان نز'),
(711, 5, 42, '[آنها] جاسوس و گوش سپارنده به دروغند و فراوان مال حرام مى‌خورند. پس اگر نزد تو آمدند، ميان آنها داورى كن، يا [اگر صلاح نبود] آنها را به حال خود واگذار. و اگر از آنها روى گردانى به تو هيچ زيانى نمى‌رسانند و چون داورى كردى، به انصاف ميانشان داورى كن، ب'),
(712, 5, 43, 'و چگونه تو را به داورى مى‌گيرند با آن كه تورات نزد آنهاست و در آن حكم خدا آمده است؟! [وانگهى‌] پس از آن [از داورى تو] روى مى‌گردانند. و آنها مؤمن نيستند'),
(713, 5, 44, 'ما تورات را نازل كرديم كه در آن نور و هدايت است. پيامبران كه مطيع فرمان خدا بودند با آن براى يهود حكم مى‌كردند، و [همچنين‌] علما و دانشمندان با همين كتاب كه به آنها سپرده شده بود و بر آن گواه بودند، داورى مى‌نمودند. پس، از مردم نترسيد و از من بترسيد'),
(714, 5, 45, 'و در تورات بر آنان مقرر كرديم كه جان در مقابل جان، چشم در برابر چشم، بينى در برابر بينى، گوش در برابر گوش، دندان در برابر دندان و زخم‌ها را قصاص بايد كرد. و هر كه از قصاص در گذرد، آن كفاره‌ى [گناهان‌] او خواهد بود. و هر كه مطابق آيات الهى داورى نكند'),
(715, 5, 46, 'و از پى پيامبران پيشين عيسى بن مريم را فرستاديم كه تورات پيش از خود را تصديق داشت، و انجيل را به او داديم كه در آن نور و هدايت بود و مصدق تورات پيش از خود، و هدايت و پندى براى پارسايان بود'),
(716, 5, 47, 'و بايد اهل انجيل مطابق آنچه خدا در آن نازل كرده حكم كنند و هر كه مطابق آيات الهى حكم نكند، آنها قطعا از اطاعت خدا بيرون رفته‌اند'),
(717, 5, 48, 'ما اين كتاب (قرآن) را به راستى و حق بر تو نازل كرديم كه مصدق و نگهبان كتاب‌هاى پيش از خود است. پس ميان آنها بر طبق آنچه خدا نازل كرده داورى كن، و به جاى حقى كه از جانب خدا آمده از هوس‌هاى آنها پيروى مكن. ما براى هر يك از شما شريعت و راه روشنى قرار دا'),
(718, 5, 49, 'و ميان آنها مطابق آنچه خدا نازل كرده حكم كن و از هوس‌هايشان پيروى مكن، و از ايشان برحذر باش كه مبادا تو را از برخى چيزها كه خدا بر تو نازل كرده است غافل سازند. پس اگر پشت كردند، بدان كه خدا مى‌خواهد سزاى پاره‌اى از گناهانشان را به آنها برساند، و به ر'),
(719, 5, 50, 'آيا آنها خواهان حكم جاهليت‌اند؟ و براى قومى كه يقين دارند، حكم چه كسى از حكم خدا بهتر است'),
(720, 5, 51, 'اى كسانى كه ايمان آورده‌ايد! يهود و نصارا را [تكيه گاه و] دوستان خود نگيريد آنها دوستان و هوادار يكديگرند و هر كه از شما آنها را به دوستى گيرد از آنهاست. همانا خداوند ظالمان را هدايت نمى‌كند'),
(721, 5, 52, 'و ليكن بيماردلان را مى‌بينى كه در [دوستى‌] آنها مى‌شتابند و مى‌گويند: مى‌ترسيم حادثه بدى براى ما پيش آيد [و به كمك آنها نيازمند شويم‌]. ولى شايد خدا پيروزى يا حادثه ديگرى از جانب خود [براى مسلمين‌] پيش آورد و اين دسته از آنچه در دل نهان داشتند پشيما'),
(722, 5, 53, 'و كسانى كه ايمان آورده‌اند مى‌گويند: آيا اين [منافقان‌] بودند كه با تأكيد به خدا قسم مى‌خوردند كه واقعا با شما هستند؟ [آرى‌] اعمال آنها تباه شد و زيانكار گرديدند'),
(723, 5, 54, 'اى كسانى كه ايمان آورده‌ايد! هر كس از شما از دينش برگردد، خدا به زودى جمعيتى را به عرصه مى‌آورد كه دوستشان دارد و دوستش مى‌دارند، با مؤمنان افتاده و با كافران سختگيرند، در راه خدا جهاد مى‌كنند و از ملامت هيچ ملامتگرى نمى‌ترسند. اين فضل خداست كه به ه'),
(724, 5, 55, 'ولىّ و سرپرست شما تنها خدا و رسول اوست و كسانى كه ايمان آورده‌اند همانها كه نماز را برپا مى‌دارند و در حال ركوع زكات مى‌دهند [على عليه السّلام‌]'),
(725, 5, 56, 'و هر كه ولايت خدا و رسول او و كسانى را كه ايمان آورده‌اند بپذيرد [از حزب خداست‌]، و بى‌ترديد حزب خدا پيروزند'),
(726, 5, 57, 'اى كسانى كه ايمان آورده‌ايد! كسانى را كه دين شما را به بازيچه گرفته‌اند- چه از كسانى كه پيش از شما كتاب داده شده‌اند و چه از كافران- دوستان خود مگيريد، و از خدا بترسيد اگر واقعا مؤمنيد'),
(727, 5, 58, 'و چون [با اذان‌] به نماز ندا دهيد، آن را به مسخره و بازى مى‌گيرند. اين از آن روست كه آنها مردمى نابخردند'),
(728, 5, 59, 'بگو: اى اهل كتاب! آيا [علت اين كه‌] بر ما خرده مى‌گيريد؟ جز اين است كه ما به خداى يگانه و به آنچه بر ما نازل شده و آنچه پيش از اين نازل گرديده، ايمان آورده‌ايم؟ و اين به خاطر آن است كه اغلب شما نافرمانيد'),
(729, 5, 60, 'بگو: آيا شما را از چيزى كه عقوبتش نزد خدا از اين هم بدتر است خبر بدهم؟ كسانى كه خدا آنها را از رحمتش دور ساخت و بر آنان خشم گرفت و برخى از ايشان را به ميمون‌ها و خوك‌ها مسخ نمود، و آنان كه طاغوت و بت را پرستيدند. اينانند كه موقعيت آنها [خيلى‌] بدتر ا'),
(730, 5, 61, 'و چون نزد شما آيند گويند: ايمان آورديم در حالى كه با كفر وارد شده‌اند و با كفر بيرون رفته‌اند، و خدا به آنچه كتمان مى‌كنند آگاه‌تر است'),
(731, 5, 62, 'و بسيارى از آنها را مى‌بينى كه در گناه و تعدى و حرامخوارى سبقت مى‌گيرند واقعا بد عمل مى‌كنند'),
(732, 5, 63, 'چرا علما و دانشمندان، آنها را از سخنان گناه آلود و خوردن مال حرام باز نمى‌دارند؟ به راستى زشت است آنچه انجام مى‌دادند'),
(733, 5, 64, 'و يهود گفتند: دست خدا بسته است. دست‌هاى خودشان بسته باد! و به سزاى اين [ناروا] كه گفتند از رحمت خدا دور گشتند بلكه دو دست او باز است، هرگونه بخواهد مى‌بخشد. و به تحقيق آنچه از جانب خدا بر تو نازل شده بر كفر و طغيان بسيارى از آنان مى‌افزايد، و تا روز'),
(734, 5, 65, 'و اگر اهل كتاب ايمان آورده و تقوا پيشه كرده بودند، قطعا گناهانشان را مى‌بخشيديم و آنها را به باغ‌هاى پرنعمت داخل مى‌ساختيم'),
(735, 5, 66, 'و اگر آنها [احكام‌] تورات و انجيل و آنچه را كه از جانب پروردگارشان به سوى آنها نازل شده، برپا مى‌كردند، بى‌ترديد از بالاى سر و از زير پايشان روزى مى‌خوردند. از آنها گروهى ميانه رو و معتدل هستند ولى بسيارى از ايشان بد عمل مى‌كنند'),
(736, 5, 67, 'اى پيامبر! آنچه از جانب پروردگارت بر تو نازل شده ابلاغ كن و اگر نكنى رسالت او را انجام نداده‌اى، و خدا تو را از [گزند] مردم نگاه مى‌دارد. همانا خدا گروه كافران را هدايت نمى‌كند'),
(737, 5, 68, 'بگو: اى اهل كتاب! تا وقتى كه [احكام‌] تورات و انجيل و آنچه را از پروردگارتان به سوى شما نازل شده برپا نكنيد هيچ اعتبارى نداريد، و [هشدار كه‌] آنچه از جانب پروردگارت بر تو نازل شده، بى‌گمان بر كفر و طغيان بسياريشان مى‌افزايد. پس بر قوم كافر اندوه مخور'),
(738, 5, 69, 'بى‌گمان، آنها كه [صورى‌] ايمان آوردند و آنها كه يهودى شدند و مسيحيان و صابئى‌ها، هر كه [از اينان‌] به خداى يگانه و روز جزا ايمان [واقعى‌] بياورد و عمل نيكو انجام دهد، نه ترسى بر آنهاست و نه غمگين مى‌شوند'),
(739, 5, 70, 'به تحقيق ما از بنى اسرائيل پيمان گرفتيم و پيامبرانى به سوى آنها فرستاديم، [اما] هرگاه پيامبرى تعليماتى براى آنها آورد كه دلخواهشان نبود، گروهى را دروغگو شمردند و گروهى را كشتند'),
(740, 5, 71, 'و پنداشتند كه گرفتارى و آزمايشى در كار نيست، لذا [از درك حقايق‌] كور و كر شدند. سپس [چون متنبّه شدند] خدا به آنها عطف توجه كرد. باز بسيارى از آنها كور و كر شدند، و خدا به آنچه انجام مى‌دهند بصير است'),
(741, 5, 72, 'آنها كه گفتند: خدا همان مسيح، پسر مريم است، كافر شدند، در حالى كه مسيح مى‌گفت: اى بنى اسرائيل! خداى يگانه را كه پروردگار من و شماست بپرستيد، كه هر كس به خدا شرك آورد، [بداند كه‌] خدا مسلما بهشت را بر او حرام كرده و جايگاه او جهنم است، و ستمكاران را ه'),
(742, 5, 73, 'به تحقيق، كسانى كه [به تثليث قائل شده‌] گفتند: خدا سوم سه [خدا] ست، كافر شدند، و حال آن كه هيچ معبودى جز خداى يكتا نيست. و اگر از آنچه مى‌گويند دست برندارند، حتما عذاب دردناكى به كسانى از ايشان كه كافر شدند خواهد رسيد'),
(743, 5, 74, 'چرا به سوى خدا توبه نمى‌كنند و از او آمرزش نمى‌خواهند؟ و خداوند آمرزنده‌ى مهربان است'),
(744, 5, 75, 'مسيح پسر مريم جز پيامبرى نبود كه پيش از او [نيز] پيامبرانى گذشتند و مادرش زن بسيار درستكارى بود و هر دو [مانند انسان‌هاى ديگر] غذا مى‌خوردند. بنگر چگونه آيات را براى آنها توضيح مى‌دهيم، آن‌گاه ببين چگونه [از حقيقت‌] منحرف مى‌شوند'),
(745, 5, 76, 'بگو: آيا غير از خدا چيزى را كه مالك سود و زيان شما نيست مى‌پرستيد؟ و خداوند، همو شنواى داناست'),
(746, 5, 77, 'بگو: اى اهل كتاب! در دين خود به ناحق غلو و زياده‌روى نكنيد، و از هوا و هوس قومى كه پيش از اين، هم خود گمراه شدند و هم بسيارى را گمراه كردند و از راه راست به دور افتادند، پيروى نكنيد'),
(747, 5, 78, 'آن كسان از بنى اسرائيل كه به كفر گراييدند، بر زبان داود و عيسى بن مريم لعنت شدند. اين از آن روست كه [آنها] سركشى كردند و پيوسته تجاوز مى‌نمودند'),
(748, 5, 79, 'آنها يكديگر را از اعمال زشتى كه مرتكب مى‌شدند باز نمى‌داشتند. به راستى آنچه مى‌كردند بد بود'),
(749, 5, 80, 'بسيارى از آنها را مى‌بينى كه با كافران دوستى مى‌كنند، به راستى آنچه نفس [سركش‌] آنها برايشان پيش فرستاده زشت است كه خدا بر ايشان خشم گرفت و ايشان در عذاب جاودانه خواهند ماند'),
(750, 5, 81, 'و اگر به خدا و پيامبر و آنچه بر او نازل شده ايمان مى‌آوردند، آنها را دوست خود نمى‌گرفتند، ولى [دريغا كه‌] بسيارى از آنان نافرمان هستند'),
(751, 5, 82, 'مسلما يهوديان و مشركان را از همه مردم در عداوت با مؤمنان سخت‌تر خواهى يافت، و بى‌ترديد نزديك‌تر از همه به مهر و محبت مؤمنان، كسانى را خواهى يافت و البته آنان گفتند: ما نصرانى هستيم، اين از آن روست كه از ايشان كشيشان و راهبانى هستند و البته آنان تكبر'),
(752, 5, 83, 'و چون آياتى را كه بر اين پيامبر نازل شده بشنوند، چشمانشان را بينى به جهت آن كه حق را شناخته‌اند اشك مى‌بارد. گويند: بار الها، ايمان آورديم، پس ما را با گواهان بنويس'),
(753, 5, 84, 'چرا به خدا و دين حق كه سوى ما آمده ايمان نياوريم، در حالى كه آرزو داريم پروردگارمان با گروه صالحان قرينمان كند'),
(754, 5, 85, 'پس خداوند به پاس آنچه گفتند، بهشت را پاداششان داد كه از پاى درختانش نهرها جارى است و در آن جاودان بمانند، و اين پاداش نيكوكاران است'),
(755, 5, 86, 'و كسانى كه كافر شدند و آيات ما را دروغ پنداشتند آنان دوزخى‌اند'),
(756, 5, 87, 'اى مؤمنان! چيزهاى پاكيزه را كه خدا براى شما حلال كرده حرام نكنيد و از حد مگذريد كه خداوند متجاوزان را دوست نمى‌دارد'),
(757, 5, 88, 'و از آنچه خدا روزى شما كرده حلال و پاكيزه بخوريد، و از خدايى كه به او ايمان داريد، پروا داشته باشيد'),
(758, 5, 89, 'خدا شما را به سوگندهاى بيهوده‌تان بازخواست نمى‌كند، ولى به سوگندى كه با قصد مى‌خوريد [و مى‌شكنيد] مؤاخذه مى‌كند. پس كفاره‌ى آن اطعام ده مسكين است، از غذاهاى متوسطى كه به كسان خود مى‌خورانيد، يا پوشاندن آنها، يا آزاد كردن بنده‌اى و هر كه [اينها را] ني'),
(759, 5, 90, 'اى مؤمنان! حق اين است كه شراب و قمار و بت‌هاى نصب شده و تيرهاى بخت‌آزما، پليدى [و] از عمل شيطان هستند، پس از آنها دورى كنيد تا رستگار شويد'),
(760, 5, 91, 'جز اين نيست كه شيطان مى‌خواهد با شراب و قمار، ميان شما دشمنى و كينه افكند و از ياد خدا و از نماز بازتان دارد. پس آيا دست بردار هستيد'),
(761, 5, 92, 'و اطاعت خدا و اطاعت رسول كنيد و [از گناه‌] برحذر باشيد، اگر پشت كرديد بدانيد كه پيامبر ما فقط عهده‌دار ابلاغ روشن است'),
(762, 5, 93, 'بر كسانى كه ايمان آورده و كارهاى شايسته كرده‌اند، در باره‌ى آنچه [پيش از حكم تحريم‌] خورده‌اند گناهى نيست، به شرط آن كه پرهيزكار و مؤمن گردند و كارهاى شايسته كنند، سپس [از محرمات‌] پرهيز نمايند و [به تحريم آن‌] مؤمن باشند، سپس [با جدّيت‌] پرهيزكارى'),
(763, 5, 94, 'اى مؤمنان! خدا شما را به [تحريم‌] صيدى كه در دسترس شما و نيزه‌هاى شماست مى‌آزمايد، تا معلوم دارد چه كسى در نهان از او پروا مى‌دارد. پس هر كه بعد از اين [دستور] تجاوز كند، او را عذابى دردناك خواهد بود'),
(764, 5, 95, 'اى مؤمنان! وقتى كه احرام داريد شكار را مكشيد، و هر كس از شما عمدا آن را بكشد، بايد كفاره‌اى معادل آن از چهارپايان بدهد، كه دو نفر عادل از خودتان [معادل بودن‌] آن را تصديق كنند و به عنوان قربانى به كعبه برسد [و قربانى شود]، يا [او را] كفاره‌اى [مانند'),
(765, 5, 96, 'صيد دريا و خوراك آن براى شما حلال شد تا براى شما و مسافران بهره‌اى باشد. و شكار بيابان مادام كه احرام داريد، بر شما حرام شده است و از خدايى كه به سوى او محشور مى‌شويد پروا كنيد'),
(766, 5, 97, 'خداوند، كعبه آن خانه حرمت دار و نيز ماه‌هاى حرام و قربانى‌هاى بى‌نشان و نشاندار را مايه قوام كار مردم قرار داده است. اين بدان سبب است كه بدانيد خداوند هر چه را كه در آسمان‌ها و زمين است مى‌داند و خدا به همه چيز واقف است'),
(767, 5, 98, 'بدانيد كه خداوند هم سخت كيفر است و هم بسيار آمرزنده و مهربان'),
(768, 5, 99, 'پيامبر جز ابلاغ، تعهدى ندارد، و خدا آنچه را عيان كنيد يا نهان كنيد مى‌داند'),
(769, 5, 100, 'بگو: پليد و پاكيزه يكسان نيستند، هر چند كثرت پليد تو را به شگفت آورد. پس اى صاحبان خرد! از خدا بترسيد، باشد كه رستگار شويد'),
(770, 5, 101, 'اى مؤمنان! از چيزهايى نپرسيد كه اگر براى شما فاش گردد موجب اندوهتان مى‌شود. و اگر به هنگام نزول قرآن از آن بپرسيد [حكم آن‌] براى شما روشن خواهد شد. خدا از آن [سؤالات بيجا] درگذشت و خداوند آمرزنده‌ى بردبار است'),
(771, 5, 102, 'پيش از شما هم گروهى از اين پرسش‌ها كردند، آن‌گاه [كه برايشان بيان گرديد] بدان كافر شدند'),
(772, 5, 103, 'خداوند [حكمى به عنوان‌] بحيره و سائبه و وصيله و حام مقرر نداشته است، و ليكن كافرانند كه بر خدا دروغ مى‌بندند و بيشترشان تعقل نمى‌كنند'),
(773, 5, 104, 'و چون به ايشان گفته شود: به سوى آنچه خدا نازل كرده و به سوى پيامبر بياييد، گويند: آنچه پدران خويش را بر آن يافته‌ايم ما را بس است. آيا اگر چه پدرانشان چيزى نمى‌دانستند و راهى نيافته بودند [باز پيروى آنها مى‌كنند]'),
(774, 5, 105, 'اى كسانى كه ايمان آورده‌ايد! مراقب خودتان باشيد، وقتى كه شما هدايت يافتيد، بيراهه رفتن ديگران به شما زيان نمى‌رساند. بازگشت همه شما به سوى خداست و از آنچه مى‌كرده‌ايد آگاهتان مى‌سازد'),
(775, 5, 106, 'اى مؤمنان! چون يكى از شما را مرگ فرا رسد، به هنگام وصيّت دو تن عادل از [همكيشان‌] خودتان را شاهد بگيريد، و چنانچه در سفر بوديد و دچار حادثه مرگ شديد [و مسلمانى نيافتيد] دو تن از غير [همكيشان‌] شما شاهد باشند، و اگر [در صداقت آنها] شك كرديد آنها را ن'),
(776, 5, 107, 'و اگر معلوم شد كه آنان دستخوش خيانت شده‌اند، دو تن ديگر از كسانى كه بر آنها ستم رفته است و از نزديكان [متوفّى‌] هستند به جاى آنان قيام كنند و به خداوند سوگند بخورند كه شهادت ما قطعا از شهادت آن دو درست‌تر است و ما از حق تجاوز نكرده‌ايم و در غير اين'),
(777, 5, 108, 'اين روش براى آن كه شهادت به صورت درست ادا شود، يا شاهدان بترسند كه مبادا سوگندى بعد از سوگند آنها به ميان آيد، به صواب نزديك‌تر است. و از خدا بترسيد و [اين پندها را] بشنويد و [بدانيد كه‌] خداوند گروه فاسقان را هدايت نمى‌كند'),
(778, 5, 109, 'روزى كه خدا همه پيامبران را جمع كند و گويد: [از ناحيه امت‌ها] چه پاسخى به شما داده شد؟ گويند: ما را هيچ علمى نيست، همانا تو خودت از رازهاى نهان آگاهى'),
(779, 5, 110, '[ياد كن‌] آن‌گاه كه خداوند گفت: اى عيسى بن مريم! موهبت مرا بر خودت و بر مادرت به ياد آور، آن‌گاه كه تو را به روح القدس يارى دادم كه در گهواره [به اعجاز] و در ميانسالى [به وحى‌] با مردم سخن مى‌گفتى، و آن‌گاه كه به تو كتاب و حكمت و تورات و انجيل را آم'),
(780, 5, 111, 'و چون به حواريون الهام كردم كه به من و پيامبرم ايمان آوريد. گفتند: ايمان آورديم و گواه باش كه ما فرمانبرداريم'),
(781, 5, 112, '[و] آن وقت كه حواريون گفتند: اى عيسى بن مريم! آيا پروردگارت مى‌تواند براى ما مائده‌اى از آسمان نازل كند؟ گفت: از خداوند پروا كنيد اگر مؤمنيد'),
(782, 5, 113, 'گفتند: مى‌خواهيم از آن بخوريم و دل‌هايمان مطمئن شود و بدانيم كه به ما قطعا راست گفته‌اى و خود از شاهدان آن باشيم'),
(783, 5, 114, 'عيسى بن مريم گفت: خداوندا! پروردگار! براى ما از آسمان مائده‌اى بفرست كه هم عيدى براى اولين و آخرين ما و هم حجّت و نشانه‌اى از تو باشد، و به ما روزى بخش كه تو بهترين روزى دهندگانى'),
(784, 5, 115, 'خداوند گفت: من آن را بر شما خواهم فرستاد، ولى هر كس از شما پس از آن كافر شود او را چنان عذابى كنم كه هيچ يك از جهانيان را عذاب نكرده باشم'),
(785, 5, 116, 'و [ياد كن‌] آن‌گاه كه خدا گويد: اى عيسى بن مريم! آيا تو به مردم گفتى من و مادرم را به جاى خدا به عنوان دو معبود بپرستيد؟ گويد: منزّهى تو، مرا نشايد كه آنچه حق من نيست بگويم. اگر من اين را گفته بودم بى‌شك مى‌دانستى. تو آنچه در ضمير من است مى‌دانى، ول'),
(786, 5, 117, 'من به آنان نگفتم جز آنچه به من امر كردى كه: خدا را كه پروردگار من و شماست بپرستيد، و تا وقتى كه در ميانشان بودم بر آنان شاهد و ناظر بودم پس چون مرا برگرفتى، تو خود مراقب آنان بودى، و تو بر همه چيز شاهدى'),
(787, 5, 118, 'اگر آنها را عذاب كنى، بندگان تواند و اگر از آنان درگذرى اين تويى كه شكست‌ناپذير و حكيمى'),
(788, 5, 119, 'خدا فرمود: امروز روزى است كه صدق راستگويان سودشان دهد. براى آنها باغ‌هايى است كه از پاى درختانش نهرها جارى است كه جاودانه در آنند. هم خدا از آنها خشنود است و هم آنها از خدا خشنودند، و اين كاميابى بزرگ است'),
(789, 5, 120, 'فرمانروايى آسمان‌ها و زمين و آنچه در ميان آنهاست از آن خداوند است، و او بر هر كارى تواناست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ستايش خداى را كه آسمان‌ها و زمين را آفريد و تاريكى‌ها و روشنايى را پديد آورد، با اين همه كافران براى پروردگارشان همتا قرار مى‌دهند'),
(791, 6, 2, 'او كسى است كه شما را از گل آفريد، سپس [براى شما] عمرى مقرر داشت، و اجل حتمى نزد اوست، آن‌گاه شما [باز هم در قدرت او] شك مى‌كنيد'),
(792, 6, 3, 'و او در آسمان‌ها و زمين خداست. نهان و آشكار شما و آنچه را كسب مى‌كنيد مى‌داند'),
(793, 6, 4, 'هيچ آيه‌اى از آيات پروردگارشان به سوى آنها نمى‌آمد مگر اين كه از آن رويگردان بودند'),
(794, 6, 5, 'و همانا حق را چون برايشان آمد تكذيب كردند، پس به زودى اخبار آنچه بدان استهزا مى‌كردند به سويشان خواهد آمد'),
(795, 6, 6, 'آيا نديدند كه پيش از آنها چه بسيار اقوامى را هلاك كرديم كه در روى زمين به آنها تمكّنى داديم كه به شما نداديم و آسمان را بر آنها بارانديم و نهرها در زير پاهايشان روان ساختيم؟ آن‌گاه آنان را به سزاى گناهانشان نابود كرديم و از پى آنان قومى ديگر پديد آورد'),
(796, 6, 7, 'و اگر مكتوبى بر كاغذى بر تو نازل مى‌كرديم و آنها آن را با دست خود لمس مى‌كردند، [باز هم‌] كافران مى‌گفتند: اين چيزى جز يك سحر آشكار نيست'),
(797, 6, 8, 'و گفتند: چرا فرشته‌اى بر او نازل نشده؟ و اگر فرشته‌اى نازل مى‌كرديم، قطعا كار [آزمايش‌] پايان مى‌گرفت و ديگر مجال و مهلتى داده نمى‌شدند'),
(798, 6, 9, 'و اگر فرستاده‌ى خويش را فرشته‌اى مى‌كرديم، حتما وى را به صورت مردى قرار مى‌داديم و [قهرا] امر را همچنان بر آنها مشتبه مى‌ساختيم [باز مى‌گفتند: اين هم بشرى است‌]'),
(799, 6, 10, 'و پيش از تو پيامبرانى به استهزا گرفته شدند، پس كيفر اين استهزا، گريبان ريشخند كنندگان را گرفت'),
(800, 6, 11, 'بگو: در زمين سير كنيد، سپس بنگريد كه سر انجام تكذيب كنندگان چگونه بوده است'),
(801, 6, 12, 'بگو: آنچه در آسمان‌ها و زمين است براى كيست؟ بگو: از آن خداست. او رحمت را بر خود مقرّر داشته و مسلما شما را در روز قيامت كه شكى در آن نيست گرد مى‌آورد. كسانى كه خويشتن را در زيان افكندند ايمان نمى‌آورند'),
(802, 6, 13, 'و آنچه در شب و روز قرار گرفته از آن اوست، و او شنواى داناست'),
(803, 6, 14, 'بگو: آيا غير خدا را كه آفريدگار آسمان‌ها و زمين است سرپرست خود گيرم، در حالى اوست كه مى‌خوراند و خورانده نمى‌شود؟ بگو: دستور يافته‌ام نخستين كسى باشم كه تسليم شده و [گفته است كه‌] هرگز از مشركان مباش'),
(804, 6, 15, 'بگو: من اگر پروردگارم را نافرمانى كنم، از عذاب روزى بزرگ بيم دارم'),
(805, 6, 16, 'هر كس در آن روز [عذاب‌] از وى برگردانده شود، حقا [خدا] به او رحم كرده است، و اين كاميابى آشكار است'),
(806, 6, 17, 'و اگر خداوند به تو گزندى رساند، كسى جز او برطرف كننده‌ى آن نيست و اگر خيرى به تو رساند، او بر هر كارى تواناست'),
(807, 6, 18, 'و او بربندگان خويش قاهر و چيره است، و او حكيم آگاه است'),
(808, 6, 19, 'بگو: كدام چيز گواهى‌اش بزرگ‌تر است؟ بگو: خدا ميان من و شما گواه است، و اين قرآن به من وحى شده است كه شما و هر كس را كه [اين پيام‌] به او برسد بدان هشدار دهم. آيا شما شهادت مى‌دهيد كه با خدا خدايان ديگرى هست؟ بگو: من چنين شهادتى نمى‌دهم. بگو: فقط او م'),
(809, 6, 20, 'كسانى كه كتابشان داده‌ايم، او [پيامبر] را مى‌شناسند آن سان كه فرزندان خود را مى‌شناسند. كسانى كه خود را به زيان افكنده‌اند ايمان نمى‌آورند'),
(810, 6, 21, 'و كيست ستمكارتر از كسى كه بر خدا دروغ بندد يا آيات او را تكذيب كند؟ بى‌ترديد ستمكاران رستگار نمى‌شوند'),
(811, 6, 22, 'و روزى كه همه آنها را گرد آوريم، آن‌گاه به مشركان گوييم: كجايند شريكان شما كه آنها را [شريك خدا] مى‌پنداشتيد'),
(812, 6, 23, 'سپس پاسخ و عذر آنها جز اين نيست كه بگويند: به خدايى كه پروردگار ماست [سوگند] ما مشرك نبوديم'),
(813, 6, 24, 'بنگر كه چگونه به خود دروغ گفتند و آنچه مى‌بافتند از [دست‌] آنها بر باد رفت'),
(814, 6, 25, 'و از ايشان كسانى هستند كه به [ظاهر] به تو گوش مى‌دهند، و ما بر دل‌هايشان سرپوش قرار داده‌ايم كه فهم آن نكنند و در گوششان سنگينى نهاده‌ايم. و اگر تمام نشانه‌هاى حق را ببينند باز به آن ايمان نمى‌آورند، تا جايى كه چون نزد تو آيند كه با تو جدال كنند، كاف'),
(815, 6, 26, 'و آنان [مردم را] از آن باز مى‌دارند و [خود نيز] از آن دور مى‌شوند، ولى جز خودشان را هلاك نمى‌كنند و نمى‌دانند'),
(816, 6, 27, 'و كاش آنها را هنگامى كه برابر آتش نگاه داشته مى‌شوند مى‌ديدى كه مى‌گويند: اى كاش بازگردانده مى‌شديم و ديگر آيات پروردگار خويش را انكار نمى‌كرديم و از مؤمنان مى‌شديم'),
(817, 6, 28, '[چنين نيست‌] بلكه آنچه در گذشته پنهان مى‌داشتند بر آنان آشكار شده، و اگر هم بازگردانده شوند، بى‌شك به همانچه از آن نهى شده بودند، بر مى‌گردند، و بى‌ترديد ايشان دروغگويند'),
(818, 6, 29, 'و گفتند: ما را حياتى جز همين زندگى دنيوى نيست و ما زنده شدنى نيستيم'),
(819, 6, 30, 'و اگر مى‌ديدى آن‌گاه را كه ايشان در پيشگاه پروردگارشان نگاه داشته شوند [به آنها گويد:] آيا اين [برانگيخته شدن‌] حق نيست؟ گويند: چرا، سوگند به پروردگارمان. گويد: پس به خاطر انكارى كه مى‌كرديد عذاب را بچشيد'),
(820, 6, 31, 'كسانى كه لقاى خدا را دروغ شمردند قطعا زيان كردند و چون رستاخيز ناگهان بر آنها در آيد، گويند: اى دريغ بر ما از آن كوتاهى كه در [دنيا] كرديم. و اينان بار گناهان خود را به دوش مى‌كشند. وه، چه بد بارى مى‌كشند'),
(821, 6, 32, 'و زندگى دنيا جز بازيچه و سرگرمى نيست، و قطعا سراى آخرت براى كسانى كه پرهيزكارى كنند بهتر است، پس آيا نمى‌انديشيد'),
(822, 6, 33, 'ما نيك مى‌دانيم كه آنچه ايشان مى‌گويند تو را [سخت‌] غمگين مى‌كند ولى در واقع آنها تو را تكذيب نمى‌كنند، بلكه ظلم پيشگان آيات خدا را انكار مى‌كنند'),
(823, 6, 34, 'بى‌ترديد، پيش از تو نيز پيامبرانى تكذيب شدند، ولى بر آنچه تكذيب شدند و آزار ديدند صبر كردند تا نصرت ما به ايشان رسيد. و سخنان خداوند را تغيير دهنده‌اى نيست، و البته از اخبار پيامبران به تو رسيده است'),
(824, 6, 35, 'و چنانچه اعراض آنها بر تو گران مى‌آيد، پس اگر مى‌توانى نقبى در زمين يا نردبانى در آسمان فراهم آور تا معجزه‌اى [ديگر] بر آنان بياورى [چنين كن‌]. و اگر خدا مى‌خواست آنها را [به اجبار] بر هدايت گرد مى‌آورد، پس زنهار از نادانان مباش'),
(825, 6, 36, 'تنها كسانى [دعوت تو را] اجابت مى‌كنند كه گوش شنوا دارند، [ولى اينها مردگانند] و مردگان را خداوند [در قيامت‌] بر مى‌انگيزد و سپس به سوى او بازگردانده مى‌شوند'),
(826, 6, 37, 'و گفتند: چرا معجزه‌اى [كه ما مى‌خواهيم‌] از پروردگارش بر او نازل نمى‌شود؟ بگو: بى‌ترديد خداوند قادر است كه معجزه‌اى نازل كند، ولى اكثرشان نمى‌دانند'),
(827, 6, 38, 'و هيچ جنبنده‌اى در زمين و هيچ پرنده‌اى كه با دو بال خويش پرواز مى‌كند نيست، مگر اين كه آنها [نيز] گروه‌هايى مانند شما هستند. ما در اين كتاب از [توضيح‌] چيزى فروگذار نكرده‌ايم، آن‌گاه [همه‌] به سوى پروردگارشان محشور مى‌گردند'),
(828, 6, 39, 'و كسانى كه آيات ما را تكذيب كردند، كران و لالانى در تاريكى‌ها هستند. خدا هر كه را بخواهد بيراه مى‌گذارد و هر كه را بخواهد بر راه راست قرارش مى‌دهد'),
(829, 6, 40, 'بگو: اگر راست مى‌گوييد، به من بگوييد اگر شما را عذاب الهى و يا قيامت فرا رسد، آيا جز خدا كسى را مى‌خوانيد'),
(830, 6, 41, 'بلكه تنها او را مى‌خوانيد، و اگر بخواهد، آنچه را كه درخواست مى‌كنيد [از شما] برطرف مى‌سازد، [ولى‌] آنچه را [امروز] شريك او مى‌سازيد، فراموش خواهيد كرد'),
(831, 6, 42, 'البته، به سوى امّت‌هاى پيش از تو [نيز] پيامبرانى فرستاديم و آنها را به تنگدستى و ناخوشى دچار ساختيم، شايد تضرّع و زارى كنند'),
(832, 6, 43, 'پس چرا هنگامى كه عذاب ما به سراغشان آمد زارى و خاكسارى نكردند، بلكه دل‌هاشان سخت شد و شيطان كارهايى را كه مى‌كردند در نظرشان بياراست'),
(833, 6, 44, 'پس چون هشدارهايى را كه به آنها داده شده بود فراموش كردند، درهاى همه چيز را [براى امتحان‌] بر آنان گشوديم، و چون بدانچه داده شده بودند سرمست شدند، ناگاه گرفتارشان كرديم و در آن هنگام يكباره نوميد شدند'),
(834, 6, 45, 'پس ريشه گروهى كه ستم كردند بركنده شد، و سپاس خداى را كه پروردگار عالميان است'),
(835, 6, 46, 'بگو: چه مى‌گوييد، اگر خدا چشم و گوش شما را بستاند و بر دل‌هايتان مهر نهد، كدام معبودى غير از خداوند آنها را به شما بر مى‌گرداند؟ ببين چگونه آيات خود را گوناگون بيان مى‌كنيم باز آنها روى مى‌گردانند'),
(836, 6, 47, 'بگو: به نظر شما اگر عذاب الهى ناگهان يا آشكارا به سراغتان بيايد آيا جز قوم ستمكار نابود مى‌شوند'),
(837, 6, 48, 'و ما پيامبران را صرفا [به عنوان‌] بشارتگر و بيم رسان مى‌فرستيم، پس كسى كه ايمان آورد و اصلاح كند، نه خوفى بر آنهاست و نه محزون مى‌شوند'),
(838, 6, 49, 'و كسانى كه آيات ما را تكذيب كردند، به سزاى نافرمانى‌شان عذاب [ما] آنها را در خواهد گرفت'),
(839, 6, 50, 'بگو: به شما نمى‌گويم كه خزاين خدا نزد من است، و نه غيب مى‌دانم و نه به شما مى‌گويم كه فرشته‌ام. جز از آنچه به من وحى مى‌شود پيروى نمى‌كنم. بگو: آيا كور و بينا يكسانند؟ پس آيا نمى‌انديشيد'),
(840, 6, 51, 'و كسانى را كه از محشور شدن به سوى پروردگارشان انديشناكند به اين [قرآن‌] هشدار ده، كه غير از او براى آنها هيچ ياور و واسطه‌اى نيست، باشد كه پروا كنند'),
(841, 6, 52, 'و كسانى را كه بامداد و شامگاه پروردگار خود را مى‌خوانند و رضاى او را مى‌جويند طرد مكن، نه از حساب آنها چيزى بر توست و نه چيزى از حساب تو بر آنهاست كه طردشان كنى و از ستمكاران شوى'),
(842, 6, 53, 'و بدين گونه آنها را به يكديگر آزموديم، تا سر انجام [در باره‌ى مؤمنان‌] بگويند: آيا اينانند كه خدا از ميان ما موهبتشان داده است؟ مگر نه اين است كه خدا شاكران را بهتر مى‌شناسد'),
(843, 6, 54, 'و چون كسانى كه به آيات ما ايمان دارند، پيش تو آيند، بگو: سلام بر شما، پروردگارتان رحمت را بر خود مقرر داشته است كه هر كس از شما به نادانى كار بدى انجام دهد و سپس از آن توبه كند و به صلاح آيد، [بداند كه‌] خدا مسلما آمرزنده و مهربان است'),
(844, 6, 55, 'و بدين‌سان آيات خود را به روشنى بيان مى‌داريم، تا راه و رسم گناهكاران آشكار گردد'),
(845, 6, 56, 'بگو: همانا من از پرستش كسانى كه شما غير از خدا مى‌خوانيد نهى شده‌ام. بگو: از هوس‌هاى شما پيروى نمى‌كنم، زيرا در اين صورت قطعا به گمراهى مى‌افتم و از ره يافتگان نخواهم بود'),
(846, 6, 57, 'بگو: بى‌ترديد، من بر حجت‌ى روشن از پروردگار خود هستم كه شما آن را تكذيب كرديد. آنچه را [از عذاب خدا] كه شما خواستار شتاب در آنيد نزد من نيست. فرمان جز به دست خدا نيست كه حق را بيان مى‌كند و او بهترين داوران است'),
(847, 6, 58, 'بگو: اگر چيزى كه شتاب در آن را مى‌خواهيد نزد من بود، قطعا ميان من و شما كار يكسره مى‌شد، و خدا به ستمكاران آگاه‌تر است'),
(848, 6, 59, 'و كليدهاى غيب نزد اوست. كسى جز او آن را نمى‌داند، و هر چه در خشكى و درياست مى‌داند، و هيچ برگى نمى‌افتد مگر آن كه بدان آگاه است، و هيچ دانه‌اى در تاريكى‌هاى زمين و هيچ تر و خشكى نيست مگر اين كه در كتابى روشن [درج‌] است'),
(849, 6, 60, 'و او كسى است كه روح شما را در شب [به وقت خواب‌] بر مى‌گيرد و آنچه را در روز كرده‌ايد مى‌داند، سپس شما را در روز بر مى‌انگيزد تا مهلت معين سر آيد. سپس بازگشت شما به سوى اوست و آن‌گاه شما را از [نتيجه‌] آنچه مى‌كرديد آگاه مى‌سازد'),
(850, 6, 61, 'و او بر بندگان خود غالب است و بر شما محافظانى مى‌فرستد، تا وقتى كه مرگ يكى از شما فرا رسد، فرستادگان ما جانش را مى‌ستانند و [در انجام وظيفه‌] كوتاهى نمى‌كنند'),
(851, 6, 62, 'آن‌گاه به سوى خداوند، مولاى حقيقى‌شان بازگردانده مى‌شوند. بدانيد كه حكم از آن اوست و او سريع‌ترين حسابرسان است'),
(852, 6, 63, 'بگو: چه كسى شما را از ظلمات خشكى و دريا مى‌رهاند، در حالى كه او را [علنى‌] و نهانى به زارى مى‌خوانيد كه اگر ما را از اين [مهلكه‌] نجات داد، بى‌گمان از شاكران خواهيم شد'),
(853, 6, 64, 'بگو: خداست كه شما را از آن مهلكه و هر بلايى نجات مى‌دهد، آن‌گاه شما شرك مى‌ورزيد'),
(854, 6, 65, 'بگو: او تواناست كه بر شما عذابى از بالا يا زير پايتان بفرستد يا شما را دچار تفرقه سازد و شرارت بعضى از شما را به بعضى ديگر بچشاند. بنگر چگونه آيات را به صورت‌هاى گوناگون بيان مى‌داريم باشد كه آنها فهم كنند'),
(855, 6, 66, 'و قوم تو آن (قرآن) را تكذيب كردند در حالى كه آن حق است، بگو: من نگهبان شما نيستم [كه شما را به ايمان اجبار كنم‌]'),
(856, 6, 67, 'هر خبرى را وقت وقوعى هست و به زودى خواهيد دانست'),
(857, 6, 68, 'و چون كسانى را ببينى كه در آيات ما [به سوء نيت‌] پرگويى مى‌كنند، از آنان روى برتاب تا به سخنى غير از آن پردازند، و اگر شيطان [آن را] از ياد تو برد، بعد از آن كه متذكر شدى با قوم ستمكار منشين'),
(858, 6, 69, 'و چيزى از حساب آنان (ستمكاران) بر عهده‌ى پرهيزكاران نيست، و ليكن يادآورى [و ارشاد لازم‌] است تا شايد بترسند'),
(859, 6, 70, 'و كسانى را كه دين خود را بازيچه و سرگرمى گرفته و زندگى دنيا فريبشان داده است رها كن، و تو با آن [قرآن‌] پندشان ده تا مبادا كسى به سبب كارى كه كرده به عذاب الهى دچار شود، كه او را جز خدا ياور و شفاعتگرى نباشد، هرگونه عوضى دهد از او نگيرند. آنان كسانى'),
(860, 6, 71, 'بگو: آيا به جاى خداوند چيزى را بپرستيم كه نه سودى به ما رساند و نه زيانى و بعد از آن كه خدا هدايتمان كرده عقبگرد كنيم؟ مانند كسى كه شياطين او را زمين‌گير كرده [و از حركت بازداشته‌اند] كه سرگشته مانده است، [در حالى كه‌] يارانى هم دارد كه وى را به هداي'),
(861, 6, 72, 'و اين كه [گفته است‌]: نماز به پا كنيد و از او پروا داريد، و او كسى است كه به سوى وى محشور مى‌شويد'),
(862, 6, 73, 'و اوست كه آسمان‌ها و زمين را به حق آفريد، و روزى كه [به قيامت‌] گويد باش، وجود مى‌يابد، گفته او شدنى است. و روزى كه در صور دميده شود، حاكميت و فرمان از آن اوست. داناى پنهان و پيداست، و او حكيم و آگاه است'),
(863, 6, 74, 'و آن‌گاه كه ابراهيم به پدرش آزر گفت: آيا بتان را به خدايى مى‌گيرى؟ حقيقت اين است كه من، تو و قومت را در گمراهى آشكار مى‌بينم'),
(864, 6, 75, 'و بدين سان ملكوت آسمان‌ها و زمين را به ابراهيم نشان مى‌داديم تا [بصيرت يابد] و تا از اهل يقين شود'),
(865, 6, 76, 'پس چون شب بر او تاريك شد، ستاره‌اى ديد. [براى احتجاج با مشركان‌] گفت: اين پروردگار من است، آن‌گاه چون افول كرد، گفت: زوال پذيران را دوست ندارم'),
(866, 6, 77, 'و چون ماه را تابان ديد، گفت: اين پروردگار من است، و چون فرو شد، گفت: اگر پروردگارم مرا هدايت نكند بى‌گمان از گمراهان خواهم شد'),
(867, 6, 78, 'پس وقتى كه خورشيد را طالع ديد، گفت: اين پروردگار من است، اين بزرگ‌تر است. و چون افول كرد، گفت: اى قوم! همانا من از آنچه شريك خدا مى‌پنداريد بيزارم'),
(868, 6, 79, 'به راستى من خالصانه روى دل به سوى كسى داشته‌ام كه آسمان‌ها و زمين را آفريد و من از مشركان نيستم'),
(869, 6, 80, 'و قومش با او به جدال برخاستند. گفت: آيا با من در باره‌ى خدا محاجّه مى‌كنيد، در صورتى كه او مرا به راه راست هدايت كرده؟ و من از [شرّ] آنچه شريك او مى‌سازيد بيم ندارم، مگر آن كه پروردگارم چيزى بخواهد [و مرا بترساند]. دانش خداى من همه چيز را فرا گرفته ا'),
(870, 6, 81, 'و چگونه از آنچه شريك خدا كرده‌ايد بترسم، در صورتى كه شما از اين كارتان نمى‌ترسيد كه براى خدا چيزى را شريك مى‌كنيد كه خدا در باره‌ى آن حجتى براى شما نازل نكرده است. پس كدام يك از ما دو گروه به ايمنى سزاوارتر است اگر مى‌دانيد'),
(871, 6, 82, 'كسانى كه ايمان آوردند و ايمان خويش را به ستم (شرك) نيالودند، امنيت مخصوص آنهاست و هم آنان هدايت يافتگانند'),
(872, 6, 83, 'و آن (اسلوب بيان) حجّت ماست كه به ابراهيم در برابر قومش داديم. هر كه را بخواهيم درجه‌ها بالا مى‌بريم. بى‌گمان پروردگار تو حكيم و داناست'),
(873, 6, 84, 'و اسحاق و يعقوب را به او بخشيديم [و] همه را هدايت كرديم، و نوح را از پيش هدايت كرده بوديم، و از نسل او داود و سليمان و ايوب و يوسف و موسى و هارون را هدايت كرديم، و بدين‌سان نيكوكاران را پاداش مى‌دهيم'),
(874, 6, 85, 'و همچنين زكريا و يحيى و عيسى و الياس را كه همگى از صالحان بودند'),
(875, 6, 86, 'و اسماعيل و يسع و يونس و لوط را، جملگى را بر جهانيان برترى داديم'),
(876, 6, 87, 'و از پدران و فرزندان و برادرانشان [نيز مشمول برترى قرار داديم‌] و آنان را برگزيديم و به راه راست هدايت كرديم'),
(877, 6, 88, 'اين هدايت خداست كه هر كس از بندگان خويش را بخواهد بدان هدايت مى‌كند، و اگر [آنها] شرك مى‌ورزيدند، قطعا آنچه انجام مى‌دادند تباه مى‌شد'),
(878, 6, 89, 'آنها كسانى بودند كه كتاب و حكمت و پيامبرى به آنها داده بوديم پس اگر اينان [قريش‌] به اين [اصول و شرايع آسمانى‌] كفر ورزند [آئين حق متروك نمى‌ماند بلكه‌]، قومى [ديگر] را نگاهبان آن مى‌كنيم كه بر آن كافر نباشند'),
(879, 6, 90, 'آنان كسانى بودند كه خدا هدايتشان كرد، پس به هدايت آنان اقتدا كن. بگو: براى اين [رسالت‌] از شما مزدى نمى‌خواهم، كه اين جز اندرزى براى جهانيان نيست'),
(880, 6, 91, 'و خدا را چنان كه سزاوار منزلت اوست نشناختند، آن‌گاه كه گفتند: خدا بر هيچ بشرى چيزى نازل نكرده است. بگو: چه كسى آن كتابى را كه موسى آورده نازل كرد؟ كه براى مردم نور و هدايتى بود، اما شما آن را به صورت پراكنده در كاغذها قرار داده، [قسمتى را] آشكار و بس'),
(881, 6, 92, 'و اين كتابى كه ما آن را نازل كرديم، مبارك و مصدق كتاب‌هايى است كه پيش از آن آمده و براى اين است كه مردم مكه و حومه آن را هشدار دهى. و كسانى كه به آخرت ايمان دارند به قرآن نيز ايمان مى‌آورند، و آنان بر نمازهاى خويش مراقبت دارند'),
(882, 6, 93, 'و چه كسى ستمكارتر است از آن كه بر خدا دروغ بندد، يا بگويد: به من وحى شده، ولى چيزى بر او وحى نشده و كسى كه گويد: به زودى نظير آنچه خداوند نازل كرده، نازل خواهم كرد؟ و كاش اين ستمكاران را در سكرات مرگ مى‌ديدى كه فرشتگان [بر آنان‌] دست گشوده‌اند [و نهي'),
(883, 6, 94, 'و همانا [در ابتداى ورود به عالم ديگر به آنها گفته شود] البته تك‌تك به نزد ما آمده‌ايد، چنان كه نخستين بار شما را همين گونه آفريديم، و آنچه به شما عطا كرده بوديم پشت سر گذاشتيد، و شفيعانى را كه در ميان خودتان شركاى خدا مى‌پنداشتيد با شما نمى‌بينيم، و'),
(884, 6, 95, 'همانا خدا شكافنده‌ى بذرها و هسته‌هاست. زنده را از مرده بيرون آورد و بيرون آرنده‌ى مرده از زنده است. اين است خداى شما، پس كجا منحرف مى‌شويد'),
(885, 6, 96, 'او شكافنده‌ى صبح است و شب را [وقت‌] آرامش ساخته، و خورشيد و ماه را وسيله حساب كردن قرار داده است. اين نظم‌بندى [خداى‌] تواناى داناست'),
(886, 6, 97, 'و اوست كه ستارگان را براى شما قرار داد تا به وسيله آنها در تاريكى‌هاى خشكى و دريا راه يابيد. آرى، ما آيات [خود] را براى اهل دانش به تفصيل بيان كرده‌ايم'),
(887, 6, 98, 'و او كسى است كه شما را از يك تن پديد آورد، پس [برخى از شما در دنيا] مستقرند و [برخى‌] در وديعتگاه قرار دارند [كه هنوز از صلب يا رحم خارج نشده‌اند]. به راستى ما آيات [خود] را براى مردمى كه مى‌فهمند به تفصيل بيان كرده‌ايم'),
(888, 6, 99, 'و اوست كه از آسمان آبى فرستاد، پس بدان هر چيز روييدنى را برآورديم، و از آن [جوانه و ساقه‌اى‌] سرسبز خارج ساختيم كه از آن دانه‌هاى روى هم چيده بيرون مى‌آوريم. و از شكوفه نخل، خوشه‌هاى نزديك به هم و بوستان‌هايى از انگور و زيتون و انار، همگون و ناهمگون'),
(889, 6, 100, 'و براى خدا از جنيان، كه آنها را خدا آفريده شريكانى انگاشتند و بى‌خردانه براى او پسران و دختران ساختند. منزه است خدا و از آنچه وصف مى‌كنند فراتر است'),
(890, 6, 101, 'ابداع كننده‌ى آسمان‌ها و زمين است. چگونه او را پسرى باشد، حال آن كه او را همسرى نبوده و او همه چيز را آفريده و او به همه چيز آگاه است'),
(891, 6, 102, 'اين است خداى يكتا كه پروردگار شماست. معبودى جز او نيست. خالق همه چيز است، پس او را بپرستيد كه او نگهبان [و مدبّر] همه چيز است'),
(892, 6, 103, 'ديدگان، او را در نيابند و اوست كه ديدگان را در مى‌يابد و او باريك بين و آگاه است'),
(893, 6, 104, 'به راستى شما را روشنگرى‌هايى از سوى پروردگارتان آمده است پس هر كه چشم باز كند [و بنگرد] به سود خود اوست، و هر كه كورى كند [و ننگرد] به زيان اوست و من محافظ شما نيستم'),
(894, 6, 105, 'و بدين‌سان آيات [خود] را گوناگون بيان مى‌داريم، تا سر انجام [از روى عناد] بگويند: تو درس گرفته‌اى و نيز براى اين كه آن را براى قومى كه مى‌فهمند روشن ساخته باشيم'),
(895, 6, 106, 'آنچه را از پروردگارت به تو وحى شده پيروى كن، كه خدايى جز او نيست، و از مشركان روى برتاب'),
(896, 6, 107, 'و اگر خدا مى‌خواست شرك نمى‌آوردند، و ما تو را نگهبان آنها نكرده‌ايم و تو عهده‌دار امور آنها نيستى'),
(897, 6, 108, 'و كسانى را كه غير خدا را مى‌خوانند، دشنام ندهيد كه از سر دشمنى و نادانى، خداوند را دشنام دهند. بدين‌سان براى هر امتى عملشان را بياراستيم، سپس بازگشت آنها به سوى پروردگارشان است و ايشان را از آنچه انجام مى‌دادند آگاه خواهد ساخت'),
(898, 6, 109, 'و به خداوند قسم‌هاى مؤكد خوردند كه اگر معجزه‌اى سوى آنها بيايد، حتما به آن ايمان مى‌آورند. بگو: معجزات صرفا در اختيار خداست، و شما چه مى‌دانيد كه اگر معجزه‌اى هم بيايد باز ايمان نياورند'),
(899, 6, 110, 'و دل‌ها و ديدگانشان را [به خاطر عنادى كه دارند] مى‌گردانيم [پس ايمان نمى‌آورند] همچنان كه نخستين بار هم به آن ايمان نياوردند و آنها را در طغيانشان رها مى‌كنيم كه سرگشته و كوردل بمانند'),
(900, 6, 111, 'و اگر ما [همه‌] فرشتگان را به سوى آنها مى‌فرستاديم، و مردگان با آنان به سخن مى‌آمدند، و همه چيز را فوج فوج در برابرشان گرد مى‌آورديم، باز هم ايمان نمى‌آوردند، مگر اين كه خدا بخواهد، و ليكن بيشترشان نمى‌دانند'),
(901, 6, 112, 'و بدين‌گونه براى هر پيامبرى دشمنانى از شياطين انس و جن برگماشتيم كه براى فريب، بر يكديگر سخنان فريبنده القا مى‌كنند، و اگر پروردگار تو مى‌خواست چنين نمى‌كردند. پس ايشان را با افترايى كه مى‌بندند رها كن'),
(902, 6, 113, 'و [نتيجه‌ى اين وسوسه‌ها] براى اين است كه قلوب كسانى كه به آخرت ايمان ندارند به آن گفتار [باطل‌] مايل شود و به آن دل خوش دارند و آنچه را در صدد ارتكاب آن هستند، مرتكب شوند'),
(903, 6, 114, 'پس آيا داورى غير از خدا جويم؟ در حالى كه اوست كه اين كتاب را به تفصيل به سوى شما نازل كرده است. و كسانى كه كتابشان داده‌ايم، مى‌دانند كه قرآن از جانب پروردگار تو به حق نازل شده است. پس هرگز از اهل ترديد مباش'),
(904, 6, 115, 'و سخن پروردگارت از لحاظ صدق و عدل تمام و كمال است، و كلمات او را تغيير دهنده‌اى نيست و او شنواى داناست'),
(905, 6, 116, 'و اگر از بيشتر مردم روى زمين پيروى كنى، تو را از راه خدا به در مى‌كنند، آنها صرفا از وهم و گمان پيروى مى‌كنند و جز اين نيست كه به گزاف و حدس سخن مى‌گويند'),
(906, 6, 117, 'مسلما پروردگار تو بهتر مى‌داند چه كسى از راه او منحرف مى‌شود، و همو به راه يافتگان داناتر است'),
(907, 6, 118, 'پس اگر به آيات او ايمان داريد، از ذبحى كه نام خدا بر آن ياد شده است بخوريد'),
(908, 6, 119, 'و شما را چه شده كه از ذبحى كه نام خدا بر آن ياد شده است نمى‌خوريد؟ در حالى كه خدا آنچه را بر شما حرام كرده برايتان شرح داده است، جز آنچه بدان ناچار شديد. و به راستى بسيارى به هوس‌هاى خويش، بدون آگاهى [مردم را] گمراه مى‌كنند. آرى، پروردگار تو به [حال'),
(909, 6, 120, 'و گناه را چه آشكار و چه پنهان ترك كنيد [زيرا] آنها كه گناه مى‌كنند، به زودى سزاى اعمالى را كه مرتكب مى‌شدند خواهند ديد'),
(910, 6, 121, 'و از ذبحى كه نام خدا بر آن ياد نشده است مخوريد، چرا كه آن قطعا نافرمانى است، و بى‌گمان شياطين دوستان خود را وسوسه مى‌كنند تا با شما مجادله كنند، و اگر اطاعتشان كنيد همانا شما [هم‌] مشركيد'),
(911, 6, 122, 'آيا كسى كه مرده بود و زنده‌اش كرديم و برايش نورى پديد آورديم كه با آن در ميان مردم راه برود، مانند كسى است كه گويا در تاريكى‌هاست و از آن بيرون آمدنى نيست؟ بدين‌سان براى كافران اعمالى كه انجام مى‌دادند، جلوه يافته است'),
(912, 6, 123, 'و بدين‌گونه در هر شهرى رؤساى تبهكارش را گماشتيم [و همه گونه امكانات به آنها داديم‌] كه عاقبت در آن به فريبكارى دست يازند. و آنها صرفا به خودشان نيرنگ مى‌زنند و درك نمى‌كنند'),
(913, 6, 124, 'و چون آيتى سويشان آيد گويند: هرگز ايمان نمى‌آوريم، مگر آن كه نظير آنچه به رسولان خدا داده شده به ما نيز داده شود. خدا بهتر مى‌داند رسالتش را كجا قرار دهد. به زودى كسانى كه مرتكب گناه شدند، به سزاى اين كه مكر مى‌كردند دچار خفّت در پيشگاه خدا و عذابى'),
(914, 6, 125, 'پس هر كه را خدا بخواهد هدايت كند، سينه‌اش را براى اسلام بگشايد و هر كه را بخواهد در گمراهى [وا] گذارد، سينه‌اش را تنگ و تنگ‌تر گرداند چنان كه گويى در آسمان بالا مى‌رود. خدا اين چنين بر كسانى كه ايمان نمى‌آورند پليدى مى‌نهد'),
(915, 6, 126, 'و راه راست پروردگار تو همين است. ما آيات [خود] را براى مردمى كه پند مى‌گيرند، به روشنى بيان داشته‌ايم'),
(916, 6, 127, 'براى آنها نزد پروردگارشان سراى سلامت است، و او دوستدار و ياور آنهاست به خاطر اعمالى كه انجام مى‌دادند'),
(917, 6, 128, 'و روزى كه همه آنها را گرد مى‌آورد [و مى‌گويد]: اى گروه جنّيان! از آدميان، پيروان زيادى فراهم آورديد، و هواخواهان آنان از آدميان گويند: پروردگارا! برخى از ما از ديگرى بهره گرفت، و به اجلى كه براى ما مقرر كرده بودى رسيديم. [خدا] فرمايد: جايگاه شما آت'),
(918, 6, 129, 'و اين گونه برخى از ستمكاران را به سزاى اعمالى كه مرتكب مى‌شدند به بعضى ديگر وا مى‌گذاريم'),
(919, 6, 130, 'اى گروه جنّ و انس! مگر رسولانى از خودتان سوى شما نيامدند كه آيات مرا بر شما مى‌خواندند و شما را از ديدار امروزتان بيم مى‌دادند؟ گويند: بر ضدّ خويش گواهى مى‌دهيم [كه آرى آمدند]، و زندگى دنيا فريبشان داد و ضدّ خود گواهى دادند كه كافر بوده‌اند'),
(920, 6, 131, 'اين [اتمام حجت‌] از آن روست كه پروردگار تو هيچ‌گاه [اهل‌] شهرها را به ستم هلاك كننده نبوده است در حالى كه مردم آن غافل باشند'),
(921, 6, 132, 'و براى هر يك [از بندگان‌] براى آنچه انجام داده‌اند [نزد خدا] رتبه‌هايى است، و پروردگار تو از آنچه مى‌كنند غافل نيست'),
(922, 6, 133, 'و پروردگار تو غنى و صاحب رحمت است، اگر بخواهد شما را مى‌برد و پس از شما هر چه [از آفريدگان خود] را بخواهد جانشين مى‌كند آن چنان كه شما را نيز از نسل گروهى ديگر پديد آورد'),
(923, 6, 134, 'آنچه به شما وعده داده مى‌شود حتما خواهد آمد، و شما [بر خواست خدا] غالب نخواهيد شد'),
(924, 6, 135, 'بگو: اى قوم من! هر چه در توان داريد به كار گيريد كه من نيز در كارم، [امّا] به زودى خواهيد دانست كه عاقبت آن سرا براى كيست. و يقينا ستمكاران رستگار نمى‌شوند'),
(925, 6, 136, 'و براى خدا آنچه از زراعت و دام‌ها كه او آفريده سهمى گذاشتند، [و سهمى براى بت‌ها] و به خيال خود گفتند: اين سهم خدا و اين هم براى بت‌هاى ماست. پس آن سهمى كه مخصوص بت‌هايشان بود به خدا نمى‌رسيد، ولى آنچه براى خدا بود به بت‌ها مى‌رسيد [و سهم خدا را به ب'),
(926, 6, 137, 'و معبودانشان اين گونه براى بسيارى از مشركان، كشتن فرزندانشان را جلوه دادند تا هلاكشان كنند و دينشان را بر آنان مشوش سازند. و اگر خدا مى‌خواست چنين نمى‌كردند، پس ايشان را با دروغ‌هايى كه مى‌بافند رها كن'),
(927, 6, 138, 'و به پندار خويش گفتند: اين‌ها چهارپايان و زراعت‌هايى ممنوع هستند و جز كسى كه ما بخواهيم از آن نمى‌خورد، و چهارپايانى هستند كه سوارى آنها حرام شده و [حال‌] چهارپايانى را نيز بدون ذكر نام خدا ذبح مى‌كنند [و اين احكام را] بر خدا مى‌بندند. به زودى خدا آ'),
(928, 6, 139, 'و گفتند: آنچه در شكم اين دام‌هاست صرفا از آن مردان ماست و بر زنان ما حرام است، و اگر مرده باشد، همه در آن شريكند. به زودى خداوند توصيف آنان [از دين‌] را سزا خواهد داد. بى‌گمان او حكيم آگاه است'),
(929, 6, 140, 'محققا كسانى كه فرزندان خود را به سفاهت و نادانى كشتند و آنچه را خدا روزيشان كرده بود، از روى افترا به خدا حرام كردند، سخت زيان ديدند. به راستى آنها گمراه شدند و راه يافته نبودند'),
(930, 6, 141, 'و اوست كه باغ‌هايى از درختان داربستى [مانند انگور] و بدون داربست و نخل و زراعت با ميوه‌هاى گوناگون، و زيتون و انار، مشابه و غير مشابه را پديد آورد. شما از ميوه‌ى آن به هنگامى كه بار مى‌دهد بخوريد، و روز چيدنش حق آن را [به مستمندان‌] بپردازيد ولى زيا'),
(931, 6, 142, 'و از چهارپايان براى شما باربر و حيوانات كوچك [تر بهره ده‌] بيافريد. از آنچه خدا روزيتان كرده است بخوريد، و از گام‌هاى شيطان پيروى نكنيد كه او دشمن آشكار شماست'),
(932, 6, 143, 'هشت جفت [بر شما حلال است‌] از گوسفند دو تا، و از بز دو تا. بگو: آيا نرهاى آنها را حرام كرده يا ماده‌ها را، يا آنچه را رحم ماده‌ها در بر دارد؟ [چرا از خود حكم مى‌سازيد؟] اگر راست مى‌گوييد مرا از روى علم آگاه سازيد'),
(933, 6, 144, 'و از شتر دو تا، و از گاو دو تا. بگو: آيا نرهاى آنها را حرام كرده يا ماده‌ها را يا آنچه را رحم ماده‌ها در بر دارد؟ يا شما شاهد زمانى كه خدا شما را به اين حكم [ناروا] توصيه كرد؟ پس چه كسى ستمكارتر از آن كس است كه بر خدا دروغ بندد تا مردم را بى‌هيچ دان'),
(934, 6, 145, 'بگو: در آنچه به من وحى شده چيزى را كه خوردنش بر خورنده‌اى حرام باشد نمى‌يابم، مگر اين كه مردار يا خون ريخته يا گوشت خوك باشد كه بى‌شك آن پليد است، يا ذبحى كه از روى نافرمانى نام غير خدا بر آن ياد شده باشد. اما هر كه بدون زياده‌خواهى و سركشى ناچار گر'),
(935, 6, 146, 'و بر يهوديان هر [حيوان‌] ناخن‌دارى را حرام كرديم، و از گاو و گوسفند، پيه آنها را بر آنان تحريم نموديم، به استثناى پيه‌هايى كه بر پشت آنها يا بر روده‌هاست يا آنچه به استخوان چسبيده باشد. اين [تحريم‌] را به جرم سركشى‌شان، به آنها كيفر داديم و بى‌شك م'),
(936, 6, 147, 'پس اگر تو را تكذيب كردند، بگو: پروردگارتان صاحب رحمتى وسيع است، ولى عذاب او از گروه گناهكاران برگردانده نشود'),
(937, 6, 148, 'مشركان خواهند گفت: اگر خدا مى‌خواست، نه ما و نه پدران ما شرك نمى‌آورديم و چيزى را تحريم نمى‌كرديم. پيشينيان آنها نيز همين گونه تكذيب كردند، تا آن كه عذاب ما را چشيدند. بگو: آيا نزد شما دانشى هست كه آن را براى ما آشكار كنيد؟ [ولى حق اين است كه‌] شما'),
(938, 6, 149, 'بگو: برهان رسا و روشن مخصوص خداست [كه براى كسى بهانه‌اى باقى نمى‌گذارد] و اگر اراده مى‌كرد شما را جملگى [به جبر] هدايت مى‌نمود'),
(939, 6, 150, 'بگو: گواهان خود را كه شهادت مى‌دهند كه خدا اين چيزها را حرام كرده است بياوريد، پس اگر آنها شهادت دادند تو با آنها شهادت مده و از هوا و هوس كسانى كه آيات ما را تكذيب كردند و از اميال كسانى كه به آخرت ايمان ندارند و براى پروردگارشان همتا و معادل مى‌تر'),
(940, 6, 151, 'بگو: بياييد تا آنچه را كه پروردگارتان بر شما حرام كرده برايتان بخوانم: كه چيزى را با او شريك نكنيد، و با پدر و مادر نيكى كنيد و فرزندان خويش را از [بيم‌] تنگدستى نكشيد كه ما شما و آنها را روزى مى‌دهيم و به كارهاى بسيار زشت، چه علنى و چه پوشيده نزدي'),
(941, 6, 152, 'و به مال يتيم جز به نيكوترين صورت نزديك نشويد، تا او به مرحله رشد خود برسد. و حق پيمانه و وزن را به انصاف، ادا كنيد. ما هيچ كس را جز به قدر توانش تكليف نمى‌كنيم. و چون سخن گوييد دادگرى كنيد، هر چند [در باره‌ى‌] خويشان باشد و به پيمان خدا وفا كنيد. ا'),
(942, 6, 153, 'و همانا راه راست من همين است، پس از آن پيروى كنيد و به راه‌هاى [متفرقه‌] نرويد كه موجب تفرقه و جدايى از راه او شود. اين است آنچه خدا شما را به آن سفارش كرد، باشد كه شما پرهيزكار شويد'),
(943, 6, 154, 'سپس به موسى كتاب داديم، براى اتمام نعمت بر كسى كه نيكى كرد و براى اين كه [حكم‌] هر چيزى را بيان نماييم، و هدايت و رحمتى است، باشد كه آنان به لقاى پروردگارشان ايمان بياورند'),
(944, 6, 155, 'و اين مبارك كتابى است كه ما آن را نازل كرديم پس پيرو آن باشيد و تقوا پيشه كنيد، اميد كه شما مشمول رحمت [خدا] گرديد'),
(945, 6, 156, 'تا مبادا بگوييد: كتاب آسمانى، فقط بر دو طايفه پيش از ما نازل شد و همانا ما از آموختن آنها غافل بوديم'),
(946, 6, 157, 'يا اين كه بگوييد: اگر كتاب آسمانى بر ما نازل مى‌شد، قطعا از آنها راه يافته‌تر بوديم پس اينك از خدايتان حجت و هدايت و رحمتى سوى شما آمده است. پس كيست ستمكارتر از آن كس كه آيات خدا را تكذيب كند و از آن روى گرداند؟ به زودى كسانى را كه از آيات ما روى مى'),
(947, 6, 158, 'آيا جز اين انتظار دارند كه فرشتگان به سويشان بيايند يا پروردگارت بيايد، يا پاره‌اى از آيات (معجزات) پروردگار تو ظاهر گردد؟ [اما] روزى كه برخى آيات پروردگارت بيايد، براى كسى كه قبلا ايمان نياورده و يا در ايمان خويش عمل خيرى فراهم نكرده باشد، ايمان آو'),
(948, 6, 159, 'تحقيقا كسانى كه دينشان را دستخوش تفرقه ساختند و فرقه فرقه شدند، تو را با آنان كارى نيست، سر و كارشان فقط با خداست آن‌گاه به كارى كه انجام مى‌دادند آگاهشان خواهد كرد'),
(949, 6, 160, 'هر كس كار نيكى بياورد، ده چندان آن پاداش دارد، و هر كس كار بدى بياورد، جز مانند آن مجازات نشود، و بر آنها ستم نرود'),
(950, 6, 161, 'بگو: مسلما پروردگارم مرا به راه راست، دينى استوار كه همان آيين ابراهيم حقگراست، هدايت نمود و او از مشركان نبود'),
(951, 6, 162, 'بگو: بى‌گمان، نماز و عبادات من، و زندگى و مرگ من همه براى خدا پروردگار جهانيان است'),
(952, 6, 163, 'كه شريكى ندارد و به همين امر دستور يافته‌ام و من نخستين مسلمانم'),
(953, 6, 164, 'بگو: آيا جز خدا پروردگارى بجويم، در حالى كه او پروردگار همه چيز است؟ و هيچ كس [گناهى‌] جز به زيان خود مرتكب نمى‌شود، و هيچ كس بار ديگرى را بر دوش نمى‌گيرد، سپس بازگشت شما به سوى پروردگارتان است آن‌گاه شما را به آنچه در آن اختلاف مى‌كرديد آگاهتان مى‌'),
(954, 6, 165, 'و اوست كسى كه شما را در زمين جانشين [يكديگر] نمود و بعضى را بر بعضى ديگر به مراتبى برترى داد تا شما را در داده‌هايش به شما بيازمايد، بى‌گمان پروردگار تو زود كيفر و همو آمرزنده‌ى مهربان است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف، لام، ميم، صاد'),
(956, 7, 2, 'كتابى است كه به سوى تو نازل شده، پس در سينه تو از [تبليغ‌] آن هيچ تنگى و ترديد نباشد، تا بدان هشدار دهى و براى مؤمنان تذكارى باشد'),
(957, 7, 3, 'از آنچه از جانب پروردگارتان به سوى شما نازل شده پيروى كنيد و از غير او به عنوان سرپرستان پيروى نكنيد. چه اندك پند مى‌گيريد'),
(958, 7, 4, 'و چه بسيار شهرها كه [مردم‌] آن را هلاك نموديم و عذاب ما شب هنگام يا در خواب نيمروز به آنها رسيد'),
(959, 7, 5, 'و هنگامى كه عذاب ما بر آنان آمد سخنشان جز اين نبود كه گفتند: بى‌ترديد ما ستمكار بوديم'),
(960, 7, 6, 'پس قطعا از كسانى كه [پيامبران‌] به سوى آنان فرستاده شده‌اند خواهيم پرسيد، و از فرستادگان نيز خواهيم پرسيد'),
(961, 7, 7, 'و بى‌شك [داستانشان را] از روى علم براى آنها نقل خواهيم كرد و ما [از احوال آنها] غايب نبوده‌ايم'),
(962, 7, 8, 'و در آن روز [معيار] سنجش، حق است پس هر كه اعمال وزن شده‌اش سنگين شد هم آنان رستگارانند'),
(963, 7, 9, 'و هر كه اعمال وزن شده‌اش سبك شد، آنان كسانى هستند كه خود را ارزان باخته‌اند، چرا كه به آيات ما ستم مى‌كردند'),
(964, 7, 10, 'و به راستى شما را در زمين مكنت داديم، و براى شما در آن انواع معيشت‌ها نهاديم، [اما] كمتر قدردانى مى‌كنيد'),
(965, 7, 11, 'و همانا ما شما را خلق كرديم، سپس صورتتان بخشيديم، آن‌گاه به فرشتگان گفتيم: براى آدم سجده كنيد پس سجده كردند جز ابليس كه از سجده كنندگان نشد'),
(966, 7, 12, 'فرمود: چه چيز تو را از سجده كردن بازداشت وقتى كه به تو امر كردم؟ گفت: من از او بهترم مرا از آتش آفريدى و او را از گل'),
(967, 7, 13, 'گفت: از آن جا فرو شو، كه تو را نرسد در آن تكبر نمايى پس بيرون شو كه تو از فرومايگانى'),
(968, 7, 14, 'گفت: مرا تا روزى كه [مردم‌] برانگيخته مى‌شوند مهلت ده'),
(969, 7, 15, 'گفت: همانا تو از مهلت يافتگانى'),
(970, 7, 16, 'گفت: پس به خاطر اين كه مرا به بيراهه افكندى، من هم براى [گمراهى‌] آنها حتما بر سر راه راست تو خواهم نشست'),
(971, 7, 17, 'آن‌گاه از پيش روى و پشت سرشان و از طرف راست و چپشان به سراغ آنها مى‌آيم، و اكثرشان را سپاسگزار نخواهى يافت'),
(972, 7, 18, 'فرمود: نكوهيده و مطرود از آن [مقام‌] بيرون شو كه هر كس از آنان تو را پيروى كند، بى‌ترديد جهنّم را از همه شما پر خواهم كرد'),
(973, 7, 19, 'و اى آدم! تو با همسر خويش در آن باغ منزل كن، و از هر جا كه خواهيد بخوريد، ولى به اين يك درخت نزديك نشويد كه از ستمكاران خواهيد شد'),
(974, 7, 20, 'پس شيطان آن دو را وسوسه كرد تا زشتى‌هاى پوشيده‌ى آنان را برايشان نمايان كند، و گفت: پروردگارتان شما را از اين درخت منع نكرد جز براى آن كه [مبادا] فرشته گرديد يا از جاودانان شويد'),
(975, 7, 21, 'و براى آنها سوگند ياد كرد كه: من قطعا از خير خواهان شما هستم'),
(976, 7, 22, 'پس آن دو را با فريب به سقوط كشانيد پس چون از آن درخت چشيدند، زشتى‌هاشان بر آنان آشكار شد و بر آن شدند كه با برگ‌هاى بهشت، خويشتن را بپوشانند، و پروردگارشان آنها را ندا داد: مگر شما را از آن درخت منع نكردم و به شما نگفتم كه شيطان دشمن آشكار شماست'),
(977, 7, 23, 'گفتند: پروردگارا! ما بر خويشتن ستم كرديم، و اگر بر ما نبخشايى و به ما رحم نكنى، بى‌شك از زيانكاران خواهيم شد'),
(978, 7, 24, 'فرمود: پايين رويد كه شما دشمن يكديگريد و براى شما در زمين، تا وقتى معين قرارگاه و برخوردارى است'),
(979, 7, 25, 'فرمود: در آن زندگى مى‌كنيد و در آن مى‌ميريد و از آن بيرون آورده مى‌شويد'),
(980, 7, 26, 'اى فرزندان آدم! به راستى ما براى شما لباسى فرو فرستاديم كه هم زشتى‌هاى شما را مى‌پوشاند و هم زينت شماست ولى جامه تقوا همان بهتر است. اين از آيت‌هاى خداست، باشد كه پند گيرند'),
(981, 7, 27, 'اى فرزندان آدم! مبادا شيطان شما را به فتنه اندازد، چنان كه پدر و مادر شما را از بهشت بيرون راند كه جامه‌ى [بندگى‌] آنها را از تنشان بر مى‌كند تا زشتى‌شان را بر آنها نمايان كند. بى‌شك او و گروهش از آن جا كه آنها را نمى‌بينيد شما را مى‌بينند. ما شياطي'),
(982, 7, 28, 'و چون كار بسيار زشتى كنند، مى‌گويند: پدران خود را بر آن يافتيم و خدا ما را بدان فرمان داده است. بگو: مسلما خدا به كار زشت فرمان نمى‌دهد، آيا چيزى را كه نمى‌دانيد به خدا نسبت مى‌دهيد'),
(983, 7, 29, 'بگو: پروردگارم به انصاف فرمان داده است و اين كه نزد هر مسجدى [و هنگام هر نمازى‌] روى خود را [به سوى او] فرا داريد و خدا را با خالص كردن دين و طاعت براى او بخوانيد. همان گونه كه شما را پديد آورد بر مى‌گرديد'),
(984, 7, 30, 'گروهى را هدايت كرد و گروهى گمراهى بر آنها محقق شد آنها شياطين را به جاى خدا ولىّ خود گرفتند و مى‌پندارند كه راه يافته‌اند'),
(985, 7, 31, 'اى فرزندان آدم! نزد هر مسجدى زينت خود را برگيريد و بخوريد و بنوشيد و زياده‌روى نكنيد كه او اسرافكاران را دوست نمى‌دارد'),
(986, 7, 32, 'بگو: چه كسى زينت الهى را كه خدا براى بندگانش پديد آورده و روزى‌هاى پاكيزه و مطبوع را حرام كرده است؟ بگو: اين نعمت‌ها در زندگى دنيا براى كسانى است كه ايمان آورده‌اند [هر چند ديگران نيز استفاده مى‌كنند] ولى روز قيامت مخصوص ايشان است. ما اين گونه، آيات'),
(987, 7, 33, 'بگو: پروردگار من تنها كارهاى زشت را، چه آشكار و چه پنهان، حرام كرده و گناه و زياده‌خواهى به ناحق را، و [نيز] اين كه چيزهايى را كه دليلى در باره‌ى آن نازل نشده، شريك خدا سازيد و يا چيزى را كه نمى‌دانيد به خدا نسبت دهيد'),
(988, 7, 34, 'و براى هر امتى دوره و مدتى است، پس چون مدتشان به سر آيد لحظه‌اى پس و پيش نمى‌افتند'),
(989, 7, 35, 'اى فرزندان آدم! چون پيامبرانى از خودتان سوى شما آيند كه آيات مرا بر شما حكايت كنند، هر كس پرهيزكارى كند و به صلاح آيد، نه بيمى بر آنهاست و نه غمگين شوند'),
(990, 7, 36, 'و كسانى كه آيات ما را تكذيب كردند و از پذيرش آن تكبر ورزيدند، آنها اهل آتشند و در آن جاودانند'),
(991, 7, 37, 'پس ستمكارتر از آن كس كه بر خدا دروغ بندد يا آيات او را تكذيب كند؟ اينان كسانى هستند كه نصيبشان از آنچه مكتوب شده به ايشان خواهد رسيد، تا آن‌گاه كه فرستادگان ما سوى ايشان آيند كه جانشان بگيرند، گويند: كجايند معبودهايى كه به جاى خدا مى‌خوانديد؟ گويند:'),
(992, 7, 38, 'گويد: شما هم در جمع گروه‌هايى از جن و انس كه پيش از شما گذشتند به آتش داخل شويد. هر بار كه گروهى داخل شود، همكيشان خود را لعنت كند، تا وقتى كه همگى در آن جا به هم رسند، پسينيان آنها (پيروان) در باره‌ى پيشينيانشان (پيشوايان) گويند: پروردگارا! اينها بو'),
(993, 7, 39, 'و پيشوايان به پيروانشان گويند: شما را بر ما امتيازى نيست، پس به سزاى آنچه انجام مى‌داديد عذاب را بچشيد'),
(994, 7, 40, 'همانا كسانى كه آيات ما را تكذيب كردند و از پذيرش آن تمرد نمودند، درهاى آسمان به رويشان باز نشود، و به بهشت داخل نشوند مگر آن كه شتر در سوراخ سوزن داخل شود، و بدين‌سان مجرمان را كيفر مى‌دهيم'),
(995, 7, 41, 'براى آنها بسترى از جهنم و از رويشان لحاف‌هاى آتش است و اين گونه بيدادگران را سزا مى‌دهيم'),
(996, 7, 42, 'و كسانى كه ايمان آورده و كارهاى شايسته كردند،- كه البته هيچ كس را جز به قدر توانش تكليف نمى‌كنيم- آنها اهل بهشت‌اند و در آن ماندگارند'),
(997, 7, 43, 'و از سينه‌هايشان هر كينه‌اى كه باشد مى‌زداييم [و] از زير [پاى‌] شان نهرها جارى است، و مى‌گويند: حمد خدايى را كه ما را به اين [نعمت‌ها] رهنمون شد، و اگر خدا ما را هدايت نكرده بود، راه نمى‌يافتيم. بى‌شك فرستادگان پروردگارمان حقيقت را آوردند. و به آنها'),
(998, 7, 44, 'و اهل بهشت دوزخيان را آواز دهند كه ما آنچه را پروردگارمان به ما وعده داده بود درست يافتيم آيا شما [نيز] وعده‌ى پروردگارتان را درست يافتيد؟ گويند: آرى. پس ندا دهنده‌اى ميان آنها آواز دهد كه لعنت خدا بر ظالمان باد'),
(999, 7, 45, 'همانان كه [مردم را] از راه خدا باز مى‌دارند و آن را [با القاى شبهات‌] منحرف مى‌خواهند و آخرت را منكرند'),
(1000, 7, 46, 'و ميان آن دو [گروه‌] حجابى است، و بر اعراف مردانى [والا مقام‌] هستند كه هر دو [گروه‌] را از سيمايشان مى‌شناسند، و بهشتيان را كه هنوز وارد آن نشده و به آن اميد بسته‌اند، آواز مى‌دهند كه: سلام بر شما'),
(1001, 7, 47, 'و چون چشمشان به جانب دوزخيان گردانده شود، گويند: پروردگارا! ما را با ستمكاران قرار مده'),
(1002, 7, 48, 'و اهل اعراف مردانى را كه آنان را از سيمايشان مى‌شناسند، ندا دهند و گويند: [ديديد كه‌] جمعيت شما و آن همه گردنكشى كه مى‌كرديد، به حال شما سودى نداشت'),
(1003, 7, 49, 'آيا اينانند كسانى كه سوگند ياد مى‌كرديد كه خدا آنها را مورد رحمت خود قرار نمى‌دهد؟ [آن‌گاه به مؤمنان گويند:] به بهشت در آييد، نه بيمى بر شماست و نه اندوهگين مى‌شويد'),
(1004, 7, 50, 'و اهل دوزخ بهشتيان را آواز دهند كه از آب يا از آنچه خدا روزيتان كرده بر ما فرو ريزيد. گويند: خدا آنها را بر كافران حرام كرده است'),
(1005, 7, 51, 'همان كسانى كه دين خويش را سرگرمى و بازيچه گرفتند و زندگى دنيا فريبشان داد. پس امروز آنان را از ياد مى‌بريم همان‌طور كه ديدار امروزشان را از ياد بردند و آيات ما را انكار مى‌كردند'),
(1006, 7, 52, 'و به راستى ما براى آنان كتابى آورديم كه آن را بر اساس علم و دانش تفصيل داديم تا براى قومى كه ايمان مى‌آورند هدايت و رحمتى باشد'),
(1007, 7, 53, 'آيا آنها جز در انتظار تأويل آنند؟ روزى كه تأويل آن بيايد، كسانى كه پيش‌تر آن را به فراموشى سپردند گويند: حقا فرستادگان پروردگارمان حق را آوردند، پس آيا براى ما شفيعانى هست كه شفاعت ما كنند، يا بازگردانده شويم تا غير از آنچه مى‌كرديم انجام دهيم؟ [آرى‌'),
(1008, 7, 54, 'همانا پروردگار شما آن خدايى است كه آسمان‌ها و زمين را در شش روز آفريد سپس بر عرش استيلا يافت. روز را به شب مى‌پوشاند كه شتابان از پى آن است، و خورشيد و ماه و ستارگان را [آفريد] كه مسخّر فرمان او هستند. آگاه باش كه خلق و امر از آن اوست. پاينده است خد'),
(1009, 7, 55, 'پروردگار خود را به زارى و نهانى بخوانيد كه او متجاوزان را دوست نمى‌دارد'),
(1010, 7, 56, 'و در زمين از پس اصلاح آن فساد نكنيد، و او را با بيم و اميد بخوانيد كه رحمت خدا به نيكوكاران نزديك است'),
(1011, 7, 57, 'و اوست كه بادها را پيشاپيش [باران‌] رحمتش مژده‌رسان مى‌فرستد، تا آن‌گاه كه ابرهاى گرانبار را بردارند، آن را به سوى سرزمين مرده برانيم و آب از آن فرود آورده، با آن، همه گونه محصولات برآوريم. مردگان را نيز همين گونه بيرون مى‌آوريم، باشد كه متذكر شويد'),
(1012, 7, 58, 'و سرزمين پاك، گياه آن به اذن پروردگارش [نيكو] برآيد و آن [زمينى‌] كه ناپاك است جز حاصلى ناچيز بر نياورد. بدين‌سان آيات خود را براى مردمى كه شاكرند گوناگون بيان مى‌كنيم'),
(1013, 7, 59, 'همانا نوح را به سوى قومش فرستاديم. پس گفت: اى قوم من! خدا را بپرستيد كه براى شما معبودى جز او نيست. همانا من از عذاب روزى بزرگ بر شما بيمناكم'),
(1014, 7, 60, 'سران قومش گفتند: به راستى ما تو را در ضلالتى آشكار مى‌بينيم'),
(1015, 7, 61, 'گفت: اى قوم من! هيچ گونه گمراهى در من نيست، بلكه من فرستاده‌اى از جانب پروردگار عالميانم'),
(1016, 7, 62, 'پيام‌هاى پروردگار خويش را به شما مى‌رسانم و اندرزتان مى‌دهم و از خدا چيزها مى‌دانم كه شما نمى‌دانيد'),
(1017, 7, 63, 'آيا تعجب كرديد كه بر مردى از خودتان تذكارى از جانب پروردگارتان آمده كه شما را هشدار دهد و براى اين كه پرهيزكار شويد و شايد مورد رحمت قرار گيريد'),
(1018, 7, 64, 'پس او را تكذيب كردند، و ما او و كسانى را كه با وى در كشتى بودند نجات داديم، و آنها را كه آيات ما را تكذيب نمودند غرق كرديم. به تحقيق آنها قومى كوردل بودند'),
(1019, 7, 65, 'و به سوى قوم عاد، برادرشان هود را [فرستاديم‌]. گفت: اى قوم من! خدا را بپرستيد كه براى شما معبودى جز او نيست، پس آيا پروا نمى‌كنيد'),
(1020, 7, 66, 'سران قومش كه كافر شدند، گفتند: بى‌ترديد ما تو را در سفاهت مى‌بينيم و جدّا تو را از دروغگويان مى‌پنداريم'),
(1021, 7, 67, 'گفت: اى قوم من، مرا هيچ سفاهتى نيست، بلكه من فرستاده‌اى از جانب پروردگار عالميانم'),
(1022, 7, 68, 'پيام‌هاى پروردگارم را به شما مى‌رسانم و من براى شما خيرخواهى امين هستم'),
(1023, 7, 69, 'آيا تعجب كرديد كه بر مردى از خودتان تذكارى از جانب پروردگارتان آمده تا شما را هشدار دهد؟ و به ياد آوريد زمانى را كه خدا شما را از پس قوم نوح جانشين آنان كرد، و در آفرينش بر توانايى شما افزود، پس نعمت‌هاى خدا را ياد كنيد شايد رستگار شويد'),
(1024, 7, 70, 'گفتند: آيا سراغ ما آمده‌اى كه تنها خدا را بپرستيم و آنچه پدرانمان مى‌پرستيدند رها كنيم؟ اگر راست مى‌گويى آنچه را به ما وعده مى‌دهى بياور'),
(1025, 7, 71, 'گفت: بى‌گمان عذاب و خشمى [سخت‌] از پروردگارتان بر شما حتمى‌گرديد. آيا در باره‌ى عنوان‌هاى بى‌معنايى كه شما و پدرانتان براى بت‌ها ساخته‌ايد و خدا بر آن هيچ حجتى نازل نكرده است، با من مجادله مى‌كنيد؟ پس منتظر باشيد كه من هم با شما از منتظرانم'),
(1026, 7, 72, 'پس او و كسانى را كه با وى بودند به رحمت خويش نجات داديم و آنها را كه آيات ما را تكذيب كردند و مؤمن نبودند ريشه‌كن نموديم'),
(1027, 7, 73, 'و به سوى قوم ثمود، صالح برادرشان را [فرستاديم‌]. گفت: اى قوم من! خدا را بپرستيد كه براى شما معبودى جز او نيست. اينك براى شما از جانب پروردگارتان حجتى آشكار آمده است. اين، ماده شتر خدا معجزه‌اى براى شماست، پس آن را بگذاريد تا در زمين خدا بخورد، و گزند'),
(1028, 7, 74, 'و ياد آوريد هنگامى را كه از پس قوم عاد شما را جانشين آنان كرد، و در اين سرزمين جايتان داد كه در دشت‌هاى آن كاخ‌ها مى‌سازيد و از كوه‌ها خانه‌هايى مى‌تراشيد. پس نعمت‌هاى خدا را به ياد آوريد و در اين سرزمين سر به فساد برنداريد'),
(1029, 7, 75, 'سران قوم كه سركشى مى‌كردند، به گروهى از ايمان آورندگان كه ضعيف شمرده مى‌شدند، گفتند: آيا مطمئن هستيد كه صالح از طرف پروردگارش فرستاده شده است؟ گفتند: بى‌ترديد ما به آنچه وى بدان رسالت يافته مؤمن هستيم'),
(1030, 7, 76, 'آنان كه گردنكشى كردند گفتند: ما به چيزى كه شما به آن ايمان آورده‌ايد، كافريم'),
(1031, 7, 77, 'پس آنها شتر را پى كردند و [بدين‌سان‌] از فرمان پروردگار خويش سرباز زدند و گفتند: اى صالح! اگر از پيامبران هستى، آنچه را به ما وعده مى‌دهى براى ما بياور'),
(1032, 7, 78, 'پس زمين لرزه آنها را بگرفت و در خانه‌هايشان در جاى مردند'),
(1033, 7, 79, 'پس [صالح‌] از ايشان روى برتافت و گفت: اى قوم من! به راستى من پيام پروردگارم را به شما رساندم و شما را نصيحت كردم، و ليكن شما خيرخواهان را دوست نمى‌داريد'),
(1034, 7, 80, 'و لوط را [فرستاديم‌] كه به قوم خود گفت: آيا عمل زشتى را مرتكب مى‌شويد كه پيش از شما هيچ يك از جهانيان مرتكب نشده‌اند'),
(1035, 7, 81, 'شما از روى شهوت، به جاى زنان به سراغ مردان مى‌رويد، آرى شما مردمى تجاوز پيشه‌ايد'),
(1036, 7, 82, 'ولى پاسخ قومش جز اين نبود كه گفتند: آنها را از شهر خود بيرون كنيد آنها كسانى‌اند كه به پاكى تظاهر مى‌كنند'),
(1037, 7, 83, 'پس او را با كسانش نجات داديم، جز همسرش را كه از واپس ماندگان شد'),
(1038, 7, 84, 'و بر سر آنان باران [عذاب‌] بارانديم، پس ببين عاقبت گناهكاران چگونه بود'),
(1039, 7, 85, 'و به سوى [قوم‌] مدين برادرشان شعيب را [فرستاديم‌]. گفت: اى قوم من! خدا را بپرستيد كه براى شما هيچ معبودى جز او نيست. همانا شما را از جانب پروردگارتان حجت روشنى آمده است. پس پيمانه و وزن را تمام دهيد و از كالاى مردم نكاهيد و در زمين از پس اصلاح آن فسا'),
(1040, 7, 86, 'و بر سر هر راهى كمين نكنيد كه مردم را بترسانيد و كسى را كه به خدا ايمان آورده از راه خدا بازداريد و در پى انحراف راه او باشيد، و به ياد آريد وقتى را كه اندك بوديد پس او افزونتان كرد، و بنگريد كه عاقبت كار اهل فساد چگونه شد'),
(1041, 7, 87, 'و اگر طايفه‌اى از شما به اين آيين كه من براى آن فرستاده شده‌ام، ايمان آورده و طايفه ديگر ايمان نياورده‌اند، پس صبر كنيد تا خدا ميان ما داورى كند كه او بهترين داوران است'),
(1042, 7, 88, 'سران قوم او كه سركشى كردند، گفتند اى شعيب! به يقين تو و كسانى را كه با تو ايمان آورده‌اند از شهر خود بيرون مى‌كنيم، مگر آن كه به آيين ما بازگرديد. گفت: حتى اگر دوست نداشته باشيم'),
(1043, 7, 89, 'اگر پس از آن كه خدا ما را از آيين شما رهانيده، به آن بازگرديم، قطعا بر خدا دروغ بسته‌ايم، و ما را نسزد كه به آن بازگرديم، مگر آن كه خدا، پروردگار ما بخواهد. علم پروردگارمان همه چيز را فرا گرفته است. ما بر خدا توكل كرده‌ايم. بار الها! ميان ما و قومما'),
(1044, 7, 90, 'مهتران قوم او كه كافر بودند گفتند: اگر از شعيب پيروى كنيد، در اين صورت بى‌ترديد زيانكار خواهيد شد'),
(1045, 7, 91, 'پس زلزله آنها را در گرفت و صبحگاهان در خانه‌هاى خود در جاى مردند'),
(1046, 7, 92, 'آنها كه شعيب را تكذيب كردند، گويى هرگز در آن ديار نبوده‌اند. آنها كه شعيب را تكذيب كردند، همانا زيانكاران بودند'),
(1047, 7, 93, 'آن‌گاه [شعيب‌] از آنان روى گرداند و گفت: اى قوم من! حقيقت اين است كه من پيام‌هاى پروردگارم را به شما رساندم و اندرزتان دادم، پس چگونه بر قوم كافر تأسف بخورم'),
(1048, 7, 94, 'و ما در هر [شهر و] ديارى كه پيامبرى فرستاديم، ساكنان آن را به سختى و بيمارى گرفتار كرديم تا مگر زارى كنند [و رو به خدا آرند]'),
(1049, 7, 95, 'آن‌گاه به جاى محنت‌ها خوشى آورديم تا آن كه وضعشان خوب شد [باز متنبه نشدند] و گفتند: البته به پدران ما [هم از گردش روزگار] رنج و خوشى مى‌رسيد. پس ناگهان، در آن حال كه غافل بودند ايشان را بگرفتيم'),
(1050, 7, 96, 'و اگر اهل شهرها ايمان آورده و تقوا پيشه مى‌كردند، بركاتى از آسمان و زمين به رويشان مى‌گشوديم، ولى آنها تكذيب كردند پس ما نيز ايشان را به سزاى آنچه مى‌كردند گرفتار ساختيم'),
(1051, 7, 97, 'مگر مردم اين شهرها ايمنند كه عذاب ما شب هنگام زمانى كه در خوابند سراغشان بيايد'),
(1052, 7, 98, 'و آيا مردم اين ديار ايمنند كه عذاب ما در روز روشن به سراغشان بيايد در حالى كه به بازى سرگرم‌اند'),
(1053, 7, 99, 'مگر از مكر و تدبير خدا ايمن شده‌اند؟ كه جز زيانكاران از تدبير خدا ايمن ننشينند'),
(1054, 7, 100, 'آيا براى آنان كه اين سرزمين را از پيشينيان به ارث برده‌اند روشن نكرده است كه اگر بخواهيم آنها را به سزاى گناهانشان عقوبت مى‌كنيم و بر دل‌هايشان مهر مى‌نهيم تا ديگر نشنوند'),
(1055, 7, 101, 'اين شهرهاست كه ما خبرهاى آن را بر تو نقل مى‌كنيم. به حقيقت پيامبرانشان با حجت‌هاى روشن به سويشان آمدند، ولى آنها به آنچه قبلا تكذيب كرده بودند ايمان نمى‌آوردند. اين گونه خداوند بر دل‌هاى كافران مهر مى‌نهد'),
(1056, 7, 102, 'و ما بيشترشان را پاى بند تعهد نيافتيم و جدّا بيشترشان را نافرمان يافتيم'),
(1057, 7, 103, 'آن‌گاه از پس ايشان موسى را با آيات خويش به سوى فرعون و سران قومش فرستاديم، ولى در حق آن [آيات‌] ستم كردند. اينك بنگر كه عاقبت مفسدان چگونه گشت'),
(1058, 7, 104, 'و موسى گفت: اى فرعون! بى‌ترديد من فرستاده‌اى از جانب پروردگار عالميانم'),
(1059, 7, 105, 'شايسته است كه در باره‌ى خداوند جز حق نگويم. همانا براى شما معجزه‌اى از جانب پروردگارتان آورده‌ام، پس بنى اسرائيل را با من بفرست'),
(1060, 7, 106, 'گفت: اگر راست مى‌گويى و معجزه‌اى آورده‌اى، آن را بياور'),
(1061, 7, 107, 'پس عصاى خويش بيفكند كه در دم اژدهايى واقعى شد'),
(1062, 7, 108, 'و دست خود [از گريبان‌] برآورد كه ناگه براى ناظران درخشان گرديد'),
(1063, 7, 109, 'سران قوم فرعون گفتند: به راستى اين جادوگرى ماهر است'),
(1064, 7, 110, 'مى‌خواهد شما را از سرزمينتان بيرون كند. اينك چه رأى مى‌دهيد'),
(1065, 7, 111, 'گفتند: [كار] او و برادرش را به تأخير انداز، و مأموران جمع‌آورى را به شهرها بفرست'),
(1066, 7, 112, 'تا هر چه ساحر ماهر است نزد تو آورند'),
(1067, 7, 113, 'جادوگران به حضور فرعون آمدند. گفتند: اگر ما غالب شديم آيا حتما مزدى خواهيم داشت'),
(1068, 7, 114, 'گفت: آرى، البته شما از مقربان خواهيد بود'),
(1069, 7, 115, 'گفتند: اى موسى! تو اول مى‌افكنى يا ما بيفكنيم'),
(1070, 7, 116, 'گفت: شما بيفكنيد. و چون افكندند، مردم را چشم بندى كردند و آنها را ترساندند و سحر بزرگى آوردند'),
(1071, 7, 117, 'و به موسى وحى كرديم كه عصاى خويش بيفكن. پس همان دم ساخته‌هاى آنها را [يك به يك‌] مى‌بلعيد'),
(1072, 7, 118, 'پس حقيقت ثابت شد و كارى كه مى‌كردند بر باد رفت'),
(1073, 7, 119, 'اين جا بود كه شكست خوردند و با خوارى برگشتند'),
(1074, 7, 120, 'و ساحران [بى‌اختيار] به سجده افتادند'),
(1075, 7, 121, 'گفتند: ما به پروردگار عالميان ايمان آورديم'),
(1076, 7, 122, 'پروردگار موسى و هارون'),
(1077, 7, 123, 'فرعون گفت: پيش از آن كه به شما رخصت دهم به او ايمان آورديد؟! حتما اين توطئه‌اى است كه در شهر به راه انداخته‌ايد تا ساكنانش را از آن بيرون كنيد، پس به زودى خواهيد دانست'),
(1078, 7, 124, 'حتما، و يقينا دست‌ها و پاهايتان را چپ و راست قطع مى‌كنم، آن‌گاه شما را جملگى به دار مى‌زنم'),
(1079, 7, 125, 'گفتند: [هر چه مى‌خواهى بكن‌] ما به سوى پروردگارمان باز مى‌گرديم'),
(1080, 7, 126, 'و تو از ما انتقام نمى‌گيرى مگر براى اين كه ما به آيات پروردگارمان- وقتى براى ما آمد- ايمان آورديم. بار الها! بر ما صبورى ببار و مسلمانمان بميران'),
(1081, 7, 127, 'سران قوم فرعون گفتند: آيا موسى و قوم او را وا مى‌گذارى كه در زمين فساد كنند و تو و خدايانت را واگذارد؟ گفت: به زودى پسرانشان را مى‌كشيم و زنانشان را زنده مى‌گذاريم كه بى‌ترديد ما بر آنان تسلط داريم'),
(1082, 7, 128, 'موسى به قوم خود گفت: از خداوند مدد بجوييد و استقامت ورزيد كه زمين از آن خداست، آن را به هر كس از بندگانش كه بخواهد به ارث مى‌دهد، و فرجام [نيكو] از آن پرهيزكاران است'),
(1083, 7, 129, 'گفتند: پيش از آن كه سوى ما بيايى و بعد از آن كه آمدى همواره آزار شده‌ايم. [موسى‌] گفت: اميد است پروردگارتان دشمن شما را هلاك كند و شما را در [اين‌] سرزمين جانشين [آنان‌] گرداند تا ببيند چگونه عمل مى‌كنيد'),
(1084, 7, 130, 'و به راستى فرعونيان را به خشكسالى و كاهش محصولات دچار كرديم تا به خود آيند'),
(1085, 7, 131, 'و چون ناز و نعمت به آنها مى‌رسيد مى‌گفتند: اين حق ماست، و چون گزندى به ايشان مى‌رسيد، به موسى و پيروان او فال بد مى‌زدند. آگاه باش كه بخت بد آنها نزد خداست ولى اكثرشان نمى‌فهمند'),
(1086, 7, 132, 'و گفتند: هرگاه كه معجزه‌اى براى ما بياورى كه به وسيله آن سحرمان كنى ما به تو ايمان نخواهيم آورد'),
(1087, 7, 133, 'پس طوفان و ملخ و شپش و وزغ‌ها و خون را كه معجزاتى از هم جدا بودند برايشان فرستاديم، باز هم سركشى كردند و قومى بزهكار بودند'),
(1088, 7, 134, 'و چون بلا بر آنها واقع شد، گفتند: اى موسى! پروردگار خويش را بدان عهدى كه با تو دارد براى ما بخوان كه: اگر عذاب را از ما بردارى قطعا به تو ايمان مى‌آوريم و حتما بنى اسرائيل را با تو مى‌فرستيم'),
(1089, 7, 135, 'اما هنگامى كه عذاب را تا موعدى كه آنان به آن مى‌رسيدند از آنها برمى‌داشتيم، باز هم پيمان خود مى‌شكستند'),
(1090, 7, 136, 'ما هم سر انجام از آنان انتقام گرفتيم و در دريا غرقشان ساختيم، چرا كه آيات ما را تكذيب كردند و از آن غفلت مى‌ورزيدند'),
(1091, 7, 137, 'و به آن گروهى كه تضعيف مى‌شدند، مشرق‌ها و مغرب‌هاى [آن‌] سرزمين را كه بركت داده بوديم واگذاشتيم و وعده‌ى نيك پروردگار تو در حق بنى اسرائيل، به پاس صبوريشان تحقق يافت و آنچه را كه فرعون و قوم او مى‌ساختند و آنچه را كه بر مى‌افراشتند ويران كرديم'),
(1092, 7, 138, 'و بنى اسرائيل را از دريا عبور داديم. پس [در راه‌] به قومى رسيدند كه بت‌هاى خويش را پرستش مى‌كردند. گفتند: اى موسى! براى ما نيز معبودى بساز چونان كه آنها معبودهايى دارند. گفت: به راستى شما قومى هستيد كه نادانى مى‌كنيد'),
(1093, 7, 139, 'بى‌ترديد اينها [كه مى‌بينيد] كارشان بر باد است و آنچه مى‌كنند باطل است'),
(1094, 7, 140, 'گفت: آيا غير از خدا معبودى براى شما بجويم در صورتى كه او شما را بر عالميان برترى داد'),
(1095, 7, 141, 'و [فراموش نكنيد] هنگامى را كه شما را از فرعونيان نجات داديم. آنان شما را عذاب سختى مى‌كردند، پسرانتان را مى‌كشتند و زنانتان را زنده مى‌گذاشتند، و در اين [گرفتارى‌ها] از پروردگارتان امتحان بزرگى بود'),
(1096, 7, 142, 'و با موسى سى شب وعده كرديم و آن را به ده شب ديگر كامل نموديم پس ميقات پروردگارش در چهل شب به سر آمد. و موسى به برادرش هارون گفت: ميان قوم من نايب من باش و به اصلاح [كارشان‌] پرداز و طريق مفسدان را پيروى مكن'),
(1097, 7, 143, 'و چون موسى به وعده‌گاه ما آمد و پروردگارش با او سخن گفت، عرض كرد: بار الها! خود را به من بنماى كه تو را بنگرم. گفت: هرگز مرا نخواهى ديد، ليكن به اين كوه بنگر، پس اگر در جايش برقرار ماند مرا خواهى ديد، سپس همين كه پروردگار او بر كوه تجلّى كرد، آن را'),
(1098, 7, 144, 'گفت: اى موسى! من تو را به رسالت‌هاى خويش و سخن گفتنم [با تو]، بر مردم برگزيدم، پس آنچه را به تو دادم بگير و از شاكران باش'),
(1099, 7, 145, 'و براى وى در الواح از هرگونه اندرزى و شرحى براى هر چيز نوشتيم [و گفتيم:] پس آن را محكم بگير و قوم خود را فرمان ده كه بهترينش را به كار گيرند، و به زودى جايگاه فاسقان را به شما نشان خواهم داد'),
(1100, 7, 146, 'و به زودى كسانى را كه در [اين‌] سرزمين به ناحق بزرگى مى‌كنند از آيات خود منصرف خواهم كرد. آنها اگر همه معجزه‌ها را هم ببينند به آن ايمان نمى‌آورند، و اگر راه رشد را ببينند آن را پيش نمى‌گيرند، و اگر راه ضلالت را ببينند آن را پيش مى‌گيرند. اين از آن'),
(1101, 7, 147, 'و كسانى كه آيات ما و ديدار آخرت را تكذيب كردند، اعمالشان تباه است. آيا جز آنچه را كه عمل مى‌كردند كيفر مى‌بينند'),
(1102, 7, 148, 'و قوم موسى در غياب او از زيورهاى خود تنديس گوساله‌اى ساختند كه صداى گاو داشت. مگر نمى‌ديدند كه با آنها سخن نمى‌گويد و به راهى هدايتشان نمى‌كند؟ آن را [معبود] گرفتند در حالى كه ستمكار بودند'),
(1103, 7, 149, 'و چون پشيمان شدند و ديدند كه واقعا گمراه شده‌اند گفتند: اگر خدايمان بر ما رحم نياورد و ما را نيامرزد قطعا از زيانكاران خواهيم شد'),
(1104, 7, 150, 'و چون موسى خشمناك و اندوهگين به نزد قوم خود بازگشت، گفت: پس از من بد جانشينانى برايم بوديد! آيا در باره‌ى امر پروردگار خويش [و تمديد ميعاد] عجله كرديد؟ و الواح را بينداخت و سر برادرش را گرفته به سوى خود مى‌كشيد. [هارون‌] گفت: اى پسر مادرم! اين قوم،'),
(1105, 7, 151, '[موسى‌] گفت: بار الها! من و برادرم را بيامرز و ما را در [پناه‌] رحمت خود در آور، و تو از همه مهربانان مهربان‌ترى'),
(1106, 7, 152, 'همانا كسانى كه گوساله را پرستيدند به زودى خشمى از پروردگارشان و ذلتى در زندگى دنيا به آنان خواهد رسيد، و دروغسازان را اين گونه سزا مى‌دهيم'),
(1107, 7, 153, 'و كسانى كه كارهاى بد كردند و پس از آن توبه آوردند و مؤمن شدند، قطعا پروردگار تو از پى آن آمرزنده و مهربان است'),
(1108, 7, 154, 'و چون خشم موسى فرو نشست، الواح را برداشت، و در نوشته‌هاى آن براى خدا ترسان هدايت و رحمتى بود'),
(1109, 7, 155, 'و موسى از ميان قوم خود هفتاد مرد براى وعده‌گاه ما برگزيد، و چون زلزله آنها را گرفت، گفت: پروردگارا! اگر مى‌خواستى آنها و مرا پيش از اين هلاك مى‌كردى. آيا ما را به خاطر آنچه سفيهان ما كرده‌اند هلاك مى‌كنى؟ اين جز امتحان تو نيست هر كه را خواهى بدان گم'),
(1110, 7, 156, 'و در اين دنيا و در آخرت براى ما نيكى بنويس، كه ما به تو بازگشته‌ايم. گفت: عذاب خويش را به هر كه خواهم مى‌رسانم، و رحمتم همه چيز را فرا گرفته است. پس به زودى آن را براى كسانى كه تقوا پيشه‌اند و زكات مى‌دهند، و كسانى كه به آيات ما ايمان دارند، مقرر خو'),
(1111, 7, 157, 'همان كسانى كه از اين رسول، پيامبر درس ناخوانده پيروى مى‌كنند كه [وصف‌] او را نزد خويش در تورات و انجيل مكتوب مى‌يابند، كه آنها را به كار پسنديده فرمان مى‌دهد و از كار ناپسند منع مى‌كند و پاكيزه‌ها را برايشان حلال و پليدى‌ها را بر آنان حرام مى‌كند،'),
(1112, 7, 158, 'بگو: اى مردم! من فرستاده‌ى خدا به سوى همه شما هستم همان كه فرمانروايى آسمان‌ها و زمين از آن اوست. هيچ معبودى جز او نيست، زنده مى‌كند و مى‌ميراند. پس به خدا و رسول او، آن پيامبر امّى كه به خدا و كلمات او ايمان دارد، ايمان بياوريد و از او پيروى كنيد،'),
(1113, 7, 159, 'و از قوم موسى گروهى هستند كه به حق هدايت مى‌كنند و به آن حكم مى‌دهند'),
(1114, 7, 160, 'و آنان را به دوازده عشيره كه هر يك شاخه‌اى بود تقسيم كرديم، و به موسى آن‌گاه كه قومش از او آب خواستند وحى كرديم كه با عصايت به سنگ بزن. آن‌گاه از آن دوازده چشمه جوشيد كه هر گروهى آبخور خويش را بشناخت و ابر را سايبانشان نموديم و برايشان منّ و سلوى نا'),
(1115, 7, 161, 'و آن‌گاه كه به ايشان گفته شد: در اين شهر مسكن گزينيد و در هر جا از آن خواستيد بخوريد و بگوييد: [بار الها!] گناهان ما را فرو ريز، و از اين دروازه فروتنانه وارد شويد تا گناهانتان را بيامرزيم و البته به پاداش نيكوكاران خواهيم افزود'),
(1116, 7, 162, 'پس كسانى از آنها كه ستم كردند، سخن را به غير آنچه كه به آنها گفته شده بود تغيير دادند. پس ما به سزاى آن ستم‌ها كه مى‌كردند، از آسمان عذابى برايشان فرستاديم'),
(1117, 7, 163, 'و از آنها در باره‌ى [ساكنان‌] شهرى كه در ساحل دريا بود سؤال كن، آنگاه كه در روز شنبه سنت مى‌شكستند چون روز شنبه ماهيان آنها روى آب ظاهر مى‌شدند و روزهاى غير شنبه به طرف آنها نمى‌آمدند. بدين‌سان ما آنان را به خاطر آن كه نافرمانى مى‌كردند آزمايش و گرف'),
(1118, 7, 164, 'و آنگاه كه گروهى از ايشان [به نهى‌كنندگان از منكر] گفتند: چرا قومى را پند مى‌دهيد كه خداوند هلاك كننده يا عذاب كننده‌ى آنان به عذابى شديد است؟ گفتند: [پند دادن ما] براى اعتذار [و رفع مسئوليت‌] در پيشگاه پروردگارتان است و به اين اميد كه پرهيزكارى كنن'),
(1119, 7, 165, 'و چون پندى را كه به ايشان داده شده بود از ياد بردند، ما نهى كنندگان از منكر را نجات داديم و ستمكاران را به عذاب بدى گرفتار نموديم، چرا كه نافرمانى مى‌كردند'),
(1120, 7, 166, 'و چون در برابر آنچه از آن نهى شده بودند، سركشى كردند، به آنها گفتيم: بوزينگانى رانده شده باشيد'),
(1121, 7, 167, 'و [ياد آر] آن‌گاه كه پروردگارت اعلام نمود تا روز قيامت بر يهوديان كسى را خواهد گماشت كه به آنها سخت‌ترين عذاب را بچشاند. قطعا پروردگار تو زود كيفر است و همو آمرزگار مهربان است'),
(1122, 7, 168, 'و آنان را در زمين به صورت گروه‌هايى پراكنده ساختيم بعضى از آنان صالح و بعضى پايين‌ترند، و آنها را به خوشى‌ها و ناخوشى‌ها آزموديم، باشد كه [به راه حق‌] بازگردند'),
(1123, 7, 169, 'آن‌گاه بعد از آنان نسلى به جايشان نشستند و وارث كتاب [آسمانى‌] شدند كه متاع دنياى پست را [به نامشروع‌]. مى‌گيرند و مى‌گويند: ما بخشيده خواهيم شد، و اگر متاع ديگرى همانند آن به ايشان برسد باز آن را مى‌گيرند. آيا از آنها در كتاب، كه محتواى آن را آموخت'),
(1124, 7, 170, 'و كسانى كه به كتاب آسمانى تمسك مى‌جويند و نماز برپا مى‌دارند [بدانند كه‌] ما پاداش اصلاحگران را ضايع نخواهيم كرد'),
(1125, 7, 171, 'و [ياد كن‌] هنگامى را كه كوه را كنديم و بالاى سرشان قرار داديم كه گويى سايبانى بود، و پنداشتند كه بر سرشان خواهد افتاد. [گفتيم:] آنچه به شما داده‌ايم با جدّيت بگيريد و محتواى آن را به خاطر بسپاريد، اميد كه پرهيزكار شويد'),
(1126, 7, 172, 'و آن‌گاه كه پروردگار تو از پسران آدم، از پشت‌هايشان، نسل و نژاد آنها را برگرفت و آنها را بر خودشان گواه كرد كه: آيا من پروردگار شما نيستم؟ [آنها به زبان فطرت‌] گفتند: چرا! گواهى داديم. تا در روز رستاخيز نگوييد ما از آن غافل بوديم'),
(1127, 7, 173, 'يا بگوييد: همانا پدران ما پيش از اين شرك آوردند و ما نسلى [دنباله‌رو] از پى ايشان بوديم، آيا ما را به سبب كارى كه باطل انديشان كرده‌اند مجازات مى‌كنى'),
(1128, 7, 174, 'و اين گونه، آيات را [به روشنى‌] شرح مى‌دهيم، باشد كه [به راه حق‌] بازگردند'),
(1129, 7, 175, 'و خبر آن كس (بلعم باعورا) را كه آيات خويش (اجابت دعا) را به او داده بوديم برايشان بخوان كه از آن عارى گشت، و شيطان در پى او افتاد پس از گمراهان شد'),
(1130, 7, 176, 'و اگر مى‌خواستيم [مقام‌] وى را با اين آيات بالا مى‌برديم، ولى او به زمين دل بست و از هواى نفس خويش پيروى كرد. پس حكايت وى، حكايت سگ است كه اگر بر آن بتازى، زبان برآورد و اگر رهايش كنى باز هم زبان برآورد. اين حكايت قومى است كه آيات ما را تكذيب كردند'),
(1131, 7, 177, 'زشت است حكايت قومى كه آيات ما را تكذيب نمودند و به خود ستم مى‌كردند'),
(1132, 7, 178, 'هر كه را خدا هدايت كند، او هدايت يافته است و هر كه را گمراه كند، هم آنان زيانكارند'),
(1133, 7, 179, 'همانا بسيارى از جنيان و آدميان را [گويى‌] براى دوزخ آفريده‌ايم، [چرا كه‌] دل‌هايى دارند كه با آن فهم نمى‌كنند و چشم‌هايى دارند كه با آن نمى‌بينند، و گوش‌هايى دارند كه با آن نمى‌شنوند. آنها چون چهارپايانند بلكه گمراه‌ترند، و آنان غافلانند'),
(1134, 7, 180, 'و زيباترين نام‌ها به خداوند تعلق دارد، پس او را بدانها بخوانيد، و كسانى را كه در نام‌هاى او بى‌راهه مى‌روند واگذاريد كه به زودى به سزاى آنچه مى‌كردند خواهند رسيد'),
(1135, 7, 181, 'و از كسانى كه بيافريديم گروهى هستند كه به حق هدايت مى‌كنند و به حق حكم مى‌دهند'),
(1136, 7, 182, 'و كسانى كه آيات ما را تكذيب كردند، اندك اندك، از جايى كه خود نفهمند به دامشان مى‌كشيم'),
(1137, 7, 183, 'و به آنها فرصت مى‌دهم، كه مكر و تدبير من بسى محكم است'),
(1138, 7, 184, 'آيا فكر نكردند كه يارشان [پيامبر] جنونى ندارد؟ او جز هشدار دهنده‌اى آشكار نيست'),
(1139, 7, 185, 'آيا در ملكوت آسمان‌ها و زمين و آنچه خدا آفريده ننگريستند، و اين كه شايد اجلشان نزديك شده باشد؟ پس به كدامين سخن، بعد از قرآن ايمان مى‌آورند'),
(1140, 7, 186, 'هر كه را خدا گمراه كند، براى او هيچ راهبرى نيست، و آنها را در طغيانشان سرگردان رها مى‌كند'),
(1141, 7, 187, 'از تو در باره‌ى قيامت مى‌پرسند كه چه وقت برپا مى‌شود؟ بگو: علم آن تنها نزد پروردگار من است و جز او آن را به موقع خود آشكار نمى‌سازد. [تحقق آن‌] در آسمان‌ها و زمين سنگين است و جز به طور ناگهانى به سراغتان نمى‌آيد. از تو چنان مى‌پرسند كه گويى تو از آ'),
(1142, 7, 188, 'بگو: من مالك سود و زيان خود نيستم، مگر آنچه خدا خواسته باشد، و اگر غيب مى‌دانستم سود فراوانى مى‌اندوختم و هيچ زيانى به من نمى‌رسيد. من صرفا براى قومى كه ايمان مى‌آورند بيم‌رسان و بشارت دهنده‌ام'),
(1143, 7, 189, 'اوست كه شما را از نفس واحدى آفريد و جفت وى را از او پديد آورد تا بدان آرام گيرد. پس چون با او بياميخت، بارى سبك گرفت و با آن مدتى سر كرد. و چون سنگين شد، هر دو از خداوند، پروردگار خويش خواستند كه: اگر فرزند شايسته‌اى به ما دهى قطعا از سپاسگزاران خوا'),
(1144, 7, 190, 'اما هنگامى كه فرزند [سالم و] شايسته‌اى به آنها داد، براى خدا در موهبتى كه داده بود شريكانى قائل شدند، ولى خدا از آنچه شريك او مى‌كنند بالاتر است'),
(1145, 7, 191, 'آيا موجوداتى را شريك او مى‌سازند كه چيزى نمى‌آفرينند و خود آفريده مى‌شوند'),
(1146, 7, 192, 'و نه مى‌توانند آنها را يارى دهند و نه خودشان را يارى كنند'),
(1147, 7, 193, 'و اگر آنها را به هدايت دعوت كنيد از شما پيروى نمى‌كنند. چه آنها را بخوانيد يا خاموش باشيد، بر شما يكسان است'),
(1148, 7, 194, 'همانا كسانى را كه سواى خدا مى‌خوانيد بندگانى مانند شمايند. پس آنها را بخوانيد، و اگر راست مى‌گوييد بايد شما را پاسخ دهند'),
(1149, 7, 195, 'آيا پاهايى دارند كه با آن راه روند؟ يا دست‌هايى دارند كه با آن كارى كنند؟ يا چشمانى دارند كه با آن ببينند؟ و يا گوش‌هايى دارند كه با آن بشنوند؟ بگو: شركاى خود را بخوانيد، سپس در حق من [هر چه مى‌توانيد] نيرنگ كنيد و مهلتم ندهيد'),
(1150, 7, 196, 'بى‌ترديد سرپرست من خداوند است كه اين كتاب را نازل كرده و او شايستگان را سرپرستى مى‌كند'),
(1151, 7, 197, 'و كسانى را كه به جاى او مى‌خوانيد، نه مى‌توانند شما را نصرت دهند و نه خودشان را يارى كنند'),
(1152, 7, 198, 'و اگر آنها را به هدايت بخوانيد نمى‌شنوند، و مى‌بينى [مانند مجسمه‌] به سوى تو مى‌نگرند ولى نمى‌بينند'),
(1153, 7, 199, 'عفو را پيشه كن و به نيكى فرمان ده و از نادان‌ها رخ برتاب'),
(1154, 7, 200, 'و اگر از شيطان وسوسه‌اى تو را برانگيخت، به خداوند پناه ببر [كه‌] بى‌شك او شنواى داناست'),
(1155, 7, 201, 'البته، هنگامى كه پرهيزكاران گرفتار وسوسه شيطانى شوند به ياد [خدا] مى‌افتند و ناگهان بينا و بصير مى‌شوند'),
(1156, 7, 202, 'و [اما مشركان‌] برادرانشان [از شياطين‌] آنها را به ضلالت مى‌كشانند و هيچ كوتاهى نمى‌كنند'),
(1157, 7, 203, 'و هرگاه براى آنان آيه‌اى نياورى، گويند: چرا [از پيش خود] آن را برنگزيدى؟ بگو: من فقط از چيزى پيروى مى‌كنم كه از پروردگارم به من وحى مى‌شود. اين [قرآن‌] روشنگرى‌هايى از پروردگار شما است و هدايت و رحمتى است براى قومى كه ايمان مى‌آورند'),
(1158, 7, 204, 'و چون قرآن خوانده شود، به آن گوش [جان‌] بسپاريد و خاموش باشيد تا مشمول رحمت شويد'),
(1159, 7, 205, 'و پروردگار خويش را در ضمير خود و به آرامى هر صبح و شام با خوف و زارى ياد كن و از غافلان مباش'),
(1160, 7, 206, 'به يقين، كسانى كه نزد پروردگار تو [و در مقام قرب معنوى‌] هستند از بندگى او سر نتابند و تسبيحش گويند و براى او سجده كنند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'از تو در باره‌ى غنيمت‌ها مى‌پرسند، بگو: غنيمت‌ها از آن خدا و رسول است. پس از خدا پروا داريد و روابط خود را اصلاح كنيد و از خدا و رسولش اطاعت كنيد اگر مؤمن هستيد'),
(1162, 8, 2, 'مؤمنان فقط آن كسانند كه چون ياد خدا شود دل‌هايشان ترسان شود و چون آيات او بر آنها تلاوت شود ايمانشان را بيفزايد و بر خداى خويش توكل كنند'),
(1163, 8, 3, 'همانان كه نماز را برپا دارند و از آنچه روزيشان داده‌ايم انفاق كنند'),
(1164, 8, 4, 'آنها مؤمن حقيقى هستند، و ايشان را نزد پروردگارشان منزلت‌ها و آمرزش و روزى كريمانه است'),
(1165, 8, 5, 'همان گونه كه پروردگارت تو را [براى جنگ بدر] به حق از خانه‌ات بيرون فرستاد ولى گروهى از مؤمنان به شدت كراهت داشتند'),
(1166, 8, 6, 'آنها پس از روشن شدن حق [و ضرورت جهاد] باز با تو در باره آن مجادله مى‌كنند گويى به سوى مرگ رانده مى‌شوند و خود مى‌نگرند'),
(1167, 8, 7, 'و [فراموش نكنيد] آن زمان را كه خداوند به شما وعده مى‌داد كه يكى از آن دو گروه نصيب شماست، ولى شما دوست داشتيد [كاروان‌] غير مسلح نصيب شما گردد، و خدا مى‌خواست كه با سخنان خويش حق را پايدار سازد و ريشه كافران را بركند'),
(1168, 8, 8, 'تا حق را تثبيت كند و باطل را زايل گرداند، هر چند مجرمان خوش نداشته باشند'),
(1169, 8, 9, '[ياد آوريد] هنگامى را كه از خداى خود كمك مى‌خواستيد، پس او دعاى شما را اجابت كرد كه من با هزار فرشته كه از پى يكديگر در آيند مددكار شمايم'),
(1170, 8, 10, 'و خداوند آن را جز بشارتى براى شما قرار نداد، تا دل‌هاى شما به آن اطمينان يابد، و نصرت و پيروزى تنها از جانب خداست. [و] مسلما خداوند شكست‌ناپذير حكيم است'),
(1171, 8, 11, 'و آن‌گاه كه شما را بدان خواب سبك كه امانى از جانب وى بود فرو مى‌پوشانيد، و از آسمان بارانى بر شما مى‌فرستاد كه با آن پاكتان كند و آلودگى شيطان را از شما بزدايد و دل‌هايتان را محكم و گام‌هايتان را بدان استوار سازد'),
(1172, 8, 12, 'آن‌گاه كه پروردگارت به فرشتگان وحى مى‌كرد كه من با شمايم، پس مؤمنان را مقاومت بخشيد به زودى در دل‌هاى كافران هراس خواهم افكند، و فراز گردن‌ها [يشان‌] را بزنيد و سر انگشتانشان را قطع كنيد'),
(1173, 8, 13, 'اين از آن روست كه آنها با خدا و رسول او به مخالفت برخاستند، و هر كه با خدا و رسولش به مخالفت برخيزد مسلما خداوند سخت كيفر است'),
(1174, 8, 14, 'اينها [عقوبت‌] است پس آن را بچشيد و [بدانيد] كه براى كافران عذاب آتش است'),
(1175, 8, 15, 'اى مؤمنان! هنگامى كه با انبوه كافران رو به رو شديد، هرگز به آنها پشت نكنيد'),
(1176, 8, 16, 'و هر كس در آن هنگام به آنان پشت كند- مگر آن كه با هدف كناره‌جويى براى نبرد مجدد يا پيوستن به گروه [خودى‌] باشد- [چنين كسى‌] قطعا به خشم الهى گرفتار گشته و جايگاه او دوزخ است و بد سرنوشتى است'),
(1177, 8, 17, 'اين شما نبوديد كه آنها را كشتيد بلكه خداوند آنها را كشت، و وقتى [ريگ‌ها را] انداختى اين تو نبودى كه انداختى بلكه خدا بود كه انداخت، تا مؤمنان را بدين وسيله به امتحان خوبى از جانب خود بيازمايد. همانا خداوند شنواى داناست'),
(1178, 8, 18, '[دليلش‌] اين بود، و اين كه خداوند سست كننده‌ى نقشه كافران است'),
(1179, 8, 19, '[اى مشركان!] اگر پيروزى مى‌خواهيد، اينك پيروزى به سراغ شما آمده [و اسلام پيروز شده است نه شما] و اگر [از كفر] باز ايستيد همان براى شما بهتر است، و اگر باز گرديد [ما هم‌] باز مى‌گرديم و سپاه شما هر چند انبوه باشد، هرگز از هيچ جهت شما را بى‌نياز نمى‌كن'),
(1180, 8, 20, 'اى مؤمنان! از خدا و رسول او اطاعت كنيد و از او روى نگردانيد در حالى كه [سخنان او را] مى‌شنويد'),
(1181, 8, 21, 'و مانند كسانى نباشيد كه گفتند شنيديم ولى نمى‌شنيدند'),
(1182, 8, 22, 'به راستى بدترين جنبندگان نزد خداوند ناشنوايان گنگى هستند كه تعقل نمى‌كنند'),
(1183, 8, 23, 'و اگر خداوند خيرى در آنها سراغ داشت گوششان را شنوا مى‌كرد، و اگر هم شنوا مى‌كرد باز با حال اعراض پشت مى‌كردند'),
(1184, 8, 24, 'اى مؤمنان! نداى خدا و پيامبر را آن‌گاه كه شما را به پيامى حياتبخش مى‌خواند، اجابت كنيد، و بدانيد كه خدا ميان انسان و دل او حايل مى‌گردد [و همه چيز را مى‌داند] و به حضور او گرد آورده مى‌شويد'),
(1185, 8, 25, 'و از فتنه‌اى بترسيد كه تنها دامن ستمگران شما را نمى‌گيرد، و بدانيد كه خداوند سخت كيفر است'),
(1186, 8, 26, 'و [ياد كنيد] هنگامى را كه در [اين‌] سرزمين گروهى اندك و مستضعف بوديد و بيم آن داشتيد كه مردم شما را از ميان بردارند ولى او شما را پناه داد و به نصرت خويش ياريتان كرد و از نعمت‌هاى پاكيزه به شما روزى داد، باشد كه شاكر باشيد'),
(1187, 8, 27, 'اى مؤمنان! به خدا و پيامبر خيانت نورزيد و دانسته به امانات خود خيانت نكنيد'),
(1188, 8, 28, 'و بدانيد كه اموال و اولادتان [وسيله‌] آزمايش است و پاداش بزرگ نزد خداوند است'),
(1189, 8, 29, 'اى مؤمنان! اگر از خداوند پروا كنيد، براى شما نيروى تشخيص حق از باطل قرار مى‌دهد و بدى‌هايتان را مى‌پوشاند و شما را مى‌آمرزد و خداوند صاحب بخشش بزرگ است'),
(1190, 8, 30, 'و [يادآور] وقتى را كه كافران در حق تو نيرنگ مى‌كردند تا تو را در بند كشند يا بكشند يا بيرونت كنند، و آنها نيرنگ مى‌كردند و خدا هم نيرنگ مى‌كرد و خداوند از همه نيرنگ كنندگان ماهرتر است'),
(1191, 8, 31, 'و چون آيات ما بر آنها تلاوت شود مى‌گويند: به خوبى شنيديم، اگر مى‌خواستيم ما نيز همانند آن را مى‌گفتيم، اين [قرآن‌] جز افسانه‌هاى پيشينيان نيست'),
(1192, 8, 32, 'و [يادآر] هنگامى را كه گفتند: پروردگارا! اگر اين [كتاب‌] حق و از سوى توست، بر ما از آسمان سنگى ببار يا ما را عذاب دردناكى بياور'),
(1193, 8, 33, 'و تا تو در ميان آنها هستى خدا بر آن نيست كه آنها را عذاب كند و تا آنها استغفار مى‌كنند خدا عذاب كننده‌ى آنها نيست'),
(1194, 8, 34, 'و چرا خدا عذابشان نكند، در حالى كه مردم را از مسجد الحرام باز مى‌دارند در صورتى كه متوليان آن نبوده‌اند. متوليان مسجد جز پارسايان نمى‌باشند ولى اكثر آنان نمى‌فهمند'),
(1195, 8, 35, 'و نماز ايشان در كنار خانه خدا جز سوت كشيدن و كف زدن نبود پس به سزاى انكارتان عذاب را بچشيد'),
(1196, 8, 36, 'همانا كافران اموال خود را صرف مى‌كنند تا مردم را از راه خدا باز دارند و باز هم [در آينده‌] خرج خواهند كرد و عاقبت، حسرتى براى آنان خواهد گشت و سر انجام مغلوب مى‌شوند. و كسانى كه كافر شدند به سوى جهنم گردآورى خواهند شد'),
(1197, 8, 37, 'تا خداوند ناپاك را از پاك جدا سازد، و ناپاك‌ها را روى هم گذارد و هم را انباشته كند و يكجا در دوزخ افكند. اينانند كه زيانكارانند'),
(1198, 8, 38, 'به كافران بگو: اگر باز ايستند، گذشته آنها بخشوده خواهد شد، و اگر بازگردند سنت خدا در حق گذشتگان جارى شده است، [در باره‌ى آنها نيز جارى مى‌شود]'),
(1199, 8, 39, 'و با آنها بجنگيد تا ديگر فتنه‌اى نباشد و دين يكسره از آن خدا گردد. پس اگر [از كفر] باز ايستادند، بى‌ترديد خداوند به آنچه انجام مى‌دهند بيناست'),
(1200, 8, 40, 'و اگر روى گرداندند، بدانيد كه خداوند مولاى شماست. كه نيكو مولا و نيكو ياورى است'),
(1201, 8, 41, 'و بدانيد هر گونه غنيمتى به دست آورديد، يك پنجم آن براى خدا و پيامبر و از آن خويشاوندان و يتيمان و تنگدستان و در راه ماندگان است، اگر به خدا و به آنچه بر بنده‌ى خود در روز جدايى [حق از باطل‌]، روزى كه آن دو گروه با هم رو در رو شدند، نازل كرديم ايمان آ'),
(1202, 8, 42, 'آن‌گاه كه شما نزديك‌تر [به مدينه‌] بوديد و آنها دورتر، و كاروان [تجارى‌] پايين‌تر از شما بود، و اگر شما با يكديگر وعده‌ى [جنگ‌] گذاشته بوديد، قطعا در باره‌ى قرار جنگ [به خاطر كثرت دشمن‌] اختلاف مى‌كرديد، ولى [چنين شد] تا خدا كارى را كه مى‌بايست انجا'),
(1203, 8, 43, '[ياد كن‌] آن‌گاه كه خداوند تعداد آنان را در خوابت به تو اندك نشان مى‌داد و اگر شمارشان را بسيار نشان مى‌داد مسلما سست مى‌شديد و حتما در كار [جنگ‌] منازعه مى‌كرديد، ولى خدا [شما را] به سلامت داشت، چرا كه او به راز دل‌ها آگاه است'),
(1204, 8, 44, 'و آن‌گاه در هنگامى كه با هم رو به رو شديد، آنها را در چشم شما كم نشان داد و شما را [نيز] در چشم آنها كم مى‌نمود، [كه جنگ اتفاق افتد] تا خداوند كارى را كه بايد انجام گيرد تحقق بخشد [و دشمن شكست بخورد] و كارها به خداوند باز مى‌گردد'),
(1205, 8, 45, 'اى كسانى كه ايمان آورده‌ايد! چون با گروهى [از دشمن‌] رويارو شديد پايدارى كنيد و خدا را بسيار ياد كنيد، باشد كه رستگار شويد'),
(1206, 8, 46, 'از خدا و رسولش اطاعت كنيد و با هم نزاع نكنيد كه سست شويد و هيبت شما از بين برود، و صبر پيشه كنيد كه خدا با صابران است'),
(1207, 8, 47, 'و مانند كسانى نباشيد (كفار قريش) كه با سركشى و خودنمايى به مردم از خانه‌هايشان [به سوى بدر] خارج شدند و [مردم را] از راه خدا باز مى‌داشتند، و خدا به آنچه مى‌كنند احاطه دارد'),
(1208, 8, 48, 'و [ياد كن‌] هنگامى را كه شيطان اعمال آنها را برايشان بيار است و گفت: امروز كسى از اين مردم بر شما پيروز نخواهد شد، و من يار و پناه شمايم. پس همين كه دو گروه، رويارو شدند [شيطان‌] به عقب برگشت و گفت: من از شما بيزارم، من [از حضور فرشتگان‌] چيزى را مى‌'),
(1209, 8, 49, 'آن‌گاه كه منافقان و كسانى كه در دل‌هايشان بيمارى است مى‌گفتند: اين [مسلمان‌] ها را دينشان مغرور كرده است. و هر كه بر خدا توكل كند، به يقين خداوند شكست‌ناپذير حكيم است'),
(1210, 8, 50, 'كاش مى‌ديدى آن دم كه فرشتگان جان كافران را مى‌ستانند، [چگونه‌] بر صورت‌ها و پشت‌هايشان مى‌زنند و [مى‌گويند]: بچشيد عذاب سوزان را'),
(1211, 8, 51, 'اين به سزاى اعمالى است كه با دستان خويش پيش فرستاده‌ايد و خدا هرگز بر بندگان ستمگر نيست'),
(1212, 8, 52, 'مانند رسم فرعونيان و كسانى [رفتار مى‌كنند] كه پيش از ايشان بودند و به آيات خدا كافر شدند پس خدا به خاطر گناهانشان آنها را گرفتار ساخت بى‌گمان خدا نيرومند سخت كيفر است'),
(1213, 8, 53, 'اين بدان سبب است كه خداوند هيچ‌گاه نعمتى را كه بر قومى ارزانى داشته تغيير نمى‌دهد، مگر اين كه آنچه را در خود دارند تغيير دهند، و خداوند شنواى داناست'),
(1214, 8, 54, 'مانند رسم فرعونيان و كسانى [رفتار مى‌كنند] كه پيش از ايشان بودند، كه آيات پروردگار خويش را منكر شدند و ما هم به كيفر گناهانشان آنها را هلاك نموديم و فرعونيان را غرق ساختيم و همه آنان ستمكار بودند'),
(1215, 8, 55, 'بى‌ترديد، بدترين جنبندگان نزد خدا كسانى هستند كه كافر شدند پس آنها ايمان نمى‌آورند'),
(1216, 8, 56, 'همان كسانى كه از ايشان پيمان گرفتى، ولى هر بار پيمان خود را مى‌شكنند و هيچ پروا نمى‌كنند'),
(1217, 8, 57, 'پس اگر در جنگ بر آنان دست يافتى با [تاختن بر] آنان كسانى را كه پشت سر آنهايند متفرق ساز، باشد كه عبرت گيرند'),
(1218, 8, 58, 'و هرگاه از قومى بيم خيانت [در پيمان‌] داشتى، تو هم مثل خودشان پيمانشان را لغو كن، چرا كه خداوند خائنان را دوست نمى‌دارد'),
(1219, 8, 59, 'و كافران گمان نكنند كه جلو افتاده‌اند آنان هيچ وقت نمى‌توانند [خدا را] عاجز كنند'),
(1220, 8, 60, 'و تا مى‌توانيد در برابر آنها نيرو و اسب‌هاى آماده و زين كرده، بسيج كنيد تا با آن، دشمن خدا و دشمن خودتان و ديگر دشمنان را كه آنها را نمى‌شناسيد و خدا مى‌شناسد، بترسانيد و هر چه در راه خدا انفاق كنيد به تمامى به شما بازگردانده مى‌شود و بر شما ستم نمى‌'),
(1221, 8, 61, 'و اگر به صلح راضى شدند تو نيز بدان راضى باش و بر خدا تكيه كن كه او شنواى داناست'),
(1222, 8, 62, 'و اگر بخواهند تو را بفريبند خداوند تو را بس است. او كسى است كه تو را به نصرت خويش و به مؤمنان نيرو بخشيد'),
(1223, 8, 63, 'و دل‌هايشان را الفت داد، كه اگر تو دارايى زمين را يكجا هزينه مى‌كردى نمى‌توانستى دل‌هايشان را الفت دهى، و اين خدا بود كه ميان آنان الفت انداخت، [و] او شكست‌ناپذير حكيم است'),
(1224, 8, 64, 'اى پيامبر! خداوند و مؤمنانى كه پيرويت كردند تو را بس است'),
(1225, 8, 65, 'اى پيامبر! مؤمنان را به جهاد برانگيز. اگر از شما بيست نفر صابر باشند بر دويست نفر غالب مى‌شوند، و اگر از شما صد نفر باشند بر هزار نفر از كافران پيروز مى‌گردند، زيرا آنها قومى هستند كه نمى‌فهمند'),
(1226, 8, 66, 'اكنون خدا به شما تخفيف داد و معلوم داشت كه در شما ضعفى هست، پس اگر از شما صد نفر با استقامت باشند بر دويست نفر پيروز مى‌گردند، و اگر از شما هزار نفر باشند به خواست خدا بر دو هزار غلبه مى‌كنند، و خداوند با صابران است'),
(1227, 8, 67, 'هيچ پيامبرى مجاز نيست اسير داشته باشد [و نيروى خود را مشغول اسيران كند] تا آن وقت كه سيطره‌ى خود را در منطقه تثبيت كند. شما دنياى عرضى را مى‌خواهيد و خدا [براى شما] آخرت را مى‌خواهد، و خداوند شكست‌ناپذير حكيم است'),
(1228, 8, 68, 'اگر پيش‌تر حكم [مهلت‌] صادر نشده بود، مسلما در آنچه [از اسيران‌] گرفته‌ايد، بر شما عذابى بزرگ مى‌رسيد'),
(1229, 8, 69, 'اينك، از آنچه به غنيمت برده‌ايد، حلال و پاكيزه بخوريد و از خدا پروا داريد كه خداوند آمرزنده‌ى مهربان است'),
(1230, 8, 70, 'اى پيامبر! به اسيرانى كه در دست شما هستند بگو: اگر خدا در دل‌هايتان خير و نيكى سراغ داشته باشد، بهتر از آنچه از شما گرفته شده عطايتان مى‌كند و بر شما مى‌بخشد و خداوند آمرزگار مهربان است'),
(1231, 8, 71, 'و اگر بخواهند به تو خيانت كنند، البته پيش از اين نيز به خدا خيانت كرده‌اند پس [خدا تو را] بر آنها تمكّن و غلبه داد، و خداوند داناى حكيم است'),
(1232, 8, 72, 'بى‌ترديد، كسانى كه ايمان آوردند و هجرت نمودند و در راه خدا با مال و جان خود جهاد كردند و آنان كه [مهاجران را] پناه دادند و يارى نمودند، آنان دوستان و حامى يكديگرند، و كسانى كه ايمان آوردند ولى هجرت نكردند، براى شما در باره آنها هيچ دوستى و تعهدى نيس'),
(1233, 8, 73, 'و كافران نيز دوست و حامى يكديگرند. اگر [اين هميارى‌] را انجام ندهيد، فتنه و فساد بزرگى در زمين روى خواهد داد'),
(1234, 8, 74, 'و آنان كه ايمان آوردند و هجرت كردند و در راه خدا جهاد نمودند، و آنها كه پناه دادند و يارى كردند، آنانند كه به راستى مؤمنند و برايشان مغفرت و روزى كريمانه خواهد بود'),
(1235, 8, 75, 'و كسانى كه بعدا ايمان آوردند و همراه شما هجرت نمودند و جهاد كردند، آنان از شمايند، و خويشاوندان در كتاب خدا، بعضى‌شان بر ديگرى اولويت دارند. [و] خدا به هر چيزى داناست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'اين [اعلان‌] بيزارى است از طرف خدا و رسولش به آن مشركانى كه با ايشان پيمان بسته‌ايد'),
(1237, 9, 2, 'پس چهار ماه [ديگر با امنيّت‌] در [اين سر] زمين بگرديد و بدانيد كه شما هيچ‌گاه نمى‌توانيد خدا را عاجز كنيد و خدا رسواگر كافران است'),
(1238, 9, 3, 'و اين اعلانى است از ناحيه خدا و رسول او به مردم در روز حج اكبر كه خدا و رسولش از مشركان بيزارند. پس اگر توبه كنيد براى شما بهتر است، و اگر رو برتابيد بدانيد كه شما خدا را عاجز نتوانيد كرد و كافران را به عذابى دردناك بشارت ده'),
(1239, 9, 4, 'مگر آن مشركانى كه با آنها پيمان بستيد و سپس چيزى از پيمان شما را نكاستند و با هيچ كس بر ضد شما همدست نشدند، پس پيمان آنان را تا پايان مدتشان تمام كنيد. همانا خداوند پرهيزكاران را دوست دارد'),
(1240, 9, 5, 'پس چون ماه‌هاى حرام به سر آمد، هر جا مشركان را يافتيد بكشيد و دستگيرشان كنيد و به محاصره در آوريد و در همه جا به كمينشان بنشينيد اما اگر توبه كردند و نماز برپا داشتند و زكات دادند، راهشان را باز كنيد كه خداوند بخشنده‌ى مهربان است'),
(1241, 9, 6, 'و اگر كسى از مشركان از تو پناه خواست پناهش ده تا كلام خدا را بشنود سپس او را به نقطه امنش برسان، زيرا آنها مردمى نادانند'),
(1242, 9, 7, 'چگونه مشركان را نزد خدا و رسول او پيمانى تواند بود؟ مگر آنها كه كنار مسجد الحرام با ايشان پيمان بسته‌ايد، پس تا هنگامى كه با شما بر سر پيمان ايستادند، بر سر پيمانتان بايستيد [زيرا] خداوند پرهيزكاران را دوست دارد'),
(1243, 9, 8, 'چگونه [عهدى داشته باشند] در حالى كه اگر بر شما ظفر يابند، نه قرابتى را در باره‌ى شما مراعات مى‌كنند و نه پيمانى را، شما را به زبان خويش راضى مى‌كنند اما دل‌هايشان امتناع دارد و بيشترشان فاسقند'),
(1244, 9, 9, 'آنها آيات خدا را به بهايى اندك فروختند پس [مردم را] از راه او بازداشتند، به راستى آنها بد عمل مى‌كردند'),
(1245, 9, 10, 'آنها در باره‌ى هيچ مؤمنى قرابت و پيمانى را رعايت نمى‌كنند و آنها واقعا متجاوزند'),
(1246, 9, 11, 'پس اگر توبه كردند و نماز به پا داشتند و زكات دادند، برادران دينى شما هستند، و ما آيات [خود] را براى قومى كه مى‌فهمند توضيح مى‌دهيم'),
(1247, 9, 12, 'و اگر قسم‌هاى خود را پس از پيمان خويش شكستند و به آيين شما طعنه زدند، با سران كفر بجنگيد [چرا كه‌] آنها پاى‌بند سوگندها نيستند، باشد كه [از پيمان‌شكنى‌] باز ايستند'),
(1248, 9, 13, 'چرا با گروهى كه قسم‌هاى خود را شكستند و در بيرون راندن رسول خدا كوشيدند، نمى‌جنگيد؟ در حالى كه آنها بودند كه بار اول [جنگ را] با شما آغاز كردند. آيا از آنها مى‌ترسيد؟ اگر مؤمنيد خدا سزاوارتر است كه از او بترسيد'),
(1249, 9, 14, 'با آنها بجنگيد تا خدا به دست شما عذابشان كند و خوارشان سازد و شما را بر آنها نصرت دهد و دل‌هاى مؤمنان را آرام بخشد'),
(1250, 9, 15, 'و خشم دل‌هايشان را ببرد، و خدا بر هر كه بخواهد [به رحمت‌] باز مى‌گردد، و خداوند داناى حكيم است'),
(1251, 9, 16, 'آيا پنداشته‌ايد كه به حال خود رها مى‌شويد [و امتحان نمى‌شويد]؟ در حالى كه خدا هنوز كسانى از شما را كه جهاد كردند و غير از خدا و رسول و مؤمنان، همرازى نگرفتند، معلوم نداشته است؟ و خدا به آنچه انجام مى‌دهيد آگاه است'),
(1252, 9, 17, 'مشركان كه شاهد كفر خويشند حق ندارند مسجدهاى خدا را آباد كنند. آنها اعمالشان باطل است و در آتش جاودانند'),
(1253, 9, 18, 'مساجد خدا را تنها كسى آباد مى‌كند كه به خدا و روز واپسين ايمان آورده و نماز برپا داشته و زكات داده و جز از خدا نترسيده است، پس اميد است كه اينان از هدايت يافتگان باشند'),
(1254, 9, 19, 'آيا آب دادن به حاجيان و آباد كردن مسجد الحرام را مانند [كار] كسى قرار داده‌ايد كه به خدا و روز واپسين ايمان آورده و در راه خدا جهاد كرده است؟ [اين دو] نزد خدا يكسان نيستند، و خداوند بيدادگران را هدايت نمى‌كند'),
(1255, 9, 20, 'آنها كه ايمان آوردند و هجرت نمودند و با مال و جان خود در راه خدا جهاد كردند، نزد خدا منزلتى والاتر دارند و هم آنان كامروايانند'),
(1256, 9, 21, 'پروردگارشان آنها را به رحمت و رضوان خويش و بهشتى كه در آن نعمت‌هاى دايم دارند، نويد مى‌دهد'),
(1257, 9, 22, 'كه تا ابد در آن خواهند بود [و] بى‌ترديد اجرى عظيم نزد خداوند است'),
(1258, 9, 23, 'اى كسانى كه ايمان آورده‌ايد! اگر پدران و برادرانتان كفر را بر ايمان ترجيح دادند [آنها را] به دوستى نگيريد، و هر كس از شما آنها را دوست بگيرد، پس آنان به راستى ستمكارند'),
(1259, 9, 24, 'بگو: اگر پدران و پسران و برادران و زنان و خويشان شما و اموالى كه به دست آورده‌ايد و تجارتى كه از كسادش بيم داريد و سراهايى كه به آن دلخوشيد، نزد شما از خدا و پيامبرش و جهاد در راه او محبوب‌تر است پس منتظر باشيد تا خدا حكم خويش را صادر كند، و خداوند ق'),
(1260, 9, 25, 'همانا خداوند شما [مؤمنان‌] را در موارد بسيارى يارى كرده است و [نيز] در روز حنين، آن هنگام كه كثرت جمعيتتان شما را مغرور ساخت ولى از شما مشكلى را رفع نكرد، و زمين با همه فراخى بر شما تنگ آمد، سپس پشت‌كنان گريختيد'),
(1261, 9, 26, 'آن‌گاه خداوند آرامش خويش را بر فرستاده‌ى خود و بر مؤمنان نازل كرد، و سپاهى فرو فرستاد كه آنها را نمى‌ديديد، و كافران را عذاب كرد، و سزاى كافران همين است'),
(1262, 9, 27, 'سپس خدا بعد از آن [واقعه‌] توبه هر كس را كه بخواهد مى‌پذيرد، و خداوند آمرزنده‌ى مهربان است'),
(1263, 9, 28, 'اى كسانى كه ايمان آورده‌ايد! حق اين است كه مشركان ناپاكند، پس نبايد از سال آينده به مسجد الحرام نزديك شوند، و اگر [در اين قطع رابطه‌] از تنگدستى بيم داريد، اگر خدا بخواهد زودا كه از فضل خويش بى‌نيازتان كند كه خداوند داناى حكيم است'),
(1264, 9, 29, 'با كسانى [از اهل كتاب‌] كه به خدا و روز واپسين ايمان نمى‌آورند و آنچه را خدا و رسولش حرام كرده، حرمت نمى‌دارند و به دين حق نمى‌گرايند، كارزار كنيد، تا زمانى كه به دست [خود] خاكسارانه جزيه دهند'),
(1265, 9, 30, 'و يهود گفتند: عزير پسر خداست، و نصارا گفتند: مسيح پسر خداست. اين سخن [باطلى‌] است كه بر زبان مى‌آورند و به گفتار كسانى كه پيش از اين كافر شدند شباهت دارد. خدا بكشدشان، به كجا منحرف مى‌شوند'),
(1266, 9, 31, '[هم اينان‌] علما و راهبان خود و مسيح پسر مريم را به جاى خدا اربابان خود گرفتند، با آن كه دستور داشتند جز خداى يگانه را كه معبودى جز او نيست نپرستند. او منزه است از آنچه شريكش مى‌سازند'),
(1267, 9, 32, 'مى‌خواهند نور خدا را با دهان‌هاى خود خاموش كنند ولى خداوند نمى‌گذارد تا اين كه نور خود را كامل كند و هر چند كافران خوش ندارند'),
(1268, 9, 33, 'او كسى است كه رسول خود را با هدايت و دين حق فرستاد تا آن را بر هر چه دين است پيروز گرداند، هر چند مشركان خوش ندارند'),
(1269, 9, 34, 'اى كسانى كه ايمان آورده‌ايد! بسيارى از علماى يهود و راهبان قطعا اموال مردم را به ناحق مى‌خورند و [آنان را] از راه خدا باز مى‌دارند. و كسانى را كه زر و سيم مى‌اندوزند و در راه خدا انفاق نمى‌كنند، به عذابى دردناك نويدشان ده'),
(1270, 9, 35, 'روزى كه آن را در آتش دوزخ سرخ كنند و پيشانى و پهلو و پشتشان را با آن داغ نهند [و گويند:] اين است آنچه براى خود مى‌اندوختيد، پس بچشيد آنچه را كه مى‌اندوختيد'),
(1271, 9, 36, 'همانا تعداد ماه‌ها نزد خدا، آن روزى كه آسمان‌ها و زمين را بيافريد، در كتاب الهى، دوازده ماه بود، كه از آن‌ها چهار ماه حرام است. [و] آيين درست همين است، پس در آن ماه‌ها بر خود ستم نكنيد، و [نيز] همان طور كه مشركان جملگى به جنگ با شما بر مى‌خيزند شما ه'),
(1272, 9, 37, 'جز اين نيست كه جابه‌جا كردن [ماه‌هاى حرام‌] افزونى در كفر است كه كافران با آن گمراه مى‌شوند، آن را يك سال حلال مى‌شمارند و يك سال حرام مى‌دانند، تا با عده‌ى ماه‌هايى كه خدا حرام كرده است برابر كنند، در نتيجه ماهى را كه خدا حرام كرده حلال مى‌كنند. زشت'),
(1273, 9, 38, 'اى كسانى كه ايمان آورده‌ايد! شما را چه شده است كه وقتى به شما گفته مى‌شود: در راه خدا بسيج شويد، به زمين مى‌چسبيد؟ آيا به جاى آخرت به زندگى دنيا راضى شده‌ايد؟ در حالى كه زندگى دنيا در برابر آخرت جز اندكى نيست'),
(1274, 9, 39, 'اگر بسيج نشويد، [خدا] شما را به عذابى دردناك مجازات مى‌كند، و گروهى ديگر را به جاى شما مى‌آورد و او را هيچ زيانى نمى‌رسانيد، و خدا بر هر چيزى تواناست'),
(1275, 9, 40, 'اگر او را يارى نكنيد، البته خدا ياريش كرد، آن دم كه كافران او را [از مكه‌] بيرون كردند [كه يك همراه بيشتر نداشت و او] دومين دو نفر بود، آن‌گاه كه آنها در غار بودند، وقتى به همراه خود [أبو بكر] مى‌گفت: اندوه مدار كه خدا با ماست. پس خدا آرامش خود را ب'),
(1276, 9, 41, 'سبكبار و گرانبار [براى جهاد] بيرون رويد و با مال و جانتان در راه خدا جهاد كنيد كه اين برايتان بهتر است اگر بدانيد'),
(1277, 9, 42, 'اگر غنايمى [دست يافتنى و] نزديك و سفرى كوتاه بود، البته از پى تو مى‌آمدند، ولى راه پر مشقّت به نظرشان دور آمد، و زودا كه به خدا سوگند خورند كه اگر مى‌توانستيم حتما با شما بيرون مى‌آمديم. خويشتن را هلاك مى‌كنند و خدا مى‌داند كه آنها دروغگويند'),
(1278, 9, 43, 'خدا تو را ببخشد، چرا پيش از آن كه راستگويان بر تو روشن شوند و دروغگويان را بازشناسى، به آنان اجازه دادى [در جهاد شركت نكنند]'),
(1279, 9, 44, 'كسانى كه به خدا و روز واپسين ايمان دارند، براى جهاد با مال و جانشان از تو رخصت و عذر نمى‌خواهند، و خدا پرهيزكاران را مى‌شناسد'),
(1280, 9, 45, 'تنها كسانى از تو رخصت مى‌خواهند كه به خدا و روز واپسين ايمان ندارند و دل‌هايشان به شك افتاده و در شك خود سرگردانند'),
(1281, 9, 46, 'و اگر آنها عزم بيرون رفتن داشتند، قطعا براى آن ساز و برگى تدارك مى‌ديدند، ولى خداوند بسيج شدن آنها را خوش نداشت پس آنها را [با سلب توفيق‌] بازداشت و [به آنها] گفته شد: با نشستگان بنشينيد'),
(1282, 9, 47, 'اگر با شما بيرون آمده بودند جز ترديد و تباهى در كارتان نمى‌افزودند، و در بين شما رخنه مى‌كردند تفرقه و فتنه مى‌انداختند، و در ميان شما براى خود جاسوسانى دارند، و خداوند به [حال‌] ستمكاران آگاه است'),
(1283, 9, 48, 'در حقيقت، پيش از اين هم فتنه‌جويى كردند و كارها را بر تو آشفته ساختند تا اين كه حق آمد و امر خدا ظاهر شد، در حالى كه آنها كراهت داشتند'),
(1284, 9, 49, 'و از آنها كسى است كه مى‌گويد: مرا اجازه ده [كه بمانم‌] و در فتنه‌ام مينداز. آگاه باش كه آنان در فتنه افتاده‌اند و بى‌ترديد جهنّم بر كافران احاطه دارد'),
(1285, 9, 50, 'اگر تو را نيكى رسد آنها را بدحال مى‌كند، و اگر مصيبتى به تو رسد مى‌گويند: ما تصميم خود را از پيش گرفته‌ايم، و شادمان باز مى‌گردند'),
(1286, 9, 51, 'بگو: هرگز به ما جز آنچه خدا براى ما مقرّر داشته نمى‌رسد، او مولاى ماست، و مؤمنان بايد بر خدا توكل كنند'),
(1287, 9, 52, 'بگو: آيا براى ما جز يكى از اين دو نيكى [پيروزى يا شهادت‌] را انتظار مى‌بريد؟ ولى ما انتظار داريم كه خدا از جانب خويش و يا به دست ما شما را عذابى برساند. پس منتظر باشيد كه ما نيز با شما منتظريم'),
(1288, 9, 53, 'بگو: [اگر] انفاق كنيد به رغبت يا اكراه، هرگز از شما پذيرفته نمى‌شود همانا شما قومى نافرمان هستيد'),
(1289, 9, 54, 'و چيزى مانع قبول انفاق‌هايشان نشد، جز اين كه آنها خدا و رسول او را منكر شدند و جز با كسالت نماز نمى‌گزارند و جز به كراهت انفاق نمى‌كنند'),
(1290, 9, 55, 'پس مبادا اموال و اولاد آنها تو را به شگفت آورد! جز اين نيست كه خدا مى‌خواهد در زندگى دنيا با اينها عذابشان كند و جانشان در حال كفر برآيد'),
(1291, 9, 56, 'و به خدا سوگند مى‌خورند كه قطعا از شمايند، ولى از شما نيستند، بلكه گروهى هستند كه [از شما] ترس دارند'),
(1292, 9, 57, 'اگر پناهگاه يا غار يا گريزگاهى پيدا مى‌كردند، شتابان به سوى آن روى مى‌آوردند'),
(1293, 9, 58, 'و برخى از آنان [در تقسيم‌] صدقات بر تو خرده مى‌گيرند، پس اگر از آن به ايشان عطا شود راضى مى‌شوند و اگر از آن به ايشان داده نشود يكباره خشمگين مى‌گردند'),
(1294, 9, 59, 'و اگر آنها به آنچه خدا و پيامبرش [از صدقات‌] به ايشان داده راضى مى‌شدند و مى‌گفتند: خدا ما را بس است، زودا كه خدا از فضل خويش و نيز پيامبرش به ما عطا كند و ما مشتاق خداونديم [براى آنها بهتر بود]'),
(1295, 9, 60, 'صدقه‌ها [زكات مال‌] فقط به فقرا و بينوايان و عاملان جمع‌آورى زكات و دلجويى شدگان و آزادى بردگان، و وامداران، و [صرف‌] در راه خدا و در راه ماندگان تعلق دارد كه اين فريضه‌اى از جانب خدا است، و خداوند داناى حكيم است'),
(1296, 9, 61, 'و از ايشان كسانى هستند كه پيامبر را آزار مى‌دهند و مى‌گويند: او [سراپا] گوش است. بگو: او گوش [فرا دهنده‌ى‌] خوبى براى شماست، [چرا كه‌] هم به خدا ايمان داشته و هم مؤمنان را باور دارد و براى كسانى از شما كه ايمان آورده‌اند رحمتى است، و كسانى كه رسول خد'),
(1297, 9, 62, 'براى شما به خدا سوگند مى‌خورند كه شما را راضى كنند، و حال آن كه اگر مؤمن بودند سزاوارتر اين بود كه خدا و رسول او را راضى كنند'),
(1298, 9, 63, 'آيا ندانسته‌اند كه هر كس با خدا و پيامبرش دشمنى كند آتش جهنم براى اوست كه در آن جاودانه است؟ اين است آن رسوايى بزرگ'),
(1299, 9, 64, 'منافقان از آن بيم دارند كه سوره‌اى بر ضد آنها نازل شود و به آنچه در دل دارند آگاهشان سازد. بگو: مسخره كنيد، بى‌ترديد خداوند افشاگر چيزى است كه از آن بيم داريد'),
(1300, 9, 65, 'و اگر از ايشان سؤال كنى [چرا استهزا مى‌كرديد]، مسلما خواهند گفت: ما صرفا به شوخى حرف مى‌زديم و تفريح مى‌كرديم. بگو: آيا خدا و آيات او و رسولش را مسخره مى‌كرديد'),
(1301, 9, 66, 'عذر نياوريد به راستى بعد از ايمانتان كافر شديد. اگر از تقصير گروهى از شما بگذريم، گروهى ديگر را عذاب خواهيم كرد، چرا كه آنان تبهكار بودند'),
(1302, 9, 67, 'مردان و زنان منافق همه از يكديگرند كه امر به منكر و نهى از معروف مى‌كنند و دست‌هاى خود را [از انفاق‌] بسته مى‌دارند. خدا را فراموش كرده‌اند و او هم فراموششان كرده است. آرى، منافقان همان فاسقانند'),
(1303, 9, 68, 'خدا به مردان و زنان منافق و به كافران، آتش جهنم را وعده داده است كه در آن جاودانه‌اند. همين براى ايشان بس است، و خداوند لعنتشان كرده و براى آنان عذابى دايم است'),
(1304, 9, 69, '[شما منافقان‌] مانند آن كسانى هستيد كه پيش از شما بودند. [و] نيرويى افزون‌تر از شما داشتند و دارايى و اولادشان بيشتر بود. آنان از بهره‌ى خود استفاده كردند، شما نيز همان طور كه پيشينيانتان بهره‌مند شدند از بهره‌ى خود استفاده كرده‌ايد، و شما بيهوده گفت'),
(1305, 9, 70, 'آيا خبر كسانى كه پيش از آنان بودند: قوم نوح و عاد و ثمود و قوم ابراهيم و اهل مدين و شهرهاى زير و رو شده به ايشان نرسيده‌است؟ پيامبرانشان حجت‌هاى روشن برايشان آوردند و چنين نبود كه خدا بر آنان ستم كند، بلكه آنها بودند كه به خود ستم مى‌كردند'),
(1306, 9, 71, 'و مردان و زنان با ايمان يار و دوستدار يكديگرند كه امر به معروف و نهى از منكر مى‌كنند و نماز به پا مى‌دارند و زكات مى‌دهند و از خدا و پيامبرش فرمان مى‌برند. آنان را خدا به زودى مشمول رحمت قرار مى‌دهد [و] خداوند شكست‌ناپذير، حكيم است'),
(1307, 9, 72, 'خداوند به مردان و زنان با ايمان، بهشت‌هايى را كه از پاى درختانش نهرها جارى است، كه هميشه در آنند و سراهايى پاكيزه در جنات عدن وعده داده است، ولى رضوان خدا [از همه‌] برتر است، اين همان كاميابى بزرگ است'),
(1308, 9, 73, 'اى پيامبر! با كافران و منافقان جهاد كن و بر آنان سخت بگير و جايگاهشان دوزخ است، و چه بد سر انجامى است'),
(1309, 9, 74, 'به خدا سوگند مى‌خورند كه [چيزى‌] نگفته‌اند، ولى قطعا سخن كفرآميز گفته‌اند و پس از اسلامشان كافر شدند و تصميم به چيزى گرفتند (قتل پيامبر) كه به آن نرسيدند. آنها فقط از اين انتقام گرفتند كه خدا و رسولش آنها را با فضل خود بى‌نياز كرد! [پس به جاى شكر انت'),
(1310, 9, 75, 'بعضى از آنها با خدا عهد بستند كه اگر از كرم خويش به ما عطا كند، قطعا صدقه خواهيم داد و بى‌ترديد از صالحان خواهيم شد'),
(1311, 9, 76, 'اما هنگامى كه از فضل خويش به آنان بخشيد، بدان بخل ورزيدند، و به اعراض روى برتافتند'),
(1312, 9, 77, 'پس [اين رفتار] روحيه نفاق را تا روزى كه خدا را ملاقات كنند در دل‌هايشان قرار داد، بدين سبب كه با خدا خلف وعده كردند و به اين خاطر كه دروغ مى‌گفتند'),
(1313, 9, 78, 'آيا ندانسته‌اند كه خدا راز و نجوايشان را مى‌داند و خدا داننده‌ى غيب‌هاست'),
(1314, 9, 79, 'كسانى كه به مؤمنانى كه داوطلبانه صدقات مى‌دهند طعنه مى‌زنند و آنان را كه بيش از استطاعت خويش نمى‌يابند [كه احسان كنند] مسخره‌شان مى‌كنند، خدا نيز آنها را مسخره مى‌كند و آنها را عذابى دردناك است'),
(1315, 9, 80, '[مى‌خواهى‌] برايشان آمرزش بخواه، يا برايشان آمرزش نخواه، اگر هفتاد بار هم برايشان آمرزش بخواهى، خدا هرگز آنها را نخواهد بخشيد. اين به سبب آن است كه آنها خدا و رسول او را انكار كردند، و خداوند مردم نافرمان را هدايت نمى‌كند'),
(1316, 9, 81, 'آنها كه در خانه‌ها نشستند و از همراهى با رسول خدا [در جهاد] تخلف ورزيدند خوشحال شدند و از جهاد با مال و جان خود در راه خدا كراهت داشتند، و گفتند: در اين گرما به جنگ نرويد. بگو: آتش جهنم سوزان‌تر است، اگر مى‌فهميدند'),
(1317, 9, 82, 'پس به سزاى آنچه كسب مى‌كردند بايد كم بخندند و بسيار بگريند'),
(1318, 9, 83, 'و اگر خدا تو را به سوى گروهى از آنها برگردانيد و از تو براى اعزام شدن [به جنگ ديگرى‌] اذن خواستند، بگو: هرگز با من راهى [جنگ‌] نخواهيد شد، و هرگز همراه من با هيچ دشمنى نبرد نخواهيد كرد، زيرا شما از همان اول به ماندن رضايت داديد، پس [حالا هم‌] با واما'),
(1319, 9, 84, 'و هرگز بر هيچ يك از آنها كه بميرد نماز نخوان و بر سر گورش نايست، چرا كه آنان به خدا و پيامبر او كافر شدند و در حال فسق مردند'),
(1320, 9, 85, 'و اموال و اولاد آنها تو را به شگفت نيندازند، جز اين نيست كه خدا مى‌خواهد در دنيا با اينها عذابشان كند و جانشان در حال كفر برآيد'),
(1321, 9, 86, 'و چون سوره‌اى نازل شود كه به خدا ايمان آوريد و در كنار پيامبرش جهاد كنيد، توانگرانشان از تو رخصت خواهند و گويند: بگذار ما با خانه نشينان باشيم'),
(1322, 9, 87, 'راضى شدند كه با خانه‌نشينان باشند، و بر دل‌هايشان مهر زده شده است از اين رو نمى‌فهمند'),
(1323, 9, 88, 'ولى پيامبر و كسانى كه با او ايمان آورده‌اند، با مال و جانشان جهاد كردند، و اينانند كه خوبى‌ها براى آنان است، و هم آنان رستگارانند'),
(1324, 9, 89, 'خدا براى آنان باغ‌هايى آماده كرده است كه از پاى درختانش نهرها جارى است و در آن جاودانه‌اند [و] اين كاميابى بزرگ است'),
(1325, 9, 90, 'و عذرخواهان باديه نشين آمدند تا به آنان اجازه‌ى [ترك جهاد] داده شود، و كسانى كه به خدا و فرستاده‌ى او دروغ گفتند [نيز در خانه‌] نشستند. زودا كه آن دسته از آنان را كه كافر شدند عذابى دردناك برسد'),
(1326, 9, 91, 'بر ضعيفان و بيماران و آنها كه چيزى نمى‌يابند [تا در راه جهاد] انفاق كنند ايرادى نيست در صورتى كه براى خدا و پيامبرش مخلصانه خيرخواهى كنند، كه بر نيكوكاران ملامتى نيست، و خداوند آمرزنده‌ى مهربان است'),
(1327, 9, 92, 'و نه بر آنها كه چون نزد تو آمدند كه سوار [و اعزامشان‌] كنى، گفتى: چيزى ندارم كه بر آن سوارتان كنم، و آنها برگشتند در حالى كه از غم اين كه چيزى ندارند [براى جهاد] خرج كنند چشمانشان اشك مى‌ريخت'),
(1328, 9, 93, 'ايراد فقط بر كسانى است كه توانگرند و از تو اجازه مى‌خواهند [در جهاد شركت نكنند]. آنها به اين رضايت داده‌اند كه با بازماندگان [خانه‌نشين‌] باشند، و خدا بر دل‌هايشان مهر زده است، پس آنها نمى‌فهمند'),
(1329, 9, 94, 'هنگامى كه به سوى آنان بازگرديد براى شما عذر مى‌آورند، بگو: عذر نياوريد كه هرگز باورتان نخواهيم داشت، همانا خدا ما را از اخبار شما مطلع كرده است و زود باشد كه خدا و رسولش عملتان را ببينند، آن‌گاه به سوى داناى نهان و آشكار بازگردانده مى‌شويد و از آنچه'),
(1330, 9, 95, 'وقتى به سوى آنان بازگشتيد براى شما به خدا سوگند مى‌خورند تا از ايشان صرف نظر كنيد، پس از آنان روى گردانيد آنها پليدند و به سزاى آنچه مرتكب مى‌شدند جايشان دوزخ است'),
(1331, 9, 96, 'براى شما سوگند مى‌خورند تا از آنها راضى شويد. اگر شما هم از ايشان راضى شويد، قطعا خدا از قوم نافرمان راضى نخواهد شد'),
(1332, 9, 97, 'باديه نشينان عرب در كفر و نفاق از ديگران سخت‌ترند، و سزاوارترند كه حدود آنچه را خدا بر پيامبرش نازل كرده است ندانند، و خداوند داناى حكيم است'),
(1333, 9, 98, 'و برخى از باديه‌نشينان هستند كه آنچه را در راه خدا هزينه مى‌كنند غرامت مى‌شمارند و براى شما پيشامدهاى بد را انتظار مى‌برند. پيشامد بد تنها بر آنهاست، و خدا شنواى داناست'),
(1334, 9, 99, 'و برخى از باديه‌نشينان هستند كه به خدا و روز واپسين ايمان دارند و آنچه را انفاق مى‌كنند مايه تقرب به خدا و دعاهاى پيامبر مى‌دانند. بدانيد كه اين انفاق مايه تقرب آنان است. به زودى خداوند ايشان را در جوار رحمت خويش در آورد، كه خدا آمرزنده‌ى مهربان است'),
(1335, 9, 100, 'و پيشگامان نخستين از مهاجران و انصار و كسانى كه به نيكى پيرويشان كردند، خدا از ايشان خشنود و آنها از او خشنودند، و براى آنان باغ‌هايى آماده كرده كه از پاى درختانش نهرها جارى است و هميشه در آنند. اين است كاميابى بزرگ'),
(1336, 9, 101, 'و گروهى از باديه نشينان كه اطراف شمايند منافقند، و از ساكنان مدينه نيز عده‌اى سخت بر نفاق خو كرده‌اند. تو آنها را نمى‌شناسى، ما آنها را مى‌شناسيم. به زودى دوبار عذابشان خواهيم كرد، سپس به عذابى بزرگ برگردانده مى‌شوند'),
(1337, 9, 102, 'و ديگرانى هستند كه به گناهان خود معترفند، [آنان‌] كار شايسته و ناشايسته را به هم آميختند اميد است خدا توبه آنها را بپذيرد، كه بى‌ترديد خدا بخشنده‌ى مهربان است'),
(1338, 9, 103, 'از اموال آنها صدقه بگير تا بدان پاكشان كنى و رشدشان دهى، و دعايشان كن كه دعاى تو آرامشى براى آنهاست، و خدا شنواى داناست'),
(1339, 9, 104, 'آيا ندانسته‌اند كه تنها خداست كه از بندگانش توبه را مى‌پذيرد و صدقات را مى‌ستاند؟ و خداست كه توبه‌پذير مهربان است'),
(1340, 9, 105, 'و بگو: [هر كارى مى‌خواهيد] بكنيد كه به زودى خدا و رسول او و مؤمنان كردار شما را خواهند ديد، و به زودى به سوى داناى نهان و آشكار بازگردانده مى‌شويد، پس شما را به آنچه انجام مى‌داديد آگاه خواهد كرد'),
(1341, 9, 106, 'و عدّه‌اى ديگر كارشان به مشيت خدا واگذار شده است كه يا عذابشان مى‌كند يا توبه‌شان را مى‌پذيرد، و خدا داناى حكيم است'),
(1342, 9, 107, 'و كسانى [هستند] كه مسجدى اختيار كردند كه باعث زيان زدن و كفر و پراكندگى ميان مؤمنان است، و نيز كمينگاهى براى كسانى كه با خدا و پيامبر او از پيش در جنگ بودند مى‌باشد و [اكنون‌] سخت سوگند مى‌خورند كه جز نيكى قصدى نداشتيم، ولى خدا گواهى مى‌دهد كه آنها'),
(1343, 9, 108, 'هرگز در آن [مسجد] نايست [زيرا] مسجدى كه از روز اول بر تقوا بنا شده شايسته‌تر است كه در آن نماز كنى. در آن جا مردانى هستند كه دوست دارند خود را پاك سازند، و خدا كسانى را كه خواهان پاكى‌اند دوست مى‌دارد'),
(1344, 9, 109, 'آيا كسى كه بنياد كار خود را بر پايه تقوا و رضاى خدا نهاده بهتر است يا كسى كه بناى خود را بر لب پرتگاهى فرو ريختنى نهاده و با آن در آتش دوزخ سرنگون مى‌گردد؟ و خدا گروه ظالمان را هدايت نمى‌كند'),
(1345, 9, 110, 'آن بنايى كه برافراشتند همواره به صورت شك [و نفاقى‌] در دلشان بر جاى خواهد ماند، مگر اين كه دل‌هايشان پاره پاره شود [و بميرند]، و خدا داناى حكيم است'),
(1346, 9, 111, 'همانا خدا از مؤمنان جان و مالشان را به بهاى بهشت خريدارى كرد آنان در راه خدا مى‌جنگند و مى‌كشند و كشته مى‌شوند. اين در تورات و انجيل و قرآن وعده‌ى حقى بر عهده‌ى خدا است، و چه كسى از خدا به عهد خويش وفادارتر است؟ پس به اين معامله‌اى كه با خدا كرده‌اي'),
(1347, 9, 112, '[آن مؤمنان‌] همان توبه كنندگان، عبادت پيشگان، ستايشگران، روزه‌داران، ركوع كنندگان و سجده كنندگان، امر كنندگان به معروف و نهى كنندگان از منكر و پاسداران مقررات الهى‌اند، و مؤمنان را مژده رسان'),
(1348, 9, 113, 'پيامبر و كسانى كه ايمان آورده‌اند نمى‌بايد براى مشركان- پس از آن كه برايشان آشكار گرديد كه آنها اهل دوزخند- طلب آمرزش كنند، هر چند خويشاوند باشند'),
(1349, 9, 114, 'و طلب آمرزش ابراهيم براى پدرش جز به خاطر وعده‌اى كه به او داده بود صورت نگرفت، ولى هنگامى كه براى او روشن شد كه وى دشمن خداست از او بيزارى جست. به راستى ابراهيم بسى نيايشگر و بردبار بود'),
(1350, 9, 115, 'و خدا چنين نيست كه قومى را پس از آن هدايت كرده گمراه كند [و بى‌راه گذارد] تا اين كه، آنچه را كه بايد [از آن‌] بپرهيزند بر آنها روشن سازد. بى‌ترديد خدا به هر چيزى داناست'),
(1351, 9, 116, 'همانا خداست كه فرمانروايى آسمان‌ها و زمين از آن اوست، زنده مى‌كند و مى‌ميراند، و براى شما جز خدا يار و ياورى نيست'),
(1352, 9, 117, 'همانا خدا بر پيامبر و مهاجران و انصار كه در آن ساعت دشوار از او پيروى كردند عنايت كرد، بعد از آن كه چيزى نمانده بود كه گروهى از آنها دلشان بلغزد [و ترك جهاد كنند]، سپس بر آنها عنايت كرد و توبه‌شان را پذيرفت، چرا كه او نسبت به مؤمنان رئوف و مهربان اس'),
(1353, 9, 118, 'و نيز بر آن سه تن [عنايت كرد] كه [از جهاد] واپس نهاده شدند [و مردم از آنها بريدند] تا بدانجا كه زمين با همه فراخى بر آنها تنگ شد و از خود به تنگ آمدند و دانستند كه از خدا، جز به سوى او پناهى نيست. آنگاه خدا به آنها بازگشت تا توبه كنند. بى‌ترديد خدا'),
(1354, 9, 119, 'اى كسانى كه ايمان آورده‌ايد! از خدا پروا كنيد و با راستگويان باشيد'),
(1355, 9, 120, 'مردم مدينه و باديه‌نشينان اطرافش را نرسد كه از رسول خدا تخلف كنند و جان خود را عزيزتر از جان او بدانند. اين از آن روست كه در راه خدا هيچ تشنگى و رنج و گرسنگى به آنها نرسد و در هيچ مكانى كه كافران را به خشم مى‌آورد قدم نگذارند و از دشمن ضربه‌اى نخورن'),
(1356, 9, 121, 'و هيچ هزينه كوچك و بزرگى خرج نمى‌كنند و هيچ واديى را نمى‌پيمايند مگر اين كه به حساب آنان نوشته مى‌شود، تا خدا آنها را بهتر از آنچه مى‌كردند پاداش دهد'),
(1357, 9, 122, 'و مؤمنان را وظيفه نيست كه همگى [به سوى جهاد] كوچ كنند، پس چرا از هر گروهى از آنان، طايفه‌اى كوچ نمى‌كنند تا در دين دانا شوند و چون بازگشتند قوم خود را انذار كنند شايد آنها از معاصى بر حذر باشند'),
(1358, 9, 123, 'اى كسانى كه ايمان آورده‌ايد! با كافرانى كه مجاور شما هستند پيكار كنيد، و آنها بايد در شما شدت و قاطعيت بيابند، و بدانيد كه خدا با پرهيزكاران است'),
(1359, 9, 124, 'و چون سوره‌اى نازل شود، بعضى از آنها مى‌گويند: اين سوره بر ايمان كدام يك از شما افزود؟ امّا كسانى كه ايمان آورده‌اند بر ايمانشان مى‌افزايد و شادمان مى‌شوند'),
(1360, 9, 125, 'ولى كسانى كه در دلشان بيمارى است، بر پليديشان پليدى افزود و در حال كفر مردند'),
(1361, 9, 126, 'آيا نمى‌بينند كه آنها در هر سال يك يا دو بار امتحان مى‌شوند، ولى نه توبه مى‌كنند و نه متذكر مى‌شوند'),
(1362, 9, 127, 'و چون سوره‌اى نازل گردد، بعضى به بعضى ديگر نگاه كنند [و گويند:] آيا كسى شما را مى‌بيند؟ سپس [مخفيانه‌] باز مى‌گردند. خدا نيز دل‌هايشان را [از حق‌] بازگرداند، زيرا آنها گروهى هستند كه نمى‌فهمند'),
(1363, 9, 128, 'همانا شما را پيامبرى از خودتان آمد كه رنج و زيان‌تان بر او گران است، به [هدايت‌] شما اصرار دارد و به مؤمنان دلسوز و مهربان است'),
(1364, 9, 129, 'پس اگر پشت كردند، بگو: خدا مرا بس است كه معبودى جز او نيست، بر او توكّل كردم و او صاحب عرش بزرگ است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، را. اينها آيات كتاب حكمت‌آموز است'),
(1366, 10, 2, 'آيا براى مردم شگفت‌آور است كه به مردى از خودشان وحى كرديم كه: مردم را بترسان و مؤمنان را بشارت ده كه نزد پروردگارشان پايگاهى رفيع دارند؟ كافران گفتند: بى‌ترديد، اين مرد افسونگرى آشكار است'),
(1367, 10, 3, 'پروردگار شما آن خدايى است كه آسمان‌ها و زمين را در شش روز آفريد، آن‌گاه به عرش پرداخت. كار جهان را تدبير مى‌كند. بى‌اجازه‌ى او شفاعتگرى نيست. اين خداى يكتا پروردگار شماست، پس او را بپرستيد. آيا پند نمى‌گيريد'),
(1368, 10, 4, 'بازگشت همه‌ى شما به سوى اوست. وعده‌ى خدا حق است. هموست كه آفرينش را آغاز مى‌كند سپس آن را باز مى‌گرداند تا كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند به انصاف جزا دهد. و كسانى كه كافر شدند، به سزاى كفرشان آشاميدنى از آب جوشان و عذابى دردناك خواه'),
(1369, 10, 5, 'اوست كسى كه خورشيد را درخشان و ماه را تابان كرد و براى آن منزل‌هايى تقدير نمود تا عدد سال‌ها و حساب [كارها] را بدانيد. خدا اينها را جز به درستى نيافريده است. او آيات خود را براى قومى كه مى‌دانند شرح مى‌دهد'),
(1370, 10, 6, 'به راستى در آمد و شد شب و روز و آنچه خدا در آسمان‌ها و زمين آفريده براى اهل تقوا نشانه‌هايى است'),
(1371, 10, 7, 'همانا كسانى كه ديدار ما را باور ندارند و به زندگى دنيا دل خوش كرده و بدان دل آرام شده‌اند و كسانى كه از آيات ما غافلند'),
(1372, 10, 8, 'آنها جايشان جهنم است به سزاى آنچه مى‌كرده‌اند'),
(1373, 10, 9, 'همانا كسانى كه ايمان آوردند و كارهاى شايسته كردند، پروردگارشان به سبب ايمانشان آنها را هدايت مى‌كند. از پاى [قصرها] شان در بهشت‌هاى پر نعمت نهرها روان است'),
(1374, 10, 10, 'در آن جا دعايشان اين است: بار خدايا! تو منزهى. و درودشان در آن جا سلام است، و پايان دعايشان اين است: سپاس خداى را كه پروردگار جهانيان است'),
(1375, 10, 11, 'اگر خدا با همان شتابى كه مردم براى خود خير را مى‌طلبند در رساندن شر به آنان تعجيل مى‌كرد، قطعا مرگشان فرا رسيده بود. پس كسانى را كه ديدار ما را باور ندارند در طغيانشان رها مى‌كنيم تا سرگشته بمانند'),
(1376, 10, 12, 'و چون به آدمى گزندى رسد، ما را [در همه حال‌] به پهلو خفته يا نشسته و يا ايستاده مى‌خواند، و چون گرفتارى‌اش را برطرف ساختيم چنان مى‌رود كه گويى اصلا ما را براى محنتى كه بدو رسيده نخوانده است. اين گونه براى اسرافكاران اعمالى كه انجام مى‌دادند زينت داد'),
(1377, 10, 13, 'و همانا نسل‌هاى پيش از شما را هنگامى كه ستم كردند هلاك نموديم. پيامبرانشان دلايل آشكار برايشان آوردند، ولى بر آن نبودند كه ايمان بياورند. اين گونه مردم بزهكار را جزا مى‌دهيم'),
(1378, 10, 14, 'آن‌گاه شما را پس از آنان در زمين جانشين كرديم تا بنگريم چگونه عمل مى‌كنيد'),
(1379, 10, 15, 'و چون آيات ما كه روشنگر است بر آنها خوانده مى‌شود، آنها كه ديدار ما را باور ندارند مى‌گويند: قرآنى غير از اين بياور يا آن را عوض كن، بگو: مرا نمى‌رسد آن را از پيش خود عوض كنم، من جز آنچه را كه به من وحى مى‌شود پيروى نمى‌كنم. اگر پروردگارم را نافرمان'),
(1380, 10, 16, 'بگو: اگر خدا مى‌خواست آن را بر شما نمى‌خواندم و [خدا هم‌] شما را بدان آگاه نمى‌كرد. همانا پيش از اين در ميان شما عمرى زندگى كرده‌ام، چرا تعقل نمى‌كنيد'),
(1381, 10, 17, 'پس كيست ستمكارتر از آن كس كه بر خدا دروغ بندد يا آيات او را تكذيب كند؟ حقيقت اين است كه مجرمان رستگار نمى‌شوند'),
(1382, 10, 18, 'و غير از خدا چيزهايى را مى‌پرستند كه نه سودشان مى‌رساند و نه زيان، و مى‌گويند: اينها شفيعان ما در نزد خدايند. بگو: آيا خدا را از چيزى خبر مى‌دهيد كه در آسمان‌ها و زمين سراغ ندارد؟ او منزه و برتر است از آنچه شريك وى مى‌سازند'),
(1383, 10, 19, 'و مردم جز يك امّت نبودند، پس اختلاف كردند. و اگر پيش‌تر حكم [مهلت‌] از جانب پروردگارت صادر نشده بود، قطعا در آنچه بر سرش اختلاف دارند ميانشان فيصله داده شده بود [و همگى هلاك مى‌شدند]'),
(1384, 10, 20, 'و مى‌گويند: چرا معجزه‌اى [كه ما مى‌خواهيم‌] از جانب پروردگارش بر او نازل نمى‌شود؟ بگو: علم غيب تنها از آن خداست، [شما كه بهانه‌جوئى مى‌كنيد] پس منتظر باشيد كه من هم با شما از منتظرانم'),
(1385, 10, 21, 'و چون مردم را پس از آسيبى كه به ايشان رسيده رحمتى بچشانيم، به ناگاه آنها را مى‌بينى كه در آيات ما مكر و بدانديشى مى‌كنند. بگو: مكر خدا سريع‌تر است، و مسلما رسولان ما (فرشتگان) آنچه مكر مى‌كنيد مى‌نويسند'),
(1386, 10, 22, 'او كسى است كه شما را در خشكى و دريا سير مى‌دهد، تا وقتى كه در كشتى باشيد و به سبب بادى خوش [و موافق‌] آنها را به حركت در آورد و به آن شاد گردند، ناگهان طوفان سختى بر آنها بوزد و موج از هر طرف بر آنها بتازد، و بدانند كه در محاصره افتاده‌اند، در آن حا'),
(1387, 10, 23, 'پس همين كه نجاتشان داد، ناگهان در زمين به ناحق سركشى مى‌كنند. اى مردم! سركشى شما فقط به زيان خود شماست. تنها بهره‌اى [كوتاه‌] از زندگى دنيا مى‌بريد و عاقبت بازگشت شما به سوى ما خواهد بود، پس شما را از آنچه انجام مى‌داديد با خبر خواهيم كرد'),
(1388, 10, 24, 'در حقيقت مثل زندگى دنيا مثل بارانى است كه از آسمان نازل كرديم، پس گياهان زمين از آنچه مردم و چهارپايان مى‌خورند با آن در آميخت، [و سيراب شد] تا چون زمين رونق و زينت خود را گرفت و صاحبان زمين احساس كردند كه محصولات را در چنگ خود دارند، [به ناگاه‌] فرم'),
(1389, 10, 25, 'و خداوند به سراى سلامت [و بهشت‌] مى‌خواند و هر كه را بخواهد به راه راست هدايت مى‌كند'),
(1390, 10, 26, 'براى آنان كه كار نيك كرده‌اند عاقبت نيكوتر و بيش از آن هست، و سيه رويى و خوارى چهره‌هايشان را نمى‌گيرد. آنها اهل بهشتند و در آن جاودانه خواهند بود'),
(1391, 10, 27, 'و كسانى كه كارهاى بد كردند، جزاى هر بدى همانند آن است و خوارى آنها را فرا مى‌گيرد. در مقابل خدا هيچ كس حامى آنها نيست. چنان شوند كه گويى چهره‌هايشان با پاره‌هايى از شب ظلمانى پوشيده شده است. آنها اهل آتشند و در آن جاودانند'),
(1392, 10, 28, 'و روزى كه همه آنها را گرد مى‌آوريم، آن‌گاه به كسانى كه شرك ورزيده‌اند مى‌گوييم: شما و شريكانتان در جاى خود بايستيد. سپس آنها را از هم جدا مى‌كنيم، و شريكان آنها مى‌گويند: [در حقيقت‌] شما ما را نمى‌پرستيديد [بلكه هواى نفس خود را مى‌پرستيديد]'),
(1393, 10, 29, 'و خدا به شهادت ميان ما و شما كافى است. حقيقتا ما از عبادت شما بى‌خبر بوديم'),
(1394, 10, 30, 'آن جاست كه هر كسى آنچه را از پيش فرستاده است بازيابد و [همه‌] به سوى خدا، مولاى حقيقى خود بازگردانده شوند و آنچه به دروغ مى‌ساختند از دستشان برود'),
(1395, 10, 31, 'بگو: كيست كه از آسمان و زمين به شما روزى مى‌دهد؟ يا كيست مالك گوش‌ها و چشم‌ها و كيست كه زنده را از مرده و مرده را از زنده بر مى‌آورد؟ و كيست كه تدبير امور مى‌كند؟ حتما خواهند گفت: خدا. بگو: پس چرا [از شرك‌] پرهيز نمى‌كنيد'),
(1396, 10, 32, 'پس آن خداى يكتا، پروردگار حقيقى شماست، و از حق كه بگذرى جز ضلال چيست؟ پس به كجا منحرف مى‌شويد'),
(1397, 10, 33, 'اين گونه سخن پروردگارت در باره‌ى كسانى كه نافرمانى [و لجاجت‌] كردند به حقيقت پيوست كه آنها ايمان نخواهند آورد'),
(1398, 10, 34, 'بگو: آيا از معبودان شما كسى هست كه آفرينش را آغاز كند و سپس آن را برگرداند؟ بگو: خداست كه آفرينش را آغاز مى‌كند و باز آن را بر مى‌گرداند، پس به كجا منحرف مى‌شويد'),
(1399, 10, 35, 'بگو: آيا از معبودان شما كسى هست كه به سوى حق رهبرى كند؟ بگو: خداست كه به سوى حق رهبرى مى‌كند. پس آيا كسى كه به سوى حق رهبرى مى‌كند سزاوارتر است مورد پيروى قرار گيرد يا كسى كه راه نمى‌يابد مگر آن كه هدايتش كنند؟ شما را چه شده است، چگونه حكم مى‌كنيد'),
(1400, 10, 36, 'و بيشترشان فقط تابع گمانند، و گمان به هيچ وجه [آدمى را] از حقيقت بى‌نياز نمى‌گرداند. همانا خدا به آنچه مى‌كنند داناست'),
(1401, 10, 37, 'و چنان نيست كه اين قرآن از جانب غير خدا به دروغ ساخته شده باشد، بلكه تصديق كتاب‌هايى است كه پيش از آن آمده و شرح و توضيح آنهاست و ترديدى در آن نيست و از پروردگار جهانيان است'),
(1402, 10, 38, 'آيا مى‌گويند: آن را به دروغ ساخته است؟ بگو: اگر راست مى‌گوييد، سوره‌اى مانند آن بياوريد و هر كه را جز خدا مى‌توانيد [به يارى‌] فراخوانيد'),
(1403, 10, 39, 'بلكه چيزى را دروغ شمردند كه به علم آن نرسيده‌اند و هنوز واقعيتش برايشان روشن نشده است. كسانى هم كه پيش از آنان بودند همين گونه تكذيب كردند. پس بنگر عاقبت ستمگران چگونه بود'),
(1404, 10, 40, 'و برخى از آنان به قرآن ايمان مى‌آورند و برخى ايمان نمى‌آورند، و پروردگار تو مفسدان را بهتر مى‌شناسد'),
(1405, 10, 41, 'و اگر تو را تكذيب كردند، بگو: عمل من از آن من است و عمل شما از آن شماست، و شما از آنچه من انجام مى‌دهم به دوريد و من از آنچه شما انجام مى‌دهيد به دورم'),
(1406, 10, 42, 'و برخى از آنها [به ظاهر] به تو گوش فرا مى‌دهند، اما مگر تو مى‌توانى كران را شنوا كنى هر چند تعقل نكنند'),
(1407, 10, 43, 'و برخى از آنها به تو مى‌نگرند [ولى نمى‌بينند]، مگر تو كوران را هدايت توانى كرد هر چند كه نبينند'),
(1408, 10, 44, 'خدا به مردم هيچ ستمى نمى‌كند، ليكن مردم به خودشان ستم مى‌كنند'),
(1409, 10, 45, 'و روزى كه آنها را گرد مى‌آورد، چنان كه گويى تنها ساعتى از روز در دنيا بوده‌اند، [در حدى كه‌] يكديگر را ببينند و شناسايى كنند. [بنابر اين‌] كسانى كه لقاى خدا را دروغ شمردند زيان كردند و هدايت نيافتند'),
(1410, 10, 46, 'و اگر پاره‌اى از آنچه را كه به آنها وعده مى‌دهيم به تو نشان دهيم يا تو را بميرانيم، در هر صورت بازگشتشان به سوى ماست، پس [بدانند كه‌] خدا بر آنچه مى‌كنند ناظر است'),
(1411, 10, 47, 'و هر امّتى را پيامبرى است، پس هنگامى كه پيامبرشان بيايد [حجت بر آنها تمام و] ميانشان به عدالت داورى مى‌شود و به آنها ستم نمى‌رود'),
(1412, 10, 48, 'و مى‌گويند: اگر راست مى‌گوييد، اين وعده چه وقت است'),
(1413, 10, 49, 'بگو: من براى خود زيان و سودى در اختيار ندارم مگر آنچه را كه خدا بخواهد. هر امّتى را اجلى محدود است كه وقتى اجلشان به سر رسيد، نه ساعتى از آن تأخير كنند و نه پيشى گيرند'),
(1414, 10, 50, 'بگو: به من بگوييد، اگر عذاب او شب يا روز به سراغتان بيايد [آيا مى‌توانيد آن را دفع كنيد]؟ پس مجرمان براى چه به [آن عذاب‌] عجله مى‌كنند'),
(1415, 10, 51, 'سپس آيا آن‌گاه كه عذاب واقع شد به آن ايمان مى‌آوريد؟ آيا حالا؟ در صورتى كه قبلا [به تمسخر] شتاب در آن را مى‌خواستيد'),
(1416, 10, 52, 'آن‌گاه به كسانى كه ستم كردند گفته شود: عذاب ابدى را بچشيد. آيا جز به كيفر آنچه مى‌كرديد مجازات مى‌شويد'),
(1417, 10, 53, 'و از تو مى‌پرسند: آيا آن عذاب راست است؟ بگو: آرى! به خدا قسم كه آن يقينا راست است و شما نمى‌توانيد مانع آن شويد'),
(1418, 10, 54, 'و اگر هر كسى كه ستم كرده، آنچه در زمين است از آن او باشد، حتما آن را [براى نجات خود] فديه مى‌دهد، و هنگامى كه عذاب را ببينند پشيمانى خود را نهان دارند و ميانشان به انصاف داورى شود و ستمى بر آنها نرود'),
(1419, 10, 55, 'بدانيد كه هر چه در آسمان‌ها و زمين است از آن خداست. بدانيد كه وعده‌ى خدا حق است، ولى بيشترشان نمى‌دانند'),
(1420, 10, 56, 'او زنده مى‌كند و مى‌ميراند و به سوى او برگردانده مى‌شويد'),
(1421, 10, 57, 'اى مردم! به راستى براى شما از جانب پروردگارتان موعظه‌اى آمده است و شفايى براى آنچه در دل‌هاست و هدايت و رحمتى براى مؤمنان'),
(1422, 10, 58, 'بگو: به فضل و رحمت خدا، بايد خوشحال شوند كه اين از آنچه مى‌اندوزند بهتر است'),
(1423, 10, 59, 'بگو: به من بگوييد، آن روزى‌هايى كه خدا براى شما نازل كرده و شما برخى از آن را حرام و برخى را حلال قرار داده‌ايد، آيا خدا به شما اجازه داده است يا بر خدا دروغ مى‌بنديد'),
(1424, 10, 60, 'آنها كه به خدا دروغ مى‌بندند به روز قيامت چه گمان مى‌برند [جز عذاب را؟] همانا خدا نسبت به مردم، صاحب كرم است اما بيشتر آنها سپاسگزار نيستند'),
(1425, 10, 61, 'در هيچ كارى وارد نمى‌شوى و هيچ قرآنى از جانب او نمى‌خوانى و هيچ عملى نمى‌كنيد جز اين كه همان لحظه كه بدان مى‌پردازيد ما ناظر شماييم، و هموزن ذره‌اى در زمين و نه در آسمان از خداى تو پوشيده نيست، و نه كوچك‌تر از آن و نه بزرگ‌تر، مگر آن كه در مكتوبى روش'),
(1426, 10, 62, 'آگاه باشيد كه اولياى خدا را نه بيمى است و نه اندوهگين مى‌شوند'),
(1427, 10, 63, 'همانان كه ايمان آوردند و پرهيزكارى مى‌كردند'),
(1428, 10, 64, 'براى ايشان در زندگى اين دنيا و در آخرت بشارت است. وعده‌هاى خدا را تغييرى نيست. اين است آن كاميابى بزرگ'),
(1429, 10, 65, 'سخن آنها تو را محزون نكند! عزت به تمامى از آن خداست، او شنواى داناست'),
(1430, 10, 66, 'آگاه باش كه هر كه در آسمان‌ها و هر كه در زمين است از آن خداست، و كسانى كه غير از خدا شريكانى [براى او] مى‌خوانند، از چه پيروى مى‌كنند؟ آنها صرفا از پندار پيروى مى‌كنند و جز دروغ نمى‌گويند'),
(1431, 10, 67, 'اوست كه شب را برايتان پديد آورد تا در آن بياراميد و روز را [چشم‌اندازى‌] روشن گردانيد. بى‌ترديد در اينها براى مردمى كه مى‌شنوند نشانه‌هاست'),
(1432, 10, 68, 'گفتند: خدا فرزندى گرفته است، منزّه است او. بى‌نياز است او. آنچه در آسمان‌ها و آنچه در زمين است از آن اوست. شما بر اين گفته حجت نداريد. آيا چيزى را كه نمى‌دانيد به خدا نسبت مى‌دهيد'),
(1433, 10, 69, 'بگو: آنان كه به خدا دروغ مى‌بندند، يقينا رستگار نمى‌شوند'),
(1434, 10, 70, 'برخوردارى [اندكى‌] در اين دنياست، سپس بازگشتشان به سوى ماست، آن‌گاه به خاطر اين كه كفر مى‌ورزيدند عذاب سختشان مى‌چشانيم'),
(1435, 10, 71, 'و خبر نوح را بر آنها بخوان، آن‌گاه كه به قوم خود گفت: اى قوم من! اگر قيام و حضور من در ميان شما و تذكراتم در باره‌ى آيات الهى بر شما گران آمده است، [بايد بدانيد كه‌] من بر خدا توكل كرده‌ام پس كار و نيرو و معبودهايتان را يكجا كنيد تا كارتان بر خودتان'),
(1436, 10, 72, 'و اگر پشت كرديد، من از شما مزدى نخواستم، كه مزد من جز بر خدا نيست و دستور يافته‌ام كه از اهل تسليم باشم'),
(1437, 10, 73, 'اما او را تكذيب كردند و ما او و كسانى را كه در كشتى با او بودند نجات داديم و آنها را جانشين [كافران‌] كرديم و كسانى را كه آيات ما را تكذيب كردند غرق نموديم. پس بنگر كه عاقبت بيم داده شدگان چسان شد'),
(1438, 10, 74, 'آن‌گاه از پى او رسولانى را به سوى قومشان فرستاديم كه برايشان حجت‌ها آوردند، ولى آنها بر آن نبودند تا به چيزى كه قبلا تكذيبش كرده بودند ايمان بياورند. ما بدين سان بر دل‌هاى متجاوزان مهر غفلت مى‌نهيم'),
(1439, 10, 75, 'آن‌گاه از پى آنان موسى و هارون را با آيات خويش به سوى فرعون و اشراف او فرستاديم، ولى آنها بزرگى كردند و قومى عصيانگر بودند'),
(1440, 10, 76, 'پس چون حق از نزد ما سويشان آمد، گفتند: به راستى اين جادويى آشكار است'),
(1441, 10, 77, 'موسى گفت: آيا در باره‌ى حق، اكنون كه براى شما آمده مى‌گوييد: [اين سحر است؟] در حالى كه ساحران رستگار نمى‌شوند'),
(1442, 10, 78, 'گفتند: آيا به سوى ما آمده‌اى تا ما را از آيينى كه پدرانمان را بر آن يافته‌ايم بازگردانى و در اين سرزمين بزرگى مخصوص شما دو تن باشد؟ و ما هرگز به شما ايمان‌آور نيستيم'),
(1443, 10, 79, 'و فرعون گفت: همه جادوگران دانا و ماهر را پيش من آريد'),
(1444, 10, 80, 'و چون جادوگران آمدند، موسى به آنها گفت: آنچه قرار است بيفكنيد، بيفكنيد'),
(1445, 10, 81, 'چون بيفكندند، موسى گفت: آنچه آورده‌ايد جادوست و خدا به زودى باطلش مى‌كند كه خدا كار مفسدان را به سامان نمى‌رساند'),
(1446, 10, 82, 'و خدا با كلمات خويش حق را تثبيت مى‌كند، هر چند مجرمان خوش نداشته باشند'),
(1447, 10, 83, 'و جز گروهى از فرزندان قوم موسى به او ايمان نياوردند، آن هم با ترسى از فرعون و اشراف وى كه مبادا عقوبتشان كند. به راستى فرعون در [آن‌] سرزمين خيلى سركش و از افراطكاران بود'),
(1448, 10, 84, 'و موسى گفت: اى قوم من! اگر به خدا ايمان آورده‌ايد، فقط بر او توكل كنيد اگر سر تسليم داريد'),
(1449, 10, 85, 'گفتند: تنها بر خدا توكل كرديم. پروردگارا! ما را مايه آزمون و آزار قوم ستمگر قرار مده'),
(1450, 10, 86, 'و ما را به رحمت خويش از قوم كافر رهايى بخش'),
(1451, 10, 87, 'و به موسى و برادرش وحى كرديم كه براى قوم خود در مصر خانه‌هايى ترتيب دهيد و خانه‌هاى خويش را [به خاطر امنيّت‌] رو به روى هم قرار دهيد و نماز برپا داريد و مؤمنان را مژده ده'),
(1452, 10, 88, 'و موسى گفت: پروردگارا! تو فرعون و مهترانش را در زندگى دنيا زينت و مال و منال داده‌اى، پروردگارا، تا [بندگانت را] از راه تو گمراه كنند. پروردگارا! اموالشان را نابود و دل‌هايشان را سخت گردان كه ايمان نياورند تا عذاب دردناك را ببينند'),
(1453, 10, 89, 'فرمود: دعاى شما دو تن اجابت شد، پس استقامت كنيد و به راه جاهلان نرويد'),
(1454, 10, 90, 'و بنى اسرائيل را از دريا عبور داديم. پس فرعون و سپاهش از سر ظلم و تجاوز آنها را دنبال كردند، تا چون غرق شدنش در رسيد، گفت: ايمان آوردم كه هيچ معبودى نيست جز همان كه بنى اسرائيل به او ايمان آوردند و من از تسليم شدگانم'),
(1455, 10, 91, 'آيا حالا [ايمان مى‌آورى؟] در حالى كه پيش از اين نافرمانى كردى و از تباهكاران بودى'),
(1456, 10, 92, 'ما امروز پيكرت را [از آب‌] مى‌رهانيم تا عبرت آيندگان شوى، و همانا بسيارى از مردم از آيت‌هاى ما بى‌خبرند'),
(1457, 10, 93, 'به راستى ما بنى اسرائيل را در جايگاهى راستين و شايسته جاى داديم و از نعمت‌هاى پاكيزه روزيشان كرديم پس آنها اختلاف نكردند مگر پس از آن كه علم برايشان حاصل شد. همانا پروردگار تو روز قيامت در باره‌ى آنچه اختلاف مى‌كردند ميانشان حكم خواهد كرد'),
(1458, 10, 94, 'و اگر در آنچه به سوى تو نازل كرده‌ايم ترديد دارى، از آنها كه كتاب [آسمانى‌] پيش از تو را مى‌خوانند سؤال كن، [بدان كه‌] حق از جانب پروردگارت به سوى تو آمده است، پس از ترديد كنندگان مباش'),
(1459, 10, 95, 'و از كسانى كه آيات خدا را تكذيب كردند مباش كه از زيانكاران مى‌شوى'),
(1460, 10, 96, 'همانا كسانى كه سخن پروردگارت (وعده‌ى عذاب) در باره‌ى آنها تحقق يافته است ايمان نمى‌آورند'),
(1461, 10, 97, 'گرچه هر گونه معجزه‌اى برايشان مى‌آمد [ايمان نمى‌آوردند] تا اين كه عذاب دردناك را ببينند'),
(1462, 10, 98, 'چرا هيچ شهرى نبود كه [به موقع‌] ايمان بياورد تا ايمان او سودش دهد؟ مگر قوم يونس كه وقتى ايمان آوردند عذاب خوارى را در زندگى دنيا از آنها برداشتيم و تا مدتى [از زندگى دنيا] برخوردارشان كرديم'),
(1463, 10, 99, 'و اگر پروردگار تو مى‌خواست، قطعا كسانى كه در زمين‌اند همه آنها يكسره [به اجبار] ايمان مى‌آوردند. حال آيا تو مردم را مجبور مى‌كنى كه مؤمن شوند'),
(1464, 10, 100, 'و هيچ كس را نرسد كه جز به اذن [و توفيق‌] خدا ايمان بياورد، و [خدا] پليدى را بر كسانى مى‌نهد كه نمى‌انديشند'),
(1465, 10, 101, 'بگو: بنگريد كه در آسمان‌ها و زمين چه [آيت‌] هاست، ولى اين آيت‌ها و هشدارها قومى را كه ايمان نمى‌آورند سود نمى‌بخشد'),
(1466, 10, 102, 'پس آيا آنها جز نظير سرگذشت كسانى را كه پيش از آنها در گذشتند انتظار مى‌برند؟ بگو: منتظر باشيد كه من [نيز] با شما از منتظرانم'),
(1467, 10, 103, 'سپس [هنگام نزول عذاب‌] فرستادگان خود و كسانى را كه گرويده‌اند نجات مى‌دهيم، زيرا بر عهده‌ى ماست كه مؤمنان را نجات دهيم'),
(1468, 10, 104, 'بگو: اى مردم! اگر در دين من ترديد داريد، [بدانيد كه‌] من آنهايى را كه جز خدا مى‌پرستيد نمى‌پرستم، بلكه خدايى را مى‌پرستم كه جان شما را مى‌ستاند و فرمان يافته‌ام كه از مؤمنان باشم'),
(1469, 10, 105, 'و [به من امر شده‌] كه با گرايش به حق به اين دين روى آور و هرگز از مشركان مباش'),
(1470, 10, 106, 'و جز خدا چيزى را كه سود و زيانى به تو نمى‌رساند مخوان، كه اگر چنين كنى، در آن صورت قطعا از ستمكارانى'),
(1471, 10, 107, 'و اگر خدا زيانى به تو برساند، هيچ كس جز او برطرف كننده‌ى آن نيست، و اگر براى تو خيرى بخواهد، كرم او را هيچ بازدارنده‌اى نخواهد بود. آن را به هر كس از بندگانش كه بخواهد مى‌رساند، و او آمرزنده‌ى مهربان است'),
(1472, 10, 108, 'بگو: اى مردم! پيام حق از جانب پروردگارتان براى شما آمده است. پس هر كه هدايت پذيرد تنها به سود خويش هدايت مى‌شود و هر كه گمراه شود فقط به زيان خود گمراه مى‌گردد، و من بر شما نگهبان نيستم'),
(1473, 10, 109, 'و از آنچه بر تو وحى مى‌شود پيروى كن و صبور باش تا خدا داورى كند، و او بهترين داوران است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف، لام، را. كتابى است كه آيات آن استحكام يافته، سپس از جانب حكيمى آگاه توضيح داده شده است'),
(1475, 11, 2, 'كه جز خدا را نپرستيد و من شما را از جانب او بيم دهنده و مژده رسانم'),
(1476, 11, 3, 'و ديگر اين كه از پروردگارتان آمرزش بخواهيد سپس به درگاه او روى كنيد تا شما را از بهره‌اى نيكو تا مدتى معين بهره‌مند سازد و به هر صاحب فضيلتى فضل او را [به تناسب كمالش‌] عطا كند. و اگر رويگردان شويد، من از عذاب روزى بزرگ بر شما بيمناكم'),
(1477, 11, 4, 'بازگشت شما به سوى خداست و او بر هر چيزى تواناست'),
(1478, 11, 5, 'آگاه باش كه آنها سينه‌هاى خود را خم مى‌كنند [و سر به زير مى‌اندازند] تا خود را از او [و شنيدن كلام خدا] پنهان دارند. آگاه باش، آن‌گاه كه جامه‌هايشان را بر سر مى‌كشند [تا به خيال خود پنهان بمانند] خدا هر آنچه را نهان مى‌دارند يا عيان مى‌كنند، مى‌داند.'),
(1479, 11, 6, 'و هيچ جنبنده‌اى در زمين نيست جز آن كه روزى آن بر خداست و قرارگاه اصلى و جايگاه موقت [و محل انتقال‌] آن را مى‌داند. همه در كتابى روشن موجود است'),
(1480, 11, 7, 'و اوست كسى كه آسمان‌ها و زمين را در شش روز آفريد و عرش او بر آب بود، تا بيازمايد كه كدام يك از شما نيكوكارتر است. و اگر بگويى: شما پس از مرگ زنده مى‌شويد، مسلما كسانى كه كافر شدند خواهند گفت: اين جز سحرى آشكار نيست'),
(1481, 11, 8, 'و اگر عذاب را تا چند گاهى از آنان به تأخير اندازيم، [به ريشخند] خواهند گفت: چه چيزى مانع آن مى‌شود؟ آگاه باش، روزى كه عذابشان فرا رسد از آنها بازگردانده نشود، و آنچه مسخره‌اش مى‌كردند آنها را فرا گيرد'),
(1482, 11, 9, 'و اگر از جانب خود رحمتى به انسان بچشانيم، سپس آن را از وى سلب كنيم بسيار مأيوس مى‌شود و كفران مى‌ورزد'),
(1483, 11, 10, 'و اگر پس از محنتى كه به او رسيده نعمتى به او بچشانيم، حتما خواهد گفت: گرفتارى ها از من دور شد [و ديگر بر نمى‌گردد] به راستى او سرمست و فخر فروش است'),
(1484, 11, 11, 'مگر كسانى كه شكيبايى كردند و كارهاى شايسته انجام دادند كه آنان را آمرزش و پاداشى بزرگ است'),
(1485, 11, 12, 'چه بسا [ابلاغ‌] برخى آيات را كه به تو وحى مى‌شود [به خاطر عدم پذيرش آنها] ترك كنى و دلت از آن جهت كه مى‌گويند: چرا بر او گنجى فرستاده نشده يا فرشته‌اى همراه او نيامده است، تنگ شود. ولى تو فقط هشدار دهنده‌اى و خدا بر همه چيز مراقب است'),
(1486, 11, 13, 'آيا مى‌گويند: اين [قرآن‌] را به دروغ بافته است؟ بگو: اگر راست مى‌گوييد [شما هم‌] ده- سوره‌ى ساخته شده همانند آن را بياوريد و غير از خدا هر كه را مى‌توانيد فرا خوانيد'),
(1487, 11, 14, 'پس اگر پاسخ شما را ندادند، بدانيد كه آنچه نازل شده به علم خداست و هيچ معبودى جز او نيست، پس آيا تسليم مى‌شويد'),
(1488, 11, 15, 'هر كه زندگى اين دنيا و تجمّل آن را بخواهد [مزد] اعمالشان را به تمامى در همين دنيا به آنها مى‌دهيم و چيزى از آن كم داده نمى‌شوند'),
(1489, 11, 16, 'اينان كسانى هستند كه در آخرت جز آتش برايشان نخواهد بود و آنچه در دنيا انجام داده‌اند بر باد رفته و آنچه عمل مى‌كردند باطل است'),
(1490, 11, 17, 'آيا كسى كه از جانب پروردگارش بر حجّتى روشن است و شاهدى از [جانب خدا] در پى اوست، و پيش از او نيز كتاب موسى كه پيشوا و رحمتى بود [گواه او است، دروغ مى‌بافد]؟ آنها [كه در پى حقيقت‌اند] به آن ايمان مى‌آورند و هر كه از گروه‌ها به آن كافر شود آتش وعده‌گا'),
(1491, 11, 18, 'و كيست ستمكارتر از آن كسى كه بر خدا دروغ بندد؟ آنان بر پروردگارشان عرضه مى‌شوند، و شاهدان مى‌گويند: همين كسانند كه بر پروردگارشان دروغ بستند. هان، لعنت خدا بر ظالمان باد'),
(1492, 11, 19, 'همانان كه [مردم را] از راه خدا باز مى‌دارند و راه خدا را منحرف مى‌خواهند و آخرت را هم باور ندارند'),
(1493, 11, 20, 'آنها در زمين عاجز كننده‌ى خدا نبودند و جز خدا سرپرستى نداشتند. عذاب براى آنان دو چندان مى‌شود، كه نه توان شنيدن [آيات حق را] داشتند و نه مى‌ديدند'),
(1494, 11, 21, 'آنها هستند كه خويشتن را ارزان باختند و آنچه به دروغ برساخته بودند از دست دادند'),
(1495, 11, 22, 'و ناگزير آنها در آخرت [از همه‌] زيانكارترند'),
(1496, 11, 23, 'بى‌گمان كسانى كه ايمان آورده و كارهاى شايسته كردند و به پروردگارشان خاضعانه دل بستند، اهل بهشتند و در آن جاودانه خواهند بود'),
(1497, 11, 24, 'مثل اين دو گروه مثل كور و كر و بينا و شنواست، آيا در مثل يكسانند؟ پس آيا پند نمى‌گيريد'),
(1498, 11, 25, 'و به راستى نوح را به سوى قومش فرستاديم، [كه بگويد] من براى شما بيم‌رسانى آشكارم'),
(1499, 11, 26, 'كه جز خدا را نپرستيد همانا من از عذاب روزى دردناك بر شما بيمناكم'),
(1500, 11, 27, 'پس سران قومش كه كافر شدند، گفتند: ما تو را جز بشرى مثل خود نمى‌بينيم، و نمى‌بينيم كسى جز فرومايگان ما بدون تأمل از تو پيروى كرده باشد، و نمى‌بينيم كه شما را بر ما فضيلتى باشد، بلكه شما را دروغگو مى‌انگاريم'),
(1501, 11, 28, 'گفت: اى قوم من! به من بگوييد اگر از پروردگارم حجّتى روشن داشته باشم و مرا از نزد خود رحمتى داده باشد كه بر شما پوشيده است، [باز هم مرا انكار مى‌كنيد؟] آيا [مى‌توانيم‌] شما را به [پذيرفتن‌] آن مجبور سازيم، در حالى كه از آن اكراه داريد'),
(1502, 11, 29, 'و اى قوم من! بر اين [رسالت‌] مالى از شما درخواست نمى‌كنم، مزد من فقط با خداست، و كسانى را كه ايمان آورده‌اند از خود نمى‌رانم [و] آنها قطعا خداى خويش را ملاقات خواهند كرد، و ليكن من شما را مردمى نادان مى‌بينم'),
(1503, 11, 30, 'و اى قوم من! اگر آنها را طرد كنم، چه كسى مرا در برابر خدا يارى خواهد كرد؟ آيا توجه نمى‌كنيد'),
(1504, 11, 31, 'و به شما نمى‌گويم خزاين الهى نزد من است، و غيب هم نمى‌دانم و نمى‌گويم من فرشته‌ام، و در باره‌ى كسانى كه در چشم شما خوار مى‌آيند، [مثل شما] نمى‌گويم كه خدا هرگز خيرشان نخواهد داد. خدا به آنچه در ضمير آنهاست آگاه‌تر است. [پس اگر طردشان كنم‌] من در آن ص'),
(1505, 11, 32, 'گفتند: اى نوح! واقعا با ما جدال كردى، و بسيار هم جدال كردى پس اگر راست مى‌گويى آنچه را به ما وعده مى‌دهى بر سر ما بياور'),
(1506, 11, 33, 'گفت: تنها خداست كه اگر بخواهد آن را براى شما مى‌آورد و شما عاجز كننده‌ى [او] نيستيد'),
(1507, 11, 34, 'و اگر من بخواهم شما را اندرز دهم، در صورتى كه خداوند [به سزاى كفرتان‌] بخواهد گمراهتان كند، اندرز من شما را سودى نخواهد بخشيد. او پروردگار شماست و به سوى او بازگردانده مى‌شويد'),
(1508, 11, 35, 'آيا [در باره‌ى قرآن‌] مى‌گويند: آن را خود بافته است؟ بگو: اگر آن را بافته باشم، گناهم بر عهده‌ى من است و من از گناهانى كه شما مى‌كنيد بيزارم'),
(1509, 11, 36, 'و به نوح وحى شد كه از قوم تو جز آنها كه [تاكنون‌] ايمان آورده‌اند ديگر كسى ايمان نخواهد آورد، پس از آنچه مى‌كنند غمگين مباش'),
(1510, 11, 37, 'و كشتى را زير نظر ما و به اشاره‌ى ما بساز، و در باره‌ى كسانى كه ستم كردند با من سخن مگوى كه آنها غرق شدنى هستند'),
(1511, 11, 38, 'و [نوح‌] كشتى را مى‌ساخت و هر بار كه اشرافى از قومش بر او مى‌گذشتند مسخره‌اش مى‌كردند. گفت: اگر ما را مسخره كنيد، ما [نيز] شما را همان‌گونه كه مسخره مى‌كنيد مسخره خواهيم كرد'),
(1512, 11, 39, 'به زودى خواهيد دانست كه عذاب [الهى‌] سراغ چه كسى مى‌آيد كه خوارش سازد و عذابى پايدار بر سرش فرود آيد'),
(1513, 11, 40, 'تا آن‌گاه كه فرمان ما در رسيد و تنور جوشيدن گرفت، فرموديم: از هر جفتى [از حيوانات‌] دو تا و خاندان خود را- مگر كسى كه قبلا در باره‌ى او حكم صادر شده- و نيز كسانى را كه ايمان آورده‌اند در كشتى سوار كن و جز عدّه‌ى كمى با او ايمان نياوردند'),
(1514, 11, 41, 'و [نوح‌] گفت: در آن سوار شويد. به نام خداست حركت آن و به نام خداست ايستادن آن [و] پروردگار من آمرزنده‌ى مهربان است'),
(1515, 11, 42, 'و آن [كشتى‌] آنها را در ميان امواجى همچون كوه‌ها مى‌برد، و نوح پسرش را كه در كنارى بود صدا زد: اى پسرك من! با ما سوار شو و با كافران مباش'),
(1516, 11, 43, 'گفت: به زودى به كوهى پناه خواهم برد كه مرا از آب در امان نگاه دارد. [نوح‌] گفت: امروز از فرمان خداوند هيچ نگاه دارنده‌اى نيست مگر كسى كه [خدا بر او] رحم كند. و موج ميانشان حايل شد و از غرق شدگان گرديد'),
(1517, 11, 44, 'و گفته شد: اى زمين! آب خود فرو بر، و اى آسمان بازايست، و آب فروشد و كار پايان يافت و [كشتى‌] بر جودى قرار گرفت و گفته شد: مرگ بر ستمگران'),
(1518, 11, 45, 'و نوح پروردگار خود را ندا كرد و گفت: پروردگارا! پسرم از خاندان من است و قطعا وعده‌ى تو راست است و تو بهترين داورانى'),
(1519, 11, 46, 'فرمود: اى نوح! در حقيقت او از كسان تو نيست، او عملى ناصالح است، پس چيزى را كه بدان علم ندارى از من مخواه. من به تو اندرز مى‌دهم كه مبادا از جاهلان باشى'),
(1520, 11, 47, 'گفت: پروردگارا! من به تو پناه مى‌برم كه از تو چيزى بخواهم كه بدان علم ندارم، و اگر مرا نيامرزى و بر من ترحم نكنى از زيانكاران مى‌شوم'),
(1521, 11, 48, 'گفته شد: اى نوح! [از كشتى‌] فرود آى با سلامت و بركاتى از ما بر تو و بر گروه‌هايى كه با تواند، و گروه‌هايى نيز هستند كه برخوردارشان خواهيم كرد، آن‌گاه عذاب دردناكى از ما به آنها خواهد رسيد'),
(1522, 11, 49, 'اين‌ها از خبرهاى غيب است كه به تو وحى مى‌كنيم. پيش از اين نه تو آن را مى‌دانستى و نه قوم تو. پس صبورى كن كه عاقبت [نيك‌] از آن پرهيزكاران است'),
(1523, 11, 50, 'و به سوى قوم عاد، برادرشان هود را [فرستاديم‌]. گفت: اى قوم من! خدا را بپرستيد كه شما را هيچ خدايى جز او نيست، [و] شما [در شرك به خدا] دروغبافانى بيش نيستيد'),
(1524, 11, 51, 'اى قوم من! براى اين كار از شما مزدى نمى‌خواهم. مزد من فقط با آن كسى است كه مرا آفريد. پس آيا تعقل نمى‌كنيد'),
(1525, 11, 52, 'و اى قوم من! از پروردگار خويش آمرزش بخواهيد و به درگاه او توبه كنيد تا از آسمان بر شما باران پياپى فرستد و نيرويى بر نيروى شما بيفزايد، و خلافكارانه روى برمتابيد'),
(1526, 11, 53, 'گفتند: اى هود! براى ما دليل روشنى نياورده‌اى، و ما به گفته تو از خدايان خويش دست بردار نيستيم و ما هرگز تو را باور نداريم'),
(1527, 11, 54, '[چيزى‌] جز اين نمى‌گوييم كه بعضى از خدايان ما به تو آسيب رسانده‌اند. گفت: من خدا را گواه مى‌گيرم و [شما نيز] شاهد باشيد كه من از آنچه شريك او مى‌كنيد بيزارم'),
(1528, 11, 55, 'پس همگى [در باره‌] من نيرنگ كنيد سپس مهلتم ندهيد'),
(1529, 11, 56, 'بى‌گمان، من بر خداوند كه پروردگار من و پروردگار شماست توكّل كردم. هيچ جنبنده‌اى نيست مگر اين كه خداوند گيرنده‌ى زمام هستى اوست. به راستى پروردگار من بر راه راست است'),
(1530, 11, 57, 'پس اگر روى بگردانيد، به يقين [بدانيد] آنچه را كه به منظور آن به سوى شما فرستاده شده‌ام به شما رساندم و پروردگار من قومى غير از شما را جانشين [شما] خواهد كرد و به او ضررى نتوانيد زد. بى‌شك پروردگار من حافظ هر چيزى است'),
(1531, 11, 58, 'و چون فرمان ما در رسيد، هود و كسانى را كه با او گرويده بودند به رحمتى از جانب خود نجات داديم و آنها را از عذابى سخت رهانيديم'),
(1532, 11, 59, 'و اين [قوم‌] عاد بودند كه آيات پروردگارشان را انكار كردند و فرستادگانش را نافرمانى نمودند و از فرمان هر جبّار دشمن حق پيروى كردند'),
(1533, 11, 60, 'و [سر انجام‌] در اين دنيا و روز قيامت با لعنت و نفرين بدرقه شدند. آگاه باشيد كه عاديان به پروردگارشان كافر شدند. اى طرد و هلاك باد بر عاديان، قوم هود'),
(1534, 11, 61, 'و به سوى [قوم‌] ثمود، برادرشان صالح را [فرستاديم‌]. گفت: اى قوم من! خدا را بپرستيد كه براى شما هيچ معبودى جز او نيست. اوست كه شما را از زمين پديد آورد و شما را به عمران آن وا داشت. پس از او آمرزش بخواهيد، آن‌گاه به درگاه او توبه كنيد كه پروردگار من ['),
(1535, 11, 62, 'گفتند: اى صالح! به راستى تو پيش از اين ميان ما مايه اميد بودى، آيا ما را از پرستش آنچه پدرانمان مى‌پرستيدند باز مى‌دارى؟ و بى‌گمان ما از آنچه تو ما را بدان مى‌خوانى سخت دچار شكّيم و [نسبت به آن‌] مردّديم'),
(1536, 11, 63, 'گفت: اى قوم من! چه مى‌گوييد اگر [در اين فراخوان‌] بر حجّتى روشن از پروردگار خود باشم و از جانب خود رحمتى به من داده باشد، چه كسى در برابر خدا مرا يارى مى‌كند اگر او را نافرمانى كنم؟ پس شما مرا جز خسارت نمى‌افزاييد'),
(1537, 11, 64, 'و اى قوم من! اين ماده شتر خداست كه براى شما معجزه‌اى است. پس بگذاريدش در زمين خدا بچرد و آسيبش نرسانيد كه عذابى زودرس شما را فرا خواهد گرفت'),
(1538, 11, 65, 'امّا آن شتر را پى كردند و [صالح‌] گفت: سه روز در خانه‌هايتان بهره بريد [تا عذاب فرا رسد]. اين وعده‌اى بى‌دروغ است'),
(1539, 11, 66, 'پس همين كه فرمان ما سر رسيد، صالح و كسانى را كه با او ايمان آورده بودند به رحمت خود از بلا و رسوايى آن روز نجات داديم. به يقين پروردگار تو همو تواناى شكست‌ناپذير است'),
(1540, 11, 67, 'و آن صيحه مرگبار كسانى را كه ستم كرده بودند فرا گرفت و در خانه‌هايشان بر جاى مردند'),
(1541, 11, 68, '[چنان كه‌] گويا اصلا در آن جا نبوده‌اند. آگاه باشيد كه ثموديان به پروردگارشان كافر شدند. هان! طرد و هلاك باد ثموديان را'),
(1542, 11, 69, 'و به راستى فرستادگان ما براى ابراهيم مژده آوردند. گفتند: سلام بر تو. [او نيز در جواب‌] گفت: سلام بر شما. و طولى نكشيد كه گوساله‌اى بريان پيش آورد'),
(1543, 11, 70, 'و چون ديد دستشان به غذا نمى‌رود آنها را غير عادى يافت و از آنها احساس ترس كرد. گفتند: مترس كه ما به سوى قوم لوط فرستاده شده‌ايم'),
(1544, 11, 71, 'و زن او ايستاده بود. [از خوشحالى‌] بخنديد و ما وى را به اسحاق و از پى اسحاق به يعقوب مژده داديم'),
(1545, 11, 72, 'گفت: اى واى بر من! آيا من فرزند مى‌آورم در حالى كه پيرزنم و اين شوهرم پيرمرد است؟ واقعا اين چيز بسيار عجيبى است'),
(1546, 11, 73, 'گفتند: آيا از كار خدا تعجّب مى‌كنى؟ رحمت خدا و بركات او بر شما خانواده است. همانا او ستوده‌ى بزرگوار است'),
(1547, 11, 74, 'پس وقتى ترس از ابراهيم برفت و بشارت به او رسيد، در باره‌ى قوم لوط با ما [به قصد شفاعت‌] چون و چرا مى‌كرد'),
(1548, 11, 75, 'همانا ابراهيم بردبار و بسيار نيايشگر و توبه كار بود'),
(1549, 11, 76, 'اى ابراهيم! از اين [درخواست‌] صرف نظر كن كه فرمان پروردگارت آمده و عذابى برگشت ناپذير به سراغشان خواهد آمد'),
(1550, 11, 77, 'و چون فرستادگان ما نزد لوط آمدند از [آمدن‌] آنان بدحال شد و در كارشان فرو ماند و گفت: امروز روز سختى است'),
(1551, 11, 78, 'و قوم او شتابان [به قصد مزاحمت مهمانان‌] به سوى او هجوم آوردند، در حالى كه پيش از آن [هم‌] مرتكب زشتى‌ها مى‌شدند. [لوط] گفت: اى قوم من! اينان دختران منند آنان براى شما پاكيزه‌ترند [با آنها ازدواج كنيد]، پس از خدا بترسيد و مرا در كار مهمانانم رسوا مكن'),
(1552, 11, 79, 'گفتند: تو خوب مى‌دانى كه ما را به دختران تو حاجتى نيست و تو خوب مى‌دانى كه ما چه مى‌خواهيم'),
(1553, 11, 80, '[لوط] گفت: اى كاش در برابر شما قدرتى مى‌داشتم يا به تكيه‌گاهى قوى پناه مى‌بردم'),
(1554, 11, 81, 'گفتند: اى لوط! ما فرستادگان پروردگار توييم. آنها هرگز به تو دست نخواهند يافت. پس پاسى از شب گذشته خانواده‌ات را حركت ده و كسى از شما پشت سرش را نگاه نكند مگر زنت را كه هر چه به آنها برسد به او نيز خواهد رسيد. همانا موعد آنها صبح است. آيا صبح نزديك ني'),
(1555, 11, 82, 'پس چون فرمان ما آمد، آن [شهر] را زير و رو كرديم و بر آن سنگ‌هايى از سجّيل پى‌درپى بارانديم'),
(1556, 11, 83, '[سنگ‌هايى‌] كه نزد پروردگارت نشاندار بود و آن از [ساير] ستمگران به دور نيست'),
(1557, 11, 84, 'و به سوى [اهل‌] مدين برادرشان شعيب را [فرستاديم‌]. گفت: اى قوم من! خداوند را بپرستيد كه براى شما جز او معبودى نيست، و پيمانه و ترازو را مكاهيد، به راستى شما را در نعمت و خوشى مى‌بينم، ولى از عذاب روزى فراگير بر شما بيمناكم'),
(1558, 11, 85, 'و اى قوم من! پيمانه و ترازو را به انصاف تمام دهيد و از اجناس مردم نكاهيد و در زمين سر به فساد بر مداريد'),
(1559, 11, 86, 'اگر مؤمن باشيد آنچه خدا [از كسب حلال‌] براى شما باقى گذارده بهتر است و من بر شما نگاهبان نيستم'),
(1560, 11, 87, 'گفتند: اى شعيب! آيا نمازت تو را بر آن مى‌دارد كه آنچه را پدرانمان مى‌پرستيدند رها كنيم يا در اموال خود هر طور كه بخواهيم عمل نكنيم؟ همانا تو مردى بردبار و فرزانه‌اى [پس چرا ما را محدود مى‌كنى‌]'),
(1561, 11, 88, 'گفت: اى قوم من! چه مى‌گوييد اگر از جانب پروردگارم دليلى روشن داشته باشم و او مرا موهبتى نيكو (نبوت) عطا كرده باشد [آيا مى‌توانم از فرمانش سرپيچى كنم؟] من نمى‌خواهم در آنچه شما را از آن باز مى‌دارم خود خلاف آن كنم و تا آن جا كه بتوانم جز اصلاح نمى‌خوا'),
(1562, 11, 89, 'و اى قوم من! دشمنى با من شما را بر آن ندارد [كارى كنيد] كه به شما آن رسد كه به قوم نوح يا قوم هود يا قوم صالح رسيد، و قوم لوط از شما چندان دور نيست'),
(1563, 11, 90, 'و از پروردگار خود آمرزش بخواهيد، سپس به سوى او بازگرديد كه پروردگار من مهربان و دوستدار [بندگان‌] است'),
(1564, 11, 91, 'گفتند: اى شعيب! بسيارى از آنچه را كه مى‌گويى نمى‌فهميم و ما به راستى تو را در ميان خود ناتوان مى‌بينيم، و اگر [به احترام‌] عشيره‌ات نبود سنگسارت مى‌كرديم، و تو بر ما غالب نيستى'),
(1565, 11, 92, 'گفت: اى قوم من! آيا عشيره‌ى من در نزد شما عزيزتر از خداست كه او را پشت سر نهاده‌ايد؟ بى‌ترديد پروردگار من به آنچه مى‌كنيد احاطه دارد'),
(1566, 11, 93, 'و اى قوم من! شما هر چه در توان داريد عمل كنيد، من [نيز] عمل مى‌كنم. به زودى خواهيد دانست كه به چه كسى عذابى رسد كه رسوايش كند؟ و دروغگو كيست؟ منتظر باشيد كه من نيز با شما منتظرم'),
(1567, 11, 94, 'و چون فرمان ما آمد، شعيب و كسانى را كه با او ايمان آورده بودند به رحمت خويش نجات داديم و كسانى را كه ستم كرده بودند صيحه بگرفت و در خانه‌هايشان به رو افتادند [و هلاك شدند]'),
(1568, 11, 95, 'گويى هرگز در آن [خانه‌ها] نبوده‌اند. هان كه طرد و هلاك باد [مردم‌] مدين را، همان گونه كه ثمود هلاك شدند'),
(1569, 11, 96, 'و به راستى موسى را با آيات خود و حجتى روشن فرستاديم'),
(1570, 11, 97, 'به سوى فرعون و سران قوم وى، ولى آنها از فرمان فرعون پيروى كردند و فرمان فرعون هدايتگر نبود'),
(1571, 11, 98, 'روز قيامت پيشاپيش قومش مى‌رود و آنها را به آتش وارد مى‌كند، و بد جاى ورودى است'),
(1572, 11, 99, 'و در اين دنيا و روز قيامت به لعنت بدرقه شدند، و بد عطايى نصيبشان شد'),
(1573, 11, 100, 'اين از خبرهاى آن شهرهاست كه آن را بر تو حكايت مى‌كنيم. برخى از آنها برپا هستند و برخى ويران شده‌اند'),
(1574, 11, 101, 'و ما به آنها ستم نكرديم، بلكه آنها [بودند كه‌] به خودشان ستم كردند. پس چون فرمان پروردگارت آمد، خدايانى كه به جاى خدا مى‌خواندند هيچ مشكلى را از آنها دور نكردند و جز بر هلاكشان نيفزودند'),
(1575, 11, 102, 'و اين گونه است گرفتار كردن پروردگارت آن‌گاه كه مردم شهرها را به سزاى ظلمشان گرفتار مى‌كند. بى‌ترديد مجازات خداوند دردناك و سخت است'),
(1576, 11, 103, 'قطعا در اين [يادآورى‌ها] براى كسى كه از عذاب آخرت مى‌ترسد عبرتى است. آن [روز] روزى است كه مردم براى آن گرد آورده شوند و آن [روز] روز حضور و شهود است'),
(1577, 11, 104, 'و ما آن را جز تا زمانى محدود به تأخير نمى‌اندازيم'),
(1578, 11, 105, 'روزى فرا رسد كه هيچ كس جز به اذن او سخن نگويد، پس بعضى از مردم تيره‌بخت و برخى نيكبخت‌اند'),
(1579, 11, 106, 'اما كسانى كه تيره‌بخت شدند، برايشان در آتش ضجّه و ناله است'),
(1580, 11, 107, 'تا آسمان‌ها و زمين برپاست در آن ماندگار خواهند بود، مگر آن كه پروردگار تو بخواهد، كه بى‌ترديد خداى تو هر چه را خواهد كننده است'),
(1581, 11, 108, 'و اما كسانى كه نيكبخت شدند، تا آسمان‌ها و زمين برپاست جاودانه در بهشت خواهند بود، مگر آن كه پروردگارت بخواهد، كه اين عطايى قطع ناشدنى است'),
(1582, 11, 109, 'پس در مورد آنچه ايشان مى‌پرستند در ترديد مباش. آنها جز مانند آنچه را كه پدرانشان قبلا مى‌پرستيدند نمى‌پرستند و ما قطعا بهره‌ى آنها را كامل و بى‌كم و كاست خواهيم داد'),
(1583, 11, 110, 'و همانا ما به موسى كتاب [آسمانى‌] داديم، پس در مورد آن اختلاف شد، و اگر پيش‌تر وعده‌ى [مهلت‌] از جانب خدا صادر نشده بود، حتما ميانشان داورى مى‌شد، و همانا ايشان در باره‌ى آن سخت در شكّ و ترديدند'),
(1584, 11, 111, 'و قطعا پروردگارت [حاصل‌] اعمال همه را تماما به آنها خواهد داد، [و] او به آنچه مى‌كنند آگاه است'),
(1585, 11, 112, 'پس همان گونه كه فرمان يافته‌اى پايدارى كن و نيز هر كه با تو رو به سوى خدا آورده است. و سركشى نكنيد كه او به آنچه مى‌كنيد بيناست'),
(1586, 11, 113, 'و به كسانى كه ستم كرده‌اند تمايل نشان ندهيد كه آتش [دوزخ‌] شما را مى‌گيرد در حالى كه غير از خدا شما را مولا و سرپرستى نيست و آن‌گاه يارى نخواهيد شد'),
(1587, 11, 114, 'و در دو طرف روز و اوايل شب نماز را برپا دار، كه نيكى‌ها بدى‌ها را از بين مى‌برد. اين تذكرى براى پند گيرندگان است'),
(1588, 11, 115, 'و صبر كن كه خدا پاداش نيكوكاران را ضايع نمى‌كند'),
(1589, 11, 116, 'از چه رو در ميان نسل‌هاى پيش از شما خردمندانى نبودند كه [مردم را] از فساد در زمين نهى كنند [تا گرفتار عذاب نشوند]؟ جز اندكى از كسانى از آنها كه نجاتشان داديم (پيامبران و پيروانشان). و كسانى كه ستم كردند، [طبعا] به دنبال رفاه و خوشگذرانى رفتند و اهل'),
(1590, 11, 117, 'و از سنت خداى تو نبوده است كه شهرها را در حالى كه مردمش اصلاحگرند به ستم هلاك كند'),
(1591, 11, 118, 'و اگر پروردگار تو مى‌خواست، همه مردم را [به اجبار] يك امّت [و بر يك دين‌] قرار مى‌داد، و حال آن كه آنها همواره در اختلافند'),
(1592, 11, 119, 'مگر كسى كه پروردگارت به او مرحمتى كند، و براى همين آنان را خلق كرده است، و سخن پروردگارت بر اين رفته است كه حتما جهنم را از جنّ و انس [نافرمان‌] يكسره پر خواهم كرد'),
(1593, 11, 120, 'و هر كدام از سرگذشت‌هاى پيامبران را كه بر تو حكايت مى‌كنيم، چيزى است كه دلت را بدان استوار مى‌گردانيم، و در اين [سرگذشت‌ها] حق براى تو آمده و مؤمنان را اندرز و تذكرى است'),
(1594, 11, 121, 'و به كسانى كه ايمان نمى‌آورند بگو: با هر امكانى كه داريد عمل كنيد كه ما هم عمل خواهيم كرد'),
(1595, 11, 122, 'و منتظر باشيد كه ما نيز منتظريم'),
(1596, 11, 123, 'و نهان آسمان‌ها و زمين از آن خداست، و تمام كارها [سر انجام‌] به او بازگردانده مى‌شود. پس او را پرستش كن و بر او توكّل نماى، و پروردگار تو از آنچه مى‌كنيد غافل نيست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، را. اينها آيات كتاب روشنگر است'),
(1598, 12, 2, 'ما آن را قرآنى عربى نازل كرديم، باشد كه شما بينديشيد'),
(1599, 12, 3, 'ما بهترين سرگذشت را به موجب اين قرآن كه به تو وحى كرديم بر تو حكايت مى‌كنيم، و تو بى‌ترديد پيش از آن از بى‌خبران بودى'),
(1600, 12, 4, '[ياد كن‌] زمانى را كه يوسف به پدرش گفت: اى پدر! همانا من در خواب يازده ستاره را با خورشيد و ماه ديدم، آنها را ديدم كه براى من سجده مى‌كنند. انّى رأيت: اى پدر من! همانا من ديدم. رأيت (رأى): ديدم. احد عشر كوكبا: يازده ستاره. ساجدين جمع ساجد: سجده كنان.'),
(1601, 12, 5, '[يعقوب‌] گفت: اى پسرك من! خوابت را براى برادرانت حكايت مكن كه براى تو حيله‌اى مى‌انديشند همانا شيطان براى انسان دشمنى آشكار است'),
(1602, 12, 6, 'و اين گونه، پروردگارت تو را بر مى‌گزيند و از تعبير خواب‌ها به تو مى‌آموزد و نعمتش را بر تو و بر خاندان يعقوب تمام مى‌كند، همان گونه كه قبلا بر پدران تو ابراهيم و اسحاق تمام كرد. همانا پروردگار تو داناى حكيم است'),
(1603, 12, 7, 'به راستى در ماجراى يوسف و برادرانش براى سؤال كنندگان عبرت‌هاست'),
(1604, 12, 8, 'آن‌گاه كه گفتند: يوسف و برادرش نزد پدرمان با آن كه ما جمعى نيرومند هستيم محبوب‌ترند. قطعا پدر ما در گمراهى آشكارى است'),
(1605, 12, 9, 'يوسف را بكشيد يا او را به سرزمينى [دور] بيندازيد تا توجه پدرتان معطوف شما گردد و پس از او مردمى صالح باشيد'),
(1606, 12, 10, 'يكى از آنها گفت: يوسف را مكشيد. اگر كارى مى‌كنيد، او را در نهانخانه چاه بيفكنيد تا يكى از كاروان‌ها او را برگيرد'),
(1607, 12, 11, 'گفتند: اى پدر! تو را چه شده است كه ما را بر يوسف امين نمى‌دانى در حالى كه ما قطعا خيرخواه او هستيم؟'),
(1608, 12, 12, 'فردا او را با ما بفرست تا در چمن گردش و بازى كند و ما به خوبى نگهبان او خواهيم بود'),
(1609, 12, 13, 'گفت: اين كه او را ببريد، سخت مرا اندوهگين مى‌كند و مى‌ترسم از او غافل شويد و گرگ او را بخورد'),
(1610, 12, 14, 'گفتند: اگر گرگ او را بخورد با اين كه ما گروهى نيرومند هستيم، البته در اين صورت ما زيانكار خواهيم بود'),
(1611, 12, 15, 'پس وقتى او را بردند و همداستان شدند تا او را در نهانخانه چاه بگذارند [چنين كردند]، و به او وحى كرديم كه قطعا آنها را از اين كارشان در حالى كه نمى‌دانند خبرخواهى داد'),
(1612, 12, 16, 'و وقت غروب گريان نزد پدر خود آمدند'),
(1613, 12, 17, 'گفتند: اى پدر! ما رفتيم مسابقه دهيم و يوسف را پيش اثاث خود نهاديم، آن‌گاه گرگ او را خورد، ولى تو سخن ما را هر چند راستگو باشيم باور نخواهى كرد'),
(1614, 12, 18, 'و بر پيراهن وى خونى دروغين آوردند، [يعقوب‌] گفت: [نه‌]، بلكه نفس شما كارى را براى شما آراسته است. پس اينك صبرى نيكو [بهتر است‌]، و بر آنچه توصيف مى‌كنيد از خداوند يارى مى‌خواهم'),
(1615, 12, 19, 'و كاروانى آمد و آب آورشان را فرستادند، پس او دلو خود را انداخت. گفت: مژده باد! اين يك پسر بچه است. و او را به عنوان كالايى پنهان داشتند، و خدا به آنچه مى‌كردند آگاه بود'),
(1616, 12, 20, 'و او را به بهاى ناچيزى، چند درهمى، فروختند و در [مورد] آن بى‌رغبت بودند [چون مى‌دانستند صاحب دارد]'),
(1617, 12, 21, 'و آن كس از مصر كه او را خريده بود به همسرش گفت: جايگاه او را گرامى بدار، شايد به حال ما سودمند باشد يا او را به فرزندى اختيار كنيم. و بدين گونه ما يوسف را در آن سرزمين تمكّن بخشيديم، و تا به او تأويل رؤياها را بياموزيم، و خدا بر كار خويش غالب است ول'),
(1618, 12, 22, 'و چون به حدّ رشد رسيد، او را حكمت و دانش عطا كرديم، و نيكوكاران را چنين پاداش مى‌دهيم'),
(1619, 12, 23, 'و آن زنى كه يوسف در خانه‌اش بود از او كام خواست، و درها را محكم بست و گفت: بيا كه از آن توام. يوسف گفت: پناه بر خدا! او پروردگار من است [و] جايگاه مرا نيكو داشته است. قطعا ستمكاران رستگار نمى‌شوند'),
(1620, 12, 24, 'و آن زن آهنگ وى كرد، و يوسف نيز اگر برهان پروردگارش را نديده بود آهنگ او مى‌كرد. [و وى را مى‌زد و متهم مى‌شد] چنين كرديم تا بدى و زشتكارى را از او بازگردانيم، او از بندگان خالص شده‌ى ما بود'),
(1621, 12, 25, 'و آن دو به سوى در از يكديگر پيشى گرفتند، و زن پيراهن وى را از پشت بدريد، و در كنار در آقاى زن را يافتند. زن گفت: جزاى كسى كه قصد بدى به خانواده‌ى تو كرده جز اين كه زندانى يا كيفر دردناكى شود چه خواهد بود'),
(1622, 12, 26, 'يوسف گفت: او از من كام خواست. و شاهدى از خانواده‌ى آن زن شهادت داد كه اگر پيراهن او از جلو چاك خورده، زن راست گفته و او از دروغگويان است'),
(1623, 12, 27, 'و اگر پيراهنش از پشت دريده شده، زن دروغ گفته و او از راستگويان است'),
(1624, 12, 28, 'پس چون [شوهرش‌] ديد پيراهن او از پشت چاك خورده گفت: بى‌شك، اين از مكر شما زنان است، كه مكر شما بزرگ است'),
(1625, 12, 29, 'اى يوسف! از اين [پيشامد] درگذر، و اى زن تو نيز براى گناه خود استغفار كن كه تو از خطاكاران بوده‌اى'),
(1626, 12, 30, 'و زنانى در شهر گفتند: زن عزيز از غلام خود كام مى‌خواهد و سخت دلداده‌ى او شده است، به راستى ما او را در گمراهى آشكارى مى‌بينيم'),
(1627, 12, 31, 'پس همين كه [همسر عزيز] مكرشان را بشنيد، در پى آنان فرستاد و محفلى برايشان آماده ساخت و به هر يك از آنها [ميوه و] كاردى داد و به يوسف گفت: بر آنان درآى. پس چون زنان او را ديدند [حيران وى شده‌] بزرگش يافتند [و از شدت هيجان‌] دست‌هاى خود را بريدند و گفت'),
(1628, 12, 32, 'گفت: اين همان است كه در باره‌ى او ملامتم مى‌كرديد. آرى، من از او كام خواستم و او خويشتن نگاه داشت، و اگر آنچه را دستورش مى‌دهم نكند، قطعا زندانى خواهد شد و حتما از حقيران خواهد گرديد'),
(1629, 12, 33, '[يوسف‌] گفت: پروردگارا! زندان براى من از آنچه مرا به آن مى‌خوانند محبوب‌تر است، و اگر مكرشان را از من بازنگردانى به سوى آنان ميل خواهم كرد و از نادانان خواهم شد'),
(1630, 12, 34, 'پس پروردگارش دعاى او را اجابت كرد و مكر زنان را از او بگردانيد. آرى او شنواى داناست'),
(1631, 12, 35, 'آن‌گاه پس از ديدن همه نشانه‌هاى [پاكى يوسف‌] به نظرشان رسيد كه او را تا چندى به زندان افكنند'),
(1632, 12, 36, 'و دو جوان با يوسف وارد زندان شدند. [روزى‌] يكى از آنها گفت: من خويشتن را به خواب ديدم كه [انگور براى‌] شراب مى‌فشارم، و ديگرى گفت: من خود را به خواب ديدم كه بر روى سرم نان مى‌برم و پرندگان از آن مى‌خورند، ما را از تعبيرش آگاه كن كه ما تو را از نيكوكا'),
(1633, 12, 37, 'گفت: تا غذايى را كه روزى خواهيد شد برايتان بياورند، من از تعبير آن پيش از آن كه وقوع يابد شما را آگاه مى‌سازم، و اين از آن چيزهايى است كه پروردگارم به من آموخته است. من آيين قومى را كه به خداوند ايمان ندارند و منكر آخرتند رها كرده‌ام'),
(1634, 12, 38, 'و از آيين پدران خويش ابراهيم و اسحاق و يعقوب پيروى كرده‌ام. براى ما سزاوار نيست كه چيزى را شريك خدا كنيم. اين از كرم خدا بر ما و بر مردم است، ولى بيشتر مردم ناسپاسند'),
(1635, 12, 39, 'اى دو رفيق زندانى‌ام! آيا خدايان پراكنده بهترند يا خداى يگانه قهار'),
(1636, 12, 40, 'شما غير خدا، جز نام‌هايى را كه خود و پدرانتان آنها را ناميده‌ايد، نمى‌پرستيد كه خداوند بر آن هيچ دليلى نازل نكرده است. حكم فقط از آن خداست [و] فرمان داده است غير او را نپرستيد. اين دين استوار است، ولى بيشتر مردم نمى‌دانند'),
(1637, 12, 41, 'اى دوستان زندانى من! اما يكى از شما خواجه خود را شراب مى‌دهد و اما ديگرى بردار مى‌شود و مرغان از سر او مى‌خورند. موضوعى كه شما دو تن از من نظر خواستيد قطعى و حتمى است'),
(1638, 12, 42, 'و به يكى از آن دو كه مى‌دانست آزاد شدنى است گفت: مرا نزد صاحب خود ياد كن، ولى شيطان يادآورى او را در نزد صاحبش از خاطر وى برد، در نتيجه [يوسف‌] چند سالى در زندان ماند'),
(1639, 12, 43, 'و شاه گفت: من در خواب ديدم كه هفت گاو فربه را هفت گاو لاغر مى‌خورند، و هفت خوشه سبز و هفت خوشه خشكيده‌ى ديگر. اى مهتران! اگر خواب تعبير مى‌كنيد، در باره‌ى خواب من نظر بدهيد'),
(1640, 12, 44, 'گفتند: [اينها] خواب‌هاى آشفته است و ما به تعبير خواب‌هاى آشفته آگاه نيستيم'),
(1641, 12, 45, 'و آن كس از آن دو زندانى كه نجات يافته بود و پس از مدتى يوسف را به خاطر آورد گفت: من از تعبير آن خبرتان مى‌دهم، پس مرا [به زندان‌] بفرستيد'),
(1642, 12, 46, 'اى يوسف! اى مرد راستين! در باره‌ى اين خواب كه هفت گاو فربه را هفت گاو لاغر مى‌خورند و هفت خوشه سبز و هفت خوشه خشكيده‌ى ديگر، براى ما تعبير كن تا نزد مردم برگردم شايد آنها [از تعبير اين خواب‌] آگاه شوند'),
(1643, 12, 47, 'گفت: هفت سال با جدّيت و به طور متوالى مى‌كاريد، پس هر چه درو كرديد آن را در خوشه‌اش نگاه داريد، جز اندكى را كه مى‌خوريد'),
(1644, 12, 48, 'آن‌گاه پس از آن، هفت سال قحطى مى‌آيد كه آنچه از پيش براى آن سال‌ها كنار گذاشته‌ايد مى‌خورند جز اندكى از آنچه ذخيره مى‌كنيد'),
(1645, 12, 49, 'سر انجام پس از آن، سالى فرا مى‌رسد كه باران فراوان نصيب مردم مى‌شود و در آن سال [بر اثر فراوانى، از ميوه‌ها] عصاره مى‌گيرند'),
(1646, 12, 50, 'و پادشاه گفت: او را نزد من آوريد. پس هنگامى كه آن فرستاده نزد وى آمد، [يوسف‌] گفت: نزد آقاى خويش برگرد و از او بپرس ماجراى آن زنانى كه دست‌هاى خود را بريدند چه بود؟ همانا پروردگار من به مكرشان آگاه است'),
(1647, 12, 51, 'پادشاه [به زنان‌] گفت: قصه شما آن دم كه از يوسف كام خواستيد چه بود؟ گفتند: منزه است خدا! ما از او هيچ بدى سراغ نداريم. زن عزيز گفت: اكنون حقيقت آشكار شد. من بودم كه از او كام خواستم و بى‌شك او از راستگويان است'),
(1648, 12, 52, 'آن [به خاطر اين است‌] تا او بداند كه من در غياب، به او خيانت نكردم و خدا كيد خائنان را به جايى نمى‌رساند'),
(1649, 12, 53, 'و من خود را تبرئه نمى‌كنم، چرا كه مسلما نفس [آدمى‌] پيوسته به بدى‌ها امر مى‌كند، مگر آن كه پروردگارم رحم كند. همانا پروردگار من آمرزنده‌ى مهربان است'),
(1650, 12, 54, 'و شاه گفت: او را نزد من آوريد تا وى را [نديم‌] ويژه‌ى خود سازم. پس چون با وى به سخن پرداخت گفت: تو امروز نزد ما صاحب مقام و مورد اعتمادى'),
(1651, 12, 55, '[يوسف‌] گفت: سرپرستى خزانه‌هاى [اين‌] سرزمين را به من بسپار كه من نگهبانى كاردانم'),
(1652, 12, 56, 'و بدين گونه يوسف را در [آن‌] سرزمين قدرت داديم كه در هر جا از آن مى‌خواست منزل مى‌كرد. [و همه جا نفوذ داشت‌] ما رحمت خود را به هر كس بخواهيم ارزانى مى‌داريم و اجر نيكوكاران را ضايع نمى‌سازيم'),
(1653, 12, 57, 'و البته اجر آخرت براى كسانى كه ايمان آوردند و پرهيزكارى مى‌كردند [از اين هم‌] بهتر است'),
(1654, 12, 58, 'و برادران يوسف [به مصر] آمدند و بر او وارد شدند او آنان را شناخت ولى آنها وى را نشناختند'),
(1655, 12, 59, 'و چون توشه آنها را آماده كرد، گفت: [نوبت آينده‌] آن برادرى را كه از پدر داريد نزد من آوريد. مگر نمى‌بينيد كه من پيمانه را تمام مى‌دهم و من بهترين ميزبانانم'),
(1656, 12, 60, 'پس اگر او را نزد من نياوريد پيش من پيمانه‌اى نخواهيد داشت و به من نزديك نشويد'),
(1657, 12, 61, 'گفتند: او را [با هر ترفندى‌] از پدرش خواهيم خواست و حتما اين كار را مى‌كنيم'),
(1658, 12, 62, 'يوسف به غلامان خود گفت: [پرداختى و] سرمايه آنها را در بارهايشان بگذاريد تا پس از بازگشت به خانواده‌ى خويش آن را بشناسند، شايد كه [دوباره‌] برگردند'),
(1659, 12, 63, 'و چون نزد پدرشان بازگشتند، گفتند: اى پدر! پيمانه [و سهم غلّه‌] از ما منع شد. برادرمان را با ما بفرست تا سهم خويش بگيريم، و ما حتما نگهبان او خواهيم بود'),
(1660, 12, 64, '[يعقوب‌] گفت: آيا در باره‌ى او از شما خاطر جمع باشم همان طور كه پيش‌تر در باره‌ى برادرش به شما اطمينان كردم؟ [ولى‌] خداوند بهترين حافظ است و او مهربان‌ترين مهربانان است'),
(1661, 12, 65, 'و هنگامى كه بارهاى خود را باز كردند، ديدند كه سرمايه‌شان پس داده شده است. گفتند: اى پدر! ديگر چه مى‌خواهيم؟ اين سرمايه ماست كه به ما پس داده‌اند. [بگذار برويم‌] براى كسان خود آذوقه مى‌آوريم و برادرمان را [نيز] حفاظت مى‌كنيم و [با بردن او] يك بار شتر'),
(1662, 12, 66, 'گفت: هرگز او را با شما نخواهم فرستاد تا به من پيمان اكيد از خدا بدهيد كه او را حتما نزد من خواهيد آورد، مگر آن كه گرفتار حادثه‌اى شويد. پس چون پيمان خود را بدو دادند، گفت: خدا بر آنچه مى‌گوييم و كيل [و شاهد] است'),
(1663, 12, 67, 'و گفت: اى پسران من! از يك دروازه داخل نشويد و از دروازه‌هاى مختلف وارد شويد، و من شما را از [تقدير] خدا حتى اندكى بى‌نياز نمى‌كنم. حكم، حكم خداست و من بر او توكل كرده‌ام، و اهل توكل بايد تنها بر او توكل كنند'),
(1664, 12, 68, 'و چون از آن جا كه پدرشان دستور داده بود وارد شدند، [اين ترفند] هيچ حادثه الهى را نمى‌توانست از آنها دور سازد، جز اين كه نيازى در روح يعقوب را برآورده كرد [و خشنودش نمود]، و او با تعاليم ما بسيار دانا بود، ولى اكثر مردم نمى‌دانند'),
(1665, 12, 69, 'و هنگامى كه بر يوسف وارد شدند، برادرش [بنيامين‌] را به نزد خود جاى داد و گفت: همانا من برادر توام، پس از كارهايى كه [برادرانت‌] مى‌كردند غمگين مباش'),
(1666, 12, 70, 'پس چون بارهايشان را آماده كرد، جام را در باردان برادرش نهاد. آن‌گاه منادى بانگ در داد: اى كاروانيان! بى‌گمان شما دزد هستيد'),
(1667, 12, 71, 'آنان رو به سويشان كردند و گفتند: چه گم كرده‌ايد'),
(1668, 12, 72, 'گفتند: پيمانه شاه را گم كرده‌ايم، و براى هر كس كه آن را بياورد يك بار شتر خواهد بود، و من [منادى‌] ضامن آن هستم'),
(1669, 12, 73, 'گفتند: به خدا سوگند، شما خوب مى‌دانيد كه ما نيامده‌ايم در اين سرزمين فساد كنيم، و ما دزد نبوده‌ايم'),
(1670, 12, 74, 'گفتند: پس اگر دروغ گفته باشيد كيفر دزد چيست'),
(1671, 12, 75, 'گفتند: سزايش آن است كه هر كه [جام‌] در بار وى يافت شد او خودش سزاى آن است. ما ستمكاران را اين گونه كيفر مى‌دهيم'),
(1672, 12, 76, 'آن‌گاه [يوسف‌] پيش از بار برادرش، به جست و جوى بار آنان پرداخت سپس آن [جام‌] را از بار برادرش [بنيامين‌] بيرون آورد. [آرى‌] اين گونه يوسف را تدبير آموختيم، زيرا در آيين شاه نمى‌توانست برادرش را بازداشت كند، مگر اين كه خدا بخواهد [و چنين راهى به او نش'),
(1673, 12, 77, 'گفتند: اگر او دزدى كرده [تعجب ندارد]، پيش از اين برادرش نيز دزدى كرده بود. يوسف اين سخن [تلخ‌] را در دل نگاه داشت و به رويشان نياورد. گفت: وضع شما بدتر [از اين‌] است، و خدا به آنچه توصيف مى‌كنيد آگاه‌تر است'),
(1674, 12, 78, 'گفتند: اى عزيز! او پدرى سالخورده دارد، پس يكى از ما را به جاى او بگير، كه ما تو را از نيكوكاران مى‌بينيم'),
(1675, 12, 79, 'گفت: پناه بر خدا كه جز آن كس را كه كالاى خود را نزد وى يافته‌ايم بازداشت كنيم، زيرا در اين صورت حتما ستمكار خواهيم بود'),
(1676, 12, 80, 'پس چون از او نوميد شدند، نجواكنان كنار كشيدند. بزرگشان گفت: مگر نمى‌دانيد كه پدرتان از شما به نام خدا پيمان اكيد گرفته است و قبلا هم در حق يوسف چه تقصيرى كرده‌ايد؟ پس من هرگز از اين جا خارج نمى‌شوم تا پدرم به من رخصت دهد يا اين كه خداوند در حق من حك'),
(1677, 12, 81, 'پيش پدرتان بازگرديد و بگوييد: اى پدر! پسرت دزدى كرده است، و ما جز به آنچه مى‌دانستيم گواهى نداديم، و نگاهبان غيب هم نبوديم'),
(1678, 12, 82, 'و از مردم شهرى كه در آن بوديم و كاروانى كه همراهش آمديم جويا شو، و ما قطعا راستگو هستيم'),
(1679, 12, 83, 'گفت: [چنين نيست‌] بلكه نفس شما كارى را براى شما آراسته است. صبرى جميل [لازم است‌] اميد كه خداوند همه آنها را سوى من باز آورد، كه او داناى حكيم است'),
(1680, 12, 84, 'و از آنها روى گرداند و گفت: اى افسوس بر يوسف! و همچنان كه اندوه خود را فرو مى‌خورد، چشمانش از غصه سپيد شد'),
(1681, 12, 85, 'گفتند: به خدا سوگند، تو پيوسته يوسف را ياد مى‌كنى تا سر انجام يا بيمارى شوى يا خود را هلاك كنى'),
(1682, 12, 86, 'گفت: من شرح درد و اندوه خويش را تنها با خدا مى‌گويم، و از خداوند چيزى مى‌دانم كه شما نمى‌دانيد'),
(1683, 12, 87, 'اى پسران من! برويد و از يوسف و برادرش جست و جو كنيد و از رحمت خدا نوميد نشويد، كه از رحمت خداوند جز كافران نوميد نمى‌شوند'),
(1684, 12, 88, 'پس چون برادران بر او وارد شدند، گفتند: هان اى عزيز! ما و كسانمان را فقر و قحطى گرفته و سرمايه‌اى ناچيز آورده‌ايم، پس پيمانه ما را كامل كن و به ما صدقه بده كه خدا صدقه دهندگان را پاداش مى‌دهد'),
(1685, 12, 89, 'گفت: آيا دانستيد با يوسف و برادرش چه كرديد آن‌گاه كه جاهل بوديد'),
(1686, 12, 90, 'گفتند: آيا واقعا تو همان يوسفى؟ گفت: آرى، من يوسفم و اين برادر من است. به راستى خدا بر ما منّت نهاده است. بى‌گمان هر كه تقوا و صبر پيشه كند، خدا پاداش نيكوكاران را تباه نمى‌كند'),
(1687, 12, 91, 'گفتند: به خدا سوگند كه واقعا خدا تو را بر ما برترى داده و ما جدّا خطاكار بوده‌ايم'),
(1688, 12, 92, 'گفت: امروز ملامتى بر شما نيست. خدا شما را مى‌آمرزد و او مهربان‌ترين مهربانان است'),
(1689, 12, 93, 'اين پيراهن مرا ببريد و بر صورت پدرم بيفكنيد تا بينا شود، و كسان خود را همگى نزد من آوريد'),
(1690, 12, 94, 'و چون كاروان [از مصر] به راه افتاد، پدرشان گفت: اگر مرا به كم خردى متهم نكنيد، من بوى يوسف را مى‌شنوم'),
(1691, 12, 95, 'گفتند: به خدا سوگند كه تو هنوز در خطاى سابق خود هستى'),
(1692, 12, 96, 'پس چون مژده‌رسان آمد، آن پيراهن را بر صورت او انداخت و او بينا شد. گفت: آيا به شما نگفتم كه من از [الطاف‌] خدا آن مى‌دانم كه شما نمى‌دانيد'),
(1693, 12, 97, 'گفتند: اى پدر! براى گناهان ما آمرزش بخواه كه ما خطاكار بوده‌ايم'),
(1694, 12, 98, 'گفت: به زودى از پروردگارم براى شما آمرزش خواهم خواست كه بى‌ترديد او آمرزنده‌ى مهربان است'),
(1695, 12, 99, 'پس چون بر يوسف وارد شدند، پدر و مادر خود را به آغوش كشيد و گفت: وارد مصر شويد كه به خواست خدا در امان خواهيد بود'),
(1696, 12, 100, 'و پدر و مادر خود را بر تخت نشاند و همه آنها در برابر او به سجده افتادند و يوسف گفت: اى پدر! اين تعبير خوابى است كه قبلا ديده بودم و اينك پروردگارم آن را راست گردانيد، و به من احسان نمود كه مرا از زندان بيرون آورد و شما را از باديه [كنعان به اين جا]'),
(1697, 12, 101, 'پروردگارا! تو به من بهره‌اى از فرمانروايى دادى و راز تعبير خواب‌ها به من آموختى. اى پديد آورنده‌ى آسمان‌ها و زمين! تو در دنيا و آخرت مولاى منى، مرا مسلمان بميران و قرين شايستگان ساز'),
(1698, 12, 102, 'اين از خبرهاى مهم غيب است كه به تو وحى مى‌كنيم، و تو هنگامى كه آنان در كار خويش همداستان شدند و حيله مى‌كردند نزدشان نبودى'),
(1699, 12, 103, 'و بيشتر مردم، هر چند كه تو اصرار داشته باشى، مؤمن نخواهند شد'),
(1700, 12, 104, 'و تو بر اين كار پاداشى از آنها نمى‌خواهى. اين [قرآن‌] جز پندى براى جهانيان نيست'),
(1701, 12, 105, 'و چه بسيار نشانه‌ها در آسمان‌ها و زمين است كه بر آنها مى‌گذرند در حالى كه از آنها رويگردانند [و توجه نمى‌كنند]'),
(1702, 12, 106, 'و بيشترشان به خدا ايمان نمى‌آورند جز اين كه همچنان مشركند'),
(1703, 12, 107, 'آيا ايمن از آنند كه عذاب فراگير خدا به ايشان رسد، يا قيامت ناگهان آنها را در حال بى‌خبرى دريابد'),
(1704, 12, 108, 'بگو: اين راه من است. من و پيروانم از روى بصيرت [مردم را] به سوى خدا دعوت مى‌كنيم، و خدا پاك و منزه است و من از مشركان نيستم'),
(1705, 12, 109, 'و پيش از تو [نيز] نفرستاديم جز مردانى از اهل شهرها را كه به آنان وحى مى‌كرديم. آيا در زمين گردش نكردند تا عاقبت كار كسانى را كه پيش‌تر بوده‌اند بنگرند؟ و قطعا سراى آخرت براى پارسايان بهتر است. آيا نمى‌انديشيد'),
(1706, 12, 110, 'تا آن جا كه فرستادگان [از ايمان كافران‌] نوميد شدند و [مردم‌] پنداشتند كه به آنها دروغ گفته شده، در اين هنگام يارى ما به آنها رسيد. پس هر كه را خواسته بوديم نجات يافت، و عذاب ما از قوم گنهكار برگشت ندارد'),
(1707, 12, 111, 'البته در سرگذشت آنها براى خردمندان درس عبرتى است. اين [قرآن‌] سخنى نيست كه ساخته شده باشد، بلكه تصديق كننده‌ى آن [كتابى‌] است كه پيش روى آن است، و شرح و بيان هر چيز است، و هدايت و رحمتى است براى مردمى كه ايمان مى‌آورند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، ميم، را. اين آيات كتاب [خدا] است، و آنچه از پروردگارت به سوى تو نازل شده حق است، ولى بيشتر مردم ايمان نمى‌آورند'),
(1709, 13, 2, 'خداوند كسى است كه آسمان‌ها را بدون ستون‌هايى كه ببينيد برافراشت. آن‌گاه به عرش پرداخت و خورشيد و ماه را مسخر كرد. هر يك براى مدتى معين روانند. [خداوند] كارها را تدبير مى‌كند و آيات خود را شرح مى‌دهد، اميد كه به لقاى پروردگارتان يقين كنيد'),
(1710, 13, 3, 'و اوست كه زمين را بگسترد و در آن كوه‌هاى استوار و رودها پديد آورد، و در آن از هر گونه ميوه‌اى جفت جفت آفريد. شب را بر روز مى‌پوشاند. قطعا در اينها آياتى است براى مردمى كه تفكر كنند'),
(1711, 13, 4, 'و در زمين قطعه‌هايى در كنار هم قرار دارد [كه با هم متفاوتند] و باغ‌هايى از انگور و زراعت و نخل‌هايى از يك ريشه و غير يك ريشه، كه همه آنها با يك آب سيراب مى‌شوند، و بعضى را بر بعضى ديگر در محصول برترى مى‌دهيم. بى‌گمان در اينها براى مردمى كه تعقل مى‌كنن'),
(1712, 13, 5, 'و اگر تعجب كنى، سخن آنها عجب است كه: آيا وقتى خاك شديم، واقعا به آفرينش تازه‌اى وارد مى‌شويم؟ اينان كسانى هستند كه به پروردگارشان كافر شدند و زنجيرها در گردنشان است، و آنها اهل آتشند و در آن ماندگار خواهند بود'),
(1713, 13, 6, 'و از تو پيش از نيكى بدى را به شتاب مى‌خواهند، با اين كه پيش از آنها عقوبت‌ها [ى عبرت‌آموز] نازل شده است. همانا پروردگار تو نسبت به مردم با وجود ظلمشان صاحب آمرزش است، و [در عين حال‌] پروردگار تو سخت كيفر است'),
(1714, 13, 7, 'و آنان كه كافر شدند مى‌گويند: چرا معجزه‌اى از جانب پروردگارش بر او نازل نمى‌شود؟ [اى پيامبر!] تو فقط هشدار دهنده‌اى و هر قومى را راهنمايى است'),
(1715, 13, 8, 'خدا مى‌داند هر ماده‌اى چه بارى [در رحم‌] دارد، و آنچه را كه رحم‌ها مى‌كاهند يا مى‌افزايند نيز مى‌داند، و هر چيزى نزد او به اندازه است'),
(1716, 13, 9, 'داناى نهان و آشكار، و بزرگ و بلند مرتبه است'),
(1717, 13, 10, '[براى او] يكسان است، از شما هر كه سخن نهان دارد و هر كه آن را آشكار كند، و آن كه به شب پنهان باشد و در روز راه رود'),
(1718, 13, 11, 'و براى انسان تعقيب كنندگانى [از فرشتگان‌] است كه او را از پيش رو و پشت سر به امر خدا حفاظت مى‌كنند. بى‌ترديد خداوند سرنوشت قومى را تغيير نمى‌دهد تا آنها وضع خود را تغيير دهند. و چون خدا براى قومى بدى خواهد، آن را بازگشتى نيست، و جز خدا كارسازى براى آ'),
(1719, 13, 12, 'او كسى است كه برق را براى بيم و اميد به شما نشان مى‌دهد و ابرهاى سنگين را پديد مى‌آورد'),
(1720, 13, 13, 'و رعد به حمد او و فرشتگان [جملگى‌] از بيم او تسبيح مى‌گويند، و صاعقه‌ها را مى‌فرستد و هر كه را بخواهد بدان آسيب مى‌رساند، ولى آنها [با مشاهده‌ى اين آيات‌] باز هم در باره‌ى خدا مجادله مى‌كنند در حالى كه او سخت كيفر است'),
(1721, 13, 14, 'دعوت حق [و راستين‌] از آن اوست، و معبودهايى را كه به جاى خدا مى‌خوانند هيچ جوابى به آنها نمى‌دهند، مگر مانند كسى كه دو دستش را به سوى آب باز كرده كه آب به دهانش رسد، اما نمى‌رسد، و دعوت كافران جز در گمراهى نيست'),
(1722, 13, 15, 'و هر كه در آسمان‌ها و زمين است، خواه و ناخواه، و [همچنين‌] سايه‌هايشان، بامدادان و شامگاهان براى خدا سجده مى‌كنند'),
(1723, 13, 16, 'بگو: پروردگار آسمان‌ها و زمين كيست؟ بگو: خدا. بگو: پس چرا غير او سرپرستانى گرفته‌ايد كه اختيار سود و زيان خود را ندارند؟ بگو: آيا كور و بينا يكسانند؟ يا تاريكى‌ها و نور برابرند؟ آيا براى خدا همتايانى قرار داده‌اند كه مانند آفرينش او آفريده‌اند و اين'),
(1724, 13, 17, 'از آسمان آبى فرو فرستاد، پس دره‌ها و مسيل‌ها به وسعت خود جارى شدند، و سيلاب، كف بالا آمده‌اى برداشت، و از آنچه [از فلزات‌] بر آتش ذوب مى‌كنند تا زيور و كالايى سازند نيز مانند آن كفى است. خداوند حق و باطل را چنين مثل مى‌زند. اما كف به كنار مى‌رود، ول'),
(1725, 13, 18, 'كسانى كه پروردگارشان را اجابت كردند براى آنها بهترين [پاداش‌] است، و كسانى كه او را اجابت نكردند، اگر همه آنچه در زمين است و همانندش از آن آنها باشد، حتما آن را براى بازخريد خويش خواهند داد. آنها را حساب بدى است و جايشان دوزخ است و بد آرامگاهى است'),
(1726, 13, 19, 'پس آيا كسى كه مى‌داند آنچه از جانب پروردگارت به سوى تو نازل شده حق است، مانند آن كسى است كه كور [دل‌] است؟ فقط خردمندان متذكر مى‌شوند'),
(1727, 13, 20, 'همانان كه به عهد خدا وفا مى‌كنند و پيمان [خدا] را نمى‌شكنند'),
(1728, 13, 21, 'و كسانى كه هر آنچه را خداوند به پيوستن آن فرمان داده مى‌پيوندند و از پروردگارشان مى‌ترسند و از بدى حساب بيم دارند'),
(1729, 13, 22, 'و كسانى كه براى طلب رضاى پروردگارشان صبورى كردند و نماز برپا داشتند و از آنچه روزيشان داديم در نهان و عيان انفاق كردند، و بدى را به نيكى برطرف ساختند، نيك فرجامى سراى آخرت براى آنهاست'),
(1730, 13, 23, 'بهشت‌هاى جاودانى كه آنها با پدران و همسران و فرزندانشان كه درستكارند در آن داخل مى‌شوند، و فرشتگان از هر درى بر آنها وارد مى‌شوند'),
(1731, 13, 24, '[و مى‌گويند:] سلام بر شما به خاطر صبرتان. پس چه نيك است فرجام آن سراى'),
(1732, 13, 25, 'و كسانى كه پيمان خدا را پس از تحكيم آن مى‌شكنند، و آنچه را خدا به پيوستن آن فرمان داده مى‌گسلند و در زمين فساد مى‌كنند، آنها برايشان لعنت است و آنان راست بد فرجامى سراى آخرت'),
(1733, 13, 26, 'خداوند روزى را براى هر كه بخواهد وسعت مى‌دهد يا تنگ مى‌كند، و آنها به زندگى دنيا سرمست شدند، در حالى كه زندگى دنيا در برابر آخرت جز متاعى اندك نيست'),
(1734, 13, 27, 'و كسانى كه كافر شدند مى‌گويند: چرا از جانب پروردگارش معجزه‌اى بر او نازل نشد؟ بگو: خداست كه هر كه را بخواهد بى‌راه مى‌گذارد و هر كه را توبه كند و باز گردد، به سوى خويش هدايت مى‌كند'),
(1735, 13, 28, 'همانان كه ايمان آوردند و دل‌هايشان به ياد خدا آرام مى‌گيرد. آگاه باش كه با ياد خدا دل‌ها آرام گيرد'),
(1736, 13, 29, 'كسانى كه ايمان آوردند و كارهاى شايسته كردند، پاكيزه‌ترين [زندگى‌] و سر انجام نيكو براى آنهاست'),
(1737, 13, 30, 'بدين گونه تو را در ميان امتى فرستاديم كه پيش از آن امت‌هايى گذشته‌اند، تا آنچه را به تو وحى كرده‌ايم بر آنها بخوانى، در حالى كه آنها به خداى رحمان كافر مى‌شوند. بگو: او پروردگار من است. معبودى جز او نيست. بر او توكل كرده‌ام و رويكرد من به سوى اوست'),
(1738, 13, 31, 'و اگر قرآنى بود كه كوه‌ها بدان به راه مى‌افتادند يا زمين بدان تكه تكه مى‌گرديد يا مردگان با آن به سخن مى‌آمدند [باز ايمان نمى‌آوردند]، بلكه رشته همه كارها به دست خداست. آيا كسانى كه ايمان آورده‌اند [از ايمان اينان‌] مأيوس نشده [و ندانسته‌] اند كه اگ'),
(1739, 13, 32, 'و بى‌گمان فرستادگان پيش از تو نيز مسخره شدند، پس به كسانى كه كافر شدند مهلت دادم، آن‌گاه آنها را عقوبت كردم پس [بنگر كه‌] عقوبت من چگونه بود'),
(1740, 13, 33, 'آيا كسى كه مراقب عملكرد هر فردى است [شريك دارد]؟ و حال آن كه براى خدا شريكانى قرار دادند. بگو: آنها را [به اوصافشان‌] نام ببريد آيا چيزى را به او خبر مى‌دهيد كه از وجود آن در زمين بى‌خبر است يا سخنى سطحى و بيهوده مى‌گوييد؟ آرى، در نظر كافران مكرشان ز'),
(1741, 13, 34, 'براى آنها در زندگى دنيا عذابى است، و عذاب آخرت قطعا دشوارتر است، و ايشان را در برابر خدا هيچ حافظى نيست'),
(1742, 13, 35, 'وصف بهشتى كه به پرهيزكاران وعده داده شده اين است كه از زير درختانش نهرها جارى است [و] ميوه‌ها و سايه‌اش دائمى است. اين سر انجام تقواپيشگان است، و عاقبت كافران دوزخ است'),
(1743, 13, 36, 'و كسانى كه به آنان كتاب آسمانى داده‌ايم، از آنچه به تو نازل شده شاد مى‌شوند، و برخى از گروه‌ها بخشى از آن را انكار مى‌كنند. بگو: جز اين نيست كه من دستور يافته‌ام خدا را بپرستم و به او شرك نورزم. به سوى او دعوت مى‌كنم و بازگشت من به سوى اوست'),
(1744, 13, 37, 'و بدين‌سان اين [قرآن‌] را فرمانى به زبان عربى نازل كرديم، و اگر پس از دانشى كه براى تو آمده، از هوس‌هاى آنها پيروى كنى، در برابر خدا هيچ دوست و مدافعى نخواهى داشت'),
(1745, 13, 38, 'و همانا پيش از تو نيز رسولانى فرستاديم و براى آنها همسران و فرزندانى قرار داديم، و هيچ پيامبرى را نرسد كه معجزه‌اى بياورد جز به اذن خدا. و براى هر زمانى نوشته‌اى [و براى هر امرى موعدى‌] است'),
(1746, 13, 39, 'خدا آنچه را خواهد محو مى‌كند و [آنچه را خواهد] تثبيت مى‌كند، و اصل كتاب نزد اوست'),
(1747, 13, 40, 'و اگر پاره‌اى از آنچه را كه به آنها وعده مى‌دهيم به تو نشان دهيم يا تو را [پيش از كيفرشان‌] بميرانيم، جز اين نيست كه بر تو ابلاغ است و بر ما حساب'),
(1748, 13, 41, 'آيا نديدند كه ما پيوسته به سراغ زمين مى‌آييم و از اطراف آن مى‌كاهيم [و تمدن‌ها و شخصيت‌هاى آنها را از بين مى‌بريم‌]؟ و خداست كه حكم مى‌كند و حكم او را هيچ تأخير اندازه‌اى نيست، و او سريع الحساب است'),
(1749, 13, 42, 'و پيش از آنها نيز كسانى طرح‌ها و نقشه‌ها كشيدند، ولى تمام نقشه‌ها و تدبيرها از آن خداست كه مى‌داند هر كسى چه مى‌كند. و كافران به زودى خواهند دانست عاقبت سراى آخرت از آن كيست'),
(1750, 13, 43, 'و كسانى كه كافر شدند مى‌گويند: تو پيامبر نيستى. بگو: شهادت خدا و آن كسى كه علم كتاب نزد اوست ميان من و شما كافى است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف، لام، را. [اين‌] كتابى است كه آن را به سوى تو نازل كرديم تا مردم را به اذن پروردگارشان از تاريكى‌ها به سوى روشنايى در آورى، به راه آن شكست‌ناپذير ستوده'),
(1752, 14, 2, 'خدايى كه هر چه در آسمان‌ها و هر چه در زمين است از آن اوست، و واى بر كافران از عذابى سخت'),
(1753, 14, 3, 'همانان كه زندگى دنيا را بر آخرت ترجيح مى‌دهند و از راه خدا باز مى‌دارند و آن را منحرف مى‌خواهند. آنها سخت در گمراهى هستند'),
(1754, 14, 4, 'و ما هيچ پيامبرى را جز به زبان قومش نفرستاديم، تا [حقايق را] براى آنها بيان كند. پس خدا هر كه را بخواهد گمراه و هر كه را بخواهد هدايت مى‌كند، و او شكست‌ناپذير حكيم است'),
(1755, 14, 5, 'و همانا موسى را با آيات خود فرستاديم كه: قوم خود را از تاريكى‌ها به سوى روشنايى بيرون آور، و روزهاى [عبرت انگيز] الهى را به آنان يادآورى كن كه قطعا در اين [يادآورى‌] براى هر صبر پيشه شكرگزارى عبرت‌هاست'),
(1756, 14, 6, 'و آن‌گاه كه موسى به قوم خود گفت: نعمت خدا را كه بر شما ارزانى داشت به ياد آوريد، آن‌گاه كه شما را از فرعونيان نجات داد كه بر شما عذاب سخت روا مى‌داشتند و پسرانتان را سر مى‌بريدند و زنانتان را زنده مى‌گذاشتند، و در اين [براى شما] از جانب پروردگارتان آز'),
(1757, 14, 7, 'و آن‌گاه كه پروردگارتان اعلام كرد كه اگر واقعا سپاسگزارى كنيد، البته نعمت شما را افزون خواهم كرد، و اگر ناسپاسى كنيد عذاب من بسيار سخت است'),
(1758, 14, 8, 'و موسى گفت: اگر شما و هر كه در زمين است همگى كافر شويد، به يقين خدا بى‌نياز و شايسته ستايش است'),
(1759, 14, 9, 'آيا خبر كسانى كه پيش از شما بودند: قوم نوح و عاد و ثمود و كسانى كه پس از آنان بودند و تنها خدا آنها را مى‌شناسد، به شما نرسيده است؟ پيامبرانشان دلايل روشن برايشان آوردند، ولى آنها دست بر دهان گرفتند و گفتند: ما به آنچه شما مأموريت داريد كافريم و از آن'),
(1760, 14, 10, 'پيامبرانشان گفتند: مگر در باره‌ى خدا- پديد آورنده‌ى آسمان‌ها و زمين- شكى هست؟! شما را دعوت مى‌كند تا بخشى از گناهانتان را ببخشايد و اجل شما را تا مدتى معين به تأخير اندازد. گفتند: شما جز بشرى مانند ما نيستيد، مى‌خواهيد ما را از آنچه پدرانمان مى‌پرستي'),
(1761, 14, 11, 'پيامبرانشان به آنها گفتند: ما جز بشرى مثل شما نيستيم، ولى خدا بر هر كه از بندگانش بخواهد منّت مى‌نهد [و مقام رسالت مى‌دهد]، و ما را نرسد كه جز به اذن خدا براى شما معجزه‌اى بياوريم، و مؤمنان بايد بر خدا توكل كنند و بس'),
(1762, 14, 12, 'و چرا بر خدا توكل نكنيم و حال آن كه ما را به راه‌هايمان رهبرى كرده است؟ و ما بر آزارى كه به ما رسانديد البته صبر خواهيم كرد، و اهل توكل بايد تنها بر خدا توكل كنند'),
(1763, 14, 13, 'و كسانى كه كافر شدند، به پيامبرانشان گفتند: [يا] شما را از سرزمين خود بيرون مى‌كنيم، يا به كيش ما باز مى‌گرديد. پس پروردگارشان به آنها وحى كرد كه حتما ستمگران را هلاك خواهيم كرد'),
(1764, 14, 14, 'و قطعا شما را پس از ايشان در [آن‌] سرزمين اسكان خواهيم داد. اين براى كسى است كه از مقام من بترسد و از تهديدم بيمناك باشد'),
(1765, 14, 15, 'و آنها پيروزى خواستند و [سر انجام‌] هر زورگوى لجوجى ناكام شد'),
(1766, 14, 16, 'از پشت سرش جهنم است، و از آب چرك و خونش مى‌نوشانند'),
(1767, 14, 17, 'آن را جرعه جرعه مى‌نوشد و نمى‌تواند آن را فرو برد، و مرگ از هر طرف به سويش مى‌آيد اما مردنى نيست، و عذابى سنگين به دنبال دارد'),
(1768, 14, 18, 'مثل كسانى كه به پروردگار خود كافر شدند، اعمالشان همچون خاكسترى است كه در روز طوفانى باد شديدى بر آن بوزد، كه به چيزى از آنچه كسب كرده‌اند دست نيابند. اين همان گمراهى دور است'),
(1769, 14, 19, 'آيا نديدى كه خدا آسمان‌ها و زمين را به حق آفريده است؟ اگر بخواهد شما را مى‌برد و خلق تازه‌اى مى‌آورد'),
(1770, 14, 20, 'و اين بر خدا دشوار نيست'),
(1771, 14, 21, 'و [در قيامت‌] همگى در برابر خدا حاضر شوند. پس ضعيفان به گردنكشان گويند: ما پيروان شما بوديم، آيا شما [مى‌توانيد] چيزى از عذاب خدا را از ما دفع كنيد؟ مى‌گويند: اگر خدا ما را هدايت كرده بود، قطعا شما را هدايت مى‌كرديم. حال چه بى‌تابى كنيم يا صبورى كني'),
(1772, 14, 22, 'و شيطان هنگامى كه كار از كار گذشت، گويد: در حقيقت خدا به شما وعده‌ى راست داد، و من هم به شما وعده دادم و با شما خلاف كردم، و مرا بر شما هيچ تسلطى نبود جز اين كه شما را دعوت كردم و اجابتم نموديد. پس مرا ملامت نكنيد و خود را ملامت كنيد. من فريادرس شما'),
(1773, 14, 23, 'و كسانى كه ايمان آوردند و كارهاى شايسته كردند، به بهشت‌هايى در آورده مى‌شوند كه از پاى درختانش نهرها جارى است كه به خواست پروردگارشان در آن جا جاودانه به سر مى‌برند، و درودشان در آن جا سلام است'),
(1774, 14, 24, 'آيا نديدى خدا چگونه مثل زده است؟ گفتار و اعتقاد پاكيزه مانند درختى پاك است كه ريشه‌اش استوار و شاخه‌اش در آسمان است'),
(1775, 14, 25, '[و] ميوه‌اش را هر دم به اذن پروردگارش مى‌دهد، و خدا براى مردم مثل‌ها مى‌زند، شايد متوجه شوند'),
(1776, 14, 26, 'و مثل گفتار ناپاك چون درختى ناپاك است كه از روى زمين كنده شده و قرارى ندارد'),
(1777, 14, 27, 'خداوند كسانى را كه ايمان آورده‌اند، در زندگى دنيا و در آخرت با سخن و اعتقاد استوار ثابت مى‌گرداند، و ستمگران را گمراه مى‌كند و خدا هر چه بخواهد انجام مى‌دهد'),
(1778, 14, 28, 'آيا نديدى كسانى را كه نعمت خدا را به كفر تبديل كردند و قوم خود را به سراى هلاكت و نابودى در آوردند'),
(1779, 14, 29, '[همان‌] جهنمى كه وارد آن مى‌شوند و بد قرارگاهى است'),
(1780, 14, 30, 'و براى خدا مانندهايى قرار دادند تا [مردم را] از راه او گمراه سازند. بگو: [اكنون‌] برخوردار شويد كه قطعا بازگشت شما به سوى آتش است'),
(1781, 14, 31, 'به بندگان من كه ايمان آورده‌اند بگو نماز را بر پا دارند و از آنچه روزيشان كرده‌ايم در نهان و آشكار انفاق كنند پيش از آن كه روزى فرا رسد كه در آن نه داد و ستدى باشد و نه رفاقتى'),
(1782, 14, 32, 'خداست كه آسمان‌ها و زمين را آفريد و از آسمان آبى فرستاد و به وسيله آن از ميوه‌ها براى شما روزى بيرون آورد، و كشتى را مسخّر شما كرد تا به فرمان او در دريا حركت كند، و نهرها را مسخر شما نمود'),
(1783, 14, 33, 'و خورشيد و ماه را كه پيوسته در حركتند به تسخير شما در آورد، و شب و روز را [نيز] مسخّر شما ساخت'),
(1784, 14, 34, 'و از هر آنچه از او خواسته‌ايد به شما داده است. و اگر نعمت‌هاى خدا را بشماريد نمى‌توانيد [همه‌] آن را به شمار آوريد. حقا كه انسان ستم پيشه ناسپاس است'),
(1785, 14, 35, 'و آن‌گاه كه ابراهيم گفت: پروردگارا! اين شهر را ايمن گردان و من و فرزندانم را از پرستش بتان دور بدار'),
(1786, 14, 36, 'پروردگارا! آنها بسيارى از مردم را گمراه كرده‌اند پس هر كه از من پيروى كرد بى‌گمان او از من است، و هر كه نافرمانى كرد همانا تو آمرزنده و مهربانى'),
(1787, 14, 37, 'پروردگارا! من برخى فرزندانم را در درّه‌اى بى‌آب و علف در جوار خانه مكرّم تو اسكان دادم پروردگارا! تا نماز را به پا دارند. پس دل‌هايى از مردم را به سوى آنها مايل گردان و از ميوه‌ها روزيشان ده تا شكر گزارند'),
(1788, 14, 38, 'پروردگارا! بى‌گمان، هر چه را نهان كنيم يا عيان كنيم تو مى‌دانى و هيچ چيز در زمين و آسمان بر خدا پنهان نمى‌ماند'),
(1789, 14, 39, 'ستايش خداى را كه در وقت پيرى، اسماعيل و اسحاق را به من عطا كرد. به راستى پروردگار من شنونده‌ى دعاست'),
(1790, 14, 40, 'پروردگارا! مرا به پا دارنده‌ى نماز كن و فرزندان مرا نيز. پروردگارا! و دعاى مرا بپذير'),
(1791, 14, 41, 'پروردگارا! من و پدر و مادرم و همه مؤمنان را در آن روز كه حساب بر پا مى‌شود بيامرز'),
(1792, 14, 42, 'و مپندار كه خدا از آنچه ستمكاران مى‌كنند غافل است جز اين نيست كه آنها را براى روزى كه چشم‌ها در آن خيره مى‌شود مهلت مى‌دهد'),
(1793, 14, 43, 'در حالى كه گردن كشيده سر به هوا راه مى‌روند و چشمشان بر هم نمى‌آيد و [از وحشت‌] دل‌هايشان تهى است'),
(1794, 14, 44, 'و مردم را از روزى بترسان كه عذاب الهى به سراغشان مى‌آيد پس آنان كه ستم كردند مى‌گويند: پروردگارا! ما را تا چندى مهلت بخش تا دعوت تو را پاسخ گوييم و از رسولان پيروى كنيم. [گفته شود:] آيا شما نبوديد كه پيش‌تر سوگند مى‌خورديد كه شما را فنايى نيست'),
(1795, 14, 45, 'و در خانه‌هاى كسانى كه بر خود ستم كردند ساكن شديد، و براى شما روشن گرديد كه با آنها چگونه معامله كرديم، و مثل‌ها براى شما زديم [باز هم بيدار نشديد]'),
(1796, 14, 46, 'و آنها نهايت نيرنگ خود را به كار زدند و مكرشان نزد خدا [معلوم‌] است، هر چند از مكرشان كوه‌ها از جا كنده شود'),
(1797, 14, 47, 'پس خدا را خلاف كننده‌ى وعده‌ى خويش با پيامبرانش مپندار چرا كه خداوند شكست‌ناپذير انتقام‌گير است'),
(1798, 14, 48, 'روزى كه زمين به زمين ديگر و آسمان‌ها [به آسمان‌هاى ديگر] مبدل شود، و [مردم‌] در برابر خداى يگانه قهار ظاهر شوند'),
(1799, 14, 49, 'و گناهكاران را در آن روز مى‌بينى كه در زنجيرها بسته شده‌اند'),
(1800, 14, 50, 'جامه‌هايشان از مس گداخته [يا قير] است و صورت‌هايشان را آتش مى‌پوشاند'),
(1801, 14, 51, 'تا خدا هر كس را به آنچه انجام داده كيفر دهد. به راستى كه خدا در حساب سريع است'),
(1802, 14, 52, 'اين [قرآن‌] پيامى روشنى براى مردم است، و براى اين كه بدان انذار شوند و بدانند كه تنها او معبود يگانه است، و تا صاحبان خرد پند گيرند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف، لام، را. اين آيات كتاب و قرآن روشنگر است'),
(1804, 15, 2, 'چه بسا كسانى كه كافر شدند آرزو مى‌كنند كه اى كاش مسلمان بودند'),
(1805, 15, 3, 'آنها را بگذار تا بخورند و بهره‌گيرند و آرزوها سرگرمشان كند، ولى به زودى خواهند دانست'),
(1806, 15, 4, 'و هيچ شهرى را هلاك نكرديم مگر اين كه براى آن اجلى معين بود'),
(1807, 15, 5, 'هيچ امتى از اجل خويش نه پيش مى‌افتد و نه پس مى‌ماند'),
(1808, 15, 6, 'و گفتند: اى كسى كه قرآن بر او نازل شده، تو به يقين ديوانه‌اى'),
(1809, 15, 7, 'اگر راست مى‌گويى چرا فرشته‌ها را نزد ما نمى‌آورى'),
(1810, 15, 8, 'ما فرشتگان را جز به حق نمى‌فرستيم و در آن هنگام ديگر مهلت داده نمى‌شوند'),
(1811, 15, 9, 'بى‌ترديد ما خود اين قرآن را نازل كرديم و قطعا ما خود نگهدار آنيم'),
(1812, 15, 10, 'و البته، پيش از تو نيز در ميان گروه‌هاى پيشينيان [پيامبرانى‌] فرستاديم'),
(1813, 15, 11, 'و هيچ پيامبرى برايشان نمى‌آمد جز آن كه او را مسخره مى‌كردند'),
(1814, 15, 12, 'بدين‌سان آن [قرآن‌] را در دل‌هاى مجرمان راه مى‌دهيم'),
(1815, 15, 13, '[با اين حال‌] به آن ايمان نمى‌آورند، و روش پيشينيان نيز [بر اين‌] رفته است [كه پيامبران را تكذيب مى‌كردند]'),
(1816, 15, 14, 'و اگر درى از آسمان بر آنان مى‌گشوديم كه در آن پيوسته بالا مى‌رفتند'),
(1817, 15, 15, 'قطعا مى‌گفتند: جز اين نيست كه ما چشم‌بندى شده‌ايم، بلكه ما گروهى جادو زده‌ايم'),
(1818, 15, 16, 'و همانا ما در آسمان برج‌هايى قرار داديم و آن را براى تماشاگران آراستيم'),
(1819, 15, 17, 'و آن را از [ورود] هر شيطان مطرودى نگاه داشتيم'),
(1820, 15, 18, 'مگر آن كس كه دزديده گوش فرا دهد كه شهابى روشن او را دنبال مى‌كند'),
(1821, 15, 19, 'و زمين را گسترديم و در آن كوه‌هاى استوار افكنديم و در آن از هر چيز متناسب [نياز انسان‌] رويانديم'),
(1822, 15, 20, 'و در آن براى شما و براى آن كه روزى رسان وى نيستيد اسباب زندگانى پديد آورديم'),
(1823, 15, 21, 'و هر چه هست، خزينه‌هاى آن نزد ماست و ما آن را جز به اندازه‌ى معين نازل نمى‌كنيم'),
(1824, 15, 22, 'و بادها را بارور كننده فرستاديم و از آسمان آبى نازل كرديم و شما را بدان سيراب نموديم، در حالى كه شما ذخيره كننده‌ى آن نبوديد'),
(1825, 15, 23, 'و بى‌ترديد، اين ماييم كه زنده مى‌كنيم و مى‌ميرانيم و ما وارث [همه‌] هستيم'),
(1826, 15, 24, 'و ما به خوبى رفتگان شما را شناخته‌ايم و بازماندگان شما را نيز شناخته‌ايم'),
(1827, 15, 25, 'و مسلما پروردگار تو هموست كه آنها را محشور خواهد كرد، [و] او حكيم داناست'),
(1828, 15, 26, 'همانا انسان را از گلى خشكيده، از لجن مانده‌ى بدبوى آفريديم'),
(1829, 15, 27, 'و جن را پيش از آن از آتش نافذ آفريديم'),
(1830, 15, 28, 'و [ياد كن‌] هنگامى را كه پروردگار تو به فرشتگان گفت: من آفريننده بشرى از گل خشكيده، از لجن مانده‌ى بدبوى خواهم بود'),
(1831, 15, 29, 'پس وقتى آن را پرداختم و از روح خود در آن دميدم براى او به سجده در افتيد'),
(1832, 15, 30, 'پس فرشتگان همگى يكسره سجده كردند'),
(1833, 15, 31, 'جز ابليس كه سرباز زد از اين كه با سجده كنندگان باشد'),
(1834, 15, 32, 'گفت: اى ابليس! تو را چه شده است كه با سجده كنندگان نباشى'),
(1835, 15, 33, 'گفت: من آن نيستم كه براى بشرى كه او را از گلى خشك، از لجن مانده‌ى بدبوى آفريده‌اى سجده كنم'),
(1836, 15, 34, 'گفت: پس از آن جا بيرون شو كه بى‌شك تو مطرودى'),
(1837, 15, 35, 'و بر توست لعنت و نفرين تا روز قيامت'),
(1838, 15, 36, 'گفت: اى پروردگار من! پس تا روزى كه [مردم‌] برانگيخته مى‌شوند مهلتم ده'),
(1839, 15, 37, 'گفت: تو از مهلت يافتگانى'),
(1840, 15, 38, 'تا روز وقت معين'),
(1841, 15, 39, 'گفت: پروردگارا! به سبب آن كه مرا گمراه كردى، من هم [باطل را] در روى زمين حتما برايشان مى‌آرايم و همه را گمراه مى‌كنم'),
(1842, 15, 40, 'مگر بندگان خالص شده‌ى تو را از ميان آنها'),
(1843, 15, 41, '[خداى‌] گفت: اين سنت و راه راستى است بر عهده‌ى من'),
(1844, 15, 42, 'كه تو را بر بندگان من تسلطى نيست، جز كسانى از گمراهان كه تو را پيروى كنند'),
(1845, 15, 43, 'و قطعا جهنم وعده‌گاه همه آنهاست'),
(1846, 15, 44, 'آن را هفت در است و براى هر درى، گروه معينى از آن [گمراه‌] ها تقسيم شده‌اند'),
(1847, 15, 45, 'بى‌گمان، پرهيزكاران در باغ‌ها و چشمه‌سارانند'),
(1848, 15, 46, '[به آنها گويند] به سلامت و ايمنى داخل آن شويد'),
(1849, 15, 47, 'و هر كينه‌اى را از سينه آنها برداشتيم، كه برادرانه بر تخت‌ها رو به روى هم قرار گيرند'),
(1850, 15, 48, 'در آن جا رنجى به آنان نرسد و نه از آن بيرون رانده مى‌شوند'),
(1851, 15, 49, 'به بندگان من خبر ده كه همانا من آمرزنده‌ى مهربانم'),
(1852, 15, 50, 'و اين كه عذاب من عذابى دردآور است'),
(1853, 15, 51, 'و آنها را از مهمانان ابراهيم خبر كن'),
(1854, 15, 52, 'هنگامى كه بر او وارد شدند و سلام گفتند. گفت: همانا ما از شما بيمناكيم'),
(1855, 15, 53, 'گفتند: مترس كه ما تو را به پسرى دانا مژده مى‌دهيم'),
(1856, 15, 54, 'گفت: آيا با اين كه مرا پيرى فرا رسيده است بشارتم مى‌دهيد؟ به چه بشارت مى‌دهيد'),
(1857, 15, 55, 'گفتند: ما تو را به حق بشارت داديم، پس از نوميدان مباش'),
(1858, 15, 56, 'گفت: چه كسى جز گمراهان، از رحمت پروردگارش نوميد مى‌شود'),
(1859, 15, 57, 'گفت: اى فرستادگان! پس كار شما چيست'),
(1860, 15, 58, 'گفتند: ما به سوى مردمى گناهكار فرستاده شده‌ايم'),
(1861, 15, 59, 'مگر خاندان لوط كه ما قطعا همه آنها را نجات مى‌دهيم'),
(1862, 15, 60, 'جز زنش را كه مقدّر كرده‌ايم وى از بازماندگان [در عذاب‌] باشد'),
(1863, 15, 61, 'پس چون فرستادگان نزد خاندان لوط آمدند'),
(1864, 15, 62, 'لوط گفت: شما مردمى ناشناس هستيد'),
(1865, 15, 63, 'گفتند: ما همان را براى تو آورده‌ايم كه در آن ترديد مى‌كردند [نزول عذاب را]'),
(1866, 15, 64, 'و ما براى تو خبر راست آورده‌ايم و قطعا ما راست گوييم'),
(1867, 15, 65, 'بنابر اين، در پاسى از شب خاندان خود را حركت ده و خود از پى ايشان روانه شو، و كسى از شما به پشت سر ننگرد، و همان جا كه دستور داده مى‌شويد برويد'),
(1868, 15, 66, 'و او را از اين امر آگاه كرديم كه صبحگاهان همه آنها ريشه كن خواهند شد'),
(1869, 15, 67, 'و اهل شهر شادى‌كنان سر رسيدند'),
(1870, 15, 68, 'لوط گفت: اينان مهمانان منند، پس آبرويم مريزيد'),
(1871, 15, 69, 'و از خدا پروا كنيد و مرا خوار نسازيد'),
(1872, 15, 70, 'گفتند: مگر تو را از [مهمانى و پناه دادن‌] مردمان منع نكرده‌ايم'),
(1873, 15, 71, 'گفت: اينان دختران منند، [با آنها ازدواج نماييد] اگر كارى مى‌كنيد'),
(1874, 15, 72, 'به جان تو سوگند كه آنها در مستى خود سرگشته بودند'),
(1875, 15, 73, 'پس به هنگام طلوع آفتاب [صيحه‌ى مرگبار] آنها را فرا گرفت'),
(1876, 15, 74, 'پس آن شهر را زير و رو كرديم و بر آنان سنگ‌هايى از سجّيل بارانديم'),
(1877, 15, 75, 'به يقين، در اين [كيفر] براى هوشياران عبرت‌هاست'),
(1878, 15, 76, 'و ويرانه‌هاى آن هنوز بر سر راهى است كه بر جاى مانده'),
(1879, 15, 77, 'بى‌گمان، در اين براى مؤمنان عبرتى است'),
(1880, 15, 78, 'و به راستى اهل «ايكه» ستمگر بودند'),
(1881, 15, 79, 'پس از آنها انتقام گرفتيم، و آن دو شهر اكنون بر رهگذرى آشكار است'),
(1882, 15, 80, 'و اهل حجر (قوم صالح) نيز پيامبران را تكذيب كردند'),
(1883, 15, 81, 'و آيات خود را به آنها داديم ولى از آن رويگردان بودند'),
(1884, 15, 82, 'و براى خود از كوه‌ها خانه‌هايى مى‌تراشيدند كه در امان بمانند'),
(1885, 15, 83, 'پس صبحدم صيحه [مرگبار] آنها را در گرفت'),
(1886, 15, 84, 'و آنچه [از امكانات‌] به دست آورده بودند به كارشان نيامد'),
(1887, 15, 85, 'و ما آسمان‌ها و زمين و آنچه را كه ميان آن دو است جز به حق نيافريديم، و يقينا قيامت آمدنى است، پس [با آنها] بساز، سازشى نيكو'),
(1888, 15, 86, 'پروردگار تو همان آفريدگار آگاه است'),
(1889, 15, 87, 'و به راستى تو را سبع المثانى (سوره‌ى فاتحه) و قرآن بزرگ عطا كرديم'),
(1890, 15, 88, 'چشمان خود را به امكاناتى كه به گروه‌هايى از آنها داده‌ايم خيره مكن، و براى ايشان اندوه مخور، و بال محبت خود را بر مؤمنان بگستر'),
(1891, 15, 89, 'و بگو: من همان هشدار دهنده‌ى آشكارم [به عذابى سخت‌]'),
(1892, 15, 90, 'آن سان كه بر تجزيه گران [آيات الهى‌] نازل كرديم'),
(1893, 15, 91, 'همانان كه قرآن را بخش بخش كردند [برخى را قبول، برخى را مردود و برخى را شعر يا سحر ناميدند]'),
(1894, 15, 92, 'پس سوگند به پروردگارت كه همه آنها را بازخواست خواهيم كرد'),
(1895, 15, 93, 'از آنچه انجام مى‌دادند'),
(1896, 15, 94, 'پس آنچه را بدان دستور مى‌يابى آشكار كن و از مشركان روى بگردان'),
(1897, 15, 95, 'ما شرّ مسخره كنندگان را از تو دور مى‌سازيم'),
(1898, 15, 96, 'همانان كه با خدا معبود ديگرى قرار مى‌دهند، اما به زودى خواهند دانست'),
(1899, 15, 97, 'و ما به خوبى مى‌دانيم كه تو از آنچه مى‌گويند دلتنگ مى‌شوى'),
(1900, 15, 98, 'پس با ستايش پروردگارت تسبيح گوى و از سجده كنندگان باش'),
(1901, 15, 99, 'و پروردگارت را بندگى كن تا وقتى كه تو را مرگ فرا رسد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'فرمان [عذاب‌] خدا آمد، پس شتاب در آن را طلب نكنيد. او منزّه و فراتر است از آنچه [با وى‌] شريك مى‌سازند'),
(1903, 16, 2, 'فرشتگان را با روح (حامل وحى) به امر خود، بر هر كس از بندگانش كه بخواهد نازل مى‌كند كه [مردم را] بيم دهيد كه معبودى جز من نيست، پس از من بترسيد'),
(1904, 16, 3, 'آسمان‌ها و زمين را به حق آفريد. او از هر چه با وى شريك مى‌سازند فراتر است'),
(1905, 16, 4, 'انسان را از نطفه‌اى آفريد و آن‌گاه او ستيزه‌گرى آشكار است'),
(1906, 16, 5, 'و چهارپايان را براى شما آفريد كه در آنها برايتان گرمى [پشم‌] و سودهايى است و از آنها تغذيه مى‌كنيد'),
(1907, 16, 6, 'و در آنها براى شما زيبايى [و شكوهى‌] است، آن‌گاه كه [آنها را] از چراگاه باز مى‌آوريد و هنگامى كه به چرا مى‌فرستيد'),
(1908, 16, 7, 'و بارهاى سنگين شما را به شهرى كه جز با مشقت جانكاه نمى‌توانستيد بدانجا برسيد حمل مى‌كنند. به راستى پروردگار شما رئوف و مهربان است'),
(1909, 16, 8, 'و اسبان و استران و خران را [آفريد] تا بر آنها سوار شويد و [شما را] زينتى باشند، و چيزهايى مى‌آفريند كه نمى‌دانيد'),
(1910, 16, 9, 'و بر خداست كه راه راست را نشان دهد، و برخى راه‌ها منحرف است، و اگر خدا مى‌خواست مسلما همه شما را [به اجبار] هدايت مى‌كرد'),
(1911, 16, 10, 'اوست كه از آسمان آبى فرستاد كه از آن مى‌نوشيد و از آن درخت و گياه مى‌رويد كه در آن [چهارپايان را] مى‌چرانيد'),
(1912, 16, 11, 'و با آن براى شما كشت و زيتون و نخل‌ها و انگورها و هر نوع ميوه‌اى مى‌روياند. قطعا در اينها براى مردمى كه مى‌انديشند آيتى است'),
(1913, 16, 12, 'و شب و روز و خورشيد و ماه را براى شما رام كرد، و ستارگان مسخر فرمان اويند. مسلما در اينها براى مردمى كه تعقّل مى‌كنند شواهدى است'),
(1914, 16, 13, 'و آنچه را كه در زمين به رنگ‌هاى گوناگون براى شما پديد آورد [مسخر شما ساخت‌]. بى‌ترديد در اينها براى مردمى كه پند مى‌گيرند نشانه‌اى است'),
(1915, 16, 14, 'و اوست آن كه دريا را مسخّر [شما] ساخت تا از آن گوشت تازه بخوريد و از آن زيورى استخراج كنيد كه آن را مى‌پوشيد، و كشتى‌ها را در دريا شكافنده‌ى آب مى‌بينى، و براى اين است كه از كرم خدا روزى بطلبيد و به اين اميد كه شكرگزار باشيد'),
(1916, 16, 15, 'و در زمين كوه‌هايى استوار افكند تا شما را نلرزاند، و رودها و راه‌ها پديد آورد تا شما راه خود را پيدا كنيد'),
(1917, 16, 16, 'و نيز علاماتى [قرار داد]، و با ستارگان، آنها راه مى‌يابند'),
(1918, 16, 17, 'پس آيا آن كه مى‌آفريند مانند كسى است كه نمى‌آفريند؟ چرا متوجه نمى‌شويد'),
(1919, 16, 18, 'و اگر نعمت‌هاى خدا را شماره كنيد، نمى‌توانيد آنها را به آخر رسانيد. بى‌شك خداوند آمرزنده‌ى مهربان است'),
(1920, 16, 19, 'و خدا آنچه را نهان مى‌كنيد و آنچه را عيان مى‌سازيد مى‌داند'),
(1921, 16, 20, 'و معبودهايى را كه غير خدا مى‌خوانند چيزى را خلق نمى‌كنند، بلكه خودشان آفريده مى‌شوند'),
(1922, 16, 21, 'مردگانى بى‌جانند، و نمى‌فهمند چه زمانى برانگيخته خواهند شد'),
(1923, 16, 22, 'معبود شما معبودى يگانه است، پس كسانى كه به آخرت ايمان نمى‌آورند دل‌هايشان منكر [حق‌] است و ايشان متكبرند'),
(1924, 16, 23, 'شكى نيست كه خداوند آنچه را پنهان مى‌دارند و آنچه را عيان مى‌سازند مى‌داند. بى‌ترديد او گردنكشان را دوست نمى‌دارد'),
(1925, 16, 24, 'و چون به آنها گفته شود: پروردگارتان چه نازل كرده است؟ گويند: افسانه‌هاى پيشينيان'),
(1926, 16, 25, 'تا روز قيامت، هم بار گناهان خود را به تمامى بردارند و هم سهمى از بار كسانى را كه بدون علم گمراهشان مى‌كنند. آگاه باش كه بد بارى را مى‌كشند'),
(1927, 16, 26, 'همانا كسانى هم كه پيش از آنها بودند مكر كردند و خدا به سراغشان آمد و از پايه‌ها بر بنيانشان زد، و سقف بر سرشان فرود آمد و عذاب از آن جا كه حدس نمى‌زدند بر آنان فرا رسيد'),
(1928, 16, 27, 'سپس روز قيامت رسوايشان سازد و گويد: كجايند آن شريكان من كه در باره‌ى آنها مناقشه مى‌كرديد؟ آنها كه علم داده شده‌اند گويند: در حقيقت امروز رسوايى و بدبختى بر كافران است'),
(1929, 16, 28, 'همانان كه فرشتگان جانشان را در حالى كه ستمگر خويشند بستانند، پس سر تسليم فرود آرند [و گويند]: ما هيچ كارى بدى نمى‌كرديم. آرى، مسلما خدا به آنچه مى‌كرديد داناست'),
(1930, 16, 29, 'پس از درهاى دوزخ وارد شويد و در آن هميشه بمانيد، و راستى بد است جايگاه متكبران'),
(1931, 16, 30, 'و به كسانى كه تقوا پيشه كردند گفته شود: پروردگارتان چه نازل كرد؟ گويند: خير و خوبى. براى آنها كه در اين دنيا نيكى كردند پاداش خوبى است، و سراى آخرت حتما بهتر است، و سراى پرهيزكاران نيكوست'),
(1932, 16, 31, 'بهشت‌هاى جاودان كه در آن داخل مى‌شوند و نهرها از پاى درختانش جارى است. در آن جا هر چه بخواهند برايشان فراهم است. بدين گونه خداوند پرهيزكاران را پاداش مى‌دهد'),
(1933, 16, 32, 'همانان كه فرشتگان جانشان را در حالى كه پاكيزه‌اند مى‌ستانند و مى‌گويند: سلام بر شما، به پاداش آنچه مى‌كرديد به بهشت درآييد'),
(1934, 16, 33, 'آيا [كافران‌] جز اين انتظار دارند كه فرشتگان سويشان بيايند يا فرمان [عذاب‌] پروردگارت بيايد؟ كسانى كه پيش از آنها بودند نيز چنين كردند، و خداوند به آنان ستم نكرد بلكه آنها به خويشتن ستم مى‌كردند'),
(1935, 16, 34, 'پس كيفر بدى‌هايى كه كردند به آنها رسيد، و آنچه مسخره‌اش مى‌كردند گريبانشان را گرفت'),
(1936, 16, 35, 'و مشركان گفتند: اگر خدا مى‌خواست، نه ما و نه پدرانمان هيچ چيزى را غير از او نمى‌پرستيديم و بى‌خواست او چيزى را حرام نمى‌كرديم. كسانى كه پيش از آنها بودند نيز چنين كردند، ولى آيا پيامبران وظيفه‌اى جز ابلاغ آشكار بر عهده دارند'),
(1937, 16, 36, 'و ما در هر امتى رسولى برانگيختيم كه [بگويد]: خدا را بپرستيد و از طغيانگر دورى كنيد. پس برخى از آنان را خدا هدايت كرد و برخى نيز سزاوار گمراهى گشتند پس در زمين بگرديد و ببينيد عاقبت تكذيب كنندگان چگونه بوده است'),
(1938, 16, 37, 'اگر بر هدايت آنها اصرار ورزى، [بدان كه‌] خدا كسى را كه گمراه كرده [و به خودش واگذاشته‌] هدايت نمى‌كند و براى ايشان هيچ ياورى نخواهد بود'),
(1939, 16, 38, 'و به خدا قسم خوردند، قسم‌هاى مؤكد كه هر كس بميرد خدا زنده‌اش نمى‌كند. چرا [زنده مى‌كند] و اين وعده‌ى حقى است كه بر عهده‌ى اوست ولى بيشتر مردم نمى‌دانند'),
(1940, 16, 39, 'تا آنچه را در مورد آن اختلاف دارند برايشان روشن سازد، و تا كافران بدانند كه دروغگو بوده‌اند'),
(1941, 16, 40, 'همانا سخن ما به چيزى كه آن را خواسته باشيم اين است كه به آن بگوييم: باش، پس مى‌باشد'),
(1942, 16, 41, 'و كسانى كه پس از ستمديدگى، به خاطر خدا مهاجرت كردند بى‌ترديد آنان را در دنيا جاى نيكويى مى‌دهيم و بى‌شك پاداش آخرت بزرگ‌تر است اگر مى‌دانستند'),
(1943, 16, 42, 'همانان كه صبورى كردند و بر خداى خويش توكّل مى‌كنند'),
(1944, 16, 43, 'و پيش از تو نفرستاديم جز مردانى را كه به ايشان وحى مى‌كرديم، پس اگر نمى‌دانيد از اهل ذكر (و آگاهان) بپرسيد'),
(1945, 16, 44, '[آنها را] با حجت‌ها و كتاب‌ها [فرستاديم‌] و اين قرآن را به سوى تو فرستاديم تا براى مردم آنچه را كه به سويشان نازل شده است بيان كنى و اميد كه بينديشند'),
(1946, 16, 45, 'پس آيا كسانى كه نقشه‌هاى بد كشيدند ايمن شده‌اند از اين كه خدا آنها را در زمين فرو برد يا عذاب از جايى كه نمى‌فهمند سراغشان بيايد؟'),
(1947, 16, 46, 'يا هنگام [توطئه و] مشغوليتشان كه كارى نتوانند بكنند گريبانشان را بگيرد'),
(1948, 16, 47, 'يا اين كه آنها را به تدريج و در حال خوف [از آمدن بلا] بگيرد [كه در آن مهلت توبه هست‌] چرا كه خداى شما رئوف و مهربان است'),
(1949, 16, 48, 'آيا چيزهايى را كه خدا آفريده نديدند كه چگونه سايه‌هايشان از راست و چپ مى‌گردد و فروتنانه براى خدا سجده مى‌آورند و تخلف نمى‌كنند'),
(1950, 16, 49, '[نه تنها سايه‌ها بلكه‌] هر چه در آسمان‌ها و هر چه در زمين از جنبندگان و فرشتگان است همه خدا را سجده مى‌كنند و تكبّر نمى‌ورزند'),
(1951, 16, 50, 'از پروردگارشان كه بر آنها سيطره دارد مى‌ترسند و هر چه دستور مى‌يابند همان مى‌كنند'),
(1952, 16, 51, 'و خدا فرمود: دو معبود براى خود نگيريد. جز اين نيست كه [معبود شما] همان خداى يگانه است، پس تنها از من بترسيد'),
(1953, 16, 52, 'و آنچه در آسمان‌ها و زمين است از آن اوست، و دين و بندگى نيز هميشه براى اوست، پس آيا از غير خدا مى‌ترسيد'),
(1954, 16, 53, 'و هر چه نعمت داريد از جانب خداست، و آن‌گاه وقتى كه محنتى به شما رسد به درگاه او لابه و زارى مى‌كنيد'),
(1955, 16, 54, 'سپس چون آن محنت را از شما برداشت، آن وقت گروهى از شما به پروردگارشان شرك مى‌آورند'),
(1956, 16, 55, 'تا نعمتى را كه به آنها داده‌ايم انكار كنند. پس فعلا بهره گيريد ولى زود است كه بدانيد'),
(1957, 16, 56, 'و از آنچه به ايشان روزى داده‌ايم نصيبى براى معبودانى كه نمى‌شناسند معين مى‌كنند. به خدا سوگند حتما از آن دروغ‌ها كه مى‌سازيد بازخواست مى‌شويد'),
(1958, 16, 57, 'و براى خدا دخترانى قائل مى‌شوند. منزّه است او. و براى خودشان آنچه را ميل دارند قرار مى‌دهند'),
(1959, 16, 58, 'و هرگاه يكيشان به دختر مژده يابد رويش سياه مى‌گردد در حالى كه خشم و اندوه خود را فرو مى‌خورد'),
(1960, 16, 59, 'از ناگوارى خبرى كه بشارتش داده‌اند از قوم خود متوارى مى‌شود كه آيا او را به خوارى نگاه دارد يا در خاك پنهانش كند؟ وه، چه بد داورى مى‌كنند'),
(1961, 16, 60, 'صفت زشت براى كسانى است كه به آخرت ايمان نمى‌آورند و بهترين وصف از آن خداست و او مقتدر حكيم است'),
(1962, 16, 61, 'و اگر خداوند مردم را به خاطر ظلمشان مؤاخذه مى‌كرد، جنبنده‌اى بر روى زمين باقى نمى‌گذاشت، و ليكن [كيفر] آنها را تا وقتى معين به تأخير مى‌اندازد، و چون اجلشان فرا رسيد نه ساعتى از آن تأخير كنند و نه پيش افتند'),
(1963, 16, 62, 'و براى خدا چيزهايى قرار مى‌دهند كه خود از آن كراهت دارند و زبانشان دروغ‌پردازى مى‌كند كه براى آنها عاقبت خوبى است، بلكه ناگزير آتش [جهنم‌] براى آنهاست و به سوى آن پيش فرستاده خواهند شد'),
(1964, 16, 63, 'سوگند به خدا كه به سوى امت‌هاى پيش از تو [رسولانى‌] فرستاديم ولى شيطان اعمالشان را برايشان آراست و امروز هم سرپرستشان هموست و برايشان عذابى دردناك است'),
(1965, 16, 64, 'و ما اين كتاب را بر تو نازل نكرديم مگر براى اين كه آنچه را در آن اختلاف دارند برايشان بيان كنى و هدايت و رحمتى باشد براى مردمى كه ايمان مى‌آورند'),
(1966, 16, 65, 'و خدا از آسمان آبى فرود آورد و با آن زمين را پس از مردنش زنده كرد. قطعا در اين امر براى مردمى كه بشنوند حجتى است'),
(1967, 16, 66, 'و در دام‌ها قطعا براى شما عبرتى است: از آنچه در شكم آنهاست، از ميان علف‌هاى جويده و خون، شير خالصى به شما مى‌نوشانيم كه براى نوشندگان گواراست'),
(1968, 16, 67, 'و از ميوه‌ى نخل‌ها و انگورها خمر [مستى بخش ناپاك‌] و روزى پاكيزه تهيه مى‌كنيد. همانا در اينها براى مردمى كه تعقل كنند حجتى است'),
(1969, 16, 68, 'و پروردگار تو به زنبور عسل الهام كرد كه: از كوه‌ها و درختان و از داربست‌هايى كه [مردم‌] مى‌سازند لانه‌هايى بگير'),
(1970, 16, 69, 'سپس از همه ميوه‌ها بخور و راه‌هاى پروردگارت را مطيعانه طى كن. از شكم آنها شربتى رنگارنگ بيرون مى‌آيد كه در آن شفاى مردم است. به راستى در اين [امر] براى مردمى كه تفكر مى‌كنند نشانه‌ى [علم و قدرت‌] هست'),
(1971, 16, 70, 'و خدا شما را آفريد، سپس جان شما را مى‌گيرد، و برخى از شما به پست‌ترين دوران عمر مى‌رسد تا [سر انجام‌] هر چه را آموخته است از ياد ببرد. به راستى خدا داناى تواناست'),
(1972, 16, 71, 'و خدا بعضى از شما را [به خاطر تفاوت تلاش‌ها و امتحان‌] در روزى بر بعضى ديگر فزونى داده است، ولى كسانى كه فزونى داده شده‌اند حاضر نيستند روزى خود را به بردگان خود بازدهند تا در آن با هم مساوى باشند. آيا نعمت خدا را انكار مى‌كنند [و شرك مى‌ورزند]'),
(1973, 16, 72, 'و خداوند براى شما از خودتان همسرانى قرار داد و از همسرانتان براى شما پسران و نوادگانى پديد آورد و از پاكيزه‌ها روزيتان داد. آيا [باز هم‌] به باطل مى‌گروند، و به نعمت خدا كافر مى‌شوند'),
(1974, 16, 73, 'و به جاى خدا چيزهايى را مى‌پرستند كه مالك هيچ رزقى از آسمان‌ها و زمين براى آنها نيستند و توانايى ندارند'),
(1975, 16, 74, 'پس براى خدا مثل‌ها [ى ناروا و شرك آميز] نزنيد، خدا مى‌داند و شما نمى‌دانيد'),
(1976, 16, 75, 'خدا بنده‌ى مملوكى را مثال زده كه هيچ كارى از او بر نمى‌آيد، و كسى را كه از جانب خويش رزق نيكويش داده‌ايم كه از آن در نهان و عيان انفاق مى‌كند، آيا اين دو يكسانند؟ [خدا نيز با چيزى يكسان نيست‌] ستايش از آن خداست اما بيشترشان نمى‌دانند'),
(1977, 16, 76, 'و خدا مثل [ديگرى‌] زد: دو مردند كه يكى از آنها گنگ است و هيچ كارى از او بر نمى‌آيد و سربار مولاى خويش است هر جا او را مى‌فرستد خيرى نمى‌آورد. آيا او با كسى كه به عدالت فرمان مى‌دهد و خود بر راه راست است يكسان است'),
(1978, 16, 77, 'و غيب آسمان‌ها و زمين از آن خداست، و كار قيامت جز مانند يك چشم بر هم زدن يا نزديك‌تر از آن نيست همانا خدا بر هر چيزى قادر است'),
(1979, 16, 78, 'و خداوند شما را از شكم مادرانتان بيرون آورد در حالى كه چيزى نمى‌دانستيد، و براى شما گوش و ديدگان و دل‌ها قرار داد تا شكر گزاريد'),
(1980, 16, 79, 'آيا پرندگان را ننگريستند كه [چگونه‌] در فضاى آسمان مسخرند؟ جز خدا [با ساختارى كه به آنها داده‌] احدى آنها را نگاه نمى‌دارد. به راستى در اين [امر] براى مردمى كه ايمان مى‌آورند نشانه‌هاى [قدرت‌] است'),
(1981, 16, 80, 'و خداوند براى شما از خانه‌هايتان، اسباب آرامش شما را پديد آورد و از پوست دام‌ها برايتان [خيمه و] خانه پديد آورد كه با آنها در روز كوچ كردن و روز اقامتتان سبكبار باشيد [و به آسانى جا به جا شويد]. و از پشم‌ها و كرك‌ها و موهاى آنها اثاث و كالا [پديد آور'),
(1982, 16, 81, 'و خداوند از آنچه آفريده براى شما سايه‌سارها فراهم آورد و از كوه‌ها برايتان پناهگاه‌هايى قرار داد و براى شما جامه‌هايى پديد كرد كه از گرما نگاهتان دارد و جامه‌هايى [چون زره‌] كه در [جنگ‌ها و] سختى‌هاى شما حفظتان كند. اين گونه نعمت خويش را بر شما تمام'),
(1983, 16, 82, 'پس اگر روى برتابند، فقط ابلاغ آشكار بر عهده‌ى توست'),
(1984, 16, 83, 'نعمت خدا را مى‌شناسند آن‌گاه منكر آن مى‌شوند و بيشترشان كافرند'),
(1985, 16, 84, 'و روزى كه از هر امتى گواهى برانگيزيم، سپس به كسانى كه كافر شده‌اند نه رخصتى داده مى‌شود و نه عذرشان پذيرفته مى‌گردد'),
(1986, 16, 85, 'و چون كسانى كه ستم كرده‌اند عذاب را ببينند، نه عذابشان سبك شود و نه مهلت داده شوند'),
(1987, 16, 86, 'و چون مشركان شريكان خود را ببينند گويند: پروردگارا! اينها همان شريكان ما هستند كه آنها را به جاى تو مى‌خوانديم. ولى شريكان اين سخن را به خودشان بر مى‌گردانند كه شما حقا دروغگوييد'),
(1988, 16, 87, 'و در آن روز در برابر خدا سر تسليم افكنند و دروغ‌هايى كه مى‌ساختند بر باد رود'),
(1989, 16, 88, 'كسانى كه كفر ورزيدند و از راه خدا بازداشتند، به سزاى آن كه فساد مى‌كردند عذابى بر عذابشان مى‌افزاييم'),
(1990, 16, 89, 'و [ياد كن‌] روزى را كه در هر امتى گواهى از خودشان بر آنها برانگيزيم و تو را هم بر اين امت گواه آوريم، و اين كتاب را كه بيانگر همه چيز و براى مسلمانان رهنمود و رحمت و بشارتى است بر تو نازل كرديم'),
(1991, 16, 90, 'همانا خداوند به عدل و احسان و بخشش به خويشان فرمان مى‌دهد و از فحشا و زشتكارى و ستم نهى مى‌كند. شما را پند مى‌دهد، باشد كه بيدار شويد'),
(1992, 16, 91, 'و چون با خدا پيمان بستيد بدان وفا كنيد و سوگندهاى خود را پس از محكم ساختن آنها نشكنيد در حالى كه خدا را بر خود ضامن قرار داده‌ايد. بى‌شك خدا از آنچه مى‌كنيد آگاه است'),
(1993, 16, 92, 'و مانند آن زنى نباشيد كه رشته خود را پس از محكم تابيدن از هم واكرد كه سوگندهاى خود را به خاطر اين كه گروهى جمعيتشان افزون‌تر از ديگرى است [بشكنيد و پيمان خود را با پيامبر لغو كنيد و آن را] وسيله تقلب ميان خود قرار دهيد. جز اين نيست كه خدا شما را بدي'),
(1994, 16, 93, 'و اگر خدا مى‌خواست، همه شما را يك امت قرار مى‌داد، ولى هر كه را بخواهد گمراه مى‌كند و هر كه را بخواهد هدايت مى‌كند، و حتما از آنچه انجام مى‌داديد سؤال خواهيد شد'),
(1995, 16, 94, 'و سوگندهاى خود را دستاويز تقلب ميان خود قرار ندهيد تا مبادا گامى پس از استواريش بلغزد و به سزاى آن كه [مردم را] از راه خدا بازداشته‌ايد آثار سوء آن را بچشيد و براى شما عذابى بزرگ باشد'),
(1996, 16, 95, 'و پيمان خدا را به بهاى ناچيزى مفروشيد همانا آنچه نزد خداست همان براى شما بهتر است اگر مى‌دانستيد'),
(1997, 16, 96, 'آنچه نزد شماست تمام شدنى است و آنچه نزد خداست ماندنى است، و قطعا به كسانى كه شكيبايى كرده‌اند اجرشان را بر اساس بهترين كارى كه مى‌كردند مى‌دهيم'),
(1998, 16, 97, 'هر كس از مرد يا زن كار شايسته كند- در حالى كه مؤمن باشد- قطعا او را با زندگى پاكيزه‌اى حيات حقيقى مى‌بخشيم و مسلما اجرشان را بر پايه بهترين كارى كه مى‌كردند مى‌دهيم'),
(1999, 16, 98, 'پس آن‌گاه كه قرآن بخوانى از شيطان مطرود به خدا پناه بر'),
(2000, 16, 99, 'چرا كه او را بر كسانى كه ايمان آورده‌اند و بر خداى خود توكل مى‌كنند تسلطى نيست'),
(2001, 16, 100, 'تسلط او تنها بر كسانى است كه او را سرپرست مى‌گيرند و بر كسانى است كه به خدا شرك مى‌ورزند'),
(2002, 16, 101, 'و چون حكمى را به جاى حكم ديگر بياوريم در حالى كه خدا به آنچه نازل مى‌كند داناتر است، مى‌گويند: جز اين نيست كه تو دروغبافى. نه بلكه بيشتر آنان نمى‌دانند'),
(2003, 16, 102, 'بگو: آن را روح القدس از طرف پروردگارت به حق نازل كرده تا كسانى را كه ايمان آورده‌اند ثابت قدم كند و براى مسلمانان هدايت و بشارتى باشد'),
(2004, 16, 103, 'و ما به خوبى مى‌دانيم كه آنان مى‌گويند: جز اين نيست كه بشرى او را تعليم مى‌دهد. [نه چنين نيست، زيرا] زبان كسى كه اين نسبت را به او مى‌دهند عجمى است و اين قرآن به زبان عربى روشن است'),
(2005, 16, 104, 'مسلما كسانى كه به آيات خدا ايمان نمى‌آورند، خداوند آنها را هدايت نمى‌كند و ايشان را عذابى دردناك است'),
(2006, 16, 105, 'تنها كسانى دروغ مى‌بافند كه به آيات خدا ايمان ندارند و آنها خود دروغگويند'),
(2007, 16, 106, 'هر كس پس از ايمانش، به خدا كافر شود [جزايش دوزخ است‌] جز كسى كه تحت فشار [واقع‌] شده ولى دلش به ايمان محكم است آرى كسى كه سينه خود را به كفر بگشايد، خشمى از خدا بر آنهاست و ايشان را عذابى بزرگ خواهد بود'),
(2008, 16, 107, 'اين بدان سبب است كه آنها زندگى دنيا را بر آخرت ترجيح دادند و اين كه خدا قوم كافر را هدايت نمى‌كند'),
(2009, 16, 108, 'آنان كسانى هستند كه خدا بر دل‌ها و شنوايى و ديدگانشان مهر نهاده و آنها همان غافلانند'),
(2010, 16, 109, 'شكى نيست كه آنها در آخرت زيانكار خواهند بود'),
(2011, 16, 110, 'اما پروردگار تو نسبت به كسانى كه پس از آن همه رنج ديدن، هجرت نمودند و سپس جهاد كردند و شكيبايى ورزيدند، بى‌ترديد خداى تو بعد از آن نسبت به ايشان بخشنده و مهربان است'),
(2012, 16, 111, 'روزى كه هر كسى مى‌آيد در حالى كه از خود دفاع مى‌كند، و هر كسى در برابر آنچه كسب كرده است بى‌كم و كاست پاداش مى‌يابد و بر آنها ستم نمى‌رود'),
(2013, 16, 112, 'و خداوند شهرى را مثل زده است كه امن و آرام بود و رزقش به وفور از هر طرف به آن مى‌رسيد، اما كفران نعمت خدا كردند و خدا هم به سزاى آنچه انجام مى‌دادند طعم گرسنگى و هراس را به [مردم‌] آن چشانيد'),
(2014, 16, 113, 'و همانا فرستاده‌اى از خودشان به سوى آنها آمد، اما او را تكذيب كردند، پس در حالى كه ظالم بودند آنها را عذاب در گرفت'),
(2015, 16, 114, 'پس، از آنچه خدا شما را روزى كرده است حلال و پاكيزه بخوريد و نعمت خدا را شكر گزاريد اگر تنها او را بندگى مى‌كنيد'),
(2016, 16, 115, 'جز اين نيست كه خدا مردار و خون و گوشت خوك و آنچه را كه به غير نام خدا ذبح شده بر شما حرام كرده است. پس كسى كه به خوردن آنها ناگزير شود و از حدّ [ضرورت و حكم خدا تجاوز] نكند، قطعا خداوند آمرزنده‌ى مهربان است'),
(2017, 16, 116, 'و در باره آنچه زبانتان به دروغ و بى‌سند توصيف مى‌كند، نگوييد اين حلال است و آن حرام، تا بر خدا دروغ بسته باشيد. زيرا كسانى كه بر خدا دروغ مى‌بندند رستگار نمى‌شوند'),
(2018, 16, 117, 'بهره‌ى كمى است [كه در اين دنيا نصيبشان مى‌شود] ولى عذابى دردناك در انتظار آنهاست'),
(2019, 16, 118, 'و ما بر يهوديان آنچه را پيش‌تر بر تو حكايت نموديم حرام كرده بوديم، و ما در حق آنها ستم نكرديم بلكه آنها خود به خويشتن ستم مى‌كردند'),
(2020, 16, 119, 'سپس پروردگار تو نسبت به كسانى كه از روى جهالت گناه كردند و آن‌گاه از پس آن توبه نمودند و درستكارى كردند، البته پروردگارت بعد از آن آمرزنده‌ى مهربان است'),
(2021, 16, 120, 'به راستى ابراهيم [به تنهايى‌] يك امت و مطيع خدا و موحد بود و از مشركان نبود'),
(2022, 16, 121, 'و شكرگزار نعمت‌هاى او بود و خدا او را برگزيد و به راهى راست هدايتش كرد'),
(2023, 16, 122, 'و او را در دنيا نيكى داديم و بى‌ترديد در آخرت هم از شايستگان است'),
(2024, 16, 123, 'سپس به تو وحى كرديم كه از آيين ابراهيم حق گراى پيروى كن، و او از مشركان نبود'),
(2025, 16, 124, '[تحريم‌هاى‌] شنبه فقط بر كسانى مقرر شد كه در باره‌ى [صيد] آن اختلاف كردند، و قطعا پروردگار تو روز رستاخيز ميان آنها در باره‌ى آنچه بر سرش اختلاف مى‌كردند حكم خواهد كرد'),
(2026, 16, 125, 'مردم را با حكمت و اندرز نيكو به راه پروردگارت فراخوان و با آنان به نكوترين شيوه مجادله كن. بى‌ترديد پروردگار تو به حال كسى كه از راه او منحرف شده داناتر است و هدايت يافتگان را نيز بهتر مى‌شناسد'),
(2027, 16, 126, 'و اگر سزا مى‌دهيد، مانند آنچه با شما كرده‌اند سزا دهيد، و اگر صبورى كنيد بى‌شك همان براى صابران بهتر است'),
(2028, 16, 127, 'و صبور باش كه صبر تو جز به توفيق خدا نيست، و بر ايشان محزون مباش و از آن مكرى كه مى‌كنند دل تنگ مدار'),
(2029, 16, 128, 'بى‌ترديد خدا با كسانى است كه پارسايى كرده‌اند و كسانى كه نيكوكارند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزّه است آن كه بنده‌ى خود را شبى از مسجد الحرام به سوى مسجد الأقصى كه حومه آن را بركت داده‌ايم سير داد، تا آيات خويش را به او بنماييم كه همانا او شنواى بيناست'),
(2031, 17, 2, 'و به موسى كتاب داديم و آن را براى بنى اسرائيل وسيله هدايت ساختيم كه زنهار غير من و كيل و سرپرست نگيريد'),
(2032, 17, 3, '[شما] نسل كسانى هستيد كه آنها را [در كشتى‌] با نوح برداشتيم. به راستى او بنده‌اى شكرگزار بود'),
(2033, 17, 4, 'و به بنى اسرائيل در آن كتاب اعلام كرديم كه بى‌گمان دوبار در زمين فساد خواهيد كرد و به طغيان بزرگى دست خواهيد زد'),
(2034, 17, 5, 'پس آن‌گاه كه وعده‌ى اولين آن دو فرا رسيد، بندگانى از خود را كه جنگاورانى نيرومندند بر شما برانگيزيم تا [براى تعقيب شما] داخل خانه‌ها را جست جو كنند، و اين وعده انجام شدنى است'),
(2035, 17, 6, 'آن‌گاه دوباره غلبه بر آنها را به شما بازگردانيم و شما را با اموال و پسران يارى دهيم و تعداد نفرات شما را بيشتر كنيم'),
(2036, 17, 7, '[حال‌] اگر نيكى كنيد، به خود نيكى كرده‌ايد و اگر بدى كنيد به خود بد كرده‌ايد، پس وقتى وعده‌ى [كيفر] بعدى فرا رسد [چنان بر شما بتازند] كه از اندوه صورتتان را زشت گردانند و وارد مسجد [الاقصى‌] شوند، چنان كه بار اول داخل شده بودند و به هر چه دست يابند يك'),
(2037, 17, 8, '[اگر توبه كرديد] اميد است كه پروردگارتان بر شما رحمت آورد، و اگر [به فساد] باز گشتيد ما نيز باز مى‌گرديم. و دوزخ را براى كافران زندان قرار داده‌ايم'),
(2038, 17, 9, 'بى‌ترديد، اين قرآن به آيين كه استوارتر است راه مى‌نمايد و به آن مؤمنانى كه كارهاى شايسته مى‌كنند مژده مى‌دهد كه پاداشى بزرگ برايشان خواهد بود'),
(2039, 17, 10, 'و براى كسانى كه به آخرت ايمان نمى‌آورند عذابى دردناك آماده كرده‌ايم'),
(2040, 17, 11, 'و آدمى [بدون فكر] بدى را همان گونه مى‌طلبد كه نيكى را مى‌طلبد، و انسان همواره شتابزده است'),
(2041, 17, 12, 'و شب و روز را دو نشانه قرار داديم، پس نشانه شب را محو [و تيره‌] كرديم و نشانه روز را ظاهر و روشن قرار داديم تا از پروردگارتان روزى بطلبيد و شمار سال‌ها و حساب [اوقات‌] را بدانيد، و هر چيزى را به طور كامل توضيح داديم'),
(2042, 17, 13, 'و كارنامه هر انسانى را به گردنش آويخته‌ايم، و روز قيامت براى او نامه‌اى كه آن را گشوده مى‌بيند بيرون مى‌آوريم'),
(2043, 17, 14, '[گفته شود] نامه‌ات را بخوان. كافى است كه امروز خود حسابرس خويش باشى'),
(2044, 17, 15, 'هر كه به راه آيد، تنها به سود خود به راه آمده، و هر كه بيراهه رود تنها به ضرر خود بيراهه رفته است و هيچ حمل كننده‌اى بار گناه ديگرى را بر نمى‌دارد، و ما عذاب كننده نبوده‌ايم تا وقتى كه رسولى بفرستيم'),
(2045, 17, 16, 'و چون بخواهيم شهرى را هلاك كنيم خوشگذران‌هاى آن را وا مى‌داريم [و آزاد مى‌گذاريم‌] تا در آن فسق و فساد كنند پس عذاب الهى بر آن محقق مى‌گردد و آن را به طور كامل فرو مى‌كوبيم'),
(2046, 17, 17, 'و چه بسيار نسل‌هايى را پس از نوح هلاك نموديم، و همين بس كه پروردگار تو به گناهان بندگانش آگاه و بيناست'),
(2047, 17, 18, 'هر كس خواهان دنياى زود گذر است، به زودى هر كه را خواهيم نصيبى از آن مى‌دهيم، سپس جهنم را برايش مقرّر مى‌كنيم تا نكوهيده و مطرود در آن وارد شود'),
(2048, 17, 19, 'و هر كس آخرت خواهد و با سعى لازم براى آن بكوشد- در حالى كه مؤمن باشد- آنانند كه از سعيشان قدردانى خواهد شد'),
(2049, 17, 20, 'همه را، اين گروه و آن گروه را از عطاى پروردگارت مدد مى‌بخشيم، و عطاى پروردگار تو از كسى منع نشده است'),
(2050, 17, 21, 'بنگر كه چگونه بعضى‌شان را بر بعضى ديگر برترى داديم، و البته آخرت به لحاظ درجات، عظيم‌تر و از نظر برترى، بزرگ‌تر است'),
(2051, 17, 22, 'با خدا معبود ديگرى قرار مده كه نكوهيده و وانهاده فرو نشينى'),
(2052, 17, 23, 'و پروردگار تو مقرر داشته كه جز او را نپرستيد و با پدر و مادر احسان كنيد. اگر يكى از آن دو يا هر دو در كنار تو به پيرى رسند، به آنها اف مگو! و آنها را به خشونت مران و با آنها كريمانه سخن بگوى'),
(2053, 17, 24, 'و از سر مهربانى بال فروتنى بر آنان بگستر و بگو: پروردگارا! آنها را رحمت آور، چنان كه مرا در كودكى پرورش دادند'),
(2054, 17, 25, 'پروردگارتان بهتر مى‌داند كه در ضمير شما چيست. اگر اهل صلاح باشيد، قطعا او آمرزنده‌ى توبه كاران است'),
(2055, 17, 26, 'و حق خويشاوند و تنگدست و به راه مانده را بده و ولخرجى و اسراف مكن'),
(2056, 17, 27, 'چرا كه اسرافكاران برادران شياطينند، و شيطان در برابر خداى خويش كفر پيشه است'),
(2057, 17, 28, 'و اگر به اميد رحمت [و وسعت رزق‌] از جانب پروردگارت، از [انفاق‌] به آنها روى مى‌گردانى، پس با ايشان سخنى نرم گوى'),
(2058, 17, 29, 'نه دستت را [به خسّت‌] به گردن خويش ببند و نه [به سخاوت‌] يكباره بگشاى كه ملامت زده و حسرت خورده بمانى'),
(2059, 17, 30, 'بى‌ترديد، خداى تو روزى را براى هر كه خواهد گشايش دهد يا تنگ گيرد همانا او به بندگان خويش آگاه و بيناست'),
(2060, 17, 31, 'و فرزندانتان را از بيم تنگدستى نكشيد، ما آنها و شما را روزى مى‌دهيم. قطعا كشتن آنها خطايى بزرگ است'),
(2061, 17, 32, 'و به زنا نزديك مشويد به درستى كه آن فحشا و راهى بد است'),
(2062, 17, 33, 'و كسى را كه خداوند [كشتن وى را] حرام كرده نكشيد جز به حق، و هر كس به ستم كشته شود، به ولىّ او تسلطى داده‌ايم، پس او نبايد در [مكافات‌] قتل زياده‌روى كند، همانا او حمايت شده است'),
(2063, 17, 34, 'و به مال يتيم نزديك نشويد مگر به طريقى كه بهتر است، تا آن‌گاه كه به حدّ بلوغش برسد، و به پيمان وفا كنيد كه پيمان بازخواست شدنى است'),
(2064, 17, 35, 'و چون پيمانه كنيد پيمانه را تمام دهيد و با ترازوى درست وزن كنيد، كه اين بهتر و سر انجامش نيكوتر است'),
(2065, 17, 36, 'و چيزى را كه بدان علم ندارى پيروى مكن، همانا گوش و چشم و قلب، همه آنها مورد سؤال واقع خواهند شد'),
(2066, 17, 37, 'و در روى زمين به نخوت راه مرو، تو هرگز زمين را نخواهى شكافت و در بلندى به كوه‌ها نخواهى رسيد'),
(2067, 17, 38, 'همه اينها گناهش نزد پروردگارت ناخوشايند است'),
(2068, 17, 39, 'اينها از جمله حكمت‌هايى است كه پروردگارت به تو وحى كرده است، پس با خدا معبود ديگرى قرار مده كه ملامت زده و مطرود در جهنم افكنده مى‌شوى'),
(2069, 17, 40, 'آيا پروردگارتان پسران را خاص شما كرده و خود از فرشتگان دخترانى اختيار نموده است؟ حقّا كه شما سخنى بس بزرگ بر زبان مى‌آوريد'),
(2070, 17, 41, 'و به تحقيق ما در اين قرآن [حقايق را] گونه‌گون بيان كرديم تا پند گيرند، امّا آنها را جز رميدن نمى‌افزايد'),
(2071, 17, 42, 'بگو: اگر با او چنان كه مى‌گويند خدايان ديگرى بود، در آن صورت حتما به سوى صاحب عرش راهى مى‌جستند [تا قدرت را به دست گيرند]'),
(2072, 17, 43, 'او پاك و منزّه است و از آنچه مى‌گويند بسى برتر است'),
(2073, 17, 44, 'آسمان‌هاى هفتگانه و زمين و هر كس در آنهاست او را تسبيح مى‌گويند، و هيچ موجودى نيست مگر اين كه به ستايش او تسبيح مى‌گويد، ولى شما تسبيحشان را نمى‌فهميد. به راستى كه او بردبار و آمرزنده است'),
(2074, 17, 45, 'و چون قرآن بخوانى، ميان تو و كسانى كه به آخرت ايمان نمى‌آورند حجابى نامرئى قرار مى‌دهيم'),
(2075, 17, 46, 'و بر دل‌هايشان پرده‌ها نهاده‌ايم تا آن را نفهمند، و در گوش‌هايشان سنگينى. و هنگامى كه در قرآن پروردگار خود را به يگانگى ياد كنى با نفرت پشت مى‌كنند'),
(2076, 17, 47, 'هنگامى كه به تو گوش مى‌سپارند ما بهتر مى‌دانيم به چه منظور گوش مى‌دهند، و نيز آن‌گاه كه نجوا مى‌كنند وقتى كه ستمگران مى‌گويند: شما جز مردى افسون شده را پيروى نمى‌كنيد'),
(2077, 17, 48, 'ببين چگونه براى تو مثل‌ها زدند و گمراه شدند، در نتيجه راه به جايى نتوانند برد'),
(2078, 17, 49, 'و گفتند: آيا وقتى ما استخوان و خاك شديم، دگر بار به آفرينش تازه‌اى زنده خواهيم شد'),
(2079, 17, 50, 'بگو: سنگ باشيد يا آهن'),
(2080, 17, 51, 'يا هر مخلوقى از آنچه در خاطر شما بزرگ مى‌نمايد [بالاخره برانگيخته خواهيد شد]. باز خواهند گفت: چه كسى ما را باز مى‌گرداند؟ بگو: همان كس كه بار اول شما را آفريد. پس سرهايشان را به سويت تكان داده خواهند گفت: آن در چه زمانى خواهد بود؟ بگو: شايد نزديك باش'),
(2081, 17, 52, 'روزى كه شما را فرا مى‌خواند، پس با ستايش او اجابتش مى‌كنيد، و مى‌پنداريد كه جز اندكى [در دنيا] توقف نكرده‌ايد'),
(2082, 17, 53, 'و به بندگان من بگو سخنى را كه بهتر است بگويند، كه شيطان ميانشان را به هم مى‌زند، به راستى كه شيطان براى آدمى دشمنى آشكار است'),
(2083, 17, 54, 'پروردگارتان به شما داناتر است، اگر بخواهد رحمتتان مى‌كند يا اگر بخواهد عذابتان مى‌كند، و ما تو را بر آنان و كيل نفرستاده‌ايم'),
(2084, 17, 55, 'و پروردگار تو به [احوال‌] هر كس كه در آسمان‌ها و زمين است داناتر است. و به تحقيق بعضى از انبيا را بر بعضى برترى بخشيديم، و به داود زبور داديم'),
(2085, 17, 56, 'بگو: كسانى را كه به جاى او [معبود خود] پنداشته‌ايد بخوانيد، ولى آنها نمى‌توانند از شما دفع زيان كنند و نه تغيير دهند'),
(2086, 17, 57, 'آن كسانى را كه ايشان مى‌خوانند [مانند ملائكه و غيره‌] خود به سوى خدايشان وسيله مى‌جويند كه كدامشان به او نزديك‌ترند؟ و به رحمت خدا اميدوارند و از عذابش مى‌ترسند همانا عذاب پروردگارت حذر كردنى است'),
(2087, 17, 58, 'و هيچ شهرى نيست جز اين كه ما آن را [در صورت نافرمانى‌] پيش از روز قيامت به هلاكت مى‌رسانيم يا به عذابى سخت گرفتار مى‌سازيم. اين [عقوبت‌] در كتاب الهى به قلم رفته است'),
(2088, 17, 59, 'و [چيزى‌] ما را از فرستادن معجزات [درخواستى مشركان‌] باز نداشت جز اين كه پيشينيان، آن را دروغ پنداشتند و ما به قوم ثمود آن ماده شتر را به عنوان يك حجت روشن داديم ولى به آن ستم كردند، و ما معجزه‌ها را صرفا براى بيم دادن مى‌فرستيم'),
(2089, 17, 60, 'و آن‌گاه كه به تو گفتيم: به راستى پروردگارت بر مردم احاطه دارد، و خوابى را كه به تو نمايانديم و نيز آن درخت نفرين شده در قرآن را صرفا براى امتحان مردم قرار داديم، و ما آنها را مى‌ترسانيم اما آنها را جز سركشى بزرگ نمى‌افزايد'),
(2090, 17, 61, 'و هنگامى كه به فرشتگان گفتيم: براى آدم سجده كنيد. پس همه سجده كردند، جز ابليس كه گفت: آيا كسى را كه از گل آفريده‌اى سجده كنم'),
(2091, 17, 62, 'گفت: به من بگو، آيا اين همان است كه بر من برترى دادى؟ اگر تا روز قيامت مهلتم دهى، قطعا نسل او را افسار مى‌زنم جز اندكى را'),
(2092, 17, 63, 'گفت: برو، هر كس از آنان تو را پيروى كند، مسلما جهنم سزايتان خواهد بود كه سزايى تمام است'),
(2093, 17, 64, 'و از ايشان هر كه را توانستى به آواى خود تحريك كن و با سواران و پيادگانت بر آنها بتاز، و با آنها در اموال و اولاد شريك شو، و به ايشان وعده بده، ولى شيطان جز از روى فريب به آنها وعده نمى‌دهد'),
(2094, 17, 65, 'به يقين، تو را بر بندگان [صالح‌] من تسلطى نيست، و كفالت پروردگارت بس است'),
(2095, 17, 66, 'پروردگار شما كسى است كه كشتى را در دريا براى شما به حركت در مى‌آورد تا از فضل او روزى بجوييد همانا او همواره به شما مهربان است'),
(2096, 17, 67, 'و چون در دريا به شما خطرى برسد، آن كه را مى‌خوانيد گم مى‌شود جز او، و چون خدا شما را به سوى خشكى نجات داد روى مى‌گردانيد و انسان بسيار ناسپاس است'),
(2097, 17, 68, 'مگر ايمن شده‌ايد از اين كه شما را در كنار خشكى به زمين فرود برد يا بر شما طوفانى از سنگريزه‌ها بفرستد كه براى خود مدافعى نيابيد'),
(2098, 17, 69, 'يا مگر ايمن شده‌ايد از اين كه بار ديگر شما را به دريا بازگرداند و تندبادى شكننده بر شما بفرستد و به سزاى كفرتان شما را غرق كند، آن‌گاه براى نجات خود در برابر ما پى‌گيرى كننده نيابيد'),
(2099, 17, 70, 'و به راستى ما فرزندان آدم را گرامى داشتيم و آنها را در خشكى و دريا سير داديم و از نعمت‌هاى پاكيزه روزى بخشيديم و آنها را بر بسيارى از مخلوقات خود برترى داديم، برترى كامل'),
(2100, 17, 71, 'روزى كه هر گروهى را با امامشان فرا مى‌خوانيم، پس هر كه نامه او به دست راستش داده شود آنها نامه‌ى خود را مى‌خوانند و ذرّه‌اى به آنها ستم نمى‌رود'),
(2101, 17, 72, 'و هر كه در اين دنيا كور [دل‌] باشد در آخرت هم كور و گمراه‌تر خواهد بود'),
(2102, 17, 73, 'و همانا نزديك بود در آنچه به تو وحى كرده‌ايم تو را متزلزل كنند كه غير آن را به ما منسوب دارى كه در آن صورت تو را به دوستى مى‌گرفتند'),
(2103, 17, 74, 'و اگر استوارت نكرده بوديم همانا نزديك بود كمى به سوى آنها متمايل شوى'),
(2104, 17, 75, 'در آن صورت حتما تو را دو چندان [در اين‌] زندگى و دو چندان را [بعد از] مرگ عذاب مى‌چشانديم، آن‌گاه در برابر ما براى خود ياورى نمى‌يافتى'),
(2105, 17, 76, 'و همانا نزديك بود تو را از اين سرزمين بركنند تا تو را از آن بيرون برانند، و هرگاه چنين مى‌كردند آنها پس از تو جز اندك زمانى باقى نمى‌ماندند'),
(2106, 17, 77, '[مانند] سنت [ما در حمايت‌] پيامبرانى كه پيش از تو فرستاديم، و براى سنت و قانون ما تغييرى نخواهى يافت'),
(2107, 17, 78, 'نماز را از زوال آفتاب تا نهايت تاريكى شب برپادار، و نيز نماز صبح را، كه نماز صبح مشهود [فرشتگان‌] است'),
(2108, 17, 79, 'و پاسى از شب را زنده بدار كه نافله‌اى مخصوص تو است، باشد كه پروردگارت تو را به مقامى ستوده برساند'),
(2109, 17, 80, 'و بگو: پروردگارا! مرا در هر كارى به درستى وارد كن و به درستى خارج ساز، و از جانب خود برايم حجتى يارى بخش پديد آور'),
(2110, 17, 81, 'و بگو: حق آمد و باطل نابود شد. بى‌شك باطل نابود شدنى است'),
(2111, 17, 82, 'و از قرآن آنچه را براى مؤمنان شفا و رحمت است نازل مى‌كنيم، ولى ستمگران را جز زيان نمى‌افزايد'),
(2112, 17, 83, 'و چون به انسان نعمت ارزانى داريم روى مى‌گرداند و دورى مى‌گزيند، و چون به وى بدى رسد نوميد مى‌گردد'),
(2113, 17, 84, 'بگو: هر كس بر حسب ساختار روحى خود عمل مى‌كند، و پروردگار شما بهتر مى‌داند چه كسى راه يافته‌تر است'),
(2114, 17, 85, 'و از تو در باره‌ى روح مى‌پرسند، بگو: روح از [عالم بالا و] فرمان خداى من است و شما از علم جز اندكى داده نشده‌ايد'),
(2115, 17, 86, 'و اگر مى‌خواستيم، قطعا آنچه را به تو وحى كرده‌ايم مى‌برديم، آن‌گاه در باره‌ى آن براى خويش در برابر ما مدافعى نمى‌يافتى'),
(2116, 17, 87, 'مگر به رحمتى از جانب پروردگارت كه تفضل او در حق تو بزرگ است'),
(2117, 17, 88, 'بگو: اگر همه انس و جن فراهم آيند كه نظير اين قرآن را بياورند نمى‌توانند مانند آن را بياورند، هر چند آنها پشتيبان يكديگر باشند'),
(2118, 17, 89, 'بى‌شك ما در اين قرآن براى مردم از هر گونه مثل و نمونه آورديم اما بيشتر مردم ابا كرده، جز كفر و انكار [اظهار] نداشتند'),
(2119, 17, 90, 'و گفتند: ما هرگز به تو ايمان نخواهيم آورد تا آن كه از زمين چشمه‌اى براى ما بجوشانى'),
(2120, 17, 91, 'يا باغى از خرما و انگور داشته باشى كه از لابه‌لاى آن به فراوانى نهرها جارى سازى'),
(2121, 17, 92, 'يا چنان كه ادعا مى‌كنى آسمان را پاره پاره بر سر ما بيفكنى يا خدا و فرشتگان را در برابر ما حاضر آورى'),
(2122, 17, 93, 'يا براى تو خانه‌اى زرّين باشد يا به آسمان بالا روى، و البته بالا رفتن تو را هم باور نخواهيم كرد مگر اين كه بر ما نوشته‌اى نازل كنى كه آن را بخوانيم. بگو: پاك است پروردگار من! آيا من جز بشرى فرستاده [شده‌] هستم'),
(2123, 17, 94, 'و مانع ايمان آوردن مردم- هنگامى كه هدايت سويشان آمد- جز اين نبود كه گفتند: آيا خداوند بشرى را به رسالت مبعوث كرده است؟'),
(2124, 17, 95, 'بگو: اگر در روى زمين فرشتگانى بودند كه آسوده خاطر راه مى‌رفتند، البته بر آنها نيز از آسمان فرشته‌اى را به عنوان پيامبر مى‌فرستاديم'),
(2125, 17, 96, 'بگو: همين بس كه خداوند ميان من و شما گواه است، همانا او به حال بندگانش آگاه و بيناست'),
(2126, 17, 97, 'و هر كه را خدا هدايت كند رهيافته اوست، و هر كه را گمراه كند جز خدا برايشان سرپرستى نخواهى يافت، و روز قيامت آنها را به رو افتاده و چشم بسته و لال و كر محشور مى‌كنيم. جايگاهشان دوزخ است كه هر دم آتش آن فرو نشيند، شراره‌اى تازه برايشان مى‌افزاييم'),
(2127, 17, 98, 'سزايشان همين است، چرا كه آنها آيات ما را انكار كردند و گفتند: آيا وقتى ما استخوان و خاك شديم باز در آفرينشى جديد زنده خواهيم شد'),
(2128, 17, 99, 'آيا ندانسته‌اند خدايى كه آسمان‌ها و زمين را آفريده قادر است همانند آنها را بيافريند و براى آنها اجلى معين كرده است كه شكى در آن نيست؟ ولى ستمگران، سر باز زده، جز كفر و انكار [نخواستند]'),
(2129, 17, 100, 'بگو: اگر شما مالك گنج‌هاى رحمت پروردگار من بوديد، در آن صورت از بيم هزينه كردن قطعا امساك مى‌كرديد، و آدمى همواره بخيل است'),
(2130, 17, 101, 'و همانا ما به موسى نه معجزه‌ى آشكار داديم- پس از بنى اسرائيل بپرس- آن‌گاه كه موسى نزد آنها آمد و فرعون به او گفت: اى موسى! من جدّا تو را جادو شده مى‌پندارم'),
(2131, 17, 102, 'گفت: تو نيك مى‌دانى كه اين آيات را كه مايه بينش‌هاست جز پروردگار آسمان‌ها و زمين نازل نكرده است، و راستى اى فرعون! من تو را هلاك شده مى‌پندارم'),
(2132, 17, 103, 'پس فرعون تصميم گرفت تا آنها را از سرزمين مصر بيرون كند و ما، او و هر كه را با وى بود همه را غرق كرديم'),
(2133, 17, 104, 'و پس از او به بنى اسرائيل گفتيم: در اين سرزمين ساكن شويد، پس وقتى وعده‌ى آخرت فرا رسيد همه شما را با هم مى‌آوريم'),
(2134, 17, 105, 'و آن [قرآن‌] را به حق نازل كرديم و به حق هم نازل شد، و تو را جز بشارت دهنده و بيم رسان نفرستاديم'),
(2135, 17, 106, 'و قرآنى كه بخش بخش بر تو نازل كرديم تا آن را بر مردم آرام آرام بخوانى و آن را به تدريج نازل كرديم'),
(2136, 17, 107, 'بگو: خواه به آن ايمان بياوريد خواه نياوريد، بى‌گمان كسانى كه پيش از آن علم داده شده‌اند هنگامى كه اين كتاب بر آنها تلاوت شود سجده‌كنان بر چانه‌ها به خاك افتند'),
(2137, 17, 108, 'و مى‌گويند: منزه است پروردگار ما، كه وعده‌ى پروردگارمان قطعا انجام شدنى است'),
(2138, 17, 109, 'و گريه كنان بر چانه‌ها به خاك افتند و خشوعشان افزون شود'),
(2139, 17, 110, 'بگو: اللّه را بخوانيد يا رحمان را بخوانيد، هر كدام را خوانديد بهترين نام‌ها براى اوست، و نمازت را زياد بلند يا خيلى آهسته نخوان و ميان آن دو راهى معتدل برگير'),
(2140, 17, 111, 'و بگو: ستايش خداى را كه نه فرزندى گرفته و نه در فرمانروايى شريكى دارد و نه از زبونى او را ياورى بوده، و او را به بزرگداشتى تمام بزرگ دار');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ستايش خداى را كه اين كتاب [آسمانى‌] را بر بنده‌ى خود نازل كرد و هيچ گونه انحرافى در آن ننهاد'),
(2142, 18, 2, '[كتابى‌] راست و درست، تا به عقوبتى سخت از نزد خود هشدار دهد و مؤمنان را كه كارهاى شايسته مى‌كنند نويد بخشد كه ايشان را پاداشى نيكوست'),
(2143, 18, 3, 'كه جاودانه در آن خواهند بود'),
(2144, 18, 4, 'و تا كسانى را كه گفتند: خداوند فرزندى گرفته است، هشدار دهد'),
(2145, 18, 5, 'نه آنان و نه پدرانشان به اين [گفتار] دانشى ندارند. سخن بزرگى است كه از دهانشان بيرون مى‌آيد. [و] جز دروغ نمى‌گويند'),
(2146, 18, 6, 'چه بسا تو از پى [اعراض‌] آنها، اگر به اين گفتار ايمان نياورند، خود را از اندوه هلاك كنى'),
(2147, 18, 7, 'همانا ما آنچه را كه بر زمين است زينتى براى آن قرار داديم تا امتحانشان كنيم كه كدام يك از ايشان بهتر عمل مى‌كنند'),
(2148, 18, 8, 'و ما [سر انجام‌] آنچه را كه بر روى زمين است قطعا بيابانى خشك و باير خواهيم كرد'),
(2149, 18, 9, 'آيا پنداشته‌اى كه اصحاب كهف و سنگ نوشته از آيات عجيب ما بودند'),
(2150, 18, 10, 'آن‌گاه كه آن جوانان به سوى غار پناه بردند و گفتند: پروردگارا! ما را از جانب خود رحمتى عطا كن و در كارمان براى ما رشد و تعالى فراهم ساز'),
(2151, 18, 11, 'پس در آن غار ساليانى چند بر گوش‌هايشان [پرده‌ى خواب‌] زديم'),
(2152, 18, 12, 'آن‌گاه آنها را برانگيختيم تا معلوم داريم كدام يك از آن دو گروه مدت ماندنشان را بهتر حساب مى‌كنند'),
(2153, 18, 13, 'ما خبرشان را به حق بر تو حكايت مى‌كنيم. آنها جوانانى بودند كه به پروردگار خود ايمان آوردند و ما نيز بر هدايتشان افزوديم'),
(2154, 18, 14, 'و بر دل‌هايشان قوت بخشيديم آن‌گاه كه [در مقابل مشركان‌] قيام كردند و گفتند: پروردگار ما پروردگار آسمان‌ها و زمين است، غير او هرگز معبودى را نخواهيم خواند كه در اين صورت حتما ناحق گفته‌ايم'),
(2155, 18, 15, 'اينان قوم ما هستند كه غير او معبودانى اختيار كرده‌اند، چرا بر [الوهيت‌] آنها دليل روشنى نمى‌آورند؟ پس كيست ظالم‌تر از كسى كه بر خدا دروغ ببندد'),
(2156, 18, 16, 'و چون از آنها و از آنچه جز خدا مى‌پرستند كناره گرفتيد، پس به غار پناه جوييد تا پروردگارتان از رحمت خود بر شما بگسترد و براى شما در كارتان گشايشى فراهم سازد'),
(2157, 18, 17, 'و خورشيد را مى‌بينى كه وقتى طلوع مى‌كند از غارشان به سمت راست ميل مى‌كند، و چون غروب مى‌كند از سمت چپشان مى‌گذرد و آنها در جايى فراخ از آن غار بودند. اين از نشانه‌هاى قدرت خداست. هر كه را خدا هدايت كند، او هدايت يافته است و هر كه را گمراه كند هرگز بر'),
(2158, 18, 18, 'و آنان را بيدار مى‌پنداشتى، در حالى كه خفته بودند. و آنها را به پهلوى راست و چپ مى‌گردانديم، و سگشان بر آستانه غار دو دست خود را گشاده بود. اگر بر آنها مشرف مى‌شدى، [و از نزديك مى‌ديدى‌]، بى‌ترديد به آنها پشت كرده مى‌گريختى و وجودت از بيمشان آكنده مى'),
(2159, 18, 19, 'و اين چنين آنان را برانگيختم تا ميان خود از يكديگر پرستش كنند. يكى از آنها گفت: چه مدت [اين جا] مانده‌ايد؟ گفتند: روزى يا مقدارى از روز مانده‌ايم. [سر انجام‌] گفتند: پروردگارتان بهتر مى‌داند چه مدت مانده‌ايد. اينك يكى از خودتان را با اين پولتان به شه'),
(2160, 18, 20, 'چرا كه آنها اگر بر شما دست يابند سنگسارتان مى‌كنند يا شما را به كيش خود باز مى‌گردانند و در آن صورت هرگز رستگار نخواهيد شد'),
(2161, 18, 21, 'و بدين‌سان [اهل شهر را] بر آنها واقف كرديم تا بدانند كه وعده‌ى خدا راست است و در [وقوع‌] قيامت ترديدى نيست و اين هنگامى بود كه [مردم‌] در ميان خود در باره‌ى امر [معاد] مناقشه داشتند پس [مشركان‌] گفتند: بر آنها بنايى بسازيد [تا كسى از آنها آگاه نشود]'),
(2162, 18, 22, 'به زودى خواهند گفت: سه تن بودند و چهارمى‌شان سگشان بود، و مى‌گويند: پنج تن بودند و ششمى‌شان سگشان- تيرى به ناپيدا [مى‌زنند]- و مى‌گويند: هفت تن بودند و هشتمى‌شان سگشان بود. بگو: پروردگارم به وعده آنها آگاه‌تر است و جز شمار اندكى تعداد آنها را نمى‌دان'),
(2163, 18, 23, 'و هيچ وقت در باره‌ى كارى مگوى كه فردا چنين مى‌كنم'),
(2164, 18, 24, 'مگر آن كه [بگويى:] اگر خدا خواهد. و وقتى فراموش كردى، پروردگار خويش را ياد كن و بگو: اميد است خدايم مرا به چيزى كه به صواب نزديك‌تر از اين باشد، هدايت كند'),
(2165, 18, 25, 'و سيصد سال در غارشان درنگ كردند و نه سال نيز افزودند'),
(2166, 18, 26, 'بگو: خدا به مدتى كه درنگ كردند آگاه‌تر است. غيب آسمان‌ها و زمين خاص اوست. راستى، چه بينا و شنواست! آنان را جز او سرپرستى نيست، و او در حكم خود كسى را شريك نمى‌سازد'),
(2167, 18, 27, 'و از كتاب پروردگارت آنچه را كه به تو وحى شده است تلاوت كن. كلمات او را تغيير دهنده‌اى نيست، و هرگز جز او پشت و پناهى نخواهى يافت'),
(2168, 18, 28, 'و با كسانى كه پروردگارشان را صبح و شام مى‌خوانند و خشنودى او را مى‌جويند شكيبايى كن، و براى تجمل زندگى دنيا چشم از ايشان بر مگير، و از آن كس كه قلبش را از ياد خودمان غافل ساخته‌ايم و از پى هواى نفس خود مى‌رود و اساس كارش بر تجاوز است اطاعت مكن'),
(2169, 18, 29, 'و بگو: [سخن‌] حق از پروردگار شماست، پس هر كه خواهد ايمان بياورد و هر كه خواهد كافر شود، كه ما براى ستمگران آتشى آماده كرده‌ايم كه سراپرده‌هاى آن ايشان را احاطه كند و اگر فريادرسى خواهند، به آبى چون مس گداخته كه چهره‌ها را بريان مى‌كند فريادرسى شوند،'),
(2170, 18, 30, 'بى‌ترديد، كسانى كه ايمان آوردند و كارهاى شايسته كردند، ما پاداش كسى را كه نيكوكارى كرده است ضايع نمى‌كنيم'),
(2171, 18, 31, 'آنانند كه بهشت‌هاى ابدى مخصوص ايشان است كه از زير [قصرها] شان نهرها جارى است. در آن جا با دستبندهاى زرّين آراسته مى‌شوند و جامه‌هاى سبز از حرير نازك و حرير ضخيم مى‌پوشند، در حالى كه بر تخت‌ها تكيه زده‌اند. خوش پاداش و نيكو استراحتگاهى است'),
(2172, 18, 32, 'و براى آنها [داستان‌] دو مردى را مثل بزن كه به يكى از آنها دو باغ انگور داديم و پيرامون آنها را با درختان خرما پوشانديم و ميان آنها كشتزارى پديد آورديم'),
(2173, 18, 33, 'هر دو باغ محصول خود را بى‌هيچ كم و كاست مى‌داد و ميان آن دو نهرى جارى ساختيم'),
(2174, 18, 34, 'و او را محصولات [گوناگون‌] بود. پس به دوست خود كه با او گفت و گو مى‌كرد گفت: من به مال از تو بيشتر و به افراد از تو نيرومندترم'),
(2175, 18, 35, 'و او در حالى كه ستمكار به نفس خويش بود [مغرورانه‌] به باغ خود در آمد و گفت: گمان ندارم كه اين [باغ‌] هرگز زوال پذيرد'),
(2176, 18, 36, 'و گمان ندارم كه قيامت برپا شود، و اگر هم به نزد پروردگارم بازگردانده شوم، قطعا جايگاهى بهتر از اين خواهم يافت'),
(2177, 18, 37, 'رفيقش كه با او گفت و گو مى‌كرد به وى گفت: آيا به كسى كه تو را از خاك و سپس از نطفه آفريد، آن‌گاه تو را به صورت مردى بياراست كافر شدى؟'),
(2178, 18, 38, 'امّا من [مى‌گويم‌]: اللّه پروردگار من است و هيچ كس را شريك پروردگار خويش نمى‌سازم'),
(2179, 18, 39, 'و چرا وقتى داخل باغت شدى نگفتى: هر چه خدا خواهد [همان شود] و هيچ نيرويى جز به تأييد خدا نيست؟ اگر مرا به مال و فرزند كمتر از خود مى‌بينى'),
(2180, 18, 40, 'اميد است كه پروردگارم بهتر از باغ تو را به من دهد، و بر آن باغ آفتى از آسمان بفرستد تا به زمينى باير و بى‌گياه بدل شود'),
(2181, 18, 41, 'يا آب آن در زمين فرو رود كه هرگز نتوانى آن را به دست آورى'),
(2182, 18, 42, 'و [سر انجام‌] ميوه‌هايش نابود شد. آن‌گاه براى آنچه در باغ هزينه كرده بود، دست حسرت بر دست مى‌ساييد در حالى كه همه داربست‌ها و در و ديوار آن فرو ريخته بود و [به حسرت‌] مى‌گفت: اى كاش كسى را شريك پروردگار خود نساخته بودم'),
(2183, 18, 43, 'و گروهى نداشت كه او را در برابر خدا يارى دهند و خود نيز قدرت دفاع نداشت'),
(2184, 18, 44, 'آن جا ثابت شد كه حاكميت از آن خداى حق است. او در پاداش دادن بهتر و در فرجام نيكوتر است'),
(2185, 18, 45, 'و برايشان زندگى دنيا را مثل بزن كه مانند آبى است كه آن را از آسمان فرستاديم، پس گياه زمين با آن در آميخت [و سرسبز شد] و آن‌گاه خشكيد كه بادها آن را به هر سو مى‌پراكند، و خدا بر هر چيزى قادر است'),
(2186, 18, 46, 'مال و فرزندان زينت زندگى دنيايند، و نيكى‌هاى ماندگار، ثوابش نزد پروردگار تو بهتر و اميد بخش‌تر است'),
(2187, 18, 47, 'و روزى كه كوه‌ها را به حركت در آوريم، و زمين را صاف و هموار بينى، و آنان را گرد آوريم و هيچ يك از آنها را فروگذار نكنيم'),
(2188, 18, 48, 'و همه در يك صف به [پيشگاه‌] پروردگارت عرضه شوند [و به آنها گفته شود:] اكنون نزد ما [تنها] آمده‌ايد همان گونه كه اول بار شما را آفريديم، ولى گمان مى‌كرديد كه هرگز براى شما موعد [ديدار] نخواهيم نهاد'),
(2189, 18, 49, 'و كارنامه‌ها را در ميان گذارند، آن‌گاه مجرمان را از آنچه در آن است هراسان بينى، و گويند: اى واى بر ما! اين چه كارنامه‌اى است كه هيچ [كار] كوچك و بزرگى را وانگذاشته مگر اين كه آن را برشمرده است! و آنان كرده‌هاى خويش را آماده مى‌يابند، و پروردگار تو ب'),
(2190, 18, 50, 'و آن‌گاه كه به فرشتگان گفتيم: براى آدم سجده كنيد. پس همه سجده كردند، جز ابليس كه از جنيان بود و از فرمان پروردگارش سر بتافت. آيا با اين حال او و ذريه‌اش را به جاى من اولياى خود مى‌گيريد و حال آن كه آنها دشمن شمايند؟ ستمكاران [شيطان را] بد جانشينى [ب'),
(2191, 18, 51, 'آنها را نه در آفرينش آسمان‌ها و زمين شاهد ساختم و نه در آفرينش خودشان، و من آن نيستم كه گمراه كنندگان را دستيار خود گيرم'),
(2192, 18, 52, 'و روزى كه خدا فرمايد: آنهايى را كه مى‌پنداشتيد شريكان منند [به يارى خود] ندا دهيد. پس آنها را ندا دهند ولى اجابتشان نكنند، و ما ميان آنها كانون مهلكه‌اى قرار داده‌ايم [كه از هم جدا افتند]'),
(2193, 18, 53, 'و گناهكاران آتش دوزخ را مى‌بينند و در مى‌يابند كه در آن افتادنى هستند، و از آن راه گريزى نمى‌يابند'),
(2194, 18, 54, 'و به راستى در اين قرآن براى مردم از هر گونه مثلى آورديم، ولى انسان بيش از هر چيزى، جدال كننده است'),
(2195, 18, 55, 'و چيزى مانع مردم نشد از اين كه وقتى هدايت به سويشان آمد ايمان بياورند و از پروردگارشان آمرزش بخواهند جز اين كه همان بلاها بر سرشان بيايد كه بر پيشينيان آمد يا عذاب رويارويشان درآيد [تا به خود آيند]'),
(2196, 18, 56, 'و ما پيامبران را جز مژده دهندگان و بيم‌رسانان نمى‌فرستيم. و كسانى كه كافر شدند به باطل مجادله مى‌كنند تا به وسيله آن حق را پايمال كنند. [آنها] آيات من و آنچه را بدان بيم داده شده‌اند به ريشخند گرفتند'),
(2197, 18, 57, 'كيست ظالم‌تر از كسى كه آيات پروردگارش به وى تذكر داده شود و او اعراض كند و آنچه را كه پيش فرستاده از ياد ببرد؟ ما بر دل‌هاى آنان پوشش‌هايى [از قساوت‌] قرار داديم كه آن (قرآن) را نفهمند و در گوش‌هايشان سنگينى نهاديم. و اگر به سوى هدايت دعوتشان كنى، د'),
(2198, 18, 58, 'و پروردگار تو آمرزنده و صاحب رحمت است. اگر مردم را به عملشان مى‌گرفت، حتما عذاب را زودتر به آنها مى‌رسانيد، ولى براى آنها موعدى است كه هرگز از آن گريزى نمى‌يابند'),
(2199, 18, 59, 'و مردم آن شهرها را چون بيدادگرى كردند هلاك كرديم و براى هلاكتشان موعدى نهاديم'),
(2200, 18, 60, 'و هنگامى كه موسى به جوان [همراه‌] خود گفت: همچنان خواهم رفت تا به محل تلاقى دو دريا برسم، هر چند مدتى طولانى به راه خود ادامه دهم'),
(2201, 18, 61, 'پس چون به محل تلاقى آن دو دريا رسيدند، ماهى خود را [كه براى تغذيه بود] فراموش كردند، پس [ماهى‌] راه خود را در دريا پيش گرفت'),
(2202, 18, 62, 'و چون از آن جا گذشتند، موسى به جوان همراه خود گفت: غذايمان را بياور كه راستى ما از اين سفر خستگى بسيار ديديم'),
(2203, 18, 63, 'گفت: آيا به ياد دارى؟ وقتى كنار آن صخره جا گرفته بوديم، من [به آب افتادن‌] ماهى را فراموش كردم [كه با تو بگويم‌] و جز شيطان فراموشم نساخت از اين كه يادآور آن شوم، و ماهى به طور عجيبى راه خود را در دريا پيش گرفت'),
(2204, 18, 64, 'گفت: اين همان [جايگاه خضر] است كه به دنبالش بوديم، آن‌گاه پى‌جويانه ردّ پاى خود را گرفتند و برگشتند'),
(2205, 18, 65, 'تا اين كه [خضر] بنده‌اى از بندگان ما را يافتند كه از جانب خود به او رحمتى عطا كرده و از نزد خود به او دانشى آموخته بوديم'),
(2206, 18, 66, 'موسى به او گفت: آيا از پى تو بيايم تا از حكمتى كه تعليم شده‌اى به من بياموزى'),
(2207, 18, 67, 'گفت: تو هرگز نمى‌توانى همراه من شكيبايى كنى'),
(2208, 18, 68, 'و چگونه مى‌توانى امرى را كه به اسرارش احاطه ندارى تحمّل كنى'),
(2209, 18, 69, 'گفت: اگر خدا خواهد شكيبا خواهى يافت و در هيچ كارى تو را نافرمانى نخواهم كرد'),
(2210, 18, 70, 'گفت: پس اگر از پى من آمدى، از چيزى سؤال مكن تا خود از آن با تو سخن آغاز كنم'),
(2211, 18, 71, 'پس به راه افتادند، تا وقتى كه سوار كشتى شدند [خضر] آن را سوراخ كرد. موسى گفت: آيا كشتى را سوراخ كردى تا سرنشينانش را غرق كنى؟ واقعا به كار ناروايى مبادرت كردى'),
(2212, 18, 72, 'گفت: آيا نگفتم كه تو هرگز نمى‌توانى همراه من شكيبايى كنى'),
(2213, 18, 73, 'موسى گفت: به سبب آنچه فراموش كردم مرا مؤاخذه مكن و در كارم بر من سخت مگير'),
(2214, 18, 74, 'پس برفتند تا به نوجوانى برخوردند، پس [خضر] او را كشت. موسى گفت: آيا بى‌گناهى را بى‌آن كه كسى را كشته باشد كشتى؟ واقعا كار منكرى مرتكب شدى'),
(2215, 18, 75, 'گفت: آيا به تو نگفتم كه هرگز نمى‌توانى همراه من شكيبايى كنى'),
(2216, 18, 76, 'موسى گفت: اگر از اين پس چيزى از تو پرسيدم، ديگر با من همراهى نكن و از جانب من البته معذور خواهى بود'),
(2217, 18, 77, 'پس راه افتادند، تا وقتى كه به مردم قريه‌اى رسيدند، از اهل آن غذا خواستند، ولى آنها از مهمان كردنشان ابا كردند. پس در آن جا ديوارى يافتند كه مى‌خواست فرو ريزد. [خضر] آن را به پا داشت. موسى گفت: اگر مى‌خواستى، براى اين كار مزدى مى‌گرفتى'),
(2218, 18, 78, 'گفت: اين [زمان‌] جدايى ميان من و توست. هم اينك تو را از باطن آنچه كه نتوانستى بر آن صبر كنى خبردار مى‌كنم'),
(2219, 18, 79, 'اما آن كشتى، مال بينوايانى بود كه [با آن‌] در دريا كار مى‌كردند، پس خواستم آن را معيوب كنم، چون پشت سرشان پادشاهى بود كه هر كشتى [سالمى‌] را به زور مى‌گرفت'),
(2220, 18, 80, 'و اما آن نوجوان، پدر و مادرش هر دو مؤمن بودند، پس ترسيديم مبادا آن دو را به طغيان و كفر بكشاند'),
(2221, 18, 81, 'پس خواستيم كه پروردگارشان آن دو را به پاك‌تر و مهربان‌تر از او عوض دهد'),
(2222, 18, 82, 'و اما آن ديوار از آن دو پسر بچه يتيم در آن شهر بود، و زير آن گنجى براى آن دو وجود داشت، و پدرشان مردى شايسته بود، پس پروردگار تو خواست آن دو يتيم به حدّ رشد برسند و گنج خويش را بيرون آورند، كه رحمتى بود از پروردگار تو. و اين كارها را از پيش خود نكردم'),
(2223, 18, 83, 'و از تو در باره‌ى ذو القرنين مى‌پرسند. بگو: به زودى خبرى از او براى شما خواهم خواند'),
(2224, 18, 84, 'ما وى را در زمين تمكن داديم و از هر چيزى وسيله‌اى به او عطا كرديم'),
(2225, 18, 85, 'پس وسيله‌اى را پى گرفت'),
(2226, 18, 86, 'تا آن‌گاه كه به غروبگاه خورشيد رسيد، به نظرش آمد كه خورشيد در چشمه‌اى گل‌آلود و سياه غروب مى‌كند، و نزديك آن قومى را يافت. گفتيم: اى ذو القرنين! يا [اينان را] عقوبت مى‌كنى يا ميانشان [روش‌] نيكويى پيش مى‌گيرى [اختيار با توست‌]'),
(2227, 18, 87, 'گفت: اما هر كه ستم كند به زودى عذابش خواهيم كرد، سپس به سوى پروردگارش بازگردانده مى‌شود، آن‌گاه او را عذابى سخت خواهد كرد'),
(2228, 18, 88, 'و اما هر كه ايمان بياورد و كار شايسته كند پاداشى هر چه نيكوتر خواهد داشت و به او از فرمان خود تكليفى آسان خواهيم گفت'),
(2229, 18, 89, 'سپس وسيله‌اى ديگر را پى گرفت'),
(2230, 18, 90, 'تا آن‌گاه كه به طلوعگاه خورشيد رسيد، آن را ديد بر قومى طلوع مى‌كند كه براى آنها در برابر آن پوششى قرار نداده بوديم [و هيچ گونه سايبانى نداشتند]'),
(2231, 18, 91, 'اين گونه بود و ما به آنچه نزد وى [از امكانات‌] بود احاطه داشتيم'),
(2232, 18, 92, 'باز وسيله‌اى را پى گرفت'),
(2233, 18, 93, 'تا وقتى كه به ميان دو سدّ (دو كوه) رسيد، در كنار آن قومى را يافت كه هيچ سخنى را نمى‌فهميدند'),
(2234, 18, 94, 'گفتند: اى ذو القرنين! يأجوج و مأجوج سخت در زمين فساد مى‌كنند، آيا ممكن است براى تو هزينه‌اى قرار دهيم تا ميان ما و آنها سدّى بسازى'),
(2235, 18, 95, 'گفت: تمكّنى كه پروردگارم به من داده بهتر است پس مرا به نيروى خويش كمك دهيد تا ميان شما و آنها سدّ [محكمى‌] بسازم'),
(2236, 18, 96, 'پاره‌هاى آهن براى من بياوريد. تا آن‌گاه كه ميان دو كوه را [انباشت و] برابر نمود، گفت: [در آن‌] بدميد. تا آن وقت كه آن را سرخ و گداخته كرد، گفت: حالا مس مذاب برايم بياوريد تا روى آن بريزم'),
(2237, 18, 97, 'پس نتوانستند از آن سدّ بالا روند و نتوانستند آن را سوراخ كنند'),
(2238, 18, 98, 'گفت: اين [سدّسازى‌] رحمتى از جانب پروردگار من است، ولى چون وعده‌ى پروردگارم فرا رسد آن [سدّ] را در هم كوبد، و وعده‌ى پروردگار من حق است'),
(2239, 18, 99, 'و در آن روز آنها را چنان رها كنيم كه در يكديگر موج زنند، و همين كه در صور دميده شد آنها را چنان كه بايد جمع مى‌كنيم'),
(2240, 18, 100, 'و آن روز جهنم را چنان كه بايد بر كافران عرضه مى‌كنيم'),
(2241, 18, 101, 'همان كسانى كه چشمانشان از ياد من در پرده بود و توانايى شنيدن [حق را] نداشتند'),
(2242, 18, 102, 'آيا كسانى كه كافر شدند پنداشته‌اند كه مى‌توانند به جاى من بندگانم را سرپرست بگيرند؟ ما جهنم را براى پذيرايى كافران آماده كرده‌ايم'),
(2243, 18, 103, 'بگو: آيا شما را از آنها كه در كردار زيانكارترند آگاه سازيم'),
(2244, 18, 104, 'كسانى هستند كه سعيشان در زندگى دنيا هدر رفته و با اين حال مى‌پندارند كه كار نيك انجام مى‌دهند'),
(2245, 18, 105, 'آنان كسانى‌اند كه آيات پروردگارشان و لقاى او را انكار كردند، در نتيجه اعمالشان تباه گرديد و روز قيامت براى آنها ميزانى برپا نمى‌كنيم'),
(2246, 18, 106, 'اين است سزايشان كه همان جهنم است، چرا كه كافر شدند و آيات من و پيامبرانم را به ريشخند گرفتند'),
(2247, 18, 107, 'بى‌گمان كسانى كه ايمان آورده و كارهاى شايسته كردند باغ‌هاى فردوس جاى پذيرايى آنهاست'),
(2248, 18, 108, 'در آن جاودانه باشند و از آن جا انتقال نطلبند'),
(2249, 18, 109, 'بگو: اگر دريا براى [ثبت‌] كلمات پروردگارم مركّب مى‌گشت، بى‌شك دريا تمام مى‌شد پيش از آن كه كلمات پروردگارم تمام شود، هر چند درياى ديگرى نيز كمك بياوريم'),
(2250, 18, 110, 'بگو: جز اين نيست كه من بشرى مثل شما هستم [با اين تفاوت‌] كه به من وحى مى‌شود كه خداى شما خدايى يگانه است. پس هر كس به لقاى پروردگارش اميد دارد بايد عمل شايسته كند و كسى را در عبادت پروردگارش شريك نسازد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'كاف، ها، يا، عين، صاد'),
(2252, 19, 2, 'اين، ياد كرد رحمت پروردگار تو در باره‌ى بنده‌اش زكرياست'),
(2253, 19, 3, 'آن‌گاه كه [زكريا] پروردگارش را به آواى آهسته ندا كرد'),
(2254, 19, 4, 'گفت: پروردگارا! استخوانم سست و سرم از پيرى سپيد گشته است و هرگز در دعاى تو- اى پروردگار من- [از اجابت‌] بى‌بهره نبوده‌ام'),
(2255, 19, 5, 'و من پس از خود، از بستگانم [نسبت به حفظ آيين تو] بيمناكم، و زنم نازاست، پس مرا از جانب خود فرزندى عطا كن'),
(2256, 19, 6, 'كه هم وارث من باشد و هم وارث خاندان يعقوب، و اى پروردگار من! او را پسنديده گردان'),
(2257, 19, 7, 'اى زكريا! ما تو را به پسرى كه نامش يحيى است مژده مى‌دهيم كه قبلا همنامى براى او قرار نداده‌ايم'),
(2258, 19, 8, 'گفت: پروردگارا! چگونه مرا پسرى خواهد بود حال آن كه زنم نازاست و من از پيرى به فرتوتى رسيده‌ام'),
(2259, 19, 9, 'گفت: فرمان چنين است. خداى تو فرموده: اين بر من آسان است، و خودت را نيز پيش از اين من آفريدم كه چيزى نبودى'),
(2260, 19, 10, 'گفت: پروردگارا! براى من نشانه‌اى قرار ده. فرمود: نشانه تو اين است كه سه شبانه [روز] با اين كه تندرستى با مردم تكلم نتوانى كرد'),
(2261, 19, 11, 'پس زكريا از محراب به ميان قوم خويش بيرون آمد و به آنها اشاره كرد كه صبح و شام خدا را تسبيح گوييد'),
(2262, 19, 12, '[گفتيم:] اى يحيى! كتاب [خدا] را محكم بگير، و او را در كودكى حكمت داديم'),
(2263, 19, 13, 'و نيز از جانب خود مهربانى و پاكى [به او داديم‌] و او پرهيزكار بود'),
(2264, 19, 14, 'و با پدر و مادر خود نيكرفتار بود و سركش و عصيانگر نبود'),
(2265, 19, 15, 'و درود بر او روزى كه زاده شد و روزى كه مى‌ميرد و روزى كه زنده برانگيخته مى‌شود'),
(2266, 19, 16, 'و در اين كتاب از مريم ياد كن، آن‌گاه كه از كسان خود در ناحيه شرقى [بيت المقدس‌] خلوت گزيد'),
(2267, 19, 17, 'و ميان خود و آنها پرده‌اى افكند [تا خلوتگاه عبادتش باشد] پس روح خود را به سوى او فرستاديم تا به شكل بشرى خوش اندام بر او نمايان شد'),
(2268, 19, 18, 'مريم گفت: من از تو به خداى رحمان پناه مى‌برم اگر پرهيزكار باشى'),
(2269, 19, 19, 'گفت: من فقط فرستاده‌ى پروردگار توام براى اين كه به تو پسر پاكيزه‌اى ببخشم'),
(2270, 19, 20, 'گفت: چگونه مرا پسرى باشد با آن كه دست بشرى به من نرسيده و بدكار نبوده‌ام'),
(2271, 19, 21, 'گفت: [فرمان‌] چنين است. پروردگار تو فرمود: آن بر من آسان است و براى اين است كه او را آيتى براى مردم قرار دهيم و رحمتى از جانب ما باشد و اين امرى حتمى است'),
(2272, 19, 22, 'پس مريم به او حامله شد و با او در نقطه‌اى دوردست خلوت گزيد'),
(2273, 19, 23, 'آن‌گاه درد زايمان او را به سوى تنه نخلى كشاند. گفت: اى كاش پيش از اين مرده بودم و يكسره فراموش شده بودم'),
(2274, 19, 24, 'پس از طرف پايين پايش او را صدا زد كه: غم مدار، پروردگارت زير پاى تو چشمه آبى پديد آورده است'),
(2275, 19, 25, 'و اين تنه نخل را به طرف خود تكان ده، كه بر تو رطب تازه بريزد'),
(2276, 19, 26, 'و بخور و بنوش و چشم روشن دار. پس اگر كسى از آدميان را ديدى بگو: من براى خداى رحمان روزه نذر كرده‌ام و امروز مطلقا با بشرى سخن نخواهم گفت'),
(2277, 19, 27, 'پس [مريم‌] در حالى كه او را در آغوش گرفته بود به نزد قومش آورد. گفتند: اى مريم! به راستى چيز تازه و ناپسندى آورده‌اى'),
(2278, 19, 28, 'اى خواهر هارون! نه پدرت مرد بدى بود و نه مادرت زنى بدكاره'),
(2279, 19, 29, 'پس به سوى نوزاد اشاره كرد. گفتند: چگونه با كودكى كه در گهواره است سخن بگوييم؟'),
(2280, 19, 30, '[كودك‌] گفت: منم بنده‌ى خداوند كه به من كتاب داده و مرا پيامبر قرار داده است'),
(2281, 19, 31, 'و هر جا كه باشم مرا وجودى مبارك ساخته، و تا زنده‌ام مرا به نماز و زكات سفارش كرده است'),
(2282, 19, 32, 'و مرا نسبت به مادرم نيكوكار كرده و زورگو و نافرمانم نگردانيده است'),
(2283, 19, 33, 'و درود بر من روزى كه زاده شدم و روزى كه مى‌ميرم و روزى كه زنده برانگيخته مى‌شوم'),
(2284, 19, 34, 'اين است ماجراى عيسى پسر مريم، همان سخن حقى كه در آن ترديد مى‌كنند'),
(2285, 19, 35, 'خدا را نسزد كه فرزندى برگيرد. منزّه است او. چون كارى را اراده كند، به آن فقط مى‌گويد: باش و مى‌شود'),
(2286, 19, 36, 'و بى‌ترديد، اللّه پروردگار من و پروردگار شماست، پس او را بندگى كنيد كه راه راست همين است'),
(2287, 19, 37, 'اما گروه‌هايى از ميان آنها اختلاف كردند، پس واى براى كافران از حضور در روزى بزرگ'),
(2288, 19, 38, 'روزى كه پيش ما آيند چه شنوا و چه بينا خواهند بود، ولى ستمگران امروز در گمراهى آشكارند'),
(2289, 19, 39, 'و آنها را از روز حسرت آنگاه كه كار تمام مى‌شود بترسان، و حال آن كه هم اكنون غافلند و ايمان نمى‌آورند'),
(2290, 19, 40, 'ماييم كه زمين را با هر كه در آن است به ميراث مى‌بريم و همه به سوى ما بازگردانده مى‌شوند'),
(2291, 19, 41, 'و در اين كتاب ابراهيم را ياد كن. به راستى او پيامبرى بسيار راستگو [و درست كردار] بود'),
(2292, 19, 42, 'هنگامى كه به پدرش گفت: اى پدر! چرا چيزى را مى‌پرستى كه نمى‌شنود و نمى‌بيند و از تو نيازى را برطرف نمى‌سازد'),
(2293, 19, 43, 'اى پدر! به راستى مرا از دانش [وحى، حقايقى‌] به دست آمده كه تو را نيامده است، پس از من پيروى كن تا تو را به راهى راست هدايت نمايم'),
(2294, 19, 44, 'اى پدر! شيطان را مپرست كه شيطان نسبت به خداى رحمان نافرمان بوده است'),
(2295, 19, 45, 'اى پدر! من از آن مى‌ترسم كه از جانب خداى رحمان عذابى به تو رسد و تو يار شيطان گروى'),
(2296, 19, 46, 'گفت: اى ابراهيم! آيا تو از خدايان من بيزارى؟ اگر دست بر ندارى تو را سنگسار خواهم كرد، و مدت زيادى از من دور شو'),
(2297, 19, 47, '[ابراهيم‌] گفت: سلام بر تو، به زودى من از خدايم براى تو آمرزش مى‌طلبم، كه او با من بسيار مهربان است'),
(2298, 19, 48, 'و از شما و از آن چيزها كه به جاى خدا مى‌خوانيد كناره مى‌گيرم و پروردگار خود را مى‌خوانم. اميدوارم كه در خواندن پروردگارم بى‌پاسخ نمانم'),
(2299, 19, 49, 'و چون از آنها و از آنچه سواى خدا مى‌پرستيدند كناره گرفت، اسحاق و يعقوب را به او عطا كرديم و همه را پيامبر نموديم'),
(2300, 19, 50, 'و آنها را از رحمت خويش بهره‌مند ساختيم، و نام نيك و بلندى [در ميان امت‌ها] برايشان قرار داديم'),
(2301, 19, 51, 'و در اين كتاب از موسى ياد كن، همانا او خالص شده و فرستاده‌اى پيامبر بود'),
(2302, 19, 52, 'و از جانب راست طور او را ندا داديم و رازگويان نزديكش ساختيم'),
(2303, 19, 53, 'و به رحمت خويش برادرش هارون پيامبر را [براى مساعدت‌] به او بخشيديم'),
(2304, 19, 54, 'و در اين كتاب از اسماعيل ياد كن، همانا او خوش وعده و رسول [و] پيامبرى بود'),
(2305, 19, 55, 'و خاندان خود را به نماز و زكات فرمان مى‌داد و نزد پروردگارش مورد رضايت بود'),
(2306, 19, 56, 'و در اين كتاب از ادريس ياد كن كه او راستگو و پيامبر بود'),
(2307, 19, 57, 'و ما او را به مقامى بلند ارتقا داديم'),
(2308, 19, 58, 'آنان گروهى از پيامبران بودند كه خداوند به آنها نعمت [هدايت‌] داد از فرزندان آدم و از كسانى كه همراه نوح بر كشتى سوار كرديم و از فرزندان ابراهيم و يعقوب و از كسانى كه هدايت نموديم و برگزيديم، كه هرگاه آيات خداى رحمان برايشان تلاوت مى‌شد سجده‌كنان و گر'),
(2309, 19, 59, 'آن‌گاه كسانى جانشين اينان شدند كه نماز را ضايع كردند و شهوات را پيروى نمودند كه به زودى [سزاى‌] گمراهى را خواهند ديد'),
(2310, 19, 60, 'مگر كسانى كه توبه كردند و ايمان آوردند و كار شايسته انجام دادند، كه آنها به بهشت داخل شوند و هيچ‌گونه ستمى بر ايشان نرود'),
(2311, 19, 61, 'بهشت‌هاى جاودانى كه خداى رحمان بندگانش را به ناديده وعده داده است بى‌ترديد وعده‌ى او آمدنى است'),
(2312, 19, 62, 'در آن جا سخن بيهوده نشنوند جز درود، و صبح و شام روزيشان در آن جا برقرار است'),
(2313, 19, 63, 'اين همان بهشتى است كه به هر يك از بندگان ما كه پرهيزگار باشد به ميراث دهيم'),
(2314, 19, 64, 'و [جبرئيل گفت:] ما جز به فرمان پروردگار تو فرود نمى‌آييم. از آن اوست گذشته و آينده‌ى ما و آنچه ميان اين دو است. و پروردگار تو فراموشكار نيست'),
(2315, 19, 65, 'اوست پروردگار آسمان‌ها و زمين و آنچه ميان آن دو است، پس او را بندگى كن و در عبادت او شكيبا باش. آيا همنامى براى او مى‌شناسى'),
(2316, 19, 66, 'و انسان مى‌گويد: آيا وقتى مردم، راستى از گور زنده بيرون آورده خواهم شد'),
(2317, 19, 67, 'آيا انسان به خاطر نمى‌آورد كه ما او را قبلا آفريديم در حالى كه چيزى نبود'),
(2318, 19, 68, 'پس به پروردگارت سوگند كه حتما آنها را همراه شياطين گرد مى‌آوريم، آن‌گاه همه را به زانو نشسته پيرامون جهنم حاضر مى‌كنيم'),
(2319, 19, 69, 'وانگهى از هر گروه كسانى را كه در بر خداى رحمان سركش‌تر بودند بيرون مى‌كشيم'),
(2320, 19, 70, 'آن‌گاه ما كسانى را كه براى سوختن در آتش سزاوارترند بهتر مى‌شناسيم'),
(2321, 19, 71, 'و همه شما بدون استثنا وارد جهنم مى‌شويد كه اين كار بر خداى تو حتمى و قطعى است'),
(2322, 19, 72, 'سپس كسانى را كه تقوا پيشه كردند نجات مى‌دهيم و ظالمان را به زانو درآمده در آن رها مى‌كنيم'),
(2323, 19, 73, 'و چون آيات روشن ما بر آنها تلاوت شود، كسانى كه كافر شدند به مؤمنان گويند: كدام يك از ما دو گروه جايگاهش بهتر و محفلش نيكوتر است'),
(2324, 19, 74, 'و چه بسيار اقوامى را پيش از آنها نابود كرديم كه هم ساز و برگى بهتر و هم منظرى نيكوتر داشتند'),
(2325, 19, 75, 'بگو: هر كه در ضلالت باشد، بايد خداى رحمان او را تا زمانى مهلت و بهره دهد، تا وقتى كه آنچه را به آنها وعده داده مى‌شود با چشم خود ببينند، يا عذاب [اين دنيا] يا قيامت را، و آن روز خواهند دانست چه كسى جايش بدتر و سپاهش ناتوان‌تر است'),
(2326, 19, 76, 'و خداوند بر هدايت كسانى كه هدايت را پذيرفتند، مى‌افزايد. و نيكى‌هاى ماندگار در پيشگاه پروردگارت از نظر پاداش و عاقبت بهتر است'),
(2327, 19, 77, 'آيا ديدى آن كسى را كه به آيات ما كفر ورزيد و گفت: قطعا به من مال و فرزند بسيار داده خواهد شد'),
(2328, 19, 78, 'آيا از غيب خبر يافته يا نزد خداى رحمان پيمانى گرفته است'),
(2329, 19, 79, 'چنين نيست، آنچه را مى‌گويد خواهيم نوشت و عذابش را تداوم خواهيم داد'),
(2330, 19, 80, 'و آنچه را مى‌گويد [از اولاد و امكانات‌] از او به ارث مى‌بريم و نزد ما تنها مى‌آيد'),
(2331, 19, 81, 'و به جاى خدا معبودانى اختيار كردند تا سبب عزتشان باشد'),
(2332, 19, 82, 'چنين نيست زودا كه [آن معبودان‌] عبادت ايشان را انكار كنند و مخالف آنها شوند'),
(2333, 19, 83, 'آيا ندانستى كه ما شياطين را بر كافران فرستاديم تا آنها را [با وسوسه‌ها] شديدا تحريك كنند'),
(2334, 19, 84, 'پس بر [عذاب‌] آنها شتاب مكن، ما براى آنها دقيقا روز شمارى مى‌كنيم'),
(2335, 19, 85, 'روزى كه پرهيزكاران را به مهمانى و ديدار به سوى خداى رحمان فراهم آريم'),
(2336, 19, 86, 'و مجرمان را تشنه كام، به سوى دوزخ برانيم'),
(2337, 19, 87, 'آنها مالك شفاعت نيستند، جز آن كس كه نزد خداى رحمان پيمانى گرفته باشد'),
(2338, 19, 88, 'و گفتند: خداى رحمان فرزندى گرفته است'),
(2339, 19, 89, 'واقعا چيز زشتى بر زبان آورديد'),
(2340, 19, 90, 'نزديك است آسمان‌ها از اين سخن بشكافند و زمين چاك خورد و كوه‌ها فرو ريخته درهم بشكنند'),
(2341, 19, 91, 'كه براى خداى رحمان فرزند قايل شدند'),
(2342, 19, 92, 'و حال آن كه خداى رحمان را نشايد كه فرزند گيرد'),
(2343, 19, 93, 'در آسمان‌ها و زمين هيچ كسى نيست مگر آن كه بنده‌وار نزد خداى رحمان مى‌آيد'),
(2344, 19, 94, 'بى‌شك همه آنها را بر شمرده و كامل شمرده است'),
(2345, 19, 95, 'و روز قيامت همه آنها به نزد او تنها خواهند آمد'),
(2346, 19, 96, 'كسانى كه ايمان آورده و كارهاى شايسته كرده‌اند، به زودى خداى رحمان براى آنها محبتى [در دل‌ها] قرار خواهد داد'),
(2347, 19, 97, 'جز اين نيست كه ما اين [قرآن‌] را به زبان تو آسان ساختيم تا پرهيزكاران را بدان مژده دهى و ستيزه گران را بدان بترسانى'),
(2348, 19, 98, 'و چه بسيار نسل‌هايى را كه پيش از آنها هلاك كرديم. آيا كسى از آنها را مى‌يابى يا [كمترين‌] صدايى از آنها مى‌شنوى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طا، ها'),
(2350, 20, 2, 'ما قرآن را بر تو نازل نكرديم كه به زحمت افتى'),
(2351, 20, 3, 'مگر پندى براى آن كس كه [از خدا] مى‌ترسد'),
(2352, 20, 4, 'نازل شده‌اى است از طرف آن كه زمين و آسمان‌هاى بلند را آفريد'),
(2353, 20, 5, 'خداى رحمان، بر عرش استيلا يافت'),
(2354, 20, 6, 'آنچه در آسمان‌ها و زمين است و آنچه ميان آن دو و آنچه زير خاك است، از آن اوست'),
(2355, 20, 7, 'و اگر سخن به آواز گويى [يا آهسته‌]، بى‌ترديد او نهان و نهان‌تر را مى‌داند'),
(2356, 20, 8, 'خداى يكتا جز او معبودى نيست و بهترين نام‌ها [و صفات برتر] از آن اوست'),
(2357, 20, 9, 'و آيا حكايت موسى به تو رسيده است'),
(2358, 20, 10, 'آن‌گاه كه آتشى ديد، پس به خانواده‌ى خود گفت: درنگ كنيد كه من آتشى ديده‌ام، شايد شعله‌اى از آن برايتان بياورم يا در [روشنايى‌] آتش راهى بيابم'),
(2359, 20, 11, 'پس چون به آن رسيد، ندا داده شد: اى موسى'),
(2360, 20, 12, 'همانا من پروردگار تو هستم. پس هر دو كفش خود را درآور كه [اينك‌] تو در وادى مقدس «طوى» هستى'),
(2361, 20, 13, 'و من تو را برگزيده‌ام، پس بدانچه وحى مى‌شود گوش فرا دار'),
(2362, 20, 14, 'اين منم خداوند [يكتا] كه خدايى جز من نيست، پس مرا پرستش كن و به ياد من نماز بپادار'),
(2363, 20, 15, 'قيامت حتما آمدنى است. مى‌خواهم وقت آن را پنهان دارم تا هر كسى به موجب آنچه مى‌كوشد جزا داده شود'),
(2364, 20, 16, 'پس هرگز نبايد كسى كه به آن ايمان ندارد و از هواى خويش پيروى كرده، تو را از [ايمان به‌] آن باز دارد كه هلاك بشوى'),
(2365, 20, 17, 'اى موسى! آن چيست به دست راست تو'),
(2366, 20, 18, 'گفت: اين عصاى من است، بر آن تكيه مى‌كنم و با آن براى گوسفندانم برگ مى‌تكانم، و نيازهاى ديگرى هم براى من در آن هست'),
(2367, 20, 19, 'گفت: آن را بينداز اى موسى'),
(2368, 20, 20, 'پس آن را انداخت و ناگاه مارى شد كه به سرعت مى‌خزيد'),
(2369, 20, 21, 'گفت: آن را بگير و مترس، به زودى آن را به صورت اولش باز مى‌گردانيم'),
(2370, 20, 22, 'و دست خويش در بغل كن، تا بى‌هيچ گزندى [به عنوان‌] معجزه‌اى ديگر سفيد [و درخشان‌] بيرون آيد'),
(2371, 20, 23, 'تا پاره‌اى از آيات بزرگ‌تر خود را به تو نشان دهيم'),
(2372, 20, 24, 'به سوى فرعون برو كه او طغيان كرده است'),
(2373, 20, 25, 'گفت: پروردگارا! سينه‌ام را وسعت بخش'),
(2374, 20, 26, 'و كارم را برايم آسان ساز'),
(2375, 20, 27, 'و گره از زبانم بگشاى'),
(2376, 20, 28, 'تا سخنم را دريابند'),
(2377, 20, 29, 'و براى من دستيارى از كسانم قرار ده'),
(2378, 20, 30, 'هارون برادرم را'),
(2379, 20, 31, 'پشت من را بدو محكم كن'),
(2380, 20, 32, 'و او را در كارم شريك ساز'),
(2381, 20, 33, 'تا تو را فراوان تسبيح گوييم'),
(2382, 20, 34, 'و تو را بسيار ياد كنيم'),
(2383, 20, 35, 'بى‌ترديد تو بر حال ما ناظر بوده‌اى'),
(2384, 20, 36, 'گفت: اى موسى! خواسته‌ات به تو داده شد'),
(2385, 20, 37, 'و البته يك بار ديگر هم بر تو عنايت كرديم'),
(2386, 20, 38, 'آن‌گاه كه به مادرت آنچه وحى شد وحى كرديم'),
(2387, 20, 39, 'كه او را در صندوقى بنه، و آن را به دريا بيفكن تا دريا او را به ساحل اندازد و دشمن من و دشمن وى او را برگيرد. و بر تو از جانب خود مهرى [در دل‌ها] افكندم تا زير نظر من پرورش يابى'),
(2388, 20, 40, 'آن‌گاه كه خواهرت [در پى صندوق تو] راه مى‌رفت و مى‌گفت: آيا كسى را به شما نشان دهم كه سرپرستى او را عهده دار شود؟ پس تو را به آغوش مادرت بازگردانديم تا چشمش روشن شود و اندوه نخورد، و شخصى [از فرعونيان‌] را كشتى و ما تو را از اندوه رهانيديم، و بارها ت'),
(2389, 20, 41, 'و تو را براى خود پروردم'),
(2390, 20, 42, 'اينك تو و برادرت با آيات من برويد و در ياد من سستى مكنيد'),
(2391, 20, 43, 'به سوى فرعون برويد كه او به سركشى برخاسته است'),
(2392, 20, 44, 'و با او سخنى نرم گوييد، شايد پند گيرد يا بترسد'),
(2393, 20, 45, 'گفتند: پروردگارا! ما مى‌ترسيم كه بر ما پيشدستى كند يا سر به طغيان بردارد'),
(2394, 20, 46, 'گفت: مترسيد، من همراه شما هستم، مى‌شنوم و مى‌بينم'),
(2395, 20, 47, 'پس نزد او بياييد [من آنجا حاضرم‌] و بگوييد: ما دو فرستاده‌ى پروردگار توييم، پس بنى اسرائيل را با ما بفرست و آزارشان مده، همانا ما براى تو از سوى پروردگارت معجزه‌اى آورده‌ايم، و درود بر آن كس كه از هدايت پيروى كند'),
(2396, 20, 48, 'همانا به ما وحى شده كه عذاب بر كسى است كه تكذيب كند و روى گرداند'),
(2397, 20, 49, 'فرعون گفت: پروردگار شما دو نفر كيست اى موسى!'),
(2398, 20, 50, 'گفت: پروردگار ما كسى است كه آفرينش هر چيزى را به او عطا كرده سپس هدايتش نموده است'),
(2399, 20, 51, 'گفت: پس سرنوشت مردمان نخستين كه [ايمان نياوردند] چه مى‌شود'),
(2400, 20, 52, 'گفت: علم آن در كتابى نزد پروردگار من است. پروردگارم نه دچار خطا مى‌شود و نه فراموش مى‌كند'),
(2401, 20, 53, 'خدايى كه زمين را برايتان آسايشگاه قرار داد و براى شما در آن راه‌ها كشيد و از آسمان آبى فرستاد، پس با آن انواع مختلف از روييدنى‌ها را بيرون آورديم'),
(2402, 20, 54, 'بخوريد و دام‌هايتان را بچرانيد كه قطعا در اين‌ها عاقلان را نشانه‌هايى [عبرت آور] است'),
(2403, 20, 55, 'شما را از زمين آفريديم و [با مرگ‌] در آن بازتان مى‌گردانيم و بار ديگر از آن بيرونتان مى‌آوريم'),
(2404, 20, 56, 'البته ما همه آيات خود را به او [فرعون‌] نشان داديم ولى او آنها را تكذيب كرد و سر باز زد'),
(2405, 20, 57, 'گفت: آيا نزد ما آمده‌اى تا ما را با سحر خود از ديارمان بيرون كنى اى موسى!'),
(2406, 20, 58, 'ما هم قطعا براى تو جادويى مانند آن مى‌آوريم پس ميان ما و خودت محل قرارى بگذار كه نه ما آن را خلاف كنيم و نه تو، آن هم در زمينى هموار'),
(2407, 20, 59, 'گفت: موعد شما روز جشن باشد كه مردم پيش از ظهر گردآورده مى‌شوند'),
(2408, 20, 60, 'پس فرعون برگشت و تمام شگرد خود را گرد آورد، سپس باز آمد'),
(2409, 20, 61, 'موسى به آنان گفت: واى بر شما! بر خدا دروغ نبنديد كه خدا شما را با عذابى [سخت‌] هلاك مى‌كند. و هر كه دروغ سازد بى‌ترديد نوميد و زيانكار شده است'),
(2410, 20, 62, 'پس [ساحران‌] ميان خود در باره‌ى كارشان اختلاف پيدا كردند و به نجوا پرداختند'),
(2411, 20, 63, '[فرعونيان‌] گفتند: قطعا اين دو تن ساحرند و مى‌خواهند شما را با سحر خود از ديارتان بيرون كنند و آيين والاى شما را براندازند'),
(2412, 20, 64, 'پس شگردهاى خود را فراهم آوريد و در يك صف پيش آييد كه بى‌ترديد امروز هر كه فايق آيد رستگار شده است'),
(2413, 20, 65, '[ساحران‌] گفتند: اى موسى! يا تو مى‌افكنى يا ما اولين كسى باشيم كه مى‌افكند'),
(2414, 20, 66, 'گفت: [نه‌] بلكه شما بيندازيد. پس ناگاه ريسمان‌ها و چوبدستى‌هايشان از جادوى آنان چنان به نظر او آمد كه آنها به شتاب مى‌خزند'),
(2415, 20, 67, 'و موسى در خود احساس ترس كرد'),
(2416, 20, 68, 'گفتيم: مترس كه تو حتما برترى'),
(2417, 20, 69, 'و آنچه در دست راست دارى بينداز تا هر چه را ساخته‌اند ببلعد، [زيرا] آنچه آنها ساخته‌اند صرفا ترفند جادوگر است و جادوگر هر جا رود رستگار نمى‌شود'),
(2418, 20, 70, 'پس ساحران به سجده افتادند [و] گفتند: به پروردگار موسى و هارون ايمان آورديم'),
(2419, 20, 71, '[فرعون‌] گفت: آيا پيش از آن كه به شما اذن دهم به او ايمان آورديد؟ قطعا او بزرگ شماست كه به شما سحر آموخته است. پس حتما دست‌ها و پاهايتان را مخالف [يكديگر از چپ و راست‌] قطع مى‌كنم و يقينا شما را بر تنه‌هاى نخل دار مى‌زنم و خوب خواهيد دانست كيفر كدام'),
(2420, 20, 72, 'گفتند: ما هرگز تو را بر آن نشانه‌هاى روشنى كه براى ما آمده و بر آن كس كه ما را پديد آورده ترجيح نخواهيم داد، پس هر حكمى كه مى‌خواهى بكن، تو فقط در اين زندگى دنيا حكم مى‌رانى'),
(2421, 20, 73, 'ما به پروردگار خويش ايمان آورديم تا از گناهان ما و اين سحرى كه بر ما تحميل كردى در گذرد، و خداوند بهتر و پايدارتر است'),
(2422, 20, 74, '[زيرا] هر كه گنهكار به پيشگاه پروردگارش درآيد، بى‌ترديد او را جهنم است كه در آن نه بميرد و نه زندگى يابد'),
(2423, 20, 75, 'و هر كه با ايمان نزد وى آيد و اعمال صالح كرده باشد، چنين كسانى برايشان درجات والا خواهد بود'),
(2424, 20, 76, 'بهشت‌هاى جاويدى كه از پاى درختانش نهرها جارى است كه در آن جاودانه باشند، و اين پاداش كسى است كه پاكى بجويد'),
(2425, 20, 77, 'و همانا به موسى وحى كرديم كه بندگانم را شبانه روانه كن و راهى خشك در دريا براى آنان باز كن كه نه از تعقيب دشمن خواهى ترسيد و نه [از غرق شدن‌] مى‌هراسى'),
(2426, 20, 78, '[پس‌] فرعون با لشكريانش از پى آنها رفتند و موجى از دريا كه بر آنها افتاد، آنها را فرا گرفت'),
(2427, 20, 79, 'و فرعون قوم خود را گمراه كرد و به جايى نرسانيد'),
(2428, 20, 80, 'اى بنى اسرائيل! به حقيقت ما شما را از دشمنتان رهانيديم و با شما در كنار راست كوه طور [براى نزول تورات‌] وعده كرديم و بر شما منّ و سلوى فرستاديم'),
(2429, 20, 81, 'از پاكيزه‌هاى آنچه روزيتان كرده‌ايم بخوريد و در آن سركشى نكنيد كه خشم من بر شما فرود آيد و هر كه خشم من بر او فرود آيد قطعا سقوط كرده است'),
(2430, 20, 82, 'و بى‌ترديد من آمرزگار كسى هستم كه توبه كند و ايمان آورد و كار نيكو كند و آن‌گاه به راه [راست‌] آيد'),
(2431, 20, 83, 'و اى موسى! چه چيز موجب شد تو جلوتر از قومت [به وعده‌گاه‌] بشتابى'),
(2432, 20, 84, 'گفت: اينك آنها پشت سر منند، و من به حضورت شتافتم- اى پروردگار من- تا خشنود شوى'),
(2433, 20, 85, 'فرمود: ما از پس تو قومت را امتحان كرديم و سامرى آنها را گمراه كرد'),
(2434, 20, 86, 'پس موسى خشمگين و اندوهناك به سوى قوم خود بازگشت و گفت: اى قوم من! آيا پروردگارتان به شما وعده‌ى نيكو نداده بود؟ آيا مدت [درنگ من در كوه طور] بر شما طولانى شد؟ يا خواستيد خشمى از پروردگارتان بر شما فرود آيد كه وعده‌ى مرا خلاف كرديد'),
(2435, 20, 87, 'گفتند: ما به اختيار خود وعده‌ى تو را خلاف نكرديم، ولى محموله‌هايى از زيور آلات قوم [فرعون‌] را بر دوش ما نهادند پس آنها را [در كوره‌ى آتش‌] انداختيم و سامرى نيز [آنچه دانست‌] انداخت'),
(2436, 20, 88, 'پس براى آنان پيكر گوساله‌اى ساخت كه صداى گاو داشت، پس [او و پيروانش‌] گفتند: خداى شما و خداى موسى همين است كه فراموش كرد'),
(2437, 20, 89, 'مگر نمى‌بينند كه [گوساله‌] پاسخ آنها را بر نمى‌گرداند و به حالشان سود و زيانى ندارد'),
(2438, 20, 90, 'و پيش از آن هارون نيز با تأكيد به آنها گفته بود: اى قوم من! جز اين نيست كه شما با اين گوساله امتحان شده‌ايد و بى‌ترديد پروردگار حقيقى شما خداى رحمان است، پس پيرو من باشيد و فرمان مرا اطاعت كنيد'),
(2439, 20, 91, 'گفتند: ما همچنان بر پرستش آن خواهيم بود تا موسى به نزد ما بازگردد'),
(2440, 20, 92, 'موسى گفت: اى هارون! وقتى ديدى آنها گمراه شدند چه چيز مانع تو شد'),
(2441, 20, 93, 'كه از من پيروى نكنى؟ آيا امر مرا نافرمانى كردى'),
(2442, 20, 94, 'گفت: اى پسر مادرم! ريش و سر مرا مگير، من از آن ترسيدم كه بگويى ميان بنى اسرائيل تفرقه انداختى و سخنم را رعايت نكردى'),
(2443, 20, 95, 'گفت: اى سامرى! اين چه كارى بود كه كردى'),
(2444, 20, 96, 'گفت: من چيزى ديدم كه آنها نديدند، پس مشتى از ردّ پاى فرستاده [خدا، جبرئيل‌] برداشتم و آن را [در آتش‌] انداختم و نفس من اين گونه برايم بياراست'),
(2445, 20, 97, 'گفت: پس دور شو كه تو تا زنده هستى بايد [چنان مريض شوى كه‌] هر كس به تو نزديك شود] بگويى به من دست نزنيد! و تو را وعده‌گاه [عذابى‌] است كه هرگز در مورد آن با تو تخلف نشود، و [اينك‌] به آن معبودى كه در عبادتش بودى بنگر كه آن را مى‌سوزانيم سپس [خاكستر]'),
(2446, 20, 98, 'معبود شما تنها آن خدايى است كه جز او معبودى نيست و علم او همه چيز را در بر گرفته است'),
(2447, 20, 99, 'اين گونه از اخبار مهم گذشته بر تو حكايت مى‌كنيم، و همانا از طرف خود به تو [در قرآن‌] ذكرى داده‌ايم'),
(2448, 20, 100, 'هر كس از آن روى برتابد، روز قيامت بار گناهى بر دوش مى‌كشد'),
(2449, 20, 101, 'پيوسته در آن حال مى‌مانند، و بد بارى در روز قيامت خواهند داشت'),
(2450, 20, 102, 'روزى كه در صور دميده شود، و آن روز مجرمان را با چشم كبود محشور مى‌كنيم'),
(2451, 20, 103, 'ميان خود آهسته با هم مى‌گويند: شما در دنيا جز ده روز به سر نبرده‌ايد'),
(2452, 20, 104, 'ما داناتريم به آنچه مى‌گويند آن‌گاه كه رهيافته‌ترين آنها مى‌گويد: فقط يك روز مانده‌ايد'),
(2453, 20, 105, 'و از تو در باره‌ى كوه‌ها مى‌پرسند. بگو: پروردگارم آنها را [در قيامت‌]، به طور كامل از هم مى‌پاشد'),
(2454, 20, 106, 'پس آنها را به صورت باير و هموار وا مى‌گذارد'),
(2455, 20, 107, 'كه در آن نه پستى بينى و نه بلندى'),
(2456, 20, 108, 'در آن روز مردم از پى آن منادى [الهى‌] بدون تخطى، حركت مى‌كنند، و صداها در مقابل خداى رحمان خاشع مى‌گردد، و جز صداى آهسته [و زير لب‌] چيزى نمى‌شنوى'),
(2457, 20, 109, 'آن روز شفاعت بى‌فايده است، مگر [شفاعت‌] آن كس كه خداى رحمان به او اذن دهد و از گفته او راضى باشد'),
(2458, 20, 110, 'خداوند آينده و گذشته‌شان را مى‌داند و آنان به او احاطه علمى ندارند'),
(2459, 20, 111, 'و در آن روز چهره‌ها در برابر [خداى‌] زنده‌ى پاينده خاضع شوند و هر كه بار ستم برداشت مأيوس و زيانكار گردد'),
(2460, 20, 112, 'و هر كس كارهاى شايسته به جا آورد در حالى كه مؤمن باشد، نه از ستمى مى‌هراسد و نه از پايمال شدن'),
(2461, 20, 113, 'و اين گونه آن را قرآنى عربى نازل كرديم و در آن از انواع هشدارها سخن آورديم، شايد تقوا پيش گيرند يا پندى براى آنها پديد آرد'),
(2462, 20, 114, 'پس بلند مرتبه است خدا آن فرمانرواى كه حق و در خواندن قرآن پيش از آن كه وحى آن بر تو پايان يابد شتاب مكن، و بگو: پروردگارا! بر دانشم بيفزاى'),
(2463, 20, 115, 'و ما از پيش با آدم پيمان بستيم، ولى فراموش كرد و ما براى او عزمى [راسخ‌] نيافتيم'),
(2464, 20, 116, 'و چون به فرشتگان گفتيم: براى آدم سجده كنيد. پس [همه‌] سجده كردند جز ابليس كه ابا كرد'),
(2465, 20, 117, 'سپس گفتيم: اى آدم! بى‌گمان، اين براى تو و همسرت دشمنى [خطرناك‌] است، زنهار تا شما را از بهشت به در نكند كه تيره‌بخت شوى'),
(2466, 20, 118, 'همانا تو را [در آن جا اين نعمت‌] است كه نه گرسنه شوى و نه برهنه مانى'),
(2467, 20, 119, 'و هم اين كه در آن جا نه تشنه مى‌شوى و نه آفتاب زده'),
(2468, 20, 120, 'پس شيطان او را وسوسه كرد، گفت: اى آدم! آيا تو را به درخت جاودانگى و فرمانروايى بى‌زوال راه بنمايم'),
(2469, 20, 121, 'آن‌گاه از آن درخت خوردند و برهنگى آنان برايشان نمايان شد و چسبانيدن برگ‌هاى بهشت را بر خود آغاز كردند، و [اين گونه‌] آدم پروردگار خود را عصيان كرد و بيراهه رفت'),
(2470, 20, 122, 'سپس پروردگارش او را برگزيد و بر او ببخشود و هدايتش كرد'),
(2471, 20, 123, 'گفت: هر دو از آن جا پايين رويد كه دشمن يكديگر خواهيد بود. پس اگر براى شما از جانب من رهنمودى برسد، هر كس از راهنمايى من پيروى كند نه گمراه مى‌شود و نه تيره‌بخت'),
(2472, 20, 124, 'و هر كه از ياد من روى بگرداند، بى‌ترديد زندگى سختى خواهد داشت و روز رستاخيز نابينا محشورش مى‌كنيم'),
(2473, 20, 125, 'مى‌گويد: پروردگارا! چرا مرا نابينا محشور كردى حال آن كه بينا بودم'),
(2474, 20, 126, 'مى‌فرمايد: بدين‌سان بود كه آيات ما به تو رسيد ولى آنها را فراموش كردى و همان گونه امروز فراموش مى‌شوى'),
(2475, 20, 127, 'و بدين‌سان هر كه را زياده‌روى كرده و به آيات پروردگارش ايمان نياورده است سزا مى‌دهيم، و قطعا عذاب آخرت سخت‌تر و پايدارتر است'),
(2476, 20, 128, 'آيا به ايشان راه ننمود كه چه بسيار مردمى را پيش از آنها هلاك نموديم كه اينك در خانه‌هاى آنها راه مى‌روند؟ به راستى براى خردمندان در اين امر عبرت‌هاست'),
(2477, 20, 129, 'و اگر از جانب پروردگارت سخنى [مبنى بر مهلت‌] نگذشته بود و سر رسيدى معين در كار نبود، حتما [عذاب‌] بر آنها [در همين دنيا] لازم مى‌شد'),
(2478, 20, 130, 'پس بر آنچه مى‌گويند صبور باش، و پيش از طلوع آفتاب و قبل از غروب آن پروردگارت را تسبيح گوى، و از ساعات شب و حوالى روز به نيايش پرداز، باشد كه [از الطاف الهى‌] خشنود گردى'),
(2479, 20, 131, 'و زنهار چشم مدوز به آنچه از زينت زندگى دنيا به گروهى از آنان داده‌ايم، تا آنها را در آن امتحان كنيم، و روزى پروردگار تو بهتر و پايدارتر است'),
(2480, 20, 132, 'و كسان خود را به نماز فرمان ده و بر آن پافشارى كن. از تو روزى نمى‌خواهيم، ما [خود] به تو روزى مى‌دهيم، و عاقبت [خير] از براى پرهيزكارى است'),
(2481, 20, 133, 'و گفتند: چرا از جانب پروردگارش معجزه‌اى براى ما نمى‌آورد؟ آيا دليل روشنى كه در كتاب‌هاى پيشين است براى آنها نيامده است'),
(2482, 20, 134, 'و اگر ما پيش از اين به عذابى هلاكشان مى‌كرديم، قطعا مى‌گفتند: پروردگارا! چرا پيامبرى به سوى ما نفرستادى تا پيش از آن كه به خوارى و رسوايى افتيم از آيات تو پيروى كنيم'),
(2483, 20, 135, 'بگو: همگى در انتظاريم، پس منتظر باشيد. زودا كه بدانيد رهروان راه راست كيانند و هدايت يافتگان كدامند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'مردم را وقت حسابشان نزديك شده و آنها همچنان در غفلت و بى‌خبرى رويگردانند'),
(2485, 21, 2, 'هيچ پند تازه‌اى از پروردگارشان برايشان نيامد مگر اين كه بازى‌كنان آن را گوش دادند'),
(2486, 21, 3, 'در حالى كه دل‌هايشان [به دنيا] مشغول بود. و آنها كه ظلم كردند، پنهانى نجوا كردند كه: آيا اين جز بشرى مانند شماست؟ آيا ديده و دانسته به جادو روى مى‌آوريد'),
(2487, 21, 4, '[پيامبر] گفت: پروردگار من هر گفتارى را در آسمان و زمين مى‌داند، و شنواى دانا اوست'),
(2488, 21, 5, 'بلكه گفتند: خواب‌هاى پريشان است، بلكه آن را بر بافته، نه بلكه او شاعر است. پس بايد براى ما معجزه‌اى بياورد همان گونه كه پيشينيان فرستاده شدند'),
(2489, 21, 6, 'قبل از آنها [نيز مردم‌] هيچ شهرى كه آنها را هلاك نموديم، [به معجزه‌هاى ما] ايمان نياوردند، پس آيا اينان ايمان مى‌آورند'),
(2490, 21, 7, 'و پيش از تو نيز نفرستاديم مگر مردانى را كه به آنها وحى مى‌كرديم. پس اگر خود نمى‌دانيد از اهل ذكر [و كتاب دانان‌] بپرسيد'),
(2491, 21, 8, 'و آنان را جسدى قرار نداديم كه طعام نخورند، و عمر جاويدان هم نداشتند'),
(2492, 21, 9, 'سپس وعده‌ى خود را بر آنها محقق كرديم و آنها و هر كه را خواستيم نجات داديم و تجاوزكاران را هلاك نموديم'),
(2493, 21, 10, 'همانا ما كتابى به سوى شما نازل كرديم كه ياد شما در آن است، آيا نمى‌انديشيد'),
(2494, 21, 11, 'و چه بسيار شهرهايى را كه [مردمش‌] ستمكار بودند درهم شكستيم و پس از آنها قومى ديگر پديد آورديم'),
(2495, 21, 12, 'پس چون عذاب ما را احساس كردند، به ناگاه از شهر مى‌گريختند'),
(2496, 21, 13, '[به استهزا گفته مى‌شد]، نگريزيد و به زندگى خوش و به خانه‌هاى خود برگرديد تا شايد [فقرا] از شما حاجت خواهند [و شما هم ندهيد]'),
(2497, 21, 14, 'گفتند: اى واى بر ما، كه ما واقعا ستمگر بوديم'),
(2498, 21, 15, 'سخنشان پيوسته همين بود تا آنان را درو كرديم و خاموش ساختيم'),
(2499, 21, 16, 'و آسمان و زمين و آنچه را كه ميان آن دو است بازى‌كنان نيافريديم'),
(2500, 21, 17, 'اگر مى‌خواستيم مشغوليتى بگيريم، قطعا آن را از نزد خود اختيار مى‌كرديم'),
(2501, 21, 18, 'بلكه حق را بر سر باطل مى‌افكنيم پس آن را درهم مى‌شكند و ناگهان آن نابود مى‌گردد. واى بر شما از آنچه وصف مى‌كنيد'),
(2502, 21, 19, 'و هر كه در آسمان‌ها و زمين است از آن اوست، و كسانى كه نزد اويند از پرستش وى سرباز نمى‌زنند و ملول نمى‌شوند'),
(2503, 21, 20, 'آنها شب و روز بى‌هيچ سستى تسبيح مى‌كنند'),
(2504, 21, 21, 'آيا براى خود خدايانى از زمين اختيار كرده‌اند كه آنها حيات دوباره مى‌دهند'),
(2505, 21, 22, 'اگر در زمين و آسمان غير از خداى يگانه خدايان ديگرى مى‌بود، قطعا زمين و آسمان تباه مى‌شد. پس منزّه است خدا- صاحب عرش- از آنچه وصف مى‌كنند'),
(2506, 21, 23, '[خدا] از آنچه مى‌كند بازخواست نمى‌شود، ولى آن‌ها بازخواست مى‌شوند'),
(2507, 21, 24, 'آيا به جاى او خدايانى براى خود گرفته‌اند؟ بگو: برهانتان را بياوريد. اين [حجت خواهى‌] سخن و تذكار كسانى است كه با منند و تذكار كسانى است كه پيش از من بودند. اما بيشترشان حق را نمى‌شناسند و لذا از آن رويگردانند'),
(2508, 21, 25, 'و پيش از تو هيچ پيامبرى نفرستاديم جز اين كه به او وحى مى‌كرديم كه: خدايى جز من نيست، پس مرا بپرستيد'),
(2509, 21, 26, 'و گفتند: خداى رحمان فرزندى گرفته است، منزّه است او، بلكه [فرشتگان و اوليا] بندگانى ارجمندند'),
(2510, 21, 27, 'كه در سخن بر او پيشى نمى‌گيرند و به فرمان او كار مى‌كنند'),
(2511, 21, 28, 'آنچه پيش رو و پشت سرشان است مى‌داند، و جز براى كسى كه [خدا] رضايت دهد شفاعت نمى‌كنند و از مهابت او هراسانند'),
(2512, 21, 29, 'و هر كه از آنها بگويد: من معبودى غير از خدا هستم، او را به جهنم كيفر مى‌دهيم. [آرى‌] ستمكاران را اين گونه سزا مى‌دهيم'),
(2513, 21, 30, 'آيا كسانى كه كافر شدند، نديدند كه آسمان‌ها و زمين بسته بودند [و آب و گياه نمى‌دادند] و ما آنها را شكافتيم و هر چيز زنده‌اى را از آب پديد آورديم؟ پس آيا ايمان نمى‌آورند'),
(2514, 21, 31, 'و در زمين كوه‌هايى استوار نهاديم تا زمين آنان را نلرزاند، و در آن راه‌هايى فراخ پديد آورديم تا راه خود را بيابند'),
(2515, 21, 32, 'و آسمان را سقفى محفوظ قرار داديم، ولى آنها از [مطالعه‌] نشانه‌هاى آن رويگردانند'),
(2516, 21, 33, 'و اوست آن كه شب و روز و خورشيد و ماه را آفريد، كه هر كدام در مدارى معين شناورند'),
(2517, 21, 34, 'و پيش از تو براى هيچ بشرى جاودانگى [در دنيا] مقرر نكرديم، آيا اگر تو مردى آنها جاويد مى‌مانند'),
(2518, 21, 35, 'هر نفسى چشنده‌ى مرگ است، و شما را با خير و شر مى‌آزماييم آزمونى [ويژه‌]، و به سوى ما بازگردانده مى‌شويد'),
(2519, 21, 36, 'و كسانى كه كافر شدند، چون تو را ببينند جز به مسخره‌ات نگيرند [و گويند]: آيا اين همان كس است كه خدايانتان را [به بدى‌] ياد مى‌كند؟ در حالى كه آنان خود ياد خداى رحمان را منكرند'),
(2520, 21, 37, 'انسان [گويى‌] از شتاب آفريده شده است! به زودى آيات خود را به شما نشان مى‌دهم، پس عجله نكنيد'),
(2521, 21, 38, 'و مى‌گويند: اين وعده [ى قيامت‌] كى خواهد بود اگر راست مى‌گوييد'),
(2522, 21, 39, 'اگر كافران مى‌دانستند زمانى را كه نه مى‌توانند آتش را از چهره و پشت خود باز دارند و نه يارى مى‌شوند [در آمدن قيامت شتاب نمى‌كردند]'),
(2523, 21, 40, 'بلكه [قيامت‌] ناگهان بر آنها فرا مى‌رسد و مبهوتشان مى‌كند، كه نه مى‌توانند آن را برگردانند و نه به آنها مهلت داده مى‌شود'),
(2524, 21, 41, 'و همانا پيامبرانى هم كه پيش از تو بودند مورد استهزا قرار گرفتند، پس كسانى كه ايشان را تمسخر مى‌كردند، همان عذابى كه مسخره‌اش مى‌كردند دامانشان را گرفت'),
(2525, 21, 42, 'بگو: چه كسى شما را شب و روز از [قهر خداى‌] رحمان حفظ مى‌كند؟ بلكه آنها از ياد پروردگارشان رويگردانند'),
(2526, 21, 43, 'آيا برايشان خدايانى است كه در برابر ما از آنها دفاع كنند؟ [آن خدايان‌] نه مى‌توانند خود را يارى كنند و نه از جانب ما حمايت مى‌شوند'),
(2527, 21, 44, 'بلكه ما آنان و پدرانشان را برخوردار كرديم، تا آن جا كه عمرشان طولانى شد [و موجب غرورشان گرديد]. آيا نمى‌بينند كه ما به سراغ زمين مى‌آييم و آن را از هر طرف مى‌كاهيم [و مردمش را مى‌بريم‌]؟ آيا پس آنها پيروزند'),
(2528, 21, 45, 'بگو: من تنها با وحى [الهى و قرآن‌] شما را هشدار مى‌دهم ولى كران چون بيم داده شوند دعوت را نمى‌شنوند'),
(2529, 21, 46, 'و اگر شمه‌اى از عذاب پروردگارت به آنها برسد، حتما خواهند گفت: اى واى بر ما، به راستى ما ستمكار بوديم'),
(2530, 21, 47, 'و ترازوهاى عدل را در روز رستاخيز مى‌نهيم، پس هيچ كس اندك ستمى نمى‌بيند، و اگر [عملى‌] هموزن دانه خردلى هم باشد آن را مى‌آوريم، و كافى است كه ما حسابرس باشيم'),
(2531, 21, 48, 'و بى‌شك به موسى و هارون [كتاب‌] فرقان داديم كه براى پرهيزگاران روشنايى و يادآورى بود'),
(2532, 21, 49, '[همان‌] كسانى كه از پروردگارشان در نهان پروا دارند و از قيامت بيمناكند'),
(2533, 21, 50, 'و اين [قرآن‌] ذكر مباركى است كه آن را نازل كرده‌ايم، پس آيا شما آن را انكار مى‌كنيد'),
(2534, 21, 51, 'و همانا ما از پيش به ابراهيم رشد و كمالش را عطا كرديم و ما به [شايستگى‌] او دانا بوديم'),
(2535, 21, 52, 'آن‌گاه كه به پدر خود و قومش گفت: اين مجسمه‌هايى كه شما به پرستش آنها معتكفيد چيستند'),
(2536, 21, 53, 'گفتند: پدران خود را پرستشگر آنها يافتيم'),
(2537, 21, 54, 'گفت: قطعا شما و پدرانتان در گمراهى آشكارى بوده‌ايد'),
(2538, 21, 55, 'گفتند: آيا [سخن‌] حق را براى ما آورده‌اى يا از بازيگرانى'),
(2539, 21, 56, 'گفت: آرى [حق را آورده‌ام‌]، پروردگارتان پروردگار آسمان‌ها و زمين است كه آنها را پديد آورده و من بر اين [واقعيت‌] از گواهانم'),
(2540, 21, 57, 'و [در دل گفت:] به خدا سوگند پس از آن كه پشت كرديد و رفتيد، قطعا براى بت‌هاى شما چاره‌اى خواهم كرد'),
(2541, 21, 58, 'پس آنها را خرد كرد، جز بزرگشان را، تا به سوى او مراجعه كنند [كه حق را روشن كند]'),
(2542, 21, 59, 'گفتند: چه كسى با خدايان ما چنين كرده است؟ او واقعا از ستمكاران است'),
(2543, 21, 60, 'گفتند: شنيده‌ايم جوانى كه به او ابراهيم گفته مى‌شود از آنها به بدى ياد مى‌كند'),
(2544, 21, 61, 'گفتند: پس او را در برابر چشم مردم حاضر كنيد تا آنها [به جرم او] گواهى دهند'),
(2545, 21, 62, 'گفتند: اى ابراهيم! آيا تو با خدايان ما چنين كرده‌اى'),
(2546, 21, 63, 'گفت: نه، بلكه اين كار را بزرگ آنها كرده است. اگر حرف مى‌زنند از خودشان بپرسيد'),
(2547, 21, 64, 'پس به خويشتن بازگشتند و [به يكديگر] گفتند: در حقيقت شما خودتان ستمكاريد'),
(2548, 21, 65, 'سپس سر افكنده شدند [و گفتند:] تو خوب مى‌دانى كه اينها سخن نمى‌گويند'),
(2549, 21, 66, 'گفت: پس آيا سواى خدا چيزى را مى‌پرستيد كه هيچ سود و زيانى به شما نمى‌رساند'),
(2550, 21, 67, 'اف بر شما و بر آنچه غير از خدا مى‌پرستيد، پس چرا نمى‌انديشيد'),
(2551, 21, 68, 'گفتند: اگر مى‌خواهيد كارى كنيد او را بسوزانيد و خدايانتان را نصرت دهيد'),
(2552, 21, 69, 'گفتيم: اى آتش! براى ابراهيم سرد و سلامت باش'),
(2553, 21, 70, 'و خواستند به او نيرنگى بزنند ولى ما آنها را زيانكارتر [و بازنده‌ى اصلى‌] قرار داديم'),
(2554, 21, 71, 'و او و لوط را [با مهاجرت‌] به سوى سرزمينى كه در آن براى مردم بركت گذاشته‌ايم، نجات داديم'),
(2555, 21, 72, 'و اسحاق و يعقوب را به عنوان عطيه به او بخشيديم و همه را شايسته قرار داديم'),
(2556, 21, 73, 'و آنها را پيشوايانى كرديم كه [مردم را] به فرمان ما هدايت مى‌كردند، و به ايشان انجام دادن كارهاى نيك و برپا داشتن نماز و اداى زكات را وحى كرديم و آنان پرستشگر ما بودند'),
(2557, 21, 74, 'و به لوط داورى و دانش عطا كرديم و او را از آن شهرى كه مردمش كارهاى پليد مى‌كردند نجات داديم. به راستى آنها مردمى بدكار و فاسق بودند'),
(2558, 21, 75, 'و او را در رحمت خويش داخل كرديم همانا او از شايستگان بود'),
(2559, 21, 76, 'و نوح را ياد كن آن‌گاه كه پيش از اين ندا كرد و ما او را اجابت كرديم و وى را با خانواده‌اش از آن بلاى بزرگ نجات داديم'),
(2560, 21, 77, 'و او را در مقابل مردمى كه آيات ما را تكذيب كردند نصرت داديم، آنها مردم بدكارى بودند، پس همه آنها را غرق ساختيم'),
(2561, 21, 78, 'و داود و سليمان را [ياد كن‌]، هنگامى كه در باره‌ى آن كشتزار كه گوسفندان قوم، شبانه در آن چريده بودند داورى مى‌كردند و ما شاهد حكمتشان بوديم'),
(2562, 21, 79, 'پس شيوه‌ى داورى را به سليمان فهمانديم و هر يك را حكم و دانش عطا كرديم، و كوه‌ها و پرندگان را با داود مسخر كرديم كه با او تسبيح مى‌گفتند، و ما انجام دهنده‌ى اين كار بوديم'),
(2563, 21, 80, 'و براى شما به داود فن زره‌سازى آموختيم تا شما را از خطرات جنگتان حفظ كند. پس آيا سپاسگزار هستيد'),
(2564, 21, 81, 'و تندباد را مسخر سليمان كرديم كه به فرمان او به سوى سرزمينى كه در آن بركت نهاده بوديم روان مى‌گشت، و ما به هر چيزى آگاه بوديم'),
(2565, 21, 82, 'و عده‌اى از شياطين براى او غواصى مى‌كردند و غير از آن نيز كارهايى انجام مى‌دادند، و ما مراقب اوضاع آنها بوديم [تا از فرمان سليمان تمرد نكنند]'),
(2566, 21, 83, 'و ايوب را [ياد كن‌]، هنگامى كه پروردگارش را ندا داد كه مرا رنج و عسرت گرفته است و تو از همه مهربانان مهربان‌ترى'),
(2567, 21, 84, 'پس دعاى او را اجابت نموديم و آسيب وارد شده بر او را برطرف كرديم، و خانواده‌ى او و نظيرشان را همراه با آنان مجددا به وى عطا كرديم تا رحمتى از جانب ما و تذكرى براى خداپرستان باشد'),
(2568, 21, 85, 'و اسماعيل و ادريس و ذو الكفل را [ياد كن‌] كه همه از صابران بودند'),
(2569, 21, 86, 'و آنها را در جوار رحمت خود در آورديم، همانا آنان از شايستگان بودند'),
(2570, 21, 87, 'و ذو النّون (يونس) را [ياد كن‌]، آن‌گاه كه خشمگين [از ميان مردم خود] برفت و پنداشت كه ما هرگز بر او تنگ نمى‌گيريم، [كه ماهى او را بلعيد،] پس درون تاريكى‌ها ندا در داد كه معبودى جز تو نيست. منزّهى تو. به راستى كه من از ستمكاران بودم'),
(2571, 21, 88, 'پس دعايش را اجابت كرديم و او را از اندوه رهانيديم، و مؤمنان را چنين نجات مى‌دهيم'),
(2572, 21, 89, 'و زكريّا را [ياد كن‌]، هنگامى كه پروردگار خود را خواند: پروردگارا! مرا تنها [و بى‌فرزند] مگذار و تو بهترين وارثانى'),
(2573, 21, 90, 'پس دعاى او را اجابت نموديم و يحيى را بدو بخشيديم، و همسرش را براى او شايسته [و آماده‌ى حمل‌] كرديم همانا آنها در كارهاى نيك پيشقدم بودند و با بيم و اميد ما را مى‌خواندند و در برابر ما خاشع بودند'),
(2574, 21, 91, 'و [به ياد آور] آن بانو (مريم) را كه دامنش را پاك داشت، پس از روح خويش در او دميديم و او و پسرش را براى جهانيان آيتى قرار داديم'),
(2575, 21, 92, '[گفتيم:] اين است آيين شما كه آيينى يگانه است [با يك هدف و يك خدا]، و من پروردگار شمايم، پس مرا بپرستيد'),
(2576, 21, 93, 'ولى آنها امر [دين‌] خويش را ميان خود قطعه قطعه كردند [و فرقه‌ها شدند، ولى‌] همه آنان به سوى ما باز مى‌گردند'),
(2577, 21, 94, 'پس هر كه كارهاى شايسته انجام دهد و مؤمن باشد، سعى او ناسپاسى نخواهد شد، و ما نويسنده‌ى اعمال او هستيم'),
(2578, 21, 95, 'و مردم شهرى كه آن را هلاك كرده‌ايم محال است بازگشتى داشته باشند'),
(2579, 21, 96, 'تا وقتى كه [راه‌] يأجوج و مأجوج گشوده شود و آنها از هر بلندى بتازند'),
(2580, 21, 97, 'و آن وعده‌ى راستين [قيامت‌] نزديك مى‌شود و به ناگاه چشمان كسانى كه كافر شدند خيره مى‌شود [و مى‌گويند:] اى واى بر ما كه از اين روز غافل بوديم! بلكه ما ستمگر بوده‌ايم'),
(2581, 21, 98, 'در حقيقت شما و آنچه غير از خدا مى‌پرستيد، هيزم دوزخيد و همه در آن وارد مى‌شويد'),
(2582, 21, 99, 'اگر اينها خدا بودند وارد جهنم نمى‌شدند، و حال آن كه جملگى در آن ماندگارند'),
(2583, 21, 100, 'آنها را در آن جا ناله‌هايى [دردناك‌] است و ايشان در آن جا چيزى نمى‌شنوند'),
(2584, 21, 101, 'البته كسانى كه قبلا از جانب ما به آنها وعده‌ى نيكو داده شده، از آن [عذاب‌] دور نگاه داشته شدگانند'),
(2585, 21, 102, 'صداى دوزخ را نمى‌شنوند و در آنچه دل‌هايشان بخواهد جاودانند'),
(2586, 21, 103, 'آن دلهره‌ى بزرگ‌تر آنها را غمگين نكند و فرشتگان استقبالشان كنند [و گويند:] اين همان روزى است كه به شما وعده داده مى‌شد'),
(2587, 21, 104, 'روزى كه آسمان را چون طومار نوشته‌ها درهم مى‌پيچيم. چنان كه نخستين بار [مخلوقات را] بيافريديم آن را باز مى‌گردانيم. اين وعده‌اى بر عهده‌ى ما است، و ما بى‌ترديد انجام دهنده‌ى آنيم'),
(2588, 21, 105, 'و همانا در زبور پس از تورات نوشتيم كه زمين را بندگان شايسته ما به ارث خواهند برد'),
(2589, 21, 106, 'به راستى در اين سخن براى خداپرستان پيام روشنى است'),
(2590, 21, 107, 'و ما تو را جز رحمتى براى جهانيان نفرستاديم'),
(2591, 21, 108, 'بگو: جز اين نيست كه به من وحى مى‌شود كه خداى شما خدايى يگانه است، حال آيا شما تسليم مى‌شويد'),
(2592, 21, 109, 'پس اگر روى برتافتند، بگو: به همه شما به طور يكسان اعلام كردم و من چه مى‌دانم آيا آنچه وعده داده مى‌شويد نزديك است يا دور'),
(2593, 21, 110, 'آرى او، هم سخن آشكار را مى‌داند و هم آنچه را نهان مى‌داريد مى‌داند'),
(2594, 21, 111, 'و نمى‌دانم، شايد آن [تأخير عذاب‌] براى شما آزمايشى و [مهلت‌] برخوردارى تا زمانى [معين‌] باشد'),
(2595, 21, 112, '[پيامبر] گفت: اى خداى من! به حق داورى كن [و ظالمان را كيفر ده‌]، و پروردگار ما خداى رحمان، در برابر آنچه توصيف مى‌كنيد، مورد استمداد است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اى مردم! از پروردگارتان پروا كنيد همانا زلزله قيامت واقعه بزرگى است'),
(2597, 22, 2, 'روزى كه آن را ببينيد، [از هول آن‌] هر شير دهنده‌اى شير خواره‌اش را از ياد مى‌برد و هر آبستنى بار خود را [بر زمين‌] مى‌نهد، و مردم را مست مى‌بينى حال آن كه مست نيستند، بلكه عذاب خدا شديد است'),
(2598, 22, 3, 'و برخى از مردم در باره‌ى خدا بدون هيچ دانشى مجادله مى‌كنند و از هر شيطان سركشى پيروى مى‌نمايند'),
(2599, 22, 4, 'بر وى (شيطان) مقرر شده كه هر كس او را ولى بگيرد بى‌ترديد او را گمراه كند و به عذاب آتش بكشاند'),
(2600, 22, 5, 'اى مردم! اگر در رستاخيز شك داريد [توجه كنيد كه‌] همانا ما شما را از خاك، سپس از نطفه، سپس از لخته خونى آن‌گاه از پاره گوشتى شكل يافته و شكل نايافته، آفريديم تا [قدرت خود را] براى شما روشن سازيم، و هر چه را بخواهيم تا زمانى معين در رحم‌ها نگاه مى‌داري'),
(2601, 22, 6, 'اين [قدرت نمايى‌ها] از آن روست كه خدا حق است و اوست كه مردگان را زنده مى‌كند و هم اوست كه بر هر چيزى تواناست'),
(2602, 22, 7, '[و اين‌] كه بى‌ترديد قيامت در پيش است و شكى در آن نيست و خدا هر كه را در گورهاست بر مى‌انگيزد'),
(2603, 22, 8, 'و از ميان مردم كسى است كه بدون هيچ دانش و بى‌هيچ رهنمود و كتاب روشنى در باره‌ى خدا به مجادله مى‌پردازد'),
(2604, 22, 9, 'از سر نخوت پشت مى‌كند تا [مردم را] از راه خدا گمراه كند. در اين دنيا براى او خوارى است و روز رستاخيز عذاب آتش سوزان را به او مى‌چشانيم'),
(2605, 22, 10, '[گفته شود:] اين به سزاى آن چيزى است كه به دستان خود پيش فرستاده‌اى، و البته خداوند به بندگان خويش ستمگر نيست'),
(2606, 22, 11, 'و از مردم كسى است كه خدا را در حاشيه [و با ترديد] مى‌پرستد حال اگر خيرى [از ديندارى‌] به او رسد بدان آرام گيرد، و چون امتحانى براى او پيش آيد [از دين‌] روى برتابد. او در دنيا و آخرت زيانكار است و اين همان خسارت آشكار است'),
(2607, 22, 12, 'به جاى خدا چيزى را مى‌خواند كه نه زيانى به او مى‌رساند و نه سودش مى‌دهد. گمراهى دور همين است'),
(2608, 22, 13, 'كسى را مى‌خواند كه زيانش نزديك‌تر از سود اوست. وه، چه بد سرپرستى و چه بد معاشرى است!'),
(2609, 22, 14, 'بى‌گمان، خدا كسانى را كه گرويده و كارهاى شايسته كرده‌اند به بهشت‌هايى در مى‌آورد كه از پاى درختانش نهرها جارى است. خدا هر چه خواهد انجام مى‌دهد'),
(2610, 22, 15, 'هر كس گمان مى‌كرد كه خدا پيامبرش را در دنيا و آخرت هرگز يارى نخواهد كرد [و حال كه مى‌بيند يارى كرده، به خشم آمده است‌] پس ريسمانى به بالا بكشد، آن‌گاه خود را حلق آويز كرده، آن را قطع كند و آن‌گاه بنگرد كه آيا اين نقشه خشم او را از ميان مى‌برد'),
(2611, 22, 16, 'و بدين گونه قرآن را به صورت آياتى روشنگر نازل كرديم، و خداست كه هر كه را بخواهد راه مى‌نمايد'),
(2612, 22, 17, 'همانا كسانى كه ايمان آوردند و كسانى كه يهودى شدند و صابئى‌ها و مسيحيان و زرتشتيان و كسانى كه شرك ورزيدند، بى‌ترديد خداوند روز قيامت ميانشان داورى خواهد كرد همانا خدا بر هر چيزى گواه است'),
(2613, 22, 18, 'آيا نديدى كه هر كس در آسمان‌ها و هر كه در زمين است و خورشيد و ماه و تمام ستارگان و كوه‌ها و درختان و جنبندگان و بسيارى از مردم براى خدا سجده مى‌كنند؟ و بسيارى هم [ابا دارند كه‌] عذاب در باره‌ى آنان حتمى است. و هر كه را خدا خوار كند، او را اكرام كننده'),
(2614, 22, 19, 'اينان (مؤمنان و كافران) دو خصم‌اند كه در باره‌ى پروردگارشان به خصومت برخاسته‌اند. پس كسانى كه كافر شدند، جامه‌هايى از آتش برايشان بريده شده است و از بالاى سرشان آب جوشان ريخته مى‌شود'),
(2615, 22, 20, 'به نحوى كه هم آنچه در شكم آنهاست با آن گداخته مى‌شود و هم پوستشان'),
(2616, 22, 21, 'و آنها را گرزهاى آهنين است'),
(2617, 22, 22, 'هر بار بخواهند از [كثرت‌] اندوه از آن بيرون آيند به آن بازگردانده مى‌شوند و [گفته مى‌شود:] بچشيد عذاب آتش سوزان را'),
(2618, 22, 23, 'همانا خدا كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند به بهشت‌هايى داخل مى‌كند كه نهرها از پاى درختانش جارى است. در آن جا با دستبندهاى زرّين و مرواريد آراسته شوند و لباسشان در آن جا حرير است'),
(2619, 22, 24, 'و به گفتار پاك هدايت شده و به راه [خداى‌] ستوده هدايت گرديده‌اند'),
(2620, 22, 25, 'بى‌ترديد كسانى كه كافر شدند و [مردم را] از راه خدا و مسجد الحرام- كه آن را براى مردم، چه مقيم آن و چه مسافر، يكسان قرار داده‌ايم- باز مى‌دارند، [در خور عذابند]، و هر كه بخواهد در اين سرزمين به ستم كجروى كند، او را از عذابى دردناك مى‌چشانيم'),
(2621, 22, 26, 'و چون براى ابراهيم جايگاه كعبه را آماده كرديم [گفتيم: در اين كانون توحيد] چيزى را شريك من نكن، و خانه‌ام را براى طواف كنندگان و قيام كنندگان [به عبادت‌] و ركوع كنندگان و سجده كنندگان پاك دار'),
(2622, 22, 27, 'و ميان مردم نداى حج ده تا پياده و سوار بر مركب‌هاى لاغر [تندرو] از هر راه دورى به سوى تو آيند'),
(2623, 22, 28, 'تا ناظر منافع خود باشند و نام خدا را در روزهاى معين، بر [ذبح‌] آنچه از چهارپايان روزيشان كرده است ياد كنند پس از آنها بخوريد و به درمانده‌ى مستمند بخورانيد'),
(2624, 22, 29, 'سپس بايد آلودگى خود را بزدايند و به نذرهايشان وفا كنند و برگرد خانه‌ى كهن طواف نمايند'),
(2625, 22, 30, 'اين است [آنچه مقرر شده‌] و هر كس مقررات خدا را بزرگ دارد، آن براى او نزد پروردگارش بهتر است. و چهارپايان براى شما جز آنچه [حرمت آن‌] بر شما خوانده مى‌شود حلال است، پس از پليدى كه بت‌ها هستند دورى كنيد و از گفتار باطل اجتناب ورزيد'),
(2626, 22, 31, 'در حالى كه مخلصان خدا باشيد، نه مشرك به او. و هر كس به خدا شرك آورد، بدان ماند كه گويى از آسمان افتاده و مرغان، او را مى‌ربايند يا تندباد، او را به نقطه‌اى دور پرتاب مى‌كند'),
(2627, 22, 32, 'اين است [سخن حق‌]، و هر كس شعائر خدا را بزرگ دارد بى‌ترديد آن نشانه تقواى دل‌هاست'),
(2628, 22, 33, 'در آن [قربانى‌ها] براى شما منافعى است [چون شير و پشم‌] تا زمانى معين، سپس محل [قربانى‌] آنها در كنار خانه‌ى كهن است'),
(2629, 22, 34, 'و براى هر امتى قربانگاهى قرار داديم تا نام خدا را بر [ذبح‌] چهارپايانى كه روزى آنها كرده است ببرند، پس [بدانيد كه‌] خداى شما خداى يگانه است، پس به فرمان او گردن نهيد. و فروتنان را بشارت ده'),
(2630, 22, 35, 'همانان كه چون نام خدا ياد شود دل‌هايشان هراس يابد، و آنان كه بر مصيبتى كه بر سرشان آيد صابرند و برپا دارندگان نمازند و از آنچه روزيشان داده‌ايم انفاق مى‌كنند'),
(2631, 22, 36, 'و [قربانى‌] شتران فربه را براى شما از جمله شعائر خدا قرار داديم، در آنها براى شما خير است. پس نام خدا را بر [ذبح‌] آنها در حالى كه ايستاده‌اند ببريد، و چون به پهلو افتادند [و جان دادند] از آنها بخوريد و فقير قانع و فقير سائل را اطعام كنيد. اين گونه آ'),
(2632, 22, 37, 'هرگز گوشت و خون آنها به خدا نخواهد رسيد و ليكن تقواى شماست كه به او مى‌رسد. اين گونه آنها را مسخر شما كرد تا خدا را به خاطر آن كه هدايتتان نموده به بزرگى ياد كنيد. و نيكوكاران را مژده ده'),
(2633, 22, 38, 'قطعا خداوند از كسانى كه ايمان آورده‌اند دفاع مى‌كند [زيرا] خدا هيچ خيانتكار كفر پيشه‌اى را دوست ندارد'),
(2634, 22, 39, 'به كسانى كه جنگ بر آنها تحميل شده اذن [جهاد] داده شده است، چرا كه مورد ظلم قرار گرفته‌اند، و البته خدا بر نصرت آنها تواناست'),
(2635, 22, 40, 'كسانى كه به ناحق از خانه‌هايشان بيرون رانده شدند، چرا كه مى‌گفتند: پروردگار ما خداست. و اگر خدا بعضى از مردم را با بعضى ديگر دفع نمى‌كرد، صومعه‌ها و كليساها و كنيسه‌ها و مساجدى كه نام خدا در آنها بسيار ذكر مى‌شود سخت ويران مى‌شد، و قطعا خدا به كسى ك'),
(2636, 22, 41, 'كسانى كه چون در زمين به آنها توانايى دهيم نماز برپا مى‌دارند و زكات مى‌دهند و امر به معروف و نهى از منكر مى‌كنند، و سر انجام همه كارها با خداست'),
(2637, 22, 42, 'و اگر تو را تكذيب كنند، البته پيش از آنها قوم نوح و عاد و ثمود نيز [پيامبران خود را] تكذيب كردند'),
(2638, 22, 43, 'و نيز قوم ابراهيم و قوم لوط'),
(2639, 22, 44, 'و همچنين اهل مدين و موسى نيز تكذيب شد پس كافران را مهلت دادم، سپس گريبان آنها را گرفتم [بنگر] عقوبت من چگونه بود'),
(2640, 22, 45, 'و چه بسيار شهرها را كه ستمكار بودند هلاكشان كرديم، و اينك آن شهرها بر سقف‌هايش فرو ريخته است، و بسا چاه‌هاى آب كه رها شده و قصرهاى افراشته [كه بى‌صاحب مانده‌اند]'),
(2641, 22, 46, 'آيا در زمين گردش نكردند تا صاحب دل‌هايى شوند كه با آن بينديشند يا گوش‌هايى كه با آن بشنوند؟ در حقيقت چشم‌ها كور نمى‌شود، و ليكن دل‌هايى كه در سينه‌هاست كور مى‌شود'),
(2642, 22, 47, 'و از تو تقاضاى تعجيل در عذاب مى‌كنند، با آن كه هرگز خدا از وعده‌اش تخلف نمى‌كند، و در حقيقت يك روز [از قيامت‌] نزد پروردگار تو مانند هزار سال است از آنچه مى‌شماريد'),
(2643, 22, 48, 'و چه بسيار شهرها كه به آنها مهلت دادم در حالى كه ستمكار بودند، سپس گرفتارشان كردم، و بازگشت به سوى من است'),
(2644, 22, 49, 'بگو: اى مردم! من براى شما فقط هشدار دهنده‌اى آشكارم'),
(2645, 22, 50, 'پس، آنها كه ايمان آوردند و كارهاى شايسته كردند آمرزش و روزى نيكو براى ايشان خواهد بود'),
(2646, 22, 51, 'و كسانى كه در [تخريب‌] آيات ما كوشيدند كه ما را عاجز كنند، آنها اهل دوزخ‌اند'),
(2647, 22, 52, 'و پيش از تو نيز هيچ رسول و پيامبرى را نفرستاديم جز اين كه هرگاه آرزو مى‌كرد [و طرحى براى اهداف رسالت خود مى‌ريخت‌] شيطان در خواسته او القاى [شبهه و يأس‌] مى‌كرد، [اما] خدا آنچه را شيطان القا مى‌كرد محو مى‌گردانيد، سپس خدا آيات خود را استوار مى‌ساخت.'),
(2648, 22, 53, 'تا آنچه را كه شيطان القا مى‌كند آزمودنى براى بيماردلان و سنگدلان قرار دهد، و ستمگران در ستيز بزرگى [با حق‌] گرفتارند'),
(2649, 22, 54, 'و تا دانش يافتگان بدانند، كه قرآن حق است و از جانب پروردگار توست و به آن بگروند و دل‌هايشان در برابر آن خاضع گردد. و خداوند بى‌ترديد راهنماى مؤمنان به راه راست است'),
(2650, 22, 55, 'و كسانى كه كافر شدند همواره از بابت آن در شك‌اند تا آن‌گاه كه قيامت بر آنها در رسد يا عذاب روز عقيم به سراغشان بيايد [كه قادر به جبران گذشته نباشند]'),
(2651, 22, 56, 'در آن روز فرمانروايى از آن خداست [و] ميانشان حكم مى‌كند. سپس كسانى كه ايمان آورده و كارهاى شايسته كرده‌اند در باغ‌هاى پر ناز و نعمت خواهند بود'),
(2652, 22, 57, 'و كسانى كه كافر شدند و آيات ما را تكذيب كردند، براى آنان عذابى خفّت‌بار خواهد بود'),
(2653, 22, 58, 'و آنها كه در راه خدا مهاجرت كردند، سپس كشته شدند يا مردند، قطعا خداوند به آنها رزقى نيكو مى‌بخشد، و بى‌ترديد خداوند بهترين روزى دهندگان است'),
(2654, 22, 59, 'آنان را حتما به جايگاهى كه آن را مى‌پسندند وارد مى‌كند، و شك نيست كه خداوند داناى بردبار است'),
(2655, 22, 60, 'اين [حكم خداست‌] و هر كس به آن اندازه كه به او ستم شده مجازات كند و سپس باز بر او ستم كنند، قطعا خدا او را نصرت دهد همانا خدا بخشنده‌ى آمرزنده است'),
(2656, 22, 61, 'اين [نصرت مظلوم‌] از آن روست كه خدا [قادر است چيزى را بر ديگرى غالب كند چنان كه‌] شب را در روز داخل مى‌كند [و ظلمت مغلوب مى‌شود] و روز را در شب در مى‌آورد و اين كه خدا شنواى بيناست'),
(2657, 22, 62, 'اين از آن روست كه خدا خود حق است و آنچه به جاى او مى‌خوانند باطل است، و اين خداست كه والا و بزرگ است'),
(2658, 22, 63, 'آيا نديدى كه خدا از آسمان آبى مى‌فرستد آنگاه زمين سرسبز مى‌گردد؟ آرى، خداوند باريك بين و آگاه است'),
(2659, 22, 64, 'آنچه در آسمان‌ها و آنچه در زمين است از آن اوست، و در حقيقت خداست كه بى‌نياز و ستوده است'),
(2660, 22, 65, 'آيا نديدى كه خدا آنچه را در زمين است مسخر شما كرده و كشتى‌ها در دريا به امر او مى‌روند و آسمان را نگاه مى‌دارد كه جز به اذن او بر زمين نيفتد؟ بى‌ترديد خداوند نسبت به مردم رئوف و مهربان است'),
(2661, 22, 66, 'و اوست آن كه شما را زنده كرد، سپس شما را مى‌ميراند و آن‌گاه دوباره زنده‌تان مى‌كند. حقا كه انسان سخت ناسپاس است'),
(2662, 22, 67, 'ما براى هر امتى عبادتى قرار داديم كه بدان عمل مى‌كنند پس نبايد با تو در اين امر مجادله كنند. و به سوى پروردگارت بخوان كه تو به راستى بر هدايتى مستقيم هستى'),
(2663, 22, 68, 'و اگر با تو مجادله كنند بگو: خدا به آنچه مى‌كنيد داناتر است'),
(2664, 22, 69, 'خدا روز قيامت ميان شما در مورد آنچه اختلاف مى‌كرديد حكم خواهد كرد'),
(2665, 22, 70, 'آيا ندانسته‌اى كه خداوند آنچه را در آسمان و زمين است مى‌داند؟ اينها همه در كتابى [كه علم الهى است مسطور] است. قطعا اين امر بر خدا آسان است'),
(2666, 22, 71, 'و به جاى خدا چيزى را مى‌پرستند كه بر [تأييد] آن حجّتى نازل نكرده و بدان آگاهى ندارند. و ستمكاران را هيچ ياورى نيست'),
(2667, 22, 72, 'و چون آيات روشن ما بر آنها تلاوت شود، در چهره‌ى كسانى كه كافر شدند آثار ترشرويى و انكار را خواهى ديد. چيزى نمانده است كه بر كسانى كه آيات ما را بر ايشان تلاوت مى‌كنند حمله‌ور شوند. بگو: آيا شما را به بدتر از اين [كه در كمين شماست‌] خبر بدهم؟ آن آتش د'),
(2668, 22, 73, 'اى مردم مثلى زده شده است، پس بدان گوش فرا دهيد: كسانى را كه جز خدا مى‌خوانيد، هرگز مگسى را خلق نتوانند كرد هر چند براى آفريدن آن گرد آيند، و اگر آن مگس چيزى از آنها بربايد نمى‌توانند آن را پس بگيرند. طالب و مطلوب هر دو ناتوانند'),
(2669, 22, 74, 'منزلت خدا را چنان كه در خور اوست نشناختند. به راستى خدا نيرومند شكست ناپذير است'),
(2670, 22, 75, 'خدا از ميان فرشتگان رسولانى بر مى‌گزيند و از مردم نيز. بى‌گمان خدا شنواى بيناست'),
(2671, 22, 76, 'آنچه پيش رويشان و آنچه پشت سرشان است (گذشته و آينده‌ى آنها را) مى‌داند و همه كارها به خدا بازگردانده مى‌شود'),
(2672, 22, 77, 'اى كسانى كه ايمان آورده‌ايد! ركوع كنيد و سجده آوريد و پروردگارتان را بندگى كنيد و كار خير انجام دهيد، باشد كه رستگار شويد'),
(2673, 22, 78, 'و در راه خدا چنان كه شايسته اوست جهاد كنيد. او شما را برگزيد و بر شما در اين دين سختى قرار نداد كه آيين پدرتان ابراهيم است. او شما را از پيش و هم در اين قرآن مسلمان ناميد تا اين پيامبر بر شما گواه باشد و شما بر مردم گواه باشيد. پس نماز را بر پا داري');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'به راستى كه مؤمنان رستگار شدند'),
(2675, 23, 2, 'آنان كه در نمازشان خاشع‌اند'),
(2676, 23, 3, 'و آنان كه از بيهوده رويگردانند'),
(2677, 23, 4, 'و آنان كه زكات مى‌پردازند'),
(2678, 23, 5, 'و كسانى كه شرمگاه خود را حفظ مى‌كنند'),
(2679, 23, 6, 'مگر در مورد همسرانشان يا كنيزانى كه مالك آنها شده‌اند كه در اين صورت بر آنان ملامتى نيست'),
(2680, 23, 7, 'پس كسانى كه سواى اين را بجويند، آنان حتما متجاوزند'),
(2681, 23, 8, 'و آنان كه امانت‌ها و پيمان خود را رعايت مى‌كنند'),
(2682, 23, 9, 'و آنان كه بر نمازهايشان محافظت دارند'),
(2683, 23, 10, 'آنها هستند كه وارثان‌اند'),
(2684, 23, 11, '[وارثانى‌] كه فردوس را به ارث مى‌برند و در آن جاودان مى‌مانند'),
(2685, 23, 12, 'و همانا انسان را از عصاره‌اى از گل آفريديم'),
(2686, 23, 13, 'سپس او را به صورت نطفه‌اى در جايگاهى استوار قرار داديم'),
(2687, 23, 14, 'آن‌گاه نطفه را لخته خونى كرديم، و لخته خون را به پاره گوشتى مبدل نموديم پس آن را استخوان‌ها كرديم، آن‌گاه به استخوان‌ها گوشتى پوشانديم، سپس آن را آفرينشى ديگر داديم [و در آن روح دميديم‌]. پس پرخير و پاينده است خدايى كه بهترين آفرينندگان است'),
(2688, 23, 15, 'سپس شما بعد از اين [مراحل‌] قطعا خواهيد مرد'),
(2689, 23, 16, 'آن‌گاه شما روز رستاخيز برانگيخته مى‌شويد'),
(2690, 23, 17, 'و به راستى ما بالاى سر شما هفت راه [هفت آسمان‌] آفريديم و ما از آفرينش غافل نبوده‌ايم'),
(2691, 23, 18, 'و از آسمان به اندازه، آب نازل كرديم، و آن را در زمين جاى داديم و ما مسلما براى از بين بردن آن قادريم'),
(2692, 23, 19, 'پس براى شما به وسيله آن، باغ‌هايى از نخل و انگور پديدار كرديم كه در آنها براى شما ميوه‌هاى فراوان است و از آنها مى‌خوريد'),
(2693, 23, 20, 'و نيز درختى [براى شما پديد آورديم‌] كه از طور سينا بر مى‌آيد و روغن [زيتون‌] مى‌دهد و خورشى براى خورندگان است'),
(2694, 23, 21, 'و البته براى شما در چهارپايان درس عبرتى است از آنچه در شكم آنهاست به شما [شير] مى‌نوشانيم و براى شما در آنها منافع زيادى است و از آنها مى‌خوريد'),
(2695, 23, 22, 'و بر آنها و بر كشتى‌ها سوار مى‌شويد'),
(2696, 23, 23, 'به راستى نوح را به سوى قومش فرستاديم، پس [به آنها] گفت: اى قوم من! خدا را بندگى كنيد، كه شما را غير او خدايى نيست. آيا [از او] نمى‌ترسيد'),
(2697, 23, 24, 'و اشراف قومش كه كافر بودند گفتند: اين جز بشرى همانند شما نيست، مى‌خواهد بر شما برترى جويد، و اگر خدا مى‌خواست قطعا فرشتگانى مى‌فرستاد. ما در ميان پدران گذشته خود چنين چيزى نشنيده‌ايم'),
(2698, 23, 25, 'او نيست جز مردى كه جنون دارد، پس تا چندى در باره‌اش منتظر بمانيد'),
(2699, 23, 26, 'نوح گفت: پروردگارا! مرا در برابر اين كه تكذيبم مى‌كنند يارى كن'),
(2700, 23, 27, 'پس به او وحى كرديم كه زير نظر ما و با وحى ما كشتى بساز، و چون فرمان ما رسيد و تنور به فوران آمد، از هر نوع حيوانى دو تا [نر و ماده‌]، و همچنين خانواده‌ات را روانه كشتى كن، به جز كسى از آنان كه حكم عذاب بر او صادر شده است، و در باره‌ى كسانى كه ظلم كرد'),
(2701, 23, 28, 'و چون تو با همراهانت بر كشتى نشستى، بگو: ستايش خدايى را كه ما را از چنگ گروه ستمگران رهانيد'),
(2702, 23, 29, 'و بگو: پروردگارا! مرا در جايى پر بركت فرود آور، كه تو بهترين فرود آرندگانى'),
(2703, 23, 30, 'در حقيقت، در اين [ماجرا] عبرت‌هايى است، و ما همواره آزمايش كننده بوده‌ايم'),
(2704, 23, 31, 'سپس بعد از آنان نسل‌هايى ديگر پديد آورديم'),
(2705, 23, 32, 'و در ميانشان پيامبرى از خودشان روانه كرديم كه خدا را بپرستيد، جز او براى شما معبودى نيست. آيا پروا نمى‌كنيد'),
(2706, 23, 33, 'و اشراف قومش كه كافر شدند و ديدار آخرت را دروغ پنداشتند و در زندگى دنيا آنها را متنعّم ساخته بوديم، گفتند: اين، بشرى جز همانند شما نيست، از آنچه شما مى‌خوريد، مى‌خورد، و از آنچه مى‌نوشيد، مى‌نوشد'),
(2707, 23, 34, 'و اگر از بشرى مثل خودتان اطاعت كنيد، البته در آن صورت زيانكاريد'),
(2708, 23, 35, 'آيا به شما وعده مى‌دهد كه وقتى مرديد و خاك و استخوان شديد باز شما [از گور، زنده‌] بيرون آورده مى‌شويد'),
(2709, 23, 36, 'دور است، دور، آنچه وعده داده مى‌شويد'),
(2710, 23, 37, 'جز همين زندگانى دنيوى ما، هيچ نيست [گروهى‌] مى‌ميريم و [و گروهى به جاى آنها] زنده مى‌شويم و ما [مجددا] زنده شدنى نيستيم'),
(2711, 23, 38, 'او جز مردى كه بر خدا دروغ مى‌بندد نيست و ما او را باور نداريم'),
(2712, 23, 39, 'گفت: پروردگارا! در برابر اين كه تكذيبم كردند، مرا يارى كن'),
(2713, 23, 40, 'فرمود: به همين زودى سخت پشيمان خواهند شد'),
(2714, 23, 41, 'پس صيحه آنها را، به حق فرو گرفت و آنها را [چون‌] خاشاكى قرار داديم. پس نفرين بر گروه ستمكاران'),
(2715, 23, 42, 'آن‌گاه پس از آنان نسل‌هاى ديگرى پديد آورديم'),
(2716, 23, 43, 'هيچ امّتى از اجل خود، نه پيش مى‌افتد و نه تأخير مى‌كند'),
(2717, 23, 44, 'سپس پيامبران خود را پى‌درپى فرستاديم. هر بار كه پيامبرى به سوى امتش آمد تكذيبش كردند، ما نيز آنها را از پى يكديگر هلاك نموديم و آنها را عبرت‌ها و داستان‌ها كرديم. پس نابود باد قومى كه ايمان نمى‌آورند'),
(2718, 23, 45, 'سپس موسى و برادرش هارون را با آيات خود و حجتى آشكار فرستاديم'),
(2719, 23, 46, 'به سوى فرعون و سران قوم او، ولى تكبّر نمودند و مردمى گردنكش بودند'),
(2720, 23, 47, 'پس گفتند: آيا به دو انسانى كه مثل ما هستند ايمان بياوريم در حالى كه قومشان بردگان ما هستند'),
(2721, 23, 48, 'در نتيجه آن دو را تكذيب كردند و از هلاك شدگان گشتند'),
(2722, 23, 49, 'و به راستى ما به موسى كتاب آسمانى داديم، باشد كه هدايت يابند'),
(2723, 23, 50, 'و پسر مريم و مادرش را آيتى ساختيم و آن دو را در سرزمين مرتفعى كه جاى زيستن و داراى آب جارى بود، جاى داديم'),
(2724, 23, 51, 'اى پيامبران! از پاكيزه‌ها بخوريد و كار شايسته كنيد كه من به آنچه انجام مى‌دهيد آگاهم'),
(2725, 23, 52, 'و اين آيين همه شماست، آيين يگانه (با يك هدف و با يك خدا)، و من پروردگار شمايم، پس از من پروا داريد'),
(2726, 23, 53, 'ولى آنها دينشان را ميان خود شعبه‌ها كردند و [فرقه‌ها شدند كه‌] هر فرقه‌اى به آنچه نزدشان است دلخوش‌اند'),
(2727, 23, 54, 'پس آنها را تا چندى در ورطه گمراهى‌شان رها كن'),
(2728, 23, 55, 'آيا مى‌پندارند كه آنچه از مال و پسران كه بديشان مى‌دهيم'),
(2729, 23, 56, 'در رساندن نيكى‌ها شتاب مى‌كنيم؟ نه، بلكه آنها نمى‌فهمند'),
(2730, 23, 57, 'همانا كسانى كه از مهابت پروردگارشان ترسانند'),
(2731, 23, 58, 'و كسانى كه به آيات پروردگارشان ايمان مى‌آورند'),
(2732, 23, 59, 'و آنان كه به پروردگارشان شرك نمى‌آورند'),
(2733, 23, 60, 'و كسانى كه آنچه [از مال و جان‌] مى‌دهند، در حالى مى‌دهند كه دل‌هايشان ترسان است از اين كه به سوى پروردگارشان باز خواهند گشت'),
(2734, 23, 61, 'آنان در كارهاى نيك مى‌شتابند و هم آنان در انجام آن پيشتازند'),
(2735, 23, 62, 'و هيچ كس را جز به قدر توانش تكليف نمى‌كنيم، و نزد ما كتابى است كه به حق سخن مى‌گويد، و آنان مورد ستم قرار نمى‌گيرند'),
(2736, 23, 63, 'نه، بلكه دل‌هاى آنان از اين [حقيقت‌] در غفلت است، و آنان كارهاى [نارواى ديگرى‌] جز اين دارند كه پيوسته مرتكب مى‌شوند'),
(2737, 23, 64, 'تا وقتى كه عيّاشان آنها را به عذاب گرفتار ساختيم، به ناگاه ناله‌هاى دردناك سر مى‌دهند'),
(2738, 23, 65, 'امروز زارى مكنيد كه قطعا شما از جانب ما يارى نخواهيد شد'),
(2739, 23, 66, 'همانا آيات من بر شما خوانده مى‌شد و شما به عقب خود بر مى‌گشتيد'),
(2740, 23, 67, 'و متكبّرانه در شب‌نشينى خود از آن بد مى‌گفتيد'),
(2741, 23, 68, 'آيا در اين سخن تفكّر نكرده‌اند، يا چيزى براى آنها آمده كه براى پدران پيشين آنها نيامده است'),
(2742, 23, 69, 'يا پيامبر خود را در دست نشناخته‌اند كه انكارش مى‌كنند'),
(2743, 23, 70, 'يا مى‌گويند او جنون دارد؟ نه [چنين نيست‌] بلكه او [دين‌] حق را برايشان آورده، ولى بيشترشان حقيقت را خوش ندارند'),
(2744, 23, 71, 'و اگر حق از هوس‌هاى آنها پيروى مى‌كرد، قطعا آسمان‌ها و زمين و هر كه در آنهاست تباه مى‌شد، ولى ما پندنامه‌شان (قرآن) را بديشان داديم اما آنها از [آنچه مايه‌] يادآوريشان است رويگردانند'),
(2745, 23, 72, 'آيا از ايشان مزدى مى‌خواهى؟ ولى مزد پروردگارت بهتر است كه بهترين روزى دهندگان است'),
(2746, 23, 73, 'و به راستى تو حقا آنها را به راه راست مى‌خوانى'),
(2747, 23, 74, 'و كسانى كه به آخرت ايمان ندارند، حتما از راه [درست‌] منحرف‌اند'),
(2748, 23, 75, 'و اگر به آنها رحم كنيم و بلايى كه بدان گرفتارند از ايشان برطرف سازيم، باز در طغيان خود، سرگشته لجاجت مى‌ورزند'),
(2749, 23, 76, 'و به راستى ايشان را گرفتار عذاب كرديم [تا بيدار شوند]، ولى در مقابل پروردگارشان، نه فروتنى كردند، و نه تضرّع مى‌كردند'),
(2750, 23, 77, 'تا وقتى كه درى از عذاب سخت به رويشان مى‌گشاييم، كه به ناگاه از همه جا قطع اميد كنند'),
(2751, 23, 78, 'و اوست [خدايى‌] كه براى شما گوش و چشم و دل پديد آورد، امّا كمتر سپاسگزارى مى‌كنيد'),
(2752, 23, 79, 'و اوست كه شما را در زمين پديد آورد و به سوى او محشور مى‌شويد'),
(2753, 23, 80, 'و اوست آن كه زنده مى‌كند و مى‌ميراند و آمد و شد شب و روز از اوست، پس چرا تعقل نمى‌كنيد'),
(2754, 23, 81, 'بلكه آنها نيز همان سخنان گفتند كه پيشينيان مى‌گفتند'),
(2755, 23, 82, 'گفتند: آيا چون مرديم و خاك و استخوان شديم، واقعا باز برانگيخته مى‌شويم'),
(2756, 23, 83, 'درست همين را قبلا ما و پدرانمان وعده داده شده‌ايم اينها جز افسانه‌هاى پيشينيان نيست'),
(2757, 23, 84, 'بگو: اگر مى‌دانيد [بگوييد] زمين و هر كه در آن است به چه كسى تعلق دارد'),
(2758, 23, 85, 'به زودى خواهند گفت: از آن خداست. بگو: پس آيا متذكّر نمى‌شويد'),
(2759, 23, 86, 'بگو: كيست پروردگار آسمان‌هاى هفتگانه و صاحب عرش بزرگ'),
(2760, 23, 87, 'به زودى خواهند گفت: از آن خداست. بگو: پس آيا پروا نمى‌كنيد'),
(2761, 23, 88, 'بگو: اگر مى‌دانيد، فرمانروايى همه موجودات در دست كيست، در حالى كه او پناه دهد و به كسى [از عذاب او] پناه داده نشود'),
(2762, 23, 89, 'خواهند گفت: از آن خداست. بگو: پس چگونه دستخوش افسون مى‌شويد'),
(2763, 23, 90, 'بلكه حقيقت را برايشان آورديم و قطعا آنان دروغگويند'),
(2764, 23, 91, 'خدا فرزندى اختيار نكرده و با او معبودى ديگر نيست، و اگر چنين مى‌بود قطعا هر خدايى آنچه را آفريده بود با خود مى‌برد، و حتما بعضى از آنان بر بعضى ديگر برترى مى‌جستند، منزه است خدا از آنچه وصف مى‌كنند'),
(2765, 23, 92, 'داناى نهان و آشكار است، پس برتر است از آنچه با او شريك مى‌گردانند'),
(2766, 23, 93, 'بگو: پروردگارا! اگر عذابى را كه به آنها وعده داده مى‌شود به من بنمودى'),
(2767, 23, 94, 'بار خدايا! مرا [در اين عذاب‌ها] با قوم ستمكار قرار مده'),
(2768, 23, 95, 'و بى‌ترديد ما قادريم عذابى را كه به آنها وعده مى‌دهيم به تو نشان دهيم'),
(2769, 23, 96, 'سخن بد [آنها] را به طريقى كه نيكوتر است دفع كن، ما به آنچه وصف مى‌كنند داناتريم'),
(2770, 23, 97, 'و بگو: پروردگارا! از وسوسه‌هاى شياطين به تو پناه مى‌برم'),
(2771, 23, 98, 'و اى پروردگار من! از حضور آنها نزد من، به تو پناه مى‌آورم'),
(2772, 23, 99, 'تا آن‌گاه كه مرگ هر يك از ايشان فرا رسد مى‌گويد: پروردگارا! مرا باز گردانيد'),
(2773, 23, 100, 'شايد من در آنچه پشت سر گذاشته‌ام، كار نيكى انجام دهم. حاشا، اين سخنى است كه او [لفظا] گوينده‌ى آن است و پشت سرشان برزخى است تا روزى كه برانگيخته شوند'),
(2774, 23, 101, 'پس آن‌گاه كه در صور دميده شود، ديگر آن روز ميانشان نسبت خويشاوندى وجود ندارد و از حال يكديگر نمى‌پرسند'),
(2775, 23, 102, 'پس هر كه ميزان اعمالش سنگين باشد، آنها حقا رستگاران‌اند'),
(2776, 23, 103, 'و هر كه ميزان اعمالش سبك باشد، آنان كسانى‌اند كه به خويشتن زيان زده‌اند و هميشه در جهنم مى‌مانند'),
(2777, 23, 104, 'آتش چهره‌ى آنها را مى‌سوزاند و آنها در آن زشت منظرند'),
(2778, 23, 105, '[گفته شود:] مگر آيات من بر شما تلاوت نمى‌شد و شما آنها را تكذيب مى‌كرديد'),
(2779, 23, 106, 'مى‌گويند: پروردگارا! بدبختى‌مان بر ما چيره شد و ما مردمى گمراه بوديم'),
(2780, 23, 107, 'پروردگارا! ما را از اين آتش بيرون آر، پس اگر [به خلاف‌] برگشتيم، در آن صورت ستمگر خواهيم بود'),
(2781, 23, 108, 'خدا گويد: دور شويد در آن، و با من سخن نگوييد'),
(2782, 23, 109, 'همانا گروهى از بندگان من بودند كه مى‌گفتند: پروردگارا! ايمان آورديم، پس بر ما ببخشاى و به ما رحم كن كه تو بهترين مهربانانى'),
(2783, 23, 110, 'امّا شما آنها را به ريشخند گرفتيد تا اين كه [شما را رها كردند و] ذكر مرا از ياد شما بردند و شما همچنان بر آنان مى‌خنديديد'),
(2784, 23, 111, 'من امروز آنها را براى اين كه صبر كردند پاداششان دادم، كه بى‌ترديد ايشان‌اند كاميابان'),
(2785, 23, 112, '[خداوند] فرمايد: چه مدت به حساب سال‌ها در زمين به سر برديد'),
(2786, 23, 113, 'گويند: يك روز يا پاره‌اى از يك روز به سر برديم، پس از حسابگران بپرس'),
(2787, 23, 114, 'گويد: جز اندكى درنگ نكرده‌ايد، اگر به درستى مى‌دانستيد'),
(2788, 23, 115, 'پس آيا پنداشته‌ايد كه شما را بيهوده آفريده‌ايم و شما به نزد ما بازگردانده نمى‌شويد'),
(2789, 23, 116, 'پس خدا والاست، آن فرمانروايى كه حق است، هيچ خدايى جز او نيست، و او پروردگار [و صاحب‌] عرش كريم است'),
(2790, 23, 117, 'هر كه با خداى يكتا معبود ديگرى را بخواند كه هيچ برهانى براى آن ندارد، حساب او فقط نزد پروردگارش خواهد بود. بى‌گمان كافران رستگار نمى‌شوند'),
(2791, 23, 118, 'و بگو: پروردگارا! ببخشاى و رحمت‌آور كه تو بهترين مهربانانى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'سوره‌اى است كه آن را نازل كرده، [احكامش را] واجب نموده، در آن آياتى روشن آورديم تا شما پند گيريد'),
(2793, 24, 2, 'زن زناكار و مرد زناكار، هر يك از آن دو را صد تازيانه بزنيد، و اگر به خدا و روز واپسين ايمان داريد مبادا در دين خدا نسبت به آن دو دستخوش ترحّم گرديد، و بايد گروهى از مؤمنان در كيفر آن دو حضور يابند'),
(2794, 24, 3, 'مرد زناكار جز زن زناكار يا مشرك را به همسرى نمى‌گيرد، و زن زناكار را جز مرد زناكار يا مشرك نمى‌گيرد، و اين [ازدواج‌] بر مؤمنان حرام شده است'),
(2795, 24, 4, 'و كسانى كه به زنان پاكدامن نسبت زنا مى‌دهند، سپس چهار شاهد نمى‌آورند، به آنها هشتاد تازيانه بزنيد و هيچ‌گاه شهادتى از آنها نپذيريد، و اينها بى‌شك فاسق‌اند'),
(2796, 24, 5, 'مگر كسانى كه بعد از آن توبه كرده و اصلاح نمودند كه خداوند البته آمرزنده‌ى مهربان است'),
(2797, 24, 6, 'و كسانى كه همسران خود را به زنا متهم مى‌كنند، و جز خودشان شاهدان ديگرى ندارند، گواهى يكى از آنها چهار بار گواهى، با سوگند به خداست [مبنى بر اين‌] كه بى‌ترديد او از راستگويان است'),
(2798, 24, 7, 'و بار پنجم بگويد: لعنت خدا بر او باد اگر از دروغگويان باشد'),
(2799, 24, 8, 'و از آن [زن‌] كيفر را بر مى‌دارد اين كه چهار بار به خدا سوگند ياد كند كه شوهر او حتما از دروغگويان است'),
(2800, 24, 9, 'و بار پنجم بگويد كه خشم خدا بر او باد اگر [مرد] از راستگويان باشد'),
(2801, 24, 10, 'و اگر فضل و رحمت خدا بر شما نبود و اين كه خدا توبه‌پذير حكيم است [هلاك مى‌شديد]'),
(2802, 24, 11, 'همانا كسانى كه آن تهمت عظيم را [در باره‌ى يكى از زنان پيامبر] به ميان آوردند، دسته‌اى از شما بودند. آن تهمت را براى خود شرّ مپنداريد، بلكه آن براى شما خير و مصلحت است. بر عهده‌ى هر فردى از آنها سهمى از گناه است، و آن كه بخش عمده‌ى آن دروغ‌سازى را بر'),
(2803, 24, 12, 'چرا هنگامى كه آن [تهمت‌] را شنيديد، مردان و زنان مؤمن نسبت به خودشان گمان نيك نبردند و نگفتند: اين تهمتى آشكار است'),
(2804, 24, 13, 'چرا بر آن [ادعا] چهار شاهد نياوردند؟ پس حالا كه شاهدان را نياوردند، اينان خود در پيشگاه خدا دروغگويند'),
(2805, 24, 14, 'و اگر فضل خدا و رحمتش در دنيا و آخرت شامل شما نمى‌شد، قطعا به [سزاى‌] بهتانى كه وارد آن شديد، به شما عذابى بزرگ مى‌رسيد'),
(2806, 24, 15, 'آن‌گاه كه آن [بهتان‌] را از زبان يكديگر مى‌گرفتيد و خبرى را كه بدان علم نداشتيد، دهان به دهان مى‌گفتيد و مى‌پنداشتيد كه كارى كوچك و ساده است، در حالى كه آن [امر] نزد خدا بسى بزرگ بود'),
(2807, 24, 16, 'و [گر نه‌] چرا وقتى آن را شنيديد نگفتيد: ما را نشايد كه در اين باره سخن گوييم، [خداوندا] منزهى تو، اين تهمتى بزرگ است'),
(2808, 24, 17, 'خدا شما را اندرز مى‌دهد كه ديگر هيچ‌گاه، نظير آن را تكرار نكنيد اگر مؤمنيد'),
(2809, 24, 18, 'و خدا براى شما آيات را بيان مى‌كند و خداى داناى حكيم است'),
(2810, 24, 19, 'كسانى كه دوست دارند زشتكارى در حق مؤمنان شايع شود، براى آنها در دنيا و آخرت عذابى پردرد خواهد بود، و خدا مى‌داند و شما نمى‌دانيد'),
(2811, 24, 20, 'و اگر فضل و رحمت خدا بر شما نبود، و اين كه خدا رئوف و مهربان است [هلاك مى‌شديد]'),
(2812, 24, 21, 'اى كسانى كه ايمان آورده‌ايد! از گام‌هاى شيطان پيروى نكنيد، و هر كس پيروى گام‌هاى شيطان كند، [بداند كه‌] او قطعا به فحشا و منكر امر مى‌كند. و اگر فضل خدا و رحمتش بر شما نبود، هرگز كسى از شما پاك نمى‌شد، ولى خداست كه هر كس را بخواهد پاك مى‌گرداند، و خد'),
(2813, 24, 22, 'و صاحبان بخشش و توانگران شما نبايد از دادن [مال‌] به خويشاوندان و تهيدستان و مهاجران راه خدا كوتاهى كنند، و بايد عفو كنند و چشم بپوشند. مگر دوست نداريد كه خدا شما را بيامرزد؟ و خدا آمرزنده‌ى مهربان است'),
(2814, 24, 23, 'بى‌گمان كسانى كه به زنان پاكدامن بى‌خبر [از فحشا] و با ايمان، نسبت زنا مى‌دهند، در دنيا و آخرت لعنت شده‌اند و براى آنها عذابى سخت خواهد بود'),
(2815, 24, 24, 'روزى كه زبان‌ها و دست‌ها و پاهايشان بر ضد آنها در باره‌ى آنچه انجام مى‌دادند شهادت مى‌دهند'),
(2816, 24, 25, 'آن روز خدا جزاى واقعى آنها را بى‌كم و كاست مى‌دهد، و مى‌فهمند كه خداوند همان حق آشكار است'),
(2817, 24, 26, 'زنان پليد براى مردان پليد و مردان پليد براى زنان پليدند، و زنان پاك براى مردان پاك و مردان پاك براى زنان پاك‌اند. آنها از آنچه (تهمت زنا) در باره‌شان مى‌گويند بركنارند. براى آنها آمرزش و رزقى كريمانه خواهد بود'),
(2818, 24, 27, 'اى كسانى كه ايمان آورده‌ايد! به خانه‌هايى كه خانه‌هاى شما نيست، [سرزده‌] داخل نشويد، تا آشنايى دهيد و اجازه خواهيد و بر اهل آن سلام كنيد. اين براى شما بهتر است، باشد كه پند گيريد'),
(2819, 24, 28, 'و اگر در خانه كسى را نيافتيد، داخل آن نشويد تا به شما اجازه داده شود و اگر به شما گفته شد: برگرديد، برگرديد، كه آن براى شما پاكيزه‌تر است، و خدا به آنچه انجام مى‌دهيد داناست'),
(2820, 24, 29, 'بر شما گناهى نيست كه به خانه‌هاى غير مسكونى [و اماكن عمومى‌] كه كالايى در آن داريد داخل شويد، و خدا آنچه را عيان كنيد و آنچه را نهان داريد مى‌داند'),
(2821, 24, 30, 'به مردان با ايمان بگو: ديده [از نامحرم‌] فرونهند و پاكدامنى خود را حفظ كنند، كه اين براى آنها پاكيزه‌تر است. همانا خدا به آنچه مى‌كنند آگاه است'),
(2822, 24, 31, 'و به زنان با ايمان بگو: ديدگان خود را [از نامحرم‌] فرو بندند و دامان خويش را حفظ كنند و زينت خود را جز آنچه نمايان است [مانند دست و صورت‌] آشكار نسازند، و بايد روسرى‌هاى خود را بر سينه‌هاى خويش فرو اندازند و زينت و آرايش خود را آشكار نكنند جز براى شو'),
(2823, 24, 32, 'و زنان و مردان مجرد خود و بردگان و كنيزان درستكارتان را همسر دهيد. اگر تنگدست باشند، خداوند آنها را به فضل خويش بى‌نياز خواهد كرد، و خدا گشايشگر داناست'),
(2824, 24, 33, 'و كسانى كه امكانات زناشويى نمى‌يابند، بايد عفت بورزند تا خدا آنها را از كرم خويش بى‌نياز گرداند. و از مملوكان شما، كسانى كه خواستار مكاتبه [باز خريد] خويش هستند، اگر در آنها خيرى (توانايى) يافتيد، قرار بازخريد آنها را بنويسيد، و از مالى كه خدا به شم'),
(2825, 24, 34, 'و البته به سوى شما آياتى روشنگر، و سرگذشتى از كسانى كه پيش از شما درگذشتند و موعظه‌اى براى اهل تقوا نازل كرديم'),
(2826, 24, 35, 'خدا نور آسمان‌ها و زمين است. مثل نور او (خصلت ايمان و هدايت) چون چراغدانى است كه در آن چراغى و آن چراغ در شيشه‌اى است. آن شيشه گويى اخترى درخشان است كه از درخت با بركت زيتونى كه نه شرقى است و نه غربى [بلكه در وسط باغ آفتاب مى‌خورد] افروخته مى‌شود. رو'),
(2827, 24, 36, '[آن نور هدايت‌] در خانه‌هايى است كه خدا رخصت داده كه ارجمندش دارند و نام خدا در آنها ياد شود. در آن خانه‌ها هر بامداد و شامگاه او را نيايش مى‌كنند'),
(2828, 24, 37, 'مردانى كه نه تجارت و نه داد و ستدى آنها را از ياد خدا و برپا داشتن نماز و دادن زكات غافل نمى‌كند، و از روزى كه دل‌ها و ديده‌ها در آن دگرگون مى‌شود بيم دارند'),
(2829, 24, 38, 'تا خدا بهتر از آنچه انجام داده‌اند به ايشان جزا دهد و از كرم خود افزونشان كند، و خدا هر كس را بخواهد بى‌حساب روزى مى‌دهد'),
(2830, 24, 39, 'و كسانى كه كافر شدند، اعمالشان چون سرابى در بيابانى هموار است كه تشنه آن را آب مى‌پندارد، تا چون بدان رسد مى‌بينيد آن چيزى نبوده، و خدا را نزد آن مى‌يابد و جزاى او را به تمام مى‌دهد. و خدا سريع الحساب است'),
(2831, 24, 40, 'يا [اعمالشان‌] مانند تاريكى‌هايى در درياى عميق و پهناور است كه موجى آن را مى‌پوشاند، و روى آن موجى ديگر است و بر فرازش ابرى است. ظلمت‌هايى است كه بر روى يكديگر قرار گرفته است. وقتى دستش را بيرون آورد ممكن نيست آن را ببيند. و هر كه را خدا برايش نورى ق'),
(2832, 24, 41, 'آيا نديده‌اى كه هر كه در آسمان‌ها و زمين است، و نيز مرغانى كه در پروازند خداى را تسبيح مى‌گويند، همگى دعا و نيايش خود را مى‌دانند، و خدا به آنچه مى‌كنند آگاه است'),
(2833, 24, 42, 'و فرمانروايى آسمان‌ها و زمين از آن خداست و بازگشت [همه‌] به سوى خداست'),
(2834, 24, 43, 'آيا نديده‌اى كه خدا ابرهايى را مى‌راند، سپس ميان اجزاى آن پيوند مى‌دهد، آن‌گاه ابرى انبوه پديد مى‌آورد، پس باران را مى‌بينى كه از لاى‌به‌لاى آن بيرون مى‌آيد؟ و از آسمان، از ابرهاى كوه پيكر كه در آن است تگرگى نازل مى‌كند، و هر كه را بخواهد با آن مى‌زن'),
(2835, 24, 44, 'خداست كه شب و روز را جابه‌جا مى‌كند. به راستى در اين [تبديل‌] براى صاحبان بصيرت درس عبرتى است'),
(2836, 24, 45, 'و خداست كه هر جنبنده‌اى را از آب آفريد. پس پاره‌اى از آنها بر روى شكم راه مى‌روند، و پاره‌اى از آنها بر روى دو پا و بعضى از آنها بر روى چهار پا راه مى‌روند. خدا هر چه بخواهد مى‌آفريند. بى‌ترديد خدا بر هر چيزى تواناست'),
(2837, 24, 46, 'البته ما آياتى روشنگر نازل كرديم، و خدا هر كه را بخواهد به راه راست هدايت مى‌كند'),
(2838, 24, 47, 'و مى‌گويند: به خدا و رسول او ايمان آورديم و فرمان برديم، آن‌گاه دسته‌اى از ايشان پس از اين [ادّعا] باز مى‌گردند، و آنها مؤمن نيستند'),
(2839, 24, 48, 'و چون به سوى خدا و پيامبر او خوانده شوند تا ميان آنها داورى كند، ناگاه دسته‌اى از آنها روى بر مى‌تابند'),
(2840, 24, 49, 'و اگر [در موارد نزاع‌] حكم به سود آنان باشد، مطيعانه به سوى او مى‌آيند'),
(2841, 24, 50, 'آيا در دل‌هايشان بيمارى است يا به شك افتاده‌اند، يا از آن مى‌ترسند كه خدا و فرستاده‌اش به آنها ستم كنند؟ نه، بلكه خودشان ستمكارند'),
(2842, 24, 51, '[امّا] سخن مؤمنان، وقتى به سوى خدا و پيامبرش خوانده شوند تا ميانشان داورى كند، تنها اين است كه مى‌گويند: شنيديم و فرمان برديم، و اينان همان رستگارانند'),
(2843, 24, 52, 'و هر كه خدا و فرستاده‌ى او را فرمان برد و از خدا بترسد و از [مخالفت امرش‌] بپرهيزد، اينانند كه كامياب خواهند بود'),
(2844, 24, 53, 'و با سوگندهاى سخت خود قسم خوردند كه اگر به آنها فرمان دهى، بى‌شك [براى جهاد] بيرون خواهند آمد. بگو: سوگند مخوريد. اطاعتى نيكو و خالصانه [از پيامبر لازم‌] است البته خداوند به آنچه مى‌كنيد آگاه است'),
(2845, 24, 54, 'بگو: اطاعت خدا كنيد و پيامبر را فرمان بريد، پس اگر روى برتابيد، پيامبر مسئول چيزى است كه بر او تكليف شده و شما مسئول چيزى هستيد كه بدان تكليف شده‌ايد. اما اگر اطاعتش كنيد، هدايت خواهيد يافت. و بر پيامبر [حكمى‌] جز ابلاغ آشكار نيست'),
(2846, 24, 55, 'خدا به كسانى از شما كه ايمان آورده و كارهاى شايسته كرده‌اند، وعده داده است كه حتما آنها را در زمين جانشين كند، چنان كه اسلافشان را جانشين كرد، و دينشان را كه برايشان پسنديده، براى آنها استقرار بخشد و خوفشان را به ايمنى بدل سازد، آنان مرا عبادت مى‌كنن'),
(2847, 24, 56, 'و نماز را برپا كنيد و زكات بدهيد و پيامبر را فرمان بريد تا مشمول رحمت شويد'),
(2848, 24, 57, 'گمان نكنيد كسانى كه كافر شدند، ناتوان كننده‌ى ما در زمين هستند، جايگاهشان آتش است، و بد سر انجامى است'),
(2849, 24, 58, 'اى كسانى كه ايمان آورده‌ايد! بردگان شما و كسانى از شما كه به سن بلوغ نرسيده‌اند، بايد در سه وقت از شما اجازه بگيرند: پيش از نماز صبح و هنگام ظهر كه لباس از تن بيرون مى‌كنيد و بعد از نماز عشا. اين سه وقت، خلوت شماست. در غير اين مواقع، نه بر شما و نه ب'),
(2850, 24, 59, 'و چون كودكان شما به سن بلوغ رسيدند، بايد همانند كسانى كه پيش از ايشان [در همين شرايط] اجازه مى‌گرفتند، كسب اجازه كنند. خدا آيات خود را اين گونه براى شما بيان مى‌كند، و خدا داناى حكيم است'),
(2851, 24, 60, 'و بر زنان از كار افتاده‌اى كه اميد زناشويى ندارند گناهى نيست كه پوشش خود را كنار نهند، در صورتى كه زينت نمايى نكنند، و عفت ورزيدن براى آنها بهتر است، و خدا شنواى داناست'),
(2852, 24, 61, 'بر نابينا و لنگ و بيمار و بر خودتان ايرادى نيست كه از خانه‌هاى خود [و فرزندان و زنانتان‌] بخوريد، يا از خانه‌هاى پدرانتان، يا خانه‌هاى مادرانتان، يا خانه‌هاى برادرانتان، يا خانه‌هاى خواهرانتان، يا خانه‌هاى عموهايتان، يا خانه‌هاى عمّه‌هايتان، يا خانه‌'),
(2853, 24, 62, 'جز اين نيست كه مؤمنان [واقعى‌] كسانى هستند كه به خدا و پيامبرش گرويده‌اند، و هنگامى كه با پيامبر در كارى عمومى گرد آيند، جايى نميروند مگر از او اجازه بگيرند. در حقيقت كسانى كه از تو كسب اجازه مى‌كنند، آنانند كه به خدا و پيامبرش ايمان دارند. پس چون بر'),
(2854, 24, 63, 'صدا كردن پيامبر را ميان خود مانند صدا كردن [معمولى‌] يكديگر قرار ندهيد. خدا كسانى از شما را كه در پناه يكديگر دزدانه در مى‌روند خوب مى‌شناسد. پس كسانى كه خلاف امر او مى‌كنند، بايد بترسند كه يا بلايى به ايشان برسد، يا عذابى دردناك'),
(2855, 24, 64, 'آگاه باشيد كه آنچه در آسمان‌ها و زمين است از آن خداست. به يقين مى‌داند شما در چه حالى هستيد، و آگاه است از روزى كه به سوى او بازگردانده مى‌شوند و آنها را از حقيقت آنچه انجام داده‌اند خبر مى‌دهد و خدا به هر چيزى داناست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'پرخير و پاينده است آن [خدايى‌] كه بر بنده‌ى خود [كتاب‌] جدا كننده‌ى حق و باطل را نازل كرد تا براى جهانيان هشدار دهنده باشد'),
(2857, 25, 2, 'خدايى كه فرمانروايى آسمان‌ها و زمين از آن اوست، و فرزندى اختيار نكرده و براى او شريكى در فرمانروايى نبوده است، و هر چيزى را آفريده و حدّ و اندازه‌اى براى آن تدبير نموده است'),
(2858, 25, 3, 'و به جاى او خدايانى براى خود گرفتند كه چيزى نمى‌آفرينند و خود آفريده مى‌شوند، و براى خود اختيار زيان و سودى ندارند، و اختيار مرگ و زندگى و تجديد حيات ندارند'),
(2859, 25, 4, 'و كسانى كه كافر شدند، گفتند: اين [كتاب‌]، جز دروغى كه آن را بربافته، و گروهى ديگر او را بر آن يارى كرده‌اند، نيست. حقا كه به جانب ستم و دروغ [بزرگى‌] رو آوردند'),
(2860, 25, 5, 'و گفتند: افسانه‌هاى پيشينيان است كه به درخواست او برايش نوشته‌اند، و هر صبح و شام بر او خوانده مى‌شود. [تا حفظ كند'),
(2861, 25, 6, 'بگو: آن را كسى نازل كرده كه راز نهان را در آسمان‌ها و زمين مى‌داند، و همو آمرزنده‌ى مهربان است'),
(2862, 25, 7, 'و گفتند: اين چه پيامبرى است كه غذا مى‌خورد و در بازارها راه مى‌رود؟! چرا فرشته‌اى به سوى او نازل نشده تا همراه وى هشدار دهنده باشد'),
(2863, 25, 8, 'يا گنجى به طرف او افكنده شود، يا باغى داشته باشد كه از [بارو بر] آن بخورد. و ستمكاران گفتند: شما جز مردى افسون شده را پيروى نمى‌كنيد'),
(2864, 25, 9, 'بنگر چگونه براى تو مثل‌ها زدند و گمراه شدند، در نتيجه نمى‌توانند راهى بيابند'),
(2865, 25, 10, 'پرخير و پاينده است خدايى كه اگر مى‌خواست، تو را بهتر از اين مى‌داد باغ‌هايى كه جويبارها از پاى درختانش روان است، و براى تو كاخ‌ها پديد مى‌آورد'),
(2866, 25, 11, '[اينها بهانه است،] بلكه آنها رستاخيز را تكذيب كردند، و براى هر كه رستاخيز را دروغ خواند، آتش افروخته آماده كرده‌ايم'),
(2867, 25, 12, 'چون دوزخ آنها را از فاصله‌اى دور ببيند، خشم و خروشى از آن مى‌شنوند'),
(2868, 25, 13, 'و چون آنها را در جاى تنگى از آن، زنجير شده بيندازند، آن جا فرياد «وا هلاكا» سر مى‌دهند'),
(2869, 25, 14, 'امروز يك بار وا هلاكا مگوييد و بسيار وا هلاكا بگوييد'),
(2870, 25, 15, 'بگو: آيا اين [عقوبت‌] بهتر است يا بهشت جاويدانى كه به پرهيزكاران وعده داده شده، بهشتى كه پاداش و محل بازگشت آنهاست'),
(2871, 25, 16, 'هر چه بخواهند در آنجا دارند و جاودانه در آنند، وعده‌ى تعهد شده‌اى است بر پروردگار تو'),
(2872, 25, 17, 'و روزى كه آنها را با چيزهايى كه به جاى خدا مى‌پرستند [از فرشتگان و انسان‌ها] محشور مى‌كند، سپس مى‌گويد: آيا شما اين بندگان مرا گمراه كرديد، يا آنها خود گمراه شدند'),
(2873, 25, 18, 'مى‌گويند: منزهى تو. ما را نسزد كه جز تو دوستى براى خود بگيريم، ولى تو آنها و پدرانشان را برخوردار كردى تا اين كه ياد تو را فراموش كردند و قومى هلاك شده گشتند'),
(2874, 25, 19, 'قطعا [خدايانتان‌] آنچه را مى‌گوييد دروغ خوانده‌اند، و اكنون نه مى‌توانيد عذاب را از خود دفع كنيد و نه خود را يارى نماييد، و هر كه از شما ستم كند، عذابى سهمگين به او مى‌چشانيم'),
(2875, 25, 20, 'و پيش از تو پيامبران را نفرستاديم جز اين كه آنها نيز غذا مى‌خوردند و در بازارها راه مى‌رفتند، و ما شما [مردم‌] را وسيله آزمايش يكديگر قرار داده‌ايم آيا صبورى مى‌كنيد؟ و پروردگار تو بيناست'),
(2876, 25, 21, 'و كسانى كه به لقاى ما اميد ندارند گفتند: چرا فرشتگان بر ما نازل نشدند؟ يا پروردگارمان را نمى‌بينيم؟ راستى كه آنها در باره‌ى خويشتن تكبر ورزيدند و طغيان كردند، طغيانى بزرگ'),
(2877, 25, 22, 'روزى كه فرشتگان را ببينند، بشارتى در آن روز براى مجرمان نخواهد بود و [به التماس‌] مى‌گويند: ما را امان دهيد و معاف داريد'),
(2878, 25, 23, 'و به سراغ اعمالى كه كرده‌اند مى‌رويم و آن را چون گردى پراكنده بر باد مى‌دهيم'),
(2879, 25, 24, 'آن روز جايگاه اهل بهشت بهتر و استراحتگاهشان نيكوتر است'),
(2880, 25, 25, 'و روزى كه آسمان با ابرهايش بشكافد و فرشتگان چنان كه بايد فرستاده شوند'),
(2881, 25, 26, 'آن روز فرمانروايى، واقعى و از آن [خداى‌] رحمان است و روزى است كه بر كافران بسى دشوار خواهد بود'),
(2882, 25, 27, 'و روزى كه ستمكار دست‌هاى خود را به دندان گزد و گويد: اى كاش با پيامبر راهى پيش مى‌گرفتم'),
(2883, 25, 28, 'اى واى بر من، كاش فلانى را دوست خود نگرفته بودم'),
(2884, 25, 29, 'همانا مرا از قرآن پس از آن كه براى من آمده بود دور ساخت و شيطان همواره خوار كننده و فرو گذارنده‌ى انسان بوده است'),
(2885, 25, 30, 'و پيامبر گويد: پروردگارا! قوم من اين قرآن را رها كردند'),
(2886, 25, 31, 'و اين گونه براى هر پيامبرى دشمنى از ميان مجرمان قرار داديم، و همين بس كه پروردگارت راهنما و ياور [تو] باشد'),
(2887, 25, 32, 'و كسانى كه كافر شدند گفتند: چرا قرآن يكجا بر او نازل نشده است؟ اين گونه [فرستاديم‌] تا دل تو را به وسيله آن استوار گردانيم و آن را به تدريج بر تو خوانديم'),
(2888, 25, 33, 'و براى تو هيچ مثلى نمى‌آورند مگر آن كه [پاسخى‌] حق و تفسيرى نيكوتر برايت مى‌آوريم'),
(2889, 25, 34, 'كسانى كه به رو افتاده به سوى جهنم رانده مى‌شوند، در بدترين جايگاه و بى‌راهه‌ترين راه قرار دارند'),
(2890, 25, 35, 'و به راستى ما به موسى كتاب آسمانى عطا كرديم و برادرش هارون را همراه او مددكارش كرديم'),
(2891, 25, 36, 'پس گفتيم: به سوى قومى برويد كه آيات ما را تكذيب كردند پس ما آنان را به سختى هلاك نموديم'),
(2892, 25, 37, 'و قوم نوح را آن‌گاه كه پيامبران را تكذيب كردند غرقشان كرديم و آنها را براى مردم عبرتى ساختيم، و براى ستمكاران عذابى پر درد آماده كرده‌ايم'),
(2893, 25, 38, 'و نيز عاديان و ثموديان و اصحاب رسّ و نسل‌هاى بسيارى را كه ميان اين جماعت‌ها بودند [هلاك كرديم‌]'),
(2894, 25, 39, 'و براى همه آنان مثل‌ها زديم و [چون پند نگرفتند] همه را به طور كامل نابود كرديم'),
(2895, 25, 40, 'و قطعا بر شهرى كه باران بلا بر آن بارانده شد گذر كرده‌اند آيا آن را نمى‌ديدند؟ لكن آنها زنده شدن [خود] را باور نداشتند'),
(2896, 25, 41, 'و چون تو را ببينند، جز به ريشخندت نگيرند كه آيا اين همان كسى است كه خدا او را به رسالت فرستاده است؟'),
(2897, 25, 42, 'چيزى نمانده بود كه ما را از خدايانمان اگر بر آن‌ها ايستادگى نمى‌كرديم گمراه سازد. به زودى هنگامى كه عذاب را ببينند خواهند دانست چه كسى گمراه‌تر است'),
(2898, 25, 43, 'آيا آن كس كه هواى نفس خود را معبود خويش گرفته است ديدى؟ آيا تو ضامن او هستى'),
(2899, 25, 44, 'آيا گمان مى‌كنى كه بيشترشان مى‌شنوند يا مى‌انديشند؟ آنان جز مانند ستوران نيستند، بلكه گمراه‌ترند'),
(2900, 25, 45, 'آيا ننگريستى كه پروردگارت چگونه سايه را امتداد داده است؟ و اگر مى‌خواست آن را ساكن قرار مى‌داد [كه يكسره شب باشد]، آن‌گاه خورشيد را نمايانگر آن قرار داديم'),
(2901, 25, 46, 'سپس آن [سايه‌] را اندك اندك به سوى خود بازگرفتيم'),
(2902, 25, 47, 'و اوست كسى كه شب را براى شما پوشش و خواب را استراحت و روز را [زمان‌] برخاستن [براى كسب و كار] قرار داد'),
(2903, 25, 48, 'و اوست آن كس كه بادها را پيش از باران رحمتش بشارت دهنده فرستاد، و از آسمان آبى [پاك و] پاك كننده نازل كرديم'),
(2904, 25, 49, 'تا با آن زمين مرده را زنده كنيم و آن را به آفريده‌هاى خويش، چهارپايان و آدميان بسيار بنوشانيم'),
(2905, 25, 50, 'و به راستى ما آن [آب‌] را در ميان مردم گردانيديم تا [به نعمت خدا] توجه كنند، اما بيشتر مردم سرباز زدند و جز ناسپاسى نكردند'),
(2906, 25, 51, 'و اگر مى‌خواستيم قطعا در هر شهرى هشدار دهنده‌اى بر مى‌انگيختيم [ليكن تأثير نداشت‌]'),
(2907, 25, 52, 'پس از كافران اطاعت مكن و به وسيله قرآن با ايشان به جهادى بزرگ بپرداز'),
(2908, 25, 53, 'و اوست كسى كه دو دريا را به هم پيوست، اين يكى شيرين و گوارا و آن يكى شور و تلخ است، و ميان آن دو مانع و حايل جدا كننده قرار داد [كه همديگر را دفع مى‌كنند'),
(2909, 25, 54, 'و اوست كسى كه از آب بشرى آفريد و او را داراى پيوند نسبى و سببى گردانيد، و پروردگار تو توانا بوده است'),
(2910, 25, 55, 'و غير از خدا چيزى را مى‌پرستند كه نه سودشان مى‌دهد و نه زيانشان مى‌رساند، و كافر همواره بر ضد پروردگار خود پشتيبان [باطل‌] است'),
(2911, 25, 56, 'و تو را جز بشارت دهنده و بيم رسان نفرستاديم'),
(2912, 25, 57, 'بگو: من بر اين رسالت اجرى از شما طلب نمى‌كنم مگر كسى كه بخواهد راهى به سوى پروردگار خود در پيش گيرد [كه همين مزد من است‌]'),
(2913, 25, 58, 'و بر آن زنده كه نمى‌ميرد توكل كن و به ستايش او تسبيح گوى و همين بس كه او به گناهان بندگانش آگاه است'),
(2914, 25, 59, 'همان كسى كه آسمان‌ها و زمين و آنچه را كه ميان آن دو است، در شش روز آفريد، آن گاه به عرش پرداخت. او رحمن است. [حقيقت حال را] از او بپرس كه باخبر است'),
(2915, 25, 60, 'و چون به آنها گفته شود: خداى رحمان را سجده كنيد، مى‌گويند: رحمان چيست؟ آيا آن را كه تو به ما فرمان مى‌دهى سجده كنيم؟! و [دعوت تو] بر نفرتشان مى‌افزايد'),
(2916, 25, 61, 'پرخير و پاينده است آن كسى كه در آسمان برج‌هايى نهاد، و در آن چراغى و ماهى تابان قرار داد'),
(2917, 25, 62, 'و اوست كه شب و روز را از پى هم قرار داد، براى هر كس كه بخواهد پند گيرد يا سپاسگزار باشد'),
(2918, 25, 63, 'و بندگان خداى رحمان كسانى‌اند كه روى زمين به فروتنى راه مى‌روند، و چون جاهلان [به ناروا] خطابشان كنند، سلام گويند [و در گذرند]'),
(2919, 25, 64, 'و آنانند كه شب را در حال سجده و قيام به روز آورند'),
(2920, 25, 65, 'و كسانى‌اند كه مى‌گويند: پروردگار ما! عذاب جهنم را از ما بگردان كه عذابش سخت و دائمى است'),
(2921, 25, 66, 'به راستى آن بد قرارگاه و جايگاهى است'),
(2922, 25, 67, 'و كسانى‌اند كه چون انفاق و هزينه كنند، نه زياده‌روى مى‌كنند و نه خسّت مى‌ورزند، و ميان اين دو به راه اعتدال باشند'),
(2923, 25, 68, 'و كسانى‌اند كه با خدا معبود ديگرى نخوانند، و كسى را كه خدا [خونش را] حرام كرده جز به حق نكشند، و زنا نكنند. و هر كس چنين كند عقوبت [گناه خود] را خواهد ديد'),
(2924, 25, 69, 'براى او در روز قيامت عذاب دو چندان مى‌شود و تا ابد با خوارى در آن مى‌ماند'),
(2925, 25, 70, 'مگر كسى كه توبه كند و ايمان آورد و كار شايسته كند. پس آنانند كه خدا بدى‌هاشان را به حسنات تبديل مى‌كند، و خدا آمرزنده‌ى مهربان است'),
(2926, 25, 71, 'و هر كس توبه كند و كار شايسته انجام دهد، به طور نيكو به سوى خدا باز مى‌گردد'),
(2927, 25, 72, 'و كسانى‌اند كه گواهى دروغ نمى‌دهند و چون بر ناپسندى بگذرند كريمانه عبور مى‌كنند [و آلوده نمى‌شوند]'),
(2928, 25, 73, 'و كسانى‌اند كه چون به آيات پروردگارشان تذكر داده شوند، كر و كور بر آن نيفتند'),
(2929, 25, 74, 'و كسانى‌اند كه مى‌گويند: پروردگارا! به ما از همسران و فرزندانمان آن ده كه مايه روشنى چشمان ما باشد، و ما را پيشواى پرهيزكاران گردان'),
(2930, 25, 75, 'اينانند كه به پاس آن كه صبر كردند غرفه بهشت را پاداش مى‌گيرند و در آن جا با سلام و درود مواجه مى‌شوند'),
(2931, 25, 76, 'در آن جا جاودانه خواهند ماند. چه خوش قرارگاه و مقامى است'),
(2932, 25, 77, 'بگو: اگر دعا و عبادت شما نباشد، پروردگارم هيچ اعتنايى به شما نمى‌كند، شما [آيات خدا و پيامبران را] تكذيب كرديد، پس به زودى [كيفر آن‌] دامنگير و ملازم شما خواهد شد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سين، ميم'),
(2934, 26, 2, 'اين آيات كتاب روشنگر است'),
(2935, 26, 3, 'شايد تو از اين كه مشركان ايمان نمى‌آورند جان خود را هلاك سازى'),
(2936, 26, 4, 'اگر بخواهيم، معجزه‌اى از آسمان بر آنها نازل مى‌كنيم تا در برابر آن خاضعانه گردن نهند'),
(2937, 26, 5, 'و هيچ تذكّر جديدى از سوى خداى رحمان برايشان نمى‌آيد جز اين كه از آن روى بر مى‌تابند'),
(2938, 26, 6, 'همانا [آن را] تكذيب كردند، پس به زودى اخبار آنچه مسخره‌اش مى‌كردند بديشان خواهد رسيد'),
(2939, 26, 7, 'آيا به زمين ننگريستند كه چه بسيار از گونه‌هاى گياهان زيبا در آن رويانديم'),
(2940, 26, 8, 'قطعا در اين [روياندن‌] عبرتى است ولى بيشترشان مؤمن نيستند'),
(2941, 26, 9, 'و حقا پروردگار تو همان شكست ناپذير مهربان است'),
(2942, 26, 10, 'و [ياد كن‌] هنگامى را كه پروردگارت موسى را ندا داد كه به سوى قوم ستمكار بيا'),
(2943, 26, 11, '[به سوى‌] قوم فرعون. آيا [از كفر و سركشى‌] نمى‌پرهيزند'),
(2944, 26, 12, 'گفت: پروردگارا! خوف آن دارم كه تكذيبم كنند'),
(2945, 26, 13, 'و سينه‌ام تنگ مى‌شود و زبانم [به گفتار] روان نيست. پس به هارون [نيز پيام‌] فرست'),
(2946, 26, 14, 'و آنان بر گردن من خونى دارند، پس مى‌ترسم مرا بكشند'),
(2947, 26, 15, 'گفت: نه، چنين نيست، [تو و هارون‌] آيات ما را [براى آنها] ببريد كه ما همراه شما شنونده‌ايم'),
(2948, 26, 16, 'پس به سوى فرعون بياييد [من آنجا حاضرم‌] و بگوييد: ما فرستاده‌ى پروردگار جهانيانيم'),
(2949, 26, 17, 'كه فرزندان اسرائيل را با ما بفرست'),
(2950, 26, 18, 'فرعون گفت: آيا تو را از كودكى در ميان خود بزرگ نكرديم، و ساليانى از عمرت را در ميان ما نبودى'),
(2951, 26, 19, 'و آن كرده‌ى خويش را كردى [و كسى را كشتى‌] در حالى كه تو از ناسپاسان بودى. كودكى. لبثت (لبث): توقف كردى، ماندى. فينا: در ميان ما. عمرك: عمرت. سنين جمع سنة: سالها. فعلت فعلتك: انجام دادى آن كارت را، كار خود را كردى. كافر: كفران كننده، ناشكر'),
(2952, 26, 20, 'گفت: آن را هنگامى مرتكب شدم كه از ناآگاهان بودم [نمى‌دانستم مشتى سبب قتل است‌]'),
(2953, 26, 21, 'و چون از شما ترسيدم گريختم، پس پروردگارم به من داورى و دانش داد و مرا از پيامبران قرار داد'),
(2954, 26, 22, 'و آن هم نعمتى است كه بر من منت مى‌نهى كه فرزندان اسرائيل را بنده‌ى خود ساخته‌اى'),
(2955, 26, 23, 'فرعون گفت: رب العالمين چيست'),
(2956, 26, 24, 'گفت: او پروردگار آسمان‌ها و زمين و پديده‌هاى ميان آن دو است، اگر اهل يقين باشيد'),
(2957, 26, 25, 'فرعون به اطرافيان خود گفت: آيا نمى‌شنويد [چه مى‌گويد]'),
(2958, 26, 26, '[موسى دوباره‌] گفت: پروردگار شما و پروردگار پدران پيشين شماست'),
(2959, 26, 27, 'گفت: واقعا اين پيامبرى كه به سوى شما فرستاده شده ديوانه است'),
(2960, 26, 28, 'موسى گفت: اوست پروردگار مشرق و مغرب و آنچه ميان آنهاست، اگر تعقل كنيد'),
(2961, 26, 29, 'گفت: اگر خدايى غير از من اختيار كنى، قطعا تو را از زندانيان قرار مى‌دهم'),
(2962, 26, 30, 'گفت: حتى اگر براى تو حجتى روشن بياوريم'),
(2963, 26, 31, 'گفت: آن را بياور اگر راست مى‌گويى'),
(2964, 26, 32, 'پس عصاى خود بيفكند و در دم اژدهايى هويدا گشت'),
(2965, 26, 33, 'و دستش را بيرون كشيد و به ناگاه آن براى تماشاگران درخشان گشت'),
(2966, 26, 34, 'فرعون به سران قومش كه اطراف او بودند گفت: واقعا اين ساحرى بسيار داناست'),
(2967, 26, 35, 'مى‌خواهد با سحر خود شما را از سرزمينتان بيرون كند، اكنون چه رأى مى‌دهيد'),
(2968, 26, 36, 'گفتند: [تصميم در باره‌ى‌] او و برادرش را تأخير بينداز و مأموران جمع‌آورى را به شهرها بفرست'),
(2969, 26, 37, 'تا هر ساحر ماهرى را نزد تو بياورند'),
(2970, 26, 38, 'پس ساحران براى موعد روزى معين گردآورى شدند'),
(2971, 26, 39, 'و به مردم گفته شد: آيا شما هم [در اين صحنه‌] فراهم مى‌آييد'),
(2972, 26, 40, 'تا شايد ما از جادوگران پيروى كنيم اگر آنان پيروز شدند'),
(2973, 26, 41, 'و چون ساحران آمدند به فرعون گفتند: آيا واقعا براى ما مزدى خواهد بود اگر ما غالب آييم'),
(2974, 26, 42, 'گفت: آرى و حتما در اين صورت از مقرّبان خواهيد شد'),
(2975, 26, 43, 'موسى به آنها گفت: آنچه افكندنى هستيد بيفكنيد'),
(2976, 26, 44, 'پس ريسمان‌ها و چوبدستى‌هايشان را انداختند و گفتند: به عزّت فرعون كه ما حتما پيروزيم'),
(2977, 26, 45, 'پس موسى عصايش را انداخت و در دم هر چه را به صورت غير واقعى ساخته بودند مى‌بلعيد'),
(2978, 26, 46, 'پس ساحران به حالت سجده افتادند'),
(2979, 26, 47, 'گفتند: به پروردگار عالميان ايمان آورديم'),
(2980, 26, 48, 'پروردگار موسى و هارون'),
(2981, 26, 49, 'گفت: آيا پيش از آن كه به شما اجازه دهم به او ايمان آورديد؟ قطعا او همان بزرگ شماست كه به شما سحر آموخته است، پس به زودى خواهيد دانست. حتما دست‌ها و پاهاى شما را به عكس يكديگر خواهم بريد و همه‌تان را به دار خواهم آويخت'),
(2982, 26, 50, 'گفتند: باكى نيست، ما به سوى پروردگار خود باز مى‌گرديم'),
(2983, 26, 51, 'ما اميد داريم كه پروردگار ما گناهانمان را بر ما ببخشايد، چرا كه ما اولين مؤمنان شديم'),
(2984, 26, 52, 'و به موسى وحى كرديم كه بندگان مرا شبانه حركت ده، كه شما مورد تعقيب هستيد'),
(2985, 26, 53, 'پس فرعون مأموران جمع‌آورى را به شهرها فرستاد'),
(2986, 26, 54, '[و گفت:] همانا اينها عدّه‌اى ناچيزند'),
(2987, 26, 55, 'و راستى آنها ما را به خشم آورده‌اند'),
(2988, 26, 56, 'و ما همگى آماده‌ى پيكار هستيم'),
(2989, 26, 57, 'پس ما آنها را از باغ‌ها و چشمه‌سارها بيرون كرديم'),
(2990, 26, 58, 'و از گنج‌ها و اقامتگاه‌هاى نيكو'),
(2991, 26, 59, 'بدين‌سان بنى اسرائيل را وارث آنها كرديم'),
(2992, 26, 60, 'پس هنگام برآمدن آفتاب از پى آنها رسيدند'),
(2993, 26, 61, 'و چون دو گروه همديگر را ديدند، ياران موسى گفتند: ما قطعا گرفتار خواهيم شد'),
(2994, 26, 62, 'گفت: چنين نيست، بى‌ترديد پروردگارم با من است و به زودى هدايتم خواهد كرد'),
(2995, 26, 63, 'آن‌گاه به موسى وحى كرديم كه با عصاى خود بر دريا بزن. پس از هم شكافت و هر قسمت آن چون كوهى عظيم گرديد'),
(2996, 26, 64, 'و گروه ديگر (فرعونيان) را به آن جا نزديك كرديم'),
(2997, 26, 65, 'و موسى و همه همراهانش را نجات داديم'),
(2998, 26, 66, 'سپس گروه ديگر را غرق كرديم'),
(2999, 26, 67, 'مسلما در اين [واقعه‌] عبرتى بود ولى بيشترشان مؤمن نبودند'),
(3000, 26, 68, 'و قطعا پروردگار تو شكست‌ناپذير مهربان است'),
(3001, 26, 69, 'و بر آنان خبر مهم ابراهيم را بخوان'),
(3002, 26, 70, 'آن‌گاه كه به پدر خود و قومش گفت: چه مى‌پرستيد'),
(3003, 26, 71, 'گفتند: بت‌هايى را مى‌پرستيم و همواره ملازم عبادت آنهاييم'),
(3004, 26, 72, 'گفت: آيا وقتى آنها را مى‌خوانيد از شما مى‌شنوند'),
(3005, 26, 73, 'يا به شما سود و زيان مى‌رسانند'),
(3006, 26, 74, 'گفتند: [نه‌]، بلكه پدران خود را يافتيم كه چنين مى‌كردند'),
(3007, 26, 75, 'گفت: آيا در آنچه مى‌پرستيده‌ايد تأمل كرده‌ايد'),
(3008, 26, 76, 'شما و نياكان گذشته شما'),
(3009, 26, 77, 'آن [معبود] ها دشمنان منند، مگر پروردگار جهانيان [كه ولىّ من است‌]'),
(3010, 26, 78, 'همان كسى كه مرا آفريده و او هدايتم مى‌كند'),
(3011, 26, 79, 'و كسى كه او مرا اطعام مى‌كند و آبم مى‌دهد'),
(3012, 26, 80, 'و چون بيمار شوم او مرا شفا مى‌دهد'),
(3013, 26, 81, 'و كسى كه مرا مى‌ميراند و سپس زنده‌ام مى‌كند'),
(3014, 26, 82, 'و كسى كه اميد دارم روز جزا خطايم را بر من ببخشايد'),
(3015, 26, 83, 'پروردگارا! مرا حكمتى عطا كن و مرا به نيكان ملحق گردان'),
(3016, 26, 84, 'و براى من در ميان آيندگان زبان صدق و ثناگو قرار ده'),
(3017, 26, 85, 'و مرا از وارثان بهشت پرنعمت گردان'),
(3018, 26, 86, 'و بر پدرم ببخشاى كه او از گمراهان بود'),
(3019, 26, 87, 'و روزى كه [مردم‌] برانگيخته مى‌شوند مرا خوار مگردان'),
(3020, 26, 88, 'روزى كه نه مال سود مى‌دهد و نه فرزندان'),
(3021, 26, 89, 'مگر كسى كه با دلى [پاك و] سالم به سوى خدا بيايد'),
(3022, 26, 90, 'و [آن روز] بهشت براى پرهيزكاران نزديك آورده شود'),
(3023, 26, 91, 'و جهنم براى گمراهان نمايان شود'),
(3024, 26, 92, 'و به آنها گفته شود: كجايند آنچه عبادت مى‌كرديد'),
(3025, 26, 93, '[معبودانى‌] غير از خدا؟ آيا ياريتان مى‌كنند يا خود را يارى مى‌دهند'),
(3026, 26, 94, 'پس آنها و همه گمراهان به حالت سرنگون در آن [آتش‌] افكنده شوند'),
(3027, 26, 95, 'و نيز همه سپاهيان ابليس'),
(3028, 26, 96, 'آنها در آن جا در حالى كه با يكديگر ستيزه مى‌كنند گويند'),
(3029, 26, 97, 'سوگند به خدا كه ما در گمراهى آشكارى بوديم'),
(3030, 26, 98, 'آن‌گاه كه شما [معبودان‌] را با پروردگار جهانيان برابر مى‌داشتيم'),
(3031, 26, 99, 'و ما را جز مجرمان گمراه نكردند'),
(3032, 26, 100, 'و اكنون ما را هيچ شفيعى نيست'),
(3033, 26, 101, 'و نه دوست مهربانى'),
(3034, 26, 102, 'اى كاش ما را برگشتى بود تا از مؤمنان مى‌شديم'),
(3035, 26, 103, 'حقا در اين [سرنوشت‌] درس عبرتى است، ولى اكثر آنان مؤمن نبودند'),
(3036, 26, 104, 'و به راستى پروردگار تو شكست ناپذير مهربان است'),
(3037, 26, 105, 'قوم نوح، پيامبران را تكذيب كردند'),
(3038, 26, 106, 'آن زمان كه برادرشان نوح به آنها گفت: آيا [از خدا] پروا نمى‌كنيد'),
(3039, 26, 107, 'من براى شما فرستاده‌اى امين هستم'),
(3040, 26, 108, 'پس از خدا بترسيد و مرا اطاعت كنيد'),
(3041, 26, 109, 'و بر اين [رسالت‌] اجرى از شما طلب نمى‌كنم اجر من جز بر پروردگار جهانيان نيست'),
(3042, 26, 110, 'پس از خدا پروا كنيد و مرا فرمان بريد'),
(3043, 26, 111, 'گفتند: آيا به تو ايمان بياوريم و حال آن كه فرومايگان از تو پيروى كرده‌اند'),
(3044, 26, 112, 'نوح گفت: مرا بدانچه آنها مى‌كردند چه دانشى است'),
(3045, 26, 113, 'حسابشان جز بر پروردگار من نيست، اگر مى‌فهميديد'),
(3046, 26, 114, 'و من طرد كننده‌ى مؤمنان نيستم'),
(3047, 26, 115, 'من صرفا هشدار دهنده‌اى آشكار هستم'),
(3048, 26, 116, 'گفتند: اى نوح! اگر دست برندارى، قطعا از جمله سنگسار شدگان خواهى شد'),
(3049, 26, 117, 'گفت: پروردگارا! قوم من مرا تكذيب كردند'),
(3050, 26, 118, 'پس ميان من و آنها فيصله ده، و من و مؤمنان همراه مرا نجات بخش'),
(3051, 26, 119, 'پس او و هر كه را در آن كشتى پر شده با او بود نجات داديم'),
(3052, 26, 120, 'سپس بعد از آنها بازماندگان را غرق كرديم'),
(3053, 26, 121, 'قطعا در اين [ماجرا] درس عبرتى بود ولى اكثرشان مؤمن نبودند'),
(3054, 26, 122, 'و همانا پروردگار توست كه تواناى مهربان است'),
(3055, 26, 123, 'عاديان پيامبران را تكذيب كردند'),
(3056, 26, 124, 'آن‌گاه كه برادرشان هود به آنها گفت: آيا [از خدا] پروا نمى‌داريد'),
(3057, 26, 125, 'من براى شما فرستاده‌اى امين هستم'),
(3058, 26, 126, 'پس از خدا پروا كنيد و مرا فرمان بريد'),
(3059, 26, 127, 'و بر اين [رسالت‌] اجرى از شما طلب نمى‌كنم، اجر من جز بر پروردگار جهانيان نيست'),
(3060, 26, 128, 'آيا بر هر بلندى [بنا و] نشانه‌اى از سر بازيچه مى‌سازيد'),
(3061, 26, 129, 'و كاخ‌هاى استوار مى‌گيريد كه گويا هميشه خواهيد ماند'),
(3062, 26, 130, 'و وقتى كسى را عقوبت مى‌كنيد جبارانه عقوبت مى‌كنيد'),
(3063, 26, 131, 'پس از خدا پروا داريد و مرا فرمان بريد'),
(3064, 26, 132, 'و بترسيد از آن كس كه شما را با آنچه [خود] مى‌دانيد يارى داد'),
(3065, 26, 133, 'شما را به دادن چهارپايان و پسران مدد كرد'),
(3066, 26, 134, 'و به باغ‌ها و چشمه‌ساران'),
(3067, 26, 135, 'من از عذاب روزى هولناك بر شما مى‌ترسم'),
(3068, 26, 136, 'گفتند: براى ما يكسان است، خواه ما را اندرز دهى يا اندرز ندهى، [ايمان نخواهيم آورد]'),
(3069, 26, 137, '[گفتند] اين جز شيوه‌ى پيشينيان نيست'),
(3070, 26, 138, 'و ما عذاب نخواهيم شد'),
(3071, 26, 139, 'پس تكذيبش كردند و هلاكشان كرديم. قطعا در اين [ماجرا] درس عبرتى است و اكثر آنان مؤمن نبودند'),
(3072, 26, 140, 'و به راستى پروردگار تو تواناى مهربان است'),
(3073, 26, 141, 'ثموديان پيامبران را تكذيب كردند'),
(3074, 26, 142, 'آن‌گاه كه برادرشان صالح به آنها گفت: آيا [از خدا] پروا نمى‌كنيد'),
(3075, 26, 143, 'من براى شما فرستاده‌اى امين هستم'),
(3076, 26, 144, 'پس از خدا پروا كنيد و از من فرمان بريد'),
(3077, 26, 145, 'و بر اين [رسالت‌] اجرى از شما طلب نمى‌كنم، اجر من جز بر پروردگار جهانيان نيست'),
(3078, 26, 146, 'آيا [مى‌پنداريد كه‌] شما را در اين نعمت‌هايى كه اين جاست آسوده رها مى‌كنند'),
(3079, 26, 147, 'در اين باغ‌ها و چشمه ساران'),
(3080, 26, 148, 'و كشتزارها و نخل‌ها كه ميوه‌هايشان لطيف است'),
(3081, 26, 149, 'و براى خود از كوه‌ها ماهرانه و سرمستانه خانه‌هايى مى‌تراشيد'),
(3082, 26, 150, 'پس از خدا پروا كنيد و از من فرمان بريد'),
(3083, 26, 151, 'و فرمان مسرفان را اطاعت نكنيد'),
(3084, 26, 152, 'همانها كه در زمين فساد مى‌كنند و اصلاح نمى‌كنند'),
(3085, 26, 153, 'گفتند: جز اين نيست كه تو از سحر شدگانى'),
(3086, 26, 154, 'تو جز بشرى مانند ما نيستى معجزه‌اى بياور اگر راست مى‌گويى'),
(3087, 26, 155, 'گفت: اين ماده شترى است كه يك روز نوبت آب حق او، و يك روز معين حق شماست'),
(3088, 26, 156, 'و به آن گزندى نرسانيد كه عذاب روزى هولناك شما را فرا گيرد'),
(3089, 26, 157, 'پس آن را پى كردند و پشيمان گشتند'),
(3090, 26, 158, 'آنگاه آنها را عذاب در گرفت قطعا در اين [ماجرا] عبرتى است و اكثر آنان مؤمن نبودند'),
(3091, 26, 159, 'و به راستى پروردگار تو شكست‌ناپذير مهربان است'),
(3092, 26, 160, 'قوم لوط فرستادگان را تكذيب كردند'),
(3093, 26, 161, 'آن‌گاه كه برادرشان لوط به آنها گفت: آيا [از خدا] پروا نمى‌كنيد'),
(3094, 26, 162, 'من براى شما فرستاده‌اى امين هستم'),
(3095, 26, 163, 'پس از خدا پروا كنيد و از من فرمان بريد'),
(3096, 26, 164, 'و بر اين [رسالت‌] اجرى از شما طلب نمى‌كنم اجر من جز بر پروردگار جهانيان نيست'),
(3097, 26, 165, 'آيا از ميان جهانيان با مردها در مى‌آميزيد'),
(3098, 26, 166, 'و همسرانى را كه پروردگارتان براى شما آفريده وا مى‌گذاريد؟ بلكه شما مردمى تجاوز كاريد'),
(3099, 26, 167, 'گفتند: اى لوط! اگر دست بر ندارى، قطعا [از شهر] اخراج خواهى شد'),
(3100, 26, 168, 'گفت: به راستى من از مخالفان عمل شمايم'),
(3101, 26, 169, 'پروردگارا! من و كسان مرا از آنچه مى‌كنند نجات بخش'),
(3102, 26, 170, 'پس او و كسانش را همگى نجات داديم'),
(3103, 26, 171, 'جز پيرزنى كه در ميان بازماندگان بود'),
(3104, 26, 172, 'سپس ديگران را سخت هلاك كرديم'),
(3105, 26, 173, 'و باران [عذاب‌] بر ايشان بارانديم و باران انذار شدگان، بد بارانى است'),
(3106, 26, 174, 'قطعا در اين [عقوبت‌] عبرتى است و اكثر آنان مؤمن نبودند'),
(3107, 26, 175, 'و به راستى پروردگار تو شكست‌ناپذير مهربان است'),
(3108, 26, 176, 'اهل «ايكه» فرستادگان را تكذيب كردند'),
(3109, 26, 177, 'آنگاه كه شعيب به آنان گفت: آيا [از خدا] پروا نمى‌كنيد'),
(3110, 26, 178, 'من براى شما فرستاده‌اى امين هستم'),
(3111, 26, 179, 'پس از خدا پروا كنيد و از من فرمان بريد'),
(3112, 26, 180, 'و بر اين [رسالت‌] اجرى از شما طلب نمى‌كنم اجر من جز بر پروردگار جهانيان نيست'),
(3113, 26, 181, 'پيمانه را تمام دهيد و از كم فروشان مباشيد'),
(3114, 26, 182, 'و با ترازوى درست بسنجيد'),
(3115, 26, 183, 'و اجناس مردم را كم ندهيد و در زمين سر به فساد بر نداريد'),
(3116, 26, 184, 'و از [نافرمانى‌] كسى كه شما و نسل‌هاى گذشته را آفريد پروا كنيد'),
(3117, 26, 185, 'گفتند: جز اين نيست كه تو از سحر شدگانى'),
(3118, 26, 186, 'و تو جز بشرى مانند ما نيستى و بى‌شك تو را از دروغگويان مى‌دانيم'),
(3119, 26, 187, 'پس قطعه‌اى از آسمان بر سر ما بيفكن اگر از راستگويانى'),
(3120, 26, 188, 'شعيب گفت: پروردگارم به آنچه مى‌كنيد داناتر است'),
(3121, 26, 189, 'پس او را تكذيب كردند و عذاب روز ابر [آتشبار] آنها را بگرفت. به راستى كه آن، عذاب روزى بزرگ بود'),
(3122, 26, 190, 'قطعا در اين [عقوبت‌] درس عبرتى است و بيشتر آنان مؤمن نبودند'),
(3123, 26, 191, 'و همانا پروردگار تو شكست ناپذير مهربان است'),
(3124, 26, 192, 'و به راستى كه اين [قرآن‌] نازل شده‌ى پروردگار جهانيان است'),
(3125, 26, 193, 'روح الامين آن را نازل كرده است'),
(3126, 26, 194, 'بر قلب تو، تا از بيم دهندگان باشى'),
(3127, 26, 195, 'به زبان عربى روشن'),
(3128, 26, 196, 'و قطعا [توصيف‌] آن در نوشته‌هاى پيشينيان آمده است'),
(3129, 26, 197, 'آيا اين براى آنها دليل و نشانه نيست كه علماى بنى اسرائيل آن را مى‌دانند [و مى‌شناسند]'),
(3130, 26, 198, 'و اگر آن را بر برخى از غير عرب‌ها نازل مى‌كرديم'),
(3131, 26, 199, 'و آن را برايشان مى‌خواند به آن ايمان نمى‌آوردند'),
(3132, 26, 200, 'بدين سان قرآن را در دل‌هاى گناهكاران راه داديم [تا حجت تمام شود]'),
(3133, 26, 201, '[اما] به آن ايمان نمى‌آورند تا وقتى كه عذاب پردرد را ببينند'),
(3134, 26, 202, 'كه به طور ناگهانى به سراغشان بيايد در حالى كه توجه ندارند'),
(3135, 26, 203, '[و در آن هنگام‌] مى‌گويند: آيا ما را مهلتى داده خواهد شد'),
(3136, 26, 204, 'آيا به عذاب ما عجله مى‌كنند'),
(3137, 26, 205, 'به ما بگو كه اگر ساليانى آنها را از زندگى برخوردار كنيم'),
(3138, 26, 206, 'آن‌گاه عذاب موعود بر سرشان بيايد'),
(3139, 26, 207, 'آنچه از زندگى برخوردار شده بودند چه [عذابى را] از آنها دفع خواهد كرد'),
(3140, 26, 208, 'و [اهل‌] هيچ شهرى را هلاك نكرديم مگر اين كه براى آنها هشدار دهندگانى بود'),
(3141, 26, 209, 'تا آنان را تذكّر دهند، و ما ستمكار نبوده‌ايم [كه اتمام حجت نكنيم‌]'),
(3142, 26, 210, 'و آن [قرآن‌] را شياطين نازل نكرده‌اند'),
(3143, 26, 211, 'و آنها را نسزد و نمى‌توانند'),
(3144, 26, 212, 'بى‌ترديد آنها از شنيدن [وحى‌] منع شده‌اند'),
(3145, 26, 213, 'پس با خدا معبودى ديگر مخوان كه از عذاب شدگان مى‌گردى'),
(3146, 26, 214, 'و خويشان نزديك را هشدار ده'),
(3147, 26, 215, 'و براى آن مؤمنانى كه تو را پيروى كرده‌اند بال [محبت‌] بگستر'),
(3148, 26, 216, 'و اگر تو را نافرمانى كردند بگو: من از آنچه مى‌كنيد بيزارم'),
(3149, 26, 217, 'و بر [خداى‌] عزيز مهربان توكل كن'),
(3150, 26, 218, 'خدايى كه چون [به عبادت‌] بر مى‌خيزى تو را مى‌بيند'),
(3151, 26, 219, 'و حركت تو را در ميان سجده كنندگان [مى‌بيند]'),
(3152, 26, 220, 'او همان شنواى داناست'),
(3153, 26, 221, 'آيا شما را خبر دهم كه شياطين بر چه كسى نازل مى‌شوند'),
(3154, 26, 222, 'بر هر دروغزن گناهكار نازل مى‌شوند'),
(3155, 26, 223, 'شنيده‌هاى [خويش‌] را القا مى‌كنند و بيشترشان دروغگويند'),
(3156, 26, 224, 'و شاعران را گمراهان پيروى مى‌كنند'),
(3157, 26, 225, 'آيا نديده‌اى كه آنان در هر وادى [از افكار خيالى‌] سرگردانند'),
(3158, 26, 226, 'و آنان چيزهايى مى‌گويند كه انجام نمى‌دهند'),
(3159, 26, 227, 'مگر كسانى كه ايمان آورده، كارهاى شايسته نموده، خدا را بسيار ياد كردند و پس از ستم ديدن به دفاع و نصرت خواهى برخاستند. و كسانى كه ستم كردند به زودى خواهند دانست به كدام بازگشتگاهى بر مى‌گردند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سين. اين آيات قرآن و كتاب روشنگر است'),
(3161, 27, 2, 'كه هدايت و بشارتى براى مؤمنان است'),
(3162, 27, 3, 'همانان كه نماز برپا مى‌دارند و زكات مى‌دهند و ايشان به آخرت يقين دارند'),
(3163, 27, 4, 'بى‌ترديد كسانى كه به آخرت ايمان نمى‌آورند كارهايشان را در نظرشان آراسته‌ايم، از اين رو ايشان سرگشته‌اند'),
(3164, 27, 5, 'آنان كسانى‌اند كه عذاب سختى برايشان خواهد بود و در آخرت از همه زيانكارترند'),
(3165, 27, 6, 'و حقا اين قرآن از سوى حكيمى دانا بر تو القا مى‌شود'),
(3166, 27, 7, '[ياد آر] هنگامى را كه موسى به خانواده‌ى خود گفت: من آتشى ديدم، به زودى براى شما خبرى از آن خواهم آورد يا شعله آتشى براى شما مى‌آورم تا گرم شويد'),
(3167, 27, 8, 'چون نزد آن آمد، ندايى برخاست كه: مبارك باد آن كه در آتش است و آن كه در اطراف آن است (موسى و فرشتگان)، و منزه است خدا پروردگار جهانيان'),
(3168, 27, 9, 'اى موسى! اين منم خداى شكست ناپذير حكيم'),
(3169, 27, 10, 'و عصايت را بيفكن. پس چون ديدش همانند مارى [به سرعت‌] مى‌خزد، پشت كرده گريخت و به عقب خود نگاه نكرد. [خطاب شد:] اى موسى! مترس كه رسولان نزد من نمى‌ترسند'),
(3170, 27, 11, 'مگر كسى كه ستم كند، آن‌گاه بدى را به نيكى تبديل نمايد، پس البته من آمرزنده‌ى مهربانم'),
(3171, 27, 12, 'و دستت را در گريبانت كن تا بى‌هيچ عيبى، سفيد و درخشان بيرون آيد، [اين‌] در زمره‌ى معجزات نه گانه‌اى است [كه تو با آنها] به سوى فرعون و قومش [فرستاده مى‌شوى‌] همانا آنان قومى فاسقند'),
(3172, 27, 13, 'پس هنگامى كه آيات ما روشنگرانه به سويشان آمد، گفتند: اين سحرى آشكار است'),
(3173, 27, 14, 'و با آن كه باطنشان بدان يقين داشت، از روى ظلم و تكبّر آن را انكار كردند. پس ببين عاقبت مفسدان چگونه شد'),
(3174, 27, 15, 'و به راستى به داود و سليمان دانشى عطا كرديم، و گفتند: ستايش خدايى را كه ما را بر بسيارى از بندگان مؤمنش برترى داده است'),
(3175, 27, 16, 'و سليمان وارث داود شد، و گفت: اى مردم! ما زبان پرندگان را تعليم يافته‌ايم و از هر چيزى [بهره‌اى‌] داده شده‌ايم. راستى اين همان تفضل آشكار است'),
(3176, 27, 17, 'و براى سليمان سپاهيانش از جنّ و انس و پرندگان گردآورى شدند تا آنها يك جا جمع شوند [و در اختيار باشند]'),
(3177, 27, 18, 'تا آن‌گاه كه به وادى مورچگان رسيدند، مورچه‌اى گفت: اى مورچگان! به خانه‌هايتان داخل شويد، مبادا سليمان و سپاهيانش بدون توجه شما را پايمال كنند'),
(3178, 27, 19, 'سليمان از گفتار او تبسمى كرد و خنديد و گفت: پروردگارا! مرا بر آن دار تا نعمتى را كه به من و پدر و مادرم ارزانى داشته‌اى شكر كنم و عمل صالحى كه مى‌پسندى انجام دهم، و مرا به رحمت خويش در جمع بندگان شايسته‌ات در آور'),
(3179, 27, 20, 'و جوياى حال پرندگان شد و گفت: چرا من هدهد را نمى‌بينم؟ آيا از غايبان است'),
(3180, 27, 21, 'حتما او را به عذابى سخت عذاب مى‌كنم يا سرش را مى‌برم، مگر آن كه دليل روشنى براى من بياورد'),
(3181, 27, 22, 'پس ديرى نپاييد كه [هدهد آمد و] گفت: به چيزى دست يافته‌ام كه تو به آن دست نيافته‌اى، و از سرزمين سبا براى تو خبر يقينى آورده‌ام'),
(3182, 27, 23, 'من [آن جا] زنى را يافتم كه بر آنها سلطنت مى‌كند و از هر نعمتى به او داده شده است و تختى بزرگ دارد'),
(3183, 27, 24, 'او و قومش را چنين يافتم كه به جاى خدا براى خورشيد سجده مى‌كنند، و شيطان اعمالشان را براى آنها آراسته و آنها را از راه [راست‌] بازداشته است، از اين رو آنها هدايت نمى‌يابند'),
(3184, 27, 25, '[كار شيطان اين بود] كه آنها سجده نكنند براى خدايى كه پنهانى‌ها را در آسمان‌ها و زمين بيرون مى‌آورد و آنچه را كه نهان مى‌داريد يا عيان مى‌سازيد مى‌داند'),
(3185, 27, 26, 'خداوندى كه هيچ خدايى جز او نيست، صاحب عرش بزرگ است'),
(3186, 27, 27, '[سليمان‌] گفت: خواهيم ديد آيا راست گفته‌اى يا از دروغگويان بوده‌اى'),
(3187, 27, 28, 'اين نامه مرا ببر و به طرف آنها بيفكن، آن‌گاه از آنها دور شو و بنگر چه پاسخ مى‌دهند'),
(3188, 27, 29, '[ملكه سبا] گفت: اى سران كشور! نامه‌اى ارجمند به سوى من انداخته شده است'),
(3189, 27, 30, 'كه آن از طرف سليمان و به نام خداوند هستى بخش مهربان است'),
(3190, 27, 31, '[و در آن آمده است‌] كه بر من برترى مجوييد و مطيعانه به سوى من آييد'),
(3191, 27, 32, 'گفت: اى سران قوم! نظر خود را در اين امر به من بگوييد كه من [تاكنون‌] بى‌حضور شما تصميم قطعى نگرفته‌ام'),
(3192, 27, 33, 'گفتند: ما صاحبان قدرت و سخت جنگاوريم و اختيار كار با توست، پس بنگر چه فرمان مى‌دهى'),
(3193, 27, 34, '[ملكه‌] گفت: پادشاهان چون به شهرى در آيند، آن را تباه و عزيزانش را خوار مى‌گردانند و اين گونه عمل مى‌كنند'),
(3194, 27, 35, 'و اينك من هديه‌اى به سويشان مى‌فرستم و منتظر مى‌مانم كه فرستادگان با چه [پاسخى‌] باز مى‌گردند'),
(3195, 27, 36, 'و چون [فرستاده‌] نزد سليمان آمد، گفت: آيا مرا به مال مدد مى‌دهيد؟ آنچه خدا به من عطا كرده بهتر است از آنچه به شما داده است. بلكه شما به هديه‌تان دلخوشيد'),
(3196, 27, 37, '[اكنون‌] به سوى آنها بازگرد كه قطعا سپاهيانى برسرشان مى‌آوريم كه در برابر آنها تاب ايستادگى نداشته باشند و از آن ديار به خوارى و زبونى بيرونشان مى‌كنيم'),
(3197, 27, 38, '[سپس‌] گفت: اى سركردگان! كدام يك از شما تخت او را پيش از آن كه به حالت تسليم نزد من آيند برايم مى‌آورد'),
(3198, 27, 39, 'عفريتى از جنّ گفت: من آن را پيش از آن كه از جايت برخيزى نزد تو حاضر مى‌كنم، و من بر اين توانا و امينم'),
(3199, 27, 40, 'كسى كه نزد او علمى از كتاب بود، گفت: من آن را پيش از آن كه چشم خود را بر هم زنى نزد تو مى‌آورم. پس چون سليمان آن تخت را نزد خود حاضر ديد، گفت: اين از فضل پروردگار من است، تا مرا بيازمايد كه آيا سپاس مى‌گزارم يا ناسپاسى مى‌كنم و هر كه سپاس گزارد تنها'),
(3200, 27, 41, 'گفت: تخت ملكه را برايش ناشناس سازيد تا ببينيم آيا پى مى‌برد يا از آنهاست كه پى نمى‌برند'),
(3201, 27, 42, 'پس وقتى ملكه آمد بدو گفته شد: آيا تخت تو همين گونه است؟ گفت: گويا اين خود آن است، و ما پيش از اين هم [به قدرت سليمان‌] واقف شده بوديم و تسليم بوده‌ايم'),
(3202, 27, 43, 'و او را آنچه به جاى خدا مى‌پرستيد [از ايمان به خدا] بازداشته بود، همانا او از گروه كافران بود'),
(3203, 27, 44, 'به او گفته شد: وارد كاخ شو. پس چون آن را ديد، پنداشت نهر آبى است، و جامه از دو ساق خويش بالا زد [تا از آب بگذرد] سليمان گفت: اين كاخى صيقلى شده از بلورهاست. [ملكه‌] گفت: پروردگارا! من به خود ستم كردم و اينك با سليمان تسليم خداوند، پروردگار جهانيان شد'),
(3204, 27, 45, 'و به راستى به سوى ثمود برادرشان صالح را فرستاديم كه خدا را بپرستيد اما آنها دو گروه شدند و به خصومت برخاستند'),
(3205, 27, 46, 'گفت: اى قوم من! چرا پيش از رحمت، خواهان شتاب در عذابيد؟ چرا از خدا طلب آمرزش نمى‌كنيد تا مورد رحمت قرار گيريد'),
(3206, 27, 47, 'گفتند: ما تو و هر كه را با توست به فال بد گرفته‌ايم. گفت: فال [نيك و بد] شما نزد خداست، بلكه شما مردمى هستيد كه امتحان مى‌شويد'),
(3207, 27, 48, 'و در آن شهر، نه دسته بودند كه در آن سرزمين فساد مى‌كردند و به اصلاح نمى‌پرداختند'),
(3208, 27, 49, 'گفتند: بياييد به نام خدا هم قسم شويد كه حتما به «صالح» و كسانش شبيخون مى‌زنيم، سپس به خونخواه وى [به دروغ‌] خواهيم گفت: ما در محلّ قتل كسانش حاضر نبوديم و ما قطعا راست مى‌گوييم'),
(3209, 27, 50, 'و نيرنگى طرح كردند و ما نيز نيرنگى تدبير كرديم و خبر نداشتند'),
(3210, 27, 51, 'پس بنگر كه عاقبت نيرنگشان چگونه شد، كه ما آنها و قومشان را همگى هلاك كرديم'),
(3211, 27, 52, 'و اين هم خانه‌هاى خالى آنهاست به سزاى ظلمى كه كردند. قطعا در اين [امر] براى مردمى كه بدانند عبرتى است'),
(3212, 27, 53, 'و كسانى را كه ايمان آورده و پرهيزكارى مى‌كردند نجات داديم'),
(3213, 27, 54, 'و [ياد كن‌] لوط را كه چون به قوم خود گفت: آيا به فحشا روى مى‌آوريد در حالى كه [نتايج شوم آن را] مى‌بينيد'),
(3214, 27, 55, 'آيا شما به جاى زنان، از روى شهوت به سراغ مردان مى‌آييد؟ بلكه شما مردمى جهالت پيشه‌ايد'),
(3215, 27, 56, 'ولى پاسخ قومش غير از اين نبود كه گفتند: خاندان لوط را از شهرتان بيرون كنيد كه آنها مردمى هستند كه پاكى مى‌ورزند [و مقدس مآبند]'),
(3216, 27, 57, 'پس او و خانواده‌اش را نجات داديم، جز زنش را كه مقدر كرديم از بازماندگان باشد'),
(3217, 27, 58, 'و باران [عذاب‌] برايشان بارانديم و باران بيم داده شدگان، بد بارانى است'),
(3218, 27, 59, 'بگو: سپاس براى خداست، و سلام بر آن بندگانش كه برگزيده است. آيا خدا بهتر است يا آنچه [با او] شريك مى‌كنند'),
(3219, 27, 60, 'يا آن كس كه آسمان‌ها و زمين را خلق كرد و براى شما آبى از آسمان نازل نمود، سپس به وسيله آن باغ‌هاى با شكوهى رويانيديم كه رويانيدن درختانش كار شما نبود؟ آيا با اين خداوند خدايى است؟ بلكه آنها گروهى هستند كه [از حق‌] عدول مى‌كنند'),
(3220, 27, 61, 'يا آن كه زمين را قرارگاه كرد و در آن نهرها پديد آورد و براى آن كوه‌هاى استوار قرار داد و ميان دو دريا حايلى گذاشت؟ آيا با اين خداوند خدايى است؟ بلكه بيشترشان نمى‌دانند'),
(3221, 27, 62, 'يا كيست كه درمانده را، چون وى را بخواند اجابت مى‌كند و گرفتارى را برطرف مى‌سازد و شما را در زمين جانشين مى‌كند؟ آيا با اين خداوند خدايى است؟ چه كم متذكّر مى‌شويد'),
(3222, 27, 63, 'يا كيست كه شما را در تاريكى‌هاى صحرا و دريا هدايت مى‌كند و كيست كه پيشاپيش باران رحمتش، بادها را به مژده مى‌فرستد؟ آيا با اين خداوند خدايى است؟ خداوند فراتر از چيزى است كه با او شريك مى‌كنند'),
(3223, 27, 64, 'يا آن كس كه خلقت را آغاز مى‌كند و سپس آن را برمى‌گرداند و آن كس كه از آسمان و زمين به شما روزى مى‌دهد؟ آيا با اين خداوند خدايى است؟ بگو: برهان خويش را بياوريد اگر راست مى‌گوييد'),
(3224, 27, 65, 'بگو: جز خدا، هر كه در آسمان‌ها و زمين است غيب نمى‌داند و نمى‌دانند كى برانگيخته مى‌شوند'),
(3225, 27, 66, 'بلكه علم آنها در باره‌ى آخرت به پايان رسيده [كه آن را انكار مى‌كنند]؟ بلكه ايشان در [اصل‌] آن ترديد دارند. بلكه آنها در آن زمينه كوردل‌اند'),
(3226, 27, 67, 'و كسانى كه كافر شدند گفتند: آيا وقتى كه ما و پدرانمان خاك شديم، حتما [زنده از گور] بيرون آورده مى‌شويم'),
(3227, 27, 68, 'البته به ما و پدرانمان نيز قبلا همين را وعده داده بودند. اينها جز افسانه‌هاى پيشينيان نيست'),
(3228, 27, 69, 'بگو: در زمين بگرديد و بنگريد عاقبت گنه‌پيشگان چگونه شد'),
(3229, 27, 70, 'و بر آنان غم مخور و از نيرنگ‌هايى كه مى‌زنند تنگدل مباش'),
(3230, 27, 71, 'و مى‌گويند: اين وعده كى خواهد بود اگر راست مى‌گوييد'),
(3231, 27, 72, 'بگو: شايد برخى از آنچه شتاب در آن را مى‌خواهيد در تعقيب شما باشد'),
(3232, 27, 73, 'و راستى پروردگارت نسبت به مردم، صاحب كرم است ولى بيشترشان سپاس نمى‌دارند'),
(3233, 27, 74, 'و بى‌ترديد پروردگار تو آنچه را سينه‌هايشان پنهان مى‌دارد و آنچه را آشكار مى‌كنند مى‌داند'),
(3234, 27, 75, 'و هيچ پنهانى در آسمان و زمين نيست مگر اين كه در كتابى روشن (لوح محفوظ) ثبت است'),
(3235, 27, 76, 'بى‌گمان، اين قرآن بسيارى از چيزهايى را كه بنى اسرائيل در آن اختلاف دارند برايشان حكايت مى‌كند'),
(3236, 27, 77, 'و به راستى كه آن براى مؤمنان هدايت و رحمت است'),
(3237, 27, 78, 'البته پروردگار تو طبق حكم خود ميان آنان داورى مى‌كند و او شكست‌ناپذير داناست'),
(3238, 27, 79, 'پس بر خدا توكّل كن كه تو واقعا بر حق آشكار هستى'),
(3239, 27, 80, 'البته تو نمى‌توانى مردگان را شنوا كنى و نمى‌توانى نداى دعوت را به گوش كران برسانى هنگامى كه پشت مى‌كنند و روى بر مى‌تابند'),
(3240, 27, 81, 'و تو هدايت كننده‌ى كوردلان از گمراهى‌شان نيستى. تو فقط به گوش كسانى توانى رساند كه به آيات ما ايمان دارند و خود [مطيع و] تسليمند'),
(3241, 27, 82, 'و چون وعده‌ى عذاب بر ايشان واقع شود، جنبنده‌اى را از زمين براى ايشان بيرون مى‌آوريم كه با آنها تكلم مى‌كند [و مى‌گويد]: مردم [چنان كه بايد] به آيات ما يقين نمى‌كنند'),
(3242, 27, 83, 'و آن روز كه از هر امّتى گروهى را كه آيات ما را تكذيب مى‌كردند گرد مى‌آوريم و آن گاه نگاه داشته مى‌شوند [تا همه به هم ملحق شوند]'),
(3243, 27, 84, 'تا زمانى كه [به پاى حساب‌] بيايند، خدا مى‌گويد: آيا آيات مرا تكذيب كرديد بى‌آن كه بدان احاطه علمى داشته باشيد؟ اين چه كارى بود كه مى‌كرديد'),
(3244, 27, 85, 'و حكم عذاب بر ايشان واقع گردد به سبب ظلمى كه كردند. پس ايشان دم بر نيارند'),
(3245, 27, 86, 'آيا نديده‌اند كه ما شب را پديد آورديم تا در آن بياسايند و روز را روشنى بخش گردانيديم؟ قطعا در اين [امر] براى مردمى كه ايمان مى‌آورند مايه‌هاى عبرت است'),
(3246, 27, 87, 'و روزى كه در صور دميده شود، پس هر كه در آسمان‌ها و هر كه در زمين است به هراس افتد، مگر آن كس كه خدا بخواهد. و جملگى خاضعانه به سوى او مى‌آيند'),
(3247, 27, 88, 'و كوه‌ها را مى‌بينى و آنها را ساكن مى‌انگارى در حالى كه مانند ابر در حركتند. اين صنع خداست كه هر چيزى را در كمال استوارى پديد آورده. و او به آنچه مى‌كنيد مسلما آگاه است'),
(3248, 27, 89, 'هر كس [عمل‌] نيكى آورد، پاداشى بهتر از آن خواهد داشت، و آنها آن روز از هراسى بزرگ در امانند'),
(3249, 27, 90, 'و هر كس [عمل‌] بدى آورد [با] صورت‌هايشان در آتش دوزخ واژگون شوند. [گفته مى‌شود:] آيا جز آنچه مى‌كرديد سزا داده مى‌شويد'),
(3250, 27, 91, 'من مأمورم كه تنها خداوند اين شهر را كه آن را گرامى داشته و هر چيزى از آن اوست پرستش كنم، و فرمان يافته‌ام كه از مسلمانان باشم'),
(3251, 27, 92, 'و اين كه قرآن را [بر شما] تلاوت كنم. پس هر كه هدايت پذيرد به سود خود هدايت يافته و هر كه گمراه شود، پس بگو: من فقط از هشدار دهندگانم'),
(3252, 27, 93, 'و بگو: ستايش از آن خداست. به زودى آياتش را به شما نشان خواهد داد آن را خواهيد شناخت. و پروردگار تو از آنچه مى‌كنيد غافل نيست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا، سين، ميم'),
(3254, 28, 2, 'اين آيات كتاب روشنگر است'),
(3255, 28, 3, 'ما از خبر مهم موسى و فرعون براى مردمى كه ايمان مى‌آورند به درستى بر تو مى‌خوانيم'),
(3256, 28, 4, 'همانا فرعون در زمين سربرافراشت و مردم آن را چند فرقه‌اى كرد. گروهى از آنها را به زبونى مى‌كشيد، پسرانشان را سر مى‌بريد و زنانشان را زنده مى‌گذاشت. به راستى كه وى از فسادكاران بود'),
(3257, 28, 5, 'و مى‌خواستيم بر كسانى كه در زمين به زبونى كشيده شده‌اند منّت نهيم و آنها را پيشوايان [مردم‌] گردانيم و ايشان را وارثان [زمين‌] كنيم'),
(3258, 28, 6, 'و در زمين قدرتشان دهيم و به فرعون و هامان و سپاهشان چيزى را كه از آن حذر داشتند نشان دهيم'),
(3259, 28, 7, 'و به مادر موسى الهام كرديم كه او را شير ده و چون بر [جان‌] او ترسيدى به دريايش بيفكن و نترس و اندوه مدار كه ما او را به تو باز مى‌گردانيم و از پيمبرانش قرار مى‌دهيم'),
(3260, 28, 8, 'پس خاندان فرعون او را [از آب‌] گرفتند تا سر انجام دشمن جان آنان و مايه اندوهشان گردد. آرى فرعون و هامان و سپاهشان خطاكار بودند'),
(3261, 28, 9, 'و همسر فرعون گفت: [اين كودك‌] نور چشم من و تو خواهد بود، او را مكشيد، اميد است كه ما را سودى دهد، يا او را به فرزندى بگيريم، ولى آنها خبر نداشتند [چه مى‌كنند]'),
(3262, 28, 10, 'و مادر موسى را دل تهى شد، كه اگر دلش را قوى نكرده بوديم تا از مؤمنان باشد چيزى نمانده بود كه راز را فاش سازد'),
(3263, 28, 11, 'و به خواهر موسى گفت: از پى او برو. پس او را از دور مى‌نگريست در حالى كه آنها متوجه نبودند'),
(3264, 28, 12, 'و ما شير دايگان را از پيش بر او حرام كرده بوديم. پس [خواهرش آمد و] گفت: آيا شما را بر خانواده‌اى راهنمايى كنم كه او را برايتان سرپرستى كنند و خيرخواهش باشند'),
(3265, 28, 13, 'پس او را به مادرش بازگردانديم تا چشمش [بدو] روشن شود و غم نخورد و بداند كه وعده‌ى خدا درست است، ولى بيشترشان نمى‌دانند'),
(3266, 28, 14, 'و چون به رشد و كمال خويش رسيد و برومند شد، به او حكمت و دانش عطا كرديم و نيكوكاران را چنين پاداش مى‌دهيم'),
(3267, 28, 15, 'و او در وقت سرگرمى اهل شهر، وارد آن جا شد و دو مرد را ديد كه زد و خورد مى‌كردند يكى از پيروان او و ديگرى از دشمنانش بود. آن كس كه از پيروانش بود بر ضد كسى كه دشمنش بود از وى استمداد كرد. پس موسى مشتى بر او زد و كارش را ساخت. گفت: اين كار شيطان است، چ'),
(3268, 28, 16, 'گفت: پروردگارا! من بر خويشتن ستم كردم، مرا ببخش! پس [خدا] از او درگذشت كه او آمرزنده‌ى مهربان است'),
(3269, 28, 17, 'گفت: پروردگارا! به پاس نعمتى كه بر من ارزانى داشتى هرگز پشتيبان مجرمان نخواهم شد'),
(3270, 28, 18, 'او در شهر، ترسان و نگران [از تعقيب، آن شب را] صبح كرد. ناگهان [ديد] همان كسى كه ديروز از وى استمداد كرده بود باز هم به فرياد از وى مدد مى‌خواهد. موسى به او گفت: به راستى كه تو آشكارا [ماجراجو و] گمراهى'),
(3271, 28, 19, 'و چون خواست به سوى آن كه دشمن هر دوشان بود حمله‌ور شود، گفت: اى موسى! آيا مى‌خواهى مرا بكشى، چنان كه ديروز يكى را كشتى؟ تو مى‌خواهى در [اين سر] زمين فقط گردنكشى كنى و نمى‌خواهى از اصلاحگران باشى'),
(3272, 28, 20, 'و از دور دست شهر مردى شتابان بيامد و گفت: اى موسى! سران قوم در مورد كشتن تو به مشورت نشسته‌اند، پس از شهر خارج شو كه من از خيرخواهان تو هستم'),
(3273, 28, 21, 'پس ترسان و نگران از آن [شهر] بيرون شد و گفت: اى پروردگار من! مرا از قوم ستمكار رهايى بخش'),
(3274, 28, 22, 'و چون به جانب شهر مدين روى نهاد گفت: اميد است پروردگارم مرا به راه راست هدايت كند'),
(3275, 28, 23, 'و چون به آب مدين رسيد، گروهى از مردم را بر [گرد] آن يافت كه [دام‌هاى خود را] آب مى‌دادند، و پشت سرشان دو زن را يافت كه [گوسفندان خود را] از آب باز مى‌دارند. گفت: كار شما چيست؟ گفتند: [ما به گوسفندان خود] آب نمى‌دهيم تا شبانان [گوسفندانشان را] بازگردا'),
(3276, 28, 24, 'پس براى آن دو [گوسفندانشان را] براى آن دو آب داد، آن‌گاه به سوى سايه برگشت و گفت: پروردگارا! من به هر [رزق و] خيرى كه برايم بفرستى سخت نيازمندم'),
(3277, 28, 25, 'پس يكى از آن دو در حالى كه از روى حيا گام برمى‌داشت نزد وى آمد و گفت: پدرم تو را مى‌طلبد تا پاداش آن كه براى ما آب دادى به تو بپردازد. و چون موسى نزد وى آمد و سرگذشت خود را بر او حكايت كرد، گفت: مترس كه از گروه ستمكاران نجات يافتى'),
(3278, 28, 26, 'يكى از آن دو دختر گفت: اى پدر! او را استخدام كن همانا بهترين كسى كه استخدام مى‌كنى، كسى است كه نيرومند و امين است'),
(3279, 28, 27, '[شعيب‌] گفت: من مى‌خواهم يكى از اين دو دختران خود را به نكاح تو در آورم، به اين شرط كه هشت سال براى من كار كنى، و اگر ده سال را به پايان رسانى [اختيار] از جانب توست، و نمى‌خواهم بر تو سخت بگيرم، و مرا اگر خدا بخواهد از صالحان خواهى يافت'),
(3280, 28, 28, '[موسى پذيرفت و] گفت: اين [قرارداد] ميان من و تو باشد كه هر يك از دو مدّت را به انجام رسانيدم بر من تعدى و تحميلى نباشد و خدا بر آنچه مى‌گوييم و كيل است'),
(3281, 28, 29, 'و چون موسى آن مدّت را به پايان رسانيد و با خانواده‌ى خويش راهى [مصر] شد، از جانب كوه طور آتشى ديد! به خانواده‌ى خود گفت: اين جا بمانيد كه من آتشى ديدم، شايد از آن خبرى يا شعله‌اى برايتان بياورم تا گرم شويد'),
(3282, 28, 30, 'پس چون به نزد آتش آمد، از جانب راست وادى، در آن جايگاه مبارك، از درخت ندا داده شد كه: اى موسى! همانا من خداوند، پروردگار جهانيان هستم'),
(3283, 28, 31, 'كه عصاى خود را بيفكن. پس چون ديد آن مثل مارى [به سرعت‌] مى‌خزد، به عقب بازگشت و به پشت سر نگاه نكرد. [خطاب شد:] اى موسى! پيش آى و مترس كه تو از ايمنانى'),
(3284, 28, 32, 'دست خود را به گريبانت ببر تا سفيد [و درخشان‌] و بى‌گزند بيرون آيد و دست و بازويت را به خود بچسبان تا ترس تو فرو نشيند [و مضطرب نباشى‌] و اينها دو برهان از جانب پروردگار توست [كه بايد] به سوى فرعون و مهترانش [ببرى‌]، زيرا آنها قومى نافرمان‌اند'),
(3285, 28, 33, 'گفت: پروردگارا! من كسى از ايشان را كشته‌ام، پس مى‌ترسم مرا بكشند'),
(3286, 28, 34, 'و برادرم هارون از من زبان آورتر است، پس او را با من به عنوان مددكار بفرست تا مرا تصديق كند همانا بيم آن دارم كه مرا تكذيب كنند'),
(3287, 28, 35, 'فرمود: به زودى بازويت را به وسيله برادرت محكم خواهيم كرد و براى شما دو [نفر]، سلطه‌اى قرار خواهيم داد كه با [وجود] آيات ما به شما دست نخواهند يافت شما و هر كه شما را پيروى كند پيروزيد'),
(3288, 28, 36, 'پس چون موسى معجزات روشن ما را براى آنها آورد، گفتند: اين جز سحرى ساختگى نيست و ما اين را در ميان پدران نخستين خود نشنيده‌ايم'),
(3289, 28, 37, 'و موسى گفت: پروردگار من بهتر مى‌داند چه كسى هدايت را از نزد او آورده و [بهتر مى‌داند] عاقبت نيك آن سرا براى چه كسى است مسلما ظالمان رستگار نمى‌شوند'),
(3290, 28, 38, 'و فرعون گفت: اى بزرگان قوم! من براى شما معبودى جز خويشتن نمى‌شناسم پس اى هامان! برايم بر گل آتش بيفروز [و آجر بپز] و برجى [بلند] براى من بساز تا شايد به سوى خداى موسى سركشم، و من جدا او را از دروغگويان مى‌انگارم'),
(3291, 28, 39, 'و او و سپاهيانش در زمين به ناحق سركشى كردند و پنداشتند كه ايشان به سوى ما بازگردانده نمى‌شوند'),
(3292, 28, 40, 'پس ما او و سپاهيانش را گرفتيم و آنها را در دريا افكنديم، پس بنگر كه فرجام ستمكاران چگونه شد'),
(3293, 28, 41, 'و آنها را پيشوايانى قرار داديم كه به سوى آتش مى‌خواندند، و روز رستاخيز يارى نخواهند شد'),
(3294, 28, 42, 'و در اين دنيا لعنتى بدرقه آنها كرديم و روز قيامت آنان از [جمله‌] زشت رويانند'),
(3295, 28, 43, 'و به راستى پس از آن كه نسل‌هاى نخستين را هلاك كرديم، به موسى كتاب داديم كه مردم را بصيرت‌ها و هدايت و رحمت باشد، به اميد آن كه ايشان پند گيرند'),
(3296, 28, 44, 'و تو در جانب غربى [طور] نبودى آن‌گاه كه امر [پيامبرى‌] را به موسى واگذاشتيم، و از حاضران [در آن صحنه‌] نبودى'),
(3297, 28, 45, 'ليكن ما نسل‌هايى به وجود آورديم و عمرشان طولانى شد [و آثار انبيا فراموش گرديد] و تو در ميان اهل مدين اقامت نداشتى تا [از وضع آنان آگاه باشى و] آيات ما را [در اين مورد] براى آنها [مشركان مكه‌] بخوانى ليكن ما بوديم كه پيامبران را مى‌فرستاديم [و تو را ف'),
(3298, 28, 46, 'و آن دم كه [موسى را] ندا داديم، تو در كنار طور نبودى، ولى [اين آيات‌] رحمتى از پروردگار تو است، تا قومى را كه هيچ انذار كننده‌اى پيش از تو برايشان نيامده است بيم دهى، باشد كه آنها پند گيرند'),
(3299, 28, 47, 'و اگر به كيفر آنچه به دست خود پيش فرستادند مصيبتى به آنها نمى‌رسيد، كه سپس بگويند: پروردگارا! چرا رسولى به سوى ما نفرستادى تا از آيات تو پيروى كنيم و از مؤمنان باشيم، [تو را به جانب ايشان نمى‌فرستاديم‌]'),
(3300, 28, 48, 'پس چون حق از جانب ما برايشان آمد، گفتند: چرا نظير آنچه به موسى داده شد به او داده نشده است؟ آيا اينان پيش از اين به آنچه به موسى داده شده بود كافر نشدند؟ گفتند: [تورات و قرآن‌] دو جادويى هستند كه پشتيبان يكديگرند، و گفتند: ما به همه كافريم'),
(3301, 28, 49, 'بگو: پس كتابى از جانب خداوند بياوريد كه از اين دو هدايت كننده‌تر باشد تا از آن پيروى كنم اگر راست مى‌گوييد'),
(3302, 28, 50, 'پس اگر تو را اجابت نكردند، بدان كه فقط هوس‌هاى خود را پيروى مى‌كنند و كيست گمراه‌تر از آن كه به غير از هدايت خدا از هواى نفس خود پيروى كند؟ بى‌ترديد خدا مردم ستمگر را هدايت نمى‌كند'),
(3303, 28, 51, 'و به راستى براى ايشان، سخن از پى سخن آورديم به اميد آن كه پند گيرند'),
(3304, 28, 52, 'كسانى كه پيش از اين [قرآن‌] كتابشان داديم آنها به آن ايمان مى‌آورند'),
(3305, 28, 53, 'و چون [قرآن‌] بر ايشان تلاوت شود مى‌گويند: بدان ايمان آورديم كه آن درست است و از طرف پروردگار ماست ما پيش از آن هم تسليم بوده‌ايم'),
(3306, 28, 54, 'آنان به پاس آن كه صبر كردند، و بدى را با نيكى دفع مى‌كنند و از آنچه روزى‌شان كرده‌ايم انفاق مى‌كنند، دوبار پاداش داده مى‌شوند'),
(3307, 28, 55, 'و چون سخن بيهوده بشنوند از آن روى بر مى‌تابند و مى‌گويند: اعمال ما از آن ما و اعمال شما از آن شماست سلام بر شما، ما طالب [معاشرت‌] جاهلان نيستيم'),
(3308, 28, 56, 'در حقيقت تو هر كه را دوست دارى نمى‌توانى [به اجبار] هدايت كنى، ليكن خداست كه هر كس را بخواهد هدايت مى‌كند، و او هدايت پذيران را بهتر مى‌شناسد'),
(3309, 28, 57, 'و گفتند: اگر با تو از هدايت پيروى كنيم از سرزمين خود ربوده [و تبعيد] خواهيم شد. آيا آنها را در حرمى امن جاى نداده‌ايم كه فرآورده‌هاى هر چيزى كه رزقى از جانب ماست به سوى آن سرازير مى‌شود؟ ولى بيشترشان نمى‌دانند'),
(3310, 28, 58, 'و چه بسيار شهرهايى را كه [از شدّت رفاه‌] در زندگى خويش طغيان كرده بودند هلاك كرديم، و اين خانه‌هاى آنهاست كه بعد از ايشان جز اندكى مورد سكونت قرار نگرفت، و وارثان، خود ما بوديم'),
(3311, 28, 59, 'و پروردگار تو هرگز ويرانگر شهرها نبوده است تا آن‌گاه كه در مركز آنها پيامبرى برانگيزد كه آيات ما را بر ايشان تلاوت كند و ما ويران كننده‌ى شهرها نبوديم مگر آن كه مردمش ستمكار بودند'),
(3312, 28, 60, 'و آنچه به شما داده شده است، كالاى زندگى دنيا و زينت آن است، ولى آنچه نزد خداست بهتر و ماندنى‌تر است پس آيا نمى‌انديشيد'),
(3313, 28, 61, 'آيا كسى كه وعده‌ى نيكو به او داده‌ايم و او ديدار كننده آن است، مانند كسى است كه از متاع زندگى دنيا بهره‌مندش گردانيديم، سپس او روز قيامت از جمله احضار شدگان [براى كيفر] خواهد بود'),
(3314, 28, 62, 'و روزى كه آنها را ندا مى‌دهد و مى‌گويد: آن شريكان من كه مى‌پنداشتيد كجايند'),
(3315, 28, 63, 'آنان كه حكم عذاب برايشان حتمى شده مى‌گويند: پروردگارا! اينانند كسانى كه گمراهشان كرديم گمراهشان كرديم هم چنان كه خود گمراه شديم. [ما از آنها] به سوى تو بيزارى مى‌جوييم. اينان ما را نمى‌پرستيدند [بلكه هواى نفس خود را مى‌پرستيدند]'),
(3316, 28, 64, 'و گفته شود: شريكان خود را فراخوانيد. پس آنها را مى‌خوانند، ولى پاسخشان نمى‌دهند و عذاب را مى‌بينند [و آرزو مى‌كنند] كاش هدايت يافته بودند'),
(3317, 28, 65, 'و روزى را كه خدا آنها را ندا مى‌دهد و مى‌گويد: فرستادگان را چه پاسخ داديد'),
(3318, 28, 66, 'پس در آن روز خبرها برايشان پوشيده بماند، پس از يكديگر نيز نمى‌پرسند'),
(3319, 28, 67, 'و امّا كسى كه توبه كند و ايمان آورد و به كار شايسته بپردازد، اميد كه از رستگاران شود'),
(3320, 28, 68, 'و پروردگار تو هر چه بخواهد مى‌آفريند و بر مى‌گزيند، و آنها را اختيارى نيست. منزه است خدا و فراتر است از چيزى كه [با او] شريك مى‌كنند'),
(3321, 28, 69, 'و پروردگار تو آنچه را سينه‌هايشان مستور مى‌دارد و آنچه را آشكار مى‌كنند مى‌داند'),
(3322, 28, 70, 'و اوست خداوند جز او معبودى نيست، و حمد در دنيا و آخرت از آن اوست، و حاكميت نيز از آن اوست، و به سوى او بازگردانده مى‌شويد'),
(3323, 28, 71, 'بگو: چه مى‌پنداريد؟ اگر خدا شب را تا روز رستاخيز بر شما ادامه دهد، جز خداوند كدامين معبود براى شما روشنى مى‌آورد؟ آيا نمى‌شنويد'),
(3324, 28, 72, 'بگو: چه مى‌پنداريد؟ اگر خدا روز را تا روز قيامت بر شما ادامه دهد، جز خدا كدامين معبود براى شما شبى مى‌آورد كه در آن آرام گيريد؟ آيا نمى‌بينيد'),
(3325, 28, 73, 'و از رحمت خداست كه شب و روز را براى شما پديد آورد تا در اين [يك‌] بياراميد و [در آن يك‌] از كرم او [روزى‌] بجوييد و باشد كه شكر گزاريد'),
(3326, 28, 74, 'و روزى كه [خداوند] ندايشان مى‌دهد و مى‌گويد: كجايند آن شريكانى كه براى من مى‌پنداشتيد'),
(3327, 28, 75, 'و از هر امتى گواهى جدا مى‌كنيم و مى‌گوييم: برهان خود را بياوريد. پس بدانند كه حق از آن خداست و آنچه افترا مى‌بستند از دستشان مى‌رود'),
(3328, 28, 76, 'قارون از قوم موسى بود و بر آنان ستم كرد، و از گنجينه‌ها آن قدر به او داده بوديم كه كليدهاى آنها بر گروه‌ى نيرومند سنگينى مى‌كرد، آن‌گاه كه قوم وى بدو گفتند: سرمستى مكن كه خدا سرمستى كنندگان را دوست نمى‌دارد'),
(3329, 28, 77, 'و در آنچه خدا به تو داده سراى آخرت را بطلب و سهم خود را [نيز] از دنيا فراموش مكن و همان‌طور كه خدا به تو احسان كرده احسان كن و در زمين فساد مجوى، كه خدا فسادگران را دوست نمى‌دارد'),
(3330, 28, 78, '[قارون‌] گفت: من اينها را از روى علمى كه نزد من است يافته‌ام. آيا ندانست كه خدا نسل‌هايى را پيش از او نابود كرد كه از او قوى‌تر و مال اندوزتر بودند؟ مجرمان از گناهانشان پرسيده نمى‌شوند [و بى‌حساب دوزخ مى‌روند]'),
(3331, 28, 79, 'پس قارون با تجملات خود بر قومش برآمد. كسانى كه طالب زندگى دنيا بودند گفتند: اى كاش، مثل آنچه به قارون داده شده به ما داده مى‌شد واقعا او صاحب بهره‌اى عظيم است'),
(3332, 28, 80, 'و دانش يافتگان گفتند: واى بر شما! پاداش خدا براى كسى كه ايمان آورده و كار شايسته كرده بهتر است، و آن را جز صابران در نيابند'),
(3333, 28, 81, 'آن‌گاه قارون را با خانه‌اش در زمين فرو برديم و او در برابر خدا هيچ گروهى نداشت كه يارى‌اش كنند و خود از انتقام گيرندگان نبود [كه از خود دفاع كند]'),
(3334, 28, 82, 'و كسانى كه ديروز موقعيت او را آرزو مى‌كردند صبح‌گاه مى‌گفتند: عجبا، مثل اين كه خدا روزى را براى هر كس از بندگانش بخواهد گسترش مى‌دهد يا تنگ مى‌گيرد. اگر خدا بر ما منّت ننهاده بوده ما را [نيز به زمين‌] فرو برده بود. عجبا، گويى كه كافران هرگز رستگار نم'),
(3335, 28, 83, 'آن سراى آخرت است كه براى كسانى قرار مى‌دهيم كه در زمين خواستار برترى جويى و فساد نيستند، و عاقبت [خوش‌] از آن پرهيزگاران است'),
(3336, 28, 84, 'هر كه نيكى بياورد، براى او بهتر از آن خواهد بود، و هر كه بدى آورد، آنها كه بدى كردند جز آنچه كرده‌اند كيفر نبينند'),
(3337, 28, 85, 'بى‌ترديد، همان كه اين قرآن را بر تو واجب كرد تو را به جايگاهت [مكه‌] باز مى‌گرداند. بگو: پروردگارم كسى را كه هدايت آورده و كسى را كه در گمراهى آشكار است بهتر مى‌شناسد'),
(3338, 28, 86, 'و تو اميد نداشتى كه اين كتاب بر تو القا شود، جز اين كه رحمتى از پروردگار تو باشد. پس هرگز پشتيبان كافران مباش'),
(3339, 28, 87, 'مبادا تو را از [تبليغ‌] آيات خدا بعد از آن كه بر تو نازل شده است بازدارند، و به سوى پروردگارت دعوت كن، و زنهار از مشركان مباش'),
(3340, 28, 88, 'و با خداوند معبود ديگرى مخوان. خدايى جز او نيست. جز ذات او همه چيز فانى است. فرمان از آن اوست و به سوى او بازگردانده مى‌شويد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف، لام، ميم'),
(3342, 29, 2, 'آيا مردم پنداشتند همين كه گفتند ايمان آورديم به حال خود رها مى‌شوند و مورد آزمايش قرار نمى‌گيرند'),
(3343, 29, 3, 'و به يقين كسانى را كه پيش از اينان بودند آزموديم، پس خدا حتما آنها را كه راست گفتند مى‌شناسد و [نيز] دروغگويان را مى‌شناسد'),
(3344, 29, 4, 'آيا كسانى كه مرتكب زشتى‌ها مى‌شوند مى‌پندارند كه بر ما پيشى مى‌گيرند؟ چه بد داورى مى‌كنند'),
(3345, 29, 5, 'كسى كه به ديدار خدا اميد دارد، به يقين زمان [ديدار] خدا آمدنى است، و او شنواى داناست'),
(3346, 29, 6, 'و هر كه بكوشد تنها به سود خود مى‌كوشد، همانا خداوند از جهانيان سخت بى‌نياز است'),
(3347, 29, 7, 'و كسانى كه ايمان آورده و كارهاى شايسته كردند، قطعا گناهانشان را از آنها مى‌زداييم و بهتر از آنچه مى‌كردند پاداششان مى‌دهيم'),
(3348, 29, 8, 'و به انسان سفارش كرديم كه به پدر و مادر خود نيكى كند. و اگر آنها كوشيدند تا تو چيزى را شريك من كنى كه بدان علم ندارى، از ايشان اطاعت مكن. بازگشت شما به سوى من است و شما را به كارهايى كه مى‌كرديد آگاه مى‌كنم'),
(3349, 29, 9, 'و كسانى كه ايمان آوردند و كارهاى شايسته كردند، البتّه آنان را در گروه شايستگان در مى‌آوريم'),
(3350, 29, 10, 'و از مردم كسانى هستند كه مى‌گويند: به خدا ايمان آورده‌ايم، و چون در راه خدا اذيت شوند، آزار مردم را مانند عذاب خدا قرار مى‌دهند، و اگر از جانب پروردگار تو يارى رسد حتما خواهند گفت: ما با شما بوديم. مگر خدا به آنچه در دل‌هاى جهانيان است داناتر نيست'),
(3351, 29, 11, 'البتّه خدا كسانى را كه ايمان آورده‌اند مى‌شناسد و منافقان را نيز نيك مى‌شناسد'),
(3352, 29, 12, 'و كسانى كه كافر شدند، به كسانى كه ايمان آورده‌اند گفتند: طريقت ما را پيروى كنيد و بايد گناهانتان را گردن بگيريم! ولى آنها چيزى از گناهانشان را به گردن نخواهند گرفت. قطعا آنها دروغگويانند'),
(3353, 29, 13, 'و بى‌ترديد بارهاى گناه خودشان و بارهاى ديگران را اضافه بر بار خود بر دوش مى‌كشند، و مسلما روز قيامت از آنچه افترا مى‌بستند بازخواست خواهند شد'),
(3354, 29, 14, 'و به راستى نوح را به سوى قومش فرستاديم. پس در ميان آنها هزار سال جز پنجاه سال بماند، امّا سر انجام طوفان آنها را در گرفت در حالى كه ستمكار بودند. و ما هم بحاملين: ولى ايشان به گردن گيرنده نيستند. خطايا جمع خطيئة: گناهان. ليحملنّ: البته حتما به گردن م'),
(3355, 29, 15, 'پس او را با كشتى‌نشينان نجات داديم و آن را براى جهانيان عبرتى ساختيم'),
(3356, 29, 16, 'و ابراهيم را [فرستاديم‌] چون به قوم خويش گفت: خدا را بپرستيد و از او پروا داريد. اين كار براى شما بهتر است، اگر مى‌دانستيد'),
(3357, 29, 17, 'جز اين نيست كه به جاى خداوند، بت‌هايى را مى‌پرستيد و دروغى مى‌سازيد. در حقيقت، آنها را كه جز خدا مى‌پرستيد، مالك روزى شما نيستند. پس روزى را نزد خدا بجوييد و او را بپرستيد و وى را سپاس گزاريد كه به سوى او بازگردانده مى‌شويد'),
(3358, 29, 18, 'و اگر تكذيب مى‌كنيد، در حقيقت امّت‌هايى كه پيش از شما بودند نيز تكذيب كردند، و بر پيامبر جز ابلاغ آشكار وظيفه‌اى نيست'),
(3359, 29, 19, 'آيا نديدند كه خدا چگونه آفرينش را آغاز مى‌كند، سپس آن را باز مى‌گرداند؟ به راستى اين [كار] بر خدا آسان است'),
(3360, 29, 20, 'بگو: در زمين بگرديد و بنگريد چگونه آفرينش را آغاز كرده است. سپس خداوند [همين گونه‌] جهان ديگر را پديد مى‌آورد. بى‌ترديد خدا بر هر چيزى تواناست'),
(3361, 29, 21, 'هر كه را بخواهد عذاب مى‌كند و هر كه را بخواهد رحمت مى‌كند. و به سوى او بازگردانده مى‌شويد'),
(3362, 29, 22, 'و شما نه در زمين و نه در آسمان عاجز كنندگان [او] نيستيد، و جز خدا نه سرپرستى داريد و نه ياورى'),
(3363, 29, 23, 'و كسانى كه آيات خدا و ديدار او را منكر شدند، آنان از رحمت من نوميدند، و آنها را عذابى پردرد خواهد بود'),
(3364, 29, 24, 'و پاسخ قوم [ابراهيم‌] جز اين نبود كه گفتند: او را بكشيد يا بسوزانيد. ولى خدا او را از آتش نجات بخشيد. آرى در اين [ماجرا] براى مردمى كه ايمان مى‌آورند بسى حجّت‌هاست'),
(3365, 29, 25, 'و [ابراهيم‌] گفت: جز اين نيست كه شما به خاطر دوستى [و ارتباط] ميان خودتان در زندگى دنيا به جاى خدا بت‌هايى را گرفته‌ايد. ولى روز قيامت منكر يكديگر مى‌شويد و همديگر را لعنت مى‌كنيد و جايگاهتان آتش است و براى شما هيچ ياورى نخواهد بود'),
(3366, 29, 26, 'پس لوط به او ايمان آورد و [ابراهيم‌] گفت: من به سوى پروردگار خود هجرت مى‌كنم كه او شكست ناپذير حكيم است'),
(3367, 29, 27, 'و اسحاق و يعقوب را به او عطا كرديم و در نسل او نبوّت و كتاب قرار داديم و پاداش او را در اين دنيا داديم و قطعا او در آخرت از صالحان است'),
(3368, 29, 28, 'و لوط را [فرستاديم‌] هنگامى كه به قوم خود گفت: شما به كار زشتى مى‌پردازيد كه هيچ يك از مردم جهان در آن بر شما پيشى نگرفته است'),
(3369, 29, 29, 'آيا شما با مردها در مى‌آميزيد و راه را مى‌بنديد و در محافل خود پليدكارى مى‌كنيد؟ ولى پاسخ قومش جز اين نبود كه گفتند: عذاب خدا را بر ما بياور اگر از راست گويانى'),
(3370, 29, 30, 'گفت: پروردگارا! مرا بر قوم تبهكار نصرت ده'),
(3371, 29, 31, 'و چون فرستادگان ما براى ابراهيم مژده‌ى [فرزند] آوردند، گفتند: البته ما هلاك كنندگان اهل اين شهر خواهيم بود، چرا كه مردمش ستمكار بوده‌اند'),
(3372, 29, 32, 'گفت: لوط در آن جاست. گفتند: ما بهتر مى‌دانيم چه كسانى در آن جا هستند. او و كسانش را نجات خواهيم داد، جز زنش را كه از باقى ماندگان خواهد بود'),
(3373, 29, 33, 'و هنگامى كه فرستادگان ما به سوى لوط آمدند، از حضورشان بدحال شد و در كارشان فرو ماند. گفتند: نترس و غم مدار كه البته ما تو و خاندانت را نجات مى‌دهيم، جز همسرت كه در ميان قوم باقى مى‌ماند'),
(3374, 29, 34, 'ما بر مردم اين شهر به سزاى اين كه فسق مى‌كردند عذابى از آسمان فرو خواهيم فرستاد'),
(3375, 29, 35, 'و از آن شهر [ويران شده‌] براى مردمى كه مى‌انديشند نشانه روشنى باقى گذاشتيم'),
(3376, 29, 36, 'و به سوى [مردم‌] مدين برادرشان شعيب را [فرستاديم‌]. گفت: اى قوم من! خدا را بپرستيد، و به روز قيامت اميد داشته باشيد، و در زمين سر به فساد برنداريد'),
(3377, 29, 37, 'پس او را تكذيب كردند و زلزله آنها را فرو گرفت و بامدادان در خانه‌هايشان در جا مردند'),
(3378, 29, 38, 'و عاد و ثمود را [نيز هلاك كرديم‌]، و همانا [اين هلاكت‌] از خانه‌هاى [ويران شده‌ى‌] آنان به خوبى براى شما پديدار گرديد، و شيطان كارهايشان را در نظرشان بياراست و از راه بازشان داشت با آن كه اهل فكر و بصيرت بودند'),
(3379, 29, 39, 'و قارون و فرعون و هامان را نيز [هلاك كرديم‌]، و به راستى موسى براى آنان حجت‌هاى روشن آورد، ولى آنها در زمين سركشى كردند، اما سبقت گيرندگان [بر اراده‌ى خدا] نبودند'),
(3380, 29, 40, 'و همه را به گناهشان گرفتيم. پس بر بعضى از آنها طوفان ريگ فرستاديم و برخى‌شان را صيحه مرگبار گرفت و برخى را در زمين فرو برديم و بعضى ديگر را غرق كرديم. و اين خدا نبود كه بر ايشان ستم كرد بلكه خودشان بر خود ستم مى‌كردند'),
(3381, 29, 41, 'مثل كسانى كه غير خدا را اوليا گرفتند، چون مثل عنكبوت است كه خانه‌اى [بى‌دوام‌] برگرفت، و حقا كه سست‌ترين خانه‌ها، همان خانه عنكبوت است، اگر مى‌دانستند'),
(3382, 29, 42, 'خداوند آنچه را كه جز او مى‌خوانند، مى‌داند و هم اوست شكست ناپذير حكيم'),
(3383, 29, 43, 'و اين مثل‌ها را براى مردم مى‌زنيم ولى جز دانايان آنها را در نمى‌يابند'),
(3384, 29, 44, 'خداوند، آسمان‌ها و زمين را به حق آفريد. قطعا در اين [آفرينش‌] براى مؤمنان عبرتى است'),
(3385, 29, 45, 'آنچه از كتاب به سوى تو وحى شده بخوان و نماز را برپا دار، كه نماز [آدمى را] از زشتكارى و ناشايست باز مى‌دارد، و قطعا ياد خدا مهم‌تر است. و خدا مى‌داند چه مى‌كنيد'),
(3386, 29, 46, 'و با اهل كتاب جز به شيوه‌اى كه بهتر است مجادله نكنيد، مگر كسانى از آنها كه ستم كردند، و بگوييد: به آنچه به سوى ما نازل شده و آنچه به سوى شما نازل گرديده، ايمان آورده‌ايم و خداى ما و خداى شما يكى است و ما تسليم اوييم'),
(3387, 29, 47, 'و به همين ترتيب، كتاب [قرآن‌] را بر تو نازل كرديم. پس آنها كه به ايشان كتاب داده‌ايم، بدان ايمان مى‌آورند، و از آنان [كه مشركند] نيز كسانى به آن مى‌گروند، و آيات ما را جز كافران انكار نمى‌كنند'),
(3388, 29, 48, 'و تو پيش از [نزول‌] آن هيچ كتابى را نمى‌خواندى و با دست خود آن را نمى‌نوشتى، و گر نه باطل‌انديشان قطعا [در باره‌ى قرآن‌] به شك مى‌افتادند'),
(3389, 29, 49, 'بلكه آن آياتى روشن است كه در سينه دانشوران جاى دارد، و آيات ما را جز ستمكاران انكار نمى‌كنند'),
(3390, 29, 50, 'و گفتند: چرا بر او از جانب پروردگارش [معجزات و] نشانه‌هايى نازل نشده است؟ بگو: جز اين نيست كه معجزات نزد خداست و من فقط هشدار دهنده‌ى آشكارم'),
(3391, 29, 51, 'آيا ايشان را كافى نيست كه اين كتاب را كه بر آنها تلاوت مى‌شود بر تو نازل كرديم؟ بى‌گمان در اين [كار] براى مردمى كه ايمان مى‌آورند رحمت و يادآورى است'),
(3392, 29, 52, 'بگو: كافى است كه خدا ميان من و شما شاهد باشد. آنچه را كه در آسمان‌ها و زمين است مى‌داند، و آنها كه به باطل گرويدند و به خدا كافر شدند، قطعا زيانكارند'),
(3393, 29, 53, 'و از تو خواهان شتاب در عذابند. و اگر [آن را] موعد مقررى نبود، بى‌گمان عذاب بديشان مى‌رسيد، و البته بى‌آن كه خبردار شوند قطعا [عذاب‌] ناگهانى به سراغشان خواهد آمد'),
(3394, 29, 54, 'از تو خواهان شتاب در عذابند، در حالى كه جهنم قطعا بر كافران احاطه دارد'),
(3395, 29, 55, 'آن روز كه عذاب از بالاى سرشان و از زير پاهايشان آنها را فرا مى‌گيرد و [خدا] مى‌فرمايد: بچشيد [نتيجه‌] آنچه را كه مى‌كرديد'),
(3396, 29, 56, 'اى بندگان من كه ايمان آورده‌ايد! همانا زمين من وسيع است پس تنها مرا بپرستيد [و تسليم فشار محيط نباشيد]'),
(3397, 29, 57, 'هر كسى چشنده‌ى مرگ است، آن‌گاه به سوى ما بازگردانده مى‌شويد'),
(3398, 29, 58, 'و كسانى كه ايمان آوردند و كارهاى شايسته كردند، قطعا آنها را در غرفه‌هايى از بهشت جاى مى‌دهيم كه از پاى درختانش نهرها جارى است، جاودانه در آن جا خواهند بود پاداش عمل كنندگان چه نيكوست'),
(3399, 29, 59, 'همان كسانى كه صبورى كردند و بر پروردگارشان توكل مى‌كنند'),
(3400, 29, 60, 'و چه بسيار جاندارانى كه [نمى‌توانند] روزى خود را حمل [يا ذخيره‌] كنند، خداست كه آنها و شما را روزى مى‌دهد، و او شنواى داناست'),
(3401, 29, 61, 'و اگر از ايشان بپرسى: چه كسى آسمان‌ها و زمين را آفريد و خورشيد و ماه را مسخّر نمود؟ حتما خواهند گفت: خدا. پس به كجا بازگردانده مى‌شوند'),
(3402, 29, 62, 'خدا براى هر يك از بندگانش كه بخواهد روزى را فراخ مى‌كند يا تنگ مى‌گرداند همانا خدا به هر چيزى داناست'),
(3403, 29, 63, 'و اگر از آنها بپرسى: چه كسى از آسمان باران فرستاد و زمين را پس از مردگى‌اش بدان زنده كرد؟ حتما خواهند گفت: خدا. بگو: ستايش از آن خداست. با اين همه، بيشترشان نمى‌انديشند'),
(3404, 29, 64, 'و زندگى اين دنيا جز سرگرمى و بازيچه نيست، و حيات حقيقى همانا سراى آخرت است اگر مى‌دانستند'),
(3405, 29, 65, 'و هنگامى كه بر كشتى سوار مى‌شوند، خدا را با اخلاص در دين مى‌خوانند، و آن‌گاه كه نجاتشان داد و به خشكى آورد، به ناگاه همانها شرك مى‌ورزند'),
(3406, 29, 66, 'آنچه را كه به آنها داده‌ايم انكار كنند و از آنچه مى‌خواهند برخوردار شوند، اما به زودى خواهند فهميد'),
(3407, 29, 67, 'آيا نديدند كه ما [مكّه را] حرم امنى قرار داديم و حال آن كه مردم پيرامونشان ربوده مى‌شوند [و ناامنى دارند]؟ آيا به باطل ايمان مى‌آورند و به نعمت خدا كافر مى‌شوند'),
(3408, 29, 68, 'و كيست ستمكارتر از آن كه بر خدا دروغ بندد يا حق را آن‌گاه كه به سوى او آمد تكذيب كند؟ آيا كافران را در جهنم جايگاهى نيست'),
(3409, 29, 69, 'و كسانى كه در راه ما مجاهدت كنند، به يقين آنها را به راه‌هاى خود هدايت مى‌كنيم، و بى‌ترديد خدا با نيكوكاران است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف، لام، ميم'),
(3411, 30, 2, 'روميان مغلوب شدند'),
(3412, 30, 3, 'در نزديك‌ترين [نقطه اين‌] سرزمين، ولى آنها بعد از شكستشان، به زودى پيروز خواهند شد'),
(3413, 30, 4, 'در ظرف چند سال. امر، امر خداست چه پيش [از پيروزى‌] و چه بعد [از پيروزى‌] و در آن روز مؤمنان شاد مى‌گردند'),
(3414, 30, 5, 'به نصرت الهى. هر كه را خواهد يارى مى‌دهد، و اوست شكست ناپذير مهربان. در ظرف چند سال «بضع مدتى ميان سه و نه است». للّه الامر: حكم و فرمان از آن خداست. يومئذ: در آن روز. يفرح: شاد مى‌شود. يفرح المؤمنون بنصر اللّه: خوشحال مى‌شوند مؤمنان به خاطر يارى خدا.'),
(3415, 30, 6, 'وعده‌ى خداست. خدا وعده‌اش را خلاف نمى‌كند، و ليكن بيشتر مردم نمى‌دانند'),
(3416, 30, 7, 'از زندگى دنيا ظاهرى را مى‌شناسند و آنها از آخرت بى‌خبرند'),
(3417, 30, 8, 'آيا پيش خود نينديشيده‌اند كه خدا آسمان‌ها و زمين و آنچه را كه ميان آن دو است، جز به حق و تا سر آمدى معين، نيافريده است؟ و [با اين همه‌] بسيارى از مردم ملاقات پروردگارشان را سخت منكرند'),
(3418, 30, 9, 'آيا در زمين نگرديدند تا ببينند عاقبت كسانى كه پيش از آنها بودند، چگونه شد؟ آنها بسى نيرومندتر از ايشان بودند، و زمين را زير و رو كردند و بيش از آنچه آنها آبادش كردند آن را آباد ساختند، و پيامبرانشان دلايل آشكار برايشان آوردند. بنابر اين خدا بر آن نبو'),
(3419, 30, 10, 'آن‌گاه عاقبت كسانى كه زشتكارى كردند، اين شد كه آيات خدا را تكذيب كردند و آنها را به ريشخند مى‌گرفتند'),
(3420, 30, 11, 'خداوند آفرينش را آغاز مى‌كند و سپس آن را باز مى‌گرداند و سپس شما به سوى او بازگردانده مى‌شويد'),
(3421, 30, 12, 'و روزى كه قيامت برپا شود مجرمان [از همه جا] نااميد مى‌شوند'),
(3422, 30, 13, 'و براى آنها از شريكانشان شفيعانى نخواهد بود و منكر شريكان خود مى‌شوند'),
(3423, 30, 14, 'و روزى كه رستاخيز برپا شود، آن روز [بهشتيان و دوزخيان‌] از هم جدا مى‌شوند'),
(3424, 30, 15, 'امّا كسانى كه ايمان آورده و كارهاى شايسته كردند، در گلستانى شادمان مى‌گردند'),
(3425, 30, 16, 'و امّا كسانى كه كافر شده و آيات ما و ديدار آخرت را تكذيب كردند، همه در عذاب [الهى‌] احضار شدگانند'),
(3426, 30, 17, 'پس حمد و ثنا براى خداست آن‌گاه كه به شامگاه مى‌رسيد و آن‌گاه كه صبح مى‌كنيد'),
(3427, 30, 18, 'و ستايش از آن اوست در آسمان‌ها و زمين و شامگاهان و وقتى كه ظهر مى‌كنيد'),
(3428, 30, 19, 'زنده را از مرده بيرون مى‌آورد و مرده را از زنده بيرون مى‌آورد، و زمين را بعد از مرگش زنده مى‌كند، و [شما نيز] اين گونه [از گورها] بيرون آورده مى‌شويد'),
(3429, 30, 20, 'و از نشانه‌هاى او اين است كه شما را از خاك آفريد، سپس به ناگاه شما [به صورت‌] بشرى پراكنده مى‌شويد'),
(3430, 30, 21, 'و از نشانه‌هاى او اين كه از خودتان همسرانى براى شما آفريد تا در كنارشان آرام گيريد، و ميانتان دوستى و مهربانى نهاد. آرى در اين [نعمت‌] براى مردمى كه مى‌انديشند نشانه‌هايى است'),
(3431, 30, 22, 'و از نشانه‌هاى [قدرت‌] او آفرينش آسمان‌ها و زمين و اختلاف زبان‌ها و رنگ‌هاى شماست. قطعا در اين [امور] براى دانشوران نشانه‌هاست'),
(3432, 30, 23, 'و از نشانه‌هاى [حكمت‌] او خواب شما در شب و روز و طلب [معاش‌] شما از فضل اوست. به راستى در اين، براى مردمى كه گوش شنوا دارند دلايلى است'),
(3433, 30, 24, 'و از نشانه‌هاى او [اين كه‌] برق را براى بيم و اميد نشانتان مى‌دهد، و از آسمان باران مى‌فرستد و زمين را پس از مرگش بدان زنده مى‌كند. همانا در اين براى مردمى كه تعقّل مى‌كنند نشانه‌هاست'),
(3434, 30, 25, 'و از نشانه‌هاى او اين است كه آسمان و زمين به امر او برپاست، سپس چون شما را با يك بار خواندن از زمين بخواند، در دم شما [از گورها] خارج مى‌شويد'),
(3435, 30, 26, 'و هر كه در آسمان‌ها و زمين است از آن اوست، همه فرمانبردار اويند'),
(3436, 30, 27, 'و اوست آن كس كه آفرينش را آغاز مى‌كند سپس آن را باز مى‌گرداند و اين [كار] براى او آسان‌تر است. و براى اوست صفت برتر در آسمان‌ها و زمين، و اوست شكست ناپذير حكيم'),
(3437, 30, 28, '[خداوند] براى شما از خودتان مثلى زده است: آيا از بردگانتان در اموالى كه روزيتان كرده‌ايم شريكانى هست تا در آن [اموال‌] با آنها برابر باشيد و همان طور كه [در تصرف مال‌] از يكديگر بيم داريد، از [تصرف و تعدى‌] آنها بترسيد [پس چگونه مملوك او را شريك وى م'),
(3438, 30, 29, 'بلكه ستم پيشگان بى‌هيچ دانشى از هوس‌هاى خود پيروى كردند. پس آن را كه خدا گمراه كرده، چه كسى هدايت مى‌كند؟ و آنها را هيچ ياورى نيست'),
(3439, 30, 30, 'پس حق گرايانه، به سوى اين آيين روى آور، [با همان‌] فطرتى كه خدا مردم را بر آن سرشته است. تغييرى در آفرينش خدا نيست. آيين پايدار همين است و ليكن بيشتر مردم نمى‌دانند'),
(3440, 30, 31, 'در حالى كه به سوى او [از مخالفت‌ها] بازگشت مى‌كنيد، [به او رو كنيد]، و از او پروا داريد و نماز را برپا كنيد و از مشركان نباشيد'),
(3441, 30, 32, 'از آنها كه دين خود را قطعه قطعه كردند و فرقه‌هايى شدند [و تعجب اينجاست كه‌] هر فرقه‌اى بدانچه نزد خود دارند، دلخوشند'),
(3442, 30, 33, 'و چون مردم را زيانى رسد، پروردگار خود را با حال انابه مى‌خوانند، و آن‌گاه كه از جانب خود رحمتى به آنها چشاند، به ناگاه دسته‌اى از ايشان به پروردگار خود شرك مى‌ورزند'),
(3443, 30, 34, 'تا به آنچه بدانها عطا كرده‌ايم كفران ورزند. [بگو:] برخوردار شويد، امّا به زودى خواهيد فهميد'),
(3444, 30, 35, 'آيا حجّتى بر آنها نازل كرده‌ايم كه در باره‌ى آنچه شرك مى‌آورند سخن مى‌گويد'),
(3445, 30, 36, 'و چون به مردم رحمتى بچشانيم، بدان شاد مى‌گردند، و چون به [سزاى‌] آنچه دست‌هايشان پيش فرستاده زيانى به ايشان برسد، به ناگاه آنان [از رحمت حق‌] نوميد مى‌شوند'),
(3446, 30, 37, 'آيا ندانسته‌اند كه [اين‌] خداست كه روزى را براى هر كه بخواهد فراخ مى‌كند يا تنگ مى‌گرداند؟ قطعا در اين [امر] براى مردمى كه ايمان مى‌آورند عبرت‌هاست'),
(3447, 30, 38, 'پس حق خويشاوند و تنگدست و در راه مانده را ادا كن. اين [انفاق‌] براى كسانى كه خواهان خشنودى خدايند بهتر است، و چنين كسانى رستگارند'),
(3448, 30, 39, 'و آنچه [به عنوان‌] ربا داده‌ايد تا در اموال مردم فزونى يابد، [بدانيد كه‌] نزد خدا زياد نمى‌شود [و بركتى ندارد]، ولى آنچه از زكات براى رضاى خدا مى‌دهيد، آنانند كه [مالشان را] چند برابر مى‌كنند'),
(3449, 30, 40, 'خدا كسى است كه شما را آفريد، سپس به شما روزى داد، سپس شما را مى‌ميراند و پس از آن زنده مى‌كند. آيا در ميان شريكان شما كسى هست كه كارى از اين [قبيل‌] كند؟ منزّه است او، و فراتر است از آنچه [با وى‌] شريك مى‌كنند'),
(3450, 30, 41, 'فساد و تباهى در خشكى و دريا به سبب آنچه دست‌هاى مردم [از گناهان‌] فراهم آورده پديدار گشت، تا [سزاى‌] بعضى از آنچه را كرده‌اند به آنان بچشاند، باشد كه [به حق‌] باز گردند'),
(3451, 30, 42, 'بگو: در زمين بگرديد و بنگريد عاقبت گذشتگان كه بيشترشان مشرك بودند چگونه شد'),
(3452, 30, 43, 'پس روى خود را به سوى اين آيين استوار، فرادار پيش از آن كه روزى فرا رسد كه از جانب خدا برگشتى ندارد، و در آن روز [مردم‌] به گروه‌هايى [بهشتى و دوزخى‌] تقسيم مى‌شوند'),
(3453, 30, 44, 'هر كه كفر ورزد، كفرش به زيان اوست، و كسانى كه كار شايسته كنند، پس [فرجام نيك را] به سود خودشان آماده مى‌كنند'),
(3454, 30, 45, 'تا كسانى را كه ايمان آوردند و كارهاى شايسته كردند از فضل خويش پاداش دهد همانا او كافران را دوست نمى‌دارد'),
(3455, 30, 46, 'و از نشانه‌هاى او اين است كه بادها را بشارت دهنده مى‌فرستد، و تا بخشى از [باران‌] رحمتش را به شما بچشاند و تا كشتى‌ها به فرمان او روان شوند، و شما از فضل او [روزى‌] بجوييد، باشد كه شكرگزار باشيد'),
(3456, 30, 47, 'و همانا پيش از تو فرستادگانى به سوى قومشان گسيل داشتيم، پس دلايل آشكار برايشان آوردند، آن‌گاه از كسانى كه مرتكب جرم شدند انتقام گرفتيم و نصرت مؤمنان حقى است بر عهده‌ى ما'),
(3457, 30, 48, 'خداست كه بادها را مى‌فرستد، تا ابرى را حركت دهند، سپس آن را در آسمان آن‌گونه كه بخواهد مى‌گسترد و آن را قطعه‌هايى روى هم قرار مى‌دهد، سپس باران را مى‌بينى كه از لابه‌لاى آن بيرون مى‌آيد، و چون آن را به هر كس از بندگانش كه خواهد برساند، به ناگاه آنان'),
(3458, 30, 49, 'و البته پيش از آن كه باران بر ايشان نازل گردد، [آرى‌] پيش از آن نااميد بودند'),
(3459, 30, 50, 'پس به آثار رحمت خدا بنگر كه چگونه زمين را پس از مرگش زنده مى‌كند. به يقين چنين خدايى زنده كننده‌ى مردگان است و او بر هر چيزى تواناست'),
(3460, 30, 51, 'و اگر بادى [آفت زا] بفرستيم و كشت خود را زرد شده ببينند، قطعا از آن پس [به جاى تنبّه‌] كافر مى‌شوند'),
(3461, 30, 52, 'پس مسلما تو نمى‌توانى مردگان را شنوا كنى و نمى‌توانى دعوت [خود] را به گوش كرها برسانى هنگامى كه پشت كنند و برگردند'),
(3462, 30, 53, 'و تو هدايت كننده‌ى كوردلان از گمراهى‌شان نتوانى بود. تو تنها كسانى را مى‌شنوانى كه به آيات ما ايمان مى‌آورند و خود [در برابر حق‌] تسليم‌اند'),
(3463, 30, 54, 'خداست كه شما را از ناتوانى آفريد [كه ضعيف بوديد]، آن‌گاه پس از ناتوانى قوّت بخشيد، سپس بعد از قوّت، ناتوانى و پيرى داد. هر چه بخواهد مى‌آفريند و هموست داناى توانا'),
(3464, 30, 55, 'و روزى كه رستاخيز بر پا شود، مجرمان سوگند خورند كه جز ساعتى توقف نكرده‌اند. [در دنيا هم‌] اين گونه به انحراف كشيده مى‌شدند'),
(3465, 30, 56, 'و كسانى كه دانش و ايمان يافته‌اند، گويند: قطعا شما در مكتوب خدا تا روز رستاخيز به سر برده‌ايد، و اين همان روز رستاخيز است ولى شما نمى‌دانستيد'),
(3466, 30, 57, 'پس در چنين روزى پوزش آنها كه ستم كردند سودى نبخشد و عذرخواهى‌شان را نپذيرند'),
(3467, 30, 58, 'و به راستى در اين قرآن براى مردم از هر گونه مثلى آورديم. و اگر براى ايشان آيتى بياورى، آنان كه كافر شدند حتما خواهند گفت: شما بيهوده‌گويانى بيش نيستيد'),
(3468, 30, 59, 'اين گونه، خدا بر دل‌هاى كسانى كه جاهلند مهر مى‌نهد'),
(3469, 30, 60, 'پس صبر پيشه كن كه وعده‌ى خدا حق است، و زنهار كسانى كه [به وعده‌ى خدا] يقين ندارند، تو را به سبكسرى [و خود باختگى‌] وا ندارند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف، لام، ميم'),
(3471, 31, 2, 'اينها آيات كتاب حكمت آموز است'),
(3472, 31, 3, '[كه‌] هدايت و رحمتى براى نيكوكاران است'),
(3473, 31, 4, 'آنان كه نماز برپا مى‌دارند و زكات مى‌دهند و هم ايشانند كه به آخرت يقين دارند'),
(3474, 31, 5, 'آنان بر هدايتى از جانب پروردگارشان استوارند و هم ايشان رستگارانند'),
(3475, 31, 6, 'و برخى از مردم كسانى‌اند كه سخن بيهوده را مى‌خرند تا [مردم را] بى‌هيچ دانشى از راه خدا گمراه كنند و آيات الهى را به ريشخند گيرند براى آنان عذابى خوار كننده خواهد بود'),
(3476, 31, 7, 'و چون آيات ما بر او تلاوت شود، با نخوت روى بر مى‌گرداند، چنان كه گويى آن را نشنيده است، گويى در دو گوش او سنگينى است. پس او را به عذابى دردناك بشارت ده'),
(3477, 31, 8, 'بى‌ترديد كسانى كه ايمان آوردند و كارهاى شايسته كردند، باغ‌هاى پرنعمت [بهشت‌]، براى آنهاست'),
(3478, 31, 9, 'كه در آن جاودان مى‌مانند وعده‌ى خدا حق است و اوست شكست ناپذير حكيم'),
(3479, 31, 10, 'آسمان‌ها را بى‌هيچ ستونى كه آن را ببينيد خلق كرد، و در زمين كوه‌هاى استوار بيفكند تا شما را نجنباند. و در آن از هر گونه جنبنده‌اى منتشر ساخت. و از آسمان آبى فرستاديم و از هر نوع گياه پر بهره‌ى نيكو در آن رويانيديم'),
(3480, 31, 11, 'اين آفرينش خداست. [اينك‌] به من نشان دهيد آنها كه غير اويند چه آفريده‌اند؟ [هيچ!] بلكه ستمگران در گمراهى آشكارند'),
(3481, 31, 12, 'و البته لقمان را حكمت داديم كه: خدا را سپاس دار. و هر كه سپاس دارد، تنها به سود خود سپاس مى‌دارد، و هر كس كفران كند، البته خدا بى‌نياز ستوده است'),
(3482, 31, 13, 'و هنگامى كه لقمان به پسر خود در حالى كه او را پند مى‌داد، گفت: اى پسرك من! به خدا شرك مورز كه به راستى شرك، ظلم بزرگى است'),
(3483, 31, 14, 'و ما به انسان در حق پدر و مادرش سفارش كرديم مادرش او را با ضعف روزافزون حمل كرد، و دوران شيرخوارگى او در دو سال است. [سفارش كرديم‌] كه شكرگزار من و پدر و مادرت باش كه سر انجام [همه‌] به سوى من است'),
(3484, 31, 15, 'و اگر آن دو سعى كنند كه تو چيزى را كه بدان علم ندارى شريك من انگارى، فرمان ايشان مبر، و در دنيا با آنها به نيكى معاشرت كن و راه كسى را پيروى كن كه به من روى مى‌آورد و [سر انجام‌]، بازگشت شما به سوى من است، پس شما را بدانچه مى‌كرديد آگاه مى‌سازم'),
(3485, 31, 16, 'اى پسرك من! اگر [عمل تو] هموزن دانه خردلى و در درون صخره‌اى يا در آسمان‌ها يا در زمين باشد، خدا آن را مى‌آورد همانا خدا بس دقيق و آگاه است'),
(3486, 31, 17, 'اى پسرك من! نماز را برپا دار و به معروف وادار و از منكر بازدار، و بر آسيبى كه [در اين راه‌] به تو رسد صبر كن. بى‌ترديد اين [صبورى‌] از كارهاى استوار است [كه اراده‌ى قوى مى‌خواهد]'),
(3487, 31, 18, 'و از مردمان [به نخوت‌] رخ بر متاب، و در زمين به تكبر راه مرو، كه خدا هيچ خود پسند مغرورى را دوست نمى‌دارد'),
(3488, 31, 19, 'و در راه رفتن خود ميانه‌رو باش، و از صداى خود بكاه، كه بدترين بانگ‌ها بانگ خران است'),
(3489, 31, 20, 'آيا نديديد كه خدا آنچه را كه در آسمان‌ها و در زمين است مسخّر شما ساخته، و نعمت‌هاى آشكار و پنهان خود را بر شما كامل كرده است؟ [باز] برخى از مردم، بدون هيچ دانش و هدايت و كتاب روشنگرى در باره‌ى خدا مجادله مى‌كنند'),
(3490, 31, 21, 'و چون به آنها گفته شود: آنچه را كه خدا نازل كرده پيروى كنيد، گويند: [نه،] بلكه از آنچه پدرانمان را بر آن يافته‌ايم پيروى مى‌كنيم. آيا هر چند شيطان آنها را به سوى عذاب شعله‌ور فرا خواند [باز هم از آنها پيروى مى‌كنند]'),
(3491, 31, 22, 'و هر كس خود را در حالى كه نيكوكار باشد تسليم خدا كند، قطعا به محكم‌ترين دستاويز چنگ زده، و عاقبت كارها به سوى خداست'),
(3492, 31, 23, 'و هر كس كفر ورزد، كفر او تو را غمگين نكند. بازگشتشان به سوى ماست، پس آنها را به [حقيقت‌] آنچه كرده‌اند آگاه خواهيم كرد. بى‌ترديد خدا به راز دل‌ها آگاه است'),
(3493, 31, 24, 'اندكى برخوردارشان مى‌سازيم، سپس آنها را به عذابى شديد مى‌كشانيم'),
(3494, 31, 25, 'و اگر از آنها بپرسى: چه كسى آسمان‌ها و زمين را آفريده است؟ مسلما خواهند گفت: خدا. بگو: ستايش از آن خداست، ولى بيشترشان نمى‌دانند'),
(3495, 31, 26, 'آنچه در آسمان‌ها و زمين است از آن خداست. همانا خداست كه بى‌نياز ستوده است'),
(3496, 31, 27, 'و اگر هر چه درخت در زمين است قلم بود و دريا [مركب و] هفت درياى ديگر به مدد آن مى‌آمد، [تا كلمات خدا را بنويسند] كلمات خدا پايان نمى‌پذيرفت، قطعا خداست كه شكست ناپذير حكيم است'),
(3497, 31, 28, 'آفرينش و برانگيختن شما [در نزد ما] جز مانند [آفرينش‌] يك تن نيست. همانا خدا شنواى بيناست'),
(3498, 31, 29, 'آيا نديدى كه خدا شب را در روز داخل مى‌كند [و شب بلند مى‌شود] و روز را در شب داخل مى‌كند [و روز بلند مى‌شود] و آفتاب و ماه را مسخّر ساخته و هر كدام تا زمان معينى [در مدارى‌] روانند و خدا به آنچه مى‌كنيد آگاه است'),
(3499, 31, 30, 'اينها [همه‌] دليل آن است كه خداوند، همو حق است و غير از او هر چه مى‌خوانند باطل است و اين كه خدا بلند مرتبه بزرگ است'),
(3500, 31, 31, 'آيا نديدى كه كشتى‌ها به نعمت خدا در دريا حركت مى‌كنند تا برخى از نشانه‌هاى [قدرت‌] خود را به شما نشان دهد؟ قطعا در اين [قدرت‌نمايى‌] براى هر شكيباى سپاسگزارى، نشانه‌هاست'),
(3501, 31, 32, 'و هنگامى كه آنها (سرنشينان كشتى) را موجى مانند سايبان‌ها فرا گيرد، خدا را از روى اخلاص در دين [و بندگى‌] بخوانند، و چون به سوى خشكى نجاتشان داد، [تنها] برخى از آنها راه مستقيم را مى‌روند، و البتّه آيات ما را جز دغل‌هاى كفر پيشه انكار نمى‌كنند'),
(3502, 31, 33, 'اى مردم! از پروردگارتان پروا كنيد و بترسيد از روزى كه نه پدرى از فرزندش دفع بلا كند و نه فرزندى از پدرش. به يقين وعده‌ى خدا حق است پس زندگى دنيا شما را نفريبد و آن فريبنده شما را به خدا مغرور نكند'),
(3503, 31, 34, 'بى‌ترديد، خداست كه علم قيامت نزد اوست، و باران مى‌فرستد، و آنچه را كه در رحم‌هاست مى‌داند، و كسى نمى‌داند فردا چه به دست مى‌آورد، و كسى نمى‌داند به كدام سرزمين مى‌ميرد. در حقيقت، خدا داناى آگاه است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف، لام، ميم'),
(3505, 32, 2, 'فرو فرستادن [اين‌] كتاب، كه هيچ [جاى‌] شكى در آن نيست از طرف پروردگار جهانيان است'),
(3506, 32, 3, 'آيا مى‌گويند: [محمد] آن را به دروغ ساخته است؟ [نه‌]، بلكه آن حق است و از جانب پروردگار توست، تا مردمى را كه پيش از تو بيم دهنده‌اى برايشان نيامده هشدار دهى، اميد كه هدايت يابند'),
(3507, 32, 4, 'خدا كسى است كه آسمان‌ها و زمين و آنچه را كه ميان آن دو است در شش روز آفريد، آن‌گاه بر عرش [قدرت‌] قرار گرفت، براى شما غير از او سرپرست و شفيعى نيست، پس آيا پند نمى‌گيريد'),
(3508, 32, 5, 'امور [جهان‌] را از آسمان به سوى زمين تدبير مى‌كند، سپس [نتيجه آن‌] در روزى كه مقدار آن هزار سال از سال‌هايى است كه شما مى‌شمريد، به سوى او بالا مى‌رود [و دنيا پايان مى‌يابد]'),
(3509, 32, 6, 'او داناى نهان و آشكار است، شكست ناپذير مهربان است'),
(3510, 32, 7, 'همان كسى كه همه چيز را نيكو آفريد، و آفرينش انسان را از گل آغاز كرد'),
(3511, 32, 8, 'سپس [تداوم‌] نسل او را از چكيده‌ى آبى حقير مقرّر كرد'),
(3512, 32, 9, 'آن‌گاه [ساختار] او را تنظيم كرد، و از روح خويش در او دميد، و براى شما شنوايى و چشم‌ها و دل‌ها قرار داد، [ولى‌] اندكى سپاس مى‌گزاريد'),
(3513, 32, 10, 'و گفتند: آيا وقتى [مرديم و] در زمين ناپديد شديم، در آفرينش تازه‌اى خواهيم شد؟ بلكه آنها به لقاى پروردگارشان كافرند'),
(3514, 32, 11, 'بگو: فرشته مرگ كه بر شما گمارده شده، جانتان را مى‌ستاند، آن‌گاه به سوى پروردگارتان بازگردانده مى‌شويد'),
(3515, 32, 12, 'و اگر مى‌ديدى هنگامى كه مجرمان در پيشگاه پروردگارشان سرها را به زير افكنده [مى‌گويند]: پروردگارا! [آنچه وعده كرده بودى‌] ديديم و شنيديم، پس ما را بازگردان تا كار شايسته كنيم، كه اينك ما به يقين رسيده‌ايم'),
(3516, 32, 13, 'و اگر مى‌خواستيم، به هر كسى [از روى جبر] هدايتش را مى‌داديم، ليكن اين سخن از جانب من حتمى شده است كه به يقين جهنم را از همه جنّيان و آدميان [كه به اختيار خود خلاف مى‌كنند] آكنده سازم'),
(3517, 32, 14, 'پس به [سزاى‌] آن كه ديدار اين روزتان را از ياد برديد [عذاب را] بچشيد. ما نيز شما را از ياد برده‌ايم، و بچشيد عذاب هميشگى را به خاطر اعمالى كه انجام مى‌داديد'),
(3518, 32, 15, 'تنها كسانى به آيات ما مى‌گروند كه چون آن را به ايشان يادآورى كنند، به حال سجده مى‌افتند و به ستايش پروردگارشان تسبيح مى‌گويند و آنها بزرگى نمى‌فروشند'),
(3519, 32, 16, 'از بسترهاى خواب [براى عبادت شب‌] پهلو تهى مى‌كنند و پروردگارشان را با خوف و رجا مى‌خوانند و از آنچه روزيشان كرده‌ايم انفاق مى‌كنند'),
(3520, 32, 17, 'پس هيچ كس نمى‌داند چه چيزى از روشنى چشم‌ها به پاداش آنچه انجام مى‌دادند برايشان نهفته شده است'),
(3521, 32, 18, 'پس آيا كسى كه مؤمن است مانند كسى است كه نافرمان است؟ برابر نيستند'),
(3522, 32, 19, 'اما كسانى كه ايمان آوردند و كارهاى شايسته كردند، ايشان را براى پذيرايى، به خاطر آنچه انجام مى‌دادند منزلگاه‌هاى بهشت است'),
(3523, 32, 20, 'و اما كسانى كه نافرمانى كردند، جايگاهشان آتش است. هر بار كه بخواهند از آن بيرون بيايند به درون آن بازگردانده مى‌شوند و به آنها گفته مى‌شود: بچشيد عذاب آتشى را كه دروغش مى‌پنداشتيد'),
(3524, 32, 21, 'و قطعا پيش از عذاب بزرگ‌تر، از عذاب نزديك‌تر [اين دنيا] به آنها مى‌چشانيم، شايد كه آنها [به خدا] بازگردند'),
(3525, 32, 22, 'و كيست ظالم‌تر از آن كس كه به آيات پروردگارش توجه داده شود و آن‌گاه از آن روى بگرداند؟ قطعا ما از مجرمان انتقام خواهيم گرفت'),
(3526, 32, 23, 'و به راستى [ما] به موسى كتاب داديم پس در ديدار با او ترديد مكن و آن [كتاب‌] را راهنماى بنى اسرائيل قرار داديم'),
(3527, 32, 24, 'و نظر به اين كه شكيبايى كردند و به آيات ما يقين داشتند از آنها پيشوايانى قرار داديم كه به فرمان ما [مردم را] هدايت مى‌كردند'),
(3528, 32, 25, 'البته پروردگار تو خودش روز قيامت در آنچه اختلاف مى‌كردند، ميانشان داورى خواهد نمود'),
(3529, 32, 26, 'آيا آنها را راه ننمود كه چه بسيار نسل‌ها را پيش از آنها نابود كرديم كه [اينان‌] در خانه‌هاى [ويران شده‌ى‌] آنها راه مى‌روند؟ قطعا در اين [امر] عبرت‌هاست. آيا نمى‌شنوند'),
(3530, 32, 27, 'آيا ننگريستند كه ما باران را به سوى زمين باير مى‌رانيم و بدان كشتزارى را بر مى‌آوريم كه دام‌هايشان و خودشان از آن مى‌خورند؟ آيا نمى‌بينند'),
(3531, 32, 28, 'و مى‌گويند: اين پيروزى [شما] كى خواهد بود اگر راست مى‌گوييد'),
(3532, 32, 29, 'بگو: روز فتح، ايمان كسانى كه كافر شدند سودى نمى‌دهد و مهلت داده نمى‌شوند'),
(3533, 32, 30, 'پس، از ايشان روى برتاب و منتظر باش كه آنها نيز در انتظارند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اى پيامبر! از خدا بترس و كافران و منافقان را اطاعت مكن. بى‌ترديد خداوند داناى حكيم است'),
(3535, 33, 2, 'و آنچه را كه از جانب پروردگارت به تو وحى مى‌شود، پيروى كن. به يقين خداوند به آنچه مى‌كنيد آگاه است'),
(3536, 33, 3, 'و بر خدا توكّل كن، و همين بس كه خدا نگهبان [تو] است'),
(3537, 33, 4, 'خداوند براى هيچ مردى در درونش دو دل ننهاده است، و آن زنانتان را كه ظهار مى‌كنيد [و آنها را مادران خود مى‌خوانيد در واقع‌] مادران شما نگردانيده، و پسر خواندگانتان را پسران [واقعى‌] شما قرار نداده است. اين، گفتار شما با دهان [و زبانتان‌] است، ولى خدا حق'),
(3538, 33, 5, 'آنها (پسر خواندگان) را به [نام‌] پدرانشان بخوانيد، كه اين نزد خدا منصفانه‌تر است، و اگر پدرانشان را نمى‌شناسيد، پس برادران دينى و دوستان شمايند، [آنها را به نام برادر يا دوست خطاب كنيد.] و بر شما در آنچه اشتباه كرده‌ايد گناهى نيست، ولى در آنچه دل‌هايت'),
(3539, 33, 6, 'پيامبر نسبت به مؤمنان از خودشان مقدم‌تر است و همسرانش [در حكم‌] مادران ايشانند. و خويشاوندان طبق كتاب خدا، از مؤمنان و مهاجران نسبت به يكديگر [در ارث‌] اولويت و تقدّم دارند، مگر آن كه بخواهيد به دوستان خود نيكى كنيد [و سهمى از مال خود را برايشان وصيت'),
(3540, 33, 7, 'و [ياد كن‌] هنگامى را كه از پيامبران پيمان گرفتيم و از تو و از نوح و ابراهيم و موسى و عيسى بن مريم، و از [همه‌] آنان پيمانى استوار گرفتيم [كه در تكليف رسالت كوتاهى نكنند]'),
(3541, 33, 8, 'تا [خدا] راستگويان را از صدقشان سؤال كند، و براى كافران عذابى دردناك آماده كرده است'),
(3542, 33, 9, 'اى كسانى كه ايمان آورده‌ايد! نعمت خدا بر خودتان را ياد كنيد، آن‌گاه كه شما را لشكرهايى [در بدر] آمدند، پس بر سر آنان تندبادى و لشكرهايى كه آنها را نمى‌ديديد فرستاديم، و خدا به آنچه مى‌كنيد ناظر است'),
(3543, 33, 10, '[ياد كنيد] آن‌گاه كه از بالا و پايين [شهر] تان بر شما تاختند، و آن‌گاه كه چشم‌ها خيره شد و جان‌ها به گلوگاه رسيد و به خدا گمان‌هاى گوناگون مى‌برديد'),
(3544, 33, 11, 'آن جا بود كه مؤمنان امتحان شدند و سخت تكان خوردند'),
(3545, 33, 12, 'و هنگامى كه منافقان و كسانى كه در دل‌هايشان بيمارى است مى‌گفتند: خدا و فرستاده‌اش جز فريب به ما وعده ندادند'),
(3546, 33, 13, 'و آن‌گاه كه گروهى از آنان گفتند: اى مردم مدينه! ديگر براى شما جاى ماندن نيست، پس [از جبهه‌] برگرديد. و گروهى از آنها از پيامبر اجازه مى‌خواستند و مى‌گفتند: خانه‌هاى ما بى‌حفاظ است، در حالى كه بى‌حفاظ نبود و جز فرار قصدى نداشتند'),
(3547, 33, 14, 'و اگر از اطراف [مدينه به وسيله كفار] مورد هجوم واقع مى‌شدند، سپس از آنها فتنه‌گرى [و بازگشت به شرك‌] خواسته مى‌شد، حتما آن را مى‌پذيرفتند، و جز اندك زمانى در آن درنگ نمى‌كردند'),
(3548, 33, 15, 'در حالى كه آنها پيش از اين با خدا پيمان بسته بودند كه پشت [به دشمن‌] نكنند، و پيمان خدا همواره بازخواست شدنى است'),
(3549, 33, 16, 'بگو: اگر از مرگ يا كشته شدن بگريزيد، اين گريز اصلا سودى به حال شما نخواهد داشت و در آن صورت جز اندكى [از زندگى‌] برخوردار نخواهيد شد'),
(3550, 33, 17, 'بگو: چه كسى مى‌تواند در برابر خدا از شما حمايت كند اگر او براى شما بدى بخواهد يا رحمت بخواهد؟ و آنان غير از خدا براى خود يار و ياورى نخواهند، يافت'),
(3551, 33, 18, 'همانا خدا كسانى از شما را كه از جنگ باز مى‌داشتند و كسانى را كه به برادرانشان مى‌گفتند: به سوى ما بياييد [و به جنگ نرويد]، به خوبى مى‌شناسد و جز اندكى به جنگ تن در نمى‌دهند'),
(3552, 33, 19, '[آنها در همه چيز] بر شما بخيلند، و هنگامى كه بيم [جنگ‌] پيش آيد، آنها را مى‌بينى كه به تو مى‌نگرند و چشمانشان [از ترس در حدقه‌] مى‌گردد، مثل كسى كه از مرگ بيهوش شده باشد، و چون ترس برطرف شد، شما را به خاطر حرص مال با زبان‌هاى تند [و نيش‌دار در باره‌'),
(3553, 33, 20, 'مى‌پندارند كه احزاب [هنوز از اطراف مدينه‌] نرفته‌اند و اگر احزاب بيايند، [از ترس آنها] دوست دارند كاش در ميان باديه‌نشين‌ها بودند [و پنهان مى‌شدند] و از اخبار [جنگ‌] شما مى‌پرسيدند، و اگر در ميان شما هم بودند جز اندكى پيكار نمى‌كردند'),
(3554, 33, 21, 'قطعا براى شما در [رفتار] رسول خدا سرمشقى نيكوست، براى كسى كه به خدا و روز واپسين اميد دارد و خدا را فراوان ياد مى‌كند'),
(3555, 33, 22, '[امّا] وقتى مؤمنان احزاب را ديدند، گفتند: اين همان چيزى است كه خدا و فرستاده‌اش به ما وعده دادند و خدا و رسولش راست گفتند، و جز بر ايمان و تسليم‌شان نيفزود'),
(3556, 33, 23, 'از مؤمنان مردانى هستند كه به آنچه با خدا عهد بستند صادقانه وفا كردند، پس برخى از آنها نذر خود را ادا كرده [به شهادت رسيدند] و برخى از آنها در [همين‌] انتظارند و هرگز پيمان خود را تغيير ندادند'),
(3557, 33, 24, 'تا خدا راستگويان را به خاطر راستى‌شان پاداش دهد، و منافقان را اگر بخواهد عذاب كند يا توبه‌شان را بپذيرد كه خدا آمرزنده‌ى مهربان است'),
(3558, 33, 25, 'و خدا كافران خشمگين را دفع كرد، بى‌آن كه به غنيمتى رسيده باشند و خداوند مؤمنان را از جنگ كفايت كرد [و پيروزيشان داد] و خدا قوى شكست ناپذير است'),
(3559, 33, 26, 'و كسانى از اهل كتاب را كه با [احزاب‌] همپشتى كرده بودند، از دژهايشان به زير آورد و در دل‌هايشان هراس افكند كه گروهى را مى‌كشتيد و گروهى را اسير مى‌كرديد'),
(3560, 33, 27, 'و زمينشان و خانه‌ها و اموالشان و سرزمينى را كه در آن پا نگذاشته بوديد، به ارث شما در آورد، و خدا بر هر چيزى تواناست'),
(3561, 33, 28, 'اى پيامبر! به همسرانت بگو: اگر خواهان زندگى دنيا و زينت آن هستيد، بياييد تا برخوردارتان كنم و به خوشى و نيكى رهايتان سازم'),
(3562, 33, 29, 'و اگر خواستار خدا و فرستاده‌ى وى و سراى آخرتيد، به راستى خدا براى نيكوكاران شما پاداش بزرگى مهيا كرده است'),
(3563, 33, 30, 'اى همسران پيامبر! هر كس از شما مبادرت به كار زشت آشكارى كند، عذابش دو چندان خواهد بود و اين بر خداوند آسان است'),
(3564, 33, 31, 'و هر كس از شما براى خدا و رسولش، فروتنى و تسليم پيشه كند و كار شايسته انجام دهد، پاداش او را دو چندان مى‌دهيم و برايش روزى كريمانه‌اى فراهم ساخته‌ايم'),
(3565, 33, 32, 'اى همسران پيامبر! شما مانند هيچ يك از ديگر زنان نيستيد، اگر تقوا پيشه مى‌كنيد، پس به ناز سخن مگوييد، تا آن كس كه در دلش بيمارى است طمع ورزد و سخن به متعارف گوييد'),
(3566, 33, 33, 'و در خانه‌هايتان بنشينيد، و مانند روزگار جاهليت قديم، با خودنمايى ظاهر نشويد، و نماز به پا داريد و زكات بدهيد، و خدا و رسولش را اطاعت كنيد. جز اين نيست كه خدا مى‌خواهد آلودگى را از شما اهل بيت بزدايد و شما را كاملا پاكيزه گرداند'),
(3567, 33, 34, 'و آنچه را كه از آيات خدا و حكمت در خانه‌هاى شما خوانده مى‌شود ياد كنيد بى‌ترديد خداوند دقيق و آگاه است'),
(3568, 33, 35, 'بى‌گمان، مردان و زنان مسلمان، و مردان و زنان با ايمان، و مردان و زنان اطاعت پيشه، و مردان و زنان راستگو، و مردان و زنان شكيبا، و مردان و زنان فروتن و مردان و زنان صدقه دهنده، و مردان و زنان روزه‌دار، و مردان و زنان پاكدامن، و مردان و زنانى كه خدا را'),
(3569, 33, 36, 'و هيچ مرد و زن مؤمنى را نرسد كه چون خدا و رسولش به كارى حكم دهند، براى آنها در كارشان اختيارى باشد. و هر كس خدا و رسولش را نافرمانى كند، قطعا دچار گمراهى آشكارى گرديده است'),
(3570, 33, 37, 'و آن‌گاه كه به آن كس كه خدا نعمتش داده بود و تو در حق او احسان كرده بودى [فرزند خوانده‌ات زيد]، گفتى: همسرت را نگاه دار و از خدا پروا بدار، و چيزى را كه خدا عيان كننده‌ى آن بود در ضمير خويش نهان مى‌داشتى، و از مردم مى‌ترسيدى، و خدا سزاوارتر است كه ا'),
(3571, 33, 38, 'بر پيامبر در آنچه خدا براى او مقرر كرده محذورى نيست. [اين‌] سنت خداست كه از ديرباز در ميان گذشتگان (پيامبران) نيز بوده و فرمان خدا همواره سنجيده و حساب شده است'),
(3572, 33, 39, '[پيامبران‌] كسانى هستند كه پيام‌هاى خدا را مى‌رسانند و از او مى‌ترسند و از هيچ كس جز خدا نمى‌ترسند، و خدا براى حسابرسى كفايت مى‌كند'),
(3573, 33, 40, 'محمد (صلّى الله عليه وآله وسلّم) پدر هيچ يك از مردان شما نيست، و ليكن فرستاده‌ى خدا و خاتم پيامبران است، و خدا به هر چيزى داناست'),
(3574, 33, 41, 'اى كسانى كه ايمان آورده‌ايد! خدا را بسيار ياد كنيد'),
(3575, 33, 42, 'و صبح و شام او را نيايش كنيد'),
(3576, 33, 43, 'اوست كه بر شما درود مى‌فرستد و فرشتگان او نيز، تا شما را از تاريكى‌ها به سوى روشنايى بيرون آورد، و او به مؤمنان مهربان است'),
(3577, 33, 44, 'روزى كه خدا را ملاقات مى‌كنند، درودشان سلام است، و خدا پاداش نفيسى براى آنها آماده كرده است'),
(3578, 33, 45, 'اى پيامبر! ما تو را شاهد و مژده رسان و بيم دهنده فرستاده‌ايم'),
(3579, 33, 46, 'و دعوت كننده به سوى خدا به خواست او، و چراغى تابان'),
(3580, 33, 47, 'و مؤمنان را مژده ده كه براى آنها از جانب خدا تفضّلى بزرگ خواهد بود'),
(3581, 33, 48, 'و كافران و منافقان را اطاعت نكن و آزارشان را ناديده بگير، و بر خدا توكّل كن، كه حمايت خدا كفايت مى‌كند'),
(3582, 33, 49, 'اى كسانى كه ايمان آورده‌ايد! وقتى زنان مؤمن را به همسرى گرفتيد، آن‌گاه پيش از آن كه با آنها نزديكى كنيد طلاقشان داديد، شما را بر آنها عده‌اى نيست كه حسابش را نگاه داريد پس مهرشان را بدهيد و به خوشى رهايشان كنيد'),
(3583, 33, 50, 'اى پيامبر! ما براى تو آن همسرانى را كه مهرشان را داده‌اى حلال كرديم، و كنيزانى كه خدا از غنيمت جنگى در اختيار تو قرار داده، و دختران عمويت و دختران عمّه‌هايت و دختران دايى تو و دختران خاله‌هايت كه با تو مهاجرت كرده‌اند [ازدواج با آنها براى تو حلال اس'),
(3584, 33, 51, '[نوبت‌] هر كدام از آن زنان را كه مى‌خواهى به تأخير مى‌اندازى و هر كدام را كه مى‌خواهى نزد خود جاى مى‌دهى، و اگر هر كدام را كه دور كرده بودى [دوباره‌] بطلبى، بر تو باكى نيست، اين نزديك‌تر است به اين كه چشم‌هاى ايشان روشن شود و محزون نشوند و همگى به آن'),
(3585, 33, 52, 'بعد از اين، هيچ زنى [جز همسران كنونى‌ات‌] بر تو حلال نيست، و نيز نمى‌توانى همسرانت را به همسران ديگرى مبدّل سازى، هر چند جمال آنها تو را جلب كند، مگر آنچه به ملك تو در آيد [يعنى كنيزان‌]، و خدا بر هر چيزى مراقب است'),
(3586, 33, 53, 'اى كسانى كه ايمان آورده‌ايد! داخل خانه‌هاى پيامبر مشويد، مگر آن كه براى [خوردن‌] طعامى به شما اجازه داده شود، [آن هم‌] بى‌آن كه [قبل از وقت بياييد و] منتظر آماده شدن غذا باشيد، ولى هنگامى كه دعوت شديد در آييد، و وقتى غذا خورديد متفرق شويد و به گفت و'),
(3587, 33, 54, 'اگر چيزى را آشكار كنيد يا آن را پنهان داريد، بى‌ترديد خداوند از همه چيز آگاه است'),
(3588, 33, 55, 'بر آنها در مورد پدران و پسران و برادران و برادرزاده‌ها و خواهرزاده‌هايشان و زنان [همكيش‌] خود و بردگانشان گناهى نيست [كه نزدشان بى‌حجاب باشند]، و [اى زنان پيامبر!] از خدا بترسيد كه خدا بر هر چيزى ناظر است'),
(3589, 33, 56, 'بى‌گمان، خدا و فرشتگان او بر پيامبر درود مى‌فرستند. اى كسانى كه ايمان آورده‌ايد! بر او درود فرستيد و سلام گوييد، سلامى [شايسته و تسليم فرمان او باشيد]'),
(3590, 33, 57, 'بى‌گمان، كسانى كه خدا و پيامبر او را آزار مى‌دهند، خدا آنها را در دنيا و آخرت لعنت كرده و برايشان عذابى خفت‌بار آماده كرده است'),
(3591, 33, 58, 'و كسانى كه مردان و زنان مؤمن را با [نسبت دادن‌] گناهى كه نكرده‌اند آزار مى‌دهند، قطعا تهمت و گناه آشكارى را به گردن گرفته‌اند'),
(3592, 33, 59, 'اى پيامبر! به زنان و دخترانت و به زنان مؤمنان بگو پوشش‌هاى خود را بر خود بپيچند. اين براى آن كه [به عفيف بودن‌] شناخته شوند و مورد تعرض [هوسبازان‌] قرار نگيرند نزديك‌تر است، و خدا آمرزنده و مهربان است'),
(3593, 33, 60, 'اگر منافقان و كسانى كه در دل‌هايشان مرض است و شايعه افكنان در مدينه، [از كارهاى خود و آزار زنان‌] باز نايستند، تو را سخت بر آنان مى‌شورانيم كه جز مدت كوتاهى نمى‌توانند در كنار تو در اين شهر بمانند'),
(3594, 33, 61, 'مورد خشم و لعنت [خدا] هستند و هر كجا يافته شوند دستگير و به سختى كشته مى‌شوند'),
(3595, 33, 62, 'اين سنّت خدا در باره‌ى كسانى است كه پيش‌تر بوده‌اند، و در سنّت خدا هرگز تغييرى نخواهى يافت'),
(3596, 33, 63, 'مردم از تو از وقت رستاخيز مى‌پرسند، بگو: علم آن فقط نزد خداست، و تو چه مى‌دانى، شايد رستاخيز نزديك باشد'),
(3597, 33, 64, 'به يقين، خدا كافران را لعنت كرده و براى آنها آتشى برافروخته آماده كرده است'),
(3598, 33, 65, 'جاودانه در آن مى‌مانند نه دوستى مى‌يابند و نه ياورى'),
(3599, 33, 66, 'روزى كه چهره‌هايشان در آتش دگرگون شود، مى‌گويند: اى كاش خدا را فرمان مى‌برديم و پيامبر را اطاعت مى‌كرديم'),
(3600, 33, 67, 'و گويند: پروردگارا! ما پيشوايان و بزرگان خويش را اطاعت كرديم و آنها ما را از راه به در كردند'),
(3601, 33, 68, 'پروردگارا! آنها را دو چندان عذاب ده و لعنتشان كن، لعنتى بزرگ'),
(3602, 33, 69, 'اى كسانى كه ايمان آورده‌ايد! مانند كسانى نباشيد كه موسى را [با اتّهام خود] آزار دادند، و خدا او را از آنچه گفتند مبرّا ساخت، و او نزد خدا آبرومند بود'),
(3603, 33, 70, 'اى كسانى كه ايمان آورده‌ايد! از خدا بترسيد و سخن صواب گوييد'),
(3604, 33, 71, 'تا كارهاى شما را اصلاح كند و گناهانتان را بر شما ببخشايد. و هر كه خدا و رسولش را اطاعت كند، قطعا به كاميابى بزرگى نايل آمده است'),
(3605, 33, 72, 'ما امانت [الهى و بار تكليف‌] را بر آسمان‌ها و زمين و كوه‌ها عرضه كرديم، و آنها [تكوينا] از پذيرفتن آن سرباز زدند و از آن هراسيدند، ولى انسان آن را بر دوش گرفت به راستى او ستمگرى نادان است'),
(3606, 33, 73, '[هدف اين بود] كه خدا مردان و زنان منافق، و مردان و زنان مشرك را [به خاطر سرپيچى از تكليف‌] عذاب كند، و به مردان و زنان با ايمان توجه و عنايت كند، و خدا آمرزنده و مهربان است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ستايش خدايى را كه آنچه در آسمان‌ها و آنچه در زمين است از آن اوست، و در آخرت [نيز] حمد و سپاس از آن اوست، و هم اوست حكيم آگاه'),
(3608, 34, 2, 'آنچه در زمين فرو مى‌رود و آنچه از آن بر مى‌آيد و آنچه از آسمان فرود مى‌آيد و آنچه در آن بالا مى‌رود [همه را] مى‌داند، و اوست مهربان آمرزنده'),
(3609, 34, 3, 'و كسانى كه كافر شدند، گفتند: ما را رستاخيز نخواهد آمد. بگو: چرا، سوگند به پروردگارم كه حتما به سراغتان خواهد آمد. [خدايى كه‌] عالم غيب است و هموزن ذرّه‌اى، نه در آسمان‌ها و نه در زمين، از او پوشيده نيست، و هيچ كوچك‌تر و بزرگ‌تر از آن نيست، مگر اين كه'),
(3610, 34, 4, 'تا كسانى را كه ايمان آوردند و كارهاى شايسته كردند پاداش دهد، آنان آمرزش و روزى كريمانه برايشان خواهد بود'),
(3611, 34, 5, 'و كسانى كه در [ابطال‌] آيات ما كوشش مى‌كنند كه ما را به عجز آورند، برايشان عذابى دردناك از شكنجه است'),
(3612, 34, 6, 'و كسانى كه از دانش بهره يافته‌اند، مى‌دانند كه آنچه از جانب پروردگارت به سوى تو نازل شده، حق است و به راه [آن خداى‌] شكست‌ناپذير ستوده هدايت مى‌كند'),
(3613, 34, 7, 'و كسانى كه كافر شدند، گفتند: آيا مردى را به شما نشان دهيم كه شما را خبر مى‌دهد كه چون كاملا متلاشى شديد، قطعا آفرينشى جديد خواهيد يافت'),
(3614, 34, 8, 'آيا [اين مرد] بر خدا دروغ بسته يا جنونى در اوست؟ [نه،] بلكه آنها كه به آخرت ايمان نمى‌آورند، در عذاب و گمراهى بس دورى هستند'),
(3615, 34, 9, 'آيا به آنچه پيش رو و پشت سرشان از آسمان و زمين است، نگاه نكردند؟ اگر بخواهيم آنها را در زمين فرو مى‌بريم، يا قطعاتى از آسمان بر سرشان مى‌افكنيم. قطعا در اين [هشدار] براى هر بنده‌ى توبه كارى عبرت است'),
(3616, 34, 10, 'و به راستى داود را از جانب خويش مزيّتى عطا كرديم، [و گفتيم:] اى كوه‌ها و اى پرندگان! [در تسبيح‌] با او هم آواز شويد، و آهن را براى او نرم كرديم'),
(3617, 34, 11, 'كه زره‌هاى فراخ بساز و بافت آن را يكنواخت كن، و كار شايسته كنيد همانا من به آنچه انجام مى‌دهيد بينايم'),
(3618, 34, 12, 'و باد را مسخر سليمان كرديم كه [سير] صبحگاهش [مسير] يك ماه و عصرگاهش [نيز مسير] يك ماه بود، و چشمه مس مذاب را براى او روان ساختيم، و عده‌اى از جنّ به فرمان پروردگارشان پيش روى او كار مى‌كردند، و هر كه از آنها از دستور ما سر بر مى‌تافت، از عذاب سوزان ب'),
(3619, 34, 13, 'براى او هر چه مى‌خواست از معبدها و مجسمه‌ها و ظروف بزرگ مانند حوضچه‌ها و ديگ‌هاى ثابت مى‌ساختند. اى خاندان داود! شكرگزار باشيد، و تعداد كمى از بندگان من سپاسگزارند'),
(3620, 34, 14, '[سليمان با اين قدرت‌] هنگامى كه مرگ را براى او مقرر داشتيم، آنها را به مرگ او آگاه نساخت مگر موريانه‌اى كه عصاى او را مى‌خورد. پس چون [پيكرش‌] فرو افتاد، جنّيان دريافتند كه اگر غيب مى‌دانستند، در عذاب خفت بار نمى‌ماندند'),
(3621, 34, 15, 'همانا براى [مردم‌] سبأ در محل سكونتشان نشانه [رحمتى‌] بود دو باغستان داشتند در جانب راست و چپ، [گفتيم:] از روزى پروردگارتان بخوريد و او را شكر كنيد كه شهرى است پاكيزه و پروردگارى آمرزگار'),
(3622, 34, 16, 'ولى روى گردانيدند پس بر آنها سيل ويرانگر فرستاديم، و دو باغستان آنها را به دو باغى با ميوه‌هاى تلخ و شورگز و اندكى از درخت سدر مبدل ساختيم'),
(3623, 34, 17, 'اين [عقوبت‌] را به [سزاى‌] كفرانشان به آنها داديم و آيا جز ناسپاس را مجازات مى‌كنيم'),
(3624, 34, 18, 'و ميان آنها و ميان شهرهايى كه در آنها بركت نهاده بوديم، آبادى‌هايى پيدا [و نزديك به هم‌] پديد آورديم، و در آنها سير و سفر را به اندازه كرديم [و فاصله‌ها برابر بود، و گفتيم:] شب‌ها و روزها به ايمنى در آنها سفر كنيد'),
(3625, 34, 19, 'ولى [اين مردم ناسپاس‌] گفتند: پروردگارا! ميان سفرهايمان دورى افكن [تا درويش و غنى متمايز شوند]، و بر خويشتن ستم كردند پس آنها را افسانه‌هايى [عبرت‌آموز] گردانيديم، و سخت تار و مارشان كرديم. قطعا در اين [ماجرا] براى هر صبر پيشه‌ى سپاسگزارى عبرت‌هاست'),
(3626, 34, 20, 'و همانا شيطان گمان خود را در مورد آنها راست يافت، و جز گروهى از مؤمنان، همه از او پيروى كردند'),
(3627, 34, 21, 'و را بر آنها تسلّطى نبود، جز آن كه [مى‌خواستيم‌] كسى را كه به آخرت ايمان دارد از كسى كه در باره‌ى آن مردّد است باز شناسيم، و پروردگار تو بر هر چيزى نگاهبان است'),
(3628, 34, 22, 'بگو: كسانى را كه به جاى خدا پنداشته‌ايد بخوانيد آنها به اندازه‌ى ذرّه‌اى را نه در آسمان‌ها و نه در زمين مالك نيستند، و در آن دو هيچ شركتى ندارند، و از آنها كسى مددكار خدا نيست'),
(3629, 34, 23, 'و شفاعت در پيشگاه او سود نمى‌بخشد، مگر در باره‌ى كسى كه او را اجازه دهد تا چون هراس از دل‌هايشان برطرف شود، [مجرمان به شافعان‌] گويند: پروردگارتان چه گفت؟ گويند: حق را، و او بلند مرتبه و بزرگ است'),
(3630, 34, 24, 'بگو: كيست كه شما را از آسمان‌ها و زمين روزى مى‌دهد؟ بگو: خداى، و در حقيقت ما و شما يا بر هدايتيم يا در گمراهى آشكار'),
(3631, 34, 25, 'بگو: [شما] از آنچه ما گناه كرده‌ايم بازخواست نمى‌شويد، و [ما نيز] از آنچه شما انجام مى‌دهيد بازخواست نخواهيم شد'),
(3632, 34, 26, 'بگو: پروردگارمان ما و شما را [يكجا] جمع مى‌كند، سپس ميان ما به حق داورى مى‌كند و اوست داور دانا'),
(3633, 34, 27, 'بگو: كسانى را كه به عنوان شريكان به او ملحق كرده‌ايد، به من نشان دهيد. چنين نيست، بلكه اوست خداى شكست‌ناپذير حكيم'),
(3634, 34, 28, 'و ما تو را نفرستاديم جز بشارتگر و هشدار دهنده براى همه مردم ليكن بيشتر مردم نمى‌دانند'),
(3635, 34, 29, 'و مى‌گويند: اين وعده‌ى [قيامت‌] كى خواهد بود، اگر راست مى‌گوييد'),
(3636, 34, 30, 'بگو: براى شما وعدگاه روزى است كه نه ساعتى از آن تأخير مى‌كنيد و نه پيش مى‌افتيد'),
(3637, 34, 31, 'و كسانى كه كافر شدند گفتند: نه به اين قرآن و نه به آن [كتاب‌هايى‌] كه پيش از آن است هرگز ايمان نخواهيم آورد. و اى كاش اين ظالمان را هنگامى كه در پيشگاه پروردگارشان بازداشت شده‌اند ببينى كه [چگونه‌] سخن را به يكديگر برمى‌گردانند [و جدل مى‌كنند و] ضعي'),
(3638, 34, 32, 'مستكبران به ضعيف شدگان گويند: مگر ما بوديم كه شما را از هدايت پس از آن كه به سوى شما آمد بازداشتيم؟ بلكه خودتان گناهكار بوديد'),
(3639, 34, 33, 'و ضعيف شدگان به مستكبران گويند: [نه،] بلكه نيرنگ شب و روز [شما بود]، آن گاه كه ما را وادار مى‌كرديد كه به خدا كافر شويم و براى او همتايانى قرار دهيم. و هنگامى كه عذاب را ببينند پشيمانى [خود] را پنهان مى‌دارند و در گردن‌هاى كسانى كه كافر شدند غل‌ها مى'),
(3640, 34, 34, 'و در هيچ شهرى هشدار دهنده‌اى نفرستاديم جز آن كه خوشگذرانان آنها گفتند: ما به آنچه فرستاده شده‌ايد كافريم'),
(3641, 34, 35, 'و گفتند: اموال و اولاد ما [از همه‌] بيشتر است [و اين نشانه قرب ما به خداست‌] و ما عذاب نخواهيم شد'),
(3642, 34, 36, 'بگو: پروردگار من [جهت مصالحى‌] روزى را براى هر كس كه بخواهد وسعت مى‌دهد يا تنگ مى‌گيرد [اين ربطى به قرب خدا ندارد] ولى بيشتر مردم نمى‌دانند'),
(3643, 34, 37, 'و اموال و فرزندانتان چيزى نيست كه شما را به پيشگاه ما نزديك گرداند، مگر كسانى كه ايمان آورده و كار شايسته كرده باشند، كه آنها را بدانچه كرده‌اند دو برابر پاداش است و آنها در غرفه‌هاى [بهشتى‌] آسوده خاطر خواهند بود'),
(3644, 34, 38, 'و كسانى كه در [ابطال‌] آيات ما مى‌كوشند كه [ما را] به عجز آورند، آنان احضار شوندگان در عذاب [الهى‌] خواهند بود'),
(3645, 34, 39, 'بگو: در حقيقت، پروردگار من روزى را براى هر كس از بندگانش بخواهد وسعت مى‌دهد يا براى او تنگ مى‌گيرد. و هر چه انفاق كرديد عوضش را خدا مى‌دهد، و او بهترين روزى دهندگان است'),
(3646, 34, 40, 'و روزى كه همه آنها را گرد مى‌آورد، آن‌گاه به فرشتگان مى‌گويد: آيا اينان شما را مى‌پرستيدند'),
(3647, 34, 41, 'گويند: منزّهى تو! سرپرست ما تويى نه آنها، بلكه جنّيان را مى‌پرستيدند بيشترشان به آنها ايمان داشتند'),
(3648, 34, 42, 'پس امروز براى يكديگر سود و زيانى نداريد، و به كسانى كه ستم كرده‌اند مى‌گوييم: بچشيد عذاب آتشى را كه آن را دروغ مى‌شمرديد'),
(3649, 34, 43, 'و چون آيات روشن ما بر آنها خوانده شود گويند: اين نيست جز مردى كه مى‌خواهد شما را از آنچه پدرانتان مى‌پرستيدند باز دارد، و [نيز] گويند: اين جز دروغى بربافته نيست. و كسانى كه كافر شدند، در باره‌ى حق وقتى به سويشان آمد گفتند: اين جز افسونى آشكار نيست'),
(3650, 34, 44, 'و ما هيچ كتابى به آنها نداده بوديم كه آن را بخوانند، و پيش از تو هشدار دهنده‌اى به سويشان نفرستاده بوديم. [كه به موجب آن تو را تكذيب كنند'),
(3651, 34, 45, 'و كسانى كه پيش از اينان بودند [نيز] تكذيب كردند، در حالى كه اينان (اهل مكّه) به ده يك آنچه [از قدرت‌] به آنها داده بوديم نمى‌رسند، [با اين حال‌] فرستادگان مرا تكذيب كردند پس [بنگر] چگونه بود عقوبت من'),
(3652, 34, 46, 'بگو: من فقط به شما يك اندرز مى‌دهم كه: دو تا دو تا و يك يك براى خدا به پا خيزيد، سپس بينديشيد كه اين دوست شما [محمد صلّى الله عليه وآله‌] هيچ گونه جنونى ندارد. او صرفا شما را از عذاب سختى كه در پيش است هشدار مى‌دهد'),
(3653, 34, 47, 'بگو: هر مزدى كه از شما خواستم به نفع خودتان است، مزد من جز بر خدا نيست و او بر هر چيزى گواه است'),
(3654, 34, 48, 'بگو: بى‌گمان، پروردگارم حق را [از راه وحى بر دل من‌] مى‌افكند و او داننده‌ى غيب‌هاست'),
(3655, 34, 49, 'بگو: حق آمد و باطل از سر نمى‌گيرد و بر نمى‌گردد'),
(3656, 34, 50, 'بگو: اگر گمراه شوم، فقط به زيان خود گمراه شده‌ام، و اگر هدايت يابم به خاطر آن است كه پروردگارم به من وحى مى‌كند، كه او شنوا و نزديك است'),
(3657, 34, 51, 'و اى كاش مى‌ديدى هنگامى را كه [كافران از اوضاع معاد] وحشت زده‌اند، و گريزى نمانده است و از مكانى نزديك دستگير مى‌شوند'),
(3658, 34, 52, 'و گويند: [اينك‌] به آن [قرآن‌] ايمان آورديم، ولى چگونه از مكانى دور دستيابى [به ايمان‌] براى آنها ميسر است'),
(3659, 34, 53, 'و حال آن كه پيش از اين منكر آن شدند، و از جايى دور به ناديدنى، تير انكار مى‌افكندند'),
(3660, 34, 54, 'و ميان آنها و آنچه از لذايذ مى‌خواستند جدايى افتاد، همان‌گونه كه پيش‌تر با امثال ايشان چنين رفتار شد، زيرا آنها [در باره‌ى رستاخيز] سخت در ترديد بودند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سپاس خداى را كه پديد آورنده‌ى آسمان‌ها و زمين است [و] فرشتگان را رسولانى كرد كه داراى بال‌هاى دوگانه و سه‌گانه و چهارگانه‌اند. در آفرينش، هر چه بخواهد مى‌افزايد، چرا كه خدا بر هر چيزى تواناست'),
(3662, 35, 2, 'هر رحمتى را كه خدا براى مردم بگشايد، بازدارنده‌اى براى آن نيست، و آنچه را كه بازدارد، پس از آن، كسى قادر به فرستادن آن نيست، و او شكست‌ناپذير حكيم است'),
(3663, 35, 3, 'اى مردم! نعمت خدا بر خودتان را ياد كنيد. آيا غير از خدا آفريدگارى هست كه شما را از آسمان و زمين روزى دهد؟ خدايى جز او نيست، پس چگونه [از حق‌] برگردانده مى‌شويد'),
(3664, 35, 4, 'و اگر تو را تكذيب كنند، قطعا پيش از تو [هم‌] فرستادگانى تكذيب شدند، و [همه‌] كارها به سوى خدا بازگردانده مى‌شود'),
(3665, 35, 5, 'اى مردم! همانا وعده‌ى خدا حق است، پس مبادا زندگى دنيا شما را فريب دهد، و زنهار تا [شيطان‌] فريبكار، شما را به [كرم‌] خدا نفريبد'),
(3666, 35, 6, 'در حقيقت، شيطان دشمن شماست، پس او را دشمن بگيريد. جز اين نيست كه او گروه خود را مى‌خواند تا از دوزخيان شوند'),
(3667, 35, 7, 'كسانى كه كافر شدند، عذابى سخت خواهند داشت، و كسانى كه ايمان آوردند و كارهاى شايسته كردند، براى آنها آمرزش و پاداشى بزرگ است'),
(3668, 35, 8, 'آيا آن كس كه زشتى كردارش براى او آراسته شده و آن را زيبا مى‌بيند [مانند مؤمن نيكوكار است‌]؟ پس خداست كه هر كه را بخواهد بى‌راه مى‌گذارد و هر كه را بخواهد هدايت مى‌كند. پس مبادا جان تو بر سر اندوه بر آنها [از كف‌] برود. قطعا خدا به آنچه مى‌كنند داناست'),
(3669, 35, 9, 'و خدا كسى است كه بادها را روانه كرد، كه [همواره‌] ابرى را بر مى‌انگيزند، و [ما] آن را به سوى سرزمين مرده رانديم، و آن زمين را پس از مرگش به آن زنده كرديم. برخاستن [از قبرها] نيز چنين است'),
(3670, 35, 10, 'هر كس عزت مى‌خواهد، عزت يكسره از آن خداست. سخنان پاكيزه به سوى او بالا مى‌رود و عمل صالح، آن را بالا مى‌برد. و كسانى كه زشتى‌ها را طرح‌ريزى مى‌كنند عذابى سخت خواهند داشت، و نيرنگ آنها حتما تباه مى‌شود'),
(3671, 35, 11, 'و خداوند شما را از خاكى آفريد، سپس از نطفه‌اى، آن‌گاه شما را زوج‌ها گردانيد و هيچ زنى باردار نمى‌شود و بار نمى‌نهد مگر با علم او، و هيچ كهنسالى عمر طولانى نمى‌كند و يا از عمرش كاسته نمى‌شود، مگر آن كه در كتابى [مسطور] است. بى‌شك، اين [كار] بر خدا آسا'),
(3672, 35, 12, 'و دو دريا يكسان نيستند: اين يك، شيرين و گوارا و نوشيدنش خوشگوار و آن يك شور و تلخ است و شما از هر دو گوشت تازه مى‌خوريد و وسايل زينتى پوشيدنى استخراج مى‌كنيد، و كشتى‌ها را در آن مى‌بينى كه [موج‌ها را] مى‌شكافند [و پيش مى‌روند] تا از فضل او روزى جويي'),
(3673, 35, 13, 'شب را در روز داخل مى‌كند [و روز بلند مى‌شود] و روز را در شب وارد مى‌كند، و آفتاب و ماه را تسخير كرده است [كه‌] هر يك تا سر آمد معينى روانند. اين است خداوند كه پروردگار شماست. فرمانروايى از آن اوست، و كسانى را كه غير از او مى‌خوانيد، مالك پوست هسته خر'),
(3674, 35, 14, 'اگر آنها را بخوانيد، صداى شما را نمى‌شنوند، و اگر مى‌شنيدند اجابتتان نمى‌كردند، و روز قيامت شرك شما را انكار مى‌كنند و [هيچ كس‌] مثل [خداى‌] آگاه، تو را خبردار نمى‌كند'),
(3675, 35, 15, 'اى مردم! شما به خدا نيازمنديد، و خداست كه بى‌نياز و ستوده است'),
(3676, 35, 16, 'اگر بخواهد، شما را مى‌برد و خلق جديدى مى‌آورد'),
(3677, 35, 17, 'و اين [امر] براى خدا دشوار نيست'),
(3678, 35, 18, 'و هيچ گناهكارى، بار [گناه‌] ديگرى را بر دوش نمى‌كشد، و اگر [شخص‌] گرانبارى [ديگرى را] براى حمل بار گناه خود بخواند، چيزى از آن برداشته نمى‌شود، هر چند خويشاوند باشد. تو تنها كسانى را كه در نهان از پروردگارشان مى‌ترسند و نماز برپا داشته‌اند هشدار توان'),
(3679, 35, 19, 'و نابينا و بينا يكسان نيستند'),
(3680, 35, 20, 'و نه ظلمت‌ها و نه روشنايى'),
(3681, 35, 21, 'و نه سايه و نه گرماى آفتاب'),
(3682, 35, 22, 'و نه زندگان يكسانند و نه مردگان. همانا خدا هر كه را بخواهد شنوا مى‌كند، و تو كسانى را كه در گورهايند شنوا نتوانى كرد'),
(3683, 35, 23, 'تو صرفا [يك‌] هشدار دهنده هستى'),
(3684, 35, 24, 'ما تو را به حق، بشارتگر و هشدار دهنده فرستاديم، و هيچ امّتى نبوده مگر اين كه در ميان آنها هشدار دهنده‌اى گذشته است'),
(3685, 35, 25, 'و اگر تو را تكذيب كنند، قطعا كسانى كه پيش از آنها بودند [نيز] به تكذيب پرداختند پيامبرانشان دلايل آشكار و نوشته‌ها و كتاب روشنگر براى آنها آوردند'),
(3686, 35, 26, 'آن‌گاه كسانى را كه كافر شدند، گرفتار كردم، پس [بنگر] چگونه بود عقوبت من'),
(3687, 35, 27, 'آيا نديدى كه خدا از آسمان، آبى فرستاد و بدان ميوه‌هايى با رنگ‌هاى گوناگون پديد آورديم؟ و برخى از كوه‌ها [و سنگ‌ها] داراى خطوط [و رگه‌هايى‌] سفيد و سرخ به رنگ‌هاى مختلف و [گاه‌] به رنگ كاملا سياه هستند'),
(3688, 35, 28, 'و از انسان‌ها و جنبندگان و چهارپايان نيز همان طور رنگ‌هايشان گوناگون است. از بندگان خدا تنها عالمان از او مى‌ترسند. آرى، خداوند شكست ناپذير آمرزنده است'),
(3689, 35, 29, 'به يقين كسانى كه كتاب خدا را تلاوت مى‌كنند و نماز برپا داشته و از آنچه به ايشان روزى داده‌ايم در نهان و آشكار انفاق كرده‌اند، به تجارتى اميد بسته‌اند كه هرگز زوال نمى‌پذيرد'),
(3690, 35, 30, 'تا [خدا] پاداششان را به طور كامل به آنها بدهد و از فضل خود بر ايشان بيفزايد كه او آمرزنده‌ى قدرشناس است'),
(3691, 35, 31, 'و آنچه را از كتاب به سوى تو وحى كرديم، خود حق و تصديق كننده‌ى [كتاب‌هاى‌] پيش از آن است. قطعا خدا نسبت به بندگانش آگاه و بيناست'),
(3692, 35, 32, 'سپس اين كتاب را به كسانى از بندگان خود كه برگزيديم به ميراث داديم كه از آنها برخى بر خود ستمكارند و برخى ميانه‌رو، و گروهى به اذن خدا در نيكى‌ها پيشگامند. اين همان فضيلت بزرگ است'),
(3693, 35, 33, 'بهشت‌هاى جاويدانى كه در آن وارد مى‌شوند. در آن جا با دستبندهايى زرين و مرواريد زيور مى‌يابند، و در آن جا جامه‌شان حرير است'),
(3694, 35, 34, 'و گويند: سپاس خدايى را كه اندوه را از ما برد. به راستى پروردگار ما آمرزنده و قدردان است'),
(3695, 35, 35, 'خدايى كه ما را به فضل خويش در اين سراى ماندنى [و جاويدان‌] در آورد كه در آن جا نه رنجى به ما مى‌رسد و نه خستگى'),
(3696, 35, 36, 'كسانى كه كافر شدند، آتش جهنم براى آنهاست. نه بر آنها حكم شود تا بميرند، و نه عذاب آن از ايشان كاسته شود. [آرى‌] اين چنين هر كفر پيشه‌اى را عقوبت مى‌كنيم'),
(3697, 35, 37, 'و ايشان در آن جا فرياد بر مى‌آورند: پروردگارا! ما را بيرون بياور، تا غير از آنچه مى‌كرديم، كار شايسته كنيم. [گفته شود:] آيا شما را چندان عمر نداديم كه هر كه پندپذير است در آن مدت پند گيرد؟ و شما را هشدار دهنده هم آمد. پس بچشيد كه براى ستمگران ياورى ن'),
(3698, 35, 38, 'خداست كه داناى نهان آسمان‌ها و زمين است، و اوست كه به راز دل‌ها آگاه است'),
(3699, 35, 39, 'اوست كه شما را در اين سرزمين جانشين گردانيد. پس هر كه كافر شود كفرش به زيان اوست، و كافران را كفرشان جز خشم و بيزارى نزد پروردگارشان نمى‌افزايد، و كافران را كفرشان غير از زيان نمى‌افزايد'),
(3700, 35, 40, 'بگو: به من خبر دهيد از شريكان خودتان كه به جاى خدا مى‌خوانيد به من نشان دهيد كه چه چيزى از زمين را آفريده‌اند، و يا آنها را در [كار] آسمان‌ها مشاركتى است، يا به ايشان كتابى داده‌ايم كه از آن دليلى بر [حقانيت‌] خود دارند؟ [هيچ كدام نيست‌] بلكه ستمكارا'),
(3701, 35, 41, 'همانا خدا آسمان‌ها و زمين را نگاه مى‌دارد كه فرو نريزند، و اگر [به اذن او] فرو ريختند، بعد از او هيچ كس آنها را نگاه نمى‌دارد. اوست بردبار آمرزنده'),
(3702, 35, 42, 'و [مشركان‌] با سوگندهاى سخت خود به خدا سوگند ياد كردند كه اگر بيم رسانى براى آنها بيايد، قطعا راه يافته‌تر از هر امّتى خواهند بود، ولى چون بيم‌رسانى برايشان آمد، جز بر رميدنشان نيفزود'),
(3703, 35, 43, '[زيرا انگيزه‌ى آنها] گردنكشى در زمين و نيرنگ زشت بود، و نيرنگ زشت جز دامن صاحبش را نگيرد. پس آيا جز سنّت [و سرنوشت شوم‌] پيشينيان را انتظار مى‌برند؟ و هرگز براى سنّت خدا تبديلى نخواهى يافت، و هرگز براى سنّت خدا دگرگونى نخواهى يافت'),
(3704, 35, 44, 'آيا در زمين نگرديدند تا عاقبت [كار] كسانى را كه پيش از ايشان بودند و نيرومندتر از آنها بودند بنگرند؟ و هيچ چيز، نه در آسمان‌ها و نه در زمين خدا را ناتوان نمى‌سازد، چرا كه او داناى تواناست'),
(3705, 35, 45, 'و اگر خدا مردم را به [سزاى‌] آنچه انجام داده‌اند مؤاخذه مى‌كرد، هيچ جنبنده‌اى را روى زمين باقى نمى‌گذاشت ولى تا مدتى معين مهلتشان مى‌دهد، و چون اجلشان فرا رسيد، خدا به [كار] بندگانش بيناست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'يا، سين'),
(3707, 36, 2, 'سوگند به قرآن حكيم'),
(3708, 36, 3, 'كه قطعا تو از پيامبرانى'),
(3709, 36, 4, 'بر راهى راست'),
(3710, 36, 5, '[و اين قرآن‌] نازل شده‌ى آن عزيز مهربان است'),
(3711, 36, 6, 'تا مردمى را بيم دهى كه پدرانشان بيم داده نشدند و از اين رو ناآگاهند'),
(3712, 36, 7, '[چون نپذيرفتند] وعده‌ى عذاب بى‌شك در باره‌ى اكثرشان تثبيت شد، پس ايمان نخواهند آورد'),
(3713, 36, 8, 'ما در گردن‌هاى آنها، تا چانه‌هايشان، غل‌هايى نهاده‌ايم، به طورى كه سرهايشان را بالا نگه داشته‌اند [كه زير پايشان را نتوانند ديد]'),
(3714, 36, 9, 'و ما در برابرشان حايلى نهاديم و در پشت سرشان حايلى و بر چشمانشان پرده افكنديم، پس ايشان نمى‌توانند ديد'),
(3715, 36, 10, 'و تفاوتشان نمى‌كند، چه هشدارشان دهى چه هشدارشان ندهى، ايمان نمى‌آورند'),
(3716, 36, 11, 'تو فقط كسى را هشدار مى‌دهى كه تابع قرآن باشد و از خداى رحمان در نهان بترسد. پس او را به آمرزش و پاداشى بى‌كران نويد ده'),
(3717, 36, 12, 'اين ماييم كه مردگان را زنده مى‌كنيم و هر چه را از پيش فرستاده‌اند و آثارشان را مى‌نويسيم، و همه چيز را در لوحى نمايان بر شمرده‌ايم'),
(3718, 36, 13, 'و براى آنها (اهل مكّه)، جريان آن شهر را مثل بزن، آن‌گاه كه رسولان بدان جا آمدند'),
(3719, 36, 14, 'آن‌گاه كه دو تن را به سويشان فرستاديم، ولى تكذيبشان كردند و [آنها را] با سومى تقويت نموديم، پس گفتند: ما به سوى شما فرستاده شده‌ايم'),
(3720, 36, 15, 'گفتند: شما جز بشرى مانند ما نيستيد، و خداى رحمان چيزى نفرستاده است و شما جز دروغ نمى‌گوييد'),
(3721, 36, 16, 'گفتند: پروردگار ما مى‌داند كه ما واقعا به سوى شما فرستاده شده‌ايم'),
(3722, 36, 17, 'و بر عهده‌ى ما چيزى جز ابلاغ آشكار نيست'),
(3723, 36, 18, 'آنها گفتند: ما شما را به فال بد گرفته‌ايم. اگر دست برنداريد، حتما سنگسارتان مى‌كنيم و قطعا عذاب دردناكى از ما به شما خواهد رسيد'),
(3724, 36, 19, '[رسولان‌] گفتند: شومى شما با خود شماست. آيا اگر پندتان دهند [به فال بد مى‌گيريد]؟ بلكه شما قومى افراط كار هستيد'),
(3725, 36, 20, 'و [در اين ميان‌] مردى از دورترين جاى شهر دوان دوان آمد [و] گفت: اى قوم من! از فرستادگان پيروى كنيد'),
(3726, 36, 21, 'از كسانى كه پاداشى از شما نمى‌خواهند و خود هدايت يافته‌اند، پيروى كنيد'),
(3727, 36, 22, 'آخر چرا كسى را نپرستم كه مرا آفريده است و [همه شما] به سوى او بازگردانده مى‌شويد'),
(3728, 36, 23, 'آيا به جاى او خدايانى بگيرم كه اگر خداى رحمان برايم زيانى بخواهد، نه شفاعتشان مشكلى از من دفع مى‌كند و نه نجاتم مى‌دهند'),
(3729, 36, 24, 'در آن صورت من قطعا در گمراهى آشكارى خواهم بود'),
(3730, 36, 25, 'من به پروردگارتان ايمان آوردم، پس [اقرار] مرا بشنويد'),
(3731, 36, 26, '[چون او را بكشتند] گفته شد: به بهشت درآى. گفت: اى كاش، قوم من مى‌دانستند'),
(3732, 36, 27, 'كه پروردگارم چگونه مرا آمرزيد و از اهل كرامتم گردانيد'),
(3733, 36, 28, 'و پس از [شهادت‌] وى بر قومش هيچ سپاهى از آسمان نازل نكرديم و نازل كننده نبوديم'),
(3734, 36, 29, 'تنها يك صيحه بود و بس، كه ناگاه همه در جا خاموش و بى‌جان افتادند'),
(3735, 36, 30, 'اى افسوس بر اين بندگان كه هيچ فرستاده‌اى براى آنها نيامد مگر آن كه او را مسخره مى‌كردند'),
(3736, 36, 31, 'آيا نديدند كه چه بسيار نسل‌ها را پيش از آنان هلاك نموديم كه ديگر آنها به سويشان بر نمى‌گردند [و زنده نمى‌شوند]'),
(3737, 36, 32, 'و قطعا همه آنان در پيشگاه ما احضار خواهند شد'),
(3738, 36, 33, 'و زمين مرده برايشان برهانى است كه آن را زنده كرديم و دانه از آن برآورديم كه از آن مى‌خورند'),
(3739, 36, 34, 'و در آن باغ‌هايى از درختان خرما و انگور پديد آورديم و چشمه‌ها در آن روان كرديم'),
(3740, 36, 35, 'تا از ميوه‌ى آن و آنچه دستانشان به عمل آورده است بخورند. آيا سپاس نمى‌گزارند'),
(3741, 36, 36, 'پاك است آن [خدايى‌] كه همه زوج‌ها را آفريد، از آنچه زمين مى‌روياند و از خودشان و از آنچه نمى‌شناسند'),
(3742, 36, 37, 'و نشانه‌اى [ديگر] برايشان شب است كه روز را [مانند پوست‌] از آن بر مى‌كنيم و به ناگاه آنان در تاريكى فرو مى‌روند'),
(3743, 36, 38, 'و خورشيد به سوى قرارگاه خويش روان است. اين تقدير آن مقتدر داناست'),
(3744, 36, 39, 'و براى ماه منزل‌هايى مقرر كرديم تا [به صورت هلال‌] چون شاخه خشك خرما [باريك شود و] به حال قبلى بازگردد'),
(3745, 36, 40, 'نه خورشيد را سزد كه به ماه رسد، و نه شب پيش گيرنده بر روز است، و هر كدام در مدارى شناورند'),
(3746, 36, 41, 'و آيتى [ديگر] براى آنها اين كه ذرّيّه آنها را در كشتى انباشته شده حمل كرديم'),
(3747, 36, 42, 'و براى ايشان مانند آن، چيزى آفريديم كه بر آن سوار شوند'),
(3748, 36, 43, 'و اگر بخواهيم غرقشان مى‌سازيم كه نه فريادرسى داشته باشند و نه نجات يابند'),
(3749, 36, 44, 'مگر رحمتى از جانب ما [شامل حالشان گردد] و تا چندى زندگى كنند'),
(3750, 36, 45, 'و چون به ايشان گفته شود: از آنچه در پيش رو و پشت سر داريد بترسيد، باشد كه مورد رحمت قرار گيريد [اعتنا نمى‌كنند]'),
(3751, 36, 46, 'و هيچ آيتى از آيات پروردگارشان براى آنان نمى‌آيد جز اين كه از آن رويگردان مى‌شوند'),
(3752, 36, 47, 'و چون به آنها گفته شود: از آنچه خدا روزيتان كرده انفاق كنيد، كسانى كه كافر شدند به مؤمنان مى‌گويند: آيا كسى را طعام دهيم كه اگر خدا مى‌خواست وى را اطعام مى‌كرد؟ شما جز در گمراهى آشكار نيستيد'),
(3753, 36, 48, 'و مى‌گويند: اين وعده‌ى [قيامت‌] كى خواهد بود اگر راست مى‌گوييد'),
(3754, 36, 49, 'آنها جز يك صيحه‌ى [آسمانى‌] را انتظار نخواهند كشيد كه آنان را در حالى كه سرگرم جدالند، غافلگير مى‌كند'),
(3755, 36, 50, 'آن‌گاه نه بتوانند وصيتى كنند و نه به نزد كسان خويش باز گردند'),
(3756, 36, 51, 'و [بار ديگر] در صور دميده شود و به ناگاه از قبرها شتابان به سوى پروردگار خويش روان شوند'),
(3757, 36, 52, 'گويند: واى بر ما، چه كسى ما را از خوابگاهمان برانگيخت؟ اين همان چيزى است كه خداى رحمان وعده داده بود و پيامبران راست گفتند'),
(3758, 36, 53, 'آن جز يك صيحه نخواهد بود كه ناگهان همگى نزد ما احضار خواهند شد'),
(3759, 36, 54, 'امروز بر هيچ كس هيچ ستمى نمى‌رود و جز در برابر آنچه مى‌كرديد پاداش نمى‌بينيد'),
(3760, 36, 55, 'اهل بهشت در اين روز به شادمانى سرگرمند'),
(3761, 36, 56, 'آنها و همسرانشان در سايه‌ها بر تخت‌ها تكيه زده‌اند'),
(3762, 36, 57, 'در آن جا براى آنها [هر گونه‌] ميوه است و هر چه دلشان بخواهد براى آنها [مهيا] ست'),
(3763, 36, 58, 'و [آنها را] سلامى است كه سخنى از پروردگار مهربان است'),
(3764, 36, 59, '[ندا آيد:] امروز [از مؤمنان‌] جدا شويد اى تبهكاران'),
(3765, 36, 60, 'مگر با شما پيمان نبستم اى فرزندان آدم! كه عبادت شيطان نكنيد كه وى دشمن آشكار شماست'),
(3766, 36, 61, 'و اين كه تنها مرا بندگى كنيد كه راه راست همين است'),
(3767, 36, 62, 'و حقا كه او بسيارى از شما را به بيراهه كشاند. چرا تعقل نمى‌كرديد'),
(3768, 36, 63, 'اين همان دوزخى است كه به شما وعده داده مى‌شد'),
(3769, 36, 64, 'هم اكنون در شعله‌هاى آن بسوزيد به سزاى آن كه كفر مى‌ورزيديد'),
(3770, 36, 65, 'امروز بر دهان‌هاشان مهر مى‌نهيم و دست‌هايشان با ما سخن مى‌گويد و پاهايشان بدانچه فراهم مى‌كردند گواهى مى‌دهد'),
(3771, 36, 66, 'و اگر مى‌خواستيم، بى‌ترديد چشمانشان را محو مى‌كرديم، پس [با چشم بسته‌] در راه [نجات‌] بر هم سبقت مى‌جستند، ولى كجا را مى‌ديدند'),
(3772, 36, 67, 'و اگر مى‌خواستيم، قطعا آنان را بر جاى خود مسخ مى‌كرديم [و مى‌خشكانديم‌] كه نتوانند پيش بروند و نه به عقب برگردند'),
(3773, 36, 68, 'و هر كه را عمر دراز دهيم، در آفرينش دگرگونش كنيم [كه ناتوان شود]، پس آيا نمى‌انديشند'),
(3774, 36, 69, 'و [ما] به او شعر نياموختيم و در خور وى نيست. اين [سخن‌] جز يادآورى و قرآنى روشن نيست'),
(3775, 36, 70, 'تا آن را كه [دلش‌] زنده است بيم دهد و گفتار [وعده‌ى جهنم‌] بر كافران تحقق يابد'),
(3776, 36, 71, 'آيا نديدند كه ما با دستان [قدرت‌] خود براى ايشان چهارپايانى آفريديم كه آنها را در اختيار دارند'),
(3777, 36, 72, 'و آنها را برايشان رام كرديم كه هم سوارشان مى‌شوند و هم از آنها تغذيه مى‌كنند'),
(3778, 36, 73, 'و از آنها بهره‌ها و نوشيدنى‌ها دارند. پس چرا شكر نمى‌گزارند'),
(3779, 36, 74, 'و به جاى خدا معبودانى گرفتند به اميد آن كه يارى داده شوند'),
(3780, 36, 75, '[ولى اين معبودان‌] نمى‌توانند آنها را يارى كنند، و اينان براى آنها سپاهى احضار شده‌اند [كه با هم به دوزخ در خواهند آمد]'),
(3781, 36, 76, 'پس گفتار آنها تو را محزون نكند. قطعا ما آنچه را پنهان مى‌دارند و آنچه را آشكار مى‌سازند مى‌دانيم'),
(3782, 36, 77, 'آيا اين انسان نمى‌داند كه او را از نطفه‌اى آفريديم كه هم اكنون دشمنى آشكار شده است'),
(3783, 36, 78, 'و براى ما مثلى آورد و آفرينش خود را فراموش كرد، گفت: چه كسى استخوان‌ها را كه چنين پوسيده است زنده مى‌كند'),
(3784, 36, 79, 'بگو: همان كسى كه نخستين بار آن را آفريد زنده‌اش مى‌كند، و او به هر گونه آفرينشى آگاه است'),
(3785, 36, 80, 'همان [خدايى‌] كه برايتان از درخت سبز آتش آفريد و به ناگاه شما از آن آتش مى‌افروزيد'),
(3786, 36, 81, 'آيا كسى كه آسمان‌ها و زمين را آفريده قادر نيست كه مانند ايشان را بيافريند؟ چرا، و او آفريدگار آگاه است'),
(3787, 36, 82, 'فرمان او هرگاه چيزى را اراده كند، تنها اين است كه به آن بگويد: باش، پس مى‌شود'),
(3788, 36, 83, 'پس منزه است خدايى كه مالكيت و حاكميت هر چيزى به دست اوست و به سوى او بازگردانده مى‌شويد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به صف بستگان، صفى [عظيم‌]'),
(3790, 37, 2, 'و سوگند به نهى كنندگان كه به سختى طرد مى‌كنند'),
(3791, 37, 3, 'و سوگند به تلاوت كنندگان ذكر (و آيات الهى)'),
(3792, 37, 4, 'كه قطعا معبود شما يگانه است'),
(3793, 37, 5, 'پروردگار آسمان‌ها و زمين و آنچه ميان آنهاست و پروردگار مشرق‌هاست'),
(3794, 37, 6, 'ما آسمان نزديك‌تر را به زيور اختران آراستيم'),
(3795, 37, 7, 'و [آن را] از هر شيطان سركشى محفوظ داشتيم'),
(3796, 37, 8, '[به طورى كه‌] نمى‌توانند به [سخن‌] ساكنان عالم بالا گوش فرا دهند، و از هر سويى پرتاب مى‌شوند'),
(3797, 37, 9, 'با شدت به عقب رانده مى‌شوند، و برايشان عذابى پيوسته است'),
(3798, 37, 10, 'مگر كسى كه [از سخن بالاييان‌] چيزى بربايد كه شهابى سوراخ كننده دنبالش مى‌كند'),
(3799, 37, 11, 'پس [از مشركان‌] بپرس: آيا آنها از لحاظ آفرينش سخت‌ترند يا كسانى كه [از فرشتگان و غيره‌] خلق كرده‌ايم؟ آنها را از گلى چسبنده پديد آورديم [كه ضعيفند]'),
(3800, 37, 12, 'بلكه [تو از انكارشان‌] تعجب مى‌كنى، و آنها استهزا مى‌كنند'),
(3801, 37, 13, 'و چون به آنها پند داده شود پند نمى‌گيرند'),
(3802, 37, 14, 'و چون آيتى ببينند آن را به مسخره مى‌گيرند'),
(3803, 37, 15, 'و مى‌گويند: اين جز سحرى آشكار نيست'),
(3804, 37, 16, 'آيا چون مرديم و خاك و استخوان‌هايى شديم، به راستى دوباره زنده مى‌شويم'),
(3805, 37, 17, 'و آيا نياكان ما نيز [زنده مى‌شوند]'),
(3806, 37, 18, 'بگو: آرى، و شما [زنده مى‌شويد] در حالى كه خوار خواهيد بود'),
(3807, 37, 19, 'پس آن تنها يك صيحه است و بس، و به ناگاه همه آنها [برخيزند و] بنگرند'),
(3808, 37, 20, 'و مى‌گويند: واى بر ما! اين روز جزاست'),
(3809, 37, 21, 'اين همان روز داورى است كه تكذيبش مى‌كرديد'),
(3810, 37, 22, '[اى فرشتگان!] گرد آوريد ظالمان و همگنانشان و آنچه را كه پرستش مى‌كردند'),
(3811, 37, 23, '[آرى آنچه را] جز خدا [مى‌پرستيدند گرد آوريد] و به راه جهنم هدايتشان كنيد'),
(3812, 37, 24, 'و آنها را نگه داريد كه بازخواست خواهند شد'),
(3813, 37, 25, 'شما را چه شده است كه يكديگر را يارى نمى‌كنيد'),
(3814, 37, 26, 'بلكه امروز آنها تسليم شدگانند'),
(3815, 37, 27, 'و روى به يكديگر كنند و از هم بپرسند'),
(3816, 37, 28, 'گويند: [پس چه شد؟] شما كه از در نيكخواهى سراغ ما مى‌آمديد'),
(3817, 37, 29, '[مستكبران‌] مى‌گويند: [نه،] بلكه شما [خودتان‌] مؤمن نبوديد'),
(3818, 37, 30, 'و ما را بر شما هيچ تسلطى نبود، بلكه شما خود مردمى طغيانگر بوديد'),
(3819, 37, 31, 'پس وعده‌ى پروردگارمان بر ما محقق شد و اكنون بايد [عذاب را] بچشيم'),
(3820, 37, 32, 'پس شما را گمراه كرديم، زيرا خودمان نيز گمراه بوديم'),
(3821, 37, 33, 'پس بى‌ترديد [همه‌] ايشان در آن روز در عذاب مشتركند'),
(3822, 37, 34, '[آرى‌] ما با مجرمان چنين مى‌كنيم'),
(3823, 37, 35, 'چرا كه آنان بودند كه وقتى به ايشان گفته مى‌شد: خدايى جز خداى يگانه نيست تكبر مى‌ورزيدند'),
(3824, 37, 36, 'و مى‌گفتند: آيا ما خدايان خويش را براى گفته شاعرى مجنون رها كنيم'),
(3825, 37, 37, 'بلكه او حق را آورده و پيامبران را تصديق كرده است'),
(3826, 37, 38, 'بى‌ترديد، شما چشنده‌ى عذاب دردناك خواهيد بود'),
(3827, 37, 39, 'و جز در برابر آنچه مى‌كرديد جزا نمى‌يابيد'),
(3828, 37, 40, 'مگر بندگان خالص شده‌ى خدا'),
(3829, 37, 41, 'كه آنها رزقى معين دارند'),
(3830, 37, 42, '[انواع‌] ميوه‌ها و آنها گرامى داشتگانند'),
(3831, 37, 43, 'در باغ‌هاى پرنعمت'),
(3832, 37, 44, 'بر تخت‌هايى رو به روى هم'),
(3833, 37, 45, 'جامى از شراب طهور بر آنان مى‌گردانند'),
(3834, 37, 46, 'شرابى سپيد و روشن كه براى نوشندگان لذّت بخش است'),
(3835, 37, 47, 'نه در آن تباهى عقل است و نه از آن مست مى‌شوند'),
(3836, 37, 48, 'و در كنارشان دلبرانى درشت چشم است كه [به همسران خود] چشم دوزند'),
(3837, 37, 49, '[سيمين تن‌] گويى تخم ماكيانند كه در پوشش نهاده‌اند'),
(3838, 37, 50, 'رو به يكديگر كنند و از همديگر پرس و جو كنند'),
(3839, 37, 51, 'گوينده‌اى از آنها مى‌گويد: راستى من [در دنيا] همنشينى داشتم'),
(3840, 37, 52, '[كه به من‌] مى‌گفت: آيا واقعا تو [هم‌] از باور دارندگانى'),
(3841, 37, 53, 'آيا وقتى مرديم و خاك و استخوان شديم آيا واقعا [زنده شده‌] مجازات مى‌شويم'),
(3842, 37, 54, 'گويد: آيا شما سر بلند مى‌كنيد ببينيد [او كجاست‌]'),
(3843, 37, 55, 'پس سر بلند مى‌كند و او را در وسط آتش مى‌بيند'),
(3844, 37, 56, 'مى‌گويد: به خدا سوگند چيزى نمانده بود كه مرا به هلاكت اندازى'),
(3845, 37, 57, 'و اگر رحمت پروردگارم نبود، بى‌ترديد من نيز از احضار شدگان [در آتش‌] بودم'),
(3846, 37, 58, '[و از روى شوق مى‌گويد:] آيا ما را ديگر مرگى نيست'),
(3847, 37, 59, 'جز همان مرگ نخستين ما؟ و ما عذاب نخواهيم شد'),
(3848, 37, 60, 'به راستى اين همان كاميابى بزرگ است'),
(3849, 37, 61, 'براى اين (رقم نعمت‌ها) بايد اهل عمل بكوشند'),
(3850, 37, 62, 'آيا اين براى پذيرايى بهتر است يا درخت زقوم'),
(3851, 37, 63, 'همانا ما آن [درخت‌] را براى ستمگران، عذاب قرار داده‌ايم'),
(3852, 37, 64, '[و] آن درختى است كه در اعماق جهنم مى‌رويد'),
(3853, 37, 65, 'ميوه‌اش گويى سرهاى ديوان است'),
(3854, 37, 66, 'پس [دوزخيان‌] حتما از آن مى‌خورند و شكم‌ها را از آن پر مى‌كنند'),
(3855, 37, 67, 'سپس براى ايشان بر روى آن [زقوم‌] مخلوطى از آب جوشان است'),
(3856, 37, 68, 'سپس بازگشتشان بى‌گمان به جانب آتش دوزخ است'),
(3857, 37, 69, 'همانا آنها پدران خود را گمراه يافتند'),
(3858, 37, 70, 'و [با اين حال‌] با سرعت در پى آنها كشانده مى‌شوند'),
(3859, 37, 71, 'و البته پيش از آنها بيشتر پيشينيان گمراه شدند'),
(3860, 37, 72, 'و حال آن كه ما هشدار دهندگانى در ميانشان فرستاديم'),
(3861, 37, 73, 'پس ببين فرجام هشدار داده شدگان چگونه شد'),
(3862, 37, 74, 'به استثناى بندگان خالص شده‌ى خدا'),
(3863, 37, 75, 'و همانا نوح ما را ندا داد و چه نيكو اجابت كننده بوديم'),
(3864, 37, 76, 'و او و كسانش را از آن محنت بزرگ رهانيديم'),
(3865, 37, 77, 'و [تنها] نسل او را باقى گذاشتيم'),
(3866, 37, 78, 'و در ميان آيندگان [آوازه‌ى نيك‌] او را بر جاى نهاديم'),
(3867, 37, 79, 'درود بر نوح در ميان جهانيان'),
(3868, 37, 80, 'ما اين گونه نيكوكاران را پاداش مى‌دهيم'),
(3869, 37, 81, 'به راستى او از بندگان مؤمن ما بود'),
(3870, 37, 82, 'سپس ديگران را غرق كرديم'),
(3871, 37, 83, 'و بى‌گمان، ابراهيم از پيروان او بود'),
(3872, 37, 84, 'آن‌گاه كه با دلى پاك به [پيشگاه‌] پروردگارش روى آورد'),
(3873, 37, 85, 'چون به پدر و قوم خود گفت: [اينها] چيست كه مى‌پرستيد'),
(3874, 37, 86, 'آيا غير از خداوند خدايان دروغين را مى‌خواهيد'),
(3875, 37, 87, 'پس به پروردگار جهانيان چه گمان داريد'),
(3876, 37, 88, 'پس نظرى به ستارگان افكند'),
(3877, 37, 89, 'و گفت: من بيمارم [و با شما به مراسم عيد نمى‌آيم‌]'),
(3878, 37, 90, 'پس از او روى گرداندند و برگشتند'),
(3879, 37, 91, 'پس مخفيانه به سوى خدايانشان رفت و [به ريشخند] گفت: چرا چيزى نمى‌خوريد'),
(3880, 37, 92, 'شما را چه شده كه سخن نمى‌گوييد'),
(3881, 37, 93, 'پس مخفيانه با دست راست ضربتى بر سر آنها كوفت [و آنها را در هم شكست‌]'),
(3882, 37, 94, 'پس [قومش‌] شتابان به سوى او روى آوردند'),
(3883, 37, 95, '[ابراهيم‌] گفت: آيا آنچه را كه [با دست خود] مى‌تراشيد، مى‌پرستيد'),
(3884, 37, 96, 'حال آن كه خداوند هم شما را آفريده و هم آنچه را كه مى‌سازيد'),
(3885, 37, 97, 'گفتند: برايش بنايى بسازيد و او را در جهنمى [از آتش‌] افكنيد'),
(3886, 37, 98, 'پس طرحى براى [نابودى‌] او ريختند ولى ما آنها را پست و [مغلوب‌] ساختيم'),
(3887, 37, 99, 'و [ابراهيم‌] گفت: من به سوى پروردگارم روانم، زودا كه هدايتم كند'),
(3888, 37, 100, '[اى‌] پروردگار من! مرا [فرزندى‌] از شايستگان عطا كن'),
(3889, 37, 101, 'پس پسرى بردبار را به او مژده داديم'),
(3890, 37, 102, 'و چون او همراه پدر به [سنّ‌] كار و كوشش رسيد، گفت: اى پسرك من! من پيوسته در خواب مى‌بينم كه تو را سر مى‌برم. بنگر كه رأى تو چيست؟ گفت: اى پدر من! آنچه را دستور يافته‌اى انجام ده، اگر خدا بخواهد مرا از صابران خواهى يافت'),
(3891, 37, 103, 'پس وقتى هر دو [بر اين كار] تسليم شدند و پسر را به پيشانى بر خاك افكند'),
(3892, 37, 104, 'او را ندا داديم كه اى ابراهيم'),
(3893, 37, 105, 'حقا كه خوابت را تحقق بخشيدى. ما نيكوكاران را چنين پاداش مى‌دهيم'),
(3894, 37, 106, 'به راستى كه اين همان آزمايش آشكار بود'),
(3895, 37, 107, 'و او را در ازاى قربانى بزرگ بازخريديم'),
(3896, 37, 108, 'و در [ميان‌] آيندگان براى او [آوازه‌ى نيك‌] بر جاى نهاديم'),
(3897, 37, 109, 'سلام بر ابراهيم'),
(3898, 37, 110, 'اين چنين نيكوكاران را پاداش مى‌دهيم'),
(3899, 37, 111, 'در حقيقت، او از بندگان مؤمن ما بود'),
(3900, 37, 112, 'و او را به اسحاق، پيامبرى از صالحان بشارت داديم'),
(3901, 37, 113, 'و بر او و بر اسحاق بركت داديم، و از فرزندانشان برخى نيكوكارند و برخى آشكارا بر خود ستمكارند'),
(3902, 37, 114, 'و به راستى به موسى و هارون موهبت داديم'),
(3903, 37, 115, 'و آن دو و قومشان را از محنتى بزرگ نجات داديم'),
(3904, 37, 116, 'و آنان را يارى داديم تا ايشان غالب آمدند'),
(3905, 37, 117, 'و آن دو را كتاب روشنگر داديم'),
(3906, 37, 118, 'و هر دو را به راه راست هدايت كرديم'),
(3907, 37, 119, 'و براى آن دو در [ميان‌] آيندگان [نام نيك‌] بر جاى نهاديم'),
(3908, 37, 120, 'سلام بر موسى و هارون'),
(3909, 37, 121, 'به راستى ما نيكوكاران را چنين پاداش مى‌دهيم'),
(3910, 37, 122, 'آن دو از بندگان مؤمن ما بودند'),
(3911, 37, 123, 'و به راستى الياس از فرستادگان [ما] بود'),
(3912, 37, 124, 'هنگامى كه به قوم خود گفت: آيا پروا نمى‌داريد'),
(3913, 37, 125, 'آيا بت «بعل» را مى‌پرستيد و بهترين آفرينندگان را وا مى‌گذاريد'),
(3914, 37, 126, 'خدا را [ترك مى‌كنيد] كه پروردگار شما و پروردگار پدران پيشين شماست'),
(3915, 37, 127, 'اما او را تكذيب كردند و قطعا آنها از احضار شدگانند'),
(3916, 37, 128, 'مگر بندگان خالص شده‌ى خدا'),
(3917, 37, 129, 'و براى او در [ميان‌] آيندگان [آوازه‌ى نيك‌] بر جاى نهاديم'),
(3918, 37, 130, 'سلام بر الياس'),
(3919, 37, 131, 'ما نيكوكاران را اين گونه پاداش مى‌دهيم'),
(3920, 37, 132, 'همانا او از بندگان مؤمن ما بود'),
(3921, 37, 133, 'و البته لوط [نيز] از پيامبران بود'),
(3922, 37, 134, 'آن‌گاه كه او و همه كسانش را نجات داديم'),
(3923, 37, 135, 'جز پيرزنى را كه در ميان باقى ماندگان [در عذاب‌] بود'),
(3924, 37, 136, 'سپس ديگران را هلاك نموديم'),
(3925, 37, 137, 'و در حقيقت شما بر [آثار] آنان صبحگاهان مى‌گذريد'),
(3926, 37, 138, 'و نيز شامگاهان. آيا تعقل نمى‌كنيد'),
(3927, 37, 139, 'و همانا يونس از زمره‌ى فرستادگان بود'),
(3928, 37, 140, 'آن‌گاه كه به سوى كشتى پر [از جمعيت‌] بگريخت'),
(3929, 37, 141, 'پس [يونس با سرنشينان كشتى‌] قرعه انداخت و خود از باختگان شد'),
(3930, 37, 142, '[او را به دريا افكندند] و ماهى او را لقمه كرد، در حالى كه او ملامتگر [خويش‌] بود'),
(3931, 37, 143, 'پس اگر او از تسبيح گويان نبود'),
(3932, 37, 144, 'قطعا تا روزى كه برانگيخته مى‌شوند، در شكم آن [ماهى‌] مى‌ماند'),
(3933, 37, 145, 'پس او را در حالى كه ناخوش بود به خشكى انداختيم'),
(3934, 37, 146, 'و بر بالاى [سر] او بوته كدويى رويانديم'),
(3935, 37, 147, 'و او را به سوى صد هزار نفر يا بيشتر [به پيامبرى‌] فرستاديم'),
(3936, 37, 148, 'پس ايمان آوردند و تا مدت زمانى برخوردارشان كرديم'),
(3937, 37, 149, 'پس، از ايشان بپرس، آيا دختران براى پروردگار تو و پسران براى آنهاست'),
(3938, 37, 150, 'آيا فرشتگان را مؤنث آفريديم و آنها ناظر بودند'),
(3939, 37, 151, 'آگاه باش كه اينان از دروغ پردازيشان مى‌گويند'),
(3940, 37, 152, 'خدا فرزند آورده است. در حالى كه آنها قطعا دروغگويند'),
(3941, 37, 153, 'آيا [خدا] دختران را بر پسران ترجيح داده است'),
(3942, 37, 154, 'شما را چه شده؟ چگونه داورى مى‌كنيد'),
(3943, 37, 155, 'چرا متذكر نمى‌شويد'),
(3944, 37, 156, 'يا دليل روشنى [در اختيار] داريد'),
(3945, 37, 157, 'پس كتابتان را بياوريد اگر راست مى‌گوييد'),
(3946, 37, 158, 'و ميان خدا و جنّ‌ها خويشاوندى انگاشتند و حال آن كه جنيان نيك دانسته‌اند كه خود احضار شونده‌اند'),
(3947, 37, 159, 'خدا منزه است از آنچه توصيف مى‌كنند'),
(3948, 37, 160, 'مگر بندگان خالص شده‌ى خدا'),
(3949, 37, 161, 'در واقع شما [مشركان‌] و آنچه مى‌پرستيد'),
(3950, 37, 162, 'نمى‌توانيد بر ضد آن [هدف الهى‌] فتنه‌انگيز باشيد'),
(3951, 37, 163, 'مگر كسى را كه به آتش وارد شدنى است'),
(3952, 37, 164, 'و هيچ يك از ما [فرشتگان‌] نيست مگر اين كه براى او مقام [و موقعيتى‌] معين است'),
(3953, 37, 165, 'و البته ما [براى اطاعت خدا] به صف ايستاده‌ايم'),
(3954, 37, 166, 'و بى‌ترديد ما جملگى تسبيح گويان او هستيم'),
(3955, 37, 167, 'و [مشركان‌] به تأكيد مى‌گفتند'),
(3956, 37, 168, 'اگر پندنامه‌اى از پيشينيان نزد ما بود'),
(3957, 37, 169, 'قطعا بندگان خالص شده‌ى خدا مى‌شديم'),
(3958, 37, 170, 'ولى [وقتى قرآن آمد] به آن كافر شدند پس به زودى خواهند دانست'),
(3959, 37, 171, 'و قطعا وعده‌ى [نصرت‌] ما براى بندگان فرستاده شده‌ى ما از پيش مسلم شده است'),
(3960, 37, 172, 'كه آنان حتما [از طرف ما] يارى شدگانند'),
(3961, 37, 173, 'و سپاه ما بى‌ترديد همانها پيروزند'),
(3962, 37, 174, 'پس [اى محمد!] تا مدتى از آنان [كافران‌] روى بگردان [تا فرمان جهاد آيد]'),
(3963, 37, 175, '[عاقبت‌] آنها را بنگر كه به زودى [آنها نيز] خواهند ديد'),
(3964, 37, 176, 'آيا به عذاب ما عجله مى‌كنند'),
(3965, 37, 177, 'پس وقتى [عذاب ما] به آستانه آنها فرود آيد، صبحگاه بيم داده شدگان بد خواهد بود'),
(3966, 37, 178, 'و از ايشان تا مدتى روى بگردان'),
(3967, 37, 179, 'و [عاقبتشان را] بنگر كه به زودى [آنها نيز] خواهند ديد'),
(3968, 37, 180, 'منزه است پروردگار تو، صاحب عزت و بزرگى، از آنچه وصف مى‌كنند'),
(3969, 37, 181, 'و سلام بر فرستادگان'),
(3970, 37, 182, 'و ستايش، ويژه‌ى خداوند، پروردگار جهانيان است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صاد، سوگند به قرآن حاوى ياد [خدا و اندرز]'),
(3972, 38, 2, 'ولى آنان كه كافر شدند در نخوت و مخالفت [با قرآن‌] اند'),
(3973, 38, 3, 'چه بسيار نسل‌هايى را پيش از آنها به هلاكت رسانديم كه فرياد بر مى‌آوردند، ولى وقت گريز گذشته بود'),
(3974, 38, 4, 'و تعجب كردند از اين كه بيم دهنده‌اى از خودشان به سراغ آنها آمده است، و كافران گفتند: اين ساحرى بس دروغ‌پرداز است'),
(3975, 38, 5, 'آيا او همه خدايان را يك خدا قرار داده است؟ اين واقعا چيز عجيبى است'),
(3976, 38, 6, 'و بزرگانشان به راه افتادند [و گفتند:] برويد و بر خدايان خود پايدارى كنيد، اين چيزى است كه [از شما] خواسته مى‌شود'),
(3977, 38, 7, 'ما در اين آيين اخير چنين سخنى نشنيده‌ايم و اين جز دروغبافى نيست'),
(3978, 38, 8, 'آيا از ميان همه ما قرآن بر او نازل شده است؟ بلكه آنان در باره‌ى پندنامه من در ترديدند، بلكه هنوز عذاب مرا نچشيده‌اند [كه چنين گستاخى مى‌كنند]'),
(3979, 38, 9, 'آيا گنجينه‌هاى رحمت پروردگار مقتدر بخشنده‌ات نزد ايشان است'),
(3980, 38, 10, 'يا فرمانروايى آسمان‌ها و زمين و آنچه ميان آن دو است از آن ايشان است؟ [اگر چنين است‌] پس بايد با نردبان‌ها خود را بالا بكشند [و مانع وحى شوند]'),
(3981, 38, 11, '[آرى اينها] در آن جا سپاه كوچك شكست خورده‌اى از همان گروه‌ها هستند [كه با رسولان مقابله كردند]'),
(3982, 38, 12, 'پيش از ايشان نيز قوم نوح و عاد و فرعون صاحب ميخ‌ها [ى شكنجه، پيامبران را] تكذيب كردند'),
(3983, 38, 13, 'و ثمود و قوم لوط و اصحاب ايكه [نيز به تكذيب پرداختند]. آنها دسته‌ها [ى مخالف‌] بودند'),
(3984, 38, 14, 'هيچ يك از اينها نبودند جز اين كه پيامبران را تكذيب كردند، پس عقوبت من [بر آنان‌] واقع شد'),
(3985, 38, 15, 'و اينان نيز جز يك صيحه را انتظار نمى‌كشند كه هيچ مهلت [و بازگشتى‌] براى آن نيست'),
(3986, 38, 16, '[از روى نخوت‌] گفتند: پروردگارا! پيش از روز حساب، نصيب ما را [از عذاب‌] هر چه زودتر بده'),
(3987, 38, 17, 'بر آنچه مى‌گويند صبر كن، و داود بنده‌ى ما را كه داراى قدرت بود ياد كن. آرى، او بسيار رجوع كننده [به سوى خدا] بود'),
(3988, 38, 18, 'ما كوه‌ها را با او مسخر ساختيم كه شامگاهان و بامدادان [خدا را] نيايش مى‌كردند'),
(3989, 38, 19, 'و پرندگان را [بر او] گرد آوريم كه همه به سوى او باز مى‌گشتند [و در فرمانش بودند]'),
(3990, 38, 20, 'و حاكميتش را استوار كرديم و او را حكمت و داورى قاطع عطا كرديم'),
(3991, 38, 21, 'و آيا خبر شاكيان كه از ديوار محراب [او] بالا رفتند، به تو رسيد'),
(3992, 38, 22, 'وقتى [به طور ناگهانى‌] بر داود در آمدند، پس او از آنها به هراس افتاد. گفتند: نترس [ما] دو نفر شاكى هستيم كه يكى از ما بر ديگرى تجاوز كرده، پس ميان ما به حق داورى كن و ستم روا مدار، و ما را به راه راست هدايت كن'),
(3993, 38, 23, 'اين برادر من است، او را نود و نه ميش و مرا يك ميش است، پس مى‌گويد: آن را هم به من واگذار، و در سخن بر من چيره شده است'),
(3994, 38, 24, '[داود] گفت: قطعا او در مطالبه ميش تو كه بر ميش‌هاى خود بيفزايد بر تو ستم كرده است، و در حقيقت بسيارى از شريكان به يكديگر ستم روا مى‌دارند، مگر كسانى كه ايمان آورده و كارهاى شايسته كرده‌اند، و اينها هم اندكند. و داود فهميد كه او را [با اين ماجرا] آزمو'),
(3995, 38, 25, 'پس بر او بخشيديم و بى‌شك او را نزد ما تقرّب و سر انجامى نيكوست'),
(3996, 38, 26, 'اى داود! ما تو را در زمين جانشين [و نماينده‌ى خود] كرديم، پس ميان مردم به حق داورى كن و زنهار از هوس پيروى مكن كه تو را از راه خدا به در كند. البته كسانى كه از راه خدا به در مى‌روند، به سزاى آن كه روز حساب را از ياد برده‌اند عذابى سخت خواهند داشت'),
(3997, 38, 27, 'و آسمان و زمين و آنچه را كه ميان اين دو است به باطل نيافريديم. اين گمان كسانى است كه كافر شدند، پس واى بر كافران از آتش'),
(3998, 38, 28, 'آيا كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند، همانند مفسدان در زمين مى‌كنيم، يا پرهيزكاران را چون فاجران قرار مى‌دهيم'),
(3999, 38, 29, '[اين‌] كتابى مبارك است كه آن را به سوى تو نازل كرديم تا در آيات آن بينديشند و خردمندان پند گيرند'),
(4000, 38, 30, 'و سليمان را به داود عطا كرديم، چه نيكو بنده‌اى! به راستى او بسيار رجوع كننده [به سوى خدا] بود'),
(4001, 38, 31, 'آن‌گاه كه نزديك غروب، اسب‌هاى اصيل تندرو را به او عرضه كردند'),
(4002, 38, 32, '[سليمان‌] گفت: واقعا من دوستى و توجه به اسبان را بر ياد پروردگارم مقدم داشتم تا اين كه [اول وقت نماز گذشت و خورشيد] در پرده نهان شد'),
(4003, 38, 33, '[گفت:] اسب‌ها را نزد من باز آوريد، آن‌گاه بنا كرد به دست كشيدن بر ساق‌ها و گردن آنها [به علامت وقف كردن‌]'),
(4004, 38, 34, 'و همانا سليمان را آزموديم و بر تخت او جسد [بى‌جان فرزندش‌] را بيفكنديم، سپس به توبه باز آمد'),
(4005, 38, 35, 'گفت: پروردگارا! مرا ببخش و حكومتى به من ارزانى دار كه هيچ كس را پس از من سزاوار نباشد. همانا اين تويى كه بسيار بخشنده‌اى'),
(4006, 38, 36, 'پس باد را در اختيار او قرار داديم كه به فرمان او هر جا مى‌خواست به نرمى روان مى‌شد'),
(4007, 38, 37, 'و ديوان را كه بنّا و غواص بودند [مسخر او كرديم‌]'),
(4008, 38, 38, 'و ديگر ديوان [متمرد] را به زنجير بسته [تحت فرمانش در آورديم‌]'),
(4009, 38, 39, '[گفتيم:] اين عطاى ماست، پس بى‌حساب [به هر كس خواهى‌] ببخش و يا امساك كن'),
(4010, 38, 40, 'و قطعا براى او در پيشگاه ما تقرب و فرجامى نيكوست'),
(4011, 38, 41, 'و بنده‌ى ما ايوب را يادآور، آن‌گاه كه پروردگارش را ندا داد كه: شيطان مرا به رنجورى و عذاب افكنده است'),
(4012, 38, 42, '[به او گفتيم:] با پاى خود [به زمين‌] بكوب، اينك اين [چشمه‌سار] شستشوگاهى است خنك و نوشيدنى'),
(4013, 38, 43, '[و دوباره‌] كسانش و مانند آنها را همراه آنها به او بخشيديم، تا رحمتى از جانب ما و تذكرى براى خردمندان باشد'),
(4014, 38, 44, '[و به او گفتيم براى وفا به سوگند:] يك دسته‌اى تركه به دستت برگير و [همسرت را] با آن بزن و سوگند مشكن. همانا ما او را صبور يافتيم. چه نيكو بنده‌اى بود! به راستى او بسيار [به سوى خدا] بازگشت كننده بود'),
(4015, 38, 45, 'و ياد آور بندگان ما ابراهيم و اسحاق و يعقوب را كه [در عبوديت‌] صاحبان قوت و بصيرت بودند'),
(4016, 38, 46, 'ما آنها را با خصلتى بى‌آلايش كه آخرت انديشى بود خالص كرديم'),
(4017, 38, 47, 'و آنها در پيشگاه ما قطعا از برگزيدگان و نيكانند'),
(4018, 38, 48, 'و ياد كن اسماعيل و يسع و ذو الكفل را [كه‌] همه از نيكان بودند'),
(4019, 38, 49, '[اين‌] ياد كردى است، و قطعا براى پرهيزكاران بازگشتگاهى نيكوست'),
(4020, 38, 50, 'بهشت‌هاى جاويدانى كه درهاى آنها به رويشان باز است'),
(4021, 38, 51, 'در آن جا تكيه مى‌زنند [و] ميوه‌هاى فراوان و نوشيدنى در آن جا طلب مى‌كنند'),
(4022, 38, 52, 'و در كنارشان [دلبران‌] همسالى است كه به همسران خود چشم دوزند'),
(4023, 38, 53, 'اين است آنچه براى روز حساب وعده داده مى‌شويد'),
(4024, 38, 54, 'همانا اين رزق ماست كه تمامى ندارد'),
(4025, 38, 55, 'اين است [حال بهشتيان‌] و [امّا] براى طغيانگران واقعا بدترين بازگشتگاه است'),
(4026, 38, 56, 'جهنم كه به آن در مى‌آيند، و بد آرامگاهى است'),
(4027, 38, 57, 'اين آب جوشان و چركاب است كه بايد آن را بچشند'),
(4028, 38, 58, 'و از همين گونه، انواع ديگر [عذاب‌ها]'),
(4029, 38, 59, '[اى رهبران كفر!] اينها گروهى هستند كه با شما به اجبار [به آتش‌] داخل مى‌شوند. خوش آمدشان مباد! بى‌ترديد آنها داخل آتش مى‌شوند'),
(4030, 38, 60, '[آنها به رؤساى خود] مى‌گويند: بلكه بر خود شما خوش آمد مباد! اين [عذاب‌] را شما براى ما از پيش فراهم آورديد، و چه بدقرارگاهى است'),
(4031, 38, 61, 'مى‌گويند: پروردگارا! هر كس اين [عذاب‌] را براى ما پيش فرستاده عذاب او را در آتش دو چندان كن'),
(4032, 38, 62, 'و [دوزخيان‌] گويند: چرا مردانى را كه از بدان مى‌شمرديم در اين جا نمى‌بينيم'),
(4033, 38, 63, 'آيا ما آنها را [كه در دنيا] به ريشخند گرفتيم [اهل نجات بوده‌اند] يا چشم‌ها [ى ما] بر آنها نمى‌افتد'),
(4034, 38, 64, 'بى‌ترديد، اين جدال اهل جهنم يك واقعيت است'),
(4035, 38, 65, 'بگو: من تنها يك بيم دهنده‌ام و جز خداى يگانه قهار معبودى نيست'),
(4036, 38, 66, 'پروردگار آسمان‌ها و زمين و آنچه ميان آنهاست كه توانا و آمرزنده است'),
(4037, 38, 67, 'بگو: اين خبرى بزرگ است'),
(4038, 38, 68, '[كه‌] شما از آن رويگردانيد'),
(4039, 38, 69, 'مرا از ساكنان عالم بالا به هنگامى كه [در باره‌ى خلقت آدم‌] مخاصمه مى‌كردند هيچ آگاهى نيست'),
(4040, 38, 70, 'تنها از آن جهت به من وحى مى‌شود كه من فقط بيم دهنده‌اى آشكارم'),
(4041, 38, 71, 'آن‌گاه كه پروردگارت به فرشتگان گفت: من آفريننده‌ى بشرى از گل خواهم بود'),
(4042, 38, 72, 'پس چون او را [كاملا] نظام بخشيدم و از روح خويش در وى دميدم براى او به سجده افتيد'),
(4043, 38, 73, 'پس همه فرشتگان يكسره سجده كردند'),
(4044, 38, 74, 'مگر ابليس كه بزرگى فروخت و از كافران شد'),
(4045, 38, 75, 'گفت: اى ابليس! چه چيز تو را بازداشت از اين كه براى چيزى كه به دستان قدرت خود آفريدم سجده كنى؟ آيا خود را بزرگ پنداشتى يا [واقعا] از بلند پايگان بودى'),
(4046, 38, 76, 'گفت: من از او بهترم، مرا از آتش و او را از گل آفريدى'),
(4047, 38, 77, 'گفت: پس، از آن بيرون شو، كه تو مطرودى'),
(4048, 38, 78, 'و تا روز جزا لعنت من بر توست'),
(4049, 38, 79, 'گفت: پروردگارا! پس مرا تا روزى كه برانگيخته مى‌شوند مهلت ده'),
(4050, 38, 80, 'گفت: حتما تو از مهلت يافتگانى'),
(4051, 38, 81, 'تا روز وقت معلوم شده'),
(4052, 38, 82, '[ابليس‌] گفت: پس به عزت تو سوگند كه بى‌ترديد همه‌شان را گمراه خواهم كرد'),
(4053, 38, 83, 'مگر آن بندگان خالص تو از ميان آنها'),
(4054, 38, 84, 'گفت: حق است و حق را مى‌گويم'),
(4055, 38, 85, 'به طور حتم و يقين جهنم را از تو و پيروانت يكسره پر خواهم كرد'),
(4056, 38, 86, 'بگو: مزدى بر اين [رسالت‌] از شما طلب نمى‌كنم و من از كسانى نيستم كه [اين رسالت را] بر خود ببندم'),
(4057, 38, 87, 'و اين [قرآن‌] جز تذكارى براى جهانيان نيست'),
(4058, 38, 88, 'و قطعا پس از چندى خبر آن را خواهيد دانست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'فرو فرستادن [اين‌] كتاب از جانب خداى شكست ناپذير حكيم است'),
(4060, 39, 2, 'به راستى ما [اين‌] كتاب را به حق به سوى تو نازل كرديم، پس خدا را بندگى كن در حالى كه دين را براى او خالص كرده‌اى'),
(4061, 39, 3, 'آگاه باش كه عبادت و دين خالص براى خداست، و كسانى كه جز خدا سرپرستانى گرفته‌اند [مى‌گويند:] ما آنها را عبادت نمى‌كنيم جز براى اين كه ما را به خدا نزديك كنند. به يقين خداوند در باره‌ى آنچه اختلاف مى‌كنند ميانشان داورى خواهد كرد. بى‌ترديد خدا آن كسى را ك'),
(4062, 39, 4, 'اگر خدا مى‌خواست براى خود فرزندى بگيرد، قطعا از [ميان‌] آنچه خلق مى‌كند، آنچه را كه مى‌خواست برمى‌گزيد. او [از اين چيزها] منزه است. اوست خداى يگانه‌ى قهار'),
(4063, 39, 5, 'آسمان‌ها و زمين را به حق آفريد. شب را به روز مى‌پيچد و روز را به شب مى‌پيچد، و آفتاب و ماه را مسخّر كرد. هر كدام تا زمانى معين در حركتند. آگاه باش كه او همان تواناى آمرزنده است'),
(4064, 39, 6, 'او شما را از نفسى واحد آفريد، سپس زوجش را از [نوع‌] آن پديد آورد، و براى شما از دام‌ها هشت قسم پديد آورد. شما را [پيوسته‌] در شكم‌هاى مادرانتان مرحله به مرحله در تاريكى‌هاى سه گانه مى‌آفريند. اين است خداوند [يگانه‌] كه پروردگار شماست. فرمانروايى از آ'),
(4065, 39, 7, 'اگر كفر ورزيد، خدا از شما سخت بى‌نياز است و كفر را براى بندگانش نمى‌پسندد، و اگر سپاس داريد آن را براى شما مى‌پسندد. و هيچ گناهكارى بار [گناه‌] ديگرى را بر نمى‌دارد. آن‌گاه بازگشت شما به سوى پروردگارتان است و شما را از آنچه مى‌كرديد خبر خواهد داد، كه'),
(4066, 39, 8, 'و چون به انسان آسيبى رسد، پروردگارش را به حال توبه و انابه مى‌خواند، سپس چون او را از جانب خود نعمتى عطا كند، آن دعاهايى را كه پيش از اين [براى رفع بلا] كرده بود از ياد مى‌برد و براى خدا همتايانى قرار مى‌دهد تا از راه خدا گمراه كند. بگو: اندكى به كفر'),
(4067, 39, 9, '[آيا چنين كسى بهتر است‌] يا آن كسى كه در طول شب در سجده و قيام اطاعت مى‌كند [در حالى كه‌] از آخرت مى‌ترسد و رحمت پروردگارش را اميد دارد؟ بگو: آيا كسانى كه مى‌دانند و كسانى كه نمى‌دانند يكسانند؟ تنها خردمندان متذكر مى‌شوند'),
(4068, 39, 10, 'بگو: اى بندگان من كه ايمان آورده‌ايد! از پروردگارتان بپرهيزيد. براى كسانى كه در اين دنيا نيكى كرده‌اند [پاداش‌] خوبى هست، و زمين خدا وسيع است [از مهاجرت نترسيد]. بى‌ترديد پاداش شكيبايان بى‌حساب و به طور كامل داده مى‌شود'),
(4069, 39, 11, 'بگو: من مأمورم كه خدا را در حالى كه دين را براى او خالص كرده‌ام بپرستم'),
(4070, 39, 12, 'و مأمورم كه نخستين مسلمانان باشم'),
(4071, 39, 13, 'بگو: اگر من نافرمانى خدايم كنم از عذاب روزى هولناك مى‌ترسم'),
(4072, 39, 14, 'بگو: تنها خدا را مى‌پرستم در حالى كه دين خود را براى او خالص ساخته‌ام'),
(4073, 39, 15, 'پس هر چه را غير از او مى‌خواهيد بپرستيد. بگو: زيانكاران [واقعى‌] كسانى هستند كه خود و كسانشان را در روز قيامت به زيان انداخته‌اند. آگاه باش كه زيان آشكار همين است'),
(4074, 39, 16, 'آنها را از بالاى سرشان و از پايين‌شان پرده‌هايى از آتش است. اين چيزى است كه خدا بندگانش را بدان بيم مى‌دهد. اى بندگان من! پس از [نافرمانى‌] من بترسيد'),
(4075, 39, 17, 'و كسانى كه از بندگى طاغوت اجتناب كرده و به خدا روى آورده‌اند، آنها را بشارت است. پس بندگان مرا بشارت بده'),
(4076, 39, 18, 'همان‌هايى كه به سخن گوش فرا مى‌دهند و بهترين آن را پيروى مى‌كنند. اينانند كه خدايشان هدايت كرده و هم اينانند خردمندان'),
(4077, 39, 19, 'پس آيا كسى كه حكم عذاب بر او قطعى شده [رهايى خواهد يافت؟] آيا تو كسى را كه در آتش است رها توانى كرد'),
(4078, 39, 20, 'ليكن كسانى كه از پروردگارشان پروا داشتند، براى ايشان غرفه‌هايى است كه بالاى آنها غرفه‌هايى [ديگر] بنا شده كه نهرها از زير آن روان است وعده‌ى خداست [و] خدا خلف وعده نمى‌كند'),
(4079, 39, 21, 'آيا نديدى كه خدا از آسمان، آبى فرستاد، پس آن را به چشمه‌هايى كه در زمين است راه داد، آن‌گاه به وسيله آن كشتزارى با رنگ‌هاى گوناگون بيرون مى‌آورد، سپس خشك مى‌گردد، آن‌گاه آن را [به تدريج‌] زرد و بى‌روح مى‌بينى، سپس خردش مى‌گرداند؟ قطعا در اين [دگرگونى'),
(4080, 39, 22, 'پس آيا كسى كه خدا سينه‌اش را براى اسلام گشاده و او بر نورى از جانب پروردگار خويش است [مانند سختدلان است‌]؟ پس واى بر سنگدلانى كه ياد خدا نمى‌كنند. اينان در گمراهى آشكارند'),
(4081, 39, 23, 'خدا زيباترين سخن را به صورت كتابى كه [آياتش‌] همانند يكديگر و با هم سازگار است نازل كرده است. آنان كه از پروردگارشان خشيت دارند، پوست بدنشان از آن به لرزه مى‌افتد. سپس پوستشان و دلشان به ياد خدا نرم و آرام مى‌گردد. اين است هدايت خدا كه هر كس را بخواه'),
(4082, 39, 24, 'پس آيا آن كس كه در روز قيامت [دست بسته‌] با صورت خود آن عذاب سخت را دفع مى‌كند [مانند كسى است كه از عذاب الهى ايمن است؟] و به ستمگران گفته مى‌شود: آنچه را كه دستاوردتان است بچشيد'),
(4083, 39, 25, 'كسانى [هم‌] كه پيش از آنان بودند به تكذيب پرداختند، پس از آن جا كه حدس نمى‌زدند عذاب به سراغشان آمد'),
(4084, 39, 26, 'پس خدا در زندگى دنيا رسوايى را به آنان چشانيد و قطعا عذاب آخرت بزرگ‌تر است، اگر مى‌دانستند'),
(4085, 39, 27, 'و به راستى در اين قرآن از هر گونه مثلى براى مردم آورديم، باشد كه آنان پند گيرند'),
(4086, 39, 28, 'قرآن عربى كه هيچ كژى در آن نيست. باشد كه آنها راه تقوا پويند'),
(4087, 39, 29, 'خدا [در باره‌ى چند خدايى و يك خدايى‌] مردى را مثل زده است كه مملوك شريكانى بدخو و ناسازگار است و مردى است كه تنها تسليم يك نفر است. آيا اين دو در مثل يكسانند؟ حمد مخصوص خداست ولى بيشترشان نمى‌دانند'),
(4088, 39, 30, 'قطعا تو خواهى مرد، و آنها [نيز] خواهند مرد'),
(4089, 39, 31, 'سپس شما روز قيامت نزد پروردگارتان مجادله خواهيد كرد'),
(4090, 39, 32, 'پس كيست ستمكارتر از آن كسى كه بر خدا دروغ بست و [سخن‌] راست را چون به سوى او آمد تكذيب كرد؟ آيا كافران را جاى در دوزخ نيست'),
(4091, 39, 33, 'و آن كس كه [سخن‌] راست آورد و آن را تصديق نمود [و به آن عمل كرد]، آنها حقا پرهيزكارانند'),
(4092, 39, 34, 'براى آنها، هر چه بخواهند نزد پروردگارشان خواهد بود. اين پاداش نيكوكاران است'),
(4093, 39, 35, 'تا خدا، بدترين عملى را كه انجام داده‌اند از ايشان بپوشاند و آنها را طبق بهترين اعمالى كه انجام مى‌دادند پاداش دهد'),
(4094, 39, 36, 'آيا خداوند [در حمايت‌] بنده‌اش كافى نيست؟ كه تو را از كسانى كه غير اويند مى‌ترسانند، و هر كه را خدا گمراه كند، برايش هيچ راهنمايى نخواهد بود'),
(4095, 39, 37, 'و هر كه را خدا هدايت كند، گمراه كننده‌اى نخواهد داشت. مگر خدا نيست كه شكست‌ناپذير دادستان نيست'),
(4096, 39, 38, 'و اگر از آنها بپرسى چه كسى آسمان‌ها و زمين را خلق كرده؟ قطعا خواهند گفت: خدا. بگو: پس آيا به نظر شما اينها را كه سواى او مى‌خوانيد اگر خداوند بخواهد به من بلايى برساند آيا اينها مى‌توانند آن بلا را برطرف سازند؟ يا اگر بخواهد به من رحمتى برساند، آيا ا'),
(4097, 39, 39, 'بگو: اى قوم من! آنچه در توان داريد عمل كنيد، من [نيز] عمل مى‌كنم اما به زودى خواهيد دانست'),
(4098, 39, 40, 'چه كسى را عذابى خواهد آمد كه رسوايش كند و در [قيامت‌] عذابى دائم بر او نازل مى‌شود'),
(4099, 39, 41, 'ما اين كتاب را به حق براى [هدايت‌] مردم بر تو نازل كرديم. پس هر كس هدايت پذيرد به سود خود اوست و هر كس بيراهه رود، تنها به زيان خودش گمراه مى‌شود و تو بر آنها نگهبان نيستى'),
(4100, 39, 42, 'خداوند روح انسان‌ها را هنگام مرگشان به تمامى باز مى‌ستاند، و [نيز] روح كسى را كه نمرده است، به هنگام خواب [قبض مى‌كند] پس آن [روح‌] را كه مرگ را بر او مقدر كرده نگاه مى‌دارد، و ديگرى را تا سر آمدى معيّن باز مى‌فرستد. قطعا در اين [امر] براى مردمى كه م'),
(4101, 39, 43, 'آيا غير از خدا شفيعانى براى خود گرفته‌اند؟ بگو: آيا هر چند مالك چيزى نباشند و تعقل نكنند'),
(4102, 39, 44, 'بگو: شفاعت، يكسره از آن خداست. فرمانروايى آسمان‌ها و زمين خاص اوست، سپس به سوى او بازگردانده مى‌شويد'),
(4103, 39, 45, 'و چون خدا به يگانگى ياد شود، دل‌هاى كسانى كه به آخرت ايمان ندارند دچار نفرت مى‌شود، و چون كسانى غير از او ياد شوند، به ناگاه آنها مسرور مى‌شوند'),
(4104, 39, 46, 'بگو: بار الها! [اى‌] پديد آورنده‌ى آسمان‌ها و زمين! [اى‌] داناى نهان و آشكار! تويى كه در ميان بندگانت در آنچه بر سرش اختلاف مى‌كردند داورى خواهى كرد'),
(4105, 39, 47, 'و اگر همه آنچه در زمين است و همانند آن براى ستمكاران مى‌بود، قطعا آن را براى رهايى خودشان از عذاب سخت روز قيامت فديه مى‌دادند. و از خدا برايشان چيزهايى آشكار مى‌شود كه [هرگز] حسابش را نمى‌كردند'),
(4106, 39, 48, 'و [زشتى‌] گناهانى كه مرتكب شده‌اند، برايشان ظاهر مى‌شود، و آنچه را كه بدان ريشخند مى‌كردند، آنها را فرو مى‌گيرد'),
(4107, 39, 49, 'و چون انسان را گزندى رسد، ما را فرا مى‌خواند، سپس چون نعمتى از جانب خود عطايش كنيم، مى‌گويد: تنها به سبب [كاردانى و] دانايى من اين نعمت به من داده شده است. [نه چنان است‌] بلكه آن آزمايشى است، ولى بيشترشان نمى‌دانند'),
(4108, 39, 50, 'آرى، كسانى كه پيش از آنان بودند [نيز] اين [سخن‌] را گفتند، پس آنچه به دست مى‌آوردند كارى برايشان نكرد'),
(4109, 39, 51, 'تا [آن كه‌] كيفر آنچه مرتكب شده بودند بديشان رسيد، و ظالمان اين گروه (اهل مكّه) نيز به زودى نتايج سوء آنچه مرتكب شده‌اند، بديشان خواهد رسيد، و آنها عاجز كننده [ى خدا] نيستند'),
(4110, 39, 52, 'آيا ندانسته‌اند كه خدا روزى را براى هر كه بخواهد فراخ مى‌كند يا تنگ مى‌گيرد؟ قطعا در اين [اندازه‌گيرى‌] براى مردمى كه ايمان مى‌آورند عبرت‌هاست'),
(4111, 39, 53, 'بگو: اى بندگان من كه بر خويشتن زياده‌روى روا داشته‌ايد! از رحمت خدا مأيوس نشويد. همانا خداوند، همه گناهان را [با شرايطش‌] مى‌آمرزد، كه او خود آمرزنده‌ى مهربان است'),
(4112, 39, 54, 'و پيش از آن كه شما را عذاب فرا رسد و ديگر [از سوى هيچ كس‌] يارى نشويد، به سوى پروردگارتان روى آوريد و تسليم او شويد'),
(4113, 39, 55, 'و از بهترين چيزى كه از جانب پروردگارتان به سوى شما نازل شده پيروى كنيد، پيش از آن كه عذاب الهى ناگهان به سراغ شما آيد، در حالى كه از آن خبر نداريد'),
(4114, 39, 56, 'تا [مبادا] كسى بگويد: افسوس بر آنچه در كار خدا كوتاهى كردم! و حقّا كه من از ريشخند كنندگان بودم'),
(4115, 39, 57, 'يا بگويد: اگر خدا مرا هدايت مى‌كرد، بى‌شك از پرهيزگاران مى‌شدم'),
(4116, 39, 58, 'يا چون عذاب را ببيند، بگويد: كاش مرا برگشتى [به دنيا] بود تا از نيكوكاران مى‌شدم'),
(4117, 39, 59, '[به او گفته مى‌شود]: آرى، آيات من بر تو آمد و آنها را تكذيب كردى و تكبّر نمودى و از كافران شدى'),
(4118, 39, 60, 'و روز قيامت كسانى را كه بر خدا دروغ بستند مى‌بينى روهايشان سياه است. آيا سركشان را جاى در دوزخ نيست'),
(4119, 39, 61, 'و خدا پرهيزگاران را به سبب كاميابى‌شان [در تقوا] نجات مى‌دهد. به آنها نه [هيچ گونه‌] بدى مى‌رسد و نه اندوهگين مى‌شوند'),
(4120, 39, 62, 'خدا آفريدگار همه چيز است، و همو نگهبان همه چيز است'),
(4121, 39, 63, 'كليدهاى آسمان‌ها و زمين از آن اوست، و كسانى كه آيات خدا را انكار كردند آنها حقا زيانكارند'),
(4122, 39, 64, 'بگو: آيا مرا وادار مى‌كنيد كه غير خدا را بپرستم اى نادان‌ها'),
(4123, 39, 65, 'و قطعا به تو و كسانى كه پيش از تو بودند وحى شده است كه اگر شرك ورزى مسلما عمل تو تباه مى‌شود و بى‌شك از زيانكاران مى‌شوى'),
(4124, 39, 66, 'بلكه [تنها] خدا را پرستش كن و از سپاسگزاران باش'),
(4125, 39, 67, 'و خدا را آن چنان كه بايد ارج ننهادند، حال آن كه در روز قيامت زمين يكجا در قبضه او و آسمان‌ها پيچيده شده به دست اوست. او منزّه است و برتر از آنچه [با وى‌] شريك مى‌گردانند'),
(4126, 39, 68, 'و در صور دميده شود، پس هر كه در آسمان‌ها و هر كه در زمين است بى‌جان افتد، مگر كسى كه خدا بخواهد. سپس بار ديگر در آن دميده شود و به ناگاه آنها به پا خيزند و [با نگرانى‌] نگاه كنند'),
(4127, 39, 69, 'و زمين به نور پروردگارش روشن گردد، و كارنامه [اعمال در ميان‌] نهاده شود، و پيامبران و شاهدان را بياورند، و ميانشان به حق داورى گردد و بر آنان ستم نرود'),
(4128, 39, 70, 'و به هر كس آنچه انجام داده به تمامى ادا شود، و خدا به آنچه مى‌كنند آگاه‌تر است'),
(4129, 39, 71, 'و كسانى كه كافر شدند، گروه گروه به سوى جهنّم رانده شوند. تا چون بدان جا رسند، درهاى آن [به رويشان‌] گشوده شود و نگهبانانش به آنها گويند: مگر فرستادگانى از خودتان به سوى شما نيامدند كه آيات پروردگارتان را بر شما بخوانند و به ديدار چنين روزى شما را هشد'),
(4130, 39, 72, 'و گفته شود: از درهاى دوزخ داخل شويد و جاودانه در آن بمانيد، پس بد است جايگاه متكبران'),
(4131, 39, 73, 'و كسانى كه از پروردگارشان پروا داشتند، گروه گروه به سوى بهشت سوق داده شوند. تا چون بدان جا رسند و درهاى آن [به رويشان‌] گشوده گردد و نگهبانان آن به ايشان گويند: سلام بر شما! پاك شديد، پس براى هميشه داخل آن شويد'),
(4132, 39, 74, 'و گويند: سپاس خدايى را كه وعده‌اش را بر ما راست گردانيد و سرزمين [بهشت‌] را به ما ميراث داد. اكنون در هر جاى بهشت كه بخواهيم جاى مى‌گيريم، پس چه نيكوست پاداش عمل كنندگان'),
(4133, 39, 75, 'و فرشتگان را مى‌بينى كه پيرامون عرش حلقه زنان به ستايش پروردگارشان تسبيح مى‌گويند، و [سر انجام‌] ميان بندگان به حق داورى مى‌گردد و گفته مى‌شود: ستايش ويژه‌ى خداست كه، پروردگار جهانيان است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حا، ميم'),
(4135, 40, 2, 'فرو فرستادن [اين‌] كتاب از جانب خداى شكست ناپذير داناست'),
(4136, 40, 3, 'آمرزنده‌ى گناه، پذيرنده‌ى توبه، سخت كيفر و صاحب نعمت [فراوان‌] است. خدايى جز او نيست. بازگشت [همه‌] به سوى اوست'),
(4137, 40, 4, 'در آيات خدا جز كسانى كه كافر شدند جدال نمى‌كنند پس رفت و آمد [و قدرت نمايى‌] آنها در شهرها تو را نفريبد'),
(4138, 40, 5, 'پيش از آنها قوم نوح، و بعد از ايشان دسته‌هاى مخالف، [پيامبران‌] را تكذيب كردند، و هر امّتى قصد آن كرد كه پيامبرش را گرفتار سازد، و به باطل جدال نمودند تا حقيقت را با آن از ميان بردارند. امّا من آنها را [به عقوبت‌] گرفتم، پس [بنگر كه‌] عقوبت من چگونه ب'),
(4139, 40, 6, 'و بدين‌سان حكم پروردگارت در باره‌ى كسانى كه كفر ورزيدند تحقق يافت كه ايشان اهل آتشند'),
(4140, 40, 7, 'فرشتگانى كه حاملان عرشند و آنها كه پيرامون آنند، به ستايش پروردگارشان تسبيح مى‌گويند و به او ايمان دارند و براى مؤمنان آمرزش مى‌خواهند [و مى‌گويند:] پروردگارا! رحمت و دانش تو شامل همه چيز شده است، پس كسانى را كه توبه كرده و راه تو را دنبال نموده‌اند ب'),
(4141, 40, 8, 'پروردگارا! و آنان و هر كه از پدران و همسران و فرزندانشان را كه به صلاح آمده‌اند، به بهشت‌هاى جاودانى كه وعده‌شان داده‌اى داخل كن، همانا تو خود شكست‌ناپذير و حكيمى'),
(4142, 40, 9, 'و آنها را از عقوبت‌ها نگاه دار، و هر كه را در آن روز از عقوبات حفظ كنى به راستى بر او رحمت آورده‌اى، و اين همان كاميابى بزرگ است'),
(4143, 40, 10, 'كافران ندا داده شوند كه نفرت و خشم خدا نسبت به شما از نفرت و خشم شما با يكديگر قطعا بزرگ‌تر است، آن‌گاه كه شما به ايمان دعوت مى‌شديد و انكار مى‌كرديد'),
(4144, 40, 11, 'مى‌گويند: پروردگارا! ما را دوبار ميراندى و دوبار زنده كردى. حالا به گناهانمان اعتراف كرديم، پس آيا راهى به سوى خروج [از آتش‌] هست'),
(4145, 40, 12, 'اين [كيفر] براى آن است كه چون خدا به تنهايى خوانده مى‌شد، انكار مى‌كرديد، و چون به او شرك آورده مى‌شد [به آن‌] ايمان مى‌آورديد. پس [امروز] حكم [در باره‌ى شما] از آن خداى والاى بزرگ است'),
(4146, 40, 13, 'اوست آن كس كه آيات خود را به شما نشان مى‌دهد و برايتان از آسمان روزى مى‌فرستد، و تنها كسى [از اين امور] پند مى‌گيرد كه روى به خدا آورد'),
(4147, 40, 14, 'پس خدا را در حالى كه عبادت را براى او خالص كرده‌ايد بخوانيد، هر چند كافران را خوش نيايد'),
(4148, 40, 15, '[خدايى كه‌] داراى منزلت‌هاى رفيع و صاحب عرش است. به هر كس از بندگانش كه خواهد، آن روح (فرشته) را به فرمان خويش مى‌فرستد، تا [مردم را] از روز ملاقات [با خدا] بترساند'),
(4149, 40, 16, 'آن روز كه مردم حاضر و نمايان شوند، چيزى از آنها بر خدا پوشيده نماند. امروز فرمانروايى از آن كيست؟ از آن خداوند يكتاى قهّار است'),
(4150, 40, 17, 'امروز هر كسى به [موجب‌] آنچه انجام داده است جزا داده مى‌شود. امروز هيچ ستمى نيست و خدا زود به حساب‌ها مى‌رسد'),
(4151, 40, 18, 'و آنها را از آن روز قريب الوقوع بترسان، آن‌گاه كه جان‌ها به گلوگاه مى‌رسد در حالى كه اندوه خود را فرو مى‌خورند. براى ستمگران نه ياورى هست و نه شفاعتگرى كه مورد اطاعت باشد'),
(4152, 40, 19, '[خدا] نگاه‌هاى خيانت بار و آنچه را كه سينه‌ها پنهان مى‌دارند، مى‌داند'),
(4153, 40, 20, 'و خدا به حق حكم مى‌كند، و معبودهايى را كه در برابر او مى‌خوانند هيچ گونه داورى ندارند همانا خداست كه شنواى بيناست'),
(4154, 40, 21, 'آيا در زمين سير نكردند تا ببينند عاقبت كسانى كه پيش از آنها بودند چگونه شد؟ آنها از ايشان در قدرت و آثارى كه در زمين داشتند نيرومندتر بودند، ولى خدا آنها را به كيفر گناهانشان گرفتار كرد، و در برابر خدا مدافعى نداشتند'),
(4155, 40, 22, 'اين [كيفر] از آن روى بود كه پيامبرانشان دلايل آشكار برايشان مى‌آوردند، ولى [آنها] انكار مى‌كردند. پس خدا [گريبان‌] آنها را گرفت همانا او نيرومند سخت كيفر است'),
(4156, 40, 23, 'و به راستى موسى را با آيات خود و حجّتى آشكار فرستاديم'),
(4157, 40, 24, 'به سوى فرعون و هامان و قارون، ولى آنها گفتند: او جادوگرى دروغزن است'),
(4158, 40, 25, 'پس وقتى حقيقت را از جانب ما براى آنان آورد، گفتند: پسران كسانى را كه با او ايمان آورده‌اند بكشيد و زنانشان را [براى بيگارى‌] زنده بگذاريد. ولى نيرنگ كافران جز در تباهى نيست'),
(4159, 40, 26, 'و فرعون گفت: مرا بگذاريد موسى را بكشم و او پروردگارش را [به يارى‌] بخواند. من مى‌ترسم آيين شما را تغيير دهد يا در زمين شورش پديد آورد'),
(4160, 40, 27, 'و موسى گفت: من از هر متكبرى كه به روز حساب ايمان نمى‌آورد، به پروردگار خود و پروردگار شما پناه مى‌برم'),
(4161, 40, 28, 'و مرد مؤمنى از خاندان فرعون كه ايمان خود را كتمان مى‌كرد گفت: آيا مردى را مى‌كشيد كه مى‌گويد: پروردگار من خداست؟ در صورتى كه براى شما از جانب پروردگارتان دلايل آشكارى آورده، و اگر دروغگو باشد دروغش به زيان اوست، و اگر راستگو باشد [لا اقل‌] برخى از آن'),
(4162, 40, 29, 'اى قوم من! امروز حكومت از آن شماست [و] در زمين غلبه داريد، ولى اگر عذاب خدا بر سر ما آيد چه كسى ياريمان خواهد كرد؟ فرعون گفت: جز آنچه [مصلحت‌] مى‌بينم به شما ارائه نمى‌دهم و شما را جز به راه صحيح راهنمايى نمى‌كنم [مصلحت در قتل موسى است‌]'),
(4163, 40, 30, 'و كسى كه ايمان آورده بود، گفت: اى قوم من! همانا من بر شما از [روزى‌] مانند روزگار [هلاك‌] گروه‌ها [ى مخالف پيامبران‌] بيم دارم'),
(4164, 40, 31, 'نظير جريان قوم نوح و عاد و ثمود و كسانى كه پس از آنها بودند و خدا بر بندگان ستم نمى‌خواهد'),
(4165, 40, 32, 'و اى قوم من! من بر شما از روزى كه مردم يكديگر را [به يارى هم‌] بانگ مى‌زنند بيمناكم'),
(4166, 40, 33, 'روزى كه پشت‌كنان باز مى‌گرديد، براى شما در برابر خدا هيچ پناه دهنده‌اى نيست. و هر كه را خدا در بيراهه بگذارد، وى را راهنمايى نخواهد بود'),
(4167, 40, 34, 'و همانا يوسف پيش از اين براى شما، دلايل آشكار آورد، ولى شما از آنچه برايتان آورد همواره در شك بوديد تا زمانى كه از دنيا رفت، گفتيد: خدا بعد از او هرگز فرستاده‌اى مبعوث نخواهد كرد. آرى، اين گونه خدا هر كه را افراطگر شكاك است بى‌راه مى‌گذارد'),
(4168, 40, 35, 'كسانى كه در باره‌ى آيات خدا بى‌آن كه حجّتى برايشان آمده باشد، مجادله مى‌كنند، مورد خشم و عداوت سختى در پيشگاه خدا و نزد كسانى كه ايمان آورده‌اند، مى‌باشند. اين گونه خدا بر دل هر متكبّر زورگويى مهر مى‌نهد'),
(4169, 40, 36, 'و فرعون گفت: اى هامان! براى من بناى مرتفعى بساز، شايد من به آن راه‌ها دست يابم'),
(4170, 40, 37, 'راه‌هاى آسمان‌ها، تا از خداى موسى اطلاعى حاصل كنم. همانا من او را سخت دروغپرداز مى‌پندارم. و اين گونه براى فرعون، زشتى كارش آراسته شد و از راه [راست‌] بازماند. و نيرنگ فرعون جز در زيان و تباهى نبود'),
(4171, 40, 38, 'و آن كس كه ايمان آورده بود گفت: اى قوم من! مرا پيروى كنيد تا شما را به راه درست هدايت كنم'),
(4172, 40, 39, 'اى قوم من! زندگى اين دنيا تنها متاع [ناچيزى‌] است، و آخرت است كه سراى بقاست'),
(4173, 40, 40, 'هر كه بدى كند، جز به قدر آن مجازات نمى‌شود، و كسانى كه كار شايسته كنند- چه مرد و چه زن- و مؤمن باشند، آنها داخل بهشت شده، در آن جا بى‌حساب روزى داده مى‌شوند'),
(4174, 40, 41, 'و اى قوم من! چيست كه من شما را به نجات مى‌خوانم و [شما] مرا به آتش مى‌خوانيد'),
(4175, 40, 42, 'مرا فرا مى‌خوانيد تا به خدا كافر شوم و چيزى را كه بدان علمى ندارم با او شريك گردانم، ولى من شما را به سوى آن شكست ناپذير آمرزنده دعوت مى‌كنم'),
(4176, 40, 43, 'بى‌ترديد آنچه مرا به سوى آن دعوت مى‌كنيد، نه در دنيا دعوت [و حاكميّتى‌] دارد و نه در آخرت. و به راستى، برگشت ما تنها به سوى خداست، و افراطگران اهل آتشند'),
(4177, 40, 44, 'پس به زودى آنچه را به شما مى‌گويم به خاطر خواهيد آورد و من كار خويش را به خدا واگذار مى‌كنم، كه خدا به [حال‌] بندگان بيناست'),
(4178, 40, 45, 'پس خدا او را از نقشه‌هاى سوء آنچه [برايش‌] انديشيده بودند نگه داشت، و فرعونيان را عذاب سخت فرا گرفت'),
(4179, 40, 46, 'هر صبح و شام بر آتش عرضه مى‌شوند، و روزى كه رستاخيز برپا شود [فرمان مى‌رسد كه:] فرعونيان را در سخت‌ترين عذاب داخل كنيد'),
(4180, 40, 47, 'و آن‌گاه كه در آتش به مجادله برخيزند، ضعيفان به مستكبران گويند: ما پيرو شما بوديم آيا مى‌توانيد مقدارى از اين آتش را از ما دفع كنيد'),
(4181, 40, 48, 'كسانى كه گردنكشى مى‌كردند، گويند: [اكنون‌] همه ما در آتشيم. همانا خدا ميان بندگان [خود] داورى كرده است'),
(4182, 40, 49, 'و كسانى كه در آتشند، به نگاهبانان جهنّم گويند: پروردگارتان را بخوانيد تا يك روز از اين عذاب را به ما تخفيف دهد'),
(4183, 40, 50, 'گويند: مگر پيامبرانتان دلايل روشن برايتان نياوردند؟ گويند: چرا. گويند: پس بخوانيد كه دعاى كافران جز در بيراهه نيست [و به جايى نمى‌رسد]'),
(4184, 40, 51, 'بى‌ترديد ما فرستادگان خود و كسانى را كه ايمان آوردند، در زندگى دنيا و روزى كه شاهدان [براى شهادت‌] بر مى‌خيزند، يارى مى‌كنيم'),
(4185, 40, 52, 'روزى كه ستمگران را معذرتشان سود نمى‌دهد، و براى آنان لعنت است، و برايشان بد فرجامى آن سراست'),
(4186, 40, 53, 'و همانا موسى را هدايت داديم و بنى اسرائيل را وارث كتاب [تورات‌] نموديم'),
(4187, 40, 54, 'كه رهنمود و ياد كردى براى خردمندان است'),
(4188, 40, 55, 'پس صبر كن كه وعده‌ى خدا حق است، و براى گناهت آمرزش بخواه و با ستايش پروردگارت، شامگاهان و بامدادان تسبيح گوى'),
(4189, 40, 56, 'به راستى آنان كه در باره‌ى آيات خدا- بى‌آن كه حجّتى برايشان آمده باشد- به مجادله بر مى‌خيزند، در دل‌هايشان جز تكبر و نخوت نيست كه به [خواسته خود] نخواهند رسيد. پس به خدا پناه ببر، كه او خود شنواى بيناست'),
(4190, 40, 57, 'قطعا آفرينش آسمان‌ها و زمين از آفرينش مردم بزرگ‌تر [و دشوارتر] است، ولى بيشتر مردم نمى‌دانند'),
(4191, 40, 58, 'و كور و بينا يكسان نيستند، و كسانى كه ايمان آورده و كارهاى شايسته كرده‌اند با بدكاران [برابر] نيستند، [ولى‌] اندكى پند مى‌پذيريد'),
(4192, 40, 59, 'بى‌گمان رستاخيز آمدنى است و در آن ترديدى نيست، ولى بيشتر مردم ايمان نمى‌آورند'),
(4193, 40, 60, 'و پروردگارتان گفته است: مرا بخوانيد تا شما را اجابت كنم. بى‌ترديد كسانى كه از پرستش من تكبر مى‌ورزند زودا كه سر افكنده وارد دوزخ شوند'),
(4194, 40, 61, 'خدا كسى است كه شب را براى شما پديد آورد تا در آن آرام گيريد، و روز را روشن ساخت. آرى خدا بر مردم بسيار صاحب تفضّل است، ولى بيشتر مردم سپاس نمى‌گزارند'),
(4195, 40, 62, 'اين است خداوند [يكتا]، پروردگار شما [كه‌] آفريننده‌ى هر چيزى است [و] خدايى جز او نيست. پس چگونه [از او] بازگردانده مى‌شويد'),
(4196, 40, 63, 'كسانى كه آيات خدا را انكار مى‌كردند، اين گونه به انحراف كشيده مى‌شوند'),
(4197, 40, 64, 'خدا كسى است كه زمين را براى شما قرارگاه ساخت و آسمان را بنايى [گردانيد] و شما را صورتگرى كرد و صورت‌هاى شما را نيكو نمود و از چيزهاى پاكيزه به شما روزى داد. اين است خداوند يكتا، پروردگار شما! پرخير و پاينده است خداوند، پروردگار جهانيان'),
(4198, 40, 65, 'او زنده [ى واقعى‌] است. معبودى جز او نيست. پس او را بخوانيد در حالى كه بندگى را براى او خالص كرده‌ايد. ستايش ويژه‌ى خدا، پروردگار جهانيان است'),
(4199, 40, 66, 'بگو: من نهى شده‌ام از اين كه آنها را كه شما سواى خدا مى‌خوانيد پرستش كنم، حال كه از جانب پروردگارم دلايل روشن براى من آمده است، و فرمان يافته‌ام كه تسليم پروردگار جهانيان باشم'),
(4200, 40, 67, 'او كسى است كه شما را از خاكى، سپس از نطفه‌اى، آن‌گاه از علقه‌اى آفريد، و بعد شما را [به صورت‌] كودكى بيرون مى‌آورد تا به كمال قوت خود برسيد و تا سالمند شويد. و از ميان شما كسى است كه پيش از پيرى مى‌ميرد، و تا [برخى‌تان‌] به مدتى كه مقرر است برسيد، و'),
(4201, 40, 68, 'اوست كه زنده مى‌كند و مى‌ميراند، و چون به كارى حكم كند، تنها به آن مى‌گويد: باش، پس مى‌شود'),
(4202, 40, 69, 'آيا نديدى كسانى را كه در آيات خدا مجادله مى‌كنند چگونه [از راه حق‌] برگردانده مى‌شوند'),
(4203, 40, 70, 'كسانى كه كتاب [خدا] و آنچه را كه فرستادگان خود را بدان گسيل داشته‌ايم تكذيب كردند، به زودى خواهند دانست'),
(4204, 40, 71, 'آن‌گاه كه غل‌ها و زنجيرها در گردن‌هايشان باشد و بر روى كشانيده شوند'),
(4205, 40, 72, 'در آب جوشان و آن‌گاه در آتش افروخته شوند'),
(4206, 40, 73, 'آن‌گاه به آنان گفته مى‌شود: آن شريكانى كه براى خدا مى‌پنداشتند كجا هستند'),
(4207, 40, 74, 'از غير خدا؟ مى‌گويند: آنها از [ديد] ما گم شدند، بلكه قبلا [هم‌] ما چيزى را نمى‌پرستيديم. خدا كافران را بدين‌سان بى‌راه مى‌گذارد'),
(4208, 40, 75, 'اين [عقوبت‌] به سبب آن است كه در زمين به ناروا شادى و سرمستى مى‌كرديد و بدان سبب كه [سخت به خود] مى‌نازيديد'),
(4209, 40, 76, 'از درهاى دوزخ داخل شويد، در آن جاودان [بمانيد]، پس جايگاه سركشان بد است'),
(4210, 40, 77, 'پس صبر كن كه وعده‌ى خدا راست است. پس چه بخشى از مجازات‌هايى را كه به آنها وعده داده‌ايم به تو نشان دهيم، چه تو را از دنيا ببريم، [تفاوتى نمى‌كند آنها] به سوى ما بازگردانده مى‌شوند'),
(4211, 40, 78, 'و مسلما پيش از تو رسولانى را روانه كرديم كه سرگذشت برخى از آنها را بر تو حكايت كرده‌ايم و سرگذشت برخى از ايشان را بر تو حكايت نكرده‌ايم. و هيچ رسولى را نرسد كه جز به اذن خدا معجزه‌اى بياورد. پس چون فرمان خدا [براى مجازات آنها] برسد به حق داورى مى‌شو'),
(4212, 40, 79, 'خداست كسى كه چهارپايان را براى شما پديد آورد تا از برخى از آنها سوارى بگيريد و از برخى‌شان تغذيه كنيد'),
(4213, 40, 80, 'و در آنها براى شما منافعى است و مى‌توانيد با آنها به حاجتى كه در دل داريد برسيد، و بر آنها و بر كشتى سوار مى‌شويد'),
(4214, 40, 81, 'و نشانه‌هاى [قدرت‌] خويش را به شما مى‌نماياند. پس كدام يك از آيات خدا را انكار مى‌كنيد'),
(4215, 40, 82, 'آيا در زمين سير نكردند تا ببينند فرجام كسانى كه پيش از آنان بودند چه شد؟ آنها [از اهل مكّه‌] زيادتر و در روى زمين نيرومندتر و پر اثرتر بودند، ولى آنچه [از امكانات‌] به دست آورده بودند بى‌نيازشان نكرد'),
(4216, 40, 83, 'و چون پيامبرانشان دلايل آشكار برايشان آوردند، به دانشى كه نزدشان بود باليدند و [سر انجام‌] آنچه [از عذاب‌] به ريشخند مى‌گرفتند آنها را فرا گرفت'),
(4217, 40, 84, 'پس چون سختى [عذاب‌] ما را ديدند، گفتند: تنها به خدا ايمان آورديم و بدانچه با او شريك مى‌كرديم كافر شديم'),
(4218, 40, 85, '[ولى‌] ايمانشان آنها را سودى نبخشيد آن هنگام كه عذاب ما را مشاهده كردند. اين سنّت خداست كه از [ديرباز] در باره‌ى بندگانش چنين جارى شده، و آن جاست كه كافران زيان كرده‌اند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حا، ميم'),
(4220, 41, 2, '[اين كتاب‌] فرو فرستاده از جانب [خداى‌] هستى بخش مهربان است'),
(4221, 41, 3, 'كتابى است كه آيات آن تفصيل داده شده [به شكل‌] قرآنى به زبان عربى براى مردمى كه بدانند'),
(4222, 41, 4, 'بشارتگر و هشدار دهنده است. ولى بيشتر آنان رويگردان شدند، از اين رو [چيزى‌] نمى‌شنوند'),
(4223, 41, 5, 'و گفتند: دل‌هاى ما از آنچه ما را به آن مى‌خوانى سخت محجوب است، و در گوش‌هاى ما سنگينى و ميان ما و تو حجابى است. پس تو به كار خود پرداز ما [هم‌] به كار خود مى‌پردازيم'),
(4224, 41, 6, 'بگو: من تنها بشرى چون شمايم، [جز اين كه‌] به من وحى مى‌شود كه خداى شما خداى يگانه است. پس مستقيما به او توجه كنيد و از او آمرزش بخواهيد، و واى بر مشركان'),
(4225, 41, 7, 'همان‌ها كه زكات نمى‌دهند و ايشان به آخرت كافرند'),
(4226, 41, 8, 'همانا كسانى كه ايمان آورده، كارهاى شايسته كردند، آنان را پاداشى بى‌پايان است'),
(4227, 41, 9, 'بگو: آيا شما واقعا به آن كسى كه زمين را در دو روز آفريد، كافر مى‌شويد و براى او همتايانى قرار مى‌دهيد؟ او پروردگار جهانيان است'),
(4228, 41, 10, 'و در زمين كوه‌هاى ريشه‌دارى بر روى آن قرار داد و در آن خير و بركت نهاد و رزق و روزى آن را در چهار روز درست به اندازه‌ى نياز تقاضا كنندگان تقدير نمود'),
(4229, 41, 11, 'سپس به آسمان پرداخت در حالى كه [به صورت‌] دودى بود. پس به آن و به زمين گفت: با ميل يا اجبار بياييد. گفتند: فرمانبردارانه آمديم'),
(4230, 41, 12, 'پس آنها را در دو روز [يا دوره‌] هفت آسمان كرد و در هر آسمانى كار [مربوط به‌] آن را وحى كرد، و آسمان نزديك‌تر را به چراغ‌ها آذين كرديم و حفاظت [نموديم‌]. اين تدبير آن نيرومند داناست'),
(4231, 41, 13, 'پس اگر روى برتافتند بگو: شما را از صاعقه‌اى مانند صاعقه عاد و ثمود هشدار دادم'),
(4232, 41, 14, 'چون فرستادگان [ما] از پيش رو و از پشت سرشان به سراغشان آمدند [و گفتند] كه جز خدا را مپرستيد. گفتند: اگر پروردگار ما مى‌خواست [پيامبر بفرستد]، قطعا فرشتگانى مى‌فرستاد، پس ما به آنچه بدان مبعوث شده‌ايد كافريم'),
(4233, 41, 15, 'و امّا عاديان، به ناحق، در زمين سر برافراشتند و گفتند: چه كسى از ما نيرومندتر است؟ آيا ندانسته‌اند كه آن خدايى كه خلقشان كرده خود از ايشان نيرومندتر است؟ و [به خاطر اين احساس قدرت‌] آيات ما را انكار مى‌كردند'),
(4234, 41, 16, 'پس بر آنها تندبادى غرّنده در روزهايى شوم فرستاديم تا در زندگى دنيا عذاب رسوايى را بدانان بچشانيم، و قطعا عذاب آخرت رسوا كننده‌تر است و ايشان يارى نمى‌شوند'),
(4235, 41, 17, 'و امّا ثموديان، پس آنها را هدايت كرديم، ولى كورى را بر هدايت برگزيدند، پس به خاطر آنچه مرتكب مى‌شدند صاعقه‌ى عذاب خفّت‌بار آنها را در گرفت'),
(4236, 41, 18, 'و كسانى را كه ايمان آورده و پرهيزكارى مى‌كردند نجات داديم'),
(4237, 41, 19, 'و روزى كه دشمنان خدا به سوى آتش گردآورى و سپس يكجا بازداشت مى‌شوند'),
(4238, 41, 20, 'تا زمانى كه نزد آن [آتش‌] بيايند، گوششان و ديدگانشان و پوستشان به آنچه مى‌كرده‌اند بر ضدّشان گواهى دهند'),
(4239, 41, 21, 'و به پوست [بدن‌] خود مى‌گويند: چرا بر ضدّ ما شهادت داديد؟ گويند: همان خدايى كه هر چيز را به سخن مى‌آورد، ما را به سخن آورده و او نخستين بار شما را [كه چيزى نبوديد] آفريد و به سوى او برگردانده مى‌شويد'),
(4240, 41, 22, 'شما كه [گناهانتان را] پنهان مى‌كرديد به خاطر اين نبود كه از شهادت گوش و چشم و پوست تن خود بيم داشتيد، بلكه خيال مى‌كرديد كه خدا از بسيارى از كارهايى كه مى‌كنيد آگاه نمى‌شود'),
(4241, 41, 23, 'و اين گمان بدى بود كه در باره‌ى پروردگارتان داشتيد شما را هلاك نمود و از زيانكاران شديد'),
(4242, 41, 24, 'پس اگر صبر كنند [يا نكنند]، آتش جايگاه آنهاست، و اگر رضاى خدا را بخواهند [و پوزش بطلبند] مورد رضايت قرار نمى‌گيرند'),
(4243, 41, 25, 'و براى آنها همدمانى گماشتيم كه آنچه [از لذت‌هاى دنيوى‌] در پيش روى خود و آنچه پشت سرشان [در حال و آينده‌] داشتند در نظر آنها بياراستند، و بر آنها نيز همان عذابى كه بر سر پيشينيان آنها از جن و انس آمده بود محقّق شد همانا آنها زيانكار بودند'),
(4244, 41, 26, 'و كسانى كه كافر شدند گفتند: به اين قرآن گوش ندهيد و در [تلاوت‌] آن هياهو كنيد تا غلبه يابيد'),
(4245, 41, 27, 'و قطعا كسانى را كه كافر شدند عذابى سخت مى‌چشانيم، و حتما آنها را به بدتر از آنچه مى‌كردند جزا مى‌دهيم'),
(4246, 41, 28, 'آرى، سزاى دشمنان خدا همان آتش است كه در آن منزل هميشگى دارند به سزاى اين كه آيات ما را انكار مى‌كردند'),
(4247, 41, 29, 'و كسانى كه كفر ورزيدند، گويند: پروردگارا! آن دو گروه از جنّ و انس را كه ما را گمراه كردند به ما نشان ده تا آنها را زير پاهايمان نهيم [و لگدمال كنيم‌] تا از پست‌ترين‌ها شوند'),
(4248, 41, 30, 'بى‌گمان، كسانى كه گفتند: پروردگار ما خداست، سپس ايستادگى كردند، فرشتگان بر آنها نازل مى‌شوند كه بيم مداريد و غمگين مباشيد و مژده باد شما را به بهشتى كه وعده داده مى‌شديد'),
(4249, 41, 31, 'ما در زندگى دنيا و در آخرت مددكاران و دوستان شماييم، و هر چه دلتان بخواهد، در [بهشت‌] براى شما فراهم است، و هر چه در آن جا خواستار باشيد خواهيد داشت'),
(4250, 41, 32, 'سفره‌اى از جانب خداى آمرزنده‌ى مهربان است'),
(4251, 41, 33, 'و كيست خوش گفتارتر از آن كس كه به سوى خدا دعوت نمايد و كار نيك كند و گويد: من از مسلمانانم'),
(4252, 41, 34, 'و نيكى با بدى يكسان نيست. [بدى را] با آنچه نيكوتر است دفع كن كه ناگاه [خواهى ديد] همان كسى كه ميان تو و او دشمنى بود، چون دوستى صميمى گشته است'),
(4253, 41, 35, 'و اين [خصلت خوب‌] جز به كسانى كه شكيبايند عطا نشود و آن را جز كسى كه داراى بهره‌ى بزرگ [از ايمان و تقوا] است به دست نياورد'),
(4254, 41, 36, 'و اگر از جانب شيطان دستخوش وسوسه‌اى شدى، به خدا پناه ببر كه او خود شنواى داناست'),
(4255, 41, 37, 'و از آيات خدا شب و روز و خورشيد و ماه است. نه براى خورشيد سجده كنيد نه براى ماه، و خدا را سجده كنيد كه آنها را خلق كرده، اگر تنها او را مى‌پرستيد'),
(4256, 41, 38, 'پس اگر [از عبادت خدا] تكبر كردند، [در عوض‌] كسانى كه در پيشگاه پروردگار تواند (فرشتگان و اوليا) شبانه‌روز او را نيايش مى‌كنند و خسته نمى‌شوند'),
(4257, 41, 39, 'و از [ديگر] نشانه‌هاى او اين است كه تو زمين را افسرده مى‌بينى، و چون باران بر آن فرو فرستيم به جنبش در آيد و گياه آورد. آرى كسى كه آن را زنده كرده قطعا زنده كننده‌ى مردگان است. بى‌ترديد او بر هر چيزى تواناست'),
(4258, 41, 40, 'همانا كسانى كه در آيات ما به انحراف مى‌روند بر ما پوشيده نيستند. آيا كسى كه در آتش افكنده مى‌شود بهتر است يا كسى كه روز قيامت آسوده خاطر مى‌آيد؟ هر چه مى‌خواهيد بكنيد كه او به آنچه مى‌كنيد بيناست'),
(4259, 41, 41, 'البتّه كسانى كه به اين قرآن، وقتى كه برايشان آمد كافر شدند [نيز بر ما پوشيده نيستند]، و قطعا اين كتابى شكست ناپذير است'),
(4260, 41, 42, 'كه هيچ گونه باطلى نه از پيش رو و نه از پشت سر [نه در حال و نه در آينده‌] به آن راه ندارد، [چرا كه‌] از جانب حكيمى ستوده نازل شده است'),
(4261, 41, 43, 'به تو گفته نمى‌شود مگر آنچه به پيامبران پيش از تو گفته شد. به راستى پروردگار تو، هم داراى مغفرت و هم صاحب عقوبتى دردناك است'),
(4262, 41, 44, 'و اگر [اين كتاب را] قرآنى غير عربى كرده بوديم، قطعا مى‌گفتند: چرا آيه‌هاى آن روشن بيان نشده است؟ آيا [كتاب‌] عجمى و [پيامبرى‌] عربى؟ بگو: اين [كتاب‌] براى كسانى كه ايمان آورده‌اند هدايت و درمان است، و كسانى كه ايمان نمى‌آورند در گوش‌هايشان سنگينى اس'),
(4263, 41, 45, 'و به راستى به موسى كتاب [تورات‌] را داديم، پس در آن اختلاف واقع شد. و اگر از جانب پروردگارت سخنى [از مهلت بخشى‌] نگذشته بود، قطعا ميان آنها حكم [به هلاكت‌] مى‌شد، و مسلما آنها در باره‌ى آن دچار شكى سخت هستند'),
(4264, 41, 46, 'هر كه كار شايسته كند به سود اوست، و هر كه بدى كند به زيان اوست و پروردگار تو نسبت به بندگان ستمكار نيست'),
(4265, 41, 47, 'علم قيامت تنها به او باز مى‌گردد، و ميوه‌ها از غلاف‌هايشان بيرون نمى‌آيند و هيچ ماده‌اى باردار نمى‌شود و نمى‌زايد مگر با علم و آگاهى او. و روزى كه آنها را ندا دهد: شريكان من [كه مى‌پنداشتيد] كجايند؟ گويند: به تو اعلام مى‌كنيم كه از ما هيچ كس [بر شري'),
(4266, 41, 48, 'و آنچه پيش از اين [به خدايى‌] مى‌خواندند، از ايشان ناپديد مى‌شود، و در مى‌يابند كه آنها را هيچ راه فرارى نيست'),
(4267, 41, 49, 'انسان از درخواست خير خسته نمى‌شود، و اگر بدى به او رسد، بسيار مأيوس و دلسرد مى‌گردد'),
(4268, 41, 50, 'و اگر پس از رنجى كه به او رسيده، از جانب خود رحمتى به او بچشانيم، قطعا خواهد گفت: اين حق من است، و نپندارم كه رستاخيز برپا شود و اگر به سوى پروردگارم بازگردانده شوم، حتما [منزلتى‌] خوش‌تر نزد او برايم خواهد بود. پس بدون شك كافران را از آنچه انجام داد'),
(4269, 41, 51, 'و هرگاه به انسان نعمت ارزانى داشتيم، روى برتافت و خود را [از بندگى خدا] كنار كشيد، و هرگاه آسيبى بدو رسيد دعاى دور و درازى داشت'),
(4270, 41, 52, 'بگو: به من خبر دهيد، اگر [قرآن‌] از نزد خدا باشد و آن‌گاه شما آن را انكار كنيد، كيست گمراه‌تر از آن كه در مخالفت سختى [با آن‌] است'),
(4271, 41, 53, 'زودا كه آيات قدرت خود را در اطراف [جهان‌] و در جانشان به آنها بنماييم، تا برايشان روشن گردد كه او حق است. آيا كافى نيست كه پروردگار تو بر همه چيز شاهد است'),
(4272, 41, 54, 'آگاه باش كه آنها از ديدار پروردگارشان در ترديدند. آگاه باش كه مسلما او به همه چيز احاطه دارد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حا، ميم'),
(4274, 42, 2, 'عين، سين، قاف'),
(4275, 42, 3, 'اين گونه خداوند مقتدر حكيم به تو و به كسانى كه پيش از تو بودند وحى مى‌كند'),
(4276, 42, 4, 'آنچه در آسمان‌ها و آنچه در زمين است از آن اوست، و او بلند مرتبه و بزرگ است'),
(4277, 42, 5, 'نزديك است آسمان‌ها [به خاطر عظمت وحى‌] از فراز يكديگر بشكافند و فرشتگان به حمد پروردگارشان تسبيح مى‌گويند و براى كسانى كه در زمينند آمرزش مى‌طلبند. آگاه باش كه بى‌شك خداست كه آمرزنده‌ى مهربان است'),
(4278, 42, 6, 'و خدا مراقب كسانى است كه جز او را ولى و سرپرست گرفتند و تو بر آنها و كيل نيستى'),
(4279, 42, 7, 'و بدين سان قرآن عربى را به تو وحى كرديم تا اهل مكه و هر كه را پيرامون آن است بيم دهى و از روز گرد آمدن [خلق‌] كه ترديدى در آن نيست بترسانى كه گروهى در بهشت و گروهى در دوزخند'),
(4280, 42, 8, 'و اگر خدا مى‌خواست، قطعا آنها را يك امت قرار مى‌داد [و همه را به اجبار هدايت مى‌كرد] ليكن او [آنها را مختار ساخت تا] هر كه را بخواهد به رحمت خويش درآورد، و ستمگران را هيچ ولىّ و ياورى نيست'),
(4281, 42, 9, 'آيا به جاى او سرپرستانى براى خود گرفته‌اند؟ خداست كه ولىّ [حقيقى‌] است، و اوست كه مردگان را زنده مى‌كند و هموست كه بر هر چيزى تواناست'),
(4282, 42, 10, 'و در باره‌ى هر چه اختلاف كرديد، حكمش با خداست. اين خداى يكتا است كه پروردگار من است بر او توكل كرده‌ام و به او روى مى‌آورم'),
(4283, 42, 11, 'خالق آسمان‌ها و زمين است. از خودتان براى شما زوج‌هايى پديد آورد، و از چهارپايان نيز جفت‌ها بيافريد. شما را در اين [زوجيت‌] تكثير مى‌كند. چيزى همانند او نيست، و اوست شنواى بينا'),
(4284, 42, 12, 'كليدهاى آسمان‌ها و زمين از آن اوست. براى هر كس بخواهد روزى را گشايش مى‌دهد يا تنگ مى‌گيرد. همانا او به هر چيزى آگاه است'),
(4285, 42, 13, 'براى شما از [احكام‌] دين همان را تشريع كرد كه به نوح توصيه كرده بود، و [نيز] آنچه را به تو وحى كرديم و آنچه را كه به ابراهيم و موسى و عيسى توصيه نموديم كه دين را برپا داريد و در آن تفرقه نكنيد. بر مشركان آنچه بدان دعوتشان مى‌كنى، گران است [و از توحي'),
(4286, 42, 14, 'و آنها اختلاف نكردند مگر پس از آن كه علم برايشان آمد [آن هم‌] به صرف حسد [و برترى جويى‌] كه ميانشان بود. و اگر سخنى از پروردگارت بر اين نگذشته بود كه تا مدتى معين [مهلت داده شوند] قطعا ميانشان حكم [به هلاك‌] مى‌شد. و كسانى كه بعد از آنها كتاب [تورات‌'),
(4287, 42, 15, 'پس براى اين [دين اسلام‌] دعوت كن، و همان گونه كه دستور يافته‌اى پايدارى كن و از هوس‌هاى آنها پيروى مكن و بگو: [من‌] به هر كتابى كه خدا نازل كرده ايمان آورده‌ام و مأمور شده‌ام ميان شما به عدالت حكم كنم. خداوند [يگانه‌] پروردگار ما و پروردگار شماست. اع'),
(4288, 42, 16, 'و كسانى كه در باره‌ى خدا [و دين او] پس از آن كه [دعوت او] پذيرفته شد مجادله مى‌كنند، حجت آنها نزد پروردگارشان باطل است، و خشمى [از خدا] برايشان است و براى آنها عذابى سخت خواهد بود'),
(4289, 42, 17, 'خدا كسى است كه كتاب و ترازو [ى اعمال و ايمان‌] را به حق نازل كرد، و تو چه مى‌دانى شايد قيامت نزديك باشد'),
(4290, 42, 18, 'كسانى كه به آن ايمان ندارند شتاب در آمدن آن را مى‌خواهند، و كسانى كه ايمان آورده‌اند، از آن در هراسند و مى‌دانند كه آن حق است. آگاه باش كسانى كه در مورد قيامت جدال مى‌كنند سخت در گمراهى [پرت و] دورى هستند'),
(4291, 42, 19, 'خدا نسبت به بندگانش لطف دارد، هر كه را بخواهد روزى مى‌دهد و اوست نيرومند شكست ناپذير'),
(4292, 42, 20, 'هر كه كشت آخرت را بخواهد، براى وى در كشتش مى‌افزاييم و كسى كه تنها كشت اين دنيا را طلب كند به او از آن مى‌دهيم ولى در آخرت او را هيچ نصيبى نيست'),
(4293, 42, 21, 'آيا براى آنها معبودهايى است كه برايشان از دين چيزى را كه خدا بدان رخصت نداده تشريع كرده‌اند؟ و اگر نبود آن سخن قطعى [مهلت‌] مسلما ميان آنها [به هلاكت‌] حكم مى‌شد و البته براى ظالمان عذابى دردناك است'),
(4294, 42, 22, '[آن روز] ظالمان را مى‌بينى كه از آنچه كسب كرده‌اند بيمناكند و همان بر آنها واقع مى‌شود، و كسانى كه ايمان آورده و كارهاى شايسته كرده‌اند، در باغ‌هاى بهشتند. براى آنها هر چه بخواهند نزد پروردگارشان موجود است. اين همان تفضل بزرگ است'),
(4295, 42, 23, 'اين همان [پاداشى‌] است كه خدا بندگان خود را كه ايمان آورده و كارهاى شايسته كرده‌اند [بدان‌] مژده مى‌دهد. بگو: من بر اين [رسالت‌] مزدى از شما نمى‌خواهم مگر محبت و دوستى خويشان [و اهل بيت‌] را. و هر كس نيكى كسب كند براى او در آن نيكى مى‌افزاييم. بى‌ترد'),
(4296, 42, 24, 'آيا مى‌گويند بر خدا دروغ بسته؟ پس اگر خدا بخواهد بر دلت مهر مى‌نهد [تا نتوانى خلاف بگويى‌]، و خدا باطل را محو و حق را با سخنان خويش استوار مى‌گرداند. بى‌ترديد او به راز دل‌ها آگاه است'),
(4297, 42, 25, 'و او كسى است كه توبه را از بندگان خود مى‌پذيرد و از بدى‌ها در مى‌گذرد و هر چه مى‌كنيد مى‌داند'),
(4298, 42, 26, 'و [دعاى‌] كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند اجابت مى‌كند و از فضل خويش به آنها زياده مى‌دهد، ولى كافران را عذابى سخت خواهد بود'),
(4299, 42, 27, 'و اگر خدا روزى را براى بندگانش وسعت مى‌داد، مسلما در زمين سر به عصيان بر مى‌داشتند، و ليكن به اندازه‌اى كه مى‌خواهد نازل مى‌كند. به راستى او به [حال‌] بندگانش آگاه و بيناست'),
(4300, 42, 28, 'و اوست خدايى كه پس از نوميدى‌شان باران مى‌فرستد و رحمت خويش را [بر همه‌جا] مى‌گسترد، و سرپرست ستوده اوست'),
(4301, 42, 29, 'و از نشانه‌هاى [قدرت‌] او آفرينش آسمان‌ها و زمين و آنچه از [انواع‌] جنبندگان در ميان آن دو پخش نموده است، و او هرگاه بخواهد برگردآوردن آنها قادر است'),
(4302, 42, 30, 'و هر مصيبتى به شما برسد به سبب دستاورد خود شماست و [خدا] از بسيارى [گناهان‌] در مى‌گذرد'),
(4303, 42, 31, 'و شما در زمين عاجز كننده [ى خدا] نيستيد و جز خدا شما را سرپرست و ياورى نيست'),
(4304, 42, 32, 'و از نشانه‌هاى [قدرت‌] او كشتى‌هايى است چون كوه‌ها كه در دريا روانند'),
(4305, 42, 33, 'اگر بخواهد باد را ساكن مى‌گرداند تا [كشتى‌ها] بر روى آب متوقف بمانند. قطعا در اين [امر] صابران شكر پيشه را عبرت‌هاست'),
(4306, 42, 34, 'يا آنها را به سزاى [برخى از] آنچه مرتكب شده‌اند هلاكشان مى‌كند و از بسيارى در مى‌گذرد'),
(4307, 42, 35, 'و تا آنان كه در آيات ما مجادله مى‌كنند بدانند كه ايشان را هيچ گريزى نيست'),
(4308, 42, 36, 'و آنچه به شما داده شده متاع زندگى دنياست، و آنچه نزد خداست براى آنها كه ايمان آورده‌اند و بر خداى خود توكل مى‌كنند بهتر و پايدارتر است'),
(4309, 42, 37, 'و [و نيز براى‌] كسانى كه از گناهان بزرگ و زشتكارى‌ها اجتناب مى‌كنند و چون به خشم آمدند در مى‌گذرند'),
(4310, 42, 38, 'و كسانى كه دعوت پروردگارشان را اجابت كردند و نماز برپا داشتند و كارشان در ميانشان [بر پايه‌] مشورت است و از آنچه روزيشان كرده‌ايم انفاق مى‌كنند'),
(4311, 42, 39, 'و آنان كه چون ستمى به آنها رسد [تسليم ظالم نمى‌شوند و بر ضد آنها] يارى مى‌جويند'),
(4312, 42, 40, 'و سزاى بدى، بدى همانند آن است. پس هر كه در گذرد و اصلاح كند، پاداش او بر خداست. همانا ظالمان را دوست نمى‌دارد'),
(4313, 42, 41, 'و البته هر كه بعد از مظلوم شدنش يارى جويد [و انتقام گيرد] چنين كسانى هيچ راه [ملامتى‌] بر ايشان نيست'),
(4314, 42, 42, 'راه [ملامت‌] تنها بر كسانى است كه به مردم ظلم مى‌كنند و در زمين به ناحق سر به طغيان بر مى‌دارند، اينانند كه برايشان عذاب دردناكى است'),
(4315, 42, 43, 'و البته هر كه صبورى و گذشت پيشه كرد، مسلما اين از كارهاى استوار و مهم است'),
(4316, 42, 44, 'و هر كه را خدا بى‌راه گذارد، از آن پس او را غير از خدا يار و ياورى نخواهد بود. و ظالمان را مى‌بينى كه چون عذاب را ببينند گويند: آيا راهى براى بازگشتن [به دنيا] وجود دارد'),
(4317, 42, 45, 'و آنان را مى‌بينى كه بر آن [آتش‌] عرضه مى‌شوند، در حالى كه از ذلت و زبونى فروتن شده و زير چشمى مى‌نگرند، و كسانى كه ايمان آورده‌اند گويند: همانا زيانكاران [واقعى‌] كسانى هستند كه روز قيامت خويشتن و كسان خود را دچار زيان كردند. آگاه باش كه ظالمان [آن'),
(4318, 42, 46, 'و براى آنها دوستان و ياورانى نخواهد بود كه ايشان را در مقابل خدا يارى كنند. و هر كه را خدا بى‌راه گذارد، هيچ راه [نجاتى‌] براى او نخواهد بود'),
(4319, 42, 47, '[دعوت‌] پروردگارتان را اجابت كنيد پيش از آن كه روزى فرا رسد كه از جانب خدا بازگشتى براى [تحقق‌] آن نيست. آن روز براى شما نه پناهگاهى است و نه برايتان [مجال‌] انكار و دفاعى'),
(4320, 42, 48, 'پس اگر روى برتافتند، ما تو را نفرستاديم كه نگهبانشان باشى. بر تو جز تبليغ [رسالت، چيزى‌] نيست. و به راستى ما چون به انسان رحمتى از جانب خود بچشانيم، بدان شاد [و سرمست‌] گردد، و اگر به سزاى كارى كه پيش فرستاده‌اند بديشان گزندى رسد [اين جاست كه‌] انسا'),
(4321, 42, 49, 'فرمانروايى آسمان‌ها و زمين از آن اوست، هر چه بخواهد مى‌آفريند، به هر كه خواهد دخترانى مى‌بخشد و هر كه را خواهد پسرانى مى‌دهد'),
(4322, 42, 50, 'يا [اگر بخواهد] آنها را پسران و دختران [هر دو را] مى‌دهد، و هر كه را بخواهد نازا مى‌كند. به راستى او داناى تواناست'),
(4323, 42, 51, 'و هيچ بشرى را نرسد كه خدا با او سخن بگويد جز [از راه‌] وحى يا از فراسوى حجابى، يا رسولى بفرستد كه به اذن او هر چه بخواهد وحى كند. همانا او والا مقام حكيم است'),
(4324, 42, 52, 'و همين گونه ما روحى از امر خود را [به صورت قرآن‌] به تو وحى كرديم، [و گر نه‌] تو نمى‌دانستى كتاب چيست و ايمان كدام است؟ ولى ما آن را نورى ساختيم كه هر يك از بندگان خود را كه بخواهيم به وسيله آن هدايت مى‌كنيم، و بى‌ترديد تو [مردم را] به راه راست هداي'),
(4325, 42, 53, 'راه خدايى كه آنچه در آسمان‌ها و آنچه در زمين است از آن اوست. آگاه باش كه كارها به خدا باز مى‌گردد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حا، ميم'),
(4327, 43, 2, 'سوگند به [اين‌] كتاب روشنگر'),
(4328, 43, 3, 'همانا ما آن را قرآنى عربى كرديم، باشد كه تعقل كنيد'),
(4329, 43, 4, 'و همانا آن در امّ الكتاب (لوح محفوظ) نزد ما تحقيقا متعالى و پرحكمت است'),
(4330, 43, 5, 'آيا به سبب اين كه شما قومى گزافكاريد، قرآن را از شما باز مى‌گيريم [و از هدايت شما منصرف شويم؟'),
(4331, 43, 6, 'و چه بسيار پيامبرانى كه در ميان گذشتگان فرستاديم'),
(4332, 43, 7, 'و هيچ پيامبرى سوى ايشان نمى‌آمد مگر اين كه او را استهزا مى‌كردند'),
(4333, 43, 8, 'پس نيرومندتر از آنها را به هلاكت رسانديم و داستان [هلاكت‌] پيشينيان گذشت'),
(4334, 43, 9, 'و اگر از آنها بپرسى: آسمان‌ها و زمين را چه كسى آفريد؟ قطعا مى‌گويند: آنها را آن قادر آگاه آفريد'),
(4335, 43, 10, 'آن كسى كه زمين را بسترى براى شما گردانيد و براى شما در آن راه‌ها پديد آورد تا شما راه يابيد'),
(4336, 43, 11, 'و آن كسى كه از آسمان آبى به اندازه فرستاد پس به وسيله آن سرزمين مرده را زنده كرديم، به همين صورت [از قبرها] بيرون آورده مى‌شويد'),
(4337, 43, 12, 'خدايى كه همه جفت‌ها [حيوانات و غيره‌] را آفريد و براى شما كشتى‌ها و چهارپايان را كه بر آنها سوار مى‌شويد پديد آورد'),
(4338, 43, 13, 'تا بر پشت آنها قرار گيريد، پس چون بر آنها برنشستيد، نعمت پروردگار خود را ياد كنيد و بگوييد: پاك است خدايى كه اينها را مسخر ما كرد و [گر نه‌] ما را توان [تسخير] آنها نبود'),
(4339, 43, 14, 'و بى‌ترديد ما به سوى پروردگارمان باز خواهيم گشت'),
(4340, 43, 15, 'و براى او از ميان بندگانش جزئى [چون فرزند] قرار دادند. به راستى كه انسان آشكارا ناسپاس است'),
(4341, 43, 16, 'آيا از آنچه مى‌آفريند، خود دختران را برگرفته و شما را به [داشتن‌] پسران برگزيده است'),
(4342, 43, 17, 'ولى هنگامى كه به يكى از آنها مژده‌ى [تولد] همان چيزى داده شود كه به خداى رحمان نسبت داده، چهره‌اش سياه مى‌گردد در حالى كه خشم و تأسف خود را فرو مى‌خورد'),
(4343, 43, 18, 'آيا آن كسى كه در [لابه‌لاى‌] پيرايه و زيور پرورش مى‌يابد (دختر) و به هنگام جدال قدرت تبيين ندارد [فرزند خداست‌]'),
(4344, 43, 19, 'و فرشتگانى را كه بندگان رحمانند، مؤنث پنداشتند. آيا در آفرينش آنها حضور داشته‌اند؟ زودا كه گواهى ايشان نوشته شود و بازخواست شوند'),
(4345, 43, 20, 'و گفتند: اگر خداى رحمان مى‌خواست، آنها را نمى‌پرستيديم. آنان به اين [سخن‌] علم ندارند و جز دروغ نمى‌گويند'),
(4346, 43, 21, 'آيا [ما] پيش از اين كتابى به آنها داده‌ايم، كه بدان تمسك مى‌جويند'),
(4347, 43, 22, 'بلكه گفتند: ما پدران خود را بر آيينى يافته‌ايم و ما بر آثار ايشان هدايت شده‌ايم'),
(4348, 43, 23, 'و همچنين پيش از تو در هيچ شهرى هشدار دهنده‌اى نفرستاديم مگر آن كه خوشگذران‌هاى آن گفتند: ما پدران خود را بر آيينى يافته‌ايم و ما به آثار آنها اقتدا مى‌كنيم'),
(4349, 43, 24, '[پيامبرشان‌] گفت: حتى اگر براى شما چيزى بياورم كه از آنچه پدران خود را بر آن يافته‌ايد، هدايت كننده‌تر باشد [باز هم از آنها پيروى مى‌كنيد] گفتند: ما [نسبت‌] به آنچه بدان فرستاده شده‌ايد كافريم'),
(4350, 43, 25, 'پس، از آنها انتقام گرفتيم پس بنگر كه عاقبت تكذيب كنندگان چگونه شد'),
(4351, 43, 26, 'و [ياد كن‌] هنگامى را كه ابراهيم به پدر و قوم خود گفت: من واقعا از آنچه مى‌پرستيد بيزارم'),
(4352, 43, 27, 'مگر آن كس كه مرا آفريد كه البته او به زودى مرا هدايت خواهد كرد'),
(4353, 43, 28, 'و اين [سخن يكتاپرستى‌] را در نسل او سخنى ماندگار قرار داد، باشد كه [به سوى خدا] بازگردند'),
(4354, 43, 29, 'بلكه اينان و پدرانشان را برخوردار كردم تا اين كه حق و فرستاده‌اى روشنگر به سويشان آمد'),
(4355, 43, 30, 'و چون حق به سويشان آمد گفتند: اين افسون است و ما بدان كافريم'),
(4356, 43, 31, 'و گفتند: چرا اين قرآن بر مردى بزرگ از آن دو قريه [مكه و طائف‌] نازل نشده است'),
(4357, 43, 32, 'مگر رحمت پروردگار تو را آنها تقسيم مى‌كنند؟ اين ماييم كه معيشت آنها را در زندگى دنيا ميانشان تقسيم كرده‌ايم، و بعضى از آنها را بر بعضى مراتبى برترى داديم تا يكديگر را به خدمت گيرند [و چرخه زندگى بگردد]، و رحمت پروردگار تو از آنچه مى‌اندوزند بهتر است'),
(4358, 43, 33, 'و اگر بيم آن نبود كه مردم [در انكار خدا] يك امت گردند، قطعا سقف خانه‌هاى كسانى را كه به [خداى‌] رحمان كافر مى‌شوند، از نقره مى‌كرديم و بر آنها نردبان‌ها مى‌نهاديم كه از آن بالا روند'),
(4359, 43, 34, 'و براى خانه‌هايشان نيز درها و تخت‌هايى [سيمين مى‌ساختيم‌] كه بر آنها تكيه زنند'),
(4360, 43, 35, 'و زر و زيورهايى [ديگر نيز]، ولى اينها همه جز كالاى زندگى دنيا نيست، و آخرت نزد پروردگار تو از آن پرهيزكاران است'),
(4361, 43, 36, 'و هر كه از ياد [خداى‌] رحمان دل بگرداند، شيطانى بر او مى‌گماريم كه همدم او گردد'),
(4362, 43, 37, 'و مسلما آن [شيطان‌] ها ايشان را از راه باز مى‌دارند ولى آنها مى‌پندارند كه هدايت يافتگانند'),
(4363, 43, 38, 'تا آن‌گاه كه او [با همدم خود] به حضور ما آيد، [به شيطان خود] گويد: اى كاش ميان من و تو فاصله مشرق و مغرب مى‌بود. پس چه بد همنشينى بودى'),
(4364, 43, 39, 'و چون ستم كرديد، اين كه شما امروز در عذاب مشتركيد، شما را هرگز سودى نخواهد بخشيد'),
(4365, 43, 40, 'پس آيا تو مى‌توانى كران را شنوا كنى يا كوردلان و كسانى را كه در ضلالتى آشكارند هدايت كنى'),
(4366, 43, 41, 'پس اگر ما تو را [از دنيا] ببريم، قطعا از آنها انتقام مى‌گيريم'),
(4367, 43, 42, 'يا آنچه را به ايشان وعده داده‌ايم به تو نشان مى‌دهيم، چرا كه ما بر آنان تواناييم'),
(4368, 43, 43, 'پس به آنچه به تو وحى شده تمسك جوى كه تو بر راهى راست هستى'),
(4369, 43, 44, 'و بى‌گمان اين [قرآن‌] براى تو و براى قوم تو تذكرى است، و به زودى [در مورد آن‌] بازخواست خواهيد شد'),
(4370, 43, 45, 'و از رسولان ما كه پيش از تو فرستاديم بپرس: آيا در برابر [خداى‌] رحمان خدايانى قرار داده‌ايم كه عبادت شوند'),
(4371, 43, 46, 'و به راستى موسى را با آيات خويش به سوى فرعون و بزرگانش فرستاديم، پس گفت: من فرستاده‌ى پروردگار جهانيانم'),
(4372, 43, 47, 'پس چون آيات ما را براى آنها آورد، به ناگاه آنها به آن [آيات از روى تمسخر] مى‌خنديدند'),
(4373, 43, 48, 'و ما هيچ معجزه‌اى به آنها نشان نداديم مگر آن كه از مشابهش عظيم‌تر بود، و آنها را به عذاب گرفتار كرديم تا مگر باز گردند'),
(4374, 43, 49, 'و گفتند: اى افسونگر! پروردگار خود را به آن عهدى كه با تو كرده براى ما بخوان [كه اين عذاب را از ما بردارد] كه ما حتما هدايت پذير خواهيم بود'),
(4375, 43, 50, 'پس هنگامى كه عذاب را از آنها برداشتيم به ناگاه ايشان پيمان مى‌شكستند'),
(4376, 43, 51, 'و فرعون در ميان مردمش بانگ برآورد [و گفت:] اى قوم من! آيا حكومت مصر و اين نهرها كه از زير [كاخ‌هاى‌] من جارى است از آن من نيست؟ آيا نمى‌بينيد'),
(4377, 43, 52, 'بلكه من از اين مردى كه بى‌مقدار است و قادر بر بيان نيست بهترم'),
(4378, 43, 53, 'پس چرا بر او دستبندهاى زرين [از جانب خدا] افكنده نشده يا فرشتگان همراه وى نيامده‌اند'),
(4379, 43, 54, 'پس قوم خود را سبك مغز گردانيد در نتيجه اطاعتش كردند، مسلما آنها مردمى نافرمان بودند'),
(4380, 43, 55, 'و چون ما را به خشم آوردند از آنها انتقام گرفتيم و همگى‌شان را غرق كرديم'),
(4381, 43, 56, 'پس آنها را پيشينيانى [براى كفر و فسق‌] و عبرتى براى آيندگان قرار داديم'),
(4382, 43, 57, 'و هنگامى كه [تولد] پسر مريم [براى قدرت خدا] مثل زده شد، به ناگاه قوم تو از آن سخن [به تمسخر و خنده‌] سرو صدا راه انداختند'),
(4383, 43, 58, 'و گفتند: آيا معبودهاى ما بهترند يا او؟ [اگر معبودهاى ما در آتشند او نيز به دليل معبود واقع شدن در آتش است‌]. اين [مثل‌] را براى تو نزدند مگر از روى ستيزه‌جويى، بلكه آنها مردمى ستيزه جويند'),
(4384, 43, 59, 'او جز بنده‌اى كه موهبتش داديم و او را براى بنى اسرائيل سرمشق و نمونه ساختيم نيست'),
(4385, 43, 60, 'و اگر مى‌خواستيم، البته به جاى شما فرشتگانى قرار مى‌داديم كه در زمين جانشين [شما] شوند'),
(4386, 43, 61, 'و همانا وجود او (عيسى) دانشى براى [فهم‌] رستاخيز است، پس زنهار در آن ترديد نكنيد و از من پيروى كنيد كه راه راست اين است'),
(4387, 43, 62, 'و شيطان شما را از راه باز ندارد، چرا كه او دشمن آشكار شماست'),
(4388, 43, 63, 'و هنگامى كه عيسى دلايل روشن [براى آنها] آورد گفت: همانا براى شما حكمت آورده‌ام و [آمده‌ام‌] تا برخى چيزها را كه در آن اختلاف مى‌كنيد برايتان بيان كنم، پس از خدا بترسيد و از من اطاعت كنيد'),
(4389, 43, 64, 'بى‌ترديد خداوند [يكتا]، همو پروردگار من و پروردگار شماست، پس [تنها] او را بپرستيد كه راه راست همين است'),
(4390, 43, 65, 'پس گروه‌ها از ميان خودشان [در باره‌ى مسيح‌] اختلاف كردند. پس واى بر كسانى كه ستم كردند از عذاب روزى دردناك'),
(4391, 43, 66, 'آيا جز رستاخيز را چشم به راهند كه ناگهان به سراغشان آيد در حالى كه بى‌خبرند'),
(4392, 43, 67, 'در آن روز دوستان، بعضى دشمن بعضى ديگرند، مگر پرهيزگاران'),
(4393, 43, 68, 'اى بندگان من! امروز بر شما بيمى نيست و شما غمگين نخواهيد شد'),
(4394, 43, 69, 'همان كسانى كه به آيات ما ايمان آوردند و تسليم بودند'),
(4395, 43, 70, 'شما و همسرانتان شادمانه داخل بهشت شويد'),
(4396, 43, 71, 'كاسه‌هاى زرين و سبوهايى برگرد آنها مى‌گردانند و در آن جا هر چه دلخواه آنهاست و مايه لذت ديدگان است وجود دارد، و شما در آن جا جاودانه خواهيد بود'),
(4397, 43, 72, 'و اين همان بهشتى است كه به [پاداش‌] آنچه مى‌كرديد وارث آن شده‌ايد'),
(4398, 43, 73, 'در آن جا براى شما ميوه‌هاى فراوان است كه از آن مى‌خوريد'),
(4399, 43, 74, 'همانا مجرمان در عذاب جهنم ماندگارند'),
(4400, 43, 75, '[عذاب‌] از آنها كاسته نمى‌شود و ايشان در آن جا نااميدند'),
(4401, 43, 76, 'و ما بر آنها ستم نكرديم بلكه خودشان ستمكار بودند'),
(4402, 43, 77, 'و فرياد برآورند: اى مالك! پروردگارت مى‌بايست جان ما را بستاند. او گويد: شما [همچنان‌] ماندگاريد'),
(4403, 43, 78, 'همانا حقيقت را برايتان آورديم، ليكن بيشتر شما حقيقت را خوش نداشتيد'),
(4404, 43, 79, 'بلكه در كار [مخالفت با حق‌] اصرار ورزيدند، ما [نيز بر كيفرشان‌] اصرار مى‌ورزيم'),
(4405, 43, 80, 'آيا مى‌پندارند كه ما راز آنها و نجوايشان را نمى‌شنويم؟ چرا [مى‌شنويم‌] و رسولان ما نزد آنها مى‌نويسند'),
(4406, 43, 81, 'بگو: اگر [خداى‌] رحمان را فرزندى باشد، خود من نخستين پرستندگانم [و بايد او را بپرستم‌]'),
(4407, 43, 82, 'منزّه است پروردگار آسمان‌ها و زمين، صاحب عرش، از آنچه وصف مى‌كنند'),
(4408, 43, 83, 'پس آنها را رها كن تا به [همان سخنان‌] باطل مشغول باشند و بازى كنند تا آن روزى را كه بدان وعده داده مى‌شوند ديدار كنند'),
(4409, 43, 84, 'و اوست كه در آسمان معبود و در زمين معبود است، و هموست كه حكيم داناست'),
(4410, 43, 85, 'و پرخير و پاينده است كسى كه فرمانروايى آسمان‌ها و زمين و آنچه ميان آنهاست از آن اوست، و علم قيامت نزد اوست و به سوى او بازگردانده مى‌شويد'),
(4411, 43, 86, 'و كسانى را كه به جاى او مى‌خوانند اختيار شفاعت ندارند، مگر كسانى كه از روى علم، به حق گواهى داده [و موحد] باشند'),
(4412, 43, 87, 'و اگر از آنها بپرسى چه كسى آنها را آفريد؟ مسلما خواهند گفت: خدا. پس چگونه [از بندگى او] بازگردانده مى‌شوند'),
(4413, 43, 88, 'و گفته پيامبر [اين است:] اى پروردگار من! اينها گروهى هستند كه ايمان نمى‌آورند'),
(4414, 43, 89, '[و گفته شود:] پس از آنها صرف نظر كن و بگو: به سلامت! پس زودا كه بدانند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حا، ميم'),
(4416, 44, 2, 'سوگند به [اين‌] كتاب روشنگر'),
(4417, 44, 3, 'همانا ما آن را در شبى مبارك نازل كرديم، كه ما [همواره‌] بيم دهنده بوده‌ايم'),
(4418, 44, 4, 'در آن [شب‌] هر كار حكيمانه‌اى از هم جدا و تدبير مى‌گردد'),
(4419, 44, 5, 'امرى از جانب ماست، همانا ما فرستنده‌ى [پيامبران‌] بوده‌ايم'),
(4420, 44, 6, '[اينها] به خاطر رحمتى از پروردگار توست كه او شنواى داناست'),
(4421, 44, 7, 'همان پروردگار آسمان‌ها و زمين و آنچه ميان آن دو است، اگر اهل يقين باشيد'),
(4422, 44, 8, 'خدايى جز او نيست، زنده مى‌كند و مى‌ميراند پروردگار شما و پروردگار پدران پيشين شماست'),
(4423, 44, 9, 'ولى آنها در شكّند، و [با حقايق‌] بازى مى‌كنند'),
(4424, 44, 10, 'پس در انتظار روزى باش كه آسمان، دودى نمايان بياورد'),
(4425, 44, 11, 'كه مردم را فرو مى‌پوشد، اين عذابى پردرد است'),
(4426, 44, 12, '[مى‌گويند:] پروردگارا! اين عذاب را از ما دور كن كه ما [اينك‌] مؤمنيم'),
(4427, 44, 13, 'اين پندگيرى كجا به سودشان است و حال آن كه پيامبرى روشنگر براى آنها آمد [و پند نگرفتند]'),
(4428, 44, 14, 'آن‌گاه از او اعراض كردند و گفتند: تعليم يافته‌اى ديوانه است'),
(4429, 44, 15, '[اينك‌] ما عذاب را اندك زمانى برمى‌داريم، [ولى‌] شما [باز به كارهاى خود] باز مى‌گرديد'),
(4430, 44, 16, 'روزى كه با بزرگ‌ترين قدرت [كافران را] بگيريم همانا ما انتقام گيرنده‌ايم'),
(4431, 44, 17, 'و البته پيش از آنها نيز قوم فرعون را بيازموديم و پيامبرى بزرگوار برايشان آمد'),
(4432, 44, 18, 'كه [به آنها گفت:] بندگان خدا را به من تسليم كنيد، همانا من براى شما پيامبرى امينم'),
(4433, 44, 19, 'و اين كه بر خدا گردنكشى نكنيد كه من براى شما حجتى آشكار آورده‌ام'),
(4434, 44, 20, 'و همانا من به پروردگار خود و پروردگار شما پناه مى‌برم از اين كه سنگسارم كنيد'),
(4435, 44, 21, 'و اگر به من ايمان نمى‌آوريد، پس از من كناره گيريد'),
(4436, 44, 22, 'پس پروردگار خود را بخواند كه: اينها مردمى گناهكارند'),
(4437, 44, 23, 'پس [گفتيم:] بندگان مرا شبانه حركت بده، كه شما مورد تعقيب خواهيد بود'),
(4438, 44, 24, 'و دريا را آرام پشت سر بگذار كه آنها سپاهى غرق شدنى هستند'),
(4439, 44, 25, 'چه بسيار باغ‌ها و چشمه‌سارانى كه بر جاى گذاشتند'),
(4440, 44, 26, 'و كشتزارها و جايگاه‌هاى نيكو'),
(4441, 44, 27, 'و نعمتى كه در آن شادكام بودند'),
(4442, 44, 28, 'آرى اين گونه بود، و ما [همه‌] آنها را به قومى ديگر ميراث داديم'),
(4443, 44, 29, 'آن‌گاه نه آسمان به حال آنها گريست و نه زمين، و نه مهلتى به آنها داده شد'),
(4444, 44, 30, 'و به راستى فرزندان اسرائيل را از عذاب خفت‌بار نجات داديم'),
(4445, 44, 31, 'از [ظلم‌] فرعون كه او برترى جويى از اسرافكاران بود'),
(4446, 44, 32, 'و همانا آنها را از روى علم بر مردم جهان برگزيديم'),
(4447, 44, 33, 'و آنها را از آيات و معجزات چيزى عطا كرديم كه در آن امتحانى آشكار بود'),
(4448, 44, 34, 'البته اين [كافران‌] حتما خواهند گفت'),
(4449, 44, 35, 'پايان كار جز همين مرگ نخستين نيست و ما زنده شدنى نيستيم'),
(4450, 44, 36, 'پس پدران ما را باز آوريد اگر راست مى‌گوييد'),
(4451, 44, 37, 'آيا ايشان بهترند يا قوم «تبع» و كسانى كه پيش از آنها بودند؟ آنها را نابود كرديم، [زيرا] آنان مجرم بودند'),
(4452, 44, 38, 'و ما آسمان‌ها و زمين و آنچه را كه ميان آن دو است به بازى نيافريديم'),
(4453, 44, 39, 'ما آنها را جز به حق نيافريديم، ولى بيشترشان نمى‌دانند'),
(4454, 44, 40, 'به يقين، روز جدايى وعده‌گاه همه آنهاست'),
(4455, 44, 41, 'روزى كه نه هيچ دوستى از دوست [خود] مشكلى را دفع مى‌كند و نه ايشان يارى مى‌شوند'),
(4456, 44, 42, 'مگر كسى كه خدا به او رحم كند، همانا او مقتدر مهربان است'),
(4457, 44, 43, 'مسلما درخت زقوم'),
(4458, 44, 44, 'خوراك گناه پيشه است'),
(4459, 44, 45, 'مانند مس گداخته است كه در شكم‌ها مى‌جوشد'),
(4460, 44, 46, 'مانند جوشيدن آب جوشان'),
(4461, 44, 47, '[ندا آيد:] او را بگيريد و به ميان دوزخ بكشانيد'),
(4462, 44, 48, 'آن‌گاه روى سرش از عذاب آب جوشان بريزيد'),
(4463, 44, 49, 'بچش كه تو [به خيال خود] همان نيرومند گرامى هستى'),
(4464, 44, 50, 'البته اين همان است كه به آن شك مى‌كرديد'),
(4465, 44, 51, 'بى‌گمان، پرهيزگاران در جايگاه امنى هستند'),
(4466, 44, 52, 'در باغ‌ها و چشمه‌سارها'),
(4467, 44, 53, 'حرير نازك و حرير ضخيم مى‌پوشند [و] روياروى هم [مى‌نشينند]'),
(4468, 44, 54, '[آرى‌] اين گونه است و حور العين را به همسرى‌شان در مى‌آوريم'),
(4469, 44, 55, 'در آن جا هر ميوه‌اى را [كه بخواهند] آسوده خاطر مى‌طلبند'),
(4470, 44, 56, 'در آن جا مرگ را نخواهند چشيد، جز همان مرگ نخستين را [كه چشيده‌اند]، و آنها را از عذاب دوزخ نگاه مى‌دارد'),
(4471, 44, 57, '[اين‌] تفضلى است از جانب پروردگار تو، اين همان كاميابى بزرگ است'),
(4472, 44, 58, 'پس جز اين نيست كه اين [قرآن‌] را به زبان تو آسان ساختيم، باشد كه پند گيرند'),
(4473, 44, 59, 'پس منتظر باش كه آنها [نيز] منتظرند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حا، ميم'),
(4475, 45, 2, 'فرو فرستادن [اين‌] كتاب از جانب خداى شكست ناپذير حكيم است'),
(4476, 45, 3, 'به راستى در آسمان‌ها و زمين براى مؤمنان نشانه‌هايى [عبرت آموز] هست'),
(4477, 45, 4, 'و در آفرينش خودتان و آنچه از جنبندگان مى‌پراكند براى مردمى كه يقين دارند نشانه‌هاست'),
(4478, 45, 5, 'و در آمد و شد شب و روز و رزقى كه خدا از آسمان نازل كرده و زمين را پس از مرگش بدان زنده ساخته و در گرداندن بادها براى مردمى كه مى‌انديشند نشانه‌هاست'),
(4479, 45, 6, 'اينها آيات الهى است كه آنها را به درستى بر تو مى‌خوانيم، پس بعد از خدا و آيات او به كدام سخن ايمان مى‌آورند'),
(4480, 45, 7, 'واى بر هر دروغزن گناه پيشه'),
(4481, 45, 8, '[كه‌] آيات خدا را كه بر او خوانده مى‌شود مى‌شنود، سپس به گردنكشى پاى مى‌فشارد، چنان كه گويى آن را نشنيده است، پس او را به عذابى دردآور بشارت ده'),
(4482, 45, 9, 'و چون به چيزى از آيات ما آگاه شود آن را به ريشخند مى‌گيرد. اينان عذابى خفت‌بار برايشان خواهد بود'),
(4483, 45, 10, 'از پشت سرشان دوزخ است، و آنچه را كه به دست آورده و آن دوستانى را كه غير از خدا اختيار نموده‌اند به كارشان نمى‌آيد و براى ايشان عذابى بزرگ است'),
(4484, 45, 11, 'اين يك رهنمود است، و كسانى كه آيات پروردگارشان را انكار كردند برايشان عذاب دردناك از شكنجه و اضطراب است'),
(4485, 45, 12, 'خدا كسى است كه دريا را مسخر شما كرد تا كشتى‌ها در آن به فرمان او حركت كنند و تا از فضل او طلب [معيشت‌] كنيد و باشد كه شكر گزاريد'),
(4486, 45, 13, 'و آنچه را در آسمان‌ها و آنچه را در زمين است مسخر شما كرد كه همه از اوست. قطعا در اين [امر] براى مردمى كه مى‌انديشند نشانه‌هاست'),
(4487, 45, 14, 'به كسانى كه ايمان آورده‌اند بگو تا از كسانى كه روزهاى [سخت و ناگوار] الهى را باور ندارند در گذرند، تا [خدا] قومى را بدانچه مى‌كردند جزا دهد'),
(4488, 45, 15, 'هر كه كارى شايسته كند، به سود خود اوست و هر كه بدى كند به زيانش مى‌باشد، سپس به سوى پروردگارتان بازگردانده مى‌شويد'),
(4489, 45, 16, 'ما بنى اسرائيل را كتاب و حكومت و نبوت داديم و از پاكيزه‌ها روزيشان كرديم و آنها را بر جهانيان برترى داديم'),
(4490, 45, 17, 'و دلايل روشنى در امر [دين‌] به آنها عطا كرديم پس ايشان اختلاف نكردند مگر بعد از آن كه علم برايشان آمد [آن هم‌] به خاطر حسد و رقابتى كه ميان خودشان بود. قطعا پروردگار تو روز قيامت ميان آنها در آنچه بر سرش اختلاف مى‌كردند حكم خواهد كرد'),
(4491, 45, 18, 'سپس تو را بر شريعتى كه از امر [خدا] است قرار داديم، پس از آن پيروى كن و از هوس‌هاى كسانى كه نمى‌دانند پيروى مكن'),
(4492, 45, 19, 'آنها هرگز در برابر خدا از تو حمايتى نتوانند كرد، و همانا ستمگران يار و ياور يكديگرند و خدا ياور پرهيزكاران است'),
(4493, 45, 20, 'اين [كتاب مايه‌ى‌] بصيرت‌هايى براى مردم است و براى قومى كه يقين آورند هدايت و رحمتى است'),
(4494, 45, 21, 'آيا كسانى كه مرتكب زشتى‌ها شدند پنداشته‌اند كه آنها را مانند كسانى قرار مى‌دهيم كه ايمان آوردند و كارهاى شايسته كردند [و] حيات و مماتشان يكسان است؟ بد قضاوتى دارند'),
(4495, 45, 22, 'و خدا آسمان‌ها و زمين را به حق آفريد و تا هر كسى به موجب آنچه به دست آورده جزا داده شود و به آنها ستم نمى‌شود'),
(4496, 45, 23, 'پس آيا ديدى كسى را كه هواى خويش را معبود خود قرار داده و خدا او را از روى علم [به اين كه پذيراى هدايت نبود] گمراه ساخته و بر گوش و دلش مهر زده و بر ديده‌اش پرده نهاده است؟ آيا پس از خدا چه كسى او را هدايت خواهد كرد؟ آيا متذكر نمى‌شويد'),
(4497, 45, 24, 'و گفتند: جز زندگى دنيوى ما هيچ نيست مى‌ميريم و زنده مى‌شويم و ما را جز طبيعت هلاك نمى‌كند، آنان را به اين [مطلب‌] هيچ دانشى نيست و جز اين نيست كه گمان مى‌كنند'),
(4498, 45, 25, 'و چون آيات روشن ما بر آنها خوانده شود، دليلشان جز اين نيست كه مى‌گويند: پدران ما را [زنده‌] بياوريد اگر راست مى‌گوييد'),
(4499, 45, 26, 'بگو: خداست كه شما را زنده مى‌كند، سپس مى‌ميراند، آن‌گاه به روز رستاخيز كه ترديدى در آن نيست گرد مى‌آورد، ولى بيشتر مردم [اين را] نمى‌دانند'),
(4500, 45, 27, 'و فرمانروايى آسمان‌ها و زمين از آن خداست، و روزى كه رستاخيز برپا شود، در چنين روزى باطل‌انديشان زيان مى‌كنند'),
(4501, 45, 28, 'و هر امتى را به زانو درآمده مى‌بينى. هر امتى به سوى كتاب خويش خوانده مى‌شود [و گفته شود:] امروز سزاى آنچه را كه مى‌كرديد مى‌بينيد'),
(4502, 45, 29, 'اين نامه ماست كه بر ضد شما به حق سخن مى‌گويد. همانا ما از آنچه مى‌كرديد نسخه بر مى‌داشتيم'),
(4503, 45, 30, 'و اما كسانى كه ايمان آوردند و كارهاى شايسته كردند، پروردگارشان آنها را در [جوار] رحمت خود در مى‌آورد. اين همان كاميابى آشكار است'),
(4504, 45, 31, 'و اما كسانى كه كافر شدند [به آنان گفته شود:] مگر آيات من بر شما خوانده نمى‌شد و گردنكشى كرديد و مردمى گناهكار بوديد'),
(4505, 45, 32, 'و چون گفته مى‌شد كه وعده‌ى خدا راست است و در قيامت شكى نيست، مى‌گفتيد: ما نمى‌دانيم قيامت چيست و [آن را] جز گمانى نمى‌پنداريم و ما به يقين نرسيده‌ايم'),
(4506, 45, 33, 'و [حقيقت‌] بدى‌هايى كه كرده‌اند براى آنها ظاهر مى‌شود و آنچه را كه بدان ريشخند مى‌كردند ايشان را فرا مى‌گيرد'),
(4507, 45, 34, 'و به آنها گفته شود: امروز شما را فراموش مى‌كنيم، همان گونه كه شما ديدار امروزتان را فراموش كرديد، و جايگاه شما آتش است و براى شما هيچ ياورى نخواهد بود'),
(4508, 45, 35, 'اين بدان سبب است كه شما آيات خدا را به ريشخند گرفتيد و زندگى دنيا فريبتان داد پس امروز نه از اين [آتش‌] بيرون آورده شوند و نه عذرشان پذيرفته گردد'),
(4509, 45, 36, 'پس حمد و سپاس از آن خداست كه پروردگار آسمان‌ها و پروردگار زمين و پروردگار جهانيان است'),
(4510, 45, 37, 'و كبريايى و عظمت در آسمان‌ها و زمين، از آن اوست و اوست شكست ناپذير حكيم');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حا، ميم'),
(4512, 46, 2, 'اين كتاب از جانب خداى شكست ناپذير حكيم نازل شده است'),
(4513, 46, 3, '[ما] آسمان‌ها و زمين و آنچه را كه ميان آن دو است جز به حق و تا سر آمدى معين نيافريديم، و كسانى كه كافر شدند، از هشدارهايى كه داده مى‌شوند رو گردانند'),
(4514, 46, 4, 'بگو: به من خبر دهيد، آنچه را غير از خدا مى‌خوانيد، به من نشان دهيد كه چه چيزى از زمين را آفريده‌اند و يا در [خلقت‌] آسمان‌ها شركت داشته‌اند؟ اگر راست مى‌گوييد [دليلى از] كتابى كه پيش از اين [قرآن‌] است يا اثرى علمى [از پيشينيان‌] براى من بياوريد [كه ا'),
(4515, 46, 5, 'و كيست گمراه‌تر از آن كس كه به جاى خدا كسى را مى‌خواند كه تا روز قيامت هم او را پاسخ نخواهد داد و آنها از دعايشان بى‌خبرند'),
(4516, 46, 6, 'و زمانى كه مردم محشور گردند، [اين معبودان‌] دشمنان آنها شوند و عبادتشان را انكار كنند'),
(4517, 46, 7, 'و چون آيات روشن ما بر ايشان تلاوت شود، منكران حق، وقتى حق به سويشان آيد، گويند: اين سحرى آشكار است'),
(4518, 46, 8, 'آيا مى‌گويند: اين [كتاب‌] را به دروغ بافته است؟ بگو: اگر آن را بافته باشم [بايد مجازات شوم و] شما نمى‌توانيد خشم خدا را از من بازداريد. او به آنچه [با طعنه‌] به آن مى‌پردازيد [و جادويش مى‌خوانيد] آگاه‌تر است، و شهادت او ميان من و شما كافى است و اوست آ'),
(4519, 46, 9, 'بگو: من از [ميان‌] پيامبران، پيامبر نوظهورى نيستم، و نمى‌دانم با من و شما چه معامله‌اى خواهد شد. جز آنچه را كه به من وحى مى‌شود پيروى نمى‌كنم، و من جز هشدار دهنده‌اى آشكار نيستم'),
(4520, 46, 10, 'بگو: به من خبر دهيد، اگر اين [قرآن‌] از نزد خدا باشد و شما بدان كافر شويد و شاهدى از بنى اسرائيل [مانند عبد اللّه بن سلام‌] بر [حقانيت‌] مثل آن گواهى دهد و ايمان آورد و شما تكبر كنيد [آيا در اين صورت ستمكار نيستيد؟] البته خدا قوم ستمگر را هدايت نمى‌ك'),
(4521, 46, 11, 'و كافران در حق مؤمنان گفتند: اگر [اين قرآن‌] چيز خوبى بود، به [پذيرفتن‌] آن بر ما پيشى نگرفته بودند، و چون خود به وسيله آن هدايت نيافته‌اند به زودى خواهند گفت: اين يك دروغ كهنه است'),
(4522, 46, 12, 'و [حال آن كه‌] پيش از آن، كتاب موسى راهنما و رحمتى بود، و اين [قرآن‌] كتابى به زبان عربى است كه تصديق كننده [ى آن‌] است، تا كسانى را كه ستم كردند هشدار دهد و نيكوكاران را مژده‌اى باشد'),
(4523, 46, 13, 'محققا كسانى كه گفتند: پروردگار ما خداست و استقامت كردند، نه بيمى بر آنهاست و نه غمگين مى‌شوند'),
(4524, 46, 14, 'ايشان اهل بهشتند و براى هميشه در آن ماندگارند [اين‌] پاداشى است به خاطر آنچه انجام مى‌دادند'),
(4525, 46, 15, 'و انسان را به نيكى كردن به پدر و مادر خود سفارش كرديم. مادرش با تحمل رنج به او باردار شد و با تحمل رنج او را بر زمين نهاد، و [دوران‌] حمل و از شير گرفتن او سى ماه است، تا آن كه به رشد كامل خود برسد و به چهل سال برسد، مى‌گويد: پروردگارا! بر دلم بيفكن'),
(4526, 46, 16, 'آنان كسانى‌اند كه بهترين عملى را كه انجام داده‌اند از ايشان مى‌پذيريم و از بدى‌هايشان در زمره بهشتيان در مى‌گذريم وعده‌ى راستى كه وعده داده مى‌شدند'),
(4527, 46, 17, 'و آن كه به پدر و مادر خود گفت: اف بر شما! آيا به من وعده مى‌دهيد كه [از قبر] بيرون آورده مى‌شوم و حال آن كه پيش از من نسل‌ها گذشتند [و برنخاستند]، و آن دو به [درگاه‌] خدا زارى مى‌كنند كه: واى بر تو! ايمان بياور، وعده‌ى خدا حق است، و او پاسخ مى‌دهد: ا'),
(4528, 46, 18, 'آنان كسانى‌اند كه وعده‌ى عذاب بر آنها در [زمره‌ى‌] گروه‌هايى از جنّ و انس كه پيش از آنها بگذشتند حتمى شد كه بى‌ترديد آنها زيانكار بودند'),
(4529, 46, 19, 'و براى هر يك [از مؤمن و كافر] بر حسب آنچه انجام داده‌اند درجاتى است، و تا [خدا پاداش‌] اعمالشان را تمام بدهد، و آنها مورد ستم واقع نخواهند شد'),
(4530, 46, 20, 'و آن روز كه كافران بر آتش عرضه شوند، [به آنها گفته شود:] نعمت‌هاى پاكيزه [و خوشى‌هاى‌] خود را در زندگى دنيايتان [از بين‌] برديد و از آنها برخوردار شديد، پس امروز به سزاى آن كه در زمين به ناحق سركشى مى‌كرديد و به سبب آن كه نافرمانى مى‌كرديد به عذابى خ'),
(4531, 46, 21, 'و برادر عاديان را به ياد آور، آن‌گاه كه قوم خويش را در [سرزمين‌] احقاف هشدار داد، با آن كه پيش از او و پس از او نيز هشدار دهندگانى بودند كه: جز خدا را مپرستيد. واقعا من بر شما از عذاب روزى هولناك مى‌ترسم'),
(4532, 46, 22, 'گفتند: آيا به سوى ما آمده‌اى كه ما را از خدايانمان برگردانى؟ پس آنچه به ما وعده مى‌دهى بياور اگر راست مى‌گويى'),
(4533, 46, 23, 'گفت: جز اين نيست كه آگاهى [از زمان وقوع عذاب‌] نزد خداست، و من آنچه را بدان فرستاده شده‌ام به شما ابلاغ مى‌كنم، ولى شما را مردمى جاهل مى‌بينم'),
(4534, 46, 24, 'پس چون آن [عذاب‌] را ديدند كه به صورت ابرى به طرف وادى‌هايشان روى آورده، گفتند: اين ابرى است كه بر ما خواهد باريد. [گفته شد: نه،] بلكه اين همان چيزى است كه شتاب در آن را مى‌خواستيد تندبادى است كه در آن عذابى دردناك است'),
(4535, 46, 25, 'همه چيز را به فرمان پروردگارش ريشه كن مى‌كند. پس چنان شدند كه چيزى جز خانه‌هايشان ديده نمى‌شد. [آرى‌]، اين چنين مردم مجرم را سزا مى‌دهيم'),
(4536, 46, 26, 'و البته به آنها در چيزهايى قدرت و تمكّن داده بوديم كه شما را در آن تمكّن نداده‌ايم، و براى آنان شنوايى و ديده‌ها و دل‌هايى [نيرومندتر از شما] قرار داده بوديم، ولى چون آيات الهى را انكار كردند، نه شنوايى‌شان و نه ديدگانشان و نه دل‌هايشان به هيچ وجه ب'),
(4537, 46, 27, 'و بى‌گمان، شهرهايى را كه پيرامون شما بود هلاك كرديم و آيات خود را [به صورت‌هاى‌] گوناگون بيان داشتيم به اميد آن كه برگردند'),
(4538, 46, 28, 'پس چرا كسانى را كه جز خدا براى تقرب، به خدايى گرفته بودند ياريشان نكردند، بلكه از نظرشان ناپديد شدند؟ و اين بود [نتيجه‌ى‌] دروغ آنها و آنچه افترا مى‌بستند'),
(4539, 46, 29, 'و چون تنى چند از جنّيان را به سوى تو روانه كرديم كه قرآن را مى‌شنيدند پس وقتى بر آن حضور يافتند، [به يكديگر] گفتند: ساكت باشيد و بشنويد و چون به پايان رسيد، براى هشدار به سوى قوم خود بازگشتند'),
(4540, 46, 30, 'گفتند: اى قوم ما! ما كتابى را شنيديم كه بعد از موسى نازل شده و [كتاب‌هاى‌] پيش از خود را تصديق مى‌كند و به سوى حق و به راهى راست هدايت مى‌كند'),
(4541, 46, 31, 'اى قوم ما! دعوت كننده‌ى الهى را اجابت كنيد و به او ايمان آوريد تا [خدا] از گناهانتان بگذرد و شما را از عذابى دردناك پناه دهد'),
(4542, 46, 32, 'و هر كه دعوت كننده‌ى الهى را اجابت نكند، عاجز كننده‌ى خدا در زمين نيست و در برابر او هيچ دوست و حمايتگرى نخواهد داشت [و] آنها در گمراهى آشكارند'),
(4543, 46, 33, 'آيا نديدند آن خدايى كه آسمان‌ها و زمين را آفريد و در آفريدن آنها درمانده نشد، قادر است مردگان را زنده كند؟ آرى! او بر هر كارى تواناست'),
(4544, 46, 34, 'و روزى كه كافران بر آتش عرضه شوند [منادى گويد:] آيا اين [آتش‌] حق نيست؟ گويند: چرا، به پروردگارمان سوگند [كه حق است‌]. گويد: پس به سزاى اين كه انكار مى‌كرديد عذاب را بچشيد'),
(4545, 46, 35, 'پس صبر كن همان‌گونه كه پيامبران اولو العزم صبر كردند، و شتاب [در عقوبت‌] آنان مكن. روزى كه آنچه را وعده داده مى‌شوند ببينند، [احساس مى‌كنند كه‌] گويى جز ساعتى از يك روز [در دنيا] نبوده‌اند. [اين‌] ابلاغى است. پس آيا جز مردم نافرمان هلاك مى‌شوند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'كسانى كه كافر شدند و از راه خدا بازداشتند، [خدا] اعمالشان را تباه كرد'),
(4547, 47, 2, 'و آنان كه ايمان آوردند و كارهاى شايسته كردند و به آنچه بر محمد نازل شده- كه آن حق و از جانب پروردگارشان است- ايمان آوردند، [خداوند] بدى‌هايشان را بزدود و كار و بارشان را سامان بخشيد'),
(4548, 47, 3, 'اين بدان سبب است كه آنها كه كافر شدند، از باطل پيروى كردند، و كسانى كه ايمان آوردند از حق كه از جانب پروردگارشان است پيروى نمودند. بدين‌سان خداوند براى مردم وصف حالشان را بيان مى‌كند'),
(4549, 47, 4, 'پس چون با كافران روبه رو شديد [مهلت ندهيد و] گردن‌ها را بزنيد، تا چون آنها را از پاى در آورديد، بندها [ى اسيران‌] را محكم كنيد، آن‌گاه يا به منّت [آزادشان كنيد] يا به فديه، تا جنگ بارهايش را فرو نهد. اين [فرمان خداست‌]، و اگر خدا مى‌خواست خود از ايشا'),
(4550, 47, 5, 'به زودى آنها را [به بهشت‌] هدايت مى‌كند و حالشان را نيكو مى‌گرداند'),
(4551, 47, 6, 'و در بهشتى كه براى آنها معرفى كرده داخلشان مى‌كند'),
(4552, 47, 7, 'اى كسانى كه ايمان آورده‌ايد! اگر خدا را يارى كنيد، ياريتان مى‌كند و گام‌هايتان را استوار مى‌دارد'),
(4553, 47, 8, 'و كسانى كه كافر شدند، مرگ بر آنها، و [خدا] اعمالشان را نابود كرد'),
(4554, 47, 9, 'اين بدان سبب است كه آنها آنچه را خدا نازل كرده است خوش نداشتند و [خدا نيز] كارهايشان را نابود كرد'),
(4555, 47, 10, 'آيا در زمين سير نكردند تا ببينند فرجام كسانى كه پيش از آنها بودند چگونه شد؟ خدا زير و رويشان كرد و كافران را نظاير آن خواهد بود'),
(4556, 47, 11, 'اين بدان سبب است كه خدا دوستدار و مولاى كسانى است كه ايمان آوردند، ولى كافران را مولايى نيست'),
(4557, 47, 12, 'همانا خدا كسانى را كه ايمان آوردند و كارهاى شايسته كردند، به باغ‌هايى داخل مى‌كند كه از پاى درختانش نهرها جارى است. و كسانى كه كافر شدند، [چند صباحى‌] بهره مى‌جويند و مى‌خورند همان گونه كه چهارپايان مى‌خورند، و [سر انجام‌] جايگاهشان آتش است'),
(4558, 47, 13, 'و بسا شهرها كه نيرومندتر از آن شهرى بود كه تو را بيرون كرد كه ما هلاكشان كرديم و براى آنها هيچ ياورى نبود'),
(4559, 47, 14, 'پس آيا كسى كه از جانب پروردگارش بر راه روشنى قرار دارد، مانند كسى است كه زشتى عملش براى او زينت داده شده و از هوس‌هاى خود پيروى كرده‌اند'),
(4560, 47, 15, 'وصف بهشتى كه به پرهيزگاران وعده داده شده [اين است كه‌] در آن نهرهايى است از آبى كه [رنگ و بو و طعمش‌] برنگردد، و نهرهايى از شيرى كه مزه‌اش دگرگون نشود، و نهرهايى از شراب كه براى نوشندگان لذّتى است، و نهرهايى از عسل مصفا. و در آن جا هر گونه محصول [كه'),
(4561, 47, 16, 'و از آنها كسانى هستند كه [در ظاهر] به تو گوش مى‌دهند ولى چون از نزد تو بيرون مى‌روند، به دانش يافتگان مى‌گويند: [اين پيامبر] هم اكنون چه گفت؟ اينان كسانى‌اند كه خدا بر دل‌هايشان مهر نهاده و از هوس‌هاى خود پيروى كرده‌اند'),
(4562, 47, 17, 'و آنها كه هدايت پذير شدند، [خدا] بر هدايتشان افزود و [روح‌] تقوايشان بخشيد'),
(4563, 47, 18, 'آيا [كافران‌] جز اين انتظار دارند كه قيامت به ناگاه بر آنان در رسد؟ اينك نشانه‌هاى آن آمده است. پس آنگاه كه [رستاخيز] بر آنان درآيد، پند گرفتنشان را چه سود'),
(4564, 47, 19, 'پس بدان كه هيچ معبودى جز خدا نيست، و براى گناه خويش و براى مردان و زنان مؤمن آمرزش بخواه، و خداست كه آمد و شد و جايگاه [و وابستگى‌هاى فكرى و عملى‌] شما را مى‌داند'),
(4565, 47, 20, 'و كسانى كه ايمان آورده‌اند مى‌گويند: چرا سوره‌اى [در باره‌ى جهاد] نازل نمى‌شود؟ اما وقتى سوره‌اى صريح نازل گردد و در آن سخن از كارزار رود، بيماردلان را مى‌بينى كه، مانند كسى كه به حال احتضار افتاده به تو مى‌نگرند، پس همان سزاوارشان است'),
(4566, 47, 21, 'فرمان‌پذيرى و سخنى شايسته [بهتر است‌] و هنگامى كه كار [جهاد] قطعى شد، اگر با خدا راست مى‌گفتند برايشان بهتر بود'),
(4567, 47, 22, 'پس [اى منافقان!] اگر روى گردانديد، آيا جز اين انتظار مى‌رود كه در زمين تباهى كنيد و قطع رحم نماييد'),
(4568, 47, 23, 'اينان كسانى هستند كه خدا لعنتشان كرده و گوش‌هايشان را كر و چشم‌هايشان را كور نموده است'),
(4569, 47, 24, 'آيا در قرآن تدبر نمى‌كنند يا بر دل‌ها [يشان‌] قفل‌هاى [عناد] است'),
(4570, 47, 25, 'بى‌گمان، كسانى كه پس از آن كه [راه‌] هدايت براى آنها روشن شد [به حقيقت‌] پشت كردند، شيطان آنها را فريفت و آرزويشان را دراز كرد'),
(4571, 47, 26, 'اين بدان سبب است كه آنها به كسانى كه نزول قرآن از جانب خدا را مكروه داشتند، گفتند: به زودى در برخى كارها از شما اطاعت خواهيم كرد. و خدا همداستانى سرّى آنها را مى‌داند'),
(4572, 47, 27, 'پس چگونه‌اند هنگامى كه فرشتگان جانشان را مى‌گيرند در حالى كه بر صورت‌ها و پشت‌هايشان مى‌كوبند'),
(4573, 47, 28, 'اين بدان سبب است كه آنها از آنچه خدا را به خشم آورده پيروى كردند و از خرسندى‌اش كراهت داشتند پس [خداوند] اعمالشان را نابود كرد'),
(4574, 47, 29, 'آيا كسانى كه در دل‌هايشان بيمارى است، پنداشته‌اند كه خدا هرگز كينه‌هايشان را فاش نخواهد كرد'),
(4575, 47, 30, 'و اگر مى‌خواستيم، قطعا آنها را به تو نشان مى‌داديم و در نتيجه ايشان را به سيمايشان مى‌شناختى، و البته تو آنها را در لحن [و زهر] كلامشان [به راحتى‌] مى‌شناسى، و خدا از [ظاهر و باطن‌] اعمالتان آگاه است'),
(4576, 47, 31, 'و البته شما را مى‌آزماييم تا مجاهدان و صابران شما را بشناسيم و گزارش‌هاى [مربوط به‌] شما را رسيدگى و برملا كنيم'),
(4577, 47, 32, '[و] كسانى كه كافر شدند و [مردم را] از راه خدا بازداشتند و پس از آن كه راه هدايت براى آنها آشكار شد، با رسول [خدا] در افتادند، هرگز به خدا گزندى نمى‌رسانند، و [خدا] به زودى كرده‌هايشان را تباه مى‌كند'),
(4578, 47, 33, 'اى كسانى كه ايمان آورده‌ايد! اطاعت خدا كنيد و اطاعت رسول كنيد و اعمال خود را باطل نسازيد'),
(4579, 47, 34, 'محققا آنان كه كفر ورزيدند و مانع راه خدا شدند، سپس در حال كفر مردند، خدا هرگز آنها را نخواهد بخشيد'),
(4580, 47, 35, 'پس سست نشويد و [كافران را] به آشتى مخوانيد و شما برتريد و خدا با شماست و كارها [و تلاش‌] هاى شما را ناقص نخواهد گذاشت [و پيروزتان خواهد كرد]'),
(4581, 47, 36, 'زندگى دنيا جز بازيچه و بيهودگى نيست، و اگر ايمان بياوريد و تقوا پيشه كنيد [خدا] پاداش شما را مى‌دهد و اموالتان را [به عوض‌] نمى‌خواهد'),
(4582, 47, 37, 'اگر از شما مالى طلب كند و اصرار نمايد، بخل مى‌ورزيد و كينه‌هاى شما را برملا مى‌سازد'),
(4583, 47, 38, 'آگاه باشيد! اين شماها هستيد كه براى انفاق در راه خدا دعوت مى‌شويد، سپس برخى از شما بخل مى‌ورزد. و هر كس بخل ورزد، فقط در حق خود بخل ورزيده و [گر نه‌] خدا بى‌نياز است و شما نيازمندانيد. و اگر روى برتابيد، خداوند قومى غير از شما را جانشين مى‌كند كه آنه');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'حقا ما تو را پيروزى بخشيديم، پيروزى درخشانى'),
(4585, 48, 2, 'تا خدا گناه گذشته و اخيرت را [كه به تو نسبت مى‌دادند] برطرف سازد و نعمتش را بر تو تمام كند و تو را به راهى راست هدايت نمايد'),
(4586, 48, 3, 'و تو را به نصرتى بى‌شكست يارى دهد'),
(4587, 48, 4, 'او كسى است كه آرامش را در دل‌هاى مؤمنان نازل كرد تا ايمانى بر ايمانشان بيفزايند، و لشكريان آسمان‌ها و زمين از آن خداست و خداوند داناى حكيم است'),
(4588, 48, 5, 'تا مردان و زنانى را كه ايمان آورده‌اند، به بهشت‌هايى داخل كند كه از پاى درختانش نهرها جارى است و در آن جاويدان بمانند و بدى‌هايشان را از آنها بپوشاند و اين در پيشگاه خدا كاميابى بزرگى است'),
(4589, 48, 6, 'و [تا] مردان و زنان نفاق پيشه و مردان و زنان مشرك را كه به خدا سوءظن دارند، عذاب كند. حوادث بد زمانه بر آنها باد، و خدا بر ايشان خشم گرفته و لعنتشان كرده و جهنم را براى آنها آماده كرده و چه بد سر انجامى است'),
(4590, 48, 7, 'جنود [و قواى‌] آسمان‌ها و زمين از آن خداست، و خدا شكست ناپذير حكيم است'),
(4591, 48, 8, '[اى پيامبر!] ما تو را گواه [بر اعمال آنها] و بشارتگر و هشدار دهنده فرستاديم'),
(4592, 48, 9, 'تا به خدا و فرستاده‌اش ايمان آوريد و او را يارى كنيد و بزرگش داريد و خدا را صبح و شام به پاكى بستاييد'),
(4593, 48, 10, 'كسانى كه با تو بيعت مى‌كنند، جز اين نيست كه با خدا بيعت مى‌كنند دست خدا بالاى دست‌هايشان است. پس هر كه پيمان بشكند، تنها به زيان خود مى‌شكند، و هر كه بر آنچه با خدا عهد بسته وفادار بماند، به زودى خدا پاداشى بزرگ به او مى‌بخشد'),
(4594, 48, 11, 'به زودى اعراب باديه نشين كه [از جنگ‌] وامانده‌اند به تو خواهند گفت: اموال ما و كسانمان ما را [از همراهى با شما] مشغول داشت، پس براى ما آمرزش بخواه. به زبان‌هايشان چيزى را مى‌گويند كه در دل‌هايشان نيست. بگو: چه كسى در برابر خدا براى شما اختيار چيزى ر'),
(4595, 48, 12, 'بلكه پنداشتيد كه پيامبر و مؤمنان هرگز به خانمان خود برنخواهند گشت، و اين [پندار] در دل‌هايتان زينت يافت و گمان بد برديد و مردمى هلاكت زده شديد'),
(4596, 48, 13, 'و هر كس به خدا و پيامبر او ايمان نياورد، [بداند كه‌] ما براى كافران آتشى سوزان آماده كرده‌ايم'),
(4597, 48, 14, 'و فرمانروايى آسمان‌ها و زمين از آن خداست. هر كه را بخواهد مى‌بخشايد و هر كه را بخواهد عذاب مى‌كند، و خدا آمرزنده‌ى مهربان است'),
(4598, 48, 15, 'به زودى واماندگان، هنگامى كه شما به طرف غنايم [خيبر] حركت كنيد تا آن را به تصرف در آوريد، به شما خواهند گفت: بگذاريد ما [هم‌] به دنبال شما بياييم. [اين گونه‌] مى‌خواهند سخن خدا را تغيير دهند. بگو: هرگز از پى ما نخواهيد آمد. خدا از پيش چنين گفته است.'),
(4599, 48, 16, 'به واماندگان باديه نشين بگو: به زودى به سوى قومى بسيار زورمند فراخوانده مى‌شويد كه يا با آنها مى‌جنگيد يا مسلمان مى‌شوند. پس اگر اطاعت كنيد، خدا شما را پاداشى نيكو مى‌بخشد و اگر روى بگردانيد، چنان كه قبلا نافرمانى كرديد، شما را به عذاب دردناكى عذاب م'),
(4600, 48, 17, 'بر نابينا و لنگ و بيمار گناهى نيست [كه در جهاد شركت نكنند]. و هر كه اطاعت خدا و رسول او كند، وى را در باغ‌هايى داخل مى‌كند كه از پاى درختانش نهرها جارى است و هر كه سر بتابد، او را به عذابى دردناك عذاب مى‌كند'),
(4601, 48, 18, 'به راستى خدا هنگامى كه مؤمنان زير درخت با تو بيعت مى‌كردند از آنها راضى شد و از آنچه در دلشان [از صدق نيت‌] بود آگاه گرديد، پس آرامش را بر آنها نازل كرد و فتحى نزديك (فتح خيبر) را به آنها پاداش داد'),
(4602, 48, 19, 'و [نيز] غنيمت‌هاى فراوانى كه مى‌گيرند، و خدا شكست ناپذير حكيم است'),
(4603, 48, 20, 'خدا به شما غنيمت‌هاى فراوانى را وعده داده است كه آنها را خواهيد گرفت ولى اين [غنايم خيبر] را زودتر به شما ارزانى داشت و دست [تعدى‌] مردم را از شما كوتاه ساخت تا براى مؤمنان نشانه‌اى باشد، و شما را به راه راست هدايت كند'),
(4604, 48, 21, 'و [غنيمت‌هاى‌] ديگرى [نيز هست‌] كه به آنها دست نيافته‌ايد تنها خدا بر آنها احاطه دارد، و خدا بر هر چيزى قادر است'),
(4605, 48, 22, 'و اگر كسانى كه كافر شدند، [در حديبيّه‌] به جنگ با شما بر مى‌خاستند، قطعا [به شما] پشت مى‌كردند و يار و ياورى [براى خود] نمى‌يافتند'),
(4606, 48, 23, 'سنت الهى است كه در گذشته نيز بوده است، و هرگز براى سنت الهى تغييرى نخواهى يافت'),
(4607, 48, 24, 'و اوست آن كه در دل مكه پس از پيروز كردن شما بر آنان، دست‌هاى آنها را از شما و دست‌هاى شما را از آنها كوتاه كرد [تا جنگى رخ ندهد كه مصلحت در اين بود]، و خدا به آنچه مى‌كنيد بيناست'),
(4608, 48, 25, 'آنها كسانى بودند كه كافر شدند و شما را از مسجد الحرام بازداشتند و نگذاشتند قربانى [شما] كه بازداشته شده بود به محلّش برسد. و اگر مردان و زنان مؤمنى كه شما آنها را نمى‌شناختيد [در ميان آنها] نبودند كه [ممكن بود] ندانسته آنها را بكوبيد و [از اين بابت‌'),
(4609, 48, 26, 'آن‌گاه كه كافران در دل خود تعصب ورزيدند تعصب جاهليت، [و مانع ورود شما به مكه شدند]، پس خدا آرامش خود را بر فرستاده‌ى خويش و بر مؤمنان نازل كرد و آنها را پاى‌بند آرمان تقوا نمود كه آنها بدان سزاوارتر بودند و اهليت آن را داشتند، و خدا به هر چيزى داناست'),
(4610, 48, 27, 'حقا خدا رؤياى پيامبر خود را راست كرد كه شما حتما به خواست خدا سر تراشيده و موى سر كوتاه كرده بدون هيچ ترسى با خاطره‌ى آسوده به مسجد الحرام وارد خواهيد شد پس خدا چيزهايى [از مصالح پشت پرده‌] مى‌دانست كه شما نمى‌دانستيد، لذا پيش از آن [براى شما با صلح'),
(4611, 48, 28, 'او كسى است كه پيامبر خود را با هدايت و آيين درست [به سوى مردم‌] فرستاد تا آن را بر همه اديان پيروز گرداند، و گواهى خدا [بر حقانيت پيامبر] كفايت مى‌كند'),
(4612, 48, 29, 'محمد صلّى الله عليه وآله وسلّم فرستاده‌ى خداست و كسانى كه با او هستند، بر كافران سختگير [و] با همديگر مهربانند. [همواره‌] آنها را در ركوع و سجود مى‌بينى كه فضل و خشنودى خدا را مى‌جويند. نشانه آنها اثر سجده‌اى است كه در چهره‌هايشان است. اين وصف ايشان');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اى كسانى كه ايمان آورده‌ايد! در برابر خدا و رسولش [در هيچ كارى‌] جلو نيفتيد، و از خدا بترسيد. بى‌گمان خدا شنواى داناست'),
(4614, 49, 2, 'اى كسانى كه ايمان آورده‌ايد! صدايتان را بلندتر از صداى پيامبر نكنيد و با او بلند سخن نگوييد آن‌گونه كه با يكديگر بلند سخن مى‌گوييد، مبادا بى‌آن كه خود بفهميد اعمالتان تباه شود'),
(4615, 49, 3, 'مسلما كسانى كه نزد پيامبر خدا صدايشان را فرو مى‌كشند همان‌ها هستند كه خدا دل‌هايشان را براى پرهيزگارى خالص كرده است. آنان را آمرزش و پاداشى بزرگ است'),
(4616, 49, 4, 'بى‌ترديد كسانى كه تو را از پشت اتاق‌ها صدا مى‌زنند، بيشترشان نمى‌انديشند'),
(4617, 49, 5, 'و اگر آنها صبر مى‌كردند تا تو [خود] به سراغشان آيى برايشان بهتر بود، و خدا آمرزنده‌ى مهربان است'),
(4618, 49, 6, 'اى كسانى كه ايمان آورده‌ايد! اگر فاسقى برايتان خبرى آورد، نيك وارسى كنيد تا مبادا به نادانى، گروهى را آسيب رسانيد آن‌گاه بر آنچه كرده‌ايد پشيمان شويد'),
(4619, 49, 7, 'و بدانيد كه رسول خدا در ميان شماست. اگر در بسيارى از كارها از [رأى و ميل‌] شما پيروى مى‌كرد، دچار هلاك و زحمت مى‌شديد، ليكن خدا ايمان را محبوب شما كرد و آن را در دل‌هاى شما آراست و انكار و نافرمانى و عصيان را منفور شما گردانيد. آنها واقعا رشد يافتگانن'),
(4620, 49, 8, '[و اين‌] كرامت و نعمتى از جانب خداوند است و خدا داناى حكيم است'),
(4621, 49, 9, 'و اگر دو طايفه از مؤمنان با هم جنگيدند، ميان آن دو را اصلاح كنيد و اگر يكى از آن دو بر ديگرى تعدى كرد، با آن كه تعدى مى‌كند بجنگيد تا به فرمان خدا بازگردد. پس اگر بازگشت، ميانشان به عدالت صلح دهيد و عدل و انصاف روا داريد، كه بى‌شك خداوند دادگران را دو'),
(4622, 49, 10, 'جز اين نيست كه مؤمنان با يكديگر برادرند، پس ميان برادرانتان را اصلاح كنيد و از خدا بترسيد، اميد كه مورد رحمت قرار گيريد'),
(4623, 49, 11, 'اى كسانى كه ايمان آورده‌ايد! نبايد قومى قوم ديگر را استهزا كنند، شايد آنها از اينها بهتر باشند، و نه زنانى زنان [ديگر] را، شايد آنها از اينها بهتر باشند، و عيب يكديگر را به رخ نكشيد و همديگر را به لقب‌هاى زشت مخوانيد كه بدنامى است نام كفر و فسوق پس ا'),
(4624, 49, 12, 'اى كسانى كه ايمان آورده‌ايد! از بسيارى گمان‌ها بپرهيزيد، چرا كه پاره‌اى از گمان‌ها گناه است، و [در كار ديگران‌] تجسّس نكنيد و كسى از شما غيبت ديگرى نكند آيا كسى از شما دوست دارد گوشت برادر مرده‌اش را بخورد؟ [بى‌شك همه شما] از آن كراهت داريد، [غيبت ني'),
(4625, 49, 13, 'اى مردم! ما شما را از يك مرد و زن آفريديم، و شما را تيره‌ها و قبيله‌ها قرار داديم تا يكديگر را بشناسيد [و اينها ملاك امتياز نيست‌]، قطعا ارجمندترين شما نزد خدا پرهيزگارترين شماست. بى‌ترديد خداوند داناى آگاه است'),
(4626, 49, 14, 'باديه‌نشينان گفتند: ايمان آورديم. بگو: ايمان نياورده‌ايد، ولى [بهتر است‌] بگوييد: اسلام آورده‌ايم، و ايمان هنوز به دل‌هاى شما راه نيافته است، و اگر خدا و پيامبر او را فرمان ببريد، چيزى از [پاداش‌] كارهاى شما را نمى‌كاهد. همانا خدا آمرزنده‌ى مهربان اس'),
(4627, 49, 15, 'مؤمنان [واقعى‌] تنها كسانى‌اند كه به خدا و رسول او ايمان آوردند و هيچ‌گاه شك ننمودند و با مال و جانشان در راه خدا جهاد كردند، تنها آنان صادقند'),
(4628, 49, 16, 'بگو: آيا خدا را از ديندارى خود باخبر مى‌سازيد؟ در صورتى كه خدا آنچه در آسمان‌ها و آنچه در زمين است مى‌داند، و خدا از همه چيز آگاه است'),
(4629, 49, 17, 'بر تو منّت مى‌نهند كه اسلام آورده‌اند. بگو: به اسلام خويش بر من منت منهيد، بلكه اين خداست كه بر شما منّت مى‌نهد كه شما را به ايمان هدايت كرده است، اگر [در مدعاى خود] صادقيد'),
(4630, 49, 18, 'همانا خدا غيب آسمان‌ها و زمين را مى‌داند، و خداوند به آنچه مى‌كنيد بيناست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف. سوگند به قرآن مجيد [كه اين پيامبر، راستگو و رستاخيز حق است‌]'),
(4632, 50, 2, 'بلكه از اين كه هشدار دهنده‌اى از خودشان به سوى آنها آمد تعجب كردند و كافران گفتند: اين چيز عجيبى است'),
(4633, 50, 3, 'آيا وقتى كه مرديم و خاك شديم [زنده مى‌شويم‌]؟ اين بازگشتى بعيد است'),
(4634, 50, 4, 'قطعا ما مى‌دانيم كه زمين از اجسادشان چه مى‌كاهد، و پيش ما كتابى است كه [همه چيز در آن‌] محفوظ است'),
(4635, 50, 5, '[نه،] بلكه حقيقت را وقتى برايشان آمد دروغ خواندند، پس آنها در وضعى سر در گم قرار دارند'),
(4636, 50, 6, 'آيا به آسمان بالاى سرشان نظر نكرده‌اند كه آن را چگونه بنا كرده و [با ستارگان‌] زينتش داده‌ايم كه هيچ گونه خلل و شكافى در آن نيست'),
(4637, 50, 7, 'و زمين را گسترديم و در آن كوه‌هاى استوار افكنديم و در آن از هر نوع گياهان خرّم رويانديم'),
(4638, 50, 8, 'تا مايه بصيرت و يادآورى براى هر بنده‌ى توبه كار باشد'),
(4639, 50, 9, 'و از آسمان آبى پربركت نازل كرديم و به وسيله آن، باغ‌ها و حبوبات درو كردنى را رويانديم'),
(4640, 50, 10, 'و نخل‌هاى بلند با خوشه‌هاى بر هم چيده [رويانديم‌]'),
(4641, 50, 11, 'تا روزى بندگان باشد، و با آن [آب‌] سرزمين مرده را زنده كرديم. خروج [از قبرها] نيز چنين است'),
(4642, 50, 12, 'پيش از ايشان قوم نوح و اهل رسّ و ثمود [پيامبران را] تكذيب كردند'),
(4643, 50, 13, 'و عاد و فرعون و برادران لوط'),
(4644, 50, 14, 'و اهل ايكه (قوم شعيب) و قوم تبّع همگى فرستادگان را تكذيب نمودند و در نتيجه وعده‌ى [عذاب‌] من تحقق يافت'),
(4645, 50, 15, 'پس آيا در آفرينش نخستين درمانده شديم [كه دوباره نتوانيم بيافرينيم؟] بلكه آنها [با اين همه دلايل‌] باز در آفرينش جديد در شك‌اند'),
(4646, 50, 16, 'و همانا ما انسان را آفريديم، و مى‌دانيم كه نفس او چه وسوسه‌اى به او مى‌كند، و ما از رشته رگ‌ها به او نزديك‌تريم'),
(4647, 50, 17, 'آن دم كه دو [فرشته‌] دريافت كننده، [اعمال او را] دريافت مى‌كنند كه از راست و چپ [در كمين‌] نشسته‌اند'),
(4648, 50, 18, 'هيچ سخنى را بر زبان نمى‌آورد مگر اين كه مراقبى آماده نزد اوست'),
(4649, 50, 19, 'و [سر انجام‌] سكرات مرگ به حق بيايد [و به او گفته شود:] اين همان است كه از آن مى‌گريختى'),
(4650, 50, 20, 'و در صور دميده شود. اين است روز وعده [ى عذاب‌]'),
(4651, 50, 21, 'و هر كسى همراه يك مأمور سوق دهنده و يك شاهد بيايد'),
(4652, 50, 22, '[گفته شود:] واقعا كه از اين [روز] در غفلت بودى، اينك از تو پرده‌ات را برداشتيم پس ديده‌ات امروز تيزبين است'),
(4653, 50, 23, 'و [فرشته‌] همراه او گويد: اين [نامه اعمال او] نزد من حاضر است'),
(4654, 50, 24, '[به آن دو فرشته خطاب شود:] هر كفر پيشه ستيزه‌گر را در جهنم افكنيد'),
(4655, 50, 25, 'منع كننده‌ى خير و متجاوز شكّاك را'),
(4656, 50, 26, 'آن كه با خداوند، خداى ديگرى نهاد، پس در عذاب شديدش بيفكنيد'),
(4657, 50, 27, '[شيطان‌] همدم وى گويد: [اى‌] پروردگار ما! من او را به سركشى وانداشتم، ليكن او خود در ضلالتى دور بود'),
(4658, 50, 28, '[خداوند] فرمايد: در پيشگاه من جدال نكنيد، كه من از پيش هشدارتان داده بودم'),
(4659, 50, 29, 'سخن [وعده‌ى عذاب‌] نزد من تغيير نيابد و من بر بندگان هيچ ستم نكنم'),
(4660, 50, 30, 'آن روز به جهنم گوييم: آيا پر شدى؟ و گويد: آيا افزون [بر اين‌] هم هست'),
(4661, 50, 31, 'و بهشت براى پرهيزگاران نزديك آورده شود و دور نباشد'),
(4662, 50, 32, '[و به آنها گفته شود:] اين است آنچه نويد داده مى‌شديد، براى هر توبه كار نگهدارنده [ى حدود خدا]'),
(4663, 50, 33, 'آن كه در نهان از خداى بخشاينده ترسيد و با قلبى مطيع و رام پيش آمد'),
(4664, 50, 34, 'به سلامت وارد [بهشت‌] شويد كه اين، روز جاودانگى است'),
(4665, 50, 35, 'برايشان هر چه بخواهند در آن جا موجود است و نزد ما بيشتر نيز هست. ستم كننده، ظلم پيشه. عبيد جمع عبد: بندگان. نقول: مى‌گوييم. امتلأت: پر شدى. هل من مزيد: آيا افزون بر اين هم هست. ازلفت: نزديك گردانده شد. توعدون (وعد): وعده داده مى‌شويد. اوّاب (اوب): تو'),
(4666, 50, 36, 'و چه بسيار نسل‌هايى را كه پيش از ايشان (اهل مكه) هلاك نموديم كه از آنها قوى‌تر بودند و در شهرها رخنه كردند. آيا [از قدرت خدا] گريزى هست'),
(4667, 50, 37, 'قطعا در اين [عقوبت‌ها] براى آن كس كه صاحب دل است يا با حضور قلب گوش فرا مى‌دهد عبرتى است'),
(4668, 50, 38, 'و همانا آسمان‌ها و زمين و آنچه را كه ميان آن دو است در شش روز آفريديم و ما را هيچ ضعف و خستگى نرسيد [پس چگونه زنده كردن مردگان سخت باشد]'),
(4669, 50, 39, 'پس بر آنچه مى‌گويند صبر كن، و پيش از برآمدن آفتاب و قبل از غروب به ستايش پروردگارت تسبيح گوى'),
(4670, 50, 40, 'و پاسى از شب و در پى سجده‌ها [و نمازها] او را تسبيح گوى'),
(4671, 50, 41, 'و روزى كه منادى از مكانى نزديك ندا مى‌دهد، گوش فرا دار'),
(4672, 50, 42, 'روزى كه صيحه [ى رستاخيز] را به حق مى‌شنوند. آن [روز] روز بيرون شدن [از قبرها] است'),
(4673, 50, 43, 'البته ماييم كه زنده مى‌كنيم و مى‌ميرانيم و بازگشت [همه‌] به سوى ماست'),
(4674, 50, 44, 'روزى كه زمين به سرعت از [روى‌] آنها بشكافد [و از قبرها خارج گردند]. اين گردآوردنى است كه بر ما آسان است'),
(4675, 50, 45, 'ما بهتر مى‌دانيم چه مى‌گويند، و تو مأمور اجبار آنها [به ايمان‌] نيستى، پس به وسيله‌ى قرآن هر كس را كه از تهديد من مى‌ترسد متذكر ساز');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهايى كه ابر و خاك را مى‌پراكنند'),
(4677, 51, 2, 'و قسم به ابرهاى گرانبار [از باران‌]'),
(4678, 51, 3, 'و قسم به كشتى‌ها كه به آسانى روانند'),
(4679, 51, 4, 'و سوگند به [فرشتگان‌] تقسيم كننده‌ى كار'),
(4680, 51, 5, 'كه آنچه وعده داده مى‌شويد حتما راست است'),
(4681, 51, 6, 'و [روز] پاداش حتما واقع شدنى است'),
(4682, 51, 7, 'سوگند به آسمان كه داراى مسيرها و زينت‌هاست'),
(4683, 51, 8, 'كه شما [در مورد پيام‌هاى قرآن‌] در گفتارى مختلف و گوناگونيد'),
(4684, 51, 9, 'كسى از [ايمان به‌] آن منحرف مى‌شود كه [از حق‌] منحرف شده باشد'),
(4685, 51, 10, 'مرگ بر دروغپردازان'),
(4686, 51, 11, 'همانان كه در گرداب جهالت فرو رفته و بى‌خبرند'),
(4687, 51, 12, 'مى‌پرسند: روز جزا چه وقت است'),
(4688, 51, 13, 'روزى كه آنها بر آتش [عذاب‌] عقوبت مى‌شوند'),
(4689, 51, 14, 'عذابتان را بچشيد، اين همان است كه بدان عجله مى‌كرديد'),
(4690, 51, 15, 'همانا پرهيزگاران در باغ‌ها و چشمه‌سارانند'),
(4691, 51, 16, 'در حالى كه آنچه را پروردگارشان به آنها عطا فرموده دريافت مى‌كنند آنها پيش از اين نيكوكار بودند'),
(4692, 51, 17, 'و آنها كمى از شب را مى‌خفتند'),
(4693, 51, 18, 'و در سحرگاهان استغفار مى‌كردند'),
(4694, 51, 19, 'و در اموالشان سهمى براى سائل و محروم بود'),
(4695, 51, 20, 'و در زمين براى اهل يقين نشانه‌هايى است'),
(4696, 51, 21, 'و نيز در وجود خودتان. پس آيا نمى‌بينيد'),
(4697, 51, 22, 'و روزى شما و آنچه وعده داده مى‌شويد در آسمان است'),
(4698, 51, 23, 'پس سوگند به پروردگار آسمان و زمين كه اين سخن درست مثل اين كه شما سخن مى‌گوييد حق است'),
(4699, 51, 24, 'آيا خبر مهمانان ارجمند ابراهيم به تو رسيده است'),
(4700, 51, 25, 'آن‌گاه كه بر او وارد شدند پس گفتند سلام [بر تو]. گفت: سلام! [بر شما كه‌] گروهى ناشناسيد'),
(4701, 51, 26, 'پس آهسته به سوى خانواده‌ى خويش رفت و گوساله‌اى فربه [و بريان‌] آورد'),
(4702, 51, 27, 'پس آن را به نزدشان برد [ولى با تعجب ديد دست به غذا نمى‌برند.] گفت: آيا غذا نمى‌خوريد'),
(4703, 51, 28, 'پس [در دلش‌] از آنان بيمناك شد. گفتند: مترس، و او را به [تولد] پسرى دانا مژده دادند'),
(4704, 51, 29, 'و زنش فريادكنان سر رسيد و بر صورت خود زد و گفت: زنى پير نازا [چگونه بزايد]'),
(4705, 51, 30, 'گفتند: پروردگارت چنين گفته است. همانا او حكيم داناست'),
(4706, 51, 31, '[ابراهيم‌] گفت: اى فرستادگان [خدا]! مأموريت شما چيست'),
(4707, 51, 32, 'گفتند: ما به سوى مردمى پليدكار فرستاده شده‌ايم'),
(4708, 51, 33, 'تا سنگ‌هايى از گل بر سرشان فرو فرستيم'),
(4709, 51, 34, '[كه‌] نزد پروردگارت براى تجاوزكاران، نشاندار شده‌اند'),
(4710, 51, 35, 'پس هر كه از مؤمنان در آن [شهرها] بود بيرون برديم'),
(4711, 51, 36, 'اما از مسلمانان جز يك خانه در آن جا نيافتيم'),
(4712, 51, 37, 'و در آن جا براى آنها كه از عذاب دردناك مى‌ترسند نشانى بر جاى گذاشتيم'),
(4713, 51, 38, 'و در [ماجراى‌] موسى [نيز نشانه و عبرتى است‌] آن‌گاه كه او را با حجتى آشكار به سوى فرعون فرستاديم'),
(4714, 51, 39, 'پس [فرعون‌] با اركان [و نيروى‌] خود روى برتافت و گفت: [اين شخص‌] ساحرى يا ديوانه‌اى است'),
(4715, 51, 40, 'پس او و سپاهيانش را گرفتيم و آنها را در دريا افكنديم در حالى كه او [در آخرين لحظه‌] ملامتگر [خويش‌] بود'),
(4716, 51, 41, 'و در [ماجراى‌] عاد [نيز عبرتى است‌] آن‌گاه كه بر سر آنها باد مهلك فرستاديم'),
(4717, 51, 42, 'بر هيچ چيزى نمى‌گذشت مگر اين كه آن را چون استخوان پوسيده مى‌گرداند'),
(4718, 51, 43, 'و در [ماجراى‌] ثمود [نيز عبرتى است‌] آن‌گاه كه به ايشان گفته شد: تا چندى برخوردار شويد [كه به حسابتان خواهيم رسيد]'),
(4719, 51, 44, 'تا [آن كه‌] از فرمان پروردگار خود سر برتافتند، پس در حالى كه آنها مى‌نگريستند صاعقه آنها را گرفت'),
(4720, 51, 45, '[چنان بر زمين افتادند] كه نه توانستند برخيزند و نه از خود دفاع كنند'),
(4721, 51, 46, 'و از پيش، قوم نوح را [كيفر نموديم، زيرا] آنان قومى نافرمان بودند'),
(4722, 51, 47, 'و آسمان را با دستان [قدرت‌] خود بنا نهاديم و البته ما گسترش دهنده [ى آن‌] هستيم'),
(4723, 51, 48, 'و زمين را گسترديم و نيكو گسترندگانى هستيم'),
(4724, 51, 49, 'و از هر چيزى دو زوج آفريديم، اميد كه شما عبرت گيريد'),
(4725, 51, 50, 'پس به سوى خدا بگريزيد كه من شما را از طرف او بيم دهنده‌اى آشكارم'),
(4726, 51, 51, 'و با خدا معبود ديگرى قرار مدهيد به راستى من از جانب او براى شما هشدار دهنده‌اى آشكارم'),
(4727, 51, 52, 'همچنين، براى كسانى كه پيش از آنها بودند هيچ پيامبرى نيامد جز اين كه گفتند: ساحرى يا ديوانه‌اى است'),
(4728, 51, 53, 'آيا همديگر را به اين [سخن‌] سفارش كرده بودند؟ [نه،] بلكه آنان مردمى سركش بودند'),
(4729, 51, 54, 'پس از آنها روى بگردان كه تو مورد ملامت نخواهى بود'),
(4730, 51, 55, 'و اندرز ده كه اندرز، مؤمنان را سود مى‌بخشد'),
(4731, 51, 56, 'و جن و انس را نيافريدم مگر براى اين كه مرا بندگى كنند'),
(4732, 51, 57, 'از آنان هيچ روزيى نمى‌خواهم، و نمى‌خواهم كه مرا اطعام كنند'),
(4733, 51, 58, 'همانا خداوند، خود روزى‌رسان و صاحب قدرت و توانمند است'),
(4734, 51, 59, 'پس براى كسانى كه ستم كردند، سهم بزرگى از عذاب است همانند سهم يارانشان [از ستمگران گذشته‌]، بنابر اين عجله نكنند'),
(4735, 51, 60, 'پس واى بر كسانى كه كافر شدند از آن روزشان كه وعده داده مى‌شوند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به طور'),
(4737, 52, 2, 'و سوگند به كتاب نوشته شده'),
(4738, 52, 3, 'در ورقى سر گشاده'),
(4739, 52, 4, 'و سوگند به بيت المعمور'),
(4740, 52, 5, 'و سوگند به سقف برافراشته'),
(4741, 52, 6, 'و سوگند به درياى آكنده'),
(4742, 52, 7, 'كه بى‌ترديد عذاب پروردگار تو واقع شدنى است'),
(4743, 52, 8, 'آن را هيچ بازدارنده‌اى نيست'),
(4744, 52, 9, 'روزى كه آسمان سخت به لرزه در آيد'),
(4745, 52, 10, 'و كوه‌ها [جمله‌] به حركت در آيند'),
(4746, 52, 11, 'پس در آن روز واى بر تكذيب كنندگان'),
(4747, 52, 12, 'آنان كه به ياوه‌گويى [و انكار آيات‌] سرگرمند'),
(4748, 52, 13, 'روزى كه با خشونت و شدت به سوى آتش جهنم رانده مى‌شوند'),
(4749, 52, 14, '[گفته شود:] اين همان آتشى است كه دروغش مى‌پنداشتيد. مشغله بيهوده. يلعبون (لعب): بازى مى‌كنند، سرگرم مى‌شوند. يدعّون (دعّ): با خشونت انداخته مى‌شوند، با قهر كشانده مى‌شوند. كنتم تكذّبون: تكذيب مى‌كرديد'),
(4750, 52, 15, 'پس آيا اين افسون است يا شما نمى‌بينيد'),
(4751, 52, 16, 'داخل آن شويد، مى‌خواهيد صبر كنيد، يا نكنيد، بر شما يكسان است. جز اين نيست كه بدانچه مى‌كرديد مجازات مى‌شويد'),
(4752, 52, 17, 'بى‌شك پرهيزگاران در باغ‌ها و ناز و نعمت‌اند'),
(4753, 52, 18, 'به آنچه پروردگارشان به آنها داده شاد و مسرورند و خدايشان آنها را از عذاب دوزخ مصون داشته است'),
(4754, 52, 19, 'به [پاداش‌] آنچه مى‌كرديد بخوريد و بنوشيد، گوارايتان باد'),
(4755, 52, 20, 'بر تخت‌هايى رديف هم تكيه زنند، و حوران درشت چشم را همسرشان گردانيم'),
(4756, 52, 21, 'و كسانى كه ايمان آوردند و فرزندانشان در ايمان پيروى‌شان كردند، فرزندانشان را به آنها ملحق كنيم و از [پاداش‌] عملشان چيزى نكاهيم. هر كسى در گرو دستاورد خويش است'),
(4757, 52, 22, 'و از انواع ميوه‌ها و گوشتى كه دلخواه آنهاست عطايشان مى‌كنيم'),
(4758, 52, 23, 'در آن جا، از دست هم [به شوخى‌] جام مى‌ربايند [ولى‌] در آن نه سخن بيهوده باشد و نه گناهكارى'),
(4759, 52, 24, 'و براى [خدمتشان‌] پسرانى خاص آنها چون مرواريد درون [صدف‌] بر گردشان مى‌چرخند'),
(4760, 52, 25, 'و برخى‌شان رو به برخى ديگر كرده از هم بپرسند'),
(4761, 52, 26, 'گويند: به راستى ما پيش‌تر در ميان كسان خود بيمناك بوديم'),
(4762, 52, 27, 'پس خدا بر ما منت نهاد و از عذاب سوزنده‌ى نافذ نگاهمان داشت'),
(4763, 52, 28, 'به راستى ما پيش از اين او را مى‌خوانديم [و مى‌پرستيديم‌] بى‌ترديد او نيكى‌رسان و مهربان است'),
(4764, 52, 29, 'پس تذكر ده كه تو به لطف پروردگارت نه كاهنى و نه ديوانه'),
(4765, 52, 30, 'آيا مى‌گويند: شاعرى است كه ما براى وى منتظر مرگ و حوادث روزگاريم'),
(4766, 52, 31, 'بگو: منتظر باشيد كه من نيز با شما از منتظرانم'),
(4767, 52, 32, 'آيا عقل‌هايشان آنها را به اين [موضعگيرى‌] وا مى‌دارد يا آنها مردمى سركش هستند'),
(4768, 52, 33, 'يا مى‌گويند: آن [قرآن‌] را خود ساخته؟ [نه، اينها بهانه است‌] بلكه ايمان نمى‌آورند'),
(4769, 52, 34, 'پس بايد سخنى مانند آن بياورند اگر راست مى‌گويند'),
(4770, 52, 35, 'آيا آنها از هيچ [و بى‌هيچ خالقى‌] خلق شده‌اند يا خود خالق [خويش‌] اند'),
(4771, 52, 36, 'مگر آسمان‌ها و زمين را آنها خلق كرده‌اند [كه فرمان نپذيرند]؟ بلكه جوياى يقين نيستند'),
(4772, 52, 37, 'آيا ذخاير پروردگار تو نزد آنهاست يا ايشان [بر جهان هستى‌] تسلط دارند'),
(4773, 52, 38, 'آيا آنها نردبانى دارند در آن [اخبار غيبى را] مى‌شنوند؟ پس بايد شنونده‌ى آنان برهانى آشكار بياورد'),
(4774, 52, 39, 'آيا خداوند را دختران است و شما را پسران [كه فرشتگان را دختران خدا مى‌ناميد]'),
(4775, 52, 40, 'آيا از آنها مزدى مى‌خواهى كه ايشان از غرامت [آن‌] سنگين بارند'),
(4776, 52, 41, 'آيا اسرار غيب نزد آنهاست و آنها مى‌نويسند'),
(4777, 52, 42, 'آيا مى‌خواهند نيرنگى بزنند؟ ولى كافران خودشان نيرنگ خورده‌اند'),
(4778, 52, 43, 'آيا ايشان را جز خدا معبودى است؟ منزه است خداى يگانه از آنچه شريك او مى‌سازند'),
(4779, 52, 44, 'و اگر پاره سنگى را در حال سقوط از آسمان بينند [باز ايمان نياورند و] گويند: ابرى متراكم است'),
(4780, 52, 45, 'پس آنها را رها كن تا روز مرگ خود را كه بيهوش مى‌افتند ملاقات كنند'),
(4781, 52, 46, 'روزى كه تدبيرشان به هيچ وجه به كارشان نيايد و ايشان يارى نشوند'),
(4782, 52, 47, 'و همانا كسانى كه ستم كردند، عذابى ديگر دارند، و ليكن بيشترشان نمى‌دانند'),
(4783, 52, 48, 'و براى [ابلاغ‌] فرمان پروردگارت صابر باش كه مسلما تو تحت نظر مايى، و پروردگارت را هنگامى كه برمى‌خيزى، به ستايش تسبيح گوى'),
(4784, 52, 49, 'و او را پاسى از شب و به هنگام پشت كردن ستارگان تسبيح گوى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به [آن‌] ستاره وقتى كه فرو افتد'),
(4786, 53, 2, 'كه يار شما نه گمراه شده و نه منحرف'),
(4787, 53, 3, 'و از روى هوس سخن نمى‌گويد'),
(4788, 53, 4, 'نيست اين سخن جز وحيى كه نازل مى‌شود'),
(4789, 53, 5, 'او را [آن فرشته‌ى‌] بس نيرومند آموخت'),
(4790, 53, 6, 'توانمندى كه [به صورت اصلى خود در برابر پيامبر] ايستاد'),
(4791, 53, 7, 'در حالى كه او (جبرئيل) در افق برتر قرار داشت'),
(4792, 53, 8, 'سپس نزديك آمد و نزديك‌تر شد'),
(4793, 53, 9, 'تا به اندازه‌ى دو كمان يا نزديك‌تر گرديد'),
(4794, 53, 10, 'پس [توسط جبرئيل‌] به بنده‌ى خود آنچه را بايد وحى كند، وحى كرد'),
(4795, 53, 11, 'قلب [پاك محمد] آن [صورت اصلى جبرئيل‌] را كه ديد دروغ نگفت'),
(4796, 53, 12, 'آيا در آنچه مى‌بيند با او مجادله مى‌كنيد'),
(4797, 53, 13, 'و همانا در يك نزول ديگر هم او (جبرئيل) را ديده است'),
(4798, 53, 14, 'در كنار سدرة المنتهى'),
(4799, 53, 15, 'كه جنة الماوى در آن جاست'),
(4800, 53, 16, 'آن‌گاه كه آن درخت سدره را مى‌پوشاند آنچه [از امور خيره كننده‌] مى‌پوشاند'),
(4801, 53, 17, 'چشم [پيامبر] خطا نرفت و از حد نگذشت [آنچه ديد واقعيت بود]'),
(4802, 53, 18, 'به راستى كه برخى از آيات بزرگ‌تر پروردگارش را بديد'),
(4803, 53, 19, 'آيا [بت‌هاى‌] لات و عزّى، را ديده‌ايد'),
(4804, 53, 20, 'و منات، آن سومين [بت‌] ديگر را [كه دختران خدا مى‌خوانيد]'),
(4805, 53, 21, 'آيا [به خيالتان‌] براى شما پسر است و براى او دختر [و شما دختران را حقير مى‌پنداريد]'),
(4806, 53, 22, 'در اين صورت اين تقسيمى ظالمانه است'),
(4807, 53, 23, 'اينها نيستند مگر نام‌هايى كه شما و پدرانتان آنها را ناميده‌ايد و خدا هيچ حجتى بر [خدايى‌] آنها نفرستاده است. آنان جز از گمان و هواى نفس خويش پيروى نمى‌كنند، در حالى كه از سوى پروردگارشان هدايت برايشان آمده است'),
(4808, 53, 24, 'آيا انسان بدانچه تمنا كند مى‌رسد'),
(4809, 53, 25, 'پس آخرت و دنيا از آن خداست'),
(4810, 53, 26, 'و چه بسيار فرشته كه در آسمان‌هاست كه شفاعتشان سودى نمى‌بخشد مگر پس از آن كه خدا براى هر كه بخواهد و بپسندد، اذن دهد'),
(4811, 53, 27, 'همانا كسانى كه به آخرت ايمان ندارند، فرشتگان را به نام دختران مى‌نامند'),
(4812, 53, 28, 'و ايشان در اين [كار] شناختى ندارند. آنها جز از گمان پيروى نمى‌كنند، و البته گمان به هيچ وجه از [شناخت‌] حقيقت كفايت نمى‌كند'),
(4813, 53, 29, 'پس از كسى كه از ياد ما دل مى‌گرداند و جز زندگى دنيا را نمى‌خواهد روى گردان'),
(4814, 53, 30, 'اين نهايت فهم آنهاست. بى‌ترديد، پروردگار تو به كسى كه از راه او منحرف شده آگاه‌تر است، و او كسى را كه هدايت يافته است بهتر مى‌شناسد'),
(4815, 53, 31, 'و هر چه در آسمان‌ها و هر چه در زمين است از آن خداست، تا كسانى را كه بد كرده‌اند بدانچه انجام داده‌اند كيفر دهد و آنها را كه نيكى كرده‌اند پاداشى بهتر دهد'),
(4816, 53, 32, 'آنان كه از گناهان بزرگ و زشتكارى‌ها- جز لغزش‌هاى كوچك- اجتناب مى‌كنند، بى‌ترديد آمرزش پروردگار تو وسيع است. او به [حال‌] شما دمى كه شما را از زمين پديد آورد و هنگامى كه به صورت جنين در شكم مادرانتان بوديد داناتر است، پس خود را نستاييد، او به كسى كه پ'),
(4817, 53, 33, 'پس آيا ديدى آن كسى را كه [از حق‌] روى برتافت'),
(4818, 53, 34, 'و كمى بخشيد [ولى ادامه نداد] و امتناع ورزيد'),
(4819, 53, 35, 'آيا علم غيب نزد اوست و او [حقايق را] مى‌بيند'),
(4820, 53, 36, 'آيا بدانچه در صحيفه‌هاى موسى آمده خبر نيافته است'),
(4821, 53, 37, 'و [در نوشته‌هاى‌] ابراهيم كه [به پيمان‌هاى خدا] وفا كرد'),
(4822, 53, 38, 'كه هيچ كس بار گناه ديگرى را برنمى‌دارد'),
(4823, 53, 39, 'و اين كه براى انسان جز آنچه تلاش كرده نيست'),
(4824, 53, 40, 'و حتما كوشش او به زودى ديده خواهد شد'),
(4825, 53, 41, 'سپس به او جزاى كافى داده خواهد شد'),
(4826, 53, 42, 'و اين كه پايان [كار] به سوى پروردگار توست'),
(4827, 53, 43, 'و هم اوست كه بخنداند و بگرياند'),
(4828, 53, 44, 'و هم اوست كه ميراند و زنده كرد'),
(4829, 53, 45, 'و اوست كه دو زوج نر و ماده را آفريد'),
(4830, 53, 46, 'از نطفه‌اى آن‌گاه كه ريخته شود'),
(4831, 53, 47, 'و اين كه ايجاد عالم ديگر (رستاخيز) بر عهده‌ى اوست'),
(4832, 53, 48, 'و هم اوست كه [شما را] بى‌نياز كرد و مال بخشيد'),
(4833, 53, 49, 'و هم اوست پروردگار ستاره‌ى شعرى (كه او را مى‌پرستيدند)'),
(4834, 53, 50, 'و هم اوست كه عاديان نخستين را هلاك كرد'),
(4835, 53, 51, 'و ثموديان را باقى نگذاشت'),
(4836, 53, 52, 'و پيش از آنها قوم نوح را [هلاك نمود] آنها ظالم‌تر و طاغى‌تر بودند'),
(4837, 53, 53, 'و شهرهاى واژگون شده [ى قوم لوط] را در هم فرو ريخت'),
(4838, 53, 54, 'پس آنها را [با عذاب فراگيرى‌] چنان كه بايد فرا پوشانيد'),
(4839, 53, 55, 'پس در كدام يك از نعمت‌هاى پروردگارت ترديد مى‌كنى'),
(4840, 53, 56, 'اين [پيامبر] بيم رسانى از بيم رسانان پيشين است'),
(4841, 53, 57, 'رستاخيز نزديك است'),
(4842, 53, 58, 'كسى جز خداوند برطرف كننده‌ى [عذاب‌] آن نيست'),
(4843, 53, 59, 'پس آيا از اين سخن عجب داريد'),
(4844, 53, 60, 'و مى‌خنديد و [از خوف قيامت‌] نمى‌گرييد'),
(4845, 53, 61, 'و شما [از آن‌] در غفلت به سر مى‌بريد'),
(4846, 53, 62, 'پس خدا را سجده آريد و پرستش كنيد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قيامت نزديك شد و ماه بشكافت'),
(4848, 54, 2, 'و اگر معجزه‌اى ببينند، روى بگردانند و گويند: سحرى دائمى است'),
(4849, 54, 3, 'و [حقيقت را] تكذيب كردند و پيرو هوس‌هاى خود شدند، و هر امرى [به جاى خود] قرار گيرد [و حق و باطل روشن شود]'),
(4850, 54, 4, 'و به تحقيق از اخبار [پيشينيان‌] آنچه در آن بازدارندگى است برايشان آمده است'),
(4851, 54, 5, '[آيات الهى‌] حكمتى رساست، ليكن هشدارها [حق ناپذيران را] فايده نمى‌دهد'),
(4852, 54, 6, 'پس از آنان روى برتاب [و منتظر باش‌] روزى را كه داعى [حق‌] به سوى امرى دهشتناك دعوت كند [و به پاى حساب بكشد]'),
(4853, 54, 7, 'در حالى كه چشمانشان فرو شكسته، از قبرها بيرون مى‌آيند، گويى آنان ملخ‌هاى پراكنده‌اند'),
(4854, 54, 8, 'گردن‌كشان به سرعت به سوى آن دعوتگر بشتابند كافران گويند: امروز روز سختى است'),
(4855, 54, 9, 'پيش از آن قوم نوح تكذيب كردند و بنده‌ى ما را دروغگو خواندند و گفتند: ديوانه‌اى طرد شده [از اجتماع‌] است'),
(4856, 54, 10, 'پس پروردگار خود را خواند كه: من مغلوبم پس انتقام گير'),
(4857, 54, 11, 'پس درهاى آسمان را به آبى سيل آسا گشوديم'),
(4858, 54, 12, 'و زمين را به صورت چشمه‌هايى شكافتيم تا آب [زمين و آسمان‌] براى امرى كه تقدير شده بود به هم پيوست'),
(4859, 54, 13, 'و او را بر [كشتى ساخته شده‌] از تخته‌ها و ميخ‌ها حمل كرديم'),
(4860, 54, 14, '[كشتى‌] زير نظر ما مى‌رفت، [اين‌] پاداش كسى بود كه مورد انكار واقع شده بود'),
(4861, 54, 15, 'و به راستى آن [سفينه‌] را بر جاى نهاديم تا عبرتى باشد، پس آيا پند گيرنده‌اى هست'),
(4862, 54, 16, 'پس چگونه بود عذاب من و هشدارهاى من'),
(4863, 54, 17, 'و قطعا قرآن را براى پند آموزى آسان كرديم، پس آيا پند گيرنده‌اى هست'),
(4864, 54, 18, 'عاديان [هود را] تكذيب كردند، پس چگونه بود عذاب من و هشدارهاى من'),
(4865, 54, 19, 'همانا ما بر [سر] آنها تندبادى سوزان در روزى پيوسته شوم فرستاديم'),
(4866, 54, 20, '[كه‌] مردم را از جا مى‌كند، گويى تنه‌هاى نخلى بودند كه ريشه كن شده‌اند'),
(4867, 54, 21, 'پس [بنگر] چگونه بود عذاب من و هشدارهاى من'),
(4868, 54, 22, 'و قطعا قرآن را براى پندآموزى آسان كرديم، پس آيا پند گيرنده‌اى هست'),
(4869, 54, 23, 'قوم ثمود بيم رسانان را تكذيب كردند'),
(4870, 54, 24, 'پس گفتند: آيا بشرى يكه و تنها از خودمان را پيروى كنيم؟ در اين صورت ما واقعا در گمراهى و جنون خواهيم بود'),
(4871, 54, 25, 'آيا از ميان همه‌ى ما وحى بر او القا شده است؟ [نه،] بلكه او دروغگويى خودخواه است'),
(4872, 54, 26, 'زودا كه فردا بدانند دروغگوى خودخواه كيست'),
(4873, 54, 27, '[به صالح گفتيم:] همانا ما اين ماده شتر را به امتحانشان مى‌فرستيم، پس مراقبشان باش و صبورى كن'),
(4874, 54, 28, 'و به آنان خبر ده كه آب، ميانشان [و ميان ناقه‌] تقسيم شده است، هر كدام در نوبت خود [بر سر آب‌] حاضر شود'),
(4875, 54, 29, 'پس رفيقشان را ندا دادند و او شمشير كشيد و [ناقه را] پى كرد'),
(4876, 54, 30, 'پس [بنگر] چگونه بود عذاب من و هشدارهاى من'),
(4877, 54, 31, 'همانا ما بر سرشان يك صيحه [ى مرگبار] فرستاديم و همگى چون علف خشك، خرد و ريز شدند'),
(4878, 54, 32, 'و قطعا قرآن را براى پندآموزى آسان كرديم، پس آيا پند گيرنده‌اى هست'),
(4879, 54, 33, 'قوم لوط بيم رسانان را تكذيب كردند'),
(4880, 54, 34, 'همانا ما بر سر آنها طوفانى سنگبار فرو فرستاديم، بجز خاندان لوط كه به وقت سحر نجاتشان داديم'),
(4881, 54, 35, '[و اين‌] رحمتى از جانب ما بود. ما اين گونه هر كه را سپاس داشت پاداش مى‌دهيم'),
(4882, 54, 36, 'و همانا [لوط] آنها را از عذاب سخت ما بيم داده بود، ولى با آن هشدارها به ستيزه برخاستند'),
(4883, 54, 37, 'و از مهمانان او كام زشت خواستند، پس چشمانشان را كور كرديم [و گفتيم:] اينك بچشيد عذاب و هشدارهاى مرا'),
(4884, 54, 38, 'و همانا سپيده دم عذابى پايدار و ثابت بر سرشان آمد'),
(4885, 54, 39, 'پس عذاب و هشدارهاى مرا بچشيد'),
(4886, 54, 40, 'و قطعا قرآن را براى پند آموزى آسان كرديم، پس آيا پند گيرنده‌اى هست'),
(4887, 54, 41, 'و همانا بيم رسانان به جانب فرعونيان آمدند'),
(4888, 54, 42, '[اما آنها] همه معجزات ما را تكذيب كردند، پس آنها را همچون گرفتن پيروزمندى مقتدر بگرفتيم'),
(4889, 54, 43, 'آيا كافران شما [اهل مكه‌] از آنها [كه برشمرديم‌] برترند، يا شما را در نوشته‌ها [ى آسمانى‌] امان نامه‌اى است'),
(4890, 54, 44, 'يا مى‌گويند: ما جماعتى هستيم هميار و انتقامگير'),
(4891, 54, 45, 'زودا كه اين جمع درهم شكسته شود و پشت كرده بگريزند'),
(4892, 54, 46, 'بلكه موعدشان قيامت است، و [مجازات‌] قيامت هولناك‌تر و تلخ‌تر است'),
(4893, 54, 47, 'قطعا بزهكاران در گمراهى و شعله‌هاى آتشند'),
(4894, 54, 48, 'روزى كه در آتش بر صورت‌هايشان كشانده شوند، [و گفته شود:] درد لمس كردن لهيب آتش را بچشيد'),
(4895, 54, 49, 'همانا ما هر چيزى را به اندازه آفريده‌ايم'),
(4896, 54, 50, 'و فرمان ما جز يك فرمان نيست، چون چشم بر هم زدنى'),
(4897, 54, 51, 'و همفكران شما را سخت به هلاكت رسانديم، پس آيا پند گيرنده‌اى هست'),
(4898, 54, 52, 'و هر چه كرده‌اند در نامه‌ها [ى اعمال‌] ثبت است'),
(4899, 54, 53, 'و هر خرد و كلانى [از اعمال‌] به قلم رفته است'),
(4900, 54, 54, 'محققا پارسايان در باغ‌ها و [كنار] نهرهايند'),
(4901, 54, 55, 'در جايگاه صدق، نزد پادشاهى با اقتدار');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '[خداوند] رحمان'),
(4903, 55, 2, 'قرآن را تعليم داد'),
(4904, 55, 3, 'انسان را آفريد'),
(4905, 55, 4, 'او را گفتن آموخت'),
(4906, 55, 5, 'خورشيد و ماه بر حسابى [مقرر] در كارند'),
(4907, 55, 6, 'و گياه و درخت [سر به فرمانند و] سجده مى‌آورند'),
(4908, 55, 7, 'و آسمان را برافراشت و [در آن‌] ميزان نهاد'),
(4909, 55, 8, 'تا در وزن كردن خلاف نكنيد'),
(4910, 55, 9, 'و ترازو را به انصاف برپا داريد و در سنجش مكاهيد'),
(4911, 55, 10, 'و زمين را براى آدميان آفريد'),
(4912, 55, 11, 'در آن ميوه [ها] و نخل‌هايى با خوشه‌هاى غلاف دار است'),
(4913, 55, 12, 'و دانه‌هاى سبوس دار [با برگ و كاه‌] و گياهان خوشبو'),
(4914, 55, 13, 'پس [اى جن و انس‌] كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4915, 55, 14, 'انسان را از گل خشكيده‌اى سفال مانند آفريد'),
(4916, 55, 15, 'و جن را از تشعشعى از آتش خلق كرد'),
(4917, 55, 16, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4918, 55, 17, 'او پروردگار دو مشرق و پروردگار دو مغرب است'),
(4919, 55, 18, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4920, 55, 19, 'دو درياى [تلخ و شيرين‌] را كه با هم برخورد دارند روان كرد [ولى يكديگر را تغيير نمى‌دهند]'),
(4921, 55, 20, '[چرا كه‌] ميان آن دو حايلى است كه به همديگر تجاوز نمى‌كنند'),
(4922, 55, 21, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4923, 55, 22, 'از آن دو لؤلؤ و مرجان بيرون مى‌آيد'),
(4924, 55, 23, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4925, 55, 24, 'و براى اوست كشتى‌هاى روان در دريا كه چون كوه‌هايند'),
(4926, 55, 25, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4927, 55, 26, 'هر آن كس كه بر روى زمين است فناپذير است'),
(4928, 55, 27, 'و تنها ذات با شكوه و ارجمند پروردگارت باقى مى‌ماند'),
(4929, 55, 28, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4930, 55, 29, 'هر كه در آسمان‌ها و زمين است از او حاجت مى‌خواهد، و او هر روز به كارى‌ست'),
(4931, 55, 30, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4932, 55, 31, 'زودا كه به [حسابرسى‌] شما بپردازيم اى جن و انس'),
(4933, 55, 32, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4934, 55, 33, 'اى گروه جن و انس! اگر توانستيد از مرزهاى آسمان‌ها و زمين بگذريد پس بگذريد، [ولى‌] نتوانيد گذشت، مگر با داشتن قدرتى'),
(4935, 55, 34, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4936, 55, 35, 'بر سرتان شراره‌هايى از آتش و دود و مس گداخته فرستاده مى‌شود كه از خود دفاع نتوانيد كرد'),
(4937, 55, 36, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4938, 55, 37, 'پس آن‌گاه كه آسمان شكافته شود و چونان چرمى سرخ و گلگون گردد'),
(4939, 55, 38, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4940, 55, 39, 'پس در آن روز هيچ انس و جنى از گناهش پرسيده نشود [زيرا همه چيز روشن است‌]'),
(4941, 55, 40, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4942, 55, 41, 'تبهكاران به سيمايشان شناخته مى‌شوند، پس موى پيشانى و پاها [ى آنها] گرفته مى‌شود [تا به دوزخ افكنده شوند]'),
(4943, 55, 42, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4944, 55, 43, 'اين همان جهنمى است كه مجرمان آن را دروغ مى‌انگارند'),
(4945, 55, 44, 'ميان آتش و آب جوشان مى‌گردند'),
(4946, 55, 45, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4947, 55, 46, 'و هر كس را كه از مقام پروردگارش بترسد دو باغ [در بهشت‌] است'),
(4948, 55, 47, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4949, 55, 48, '[آن دو باغ‌] داراى انواع درختان و شاخسارانند'),
(4950, 55, 49, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4951, 55, 50, 'در آنها دو چشمه جارى‌اند'),
(4952, 55, 51, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4953, 55, 52, 'در آن دو [باغ‌] از هر ميوه‌اى دو گونه هست'),
(4954, 55, 53, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4955, 55, 54, 'بر بسترهايى كه آستر آنها از ديباى سبز است تكيه مى‌زنند، و چيدن [ميوه‌هاى‌] آن دو باغ [به آسانى‌] در دسترس است'),
(4956, 55, 55, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4957, 55, 56, 'در آنها دلبرانى هست كه به همسران خود چشم دوزند كه پيش از آنها دست هيچ انس و جنّى به آنها نرسيده است'),
(4958, 55, 57, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4959, 55, 58, 'گويى كه آنها ياقوت و مرجانند'),
(4960, 55, 59, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4961, 55, 60, 'آيا پاداش احسان جز احسان است'),
(4962, 55, 61, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4963, 55, 62, 'و غير از آن دو [باغ‌] دو باغ [ديگر نيز] هست'),
(4964, 55, 63, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4965, 55, 64, '[آن دو باغ‌] چنان سبز است كه به سياهى مى‌زند'),
(4966, 55, 65, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4967, 55, 66, 'در آنها دو چشمه جوشان است'),
(4968, 55, 67, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4969, 55, 68, 'در آن دو، ميوه و خرما و انار است'),
(4970, 55, 69, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4971, 55, 70, 'در آن جا [زنانى‌] نيك سيرت و زيبا رويند'),
(4972, 55, 71, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4973, 55, 72, 'حورانى پرده‌نشين در ميان خيمه‌ها هستند'),
(4974, 55, 73, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4975, 55, 74, 'دست هيچ انس و جنى پيش از ايشان به آنها نرسيده است'),
(4976, 55, 75, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4977, 55, 76, 'بر پشتى‌هاى سبز و بسترهاى زيبا تكيه زده‌اند'),
(4978, 55, 77, 'پس كدامين نعمت‌هاى پروردگارتان را انكار مى‌كنيد'),
(4979, 55, 78, 'پرخير و مبارك است نام پروردگار تو كه شكوهمند و با كرامت است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'هنگامى كه آن واقعه (رستاخيز) رخ دهد'),
(4981, 56, 2, '[خواهيد ديد كه‌] در وقوع آن دروغى نيست'),
(4982, 56, 3, 'پايين آورنده [ى گروهى‌] و بالا برنده [ى گروهى‌] است'),
(4983, 56, 4, 'آن‌گاه كه زمين با تكان سختى لرزانده شود'),
(4984, 56, 5, 'و كوه‌ها [به تمامى‌] خرد و ريز شوند'),
(4985, 56, 6, 'و به صورت غبارى پراكنده گردند'),
(4986, 56, 7, 'و شما سه دسته شويد'),
(4987, 56, 8, 'پس [دسته‌اى‌] اهل سعادت، چه اهل سعادتى'),
(4988, 56, 9, 'و [دسته‌اى‌] تيره بختان، چه تيره بختانى'),
(4989, 56, 10, 'و پيشگامان كه پيشگامند'),
(4990, 56, 11, 'آنها مقربانند'),
(4991, 56, 12, 'در باغ‌هاى پرنعمت [بهشت جاى دارند]'),
(4992, 56, 13, 'بسيارى، از امت‌هاى نخستين'),
(4993, 56, 14, 'و اندكى، از متأخرانند (امت اسلام)'),
(4994, 56, 15, 'بر تخت‌هايى زربافت جواهر نشان'),
(4995, 56, 16, 'روياروى هم بر آنها تكيه داده‌اند'),
(4996, 56, 17, 'بر گردشان پسرانى هميشگى [به خدمت‌] مى‌گردند'),
(4997, 56, 18, 'با جام‌ها و ابريق‌ها و پياله‌اى از مى روان'),
(4998, 56, 19, 'كه از آن نه سر درد گيرند و نه مست و بى‌خرد شوند'),
(4999, 56, 20, 'و ميوه از هر چه اختيار كنند'),
(5000, 56, 21, 'و از گوشت پرنده هر چه بخواهند'),
(5001, 56, 22, 'و حوران درشت چشم'),
(5002, 56, 23, 'همچو مرواريد پنهان در صدف'),
(5003, 56, 24, '[اينها] پاداشى است به خاطر آنچه مى‌كردند'),
(5004, 56, 25, 'در آن جا نه سخن بيهوده شنوند و نه گناه آلود'),
(5005, 56, 26, 'مگر سخنى كه سلام است و سلام'),
(5006, 56, 27, 'و اهل سعادت، چه اهل سعادتى'),
(5007, 56, 28, 'در [زير] درختان سدر بى‌خارند'),
(5008, 56, 29, 'و درختان موز كه ميوه‌اش بر هم چيده است'),
(5009, 56, 30, 'و سايه‌اى گسترده'),
(5010, 56, 31, 'و [كنار] آبشارها'),
(5011, 56, 32, 'و ميوه‌هاى فراوان'),
(5012, 56, 33, 'نه تمام شدنى و نه منع شدنى'),
(5013, 56, 34, 'و همسرانى بلند [مرتبه‌]'),
(5014, 56, 35, 'همانا آن زنان را به آفرينشى نو آفريديم'),
(5015, 56, 36, 'و آنان را دوشيزه قرار داديم'),
(5016, 56, 37, 'عشق ورزان [به همسر] و هم سنّ و سالند'),
(5017, 56, 38, '[اينها همه‌] براى اهل سعادت است'),
(5018, 56, 39, 'كه بسيارى از امت‌هاى نخستين'),
(5019, 56, 40, 'و بسيارى از امت‌هاى متأخرند'),
(5020, 56, 41, 'و تيره بختان چه تيره بختانى'),
(5021, 56, 42, 'در [عذاب‌] باد سوزان و آب جوشانند'),
(5022, 56, 43, 'و سايه‌اى از دود سياه'),
(5023, 56, 44, 'نه خنك است و نه خوش'),
(5024, 56, 45, 'همانا آنان پيش از اين خوشگذران [و غافل از آخرت‌] بودند'),
(5025, 56, 46, 'و بر گناه بزرگ اصرار مى‌ورزيدند'),
(5026, 56, 47, 'و مى‌گفتند: آيا وقتى مرديم و خاك و استخوان شديم، واقعا ما برانگيخته مى‌شويم'),
(5027, 56, 48, 'همچنين نياكان ما'),
(5028, 56, 49, 'بگو: يقينا اولين و آخرين'),
(5029, 56, 50, 'بى‌ترديد جملگى در آن وعده‌گاه روز معلوم گردآورده مى‌شوند'),
(5030, 56, 51, 'آن‌گاه شما اى گمراهان تكذيب كننده'),
(5031, 56, 52, 'قطعا از درختى كه از زقّوم است خواهيد خورد'),
(5032, 56, 53, 'كه شكم‌ها از آن پر مى‌كنيد'),
(5033, 56, 54, 'و بر سر آن از آب جوشان مى‌نوشيد'),
(5034, 56, 55, 'پس مى‌نوشيد چون نوشيدن اشتران عطش زده'),
(5035, 56, 56, 'اين است پذيرايى آنان در روز جزا'),
(5036, 56, 57, 'ما شما را آفريده‌ايم، پس چرا [آفرينش جديد را] تصديق نمى‌كنيد'),
(5037, 56, 58, 'آيا آنچه را [كه به صورت نطفه‌] فرو مى‌ريزيد ملاحظه كرده‌ايد'),
(5038, 56, 59, 'آيا شما آن را خلق مى‌كنيد يا ما آفريننده‌ايم'),
(5039, 56, 60, 'ماييم كه ميان شما مرگ را مقدر كرده‌ايم و ما ناتوان نيستيم'),
(5040, 56, 61, 'بر اين كه امثالثان را به جاى شما قرار دهيم و شما را در جهان ديگرى كه نمى‌دانيد آفرينش تازه‌اى بخشيم'),
(5041, 56, 62, 'و همانا شما پيدايش نخستين خود را دانسته‌ايد [و زنده شدن دوباره مثل آن است‌]، پس چرا توجه نمى‌كنيد'),
(5042, 56, 63, 'آيا آنچه را كه مى‌كاريد ملاحظه كرده‌ايد'),
(5043, 56, 64, 'آيا شما آن را مى‌رويانيد يا ما روياننده‌ايم'),
(5044, 56, 65, 'اگر مى‌خواستيم قطعا آن را خاشاك مى‌كرديم پس در شگفت مى‌شديد'),
(5045, 56, 66, '[و مى‌گفتيد:] واقعا ما زيان ديده‌ايم'),
(5046, 56, 67, 'بلكه ما [به كلى از رزق‌] محروم شده‌ايم'),
(5047, 56, 68, 'آيا آبى را كه مى‌نوشيد ديده‌ايد'),
(5048, 56, 69, 'آيا شما آن را از ابر فرو فرستاده‌ايد يا ما فرو فرستنده‌ايم'),
(5049, 56, 70, 'اگر مى‌خواستيم آن را تلخ مى‌گردانديم پس چرا شكر نمى‌كنيد'),
(5050, 56, 71, 'آيا آن آتشى را كه بر مى‌افروزيد ديده‌ايد'),
(5051, 56, 72, 'آيا درختش را شما پديد آورده‌ايد يا ما پديد آورنده‌ايم'),
(5052, 56, 73, 'ما آن را يادآورى [براى آتش قيامت‌] و كالايى براى مسافران [و جويندگان آتش‌] قرار داده‌ايم'),
(5053, 56, 74, 'پس به نام پروردگار بزرگ خود تسبيح گوى'),
(5054, 56, 75, 'پس سوگند به جايگاه‌هاى ستارگان'),
(5055, 56, 76, 'و البته اگر مى‌دانستيد، اين سوگندى بزرگ است'),
(5056, 56, 77, 'قطعا اين قرآنى كريم است'),
(5057, 56, 78, 'كه در كتابى مستور [لوحى محفوظ] جاى دارد'),
(5058, 56, 79, 'كه جز پاكان بر آن دست نيازند'),
(5059, 56, 80, 'نازل شده‌اى از جانب پروردگار جهانيان است'),
(5060, 56, 81, 'پس آيا شما اين سخن را سبك مى‌گيريد'),
(5061, 56, 82, 'و نصيب خود را اين قرار مى‌دهيد كه آن را تكذيب كنيد'),
(5062, 56, 83, 'پس چرا وقتى كه [جان‌] به گلوگاه مى‌رسد'),
(5063, 56, 84, 'و شما در آن هنگام [تنها] نظاره گريد'),
(5064, 56, 85, 'و ما به آن [محتضر] از شما نزديك‌تريم و ليكن شما نمى‌بينيد'),
(5065, 56, 86, 'پس اگر شما مجازات شدنى نيستيد [و معادى در كار نيست‌]'),
(5066, 56, 87, 'چرا او را بر نمى‌گردانيد اگر راست مى‌گوييد'),
(5067, 56, 88, 'و امّا اگر [محتضر] از مقربان باشد'),
(5068, 56, 89, 'آسايش و راحتى و بهشت پرنعمت [براى او] است'),
(5069, 56, 90, 'و امّا اگر از اهل سعادت باشد'),
(5070, 56, 91, 'پس [به او گفته شود:] تو را از جانب اهل سعادت سلامى است'),
(5071, 56, 92, 'و امّا اگر از تكذيب كنندگان گمراه باشد'),
(5072, 56, 93, 'پس [وى را] ضيافتى از آب جوشان'),
(5073, 56, 94, 'و به دوزخ در آوردن است'),
(5074, 56, 95, 'بى‌ترديد، اين همان حقيقت يقينى است'),
(5075, 56, 96, 'پس به نام پروردگار بزرگت تسبيح گوى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمان‌ها و زمين است خدا را به پاكى مى‌ستايند و او مقتدر حكيم است'),
(5077, 57, 2, 'فرمانروايى آسمان‌ها و زمين از آن اوست، زنده مى‌كند و مى‌ميراند و او بر هر چيزى تواناست'),
(5078, 57, 3, 'اوست اول و آخر و ظاهر و باطن، و او به هر چيزى داناست'),
(5079, 57, 4, 'او كسى است كه آسمان‌ها و زمين را در شش روز آفريد، سپس [براى تدبير جهان‌] به عرش پرداخت. آنچه در زمين فرو رود و آنچه از آن بيرون آيد و آنچه از آسمان فرود آيد و آنچه در آن بالا رود [همه‌] را مى‌داند، و هر كجا باشيد او با شماست، و خدا به آنچه مى‌كنيد بين'),
(5080, 57, 5, 'فرمانروايى آسمان‌ها و زمين از آن اوست، و كارها به سوى خدا بازگردانده مى‌شود'),
(5081, 57, 6, 'شب را در روز فرو مى‌برد [و روز بلند مى‌شود] و روز را در شب فرو مى‌برد [و شب بلند مى‌گردد]، و او به راز سينه‌ها داناست'),
(5082, 57, 7, 'به خدا و پيامبر او ايمان آوريد و از اموالى كه شما را در آن جانشين [خود] قرار داده انفاق كنيد پس كسانى از شما كه ايمان آوردند و انفاق كردند برايشان پاداش بزرگى است'),
(5083, 57, 8, 'و شما را چه شده است كه به خدا ايمان نمى‌آوريد و حال آن كه پيامبر، شما را دعوت مى‌كند تا به پروردگارتان ايمان آوريد؟ در حالى كه [خدا] از شما پيمان گرفته است، اگر مؤمن هستيد'),
(5084, 57, 9, 'او كسى است كه بر بنده‌ى خود آياتى روشن فرو مى‌فرستد تا شما را از تاريكى‌ها به روشنايى در آورد، و به راستى خداوند به شما سخت رئوف و مهربان است'),
(5085, 57, 10, 'و شما را چه شده است كه در راه خدا انفاق نمى‌كنيد و حال آن كه ميراث آسمان‌ها و زمين به خدا تعلق دارد؟ كسانى از شما كه پيش از فتح [مكه‌] انفاق و جهاد كرده‌اند [با ديگران‌] يكسان نيستند. آنها از حيث درجه بزرگ‌تر از كسانى‌اند كه بعدا به انفاق و جهاد پردا'),
(5086, 57, 11, 'كيست آن كس كه به خدا وام دهد تا آن را براى وى دو چندان كند و او را [در آخرت‌] پاداشى كريمانه باشد'),
(5087, 57, 12, 'روزى كه مردان و زنان مؤمن را مى‌بينى كه نورشان پيشاپيش آنها و به جانب راستشان شتابان مى‌رود، [به آنها گفته مى‌شود:] مژده‌ى شما امروز باغ‌هايى است كه از پاى درختانش نهرها جارى است، [و] در آنها جاويدانيد. اين همان كاميابى بزرگ است'),
(5088, 57, 13, 'آن روز كه مردان و زنان منافق به كسانى كه ايمان آورده‌اند گويند: نظرى به ما بيفكنيد تا از نورتان فروغى برگيريم. گفته شود: به پشت سرتان [دنيا] بازگرديد و از آن جا نورى بطلبيد. آن‌گاه ميان آنها ديوارى زده شود كه آن را درى است كه اندرون آن رحمت است و بير'),
(5089, 57, 14, '[منافقان‌] آنها را ندا دهند: آيا ما با شما نبوديم؟ مى‌گويند: چرا، ولى شما خود را در فتنه و گرفتارى افكنديد و به انتظار نشستيد و ترديد كرديد و آرزوها شما را فريفت، تا فرمان خدا [و مرگتان‌] فرا رسيد و فريبكار، شما را در باره‌ى خدا فريفت'),
(5090, 57, 15, 'پس امروز نه از شما و نه از كسانى كه كافر شده‌اند عوضى پذيرفته نمى‌شود. جايگاهتان آتش است و همان سزاوار شما است، و بد سر انجامى است'),
(5091, 57, 16, 'آيا براى كسانى كه ايمان آورده‌اند وقت آن نرسيده است كه دل‌هايشان به ياد خدا و آن حقيقتى كه نازل شده (قرآن) نرم و خاشع گردد و مانند كسانى نباشند كه در گذشته كتابشان داده شد پس زمان بر آنها طولانى شد و دل‌هايشان را قساوت گرفت و بسيارى از آنها فاسق بودن'),
(5092, 57, 17, 'بدانيد كه خدا زمين را پس از مرگش زنده مى‌گرداند. به تحقيق ما آيات [خود] را براى شما آشكار گردانديم، باشد كه بينديشيد'),
(5093, 57, 18, 'همانا مردان و زنان صدقه دهنده و [آنان كه‌] به خدا وام نيكو دادند، دو چندان داده مى‌شوند و برايشان [در آخرت‌] اجرى كريمانه است'),
(5094, 57, 19, 'و كسانى كه به خدا و پيامبران او ايمان آوردند، آنها صديقان و گواهان نزد پروردگارند كه اجر و نورشان براى آنها [محفوظ] است، و كسانى كه كافر شدند و آيات ما را تكذيب كردند، آنها اهل آتشند'),
(5095, 57, 20, 'بدانيد كه زندگى دنيا بازى و سرگرمى و زينت و فخرفروشى شما به يكديگر و فزون خواهى در اموال و فرزندان است. [مثل آنها] مثل بارانى است كه رويش [گياهان‌] آن كشاورزان را به شگفت آورد، سپس خشك و پژمرده شود و ببينى كه زرد گشته و آن‌گاه خاشاك شود، و در آخرت [د'),
(5096, 57, 21, 'به سوى آمرزشى از پروردگارتان و بهشتى كه پهناى آن چون پهناى آسمان و زمين است بر يكديگر پيشى گيريد. [اين بهشت‌] براى كسانى آماده شده كه به خدا و پيامبرانش ايمان آورده‌اند. اين فضل خداست كه به هر كس كه خواهد مى‌دهد و خداوند صاحب فضل بزرگ است'),
(5097, 57, 22, 'هيچ مصيبتى نه در زمين و نه در جانتان روى ندهد مگر پيش از آن كه آن را پديد آوريم در كتابى ثبت است. همانا اين [كار] بر خدا آسان است'),
(5098, 57, 23, 'تا بر آنچه از دست شما رفته است اندوه نخوريد و بدانچه به شما داده [دلبسته و] سرمست نباشيد، و خدا هيچ خودپسند فخرفروشى را دوست نمى‌دارد'),
(5099, 57, 24, 'همان كسانى كه بخل مى‌ورزند و مردم را به بخل وا مى‌دارند. و هر كه [از فرمان‌] روى گرداند، قطعا خدا بى‌نياز ستوده است'),
(5100, 57, 25, 'به راستى [ما] پيامبران خود را با دلايل آشكار فرستاديم و با آنها كتاب و ميزان [حق و باطل‌] نازل نموديم تا مردم به عدل و انصاف برخيزند، و آهن را كه در آن نيرويى شديد و منافعى براى مردم است پديد آورديم، و تا خدا معلوم بدارد چه كسى در نهان، او و پيامبران'),
(5101, 57, 26, 'و همانا نوح و ابراهيم را فرستاديم و در ميان فرزندان آن دو نبوت و كتاب نهاديم، پس برخى از آنها هدايت يافته و بسيارى از آنان فاسق بودند'),
(5102, 57, 27, 'آن‌گاه به دنبال آنان پيامبران خود را پى‌درپى آورديم، و عيسى پسر مريم را از پى [آنها] فرستاديم و او را انجيل داديم، و در دل‌هاى كسانى كه از او پيروى كردند رأفت و رحمت نهاديم و ترك دنيايى كه از خود ساختند ما آن را بر ايشان مقرر نكرديم [و آنها] جز به طل'),
(5103, 57, 28, 'اى كسانى كه ايمان آورده‌ايد! از خدا پروا داريد و به پيامبر او ايمان آوريد تا دو سهم از رحمتش به شما ببخشد و براى شما نورى قرار دهد كه بدان راه [راست‌] رويد و شما را بيامرزد، و خداوند آمرزنده‌ى مهربان است'),
(5104, 57, 29, '[خدا به مؤمنان رحمت مضاعف داد] تا اهل كتاب [اين طور] ندانند كه مؤمنان به چيزى از فضل خدا دست نمى‌يابند و بى‌ترديد فضل و بخشش به دست خداست، آن را به هر كه خواهد عطا مى‌كند، و خدا صاحب فضل و بخشش بزرگ است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'قطعا خدا سخن زنى را كه در باره‌ى شوهرش با تو مجادله مى‌كرد و به خدا شكايت مى‌نمود شنيد، و خدا گفت و گوى شما را مى‌شنود. همانا خدا شنواى بيناست'),
(5106, 58, 2, 'از شما كسانى كه زنانشان را «ظهار» مى‌كنند [و مى‌گويند: تو نسبت به من به منزله مادرم هستى‌] آنها مادرانشان نيستند. مادرانشان تنها كسانى‌اند كه ايشان را زاده‌اند، و قطعا آنها سخنى زشت و باطل مى‌گويند، و البته خدا باگذشت و آمرزنده است'),
(5107, 58, 3, 'و كسانى كه زنانشان را ظهار مى‌كنند، سپس از گفته خود باز مى‌گردند، بايد پيش از آن كه آميزش كنند، بنده‌اى را آزاد نمايند. اين [حكمى‌] است كه بدان پند داده مى‌شويد، و خدا بدانچه مى‌كنيد آگاه است'),
(5108, 58, 4, 'و هر كه [برده‌اى‌] نيابد، بايد پيش از آميزش، دو ماه پياپى روزه بدارد، و هر كس نتواند، شصت مستمند را اطعام كند. اين [حكم‌] براى آن است كه به خدا و پيامبرش ايمان بياوريد، و اين حدود خداست، و براى كافران عذابى دردناك است'),
(5109, 58, 5, 'بى‌گمان كسانى كه با خدا و رسولش دشمنى و مخالفت مى‌كنند خوار و ذليل مى‌شوند، چنان كه پيشينيان آنها خوار و ذليل شدند و همانا آيات روشن [خود] را فرستاده‌ايم، و كافران را عذابى خفت‌بار است'),
(5110, 58, 6, 'روزى كه خداوند همه آنان را برانگيزد و بدانچه كرده‌اند آگاهشان سازد. خدا حساب آن [اعمال‌] را نگه داشته و آنها فراموش كرده‌اند، و خدا بر هر چيزى گواه است'),
(5111, 58, 7, 'آيا ندانسته‌اى كه خدا آنچه را در آسمان‌ها و آنچه را در زمين است مى‌داند؟ [هيچ‌گاه‌] سه نفر با هم نجوا نمى‌كنند مگر اين كه او چهارمين آنهاست، و نه پنج نفر مگر اين كه او ششمين آنهاست، و نه كمتر از آن و نه بيشتر مگر اين كه هر كجا باشند او با آنهاست. آن‌گ'),
(5112, 58, 8, 'آيا به كسانى كه از نجوا منع شده بودند نظر نيفكندى كه باز بدانچه از آن منع شده‌اند بر مى‌گردند و به [منظور] گناه و تعدى و مخالفت پيامبر با يكديگر نجوا مى‌كنند و چون نزد تو مى‌آيند تو را بدان شيوه كه خدا سلام و تحيّت نگفته است سلام مى‌دهند و در دل خود م'),
(5113, 58, 9, 'اى كسانى كه ايمان آورده‌ايد! چون با يكديگر نجوا مى‌كنيد، در باب گناه و تعدى و نافرمانى پيامبر نجوا نكنيد، و به نيكوكارى و پرهيزكارى نجوا كنيد، و از خدايى كه به سوى او محشور مى‌شويد پروا بداريد'),
(5114, 58, 10, 'جز اين نيست كه نجوا از شيطان است، تا كسانى را كه ايمان آورده‌اند محزون كند، ولى جز به اذن خدا هيچ آسيبى به آنها نمى‌رساند، و مؤمنان بايد تنها بر خدا توكل كنند'),
(5115, 58, 11, 'اى كسانى كه ايمان آورده‌ايد! هرگاه به شما گفته شد در مجلس‌ها [براى برادرانتان‌] جاى باز كنيد، جاى باز كنيد تا خدا براى شما [در بهشت‌] جاى بگشايد. و چون گفته شد برخيزيد، برخيزيد تا خدا كسانى از شما را كه ايمان آورده و كسانى را كه دانش داده شده‌اند رتب'),
(5116, 58, 12, 'اى كسانى كه ايمان آورده‌ايد! چون [خواستيد] با پيامبر نجوا كنيد، پيش از نجوايتان صدقه‌اى تقديم داريد. اين [كار] براى شما بهتر و پاكيزه‌تر است پس اگر چيزى نيافتيد، بى‌گمان خدا آمرزنده‌ى مهربان است'),
(5117, 58, 13, 'آيا [از فقر] ترسيديد كه پيش از نجوايتان صدقه‌هايى بدهيد؟ پس حالا كه عمل نكرديد و خدا هم بر شما بخشود، نماز برپا داريد و زكات دهيد و خدا و رسول او را اطاعت كنيد، و خدا به آنچه مى‌كنيد آگاه است'),
(5118, 58, 14, 'آيا نديدى كسانى را كه قومى را دوست گرفتند كه خدا بر آنها خشم گرفته است؟ اينان نه از شما هستند و نه از آنها، و به دروغ سوگند مى‌خورند [كه با شمايند] و خود مى‌دانند'),
(5119, 58, 15, 'خدا براى آنها عذابى سخت فراهم كرده است. به راستى آنها بد عمل مى‌كردند'),
(5120, 58, 16, 'سوگندهاى خود را سپرى گرفتند و [مردم را] از راه خدا بازداشتند، پس براى ايشان عذابى خفت‌بار است'),
(5121, 58, 17, 'در برابر خدا نه اموالشان و نه اولادشان چيزى [از عذاب‌] را از آنها برطرف نمى‌سازد. آنها ياران آتشند و در آن جاودانن'),
(5122, 58, 18, 'روزى كه خدا همه آنها را برمى‌انگيزد، پس براى خدا سوگند مى‌خورند، چنان كه [امروز] براى شما سوگند مى‌خورند [كه مسلمانند]، و مى‌پندارند كه بر چيزى هستند [يا اعتبارى دارند]. آگاه باش كه آنها همان دروغگويانند'),
(5123, 58, 19, 'شيطان بر آنها چيره شده و خدا را از يادشان برده است. آنها حزب شيطانند. آگاه باش كه حزب شيطان همان زيانكارانند'),
(5124, 58, 20, 'بى‌ترديد، كسانى كه با خدا و رسولش دشمنى و مخالفت مى‌كنند، آنها در [زمره‌ى‌] خوارترين افرادند'),
(5125, 58, 21, 'خدا مقرر داشته است كه: قطعا پيروز مى‌شوم من و رسولانم [نيز]. همانا خداوند، قوى و شكست ناپذير است'),
(5126, 58, 22, 'هيچ قومى را نيابى كه به خدا و روز واپسين ايمان آورده باشند در حالى كه با كسانى كه با خدا و رسولش مخالفت كرده‌اند دوستى كنند، هر چند پدرانشان يا پسرانشان يا برادرانشان يا عشيره‌ى آنها باشند. آنانند كه [خدا] ايمان را در دل‌هايشان رقم زده و با روحى از ج');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمان‌ها و آنچه در زمين است خدا را تسبيح مى‌گويند، و او شكست ناپذير حكيم است'),
(5128, 59, 2, 'اوست آن كه كافران اهل كتاب (بنى نضير) را براى نخستين تبعيد دسته جمعى از خانه‌هايشان بيرون راند. شما گمان نمى‌كرديد كه [به اين آسانى‌] بيرون روند، و خودشان هم مى‌پنداشتند كه قلعه‌هايشان نگهدارشان از [خشم‌] خداست، اما خدا از آن جايى كه تصور نمى‌كردند ب'),
(5129, 59, 3, 'و اگر خدا جلاى وطن را بر آنها مقرر نكرده بود، قطعا آنها را در دنيا عذاب مى‌كرد، و آنها را در آخرت عذاب آتش است'),
(5130, 59, 4, 'اين [جلاى وطن‌] براى آن بود كه آنها با خدا و رسولش در افتادند، و هر كس با خدا درافتد، [بداند كه‌] خدا سخت كيفر است'),
(5131, 59, 5, 'هر نخله‌اى را كه قطع كرديد يا آن را بر ريشه‌هايش ايستاده وا نهاديد، همه به خواست خدا بود، و تا فاسقان را خوار كند'),
(5132, 59, 6, 'و آنچه را خدا از [اموال‌] آنان (يهوديان) به رسم «فيى‌ء» عايد پيامبر خود گردانيد، شما [براى به دست آوردن آن‌] هيچ اسب و شترى بر آن نتاختيد [پس در آن حقى نداريد] و ليكن خداست كه فرستادگان خود را بر هر كه بخواهد چيره مى‌گرداند، و خدا بر هر كارى تواناست'),
(5133, 59, 7, 'آنچه خدا [به صورت فيى‌ء] از اموال ساكنان آن قريه‌ها عايد پيامبرش گردانيد، خاص خدا و پيامبر و خويشاوندان [او] و يتيمان و مستمندان و در راه مانده است، تا ميان توانگران شما دست به دست نگردد. و آنچه را فرستاده [ى او] به شما داد بگيريد و از آنچه شما را باز'),
(5134, 59, 8, '[و نيز اين غنايم‌] براى مهاجران نيازمندى است كه از ديارشان و اموالشان رانده شده‌اند و در طلب فضل خدا و رضاى اويند و خدا و رسولش را يارى مى‌كنند. هم اينان راستگويانند'),
(5135, 59, 9, 'و كسانى [از انصار] كه پيش از آنها خانه و ايمان [خويش‌] را آماده كردند، كسانى را كه به سويشان هجرت كردند دوست دارند، و نسبت به آنچه به آنها داده شده در دل خود احساس نياز نمى‌كنند و [آنها را] بر خود مقدم مى‌دارند، هر چند خود بدان نياز مبرمى داشته باشند'),
(5136, 59, 10, 'و كسانى كه پس از آنان [و بعد از فتح مكه‌] آمدند، مى‌گويند: پروردگارا! ما و آن برادران ما را كه در ايمان بر ما پيشى گرفتند بيامرز، و در دل‌هايمان نسبت به كسانى كه ايمان آورده‌اند [هيچ گونه‌] كينه‌اى مگذار. پروردگارا! به راستى تو رئوف و مهربانى'),
(5137, 59, 11, 'آيا كسانى را كه به نفاق برخاستند نديدى كه به برادران كافرشان از اهل كتاب مى‌گفتند: اگر شما اخراج شديد، حتما با شما بيرون خواهيم آمد و در باره‌ى شما هرگز از كسى فرمان نخواهيم برد، و اگر با شما جنگيدند حتما شما را يارى خواهيم كرد؟ و خدا گواهى مى‌دهد ك'),
(5138, 59, 12, 'اگر [يهود] اخراج شوند، آنها با ايشان بيرون نخواهند رفت، و اگر با آنان جنگى شود آنها را يارى نخواهند كرد، و اگر هم ياريشان كنند حتما پشت مى‌كنند [و تنهايشان مى‌گذارند] و آن‌گاه يارى نمى‌شوند'),
(5139, 59, 13, 'البته شما در دل‌هاى آنان از خداوند پر مهابت‌تريد، چرا كه آنها مردمى هستند كه نمى‌فهمند'),
(5140, 59, 14, 'آنها با شما به صورت گروهى [و در يك صف متحد] نمى‌جنگند مگر در قريه‌هاى قلعه‌دار يا از پس ديوارها. زورمندى ايشان در ميان خودشان سخت است [اما در مقابل شما ضعيفند]. آنها را متحد مى‌پندارى ولى دل‌هايشان پراكنده است. اين بدان سبب است كه آنان مردمى هستند ك'),
(5141, 59, 15, 'درست مانند كسانى كه چندى پيش از آنها [در واقعه بدر] سزاى كار خود را چشيدند، و براى آنها عذابى دردناك است'),
(5142, 59, 16, '[وعده‌ى منافقان به يهوديان‌] همچون حكايت شيطان است آنگاه كه به آدمى گويد: كافر شو! و چون كافر شود، گويد: من از تو بيزارم، زيرا من از خدا، پروردگار جهانيان مى‌ترسم'),
(5143, 59, 17, 'پس فرجام آن دو اين است كه هر دو جاودانه در آتش باشند، و سزاى ستمگران همين است'),
(5144, 59, 18, 'اى كسانى كه ايمان آورده‌ايد! از خدا پروا داريد، و انسان بايد بنگرد كه براى فردا چه پيش فرستاده است، و از خدا بترسيد. بى‌ترديد خدا بدانچه مى‌كنيد آگاه است'),
(5145, 59, 19, 'و مانند كسانى نباشيد كه خدا را فراموش كردند پس خدا هم خودشان را از يادشان برد. آنان همان فاسقانند'),
(5146, 59, 20, 'دوزخيان با بهشتيان يكسان نيستند، بهشتيانند كه كاميابند'),
(5147, 59, 21, 'اگر اين قرآن را بر كوهى نازل مى‌كرديم، بى‌شك آن را از بيم خدا خاكسار و متلاشى مى‌ديدى، و اين مثل‌ها را براى مردم مى‌زنيم، باشد كه آنها فكر كنند'),
(5148, 59, 22, 'او خدايى است كه غير از او معبودى نيست، داننده‌ى نهان و آشكار است، اوست هستى بخش مهربان'),
(5149, 59, 23, 'او خدايى است كه جز او معبودى نيست، فرمانرواست، پاك است، عارى از هر نقصى است، ايمنى بخش است، نگاهبان، شكست ناپذير، قدرت مدار و با كبرياست. خداوند از هر چه شريك او كنند منزه است'),
(5150, 59, 24, 'او خداى خالق هستى بخش صورتگر است كه بهترين نام‌ها و صفات از آن اوست. آنچه در آسمان‌ها و زمين است [جمله‌] تسبيح او گويند، و او شكست ناپذير حكيم است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اى كسانى كه ايمان آورده‌ايد! دشمن من و دشمن خودتان را دوست نگيريد- كه نسبت به آنها اظهار محبت كنيد در حالى كه قطعا به آن حقيقت كه براى شما آمده [يعنى قرآن‌] كافر شدند و به اين علت كه به خدا، پروردگارتان ايمان آورده‌ايد، پيامبر و شما را [از ديارتان‌] ب'),
(5152, 60, 2, 'اگر بر شما دست يابند، دشمن شما خواهند بود و به آزارتان دست مى‌يازند و زبانشان را به سوى شما به بدى مى‌گشايند و دوست دارند كه شما [نيز] كافر شويد'),
(5153, 60, 3, 'روز قيامت نه خويشان شما و نه فرزندانتان هرگز به شما سودى نمى‌رسانند. [خدا] ميان شما جدايى مى‌اندازد و خداوند به آنچه مى‌كنيد بيناست'),
(5154, 60, 4, 'قطعا براى شما در [حالات‌] ابراهيم و كسانى كه با او بودند سرمشق خوبى است، آن گاه كه به قوم خود گفتند: ما از شما و از آنچه به جاى خدا مى‌پرستيد بيزاريم و [آيين‌] شما را منكريم، و ميان ما و شما براى هميشه دشمنى و كينه پديد آمده است تا وقتى كه به خداى يگا'),
(5155, 60, 5, 'پروردگارا! ما را مورد آزار و آزمايش كسانى كه كافر شدند قرار مده و ما را بيامرز، پروردگارا! به درستى كه تو مقتدر و حكيمى'),
(5156, 60, 6, 'قطعا براى شما در [رفتار] آنها سرمشقى نيكوست، براى كسى كه به خدا و روز واپسين اميد دارد. و هر كه روى برتابد [به خود زيان زده است‌] زيرا خداوند خود بى‌نياز ستوده است'),
(5157, 60, 7, 'اميد است كه خدا ميان شما و كسانى از آنان كه [ايشان را] دشمن داشتيد [با اسلام آوردنشان‌] دوستى برقرار كند، و خدا تواناست، و خدا آمرزنده‌ى مهربان است'),
(5158, 60, 8, 'خداوند شما را از نيكى كردن و دادگرى با كسانى كه در [امر] دين با شما نجنگيده و شما را از ديارتان بيرون نكرده‌اند منعت نمى‌كند. بى‌ترديد خداوند دادگران را دوست مى‌دارد'),
(5159, 60, 9, 'خدا شما را تنها از [دوستى و رابطه با] كسانى باز مى‌دارد كه در [امر] دين با شما جنگيدند و شما را از خانه‌هايتان بيرون راندند و در بيرون راندنتان با يكديگر همدستى كردند. و هر كس با آنها دوستى كند آنان همان ستمگرانند'),
(5160, 60, 10, 'اى كسانى كه ايمان آورده‌ايد! وقتى زنان با ايمان به عنوان هجرت نزد شما آمدند امتحانشان كنيد. خدا به [حقيقت‌] ايمانشان داناتر است. پس اگر آنها را مؤمن دانستيد آنها را به سوى كافران باز نگردانيد. نه اينان براى كفار حلالند و نه آنها براى اينان حلال و هر'),
(5161, 60, 11, 'و اگر يكى از همسران شما از دستتان رفت و به كفار پيوست و [آنها مهر مورد مطالبه شما را ندادند و شما از كفار] غنيمت يافتيد، پس به كسانى كه همسرانشان رفته‌اند معادل آنچه خرج كرده‌اند [از غنايم‌] بپردازيد. و از خدايى كه به او ايمان داريد بترسيد'),
(5162, 60, 12, 'اى پيامبر! چون زنان با ايمان نزد تو آيند و با تو بيعت كنند كه چيزى را با خدا شريك نسازند و دزدى و زنا نكنند و فرزندان خود را نكشند و فرزندى نامشروع پيش دست و پاى خود نياورند كه به دروغ [به شوهران خود] ببندند، و در كار نيك نافرمانى تو نكنند، با آنها ب'),
(5163, 60, 13, 'اى كسانى كه ايمان آورده‌ايد! مردمى را كه خدا بر آنها خشم گرفته به دوستى مگيريد. همانا از آخرت نوميدند [چون منكر آنند]، همان گونه كه كافران از [باز آمدن‌] اهل قبور نوميدند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمان‌ها و آنچه در زمين است همه تسبيح خدا مى‌گويند، و او مقتدر حكيم است'),
(5165, 61, 2, 'اى كسانى كه ايمان آورده‌ايد! چرا چيزى را مى‌گوييد كه عمل نمى‌كنيد'),
(5166, 61, 3, 'نزد خدا سخت مبغوض است كه چيزى را بگوييد كه عمل نمى‌كنيد'),
(5167, 61, 4, 'همانا خداوند كسانى را كه در راه او صف در صف جهاد مى‌كنند چنانكه گويى بنايى آهنينند دوست مى‌دارد'),
(5168, 61, 5, 'و [ياد كن‌] هنگامى را كه موسى به قوم خود گفت: اى قوم من! چرا آزارم مى‌دهيد در حالى كه مى‌دانيد من فرستاده‌ى خدا به سوى شمايم. پس چون [از حق‌] برگشتند، خدا هم دل‌هايشان را برگرداند، و خدا مردم نافرمان را هدايت نمى‌كند'),
(5169, 61, 6, 'و هنگامى كه عيسى پسر مريم گفت: اى فرزندان اسرائيل! بى‌ترديد من فرستاده‌ى خدا به سوى شمايم، در حالى كه آنچه را پيش از من آمده كه تورات است تصديق مى‌كنم و به رسولى كه پس از من مى‌آيد و نامش احمد است بشارت مى‌دهم، ولى همين كه [آن رسول‌] با دلايل روشن سرا'),
(5170, 61, 7, 'و كيست ستمكارتر از آن كس كه بر خدا دروغ ببندد در حالى كه به اسلام دعوت مى‌شود؟ و خدا مردم ستمگر را هدايت نمى‌كند'),
(5171, 61, 8, 'مى‌خواهند نور خدا را با دهان‌هاى خود خاموش كنند، ولى خدا كامل‌كننده‌ى نور خويش است، هر چند كافران خوش نداشته باشند'),
(5172, 61, 9, 'اوست كه رسول خود را با هدايت و آيين درست فرستاد تا آن را بر همه اديان چيره سازد، هر چند مشركان را ناخوش آيد'),
(5173, 61, 10, 'اى كسانى كه ايمان آورده‌ايد! آيا شما را بر تجارتى راه نمايم كه شما را از عذابى دردناك مى‌رهاند'),
(5174, 61, 11, 'به خدا و رسولش ايمان مى‌آوريد و در راه خدا با مال و جانتان جهاد مى‌كنيد. اين براى شما بهتر است، اگر مى‌دانستيد'),
(5175, 61, 12, 'تا گناهانتان را بر شما ببخشايد و شما را به باغ‌هايى كه از پاى درختانش نهرها جارى است و به سراهاى دلپسند در بهشت‌هاى جاويدان در آورد كه اين همان كاميابى بزرگ است'),
(5176, 61, 13, 'و [نعمت‌] ديگرى كه دوستش داريد، نصرتى از جانب خدا و فتحى نزديك است و مؤمنان را بشارت ده'),
(5177, 61, 14, 'اى كسانى كه ايمان آورده‌ايد! ياران خدا باشيد، همان گونه كه عيسى بن مريم به حواريون گفت: ياران من در راه خدا چه كسانى‌اند؟ حواريون گفتند: ما ياران خداييم. پس گروهى از بنى اسرائيل ايمان آوردند و گروهى كافر شدند، و كسانى را كه ايمان آوردند بر دشمنانشا');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمان‌ها و آنچه در زمين است تسبيح خدا مى‌گويند، خدايى كه فرمانرواى پاك و شكست ناپذير حكيم است'),
(5179, 62, 2, 'اوست آن كه در ميان درس ناخوانده‌ها پيامبرى از خودشان برانگيخت كه آيات او را بر آنها مى‌خواند و پاكشان مى‌سازد و كتاب و حكمتشان مى‌آموزد، و حقا كه از پيش در ضلالت آشكارى بودند'),
(5180, 62, 3, 'و گروه ديگرى از ايشان را نيز كه هنوز به آنها نپيوسته‌اند، [تعليم مى‌دهد] و او مقتدر حكيم است'),
(5181, 62, 4, 'اين [رسالت‌] فضل خداست، آن را به هر كه بخواهد عطا مى‌كند و خدا صاحب فضل بزرگ است'),
(5182, 62, 5, 'مثل كسانى كه به [پيروى از] از تورات مكلف شدند سپس به آن عمل نكردند، همچون مثل درازگوشى است كه كتاب‌هايى را حمل كند. بد است وصف آن قومى كه آيات خدا را تكذيب كردند! و خدا مردم ستمگر را هدايت نمى‌كند'),
(5183, 62, 6, 'بگو: اى كسانى كه يهودى شديد! اگر گمان داريد كه شما دوستان خداييد نه ديگر مردمان، پس تمنّاى مرگ كنيد اگر راست مى‌گوييد'),
(5184, 62, 7, 'ولى به سبب آنچه از پيش فرستاده‌اند هرگز آن را آرزو نخواهند كرد، و خداوند به ستمكاران آگاه است'),
(5185, 62, 8, 'بگو: مرگى كه از آن مى‌گريزيد، قطعا به سراغ شما مى‌آيد، سپس به سوى داناى نهان و آشكار بازگردانده مى‌شويد، آن‌گاه شما را از آنچه انجام مى‌داديد خبر ميدهد'),
(5186, 62, 9, 'اى كسانى كه ايمان آورده‌ايد! چون براى نماز روز جمعه ندا داده شد، به سوى ذكر خدا بشتابيد و داد و ستد را واگذاريد كه اين براى شما بهتر است، اگر مى‌دانستيد'),
(5187, 62, 10, 'و چون نماز پايان گرفت، در زمين پراكنده شويد و از فضل خدا [روزى‌] بجوييد و خدا را بسيار ياد كنيد، باشد كه رستگار شويد'),
(5188, 62, 11, 'و هنگامى كه داد و ستد يا سرگرمى‌اى ديدند به سوى آن پراكنده شدند و تو را به حال ايستاده رها كردند. بگو: آنچه نزد خداست از سرگرمى و تجارت بهتر است، و خدا بهترين روزى دهندگان است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'چون منافقان نزد تو آيند گويند: گواهى مى‌دهيم كه تو واقعا رسول خدايى. و خدا مى‌داند كه تو واقعا رسول اويى، و خدا گواهى مى‌دهد كه منافقان قطعا دروغگويند'),
(5190, 63, 2, 'سوگندهاى خود را سپرى گرفته‌اند، و از راه خدا بازداشته‌اند. به راستى آنها بد عمل مى‌كنند'),
(5191, 63, 3, 'اين بدان سبب است كه آنها ايمان آوردند، سپس كافر شدند، در نتيجه بر دل‌هايشان مهر زده شد. از اين رو آنها نمى‌فهمند'),
(5192, 63, 4, 'و وقتى آنها را ببينى، [گيرايى‌] ظاهرشان تو را به شگفت مى‌آورد و اگر سخن گويند به گفتارشان گوش فرا مى‌دهى، گويى آنها چوب‌هاى خشكى هستند كه [به ديوارى‌] تكيه داده شده‌اند [و ريشه ندارند] هر فريادى را بر ضد خويش مى‌پندارند. آنها دشمنند، پس از ايشان بر حذ'),
(5193, 63, 5, 'و چون به آنها گفته شود: بياييد تا رسول خدا برايتان آمرزش بخواهد، سرهاى خود را بر مى‌گردانند، و مى‌بينى كه آنها متكبرانه روى بر مى‌تابند'),
(5194, 63, 6, 'براى آنها يكسان است، چه برايشان آمرزش بخواهى يا برايشان آمرزش نخواهى، خدا هرگز آنها را نمى‌آمرزد. مسلما خدا مردم فاسق را هدايت نمى‌كند'),
(5195, 63, 7, 'آنها همان كسانند كه مى‌گويند: به كسانى كه نزد رسول خدايند انفاق نكنيد تا پراكنده شوند، و حال آن كه خزاين آسمان‌ها و زمين از آن خداست، ولى منافقان در نمى‌يابند'),
(5196, 63, 8, 'مى‌گويند: اگر به مدينه برگرديم، قطعا عزيزتر، ذليل‌تر [يعنى مؤمنان‌] را از آن جا بيرون خواهد كرد، و حال آن كه عزت از آن خدا و از آن رسول او و از آن مؤمنان است، ليكن منافقان نمى‌دانند'),
(5197, 63, 9, 'اى كسانى كه ايمان آورده‌ايد! مبادا اموال شما و فرزندانتان شما را از ياد خدا غافل سازد! و هر كس چنين كند پس آنها همان زيانكارانند'),
(5198, 63, 10, 'و از آنچه روزيتان كرده‌ايم انفاق كنيد پيش از آن كه مرگ يكى از شما در رسد، پس بگويد: پروردگارا! چرا مرا تا مدتى نزديك مهلت ندادى تا صدقه دهم و از شايستگان شوم'),
(5199, 63, 11, 'و خدا هرگز [مرگ‌] كسى را وقتى اجلش فرا رسيد به تأخير نمى‌اندازد، و خدا بدانچه مى‌كنيد آگاه است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'آنچه در آسمان‌ها و آنچه در زمين است خدا را تسبيح مى‌گويند. فرمانروايى از آن او و ستايش مختص اوست، و او بر هر چيزى تواناست'),
(5201, 64, 2, 'اوست كه شما را آفريد پس برخى از شما كافرند و برخى مؤمن، و خدا بدانچه مى‌كنيد بيناست'),
(5202, 64, 3, 'آسمان‌ها و زمين را به حق آفريد و شما را صورتگرى كرد و صورت‌هايتان را نيكو ساخت، و بازگشت [همه‌] به سوى اوست'),
(5203, 64, 4, 'آنچه را كه در آسمان‌ها و زمين است مى‌داند، و آنچه را پنهان كنيد و آنچه را آشكار سازيد مى‌داند، و خدا به راز دل‌ها داناست'),
(5204, 64, 5, 'آيا خبر كسانى كه پيش از اين كافر شدند و سزاى كارشان را چشيدند و عذابى دردناك خواهند داشت، به شما نرسيده است'),
(5205, 64, 6, 'اين [بد فرجامى‌] از آن روست كه پيامبرانشان دلايل آشكار برايشان آوردند ولى آنها گفتند: آيا بشرى ما را هدايت مى‌كند؟ پس كافر شدند و روى گردانيدند، و خدا [از ايمانشان‌] بى‌نيازى نمود، و خدا بى‌نياز ستوده است'),
(5206, 64, 7, 'كسانى كه كافر شدند، پنداشتند كه هرگز برانگيخته نمى‌شوند. بگو: چرا، سوگند به پروردگارم كه حتما برانگيخته مى‌شويد، سپس از [حقيقت‌] آنچه كرده‌ايد قطعا خبردار مى‌شويد، و اين بر خدا آسان است'),
(5207, 64, 8, 'پس به خدا و رسول او و نورى كه فرو فرستاده‌ايم ايمان بياوريد، و خدا به آنچه مى‌كنيد آگاه است'),
(5208, 64, 9, 'روزى كه شما را براى روز حشر گرد مى‌آورد، آن روز، روز حسرت و زيان است. و هر كس به خدا ايمان بياورد و كار شايسته كند، بدى‌هايش را از او بزدايد و او را به باغ‌هايى كه از پاى درختانش نهرها جارى است در آورد، كه در آن جاودانه بمانند. اين كاميابى بزرگ است'),
(5209, 64, 10, 'و كسانى كه كافر شده و آيات ما را تكذيب كردند اهل آتشند كه در آن ماندگار خواند بود، و بد سر انجامى است'),
(5210, 64, 11, 'هيچ مصيبتى جز به اذن خدا نرسد، و هر كه به خدا ايمان آورد، قلب وى را هدايت كند، و خدا به هر چيزى داناست'),
(5211, 64, 12, 'و خدا را فرمان بريد و پيامبر را فرمان بريد، پس اگر روى بگردانيد، بر پيامبر ما فقط ابلاغ آشكار [پيام‌] است'),
(5212, 64, 13, 'خداوند [يكتا] معبودى جز او نيست، پس مؤمنان بايد تنها بر خدا توكل كنند'),
(5213, 64, 14, 'اى كسانى كه ايمان آورده‌ايد! بى‌ترديد برخى از همسران شما و فرزندانتان [به خاطر ايمانتان‌] دشمن شمايند، از آنها برحذر باشيد. و اگر [از خلاف آنها] در گذريد و چشم بپوشيد و ببخشاييد، بى‌گمان خدا آمرزنده‌ى مهربان است'),
(5214, 64, 15, 'اموال شما و فرزندانتان صرفا [وسيله‌] آزمايشند، و پاداش بزرگ نزد خداوند است'),
(5215, 64, 16, 'پس تا مى‌توانيد از خدا پروا كنيد و بشنويد و اطاعت كنيد، و انفاق نماييد كه براى خودتان بهتر است. و هر كه از حرص و آز نفس خويش نگاه داشته شود، چنين كسانى رستگار خواهند بود'),
(5216, 64, 17, 'اگر خدا را قرض الحسنه دهيد، آن را براى شما دو چندان مى‌گرداند و بر شما مى‌بخشايد، و خداوند بسيار قدرشناس و بردبار است'),
(5217, 64, 18, 'داناى نهان و آشكار و مقتدر حكيم است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اى پيامبر! چون زنان را طلاق دهيد، آنها را به وقت عده‌شان [پس از پاك شدن از حيض‌] طلاق دهيد و حساب عده را نگه داريد و از خدا، پروردگارتان بترسيد. آنها را [در دوران عده‌] از خانه‌هايشان بيرون نكنيد و [آنها هم‌] بيرون نروند، مگر آن كه مرتكب كار زشت آشكار'),
(5219, 65, 2, 'پس چون به آخر عدّه‌شان رسيدند، يا به شايستگى نگاهشان داريد و يا به شايستگى از آنها جدا شويد، و دو تن عادل از خودتان را گواه بگيريد و شهادت را براى خدا به پا داريد. با اين [دستورات‌] كسى كه به خدا و روز رستاخيز ايمان دارد موعظه مى‌شود. و هر كه از خدا پ'),
(5220, 65, 3, 'و او را از جايى كه گمان نمى‌برد روزى مى‌دهد. و هر كس بر خدا توكل كند او براى وى كافى است. همانا خدا [به ثمر] رساننده‌ى امر خويش است. بى‌ترديد خدا براى هر چيزى اندازه‌اى نهاده است'),
(5221, 65, 4, 'و از زنان شما آنهايى كه از عادت ماهانه مأيوس شده‌اند، اگر در وضع آنها [از نظر باردارى و عوارض ديگر] شك داريد، عدّه‌ى آنها سه ماه است، و زنانى كه [با وجود اقتضاى سنّ‌] عادت ماهانه نديده‌اند [نيز عده‌شان سه ماه است‌]. و زنان باردار مدتشان اين است كه وض'),
(5222, 65, 5, 'اين فرمان خداست كه آن را به سوى شما فرو فرستاده است و هر كس از خدا پروا كند، بدى‌هايش را از او بزدايد و پاداش او را بزرگ سازد'),
(5223, 65, 6, 'در حد توان خود، آنان را [تا سر آمد عده‌شان‌] در همان جا كه خود ساكنيد سكونت دهيد و به آنها آسيب و زيان نرسانيد تا عرصه را بر آنان تنگ كنيد. و اگر بار دارند خرجشان را بدهيد تا وضع حمل كنند. پس اگر براى شما [فرزند را] شير دادند پاداش آنها را بپردازد و ['),
(5224, 65, 7, 'صاحب وسعت بايد از وسعت خود انفاق كند و هر كه تنگدست باشد، بايد از آنچه خدايش داده انفاق كند. خدا هيچ كس را جز به اندازه‌اى كه به او داده تكليف نمى‌كند. زودا كه خدا پس از دشوارى آسانى پيش آورد'),
(5225, 65, 8, 'و چه بسيار شهرها كه از فرمان پروردگار خود و پيامبرانش سرپيچيدند، پس از آنها حسابى سخت كشيديم و آنها را به عذابى شديد عذاب كرديم'),
(5226, 65, 9, 'پس سزاى كار خود را چشيدند و پايان كارشان خسران بود'),
(5227, 65, 10, 'خدا براى آنها عذابى سخت آماده كرده است. پس اى خردمندانى كه ايمان آورده‌ايد! از خدا بترسيد. همانا خدا به سوى شما تذكارى نازل كرده است'),
(5228, 65, 11, 'پيامبرى را كه آيات روشنگر خدا را بر شما تلاوت مى‌كند تا كسانى را كه ايمان آورده و كارهاى شايسته كرده‌اند از تاريكى‌ها به سوى روشنايى بيرون برد. و هر كس به خدا بگرود و كار شايسته كند او را به باغ‌هايى كه از پاى درختانش نهرها جارى است در آورد كه جاودان'),
(5229, 65, 12, 'خدا كسى است كه هفت آسمان را آفريد و از زمين نيز همانند آنها را. فرمان [خدا] در ميان آنها فرود مى‌آيد [و تدبير مى‌كند] تا بدانيد كه خدا بر هر چيزى تواناست و اين كه علم او همه چيز را احاطه كرده است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اى پيامبر! چرا براى كسب خشنودى همسرانت چيزى را كه خدا براى تو حلال كرده حرام مى‌كنى؟ و خداوند آمرزنده‌ى مهربان است'),
(5231, 66, 2, 'در حقيقت، خدا براى شما [راه‌] شكستن سوگندهايتان را [به كفّاره‌] معين داشته، و خدا مولاى شماست، و او داناى حكيم است'),
(5232, 66, 3, 'و آن‌گاه كه پيامبر با يكى از همسرانش [حفصه‌] سخنى را به راز گفت، همين كه وى آن را [به عايشه‌] خبر داد و خدا [پيامبر] را از آن [افشاى راز] آگاه ساخت، [پيامبر] گوشه‌اى از اين [خيانت‌] را به وى اعلام و از برخى صرف نظر كرد. پس هنگامى كه [پيامبر] به وى خب'),
(5233, 66, 4, 'اگر [شما دو زن‌] به درگاه خدا توبه كنيد [به نفع شماست، زيرا] واقعا دل‌هايتان از حق برگشته است، و اگر بر ضدّ او همدست شويد، بى‌ترديد خداوند خود ياور اوست، و گذشته از اين جبرئيل و نيكوكار مؤمنان [على «ع»] و فرشتگان هم پشتيبانند'),
(5234, 66, 5, 'اگر شما را طلاق دهد چه بسا پروردگارش همسرانى بهتر از شما به او عوض دهد همسرانى مسلمان، مؤمن، مطيع، توبه كار، عبادتگر، روزه‌گير، بيوه و دوشيزه'),
(5235, 66, 6, 'اى كسانى كه ايمان آورده‌ايد! خود و خانواده‌ى خويش را از آتشى كه هيزم آن، مردم و سنگ‌ها هستند حفظ كنيد كه بر آن فرشتگانى خشن و سختگير نگهبانند و از آنچه خدا به آنان دستور داده سرپيچى نمى‌كنند و آنچه را كه مأمور شده‌اند انجام مى‌دهند'),
(5236, 66, 7, 'اى كسانى كه كافر شده‌ايد! امروز عذر مخواهيد، جز اين نيست كه شما بدانچه مى‌كرده‌ايد مجازات مى‌شويد'),
(5237, 66, 8, 'اى كسانى كه ايمان آورده‌ايد! به درگاه خدا توبه كنيد، توبه‌اى راستين و خالص، اميد است كه پروردگار شما بدى‌هايتان را از شما بزدايد و شما را به باغ‌هايى كه از پاى درختانش نهرها جارى است در آورد. روزى كه خدا، پيامبر و كسانى را كه با او ايمان آورده‌اند خوا'),
(5238, 66, 9, 'اى پيامبر! با كافران و منافقان جهاد كن و بر آنها سخت گير كه جايگاهشان جهنم است و چه بد سر انجامى است'),
(5239, 66, 10, 'خدا براى كسانى كه كافر شده‌اند، زن نوح و زن لوط را مثل آورده كه هر دو در نكاح دو بنده‌ى شايسته از بندگان ما بودند و به آنها خيانت كردند، و آن دو [پيامبر] نتوانستند در برابر خدا كارى براى آن دو [زن‌] انجام دهند و گفته شد: با داخل شوندگان، داخل آتش شوي'),
(5240, 66, 11, 'و خدا براى كسانى كه ايمان آورده‌اند، همسر فرعون را مثل آورده، آن‌گاه كه گفت: پروردگارا! براى من نزد خويش در بهشت خانه‌اى بساز و مرا از [شرّ] فرعون و عمل او نجات ده و مرا از مردم ستمكار برهان'),
(5241, 66, 12, 'و مريم دختر عمران را [مثل آورده‌] كه دامان خود را پاك نگاه داشت پس ما از روح خود در آن دميديم و او سخنان پروردگار خود و كتاب‌هاى او را تصديق نمود و از فرمانبرداران بود');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'پرخير و پاينده است آن كه فرمانروايى به دست اوست و او بر هر چيزى تواناست'),
(5243, 67, 2, 'همان كه مرگ و زندگى را بيافريد تا شما را بيازمايد كه كدامتان به عمل نيكوتر است، و او شكست ناپذير آمرزنده است'),
(5244, 67, 3, 'همان كه هفت آسمان را بالاى هم بيافريد. در آفرينش [آن خداى‌] هستى بخش هيچ گونه تفاوت و اختلافى نمى‌بينى. پس چشم بگردان، آيا خلل و نقصانى مى‌بينى'),
(5245, 67, 4, 'باز دوباره چشم بگردان [و بنگر، ولى‌] چشم، ناكام و خسته [از يافتن عيب‌] به سوى تو باز مى‌گردد'),
(5246, 67, 5, 'و همانا آسمان نزديك‌تر را با چراغ‌هايى زينت داديم و آنها را مايه‌ى راندن شياطين قرار داديم، و براى آنها عذاب آتش فروزان آماده كرده‌ايم'),
(5247, 67, 6, 'و براى كسانى كه به پروردگارشان كافر شدند عذاب جهنم است، و بد سر انجامى است'),
(5248, 67, 7, 'چون در آن جا افكنده شوند، از آن در حالى كه فوران مى‌كند خروشى سهمگين مى‌شنوند'),
(5249, 67, 8, 'نزديك است كه از خشم پاره پاره شود. هر بار كه گروهى در آن افكنده شوند، نگاهبانان آن از ايشان پرسند: آيا شما را هشدار دهنده‌اى نيامد'),
(5250, 67, 9, 'گويند: چرا البته هشدار دهنده‌اى به سوى ما آمد، ولى تكذيب كرديم و گفتيم: خدا چيزى نفرستاده است جز اين نيست كه شما در گمراهى بزرگى هستيد'),
(5251, 67, 10, 'و گويند: اگر مى‌شنيديم يا تعقل مى‌كرديم در زمره‌ى دوزخيان نبوديم'),
(5252, 67, 11, 'آرى به گناه خود اعتراف مى‌كنند، پس طرد و هلاكت باد بر اهل جهنم'),
(5253, 67, 12, 'كسانى كه در نهان از پروردگارشان مى‌ترسند، آنها را آمرزش و پاداشى بزرگ است'),
(5254, 67, 13, 'و [اگر] گفتارتان را پنهان داريد يا آشكار نماييد، بى‌گمان او به راز دل‌ها آگاه است'),
(5255, 67, 14, 'آيا كسى كه آفريده است نمى‌داند؟ حال آن كه او باريك‌بين آگاه است'),
(5256, 67, 15, 'او كسى است كه زمين را براى شما رام كرد، پس در اطراف و اكناف آن قدم برداريد و از روزى او بخوريد و [بدانيد كه‌] برانگيخته شدن [شما] به سوى اوست'),
(5257, 67, 16, 'آيا از آن كس كه در آسمان است ايمن شده‌ايد كه شما را به زمين فرو برد، پس به ناگاه زمين بلرزد'),
(5258, 67, 17, 'يا از آن كس كه در آسمان است ايمن شده‌ايد كه بر سر شما تندبادى سنگبار فرو فرستد؟ پس زودا كه بدانيد بيم دادن من چگونه است'),
(5259, 67, 18, 'و البته كسانى هم كه پيش از آنها بودند به تكذيب پرداختند، پس [بنگر] عذاب من چگونه بود'),
(5260, 67, 19, 'آيا به پرندگان بالاى سرشان ننگريسته‌اند كه بال‌هايشان را باز و بسته مى‌كنند؟ آنها را جز [خداى‌] رحمان نگاه نمى‌دارد. مسلما او به هر چيزى بيناست'),
(5261, 67, 20, 'آيا كيست آن كسى كه او سپاه [و پشتوانه‌] شماست و شما را در برابر [خداى‌] رحمان يارى مى‌كند؟ كافران جز دچار فريب نيستند'),
(5262, 67, 21, 'آيا كيست آن كه اگر [خدا] روزى خود را از شما باز دارد روزيتان مى‌دهد؟ [ولى آنها پند نمى‌گيرند،] بلكه در سركشى و فرار از حق اصرار مى‌ورزند'),
(5263, 67, 22, 'پس آيا آن كه نگونسار بر صورت خويش مى‌رود هدايت يافته‌تر است يا آن كسى كه راست قامت بر راه راست مى‌رود'),
(5264, 67, 23, 'بگو: اوست آن كس كه شما را آفريد و براى شما شنوايى و ديدگان و دل‌ها پديد آورد. [ولى‌] كم سپاس مى‌گزاريد'),
(5265, 67, 24, 'بگو: اوست كه شما را در زمين پراكنده ساخته و به سوى او محشور مى‌شويد'),
(5266, 67, 25, 'و مى‌گويند: اين وعده كى خواهد بود اگر راست مى‌گوييد'),
(5267, 67, 26, 'بگو: علم آن فقط نزد خداست و من صرفا هشداردهنده‌اى آشكارم'),
(5268, 67, 27, 'و چون آن [لحظه موعود] را نزديك ببينند، چهره‌هاى كسانى كه كافر شده‌اند درهم رود و گفته شود: اين همان چيزى است كه طلب مى‌كرديد'),
(5269, 67, 28, 'بگو: به من خبر دهيد، اگر خدا مرا و هر كه را با من است هلاك كند يا ما را مورد رحمت قرار دهد، چه كسى كافران را از عذابى دردناك پناه مى‌دهد'),
(5270, 67, 29, 'بگو: او [خداى‌] رحمان است به او ايمان آورديم و تنها بر او توكل كرديم. پس به زودى خواهيد دانست چه كسى در گمراهى آشكار است'),
(5271, 67, 30, 'بگو: به من خبر دهيد، اگر آب شما [در زمين‌] فرو رود، چه كسى آب روان برايتان خواهد آورد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون. سوگند به قلم و آنچه مى‌نويسند'),
(5273, 68, 2, 'كه تو، به لطف پروردگارت، مجنون نيستى'),
(5274, 68, 3, 'و بى‌گمان، تو را پاداشى بى‌منت [و بى‌پايان‌] خواهد بود'),
(5275, 68, 4, 'و به راستى تو بر خلق و خوى بس بزرگى هستى'),
(5276, 68, 5, 'پس به زودى خواهى ديد و خواهند ديد'),
(5277, 68, 6, 'كه كدام يك از شما مجنون است'),
(5278, 68, 7, 'بى‌گمان، پروردگار تو كسى را كه از راه او منحرف شده، بهتر مى‌شناسد، و او به راه‌يافتگان داناتر است'),
(5279, 68, 8, 'پس از تكذيب كنندگان اطاعت مكن'),
(5280, 68, 9, '[آنها] دوست دارند كه سازش كنى تا سازش كنند'),
(5281, 68, 10, 'و از هيچ سوگند پيشه فرومايه‌اى اطاعت نكن'),
(5282, 68, 11, 'كه عيبجوست و براى خبرچينى [به هر جا] رونده است'),
(5283, 68, 12, 'بسيار مانع خير، متجاوز و گناه‌پيشه است'),
(5284, 68, 13, 'گذشته از آن درشت‌خوى، بى‌تبار و بدنام است'),
(5285, 68, 14, 'به صرف اين كه مالدار و صاحب پسران است'),
(5286, 68, 15, 'چون آيات ما بر او خوانده شود گويد: افسانه‌هاى پيشينيان است'),
(5287, 68, 16, 'زودا كه بر بينى‌اش داغ نهيم [و رسوايش كنيم‌]'),
(5288, 68, 17, 'ما آنها (مشركان) را گرفتار و مبتلا كرديم، همان گونه كه باغداران را گرفتار ساختيم، آن‌گاه كه سوگند خوردند كه: صبحگاهان آن [ميوه‌هاى باغ‌] را [دور از چشم مستمندان‌] خواهيم چيد'),
(5289, 68, 18, 'و استثنا نكردند (ان شاء اللّه نگفتند)'),
(5290, 68, 19, 'پس بلايى فراگير از جانب پروردگارت آن باغ را فراگرفت در حالى كه آنها خواب بودند'),
(5291, 68, 20, 'پس آن [باغ‌] همچون زمين باير گرديد'),
(5292, 68, 21, 'سپس صبحگاهان يكديگر را صدا زدند'),
(5293, 68, 22, 'كه بامدادان به سوى كشت خويش رويد اگر ميوه چينيد'),
(5294, 68, 23, 'پس به راه افتادند و آهسته به هم مى‌گفتند'),
(5295, 68, 24, '[مواظب باشيد] كه امروز فقيرى در آن جا بر شما وارد نشود'),
(5296, 68, 25, 'و به وقت صبح با قدرت بر منع [مستمندان‌] بيرون شدند'),
(5297, 68, 26, 'پس همين كه آن [باغ سوخته‌] را ديدند گفتند: محققا ما راه گم كرده‌ايم'),
(5298, 68, 27, 'بلكه ما محروم شدگانيم [و همه چيز را از دست داده‌ايم‌]'),
(5299, 68, 28, 'عاقل‌ترشان گفت: آيا به شما نگفتم: چرا [خدا را] تسبيح نمى‌گوييد'),
(5300, 68, 29, 'گفتند: پروردگار ما منزه است، ما واقعا ستمگر بوده‌ايم'),
(5301, 68, 30, 'پس بعضى بر بعضى روى كرده يكديگر را ملامت مى‌كردند'),
(5302, 68, 31, 'گفتند: اى واى بر ما! در حقيقت ما سركش بوده‌ايم'),
(5303, 68, 32, 'اميد است كه پروردگارمان بهتر از آن را به ما عوض دهد، [زيرا] ما به پروردگارمان روى آورده‌ايم'),
(5304, 68, 33, 'عذاب [دنيا] چنين است، و عذاب آخرت قطعا بزرگ‌تر است اگر مى‌دانستند'),
(5305, 68, 34, 'همانا براى پرهيزكاران نزد پروردگارشان بهشت‌هاى پرناز و نعمت است'),
(5306, 68, 35, 'پس آيا ما مطيعان را چون بدكاران قرار مى‌دهيم'),
(5307, 68, 36, 'شما را چه شده است؟ چگونه داورى مى‌كنيد'),
(5308, 68, 37, 'آيا شما را كتابى است كه [اين حكم را] در آن مى‌خوانيد'),
(5309, 68, 38, 'كه هر چه را شما انتخاب كنيد از آن شماست'),
(5310, 68, 39, 'آيا شما را تا روز قيامت بر عهده‌ى ما پيمان‌هاى مؤكدى است كه هر چه شما حكم كنيد حق شما باشد'),
(5311, 68, 40, 'از آنها بپرس: كدامشان ضامن اين [ادعا] يند'),
(5312, 68, 41, 'آيا شريكانى دارند؟ پس بايد شريكان خود را بياورند اگر راست مى‌گويند'),
(5313, 68, 42, 'روزى كه كار [بر كافران‌] سخت و دشوار شود و آنها [به تمسخر] به سجده فرا خوانده شوند ولى نتوانند'),
(5314, 68, 43, 'ديدگانشان [هراسان‌] به زير افتد و ذلت و خوارى وجودشان را فرا گيرد، در حالى كه [در دنيا] به وقت تندرستى به سجده دعوت مى‌شدند [ولى ابا مى‌كردند]'),
(5315, 68, 44, 'پس مرا با كسى كه اين سخن [هاى قرآن‌] را تكذيب مى‌كند واگذار. ما به تدريج آنها را از آن جا كه نمى‌دانند گرفتار خواهيم ساخت'),
(5316, 68, 45, 'و مهلتشان مى‌دهم كه نقشه من بسى محكم است'),
(5317, 68, 46, 'آيا از آنها مزدى مى‌خواهى كه از پرداخت آن [عاجز و] گرانبار شده‌اند'),
(5318, 68, 47, 'آيا [اسرار] غيب نزدشان است و آنها مى‌نويسند'),
(5319, 68, 48, 'پس بر حكم پروردگارت شكيبايى كن و مانند آن همدم ماهى (يونس) مباش آن‌گاه كه [خدايش را] ندا داد و سخت غمگين بود'),
(5320, 68, 49, 'اگر رحمت پروردگارش به او نرسيده بود، قطعا ملامت شده به صحرا افكنده مى‌شد'),
(5321, 68, 50, 'سپس پروردگارش وى را برگزيد و از شايستگانش قرار داد'),
(5322, 68, 51, 'و همانا كسانى كه كافر شدند، وقتى كه قرآن را شنيدند، نزديك بود تو را با چشمان [خشمگين‌] خود از جاى بركنند، و مى‌گويند: او حتما ديوانه است'),
(5323, 68, 52, 'و حال آن كه [قرآن‌] جز تذكارى براى جهانيان نيست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن واقعه حتمى [رستاخيز]'),
(5325, 69, 2, 'چيست آن واقعه حتمى'),
(5326, 69, 3, 'و تو چه دانى كه آن واقعه حتمى چيست'),
(5327, 69, 4, 'ثمود و عاد، آن حادثه كوبنده را تكذيب كردند'),
(5328, 69, 5, 'اما ثمود با صيحه طغيانگر به هلاكت رسيدند'),
(5329, 69, 6, 'و امّا عاد، با تندبادى توفنده و سركش هلاك شدند'),
(5330, 69, 7, '[خدا] آن را هفت شب و هشت روز پياپى بر آنها مسلط كرد، كه آن قوم را در آن به خاك افتاده مى‌ديدى گويى آنها تنه‌هاى تو خالى و فرو افتاده‌ى نخلند'),
(5331, 69, 8, 'پس آيا از آنها هيچ باقى مانده‌اى مى‌بينى'),
(5332, 69, 9, 'و فرعون و كسانى كه پيش از او بودند، و [مردم‌] شهرهاى سرنگون شده‌ى [قوم لوط] مرتكب خطا شدند'),
(5333, 69, 10, 'و از فرمان فرستاده‌ى پروردگارشان سرپيچى كردند پس آنها را بكيفرى سخت بگرفت'),
(5334, 69, 11, 'ما وقتى كه آب طغيان كرد، شما را بر كشتى سوار نموديم'),
(5335, 69, 12, 'تا آن را براى شما [مايه‌] تذكّرى گردانيم و گوش‌هاى فرا گيرنده آن را نگاه دارد'),
(5336, 69, 13, 'پس آنگاه كه در صور يك بار دميده شود'),
(5337, 69, 14, 'و زمين و كوه‌ها از جاى خود برداشته شده، هر دوى آنها يكباره در هم كوبيده شوند'),
(5338, 69, 15, 'آن روز است كه آن واقعه [عظيم‌] روى دهد'),
(5339, 69, 16, 'و آسمان از هم بشكافد، پس در آن روز سست و متلاشى گردد'),
(5340, 69, 17, 'و فرشتگان در اطراف [آسمان‌] اند، و عرش پروردگارت را آن روز هشت [فرشته‌] بر بالاى سر خود حمل مى‌كنند'),
(5341, 69, 18, 'در آن روز، [شما به پيشگاه خدا] عرضه مى‌شويد و هيچ [كار] پوشيده‌اى از شما پنهان نمى‌ماند'),
(5342, 69, 19, 'امّا كسى كه كارنامه‌اش به دست راستش داده شود، گويد: بياييد و نامه مرا بخوانيد'),
(5343, 69, 20, 'همانا من باور داشتم كه با حساب خود رو به رو مى‌شوم'),
(5344, 69, 21, 'پس او در يك زندگى خوش باشد'),
(5345, 69, 22, 'در بهشتى برين'),
(5346, 69, 23, '[كه‌] ميوه‌هايش در دسترس است'),
(5347, 69, 24, 'بخوريد و بنوشيد، گوارايتان باد، به [پاداش‌] آنچه در روزهاى گذشته پيش فرستاده‌ايد'),
(5348, 69, 25, 'و اما كسى كه كارنامه‌اش بدست چپ او داده شود، گويد: اى كاش نامه‌ام به من داده نمى‌شد'),
(5349, 69, 26, 'و نمى‌دانستم حسابم چيست'),
(5350, 69, 27, 'اى كاش آن [مرگ‌] پايان دهنده‌ى كار بود [و دوباره زنده نمى‌شدم‌]'),
(5351, 69, 28, 'مال من چيزى از من دفع نكرد'),
(5352, 69, 29, 'قدرتم از دستم برفت'),
(5353, 69, 30, '[پس فرمان رسد:] او را بگيريد و در زنجير كنيد'),
(5354, 69, 31, 'آن‌گاه به دوزخش در آوريد'),
(5355, 69, 32, 'سپس او را در زنجيرى كه طول آن هفتاد ذراع است در بند كشيد'),
(5356, 69, 33, 'چرا كه او به خداى بزرگ ايمان نمى‌آورد'),
(5357, 69, 34, 'و به اطعام مسكين ترغيب نمى‌كرد'),
(5358, 69, 35, 'پس امروز او را در اين جا دوستى صميمى نيست'),
(5359, 69, 36, 'و نه طعامى مگر از چرك و خون [دوزخيان‌]'),
(5360, 69, 37, 'كه آن را جز خطاكاران نمى‌خورند'),
(5361, 69, 38, 'پس سوگند به آنچه مى‌بينيد'),
(5362, 69, 39, 'و آنچه نمى‌بينيد'),
(5363, 69, 40, 'كه اين [قرآن‌] قطعا گفتار فرستاده‌اى بزرگوار است'),
(5364, 69, 41, 'و آن گفتار يك شاعر نيست. اما كمتر ايمان مى‌آوريد'),
(5365, 69, 42, 'و نه گفتار كاهنى. چه اندك متذكر مى‌شويد'),
(5366, 69, 43, '[بلكه‌] نازل شده از جانب پروردگار جهانيان است'),
(5367, 69, 44, 'و اگر او حتى يك كلمه [از پيش خود] بر ما دروغ مى‌بست'),
(5368, 69, 45, 'ما دست راست او را مى‌گرفتيم'),
(5369, 69, 46, 'سپس شريان قلبش را قطع مى‌كرديم'),
(5370, 69, 47, 'آن‌گاه احدى از شما مدافع او نبود'),
(5371, 69, 48, 'همانا اين [قرآن‌] تذكارى براى پرهيزكاران است'),
(5372, 69, 49, 'و ما بى‌ترديد مى‌دانيم كه برخى از شما تكذيب كنندگان [آن‌] هستيد'),
(5373, 69, 50, 'و همين [تكذيب‌] مايه‌ى حسرتى براى كافران است'),
(5374, 69, 51, 'و اين [قرآن‌]، بى‌شبهه، حقيقت ثابت است'),
(5375, 69, 52, 'پس به نام پروردگار بزرگت تسبيح گوى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'سؤال كننده‌اى [از كفار] عذابى حتمى را درخواست كرد'),
(5377, 70, 2, 'كه مخصوص كافران است و دفع كننده‌اى ندارد'),
(5378, 70, 3, 'و از جانب خداى صاحب درجات است'),
(5379, 70, 4, 'فرشتگان و روح، در روزى كه مقدارش پنجاه هزار سال است به سوى او بالا مى‌روند'),
(5380, 70, 5, 'پس صبرى زيبا پيشه كن'),
(5381, 70, 6, 'همانا آنها آن [روز] را دور مى‌بينند'),
(5382, 70, 7, 'و ما نزديكش مى‌بينيم'),
(5383, 70, 8, 'روزى كه آسمان چون فلز گداخته شود'),
(5384, 70, 9, 'و كوه‌ها چون پشم زده شده [متلاشى‌] گردد'),
(5385, 70, 10, 'و هيچ دوست صميمى از دوست صميمى [حال‌] نپرسد'),
(5386, 70, 11, 'دوستان صميمى به آنها نشان داده مى‌شوند، [ولى هر كسى گرفتار كار خويش است.] گناهكار دوست مى‌دارد كه كاش براى رهايى از عذاب آن روز پسران خود را عوض دهد'),
(5387, 70, 12, 'و [نيز] همسر و برادر خود را'),
(5388, 70, 13, 'و اقوامش را كه به او پناه مى‌دادند'),
(5389, 70, 14, 'و هر كه را كه در روى زمين است همه را [عوض مى‌داد] و آن‌گاه خود را [از عذاب‌] مى‌رهانيد'),
(5390, 70, 15, 'نه، هرگز [نجاتى در كار نيست‌]. همانا آن آتشى شعله‌ور است'),
(5391, 70, 16, 'بركننده‌ى پوست سر و اندام است'),
(5392, 70, 17, 'فرا مى‌خواند هر كه را پشت كرده و [از دعوت حق‌] روى برتافته'),
(5393, 70, 18, 'و [مال‌] گردآورده و ذخيره نموده است'),
(5394, 70, 19, 'به راستى انسان بسيار حريص [و بى‌تاب‌] خلق شده است'),
(5395, 70, 20, 'چون آسيبى به او رسد بى‌تاب است'),
(5396, 70, 21, 'و چون خيرى به او رسد بخل ورز است'),
(5397, 70, 22, 'غير از نمازگزاران'),
(5398, 70, 23, 'همان كسانى كه بر نمازشان پايدارند'),
(5399, 70, 24, 'و همانان كه در اموالشان حقى معين است'),
(5400, 70, 25, 'براى سائل و محروم'),
(5401, 70, 26, 'و كسانى كه روز جزا را باور دارند'),
(5402, 70, 27, 'و آنان كه از عذاب پروردگارشان بيمناكند'),
(5403, 70, 28, 'چرا كه از عذاب پروردگارشان ايمنى نيست'),
(5404, 70, 29, 'و كسانى كه شرمگاه خود را حفظ كنند'),
(5405, 70, 30, 'مگر بر همسران خود يا كنيزانشان كه [در اين صورت‌] مورد نكوهش نيستند'),
(5406, 70, 31, 'پس هر كه فراتر از اين جويد، آنها همان تجاوزكارانند'),
(5407, 70, 32, 'و كسانى كه امانت‌ها و پيمان خود را مراعات كنند'),
(5408, 70, 33, 'و آنان كه بر شهادت‌هاى خود ايستاده‌اند'),
(5409, 70, 34, 'و كسانى كه بر نمازشان مواظبت دارند'),
(5410, 70, 35, 'آنها هستند كه در باغ‌هايى [از بهشت‌] گرامى داشته مى‌شوند'),
(5411, 70, 36, 'پس چيست كافران را كه به سوى تو مى‌شتابند'),
(5412, 70, 37, 'دسته دسته از راست و چپ [هجوم مى‌آورند]'),
(5413, 70, 38, 'آيا هر كسى از آنها طمع دارد كه در بهشت پرنعمت در آورده شود'),
(5414, 70, 39, 'حاشا! بى‌ترديد ما آنها را از آنچه مى‌دانند آفريده‌ايم'),
(5415, 70, 40, 'پس به پروردگار خاوران و باختران سوگند كه قطعا ما تواناييم'),
(5416, 70, 41, 'بر اين كه به جاى آنها بهتر از ايشان را بياوريم و ما عقب نمى‌افتيم'),
(5417, 70, 42, 'پس بگذارشان در باطل فرو روند و بازى كنند تا روزشان را كه وعده داده مى‌شوند ديدار كنند'),
(5418, 70, 43, 'روزى كه از گورها شتابان برآيند، گويى كه [چون تيرى‌] به سوى نشانه‌ها مى‌دوند'),
(5419, 70, 44, 'در حالى كه ديدگانشان [هراسان‌] فرو افتاده، ذلت آنها را فرو گيرد. اين همان روزى است كه [به آن‌] وعده داده مى‌شدند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'همانا ما نوح را به سوى قومش فرستاديم كه قومت را هشدار ده پيش از آن كه عذابى دردناك به سراغشان آيد'),
(5421, 71, 2, 'گفت: اى قوم من! همانا من شما را هشدار دهنده‌اى آشكارم'),
(5422, 71, 3, 'كه خدا را بپرستيد و از او پروا داريد و مرا اطاعت كنيد'),
(5423, 71, 4, 'تا [برخى‌] از گناهانتان را بر شما ببخشايد و [اجل‌] شما را تا وقتى معين به تأخير اندازد، كه اگر مى‌دانستيد، وقتى اجل مقرر خدا برسد ديگر به تأخير نيفتد'),
(5424, 71, 5, '[نوح‌] گفت: پروردگارا! من قوم خود را شب و روز دعوت كردم'),
(5425, 71, 6, 'و دعوت من جز بر گريزشان نيفزود'),
(5426, 71, 7, 'و من هر بار كه آنها را دعوت كردم تا ايشان را بيامرزى، انگشتانشان را در گوش‌هاى خود كردند و جامه‌هاى خويش بر سر كشيدند و [بر كفر خويش‌] پاى فشردند و هر چه بيشتر بر تكبر خود افزودند'),
(5427, 71, 8, 'سپس من آنها را به آواز بلند دعوت كردم'),
(5428, 71, 9, 'و آن‌گاه باز من به آنها علنى گفتم و در خلوت و خصوصى نيز به ايشان گفتم'),
(5429, 71, 10, 'و گفتم: از پروردگارتان آمرزش بخواهيد كه او قطعا آمرزنده است'),
(5430, 71, 11, 'تا بر شما از آسمان باران پى‌درپى فرستد'),
(5431, 71, 12, 'و شما را به اموال و پسران مدد كند، و برايتان باغ‌ها قرار دهد و نهرها پديد آورد'),
(5432, 71, 13, 'چيست شما را كه براى خدا عظمت قائل نمى‌شويد'),
(5433, 71, 14, 'و حال آنكه شما را به گونه‌هاى مختلف آفريده است'),
(5434, 71, 15, 'آيا نديده‌ايد كه چگونه خدا هفت آسمان را بالاى هم آفريد'),
(5435, 71, 16, 'و ماه را در ميان آنها روشنى بخش گردانيد و خورشيد را چراغى قرار داد'),
(5436, 71, 17, 'و خداست كه شما را [چون گياه‌] به طرز خاصى از زمين رويانيد'),
(5437, 71, 18, 'سپس شما را در آن باز مى‌گرداند و باز به طرزى عجيب بيرون مى‌آورد'),
(5438, 71, 19, 'و خدا زمين را براى شما فرشى [گسترده‌] ساخت'),
(5439, 71, 20, 'تا در راه‌هاى وسيع آن تردّد كنيد'),
(5440, 71, 21, 'نوح گفت: پروردگارا! آنها مرا نافرمانى كردند و كسى را پيروى نمودند كه مال و فرزندش جز بر زيانكارى وى نيفزود'),
(5441, 71, 22, 'و دست به نيرنگى بس بزرگ زدند'),
(5442, 71, 23, 'و گفتند: زنهار، خدايان خود را رها مكنيد، و هرگز «ودّ» و «سواع» و «يغوث» و «يعوق» و «نسر» را وانگذاريد'),
(5443, 71, 24, 'و تحقيقا بسيارى را گمراه كردند. و [بار خدايا!] ظالمان را جز ضلالت ميفزاى'),
(5444, 71, 25, '[تا] در اثر گناهانشان غرقه گشتند و در آتش در آورده شدند، پس براى خود، در برابر خدا هيچ ياورى نيافتند'),
(5445, 71, 26, 'و نوح گفت: پروردگارا! احدى از كافران را بر روى زمين باقى مگذار'),
(5446, 71, 27, 'كه اگر تو آنها را باقى گذارى، بندگانت را گمراه مى‌كنند و جز پليدكار ناسپاس نمى‌زايند'),
(5447, 71, 28, 'پروردگارا! بر من و بر پدر و مادرم و هر كس كه با ايمان به خانه من در آيد و بر مردان و زنان با ايمان ببخشاى و ظالمان را جز هلاكت و تباهى ميفزاى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: به من وحى شده است كه تنى چند از جنّيان [به وحى آسمانى‌] گوش فرا داشتند و گفتند: به راستى ما قرآنى شگفت‌آور شنيديم'),
(5449, 72, 2, 'كه به راه راست هدايت مى‌كند پس به آن ايمان آورديم، و هرگز كسى را شريك پروردگارمان قرار نخواهيم داد'),
(5450, 72, 3, 'و اين كه شأن و عظمت پروردگار ما بلند است، نه همسرى گرفته و نه فرزندى'),
(5451, 72, 4, 'و اين كه كم خرد ما، در باره‌ى خدا پريشان مى‌گفت و دروغ مى‌بست'),
(5452, 72, 5, 'و ما پنداشته بوديم كه انس و جن هرگز بر خدا دروغ نمى‌بندند'),
(5453, 72, 6, 'و مردانى از آدميان به مردانى از جن پناه مى‌بردند و بر سركشى آنها مى‌افزودند'),
(5454, 72, 7, 'و آنها (انسان‌ها) نيز آن گونه كه شما پنداشته‌ايد، گمان بردند كه خدا هرگز كسى را [به پيامبرى‌] مبعوث نمى‌كند'),
(5455, 72, 8, 'و اين كه ما به آسمان دست يافتيم و آن را پر از نگهبانان قدرتمند (فرشتگان) و تيرهاى شهاب يافتيم'),
(5456, 72, 9, 'و اين كه ما [سابقا] از آسمان در محل‌هايى براى شنيدن [اخبار آسمانى‌] مى‌نشستيم، اما اكنون هر كه بخواهد به گوش باشد، تير شهابى در كمين خود مى‌يابد'),
(5457, 72, 10, 'و ما [درست‌] نمى‌دانيم كه آيا براى ساكنان زمين شرّى اراده شده يا پروردگارشان براى آنها هدايتى خواسته است'),
(5458, 72, 11, 'و اين كه از ميان ما برخى صالحند و برخى غير آن، ما گروههايى در راههاى گوناگونى بوديم'),
(5459, 72, 12, 'و ما اعتقاد داريم كه هرگز نمى‌توانيم خدا را در زمين عاجز كنيم و هرگز نمى‌توانيم با گريختن، او را عاجز كنيم'),
(5460, 72, 13, 'و ما همين كه هدايت [قرآن‌] را شنيديم بدان گرويديم پس هر كه به پروردگار خود ايمان آورد، نه از نقصان [پاداش خود] مى‌ترسد و نه از ستمى'),
(5461, 72, 14, 'و از ميان ما برخى تسليم [امر خدا] و برخى منحرفند پس هر كه تسليم خدا گردد، آنهايند كه راه رشد را در پيش گرفته‌اند'),
(5462, 72, 15, 'ولى منحرفان، هيزم جهنم خواهند بود'),
(5463, 72, 16, 'و اگر [جن و انس‌] در راه درست پايدارى كنند، قطعا آبى گوارا بديشان مى‌نوشانيم [و روزى فراوان به آنان مى‌دهيم‌]'),
(5464, 72, 17, 'تا آنها را در اين [نعمت فراوان‌] بيازماييم. و هر كس از ياد پروردگار خود روى بگرداند، وى را در عذابى روز افزون بكشاند'),
(5465, 72, 18, 'و اين كه مساجد ويژه‌ى خداست، پس هيچ كس را با خدا نخوانيد'),
(5466, 72, 19, 'و اين كه چون بنده‌ى خدا [پيامبر به عبادت‌] برخاست تا او را بخواند، نزديك بود كه [به ازدحام‌] بر سر او فرو افتند'),
(5467, 72, 20, 'بگو: تنها پروردگار خود را مى‌خوانم و كسى را شريك او نمى‌كنم'),
(5468, 72, 21, 'بگو: بى‌ترديد من [از جانب خودم‌] براى شما اختيار هيچ زيانى و هيچ رشد و هدايتى را ندارم'),
(5469, 72, 22, 'بگو: هرگز كسى مرا از [عقوبت‌] خدا پناه نمى‌دهد، و هرگز پناهگاهى جز او نخواهم يافت'),
(5470, 72, 23, '[آنچه مى‌توانم‌] تنها ابلاغى از جانب خدا و رساندن پيام‌هاى اوست. و هر كه خدا و رسولش را نافرمانى كند، قطعا براى او آتش جهنم است كه جاودانه در آن خواهند ماند'),
(5471, 72, 24, 'تا آن‌گاه كه آنچه را وعده داده مى‌شوند ببينند، پس به زودى خواهند دانست چه كسى از جهت [داشتن‌] ياور ضعيف‌تر و در عدد كم جمعيت‌تر است'),
(5472, 72, 25, 'بگو: من نمى‌دانم كه آيا آنچه وعده داده مى‌شويد نزديك است يا پروردگارم براى آن مدتى قرار مى‌دهد'),
(5473, 72, 26, '[او] داناى غيب است و كسى را بر غيب خود آگاه نمى‌كند'),
(5474, 72, 27, 'مگر پيامبرى را كه [براى اين امر] بپسندد كه از پيش رو و پشت سرش مراقبانى گسيل مى‌دارد'),
(5475, 72, 28, 'تا بداند كه محققا پيام‌هاى پروردگار خود را رسانده‌اند و [خدا] بدانچه نزد ايشان است احاطه دارد و هر چيزى را به عدد بر شمرده است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'هان، اى جامه به خود پيچيده'),
(5477, 73, 2, 'شب را جز اندكى، [به عبادت‌] برخي'),
(5478, 73, 3, 'نيمى از آن را، يا اندكى از آن بكاه'),
(5479, 73, 4, 'يا بر آن [نيمه‌] بيفزاى و قرآن را شمرده و با تأمل بخوان'),
(5480, 73, 5, 'همانا ما به زودى بر تو گفتارى گران القا خواهيم كرد'),
(5481, 73, 6, 'مسلما پديده‌ى شب [خيزى‌] داراى استحكام بيشتر و ذكر و قرائت [در آن‌] استوارتر است'),
(5482, 73, 7, 'تحقيقا تو را در روز آمد و شدى طولانى است'),
(5483, 73, 8, 'و نام پروردگار خود را ياد كن و [از همه‌] بريده يكسره به سوى او روى آور'),
(5484, 73, 9, '[اوست‌] پروردگار مشرق و مغرب كه معبودى جز او نيست، پس او را كارساز خويش گير'),
(5485, 73, 10, 'و بر آنچه مى‌گويند صبر كن و به شيوه‌اى نيكو از آنان دورى گزين'),
(5486, 73, 11, 'و مرا با تكذيب كنندگان صاحب نعمت واگذار و اندكى مهلتشان ده'),
(5487, 73, 12, 'همانا نزد ما زنجيرها و آتش افروخته است'),
(5488, 73, 13, 'و طعامى گلوگير و عذابى دردناك'),
(5489, 73, 14, 'روزى كه زمين و كوه‌ها به لرزه در آيند و كوه‌ها توده‌اى از ريگ روان شوند'),
(5490, 73, 15, 'در حقيقت ما به سوى شما رسولى كه گواه بر شماست روانه كرديم، همان‌گونه كه رسولى به سوى فرعون فرستاديم'),
(5491, 73, 16, '[ولى‌] فرعون آن رسول را نافرمانى كرد پس ما او را به شدّت فرو گرفتيم'),
(5492, 73, 17, 'پس اگر كافر شويد، چگونه از [عذاب‌] روزى كه كودكان را پير مى‌كند خود را حفظ مى‌كنيد'),
(5493, 73, 18, '[روزى كه‌] آسمان از [هيبت‌] آن در هم شكافد. وعده‌ى او انجام شدنى است'),
(5494, 73, 19, 'بى‌ترديد اين [آيات‌] تذكارى است، پس هر كه خواست، به سوى پروردگار خود راهى در پيش گيرد'),
(5495, 73, 20, 'قطعا پروردگارت مى‌داند كه تو نزديك به دو ثلث از شب و گاه نصف آن يا ثلث آن را [به نماز] بر مى‌خيزى و گروهى از آنان كه با تو هستند نيز [چنين مى‌كنند]، و خداست كه اندازه‌ى شب و روز را تقدير مى‌كند. مى‌داند كه شما هرگز نمى‌توانيد حساب آن را [براى زمان عب');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'هان، اى جامه بر خود كشيده'),
(5497, 74, 2, 'برخيز و بترسان'),
(5498, 74, 3, 'و پروردگار خود را به بزرگى ياد كن'),
(5499, 74, 4, 'و جامه‌ات را پاكيزه دار'),
(5500, 74, 5, 'و از پليدى دورى كن'),
(5501, 74, 6, 'و [براى احسانت‌] منّت نگذار و آن را زياد مشمار'),
(5502, 74, 7, 'و براى پروردگارت شكيبا باش'),
(5503, 74, 8, 'پس چون در صور دميده شود'),
(5504, 74, 9, 'آن روز، روز سختى خواهد بود'),
(5505, 74, 10, 'بر كافران آسان نيست'),
(5506, 74, 11, 'مرا واگذار با آن كس كه [او را] تنها آفريدم'),
(5507, 74, 12, 'و به او مالى بسيار دادم'),
(5508, 74, 13, 'و پسرانى حاضر [به خدمت دادم‌]'),
(5509, 74, 14, 'و برايش بساط [زندگى خوش‌] گستردم'),
(5510, 74, 15, 'باز [هم‌] طمع دارد كه زياد كنم'),
(5511, 74, 16, 'حاشا! همانا او نسبت به آيات ما عناد دارد'),
(5512, 74, 17, 'زودا كه او را به بالا رفتن از گردنه [ى عذاب‌] وادار كنم'),
(5513, 74, 18, 'همانا او [براى مبارزه با قرآن‌] فكر كرد و سنجيد'),
(5514, 74, 19, 'پس مرگ بر او، چگونه سنجيد؟ [و تصميم گرفت.'),
(5515, 74, 20, 'باز هم مرگ بر او، چگونه سنجيد'),
(5516, 74, 21, 'سپس نگاهى افكند'),
(5517, 74, 22, 'آن‌گاه رو ترش كرد و چهره در هم كشيد'),
(5518, 74, 23, 'سپس رو گردانيد و تكبر ورزيد'),
(5519, 74, 24, 'و [در آخر] گفت: اين [قرآن‌] جز سحرى كه [از پيشينيان‌] نقل مى‌شود، هيچ نيست'),
(5520, 74, 25, 'اين نيست مگر سخن آدمى'),
(5521, 74, 26, 'زودا كه او را به «سقر» درآورم'),
(5522, 74, 27, 'و تو چه مى‌دانى كه «سقر» چيست'),
(5523, 74, 28, 'نه باقى مى‌گذارد و نه رها مى‌كند'),
(5524, 74, 29, 'پوست‌ها را سياه مى‌گرداند'),
(5525, 74, 30, 'بر آن [دوزخ‌] نوزده [نگهبان‌] است'),
(5526, 74, 31, 'و ما آتشبانان را جز فرشتگانى قرار نداديم، و تعداد آنها را جز براى امتحان كافران مقرر نكرديم، تا آنان كه اهل كتابند به يقين برسند [كه پيامبر حق است، چون اين مطلب در كتاب آنها نيز هست،] و بر ايمان مؤمنان [نيز] بيفزايد، و اهل كتاب و مؤمنان [در حقانيت قر'),
(5527, 74, 32, 'نه چنين است [كه مى‌پندارند]، سوگند به ماه'),
(5528, 74, 33, 'سوگند به شب چون پشت كند'),
(5529, 74, 34, 'سوگند به بامداد چون رخ نمايد'),
(5530, 74, 35, 'كه قطعا آن [قيامت يا قرآن‌] يكى از بزرگ‌ترين پديده‌هاست'),
(5531, 74, 36, 'هشدارى براى بشر است'),
(5532, 74, 37, 'براى هر كس از شما كه بخواهد [در اطاعت‌] پيش افتد يا پس افتد'),
(5533, 74, 38, 'هر كسى در گرو دستاورد خويش است'),
(5534, 74, 39, 'مگر اهل سعادت، [كه اعمال بد ندارند'),
(5535, 74, 40, 'كه در بهشت‌ها [نشسته‌] از هم مى‌پرسند'),
(5536, 74, 41, 'در باره‌ى مجرمان'),
(5537, 74, 42, '[به آنها مى‌گويند:] چه چيز شما را به «سقر» كشانيد'),
(5538, 74, 43, 'گويند: ما از نمازگزاران نبوديم'),
(5539, 74, 44, 'و بينوايان را غذا نمى‌داديم'),
(5540, 74, 45, 'و با آنها كه باطل مى‌بافتند هم آواز مى‌شديم'),
(5541, 74, 46, 'و همواره روز جزا را تكذيب مى‌كرديم'),
(5542, 74, 47, 'تا آن كه مرگ به سراغمان آمد'),
(5543, 74, 48, 'پس شفاعت شفيعان سودشان نبخشد'),
(5544, 74, 49, 'پس آنها را چه شده است كه از اين تذكار [قرآن‌] روى گردانند'),
(5545, 74, 50, 'گويى آنان خرانى رمنده‌اند'),
(5546, 74, 51, 'كه از شيرى گريخته‌اند'),
(5547, 74, 52, 'بلكه هر فردى از آنها انتظار دارد كه وى را نامه‌هاى سرگشاده بدهند'),
(5548, 74, 53, 'چنين نيست، بلكه آنها از آخرت نمى‌ترسند'),
(5549, 74, 54, 'چنين نيست، بى‌ترديد اين [قرآن‌] تذكارى است'),
(5550, 74, 55, 'پس هر كه خواست از آن پند گيرد'),
(5551, 74, 56, 'ولى پند نگيرند مگر آن كه خدا خواهد. او سزاوار پروا و او سزاوار آمرزش است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'نه، سوگند به روز رستاخيز'),
(5553, 75, 2, 'و [باز] نه، سوگند به نفس ملامتگر [كه زنده مى‌شويد]'),
(5554, 75, 3, 'آيا انسان مى‌پندارد كه هرگز استخوان‌هايش را جمع نخواهيم كرد'),
(5555, 75, 4, 'چرا، در حالى كه قادريم بر اين كه سر انگشتان او را هم بازسازيم'),
(5556, 75, 5, '[انكار معاد دليل موجهى ندارد]، بلكه انسان مى‌خواهد در آينده‌ى خود بدكارى كند'),
(5557, 75, 6, '[از سر انكار] مى‌پرسد: روز رستاخيز چه وقت است'),
(5558, 75, 7, 'پس آن‌گاه كه چشم [از وحشت‌] خيره گردد'),
(5559, 75, 8, 'و ماه در خسوف افتد'),
(5560, 75, 9, 'و خورشيد و ماه يك جا جمع شوند'),
(5561, 75, 10, 'آن روز انسان مى‌گويد: گريزگاه كجاست'),
(5562, 75, 11, 'حاشا! پناهگاهى نيست'),
(5563, 75, 12, 'آن روز قرارگاه [نهايى‌] به سوى پروردگار توست'),
(5564, 75, 13, 'آن روز انسان بدانچه پيش فرستاده و بعد فرستاده آگاهى داده مى‌شود'),
(5565, 75, 14, 'بلكه انسان خود بر [نيك و بد] خويشتن بيناست'),
(5566, 75, 15, 'هر چند [به زبان‌] عذرهايش را پيش آورد'),
(5567, 75, 16, 'زبانت را به قرآن [پيش از تمام شدن وحيش‌] حركت مده كه به [قرائت‌] آن شتاب كنى'),
(5568, 75, 17, 'چرا كه جمع آورى و قرائت آن بر [عهده‌ى‌] ماست'),
(5569, 75, 18, 'پس چون آن را قرائت كرديم، تو آن قرائت را پيروى كن'),
(5570, 75, 19, 'سپس توضيح آن [نيز] بر عهده‌ى ماست'),
(5571, 75, 20, 'ولى نه، [شما دنياى‌] زود گذر را دوست مى‌داريد'),
(5572, 75, 21, 'و آخرت را وا مى‌گذاريد'),
(5573, 75, 22, 'در آن روز چهره‌هايى خرم و شادابند'),
(5574, 75, 23, 'به سوى پروردگار خود مى‌نگرند'),
(5575, 75, 24, 'و چهره‌هايى در آن روز عبوس و درهم است'),
(5576, 75, 25, '[چرا كه‌] مى‌داند عذاب شكننده‌اى در پيش دارد'),
(5577, 75, 26, 'نه، چنين نيست، آن‌گاه كه جان به گلوگاه رسيد'),
(5578, 75, 27, 'و گفته شد: چاره‌ساز كيست'),
(5579, 75, 28, 'و دانست كه آن، هنگامه جدايى [از دنيا] است'),
(5580, 75, 29, 'و [محتضر از سختى جان دادن‌] ساق‌هايش به هم پيچيد'),
(5581, 75, 30, 'آن روز، روز رانده شدن به سوى پروردگار تو [براى حساب‌] است'),
(5582, 75, 31, 'پس نه تصديق كرد و نه نماز گزارد'),
(5583, 75, 32, 'بلكه تكذيب كرد و روى گردانيد'),
(5584, 75, 33, 'سپس مغرورانه سوى كسانش رفت'),
(5585, 75, 34, '[با اين اعمال‌] واى بر تو! پس واى بر تو'),
(5586, 75, 35, 'باز هم واى بر تو! سپس واى بر تو'),
(5587, 75, 36, 'آيا انسان مى‌پندارد كه بى‌هدف رها مى‌شود'),
(5588, 75, 37, 'مگر او نطفه‌اى كه [در رحم‌] ريخته مى‌شود نبود'),
(5589, 75, 38, 'سپس خون بسته‌اى شد و [خدايش‌] شكل داد و درست كرد'),
(5590, 75, 39, 'پس دو جنس نر و ماده را از آن، پديد آورد'),
(5591, 75, 40, 'آيا اين [خدا] قادر نيست كه مردگان را زنده كند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'مگر نه اين است كه مدت زمانى بر انسان گذشت كه چيز قابل ذكرى نبود'),
(5593, 76, 2, 'همانا ما انسان را از نطفه‌اى آميخته آفريديم، كه او را خواهيم آزمود، پس وى را شنوا و بينا گردانيديم'),
(5594, 76, 3, 'ما راه را به او نشان داديم، يا سپاسگزار است [مى‌پذيرد] و يا كفران [مى‌كند]'),
(5595, 76, 4, 'بى‌گمان، ما براى كافران زنجيرها و بندها و شعله‌هاى سوزان آماده كرده‌ايم'),
(5596, 76, 5, 'همانا نيكان از جامى نوشند كه با عطر خوشى آميخته است'),
(5597, 76, 6, 'چشمه‌اى كه بندگان [خاص‌] خدا از آن بنوشند و [هر جا بخواهند] جارى‌اش سازند'),
(5598, 76, 7, '[آنها] به نذر خود وفا كنند و از روزى كه شرّ آن فراگير است بترسند'),
(5599, 76, 8, 'و طعام را با آن كه دوستش دارند، به مستمند و يتيم و اسير دهند'),
(5600, 76, 9, '[و در دل گويند:] ما فقط براى رضاى خدا شما را اطعام مى‌كنيم و هيچ پاداش و سپاسى از شما نمى‌خواهيم'),
(5601, 76, 10, 'ما از پروردگار خود از روزى كه سخت غمبار است هراسانيم'),
(5602, 76, 11, 'پس خدا آنها را از شرّ آن روز نگاه دارد و بهجت و سرور به آنها ارزانى دارد'),
(5603, 76, 12, 'و به پاس آن كه صبر كردند، بهشت و حرير پاداششان دهد'),
(5604, 76, 13, 'كه در آن جا بر تخت‌ها تكيه زنند. در آن جا نه [گرماى‌] آفتاب بينند و نه سرما'),
(5605, 76, 14, 'و سايه‌ها [ى درختان‌] بر آنها نزديك و ميوه‌هايش [براى چيدن‌] بسيار آسان است'),
(5606, 76, 15, 'و ظروف سيمين و جام‌هاى بلورين بر آنها بگردانند'),
(5607, 76, 16, 'جام‌هايى بلورين از نقره كه درست به اندازه باشند'),
(5608, 76, 17, 'و در آن جا از جامى كه آميزه‌ى زنجبيل دارد به آنان مى‌نوشانند'),
(5609, 76, 18, 'از چشمه‌اى در بهشت كه «سلسبيل» ناميده مى‌شود'),
(5610, 76, 19, 'و بر گرد آنها پسرانى هميشه جوان بگردند كه چون آنها را ببينى، پنداريشان كه مرواريدهايى پراكنده‌اند'),
(5611, 76, 20, 'و چون آن جا را بنگرى، ناز و نعمت [فراوان‌] و ملكى بى‌كران ببينى'),
(5612, 76, 21, 'بر تنشان جامه‌هاى ابريشمى سبز و ديباى ستبر است و با دستبندهاى سيمين آراسته شده‌اند، و پروردگارشان باده‌اى پاك به آنان بنوشاند'),
(5613, 76, 22, 'همانا اين پاداشى براى شماست و از تلاش شما سپاسگزارى شده است'),
(5614, 76, 23, 'به تحقيق ما قرآن را آن طور كه بايد بر تو نازل كرديم'),
(5615, 76, 24, 'پس براى حكم پروردگارت صبور باش، و از آنها هيچ گناه پيشه يا ناسپاسى را اطاعت مكن'),
(5616, 76, 25, 'و هر صبح و شام نام پروردگارت را ياد كن'),
(5617, 76, 26, 'و پاسى از شب را براى او سجده كن و بخشى طولانى از شب او را تسبيح گوى'),
(5618, 76, 27, 'به تحقيق اينان دنياى زود گذر را دوست مى‌دارند و روزى گرانبار را پشت سرشان رها مى‌كنند [و به فراموشى مى‌سپارند]'),
(5619, 76, 28, 'ما آنها را آفريديم و بندبندشان را استوار كرديم، و هر وقت بخواهيم [آنها را مى‌بريم و] به راحتى امثالشان را جايگزين مى‌كنيم'),
(5620, 76, 29, 'مسلما اين [آيات‌] تذكارى است، پس هر كه خواهد راهى به سوى پروردگار خود در پيش گيرد'),
(5621, 76, 30, 'و [اين را] نمى‌خواهيد مگر آن كه خدا بخواهد، قطعا خدا داناى حكيم است'),
(5622, 76, 31, 'هر كه را خواهد به رحمت خويش درمى‌آورد و براى ظالمان عذابى دردناك آماده كرده است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به فرستادگان پى‌درپى'),
(5624, 77, 2, 'كه سخت توفنده‌اند'),
(5625, 77, 3, 'و سوگند به نشر دهندگان به نشرى خاص'),
(5626, 77, 4, 'كه به وضوح و آشكار جدا كننده‌اند'),
(5627, 77, 5, 'و وحى را القا مى‌كنند'),
(5628, 77, 6, 'براى [اتمام حجت و رفع‌] بهانه يا براى هشدار'),
(5629, 77, 7, 'كه آنچه وعده داده مى‌شويد قطعا رخ دادنى است'),
(5630, 77, 8, 'پس وقتى كه ستارگان محو شوند'),
(5631, 77, 9, 'و آن‌گاه كه آسمان شكافته شود'),
(5632, 77, 10, 'و آن‌گاه كه كوه‌ها از جا كنده شوند'),
(5633, 77, 11, 'و آن‌گاه كه براى پيامبران تعيين وقت شود'),
(5634, 77, 12, 'براى چه روزى تعيين وقت شده است'),
(5635, 77, 13, 'براى روز جدايى [حق از باطل‌]'),
(5636, 77, 14, 'و تو چه دانى كه روز جدايى چيست'),
(5637, 77, 15, 'واى در آن روز بر تكذيب كنندگان'),
(5638, 77, 16, 'آيا ما پيشينيان را هلاك نكرديم'),
(5639, 77, 17, 'سپس پسينيان [از مجرمان‌] را از پى آنها مى‌فرستيم'),
(5640, 77, 18, 'ما با مجرمان چنين مى‌كنيم'),
(5641, 77, 19, 'واى در آن روز بر تكذيب كنندگان'),
(5642, 77, 20, 'آيا شما را از آبى ناچيز نيافريديم'),
(5643, 77, 21, 'پس آن را در جايگاهى استوار نهاديم'),
(5644, 77, 22, 'تا وقتى معلوم'),
(5645, 77, 23, 'پس توانا بوديم و نيكو تواناييم'),
(5646, 77, 24, 'واى در آن روز بر تكذيب كنندگان'),
(5647, 77, 25, 'آيا زمين را در برگيرنده [ى انسان‌ها] قرار نداديم'),
(5648, 77, 26, 'در حال حيات و در حال مرگشان'),
(5649, 77, 27, 'و كوه‌هاى استوار بلند در آن نهاديم و به شما آبى گوارا نوشانديم'),
(5650, 77, 28, 'واى در آن روز بر تكذيب كنندگان'),
(5651, 77, 29, 'برويد به سوى همان چيزى كه تكذيبش مى‌كرديد'),
(5652, 77, 30, 'به سوى دودى سه شاخه به راه افتيد'),
(5653, 77, 31, 'نه سايبان است و نه از لهيب آتش باز مى‌دارد'),
(5654, 77, 32, 'همانا آن [دوزخ‌] شراره‌هايى چون كاخ پرتاب مى‌كند'),
(5655, 77, 33, 'گويى اشترانى زردرنگند'),
(5656, 77, 34, 'واى در آن روز بر تكذيب كنندگان'),
(5657, 77, 35, 'اين روزى است كه سخن نمى‌گويند'),
(5658, 77, 36, 'و به آنها رخصت داده نمى‌شود تا پوزش خواهند'),
(5659, 77, 37, 'واى در آن روز بر تكذيب كنندگان'),
(5660, 77, 38, 'اين، روز فيصله [و سرنوشت ساز] است كه شما و پيشينيان را گرد مى‌آوريم'),
(5661, 77, 39, 'پس اگر نيرنگى داريد، در برابر من به كار گيريد'),
(5662, 77, 40, 'واى در آن روز بر تكذيب كنندگان'),
(5663, 77, 41, 'همانا پارسايان زير سايه‌ها و كنار چشمه‌سارانند'),
(5664, 77, 42, 'و ميوه‌هايى از آنچه خوش دارند'),
(5665, 77, 43, 'به [پاداش‌] آنچه مى‌كرديد بخوريد و بياشاميد، گوارايتان باد'),
(5666, 77, 44, 'بى‌ترديد ما نيكوكاران را چنين پاداش مى‌دهيم'),
(5667, 77, 45, 'واى در آن روز بر تكذيب‌كنندگان'),
(5668, 77, 46, '[اى كافران‌] در اندك [زمان زندگى‌] بخوريد برخوردار شويد كه شما گناهكاريد'),
(5669, 77, 47, 'واى در آن روز بر تكذيب كنندگان'),
(5670, 77, 48, 'و چون به آنها گفته شود: ركوع كنيد، ركوع نمى‌كنند'),
(5671, 77, 49, 'واى در آن روز بر تكذيب كنندگان'),
(5672, 77, 50, 'پس به كدامين سخن بعد از آن (قرآن) ايمان مى‌آورند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'در باره‌ى چه چيز از يكديگر مى‌پرسند'),
(5674, 78, 2, 'در باره‌ى آن خبر بزرگ'),
(5675, 78, 3, 'همان كه همواره در آن اختلاف دارند'),
(5676, 78, 4, 'چنين نيست [كه مى‌پندارند]، زودا كه بدانند'),
(5677, 78, 5, 'باز هم چنين نيست [كه مى‌پندارند]، زودا كه بدانند'),
(5678, 78, 6, 'آيا زمين را آسايشگاه نكرديم'),
(5679, 78, 7, 'و كوه‌ها را ميخ‌هاى [آن قرار نداديم‌]'),
(5680, 78, 8, 'و شما را زوج‌ها آفريديم'),
(5681, 78, 9, 'و خوابتان را مايه آسايش قرار داديم'),
(5682, 78, 10, 'و شب را پوششى گردانيديم'),
(5683, 78, 11, 'و روز را براى كسب و كار ساختيم'),
(5684, 78, 12, 'و بر فراز شما هفت آسمان استوار بنا كرديم'),
(5685, 78, 13, 'و [در آن‌] چراغى پرتو افشان نهاديم'),
(5686, 78, 14, 'و از ابرهاى فشرده آبى ريزان فرو فرستاديم'),
(5687, 78, 15, 'تا به وسيله آن، دانه و گياه بر آوريم'),
(5688, 78, 16, 'و باغ‌هاى پردرخت'),
(5689, 78, 17, 'به يقين روز داورى زمان وعده است'),
(5690, 78, 18, 'روزى كه در صور دميده شود، پس گروه گروه بياييد'),
(5691, 78, 19, 'و آسمان گشوده شود، پس درهايى شود'),
(5692, 78, 20, 'و كوه‌ها به راه انداخته شوند و سرابى گردند'),
(5693, 78, 21, 'بى‌ترديد دوزخ كمينگاهى شود'),
(5694, 78, 22, 'براى سركشان محل بازگشت است'),
(5695, 78, 23, 'كه روزگارانى دراز در آن بمانند'),
(5696, 78, 24, 'نه خنكى در آن مى‌چشند و نه نوشيدنى'),
(5697, 78, 25, 'مگر آبى جوشان و خونابه'),
(5698, 78, 26, 'پاداشى است موافق كردار'),
(5699, 78, 27, 'چرا كه آنان هيچ حسابى را باور نداشتند'),
(5700, 78, 28, 'و آيات ما را سخت دروغ مى‌شمردند'),
(5701, 78, 29, 'و حال آن كه ما همه چيز را به صورت مكتوب بر شمرده‌ايم'),
(5702, 78, 30, 'پس بچشيد كه هرگز جز عذابتان نيفزاييم'),
(5703, 78, 31, 'مسلما براى پارسايان كاميابى بزرگى است'),
(5704, 78, 32, 'باغ‌ها و انواع انگورها'),
(5705, 78, 33, 'و دختران نارپستان همسال'),
(5706, 78, 34, 'و جام‌هاى سرشار'),
(5707, 78, 35, 'نه سخن ناروا در آن مى‌شنوند و نه دروغى'),
(5708, 78, 36, 'پاداشى است از پروردگارت و بخششى است به حساب'),
(5709, 78, 37, 'پروردگار آسمان‌ها و زمين و آنچه ميان آنهاست، خداى رحمان كه [مردمان‌] را از جانب او ياراى سخن نباشد'),
(5710, 78, 38, 'روزى كه آن روح و فرشتگان به صف ايستند، سخن نگويند مگر كسى كه خداى رحمان به او اذن دهد و صواب را گويد [آنچه تصويب شده‌]'),
(5711, 78, 39, 'آن روز، روز حق است پس هر كه خواهد، به سوى پروردگارش بازگشتى پيش گيرد'),
(5712, 78, 40, 'همانا ما از عذابى نزديك بيمتان داديم، روزى كه شخص به آنچه پيش فرستاده بنگرد و كافر گويد: كاش خاك بودمى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به فرشتگانى كه به سختى [جان‌ها] را بر مى‌كنند'),
(5714, 79, 2, 'و فرشتگانى كه [جان مؤمنان‌] را بامدارا مى‌گيرند'),
(5715, 79, 3, 'و سوگند به فرشتگانى كه بسى تيزروند'),
(5716, 79, 4, 'پس بر يكديگر پيشى گيرند'),
(5717, 79, 5, 'و آنان كه امور را تدبير مى‌كنند'),
(5718, 79, 6, 'روزى كه آن زلزله [وحشتناك‌] همه چيز را بلرزاند'),
(5719, 79, 7, 'و در پى آن لرزه‌اى [ديگر] رخ دهد'),
(5720, 79, 8, 'دل‌هايى در آن روز مضطرب باشد'),
(5721, 79, 9, 'ديدگانشان هراسناك و فرو شكسته است'),
(5722, 79, 10, 'گويند: آيا ما به زندگى مجدد بازگردانده مى‌شويم'),
(5723, 79, 11, 'آيا وقتى كه استخوان‌هاى پوسيده‌اى شديم [دوباره باز مى‌گرديم‌]'),
(5724, 79, 12, 'گويند: در اين صورت آن باز گشتى زيانبار است'),
(5725, 79, 13, 'ولى اين بازگشت تنها با يك نهيب واقع مى‌شود'),
(5726, 79, 14, 'پس به ناگاه همگى بر عرصه محشر ظاهر مى‌شوند'),
(5727, 79, 15, 'آيا داستان موسى به تو رسيده است'),
(5728, 79, 16, 'آن وقت كه پروردگارش او را در وادى مقدس «طوى» ندا داد'),
(5729, 79, 17, 'به سوى فرعون برو كه او سر برداشته است'),
(5730, 79, 18, 'پس بگو: آيا مى‌خواهى پاكيزه شوى'),
(5731, 79, 19, 'و من تو را به سوى پروردگارت راه نمايم تا خدا ترس شوى'),
(5732, 79, 20, 'پس بزرگ‌ترين معجزه را به وى نشان داد'),
(5733, 79, 21, 'اما او تكذيب كرد و نافرمانى نمود'),
(5734, 79, 22, 'آن‌گاه روى گردانيد و دست به كار شد'),
(5735, 79, 23, '[و مردم را] جمع كرد و فرياد بر آورد'),
(5736, 79, 24, 'پس گفت: من پروردگار برتر شمايم'),
(5737, 79, 25, 'پس خدا او را به كيفر آخرت و عذاب اين دنيا بگرفت'),
(5738, 79, 26, 'به راستى در اين [داستان‌] عبرتى است براى كسى كه بترسد'),
(5739, 79, 27, 'آيا آفرينش شما سخت‌تر است يا آسمان كه بنايش كرد'),
(5740, 79, 28, 'سقفش را برافراشت و آن را سامان داد'),
(5741, 79, 29, 'و شبش را تاريك و روزش را نمايان كرد'),
(5742, 79, 30, 'و پس از آن زمين را بگسترد'),
(5743, 79, 31, 'آب و چراگاهش را از آن بيرون آورد'),
(5744, 79, 32, 'و كوه‌ها را استوار نمود'),
(5745, 79, 33, 'تا شما و چهارپايانتان را بهره‌اى باشد'),
(5746, 79, 34, 'پس چون آن مصيبت بزرگ در رسد'),
(5747, 79, 35, 'آن روز است كه آدمى آنچه را كوشيده به ياد آورد'),
(5748, 79, 36, 'و دوزخ براى هر كسى ببيند ظاهر گردد'),
(5749, 79, 37, 'پس اما آن كه سركشى كرد'),
(5750, 79, 38, 'و زندگى پست‌تر را برگزيد'),
(5751, 79, 39, 'پس همانا دوزخ جايگاه اوست'),
(5752, 79, 40, 'و اما آن كس كه از مقام پروردگارش ترسان بود و خويشتن را از هوس‌ها بازداشت'),
(5753, 79, 41, 'پس همانا بهشت جايگاه اوست'),
(5754, 79, 42, 'از تو در باره‌ى رستاخيز مى‌پرسند كه برپائيش كى است'),
(5755, 79, 43, 'تو را با سخن گفتن در آن چه كار'),
(5756, 79, 44, 'سر انجام آن به جانب پروردگار توست'),
(5757, 79, 45, 'تو فقط بيم دهنده‌ى كسى هستى كه از آن بترسد'),
(5758, 79, 46, 'روزى كه آن را ببينند [پندارند كه‌]، گويى توقفشان [در دنيا] جز شامگاهى يا بامدادى بيش نبوده است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'رو ترش كرد و رخ برتافت'),
(5760, 80, 2, 'كه آن نابينا نزد وى آمد'),
(5761, 80, 3, 'و تو چه دانى؟ شايد او پاك شود'),
(5762, 80, 4, 'يا پند گيرد و آن پند سودش دهد'),
(5763, 80, 5, 'اما آن كه [از پند تو] اظهار بى‌نيازى مى‌كند'),
(5764, 80, 6, 'پس تو به او مى‌پردازى'),
(5765, 80, 7, 'و اگر هم پاك نشود، گناهى بر تو نيست'),
(5766, 80, 8, 'و اما آن كه شتابان به سويت آمده'),
(5767, 80, 9, 'در حالى كه از خدا مى‌ترسد'),
(5768, 80, 10, 'پس تو از او به ديگرى مى‌پردازى'),
(5769, 80, 11, 'چنين مپندار، كه اين [آيات‌] تذكارى است'),
(5770, 80, 12, 'پس هر كه خواهد آن را ياد كند'),
(5771, 80, 13, '[كه‌] در صحيفه‌هايى ارجمند'),
(5772, 80, 14, 'والا و پاكيزه'),
(5773, 80, 15, 'به دست سفيرانى'),
(5774, 80, 16, 'گرامى و نيكوكار است'),
(5775, 80, 17, 'مرگ بر اين انسان، چقدر حق پوش و ناسپاس است'),
(5776, 80, 18, 'خدا او را از چه چيزى آفريده'),
(5777, 80, 19, 'او را از نطفه‌اى آفريد و موزونش ساخت'),
(5778, 80, 20, 'آن‌گاه راه [ايمان و اطاعت‌] را بر او آسان كرد'),
(5779, 80, 21, 'سپس وى را بميراند و به گورش برد'),
(5780, 80, 22, 'سپس هر وقت بخواهد زنده‌اش مى‌كند'),
(5781, 80, 23, 'ولى نه! هنوز [انسان‌] آنچه را كه خدايش فرمان داده به جا نياورده است'),
(5782, 80, 24, 'پس آدمى بايد طعام خويش را بنگرد'),
(5783, 80, 25, 'به تحقيق ما آب فراوانى [از آسمان‌] فرو ريختيم'),
(5784, 80, 26, 'سپس زمين را از هم شكافتيم، شكافتنى'),
(5785, 80, 27, 'و در آن دانه‌ها برويانديم'),
(5786, 80, 28, 'و انگور و سبزى'),
(5787, 80, 29, 'و زيتون و خرما'),
(5788, 80, 30, 'و باغ‌هاى پردرخت'),
(5789, 80, 31, 'و ميوه و علفزار'),
(5790, 80, 32, 'تا متاعى براى شما و چهارپايانتان باشد'),
(5791, 80, 33, 'پس چون آن صيحه گوش خراش در رسد'),
(5792, 80, 34, 'روزى كه آدمى از برادرش بگريزد'),
(5793, 80, 35, 'و از مادر و پدرش'),
(5794, 80, 36, 'و از همسر و فرزندانش'),
(5795, 80, 37, 'در آن روز هر كسى از مردم وضعى دارد كه به خود مشغولش سازد'),
(5796, 80, 38, 'آن روز بعضى چهره‌ها باز و روشن‌اند'),
(5797, 80, 39, 'خندان و شادمانند'),
(5798, 80, 40, 'و برخى چهره‌ها آن روز [گرفته‌اند و] بر آنها غبار [اندوه‌] نشسته است'),
(5799, 80, 41, 'تيرگى و سياهى آنها را پوشانده است'),
(5800, 80, 42, 'آنان همان كافران بدكارند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آن‌گاه كه خورشيد درهم پيچيده شود'),
(5802, 81, 2, 'و آن‌گاه كه ستارگان تيره شوند [و فرو ريزند]'),
(5803, 81, 3, 'و آن‌گاه كه كوه‌ها به راه افتند [و خرد شوند]'),
(5804, 81, 4, 'و آن‌گاه كه شتران آبستن وا نهاده شوند'),
(5805, 81, 5, 'و آن‌گاه كه درندگان گرد آورده شوند'),
(5806, 81, 6, 'و آن‌گاه كه درياها بجوشند'),
(5807, 81, 7, 'و آن‌گاه كه جان‌ها جفت شوند [و با قرين خود همراه گردند]'),
(5808, 81, 8, 'و آن‌گاه كه از دختر زنده به گور پرسيده شود'),
(5809, 81, 9, 'كه به كدامين گناه كشته شده است'),
(5810, 81, 10, 'و آن‌گاه كه نامه‌هاى اعمال گشوده شود'),
(5811, 81, 11, 'و آن‌گاه كه آسمان بركنده شود'),
(5812, 81, 12, 'و آن‌گاه كه دوزخ شعله‌ور گردد'),
(5813, 81, 13, 'و آن‌گاه كه بهشت نزديك آورده شود'),
(5814, 81, 14, '[در آن روز] هر كس داند چه حاضر كرده است'),
(5815, 81, 15, 'پس سوگند به اختران بازگردنده'),
(5816, 81, 16, 'كه مى‌روند و نهان مى‌شوند'),
(5817, 81, 17, 'و سوگند به شب، آن‌گاه كه پشت كند'),
(5818, 81, 18, 'و سوگند به صبح چون بردمد'),
(5819, 81, 19, 'كه قطعا اين قرآن سخن فرستاده‌اى ارجمند است (جبرئيل)'),
(5820, 81, 20, 'نيرومندى كه نزد خداوند عرش، منزلت والايى دارد'),
(5821, 81, 21, 'در آن جا فرمانروا و امين است'),
(5822, 81, 22, 'و اين رفيق شما ديوانه نيست'),
(5823, 81, 23, 'او [جبرئيل‌] را در افق روشن ديده است'),
(5824, 81, 24, 'و او در [ابلاغ‌] وحى بخيل نيست'),
(5825, 81, 25, 'و اين [قرآن‌] سخن شيطان مطرود نيست'),
(5826, 81, 26, 'پس به كجا مى‌رويد'),
(5827, 81, 27, 'اين جز تذكارى براى جهانيان نيست'),
(5828, 81, 28, 'براى هر كس از شما كه خواهد راست و مستقيم شود'),
(5829, 81, 29, 'و شما نمى‌خواهيد مگر آن كه خدا، پروردگار جهانيان، بخواهد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'آن‌گاه كه آسمان بشكافد'),
(5831, 82, 2, 'و آن‌گاه كه اختران پراكنده شوند'),
(5832, 82, 3, 'و آن‌گاه كه درياها درهم روان شوند'),
(5833, 82, 4, 'و آن‌گاه كه قبرها زير و رو شوند'),
(5834, 82, 5, 'هر كسى بداند آنچه را پيش فرستاده و آنچه را پس نهاده است'),
(5835, 82, 6, 'اى انسان! چيست كه تو را در برابر پروردگار كريمت مغرور ساخته'),
(5836, 82, 7, 'همان كه تو را آفريد و پرداخت و تناسب بخشيد'),
(5837, 82, 8, 'به هر صورتى كه خواست تو را تركيب نمود'),
(5838, 82, 9, 'چنين نيست [كه مى‌پنداريد،] بلكه شما رستاخيز را دروغ مى‌انگاريد'),
(5839, 82, 10, 'و قطعا بر شما نگاهبانانى است'),
(5840, 82, 11, 'نويسندگانى ارجمند'),
(5841, 82, 12, 'مى‌دانند آنچه مى‌كنيد'),
(5842, 82, 13, 'همانا نيكان در بهشت پر نعمت‌اند'),
(5843, 82, 14, 'و به يقين بدكاران در دوزخند'),
(5844, 82, 15, 'كه روز جزا در آن مى‌سوزند'),
(5845, 82, 16, 'و هيچ‌گاه از آن دور نباشند، [از آن بيرون نيايند]'),
(5846, 82, 17, 'تو چه مى‌دانى روز جزا چيست'),
(5847, 82, 18, 'باز تو چه مى‌دانى روز جزا چيست'),
(5848, 82, 19, 'روزى است كه هيچ كس براى كسى اختيار چيزى را ندارد، و حكم در آن روز مخصوص خداست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'واى بر كم فروشان'),
(5850, 83, 2, 'آنان كه چون از مردم پيمانه بستانند، تمام مى‌ستانند'),
(5851, 83, 3, 'و چون به ديگران پيمانه دهند يا وزن كنند، كم مى‌دهند'),
(5852, 83, 4, 'آيا آنان فكر نمى‌كنند كه برانگيخته مى‌شوند'),
(5853, 83, 5, 'براى روزى بزرگ'),
(5854, 83, 6, 'روزى كه مردم در پيشگاه پروردگار جهانيان بايستند'),
(5855, 83, 7, 'چنين نيست [كه خيال مى‌كنند،] مسلما كارنامه بدكاران در «سجّين» است'),
(5856, 83, 8, 'و تو چه مى‌دانى «سجّين» چيست'),
(5857, 83, 9, 'كارنامه‌اى است نوشته و خوانا [كه مجازات اعمال در آن است‌]'),
(5858, 83, 10, 'واى در آن روز بر تكذيب كنندگان'),
(5859, 83, 11, 'همانان كه روز جزا را دروغ مى‌پندارند'),
(5860, 83, 12, 'و تنها كسانى آن را انكار مى‌كنند كه متجاوز و گنهكارند'),
(5861, 83, 13, 'هنگامى كه آيات ما بر او خوانده شود، گويد: افسانه پيشينيان است'),
(5862, 83, 14, 'چنين نيست [كه مى‌پندارند،] بلكه اعمالشان زنگارى بر دل‌هاشان گرديده است'),
(5863, 83, 15, 'چنين نيست [كه مى‌پندارند،] البته آنها در آن روز از پروردگارشان سخت محجوبند'),
(5864, 83, 16, 'سپس آنها قطعا به آتش جهنم مى‌سوزند'),
(5865, 83, 17, 'آن‌گاه به آنها گفته مى‌شود: اين همان چيزى است كه آن را دروغ مى‌شمرديد'),
(5866, 83, 18, 'نه! چنين نيست [كه مى‌پندارند]، بى‌ترديد كارنامه نيكان در «عليين» است'),
(5867, 83, 19, 'و تو چه مى‌دانى «عليين» چيست'),
(5868, 83, 20, 'كارنامه‌اى نوشته و خوانا است [كه پاداش نيكان در آن است‌]'),
(5869, 83, 21, 'كه مقربان آن را مى‌بينند [و بدان گواهى مى‌دهند]'),
(5870, 83, 22, 'همانا نيكان در ناز و نعمتند'),
(5871, 83, 23, 'بر تخت‌ها تكيه كرده تماشا مى‌كنند'),
(5872, 83, 24, 'در چهره‌هايشان طراوت و خرمى نعمت را مى‌يابى'),
(5873, 83, 25, 'از مى صاف سر بسته‌اى سيراب شوند'),
(5874, 83, 26, 'كه مهر آن از مشك است، و مشتاقان بايد در آن رقابت كنند'),
(5875, 83, 27, 'و تركيب آن از چشمه «تسنيم» است'),
(5876, 83, 28, 'چشمه‌اى كه مقربان از آن مى‌نوشند'),
(5877, 83, 29, 'همانا بدكاران پيوسته به كسانى كه ايمان آورده بودند مى‌خنديدند'),
(5878, 83, 30, 'و چون بر آنان مى‌گذشتند به يكديگر چشمك مى‌زدند'),
(5879, 83, 31, 'و وقتى به سوى كسان خويش باز مى‌گشتند [از ريشخند خود] شاد بودند'),
(5880, 83, 32, 'و هر وقت آنها را مى‌ديدند مى‌گفتند: اينان بى‌ترديد گمراهند'),
(5881, 83, 33, 'در حالى كه آنها به نگهبانى مؤمنان فرستاده نشده بودند'),
(5882, 83, 34, 'ولى امروز آنها كه ايمان آورده‌اند، به كفار مى‌خندند'),
(5883, 83, 35, 'بر تخت‌ها تكيه كرده تماشا مى‌كنند'),
(5884, 83, 36, '[حال‌] آيا كفار پاداش كردار خود را گرفتند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آن‌گاه كه آسمان بشكافد'),
(5886, 84, 2, 'و فرمان پروردگار خويش برد و حق همين است'),
(5887, 84, 3, 'و آن‌گاه كه زمين كشيده شود'),
(5888, 84, 4, 'و آنچه را درون خود دارد بيرون افكند و تهى شود'),
(5889, 84, 5, 'و فرمان پروردگار خويش برد و حق همين باشد'),
(5890, 84, 6, 'اى انسان! مسلما تو با تلاش و رنجى فراوان به سوى پروردگارت مى‌روى، پس ديدارش خواهى كرد'),
(5891, 84, 7, 'پس آن كه كارنامه‌اش به دست راستش داده شود'),
(5892, 84, 8, 'پس زودا كه با حسابى آسان محاسبه شود'),
(5893, 84, 9, 'و خوشحال نزد كسان خويش بازگردد'),
(5894, 84, 10, 'و اما آن كه كارنامه‌اش از پشت سرش به او داده شود'),
(5895, 84, 11, 'پس زودا كه فريادش بلند شود كه واى بر من'),
(5896, 84, 12, 'و در آتش در آيد'),
(5897, 84, 13, 'كه وى [در دنيا] ميان كسان خويش [از گناه‌] شادمان بود'),
(5898, 84, 14, 'او مى‌پنداشت كه هرگز بازگشتى نخواهد داشت'),
(5899, 84, 15, 'چرا، به راستى پروردگارش به او بينا بود'),
(5900, 84, 16, 'پس سوگند به شفق'),
(5901, 84, 17, 'سوگند به شب و آنچه را در بر گيرد'),
(5902, 84, 18, 'سوگند به ماه آن‌گاه كه كامل شود'),
(5903, 84, 19, 'كه شما حالات و منازلى را پشت سر مى‌گذاريد [حيات و مرگ و قيامت و حساب‌]'),
(5904, 84, 20, 'پس آنها را چه شده است كه ايمان نمى‌آورند'),
(5905, 84, 21, 'و چون قرآن بر آنها خوانده شود سجده نمى‌كنند'),
(5906, 84, 22, 'نه، بلكه كافران [همواره‌] تكذيب مى‌كنند'),
(5907, 84, 23, 'و خدا بهتر داند كه در خاطر چه دارند'),
(5908, 84, 24, 'پس به عذاب دردناكى بشارتشان ده'),
(5909, 84, 25, 'مگر كسانى كه ايمان آوردند و اعمال شايسته كردند كه ايشان را پاداشى بى‌پايان است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'قسم به آسمان كه برج‌ها دارد'),
(5911, 85, 2, 'و قسم به روز موعود'),
(5912, 85, 3, 'و قسم به شاهد و مشهود [كه همه در ديدگاهند]'),
(5913, 85, 4, 'مرگ بر آدم سوزان خندق'),
(5914, 85, 5, '[خندق‌هاى‌] پر از آتش'),
(5915, 85, 6, 'آن وقت كه كنار آن نشسته بودند'),
(5916, 85, 7, 'و نظاره‌گر شكنجه‌اى بودند كه بر مؤمنان مى‌رفت'),
(5917, 85, 8, 'و از آنها انتقام نمى‌گرفتند جز اين كه آنان به خداى شكست ناپذير ستوده ايمان آورده بودند'),
(5918, 85, 9, 'خدايى كه حكومت آسمان‌ها و زمين خاص اوست، و خدا بر همه چيز شاهد است'),
(5919, 85, 10, 'همانا كسانى كه مردان و زنان مؤمن را آزردند، سپس از آن باز نگشتند، عذاب دوزخ و عذاب آتش سوزان براى آنهاست'),
(5920, 85, 11, 'همانا كسانى كه ايمان آوردند و اعمال شايسته انجام دادند، برايشان باغ‌هايى است كه نهرها از زير درختان آن جارى است. اين است آن كاميابى بزرگ'),
(5921, 85, 12, 'همانا صلابت قهرآميز پروردگارت شديد است'),
(5922, 85, 13, 'همانا اوست كه خلق را پديد مى‌آورد و همو باز مى‌گرداند'),
(5923, 85, 14, 'و اوست آمرزگار و دوستدار [مؤمنان‌]'),
(5924, 85, 15, 'و صاحب ارجمند عرش'),
(5925, 85, 16, 'آنچه را بخواهد كننده است'),
(5926, 85, 17, 'آيا خبر آن سپاهيان به تو رسيده است'),
(5927, 85, 18, 'سپاه فرعون و ثمود'),
(5928, 85, 19, 'بلى، كافران همواره در حال تكذيبند'),
(5929, 85, 20, 'و خدا از هر سو به آنها احاطه دارد'),
(5930, 85, 21, 'آرى، اين قرآن ارجمند است'),
(5931, 85, 22, 'كه در لوح محفوظ است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'قسم به آسمان و آن ستاره‌ى شبگرد'),
(5933, 86, 2, 'و تو چه مى‌دانى ستاره‌ى شبگرد چيست'),
(5934, 86, 3, 'آن ستاره‌ى درخشان'),
(5935, 86, 4, 'هيچ كس نيست مگر اين كه نگهبانى بر او هست'),
(5936, 86, 5, 'پس بايد آدمى بنگرد كه از چه آفريده شده'),
(5937, 86, 6, 'از آبى جهنده آفريده شده است'),
(5938, 86, 7, 'كه از ميان ستون پشت و استخوان‌هاى سينه بيرون مى‌آيد'),
(5939, 86, 8, 'بى‌ترديد، خداوند بر بازگرداندن آن تواناست'),
(5940, 86, 9, 'روزى كه رازها فاش گردد'),
(5941, 86, 10, 'پس او را نه نيرويى باشد و نه ياورى'),
(5942, 86, 11, 'سوگند به آسمان [پر باران‌]'),
(5943, 86, 12, 'و قسم به زمين شكاف پذير [براى رشد گياهان‌]'),
(5944, 86, 13, 'كه اين [قرآن‌] سخنى قاطع است'),
(5945, 86, 14, 'و آن بيهوده و شوخى نيست'),
(5946, 86, 15, 'به راستى آنها پيوسته حيله مى‌كنند'),
(5947, 86, 16, 'و من نيز طرحى تدبير مى‌كنم'),
(5948, 86, 17, 'پس كافران را مهلت ده و آنان را اندكى به خودشان واگذار');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار والايت را به پاكى بستاى'),
(5950, 87, 2, 'همان كه آفريد و سامان داد'),
(5951, 87, 3, 'و آن كه تقدير كرد و هدايت نمود'),
(5952, 87, 4, 'و آن كه چراگاه را پديد آورد'),
(5953, 87, 5, 'پس آن را خشك و تيره كرد'),
(5954, 87, 6, 'به زودى تو را [به قرآن‌] خوانا كنيم كه فراموش نكنى'),
(5955, 87, 7, 'مگر آنچه خدا خواهد، كه بى‌گمان او آشكار و پنهان را مى‌داند'),
(5956, 87, 8, 'و تو را براى آسان‌ترين راه آماده مى‌سازيم'),
(5957, 87, 9, 'پس تذكر ده، اگر تذكر سود دهد'),
(5958, 87, 10, 'به زودى كسى كه خدا ترس است پند خواهد گرفت'),
(5959, 87, 11, 'و بدبخت‌ترين مردم از آن [پند] كناره مى‌گيرد'),
(5960, 87, 12, 'همان كه به بزرگ‌ترين آتش مى‌سوزد'),
(5961, 87, 13, 'آن‌گاه در آن آتش نه مى‌ميرد و نه زنده مى‌ماند'),
(5962, 87, 14, 'حقا كه هر كه پاكى جست، رستگار شد'),
(5963, 87, 15, 'و نام پروردگارش را ذكر كرد و نماز گزارد'),
(5964, 87, 16, 'بلكه شما زندگى دنيا را ترجيح مى‌دهيد'),
(5965, 87, 17, 'حال آن كه آخرت بهتر و پايدارتر است'),
(5966, 87, 18, 'همانا اين [گفتار] در كتب [رسولان‌] پيشين آمده است'),
(5967, 87, 19, '[در] كتاب‌هاى ابراهيم و موسى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آيا خبر آن بلاى فراگير به تو رسيده است'),
(5969, 88, 2, 'چهره‌هايى در آن روز ذلت بارند'),
(5970, 88, 3, 'عمل [بيهوده‌] كرده و رنج [آخرت‌] برده‌اند'),
(5971, 88, 4, '[ناچار] به آتشى سوزان در آيند'),
(5972, 88, 5, 'از چشمه‌اى داغ نوشانده شوند'),
(5973, 88, 6, 'طعامى جز از خار تلخ ندارند'),
(5974, 88, 7, 'كه نه فربه كند و نه گرسنگى زدايد'),
(5975, 88, 8, 'چهره‌هايى در آن روز شاداب و متنعّمند'),
(5976, 88, 9, 'از سعى خود خشنودند'),
(5977, 88, 10, 'در بهشت برينند'),
(5978, 88, 11, 'در آن بيهوده‌اى نشنوند'),
(5979, 88, 12, 'در آن چشمه‌اى روان است'),
(5980, 88, 13, 'در آن جا تخت‌هايى است بلند'),
(5981, 88, 14, 'و جام‌هايى [در كنار چشمه‌] نهاده'),
(5982, 88, 15, 'و پشتى‌هايى به رديف چيده شده'),
(5983, 88, 16, 'و فرش‌هايى فاخر گسترده است'),
(5984, 88, 17, 'چرا شتر را نمى‌نگرند كه چطور آفريده شده'),
(5985, 88, 18, 'و به آسمان كه چگونه برافراشته شده'),
(5986, 88, 19, 'و به كوه‌ها كه چسان ميخ كوب شده‌اند'),
(5987, 88, 20, 'و به زمين كه چگونه گسترده شده'),
(5988, 88, 21, 'پس پند ده كه تو تنها پند رسانى'),
(5989, 88, 22, 'تو بر كفار گماشته و مراقب نيستى'),
(5990, 88, 23, 'مگر آنكس كه پشت كند و كافر شود'),
(5991, 88, 24, 'پس خدا او را به عذاب بزرگتر، عذاب مى‌كند'),
(5992, 88, 25, 'همانا بازگشتشان به سوى ماست'),
(5993, 88, 26, 'و آن‌گاه حسابشان با ماست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'به سپيده دم سوگند'),
(5995, 89, 2, 'و به شب‌هاى ده‌گانه'),
(5996, 89, 3, 'و به زوج و فرد'),
(5997, 89, 4, 'و به شب سوگند، آنگاه كه برود'),
(5998, 89, 5, 'آيا در آن براى خردمند سوگندى هست'),
(5999, 89, 6, 'آيا نديدى پروردگارت با قوم عاد چه كرد'),
(6000, 89, 7, 'با ارم كه داراى كاخ‌ها و ستون‌ها بود'),
(6001, 89, 8, 'كه نظيرش در بلاد ديگر ساخته نشده بود'),
(6002, 89, 9, 'و با قوم ثمود كه در آن وادى سنگ را مى‌بريدند'),
(6003, 89, 10, 'و با فرعون صاحب ميخ‌ها [ى شكنجه‌]'),
(6004, 89, 11, 'همان‌ها كه در شهرها طغيان كردند'),
(6005, 89, 12, 'و در آنها فساد را به اوج رساندند'),
(6006, 89, 13, 'پس پروردگارت تازيانه عذاب را بر سرشان كوفت'),
(6007, 89, 14, 'به تحقيق پروردگار تو البته در كمين است'),
(6008, 89, 15, 'اما آدمى را چون پروردگارش بيازمايد و او را گرامى داشته نعمتش دهد گويد: خدايم مرا گرامى داشت'),
(6009, 89, 16, 'و اما چون او را بيازمايد و روزيش را تنگ سازد پس گويد: پروردگارم مرا خوار كرد'),
(6010, 89, 17, 'چنين نيست [كه خيال مى‌كنيد] بلكه شما يتيمان را گرامى نمى‌داريد'),
(6011, 89, 18, 'و يكديگر را بر اطعام بينوايان ترغيب نمى‌كنيد'),
(6012, 89, 19, 'و ميراث [ضعيفان‌] را يكجا مى‌خوريد'),
(6013, 89, 20, 'و مال را بسيار دوست مى‌داريد [در حد دلباختگى‌]'),
(6014, 89, 21, 'چه خيال مى‌كنيد، وقتى كه زمين درهم شكست و به سختى متلاشى گرديد'),
(6015, 89, 22, 'و [فرمان‌] پروردگارت فرا رسيد و فرشتگان صف در صف حاضر شدند'),
(6016, 89, 23, 'و در آن روز جهنم را حاضر كنند، آن زمان است كه انسان بيدار شود، اما از اين بيدارى او را چه سود؟'),
(6017, 89, 24, 'گويد: اى كاش براى اين زندگى‌ام چيزى فرستاده بودم'),
(6018, 89, 25, 'پس در آن روز هيچ كس مانند عذاب كردن او عذاب نمى‌كند'),
(6019, 89, 26, 'و هيچ كس مانند بند كشيدن او به بند نمى‌كشد'),
(6020, 89, 27, 'تو اى جان آرام يافته، به اطمينان رسيده'),
(6021, 89, 28, 'به سوى پروردگارت بازگرد كه تو از او خشنود و او از تو خشنود است'),
(6022, 89, 29, 'پس به جمع بندگانم در آى'),
(6023, 89, 30, 'و در بهشت من داخل شو');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'سوگند به اين شهر [مكّه‌]'),
(6025, 90, 2, 'شهرى كه تو ساكن آن هستى'),
(6026, 90, 3, 'و قسم به پدر [ابراهيم‌]، و فرزندى كه آورد'),
(6027, 90, 4, 'كه ما انسان را در رنج آفريديم'),
(6028, 90, 5, 'آيا مى‌پندارد كه هيچ كس نمى‌تواند بر او دست يابد'),
(6029, 90, 6, 'مى‌گويد: مال انبوهى را نابود كردم'),
(6030, 90, 7, 'آيا مى‌پندارد كسى او را نديده'),
(6031, 90, 8, 'آيا براى او دو چشم نساختيم'),
(6032, 90, 9, 'و يك زبان و دو لب'),
(6033, 90, 10, 'و او را به دو راه مشخص ره نموديم'),
(6034, 90, 11, 'ولى او از آن گردنه بالا نرفت'),
(6035, 90, 12, 'و تو چه مى‌دانى آن گردنه چيست'),
(6036, 90, 13, 'آزاد كردن برده‌اى'),
(6037, 90, 14, 'يا اطعام به روز قحطى'),
(6038, 90, 15, 'به يتيمى خويشاوند'),
(6039, 90, 16, 'يا مستمندى خاك‌نشين'),
(6040, 90, 17, 'وانگهى از كسانى باشد كه ايمان آوردند و يكديگر را به صبر توصيه كرده و به ترحّم سفارش كردند'),
(6041, 90, 18, 'آنها اهل سعادتند'),
(6042, 90, 19, 'و آنها كه به آيات ما كافر شدند اهل شومى و شقاوتند'),
(6043, 90, 20, 'بر آنها آتشى سرپوشيده است [كه راه نفسى ندارد]');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشيد و روشنايى آن'),
(6045, 91, 2, 'و به ماه چون از پى آن برآيد'),
(6046, 91, 3, 'و قسم به روز چون آن را هويدا سازد'),
(6047, 91, 4, 'و به شب وقتى كه آن را بپوشد [و آفاق را تاريك سازد]'),
(6048, 91, 5, 'و به آسمان و آنكه بنايش كرد'),
(6049, 91, 6, 'و به زمين و آن كه آن را گستراند'),
(6050, 91, 7, 'و سوگند به نفس آدمى و آنكه او را سامان داد'),
(6051, 91, 8, 'پس [تشخيص‌] فجور و تقوى را به وى الهام كرد'),
(6052, 91, 9, 'بى‌شك هر كه خود را تزكيه كرد رستگار شد'),
(6053, 91, 10, 'و بى‌گمان آنكه خود را بيالود، محروم گشت'),
(6054, 91, 11, 'ثمود از روى سركشى [پيامبرشان‌] را تكذيب كردند'),
(6055, 91, 12, 'آنگاه كه شقى‌ترين آنها به پاخاست'),
(6056, 91, 13, 'پس پيامبر خدا به آنها گفت ناقه خدا را با آب خوردنش [آزاد] گذاريد'),
(6057, 91, 14, 'ولى او را تكذيب نمودند و ناقه را پى كردند، پس خدا آنها را به گناهانشان بگرفت و با خاك يكسانشان كرد'),
(6058, 91, 15, 'و از عاقبت آن باكى نمى‌داشت');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'قسم به شب وقتى كه فراپوشد'),
(6060, 92, 2, 'و به روز آنگاه كه تجلى كند'),
(6061, 92, 3, 'و به آنكه نر و ماده را آفريد'),
(6062, 92, 4, 'كه سعى و تلاش شما بسى پراكنده است [و هر كس به راهى مى‌رود]'),
(6063, 92, 5, 'اما آنكه انفاق كرد و پارسائى نمود'),
(6064, 92, 6, 'و [وعده‌ى‌] نيكو را باور داشت'),
(6065, 92, 7, 'پس زودا كه راه آسانى پيش پاى او گذاريم'),
(6066, 92, 8, 'و اما آن كه بخل ورزيد و [از دين‌] بى‌نيازى نمود'),
(6067, 92, 9, 'و آن [وعده‌ى‌] نيكو را انكار كرد'),
(6068, 92, 10, 'پس زودا كه طريقه سختى پيش وى آريم'),
(6069, 92, 11, 'و آن‌گاه كه به هلاكت افتد، مالش به حال او سودى ندهد'),
(6070, 92, 12, 'همانا هدايت بر عهده‌ى ماست'),
(6071, 92, 13, 'و آخرت و دنيا هم متعلق به ماست'),
(6072, 92, 14, 'پس شما را به آتشى كه زبانه كشد بيم دادم'),
(6073, 92, 15, 'كه جز تيره بخت‌ترين مردم، بدان نسوزد'),
(6074, 92, 16, 'همان كه تكذيب كرد و رخ برتافت'),
(6075, 92, 17, 'زودا كه پرهيزكارترين مردم، از آن دور داشته شود'),
(6076, 92, 18, 'همان كه مال خود را مى‌دهد تا پاك شود'),
(6077, 92, 19, 'و هيچ كس را نزد او حق نعمتى نيست تا او را پاداش دهد'),
(6078, 92, 20, 'جز طلب رضاى پروردگار والايش'),
(6079, 92, 21, 'و به زودى خشنود گردد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'قسم به روز روشن'),
(6081, 93, 2, 'و به شب، آن دم كه آرام گيرد'),
(6082, 93, 3, 'كه پروردگارت نه تو را واگذاشته و نه دشمن داشته'),
(6083, 93, 4, 'و مسلما آخرت براى تو از دنيا بهتر است'),
(6084, 93, 5, 'و زود باشد كه پروردگارت [آنقدر] عطايت كند كه راضى شوى'),
(6085, 93, 6, 'مگر نه يتيمت يافت و پناه داد'),
(6086, 93, 7, 'و سرگشته‌ات يافت پس راه نمود'),
(6087, 93, 8, 'و محتاجت يافت و بى‌نياز كرد'),
(6088, 93, 9, 'پس يتيم را خوار مدار و مران'),
(6089, 93, 10, 'و با سائل خشونت مكن و مران'),
(6090, 93, 11, 'و از موهبت پروردگارت [رسالت و معارف‌] سخن گوى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آيا ما سينه تو را فراخ نكرديم'),
(6092, 94, 2, 'و بار گرانت را از تو فرو نهاديم'),
(6093, 94, 3, 'كه پشتت را شكسته بود'),
(6094, 94, 4, 'و آوازه‌ات را بلند كرديم'),
(6095, 94, 5, 'پس بى‌ترديد با [هر] دشوارى آسانى است'),
(6096, 94, 6, '[آرى‌] مسلما با [هر] دشوارى آسانى است'),
(6097, 94, 7, 'پس چون فراغت يافتى به [عبادت‌] كوش'),
(6098, 94, 8, 'و با اشتياق به سوى پروردگارت روى آور');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به انجير و زيتون'),
(6100, 95, 2, 'و به طور سينا'),
(6101, 95, 3, 'و به اين شهر امن'),
(6102, 95, 4, 'كه به راستى ما انسان را به نيكوترين ساختار آفريديم'),
(6103, 95, 5, 'سپس او را [به سبب كفرش‌] به پست‌ترين دركات بازگردانديم'),
(6104, 95, 6, 'مگر آنها را كه ايمان آورده اعمال صالح كردند كه برايشان پاداشى بى‌پايان است'),
(6105, 95, 7, 'پس چيست كه تو را به تكذيب روز جزا وا مى‌دارد'),
(6106, 95, 8, 'آيا خدا بهتر از همه حكم كنندگان نيست');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت كه آفريد'),
(6108, 96, 2, 'انسان را از خون بسته‌اى آفريد'),
(6109, 96, 3, 'بخوان كه پروردگارت از همه كريم‌تر است'),
(6110, 96, 4, 'همان كه به وسيله قلم آموخت'),
(6111, 96, 5, 'به انسان آنچه را نمى‌دانست ياد داد'),
(6112, 96, 6, 'چنين نيست [كه مى‌پندارند]، بى‌شك آدمى طغيان مى‌كند'),
(6113, 96, 7, 'همين كه خود را بى‌نياز بيند'),
(6114, 96, 8, 'مسلما بازگشت، به سوى پروردگار تو است'),
(6115, 96, 9, 'مگر آن را نبينى كه باز مى‌دارد'),
(6116, 96, 10, 'وقتى بنده‌اى نماز كند'),
(6117, 96, 11, 'چه پندارى اگر اين بنده بر هدايت باشد'),
(6118, 96, 12, 'يا به پارسايى فرمان دهد [آزارش چه زشت است؟]'),
(6119, 96, 13, 'مگر ندانى كه اگر تكذيب كند و روى بگرداند [سزا خواهد ديد]'),
(6120, 96, 14, 'آيا ندانسته است كه يقينا خدا مى‌بيند'),
(6121, 96, 15, 'حاشا كه اگر دست بر ندارد موى پيشانى او را گرفته [به دوزخ‌] مى‌كشانيمش'),
(6122, 96, 16, 'موى پيشانى دروغزن خطاكار را'),
(6123, 96, 17, 'پس او گروهش را بخواند'),
(6124, 96, 18, 'ما هم آتشبانان را خواهيم خواند'),
(6125, 96, 19, 'زنهار اطاعت او مكن و سجده كن و تقرب جوى');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'همانا ما آن [قرآن‌] را در شب قدر نازل كرديم'),
(6127, 97, 2, 'و تو چه مى‌دانى شب قدر چيست'),
(6128, 97, 3, 'شب قدر از هزار ماه بهتر است'),
(6129, 97, 4, 'در آن شب فرشتگان و روح به اذن پروردگارشان از براى هر كارى فرو مى‌آيند'),
(6130, 97, 5, 'شبى سراسر سلامت است تا صبحدم');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'كافران اهل كتاب و مشركان [از آيين خود] دست بردار نبودند تا اين كه دليل روشنى برايشان بيايد'),
(6132, 98, 2, 'پيامبرى از جانب خدا كه صحيفه‌هاى پاكى را بر آنها بخواند'),
(6133, 98, 3, 'كه در آن، نوشته‌هاى صحيح و استوارى است [اما ايمان نياوردند]'),
(6134, 98, 4, 'ولى اهل كتاب پراكنده نشدند مگر پس از آن كه اين دليل روشن برايشان آمد [كه پيامبر و قرآن است‌]'),
(6135, 98, 5, 'و آنها دستور نداشتند جز اين كه خداى يگانه را بپرستند و دين را ويژه‌ى او كنند و موحّد باشند و نماز را بر پا دارند و زكات دهند و اين است دين پايدار'),
(6136, 98, 6, 'بى‌ترديد كسانى از اهل كتاب كه كافر شدند و مشركان، براى هميشه در آتش دوزخند و آنها بدترين آفريدگانند'),
(6137, 98, 7, 'البته كسانى كه ايمان آورده و اعمال شايسته كردند، آنهايند كه بهترين آفريدگانند'),
(6138, 98, 8, 'پاداش آنها نزد پروردگارشان بهشت‌هاى پاينده‌اى است كه از پاى درختانش نهرها جارى است و تا ابد در آن بمانند، هم خدا از آنها خشنود است و هم آنها از خدا خشنود، اين [مقام والا] براى كسى است كه از پروردگار خود پروا كند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'هنگامى كه زمين با زلزله مخصوص خود لرزانده شود'),
(6140, 99, 2, 'و زمين بارهايش را بيرون افكند'),
(6141, 99, 3, 'و آدمى گويد، آن را چه شده است'),
(6142, 99, 4, 'در آن روز زمين خبرهايش را باز گويد'),
(6143, 99, 5, 'مسلما از آن روست كه پروردگارت به آن الهام كرده است'),
(6144, 99, 6, 'آن روز مردم دسته دسته بازگردند تا كارهايشان به آنها نشان داده شود'),
(6145, 99, 7, 'پس هر كه به اندازه‌ى ذره‌اى نيكى كند آن را ببيند'),
(6146, 99, 8, 'و هر كه به اندازه‌ى ذره‌اى بدى كند آن را ببيند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'قسم به اسبان تيزپا [ى جهاد] كه نفس نفس زنند'),
(6148, 100, 2, 'و به آنها كه با سم‌هاى خود جرقه‌ها افروزند'),
(6149, 100, 3, 'و قسم به هجوم آوران در سپيده دم'),
(6150, 100, 4, 'كه بدان گردى برانگيزند'),
(6151, 100, 5, 'و [ناگهان‌] با آن بر قلب دشمن زنند'),
(6152, 100, 6, 'آرى انسان نسبت به پروردگارش بسى ناسپاس است'),
(6153, 100, 7, 'و او خود بر اين معنا گواه است'),
(6154, 100, 8, 'و به راستى او علاقه شديدى به مال دارد'),
(6155, 100, 9, 'مگر نمى‌داند وقتى كه خفتگان قبور برخيزند'),
(6156, 100, 10, 'و آنچه [از كفر و ايمان‌] در سينه‌هاست فاش گردد'),
(6157, 100, 11, 'در آن روز پروردگارشان به آنها كاملا آگاه است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'آن واقعه كوبنده'),
(6159, 101, 2, 'چه كوبنده‌اى! [كه قابل وصف نيست‌'),
(6160, 101, 3, 'و تو چه مى‌دانى آن كوبنده چيست'),
(6161, 101, 4, 'روزى كه مردم مانند پروانگان پراكنده باشند'),
(6162, 101, 5, 'و كوه‌ها چون پشم رنگين حلاجى شده شوند'),
(6163, 101, 6, 'پس اما هر كه وزنه اعمالش سنگين باشد'),
(6164, 101, 7, 'پس وى در يك زندگى رضايت بخشى است'),
(6165, 101, 8, 'و اما آنكه وزنه اعمالش سبك شود'),
(6166, 101, 9, 'پس جايگاهش هاويه است'),
(6167, 101, 10, 'و تو چه مى‌دانى آن چيست'),
(6168, 101, 11, 'آتشى سوزنده است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تفاخر و افزون طلبى، شما را سرگرم ساخت'),
(6170, 102, 2, 'تا وقتى كه به ديدار گورها رفتيد'),
(6171, 102, 3, 'چنين نيست [كه مى‌پنداريد]، به زودى خواهيد دانست [نتيجه غفلت را'),
(6172, 102, 4, 'باز چنين نيست، [كه مى‌پنداريد] به زودى خواهيد دانست'),
(6173, 102, 5, 'چنين نيست [كه مى‌پنداريد] اگر شما به علم يقينى مى‌دانستيد [كه آخرتى هست‌]'),
(6174, 102, 6, 'به يقين جهنم را مى‌ديديد'),
(6175, 102, 7, 'سپس يقينا آن دوزخ را به ديده‌ى يقين [و لمس‌] مشاهده مى‌كرديد'),
(6176, 102, 8, 'و آنگاه آن روز از اين نعمت بازخواست مى‌شويد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'قسم به عصر'),
(6178, 103, 2, 'كه انسان‌ها همه در زيانند'),
(6179, 103, 3, 'مگر آنها كه ايمان آورده و اعمال شايسته كردند و يكديگر را به حق سفارش نمودند و به صبر توصيه كردند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'واى بر هر بدگوى كننده‌ى عيبجوى'),
(6181, 104, 2, 'آنكه مالى فراهم كرد و بر شمرد'),
(6182, 104, 3, 'مى‌پندارد كه مالش او را جاويد كند'),
(6183, 104, 4, 'چنين نيست [كه مى‌پندارد]، حتما در آتشى شكننده افكنده شود'),
(6184, 104, 5, 'و تو چه مى‌دانى آتش شكننده چيست'),
(6185, 104, 6, 'آتش برافروخته الهى است'),
(6186, 104, 7, 'آتشى كه بر دل‌ها افتد و زبانه كشد'),
(6187, 104, 8, 'اين آتش بر آنها سرپوش است [و راه گريز ندارد]'),
(6188, 104, 9, 'در ستون‌هايى است كشيده شده');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آيا نديدى پروردگارت با پيلداران چه كرد'),
(6190, 105, 2, 'آيا نقشه آنها را باطل نساخت'),
(6191, 105, 3, 'و بر سرشان پرندگان گروهى را فرستاد'),
(6192, 105, 4, 'كه با سنگ‌هايى از سجّيل آنها را هدف قرار مى‌دادند'),
(6193, 105, 5, 'پس آنها را همچون كاه جويده شده [متلاشى‌] گردانيد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, '[شكست ابرهه‌] براى اين بود كه قريش [به كعبه‌] الفت گيرند'),
(6195, 106, 2, 'الفتشان به سفرهاى [تجارى‌] زمستانى و تابستانى [ادامه يابد]'),
(6196, 106, 3, 'پس بايد صاحب اين خانه را پرستش كنند'),
(6197, 106, 4, 'همان كه آنها را از گرسنگى سيرشان كرد و از بيم [دشمن‌] ايمنشان ساخت');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آيا آن كس را كه دين را تكذيب مى‌كند ديدى'),
(6199, 107, 2, 'پس او همانست كه يتيم را به خشونت مى‌راند'),
(6200, 107, 3, 'و بر اطعام بينوا، ترغيب نمى‌كند'),
(6201, 107, 4, 'پس واى بر نمازگزاران'),
(6202, 107, 5, 'آنان كه از نمازشان غافلند'),
(6203, 107, 6, 'آنها كه خودنمايى مى‌كنند'),
(6204, 107, 7, 'و از رفع نياز ديگران دريغ مى‌ورزند');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'بى‌ترديد ما به تو خير بسيار داديم'),
(6206, 108, 2, 'پس براى پروردگارت نمازگزار و قربانى كن'),
(6207, 108, 3, 'البته دشمن تو، همو بى‌نسل است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو هان! اى كافران'),
(6209, 109, 2, 'من آنچه را كه مى‌پرستيد، نمى‌پرستم'),
(6210, 109, 3, 'و شما [نيز] پرستش‌گر آنچه مى‌پرستم نيستيد'),
(6211, 109, 4, 'و من [نيز] پرستشگر آنچه [شما] پرستيده‌ايد نيستم'),
(6212, 109, 5, 'و نه شما پرستنده‌ى چيزى هستيد كه [من‌] مى‌پرستم'),
(6213, 109, 6, 'شما را دين خودتان و مرا دين خويش');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'آن‌گاه كه يارى خدا و پيروزى فرا رسيد'),
(6215, 110, 2, 'و ديدى كه مردم گروه گروه در دين خدا داخل مى‌شوند'),
(6216, 110, 3, 'پس به ستايش پروردگارت تسبيح گوى و آمرزش خواه كه او قطعا توبه پذير است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'بريده باد هر دو دست ابو لهب و مرگ بر او'),
(6218, 111, 2, 'مال و دست آوردش او را بى‌نياز نكرد'),
(6219, 111, 3, 'زودا كه وارد آتشى شعله‌ور شود'),
(6220, 111, 4, 'و همسر او [نيز]، كه باركش هيزم است'),
(6221, 111, 5, 'به گردنش ريسمانى از ليف خرما است');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو حق اين است كه خدا يگانه است'),
(6223, 112, 2, 'خدايى كه همه، نياز بدو برند'),
(6224, 112, 3, 'نزاده و زاده نشده است'),
(6225, 112, 4, 'و او را هيچ همتايى نباشد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو به پروردگار سپيده دم پناه مى‌برم'),
(6227, 113, 2, 'از شرّ آنچه آفريد'),
(6228, 113, 3, 'و از شرّ شب تار، وقتى كه هجوم آورد'),
(6229, 113, 4, 'و از شرّ دمندگان در گروه‌ها'),
(6230, 113, 5, 'و از شرّ حسود آن‌گاه كه حسد ورزد');
INSERT INTO `fa_bahrampour` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو پناه مى‌برم به پروردگار مردم'),
(6232, 114, 2, 'به فرمانرواى مردم'),
(6233, 114, 3, 'به معبود مردم'),
(6234, 114, 4, 'از شرّ وسوسه‌گر پنهان شونده [و بازگردنده‌]'),
(6235, 114, 5, 'آن كه در سينه‌هاى مردم وسوسه مى‌كند'),
(6236, 114, 6, 'از پريان و آدميان');
