INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداوند رحمتگر مهربان'),
(2, 1, 2, 'ستایش خدایی را که پروردگار جهانیان،'),
(3, 1, 3, 'رحمتگر مهربان،'),
(4, 1, 4, '[و] خداوند روز جزاست.'),
(5, 1, 5, '[بار الها] تنها تو را می‌پرستیم، و تنها از تو یاری می‌جوییم.'),
(6, 1, 6, 'ما را به راه راست هدایت فرما،'),
(7, 1, 7, 'راه آنان که گرامی‌شان داشته‌ای، نه [راه‌] مغضوبان، و نه [راه‌] گمراهان.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف، لام، میم.'),
(9, 2, 2, 'این است کتابی که در [حقانیت‌] آن هیچ تردیدی نیست؛ [و] مایه هدایت تقواپیشگان است:'),
(10, 2, 3, 'آنان که به غیب ایمان می‌آورند، و نماز را بر پا می‌دارند، و از آنچه به ایشان روزی داده‌ایم انفاق می‌کنند؛'),
(11, 2, 4, 'و آنان که بدانچه به سوی تو فرود آمده، و به آنچه پیش از تو نازل شده است، ایمان می آورند؛ و آنانند که به آخرت یقین دارند.'),
(12, 2, 5, 'آنان برخوردار از هدایتی از سوی پروردگار خویشند؛ و آنان همان رستگارانند.'),
(13, 2, 6, 'در حقیقت کسانی که کفر ورزیدند -چه بیمشان دهی، چه بیمشان ندهی- بر ایشان یکسان است؛ [آنها] نخواهند گروید.'),
(14, 2, 7, 'خداوند بر دلهای آنان، و بر شنوایی ایشان مُهر نهاده؛ و بر دیدگانشان پرده‌ای است؛ و آنان را عذابی دردناک است.'),
(15, 2, 8, 'و برخی از مردم می‌گویند: «ما به خدا و روز بازپسین ایمان آورده‌ایم»، ولی گروندگان [راستین‌] نیستند.'),
(16, 2, 9, 'با خدا و مؤمنان نیرنگ می‌بازند؛ ولی جز بر خویشتن نیرنگ نمی‌زنند، و نمی‌فهمند.'),
(17, 2, 10, 'در دلهایشان مرضی است؛ و خدا بر مرضشان افزود؛ و به [سزای‌] آنچه به دروغ می گفتند، عذابی دردناک [در پیش‌] خواهند داشت.'),
(18, 2, 11, 'و چون به آنان گفته شود: «در زمین فساد مکنید»، می‌گویند: «ما خود اصلاحگریم.»'),
(19, 2, 12, 'بهوش باشید که آنان فسادگرانند، لیکن نمی‌فهمند.'),
(20, 2, 13, 'و چون به آنان گفته شود: «همان گونه که مردم ایمان آوردند، شما هم ایمان بیاورید»، می‌گویند: «آیا همان گونه که کم خردان ایمان آورده‌اند، ایمان بیاوریم؟» هشدار که آنان همان کم‌خردانند؛ ولی نمی‌دانند.'),
(21, 2, 14, 'و چون با کسانی که ایمان آورده‌اند برخورد کنند، می‌گویند: «ایمان آوردیم »، و چون با شیطانهای خود خلوت کنند، می‌گویند: «در حقیقت ما با شماییم، ما فقط [آنان را] ریشخند می‌کنیم.»'),
(22, 2, 15, 'خدا [است که‌] ریشخندشان می‌کند، و آنان را در طغیانشان فرو می‌گذارد تا سرگردان شوند.'),
(23, 2, 16, 'همین کسانند که گمراهی را به [بهای‌] هدایت خریدند، در نتیجه داد و ستدشان سود[ی به بار] نیاورد؛ و هدایت‌یافته نبودند.'),
(24, 2, 17, 'مَثَل آنان، همچون مَثَل کسانی است که آتشی افروختند، و چون پیرامون آنان را روشنایی داد، خدا نورشان را برد؛ و در میان تاریکیهایی که نمی‌بینند رهایشان کرد.'),
(25, 2, 18, 'کرند، لالند، کورند؛ بنابراین به راه نمی‌آیند.'),
(26, 2, 19, 'یا چون [کسانی که در معرض‌] رگباری از آسمان -که در آن تاریکیها و رعد و برقی است- [قرار گرفته‌اند]؛ از [نهیب‌] آذرخش [و] بیم مرگ، سر انگشتان خود را در گوشهایشان نهند، ولی خدا بر کافران احاطه دارد.'),
(27, 2, 20, 'نزدیک است که برق چشمانشان را برباید؛ هر گاه که بر آنان روشنی بخشد، در آن گام زنند؛ و چون راهشان را تاریک کند، [بر جای خود] بایستند؛ و اگر خدا می‌خواست شنوایی و بینایی‌شان را برمی‌گرفت، که خدا بر همه چیز تواناست.'),
(28, 2, 21, 'ای مردم، پروردگارتان را که شما، و کسانی را که پیش از شما بوده‌اند آفریده است، پرستش کنید؛ باشد که به تقوا گرایید.'),
(29, 2, 22, 'همان [خدایی‌] که زمین را برای شما فرشی [گسترده‌]، و آسمان را بنایی [افراشته‌] قرار داد؛ و از آسمان آبی فرود آورد؛ و بدان از میوه‌ها رزقی برای شما بیرون آورد؛ پس برای خدا همتایانی قرار ندهید، در حالی که خود می‌دانید.'),
(30, 2, 23, 'و اگر در آنچه بر بنده خود نازل کرده‌ایم شک دارید، پس -اگر راست می‌گویید- سوره‌ای مانند آن بیاورید؛ و گواهان خود را -غیر خدا- فرا خوانید.'),
(31, 2, 24, 'پس اگر نکردید -و هرگز نمی‌توانید کرد- از آن آتشی که سوختش مردمان و سنگها هستند، و برای کافران آماده شده، بپرهیزید.'),
(32, 2, 25, 'و کسانی را که ایمان آورده‌اند و کارهای شایسته انجام داده‌اند، مژده ده که ایشان را باغهایی خواهد بود که از زیر [درختان‌] آنها جویها روان است. هر گاه میوه‌ای از آن روزیِ ایشان شود، می‌گویند: «این همان است که پیش از این [نیز ]روزیِ ما بوده.» و مانند آن [نعمتها] به ایشان داده شود؛ و در آنجا همسرانی پاکیزه خواهند داشت؛ و در آنجا جاودانه بمانند.'),
(33, 2, 26, 'خدای را از اینکه به پشه‌ای -یا فروتر [یا فراتر] از آن- مَثَل زند، شرم نیاید. پس کسانی که ایمان آورده‌اند می‌دانند که آن [مَثَل‌] از جانب پروردگارشان بجاست؛ ولی کسانی که به کفر گراییده‌اند می‌گویند: «خدا از این مَثَل چه قصد داشته است؟» [خدا] بسیاری را با آن گمراه، و بسیاری را با آن راهنمایی می‌کند؛ و[لی‌] جز نافرمانان را با آن گمراه نمی‌کند.'),
(34, 2, 27, 'همانانی که پیمان خدا را پس از بستن آن می‌شکنند؛ و آنچه را خداوند به پیوستنش امر فرموده می‌گسلند؛ و در زمین به فساد می‌پردازند؛ آنانند که زیانکارانند.'),
(35, 2, 28, 'چگونه خدا را منکرید؟ با آنکه مردگانی بودید و شما را زنده کرد؛ باز شما را می میراند [و] باز زنده می‌کند؛ [و] آنگاه به سوی او بازگردانده می‌شوید.'),
(36, 2, 29, 'اوست آن کسی که آنچه در زمین است، همه را برای شما آفرید؛ سپس به [آفرینش‌] آسمان پرداخت، و هفت آسمان را استوار کرد؛ و او به هر چیزی داناست.'),
(37, 2, 30, 'و چون پروردگار تو به فرشتگان گفت: «من در زمین جانشینی خواهم گماشت»، [فرشتگان‌] گفتند: «آیا در آن کسی را می‌گماری که در آن فساد انگیزد، و خونها بریزد؟ و حال آنکه ما با ستایش تو، [تو را] تنزیه می‌کنیم؛ و به تقدیست می‌پردازیم.» فرمود: «من چیزی می‌دانم که شما نمی‌دانید.»'),
(38, 2, 31, 'و [خدا] همه [معانی‌] نامها را به آدم آموخت؛ سپس آنها را بر فرشتگان عرضه نمود و فرمود: «اگر راست می‌گویید، از اسامی اینها به من خبر دهید.»'),
(39, 2, 32, 'گفتند: «منزهی تو! ما را جز آنچه [خود] به ما آموخته‌ای، هیچ دانشی نیست؛ تویی دانای حکیم.»'),
(40, 2, 33, 'فرمود: «ای آدم، ایشان را از اسامی آنان خبر ده.» و چون [آدم‌] ایشان را از اسماءشان خبر داد، فرمود: «آیا به شما نگفتم که من نهفته آسمانها و زمین را می‌دانم؛ و آنچه را آشکار می‌کنید، و آنچه را پنهان می‌داشتید می‌دانم؟»'),
(41, 2, 34, 'و چون فرشتگان را فرمودیم: «برای آدم سجده کنید»، پس بجز ابلیس -که سر باز زد و کبر ورزید و از کافران شد- [همه‌] به سجده درافتادند.'),
(42, 2, 35, 'و گفتیم: «ای آدم، خود و همسرت در این باغ سکونت گیر[ید]؛ و از هر کجای آن خواهید فراوان بخورید؛ و[لی‌] به این درخت نزدیک نشوید، که از ستمکاران خواهید بود.»'),
(43, 2, 36, 'پس شیطان هر دو را از آن بلغزانید؛ و از آنچه در آن بودند ایشان را به درآورد؛ و فرمودیم: «فرود آیید، شما دشمن همدیگرید؛ و برای شما در زمین قرارگاه، و تا چندی برخورداری خواهد بود.»'),
(44, 2, 37, 'سپس آدم از پروردگارش کلماتی را دریافت نمود؛ و [خدا] بر او ببخشود؛ آری، او[ست که‌] توبه‌پذیرِ مهربان است.'),
(45, 2, 38, 'فرمودیم: «جملگی از آن فرود آیید. پس اگر از جانب من شما را هدایتی رسد، آنان که هدایتم را پیروی کنند بر ایشان بیمی نیست و غمگین نخواهند شد.'),
(46, 2, 39, 'و[لی‌] کسانی که کفر ورزیدند و نشانه‌های ما را دروغ انگاشتند، آنانند که اهل آتشند؛ و در آن ماندگار خواهند بود.»'),
(47, 2, 40, 'ای فرزندان اسرائیل، نعمتهایم را که بر شما ارزانی داشتم به یاد آرید، و به پیمانم وفا کنید، تا به پیمانتان وفا کنم، و تنها از من بترسید.'),
(48, 2, 41, 'و بدانچه نازل کرده‌ام -که مؤید همان چیزی است که با شماست- ایمان آرید؛ و نخستین منکر آن نباشید، و آیات مرا به بهایی ناچیز نفروشید، و تنها از من پروا کنید.'),
(49, 2, 42, 'و حق را به باطل درنیامیزید، و حقیقت را -با آنکه خود می‌دانید- کتمان نکنید.'),
(50, 2, 43, 'و نماز را بر پا دارید، و زکات را بدهید، و با رکوع‌کنندگان رکوع کنید.'),
(51, 2, 44, 'آیا مردم را به نیکی فرمان می‌دهید و خود را فراموش می‌کنید، با اینکه شما کتاب [خدا] را می‌خوانید؟ آیا [هیچ‌] نمی‌اندیشید؟'),
(52, 2, 45, 'از شکیبایی و نماز یاری جویید. و به راستی این [کار] گران است، مگر بر فروتنان:'),
(53, 2, 46, 'همان کسانی که می‌دانند با پروردگار خود دیدار خواهند کرد؛ و به سوی او باز خواهند گشت.'),
(54, 2, 47, 'ای فرزندان اسرائیل، از نعمتهایم که بر شما ارزانی داشتم، و [از] اینکه من شما را بر جهانیان برتری دادم، یاد کنید.'),
(55, 2, 48, 'و بترسید از روزی که هیچ کس چیزی [از عذاب خدا] را از کسی دفع نمی‌کند؛ و نه از او شفاعتی پذیرفته، و نه به جای وی بدلی گرفته می‌شود؛ و نه یاری خواهند شد.'),
(56, 2, 49, 'و [به یاد آرید] آنگاه که شما را از [چنگ‌] فرعونیان رهانیدیم؛ [آنان‌] شما را سخت شکنجه می‌کردند؛ پسران شما را سر می‌بریدند؛ و زنهایتان را زنده می‌گذاشتند، و در آن [امر، بلا و] آزمایش بزرگی از جانب پروردگارتان بود.'),
(57, 2, 50, 'و هنگامی که دریا را برای شما شکافتیم و شما را نجات بخشیدیم؛ و فرعونیان را -در حالی که شما نظاره می‌کردید- غرق کردیم.'),
(58, 2, 51, 'و آنگاه که با موسی چهل شب قرار گذاشتیم؛ آنگاه در غیاب وی، شما گوساله را [به پرستش‌] گرفتید، در حالی که ستمکار بودید.'),
(59, 2, 52, 'پس از آن بر شما بخشودیم، باشد که شکرگزاری کنید.'),
(60, 2, 53, 'و آنگاه که موسی را کتاب و فرقان [=جداکننده حق از باطل‌] دادیم، شاید هدایت یابید.'),
(61, 2, 54, 'و چون موسی به قوم خود گفت: «ای قوم من، شما با [به پرستش‌] گرفتن گوساله، برخود ستم کردید، پس به درگاه آفریننده خود توبه کنید، و [خطاکاران‌] خودتان را به قتل برسانید، که این [کار] نزد آفریدگارتان برای شما بهتر است.» پس [خدا] توبه شما را پذیرفت، که او توبه‌پذیر مهربان است.'),
(62, 2, 55, 'و چون گفتید: «ای موسی، تا خدا را آشکارا نبینیم، هرگز به تو ایمان نخواهیم آورد.» پس -در حالی که می‌نگریستید- صاعقه شما را فرو گرفت.'),
(63, 2, 56, 'سپس شما را پس از مرگتان برانگیختیم؛ باشد که شکرگزاری کنید.'),
(64, 2, 57, 'و بر شما ابر را سایه‌گستر کردیم؛ و بر شما «گزانگبین» و «بلدرچین» فرو فرستادیم؛ [و گفتیم:] «از خوراکیهای پاکیزه‌ای که به شما روزی داده‌ایم، بخورید.» و[لی آنان‌] بر ما ستم نکردند، بلکه بر خویشتن ستم روا می‌داشتند.'),
(65, 2, 58, 'و [نیز به یادآرید] هنگامی را که گفتیم: «بدین شهر درآیید، و از [نعمتهای‌] آن، هر گونه خواستید، فراوان بخورید، و سجده‌کنان از در [بزرگ‌] درآیید؛ و بگویید: [خداوندا،] گناهان ما را بریز. تا خطاهای شما را ببخشاییم، و [پاداش‌] نیکوکاران را خواهیم افزود.»'),
(66, 2, 59, 'اما کسانی که ستم کرده بودند، [آن سخن را] به سخن دیگری -غیر از آنچه به ایشان گفته شده بود- تبدیل کردند. و ما [نیز] بر آنان که ستم کردند، به سزای اینکه نافرمانی پیشه کرده بودند، عذابی از آسمان فرو فرستادیم.'),
(67, 2, 60, 'و هنگامی که موسی برای قوم خود در پی آب برآمد، گفتیم: «با عصایت بر آن تخته‌سنگ بزن.» پس دوازده چشمه از آن جوشیدن گرفت، [به گونه‌ای که‌] هر قبیله‌ای آبشخور خود را می‌دانست. [و گفتیم:] «از روزی خدا بخورید و بیاشامید، و[لی ]در زمین سر به فساد برمدارید.»'),
(68, 2, 61, 'و چون گفتید: «ای موسی! هرگز بر یک [نوع‌] خوراک تاب نیاوریم، از خدای خود برای ما بخواه تا از آنچه زمین می‌رویاند، از [قبیل‌] سبزی و خیار و سیر و عدس و پیاز، برای ما برویاند» [موسی‌] گفت: «آیا به جای چیز بهتر، خواهان چیز پست‌ترید؟ پس به شهر فرود آیید، که آنچه را خواسته‌اید برای شما [در آنجا مهیا]ست.» و [داغ‌] خواری و ناداری بر [پیشانی‌] آنان زده شد، و به خشم خدا گرفتار آمدند؛ چرا که آنان به نشانه‌های خدا کفر ورزیده بودند، و پیامبران را بناحق می‌کشتند؛ این، از آن روی بود که سرکشی نموده، و از حد درگذرانیده بودند.'),
(69, 2, 62, 'در حقیقت، کسانی که [به اسلام‌] ایمان آورده، و کسانی که یهودی شده‌اند، و ترسایان و صابئان، هر کس به خدا و روز بازپسین ایمان داشت و کار شایسته کرد، پس اجرشان را پیش پروردگارشان خواهند داشت، و نه بیمی بر آنان است، و نه اندوهناک خواهند شد.'),
(70, 2, 63, 'و چون از شما پیمان محکم گرفتیم، و [کوه‌] طور را بر فراز شما افراشتیم، [و فرمودیم:] «آنچه را به شما داده‌ایم به جد و جهد بگیرید، و آنچه را در آن است به خاطر داشته باشید، باشد که به تقوا گرایید.»'),
(71, 2, 64, 'سپس شما بعد از آن [پیمان‌] رویگردان شدید، و اگر فضل خدا و رحمت او بر شما نبود، مسلما از زیانکاران بودید.'),
(72, 2, 65, 'و کسانی از شما را که در روز شنبه [از فرمان خدا] تجاوز کردند نیک شناختید، پس ایشان را گفتیم: «بوزینگانی طردشده باشید.»'),
(73, 2, 66, 'و ما آن [عقوبت‌] را برای حاضران، و [نسلهای‌] پس از آن، عبرتی، و برای پرهیزگاران پندی قرار دادیم.'),
(74, 2, 67, 'و هنگامی که موسی به قوم خود گفت: «خدا به شما فرمان می‌دهد که: ماده گاوی را سر ببرید»، گفتند: «آیا ما را به ریشخند می‌گیری؟» گفت: «پناه می‌برم به خدا که [مبادا] از جاهلان باشم.»'),
(75, 2, 68, 'گفتند: «پروردگارت را برای ما بخوان، تا بر ما روشن سازد که آن چگونه [گاوی‌] است؟» گفت: «وی می‌فرماید: آن ماده گاوی است نه پیر و نه خردسال، [بلکه‌] میانسالی است بین این دو. پس آنچه را [بدان ]مأمورید به جای آرید.»'),
(76, 2, 69, 'گفتند: «از پروردگارت بخواه، تا بر ما روشن کند که رنگش چگونه است؟» گفت: «وی می‌فرماید: آن ماده گاوی است زرد یکدست و خالص، که رنگش بینندگان را شاد می‌کند.»'),
(77, 2, 70, 'گفتند: «از پروردگارت بخواه، تا بر ما روشن گرداند که آن چگونه [گاوی‌] باشد؟ زیرا [چگونگی‌] این ماده گاو بر ما مشتبه شده، و[لی با توضیحات بیشتر تو] ما ان شاء الله حتماً هدایت خواهیم شد.»'),
(78, 2, 71, 'گفت: «وی می‌فرماید: در حقیقت، آن ماده گاوی است که نه رام است تا زمین را شخم زند؛ و نه کشتزار را آبیاری کند؛ بی‌نقص است؛ و هیچ لکه‌ای در آن نیست.» گفتند: «اینک سخن درست آوردی.» پس آن را سر بریدند، و چیزی نمانده بود که نکنند.'),
(79, 2, 72, 'و چون شخصی را کشتید، و در باره او با یکدیگر به ستیزه برخاستید، و حال آنکه خدا، آنچه را کتمان می‌کردید، آشکار گردانید.'),
(80, 2, 73, 'پس فرمودیم: «پاره‌ای از آن [گاو سر بریده را] به آن [مقتول‌] بزنید» [تا زنده شود]. این گونه خدا مردگان را زنده می‌کند، و آیات خود را به شما می‌نمایاند، باشد که بیندیشید.'),
(81, 2, 74, 'سپس دلهای شما بعد از این [واقعه‌] سخت گردید، همانند سنگ، یا سخت‌تر از آن، چرا که از برخی سنگها، جویهایی بیرون می‌زند، و پاره‌ای از آنها می‌شکافد و آب از آن خارج می‌شود، و برخی از آنها از بیم خدا فرو می‌ریزد، و خدا از آنچه می‌کنید غافل نیست.'),
(82, 2, 75, 'آیا طمع دارید که [اینان‌] به شما ایمان بیاورند؟ با آنکه گروهی از آنان سخنان خدا را می‌شنیدند، سپس آن را بعد از فهمیدنش تحریف می‌کردند، و خودشان هم می‌دانستند.'),
(83, 2, 76, 'و [همین یهودیان‌] چون با کسانی که ایمان آورده‌اند برخورد کنند، می‌گویند: «ما ایمان آورده‌ایم.» و وقتی با همدیگر خلوت می‌کنند، می‌گویند: «چرا از آنچه خداوند بر شما گشوده است، برای آنان حکایت می‌کنید تا آنان به [استناد] آن، پیش پروردگارتان بر ضد شما استدلال کنند؟ آیا فکر نمی‌کنید؟»'),
(84, 2, 77, 'آیا نمی‌دانند که خداوند آنچه را پوشیده می‌دارند، و آنچه را آشکار می‌کنند، می‌داند؟'),
(85, 2, 78, 'و [بعضی‌] از آنان بی‌سوادانی هستند که کتاب [خدا] را جز خیالات خامی نمی‌دانند، و فقط گمان می‌برند.'),
(86, 2, 79, 'پس وای بر کسانی که کتاب [تحریف‌شده‌ای‌] با دستهای خود می‌نویسند، سپس می‌گویند: «این از جانب خداست»، تا بدان بهای ناچیزی به دست آرند؛ پس وای بر ایشان از آنچه دستهایشان نوشته، و وای بر ایشان از آنچه [از این راه‌] به دست می‌آورند.'),
(87, 2, 80, 'و گفتند: «جز روزهایی چند، هرگز آتش به ما نخواهد رسید.» بگو: «مگر پیمانی از خدا گرفته‌اید؟ -که خدا پیمان خود را هرگز خلاف نخواهد کرد- یا آنچه را نمی‌دانید به دروغ به خدا نسبت می‌دهید؟»'),
(88, 2, 81, 'آری، کسی که بدی به دست آورد، و گناهش او را در میان گیرد، پس چنین کسانی اهل آتشند، و در آن ماندگار خواهند بود.'),
(89, 2, 82, 'و کسانی که ایمان آورده، و کارهای شایسته کرده‌اند، آنان اهل بهشتند، و در آن جاودان خواهند ماند.'),
(90, 2, 83, 'و چون از فرزندان اسرائیل پیمان محکم گرفتیم که: «جز خدا را نپرستید، و به پدر و مادر، و خویشان و یتیمان و مستمندان احسان کنید، و با مردم [به زبان‌] خوش سخن بگویید، و نماز را به پا دارید، و زکات را بدهید»؛ آنگاه، جز اندکی از شما، [همگی‌] به حالت اعراض روی برتافتید.'),
(91, 2, 84, 'و چون از شما پیمان محکم گرفتیم که: «خون همدیگر را مریزید، و یکدیگر را از سرزمین خود بیرون نکنید»؛ سپس [به این پیمان‌] اقرار کردید، و خود گواهید.'),
(92, 2, 85, '[ولی‌] باز همین شما هستید که یکدیگر را می‌کشید، و گروهی از خودتان را از دیارشان بیرون می‌رانید، و به گناه و تجاوز، بر ضد آنان به یکدیگر کمک می‌کنید. و اگر به اسارت پیش شما آیند، به [دادن‌] فدیه، آنان را آزاد می‌کنید، با آنکه [نه تنها کشتن، بلکه‌] بیرون کردن آنان بر شما حرام شده است. آیا شما به پاره‌ای از کتاب [تورات‌] ایمان می‌آورید، و به پاره‌ای کفر می‌ورزید؟ پس جزای هر کس از شما که چنین کند، جز خواری در زندگی دنیا چیزی نخواهد بود، و روز رستاخیز ایشان را به سخت‌ترین عذابها باز برند، و خداوند از آنچه می‌کنید غافل نیست.'),
(93, 2, 86, 'همین کسانند که زندگی دنیا را به [بهای‌] جهان دیگر خریدند. پس نه عذاب آنان سبک گردد، و نه ایشان یاری شوند.'),
(94, 2, 87, 'و همانا به موسی کتاب [تورات‌] را دادیم، و پس از او پیامبرانی را پشت سر هم فرستادیم، و عیسی پسر مریم را معجزه‌های آشکار بخشیدیم، و او را با «روح القدس» تأیید کردیم؛ پس چرا هر گاه پیامبری چیزی را که خوشایند شما نبود برایتان آورد، کبر ورزیدید؟ گروهی را دروغگو خواندید و گروهی را کشتید.'),
(95, 2, 88, 'و گفتند: «دلهای ما در غلاف است.» [نه، چنین نیست‌] بلکه خدا به سزای کفرشان، لعنتشان کرده است. پس آنان که ایمان می‌آورند چه اندک‌شماره‌اند.'),
(96, 2, 89, 'و هنگامی که از جانب خداوند کتابی که مؤید آنچه نزد آنان است برایشان آمد، و از دیرباز [در انتظارش‌] بر کسانی که کافر شده بودند پیروزی می‌جستند؛ ولی همین که آنچه [که اوصافش‌] را می‌شناختند برایشان آمد، انکارش کردند. پس لعنت خدا بر کافران باد.'),
(97, 2, 90, 'وه که به چه بد بهایی خود را فروختند که به آنچه خدا نازل کرده بود از سر رشک انکار آوردند، که چرا خداوند از فضل خویش بر هر کس از بندگانش که بخواهد [آیاتی‌] فرو می‌فرستد. پس به خشمی بر خشم دیگر گرفتار آمدند. و برای کافران عذابی خفت‌آور است.'),
(98, 2, 91, 'و چون به آنان گفته شود: «به آنچه خدا نازل کرده ایمان آورید»، می‌گویند: «ما به آنچه بر [پیامبر] خودمان نازل شده ایمان می‌آوریم.» و غیر آن را -با آنکه [کاملا] حق و مؤید همان چیزی است که با آنان است- انکار می‌کنند. بگو: «اگر مؤمن بودید، پس چرا پیش از این، پیامبران خدا را می‌کشتید؟»'),
(99, 2, 92, 'و قطعاً موسی برای شما معجزات آشکاری آورد، سپس آن گوساله را در غیاب وی [به خدایی‌] گرفتید، و ستمکار شدید.'),
(100, 2, 93, 'و آنگاه که از شما پیمان محکم گرفتیم، و [کوه‌] طور را بر فراز شما برافراشتیم، [و گفتیم:] «آنچه را به شما داده‌ایم به جد و جهد بگیرید، و [به دستورهای آن‌] گوش فرا دهید.» گفتند: «شنیدیم و نافرمانی کردیم.» و بر اثر کفرشان، [مِهر ]گوساله در دلشان سرشته شد. بگو: «اگر مؤمنید [بدانید که‌] ایمانتان شما را به بد چیزی وامی‌دارد.»'),
(101, 2, 94, 'بگو: «اگر در نزد خدا، سرای بازپسین یکسر به شما اختصاص دارد، نه دیگر مردم، پس اگر راست می‌گویید آرزوی مرگ کنید.»'),
(102, 2, 95, 'ولی به سبب کارهایی که از پیش کرده‌اند، هرگز آن را آرزو نخواهند کرد. و خدا به [حال‌] ستمگران داناست.'),
(103, 2, 96, 'و آنان را مسلما آزمندترین مردم به زندگی، و [حتی حریص‌تر] از کسانی که شرک می‌ورزند خواهی یافت. هر یک از ایشان آرزو دارد که کاش هزار سال عمر کند با آنکه اگر چنین عمری هم به او داده شود، وی را از عذاب دور نتواند داشت. و خدا بر آنچه می‌کنند بیناست.'),
(104, 2, 97, 'بگو: «کسی که دشمن جبرئیل است [در واقع دشمن خداست‌] چرا که او، به فرمان خدا، قرآن را بر قلبت نازل کرده است، در حالی که مؤید [کتابهای آسمانی‌] پیش از آن، و هدایت و بشارتی برای مؤمنان است.»'),
(105, 2, 98, 'هر که دشمن خدا، و فرشتگان و فرستادگان او، و جبرئیل و میکائیل است [بداند که‌] خدا یقینا دشمن کافران است.'),
(106, 2, 99, 'و همانا بر تو آیاتی روشن فرو فرستادیم، و جز فاسقان [کسی‌] آنها را انکار نمی‌کند.'),
(107, 2, 100, 'و مگر نه این بود که [یهود] هر گاه پیمانی بستند، گروهی از ایشان آن را دور افکندند؟ بلکه [حقیقت این است که‌] بیشترشان ایمان نمی‌آورند.'),
(108, 2, 101, 'و آنگاه که فرستاده‌ای از جانب خداوند برایشان آمد -که آنچه را با آنان بود تصدیق می‌داشت- گروهی از اهل کتاب، کتاب خدا را پشت سر افکندند، چنانکه گویی [از آن هیچ‌] نمی‌دانند.'),
(109, 2, 102, 'و آنچه را که شیطان‌[صفت‌]ها در سلطنت سلیمان خوانده [و درس گرفته‌] بودند، پیروی کردند. و سلیمان کفر نورزید، لیکن آن شیطان‌[صفت‌]ها به کفر گراییدند که به مردم سحر می‌آموختند. و [نیز از] آنچه بر آن دو فرشته، هاروت و ماروت، در بابِل فرو فرستاده شده بود [پیروی کردند]، با اینکه آن دو [فرشته‌] هیچ کس را تعلیم [سحر] نمی‌کردند مگر آنکه [قبلا به او] می‌گفتند: «ما [وسیله‌] آزمایشی [برای شما ]هستیم، پس زنهار کافر نشوی.» و[لی‌] آنها از آن دو [فرشته‌] چیزهایی می‌آموختند که به وسیله آن میان مرد و همسرش جدایی بیفکنند. هر چند بدون فرمان خدا نمی‌توانستند به وسیله آن به احدی زیان برسانند. و [خلاصه‌] چیزی می‌آموختند که برایشان زیان داشت، و سودی بدیشان نمی‌رسانید. و قطعاً [یهودیان‌] دریافته بودند که هر کس خریدار این [متاع‌] باشد، در آخرت بهره‌ای ندارد. وه که چه بد بود آنچه به جان خریدند-اگر می‌دانستند.'),
(110, 2, 103, 'اگر آنها گرویده و پرهیزگاری کرده بودند قطعاً پاداشی [که‌] از جانب خدا [می‌یافتند] بهتر بود-اگر می‌دانستند.'),
(111, 2, 104, 'ای کسانی که ایمان آورده‌اید، نگویید: «راعنا»، و بگویید: «انظرنا»، و [این توصیه را] بشنوید؛ و [گر نه‌] کافران را عذابی دردناک است.'),
(112, 2, 105, 'نه کسانی که از اهل کتاب کافر شده‌اند، و نه مشرکان [هیچ کدام‌] دوست نمی‌دارند خیری از جانب پروردگارتان بر شما فرود آید، با آنکه خدا هر که را خواهد به رحمت خود اختصاص دهد، و خدا دارای فزون‌بخشی عظیم است.'),
(113, 2, 106, 'هر حکمی را نسخ کنیم، یا آن را به [دست‌] فراموشی بسپاریم، بهتر از آن، یا مانندش را می‌آوریم؛ مگر ندانستی که خدا بر هر کاری تواناست؟'),
(114, 2, 107, 'مگر ندانستی که فرمانروایی آسمانها و زمین از آن خداست؛ و شما جز خدا سرور و یاوری ندارید؟'),
(115, 2, 108, 'آیا می‌خواهید از پیامبر خود همان را بخواهید که قبلا از موسی خواسته شد؟ و هر کس کفر را با ایمان عوض کند، مسلما از راه درست گمراه شده است.'),
(116, 2, 109, 'بسیاری از اهل کتاب -پس از اینکه حق برایشان آشکار شد- از روی حسدی که در وجودشان بود، آرزو می‌کردند که شما را، بعد از ایمانتان، کافر گردانند. پس عفو کنید و درگذرید، تا خدا فرمان خویش را بیاورد، که خدا بر هر کاری تواناست.'),
(117, 2, 110, 'و نماز را به پا دارید و زکات را بدهید؛ و هر گونه نیکی که برای خویش از پیش فرستید، آن را نزد خدا باز خواهید یافت؛ آری، خدا به آنچه می‌کنید بیناست.'),
(118, 2, 111, 'و گفتند: «هرگز کسی به بهشت درنیاید، مگر آنکه یهودی یا ترسا باشد.» این آرزوهای [واهیِ‌] ایشان است. بگو: «اگر راست می‌گویید، دلیل خود را بیاورید.»'),
(119, 2, 112, 'آری، هر کس که خود را با تمام وجود، به خدا تسلیم کند و نیکوکار باشد، پس مزد وی پیش پروردگار اوست، و بیمی بر آنان نیست، و غمگین نخواهند شد.'),
(120, 2, 113, 'و یهودیان گفتند: «ترسایان بر حق نیستند.» و ترسایان گفتند: «یهودیان بر حق نیستند» -با آنکه آنان کتاب [آسمانی‌] را می‌خوانند. افراد نادان نیز [سخنی‌] همانند گفته ایشان گفتند. پس خداوند، روز رستاخیز در آنچه با هم اختلاف می‌کردند، میان آنان داوری خواهد کرد.'),
(121, 2, 114, 'و کیست بیدادگرتر از آن کس که نگذارد در مساجد خدا، نام وی برده شود، و در ویرانی آنها بکوشد؟ آنان حق ندارند جز ترسان-لرزان در آن [مسجد]ها درآیند. در این دنیا ایشان را خواری، و در آخرت عذابی بزرگ است.'),
(122, 2, 115, 'و مشرق و مغرب از آنِ خداست؛ پس به هر سو رو کنید، آنجا روی [به‌] خداست. آری، خدا گشایشگر داناست.'),
(123, 2, 116, 'و گفتند: «خداوند فرزندی برای خود اختیار کرده است.» او منزه است. بلکه هر چه در آسمانها و زمین است، از آن اوست، [و] همه فرمانپذیر اویند.'),
(124, 2, 117, '[او] پدید آورنده آسمانها و زمین [است‌]، و چون به کاری اراده فرماید، فقط می‌گوید: «[موجود] باش»؛ پس [فوراً موجود] می‌شود.'),
(125, 2, 118, 'افراد نادان گفتند: «چرا خدا با ما سخن نمی‌گوید؟ یا برای ما معجزه‌ای نمی‌آید؟» کسانی که پیش از اینان بودند [نیز] مثل همین گفته ایشان را می‌گفتند. دلها [و افکار]شان به هم می‌ماند. ما نشانه‌ها[ی خود] را برای گروهی که یقین دارند، نیک روشن گردانیده‌ایم.'),
(126, 2, 119, 'ما تو را بحق فرستادیم، تا بشارتگر و بیم‌دهنده باشی، و [لی‌] درباره دوزخیان، از تو پرسشی نخواهد شد.'),
(127, 2, 120, 'و هرگز یهودیان و ترسایان از تو راضی نمی‌شوند، مگر آنکه از کیش آنان پیروی کنی. بگو: «در حقیقت، تنها هدایت خداست که هدایت [واقعی‌] است.» و چنانچه پس از آن علمی که تو را حاصل شد، باز از هوسهای آنان پیروی کنی، در برابر خدا سرور و یاوری نخواهی داشت.'),
(128, 2, 121, 'کسانی که کتاب [آسمانی‌] به آنان داده‌ایم، [و] آن را چنانکه باید می‌خوانند، ایشانند که بدان ایمان دارند. و[لی‌] کسانی که بدان کفر ورزند، همانانند که زیانکارانند.'),
(129, 2, 122, 'ای فرزندان اسرائیل، نعمتم را، که بر شما ارزانی داشتم، و اینکه شما را بر جهانیان برتری دادم، یاد کنید.'),
(130, 2, 123, 'و بترسید از روزی که هیچ کس چیزی [از عذاب خدا] را از کسی دفع نمی‌کند، و نه بدل و بلاگردانی از وی پذیرفته شود، و نه او را میانجیگری سودمند افتد، و نه یاری شوند.'),
(131, 2, 124, 'و چون ابراهیم را پروردگارش با کلماتی بیازمود، و وی آن همه را به انجام رسانید، [خدا به او] فرمود: «من تو را پیشوای مردم قرار دادم.» [ابراهیم‌] پرسید: «از دودمانم [چطور]؟» فرمود: «پیمان من به بیدادگران نمی‌رسد.»'),
(132, 2, 125, 'و چون خانه [کعبه‌] را برای مردم محل اجتماع و [جای‌] امنی قرار دادیم، [و فرمودیم:] «در مقام ابراهیم، نمازگاهی برای خود اختیار کنید»، و به ابراهیم و اسماعیل فرمان دادیم که: «خانه مرا برای طواف‌کنندگان و معتکفان و رکوع و سجودکنندگان پاکیزه کنید.»'),
(133, 2, 126, 'و چون ابراهیم گفت: «پروردگارا، این [سرزمین‌] را شهری امن گردان، و مردمش را -هر کس از آنان که به خدا و روز بازپسین ایمان بیاورد- از فرآورده‌ها روزی بخش»، فرمود: «و[لی‌] هر کس کفر بورزد، اندکی برخوردارش می‌کنم، سپس او را با خواری به سوی عذاب آتش [دوزخ‌] می‌کشانم، و چه بد سرانجامی است.»'),
(134, 2, 127, 'و هنگامی که ابراهیم و اسماعیل پایه‌های خانه [کعبه‌] را بالا می‌بردند، [می‌گفتند:] «ای پروردگار ما، از ما بپذیر که در حقیقت، تو شنوای دانایی.'),
(135, 2, 128, 'پروردگارا، ما را تسلیم [فرمان‌] خود قرار ده؛ و از نسل ما، امتی فرمانبردار خود [پدید آر]؛ و آداب دینی ما را به ما نشان ده؛ و بر ما ببخشای، که تویی توبه‌پذیر مهربان.'),
(136, 2, 129, 'پروردگارا، در میان آنان، فرستاده‌ای از خودشان برانگیز، تا آیات تو را بر آنان بخواند، و کتاب و حکمت به آنان بیاموزد و پاکیزه‌شان کند، زیرا که تو خود، شکست‌ناپذیر حکیمی.»'),
(137, 2, 130, 'و چه کسی -جز آنکه به سبک‌مغزی گراید- از آیین ابراهیم روی برمی‌تابد؟ و ما او را در این دنیا برگزیدیم؛ و البته در آخرت [نیز] از شایستگان خواهد بود.'),
(138, 2, 131, 'هنگامی که پروردگارش به او فرمود: «تسلیم شو»، گفت: «به پروردگار جهانیان تسلیم شدم.»'),
(139, 2, 132, 'و ابراهیم و یعقوب، پسران خود را به همان [آیین‌] سفارش کردند؛ [و هر دو در وصیتشان چنین گفتند:] «ای پسران من، خداوند برای شما این دین را برگزید؛ پس، البته نباید جز مسلمان بمیرید.»'),
(140, 2, 133, 'آیا وقتی که یعقوب را مرگ فرا رسید، حاضر بودید؟ هنگامی که به پسران خود گفت: «پس از من، چه را خواهید پرستید؟» گفتند: «معبود تو، و معبود پدرانت، ابراهیم و اسماعیل و اسحاق -معبودی یگانه- را می‌پرستیم؛ و در برابر او تسلیم هستیم.»'),
(141, 2, 134, 'آن جماعت را روزگار به سر آمد؛ دستاورد آنان برای آنان و دستاورد شما برای شماست؛ و از آنچه آنان می‌کرده‌اند، شما بازخواست نخواهید شد.'),
(142, 2, 135, 'و [اهل کتاب‌] گفتند: «یهودی یا مسیحی باشید، تا هدایت یابید»؛ بگو: «نه، بلکه [بر] آیین ابراهیم حق‌گرا [هستم‌]؛ و وی از مشرکان نبود.»'),
(143, 2, 136, 'بگویید: «ما به خدا، و به آنچه بر ما نازل شده، و به آنچه بر ابراهیم و اسحاق و یعقوب و اسباط نازل آمده، و به آنچه به موسی و عیسی داده شده، و به آنچه به همه پیامبران از سوی پروردگارشان داده شده، ایمان آورده‌ایم؛ میان هیچ یک از ایشان فرق نمی‌گذاریم؛ و در برابر او تسلیم هستیم.»'),
(144, 2, 137, 'پس اگر آنان [هم‌] به آنچه شما بدان ایمان آورده‌اید، ایمان آوردند، قطعاً هدایت شده‌اند، ولی اگر روی برتافتند، جز این نیست که سر ستیز [و جدایی ] دارند؛ و به زودی خداوند [شر] آنان را از تو کفایت خواهد کرد، که او شنوای داناست.'),
(145, 2, 138, 'این است نگارگری الهی؛ و کیست خوش‌نگارتر از خدا؟ و ما او را پرستندگانیم.'),
(146, 2, 139, 'بگو: آیا درباره خدا با ما بحث و گفتگو می‌کنید؟ با آنکه او پروردگار ما و پروردگار شماست؛ و کردارهای ما از آن ما، و کردارهای شما از آن شماست، و ما برای او اخلاص می‌ورزیم.'),
(147, 2, 140, 'یا می‌گویید: «ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط [دوازده‌گانه‌] یهودی یا نصرانی بوده‌اند؟» بگو: «آیا شما بهتر می‌دانید یا خدا؟» و کیست ستمکارتر از آن کس که شهادتی از خدا را در نزد خویش پوشیده دارد؟ و خدا از آنچه می‌کنید غافل نیست.'),
(148, 2, 141, 'آن جماعت را روزگار سپری شد؛ برای ایشان است آنچه به دست آورده‌اند؛ و برای شماست آنچه به دست آورده‌اید؛ و از آنچه آنان می‌کرده‌اند، شما بازخواست نخواهید شد.'),
(149, 2, 142, 'به زودی مردم کم خرد خواهند گفت: «چه چیز آنان را از قبله‌ای که بر آن بودند رویگردان کرد؟» بگو: «مشرق و مغرب از آن خداست؛ هر که را خواهد به راه راست هدایت می‌کند.»'),
(150, 2, 143, 'و بدین گونه شما را امتی میانه قرار دادیم، تا بر مردم گواه باشید؛ و پیامبر بر شما گواه باشد. و قبله‌ای را که [چندی‌] بر آن بودی، مقرر نکردیم جز برای آنکه کسی را که از پیامبر پیروی می‌کند، از آن کس که از عقیده خود برمی‌گردد بازشناسیم؛ هر چند [این کار] جز بر کسانی که خدا هدایت‌[شان‌] کرده، سخت گران بود؛ و خدا بر آن نبود که ایمان شما را ضایع گرداند، زیرا خدا [نسبت‌] به مردم دلسوز و مهربان است.'),
(151, 2, 144, 'ما [به هر سو] گردانیدنِ رویت در آسمان را نیک می‌بینیم. پس [باش تا] تو را به قبله‌ای که بدان خشنود شوی برگردانیم؛ پس روی خود را به سوی مسجدالحرام کن؛ و هر جا بودید، روی خود را به سوی آن بگردانید. در حقیقت، اهل کتاب نیک می‌دانند که این [تغییر قبله‌] از جانب پروردگارشان [بجا و] درست است؛ و خدا از آنچه می‌کنند غافل نیست.'),
(152, 2, 145, 'و اگر هر گونه معجزه‌ای برای اهل کتاب بیاوری [باز] قبله تو را پیروی نمی‌کنند، و تو [نیز] پیرو قبله آنان نیستی، و خود آنان پیرو قبله یکدیگر نیستند، و پس از علمی که تو را [حاصل‌] آمده، اگر از هوسهای ایشان پیروی کنی، در آن صورت جداً از ستمکاران خواهی بود.'),
(153, 2, 146, 'کسانی که به ایشان کتاب [آسمانی‌] داده‌ایم، همان گونه که پسران خود را می‌شناسند، او [=محمد] را می‌شناسند؛ و مسلماً گروهی از ایشان حقیقت را نهفته می‌دارند، و خودشان [هم‌] می‌دانند.'),
(154, 2, 147, 'حق از جانب پروردگار توست. پس مبادا از تردیدکنندگان باشی.'),
(155, 2, 148, 'و برای هر کسی قبله‌ای است که وی روی خود را به آن [سوی‌] می‌گرداند؛ پس در کارهای نیک بر یکدیگر پیشی گیرید. هر کجا که باشید، خداوند همگی شما را [به سوی خود باز] می‌آورد؛ در حقیقت، خدا بر همه چیز تواناست.'),
(156, 2, 149, 'و از هر کجا بیرون آمدی، روی خود را به سوی مسجدالحرام بگردان، و البته این [فرمان‌] حق است و از جانب پروردگار تو است و خداوند از آنچه می‌کنید غافل نیست.'),
(157, 2, 150, 'و از هر کجا بیرون آمدی، [به هنگام نماز] روی خود را به سمت مسجدالحرام بگردان؛ و هر کجا بودید رویهای خود را به سوی آن بگردانید، تا برای مردم -غیر از ستمگرانشان- بر شما حجتی نباشد. پس، از آنان نترسید، و از من بترسید، تا نعمت خود را بر شما کامل گردانم، و باشد که هدایت شوید.'),
(158, 2, 151, 'همان طور که در میان شما، فرستاده‌ای از خودتان روانه کردیم، [که‌] آیات ما را بر شما می‌خواند، و شما را پاک می‌گرداند، و به شما کتاب و حکمت می‌آموزد، و آنچه را نمی‌دانستید به شما یاد می‌دهد.'),
(159, 2, 152, 'پس مرا یاد کنید، [تا] شما را یاد کنم؛ و شکرانه‌ام را به جای آرید؛ و با من ناسپاسی نکنید.'),
(160, 2, 153, 'ای کسانی که ایمان آورده‌اید، از شکیبایی و نماز یاری جویید؛ زیرا خدا با شکیبایان است.'),
(161, 2, 154, 'و کسانی را که در راه خدا کشته می‌شوند، مرده نخوانید، بلکه زنده‌اند؛ ولی شما نمی‌دانید.'),
(162, 2, 155, 'و قطعاً شما را به چیزی از [قبیلِ‌] ترس و گرسنگی، و کاهشی در اموال و جانها و محصولات می‌آزماییم؛ و مژده ده شکیبایان را:'),
(163, 2, 156, '[همان‌] کسانی که چون مصیبتی به آنان برسد، می‌گویند: «ما از آنِ خدا هستیم، و به سوی او باز می‌گردیم.»'),
(164, 2, 157, 'بر ایشان درودها و رحمتی از پروردگارشان [باد] و راه‌یافتگان [هم‌] خود ایشانند.'),
(165, 2, 158, 'در حقیقت، «صفا» و «مروه» از شعایر خداست [که یادآور اوست‌]؛ پس هر که خانه [خدا] را حج کند، یا عمره گزارد، بر او گناهی نیست که میان آن دو سعی به جای آورد. و هر که افزون بر فریضه، کار نیکی کند، خدا حق شناس و داناست.'),
(166, 2, 159, 'کسانی که نشانه‌های روشن، و رهنمودی را که فرو فرستاده‌ایم، بعد از آنکه آن را برای مردم در کتاب توضیح داده‌ایم، نهفته می‌دارند، آنان را خدا لعنت می‌کند، و لعنت‌کنندگان لعنتشان می‌کنند.'),
(167, 2, 160, 'مگر کسانی که توبه کردند، و [خود را] اصلاح نمودند، و [حقیقت را] آشکار کردند، پس بر آنان خواهم بخشود؛ و من توبه‌پذیر مهربانم.'),
(168, 2, 161, 'کسانی که کافر شدند، و در حال کفر مردند، لعنت خدا و فرشتگان و تمام مردم بر آنان باد.'),
(169, 2, 162, 'در آن [لعنت‌] جاودانه بمانند؛ نه عذابشان کاسته گردد، و نه مهلت یابند.'),
(170, 2, 163, 'و معبود شما، معبود یگانه‌ای است که جز او هیچ معبودی نیست، [و اوست‌] بخشایشگر مهربان.'),
(171, 2, 164, 'راستی که در آفرینش آسمانها و زمین، و در پی یکدیگر آمدن شب و روز، و کشتیهایی که در دریا روانند با آنچه به مردم سود می‌رساند، و [همچنین‌] آبی که خدا از آسمان فرو فرستاده، و با آن، زمین را پس از مردنش زنده گردانیده، و در آن هر گونه جنبنده‌ای پراکنده کرده، و [نیز در] گردانیدن بادها، و ابری که میان آسمان و زمین آرمیده است، برای گروهی که می‌اندیشند، واقعاً نشانه‌هایی [گویا] وجود دارد.'),
(172, 2, 165, 'و برخی از مردم، در برابر خدا، همانندهایی [برای او] برمی‌گزینند، و آنها را چون دوستی خدا، دوست می‌دارند؛ ولی کسانی که ایمان آورده‌اند، به خدا محبت بیشتری دارند. کسانی که [با برگزیدن بتها، به خود] ستم نموده‌اند اگر می‌دانستند هنگامی که عذاب را مشاهده کنند تمام نیرو[ها] از آنِ خداست، و خدا سخت‌کیفر است.'),
(173, 2, 166, 'آنگاه که پیشوایان از پیروان بیزاری جویند؛ و عذاب را مشاهده کنند، و میانشان پیوندها بریده گردد.'),
(174, 2, 167, 'و پیروان می‌گویند: «کاش برای ما بازگشتی بود تا همان گونه که [آنان‌] از ما بیزاری جستند [ما نیز] از آنان بیزاری می‌جستیم.» این گونه خداوند، کارهایشان را -که بر آنان مایه حسرتهاست- به ایشان می‌نمایاند، و از آتش بیرون‌آمدنی نیستند.'),
(175, 2, 168, 'ای مردم، از آنچه در زمین است حلال و پاکیزه را بخورید، و از گامهای شیطان پیروی مکنید که او دشمن آشکار شماست.'),
(176, 2, 169, '[او] شما را فقط به بدی و زشتی فرمان می‌دهد، و [وامی‌دارد] تا بر خدا، چیزی را که نمی‌دانید بربندید.'),
(177, 2, 170, 'و چون به آنان گفته شود: «از آنچه خدا نازل کرده است پیروی کنید»؛ می‌گویند: «نه، بلکه از چیزی که پدران خود را بر آن یافته‌ایم، پیروی می‌کنیم.» آیا هر چند پدرانشان چیزی را درک نمی‌کرده و به راه صواب نمی‌رفته‌اند [باز هم در خور پیروی هستند]؟'),
(178, 2, 171, 'و مَثَل [دعوت‌کننده‌] کافران چون مَثَل کسی است که حیوانی را که جز صدا و ندایی [مبهم، چیزی‌] نمی‌شنود بانگ می‌زند. [آری،] کرند، لالند، کورند. [و] درنمی‌یابند.'),
(179, 2, 172, 'ای کسانی که ایمان آورده‌اید، از نعمتهای پاکیزه‌ای که روزی شما کرده‌ایم، بخورید و اگر تنها او را می‌پرستید خدا را شکر کنید.'),
(180, 2, 173, '[خداوند،] تنها مردار و خون و گوشت خوک و آنچه را که [هنگام سر بریدن‌] نام غیر خدا بر آن برده شده، بر شما حرام گردانیده است. [ولی‌] کسی که [برای حفظ جان خود به خوردن آنها] ناچار شود، در صورتی که ستمگر و متجاوز نباشد بر او گناهی نیست، زیرا خدا آمرزنده و مهربان است.'),
(181, 2, 174, 'کسانی که آنچه را خداوند از کتاب نازل کرده، پنهان می‌دارند و بدان بهای ناچیزی به دست می‌آورند، آنان جز آتش در شکمهای خویش فرو نبرند. و خدا روز قیامت با ایشان سخن نخواهد گفت، و پاکشان نخواهد کرد، و عذابی دردناک خواهند داشت.'),
(182, 2, 175, 'آنان همان کسانی هستند که گمراهی را به [بهای‌] هدایت، و عذاب را به [ازای‌] آمرزش خریدند، پس به راستی چه اندازه باید بر آتش شکیبا باشند!'),
(183, 2, 176, 'چرا که خداوند کتاب [تورات‌] را به حق نازل کرده است، و کسانی که در باره کتاب [خدا] با یکدیگر به اختلاف پرداختند، در ستیزه‌ای دور و درازند.'),
(184, 2, 177, 'نیکوکاری آن نیست که روی خود را به سوی مشرق و [یا] مغرب بگردانید، بلکه نیکی آن است که کسی به خدا و روز بازپسین و فرشتگان و کتاب [آسمانی‌] و پیامبران ایمان آورد، و مال [خود] را با وجود دوست داشتنش، به خویشاوندان و یتیمان و بینوایان و در راه‌ماندگان و گدایان و در [راه آزاد کردن‌] بندگان بدهد، و نماز را برپای دارد، و زکات را بدهد، و آنان که چون عهد بندند، به عهد خود وفادارانند؛ و در سختی و زیان، و به هنگام جنگ شکیبایانند؛ آنانند کسانی که راست گفته‌اند، و آنان همان پرهیزگارانند.'),
(185, 2, 178, 'ای کسانی که ایمان آورده‌اید، در باره کشتگان، بر شما [حق‌] قصاص مقرر شده: آزاد عوض آزاد و بنده عوض بنده و زن عوض زن. و هر کس که از جانب برادر [دینی‌]اش [یعنی ولیّ مقتول‌]، چیزی [از حق قصاص‌] به او گذشت شود، [باید از گذشت ولیّ مقتول‌] به طور پسندیده پیروی کند، و با [رعایت‌] احسان، [خونبها را] به او بپردازد. این [حکم‌] تخفیف و رحمتی از پروردگار شماست؛ پس هر کس، بعد از آن از اندازه درگذرد، وی را عذابی دردناک است.'),
(186, 2, 179, 'و ای خردمندان، شما را در قصاص زندگانی است، باشد که به تقوا گرایید.'),
(187, 2, 180, 'بر شما مقرر شده است که چون یکی از شما را مرگ فرا رسد، اگر مالی بر جای گذارد، برای پدر و مادر و خویشاوندان [خود] به طور پسندیده وصیت کند؛ [این کار] حقی است بر پرهیزگاران.'),
(188, 2, 181, 'پس هر کس آن [وصیت‌] را بعد از شنیدنش تغییر دهد، گناهش تنها بر [گردن‌] کسانی است که آن را تغییر می‌دهند. آری، خدا شنوای داناست.'),
(189, 2, 182, 'ولی کسی که از انحراف [و تمایل بیجای‌] وصیت‌کننده‌ای [نسبت به ورثه‌اش‌] یا از گناه او [در وصیت به کار خلاف‌] بیم داشته باشد و میانشان را سازش دهد، بر او گناهی نیست، که خدا آمرزنده مهربان است.'),
(190, 2, 183, 'ای کسانی که ایمان آورده‌اید، روزه بر شما مقرر شده است، همان گونه که بر کسانی که پیش از شما [بودند] مقرر شده بود، باشد که پرهیزگاری کنید.'),
(191, 2, 184, '[روزه در] روزهای معدودی [بر شما مقرر شده است‌]. [ولی‌] هر کس از شما بیمار یا در سفر باشد، [به همان شماره‌] تعدادی از روزهای دیگر [را روزه بدارد]، و بر کسانی که [روزه‌] طاقت‌فرساست، کفاره‌ای است که خوراک دادن به بینوایی است. و هر کس به میل خود، بیشتر نیکی کند، پس آن برای او بهتر است، و اگر بدانید، روزه گرفتن برای شما بهتر است.'),
(192, 2, 185, 'ماه رمضان [همان ماه‌] است که در آن، قرآن فرو فرستاده شده است، [کتابی ] که مردم را راهبر، و [متضمّن‌] دلایل آشکار هدایت، و [میزان‌] تشخیص حق از باطل است. پس هر کس از شما این ماه را درک کند باید آن را روزه بدارد، و کسی که بیمار یا در سفر است [باید به شماره آن،] تعدادی از روزهای دیگر [را روزه بدارد]. خدا برای شما آسانی می‌خواهد و برای شما دشواری نمی‌خواهد؛ تا شماره [مقرر ]را تکمیل کنید و خدا را به پاس آنکه رهنمونیتان کرده است به بزرگی بستایید، و باشد که شکرگزاری کنید.'),
(193, 2, 186, 'و هرگاه بندگان من، از تو در باره من بپرسند، [بگو] من نزدیکم، و دعای دعاکننده را -به هنگامی که مرا بخواند- اجابت می‌کنم، پس [آنان‌] باید فرمان مرا گردن نهند و به من ایمان آورند، باشد که راه یابند.'),
(194, 2, 187, 'در شبهای روزه، همخوابگی با زنانتان بر شما حلال گردیده است. آنان برای شما لباسی هستند و شما برای آنان لباسی هستید. خدا می‌دانست که شما با خودتان ناراستی می‌کردید، پس توبه شما را پذیرفت و از شما درگذشت. پس، اکنون [در شبهای ماه رمضان می‌توانید] با آنان همخوابگی کنید، و آنچه را خدا برای شما مقرر داشته طلب کنید. و بخورید و بیاشامید تا رشته سپید بامداد از رشته سیاه [شب‌] بر شما نمودار شود؛ سپس روزه را تا [فرا رسیدن‌] شب به اتمام رسانید. و در حالی که در مساجد معتکف هستید [با زنان‌] درنیامیزید. این است حدود احکام الهی! پس [زنهار به قصد گناه‌] بدان نزدیک نشوید. این گونه، خداوند آیات خود را برای مردم بیان می‌کند، باشد که پروا پیشه کنند.'),
(195, 2, 188, 'و اموالتان را میان خودتان به ناروا مخورید، و [به عنوان رشوه قسمتی از] آن را به قضات مدهید تا بخشی از اموال مردم را به گناه بخورید، در حالی که خودتان [هم خوب‌] می‌دانید.'),
(196, 2, 189, 'در باره [حکمت‌] هلالها[ی ماه‌] از تو می‌پرسند، بگو: «آنها [شاخص‌] گاه‌شماری برای مردم و [موسم‌] حجّ‌اند.» و نیکی آن نیست که از پشت خانه‌ها درآیید، بلکه نیکی آن است که کسی تقوا پیشه کند، و به خانه‌ها از در [ورودی‌] آنها درآیید، و از خدا بترسید، باشد که رستگار گردید.'),
(197, 2, 190, 'و در راه خدا، با کسانی که با شما می‌جنگند، بجنگید، ولی از اندازه درنگذرید، زیرا خداوند تجاوزکاران را دوست نمی‌دارد؛'),
(198, 2, 191, 'و هر کجا بر ایشان دست یافتید آنان را بکشید، و همان گونه که شما را بیرون راندند، آنان را بیرون برانید، [چرا که‌] فتنه [=شرک‌] از قتل بدتر است، [با این همه‌] در کنار مسجد الحرام با آنان جنگ مکنید، مگر آنکه با شما در آن جا به جنگ درآیند، پس اگر با شما جنگیدند، آنان را بکشید، که کیفر کافران چنین است.'),
(199, 2, 192, 'و اگر بازایستادند، البته خدا آمرزنده مهربان است.'),
(200, 2, 193, 'با آنان بجنگید تا دیگر فتنه‌ای نباشد، و دین، مخصوص خدا شود. پس اگر دست برداشتند، تجاوز جز بر ستمکاران روا نیست.'),
(201, 2, 194, 'این ماه حرام در برابر آن ماه حرام است، و [هتک‌] حرمتها قصاص دارد. پس هر کس بر شما تعدّی کرد، همان گونه که بر شما تعدّی کرده، بر او تعدّی کنید و از خدا پروا بدارید و بدانید که خدا با تقواپیشگان است.'),
(202, 2, 195, 'و در راه خدا انفاق کنید، و خود را با دست خود به هلاکت میفکنید، و نیکی کنید که خدا نیکوکاران را دوست می‌دارد.'),
(203, 2, 196, 'و برای خدا حجّ و عمره را به پایان رسانید، و اگر [به علت موانعی‌] بازداشته شدید، آنچه از قربانی میسر است [قربانی کنید]؛ و تا قربانی به قربانگاه نرسیده سر خود را متراشید؛ و هر کس از شما بیمار باشد یا در سر ناراحتیی داشته باشد [و ناچار شود در احرام سر بتراشد] به کفاره [آن، باید] روزه‌ای بدارد، یا صدقه‌ای دهد، یا قربانیی بکند؛ و چون ایمنی یافتید، پس هر کس از [اعمال‌] عمره به حجّ پرداخت، [باید ]آنچه از قربانی میسر است [قربانی کند]، و آن کس که [قربانی ]نیافت [باید] در هنگام حجّ، سه روز روزه [بدارد]؛ و چون برگشتید هفت [روز دیگر روزه بدارید]؛ این ده [روز] تمام است. این [حجّ تمتّع ]برای کسی است که اهل مسجد الحرام [=مکّه‌] نباشد؛ و از خدا بترسید، و بدانید که خدا سخت‌کیفر است.'),
(204, 2, 197, 'حجّ در ماههای معینی است. پس هر کس در این [ماه‌]ها، حجّ را [برخود] واجب گرداند، [بداند که‌] در اثنای حجّ، همبستری و گناه و جدال [روا] نیست، و هر کار نیکی انجام می‌دهید، خدا آن را می‌داند، و برای خود توشه برگیرید که در حقیقت، بهترین توشه، پرهیزگاری است، و ای خردمندان! از من پروا کنید.'),
(205, 2, 198, 'بر شما گناهی نیست که [در سفر حجّ‌] از فضل پروردگارتان [روزیِ خویش‌] بجویید؛ پس چون از عرفات کوچ نمودید، خدا را در مشعر الحرام یاد کنید، و یادش کنید که شما را که پیشتر از بیراهان بودید، فرا راه آورد.'),
(206, 2, 199, 'پس، از همان جا که [انبوه‌] مردم روانه می‌شوند، شما نیز روانه شوید و از خداوند آمرزش خواهید که خدا آمرزنده مهربان است.'),
(207, 2, 200, 'و چون آداب ویژه حجّ خود را به جای آوردید، همان گونه که پدران خود را به یاد می‌آورید، یا با یادکردنی بیشتر، خدا را به یاد آورید. و از مردم کسی است که می‌گوید: «پروردگارا، به ما، در همین دنیا عطا کن» و حال آنکه برای او در آخرت نصیبی نیست.'),
(208, 2, 201, 'و برخی از آنان می‌گویند: «پروردگارا! در این دنیا به ما نیکی و در آخرت [نیز] نیکی عطا کن، و ما را از عذاب آتش [دور] نگه دار.»'),
(209, 2, 202, 'آنانند که از دستاوردشان بهره‌ای خواهند داشت و خدا زودشمار است.'),
(210, 2, 203, 'و خدا را در روزهایی معیّن یاد کنید، پس هر کس شتاب کند [و اعمال را] در دو روز [انجام دهد]، گناهی بر او نیست، و هر که تأخیر کند [و اعمال را در سه روز انجام دهد] گناهی بر او نیست. [این اختیار ]برای کسی است که [از محرمات‌] پرهیز کرده باشد. و از خدا پروا کنید و بدانید که شما را به سوی او گرد خواهد آورد.'),
(211, 2, 204, 'و از میان مردم کسی است که در زندگی این دنیا سخنش تو را به تعجّب وامی‌دارد، و خدا را بر آنچه در دل دارد گواه می‌گیرد، و حال آنکه او سخت‌ترین دشمنان است.'),
(212, 2, 205, 'و چون برگردد [یا ریاستی یابد] کوشش می‌کند که در زمین فساد نماید و کِشت و نسل را نابود سازد، و خداوند تباهکاری را دوست ندارد.'),
(213, 2, 206, 'و چون به او گفته شود: «از خدا پروا کن» نخوت، وی را به گناه کشاند. پس جهنم برای او بس است، و چه بد بستری است.'),
(214, 2, 207, 'و از میان مردم کسی است که جان خود را برای طلب خشنودی خدا می‌فروشد، و خدا نسبت به [این‌] بندگان مهربان است.'),
(215, 2, 208, 'ای کسانی که ایمان آورده‌اید، همگی به اطاعت [خدا] درآیید، و گامهای شیطان را دنبال مکنید که او برای شما دشمنی آشکار است.'),
(216, 2, 209, 'و اگر پس از آنکه برای شما دلایل آشکار آمد، دستخوش لغزش شدید، بدانید که خداوند توانای حکیم است.'),
(217, 2, 210, 'مگر انتظار آنان غیر از این است که خدا و فرشتگان، در [زیر] سایبانهایی از ابر سپید به سوی آنان بیایند و کار [داوری‌] یکسره شود؟ و کارها به سوی خدا بازگردانده می‌شود.'),
(218, 2, 211, 'از فرزندان اسرائیل بپرس: چه بسیار نشانه‌های روشنی به آنان دادیم و هر کس نعمت خدا را -پس از آنکه برای او آمد- [به کفران‌] بدل کند، خدا سخت‌کیفر است.'),
(219, 2, 212, 'زندگی دنیا در چشم کافران آراسته شده است، و مؤمنان را ریشخند می‌کنند و [حال آنکه‌] کسانی که تقواپیشه بوده‌اند، در روز رستاخیز، از آنان برترند و خدا به هر که بخواهد، بی‌شمار روزی می‌دهد.'),
(220, 2, 213, 'مردم، امتی یگانه بودند؛ پس خداوند پیامبران را نویدآور و بیم‌دهنده برانگیخت، و با آنان، کتاب [خود] را بحق فرو فرستاد، تا میان مردم در آنچه با هم اختلاف داشتند داوری کند. و جز کسانی که [کتاب‌] به آنان داده شد -پس از آنکه دلایل روشن برای آنان آمد- به خاطر ستم [و حسدی‌] که میانشان بود، [هیچ کس‌] در آن اختلاف نکرد. پس خداوند آنان را که ایمان آورده بودند، به توفیق خویش، به حقیقت آنچه که در آن اختلاف داشتند، هدایت کرد. و خدا هر که را بخواهد به راه راست هدایت می‌کند.'),
(221, 2, 214, 'آیا پنداشتید که داخل بهشت می‌شوید و حال آنکه هنوز مانند آنچه بر [سر] پیشینیان شما آمد، بر [سر] شما نیامده است؟ آنان دچار سختی و زیان شدند و به [هول و] تکان درآمدند، تا جایی که پیامبر [خدا ]و کسانی که با وی ایمان آورده بودند گفتند: «پیروزی خدا کی خواهد بود؟» هش دار، که پیروزی خدا نزدیک است.'),
(222, 2, 215, 'از تو می‌پرسند: «چه چیزی انفاق کنند [و به چه کسی بدهند]؟» بگو: «هر مالی انفاق کنید، به پدر و مادر و نزدیکان و یتیمان و مسکینان و به در راه‌مانده تعلّق دارد، و هر گونه نیکی کنید البته خدا به آن داناست.»'),
(223, 2, 216, 'بر شما کارزار واجب شده است، در حالی که برای شما ناگوار است. و بسا چیزی را خوش نمی‌دارید و آن برای شما خوب است، و بسا چیزی را دوست می‌دارید و آن برای شما بد است، و خدا می‌داند و شما نمی‌دانید.'),
(224, 2, 217, 'از تو در باره ماهی که کارزار در آن حرام است می‌پرسند. بگو: «کارزار در آن، گناهی بزرگ و باز داشتن از راه خدا و کفر ورزیدن به او و باز داشتن از مسجدالحرام [=حج‌]، و بیرون راندن اهل آن از آنجا، نزد خدا [گناهی‌] بزرگتر، و فتنه [=شرک‌] از کشتار بزرگتر است.» و آنان پیوسته با شما می‌جنگند تا -اگر بتوانند- شما را از دینتان برگردانند. و کسانی از شما که از دین خود برگردند و در حال کفر بمیرند، آنان کردارهایشان در دنیا و آخرت تباه می‌شود، و ایشان اهل آتشند و در آن ماندگار خواهند بود.'),
(225, 2, 218, 'آنان که ایمان آورده، و کسانی که هجرت کرده و راه خدا جهاد نموده‌اند، آنان به رحمت خدا امیدوارند، خداوند آمرزنده مهربان است.'),
(226, 2, 219, 'در باره شراب و قمار، از تو می‌پرسند، بگو: «در آن دو، گناهی بزرک، و سودهایی برای مردم است، و[لی‌] گناهشان از سودشان بزرگتر است.» و از تو می‌پرسند: «چه چیزی انفاق کنند؟» بگو: «مازاد [بر نیازمندی خود] را.» این گونه، خداوند آیات [خود را] برای شما روشن می‌گرداند، باشد که در [کار] دنیا و آخرت بیندیشید.'),
(227, 2, 220, 'و در باره یتیمان از تو می‌پرسند، بگو: «به صلاح آنان کار کردن بهتر است، و اگر با آنان همزیستی کنید، برادران [دینی‌] شما هستند. و خدا تباهکار را از درستکار بازمی‌شناسد.» و اگر خدا می‌خواست [در این باره‌] شما را به دشواری می‌انداخت. آری، خداوند توانا و حکیم است.'),
(228, 2, 221, 'و با زنان مشرک ازدواج مکنید، تا ایمان بیاورند. قطعاً کنیز با ایمان بهتر از زن مشرک است، هر چند [زیبایی‌] او شما را به شگفت آورد. و به مردان مشرک زن مدهید تا ایمان بیاورند. قطعاً برده با ایمان بهتر از مرد آزاد مشرک است، هر چند شما را به شگفت آورد. آنان [شما را] به سوی آتش فرا می‌خوانند، و خدا به فرمان خود، [شما را] به سوی بهشت و آمرزش می‌خواند، و آیات خود را برای مردم روشن می‌گرداند، باشد که متذکر شوند.'),
(229, 2, 222, 'از تو در باره عادت ماهانه [زنان‌] می‌پرسند، بگو: «آن، رنجی است. پس هنگام عادت ماهانه، از [آمیزش با] زنان کناره گیری کنید، و به آنان نزدیک نشوید تا پاک شوند. پس چون پاک شدند، از همان جا که خدا به شما فرمان داده است، با آنان آمیزش کنید.» خداوند توبه‌کاران و پاکیزگان را دوست می‌دارد.'),
(230, 2, 223, 'زنان شما کشتزار شما هستند. پس، از هر جا [و هر گونه‌] که خواهید به کشتزار خود [در]آیید، و آنها را برای خودتان مقدم دارید، و از خدا پروا کنید و بدانید که او را دیدار خواهید کرد، و مؤمنان را [به این دیدار] مژده ده.'),
(231, 2, 224, 'و خدا را دستاویز سوگندهای خود قرار مدهید، تا [بدین بهانه‌] از نیکوکاری و پرهیزگاری و سازش‌دادن میان مردم [باز ایستید]، و خدا شنوای داناست.'),
(232, 2, 225, 'خداوند شما را به سوگندهای لغوتان مؤاخذه نمی‌کند، ولی شما را بدانچه دلهایتان [از روی عمد] فراهم آورده است، مؤاخذه می‌کند، و خدا آمرزنده بردبار است.'),
(233, 2, 226, 'برای کسانی که به ترک همخوابگی با زنان خود، سوگند می‌خورند [=ایلاء]، چهار ماه انتظار [و مهلت‌] است، پس اگر [به آشتی‌] بازآمدند، خداوند آمرزنده مهربان است.'),
(234, 2, 227, 'و اگر آهنگ طلاق کردند، در حقیقت خدا شنوای داناست.'),
(235, 2, 228, 'و زنان طلاق داده شده، باید مدت سه پاکی انتظار کشند، و اگر به خدا و روز بازپسین ایمان دارند، برای آنان روا نیست که آنچه را خداوند در رحم آنان آفریده، پوشیده دارند؛ و شوهرانشان اگر سر آشتی دارند، به بازآوردن آنان در این [مدّت‌] سزاوارترند. و مانند همان [وظایفی‌] که بر عهده زنان است، به طور شایسته، به نفع آنان [بر عهده مردان‌] است، و مردان بر آنان درجه برتری دارند، و خداوند توانا و حکیم است.'),
(236, 2, 229, 'طلاق [رجعی‌] دو بار است. پس از آن یا [باید زن را] بخوبی نگاه داشتن، یا بشایستگی آزاد کردن. و برای شما روا نیست که از آنچه به آنان داده‌اید، چیزی بازستانید. مگر آنکه [طرفین‌] در به پا داشتن حدود خدا بیمناک باشند. پس اگر بیم دارید که آن دو، حدود خدا را برپای نمی‌دارند، در آنچه که [زن برای آزاد کردن خود] فدیه دهد، گناهی بر ایشان نیست. این است حدود احکام الهی؛ پس، از آن تجاوز مکنید. و کسانی که از حدود احکام الهی تجاوز کنند، آنان همان ستمکارانند.'),
(237, 2, 230, 'و اگر [شوهر برای بار سوم‌] او را طلاق گفت، پس از آن، دیگر [آن زن‌] برای او حلال نیست، تا اینکه با شوهری غیر از او ازدواج کند [و با او همخوابگی نماید]. پس اگر [شوهر دوم‌] وی را طلاق گفت، اگر آن دو [همسر سابق‌] پندارند که حدود خدا را برپا می‌دارند، گناهی بر آن دو نیست که به یکدیگر بازگردند. و اینها حدود احکام الهی است که آن را برای قومی که می‌دانند، بیان می‌کند.'),
(238, 2, 231, 'و چون آنان را طلاق گفتید، و به پایان عدّه خویش رسیدند، پس بخوبی نگاهشان دارید یا بخوبی آزادشان کنید. و[لی‌] آنان را برای [آزار و] زیان رساندن [به ایشان‌] نگاه مدارید تا [به حقوقشان‌] تعدّی کنید. و هر کس چنین کند، قطعاً بر خود ستم نموده است. و آیات خدا را به ریشخند مگیرید، و نعمت خدا را بر خود و آنچه را که از کتاب و حکمت بر شما نازل کرده و به [وسیله‌] آن به شما اندرز می‌دهد، به یاد آورید؛ و از خدا پروا داشته باشید، و بدانید که خدا به هر چیزی داناست.'),
(239, 2, 232, 'و چون زنان را طلاق گفتید، و عدّه خود را به پایان رساندند، آنان را از ازدواج با همسران [سابق‌] خود، چنانچه بخوبی با یکدیگر تراضی نمایند، جلوگیری مکنید. هر کس از شما به خدا و روز بازپسین ایمان دارد، به این [دستورها] پند داده می‌شود. [مراعات‌] این امر برای شما پربرکت‌تر و پاکیزه‌تر است، و خدا می‌داند و شما نمی‌دانید.'),
(240, 2, 233, 'و مادران [باید] فرزندان خود را دو سال تمام شیر دهند. [این حکم‌] برای کسی است که بخواهد دوران شیرخوارگی را تکمیل کند. و خوراک و پوشاک آنان [=مادران‌]، به طور شایسته، بر عهده پدر است. هیچ کس جز به قدر وسعش مکلف نمی‌شود. هیچ مادری نباید به سبب فرزندش زیان ببیند، و هیچ پدری [نیز] نباید به خاطر فرزندش [ضرر ببیند]. و مانند همین [احکام‌] بر عهده وارث [نیز] هست. پس اگر [پدر و مادر] بخواهند با رضایت و صوابدید یکدیگر، کودک را [زودتر] از شیر بازگیرند، گناهی بر آن دو نیست. و اگر خواستید برای فرزندان خود دایه بگیرید، بر شما گناهی نیست، به شرط آنکه چیزی را که پرداخت آن را به عهده گرفته‌اید، به طور شایسته بپردازید. و از خدا پروا کنید و بدانید که خداوند به آنچه انجام می‌دهید بیناست.'),
(241, 2, 234, 'و کسانی از شما که می‌میرند و همسرانی بر جای می‌گذارند، [همسران‌] چهار ماه و ده روز انتظار می‌برند؛ پس هرگاه عدّه خود را به پایان رساندند، در آنچه آنان به نحو پسندیده درباره خود انجام دهند، گناهی بر شما نیست، و خداوند به آنچه انجام می‌دهید آگاه است.'),
(242, 2, 235, 'و در باره آنچه شما به طور سربسته، از آنان [در عدّه وفات‌] خواستگاری کرده، یا [آن را] در دل پوشیده داشته‌اید، بر شما گناهی نیست. خدا می‌دانست که [شما] به زودی به یاد آنان خواهید افتاد، ولی با آنان قول و قرار پنهانی مگذارید، مگر آنکه سخنی پسندیده بگویید. و به عقد زناشویی تصمیم مگیرید، تا زمان مقرر به سرآید، و بدانید که خداوند آنچه را در دل دارید می‌داند. پس، از [مخالفت‌] او بترسید، و بدانید که خداوند آمرزنده و بردبار است.'),
(243, 2, 236, 'اگر زنان را، مادامی که با آنان نزدیکی نکرده و بر ایشان مَهری [نیز] معین نکرده‌اید، طلاق گویید، بر شما گناهی نیست، و آنان را به طور پسندیده، به نوعی بهره‌مند کنید -توانگر به اندازه [توان‌] خود، و تنگدست به اندازه [وسع‌] خود. [این کاری است‌] شایسته نیکوکاران.'),
(244, 2, 237, 'و اگر پیش از آنکه با آنان نزدیکی کنید، طلاقشان گفتید، در حالی که برای آنان مَهری معین کرده‌اید، پس نصف آنچه را تعیین نموده‌اید [به آنان بدهید]، مگر اینکه آنان خود ببخشند، یا کسی که پیوند نکاح به دست اوست ببخشد؛ و گذشت کردنِ شما به تقوا نزدیکتر است. و در میان یکدیگر بزرگواری را فراموش مکنید، زیرا خداوند به آنچه انجام می‌دهید بیناست.'),
(245, 2, 238, 'بر نمازها و نماز میانه مواظبت کنید، و خاضعانه برای خدا به پا خیزید.'),
(246, 2, 239, 'پس اگر بیم داشتید، پیاده یا سواره [نماز کنید]؛ و چون ایمن شدید، خدا را یاد کنید که آنچه نمی‌دانستید به شما آموخت.'),
(247, 2, 240, 'و کسانی از شما که مرگشان فرا می‌رسد، و همسرانی بر جای می‌گذارند، [باید] برای همسران خویش وصیّت کنند که آنان را تا یک سال بهره‌مند سازند و [از خانه شوهر] بیرون نکنند. پس اگر بیرون بروند، در آنچه آنان به طور پسندیده در باره خود انجام دهند، گناهی بر شما نیست. و خداوند توانا و حکیم است.'),
(248, 2, 241, 'و فرض است بر مردان پرهیزگار که زنان طلاق داده شده را بشایستگی چیزی دهند.'),
(249, 2, 242, 'بدین گونه، خداوند آیات خود را برای شما بیان می‌کند، باشد که بیندیشید.'),
(250, 2, 243, 'آیا از [حال‌] کسانی که از بیم مرگ از خانه‌های خود خارج شدند، و هزاران تن بودند، خبر نیافتی؟ پس خداوند به آنان گفت: «تن به مرگ بسپارید» آنگاه آنان را زنده ساخت. آری، خداوند نسبت به مردم، صاحب بخشش است، ولی بیشتر مردم سپاسگزاری نمی‌کنند.'),
(251, 2, 244, 'و در راه خدا کارزار کنید، و بدانید که خداوند شنوای داناست.'),
(252, 2, 245, 'کیست آن کس که به [بندگان‌] خدا وام نیکویی دهد تا [خدا] آن را برای او چند برابر بیفزاید؟ و خداست که [در معیشت بندگان‌] تنگی و گشایش پدید می‌آورد؛ و به سوی او بازگردانده می‌شوید.'),
(253, 2, 246, 'آیا از [حال‌] سران بنی اسرائیل پس از موسی خبر نیافتی آنگاه که به پیامبری از خود گفتند: «پادشاهی برای ما بگمار تا در راه خدا پیکار کنیم»، [آن پیامبر] گفت: «اگر جنگیدن بر شما مقرر گردد، چه بسا پیکار نکنید.» گفتند: «چرا در راه خدا نجنگیم با آنکه ما از دیارمان و از [نزد] فرزندانمان بیرون رانده شده‌ایم.» پس هنگامی که جنگ بر آنان مقرر شد، جز شماری اندک از آنان، [همگی‌] پشت کردند، و خداوند به [حال‌] ستمکاران داناست.'),
(254, 2, 247, 'و پیامبرشان به آنان گفت: «در حقیقت، خداوند، طالوت را بر شما به پادشاهی گماشته است.» گفتند: «چگونه او را بر ما پادشاهی باشد با آنکه ما به پادشاهی از وی سزاوارتریم و به او از حیث مال، گشایشی داده نشده است؟» پیامبرشان گفت: «در حقیقت، خدا او را بر شما برتری داده، و او را در دانش و [نیروی‌] بدنی بر شما برتری بخشیده است، و خداوند پادشاهی خود را به هر کس که بخواهد می‌دهد، و خدا گشایشگر داناست.»'),
(255, 2, 248, 'و پیامبرشان بدیشان گفت: «در حقیقت، نشانه پادشاهی او این است که آن صندوق [عهد] که در آن آرامش خاطری از جانب پروردگارتان، و بازمانده‌ای از آنچه خاندان موسی و خاندان هارون [در آن‌] بر جای نهاده‌اند -در حالی که فرشتگان آن را حمل می‌کنند- به سوی شما خواهد آمد. مسلما اگر مؤمن باشید، برای شما در این [رویداد] نشانه‌ای است.»'),
(256, 2, 249, 'و چون طالوت با لشکریان [خود] بیرون شد، گفت: «خداوند شما را به وسیله رودخانه‌ای خواهد آزمود. پس هر کس از آن بنوشد از [پیروان‌] من نیست، و هر کس از آن نخورد، قطعاً او از [پیروان‌] من است، مگر کسی که با دستش کفی برگیرد. پس [همگی‌] جز اندکی از آنها، از آن نوشیدند. و هنگامی که [طالوت ] با کسانی که همراه وی ایمان آورده بودند، از آن [نهر] گذشتند، گفتند:» امروز ما را یارای [مقابله با ]جالوت و سپاهیانش نیست. «کسانی که به دیدار خداوند یقین داشتند، گفتند:» بسا گروهی اندک که بر گروهی بسیار، به اذن خدا پیروز شدند، و خداوند با شکیبایان است.'),
(257, 2, 250, 'و هنگامی که با جالوت و سپاهیانش روبرو شدند، گفتند: «پروردگارا، بر [دلهای‌] ما شکیبایی فرو ریز، و گامهای ما را استوار دار، و ما را بر گروه کافران پیروز فرمای.»'),
(258, 2, 251, 'پس آنان را به اذن خدا شکست دادند، و داوود، جالوت را کشت، و خداوند به او پادشاهی و حکمت ارزانی داشت، و از آنچه می‌خواست به او آموخت. و اگر خداوند برخی از مردم را به وسیله برخی دیگر دفع نمی‌کرد، قطعاً زمین تباه می‌گردید. ولی خداوند نسبت به جهانیان تفضّل دارد.'),
(259, 2, 252, 'این‌[ها] آیات خداست که ما آن را بحق بر تو می‌خوانیم، و به راستی تو از جمله پیامبرانی.'),
(260, 2, 253, 'برخی از آن پیامبران را بر برخی دیگر برتری بخشیدیم. از آنان کسی بود که خدا با او سخن گفت و درجات بعضی از آنان را بالا برد؛ و به عیسی پسر مریم دلایل آشکار دادیم، و او را به وسیله روح القدس تأیید کردیم؛ و اگر خدا می‌خواست، کسانی که پس از آنان بودند، بعد از آن [همه‌] دلایل روشن که برایشان آمد، به کشتار یکدیگر نمی‌پرداختند، ولی با هم اختلاف کردند؛ پس، بعضی از آنان کسانی بودند که ایمان آوردند، و بعضی از آنان کسانی بودند که کفر ورزیدند؛ و اگر خدا می‌خواست با یکدیگر جنگ نمی‌کردند، ولی خداوند آنچه را می‌خواهد انجام می‌دهد.'),
(261, 2, 254, 'ای کسانی که ایمان آورده‌اید، از آنچه به شما روزی داده‌ایم انفاق کنید، پیش از آنکه روزی فرا رسد که در آن نه داد و ستدی است و نه دوستی و نه شفاعتی. و کافران خود ستمکارانند.'),
(262, 2, 255, 'خداست که معبودی جز او نیست؛ زنده و برپادارنده است؛ نه خوابی سبک او را فرو می‌گیرد و نه خوابی گران؛ آنچه در آسمانها و آنچه در زمین است، از آنِ اوست. کیست آن کس که جز به اذن او در پیشگاهش شفاعت کند؟ آنچه در پیش روی آنان و آنچه در پشت سرشان است می‌داند. و به چیزی از علم او، جز به آنچه بخواهد، احاطه نمی‌یابند. کرسی او آسمانها و زمین را در بر گرفته، و نگهداری آنها بر او دشوار نیست، و اوست والای بزرگ.'),
(263, 2, 256, 'در دین هیچ اجباری نیست. و راه از بیراهه بخوبی آشکار شده است. پس هر کس به طاغوت کفر ورزد، و به خدا ایمان آورد، به یقین، به دستاویزی استوار، که آن را گسستن نیست، چنگ زده است. و خداوند شنوای داناست.'),
(264, 2, 257, 'خداوند سرور کسانی است که ایمان آورده‌اند. آنان را از تاریکیها به سوی روشنایی به در می‌برد. و[لی‌] کسانی که کفر ورزیده‌اند، سرورانشان [همان عصیانگران=] طاغوتند، که آنان را از روشنایی به سوی تاریکیها به در می‌برند. آنان اهل آتشند که خود، در آن جاودانند.'),
(265, 2, 258, 'آیا از [حال‌] آن کس که چون خدا به او پادشاهی داده بود [و بدان می‌نازید، و] در باره پروردگار خود با ابراهیم محاجّه [می‌]کرد، خبر نیافتی؟ آنکاه که ابراهیم گفت: «پروردگار من همان کسی است که زنده می‌کند و می‌میراند.» گفت: «من [هم‌] زنده می‌کنم و [هم‌] می‌میرانم.» ابراهیم گفت: «خدا[ی من‌] خورشید را از خاور برمی‌آورد، تو آن را از باختر برآور.» پس آن کس که کفر ورزیده بود مبهوت ماند. و خداوند قوم ستمکار را هدایت نمی‌کند.'),
(266, 2, 259, 'یا چون آن کس که به شهری که بامهایش یکسر فرو ریخته بود، عبور کرد؛ [و با خود می‌]گفت: «چگونه خداوند، [اهل‌] این [ویرانکده‌] را پس از مرگشان زنده می‌کند؟». پس خداوند، او را [به مدت‌] صد سال میراند. آنگاه او را برانگیخت، [و به او] گفت: «چقدر درنگ کردی؟» گفت: «یک روز یا پاره‌ای از روز را درنگ کردم.» گفت: «[نه‌] بلکه صد سال درنگ کردی، به خوراک و نوشیدنی خود بنگر [که طعم و رنگ آن‌] تغییر نکرده است، و به درازگوش خود نگاه کن [که چگونه متلاشی شده است. این ماجرا برای آن است که هم به تو پاسخ گوییم‌] و هم تو را [در مورد معاد] نشانه‌ای برای مردم قرار دهیم. و به [این ]استخوانها بنگر، چگونه آنها را برداشته به هم پیوند می‌دهیم؛ سپس گوشت بر آن می‌پوشانیم.» پس هنگامی که [چگونگی زنده ساختن مرده‌] برای او آشکار شد، گفت: «[اکنون‌] می‌دانم که خداوند بر هر چیزی تواناست.»'),
(267, 2, 260, 'و [یاد کن‌] آنگاه که ابراهیم گفت: «پروردگارا، به من نشان ده؛ چگونه مردگان را زنده می‌کنی؟» فرمود: «مگر ایمان نیاورده‌ای؟» گفت: «چرا، ولی تا دلم آرامش یابد.» فرمود: «پس، چهار پرنده برگیر، و آنها را پیش خود، ریز ریز گردان؛ سپس بر هر کوهی پاره‌ای از آنها را قرار ده؛ آنگاه آنها را فرا خوان، شتابان به سوی تو می‌آیند، و بدان که خداوند توانا و حکیم است.»'),
(268, 2, 261, 'مَثَل [صدقات‌] کسانی که اموال خود را در راه خدا انفاق می‌کنند همانند دانه‌ای است که هفت خوشه برویاند که در هر خوشه‌ای صد دانه باشد؛ و خداوند برای هر کس که بخواهد [آن را] چند برابر می‌کند، و خداوند گشایشگر داناست.'),
(269, 2, 262, 'کسانی که اموال خود را در راه خدا انفاق می‌کنند، سپس در پی آنچه انفاق کرده‌اند، منّت و آزاری روا نمی‌دارند، پاداش آنان برایشان نزد پروردگارشان [محفوظ] است، و بیمی بر آنان نیست و اندوهگین نمی‌شوند.'),
(270, 2, 263, 'گفتاری پسندیده [در برابر نیازمندان‌] و گذشت [از اصرار و تندیِ آنان‌] بهتر از صدقه‌ای است که آزاری به دنبال آن باشد، و خداوند بی‌نیاز بردبار است.'),
(271, 2, 264, 'ای کسانی که ایمان آورده‌اید، صدقه‌های خود را با منّت و آزار، باطل مکنید، مانند کسی که مالش را برای خودنمایی به مردم، انفاق می‌کند و به خدا و روز بازپسین ایمان ندارد. پس مَثَل او همچون مَثَل سنگ خارایی است که بر روی آن، خاکی [نشسته‌] است، و رگباری به آن رسیده و آن [سنگ‌] را سخت و صاف بر جای نهاده است. آنان [=ریاکاران‌] نیز از آنچه به دست آورده‌اند، بهره‌ای نمی‌برند؛ و خداوند، گروه کافران را هدایت نمی‌کند.'),
(272, 2, 265, 'و مَثَل [صدقات‌] کسانی که اموال خویش را برای طلب خشنودی خدا و استواری روحشان انفاق می‌کنند، همچون مَثَل باغی است که بر فراز پشته‌ای قرار دارد [که اگر] رگباری بر آن برسد، دو چندان محصول برآورد، و اگر رگباری هم بر آن نرسد، بارانِ ریزی [برای آن بس است‌]، و خداوند به آنچه انجام می‌دهید بیناست.'),
(273, 2, 266, 'آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد که از زیر آنها نهرها روان است، و برای او در آن [باغ‌] از هر گونه میوه‌ای [فراهم‌] باشد، و در حالی که او را پیری رسیده و فرزندانی خردسال دارد، [ناگهان‌] گردبادی آتشین بر آن [باغ‌] زند و [باغ یکسر] بسوزد؟ این گونه، خداوند آیات [خود] را برای شما روشن می‌گرداند، باشد که شما بیندیشید.'),
(274, 2, 267, 'ای کسانی که ایمان آورده‌اید، از چیزهای پاکیزه‌ای که به دست آورده‌اید، و از آنچه برای شما از زمین برآورده‌ایم، انفاق کنید، و در پی ناپاک آن نروید که [از آن‌] انفاق نمایید، در حالی که آن را [اگر به خودتان می‌دادند] جز با چشم‌پوشی [و بی‌میلی‌] نسبت به آن، نمی‌گرفتید، و بدانید که خداوند، بی‌نیازِ ستوده [صفات‌] است.'),
(275, 2, 268, 'شیطان شما را از تهیدستی بیم می‌دهد و شما را به زشتی وامی‌دارد؛ و[لی‌] خداوند از جانب خود به شما وعده آمرزش و بخشش می‌دهد، و خداوند گشایشگر داناست.'),
(276, 2, 269, '[خدا] به هر کس که بخواهد حکمت می‌بخشد، و به هر کس حکمت داده شود، به یقین، خیری فراوان داده شده است؛ و جز خردمندان، کسی پند نمی‌گیرد.'),
(277, 2, 270, 'و هر نفقه‌ای را که انفاق، یا هر نذری را که عهد کرده‌اید، قطعاً خداوند آن را می‌داند، و برای ستمکاران هیچ یاوری نیست.'),
(278, 2, 271, 'اگر صدقه‌ها را آشکار کنید، این، کار خوبی است، و اگر آن را پنهان دارید و به مستمندان بدهید، این برای شما بهتر است؛ و بخشی از گناهانتان را می‌زداید، و خداوند به آنچه انجام می‌دهید آگاه است.'),
(279, 2, 272, 'هدایت آنان بر عهده تو نیست، بلکه خدا هر که را بخواهد هدایت می‌کند، و هر مالی که انفاق کنید، به سود خود شماست، و[لی‌] جز برای طلب خشنودی خدا انفاق مکنید، و هر مالی را که انفاق کنید [پاداش آن‌] به طور کامل به شما داده خواهد شد و ستمی بر شما نخواهد رفت.'),
(280, 2, 273, '[این صدقات‌] برای آن [دسته از] نیازمندانی است که در راه خدا فرومانده‌اند، و نمی‌توانند [برای تأمین هزینه زندگی‌] در زمین سفر کنند. از شدّت خویشتن‌داری، فرد بی‌اطلاع، آنان را توانگر می‌پندارد. آنها را از سیمایشان می‌شناسی. با اصرار، [چیزی‌] از مردم نمی‌خواهند. و هر مالی [به آنان‌] انفاق کنید، قطعاً خدا از آن آگاه است.'),
(281, 2, 274, 'کسانی که اموال خود را شب و روز، و نهان و آشکارا، انفاق می‌کنند، پاداش آنان نزد پروردگارشان برای آنان خواهد بود؛ و نه بیمی بر آنان است و نه اندوهگین می‌شوند.'),
(282, 2, 275, 'کسانی که ربا می‌خورند، [از گور] برنمی‌خیزند مگر مانند برخاستن کسی که شیطان بر اثر تماس، آشفته‌سرش کرده است. این بدان سبب است که آنان گفتند: «داد و ستد صرفاً مانند رباست.» و حال آنکه خدا داد و ستد را حلال، و ربا را حرام گردانیده است. پس، هر کس، اندرزی از جانب پروردگارش بدو رسید، و [از رباخواری‌] باز ایستاد، آنچه گذشته، از آنِ اوست، و کارش به خدا واگذار می‌شود، و کسانی که [به رباخواری‌] باز گردند، آنان اهل آتشند و در آن ماندگار خواهند بود.'),
(283, 2, 276, 'خدا از [برکت‌] ربا می‌کاهد، و بر صدقات می‌افزاید، و خداوند هیچ ناسپاس گناهکاری را دوست نمی‌دارد.'),
(284, 2, 277, 'کسانی که ایمان آورده و کارهای شایسته کرده و نماز بر پا داشته و زکات داده‌اند، پاداش آنان نزد پروردگارشان برای آنان خواهد بود؛ و نه بیمی بر آنان است و نه اندوهگین می‌شوند.'),
(285, 2, 278, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید؛ و اگر مؤمنید، آنچه از ربا باقی مانده است واگذارید.'),
(286, 2, 279, 'و اگر [چنین‌] نکردید، بدانید به جنگ با خدا و فرستاده وی، برخاسته‌اید؛ و اگر توبه کنید، سرمایه‌های شما از خودتان است. نه ستم می‌کنید و نه ستم می‌بینید.'),
(287, 2, 280, 'و اگر [بدهکارتان‌] تنگدست باشد، پس تا [هنگام‌] گشایش، مهلتی [به او دهید]؛ و [اگر به راستی قدرت پرداخت ندارد،] بخشیدن آن برای شما بهتر است-اگر بدانید.'),
(288, 2, 281, 'و بترسید از روزی که در آن، به سوی خدا بازگردانده می‌شوید، سپس به هر کسی [پاداش‌] آنچه به دست آورده، تمام داده شود؛ و آنان مورد ستم قرار نمی‌گیرند.'),
(289, 2, 282, 'ای کسانی که ایمان آورده‌اید، هر گاه به وامی تا سررسیدی معین، با یکدیگر معامله کردید، آن را بنویسید. و باید نویسنده‌ای [صورت معامله را] بر اساس عدالت، میان شما بنویسد. و هیچ نویسنده‌ای نباید از نوشتن خودداری کند؛ همان گونه [و به شکرانه آن‌] که خدا او را آموزش داده است. و کسی که بدهکار است باید املا کند، و او [=نویسنده‌] بنویسد. و از خدا که پروردگار اوست پروا نماید، و از آن، چیزی نکاهد. پس اگر کسی که حق بر ذمه اوست، سفیه یا ناتوان است، یا خود نمی‌تواند املا کند، پس ولیّ او باید با [رعایت‌] عدالت، املا نماید. و دو شاهد از مردانتان را به شهادت طلبید، پس اگر دو مرد نبودند، مردی را با دو زن، از میان گواهانی که [به عدالت آنان‌] رضایت دارید [گواه بگیرید]، تا [اگر] یکی از آن دو [زن‌] فراموش کرد، [زنِ‌] دیگر، وی را یادآوری کند. و چون گواهان احضار شوند، نباید خودداری ورزند. و از نوشتن [بدهی‌] چه خرد باشد یا بزرگ، ملول نشوید، تا سررسیدش [فرا رسد]. این [نوشتنِ‌] شما، نزد خدا عادلانه‌تر، و برای شهادت استوارتر، و برای اینکه دچار شک نشوید [به احتیاط ]نزدیکتر است، مگر آنکه داد و ستدی نقدی باشد که آن را میان خود [دست به دست‌] برگزار می‌کنید؛ در این صورت، بر شما گناهی نیست که آن را ننویسید. و [در هر حال‌] هر گاه داد و ستد کردید گواه بگیرید. و هیچ نویسنده و گواهی نباید زیان ببیند، و اگر چنین کنید، از نافرمانی شما خواهد بود. و از خدا پروا کنید، و خدا [بدین گونه‌] به شما آموزش می‌دهد، و خدا به هر چیزی داناست.'),
(290, 2, 283, 'و اگر در سفر بودید و نویسنده‌ای نیافتید وثیقه‌ای بگیرید؛ و اگر برخی از شما برخی دیگر را امین دانست، پس آن کس که امین شمرده شده، باید سپرده وی را بازپس دهد؛ و باید از خداوند که پروردگار اوست، پروا کند. و شهادت را کتمان مکنید، و هر که آن را کتمان کند قلبش گناهکار است، و خداوند به آنچه انجام می‌دهید داناست.'),
(291, 2, 284, 'آنچه در آسمانها و آنچه در زمین است از آنِ خداست. و اگر آنچه در دلهای خود دارید، آشکار یا پنهان کنید، خداوند شما را به آن محاسبه می‌کند؛ آنگاه هر که را بخواهد می‌بخشد، و هر که را بخواهد عذاب می‌کند، و خداوند بر هر چیزی تواناست.'),
(292, 2, 285, 'پیامبر [خدا] بدانچه از جانب پروردگارش بر او نازل شده است ایمان آورده است، و مؤمنان همگی به خدا و فرشتگان و کتابها و فرستادگانش ایمان آورده‌اند [و گفتند:] «میان هیچ یک از فرستادگانش فرق نمی‌گذاریم» و گفتند: «شنیدیم و گردن نهادیم، پروردگارا، آمرزش تو را [خواستاریم‌] و فرجام به سوی تو است.»'),
(293, 2, 286, 'خداوند هیچ کس را جز به قدر توانایی‌اش تکلیف نمی‌کند. آنچه [از خوبی‌] به دست آورده به سود او، و آنچه [از بدی‌] به دست آورده به زیان اوست. پروردگارا، اگر فراموش کردیم یا به خطا رفتیم بر ما مگیر، پروردگارا، هیچ بار گرانی بر [دوش‌] ما مگذار؛ همچنانکه بر [دوش‌] کسانی که پیش از ما بودند نهادی. پروردگارا، و آنچه تاب آن نداریم بر ما تحمیل مکن؛ و از ما درگذر؛ و ما را ببخشای و بر ما رحمت آور؛ سرور ما تویی؛ پس ما را بر گروه کافران پیروز کن.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف، لام، میم.'),
(295, 3, 2, 'خداست که هیچ معبودِ [بحقی‌] جز او نیست و زنده [پاینده‌] است.'),
(296, 3, 3, 'این کتاب را در حالی که مؤیّد آنچه [از کتابهای آسمانی‌] پیش از خود می‌باشد، به حق [و به تدریج‌] بر تو نازل کرد، و تورات و انجیل را...'),
(297, 3, 4, 'پیش از آن برای رهنمود مردم فرو فرستاد، و فرقان [=جداکننده حق از باطل‌] را نازل کرد. کسانی که به آیات خدا کفر ورزیدند، بی‌تردید عذابی سخت خواهند داشت، و خداوند، شکست‌ناپذیر و صاحب‌انتقام است.'),
(298, 3, 5, 'در حقیقت، هیچ چیز [نه‌] در زمین و نه در آسمان بر خدا پوشیده نمی‌ماند.'),
(299, 3, 6, 'اوست کسی که شما را آن گونه که می‌خواهد در رحمها صورتگری می‌کند. هیچ معبودی جز آن توانای حکیم نیست.'),
(300, 3, 7, 'اوست کسی که این کتاب [=قرآن‌] را بر تو فرو فرستاد. پاره‌ای از آن، آیات محکم [=صریح و روشن‌] است. آنها اساس کتابند؛ و [پاره‌ای‌] دیگر متشابهاتند [که تأویل‌پذیرند]. اما کسانی که در دلهایشان انحراف است برای فتنه‌جویی و طلب تأویل آن [به دلخواه خود،] از متشابه آن پیروی می‌کنند، با آنکه تأویلش را جز خدا و ریشه‌داران در دانش کسی نمی‌داند. [آنان که‌] می‌گویند: «ما بدان ایمان آوردیم، همه [چه محکم و چه متشابه‌] از جانب پروردگار ماست»، و جز خردمندان کسی متذکر نمی‌شود.'),
(301, 3, 8, '[می‌گویند:] پروردگارا، پس از آنکه ما را هدایت کردی، دلهایمان را دستخوش انحراف مگردان، و از جانب خود، رحمتی بر ما ارزانی دار که تو خود بخشایشگری.'),
(302, 3, 9, 'پروردگارا، به یقین، تو در روزی که هیچ تردیدی در آن نیست، گردآورنده [جمله‌] مردمانی. قطعاً خداوند در وعده [خود] خلاف نمی‌کند.'),
(303, 3, 10, 'در حقیقت، کسانی که کفر ورزیدند، اموال و اولادشان چیزی [از عذاب خدا] را از آنان دور نخواهد کرد؛ و آنان خود، هیزم دوزخند.'),
(304, 3, 11, '[آنان‌] به شیوه فرعونیان و کسانی که پیش از آنان بودند آیات ما را دروغ شمردند؛ پس خداوند به [سزای‌] گناهانشان [گریبان‌] آنان را گرفت، و خدا سخت‌کیفر است.'),
(305, 3, 12, 'به کسانی که کفر ورزیدند بگو: «به زودی مغلوب خواهید شد و [سپس در روز رستاخیز] در دوزخ محشور می‌شوید، و چه بد بستری است.»'),
(306, 3, 13, 'قطعاً در برخورد میان دو گروه، برای شما نشانه‌ای [و درس عبرتی‌] بود. گروهی در راه خدا می‌جنگیدند، و دیگر [گروه‌] کافر بودند که آنان [=مؤمنان‌] را به چشم، دو برابر خود می‌دیدند؛ و خدا هر که را بخواهد به یاری خود تأیید می‌کند، یقیناً در این [ماجرا] برای صاحبان بینش عبرتی است.'),
(307, 3, 14, 'دوستیِ خواستنیها[ی گوناگون‌] از: زنان و پسران و اموال فراوان از زر و سیم و اسب‌های نشاندار و دامها و کشتزار[ها] برای مردم آراسته شده، [لیکن‌] این جمله، مایه تمتّع زندگی دنیاست، و [حال آنکه] فرجام نیکو نزد خداست.'),
(308, 3, 15, 'بگو: «آیا شما را به بهتر از اینها خبر دهم؟ برای کسانی که تقوا پیشه کرده‌اند، نزد پروردگارشان باغهایی است که از زیر [درختان‌] آنها نهرها روان است؛ در آن جاودانه بمانند، و همسرانی پاکیزه و [نیز ]خشنودی خدا [را دارند]، و خداوند به [امور] بندگان [خود] بیناست.'),
(309, 3, 16, 'همان کسانی که می‌گویند: پروردگارا، ما ایمان آوردیم؛ پس گناهان ما را بر ما ببخش، و ما را از عذاب آتش نگاه دار.'),
(310, 3, 17, '[اینانند] شکیبایان و راستگویان و فرمانبرداران و انفاق‌کنندگان و آمرزش‌خواهان در سحرگاهان.'),
(311, 3, 18, 'خدا که همواره به عدل، قیام دارد، گواهی می‌دهد که جز او هیچ معبودی نیست؛ و فرشتگانِ [او] و دانشوران [نیز گواهی می‌دهند که:] جز او، که توانا و حکیم است، هیچ معبودی نیست.'),
(312, 3, 19, 'در حقیقت، دین نزد خدا همان اسلام است. و کسانی که کتاب [آسمانی‌] به آنان داده شده، با یکدیگر به اختلاف نپرداختند مگر پس از آنکه علم برای آنان [حاصل‌] آمد، آن هم به سابقه حسدی که میان آنان وجود داشت. و هر کس به آیات خدا کفر ورزد، پس [بداند] که خدا زودشمار است.'),
(313, 3, 20, 'پس اگر با تو به محاجّه برخاستند، بگو: «من خود را تسلیم خدا نموده‌ام، و هر که مرا پیروی کرده [نیز خود را تسلیم خدا نموده است‌].» و به کسانی که اهل کتابند و به مشرکان بگو: «آیا اسلام آورده‌اید؟» پس اگر اسلام آوردند، قطعاً هدایت یافته‌اند، و اگر روی برتافتند، فقط رساندن پیام بر عهده توست، و خداوند به [امور] بندگان بیناست.'),
(314, 3, 21, 'کسانی که به آیات خدا کفر می‌ورزند، و پیامبران را بناحق می‌کشند، و دادگستران را به قتل می‌رسانند، آنان را از عذابی دردناک خبر ده.'),
(315, 3, 22, 'آنان کسانی‌اند که در [این‌] دنیا و [در سرای‌] آخرت، اعمالشان به هدر رفته و برای آنان هیچ یاوری نیست.'),
(316, 3, 23, 'آیا داستان کسانی را که بهره‌ای از کتاب [تورات‌] یافته‌اند ندانسته‌ای که چون به سوی کتاب خدا فرا خوانده می‌شوند تا میانشان حکم کند، آنگه گروهی از آنان به حال اعراض، روی برمی‌تابند؟'),
(317, 3, 24, 'این بدان سبب بود که آنان [به پندار خود] گفتند: «هرگز آتش جز چند روزی به ما نخواهد رسید»، و برساخته‌هایشان آنان را در دینشان فریفته کرده است.'),
(318, 3, 25, 'پس چگونه خواهد بود [حالشان‌] آنگاه که آنان را در روزی که هیچ شکی در آن نیست گرد آوریم؛ و به هر کس [پاداش‌] دستاوردش به تمام [و کمال‌] داده شود و به آنان ستم نرسد؟'),
(319, 3, 26, 'بگو: «بار خدایا، تویی که فرمانفرمایی؛ هر آن کس را که خواهی، فرمانروایی بخشی؛ و از هر که خواهی، فرمانروایی را باز ستانی؛ و هر که را خواهی، عزت بخشی؛ و هر که را خواهی، خوار گردانی؛ همه خوبیها به دست توست، و تو بر هر چیز توانایی.»'),
(320, 3, 27, 'شب را به روز در می‌آوری، و روز را به شب در می‌آوری؛ و زنده را از مرده بیرون می آوری، و مرده را از زنده خارج می‌سازی؛ و هر که را خواهی، بی‌حساب روزی می‌دهی.'),
(321, 3, 28, 'مؤمنان نباید کافران را -به جای مؤمنان- به دوستی بگیرند؛ و هر که چنین کند، در هیچ چیز [او را] از [دوستی‌] خدا [بهره‌ای‌] نیست، مگر اینکه از آنان به نوعی تقیّه کند و خداوند، شما را از [عقوبت‌] خود می‌ترساند، و بازگشت [همه‌] به سوی خداست.'),
(322, 3, 29, 'بگو: «اگر آنچه در سینه‌های شماست نهان دارید یا آشکارش کنید، خدا آن را می داند، و [نیز] آنچه را در آسمانها و آنچه را در زمین است می‌داند، و خداوند بر هر چیزی تواناست.»'),
(323, 3, 30, 'روزی که هر کسی آنچه کار نیک به جای آورده و آنچه بدی مرتکب شده، حاضر شده می یابد؛ و آرزو می‌کند: کاش میان او و آن [کارهای بد] فاصله‌ای دور بود. و خداوند، شما را از [کیفر] خود می‌ترساند، و [در عین حال‌] خدا به بندگان [خود ]مهربان است.'),
(324, 3, 31, 'بگو: «اگر خدا را دوست دارید، از من پیروی کنید تا خدا دوستتان بدارد و گناهان شما را بر شما ببخشاید، و خداوند آمرزنده مهربان است.»'),
(325, 3, 32, 'بگو: «خدا و پیامبر [او] را اطاعت کنید.» پس اگر رویگردان شدند، قطعاً خداوند کافران را دوست ندارد.'),
(326, 3, 33, 'به یقین، خداوند، آدم و نوح و خاندان ابراهیم و خاندان عمران را بر مردم جهان برتری داده است.'),
(327, 3, 34, 'فرزندانی که بعضی از آنان از [نسل‌] بعضی دیگرند، و خداوند شنوای داناست.'),
(328, 3, 35, 'چون زن عمران گفت: «پروردگارا، آنچه در شکم خود دارم نذر تو کردم تا آزاد شده [از مشاغل دنیا و پرستشگر تو] باشد؛ پس، از من بپذیر که تو خود شنوای دانایی.»'),
(329, 3, 36, 'پس چون فرزندش را بزاد، گفت: «پروردگارا، من دختر زاده‌ام -و خدا به آنچه او زایید داناتر بود- و پسر چون دختر نیست، و من نامش را مریم نهادم، و او و فرزندانش را از شیطان رانده‌شده، به تو پناه می‌دهم.»'),
(330, 3, 37, 'پس پروردگارش وی [=مریم‌] را با حُسنِ قبول پذیرا شد و او را نیکو بار آورد، و زکریا را سرپرست وی قرار داد. زکریا هر بار که در محراب بر او وارد می‌شد، نزد او [نوعی‌] خوراکی می‌یافت. [می‌]گفت: «ای مریم، این از کجا برای تو [آمده است؟ او در پاسخ می‌]گفت: «این از جانب خداست، که خدا به هر کس بخواهد، بی شمار روزی می‌دهد.»'),
(331, 3, 38, 'آنجا [بود که‌] زکریا پروردگارش را خواند [و] گفت: «پروردگارا، از جانب خود، فرزندی پاک و پسندیده به من عطا کن، که تو شنونده دعایی.»'),
(332, 3, 39, 'پس در حالی که وی ایستاده [و] در محراب [خود] دعا می‌کرد، فرشتگان، او را ندا دردادند که: خداوند تو را به [ولادت‌] یحیی -که تصدیق کننده [حقانیت‌] کلمة الله [=عیسی‌] است، و بزرگوار و خویشتندار [=پرهیزنده از آنان‌] و پیامبری از شایستگان است- مژده می‌دهد.'),
(333, 3, 40, 'گفت: «پروردگارا، چگونه مرا فرزندی خواهد بود؟ در حالی که پیریِ من بالا گرفته است و زنم «نازا» است [فرشته‌] گفت: « [کار پروردگار] چنین است. خدا هر چه بخواهد می‌کند.»'),
(334, 3, 41, 'گفت: «پروردگارا، برای من نشانه‌ای قرار ده.» فرمود: «نشانه‌ات این است که سه روز با مردم، جز به اشاره سخن نگویی؛ و پروردگارت را بسیار یاد کن، و شبانگاه و بامدادان [او را] تسبیح گوی.»'),
(335, 3, 42, 'و [یاد کن‌] هنگامی را که فرشتگان گفتند: «ای مریم، خداوند تو را برگزیده و پاک ساخته و تو را بر زنان جهان برتری داده است.»'),
(336, 3, 43, '«ای مریم، فرمانبر پروردگار خود باش و سجده کن و با رکوع‌کنندگان رکوع نما.»'),
(337, 3, 44, 'این [جمله‌] از اخبار غیب است که به تو وحی می‌کنیم، و [گرنه‌] وقتی که آنان قلمهای خود را [برای قرعه‌کشی به آب‌] می‌افکندند تا کدام یک سرپرستی مریم را به عهده گیرد، نزد آنان نبودی؛ و [نیز] وقتی با یکدیگر کشمکش می‌کردند نزدشان نبودی.'),
(338, 3, 45, '[یاد کن‌] هنگامی [را] که فرشتگان گفتند: «ای مریم، خداوند تو را به کلمه‌ای از جانب خود، که نامش مسیح، عیسی‌بن‌مریم است مژده می‌دهد، در حالی که [او] در دنیا و آخرت آبرومند و از مقربان [درگاه خدا] است.»'),
(339, 3, 46, '«و در گهواره [به اعجاز] و در میانسالی [به وحی‌] با مردم سخن می‌گوید و از شایستگان است.»'),
(340, 3, 47, '[مریم‌] گفت: «پروردگارا، چگونه مرا فرزندی خواهد بود با آنکه بشری به من دست نزده است؟» گفت: «چنین است [کار] پروردگار.» خدا هر چه بخواهد می‌آفریند؛ چون به کاری فرمان دهد، فقط به آن می‌گوید: «باش»؛ پس می‌باشد.'),
(341, 3, 48, 'و به او کتاب و حکمت و تورات و انجیل می‌آموزد.'),
(342, 3, 49, 'و [او را به عنوان‌] پیامبری به سوی بنی اسرائیل [می‌فرستد، که او به آنان می‌گوید:] «در حقیقت، من از جانب پروردگارتان برایتان معجزه‌ای آورده‌ام: من از گل برای شما [چیزی‌] به شکل پرنده می‌سازم، آنگاه در آن می‌دمم، پس به اذن خدا پرنده‌ای می شود؛ و به اذن خدا نابینای مادرزاد و پیس را بهبود می‌بخشم؛ و مردگان را زنده می‌گردانم؛ و شما را از آنچه می‌خورید و در خانه هایتان ذخیره می‌کنید، خبر می‌دهم؛ مسلماً در این [معجزات‌]، برای شما -اگر مؤمن باشید- عبرت است.»'),
(343, 3, 50, 'و [می‌گوید: «آمده‌ام تا] تورات را که پیش از من [نازل شده‌] است تصدیق کننده باشم، و تا پاره‌ای از آنچه را که بر شما حرام گردیده، برای شما حلال کنم، و از جانب پروردگارتان برای شما نشانه‌ای آورده‌ام. پس، از خدا پروا دارید و مرا اطاعت کنید.»'),
(344, 3, 51, '«در حقیقت، خداوند پروردگار من و پروردگار شماست؛ پس او را بپرستید [که ] راه راست این است.»'),
(345, 3, 52, 'چون عیسی از آنان احساس کفر کرد، گفت: «یاران من در راه خدا چه کسانند؟» حواریون گفتند: «ما یاران [دین‌] خداییم، به خدا ایمان آورده‌ایم؛ و گواه باش که ما تسلیم [او] هستیم.»'),
(346, 3, 53, 'پروردگارا، به آنچه نازل کردی گرویدیم و فرستاده‌[ات‌] را پیروی کردیم؛ پس ما را در زمره گواهان بنویس.'),
(347, 3, 54, 'و [دشمنان‌] مکر ورزیدند، و خدا [در پاسخشان‌] مکر در میان آورد، و خداوند بهترین مکرانگیزان است.'),
(348, 3, 55, '[یاد کن‌] هنگامی را که خدا گفت: «ای عیسی، من تو را برگرفته و به سوی خویش بالا می‌برم، و تو را از [آلایش‌] کسانی که کفر ورزیده‌اند پاک می‌گردانم، و تا روز رستاخیز، کسانی را که از تو پیروی کرده‌اند، فوق کسانی که کافر شده‌اند قرار خواهم داد؛ آنگاه فرجام شما به سوی من است، پس در آنچه بر سر آن اختلاف می‌کردید میان شما داوری خواهم کرد.'),
(349, 3, 56, 'اما کسانی که کفر ورزیدند، در دنیا و آخرت به سختی عذابشان کنم و یاورانی نخواهند داشت.'),
(350, 3, 57, 'و اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند، [خداوند] مزدشان را به تمامی به آنان می‌دهد. و خداوند، بیدادگران را دوست نمی‌دارد.'),
(351, 3, 58, 'اینهاست که ما آن را از آیات و قرآن حکمت‌آمیز بر تو می‌خوانیم.'),
(352, 3, 59, 'در واقع، مَثل عیسی نزد خدا همچون مَثل [خلقت‌] آدم است [که‌] او را از خاک آفرید؛ سپس بدو گفت: «باش»؛ پس وجود یافت.'),
(353, 3, 60, '[آنچه درباره عیسی گفته شد] حق [و] از جانب پروردگار تو است. پس، از تردیدکنندگان مباش.'),
(354, 3, 61, 'پس هر که در این [باره‌] پس از دانشی که تو را [حاصل‌] آمده، با تو محاجه کند، بگو: «بیایید پسرانمان و پسرانتان، و زنانمان و زنانتان، و ما خویشان نزدیک و شما خویشان نزدیک خود را فرا خوانیم؛ سپس مباهله کنیم، و لعنت خدا را بر دروغگویان قرار دهیم.»'),
(355, 3, 62, 'آری، داستان درست [مسیح‌] همین است؛ و معبودی جز خدا نیست، و خداست که در واقع، همان شکست‌ناپذیر حکیم است.'),
(356, 3, 63, 'پس اگر رویگردان شدند، همانا خداوند به [حال‌] مفسدان داناست.'),
(357, 3, 64, 'بگو: «ای اهل کتاب، بیایید بر سر سخنی که میان ما و شما یکسان است بایستیم که: جز خدا را نپرستیم و چیزی را شریک او نگردانیم، و بعضی از ما بعضی دیگر را به جای خدا به خدایی نگیرد.» پس اگر [از این پیشنهاد] اعراض کردند، بگویید: «شاهد باشید که ما مسلمانیم [نه شما].»'),
(358, 3, 65, 'ای اهل کتاب، چرا در باره ابراهیم محاجه می‌کنید با آنکه تورات و انجیل بعد از او نازل شده است؟ آیا تعقل نمی‌کنید؟'),
(359, 3, 66, 'هان، شما [اهل کتاب‌] همانان هستید که در باره آنچه نسبت به آن دانشی داشتید محاجه کردید؛ پس چرا در مورد چیزی که بدان دانشی ندارید محاجه می‌کنید؟ با آنکه خدا می‌داند و شما نمی‌دانید.'),
(360, 3, 67, 'ابراهیم نه یهودی بود و نه نصرانی، بلکه حق گرایی فرمانبردار بود، و از مشرکان نبود.'),
(361, 3, 68, 'در حقیقت، نزدیکترین مردم به ابراهیم، همان کسانی هستند که او را پیروی کرده‌اند، و [نیز] این پیامبر و کسانی که [به آیین او] ایمان آورده‌اند؛ و خدا سرور مؤمنان است.'),
(362, 3, 69, 'گروهی از اهل کتاب آرزو می‌کنند کاش شما را گمراه می‌کردند، در صورتی که جز خودشان [کسی‌] را گمراه نمی‌کنند و نمی‌فهمند.'),
(363, 3, 70, 'ای اهل کتاب، چرا به آیات خدا کفر می‌ورزید با آنکه خود [به درستی آن‌] گواهی می‌دهید؟'),
(364, 3, 71, 'ای اهل کتاب، چرا حق را به باطل درمی‌آمیزید و حقیقت را کتمان می‌کنید، با اینکه خود می‌دانید؟'),
(365, 3, 72, 'و جماعتی از اهل کتاب گفتند: «در آغاز روز به آنچه بر مؤمنان نازل شد، ایمان بیاورید، و در پایان [روز] انکار کنید؛ شاید آنان [از اسلام‌] برگردند.»'),
(366, 3, 73, 'و [گفتند:] «جز به کسی که دین شما را پیروی کند، ایمان نیاورید -بگو: « هدایت، هدایت خداست »- مبادا به کسی نظیر آنچه به شما داده شده، داده شود، یا در پیشگاه پروردگارتان با شما محاجه کنند.» بگو: « [این‌] تفضل به دست خداست؛ آن را به هر کس که بخواهد می‌دهد، و خداوند، گشایشگر داناست.'),
(367, 3, 74, 'رحمت خود را به هر کس که بخواهد مخصوص می‌گرداند، و خداوند دارای بخشش بزرگ است.'),
(368, 3, 75, 'و از اهل کتاب، کسی است که اگر او را بر مال فراوانی امین شمری، آن را به تو برگرداند؛ و از آنان کسی است که اگر او را بر دیناری امین شمری، آن را به تو نمی‌پردازد، مگر آنکه دایماً بر [سر] وی به پا ایستی. این بدان سبب است که آنان [به پندار خود] گفتند: «در مورد کسانی که کتاب آسمانی ندارند، بر زیان ما راهی نیست.» و بر خدا دروغ می‌بندند با اینکه خودشان [هم‌] می‌دانند.'),
(369, 3, 76, 'آری، هر که به پیمان خود وفا کند، و پرهیزگاری نماید، بی‌تردید خداوند، پرهیزگاران را دوست دارد.'),
(370, 3, 77, 'کسانی که پیمان خدا و سوگندهای خود را به بهای ناچیزی می‌فروشند، آنان را در آخرت بهره‌ای نیست؛ و خدا روز قیامت با آنان سخن نمی‌گوید، و به ایشان نمی‌نگرد، و پاکشان نمی‌گرداند، و عذابی دردناک خواهند داشت.'),
(371, 3, 78, 'و از میان آنان گروهی هستند که زبان خود را به [خواندن‌] کتاب [تحریف شده‌ای ] می‌پیچانند، تا آن [بربافته‌] را از [مطالب‌] کتاب [آسمانی‌] پندارید، با اینکه آن از کتاب [آسمانی‌] نیست؛ و می‌گویند: آن از جانب خداست، در صورتی که از جانب خدا نیست، و بر خدا دروغ می‌بندند، با اینکه خودشان [هم‌] می‌دانند.'),
(372, 3, 79, 'هیچ بشری را نسزد که خدا به او کتاب و حکم و پیامبری بدهد؛ سپس او به مردم بگوید: «به جای خدا، بندگان من باشید.» بلکه [باید بگوید:] «به سبب آنکه کتاب [آسمانی‌] تعلیم می‌دادید و از آن رو که درس می‌خواندید، علمای دین باشید.»'),
(373, 3, 80, 'و [نیز] شما را فرمان نخواهد داد که فرشتگان و پیامبران را به خدایی بگیرید. آیا پس از آنکه سر به فرمان [خدا] نهاده‌اید [باز] شما را به کفر وامی‌دارد؟'),
(374, 3, 81, 'و [یاد کن‌] هنگامی را که خداوند از پیامبران پیمان گرفت که هر گاه به شما کتاب و حکمتی دادم، سپس شما را فرستاده‌ای آمد که آنچه را با شماست تصدیق کرد، البته به او ایمان بیاورید و حتماً یاریش کنید. آنگاه فرمود: «آیا اقرار کردید و در این باره پیمانم را پذیرفتید؟» گفتند: «آری، اقرار کردیم.» فرمود: «پس گواه باشید و من با شما از گواهانم.»'),
(375, 3, 82, 'پس، کسانی که بعد از این [پیمان‌] روی برتابند آنان، خود، نافرمانانند.'),
(376, 3, 83, 'آیا جز دین خدا را می‌جویند؟ با آنکه هر که در آسمانها و زمین است خواه و ناخواه سر به فرمان او نهاده است، و به سوی او بازگردانیده می‌شوید.'),
(377, 3, 84, 'بگو: «به خدا و آنچه بر ما نازل شده، و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط نازل گردیده، و آنچه به موسی و عیسی و انبیای [دیگر] از جانب پروردگارشان داده شده، گرویدیم؛ و میان هیچ یک از آنان فرق نمی‌گذاریم و ما او را فرمانبرداریم.'),
(378, 3, 85, 'و هر که جز اسلام، دینی [دیگر] جوید، هرگز از وی پذیرفته نشود، و وی در آخرت از زیانکاران است.'),
(379, 3, 86, 'چگونه خداوند، قومی را که بعد از ایمانشان کافر شدند، هدایت می‌کند؟ با آنکه شهادت دادند که این رسول، بر حق است و برایشان دلایل روشن آمد، و خداوند قوم بیدادگر را هدایت نمی‌کند.'),
(380, 3, 87, 'آنان، سزایشان این است که لعنت خدا و فرشتگان و مردم، همگی برایشان است.'),
(381, 3, 88, 'در آن [لعنت‌] جاودانه بمانند؛ نه عذاب از ایشان کاسته گردد و نه مهلت یابند.'),
(382, 3, 89, 'مگر کسانی که پس از آن توبه کردند و درستگاری [پیشه‌] نمودند، که خداوند آمرزنده مهربان است.'),
(383, 3, 90, 'کسانی که پس از ایمان خود کافر شدند، سپس بر کفر [خود] افزودند، هرگز توبه آنان پذیرفته نخواهد شد، و آنان خود گمراهانند.'),
(384, 3, 91, 'در حقیقت، کسانی که کافر شده و در حال کفر مرده‌اند، اگر چه [فراخنای‌] زمین را پر از طلا کنند و آن را [برای خود] فدیه دهند، هرگز از هیچ یک از آنان پذیرفته نگردد؛ آنان را عذابی دردناک خواهد بود، و یاورانی نخواهند داشت.'),
(385, 3, 92, 'هرگز به نیکوکاری نخواهید رسید تا از آنچه دوست دارید انفاق کنید؛ و از هر چه انفاق کنید قطعاً خدا بدان داناست.'),
(386, 3, 93, 'همه خوراکیها بر فرزندان اسرائیل حلال بود. جز آنچه پیش از نزول تورات، اسرائیل [=یعقوب‌] بر خویشتن حرام ساخته بود. بگو: «اگر [جز این است و] راست می‌گویید، تورات را بیاورید و آن را بخوانید.»'),
(387, 3, 94, 'پس کسانی که بعد از این، بر خدا دروغ بندند، آنان خود ستمکارانند.'),
(388, 3, 95, 'بگو: «خدا راست گفت، پس، از آیین ابراهیم که حق‌گرا بود و از مشرکان نبود، پیروی کنید.'),
(389, 3, 96, 'در حقیقت، نخستین خانه‌ای که برای [عبادت‌] مردم، نهاده شده، همان است که در مکه است و مبارک، و برای جهانیان [مایه‌] هدایت است.'),
(390, 3, 97, 'در آن، نشانه‌هایی روشن است [از جمله‌] مقام ابراهیم است؛ و هر که در آن درآید در امان است؛ و برای خدا، حج آن خانه، بر عهده مردم است؛ [البته بر] کسی که بتواند به سوی آن راه یابد. و هر که کفر ورزد، یقیناً خداوند از جهانیان بی‌نیاز است.'),
(391, 3, 98, 'بگو: «ای اهل کتاب، چرا به آیات خدا کفر می‌ورزید؟ با آنکه خدا بر آنچه می‌کنید گواه است.»'),
(392, 3, 99, 'بگو: «ای اهل کتاب، چرا کسی را که ایمان آورده است، از راه خدا بازمی‌دارید؛ و آن [راه‌] را کج می‌شمارید، با آنکه خود [به راستیِ آن‌] گواهید؟» و خدا از آنچه می‌کنید غافل نیست.'),
(393, 3, 100, 'ای کسانی که ایمان آورده‌اید، اگر از فرقه‌ای از اهل کتاب فرمان برید، شما را پس از ایمانتان به حال کفر برمی‌گردانند.'),
(394, 3, 101, 'و چگونه کفر می‌ورزید، با اینکه آیات خدا بر شما خوانده می‌شود و پیامبر او میان شماست؟ و هر کس به خدا تمسک جوید، قطعاً به راه راست هدایت شده است.'),
(395, 3, 102, 'ای کسانی که ایمان آورده‌اید، از خدا آن گونه که حق پرواکردن از اوست، پروا کنید؛ و زینهار، جز مسلمان نمیرید.'),
(396, 3, 103, 'و همگی به ریسمان خدا چنگ زنید، و پراکنده نشوید؛ و نعمت خدا را بر خود یاد کنید: آنگاه که دشمنان [یکدیگر] بودید، پس میان دلهای شما الفت انداخت، تا به لطف او برادران هم شدید؛ و بر کنار پرتگاه آتش بودید که شما را از آن رهانید. این گونه، خداوند نشانه‌های خود را برای شما روشن می‌کند، باشد که شما راه یابید.'),
(397, 3, 104, 'و باید از میان شما، گروهی، [مردم را] به نیکی دعوت کنند و به کار شایسته وادارند و از زشتی بازدارند، و آنان همان رستگارانند.'),
(398, 3, 105, 'و چون کسانی مباشید که پس از آنکه دلایل آشکار برایشان آمد، پراکنده شدند و با هم اختلاف پیدا کردند، و برای آنان عذابی سهمگین است؛'),
(399, 3, 106, '[در آن‌] روزی که چهره‌هایی سپید، و چهره‌هایی سیاه گردد. اما سیاهرویان [به آنان گویند:] آیا بعد از ایمانتان کفر ورزیدید؟ پس به سزای آنکه کفر می‌ورزیدید [این‌] عذاب را بچشید.'),
(400, 3, 107, 'و اما سپیدرویان همواره در رحمت خداوند جاویدانند.'),
(401, 3, 108, 'اینها آیات خداست که آن را به حق بر تو می‌خوانیم؛ و خداوند هیچ ستمی بر جهانیان نمی‌خواهد.'),
(402, 3, 109, 'و آنچه در آسمانها و آنچه در زمین است از آنِ خداست، و [همه‌] کارها به سوی خدا بازگردانده می‌شود.'),
(403, 3, 110, 'شما بهترین امتی هستید که برای مردم پدیدار شده‌اید: به کار پسندیده فرمان می‌دهید، و از کار ناپسند بازمی‌دارید، و به خدا ایمان دارید. و اگر اهل کتاب ایمان آورده بودند قطعاً برایشان بهتر بود؛ برخی از آنان مؤمنند و[لی‌] بیشترشان نافرمانند.'),
(404, 3, 111, '111]جز آزاری [اندک‌] هرگز به شما زیانی نخواهند رسانید؛ و اگر با شما بجنگند، به شما پشت نمایند، سپس یاری نیابند.'),
(405, 3, 112, 'هر کجا یافته شوند، به خواری دچار شده‌اند -مگر آنکه به پناه امان خدا و زینهار مردم [روند]- و به خشمی از خدا گرفتار آمدند، و [مُهر] بینوایی بر آنان زده شد. این بدان سبب بود که به آیات خدا کفر می‌ورزیدند و پیامبران را بناحق می‌کشتند. [و نیز] این [عقوبت‌] به سزای آن بود که نافرمانی کردند و از اندازه درمی‌گذرانیدند.'),
(406, 3, 113, '[ولی همه آنان‌] یکسان نیستند. از میان اهل کتاب، گروهی درستکردارند که آیات الهی را در دل شب می‌خوانند و سر به سجده می‌نهند.'),
(407, 3, 114, 'به خدا و روز قیامت ایمان دارند؛ و به کار پسندیده فرمان می‌دهند و از کار ناپسند باز می‌دارند؛ و در کارهای نیک شتاب می‌کنند، و آنان از شایستگانند.'),
(408, 3, 115, 'و هر کار نیکی انجام دهند، هرگز در باره آن ناسپاسی نبینند، و خداوند به [حال‌] تقواپیشگان داناست.'),
(409, 3, 116, 'کسانی که کفر ورزیدند، هرگز اموالشان و اولادشان چیزی [از عذاب خدا] را از آنان دفع نخواهد کرد، و آنان اهل آتشند و در آن جاودانه خواهند بود.'),
(410, 3, 117, 'مثَل آنچه [آنان‌] در زندگی این دنیا [در راه دشمنی با پیامبر] خرج می‌کنند، همانند بادی است که در آن، سرمای سختی است، که به کشتزار قومی که بر خود ستم نموده‌اند بوزد و آن را تباه سازد؛ و خدا به آنان ستم نکرده، بلکه آنان خود بر خویشتن ستم کرده‌اند.'),
(411, 3, 118, 'ای کسانی که ایمان آورده‌اید، از غیر خودتان، [دوست و] همراز مگیرید. [آنان‌] از هیچ نابکاری در حق شما کوتاهی نمی‌ورزند. آرزو دارند که در رنج بیفتید. دشمنی از لحن و سخنشان آشکار است؛ و آنچه سینه‌هایشان نهان می‌دارد، بزرگتر است. در حقیقت، ما نشانه‌ها[ی دشمنی آنان‌] را برای شما بیان کردیم، اگر تعقل کنید.'),
(412, 3, 119, 'هان، شما کسانی هستید که آنان را دوست دارید، و [حال آنکه‌] آنان شما را دوست ندارند، و شما به همه کتابها[ی خدا] ایمان دارید؛ و چون با شما برخورد کنند می‌گویند: «ایمان آوردیم.» و چون [با هم ]خلوت کنند، از شدت خشم بر شما، سر انگشتان خود را می‌گزند. بگو: «به خشم خود بمیرید» که خداوند به راز درون سینه‌ها داناست.'),
(413, 3, 120, 'اگر به شما خوشی رسد آنان را بدحال می‌کند؛ و اگر به شما گزندی رسد بدان شاد می‌شوند؛ و اگر صبر کنید و پرهیزگاری نمایید، نیرنگشان هیچ زیانی به شما نمی‌رساند؛ یقیناً خداوند به آنچه می‌کنند احاطه دارد.'),
(414, 3, 121, 'و [یاد کن‌] زمانی را که [در جنگ احد] بامدادان از پیش کسانت بیرون آمدی [تا] مؤمنان را برای جنگیدن، در مواضع خود جای دهی، و خداوند، شنوای داناست.'),
(415, 3, 122, 'آن هنگام که دو گروه از شما بر آن شدند که سستی ورزند با آنکه خدا یاورشان بود. و مؤمنان باید تنها بر خدا توکل کنند.'),
(416, 3, 123, 'و یقیناً خدا شما را در [جنگ‌] بدر -با آنکه ناتوان بودید- یاری کرد. پس، از خدا پروا کنید، باشد که سپاسگزاری نمایید.'),
(417, 3, 124, 'آنگاه که به مؤمنان می‌گفتی: «آیا شما را بس نیست که پروردگارتان، شما را با سه هزار فرشته فرودآمده، یاری کند؟»'),
(418, 3, 125, 'آری، اگر صبر کنید و پرهیزگاری نمایید، و با همین جوش [و خروش‌] بر شما بتازند، همانگاه پروردگارتان شما را با پنج هزار فرشته نشاندار یاری خواهد کرد.'),
(419, 3, 126, 'و خدا آن [وعده پیروزی‌] را، جز مژده‌ای برای شما قرار نداد تا [بدین وسیله شادمان شوید و] دلهای شما بدان آرامش یابد، و پیروزی جز از جانب خداوند توانای حکیم نیست.'),
(420, 3, 127, 'تا برخی از کسانی را که کافر شده‌اند نابود کند، یا آنان را خوار سازد، تا نومید بازگردند.'),
(421, 3, 128, 'هیچ یک از این کارها در اختیار تو نیست؛ یا [خدا] بر آنان می‌بخشاید، یا عذابشان می‌کند، زیرا آنان ستمکارند.'),
(422, 3, 129, 'و آنچه در آسمانها و آنچه در زمین است از آنِ خداست. هر که را بخواهد می‌آمرزد، و هر که را بخواهد عذاب می‌کند، و خداوند، آمرزنده مهربان است.'),
(423, 3, 130, 'ای کسانی که ایمان آورده‌اید، ربا را [با سود] چندین برابر مخورید، و از خدا پروا کنید، باشد که رستگار شوید.'),
(424, 3, 131, 'و از آتشی که برای کافران آماده شده است بترسید.'),
(425, 3, 132, 'خدا و رسول را فرمان برید، باشد که مشمول رحمت قرار گیرید.'),
(426, 3, 133, 'و برای نیل به آمرزشی از پروردگار خود، و بهشتی که پهنایش [به قدر] آسمانها و زمین است [و] برای پرهیزگاران آماده شده است، بشتابید.'),
(427, 3, 134, 'همانان که در فراخی و تنگی انفاق می‌کنند؛ و خشم خود را فرو می‌برند؛ و از مردم در می‌گذرند؛ و خداوند نکوکاران را دوست دارد.'),
(428, 3, 135, 'و آنان که چون کار زشتی کنند، یا بر خود ستم روا دارند، خدا را به یاد می‌آورند و برای گناهانشان آمرزش می‌خواهند -و چه کسی جز خدا گناهان را می‌آمرزد؟ و بر آنچه مرتکب شده‌اند، با آنکه می‌دانند [که گناه است‌]، پافشاری نمی‌کنند.'),
(429, 3, 136, 'آنان، پاداششان آمرزشی از جانب پروردگارشان، و بوستانهایی است که از زیر [درختان‌] آن جویبارها روان است. جاودانه در آن بمانند، و پاداش اهل عمل چه نیکوست.'),
(430, 3, 137, 'قطعاً پیش از شما سنّتهایی [بوده و] سپری شده است. پس، در زمین بگردید و بنگرید که فرجام تکذیب‌کنندگان چگونه بوده است؟'),
(431, 3, 138, 'این [قرآن‌] برای مردم، بیانی، و برای پرهیزگاران رهنمود و اندرزی است.'),
(432, 3, 139, 'و اگر مؤمنید، سستی مکنید و غمگین مشوید، که شما برترید.'),
(433, 3, 140, 'اگر به شما آسیبی رسیده، آن قوم را نیز آسیبی نظیر آن رسید؛ و ما این روزها[ی شکست و پیروزی‌] را میان مردم به نوبت می‌گردانیم [تا آنان پند گیرند] و خداوند کسانی را که [واقعاً] ایمان آورده‌اند معلوم بدارد، و از میان شما گواهانی بگیرد، و خداوند ستمکاران را دوست نمی‌دارد.'),
(434, 3, 141, 'و تا خدا کسانی را که ایمان آورده‌اند خالص گرداند و کافران را [به تدریج‌] نابود سازد.'),
(435, 3, 142, 'آیا پنداشتید که داخل بهشت می‌شوید، بی‌آنکه خداوند جهادگران و شکیبایان شما را معلوم بدارد؟'),
(436, 3, 143, 'و شما مرگ را پیش از آنکه با آن روبرو شوید، سخت آرزو می‌کردید؛ پس، آن را دیدید و [همچنان‌] نگاه می‌کردید.'),
(437, 3, 144, 'و محمد، جز فرستاده‌ای که پیش از او [هم‌] پیامبرانی [آمده و] گذشتند، نیست. آیا اگر او بمیرد یا کشته شود، از عقیده خود برمی‌گردید؟ و هر کس از عقیده خود بازگردد، هرگز هیچ زیانی به خدا نمی‌رساند، و به زودی خداوند سپاسگزاران را پاداش می‌دهد.'),
(438, 3, 145, 'و هیچ نفسی جز به فرمان خدا نمیرد. [خداوند، مرگ را] به عنوان سرنوشتی معین [مقرر کرده است‌]. و هر که پاداش این دنیا را بخواهد به او از آن می‌دهیم؛ و هر که پاداش آن سرای را بخواهد از آن به او می‌دهیم، و به زودی سپاسگزاران را پاداش خواهیم داد.'),
(439, 3, 146, 'و چه بسیار پیامبرانی که همراه او توده‌های انبوه، کارزار کردند؛ و در برابر آنچه در راه خدا بدیشان رسید، سستی نورزیدند و ناتوان نشدند؛ و تسلیم [دشمن‌] نگردیدند، و خداوند، شکیبایان را دوست دارد.'),
(440, 3, 147, 'و سخن آنان جز این نبود که گفتند: «پروردگارا، گناهان ما و زیاده‌روی ما، در کارمان را بر ما ببخش، و گامهای ما را استوار دار، و ما را بر گروه کافران یاری ده.»'),
(441, 3, 148, 'پس خداوند، پاداش این دنیا، و پاداش نیک آخرت را به آنان عطا کرد، و خداوند نیکوکاران را دوست دارد.'),
(442, 3, 149, 'ای کسانی که ایمان آورده‌اید، اگر از کسانی که کفر ورزیده‌اند اطاعت کنید، شما را از عقیده‌تان بازمی‌گردانند و زیانکار خواهید گشت.'),
(443, 3, 150, 'آری، خدا مولای شماست، و او بهترین یاری‌دهندگان است.'),
(444, 3, 151, 'به زودی در دلهای کسانی که کفر ورزیده‌اند بیم خواهیم افکند، زیرا چیزی را با خدا شریک گردانیده‌اند که بر [حقانیت‌] آن، [خدا] دلیلی نازل نکرده است. و جایگاهشان آتش است، و جایگاه ستمگران چه بد است.'),
(445, 3, 152, 'و [در نبرد اُحد] قطعاً خدا وعده خود را با شما راست گردانید: آنگاه که به فرمان او، آنان را می‌کشتید، تا آنکه سست شدید و در کار [جنگ و بر سر تقسیم غنایم‌] با یکدیگر به نزاع پرداختید؛ و پس از آنکه آنچه را دوست داشتید [یعنی غنایم را ]به شما نشان داد، نافرمانی نمودید. برخی از شما دنیا را و برخی از شما آخرت را می‌خواهد. سپس برای آنکه شما را بیازماید، از [تعقیب‌] آنان منصرفتان کرد و از شما درگذشت، و خدا نسبت به مؤمنان، با تفضل است.'),
(446, 3, 153, '[یاد کنید] هنگامی را که در حال گریز [از کوه‌] بالا می‌رفتید و به هیچ کس توجه نمی‌کردید؛ و پیامبر، شما را از پشت سرتان فرا می‌خواند. پس [خداوند] به سزای [این بی‌انضباطی‌] غمی بر غمتان [افزود]، تا سرانجام بر آنچه از کف داده‌اید و برای آنچه به شما رسیده است اندوهگین نشوید، و خداوند از آنچه می‌کنید آگاه است.'),
(447, 3, 154, 'سپس [خداوند] بعد از آن اندوه، آرامشی [به صورت‌] خواب سبکی، بر شما فرو فرستاد، که گروهی از شما را فرا گرفت، و گروهی [تنها] در فکر جان خود بودند؛ و در باره خدا، گمانهای ناروا، همچون گمانهای [دوران‌] جاهلیت می‌بردند. می‌گفتند: «آیا ما را در این کار اختیاری هست؟» بگو: «سررشته کارها [شکست یا پیروزی‌]، یکسر به دست خداست.» آنان چیزی را در دلهایشان پوشیده می‌داشتند، که برای تو آشکار نمی‌کردند. می‌گفتند: «اگر ما را در این کار اختیاری بود، [و وعده پیامبر واقعیت داشت،] در اینجا کشته نمی‌شدیم.» بگو: «اگر شما در خانه‌های خود هم بودید، کسانی که کشته شدن بر آنان نوشته شده، قطعاً [با پای خود] به سوی قتلگاههای خویش می‌رفتند. و [اینها ] برای این است که خداوند، آنچه را در دلهای شماست، [در عمل‌] بیازماید؛ و آنچه را در قلبهای شماست، پاک گرداند؛ و خدا به راز سینه‌ها آگاه است.'),
(448, 3, 155, 'روزی که دو گروه [در اُحد] با هم رویاروی شدند، کسانی که از میان شما [به دشمن‌] پشت کردند، در حقیقت جز این نبود که به سبب پاره‌ای از آنچه [از گناه‌] حاصل کرده بودند، شیطان آنان را بلغزانید. و قطعاً خدا از ایشان درگذشت؛ زیرا خدا آمرزگار بردبار است.'),
(449, 3, 156, 'ای کسانی که ایمان آورده‌اید، همچون کسانی نباشید که کفر ورزیدند؛ و به برادرانشان - هنگامی که به سفر رفته [و در سفر مردند] و یا جهادگر شدند [و کشته شدند]، گفتند: «اگر نزد ما [مانده‌] بودند، نمی‌مردند و کشته نمی‌شدند.» [شما چنین سخنانی مگویید] تا خدا آن را در دلهایشان حسرتی قرار دهد. و خدا[ست که‌] زنده می‌کند و می‌میراند، و خدا[ست که‌] به آنچه می‌کنید بیناست.'),
(450, 3, 157, 'و اگر در راه خدا کشته شوید یا بمیرید، قطعاً آمرزش خدا و رحمت او از [همه‌] آنچه [آنان‌] جمع می‌کنند بهتر است.'),
(451, 3, 158, 'و اگر [در راه جهاد] بمیرید یا کشته شوید، قطعاً به سوی خدا گردآورده خواهید شد.'),
(452, 3, 159, 'پس به [برکت‌] رحمت الهی، با آنان نرمخو [و پُرمِهر] شدی، و اگر تندخو و سختدل بودی قطعاً از پیرامون تو پراکنده می‌شدند. پس، از آنان درگذر و برایشان آمرزش بخواه، و در کار[ها] با آنان مشورت کن، و چون تصمیم گرفتی بر خدا توکل کن، زیرا خداوند توکل‌کنندگان را دوست می‌دارد.'),
(453, 3, 160, 'اگر خدا شما را یاری کند، هیچ کس بر شما غالب نخواهد شد؛ و اگر دست از یاری شما بردارد، چه کسی بعد از او شما را یاری خواهد کرد؟ و مؤمنان باید تنها بر خدا توکل کنند.'),
(454, 3, 161, 'و هیچ پیامبری را نسزد که خیانت ورزد، و هر کس خیانت ورزد، روز قیامت با آنچه در آن خیانت کرده بیاید؛ آنگاه به هر کس [پاداش‌] آنچه کسب کرده، به تمامی داده می‌شود، و بر آنان ستم نرود.'),
(455, 3, 162, 'آیا کسی که خشنودی خدا را پیروی می‌کند، چون کسی است که به خشمی از خدا دچار گردیده و جایگاهش جهنم است؟ و چه بد بازگشتگاهی است.'),
(456, 3, 163, '[هر یک از] ایشان را نزد خداوند درجاتی است، و خدا به آنچه می‌کنند بیناست.'),
(457, 3, 164, 'به یقین، خدا بر مؤمنان منت نهاد [که‌] پیامبری از خودشان در میان آنان برانگیخت، تا آیات خود را بر ایشان بخواند و پاکشان گرداند و کتاب و حکمت به آنان بیاموزد، قطعاً پیش از آن در گمراهی آشکاری بودند.'),
(458, 3, 165, 'آیا چون به شما [در نبرد اُحد] مصیبتی رسید -[با آنکه در نبرد بدر] دو برابرش را [به دشمنان خود] رساندید- گفتید: «این [مصیبت‌] از کجا [به ما رسید]؟» بگو: «آن از خود شما [و ناشی از بی‌انضباطی خودتان‌] است.» آری! خدا به هر چیزی تواناست.'),
(459, 3, 166, 'و روزی که [در اُحد] آن دو گروه با هم برخورد کردند، آنچه به شما رسید به اذن خدا بود [تا شما را بیازماید] و مؤمنان را معلوم بدارد؛'),
(460, 3, 167, 'همچنین کسانی را که دو رویی نمودند [نیز] معلوم بدارد. و به ایشان گفته شد: «بیایید در راه خدا بجنگید یا دفاع کنید.» گفتند: «اگر جنگیدن می‌دانستیم مسلماً از شما پیروی می‌کردیم.» آن روز، آنان به کفر نزدیکتر بودند تا به ایمان. به زبان خویش چیزی می‌گفتند که در دلهایشان نبود، و خدا به آنچه می‌نهفتند داناتر است.'),
(461, 3, 168, 'همان کسانی که [خود در خانه‌] نشستند و در باره دوستان خود گفتند: «اگر از ما پیروی می‌کردند کشته نمی‌شدند.» بگو: «اگر راست می‌گویید مرگ را از خودتان دور کنید.»'),
(462, 3, 169, 'هرگز کسانی را که در راه خدا کشته شده‌اند، مرده مپندار، بلکه زنده‌اند که نزد پروردگارشان روزی داده می‌شوند.'),
(463, 3, 170, 'به آنچه خدا از فضل خود به آنان داده است شادمانند، و برای کسانی که از پی ایشانند و هنوز به آنان نپیوسته‌اند شادی می‌کنند که نه بیمی بر ایشان است و نه اندوهگین می‌شوند.'),
(464, 3, 171, 'بر نعمت و فضل خدا و اینکه خداوند پاداش مؤمنان را تباه نمی‌گرداند، شادی می‌کنند.'),
(465, 3, 172, 'کسانی که [در نبرد اُحد] پس از آنکه زخم برداشته بودند، دعوت خدا و پیامبر [او] را اجابت کردند، برای کسانی از آنان که نیکی و پرهیزگاری کردند پاداشی بزرگ است.'),
(466, 3, 173, 'همان کسانی که [برخی از] مردم به ایشان گفتند: «مردمان برای [جنگ با] شما گرد آمده‌اند؛ پس، از آن بترسید.» و[لی این سخن‌] بر ایمانشان افزود و گفتند: «خدا ما را بس است و نیکو حمایتگری است.»'),
(467, 3, 174, 'پس با نعمت و بخششی از جانب خدا، [از میدان نبرد] بازگشتند، در حالی که هیچ آسیبی به آنان نرسیده بود، و همچنان خشنودی خدا را پیروی کردند، و خداوند دارای بخششی عظیم است.'),
(468, 3, 175, 'در واقع، این شیطان است که دوستانش را می‌ترساند؛ پس اگر مؤمنید از آنان مترسید و از من بترسید.'),
(469, 3, 176, 'و کسانی که در کفر می‌کوشند، تو را اندوهگین نسازند؛ که آنان هرگز به خدا هیچ زیانی نمی‌رسانند. خداوند می‌خواهد در آخرت برای آنان بهره‌ای قرار ندهد، و برای ایشان عذابی بزرگ است.'),
(470, 3, 177, 'در حقیقت، کسانی که کفر را به [بهای‌] ایمان خریدند، هرگز به خداوند هیچ زیانی نخواهند رسانید، و برای آنان عذابی دردناک است.'),
(471, 3, 178, 'و البته نباید کسانی که کافر شده‌اند تصور کنند اینکه به ایشان مهلت می‌دهیم برای آنان نیکوست؛ ما فقط به ایشان مهلت می‌دهیم تا بر گناه [خود] بیفزایند، و [آنگاه‌] عذابی خفت‌آور خواهند داشت.'),
(472, 3, 179, 'خدا بر آن نیست که مؤمنان را به این [حالی‌] که شما بر آن هستید، واگذارد، تا آنکه پلید را از پاک جدا کند. و خدا بر آن نیست که شما را از غیب آگاه گرداند، ولی خدا از میان فرستادگانش هر که را بخواهد برمی‌گزیند. پس، به خدا و پیامبرانش ایمان بیاورید؛ و اگر بگروید و پرهیزگاری کنید، برای شما پاداشی بزرگ خواهد بود.'),
(473, 3, 180, 'و کسانی که به آنچه خدا از فضل خود به آنان عطا کرده، بخل می‌ورزند، هرگز تصور نکنند که آن [بخل‌] برای آنان خوب است، بلکه برایشان بد است. به زودی آنچه که به آن بخل ورزیده‌اند، روز قیامت طوق گردنشان می‌شود. میراث آسمانها و زمین از آنِ خداست، و خدا به آنچه می‌کنید آگاه است.'),
(474, 3, 181, 'مسلماً خداوند، سخن کسانی را که گفتند: «خدا نیازمند است و ما توانگریم »، شنید. به زودی آنچه را گفتند، و بناحق کشتنِ آنان پیامبران را، خواهیم نوشت و خواهیم گفت: «بچشید عذاب سوزان را.»'),
(475, 3, 182, 'این [عقوبت‌] به خاطر کار و کردار پیشین شماست، [و گر نه‌] خداوند هرگز نسبت به بندگان [خود] بیدادگر نیست.'),
(476, 3, 183, 'همانان که گفتند: «خدا با ما پیمان بسته که به هیچ پیامبری ایمان نیاوریم تا برای ما قربانیی بیاورد که آتش [آسمانی‌] آن را [به نشانه قبول‌] بسوزاند.» بگو: «قطعاً پیش از من، پیامبرانی بودند که دلایل آشکار را با آنچه گفتید، برای شما آوردند. اگر راست می گویید، پس چرا آنان را کشتید؟»'),
(477, 3, 184, 'پس اگر تو را تکذیب کردند، بدان که پیامبرانی [هم‌] که پیش از تو، دلایل روشن و نوشته‌ها و کتاب روشن آورده بودند، تکذیب شدند.'),
(478, 3, 185, 'هر جانداری چشنده [طعم‌] مرگ است، و همانا روز رستاخیز پاداشهایتان به طور کامل به شما داده می‌شود. پس هر که را از آتش به دور دارند و در بهشت درآورند قطعاً کامیاب شده است؛ و زندگی دنیا جز مایه فریب نیست.'),
(479, 3, 186, 'قطعاً در مالها و جانهایتان آزموده خواهید شد، و از کسانی که پیش از شما به آنان کتاب داده شده و [نیز] از کسانی که به شرک گراییده‌اند، [سخنان دل‌]آزار بسیاری خواهید شنید، و[لی‌] اگر صبر کنید و پرهیزگاری نمایید، این [ایستادگی‌] حاکی از عزم استوار [شما] در کارهاست.'),
(480, 3, 187, 'و [یاد کن‌] هنگامی را که خداوند از کسانی که به آنان کتاب داده شده، پیمان گرفت که حتماً باید آن را [به وضوح‌] برای مردم بیان نمایید و کتمانش مکنید. پس، آن [عهد] را پشت سر خود انداختند و در برابر آن، بهایی ناچیز به دست آوردند، و چه بد معامله‌ای کردند.'),
(481, 3, 188, 'البته گمان مبر کسانی که بدانچه کرده‌اند شادمانی می‌کنند و دوست دارند به آنچه نکرده‌اند مورد ستایش قرار گیرند، قطعاً گمان مبر که برای آنان نجاتی از عذاب است، [که‌] عذابی دردناک خواهند داشت.'),
(482, 3, 189, 'و فرمانروایی آسمانها و زمین از آنِ خداست، و خداوند بر هر چیزی تواناست.'),
(483, 3, 190, 'مسلماً در آفرینش آسمانها و زمین، و در پی یکدیگر آمدن شب و روز، برای خردمندان نشانه‌هایی [قانع کننده‌] است.'),
(484, 3, 191, 'همانان که خدا را [در همه احوال‌] ایستاده و نشسته، و به پهلو آرمیده یاد می‌کنند، و در آفرینش آسمانها و زمین می‌اندیشند [که:] پروردگارا، اینها را بیهوده نیافریده‌ای؛ منزهی تو! پس ما را از عذاب آتش دوزخ در امان بدار.'),
(485, 3, 192, 'پروردگارا، هر که را تو در آتش درآوری، یقیناً رسوایش کرده‌ای، و برای ستمکاران یاورانی نیست.'),
(486, 3, 193, 'پروردگارا، ما شنیدیم که دعوتگری به ایمان فرا می‌خواند که: «به پروردگار خود ایمان آورید»، پس ایمان آوردیم. پروردگارا، گناهان ما را بیامرز، و بدیهای ما را بزدای و ما را در زمره نیکان بمیران.'),
(487, 3, 194, 'پروردگارا، و آنچه را که به وسیله فرستادگانت به ما وعده داده‌ای به ما عطا کن، و ما را روز رستاخیز رسوا مگردان، زیرا تو وعده‌ات را خلاف نمی‌کنی.'),
(488, 3, 195, 'پس، پروردگارشان دعای آنان را اجابت کرد [و فرمود که:] من عمل هیچ صاحب عملی از شما را، از مرد یا زن، که همه از یکدیگرید، تباه نمی‌کنم؛ پس، کسانی که هجرت کرده و از خانه‌های خود رانده شده و در راه من آزار دیده و جنگیده و کشته شده‌اند، بدیهایشان را از آنان می‌زدایم، و آنان را در باغهایی که از زیرِ [درختان‌] آن نهرها روان است درمی‌آورم؛ [این‌] پاداشی است از جانب خدا و پاداش نیکو نزد خداست.'),
(489, 3, 196, 'مبادا رفت و آمد [و جنب و جوش‌] کافران در شهرها تو را دستخوش فریب کند.'),
(490, 3, 197, '[این‌] کالای ناچیز [و برخورداریِ اندکی‌] است؛ سپس جایگاهشان دوزخ است، و چه بد قرارگاهی است.'),
(491, 3, 198, 'ولی کسانی که پروای پروردگارشان را پیشه ساخته‌اند باغهایی خواهند داشت که از زیر [درختان‌] آن نهرها روان است. در آنجا جاودانه بمانند؛ [این‌] پذیرایی از جانب خداست، و آنچه نزد خداست برای نیکان بهتر است.'),
(492, 3, 199, 'و البته از میان اهل کتاب کسانی هستند که به خدا و بدانچه به سوی شما نازل شده و به آنچه به سوی خودشان فرود آمده ایمان دارند، در حالی که در برابر خدا خاشعند، و آیات خدا را به بهای ناچیزی نمی‌فروشند. اینانند که نزد پروردگارشان پاداش خود را خواهند داشت. آری! خدا زودشمار است.'),
(493, 3, 200, 'ای کسانی که ایمان آورده‌اید، صبر کنید و ایستادگی ورزید و مرزها را نگهبانی کنید و از خدا پروا نمایید، امید است که رستگار شوید.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم، از پروردگارتان که شما را از «نفس واحدی» آفرید و جفتش را [نیز ] از او آفرید، و از آن دو، مردان و زنان بسیاری پراکنده کرد، پروا دارید؛ و از خدایی که به [نام‌] او از همدیگر درخواست می‌کنید پروا نمایید؛ و زنهار از خویشاوندان مَبُرید، که خدا همواره بر شما نگهبان است.'),
(495, 4, 2, 'و اموال یتیمان را به آنان [باز]دهید، و [مال پاک‌] و [مرغوب آنان ] را با [مال‌] ناپاک [خود] عوض نکنید؛ و اموال آنان را همراه با اموال خود مخورید که این گناهی بزرگ است.'),
(496, 4, 3, 'و اگر در اجرای عدالت میان دختران یتیم بیمناکید، هر چه از زنان [دیگر] که شما را پسند افتاد، دو دو، سه سه، چهار چهار، به زنی گیرید. پس اگر بیم دارید که به عدالت رفتار نکنید، به یک [زن آزاد] یا به آنچه [از کنیزان‌] مالک شده‌اید [اکتفا کنید]. این [خودداری‌] نزدیکتر است تا به ستم گرایید [و بیهوده عیال‌وار گردید].'),
(497, 4, 4, 'و مَهر زنان را به عنوان هدیه‌ای از روی طیب خاطر به ایشان بدهید؛ و اگر به میل خودشان چیزی از آن را به شما واگذاشتند، آن را حلال و گوارا بخورید.'),
(498, 4, 5, 'و اموال خود را -که خداوند آن را وسیله قوام [زندگی‌] شما قرار داده- به سفیهان مدهید، و[لی‌] از [عواید] آن به ایشان بخورانید و آنان را پوشاک دهید؛ و با آنان سخنی پسندیده بگویید.'),
(499, 4, 6, 'و یتیمان را بیازمایید تا وقتی به [سن‌] زناشویی برسند؛ پس اگر در ایشان رشد [فکری‌] یافتید، اموالشان را به آنان رد کنید، و آن را [از بیم آنکه مبادا] بزرگ شوند، به اسراف و شتاب مخورید. و آن کس که توانگر است باید [از گرفتن اجرت سرپرستی‌] خودداری ورزد؛ و هر کس تهیدست است باید مطابق عرف [از آن‌] بخورد؛ پس هر گاه اموالشان را به آنان رد کردید بر ایشان گواه بگیرید، خداوند حسابرسی را کافی است.'),
(500, 4, 7, 'برای مردان، از آنچه پدر و مادر و خویشاوندان [آنان‌] بر جای گذاشته‌اند سهمی است؛ و برای زنان [نیز] از آنچه پدر و مادر و خویشاوندان [آنان‌] بر جای گذاشته‌اند سهمی [خواهد بود] -خواه آن [مال‌] کم باشد یا زیاد- نصیب هر کس مفروض شده است.'),
(501, 4, 8, 'و هر گاه، خویشاوندان یتیمان و مستمندان در تقسیم [ارث‌] حاضر شدند، [چیزی‌] از آن را به ایشان ارزانی دارید و با آنان سخنی پسندیده گویید.'),
(502, 4, 9, 'و آنان که اگر فرزندان ناتوانی از خود بر جای بگذارند بر [آینده‌] آنان بیم دارند، باید [از ستم بر یتیمان مردم نیز] بترسند. پس باید از خدا پروا دارند و سخنی [بجا و] درست گویند.'),
(503, 4, 10, 'در حقیقت، کسانی که اموال یتیمان را به ستم می‌خورند، جز این نیست که آتشی در شکم خود فرو می‌برند، و به زودی در آتشی فروزان درآیند.'),
(504, 4, 11, 'خداوند به شما در باره فرزندانتان سفارش می‌کند: سهم پسر، چون سهم دو دختر است. و اگر [همه ورثه،] دختر [و] از دو تن بیشتر باشند، سهم آنان دو سوم ماترک است؛ و اگر [دختری که ارث می‌برد ]یکی باشد، نیمی از میراث از آنِ اوست، و برای هر یک از پدر و مادر وی [=متوفی‌] یک ششم از ماترک [مقرر شده‌] است، این در صورتی است که [متوفی‌] فرزندی داشته باشد. ولی اگر فرزندی نداشته باشد و [تنها] پدر و مادرش از او ارث برند، برای مادرش یک سوم است [و بقیه را پدر می‌برد]؛ و اگر او برادرانی داشته باشد، مادرش یک ششم می‌برد، [البته همه اینها] پس از انجام وصیتی است که او بدان سفارش کرده یا دَینی [که باید استثنا شود]. شما نمی دانید پدران و فرزندانتان کدام یک برای شما سودمندترند. [این ]فرضی است از جانب خدا، زیرا خداوند دانای حکیم است.'),
(505, 4, 12, 'و نیمی از میراث همسرانتان از آنِ شما [شوهران‌] است اگر آنان فرزندی نداشته باشند؛ و اگر فرزندی داشته باشند یک چهارم ماترک آنان از آنِ شماست، [البته‌] پس از انجام وصیتی که بدان سفارش کرده‌اند یا دَینی [که باید استثنا شود]، و یک چهارم از میراث شما برای آنان است اگر شما فرزندی نداشته باشید؛ و اگر فرزندی داشته باشید، یک هشتم برای میراث شما از ایشان خواهد بود، [البته‌] پس از انجام وصیتی که بدان سفارش کرده‌اید یا دَینی [که باید استثنا شود]، و اگر مرد یا زنی که از او ارث می‌برند کلاله [=بی‌فرزند و بی‌پدر و مادر] باشد و برای او برادر یا خواهری باشد، پس برای هر یک از آن دو، یک ششم [ماترک‌] است؛ و اگر آنان بیش از این باشند در یک سوم [ماترک‌] مشارکت دارند، [البته‌] پس از انجام وصیتی که بدان سفارش شده یا دَینی که [باید استثنا شود، به شرط آنکه از این طریق‌] زیانی [به ورثه‌] نرساند. این است سفارش خدا و خداست که دانای بردبار است.'),
(506, 4, 13, 'اینها احکام الهی است، و هر کس از خدا و پیامبر او اطاعت کند، وی را به باغهایی درآورد که از زیر [درختان‌] آن نهرها روان است. در آن جاودانه‌اند، و این همان کامیابی بزرگ است.'),
(507, 4, 14, 'و هر کس از خدا و پیامبر او نافرمانی کند و از حدود مقرر او تجاوز نماید، وی را در آتشی درآورد که همواره در آن خواهد بود و برای او عذابی خفت‌آور است.'),
(508, 4, 15, 'و از زنان شما، کسانی که مرتکب زنا می‌شوند، چهار تن از میان خود [مسلمانان‌] بر آنان گواه گیرید؛ پس اگر شهادت دادند، آنان [=زنان‌] را در خانه‌ها نگاه دارید تا مرگشان فرا رسد یا خدا راهی برای آنان قرار دهد.'),
(509, 4, 16, 'و از میان شما، آن دو تن را که مرتکب زشتکاری می‌شوند، آزارشان دهید؛ پس اگر توبه کردند و درستکار شدند از آنان صرفنظر کنید، زیرا خداوند توبه‌پذیر مهربان است.'),
(510, 4, 17, 'توبه، نزد خداوند، تنها برای کسانی است که از روی نادانی مرتکب گناه می‌شوند، سپس به زودی توبه می‌کنند؛ اینانند که خدا توبه‌شان را می‌پذیرد، و خداوند دانای حکیم است.'),
(511, 4, 18, 'و توبه کسانی که گناه می‌کنند، تا وقتی که مرگ یکی از ایشان دررسد، می‌گوید: «اکنون توبه کردم»، پذیرفته نیست؛ و [نیز توبه‌] کسانی که در حال کفر می‌میرند، پذیرفته نخواهد بود، آنانند که برایشان عذابی دردناک آماده کرده‌ایم.'),
(512, 4, 19, 'ای کسانی که ایمان آورده‌اید، برای شما حلال نیست که زنان را به اکراه ارث برید؛ و آنان را زیر فشار مگذارید تا بخشی از آنچه را به آنان داده‌اید [از چنگشان به در] برید، مگر آنکه مرتکب زشتکاری آشکاری شوند، و با آنها بشایستگی رفتار کنید؛ و اگر از آنان خوشتان نیامد، پس چه بسا چیزی را خوش نمی‌دارید و خدا در آن مصلحت فراوان قرار می‌دهد.'),
(513, 4, 20, 'و اگر خواستید همسری [دیگر] به جای همسر [پیشین خود] ستانید، و به یکی از آنان مال فراوانی داده باشید، چیزی از او پس مگیرید. آیا می‌خواهید آن [مال‌] را به بهتان و گناه آشکار بگیرید؟'),
(514, 4, 21, 'و چگونه آن [مَهر] را می‌ستانید با آنکه از یکدیگر کام گرفته‌اید، و آنان از شما پیمانی استوار گرفته‌اند؟'),
(515, 4, 22, 'و با زنانی که پدرانتان به ازدواج خود درآورده‌اند، نکاح مکنید؛ مگر آنچه که پیشتر رخ داده است، چرا که آن، زشتکاری و [مایه‌] دشمنی، و بد راهی بوده است.'),
(516, 4, 23, '[نکاح اینان‌] بر شما حرام شده است: مادرانتان، و دخترانتان، و خواهرانتان، و عمه‌هایتان، و خاله هایتان، و دختران برادر، و دختران خواهر، و مادرهایتان که به شما شیر داده‌اند، و خواهران رضاعی شما، و مادران زنانتان، و دختران همسرانتان که [آنها دختران‌] در دامان شما پرورش یافته‌اند و با آن همسران همبستر شده‌اید -پس اگر با آنها همبستر نشده‌اید بر شما گناهی نیست [که با دخترانشان ازدواج کنید]- و زنان پسرانتان که از پشت خودتان هستند، و جمع دو خواهر با همدیگر -مگر آنچه که در گذشته رخ داده باشد- که خداوند آمرزنده مهربان است.'),
(517, 4, 24, 'و زنان شوهردار [نیز بر شما حرام شده است‌] به استثنای زنانی که مالک آنان شده‌اید؛ [این‌] فریضه الهی است که بر شما مقرر گردیده است. و غیر از این [زنان نامبرده‌]، برای شما حلال است که [زنان دیگر را] به وسیله اموال خود طلب کنید -در صورتی که پاکدامن باشید و زناکار نباشید- و زنانی را که متعه کرده‌اید، مَهرشان را به عنوان فریضه‌ای به آنان بدهید، و بر شما گناهی نیست که پس از [تعیین مبلغ‌] مقرر، با یکدیگر توافق کنید [که مدت عقد یا مَهر را کم یا زیاد کنید]؛ مسلماً خداوند دانای حکیم است.'),
(518, 4, 25, 'و هر کس از شما، از نظر مالی نمی‌تواند زنان [آزاد] پاکدامن با ایمان را به همسری [خود] درآورد، پس با دختران جوانسال با ایمان شما که مالک آنان هستید [ازدواج کند]؛ و خدا به ایمان شما داناتر است. [همه‌] از یکدیگرید. پس آنان را با اجازه خانواده‌شان به همسری [خود] درآورید و مَهرشان را به طور پسندیده به آنان بدهید [به شرط آنکه‌] پاکدامن باشند نه زناکار، و دوست‌گیران پنهانی نباشند. پس چون به ازدواج [شما] درآمدند، اگر مرتکب فحشا شدند، پس بر آنان نیمی از عذاب [=مجازات‌] زنان آزاد است. این [پیشنهاد زناشویی با کنیزان‌] برای کسی از شماست که از آلایش گناه بیم دارد؛ و صبر کردن، برای شما بهتر است، و خداوند آمرزنده مهربان است.'),
(519, 4, 26, 'خدا می‌خواهد برای شما توضیح دهد، و راه [و رسم‌] کسانی را که پیش از شما بوده‌اند به شما بنمایاند، و بر شما ببخشاید، و خدا دانای حکیم است.'),
(520, 4, 27, 'خدا می‌خواهد تا بر شما ببخشاید؛ و کسانی که از خواسته‌ها[ی نفسانی‌] پیروی می‌کنند می‌خواهند شما دستخوش انحرافی بزرگ شوید.'),
(521, 4, 28, 'خدا می‌خواهد تا بارتان را سبک گرداند؛ و [می‌داند که‌] انسان، ناتوان آفریده شده است.'),
(522, 4, 29, 'ای کسانی که ایمان آورده‌اید، اموال همدیگر را به ناروا مخورید -مگر آنکه داد و ستدی با تراضی یکدیگر، از شما [انجام گرفته‌] باشد- و خودتان را مکشید، زیرا خدا همواره با شما مهربان است.'),
(523, 4, 30, 'و هر کس از روی تجاوز و ستم چنین کند، به زودی وی را در آتشی درآوریم، و این کار بر خدا آسان است.'),
(524, 4, 31, 'اگر از گناهان بزرگی که از آن‌[ها] نهی شده‌اید دوری گزینید، بدیهای شما را از شما می زداییم، و شما را در جایگاهی ارجمند درمی‌آوریم.'),
(525, 4, 32, 'و زنهار، آنچه را خداوند به [سبب‌] آن، بعضی از شما را بر بعضی [دیگر] برتری داده آرزو مکنید. برای مردان از آنچه [به اختیار] کسب کرده‌اند بهره‌ای است، و برای زنان [نیز] از آنچه [به اختیار] کسب کرده‌اند بهره‌ای است. و از فضل خدا درخواست کنید، که خدا به هر چیزی داناست.'),
(526, 4, 33, 'و از آنچه پدر و مادر و خویشاوندان، و کسانی که شما [با آنان‌] پیمان بسته‌اید، بر جای گذاشته‌اند، برای هر یک [از مردان و زنان‌]، وارثانی قرار داده‌ایم. پس نصیبشان را به ایشان بدهید، زیرا خدا همواره بر هر چیزی گواه است.'),
(527, 4, 34, 'مردان، سرپرست زنانند، به دلیل آنکه خدا برخی از ایشان را بر برخی برتری داده و [نیز] به دلیل آنکه از اموالشان خرج می‌کنند. پس، زنان درستکار، فرمانبردارند [و] به پاس آنچه خدا [برای آنان‌] حفظ کرده، اسرار [شوهران خود] را حفظ می‌کنند. و زنانی را که از نافرمانی آنان بیم دارید [نخست‌] پندشان دهید و [بعد] در خوابگاه‌ها از ایشان دوری کنید و [اگر تاثیر نکرد] آنان را بزنید؛ پس اگر شما را اطاعت کردند [دیگر] بر آنها هیچ راهی [برای سرزنش‌] مجویید، که خدا والای بزرگ است.'),
(528, 4, 35, 'و اگر از جدایی میان آن دو [: زن و شوهر] بیم دارید پس داوری از خانواده آن [شوهر] و داوری از خانواده آن [زن‌] تعیین کنید. اگر سرِ سازگاری دارند، خدا میان آن دو سازگاری خواهد داد. آری! خدا دانای آگاه است.'),
(529, 4, 36, 'و خدا را بپرستید، و چیزی را با او شریک مگردانید؛ و به پدر و مادر احسان کنید؛ و در باره خویشاوندان و یتیمان و مستمندان و همسایه خویش و همسایه بیگانه و همنشین و در راه‌مانده و بردگان خود [نیکی کنید]، که خدا کسی را که متکبر و فخرفروش است دوست نمی‌دارد:'),
(530, 4, 37, 'همان کسانی که بخل می‌ورزند، و مردم را به بخل وامی‌دارند، و آنچه را خداوند از فضل خویش بدانها ارزانی داشته پوشیده می‌دارند. و برای کافران عذابی خوارکننده آماده کرده‌ایم.'),
(531, 4, 38, 'و کسانی که اموالشان را برای نشان‌دادن به مردم انفاق می‌کنند، و به خدا و روز بازپسین ایمان ندارند. و هر کس شیطان یار او باشد، چه بد همدمی است.'),
(532, 4, 39, 'و اگر به خدا و روز بازپسین ایمان می‌آوردند، و از آنچه خدا به آنان روزی داده، انفاق می‌کردند، چه زیانی برایشان داشت؟ و خدا به [کار] آنان داناست.'),
(533, 4, 40, 'در حقیقت، خدا هم‌وزن ذره‌ای ستم نمی‌کند و اگر [آن ذره، کار] نیکی باشد دو چندانش می‌کند، و از نزد خویش پاداشی بزرگ می‌بخشد.'),
(534, 4, 41, 'پس چگونه است [حالشان‌] آنگاه که از هر امتی گواهی آوریم، و تو را بر آنان گواه آوریم؟'),
(535, 4, 42, 'آن روز، کسانی که کفر ورزیده‌اند، و از پیامبر [خدا] نافرمانی کرده‌اند، آرزو می‌کنند که ای کاش با خاک یکسان می‌شدند. و از خدا هیچ سخنی را پوشیده نمی‌توانند داشت.'),
(536, 4, 43, 'ای کسانی که ایمان آورده‌اید، در حال مستی به نماز نزدیک نشوید تا زمانی که بدانید چه می‌گویید؛ و [نیز] در حال جنابت [وارد نماز نشوید] -مگر اینکه راهگذر باشید- تا غسل کنید؛ و اگر بیمارید یا در سفرید یا یکی از شما از قضای حاجت آمد یا با زنان آمیزش کرده‌اید و آب نیافته‌اید، پس بر خاکی پاک تیمم کنید، و صورت و دستهایتان را مسح نمایید، که خدا بخشنده و آمرزنده است.'),
(537, 4, 44, 'آیا به کسانی که بهره‌ای از کتاب یافته‌اند ننگریستی؟ گمراهی را می‌خرند و می‌خواهند شما [نیز] گمراه شوید.'),
(538, 4, 45, 'و خدا به [حال‌] دشمنان شما داناتر است؛ کافی است که خدا سرپرست [شما] باشد، و کافی است که خدا یاور [شما] باشد.'),
(539, 4, 46, 'برخی از آنان که یهودی‌اند، کلمات را از جاهای خود برمی‌گردانند، و با پیچانیدن زبان خود و به قصد طعنه زدن در دین [اسلام، با درآمیختن عبری به عربی‌] می‌گویند: «شنیدیم و نافرمانی کردیم؛ و بشنو [که کاش‌] ناشنوا گردی.» و [نیز از روی استهزا می‌گویند:] «راعنا» [که در عربی یعنی: به ما التفات کن، ولی در عبری یعنی: خبیث ما،] و اگر آنان می‌گفتند: «شنیدیم و فرمان بردیم، و بشنو و به ما بنگر»، قطعاً برای آنان بهتر و درست‌تر بود، ولی خدا آنان را به علّت کفرشان لعنت کرد، در نتیجه جز [گروهی‌] اندک ایمان نمی‌آورند.'),
(540, 4, 47, 'ای کسانی که به شما کتاب داده شده است، به آنچه فرو فرستادیم و تصدیق‌کننده همان چیزی است که با شماست ایمان بیاورید، پیش از آنکه چهره‌هایی را محو کنیم و در نتیجه آنها را به قهقرا بازگردانیم؛ یا همچنانکه «اصحاب سبت» را لعنت کردیم؛ آنان را [نیز] لعنت کنیم، و فرمان خدا همواره تحقق یافته است.'),
(541, 4, 48, 'مسلماً خدا، این را که به او شرک ورزیده شود نمی‌بخشاید و غیر از آن را برای هر که بخواهد می‌بخشاید، و هر کس به خدا شرک ورزد، به یقین گناهی بزرگ بربافته است.'),
(542, 4, 49, 'آیا به کسانی که خویشتن را پاک می‌شمارند ننگریسته‌ای؟ [چنین نیست،] بلکه خداست که هر که را بخواهد پاک می‌گرداند، و به قدر نخ روی هسته خرمایی ستم نمی‌بینند.'),
(543, 4, 50, 'ببین چگونه بر خدا دروغ می‌بندند. و بس است که این، یک گناه آشکار باشد.'),
(544, 4, 51, 'آیا کسانی را که از کتاب [آسمانی‌] نصیبی یافته‌اند ندیده‌ای؟ که به «جبت» و «طاغوت» ایمان دارند، و در باره کسانی که کفر ورزیده‌اند می‌گویند: «اینان از کسانی که ایمان آورده‌اند راه‌یافته‌ترند.»'),
(545, 4, 52, 'اینانند که خدا لعنتشان کرده، و هر که را خدا لعنت کند هرگز برای او یاوری نخواهی یافت.'),
(546, 4, 53, 'آیا آنان نصیبی از حکومت دارند؟ [اگر هم داشتند،] به قدر نقطه پشت هسته خرمایی [چیزی‌] به مردم نمی‌دادند.'),
(547, 4, 54, 'بلکه به مردم، برای آنچه خدا از فضل خویش به آنان عطا کرده رشک می‌ورزند؛ در حقیقت، ما به خاندان ابراهیم کتاب و حکمت دادیم، و به آنان ملکی بزرگ بخشیدیم.'),
(548, 4, 55, 'پس برخی از آنان به وی ایمان آوردند، و برخی از ایشان از او روی برتافتند، و [برای آنان‌] دوزخ پرشراره بس است.'),
(549, 4, 56, 'به زودی کسانی را که به آیات ما کفر ورزیده‌اند، در آتشی [سوزان‌] درآوریم؛ که هر چه پوستشان بریان گردد، پوستهای دیگری بر جایش نهیم تا عذاب را بچشند. آری، خداوند توانای حکیم است.'),
(550, 4, 57, 'و به زودی کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، در باغهایی که از زیرِ [درختان‌] آن نهرها روان است درآوریم. برای همیشه در آن جاودانند، و در آنجا همسرانی پاکیزه دارند، و آنان را در سایه‌ای پایدار درآوریم.'),
(551, 4, 58, 'خدا به شما فرمان می‌دهد که سپرده‌ها را به صاحبان آنها رد کنید؛ و چون میان مردم داوری می‌کنید، به عدالت داوری کنید. در حقیقت، نیکو چیزی است که خدا شما را به آن پند می‌دهد. خدا شنوای بیناست.'),
(552, 4, 59, 'ای کسانی که ایمان آورده‌اید، خدا را اطاعت کنید و پیامبر و اولیای امر خود را [نیز] اطاعت کنید؛ پس هر گاه در امری [دینی‌] اختلاف نظر یافتید، اگر به خدا و روز بازپسین ایمان دارید، آن را به [کتاب‌] خدا و [سنت‌] پیامبر [او] عرضه بدارید، این بهتر و نیک‌فرجام‌تر است.'),
(553, 4, 60, 'آیا ندیده‌ای کسانی را که می‌پندارند به آنچه به سوی تو نازل شده و [به‌] آنچه پیش از تو نازل گردیده، ایمان آورده‌اند [با این همه‌] می‌خواهند داوری میان خود را به سوی طاغوت ببرند، با آنکه قطعاً فرمان یافته‌اند که بدان کفر ورزند، و[لی‌] شیطان می‌خواهد آنان را به گمراهی دوری دراندازد.'),
(554, 4, 61, 'و چون به ایشان گفته شود: «به سوی آنچه خدا نازل کرده و به سوی پیامبر [او] بیایید»، منافقان را می‌بینی که از تو سخت، روی برمی‌تابند.'),
(555, 4, 62, 'پس چگونه، هنگامی که به [سزای‌] کار و کردار پیشینشان مصیبتی به آنان می‌رسد، نزد تو می‌آیند و به خدا سوگند می‌خورند که ما جز نیکویی و موافقت قصدی نداشتیم؟'),
(556, 4, 63, 'اینان همان کسانند که خدا می‌داند چه در دل دارند. پس، از آنان روی برتاب، و[لی‌] پندشان ده، و با آنها سخنی رسا که در دلشان [مؤثّر] افتد، بگوی.'),
(557, 4, 64, 'و ما هیچ پیامبری را نفرستادیم مگر آنکه به توفیق الهی از او اطاعت کنند. و اگر آنان وقتی به خود ستم کرده بودند، پیش تو می‌آمدند و از خدا آمرزش می‌خواستند و پیامبر [نیز] برای آنان طلب آمرزش می‌کرد، قطعاً خدا را توبه‌پذیرِ مهربان می‌یافتند.'),
(558, 4, 65, 'ولی چنین نیست، به پروردگارت قسم که ایمان نمی‌آورند، مگر آنکه تو را در مورد آنچه میان آنان مایه اختلاف است داور گردانند؛ سپس از حکمی که کرده‌ای در دلهایشان احساس ناراحتی [و تردید] نکنند، و کاملاً سرِ تسلیم فرود آورند.'),
(559, 4, 66, 'و اگر بر آنان مقرر می‌کردیم که تن به کشتن دهید، یا از خانه‌های خود به در آیید، جز اندکی از ایشان آن را به کار نمی‌بستند؛ و اگر آنان آنچه را بدان پند داده می‌شوند به کار می‌بستند، قطعاً برایشان بهتر و در ثبات قدم ایشان مؤثرتر بود؛'),
(560, 4, 67, 'و در آن صورت، [ما هم‌] از نزد خویش، یقیناً پاداشی بزرگ به آنان می‌دادیم؛'),
(561, 4, 68, 'و قطعاً آنان را به راهی راست هدایت می‌کردیم.'),
(562, 4, 69, 'و کسانی که از خدا و پیامبر اطاعت کنند، در زمره کسانی خواهند بود که خدا ایشان را گرامی داشته [یعنی‌] با پیامبران و راستان و شهیدان و شایستگانند و آنان چه نیکو همدمانند.'),
(563, 4, 70, 'این تفضّل از جانب خداست، و خدا بس داناست.'),
(564, 4, 71, 'ای کسانی که ایمان آورده‌اید، [در برابر دشمن‌] آماده باشید [=اسلحه خود را برگیرید] و گروه گروه [به جهاد] بیرون روید یا به طور جمعی روانه شوید.'),
(565, 4, 72, 'و قطعاً از میان شما کسی است که کندی به خرج دهد؛ پس اگر آسیبی به شما رسد گوید: «راستی خدا بر من نعمت بخشید که با آنان حاضر نبودم.»'),
(566, 4, 73, 'و اگر غنیمتی از خدا به شما برسد -چنانکه گویی میان شما و میان او [رابطه‌] دوستی نبوده- خواهد گفت: «کاش من با آنان بودم و به نوای بزرگی می‌رسیدم.»'),
(567, 4, 74, 'پس، باید کسانی که زندگی دنیا را به آخرت سودا می‌کنند در راه خدا بجنگند؛ و هر کس در راه خدا بجنگد و کشته یا پیروز شود، به زودی پاداشی بزرگ به او خواهیم داد.'),
(568, 4, 75, 'و چرا شما در راه خدا [و در راه نجات‌] مردان و زنان و کودکان مستضعف نمی‌جنگید؟ همانان که می‌گویند: «پروردگارا، ما را از این شهری که مردمش ستم‌پیشه‌اند بیرون ببر، و از جانب خود برای ما سرپرستی قرار ده، و از نزد خویش یاوری برای ما تعیین فرما.»'),
(569, 4, 76, 'کسانی که ایمان آورده‌اند، در راه خدا کارزار می‌کنند. و کسانی که کافر شده‌اند، در راه طاغوت می‌جنگند. پس با یاران شیطان بجنگید که نیرنگ شیطان [در نهایت‌] ضعیف است.'),
(570, 4, 77, 'آیا ندیدی کسانی را که به آنان گفته شد: «[فعلاً] دست [از جنگ‌] بدارید، و نماز را برپا کنید و زکات بدهید»، و[لی‌] همین که کارزار بر آنان مقرر شد، بناگاه گروهی از آنان از مردم [=مشرکان مکّه‌] ترسیدند مانند ترس از خدا یا ترسی سخت‌تر. و گفتند: «پروردگارا، چرا بر ما کارزار مقرّر داشتی؟ چرا ما را تا مدتی کوتاه مهلت ندادی؟» بگو: «برخورداری [از این‌] دنیا اندک، و برای کسی که تقوا پیشه کرده، آخرت بهتر است، و [در آنجا] به قدر نخ هسته خرمایی بر شما ستم نخواهد رفت.»'),
(571, 4, 78, 'هر کجا باشید، شما را مرگ درمی‌یابد؛ هر چند در بُرجهای استوار باشید. و اگر [پیشامد] خوبی به آنان برسد، می‌گویند: «این از جانب خداست»؛ و چون صدمه‌ای به ایشان برسد، می‌گویند: «این از طرف توست.» بگو: «همه از جانب خداست.» [آخر ]این قوم را چه شده است که نمی‌خواهند سخنی را [درست‌] دریابند؟'),
(572, 4, 79, 'هر چه از خوبیها به تو می‌رسد از جانب خداست؛ و آنچه از بدی به تو می‌رسد از خود توست؛ و تو را به پیامبری، برای مردم فرستادیم، و گواه بودن خدا بس است.'),
(573, 4, 80, 'هر کس از پیامبر فرمان بَرد، در حقیقت، خدا را فرمان برده؛ و هر کس رویگردان شود، ما تو را بر ایشان نگهبان نفرستاده‌ایم.'),
(574, 4, 81, 'و می‌گویند: فرمانبرداریم، ولی چون از نزد تو بیرون می‌روند، جمعی از آنان شبانه، جز آنچه تو می‌گویی تدبیر می‌کنند. و خدا آنچه را که شبانه در سر می‌پرورند، می‌نگارد. پس، از ایشان روی برتاب و بر خدا توکل کن، و خدا بس کارساز است.'),
(575, 4, 82, 'آیا در [معانیِ‌] قرآن نمی‌اندیشند؟ اگر از جانب غیر خدا بود قطعاً در آن اختلاف بسیاری می‌یافتند.'),
(576, 4, 83, 'و چون خبری [حاکی‌] از ایمنی یا وحشت به آنان برسد، انتشارش دهند؛ و اگر آن را به پیامبر و اولیای امر خود ارجاع کنند، قطعاً از میان آنان کسانی‌اند که [می‌توانند درست و نادرست‌] آن را دریابند، و اگر فضل خدا و رحمت او بر شما نبود، مسلماً جز [شمار] اندکی، از شیطان پیروی می‌کردید.'),
(577, 4, 84, 'پس در راه خدا پیکار کن؛ جز عهده‌دار شخص خود نیستی. و[لی‌] مؤمنان را [به مبارزه‌] برانگیز. باشد که خداوند آسیب کسانی را که کفر ورزیده‌اند [از آنان‌] باز دارد، و خداست که قدرتش بیشتر و کیفرش سخت‌تر است.'),
(578, 4, 85, 'هر کس شفاعت پسندیده کند، برای وی از آن نصیبی خواهد بود و هر کس شفاعت ناپسندیده‌ای کند، برای او از آن [نیز] سهمی خواهد بود. و خدا همواره به هر چیزی تواناست.'),
(579, 4, 86, 'و چون به شما درود گفته شد، شما به [صورتی‌] بهتر از آن درود گویید، یا همان را [در پاسخ‌] برگردانید، که خدا همواره به هر چیزی حسابرس است.'),
(580, 4, 87, 'خداوند کسی است که هیچ معبودی جز او نیست. به یقین، در روز رستاخیز -که هیچ شکی در آن نیست- شما را گرد خواهد آورد، و راستگوتر از خدا در سخن کیست؟'),
(581, 4, 88, 'شما را چه شده است که در باره منافقان، دو دسته شده‌اید؟ با اینکه خدا آنان را به [سزای‌] آنچه انجام داده‌اند سرنگون کرده است. آیا می‌خواهید کسی را که خدا در گمراهی‌اش وانهاده است به راه آورید؟ و حال آنکه هر که را خدا در گمراهی‌اش وانهد هرگز راهی برای [هدایت‌] او نخواهی یافت.'),
(582, 4, 89, 'همان گونه که خودشان کافر شده‌اند، آرزو دارند [که شما نیز] کافر شوید، تا با هم برابر باشید. پس زنهار، از میان ایشان برای خود، دوستانی اختیار مکنید تا آنکه در راه خدا هجرت کنند. پس اگر روی برتافتند، هر کجا آنان را یافتید به اسارت بگیرید و بکشیدشان؛ و از ایشان یار و یاوری برای خود مگیرید.'),
(583, 4, 90, 'مگر کسانی که با گروهی که میان شما و میان آنان پیمانی است، پیوند داشته باشند، یا نزد شما بیایند در حالی که سینه آنان از جنگیدن با شما یا جنگیدن با قوم خود، به تنگ آمده باشد. و اگر خدا می‌خواست، قطعاً آنان را بر شما چیره می‌کرد و حتماً با شما می‌جنگیدند. پس اگر از شما کناره‌گیری کردند و با شما نجنگیدند و با شما طرح صلح افکندند، [دیگر] خدا برای شما راهی [برای تجاوز] بر آنان قرار نداده است.'),
(584, 4, 91, 'به زودی، گروهی دیگر را خواهید یافت که می‌خواهند از شما آسوده خاطر و از قوم خود [نیز] ایمن باشند. هر بار که به فتنه بازگردانده شوند، سر در آن فرو می‌برند. پس اگر از شما کناره‌گیری نکردند و به شما پیشنهاد صلح نکردند و از شما دست برنداشتند، هر کجا آنان را یافتید به اسارت بگیرید و بکشیدشان. آنانند که ما برای شما علیه ایشان تسلطی آشکار قرار داده‌ایم.'),
(585, 4, 92, 'و هیچ مؤمنی را نسزد که مؤمنی را -جز به اشتباه- بکشد. و هر کس مؤمنی را به اشتباه کشت، باید بنده مؤمنی را آزاد و به خانواده او خونبها پرداخت کند؛ مگر اینکه آنان گذشت کنند. و اگر [مقتول‌] از گروهی است که دشمنان شمایند و [خود] وی مؤمن است، [قاتل‌] باید بنده مؤمنی را آزاد کند [و پرداخت خونبها لازم نیست‌]. و اگر [مقتول‌] از گروهی است که میان شما و میان آنان پیمانی است، باید به خانواده وی خونبها پرداخت نماید و بنده مؤمنی را آزاد کند. و هر کس [بنده‌] نیافت، باید دو ماه پیاپی -به عنوان توبه‌ای از جانب خدا- روزه بدارد، و خدا همواره دانای سنجیده‌کار است.'),
(586, 4, 93, 'و هر کس عمداً مؤمنی را بکشد، کیفرش دوزخ است که در آن ماندگار خواهد بود؛ و خدا بر او خشم می‌گیرد و لعنتش می‌کند و عذابی بزرگ برایش آماده ساخته است.'),
(587, 4, 94, 'ای کسانی که ایمان آورده‌اید، چون در راه خدا سفر می‌کنید [خوب‌] رسیدگی کنید و به کسی که نزد شما [اظهار] اسلام می‌کند؛ مگویید: «تو مؤمن نیستی» [تا بدین بهانه‌] متاع زندگی دنیا را بجویید، چرا که غنیمتهای فراوان نزد خداست. قبلا خودتان [نیز] همین گونه بودید، و خدا بر شما منت نهاد. پس خوب رسیدگی کنید، که خدا همواره به آنچه انجام می‌دهید آگاه است.'),
(588, 4, 95, 'مؤمنان خانه‌نشین که زیان‌دیده نیستند با آن مجاهدانی که با مال و جان خود در راه خدا جهاد می‌کنند یکسان نمی‌باشند. خداوند، کسانی را که با مال و جان خود جهاد می‌کنند به درجه‌ای بر خانه‌نشینان مزیت بخشیده، و همه را خدا وعده [پاداش‌] نیکو داده، و[لی‌] مجاهدان را بر خانه‌نشینان به پاداشی بزرگ، برتری بخشیده است؛'),
(589, 4, 96, '[پاداش بزرگی که‌] به عنوان درجات و آمرزش و رحمتی از جانب او [نصیب آنان می‌شود]، و خدا آمرزنده مهربان است.'),
(590, 4, 97, 'کسانی که بر خویشتن ستمکار بوده‌اند، [وقتی‌] فرشتگان جانشان را می‌گیرند، می‌گویند: «در چه [حال‌] بودید؟» پاسخ می‌دهند: «ما در زمین از مستضعفان بودیم.» می‌گویند: «مگر زمین خدا وسیع نبود تا در آن مهاجرت کنید؟» پس آنان جایگاهشان دوزخ است، و [دوزخ‌] بد سرانجامی است.'),
(591, 4, 98, 'مگر آن مردان و زنان و کودکان فرودستی که چاره‌جویی نتوانند و راهی نیابند.'),
(592, 4, 99, 'پس آنان [که فی الجمله عذری دارند] باشد که خدا از ایشان درگذرد، که خدا همواره خطابخش و آمرزنده است.'),
(593, 4, 100, 'و هر که در راه خدا هجرت کند، در زمین اقامتگاه‌های فراوان و گشایشها خواهد یافت؛ و هر کس [به قصد] مهاجرت در راه خدا و پیامبر او، از خانه‌اش به درآید، سپس مرگش دررسد، پاداش او قطعاً بر خداست، و خدا آمرزنده مهربان است.'),
(594, 4, 101, 'و چون در زمین سفر کردید، اگر بیم داشتید که آنان که کفر ورزیده‌اند به شما آزار برسانند، گناهی بر شما نیست که نماز را کوتاه کنید، چرا که کافران پیوسته برای شما دشمنی آشکارند.'),
(595, 4, 102, 'و هر گاه در میان ایشان بودی و برایشان نماز برپا داشتی، پس باید گروهی از آنان با تو [به نماز] ایستند؛ و باید جنگ‌افزارهای خود را برگیرند؛ و چون به سجده رفتند [و نماز را تمام کردند]، باید پشت سر شما قرار گیرند، و گروه دیگری که نماز نکرده‌اند باید بیایند و با تو نماز گزارند و البته جانب احتیاط را فرو نگذارند و جنگ‌افزارهای خود را برگیرند. [زیرا] کافران آرزو می‌کنند که شما از جنگ‌افزارها و ساز و برگ خود غافل شوید تا ناگهان بر شما یورش برند. و اگر از باران در زحمتید، یا بیمارید، گناهی بر شما نیست که جنگ افزارهای خود را بر زمین نهید، ولی مواظب خود باشید. بی گمان، خدا برای کافران عذاب خفت‌آوری آماده کرده است.'),
(596, 4, 103, 'و چون نماز را به جای آوردید، خدا را [در همه حال‌] ایستاده و نشسته و بر پهلوآرمیده، یاد کنید. پس چون آسوده‌خاطر شدید، نماز را [به طور کامل‌] به پا دارید، زیرا نماز بر مؤمنان، در اوقات معین مقرر شده است.'),
(597, 4, 104, 'و در تعقیب گروه [دشمنان‌] سستی نورزید. اگر شما درد می‌کشید، آنان [نیز] همان گونه که شما درد می‌کشید، درد می‌کشند، و حال آنکه شما چیزهایی از خدا امید دارید که آنها امید ندارند، و خدا همواره دانای سنجیده‌کار است.'),
(598, 4, 105, 'ما این کتاب را به حق بر تو نازل کردیم، تا میان مردم به [موجب‌] آنچه خدا به تو آموخته داوری کنی، و زنهار جانبدار خیانتکاران مباش.'),
(599, 4, 106, 'و از خدا آمرزش بخواه، که خدا آمرزنده مهربان است.'),
(600, 4, 107, 'و از کسانی که به خویشتن خیانت می‌کنند دفاع مکن، که خداوند هر کس را که خیانتگر و گناه‌پیشه باشد دوست ندارد.'),
(601, 4, 108, '[کارهای ناروای خود را] از مردم پنهان می‌دارند، و[لی نمی‌توانند] از خدا پنهان دارند، و چون شبانگاه به چاره‌اندیشی می‌پردازند و سخنانی می‌گویند که وی [بدان‌] خشنود نیست، او با آنان است. و خدا به آنچه انجام می‌دهند همواره احاطه دارد.'),
(602, 4, 109, 'هان، شما همانان هستید که در زندگی دنیا از ایشان جانبداری کردید، پس چه کسی روز رستاخیز از آنان در برابر خدا جانبداری خواهد کرد؟ یا چه کسی حمایتگر [و مدافع‌] آنان تواند بود؟'),
(603, 4, 110, 'و هر کس کار بدی کند، یا بر خویشتن ستم ورزد؛ سپس از خدا آمرزش بخواهد، خدا را آمرزنده مهربان خواهد یافت.'),
(604, 4, 111, 'و هر کس گناهی مرتکب شود، فقط آن را به زیان خود مرتکب شده، و خدا همواره دانای سنجیده‌کار است.'),
(605, 4, 112, 'و هر کس خطا یا گناهی مرتکب شود؛ سپس آن را به بی‌گناهی نسبت دهد، قطعاً بهتان و گناه آشکاری بر دوش کشیده است.'),
(606, 4, 113, 'و اگر فضل خدا و رحمت او بر تو نبود، طایفه‌ای از ایشان آهنگ آن داشتند که تو را از راه به در کنند، و[لی‌] جز خودشان [کسی‌] را گمراه نمی‌سازند، و هیچ گونه زیانی به تو نمی‌رسانند. و خدا کتاب و حکمت بر تو نازل کرد و آنچه را نمی‌دانستی به تو آموخت، و تفضل خدا بر تو همواره بزرگ بود.'),
(607, 4, 114, 'در بسیاری از رازگوییهای ایشان خیری نیست، مگر کسی که [بدین وسیله‌] به صدقه یا کار پسندیده یا سازشی میان مردم، فرمان دهد. و هر کس برای طلب خشنودی خدا چنین کند، به زودی او را پاداش بزرگی خواهیم داد.'),
(608, 4, 115, 'و هر کس، پس از آنکه راه هدایت برای او آشکار شد با پیامبر به مخالفت برخیزد، و [راهی‌] غیر راه مؤمنان در پیش گیرد، وی را بدانچه روی خود را بدان سو کرده واگذاریم و به دوزخش کشانیم، و چه بازگشتگاه بدی است.'),
(609, 4, 116, 'خداوند، این را که به او شرک آورده شود، نمی‌آمرزد؛ و فروتر از آن را بر هر که بخواهد می‌بخشاید. و هر کس به خدا شرک ورزد، قطعاً دچار گمراهی دور و درازی شده است.'),
(610, 4, 117, '[مشرکان‌]، به جای او، جز بتهای مادینه را [به دعا] نمی‌خوانند، و جز شیطان سرکش را نمی‌خوانند.'),
(611, 4, 118, 'خدا لعنتش کند، [وقتی که‌] گفت: «بی‌گمان، از میان بندگانت نصیبی معین [برای خود] برخواهم گرفت.'),
(612, 4, 119, 'و آنان را سخت گمراه و دچار آرزوهای دور و دراز خواهم کرد، و وادارشان می‌کنم تا گوشهای دامها را شکاف دهند، و وادارشان می‌کنم تا آفریده خدا را دگرگون سازند.» و[لی‌] هر کس به جای خدا، شیطان را دوست [خدا] گیرد، قطعاً دستخوش زیان آشکاری شده است.'),
(613, 4, 120, '[آری،] شیطان به آنان وعده می‌دهد، و ایشان را در آرزوها می‌افکند، و جز فریب به آنان وعده نمی‌دهد.'),
(614, 4, 121, 'آنان جایگاهشان جهنم است، و از آن راه گریزی ندارند.'),
(615, 4, 122, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به زودی آنان را در بوستانهایی که از زیر [درختان‌] آن، نهرها روان است درآوریم. همیشه در آن جاودانند. وعده خدا راست است و چه کسی در سخن، از خدا راستگوتر است؟'),
(616, 4, 123, '[پاداش و کیفر] به دلخواه شما و به دلخواه اهل کتاب نیست؛ هر کس بدی کند، در برابر آن کیفر می‌بیند، و جز خدا برای خود یار و مددکاری نمی‌یابد.'),
(617, 4, 124, 'و کسانی که کارهای شایسته کنند -چه مرد باشند یا زن- در حالی که مؤمن باشند، آنان داخل بهشت می‌شوند، و به قدر گودی پشت هسته خرمایی مورد ستم قرار نمی‌گیرند.'),
(618, 4, 125, 'و دین چه کسی بهتر است از آن کس که خود را تسلیم خدا کرده و نیکوکار است و از آیین ابراهیم حق‌گرا پیروی نموده است؟ و خدا ابراهیم را دوست گرفت.'),
(619, 4, 126, 'و آنچه در آسمانها و آنچه در زمین است از آن خداست، و خدا همواره بر هر چیزی احاطه دارد.'),
(620, 4, 127, 'و در باره زنان، رأی تو را می‌پرسند. بگو: «خدا در باره آنان به شما فتوا می‌دهد، و [نیز] در باره آنچه در قرآن بر شما تلاوت می‌شود: در مورد زنان یتیمی که حق مقرر آنان را به ایشان نمی‌دهید و تمایل به ازدواج با آنان دارید، و [در باره‌] کودکان ناتوان و اینکه با یتیمان [چگونه‌] به داد رفتار کنید [پاسخگر شماست‌].» و هر کار نیکی انجام دهید، قطعاً خدا به آن داناست.'),
(621, 4, 128, 'و اگر زنی از شوهر خویش بیم ناسازگاری یا رویگردانی داشته باشد، بر آن دو گناهی نیست که از راه صلح با یکدیگر، به آشتی گرایند؛ که سازش بهتر است. و[لی‌] بخل [و بی‌گذشت بودن‌]، در نفوس، حضور [و غلبه‌] دارد؛ و اگر نیکی کنید و پرهیزگاری پیشه نمایید، قطعاً خدا به آنچه انجام می‌دهید آگاه است.'),
(622, 4, 129, 'و شما هرگز نمی‌توانید میان زنان عدالت کنید هر چند [بر عدالت‌] حریص باشید! پس به یک طرف یکسره تمایل نورزید تا آن [زن دیگر] را سرگشته [=بلا تکلیف‌] رها کنید. و اگر سازش نمایید و پرهیزگاری کنید، یقیناً خدا آمرزنده مهربان است.'),
(623, 4, 130, 'و اگر آن دو، از یکدیگر جدا شوند، خداوند هر یک را از گشایش خود بی‌نیاز گرداند، و خدا همواره گشایشگر حکیم است.'),
(624, 4, 131, 'و آنچه در آسمانها و آنچه در زمین است، از آنِ خداست. و ما به کسانی که پیش از شما به آنان کتاب داده شده، و [نیز] به شما سفارش کردیم که از خدا پروا کنید. و اگر کفر ورزید [چه باک؟ که‌] آنچه در آسمانها و آنچه در زمین است از آنِ خداست، و خدا بی نیاز ستوده‌[صفات‌] است.'),
(625, 4, 132, 'و آنچه در آسمانها و آنچه در زمین است از آنِ خداست، و خدا بس کارساز است.'),
(626, 4, 133, 'ای مردم، اگر [خدا] بخواهد، شما را [از میان‌] می‌برد و دیگران را [پدید ] می‌آورد، و خدا بر این [کار] تواناست.'),
(627, 4, 134, 'هر کس پاداش دنیا بخواهد، پاداش دنیا و آخرت نزد خداست، و خدا شنوای بیناست.'),
(628, 4, 135, 'ای کسانی که ایمان آورده‌اید، پیوسته به عدالت قیام کنید و برای خدا گواهی دهید، هر چند به زیان خودتان یا [به زیان‌] پدر و مادر و خویشاوندان [شما] باشد. اگر [یکی از دو طرف دعوا] توانگر یا نیازمند باشد، باز خدا به آن دو [از شما ]سزاوارتر است؛ پس، از پی هوس نروید که [درنتیجه از حق‌] عدول کنید. و اگر به انحراف گرایید یا اعراض نمایید، قطعاً خدا به آنچه انجام می‌دهید آگاه است.'),
(629, 4, 136, 'ای کسانی که ایمان آورده‌اید، به خدا و پیامبر او و کتابی که بر پیامبرش فرو فرستاد، و کتابهایی که قبلا نازل کرده بگروید؛ و هر کس به خدا و فرشتگان او و کتابها و پیامبرانش و روز بازپسین کفر ورزد، در حقیقت دچار گمراهی دور و درازی شده است.'),
(630, 4, 137, 'کسانی که ایمان آوردند، سپس کافر شدند؛ و باز ایمان آوردند، سپس کافر شدند؛ آنگاه به کفر خود افزودند، قطعاً خدا آنان را نخواهد بخشید و راهی به ایشان نخواهد نمود.'),
(631, 4, 138, 'به منافقان خبر ده که عذابی دردناک [در پیش‌] خواهند داشت.'),
(632, 4, 139, 'همانان که غیر از مؤمنان، کافران را دوستان [خود] می‌گیرند. آیا سربلندی را نزد آنان می‌جویند؟ [این خیالی خام است،] چرا که عزت، همه از آنِ خداست.'),
(633, 4, 140, 'و البتّه [خدا] در کتاب [قرآن‌] بر شما نازل کرده که: هر گاه شنیدید آیات خدا مورد انکار و ریشخند قرار می‌گیرد، با آنان منشینید تا به سخنی غیر از آن درآیند، چرا که در این صورت شما هم مثل آنان خواهید بود. خداوند، منافقان و کافران را همگی در دوزخ گرد خواهد آورد.'),
(634, 4, 141, 'همانان که مترصّد شمایند؛ پس اگر از جانب خدا به شما فتحی برسد، می‌گویند: «مگر ما با شما نبودیم؟» و اگر برای کافران نصیبی باشد، می‌گویند: «مگر ما بر شما تسلّط نداشتیم و شما را از [ورود در جمع ]مؤمنان باز نمی‌داشتیم؟» پس خداوند، روز قیامت میان شما داوری می‌کند؛ و خداوند هرگز بر [زیان‌] مؤمنان، برای کافران راه [تسلّطی‌] قرار نداده است.'),
(635, 4, 142, 'منافقان، با خدا نیرنگ می‌کنند، و حال آنکه او با آنان نیرنگ خواهد کرد؛ و چون به نماز ایستند با کسالت برخیزند. با مردم ریا می‌کنند و خدا را جز اندکی یاد نمی‌کنند.'),
(636, 4, 143, 'میان آن [دو گروه‌] دو دلند؛ نه با اینانند و نه با آنان. و هر که را خدا گمراه کند، هرگز راهی برای [نجات‌] او نخواهی یافت.'),
(637, 4, 144, 'ای کسانی که ایمان آورده‌اید، به جای مؤمنان، کافران را به دوستی خود مگیرید. آیا می‌خواهید علیه خود حجّتی روشن برای خدا قرار دهید؟'),
(638, 4, 145, 'آری، منافقان در فروترین درجات دوزخند، و هرگز برای آنان یاوری نخواهی یافت.'),
(639, 4, 146, 'مگر کسانی که توبه کردند و [عمل خود را] اصلاح نمودند و به خدا تمسّک جُستند و دین خود را برای خدا خالص گردانیدند که [در نتیجه‌] آنان با مؤمنان خواهند بود، و به زودی خدا مؤمنان را پاداشی بزرگ خواهد بخشید.'),
(640, 4, 147, 'اگر سپاس بدارید و ایمان آورید، خدا می‌خواهد با عذاب شما چه کند؟ و خدا همواره سپاس‌پذیر [=حق‌شناس‌] داناست.'),
(641, 4, 148, 'خداوند، بانگ برداشتن به بدزبانی را دوست ندارد، مگر [از] کسی که بر او ستم رفته باشد، و خدا شنوای داناست.'),
(642, 4, 149, 'اگر خیری را آشکار کنید یا پنهانش دارید، یا از بدیی درگذرید، پس خدا درگذرنده تواناست.'),
(643, 4, 150, 'کسانی که به خدا و پیامبرانش کفر می‌ورزند، و می‌خواهند میان خدا و پیامبران او جدایی اندازند، و می‌گویند: «ما به بعضی ایمان داریم و بعضی را انکار می‌کنیم» و می‌خواهند میان این [دو]، راهی برای خود اختیار کنند،'),
(644, 4, 151, 'آنان در حقیقت کافرند؛ و ما برای کافران عذابی خفّت‌آور آماده کرده‌ایم.'),
(645, 4, 152, 'و کسانی که به خدا و پیامبرانش ایمان آورده و میان هیچ کدام از آنان فرق نمی‌گذارند، به زودی [خدا] پاداش آنان را عطا می‌کند، و خدا آمرزنده مهربان است.'),
(646, 4, 153, 'اهل کتاب از تو می‌خواهند که کتابی از آسمان [یکباره‌] بر آنان فرود آوری. البتّه از موسی بزرگتر از این را خواستند و گفتند: «خدا را آشکارا به ما بنمای.» پس به سزای ظلمشان صاعقه آنان را فرو گرفت. سپس، بعد از آنکه دلایل آشکار برایشان آمد، گوساله را [به پرستش‌] گرفتند، و ما از آن هم درگذشتیم و به موسی برهانی روشن عطا کردیم.'),
(647, 4, 154, 'و کوه طور را به یادبود پیمان [با] آنان، بالای سرشان افراشته داشتیم؛ و به آنان گفتیم: «سجده‌کنان از در درآیید» و [نیز] به آنان گفتیم: «در روز شنبه تجاوز مکنید.» و از ایشان پیمانی استوار گرفتیم.'),
(648, 4, 155, 'پس به [سزای‌] پیمان‌شکنی‌شان، و انکارشان نسبت به آیات خدا، و کشتار ناحق آنان [از] انبیا، و گفتارشان که: «دلهای ما در غلاف است» [لعنتشان کردیم‌] بلکه خدا به خاطر کفرشان بر دلهایشان مُهر زده و در نتیجه جز شماری اندک [از ایشان‌] ایمان نمی‌آورند.'),
(649, 4, 156, 'و [نیز] به سزای کفرشان و آن تهمت بزرگی که به مریم زدند،'),
(650, 4, 157, 'و گفته ایشان که: «ما مسیح، عیسی بن مریم، پیامبر خدا را کشتیم»، و حال آنکه آنان او را نکشتند و مصلوبش نکردند، لیکن امر بر آنان مشتبه شد؛ و کسانی که در باره او اختلاف کردند، قطعاً در مورد آن دچار شکّ شده‌اند و هیچ علمی بدان ندارند، جز آنکه از گمان پیروی می‌کنند، و یقیناً او را نکشتند.'),
(651, 4, 158, 'بلکه خدا او را به سوی خود بالا برد، و خدا توانا و حکیم است.'),
(652, 4, 159, 'و از اهل کتاب، کسی نیست مگر آنکه پیش از مرگ خود حتماً به او ایمان می‌آورد، و روز قیامت [عیسی نیز] بر آنان شاهد خواهد بود.'),
(653, 4, 160, 'پس به سزای ستمی که از یهودیان سر زد و به سبب آنکه [مردم را] بسیار از راه خدا باز داشتند، چیزهای پاکیزه‌ای را که بر آنان حلال شده بود حرام گردانیدیم؛'),
(654, 4, 161, 'و [به سبب‌] رباگرفتنشان -با آنکه از آن نهی شده بودند- و به ناروا مال مردم خوردنشان، و ما برای کافرانِ آنان عذابی دردناک آماده کرده‌ایم.'),
(655, 4, 162, 'لیکن راسخانِ آنان در دانش، و مؤمنان، به آنچه بر تو نازل شده و به آنچه پیش از تو نازل گردیده ایمان دارند و خوشا بر نمازگزاران، و زکات‌دهندگان و ایمان‌آورندگان به خدا و روز بازپسین که به زودی به آنان پاداشی بزرگ خواهیم داد.'),
(656, 4, 163, 'ما همچنانکه به نوح و پیامبران بعد از او، وحی کردیم، به تو [نیز] وحی کردیم؛ و به ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط و عیسی و ایوب و یونس و هارون و سلیمان [نیز] وحی نمودیم، و به داوود زبور بخشیدیم.'),
(657, 4, 164, 'و پیامبرانی [را فرستادیم‌] که در حقیقت [ماجرای‌] آنان را قبلاً بر تو حکایت نمودیم؛ و پیامبرانی [را نیز برانگیخته‌ایم‌] که [سرگذشت‌] ایشان را بر تو بازگو نکرده‌ایم. و خدا با موسی آشکارا سخن گفت.'),
(658, 4, 165, 'پیامبرانی که بشارتگر و هشداردهنده بودند، تا برای مردم، پس از [فرستادن‌] پیامبران، در مقابل خدا [بهانه و] حجّتی نباشد، و خدا توانا و حکیم است.'),
(659, 4, 166, 'لیکن خدا به [حقانیّت‌] آنچه بر تو نازل کرده است گواهی می‌دهد. [او] آن را به علم خویش نازل کرده است؛ و فرشتگان [نیز] گواهی می‌دهند. و کافی است خدا گواه باشد.'),
(660, 4, 167, 'بی‌تردید، کسانی که کفر ورزیدند و [مردم را] از راه خدا باز داشتند، به گمراهیِ دور و درازی افتاده‌اند.'),
(661, 4, 168, 'کسانی که کفر ورزیدند و ستم کردند، خدا بر آن نیست که آنان را بیامرزد و به راهی هدایت کند،'),
(662, 4, 169, 'مگر راه جهنّم، که همیشه در آن جاودانند؛ و این [کار] برای خدا آسان است.'),
(663, 4, 170, 'ای مردم، آن پیامبر [موعود]، حقیقت را از سوی پروردگارتان برای شما آورده است. پس ایمان بیاورید که برای شما بهتر است. و اگر کافر شوید [بدانید که‌] آنچه در آسمانها و زمین است از آنِ خداست، و خدا دانای حکیم است.'),
(664, 4, 171, 'ای اهل کتاب، در دین خود غلوّ مکنید، و در باره خدا جز [سخن‌] درست مگویید. مسیح، عیسی بن مریم، فقط پیامبر خدا و کلمه اوست که آن را به سوی مریم افکنده و روحی از جانب اوست. پس به خدا و پیامبرانش ایمان بیاورید و نگویید [خدا ]سه‌گانه است. باز ایستید که برای شما بهتر است. خدا فقط معبودی یگانه است. منزّه از آن است که برای او فرزندی باشد. آنچه در آسمانها و آنچه در زمین است از آنِ اوست، و خداوند بس کارساز است.'),
(665, 4, 172, 'مسیح از اینکه بنده خدا باشد هرگز ابا نمی‌ورزد، و فرشتگان مقرّب [نیز ابا ندارند]؛ و هر کس از پرستش او امتناع ورزد و بزرگی فروشد، به زودی همه آنان را به سوی خود گرد می‌آورد.'),
(666, 4, 173, 'اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند، پاداششان را به تمام [و کمال‌] خواهد داد، و از فضل خود به ایشان افزونتر می‌بخشد. و اما کسانی که امتناع ورزیده و بزرگی فروخته‌اند، آنان را به عذابی دردناک دچار می‌سازد و در برابر خدا برای خود یار و یاوری نخواهند یافت.'),
(667, 4, 174, 'ای مردم، در حقیقت برای شما از جانب پروردگارتان برهانی آمده است، و ما به سوی شما نوری تابناک فرو فرستاده‌ایم.'),
(668, 4, 175, 'و اما کسانی که به خدا گرویدند و به او تمسّک جستند، به زودی [خدا] آنان را در جوار رحمت و فضلی از جانب خویش درآورد، و ایشان را به سوی خود، به راهی راست هدایت کند.'),
(669, 4, 176, 'از تو [در باره کلاله‌] فتوا می‌طلبند؛ بگو: «خدا در باره کلاله فتوا می‌دهد: اگر مردی بمیرد و فرزندی نداشته باشد، و خواهری داشته باشد، نصف میراث از آن اوست. و آن [مرد نیز] از او ارث می‌برد، اگر برای او [=خواهر] فرزندی نباشد. پس اگر [ورثه فقط] دو خواهر باشند، دو سوم میراث برای آن دو است، و اگر [چند] خواهر و برادرند، پس نصیب مرد، مانند نصیب دو زن است. خدا برای شما توضیح می‌دهد تا مبادا گمراه شوید، و خداوند به هر چیزی داناست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای کسانی که ایمان آورده‌اید، به قراردادها[ی خود] وفا کنید. برای شما [گوشت‌] چارپایان حلال گردیده، جز آنچه [حکمش‌] بر شما خوانده می‌شود، در حالی که نباید شکار را در حال احرام، حلال بشمرید. خدا هر چه بخواهد فرمان می‌دهد.'),
(671, 5, 2, 'ای کسانی که ایمان آورده‌اید، حرمت شعایر خدا، و ماه حرام، و قربانی بی‌نشان، و قربانیهای گردن‌بنددار، و راهیان بیت الحرام را که فضل و خشنودی پروردگار خود را می‌طلبند، نگه دارید. و چون از احرام بیرون آمدید [می‌توانید] شکار کنید، و البتّه نباید کینه‌توزیِ گروهی که شما را از مسجد الحرام باز داشتند، شما را به تعدّی وادارد. و در نیکوکاری و پرهیزگاری با یکدیگر همکاری کنید، و در گناه و تعدّی دستیار هم نشوید، و از خدا پروا کنید که خدا سخت‌کیفر است.'),
(672, 5, 3, 'بر شما حرام شده است: مردار، و خون، و گوشت خوک، و آنچه به نام غیر خدا کشته شده باشد، و [حیوان حلال گوشت‌] خفه شده، و به چوب مرده، و از بلندی افتاده، و به ضرب شاخ مرده، و آنچه درنده از آن خورده باشد -مگر آنچه را [که زنده دریافته و خود] سر ببرید- و [همچنین‌] آنچه برای بتان سربریده شده، و [نیز] قسمت کردن شما [چیزی را] به وسیله تیرهای قرعه؛ این [کارها همه‌] نافرمانیِ [خدا]ست. امروز کسانی که کافر شده‌اند، از [کارشکنی در] دین شما نومید گردیده‌اند. پس، از ایشان مترسید و از من بترسید. امروز دین شما را برایتان کامل و نعمت خود را بر شما تمام گردانیدم، و اسلام را برای شما [به عنوان‌] آیینی برگزیدم. و هر کس دچار گرسنگی شود، بی‌آنکه به گناه متمایل باشد [اگر از آنچه منع شده است بخورد]، بی تردید، خدا آمرزنده مهربان است.'),
(673, 5, 4, 'از تو می‌پرسند: چه چیزی برای آنان حلال شده است؟ بگو: «چیزهای پاکیزه برای شما حلال گردیده و [نیز صید] حیوانات شکارگر که شما بعنوان مربیان سگهای شکاری، از آنچه خدایتان آموخته، به آنها تعلیم داده‌اید [برای شما حلال شده است‌]. پس از آنچه آنها برای شما گرفته و نگاه داشته‌اند بخورید، و نام خدا را بر آن ببرید، و پروای خدا بدارید که خدا زودشمار است.'),
(674, 5, 5, 'امروز چیزهای پاکیزه برای شما حلال شده، و طعام کسانی که اهل کتابند برای شما حلال، و طعام شما برای آنان حلال است. و [بر شما حلال است ازدواج با] زنان پاکدامن از مسلمان، و زنان پاکدامن از کسانی که پیش از شما کتاب [آسمانی‌] به آنان داده شده، به شرط آنکه مَهرهایشان را به ایشان بدهید، در حالی که خود پاکدامن باشید نه زناکار و نه آنکه زنان را در پنهانی دوست خود بگیرید. و هر کس در ایمان خود شکّ کند، قطعاً عملش تباه شده، و در آخرت از زیانکاران است.'),
(675, 5, 6, 'ای کسانی که ایمان آورده‌اید، چون به [عزم‌] نماز برخیزید، صورت و دستهایتان را تا آرنج بشویید؛ و سر و پاهای خودتان را تا برآمدگی پیشین [هر دو پا] مسح کنید؛ و اگر جنب‌اید خود را پاک کنید [=غسل نمایید]؛ و اگر بیمار یا در سفر بودید، یا یکی از شما از قضای حاجت آمد، یا با زنان نزدیکی کرده‌اید و آبی نیافتید؛ پس با خاک پاک تیمّم کنید، و از آن به صورت و دستهایتان بکشید. خدا نمی‌خواهد بر شما تنگ بگیرد، لیکن می‌خواهد شما را پاک، و نعمتش را بر شما تمام گرداند، باشد که سپاس [او] بدارید.'),
(676, 5, 7, 'و نعمتی را که خدا بر شما ارزانی داشته و [نیز] پیمانی را که شما را به [انجام‌] آن متعهد گردانیده، به یاد آورید، آنگاه که گفتید: «شنیدیم و اطاعت کردیم.» و از خدا پروا دارید که خدا به راز دلها آگاه است.'),
(677, 5, 8, 'ای کسانی که ایمان آورده‌اید، برای خدا به داد برخیزید [و] به عدالت شهادت دهید، و البته نباید دشمنی گروهی شما را بر آن دارد که عدالت نکنید. عدالت کنید که آن به تقوا نزدیکتر است، و از خدا پروا دارید، که خدا به آنچه انجام می‌دهید آگاه است.'),
(678, 5, 9, 'خدا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، به آمرزش و پاداشی بزرگ وعده داده است.'),
(679, 5, 10, 'و کسانی که کفر ورزیدند و آیات ما را دروغ انگاشتند، آنان اهل دوزخند.'),
(680, 5, 11, 'ای کسانی که ایمان آورده‌اید، نعمت خدا را بر خود، یاد کنید: آنگاه که قومی آهنگ آن داشتند که بر شما دست یازند، و [خدا] دستشان را از شما کوتاه داشت. و از خدا پروا دارید، و مؤمنان باید تنها بر خدا توکل کنند.'),
(681, 5, 12, 'در حقیقت، خدا از فرزندان اسرائیل پیمان گرفت. و از آنان دوازده سرکرده برانگیختیم. و خدا فرمود: «من با شما هستم.» اگر نماز برپا دارید و زکات بدهید و به فرستادگانم ایمان بیاورید و یاریشان کنید و وام نیکویی به خدا بدهید، قطعاً گناهانتان را از شما می‌زدایم، و شما را به باغهایی که از زیر [درختان‌] آن نهرها روان است در می‌آورم. پس هر کس از شما بعد از این کفر ورزد، در حقیقت از راه راست گمراه شده است.'),
(682, 5, 13, 'پس به [سزای‌] پیمان شکستنشان لعنتشان کردیم و دلهایشان را سخت گردانیدیم. [به طوری که‌] کلمات را از مواضع خود تحریف می‌کنند، و بخشی از آنچه را بدان اندرز داده شده بودند به فراموشی سپردند. و تو همواره بر خیانتی از آنان آگاه می‌شوی، مگر [شماری‌] اندک از ایشان [که خیانتکار نیستند]. پس، از آنان درگذر و چشم پوشی کن که خدا نیکوکاران را دوست می‌دارد.'),
(683, 5, 14, 'و از کسانی که گفتند: «ما نصرانی هستیم»، از ایشان [نیز] پیمان گرفتیم، و[لی‌] بخشی از آنچه را بدان اندرز داده شده بودند فراموش کردند، و ما [هم‌] تا روز قیامت میانشان دشمنی و کینه افکندیم، و به زودی خدا آنان را از آنچه می‌کرده‌اند [و می‌ساخته‌اند] خبر می‌دهد.'),
(684, 5, 15, 'ای اهل کتاب، پیامبر ما به سوی شما آمده است که بسیاری از چیزهایی از کتاب [آسمانی خود] را که پوشیده می‌داشتید برای شما بیان می‌کند، و از بسیاری [خطاهای شما] درمی‌گذرد. قطعاً برای شما از جانب خدا روشنایی و کتابی روشنگر آمده است.'),
(685, 5, 16, 'خدا هر که را از خشنودی او پیروی کند، به وسیله آن [کتاب‌] به راه‌های سلامت رهنمون می‌شود، و به توفیق خویش، آنان را از تاریکیها به سوی روشنایی بیرون می‌برد و به راهی راست هدایتشان می‌کند.'),
(686, 5, 17, 'کسانی که گفتند: «خدا همان مسیح پسر مریم است» مسلماً کافر شده‌اند. بگو: اگر [خدا] اراده کند که مسیح پسر مریم و مادرش و هر که را که در زمین است، جملگی به هلاکت رساند، چه کسی در مقابل خدا اختیاری دارد؟ فرمانروایی آسمانها و زمین و آنچه میان آن دو است از آن خداست. هر چه بخواهد می‌آفریند، و خدا بر هر چیزی تواناست.'),
(687, 5, 18, 'و یهودان و ترسایان گفتند: «ما پسران خدا و دوستان او هستیم.» بگو: «پس چرا شما را به [کیفر] گناهانتان عذاب می‌کند؟ [نه،] بلکه شما [هم‌] بشرید از جمله کسانی که آفریده است. هر که را بخواهد می‌آمرزد، و هر که را بخواهد عذاب می‌کند، و فرمانروایی آسمانها و زمین و آنچه میان آن دو می‌باشد از آن خداست، و بازگشت [همه‌] به سوی اوست.»'),
(688, 5, 19, 'ای اهل کتاب، پیامبر ما به سوی شما آمده که در دوران فترت رسولان [حقایق را] برای شما بیان می‌کند، تا مبادا [روز قیامت‌] بگویید: «برای ما بشارتگر و هشداردهنده‌ای نیامد.» پس قطعاً برای شما بشارتگر و هشداردهنده‌ای آمده است. و خدا بر هر چیزی تواناست.'),
(689, 5, 20, 'و [یاد کن‌] زمانی را که موسی به قوم خود گفت: «ای قوم من، نعمت خدا را بر خود یاد کنید، آنگاه که در میان شما پیامبرانی قرار داد، و شما را پادشاهانی ساخت، و آنچه را که به هیچ کس از جهانیان نداده بود، به شما داد.»'),
(690, 5, 21, '«ای قوم من، به سرزمین مقدسی که خداوند برای شما مقرر داشته است درآیید، و به عقب بازنگردید که زیانکار خواهید شد.»'),
(691, 5, 22, 'گفتند: «ای موسی، در آنجا مردمی زورمندند و تا آنان از آنجا بیرون نروند ما هرگز وارد آن نمی‌شویم. پس اگر از آنجا بیرون بروند ما وارد خواهیم شد.»'),
(692, 5, 23, 'دو مرد از [زمره‌] کسانی که [از خدا] می‌ترسیدند و خدا به آنان نعمت داده بود، گفتند: «از آن دروازه بر ایشان [بتازید و] وارد شوید؛ که اگر از آن، درآمدید قطعاً پیروز خواهید شد، و اگر مؤمنید، به خدا توکل کنید.»'),
(693, 5, 24, 'گفتند: «ای موسی، تا وقتی آنان در آن [شهر]ند ما هرگز پای در آن ننهیم. تو و پروردگارت برو[ید] و جنگ کنید که ما همین جا می‌نشینیم.»'),
(694, 5, 25, '[موسی‌] گفت: «پروردگارا! من جز اختیار شخص خود و برادرم را ندارم؛ پس میان ما و میان این قوم نافرمان جدایی بینداز.'),
(695, 5, 26, '[خدا به موسی‌] فرمود: «[ورود به‌] آن [سرزمین‌] چهل سال بر ایشان حرام شد، [که‌] در بیابان سرگردان خواهند بود. پس تو بر گروه نافرمانان اندوه مخور.»'),
(696, 5, 27, 'و داستان دو پسر آدم را به درستی بر ایشان بخوان، هنگامی که [هر یک از آن دو،] قربانیی پیش داشتند. پس، از یکی از آن دو پذیرفته شد و از دیگری پذیرفته نشد. [قابیل‌] گفت: «حتماً تو را خواهم کشت.» [هابیل‌] گفت: «خدا فقط از تقواپیشگان می‌پذیرد.»'),
(697, 5, 28, '«اگر دست خود را به سوی من دراز کنی تا مرا بکشی، من دستم را به سوی تو دراز نمی‌کنم تا تو را بکشم، چرا که من از خداوند، پروردگار جهانیان می‌ترسم.»'),
(698, 5, 29, '«من می‌خواهم تو با گناه من و گناه خودت [به سوی خدا] بازگردی، و در نتیجه از اهل آتش باشی، و این است سزای ستمگران.»'),
(699, 5, 30, 'پس نفس [اماره‌]اش او را به قتل برادرش ترغیب کرد، و وی را کشت و از زیانکاران شد.'),
(700, 5, 31, 'پس، خدا زاغی را برانگیخت که زمین را می‌کاوید، تا به او نشان دهد چگونه جسد برادرش را پنهان کند. [قابیل‌] گفت: «وای بر من، آیا عاجزم که مثل این زاغ باشم تا جسد برادرم را پنهان کنم؟» پس از [زمره ]پشیمانان گردید.'),
(701, 5, 32, 'از این روی بر فرزندان اسرائیل مقرر داشتیم که هر کس کسی را -جز به قصاص قتل، یا [به کیفر] فسادی در زمین- بکشد، چنان است که گویی همه مردم را کشته باشد. و هر کس کسی را زنده بدارد، چنان است که گویی تمام مردم را زنده داشته است. و قطعاً پیامبران ما دلایل آشکار برای آنان آوردند، [با این همه‌] پس از آن بسیاری از ایشان در زمین زیاده‌روی می‌کنند.'),
(702, 5, 33, 'سزای کسانی که با [دوستداران‌] خدا و پیامبر او می‌جنگند و در زمین به فساد می‌کوشند، جز این نیست که کشته شوند یا بر دار آویخته گردند یا دست و پایشان در خلاف جهت یکدیگر بریده شود یا از آن سرزمین تبعید گردند. این، رسوایی آنان در دنیاست و در آخرت عذابی بزرگ خواهند داشت.'),
(703, 5, 34, 'مگر کسانی که پیش از آنکه بر ایشان دست یابید، توبه کرده باشند. پس بدانید که خدا آمرزنده مهربان است.'),
(704, 5, 35, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید؛ و به او [توسل و] تقرب جویید؛ و در راهش جهاد کنید، باشد که رستگار شوید.'),
(705, 5, 36, 'در حقیقت، کسانی که کفر ورزیدند، اگر تمام آنچه در زمین است برای آنان باشد، و مثل آن را [نیز] با آن [داشته باشند] تا به وسیله آن، خود را از عذاب روز قیامت بازخرند، از ایشان پذیرفته نمی‌شود و عذابی پر درد خواهند داشت.'),
(706, 5, 37, 'می‌خواهند که از آتش بیرون آیند، در حالی که از آن بیرون آمدنی نیستند، و برای آنان عذابی پایدار خواهد بود.'),
(707, 5, 38, 'و مرد و زن دزد را به سزای آنچه کرده‌اند، دستشان را به عنوان کیفری از جانب خدا ببرید، و خداوند توانا و حکیم است.'),
(708, 5, 39, 'پس هر که بعد از ستم‌کردنش توبه کند و به صلاح آید، خدا توبه او را می‌پذیرد، که خدا آمرزنده مهربان است.'),
(709, 5, 40, 'مگر ندانسته‌ای که فرمانروایی آسمانها و زمین از آنِ خداست. هر که را بخواهد عذاب می‌کند و هر که را بخواهد می‌بخشد، و خدا بر هر چیزی تواناست؟'),
(710, 5, 41, 'ای پیامبر، کسانی که در کفر شتاب می‌ورزند، تو را غمگین نسازند؛ [چه‌] از آنانکه با زبان خود گفتند: «ایمان آوردیم»، و حال آنکه دلهایشان ایمان نیاورده بود، و [چه‌] از یهودیان: [آنان‌] که [به سخنان تو] گوش می‌سپارند [تا بهانه‌ای ]برای تکذیب [تو بیابند]، و برای گروهی دیگر که [خود] نزد تو نیامده‌اند، خبرچینی [=جاسوسی‌] می‌کنند، کلمات را از جاهای خود دگرگون می‌کنند [و] می‌گویند: «اگر این [حکم‌] به شما داده شد، آن را بپذیرید، و اگر آن به شما داده نشد، پس دوری کنید.» و هر که را خدا بخواهد به فتنه درافکند، هرگز در برابر خدا برای او از دست تو چیزی بر نمی‌آید. اینانند که خدا نخواسته دلهایشان را پاک گرداند. در دنیا برای آنان رسوایی، و در آخرت عذابی بزرگ خواهد بود.'),
(711, 5, 42, 'پذیرا و شنوای دروغ هستند [و] بسیار مال حرام می‌خورند. پس اگر نزد تو آمدند، [یا] میان آنان داوری کن، یا از ایشان روی برتاب. و اگر از آنان روی برتابی هرگز زیانی به تو نخواهند رسانید. و اگر داوری می‌کنی، پس به عدالت در میانشان حکم کن که خداوند دادگران را دوست می‌دارد.'),
(712, 5, 43, 'و چگونه تو را داور قرار می‌دهند، با آنکه تورات نزد آنان است که در آن حکم خدا [آمده‌] است؟ سپس آنان بعد از این [طلب داوری‌] پشت می‌کنند، و [واقعاً] آنان مؤمن نیستند.'),
(713, 5, 44, 'ما تورات را که در آن رهنمود و روشنایی بود نازل کردیم. پیامبرانی که تسلیم [فرمان خدا] بودند، به موجب آن برای یهود داوری می‌کردند؛ و [همچنین‌] الهیّون و دانشمندان به سبب آنچه از کتاب خدا به آنان سپرده شده و بر آن گواه بودند. پس، از مردم نترسید و از من بترسید، و آیات مرا به بهای ناچیزی مفروشید؛ و کسانی که به موجب آنچه خدا نازل کرده داوری نکرده‌اند، آنان خود کافرانند.'),
(714, 5, 45, 'و در [تورات‌] بر آنان مقرر کردیم که جان در مقابل جان، و چشم در برابر چشم، و بینی در برابر بینی، و گوش در برابر گوش، و دندان در برابر دندان می‌باشد؛ و زخمها [نیز به همان ترتیب‌] قصاصی دارند. و هر که از آن [قصاص‌]درگذرد، پس آن، کفاره [گناهان‌] او خواهد بود. و کسانی که به موجب آنچه خدا نازل کرده داوری نکرده‌اند، آنان خود ستمگرانند.'),
(715, 5, 46, 'و عیسی پسر مریم را به دنبال آنان [=پیامبران دیگر] درآوردیم، در حالی که تورات را که پیش از او بود تصدیق داشت، و به او انجیل را عطا کردیم که در آن، هدایت و نوری است و تصدیق‌کننده توراتِ قبل از آن است، و برای پرهیزگاران رهنمود و اندرزی است.'),
(716, 5, 47, 'و اهل انجیل باید به آنچه خدا در آن نازل کرده داوری کنند، و کسانی که به آنچه خدا نازل کرده حکم نکنند، آنان خود، نافرمانند.'),
(717, 5, 48, 'و ما این کتاب [=قرآن‌] را به حق به سوی تو فرو فرستادیم، در حالی که تصدیق‌کننده کتابهای پیشین و حاکم بر آنهاست. پس میان آنان بر وفق آنچه خدا نازل کرده حکم کن، و از هواهایشان [با دور شدن‌] از حقی که به سوی تو آمده، پیروی مکن. برای هر یک از شما [امتها] شریعت و راه روشنی قرار داده‌ایم. و اگر خدا می‌خواست شما را یک امت قرار می‌داد، ولی [خواست‌] تا شما را در آنچه به شما داده است بیازماید. پس در کارهای نیک بر یکدیگر سبقت گیرید. بازگشت [همه‌] شما به سوی خداست؛ آنگاه در باره آنچه در آن اختلاف می‌کردید آگاهتان خواهد کرد.'),
(718, 5, 49, 'و میان آنان به موجب آنچه خدا نازل کرده، داوری کن و از هواهایشان پیروی مکن و از آنان برحذر باش؛ مبادا تو را در بخشی از آنچه خدا بر تو نازل کرده به فتنه دراندازند. پس اگر پشت کردند، بدان که خدا می‌خواهد آنان را فقط به [سزای‌] پاره‌ای از گناهانشان برساند، و در حقیقت بسیاری از مردم نافرمانند.'),
(719, 5, 50, 'آیا خواستار حکم جاهلیت‌اند؟ و برای مردمی که یقین دارند، داوری چه کسی از خدا بهتر است؟'),
(720, 5, 51, 'ای کسانی که ایمان آورده‌اید، یهود و نصاری را دوستان [خود] مگیرید [که‌] بعضی از آنان دوستان بعضی دیگرند. و هر کس از شما آنها را به دوستی گیرد، از آنان خواهد بود. آری، خدا گروه ستمگران را راه نمی‌نماید.'),
(721, 5, 52, 'می‌بینی کسانی که در دلهایشان بیماری است، در [دوستی‌] با آنان شتاب می‌ورزند. می‌گویند: «می‌ترسیم به ما حادثه ناگواری برسد.» امید است خدا از جانب خود فتح [منظور] یا امر دیگری را پیش آورد، تا [در نتیجه آنان‌] از آنچه در دل خود نهفته داشته‌اند پشیمان گردند.'),
(722, 5, 53, 'و کسانی که ایمان آورده‌اند، می‌گویند: «آیا اینان بودند که به خداوند سوگندهای سخت می‌خوردند که جداً با شما هستند؟» اعمالشان تباه شد و زیانکار گردیدند.'),
(723, 5, 54, 'ای کسانی که ایمان آورده‌اید، هر کس از شما از دین خود برگردد، به زودی خدا گروهی [دیگر] را می‌آورد که آنان را دوست می‌دارد و آنان [نیز] او را دوست دارند. [اینان‌] با مؤمنان، فروتن، [و] بر کافران سرفرازند. در راه خدا جهاد می‌کنند و از سرزنش هیچ ملامتگری نمی‌ترسند. این فضل خداست. آن را به هر که بخواهد می‌دهد، و خدا گشایشگر داناست.'),
(724, 5, 55, 'ولیّ شما، تنها خدا و پیامبر اوست و کسانی که ایمان آورده‌اند: همان کسانی که نماز برپا می‌دارند و در حال رکوع زکات می‌دهند.'),
(725, 5, 56, 'و هر کس خدا و پیامبر او و کسانی را که ایمان آورده‌اند ولیّ خود بداند [پیروز است، چرا که‌] حزب خدا همان پیروزمندانند.'),
(726, 5, 57, 'ای کسانی که ایمان آورده‌اید، کسانی را که دین شما را به ریشخند و بازی گرفته‌اند [چه‌] از کسانی که پیش از شما به آنان کتاب داده شده و [چه از] کافران، دوستان [خود] مگیرید، و اگر ایمان دارید از خدا پروا دارید.'),
(727, 5, 58, 'و هنگامی که [به وسیله اذان، مردم را] به نماز می‌خوانید، آن را به مسخره و بازی می‌گیرند، زیرا آنان مردمی‌اند که نمی‌اندیشند.'),
(728, 5, 59, 'بگو: «ای اهل کتاب، آیا جز این بر ما عیب می‌گیرید که ما به خدا و به آنچه به سوی ما نازل شده، و به آنچه پیش از این فرود آمده است ایمان آورده‌ایم، و اینکه بیشتر شما فاسقید؟»'),
(729, 5, 60, 'بگو: «آیا شما را به بدتر از [صاحبان‌] این کیفر در پیشگاه خدا، خبر دهم؟ همانان که خدا لعنتشان کرده و بر آنان خشم گرفته و از آنان بوزینگان و خوکان پدید آورده، و آنانکه طاغوت را پرستش کرده‌اند. اینانند که از نظر منزلت، بدتر، و از راه راست گمراه‌ترند.»'),
(730, 5, 61, 'و چون نزد شما می‌آیند، می‌گویند: «ایمان آوردیم.» در حالی که با کفر وارد شده و قطعاً با همان [کفر] بیرون رفته‌اند. و خدا به آنچه پنهان می‌داشتند داناتر است.'),
(731, 5, 62, 'و بسیاری از آنان را می‌بینی که در گناه و تعدّی و حرامخواری خود شتاب می‌کنند. واقعاً چه اعمال بدی انجام می‌دادند.'),
(732, 5, 63, 'چرا الهیّون و دانشمندان، آنان را از گفتار گناه‌[آلود] و حرامخوارگی‌شان باز نمی‌دارند؟ راستی چه بد است آنچه انجام می‌دادند.'),
(733, 5, 64, 'و یهود گفتند: «دست خدا بسته است.» دستهای خودشان بسته باد. و به [سزای ] آنچه گفتند، از رحمت خدا دور شوند. بلکه هر دو دست او گشاده است، هر گونه بخواهد می‌بخشد. و قطعاً آنچه از جانب پروردگارت به سوی تو فرود آمده، بر طغیان و کفر بسیاری از ایشان خواهد افزود، و تا روز قیامت میانشان دشمنی و کینه افکندیم. هر بار که آتشی برای پیکار برافروختند، خدا آن را خاموش ساخت. و در زمین برای فساد می‌کوشند. و خدا مفسدان را دوست نمی‌دارد.'),
(734, 5, 65, 'و اگر اهل کتاب ایمان آورده و پرهیزگاری کرده بودند، قطعاً گناهانشان را می‌زدودیم و آنان را به بوستانهای پر نعمت درمی‌آوردیم.'),
(735, 5, 66, 'و اگر آنان به تورات و انجیل و آنچه از جانب پروردگارشان به سویشان نازل شده است، عمل می‌کردند، قطعاً از بالای سرشان [برکات آسمانی‌] و از زیر پاهایشان [برکات زمینی‌] برخوردار می‌شدند. از میان آنان گروهی میانه‌رو هستند، و بسیاری از ایشان بد رفتار می‌کنند.'),
(736, 5, 67, 'ای پیامبر، آنچه از جانب پروردگارت به سوی تو نازل شده، ابلاغ کن؛ و اگر نکنی پیامش را نرسانده‌ای. و خدا تو را از [گزند] مردم نگاه می‌دارد. آری، خدا گروه کافران را هدایت نمی‌کند.'),
(737, 5, 68, 'بگو: «ای اهل کتاب، تا [هنگامی که‌] به تورات و انجیل و آنچه از پروردگارتان به سوی شما نازل شده است عمل نکرده‌اید بر هیچ [آیین بر حقی‌] نیستید.» و قطعاً آنچه از جانب پروردگارت به سوی تو نازل شده، بر طغیان و کفر بسیاری از آنان خواهد افزود. پس بر گروه کافران اندوه مخور.'),
(738, 5, 69, 'کسانی که ایمان آورده و کسانی که یهودی و صابئی و مسیحی‌اند، هر کس به خدا و روز بازپسین ایمان آورد و کار نیکو کند، پس نه بیمی بر ایشان است و نه اندوهگین خواهند شد.'),
(739, 5, 70, 'ما از فرزندان اسرائیل سخت پیمان گرفتیم، و به سویشان پیامبرانی روانه کردیم. هر بار پیامبری چیزی بر خلاف دلخواهشان برایشان آورد، گروهی را تکذیب می‌کردند و گروهی را می‌کشتند.'),
(740, 5, 71, 'و پنداشتند کیفری در کار نیست. پس کور و کر شدند. سپس خدا توبه آنان را پذیرفت. باز بسیاری از ایشان کور و کر شدند، و خدا به آنچه انجام می‌دهند بیناست.'),
(741, 5, 72, 'کسانی که گفتند: «خدا همان مسیح پسر مریم است»، قطعاً کافر شده‌اند، و حال آنکه مسیح، می‌گفت: «ای فرزندان اسرائیل، پروردگار من و پروردگار خودتان را بپرستید؛ که هر کس به خدا شرک آورد، قطعاً خدا بهشت را بر او حرام ساخته و جایگاهش آتش است، و برای ستمکاران یاورانی نیست.»'),
(742, 5, 73, 'کسانی که [به تثلیث قائل شده و] گفتند: «خدا سومین [شخص از] سه [شخص یا سه اقنوم‌] است، قطعاً کافر شده‌اند، و حال آنکه هیچ معبودی جز خدای یکتا نیست. و اگر از آنچه می‌گویند باز نایستند، به کافران ایشان عذابی دردناک خواهد رسید.'),
(743, 5, 74, 'چرا به درگاه خدا توبه نمی‌کنند، و از وی آمرزش نمی‌خواهند؟ و خدا آمرزنده مهربان است.'),
(744, 5, 75, 'مسیح، پسر مریم جز پیامبری نبود که پیش از او [نیز] پیامبرانی آمده بودند؛ و مادرش زنی بسیار راستگو بود. هر دو غذا می‌خوردند. بنگر چگونه آیات [خود] را برای آنان توضیح می‌دهیم؛ سپس ببین چگونه [از حقیقت‌] دور می‌افتند.'),
(745, 5, 76, 'بگو: «آیا غیر از خدا چیزی را که اختیار زیان و سود شما را ندارد، می‌پرستید؟ و حال آنکه خداوند شنوای داناست.»'),
(746, 5, 77, 'بگو: «ای اهل کتاب، در دین خود بناحق گزافه‌گویی نکنید، و از پی هوسهای گروهی که پیش از این گمراه گشتند و بسیاری [از مردم‌] را گمراه کردند و [خود] از راه راست منحرف شدند، نروید.»'),
(747, 5, 78, 'از میان فرزندان اسرائیل، آنان که کفر ورزیدند، به زبان داوود و عیسی بن مریم مورد لعنت قرار گرفتند. این [کیفر] به خاطر آن بود که عصیان ورزیده و [از فرمان خدا] تجاوز می‌کردند.'),
(748, 5, 79, '[و] از کار زشتی که آن را مرتکب می‌شدند، یکدیگر را بازنمی‌داشتند. راستی، چه بد بود آنچه می‌کردند.'),
(749, 5, 80, 'بسیاری از آنان را می‌بینی که با کسانی که کفر ورزیده‌اند دوستی می‌کنند. راستی چه زشت است آنچه برای خود پیش فرستادند که [در نتیجه‌] خدا بر ایشان خشم گرفت، و پیوسته در عذاب می‌مانند.'),
(750, 5, 81, 'و اگر به خدا و پیامبر و آنچه که به سوی او فرود آمده ایمان می‌آوردند، آنان را به دوستی نمی‌گرفتند، لیکن بسیاری از ایشان نافرمانند.'),
(751, 5, 82, 'مسلماً یهودیان و کسانی را که شرک ورزیده‌اند، دشمن‌ترین مردم نسبت به مؤمنان خواهی یافت؛ و قطعاً کسانی را که گفتند: «ما نصرانی هستیم»، نزدیکترین مردم در دوستی با مؤمنان خواهی یافت، زیرا برخی از آنان دانشمندان و رهبانانی‌اند که تکبّر نمی‌ورزند.'),
(752, 5, 83, 'و چون آنچه را به سوی این پیامبر نازل شده، بشنوند، می‌بینی بر اثر آن حقیقتی که شناخته‌اند، اشک از چشمهایشان سرازیر می‌شود. می‌گویند: پروردگارا، ما ایمان آورده‌ایم؛ پس ما را در زمره گواهان بنویس.'),
(753, 5, 84, 'و برای ما چه [عذری‌] است که به خدا و آنچه از حق به ما رسیده، ایمان نیاوریم و حال آنکه چشم داریم که پروردگارمان ما را با گروه شایستگان [به بهشت‌] درآورد؟'),
(754, 5, 85, 'پس به پاس آنچه گفتند، خدا به آنان باغهایی پاداش داد که از زیر [درختان‌] آن نهرها جاری است. در آن جاودانه می‌مانند، و این پاداش نیکوکاران است.'),
(755, 5, 86, 'و کسانی که کفر ورزیدند و آیات ما را دروغ پنداشتند، آنان همدم آتشند.'),
(756, 5, 87, 'ای کسانی که ایمان آورده‌اید، چیزهای پاکیزه‌ای را که خدا برای [استفاده‌] شما حلال کرده، حرام مشمارید و از حدّ مگذرید، که خدا از حدگذرندگان را دوست نمی‌دارد.'),
(757, 5, 88, 'و از آنچه خداوند روزیِ شما گردانیده، حلال و پاکیزه را بخورید، و از آن خدایی که بدو ایمان دارید پروا دارید.'),
(758, 5, 89, 'خدا شما را به سوگندهای بیهوده‌تان مؤاخذه نمی‌کند، ولی به سوگندهایی که [از روی اراده‌] می‌خورید [و می‌شکنید] شما را مؤاخذه می‌کند. و کفاره‌اش خوراک دادن به ده بینواست -از غذاهای متوسطی که به کسان خود می‌خورانید- یا پوشانیدن آنان یا آزاد کردن بنده‌ای. و کسی که [هیچ یک از اینها را] نیابد [باید] سه روز روزه بدارد. این است کفاره سوگندهای شما وقتی که سوگند خوردید. و سوگندهای خود را پاس دارید. این گونه خداوند آیات خود را برای شما بیان می‌کند، باشد که سپاسگزاری کنید.'),
(759, 5, 90, 'ای کسانی که ایمان آورده‌اید، شراب و قمار و بتها و تیرهای قرعه پلیدند [و] از عمل شیطانند. پس، از آنها دوری گزینید، باشد که رستگار شوید.'),
(760, 5, 91, 'همانا شیطان می‌خواهد با شراب و قمار، میان شما دشمنی و کینه ایجاد کند، و شما را از یاد خدا و از نماز باز دارد. پس آیا شما دست برمی‌دارید؟'),
(761, 5, 92, 'و اطاعت خدا و اطاعت پیامبر کنید و [از گناهان‌] برحذر باشید، پس اگر روی گرداندید، بدانید که بر عهده پیامبر ما، فقط رساندن [پیام‌] آشکار است.'),
(762, 5, 93, 'بر کسانی که ایمان آورده و کارهای شایسته کرده‌اند، گناهی در آنچه [قبلاً] خورده‌اند نیست، در صورتی که تقوا پیشه کنند و ایمان بیاورند و کارهای شایسته کنند؛ سپس تقوا پیشه کنند و ایمان بیاورند؛ آنگاه تقوا پیشه کنند و احسان نمایند، و خدا نیکوکاران را دوست می‌دارد.'),
(763, 5, 94, 'ای کسانی که ایمان آورده‌اید، خدا شما را به چیزی از شکار که در دسترس شما و نیزه‌های شما باشد خواهد آزمود، تا معلوم دارد چه کسی در نهان از او می‌ترسد. پس هر کس بعد از آن تجاوز کند، برای او عذابی دردناک خواهد بود.'),
(764, 5, 95, 'ای کسانی که ایمان آورده‌اید، در حالی که محرمید شکار را مکشید، و هر کس از شما عمداً آن را بکشد، باید نظیر آنچه کشته است از چهارپایان کفاره‌ای بدهد، که [نظیر بودنِ‌] آن را دو تن عادل از میان شما تصدیق کنند، و به صورت قربانی به کعبه برسد. یا به کفاره [آن‌] مستمندان را خوراک بدهد، یا معادلش روزه بگیرد، تا سزای زشتکاری خود را بچشد. خداوند از آنچه در گذشته واقع شده عفو کرده است، و[لی‌] هر کس تکرار کند خدا از او انتقام می‌گیرد، و خداوند، توانا و صاحب‌انتقام است.'),
(765, 5, 96, 'صید دریا و مأکولات آن برای شما حلال شده است تا برای شما و مسافران بهره‌ای باشد، و[لی‌] صید بیابان -مادام که محرم می‌باشید- بر شما حرام گردیده است. و از خدایی که نزد او محشور می‌شوید پروا دارید.'),
(766, 5, 97, 'خداوند [زیارت‌] کعبه بیت الحرام را وسیله به پا داشتن [مصالح‌] مردم قرار داده، و ماه حرام و قربانیهای بی‌نشان و قربانیهای نشاندار را [نیز به همین منظور مقرّر فرموده است‌]، این [جمله‌] برای آن است تا بدانید که خدا آنچه را در آسمانها و آنچه را در زمین است می‌داند، و خداست که بر هر چیزی داناست.'),
(767, 5, 98, 'بدانید که خدا سخت‌کیفر است، و [بدانید] که خدا آمرزنده مهربان است.'),
(768, 5, 99, 'بر پیامبر [خدا، وظیفه‌ای‌] جز ابلاغ [رسالت‌] نیست، و خداوند آنچه را آشکار و آنچه را پوشیده می‌دارید می‌داند.'),
(769, 5, 100, 'بگو: «پلید و پاک یکسان نیستند، هر چند کثرت پلید[ها] تو را به شگفت آورد. پس ای خردمندان، از خدا پروا کنید، باشد که رستگار شوید.»'),
(770, 5, 101, 'ای کسانی که ایمان آورده‌اید، از چیزهایی که اگر برای شما آشکار گردد شما را اندوهناک می‌کند مپرسید. و اگر هنگامی که قرآن نازل می‌شود، در باره آنها سؤال کنید، برای شما روشن می‌شود. خدا از آن [پرسشهای بیجا] گذشت، و خداوند آمرزنده بردبار است.'),
(771, 5, 102, 'گروهی پیش از شما [نیز] از این [گونه‌] پرسشها کردند؛ آنگاه به سبب آن کافر شدند.'),
(772, 5, 103, 'خدا [چیزهای ممنوعی از قبیل:] بحیره و سائبه و وصیله و حام قرار نداده است. ولی کسانی که کفر ورزیدند، بر خدا دروغ می‌بندند و بیشترشان تعقل نمی‌کنند.'),
(773, 5, 104, 'و چون به آنان گفته شود: «به سوی آنچه خدا نازل کرده و به سوی پیامبر[ش‌] بیایید»، می‌گویند: «آنچه پدران خود را بر آن یافته‌ایم ما را بس است.» آیا هر چند پدرانشان چیزی نمی‌دانسته و هدایت نیافته بودند؟'),
(774, 5, 105, 'ای کسانی که ایمان آورده‌اید، به خودتان بپردازید. هر گاه شما هدایت یافتید، آن کس که گمراه شده است به شما زیانی نمی‌رساند. بازگشت همه شما به سوی خداست. پس شما را از آنچه انجام می‌دادید، آگاه خواهد کرد.'),
(775, 5, 106, 'ای کسانی که ایمان آورده‌اید، هنگامی که یکی از شما را [نشانه‌های‌] مرگ دررسید، باید از میان خود دو عادل را در موقع وصیّت، به شهادت میان خود فرا خوانید؛ یا اگر در سفر بودید و مصیبت مرگ، شما را فرا رسید [و شاهد مسلمان نبود] دو تن از غیر [همکیشان‌] خود را [به شهادت بطلبید]. و اگر [در صداقت آنان‌] شک کردید، پس از نماز، آن دو را نگاه می‌دارید؛ پس به خدا سوگند یاد می‌کنند که ما این [حق‌] را به هیچ قیمتی نمی‌فروشیم هر چند [پای‌] خویشاوند [در کار] باشد، و شهادت الهی را کتمان نمی‌کنیم، که [اگر کتمان حق کنیم‌] در این صورت از گناهکاران خواهیم بود.'),
(776, 5, 107, 'و اگر معلوم شد که آن دو دستخوش گناه شده‌اند، دو تن دیگر از کسانی که بر آنان ستم رفته است، و هر دو [به میت‌] نزدیکترند، به جای آن دو [شاهد قبلی‌] قیام کنند؛ پس به خدا سوگند یاد می‌کنند که: گواهی ما قطعاً از گواهی آن دو درست‌تر است، و [از حق‌] تجاوز نکرده‌ایم، چرا که [اگر چنین کنیم‌] از ستمکاران خواهیم بود.'),
(777, 5, 108, 'این [روش‌] برای اینکه شهادت را به صورت درست ادا کنند، یا بترسند که بعد از سوگندخوردنشان، سوگندهایی [به وارثان میت‌] برگردانده شود، [به صواب‌] نزدیکتر است. و از خدا پروا دارید، و [این پندها را] بشنوید، و خدا گروه فاسقان را هدایت نمی‌کند.'),
(778, 5, 109, '[یاد کن‌] روزی را که خدا پیامبران را گرد می‌آورد؛ پس می‌فرماید: «چه پاسخی به شما داده شد؟» می‌گویند: «ما را هیچ دانشی نیست. تویی که دانای رازهای نهانی.»'),
(779, 5, 110, '[یاد کن‌] هنگامی را که خدا فرمود: «ای عیسی پسر مریم، نعمت مرا بر خود و بر مادرت به یاد آور، آنگاه که تو را به روح‌القدس تأیید کردم که در گهواره [به اعجاز] و در میانسالی [به وحی‌] با مردم سخن گفتی؛ و آنگاه که تو را کتاب و حکمت و تورات و انجیل آموختم؛ و آنگاه که به اذن من، از گِل، [چیزی‌] به شکل پرنده می‌ساختی، پس در آن می‌دمیدی، و به اذن من پرنده‌ای می‌شد، و کور مادرزاد و پیس را به اذن من شفا می‌دادی؛ و آنگاه که مردگان را به اذن من [زنده از قبر] بیرون می‌آوردی؛ و آنگاه که [آسیب‌] بنی‌اسرائیل را -هنگامی که برای آنان حجتهای آشکار آورده بودی- از تو باز داشتم. پس کسانی از آنان که کافر شده بودند گفتند: این‌[ها چیزی‌] جز افسونی آشکار نیست.'),
(780, 5, 111, 'و [یاد کن‌] هنگامی را که به حواریّون وحی کردم که به من و فرستاده‌ام ایمان آورید. گفتند: «ایمان آوردیم، و گواه باش که ما مسلمانیم.»'),
(781, 5, 112, 'و [یاد کن‌] هنگامی را که حواریّون گفتند: «ای عیسی پسر مریم، آیا پروردگارت می‌تواند از آسمان، خوانی برای ما فرود آورد؟» [عیسی‌] گفت: «اگر ایمان دارید از خدا پروا دارید.»'),
(782, 5, 113, 'گفتند: «می‌خواهیم از آن بخوریم، و دلهای ما آرامش یابد، و بدانیم که به ما راست گفته‌ای، و بر آن از گواهان باشیم.»'),
(783, 5, 114, 'عیسی پسر مریم گفت: «بار الها، پروردگارا، از آسمان، خوانی بر ما فرو فرست تا عیدی برای اول و آخر ما باشد و نشانه‌ای از جانب تو. و ما را روزی ده که تو بهترین روزی‌دهندگانی.»'),
(784, 5, 115, 'خدا فرمود: «من آن را بر شما فرو خواهم فرستاد، و[لی‌] هر کس از شما پس از آن انکار ورزد، وی را [چنان‌] عذابی کنم که هیچ یک از جهانیان را [آن چنان‌] عذاب نکرده باشم.»'),
(785, 5, 116, 'و [یاد کن‌] هنگامی را که خدا فرمود: «ای عیسی پسر مریم، آیا تو به مردم گفتی: من و مادرم را همچون دو خدا به جای خداوند بپرستید؟» گفت: «منزهی تو، مرا نزیبد که [در باره خویشتن‌] چیزی را که حق من نیست بگویم. اگر آن را گفته بودم قطعاً آن را می‌دانستی. آنچه در نفس من است تو می‌دانی؛ و آنچه در ذات توست من نمی‌دانم، چرا که تو خود، دانای رازهای نهانی.'),
(786, 5, 117, 'جز آنچه مرا بدان فرمان دادی [چیزی‌] به آنان نگفتم؛ [گفته‌ام‌] که: خدا، پروردگار من و پروردگار خود را عبادت کنید، و تا وقتی در میانشان بودم بر آنان گواه بودم؛ پس چون روح مرا گرفتی، تو خود بر آنان نگهبان بودی، و تو بر هر چیز گواهی.'),
(787, 5, 118, 'اگر عذابشان کنی، آنان بندگان تواند و اگر بر ایشان ببخشایی تو خود، توانا و حکیمی.»'),
(788, 5, 119, 'خدا فرمود: «این، روزی است که راستگویان را راستی‌شان سود بخشد.» برای آنان باغهایی است که از زیر [درختان‌] آن نهرها روان است. همیشه در آن جاودانند. خدا از آنان خشنود است و آنان [نیز] از او خشنودند. این است رستگاری بزرگ.'),
(789, 5, 120, 'فرمانروایی آسمانها و زمین و آنچه در آنهاست از آنِ خداست، و او بر هر چیزی تواناست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ستایش خدایی را که آسمانها و زمین را آفرید، و تاریکیها و روشنایی را پدید آورد. با این همه کسانی که کفر ورزیده‌اند، [غیر او را] با پروردگار خود برابر می‌کنند.'),
(791, 6, 2, 'اوست کسی که شما را از گِل آفرید. آنگاه مدتی را [برای شما عُمْر] مقرر داشت. و اَجَل حتمی نزد اوست. با این همه، [بعضی از] شما [در قدرت او] تردید می‌کنید.'),
(792, 6, 3, 'و او در آسمانها و زمین خداست. نهان و آشکار شما را می‌داند، و آنچه را به دست می‌آورید [نیز] می‌داند.'),
(793, 6, 4, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان به سویشان نمی‌آمد مگر آنکه از آن روی بر می‌تافتند.'),
(794, 6, 5, 'آنان حق را هنگامی که به سویشان آمد تکذیب کردند، پس به زودی، [حقیقت‌] خبرهای آنچه را که به ریشخند می‌گرفتند به آنان خواهد رسید.'),
(795, 6, 6, 'آیا ندیده‌اند که پیش از آنان چه بسیار امتها را هلاک کردیم؟ [امتهایی که‌] در زمین به آنان امکاناتی دادیم که برای شما آن امکانات را فراهم نکرده‌ایم، و [بارانهای‌] آسمان را پی در پی بر آنان فرو فرستادیم، و رودبارها از زیر [شهرهای‌] آنان روان ساختیم. پس ایشان را به [سزای‌] گناهانشان هلاک کردیم، و پس از آنان نسلهای دیگری پدید آوردیم.'),
(796, 6, 7, 'و اگر مکتوبی، نوشته بر کاغذ، بر تو نازل می‌کردیم و آنان، آن را با دستهای خود لمس می‌کردند قطعاً کافران می‌گفتند: «این [چیزی‌] جز سحر آشکار نیست.»'),
(797, 6, 8, 'و گفتند: «چرا فرشته‌ای بر او نازل نشده است؟» و اگر فرشته‌ای فرود می‌آوردیم، قطعاً کار تمام شده بود؛ سپس مهلت نمی‌یافتند.'),
(798, 6, 9, 'و اگر او را فرشته‌ای قرار می‌دادیم، حتماً وی را [به صورت‌] مردی در می‌آوردیم، و امر را همچنان بر آنان مشتبه می‌ساختیم.'),
(799, 6, 10, 'و پیش از تو پیامبرانی به استهزا گرفته شدند. پس آنچه را ریشخند می‌کردند گریبانگیر ریشخندکنندگان ایشان گردید.'),
(800, 6, 11, 'بگو: «در زمین بگردید، آنگاه بنگرید که فرجام تکذیب‌کنندگان چگونه بوده است؟»'),
(801, 6, 12, 'بگو: «آنچه در آسمانها و زمین است از آنِ کیست؟» بگو: «از آن خداست »؛ که رحمت را بر خویشتن واجب گردانیده است. یقیناً شما را در روز قیامت -که در آن هیچ شکی نیست- گرد خواهد آورد. خودباختگان کسانی‌اند که ایمان نمی‌آورند.'),
(802, 6, 13, 'و آنچه در شب و روز آرام [و تکاپو] دارد، از آنِ اوست و او شنوای داناست.'),
(803, 6, 14, 'بگو: «آیا غیر از خدا -پدیدآورنده آسمانها و زمین- سرپرستی برگزینم؟ و اوست که خوراک می‌دهد، و خوراک داده نمی‌شود.» بگو: «من مأمورم که نخستین کسی باشم که اسلام آورده است، و [به من فرمان داده شده که:] هرگز از مشرکان مباش.»'),
(804, 6, 15, 'بگو: «اگر به پروردگارم عصیان ورزم از عذاب روزی بزرگ می‌ترسم.»'),
(805, 6, 16, 'آن روز، کسی که [عذاب‌] از او برگردانده شود، قطعاً [خدا] بر او رحمت آورده، و این است همان رستگاری آشکار.'),
(806, 6, 17, 'و اگر خدا به تو زیانی برساند، کسی جز او برطرف کننده آن نیست، و اگر خیری به تو برساند پس او بر هر چیزی تواناست.'),
(807, 6, 18, 'و اوست که بر بندگان خویش چیره است، و اوست حکیم آگاه.'),
(808, 6, 19, 'بگو: «گواهی چه کسی از همه برتر است؟» بگو: «خدا میان من و شما گواه است. و این قرآن به من وحی شده تا به وسیله آن، شما و هر کس را [که این پیام به او] برسد، هشدار دهم. آیا واقعاً شما گواهی می‌دهید که در جَنب خدا، خدایان دیگری است؟» بگو: «من گواهی نمی‌دهم.» بگو: «او تنها معبودی یگانه است، و بی‌تردید، من از آنچه شریک [او] قرار می‌دهید بیزارم.»'),
(809, 6, 20, 'کسانی که کتاب [آسمانی‌] به آنان داده‌ایم، همان گونه که پسران خود را می‌شناسد، او [=پیامبر] را می‌شناسد. کسانی که به خود زیان زده‌اند، ایمان نمی‌آورند.'),
(810, 6, 21, 'و کیست ستمکارتر از آن کس که بر خدا دروغ بسته یا آیات او را تکذیب نموده؟ بی تردید، ستمکاران رستگار نمی‌شوند.'),
(811, 6, 22, 'و [یاد کن‌] روزی را که همه آنان را محشور می‌کنیم، آنگاه به کسانی که شرک آورده‌اند می‌گوییم: «کجایند شریکان شما که [آنها را شریک خدا] می‌پنداشتید؟»'),
(812, 6, 23, 'آنگاه عذرشان جز این نیست که می‌گویند: «به خدا، پروردگارمان سوگند که ما مشرک نبودیم.»'),
(813, 6, 24, 'ببین، چگونه به خود دروغ می‌گویند و آنچه برمی‌بافتند از ایشان یاوه شد.'),
(814, 6, 25, 'و برخی از آنان به تو گوش فرا می‌دهند، و[لی‌] ما بر دلهایشان پرده‌ها افکنده‌ایم تا آن را نفهمند، و در گوشهایشان سنگینی [قرار داده‌ایم‌]. و اگر هر معجزه‌ای را ببینند به آن ایمان نمی‌آورند. تا آنجا که وقتی نزد تو می‌آیند و با تو جدال می‌کنند، کسانی که کفر ورزیدند، می‌گویند: «این [کتاب‌] چیزی جز افسانه‌های پیشینیان نیست.»'),
(815, 6, 26, 'و آنان [مردم را] از آن باز می‌دارند و [خود نیز] از آن دوری می‌کنند، و[لی‌] جز خویشتن را به هلاکت نمی‌افکنند و نمی‌دانند.'),
(816, 6, 27, 'و ای کاش [منکران را] هنگامی که بر آتش عرضه می‌شوند، می‌دیدی که می‌گویند: «کاش بازگردانده می‌شدیم و [دیگر] آیات پروردگارمان را تکذیب نمی‌کردیم و از مؤمنان می‌شدیم.»'),
(817, 6, 28, '[ولی چنین نیست‌] بلکه آنچه را پیش از این نهان می‌داشتند، برای آنان آشکار شده است. و اگر هم بازگردانده شوند قطعاً به آنچه از آن منع شده بودند برمی‌گردند و آنان دروغگویند.'),
(818, 6, 29, 'و گفتند: «جز زندگی دنیای ما [زندگی دیگری‌] نیست و برانگیخته نخواهیم شد.»'),
(819, 6, 30, 'و اگر بنگری هنگامی را که در برابر پروردگارشان باز داشته می‌شوند. [خدا] می‌فرماید: «آیا این حق نیست؟» می‌گویند: «چرا، سوگند به پروردگارمان [که حق است‌].» می‌فرماید: «پس به [کیفر] آنکه کفر می‌ورزیدید، این عذاب را بچشید.»'),
(820, 6, 31, 'کسانی که لقای الهی را دروغ انگاشتند قطعاً زیان دیدند. تا آنگاه که قیامت بناگاه بر آنان دررسد، می‌گویند: «ای دریغ بر ما، بر آنچه در باره آن کوتاهی کردیم.» و آنان بار سنگین گناهانشان را به دوش می‌کشند. چه بد است باری که می‌کِشند.'),
(821, 6, 32, 'و زندگی دنیا جز بازی و سرگرمی نیست، و قطعاً سرای بازپسین برای کسانی که پرهیزگاری می‌کنند بهتر است. آیا نمی‌اندیشید؟'),
(822, 6, 33, 'به یقین، می‌دانیم که آنچه می‌گویند تو را سخت غمگین می‌کند. در واقع آنان تو را تکذیب نمی‌کنند، ولی ستمکاران آیات خدا را انکار می‌کنند.'),
(823, 6, 34, 'و پیش از تو نیز پیامبرانی تکذیب شدند، ولی بر آنچه تکذیب شدند و آزار دیدند شکیبایی کردند تا یاری ما به آنان رسید، و برای کلمات خدا هیچ تغییردهنده‌ای نیست. و مسلماً اخبار پیامبران به تو رسیده است.'),
(824, 6, 35, 'و اگر اعراض کردنِ آنان [از قرآن‌] بر تو گران است، اگر می‌توانی نَقْبی در زمین یا نردبانی در آسمان بجویی تا معجزه‌ای [دیگر] برایشان بیاوری [پس چنین کن‌]، و اگر خدا می‌خواست قطعاً آنان را بر هدایت گِرد می‌آورد، پس زنهار از نادانان مباش.'),
(825, 6, 36, 'تنها کسانی [دعوت تو را] اجابت می‌کنند که گوش شنوا دارند، و [امّا] مردگان را خداوند [در قیامت‌] بر خواهد انگیخت؛ سپس به سوی او بازگردانیده می‌شوند.'),
(826, 6, 37, 'و گفتند: «چرا معجزه‌ای از جانب پروردگارش بر او نازل نشده است؟» بگو: «بی‌تردید، خدا قادر است که پدیده‌ای شگرف فرو فرستد، لیکن بیشتر آنان نمی‌دانند.»'),
(827, 6, 38, 'و هیچ جنبنده‌ای در زمین نیست و نه هیچ پرنده‌ای که با دو بال خود پرواز می‌کند؛ مگر آنکه آنها [نیز] گروه‌هایی مانند شما هستند، ما هیچ چیزی را در کتاب [لوح محفوظ] فروگذار نکرده‌ایم؛ سپس [همه‌] به سوی پروردگارشان محشور خواهند گردید.'),
(828, 6, 39, 'و کسانی که آیات ما را دروغ پنداشتند، در تاریکیها[ی کفر] کر و لالند، هر که را خدا بخواهد گمراهش می‌گذارد؛ و هر که را بخواهد بر راه راست قرارش می‌دهد.'),
(829, 6, 40, 'بگو: «به نظر شما، اگر عذاب خدا شما را دررسد یا رستاخیز شما را دریابد، اگر راستگویید، کسی غیر از خدا را می‌خوانید؟»'),
(830, 6, 41, '[نه،] بلکه تنها او را می‌خوانید، و اگر او بخواهد رنج و بلا را از شما دور می‌گرداند، و آنچه را شریک [او] می‌گردانید فراموش می‌کنید.'),
(831, 6, 42, 'و به یقین، ما به سوی امّتهایی که پیش از تو بودند [پیامبرانی‌] فرستادیم، و آنان را به تنگی معیشت و بیماری دچار ساختیم، تا به زاری و خاکساری درآیند.'),
(832, 6, 43, 'پس چرا هنگامی که عذاب ما به آنان رسید تضرّع نکردند؟ ولی [حقیقت این است که‌] دلهایشان سخت شده، و شیطان آنچه را انجام می‌دادند برایشان آراسته است.'),
(833, 6, 44, 'پس چون آنچه را که بدان پند داده شده بودند فراموش کردند، درهای هر چیزی [از نعمتها] را بر آنان گشودیم، تا هنگامی که به آنچه داده شده بودند شاد گردیدند؛ ناگهان [گریبان‌] آنان را گرفتیم، و یکباره نومید شدند.'),
(834, 6, 45, 'پس ریشه آن گروهی که ستم کردند برکنده شد، و ستایش برای خداوند، پروردگار جهانیان است.'),
(835, 6, 46, 'بگو: «به نظر شما، اگر خدا شنوایی شما و دیدگانتان را بگیرد و بر دلهایتان مُهر نهد، آیا غیر از خدا کدام معبودی است که آن را به شما بازپس دهد؟» بنگر چگونه آیات [خود] را [گوناگون‌] بیان می‌کنیم، سپس آنان روی برمی‌تابند؟'),
(836, 6, 47, 'بگو: «به نظر شما، اگر عذاب خدا ناگهان یا آشکارا به شما برسد، آیا جز گروه ستمگران [کسی‌] هلاک خواهد شد؟»'),
(837, 6, 48, 'و ما پیامبران [خود] را جز بشارتگر و هشداردهنده نمی‌فرستیم، پس کسانی که ایمان آورند و نیکوکاری کنند بیمی بر آنان نیست و اندوهگین نخواهند شد.'),
(838, 6, 49, 'و کسانی که آیات ما را دروغ انگاشتند، به [سزای‌] آنکه نافرمانی می‌کردند، عذاب به آنان خواهد رسید.'),
(839, 6, 50, 'بگو: «به شما نمی‌گویم گنجینه‌های خدا نزد من است؛ و غیب نیز نمی‌دانم؛ و به شما نمی‌گویم که من فرشته‌ام. جز آنچه را که به سوی من وحی می‌شود پیروی نمی‌کنم.» بگو: «آیا نابینا و بینا یکسان است؟ آیا تفکّر نمی‌کنید.»'),
(840, 6, 51, 'و به وسیله این [قرآن‌] کسانی را که بیم دارند که به سوی پروردگارشان محشور شوند هشدار ده [چرا] که غیر او برای آنها یار و شفیعی نیست، باشد که پروا کنند.'),
(841, 6, 52, 'و کسانی را که پروردگار خود را بامدادان و شامگاهان می‌خوانند -در حالی که خشنودی او را می‌خواهند- مران. از حساب آنان چیزی بر عهده تو نیست، و از حساب تو [نیز] چیزی بر عهده آنان نیست، تا ایشان را برانی و از ستمکاران باشی.'),
(842, 6, 53, 'و بدین گونه ما برخی از آنان را به برخی دیگر آزمودیم، تا بگویند: «آیا اینانند که از میان ما، خدا بر ایشان منّت نهاده است؟» آیا خدا به [حال‌] سپاسگزاران داناتر نیست؟'),
(843, 6, 54, 'و چون کسانی که به آیات ما ایمان دارند، نزد تو آیند، بگو: «درود بر شما»، پروردگارتان رحمت را بر خود مقرّر کرده که هر کس از شما به نادانی کار بدی کند و آنگاه به توبه و صلاح آید، پس وی آمرزنده مهربان است.'),
(844, 6, 55, 'و این گونه، آیات [خود] را به روشنی بیان می‌کنیم تا راه و رسم گناهکاران روشن شود.'),
(845, 6, 56, 'بگو: «من نهی شده‌ام که کسانی را که شما غیر از خدا می‌خوانید بپرستم!» بگو: «من از هوسهای شما پیروی نمی‌کنم، و گر نه گمراه شوم و از راه‌یافتگان نباشم.»'),
(846, 6, 57, 'بگو: «من از جانب پروردگارم دلیل آشکاری [همراه‌] دارم، و[لی‌] شما آن را دروغ پنداشتید، [و] آنچه را به شتاب خواستار آنید در اختیار من نیست. فرمان جز به دست خدا نیست، که حق را بیان می‌کند، و او بهترین داوران است.»'),
(847, 6, 58, 'بگو: «اگر آنچه را با شتاب خواستار آنید نزد من بود، قطعاً میان من و شما کار به انجام رسیده بود، و خدا به [حال‌] ستمکاران داناتر است.»'),
(848, 6, 59, 'و کلیدهای غیب، تنها نزد اوست. جز او [کسی‌] آن را نمی‌داند، و آنچه در خشکی و دریاست می‌داند، و هیچ برگی فرو نمی‌افتد مگر [اینکه‌] آن را می‌داند، و هیچ دانه‌ای در تاریکیهای زمین، و هیچ تر و خشکی نیست مگر اینکه در کتابی روشن [ثبت ]است.'),
(849, 6, 60, 'و اوست کسی که شبانگاه، روح شما را [به هنگام خواب‌] می‌گیرد؛ و آنچه را در روز به دست آورده‌اید می‌داند؛ سپس شما را در آن بیدار می‌کند، تا هنگامی معیّن به سر آید؛ آنگاه بازگشت شما به سوی اوست؛ سپس شما را به آنچه انجام می‌داده‌اید آگاه خواهد کرد.'),
(850, 6, 61, 'و اوست که بر بندگانش قاهر [و غالب‌] است؛ و نگهبانانی بر شما می‌فرستد، تا هنگامی که یکی از شما را مرگ فرا رسد، فرشتگان ما جانش بستانند، در حالی که کوتاهی نمی‌کنند.'),
(851, 6, 62, 'آنگاه به سوی خداوند -مولای بحقشان- برگردانیده شوند. آگاه باشید که داوری از آن اوست، و او سریعترین حسابرسان است.'),
(852, 6, 63, 'بگو: «چه کسی شما را از تاریکیهای خشکی و دریا می‌رهاند؟ در حالی که او را به زاری و در نهان می‌خوانید: که اگر ما را از این [مهلکه‌] برهاند، البته از سپاسگزاران خواهیم بود.»'),
(853, 6, 64, 'بگو: «خداست که شما را از آن [تاریکیها] و از هر اندوهی می‌رهاند، باز شما شرک می‌ورزید.»'),
(854, 6, 65, 'بگو: «او تواناست که از بالای سرتان یا از زیر پاهایتان عذابی بر شما بفرستد یا شما را گروه گروه به هم اندازد [و دچار تفرقه سازد] و عذاب بعضی از شما را به بعضی [دیگر] بچشاند.» بنگر، چگونه آیات [خود] را گوناگون بیان می‌کنیم باشد که آنان بفهمند.'),
(855, 6, 66, 'و قوم تو آن [=قرآن‌] را دروغ شمردند، در حالی که آن بر حق است. بگو: «من بر شما نگهبان نیستم.»'),
(856, 6, 67, 'برای هر خبری هنگام [وقوع‌] است، و به زودی خواهید دانست.'),
(857, 6, 68, 'و چون ببینی کسانی [به قصد تخطئه‌] در آیات ما فرو می‌روند از ایشان روی برتاب، تا در سخنی غیر از آن درآیند؛ و اگر شیطان تو را [در این باره‌] به فراموشی انداخت، پس از توجّه، [دیگر] با قوم ستمکار منشین.'),
(858, 6, 69, 'و چیزی از حساب آنان [=ستمکاران‌] بر عهده کسانی که پروا[ی خدا] دارند، نیست. لیکن، تذکّر دادن [لازم‌] است، باشد که [از استهزا] پرهیز کنند.'),
(859, 6, 70, 'و کسانی را که دین خود را به بازی و سرگرمی گرفتند و زندگی دنیا آنان را فریفته است، رها کن؛ و [مردم را] به وسیله این [قرآن‌] اندرز ده؛ مبادا کسی به [کیفر] آنچه کسب کرده به هلاکت افتد، در حالی که برای او در برابر خدا یاری و شفاعتگری نباشد؛ و اگر [برای رهایی خود] هر گونه فدیه‌ای دهد، از او پذیرفته نگردد. اینانند که به [سزای‌] آنچه کسب کرده‌اند به هلاکت افتاده‌اند، و به [کیفر] آنکه کفر می‌ورزیدند، شرابی از آب جوشان و عذابی پر درد خواهند داشت.'),
(860, 6, 71, 'بگو: «آیا به جای خدا چیزی را بخوانیم که نه سودی به ما می‌رساند و نه زیانی؛ و آیا پس از اینکه خدا ما را هدایت کرده از عقیده خود بازگردیم؟ مانند کسی که شیطانها او را در بیابان از راه به در برده‌اند، و حیران [بر جای مانده ]است؟ برای او یارانی است که وی را به سوی هدایت می‌خوانند که:» به سوی ما بیا. «بگو:» هدایت خداست که هدایتِ [واقعی‌] است، و دستور یافته‌ایم که تسلیم پروردگار جهانیان باشیم.'),
(861, 6, 72, 'و اینکه نماز برپا دارید و از او بترسید، و هم اوست که نزد وی محشور خواهید گردید.'),
(862, 6, 73, 'و او کسی است که آسمانها و زمین را به حق آفرید، و هر گاه که می‌گوید: «باش»، بی‌درنگ موجود شود؛ سخنش راست است؛ و روزی که در صور دمیده شود، فرمانروایی از آن اوست؛ داننده غیب و شهود است؛ و اوست حکیم آگاه.'),
(863, 6, 74, 'و [یاد کن‌] هنگامی را که ابراهیم به پدر خود «آزر» گفت: «آیا بتان را خدایان [خود] می‌گیری؟ من همانا تو و قوم تو را در گمراهی آشکاری می‌بینم.»'),
(864, 6, 75, 'و این گونه، ملکوت آسمانها و زمین را به ابراهیم نمایاندیم تا از جمله یقین‌کنندگان باشد.'),
(865, 6, 76, 'پس چون شب بر او پرده افکند، ستاره‌ای دید؛ گفت: «این پروردگار من است.» و آنگاه چون غروب کرد، گفت: «غروب‌کنندگان را دوست ندارم.»'),
(866, 6, 77, 'و چون ماه را در حال طلوع دید، گفت: «این پروردگار من است.» آنگاه چون ناپدید شد، گفت: «اگر پروردگارم مرا هدایت نکرده بود قطعاً از گروه گمراهان بودم.»'),
(867, 6, 78, 'پس چون خورشید را برآمده دید، گفت: «این پروردگار من است. این بزرگتر است.» و هنگامی که افول کرد، گفت: «ای قوم من، من از آنچه [برای خدا] شریک می‌سازید بیزارم.»'),
(868, 6, 79, 'من از روی اخلاص، پاکدلانه روی خود را به سوی کسی گردانیدم که آسمانها و زمین را پدید آورده است؛ و من از مشرکان نیستم.'),
(869, 6, 80, 'و قومش با او به ستیزه پرداختند. گفت: «آیا با من در باره خدا محاجّه می‌کنید و حال آنکه او مرا راهنمایی کرده است؟ و من از آنچه شریک او می‌سازید بیمی ندارم، مگر آنکه پروردگارم چیزی بخواهد. علم پروردگارم به هر چیزی احاطه یافته است. پس آیا متذکّر نمی‌شوید؟»'),
(870, 6, 81, 'و چگونه از آنچه شریک [خدا] می‌گردانید بترسم، با آنکه شما خود از اینکه چیزی را شریک خدا ساخته‌اید که [خدا] دلیلی در باره آن بر شما نازل نکرده است نمی‌هراسید؟ پس اگر می‌دانید، کدام یک از [ما] دو دسته به ایمنی سزاوارتر است؟'),
(871, 6, 82, 'کسانی که ایمان آورده و ایمان خود را به شرک نیالوده‌اند، آنان راست ایمنی و ایشان راه‌یافتگانند.'),
(872, 6, 83, 'و آن حجّت ما بود که به ابراهیم در برابر قومش دادیم. درجات هر کس را که بخواهیم فرا می‌بریم، زیرا پروردگار تو حکیم داناست.'),
(873, 6, 84, 'و به او اسحاق و یعقوب را بخشیدیم، و همه را به راه راست درآوردیم، و نوح را از پیش راه نمودیم، و از نسل او داوود و سلیمان و ایوب و یوسف و موسی و هارون را [هدایت کردیم‌] و این گونه، نیکوکاران را پاداش می‌دهیم.'),
(874, 6, 85, 'و زکریّا و یحیی و عیسی و الیاس را که همه از شایستگان بودند،'),
(875, 6, 86, 'و اسماعیل و یسع و یونس و لوط، که جملگی را بر جهانیان برتری دادیم.'),
(876, 6, 87, 'و از پدران و فرزندان و برادرانشان برخی را [بر جهانیان برتری دادیم‌]، و آنان را برگزیدیم و به راه راست راهنمایی کردیم.'),
(877, 6, 88, 'این، هدایت خداست که هر کس از بندگانش را بخواهد بدان هدایت می‌کند. و اگر آنان شرک ورزیده بودند، قطعاً آن چه انجام می‌دادند از دستشان می‌رفت.'),
(878, 6, 89, 'آنان کسانی بودند که کتاب و داوری و نبوت بدیشان دادیم؛ و اگر اینان [=مشرکان‌] بدان کفر ورزند، بی‌گمان، گروهی [دیگر] را بر آن گماریم که بدان کافر نباشند.'),
(879, 6, 90, 'اینان کسانی هستند که خدا هدایتشان کرده است، پس به هدایت آنان اقتدا کن. بگو: «من، از شما هیچ مزدی بر این [رسالت‌] نمی‌طلبم. این [قرآن‌] جز تذکری برای جهانیان نیست.»'),
(880, 6, 91, 'و آنگاه که [یهودیان‌] گفتند: «خدا چیزی بر بشری نازل نکرده»، بزرگی خدا را چنانکه باید نشناختند. بگو: «چه کسی آن کتابی را که موسی آورده است نازل کرده؟ [همان کتابی که‌] برای مردم روشنایی و رهنمود است، [و] آن را به صورت طومارها درمی‌آورید. [آنچه را] از آن [می‌خواهید] آشکار و بسیاری را پنهان می‌کنید، در صورتی که چیزی که نه شما می‌دانستید و نه پدرانتان، [به وسیله آن‌] به شما آموخته شد.» بگو: «خدا [همه را فرستاده‌]»؛ آنگاه بگذار تا در ژرفای [باطل‌] خود به بازی [سرگرم‌] شوند.'),
(881, 6, 92, 'و این خجسته‌کتابی است که ما آن را فرو فرستادیم، [و] کتابهایی را که پیش از آن آمده تصدیق می‌کند. و برای اینکه [مردم‌]ام‌القری [=مکّه‌] و کسانی را که پیرامون آنند هشدار دهی. و کسانی که به آخرت ایمان می‌آورند، به آن [قرآن نیز] ایمان می‌آورند، و آنان بر نمازهای خود مراقبت می‌کنند.'),
(882, 6, 93, 'و کیست ستمکارتر از آن کس که بر خدا دروغ می‌بندد یا می‌گوید: «به من وحی شده»، در حالی که چیزی به او وحی نشده باشد، و آن کس که می‌گوید: «به زودی نظیر آنچه را خدا نازل کرده است نازل می‌کنم»؟ و کاش ستمکاران را در گردابهای مرگ می‌دیدی که فرشتگان [به سوی آنان‌] دستهایشان را گشوده‌اند [و نهیب می‌زنند:] «جانهایتان را بیرون دهید»؛ امروز به [سزای‌] آنچه بناحق بر خدا دروغ می‌بستید و در برابر آیات او تکبر می‌کردید، به عذاب خوارکننده کیفر می‌یابید.'),
(883, 6, 94, 'و همان گونه که شما را نخستین بار آفریدیم [اکنون نیز] تنها به سوی ما آمده‌اید، و آنچه را به شما عطا کرده بودیم پشت سر خود نهاده‌اید، و شفیعانی را که در [کار] خودتان، شریکان [خدا] می‌پنداشتید با شما نمی‌بینیم. به یقین، پیوند میان شما بریده شده، و آنچه را که می‌پنداشتید از دست شما رفته است.'),
(884, 6, 95, 'خدا شکافنده دانه و هسته است. زنده را از مرده، و مرده را از زنده بیرون می‌آورد. چنین است خدای شما؛ پس چگونه [از حق‌] منحرف می‌شوید؟'),
(885, 6, 96, '[هموست که‌] شکافنده صبح است، و شب را برای آرامش و خورشید و ماه را وسیله حساب قرار داده. این اندازه‌گیریِ آن توانای داناست.'),
(886, 6, 97, 'و اوست کسی که ستارگان را برای شما قرار داده تا به وسیله آنها در تاریکیهای خشکی و دریا راه یابید. به یقین، ما دلایل [خود] را برای گروهی که می‌دانند به روشنی بیان کرده‌ایم.'),
(887, 6, 98, 'و او همان کسی است که شما را از یک تن پدید آورد. پس [برای شما] قرارگاه و محل امانتی [مقرر کرد]. بی‌تردید، ما آیات [خود] را برای مردمی که می‌فهمند به روشنی بیان کرده‌ایم.'),
(888, 6, 99, 'و اوست کسی که از آسمان، آبی فرود آورد؛ پس به وسیله آن از هر گونه گیاه برآوردیم، و از آن [گیاه‌] جوانه سبزی خارج ساختیم که از آن، دانه‌های متراکمی برمی‌آوریم، و از شکوفه درخت خرما خوشه‌هایی است نزدیک به هم. و [نیز ]باغهایی از انگور و زیتون و انار -همانند و غیر همانند- خارج نمودیم. به میوه آن چون ثمر دهد و به [طرز] رسیدنش بنگرید. قطعاً در اینها برای مردمی که ایمان می‌آورند نشانه‌هاست.'),
(889, 6, 100, 'و برای خدا شریکانی از جن قرار دادند، با اینکه خدا آنها را خلق کرده است. و برای او، بی هیچ دانشی، پسران و دخترانی تراشیدند. او پاک و برتر است از آنچه وصف می‌کنند.'),
(890, 6, 101, 'پدیدآورنده آسمانها و زمین است. چگونه او را فرزندی باشد، در صورتی که برای او همسری نبوده، و هر چیزی را آفریده، و اوست که به هر چیزی داناست.'),
(891, 6, 102, 'این است خدا، پروردگار شما: هیچ معبودی جز او نیست، آفریننده هر چیزی است. پس او را بپرستید، و او بر هر چیزی نگهبان است.'),
(892, 6, 103, 'چشمها او را درنمی‌یابند و اوست که دیدگان را درمی‌یابد، و او لطیف آگاه است.'),
(893, 6, 104, 'به راستی رهنمودهایی از جانب پروردگارتان برای شما آمده است. پس هر که به دیده بصیرت بنگرد به سود خود او، و هر کس از سر بصیرت ننگرد به زیان خود اوست، و من بر شما نگهبان نیستم.'),
(894, 6, 105, 'و این گونه آیات [خود] را گوناگون بیان می‌کنیم، تا مبادا بگویند تو درس خوانده‌ای، و تا اینکه آن را برای گروهی که می‌دانند روشن سازیم.'),
(895, 6, 106, 'از آنچه از پروردگارت به تو وحی شده پیروی کن. هیچ معبودی جز او نیست، و از مشرکان روی بگردان.'),
(896, 6, 107, 'و اگر خدا می‌خواست آنان شرک نمی‌آوردند، و ما تو را بر ایشان نگهبان نکرده‌ایم، و تو وکیل آنان نیستی.'),
(897, 6, 108, 'و آنهایی را که جز خدا می‌خوانند دشنام مدهید که آنان از روی دشمنی [و] به نادانی، خدا را دشنام خواهند داد. این گونه برای هر امتی کردارشان را آراستیم. آنگاه بازگشت آنان به سوی پروردگارشان خواهد بود، و ایشان را از آنچه انجام می‌دادند آگاه خواهد ساخت.'),
(898, 6, 109, 'و با سخت‌ترین سوگندهایشان، به خدا سوگند خوردند که اگر معجزه‌ای برای آنان بیاید، حتماً بدان می‌گروند. بگو: «معجزات، تنها در اختیار خداست.» و شما چه می‌دانید که اگر [معجزه هم‌] بیاید باز ایمان نمی‌آورند.'),
(899, 6, 110, 'و دلها و دیدگانشان را برمی‌گردانیم [در نتیجه به آیات ما ایمان نمی‌آورند] چنانکه نخستین بار به آن ایمان نیاوردند. و آنان را رها می‌کنیم تا در طغیانشان سرگردان بمانند.'),
(900, 6, 111, 'و اگر ما فرشتگان را به سوی آنان می‌فرستادیم و اگر مردگان با آنان به سخن می‌آمدند، و هر چیزی را دسته دسته در برابر آنان گرد می‌آوردیم، باز هم ایمان نمی‌آوردند -جز اینکه خدا بخواهد- ولی بیشترشان نادانی می‌کنند.'),
(901, 6, 112, 'و بدین گونه برای هر پیامبری دشمنی از شیطانهای انس و جن برگماشتیم. بعضی از آنها به بعضی، برای فریب [یکدیگر]، سخنان آراسته القا می‌کنند؛ و اگر پروردگار تو می‌خواست چنین نمی‌کردند. پس آنان را با آنچه به دروغ می‌سازند واگذار.'),
(902, 6, 113, 'و [چنین مقرر شده است‌] تا دلهای کسانی که به آخرت ایمان نمی‌آورند به آن [سخن باطل‌] بگراید و آن را بپسندد، و تا اینکه آنچه را باید به دست بیاورند، به دست آورند.'),
(903, 6, 114, 'پس، آیا داوری جز خدا جویم؟ با اینکه اوست که این کتاب را به تفصیل به سوی شما نازل کرده است. و کسانی که کتاب [آسمانی‌] بدیشان داده‌ایم می‌دانند که آن از جانب پروردگارت به حق فرو فرستاده شده است. پس تو از تردیدکنندگان مباش.'),
(904, 6, 115, 'و سخن پروردگارت به راستی و داد، سرانجام گرفته است؛ و هیچ تغییردهنده‌ای برای کلمات او نیست؛ و او شنوای داناست.'),
(905, 6, 116, 'و اگر از بیشتر کسانی که در [این سر]زمین می‌باشند پیروی کنی، تو را از راه خدا گمراه می‌کنند. آنان جز از گمان [خود] پیروی نمی‌کنند و جز به حدس و تخمین نمی‌پردازند.'),
(906, 6, 117, 'باری، پروردگار تو به [حال‌] کسی که از راه او منحرف می‌شود داناتر است، و او به [حال‌] راه‌یافتگان [نیز] داناتر است.'),
(907, 6, 118, 'پس، اگر به آیات او ایمان دارید از آنچه نام خدا [به هنگام ذبح‌] بر آن برده شده است بخورید.'),
(908, 6, 119, 'و شما را چه شده است که از آنچه نام خدا بر آن برده شده است نمی‌خورید؟ با اینکه [خدا] آنچه را بر شما حرام کرده -جز آنچه بدان ناچار شده‌اید- برای شما به تفصیل بیان نموده است. و به راستی، بسیاری [از مردم، دیگران را] از روی نادانی، با هوسهای خود گمراه می‌کنند. آری، پروردگار تو به [حال‌] تجاوزکاران داناتر است.'),
(909, 6, 120, 'و گناه آشکار و پنهان را رها کنید، زیرا کسانی که مرتکب گناه می‌شوند، به زودی در برابر آنچه به دست می‌آوردند کیفر خواهند یافت.'),
(910, 6, 121, 'و از آنچه نام خدا بر آن برده نشده است مخورید، چرا که آن قطعاً نافرمانی است. و در حقیقت، شیطانها به دوستان خود وسوسه می‌کنند تا با شما ستیزه نمایند. و اگر اطاعتشان کنید قطعاً شما هم مشرکید.'),
(911, 6, 122, 'آیا کسی که مرده‌[دل‌] بود و زنده‌اش گردانیدیم و برای او نوری پدید آوردیم تا در پرتو آن، در میان مردم راه برود، چون کسی است که گویی گرفتار در تاریکیهاست و از آن بیرون‌آمدنی نیست؟ این گونه برای کافران آنچه انجام می‌دادند زینت داده شده است.'),
(912, 6, 123, 'و بدین گونه، در هر شهری گناهکاران بزرگش را می‌گماریم تا در آن به نیرنگ پردازند، و[لی‌] آنان جز به خودشان نیرنگ نمی‌زنند و درک نمی‌کنند.'),
(913, 6, 124, 'و چون آیتی برایشان بیاید، می‌گویند: «هرگز ایمان نمی‌آوریم تا اینکه نظیر آنچه به فرستادگان خدا داده شده است به ما [نیز] داده شود.» خدا بهتر می‌داند رسالتش را کجا قرار دهد. به زودی، کسانی را که مرتکب گناه شدند، به [سزای ]آنکه نیرنگ می‌کردند، در پیشگاه خدا خواری و شکنجه‌ای سخت خواهد رسید.'),
(914, 6, 125, 'پس کسی را که خدا بخواهد هدایت نماید، دلش را به پذیرش اسلام می‌گشاید؛ و هر که را بخواهد گمراه کند، دلش را سخت تنگ می‌گرداند؛ چنانکه گویی به زحمت در آسمان بالا می‌رود. این گونه، خدا پلیدی را بر کسانی که ایمان نمی‌آورند قرار می‌دهد.'),
(915, 6, 126, 'و راه راست پروردگارت همین است. ما آیات [خود] را برای گروهی که پند می‌گیرند، به روشنی بیان نموده‌ایم.'),
(916, 6, 127, 'برای آنان، نزد پروردگارشان سرای عافیت است، و به [پاداش‌] آنچه انجام می‌دادند، او یارشان خواهد بود.'),
(917, 6, 128, 'و [یاد کن‌] روزی را که همه آنان را گرد می‌آورد [و می‌فرماید:] «ای گروه جنیان، از آدمیان [پیروان‌] فراوان یافتید.» و هواخواهان آنها از [نوع‌] انسان می‌گویند: «پروردگارا، برخی از ما از برخی دیگر بهره برداری کرد، و به پایانی که برای ما معین کردی رسیدیم.» [خدا] می‌فرماید: «جایگاه شما آتش است؛ در آن ماندگار خواهید بود، مگر آنچه را خدا بخواهد [که خود تخفیف دهد]؛ آری، پروردگار تو حکیم داناست.»'),
(918, 6, 129, 'و این گونه برخی از ستمکاران را به [کیفر] آنچه به دست می‌آوردند، سرپرست برخی دیگر می‌گردانیم.'),
(919, 6, 130, 'ای گروه جن و انس، آیا از میان شما فرستادگانی برای شما نیامدند که آیات مرا بر شما بخوانند و از دیدار این روزتان به شما هشدار دهند؟ گفتند: «ما به زیان خود گواهی دهیم.» [که آری، آمدند] و زندگی دنیا فریبشان داد، و بر ضد خود گواهی دادند که آنان کافر بوده‌اند.'),
(920, 6, 131, 'این [اتمام حجت‌] بدان سبب است که پروردگار تو هیچ گاه شهرها را به ستم نابوده نکرده، در حالی که مردم آن غافل باشند.'),
(921, 6, 132, 'و برای هر یک [از این دو گروه‌]، از آنچه انجام داده‌اند، [در جزا] مراتبی خواهد بود، و پروردگارت از آنچه می‌کنند غافل نیست.'),
(922, 6, 133, 'و پروردگار تو بی‌نیاز و رحمتگر است. اگر بخواهد شما را می‌برد، و پس از شما، هر که را بخواهد جانشین [شما] می‌کند؛ همچنانکه شما را از نسل گروهی دیگر پدید آورده است.'),
(923, 6, 134, 'قطعاً آنچه به شما وعده داده می‌شود آمدنی است، و شما درمانده‌کنندگان [خدا] نیستید.'),
(924, 6, 135, 'بگو: «ای قوم من، هر چه مقدور شما هست انجام دهید؛ من [هم‌] انجام می‌دهم. به زودی خواهید دانست که فرجام [نیکوی‌] آن سرای از آنِ کیست. آری، ستمکاران رستگار نمی‌شوند.»'),
(925, 6, 136, 'و [مشرکان،] برای خدا از آنچه از کِشت و دامها که آفریده است سهمی گذاشتند، و به پندار خودشان گفتند: «این ویژه خداست و این ویژه بتان ما.» پس آنچه خاص بتانشان بود به خدا نمی‌رسید، و[لی‌] آنچه خاص خدا بود به بتانشان می‌رسید. چه بد داوری می‌کنند.'),
(926, 6, 137, 'و این گونه برای بسیاری از مشرکان، بتانشان کشتن فرزندانشان را آراستند، تا هلاکشان کنند و دینشان را بر آنان مشتبه سازند؛ و اگر خدا می‌خواست چنین نمی‌کردند. پس ایشان را با آنچه به دروغ می‌سازند رها کن.'),
(927, 6, 138, 'و به زعم خودشان گفتند: «اینها دامها و کشتزار[های‌] ممنوع است، که جز کسی که ما بخواهیم نباید از آن بخورد، و دامهایی است که [سوار شدن بر] پشت آنها حرام شده است. و دامهایی [داشتند] که [هنگام ذبح‌] نام خدا را بر آن‌[ها] نمی‌بردند به صرف افترا بر [خدا] به زودی [خدا] آنان را به خاطر آنچه افترا می‌بستند جزا می‌دهد.'),
(928, 6, 139, 'و گفتند: «آنچه در شکم این دامهاست اختصاص به مردان ما دارد و بر همسران ما حرام شده است، و اگر [آن جنین‌] مرده باشد، همه آنان [از زن و مرد ] در آن شریکند. به زودی [خدا] توصیف آنان را سزا خواهد داد، زیرا او حکیم داناست.'),
(929, 6, 140, 'کسانی که از روی بی‌خردی و نادانی، فرزندان خود را کشته‌اند، و آنچه را خدا روزیشان کرده بود -از راه افترا به خدا- حرام شمرده‌اند، سخت زیان کردند. آنان به راستی گمراه شده، و هدایت نیافته‌اند.'),
(930, 6, 141, 'و اوست کسی که باغهایی با داربست و بدون داربست، و خرمابن، و کشتزار با میوه‌های گوناگون آن، و زیتون، و انار، شبیه به یکدیگر و غیر شبیه پدید آورد. از میوه آن -چون ثمر داد- بخورید، و حق [بینوایان از] آن را روز بهره‌برداری از آن بدهید، و[لی‌] زیاده‌روی مکنید که او اسرافکاران را دوست ندارد.'),
(931, 6, 142, 'و [نیز] از دامها، حیوانات بارکش و حیوانات کرک و پشم‌دهنده را [پدید آورد]. از آنچه خدا روزیتان کرده است بخورید، و از پی گامهای شیطان مروید که او برای شما دشمنی آشکار است.'),
(932, 6, 143, 'هشت فرد [آفرید و بر شما حلال کرد]: از گوسفند دو تا، و از بز دو تا. بگو: «آیا [خدا] نرها[ی آنها] را حرام کرده یا ماده را؟ یا آنچه را که رحم آن دو ماده در بر گرفته است؟» اگر راست می‌گویید، از روی علم، به من خبر دهید.'),
(933, 6, 144, 'و از شتر دو، و از گاو دو. بگو: «آیا [خدا] نرها[ی آنها] را حرام کرده یا ماده‌ها را؟ یا آنچه را که رحم آن دو ماده در بر گرفته است؟» آیا وقتی خداوند شما را به این [تحریم‌] سفارش کرد حاضر بودید؟ پس کیست ستمکارتر از آنکس که بر خدا دروغ بندد، تا از روی نادانی، مردم را گمراه کند؟ آری، خدا گروه ستمکاران را راهنمایی نمی‌کند.'),
(934, 6, 145, 'بگو: «در آنچه به من وحی شده است، بر خورنده‌ای که آن را می‌خورد هیچ حرامی نمی‌یابم، مگر آنکه مردار یا خون ریخته یا گوشت خوک باشد که اینها همه پلیدند. یا [قربانیی که‌] از روی نافرمانی، [به هنگام ذبح‌] نام غیر خدا بر آن برده شده باشد. پس کسی که بدون سرکشی و زیاده‌خواهی [به خوردن آنها] ناچار گردد، قطعاً پروردگار تو آمرزنده مهربان است.'),
(935, 6, 146, 'و بر یهودیان، هر [حیوان‌] چنگال‌داری را حرام کردیم، و از گاو و گوسفند، پیه آن دو را بر آنان حرام کردیم، به استثنای پیه‌هایی که بر پشت آن دو یا بر روده‌هاست یا آنچه با استخوان درآمیخته است. این [تحریم‌] را به سزای ستم‌کردنشان، به آنان کیفر دادیم، و ما البتّه راستگوییم.'),
(936, 6, 147, '[ای پیامبر،] پس اگر تو را تکذیب کردند، بگو: «پروردگار شما دارای رحمتی گسترده است؛ و [با این حال‌] عذاب او از گروه مجرمان بازگردانده نخواهد شد.»'),
(937, 6, 148, 'کسانی که شرک آوردند به زودی خواهند گفت: «اگر خدا می‌خواست، نه ما و نه پدرانمان شرک نمی‌آوردیم، و چیزی را [خودسرانه‌] تحریم نمی‌کردیم.» کسانی هم که پیش از آنان بودند، همین گونه [پیامبران خود را] تکذیب کردند، تا عقوبت ما را چشیدند. بگو: «آیا نزد شما دانشی هست که آن را برای ما آشکار کنید؟ شما جز از گمان پیروی نمی‌کنید، و جز دروغ نمی‌گویید.»'),
(938, 6, 149, 'بگو: «برهان رسا ویژه خداست، و اگر [خدا] می‌خواست قطعاً همه شما را هدایت می‌کرد.»'),
(939, 6, 150, 'بگو: «گواهان خود را که گواهی می‌دهند به اینکه خدا اینها را حرام کرده، بیاورید.» پس اگر هم شهادت دادند تو با آنان شهادت مده، و هوسهای کسانی را که آیات ما را تکذیب کردند و کسانی که به آخرت ایمان نمی‌آورند و [معبودان دروغین را] با پروردگارشان همتا قرار می‌دهند، پیروی مکن.'),
(940, 6, 151, 'بگو: «بیایید تا آنچه را پروردگارتان بر شما حرام کرده برای شما بخوانم: چیزی را با او شریک قرار مدهید؛ و به پدر و مادر احسان کنید؛ و فرزندان خود را از بیم تنگدستی مکشید؛ ما شما و آنان را روزی می‌رسانیم؛ و به کارهای زشت -چه علنی آن و چه پوشیده‌[اش‌]- نزدیک مشوید؛ و نَفْسی را که خدا حرام گردانیده، جز بحق مکشید. اینهاست که [خدا] شما را به [انجام دادن‌] آن سفارش کرده است، باشد که بیندیشد.'),
(941, 6, 152, 'و به مال یتیم -جز به نحوی [هر چه نیکوتر]- نزدیک مشوید، تا به حد رشد خود برسد. و پیمانه و ترازو را به عدالت، تمام بپیمایید. هیچ کس را جز به قدر توانش تکلیف نمی‌کنیم. و چون [به داوری یا شهادت‌] سخن گویید دادگری کنید، هر چند [در باره‌] خویشاوند [شما] باشد. و به پیمان خدا وفا کنید. اینهاست که [خدا] شما را به آن سفارش کرده است، باشد که پند گیرید.'),
(942, 6, 153, 'و [بدانید] این است راه راست من؛ پس، از آن پیروی کنید. و از راه‌ها[ی دیگر] که شما را از راه وی پراکنده می‌سازد پیروی مکنید. اینهاست که [خدا] شما را به آن سفارش کرده است، باشد که به تقوا گرایید.'),
(943, 6, 154, 'آنگاه به موسی کتاب دادیم، برای اینکه [نعمت را] بر کسی که نیکی کرده است تمام کنیم، و برای اینکه هر چیزی را بیان نماییم، و هدایت و رحمتی باشد، امید که به لقای پروردگارشان ایمان بیاورند.'),
(944, 6, 155, 'و این، خجسته کتابی است که ما آن را نازل کردیم؛ پس، از آن پیروی کنید و پرهیزگاری نمایید، باشد که مورد رحمت قرار گیرید،'),
(945, 6, 156, 'تا نگویید: «کتاب [آسمانی‌]، تنها بر دو طایفه پیش از ما نازل شده، و ما از آموختن آنان بی‌خبر بودیم.»'),
(946, 6, 157, 'یا نگویید: «اگر کتاب بر ما نازل می‌شد، قطعاً از آنان هدایت‌یافته‌تر بودیم.» اینک حجّتی از جانب پروردگارتان برای شما آمده و رهنمود و رحمتی است. پس کیست ستمکارتر از آن کس که آیات خدا را دروغ پندارد و از آنها روی گرداند؟ به زودی کسانی را که از آیات ما روی می‌گردانند، به سبب [همین‌] اعراضشان، به عذابی سخت مجازات خواهیم کرد.'),
(947, 6, 158, 'آیا جز این انتظار دارند که فرشتگان به سویشان بیایند، یا پروردگارت بیاید، یا پاره‌ای از نشانه‌های پروردگارت بیاید؟ [اما] روزی که پاره‌ای از نشانه‌های پروردگارت [پدید] آید، کسی که قبلاً ایمان نیاورده یا خیری در ایمان آوردن خود به دست نیاورده، ایمان آوردنش سود نمی‌بخشد. بگو: «منتظر باشید که ما [هم‌] منتظریم.»'),
(948, 6, 159, 'کسانی که دین خود را پراکنده ساختند و فرقه فرقه شدند، تو هیچ گونه مسؤول ایشان نیستی، کارشان فقط با خداست. آنگاه به آنچه انجام می‌دادند آگاهشان خواهد کرد.'),
(949, 6, 160, 'هر کس کار نیکی بیاورد، ده برابر آن [پاداش‌] خواهد داشت، و هر کس کار بدی بیاورد، جز مانند آن جزا نیابد و بر آنان ستم نرود.'),
(950, 6, 161, 'بگو: «آری! پروردگارم مرا به راه راست هدایت کرده است: دینی پایدار، آیین ابراهیمِ حق‌گرای! و او از مشرکان نبود.»'),
(951, 6, 162, 'بگو: «در حقیقت، نماز من و [سایر] عبادات من و زندگی و مرگ من، برای خدا، پروردگار جهانیان است.»'),
(952, 6, 163, '[که‌] او را شریکی نیست، و بر این [کار] دستور یافته‌ام، و من نخستین مسلمانم.'),
(953, 6, 164, 'بگو: «آیا جز خدا پروردگاری بجویم؟ با اینکه او پروردگار هر چیزی است، و هیچ کس جز بر زیان خود [گناهی‌] انجام نمی‌دهد، و هیچ باربرداری بار [گناه‌] دیگری را برنمی‌دارد، آنگاه بازگشت شما به سوی پروردگارتان خواهد بود، پس ما را به آنچه در آن اختلاف می‌کردید آگاه خواهد کرد.'),
(954, 6, 165, 'و اوست کسی که شما را در زمین جانشین [یکدیگر] قرار داد، و بعضی از شما را بر برخی دیگر به درجاتی برتری داد تا شما را در آنچه به شما داده است بیازماید. آری، پروردگار تو زودکیفر است، و [هم‌] او بس آمرزنده مهربان است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف، لام، میم، صاد.'),
(956, 7, 2, 'کتابی است که به سوی تو فرو فرستاده شده است -پس نباید در سینه تو از ناحیه آن، تنگی باشد- تا به وسیله آن هشدار دهی و برای مؤمنان پندی باشد.'),
(957, 7, 3, 'آنچه را از جانب پروردگارتان به سوی شما فرو فرستاده شده است، پیروی کنید؛ و جز او از معبودان [دیگر] پیروی مکنید. چه اندک پند می‌گیرید!'),
(958, 7, 4, 'و چه بسیار شهرها که [مردمِ‌] آن را به هلاکت رسانیدیم، و در حالی که به خواب شبانگاهی رفته یا نیمروز غنوده بودند، عذاب ما به آنها رسید.'),
(959, 7, 5, 'و هنگامی که عذاب ما بر آنان آمد سخنشان جز این نبود که گفتند: «راستی که ما ستمکار بودیم.»'),
(960, 7, 6, 'پس، قطعاً از کسانی که [پیامبران‌] به سوی آنان فرستاده شده‌اند خواهیم پرسید، و قطعاً از [خودِ] فرستادگان [نیز] خواهیم پرسید.'),
(961, 7, 7, 'و از روی دانش به آنان گزارش خواهیم داد و ما [از احوال آنان‌] غایب نبوده‌ایم.'),
(962, 7, 8, 'و در آن روز، سنجش [اعمال‌] درست است. پس هر کس میزانهای [عمل‌] او گران باشد، آنان خود رستگارانند.'),
(963, 7, 9, 'و هر کس میزانهای [عمل‌] او سبک باشد، پس آنانند که به خود زیان زده‌اند، چرا که به آیات ما ستم کرده‌اند.'),
(964, 7, 10, 'و قطعاً شما را در زمین قدرت عمل دادیم، و برای شما در آن، وسایل معیشت نهادیم، [اما] چه کم سپاسگزاری می‌کنید.'),
(965, 7, 11, 'و در حقیقت، شما را خلق کردیم، سپس به صورتگری شما پرداختیم؛ آنگاه به فرشتگان گفتیم: «برای آدم سجده کنید.» پس [همه‌] سجده کردند، جز ابلیس که از سجده‌کنندگان نبود.'),
(966, 7, 12, 'فرمود: «چون تو را به سجده امر کردم چه چیز تو را باز داشت از اینکه سجده کنی؟» گفت: «من از او بهترم. مرا از آتشی آفریدی و او را از گِل آفریدی.»'),
(967, 7, 13, 'فرمود: «از آن [مقام‌] فرو شو، تو را نرسد که در آن [جایگاه‌] تکبّر نمایی. پس بیرون شو که تو از خوارشدگانی.»'),
(968, 7, 14, 'گفت: «مرا تا روزی که [مردم‌] برانگیخته خواهند شد مهلت ده.»'),
(969, 7, 15, 'فرمود: «تو از مهلت‌یافتگانی.»'),
(970, 7, 16, 'گفت: «پس به سبب آنکه مرا به بیراهه افکندی، من هم برای [فریفتن‌] آنان حتماً بر سر راه راست تو خواهم نشست.'),
(971, 7, 17, '«آنگاه از پیش رو و از پشت سرشان و از طرف راست و از طرف چپشان بر آنها می‌تازم، و بیشترشان را شکرگزار نخواهی یافت.»'),
(972, 7, 18, 'فرمود: «نکوهیده و رانده، از آن [مقام‌] بیرون شو؛ که قطعاً هر که از آنان از تو پیروی کند، جهنّم را از همه شما پر خواهم کرد.»'),
(973, 7, 19, '«و ای آدم! تو با جفت خویش در آن باغ سکونت گیر، و از هر جا که خواهید بخورید، و[لی‌] به این درخت نزدیک مشوید که از ستمکاران خواهید شد.»'),
(974, 7, 20, 'پس شیطان، آن دو را وسوسه کرد تا آنچه را از عورتهایشان برایشان پوشیده مانده بود، برای آنان نمایان گرداند؛ و گفت: «پروردگارتان شما را از این درخت منع نکرد، جز [برای‌] آنکه [مبادا] دو فرشته گردید یا از [زمره‌] جاودانان شوید.»'),
(975, 7, 21, 'و برای آن دو سوگند یاد کرد که: من قطعاً از خیرخواهان شما هستم.'),
(976, 7, 22, 'پس آن دو را با فریب به سقوط کشانید؛ پس چون آن دو از [میوه‌] آن درختِ [ممنوع‌] چشیدند، برهنگی‌هایشان بر آنان آشکار شد، و به چسبانیدن برگ‌[های درختانِ‌] بهشت بر خود آغاز کردند، و پروردگارشان بر آن دو بانگ بر زد: «مگر شما را از این درخت منع نکردم و به شما نگفتم که در حقیقت شیطان برای شما دشمنی آشکار است.»'),
(977, 7, 23, 'گفتند: «پروردگارا، ما بر خویشتن ستم کردیم، و اگر بر ما نبخشایی و به ما رحم نکنی، مسلماً از زیانکاران خواهیم بود.»'),
(978, 7, 24, 'فرمود: «فرود آیید، که بعضی از شما دشمن بعضی [دیگر]ید؛ و برای شما در زمین، تا هنگامی [معیّن‌] قرارگاه و برخورداری است.»'),
(979, 7, 25, 'فرمود: «در آن زندگی می‌کنید و در آن می‌میرید و از آن برانگیخته خواهید شد.»'),
(980, 7, 26, 'ای فرزندان آدم، در حقیقت، ما برای شما لباسی فرو فرستادیم که عورتهای شما را پوشیده می‌دارد و [برای شما] زینتی است، و[لی‌] بهترین جامه، [لباس‌] تقوا است. این از نشانه‌های [قدرت‌] خداست، باشد که متذکّر شوند.'),
(981, 7, 27, 'ای فرزندان آدم، زنهار تا شیطان شما را به فتنه نیندازد؛ چنانکه پدر و مادر شما را از بهشت بیرون راند، و لباسشان را از ایشان برکند، تا عورتهایشان را بر آنان نمایان کند. در حقیقت، او و قبیله‌اش، شما را از آنجا که آنها را نمی‌بینید، می‌بینند. ما شیاطین را دوستان کسانی قرار دادیم که ایمان نمی‌آورند.'),
(982, 7, 28, 'و چون کار زشتی کنند، می‌گویند: «پدران خود را بر آن یافتیم و خدا ما را بدان فرمان داده است.» بگو: «قطعاً خدا به کار زشت فرمان نمی‌دهد، آیا چیزی را که نمی‌دانید به خدا نسبت می‌دهید؟»'),
(983, 7, 29, 'بگو: «پروردگارم به دادگری فرمان داده است، و [اینکه‌] در هر مسجدی روی خود را مستقیم [به سوی قبله‌] کنید، و در حالی که دین خود را برای او خالص گردانیده‌اید وی را بخوانید، همان گونه که شما را پدید آورد [به سوی او] برمی‌گردید.»'),
(984, 7, 30, '[در حالی که‌] گروهی را هدایت نموده، و گروهی گمراهی بر آنان ثابت شده است، زیرا آنان شیاطین را به جای خدا، دوستان [خود] گرفته‌اند و می‌پندارند که راه‌یافتگانند.'),
(985, 7, 31, 'ای فرزندان آدم، جامه خود را در هر نمازی برگیرید، و بخورید و بیاشامید و[لی‌] زیاده‌روی مکنید که او اسرافکاران را دوست نمی‌دارد.'),
(986, 7, 32, '[ای پیامبر] بگو: «زیورهایی را که خدا برای بندگانش پدید آورده، و [نیز ] روزیهای پاکیزه را چه کسی حرام گردانیده؟» بگو: «این [نعمتها] در زندگی دنیا برای کسانی است که ایمان آورده‌اند و روز قیامت [نیز ]خاصّ آنان می‌باشد.» این گونه آیات [خود] را برای گروهی که می‌دانند به روشنی بیان می‌کنیم.'),
(987, 7, 33, 'بگو: «پروردگار من فقط زشتکاریها را -چه آشکارش [باشد] و چه پنهان- و گناه و ستم ناحق را حرام گردانیده است؛ و [نیز] اینکه چیزی را شریک خدا سازید که دلیلی بر [حقّانیّت‌] آن نازل نکرده؛ و اینکه چیزی را که نمی‌دانید به خدا نسبت دهید.'),
(988, 7, 34, 'و برای هر امّتی اجلی است؛ پس چون اجلشان فرا رسد، نه [می‌توانند] ساعتی آن را پس اندازند و نه پیش.'),
(989, 7, 35, 'ای فرزندان آدم، چون پیامبرانی از خودتان برای شما بیایند و آیات مرا بر شما بخوانند، پس هر کس به پرهیزگاری و صلاح گراید، نه بیمی بر آنان خواهد بود و نه اندوهگین می شوند.'),
(990, 7, 36, 'و کسانی که آیات ما را دروغ انگاشتند و از [پذیرش‌] آنها تکبّر ورزیدند آنان همدم آتشند [و] در آن جاودانند.'),
(991, 7, 37, 'پس کیست ستمکارتر از آن کس که بر خدا دروغ بندد یا آیات او را تکذیب کند؟ اینان کسانی هستند که نصیبشان از آنچه مقرّر شده به ایشان خواهد رسید، تا آنگاه که فرشتگان ما به سراغشان بیایند که جانشان بستانند، می‌گویند: «آنچه غیر از خدا می‌خواندید کجاست؟» می‌گویند: «از [چشم‌] ما ناپدید شدند» و علیه خود گواهی می‌دهند که آنان کافر بودند.'),
(992, 7, 38, 'می‌فرماید: «در میان امّتهایی از جنّ و انس، که پیش از شما بوده‌اند، داخل آتش شوید.» هر بار که امّتی [در آتش‌] درآید، همکیشان خود را لعنت کند، تا وقتی که همگی در آن به هم پیوندند؛ [آنگاه‌] پیروانشان در باره پیشوایانشان می‌گویند: «پروردگارا، اینان ما را گمراه کردند، پس دو برابر عذاب آتش به آنان بده.» [خدا] می‌فرماید: «برای هر کدام [عذاب‌] دو چندان است ولی شما نمی‌دانید.»'),
(993, 7, 39, 'و پیشوایانشان به پیروانشان می‌گویند: «شما را بر ما امتیازی نیست. پس به سزای آنچه به دست می‌آوردید عذاب را بچشید.»'),
(994, 7, 40, 'در حقیقت، کسانی که آیات ما را دروغ شمردند و از [پذیرفتن‌] آنها تکبّر ورزیدند، درهای آسمان را برایشان نمی‌گشایند و در بهشت درنمی‌آیند مگر آنکه شتر در سوراخ سوزن داخل شود. و بدینسان بزهکاران را کیفر می‌دهیم.'),
(995, 7, 41, 'برای آنان از جهنّم بستری و از بالایشان پوششهاست، و این گونه بیدادگران را سزا می‌دهیم.'),
(996, 7, 42, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند -هیچ کسی را جز به قدر توانش تکلیف نمی‌کنیم- آنان همدم بهشتند [که‌] در آن جاودانند.'),
(997, 7, 43, 'و هر گونه کینه‌ای را از سینه‌هایشان می‌زداییم. از زیر [قصرهای‌]شان نهرها جاری است، و می‌گویند: «ستایش خدایی را که ما را بدین [راه‌] هدایت نمود، و اگر خدا ما را رهبری نمی‌کرد ما خود هدایت نمی‌یافتیم. در حقیقت، فرستادگانِ پروردگارِ ما حق را آوردند.» و به آنان ندا داده می‌شود که این همان بهشتی است که آن را به [پاداش‌] آنچه انجام می‌دادید میراث یافته‌اید.'),
(998, 7, 44, 'و بهشتیان، دوزخیان را آواز می‌دهند که: «ما آنچه را پروردگارمان به ما وعده داده بود درست یافتیم؛ آیا شما [نیز] آنچه را پروردگارتان وعده کرده بود راست و درست یافتید؟» می‌گویند: «آری.» پس آوازدهنده‌ای میان آنان آواز درمی‌دهد که: «لعنت خدا بر ستمکاران باد.»'),
(999, 7, 45, 'همانان که [مردم را] از راه خدا باز می‌دارند و آن را کج می‌خواهند و آنها آخرت را منکرند.'),
(1000, 7, 46, 'و میان آن دو [گروه‌]، حایلی است، و بر اعراف، مردانی هستند که هر یک [از آن دو دسته‌] را از سیمایشان می‌شناسند، و بهشتیان را -که هنوز وارد آن نشده و[لی‌] [بدان‌] امید دارند- آواز می‌دهند که: «سلام بر شما.»'),
(1001, 7, 47, 'و چون چشمانشان به سوی دوزخیان گردانیده شود، می‌گویند: «پروردگارا، ما را در زمره گروه ستمکاران قرار مده.»'),
(1002, 7, 48, 'و اهل اَعراف، مردانی را که آنان را از سیمایشان می‌شناسند، ندا می دهند [و] می‌گویند: «جمعیّت شما و آن [همه‌] گردنکشی که می‌کردید، به حال شما سودی نداشت.»'),
(1003, 7, 49, '«آیا اینان همان کسان نبودند که سوگند یاد می‌کردید که خدا آنان را به رحمتی نخواهد رسانید؟» « [اینک‌] به بهشت درآیید. نه بیمی بر شماست و نه اندوهگین می‌شوید.»'),
(1004, 7, 50, 'و دوزخیان، بهشتیان را آواز می‌دهند که: «از آن آب یا از آنچه خدا روزیِ شما کرده، بر ما فرو ریزید.» می‌گویند: «خدا آنها را بر کافران حرام کرده است.»'),
(1005, 7, 51, 'همانان که دین خود را سرگرمی و بازی پنداشتند، و زندگی دنیا مغرورشان کرد. پس همان گونه که آنان دیدار امروز خود را از یاد بردند، و آیات ما را انکار می‌کردند، ما [هم‌] امروز آنان را از یاد می‌بریم.'),
(1006, 7, 52, 'و در حقیقت، ما برای آنان کتابی آوردیم که آن را از روی دانش، روشن و شیوایش ساخته‌ایم، و برای گروهی که ایمان می‌آورند هدایت و رحمتی است.'),
(1007, 7, 53, 'آیا [آنان‌] جز در انتظار تأویل آنند؟ روزی که تأویلش فرا رسد، کسانی که آن را پیش از آن به فراموشی سپرده‌اند می‌گویند: «حقاً فرستادگان پروردگار ما حق را آوردند. پس آیا [امروز] ما را شفاعتگرانی هست که برای ما شفاعت کنند یا [ممکن است به دنیا] بازگردانیده شویم، تا غیر از آنچه انجام می‌دادیم انجام دهیم؟» به راستی که [آنان‌] به خویشتن زیان زدند، و آنچه را به دروغ می‌ساختند از کف دادند.'),
(1008, 7, 54, 'در حقیقت، پروردگار شما آن خدایی است که آسمانها و زمین را در شش روز آفرید؛ سپس بر عرش [جهانداری‌] استیلا یافت. روز را به شب -که شتابان آن را می طلبد- می‌پوشاند، و [نیز] خورشید و ماه و ستارگان را که به فرمان او رام شده‌اند [پدید آورد]. آگاه باش که [عالم‌] خلق و امر از آن اوست. فرخنده خدایی است پروردگار جهانیان.'),
(1009, 7, 55, 'پروردگار خود را به زاری و نهانی بخوانید که او از حدگذرندگان را دوست نمی‌دارد.'),
(1010, 7, 56, 'و در زمین پس از اصلاح آن فساد مکنید، و با بیم و امید او را بخوانید که رحمت خدا به نیکوکاران نزدیک است.'),
(1011, 7, 57, 'و اوست که بادها را پیشاپیش [باران‌] رحمتش مژده‌رسان می‌فرستد، تا آن گاه که ابرهای گرانبار را بردارند، آن را به سوی سرزمینی مرده برانیم، و از آن، باران فرود آوریم؛ و از هر گونه میوه‌ای [از خاک ]برآوریم. بدینسان مردگان را [نیز از قبرها] خارج می‌سازیم، باشد که شما متذکر شوید.'),
(1012, 7, 58, 'و زمین پاک [و آماده‌]، گیاهش به اذن پروردگارش برمی‌آید؛ و آن [زمینی‌] که ناپاک [و نامناسب‌] است [گیاهش‌] جز اندک و بی‌فایده برنمی‌آید. این گونه، آیات [خود] را برای گروهی که شکر می‌گزارند، گونه‌گون بیان می‌کنیم.'),
(1013, 7, 59, 'همانا نوح را به سوی قومش فرستادیم. پس گفت: «ای قوم من، خدا را بپرستید که برای شما معبودی جز او نیست، من از عذاب روزی سترگ بر شما بیمناکم.»'),
(1014, 7, 60, 'سران قومش گفتند: «واقعاً ما تو را در گمراهی آشکاری می‌بینیم.»'),
(1015, 7, 61, 'گفت: «ای قوم من، هیچ گونه گمراهی در من نیست، بلکه من فرستاده‌ای از جانب پروردگار جهانیانم.'),
(1016, 7, 62, 'پیامهای پروردگارم را به شما می‌رسانم و اندرزتان می‌دهم و چیزهایی از خدا می‌دانم که [شما] نمی‌دانید.»'),
(1017, 7, 63, 'آیا تعجب کردید که بر مردی از خودتان، پندی از جانب پروردگارتان برای شما آمده تا شما را بیم دهد و تا شما پرهیزگاری کنید و باشد که مورد رحمت قرار گیرید؟'),
(1018, 7, 64, 'پس او را تکذیب کردند، و ما او و کسانی را که با وی در کشتی بودند نجات دادیم؛ و کسانی را که آیات ما را دروغ پنداشتند غرق کردیم، زیرا آنان گروهی کور[دل‌] بودند.'),
(1019, 7, 65, 'و به سوی عاد، برادرشان هود را [فرستادیم‌]؛ گفت: «ای قوم من، خدا را بپرستید که برای شما معبودی جز او نیست، پس آیا پرهیزگاری نمی‌کنید؟»'),
(1020, 7, 66, 'سران قومش که کافر بودند گفتند: «در حقیقت، ما تو را در [نوعی‌] سفاهت می‌بینیم و جداً تو را از دروغگویان می‌پنداریم.»'),
(1021, 7, 67, 'گفت: «ای قوم من، در من سفاهتی نیست، ولی من فرستاده‌ای از جانب پروردگار جهانیانم.'),
(1022, 7, 68, 'پیامهای پروردگارم را به شما می‌رسانم و برای شما خیر خواهی امینم.»'),
(1023, 7, 69, 'آیا تعجب کردید که بر مردی از خودتان، پندی از جانب پروردگارتان برای شما آمده تا شما را هشدار دهد؟ و به خاطر آورید زمانی را که [خداوند] شما را پس از قوم نوح، جانشینان [آنان‌] قرار داد، و در خلقت، بر قوّت شما افزود. پس نعمتهای خدا را به یاد آورید، باشد که رستگار شوید.'),
(1024, 7, 70, 'گفتند: «آیا به سوی ما آمده‌ای که تنها خدا را بپرستیم و آنچه را که پدرانمان می پرستیدند رها کنیم؟ اگر راست می‌گویی، آنچه را به ما وعده می‌دهی برای ما بیاور.»'),
(1025, 7, 71, 'گفت: «راستی که عذاب و خشمی [سخت‌] از پروردگارتان بر شما مقرر گردیده است. آیا در باره نامهایی که خود و پدرانتان [برای بتها] نامگذاری کرده‌اید، و خدا بر [حقانیت‌] آنها برهانی فرو نفرستاده با من مجادله می‌کنید؟ پس منتظر باشید که من [هم‌] با شما از منتظرانم.»'),
(1026, 7, 72, 'پس او و کسانی را که با او بودند به رحمتی از خود رهانیدیم؛ و کسانی را که آیات ما را دروغ شمردند و مؤمن نبودند ریشه‌کن کردیم.'),
(1027, 7, 73, 'و به سوی [قوم‌] ثمود، صالح، برادرشان را [فرستادیم‌]؛ گفت: «ای قوم من، خدا را بپرستید، برای شما معبودی جز او نیست؛ در حقیقت، برای شما از جانب پروردگارتان دلیلی آشکار آمده است. این، ماده‌شتر خدا برای شماست که پدیده‌ای شگرف است. پس آن را بگذارید تا در زمین خدا بخورد و گزندی به او نرسانید تا [مبادا] شما را عذابی دردناک فرو گیرد.»'),
(1028, 7, 74, 'و به یاد آورید هنگامی را که شما را پس از [قوم‌] عاد جانشینان [آنان‌] گردانید، و در زمین به شما جایِ [مناسب‌] داد. در دشتهای آن [برای خود] کاخهایی اختیار می کردید، و از کوهها خانه‌هایی [زمستانی ]می‌تراشیدید. پس نعمتهای خدا را به یاد آورید و در زمین سر به فساد برمدارید.'),
(1029, 7, 75, 'سران قوم او که استکبار می‌ورزیدند، به مستضعفانی که ایمان آورده بودند، گفتند: «آیا می‌دانید که صالح از طرف پروردگارش فرستاده شده است؟» گفتند: «بی‌تردید، ما به آنچه وی بدان رسالت یافته است مؤمنیم.»'),
(1030, 7, 76, 'کسانی که استکبار می‌ورزیدند، گفتند: «ما به آنچه شما بدان ایمان آورده‌اید کافریم.»'),
(1031, 7, 77, 'پس آن ماده‌شتر را پی کردند و از فرمان پروردگار خود سرپیچیدند و گفتند: «ای صالح، اگر از پیامبرانی، آنچه را به ما وعده می‌دهی برای ما بیاور.»'),
(1032, 7, 78, 'آنگاه زمین‌لرزه آنان را فرو گرفت و در خانه هایشان از پا درآمدند.'),
(1033, 7, 79, 'پس [صالح‌] از ایشان روی برتافت و گفت: «ای قوم من، به راستی، من پیام پروردگارم را به شما رساندم و خیر شما را خواستم ولی شما [خیرخواهان و نصیحتگران‌] را دوست نمی‌دارید.»'),
(1034, 7, 80, 'و لوط را [فرستادیم‌] هنگامی که به قوم خود گفت: «آیا آن کار زشت‌[ی‌] را مرتکب می‌شوید، که هیچ کس از جهانیان در آن بر شما پیشی نگرفته است؟'),
(1035, 7, 81, 'شما از روی شهوت، به جای زنان با مردان درمی‌آمیزید، آری، شما گروهی تجاوزکارید.»'),
(1036, 7, 82, 'ولی پاسخ قومش جز این نبود که گفتند: «آنان را از شهرتان بیرون کنید، زیرا آنان کسانی‌اند که به پاکی تظاهر می‌کنند.»'),
(1037, 7, 83, 'پس او و خانواده‌اش را -غیر از زنش که از زمره باقیماندگانِ [در خاکستر مواد گوگردی‌] بود- نجات دادیم.'),
(1038, 7, 84, 'و بر سر آنان بارشی [از مواد گوگردی‌] بارانیدیم. پس ببین فرجام گنهکاران چسان بود.'),
(1039, 7, 85, 'و به سوی [مردم‌] مدین، برادرشان شعیب را [فرستادیم‌]؛ گفت: «ای قوم من، خدا را بپرستید که برای شما هیچ معبودی جز او نیست. در حقیقت، شما را از جانب پروردگارتان برهانی روشن آمده است. پس پیمانه و ترازو را تمام نهید، و اموال مردم را کم مدهید، و در زمین، پس از اصلاح آن فساد مکنید. این [رهنمودها] اگر مؤمنید برای شما بهتر است.»'),
(1040, 7, 86, 'و بر سر هر راهی منشینید که [مردم را] بترسانید و کسی را که ایمان به خدا آورده از راه خدا باز دارید و راه او را کج بخواهید؛ و به یاد آورید هنگامی را که اندک بودید، پس شما را بسیار گردانید، و بنگرید که فرجام فسادکاران چگونه بوده است.»'),
(1041, 7, 87, 'و اگر گروهی از شما به آنچه من بدان فرستاده شده‌ام ایمان آورده و گروه دیگر ایمان نیاورده‌اند، صبر کنید تا خدا میان ما داوری کند [که‌] او بهترین داوران است.»'),
(1042, 7, 88, 'سران قومش که تکبر می‌ورزیدند، گفتند: «ای شعیب، یا تو و کسانی را که با تو ایمان آورده‌اند، از شهر خودمان بیرون خواهیم کرد؛ یا به کیش ما برگردید.» گفت: «آیا هر چند کراهت داشته باشیم؟»'),
(1043, 7, 89, 'اگر بعد از آنکه خدا ما را از آن نجات بخشیده [باز] به کیش شما برگردیم، در حقیقت به خدا دروغ بسته‌ایم؛ و ما را سزاوار نیست که به آن بازگردیم، مگر آنکه خدا، پروردگار ما بخواهد. [که‌] پروردگار ما از نظر دانش بر هر چیزی احاطه دارد. بر خدا توکل کرده‌ایم. بار پروردگارا، میان ما و قوم ما به حق داوری کن که تو بهترین داورانی.»'),
(1044, 7, 90, 'و سران قومش که کافر بودند گفتند: «اگر از شعیب پیروی کنید، در این صورت قطعاً زیانکارید.»'),
(1045, 7, 91, 'پس زمین‌لرزه آنان را فرو گرفت، و در خانه‌هایشان از پا درآمدند.'),
(1046, 7, 92, 'کسانی که شعیب را تکذیب کرده بودند، گویی خود در آن [دیار] سکونت نداشتند. کسانی که شعیب را تکذیب کرده بودند، خود، همان زیانکاران بودند.'),
(1047, 7, 93, 'پس [شعیب‌] از ایشان روی برتافت و گفت: «ای قوم من، به راستی که پیامهای پروردگارم را به شما رسانیدم و پندتان دادم؛ دیگر چگونه بر گروهی که کافرند دریغ بخورم؟»'),
(1048, 7, 94, 'و در هیچ شهری، پیامبری نفرستادیم مگر آنکه مردمش را به سختی و رنج دچار کردیم تا مگر به زاری درآیند.'),
(1049, 7, 95, 'آنگاه به جای بدی [=بلا]، نیکی [=نعمت‌] قرار دادیم تا انبوه شدند و گفتند: «پدران ما را [هم مسلماً به حکم طبیعت‌] رنج و راحت می‌رسیده است.» پس در حالی که بی خبر بودند بناگاه [گریبان‌] آنان را گرفتیم.'),
(1050, 7, 96, 'و اگر مردم شهرها ایمان آورده و به تقوا گراییده بودند، قطعاً برکاتی از آسمان و زمین برایشان می‌گشودیم، ولی تکذیب کردند؛ پس به [کیفر] دستاوردشان [گریبان‌] آنان را گرفتیم.'),
(1051, 7, 97, 'آیا ساکنان شهرها ایمن شده‌اند از اینکه عذاب ما شامگاهان -در حالی که به خواب فرو رفته‌اند- به آنان برسد؟'),
(1052, 7, 98, 'و آیا ساکنان شهرها ایمن شده‌اند از اینکه عذاب ما نیمروز -در حالی که به بازی سرگرمند- به ایشان دررسد؟'),
(1053, 7, 99, 'آیا از مکر خدا خود را ایمن دانستند؟ [با آنکه‌] جز مردم زیانکار [کسی‌] خود را از مکر خدا ایمن نمی‌داند.'),
(1054, 7, 100, 'مگر برای کسانی که زمین را پس از ساکنان [پیشین‌] آن به ارث می‌برند، باز ننموده است که اگر می‌خواستیم آنان را به [کیفر] گناهانشان می‌رساندیم و بر دلهایشان مُهر می‌نهادیم تا دیگر نشنوند.'),
(1055, 7, 101, 'این شهرهاست که برخی از خبرهای آن را بر تو حکایت می‌کنیم. در حقیقت، پیامبرانشان دلایل روشن برایشان آوردند. اما آنان به آنچه قبلاً تکذیب کرده بودند [باز] ایمان نمی‌آوردند. این گونه خدا بر دلهای کافران مُهر می‌نهد.'),
(1056, 7, 102, 'و در بیشتر آنان عهدی [استوار] نیافتیم و بیشترشان را جداً نافرمان یافتیم.'),
(1057, 7, 103, 'آنگاه بعد از آنان، موسی را با آیات خود به سوی فرعون و سران قومش فرستادیم. ولی آنها به آن [آیات‌] کفر ورزیدند. پس ببین فرجام مفسدان چگونه بود.'),
(1058, 7, 104, 'و موسی گفت: «ای فرعون، بی‌تردید، من پیامبری از سوی پروردگار جهانیانم.»'),
(1059, 7, 105, 'شایسته است که بر خدا جز [سخن‌] حق نگویم. من در حقیقت دلیلی روشن از سوی پروردگارتان برای شما آورده‌ام، پس فرزندان اسرائیل را همراه من بفرست.'),
(1060, 7, 106, '[فرعون‌] گفت: «اگر معجزه‌ای آورده‌ای، پس اگر راست می‌گویی آن را ارائه بده.»'),
(1061, 7, 107, 'پس [موسی‌] عصایش را افکند و بناگاه اژدهایی آشکار شد.'),
(1062, 7, 108, 'و دست خود را [از گریبان‌] بیرون کشید و ناگهان برای تماشاگران سپید [و درخشنده‌] بود.'),
(1063, 7, 109, 'سران قوم فرعون گفتند: «بی‌شک، این [مرد] ساحری داناست.»'),
(1064, 7, 110, 'می‌خواهد شما را از سرزمینتان بیرون کند. پس چه دستور می‌دهید؟'),
(1065, 7, 111, 'گفتند: «او و برادرش را بازداشت کن، و گردآورندگانی را به شهرها بفرست.'),
(1066, 7, 112, 'تا هر ساحر دانایی را نزد تو آرند.'),
(1067, 7, 113, 'و ساحران نزد فرعون آمدند [و] گفتند: «[آیا] اگر ما پیروز شویم برای ما پاداشی خواهد بود؟»'),
(1068, 7, 114, 'گفت: «آری، و مسلماً شما از مقربان [دربار من‌] خواهید بود.»'),
(1069, 7, 115, 'گفتند: «ای موسی، آیا تو می‌افکنی و یا اینکه ما می‌افکنیم؟»'),
(1070, 7, 116, 'گفت: «شما بیفکنید.» و چون افکندند، دیدگان مردم را افسون کردند و آنان را به ترس انداختند و سحری بزرگ در میان آوردند.'),
(1071, 7, 117, 'و به موسی وحی کردیم که: «عصایت را بینداز»؛ پس [انداخت و اژدها شد] و ناگهان آنچه را به دروغ ساخته بودند فرو بلعید.'),
(1072, 7, 118, 'پس حقیقت آشکار گردید و کارهایی که می‌کردند باطل شد.'),
(1073, 7, 119, 'و در آنجا مغلوب و خوار گردیدند.'),
(1074, 7, 120, 'و ساحران به سجده درافتادند.'),
(1075, 7, 121, '[و] گفتند: «به پروردگار جهانیان ایمان آوردیم،'),
(1076, 7, 122, 'پروردگار موسی و هارون.'),
(1077, 7, 123, 'فرعون گفت: «آیا پیش از آنکه به شما رخصت دهم، به او ایمان آوردید؟ قطعاً این نیرنگی است که در شهر به راه انداخته‌اید تا مردمش را از آن بیرون کنید. پس به زودی خواهید دانست.»'),
(1078, 7, 124, 'دستها و پاهایتان را یکی از چپ و یکی از راست خواهم برید سپس همه شما را به دار خواهم آویخت.'),
(1079, 7, 125, 'گفتند: «ما به سوی پروردگارمان بازخواهیم گشت.'),
(1080, 7, 126, 'و تو جز برای این ما را به کیفر نمی‌رسانی که ما به معجزات پروردگارمان -وقتی برای ما آمد- ایمان آوردیم. پروردگارا، بر ما شکیبایی فرو ریز و ما را مسلمان بمیران.»'),
(1081, 7, 127, 'و سران قوم فرعون گفتند: «آیا موسی و قومش را رها می‌کنی تا در این سرزمین فساد کنند و [موسی‌] تو و خدایانت را رها کند؟» [فرعون‌] گفت: «بزودی پسرانشان را می‌کُشیم و زنانشان را زنده نگاه می‌داریم، و ما بر آنان مسلطیم.»'),
(1082, 7, 128, 'موسی به قوم خود گفت: «از خدا یاری جویید و پایداری ورزید، که زمین از آنِ خداست؛ آن را به هر کس از بندگانش که بخواهد می‌دهد؛ و فرجام [نیک‌] برای پرهیزگاران است.»'),
(1083, 7, 129, '[قوم موسی‌] گفتند: «پیش از آنکه تو نزد ما بیایی و [حتی‌] بعد از آنکه به سوی ما آمدی مورد آزار قرار گرفتیم.» گفت: «امید است که پروردگارتان دشمن شما را هلاک کند و شما را روی زمین جانشین [آنان ]سازد؛ آنگاه بنگرد تا چگونه عمل می‌کنید.»'),
(1084, 7, 130, 'و در حقیقت، ما فرعونیان را به خشکسالی و کمبود محصولات دچار کردیم باشد که عبرت گیرند.'),
(1085, 7, 131, 'پس هنگامی که نیکی [و نعمت‌] به آنان روی می‌آورد می‌گفتند: «این برای [شایستگی‌] خود ماست» و چون گزندی به آنان می‌رسید، به موسی و همراهانش شگون بد می‌زدند. آگاه باشید که [سرچشمه ]بدشگونی آنان تنها نزد خداست [که آنان را به بدی اعمالشان کیفر می‌دهد]، لیکن بیشترشان نمی‌دانستند.'),
(1086, 7, 132, 'و گفتند: «هر گونه پدیده شگرفی که به وسیله آن ما را افسون کنی برای ما بیاوری، ما به تو ایمان آورنده نیستیم.»'),
(1087, 7, 133, 'پس بر آنان طوفان و ملخ و کنه ریز و غوکها و خون را به صورت نشانه‌هایی آشکار فرستادیم و باز سرکشی کردند و گروهی بدکار بودند.'),
(1088, 7, 134, 'و هنگامی که عذاب بر آنان فرود آمد، گفتند: «ای موسی، پروردگارت را به عهدی که نزد تو دارد برای ما بخوان، اگر این عذاب را از ما برطرف کنی حتماً به تو ایمان خواهیم آورد و بنی‌اسرائیل را قطعاً با تو روانه خواهیم ساخت.»'),
(1089, 7, 135, 'و چون عذاب را -تا سررسیدی که آنان بدان رسیدند- از آنها برداشتیم باز هم پیمان‌شکنی کردند.'),
(1090, 7, 136, 'سرانجام از آنان انتقام گرفتیم و در دریا غرقشان ساختیم، چرا که آیات ما را تکذیب کردند و از آنها غافل بودند.'),
(1091, 7, 137, 'و به آن گروهی که پیوسته تضعیف می‌شدند، [بخشهای‌] باختر و خاوری سرزمین [فلسطین‌] را -که در آن برکت قرار داده بودیم- به میراث عطا کردیم. و به پاس آنکه صبر کردند، وعده نیکوی پروردگارت به فرزندان اسرائیل تحقق یافت، و آنچه را که فرعون و قومش ساخته و افراشته بودند ویران کردیم.'),
(1092, 7, 138, 'و فرزندان اسرائیل را از دریا گذراندیم. تا به قومی رسیدند که بر [پرستش‌] بتهای خویش همت می‌گماشتند. گفتند: «ای موسی، همان گونه که برای آنان خدایانی است، برای ما [نیز] خدایی قرار ده.» گفت: «راستی شما نادانی می‌کنید.»'),
(1093, 7, 139, 'در حقیقت، آنچه ایشان در آنند نابود [و زایل‌] و آنچه انجام می‌دادند باطل است.'),
(1094, 7, 140, 'گفت: «آیا غیر از خدا معبودی برای شما بجویم، با اینکه او شما را بر جهانیان برتری داده است؟»'),
(1095, 7, 141, 'و [یاد کن‌] هنگامی را که شما را از فرعونیان نجات دادیم که شما را سخت شکنجه می‌کردند: پسرانتان را می‌کشتند و زنانتان را زنده باقی می‌گذاشتند و در این، برای شما آزمایشِ بزرگی از جانب پروردگارتان بود.'),
(1096, 7, 142, 'و با موسی، سی شب وعده گذاشتیم و آن را با ده شب دیگر تمام کردیم. تا آنکه وقت معین پروردگارش در چهل شب به سر آمد. و موسی [هنگام رفتن به کوه طور] به برادرش هارون گفت: «در میان قوم من جانشینم باش، و [کار آنان را] اصلاح کن، و راه فسادگران را پیروی مکن.»'),
(1097, 7, 143, 'و چون موسی به میعاد ما آمد و پروردگارش با او سخن گفت، عرض کرد: «پروردگارا، خود را به من بنمای تا بر تو بنگرم.» فرمود: «هرگز مرا نخواهی دید، لیکن به کوه بنگر؛ پس اگر بر جای خود قرار گرفت به زودی مرا خواهی دید. « پس چون پروردگارش به کوه جلوه نمود، آن را ریز ریز ساخت، و موسی بیهوش بر زمین افتاد، و چون به خود آمد، گفت: «تو منزهی! به درگاهت توبه کردم و من نخستین مؤمنانم.»'),
(1098, 7, 144, 'فرمود: «ای موسی، تو را با رسالتها و با سخن گفتنم [با تو]، بر مردم [روزگار] برگزیدم؛ پس آنچه را به تو دادم بگیر و از سپاسگزاران باش.»'),
(1099, 7, 145, 'و در الواح [تورات‌] برای او در هر موردی پندی، و برای هر چیزی تفصیلی نگاشتیم، پس [فرمودیم:] «آن را به جدّ و جهد بگیر و قوم خود را وادار کن که بهترین آن را فرا گیرند، به زودی سرای نافرمانان را به شما می‌نمایانم.»'),
(1100, 7, 146, 'به زودی کسانی را که در زمین، بناحق تکبّر می‌ورزند، از آیاتم رویگردان سازم [به طوری که‌] اگر هر نشانه‌ای را [از قدرت من‌] بنگرند، بدان ایمان نیاورند، و اگر راه صواب را ببینند آن را برنگزینند، و اگر راه گمراهی را ببینند آن را راه خود قرار دهند. این بدان سبب است که آنان آیات ما را دروغ انگاشته و غفلت ورزیدند.'),
(1101, 7, 147, 'و کسانی که آیات ما و دیدار آخرت را دروغ پنداشتند، اعمالشان تباه شده است. آیا جز در برابر آنچه می‌کردند کیفر می‌بینند؟'),
(1102, 7, 148, 'و قوم موسی پس از [عزیمت‌] او، از زیورهای خود مجسمه گوساله‌ای برای خود ساختند که صدای گاو داشت. آیا ندیدند که آن [گوساله‌] با ایشان سخن نمی‌گوید و راهی بدانها نمی‌نماید؟ آن را [به پرستش ]گرفتند و ستمکار بودند.'),
(1103, 7, 149, 'و چون انگشت ندامت گِزیدند و دانستند که واقعاً گمراه شده‌اند گفتند: «اگر پروردگار ما به ما رحم نکند و ما را نبخشاید قطعاً از زیانکاران خواهیم بود.»'),
(1104, 7, 150, 'و چون موسی، خشمناک و اندوهگین به سوی قوم خود بازگشت، گفت: «پس از من چه بد جانشینی برای من بودید! آیا بر فرمان پروردگارتان پیشی گرفتید؟ و الواح را افکند و [موی‌] سر برادرش را گرفت و او را به طرف خود کشید. [هارون ] گفت: « ای فرزند مادرم، این قوم، مرا ناتوان یافتند و چیزی نمانده بود که مرا بکشند؛ پس مرا دشمن‌شاد مکن و مرا در شمار گروه ستمکاران قرار مده.'),
(1105, 7, 151, '[موسی‌] گفت: «پروردگارا، من و برادرم را بیامرز و ما را در [پناه‌] رحمت خود درآور، و تو مهربانترین مهربانانی.»'),
(1106, 7, 152, 'آری، کسانی که گوساله را [به پرستش‌] گرفتند، به زودی خشمی از پروردگارشان و ذلّتی در زندگی دنیا به ایشان خواهد رسید؛ و ما این گونه، دروغ‌پردازان را کیفر می‌دهیم.'),
(1107, 7, 153, 'و[لی‌] کسانی که مرتکب گناهان شدند، آنگاه توبه کردند و ایمان آوردند، قطعاً پروردگار تو پس از آن آمرزنده مهربان خواهد بود.'),
(1108, 7, 154, 'و چون خشم موسی فرو نشست، الواح را برگرفت، و در رونویس آن، برای کسانی که از پروردگارشان بیمناک بودند، هدایت و رحمتی بود.'),
(1109, 7, 155, 'و موسی از میان قوم خود هفتاد مرد برای میعاد ما برگزید، و چون زلزله، آنان را فرو گرفت، گفت: «پروردگارا، اگر می‌خواستی، آنان را و مرا پیش از این هلاک می‌ساختی. آیا ما را به [سزای‌] آنچه کم‌خردان ما کرده‌اند هلاک می‌کنی؟ این جز آزمایش تو نیست؛ هر که را بخواهی به وسیله آن گمراه و هر که را بخواهی هدایت می‌کنی؛ تو سرور مایی؛ پس ما را بیامرز و به ما رحم کن، و تو بهترین آمرزندگانی.»'),
(1110, 7, 156, '«و برای ما در این دنیا نیکی مقرّر فرما و در آخرت [نیز]، زیرا که ما به سوی تو بازگشته‌ایم.» فرمود: «عذاب خود را به هر کس بخواهم می‌رسانم، و رحمتم همه چیز را فرا گرفته است؛ و به زودی آن را برای کسانی که پرهیزگاری می‌کنند و زکات می‌دهند و آنان که به آیات ما ایمان می‌آورند، مقرّر می‌دارم.»'),
(1111, 7, 157, 'همانان که از این فرستاده، پیامبر درس نخوانده -که [نام‌] او را نزد خود، در تورات و انجیل نوشته می‌یابند- پیروی می‌کنند؛ [همان پیامبری که‌] آنان را به کار پسندیده فرمان می‌دهد، و از کار ناپسند باز می‌دارد، و برای آنان چیزهای پاکیزه را حلال و چیزهای ناپاک را بر ایشان حرام می‌گرداند، و از [دوش‌] آنان قید و بندهایی را که بر ایشان بوده است برمی‌دارد. پس کسانی که به او ایمان آوردند و بزرگش داشتند و یاریش کردند و نوری را که با او نازل شده است پیروی کردند، آنان همان رستگارانند.'),
(1112, 7, 158, 'بگو: «ای مردم، من پیامبر خدا به سوی همه شما هستم، همان [خدایی‌] که فرمانروایی آسمانها و زمین از آن اوست. هیچ معبودی جز او نیست؛ که زنده می‌کند و می‌میراند. پس به خدا و فرستاده او -که پیامبر درس‌نخوانده‌ای است که به خدا و کلمات او ایمان دارد- بگروید و او را پیروی کنید، امید که هدایت شوید.»'),
(1113, 7, 159, 'و از میان قوم موسی جماعتی هستند که به حق راهنمایی می‌کنند و به حق داوری می‌نمایند.'),
(1114, 7, 160, 'و آنان را به دوازده عشیره که هر یک امتی بودند تقسیم کردیم، و به موسی -وقتی قومش از او آب خواستند- وحی کردیم که با عصایت بر آن تخته سنگ بزن. پس، از آن، دوازده چشمه جوشید. هر گروهی آبشخور خود را بشناخت؛ و ابر را بر فراز آنان سایبان کردیم، و گزانگبین و بلدرچین بر ایشان فرو فرستادیم. از چیزهای پاکیزه‌ای که روزیتان کرده‌ایم بخورید. و بر ما ستم نکردند، لیکن بر خودشان ستم می‌کردند.'),
(1115, 7, 161, 'و [یاد کن‌] هنگامی را که بدیشان گفته شد: «در این شهر سکونت گزینید، و از آن -هر جا که خواستید- بخورید، و بگویید: [خداوندا،] گناهان ما را فرو ریز. و سجده‌کنان از دروازه [شهر] درآیید، تا گناهان شما را بر شما ببخشاییم [و] به زودی بر [اجر] نیکوکاران بیفزاییم.»'),
(1116, 7, 162, 'پس، کسانی از آنان که ستم کردند، سخنی را که به ایشان گفته شده بود به سخن دیگری تبدیل کردند. پس به سزای آنکه ستم می‌ورزیدند، عذابی از آسمان بر آنان فرو فرستادیم.'),
(1117, 7, 163, 'و از اهالی آن شهری که کنار دریا بود، از ایشان جویا شو: آنگاه که به [حکم‌] روز شنبه تجاوز می‌کردند؛ آنگاه که روز شنبه آنان، ماهیهایشان روی آب می‌آمدند، و روزهای غیر شنبه به سوی آنان نمی‌آمدند. این گونه ما آنان را به سبب آنکه نافرمانی می‌کردند، می‌آزمودیم.'),
(1118, 7, 164, 'و آنگاه که گروهی از ایشان گفتند: «برای چه قومی را که خدا هلاک‌کننده ایشان است، یا آنان را به عذابی سخت عذاب خواهد کرد، پند می‌دهید؟» گفتند: «تا معذرتی پیش پروردگارتان باشد، و شاید که آنان پرهیزگاری کنند.»'),
(1119, 7, 165, 'پس هنگامی که آنچه را بدان تذکر داده شده بودند، از یاد بردند، کسانی را که از [کارِ] بد باز می‌داشتند نجات دادیم؛ و کسانی را که ستم کردند، به سزای آنکه نافرمانی می‌کردند، به عذابی شدید گرفتار کردیم.'),
(1120, 7, 166, 'و چون از آنچه از آن نهی شده بودند سرپیچی کردند، به آنان گفتیم: «بوزینگانی رانده‌شده باشید.»'),
(1121, 7, 167, 'و [یاد کن‌] هنگامی را که پروردگارت اعلام داشت که تا روز قیامت بر آنان [=یهودیان‌] کسانی را خواهد گماشت که بدیشان عذاب سخت بچشانند. آری، پروردگار تو زودکیفر است و همو آمرزنده بسیار مهربان است.'),
(1122, 7, 168, 'و آنان را در زمین به صورت گروه‌هایی پراکنده ساختیم: برخی از آنان درستکارند و برخی از آنان جز اینند. و آنها را به خوشیها و ناخوشیها آزمودیم، باشد که ایشان بازگردند.'),
(1123, 7, 169, 'آنگاه بعد از آنان، جانشینانی وارث کتاب [آسمانی‌] شدند که متاع این دنیای پست را می‌گیرند و می‌گویند: «بخشیده خواهیم شد.» و اگر متاعی مانند آن به ایشان برسد [باز] آن را می‌ستانند. آیا از آنان پیمان کتاب [آسمانی‌] گرفته نشده که جز به حق نسبت به خدا سخن نگویند، با اینکه آنچه را که در آن [کتاب‌] است آموخته‌اند؟ و سرای آخرت برای کسانی که پروا پیشه می‌کنند بهتر است. آیا باز تعقّل نمی‌کنید؟'),
(1124, 7, 170, 'و کسانی که به کتاب [آسمانی‌] چنگ درمی‌زنند و نماز برپا داشته‌اند [بدانند که‌] ما اجر درستکاران را تباه نخواهیم کرد.'),
(1125, 7, 171, 'و [یاد کن‌] هنگامی را که کوه [طور] را بر فرازشان سایبان‌آسا، برافراشتیم، و چنان پنداشتند که [کوه‌] بر سرشان فرو خواهد افتاد. [و گفتیم:] آنچه را که به شما داده‌ایم به جدّ و جهد بگیرید، و آنچه را در آن است به یاد داشته باشید. شاید که پرهیزگار شوید.'),
(1126, 7, 172, 'و هنگامی را که پروردگارت از پشت فرزندان آدم، ذریه آنان را برگرفت و ایشان را بر خودشان گواه ساخت که آیا پروردگار شما نیستم؟ گفتند: «چرا، گواهی دادیم» تا مبادا روز قیامت بگویید ما از این [امر] غافل بودیم.'),
(1127, 7, 173, 'یا بگویید پدران ما پیش از این مشرک بوده‌اند و ما فرزندانی پس از ایشان بودیم. آیا ما را به خاطر آنچه باطل‌اندیشان انجام داده‌اند هلاک می‌کنی؟'),
(1128, 7, 174, 'و اینگونه آیات [خود] را به تفصیل بیان می‌کنیم، و باشد که آنان [به سوی حق‌] بازگردند.'),
(1129, 7, 175, 'و خبر آن کس را که آیات خود را به او داده بودیم برای آنان بخوان که از آن عاری گشت؛ آنگاه شیطان، او را دنبال کرد و از گمراهان شد.'),
(1130, 7, 176, 'و اگر می‌خواستیم، قدر او را به وسیله آن [آیات‌] بالا می‌بردیم، اما او به زمین [=دنیا] گرایید و از هوای نَفْس خود پیروی کرد. از این رو داستانش چون داستان سگ است [که‌] اگر بر آن حمله‌ور شوی زبان از کام برآورد، و اگر آن را رها کنی [باز هم‌] زبان از کام برآورد. این، مَثَل آن گروهی است که آیات ما را تکذیب کردند. پس این داستان را [برای آنان‌] حکایت کن، شاید که آنان بیندیشند.'),
(1131, 7, 177, 'چه زشت است داستان گروهی که آیات ما را تکذیب و به خود ستم می‌نمودند.'),
(1132, 7, 178, 'هر که را خدا هدایت کند، او راه‌یافته است؛ و کسانی را که گمراه نماید، آنان خود زیانکارانند.'),
(1133, 7, 179, 'و در حقیقت، بسیاری از جنّیان و آدمیان را برای دوزخ آفریده‌ایم. [چرا که‌] دلهایی دارند که با آن [حقایق را] دریافت نمی‌کنند، و چشمانی دارند که با آنها نمی‌بینند، و گوشهایی دارند که با آنها نمی‌شنوند. آنان همانند چهارپایان بلکه گمراه‌ترند. [آری،] آنها همان غافل‌ماندگانند.'),
(1134, 7, 180, 'و نامهای نیکو به خدا اختصاص دارد، پس او را با آنها بخوانید، و کسانی را که در مورد نامهای او به کژی می‌گرایند رها کنید. زودا که به [سزای‌] آنچه انجام می‌دادند کیفر خواهند یافت.'),
(1135, 7, 181, 'و از میان کسانی که آفریده‌ایم، گروهی هستند که به حق هدایت می‌کنند و به حق داوری می‌نمایند.'),
(1136, 7, 182, 'و کسانی که آیات ما را تکذیب کردند، به تدریج، از جایی که نمی‌دانند گریبانشان را خواهیم گرفت.'),
(1137, 7, 183, 'و به آنان مهلت می‌دهم، که تدبیر من استوار است.'),
(1138, 7, 184, 'آیا نیندیشیده‌اند که همنشین آنان هیچ جنونی ندارد؟ او جز هشداردهنده‌ای آشکار نیست.'),
(1139, 7, 185, 'آیا در ملکوت آسمانها و زمین و هر چیزی که خدا آفریده است ننگریسته‌اند؛ و اینکه شاید هنگام مرگشان نزدیک شده باشد؟ پس به کدام سخن، بعد از قرآن ایمان می‌آورند؟'),
(1140, 7, 186, 'هر که را خداوند گمراه کند، برای او هیچ رهبری نیست، و آنان را در طغیانشان سرگردان وا می‌گذارد.'),
(1141, 7, 187, 'از تو در باره قیامت می‌پرسند [که‌] وقوع آن چه وقت است؟ بگو: «علم آن، تنها نزد پروردگار من است. جز او [هیچ کس‌] آن را به موقع خود آشکار نمی‌گرداند. [این حادثه‌] بر آسمانها و زمین گران است، جز ناگهان به شما نمی‌رسد.» [باز] از تو می‌پرسند گویا تو از [زمان وقوع‌] آن آگاهی. بگو: «علم آن، تنها نزد خداست، ولی بیشتر مردم نمی‌دانند.»'),
(1142, 7, 188, 'بگو: «جز آنچه خدا بخواهد، برای خودم اختیار سود و زیانی ندارم، و اگر غیب می‌دانستم قطعاً خیر بیشتری می‌اندوختم و هرگز به من آسیبی نمی‌رسید. من جز بیم‌دهنده و بشارتگر برای گروهی که ایمان می‌آورند، نیستم.»'),
(1143, 7, 189, 'اوست آن کس که شما را از نَفْس واحدی آفرید، و جفت وی را از آن پدید آورد تا بدان آرام گیرد. پس چون [آدم‌] با او [حوّا] درآمیخت باردار شد، باری سبک. و [چندی‌] با آن [بار سبک‌] گذرانید، و چون سنگین‌بار شد، خدا، پروردگار خود را خواندند که اگر به ما [فرزندی‌] شایسته عطا کنی قطعاً از سپاسگزاران خواهیم بود.'),
(1144, 7, 190, 'و چون به آن دو، [فرزندی‌] شایسته داد، در آنچه [خدا] به ایشان داده بود، برای او شریکانی قرار دادند، و خدا از آنچه [با او] شریک می‌گردانند برتر است.'),
(1145, 7, 191, 'آیا موجوداتی را [با او] شریک می‌گردانند که چیزی را نمی‌آفرینند و خودشان مخلوقند؟'),
(1146, 7, 192, 'و نمی‌توانند آنان را یاری کنند و نه خویشتن را یاری دهند.'),
(1147, 7, 193, 'و اگر آنها را به [راه‌] هدایت فراخوانید، از شما پیروی نمی‌کنند. چه آنها را بخوانید یا خاموش بمانید، برای شما یکسان است.'),
(1148, 7, 194, 'در حقیقت، کسانی را که به جای خدا می‌خوانید، بندگانی امثال شما هستند. پس آنها را [در گرفتاریها] بخوانید، اگر راست می‌گویید باید شما را اجابت کنند.'),
(1149, 7, 195, 'آیا آنها پاهایی دارند که با آن راه بروند، یا دستهایی دارند که با آن کاری انجام دهند، یا چشمهایی دارند که با آن بنگرند، یا گوشهایی دارند که با آن بشنوند؟ بگو: «شریکان خود را بخوانید؛ سپس در باره من حیله به کار برید و مرا مهلت مدهید.»'),
(1150, 7, 196, 'بی‌تردید، سرور من آن خدایی است که قرآن را فرو فرستاده، و همو دوستدار شایستگان است.'),
(1151, 7, 197, 'و کسانی را که به جای او می‌خوانید، نمی‌توانند شما را یاری کنند و نه خویشتن را یاری دهند.'),
(1152, 7, 198, 'و اگر آنها را به [راه‌] هدایت فرا خوانید، نمی‌شنوند، و آنها را می‌بینی که به سوی تو می‌نگرند در حالی که نمی‌بینند.'),
(1153, 7, 199, 'گذشت پیشه کن، و به [کار] پسندیده فرمان ده، و از نادانان رخ برتاب.'),
(1154, 7, 200, 'و اگر از شیطان وسوسه‌ای به تو رسد، به خدا پناه بَر، زیرا که او شنوای داناست.'),
(1155, 7, 201, 'در حقیقت، کسانی که [از خدا] پروا دارند، چون وسوسه‌ای از جانب شیطان بدیشان رسد [خدا را] به یاد آورند و بناگاه بینا شوند.'),
(1156, 7, 202, 'و یارانشان آنان را به گمراهی می‌کشانند و کوتاهی نمی‌کنند.'),
(1157, 7, 203, 'و هر گاه برای آنان آیاتی نیاوری، می‌گویند: «چرا آن را خود برنگزیدی؟ « بگو: «من فقط آنچه را که از پروردگارم به من وحی می‌شود پیروی می‌کنم. این [قرآن‌] رهنمودی است از جانب پروردگار شما و برای گروهی که ایمان می‌آورند هدایت و رحمتی است.'),
(1158, 7, 204, 'و چون قرآن خوانده شود، گوش بدان فرا دارید و خاموش مانید، امید که بر شما رحمت آید.'),
(1159, 7, 205, 'و در دل خویش، پروردگارت را بامدادان و شامگاهان با تضرع و ترس، بی‌صدای بلند، یاد کن و از غافلان مباش.'),
(1160, 7, 206, 'به یقین، کسانی که نزد پروردگار تو هستند، از پرستش او تکبر نمی‌ورزند و او را به پاکی می‌ستایند و برای او سجده می‌کنند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '[ای پیامبر،] از تو در باره غنایم جنگی می‌پرسند. بگو: «غنایم جنگی اختصاص به خدا و فرستاده [او] دارد. پس از خدا پروا دارید و با یکدیگر سازش نمایید، و اگر ایمان دارید از خدا و پیامبرش اطاعت کنید.'),
(1162, 8, 2, 'مؤمنان، همان کسانی‌اند که چون خدا یاد شود دلهایشان بترسد، و چون آیات او بر آنان خوانده شود بر ایمانشان بیفزاید، و بر پروردگار خود توکل می‌کنند.'),
(1163, 8, 3, 'همانان که نماز را به پا می‌دارند و از آنچه به ایشان روزی داده‌ایم انفاق می‌کنند.'),
(1164, 8, 4, 'آنان هستند که حقاً مؤمنند، برای آنان نزد پروردگارشان درجات و آمرزش و روزیِ نیکو خواهد بود.'),
(1165, 8, 5, 'همان گونه که پروردگارت تو را از خانه‌ات به حق بیرون آورد و حال آنکه دسته‌ای از مؤمنان سخت کراهت داشتند.'),
(1166, 8, 6, 'با تو در باره حق -بعد از آنکه روشن گردید- مجادله می‌کنند. گویی که آنان را به سوی مرگ می‌رانند و ایشان [بدان‌] می‌نگرند.'),
(1167, 8, 7, 'و [به یاد آورید] هنگامی را که خدا یکی از دو دسته [کاروان تجارتی قریش یا سپاه ابوسفیان‌] را به شما وعده داد که از آنِ شما باشد، و شما دوست داشتید که دسته بی‌سلاح برای شما باشد، و[لی‌] خدا می‌خواست حق [=اسلام‌] را با کلمات خود ثابت، و کافران را ریشه‌کن کند.'),
(1168, 8, 8, 'تا حق را ثابت و باطل را نابود گرداند، هر چند بزهکاران خوش نداشته باشند.'),
(1169, 8, 9, '[به یاد آورید] زمانی را که پروردگار خود را به فریاد می‌طلبیدید، پس دعای شما را اجابت کرد که: «من شما را با هزار فرشته پیاپی، یاری خواهم کرد.»'),
(1170, 8, 10, 'و این [وعده‌] را خداوند جز نویدی [برای شما] قرار نداد، و تا آنکه دلهای شما بدان اطمینان یابد؛ و پیروزی جز از نزد خدا نیست، که خدا شکست ناپذیر [و] حکیم است.'),
(1171, 8, 11, '[به یاد آورید] هنگامی را که [خدا] خواب سبک آرامش‌بخشی که از جانب او بود بر شما مسلط ساخت، و از آسمان بارانی بر شما فرو ریزانید تا شما را با آن پاک گرداند، و وسوسه شیطان را از شما بزداید و دلهایتان را محکم سازد و گامهایتان را بدان استوار دارد.'),
(1172, 8, 12, 'هنگامی که پروردگارت به فرشتگان وحی می‌کرد که من با شما هستم، پس کسانی را که ایمان آورده‌اند ثابت‌قدم بدارید. به زودی در دل کافران وحشت خواهم افکند. پس، فراز گردنها را بزنید، و همه سرانگشتانشان را قلم کنید.'),
(1173, 8, 13, 'این [کیفر] بدان سبب است که آنان با خدا و پیامبر او به مخالفت برخاستند، و هر کس با خدا و پیامبر او به مخالفت برخیزد قطعاً خدا سخت‌کیفر است.'),
(1174, 8, 14, 'این [عذاب دنیا] را بچشید، و [بدانید که‌] برای کافران عذاب آتش خواهد بود.'),
(1175, 8, 15, 'ای کسانی که ایمان آورده‌اید، هر گاه [در میدان نبرد] به کافران برخورد کردید که [به سوی شما] روی می‌آورند، به آنان پشت مکنید.'),
(1176, 8, 16, 'و هر که در آن هنگام به آنان پشت کند -مگر آنکه [هدفش‌] کناره‌گیری برای نبردی [مجدد] یا پیوستن به جمعی [دیگر از همرزمانش‌] باشد- قطعاً به خشم خدا گرفتار خواهد شد، و جایگاهش دوزخ است، و چه بد سرانجامی است.'),
(1177, 8, 17, 'و شما آنان را نکشتید، بلکه خدا آنان را کُشت. و چون [ریگ به سوی آنان‌] افکندی، تو نیفکندی، بلکه خدا افکند. [آری، خدا چنین کرد تا کافران را مغلوب کند] و بدین وسیله مؤمنان را به آزمایشی نیکو، بیازماید. قطعاً خدا شنوای داناست.'),
(1178, 8, 18, '[ماجرا] این بود، و [بدانید که‌] خدا نیرنگ کافران را سست می‌گرداند.'),
(1179, 8, 19, '[ای مشرکان‌] اگر شما پیروزی [حق‌] را می‌طلبید، اینک پیروزی به سراغ شما آمد [و اسلام پیروز شد]؛ و اگر [از دشمنی‌] بازایستید، آن برای شما بهتر است؛ و اگر [به جنگ‌] برگردید ما هم بر می‌گردیم، و [بدانید] که گروه شما هر چند زیاد باشد، هرگز از شما چیزی را دفع نتوانند کرد و خداست که با مؤمنان است.'),
(1180, 8, 20, 'ای کسانی که ایمان آورده‌اید، خدا و فرستاده او را فرمان برید و از او روی برنتابید در حالی که [سخنان او را] می‌شنوید.'),
(1181, 8, 21, 'و مانند کسانی مباشید که گفتند: «شنیدیم» در حالی که نمی‌شنیدند.'),
(1182, 8, 22, 'قطعاً بدترین جنبندگان نزد خدا کران و لالانی‌اند که نمی‌اندیشند.'),
(1183, 8, 23, 'و اگر خدا در آنان خیری می‌یافت قطعاً شنوایشان می‌ساخت، و اگر آنان را شنوا می‌کرد، حتماً باز به حال اعراض، روی برمی‌تافتند.'),
(1184, 8, 24, 'ای کسانی که ایمان آورده‌اید، چون خدا و پیامبر، شما را به چیزی فرا خواندند که به شما حیات می‌بخشد، آنان را اجابت کنید، و بدانید که خدا میان آدمی و دلش حایل می‌گردد، و هم در نزد او محشور خواهید شد.'),
(1185, 8, 25, 'و از فتنه‌ای که تنها به ستمکاران شما نمی‌رسد بترسید و بدانید که خدا سخت‌کیفر است.'),
(1186, 8, 26, 'و به یاد آورید هنگامی را که شما در زمین، گروهی اندک و مستضعف بودید. می ترسیدید مردم شما را بربایند، پس [خدا] به شما پناه داد و شما را به یاری خود نیرومند گردانید و از چیزهای پاک به شما روزی داد، باشد که سپاسگزاری کنید.'),
(1187, 8, 27, 'ای کسانی که ایمان آورده‌اید، به خدا و پیامبر او خیانت مکنید و [نیز] در امانتهای خود خیانت نورزید و خود می‌دانید [که نباید خیانت کرد].'),
(1188, 8, 28, 'و بدانید که اموال و فرزندان شما [وسیله‌] آزمایش [شما] هستند، و خداست که نزد او پاداشی بزرگ است.'),
(1189, 8, 29, 'ای کسانی که ایمان آورده‌اید، اگر از خدا پروا دارید، برای شما [نیروی‌] تشخیص [حق از باطل‌] قرار می‌دهد؛ و گناهانتان را از شما می‌زداید؛ و شما را می‌آمرزد؛ و خدا دارای بخشش بزرگ است.'),
(1190, 8, 30, 'و [یاد کن‌] هنگامی را که کافران در باره تو نیرنگ می‌کردند تا تو را به بند کشند یا بکشند یا [از مکه‌] اخراج کنند، و نیرنگ می‌زدند، و خدا تدبیر می‌کرد، و خدا بهترین تدبیرکنندگان است.'),
(1191, 8, 31, 'و چون آیات ما بر آنان خوانده شود، می‌گویند: «به خوبی شنیدیم، اگر می‌خواستیم، قطعاً ما نیز همانند این را می‌گفتیم، این جز افسانه‌های پیشینیان نیست.»'),
(1192, 8, 32, 'و [یاد کن‌] هنگامی را که گفتند: «خدایا، اگر این [کتاب‌] همان حق از جانب توست، پس بر ما از آسمان سنگهایی بباران یا عذابی دردناک بر سر ما بیاور.»'),
(1193, 8, 33, 'و[لی‌] تا تو در میان آنان هستی، خدا بر آن نیست که ایشان را عذاب کند، و تا آنان طلب آمرزش می‌کنند، خدا عذاب‌کننده ایشان نخواهد بود.'),
(1194, 8, 34, 'چرا خدا [در آخرت‌] عذابشان نکند، با اینکه آنان [مردم را] از [زیارت‌] مسجدالحرام باز می‌دارند در حالی که ایشان سرپرست آن نباشند. چرا که سرپرست آن جز پرهیزگاران نیستند، ولی بیشترشان نمی‌دانند.'),
(1195, 8, 35, 'و نمازشان در خانه [خدا] جز سوت کشیدن و کف زدن نبود. پس به سزای آنکه کفر می‌ورزیدید، این عذاب را بچشید!'),
(1196, 8, 36, 'بی‌گمان، کسانی که کفر ورزیدند، اموال خود را خرج می‌کنند تا [مردم را] از راه خدا بازدارند. پس به زودی [همه‌] آن را خرج می‌کنند، و آنگاه حسرتی بر آنان خواهد گشت؛ سپس مغلوب می‌شوند. و کسانی که کفر ورزیدند، به سوی دوزخ گردآورده خواهند شد.'),
(1197, 8, 37, 'تا خدا، ناپاک را از پاک جدا کند، و ناپاکها را روی یکدیگر نهد و همه را متراکم کند؛ آنگاه در جهنم قرار دهد. اینان همان زیانکارانند.'),
(1198, 8, 38, 'به کسانی که کفر ورزیده‌اند، بگو: «اگر بازایستند، آنچه گذشته است برایشان آمرزیده می‌شود؛ و اگر بازگردند، به یقین، سنت [خدا در مورد] پیشینیان گذشت.»'),
(1199, 8, 39, 'و با آنان بجنگید تا فتنه‌ای بر جای نماند و دین یکسره از آنِ خدا گردد. پس اگر [از کفر] بازایستند قطعاً خدا به آنچه انجام می‌دهند بیناست.'),
(1200, 8, 40, 'و اگر روی برتافتند، پس بدانید که خدا سرور شماست. چه نیکو سرور و چه نیکو یاوری است.'),
(1201, 8, 41, 'و بدانید که هر چیزی را به غنیمت گرفتید، یک پنجم آن برای خدا و پیامبر و برای خویشاوندان [او] و یتیمان و بینوایان و در راه‌ماندگان است، اگر به خدا و آنچه بر بنده خود در روز جدایی [حق از باطل ]-روزی که آن دو گروه با هم روبرو شدند- نازل کردیم، ایمان آورده‌اید. و خدا بر هر چیزی تواناست.'),
(1202, 8, 42, 'آنگاه که شما بر دامنه نزدیکتر [کوه‌] بودید و آنان در دامنه دورتر [کوه ]، و سواران [دشمن‌] پایین‌تر از شما [موضع گرفته‌] بودند، و اگر با یکدیگر وعده گذارده بودید، قطعاً در وعده‌گاه خود اختلاف می‌کردید، ولی [چنین شد] تا خداوند کاری را که انجام‌شدنی بود به انجام رساند [و] تا کسی که [باید] هلاک شود، با دلیلی روشن هلاک گردد، و کسی که [باید] زنده شود، با دلیلی واضح زنده بماند، و خداست که در حقیقت شنوای داناست.'),
(1203, 8, 43, '[ای پیامبر، یاد کن‌] آنگاه را که خداوند آنان [=سپاه دشمن‌] را در خوابت به تو اندک نشان داد؛ و اگر ایشان را به تو بسیار نشان می‌داد قطعاً سست می‌شدید و حتماً در کار [جهاد] منازعه می‌کردید، ولی خدا شما را به سلامت داشت، چرا که او به راز دلها داناست.'),
(1204, 8, 44, 'و آنگاه که چون با هم برخورد کردید، آنان را در دیدگان شما اندک جلوه داد و شما را [نیز] در دیدگان آنان کم نمودار ساخت تا خداوند کاری را که انجام‌شدنی بود تحقق بخشد، و کارها به سوی خدا بازگردانده می‌شود.'),
(1205, 8, 45, 'ای کسانی که ایمان آورده‌اید، چون با گروهی برخورد می‌کنید پایداری ورزید و خدا را بسیار یاد کنید، باشد که رستگار شوید.'),
(1206, 8, 46, 'و از خدا و پیامبرش اطاعت کنید و با هم نزاع مکنید که سست شوید و مهابت شما از بین برود، و صبر کنید که خدا با شکیبایان است.'),
(1207, 8, 47, 'و مانند کسانی مباشید که از خانه‌هایشان با حالت سرمستی و به صرف نمایش به مردم خارج شدند و [مردم را] از راه خدا باز می‌داشتند، و خدا به آنچه می‌کنند احاطه دارد.'),
(1208, 8, 48, 'و [یاد کن‌] هنگامی را که شیطان اعمال آنان را برایشان بیاراست و گفت: «امروز هیچ کس از مردم بر شما پیروز نخواهد شد، و من پناه شما هستم.» پس هنگامی که دو گروه، یکدیگر را دیدند [شیطان‌] به عقب برگشت و گفت: «من از شما بیزارم، من چیزی را می‌بینم که شما نمی‌بینید، من از خدا بیمناکم.» و خدا سخت‌کیفر است.'),
(1209, 8, 49, 'آنگاه که منافقان و کسانی که در دلهایشان بیماری بود می‌گفتند: «اینان [=مؤمنان‌] را دینشان فریفته است.» و هر کس بر خدا توکل کند [بداند که‌] در حقیقت خدا شکست‌ناپذیر حکیم است.'),
(1210, 8, 50, 'و اگر ببینی آنگاه که فرشتگان جان کافران را می‌ستانند، بر چهره و پشت آنان می‌زنند و [گویند:] عذاب سوزان را بچشید.'),
(1211, 8, 51, 'این [کیفر] دستاوردهای پیشین شماست، و [گر نه‌] خدا بر بندگان [خود] ستمکار نیست.'),
(1212, 8, 52, '[رفتارشان‌] مانند رفتار خاندان فرعون و کسانی است که پیش از آنان بودند: به آیات خدا کفر ورزیدند؛ پس خدا به [سزای‌] گناهانشان گرفتارشان کرد. آری، خدا نیرومند سخت‌کیفر است.'),
(1213, 8, 53, 'این [کیفر] بدان سبب است که خداوند نعمتی را که بر قومی ارزانی داشته تغییر نمی‌دهد، مگر آنکه آنان آنچه را در دل دارند تغییر دهند، و خدا شنوای داناست.'),
(1214, 8, 54, '[رفتاری‌] چون رفتار فرعونیان و کسانی که پیش از آنان بودند، که آیات پروردگارشان را تکذیب کردند؛ پس ما آنان را به [سزای‌] گناهانشان هلاک، و فرعونیان را غرق کردیم و همه آنان ستمکار بودند.'),
(1215, 8, 55, 'بی‌تردید، بدترین جنبندگان پیش خدا کسانی‌اند که کفر ورزیدند و ایمان نمی‌آورند.'),
(1216, 8, 56, 'همانان که از ایشان پیمان گرفتی ولی هر بار پیمان خود را می‌شکنند و [از خدا] پروا نمی‌دارند.'),
(1217, 8, 57, 'پس اگر در جنگ بر آنان دست یافتی با [عقوبت‌] آنان، کسانی را که در پی ایشانند تارومار کن، باشد که عبرت گیرند.'),
(1218, 8, 58, 'و اگر از گروهی بیم خیانت داری [پیمانشان را] به سویشان بینداز [تا طرفین‌] به طور یکسان [بدانند که پیمان گسسته است‌]، زیرا خدا خائنان را دوست نمی‌دارد.'),
(1219, 8, 59, 'و زنهار کسانی که کافر شده‌اند گمان نکنند که پیشی جسته‌اند، زیرا آنان نمی‌توانند [ما را] درمانده کنند.'),
(1220, 8, 60, 'و هر چه در توان دارید از نیرو و اسبهای آماده بسیج کنید، تا با این [تدارکات‌]، دشمن خدا و دشمن خودتان و [دشمنان‌] دیگری را جز ایشان -که شما نمی‌شناسیدشان و خدا آنان را می‌شناسد- بترسانید. و هر چیزی در راه خدا خرج کنید پاداشش به خود شما بازگردانیده می‌شود و بر شما ستم نخواهد رفت.'),
(1221, 8, 61, 'و اگر به صلح گراییدند، تو [نیز] بدان گرای و بر خدا توکل نما که او شنوای داناست.'),
(1222, 8, 62, 'و اگر بخواهند تو را بفریبند، [یاری‌] خدا برای تو بس است. همو بود که تو را با یاری خود و مؤمنان نیرومند گردانید.'),
(1223, 8, 63, 'و میان دلهایشان اُلفت انداخت، که اگر آنچه در روی زمین است همه را خرج می‌کردی نمی‌توانستی میان دلهایشان اُلفت برقرار کنی، ولی خدا بود که میان آنان اُلفت انداخت، چرا که او توانای حکیم است.'),
(1224, 8, 64, 'ای پیامبر، خدا و کسانی از مؤمنان که پیرو تواند تو را بس است.'),
(1225, 8, 65, 'ای پیامبر، مؤمنان را به جهاد برانگیز. اگر از [میان‌] شما بیست تن، شکیبا باشند بر دویست تن چیره می‌شوند، و اگر از شما یکصد تن باشند بر هزار تن از کافران پیروز می‌گردند، چرا که آنان قومی‌اند که نمی‌فهمند.'),
(1226, 8, 66, 'اکنون خدا بر شما تخفیف داده و معلوم داشت که در شما ضعفی هست. پس اگر از [میان‌] شما یکصد تن شکیبا باشند بر دویست تن پیروز گردند، و اگر از شما هزار تن باشند، به توفیق الهی بر دو هزار تن غلبه کنند، و خدا با شکیبایان است.'),
(1227, 8, 67, 'هیچ پیامبری را سزاوار نیست که [برای اخذ سربها از دشمنان‌] اسیرانی بگیرد، تا در زمین به طور کامل از آنان کشتار کند. شما متاع دنیا را می‌خواهید و خدا آخرت را می‌خواهد، و خدا شکست‌ناپذیر حکیم است.'),
(1228, 8, 68, 'اگر در آنچه گرفته‌اید، از جانب خدا نوشته‌ای نبود، قطعاً به شما عذابی بزرگ می‌رسید.'),
(1229, 8, 69, 'پس، از آنچه به غنیمت برده‌اید، حلال و پاکیزه بخورید و از خدا پروا دارید که خدا آمرزنده مهربان است.'),
(1230, 8, 70, 'ای پیامبر، به کسانی که در دست شما اسیرند بگو: «اگر خدا در دلهای شما خیری سراغ داشته باشد، بهتر از آنچه از شما گرفته شده به شما عطا می‌کند و بر شما می‌بخشاید و خدا آمرزنده مهربان است.»'),
(1231, 8, 71, 'و اگر بخواهند به تو خیانت کنند، پیش از این [نیز] به خدا خیانت کردند؛ [و خدا تو را] بر آنان مسلّط ساخت، و خدا دانای حکیم است.'),
(1232, 8, 72, 'کسانی که ایمان آورده و هجرت کرده‌اند و در راه خدا با مال و جان خود جهاد نموده‌اند و کسانی که [مهاجران را] پناه داده‌اند و یاری کرده‌اند، آنان یاران یکدیگرند؛ و کسانی که ایمان آورده‌اند ولی مهاجرت نکرده‌اند هیچ گونه خویشاوندی [دینی‌] با شما ندارند مگر آنکه [در راه خدا] هجرت کنند؛ و اگر در [کار] دین از شما یاری جویند، یاری آنان بر شما [واجب‌] است، مگر بر علیه گروهی باشد که میان شما و میان آنان پیمانی [منعقد شده‌] است، و خدا به آنچه انجام می‌دهید بیناست.'),
(1233, 8, 73, 'و کسانی که کفر ورزیدند یاران یکدیگرند. اگر این [دستور] را به کار نبندید، در زمین فتنه و فسادی بزرگ پدید خواهد آمد.'),
(1234, 8, 74, 'و کسانی که ایمان آورده و هجرت کرده و در راه خدا به جهاد پرداخته، و کسانی که [مهاجران را] پناه داده و یاری کرده‌اند، آنان همان مؤمنان واقعی‌اند، برای آنان بخشایش و روزیِ شایسته‌ای خواهد بود.'),
(1235, 8, 75, 'و کسانی که بعداً ایمان آورده و هجرت نموده و همراه شما جهاد کرده‌اند، اینان از زمره شمایند، و خویشاوندان نسبت به یکدیگر [از دیگران‌] در کتاب خدا سزاوارترند. آری، خدا به هر چیزی داناست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, '[این آیات‌] اعلام بیزاری [و عدم تعهّد] است از طرف خدا و پیامبرش نسبت به آن مشرکانی که با ایشان پیمان بسته‌اید.'),
(1237, 9, 2, 'پس [ای مشرکان،] چهار ماه [دیگر با امنیّت کامل‌] در زمین بگردید و بدانید که شما نمی‌توانید خدا را به ستوه آورید، و این خداست که رسواکننده کافران است.'),
(1238, 9, 3, 'و [این آیات‌] اعلامی است از جانب خدا و پیامبرش به مردم در روز حجّ اکبر که خدا و پیامبرش در برابر مشرکان تعهّدی ندارند [با این حال‌] اگر [از کفر] توبه کنید آن برای شما بهتر است، و اگر روی بگردانید پس بدانید که شما خدا را درمانده نخواهید کرد؛ و کسانی را که کفر ورزیدند از عذابی دردناک خبر ده.'),
(1239, 9, 4, 'مگر آن مشرکانی که با آنان پیمان بسته‌اید، و چیزی از [تعهّدات خود نسبت به‌] شما فروگذار نکرده، و کسی را بر ضدّ شما پشتیبانی ننموده‌اند. پس پیمان اینان را تا [پایان‌] مدّتشان تمام کنید، چرا که خدا پرهیزگاران را دوست دارد.'),
(1240, 9, 5, 'پس چون ماه‌های حرام سپری شد، مشرکان را هر کجا یافتید بکُشید و آنان را دستگیر کنید و به محاصره درآورید و در هر کمینگاهی به کمین آنان بنشینید؛ پس اگر توبه کردند و نماز برپا داشتند و زکات دادند، راه برایشان گشاده گردانید، زیرا خدا آمرزنده مهربان است.'),
(1241, 9, 6, 'و اگر یکی از مشرکان از تو پناه خواست پناهش ده تا کلام خدا را بشنود؛ سپس او را به مکان امنش برسان، چرا که آنان قومی نادانند.'),
(1242, 9, 7, 'چگونه مشرکان را نزد خدا و نزد فرستاده او عهدی تواند بود؟ مگر با کسانی که کنار مسجد الحرام پیمان بسته‌اید. پس تا با شما [بر سر عهد] پایدارند، با آنان پایدار باشید، زیرا خدا پرهیزگاران را دوست می‌دارد.'),
(1243, 9, 8, 'چگونه [برای آنان عهدی است‌] با اینکه اگر بر شما دست یابند، در باره شما نه خویشاوندی را مراعات می‌کنند و نه تعهّدی را، شما را با زبانشان راضی می‌کنند و حال آنکه دلهایشان امتناع می‌ورزد و بیشترشان منحرفند.'),
(1244, 9, 9, 'آیات خدا را به بهای ناچیزی فروختند و [مردم را] از راه او باز داشتند، به راستی آنان چه بد اعمالی انجام می‌دادند.'),
(1245, 9, 10, 'در باره هیچ مؤمنی مراعات خویشاوندی و پیمانی را نمی‌کنند، و ایشان همان تجاوزکارانند.'),
(1246, 9, 11, 'پس اگر توبه کنند و نماز برپا دارند و زکات دهند، در این صورت برادران دینی شما می‌باشند، و ما آیات [خود] را برای گروهی که می‌دانند به تفصیل بیان می‌کنیم.'),
(1247, 9, 12, 'و اگر سوگندهای خود را پس از پیمان خویش شکستند و شما را در دینتان طعن زدند، پس با پیشوایانِ کفر بجنگید، چرا که آنان را هیچ پیمانی نیست، باشد که [از پیمان‌شکنی‌] باز ایستند.'),
(1248, 9, 13, 'چرا با گروهی که سوگندهای خود را شکستند و بر آن شدند که فرستاده [خدا] را بیرون کنند، و آنان بودند که نخستین‌بار [جنگ را] با شما آغاز کردند، نمی‌جنگید؟ آیا از آنان می‌ترسید؟ با اینکه اگر مؤمنید خدا سزاوارتر است که از او بترسید.'),
(1249, 9, 14, 'با آنان بجنگید؛ خدا آنان را به دست شما عذاب و رسوایشان می‌کند و شما را بر ایشان پیروزی می‌بخشد و دلهای گروه مؤمنان را خنک می‌گرداند.'),
(1250, 9, 15, 'و خشمِ دلهایشان را بِبَرد، و خدا توبه هر که را بخواهد می‌پذیرد، و خدا دانای حکیم است.'),
(1251, 9, 16, 'آیا پنداشته‌اید که به خود واگذار می‌شوید، و خداوند کسانی را که از میان شما جهاد کرده و غیر از خدا و فرستاده او و مؤمنان، محرم اسراری نگرفته‌اند، معلوم نمی‌دارد؟ و خدا به آنچه انجام می‌دهید آگاه است.'),
(1252, 9, 17, 'مشرکان را نرسد که مساجد خدا را آباد کنند، در حالی که به کفر خویش شهادت می‌دهند. آنانند که اعمالشان به هدر رفته و خود در آتش جاودانند.'),
(1253, 9, 18, 'مساجد خدا را تنها کسانی آباد می‌کنند که به خدا و روز بازپسین ایمان آورده و نماز برپا داشته و زکات داده و جز از خدا نترسیده‌اند، پس امید است که اینان از راه‌یافتگان باشند.'),
(1254, 9, 19, 'آیا سیراب ساختن حاجیان و آباد کردن مسجد الحرام را همانند [کار] کسی پنداشته‌اید که به خدا و روز بازپسین ایمان آورده و در راه خدا جهاد می‌کند؟ [نه، این دو] نزد خدا یکسان نیستند، و خدا بیدادگران را هدایت نخواهد کرد.'),
(1255, 9, 20, 'کسانی که ایمان آورده و هجرت کرده و در راه خدا با مال و جانشان به جهاد پرداخته‌اند نزد خدا مقامی هر چه والاتر دارند و اینان همان رستگارانند.'),
(1256, 9, 21, 'پروردگارشان آنان را از جانب خود، به رحمت و خشنودی و باغهایی [در بهشت‌] که در آنها نعمتهایی پایدار دارند، مژده می‌دهد.'),
(1257, 9, 22, 'جاودانه در آنها خواهند بود، در حقیقت، خداست که نزد او پاداشی بزرگ است.'),
(1258, 9, 23, 'ای کسانی که ایمان آورده‌اید، اگر پدرانتان و برادرانتان کفر را بر ایمان ترجیح دهند [آنان را] به دوستی مگیرید، و هر کس از میان شما آنان را به دوستی گیرد، آنان همان ستمکارانند.'),
(1259, 9, 24, 'بگو: «اگر پدران و پسران و برادران و زنان و خاندان شما و اموالی که گرد آورده‌اید و تجارتی که از کسادش بیمناکید و سراهایی را که خوش می‌دارید، نزد شما از خدا و پیامبرش و جهاد در راه وی دوست‌داشتنی‌تر است، پس منتظر باشید تا خدا فرمانش را [به اجرا در]آورد.» و خداوند گروه فاسقان را راهنمایی نمی‌کند.'),
(1260, 9, 25, 'قطعاً خداوند شما را در مواضع بسیاری یاری کرده است، و [نیز] در روز «حنین»؛ آن هنگام که شمار زیادتان شما را به شگفت آورده بود، ولی به هیچ وجه از شما دفع [خطر] نکرد، و زمین با همه فراخی بر شما تنگ گردید، سپس در حالی که پشت [به دشمن‌] کرده بودید برگشتید.'),
(1261, 9, 26, 'آنگاه خدا آرامش خود را بر فرستاده خود و بر مؤمنان فرود آورد، و سپاهیانی فرو فرستاد که آنها را نمی‌دیدید، و کسانی را که کفر ورزیدند عذاب کرد، و سزای کافران همین بود.'),
(1262, 9, 27, 'سپس خدا بعد از این [واقعه‌] توبه هر کس را بخواهد می‌پذیرد، و خدا آمرزنده مهربان است.'),
(1263, 9, 28, 'ای کسانی که ایمان آورده‌اید، حقیقت این است که مشرکان ناپاکند، پس نباید از سال آینده به مسجدالحرام نزدیک شوند، و اگر [در این قطع رابطه‌] از فقر بیمناکید، پس به زودی خدا -اگر بخواهد- شما را به فضل خویش بی‌نیاز می‌گرداند، که خدا دانای حکیم است.'),
(1264, 9, 29, 'با کسانی از اهل کتاب که به خدا و روز بازپسین ایمان نمی‌آورند، و آنچه را خدا و فرستاده‌اش حرام گردانیده‌اند حرام نمی‌دارند و متدین به دین حق نمی‌گردند، کارزار کنید، تا با [کمال‌] خواری به دست خود جزیه دهند.'),
(1265, 9, 30, 'و یهود گفتند: «عُزَیر، پسر خداست.» و نصاری گفتند: «مسیح، پسر خداست.» این سخنی است [باطل‌] که به زبان می‌آورند، و به گفتار کسانی که پیش از این کافر شده‌اند شباهت دارد. خدا آنان را بکشد؛ چگونه [از حق‌] بازگردانده می‌شوند؟'),
(1266, 9, 31, 'اینان دانشمندان و راهبان خود و مسیح پسر مریم را به جای خدا به الوهیّت گرفتند، با آنکه مأمور نبودند جز اینکه خدایی یگانه را بپرستند که هیچ معبودی جز او نیست. منزه است او از آنچه [با وی‌] شریک می‌گردانند.'),
(1267, 9, 32, 'می‌خواهند نور خدا را با سخنان خویش خاموش کنند، ولی خداوند نمی‌گذارد، تا نور خود را کامل کند، هر چند کافران را خوش نیاید.'),
(1268, 9, 33, 'او کسی است که پیامبرش را با هدایت و دین درست، فرستاد تا آن را بر هر چه دین است پیروز گرداند، هر چند مشرکان خوش نداشته باشند.'),
(1269, 9, 34, 'ای کسانی که ایمان آورده‌اید، بسیاری از دانشمندان یهود و راهبان، اموال مردم را به ناروا می‌خورند، و [آنان را] از راه خدا باز می‌دارند، و کسانی که زر و سیم را گنجینه می‌کنند و آن را در راه خدا هزینه نمی‌کنند، ایشان را از عذابی دردناک خبر ده.'),
(1270, 9, 35, 'روزی که آن [گنجینه‌]ها را در آتش دوزخ بگدازند، و پیشانی و پهلو و پشت آنان را با آنها داغ کنند [و گویند:] «این است آنچه برای خود اندوختید، پس [کیفر] آنچه را می‌اندوختید بچشید.»'),
(1271, 9, 36, 'در حقیقت، شماره ماه‌ها نزد خدا، از روزی که آسمانها و زمین را آفریده، در کتاب [علم‌] خدا، دوازده ماه است؛ از این [دوازده ماه‌]، چهار ماه، [ماهِ‌] حرام است. این است آیین استوار، پس در این [چهار ماه ]بر خود ستم مکنید، و همگی با مشرکان بجنگید، چنانکه آنان همگی با شما می‌جنگند، و بدانید که خدا با پرهیزگاران است.'),
(1272, 9, 37, 'جز این نیست که جابجا کردن [ماههای حرام‌]، فزونی در کفر است که کافران به وسیله آن گمراه می‌شوند؛ آن را یکسال حلال می‌شمارند، و یکسال [دیگر]، آن را حرام می‌دانند، تا با شماره ماههایی که خدا حرام کرده است موافق سازند، و در نتیجه آنچه را خدا حرام کرده [بر خود] حلال گردانند. زشتی اعمالشان برایشان آراسته شده است، و خدا گروه کافران را هدایت نمی‌کند.'),
(1273, 9, 38, 'ای کسانی که ایمان آورده‌اید، شما را چه شده است که چون به شما گفته می‌شود: «در راه خدا بسیج شوید» کندی به خرج می‌دهید؟ آیا به جای آخرت به زندگی دنیا دل خوش کرده‌اید؟ متاع زندگی دنیا در برابر آخرت، جز اندکی نیست.'),
(1274, 9, 39, 'اگر بسیج نشوید، [خدا] شما را به عذابی دردناک عذاب می‌کند، و گروهی دیگر به جای شما می‌آورد، و به او زیانی نخواهید رسانید، و خدا بر هر چیزی تواناست.'),
(1275, 9, 40, 'اگر او [پیامبر] را یاری نکنید، قطعاً خدا او را یاری کرد: هنگامی که کسانی که کفر ورزیدند، او را [از مکّه‌] بیرون کردند، و او نفرِ دوم از دو تن بود، آنگاه که در غار [ثور] بودند، وقتی به همراه خود می‌گفت: «اندوه مدار که خدا با ماست.» پس خدا آرامش خود را بر او فرو فرستاد، و او را با سپاهیانی که آنها را نمی‌دیدید تأیید کرد، و کلمه کسانی را که کفر ورزیدند پست‌تر گردانید، و کلمه خداست که برتر است، و خدا شکست‌ناپذیرِ حکیم است.'),
(1276, 9, 41, 'سبکبار و گرانبار، بسیج شوید و با مال و جانتان در راه خدا جهاد کنید. اگر بدانید، این برای شما بهتر است.'),
(1277, 9, 42, 'اگر مالی در دسترس و سفری [آسان و] کوتاه بود، قطعاً از پی تو می‌آمدند، ولی آن راهِ پر مشقت بر آنان دور می‌نماید، و به زودی به خدا سوگند خواهند خورد که اگر می‌توانستیم حتماً با شما بیرون می‌آمدیم، [با سوگند دروغ‌]، خود را به هلاکت می‌کشانند و خدا می‌داند که آنان سخت دروغگویند.'),
(1278, 9, 43, 'خدایت ببخشاید، چرا پیش از آنکه [حال‌] راستگویان بر تو روشن شود و دروغگویان را بازشناسی، به آنان اجازه دادی؟'),
(1279, 9, 44, 'کسانی که به خدا و روز بازپسین ایمان دارند، در جهاد با مال و جانشان از تو عذر و اجازه نمی‌خواهند، و خدا به [حال‌] تقواپیشگان داناست.'),
(1280, 9, 45, 'تنها کسانی از تو اجازه می‌خواهند [به جهاد نروند] که به خدا و روز بازپسین ایمان ندارند و دلهایشان به شک افتاده و در شک خود سرگردانند.'),
(1281, 9, 46, 'و اگر [به راستی‌] اراده بیرون رفتن داشتند، قطعاً برای آن ساز و برگی تدارک می‌دیدند، ولی خداوند راه‌افتادن آنان را خوش نداشت، پس ایشان را منصرف گردانید و [به آنان‌] گفته شد: «با ماندگان بمانید.»'),
(1282, 9, 47, 'اگر با شما بیرون آمده بودند جز فساد برای شما نمی‌افزودند، و به سرعت خود را میان شما می‌انداختند و در حق شما فتنه‌جویی می‌کردند، و در میان شما جاسوسانی دارند [که‌] به نفع آنان [اقدام می‌کنند]، و خدا به [حال‌] ستمکاران داناست.'),
(1283, 9, 48, 'در حقیقت، پیش از این [نیز] در صدد فتنه‌جویی برآمدند و کارها را بر تو وارونه ساختند، تا حقّ آمد و امر خدا آشکار شد، در حالی که آنان ناخشنود بودند.'),
(1284, 9, 49, 'و از آنان کسی است که می‌گوید: «مرا [در ماندن‌] اجازه ده و به فتنه‌ام مینداز.» هش‌دار، که آنان خود به فتنه افتاده‌اند، و بی‌تردید جهنم بر کافران احاطه دارد.'),
(1285, 9, 50, 'اگر نیکی به تو رسد آنان را بدحال می‌سازد، و اگر پیشامد ناگواری به تو رسد می‌گویند: «ما پیش از این تصمیم خود را گرفته‌ایم.» و شادمان روی بر می‌تابند.'),
(1286, 9, 51, 'بگو: «جز آنچه خدا برای ما مقرر داشته هرگز به ما نمی‌رسد. او سرپرست ماست، و مؤمنان باید تنها بر خدا توکل کنند.»'),
(1287, 9, 52, 'بگو: «آیا برای ما جز یکی از این دو نیکی را انتظار می‌برید؟ در حالی که ما انتظار می‌کشیم که خدا از جانب خود یا به دست ما عذابی به شما برساند. پس انتظار بکشید که ما هم با شما در انتظاریم.»'),
(1288, 9, 53, 'بگو: «چه به رغبت چه با بی‌میلی انفاق کنید، هرگز از شما پذیرفته نخواهد شد، چرا که شما گروهی فاسق بوده‌اید.»'),
(1289, 9, 54, 'و هیچ چیز مانع پذیرفته شدن انفاقهای آنان نشد جز اینکه به خدا و پیامبرش کفر ورزیدند، و جز با [حال‌] کسالت نماز به جا نمی‌آورند، و جز با کراهت انفاق نمی‌کنند.'),
(1290, 9, 55, 'اموال و فرزندانشان تو را به شگفت نیاورد. جز این نیست که خدا می‌خواهد در زندگی دنیا به وسیله اینها عذابشان کند و جانشان در حال کفر بیرون رود.'),
(1291, 9, 56, 'و به خدا سوگند یاد می‌کنند که آنان قطعاً از شمایند، در حالی که از شما نیستند، لیکن آنان گروهی هستند که می‌ترسند.'),
(1292, 9, 57, 'اگر پناهگاه یا غارها یا سوراخی [برای فرار] می‌یافتند، شتابزده به سوی آن روی می‌آوردند.'),
(1293, 9, 58, 'و برخی از آنان در [تقسیم‌] صدقات بر تو خرده می‌گیرند، پس اگر از آن [اموال‌] به ایشان داده شود خشنود می‌گردند، و اگر از آن به ایشان داده نشود بناگاه به خشم می‌آیند.'),
(1294, 9, 59, 'و اگر آنان بدانچه خدا و پیامبرش به ایشان داده‌اند خشنود می‌گشتند و می‌گفتند: «خدا ما را بس است به زودی خدا و پیامبرش از کرم خود به ما می‌دهند و ما به خدا مشتاقیم» [قطعاً برای آنان بهتر بود].'),
(1295, 9, 60, 'صدقات، تنها به تهیدستان و بینوایان و متصدیّان [گردآوری و پخش‌] آن، و کسانی که دلشان به دست آورده می‌شود، و در [راه آزادی‌] بردگان، و وامداران، و در راه خدا، و به در راه مانده، اختصاص دارد. [این‌] به عنوان فریضه از جانب خداست، و خدا دانای حکیم است.'),
(1296, 9, 61, 'و از ایشان کسانی هستند که پیامبر را آزار می‌دهند و می‌گویند: «او زودباور است.» بگو: «گوش خوبی برای شماست، به خدا ایمان دارد و [سخن‌] مؤمنان را باور می‌کند، و برای کسانی از شما که ایمان آورده‌اند رحمتی است.» و کسانی که پیامبر خدا را آزار می‌رسانند، عذابی پر درد [در پیش‌] خواهند داشت.'),
(1297, 9, 62, 'برای [اِغفال‌] شما به خدا سوگند یاد می‌کنند تا شما را خشنود گردانند، در صورتی که اگر مؤمن باشند [بدانند] سزاوارتر است که خدا و فرستاده او را خشنود سازند.'),
(1298, 9, 63, 'آیا ندانسته‌اند که هر کس با خدا و پیامبر او درافتد برای او آتش جهنم است که در آن جاودانه خواهد بود، این همان رسوایی بزرگ است.'),
(1299, 9, 64, 'منافقان بیم دارند از اینکه [مبادا] سوره‌ای در باره آنان نازل شود که ایشان را از آنچه در دلهایشان هست خبر دهد. بگو: «ریشخند کنید، بی‌تردید خدا آنچه را که [از آن‌] می‌ترسید برملا خواهد کرد.»'),
(1300, 9, 65, 'و اگر از ایشان بپرسی، مسلماً خواهند گفت: «ما فقط شوخی و بازی می‌کردیم.» بگو: «آیا خدا و آیات او و پیامبرش را ریشخند می‌کردید؟»'),
(1301, 9, 66, 'عذر نیاورید، شما بعد از ایمانتان کافر شده‌اید. اگر از گروهی از شما درگذریم، گروهی [دیگر] را عذاب خواهیم کرد، چرا که آنان تبهکار بودند.'),
(1302, 9, 67, 'مردان و زنان دو چهره، [همانند] یکدیگرند. به کار ناپسند وامی‌دارند و از کار پسندیده باز می‌دارند، و دستهای خود را [از انفاق‌] فرو می‌بندند. خدا را فراموش کردند، پس [خدا هم‌] فراموششان کرد. در حقیقت، این منافقانند که فاسقند.'),
(1303, 9, 68, 'خدا به مردان و زنان دو چهره و کافران، آتش جهنم را وعده داده است. در آن جاودانه‌اند. آن [آتش‌] برای ایشان کافی است، و خدا لعنتشان کرده و برای آنان عذابی پایدار است.'),
(1304, 9, 69, '[حال شما منافقان‌] چون کسانی است که پیش از شما بودند: آنان از شما نیرومندتر و دارای اموال و فرزندان بیشتر بودند. پس، از نصیب خویش [در دنیا] برخوردار شدند، و شما [هم‌] از نصیب خود برخوردار شدید؛ همان گونه که آنان که پیش از شما بودند از نصیب خویش برخوردار شدند، و شما [در باطل‌] فرو رفتید؛ همان گونه که آنان فرو رفتند. آنان اعمالشان در دنیا و آخرت به هدر رفت و آنان همان زیانکارانند.'),
(1305, 9, 70, 'آیا گزارش [حال‌] کسانی که پیش از آنان بودند: قوم نوح و عاد و ثمود و قوم ابراهیم و اصحاب مَدْیَن و شهرهای زیر و رو شده، به ایشان نرسیده است؟ پیامبرانشان دلایل آشکار برایشان آوردند، خدا بر آن نبود که به آنان ستم کند ولی آنان بر خود ستم روا می‌داشتند.'),
(1306, 9, 71, 'و مردان و زنان با ایمان، دوستان یکدیگرند، که به کارهای پسندیده وا می‌دارند، و از کارهای ناپسند باز می‌دارند، و نماز را بر پا می‌کنند و زکات می‌دهند، و از خدا و پیامبرش فرمان می‌برند. آنانند که خدا به زودی مشمول رحمتشان قرار خواهد داد، که خدا توانا و حکیم است.'),
(1307, 9, 72, 'خداوند به مردان و زنان با ایمان باغهایی وعده داده است که از زیر [درختان‌] آن نهرها جاری است. در آن جاودانه خواهند بود. و [نیز] سراهایی پاکیزه در بهشتهای جاودان [به آنان وعده داده است‌] و خشنودی خدا بزرگتر است. این است همان کامیابی بزرگ.'),
(1308, 9, 73, 'ای پیامبر، با کافران و منافقان جهاد کن و بر آنان سخت بگیر، و جایگاهشان دوزخ است، و چه بد سرانجامی است.'),
(1309, 9, 74, 'به خدا سوگند می‌خورند که [سخن ناروا] نگفته‌اند، در حالی که قطعاً سخن کفر گفته و پس از اسلام آوردنشان کفر ورزیده‌اند، و بر آنچه موفق به انجام آن نشدند همّت گماشتند، و به عیبجویی برنخاستند مگر [بعد از] آنکه خدا و پیامبرش از فضل خود آنان را بی‌نیاز گردانیدند. پس اگر توبه کنند برای آنان بهتر است، و اگر روی برتابند، خدا آنان را در دنیا و آخرت عذابی دردناک می‌کند، و در روی زمین یار و یاوری نخواهند داشت.'),
(1310, 9, 75, 'و از آنان کسانی‌اند که با خدا عهد کرده‌اند که اگر از کرم خویش به ما عطا کند، قطعاً صدقه خواهیم داد و از شایستگان خواهیم شد.'),
(1311, 9, 76, 'پس چون از فضل خویش به آنان بخشید، بدان بخل ورزیدند، و به حال اعراض روی برتافتند.'),
(1312, 9, 77, 'در نتیجه، به سزای آنکه با خدا خلف وعده کردند و از آن روی که دروغ می‌گفتند، در دلهایشان -تا روزی که او را دیدار می‌کنند- پیامدهای نفاق را باقی گذارد.'),
(1313, 9, 78, 'آیا ندانسته‌اند که خدا راز آنان و نجوای ایشان را می‌داند و خدا دانای رازهای نهانی است؟'),
(1314, 9, 79, 'کسانی که بر مؤمنانی که [افزون بر صدقه واجب‌]، از روی میل، صدقات [مستحبّ نیز] می‌دهند، عیب می‌گیرند، و [همچنین‌] از کسانی که [در انفاق‌] جز به اندازه توانشان نمی‌یابند، [عیبجویی می‌کنند] و آنان را به ریشخند می‌گیرند، [بدانند که ]خدا آنان را به ریشخند می‌گیرد و برای ایشان عذابی پر درد خواهد بود.'),
(1315, 9, 80, 'چه برای آنان آمرزش بخواهی یا برایشان آمرزش نخواهی [یکسان است، حتی‌] اگر هفتاد بار برایشان آمرزش طلب کنی هرگز خدا آنان را نخواهد آمرزید، چرا که آنان به خدا و فرستاده‌اش کفر ورزیدند، و خدا گروه فاسقان را هدایت نمی‌کند.'),
(1316, 9, 81, 'بر جای‌ماندگان، به [خانه‌] نشستن خود، پس از رسول خدا، شادمان شدند، و از اینکه با مال و جان خود در راه خدا جهاد کنند، کراهت داشتند، و گفتند: «در این گرما بیرون نروید.» بگو: «-اگر دریابند- آتش جهنّم سوزان‌تر است.»'),
(1317, 9, 82, 'از این پس کم بخندند، و به جزای آنچه به دست می‌آوردند، بسیار بگریند.'),
(1318, 9, 83, 'و اگر خدا تو را به سوی طایفه‌ای از آنان بازگردانید، و آنان برای بیرون آمدن [به جنگ دیگری‌] از تو اجازه خواستند، بگو: «شما هرگز با من خارج نخواهید شد، و هرگز همراه من با هیچ دشمنی نبرد نخواهید کرد، زیرا شما نخستین‌بار به نشستن تن دردادید. پس [اکنون هم‌] با خانه‌نشینان بنشینید.»'),
(1319, 9, 84, 'و هرگز بر هیچ مرده‌ای از آنان نماز مگزار و بر سر قبرش نایست، چرا که آنان به خدا و پیامبر او کافر شدند و در حال فسق مردند.'),
(1320, 9, 85, 'و اموال و فرزندان آنان تو را به شگفت نیندازد. جز این نیست که خدا می‌خواهد ایشان را در دنیا به وسیله آن عذاب کند و جانشان در حال کفر بیرون رود.'),
(1321, 9, 86, 'و چون سوره‌ای نازل شود که به خدا ایمان آورید و همراه پیامبرش جهاد کنید، ثروتمندانشان از تو عذر و اجازه خواهند و گویند: «بگذار که ما با خانه‌نشینان باشیم.»'),
(1322, 9, 87, 'راضی شدند که با خانه‌نشینان باشند، و بر دلهایشان مُهر زده شده است، در نتیجه قدرت درک ندارند.'),
(1323, 9, 88, 'ولی پیامبر و کسانی که با او ایمان آورده‌اند با مال و جانشان به جهاد برخاسته‌اند. و اینانند که همه خوبیها برای آنان است، اینان همان رستگارانند.'),
(1324, 9, 89, 'خدا برای آنان باغهایی آماده کرده است که از زیرِ [درختان‌] آن نهرها روان است، و در آن جاودانه‌اند. این همان رستگاریِ بزرگ است.'),
(1325, 9, 90, 'و عذرخواهان بادیه‌نشین [نزد تو] آمدند تا به آنان اجازه [ترک جهاد] داده شود. و کسانی که به خدا و فرستاده او دروغ گفتند نیز در خانه نشستند. به زودی کسانی از آنان را که کفر ورزیدند عذابی دردناک خواهد رسید.'),
(1326, 9, 91, 'بر ناتوانان و بر بیماران و بر کسانی که چیزی نمی‌یابند [تا در راه جهاد ] خرج کنند -در صورتی که برای خدا و پیامبرش خیرخواهی نمایند- هیچ گناهی نیست، [و نیز] بر نیکوکاران ایرادی نیست، و خدا آمرزنده مهربان است.'),
(1327, 9, 92, 'و [نیز] گناهی نیست بر کسانی که چون پیش تو آمدند تا سوارشان کنی [و] گفتی: «چیزی پیدا نمی‌کنم تا بر آن سوارتان کنم»، برگشتند، و در اثر اندوه، از چشمانشان اشک فرو می‌ریخت که [چرا] چیزی نمی‌یابند تا [در راه جهاد] خرج کنند.'),
(1328, 9, 93, 'ایراد فقط بر کسانی است که با اینکه توانگرند از تو اجازه [ترک جهاد] می‌خواهند. [و به این‌] راضی شده‌اند که با خانه‌نشینان باشند، و خدا بر دلهایشان مُهر نهاد، در نتیجه آنان نمی‌فهمند.'),
(1329, 9, 94, 'هنگامی که به سوی آنان بازگردید برای شما عذر می‌آورند. بگو: «عذر نیاورید، هرگز شما را باور نخواهیم داشت؛ خدا ما را از خبرهای شما آگاه گردانیده، و به زودی خدا و رسولش عمل شما را خواهند دید. آنگاه به سوی دانای نهان و آشکار، بازگردانیده می‌شوید، و از آنچه انجام می‌دادید به شما خبر می‌دهد.»'),
(1330, 9, 95, 'وقتی به سوی آنان بازگشتید، برای شما به خدا سوگند می‌خورند تا از ایشان صرفنظر کنید. پس، از آنان روی برتابید، چرا که آنان پلیدند، و به [سزای‌] آنچه به دست آورده‌اند جایگاهشان دوزخ خواهد بود.'),
(1331, 9, 96, 'برای شما سوگند یاد می‌کنند تا از آنان خشنود گردید. پس اگر شما هم از ایشان خشنود شوید قطعاً خدا از گروه فاسقان خشنود نخواهد شد.'),
(1332, 9, 97, 'بادیه‌نشینان عرب، در کفر و نفاق [از دیگران‌] سخت‌تر، و به اینکه حدود آنچه را که خدا بر فرستاده‌اش نازل کرده، ندانند، سزاوارترند. و خدا دانای حکیم است.'),
(1333, 9, 98, 'و برخی از آن بادیه‌نشینان کسانی هستند که آنچه را [در راه خدا] هزینه می‌کنند، خسارتی [برای خود] می‌دانند، و برای شما پیشامدهای بد انتظار می‌برند. پیشامد بد برای آنان خواهد بود، و خدا شنوای داناست.'),
(1334, 9, 99, 'و برخی [دیگر] از بادیه‌نشینان کسانی‌اند که به خدا و روز بازپسین ایمان دارند و آنچه را انفاق می‌کنند مایه تقرّب نزد خدا و دعاهای پیامبر می‌دانند. بدانید که این [انفاق‌] مایه تقرّب آنان است. به زودی خدا ایشان را در جوار رحمت خویش درآورد، که خدا آمرزنده مهربان است.'),
(1335, 9, 100, 'و پیشگامان نخستین از مهاجران و انصار، و کسانی که با نیکوکاری از آنان پیروی کردند، خدا از ایشان خشنود و آنان [نیز] از او خشنودند، و برای آنان باغهایی آماده کرده که از زیر [درختان‌] آن نهرها روان است. همیشه در آن جاودانه‌اند. این است همان کامیابی بزرگ.'),
(1336, 9, 101, 'و برخی از بادیه‌نشینانی که پیرامون شما هستند منافقند، و از ساکنان مدینه [نیز عده‌ای‌] بر نفاق خو گرفته‌اند. تو آنان را نمی‌شناسی، ما آنان را می‌شناسیم. به زودی آنان را دو بار عذاب می‌کنیم؛ سپس به عذابی بزرگ بازگردانیده می‌شوند.'),
(1337, 9, 102, 'و دیگرانی هستند که به گناهان خود اعتراف کرده و کار شایسته را با [کاری‌] دیگر که بد است درآمیخته‌اند. امید است خدا توبه آنان را بپذیرد، که خدا آمرزنده مهربان است.'),
(1338, 9, 103, 'از اموال آنان صدقه‌ای بگیر تا به وسیله آن پاک و پاکیزه‌شان سازی، و برایشان دعا کن، زیرا دعای تو برای آنان آرامشی است، و خدا شنوای داناست.'),
(1339, 9, 104, 'آیا ندانسته‌اند که تنها خداست که از بندگانش توبه را می‌پذیرد و صدقات را می‌گیرد، و خداست که خود توبه‌پذیر مهربان است؟'),
(1340, 9, 105, 'و بگو: «[هر کاری می‌خواهید] بکنید، که به زودی خدا و پیامبر او و مؤمنان در کردار شما خواهند نگریست، و به زودی به سوی دانای نهان و آشکار بازگردانیده می‌شوید؛ پس ما را به آنچه انجام می‌دادید آگاه خواهد کرد.»'),
(1341, 9, 106, 'و عدّه‌ای دیگر [کارشان‌] موقوف به فرمان خداست: یا آنان را عذاب می‌کند و یا توبه آنها را می‌پذیرد، و خدا دانای سنجیده‌کار است.'),
(1342, 9, 107, 'و آنهایی که مسجدی اختیار کردند که مایه زیان و کفر و پراکندگی میان مؤمنان است، و [نیز] کمینگاهی است برای کسی که قبلاً با خدا و پیامبر او به جنگ برخاسته بود، و سخت سوگند یاد می‌کنند که جز نیکی قصدی نداشتیم. و[لی‌] خدا گواهی می‌دهد که آنان قطعاً دروغگو هستند.'),
(1343, 9, 108, 'هرگز در آن جا مایست، چرا که مسجدی که از روز نخستین بر پایه تقوا بنا شده، سزاوارتر است که در آن [به نماز] ایستی. [و] در آن، مردانی‌اند که دوست دارند خود را پاک سازند، و خدا کسانی را که خواهان پاکی‌اند دوست می‌دارد.'),
(1344, 9, 109, 'آیا کسی که بنیاد [کار] خود را بر پایه تقوا و خشنودی خدا نهاده بهتر است یا کسی که بنای خود را بر لب پرتگاهی مُشرف به سقوط پی‌ریزی کرده و با آن در آتش دوزخ فرو می‌افتد؟ و خدا گروه بیدادگران را هدایت نمی‌کند.'),
(1345, 9, 110, 'همواره آن ساختمانی که بنا کرده‌اند، در دلهایشان مایه شک [و نفاق‌] است، تا آنکه دلهایشان پاره پاره شود، و خدا دانای سنجیده‌کار است.'),
(1346, 9, 111, 'در حقیقت، خدا از مؤمنان، جان و مالشان را به [بهای‌] اینکه بهشت برای آنان باشد، خریده است؛ همان کسانی که در راه خدا می‌جنگند و می‌کُشند و کشته می‌شوند. [این‌] به عنوان وعده حقی در تورات و انجیل و قرآن بر عهده اوست. و چه کسی از خدا به عهد خویش وفادارتر است؟ پس به این معامله‌ای که با او کرده‌اید شادمان باشید، و این همان کامیابی بزرگ است.'),
(1347, 9, 112, '[آن مؤمنان،] همان توبه‌کنندگان، پرستندگان، سپاسگزاران، روزه‌داران، رکوع‌کنندگان، سجده‌کنندگان، وادارندگان به کارهای پسندیده، بازدارندگان از کارهای ناپسند و پاسداران مقرّرات خدایند. و مؤمنان را بشارت ده.'),
(1348, 9, 113, 'بر پیامبر و کسانی که ایمان آورده‌اند سزاوار نیست که برای مشرکان -پس از آنکه برایشان آشکار گردید که آنان اهل دوزخند- طلب آمرزش کنند، هر چند خویشاوندِ [آنان‌] باشند.'),
(1349, 9, 114, 'و طلب آمرزش ابراهیم برای پدرش جز برای وعده‌ای که به او داده بود، نبود. و[لی‌] هنگامی که برای او روشن شد که وی دشمن خداست، از او بیزاری جست. راستی، ابراهیم، دلسوزی بردبار بود.'),
(1350, 9, 115, 'و خدا بر آن نیست که گروهی را پس از آنکه هدایتشان نمود بی‌راه بگذارد، مگر آنکه چیزی را که باید از آن پروا کنند برایشان بیان کرده باشد. آری، خدا به هر چیزی داناست.'),
(1351, 9, 116, 'در حقیقت، فرمانروایی آسمانها و زمین از آن خداست. زنده می‌کند و می‌میراند، و برای شما جز خدا یار و یاوری نیست.'),
(1352, 9, 117, 'به یقین، خدا بر پیامبر و مهاجران و انصار که در آن ساعت دشوار از او پیروی کردند ببخشود، بعد از آنکه چیزی نمانده بود که دلهای دسته‌ای از آنان منحرف شود. باز برایشان ببخشود، چرا که او نسبت به آنان مهربان و رحیم است.'),
(1353, 9, 118, 'و [نیز] بر آن سه تن که بر جای مانده بودند، [و قبول توبه آنان به تعویق افتاد] تا آنجا که زمین با همه فراخی‌اش بر آنان تنگ گردید، و از خود به تنگ آمدند و دانستند که پناهی از خدا جز به سوی او نیست. پس [خدا] به آنان [توفیق ]توبه داد، تا توبه کنند. بی تردید خدا همان توبه‌پذیر مهربان است.'),
(1354, 9, 119, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید و با راستان باشید.'),
(1355, 9, 120, 'مردم مدینه و بادیه‌نشینان پیرامونشان را نرسد که از [فرمان‌] پیامبر خدا سر باز زنند و جان خود را عزیزتر از جان او بدانند، چرا که هیچ تشنگی و رنج و گرسنگیی در راه خدا به آنان نمی‌رسد؛ و در هیچ مکانی که کافران را به خشم می‌آورد قدم نمی‌گذارند و از دشمنی غنیمتی به دست نمی‌آورند مگر اینکه به سبب آن، عمل صالحی برای آنان [در کارنامه‌شان‌] نوشته می‌شود، زیرا خدا پاداش نیکوکاران را ضایع نمی‌کند.'),
(1356, 9, 121, 'و هیچ مال کوچک و بزرگی را انفاق نمی‌کنند و هیچ وادیی را نمی‌پیمایند مگر اینکه به حساب آنان نوشته می‌شود، تا خدا آنان را به بهتر از آنچه می‌کردند پاداش دهد.'),
(1357, 9, 122, 'و شایسته نیست مؤمنان همگی [برای جهاد] کوچ کنند. پس چرا از هر فرقه‌ای از آنان، دسته‌ای کوچ نمی‌کنند تا [دسته‌ای بمانند و] در دین آگاهی پیدا کنند و قوم خود را -وقتی به سوی آنان بازگشتند بیم دهند- باشد که آنان [از کیفر الهی ]بترسند؟'),
(1358, 9, 123, 'ای کسانی که ایمان آورده‌اید، با کافرانی که مجاور شما هستند کارزار کنید، و آنان باید در شما خشونت بیابند، و بدانید که خدا با تقواپیشگان است.'),
(1359, 9, 124, 'و چون سوره‌ای نازل شود، از میان آنان کسی است که می‌گوید: «این [سوره‌] ایمان کدام یک از شما را افزود؟» اما کسانی که ایمان آورده‌اند بر ایمانشان می‌افزاید و آنان شادمانی می‌کنند.'),
(1360, 9, 125, 'اما کسانی که در دلهایشان بیماری است، پلیدی بر پلیدیشان افزود و در حال کفر درمی‌گذرند.'),
(1361, 9, 126, 'آیا نمی‌بینند که آنان در هر سال، یک یا دو بار آزموده می‌شوند، باز هم توبه نمی‌کنند و عبرت نمی‌گیرند؟'),
(1362, 9, 127, 'و چون سوره‌ای نازل شود، بعضی از آنان به بعضی دیگر نگاه می‌کنند [و می‌گویند:] «آیا کسی شما را می‌بیند؟» سپس [مخفیانه از حضور پیامبر] بازمی‌گردند. خدا دلهایشان را [از حق‌] برگرداند، زیرا آنان گروهی هستند که نمی‌فهمند.'),
(1363, 9, 128, 'قطعاً، برای شما پیامبری از خودتان آمد که بر او دشوار است شما در رنج بیفتید، به [هدایت‌] شما حریص، و نسبت به مؤمنان، دلسوز مهربان است.'),
(1364, 9, 129, 'پس اگر روی برتافتند، بگو: «خدا مرا بس است. هیچ معبودی جز او نیست. بر او توکل کردم، و او پروردگار عرش بزرگ است.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، راء. این است آیات کتاب حکمت‌آموز.'),
(1366, 10, 2, 'آیا برای مردم شگفت‌آور است که به مردی از خودشان وحی کردیم که: مردم را بیم ده و به کسانی که ایمان آورده‌اند مژده ده که برای آنان نزد پروردگارشان سابقه نیک است؟ کافران گفتند: «این [مرد] قطعاً افسونگری آشکار است.»'),
(1367, 10, 3, 'پروردگار شما آن خدایی است که آسمانها و زمین را در شش هنگام آفرید. سپس بر عرش استیلا یافت. کار [آفرینش‌] را تدبیر می‌کند. شفاعتگری جز پس از اذن او نیست. این است خدا، پروردگار شما، پس او را بپرستید. آیا پند نمی‌گیرید؟'),
(1368, 10, 4, 'بازگشت همه شما به سوی اوست. وعده خدا حق است؛ هموست که آفرینش را آغاز می‌کند سپس آن را باز می‌گرداند تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به عدالت پاداش دهد، و کسانی که کفر ورزیده‌اند به سزای کفرشان شربتی از آب جوشان و عذابی پر درد خواهند داشت.'),
(1369, 10, 5, 'اوست کسی که خورشید را روشنایی بخشید و ماه را تابان کرد، و برای آن منزلهایی معین کرد تا شماره سالها و حساب را بدانید. خدا اینها را جز به حق نیافریده است. نشانه‌ها[ی خود] را برای گروهی که می‌دانند به روشنی بیان می‌کند.'),
(1370, 10, 6, 'به راستی، در آمد و رفت شب و روز و آنچه خدا در آسمانها و زمین آفریده، برای مردمی که پروا دارند دلایلی [آشکار] است.'),
(1371, 10, 7, 'کسانی که امید به دیدار ما ندارند، و به زندگی دنیا دل خوش کرده و بدان اطمینان یافته‌اند، و کسانی که از آیات ما غافلند،'),
(1372, 10, 8, 'آنان به [کیفر] آنچه به دست می‌آوردند، جایگاهشان آتش است.'),
(1373, 10, 9, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، پروردگارشان به پاس ایمانشان آنان را هدایت می‌کند به باغهای [پر ناز و] نعمت، که از زیر [پای‌] آنان نهرها روان خواهد بود [در خواهند آمد].'),
(1374, 10, 10, 'نیایش آنان در آنجا سبحانک اللهم [=خدایا! تو پاک و منزهی‌] و درودشان در آنجا سلام است، و پایان نیایش آنان این است که: الحمد لله رب العالمین [=ستایش ویژه پروردگار جهانیان است‌].'),
(1375, 10, 11, 'و اگر خدا برای مردم به همان شتاب که آنان در کار خیر می‌طلبند، در رساندن بلا به آنها شتاب می‌نمود، قطعاً اجلشان فرا می‌رسید. پس کسانی را که به دیدار ما امید ندارند، در طغیانشان رها می‌کنیم تا سرگردان بمانند.'),
(1376, 10, 12, 'و چون انسان را آسیبی رسد، ما را -به پهلو خوابیده یا نشسته یا ایستاده- می‌خواند، و چون گرفتاریش را برطرف کنیم چنان می‌رود که گویی ما را برای گرفتاریی که به او رسیده، نخوانده است. این گونه برای اسرافکاران آنچه انجام می‌دادند زینت داده شده است.'),
(1377, 10, 13, 'و قطعاً نسلهای پیش از شما را هنگامی که ستم کردند به هلاکت رساندیم، و پیامبرانشان دلایل آشکار برایشان آوردند و[لی‌] بر آن نبودند که ایمان بیاورند. این گونه مردم بزهکار را جزا می‌دهیم.'),
(1378, 10, 14, 'آنگاه شما را پس از آنان در زمین جانشین قرار دادیم تا بنگریم چگونه رفتار می‌کنید.'),
(1379, 10, 15, 'و چون آیات روشن ما بر آنان خوانده شود، آنانکه به دیدار ما امید ندارند می‌گویند: «قرآن دیگری جز این بیاور، یا آن را عوض کن.» بگو: «مرا نرسد که آن را از پیش خود عوض کنم. جز آنچه را که به من وحی می‌شود پیروی نمی‌کنم. اگر پروردگارم را نافرمانی کنم، از عذاب روزی بزرگ می‌ترسم.»'),
(1380, 10, 16, 'بگو: «اگر خدا می‌خواست آن را بر شما نمی‌خواندم، و [خدا] شما را بدان آگاه نمی‌گردانید. قطعاً پیش از [آوردن‌] آن، روزگاری در میان شما به سر برده‌ام. آیا فکر نمی‌کنید؟»'),
(1381, 10, 17, 'پس کیست ستمکارتر از آن کس که دروغی بر خدای بندد یا آیات او را تکذیب کند؟ به راستی مجرمان رستگار نمی‌شوند.'),
(1382, 10, 18, 'و به جای خدا، چیزهایی را می‌پرستند که نه به آنان زیان می‌رساند و نه به آنان سود می‌دهد. و می‌گویند: «اینها نزد خدا شفاعتگران ما هستند.» بگو: «آیا خدا را به چیزی که در آسمانها و در زمین نمی‌داند، آگاه می‌گردانید؟» او پاک و برتر است از آنچه [با وی‌] شریک می‌سازند.'),
(1383, 10, 19, 'و مردم جز یک امت نبودند. پس اختلاف پیدا کردند. و اگر وعده‌ای از جانب پروردگارت مقرر نگشته بود، قطعاً در آنچه بر سر آن با هم اختلاف می‌کنند، میانشان داوری می‌شد.'),
(1384, 10, 20, 'و می‌گویند: «چرا معجزه‌ای از جانب پروردگارش بر او نازل نمی‌شود؟» بگو: «غیب فقط به خدا اختصاص دارد. پس منتظر باشید که من هم با شما از منتظرانم.»'),
(1385, 10, 21, 'و چون مردم را پس از آسیبی که به ایشان رسیده است، رحمتی بچشانیم، بناگاه آنان را در آیات ما نیرنگی است. بگو: «نیرنگ خدا سریع‌تر است.» در حقیقت، فرستادگان [=فرشتگان‌] ما آنچه نیرنگ می‌کنید می‌نویسند.'),
(1386, 10, 22, 'او کسی است که شما را در خشکی و دریا می‌گرداند، تا وقتی که در کشتیها باشید و آنها با بادی خوش، آنان را بِبَرَند و ایشان بدان شاد شوند [بناگاه‌] بادی سخت بر آنها وزد و موج از هر طرف بر ایشان تازد و یقین کنند که در محاصره افتاده‌اند، در آن حال خدا را پاکدلانه می‌خوانند که: «اگر ما را از این [ورطه‌] بِرَهانی، قطعاً از سپاسگزاران خواهیم شد.»'),
(1387, 10, 23, 'پس چون آنان را رهانید، ناگهان در زمین بناحق سرکشی می‌کنند. ای مردم، سرکشی شما فقط به زیان خود شماست. شما بهره زندگی دنیا را [می‌طلبید]. سپس بازگشت شما به سوی ما خواهد بود. پس شما را از آنچه انجام می‌دادید باخبر خواهیم کرد.'),
(1388, 10, 24, 'در حقیقت، مَثَل زندگی دنیا بسان آبی است که آن را از آسمان فرو ریختیم، پس گیاه زمین -از آنچه مردم و دامها می‌خورند- با آن درآمیخت، تا آنگاه که زمین پیرایه خود را برگرفت و آراسته گردید و اهل آن پنداشتند که آنان بر آن قدرت دارند، شبی یا روزی فرمان [ویرانی‌] ما آمد و آن را چنان دِرَویده کردیم که گویی دیروز وجود نداشته است. این گونه نشانه‌ها[ی خود] را برای مردمی که اندیشه می‌کنند به روشنی بیان می‌کنیم.'),
(1389, 10, 25, 'و خدا [شما را] به سرای سلامت فرا می‌خواند، و هر که را بخواهد به راه راست هدایت می‌کند.'),
(1390, 10, 26, 'برای کسانی که کار نیکو کرده‌اند، نیکویی [بهشت‌] و زیاده [بر آن‌] است. چهره‌هایشان را غباری و ذلتی نمی‌پوشاند. اینان اهل بهشتند [و] در آن جاودانه خواهند بود.'),
(1391, 10, 27, 'و کسانی که مرتکب بدیها شده‌اند، [بدانند که‌] جزای [هر] بدی مانند آن است و خواری آنان را فرو می‌گیرد. در مقابل خدا، هیچ حمایتگری برای ایشان نیست. گویی چهره‌هایشان با پاره‌ای از شبِ تار پوشیده شده است. آنان همدم آتشند، که در آن جاودانه خواهند بود.'),
(1392, 10, 28, 'و [یاد کن‌] روزی را که همه آنان را گرد می‌آوریم. آنگاه به کسانی که شرک ورزیده‌اند می‌گوییم: «شما و شریکانتان بر جای خود باشید» پس میان آنها جدایی می‌افکنیم. و شریکان آنان می‌گویند: «در حقیقت، شما ما را نمی‌پرستیدید.»'),
(1393, 10, 29, '«و گواهی خدا میان ما و میان شما بس است. به راستی ما از عبادت شما بی‌خبر بودیم.»'),
(1394, 10, 30, 'آنجاست که هر کسی آنچه را از پیش فرستاده است می‌آزماید، و به سوی خدا مولای حقیقی خود بازگردانیده می‌شوند، و آنچه به دروغ برمی‌ساخته‌اند از دستشان به در می‌رود.'),
(1395, 10, 31, 'بگو: «کیست که از آسمان و زمین به شما روزی می‌بخشد؟ یا کیست که حاکم بر گوشها و دیدگان است؟ و کیست که زنده را از مرده بیرون می‌آورد و مرده را از زنده خارج می‌سازد؟ و کیست که کارها را تدبیر می‌کند؟» خواهند گفت: «خدا» پس بگو: «آیا پروا نمی‌کنید؟»'),
(1396, 10, 32, 'این است خدا، پروردگار حقیقی شما، و بعد از حقیقت جز گمراهی چیست؟ پس چگونه [از حق‌] بازگردانیده می‌شوید؟'),
(1397, 10, 33, 'این گونه سخن پروردگارت بر کسانی که نافرمانی کردند به حقیقت پیوست، [چرا] که آنان ایمان نمی‌آورند.'),
(1398, 10, 34, 'بگو: «آیا از شریکان شما کسی هست که آفرینش را آغاز کند و سپس آن را برگرداند؟» بگو: «خداست که آفرینش را آغاز می‌کند و باز آن را برمی‌گرداند. پس چگونه [از حق‌] بازگردانیده می‌شوید؟»'),
(1399, 10, 35, 'بگو: «آیا از شریکان شما کسی هست که به سوی حق رهبری کند؟» بگو: «خداست که به سوی حق رهبری می‌کند» پس، آیا کسی که به سوی حق رهبری می‌کند سزاوارتر است مورد پیروی قرار گیرد یا کسی که راه نمی‌نماید مگر آنکه [خود] هدایت شود؟ شما را چه شده، چگونه داوری می‌کنید؟'),
(1400, 10, 36, 'و بیشترشان جز از گمان پیروی نمی‌کنند [ولی‌] گمان به هیچ وجه [آدمی را] از حقیقت بی‌نیاز نمی‌گرداند. آری، خدا به آنچه می‌کنند داناست.'),
(1401, 10, 37, 'و چنان نیست که این قرآن از جانب غیر خدا [و] به دروغ ساخته شده باشد. بلکه تصدیق [کننده‌] آنچه پیش از آن است می‌باشد، و توضیحی از آن کتاب است، که در آن تردیدی نیست، [و] از پروردگار جهانیان است.'),
(1402, 10, 38, 'یا می‌گویند: «آن را به دروغ ساخته است؟» بگو: «اگر راست می‌گویید، سوره‌ای مانند آن بیاورید، و هر که را جز خدا می‌توانید، فرا خوانید.»'),
(1403, 10, 39, 'بلکه چیزی را دروغ شمردند که به علم آن احاطه نداشتند و هنوز تأویل آن برایشان نیامده است. کسانی [هم‌] که پیش از آنان بودند، همین گونه [پیامبرانشان را] تکذیب کردند. پس بنگر که فرجام ستمگران چگونه بوده است.'),
(1404, 10, 40, 'و از آنان کسی است که بدان ایمان می‌آورد، و از آنان کسی است که بدان ایمان نمی‌آورد، و پروردگار تو به [حال‌] فسادگران داناتر است.'),
(1405, 10, 41, 'و اگر تو را تکذیب کردند، بگو: «عمل من به من اختصاص دارد، و عمل شما به شما اختصاص دارد. شما از آنچه من انجام می‌دهم غیر مسؤولید، و من از آنچه شما انجام نمی‌دهید غیر مسؤولم.»'),
(1406, 10, 42, 'و برخی از آنان کسانی‌اند که به تو گوش فرا می‌دهند. آیا تو کران را -هر چند در نیابند- شنوا خواهی کرد؟'),
(1407, 10, 43, 'و از آنان کسی است که به سوی تو می‌نگرد. آیا تو نابینایان را -هر چند نبینند- هدایت توانی کرد؟'),
(1408, 10, 44, 'خدا به هیچ وجه به مردم ستم نمی‌کند، لیکن مردم خود بر خویشتن ستم می‌کنند.'),
(1409, 10, 45, 'و روزی که آنان را گرد می‌آورد، گویی جز به اندازه ساعتی از روز درنگ نکرده‌اند. با هم اظهار آشنایی می‌کنند. قطعاً کسانی که دیدار خدا را دروغ شمردند زیان کردند و [به حقیقت‌] راه نیافتند.'),
(1410, 10, 46, 'و اگر پاره‌ای از آنچه را که به آنان وعده می‌دهیم به تو بنمایانیم، یا تو را بمیرانیم [در هر دو صورت‌] بازگشتشان به سوی ماست. سپس خدا بر آنچه می‌کنند گواه است.'),
(1411, 10, 47, 'و هر امتی را پیامبری است. پس چون پیامبرشان بیاید، میانشان به عدالت داوری شود و بر آنان ستم نرود.'),
(1412, 10, 48, 'و می‌گویند: «اگر راست می‌گویید، این وعده چه وقت است؟»'),
(1413, 10, 49, 'بگو: «برای خود زیان و سودی در اختیار ندارم، مگر آنچه را که خدا بخواهد. هر امتی را زمانی [محدود] است. آنگاه که زمانشان به سر رسد، پس نه ساعتی [از آن‌] تأخیر کنند و نه پیشی گیرند.»'),
(1414, 10, 50, 'بگو: «به من خبر دهید، اگر عذاب او شب یا روز به شما دررسد، بزهکاران چه چیزی از آن به شتاب می‌خواهند؟»'),
(1415, 10, 51, 'سپس، آیا هنگامی که [عذاب بر شما] واقع شد، اکنون به آن ایمان آوردید، در حالی که به [آمدن‌] آن شتاب می‌نمودید؟'),
(1416, 10, 52, 'پس به کسانی که ستم ورزیدند گفته شود: «عذاب جاوید را بچشید. آیا جز به [کیفر] آنچه به دست می‌آوردید، جزا داده می‌شوید؟»'),
(1417, 10, 53, 'و از تو خبر می‌گیرند: «آیا آن راست است؟» بگو: «آری! سوگند به پروردگارم که آن قطعاً راست است، و شما نمی‌توانید [خدا را] درمانده کنید.»'),
(1418, 10, 54, 'و اگر، برای هر کسی که ستم کرده است، آنچه در زمین است می‌بود، قطعاً آن را برای [خلاصی و] بازخرید خود می‌داد. و چون عذاب را ببینند پشیمانی خود را پنهان دارند، و میان آنان به عدالت داوری شود و بر ایشان ستم نرود.'),
(1419, 10, 55, 'بدانید، که در حقیقت آنچه در آسمانها و زمین است از آنِ خداست. بدانید، که در حقیقت وعده خدا حق است ولی بیشتر آنان نمی‌دانند.'),
(1420, 10, 56, 'او زنده می‌کند و می‌میراند و به سوی او بازگردانیده می‌شوید.'),
(1421, 10, 57, 'ای مردم، به یقین، برای شما از جانب پروردگارتان اندرزی، و درمانی برای آنچه در سینه‌هاست، و رهنمود و رحمتی برای گروندگان [به خدا] آمده است.'),
(1422, 10, 58, 'بگو: «به فضل و رحمت خداست که [مؤمنان‌] باید شاد شوند.» و این از هر چه گرد می‌آورند بهتر است.'),
(1423, 10, 59, 'بگو: «به من خبر دهید، آنچه از روزی که خدا برای شما فرود آورده [چرا] بخشی از آن را حرام و [بخشی را] حلال گردانیده‌اید.» بگو: «آیا خدا به شما اجازه داده یا بر خدا دروغ می‌بندید؟»'),
(1424, 10, 60, 'و کسانی که بر خدا دروغ می‌بندند، روز رستاخیز چه گمان دارند؟ در حقیقت، خدا بر مردم، دارای بخشش است ولی بیشترشان سپاسگزاری نمی‌کنند.'),
(1425, 10, 61, 'و در هیچ کاری نباشی و از سوی او [=خدا] هیچ [آیه‌ای‌] از قرآن نخوانی و هیچ کاری نکنید، مگر اینکه ما بر شما گواه باشیم آنگاه که بدان مبادرت می‌ورزید. و هم‌وزن ذرّه‌ای، نه در زمین و نه در آسمان از پروردگار تو پنهان نیست، و نه کوچکتر و نه بزرگتر از آن چیزی نیست، مگر اینکه در کتابی روشن [درج شده‌] است.'),
(1426, 10, 62, 'آگاه باشید، که بر دوستان خدا نه بیمی است و نه آنان اندوهگین می‌شوند.'),
(1427, 10, 63, 'همانان که ایمان آورده و پرهیزگاری ورزیده‌اند.'),
(1428, 10, 64, 'در زندگی دنیا و در آخرت مژده برای آنان است. وعده‌های خدا را تبدیلی نیست؛ این همان کامیابی بزرگ است.'),
(1429, 10, 65, 'سخن آنان تو را غمگین نکند، زیرا عزّت، همه از آنِ خداست. او شنوای داناست.'),
(1430, 10, 66, 'آگاه باش، که هر که [و هر چه‌] در آسمانها، و هر که [و هر چه‌] در زمین است از آنِ خداست. و کسانی که غیر از خدا شریکانی را می‌خوانند، [از آنها] پیروی نمی‌کنند. اینان جز از گمان پیروی نمی‌کنند و جز گمان نمی‌برند.'),
(1431, 10, 67, 'اوست کسی که برای شما شب را قرار داد تا در آن بیارامید و روز را روشن [گردانید]. بی گمان، در این [امر] برای مردمی که می‌شنوند نشانه‌هایی است.'),
(1432, 10, 68, 'گفتند: «خدا فرزندی برای خود اختیار کرده است.» منزّه است او. او بی‌نیاز است. آنچه در آسمانها و آنچه در زمین است از آنِ اوست. شما را بر این [ادّعا] حجّتی نیست. آیا چیزی را که نمی‌دانید، به دروغ بر خدا می‌بندید؟'),
(1433, 10, 69, 'بگو: «در حقیقت کسانی که بر خدا دروغ می‌بندند، رستگار نمی‌شوند.»'),
(1434, 10, 70, 'بهره‌ای [اندک‌] در دنیا [دارند]. سپس بازگشتشان به سوی ماست. آنگاه به [سزای‌] آنکه کفر می‌ورزیدند، عذاب سخت به آنان می‌چشانیم.'),
(1435, 10, 71, 'و خبر نوح را بر آنان بخوان، آنگاه که به قوم خود گفت: «ای قوم من، اگر ماندن من [در میان شما] و اندرز دادن من به آیات خدا، بر شما گران آمده است، [بدانید که من‌] بر خدا توکّل کرده‌ام. پس [در] کارتان با شریکان خود همداستان شوید، تا کارتان بر شما ملتبس ننماید سپس در باره من تصمیم بگیرید و مهلتم ندهید.»'),
(1436, 10, 72, '«و اگر روی گردانیدید، من مزدی از شما نمی‌طلبم. پاداش من جز بر عهده خدا نیست، و مأمورم که از گردن‌نهندگان باشم.»'),
(1437, 10, 73, 'پس او را تکذیب کردند. آنگاه وی را با کسانی که در کشتی همراه او بودند نجات دادیم، و آنان را جانشین [تبهکاران‌] ساختیم، و کسانی را که آیات ما را تکذیب کردند غرق کردیم. پس بنگر که فرجام بیم‌داده‌شدگان چگونه بود.'),
(1438, 10, 74, 'آنگاه، پس از وی رسولانی را به سوی قومشان برانگیختیم، و آنان دلایل آشکار برایشان آوردند، ولی ایشان بر آن نبودند که به چیزی که قبلاً آن را دروغ شمرده بودند ایمان بیاورند. این گونه ما بر دلهای تجاوزکاران مُهر می‌نهیم.'),
(1439, 10, 75, 'سپس، بعد از آنان موسی و هارون را با آیات خود، به سوی فرعون و سران [قوم‌] وی فرستادیم، و[لی آنان‌] گردنکشی کردند و گروهی تبهکار بودند.'),
(1440, 10, 76, 'پس چون حق از نزد ما به سویشان آمد، گفتند: «قطعاً این سحری آشکار است.»'),
(1441, 10, 77, 'موسی گفت: «آیا وقتی حق به سوی شما آمد، می‌گویید: [این سحر است؟] آیا این سحر است؟ و حال آنکه جادوگران رستگار نمی‌شوند.»'),
(1442, 10, 78, 'گفتند: «آیا به سوی ما آمده‌ای تا ما را از شیوه‌ای که پدرانمان را بر آن یافته‌ایم بازگردانی، و بزرگی در این سرزمین برای شما دو تن باشد؟ ما به شما دو تن ایمان نداریم.»'),
(1443, 10, 79, 'و فرعون گفت: «هر جادوگر دانایی را پیش من آورید.»'),
(1444, 10, 80, 'و چون جادوگران آمدند، موسی به آنان گفت: «آنچه را می‌اندازید بیندازید.»'),
(1445, 10, 81, 'پس چون افکندند، موسی گفت: «آنچه را شما به میان آوردید سحر است. به زودی خدا آن را باطل خواهد کرد. آری، خدا کار مفسدان را تأیید نمی‌کند.»'),
(1446, 10, 82, 'و خدا با کلمات خود، حق را ثابت می‌گرداند، هر چند بزهکاران را خوش نیاید.'),
(1447, 10, 83, 'سرانجام، کسی به موسی ایمان نیاورد مگر فرزندانی از قوم وی، در حالی که بیم داشتند از آنکه مبادا فرعون و سران آنها ایشان را آزار رسانند، و در حقیقت، فرعون در آن سرزمین برتری‌جوی و از اسرافکاران بود.'),
(1448, 10, 84, 'و موسی گفت: «ای قوم من، اگر به خدا ایمان آورده‌اید، و اگر اهل تسلیمید بر او توکّل کنید.»'),
(1449, 10, 85, 'پس گفتند: «بر خدا توکّل کردیم. پروردگارا، ما را برای قوم ستمگر [وسیله‌] آزمایش قرار مده.»'),
(1450, 10, 86, '«و ما را به رحمت خویش، از گروه کافران نجات ده.»'),
(1451, 10, 87, 'و به موسی و برادرش وحی کردیم که شما دو تن برای قوم خود در مصر خانه‌هایی ترتیب دهید و سراهایتان را رو به روی هم قرار دهید و نماز برپا دارید و مؤمنان را مژده ده.'),
(1452, 10, 88, 'و موسی گفت: «پروردگارا، تو به فرعون و اشرافش در زندگی دنیا زیور و اموال داده‌ای، پروردگارا، تا [خلق را] از راه تو گمراه کنند، پروردگارا، اموالشان را نابود کن و آنان را دل‌سخت گردان که ایمان نیاورند تا عذاب دردناک را ببینند.»'),
(1453, 10, 89, 'فرمود: «دعای هر دوی شما پذیرفته شد. پس ایستادگی کنید و راه کسانی را که نمی‌دانند پیروی مکنید.»'),
(1454, 10, 90, 'و فرزندان اسرائیل را از دریا گذراندیم، پس فرعون و سپاهیانش از روی ستم و تجاوز، آنان را دنبال کردند، تا وقتی که در شُرف غرق شدن قرار گرفت، گفت: «ایمان آوردم که هیچ معبودی جز آنکه فرزندان اسرائیل به او گرویده‌اند، نیست، و من از تسلیم‌شدگانم.»'),
(1455, 10, 91, 'اکنون؟ در حالی که پیش از این نافرمانی می‌کردی و از تباهکاران بودی؟'),
(1456, 10, 92, 'پس امروز تو را با زره [زرین‌] خودت به بلندی [ساحل‌] می‌افکنیم، تا برای کسانی که از پی تو می‌آیند عبرتی باشد، و بی‌گمان، بسیاری از مردم از نشانه‌های ما غافلند.'),
(1457, 10, 93, 'به راستی ما فرزندان اسرائیل را در جایگاه‌[های‌] نیکو منزل دادیم، و از چیزهای پاکیزه به آنان روزی بخشیدیم. پس به اختلاف نپرداختند مگر پس از آنکه علم برای آنان حاصل شد. همانا پروردگار تو در روز قیامت در باره آنچه بر سر آن اختلاف می‌کردند میانشان داوری خواهد کرد.'),
(1458, 10, 94, 'و اگر از آنچه به سوی تو نازل کرده‌ایم در تردیدی، از کسانی که پیش از تو کتاب [آسمانی‌] می‌خواندند بپرس. قطعاً حق از جانب پروردگارت به سوی تو آمده است. پس زنهار از تردیدکنندگان مباش.'),
(1459, 10, 95, 'و از کسانی که آیات ما را دروغ پنداشتند مباش، که از زیانکاران خواهی بود.'),
(1460, 10, 96, 'در حقیقت، کسانی که سخن پروردگارت بر آنان تحقّق یافته ایمان نمی‌آورند،'),
(1461, 10, 97, 'هر چند هر گونه آیتی برایشان بیاید، تا وقتی که عذاب دردناک را ببینند.'),
(1462, 10, 98, 'چرا هیچ شهری نبود که [اهل آن‌] ایمان بیاورد و ایمانش به حال آن سود بخشد؟ مگر قوم یونس که وقتی [در آخرین لحظه‌] ایمان آوردند، عذاب رسوایی را در زندگی دنیا از آنان برطرف کردیم، و تا چندی آنان را برخوردار ساختیم.'),
(1463, 10, 99, 'و اگر پروردگار تو می‌خواست، قطعاً هر که در زمین است همه آنها یکسر ایمان می‌آوردند. پس آیا تو مردم را ناگزیر می‌کنی که بگروند؟'),
(1464, 10, 100, 'و هیچ کس را نرسد که جز به اذن خدا ایمان بیاورد، و [خدا] بر کسانی که نمی اندیشند، پلیدی را قرار می‌دهد.'),
(1465, 10, 101, 'بگو: «بنگرید که در آسمانها و زمین چیست؟» و[لی‌] نشانه‌ها و هشدارها، گروهی را که ایمان نمی‌آورند سود نمی‌بخشد.'),
(1466, 10, 102, 'پس آیا جز مانند روزهای کسانی را که پیش از آنان درگذشتند، انتظار می‌برند؟ بگو: «انتظار برید که من [نیز] با شما از منتظرانم.»'),
(1467, 10, 103, 'سپس فرستادگان خود و کسانی را که گرویدند می‌رهانیم، زیرا بر ما فریضه است که مؤمنان را نجات دهیم.'),
(1468, 10, 104, 'بگو: «ای مردم، اگر در دین من تردید دارید، پس [بدانید که من‌] کسانی را که به جای خدا می‌پرستید نمی‌پرستم، بلکه خدایی را می‌پرستم که جان شما را می‌ستاند، و دستور یافته‌ام که از مؤمنان باشم.»'),
(1469, 10, 105, 'و [به من دستور داده شده است‌] که به دین حنیف روی آور، و زنهار از مشرکان مباش.'),
(1470, 10, 106, 'و به جای خدا، چیزی را که سود و زیانی به تو نمی‌رساند، مخوان؛ که اگر چنین کنی، در آن صورت قطعاً از جمله ستمکارانی.'),
(1471, 10, 107, 'و اگر خدا به تو زیانی برساند، آن را برطرف‌کننده‌ای جز او نیست، و اگر برای تو خیری بخواهد، بخشش او را ردکننده‌ای نیست. آن را به هر کس از بندگانش که بخواهد می‌رساند، و او آمرزنده مهربان است.'),
(1472, 10, 108, 'بگو: «ای مردم، حق از جانب پروردگارتان برای شما آمده است. پس هر که هدایت یابد به سود خویش هدایت می‌یابد، و هر که گمراه گردد به زیان خود گمراه می‌شود، و من بر شما نگهبان نیستم.»'),
(1473, 10, 109, 'و از آنچه بر تو وحی می‌شود پیروی کن و شکیبا باش تا خدا [میان تو و آنان‌] داوری کند، و او بهترین داوران است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف، لام، راء. کتابی است که آیات آن استحکام یافته، سپس از جانب حکیمی آگاه، به روشنی بیان شده است:'),
(1475, 11, 2, 'که جز خدا را نپرستید. به راستی من از جانب او برای شما هشداردهنده و بشارتگرم،'),
(1476, 11, 3, 'و اینکه از پروردگارتان آمرزش بخواهید، سپس به درگاه او توبه کنید، [تا اینکه‌] شما را با بهره‌مندی نیکویی تا زمانی معیّن بهره‌مند سازد، و به هر شایسته نعمتی از کَرَم خود عطا کند، و اگر رویگردان شوید من از عذاب روزی بزرگ بر شما بیمناکم.'),
(1477, 11, 4, 'بازگشت شما به سوی خداست، و او بر هر چیزی تواناست.'),
(1478, 11, 5, 'آگاه باشید که آنان دل می‌گردانند [و می‌کوشند] تا [راز خود را] از او نهفته دارند. آگاه باشید آنگاه که آنان جامه‌هایشان را بر سر می‌کشند [خدا] آنچه را نهفته و آنچه را آشکار می‌دارند، می‌داند، زیرا او به اسرار سینه‌ها داناست.'),
(1479, 11, 6, 'و هیچ جنبنده‌ای در زمین نیست مگر [اینکه‌] روزیش بر عهده خداست، و [او] قرارگاه و محل مُردنش را می‌داند. همه [اینها] در کتابی روشن [ثبت‌] است.'),
(1480, 11, 7, 'و اوست کسی که آسمانها و زمین را در شش هنگام آفرید و عرش او بر آب بود، تا شما را بیازماید که کدام‌یک نیکوکارترید! و اگر بگویی: «شما پس از مرگ برانگیخته خواهید شد» قطعاً کسانی که کافر شده‌اند خواهند گفت: «این [ادّعا] جز سحری آشکار نیست.»'),
(1481, 11, 8, 'و اگر عذاب را تا چندگاهی از آنان به تأخیر افکنیم، حتماً خواهند گفت: «چه چیز آن را باز می‌دارد؟» آگاه باش، روزی که [عذاب‌] به آنان برسد از ایشان بازگشتنی نیست، و آنچه را که مسخره می‌کردند آنان را فرو خواهد گرفت.'),
(1482, 11, 9, 'و اگر از جانب خود رحمتی به انسان بچشانیم، سپس آن را از وی سلب کنیم، قطعاً نومید و ناسپاس خواهد بود.'),
(1483, 11, 10, 'و اگر -پس از محنتی که به او رسیده- نعمتی به او بچشانیم حتماً خواهد گفت: «گرفتاریها از من دور شد!» بی‌گمان، او شادمان و فخرفروش است.'),
(1484, 11, 11, 'مگر کسانی که شکیبایی ورزیده و کارهای شایسته کرده‌اند [که‌] برای آنان آمرزش و پاداشی بزرگ خواهد بود.'),
(1485, 11, 12, 'و مبادا تو برخی از آنچه را که به سویت وحی می‌شود ترک گویی و سینه‌ات بدان تنگ گردد که می‌گویند: «چرا گنجی بر او فرو فرستاده نشده یا فرشته‌ای با او نیامده است؟» تو فقط هشداردهنده‌ای، و خدا بر هر چیزی نگهبان است.'),
(1486, 11, 13, 'یا می‌گویند: «این [قرآن‌] را به دروغ ساخته است.» بگو: «اگر راست می‌گویید، ده سوره برساخته‌شده مانند آن بیاورید و غیر از خدا هر که را می‌توانید فرا خوانید.»'),
(1487, 11, 14, 'پس اگر شما را اجابت نکردند، بدانید که آنچه نازل شده است به علم خداست، و اینکه معبودی جز او نیست. پس آیا شما گردن می‌نهید؟'),
(1488, 11, 15, 'کسانی که زندگی دنیا و زیور آن را بخواهند [جزای‌] کارهایشان را در آنجا به طور کامل به آنان می‌دهیم، و به آنان در آنجا کم داده نخواهد شد.'),
(1489, 11, 16, 'اینان کسانی هستند که در آخرت جز آتش برایشان نخواهد بود، و آنچه در آنجا کرده‌اند به هدر رفته، و آنچه انجام می‌داده‌اند باطل گردیده است.'),
(1490, 11, 17, 'آیا کسی که از جانب پروردگارش بر حجتی روشن است و شاهدی از [خویشان‌] او، پیرو آن است، و پیش از وی [نیز] کتاب موسی راهبر و مایه رحمت بوده است [دروغ می‌بافد]؟ آنان [که در جستجوی حقیقت‌اند] به آن می‌گروند، و هر کس از گروه‌های [مخالف‌] به آن کفر ورزد آتش وعده‌گاه اوست. پس در آن تردید مکن که آن حقّ است [و] از جانب پروردگارت [آمده است‌] ولی بیشتر مردم باور نمی‌کنند.'),
(1491, 11, 18, 'و چه کسی ستمکارتر از آن کس است که بر خدا دروغ بندد؟ آنان بر پروردگارشان دروغ عرضه می‌شوند، و گواهان خواهند گفت: «اینان بودند که بر پروردگارشان دروغ بستند. هان! لعنت خدا بر ستمگران باد.»'),
(1492, 11, 19, 'همانان که [مردم را] از راه خدا باز می‌دارند و آن را کج می‌شمارند و خود، آخرت را باور ندارند.'),
(1493, 11, 20, 'آنان در زمین درمانده‌کنندگان [خدا] نیستند، و جز خدا دوستانی برای آنان نیست. عذاب برای آنان دو چندان می‌شود. آنان توان شنیدن [حق را] نداشتند و [حق را] نمی‌دیدند.'),
(1494, 11, 21, 'اینانند که به خویشتن زیان زده و آنچه را به دروغ برساخته بودند از دست داده‌اند.'),
(1495, 11, 22, 'شک نیست که آنان در آخرت زیانکارترند.'),
(1496, 11, 23, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته کرده و [با فروتنی‌] به سوی پروردگارشان آرام یافتند، آنان اهل بهشتند و در آن جاودانه خواهند بود.'),
(1497, 11, 24, 'مَثَلِ این دو گروه، چون نابینا و کر [در مقایسه‌] با بینا و شنواست. آیا در مَثَل یکسانند؟ پس آیا پند نمی‌گیرید؟'),
(1498, 11, 25, 'و به راستی نوح را به سوی قومش فرستادیم. [گفت:] من برای شما هشداردهنده‌ای آشکارم،'),
(1499, 11, 26, 'که جز خدا را نپرستید، زیرا من از عذاب روزی سهمگین بر شما بیمناکم.'),
(1500, 11, 27, 'پس، سران قومش که کافر بودند، گفتند: «ما تو را جز بشری مثل خود نمی‌بینیم، و جز [جماعتی از] فرومایگانِ ما، آن هم نسنجیده، نمی‌بینیم کسی تو را پیروی کرده باشد، و شما را بر ما امتیازی نیست، بلکه شما را دروغگو می‌دانیم.»'),
(1501, 11, 28, 'گفت: «ای قوم من، به من بگویید، اگر از طرف پروردگارم حجتی روشن داشته باشم، و مرا از نزد خود رحمتی بخشیده باشد که بر شما پوشیده است، آیا ما [باید] شما را در حالی که بدان اکراه دارید، به آن وادار کنیم؟»'),
(1502, 11, 29, '«و ای قوم من، بر این [رسالت،] مالی از شما درخواست نمی‌کنم. مُزد من جز بر عهده خدا نیست. و کسانی را که ایمان آورده‌اند طرد نمی‌کنم. قطعاً آنان پروردگارشان را دیدار خواهند کرد، ولی شما را قومی می‌بینم که نادانی می‌کنید.»'),
(1503, 11, 30, 'و ای قوم من! اگر آنان را برانم، چه کسی مرا در برابر خدا یاری خواهد کرد؟ آیا عبرت نمی‌گیرید؟'),
(1504, 11, 31, '«و به شما نمی‌گویم که گنجینه‌های خدا پیش من است، و غیب نمی‌دانم، و نمی‌گویم که من فرشته‌ام، و در باره کسانی که دیدگان شما به خواری در آنان می‌نگرد، نمی‌گویم خدا هرگز خیرشان نمی‌دهد. خدا به آنچه در دل آنان است آگاه‌تر است. [اگر جز این بگویم‌] من در آن صورت از ستمکاران خواهم بود.»'),
(1505, 11, 32, 'گفتند: «ای نوح، واقعاً با ما جدال کردی و بسیار [هم‌] جدال کردی. پس اگر از راستگویانی آنچه را [از عذاب خدا] به ما وعده می‌دهی برای ما بیاور.»'),
(1506, 11, 33, 'گفت: «تنها خداست که اگر بخواهد، آن را برای شما می‌آورد و شما عاجز کننده [او] نخواهید بود.»'),
(1507, 11, 34, 'و اگر بخواهم شما را اندرز دهم، در صورتی که خدا بخواهد شما را بیراه گذارد، اندرز من شما را سودی نمی‌بخشد. او پروردگار شماست و به سوی او باز گردانیده می‌شوید.'),
(1508, 11, 35, 'یا [در باره قرآن‌] می‌گویند: «آن را بربافته است.» بگو: «اگر آن را به دروغ سر هم کرده‌ام، گناه من بر عهده خود من است، و[لی‌] من از جُرمی که به من نسبت می‌دهید برکنارم.»'),
(1509, 11, 36, 'و به نوح وحی شد که: «از قوم تو، جز کسانی که [تاکنون‌] ایمان آورده‌اند هرگز [کسی‌] ایمان نخواهد آورد. پس، از آنچه می‌کردند غمگین مباش.»'),
(1510, 11, 37, '«و زیر نظر ما و [به‌] وحی ما کِشتی را بساز، و در باره کسانی که ستم کرده‌اند با من سخن مگوی، چرا که آنان غرق شدنی‌اند.»'),
(1511, 11, 38, 'و [نوح‌] کشتی را می‌ساخت، و هر بار که اشرافی از قومش بر او می‌گذشتند، او را مسخره می‌کردند. می‌گفت: «اگر ما را مسخره می‌کنید، ما [نیز] شما را همان گونه که مسخره می‌کنید، مسخره خواهیم کرد.»'),
(1512, 11, 39, 'به زودی خواهید دانست چه کسی را عذابی خوارکننده درمی‌رسد و بر او عذابی پایدار فرود می‌آید؟'),
(1513, 11, 40, 'تا آنگاه که فرمان ما دررسید و تنور فوران کرد، فرمودیم: «در آن [کشتی‌] از هر حیوانی یک جفت، با کسانت -مگر کسی که قبلا در باره او سخن رفته است- و کسانی که ایمان آورده‌اند، حمل کن.» و با او جز [عدّه‌] اندکی ایمان نیاورده بودند!'),
(1514, 11, 41, 'و [نوح‌] گفت: «در آن سوار شوید. به نام خداست روان‌شدنش و لنگرانداختنش، بی گمان پروردگار من آمرزنده مهربان است.»'),
(1515, 11, 42, 'و آن [کِشتی‌] ایشان را در میان موجی کوه‌آسا می‌بُرد، و نوح پسرش را که در کناری بود بانگ درداد: «ای پسرک من، با ما سوار شو و با کافران مباش.»'),
(1516, 11, 43, 'گفت: «به زودی به کوهی پناه می‌جویم که مرا از آب در امان نگاه می‌دارد.» گفت: «امروز در برابر فرمان خدا هیچ نگاهدارنده‌ای نیست، مگر کسی که [خدا بر او] رحم کند.» و موج میان آن دو حایل شد و [پسر] از غرق‌شدگان گردید.'),
(1517, 11, 44, 'و گفته شد: «ای زمین، آب خود را فرو بر، و ای آسمان، [از باران‌] خودداری کن. و آب فرو کاست و فرمان گزارده شده و [کشتی‌] بر جودی قرار گرفت.» و گفته شد: «مرگ بر قوم ستمکار.»'),
(1518, 11, 45, 'و نوح پروردگار خود را آواز داد و گفت: «پروردگارا، پسرم از کسان من است، و قطعاً وعده تو راست است و تو بهترین داورانی.»'),
(1519, 11, 46, 'فرمود: «ای نوح، او در حقیقت از کسان تو نیست، او [دارای‌] کرداری ناشایسته است. پس چیزی را که بدان علم نداری از من مخواه. من به تو اندرز می‌دهم که مبادا از نادانان باشی.»'),
(1520, 11, 47, 'گفت: «پروردگارا، من به تو پناه می‌برم که از تو چیزی بخواهم که بدان علم ندارم، و اگر مرا نیامرزی و به من رحم نکنی از زیانکاران باشم.»'),
(1521, 11, 48, 'گفته شد: «ای نوح، با درودی از ما و برکتهایی بر تو و بر گروههایی که با تواند، فرود آی. و گروههایی هستند که به زودی برخوردارشان می‌کنیم، سپس از جانب ما عذابی دردناک به آنان می‌رسد.»'),
(1522, 11, 49, 'این از خبرهای غیب است که آن را به تو وحی می‌کنیم. پیش از این نه تو آن را می‌دانستی و نه قوم تو. پس شکیبا باش که فرجام [نیک‌] از آنِ تقواپیشگان است.'),
(1523, 11, 50, 'و به سوی [قوم‌] عاد، برادرشان هود را [فرستادیم. هود] گفت: «ای قوم من، خدا را بپرستید. جز او هیچ معبودی برای شما نیست. شما فقط دروغ پردازید.»'),
(1524, 11, 51, '«ای قوم من، برای این [رسالت‌] پاداشی از شما درخواست نمی‌کنم. پاداش من جز بر عهده کسی که مرا آفریده است، نیست. پس آیا نمی‌اندیشید؟»'),
(1525, 11, 52, '«و ای قوم من، از پروردگارتان آمرزش بخواهید، سپس به درگاه او توبه کنید [تا] از آسمان بر شما بارش فراوان فرستد و نیرویی بر نیروی شما بیفزاید، و تبهکارانه روی بر مگردانید.»'),
(1526, 11, 53, 'گفتند: «ای هود، برای ما دلیل روشنی نیاوردی، و ما برای سخن تو دست از خدایان خود برنمی‌داریم و تو را باور نداریم.»'),
(1527, 11, 54, '«[چیزی‌] جز این نمی‌گوییم که بعضی از خدایان ما به تو آسیبی رسانده‌اند.» گفت: «من خدا را گواه می‌گیرم، و شاهد باشید که من از آنچه جز او شریک وی می‌گیرید بیزارم.'),
(1528, 11, 55, 'پس، همه شما در کار من نیرنگ کنید و مرا مهلت مدهید.'),
(1529, 11, 56, 'در حقیقت، من بر خدا، پروردگار خودم و پروردگار شما توکّل کردم. هیچ جنبنده‌ای نیست مگر اینکه او مهار هستی‌اش را در دست دارد. به راستی پروردگار من بر راه راست است.'),
(1530, 11, 57, 'پس اگر روی بگردانید، به یقین، آنچه را که به منظور آن به سوی شما فرستاده شده بودم به شما رسانیدم، و پروردگارم قومی جز شما را جانشین [شما] خواهد کرد. و به او هیچ زیانی نمی‌رسانید. در حقیقت، پروردگارم بر هر چیزی نگاهبان است.'),
(1531, 11, 58, 'و چون فرمان ما دررسید، هود و کسانی را که با او گرویده بودند، به رحمتی از جانب خود نجات بخشیدیم و آنان را از عذابی سخت رهانیدیم.'),
(1532, 11, 59, 'و این، [قوم‌] عاد بود که آیات پروردگارشان را انکار کردند، و فرستادگانش را نافرمانی نمودند، و به دنبال فرمانِ هر زورگوی ستیزه‌جوی رفتند.'),
(1533, 11, 60, 'و [سرانجام‌] در این دنیا و روز قیامت، لعنت بدرقه [راه‌] آنان گردید. آگاه باشید که عادیان به پروردگارشان کفر ورزیدند. هان، مرگ بر عادیان: قوم هود.'),
(1534, 11, 61, 'و به سوی [قوم‌] ثمود، برادرشان صالح را [فرستادیم‌]. گفت: «ای قوم من! خدا را بپرستید. برای شما هیچ معبودی جز او نیست. او شما را از زمین پدید آورد و در آن شما را استقرار داد. پس، از او آمرزش بخواهید، آنگاه به درگاه او توبه کنید، که پروردگارم نزدیک [و] اجابت‌کننده است.»'),
(1535, 11, 62, 'گفتند: «ای صالح، به راستی تو پیش از این، میان ما مایه امید بودی. آیا ما را از پرستش آنچه پدرانمان می‌پرستیدند باز می‌داری؟ و بی‌گمان، ما از آنچه تو ما را بدان می‌خوانی سخت دچار شکّیم.»'),
(1536, 11, 63, 'گفت: «ای قوم من، چه بینید، اگر [در این دعوا] بر حجتی روشن از پروردگار خود باشم و از جانب خود رحمتی به من داده باشد، پس اگر او را نافرمانی کنم چه کسی در برابر خدا مرا یاری می‌کند؟ در نتیجه، شما جز بر زیان من نمی‌افزایید.»'),
(1537, 11, 64, '«و ای قوم من، این ماده‌شتر خداست که برای شما پدیده‌ای شگرف است. پس بگذارید او در زمین خدا بخورد و آسیبش مرسانید که شما را عذابی زودرس فرو می‌گیرد. «'),
(1538, 11, 65, 'پس آن [ماده‌شتر] را پی کردند، و [صالح‌] گفت: «سه روز در خانه‌هایتان برخوردار شوید. این وعده‌ای بی‌دروغ است.»'),
(1539, 11, 66, 'پس چون فرمان ما در رسید، صالح و کسانی را که با او ایمان آورده بودند، به رحمت خود رهانیدیم و از رسوایی آن روز [نجات دادیم‌]. به یقین، پروردگار تو همان نیرومند شکست‌ناپذیر است.'),
(1540, 11, 67, 'و کسانی را که ستم ورزیده بودند، آن بانگِ [مرگبار] فرا گرفت، و در خانه‌هایشان از پا درآمدند.'),
(1541, 11, 68, 'گویا هرگز در آن [خانه‌ها] نبوده‌اند. آگاه باشید که ثمودیان به پروردگارشان کفر ورزیدند. هان، مرگ بر ثمود.'),
(1542, 11, 69, 'و به راستی، فرستادگان ما برای ابراهیم مژده آوردند، سلام گفتند، پاسخ داد: «سلام». و دیری نپایید که گوساله‌ای بریان آورد.'),
(1543, 11, 70, 'و چون دید دستهایشان به غذا دراز نمی‌شود، آنان را ناشناس یافت و از ایشان ترسی بر دل گرفت. گفتند: «مترس، ما به سوی قوم لوط فرستاده شده‌ایم.»'),
(1544, 11, 71, 'و زن او ایستاده بود. خندید. پس وی را به اسحاق و از پی اسحاق به یعقوب مژده دادیم.'),
(1545, 11, 72, '[همسر ابراهیم‌] گفت: «ای وای بر من، آیا فرزند آورم با آنکه من پیرزنم، و این شوهرم پیرمرد است؟ واقعاً این چیزِ بسیار عجیبی است.»'),
(1546, 11, 73, 'گفتند: «آیا از کار خدا تعجّب می‌کنی؟ رحمت خدا و برکات او بر شما خاندان [رسالت‌] باد. بی‌گمان، او ستوده‌ای بزرگوار است.»'),
(1547, 11, 74, 'پس وقتی ترس ابراهیم زایل شد و مژده [فرزنددار شدن‌] به او رسید، در باره قوم لوط با ما [به قصد شفاعت‌] چون و چرا می‌کرد.'),
(1548, 11, 75, 'زیرا ابراهیم، بردبار و نرمدل و بازگشت‌کننده [به سوی خدا] بود.'),
(1549, 11, 76, 'ای ابراهیم! از این [چون و چرا] روی برتاب، که فرمان پروردگارت آمده و برای آنان عذابی که بی‌بازگشت است خواهد آمد.'),
(1550, 11, 77, 'و چون فرستادگان ما نزد لوط آمدند، به [آمدنِ‌] آنان ناراحت، و دستش از حمایت ایشان کوتاه شد و گفت: «امروز، روزی سخت است.»'),
(1551, 11, 78, 'و قوم او شتابان به سویش آمدند، و پیش از آن کارهای زشت می‌کردند. [لوط] گفت: «ای قوم من، اینان دختران منند. آنان برای شما پاکیزه‌ترند. پس از خدا بترسید و مرا در کار مهمانانم رسوا مکنید. آیا در میان شما آدمی عقل‌رس پیدا نمی‌شود؟»'),
(1552, 11, 79, 'گفتند: «تو خوب می‌دانی که ما را به دخترانت حاجتی نیست و تو خوب می‌دانی که ما چه می‌خواهیم.»'),
(1553, 11, 80, '[لوط] گفت: «کاش برای مقابله با شما قدرتی داشتم یا به تکیه‌گاهی استوار پناه می‌جستم.»'),
(1554, 11, 81, 'گفتند: «ای لوط، ما فرستادگان پروردگار توییم. آنان هرگز به تو دست نخواهند یافت. پس، پاسی از شب گذشته خانواده‌ات را حرکت ده -و هیچ کس از شما نباید واپس بنگرد- مگر زنت، که آنچه به ایشان رسد به او [نیز] خواهد رسید. بی‌گمان، وعده‌گاه آنان صبح است. مگر صبح نزدیک نیست؟»'),
(1555, 11, 82, 'پس چون فرمان ما آمد، آن [شهر] را زیر و زبر کردیم و سنگ‌پاره‌هایی از [نوع‌] سنگ گلهای لایه لایه، بر آن فرو ریختیم.'),
(1556, 11, 83, '[سنگهایی‌] که نزد پروردگارت نشان‌زده بود. و [خرابه‌های‌] آن از ستمگران چندان دور نیست.'),
(1557, 11, 84, 'و به سوی [اهل‌] مَدْیَن، برادرشان شعیب را [فرستادیم‌]. گفت: «ای قوم من، خدا را بپرستید. برای شما جز او معبودی نیست. و پیمانه و ترازو را کم مکنید. به راستی شما را در نعمت می‌بینم. و[لی‌] از عذاب روزی فراگیر بر شما بیمناکم.»'),
(1558, 11, 85, '«و ای قوم من، پیمانه و ترازو را به داد، تمام دهید، و حقوق مردم را کم مدهید، و در زمین به فساد سر برمدارید.»'),
(1559, 11, 86, '«اگر مؤمن باشید، باقیمانده [حلال‌] خدا برای شما بهتر است، و من بر شما نگاهبان نیستم.»'),
(1560, 11, 87, 'گفتند: «ای شعیب، آیا نماز تو به تو دستور می‌دهد که آنچه را پدران ما می‌پرستیده‌اند رها کنیم، یا در اموال خود به میل خود تصرّف نکنیم؟ راستی که تو بردبار فرزانه‌ای.»'),
(1561, 11, 88, 'گفت: «ای قوم من، بیندیشید، اگر از جانب پروردگارم دلیل روشنی داشته باشم، و او از سوی خود روزی نیکویی به من داده باشد [آیا باز هم از پرستش او دست بردارم؟] من نمی‌خواهم در آنچه شما را از آن باز می‌دارم با شما مخالفت کنم [و خود مرتکب آن شوم‌]. من قصدی جز اصلاح [جامعه‌] تا آنجا که بتوانم، ندارم، و توفیق من جز به [یاری‌] خدا نیست. بر او توکّل کرده‌ام و به سوی او بازمی‌گردم.»'),
(1562, 11, 89, '«و ای قوم من، زنهار تا مخالفت شما با من، شما را بدانجا نکشاند که [بلایی‌] مانند آنچه به قوم نوح یا قوم هود یا قوم صالح رسید، به شما [نیز] برسد، و قوم لوط از شما چندان دور نیست.»'),
(1563, 11, 90, '«و از پروردگار خود آمرزش بخواهید، سپس به درگاه او توبه کنید که پروردگار من مهربان و دوستدار [بندگان‌] است.»'),
(1564, 11, 91, 'گفتند: «ای شعیب! بسیاری از آنچه را که می‌گویی نمی‌فهمیم، و واقعاً تو را در میان خود ضعیف می‌بینیم، و اگر عشیره تو نبود قطعاً سنگسارت می‌کردیم، و تو بر ما پیروز نیستی!»'),
(1565, 11, 92, 'گفت: «ای قوم من، آیا عشیره من پیشِ شما از خدا عزیزتر است که او را پشت سر خود گرفته‌اید [و فراموشش کرده‌اید]؟ در حقیقت، پروردگار من به آنچه انجام می‌دهید احاطه دارد.»'),
(1566, 11, 93, '«و ای قوم من، شما بر حسب امکانات خود عمل کنید، من [نیز] عمل می‌کنم. به زودی خواهید دانست که عذاب رسواکننده بر چه کسی فرود می‌آید و دروغگو کیست؛ و انتظار برید که من [هم‌] با شما منتظرم.»'),
(1567, 11, 94, 'و چون فرمان ما آمد، شعیب و کسانی را که با او ایمان آورده بودند، به رحمتی از جانب خویش نجات دادیم، و کسانی را که ستم کرده بودند، فریاد [مرگبار] فرو گرفت، و در خانه‌هایشان از پا درآمدند.'),
(1568, 11, 95, 'گویی در آن [خانه‌ها] هرگز اقامت نداشته‌اند. هان، مرگ بر [مردم‌] مَدْیَن، همان گونه که ثمود هلاک شدند.'),
(1569, 11, 96, 'و به راستی، موسی را با آیات خود و حجّتی آشکار،'),
(1570, 11, 97, 'به سوی فرعون و سران [قوم‌] وی فرستادیم، ولی [سران‌] از فرمان فرعون پیروی کردند، و فرمان فرعون صواب نبود.'),
(1571, 11, 98, 'روز قیامت پیشاپیش قومش می‌رود و آنان را به آتش درمی‌آورد، و [دوزخ‌] چه ورودگاه بدی برای واردان است.'),
(1572, 11, 99, 'و در این دنیا و روز قیامت به لعنت بدرقه شدند، و چه بد عطایی نصیب آنان می‌شود.'),
(1573, 11, 100, 'این، از خبرهای آن شهرهاست که آن را بر تو حکایت می‌کنیم. بعضی از آنها [هنوز] بر سرِ پا هستند و [بعضی‌] بر باد رفته‌اند.'),
(1574, 11, 101, 'و ما به آنان ستم نکردیم، ولی آنان به خودشان ستم کردند. پس چون فرمان پروردگارت آمد، خدایانی که به جای خدا[ی حقیقی‌] می‌خواندند هیچ به کارشان نیامد، و جز بر هلاکت آنان نیفزود.'),
(1575, 11, 102, 'و این گونه بود [به قهر] گرفتن پروردگارت، وقتی شهرها را در حالی که ستمگر بودند [به قهر] می‌گرفت. آری [به قهر] گرفتن او دردناک و سخت است.'),
(1576, 11, 103, 'قطعاً در این [یادآوریها] برای کسی که از عذاب آخرت می‌ترسد عبرتی است. آن [روز] روزی است که مردم را برای آن گرد می‌آورند، و آن [روز] روزی است که [جملگی در آن‌] حاضر می‌شوند.'),
(1577, 11, 104, 'و ما آن را جز تا زمان معیّنی به تأخیر نمی‌افکنیم.'),
(1578, 11, 105, 'روزی [است‌] که چون فرا رسد هیچ کس جز به اذن وی سخن نگوید. آنگاه بعضی از آنان تیره‌بختند و [برخی‌] نیکبخت.'),
(1579, 11, 106, 'و اما کسانی که تیره‌بخت شده‌اند، در آتش، فریاد و ناله‌ای دارند.'),
(1580, 11, 107, 'تا آسمانها و زمین برجاست، در آن ماندگار خواهند بود، مگر آنچه پروردگارت بخواهد، زیرا پروردگار تو همان کُند که خواهد.'),
(1581, 11, 108, 'و اما کسانی که نیکبخت شده‌اند، تا آسمانها و زمین برجاست، در بهشت جاودانند، مگر آنچه پروردگارت بخواهد. [که این‌] بخششی است که بریدنی نیست.'),
(1582, 11, 109, 'پس در باره آنچه آنان [=مشرکان‌] می‌پرستند در تردید مباش. آنان جز همان گونه که قبلا پدرانشان می‌پرستیدند، نمی‌پرستند. و ما بهره ایشان را تمام و ناکاسته خواهیم داد.'),
(1583, 11, 110, 'و به حقیقت، ما به موسی کتاب [آسمانی‌] دادیم، پس در مورد آن اختلاف شد، و اگر از جانب پروردگارت وعده‌ای پیشی نگرفته بود، قطعاً میان آنها داوری شده بود، و بی گمان، آنان در باره آن در شکی بهتان‌آمیزند.'),
(1584, 11, 111, 'و قطعاً پروردگارت [نتیجه‌] اعمال هر یک را به تمام [و کمال‌] به آنان خواهد داد، چرا که او به آنچه انجام می‌دهند آگاه است.'),
(1585, 11, 112, 'پس، همان گونه که دستور یافته‌ای ایستادگی کن، و هر که با تو توبه کرده [نیز چنین کند]، و طغیان مکنید که او به آنچه انجام می‌دهید بیناست.'),
(1586, 11, 113, 'و به کسانی که ستم کرده‌اند متمایل مشوید که آتش [دوزخ‌] به شما می‌رسد، و در برابر خدا برای شما دوستانی نخواهد بود، و سرانجام یاری نخواهید شد.'),
(1587, 11, 114, 'و در دو طرف روز [=اول و آخر آن‌] و نخستین ساعات شب نماز را برپا دار، زیرا خوبیها بدیها را از میان می‌برد. این برای پندگیرندگان، پندی است.'),
(1588, 11, 115, 'و شکیبا باش که خدا پاداش نیکوکاران را ضایع نمی‌گرداند.'),
(1589, 11, 116, 'پس چرا از نسلهای پیش از شما خردمندانی نبودند که [مردم را] از فساد در زمین باز دارند؟ جز اندکی از کسانی که از میان آنان نجاتشان دادیم. و کسانی که ستم کردند به دنبال ناز و نعمتی که در آن بودند رفتند، و آنان بزهکار بودند.'),
(1590, 11, 117, 'و پروردگار تو [هرگز] بر آن نبوده است که شهرهایی را که مردمش اصلاحگرند، به ستم هلاک کند.'),
(1591, 11, 118, 'و اگر پروردگار تو می‌خواست، قطعاً همه مردم را امّت واحدی قرار می‌داد، در حالی که پیوسته در اختلافند،'),
(1592, 11, 119, 'مگر کسانی که پروردگار تو به آنان رحم کرده، و برای همین آنان را آفریده است. و وعده پروردگارت [چنین‌] تحقّق پذیرفته است [که:] «البتّه جهنّم را از جنّ و انس یکسره پر خواهم کرد.»'),
(1593, 11, 120, 'و هر یک از سرگذشتهای پیامبران [خود] را که بر تو حکایت می‌کنیم، چیزی است که دلت را بدان استوار می‌گردانیم، و در اینها حقیقت برای تو آمده، و برای مؤمنان اندرز و تذکّری است.'),
(1594, 11, 121, 'و به کسانی که ایمان نمی‌آورند بگو: «بر حسب امکانات خود عمل کنید که ما [هم‌] عمل خواهیم کرد.»'),
(1595, 11, 122, '«و منتظر باشید که ما [نیز] منتظر خواهیم بود.»'),
(1596, 11, 123, 'و نهان آسمانها و زمین از آنِ خداست، و تمام کارها به او بازگردانده می‌شود؛ پس او را پرستش کن و بر او توکّل نمای، و پروردگار تو از آنچه انجام می‌دهید غافل نیست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، راء. این است آیات کتاب روشنگر.'),
(1598, 12, 2, 'ما آن را قرآنی عربی نازل کردیم، باشد که بیندیشید.'),
(1599, 12, 3, 'ما نیکوترین سرگذشت را به موجب این قرآن که به تو وحی کردیم، بر تو حکایت می‌کنیم، و تو قطعاً پیش از آن از بی‌خبران بودی.'),
(1600, 12, 4, '[یاد کن‌] زمانی را که یوسف به پدرش گفت: «ای پدر، من [در خواب‌] یازده ستاره را با خورشید و ماه دیدم. دیدم [آنها] برای من سجده می‌کنند.»'),
(1601, 12, 5, '[یعقوب‌] گفت: «ای پسرک من، خوابت را برای برادرانت حکایت مکن که برای تو نیرنگی می‌اندیشند، زیرا شیطان برای آدمی دشمنی آشکار است.'),
(1602, 12, 6, 'و این چنین، پروردگارت تو را برمی‌گزیند، و از تعبیر خوابها به تو می‌آموزد، و نعمتش را بر تو و بر خاندان یعقوب تمام می‌کند، همان گونه که قبلاً بر پدران تو، ابراهیم و اسحاق، تمام کرد. در حقیقت، پروردگار تو دانای حکیم است.'),
(1603, 12, 7, 'به راستی در [سرگذشتِ‌] یوسف و برادرانش برای پُرسندگان عبرتهاست.'),
(1604, 12, 8, 'هنگامی که [برادران او] گفتند: «یوسف و برادرش نزد پدرمان از ما -که جمعی نیرومند هستیم- دوست‌داشتنی‌ترند. قطعاً پدر ما در گمراهی آشکاری است.»'),
(1605, 12, 9, '[یکی گفت:] «یوسف را بکُشید یا او را به سرزمینی بیندازید، تا توجّه پدرتان معطوف شما گردد، و پس از او مردمی شایسته باشید.»'),
(1606, 12, 10, 'گوینده‌ای از میان آنان گفت: «یوسف را مکُشید. اگر کاری می‌کنید، او را در نهانخانه چاه بیفکنید، تا برخی از مسافران او را برگیرند.»'),
(1607, 12, 11, 'گفتند: «ای پدر، تو را چه شده است که ما را بر یوسف امین نمی‌دانی در حالی که ما خیرخواه او هستیم؟'),
(1608, 12, 12, 'فردا او را با ما بفرست تا [در چمن‌] بگردد و بازی کند، و ما به خوبی نگهبان او خواهیم بود.'),
(1609, 12, 13, 'گفت: «اینکه او را ببَرید سخت مرا اندوهگین می‌کند، و می‌ترسم از او غافل شوید و گرگ او را بخورد.»'),
(1610, 12, 14, 'گفتند: «اگر گرگ او را بخورد با اینکه ما گروهی نیرومند هستیم، در آن صورت ما قطعاً [مردمی‌] بی‌مقدار خواهیم بود.»'),
(1611, 12, 15, 'پس وقتی او را بردند و همداستان شدند تا او را در نهانخانه چاه بگذارند [چنین کردند]. و به او وحی کردیم که قطعاً آنان را از این کارشان -در حالی که نمی‌دانند- با خبر خواهی کرد.'),
(1612, 12, 16, 'و شامگاهان، گریان نزد پدر خود [باز] آمدند.'),
(1613, 12, 17, 'گفتند: «ای پدر، ما رفتیم مسابقه دهیم، و یوسف را پیش کالای خود نهادیم. آنگاه گرگ او را خورد، ولی تو ما را هر چند راستگو باشیم باور نمی‌داری.»'),
(1614, 12, 18, 'و پیراهنش را [آغشته‌] به خونی دروغین آوردند. [یعقوب‌] گفت: «[نه‌] بلکه نَفْس شما کاری [بد] را برای شما آراسته است. اینک صبری نیکو [برای من بهتر است‌]. و بر آنچه توصیف می‌کنید، خدا یاری‌ده است.»'),
(1615, 12, 19, 'و کاروانی آمد. پس آب‌آور خود را فرستادند. و دلوش را انداخت. گفت: «مژده! این یک پسر است!» و او را چون کالایی پنهان داشتند. و خدا به آنچه می‌کردند دانا بود.'),
(1616, 12, 20, 'و او را به بهای ناچیزی -چند درهم- فروختند و در آن بی‌رغبت بودند.'),
(1617, 12, 21, 'و آن کس که او را از مصر خریده بود به همسرش گفت: «نیکش بدار، شاید به حال ما سود بخشد یا او را به فرزندی اختیار کنیم.» و بدین گونه ما یوسف را در آن سرزمین مکانت بخشیدیم تا به او تأویل خوابها را بیاموزیم، و خدا بر کار خویش چیره است ولی بیشتر مردم نمی‌دانند.'),
(1618, 12, 22, 'و چون به حد رشد رسید، او را حکمت و دانش عطا کردیم، و نیکوکاران را چنین پاداش می‌دهیم.'),
(1619, 12, 23, 'و آن [بانو] که وی در خانه‌اش بود خواست از او کام گیرد، و درها را [پیاپی‌] چفت کرد و گفت: «بیا که از آنِ توام!» [یوسف‌] گفت: «پناه بر خدا، او آقای من است. به من جای نیکو داده است. قطعاً ستمکاران رستگار نمی‌شوند.»'),
(1620, 12, 24, 'و در حقیقت [آن زن‌] آهنگ وی کرد، و [یوسف نیز] اگر برهان پروردگارش را ندیده بود، آهنگ او می‌کرد. چنین [کردیم‌] تا بدی و زشتکاری را از او بازگردانیم، چرا که او از بندگان مخلص ما بود.'),
(1621, 12, 25, 'و آن دو به سوی در بر یکدیگر سبقت گرفتند، و [آن زن‌] پیراهن او را از پشت بدرید و در آستانه در آقای آن زن را یافتند. آن گفت: «کیفر کسی که قصد بد به خانواده تو کرده چیست؟ جز اینکه زندانی یا [دچار] عذابی دردناک شود.»'),
(1622, 12, 26, '[یوسف‌] گفت: «او از من کام خواست.» و شاهدی از خانواده آن زن شهادت داد: «اگر پیراهن او از جلو چاک خورده، زن راست گفته و او از دروغگویان است،'),
(1623, 12, 27, 'و اگر پیراهن او از پشت دریده شده، زن دروغ گفته و او از راستگویان است.»'),
(1624, 12, 28, 'پس چون [شوهرش‌] دید پیراهن او از پشت چاک خورده است گفت: «بی‌شک، این از نیرنگ شما [زنان‌] است، که نیرنگ شما [زنان‌] بزرگ است.»'),
(1625, 12, 29, '«ای یوسف، از این [پیشامد] روی بگردان. و تو [ای زن‌] برای گناه خود آمرزش بخواه که تو از خطاکاران بوده‌ای.»'),
(1626, 12, 30, 'و [دسته‌ای از] زنان در شهر گفتند: «زن عزیز از غلام خود، کام خواسته و سخت خاطرخواه او شده است. به راستی ما او را در گمراهی آشکاری می‌بینیم.»'),
(1627, 12, 31, 'پس چون [همسر عزیز] از مکرشان اطلاع یافت، نزد آنان [کسی‌] فرستاد، و محفلی برایشان آماده ساخت، و به هر یک از آنان [میوه و] کاردی داد و [به یوسف‌] گفت: «بر آنان درآی.» پس چون [زنان‌] او را دیدند، وی را بس شگرف یافتند و [از شدت هیجان‌] دستهای خود را بریدند و گفتند: «منزه است خدا، این بشر نیست، این جز فرشته‌ای بزرگوار نیست.»'),
(1628, 12, 32, '[زلیخا] گفت: «این همان است که در باره او سرزنشم می‌کردید. آری، من از او کام خواستم و[لی‌] او خود را نگاه داشت، و اگر آنچه را به او دستور می‌دهم نکند قطعاً زندانی خواهد شد و حتماً از خوارشدگان خواهد گردید.»'),
(1629, 12, 33, '[یوسف‌] گفت: «پروردگارا، زندان برای من دوست‌داشتنی‌تر است از آنچه مرا به آن می‌خوانند، و اگر نیرنگ آنان را از من بازنگردانی، به سوی آنان خواهم گرایید و از [جمله‌] نادانان خواهم شد.»'),
(1630, 12, 34, 'پس، پروردگارش [دعای‌] او را اجابت کرد و نیرنگ آنان را از او بگردانید. آری، او شنوای داناست.'),
(1631, 12, 35, 'آنگاه پس از دیدن آن نشانه‌ها، به نظرشان آمد که او را تا چندی به زندان افکنند.'),
(1632, 12, 36, 'و دو جوان با او به زندان درآمدند. [روزی‌] یکی از آن دو گفت: «من خویشتن را [به خواب‌] دیدم که [انگور برای‌] شراب می‌فشارم»؛ و دیگری گفت: «من خود را [به خواب‌] دیدم که بر روی سرم نان می‌برم و پرندگان از آن می‌خورند. به ما از تعبیرش خبر ده، که ما تو را از نیکوکاران می‌بینیم.»'),
(1633, 12, 37, 'گفت: «غذایی را که روزی شماست برای شما نمی‌آورند مگر آنکه من از تعبیر آن به شما خبر می‌دهم پیش از آنکه [تعبیر آن‌] به شما برسد. این از چیزهایی است که پروردگارم به من آموخته است. من آیین قومی را که به خدا اعتقاد ندارند و منکر آخرتند رها کرده‌ام،'),
(1634, 12, 38, 'و آیین پدرانم، ابراهیم و اسحاق و یعقوب را پیروی نموده‌ام. برای ما سزاوار نیست که چیزی را شریک خدا کنیم. این از عنایت خدا بر ما و بر مردم است، ولی بیشتر مردم سپاسگزاری نمی‌کنند.'),
(1635, 12, 39, 'ای دو رفیق زندانیم، آیا خدایان پراکنده بهترند یا خدای یگانه مقتدر؟'),
(1636, 12, 40, 'شما به جای او جز نامهایی [چند] را نمی‌پرستید که شما و پدرانتان آنها را نامگذاری کرده‌اید، و خدا دلیلی بر [حقانیت‌] آنها نازل نکرده است. فرمان جز برای خدا نیست. دستور داده که جز او را نپرستید. این است دین درست، ولی بیشتر مردم نمی‌دانند.'),
(1637, 12, 41, 'ای دو رفیق زندانیم، اما یکی از شما به آقای خود باده می‌نوشاند، و اما دیگری به دار آویخته می‌شود و پرندگان از [مغز] سرش می‌خورند. امری که شما دو تن از من جویا شدید تحقق یافت.'),
(1638, 12, 42, 'و [یوسف‌] به آن کس از آن دو که گمان می‌کرد خلاص می‌شود، گفت: «مرا نزد آقای خود به یاد آور.» و[لی‌] شیطان، یادآوری به آقایش را از یاد او برد؛ در نتیجه، چند سالی در زندان ماند.'),
(1639, 12, 43, 'و پادشاه [مصر] گفت: «من [در خواب‌] دیدم هفت گاو فربه است که هفت [گاو] لاغر آنها را می‌خورند، و هفت خوشه سبز و [هفت خوشه‌] خشگیده دیگر. ای سران قوم، اگر خواب تعبیر می‌کنید، در باره خواب من، به من نظر دهید.»'),
(1640, 12, 44, 'گفتند: «خوابهایی است پریشان، و ما به تعبیر خوابهای آشفته دانا نیستیم.»'),
(1641, 12, 45, 'و آن کس از آن دو [زندانی‌] که نجات یافته و پس از چندی [یوسف را] به خاطر آورده بود گفت: «مرا به [زندان‌] بفرستید تا شما را از تعبیر آن خبر دهم.»'),
(1642, 12, 46, '«ای یوسف، ای مرد راستگوی، در باره [این خواب که‌] هفت گاو فربه، هفت [گاو] لاغر آنها را می‌خورند، و هفت خوشه سبز و [هفت خوشه‌] خشگیده دیگر؛ به ما نظر ده، تا به سوی مردم برگردم، شاید آنان [تعبیرش را] بدانند.»'),
(1643, 12, 47, 'گفت: «هفت سال پی در پی می‌کارید، و آنچه را درویدید -جز اندکی را که می‌خورید- در خوشه‌اش واگذارید.'),
(1644, 12, 48, 'آنگاه پس از آن، هفت سال سخت می‌آید که آنچه را برای آن [سالها] از پیش نهاده‌اید -جز اندکی را که ذخیره می‌کنید- همه را خواهند خورد.'),
(1645, 12, 49, 'آنگاه پس از آن، سالی فرا می‌رسد که به مردم در آن [سال‌] باران می‌رسد و در آن آب میوه می‌گیرند.'),
(1646, 12, 50, 'و پادشاه گفت: «او را نزد من آورید.» پس هنگامی که آن فرستاده نزد وی آمد، [یوسف‌] گفت: «نزد آقای خویش برگرد و از او بپرس که حال آن زنانی که دستهای خود را بریدند چگونه است؟ زیرا پروردگار من به نیرنگ آنان آگاه است.»'),
(1647, 12, 51, '[پادشاه‌] گفت: «وقتی از یوسف کام [می‌]خواستید چه منظور داشتید؟» زنان گفتند: «منزه است خدا، ما گناهی بر او نمی‌دانیم،» همسر عزیز گفت: «اکنون حقیقت آشکار شد. من [بودم که‌] از او کام خواستم، و بی‌شک او از راستگویان است.»'),
(1648, 12, 52, '[یوسف گفت:] «این [درخواست اعاده حیثیت‌] برای آن بود که [عزیز] بداند من در نهان به او خیانت نکردم، و خدا نیرنگ خائنان را به جایی نمی‌رساند.'),
(1649, 12, 53, 'و من نفس خود را تبرئه نمی‌کنم، چرا که نفس قطعاً به بدی امر می‌کند، مگر کسی را که خدا رحم کند، زیرا پروردگار من آمرزنده مهربان است.'),
(1650, 12, 54, 'و پادشاه گفت: «او را نزد من آورید، تا وی را خاص خود کنم.» پس چون با او سخن راند، گفت: «تو امروز نزد ما با منزلت و امین هستی.»'),
(1651, 12, 55, '[یوسف‌] گفت: «مرا بر خزانه‌های این سرزمین بگمار، که من نگهبانی دانا هستم.»'),
(1652, 12, 56, 'و بدین گونه یوسف را در سرزمین [مصر] قدرت دادیم، که در آن، هر جا که می خواست سکونت می‌کرد. هر که را بخواهیم به رحمت خود می‌رسانیم و اجر نیکوکاران را تباه نمی‌سازیم.'),
(1653, 12, 57, 'و البته اجر آخرت، برای کسانی که ایمان آورده و پرهیزگاری می‌نمودند، بهتر است.'),
(1654, 12, 58, 'و برادران یوسف آمدند و بر او وارد شدند. [او] آنان را شناخت ولی آنان او را نشناختند.'),
(1655, 12, 59, 'و چون آنان را به خوار و بارشان مجهز کرد، گفت: «برادر پدری خود را نزد من آورید. مگر نمی‌بینید که من پیمانه را تمام می‌دهم و من بهترین میزبانانم؟'),
(1656, 12, 60, 'پس اگر او را نزد من نیاوردید، برای شما نزد من پیمانه‌ای نیست، و به من نزدیک نشوید.'),
(1657, 12, 61, 'گفتند: «او را با نیرنگ از پدرش خواهیم خواست، و محققاً این کار را خواهیم کرد.»'),
(1658, 12, 62, 'و [یوسف‌] به غلامان خود گفت: «سرمایه‌های آنان را در بارهایشان بگذارید، شاید وقتی به سوی خانواده خود برمی‌گردند آن را بازیابند، امید که آنان بازگردند.»'),
(1659, 12, 63, 'پس چون به سوی پدر خود بازگشتند، گفتند: «ای پدر، پیمانه از ما منع شد. برادرمان را با ما بفرست تا پیمانه بگیریم، و ما نگهبان او خواهیم بود.»'),
(1660, 12, 64, '[یعقوب‌] گفت: «آیا همان گونه که شما را پیش از این بر برادرش امین گردانیدم، بر او امین سازم؟ پس خدا بهترین نگهبان است، و اوست مهربانترین مهربانان.»'),
(1661, 12, 65, 'و هنگامی که بارهای خود را گشودند، دریافتند که سرمایه‌شان بدانها بازگردانیده شده است. گفتند: «ای پدر، [دیگر] چه می‌خواهیم؟ این سرمایه ماست که به ما بازگردانیده شده است. قوت خانواده خود را فراهم، و برادرمان را نگهبانی می‌کنیم، و [با بردن او] یک بار شتر می‌افزاییم، و این [پیمانه اضافی نزد عزیز] پیمانه‌ای ناچیز است.»'),
(1662, 12, 66, 'گفت: «هرگز او را با شما نخواهم فرستاد تا با من با نام خدا پیمان استواری ببندید که حتماً او را نزد من باز آورید، مگر آنکه گرفتار [حادثه‌ای‌] شوید.» پس چون پیمان خود را با او استوار کردند [یعقوب‌] گفت: «خدا بر آنچه می‌گوییم وکیل است.»'),
(1663, 12, 67, 'و گفت: «ای پسران من، [همه‌] از یک دروازه [به شهر] در نیایید، بلکه از دروازه‌های مختلف وارد شوید، و من [با این سفارش،] چیزی از [قضای‌] خدا را از شما دور نمی‌توانم داشت. فرمان جز برای خدا نیست. بر او توکل کردم، و توکل‌کنندگان باید بر او توکل کنند.»'),
(1664, 12, 68, 'و چون همان گونه که پدرانشان به آنان فرمان داده بود وارد شدند، [این کار] چیزی را در برابر خدا از آنان برطرف نمی‌کرد جز اینکه یعقوب نیازی را که در دلش بود، برآورد و بی‌گمان، او از [برکت‌] آنچه بدو آموخته بودیم دارای دانشی [فراوان‌] بود، ولی بیشتر مردم نمی‌دانند.'),
(1665, 12, 69, 'و هنگامی که بر یوسف وارد شدند، برادرش [بنیامین‌] را نزد خود جای داد [و] گفت: «من برادر تو هستم.» بنابراین، از آنچه [برادران‌] می‌کردند، غمگین مباش.'),
(1666, 12, 70, 'پس هنگامی که آنان را به خوار و بارشان مجهز کرد، آبخوری را در بار برادرش نهاد. سپس [به دستور او] نداکننده‌ای بانگ درداد: «ای کاروانیان، قطعاً شما دزد هستید.»'),
(1667, 12, 71, '[برادران‌] در حالی که به آنان روی کردند، گفتند: «چه گم کرده‌اید؟»'),
(1668, 12, 72, 'گفتند: «جام شاه را گم کرده‌ایم، و برای هر کس که آن را بیاورد یک بار شتر خواهد بود.» و [متصدی گفت:] «من ضامن آنم.»'),
(1669, 12, 73, 'گفتند: «به خدا سوگند، شما خوب می‌دانید که ما نیامده‌ایم در این سرزمین فساد کنیم و ما دزد نبوده‌ایم.»'),
(1670, 12, 74, 'گفتند: «پس، اگر دروغ بگویید، کیفرش چیست؟»'),
(1671, 12, 75, 'گفتند: «کیفرش [همان‌] کسی است که [جام‌] در بار او پیدا شود. پس کیفرش خود اوست. ما ستمکاران را این گونه کیفر می‌دهیم.»'),
(1672, 12, 76, 'پس [یوسف‌] به [بازرسی‌] بارهای آنان، پیش از بار برادرش، پرداخت. آنگاه آن را از بار برادرش [بنیامین‌] در آورد. این گونه به یوسف شیوه آموختیم. [چرا که‌] او در آیین پادشاه نمی‌توانست برادرش را بازداشت کند، مگر اینکه خدا بخواهد [و چنین راهی بدو بنماید]. درجات کسانی را که بخواهیم بالا می‌بریم و فوق هر صاحب دانشی دانشوری است.'),
(1673, 12, 77, 'گفتند: «اگر او دزدی کرده، پیش از این [نیز] برادرش دزدی کرده است. «یوسف این [سخن‌] را در دل خود پنهان داشت و آن را برایشان آشکار نکرد [ولی‌] گفت: «موقعیت شما بدتر [از او]ست، و خدا به آنچه وصف می‌کنید داناتر است.»'),
(1674, 12, 78, 'گفتند: «ای عزیز، او پدری پیر سالخورده دارد؛ بنابراین یکی از ما را به جای او بگیر، که ما تو را از نیکوکاران می‌بینیم.»'),
(1675, 12, 79, 'گفت: «پناه به خدا، که جز آن کس را که کالای خود را نزد وی یافته‌ایم بازداشت کنیم، زیرا در آن صورت قطعاً ستمکار خواهیم بود.»'),
(1676, 12, 80, 'پس چون از او نومید شدند، رازگویان کنار کشیدند. بزرگشان گفت: «مگر نمی‌دانید که پدرتان با نام خدا پیمانی استوار از شما گرفته است و قبلا [هم‌] در باره یوسف تقصیر کردید؟ هرگز از این سرزمین نمی‌روم تا پدرم به من اجازه دهد یا خدا در حق من داوری کند، و او بهترین داوران است.'),
(1677, 12, 81, 'پیش پدرتان بازگردید و بگویید: ای پدر، پسرت دزدی کرده، و ما جز آنچه می‌دانیم گواهی نمی‌دهیم و ما نگهبان غیب نبودیم.'),
(1678, 12, 82, 'و از [مردم‌] شهری که در آن بودیم و کاروانی که در میان آن آمدیم جویا شو، و ما قطعاً راست می‌گوییم.'),
(1679, 12, 83, '[یعقوب‌] گفت: «[چنین نیست،] بلکه نفس شما امری [نادرست‌] را برای شما آراسته است. پس [صبر من‌] صبری نیکوست. امید که خدا همه آنان را به سوی من [باز] آورد، که او دانای حکیم است.»'),
(1680, 12, 84, 'و از آنان روی گردانید و گفت: «ای دریغ بر یوسف، و در حالی که اندوه خود را فرو می‌خورد، چشمانش از اندوه سپید شد.»'),
(1681, 12, 85, '[پسران او] گفتند: «به خدا سوگند که پیوسته یوسف را یاد می‌کنی تا بیمار شوی یا هلاک گردی.»'),
(1682, 12, 86, 'گفت: «من شکایت غم و اندوه خود را پیش خدا می‌برم، و از [عنایت‌] خدا چیزی می‌دانم که شما نمی‌دانید.'),
(1683, 12, 87, 'ای پسران من، بروید و از یوسف و برادرش جستجو کنید و از رحمت خدا نومید مباشید، زیرا جز گروه کافران کسی از رحمت خدا نومید نمی‌شود.»'),
(1684, 12, 88, 'پس چون [برادران‌] بر او وارد شدند، گفتند: «ای عزیز، به ما و خانواده ما آسیب رسیده است و سرمایه‌ای ناچیز آورده‌ایم. بنابراین پیمانه ما را تمام بده و بر ما تصدق کن که خدا صدقه‌دهندگان را پاداش می‌دهد.»'),
(1685, 12, 89, 'گفت: «آیا دانستید، وقتی که نادان بودید، با یوسف و برادرش چه کردید؟»'),
(1686, 12, 90, 'گفتند: «آیا تو خود، یوسفی؟» گفت: «[آری،] من یوسفم و این برادر من است. به راستی خدا بر ما منت نهاده است. بی‌گمان، هر که تقوا و صبر پیشه کند، خدا پاداش نیکوکاران را تباه نمی‌کند.»'),
(1687, 12, 91, 'گفتند: «به خدا سوگند، که واقعاً خدا تو را بر ما برتری داده است و ما خطاکار بودیم.»'),
(1688, 12, 92, '[یوسف‌] گفت: «امروز بر شما سرزنشی نیست. خدا شما را می‌آمرزد و او مهربانترین مهربانان است.»'),
(1689, 12, 93, '«این پیراهن مرا ببرید و آن را بر چهره پدرم بیفکنید [تا] بینا شود، و همه کسان خود را نزد من آورید.»'),
(1690, 12, 94, 'و چون کاروان رهسپار شد، پدرشان گفت: «اگر مرا به کم‌خردی نسبت ندهید، بوی یوسف را می‌شنوم.»'),
(1691, 12, 95, 'گفتند: «به خدا سوگند که تو سخت در گمراهیِ دیرین خود هستی.»'),
(1692, 12, 96, 'پس چون مژده‌رسان آمد، آن [پیراهن‌] را بر چهره او انداخت، پس بینا گردید. گفت: «آیا به شما نگفتم که بی‌شک من از [عنایت‌] خدا چیزهایی می‌دانم که شما نمی‌دانید؟»'),
(1693, 12, 97, 'گفتند: «ای پدر، برای گناهان ما آمرزش خواه که ما خطاکار بودیم.»'),
(1694, 12, 98, 'گفت: «به زودی از پروردگارم برای شما آمرزش می‌خواهم، که او همانا آمرزنده مهربان است.»'),
(1695, 12, 99, 'پس چون بر یوسف وارد شدند، پدر و مادر خود را در کنار خویش گرفت و گفت: «ان شاء الله، با [امن و] امان داخل مصر شوید.»'),
(1696, 12, 100, 'و پدر و مادرش را به تخت برنشانید، و [همه آنان‌] پیش او به سجده درافتادند، و [یوسف‌] گفت: «ای پدر، این است تعبیر خواب پیشین من، به یقین، پروردگارم آن را راست گردانید و به من احسان کرد آنگاه که مرا از زندان خارج ساخت و شما را از بیابان [کنعان به مصر] باز آورد -پس از آنکه شیطان میان من و برادرانم را به هم زد- بی گمان، پروردگار من نسبت به آنچه بخواهد صاحب لطف است، زیرا که او دانای حکیم است.»'),
(1697, 12, 101, '«پروردگارا، تو به من دولت دادی و از تعبیر خوابها به من آموختی. ای پدیدآورنده آسمانها و زمین، تنها تو در دنیا و آخرت مولای منی؛ مرا مسلمان بمیران و مرا به شایستگان ملحق فرما.»'),
(1698, 12, 102, 'این [ماجرا] از خبرهای غیب است که به تو وحی می‌کنیم، و تو هنگامی که آنان همداستان شدند و نیرنگ می‌کردند نزدشان نبودی.'),
(1699, 12, 103, 'و بیشتر مردم -هر چند آرزومند باشی- ایمان‌آورنده نیستند.'),
(1700, 12, 104, 'و تو بر این [کار] پاداشی از آنان نمی‌خواهی. آن [قرآن‌] جز پندی برای جهانیان نیست.'),
(1701, 12, 105, 'و چه بسیار نشانه‌ها در آسمانها و زمین است که بر آنها می‌گذرند در حالی که از آنها روی برمی‌گردانند.'),
(1702, 12, 106, 'و بیشترشان به خدا ایمان نمی‌آورند جز اینکه [با او چیزی را] شریک می‌گیرند.'),
(1703, 12, 107, 'آیا ایمنند از اینکه عذاب فراگیر خدا به آنان دررسد، یا قیامت -در حالی که بی‌خبرند- بناگاه آنان را فرا رسد؟'),
(1704, 12, 108, 'بگو: «این است راه من، که من و هر کس (پیروی‌ام) کرد با بینایی به سوی خدا دعوت می‌کنیم، و منزّه است خدا، و من از مشرکان نیستم.»'),
(1705, 12, 109, 'و پیش از تو [نیز] جز مردانی از اهل شهرها را -که به آنان وحی می‌کردیم- نفرستادیم. آیا در زمین نگردیده‌اند تا فرجام کسانی را که پیش از آنان بوده‌اند بنگرند؟ و قطعاً سرای آخرت برای کسانی که پرهیزگاری کرده‌اند بهتر است. آیا نمی‌اندیشید؟'),
(1706, 12, 110, 'تا هنگامی که فرستادگان [ما] نومید شدند و [مردم‌] پنداشتند که به آنان واقعاً دروغ گفته شده، یاریِ ما به آنان رسید. پس کسانی را که می‌خواستیم، نجات یافتند، و[لی‌] عذاب ما از گروه مجرمان برگشت ندارد.'),
(1707, 12, 111, 'به راستی در سرگذشت آنان، برای خردمندان عبرتی است. سخنی نیست که به دروغ ساخته شده باشد، بلکه تصدیق آنچه [از کتابهایی‌] است که پیش از آن بوده و روشنگر هر چیز است و برای مردمی که ایمان می‌آورند رهنمود و رحمتی است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، میم، راء. این است آیات کتاب، و آنچه از جانب پروردگارت به سوی تو نازل شده، حق است، ولی بیشتر مردم نمی‌گروند.'),
(1709, 13, 2, 'خدا [همان‌] کسی است که آسمانها را بدون ستونهایی که آنها را ببینید برافراشت، آنگاه بر عرش استیلا یافت و خورشید و ماه را رام گردانید؛ هر کدام برای مدّتی معیّن به سیر خود ادامه می‌دهند. [خداوند] در کار [آفرینش‌] تدبیر می‌کند، و آیات [خود] را به روشنی بیان می‌نماید، امید که شما به لقای پروردگارتان یقین حاصل کنید.'),
(1710, 13, 3, 'و اوست کسی که زمین را گسترانید و در آن، کوهها و رودها نهاد، و از هر گونه میوه‌ای در آن، جفت جفت قرار داد. روز را به شب می‌پوشاند. قطعاً در این [امور] برای مردمی که تفکّر می‌کنند نشانه‌هایی وجود دارد.'),
(1711, 13, 4, 'و در زمین قطعاتی است کنار هم، و باغهایی از انگور و کشتزارها و درختان خرما، چه از یک ریشه و چه از غیر یک ریشه، که با یک آب سیراب می‌گردند، و [با این همه‌] برخی از آنها را در میوه [از حیث مزه و نوع و کیفیت‌] بر برخی دیگر برتری می‌دهیم. بی گمان در این [امر نیز] برای مردمی که تعقّل می‌کنند دلایل [روشنی‌] است.'),
(1712, 13, 5, 'و اگر عجب داری، عجب از سخن آنان [=کافران‌] است که: «آیا وقتی خاک شدیم، به راستی در آفرینش جدیدی خواهیم بود؟» اینان همان کسانند که به پروردگارشان کفر ورزیده‌اند و در گردنهایشان زنجیرهاست، و آنان همدم آتشند و در آن ماندگار خواهند بود.'),
(1713, 13, 6, 'و پیش از رحمت، شتابزده از تو عذاب می‌طلبند و حال آنکه پیش از آنان [بر کافران‌] عقوبتها رفته است، و به راستی پروردگار تو نسبت به مردم -با وجود ستمشان- بخشایشگر است، و به یقین پروردگار تو سخت‌کیفر است.'),
(1714, 13, 7, 'و آنان که کافر شده‌اند می‌گویند: «چرا نشانه‌ای آشکار از طرف پروردگارش بر او نازل نشده است؟» [ای پیامبر،] تو فقط هشداردهنده‌ای، و برای هر قومی رهبری است.'),
(1715, 13, 8, 'خدا می‌داند آنچه را که هر ماده‌ای [در رحم‌] بار می‌گیرد، و [نیز] آنچه را که رحمها می‌کاهند و آنچه را می‌افزایند. و هر چیزی نزد او اندازه‌ای دارد.'),
(1716, 13, 9, 'دانای نهان و آشکار، [و] بزرگِ بلندمرتبه است.'),
(1717, 13, 10, '[برای او] یکسان است: کسی از شما سخن [خود] را نهان کند و کسی که آن را فاش گرداند، و کسی که خویشتن را به شب پنهان دارد و در روز، آشکارا حرکت کند.'),
(1718, 13, 11, 'برای او فرشتگانی است که پی در پی او را به فرمان خدا از پیش رو و از پشت سرش پاسداری می‌کنند. در حقیقت، خدا حال قومی را تغییر نمی‌دهد تا آنان حال خود را تغییر دهند. و چون خدا برای قومی آسیبی بخواهد، هیچ برگشتی برای آن نیست، و غیر از او حمایتگری برای آنان نخواهد بود.'),
(1719, 13, 12, 'اوست کسی که برق را برای بیم و امید به شما می‌نمایاند، و ابرهای گرانبار را پدیدار می‌کند.'),
(1720, 13, 13, 'رعد، به حمد او، و فرشتگان [جملگی‌] از بیمش تسبیح می‌گویند، و صاعقه‌ها را فرو می‌فرستند و با آنها هر که را بخواهد، مورد اصابت قرار می‌دهد، در حالی که آنان در باره خدا مجادله می‌کنند، و او سخت‌کیفر است.'),
(1721, 13, 14, 'دعوت حق برای اوست. و کسانی که [مشرکان‌] جز او می‌خوانند، هیچ جوابی به آنان نمی‌دهند، مگر مانند کسی که دو دستش را به سوی آب بگشاید تا [آب‌] به دهانش برسد، در حالی که [آب‌] به [دهان‌] او نخواهد رسید، و دعای کافران جز بر هدر نباشد.'),
(1722, 13, 15, 'و هر که در آسمانها و زمین است -خواه و ناخواه- با سایه‌هایشان، بامدادان و شامگاهان، برای خدا سجده می‌کنند.'),
(1723, 13, 16, 'بگو: «پروردگار آسمانها و زمین کیست؟» بگو: «خدا!» بگو: «پس آیا جز او سرپرستانی گرفته‌اید که اختیار سود و زیان خود را ندارند؟» بگو: «آیا نابینا و بینا یکسانند؟ یا تاریکیها و روشنایی برابرند؟ یا برای خدا شریکانی پنداشته‌اند که مانند آفرینش او آفریده‌اند و در نتیجه، [این دو] آفرینش بر آنان مشتبه شده است؟» بگو: «خدا آفریننده هر چیزی است، و اوست یگانه قهار.»'),
(1724, 13, 17, '[همو که‌] از آسمان، آبی فرو فرستاد. پس رودخانه‌هایی به اندازه گنجایش خودشان روان شدند، و سیل، کفی بلند روی خود برداشت، و از آنچه برای به دست آوردن زینتی یا کالایی، در آتش می‌گدازند هم نظیر آن کفی برمی‌آید. خداوند، حق و باطل را چنین مَثَل می‌زند. اما کف، بیرون افتاده از میان می‌رود، ولی آنچه به مردم سود می‌رساند در زمین [باقی‌] می‌ماند. خداوند مَثَلها را چنین می‌زند.'),
(1725, 13, 18, 'برای کسانی که پروردگارشان را اجابت کرده‌اند پاداش بس نیکوست. و کسانی که وی را اجابت نکرده‌اند، اگر سراسر آنچه در زمین است و مانند آن را با آن داشته باشند، قطعاً آن را برای بازخرید خود خواهند داد. آنان به سختی بازخواست شوند و جایشان در دوزخ است و چه بد جایگاهی است.'),
(1726, 13, 19, 'پس، آیا کسی که می‌داند آنچه از جانب پروردگارت به تو نازل شده، حقیقت دارد، مانند کسی است که کوردل است؟ تنها خردمندانند که عبرت می‌گیرند.'),
(1727, 13, 20, 'همانان که به پیمان خدا وفادارند و عهد [او] را نمی‌شکنند.'),
(1728, 13, 21, 'و آنان که آنچه را خدا به پیوستنش فرمان داده می‌پیوندند و از پروردگارشان می‌ترسند و از سختی حساب بیم دارند.'),
(1729, 13, 22, 'و کسانی که برای طلب خشنودی پروردگارشان شکیبایی کردند و نماز برپا داشتند و از آنچه روزیشان دادیم، نهان و آشکارا انفاق کردند، و بدی را با نیکی می‌زدایند، ایشان راست فرجامِ خوشِ سرای باقی.'),
(1730, 13, 23, '[همان‌] بهشتهای عدن که آنان با پدرانشان و همسرانشان و فرزندانشان که درستکارند در آن داخل می‌شوند، و فرشتگان از هر دری بر آنان درمی‌آیند.'),
(1731, 13, 24, '[و به آنان می‌گویند:] «درود بر شما به [پاداش‌] آنچه صبر کردید. راستی چه نیکوست فرجام آن سرای!»'),
(1732, 13, 25, 'و کسانی که پیمان خدا را پس از بستن آن می‌شکنند و آنچه را خدا به پیوستن آن فرمان داده می‌گسلند و در زمین فساد می‌کنند، بر ایشان لعنت است و بد فرجامی آن سرای ایشان راست.'),
(1733, 13, 26, 'خدا روزی را برای هر که بخواهد گشاده یا تنگ می‌گرداند، و[لی آنان‌] به زندگی دنیا شاد شده‌اند، و زندگی دنیا در [برابر] آخرت جز بهره‌ای [ناچیز] نیست.'),
(1734, 13, 27, 'و کسانی که کافر شده‌اند می‌گویند: «چرا از جانب پروردگارش معجزه‌ای بر او نازل نشده است؟» بگو: «در حقیقت خداست که هر کس را بخواهد بی‌راه می‌گذارد و هر کس را که [به سوی او] بازگردد، به سوی خود راه می‌نماید.»'),
(1735, 13, 28, 'همان کسانی که ایمان آورده‌اند و دلهایشان به یاد خدا آرام می‌گیرد. آگاه باش که با یاد خدا دلها آرامش می‌یابد.'),
(1736, 13, 29, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، خوشا به حالشان، و خوش سرانجامی دارند.'),
(1737, 13, 30, 'بدین گونه تو را در میان امتی که پیش از آن، امتهایی روزگار به سر بردند، فرستادیم تا آنچه را به تو وحی کردیم بر آنان بخوانی، در حالی که آنان به [خدای‌] رحمان کفر می‌ورزند. بگو: «اوست پروردگار من. معبودی بجز او نیست. بر او توکل کرده‌ام و بازگشت من به سوی اوست.»'),
(1738, 13, 31, 'و اگر قرآنی بود که کوهها بدان روان می‌شد، یا زمین بدان قطعه قطعه می‌گردید، یا مردگان بدان به سخن درمی‌آمدند [باز هم در آنان اثر نمی‌کرد.] نه چنین است، بلکه همه امور بستگی به خدا دارد. آیا کسانی که ایمان آورده‌اند، ندانسته‌اند که اگر خدا می‌خواست قطعاً تمام مردم را به راه می‌آورد؟ و کسانی که کافر شده‌اند پیوسته به [سزای‌] آنچه کرده‌اند مصیبت کوبنده‌ای به آنان می‌رسد یا نزدیک خانه‌هایشان فرود می‌آید، تا وعده خدا فرا رسد. آری، خدا وعده [خود را] خلاف نمی‌کند.'),
(1739, 13, 32, 'و بی‌گمان، فرستادگان پیش از تو [نیز] مسخره شدند. پس به کسانی که کافر شده بودند مهلت دادم، آنگاه آنان را [به کیفر] گرفتم. پس چگونه بود کیفر من؟'),
(1740, 13, 33, 'آیا کسی که بر هر شخصی بدانچه کرده است مراقب است [مانند کسی است که از همه جا بی‌خبر است‌]؟ و برای خدا شریکانی قرار دادند. بگو: «نامشان را ببرید» آیا او را به آنچه در زمین است و او نمی‌داند خبر می‌دهید، یا سخنی سطحی [و میان‌تهی‌] می‌گویید؟ [چنین نیست‌] بلکه برای کسانی که کافر شده‌اند نیرنگشان آراسته شده و از راه [حق‌] بازداشته شده‌اند و هر که را خدا بی‌راه گذارد رهبری نخواهد داشت.'),
(1741, 13, 34, 'برای آنان در زندگی دنیا عذابی است، و قطعاً عذاب آخرت دشوارتر است، و برای ایشان در برابر خدا هیچ نگهدارنده‌ای نیست.'),
(1742, 13, 35, 'وصف بهشتی که به پرهیزگاران وعده داده شده [این است که‌] از زیر [درختان ] آن نهرها روان است. میوه و سایه‌اش پایدار است. این است فرجام کسانی که پرهیزگاری کرده‌اند و فرجام کافران آتش [دوزخ ]است.'),
(1743, 13, 36, 'و کسانی که به آنان کتاب [آسمانی‌] داده‌ایم، از آنچه به سوی تو نازل شده شاد می‌شوند. و برخی از دسته‌ها کسانی هستند که بخشی از آن را انکار می‌کنند. بگو: «جز این نیست که من مأمورم خدا را بپرستم و به او شرک نورزم. به سوی او می‌خوانم و بازگشتم به سوی اوست.»'),
(1744, 13, 37, 'و بدین سان آن [قرآن‌] را فرمانی روشن نازل کردیم، و اگر پس از دانشی که به تو رسیده [باز] از هوسهای آنان پیروی کنی، در برابر خدا هیچ دوست و حمایتگری نخواهی داشت.'),
(1745, 13, 38, 'و قطعاً پیش از تو [نیز] رسولانی فرستادیم، و برای آنان زنان و فرزندانی قرار دادیم. و هیچ پیامبری را نرسد که جز به اذن خدا معجزه‌ای بیاورد. برای هر زمانی کتابی است.'),
(1746, 13, 39, 'خدا آنچه را بخواهد محو یا اثبات می‌کند، و اصل کتاب نزد اوست.'),
(1747, 13, 40, 'و اگر پاره‌ای از آنچه را که به آنان وعده می‌دهیم به تو بنمایانیم، یا تو را بمیرانیم، جز این نیست که بر تو رساندن [پیام‌] است و بر ما حساب [آنان‌].'),
(1748, 13, 41, 'آیا ندیده‌اند که ما [همواره‌] می‌آییم و از اطراف این زمین می‌کاهیم؟ و خداست که حکم می‌کند. برای حکم او باز دارنده‌ای نیست، و او به سرعت حسابرسی می‌کند.'),
(1749, 13, 42, 'و به یقین، کسانی که پیش از آنان بودند نیرنگ کردند، ولی همه تدبیرها نزد خداست. آنچه را که هر کسی به دست می‌آورد می‌داند. و به زودی کافران بدانند که فرجام آن سرای از کیست.'),
(1750, 13, 43, 'و کسانی که کافر شدند می‌گویند: «تو فرستاده نیستی.» بگو: «کافی است خدا و آن کس که نزد او علم کتاب است، میان من و شما گواه باشد.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف، لام، راء. کتابی است که آن را به سوی تو فرود آوردیم تا مردم را به اذن پروردگارشان از تاریکیها به سوی روشنایی بیرون آوری: به سوی راه آن شکست ناپذیر ستوده.'),
(1752, 14, 2, 'خدایی که آنچه در آسمانها و آنچه در زمین است از آنِ اوست، و وای بر کافران از عذابی سخت.'),
(1753, 14, 3, 'همانان که زندگی دنیا را بر آخرت ترجیح می‌دهند و مانع راه خدا می‌شوند و آن را کج می‌شمارند. آنانند که در گمراهی دور و درازی هستند.'),
(1754, 14, 4, 'و ما هیچ پیامبری را جز به زبان قومش نفرستادیم، تا [حقایق را] برای آنان بیان کند. پس خدا هر که را بخواهد بی‌راه می‌گذارد و هر که را بخواهد هدایت می‌کند، و اوست ارجمند حکیم.'),
(1755, 14, 5, 'و در حقیقت، موسی را با آیات خود فرستادیم [و به او فرمودیم‌] که قوم خود را از تاریکیها به سوی روشنایی بیرون آور، و روزهای خدا را به آنان یادآوری کن، که قطعاً در این [یادآوری‌]، برای هر شکیبایِ سپاسگزاری عبرتهاست.'),
(1756, 14, 6, 'و [به خاطر بیاور] هنگامی را که موسی به قوم خود گفت: «نعمت خدا را بر خود به یاد آورید، آنگاه که شما را از فرعونیان رهانید، [همانان‌] که بر شما عذاب سخت روا می‌داشتند، و پسرانتان را سر می‌بریدند و زنانتان را زنده می‌گذاشتند، و در این [امر] برای شما از جانب پروردگارتان آزمایشی بزرگ بود.'),
(1757, 14, 7, 'و آنگاه که پروردگارتان اِعلام کرد که اگر واقعاً سپاسگزاری کنید، [نعمت‌] شما را افزون خواهم کرد، و اگر ناسپاسی نمایید، قطعاً عذاب من سخت خواهد بود.»'),
(1758, 14, 8, 'و موسی گفت: «اگر شما و هر که در روی زمین است همگی کافر شوید، بی‌گمان، خدا بی‌نیاز ستوده‌[صفات‌] است.»'),
(1759, 14, 9, 'آیا خبر کسانی که پیش از شما بودند: قوم نوح و عاد و ثمود، و آنانکه بعد از ایشان بودند [و] کسی جز خدا از آنان آگاهی ندارد، به شما نرسیده است؟ فرستادگانشان دلایل آشکار برایشان آوردند، ولی آنان دستهایشان را [به نشانه اعتراض‌] بر دهانهایشان نهادند و گفتند: «ما به آنچه شما بدان مأموریت دارید کافریم، و از آنچه ما را بدان می‌خوانید سخت در شکّیم.»'),
(1760, 14, 10, 'پیامبرانشان گفتند: «مگر در باره خدا -پدید آورنده آسمانها و زمین- تردیدی هست؟ او شما را دعوت می‌کند تا پاره‌ای از گناهانتان را بر شما ببخشاید و تا زمانِ معینی شما را مهلت دهد.» گفتند: «شما جز بشری مانند ما نیستید. می‌خواهید ما را از آنچه پدرانمان می‌پرستیدند باز دارید. پس برای ما حجّتی آشکار بیاورید.»'),
(1761, 14, 11, 'پیامبرانشان به آنان گفتند: «ما جز بشری مثل شما نیستیم. ولی خدا بر هر یک از بندگانش که بخواهد منّت می‌نهد، و ما را نرسد که جز به اذن خدا برای شما حجّتی بیاوریم، و مؤمنان باید تنها بر خدا توکل کنند.»'),
(1762, 14, 12, 'و چرا بر خدا توکل نکنیم و حال آنکه ما را به راه‌هایمان رهبری کرده است؟ و البته ما بر آزاری که به ما رساندید شکیبایی خواهیم کرد، و توکل‌کنندگان باید تنها بر خدا توکل کنند.'),
(1763, 14, 13, 'و کسانی که کافر شدند، به پیامبرانشان گفتند: «شما را از سرزمین خودمان بیرون خواهیم کرد مگر اینکه به کیش ما بازگردید.» پس پروردگارشان به آنان وحی کرد که حتماً ستمگران را هلاک خواهیم کرد.'),
(1764, 14, 14, 'و قطعاً شما را پس از ایشان در آن سرزمین سکونت خواهیم داد. این برای کسی است که از ایستادن [در محشر به هنگام حساب‌] در پیشگاه من بترسد و از تهدیدم بیم داشته باشد.'),
(1765, 14, 15, 'و [پیامبران از خدا] گشایش خواستند، و [سرانجام‌] هر زورگوی لجوجی نومید شد.'),
(1766, 14, 16, '[آن کس که‌] دوزخ پیش روی اوست و به او آبی چرکین نوشانده می‌شود.'),
(1767, 14, 17, 'آن را جرعه جرعه می‌نوشد و نمی‌تواند آن را فرو برد، و مرگ از هر جانبی به سویش می‌آید ولی نمی‌میرد و عذابی سنگین به دنبال دارد.'),
(1768, 14, 18, 'مَثَل کسانی که به پروردگار خود کافر شدند، کردارهایشان به خاکستری می‌ماند که بادی تند در روزی طوفانی بر آن بوزد: از آنچه به دست آورده‌اند هیچ [بهره‌ای‌] نمی‌توانند بُرد. این است همان گمراهی دور و دراز.'),
(1769, 14, 19, 'آیا در نیافته‌ای که خدا آسمانها و زمین را به حق آفریده؟ اگر بخواهد شما را می‌برد و خلق تازه‌ای می‌آورد،'),
(1770, 14, 20, 'و این [کار] بر خدا دشوار نیست.'),
(1771, 14, 21, 'و همگی در برابر خدا ظاهر می‌شوند. پس ناتوانان به گردنکشان می‌گویند: «ما پیروان شما بودیم. آیا چیزی از عذاب خدا را از ما دور می‌کنید؟» می‌گویند: «اگر خدا ما را هدایت کرده بود، قطعاً شما را هدایت می‌کردیم. چه بی‌تابی کنیم، چه صبر نماییم برای ما یکسان است. ما را راهِ گریزی نیست.»'),
(1772, 14, 22, 'و چون کار از کار گذشت [و داوری صورت گرفت‌] شیطان می‌گوید: «در حقیقت، خدا به شما وعده داد وعده راست، و من به شما وعده دادم و با شما خلاف کردم، و مرا بر شما هیچ تسلطی نبود، جز اینکه شما را دعوت کردم و اجابتم نمودید. پس مرا ملامت نکنید و خود را ملامت کنید. من فریادرس شما نیستم و شما هم فریادرس من نیستید. من به آنچه پیش از این مرا [در کار خدا] شریک می‌دانستید کافرم». آری! ستمکاران عذابی پردرد خواهند داشت.'),
(1773, 14, 23, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند به بهشتهایی درآورده می‌شوند که از زیر [درختان‌] آن جویبارها روان است که به اذن پروردگارشان در آنجا جاودانه به سر می‌برند، و درودشان در آنجا سلام است.'),
(1774, 14, 24, 'آیا ندیدی خدا چگونه مَثَل زده: سخنی پاک که مانند درختی پاک است که ریشه‌اش استوار و شاخه‌اش در آسمان است؟'),
(1775, 14, 25, 'میوه‌اش را هر دم به اذن پروردگارش می‌دهد. و خدا مَثَلها را برای مردم می‌زند، شاید که آنان پند گیرند.'),
(1776, 14, 26, 'و مَثَل سخنی ناپاک چون درختی ناپاک است که از روی زمین کنده شده و قراری ندارد.'),
(1777, 14, 27, 'خدا کسانی را که ایمان آورده‌اند، در زندگی دنیا و در آخرت با سخن استوار ثابت می‌گرداند، و ستمگران را بی‌راه می‌گذارد، و خدا هر چه بخواهد انجام می‌دهد.'),
(1778, 14, 28, 'آیا به کسانی که [شکر] نعمت خدا را به کفر تبدیل کردند و قوم خود را به سرای هلاکت درآوردند ننگریستی؟'),
(1779, 14, 29, '[در آن سرای هلاکت که‌] جهنم است [و] در آن وارد می‌شوند، و چه بد قرارگاهی است.'),
(1780, 14, 30, 'و برای خدا مانندهایی قرار دادند تا [مردم را] از راه او گمراه کنند. بگو: «برخوردار شوید که قطعاً بازگشت شما به سوی آتش است.»'),
(1781, 14, 31, 'به آن بندگانم که ایمان آورده‌اند بگو: «نماز را بر پا دارند و از آنچه به ایشان روزی داده‌ایم، پنهان و آشکارا انفاق کنند، پیش از آنکه روزی فرا رسد که در آن نه داد و ستدی باشد و نه دوستیی.»'),
(1782, 14, 32, 'خداست که آسمانها و زمین را آفرید، و از آسمان آبی فرستاد، و به وسیله آن از میوه‌ها برای شما روزی بیرون آورد، و کشتی را برای شما رام گردانید تا به فرمان او در دریا روان شود، و رودها را برای شما مسخّر کرد.'),
(1783, 14, 33, 'و خورشید و ماه را -که پیوسته روانند- برای شما رام گردانید و شب و روز را [نیز] مسخر شما ساخت.'),
(1784, 14, 34, 'و از هر چه از او خواستید به شما عطا کرد، و اگر نعمت خدا را شماره کنید، نمی‌توانید آن را به شمار درآورید. قطعاً انسان ستم‌پیشه ناسپاس است.'),
(1785, 14, 35, 'و [یاد کن‌] هنگامی را که ابراهیم گفت: «پروردگارا، این شهر را ایمن گردان، و مرا و فرزندانم را از پرستیدن بتان دور دار.'),
(1786, 14, 36, 'پروردگارا، آنها بسیاری از مردم را گمراه کردند. پس هر که از من پیروی کند، بی گمان، او از من است، و هر که مرا نافرمانی کند، به یقین، تو آمرزنده و مهربانی.'),
(1787, 14, 37, 'پروردگارا، من [یکی از] فرزندانم را در درّه‌ای بی‌کشت، نزد خانه محترم تو، سکونت دادم. پروردگارا، تا نماز را به پا دارند، پس دلهای برخی از مردم را به سوی آنان گرایش ده و آنان را از محصولات [مورد نیازشان‌] روزی ده، باشد که سپاسگزاری کنند.'),
(1788, 14, 38, 'پروردگارا، بی‌گمان تو آنچه را که پنهان می‌داریم و آنچه را که آشکار می‌سازیم می‌دانی، و چیزی در زمین و در آسمان بر خدا پوشیده نمی‌ماند.'),
(1789, 14, 39, 'سپاس خدای را که با وجود سالخوردگی، اسماعیل و اسحاق را به من بخشید. به راستی پروردگار من شنونده دعاست.'),
(1790, 14, 40, 'پروردگارا، مرا برپادارنده نماز قرار ده، و از فرزندان من نیز. پروردگارا، و دعای مرا بپذیر.'),
(1791, 14, 41, 'پروردگارا، روزی که حساب برپا می‌شود، بر من و پدر و مادرم و بر مؤمنان ببخشای.»'),
(1792, 14, 42, 'و خدا را از آنچه ستمکاران می‌کنند غافل مپندار. جز این نیست که [کیفر] آنان را برای روزی به تأخیر می‌اندازد که چشمها در آن خیره می‌شود.'),
(1793, 14, 43, 'شتابان سر برداشته و چشم بر هم نمی‌زنند و [از وحشت‌] دلهایشان تهی است.'),
(1794, 14, 44, 'و مردم را از روزی که عذاب بر آنان می‌آید بترسان. پس آنان که ستم کرده‌اند می‌گویند: «پروردگارا، ما را تا چندی مهلت بخش تا دعوت تو را پاسخ گوییم و از فرستادگان [تو] پیروی کنیم.» [به آنان گفته می‌شود:] «مگر شما پیش از این سوگند نمی‌خوردید که شما را فنایی نیست؟'),
(1795, 14, 45, 'و در سراهای کسانی که بر خود ستم روا داشتند سکونت گزیدید، و برای شما آشکار گردید که با آنان چگونه معامله کردیم، و مَثَلها برای شما زدیم.'),
(1796, 14, 46, 'و به یقین آنان نیرنگ خود را به کار بردند، و [جزای‌] مکرشان با خداست، هر چند از مکرشان کوهها از جای کنده می‌شد.'),
(1797, 14, 47, 'پس مپندار که خدا وعده خود را به پیامبرانش خلاف می‌کند، که خدا شکست‌ناپذیرِ انتقام‌گیرنده است.'),
(1798, 14, 48, 'روزی که زمین به غیر این زمین، و آسمانها [به غیر این آسمانها] مبدل گردد، و [مردم‌] در برابر خدای یگانه قهار ظاهر شوند.'),
(1799, 14, 49, 'و گناهکاران را در آن روز می‌بینی که با هم در زنجیرها بسته شده‌اند.'),
(1800, 14, 50, 'تن‌پوشهایشان از «قطران» است و چهره‌هایشان را آتش می‌پوشاند.'),
(1801, 14, 51, 'تا خدا به هر کس هر چه به دست آورده است جزا دهد، که خدا زودشمار است.'),
(1802, 14, 52, 'این [قرآن‌] ابلاغی برای مردم است [تا به وسیله آن هدایت شوند] و بدان بیم یابند و بدانند که او معبودی یگانه است، و تا صاحبان خرد پند گیرند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف، لام، راء. این است آیات کتاب [آسمانی‌] و قرآن روشنگر.'),
(1804, 15, 2, 'چه بسا کسانی که کافر شدند آرزو کنند که کاش مسلمان بودند.'),
(1805, 15, 3, 'بگذارشان تا بخورند و برخوردار شوند و آرزو[ها] سرگرمشان کند، پس به زودی خواهند دانست.'),
(1806, 15, 4, 'و هیچ شهری را هلاک نکردیم مگر اینکه برای آن اجلی معیّن بود.'),
(1807, 15, 5, 'هیچ امتی از اجل خویش نه پیش می‌افتد و نه پس می‌ماند.'),
(1808, 15, 6, 'و گفتند: «ای کسی که قرآن بر او نازل شده است، به یقین تو دیوانه‌ای.'),
(1809, 15, 7, 'اگر راست می‌گویی چرا فرشته‌ها را پیش ما نمی‌آوری؟»'),
(1810, 15, 8, 'فرشتگان را جز به حق فرو نمی‌فرستیم، و در آن هنگام، دیگر مهلت نیابند.'),
(1811, 15, 9, 'بی‌تردید، ما این قرآن را به تدریج نازل کرده‌ایم، و قطعاً نگهبان آن خواهیم بود.'),
(1812, 15, 10, 'و به یقین، پیش از تو [نیز] در گروههای پیشینیان [پیامبرانی‌] فرستادیم.'),
(1813, 15, 11, 'و هیچ پیامبری برایشان نیامد جز آنکه او را به مسخره می‌گرفتند.'),
(1814, 15, 12, 'بدین گونه آن [استهزا] را در دل بزهکاران راه می‌دهیم،'),
(1815, 15, 13, '[که‌] به او ایمان نمی‌آورند، و راه [و رسم‌] پیشینیان پیوسته چنین بوده است.'),
(1816, 15, 14, 'و اگر دری از آسمان بر آنان می‌گشودیم که همواره از آن بالا می‌رفتند،'),
(1817, 15, 15, 'قطعاً می‌گفتند: «در حقیقت، ما چشم‌بندی شده‌ایم، بلکه ما مردمی هستیم که افسون شده‌ایم.»'),
(1818, 15, 16, 'و به یقین، ما در آسمان بُرجهایی قرار دادیم و آن را برای تماشاگران آراستیم.'),
(1819, 15, 17, 'و آن را از هر شیطان رانده‌شده‌ای حفظ کردیم.'),
(1820, 15, 18, 'مگر آن کس که دزدیده گوش فرا دهد که شهابی روشن او را دنبال می‌کند.'),
(1821, 15, 19, 'و زمین را گسترانیدیم و در آن کوههای استوار افکندیم و از هر چیز سنجیده‌ای در آن رویانیدیم.'),
(1822, 15, 20, 'و برای شما و هر کس که شما روزی‌دهنده او نیستید، در آن وسایل زندگی قرار دادیم.'),
(1823, 15, 21, 'و هیچ چیز نیست مگر آنکه گنجینه‌های آن نزد ماست، و ما آن را جز به اندازه‌ای معین فرو نمی‌فرستیم.'),
(1824, 15, 22, 'و بادها را باردارکننده فرستادیم و از آسمان، آبی نازل کردیم، پس شما را بدان سیراب نمودیم، و شما خزانه‌دار آن نیستید.'),
(1825, 15, 23, 'و بی‌تردید، این ماییم که زنده می‌کنیم و می‌میرانیم، و ما وارث [همه‌] هستیم.'),
(1826, 15, 24, 'و به یقین، پیشینیان شما را شناخته‌ایم و آیندگان [شما را نیز] شناخته‌ایم.'),
(1827, 15, 25, 'و مسلماً پروردگار توست که آنان را محشور خواهد کرد، چرا که او حکیم داناست.'),
(1828, 15, 26, 'و در حقیقت، انسان را از گِلی خشک، از گِلی سیاه و بدبو، آفریدیم.'),
(1829, 15, 27, 'و پیش از آن، جن را از آتشی سوزان و بی‌دود خلق کردیم.'),
(1830, 15, 28, 'و [یاد کن‌] هنگامی را که پروردگار تو به فرشتگان گفت: «من بشری را از گِلی خشک، از گِلی سیاه و بدبو، خواهم آفرید.'),
(1831, 15, 29, 'پس وقتی آن را درست کردم و از روح خود در آن دمیدم، پیش او به سجده درافتید.'),
(1832, 15, 30, 'پس فرشتگان همگی یکسره سجده کردند،'),
(1833, 15, 31, 'جز ابلیس که خودداری کرد از اینکه با سجده‌کنندگان باشد.'),
(1834, 15, 32, 'فرمود: «ای ابلیس، تو را چه شده است که با سجده‌کنندگان نیستی؟»'),
(1835, 15, 33, 'گفت: «من آن نیستم که برای بشری که او را از گِلی خشک، از گِلی سیاه و بدبو، آفریده‌ای، سجده کنم.»'),
(1836, 15, 34, 'فرمود: «از این [مقام‌] بیرون شو که تو رانده‌شده‌ای.'),
(1837, 15, 35, 'و تا روز جزا بر تو لعنت باشد.'),
(1838, 15, 36, 'گفت: «پروردگارا، پس مرا تا روزی که برانگیخته خواهند شد مهلت ده.»'),
(1839, 15, 37, 'فرمود: «تو از مهلت‌یافتگانی،'),
(1840, 15, 38, 'تا روز [و] وقت معلوم.'),
(1841, 15, 39, 'گفت: «پروردگارا، به سبب آنکه مرا گمراه ساختی، من [هم گناهانشان را] در زمین برایشان می‌آرایم و همه را گمراه خواهم ساخت،'),
(1842, 15, 40, 'مگر بندگان خالص تو از میان آنان را.»'),
(1843, 15, 41, 'فرمود: «این راهی است راست [که‌] به سوی من [منتهی می‌شود].'),
(1844, 15, 42, 'در حقیقت، تو را بر بندگان من تسلّطی نیست، مگر کسانی از گمراهان که تو را پیروی کنند،'),
(1845, 15, 43, 'و قطعاً وعده‌گاه همه آنان دوزخ است،'),
(1846, 15, 44, '[دوزخی‌] که برای آن هفت در است، و از هر دری بخشی معین از آنان [وارد می‌شوند].'),
(1847, 15, 45, 'بی‌گمان، پرهیزگاران در باغها و چشمه‌سارانند.'),
(1848, 15, 46, '[به آنان گویند:] «با سلامت و ایمنی در آنجا داخل شوید.»'),
(1849, 15, 47, 'و آنچه کینه [و شائبه‌های نفسانی‌] در سینه‌های آنان است برکنیم؛ برادرانه بر تختهایی روبروی یکدیگر نشسته‌اند.'),
(1850, 15, 48, 'نه رنجی در آنجا به آنان می‌رسد و نه از آنجا بیرون رانده می‌شوند.'),
(1851, 15, 49, 'به بندگان من خبر ده که منم آمرزنده مهربان.'),
(1852, 15, 50, 'و اینکه عذاب من، عذابی است دردناک.'),
(1853, 15, 51, 'و از مهمانان ابراهیم به آنان خبر ده،'),
(1854, 15, 52, 'هنگامی که بر او وارد شدند و سلام گفتند. [ابراهیم‌] گفت: «ما از شما بیمناکیم.»'),
(1855, 15, 53, 'گفتند: «مترس، که ما تو را به پسری دانا مژده می‌دهیم.»'),
(1856, 15, 54, 'گفت: «آیا با اینکه مرا پیری فرا رسیده است بشارتم می‌دهید؟ به چه بشارت می‌دهید؟»'),
(1857, 15, 55, 'گفتند: «ما تو را به حق بشارت دادیم. پس، از نومیدان مباش.»'),
(1858, 15, 56, 'گفت: «چه کسی -جز گمراهان- از رحمت پروردگارش نومید می‌شود؟»'),
(1859, 15, 57, '[سپس‌] گفت: «ای فرشتگان، [دیگر] کارتان چیست؟»'),
(1860, 15, 58, 'گفتند: «ما به سوی گروه مجرمان فرستاده شده‌ایم،'),
(1861, 15, 59, 'مگر خانواده لوط، که ما قطعاً همه آنان را نجات می‌دهیم،'),
(1862, 15, 60, 'جز زنش را که مقدر کردیم او از بازماندگان [در عذاب‌] باشد.'),
(1863, 15, 61, 'پس چون فرشتگان نزد خاندان لوط آمدند،'),
(1864, 15, 62, '[لوط] گفت: «شما مردمی ناشناس هستید.»'),
(1865, 15, 63, 'گفتند: «[نه،] بلکه برای تو چیزی آورده‌ایم که در آن تردید می‌کردند،'),
(1866, 15, 64, 'و حق را برای تو آورده‌ایم و قطعاً ما راستگویانیم،'),
(1867, 15, 65, 'پس، پاسی از شب [گذشته‌] خانواده‌ات را حرکت ده و [خودت‌] به دنبال آنان برو، و هیچ یک از شما نباید به عقب بنگرد، و هر جا به شما دستور داده می‌شود بروید.'),
(1868, 15, 66, 'و او را از این امر آگاه کردیم که ریشه آن گروه صبحگاهان بریده خواهد شد.'),
(1869, 15, 67, 'و مردم شهر، شادی‌کنان روی آوردند.'),
(1870, 15, 68, '[لوط] گفت: «اینان مهمانان منند، مرا رسوا مکنید،'),
(1871, 15, 69, 'و از خدا پروا کنید و مرا خوار نسازید.»'),
(1872, 15, 70, 'گفتند: «آیا تو را [از مهمان کردن‌] مردم بیگانه منع نکردیم؟»'),
(1873, 15, 71, 'گفت: «اگر می‌خواهید [کاری مشروع‌] انجام دهید، اینان دختران منند [با آنان ازدواج کنید].»'),
(1874, 15, 72, 'به جان تو سوگند، که آنان در مستی خود سرگردان بودند.'),
(1875, 15, 73, 'پس به هنگام طلوع آفتاب، فریاد [مرگبار] آنان را فرو گرفت.'),
(1876, 15, 74, 'و آن [شهر] را زیر و زبر کردیم و بر آنان سنگهایی از سنگ گِل باراندیم.'),
(1877, 15, 75, 'به یقین، در این [کیفر] برای هوشیاران عبرتهاست.'),
(1878, 15, 76, 'و [آثار] آن [شهر هنوز] بر سر راهی [دایر] برجاست.'),
(1879, 15, 77, 'بی‌گمان، در این برای مؤمنان عبرتی است.'),
(1880, 15, 78, 'و راستی اهل «ایکه» ستمگر بودند.'),
(1881, 15, 79, 'پس، از آنان انتقام گرفتیم، و آن دو [شهر، اکنون‌] بر سر راهی آشکاراست.'),
(1882, 15, 80, 'و اهل «حِجر» [نیز] پیامبران [ما] را تکذیب کردند.'),
(1883, 15, 81, 'و آیات خود را به آنان دادیم، و[لی‌] از آنها اعراض کردند.'),
(1884, 15, 82, 'و [برای خود] از کوهها خانه‌هایی می‌تراشیدند که در امان بمانند.'),
(1885, 15, 83, 'پس صبحدم، فریاد [مرگبار]، آنان را فرو گرفت.'),
(1886, 15, 84, 'و آنچه به دست می‌آوردند، به کارشان نخورد.'),
(1887, 15, 85, 'و ما آسمانها و زمین و آنچه را که میان آن دو است جز به حق نیافریده‌ایم، و یقیناً قیامت فرا خواهد رسید. پس به خوبی صرف نظر کن،'),
(1888, 15, 86, 'زیرا پروردگار تو همان آفریننده داناست.'),
(1889, 15, 87, 'و به راستی، به تو سبع المثانی [=سوره فاتحه‌] و قرآن بزرگ را عطا کردیم.'),
(1890, 15, 88, 'و به آنچه ما دسته‌هایی از آنان [=کافران‌] را بدان برخوردار ساخته‌ایم چشم مدوز، و بر ایشان اندوه مخور، و بال خویش برای مؤمنان فرو گستر.'),
(1891, 15, 89, 'و بگو: «من همان هشداردهنده آشکارم.»'),
(1892, 15, 90, 'همان گونه که [عذاب را] بر تقسیم‌کنندگان نازل کردیم:'),
(1893, 15, 91, 'همانان که قرآن را جزء جزء کردند [به برخی از آن عمل کردند و بعضی را رها نمودند].'),
(1894, 15, 92, 'پس سوگند به پروردگارت که از همه آنان خواهیم پرسید،'),
(1895, 15, 93, 'از آنچه انجام می‌دادند.'),
(1896, 15, 94, 'پس آنچه را بدان مأموری آشکار کن و از مشرکان روی برتاب،'),
(1897, 15, 95, 'که ما [شر] ریشخندگران را از تو برطرف خواهیم کرد.'),
(1898, 15, 96, 'همانان که با خدا معبودی دیگر قرار می‌دهند. پس به زودی [حقیقت را] خواهند دانست.'),
(1899, 15, 97, 'و قطعاً می‌دانیم که سینه تو از آنچه می‌گویند تنگ می‌شود.'),
(1900, 15, 98, 'پس با ستایش پروردگارت تسبیح گوی و از سجده‌کنندگان باش.'),
(1901, 15, 99, 'و پروردگارت را پرستش کن تا اینکه مرگ تو فرا رسد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, '[هان‌] امر خدا دررسید، پس در آن شتاب مکنید. او منزّه و فراتر است از آنچه [با وی‌] شریک می‌سازند.'),
(1903, 16, 2, 'فرشتگان را با «روح»، به فرمان خود، بر هر کس از بندگانش که بخواهد نازل می‌کند، که بیم دهید که معبودی جز من نیست. پس، از من پروا کنید.'),
(1904, 16, 3, 'آسمانها و زمین را به حق آفریده است. او فراتر است از آنچه [با وی‌] شریک می‌گردانند.'),
(1905, 16, 4, 'انسان را از نطفه‌ای آفریده است، آنگاه ستیزه‌جویی آشکار است.'),
(1906, 16, 5, 'و چارپایان را برای شما آفرید: در آنها برای شما [وسیله‌] گرمی و سودهایی است، و از آنها می‌خورید.'),
(1907, 16, 6, 'و در آنها برای شما زیبایی است، آنگاه که [آنها را] از چراگاه برمی‌گردانید، و هنگامی که [آنها را] به چراگاه می‌برید.'),
(1908, 16, 7, 'و بارهای شما را به شهری می‌بَرند که جز با مشقّت بدنها بدان نمی‌توانستید برسید. قطعاً پروردگار شما رئوف و مهربان است.'),
(1909, 16, 8, 'و اسبان و استران و خران را [آفرید] تا بر آنها سوار شوید و [برای شما] تجملی [باشد]، و آنچه را نمی‌دانید می‌آفریند.'),
(1910, 16, 9, 'و نمودن راه راست بر عهده خداست، و برخی از آن [راهها] کژ است، و اگر [خدا] می‌خواست مسلماً همه شما را هدایت می‌کرد.'),
(1911, 16, 10, 'اوست کسی که از آسمان، آبی فرود آورد که [آب‌] آشامیدنی شما از آن است، و روییدنی‌[هایی‌] که [رمه‌های خود را] در آن می‌چرانید [نیز] از آن است.'),
(1912, 16, 11, 'به وسیله آن، کشت و زیتون و درختان خرما و انگور و از هر گونه محصولات [دیگر] برای شما می‌رویاند. قطعاً در اینها برای مردمی که اندیشه می‌کنند نشانه‌ای است.'),
(1913, 16, 12, 'و شب و روز و خورشید و ماه را برای شما رام گردانید، و ستارگان به فرمان او مسخر شده‌اند. مسلماً در این [امور] برای مردمی که تعقل می‌کنند نشانه‌هاست.'),
(1914, 16, 13, 'و [همچنین‌] آنچه را در زمین به رنگهای گوناگون برای شما پدید آورد [مسخّر شما ساخت‌]. بی‌تردید، در این [امور] برای مردمی که پند می‌گیرند نشانه‌ای است.'),
(1915, 16, 14, 'و اوست کسی که دریا را مسخر گردانید تا از آن گوشت تازه بخورید، و پیرایه‌ای که آن را می‌پوشید از آن بیرون آورید. و کشتیها را در آن، شکافنده [آب‌] می‌بینی، و تا از فضل او بجویید و باشد که شما شکر گزارید.'),
(1916, 16, 15, 'و در زمین کوههایی استوار افکند تا شما را نجنباند، و رودها و راهها [قرار داد] تا شما راه خود را پیدا کنید.'),
(1917, 16, 16, 'و نشانه‌هایی [دیگر نیز قرار داد]، و آنان به وسیله ستاره [قطبی‌] راه‌یابی می‌کنند.'),
(1918, 16, 17, 'پس آیا کسی که می‌آفریند چون کسی است که نمی‌آفریند؟ آیا پند نمی‌گیرید؟'),
(1919, 16, 18, 'و اگر نعمت‌[های‌] خدا را شماره کنید، آن را نمی‌توانید بشمارید. قطعاً خدا آمرزنده مهربان است.'),
(1920, 16, 19, 'و خدا آنچه را که پنهان می‌دارید و آنچه را که آشکار می‌سازید می‌داند.'),
(1921, 16, 20, 'و کسانی را که جز خدا می‌خوانند، چیزی نمی‌آفرینند در حالی که خود آفریده می شوند.'),
(1922, 16, 21, 'مردگانند نه زندگان، و نمی‌دانند کِی برانگیخته خواهند شد.'),
(1923, 16, 22, 'معبود شما معبودی است یگانه. پس کسانی که به آخرت ایمان ندارند، دلهایشان انکارکننده [حق‌] است و خودشان متکبرند.'),
(1924, 16, 23, 'شک نیست که خداوند آنچه را پنهان می‌دارند و آنچه را آشکار می‌سازند، می‌داند، و او گردنکشان را دوست نمی‌دارد.'),
(1925, 16, 24, 'و چون به آنان گفته شود: «پروردگارتان چه چیز نازل کرده است؟» می‌گویند: «افسانه‌های پیشینیان است.»'),
(1926, 16, 25, 'تا روز قیامت بار گناهان خود را تمام بردارند، و [نیز] بخشی از بار گناهان کسانی را که ندانسته آنان را گمراه می‌کنند. آگاه باشید، چه بد باری را می‌کشند.'),
(1927, 16, 26, 'پیش از آنان کسانی بودند که مکر کردند، و[لی‌] خدا از پایه بر بنیانشان زد، درنتیجه از بالای سرشان سقف بر آنان فرو ریخت، و از آنجا که حدس نمی‌زدند عذاب به سراغشان آمد.'),
(1928, 16, 27, 'سپس روز قیامت آنان را رسوا می‌کند و می‌گوید: «کجایند آن شریکان من که در باره آنها [با پیامبران‌] مخالفت می‌کردید؟» کسانی که به آنان علم داده شده است می‌گویند: «در حقیقت، امروز رسوایی و خواری بر کافران است.»'),
(1929, 16, 28, 'همانان که فرشتگان جانشان را می‌گیرند در حالی که بر خود ستمکار بوده‌اند. پس، از درِ تسلیم درمی‌آیند [و می‌گویند:] «ما هیچ کار بدی نمی‌کردیم.» آری، خدا به آنچه می‌کردید داناست.'),
(1930, 16, 29, 'پس، از درهای دوزخ وارد شوید و در آن همیشه بمانید، و حقا که چه بد است جایگاه متکبران.'),
(1931, 16, 30, 'و به کسانی که تقوا پیشه کردند، گفته شود: «پروردگارتان چه نازل کرد؟» می گویند: «خوبی.» برای کسانی که در این دنیا نیکی کردند [پاداش‌] نیکویی است، و قطعاً سرای آخرت بهتر است، و چه نیکوست سرای پرهیزگاران:'),
(1932, 16, 31, 'بهشتهای عدن که در آن داخل می‌شوند؛ رودها از زیر [درختان‌] آنها روان است؛ در آنجا هر چه بخواهند برای آنان [فراهم‌] است. خدا این گونه پرهیزگاران را پاداش می‌دهد.'),
(1933, 16, 32, 'همان کسانی که فرشتگان جانشان را -در حالی که پاکند- می‌ستانند [و به آنان‌] می‌گویند: «درود بر شما باد، به [پاداش‌] آنچه انجام می‌دادید به بهشت درآیید.»'),
(1934, 16, 33, 'آیا [کافران‌] جز این که فرشتگانِ [جان‌ستان‌] به سویشان آیند، یا فرمان پروردگارت [دایر بر عذابشان‌] دررسد، انتظاری می‌برند؟ کسانی که پیش از آنان بودند [نیز] این گونه رفتار کردند، و خدا به ایشان ستم نکرد، بلکه آنان به خود ستم می‌کردند.'),
(1935, 16, 34, 'پس [کیفر] بدیهایی که کردند به آنان رسید، و آنچه مسخره‌اش می‌کردند آنان را فرا گرفت.'),
(1936, 16, 35, 'و کسانی که شرک ورزیدند گفتند: «اگر خدا می‌خواست -نه ما و نه پدرانمان- هیچ چیزی را غیر از او نمی‌پرستیدیم و بدون [حکم‌] او چیزی را حرام نمی‌شمردیم.» پیش از آنان [نیز] چنین رفتار کردند، و[لی‌] آیا جز ابلاغ آشکار بر پیامبران [وظیفه‌ای‌] است؟'),
(1937, 16, 36, 'و در حقیقت، در میان هر امتی فرستاده‌ای برانگیختیم [تا بگوید:] «خدا را بپرستید و از طاغوت [=فریبگر] بپرهیزید.» پس، از ایشان کسی است که خدا [او را] هدایت کرده، و از ایشان کسی است که گمراهی بر او سزاوار است. بنابراین در زمین بگردید و ببینید فرجام تکذیب‌کنندگان چگونه بوده است.'),
(1938, 16, 37, 'اگر [چه‌] بر هدایت آنان حرص ورزی، ولی خدا کسی را که فرو گذاشته است هدایت نمی‌کند، و برای ایشان یاری‌کنندگانی نیست.'),
(1939, 16, 38, 'و با سخت‌ترین سوگندهایشان بخدا سوگند یاد کردند که خدا کسی را که می‌میرد بر نخواهد انگیخت. آری، [انجام‌] این وعده بر او حق است، لیکن بیشتر مردم نمی‌دانند.'),
(1940, 16, 39, 'تا [خدا] آنچه را در [مورد] آن اختلاف دارند، برای آنان توضیح دهد، و تا کسانی که کافر شده‌اند، بدانند که آنها خود دروغ می‌گفته‌اند.'),
(1941, 16, 40, 'ما وقتی چیزی را اراده کنیم، همین قدر به آن می‌گوییم: «باش»، بی‌درنگ موجود می‌شود.'),
(1942, 16, 41, 'و کسانی که پس از ستمدیدگی، در راه خدا هجرت کرده‌اند، در این دنیا جای نیکویی به آنان می‌دهیم، و اگر بدانند، قطعاً پاداش آخرت بزرگتر خواهد بود.'),
(1943, 16, 42, 'همانان که صبر نمودند و بر پروردگارشان توکل می‌کنند.'),
(1944, 16, 43, 'و پیش از تو [هم‌] جز مردانی که بدیشان وحی می‌کردیم گسیل نداشتیم. پس اگر نمی‌دانید، از پژوهندگان کتابهای آسمانی جویا شوید،'),
(1945, 16, 44, '[زیرا آنان را] با دلایل آشکار و نوشته‌ها [فرستادیم‌]، و این قرآن را به سوی تو فرود آوردیم، تا برای مردم آنچه را به سوی ایشان نازل شده است توضیح دهی، و امید که آنان بیندیشند.'),
(1946, 16, 45, 'آیا کسانی که تدبیرهای بد می‌اندیشند، ایمن شدند از اینکه خدا آنان را در زمین فرو ببرد، یا از جایی که حدس نمی‌زنند عذاب برایشان بیاید؟'),
(1947, 16, 46, 'یا در حال رفت و آمدشان [گریبان‌] آنان را بگیرد، و کاری از دستشان برنیاید؟'),
(1948, 16, 47, 'یا آنان را در حالی که وحشت‌زده‌اند فرو گیرد؟ همانا پروردگار شما رئوف و مهربان است.'),
(1949, 16, 48, 'آیا به چیزهایی که خدا آفریده است، ننگریسته‌اند که [چگونه‌] سایه‌هایشان از راست و [از جوانب‌] چپ می‌گردد، و برای خدا در حال فروتنی سر بر خاک می‌سایند؟'),
(1950, 16, 49, 'و آنچه در آسمانها و آنچه در زمین از جنبندگان و فرشتگان است، برای خدا سجده می‌کنند و تکبر نمی‌ورزند.'),
(1951, 16, 50, 'از پروردگارشان که حاکم بر آنهاست می‌ترسند و آنچه را مأمورند انجام می‌دهند.'),
(1952, 16, 51, 'و خدا فرمود: «دو معبود برای خود مگیرید. جز این نیست که او خدایی یگانه است. پس تنها از من بترسید.»'),
(1953, 16, 52, 'و آنچه در آسمانها و زمین است از آنِ اوست، و آیین پایدار [نیز] از آنِ اوست. پس آیا از غیر خدا پروا دارید؟'),
(1954, 16, 53, 'و هر نعمتی که دارید از خداست، سپس چون آسیبی به شما رسد به سوی او روی می آورید [و می‌نالید].'),
(1955, 16, 54, 'و چون آن آسیب را از شما برطرف کرد، آنگاه گروهی از شما به پروردگارشان شرک می‌ورزند.'),
(1956, 16, 55, '[بگذار] تا آنچه را به ایشان عطا کرده‌ایم ناسپاسی کنند. اکنون برخوردار شوید، و[لی‌] زودا که بدانید.'),
(1957, 16, 56, 'و از آنچه به ایشان روزی دادیم، نصیبی برای آن [خدایانی‌] که نمی‌دانند [چیست‌] می‌نهند. به خدا سوگند که از آنچه به دروغ برمی‌بافتید، حتماً سؤال خواهید شد.'),
(1958, 16, 57, 'و برای خدا دخترانی می‌پندارند. منزه است او. و برای خودشان آنچه را میل دارند [قرار می‌دهند].'),
(1959, 16, 58, 'و هر گاه یکی از آنان را به دختر مژده آورند، چهره‌اش سیاه می‌گردد، در حالی که خشم [و اندوه‌] خود را فرو می‌خورد.'),
(1960, 16, 59, 'از بدی آنچه بدو بشارت داده شده، از قبیله [خود] روی می‌پوشاند. آیا او را با خواری نگاه دارد، یا در خاک پنهانش کند؟ وه چه بد داوری می‌کنند.'),
(1961, 16, 60, 'وصف زشت برای کسانی است که به آخرت ایمان ندارند، و بهترین وصف از آنِ خداست، و اوست ارجمند حکیم.'),
(1962, 16, 61, 'و اگر خداوند مردم را به [سزای‌] ستمشان مؤاخذه می‌کرد، جنبنده‌ای بر روی زمین باقی نمی‌گذاشت، لیکن [کیفر] آنان را تا وقتی معین بازپس می‌اندازد، و چون اجلشان فرا رسد، ساعتی آن را پس و پیش نمی‌توانند افکنند.'),
(1963, 16, 62, 'و چیزی را که خوش نمی‌دارند، برای خدا قرار می‌دهند، و زبانشان دروغ‌پردازی می‌کند که [سرانجام‌] نیکو از آنِ ایشان است. حقاً که آتش برای آنان است و به سوی آن پیش فرستاده خواهند شد.'),
(1964, 16, 63, 'سوگند به خدا که به سوی امتهای پیش از تو [رسولانی‌] فرستادیم [اما] شیطان اعمالشان را برایشان آراست و امروز [هم‌] سرپرستشان هموست و برایشان عذابی دردناک است.'),
(1965, 16, 64, 'و ما [این‌] کتاب را بر تو نازل نکردیم، مگر برای اینکه آنچه را در آن اختلاف کرده‌اند، برای آنان توضیح دهی، و [آن‌] برای مردمی که ایمان می‌آورند، رهنمود و رحمتی است.'),
(1966, 16, 65, 'و خدا از آسمان آبی فرود آورد و با آن زمین را پس از پژمردنش زنده گردانید، قطعاً در این [امر] برای مردمی که شنوایی دارند نشانه‌ای است.'),
(1967, 16, 66, 'و در دامها قطعاً برای شما عبرتی است: از آنچه در [لابلای‌] شکم آنهاست، از میان سرگین و خون، شیری ناب به شما می‌نوشانیم که برای نوشندگان گواراست.'),
(1968, 16, 67, 'و از میوه درختان خرما و انگور، باده مستی‌بخش و خوراکی نیکو برای خود می گیرید. قطعاً در این‌[ها] برای مردمی که تعقل می‌کنند نشانه‌ای است.'),
(1969, 16, 68, 'و پروردگار تو به زنبور عسل وحی [=الهام غریزی‌] کرد که از پاره‌ای کوهها و از برخی درختان و از آنچه داربست [و چفته‌سازی‌] می‌کنند، خانه‌هایی برای خود درست کن،'),
(1970, 16, 69, 'سپس از همه میوه‌ها بخور، و راههای پروردگارت را فرمانبردارانه، بپوی. [آنگاه‌] از درون [شکم‌] آن، شهدی که به رنگهای گوناگون است بیرون می‌آید. در آن، برای مردم درمانی است. راستی در این [زندگی زنبوران‌] برای مردمی که تفکر می‌کنند نشانه [قدرت الهی‌] است.'),
(1971, 16, 70, 'و خدا شما را آفرید، سپس [جان‌] شما را می‌گیرد، و بعضی از شما تا خوارترین [دوره‌] سالهای زندگی [فرتوتی‌] بازگردانده می‌شود، به طوری که بعد از [آن همه‌] دانستن، [دیگر] چیزی نمی‌دانند. قطعاً خدا دانای تواناست.'),
(1972, 16, 71, 'و خدا بعضی از شما را در روزی بر بعضی دیگر برتری داده است. و[لی‌] کسانی که فزونی یافته‌اند، روزیِ خود را به بندگان خود نمی‌دهند تا در آن با هم مساوی باشند. آیا باز نعمت خدا را انکار می‌کنند؟'),
(1973, 16, 72, 'و خدا برای شما از خودتان همسرانی قرار داد، و از همسرانتان برای شما پسران و نوادگانی نهاد و از چیزهای پاکیزه به شما روزی بخشید. آیا [باز هم‌] به باطل ایمان می‌آورند و به نعمت خدا کفر می‌ورزند؟'),
(1974, 16, 73, 'و به جای خدا چیزهایی را می‌پرستند که در آسمانها و زمین به هیچ وجه اختیار روزی آنان را ندارند و [به کاری‌] توانایی ندارند.'),
(1975, 16, 74, 'پس برای خدا مَثَل نزنید، که خدا می‌داند و شما نمی‌دانید.'),
(1976, 16, 75, 'خدا مَثَلی می‌زند: بنده‌ای است زرخرید که هیچ کاری از او برنمی‌آید. آیا [او] با کسی که به وی از جانب خود روزی نیکو داده‌ایم، و او از آن در نهان و آشکار انفاق می‌کند یکسان است؟ سپاس خدای راست. [نه،] بلکه بیشترشان نمی‌دانند.'),
(1977, 16, 76, 'و خدا مَثَلی [دیگر] می‌زند: دو مردند که یکی از آنها لال است و هیچ کاری از او برنمی‌آید و او سربار خداوندگارش می‌باشد. هر جا که او را می‌فرستد خیری به همراه نمی‌آورد. آیا او با کسی که به عدالت فرمان می‌دهد و خود بر راه راست است یکسان است؟'),
(1978, 16, 77, 'و نهان آسمانها و زمین از آنِ خداست، و کار قیامت جز مانند یک چشم بر هم زدن یا نزدیکتر [از آن‌] نیست، زیرا خدا بر هر چیزی تواناست.'),
(1979, 16, 78, 'و خدا شما را از شکم مادرانتان -در حالی که چیزی نمی‌دانستید- بیرون آورد، و برای شما گوش و چشمها و دلها قرار داد، باشد که سپاسگزاری کنید.'),
(1980, 16, 79, 'آیا به سوی پرندگانی که در فضای آسمان، رام شده‌اند ننگریسته‌اند؟ جز خدا کسی آنها را نگاه نمی‌دارد. راستی در این [قدرت‌نمایی‌] برای مردمی که ایمان می‌آورند نشانه‌هایی است.'),
(1981, 16, 80, 'و خدا برای شما خانه‌هایتان را مایه آرامش قرار داد، و از پوست دامها برای شما خانه‌هایی نهاد که آن‌[ها] را در روز جابجا شدنتان و هنگام ماندنتان سبک می‌یابید، و از پشمها و کُرکها و موهای آنها وسایل زندگی که تا چندی مورد استفاده است [قرار داد].'),
(1982, 16, 81, 'و خدا از آنچه آفریده، به سود شما سایه‌هایی فراهم آورده و از کوهها برای شما پناهگاه‌هایی قرار داده و برای شما تن‌پوشهایی مقرّر کرده که شما را از گرما [و سرما] حفظ می‌کند، و تن‌پوشها [=زره‌ها]یی که شما را در جنگتان حمایت می‌نماید. این گونه وی نعمتش را بر شما تمام می‌گرداند، امید که شما [به فرمانش‌] گردن نهید.'),
(1983, 16, 82, 'پس اگر رویگردان شوند، بر تو فقط ابلاغ آشکار است.'),
(1984, 16, 83, 'نعمت خدا را می‌شناسند، اما باز هم منکر آن می‌شوند و بیشترشان کافرند.'),
(1985, 16, 84, 'و [یاد کن‌] روزی را که از هر امّتی گواهی برمی‌انگیزیم، سپس به کسانی که کافر شده‌اند رخصت داده نمی‌شود و آنان مورد بخشش قرار نخواهند گرفت.'),
(1986, 16, 85, 'و چون کسانی که ستم کرده‌اند عذاب را ببینند [شکنجه‌] آنان کاسته نمی‌گردد و مهلت نمی‌یابند.'),
(1987, 16, 86, 'و چون کسانی که شرک ورزیدند، شریکان خود را ببینند می‌گویند: «پروردگارا، اینها بودند آن شریکانی که ما به جای تو می‌خواندیم». و[لی شریکان‌] قول آنان را رد می‌کنند که: «شما جدا دروغگویانید.»'),
(1988, 16, 87, 'و آن روز در برابر خدا از در تسلیم درآیند و آنچه را که برمی‌بافتند بر باد می‌رود.'),
(1989, 16, 88, 'کسانی که کفر ورزیدند و از راه خدا باز داشتند به [سزای‌] آنکه فساد می‌کردند عذابی بر عذابشان می‌افزاییم.'),
(1990, 16, 89, 'و [به یاد آور] روزی را که در هر امتی گواهی از خودشان برایشان برانگیزیم، و تو را [هم‌] بر این [امت‌] گواه آوریم، و این کتاب را که روشنگر هر چیزی است و برای مسلمانان رهنمود و رحمت و بشارتگری است، بر تو نازل کردیم.'),
(1991, 16, 90, 'در حقیقت، خدا به دادگری و نیکوکاری و بخشش به خویشاوندان فرمان می‌دهد و از کار زشت و ناپسند و ستم باز می‌دارد. به شما اندرز می‌دهد، باشد که پند گیرید.'),
(1992, 16, 91, 'و چون با خدا پیمان بستید، به پیمان خود وفا کنید و سوگندهای [خود را] پس از استوار کردن آنها مشکنید، با اینکه خدا را بر خود ضامن [و گواه‌] قرار داده‌اید، زیرا خدا آنچه را انجام می‌دهید می‌داند.'),
(1993, 16, 92, 'و مانند آن [زنی‌] که رشته خود را پس از محکم بافتن، [یکی یکی‌] از هم می‌گسست مباشید که سوگندهای خود را میان خویش وسیله [فریب و] تقلّب سازید [به خیال این‌] که گروهی از گروه دیگر [در داشتن امکانات‌] افزونترند. جز این نیست که خدا شما را بدین وسیله می‌آزماید و روز قیامت در آنچه اختلاف می‌کردید، قطعاً برای شما توضیح خواهد داد.'),
(1994, 16, 93, 'و اگر خدا می‌خواست قطعاً شما را امتی واحد قرار می‌داد، ولی هر که را بخواهد بیراه و هر که را بخواهد هدایت می‌کند و از آنچه انجام می‌دادید حتماً سؤال خواهید شد.'),
(1995, 16, 94, 'و زنهار، سوگندهای خود را دستاویز تقلّب میان خود قرار مدهید، تا گامی بعد از استواریش بلغزد، و شما به [سزای‌] آنکه [مردم را] از راه خدا باز داشته‌اید دچار شکنجه شوید و برای شما عذابی بزرگ باشد.'),
(1996, 16, 95, 'و پیمان خدا را به بهای ناچیزی مفروشید، زیرا آنچه نزد خداست -اگر بدانید- همان برای شما بهتر است.'),
(1997, 16, 96, 'آنچه پیش شماست تمام می‌شود و آنچه پیش خداست پایدار است، و قطعاً کسانی را که شکیبایی کردند به بهتر از آنچه عمل می‌کردند، پاداش خواهیم داد.'),
(1998, 16, 97, 'هر کس -از مرد یا زن- کار شایسته کند و مؤمن باشد، قطعاً او را با زندگی پاکیزه‌ای، حیات [حقیقی‌] بخشیم، و مسلماً به آنان بهتر از آنچه انجام می‌دادند پاداش خواهیم داد.'),
(1999, 16, 98, 'پس چون قرآن می‌خوانی از شیطان مطرود به خدا پناه بر،'),
(2000, 16, 99, 'چرا که او را بر کسانی که ایمان آورده‌اند، و بر پروردگارشان توکل می‌کنند، تسلّطی نیست.'),
(2001, 16, 100, 'تسلّط او فقط بر کسانی است که وی را به سرپرستی برمی‌گیرند، و بر کسانی که آنها به او [=خدا] شرک می‌ورزند.'),
(2002, 16, 101, 'و چون حکمی را به جای حکم دیگر بیاوریم -و خدا به آنچه به تدریج نازل می‌کند داناتر است- می‌گویند: «جز این نیست که تو دروغ‌بافی.» [نه،] بلکه بیشتر آنان نمی‌دانند.'),
(2003, 16, 102, 'بگو: «آن را روح القدس از طرف پروردگارت به حق فرود آورده، تا کسانی را که ایمان آورده‌اند استوار گرداند، و برای مسلمانان هدایت و بشارتی است. «'),
(2004, 16, 103, 'و نیک می‌دانیم که آنان می‌گویند: «جز این نیست که بشری به او می‌آموزد.» [نه چنین نیست، زیرا] زبان کسی که [این‌] نسبت را به او می‌دهند غیر عربی است و این [قرآن‌] به زبان عربی روشن است.'),
(2005, 16, 104, 'در حقیقت، کسانی که به آیات خدا ایمان ندارند، خدا آنان را هدایت نمی‌کند و برایشان عذابی دردناک است.'),
(2006, 16, 105, 'تنها کسانی دروغ‌پردازی می‌کنند که به آیات خدا ایمان ندارند و آنان خود دروغگویانند.'),
(2007, 16, 106, 'هر کس پس از ایمان آوردن خود، به خدا کفر ورزد [عذابی سخت خواهد داشت‌] مگر آن کس که مجبور شده و[لی‌] قلبش به ایمان اطمینان دارد. لیکن هر که سینه‌اش به کفر گشاده گردد خشم خدا بر آنان است و برایشان عذابی بزرگ خواهد بود،'),
(2008, 16, 107, 'زیرا آنان زندگی دنیا را بر آخرت برتری دادند و [هم‌] اینکه خدا گروه کافران را هدایت نمی‌کند.'),
(2009, 16, 108, 'آنان کسانی‌اند که خدا بر دلها و گوش و دیدگانشان مُهر نهاده و آنان خود غافلانند.'),
(2010, 16, 109, 'شک نیست که آنها در آخرت همان زیانکارانند.'),
(2011, 16, 110, 'با این حال، پروردگار تو نسبت به کسانی که پس از [آن همه‌] زجر کشیدن، هجرت کرده و سپس جهاد نمودند و صبر پیشه ساختند، پروردگارت [نسبت به آنان‌] بعد از آن [همه مصایب‌] قطعاً آمرزنده و مهربان است.'),
(2012, 16, 111, '[یاد کن‌] روزی را که هر کس می‌آید [و] از خود دفاع می‌کند، و هر کس به آنچه کرده، بی کم و کاست پاداش می‌یابد و بر آنان ستم نمی‌رود.'),
(2013, 16, 112, 'و خدا شهری را مَثَل زده است که امن و امان بود [و] روزیش از هر سو فراوان می‌رسید، پس [ساکنانش‌] نعمتهای خدا را ناسپاسی کردند، و خدا هم به سزای آنچه انجام می‌دادند، طعم گرسنگی و هراس را به [مردم‌] آن چشانید.'),
(2014, 16, 113, 'و به یقین، فرستاده‌ای از خودشان برایشان آمد، اما او را تکذیب کردند، پس در حالی که ظالم بودند آنان را عذاب فرو گرفت.'),
(2015, 16, 114, 'پس، از آنچه خدا شما را روزی کرده است، حلال [و] پاکیزه بخورید، و نعمت خدا را -اگر تنها او را می‌پرستید- شکر گزارید.'),
(2016, 16, 115, 'جز این نیست که [خدا] مردار و خون و گوشت خوک و آنچه را که نام غیر خدا بر آن برده شده حرام گردانیده است. [با این همه،] هر کس که [به خوردن آنها] ناگزیر شود، و سرکش و زیاده‌خواه نباشد، قطعاً خدا آمرزنده مهربان است.'),
(2017, 16, 116, 'و برای آنچه زبان شما به دروغ می‌پردازد، مگویید: «این حلال است و آن حرام » تا بر خدا دروغ بندید، زیرا کسانی که بر خدا دروغ می‌بندند رستگار نمی‌شوند.'),
(2018, 16, 117, '[ایشان راست‌] اندک بهره‌ای، و[لی‌] عذابشان پر درد است.'),
(2019, 16, 118, 'و بر کسانی که یهودی شدند، آنچه را قبلا بر تو حکایت کردیم، حرام گردانیدیم، و ما بر آنان ستم نکردیم، بلکه آنها به خود ستم می‌کردند.'),
(2020, 16, 119, '[با این همه‌] پروردگار تو نسبت به کسانی که به نادانی مرتکب گناه شده، سپس توبه کرده و به صلاح آمده‌اند، البته پروردگارت پس از آن آمرزنده مهربان است.'),
(2021, 16, 120, 'به راستی ابراهیم، پیشوایی مطیع خدا [و] حق‌گرای بود و از مشرکان نبود.'),
(2022, 16, 121, '[و] نعمتهای او را شکرگزار بود. [خدا] او را برگزید و به راهی راست هدایتش کرد.'),
(2023, 16, 122, 'و در دنیا به او نیکویی و [نعمت‌] دادیم و در آخرت [نیز] از شایستگان خواهد بود.'),
(2024, 16, 123, 'سپس به تو وحی کردیم که: «از آیین ابراهیم حق‌گرای پیروی کن، [چرا که‌] او از مشرکان نبود.»'),
(2025, 16, 124, '[بزرگداشت‌] شنبه، بر کسانی که در باره آن اختلاف کردند مقرّر گردید، و قطعاً پروردگارت روز رستاخیز میان آنها در باره چیزی که در مورد آن اختلاف می‌کردند، داوری خواهد کرد.'),
(2026, 16, 125, 'با حکمت و اندرز نیکو به راه پروردگارت دعوت کن و با آنان به [شیوه‌ای‌] که نیکوتر است مجادله نمای. در حقیقت، پروردگار تو به [حال‌] کسی که از راه او منحرف شده داناتر، و او به [حال‌] راه‌یافتگان [نیز ]داناتر است.'),
(2027, 16, 126, 'و اگر عقوبت کردید، همان گونه که مورد عقوبت قرار گرفته‌اید [متجاوز را ] به عقوبت رسانید، و اگر صبر کنید البته آن برای شکیبایان بهتر است.'),
(2028, 16, 127, 'و صبر کن و صبر تو جز به [توفیق‌] خدا نیست و بر آنان اندوه مخور و از آنچه نیرنگ می‌کنند دل تنگ مدار.'),
(2029, 16, 128, 'در حقیقت، خدا با کسانی است که پروا داشته‌اند و [با] کسانی [است‌] که آنها نیکوکارند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزّه است آن [خدایی‌] که بنده‌اش را شبانگاهی از مسجد الحرام به سوی مسجد الاقصی -که پیرامون آن را برکت داده‌ایم- سیر داد، تا از نشانه‌های خود به او بنمایانیم، که او همان شنوای بیناست.'),
(2031, 17, 2, 'و کتاب آسمانی را به موسی دادیم و آن را برای فرزندان اسرائیل رهنمودی گردانیدیم که: زنهار، غیر از من کارسازی مگیرید،'),
(2032, 17, 3, '[ای‌] فرزندان کسانی که [آنان را در کِشتی‌] با نوح برداشتیم. راستی که او بنده‌ای سپاسگزار بود.'),
(2033, 17, 4, 'و در کتاب آسمانی‌[شان‌] به فرزندان اسرائیل خبر دادیم که: «قطعاً دو بار در زمین فساد خواهید کرد، و قطعاً به سرکشیِ بسیار بزرگی برخواهید خاست. »'),
(2034, 17, 5, 'پس آنگاه که وعده [تحقّق‌] نخستین آن دو فرا رسد، بندگانی از خود را که سخت نیرومندند بر شما می‌گماریم، تا میان خانه‌ها[یتان برای قتل و غارت شما] به جستجو درآیند، و این تهدید تحقّق‌یافتنی است.'),
(2035, 17, 6, 'پس [از چندی‌] دوباره شما را بر آنان چیره می‌کنیم و شما را با اموال و پسران یاری می‌دهیم و [تعداد] نفرات شما را بیشتر می‌گردانیم.'),
(2036, 17, 7, 'اگر نیکی کنید، به خود نیکی کرده‌اید، و اگر بدی کنید، به خود [بد نموده‌اید]. و چون تهدید آخر فرا رسد [بیایند] تا شما را اندوهگین کنند و در معبد[تان‌] چنانکه بار اول داخل شدند [به زور] درآیند و بر هر چه دست یافتند یکسره [آن را] نابود کنند.'),
(2037, 17, 8, 'امید است که پروردگارتان شما را رحمت کند، و[لی‌] اگر [به گناه‌] بازگردید [ما نیز به کیفر شما] بازمی‌گردیم، و دوزخ را برای کافران زندان قرار دادیم.'),
(2038, 17, 9, 'قطعاً این قرآن به [آیینی‌] که خود پایدارتر است راه می‌نماید، و به آن مؤمنانی که کارهای شایسته می‌کنند، مژده می‌دهد که پاداشی بزرگ برایشان خواهد بود.'),
(2039, 17, 10, 'و اینکه برای کسانی که به آخرت ایمان نمی‌آورند عذابی پر درد آماده کرده‌ایم.'),
(2040, 17, 11, 'و انسان [همان گونه که‌] خیر را فرا می‌خواند، [پیشامد] بد را می‌خواند و انسان همواره شتابزده است.'),
(2041, 17, 12, 'و شب و روز را دو نشانه قرار دادیم. نشانه شب را تیره‌گون و نشانه روز را روشنی‌بخش گردانیدیم تا [در آن،] فضلی از پروردگارتان بجویید، و تا شماره سالها و حساب [عمرها و رویدادها] را بدانید و هر چیزی را به روشنی باز نمودیم.'),
(2042, 17, 13, 'و کارنامه هر انسانی را به گردن او بسته‌ایم، و روز قیامت برای او نامه‌ای که آن را گشاده می‌بیند بیرون می‌آوریم.'),
(2043, 17, 14, '«نامه‌ات را بخوان؛ کافی است که امروز خودت حسابرس خود باشی.»'),
(2044, 17, 15, 'هر کس به راه آمده تنها به سود خود به راه آمده، و هر کس بیراهه رفته تنها به زیان خود بیراهه رفته است. و هیچ بردارنده‌ای بار گناه دیگری را بر نمی‌دارد، و ما تا پیامبری برنینگیزیم، به عذاب نمی‌پردازیم.'),
(2045, 17, 16, 'و چون بخواهیم شهری را هلاک کنیم، خوشگذرانانش را وا می‌داریم تا در آن به انحراف [و فساد] بپردازند، و در نتیجه عذاب بر آن [شهر] لازم گردد، پس آن را [یکسره‌] زیر و زبر کنیم.'),
(2046, 17, 17, 'و چه بسیار نسلها را که ما پس از نوح به هلاکت رساندیم، و پروردگار تو به گناهان بندگانش بس آگاه و بیناست.'),
(2047, 17, 18, 'هر کس خواهان [دنیای‌] زودگذر است، به زودی هر که را خواهیم [نصیبی‌] از آن می‌دهیم، آنگاه جهنم را که در آن خوار و رانده داخل خواهد شد، برای او مقرر می‌داریم.'),
(2048, 17, 19, 'و هر کس خواهان آخرت است و نهایت کوشش را برای آن بکند و مؤمن باشد، آنانند که تلاش آنها مورد حق‌شناسی واقع خواهد شد.'),
(2049, 17, 20, 'هر دو [دسته:] اینان و آنان را از عطای پروردگارت مدد می‌بخشیم، و عطای پروردگارت [از کسی‌] منع نشده است.'),
(2050, 17, 21, 'ببین چگونه بعضی از آنان را بر بعضی دیگر برتری داده‌ایم، و قطعاً درجات آخرت و برتری آن بزرگتر و بیشتر است.'),
(2051, 17, 22, 'معبود دیگری با خدا قرار مده تا نکوهیده و وامانده بنشینی.'),
(2052, 17, 23, 'و پروردگار تو مقرر کرد که جز او را مپرستید و به پدر و مادر [خود] احسان کنید. اگر یکی از آن دو یا هر دو، در کنار تو به سالخوردگی رسیدند به آنها [حتی‌] «اوف» مگو و به آنان پَرخاش مکن و با آنها سخنی شایسته بگوی.'),
(2053, 17, 24, 'و از سر مهربانی، بالِ فروتنی بر آنان بگستر و بگو: «پروردگارا، آن دو را رحمت کن چنانکه مرا در خردی پروردند.»'),
(2054, 17, 25, 'پروردگار شما به آنچه در دلهای خود دارید آگاه‌تر است. اگر شایسته باشید، قطعاً او آمرزنده توبه‌کنندگان است.'),
(2055, 17, 26, 'و حق خویشاوند را به او بده و مستمند و در راه‌مانده را [دستگیری کن‌] و ولخرجی و اسراف مکن.'),
(2056, 17, 27, 'چرا که اسرافکاران برادران شیطانهایند، و شیطان همواره نسبت به پروردگارش ناسپاس بوده است.'),
(2057, 17, 28, 'و اگر به امید رحمتی که از پروردگارت جویای آنی، از ایشان روی می‌گردانی، پس با آنان سخنی نرم بگوی.'),
(2058, 17, 29, 'و دستت را به گردنت زنجیر مکن و بسیار [هم‌] گشاده‌دستی منما تا ملامت‌شده و حسرت‌زده بر جای مانی.'),
(2059, 17, 30, 'بی‌گمان، پروردگار تو برای هر که بخواهد، روزی را گشاده یا تنگ می‌گرداند. در حقیقت، او به [حال‌] بندگانش آگاه بیناست.'),
(2060, 17, 31, 'و از بیم تنگدستی فرزندان خود را مکشید. ماییم که به آنها و شما روزی می‌بخشیم. آری، کشتن آنان همواره خطایی بزرگ است.'),
(2061, 17, 32, 'و به زنا نزدیک مشوید، چرا که آن همواره زشت و بد راهی است.'),
(2062, 17, 33, 'و نفسی را که خداوند حرام کرده است جز به حق مکشید، و هر کس مظلوم کشته شود، به سرپرست وی قدرتی داده‌ایم، پس [او] نباید در قتل زیاده‌روی کند، زیرا او [از طرف شرع‌] یاری شده است.'),
(2063, 17, 34, 'و به مال یتیم -جز به بهترین وجه- نزدیک مشوید تا به رشد برسد، و به پیمان [خود] وفا کنید، زیرا که از پیمان پرسش خواهد شد.'),
(2064, 17, 35, 'و چون پیمانه می‌کنید، پیمانه را تمام دهید، و با ترازوی درست بسنجید که این بهتر و خوش فرجام‌تر است.'),
(2065, 17, 36, 'و چیزی را که بدان علم نداری دنبال مکن، زیرا گوش و چشم و قلب، همه مورد پرسش واقع خواهند شد.'),
(2066, 17, 37, 'و در [روی‌] زمین به نخوت گام برمدار، چرا که هرگز زمین را نمی‌توانی شکافت، و در بلندی به کوهها نمی‌توانی رسید.'),
(2067, 17, 38, 'همه این [کارها] بدش نزد پروردگار تو ناپسندیده است.'),
(2068, 17, 39, 'این [سفارشها] از حکمتهایی است که پروردگارت به تو وحی کرده است، و با خدای یگانه معبودی دیگر قرار مده، و گرنه حسرت‌زده و مطرود در جهنم افکنده خواهی شد.'),
(2069, 17, 40, 'آیا [پنداشتید که‌] پروردگارتان شما را به [داشتن‌] پسران اختصاص داده، و خود از فرشتگان دخترانی برگرفته است؟ حقاً که شما سخنی بس بزرگ می‌گویید.'),
(2070, 17, 41, 'و به راستی، ما در این قرآن [حقایق را] گونه‌گون بیان کردیم، تا پند گیرند؛ و[لی‌] آنان را جز نفرت نمی‌افزاید.'),
(2071, 17, 42, 'بگو: «اگر - چنانکه می‌گویند- با او خدایانی [دیگر] بود، در آن صورت حتماً در صدد جستن راهی به سوی [خداوند،] صاحب عرش، برمی‌آمدند.»'),
(2072, 17, 43, 'او [پاک و] منزّه است و از آنچه می‌گویند بسی والاتر است.'),
(2073, 17, 44, 'آسمانهای هفتگانه و زمین و هر کس که در آنهاست او را تسبیح می‌گویند، و هیچ چیز نیست مگر اینکه در حال ستایش، تسبیح او می‌گوید، ولی شما تسبیح آنها را درنمی‌یابید. به راستی که او همواره بردبار [و ]آمرزنده است.'),
(2074, 17, 45, 'و چون قرآن بخوانی، میان تو و کسانی که به آخرت ایمان ندارند پرده‌ای پوشیده قرار می‌دهیم.'),
(2075, 17, 46, 'و بر دلهایشان پوششها می‌نهیم تا آن را نفهمند و در گوشهایشان سنگینی [قرار می‌دهیم‌] و چون در قرآن پروردگار خود را به یگانگی یاد کنی با نفرت پشت می‌کنند.'),
(2076, 17, 47, 'هنگامی که به سوی تو گوش فرا می‌دارند ما بهتر می‌دانیم به چه [منظور] گوش می‌دهند، و [نیز] آنگاه که به نجوا می‌پردازند؛ وقتی که ستمگران گویند: «جز مردی افسون‌شده را پیروی نمی‌کنید.»'),
(2077, 17, 48, 'ببین چگونه برای تو مَثَلها زدند و گمراه شدند، در نتیجه راه به جایی نمی‌توانند ببرند.'),
(2078, 17, 49, 'و گفتند: «آیا وقتی استخوان و خاک شدیم [باز] به آفرینشی جدید برانگیخته می‌شویم؟»'),
(2079, 17, 50, 'بگو: «سنگ باشید یا آهن،'),
(2080, 17, 51, 'یا آفریده‌ای از آنچه در خاطر شما بزرگ می‌نماید [باز هم برانگیخته خواهید شد].» پس خواهند گفت: «چه کسی ما را بازمی‌گرداند؟» بگو: «همان کس که نخستین بار شما را پدید آورد.» [باز] سرهای خود را به طرف تو تکان می‌دهند و می‌گویند: «آن کی خواهد بود؟» بگو: «شاید که نزدیک باشد.»'),
(2081, 17, 52, 'روزی که شما را فرا می‌خواند، پس در حالی که او را ستایش می‌کنید اجابتش می‌نمایید و می‌پندارید که جز اندکی [در دنیا] نمانده‌اید.'),
(2082, 17, 53, 'و به بندگانم بگو: «آنچه را که بهتر است بگویند»، که شیطان میانشان را به هم می‌زند، زیرا شیطان همواره برای انسان دشمنی آشکار است.'),
(2083, 17, 54, 'پروردگار شما به [حال‌] شما داناتر است؛ اگر بخواهد بر شما رحمت می‌آورد، یا اگر بخواهد شما را عذاب می‌کند، و تو را بر ایشان نگهبان نفرستاده‌ایم.'),
(2084, 17, 55, 'و پروردگار تو به هر که [و هر چه‌] در آسمانها و زمین است داناتر است. و در حقیقت، بعضی از انبیا را بر بعضی برتری بخشیدیم و به داوود زبور دادیم.'),
(2085, 17, 56, 'بگو: «کسانی را که به جای او [معبود خود] پنداشتید، بخوانید. [آنها] نه اختیاری دارند که از شما دفع زیان کنند و نه [آنکه بلایی را از شما] بگردانند.'),
(2086, 17, 57, 'آن کسانی را که ایشان می‌خوانند [خود] به سوی پروردگارشان تقرب می‌جویند [تا بدانند] کدام یک از آنها [به او] نزدیکترند، و به رحمت وی امیدوارند، و از عذابش می‌ترسند، چرا که عذاب پروردگارت همواره در خور پرهیز است.'),
(2087, 17, 58, 'و هیچ شهری نیست مگر اینکه ما آن را [در صورت نافرمانی،] پیش از روز رستاخیز، به هلاکت می‌رسانیم یا آن را سخت عذاب می‌کنیم. این [عقوبت‌] در کتاب [الهی‌] به قلم رفته است.'),
(2088, 17, 59, 'و [چیزی‌] ما را از فرستادن معجزات باز نداشت جز اینکه پیشینیان، آنها را به دروغ گرفتند، و به ثمود ماده‌شتر دادیم که [پدیده‌ای‌] روشنگر بود، و[لی‌] به آن ستم کردند، و ما معجزه‌ها را جز برای بیم‌دادن [مردم‌] نمی‌فرستیم.'),
(2089, 17, 60, 'و [یاد کن‌] هنگامی را که به تو گفتیم: «به راستی پروردگارت بر مردم احاطه دارد.» و آن رؤیایی را که به تو نمایاندیم، و [نیز] آن درخت لعنت شده در قرآن را جز برای آزمایش مردم قرار ندادیم؛ و ما آنان را بیم می‌دهیم، ولی جز بر طغیان بیشتر آنها نمی‌افزاید.'),
(2090, 17, 61, 'و هنگامی را که به فرشتگان گفتیم: «برای آدم سجده کنید» پس [همه‌] جز ابلیس سجده کردند. گفت: «آیا برای کسی که از گل آفریدی سجده کنم؟»'),
(2091, 17, 62, '[سپس‌] گفت: «به من بگو: این کسی را که بر من برتری دادی [برای چه بود]؟ اگر تا روز قیامت مهلتم دهی قطعاً فرزندانش را -جز اندکی [از آنها]- ریشه‌کن خواهم کرد.»'),
(2092, 17, 63, 'فرمود: «برو که هر کس از آنان تو را پیروی کند مسلماً جهنم سزایتان خواهد بود که کیفری تمام است.'),
(2093, 17, 64, 'و از ایشان هر که را توانستی با آوای خود تحریک کن و با سواران و پیادگانت بر آنها بتاز و با آنان در اموال و اولاد شرکت کن و به ایشان وعده بده»، و شیطان جز فریب به آنها وعده نمی‌دهد.'),
(2094, 17, 65, '«در حقیقت، تو را بر بندگان من تسلطی نیست، و حمایتگری [چون‌] پروردگارت بس است.»'),
(2095, 17, 66, 'پروردگار شما کسی است که کشتی را در دریا برای شما به حرکت در می‌آورد، تا از فضل او برای خود بجویید، چرا که او همواره به شما مهربان است.'),
(2096, 17, 67, 'و چون در دریا به شما صدمه‌ای برسد، هر که را جز او می‌خوانید ناپدید [و فراموش‌] می‌گردد، و چون [خدا] شما را به سوی خشکی رهانید، رویگردان می‌شوید، و انسان همواره ناسپاس است.'),
(2097, 17, 68, 'مگر ایمن شدید از اینکه شما را در کنار خشکی در زمین فرو برد یا بر شما طوفانی از سنگریزه‌ها بفرستد، سپس برای خود نگاهبانی نیابید.'),
(2098, 17, 69, 'یا [مگر] ایمن شدید از اینکه بار دیگر شما را در آن [دریا] باز گرداند و تندبادی شکننده بر شما بفرستد و به سزای آنکه کفر ورزیدید غرقتان کند؛ آنگاه برای خود در برابر ما کسی را نیابید که آن را دنبال کند؟'),
(2099, 17, 70, 'و به راستی ما فرزندان آدم را گرامی داشتیم، و آنان را در خشکی و دریا [بر مرکبها] برنشاندیم، و از چیزهای پاکیزه به ایشان روزی دادیم، و آنها را بر بسیاری از آفریده‌های خود برتری آشکار دادیم.'),
(2100, 17, 71, '[یاد کن‌] روزی را که هر گروهی را با پیشوایشان فرا می‌خوانیم. پس هر کس کارنامه‌اش را به دست راستش دهند، آنان کارنامه خود را می‌خوانند و به قدر نخک هسته خرمایی به آنها ستم نمی‌شود.'),
(2101, 17, 72, 'و هر که در این [دنیا] کور[دل‌] باشد در آخرت [هم‌] کور[دل‌] و گمراهتر خواهد بود.'),
(2102, 17, 73, 'و چیزی نمانده بود که تو را از آنچه به سوی تو وحی کرده‌ایم گمراه کنند تا غیر از آن را بر ما ببندی و در آن صورت تو را به دوستی خود بگیرند.'),
(2103, 17, 74, 'و اگر تو را استوار نمی‌داشتیم، قطعاً نزدیک بود کمی به سوی آنان متمایل شوی.'),
(2104, 17, 75, 'در آن صورت، حتماً تو را دو برابر [در] زندگی و دو برابر [پس از] مرگ [عذاب‌] می‌چشانیدیم، آنگاه در برابر ما برای خود یاوری نمی‌یافتی.'),
(2105, 17, 76, 'و چیزی نمانده بود که تو را از این سرزمین بَرْکَنَنْد، تا تو را از آنجا بیرون سازند، و در آن صورت آنان [هم‌] پس از تو جز [زمان‌] اندکی نمی‌ماندند؛'),
(2106, 17, 77, 'سنّتی که همواره در میان [امتهای‌] فرستادگانی که پیش از تو گسیل داشته‌ایم [جاری‌] بوده است، و برای سنت [و قانون‌] ما تغییری نخواهی یافت.'),
(2107, 17, 78, 'نماز را از زوال آفتاب تا نهایت تاریکی شب برپادار، و [نیز] نماز صبح را، زیرا نماز صبح همواره [مقرون با] حضور [فرشتگان‌] است.'),
(2108, 17, 79, 'و پاسی از شب را زنده بدار، تا برای تو [به منزله‌] نافله‌ای باشد، امید که پروردگارت تو را به مقامی ستوده برساند.'),
(2109, 17, 80, 'و بگو: «پروردگارا، مرا [در هر کاری‌] به طرز درست داخل کن و به طرز درست خارج ساز، و از جانب خود برای من تسلطی یاری‌بخش قرار ده.»'),
(2110, 17, 81, 'و بگو: «حق آمد و باطل نابود شد. آری، باطل همواره نابودشدنی است.»'),
(2111, 17, 82, 'و ما آنچه را برای مؤمنان مایه درمان و رحمت است از قرآن نازل می‌کنیم، و[لی‌] ستمگران را جز زیان نمی‌افزاید.'),
(2112, 17, 83, 'و چون به انسان نعمت ارزانی داریم، روی می‌گرداند و پهلو تهی می‌کند، و چون آسیبی به وی رسد نومید می‌گردد.'),
(2113, 17, 84, 'بگو: «هر کس بر حسب ساختار [روانی و بدنی‌] خود عمل می‌کند، و پروردگار شما به هر که راه‌یافته‌تر باشد داناتر است.»'),
(2114, 17, 85, 'و در باره روح از تو می‌پرسند، بگو: «روح از [سنخ‌] فرمان پروردگار من است، و به شما از دانش جز اندکی داده نشده است.»'),
(2115, 17, 86, 'و اگر بخواهیم، قطعاً آنچه را به تو وحی کرده‌ایم می‌بَریم، آنگاه برای [حفظ] آن، در برابر ما، برای خود مدافعی نمی‌یابی،'),
(2116, 17, 87, 'مگر رحمتی از جانب پروردگارت [به تو برسد]، زیرا فضل او بر تو همواره بسیار است.'),
(2117, 17, 88, 'بگو: «اگر انس و جن گرد آیند تا نظیر این قرآن را بیاورند، مانند آن را نخواهند آورد، هر چند برخی از آنها پشتیبان برخی [دیگر] باشند.»'),
(2118, 17, 89, 'و به راستی در این قرآن از هر گونه مَثَلی، گوناگون آوردیم، ولی بیشتر مردم جز سرِ انکار ندارند.'),
(2119, 17, 90, 'و گفتند: «تا از زمین چشمه‌ای برای ما نجوشانی، هرگز به تو ایمان نخواهیم آورد.'),
(2120, 17, 91, 'یا [باید] برای تو باغی از درختان خرما و انگور باشد و آشکارا از میان آنها جویبارها روان سازی،'),
(2121, 17, 92, 'یا چنانکه ادعا می‌کنی، آسمان را پاره پاره بر [سر] ما فرو اندازی، یا خدا و فرشتگان را در برابر [ما حاضر] آوری،'),
(2122, 17, 93, 'یا برای تو خانه‌ای از طلا[کاری‌] باشد، یا به آسمان بالا روی، و به بالا رفتن تو [هم‌] اطمینان نخواهیم داشت، تا بر ما کتابی نازل کنی که آن را بخوانیم. «بگو:» پاک است پروردگار من، آیا [من‌] جز بشری فرستاده هستم؟'),
(2123, 17, 94, 'و [چیزی‌] مردم را از ایمان آوردن باز نداشت، آنگاه که هدایت برایشان آمد، جز اینکه گفتند: «آیا خدا بشری را به سِمَت رسول مبعوث کرده است؟»'),
(2124, 17, 95, 'بگو: «اگر در روی زمین فرشتگانی بودند که با اطمینان راه می‌رفتند، البته بر آنان [نیز] فرشته‌ای را بعنوان پیامبر از آسمان نازل می‌کردیم.»'),
(2125, 17, 96, 'بگو: «میان من و شما، گواه بودن خدا کافی است، چرا که او همواره به [حال‌] بندگانش آگاهِ بینا است.»'),
(2126, 17, 97, 'و هر که را خدا هدایت کند او رهیافته است، و هر که را گمراه سازد، در برابر او برای آنان هرگز دوستانی نیابی، و روز قیامت آنها را کور و لال و کر، به روی چهره‌شان درافتاده، برخواهیم انگیخت: جایگاهشان دوزخ است. هر بار که آتش آن فرو نشیند، شراره‌ای [تازه‌] برایشان می‌افزاییم.'),
(2127, 17, 98, 'جزای آنها این است، چرا که آیات ما را انکار کردند و گفتند: «آیا وقتی ما استخوان و خاک شدیم [باز] در آفرینشی جدید برانگیخته خواهیم شد؟»'),
(2128, 17, 99, 'آیا ندانستند که خدایی که آسمانها و زمین را آفریده، تواناست که مانند آنان را بیافریند و [همان خداست که‌] برایشان زمانی مقرر فرموده که در آن هیچ شکی نیست؟ و[لی‌] ستمگران جز انکار [چیزی را ]نپذیرفتند.'),
(2129, 17, 100, 'بگو: «اگر شما مالک گنجینه‌های رحمت پروردگارم بودید، باز هم از بیم خرج کردن قطعاً امساک می‌ورزیدید، و انسان همواره بخیل است.»'),
(2130, 17, 101, 'و در حقیقت، ما به موسی نُه نشانه آشکار دادیم. -پس، از فرزندان اسرائیل بپرس- آنگاه که نزد آنان آمد، و فرعون به او گفت: «ای موسی‌، من جداً تو را افسون‌شده می‌پندارم.»'),
(2131, 17, 102, 'گفت: «قطعاً می‌دانی که این [نشانه‌ها] را که باعث بینشهاست، جز پروردگار آسمانها و زمین نازل نکرده است، و راستی ای فرعون، تو را تباه شده می‌پندارم.»'),
(2132, 17, 103, 'پس [فرعون‌] تصمیم گرفت که آنان را از سرزمین [مصر] برکَند، پس او و هر که را با وی بود همه را غرق کردیم.'),
(2133, 17, 104, 'و پس از او به فرزندان اسرائیل گفتیم: «در این سرزمین ساکن شوید، پس چون وعده آخرت فرا رسد شما را همه با هم محشور می‌کنیم.»'),
(2134, 17, 105, 'و آن [قرآن‌] را به حق فرود آوردیم و به حق فرود آمد، و تو را جز بشارت‌دهنده و بیم‌رسان نفرستادیم.'),
(2135, 17, 106, 'و قرآنی [با عظمت را] بخش بخش [بر تو] نازل کردیم تا آن را به آرامی به مردم بخوانی، و آن را به تدریج نازل کردیم.'),
(2136, 17, 107, 'بگو: «[چه‌] به آن ایمان بیاورید یا نیاورید، بی‌گمان کسانی که پیش از [نزول‌] آن دانش یافته‌اند، چون [این کتاب‌] بر آنان خوانده شود سجده‌کنان به روی درمی‌افتند.»'),
(2137, 17, 108, 'و می‌گویند: «منزّه است پروردگار ما، که وعده پروردگار ما قطعاً انجام‌شدنی است.»'),
(2138, 17, 109, 'و بر روی زمین می‌افتند و می‌گریند و بر فروتنی آنها می‌افزاید.'),
(2139, 17, 110, 'بگو: «خدا را بخوانید یا رحمان را بخوانید، هر کدام را بخوانید، برای او نامهای نیکوتر است.» و نمازت را به آوازِ بلند مخوان و بسیار آهسته‌اش مکن، و میان این [و آن‌] راهی [میانه‌] جوی.'),
(2140, 17, 111, 'و بگو: «ستایش خدایی را که نه فرزندی گرفته و نه در جهانداری شریکی دارد و نه خوار بوده که [نیاز به‌] دوستی داشته باشد.» و او را بسیار بزرگ شمار.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ستایش خدایی را که این کتاب [آسمانی‌] را بر بنده خود فرو فرستاد و هیچ گونه کژی در آن ننهاد،'),
(2142, 18, 2, '[کتابی‌] راست و درست، تا [گناهکاران را] از جانب خود به عذابی سخت بیم دهد، و مؤمنانی را که کارهای شایسته می‌کنند نوید بخشد که برای آنان پاداشی نیکوست.'),
(2143, 18, 3, 'در حالی که جاودانه در آن [بهشت‌] ماندگار خواهند بود.'),
(2144, 18, 4, 'و تا کسانی را که گفته‌اند: خداوند فرزندی گرفته است، هشدار دهد.'),
(2145, 18, 5, 'نه آنان و نه پدرانشان به این [ادّعا] دانشی ندارند. بزرگ سخنی است که از دهانشان برمی‌آید. [آنان‌] جز دروغ نمی‌گویند.'),
(2146, 18, 6, 'شاید، اگر به این سخن ایمان نیاورند، تو جان خود را از اندوه، در پیگیری [کار]شان تباه کنی.'),
(2147, 18, 7, 'در حقیقت، ما آنچه را که بر زمین است، زیوری برای آن قرار دادیم، تا آنان را بیازماییم که کدام یک از ایشان نیکوکارترند.'),
(2148, 18, 8, 'و ما آنچه را که بر آن است، قطعاً بیابانی بی‌گیاه خواهیم کرد.'),
(2149, 18, 9, 'مگر پنداشتی اصحاب کهف و رقیم [=خفتگان غار لوحه‌دار] از آیات ما شگفت بوده است؟'),
(2150, 18, 10, 'آنگاه که جوانان به سوی غار پناه جستند و گفتند: «پروردگار ما! از جانب خود به ما رحمتی بخش و کار ما را برای ما به سامان رسان.»'),
(2151, 18, 11, 'پس در آن غار، سالیانی چند بر گوشهایشان پرده زدیم.'),
(2152, 18, 12, 'آنگاه آنان را بیدار کردیم، تا بدانیم کدام یک از آن دو دسته، مدت درنگشان را بهتر حساب کرده‌اند.'),
(2153, 18, 13, 'ما خبرشان را بر تو درست حکایت می‌کنیم: آنان جوانانی بودند که به پروردگارشان ایمان آورده بودند و بر هدایتشان افزودیم.'),
(2154, 18, 14, 'و دلهایشان را استوار گردانیدیم آنگاه که [به قصد مخالفت با شرک‌] برخاستند و گفتند: «پروردگار ما پروردگار آسمانها و زمین است. جز او هرگز معبودی را نخواهیم خواند، که در این صورت قطعاً ناصواب گفته‌ایم.»'),
(2155, 18, 15, 'این قوم ما جز او معبودانی اختیار کرده‌اند. چرا بر [حقّانیّت‌] آنها برهانی آشکار نمی‌آورند؟ پس کیست ستمکارتر از آن کس که بر خدا دروغ بندد؟'),
(2156, 18, 16, 'و چون از آنها و از آنچه که جز خدا می‌پرستند کناره گرفتید، پس به غار پناه جویید، تا پروردگارتان از رحمت خود بر شما بگستراند و برای شما در کارتان گشایشی فراهم سازد.'),
(2157, 18, 17, 'و آفتاب را می‌بینی که چون برمی‌آید، از غارشان به سمت راست مایل است، و چون فرو می‌شود از سمت چپ دامن برمی‌چیند، در حالی که آنان در جایی فراخ از آن [غار قرار گرفته‌]اند. این از نشانه‌های [قدرت‌] خداست. خدا هر که را راهنمایی کند او راه‌یافته است، و هر که را بی‌راه گذارد، هرگز برای او یاری راهبر نخواهی یافت.'),
(2158, 18, 18, 'و می‌پنداری که ایشان بیدارند، در حالی که خفته‌اند و آنها را به پهلوی راست و چپ می‌گردانیم، و سگشان بر آستانه [غار] دو دست خود را دراز کرده [بود]. اگر بر حال آنان اطلاع می‌یافتی، گریزان روی از آنها برمی‌تافتی و از [مشاهده‌] آنها آکنده از بیم می‌شدی.'),
(2159, 18, 19, 'و این چنین بیدارشان کردیم، تا میان خود از یکدیگر پرسش کنند. گوینده‌ای از آنان گفت: «چقدر مانده‌اید؟» گفتند: «روزی یا پاره‌ای از روز را مانده‌ایم.» [سرانجام‌] گفتند: «پروردگارتان به آنچه مانده‌اید داناتر است، اینک یکی از خودتان را با این پول خود به شهر بفرستید، تا ببیند کدام یک از غذاهای آن پاکیزه‌تر است و از آن، غذایی برایتان بیاورد، و باید زیرکی به خرج دهد و هیچ کس را از [حال‌] شما آگاه نگرداند.'),
(2160, 18, 20, 'چرا که اگر آنان بر شما دست یابند، سنگسارتان می‌کنند یا شما را به کیش خود بازمی‌گردانند، و در آن صورت هرگز روی رستگاری نخواهید دید.'),
(2161, 18, 21, 'و بدین گونه [مردم آن دیار را] بر حالشان آگاه ساختیم تا بدانند که وعده خدا راست است و [در فرا رسیدن‌] قیامت هیچ شکی نیست، هنگامی که میان خود در کارشان با یکدیگر نزاع می‌کردند، پس [عده‌ای ]گفتند: «بر روی آنها ساختمانی بنا کنید، پروردگارشان به [حال‌] آنان داناتر است.» [سرانجام‌] کسانی که بر کارشان غلبه یافتند گفتند: «حتماً بر ایشان معبدی بنا خواهیم کرد.»'),
(2162, 18, 22, 'به زودی خواهند گفت: «سه تن بودند [و] چهارمین آنها سگشان بود.» و می‌گویند: «پنج تن بودند [و] ششمین آنها سگشان بود.» تیر در تاریکی می‌اندازند. و [عده‌ای‌] می‌گویند: «هفت تن بودند و هشتمین آنها سگشان بود.» بگو: «پروردگارم به شماره آنها آگاه‌تر است، جز اندکی [کسی شماره‌] آنها را نمی‌داند.» پس در باره ایشان جز به صورت ظاهر جدال مکن و در مورد آنها از هیچ کس جویا مشو.'),
(2163, 18, 23, 'و زنهار در مورد چیزی مگوی که من آن را فردا انجام خواهم داد.'),
(2164, 18, 24, 'مگر آنکه خدا بخواهد، و چون فراموش کردی پروردگارت را یاد کن و بگو: «امید که پروردگارم مرا به راهی که نزدیکتر از این به صواب است، هدایت کند.»'),
(2165, 18, 25, 'و سیصد سال در غارشان درنگ کردند و نه سال [نیز بر آن‌] افزودند.'),
(2166, 18, 26, 'بگو: «خدا به آنچه درنگ کردند داناتر است. نهان آسمانها و زمین به او اختصاص دارد. وه! چه بینا و شنواست. برای آنان یاوری جز او نیست و هیچ کس را در فرمانروایی خود شریک نمی‌گیرد.»'),
(2167, 18, 27, 'و آنچه را که از کتاب پروردگارت به تو وحی شده است بخوان. کلمات او را تغییردهنده‌ای نیست، و جز او هرگز پناهی نخواهی یافت.'),
(2168, 18, 28, 'و با کسانی که پروردگارشان را صبح و شام می‌خوانند [و] خشنودی او را می‌خواهند، شکیبایی پیشه کن، و دو دیده‌ات را از آنان برمگیر که زیور زندگی دنیا را بخواهی، و از آن کس که قلبش را از یاد خود غافل ساخته‌ایم و از هوس خود پیروی کرده و [اساس‌] کارش بر زیاده‌روی است، اطاعت مکن.'),
(2169, 18, 29, 'و بگو: «حق از پروردگارتان [رسیده‌] است. پس هر که بخواهد بگرود و هر که بخواهد انکار کند، که ما برای ستمگران آتشی آماده کرده‌ایم که سراپرده‌هایش آنان را در بر می‌گیرد، و اگر فریادرسی جویند، به آبی چون مس گداخته که چهره‌ها را بریان می‌کند یاری می‌شوند. وه! چه بد شرابی و چه زشت جایگاهی است.»'),
(2170, 18, 30, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند [بدانند که‌] ما پاداش کسی را که نیکوکاری کرده است تباه نمی‌کنیم.'),
(2171, 18, 31, 'آنانند که بهشتهای عدن به ایشان اختصاص دارد که از زیر [قصرها]شان جویبارها روان است. در آنجا با دستبندهایی از طلا آراسته می‌شوند و جامه‌هایی سبز از پرنیان نازک و حریر ستبر می‌پوشند. در آنجا بر سریرها تکیه می‌زنند. چه خوش پاداش و نیکو تکیه‌گاهی!'),
(2172, 18, 32, 'و برای آنان، آن دو مرد را مَثَل بزن که به یکی از آنها دو باغ انگور دادیم و پیرامون آن دو [باغ‌] را با درختان خرما پوشاندیم، و میان آن دو را کشتزاری قرار دادیم.'),
(2173, 18, 33, 'هر یک از این دو باغ محصول خود را [به موقع‌] می‌داد و از [صاحبش‌] چیزی دریغ نمی‌ورزید، و میان آن دو [باغ‌] نهری روان کرده بودیم.'),
(2174, 18, 34, 'و برای او میوه فراوان بود. پس به رفیقش -در حالی که با او گفت و گو می‌کرد- گفت: «مال من از تو بیشتر است و از حیث افراد از تو نیرومندترم.»'),
(2175, 18, 35, 'و در حالی که او به خویشتن ستمکار بود، داخل باغ شد [و] گفت: «گمان نمی‌کنم این نعمت هرگز زوال پذیرد.»'),
(2176, 18, 36, 'و گمان نمی‌کنم که رستاخیز بر پا شود، و اگر هم به سوی پروردگارم بازگردانده شوم قطعاً بهتر از این را در بازگشت، خواهم یافت.'),
(2177, 18, 37, 'رفیقش -در حالی که با او گفت و گو می‌کرد- به او گفت: «آیا به آن کسی که تو را از خاک، سپس از نطفه آفرید، آنگاه تو را [به صورت‌] مردی درآورد، کافر شدی؟»'),
(2178, 18, 38, 'اما من [می‌گویم:] اوست خدا، پروردگار من، و هیچ کس را با پروردگارم شریک نمی‌سازم.'),
(2179, 18, 39, 'و چون داخل باغت شدی، چرا نگفتی: ماشاء الله، نیرویی جز به [قدرت‌] خدا نیست. اگر مرا از حیث مال و فرزند کمتر از خود می‌بینی،'),
(2180, 18, 40, 'امید است که پروردگارم بهتر از باغ تو به من عطا فرماید، و بر آن [باغ تو] آفتی از آسمان بفرستد، تا به زمینی هموار و لغزنده تبدیل گردد؛'),
(2181, 18, 41, 'یا آب آن [در زمین‌] فروکش کند تا هرگز نتوانی آن را به دست آوری.»'),
(2182, 18, 42, '[تا به او رسید آنچه را باید برسد] و [آفت آسمانی‌] میوه‌هایش را فرو گرفت. پس برای [از کف دادن‌] آنچه در آن [باغ‌] هزینه کرده بود، دستهایش را بر هم می‌زد در حالی که داربستهای آن فرو ریخته بود. و [به حسرت‌] می‌گفت: «ای کاش هیچ کس را شریک پروردگارم نمی‌ساختم.»'),
(2183, 18, 43, 'و او را در برابر خدا گروهی نبود، تا یاریش کنند، و توانی نداشت که خود را یاری کند.'),
(2184, 18, 44, 'در آنجا [آشکار شد که‌] یاری به خدایِ حق تعلق دارد. اوست بهترین پاداش و [اوست‌] بهترین فرجام.'),
(2185, 18, 45, 'و برای آنان زندگی دنیا را مَثَل بزن که مانند آبی است که آن را از آسمان فرو فرستادیم؛ سپس گیاه زمین با آن درآمیخت و [چنان‌] خشک گردید که بادها پراکنده‌اش کردند، و خداست که همواره بر هر چیزی تواناست.'),
(2186, 18, 46, 'مال و پسران زیور زندگی دنیایند، و نیکیهای ماندگار از نظر پاداش نزد پروردگارت بهتر و از نظر امید [نیز] بهتر است.'),
(2187, 18, 47, 'و [یاد کن‌] روزی را که کوهها را به حرکت درمی‌آوریم، و زمین را آشکار [و صاف‌] می‌بینی، و آنان را گرد می‌آوریم و هیچ یک را فرو گذار نمی‌کنیم.'),
(2188, 18, 48, 'و ایشان به صف بر پروردگارت عرضه می‌شوند [و به آنها می‌فرماید:] به راستی همان گونه که نخستین بار شما را آفریدیم [باز] به سوی ما آمدید، بلکه پنداشتید هرگز برای شما موعدی مقرر قرار نخواهیم داد.'),
(2189, 18, 49, 'و کارنامه [عمل شما در میان‌] نهاده می‌شود، آنگاه بزهکاران را از آنچه در آن است بیمناک می‌بینی، و می‌گویند: «ای وای بر ما، این چه نامه‌ای است که هیچ [کار] کوچک و بزرگی را فرو نگذاشته، جز اینکه همه را به حساب آورده است.» و آنچه را انجام داده‌اند حاضر یابند، و پروردگار تو به هیچ کس ستم روا نمی‌دارد.'),
(2190, 18, 50, 'و [یاد کن‌] هنگامی را که به فرشتگان گفتیم: «آدم را سجده کنید،» پس [همه‌] -جز ابلیس- سجده کردند، که از [گروه‌] جن بود و از فرمان پروردگارش سرپیچید. آیا [با این حال،] او و نسلش را به جای من دوستان خود می‌گیرید، و حال آنکه آنها دشمن شمایند؟ و چه بد جانشینانی برای ستمگرانند.'),
(2191, 18, 51, '[من‌] آنان را نه در آفرینش آسمانها و زمین به شهادت طلبیدم و نه در آفرینش خودشان. و من آن نیستم که گمراهگران را همکار خود بگیرم.'),
(2192, 18, 52, 'و [یاد کن‌] روزی را که [خدا] می‌گوید: «آنهایی را که شریکان من پنداشتید، ندا دهید»، پس آنها را بخوانند و[لی‌] اجابتشان نکنند، و ما میان آنان ورطه‌ای قرار دهیم.'),
(2193, 18, 53, 'و گناهکاران آتش [دوزخ‌] را می‌بینند و درمی‌یابند که در آن خواهند افتاد، و از آن راه گریزی نیابند.'),
(2194, 18, 54, 'و به راستی در این قرآن، برای مردم از هر گونه مَثَلی آوردیم، و[لی‌] انسان بیش از هر چیز سرِ جدال دارد.'),
(2195, 18, 55, 'و چیزی مانع مردم نشد از اینکه وقتی هدایت به سویشان آمد ایمان بیاورند، و از پروردگارشان آمرزش بخواهند، جز اینکه [مستحق شوند] تا سنت [خدا در مورد عذاب‌] پیشینیان، در باره آنان [نیز] به کار رود، یا عذاب رویارویشان بیاید.'),
(2196, 18, 56, 'و پیامبران [خود] را جز بشارت‌دهنده و بیم‌رسان گسیل نمی‌داریم، و کسانی که کافر شده‌اند، به باطل مجادله می‌کنند تا به وسیله آن، حق را پایمال گردانند، و نشانه‌های من و آنچه را [بدان‌] بیم داده شده‌اند به ریشخند گرفتند.'),
(2197, 18, 57, 'و کیست ستمکارتر از آن کس که به آیات پروردگارش پند داده شده، و از آن روی برتافته، و دستاورد پیشینه خود را فراموش کرده است؟ ما بر دلهای آنان پوششهایی قرار دادیم تا آن را درنیابند و در گوشهایشان سنگینی [نهادیم‌]. و اگر آنها را به سوی هدایت فراخوانی باز هرگز به راه نخواهند آمد.'),
(2198, 18, 58, 'و پروردگار تو آمرزنده [و] صاحب رحمت است. اگر به [جرم‌] آنچه مرتکب شده‌اند، آنها را مؤاخذه می‌کرد، قطعاً در عذاب آنان تعجیل می‌نمود [ولی چنین نمی کند] بلکه برای آنها سر رسیدی است که هرگز از برابر آن راه گریزی نمی‌یابند.'),
(2199, 18, 59, 'و [مردم‌] آن شهرها چون بیدادگری کردند، هلاکشان کردیم، و برای هلاکتشان موعدی مقرر داشتیم.'),
(2200, 18, 60, 'و [یاد کن‌] هنگامی را که موسی به جوانِ [همراه‌] خود گفت: «دست بردار نیستم تا به محل برخورد دو دریا برسم، هر چند سالها[ی سال‌] سیر کنم.»'),
(2201, 18, 61, 'پس چون به محل برخورد دو [دریا] رسیدند، ماهیِ خودشان را فراموش کردند، و ماهی در دریا راه خود را در پیش گرفت [و رفت‌].'),
(2202, 18, 62, 'و هنگامی که [از آنجا] گذشتند [موسی‌] به جوان خود گفت: «غذایمان را بیاور که راستی ما از این سفر رنج بسیار دیدیم.»'),
(2203, 18, 63, 'گفت: «دیدی؟ وقتی به سوی آن صخره پناه جستیم، من ماهی را فراموش کردم، و جز شیطان، [کسی‌] آن را از یاد من نبرد، تا به یادش باشم، و به طور عجیبی راه خود را در دریا پیش گرفت.»'),
(2204, 18, 64, 'گفت: «این همان بود که ما می‌جستیم.» پس جستجوکنان ردّ پای خود را گرفتند و برگشتند.'),
(2205, 18, 65, 'تا بنده‌ای از بندگان ما را یافتند که از جانب خود به او رحمتی عطا کرده و از نزد خود بدو دانشی آموخته بودیم.'),
(2206, 18, 66, 'موسی به او گفت: «آیا تو را -به شرط اینکه از بینشی که آموخته شده‌ای به من یاد دهی- پیروی کنم؟»'),
(2207, 18, 67, 'گفت: «تو هرگز نمی‌توانی همپای من صبر کنی.'),
(2208, 18, 68, 'و چگونه می‌توانی بر چیزی که به شناخت آن احاطه نداری صبر کنی؟»'),
(2209, 18, 69, 'گفت: «ان شاء الله مرا شکیبا خواهی یافت و در هیچ کاری تو را نافرمانی نخواهم کرد.»'),
(2210, 18, 70, 'گفت: «اگر مرا پیروی می‌کنی، پس از چیزی سؤال مکن، تا [خود] از آن با تو سخن آغاز کنم.»'),
(2211, 18, 71, 'پس رهسپار گردیدند، تا وقتی که سوار کشتی شدند، [وی‌] آن را سوراخ کرد. [موسی‌] گفت: «آیا کشتی را سوراخ کردی تا سرنشینانش را غرق کنی؟ واقعاً به کار ناروایی مبادرت ورزیدی.»'),
(2212, 18, 72, 'گفت: «آیا نگفتم که تو هرگز نمی‌توانی همپای من صبر کنی؟»'),
(2213, 18, 73, '[موسی‌] گفت: «به سبب آنچه فراموش کردم، مرا مؤاخذه مکن و در کارم بر من سخت مگیر.»'),
(2214, 18, 74, 'پس رفتند تا به نوجوانی برخوردند. [بنده ما] او را کشت. [موسی به او ] گفت: «آیا شخص بی‌گناهی را بدون اینکه کسی را به قتل رسانده باشد کشتی؟ واقعاً کار ناپسندی مرتکب شدی.»'),
(2215, 18, 75, 'گفت: «آیا به تو نگفتم که هرگز نمی‌توانی همپای من صبر کنی؟»'),
(2216, 18, 76, '[موسی‌] گفت: «اگر از این پس چیزی از تو پرسیدم، دیگر با من همراهی مکن [و] از جانب من قطعاً معذور خواهی بود.»'),
(2217, 18, 77, 'پس رفتند تا به اهل قریه‌ای رسیدند. از مردم آنجا خوراکی خواستند، و[لی آنها] از مهمان نمودن آن دو خودداری کردند. پس در آنجا دیواری یافتند که می‌خواست فرو ریزد، و [بنده ما] آن را استوار کرد. [موسی‌] گفت: «اگر می‌خواستی [می‌توانستی‌] برای آن مزدی بگیری.»'),
(2218, 18, 78, 'گفت: «این [بار، دیگر وقت‌] جدایی میان من و توست. به زودی تو را از تأویل آنچه که نتوانستی بر آن صبر کنی آگاه خواهم ساخت»:'),
(2219, 18, 79, 'اما کشتی، از آنِ بینوایانی بود که در دریا کار می‌کردند، خواستم آن را معیوب کنم، [چرا که‌] پیشاپیش آنان پادشاهی بود که هر کشتی [درستی‌] را به زور می‌گرفت.'),
(2220, 18, 80, 'و اما نوجوان، پدر و مادرش [هر دو] مؤمن بودند، پس ترسیدیم [مبادا] آن دو را به طغیان و کفر بکِشد.'),
(2221, 18, 81, 'پس خواستیم که پروردگارشان آن دو را به پاکتر و مهربانتر از او عوض دهد.'),
(2222, 18, 82, 'و اما دیوار، از آنِ دو پسر [بچه‌] یتیم در آن شهر بود، و زیر آن، گنجی متعلق به آن دو بود، و پدرشان [مردی‌] نیکوکار بود، پس پروردگار تو خواست آن دو [یتیم‌] به حد رشد برسند و گنجینه خود را -که رحمتی از جانب پروردگارت بود- بیرون آورند. و این [کارها] را من خودسرانه انجام ندادم. این بود تأویل آنچه که نتوانستی بر آن شکیبایی ورزی.'),
(2223, 18, 83, 'و از تو در باره «ذوالقرنین» می‌پرسند. بگو: «به زودی چیزی از او برای شما خواهم خواند.»'),
(2224, 18, 84, 'ما در زمین به او امکاناتی دادیم و از هر چیزی وسیله‌ای بدو بخشیدیم.'),
(2225, 18, 85, 'تا راهی را دنبال کرد.'),
(2226, 18, 86, 'تا آنگاه که به غروبگاه خورشید رسید، به نظرش آمد که [خورشید] در چشمه‌ای گِل‌آلود و سیاه غروب می‌کند، و نزدیک آن طایفه‌ای را یافت. فرمودیم: «ای ذوالقرنین، [اختیار با توست‌] یا عذاب می‌کنی یا در میانشان [روش‌] نیکویی پیش می‌گیری.»'),
(2227, 18, 87, 'گفت: «اما هر که ستم ورزد عذابش خواهیم کرد، سپس به سوی پروردگارش بازگردانیده می‌شود، آنگاه او را عذابی سخت خواهد کرد.»'),
(2228, 18, 88, 'و اما هر که ایمان آورد و کار شایسته کند، پاداشی [هر چه‌] نیکوتر خواهد داشت، و به فرمان خود، او را به کاری آسان واخواهیم داشت.'),
(2229, 18, 89, 'سپس راهی [دیگر] را دنبال کرد.'),
(2230, 18, 90, 'تا آنگاه که به جایگاه برآمدن خورشید رسید. [خورشید] را [چنین‌] یافت که بر قومی طلوع می‌کرد که برای ایشان در برابر آن پوششی قرار نداده بودیم.'),
(2231, 18, 91, 'این چنین [می‌رفت‌]، و قطعاً به خبری که پیش او بود احاطه داشتیم.'),
(2232, 18, 92, 'باز راهی را دنبال نمود.'),
(2233, 18, 93, 'تا وقتی به میان دو سد رسید، در برابر آن دو [سد]، طایفه‌ای را یافت که نمی‌توانستند هیچ زبانی را بفهمند.'),
(2234, 18, 94, 'گفتند: «ای ذوالقرنین، یأجوج و مأجوج سخت در زمین فساد می‌کنند، آیا [ممکن است‌] مالی در اختیار تو قرار دهیم تا میان ما و آنان سدی قرار دهی؟»'),
(2235, 18, 95, 'گفت: «آنچه پروردگارم به من در آن تمکّن داده، [از کمک مالی شما] بهتر است. مرا با نیرویی [انسانی‌] یاری کنید [تا] میان شما و آنها سدی استوار قرار دهم.»'),
(2236, 18, 96, 'برای من قطعات آهن بیاورید، تا آنگاه که میان دو کوه برابر شد، گفت: «بدمید» تا وقتی که آن [قطعات‌] را آتش گردانید، گفت: «مس گداخته برایم بیاورید تا روی آن بریزم.»'),
(2237, 18, 97, '[در نتیجه، اقوام وحشی‌] نتوانستند از آن [مانع‌] بالا روند و نتوانستند آن را سوراخ کنند.'),
(2238, 18, 98, 'گفت: «این رحمتی از جانب پروردگار من است، و[لی‌] چون وعده پروردگارم فرا رسد، آن [سد] را درهم کوبد، و وعده پروردگارم حق است.»'),
(2239, 18, 99, 'و در آن روز آنان را رها می‌کنیم تا موج‌آسا بعضی با برخی درآمیزند و [همین که‌] در صور دمیده شود، همه آنها را گرد خواهیم آورد.'),
(2240, 18, 100, 'و آن روز، جهنم را آشکارا به کافران بنماییم.'),
(2241, 18, 101, '[به‌] همان کسانی که چشمان [بصیرت‌]شان از یاد من در پرده بود، و توانایی شنیدن [حق‌] نداشتند.'),
(2242, 18, 102, 'آیا کسانی که کفر ورزیده‌اند، پنداشته‌اند که [می‌توانند] به جای من، بندگانم را سرپرست بگیرند؟ ما جهنم را آماده کرده‌ایم تا جایگاه پذیرایی کافران باشد.'),
(2243, 18, 103, 'بگو: «آیا شما را از زیانکارترین مردم آگاه گردانم؟»'),
(2244, 18, 104, '[آنان‌] کسانی‌اند که کوشش‌شان در زندگی دنیا به هدر رفته و خود می‌پندارند که کار خوب انجام می‌دهند.'),
(2245, 18, 105, '[آری،] آنان کسانی‌اند که آیات پروردگارشان و لقای او را انکار کردند، در نتیجه اعمالشان تباه گردید، و روز قیامت برای آنها [قدر و] ارزشی نخواهیم نهاد.'),
(2246, 18, 106, 'این جهنم سزای آنان است، چرا که کافر شدند و آیات من و پیامبرانم را به ریشخند گرفتند.'),
(2247, 18, 107, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته کرده‌اند، باغهای فردوس جایگاه پذیرایی آنان است.'),
(2248, 18, 108, 'جاودانه در آن خواهند بود، و از آنجا درخواست انتقال نمی‌کنند.'),
(2249, 18, 109, 'بگو: «اگر دریا برای کلمات پروردگارم مرکب شود، پیش از آنکه کلمات پروردگارم پایان پذیرد، قطعاً دریا پایان می‌یابد، هر چند نظیرش را به مدد [آن‌] بیاوریم.»'),
(2250, 18, 110, 'بگو: «من هم مِثل شما بشری هستم و[لی‌] به من وحی می‌شود که خدای شما خدایی یگانه است. پس هر کس به لقای پروردگار خود امید دارد باید به کار شایسته بپردازد، و هیچ کس را در پرستش پروردگارش شریک نسازد.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کاف، ها، یا، عین، صاد.'),
(2252, 19, 2, '[این‌] یادی از رحمت پروردگار تو [در باره‌] بنده‌اش زکریاست.'),
(2253, 19, 3, 'آنگاه که [زکریا] پروردگارش را آهسته ندا کرد.'),
(2254, 19, 4, 'گفت: «پروردگارا، من استخوانم سست گردیده و [موی‌] سرم از پیری سپید گشته، و -ای پروردگار من- هرگز در دعای تو ناامید نبوده‌ام.»'),
(2255, 19, 5, 'و من پس از خویشتن از بستگانم بیمناکم و زنم نازاست، پس از جانب خود ولیّ [و جانشینی‌] به من ببخش،'),
(2256, 19, 6, 'که از من ارث برد و از خاندان یعقوب [نیز] ارث برد، و او را -ای پروردگار من- پسندیده گردان.'),
(2257, 19, 7, 'ای زکریا، ما تو را به پسری -که نامش یحیی است- مژده می‌دهیم، که قبلاً همنامی برای او قرار نداده‌ایم.'),
(2258, 19, 8, 'گفت: «پروردگارا، چگونه مرا پسری خواهد بود و حال آنکه زنم نازاست و من از سالخوردگی ناتوان شده‌ام؟»'),
(2259, 19, 9, '[فرشته‌] گفت: «[فرمان‌] چنین است. پروردگار تو گفته که این [کار] بر من آسان است، و تو را در حالی که چیزی نبودی قبلاً آفریده‌ام.»'),
(2260, 19, 10, 'گفت: «پروردگارا، نشانه‌ای برای من قرار ده» فرمود: «نشانه تو این است که سه شبانه [روز] با اینکه سالمی با مردم سخن نمی‌گویی.»'),
(2261, 19, 11, 'پس، از محراب بر قوم خویش درآمد و ایشان را آگاه گردانید که روز و شب به نیایش بپردازید.'),
(2262, 19, 12, 'ای یحیی، کتاب [خدا] را به جد و جهد بگیر، و از کودکی به او نبوّت دادیم.'),
(2263, 19, 13, 'و [نیز] از جانب خود، مهربانی و پاکی [به او دادیم‌] و تقواپیشه بود.'),
(2264, 19, 14, 'و با پدر و مادر خود نیک‌رفتار بود و زورگویی نافرمان نبود.'),
(2265, 19, 15, 'و درود بر او، روزی که زاده شد و روزی که می‌میرد و روزی که زنده برانگیخته می‌شود.'),
(2266, 19, 16, 'و در این کتاب از مریم یاد کن، آنگاه که از کسان خود، در مکانی شرقی به کناری شتافت.'),
(2267, 19, 17, 'و در برابر آنان پرده‌ای بر خود گرفت. پس روح خود را به سوی او فرستادیم تا به [شکل‌] بشری خوش‌اندام بر او نمایان شد.'),
(2268, 19, 18, '[مریم‌] گفت: «اگر پرهیزگاری، من از تو به خدای رحمان پناه می‌برم.»'),
(2269, 19, 19, 'گفت: «من فقط فرستاده پروردگار توام، برای اینکه به تو پسری پاکیزه ببخشم.»'),
(2270, 19, 20, 'گفت: «چگونه مرا پسری باشد با آنکه دست بشری به من نرسیده و بدکار نبوده‌ام؟»'),
(2271, 19, 21, 'گفت: « [فرمان‌] چنین است، پروردگار تو گفته که آن بر من آسان است، و تا او را نشانه‌ای برای مردم و رحمتی از جانب خویش قرار دهیم، و [این‌] دستوری قطعی بود.»'),
(2272, 19, 22, 'پس [مریم‌] به او [=عیسی‌] آبستن شد و با او به مکان دورافتاده‌ای پناه جست.'),
(2273, 19, 23, 'تا درد زایمان، او را به سوی تنه درخت خرمایی کشانید. گفت: «ای کاش، پیش از این مرده بودم و یکسر فراموش‌شده بودم.»'),
(2274, 19, 24, 'پس، از زیرِ [پای‌] او [فرشته‌] وی را ندا داد که: غم مدار، پروردگارت زیر [پای‌] تو چشمه آبی پدید آورده است.'),
(2275, 19, 25, 'و تنه درخت خرما را به طرف خود [بگیر و] بتکان، بر تو خرمای تازه می‌ریزد.'),
(2276, 19, 26, 'و بخور و بنوش و دیده روشن دار. پس اگر کسی از آدمیان را دیدی، بگوی: «من برای [خدای‌] رحمان روزه نذر کرده‌ام، و امروز مطلقاً با انسانی سخن نخواهم گفت.»'),
(2277, 19, 27, 'پس [مریم‌] در حالی که او را در آغوش گرفته بود به نزد قومش آورد. گفتند: «ای مریم، به راستی کار بسیار ناپسندی مرتکب شده‌ای.»'),
(2278, 19, 28, 'ای خواهر هارون، پدرت مرد بدی نبود و مادرت [نیز] بدکاره نبود.'),
(2279, 19, 29, '[مریم‌] به سوی [عیسی‌] اشاره کرد. گفتند: «چگونه با کسی که در گهواره [و] کودک است سخن بگوییم؟»'),
(2280, 19, 30, '[کودک‌] گفت: «منم بنده خدا، به من کتاب داده و مرا پیامبر قرار داده است،'),
(2281, 19, 31, 'و هر جا که باشم مرا با برکت ساخته، و تا زنده‌ام به نماز و زکات سفارش کرده است،'),
(2282, 19, 32, 'و مرا نسبت به مادرم نیکوکار کرده و زورگو و نافرمانم نگردانیده است،'),
(2283, 19, 33, 'و درود بر من، روزی که زاده شدم و روزی که می‌میرم و روزی که زنده برانگیخته می‌شوم.»'),
(2284, 19, 34, 'این است [ماجرای‌] عیسی پسر مریم، [همان‌] گفتار درستی که در آن شک می‌کنند.'),
(2285, 19, 35, 'خدا را نسزد که فرزندی برگیرد. منزّه است او؛ چون کاری را اراده کند، همین قدر به آن می‌گوید: «موجود شو»، پس بی‌درنگ موجود می‌شود.'),
(2286, 19, 36, 'و در حقیقت، خداست که پروردگار من و پروردگار شماست، پس او را بپرستید. این است راه راست.'),
(2287, 19, 37, 'اما دسته‌ها[ی گوناگون‌] از میان آنها به اختلاف پرداختند، پس وای بر کسانی که کافر شدند از مشاهده روزی دهشتناک.'),
(2288, 19, 38, 'چه شنوا و بینایند روزی که به سوی ما می‌آیند، ولی ستمگران امروز در گمراهی آشکارند.'),
(2289, 19, 39, 'و آنان را از روز حسرت بیم ده، آنگاه که داوری انجام گیرد، و حال آنکه آنها [اکنون‌] در غفلتند و سر ایمان آوردن ندارند.'),
(2290, 19, 40, 'ماییم که زمین را با هر که در آن است، به میراث می‌بریم و [همه‌] به سوی ما بازگردانیده می‌شوند.'),
(2291, 19, 41, 'و در این کتاب به یاد ابراهیم پرداز، زیرا او پیامبری بسیار راستگوی بود.'),
(2292, 19, 42, 'چون به پدرش گفت: «پدر جان، چرا چیزی را که نمی‌شنود و نمی‌بیند و از تو چیزی را دور نمی‌کند می‌پرستی؟'),
(2293, 19, 43, 'ای پدر، به راستی مرا از دانش [وحی، حقایقی به دست‌] آمده که تو را نیامده است. پس، از من پیروی کن تا تو را به راهی راست هدایت نمایم،'),
(2294, 19, 44, 'پدر جان، شیطان را مپرست، که شیطان [خدای‌] رحمان را عصیانگر است،'),
(2295, 19, 45, 'پدر جان، من می‌ترسم از جانب [خدای‌] رحمان عذابی به تو رسد و تو یار شیطان باشی.»'),
(2296, 19, 46, 'گفت: «ای ابراهیم، آیا تو از خدایان من متنفّری؟ اگر باز نایستی تو را سنگسار خواهم کرد، و [برو] برای مدّتی طولانی از من دور شو.»'),
(2297, 19, 47, '[ابراهیم‌] گفت: «درود بر تو باد، به زودی از پروردگارم برای تو آمرزش می‌خواهم، زیرا او همواره نسبت به من پر مهر بوده است،'),
(2298, 19, 48, 'و از شما و [از] آنچه غیر از خدا می‌خوانید کناره می‌گیرم و پروردگارم را می‌خوانم. امیدوارم که در خواندن پروردگارم ناامید نباشم.»'),
(2299, 19, 49, 'و چون از آنها و [از] آنچه به جای خدا می‌پرستیدند کناره گرفت، اسحاق و یعقوب را به او عطا کردیم و همه را پیامبر گردانیدیم.'),
(2300, 19, 50, 'و از رحمت خویش به آنان ارزانی داشتیم، و ذکر خیرِ بلندی برایشان قرار دادیم.'),
(2301, 19, 51, 'و در این کتاب از موسی یاد کن، زیرا که او پاکدل و فرستاده‌ای پیامبر بود.'),
(2302, 19, 52, 'و از جانب راست طور، او را ندا دادیم، و در حالی که با وی راز گفتیم او را به خود نزدیک ساختیم.'),
(2303, 19, 53, 'و به رحمت خویش برادرش هارون پیامبر را به او بخشیدیم.'),
(2304, 19, 54, 'و در این کتاب از اسماعیل یاد کن، زیرا که او درست‌وعده و فرستاده‌ای پیامبر بود.'),
(2305, 19, 55, 'و خاندان خود را به نماز و زکات فرمان می‌داد و همواره نزد پروردگارش پسندیده‌[رفتار] بود.'),
(2306, 19, 56, 'و در این کتاب از ادریس یاد کن که او راستگویی پیامبر بود.'),
(2307, 19, 57, 'و [ما] او را به مقامی بلند ارتقا دادیم.'),
(2308, 19, 58, 'آنان کسانی از پیامبران بودند که خداوند بر ایشان نعمت ارزانی داشت: از فرزندان آدم بودند و از کسانی که همراه نوح [بر کشتی‌] سوار کردیم؛ و از فرزندان ابراهیم و اسرائیل و از کسانی که [آنان را] هدایت نمودیم و برگزیدیم؛ [و] هر گاه آیات [خدای‌] رحمان بر ایشان خوانده می‌شد، سجده‌کنان و گریان به خاک می‌افتادند.'),
(2309, 19, 59, 'آنگاه، پس از آنان جانشینانی به جای ماندند که نماز را تباه ساخته و از هوسها پیروی کردند، و به زودی [سزای‌] گمراهی [خود] را خواهند دید.'),
(2310, 19, 60, 'مگر آنان که توبه کرده و ایمان آورده و کار شایسته انجام دادند، که آنان به بهشت درمی‌آیند و ستمی بر ایشان نخواهد رفت.'),
(2311, 19, 61, 'باغهای جاودانی که [خدای‌] رحمان به بندگانش در جهان ناپیدا وعده داده است. در حقیقت، وعده او انجام‌شدنی است.'),
(2312, 19, 62, 'در آنجا سخن بیهوده‌ای نمی‌شنوند، جز درود. و روزی‌شان صبح و شام در آنجا [آماده‌] است.'),
(2313, 19, 63, 'این همان بهشتی است که به هر یک از بندگان ما که پرهیزگار باشند به میراث می‌دهیم.'),
(2314, 19, 64, 'و [ما فرشتگان‌] جز به فرمان پروردگارت نازل نمی‌شویم. آنچه پیش روی ما و آنچه پشت سر ما و آنچه میان این دو است، [همه‌] به او اختصاص دارد، و پروردگارت هرگز فراموشکار نبوده است.'),
(2315, 19, 65, 'پروردگار آسمانها و زمین و آنچه میان آن دو است. پس او را بپرست و در پرستش او شکیبا باش. آیا برای او همنامی می‌شناسی؟'),
(2316, 19, 66, 'و انسان می‌گوید: «آیا وقتی بمیرم، راستی زنده [از قبر] بیرون آورده می‌شوم؟»'),
(2317, 19, 67, 'آیا انسان به یاد نمی‌آورد که ما او را قبلاً آفریده‌ایم و حال آنکه چیزی نبوده است؟'),
(2318, 19, 68, 'پس، به پروردگارت سوگند که آنها را با شیاطین محشور خواهیم ساخت، سپس در حالی که به زانو درآمده‌اند، آنان را گرداگرد دوزخ حاضر خواهیم کرد.'),
(2319, 19, 69, 'آنگاه از هر دسته‌ای، کسانی از آنان را که بر [خدای‌] رحمان سرکش‌تر بوده‌اند، بیرون خواهیم کشید.'),
(2320, 19, 70, 'پس از آن، به کسانی که برای درآمدن به [جهنّم‌] سزاوارترند خود داناتریم.'),
(2321, 19, 71, 'و هیچ کس از شما نیست مگر [اینکه‌] در آن وارد می‌گردد. این [امر] همواره بر پروردگارت حکمی قطعی است.'),
(2322, 19, 72, 'آنگاه کسانی را که پرهیزگار بوده‌اند می‌رهانیم، و ستمگران را به زانو درافتاده در [دوزخ‌] رها می‌کنیم.'),
(2323, 19, 73, 'و چون آیات روشن ما بر آنان خوانده شود، کسانی که کفر ورزیده‌اند به آنان که ایمان آورده‌اند می‌گویند: «کدام یک از [ما] دو گروه جایگاهش بهتر و محفلش آراسته‌تر است؟»'),
(2324, 19, 74, 'و چه بسیار نسلها را پیش از آنان نابود کردیم، که اثاثی بهتر و ظاهری فریباتر داشتند.'),
(2325, 19, 75, 'بگو: «هر که در گمراهی است [خدای‌] رحمان به او تا زمانی مهلت می‌دهد، تا وقتی آنچه به آنان وعده داده می‌شود: یا عذاب، یا روز رستاخیز را ببینند؛ پس به زودی خواهند دانست جایگاه چه کسی بدتر و سپاهش ناتوان‌تر است.»'),
(2326, 19, 76, 'و خداوند کسانی را که هدایت یافته‌اند بر هدایتشان می‌افزاید، و نیکیهای ماندگار، نزد پروردگارت از حیث پاداش بهتر و خوش‌فرجام‌تر است.'),
(2327, 19, 77, 'آیا دیدی آن کسی را که به آیات ما کفر ورزید و گفت: «قطعاً به من مال و فرزند [بسیار] داده خواهد شد»؟'),
(2328, 19, 78, 'آیا بر غیب آگاه شده یا از [خدای‌] رحمان عهدی گرفته است؟'),
(2329, 19, 79, 'نه چنین است. به زودی آنچه را می‌گوید، می‌نویسیم و عذاب را برای او خواهیم افزود.'),
(2330, 19, 80, 'و آنچه را می‌گوید، از او به ارث می‌بریم و تنها به سوی ما خواهد آمد.'),
(2331, 19, 81, 'و به جای خدا، معبودانی اختیار کردند تا برای آنان [مایه‌] عزّت باشد.'),
(2332, 19, 82, 'نه چنین است. به زودی [آن معبودان‌] عبادت ایشان را انکار می‌کنند و دشمن آنان می‌گردند.'),
(2333, 19, 83, 'آیا ندانستی که ما شیطانها را بر کافران گماشته‌ایم، تا آنان را [به گناهان‌] تحریک کنند؟'),
(2334, 19, 84, 'پس بر ضد آنان شتاب مکن، که ما [روزها] را برای آنها شماره می‌کنیم.'),
(2335, 19, 85, '[یاد کن‌] روزی را که پرهیزگاران را به سوی [خدای‌] رحمان گروه گروه محشور می‌کنیم.'),
(2336, 19, 86, 'و مجرمان را با حال تشنگی به سوی دوزخ می‌رانیم.'),
(2337, 19, 87, '[آنان‌] اختیار شفاعت را ندارند، جز آن کس که از جانب [خدای‌] رحمان پیمانی گرفته است.'),
(2338, 19, 88, 'و گفتند: «[خدای‌] رحمان فرزندی اختیار کرده است.»'),
(2339, 19, 89, 'واقعاً چیز زشتی را [بر زبان‌] آوردید.'),
(2340, 19, 90, 'چیزی نمانده است که آسمانها از این [سخن‌] بشکافند و زمین چاک خورد و کوهها به شدت فرو ریزند.'),
(2341, 19, 91, 'از اینکه برای [خدای‌] رحمان فرزندی قایل شدند.'),
(2342, 19, 92, '[خدای‌] رحمان را نسزد که فرزندی اختیار کند.'),
(2343, 19, 93, 'هر که در آسمانها و زمین است جز بنده‌وار به سوی [خدای‌] رحمان نمی‌آید.'),
(2344, 19, 94, 'و یقیناً آنها را به حساب آورده و به دقت شماره کرده است.'),
(2345, 19, 95, 'و روز قیامت همه آنها تنها، به سوی او خواهند آمد.'),
(2346, 19, 96, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به زودی [خدای‌] رحمان برای آنان محبتی [در دلها] قرار می‌دهد.'),
(2347, 19, 97, 'در حقیقت، ما این [قرآن‌] را بر زبان تو آسان ساختیم تا پرهیزگاران را بدان نوید، و مردم ستیزه‌جو را بدان بیم دهی.'),
(2348, 19, 98, 'و چه بسیار نسلها که پیش از آنان هلاک کردیم. آیا کسی از آنان را می‌یابی یا صدایی از ایشان می‌شنوی؟');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طه.'),
(2350, 20, 2, 'قرآن را بر تو نازل نکردیم تا به رنج افتی،'),
(2351, 20, 3, 'جز اینکه برای هر که می‌ترسد، پندی باشد.'),
(2352, 20, 4, '[کتابی است‌] نازل شده از جانب کسی که زمین و آسمانهای بلند را آفریده است.'),
(2353, 20, 5, 'خدای رحمان که بر عرش استیلا یافته است.'),
(2354, 20, 6, 'آنچه در آسمانها و آنچه در زمین و آنچه میان آن دو و آنچه زیر خاک است از آنِ اوست.'),
(2355, 20, 7, 'و اگر سخن به آواز گویی، او نهان و نهان‌تر را می‌داند.'),
(2356, 20, 8, 'خدایی که جز او معبودی نیست [و] نامهای نیکو به او اختصاص دارد.'),
(2357, 20, 9, 'و آیا خبر موسی به تو رسید؟'),
(2358, 20, 10, 'هنگامی که آتشی دید، پس به خانواده خود گفت: «درنگ کنید، زیرا من آتشی دیدم، امید که پاره‌ای از آن برای شما بیاورم یا در پرتو آتش راه [خود را باز] یابم.'),
(2359, 20, 11, 'پس چون بدان رسید، ندا داده شد که: «ای موسی،'),
(2360, 20, 12, 'این منم پروردگار تو، پای‌پوش خویش بیرون آور که تو در وادی مقدس «طُوی‌» هستی.'),
(2361, 20, 13, 'و من تو را برگزیده‌ام، پس بدانچه وحی می‌شود گوش فرا ده.'),
(2362, 20, 14, 'منم، من، خدایی که جز من خدایی نیست، پس مرا پرستش کن و به یاد من نماز برپا دار.'),
(2363, 20, 15, 'در حقیقت، قیامت فرارسنده است. می‌خواهم آن را پوشیده دارم، تا هر کسی به [موجب‌] آنچه می‌کوشد جزا یابد.'),
(2364, 20, 16, 'پس هرگز نباید کسی که به آن ایمان ندارد و از هوس خویش پیروی کرده است، تو را از [ایمان به‌] آن باز دارد، که هلاک خواهی شد.'),
(2365, 20, 17, 'و ای موسی، در دست راست تو چیست؟»'),
(2366, 20, 18, 'گفت: «این عصای من است، بر آن تکیه می‌دهم و با آن برای گوسفندانم برگ می تکانم، و کارهای دیگری هم برای من از آن برمی‌آید.»'),
(2367, 20, 19, 'فرمود: «ای موسی، آن را بینداز.»'),
(2368, 20, 20, 'پس آن را انداخت و ناگاه ماری شد که به سرعت می‌خزید.'),
(2369, 20, 21, 'فرمود: «آن را بگیر و مترس، به زودی آن را به حال نخستینش بازخواهیم گردانید،'),
(2370, 20, 22, 'و دست خود را به پهلویت ببر، سپید بی‌گزند برمی‌آید، [این‌] معجزه‌ای دیگر است،'),
(2371, 20, 23, 'تا به تو معجزات بزرگ خود را بنمایانیم.'),
(2372, 20, 24, 'به سوی فرعون برو که او به سرکشی برخاسته است.'),
(2373, 20, 25, 'گفت: «پروردگارا، سینه‌ام را گشاده گردان،'),
(2374, 20, 26, 'و کارم را برای من آسان ساز،'),
(2375, 20, 27, 'و از زبانم گره بگشای،'),
(2376, 20, 28, '[تا] سخنم را بفهمند،'),
(2377, 20, 29, 'و برای من دستیاری از کسانم قرار ده،'),
(2378, 20, 30, 'هارون برادرم را،'),
(2379, 20, 31, 'پشتم را به او استوار کن،'),
(2380, 20, 32, 'و او را شریک کارم گردان،'),
(2381, 20, 33, 'تا تو را فراوان تسبیح گوییم،'),
(2382, 20, 34, 'و بسیار به یاد تو باشیم،'),
(2383, 20, 35, 'زیرا تو همواره به [حال‌] ما بینایی.»'),
(2384, 20, 36, 'فرمود: «ای موسی، خواسته‌ات به تو داده شد.»'),
(2385, 20, 37, 'و به راستی، بار دیگر [هم‌] بر تو منت نهادیم،'),
(2386, 20, 38, 'هنگامی که به مادرت آنچه را که [باید] وحی می‌شد وحی کردیم:'),
(2387, 20, 39, 'که او را در صندوقچه‌ای بگذار، سپس در دریایش افکن تا دریا [=رود نیل‌] او را به کرانه اندازد [و] دشمن من و دشمن وی، او را برگیرد. و مِهری از خودم بر تو افکندم تا زیر نظر من پرورش یابی.'),
(2388, 20, 40, 'آنگاه که خواهر تو می‌رفت و می‌گفت: آیا شما را بر کسی که عهده‌دار او گردد دلالت کنم؟ پس تو را به سوی مادرت بازگردانیدیم تا دیده‌اش روشن شود و غم نخورد، و [سپس‌] شخصی را کُشتی و [ما] تو را از اندوه رهانیدیم، و تو را بارها آزمودیم، و سالی چند در میان اهل «مدین» ماندی، سپس ای موسی در زمان مقدر [و مقتضی‌] آمدی.'),
(2389, 20, 41, 'و تو را برای خود پروردم.'),
(2390, 20, 42, 'تو و برادرت معجزه‌های مرا [برای مردم‌] ببرید و در یادکردن من سستی مکنید.'),
(2391, 20, 43, 'به سوی فرعون بروید که او به سرکشی برخاسته،'),
(2392, 20, 44, 'و با او سخنی نرم گویید، شاید که پند پذیرد یا بترسد.'),
(2393, 20, 45, 'آن دو گفتند: «پروردگارا، ما می‌ترسیم که [او] آسیبی به ما برساند یا آنکه سرکشی کند.»'),
(2394, 20, 46, 'فرمود: «مترسید، من همراه شمایم، می‌شنوم و می‌بینم،'),
(2395, 20, 47, 'پس به سوی او بروید و بگویید: ما دو فرستاده پروردگار توایم، پس فرزندان اسرائیل را با ما بفرست، و عذابشان مکن، به راستی ما برای تو از جانب پروردگارت معجزه‌ای آورده‌ایم، و بر هر کس که از هدایت پیروی کند درود باد.'),
(2396, 20, 48, 'در حقیقت به سوی ما وحی آمده که عذاب بر کسی است که تکذیب کند و روی گرداند.'),
(2397, 20, 49, '[فرعون‌] گفت: «ای موسی، پروردگار شما دو تن کیست؟»'),
(2398, 20, 50, 'گفت: «پروردگار ما کسی است که هر چیزی را خلقتی که درخور اوست داده، سپس آن را هدایت فرموده است.»'),
(2399, 20, 51, 'گفت: «حال نسلهای گذشته چون است؟»'),
(2400, 20, 52, 'گفت: «علم آن، در کتابی نزد پروردگار من است. پروردگارم نه خطا می‌کند و نه فراموش می‌نماید.»'),
(2401, 20, 53, 'همان کسی که زمین را برایتان گهواره‌ای ساخت، و برای شما در آن، راهها ترسیم کرد و از آسمان آبی فرود آورد، پس به وسیله آن رستنیهای گوناگون، جفت جفت بیرون آوردیم.'),
(2402, 20, 54, 'بخورید و دامهایتان را بچرانید که قطعاً در اینها برای خردمندان نشانه‌هایی است.'),
(2403, 20, 55, 'از این [زمین‌] شما را آفریده‌ایم، در آن شما را بازمی‌گردانیم و بار دیگر شما را از آن بیرون می‌آوریم.'),
(2404, 20, 56, 'در حقیقت، [ما] همه آیات خود را به [فرعون‌] نشان دادیم، ولی [او آنها را] دروغ پنداشت و نپذیرفت.'),
(2405, 20, 57, 'گفت: «ای موسی، آمده‌ای تا با سحر خود، ما را از سرزمینمان بیرون کنی؟'),
(2406, 20, 58, 'ما [هم‌] قطعاً برای تو سحری مثل آن خواهیم آورد، پس میان ما و خودت موعدی بگذار که نه ما آن را خلاف کنیم و نه تو، [آن هم‌] در جایی هموار.»'),
(2407, 20, 59, '[موسی‌] گفت: «موعد شما روز جشن باشد که مردم پیش از ظهر گرد می‌آیند.»'),
(2408, 20, 60, 'پس فرعون رفت و [همه‌] نیرنگ خود را گرد آورد و بازآمد.'),
(2409, 20, 61, 'موسی به [ساحران‌] گفت: «وای بر شما، به خدا دروغ مبندید که شما را به عذابی [سخت‌] هلاک می‌کند، و هر که دروغ بندد نومید می‌گردد.»'),
(2410, 20, 62, '[ساحران‌] میان خود، در باره کارشان به نزاع برخاستند و به نجوا پرداختند.'),
(2411, 20, 63, '[فرعونیان‌] گفتند: «قطعاً این دو تن ساحرند [و] می‌خواهند شما را با سحر خود از سرزمینتان بیرون کنند، و آیین والای شما را براندازند.»'),
(2412, 20, 64, 'پس نیرنگ خود را گرد آورید و به صف پیش آیید. در حقیقت، امروز هر که فایق آید خوشبخت می‌شود.»'),
(2413, 20, 65, '[ساحران‌] گفتند: «ای موسی، یا تو می‌افکنی یا [ما] نخستین کس باشیم که می اندازیم؟»'),
(2414, 20, 66, 'گفت: «[نه،] بلکه شما بیندازید.» پس ناگهان ریسمانها و چوبدستی‌هایشان، بر اثر سِحْرشان، در خیال او، [چنین‌] می‌نمود که آنها به شتاب می‌خزند.'),
(2415, 20, 67, 'و موسی در خود بیمی احساس کرد.'),
(2416, 20, 68, 'گفتیم: «مترس که تو خود برتری؛'),
(2417, 20, 69, 'و آنچه در دست راست داری بینداز، تا هر چه را ساخته‌اند ببلعد. در حقیقت، آنچه سرهم‌بندی کرده‌اند، افسون افسونگر است، و افسونگر هر جا برود رستگار نمی شود.»'),
(2418, 20, 70, 'پس ساحران به سجده درافتادند. گفتند: «به پروردگار موسی و هارون ایمان آوردیم.»'),
(2419, 20, 71, '[فرعون‌] گفت: «آیا پیش از آنکه به شما اجازه دهم، به او ایمان آوردید؟ قطعاً او بزرگ شماست که به شما سحر آموخته است، پس بی‌شک دستهای شما و پاهایتان را یکی از راست و یکی از چپ قطع می‌کنم و شما را بر تنه‌های درخت خرما به دار می‌آویزم، تا خوب بدانید عذاب کدام یک از ما سخت‌تر و پایدارتر است.»'),
(2420, 20, 72, 'گفتند: «ما هرگز تو را بر معجزاتی که به سوی ما آمده و [بر] آن کس که ما را پدید آورده است، ترجیح نخواهیم داد؛ پس هر حکمی می‌خواهی بکن که تنها در این زندگی دنیاست که [تو] حکم می‌رانی.'),
(2421, 20, 73, 'ما به پروردگارمان ایمان آوردیم، تا گناهان ما و آن سحری که ما را بدان واداشتی بر ما ببخشاید، و خدا بهتر و پایدارتر است.»'),
(2422, 20, 74, 'در حقیقت، هر که به نزد پروردگارش گنهکار رود، جهنم برای اوست. در آن نه می‌میرد و نه زندگی می‌یابد.'),
(2423, 20, 75, 'و هر که مؤمن به نزد او رود، در حالی که کارهای شایسته انجام داده باشد، برای آنان درجات والا خواهد بود:'),
(2424, 20, 76, 'بهشتهای عدن که از زیر [درختان‌] آن جویبارها روان است. جاودانه در آن می‌مانند، و این است پاداش کسی که به پاکی گراید.'),
(2425, 20, 77, 'و در حقیقت به موسی وحی کردیم که: «بندگانم را شبانه ببر، و راهی خشک در دریا برای آنان باز کن که نه از فرارسیدن [دشمن‌] بترسی و نه [از غرق‌شدن‌] بیمناک باشی.»'),
(2426, 20, 78, 'پس فرعون با لشکریانش آنها را دنبال کرد، و[لی‌] از دریا آنچه آنان را فرو پوشانید، فرو پوشانید.'),
(2427, 20, 79, 'و فرعون قوم خود را گمراه کرد و هدایت ننمود.'),
(2428, 20, 80, '«ای فرزندان اسرائیل، در حقیقت، [ما] شما را از [دست‌] دشمنتان رهانیدیم، و در جانب راست طور با شما وعده نهادیم و بر شما ترنجبین و بلدرچین فرو فرستادیم.'),
(2429, 20, 81, 'از خوراکیهای پاکیزه‌ای که روزی شما کردیم، بخورید و[لی‌] در آن زیاده‌روی مکنید که خشم من بر شما فرود آید، و هر کس خشم من بر او فرود آید، قطعاً در [ورطه‌] هلاکت افتاده است.'),
(2430, 20, 82, 'و به یقین، من آمرزنده کسی هستم که توبه کند و ایمان بیاورد و کار شایسته نماید و به راه راست راهسپر شود.»'),
(2431, 20, 83, '«و ای موسی، چه چیز تو را [دور] از قوم خودت، به شتاب واداشته است؟»'),
(2432, 20, 84, 'گفت: «اینان در پی منند، و من -ای پروردگارم- به سویت شتافتم تا خشنود شوی.»'),
(2433, 20, 85, 'فرمود: «در حقیقت، ما قوم تو را پس از [عزیمت‌] تو آزمودیم و سامری آنها را گمراه ساخت.»'),
(2434, 20, 86, 'پس موسی خشمگین و اندوهناک به سوی قوم خود برگشت [و] گفت: «ای قوم من، آیا پروردگارتان به شما وعده نیکو نداد؟ آیا این مدت بر شما طولانی می‌نمود، یا خواستید خشمی از پروردگارتان بر شما فرود آید که با وعده من مخالفت کردید؟»'),
(2435, 20, 87, 'گفتند: «ما به اختیار خود با تو خلاف وعده نکردیم، ولی از زینت‌آلات قوم، بارهایی سنگین بر دوش داشتیم و آنها را افکندیم و [خود] سامری [هم زینت‌آلاتش را] همین گونه بینداخت.'),
(2436, 20, 88, 'پس برای آنان پیکر گوساله‌ای که صدایی داشت بیرون آورد، و [او و پیروانش‌] گفتند: «این خدای شما و خدای موسی است، و [پیمان خدا را] فراموش کرد.»'),
(2437, 20, 89, 'مگر نمی‌بینند که [گوساله‌] پاسخ سخن آنان را نمی‌دهد و به حالشان سود و زیانی ندارد؟'),
(2438, 20, 90, 'و در حقیقت، هارون قبلا به آنان گفته بود: «ای قوم من، شما به وسیله این [گوساله‌] مورد آزمایش قرار گرفته‌اید، و پروردگار شما [خدای‌] رحمان است، پس مرا پیروی کنید و فرمان مرا پذیرا باشید.»'),
(2439, 20, 91, 'گفتند: «ما هرگز از پرستش آن دست بر نخواهیم داشت تا موسی به سوی ما بازگردد.»'),
(2440, 20, 92, '[موسی‌] گفت: «ای هارون، وقتی دیدی آنها گمراه شدند چه چیز مانع تو شد،'),
(2441, 20, 93, 'که از من پیروی کنی؟ آیا از فرمانم سر باز زدی؟»'),
(2442, 20, 94, 'گفت: «ای پسر مادرم، نه ریش مرا بگیر و نه [موی‌] سرم را، من ترسیدم بگویی: میان بنی‌اسرائیل تفرقه انداختی و سخنم را مراعات نکردی.»'),
(2443, 20, 95, '[موسی‌] گفت: «ای سامری، منظور تو چه بود؟»'),
(2444, 20, 96, 'گفت: «به چیزی که [دیگران‌] به آن پی نبردند، پی بردم، و به قدر مشتی از رد پای فرستاده [خدا، جبرئیل‌] برداشتم و آن را در پیکر [گوساله‌] انداختم، و نفس من برایم چنین فریبکاری کرد.»'),
(2445, 20, 97, 'گفت: «پس برو که بهره تو در زندگی این باشد که [به هر که نزدیک تو آمد] بگویی: [به من‌] دست مزنید و تو را موعدی خواهد بود که هرگز از آن تخلف نخواهی کرد، و [اینک‌] به آن خدایی که پیوسته ملازمش بودی بنگر، آن را قطعاً می‌سوزانیم و خاکسترش می‌کنیم [و] در دریا فرو می‌پاشیم.»'),
(2446, 20, 98, '«معبود شما تنها آن خدایی است که جز او معبودی نیست، و دانش او همه چیز را در بر گرفته است.»'),
(2447, 20, 99, 'این گونه از اخبار پیشین بر تو حکایت می‌رانیم، و مسلماً به تو از جانب خود قرآنی داده‌ایم.'),
(2448, 20, 100, 'هر کس از [پیروی‌] آن روی برتابد، روز قیامت بار گناهی بر دوش می‌گیرد.'),
(2449, 20, 101, 'پیوسته در آن [حال‌] می‌ماند، و چه بد باری روز قیامت خواهند داشت.'),
(2450, 20, 102, '[همان‌] روزی که در صور دمیده می‌شود، و در آن روز مجرمان را کبودچشم برمی‌انگیزیم.'),
(2451, 20, 103, 'میان خود به طور پنهانی با یکدیگر می‌گویند: «شما [در دنیا] جز ده [روز، بیش‌] نمانده‌اید.»'),
(2452, 20, 104, 'ما داناتریم به آنچه می‌گویند، آنگاه که نیک‌آیین‌ترین آنان می‌گوید: «جز یک روز، بیش نمانده‌اید.»'),
(2453, 20, 105, 'و از تو در باره کوهها می‌پرسند، بگو: «پروردگارم آنها را [در قیامت‌] ریز ریز خواهد ساخت،'),
(2454, 20, 106, 'پس آنها را پهن و هموار خواهد کرد،'),
(2455, 20, 107, 'نه در آن کژی می‌بینی و نه ناهمواری.'),
(2456, 20, 108, 'در آن روز، [همه مردم‌]، داعی [حق‌] را -که هیچ انحرافی در او نیست- پیروی می‌کنند، و صداها در مقابل [خدای‌] رحمان خاشع می‌گردد، و جز صدایی آهسته نمی‌شنوی.'),
(2457, 20, 109, 'در آن روز، شفاعت [به کسی‌] سود نبخشد، مگر کسی را که [خدای‌] رحمان اجازه دهد و سخنش او را پسند آید.'),
(2458, 20, 110, 'آنچه را که آنان در پیش دارند و آنچه را که پشت سر گذاشته‌اند می‌داند، و حال آنکه ایشان بدان دانشی ندارند.'),
(2459, 20, 111, 'و چهره‌ها برای آن [خدای‌] زنده پاینده خضوع می‌کنند، و آن کس که ظلمی بر دوش دارد نومید می‌ماند.'),
(2460, 20, 112, 'و هر کس کارهای شایسته کند، در حالی که مؤمن باشد، نه از ستمی می‌هراسد و نه از کاسته شدن [حقّش‌].'),
(2461, 20, 113, 'و این گونه آن را [به صورت‌] قرآنی عربی نازل کردیم، و در آن از انواع هشدارها سخن آوردیم، شاید آنان راه تقوا در پیش گیرند، یا [این کتاب‌] پندی تازه برای آنان بیاورد.'),
(2462, 20, 114, 'پس بلندمرتبه است خدا، فرمانروای بر حق، و در [خواندن‌] قرآن، پیش از آنکه وحی آن بر تو پایان یابد، شتاب مکن، و بگو: «پروردگارا، بر دانشم بیفزای.»'),
(2463, 20, 115, 'و به یقین پیش از این با آدم پیمان بستیم، و[لی آن را] فراموش کرد، و برای او عزمی [استوار] نیافتیم.'),
(2464, 20, 116, 'و [یاد کن‌] هنگامی را که به فرشتگان گفتیم: «برای آدم سجده کنید.» پس، جز ابلیس که سر باز زد [همه‌] سجده کردند.'),
(2465, 20, 117, 'پس گفتیم: «ای آدم، در حقیقت، این [ابلیس‌] برای تو و همسرت دشمنی [خطرناک‌] است، زنهار تا شما را از بهشت به در نکند تا تیره‌بخت گردی.»'),
(2466, 20, 118, 'در حقیقت برای تو در آنجا این [امتیاز] است که نه گرسنه می‌شوی و نه برهنه می‌مانی.'),
(2467, 20, 119, 'و [هم‌] اینکه در آنجا نه تشنه می‌گردی و نه آفتاب‌زده.'),
(2468, 20, 120, 'پس شیطان او را وسوسه کرد، گفت: «ای آدم، آیا تو را به درخت جاودانگی و مُلکی که زایل نمی‌شود، راه نمایم؟»'),
(2469, 20, 121, 'آنگاه از آن [درخت ممنوع‌] خوردند و برهنگی آنان برایشان نمایان شد و شروع کردند به چسبانیدن برگهای بهشت بر خود. و [این گونه‌] آدم به پروردگار خود عصیان ورزید و بیراهه رفت.'),
(2470, 20, 122, 'سپس پروردگارش او را برگزید و بر او ببخشود و [وی را] هدایت کرد.'),
(2471, 20, 123, 'فرمود: «همگی از آن [مقام‌] فرود آیید، در حالی که بعضی از شما دشمن بعضی دیگر است، پس اگر برای شما از جانب من رهنمودی رسد، هر کس از هدایتم پیروی کند نه گمراه می‌شود و نه تیره‌بخت.'),
(2472, 20, 124, 'و هر کس از یاد من دل بگرداند، در حقیقت، زندگی تنگ [و سختی‌] خواهد داشت، و روز رستاخیز او را نابینا محشور می‌کنیم.»'),
(2473, 20, 125, 'می‌گوید: «پروردگارا، چرا مرا نابینا محشور کردی با آنکه بینا بودم؟»'),
(2474, 20, 126, 'می‌فرماید: «همان طور که نشانه‌های ما بر تو آمد و آن را به فراموشی سپردی، امروز همان گونه فراموش می‌شوی.»'),
(2475, 20, 127, 'و این گونه هر که را به افراط گراییده و به نشانه‌های پروردگارش نگرویده است سزا می‌دهیم، و قطعاً شکنجه آخرت سخت‌تر و پایدارتر است.'),
(2476, 20, 128, 'آیا برای هدایتشان کافی نبود که [ببینند] چه نسلها را پیش از آنان نابود کردیم که [اینک آنها] در سراهای ایشان راه می‌روند؟ به راستی برای خردمندان در این [امر] نشانه‌هایی [عبرت‌انگیز] است.'),
(2477, 20, 129, 'و اگر سخنی از پروردگارت پیشی نگرفته و موعدی معین مقرر نشده بود، قطعاً [عذاب آنها] لازم می‌آمد.'),
(2478, 20, 130, 'پس بر آنچه می‌گویند شکیبا باش، و پیش از بر آمدن آفتاب و قبل از فرو شدن آن، با ستایش پروردگارت [او را] تسبیح گوی، و برخی از ساعات شب و حوالی روز را به نیایش پرداز، باشد که خشنود گردی.'),
(2479, 20, 131, 'و زنهار به سوی آنچه اصنافی از ایشان را از آن برخوردار کردیم [و فقط] زیور زندگی دنیاست تا ایشان را در آن بیازماییم، دیدگان خود مدوز، و [بدان که‌] روزیِ پروردگار تو بهتر و پایدارتر است.'),
(2480, 20, 132, 'و کسان خود را به نماز فرمان ده و خود بر آن شکیبا باش. ما از تو جویای روزی نیستیم، ما به تو روزی می‌دهیم، و فرجام [نیک‌] برای پرهیزگاری است.'),
(2481, 20, 133, 'و گفتند: «چرا از جانب پروردگارش معجزه‌ای برای ما نمی‌آورد؟» آیا دلیل روشن آنچه در صحیفه‌های پیشین است برای آنان نیامده است؟'),
(2482, 20, 134, 'و اگر ما آنان را قبل از [آمدن قرآن‌] به عذابی هلاک می‌کردیم، قطعاً می‌گفتند: «پروردگارا، چرا پیامبری به سوی ما نفرستادی تا پیش از آنکه خوار و رسوا شویم از آیات تو پیروی کنیم؟»'),
(2483, 20, 135, 'بگو: «همه در انتظارند. پس در انتظار باشید. زودا که بدانید یاران راه راست کیانند و چه کسی راه‌یافته است.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'برای مردم [وقت‌] حسابشان نزدیک شده است، و آنان در بی‌خبری رویگردانند.'),
(2485, 21, 2, 'هیچ پند تازه‌ای از پروردگارشان نیامد، مگر اینکه بازی‌کنان آن را شنیدند.'),
(2486, 21, 3, 'در حالی که دلهایشان مشغول است. و آنانکه ستم کردند پنهانی به نجوا برخاستند که: «آیا این [مرد] جز بشری مانند شماست؟ آیا دیده و دانسته به سوی سحر می‌روید؟»'),
(2487, 21, 4, '[پیامبر] گفت: «پروردگارم [هر] گفتار[ی‌] را در آسمان و زمین می‌داند، و اوست شنوای دانا.»'),
(2488, 21, 5, 'بلکه گفتند: «خوابهای شوریده است، [نه‌] بلکه آن را بربافته، بلکه او شاعری است. پس همان گونه که برای پیشینیان هم عرضه شد، باید برای ما نشانه‌ای بیاورد.»'),
(2489, 21, 6, 'قبل از آنان [نیز مردم‌] هیچ شهری -که آن را نابود کردیم- [به آیات ما] ایمان نیاوردند. پس آیا اینان [به معجزه‌] ایمان می‌آورند؟'),
(2490, 21, 7, 'و پیش از تو [نیز] جز مردانی را که به آنان وحی می‌کردیم گسیل نداشتیم. اگر نمی‌دانید از پژوهندگان کتابهای آسمانی بپرسید.'),
(2491, 21, 8, 'و ایشان را جسدی که غذا نخورند قرار ندادیم و جاویدان [هم‌] نبودند.'),
(2492, 21, 9, 'سپس وعده [خود] به آنان را راست گردانیدیم و آنها و هر که را خواستیم نجات دادیم و افراطکاران را به هلاکت رسانیدیم.'),
(2493, 21, 10, 'در حقیقت، ما کتابی به سوی شما نازل کردیم که یاد شما در آن است. آیا نمی‌اندیشید؟'),
(2494, 21, 11, 'و چه بسیار شهرها را که [مردمش‌] ستمکار بودند در هم شکستیم، و پس از آنها قومی دیگر پدید آوردیم.'),
(2495, 21, 12, 'پس چون عذاب ما را احساس کردند، بناگاه از آن می‌گریختند.'),
(2496, 21, 13, '[هان‌] مگریزید، و به سوی آنچه در آن متنعّم بودید و [به سوی‌] سراهایتان بازگردید، باشد که شما مورد پرسش قرار گیرید.'),
(2497, 21, 14, 'گفتند: «ای وای بر ما، که ما واقعاً ستمگر بودیم.»'),
(2498, 21, 15, 'سخنشان پیوسته همین بود، تا آنان را دروشده بی‌جان گردانیدیم.'),
(2499, 21, 16, 'و آسمان و زمین و آنچه را که میان آن دو است به بازیچه نیافریدیم.'),
(2500, 21, 17, 'اگر می‌خواستیم بازیچه‌ای بگیریم، قطعاً آن را از پیش خود اختیار می‌کردیم.'),
(2501, 21, 18, 'بلکه حق را بر باطل فرو می‌افکنیم، پس آن را در هم می‌شکند، و بناگاه آن نابود می‌گردد. وای بر شما از آنچه وصف می‌کنید.'),
(2502, 21, 19, 'و هر که در آسمانها و زمین است برای اوست، و کسانی که نزد اویند از پرستش وی تکبّر نمی‌ورزند و درمانده نمی‌شوند.'),
(2503, 21, 20, 'شبانه روز، بی‌آنکه سستی ورزند، نیایش می‌کنند.'),
(2504, 21, 21, 'آیا برای خود خدایانی از زمین اختیار کرده‌اند که آنها [مردگان را] زنده می‌کنند؟'),
(2505, 21, 22, 'اگر در آنها [=زمین و آسمان‌] جز خدا، خدایانی [دیگر] وجود داشت، قطعاً [زمین و آسمان‌] تباه می‌شد. پس منزّه است خدا، پروردگار عرش، از آنچه وصف می‌کنند.'),
(2506, 21, 23, 'در آنچه [خدا] انجام می‌دهد چون و چرا راه ندارد، و[لی‌] آنان [=انسانها] سؤال خواهند شد.'),
(2507, 21, 24, 'آیا به جای او خدایانی برای خود گرفته‌اند؟ بگو: «برهانتان را بیاورید.» این است یادنامه هر که با من است و یادنامه هر که پیش از من بوده. [نه!] بلکه بیشترشان حق را نمی‌شناسند و در نتیجه از آن رویگردانند.'),
(2508, 21, 25, 'و پیش از تو هیچ پیامبری نفرستادیم مگر اینکه به او وحی کردیم که: «خدایی جز من نیست، پس مرا بپرستید.»'),
(2509, 21, 26, 'و گفتند: «[خدای‌] رحمان فرزندی اختیار کرده.» منزّه است او. بلکه [فرشتگان‌] بندگانی ارجمندند،'),
(2510, 21, 27, 'که در سخن بر او پیشی نمی‌گیرند، و خود به دستور او کار می‌کنند.'),
(2511, 21, 28, 'آنچه فراروی آنان و آنچه پشت سرشان است می‌داند، و جز برای کسی که [خدا] رضایت دهد، شفاعت نمی‌کنند و خود از بیم او هراسانند.'),
(2512, 21, 29, 'و هر کس از آنان بگوید: «من [نیز] جز او خدایی هستم»، او را به دوزخ کیفر می‌دهیم. [آری‌] سزای ستمکاران را این گونه می‌دهیم.'),
(2513, 21, 30, 'آیا کسانی که کفر ورزیدند ندانستند که آسمانها و زمین هر دو به هم پیوسته بودند، و ما آن دو را از هم جدا ساختیم، و هر چیز زنده‌ای را از آب پدید آوردیم؟ آیا [باز هم‌] ایمان نمی‌آورند؟'),
(2514, 21, 31, 'و در زمین کوههایی استوار نهادیم تا مبادا [زمین‌] آنان [=مردم‌] را بجنباند، و در آن راههایی فراخ پدید آوردیم، باشد که راه یابند.'),
(2515, 21, 32, 'و آسمان را سقفی محفوظ قرار دادیم، و[لی‌] آنان از [مطالعه در] نشانه‌های آن اِعراض می‌کنند.'),
(2516, 21, 33, 'و اوست آن کسی که شب و روز و خورشید و ماه را پدید آورده است. هر کدام از این دو در مداری [معیّن‌] شناورند.'),
(2517, 21, 34, 'و پیش از تو برای هیچ بشری جاودانگی [در دنیا] قرار ندادیم. آیا اگر تو از دنیا بروی آنان جاویدانند؟'),
(2518, 21, 35, 'هر نفسی چشنده مرگ است، و شما را از راه آزمایش به بد و نیک خواهیم آزمود، و به سوی ما بازگردانیده می‌شوید.'),
(2519, 21, 36, 'و کسانی که کافر شدند، چون تو را ببینند فقط به مسخره‌ات می‌گیرند [و می‌گویند:] «آیا این همان کس است که خدایانتان را [به بدی‌] یاد می‌کند؟» در حالی که آنان خود، یاد [خدای‌] رحمان را منکرند.'),
(2520, 21, 37, 'انسان از شتاب آفریده شده است. به زودی آیاتم را به شما نشان می‌دهم. پس [عذاب را] به شتاب از من مخواهید.'),
(2521, 21, 38, 'و می‌گویند: «اگر راست می‌گویید، این وعده [قیامت‌] کی خواهد بود؟»'),
(2522, 21, 39, 'کاش آنان که کافر شده‌اند می‌دانستند آنگاه که آتش را نه از چهره‌های خود و نه از پشتشان بازنمی‌توانند داشت، و خود مورد حمایت قرار نمی‌گیرند [چه حالی خواهند داشت‌].'),
(2523, 21, 40, 'بلکه [آتش‌] به طور ناگهانی به آنان می‌رسد و ایشان را بهت‌زده می‌کند [به گونه‌ای‌] که نه می‌توانند آن را برگردانند و نه به آنان مهلت داده می‌شود.'),
(2524, 21, 41, 'و مسلمّاً پیامبران پیش از تو [نیز] مورد ریشخند قرار گرفتند، پس کسانی که آنان را مسخره می‌کردند، [سزای‌] آنچه که آن را به ریشخند می‌گرفتند گریبانگیرشان شد.'),
(2525, 21, 42, 'بگو: «چه کسی شما را شب و روز از [عذاب‌] رحمان حفظ می‌کند؟» [نه‌] بلکه آنان از یاد پروردگارشان رویگردانند.'),
(2526, 21, 43, '[34]آیا برای آنان خدایانی غیر از ماست که از ایشان حمایت کنند؟ [آن خدایان‌] نه می‌توانند خود را یاری کنند و نه از جانب ما یاری شوند.'),
(2527, 21, 44, '[نه‌] بلکه اینها و پدرانشان را برخوردار کردیم تا عمرشان به درازا کشید. آیا نمی‌بینند که ما می‌آییم و زمین را از جوانب آن فرو می‌کاهیم؟ آیا باز هم آنان پیروزند؟'),
(2528, 21, 45, 'بگو: «من شما را فقط به وسیله وحی هشدار می‌دهم.» و[لی‌] چون کران بیم داده شوند، دعوت را نمی‌شنوند.'),
(2529, 21, 46, 'و اگر شمه‌ای از عذاب پروردگارت به آنان برسد، خواهند گفت: «ای وای بر ما که ستمکار بودیم.»'),
(2530, 21, 47, 'و ترازوهای داد را در روز رستاخیز می‌نهیم، پس هیچ کس [در] چیزی ستم نمی‌بیند، و اگر [عمل‌] هموزن دانه خردلی باشد آن را می‌آوریم و کافی است که ما حسابرس باشیم.'),
(2531, 21, 48, 'و در حقیقت، به موسی و هارون فرقان دادیم و [کتابشان‌] برای پرهیزگاران روشنایی و اندرزی است.'),
(2532, 21, 49, '[همان‌] کسانی که از پروردگارشان در نهان می‌ترسند و از قیامت هراسناکند.'),
(2533, 21, 50, 'و این [کتاب‌] -که آن را نازل کرده‌ایم- پندی خجسته است. آیا باز هم آن را انکار می‌کنید؟'),
(2534, 21, 51, 'و در حقیقت، پیش از آن، به ابراهیم رشد [فکری‌]اش را دادیم و ما به [شایستگی‌] او دانا بودیم.'),
(2535, 21, 52, 'آنگاه که به پدر خود و قومش گفت: «این مجسمه‌هایی که شما ملازم آنها شده‌اید چیستند؟»'),
(2536, 21, 53, 'گفتند: «پدران خود را پرستندگان آنها یافتیم.»'),
(2537, 21, 54, 'گفت: «قطعاً شما و پدرانتان در گمراهی آشکاری بودید.»'),
(2538, 21, 55, 'گفتند: «آیا حق را برای ما آورده‌ای یا تو از شوخی‌کنندگانی؟»'),
(2539, 21, 56, 'گفت: «[نه‌] بلکه پروردگارتان، پروردگار آسمانها و زمین است، همان کسی که آنها را پدید آورده است، و من بر این [واقعیت‌] از گواهانم.'),
(2540, 21, 57, 'و سوگند به خدا که پس از آنکه پشت کردید و رفتید، قطعاً در کار بتانتان تدبیری خواهم کرد.»'),
(2541, 21, 58, 'پس آنها را -جز بزرگترشان را- ریز ریز کرد، باشد که ایشان به سراغ آن بروند.'),
(2542, 21, 59, 'گفتند: «چه کسی با خدایان ما چنین [معامله‌ای‌] کرده، که او واقعاً از ستمکاران است؟»'),
(2543, 21, 60, 'گفتند: «شنیدیم جوانی، از آنها [به بدی‌] یاد می‌کرد که به او ابراهیم گفته می‌شود.»'),
(2544, 21, 61, 'گفتند: «پس او را در برابر دیدگان مردم بیاورید، باشد که آنان شهادت دهند.»'),
(2545, 21, 62, 'گفتند: «ای ابراهیم، آیا تو با خدایان ما چنین کردی؟»'),
(2546, 21, 63, 'گفت: «[نه‌] بلکه آن را این بزرگترشان کرده است، اگر سخن می‌گویند از آنها بپرسید.»'),
(2547, 21, 64, 'پس به خود آمده و [به یکدیگر] گفتند: «در حقیقت، شما ستمکارید.»'),
(2548, 21, 65, 'سپس سرافکنده شدند [و گفتند:] «قطعاً دانسته‌ای که اینها سخن نمی‌گویند.»'),
(2549, 21, 66, 'گفت: «آیا جز خدا چیزی را می‌پرستید که هیچ سود و زیانی به شما نمی‌رساند؟'),
(2550, 21, 67, 'اف بر شما و بر آنچه غیر از خدا می‌پرستید. مگر نمی‌اندیشید؟»'),
(2551, 21, 68, 'گفتند: «اگر کاری می‌کنید، او را بسوزانید و خدایانتان را یاری دهید.»'),
(2552, 21, 69, 'گفتیم: «ای آتش، برای ابراهیم سرد و بی‌آسیب باش.»'),
(2553, 21, 70, 'و خواستند به او نیرنگی بزنند و[لی‌] آنان را زیانکارترین [مردم‌] قرار دادیم.'),
(2554, 21, 71, 'و او و لوط را [برای رفتن‌] به سوی آن سرزمینی که برای جهانیان در آن برکت نهاده بودیم، رهانیدیم.'),
(2555, 21, 72, 'و اسحاق و یعقوب را [به عنوان نعمتی‌] افزون به او بخشودیم و همه را از شایستگان قرار دادیم.'),
(2556, 21, 73, 'و آنان را پیشوایانی قرار دادیم که به فرمان ما هدایت می‌کردند، و به ایشان انجام دادن کارهای نیک و برپاداشتن نماز و دادن زکات را وحی کردیم و آنان پرستنده ما بودند.'),
(2557, 21, 74, 'و به لوط حکمت و دانش عطا کردیم و او را از آن شهری که [مردمش‌] کارهای پلید [جنسی‌] می‌کردند نجات دادیم. به راستی آنها گروه بد و منحرفی بودند.'),
(2558, 21, 75, 'و او را در رحمت خویش داخل کردیم، زیرا او از شایستگان بود.'),
(2559, 21, 76, 'و نوح را [یاد کن‌] آنگاه که پیش از [سایر پیامبران‌] ندا کرد، پس ما او را اجابت کردیم، و وی را با خانواده‌اش از بلای بزرگ رهانیدیم.'),
(2560, 21, 77, 'و او را در برابر مردمی که نشانه‌های ما را به دروغ گرفته بودند پیروزی بخشیدیم، چرا که آنان مردم بدی بودند، پس همه ایشان را غرق کردیم.'),
(2561, 21, 78, 'و داوود و سلیمان را [یاد کن‌] هنگامی که در باره آن کشتزار -که گوسفندان مردم شب‌هنگام در آن چریده بودند- داوری می‌کردند، و [ما] شاهد داوری آنان بودیم.'),
(2562, 21, 79, 'پس آن [داوری‌] را به سلیمان فهماندیم، و به هر یک [از آن دو] حکمت و دانش عطا کردیم، و کوهها را با داوود و پرندگان به نیایش واداشتیم، و ما کننده [این کار] بودیم.'),
(2563, 21, 80, 'و به [داوود] فن زره [سازی‌] آموختیم، تا شما را از [خطرات‌] جنگتان حفظ کند. پس آیا شما سپاسگزارید؟'),
(2564, 21, 81, 'و برای سلیمان، تندباد را [رام کردیم‌] که به فرمان او به سوی سرزمینی که در آن برکت نهاده بودیم جریان می‌یافت، و ما به هر چیزی دانا بودیم.'),
(2565, 21, 82, 'و برخی از شیاطین بودند که برای او غواصی و کارهایی غیر از آن می‌کردند، و ما مراقب [حال‌] آنها بودیم.'),
(2566, 21, 83, 'و ایوب را [یاد کن‌] هنگامی که پروردگارش را ندا داد که: «به من آسیب رسیده است و تویی مهربانترین مهربانان.»'),
(2567, 21, 84, 'پس [دعای‌] او را اجابت نمودیم و آسیب وارده بر او را برطرف کردیم، و کسان او و نظیرشان را همراه با آنان [مجدداً] به وی عطا کردیم [تا] رحمتی از جانب ما و عبرتی برای عبادت‌کنندگان [باشد].'),
(2568, 21, 85, 'و اسماعیل و ادریس و ذوالکفل را [یاد کن‌] که همه از شکیبایان بودند.'),
(2569, 21, 86, 'و آنان را در رحمت خود داخل نمودیم، چرا که ایشان از شایستگان بودند.'),
(2570, 21, 87, 'و «ذوالنون» را [یاد کن‌] آنگاه که خشمگین رفت و پنداشت که ما هرگز بر او قدرتی نداریم، تا در [دل‌] تاریکیها ندا درداد که: «معبودی جز تو نیست، منزّهی تو، راستی که من از ستمکاران بودم.»'),
(2571, 21, 88, 'پس [دعای‌] او را برآورده کردیم و او را از اندوه رهانیدیم، و مؤمنان را [نیز] چنین نجات می‌دهیم.'),
(2572, 21, 89, 'و زکریّا را [یاد کن‌] هنگامی که پروردگار خود را خواند: «پروردگارا، مرا تنها مگذار و تو بهترین ارث برندگانی.»'),
(2573, 21, 90, 'پس [دعای‌] او را اجابت نمودیم، و یحیی را بدو بخشیدیم و همسرش را برای او شایسته [و آماده حمل‌] کردیم، زیرا آنان در کارهای نیک شتاب می‌نمودند و ما را از روی رغبت و بیم می‌خواندند و در برابر ما فروتن بودند.'),
(2574, 21, 91, 'و آن [زن را یاد کن‌] که خود را پاکدامن نگاه داشت، و از روح خویش در او دمیدیم و او و پسرش را برای جهانیان آیتی قرار دادیم.'),
(2575, 21, 92, 'این است امت شما که امتی یگانه است، و منم پروردگار شما، پس مرا بپرستید.'),
(2576, 21, 93, 'و[لی‌] دینشان را میان خود پاره پاره کردند. همه به سوی ما بازمی‌گردند.'),
(2577, 21, 94, 'پس هر که کارهای شایسته انجام دهد و مؤمن [هم‌] باشد، برای تلاش او ناسپاسی نخواهد بود، و ماییم که به سود او ثبت می‌کنیم.'),
(2578, 21, 95, 'و بر [مردم‌] شهری که آن را هلاک کرده‌ایم، بازگشتشان [به دنیا] حرام است.'),
(2579, 21, 96, 'تا وقتی که یأجوج و مأجوج [راهشان‌] گشوده شود و آنها از هر پشته‌ای بتازند،'),
(2580, 21, 97, 'و وعده حق نزدیک گردد، ناگهان دیدگان کسانی که کفر ورزیده‌اند خیره می‌شود [و می‌گویند:] «ای وای بر ما که از این [روز] در غفلت بودیم، بلکه ما ستمگر بودیم.»'),
(2581, 21, 98, 'در حقیقت، شما و آنچه غیر از خدا می‌پرستید، هیزم دوزخید. شما در آن وارد خواهید شد.'),
(2582, 21, 99, 'اگر اینها خدایانی [واقعی‌] بودند در آن وارد نمی‌شدند، و حال آنکه جملگی در آن ماندگارند.'),
(2583, 21, 100, 'برای آنها در آنجا ناله‌ای زار است و در آنجا [چیزی‌] نمی‌شنوند.'),
(2584, 21, 101, 'بی‌گمان کسانی که قبلا از جانب ما به آنان وعده نیکو داده شده است از آن [آتش‌] دور داشته خواهند شد.'),
(2585, 21, 102, 'صدای آن را نمی‌شنوند، و آنان در میان آنچه دلهایشان بخواهد جاودانند.'),
(2586, 21, 103, 'دلهره بزرگ، آنان را غمگین نمی‌کند و فرشتگان از آنها استقبال می‌کنند [و به آنان می‌گویند:] این همان روزی است که به شما وعده می‌دادند.'),
(2587, 21, 104, 'روزی که آسمان را همچون در پیچیدن صفحه نامه‌ها در می‌پیچیم. همان گونه که بار نخست آفرینش را آغاز کردیم، دوباره آن را بازمی‌گردانیم. وعده‌ای است بر عهده ما، که ما انجام‌دهنده آنیم.'),
(2588, 21, 105, 'و در حقیقت، در زبور پس از تورات نوشتیم که زمین را بندگان شایسته ما به ارث خواهند برد.'),
(2589, 21, 106, 'به راستی در این [امور] برای مردم عبادت‌پیشه ابلاغی [حقیقی‌] است.'),
(2590, 21, 107, 'و تو را جز رحمتی برای جهانیان نفرستادیم.'),
(2591, 21, 108, 'بگو: «جز این نیست که به من وحی می‌شود که خدای شما خدایی یگانه است. پس آیا مسلمان می‌شوید؟»'),
(2592, 21, 109, 'پس اگر روی برتافتند بگو: «به [همه‌] شما به طور یکسان اعلام کردم، و نمی‌دانم آنچه وعده داده شده‌اید آیا نزدیک است یا دور.»'),
(2593, 21, 110, '[آری،] او سخن آشکار را می‌داند و آنچه را پوشیده می‌دارید می‌داند.'),
(2594, 21, 111, 'و نمی‌دانم، شاید آن برای شما آزمایشی و تا چند گاهی [وسیله‌] برخورداری باشد.'),
(2595, 21, 112, 'گفت: «پروردگارا، [خودت‌] به حق داوری کن، و به رغم آنچه وصف می‌کنید، پروردگار ما همان بخشایشگر دستگیر است.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ای مردم، از پروردگار خود پروا کنید، چرا که زلزله رستاخیز امری هولناک است.'),
(2597, 22, 2, 'روزی که آن را ببینید، هر شیردهنده‌ای آن را که شیر می‌دهد [از ترس‌] فرو می‌گذارد، و هر آبستنی بار خود را فرو می‌نهد، و مردم را مست می‌بینی و حال آنکه مست نیستند، ولی عذاب خدا شدید است.'),
(2598, 22, 3, 'و برخی از مردم در باره خدا بدون هیچ علمی مجادله می‌کنند و از هر شیطان سرکشی پیروی می‌نمایند.'),
(2599, 22, 4, 'بر [شیطان‌] مقرر شده است که هر کس او را به دوستی گیرد، قطعاً او وی را گمراه می‌سازد و به عذاب آتشش می‌کشاند.'),
(2600, 22, 5, 'ای مردم، اگر در باره برانگیخته شدن در شکّید، پس [بدانید] که ما شما را از خاک آفریده‌ایم، سپس از نطفه، سپس از علقه، آنگاه از مضغه، دارای خلقت کامل و [احیاناً] خلقت ناقص، تا [قدرت خود را] بر شما روشن گردانیم. و آنچه را اراده می‌کنیم تا مدتی معین در رحمها قرار می‌دهیم، آنگاه شما را [به صورت‌] کودک برون می‌آوریم، سپس [حیات شما را ادامه می‌دهیم‌] تا به حد رشدتان برسید، و برخی از شما [زودرس‌] می‌میرد، و برخی از شما به غایت پیری می‌رسد به گونه‌ای که پس از دانستن [بسی چیزها] چیزی نمی‌داند. و زمین را خشکیده می‌بینی و[لی‌] چون آب بر آن فرود آوریم به جنبش درمی‌آید و نمو می‌کند و از هر نوع [رستنیهای‌] نیکو می‌رویاند.'),
(2601, 22, 6, 'این [قدرت نماییها] بدان سبب است که خدا خود حق است، و اوست که مردگان را زنده می‌کند و [هم‌] اوست که بر هر چیزی تواناست.'),
(2602, 22, 7, 'و [هم‌] آنکه رستاخیز آمدنی است [و] شکی در آن نیست، و در حقیقت، خداست که کسانی را که در گورهایند برمی‌انگیزد.'),
(2603, 22, 8, 'و از [میان‌] مردم کسی است که در باره خدا بدون هیچ دانش و بی‌هیچ رهنمود و کتاب روشنی به مجادله می‌پردازد،'),
(2604, 22, 9, '[آن هم‌] از سرِ نِخْوت، تا [مردم را] از راه خدا گمراه کند. در این دنیا برای او رسوایی است، و در روز رستاخیز او را عذاب آتش سوزان می‌چشانیم.'),
(2605, 22, 10, 'این [کیفر] به سزای چیزهایی است که دستهای تو پیش فرستاده است و [گرنه‌] خدا به بندگان خود بیدادگر نیست.'),
(2606, 22, 11, 'و از میان مردم کسی است که خدا را فقط بر یک حال [و بدون عمل‌] می‌پرستد. پس اگر خیری به او برسد بدان اطمینان یابد، و چون بلایی بدو رسد روی برتابد. در دنیا و آخرت زیان دیده است. این است همان زیان آشکار.'),
(2607, 22, 12, 'به جای خدا چیزی را می‌خواند که نه زیانی به او می‌رساند و نه سودش می‌دهد. این است همان گمراهی دور و دراز.'),
(2608, 22, 13, 'کسی را می‌خواند که زیانش از سودش نزدیکتر است. وه چه بد مولایی و چه بد دمسازی!'),
(2609, 22, 14, 'بی‌گمان، خدا کسانی را که گرویده و کارهای شایسته کرده‌اند به باغهایی درمی‌آورد که از زیر [درختان‌] آن رودبارها روان است. خدا هر چه بخواهد انجام می‌دهد.'),
(2610, 22, 15, 'هر که می‌پندارد که خدا [پیامبرش‌] را در دنیا و آخرت هرگز یاری نخواهد کرد [بگو] تا طنابی به سوی سقف کشد [و خود را حلق‌آویز کند] سپس [آن را] بِبُرَد. آنگاه بنگرد که آیا نیرنگش چیزی را که مایه خشم او شده از میان خواهد برد؟'),
(2611, 22, 16, 'و بدین گونه [قرآن‌] را [به صورت‌] آیاتی روشنگر نازل کردیم، و خداست که هر که را بخواهد راه می‌نماید.'),
(2612, 22, 17, 'کسانی که ایمان آوردند و کسانی که یهودی شدند و صابئی‌ها و مسیحیان و زرتشتیان و کسانی که شرک ورزیدند، البته خدا روز قیامت میانشان داوری خواهد کرد، زیرا خدا بر هر چیزی گواه است.'),
(2613, 22, 18, 'آیا ندانستی که خداست که هر کس در آسمانها و هر کس در زمین است، و خورشید و ماه و [تمام‌] ستارگان و کوهها و درختان و جنبندگان و بسیاری از مردم برای او سجده می‌کنند؟ و بسیاری‌اند که عذاب بر آنان واجب شده است. و هر که را خدا خوار کند او را گرامی‌دارنده‌ای نیست، چرا که خدا هر چه بخواهد انجام می‌دهد.'),
(2614, 22, 19, 'این دو [گروه،] دشمنان یکدیگرند که در باره پروردگارشان با هم ستیزه می‌کنند، و کسانی که کفر ورزیدند، جامه‌هایی از آتش برایشان بریده شده است [و] از بالای سرشان آب جوشان ریخته می‌شود.'),
(2615, 22, 20, 'آنچه در شکم آنهاست با پوست [بدن‌]شان بدان گداخته می‌گردد.'),
(2616, 22, 21, 'و برای [وارد کردن ضربت بر سر] آنان گرزهایی آهنین است.'),
(2617, 22, 22, 'هر بار بخواهند از [شدت‌] غم، از آن بیرون روند در آن باز گردانیده می‌شوند [که هان‌] بچشید عذاب آتش سوزان را.'),
(2618, 22, 23, 'خدا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند در باغهایی که از زیر [درختان‌] آن نهرها روان است درمی‌آورد: در آنجا با دستبندهایی از طلا و مروارید آراسته می‌شوند، و لباسشان در آنجا از پرنیان است.'),
(2619, 22, 24, 'و به گفتار پاک هدایت می‌شوند و به سوی راه [خدای‌] ستوده هدایت می‌گردند.'),
(2620, 22, 25, 'بی‌گمان، کسانی که کافر شدند و از راه خدا و مسجدالحرام -که آن را برای مردم، اعم از مقیم در آنجا و بادیه‌نشین، یکسان قرار داده‌ایم- جلو گیری می‌کنند، و [نیز] هر که بخواهد در آنجا به ستم [از حق ]منحرف شود، او را از عذابی دردناک می‌چشانیم.'),
(2621, 22, 26, 'و چون برای ابراهیم جای خانه را معین کردیم [بدو گفتیم:] «چیزی را با من شریک مگردان و خانه‌ام را برای طواف‌کنندگان و قیام‌کنندگان و رکوع‌کنندگان [و] سجده‌کنندگان پاکیزه دار.»'),
(2622, 22, 27, 'و در میان مردم برای [ادای‌] حج بانگ برآور تا [زایران‌] پیاده و [سوار] بر هر شتر لاغری -که از هر راه دوری می‌آیند- به سوی تو روی آورند،'),
(2623, 22, 28, 'تا شاهد منافع خویش باشند، و نام خدا را در روزهای معلومی بر دامهای زبان‌بسته‌ای که روزیِ آنان کرده است ببرند. پس، از آنها بخورید و به درمانده مستمند بخورانید.'),
(2624, 22, 29, 'سپس باید آلودگی خود را بزدایند و به نذرهای خود وفا کنند و بر گِرد آن خانه کهن [=کعبه‌] طواف به جای آورند.'),
(2625, 22, 30, 'این است [آنچه مقرر شده‌] و هر کس مقررات خدا را بزرگ دارد، آن برای او نزد پروردگارش بهتر است، و برای شما دامها حلال شده است، مگر آنچه بر شما خوانده می‌شود. پس، از پلیدی بتها دوری کنید، و از گفتار باطل اجتناب ورزید.'),
(2626, 22, 31, 'در حالی که گروندگان خالص به خدا باشید؛ نه شریک‌گیرندگان [برای‌] او! و هر کس به خدا شرک ورزد چنان است که گویی از آسمان فرو افتاده و مرغان [شکاری‌] او را ربوده‌اند یا باد او را به جایی دور افکنده است.'),
(2627, 22, 32, 'این است [فرایض خدا] و هر کس شعایر خدا را بزرگ دارد در حقیقت، آن [حاکی‌] از پاکی دلهاست.'),
(2628, 22, 33, 'برای شما در آن [دامها] تا مدتی معین سودهایی است، سپس جایگاه [قربانی‌کردن آنها و سایر فرایض‌] در خانه کهن [=کعبه‌] است.'),
(2629, 22, 34, 'و برای هر امتی مناسکی قرار دادیم، تا نام خدا را بر دامهای زبان‌بسته‌ای که روزی آنها گردانیده یاد کنند. پس [بدانید که‌] خدای شما خدایی یگانه است، پس به [فرمان‌] او گردن نهید. و فروتنان را بشارت ده.'),
(2630, 22, 35, 'همانان که چون [نام‌] خدا یاد شود، دلهایشان خشیت یابد و [آنان که‌] بر هر چه برسرشان آید صبر پیشه‌گانند و برپا دارندگان نمازند، و از آنچه روزیشان داده‌ایم انفاق می‌کنند.'),
(2631, 22, 36, 'و شتران فربه را برای شما از [جمله‌] شعایر خدا قرار دادیم: در آنها برای شما خیر است. پس نام خدا را بر آنها -در حالی که برپای ایستاده‌اند- ببَرید و چون به پهلو درغلتیدند از آنها بخورید و به تنگدست [سائل‌] و به بینوا[ی غیر سائل ]بخورانید. این گونه آنها را برای شما رام کردیم، امید که شکرگزار باشید.'),
(2632, 22, 37, 'هرگز [نه‌] گوشتهای آنها و نه خونهایشان به خدا نخواهد رسید، ولی [این‌] تقوای شماست که به او می‌رسد. این گونه [خداوند] آنها را برای شما رام کرد، تا خدا را به پاس آنکه شما را هدایت نموده به بزرگی یاد کنید، و نیکوکاران را مژده ده.'),
(2633, 22, 38, 'قطعاً خداوند از کسانی که ایمان آورده‌اند دفاع می‌کند، زیرا خدا هیچ خیانتکار ناسپاسی را دوست ندارد.'),
(2634, 22, 39, 'به کسانی که جنگ بر آنان تحمیل شده، رخصت [جهاد] داده شده است، چرا که مورد ظلم قرار گرفته‌اند، و البته خدا بر پیروزی آنان سخت تواناست.'),
(2635, 22, 40, 'همان کسانی که بناحق از خانه‌هایشان بیرون رانده شدند. [آنها گناهی نداشتند] جز اینکه می‌گفتند: «پروردگار ما خداست» و اگر خدا بعضی از مردم را با بعض دیگر دفع نمی‌کرد، صومعه‌ها و کلیساها و کنیسه‌ها و مساجدی که نام خدا در آنها بسیار برده می‌شود، سخت ویران می‌شد، و قطعاً خدا به کسی که [دین‌] او را یاری می‌کند، یاری می‌دهد، چرا که خدا سخت نیرومند شکست‌ناپذیر است.'),
(2636, 22, 41, 'همان کسانی که چون در زمین به آنان توانایی دهیم، نماز برپا می‌دارند و زکات می‌دهند و به کارهای پسندیده وامی‌دارند، و از کارهای ناپسند باز می‌دارند، و فرجام همه کارها از آنِ خداست.'),
(2637, 22, 42, 'و اگر تو را تکذیب کنند، قطعاً پیش از آنان قوم نوح و عاد و ثمود [نیز] به تکذیب پرداختند.'),
(2638, 22, 43, 'و [نیز] قوم ابراهیم و قوم لوط.'),
(2639, 22, 44, 'و [همچنین‌] اهل مدین. و موسی تکذیب شد، پس کافران را مهلت دادم، سپس [گریبان‌] آنها را گرفتم. بنگر، عذاب من چگونه بود؟'),
(2640, 22, 45, 'و چه بسیار شهرها را -که ستمکار بودند- هلاکشان کردیم و [اینک‌] آن [شهرها] سقفهایش فرو ریخته است، و [چه بسیار] چاههای متروک و کوشکهای افراشته را.'),
(2641, 22, 46, 'آیا در زمین گردش نکرده‌اند، تا دلهایی داشته باشند که با آن بیندیشند یا گوشهایی که با آن بشنوند؟ در حقیقت، چشمها کور نیست لیکن دلهایی که در سینه‌هاست کور است.'),
(2642, 22, 47, 'و از تو با شتاب تقاضای عذاب می‌کنند، با آنکه هرگز خدا وعده‌اش را خلاف نمی کند، و در حقیقت، یک روز [از قیامت‌] نزد پروردگارت مانند هزار سال است از آنچه می‌شمرید.'),
(2643, 22, 48, 'و چه بسا شهری که مهلتش دادم، در حالی که ستمکار بود؛ سپس [گریبان‌] آن را گرفتم، و فرجام به سوی من است.'),
(2644, 22, 49, 'بگو: «ای مردم، من برای شما فقط هشداردهنده‌ای آشکارم.»'),
(2645, 22, 50, 'پس آنان که گرویده و کارهای شایسته کرده‌اند، آمرزش و روزیِ نیکو برای ایشان خواهد بود.'),
(2646, 22, 51, 'و کسانی که در [تخطئه‌] آیات ما می‌کوشند [و به خیال خود] عاجزکنندگان ما هستند، آنان اهل دوزخند.'),
(2647, 22, 52, 'و پیش از تو [نیز] هیچ رسول و پیامبری را نفرستادیم جز اینکه هر گاه چیزی تلاوت می‌نمود، شیطان در تلاوتش القایِ [شبهه‌] می‌کرد. پس خدا آنچه را شیطان القا می‌کرد محو می‌گردانید، سپس خدا آیات خود را استوار می‌ساخت، و خدا دانای حکیم است.'),
(2648, 22, 53, 'تا آنچه را که شیطان القا می‌کند، برای کسانی که در دلهایشان بیماری است و [نیز] برای سنگدلان آزمایشی گرداند، و ستمگران در ستیزه‌ای بس دور و درازند.'),
(2649, 22, 54, 'و تا آنان که دانش یافته‌اند بدانند که این [قرآن‌] حق است [و] از جانب پروردگار توست. و بدان ایمان آورند و دلهایشان برای او خاضع گردد. و به راستی خداوند کسانی را که ایمان آورده‌اند، به سوی راهی راست راهبر است.'),
(2650, 22, 55, 'و[لی‌] کسانی که کفر ورزیده‌اند، همواره از آن در تردیدند، تا بناگاه قیامت برای آنان فرا رسد، یا عذاب روزی بدفرجام به سراغشان بیاید.'),
(2651, 22, 56, 'در آن روز، پادشاهی از آنِ خداست: میان آنان داوری می‌کند، و [در نتیجه‌] کسانی که ایمان آورده و کارهای شایسته کرده‌اند در باغهای پرناز و نعمت خواهند بود.'),
(2652, 22, 57, 'و کسانی که کفر ورزیده و نشانه‌های ما را دروغ پنداشته‌اند، برای آنان عذابی خفّت‌آور خواهد بود.'),
(2653, 22, 58, 'و آنان که در راه خدا مهاجرت کرده‌اند، و آنگاه کشته شده یا مرده‌اند، قطعاً خداوند به آنان رزقی نیکو می‌بخشد. و راستی این خداست که بهترین روزی‌دهندگان است.'),
(2654, 22, 59, 'آنان را به جایگاهی که آن را می‌پسندند درخواهد آورد، و شک نیست که خداوند دانایی بردبار است.'),
(2655, 22, 60, 'آری چنین است، و هر کس نظیر آنچه بر او عقوبت رفته است دست به عقوبت زند، سپس مورد ستم قرار گیرد، قطعاً خدا او را یاری خواهد کرد، چرا که خدا بخشایشگر و آمرزنده است.'),
(2656, 22, 61, 'این بدان سبب است که خدا شب را در روز درمی‌آورد و روز را [نیز] در شب درمی‌آورد، و خداست که شنوای بیناست.'),
(2657, 22, 62, '[آری،] این بدان سبب است که خدا خود حق است و آنچه به جای او می‌خوانند آن باطل است، و این خداست که والا و بزرگ است.'),
(2658, 22, 63, 'آیا ندیده‌ای که خدا از آسمان، آبی فرو فرستاد و زمین سرسبز گردید؟ آری، خداست که دقیق و آگاه است.'),
(2659, 22, 64, 'آنچه در آسمانها و آنچه در زمین است از آنِ اوست، و در حقیقت، این خداست که خود بی‌نیاز ستوده‌[صفات‌] است.'),
(2660, 22, 65, 'آیا ندیده‌ای که خدا آنچه را در زمین است به نفع شما رام گردانید، و کشتیها در دریا به فرمان او روانند، و آسمان را نگاه می‌دارد تا [مبادا] بر زمین فرو افتد، مگر به اذن خودش [باشد]. در حقیقت، خداوند نسبت به مردم سخت رئوف و مهربان است.'),
(2661, 22, 66, 'و اوست که شما را زندگی بخشید، سپس شما را می‌میراند، و باز زندگی [نو] می دهد. حقا که انسان سخت ناسپاس است.'),
(2662, 22, 67, 'برای هر امتی مناسکی قرار دادیم که آنها بدان عمل می‌کنند، پس نباید در این امر با تو به ستیزه برخیزند، به راه پروردگارت دعوت کن، زیرا تو بر راهی راست قرار داری.'),
(2663, 22, 68, 'و اگر با تو مجادله کردند، بگو: «خدا به آنچه می‌کنید داناتر است.»'),
(2664, 22, 69, 'خدا روز قیامت در مورد آنچه با یکدیگر در آن اختلاف می‌کردید، داوری خواهد کرد.'),
(2665, 22, 70, 'آیا ندانسته‌ای که خداوند آنچه را در آسمان و زمین است می‌داند؟ اینها [همه‌] در کتابی [مندرج‌] است. قطعاً این بر خدا آسان است.'),
(2666, 22, 71, 'و به جای خدا چیزی را می‌پرستند که بر [تأیید] آن حجّتی نازل نکرده و بدان دانشی ندارند، و برای ستمکاران یاوری نخواهد بود.'),
(2667, 22, 72, 'و چون آیات روشن ما بر آنان خوانده می‌شود، در چهره کسانی که کفر ورزیده‌اند [اثر] انکار را تشخیص می‌دهی: چیزی نمانده که بر کسانی که آیات ما را برایشان تلاوت می‌کنند حمله‌ور شوند. بگو: «آیا شما را به بدتر از این خبر دهم؟ [همان ]آتش است که خدا آن را به کسانی که کفر ورزیده‌اند وعده داده، و چه بد سرانجامی است.»'),
(2668, 22, 73, 'ای مردم، مَثَلی زده شد. پس بدان گوش فرا دهید: کسانی را که جز خدا می‌خوانید هرگز [حتی‌] مگسی نمی‌آفرینند، هر چند برای [آفریدن‌] آن اجتماع کنند، و اگر آن مگس چیزی از آنان برباید نمی‌توانند آن را بازپس گیرند. طالب و مطلوب هر دو ناتوانند.'),
(2669, 22, 74, 'قدر خدا را چنانکه در خور اوست نشناختند. در حقیقت، خداست که نیرومند شکست‌ناپذیر است.'),
(2670, 22, 75, 'خدا از میان فرشتگان رسولانی برمی‌گزیند، و نیز از میان مردم. بی‌گمان خدا شنوای بیناست.'),
(2671, 22, 76, 'آنچه در دسترس آنان و آنچه پشت سرشان است می‌داند و [همه‌] کارها به خدا بازگردانیده می‌شود.'),
(2672, 22, 77, 'ای کسانی که ایمان آورده‌اید، رکوع و سجود کنید و پروردگارتان را بپرستید و کار خوب انجام دهید، باشد که رستگار شوید.'),
(2673, 22, 78, 'و در راه خدا چنانکه حق جهاد [در راه‌] اوست جهاد کنید، اوست که شما را [برای خود] برگزیده و در دین بر شما سختی قرار نداده است. آیین پدرتان ابراهیم [نیز چنین بوده است‌] او بود که قبلاً شما را مسلمان نامید، و در این [قرآن نیز همین مطلب آمده است‌] تا این پیامبر بر شما گواه باشد و شما بر مردم گواه باشید. پس نماز را برپا دارید و زکات بدهید و به پناه خدا روید. او مولای شماست؛ چه نیکو مولایی و چه نیکو یاوری.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'به راستی که مؤمنان رستگار شدند،'),
(2675, 23, 2, 'همانان که در نمازشان فروتنند،'),
(2676, 23, 3, 'و آنان که از بیهوده رویگردانند،'),
(2677, 23, 4, 'و آنان که زکات می‌پردازند،'),
(2678, 23, 5, 'و کسانی که پاکدامنند،'),
(2679, 23, 6, 'مگر در مورد همسرانشان یا کنیزانی که به دست آورده‌اند، که در این صورت بر آنان نکوهشی نیست.'),
(2680, 23, 7, 'پس هر که فراتر از این جوید، آنان از حد درگذرندگانند.'),
(2681, 23, 8, 'و آنان که امانتها و پیمان خود را رعایت می‌کنند،'),
(2682, 23, 9, 'و آنان که بر نمازهایشان مواظبت می‌نمایند،'),
(2683, 23, 10, 'آنانند که خود وارثانند،'),
(2684, 23, 11, 'همانان که بهشت را به ارث می‌برند و در آنجا جاودان می‌مانند.'),
(2685, 23, 12, 'و به یقین، انسان را از عصاره‌ای از گِل آفریدیم.'),
(2686, 23, 13, 'سپس او را [به صورت‌] نطفه‌ای در جایگاهی استوار قرار دادیم.'),
(2687, 23, 14, 'آنگاه نطفه را به صورت علقه درآوردیم. پس آن علقه را [به صورت‌] مضغه گردانیدیم، و آنگاه مضغه را استخوانهایی ساختیم، بعد استخوانها را با گوشتی پوشانیدیم، آنگاه [جنین را در] آفرینشی دیگر پدید آوردیم. آفرین باد بر خدا که بهترین آفرینندگان است.'),
(2688, 23, 15, 'بعد از این [مراحل‌] قطعاً خواهید مُرد.'),
(2689, 23, 16, 'آنگاه شما در روز رستاخیز برانگیخته خواهید شد.'),
(2690, 23, 17, 'و به راستی [ما] بالای سر شما هفت راه [آسمانی‌] آفریدیم و از [کار] آفرینش غافل نبوده‌ایم.'),
(2691, 23, 18, 'و از آسمان، آبی به اندازه [معیّن‌] فرود آوردیم، و آن را در زمین جای دادیم، و ما برای از بین بردن آن مسلّماً تواناییم.'),
(2692, 23, 19, 'پس برای شما به وسیله آن باغهایی از درختان خرما و انگور پدیدار کردیم که در آنها برای شما میوه‌های فراوان است و از آنها می‌خورید.'),
(2693, 23, 20, 'و از طور سینا درختی برمی‌آید که روغن و نان خورشی برای خورندگان است.'),
(2694, 23, 21, 'و البته برای شما در دامها[ی گلّه درس‌] عبرتی است: از [شیری‌] که در شکم آنهاست، به شما می‌نوشانیم، و برای شما در آنها سودهای فراوان است و از آنها می‌خورید.'),
(2695, 23, 22, 'و بر آنها و بر کشتیها سوار می‌شوید.'),
(2696, 23, 23, 'و به یقین نوح را به سوی قومش فرستادیم. پس [به آنان‌] گفت: «ای قوم من، خدا را بپرستید. شما را جز او خدایی نیست. مگر پروا ندارید؟»'),
(2697, 23, 24, 'و اشراف قومش که کافر بودند گفتند: «این [مرد] جز بشری چون شما نیست، می‌خواهد بر شما برتری جوید، و اگر خدا می‌خواست قطعاً فرشتگانی می‌فرستاد. [ما]در میان پدران نخستین خود، چنین [چیزی‌] نشنیده‌ایم.»'),
(2698, 23, 25, 'او نیست جز مردی که در وی [حال‌] جنون است، پس تا چندی در باره‌اش دست نگاه دارید.'),
(2699, 23, 26, '[نوح‌] گفت: «پروردگارا، از آن روی که دروغزنم خواندند مرا یاری کن.»'),
(2700, 23, 27, 'پس به او وحی کردیم که: زیر نظر ما و [به‌] وحی ما کشتی را بساز، و چون فرمان ما دررسید و تنور به فوران آمد، پس در آن از هر نوع [حیوانی‌] دو تا [یکی نر و دیگری ماده‌] با خانواده‌ات -بجز کسی از آنان که حکم [عذاب ] بر او پیشی گرفته است- وارد کن، در باره کسانی که ظلم کرده‌اند با من سخن مگوی، زیرا آنها غرق خواهند شد.'),
(2701, 23, 28, 'و چون تو با آنان که همراه تواند بر کشتی نشستی بگو: «ستایش خدایی را که ما را از [چنگ‌] گروه ظالمان رهانید.»'),
(2702, 23, 29, 'و بگو: «پروردگارا، مرا در جایی پربرکت فرود آور [که‌] تو نیکترین مهمان‌نوازانی.»'),
(2703, 23, 30, 'در حقیقت، در این [ماجرا] عبرتهایی است، و قطعاً ما آزمایش‌کننده بودیم.'),
(2704, 23, 31, 'سپس، بعد از آنان نسل‌[هایی‌] دیگر پدید آوردیم.'),
(2705, 23, 32, 'و در میانشان پیامبری از خودشان روانه کردیم که: خدا را بپرستید. جز او برای شما معبودی نیست. آیا سرِ پرهیزگاری ندارید؟'),
(2706, 23, 33, 'و اشراف قومش که کافر شده، و دیدار آخرت را دروغ پنداشته بودند، و در زندگی دنیا آنان را مرفّه ساخته بودیم گفتند: «این [مرد] جز بشری چون شما نیست: از آنچه می‌خورید، می‌خورد؛ و از آنچه می‌نوشید، می‌نوشد.'),
(2707, 23, 34, 'و اگر بشری مثل خودتان را اطاعت کنید در آن صورت قطعاً زیانکار خواهید بود.'),
(2708, 23, 35, 'آیا به شما وعده می‌دهد که وقتی مُردید و خاک و استخوان شدید [باز] شما [از گور زنده‌] بیرون آورده می‌شوید؟'),
(2709, 23, 36, 'وه، چه دور است آنچه که وعده داده می‌شوید.'),
(2710, 23, 37, 'جز این زندگانی دنیای ما چیزی نیست. می‌میریم و زندگی می‌کنیم و دیگر برانگیخته نخواهیم شد.'),
(2711, 23, 38, 'او جز مردی که بر خدا دروغ می‌بندد نیست، و ما به او اعتقاد نداریم.»'),
(2712, 23, 39, 'گفت: «پروردگارا، از آن روی که مرا دروغزن خواندند یاریم کن.»'),
(2713, 23, 40, 'فرمود: «به زودی سخت پشیمان خواهند شد.»'),
(2714, 23, 41, 'پس فریاد [مرگبار] آنان را به حقّ فرو گرفت، و آنها را [چون‌] خاشاکی که بر آب افتد، گردانیدیم. دور باد [از رحمت خدا] گروه ستمکاران.'),
(2715, 23, 42, 'آنگاه پس از آنان نسلهای دیگری پدید آوردیم.'),
(2716, 23, 43, 'هیچ امّتی نه از اجل خود پیشی می‌گیرد و نه باز پس می‌ماند.'),
(2717, 23, 44, 'باز فرستادگان خود را پیاپی روانه کردیم. هر بار برای [هدایت‌] امتی پیامبرش آمد، او را تکذیب کردند؛ پس [ما امتهای سرکش را] یکی پس از دیگری آوردیم و آنها را مایه عبرت [و زبانزد مردم‌] گردانیدیم. دور باد [از رحمت خدا] مردمی که ایمان نمی‌آورند.'),
(2718, 23, 45, 'سپس موسی و برادرش هارون را با آیات خود و حجتی آشکار فرستادیم،'),
(2719, 23, 46, 'به سوی فرعون و سران [قوم‌] او، ولی تکبّر نمودند و مردمی گردنکش بودند،'),
(2720, 23, 47, 'پس گفتند: «آیا به دو بشر که مثل خود ما هستند و طایفه آنها بندگان ما می‌باشند ایمان بیاوریم؟»'),
(2721, 23, 48, 'در نتیجه، آن دو را دروغزن خواندند، پس از زمره هلاک‌شدگان گشتند.'),
(2722, 23, 49, 'و به یقین، ما به موسی کتاب [آسمانی‌] دادیم، باشد که آنان به راه راست روند.'),
(2723, 23, 50, 'و پسر مریم و مادرش را نشانه‌ای گردانیدیم و آن دو را در سرزمین بلندی که جای زیست و [دارای‌] آب زلال بود جای دادیم.'),
(2724, 23, 51, 'ای پیامبران، از چیزهای پاکیزه بخورید و کار شایسته کنید، که من به آنچه انجام می‌دهید دانایم.'),
(2725, 23, 52, 'و در حقیقت، این امّت شماست که امتی یگانه است، و من پروردگار شمایم؛ پس، از من پروا دارید.'),
(2726, 23, 53, 'تا کار [دین‌]شان را میان خود قطعه قطعه کردند [و] دسته دسته شدند: هر دسته‌ای به آنچه نزدشان بود، دل خوش کردند.'),
(2727, 23, 54, 'پس آنها را در ورطه گمراهی‌شان تا چندی واگذار.'),
(2728, 23, 55, 'آیا می‌پندارند که آنچه از مال و پسران که بدیشان مدد می‌دهیم،'),
(2729, 23, 56, '[از آن روی است که‌] می‌خواهیم به سودشان در خیرات شتاب ورزیم؟ [نه،] بلکه نمی‌فهمند.'),
(2730, 23, 57, 'در حقیقت، کسانی که از بیم پروردگارشان هراسانند،'),
(2731, 23, 58, 'و کسانی که به نشانه‌های پروردگارشان ایمان می‌آورند،'),
(2732, 23, 59, 'و آنان که به پروردگارشان شرک نمی‌آورند،'),
(2733, 23, 60, 'و کسانی که آنچه را دادند [در راه خدا] می‌دهند، در حالی که دلهایشان ترسان است [و می‌دانند] که به سوی پروردگارشان بازخواهند گشت،'),
(2734, 23, 61, 'آنانند که در کارهای نیک شتاب می‌ورزند و آنانند که در انجام آنها سبقت می‌جویند.'),
(2735, 23, 62, 'و هیچ کس را جز به قدر توانش تکلیف نمی‌کنیم، و نزد ما کتابی است که به حق سخن می‌گوید و آنان مورد ستم قرار نخواهند گرفت.'),
(2736, 23, 63, '[نه،] بلکه دلهای آنان از این [حقیقت‌] در غفلت است، و آنان غیر از این [گناهان‌] کردارهایی [دیگر] دارند که به انجام آن مبادرت می‌ورزند.'),
(2737, 23, 64, 'تا وقتی خوشگذرانانِ آنها را به عذاب گرفتار ساختیم، بناگاه به زاری درمی‌آیند.'),
(2738, 23, 65, 'امروز زاری مکنید که قطعاً شما از جانب ما یاری نخواهید شد.'),
(2739, 23, 66, 'در حقیقت، آیات من بر شما خوانده می‌شد و شما بودید که همواره به قهقرا می‌رفتید.'),
(2740, 23, 67, 'در حالی که از [پذیرفتن‌] آن تکبّر می‌ورزیدید و شب هنگام [در محافل خود] بدگویی می‌کردید.'),
(2741, 23, 68, 'آیا در [عظمت‌] این سخن نیندیشیده‌اند، یا چیزی برای آنان آمده که برای پدران پیشین آنها نیامده است؟'),
(2742, 23, 69, 'یا پیامبر خود را [درست‌] نشناخته و [لذا] به انکار او پرداخته‌اند؟'),
(2743, 23, 70, 'یا می‌گویند او جنونی دارد؟ [نه،] بلکه [او] حق را برای ایشان آورده و[لی‌] بیشترشان حقیقت را خوش ندارند.'),
(2744, 23, 71, 'و اگر حق از هوسهای آنها پیروی می‌کرد، قطعاً آسمانها و زمین و هر که در آنهاست تباه می‌شد. [نه!] بلکه یادنامه‌شان را به آنان داده‌ایم، ولی آنها از [پیروی‌] یادنامه خود رویگردانند.'),
(2745, 23, 72, 'یا از ایشان مزدی مطالبه می‌کنی؟ و مزد پروردگارت بهتر است، و اوست که بهترین روزی‌دهندگان است.'),
(2746, 23, 73, 'و در حقیقت، این تویی که جدّاً آنها را به راه راست می‌خوانی.'),
(2747, 23, 74, 'و به راستی کسانی که به آخرت ایمان ندارند، از راه [درست‌] سخت منحرفند.'),
(2748, 23, 75, 'و اگر ایشان را ببخشاییم، و آنچه از صدمه بر آنان [وارد آمده‌] است برطرف کنیم، در طغیان خود کوردلانه اصرار می‌ورزند.'),
(2749, 23, 76, 'و به راستی ایشان را به عذاب گرفتار کردیم و[لی‌] نسبت به پروردگارشان فروتنی نکردند و به زاری درنیامدند.'),
(2750, 23, 77, 'تا وقتی که دری از عذاب دردناک بر آنان گشودیم، بناگاه ایشان در آن [حال‌] نومید شدند.'),
(2751, 23, 78, 'و اوست آن کس که برای شما گوش و چشم و دل پدید آورد. چه اندک سپاسگزارید.'),
(2752, 23, 79, 'و اوست آن کس که شما را در زمین پدید آورد، و به سوی اوست که گرد آورده خواهید شد.'),
(2753, 23, 80, 'و اوست آن کس که زنده می‌کند و می‌میراند، و اختلاف شب و روز از اوست. مگر نمی‌اندیشید؟'),
(2754, 23, 81, '[نه،] بلکه آنان [نیز] مثل آنچه پیشینیان گفته بودند، گفتند.'),
(2755, 23, 82, 'گفتند: «آیا چون بمیریم و خاک و استخوان شویم، آیا واقعاً باز ما زنده خواهیم شد؟'),
(2756, 23, 83, 'درست همین را قبلاً به ما و پدرانمان وعده دادند. این جز افسانه‌های پیشینیان [چیزی‌] نیست.»'),
(2757, 23, 84, 'بگو: «اگر می‌دانید [بگویید] زمین و هر که در آن است به چه کسی تعلق دارد؟»'),
(2758, 23, 85, 'خواهند گفت: «به خدا.» بگو: «آیا عبرت نمی‌گیرید؟»'),
(2759, 23, 86, 'بگو: «پروردگار آسمانهای هفتگانه و پروردگار عرش بزرگ کیست؟»'),
(2760, 23, 87, 'خواهند گفت: «خدا.» بگو: «آیا پرهیزگاری نمی‌کنید؟»'),
(2761, 23, 88, 'بگو: «فرمانروایی هر چیزی به دست کیست؟ و اگر می‌دانید [کیست آنکه‌] او پناه می‌دهد و در پناه کسی نمی‌رود؟»'),
(2762, 23, 89, 'خواهند گفت: «خدا.» بگو: «پس چگونه دستخوش افسون شده‌اید؟»'),
(2763, 23, 90, '[نه!] بلکه حقیقت را بر ایشان آوردیم، و قطعاً آنان دروغگویند.'),
(2764, 23, 91, 'خدا فرزندی اختیار نکرده و با او معبودی [دیگر] نیست، و اگر جز این بود، قطعاً هر خدایی آنچه را آفریده [بود] باخود می‌برد، و حتماً بعضی از آنان بر بعضی دیگر تفوّق می‌جستند. منزه است خدا از آنچه وصف می‌کنند.'),
(2765, 23, 92, 'دانای نهان و آشکار، و برتر است از آنچه [با او] شریک می‌گردانند.'),
(2766, 23, 93, 'بگو: «پروردگارا، اگر آنچه را که [از عذاب‌] به آنان وعده داده شده است به من نشان دهی،'),
(2767, 23, 94, 'پروردگارا، پس مرا در میان قوم ستمکار قرار مده.»'),
(2768, 23, 95, 'و به راستی که ما تواناییم که آنچه را به آنان وعده داده‌ایم بر تو بنمایانیم.'),
(2769, 23, 96, 'بدی را به شیوه‌ای نیکو دفع کن. ما به آنچه وصف می‌کنند داناتریم.'),
(2770, 23, 97, 'و بگو: «پروردگارا، از وسوسه‌های شیطانها به تو پناه می‌برم.'),
(2771, 23, 98, 'و پروردگارا، از اینکه [آنها] به پیش من حاضر شوند به تو پناه می‌برم.»'),
(2772, 23, 99, 'تا آنگاه که مرگ یکی از ایشان فرا رسد، می‌گوید: «پروردگارا، مرا بازگردانید،'),
(2773, 23, 100, 'شاید من در آنچه وانهاده‌ام کار نیکی انجام دهم. نه چنین است، این سخنی است که او گوینده آن است و پشاپیش آنان برزخی است تا روزی که برانگیخته خواهند شد.'),
(2774, 23, 101, 'پس آنگاه که در صور دمیده شود، [دیگر] میانشان نسبت خویشاوندی وجود ندارد، و از [حال‌] یکدیگر نمی‌پرسند.'),
(2775, 23, 102, 'پس کسانی که کفه میزان [اعمال‌] آنان سنگین باشد، ایشان رستگارانند.'),
(2776, 23, 103, 'و کسانی که کفه میزان [اعمال‌]شان سبک باشد، آنان به خویشتن زیان زده [و] همیشه در جهنم می‌مانند.'),
(2777, 23, 104, 'آتش چهره آنها را می‌سوزاند، و آنان در آنجا ترش‌رویند.'),
(2778, 23, 105, 'آیا آیات من بر شما خوانده نمی‌شد و [همواره‌] آن را مورد تکذیب قرار نمی‌دادید؟'),
(2779, 23, 106, 'می‌گویند: «پروردگارا، شقاوت ما بر ما چیره شد و ما مردمی گمراه بودیم.»'),
(2780, 23, 107, 'پروردگارا، ما را از اینجا بیرون بر، پس اگر باز هم [به بدی‌] برگشتیم، در آن صورت ستمگر خواهیم بود.'),
(2781, 23, 108, 'می‌فرماید: «[بروید] در آن گم شوید و با من سخن مگویید.»'),
(2782, 23, 109, 'در حقیقت، دسته‌ای از بندگان من بودند که می‌گفتند: «پروردگارا، ایمان آوردیم. بر ما ببخشای و به ما رحم کن [که‌] تو بهترینِ مهربانی.»'),
(2783, 23, 110, 'و شما آنان [=مؤمنان‌] را به ریشخند گرفتید، تا [با این کار] یاد مرا از خاطرتان بردند و شما بر آنان می‌خندیدید.'),
(2784, 23, 111, 'من [هم‌] امروز به [پاس‌] آنکه صبر کردند، بدانان پاداش دادم. آری، ایشانند که رستگارانند.'),
(2785, 23, 112, 'می‌فرماید: «چه مدت به عدد سالها در زمین ماندید؟»'),
(2786, 23, 113, 'می‌گویند: «یک روز یا پاره‌ای از یک روز ماندیم. از شمارگران [خود] بپرس.»'),
(2787, 23, 114, 'می‌فرماید: «جز اندکی درنگ نکردید، کاش شما می‌دانستید.»'),
(2788, 23, 115, 'آیا پنداشتید که شما را بیهوده آفریده‌ایم و اینکه شما به سوی ما بازگردانیده نمی‌شوید؟'),
(2789, 23, 116, 'پس والاست خدا، فرمانروای برحق، خدایی جز او نیست. [اوست‌] پروردگار عرش گرانمایه.'),
(2790, 23, 117, 'و هر کس با خدا معبود دیگری بخواند، برای آن برهانی نخواهد داشت، و حسابش فقط با پروردگارش می‌باشد، در حقیقت، کافران رستگار نمی‌شوند.'),
(2791, 23, 118, 'و بگو: «پروردگارا، ببخشای و رحمت کن [که‌] تو بهترین بخشایندگانی.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '[این‌] سوره‌ای است که آن را نازل و آن را فرض گردانیدیم و در آن آیاتی روشن فرو فرستادیم، باشد که شما پند پذیرید.'),
(2793, 24, 2, 'به هر زن زناکار و مرد زناکاری صد تازیانه بزنید، و اگر به خدا و روز بازپسین ایمان دارید، در [کار] دین خدا، نسبت به آن دو دلسوزی نکنید، و باید گروهی از مؤمنان در کیفر آن دو حضور یابند.'),
(2794, 24, 3, 'مرد زناکار، جز زن زناکار یا مشرک را به همسری نگیرد، و زن زناکار، جز مرد زناکار یا مشرک را به زنی نگیرد، و بر مؤمنان این [امر] حرام گردیده است.'),
(2795, 24, 4, 'و کسانی که نسبت زنا به زنان شوهردار می‌دهند، سپس چهار گواه نمی‌آورند، هشتاد تازیانه به آنان بزنید، و هیچگاه شهادتی از آنها نپذیرید، و اینانند که خود فاسقند.'),
(2796, 24, 5, 'مگر کسانی که بعد از آن [بهتان‌] توبه کرده و به صلاح آمده باشند که خدا البته آمرزنده مهربان است.'),
(2797, 24, 6, 'و کسانی که به همسران خود نسبت زنا می‌دهند، و جز خودشان گواهانی [دیگر] ندارند، هر یک از آنان [باید] چهار بار به خدا سوگند یاد کند که او قطعاً از راستگویان است.'),
(2798, 24, 7, 'و [گواهی در دفعه‌] پنجم این است که [شوهر بگوید:] لعنت خدا بر او باد اگر از دروغگویان باشد.'),
(2799, 24, 8, 'و از [زن،] کیفر ساقط می‌شود، در صورتی که چهار بار به خدا سوگند یاد کند که [شوهر] او جداً از دروغگویان است.'),
(2800, 24, 9, 'و [گواهی‌] پنجم آنکه خشم خدا بر او باد اگر [شوهرش‌] از راستگویان باشد.'),
(2801, 24, 10, 'و اگر فضل و رحمت خدا بر شما نبود و اینکه خدا توبه‌پذیر سنجیده‌کار است [رسوا می‌شدید].'),
(2802, 24, 11, 'در حقیقت، کسانی که آن بهتان [داستان اِفک‌] را [در میان‌] آوردند، دسته‌ای از شما بودند. آن [تهمت‌] را شرّی برای خود تصوّر مکنید بلکه برای شما در آن مصلحتی [بوده‌] است. برای هر مردی از آنان [که در این کار دست داشته‌] همان گناهی است که مرتکب شده است، و آن کس از ایشان که قسمت عمده آن را به گردن گرفته است عذابی سخت خواهد داشت.'),
(2803, 24, 12, 'چرا هنگامی که آن [بهتان‌] را شنیدید، مردان و زنان مؤمن گمان نیک به خود نبردند و نگفتند: «این بهتانی آشکار است»؟'),
(2804, 24, 13, 'چرا چهار گواه بر [صحت‌] آن [بهتان‌] نیاوردند؟ پس چون گواهان [لازم‌] را نیاورده‌اند، اینانند که نزد خدا دروغگویانند.'),
(2805, 24, 14, 'و اگر فضل خدا و رحمتش در دنیا و آخرت بر شما نبود، قطعاً به [سزای‌] آنچه در آن به دخالت پرداختید، به شما عذابی بزرگ می‌رسید.'),
(2806, 24, 15, 'آنگاه که آن [بهتان‌] را از زبان یکدیگر می‌گرفتید و با زبانهای خود چیزی را که بدان علم نداشتید، می‌گفتید و می‌پنداشتید که کاری سهل و ساده است با اینکه آن [امر] نزد خدا بس بزرگ بود.'),
(2807, 24, 16, 'و [گر نه‌] چرا وقتی آن را شنیدید نگفتید: «برای ما سزاوار نیست که در این [موضوع‌] سخن گوییم. [خداوندا،] تو منزهی، این بهتانی بزرگ است.»'),
(2808, 24, 17, 'خدا اندرزتان می‌دهد که هیچ گاه دیگر مثل آن را -اگر مؤمنید- تکرار نکنید.'),
(2809, 24, 18, 'و خدا برای شما آیات [خود] را بیان می‌کند، و خدا دانای سنجیده‌کار است.'),
(2810, 24, 19, 'کسانی که دوست دارند که زشتکاری در میان آنان که ایمان آورده‌اند، شیوع پیدا کند، برای آنان در دنیا و آخرت عذابی پر درد خواهد بود، و خدا[ست که‌] می‌داند و شما نمی‌دانید.'),
(2811, 24, 20, 'و اگر فضل و رحمت خدا بر شما نبود و اینکه خدا رئوف و مهربان است [مجازات سختی در انتظارتان بود].'),
(2812, 24, 21, 'ای کسانی که ایمان آورده‌اید، پای از پی گامهای شیطان منهید، و هر کس پای بر جای گامهای شیطان نهد [بداند که‌] او به زشتکاری و ناپسند وامی‌دارد، و اگر فضل خدا و رحمتش بر شما نبود، هرگز هیچ کس از شما پاک نمی‌شد، ولی [این‌] خداست که هر کس را بخواهد پاک می‌گرداند و خدا[ست که‌] شنوای داناست.'),
(2813, 24, 22, 'و سرمایه‌داران و فراخ‌دولتان شما نباید از دادن [مال‌] به خویشاوندان و تهیدستان و مهاجران راه خدا دریغ ورزند، و باید عفو کنند و گذشت نمایند. مگر دوست ندارید که خدا بر شما ببخشاید؟ و خدا آمرزنده مهربان است.'),
(2814, 24, 23, 'بی‌گمان، کسانی که به زنان پاکدامن بی‌خبر [از همه جا] و با ایمان نسبت زنا می‌دهند، در دنیا و آخرت لعنت شده‌اند، و برای آنها عذابی سخت خواهد بود،'),
(2815, 24, 24, 'در روزی که زبان و دستها و پاهایشان، بر ضد آنان برای آنچه انجام می‌دادند، شهادت می‌دهند.'),
(2816, 24, 25, 'آن روز خدا جزای شایسته آنان را به طور کامل می‌دهد و خواهند دانست که خدا همان حقیقت آشکار است.'),
(2817, 24, 26, 'زنان پلید برای مردان پلیدند، و مردان پلید برای زنان پلید. و زنان پاک برای مردان پاکند، و مردان پاک برای زنان پاک. اینان از آنچه در باره ایشان می‌گویند بر کنارند، برای آنان آمرزش و روزیِ نیکو خواهد بود.'),
(2818, 24, 27, 'ای کسانی که ایمان آورده‌اید، به خانه‌هایی که خانه‌های شما نیست داخل مشوید تا اجازه بگیرید و بر اهل آن سلام گویید. این برای شما بهتر است، باشد که پند گیرید.'),
(2819, 24, 28, 'و اگر کسی را در آن نیافتید، پس داخلِ آن مشوید تا به شما اجازه داده شود؛ و اگر به شما گفته شد: «برگردید» برگردید، که آن برای شما سزاوارتر است، و خدا به آنچه انجام می‌دهید داناست.'),
(2820, 24, 29, 'بر شما گناهی نیست که به خانه‌های غیرمسکونی -که در آنها برای شما استفاده‌ای است- داخل شوید، و خدا آنچه را آشکار و آنچه را پنهان می‌دارید می‌داند.'),
(2821, 24, 30, 'به مردان با ایمان بگو: «دیده فرو نهند و پاکدامنی ورزند، که این برای آنان پاکیزه‌تر است، زیرا خدا به آنچه می‌کنند آگاه است.»'),
(2822, 24, 31, 'و به زنان با ایمان بگو: «دیدگان خود را [از هر نامحرمی‌] فرو بندند و پاکدامنی ورزند و زیورهای خود را آشکار نگردانند مگر آنچه که طبعاً از آن پیداست. و باید روسری خود را بر گردن خویش [فرو ]اندازند، و زیورهایشان را جز برای شوهرانشان یا پدرانشان یا پدران شوهرانشان یا پسرانشان یا پسران شوهرانشان یا برادرانشان یا پسران برادرانشان یا پسران خواهرانشان یا زنان [همکیش‌] خود یا کنیزانشان یا خدمتکاران مرد که [از زن‌] بی‌نیازند یا کودکانی که بر عورتهای زنان وقوف حاصل نکرده‌اند، آشکار نکنند؛ و پاهای خود را [به گونه‌ای به زمین‌] نکوبند تا آنچه از زینتشان نهفته می‌دارند معلوم گردد. ای مؤمنان، همگی [از مرد و زن‌] به درگاه خدا توبه کنید، امید که رستگار شوید.'),
(2823, 24, 32, 'بی‌همسران خود، و غلامان و کنیزان درستکارتان را همسر دهید. اگر تنگدستند، خداوند آنان را از فضل خویش بی‌نیاز خواهد کرد، و خدا گشایشگر داناست.'),
(2824, 24, 33, 'و کسانی که [وسیله‌] زناشویی نمی‌یابند، باید عفت ورزند تا خدا آنان را از فضل خویش بی‌نیاز گرداند. و از میان غلامانتان، کسانی که در صددند با قرارداد کتبی، خود را آزاد کنند، اگر در آنان خیری [و توانایی پرداخت مال‌] می‌یابید، قرارِ بازخریدِ آنها را بنویسید، و از آن مالی که خدا به شما داده است به ایشان بدهید [تا تدریجاً خود را آزاد کنند]، و کنیزان خود را -در صورتی که تمایل به پاکدامنی دارند- برای اینکه متاع زندگی دنیا را بجویید، به زنا وادار مکنید، و هر کس آنان را به زور وادار کند، در حقیقت، خدا پس از اجبار نمودن ایشان، [نسبت به آنها] آمرزنده مهربان است.'),
(2825, 24, 34, 'و قطعاً به سوی شما آیاتی روشنگر و خبری از کسانی که پیش از شما روزگار به سر برده‌اند، و موعظه‌ای برای اهل تقوا فرود آورده‌ایم.'),
(2826, 24, 35, 'خدا نور آسمانها و زمین است. مَثَل نور او چون چراغدانی است که در آن چراغی، و آن چراغ در شیشه‌ای است. آن شیشه گویی اختری درخشان است که از درخت خجسته زیتونی که نه شرقی است و نه غربی، افروخته می‌شود. نزدیک است که روغنش -هر چند بدان آتشی نرسیده باشد- روشنی بخشد. روشنیِ بر روی روشنی است. خدا هر که را بخواهد با نور خویش هدایت می‌کند، و این مَثَلها را خدا برای مردم می‌زند و خدا به هر چیزی داناست.'),
(2827, 24, 36, 'در خانه‌هایی که خدا رخصت داده که [قدر و منزلت‌] آنها رفعت یابد و نامش در آنها یاد شود. در آن [خانه‌]ها هر بامداد و شامگاه او را نیایش می‌کنند:'),
(2828, 24, 37, 'مردانی که نه تجارت و نه داد و ستدی، آنان را از یاد خدا و برپا داشتن نماز و دادن زکات، به خود مشغول نمی‌دارد، و از روزی که دلها و دیده‌ها در آن زیرورو می‌شود می‌هراسند.'),
(2829, 24, 38, 'تا خدا بهتر از آنچه انجام می‌دادند، به ایشان جزا دهد و از فضل خود بر آنان بیفزاید، و خدا[ست که‌] هر که را بخواهد بی‌حساب روزی می‌دهد.'),
(2830, 24, 39, 'و کسانی که کفر ورزیدند، کارهایشان چون سرابی در زمینی هموار است که تشنه، آن را آبی می‌پندارد، تا چون بدان رسد آن را چیزی نیابد و خدا را نزد خویش یابد و حسابش را تمام به او دهد و خدا زودشمار است.'),
(2831, 24, 40, 'یا [کارهایشان‌] مانند تاریکیهایی است که در دریایی ژرف است که موجی آن را می‌پوشاند [و] روی آن موجی [دیگر] است [و] بالای آن ابری است. تاریکیهایی است که بعضی بر روی بعضی قرار گرفته است. هر گاه [غرقه‌] دستش را بیرون آورد، به زحمت آن را می‌بیند، و خدا به هر کس نوری نداده باشد او را هیچ نوری نخواهد بود.'),
(2832, 24, 41, 'آیا ندانسته‌ای که هر که [و هر چه‌] در آسمانها و زمین است برای خدا تسبیح می‌گویند، و پرندگان [نیز] در حالی که در آسمان پر گشوده‌اند [تسبیح او می‌گویند]؟ همه ستایش و نیایش خود را می‌دانند، و خدا به آنچه می‌کنند داناست.'),
(2833, 24, 42, 'و فرمانروایی آسمانها و زمین از آنِ خداست، و بازگشت [همه‌] به سوی خداست.'),
(2834, 24, 43, 'آیا ندانسته‌ای که خدا[ست که‌] ابر را به آرامی می‌راند، سپس میان [اجزاء] آن پیوند می‌دهد، آنگاه آن را متراکم می‌سازد، پس دانه‌های باران را می‌بینی که از خلال آن بیرون می‌آید، و [خداست که‌] از آسمان از کوههایی [از ابر یخ‌زده ]که در آنجاست تگرگی فرو می‌ریزد؛ و هر که را بخواهد بدان گزند می‌رساند، و آن را از هر که بخواهد باز می‌دارد. نزدیک است روشنیِ برقش چشمها را بِبَرَد.'),
(2835, 24, 44, 'خداست که شب و روز را با هم جابجا می‌کند. قطعاً در این [تبدیل‌] برای دیده‌وران [درس‌] عبرتی است.'),
(2836, 24, 45, 'و خداست که هر جنبنده‌ای را [ابتدا] از آبی آفرید. پس پاره‌ای از آنها بر روی شکم راه می‌روند و پاره‌ای از آنها بر روی دو پا و بعضی از آنها بر روی چهار [پا] راه می‌روند. خدا هر چه بخواهد می‌آفریند. در حقیقت، خدا بر هر چیزی تواناست.'),
(2837, 24, 46, 'قطعاً آیاتی روشنگر فرود آورده‌ایم، و خدا هر که را بخواهد به راه راست هدایت می‌کند.'),
(2838, 24, 47, 'و می‌گویند: «به خدا و پیامبر [او] گرویدیم و اطاعت کردیم.» آنگاه دسته‌ای از ایشان پس از این [اقرار] روی برمی‌گردانند، و آنان مؤمن نیستند.'),
(2839, 24, 48, 'و چون به سوی خدا و پیامبر او خوانده شوند، تا میان آنان داوری کند، بناگاه دسته‌ای از آنها روی برمی‌تابند.'),
(2840, 24, 49, 'و اگر حق به جانب ایشان باشد، به حال اطاعت به سوی او می‌آیند.'),
(2841, 24, 50, 'آیا در دلهایشان بیماری است، یا شک دارند، یا از آن می‌ترسند که خدا و فرستاده‌اش بر آنان ستم ورزند؟ [نه،] بلکه خودشان ستمکارند.'),
(2842, 24, 51, 'گفتار مؤمنان -وقتی به سوی خدا و پیامبرش خوانده شوند تا میانشان داوری کند- تنها این است که می‌گویند: «شنیدیم و اطاعت کردیم.» اینانند که رستگارند.'),
(2843, 24, 52, 'و کسی که خدا و فرستاده او را فرمان برد، و از خدا بترسد و از او پروا کند؛ آنانند که خود کامیابند.'),
(2844, 24, 53, 'و با سوگندهای سخت خود به خدا سوگند یاد کردند که اگر به آنان فرمان دهی، بی شک [برای جهاد] بیرون خواهند آمد. بگو: «سوگند مخورید. اطاعتی پسندیده [بهتر است‌] که خدا به آنچه می‌کنید داناست.»'),
(2845, 24, 54, 'بگو: «خدا و پیامبر را اطاعت کنید. پس اگر پشت نمودید، [بدانید که‌] بر عهده اوست آنچه تکلیف شده و بر عهده شماست آنچه موظّف هستید. و اگر اطاعتش کنید راه خواهید یافت، و بر فرستاده [خدا] جز ابلاغ آشکار [مأموریتی‌] نیست.'),
(2846, 24, 55, 'خدا به کسانی از شما که ایمان آورده و کارهای شایسته کرده‌اند، وعده داده است که حتماً آنان را در این سرزمین جانشین [خود] قرار دهد؛ همان گونه که کسانی را که پیش از آنان بودند جانشین [خود] قرار داد، و آن دینی را که برایشان پسندیده است به سودشان مستقر کند، و بیمشان را به ایمنی مبدل گرداند، [تا] مرا عبادت کنند و چیزی را با من شریک نگردانند، و هر کس پس از آن به کفر گراید؛ آنانند که نافرمانند.'),
(2847, 24, 56, 'و نماز را برپا کنید و زکات را بدهید و پیامبر [خدا] را فرمان برید تا مورد رحمت قرار گیرید.'),
(2848, 24, 57, 'و مپندار کسانی که کفر ورزیدند [ما را] در زمین درمانده می‌کنند؛ جایگاهشان در آتش است و چه بد بازگشتگاهی است.'),
(2849, 24, 58, 'ای کسانی که ایمان آورده‌اید، قطعاً باید غلام و کنیزهای شما و کسانی از شما که به [سنِ‌] بلوغ نرسیده‌اند سه بار در شبانه روز از شما کسب اجازه کنند: پیش از نماز بامداد، و نیمروز که جامه‌های خود را بیرون می‌آورید، و پس از نماز شامگاهان. [این،] سه هنگام برهنگی شماست، نه بر شما و نه بر آنان گناهی نیست که غیر از این [سه هنگام‌] گرد یکدیگر بچرخید [و با هم معاشرت نمایید]. خداوند آیات [خود] را این گونه برای شما بیان می‌کند، و خدا دانای سنجیده‌کار است.'),
(2850, 24, 59, 'و چون کودکان شما به [سنِ‌] بلوغ رسیدند، باید از شما کسب اجازه کنند؛ همان گونه که آنان که پیش از ایشان بودند کسب اجازه کردند. خدا آیات خود را این گونه برای شما بیان می‌دارد، و خدا دانای سنجیده‌کار است.'),
(2851, 24, 60, 'و بر زنان از کار افتاده‌ای که [دیگر] امید زناشویی ندارند گناهی نیست که پوشش خود را کنار نهند [به شرطی که‌] زینتی را آشکار نکنند؛ و عفّت ورزیدن برای آنها بهتر است، و خدا شنوای داناست.'),
(2852, 24, 61, 'بر نابینا و لنگ و بیمار و بر شما ایرادی نیست که از خانه‌های خودتان بخورید، یا از خانه‌های پدرانتان یا خانه‌های مادرانتان یا خانه‌های برادرانتان یا خانه‌های خواهرانتان یا خانه‌های عموهایتان یا خانه‌های عمّه‌هایتان یا خانه‌های داییهایتان یا خانه‌های خاله‌هایتان یا آن [خانه‌هایی‌] که کلیدهایش را در اختیار دارید یا [خانه‌] دوستتان. [هم چنین‌] بر شما باکی نیست که با هم بخورید یا پراکنده. پس چون به خانه‌هایی [که گفته شد ]درآمدید، به یکدیگر سلام کنید؛ درودی که نزد خدا مبارک و خوش است. خداوند آیات [خود] را این گونه برای شما بیان می‌کند، امید که بیندیشید.'),
(2853, 24, 62, 'جز این نیست که مؤمنان کسانی‌اند که به خدا و پیامبرش گرویده‌اند، و هنگامی که با او بر سر کاری اجتماع کردند، تا از وی کسب اجازه نکنند نمی‌روند. در حقیقت، کسانی که از تو کسب اجازه می‌کنند؛ آنانند که به خدا و پیامبرش ایمان دارند. پس چون برای برخی از کارهایشان از تو اجازه خواستند، به هر کس از آنان که خواستی اجازه ده و برایشان آمرزش بخواه که خدا آمرزنده مهربان است.'),
(2854, 24, 63, 'خطاب کردن پیامبر را در میان خود، مانند خطاب کردن بعضی از خودتان به بعضی [دیگر] قرار مدهید. خدا می‌داند [چه‌] کسانی از شما دزدانه [از نزد او] می‌گریزند. پس کسانی که از فرمان او تمرّد می‌کنند بترسند که مبادا بلایی بدیشان رسد یا به عذابی دردناک گرفتار شوند.'),
(2855, 24, 64, 'هش‌دار که آنچه در آسمانها و زمین است از آنِ خداست. به یقین آنچه را که بر آنید می‌داند، و روزی که به سوی او بازگردانیده می‌شوند آنان را [از حقیقت‌] آنچه انجام داده‌اند خبر می‌دهد، و خدا به هر چیزی داناست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بزرگ [و خجسته‌] است کسی که بر بنده خود، فرقان [=کتاب جداسازنده حق از باطل‌] را نازل فرمود، تا برای جهانیان هشداردهنده‌ای باشد.'),
(2857, 25, 2, 'همان کس که فرمانروایی آسمانها و زمین از آنِ اوست، و فرزندی اختیار نکرده و برای او شریکی در فرمانروایی نبوده است، و هر چیزی را آفریده و بدان گونه که درخور آن بوده اندازه‌گیری کرده است.'),
(2858, 25, 3, 'و به جای او خدایانی برای خود گرفته‌اند که چیزی را خلق نمی‌کنند و خود خلق شده‌اند و برای خود نه زیانی را در اختیار دارند و نه سودی را، و نه مرگی را در اختیار دارند و نه حیاتی و نه رستاخیزی را.'),
(2859, 25, 4, 'و کسانی که کفر ورزیدند، گفتند: «این [کتاب‌] جز دروغی که آن را بربافته [چیزی‌] نیست، و گروهی دیگر او را بر آن یاری کرده‌اند.» و قطعاً [با چنین نسبتی‌] ظلم و بهتانی به پیش آوردند.'),
(2860, 25, 5, 'و گفتند: «افسانه‌های پیشینیان است که آنها را برای خود نوشته، و صبح و شام بر او املا می‌شود.»'),
(2861, 25, 6, 'بگو: «آن را کسی نازل ساخته است که راز نهانها را در آسمانها و زمین می‌داند، و هموست که همواره آمرزنده مهربان است.»'),
(2862, 25, 7, 'و گفتند: «این چه پیامبری است که غذا می‌خورد و در بازارها راه می‌رود؟ چرا فرشته‌ای به سوی او نازل نشده تا همراه وی هشداردهنده باشد؟'),
(2863, 25, 8, 'یا گنجی به طرف او افکنده نشده یا باغی ندارد که از [بار و بَر] آن بخورد؟» و ستمکاران گفتند: « جز مردی افسون‌شده را دنبال نمی‌کنید.»'),
(2864, 25, 9, 'بنگر چگونه برای تو مَثَلها زدند و گمراه شدند؛ در نتیجه نمی‌توانند راهی بیابند.'),
(2865, 25, 10, 'بزرگ [و خجسته‌] است کسی که اگر بخواهد بهتر از این را برای تو قرار می‌دهد: باغهایی که جویبارها از زیر [درختان‌] آن روان خواهد بود، و برای تو کاخها پدید می‌آورد.'),
(2866, 25, 11, '[نه!] بلکه [آنها] رستاخیز را دروغ خواندند، و برای هر کس که رستاخیز را دروغ خوانَد آتش سوزان آماده کرده‌ایم.'),
(2867, 25, 12, 'چون [دوزخ‌] از فاصله‌ای دور، آنان را ببیند، خشم و خروشی از آن می‌شنوند.'),
(2868, 25, 13, 'و چون آنان را در تنگنایی از آن به زنجیر کشیده بیندازند، آنجاست که مرگ [خود] را می‌خواهند.'),
(2869, 25, 14, '«امروز یک بار هلاک [خود] را مخواهید و بسیار هلاک [خود] را بخواهید.»'),
(2870, 25, 15, 'بگو: «آیا این [عقوبت‌] بهتر است یا بهشت جاویدان که به پرهیزگاران وعده داده شده است که پاداش و سرانجام آنان است؟»'),
(2871, 25, 16, 'جاودانه هر چه بخواهند در آنجا دارند. پروردگار تو مسؤول [تحقق‌] این وعده است.'),
(2872, 25, 17, 'و روزی که آنان را با آنچه به جای خدا می‌پرستند، محشور می‌کند، پس می‌فرماید: «آیا شما این بندگان مرا به بیراهه کشاندید یا خود گمراه شدند؟»'),
(2873, 25, 18, 'می‌گویند: «منزهی تو، ما را نسزد که جز تو دوستی برای خود بگیریم، ولی تو آنان و پدرانشان را برخوردار کردی تا [آنجا که‌] یاد [تو] را فراموش کردند و گروهی هلاک‌شده، بودند.»'),
(2874, 25, 19, 'قطعاً [خدایانتان‌] در آنچه می‌گفتید، شما را تکذیب کردند؛ در نتیجه نه می‌توانید [عذاب را از خود] دفع کنید و نه [خود را] یاری نمایید و هر کس از شما شرک ورزد عذابی سهمگین به او می‌چشانیم.'),
(2875, 25, 20, 'و پیش از تو پیامبران [خود] را نفرستادیم جز اینکه آنان [نیز] غذا می‌خوردند و در بازارها راه می‌رفتند، و برخی از شما را برای برخی دیگر [وسیله‌] آزمایش قرار دادیم. آیا شکیبایی می‌کنید؟ و پروردگار تو همواره بیناست.'),
(2876, 25, 21, 'و کسانی که به لقای ما امید ندارند، گفتند: «چرا فرشتگان بر ما نازل نشدند یا پروردگارمان را نمی‌بینیم؟» قطعاً در مورد خود تکبر ورزیدند و سخت سرکشی کردند.'),
(2877, 25, 22, 'روزی که فرشتگان را ببینند، آن روز برای گناهکاران بشارتی نیست، و می‌گویند: «دور و ممنوع [آید از رحمت خدا].»'),
(2878, 25, 23, 'و به هر گونه کاری که کرده‌اند می‌پردازیم و آن را [چون‌] گَردی پراکنده می‌سازیم.'),
(2879, 25, 24, 'آن روز، جایگاه اهل بهشت بهتر و استراحتگاهشان نیکوتر است.'),
(2880, 25, 25, 'و روزی که آسمان با ابری سپید از هم می‌شکافد و فرشتگان نزول یابند!'),
(2881, 25, 26, 'آن روز، فرمانروایی بحق، از آنِ [خدایِ‌] رحمان است و روزی است که بر کافران بسی دشوار است.'),
(2882, 25, 27, 'و روزی است که ستمکار دستهای خود را می‌گزد [و] می‌گوید: «ای کاش با پیامبر راهی برمی‌گرفتم.»'),
(2883, 25, 28, '«ای وای، کاش فلانی را دوست [خود] نگرفته بودم.'),
(2884, 25, 29, 'او [بود که‌] مرا به گمراهی کشانید پس از آنکه قرآن به من رسیده بود.» و شیطان همواره فروگذارنده انسان است.'),
(2885, 25, 30, 'و پیامبر [خدا] گفت: «پروردگارا، قوم من این قرآن را رها کردند.»'),
(2886, 25, 31, 'و این گونه برای هر پیامبری دشمنی از گناهکاران قرار دادیم، و همین بس که پروردگارت راهبر و یاور توست.'),
(2887, 25, 32, 'و کسانی که کافر شدند، گفتند: «چرا قرآن یکجا بر او نازل نشده است؟» این گونه [ما آن را به تدریج نازل کردیم‌] تا قلبت را به وسیله آن استوار گردانیم، و آن را به آرامی [بر تو] خواندیم.'),
(2888, 25, 33, 'و برای تو مَثَلی نیاوردند، مگر آنکه [ما] حق را با نیکوترین بیان برای تو آوردیم.'),
(2889, 25, 34, 'کسانی که -به رو درافتاده- به سوی جهنم رانده می‌شوند، آنان بدترین جای و گم‌ترین راه را دارند.'),
(2890, 25, 35, 'و به یقین [ما] به موسی کتاب [آسمانی‌] عطا کردیم، و برادرش هارون را همراه او دستیار[ش‌] گردانیدیم.'),
(2891, 25, 36, 'پس گفتیم: «هر دو به سوی قومی که نشانه‌های ما را به دروغ گرفتند بروید.» پس [ما] آنان را به سختی هلاک نمودیم.'),
(2892, 25, 37, 'و قوم نوح را آنگاه که پیامبران [خدا] را تکذیب کردند غرقشان ساختیم، و آنان را برای [همه‌] مردم عبرتی گردانیدیم و برای ستمکاران عذابی پر درد آماده کرده‌ایم.'),
(2893, 25, 38, 'و [نیز] عادیان و ثمودیان و اصحاب رَسّ و نسلهای بسیاری میان این [جماعتها] را [هلاک کردیم‌].'),
(2894, 25, 39, 'و برای همه آنان مَثَلها زدیم و همه را زیر و زَبَر کردیم.'),
(2895, 25, 40, 'و قطعاً بر شهری که باران بلا بر آن بارانده شد گذشته‌اند؛ مگر آن را ندیده‌اند؟ [چرا،] ولی امید به زنده‌شدن ندارند.'),
(2896, 25, 41, 'و چون تو را ببینند، جز به ریشخندت نگیرند، [که:] «آیا این همان کسی است که خدا او را به رسالت فرستاده است؟'),
(2897, 25, 42, 'چیزی نمانده بود که ما را از خدایانمان -اگر بر آن ایستادگی نمی‌کردیم- منحرف کند. «و هنگامی که عذاب را می‌بینند به زودی خواهند دانست چه کسی گمراه‌تر است.'),
(2898, 25, 43, 'آیا آن کس که هوای [نفس‌] خود را معبود خویش گرفته است دیدی؟ آیا [می‌توانی‌] ضامن او باشی؟'),
(2899, 25, 44, 'یا گمان داری که بیشترشان می‌شنوند یا می‌اندیشند؟ آنان جز مانند ستوران نیستند، بلکه گمراه‌ترند.'),
(2900, 25, 45, 'آیا ندیده‌ای که پروردگارت چگونه سایه را گسترده است؟ و اگر می‌خواست، آن را ساکن قرار می‌داد، آنگاه خورشید را بر آن دلیل گردانیدیم.'),
(2901, 25, 46, 'سپس آن [سایه‌] را اندک اندک به سوی خود بازمی‌گیریم.'),
(2902, 25, 47, 'و اوست کسی که شب را برای شما پوششی قرار داد و خواب را [مایه‌] آرامشی. و روز را زمان برخاستن [شما] گردانید.'),
(2903, 25, 48, 'و اوست آن کس که بادها را نویدی پیشاپیش رحمت خویش [=باران‌] فرستاد و از آسمان، آبی پاک فرود آوردیم،'),
(2904, 25, 49, 'تا به وسیله آن سرزمینی پژمرده را زنده گردانیم و آن را به آنچه خلق کرده‌ایم -از دامها و انسانهای بسیار- بنوشانیم.'),
(2905, 25, 50, 'و قطعاً آن [پند] را میان آنان گوناگون ساختیم تا توجه پیدا کنند، و[لی ] بیشتر مردم جز ناسپاسی نخواستند.'),
(2906, 25, 51, 'و اگر می‌خواستیم قطعاً در هر شهری هشداردهنده‌ای برمی‌انگیختیم.'),
(2907, 25, 52, 'پس، از کافران اطاعت مکن، و با [الهام گرفتن از] قرآن با آنان به جهادی بزرگ بپرداز.'),
(2908, 25, 53, 'و اوست کسی که دو دریا را موج‌زنان به سوی هم روان کرد: این یکی شیرین [و] گوارا و آن یکی شور [و] تلخ است؛ و میان آن دو، مانع و حریمی استوار قرار داد.'),
(2909, 25, 54, 'و اوست کسی که از آب، بشری آفرید و او را [دارای خویشاوندی‌] نسبی و دامادی قرار داد، و پروردگار تو همواره تواناست.'),
(2910, 25, 55, 'و غیر از خدا چیزی را می‌پرستند که نه سودشان می‌دهد و نه زیانشان می‌رساند؛ و کافر همواره در برابر پروردگار خود همپشت [شیطان‌] است.'),
(2911, 25, 56, 'و تو را جز بشارتگر و بیم‌دهنده نفرستادیم.'),
(2912, 25, 57, 'بگو: «بر این [رسالت‌] اجری از شما طلب نمی‌کنم، جز اینکه هر کس بخواهد راهی به سوی پروردگارش [در پیش‌] گیرد.»'),
(2913, 25, 58, 'و بر آن زنده که نمی‌میرد توکل کن و به ستایش او تسبیح گوی؛ و همین بس که او به گناهان بندگانش آگاه است.'),
(2914, 25, 59, 'همان کسی که آسمانها و زمین، و آنچه را که میان آن دو است، در شش روز آفرید. آنگاه بر عرش استیلا یافت. رحمتگر عام [اوست‌]. در باره وی از خبره‌ای بپرس [که می‌داند].'),
(2915, 25, 60, 'و چون به آنان گفته شود: «[خدای‌] رحمان را سجده کنید»، می‌گویند: «رحمان چیست؟ آیا برای چیزی که ما را [بدان‌] فرمان می‌دهی سجده کنیم؟» و بر رمیدنشان می‌افزاید.'),
(2916, 25, 61, '[فرخنده و] بزرگوار است آن کسی که در آسمان برجهایی نهاد، و در آن، چراغ و ماهی نوربخش قرار داد.'),
(2917, 25, 62, 'و اوست کسی که برای هر کس که بخواهد عبرت گیرد یا بخواهد سپاسگزاری نماید، شب و روز را جانشین یکدیگر گردانید.'),
(2918, 25, 63, 'و بندگان خدای رحمان کسانی‌اند که روی زمین به نرمی گام برمی‌دارند؛ و چون نادانان ایشان را طرف خطاب قرار دهند به ملایمت پاسخ می‌دهند.'),
(2919, 25, 64, 'و آنانند که در حال سجده یا ایستاده، شب را به روز می‌آورند.'),
(2920, 25, 65, 'و کسانی‌اند که می‌گویند: «پروردگارا، عذاب جهنم را از ما بازگردان که عذابش سخت و دایمی است.'),
(2921, 25, 66, 'و در حقیقت، آن بد قرارگاه و جایگاهی است.'),
(2922, 25, 67, 'و کسانی‌اند که چون انفاق کنند، نه ولخرجی می‌کنند و نه تنگ می‌گیرند، و میان این دو [روش‌] حد وسط را برمی‌گزینند.'),
(2923, 25, 68, 'و کسانی‌اند که با خدا معبودی دیگر نمی‌خوانند و کسی را که خدا [خونش را] حرام کرده است جز به حق نمی‌کُشند، و زنا نمی‌کنند، و هر کس اینها را انجام دهد سزایش را دریافت خواهد کرد.'),
(2924, 25, 69, 'برای او در روز قیامت عذاب دو چندان می‌شود و پیوسته در آن خوار می‌ماند.'),
(2925, 25, 70, 'مگر کسی که توبه کند و ایمان آورد و کار شایسته کند. پس خداوند بدیهایشان را به نیکیها تبدیل می‌کند، و خدا همواره آمرزنده مهربان است.'),
(2926, 25, 71, 'و هر کس توبه کند و کار شایسته انجام دهد، در حقیقت به سوی خدا بازمی‌گردد.'),
(2927, 25, 72, 'و کسانی‌اند که گواهی دروغ نمی‌دهند؛ و چون بر لغو بگذرند با بزرگواری می‌گذرند.'),
(2928, 25, 73, 'و کسانی‌اند که چون به آیات پروردگارشان تذکر داده شوند، کر و کور روی آن نمی‌افتند.'),
(2929, 25, 74, 'و کسانی‌اند که می‌گویند: «پروردگارا، به ما از همسران و فرزندانمان آن ده که مایه روشنی چشمان [ما] باشد، و ما را پیشوای پرهیزگاران گردان.»'),
(2930, 25, 75, 'اینانند که به پاس آنکه صبر کردند، غرفه‌[های بهشت را] پاداش خواهند یافت و در آنجا با سلام و درود مواجه خواهند شد.'),
(2931, 25, 76, 'در آنجا، جاودانه خواهند ماند. چه خوش قرارگاه و مقامی!'),
(2932, 25, 77, 'بگو: «اگر دعای شما نباشد، پروردگارم هیچ اعتنایی به شما نمی‌کند. در حقیقت شما به تکذیب پرداخته‌اید و به زودی [عذاب بر شما] لازم خواهد شد.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سین، میم.'),
(2934, 26, 2, 'این است آیه‌های کتاب روشنگر.'),
(2935, 26, 3, 'شاید تو از اینکه [مشرکان‌] ایمان نمی‌آورند، جان خود را تباه سازی.'),
(2936, 26, 4, 'اگر بخواهیم، معجزه‌ای از آسمان بر آنان فرود می‌آوریم، تا در برابر آن، گردنهایشان خاضع گردد.'),
(2937, 26, 5, 'و هیچ تذکر جدیدی از سوی [خدای‌] رحمان برایشان نیامد جز اینکه همواره از آن روی برمی‌تافتند.'),
(2938, 26, 6, '[آنان‌] در حقیقت به تکذیب پرداختند، و به زودی خبر آنچه که بدان ریشخند می کردند، بدیشان خواهد رسید.'),
(2939, 26, 7, 'مگر در زمین ننگریسته‌اند که چه قدر در آن از هر گونه جفتهای زیبا رویانیده‌ایم؟'),
(2940, 26, 8, 'قطعاً در این [هنرنمایی‌] عبرتی است و[لی‌] بیشترشان ایمان‌آورنده نیستند.'),
(2941, 26, 9, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(2942, 26, 10, 'و [یاد کن‌] هنگامی را که پروردگارت موسی را ندا درداد که به سوی قوم ستمکار برو:'),
(2943, 26, 11, 'قوم فرعون؛ آیا پروا ندارند؟!'),
(2944, 26, 12, 'گفت: «پروردگارا، می‌ترسم مرا تکذیب کنند،'),
(2945, 26, 13, 'و سینه‌ام تنگ می‌گردد، و زبانم باز نمی‌شود، پس به سوی هارون بفرست.'),
(2946, 26, 14, 'و [از طرفی‌] آنان بر [گردن‌] من خونی دارند و می‌ترسم مرا بکشند.»'),
(2947, 26, 15, 'فرمود: «نه، چنین نیست؛ نشانه‌های ما را [برای آنان‌] بِبَرید که ما با شما شنونده‌ایم.»'),
(2948, 26, 16, 'پس به سوی فرعون بروید و بگویید: «ما پیامبر پروردگار جهانیانیم،'),
(2949, 26, 17, 'فرزندان اسرائیل را با ما بفرست.»'),
(2950, 26, 18, '[فرعون‌] گفت: «آیا تو را از کودکی در میان خود نپروردیم و سالیانی چند از عمرت را پیش ما نماندی؟'),
(2951, 26, 19, 'و [سرانجام‌] کار خود را کردی، و تو از ناسپاسانی.»'),
(2952, 26, 20, 'گفت: «آن را هنگامی مرتکب شدم که از گمراهان بودم،'),
(2953, 26, 21, 'و چون از شما ترسیدم، از شما گریختم، تا پروردگارم به من دانش بخشید و مرا از پیامبران قرار داد.'),
(2954, 26, 22, 'و [آیا] اینکه فرزندان اسرائیل را بنده [خود] ساخته‌ای نعمتی است که منّتش را بر من می‌نهی؟»'),
(2955, 26, 23, 'فرعون گفت: «و پروردگار جهانیان چیست؟»'),
(2956, 26, 24, 'گفت: «پروردگار آسمانها و زمین و آنچه میان آن دو است-اگر اهل یقین باشید.»'),
(2957, 26, 25, '[فرعون‌] به کسانی که پیرامونش بودند گفت: «آیا نمی‌شنوید؟»'),
(2958, 26, 26, '[موسی دوباره‌] گفت: «پروردگار شما و پروردگار پدران پیشین شما.»'),
(2959, 26, 27, '[فرعون‌] گفت: «واقعاً این پیامبری که به سوی شما فرستاده شده، سخت دیوانه است.»'),
(2960, 26, 28, '[موسی‌] گفت: «پروردگار خاور و باختر و آنچه میان آن دو است-اگر تعقّل کنید.»'),
(2961, 26, 29, '[فرعون‌] گفت: «اگر خدایی غیر از من اختیار کنی قطعاً تو را از [جمله‌] زندانیان خواهم ساخت.»'),
(2962, 26, 30, 'گفت: «گر چه برای تو چیزی آشکار بیاورم؟»'),
(2963, 26, 31, 'گفت: «اگر راست می‌گویی آن را بیاور.»'),
(2964, 26, 32, 'پس عصای خود بیفکند و بناگاه آن اژدری نمایان شد.'),
(2965, 26, 33, 'و دستش را بیرون کشید و بناگاه آن برای تماشاگران سپید می‌نمود.'),
(2966, 26, 34, '[فرعون‌] به سرانی که پیرامونش بودند گفت: «واقعاً این ساحری بسیار داناست.'),
(2967, 26, 35, 'می‌خواهد با سحر خود، شما را از سرزمینتان بیرون کند، اکنون چه رأی می‌دهید؟»'),
(2968, 26, 36, 'گفتند: «او و برادرش را در بند دار و گردآورندگان را به شهرها بفرست،'),
(2969, 26, 37, 'تا هر ساحر ماهری را نزد تو بیاورند.»'),
(2970, 26, 38, 'پس ساحران برای موعدِ روزی معلوم گردآوری شدند.'),
(2971, 26, 39, 'و به توده مردم گفته شد: «آیا شما هم جمع خواهید شد؟'),
(2972, 26, 40, 'بدین امید که اگر ساحران غالب شدند از آنان پیروی کنیم؟»'),
(2973, 26, 41, 'و چون ساحران پیش فرعون آمدند، گفتند: «آیا اگر ما غالب آییم واقعاً برای ما مزدی خواهد بود؟»'),
(2974, 26, 42, 'گفت: «آری، و در آن صورت شما حتماً از [زمره‌] مقرّبان خواهید شد.»'),
(2975, 26, 43, 'موسی به آنان گفت: «آنچه را شما می‌اندازید بیندازید.»'),
(2976, 26, 44, 'پس ریسمانها و چوبدستی‌هایشان را انداختند و گفتند: «به عزّت فرعون که ما حتماً پیروزیم.»'),
(2977, 26, 45, 'پس موسی عصایش را انداخت و بناگاه هر چه را به دروغ برساخته بودند بلعید.'),
(2978, 26, 46, 'در نتیجه، ساحران به حالت سجده درافتادند.'),
(2979, 26, 47, 'گفتند: «به پروردگار جهانیان ایمان آوردیم:'),
(2980, 26, 48, 'پروردگار موسی و هارون.»'),
(2981, 26, 49, 'گفت: « [آیا] پیش از آنکه به شما اجازه دهم به او ایمان آوردید؟ قطعاً او همان بزرگ شماست که به شما سحر آموخته است. به زودی خواهید دانست. حتماً دستها و پاهای شما را از چپ و راست خواهم برید و همه‌تان را به دار خواهم آویخت.»'),
(2982, 26, 50, 'گفتند: «باکی نیست، ما روی به سوی پروردگار خود می‌آوریم.'),
(2983, 26, 51, 'ما امیدواریم که پروردگارمان گناهانمان را بر ما ببخشاید، [چرا] که نخستین ایمان‌آورندگان بودیم.»'),
(2984, 26, 52, 'و به موسی وحی کردیم که: «بندگان مرا شبانه حرکت ده، زیرا شما مورد تعقیب قرار خواهید گرفت.»'),
(2985, 26, 53, 'پس فرعون مأمورانِ جمع‌آوریِ [خود را] به شهرها فرستاد،'),
(2986, 26, 54, '[و گفت:] «اینها عده‌ای ناچیزند.'),
(2987, 26, 55, 'و راستی آنها ما را بر سر خشم آورده‌اند،'),
(2988, 26, 56, 'و[لی‌] ما همگی به حال آماده‌باش درآمده‌ایم.»'),
(2989, 26, 57, 'سرانجام، ما آنان را از باغستانها و چشمه‌سارها،'),
(2990, 26, 58, 'و گنجینه‌ها و جایگاه‌های پر ناز و نعمت بیرون کردیم.'),
(2991, 26, 59, '[اراده ما] چنین بود، و آن [نعمتها] را به فرزندان اسرائیل میراث دادیم.'),
(2992, 26, 60, 'پس هنگام برآمدن آفتاب، آنها را تعقیب کردند.'),
(2993, 26, 61, 'چون دو گروه، همدیگر را دیدند، یاران موسی گفتند: «ما قطعاً گرفتار خواهیم شد.»'),
(2994, 26, 62, 'گفت: «چنین نیست، زیرا پروردگارم با من است و به زودی مرا راهنمایی خواهد کرد.»'),
(2995, 26, 63, 'پس به موسی وحی کردیم: «با عصای خود بر این دریا بزن.» تا از هم شکافت، و هر پاره‌ای همچون کوهی سترگ بود.'),
(2996, 26, 64, 'و دیگران را بدانجا نزدیک گردانیدیم.'),
(2997, 26, 65, 'و موسی و همه کسانی را که همراه او بودند نجات دادیم؛'),
(2998, 26, 66, 'آنگاه دیگران را غرق کردیم.'),
(2999, 26, 67, 'مسلماً در این [واقعه‌] عبرتی بود، و[لی‌] بیشترشان ایمان‌آورنده نبودند.'),
(3000, 26, 68, 'و قطعاً پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3001, 26, 69, 'و بر آنان گزارش ابراهیم را بخوان.'),
(3002, 26, 70, 'آنگاه که به پدر خود و قومش گفت: «چه می‌پرستید؟»'),
(3003, 26, 71, 'گفتند: «بتانی را می‌پرستیم و همواره ملازم آنهاییم.»'),
(3004, 26, 72, 'گفت: «آیا وقتی دعا می‌کنید، از شما می‌شنوند؟'),
(3005, 26, 73, 'یا به شما سود یا زیان می‌رسانند؟»'),
(3006, 26, 74, 'گفتند: «نه، بلکه پدران خود را یافتیم که چنین می‌کردند.»'),
(3007, 26, 75, 'گفت: «آیا در آنچه می‌پرستیده‌اید تأمّل کرده‌اید؟'),
(3008, 26, 76, 'شما و پدران پیشین شما؟'),
(3009, 26, 77, 'قطعاً همه آنها -جز پروردگار جهانیان- دشمن منند.'),
(3010, 26, 78, 'آن کس که مرا آفریده و همو راهنماییم می‌کند،'),
(3011, 26, 79, 'و آن کس که او به من خوراک می‌دهد و سیرابم می‌گرداند،'),
(3012, 26, 80, 'و چون بیمار شوم او مرا درمان می‌بخشد،'),
(3013, 26, 81, 'و آن کس که مرا می‌میراند و سپس زنده‌ام می‌گرداند،'),
(3014, 26, 82, 'و آن کس که امید دارم روز پاداش، گناهم را بر من ببخشاید.»'),
(3015, 26, 83, 'پروردگارا، به من دانش عطا کن و مرا به صالحان ملحق فرمای،'),
(3016, 26, 84, 'و برای من در [میان‌] آیندگان آوازه نیکو گذار،'),
(3017, 26, 85, 'و مرا از وارثان بهشت پر نعمت گردان،'),
(3018, 26, 86, 'و بر پدرم ببخشای که او از گمراهان بود،'),
(3019, 26, 87, 'و روزی که [مردم‌] برانگیخته می‌شوند رسوایم مکن:'),
(3020, 26, 88, 'روزی که هیچ مال و فرزندی سود نمی‌دهد،'),
(3021, 26, 89, 'مگر کسی که دلی پاک به سوی خدا بیاورد.'),
(3022, 26, 90, 'و [آن روز] بهشت برای پرهیزگاران نزدیک می‌گردد.'),
(3023, 26, 91, 'و جهنم برای گمراهان نمودار می‌شود.'),
(3024, 26, 92, 'و به آنان گفته می‌شود: «آنچه جز خدا می‌پرستیدید کجایند؟'),
(3025, 26, 93, 'آیا یاریتان می‌کنند یا خود را یاری می‌دهند؟'),
(3026, 26, 94, 'پس آنها و همه گمراهان در آن [آتش‌] افکنده می‌شوند،'),
(3027, 26, 95, 'و [نیز] همه سپاهیان ابلیس.'),
(3028, 26, 96, 'آنها در آنجا با یکدیگر ستیزه می‌کنند [و] می‌گویند:'),
(3029, 26, 97, '«سوگند به خدا که ما در گمراهی آشکاری بودیم،'),
(3030, 26, 98, 'آنگاه که شما را با پروردگار جهانیان برابر می‌کردیم،'),
(3031, 26, 99, 'و جز تباهکاران ما را گمراه نکردند،'),
(3032, 26, 100, 'در نتیجه شفاعتگرانی نداریم،'),
(3033, 26, 101, 'و نه دوستی نزدیک.'),
(3034, 26, 102, 'و ای کاش که بازگشتی برایِ ما بود و از مؤمنان می‌شدیم.'),
(3035, 26, 103, 'حقاً در این [سرگذشت درس‌] عبرتی است و[لی‌] بیشترشان مؤمن نبودند.'),
(3036, 26, 104, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3037, 26, 105, 'قوم نوح پیامبران را تکذیب کردند.'),
(3038, 26, 106, 'چون برادرشان نوح به آنان گفت: «آیا پروا ندارید؟'),
(3039, 26, 107, 'من برای شما فرستاده‌ای در خور اعتمادم؛'),
(3040, 26, 108, 'از خدا پروا کنید و فرمانم ببرید،'),
(3041, 26, 109, 'و بر این [رسالت‌] اجری از شما طلب نمی‌کنم. اجر من جز بر عهده پروردگار جهانیان نیست.'),
(3042, 26, 110, 'پس، از خدا پروا کنید و فرمانم ببرید.»'),
(3043, 26, 111, 'گفتند: «آیا به تو ایمان بیاوریم و حال آنکه فرومایگان از تو پیروی کرده‌اند؟»'),
(3044, 26, 112, '[نوح‌] گفت: «به [جزئیات‌] آنچه می‌کرده‌اند چه آگاهی دارم؟'),
(3045, 26, 113, 'حسابشان -اگر درمی‌یابید- جز با پروردگارم نیست.'),
(3046, 26, 114, 'و من طردکننده مؤمنان نیستم.'),
(3047, 26, 115, 'من جز هشداردهنده‌ای آشکار [بیش‌] نیستم.»'),
(3048, 26, 116, 'گفتند: «ای نوح، اگر دست برنداری قطعاً از [جمله‌] سنگسارشدگان خواهی بود.»'),
(3049, 26, 117, 'گفت: «پروردگارا، قوم من مرا تکذیب کردند؛'),
(3050, 26, 118, 'میان من و آنان فیصله ده، و من و هر کس از مؤمنان را که با من است نجات بخش.»'),
(3051, 26, 119, 'پس او و هر که را در آن کشتی آکنده با او بود، رهانیدیم؛'),
(3052, 26, 120, 'آنگاه باقی‌ماندگان را غرق کردیم.'),
(3053, 26, 121, 'قطعاً در این [ماجرا درس‌] عبرتی بود، و[لی‌] بیشترشان ایمان‌آورنده نبودند.'),
(3054, 26, 122, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3055, 26, 123, 'عادیان، پیامبران [خدا] را تکذیب کردند.'),
(3056, 26, 124, 'آنگاه که برادرشان هود به آنان گفت: «آیا پروا ندارید؟'),
(3057, 26, 125, 'من برای شما فرستاده‌ای در خور اعتمادم.'),
(3058, 26, 126, 'از خدا پروا کنید و فرمانم ببرید.'),
(3059, 26, 127, 'و بر این [رسالت‌] اجری از شما طلب نمی‌کنم. اجر من جز بر عهده پروردگار جهانیان نیست.'),
(3060, 26, 128, 'آیا بر هر تپه‌ای بنایی می‌سازید که [در آن‌] دست به بیهوده‌کاری زنید؟'),
(3061, 26, 129, 'و کاخهای استوار می‌گیرید به امید آنکه جاودانه بمانید؟'),
(3062, 26, 130, 'و چون حمله‌ور می‌شوید [چون‌] زورگویان حمله‌ور می‌شوید؟'),
(3063, 26, 131, 'پس، از خدا پروا دارید و فرمانم ببرید.'),
(3064, 26, 132, 'و از آن کس که شما را به آنچه می‌دانید مدد کرد پروا دارید:'),
(3065, 26, 133, 'شما را به [دادن‌] دامها و پسران مدد کرد،'),
(3066, 26, 134, 'و به [دادن‌] باغها و چشمه‌ساران؛'),
(3067, 26, 135, 'من از عذاب روزی هولناک بر شما می‌ترسم.'),
(3068, 26, 136, 'گفتند: «خواه اندرز دهی و خواه از اندرزدهندگان نباشی برای ما یکسان است.'),
(3069, 26, 137, 'این جز شیوه پیشینیان نیست.'),
(3070, 26, 138, 'و ما عذاب نخواهیم شد.»'),
(3071, 26, 139, 'پس تکذیبش کردند و هلاکشان کردیم. قطعاً در این [ماجرا درس‌] عبرتی بود، و[لی‌] بیشترشان ایمان‌آورنده نبودند.'),
(3072, 26, 140, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3073, 26, 141, 'ثمودیان، پیامبران [خدا] را تکذیب کردند؛'),
(3074, 26, 142, 'آنگاه که برادرشان صالح به آنان گفت: «آیا پروا ندارید؟'),
(3075, 26, 143, 'من برای شما فرستاده‌ای در خور اعتمادم.'),
(3076, 26, 144, 'از خدا پروا کنید و فرمانم ببرید.'),
(3077, 26, 145, 'و بر این [رسالت‌] اجری از شما طلب نمی‌کنم. اجر من جز بر عهده پروردگار جهانیان نیست.'),
(3078, 26, 146, 'آیا شما را در آنچه اینجا دارید آسوده رها می‌کنند؟'),
(3079, 26, 147, 'در باغها و در کنار چشمه‌ساران،'),
(3080, 26, 148, 'و کشتزارها و خرمابنانی که شکوفه‌هایشان لطیف است؟'),
(3081, 26, 149, 'و هنرمندانه برای خود از کوهها خانه‌هایی می‌تراشید.'),
(3082, 26, 150, 'از خدا پروا کنید و فرمانم ببرید.'),
(3083, 26, 151, 'و فرمان افراطگران را پیروی مکنید:'),
(3084, 26, 152, 'آنان که در زمین فساد می‌کنند و اصلاح نمی‌کنند.»'),
(3085, 26, 153, 'گفتند: «قطعاً تو از افسون‌شدگانی.'),
(3086, 26, 154, 'تو جز بشری مانند ما [بیش‌] نیستی. اگر راست می‌گویی معجزه‌ای بیاور.'),
(3087, 26, 155, 'گفت: «این ماده‌شتری است که نوبتی از آب او راست و روزی معین نوبت آب شماست.'),
(3088, 26, 156, 'و به آن گزندی مرسانید که عذاب روزی هولناک شما را فرو می‌گیرد.'),
(3089, 26, 157, 'پس آن را پی کردند و پشیمان گشتند.'),
(3090, 26, 158, 'آنگاه آنان را عذاب فرو گرفت. قطعاً در این [ماجرا] عبرتی است، و[لی‌] بیشترشان ایمان‌آورنده نبودند.'),
(3091, 26, 159, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3092, 26, 160, 'قوم لوط فرستادگان را تکذیب کردند.'),
(3093, 26, 161, 'آنگاه برادرشان لوط به آنان گفت: «آیا پروا ندارید؟'),
(3094, 26, 162, 'من برای شما فرستاده‌ای در خور اعتمادم.'),
(3095, 26, 163, 'از خدا پروا دارید و فرمانم ببرید.'),
(3096, 26, 164, 'و بر این [رسالت‌] اجری از شما طلب نمی‌کنم. اجر من جز بر عهده پروردگار جهانیان نیست.'),
(3097, 26, 165, 'آیا از میان مردم جهان، با مردها در می‌آمیزید؟'),
(3098, 26, 166, 'و آنچه را پروردگارتان از همسرانتان برای شما آفریده وامی‌گذارید؟ [نه،] بلکه شما مردمی تجاوزکارید.'),
(3099, 26, 167, 'گفتند: «ای لوط، اگر دست برنداری، قطعاً از اخراج‌شدگان خواهی بود.»'),
(3100, 26, 168, 'گفت: «به راستی من دشمن کردار شمایم.'),
(3101, 26, 169, 'پروردگارا، مرا و کسان مرا از آنچه انجام می‌دهند رهایی بخش.»'),
(3102, 26, 170, 'پس او و کسانش را همگی، رهانیدیم-'),
(3103, 26, 171, 'جز پیرزنی که از باقی‌ماندگان [در خاکستر آتش‌] بود.'),
(3104, 26, 172, 'سپس دیگران را سخت هلاک کردیم.'),
(3105, 26, 173, 'و بر [سر] آنان بارانی [از آتش گوگرد] فرو ریختیم. و چه بد بود باران بیم‌داده‌شدگان.'),
(3106, 26, 174, 'قطعاً در این [عقوبت‌] عبرتی است، و[لی‌] بیشترشان ایمان‌آورنده نبودند.'),
(3107, 26, 175, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3108, 26, 176, 'اصحاب ایکه فرستادگان را تکذیب کردند.'),
(3109, 26, 177, 'آنگاه که شعیب به آنان گفت: «آیا پروا ندارید؟'),
(3110, 26, 178, 'من برای شما فرستاده‌ای در خور اعتمادم.'),
(3111, 26, 179, 'از خدا پروا دارید و فرمانم ببرید.'),
(3112, 26, 180, 'و بر این [رسالت‌] اجری از شما طلب نمی‌کنم. اجر من جز بر عهده پروردگار جهانیان نیست.'),
(3113, 26, 181, 'پیمانه را تمام دهید و از کم‌فروشان مباشید.'),
(3114, 26, 182, 'و با ترازوی درست بسنجید.'),
(3115, 26, 183, 'و از ارزش اموال مردم مکاهید و در زمین سر به فساد بر مدارید.'),
(3116, 26, 184, 'و از آن کس که شما و خلق [انبوه‌] گذشته را آفریده است پروا کنید.»'),
(3117, 26, 185, 'گفتند: «تو واقعاً از افسون‌شدگانی.»'),
(3118, 26, 186, '«و تو جز بشری مانند ما [بیش‌] نیستی، و قطعاً تو را از دروغگویان می‌دانیم.'),
(3119, 26, 187, 'پس اگر از راستگویانی، پاره‌ای از آسمان بر [سر] ما بیفکن.»'),
(3120, 26, 188, '[شعیب‌] گفت: «پروردگارم به آنچه می‌کنید داناتر است.»'),
(3121, 26, 189, 'پس او را تکذیب کردند، و عذابِ روزِ ابر [آتشبار] آنان را فرو گرفت. به راستی آن، عذاب روزی هولناک بود.'),
(3122, 26, 190, 'قطعاً در این [عقوبت درسِ‌] عبرتی است، و[لی‌] بیشترشان ایمان آورنده نبودند.'),
(3123, 26, 191, 'و در حقیقت، پروردگار تو همان شکست‌ناپذیر مهربان است.'),
(3124, 26, 192, 'و راستی که این [قرآن‌] وحی پروردگار جهانیان است.'),
(3125, 26, 193, '«روح الامین» آن را بر دلت نازل کرد،'),
(3126, 26, 194, 'تا از [جمله‌] هشداردهندگان باشی؛'),
(3127, 26, 195, 'به زبان عربی روشن،'),
(3128, 26, 196, 'و [وصف‌] آن در کتابهای پیشینیان آمده است.'),
(3129, 26, 197, 'آیا برای آنان، این خود دلیلی روشن نیست که علمای بنی‌اسرائیل از آن اطّلاع دارند؟'),
(3130, 26, 198, 'و اگر آن را بر برخی از غیر عرب زبانان نازل می‌کردیم،'),
(3131, 26, 199, 'و پیامبر آن را برایشان می‌خواند به آن ایمان نمی‌آوردند.'),
(3132, 26, 200, 'این گونه در دلهای گناهکاران، [انکار را] راه می‌دهیم:'),
(3133, 26, 201, 'که به آن نگروند تا عذاب پردرد را ببینند،'),
(3134, 26, 202, 'که به طور ناگهانی -در حالی که بی‌خبرند- بدیشان برسد.'),
(3135, 26, 203, 'و بگویند: «آیا مهلت خواهیم یافت؟»'),
(3136, 26, 204, 'پس آیا عذاب ما را به شتاب می‌خواهند؟'),
(3137, 26, 205, 'مگر نمی‌دانی که اگر سالها آنان را برخوردار کنیم،'),
(3138, 26, 206, 'و آنگاه آنچه که [بدان‌] بیم داده می‌شوند بدیشان برسد،'),
(3139, 26, 207, 'آنچه از آن برخوردار می‌شدند، به کارشان نمی‌آید [و عذاب را از آنان دفع نمی‌کند]؟'),
(3140, 26, 208, 'و هیچ شهری را هلاک نکردیم مگر آنکه برای آن هشداردهندگانی بود.'),
(3141, 26, 209, '[تا آنان را] تذکر [دهند]؛ و ما ستمکار نبوده‌ایم.'),
(3142, 26, 210, 'و شیطانها آن را فرود نیاورده‌اند.'),
(3143, 26, 211, 'و آنان را نسزد و نمی‌توانند [وحی کنند].'),
(3144, 26, 212, 'در حقیقت آنها از شنیدن، معزول [و محروم‌]اند.'),
(3145, 26, 213, 'پس با خدا، خدای دیگر مخوان که از عذاب‌شدگان خواهی شد.'),
(3146, 26, 214, 'و خویشان نزدیکت را هشدار ده.'),
(3147, 26, 215, 'و برای آن مؤمنانی که تو را پیروی کرده‌اند، بال خود را فرو گستر.'),
(3148, 26, 216, 'و اگر تو را نافرمانی کردند، بگو: «من از آنچه می‌کنید بیزارم.»'),
(3149, 26, 217, 'و بر [خدایِ‌] عزیزِ مهربان توکل کن،'),
(3150, 26, 218, 'آن کس که چون [به نماز] برمی‌خیزی تو را می‌بیند،'),
(3151, 26, 219, 'و حرکت تو را در میان سجده‌کنندگان [می‌نگرد].'),
(3152, 26, 220, 'او همان شنوای داناست.'),
(3153, 26, 221, 'آیا شما را خبر دهم که شیاطین بر چه کسی فرود می‌آیند؟'),
(3154, 26, 222, 'بر هر دروغزن گناهکاری فرود می‌آیند،'),
(3155, 26, 223, 'که [دزدانه‌] گوش فرا می‌دارند و بیشترشان دروغگویند،'),
(3156, 26, 224, 'و شاعران را گمراهان پیروی می‌کنند.'),
(3157, 26, 225, 'آیا ندیده‌ای که آنان در هر وادیی سرگردانند؟'),
(3158, 26, 226, 'و آنانند که چیزهایی می‌گویند که انجام نمی‌دهند.'),
(3159, 26, 227, 'مگر کسانی که ایمان آورده و کارهای شایسته کرده و خدا را بسیار به یاد آورده و پس از آنکه مورد ستم قرار گرفته‌اند یاری خواسته‌اند. و کسانی که ستم کرده‌اند به زودی خواهند دانست به کدام بازگشتگاه برخواهند گشت.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سین. این است آیات قرآن و [آیات‌] کتابی روشنگر،'),
(3161, 27, 2, 'که [مایه‌] هدایت و بشارت برای مؤمنان است.'),
(3162, 27, 3, 'همانان که نماز برپا می‌دارند و زکات می‌دهند و خود به آخرت یقین دارند.'),
(3163, 27, 4, 'کسانی که به آخرت ایمان ندارند، کردارهایشان را در نظرشان بیاراستیم [تا همچنان‌] سرگشته بمانند.'),
(3164, 27, 5, 'آنان کسانی‌اند که عذاب سخت برای ایشان خواهد بود، و در آخرت، خود زیانکارترین [مردم‌]اند.'),
(3165, 27, 6, 'و حقاً تو قرآن را از سوی حکیمی دانا دریافت می‌داری.'),
(3166, 27, 7, '[یادکن‌] هنگامی را که موسی به خانواده خود گفت: «من آتشی به نظرم رسید، به زودی برای شما خبری از آن خواهم آورد، یا شعله آتشی برای شما می‌آورم، باشد که خود را گرم کنید.»'),
(3167, 27, 8, 'چون نزد آن آمد، آوا رسید که: «خجسته [و مبارک گردید] آنکه در کنار این آتش و آنکه پیرامون آن است، و منزه است خدا، پروردگار جهانیان.»'),
(3168, 27, 9, '«ای موسی، این منم خدای عزیز حکیم.»'),
(3169, 27, 10, 'و عصایت را بیفکن. پس چون آن را همچون ماری دید که می‌جنبد، پشت گردانید و به عقب بازنگشت. «ای موسی، مترس که فرستادگان پیش من نمی‌ترسند.'),
(3170, 27, 11, 'لیکن کسی که ستم کرده سپس -بعد از بدی- نیکی را جایگزین [آن‌] گردانیده، [بداند] که من آمرزنده مهربانم.'),
(3171, 27, 12, 'و دستت را در گریبانت کن تا سپید بی‌عیب بیرون آید. [اینها] از [جمله‌] نشانه‌های نُه‌گانه‌ای است [که باید] به سوی فرعون و قومش [ببَری‌]، زیرا که آنان مردمی نافرمانند.'),
(3172, 27, 13, 'و هنگامی که آیات روشنگر ما به سویشان آمد گفتند: «این سحری آشکار است.»'),
(3173, 27, 14, 'و با آنکه دلهایشان بدان یقین داشت، از روی ظلم و تکبر آن را انکار کردند. پس ببین فرجام فسادگران چگونه بود.'),
(3174, 27, 15, 'و به راستی به داوود و سلیمان دانشی عطا کردیم، و آن دو گفتند: «ستایش خدایی را که ما را بر بسیاری از بندگان باایمانش برتری داده است.»'),
(3175, 27, 16, 'و سلیمان از داوود میراث یافت و گفت: «ای مردم، ما زبان پرندگان را تعلیم یافته‌ایم و از هر چیزی به ما داده شده است. راستی که این همان امتیاز آشکار است.»'),
(3176, 27, 17, 'و برای سلیمان سپاهیانش از جن و انس و پرندگان جمع‌آوری شدند و [برای رژه‌] دسته دسته گردیدند.'),
(3177, 27, 18, 'تا آنگاه که به وادی مورچگان رسیدند. مورچه‌ای [به زبان خویش‌] گفت: «ای مورچگان، به خانه‌هایتان داخل شوید، مبادا سلیمان و سپاهیانش -ندیده و ندانسته- شما را پایمال کنند.»'),
(3178, 27, 19, '[سلیمان‌] از گفتار او دهان به خنده گشود و گفت: «پروردگارا، در دلم افکن تا نعمتی را که به من و پدر و مادرم ارزانی داشته‌ای سپاس بگزارم، و به کار شایسته‌ای که آن را می‌پسندی بپردازم، و مرا به رحمت خویش در میان بندگان شایسته‌ات داخل کن.»'),
(3179, 27, 20, 'و جویای [حال‌] پرندگان شد و گفت: «مرا چه شده است که هدهد را نمی‌بینم؟ یا شاید از غایبان است؟'),
(3180, 27, 21, 'قطعاً او را به عذابی سخت عذاب می‌کنم یا سرش را می‌برم مگر آنکه دلیلی روشن برای من بیاورد.'),
(3181, 27, 22, 'پس دیری نپایید که [هدهد آمد و] گفت: «از چیزی آگاهی یافتم که از آن آگاهی نیافته‌ای، و برای تو از «سبا» گزارشی درست آورده‌ام.'),
(3182, 27, 23, 'من [آنجا] زنی را یافتم که بر آنها سلطنت می‌کرد و از هر چیزی به او داده شده بود و تختی بزرگ داشت.'),
(3183, 27, 24, 'او و قومش را چنین یافتم که به جای خدا به خورشید سجده می‌کنند، و شیطان اعمالشان را برایشان آراسته و آنان را از راه [راست‌] باز داشته بود، در نتیجه [به حق‌] راه نیافته بودند.'),
(3184, 27, 25, '[آری، شیطان چنین کرده بود] تا برای خدایی که نهان را در آسمانها و زمین بیرون می‌آورد و آنچه را پنهان می‌دارید و آنچه را آشکار می‌نمایید می‌داند، سجده نکنند؛'),
(3185, 27, 26, 'خدای یکتا که هیچ خدایی جز او نیست، پروردگار عرش بزرگ است.»'),
(3186, 27, 27, 'گفت: «خواهیم دید آیا راست گفته‌ای یا از دروغگویان بوده‌ای.»'),
(3187, 27, 28, '«این نامه مرا ببر و به سوی آنها بیفکن، آنگاه از ایشان روی برتاب، پس ببین چه پاسخ می‌دهند.»'),
(3188, 27, 29, '[ملکه سبا] گفت: «ای سران [کشور] نامه‌ای ارجمند برای من آمده است،'),
(3189, 27, 30, 'که از طرف سلیمان است و [مضمون آن‌] این است: به نام خداوند رحمتگر مهربان.'),
(3190, 27, 31, 'بر من بزرگی مکنید و مرا از در اطاعت درآیید.»'),
(3191, 27, 32, 'گفت: «ای سران [کشور] در کارم به من نظر دهید که بی‌حضور شما [تا به حال‌] کاری را فیصله نداده‌ام.»'),
(3192, 27, 33, 'گفتند: «ما سخت نیرومند و دلاوریم، و[لی‌] اختیار کار با توست، بنگر چه دستور می‌دهی؟»'),
(3193, 27, 34, '[ملکه‌] گفت: «پادشاهان چون به شهری درآیند، آن را تباه و عزیزانش را خوار می‌گردانند، و این گونه می‌کنند.»'),
(3194, 27, 35, '«و [اینک‌] من ارمغانی به سویشان می‌فرستم و می‌نگرم که فرستادگان [من‌] با چه چیز بازمی‌گردند.»'),
(3195, 27, 36, 'و چون [فرستاده‌] نزد سلیمان آمد، [سلیمان‌] گفت: «آیا مرا به مالی کمک می‌دهید؟ آنچه خدا به من عطا کرده، بهتر است از آنچه به شما داده‌است. [نه،] بلکه شما به ارمغان خود شادمانی می‌نمایید.'),
(3196, 27, 37, 'به سوی آنان بازگرد که قطعاً سپاهیانی بر [سر] ایشان می‌آوریم که در برابر آنها تاب ایستادگی نداشته باشند و از آن [دیار] به خواری و زبونی بیرونشان می‌کنیم.»'),
(3197, 27, 38, '[سپس‌] گفت: «ای سران [کشور] کدام یک از شما تخت او را -پیش از آنکه مطیعانه نزد من آیند- برای من می‌آورد؟»'),
(3198, 27, 39, 'عفریتی از جن گفت: «من آن را پیش از آنکه از مجلس خود برخیزی برای تو می‌آورم و بر این [کار] سخت توانا و مورد اعتمادم.»'),
(3199, 27, 40, 'کسی که نزد او دانشی از کتاب [الهی‌] بود، گفت: «من آن را پیش از آنکه چشم خود را بر هم زنی برایت می‌آورم.» پس چون [سلیمان‌] آن [تخت‌] را نزد خود مستقر دید، گفت: «این از فضل پروردگار من است، تا مرا بیازماید که آیا سپاسگزارم یا ناسپاسی می‌کنم. و هر کس سپاس گزارد، تنها به سود خویش سپاس می‌گزارد، و هر کس ناسپاسی کند، بی‌گمان پروردگارم بی‌نیاز و کریم است.»'),
(3200, 27, 41, 'گفت: «تخت [ملکه‌] را برایش ناشناس گردانید تا ببینیم آیا پی می‌برد یا از کسانی است که پی نمی‌برند.»'),
(3201, 27, 42, 'پس وقتی [ملکه‌] آمد، [بدو] گفته شد: «آیا تخت تو همین گونه است؟» گفت: «گویا این همان است و پیش از این، ما آگاه شده و از در اطاعت درآمده بودیم.»'),
(3202, 27, 43, 'و [در حقیقت قبلاً] آنچه غیر از خدا می‌پرستید مانع [ایمان‌] او شده بود و او از جمله گروه کافران بود.'),
(3203, 27, 44, 'به او گفته شد: «وارد ساحت کاخ [پادشاهی‌] شو.» و چون آن را دید، برکه‌ای پنداشت و ساقهایش را نمایان کرد. [سلیمان‌] گفت: «این کاخی مفروش از آبگینه است.» [ملکه‌] گفت: «پروردگارا، من به خود ستم کردم و [اینک‌] با سلیمان در برابر خدا، پروردگار جهانیان، تسلیم شدم.»'),
(3204, 27, 45, 'و به راستی، به سوی ثمود، برادرشان صالح را فرستادیم که: «خدا را بپرستید.» پس به ناگاه آنان دو دسته متخاصم شدند.'),
(3205, 27, 46, '[صالح‌] گفت: «ای قوم من، چرا پیش از [جستن‌] نیکی، شتابزده خواهان بدی هستید؟ چرا از خدا آمرزش نمی‌خواهید؟ باشد که مورد رحمت قرار گیرید.»'),
(3206, 27, 47, 'گفتند: «ما به تو و به هر کس که همراه توست شگون بد زدیم.» گفت: «سرنوشت خوب و بدتان پیش خداست، بلکه شما مردمی هستید که مورد آزمایش قرار گرفته‌اید.»'),
(3207, 27, 48, 'و در آن شهر، نُه دسته بودند که در آن سرزمین فساد می‌کردند و از در اصلاح درنمی‌آمدند.'),
(3208, 27, 49, '[با هم‌] گفتند: «با یکدیگر سوگند بخورید که: حتماً به [صالح‌] و کسانش شبیخون می‌زنیم، سپس به ولیّ او خواهیم گفت: ما در محل قتل کسانش حاضر نبودیم، و ما قطعاً راست می‌گوییم.»'),
(3209, 27, 50, 'و دست به نیرنگ زدند و [ما نیز] دست به نیرنگ زدیم و خبر نداشتند.'),
(3210, 27, 51, 'پس بنگر که فرجام نیرنگشان چگونه بود: ما آنان و قومشان را همگی هلاک کردیم.'),
(3211, 27, 52, 'و این [هم‌] خانه‌های خالی آنهاست به [سزای‌] بیدادی که کرده‌اند. قطعاً در این [کیفر] برای مردمی که می‌دانند عبرتی خواهد بود.'),
(3212, 27, 53, 'و کسانی را که ایمان آورده و تقوا پیشه کرده بودند رهانیدیم.'),
(3213, 27, 54, 'و [یاد کن‌] لوط را که چون به قوم خود گفت: «آیا دیده و دانسته مرتکب عمل ناشایست [لواط] می‌شوید؟'),
(3214, 27, 55, 'آیا شما به جای زنان، از روی شهوت با مردها در می‌آمیزید؟ [نه!] بلکه شما مردمی جهالت‌پیشه‌اید.»'),
(3215, 27, 56, 'و[لی‌] پاسخ قومش غیر از این نبود که گفتند: «خاندان لوط را از شهرتان بیرون کنید که آنها مردمی هستند که به پاکی تظاهر می‌نمایند.»'),
(3216, 27, 57, 'پس او و خانواده‌اش را نجات دادیم، جز زنش را که مقدّر کردیم از باقی‌ماندگان [در خاکستر آتش‌] باشد.'),
(3217, 27, 58, 'و بارانی [از سجّیل‌] بر ایشان فرو باریدیم، و باران هشدارداده‌شدگان، چه بد بارانی بود.'),
(3218, 27, 59, 'بگو: «سپاس برای خداست، و درود بر آن بندگانش که [آنان را] برگزیده است.» آیا خدا بهتر است یا آنچه [با او] شریک می‌گردانند؟'),
(3219, 27, 60, '[آیا آنچه شریک می‌پندارند بهتر است‌] یا آن کس که آسمانها و زمین را خلق کرد و برای شما آبی از آسمان فرود آورد، پس به وسیله آن، باغهای بهجت‌انگیز رویانیدیم. کار شما نبود که درختانش را برویانید. آیا معبودی با خداست؟ [نه،] بلکه آنان قومی منحرفند.'),
(3220, 27, 61, '[آیا شریکانی که می‌پندارند بهتر است‌] یا آن کس که زمین را قرارگاهی ساخت و در آن رودها پدید آورد و برای آن، کوه‌ها را [مانند لنگر] قرار داد، و میان دو دریا برزخی گذاشت؟ آیا معبودی با خداست؟ [نه،] بلکه بیشترشان نمی‌دانند.'),
(3221, 27, 62, 'یا [کیست‌] آن کس که درمانده را -چون وی را بخواند- اجابت می‌کند، و گرفتاری را برطرف می‌گرداند، و شما را جانشینان این زمین قرار می‌دهد؟ آیا معبودی با خداست؟ چه کم پند می‌پذیرید.'),
(3222, 27, 63, 'یا آن کس که شما را در تاریکیهای خشکی و دریا راه می‌نماید و آن کس که بادها[ی باران زا] را پیشاپیش رحمتش بشارتگر می‌فرستد؟ آیا معبودی با خداست؟ خدا برتر [و بزرگتر] است از آنچه [با او ]شریک می‌گردانند.'),
(3223, 27, 64, 'یا آن کس که خلق را آغاز می‌کند و سپس آن را بازمی‌آورد، و آن کس که از آسمان و زمین به شما روزی می‌دهد؟ آیا معبودی با خداست؟ بگو: «اگر راست می‌گویید، برهان خویش را بیاورید.»'),
(3224, 27, 65, 'بگو: «هر که در آسمانها و زمین است -جز خدا- غیب را نمی‌شناسند و نمی‌دانند کی برانگیخته خواهند شد؟»'),
(3225, 27, 66, '[نه،] بلکه علم آنان در باره آخرت نارساست؛ [نه،] بلکه ایشان در باره آن تردید دارند؛ [نه،] بلکه آنان در مورد آن کوردلند.'),
(3226, 27, 67, 'و کسانی که کفر ورزیدند، گفتند: «آیا وقتی ما و پدرانمان خاک شدیم، آیا حتماً [زنده از گور] بیرون آورده می‌شویم؟'),
(3227, 27, 68, 'در حقیقت، این را به ما و پدرانمان قبلاً وعده داده‌اند؛ این جز افسانه‌های پیشینیان نیست.»'),
(3228, 27, 69, 'بگو: «در زمین بگردید و بنگرید فرجام گنه‌پیشگان چگونه بوده است.»'),
(3229, 27, 70, 'و بر آنان غم مخور، و از آنچه مکر می‌کنند تنگدل مباش.'),
(3230, 27, 71, 'و می‌گویند: «اگر راست می‌گویید، این وعده کی خواهد بود؟»'),
(3231, 27, 72, 'بگو: «شاید برخی از آنچه را به شتاب می‌خواهید در پی شما باشد.»'),
(3232, 27, 73, 'و راستی پروردگارت بر [این‌] مردم دارای بخشش است، ولی بیشترشان سپاس نمی‌دارند.'),
(3233, 27, 74, 'و در حقیقت، پروردگار تو آنچه را در سینه‌هایشان نهفته و آنچه را آشکار می‌دارند نیک می‌داند.'),
(3234, 27, 75, 'و هیچ پنهانی در آسمان و زمین نیست، مگر اینکه در کتابی روشن [درج‌] است.'),
(3235, 27, 76, 'بی‌گمان، این قرآن بر فرزندان اسرائیل بیشتر آنچه را که آنان در باره‌اش اختلاف دارند حکایت می‌کند.'),
(3236, 27, 77, 'و به راستی که آن، رهنمود و رحمتی برای مؤمنان است.'),
(3237, 27, 78, 'در حقیقت، پروردگار تو طبق حکم خود میان آنان داوری می‌کند، و اوست شکست‌ناپذیر دانا.'),
(3238, 27, 79, 'پس بر خدا توکل کن که تو واقعاً بر حق آشکاری.'),
(3239, 27, 80, 'البته تو مردگان را شنوا نمی‌گردانی، و این ندا را به کران -چون پشت بگردانند- نمی‌توانی بشنوانی.'),
(3240, 27, 81, 'و راهبر کوران [و بازگرداننده‌] از گمراهی‌شان نیستی. تو جز کسانی را که به نشانه‌های ما ایمان آورده‌اند و مسلمانند، نمی‌توانی بشنوانی.'),
(3241, 27, 82, 'و چون قول [عذاب‌] بر ایشان واجب گردد، جنبنده‌ای را از زمین برای آنان بیرون می‌آوریم که با ایشان سخن گوید که: مردم [چنانکه باید] به نشانه‌های ما یقین نداشتند.'),
(3242, 27, 83, 'و آن روز که از هر امتی، گروهی از کسانی را که آیات ما را تکذیب کرده‌اند محشور می‌گردانیم، پس آنان نگاه داشته می‌شوند تا همه به هم بپیوندند.'),
(3243, 27, 84, 'تا چون [همه کافران‌] بیایند، [خدا] می‌فرماید: «آیا نشانه‌های مرا به دروغ گرفتید و حال آنکه از نظر علم، بدانها احاطه نداشتید؟ آیا [در طول حیات‌] چه می‌کردید؟»'),
(3244, 27, 85, 'و به [کیفر] آنکه ستم کردند، حکم [عذاب‌] بر آنان واجب گردد، در نتیجه ایشان دَم برنیارند.'),
(3245, 27, 86, 'آیا ندیده‌اند که ما شب را قرار داده‌ایم تا در آن بیاسایند، و روز را روشنی‌بخش [گردانیدیم‌]؟ قطعاً در این [امر] برای مردمی که ایمان می‌آورند مایه‌های عبرت است.'),
(3246, 27, 87, 'و روزی که در صور دمیده شود، پس هر که در آسمانها و هر که در زمین است به هراس افتد، مگر آن کس که خدا بخواهد. و جملگی با زبونی رو به سوی او آورند.'),
(3247, 27, 88, 'و کوهها را می‌بینی [و] می‌پنداری که آنها بی‌حرکتند و حال آنکه آنها ابرآسا در حرکتند. [این‌] صُنعِ خدایی است که هر چیزی را در کمال استواری پدید آورده است. در حقیقت، او به آنچه انجام می‌دهید آگاه است.'),
(3248, 27, 89, 'هر کس نیکی به میان آورد، پاداشی بهتر از آن خواهد داشت، و آنان از هراس آن روز ایمنند.'),
(3249, 27, 90, 'و هر کس بدی به میان آورد، به رو در آتش [دوزخ‌] سرنگون شوند. آیا جز آنچه می‌کردید سزا داده می‌شوید؟'),
(3250, 27, 91, 'من مأمورم که تنها پروردگار این شهر را که آن را مقدس شمرده و هر چیزی از آنِ اوست پرستش کنم، و مأمورم که از مسلمانان باشم،'),
(3251, 27, 92, 'و اینکه قرآن را بخوانم. پس هر که راه یابد تنها به سود خود راه یافته است؛ و هر که گمراه شود بگو: «من فقط از هشداردهندگانم.»'),
(3252, 27, 93, 'و بگو: «ستایش از آنِ خداست. به زودی آیاتش را به شما نشان خواهد داد و آن را خواهید شناخت.» و پروردگار تو از آنچه می‌کنید غافل نیست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا، سین، میم.'),
(3254, 28, 2, 'این است آیات کتاب روشنگر.'),
(3255, 28, 3, '[بخشی‌] از گزارش [حال‌] موسی و فرعون را برای [آگاهی‌] مردمی که ایمان می‌آورند، به درستی بر تو می‌خوانیم:'),
(3256, 28, 4, 'فرعون در سرزمین [مصر] سر برافراشت، و مردم آن را طبقه طبقه ساخت؛ طبقه‌ای از آنان را زبون می‌داشت: پسرانشان را سر می‌برید، و زنانشان را [برای بهره‌کشی‌] زنده بر جای می‌گذاشت، که وی از فسادکاران بود.'),
(3257, 28, 5, 'و خواستیم بر کسانی که در آن سرزمین فرو دست شده بودند منّت نهیم و آنان را پیشوایان [مردم‌] گردانیم، و ایشان را وارث [زمین‌] کنیم،'),
(3258, 28, 6, 'و در زمین قدرتشان دهیم و [از طرفی‌] به فرعون و هامان و لشکریانشان آنچه را که از جانب آنان بیمناک بودند، بنمایانیم.'),
(3259, 28, 7, 'و به مادر موسی وحی کردیم که: «او را شیر ده، و چون بر او بیمناک شدی او را در نیل بینداز، و مترس و اندوه مدار که ما او را به تو بازمی‌گردانیم و از [زمره‌] پیمبرانش قرار می‌دهیم.»'),
(3260, 28, 8, 'پس خاندان فرعون، او را [از آب‌] برگرفتند تا سرانجام دشمنِ [جانِ‌] آنان و مایه اندوهشان باشد. آری، فرعون و هامان و لشکریان آنها خطاکار بودند.'),
(3261, 28, 9, 'و همسر فرعون گفت: «[این کودک‌] نور چشم من و تو خواهد بود. او را مکشید. شاید برای ما سودمند باشد یا او را به فرزندی بگیریم، ولی آنها خبر نداشتند.'),
(3262, 28, 10, 'و دل مادر موسی [از هر چیز، جز از فکر فرزند] تهی گشت. اگر قلبش را استوار نساخته بودیم تا از ایمان‌آورندگان باشد، چیزی نمانده بود که آن [راز] را افشا کند.'),
(3263, 28, 11, 'و به خواهر [موسی‌] گفت: «از پی او برو.» پس او را از دور دید، در حالی که آنان متوجه نبودند.'),
(3264, 28, 12, 'و از پیش، شیر دایگان را بر او حرام گردانیده بودیم. پس [خواهرش آمد و] گفت: «آیا شما را بر خانواده‌ای راهنمایی کنم که برای شما از وی سرپرستی کنند و خیرخواه او باشند؟»'),
(3265, 28, 13, 'پس او را به مادرش بازگردانیدیم تا چشمش [بدو] روشن شود و غم نخورد و بداند که وعده خدا درست است، ولی بیشترشان نمی‌دانند.'),
(3266, 28, 14, 'و چون به رشد و کمال خویش رسید، به او حکمت و دانش عطا کردیم، و نیکوکاران را چنین پاداش می‌دهیم.'),
(3267, 28, 15, 'و داخل شهر شد بی‌آنکه مردمش متوجه باشند. پس دو مرد را با هم در زد و خورد یافت: یکی، از پیروان او و دیگری از دشمنانش [بود]. آن کس که از پیروانش بود، بر ضد کسی که دشمن وی بود، از او یاری خواست. پس موسی مشتی بدو زد و او را کشت. گفت: «این کار شیطان است، چرا که او دشمنی گمراه‌کننده [و] آشکار است.»'),
(3268, 28, 16, 'گفت: «پروردگارا، من بر خویشتن ستم کردم، مرا ببخش.» پس خدا از او درگذشت که وی آمرزنده مهربان است.'),
(3269, 28, 17, '[موسی‌] گفت: «پروردگارا به پاس نعمتی که بر من ارزانی داشتی هرگز پشتیبان مجرمان نخواهم بود.»'),
(3270, 28, 18, 'صبحگاهان در شهر، بیمناک و در انتظار [حادثه‌ای‌] بود. ناگاه همان کسی که دیروز از وی یاری خواسته بود [باز] با فریاد از او یاری خواست. موسی به او گفت: «به راستی که تو آشکارا گمراهی.»'),
(3271, 28, 19, 'و چون خواست به سوی آنکه دشمن هر دوشان بود حمله آورد، گفت: «ای موسی، آیا می‌خواهی مرا بکشی چنانکه دیروز شخصی را کشتی؟ تو می‌خواهی در این سرزمین فقط زورگو باشی، و نمی‌خواهی از اصلاحگران باشی.»'),
(3272, 28, 20, 'و از دورافتاده‌ترین [نقطه‌] شهر، مردی دوان دوان آمد [و] گفت: «ای موسی، سران قوم در باره تو مشورت می‌کنند تا تو را بکشند. پس [از شهر] خارج شو. من جداً از خیرخواهان توام.»'),
(3273, 28, 21, 'موسی ترسان و نگران از آنجا بیرون رفت [در حالی که می‌]گفت: «پروردگارا، مرا از گروه ستمکاران نجات بخش.»'),
(3274, 28, 22, 'و چون به سوی [شهر] مدین رو نهاد [با خود] گفت: «امید است پروردگارم مرا به راه راست هدایت کند.»'),
(3275, 28, 23, 'و چون به آب مدین رسید، گروهی از مردم را بر آن یافت که [دامهای خود را ] آب می‌دادند، و پشت سرشان دو زن را یافت که [گوسفندان خود را] دور می‌کردند. [موسی‌] گفت: «منظورتان [از این کار ]چیست؟» گفتند: « [ما به گوسفندان خود] آب نمی‌دهیم تا شبانان [همگی گوسفندانشان را] برگردانند، و پدر ما پیری سالخورده است.»'),
(3276, 28, 24, 'پس برای آن دو، [گوسفندان را] آب داد، آنگاه به سوی سایه برگشت و گفت: «پروردگارا، من به هر خیری که سویم بفرستی سخت نیازمندم.»'),
(3277, 28, 25, 'پس یکی از آن دو زن -در حالی که به آزرم گام بر می‌داشت- نزد وی آمد [و] گفت: «پدرم تو را می‌طلبد تا تو را به پاداش آب‌دادن [گوسفندان‌] برای ما، مزد دهد.» و چون [موسی‌] نزد او آمد و سرگذشت [خود] را بر او حکایت کرد، [وی‌] گفت: «مترس که از گروه ستمگران نجات یافتی.»'),
(3278, 28, 26, 'یکی از آن دو [دختر] گفت: «ای پدر، او را استخدام کن، چرا که بهترین کسی است که استخدام می‌کنی: هم نیرومند [و هم‌] در خور اعتماد است.»'),
(3279, 28, 27, '[شعیب‌] گفت: «من می‌خواهم یکی از این دو دختر خود را [که مشاهده می‌کنی‌] به نکاح تو در آورم، به این [شرط] که هشت سال برای من کار کنی، و اگر ده سال را تمام گردانی اختیار با تو است، و نمی‌خواهم بر تو سخت گیرم، و مرا ان شاء الله از درستکاران خواهی یافت.»'),
(3280, 28, 28, '[موسی‌] گفت: «این [قرار داد] میان من و تو باشد که هر یک از دو مدت را به انجام رسانیدم، بر من تعدی [روا] نباشد، و خدا بر آنچه می‌گوییم وکیل است.»'),
(3281, 28, 29, 'و چون موسی آن مدت را به پایان رسانید و همسرش را [همراه‌] برد، آتشی را از دور در کنار طور مشاهده کرد، به خانواده خود گفت: «[اینجا] بمانید، که من آتشی از دور دیدم، شاید خبری از آن یا شعله‌ای آتش برایتان بیاورم، باشد که خود را گرم کنید.»'),
(3282, 28, 30, 'پس چون به آن [آتش‌] رسید، از جانب راست وادی، در آن جایگاه مبارک، از آن درخت ندا آمد که: «ای موسی، منم، من، خداوند، پروردگار جهانیان.»'),
(3283, 28, 31, 'و [فرمود:] «عصای خود را بیفکن.» پس چون دید آن مِثْل ماری می‌جنبد، پشت کرد و برنگشت. «ای موسی، پیش آی و مترس که تو در امانی.»'),
(3284, 28, 32, '«دست خود را به گریبانت ببر تا سپید بی‌گزند بیرون بیاید، و [برای رهایی ] از این هراس بازویت را به خویشتن بچسبان. این دو [نشانه‌] دو برهان از جانب پروردگار تو است [که باید] به سوی فرعون و سران [کشور] او [ببری‌]، زیرا آنان همواره قومی نافرمانند.»'),
(3285, 28, 33, 'گفت: «پروردگارا، من کسی از ایشان را کشته‌ام، می‌ترسم مرا بکشند.'),
(3286, 28, 34, 'و برادرم هارون از من زبان‌آورتر است، پس او را با من به دستیاری گسیل دار تا مرا تصدیق کند، زیرا می‌ترسم مرا تکذیب کنند.»'),
(3287, 28, 35, 'فرمود: «به زودی بازویت را به [وسیله‌] برادرت نیرومند خواهیم کرد و برای شما هر دو، تسلطی قرار خواهیم داد که با [وجود] آیات ما، به شما دست نخواهند یافت شما و هر که شما را پیروی کند چیره خواهید بود.»'),
(3288, 28, 36, 'پس چون موسی آیات روشن ما را برای آنان آورد، گفتند: «این جز سحری ساختگی نیست و از پدران پیشین خود چنین [چیزی‌] نشنیده‌ایم.»'),
(3289, 28, 37, 'و موسی گفت: «پروردگارم به [حال‌] کسی که از جانب او رهنمودی آورده و [نیز] کسی که فرجام [نیکویِ‌] آن سرا برای اوست، داناتر است. در حقیقت، ظالمان رستگار نمی‌شوند.»'),
(3290, 28, 38, 'و فرعون گفت: «ای بزرگان قوم، من جز خویشتن برای شما خدایی نمی‌شناسم. پس ای هامان؛ برایم بر گل آتش بیفروز و برجی [بلند] برای من بساز، شاید به [حال‌] خدای موسی اطّلاع یابم، و من جداً او را از دروغگویان می‌پندارم.»'),
(3291, 28, 39, 'و او و سپاهیانش در آن سرزمین به ناحق سرکشی کردند و پنداشتند که به سوی ما بازگردانیده نمی‌شوند.'),
(3292, 28, 40, 'تا او و سپاهیانش را فرو گرفتیم و آنان را در دریا افکندیم، بنگر که فرجام کار ستمکاران چگونه بود.'),
(3293, 28, 41, 'و آنان را پیشوایانی که به سوی آتش می‌خوانند گردانیدیم، و روز رستاخیز یاری نخواهند شد.'),
(3294, 28, 42, 'و در این دنیا لعنتی بدرقه [نام‌] آنان کردیم و روز قیامت [نیز] ایشان از [جمله‌] زشت‌رویانند.'),
(3295, 28, 43, 'و به راستی، پس از آنکه نسلهای نخستین را هلاک کردیم، به موسی کتاب دادیم که [دربردارنده‌] روشنگری‌ها و رهنمود و رحمتی برای مردم بود، امید که آنان پند گیرند.'),
(3296, 28, 44, 'و چون امر [پیامبری‌] را به موسی واگذاشتیم، تو در جانب غربی [طور] نبودی و از گواهان [نیز] نبودی.'),
(3297, 28, 45, 'لیکن ما نسلهایی پدید آوردیم و عمرشان طولانی شد. و تو در میان ساکنان [شهر] مَدْین مقیم نبودی تا آیات ما را بر ایشان بخوانی، لیکن ما بودیم که فرستنده [پیامبران‌] بودیم.'),
(3298, 28, 46, 'و آندم که [موسی را] ندا دردادیم، تو در جانب طور نبودی، ولی [این اطّلاع تو] رحمتی است از پروردگار تو، تا قومی را که هیچ هشداردهنده‌ای پیش از تو برایشان نیامده است بیم دهی، باشد که آنان پندپذیرند.'),
(3299, 28, 47, 'و اگر نبود که وقتی به [سزای‌] پیش‌فرست دستهایشان مصیبتی به ایشان برسد، بگویند: «پروردگارا، چرا فرستاده‌ای به سوی ما نفرستادی تا از احکام تو پیروی کنیم و از مؤمنان باشیم» [قطعاً در کیفر آنان شتاب می‌کردیم‌].'),
(3300, 28, 48, 'پس چون حق از جانب ما برایشان آمد، گفتند: «چرا نظیر آنچه به موسی داده شد، به او داده نشده است؟» آیا به آنچه قبلاً به موسی داده شد کفر نورزیدند؟ گفتند: «دو ساحر با هم ساخته‌اند.» و گفتند: «ما همه را منکریم.»'),
(3301, 28, 49, 'بگو: «پس اگر راست می‌گویید، کتابی از جانب خدا بیاورید که از این دو هدایت کننده‌تر باشد تا پیرویش کنم.»'),
(3302, 28, 50, 'پس اگر تو را اجابت نکردند، بدان که فقط هوسهای خود را پیروی می‌کنند؛ و کیست گمراه‌تر از آنکه بی‌راهنمایی خدا از هوسش پیروی کند؟ بی‌تردید خدا مردم ستمگر را راهنمایی نمی‌کند.'),
(3303, 28, 51, 'و به راستی، این گفتار را برای آنان پی در پی و به هم پیوسته نازل ساختیم، امید که آنان پند پذیرند.'),
(3304, 28, 52, 'کسانی که قبل از آن، کتاب [آسمانی‌] به ایشان داده‌ایم، آنان به [قرآن‌] می‌گروند.'),
(3305, 28, 53, 'و چون بر ایشان فرو خوانده می‌شود، می‌گویند: «بدان ایمان آوردیم که آن درست است [و] از طرف پروردگار ماست؛ ما پیش از آن [هم‌] از تسلیم‌شوندگان بودیم.»'),
(3306, 28, 54, 'آنانند که به [پاس‌] آنکه صبر کردند و [برای آنکه‌] بدی را با نیکی دفع می‌نمایند و از آنچه روزی‌شان داده‌ایم انفاق می‌کنند، دو بار پاداش خواهند یافت.'),
(3307, 28, 55, 'و چون لغوی بشنوند از آن روی برمی‌تابند و می‌گویند: «کردارهای ما از آنِ ما و کردارهای شما از آنِ شماست، سلام بر شما، جویای [مصاحبت‌] نادانان نیستیم.»'),
(3308, 28, 56, 'در حقیقت، تو هر که را دوست داری نمی‌توانی راهنمایی کنی، لیکن خداست که هر که را بخواهد راهنمایی می‌کند، و او به راه‌یافتگان داناتر است.'),
(3309, 28, 57, 'و گفتند: «اگر با تو از [نورِ] هدایت پیروی کنیم، از سرزمین خود ربوده خواهیم شد.» آیا آنان را در حرمی امن جای ندادیم که محصولات هر چیزی -که رزقی از جانب ماست- به سوی آن سرازیر می‌شود؟ ولی بیشترشان نمی‌دانند.'),
(3310, 28, 58, 'و چه بسیار شهرها که هلاکش کردیم، [زیرا] زندگی خوش، آنها را سرمست کرده بود. این است سراهایشان که پس از آنان -جز برای عدّه کمی- مورد سکونت قرار نگرفته، و ماییم که وارث آنان بودیم.'),
(3311, 28, 59, 'و پروردگار تو [هرگز] ویرانگر شهرها نبوده است تا [پیشتر] در مرکز آنها پیامبری برانگیزد که آیات ما را بر ایشان بخواند، و ما شهرها را -تا مردمشان ستمگر نباشند- ویران‌کننده نبوده‌ایم.'),
(3312, 28, 60, 'و هر آنچه به شما داده شده است، کالای زندگی دنیا و زیور آن است، و[لی‌] آنچه پیش خداست بهتر و پایدارتر است؛ مگر نمی‌اندیشید؟'),
(3313, 28, 61, 'آیا کسی که وعده نیکو به او داده‌ایم و او به آن خواهد رسید، مانند کسی است که از کالای زندگی دنیا بهره‌مندش گردانیده‌ایم [ولی‌] او روز قیامت از [جمله‌] احضارشدگان [در آتش‌] است؟'),
(3314, 28, 62, 'و [به یاد آور] روزی را که آنان را ندا می‌دهد و می‌فرماید: «آن شریکان من، که می‌پنداشتید کجایند؟»'),
(3315, 28, 63, 'آنان که حکم [عذاب‌] بر ایشان واجب آمده، می‌گویند: «پروردگارا، اینانند کسانی که گمراه کردیم، گمراهشان کردیم همچنانکه خود گمراه شدیم، [از آنان‌] به سوی تو بیزاری می‌جوییم، ما را نمی‌پرستیدند [بلکه پندار خود را دنبال می‌کردند].»'),
(3316, 28, 64, 'و [به آنان‌] گفته می‌شود: «شریکان خود را فرا خوانید.» [پس آنها را می‌خوانند] ولی پاسخشان نمی‌دهند و عذاب را می‌بینند [و آرزو می‌کنند که‌] ای کاش هدایت‌یافته بودند.'),
(3317, 28, 65, 'و روزی را که [خدا] آنان را ندا درمی‌دهد و می‌فرماید: «فرستادگان [ما] را چه پاسخ دادید؟»'),
(3318, 28, 66, 'پس در آن روز اخبار بر ایشان پوشیده گردد و از یکدیگر نمی‌توانند بپرسند.'),
(3319, 28, 67, 'و امّا کسی که توبه کند و ایمان آورد و به کار شایسته پردازد، امید که از رستگاران باشد.'),
(3320, 28, 68, 'و پروردگار تو هر چه را بخواهد می‌آفریند و برمی‌گزیند، و آنان اختیاری ندارند. منزه است خدا، و از آنچه [با او] شریک می‌گردانند برتر است.'),
(3321, 28, 69, 'و آنچه را سینه‌هایشان پوشیده یا آشکار می‌دارد، پروردگارت می‌داند.'),
(3322, 28, 70, 'و اوست خدا[یی که‌] جز او معبودی نیست. در این [سرای‌] نخستین و در آخرت، ستایش از آنِ اوست، و فرمان، او راست و به سوی او بازگردانیده می‌شوید.'),
(3323, 28, 71, 'بگو: «هان، چه می‌پندارید اگر خدا تا روز رستاخیز شب را بر شما جاوید بدارد؛ جز خداوند کدامین معبود برای شما روشنی می‌آورد؟ آیا نمی‌شنوید؟»'),
(3324, 28, 72, 'بگو: «هان، چه می‌پندارید اگر خدا تا روز قیامت روز را بر شما جاوید بدارد؛ جز خداوند کدامین معبود برای شما شبی می‌آورد که در آن آرام گیرید، آیا نمی‌بینید؟»'),
(3325, 28, 73, 'و از رحمتش برایتان شب و روز را قرار داد تا در این [یک‌] بیارامید و [در آن یک‌] از فزون‌بخشی او [روزی خود] بجویید، باشد که سپاس بدارید.'),
(3326, 28, 74, 'و [یاد کن‌] روزی را که ندایشان می‌کند و می‌فرماید: «آن شریکان که می‌پنداشتید کجایند؟»'),
(3327, 28, 75, 'و از میان هر امتی گواهی بیرون می‌کشیم و می‌گوییم: «برهان خود را بیاورید.» پس بدانند که حقّ از آنِ خداست، و آنچه برمی‌بافتند از دستشان می‌رود.'),
(3328, 28, 76, 'قارون از قوم موسی بود و بر آنان ستم کرد، و از گنجینه‌ها آن قدر به او داده بودیم که کلیدهای آنها بر گروه نیرومندی سنگین می‌آمد، آنگاه که قوم وی بدو گفتند: «شادی مکن که خدا شادی‌کنندگان را دوست نمی‌دارد.'),
(3329, 28, 77, 'و با آنچه خدایت داده سرای آخرت را بجوی و سهم خود را از دنیا فراموش مکن، و همچنانکه خدا به تو نیکی کرده نیکی کن و در زمین فساد مجوی که خدا فسادگران را دوست نمی‌دارد.'),
(3330, 28, 78, '[قارون‌] گفت: «من اینها را در نتیجه دانش خود یافته‌ام.» آیا وی ندانست که خدا نسلهایی را پیش از او نابود کرد که از او نیرومندتر و مال‌اندوزتر بودند؟ و[لی این گونه‌] مجرمان را [نیازی‌] به پرسیده شدن از گناهانشان نیست.'),
(3331, 28, 79, 'پس [قارون‌] با کوکبه خود بر قومش نمایان شد؛ کسانی که خواستار زندگی دنیا بودند گفتند: «ای کاش مثل آنچه به قارون داده شده به ما [هم‌] داده می‌شد، واقعاً او بهره بزرگی [از ثروت‌] دارد.»'),
(3332, 28, 80, 'و کسانی که دانش [واقعی‌] یافته بودند، گفتند: «وای بر شما! برای کسی که گرویده و کار شایسته کرده پاداش خدا بهتر است، و جز شکیبایان آن را نیابند.»'),
(3333, 28, 81, 'آنگاه [قارون‌] را با خانه‌اش در زمین فرو بردیم، و گروهی نداشت که در برابر [عذاب‌] خدا او را یاری کنند و [خود نیز] نتوانست از خود دفاع کند.'),
(3334, 28, 82, 'و همان کسانی که دیروز آرزو داشتند به جای او باشند، صبح می‌گفتند: «وای، مثل اینکه خدا روزی را برای هر کس از بندگانش که بخواهد گشاده یا تنگ می‌گرداند، و اگر خدا بر ما منّت ننهاده بود، ما را [هم‌] به زمین فرو برده بود؛ وای، گویی که کافران رستگار نمی‌گردند.»'),
(3335, 28, 83, 'آن سرای آخرت را برای کسانی قرار می‌دهیم که در زمین خواستار برتری و فساد نیستند، و فرجام [خوش‌] از آنِ پرهیزگاران است.'),
(3336, 28, 84, 'هر کس نیکی به میان آورد، برای او [پاداشی‌] بهتر از آن خواهد بود، و هر کس بدی به میان آورد، کسانی که کارهای بد کرده‌اند جز سزای آنچه کرده‌اند نخواهند یافت.'),
(3337, 28, 85, 'در حقیقت، همان کسی که این قرآن را بر تو فرض کرد، یقیناً تو را به سوی وعده‌گاه بازمی‌گرداند. بگو: «پروردگارم بهتر می‌داند چه کس هدایت آورده و چه کس در گمراهی آشکاری است؟»'),
(3338, 28, 86, 'و تو امیدوار نبودی که بر تو کتاب القا شود، بلکه این رحمتی از پروردگار تو بود. پس تو هرگز پشتیبان کافران مباش.'),
(3339, 28, 87, 'و البته نباید تو را از آیات خدا -بعد از آنکه بر تو نازل شده است- باز دارند، و به سوی پروردگارت دعوت کن، و زنهار از مشرکان مباش.'),
(3340, 28, 88, 'و با خدا معبودی دیگر مخوان. خدایی جز او نیست. جز ذات او همه چیز نابودشونده است. فرمان از آنِ اوست. و به سوی او بازگردانیده می‌شوید.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف، لام، میم.'),
(3342, 29, 2, 'آیا مردم پنداشتند که تا گفتند ایمان آوردیم، رها می‌شوند و مورد آزمایش قرار نمی‌گیرند؟'),
(3343, 29, 3, 'و به یقین، کسانی را که پیش از اینان بودند آزمودیم، تا خدا آنان را که راست گفته‌اند معلوم دارد و دروغگویان را [نیز] معلوم دارد.'),
(3344, 29, 4, 'آیا کسانی که کارهای بد می‌کنند، می‌پندارند که بر ما پیشی خواهند جست؟ چه بد داوری می‌کنند.'),
(3345, 29, 5, 'کسی که به دیدار خدا امید دارد [بداند که‌] اَجَل [او از سوی‌] خدا آمدنی است، و اوست شنوای دانا.'),
(3346, 29, 6, 'و هر که بکوشد، تنها برای خود می‌کوشد، زیرا خدا از جهانیان سخت بی‌نیاز است.'),
(3347, 29, 7, 'و کسانی که ایمان آورده، و کارهای شایسته کرده‌اند، قطعاً گناهانشان را از آنان می زداییم، و بهتر از آنچه می‌کردند پاداششان می‌دهیم.'),
(3348, 29, 8, 'و به انسان سفارش کردیم که به پدر و مادر خود نیکی کند، و[لی‌] اگر آنها با تو درکوشند تا چیزی را که بدان علم نداری با من شریک گردانی، از ایشان اطاعت مکن. سرانجامتان به سوی من است، و شما را از [حقیقت‌] آنچه انجام می‌دادید باخبر خواهم کرد.'),
(3349, 29, 9, 'و کسانی که گرویده و کارهای شایسته کرده‌اند، البتّه آنان را در زمره شایستگان درمی‌آوریم.'),
(3350, 29, 10, 'و از میان مردم کسانی‌اند که می‌گویند: «به خدا ایمان آورده‌ایم» و چون در [راه‌] خدا آزار کشند، آزمایش مردم را مانند عذاب خدا قرار می‌دهند؛ و اگر از جانب پروردگارت پیروزی رسد حتماً خواهند گفت: «ما با شما بودیم.» آیا خدا به آنچه در دلهای جهانیان است داناتر نیست؟'),
(3351, 29, 11, 'و قطعاً خدا کسانی را که ایمان آورده‌اند می‌شناسد، و یقیناً منافقان را [نیز] می‌شناسد.'),
(3352, 29, 12, 'و کسانی که کافر شده‌اند، به کسانی که ایمان آورده‌اند می‌گویند: «راه ما را پیروی کنید و گناهانتان به گردن ما.» و[لی‌] چیزی از گناهانشان را به گردن نخواهند گرفت؛ قطعاً آنان دروغگویانند.'),
(3353, 29, 13, 'و قطعاً بارهای گران خودشان و بارهای گران [دیگر] را با بارهای گران خود برخواهند گرفت، و مسلّماً روز قیامت از آنچه به دروغ برمی‌بستند پرسیده خواهند شد.'),
(3354, 29, 14, 'و به راستی، نوح را به سوی قومش فرستادیم، پس در میان آنان نهصد و پنجاه سال درنگ کرد، تا طوفان آنها را در حالی که ستمکار بودند فرا گرفت.'),
(3355, 29, 15, 'و او را با کشتی‌نشینان برهانیدیم و آن [سفینه‌] را برای جهانیان عبرتی گردانیدیم.'),
(3356, 29, 16, 'و [یاد کن‌] ابراهیم را چون به قوم خویش گفت: «خدا را بپرستید و از او پروا بدارید؛ اگر بدانید این [کار] برای شما بهتر است.»'),
(3357, 29, 17, 'واقعاً آنچه را که شما سوای خدا می‌پرستید جز بتانی [بیش‌] نیستند و دروغی برمی‌سازید. در حقیقت، کسانی را که جز خدا می‌پرستید اختیار روزی شما را در دست ندارند. پس روزی را پیش خدا بجویید و او را بپرستید و وی را سپاس گویید، که به سوی او بازگردانیده می‌شوید.'),
(3358, 29, 18, 'و اگر تکذیب کنید، قطعاً امّتهای پیش از شما [هم‌] تکذیب کردند، و بر پیامبر [خدا] جز ابلاغ آشکار [وظیفه‌ای‌] نیست.'),
(3359, 29, 19, 'آیا ندیده‌اند که خدا چگونه آفرینش را آغاز می‌کند سپس آن را باز می‌گرداند؟ در حقیقت، این [کار] بر خدا آسان است.'),
(3360, 29, 20, 'بگو: «در زمین بگردید و بنگرید چگونه آفرینش را آغاز کرده است سپس [باز ] خداست که نشأه آخرت را پدید می‌آورد؛ خداست که بر هر چیزی تواناست.»'),
(3361, 29, 21, 'هر که را بخواهد عذاب و هر که را بخواهد رحمت می‌کند و به سوی او بازگردانیده می شوید.'),
(3362, 29, 22, 'و شما نه در زمین و نه در آسمان درمانده‌کننده [او] نیستید، و جز خدا برای شما یار و یاوری نیست.'),
(3363, 29, 23, 'و کسانی که آیات خدا و لقای او را منکر شدند، آنانند که از رحمت من نومیدند و ایشان را عذابی پر درد خواهد بود.'),
(3364, 29, 24, 'و پاسخ قومش جز این نبود که گفتند: «بکشیدش یا بسوزانیدش.» ولی خدا او را از آتش نجات بخشید. آری، در این [نجات بخشی خدا] برای مردمی که ایمان دارند قطعاً دلایلی است.'),
(3365, 29, 25, 'و [ابراهیم‌] گفت: «جز خدا، فقط بتهایی را اختیار کرده‌اید که آن هم برای دوستی میان شما در زندگی دنیاست، آنگاه روز قیامت بعضی از شما بعضی دیگر را انکار و برخی از شما برخی دیگر را لعنت می‌کنند و جایتان در آتش است و برای شما یاورانی نخواهد بود.»'),
(3366, 29, 26, 'پس لوط به او ایمان آورد و [ابراهیم‌] گفت: «من به سوی پروردگار خود روی می آورم، که اوست ارجمند حکیم.»'),
(3367, 29, 27, 'و اسحاق و یعقوب را به او عطا کردیم و در میان فرزندانش پیامبری و کتاب قرار دادیم و در دنیا پاداشش را به او بخشیدیم و قطعاً او در آخرت [نیز] از شایستگان خواهد بود.'),
(3368, 29, 28, 'و [یاد کن‌] لوط را هنگامی که به قوم خود گفت: «شما به کاری زشت می‌پردازید که هیچ یک از مردم زمین در آن [کار] بر شما پیشی نگرفته است.'),
(3369, 29, 29, 'آیا شما با مردها درمی‌آمیزید و راه [توالد و تناسل‌] را قطع می‌کنید و در محافل [اُنس‌] خود پلیدکاری می‌کنید؟» و[لی‌] پاسخ قومش جز این نبود که گفتند: «اگر راست می گویی عذاب خدا را برای ما بیاور.»'),
(3370, 29, 30, '[لوط] گفت: «پروردگارا، مرا بر قوم فسادکار غالب گردان.»'),
(3371, 29, 31, 'و چون فرستادگان ما برای ابراهیم مژده آوردند، گفتند: «ما اهل این شهر را هلاک خواهیم کرد، زیرا مردمش ستمکار بوده‌اند.»'),
(3372, 29, 32, 'گفت: «لوط [نیز] در آنجاست.» گفتند: «ما بهتر می‌دانیم چه کسانی در آنجا هستند؛ او و کسانش را -جز زنش که از باقی ماندگان [در خاکستر آتش‌] است- حتماً نجات خواهیم داد.»'),
(3373, 29, 33, 'و هنگامی که فرستادگان ما به سوی لوط آمدند، به علت [حضور] ایشان ناراحت شد و دستش از [حمایت‌] آنها کوتاه گردید. گفتند: «مترس و غم مدار که ما تو و خانواده‌ات را -جز زنت که از باقی‌ماندگان [در خاکستر آتش‌] است- حتماً می رهانیم.'),
(3374, 29, 34, 'ما بر مردم این شهر به [سزای‌] فسقی که می‌کردند، عذابی از آسمان فرو خواهیم فرستاد.'),
(3375, 29, 35, 'و از آن [شهر سوخته‌] برای مردمی که می‌اندیشند نشانه‌ای روشن باقی گذاشتیم.'),
(3376, 29, 36, 'و به سوی [مردم‌] مَدْیَن، برادرشان شعیب را [فرستادیم‌]. گفت: «ای قوم من، خدا را بپرستید و به روز بازپسین امید داشته باشید و در زمین سر به فساد برمدارید.»'),
(3377, 29, 37, 'پس او را دروغگو خواندند و زلزله آنان را فرو گرفت و بامدادان در خانه‌هایشان از پا درآمدند.'),
(3378, 29, 38, 'و عاد و ثمود را [نیز هلاک نمودیم‌]. قطعاً [فرجام آنان‌] از سراهایشان بر شما آشکار گردیده است، و شیطان کارهایشان را در نظرشان بیاراست و از راه بازشان داشت با آنکه [در کار دنیا] بینا بودند.'),
(3379, 29, 39, 'و قارون و فرعون و هامان را [هم هلاک کردیم‌]. و به راستی موسی برای آنان دلایل آشکار آورد، و[لی آنها] در آن سرزمین سرکشی نمودند و [با این همه بر ما] پیشی نجستند.'),
(3380, 29, 40, 'و هر یک [از ایشان‌] را به گناهش گرفتار [عذاب‌] کردیم: از آنان کسانی بودند که بر [سر] ایشان بادی همراه با شن فرو فرستادیم؛ و از آنان کسانی بودند که فریاد [مرگبار] آنها را فرو گرفت؛ و برخی از آنان را در زمین فرو بردیم؛ و بعضی را غرق کردیم؛ و [این‌] خدا نبود که بر ایشان ستم کرد بلکه خودشان بر خود ستم می‌کردند.'),
(3381, 29, 41, 'داستان کسانی که غیر از خدا دوستانی اختیار کرده‌اند، همچون عنکبوت است که [با آب دهان خود] خانه‌ای برای خویش ساخته، و در حقیقت -اگر می‌دانستند- سست‌ترین خانه‌ها همان خانه عنکبوت است.'),
(3382, 29, 42, 'خدا می‌داند هر آنچه را که جز او می‌خوانند، و هم اوست شکست‌ناپذیر سنجیده‌کار.'),
(3383, 29, 43, 'و این مَثَلها را برای مردم می‌زنیم و[لی‌] جز دانشوران آنها را درنیابند.'),
(3384, 29, 44, 'خداوند آسمانها و زمین را به حق آفرید. قطعاً در این [آفرینش‌] برای مؤمنان عبرتی است.'),
(3385, 29, 45, 'آنچه از کتاب به سوی تو وحی شده است بخوان، و نماز را برپا دار، که نماز از کار زشت و ناپسند باز می‌دارد، و قطعاً یاد خدا بالاتر است، و خدا می‌داند چه می‌کنید.'),
(3386, 29, 46, 'و با اهل کتاب، جز به [شیوه‌ای‌] که بهتر است، مجادله مکنید -مگر [با] کسانی از آنان که ستم کرده‌اند- و بگویید: «به آنچه به سوی ما نازل شده و [آنچه‌] به سوی شما نازل گردیده، ایمان آوردیم؛ و خدای ما و خدای شما یکی است و ما تسلیم اوییم.»'),
(3387, 29, 47, 'و همچنین ما قرآن را بر تو نازل کردیم. پس آنان که بدیشان کتاب داده‌ایم بدان ایمان می‌آورند، و از میان اینان کسانی‌اند که به آن می‌گروند، و جز کافران [کسی‌] آیات ما را انکار نمی‌کند.'),
(3388, 29, 48, 'و تو هیچ کتابی را پیش از این نمی‌خواندی و با دست [راست‌] خود [کتابی‌] نمی‌نوشتی، و گر نه باطل‌اندیشان قطعاً به شک می‌افتادند.'),
(3389, 29, 49, 'بلکه [قرآن‌] آیاتی روشن در سینه‌های کسانی است که علم [الهی‌] یافته‌اند، و جز ستمگران منکر آیات ما نمی‌شوند.'),
(3390, 29, 50, 'و گفتند: «چرا بر او از جانب پروردگارش نشانه‌هایی [معجزه‌آسا] نازل نشده است؟» بگو: «آن نشانه‌ها پیش خداست، و من تنها هشداردهنده‌ای آشکارم.»'),
(3391, 29, 51, 'آیا برای ایشان بس نیست که این کتاب را که بر آنان خوانده می‌شود بر تو فرو فرستادیم؟ در حقیقت، در این [کار] برای مردمی که ایمان دارند، رحمت و یادآوری است.'),
(3392, 29, 52, 'بگو: «کافی است خدا میان من و شما شاهد باشد. آنچه را که در آسمانها و زمین است می‌داند، و آنان که به باطل گرویده و خدا را انکار کرده‌اند همان زیانکارانند.»'),
(3393, 29, 53, 'و از تو به شتاب درخواست عذاب [الهی را] دارند، و اگر سرآمدی معین نبود، قطعاً عذاب به آنان می‌رسید و بی‌آنکه خبردار شوند غافلگیرشان می‌کرد.'),
(3394, 29, 54, 'و شتابزده از تو عذاب می‌خواهند، و حال آنکه جهنم قطعاً بر کافران احاطه دارد.'),
(3395, 29, 55, 'آن روز که عذاب از بالای [سر] آنها و از زیر پاهایشان آنها را فرو گیرد، و [خدا] می‌فرماید: « [نتیجه‌] آنچه را می‌کردید بچشید.»'),
(3396, 29, 56, '«ای بندگان من که ایمان آورده‌اید، زمین من فراخ است؛ تنها مرا بپرستید.»'),
(3397, 29, 57, 'هر نفسی چشنده مرگ است، آنگاه به سوی ما بازگردانیده خواهید شد.'),
(3398, 29, 58, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، قطعاً آنان را در غرفه‌هایی از بهشت جای می‌دهیم که از زیر آنها جویها روان است، جاودان در آنجا خواهند بود؛ چه نیکوست پاداش عمل‌کنندگان!'),
(3399, 29, 59, 'همان کسانی که شکیبایی ورزیده و بر پروردگارشان توکل نموده‌اند.'),
(3400, 29, 60, 'و چه بسیار جاندارانی که نمی‌توانند متحمّل روزی خود شوند. خداست که آنها و شما را روزی می‌دهد، و اوست شنوای دانا.'),
(3401, 29, 61, 'و اگر از ایشان بپرسی: «چه کسی آسمانها و زمین را آفریده و خورشید و ماه را [چنین‌] رام کرده است؟» حتماً خواهند گفت: «الله»؛ پس چگونه [از حق‌] بازگردانیده می‌شوند؟'),
(3402, 29, 62, 'خدا بر هر کس از بندگانش که بخواهد روزی را گشاده می‌گرداند و [یا] بر او تنگ می‌سازد، زیرا خدا به هر چیزی داناست.'),
(3403, 29, 63, 'و اگر از آنان بپرسی: «چه کسی از آسمان، آبی فرو فرستاده و زمین را پس از مرگش به وسیله آن زنده گردانیده است؟» حتماً خواهند گفت: «الله.» بگو: «ستایش از آنِ خداست با این همه، بیشترشان نمی‌اندیشند.'),
(3404, 29, 64, 'این زندگی دنیا جز سرگرمی و بازیچه نیست، و زندگی حقیقی همانا [در] سرای آخرت است؛ ای کاش می‌دانستند.'),
(3405, 29, 65, 'و هنگامی که بر کشتی سوار می‌شوند، خدا را پاکدلانه می‌خوانند، و[لی‌] چون به سوی خشکی رساند و نجاتشان داد، بناگاه شرک می‌ورزند.'),
(3406, 29, 66, 'بگذار تا به آنچه بِدیشان داده‌ایم انکار آورند و بگذار تا برخوردار شوند، زودا که بدانند.'),
(3407, 29, 67, 'آیا ندیده‌اند که ما [برای آنان‌] حرمی امن قرار دادیم و حال آنکه مردم از حوالی آنان ربوده می‌شوند؟ آیا به باطل ایمان می‌آورند و به نعمت خدا کفر می‌ورزند؟'),
(3408, 29, 68, 'و کیست ستمکارتر از آن کس که بر خدا دروغ بندد یا چون حق به سوی او آید آن را تکذیب کند؟ آیا جای کافران [در] جهنم نیست؟'),
(3409, 29, 69, 'و کسانی که در راه ما کوشیده‌اند، به یقین راه‌های خود را بر آنان می‌نماییم و در حقیقت، خدا با نیکوکاران است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف، لام، میم.'),
(3411, 30, 2, 'رومیان شکست خوردند،'),
(3412, 30, 3, 'در نزدیکترین سرزمین، و[لی‌] بعد از شکستشان، در ظرف چند سالی، به زودی پیروز خواهند گردید.'),
(3413, 30, 4, '[فرجام‌] کار در گذشته و آینده از آن خداست، و در آن روز است که مؤمنان از یاری خدا شاد می‌گردند.'),
(3414, 30, 5, 'هر که را بخواهد یاری می‌کند، و اوست شکست‌ناپذیر مهربان.'),
(3415, 30, 6, 'وعده خداست. خدا وعده‌اش را خلاف نمی‌کند، ولی بیشتر مردم نمی‌دانند.'),
(3416, 30, 7, 'از زندگی دنیا، ظاهری را می‌شناسند، و حال آنکه از آخرت غافلند.'),
(3417, 30, 8, 'آیا در خودشان به تفکر نپرداخته‌اند؟ خداوند آسمانها و زمین و آنچه را که میان آن دو است، جز به حق و تا هنگامی معین، نیافریده است، و [با این همه‌] بسیاری از مردم لقای پروردگارشان را سخت منکرند.'),
(3418, 30, 9, 'آیا در زمین نگردیده‌اند تا ببینند فرجام کسانی که پیش از آنان بودند، چگونه بوده است؟ آنها بس نیرومندتر از ایشان بودند، و زمین را زیر و رو کردند و بیش از آنچه آنها آبادش کردند آن را آباد ساختند، و پیامبرانشان دلایل آشکار برایشان آوردند. بنابراین خدا بر آن نبود که بر ایشان ستم کند، لیکن خودشان بر خود ستم می‌کردند.'),
(3419, 30, 10, 'آنگاه فرجام کسانی که بدی کردند [بسی‌] بدتر بود، [چرا] که آیات خدا را تکذیب کردند و آنها را به ریشخند می‌گرفتند.'),
(3420, 30, 11, 'خداست که آفرینش را آغاز و سپس آن را تجدید می‌کند، آنگاه به سوی او بازگردانیده می‌شوید.'),
(3421, 30, 12, 'و روزی که قیامت برپا شود مجرمان نومید می‌گردند.'),
(3422, 30, 13, 'و برای آنان از شریکانشان شفیعانی نیست، و خود منکر شریکان خود می‌شوند.'),
(3423, 30, 14, 'و روزی که رستاخیز برپا گردد، آن روز [مردم‌] پراکنده می‌شوند.'),
(3424, 30, 15, 'اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در گلستانی، شادمان می‌گردند.'),
(3425, 30, 16, 'و اما کسانی که کافر شده و آیات ما و دیدار آخرت را به دروغ گرفته‌اند، پس آنان در عذاب حاضر آیند.'),
(3426, 30, 17, 'پس خدا را تسبیح گویید آنگاه که به عصر درمی‌آیید و آنگاه که به بامداد درمی‌شوید.'),
(3427, 30, 18, 'و ستایش از آنِ اوست در آسمانها و زمین و شامگاهان و وقتی که به نیمروز می‌رسید.'),
(3428, 30, 19, 'زنده را از مرده بیرون می‌آورد، و مرده را از زنده بیرون می‌آورد، و زمین را بعد از مرگش زنده می‌سازد؛ و بدین گونه [از گورها] بیرون آورده می‌شوید.'),
(3429, 30, 20, 'و از نشانه‌های او این است که شما را از خاک آفرید؛ پس بناگاه شما [به صورت‌] بشری هر سو پراکنده شدید.'),
(3430, 30, 21, 'و از نشانه‌های او اینکه از [نوع‌] خودتان همسرانی برای شما آفرید تا بدانها آرام گیرید، و میانتان دوستی و رحمت نهاد. آری، در این [نعمت‌] برای مردمی که می‌اندیشند قطعاً نشانه‌هایی است.'),
(3431, 30, 22, 'و از نشانه‌های [قدرت‌] او آفرینش آسمانها و زمین و اختلاف زبانهای شما و رنگهای شماست. قطعاً در این [امر نیز] برای دانشوران نشانه‌هایی است.'),
(3432, 30, 23, 'و از نشانه‌های [حکمت‌] او خواب شما در شب و [نیم‌] روز و جستجوی شما [روزی خود را] از فزون‌بخشی اوست. در این [معنی نیز] برای مردمی که می‌شنوند، قطعاً نشانه‌هایی است.'),
(3433, 30, 24, 'و از نشانه‌های او [اینکه‌] برق را برای شما بیم‌آور و امیدبخش می‌نمایاند، و از آسمان به تدریج آبی فرو می‌فرستد، که به وسیله آن، زمین را پس از مرگش زنده می‌گرداند. در این [امر هم‌] برای مردمی که تعقل می‌کنند، قطعاً نشانه‌هایی است.'),
(3434, 30, 25, 'و از نشانه‌های او این است که آسمان و زمین به فرمانش برپایند؛ پس چون شما را با یک بار خواندن فرا خوانَد، بناگاه [از گورها] خارج می‌شوید.'),
(3435, 30, 26, 'و هر که در آسمانها و زمین است از آنِ اوست؛ همه او را گردن نهاده‌اند.'),
(3436, 30, 27, 'و اوست آن کس که آفرینش را آغاز می‌کند و باز آن را تجدید می‌نماید؛ و این [کار] بر او آسانتر است. و در آسمانها و زمین نمونه والا[ی هر صفت برتر] از آن اوست، و اوست شکست‌ناپذیر سنجیده‌کار.'),
(3437, 30, 28, '[خداوند] برای شما از خودتان مَثَلی زده است: آیا در آنچه به شما روزی داده‌ایم شریکانی از بردگانتان دارید که در آن [مال با هم‌] مساوی باشید و همان طور که شما از یکدیگر بیم دارید از آنها بیم داشته باشید؟ این گونه، آیات خود را برای مردمی که می‌اندیشند، به تفصیل بیان می‌کنیم.'),
(3438, 30, 29, 'نه، [این چنین نیست‌] بلکه کسانی که ستم کرده‌اند، بدون هیچ گونه دانشی هوسهای خود را پیروی کرده‌اند. پس آن کس را که خدا گمراه کرده، چه کسی هدایت می‌کند؟ و برای آنان یاورانی نخواهد بود.'),
(3439, 30, 30, 'پس روی خود را با گرایش تمام به حق، به سوی این دین کن، با همان سرشتی که خدا مردم را بر آن سرشته است. آفرینش خدای تغییرپذیر نیست. این است همان دین پایدار، ولی بیشتر مردم نمی‌دانند.'),
(3440, 30, 31, 'به سویش توبه برید و از او پروا بدارید و نماز را برپا کنید و از مشرکان مباشید:'),
(3441, 30, 32, 'از کسانی که دین خود را قطعه قطعه کردند و فرقه فرقه شدند؛ هر حزبی بدانچه پیش آنهاست دلخوش شدند.'),
(3442, 30, 33, 'و چون مردم را زیانی رسد، پروردگار خود را، در حالی که به درگاه او توبه می‌کنند، می‌خوانند، و آنگاه که از جانب خود رحمتی به آنان چشانید، بناگاه دسته‌ای از ایشان به پروردگارشان شرک می‌آورند.'),
(3443, 30, 34, 'بگذار تا به آنچه بدانها عطا کرده‌ایم کفران ورزند. [بگو:] برخوردار شوید، زودا که خواهید دانست.'),
(3444, 30, 35, 'یا [مگر] حجّتی بر آنان نازل کرده‌ایم که آن [حجّت‌] در باره آنچه با [خدا] شریک می‌گردانیده‌اند سخن می‌گوید؟'),
(3445, 30, 36, 'و چون مردم را رحمتی بچشانیم، بدان شاد می‌گردند؛ و چون به [سزای‌] آنچه دستاورد گذشته آنان است، صدمه‌ای به ایشان برسد، بناگاه نومید می‌شوند.'),
(3446, 30, 37, 'آیا ندانسته‌اند که [این‌] خداست که روزی را برای هر کس که بخواهد فراخ یا تنگ می‌گرداند؟ قطعاً در این [امر] برای مردمی که ایمان می‌آورند عبرتهاست.'),
(3447, 30, 38, 'پس حق خویشاوند و تنگدست و در راه‌مانده را بده. این [انفاق‌] برای کسانی که خواهان خشنودی خدایند بهتر است، و اینان همان رستگارانند.'),
(3448, 30, 39, 'و آنچه [به قصد] ربا می‌دهید تا در اموال مردم سود و افزایش بردارد، نزد خدا فزونی نمی‌گیرد؛ و[لی‌] آنچه را از زکات -در حالی که خشنودی خدا را خواستارید- دادید، پس آنان همان فزونی‌یافتگانند [و مضاعف می‌شود].'),
(3449, 30, 40, 'خدا همان کسی است که شما را آفرید، سپس به شما روزی بخشید، آنگاه شما را می‌میراند و پس از آن زنده می‌گرداند. آیا در میان شریکان شما کسی هست که کاری از این [قبیل‌] کند؟ منزه است او، و برتر است از آنچه [با وی‌] شریک می‌گردانند.'),
(3450, 30, 41, 'به سبب آنچه دستهای مردم فراهم آورده، فساد در خشکی و دریا نمودار شده است، تا [سزای‌] بعضی از آنچه را که کرده‌اند به آنان بچشاند، باشد که بازگردند.'),
(3451, 30, 42, 'بگو: «در زمین بگردید و بنگرید فرجام کسانی که پیشتر بوده [و] بیشترشان مشرک بودند چگونه بوده است.»'),
(3452, 30, 43, 'پس به سوی این دین پایدار روی بیاور، پیش از آنکه روزی از جانب خدا فرا رسد که برگشت‌ناپذیر باشد، و در آن روز [مردم‌] دسته دسته می‌شوند.'),
(3453, 30, 44, 'هر که کفر ورزد، کفرش به زیان اوست، و کسانی که کار شایسته کنند، [فرجام نیک را] به سود خودشان آماده می‌کنند.'),
(3454, 30, 45, 'تا [خدا] کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، به فضل خویش پاداش دهد، که او کافران را دوست نمی‌دارد.'),
(3455, 30, 46, 'و از نشانه‌های او این است که بادهای بشارت‌آور را می‌فرستد، تا بخشی از رحمتش را به شما بچشاند و تا کشتی به فرمانش روان گردد، و تا از فضل او [روزی‌] بجویید، و امید که سپاسگزاری کنید.'),
(3456, 30, 47, 'و در حقیقت، پیش از تو فرستادگانی به سوی قومشان گسیل داشتیم، پس دلایل آشکار برایشان آوردند، و از کسانی که مرتکب جرم شدند انتقام گرفتیم، و یاری‌کردن مؤمنان بر ما فرض است.'),
(3457, 30, 48, 'خدا همان کسی است که بادها را می‌فرستد و ابری برمی‌انگیزد و آن را در آسمان -هر گونه بخواهد- می‌گستراند و انبوهش می‌گرداند، پس می‌بینی باران از لابلای آن بیرون می‌آید. و چون آن را به هر کس از بندگانش که بخواهد، رسانید، بناگاه آنان شادمانی می‌کنند.'),
(3458, 30, 49, 'و قطعاً پیش از آنکه بر ایشان فرو ریزد، [آری،] پیش از آن سخت نومید بودند.'),
(3459, 30, 50, 'پس به آثار رحمت خدا بنگر که چگونه زمین را پس از مرگش زنده می‌گرداند. در حقیقت، هم اوست که قطعاً زنده‌کننده مردگان است، و اوست که بر هر چیزی تواناست.'),
(3460, 30, 51, 'و اگر بادی [آفت‌زا] بفرستیم و [کِشت خود را] زردشده ببینند، قطعاً پس از آن کفران می‌کنند.'),
(3461, 30, 52, 'و در حقیقت، تو مردگان را شنوا نمی‌گردانی، و این دعوت را به کران -آنگاه که به ادبار پشت می‌گردانند- نمی‌توانی بشنوانی.'),
(3462, 30, 53, 'و تو کوران را از گمراهی‌شان به راه نمی‌آوری. تو تنها کسانی را می‌شنوانی که به آیات ما ایمان می‌آورند و خود تسلیمند.'),
(3463, 30, 54, 'خداست آن کس که شما را ابتدا ناتوان آفرید، آنگاه پس از ناتوانی قوّت بخشید، سپس بعد از قوّت، ناتوانی و پیری داد. هر چه بخواهد می‌آفریند و هموست دانای توانا.'),
(3464, 30, 55, 'و روزی که رستاخیز بر پا شود، مجرمان سوگند یاد می‌کنند که جز ساعتی [بیش‌] درنگ نکرده‌اند؛ [در دنیا هم‌] این گونه به دروغ کشانیده می‌شدند.'),
(3465, 30, 56, 'و[لی‌] کسانی که دانش و ایمان یافته‌اند، می‌گویند: «قطعاً شما [به موجب آنچه‌] در کتاب خدا[ست‌] تا روز رستاخیز مانده‌اید، و این، روز رستاخیز است ولی شما خودتان نمی‌دانستید.»'),
(3466, 30, 57, 'و در چنین روزی، [دیگر] پوزش آنان که ستم کرده‌اند سود نمی‌بخشد، و بازگشت به سوی حق از آنان خواسته نمی‌شود.'),
(3467, 30, 58, 'و به راستی در این قرآن برای مردم از هر گونه مَثَلی آوردیم، و چون برای ایشان آیه‌ای بیاوری، آنان که کفر ورزیده‌اند حتماً خواهند گفت: «شما جز بر باطل نیستید.»'),
(3468, 30, 59, 'این گونه، خدا بر دلهای کسانی که نمی‌دانند مُهر می‌نهد.'),
(3469, 30, 60, 'پس صبر کن که وعده خدا حق است، و زنهار تا کسانی که یقین ندارند، تو را به سبکسری واندارند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف، لام، میم.'),
(3471, 31, 2, 'این است آیات کتاب حکمت‌آموز،'),
(3472, 31, 3, '[که‌] برای نیکوکاران رهنمود و رحمتی است،'),
(3473, 31, 4, '[همان‌] کسانی که نماز برپا می‌دارند و زکات می‌دهند، و [هم‌] ایشانند که به آخرت یقین دارند.'),
(3474, 31, 5, 'آنانند که از جانب پروردگارشان از هدایت برخوردارند، و ایشانند که رستگارانند.'),
(3475, 31, 6, 'و برخی از مردم کسانی‌اند که سخن بیهوده را خریدارند تا [مردم را] بی‌[هیچ‌] دانشی از راه خدا گمراه کنند، و [راه خدا] را به ریشخند گیرند؛ برای آنان عذابی خوارکننده خواهد بود.'),
(3476, 31, 7, 'و چون آیات ما بر او خوانده شود، با نِخوت روی برمی‌گرداند، چنانکه گویی آن را نشنیده [یا] گویی در گوشهایش سنگینی است؛ پس او را از عذابی پر درد خبر ده.'),
(3477, 31, 8, 'در حقیقت، کسانی که ایمان آورده و کارهای شایسته کرده‌اند، باغهای پر نعمت خواهند داشت،'),
(3478, 31, 9, 'که در آن جاودان می‌مانند؛ وعده خداست که حق است و هموست شکست‌ناپذیر سنجیده‌کار.'),
(3479, 31, 10, 'آسمانها را بی‌هیچ ستونی که آن را ببینید خلق کرد و در زمین کوههای استوار بیفکند تا [مبادا زمین‌] شما را بجنباند، و در آن از هر گونه جنبنده‌ای پراکنده گردانید، و از آسمان آبی فرو فرستادیم و از هر نوع [گیاه‌] نیکو در آن رویانیدیم.'),
(3480, 31, 11, 'این، خلق خداست. [اینک‌] به من نشان دهید کسانی که غیر از اویند چه آفریده‌اند؟ [هیچ!] بلکه ستمگران در گمراهی آشکارند.'),
(3481, 31, 12, 'و به راستی، لقمان را حکمت دادیم که: خدا را سپاس بگزار و هر که سپاس بگزارد، تنها برای خود سپاس می‌گزارد؛ و هر کس کفران کند، در حقیقت، خدا بی‌نیاز ستوده است.'),
(3482, 31, 13, 'و [یاد کن‌] هنگامی را که لقمان به پسر خویش -در حالی که وی او را اندرز می‌داد- گفت: «ای پسرک من، به خدا شرک میاور که به راستی شرک ستمی بزرگ است.»'),
(3483, 31, 14, 'و انسان را در باره پدر و مادرش سفارش کردیم؛ مادرش به او باردار شد، سستی بر روی سستی. و از شیر باز گرفتنش در دو سال است. [آری، به او سفارش کردیم‌] که شکرگزارِ من و پدر و مادرت باش که بازگشت [همه‌] به سوی من است.'),
(3484, 31, 15, 'و اگر تو را وادارند تا در باره چیزی که تو را بدان دانشی نیست به من شرک ورزی، از آنان فرمان مبر، و[لی‌] در دنیا به خوبی با آنان معاشرت کن، و راه کسی را پیروی کن که توبه‌کنان به سوی من بازمی‌گردد؛ و [سرانجام‌] بازگشت شما به سوی من است، و از [حقیقت‌] آنچه انجام می‌دادید شما را با خبر خواهم کرد.'),
(3485, 31, 16, '«ای پسرک من، اگر [عمل تو] هموزن دانه خَردلی و در تخته‌سنگی یا در آسمانها یا در زمین باشد، خدا آن را می‌آورد، که خدا بس دقیق و آگاه است.'),
(3486, 31, 17, 'ای پسرک من، نماز را برپا دار و به کار پسندیده وادار و از کار ناپسند باز دار، و بر آسیبی که بر تو وارد آمده است شکیبا باش. این [حاکی‌] از عزم [و اراده تو در] امور است.'),
(3487, 31, 18, 'و از مردم [به نِخوت‌] رُخ برمتاب، و در زمین خرامان راه مرو که خدا خودپسند لافزن را دوست نمی‌دارد.'),
(3488, 31, 19, 'و در راه‌رفتن خود میانه‌رو باش، و صدایت را آهسته‌ساز، که بدترین آوازها بانگ خران است.'),
(3489, 31, 20, 'آیا ندانسته‌اید که خدا آنچه را که در آسمانها و آنچه را که در زمین است، مسخّر شما ساخته و نعمتهای ظاهر و باطن خود را بر شما تمام کرده است؟ و برخی از مردم در باره خدا بی‌[آنکه‌] دانش و رهنمود و کتابی روشن [داشته باشند] به مجادله برمی‌خیزند.'),
(3490, 31, 21, 'و چون به آنان گفته شود: «آنچه را که خدا نازل کرده پیروی کنید»، می‌گویند: «[نه!] بلکه آنچه که پدرانمان را بر آن یافته‌ایم پیروی می‌کنیم»؛ آیا هر چند شیطان آنان را به سوی عذاب سوزان فرا خواند؟'),
(3491, 31, 22, 'و هر کس خود را -در حالی که نیکوکار باشد- تسلیم خدا کند، قطعاً در ریسمان استوارتری چنگ درزده، و فرجام کارها به سوی خداست.'),
(3492, 31, 23, 'و هر کس کفر ورزد، نباید کفر او تو را غمگین گرداند. بازگشتشان به سوی ماست، و به [حقیقت‌] آنچه کرده‌اند آگاهشان خواهیم کرد. در حقیقت، خدا به راز دلها داناست.'),
(3493, 31, 24, '[ما] آنان را اندکی برخوردار می‌سازیم، سپس ایشان را در عذابی پر فشار درمانده می‌کنیم.'),
(3494, 31, 25, 'و اگر از آنها بپرسی: «چه کسی آسمانها و زمین را آفریده است؟ مسلماً خواهند گفت: «خدا.» بگو: «ستایش از آنِ خداست» ولی بیشترشان نمی‌دانند.'),
(3495, 31, 26, 'آنچه در آسمانها و زمین است از آنِ خداست، در حقیقت، خدا همان بی‌نیاز ستوده‌[صفات‌] است.'),
(3496, 31, 27, 'و اگر آن چه درخت در زمین است قلم باشد و دریا را هفت دریای دیگر به یاری آید، سخنان خدا پایان نپذیرد. قطعاً خداست که شکست‌ناپذیر حکیم است.'),
(3497, 31, 28, 'آفرینش و برانگیختن شما [در نزد ما] جز مانند [آفرینش‌] یک تن نیست، که خدا شنوای بیناست.'),
(3498, 31, 29, 'آیا ندیده‌ای که خدا شب را در روز درمی‌آورد، و روز را [نیز] در شب درمی‌آورد، و آفتاب و ماه را تسخیر کرده است [که‌] هر یک تا وقت معلومی روانند و [نیز] خدا به آنچه می‌کنید آگاه است؟'),
(3499, 31, 30, 'این‌[ها همه‌] دلیل آن است که خدا خود حق است و غیر از او هر چه را که می‌خوانند باطل است، و خدا همان بلندمرتبه بزرگ است.'),
(3500, 31, 31, 'آیا ندیده‌ای که کشتیها به نعمت خدا در دریا روان می‌گردند تا برخی از نشانه‌های [قدرت‌] خود را به شما بنمایاند؟ قطعاً در این [قدرت نمایی،] برای هر شکیبای سپاسگزاری، نشانه‌هاست.'),
(3501, 31, 32, 'و چون موجی کوه‌آسا آنان را فرا گیرد، خدا را بخوانند و اعتقاد [خود] را برای او خالص گردانند، و[لی‌] چون نجاتشان داد و به خشکی رساند برخی از آنان میانه‌رو هستند، و نشانه‌های ما را جز هر خائن ناسپاسگزاری انکار نمی‌کند.'),
(3502, 31, 33, 'ای مردم، از پروردگارتان پروا بدارید، و بترسید از روزی که هیچ پدری به کار فرزندش نمی‌آید، و هیچ فرزندی [نیز] به کار پدرش نخواهد آمد. آری، وعده خدا حق است. زنهار تا این زندگی دنیا شما را نفریبد، و زنهار تا شیطان شما را مغرور نسازد.'),
(3503, 31, 34, 'در حقیقت، خداست که علم [به‌] قیامت نزد اوست، و باران را فرو می‌فرستد، و آنچه را که در رحمهاست می‌داند و کسی نمی‌داند فردا چه به دست می‌آورد، و کسی نمی‌داند در کدامین سرزمین می‌میرد. در حقیقت، خداست [که‌] دانای آگاه است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف، لام، میم.'),
(3505, 32, 2, 'نازل شدن این کتاب -که هیچ [جای‌] شک در آن نیست- از طرف پروردگار جهانهاست.'),
(3506, 32, 3, 'آیا می‌گویند: «آن را بربافته است»؟ [نه چنین است‌] بلکه آن حقّ و از جانب پروردگار توست، تا مردمی را که پیش از تو بیم‌دهنده‌ای برای آنان نیامده است هشدار دهی، امید که راه یابند.'),
(3507, 32, 4, 'خدا کسی است که آسمانها و زمین و آنچه را که میان آن دو است، در شش هنگام آفرید، آنگاه بر عرش [قدرت‌] استیلا یافت، برای شما غیر از او سرپرست و شفاعتگری نیست؛ آیا باز هم پند نمی‌گیرید؟'),
(3508, 32, 5, 'کار [جهان‌] را از آسمان [گرفته‌] تا زمین، اداره می‌کند؛ آنگاه [نتیجه و گزارش آن‌] در روزی که مقدارش -آن چنان که شما [آدمیان‌] برمی‌شمارید- هزار سال است، به سوی او بالا می‌رود.'),
(3509, 32, 6, 'اوست دانای نهان و آشکار، که شکوهمند مهربان است.'),
(3510, 32, 7, 'همان کسی که هر چیزی را که آفریده است نیکو آفریده، و آفرینش انسان را از گِل آغاز کرد؛'),
(3511, 32, 8, 'سپس [تداوم‌] نسل او را از چکیده آبی پست مقرر فرمود؛'),
(3512, 32, 9, 'آنگاه او را درست‌اندام کرد، و از روح خویش در او دمید، و برای شما گوش و دیدگان و دلها قرار داد؛ چه اندک سپاس می‌گزارید.'),
(3513, 32, 10, 'و گفتند: «آیا وقتی در [دلِ‌] زمین گم شدیم، آیا [باز] ما در خلقت جدیدی خواهیم بود؟» [نه،] بلکه آنها به لقای پروردگارشان [و حضور او] کافرند.'),
(3514, 32, 11, 'بگو: «فرشته مرگی که بر شما گمارده شده، جانتان را می‌ستاند، آنگاه به سوی پروردگارتان بازگردانیده می‌شوید.»'),
(3515, 32, 12, 'و کاش هنگامی را که مجرمان پیش پروردگارشان سرهاشان را به زیر افکنده‌اند می‌دیدی [که می‌گویند:] «پروردگارا، دیدیم و شنیدیم؛ ما را بازگردان تا کار شایسته کنیم، چرا که ما یقین داریم.»'),
(3516, 32, 13, 'و اگر می‌خواستیم، حتماً به هر کسی [از روی جبر] هدایتش را می‌دادیم، لیکن سخن من محقَقَ گردیده که: «هر آینه جهنم را از همه جنیان و آدمیان خواهم آکند.»'),
(3517, 32, 14, 'پس به [سزای‌] آنکه دیدار این روزتان را از یاد بردید [عذاب را] بچشید؛ ما [نیز] فراموشتان کردیم، و به [سزای‌] آنچه انجام می‌دادید عذاب جاودان را بچشید.'),
(3518, 32, 15, 'تنها کسانی به آیات ما می‌گروند که چون آن [آیات‌] را به ایشان یادآوری کنند، سجده‌کنان به روی درمی‌افتند، و به ستایش پروردگارشان تسبیح می‌گویند و آنان بزرگی نمی‌فروشند.'),
(3519, 32, 16, 'پهلوهایشان از خوابگاهها جدا می‌گردد [و] پروردگارشان را از روی بیم و طمع می‌خوانند، و از آنچه روزیشان داده‌ایم انفاق می‌کنند.'),
(3520, 32, 17, 'هیچ کس نمی‌داند چه چیز از آنچه روشنی‌بخش دیدگان است به [پاداش‌] آنچه انجام می‌دادند برای آنان پنهان کرده‌ام.'),
(3521, 32, 18, 'آیا کسی که مؤمن است، چون کسی است که نافرمان است؟ یکسان نیستند.'),
(3522, 32, 19, 'اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به [پاداش‌] آنچه انجام می‌دادند در باغهایی که در آن جایگزین می‌شوند، پذیرایی می‌گردند.'),
(3523, 32, 20, 'و اما کسانی که نافرمانی کرده‌اند، پس جایگاهشان آتش است. هر بار که بخواهند از آن بیرون بیایند، در آن بازگردانیده می‌شوند و به آنان گفته می‌شود: «عذاب آن آتشی را که دروغش می‌پنداشتید بچشید.»'),
(3524, 32, 21, 'و قطعاً غیر از آن عذاب بزرگتر، از عذاب این دنیا [نیز] به آنان می‌چشانیم، امید که آنها [به خدا] بازگردند.'),
(3525, 32, 22, 'و کیست بیدادگرتر از آن کس که به آیات پروردگارش پند داده شود [و] آنگاه از آن روی بگرداند؟ قطعاً ما از مجرمان انتقام‌کِشنده‌ایم.'),
(3526, 32, 23, 'و به راستی [ما] به موسی کتاب دادیم. پس در لقای او [با خدا]تردید مکن، و آن [کتاب‌] را برای فرزندان اسرائیل [مایه‌] هدایت قرار دادیم.'),
(3527, 32, 24, 'و چون شکیبایی کردند و به آیات ما یقین داشتند، برخی از آنان را پیشوایانی قرار دادیم که به فرمان ما [مردم را] هدایت می‌کردند.'),
(3528, 32, 25, 'در حقیقت، پروردگار تو، خود روز قیامت در آنچه با یکدیگر در باره آن اختلاف می‌کردند، میانشان داوری خواهد کرد.'),
(3529, 32, 26, 'آیا برای آنان روشن نگردیده که چه بسیار نسلها را پیش از آنها نابود گردانیدیم [که اینان‌] در سراهایشان راه می‌روند؟ قطعاً در این [امر] عبرتهاست، مگر نمی‌شنوند؟'),
(3530, 32, 27, 'آیا ننگریسته‌اند که ما باران را به سوی زمین بایر می‌رانیم، و به وسیله آن کِشته‌ای را برمی‌آوریم که دامهایشان و خودشان از آن می‌خورند؟ مگر نمی‌بینند؟'),
(3531, 32, 28, 'و می‌پرسند: «اگر راست می‌گویید، این پیروزی [شما] چه وقت است؟»'),
(3532, 32, 29, 'بگو: «روز پیروزی، ایمان کسانی که کافر شده‌اند سود نمی‌بخشد و آنان مهلت نمی‌یابند.»'),
(3533, 32, 30, 'پس، از ایشان روی برتاب و منتظر باش که آنها نیز در انتظارند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ای پیامبر، از خدا پروا بدار و کافران و منافقان را فرمان مبر، که خدا همواره دانای حکیم است.'),
(3535, 33, 2, 'و آنچه را که از جانب پروردگارت به سوی تو وحی می‌شود، پیروی کن که خدا همواره به آنچه می‌کنید آگاه است.'),
(3536, 33, 3, 'و بر خدا اعتماد کن، همین بس که خدا نگهبان [تو]ست.'),
(3537, 33, 4, 'خداوند برای هیچ مردی در درونش دو دل ننهاده است، و آن همسرانتان را که مورد «ظهار» قرار می‌دهید مادران شما نگردانیده، و پسرخواندگانتان را پسران [واقعی‌] شما قرار نداده است. این، گفتار شما به زبان شماست، و[لی‌] خدا حقیقت را می‌گوید، و او[ست که‌] به راه راست هدایت می‌کند.'),
(3538, 33, 5, 'آنان را به [نام‌] پدرانشان بخوانید، که این نزد خدا عادلانه‌تر است، و اگر پدرانشان را نمی‌شناسید پس برادران دینی و موالی شمایند، و در آنچه اشتباهاً مرتکب آن شده‌اید بر شما گناهی نیست، ولی در آنچه دلهایتان عمد داشته است [مسؤولید ]و خداست که همواره آمرزنده مهربان است.'),
(3539, 33, 6, 'پیامبر به مؤمنان از خودشان سزاوارتر [و نزدیکتر] است و همسرانش مادران ایشانند، و خویشاوندان [طبق‌] کتاب خدا، بعضی [نسبت‌] به بعضی اولویت دارند [و] بر مؤمنان و مهاجران [مقدّمند]، مگر آنکه بخواهید به دوستان [مؤمن‌] خود [وصیت یا ]احسانی کنید، و این در کتاب [خدا] نگاشته شده است.'),
(3540, 33, 7, 'و [یاد کن‌] هنگامی را که از پیامبران پیمان گرفتیم، و از تو و از نوح و ابراهیم و موسی و عیسی پسر مریم، و از [همه‌] آنان پیمانی استوار گرفتیم.'),
(3541, 33, 8, 'تا راستان را از صدقشان باز پرسد، و برای کافران عذابی دردناک آماده کرده است.'),
(3542, 33, 9, 'ای کسانی که ایمان آورده‌اید، نعمت خدا را بر خود به یاد آرید، آنگاه که لشکرهایی به سوی شما [در]آمدند، پس بر سر آنان تندبادی و لشکرهایی که آنها را نمی‌دیدید فرستادیم، و خدا به آنچه می‌کنید همواره بیناست.'),
(3543, 33, 10, 'هنگامی که از بالای [سر] شما و از زیر [پای‌] شما آمدند، و آنگاه که چشمها خیره شد و جانها به گلوگاهها رسید و به خدا گمانهایی [نابجا] می‌بردید.'),
(3544, 33, 11, 'آنجا [بود که‌] مؤمنان در آزمایش قرار گرفتند و سخت تکان خوردند.'),
(3545, 33, 12, 'و هنگامی که منافقان و کسانی که در دلهایشان بیماری است می‌گفتند: «خدا و فرستاده‌اش جز فریب به ما وعده‌ای ندادند.»'),
(3546, 33, 13, 'و چون گروهی از آنان گفتند: «ای مردم مدینه، دیگر شما را جای درنگ نیست، برگردید.» و گروهی از آنان از پیامبر اجازه می‌خواستند و می‌گفتند: «خانه‌های ما بی‌حفاظ است» و[لی خانه‌هایشان‌] بی‌حفاظ نبود، [آنان‌] جز گریز [از جهاد] چیزی نمی‌خواستند.'),
(3547, 33, 14, 'و اگر از اطراف [مدینه‌] مورد هجوم واقع می‌شدند و آنگاه آنان را به ارتداد می‌خواندند، قطعاً آن را می‌پذیرفتند و جز اندکی در این [کار] درنگ نمی‌کردند؛'),
(3548, 33, 15, 'با آنکه قبلاً با خدا سخت پیمان بسته بودند که پشت [به دشمن‌] نکنند، و پیمان خدا همواره بازخواست دارد.'),
(3549, 33, 16, 'بگو: «اگر از مرگ یا کشته شدن بگریزید، هرگز این گریز برای شما سود نمی‌بخشد، و در آن صورت جز اندکی برخوردار نخواهید شد.»'),
(3550, 33, 17, 'بگو: «چه کسی می‌تواند در برابر خدا از شما حمایت کند اگر او بخواهد برای شما بد بیاورد یا بخواهد شما را رحمت کند؟ و غیر از خدا برای خود یار و یاوری نخواهند یافت.»'),
(3551, 33, 18, 'خداوند کارشکنان [و مانع‌شوندگان‌] شما و آن کسانی را که به برادرانشان می گفتند: «نزد ما بیایید» و جز اندکی روی به جنگ نمی‌آورند [خوب‌] می‌شناسد.'),
(3552, 33, 19, 'بر شما بخیلانند، و چون خطر فرا رسد آنان را می‌بینی که مانند کسی که مرگ او را فرو گرفته، چشمانشان در حدقه می‌چرخد [و] به سوی تو می‌نگرند؛ و چون ترس برطرف شود شما را با زبانهایی تند نیش می‌زنند؛ بر مال حریصند. آنان ایمان نیاورده‌اند و خدا اعمالشان را تباه گردانیده، و این [کار] همواره بر خدا آسان است.'),
(3553, 33, 20, 'اینان [چنین‌] می‌پندارند که دسته‌های دشمن نرفته‌اند، و اگر دسته‌های دشمن بازآیند آرزو می‌کنند: کاش میان اعراب بادیه‌نشین بودند و از اخبار [مربوط به‌] شما جویا می‌شدند، و اگر در میان شما بودند، جز اندکی جنگ نمی‌کردند.'),
(3554, 33, 21, 'قطعاً برای شما در [اقتدا به‌] رسول خدا سرمشقی نیکوست: برای آن کس که به خدا و روز بازپسین امید دارد و خدا را فراوان یاد می‌کند.'),
(3555, 33, 22, 'و چون مؤمنان دسته‌های دشمن را دیدند، گفتند: «این همان است که خدا و فرستاده‌اش به ما وعده دادند و خدا و فرستاده‌اش راست گفتند»، و جز بر ایمان و فرمانبرداری آنان نیفزود.'),
(3556, 33, 23, 'از میان مؤمنان مردانی‌اند که به آنچه با خدا عهد بستند صادقانه وفا کردند. برخی از آنان به شهادت رسیدند و برخی از آنها در [همین‌] انتظارند و [هرگز عقیده خود را] تبدیل نکردند.'),
(3557, 33, 24, 'تا خدا راستگویان را به [پاداش‌] راستی‌شان پاداش دهد، و منافقان را اگر بخواهد، عذاب کند یا بر ایشان ببخشاید که خدا همواره آمرزنده مهربان است.'),
(3558, 33, 25, 'و خداوند آنان را که کفر ورزیده‌اند، بی‌آنکه به مالی رسیده باشند، به غیظ [و حسرت‌] برگرداند، و خدا [زحمت‌] جنگ را از مؤمنان برداشت، و خدا همواره نیرومند شکست‌ناپذیر است.'),
(3559, 33, 26, 'و کسانی از اهل کتاب را که با [مشرکان‌] همپُشتی کرده بودند، از دژهایشان به زیر آورد و در دلهایشان هراس افکند: گروهی را می‌کشتید و گروهی را اسیر می‌کردید.'),
(3560, 33, 27, 'و زمینشان و خانه‌ها و اموالشان و سرزمینی را که در آن پا ننهاده بودید به شما میراث داد، و خدا بر هر چیزی تواناست.'),
(3561, 33, 28, 'ای پیامبر، به همسرانت بگو: «اگر خواهان زندگی دنیا و زینت آنید، بیایید تا مَهرتان را بدهم و [خوش و] خُرّم شما را رها کنم.'),
(3562, 33, 29, 'و اگر خواستار خدا و فرستاده وی و سرای آخرتید، پس به راستی خدا برای نیکوکاران شما پاداش بزرگی آماده گردانیده است.'),
(3563, 33, 30, 'ای همسران پیامبر، هر کس از شما مبادرت به کار زشتِ آشکاری کند، عذابش دو چندان خواهد بود؛ و این بر خدا همواره آسان است.'),
(3564, 33, 31, 'و هر کس از شما خدا و فرستاده‌اش را فرمان بَرَد و کار شایسته کند، پاداشش را دو چندان می‌دهیم و برایش روزیِ نیکو فراهم خواهیم ساخت.'),
(3565, 33, 32, 'ای همسران پیامبر، شما مانند هیچ یک از زنان [دیگر] نیستید، اگر سر پروا دارید پس به ناز سخن مگویید تا آنکه در دلش بیماری است طمع ورزد؛ و گفتاری شایسته گویید.'),
(3566, 33, 33, 'و در خانه‌هایتان قرار گیرید و مانند روزگار جاهلیت قدیم زینتهای خود را آشکار مکنید و نماز برپا دارید و زکات بدهید و خدا و فرستاده‌اش را فرمان برید. خدا فقط می‌خواهد آلودگی را از شما خاندان [پیامبر ]بزداید و شما را پاک و پاکیزه گرداند.'),
(3567, 33, 34, 'و آنچه را که از آیات خدا و [سخنان‌] حکمت‌[آمیز] در خانه‌های شما خوانده می‌شود یاد کنید. در حقیقت، خدا همواره دقیق و آگاه است.'),
(3568, 33, 35, 'مردان و زنان مسلمان، و مردان و زنان با ایمان، و مردان و زنان عبادت‌پیشه، و مردان و زنان راستگو، و مردان و زنان شکیبا، و مردان و زنان فروتن، و مردان و زنان صدقه‌دهنده، و مردان و زنان روزه‌دار، و مردان و زنان پاکدامن، و مردان و زنانی که خدا را فراوان یاد می‌کنند، خدا برای [همه‌] آنان آمرزشی و پاداشی بزرگ فراهم ساخته است.'),
(3569, 33, 36, 'و هیچ مرد و زن مؤمنی را نرسد که چون خدا و فرستاده‌اش به کاری فرمان دهند، برای آنان در کارشان اختیاری باشد؛ و هر کس خدا و فرستاده‌اش را نافرمانی کند قطعاً دچار گمراهی آشکاری گردیده است.'),
(3570, 33, 37, 'و آنگاه که به کسی که خدا بر او نعمت ارزانی داشته بود و تو [نیز] به او نعمت داده بودی، می‌گفتی: «همسرت را پیش خود نگاه دار و از خدا پروا بدار» و آنچه را که خدا آشکارکننده آن بود، در دل خود نهان می‌کردی و از مردم می‌ترسیدی، با آنکه خدا سزاوارتر بود که از او بترسی. پس چون زید از آن [زن‌] کام برگرفت [و او را ترک گفت‌] وی را به نکاح تو درآوردیم تا [در آینده‌] در مورد ازدواج مؤمنان با زنان پسرخواندگانشان -چون آنان را طلاق گفتند- گناهی نباشد، و فرمان خدا صورت اجرا پذیرد.'),
(3571, 33, 38, 'بر پیامبر در آنچه خدا برای او فرض گردانیده گناهی نیست. [این‌] سنّت خداست که از دیرباز در میان گذشتگان [معمول‌] بوده، و فرمان خدا همواره به اندازه مقرر [و متناسب با توانایی‌] است.'),
(3572, 33, 39, 'همان کسانی که پیامهای خدا را ابلاغ می‌کنند و از او می‌ترسند و از هیچ کس جز خدا بیم ندارند. و خدا برای حسابرسی کفایت می‌کند.'),
(3573, 33, 40, 'محمّد پدر هیچ یک از مردان شما نیست، ولی فرستاده خدا و خاتم پیامبران است. و خدا همواره بر هر چیزی داناست.'),
(3574, 33, 41, 'ای کسانی که ایمان آورده‌اید، خدا را یاد کنید، یادی بسیار.'),
(3575, 33, 42, 'و صبح و شام او را به پاکی بستایید.'),
(3576, 33, 43, 'اوست کسی که با فرشتگان خود بر شما درود می‌فرستد تا شما را از تاریکیها به سوی روشنایی برآورد، و به مؤمنان همواره مهربان است.'),
(3577, 33, 44, 'درودشان -روزی که دیدارش کنند- سلام خواهد بود، و برای آنان پاداشی نیکو آماده کرده است.'),
(3578, 33, 45, 'ای پیامبر، ما تو را [به سِمَت‌] گواه و بشارتگر و هشداردهنده فرستادیم،'),
(3579, 33, 46, 'و دعوت‌کننده به سوی خدا به فرمان او، و چراغی تابناک.'),
(3580, 33, 47, 'و مؤمنان را مژده دِه که برای آنان از جانب خدا بخشایشی فراوان خواهد بود.'),
(3581, 33, 48, 'و کافران و منافقان را فرمان مبر، و از آزارشان بگذر و بر خدا اعتماد کن و کارسازی [چون‌] خدا کفایت می‌کند.'),
(3582, 33, 49, 'ای کسانی که ایمان آورده‌اید، اگر زنان مؤمن را به نکاح خود درآوردید، آنگاه پیش از آنکه با آنان همخوابگی کنید، طلاقشان دادید، دیگر بر عهده آنها عده‌ای که آن را بشمارید، نیست؛ پس مَهْرشان را بدهید و خوش و خرّم آنها را رها کنید.'),
(3583, 33, 50, 'ای پیامبر، ما برای تو آن همسرانی را که مَهْرشان را داده‌ای حلال کردیم، و [کنیزانی‌] را که خدا از غنیمت جنگی در اختیار تو قرار داده، و دختران عمویت و دختران عمه‌هایت و دختران دایی تو و دختران خاله‌هایت که با تو مهاجرت کرده‌اند، و زن مؤمنی که خود را [داوطلبانه‌] به پیامبر ببخشد-در صورتی که پیامبر بخواهد او را به زنی گیرد. [این ازدواج از روی بخشش‌] ویژه توست نه دیگر مؤمنان. ما نیک می‌دانیم که در مورد زنان و کنیزانشان چه بر آنان مقرر کرده‌ایم، تا برای تو مشکلی پیش نیاید، و خدا همواره آمرزنده مهربان است.'),
(3584, 33, 51, 'نوبت هر کدام از آن زنها را که می‌خواهی به تأخیر انداز و هر کدام را که می‌خواهی پیش خود جای ده، و بر تو باکی نیست که هر کدام را که ترک کرده‌ای [دوباره‌] طلب کنی. این نزدیکتر است برای اینکه چشمانشان روشن گردد و دلتنگ نشوند و همگی‌شان به آنچه به آنان داده‌ای خشنود گردند، و آنچه در دلهای شماست خدا می‌داند، و خدا همواره دانای بردبار است.'),
(3585, 33, 52, 'از این پس، دیگر [گرفتن‌] زنان و نیز اینکه به جای آنان، زنان دیگری بر تو حلال نیست، هر چند زیبایی آنها برای تو مورد پسند افتد، به استثنای کنیزان، و خدا همواره بر هر چیزی مراقب است.'),
(3586, 33, 53, 'ای کسانی که ایمان آورده‌اید، داخل اتاقهای پیامبر مشوید، مگر آنکه برای [خوردن‌] طعامی به شما اجازه داده شود، [آن هم‌] بی‌آنکه در انتظار پخته‌شدن آن باشید؛ ولی هنگامی که دعوت شدید داخل گردید، و وقتی غذا خوردید پراکنده شوید بی‌آنکه سرگرم سخنی گردید. این [رفتار] شما پیامبر را می‌رنجاند و[لی‌] از شما شرم می‌دارد، و حال آنکه خدا از حق‌[گویی‌] شرم نمی‌کند، و چون از زنان [پیامبر] چیزی خواستید از پشت پرده از آنان بخواهید؛ این برای دلهای شما و دلهای آنان پاکیزه‌تر است، و شما حق ندارید رسول خدا را برنجانید، و مطلقاً [نباید] زنانش را پس از [مرگ‌] او به نکاح خود درآورید، چرا که این [کار] نزد خدا همواره [گناهی‌] بزرگ است.'),
(3587, 33, 54, 'اگر چیزی را فاش کنید یا آن را پنهان دارید قطعاً خدا به هر چیزی داناست.'),
(3588, 33, 55, 'بر زنان در مورد پدران و پسران و برادران و پسران برادران و پسران خواهران و زنان [همکیش‌] و بردگانشان گناهی نیست [که دیده شوند]؛ و باید از خدا پروا بدارید که خدا همواره بر هر چیزی گواه است.'),
(3589, 33, 56, 'خدا و فرشتگانش بر پیامبر درود می‌فرستند. ای کسانی که ایمان آورده‌اید، بر او درود فرستید و به فرمانش بخوبی گردن نهید.'),
(3590, 33, 57, 'بی‌گمان، کسانی که خدا و پیامبر او را آزار می‌رسانند، خدا آنان را در دنیا و آخرت لعنت کرده و برایشان عذابی خفت‌آور آماده ساخته است.'),
(3591, 33, 58, 'و کسانی که مردان و زنان مؤمن را بی‌آنکه مرتکب [عمل زشتی‌] شده باشند آزار می‌رسانند، قطعاً تهمت و گناهی آشکار به گردن گرفته‌اند.'),
(3592, 33, 59, 'ای پیامبر، به زنان و دخترانت و به زنان مؤمنان بگو: «پوششهای خود را بر خود فروتر گیرند. این برای آنکه شناخته شوند و مورد آزار قرار نگیرند [به احتیاط] نزدیکتر است، و خدا آمرزنده مهربان است.'),
(3593, 33, 60, 'اگر منافقان و کسانی که در دلهایشان مرضی هست و شایعه‌افکنان در مدینه، [از کارشان‌] باز نایستند، تو را سخت بر آنان مسلط می‌کنیم تا جز [مدتی‌] اندک در همسایگی تو نپایند.'),
(3594, 33, 61, 'از رحمت خدا دور گردیده و هر کجا یافته شوند گرفته و سخت کشته خواهند شد.'),
(3595, 33, 62, 'در باره کسانی که پیشتر بوده‌اند [همین‌] سنّت خدا [جاری بوده‌] است؛ و در سنّت خدا هرگز تغییری نخواهی یافت.'),
(3596, 33, 63, 'مردم از تو در باره رستاخیز می‌پرسند؛ بگو: «علم آن فقط نزد خداست.» و چه می دانی؟ شاید رستاخیز نزدیک باشد.'),
(3597, 33, 64, 'خدا کافران را لعنت کرده و برای آنها آتش فروزانی آماده کرده است.'),
(3598, 33, 65, 'جاودانه در آن می‌مانند، نه یاری می‌یابند و نه یاوری.'),
(3599, 33, 66, 'روزی که چهره‌هایشان را در آتش زیرورو می‌کنند، می‌گویند: «ای کاش ما خدا را فرمان می‌بردیم و پیامبر را اطاعت می‌کردیم.»'),
(3600, 33, 67, 'و می‌گویند: «پروردگارا، ما رؤسا و بزرگتران خویش را اطاعت کردیم و ما را از راه به در کردند.»'),
(3601, 33, 68, '«پروردگارا، آنان را دو چندان عذاب ده و لعنتشان کن لعنتی بزرگ.»'),
(3602, 33, 69, 'ای کسانی که ایمان آورده‌اید، مانند کسانی مباشید که موسی را [با اتهام خود] آزار دادند، و خدا او را از آنچه گفتند مبرّا ساخت و نزد خدا آبرومند بود.'),
(3603, 33, 70, 'ای کسانی که ایمان آورده‌اید، از خدا پروا دارید و سخنی استوار گویید.'),
(3604, 33, 71, 'تا اعمال شما را به صلاح آورد و گناهانتان را بر شما ببخشاید، و هر کس خدا و پیامبرش را فرمان برد قطعاً به رستگاری بزرگی نایل آمده است.'),
(3605, 33, 72, 'ما امانت [الهی و بار تکلیف‌] را بر آسمانها و زمین و کوهها عرضه کردیم، پس، از برداشتن آن سر باز زدند و از آن هراسناک شدند، و[لی‌] انسان آن را برداشت؛ راستی او ستمگری نادان بود.'),
(3606, 33, 73, '[آری، چنین است‌] تا خدا مردان و زنان منافق، و مردان و زنان مشرک را عذاب کند و توبه مردان و زنان با ایمان را بپذیرد، و خدا همواره آمرزنده مهربان است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'سپاس خدایی را که آنچه در آسمانها و آنچه در زمین است از آنِ اوست، و در آخرت [نیز] سپاس از آنِ اوست، و هم اوست سنجیده‌کار آگاه.'),
(3608, 34, 2, 'آنچه در زمین فرو می‌رود و آنچه از آن بر می‌آید و آنچه از آسمان فرو می‌شود و آنچه در آن بالا می‌رود [همه را] می‌داند، و اوست مهربان آمرزنده.'),
(3609, 34, 3, 'و کسانی که کافر شدند، گفتند: «رستاخیز برای ما نخواهد آمد.» بگو: «چرا، سوگند به پروردگارم که حتماً برای شما خواهد آمد. [همان‌] دانای نهان‌[ها] که هموزن ذره‌ای، نه در آسمانها و نه در زمین، از وی پوشیده نیست، و نه کوچکتر از آن و نه بزرگتر از آن است مگر اینکه در کتابی روشن [درج شده‌] است.»'),
(3610, 34, 4, 'تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به پاداش رساند؛ آنانند که آمرزش و روزیِ خوش برایشان خواهد بود.'),
(3611, 34, 5, 'و کسانی که در [ابطال‌] آیات ما کوشش می‌ورزند که ما را درمانده کنند، برایشان عذابی از بلایی دردناک باشد.'),
(3612, 34, 6, 'و کسانی که از دانش بهره یافته‌اند، می‌دانند که آنچه از جانب پروردگارت به سوی تو نازل شده، حق است و به راه آن عزیز ستوده‌[صفات‌] راهبری می‌کند.'),
(3613, 34, 7, 'و کسانی که کفر ورزیدند، گفتند: «آیا مردی را به شما نشان دهیم که شما را خبر می‌دهد که چون کاملا متلاشی شدید، [باز] قطعاً در آفرینشی جدید خواهید بود؟'),
(3614, 34, 8, 'آیا [این مرد] بر خدا دروغی بسته یا جنونی در اوست؟ «[نه!] بلکه آنان که به آخرت ایمان ندارند در عذاب و گمراهی دور و درازند.'),
(3615, 34, 9, 'آیا به آنچه -از آسمان و زمین- در دسترسشان و پشت سرشان است ننگریسته‌اند؟ اگر بخواهیم آنان را در زمین فرو می‌بریم، یا پاره‌سنگهایی از آسمان بر سرشان می‌افکنیم. قطعاً در این [تهدید] برای هر بنده توبه‌کاری عبرت است.'),
(3616, 34, 10, 'و به راستی داوود را از جانب خویش مزیتی عطا کردیم. [و گفتیم:] ای کوهها، با او [در تسبیح خدا] همصدا شوید، و ای پرندگان [هماهنگی کنید]. و آهن را برای او نرم گردانیدیم.'),
(3617, 34, 11, '[که‌] زره‌های فراخ بساز و حلقه‌ها را درست اندازه‌گیری کن. و کار شایسته کنید، زیرا من به آنچه انجام می‌دهید بینایم.'),
(3618, 34, 12, 'و باد را برای سلیمان [رام کردیم:] که رفتن آن بامداد، یک ماه، و آمدنش شبانگاه، یک ماه [راه‌] بود، و معدن مس را برای او ذوب [و روان‌] گردانیدیم، و برخی از جن به فرمان پروردگارشان پیش او کار می‌کردند، و هر کس از آنها از دستور ما سر برمی‌تافت، از عذاب سوزان به او می‌چشانیدیم.'),
(3619, 34, 13, '[آن متخصصان‌] برای او هر چه می‌خواست: از نمازخانه‌ها و مجسمه‌ها و ظروف بزرگ مانند حوضچه‌ها و دیگهای چسبیده به زمین می‌ساختند. ای خاندان داوود، شکرگزار باشید. و از بندگان من اندکی سپاسگزارند.'),
(3620, 34, 14, 'پس چون مرگ را بر او مقرر داشتیم، جز جنبنده‌ای خاکی [=موریانه‌] که عصای او را [به تدریج‌] می‌خورد، [آدمیان را] از مرگ او آگاه نگردانید، پس چون [سلیمان‌] فرو افتاد برای جنیان روشن گردید که اگر غیب می‌دانستند، در آن عذاب خفت‌آور [باقی‌] نمی‌ماندند.'),
(3621, 34, 15, 'قطعاً برای [مردم‌] سبا در محل سکونتشان نشانه [رحمتی‌] بود: دو باغستان از راست و چپ [به آنان گفتیم:] از روزی پروردگارتان بخورید و او را شکر کنید. شهری است خوش و خدایی آمرزنده.'),
(3622, 34, 16, 'پس روی گردانیدند، و بر آن سیل [سدّ] عَرِم را روانه کردیم، و دو باغستان آنها را به دو باغ که میوه‌های تلخ و شوره‌گز و نوعی از کُنار تنک داشت تبدیل کردیم.'),
(3623, 34, 17, 'این [عقوبت‌] را به [سزای‌] آنکه کفران کردند به آنان جزا دادیم؛ و آیا جز ناسپاس را به مجازات می‌رسانیم؟'),
(3624, 34, 18, 'و میان آنان و میان آبادانیهایی که در آنها برکت نهاده بودیم شهرهای متصل به هم قرار داده بودیم، و در میان آنها مسافت را، به اندازه، مقرر داشته بودیم. در این [راه‌]ها، شبان و روزان آسوده‌خاطر بگردید.'),
(3625, 34, 19, 'تا گفتند: «پروردگارا، میان [منزلهای‌] سفرهایمان فاصله انداز.» و بر خویشتن ستم کردند. پس آنها را [برای آیندگان، موضوع‌] حکایتها گردانیدیم، و سخت تارومارشان کردیم؛ قطعاً در این [ماجرا] برای هر شکیبای سپاسگزاری عبرتهاست.'),
(3626, 34, 20, 'و قطعاً شیطان گمان خود را در مورد آنها راست یافت. و جز گروهی از مؤمنان، [بقیه‌] از او پیروی کردند!'),
(3627, 34, 21, 'و [شیطان‌] را بر آنان تسلطی نبود، جز آنکه کسی را که به آخرت ایمان دارد از کسی که در باره آن در تردید است باز شناسیم. و پروردگار تو بر هر چیزی نگاهبان است.'),
(3628, 34, 22, 'بگو: «کسانی را که جز خدا [معبود خود] پنداشته‌اید بخوانید؛ هموزن ذرّه‌ای نه در آسمانها و نه در زمین مالک نیستند، و در آن دو شرکتی ندارند، و برای وی از میان آنان هیچ پشتیبانی نیست.»'),
(3629, 34, 23, 'و شفاعتگری در پیشگاه او سود نمی‌بخشد، مگر برای آن کس که به وی اجازه دهد. تا چون هراس از دلهایشان برطرف شود، می‌گویند: «پروردگارتان چه فرمود؟» می گویند: «حقیقت؛ و هموست بلندمرتبه و بزرگ.»'),
(3630, 34, 24, 'بگو: «کیست که شما را از آسمانها و زمین روزی می‌دهد؟» بگو: «خدا؛ و در حقیقت یا ما، یا شما بر هدایت یا گمراهی آشکاریم.»'),
(3631, 34, 25, 'بگو: «[شما] از آنچه ما مرتکب شده‌ایم بازخواست نخواهید شد، و [ما نیز] از آنچه شما انجام می‌دهید بازخواست نخواهیم شد.»'),
(3632, 34, 26, 'بگو: «پروردگارمان ما و شما را جمع خواهد کرد؛ سپس میان ما به حق داوری می کند، و اوست داور دانا.»'),
(3633, 34, 27, 'بگو: «کسانی را که [به عنوان‌] شریک به او ملحق گردانیده‌اید، به من نشان دهید.» چنین نیست، بلکه اوست خدای عزیز حکیم.'),
(3634, 34, 28, 'و ما تو را جز [به سمت‌] بشارتگر و هشداردهنده برای تمام مردم، نفرستادیم؛ لیکن بیشتر مردم نمی‌دانند.'),
(3635, 34, 29, 'و می‌گویند: «اگر راست می‌گویید، این وعده چه وقت است؟»'),
(3636, 34, 30, 'بگو: «میعاد شما روزی است که نه ساعتی از آن پس توانید رفت، و نه پیشی توانید جُست.»'),
(3637, 34, 31, 'و کسانی که کافر شدند گفتند: «نه به این قرآن و نه به آن [توراتی‌] که پیش از آن است هرگز ایمان نخواهیم آورد.» و ای کاش بیدادگران را هنگامی که در پیشگاه پروردگارشان بازداشت شده‌اند می‌دیدی [که چگونه‌] برخی از آنان با برخی [دیگر جدل و] گفتگو می‌کنند؛ کسانی که زیردست بودند به کسانی که [ریاست و] برتری داشتند، می‌گویند: «اگر شما نبودید قطعاً ما مؤمن بودیم.»'),
(3638, 34, 32, 'کسانی که [ریاست و] برتری داشتند، به کسانی که زیردست بودند، می‌گویند: «مگر ما بودیم که شما را از هدایت -پس از آنکه به سوی شما آمد- بازداشتیم؟ [نه،] بلکه خودتان گناهکار بودید.»'),
(3639, 34, 33, 'و کسانی که زیردست بودند به کسانی که [ریاست و] برتری داشتند، می‌گویند: «[نه،] بلکه نیرنگ شب و روز [شما بود] آنگاه که ما را وادار می‌کردید که به خدا کافر شویم و برای او همتایانی قرار دهیم.» و هنگامی که عذاب را ببینند پشیمانی خود را آشکار کنند. و در گردنهای کسانی که کافر شده‌اند غُلها می‌نهیم؛ آیا جز به سزای آنچه انجام می‌دادند می‌رسند؟'),
(3640, 34, 34, 'و [ما] در هیچ شهری هشداردهنده‌ای نفرستادیم جز آنکه خوشگذرانان آنها گفتند: «ما به آنچه شما بدان فرستاده شده‌اید کافریم.»'),
(3641, 34, 35, 'و گفتند: «ما دارایی و فرزندانمان از همه بیشتر است و ما عذاب نخواهیم شد.»'),
(3642, 34, 36, 'بگو: «پروردگار من است که روزی را برای هر کس که بخواهد گشاده یا تنگ می گرداند؛ لیکن بیشتر مردم نمی‌دانند.»'),
(3643, 34, 37, 'و اموال و فرزندانتان چیزی نیست که شما را به پیشگاه ما نزدیک گرداند، مگر کسانی که ایمان آورده و کار شایسته کرده باشند. پس برای آنان دو برابر آنچه انجام داده‌اند پاداش است و آنها در غرفه‌ها[ی بهشتی‌] آسوده خاطر خواهند بود.'),
(3644, 34, 38, 'و کسانی که در [ابطال‌] آیات ما می‌کوشند که [ما را به خیال خود] درمانده کنند؛ آنانند که در عذاب احضار می‌شوند.'),
(3645, 34, 39, 'بگو: «در حقیقت، پروردگار من است که روزی را برای هر کس از بندگانش که بخواهد گشاده یا برای او تنگ می‌گرداند. و هر چه را انفاق کردید عوضش را او می‌دهد. و او بهترین روزی دهندگان است.»'),
(3646, 34, 40, 'و [یاد کن‌] روزی را که همه آنان را محشور می‌کند، آنگاه به فرشتگان می‌فرماید: «آیا اینها بودند که شما را می‌پرستیدند؟»'),
(3647, 34, 41, 'می‌گویند: «منزّهی تو، سرپرست ما تویی نه آنها بلکه جنّیان را می‌پرستیدند؛ بیشترشان به آنها اعتقاد داشتند.»'),
(3648, 34, 42, 'اکنون برای یکدیگر سود و زیانی ندارید، و به کسانی که ستم کرده‌اند می‌گوییم: «بچشید عذاب آتشی را که آن را دروغ می‌شمردید.»'),
(3649, 34, 43, 'و چون آیات تابناک ما بر آنان خوانده می‌شود می‌گویند: «این جز مردی نیست که می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند باز دارد.» و [نیز] می‌گویند: «این جز دروغی بربافته نیست.» و کسانی که به حق -چون به سویشان آمد- کافر شدند می‌گویند: «این جز افسونی آشکار نیست.»'),
(3650, 34, 44, 'و ما کتابهایی به آنان نداده بودیم که آن را بخوانند، و پیش از تو هشداردهنده‌ای به سویشان نفرستاده بودیم.'),
(3651, 34, 45, 'و کسانی که پیش از اینان بودند، [نیز] تکذیب کردند، در حالی که اینان به ده‌یک آنچه بدیشان داده بودیم نرسیده‌اند. [آری،] فرستادگانِ مرا دروغ شمردند؛ پس چگونه بود کیفر من؟'),
(3652, 34, 46, 'بگو: «من فقط به شما یک اندرز می‌دهم که: دو دو و به تنهایی برای خدا به پا خیزید، سپس بیندیشید که رفیق شما هیچ گونه دیوانگی ندارد. او شما را از عذاب سختی که در پیش است جز هشداردهنده‌ای [بیش‌] نیست.'),
(3653, 34, 47, 'بگو: «هر مزدی که از شما خواستم آن از خودتان مزد من جز بر خدا نیست، و او بر هر چیزی گواه است.»'),
(3654, 34, 48, 'بگو: «بی‌گمان، پروردگارم حقیقت را القا می‌کند؛ [اوست‌] دانای نهانها.»'),
(3655, 34, 49, 'بگو: «حق آمد و [دیگر] باطل از سر نمی‌گیرد و برنمی‌گردد.»'),
(3656, 34, 50, 'بگو: «اگر گمراه شوم، فقط به زیان خود گمراه شده‌ام، و اگر هدایت یابم [این از برکتِ‌] چیزی است که پروردگارم به سویم وحی می‌کند، که اوست شنوای نزدیک.»'),
(3657, 34, 51, 'و ای کاش می‌دیدی هنگامی را که [کافران‌] وحشت‌زده‌اند [آنجا که راهِ‌] گریزی نمانده است و از جایی نزدیک گرفتار آمده‌اند.'),
(3658, 34, 52, 'و می‌گویند: «به او ایمان آوردیم.» و چگونه از جایی [چنین‌] دور، دست یافتن [به ایمان‌] برای آنان میسّر است؟'),
(3659, 34, 53, 'و حال آنکه پیش از این منکر او شدند، و از جایی دور، به نادیده [تیر تهمت‌] می‌افکندند.'),
(3660, 34, 54, 'و میان آنان و میان آنچه [به آرزو] می‌خواستند حایلی قرار می‌گیرد؛ همان گونه که از دیرباز با امثال ایشان چنین رفت، زیرا آنها [نیز] در دودلیِ سختی بودند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سپاس خدای را که پدیدآورنده آسمان و زمین است [و] فرشتگان را که دارای بالهای دوگانه و سه‌گانه و چهارگانه‌اند پیام‌آورنده قرار داده است. در آفرینش، هر چه بخواهد می‌افزاید، زیرا خدا بر هر چیزی تواناست.'),
(3662, 35, 2, 'هر رحمتی را که خدا برای مردم گشاید، بازدارنده‌ای برای آن نیست، و آنچه را که باز دارد، پس از [باز گرفتن‌] گشاینده‌ای ندارد، و اوست همان شکست‌ناپذیر سنجیده‌کار.'),
(3663, 35, 3, 'ای مردم، نعمت خدا را بر خود یاد کنید. آیا غیر از خدا آفریدگاری است که شما را از آسمان و زمین روزی دهد؟ خدایی جز او نیست. پس چگونه [از حق‌] انحراف می‌یابید؟'),
(3664, 35, 4, 'و اگر تو را تکذیب کنند، قطعاً پیش از تو [هم‌] فرستادگانی تکذیب شدند. و [همه‌] کارها به سوی خدا بازگردانیده می‌شود.'),
(3665, 35, 5, 'ای مردم، همانا وعده خدا حق است. زنهار تا این زندگی دنیا شما را فریب ندهد، و زنهار تا [شیطانِ‌] فریبنده شما را در باره خدا نفریبد.'),
(3666, 35, 6, 'در حقیقت، شیطان دشمن شماست، شما [نیز] او را دشمن گیرید. [او] فقط دار و دسته خود را می‌خوانَد تا آنها از یاران آتش باشند.'),
(3667, 35, 7, 'کسانی که کفر ورزیده‌اند، عذابی سخت خواهند داشت. و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان آمرزش و پاداشی بزرگ است.'),
(3668, 35, 8, 'آیا آن کس که زشتی کردارش برای او آراسته شده و آن را زیبا می‌بیند [مانند مؤمن نیکوکار است‌]؟ خداست که هر که را بخواهد بی‌راه می‌گذارد و هر که را بخواهد هدایت می‌کند. پس مبادا به سبب حسرتها[ی گوناگون‌] بر آنان، جانت [از کف ]برود؛ قطعاً خدا به آنچه می‌کنند داناست.'),
(3669, 35, 9, 'و خدا همان کسی است که بادها را روانه می‌کند؛ پس [بادها] ابری را برمی‌انگیزند، و [ما] آن را به سوی سرزمینی مرده راندیم، و آن زمین را بدان [وسیله‌]، پس از مرگش زندگی بخشیدیم؛ رستاخیز [نیز ]چنین است.'),
(3670, 35, 10, 'هر کس سربلندی می‌خواهد، سربلندی یکسره از آنِ خداست. سخنان پاکیزه به سوی او بالا می‌رود، و کار شایسته به آن رفعت می‌بخشد. و کسانی که با حیله و مکر کارهای بد می‌کنند، عذابی سخت خواهند داشت، و نیرنگشان خود تباه می‌گردد.'),
(3671, 35, 11, 'و خدا[ست که‌] شما را از خاکی آفرید، سپس از نطفه‌ای، آنگاه شما را جفت جفت گردانید، و هیچ مادینه‌ای بار نمی‌گیرد و بار نمی‌نهد مگر به علم او. و هیچ سالخورده‌ای عمر دراز نمی‌یابد و از عمرش کاسته نمی‌شود، مگر آنکه در کتابی [مندرج ]است. در حقیقت، این [کار] بر خدا آسان است!'),
(3672, 35, 12, 'و دو دریا یکسان نیستند: این یک، شیرین تشنگی‌زدا [و] نوشیدنش گواراست؛ و آن یک، شور تلخ‌مزه است؛ و از هر یک گوشتی تازه می‌خورید و زیوری که آن را بر خود می‌پوشید بیرون می‌آورید؛ و کشتی را در آن، موج‌شکاف می‌بینی تا از فضل او [روزی خود را] جستجو کنید، و امید که سپاس بگزارید.'),
(3673, 35, 13, 'شب را به روز درمی‌آورد و روز را به شب درمی‌آورد، و آفتاب و ماه را تسخیر کرده است [که‌] هر یک تا هنگامی معین روانند؛ این است خدا پروردگار شما؛ فرمانروایی از آنِ اوست. و کسانی را که بجز او می‌خوانید، مالک پوست هسته خرمایی [هم ]نیستند.'),
(3674, 35, 14, 'اگر آنها را بخوانید، دعای شما را نمی‌شنوند، و اگر [فرضاً] بشنوند اجابتتان نمی‌کنند، و روز قیامت شرکِ شما را انکار می‌کنند؛ و [هیچ کس‌] چون [خدای‌] آگاه، تو را خبردار نمی‌کند.'),
(3675, 35, 15, 'ای مردم، شما به خدا نیازمندید، و خداست که بی‌نیاز ستوده است.'),
(3676, 35, 16, 'و اگر بخواهد شما را می‌بَرد و خلقی نو [بر سر کار] می‌آورد.'),
(3677, 35, 17, 'و این [امر] برای خدا دشوار نیست.'),
(3678, 35, 18, 'و هیچ باربَردارنده‌ای بار [گناه‌] دیگری را برنمی‌دارد، و اگر گرانباری [دیگری را به یاری‌] به سوی بارش فرا خواند چیزی از آن برداشته نمی‌شود، هر چند خویشاوند باشد. [تو] تنها کسانی را که از پروردگارشان در نهان می‌ترسند و نماز برپا می‌دارند، هشدار می‌دهی؛ و هر کس پاکیزگی جوید تنها برای خود پاکیزگی می‌جوید، و فرجام [کارها] به سوی خداست.'),
(3679, 35, 19, 'و نابینا و بینا یکسان نیستند،'),
(3680, 35, 20, 'و نه تیرگیها و روشنایی،'),
(3681, 35, 21, 'و نه سایه و گرمای آفتاب.'),
(3682, 35, 22, 'و زندگان و مردگان یکسان نیستند. خداست که هر که را بخواهد شنوا می‌گرداند؛ و تو کسانی را که در گورهایند نمی‌توانی شنوا سازی.'),
(3683, 35, 23, 'تو جز هشداردهنده‌ای [بیش‌] نیستی.'),
(3684, 35, 24, 'ما تو را بحق، [به سِمَت‌] بشارتگر و هشداردهنده گسیل داشتیم، و هیچ امتی نبوده مگر اینکه در آن هشداردهنده‌ای گذشته است.'),
(3685, 35, 25, 'و اگر تو را تکذیب کنند، قطعاً کسانی که پیش از آنها بودند [نیز] به تکذیب پرداختند. پیامبرانشان دلایل آشکار و نوشته‌ها و کتاب روشن برای آنان آوردند.'),
(3686, 35, 26, 'آنگاه کسانی را که کافر شده بودند فرو گرفتم؛ پس چگونه بود کیفر من؟'),
(3687, 35, 27, 'آیا ندیده‌ای که خدا از آسمان، آبی فرود آورد و به [وسیله‌] آن میوه‌هایی که رنگهای آنها گوناگون است بیرون آوردیم؟ و از برخی کوهها، راهها [و رگه‌ها]ی سپید و گلگون به رنگهای مختلف و سیاه پر رنگ [آفریدیم‌].'),
(3688, 35, 28, 'و از مردمان و جانوران و دامها که رنگهایشان همان گونه مختلف است [پدید آوردیم‌]. از بندگان خدا تنها دانایانند که از او می‌ترسند. آری، خدا ارجمند آمرزنده است.'),
(3689, 35, 29, 'در حقیقت، کسانی که کتاب خدا را می‌خوانند و نماز برپا می‌دارند و از آنچه بدیشان روزی داده‌ایم، نهان و آشکارا انفاق می‌کنند، امید به تجارتی بسته‌اند که هرگز زوال نمی‌پذیرد.'),
(3690, 35, 30, 'تا پاداششان را تمام بدیشان عطا کند و از فزون‌بخشی خود در حق آنان بیفزاید که او آمرزنده حق‌شناس است.'),
(3691, 35, 31, 'و آنچه از کتاب به سوی تو وحی کرده‌ایم، خود حق [و] تصدیق‌کننده [کتابهای‌] پیش از آن است. قطعاً خدا نسبت به بندگانش آگاه بیناست.'),
(3692, 35, 32, 'سپس این کتاب را به آن بندگان خود که [آنان را] برگزیده بودیم، به میراث دادیم؛ پس برخی از آنان بر خود ستمکارند و برخی از ایشان میانه‌رو، و برخی از آنان در کارهای نیک به فرمان خدا پیشگامند؛ و این خود توفیق بزرگ است.'),
(3693, 35, 33, '[در] بهشتهای همیشگی [که‌] به آنها درخواهندآمد. در آنجا با دستبندهایی از زر و مروارید زیور یابند و در آنجا جامه‌شان پَرنیان خواهد بود.'),
(3694, 35, 34, 'و می‌گویند: «سپاس خدایی را که اندوه را از ما بزدود، به راستی پروردگار ما آمرزنده [و] حق‌شناس است؛'),
(3695, 35, 35, 'همان [خدایی‌] که ما را به فضل خویش در سرای ابدی جای داد. در اینجا رنجی به ما نمی‌رسد و در اینجا درماندگی به ما دست نمی‌دهد.'),
(3696, 35, 36, 'و[لی‌] کسانی که کافر شده‌اند، آتش جهنم برای آنان خواهد بود. حکم به مرگ بر ایشان [جاری‌] نمی‌شود تا بمیرند، و نه عذاب آن از ایشان کاسته شود. [آری،] هر ناسپاسی را چنین کیفر می‌دهیم.'),
(3697, 35, 37, 'و آنان در آنجا فریاد برمی‌آورند: «پروردگارا، ما را بیرون بیاور، تا غیر از آنچه می‌کردیم، کار شایسته کنیم.» مگر شما را [آن قدر] عمر دراز ندادیم که هر کس که باید در آن عبرت گیرد، عبرت می‌گرفت؛ و [آیا ]برای شما هشداردهنده نیامد؟ پس بچشید که برای ستمگران یاوری نیست.'),
(3698, 35, 38, 'خدا[ست که‌] دانای نهان آسمانها و زمین است، و اوست که به راز دلها داناست.'),
(3699, 35, 39, 'اوست آن کس که شما را در این سرزمین جانشین گردانید. پس هر کس کفر ورزد کفرش به زیان اوست، و کافران را کفرشان جز دشمنی نزد پروردگارشان نمی‌افزاید، و کافران را کفرشان غیر از زیان نمی‌افزاید.'),
(3700, 35, 40, 'بگو: «به من خبر دهید از شریکان خودتان که به جای خدا می‌خوانید؛ به من نشان دهید که چه چیزی از زمین را آفریده‌اند؟ یا آنان در [کار] آسمانها همکاری داشته‌اند؟ یا به ایشان کتابی داده‌ایم که دلیلی بر [حقّانیّت‌] خود از آن دارند؟» [نه،] بلکه ستمکاران جز فریب به یکدیگر وعده نمی‌دهند.'),
(3701, 35, 41, 'همانا خدا آسمانها و زمین را نگاه می‌دارد تا نیفتند، و اگر بیفتند بعد از او هیچ کس آنها را نگاه نمی‌دارد؛ اوست بردبار آمرزنده.'),
(3702, 35, 42, 'و با سوگندهای سخت خود به خدا سوگند یاد کردند که اگر هرآینه هشداردهنده‌ای برای آنان بیاید، قطعاً از هر یک از امتها[ی دیگر] راه‌یافته‌تر شوند، و[لی‌] چون هشداردهنده‌ای برای ایشان آمد، جز بر نفرتشان نیفزود.'),
(3703, 35, 43, '[انگیزه‌] این کارشان فقط گردنکشی در [روی‌] زمین و نیرنگ زشت بود، و نیرنگ زشت جز [دامن‌] صاحبش را نگیرد. پس آیا جز سنّت [و سرنوشت شوم‌] پیشینیان را انتظار می‌برند؟ و هرگز برای سنّت خدا دگرگونی نخواهی یافت.'),
(3704, 35, 44, 'آیا در زمین نگردیده‌اند تا فرجام [کار] کسانی را که پیش از ایشان [زیسته‌] و نیرومندتر از ایشان بودند بنگرند؟ و هیچ چیز، نه در آسمانها و نه در زمین، خدا را درمانده نکرده است، چرا که او همواره دانای تواناست.'),
(3705, 35, 45, 'و اگر خدا مردم را به [سزای‌] آنچه انجام داده‌اند مؤاخذه می‌کرد، هیچ جنبنده‌ای را بر پشت زمین باقی نمی‌گذاشت؛ ولی تا مدتی معین مهلتشان می‌دهد، و چون اجلشان فرا رسد خدا به [کار] بندگانش بیناست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یس‌[/یاسین‌]'),
(3707, 36, 2, 'سوگند به قرآن حکمت‌آموز،'),
(3708, 36, 3, 'که قطعاً تو از [جمله‌] پیامبرانی،'),
(3709, 36, 4, 'بر راهی راست!'),
(3710, 36, 5, '[و کتابت‌] از جانب آن عزیز مهربان نازل شده است،'),
(3711, 36, 6, 'تا قومی را که پدرانشان بیم‌داده نشدند و در غفلت ماندند، بیم دهی.'),
(3712, 36, 7, 'آری، گفته [خدا] در باره بیشترشان محقق گردیده است، در نتیجه آنها نخواهند گروید.'),
(3713, 36, 8, 'ما در گردنهای آنان، تا چانه‌هایشان، غُلهایی نهاده‌ایم، به طوری که سرهایشان را بالا نگاه داشته و دیده فرو هشته‌اند.'),
(3714, 36, 9, 'و [ما] فراروی آنها سدی و پشت سرشان سدی نهاده و پرده‌ای بر [چشمان‌] آنان فرو گسترده‌ایم، در نتیجه نمی‌توانند ببینند.'),
(3715, 36, 10, 'و آنان را چه بیم دهی [و] چه بیم ندهی، به حالشان تفاوت نمی‌کند: نخواهند گروید.'),
(3716, 36, 11, 'بیم دادن تو، تنها کسی را [سودمند] است که کتاب حق را پیروی کند و از [خدای‌] رحمان در نهان بترسد. [چنین کسی را] به آمرزش و پاداشی پر ارزش مژده ده.'),
(3717, 36, 12, 'آری! ماییم که مردگان را زنده می‌سازیم و آنچه را از پیش فرستاده‌اند، با آثار [و اعمال‌]شان درج می‌کنیم، و هر چیزی را در کارنامه‌ای روشن برشمرده‌ایم.'),
(3718, 36, 13, '[داستان‌] مردم آن شهری را که رسولان بدانجا آمدند برای آنان مَثَل زن:'),
(3719, 36, 14, 'آنگاه که دو تن سوی آنان فرستادیم، و[لی‌] آن دو را دروغزن پنداشتند، تا با [فرستاده‌] سومین [آنان را] تأیید کردیم، پس [رسولان‌] گفتند: «ما به سوی شما به پیامبری فرستاده شده‌ایم.»'),
(3720, 36, 15, '[ناباوران آن دیار] گفتند: «شما جز بشری مانند ما نیستید، و [خدای‌] رحمان چیزی نفرستاده، و شما جز دروغ نمی‌پردازید.»'),
(3721, 36, 16, 'گفتند: «پروردگار ما می‌داند که ما واقعاً به سوی شما به پیامبری فرستاده شده‌ایم.»'),
(3722, 36, 17, 'و بر ما [وظیفه‌ای‌] جز رسانیدن آشکار [پیام‌] نیست.'),
(3723, 36, 18, 'پاسخ دادند: «ما [حضور] شما را به شگون بد گرفته‌ایم. اگر دست برندارید، سنگسارتان می‌کنیم و قطعاً عذاب دردناکی از ما به شما خواهد رسید.»'),
(3724, 36, 19, '[رسولان‌] گفتند: «شومیِ شما با خود شماست. آیا اگر شما را پند دهند [باز کفر می‌ورزید]؟ نه! بلکه شما قومی اسرافکارید.»'),
(3725, 36, 20, 'و [در این میان‌] مردی از دورترین جای شهر دوان دوان آمد، [و] گفت: «ای مردم، از این فرستادگان پیروی کنید.'),
(3726, 36, 21, 'از کسانی که پاداشی از شما نمی‌خواهند و خود [نیز] بر راه راست قرار دارند، پیروی کنید.'),
(3727, 36, 22, 'آخر چرا کسی را نپرستم که مرا آفریده است و [همه‌] شما به سوی او بازگشت می‌یابید؟'),
(3728, 36, 23, 'آیا به جای او خدایانی را بپرستم که اگر [خدای‌] رحمان بخواهد به من گزندی برساند، نه شفاعتشان به حالم سود می‌دهد و نه می‌توانند مرا برهانند؟'),
(3729, 36, 24, 'در آن صورت، من قطعاً در گمراهی آشکاری خواهم بود.'),
(3730, 36, 25, 'من به پروردگارتان ایمان آوردم. [اقرار] مرا بشنوید.»'),
(3731, 36, 26, '[سرانجام به جرم ایمان کشته شد، و بدو] گفته شد: «به بهشت درآی.» گفت: «ای کاش، قوم من می‌دانستند،'),
(3732, 36, 27, 'که پروردگارم چگونه مرا آمرزید و در زمره عزیزانم قرار داد.»'),
(3733, 36, 28, 'پس از [شهادت‌] وی هیچ سپاهی از آسمان بر قومش فرود نیاوردیم و [پیش از این هم‌] فروفرستنده نبودیم.'),
(3734, 36, 29, 'تنها یک فریاد بود و بس. و بناگاه [همه‌] آنها سرد بر جای فسردند.'),
(3735, 36, 30, 'دریغا بر این بندگان! هیچ فرستاده‌ای بر آنان نیامد مگر آنکه او را ریشخند می‌کردند.'),
(3736, 36, 31, 'مگر ندیده‌اند که چه بسیار نسلها را پیش از آنان هلاک گردانیدیم که دیگر آنها به سویشان بازنمی‌گردند؟'),
(3737, 36, 32, 'و قطعاً همه آنان در پیشگاه ما احضار خواهند شد.'),
(3738, 36, 33, 'و زمین مرده، برهانی است برای ایشان، که آن را زنده گردانیدیم و دانه از آن برآوردیم که از آن می‌خورند.'),
(3739, 36, 34, 'و در آن [زمین‌] باغهایی از درختان خرما و تاک قرار دادیم و چشمه‌ها در آن روان کردیم.'),
(3740, 36, 35, 'تا از میوه آن و [از] کارکرد دستهای خودشان بخورند، آیا باز [هم‌] سپاس نمی‌گزارند؟'),
(3741, 36, 36, 'پاک [خدایی‌] که از آنچه زمین می‌رویاند و [نیز] از خودشان و از آنچه نمی‌دانند، همه را نر و ماده گردانیده است.'),
(3742, 36, 37, 'و نشانه‌ای [دیگر] برای آنها شب است که روز را [مانند پوست‌] از آن برمی‌کنیم و بناگاه آنان در تاریکی فرو می‌روند.'),
(3743, 36, 38, 'و خورشید به [سوی‌] قرارگاه ویژه خود روان است. تقدیر آن عزیز دانا این است.'),
(3744, 36, 39, 'و برای ماه منزلهایی معین کرده‌ایم، تا چون شاخک خشک خوشه خرما برگردد.'),
(3745, 36, 40, 'نه خورشید را سزد که به ماه رسد، و نه شب بر روز پیشی جوید، و هر کدام در سپهری شناورند.'),
(3746, 36, 41, 'و نشانه‌ای [دیگر] برای آنان اینکه: ما نیاکانشان را در کشتی انباشته، سوار کردیم.'),
(3747, 36, 42, 'و مانند آن برای ایشان مرکوبها[ی دیگری‌] خلق کردیم.'),
(3748, 36, 43, 'و اگر بخواهیم غرقشان می‌کنیم و هیچ فریادرسی نمی‌یابند و روی نجات نمی‌بینند.'),
(3749, 36, 44, 'مگر رحمتی از جانب ما [شامل آنها گردد] و تا چندی [آنها را] برخوردار سازیم.'),
(3750, 36, 45, 'و چون به ایشان گفته شود: «از آنچه در پیش رو و پشت سر دارید بترسید، امید که مورد رحمت قرار گیرید» [نمی‌شنوند].'),
(3751, 36, 46, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان بر آنان نیامد، جز اینکه از آن رویگردان شدند.'),
(3752, 36, 47, 'و چون به آنان گفته شود: «از آنچه خدا به شما روزی داده انفاق کنید»، کسانی که کافر شده‌اند، به آنان که ایمان آورده‌اند، می‌گویند: «آیا کسی را بخورانیم که اگر خدا می‌خواست [خودش‌] وی را می‌خورانید؟ شما جز در گمراهیِ آشکاری [بیش‌] نیستید.»'),
(3753, 36, 48, 'و می‌گویند: «اگر راست می‌گویید، پس این وعده [عذاب‌] کی خواهد بود؟»'),
(3754, 36, 49, 'جز یک فریاد [مرگبار] را انتظار نخواهند کشید که هنگامی که سرگرم جدالند غافلگیرشان کند.'),
(3755, 36, 50, 'آنگاه نه توانایی وصیّتی دارند و نه می‌توانند به سوی کسان خود برگردند.'),
(3756, 36, 51, 'و در صور دمیده خواهد شد، پس بناگاه از گورهای خود شتابان به سوی پروردگار خویش می‌آیند.'),
(3757, 36, 52, 'می‌گویند: «ای وای بر ما، چه کسی ما را از آرامگاهمان برانگیخت؟ این است همان وعده خدای رحمان، و پیامبران راست می‌گفتند.»'),
(3758, 36, 53, '[باز هم‌] یک فریاد است و بس؛ و بناگاه همه در پیشگاه ما حاضر آیند.'),
(3759, 36, 54, 'امروز بر کسی هیچ ستم نمی‌رود، جز در برابر آنچه کرده‌اید پاداشی نخواهید یافت.'),
(3760, 36, 55, 'در این روز، اهل بهشت کار و باری خوش در پیش دارند.'),
(3761, 36, 56, 'آنها با همسرانشان در زیر سایه‌ها بر تختها تکیه می‌زنند.'),
(3762, 36, 57, 'در آنجا برای آنها [هر گونه‌] میوه است و هر چه دلشان بخواهد.'),
(3763, 36, 58, 'از جانب پروردگار[ی‌] مهربان [به آنان‌] سلام گفته می‌شود.'),
(3764, 36, 59, 'و ای گناهکاران، امروز [از بی‌گناهان‌] جدا شوید.'),
(3765, 36, 60, 'ای فرزندان آدم، مگر با شما عهد نکرده بودم که شیطان را مپرستید، زیرا وی دشمن آشکار شماست؟'),
(3766, 36, 61, 'و اینکه مرا بپرستید؛ این است راه راست!'),
(3767, 36, 62, 'و [او] گروهی انبوه از میان شما را سخت گمراه کرد؛ آیا تعقل نمی‌کردید؟'),
(3768, 36, 63, 'این است جهنّمی که به شما وعده داده می‌شد!'),
(3769, 36, 64, 'به [جرم‌] آنکه کفر می‌ورزیدید، اکنون در آن درآیید.'),
(3770, 36, 65, 'امروز بر دهانهای آنان مُهر می‌نهیم، و دستهایشان با ما سخن می‌گویند، و پاهایشان بدانچه فراهم می‌ساختند گواهی می‌دهند.'),
(3771, 36, 66, 'و اگر بخواهیم، هر آینه فروغ از دیدگانشان می‌گیریم تا در راه [کج‌] بر هم پیشی جویند؛ ولی [راه راست را] از کجا می‌توانند ببینند؟'),
(3772, 36, 67, 'و اگر بخواهیم، هرآینه ایشان را در جای خود مسخ می‌کنیم [به گونه‌ای‌] که نه بتوانند بروند و نه برگردند.'),
(3773, 36, 68, 'و هر که را عمر دراز دهیم، او را [از نظر] خلقت فروکاسته [و شکسته‌] گردانیم، آیا نمی‌اندیشند؟'),
(3774, 36, 69, 'و [ما] به او شعر نیاموختیم و در خور وی نیست، این [سخن‌] جز اندرز و قرآنی روشن نیست.'),
(3775, 36, 70, 'تا هر که را [دلی‌] زنده است بیم دهد، و گفتار [خدا] در باره کافران محقق گردد.'),
(3776, 36, 71, 'آیا ندیده‌اند که ما به قدرت خویش برای ایشان چهارپایانی آفریده‌ایم تا آنان مالک آنها باشند؟'),
(3777, 36, 72, 'و آنها را برای ایشان رام گردانیدیم. از برخی‌شان سواری می‌گیرند و از بعضی می‌خورند.'),
(3778, 36, 73, 'و از آنها سودها و نوشیدنیها دارند. پس چرا شکرگزار نیستید؟'),
(3779, 36, 74, 'و غیر از خدا[ی یگانه‌] خدایانی به پرستش گرفتند، تا مگر یاری شوند.'),
(3780, 36, 75, '[ولی بتان‌] نمی‌توانند آنان را یاری کنند و آنانند که برای [بتان‌] چون سپاهی احضار شده‌اند.'),
(3781, 36, 76, 'پس، گفتار آنان تو را غمگین نگرداند که ما آنچه را پنهان و آنچه را آشکار می‌کنند، می‌دانیم.'),
(3782, 36, 77, 'مگر آدمی ندانسته است که ما او را از نطفه‌ای آفریده‌ایم، پس بناگاه وی ستیزه‌جویی آشکار شده است.'),
(3783, 36, 78, 'و برای ما مَثَلی آورد و آفرینش خود را فراموش کرد؛ گفت: «چه کسی این استخوانها را که چنین پوسیده است زندگی می‌بخشد؟»'),
(3784, 36, 79, 'بگو: «همان کسی که نخستین‌بار آن را پدید آورد و اوست که به هر [گونه‌] آفرینشی داناست.»'),
(3785, 36, 80, 'همو که برایتان در درخت سبزفام اخگر نهاد که از آن [چون نیازتان افتد] آتش می‌افروزید.'),
(3786, 36, 81, 'آیا کسی که آسمانها و زمین را آفریده توانا نیست که [باز] مانند آنها را بیافریند؟ آری، اوست آفریننده دانا.'),
(3787, 36, 82, 'چون به چیزی اراده فرماید، کارش این بس که می‌گوید: «باش»؛ پس [بی‌درنگ‌] موجود می‌شود.'),
(3788, 36, 83, 'پس [شکوهمند و] پاک است آن کسی که ملکوت هر چیزی در دست اوست، و به سوی اوست که بازگردانیده می‌شوید.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به صف بستگان -که صفی [با شکوه‌] بسته‌اند-'),
(3790, 37, 2, 'و به زجرکنندگان -که به سختی زجر می‌کنند-'),
(3791, 37, 3, 'و به تلاوت‌کنندگان [آیات الهی‌]!'),
(3792, 37, 4, 'که قطعاً معبود شما یگانه است!'),
(3793, 37, 5, 'پروردگار آسمانها و زمین و آنچه میان آن دو است، و پروردگار خاورها!'),
(3794, 37, 6, 'ما آسمان این دنیا را به زیور اختران آراستیم!'),
(3795, 37, 7, 'و [آن را] از هر شیطان سرکشی نگاه داشتیم!'),
(3796, 37, 8, '[به طوری که‌] نمی‌توانند به انبوه [فرشتگان‌] عالَم بالا گوش فرا دهند، و از هر سوی پرتاب می‌شوند.'),
(3797, 37, 9, 'با شدت به دور رانده می‌شوند، و برایشان عذابی دایم است.'),
(3798, 37, 10, 'مگر کسی که [از سخن بالاییان‌] یکباره استراق سمع کند، که شهابی شکافنده از پی او می‌تازد!'),
(3799, 37, 11, 'پس، [از کافران‌] بپرس: آیا ایشان [از نظر] آفرینش سخت‌ترند یا کسانی که [در آسمانها] خلق کردیم؟ ما آنان را از گِلی چسبنده پدید آوردیم.'),
(3800, 37, 12, 'بلکه عجب می‌داری و [آنها] ریشخند می‌کنند!'),
(3801, 37, 13, 'و چون پند داده شوند عبرت نمی‌گیرند.'),
(3802, 37, 14, 'و چون آیتی ببینند به ریشخند می‌پردازند!'),
(3803, 37, 15, 'و می‌گویند: «این جز سحری آشکار نیست.»'),
(3804, 37, 16, '«آیا چون مردیم و خاک و استخوانهای [خُرد] گردیدیم، آیا راستی برانگیخته می‌شویم؟'),
(3805, 37, 17, 'و همین طور پدران اوّلیه ما؟!»'),
(3806, 37, 18, 'بگو: «آری! در حالی که شما خوارید!»'),
(3807, 37, 19, 'و آن تنها یک فریاد است و بس! و بناگاه آنان به تماشا خیزند!'),
(3808, 37, 20, 'و می‌گویند: «ای وای بر ما! این است روز جزا!»'),
(3809, 37, 21, 'این است همان روز داوری که آن را تکذیب می‌کردید!'),
(3810, 37, 22, 'کسانی را که ستم کرده‌اند، با همردیفانشان و آنچه غیر از خدا می‌پرستیده‌اند،'),
(3811, 37, 23, 'گِرد آورید و به سوی راه جهنم رهبری‌شان کنید!'),
(3812, 37, 24, 'و بازداشتشان نمایید که آنها مسؤولند!'),
(3813, 37, 25, 'شما را چه شده است که همدیگر را یاری نمی‌کنید؟!'),
(3814, 37, 26, '[نه!] بلکه امروز آنان از در تسلیم درآمدگانند!'),
(3815, 37, 27, 'و بعضی روی به بعضی دیگر می‌آورند [و] از یکدیگر می‌پرسند!'),
(3816, 37, 28, '[و] می‌گویند: «شما [ظاهراً] از درِ راستی با ما درمی‌آمدید [و خود را حق به جانب می‌نمودید]!»'),
(3817, 37, 29, '[متّهمان‌] می‌گویند: «[نه!] بلکه با ایمان نبودید.'),
(3818, 37, 30, 'و ما را بر شما هیچ تسلطی نبود، بلکه خودتان سرکش بودید.'),
(3819, 37, 31, 'پس فرمان پروردگارمان بر ما سزاوار آمد؛ ما واقعاً باید [عذاب را] بچشیم!'),
(3820, 37, 32, 'و شما را گمراه کردیم، زیرا خودمان گمراه بودیم!»'),
(3821, 37, 33, 'پس، در حقیقت، آنان در آن روز در عذاب شریک یکدیگرند!'),
(3822, 37, 34, '[آری،] ما با مجرمان چنین رفتار می‌کنیم!'),
(3823, 37, 35, 'چرا که آنان بودند که وقتی به ایشان گفته می‌شد: «خدایی جز خدای یگانه نیست»، تکبر می‌ورزیدند!'),
(3824, 37, 36, 'و می‌گفتند: «آیا ما برای شاعری دیوانه دست از خدایانمان برداریم؟!»'),
(3825, 37, 37, 'ولی نه! [او] حقیقت را آورده و فرستادگان را تصدیق کرده است.'),
(3826, 37, 38, 'در واقع، شما عذاب پر درد را خواهید چشید!'),
(3827, 37, 39, 'و جز آنچه می‌کردید جزا نمی‌یابید!'),
(3828, 37, 40, 'مگر بندگان پاکدل خدا!'),
(3829, 37, 41, 'آنان روزیِ معیّن خواهند داشت.'),
(3830, 37, 42, '[انواع‌] میوه‌ها! و آنان مورد احترام خواهند بود.'),
(3831, 37, 43, 'در باغهای پر نعمت!'),
(3832, 37, 44, 'بر سریرها در برابر همدیگر [می‌نشینند].'),
(3833, 37, 45, 'با جامی از باده ناب پیرامونشان به گردش درمی‌آیند؛'),
(3834, 37, 46, '[باده‌ای‌] سخت سپید که نوشندگان را لذتی [خاص‌] می‌دهد؛'),
(3835, 37, 47, 'نه در آن فساد عقل است و نه ایشان از آن به بدمستی [و فرسودگی‌] می‌افتند!'),
(3836, 37, 48, 'و نزدشان [دلبرانی‌] فروهشته‌نگاه و فراخ‌دیده باشند!'),
(3837, 37, 49, '[از شدّت سپیدی‌] گویی تخم شتر مرغ [زیر پَرَ]ند!'),
(3838, 37, 50, 'پس برخی‌شان به برخی روی نموده و از همدیگر پرس‌وجو می‌کنند.'),
(3839, 37, 51, 'گوینده‌ای از آنان می‌گوید: «راستی من [در دنیا] همنشینی داشتم،'),
(3840, 37, 52, '[که به من‌] می‌گفت: «آیا واقعاً تو از باوردارندگانی؟'),
(3841, 37, 53, 'آیا وقتی مُردیم و خاک و [مشتی‌] استخوان شدیم، آیا واقعاً جزا می‌یابیم؟»'),
(3842, 37, 54, '[مؤمن‌] می‌پرسد: «آیا شما اطلاع دارید [کجاست‌]؟»'),
(3843, 37, 55, 'پس اطلاع حاصل می‌کند، و او را در میان آتش می‌بیند!'),
(3844, 37, 56, '[و] می‌گوید: «به خدا سوگند، چیزی نمانده بود که تو مرا به هلاکت اندازی.'),
(3845, 37, 57, 'و اگر رحمت پروردگارم نبود، هرآینه من [نیز] از احضارشدگان بودم.»'),
(3846, 37, 58, '[و از روی شوق می‌گوید:] «آیا دیگر روی مرگ نمی‌بینیم،'),
(3847, 37, 59, 'جز همان مرگ نخستین خود؟ و ما هرگز عذاب نخواهیم شد؟!'),
(3848, 37, 60, 'راستی که این همان کامیابی بزرگ است!»'),
(3849, 37, 61, 'برای چنین [پاداشی‌] باید کوشندگان بکوشند.'),
(3850, 37, 62, 'آیا از نظر پذیرایی این بهتر است یا درخت زقوم؟!'),
(3851, 37, 63, 'در حقیقت، ما آن را برای ستمگران [مایه آزمایش و] عذابی گردانیدیم.'),
(3852, 37, 64, 'آن، درختی است که از قعر آتش سوزان می‌روید،'),
(3853, 37, 65, 'میوه‌اش گویی چون کله‌های شیاطین است،'),
(3854, 37, 66, 'پس [دوزخیان‌] حتماً از آن می‌خورند و شکمها را از آن پر می‌کنند،'),
(3855, 37, 67, 'سپس ایشان را بر سر آن، آمیغی از آب جوشان است؛'),
(3856, 37, 68, 'آنگاه بازگشتشان بی‌گمان به سوی دوزخ است.'),
(3857, 37, 69, 'آنها پدران خود را گمراه یافتند،'),
(3858, 37, 70, 'پس ایشان به دنبال آنها می‌شتابند!'),
(3859, 37, 71, 'و قطعاً پیش از آنها بیشتر پیشینیان به گمراهی افتادند.'),
(3860, 37, 72, 'و حال آنکه مسلماً در میانشان هشداردهندگانی فرستادیم.'),
(3861, 37, 73, 'پس ببین فرجام هشدارداده‌شدگان چگونه بود!'),
(3862, 37, 74, 'به استثنای بندگان پاکدل خدا!'),
(3863, 37, 75, 'و نوح، ما را ندا داد، و چه نیک اجابت‌کننده بودیم!'),
(3864, 37, 76, 'و او و کسانش را از اندوه بزرگ رهانیدیم.'),
(3865, 37, 77, 'و [تنها] نسل او را باقی گذاشتیم.'),
(3866, 37, 78, 'و در میان آیندگان [آوازه نیک‌] او را بر جای گذاشتیم.'),
(3867, 37, 79, 'درود بر نوح در میان جهانیان!'),
(3868, 37, 80, 'ما این گونه نیکوکاران را پاداش می‌دهیم.'),
(3869, 37, 81, 'به راستی او از بندگان مؤمن ما بود.'),
(3870, 37, 82, 'سپس دیگران را غرق کردیم.'),
(3871, 37, 83, 'و بی‌گمان، ابراهیم از پیروان اوست.'),
(3872, 37, 84, 'آنگاه که با دلی پاک به [پیشگاه‌] پروردگارش آمد.'),
(3873, 37, 85, 'چون به پدر[خوانده‌] و قوم خود گفت: «چه می‌پرستید؟'),
(3874, 37, 86, 'آیا غیر از آنها، به دروغ، خدایانی [دیگر] می‌خواهید؟!'),
(3875, 37, 87, 'پس گمانتان به پروردگار جهانها چیست؟»'),
(3876, 37, 88, 'پس نظری به ستارگان افکند،'),
(3877, 37, 89, 'و گفت: «من کسالت دارم!»'),
(3878, 37, 90, 'پس پشت‌کنان از او روی برتافتند!'),
(3879, 37, 91, 'تا نهانی به سوی خدایانشان رفت و [به ریشخند] گفت: «آیا غذا نمی‌خورید؟'),
(3880, 37, 92, 'شما را چه شده که سخن نمی‌گویید؟!»'),
(3881, 37, 93, 'پس با دست راست، بر سر آنها زدن گرفت!'),
(3882, 37, 94, 'تا دوان دوان سوی او روی‌آور شدند.'),
(3883, 37, 95, '[ابراهیم‌] گفت: «آیا آنچه را می‌تراشید، می‌پرستید؟'),
(3884, 37, 96, 'با اینکه خدا شما و آنچه را که برمی‌سازید آفریده است!»'),
(3885, 37, 97, 'گفتند: «برایش [کوره‌]خانه‌ای بسازید و در آتشش بیندازید»'),
(3886, 37, 98, 'پس خواستند به او نیرنگی زنند؛ و[لی‌] ما آنان را پست گردانیدیم.'),
(3887, 37, 99, 'و [ابراهیم‌] گفت: «من به سوی پروردگارم رهسپارم، زودا که مرا راه نماید!»'),
(3888, 37, 100, '«ای پروردگار من! مرا [فرزندی‌] از شایستگان بخش.»'),
(3889, 37, 101, 'پس او را به پسری بردبار مژده دادیم.'),
(3890, 37, 102, 'و وقتی با او به جایگاه «سعی» رسید، گفت: «ای پسرک من! من در خواب [چنین‌] می‌بینم که تو را سَرْ می‌بُرم، پس ببین چه به نظرت می‌آید؟» گفت: «ای پدر من! آنچه را مأموری بکن! ان شاء الله مرا از شکیبایان خواهی یافت.»'),
(3891, 37, 103, 'پس وقتی هر دو تن دردادند [و همدیگر را بدرود گفتند] و [پسر] را به پیشانی بر خاک افکند،'),
(3892, 37, 104, 'او را ندا دادیم که ای ابراهیم!'),
(3893, 37, 105, 'رؤیا[ی خود] را حقیقت بخشیدی! ما نیکوکاران را چنین پاداش می‌دهیم!'),
(3894, 37, 106, 'راستی که این همان آزمایش آشکار بود!'),
(3895, 37, 107, 'و او را در ازای قربانی بزرگی باز رهانیدیم.'),
(3896, 37, 108, 'و در [میان‌] آیندگان برای او [آوازه نیک‌] به جای گذاشتیم.'),
(3897, 37, 109, 'درود بر ابراهیم!'),
(3898, 37, 110, 'نیکوکاران را چنین پاداش می‌دهیم.'),
(3899, 37, 111, 'در حقیقت، او از بندگان با ایمان ما بود.'),
(3900, 37, 112, 'و او را به اسحاق که پیامبری از [جمله‌] شایستگان است مژده دادیم.'),
(3901, 37, 113, 'و به او و به اسحاق برکت دادیم، و از نسل آن دو برخی نیکوکار و [برخی‌] آشکارا به خود ستمکار بودند.'),
(3902, 37, 114, 'و در حقیقت، بر موسی و هارون منت نهادیم.'),
(3903, 37, 115, 'و آن دو و قومشان را از اندوه بزرگ رهانیدیم.'),
(3904, 37, 116, 'و آنان را یاری دادیم تا ایشان غالب آمدند.'),
(3905, 37, 117, 'و آن دو را کتاب روشن دادیم.'),
(3906, 37, 118, 'و هر دو را به راه راست هدایت کردیم.'),
(3907, 37, 119, 'و برای آن دو در [میان‌] آیندگان [نام نیک‌] به جای گذاشتیم.'),
(3908, 37, 120, 'درود بر موسی و هارون!'),
(3909, 37, 121, 'ما نیکوکاران را چنین پاداش می‌دهیم،'),
(3910, 37, 122, 'زیرا آن دو از بندگان با ایمان ما بودند.'),
(3911, 37, 123, 'و به راستی الیاس از فرستادگان [ما] بود.'),
(3912, 37, 124, 'چون به قوم خود گفت: «آیا پروا نمی‌دارید؟'),
(3913, 37, 125, 'آیا «بعل» را می‌پرستید و بهترین آفرینندگان را وامی‌گذارید؟!'),
(3914, 37, 126, '[یعنی:] خدا را که پروردگار شما و پروردگار پدران پیشین شماست؟!'),
(3915, 37, 127, 'پس او را دروغگو شمردند، و قطعاً آنها [در آتش‌] احضار خواهند شد-'),
(3916, 37, 128, 'مگر بندگان پاکدین خدا.'),
(3917, 37, 129, 'و برای او در [میان‌] آیندگان [آوازه نیک‌] به جای گذاشتیم.'),
(3918, 37, 130, 'درود بر پیروان الیاس!'),
(3919, 37, 131, 'ما نیکوکاران را این گونه پاداش می‌دهیم،'),
(3920, 37, 132, 'زیرا او از بندگان با ایمان ما بود.'),
(3921, 37, 133, 'و در حقیقت، لوط از زمره فرستادگان بود.'),
(3922, 37, 134, 'آنگاه که او و همه کسانش را رهانیدیم-'),
(3923, 37, 135, 'جز پیرزنی که در میان باقی‌ماندگان [و خاکسترشدگان‌] بود-'),
(3924, 37, 136, 'سپس دیگران را هلاک کردیم.'),
(3925, 37, 137, 'و در حقیقت، شما بر آنان صبحگاهان'),
(3926, 37, 138, 'و شامگاهان می‌گذرید! آیا به فکر فرو نمی‌روید؟!'),
(3927, 37, 139, 'و در حقیقت، یونس از زمره فرستادگان بود.'),
(3928, 37, 140, 'آنگاه که به سوی کشتی پر، بگریخت!'),
(3929, 37, 141, 'پس [سرنشینان‌] با هم قرعه انداختند و [یونس‌] از باختگان شد.'),
(3930, 37, 142, '[او را به دریا افکندند] و عنبرماهی او را بلعید در حالی که او نکوهشگر خویش بود!'),
(3931, 37, 143, 'و اگر او از زمره تسبیح‌کنندگان نبود،'),
(3932, 37, 144, 'قطعاً تا روزی که برانگیخته می‌شوند، در شکم آن [ماهی‌] می‌ماند!'),
(3933, 37, 145, 'پس او را در حالی که ناخوش بود به زمین خشکی افکندیم!'),
(3934, 37, 146, 'و بر بالای [سرِ] او درختی از [نوع‌] کدوبُن رویانیدیم.'),
(3935, 37, 147, 'و او را به سوی یکصدهزار [نفر از ساکنان نینوا] یا بیشتر روانه کردیم.'),
(3936, 37, 148, 'پس ایمان آوردند و تا چندی برخوردارشان کردیم.'),
(3937, 37, 149, 'پس، از مشرکان جویا شو: آیا پروردگارت را دختران و آنان را پسران است؟!'),
(3938, 37, 150, 'یا فرشتگان را مادینه آفریدیم و آنان شاهد بودند؟'),
(3939, 37, 151, 'هش‌دار که اینان از دروغ پردازی خود قطعاً خواهند گفت:'),
(3940, 37, 152, '«خدا فرزند آورده!» در حالی که آنها قطعاً دروغگویانند!'),
(3941, 37, 153, 'آیا [خدا] دختران را بر پسران برگزیده است؟'),
(3942, 37, 154, 'شما را چه شده؟ چگونه داوری می‌کنید؟'),
(3943, 37, 155, 'آیا سرِ پند گرفتن ندارید؟!'),
(3944, 37, 156, 'یا دلیلی آشکار [در دست‌] دارید؟'),
(3945, 37, 157, 'پس اگر راست می‌گویید کتابتان را بیاورید.'),
(3946, 37, 158, 'و میان خدا و جن‌ها پیوندی انگاشتند و حال آنکه جنیان نیک دانسته‌اند که [برای حساب پس‌دادن،] خودشان احضار خواهند شد.'),
(3947, 37, 159, 'خدا منزه است از آنچه در وصف می‌آورند.'),
(3948, 37, 160, 'به استثنای بندگان پاکدل خدا.'),
(3949, 37, 161, 'در حقیقت، شما و آنچه [که شما آن را] می‌پرستید،'),
(3950, 37, 162, 'بر ضد او گمراه‌گر نیستید،'),
(3951, 37, 163, 'مگر کسی را که به دوزخ رفتنی است!'),
(3952, 37, 164, 'و هیچ یک از ما [فرشتگان‌] نیست مگر [اینکه‌] برای او [مقام و] مرتبه‌ای معیّن است.'),
(3953, 37, 165, 'و در حقیقت، ماییم که [برای انجام فرمان خدا] صف بسته‌ایم.'),
(3954, 37, 166, 'و ماییم که خود تسبیح‌گویانیم.'),
(3955, 37, 167, 'و [مشرکان‌] به تأکید می‌گفتند:'),
(3956, 37, 168, '«اگر پند [نامه‌ا]ی از پیشینیان نزد ما بود،'),
(3957, 37, 169, 'قطعاً از بندگان خالص خدا می‌شدیم!»'),
(3958, 37, 170, 'ولی [وقتی قرآن آمد] به آن کافر شدند، و زودا که بدانند!'),
(3959, 37, 171, 'و قطعاً فرمان ما در باره بندگان فرستاده ما از پیش [چنین‌] رفته است:'),
(3960, 37, 172, 'که آنان [بر دشمنان خودشان‌] حتماً پیروز خواهند شد.'),
(3961, 37, 173, 'و سپاه ما هرآینه غالب‌آیندگانند.'),
(3962, 37, 174, 'پس تا مدتی [معیّن‌] از آنان روی برتاب.'),
(3963, 37, 175, 'و آنان را بنگر که خواهند دید.'),
(3964, 37, 176, 'آیا عذاب ما را شتابزده خواستارند؟'),
(3965, 37, 177, '[پس هشدارداده‌شدگان را] آنگاه که عذاب به خانه آنان فرود آید چه بد صبحگاهی است!'),
(3966, 37, 178, 'و از ایشان تا مدتی [معیّن‌] روی برتاب.'),
(3967, 37, 179, 'و بنگر که خواهند دید!'),
(3968, 37, 180, 'منزه است پروردگار تو، پروردگار شکوهمند، از آنچه وصف می‌کنند.'),
(3969, 37, 181, 'و درود بر فرستادگان!'),
(3970, 37, 182, 'و ستایش، ویژه خدا، پروردگار جهانهاست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صاد. سوگند به قرآن پراندرز!'),
(3972, 38, 2, 'آری، آنان که کفر ورزیدند در سرکشی و ستیزه‌اند.'),
(3973, 38, 3, 'چه بسیار نسلها که پیش از ایشان هلاک کردیم که [ما را] به فریاد خواندند، و[لی‌] دیگر مجال گریز نبود.'),
(3974, 38, 4, 'و از اینکه هشداردهنده‌ای از خودشان برایشان آمده درشگفتند، و کافران می گویند: «این، ساحری شیّاد است.'),
(3975, 38, 5, 'آیا خدایان [متعدد] را خدای واحدی قرار داده؟ این واقعاً چیز عجیبی است.»'),
(3976, 38, 6, 'و بزرگانشان روان شدند [و گفتند:] «بروید و بر خدایان خود ایستادگی نمایید که این امر قطعاً هدف [ما]ست.'),
(3977, 38, 7, '[از طرفی‌] این [مطلب‌] را در آیین اخیر [عیسوی هم‌] نشنیده‌ایم، این [ادّعا] جز دروغ‌بافی نیست.'),
(3978, 38, 8, 'آیا از میان ما قرآن بر او نازل شده است؟» [نه!] بلکه آنان در باره قرآنِ من دودلند. [نه،] بلکه هنوز عذاب [مرا] نچشیده‌اند.'),
(3979, 38, 9, 'آیا گنجینه‌های رحمت پروردگار ارجمندِ بسیار بخشنده تو نزد ایشان است؟'),
(3980, 38, 10, 'آیا فرمانروایی آسمانها و زمین و آنچه میان آن دو است از آنِ ایشان است؟ [اگر چنین است‌] پس [با چنگ زدن‌] در آن اسباب به بالا روند.'),
(3981, 38, 11, 'این سپاهک دسته‌های دشمن در آنجا [=بَدْر] در هم شکستنی‌اند.'),
(3982, 38, 12, 'پیش از ایشان قوم نوح و عاد و فرعونِ صاحب [عمارت و] خرگاهها تکذیب کردند.'),
(3983, 38, 13, 'و ثمود و قوم لوط و اصحاب ایکه [نیز به تکذیب پرداختند] آنها دسته‌های مخالف بودند.'),
(3984, 38, 14, 'هیچ کدام نبودند که پیامبران [ما] را تکذیب نکنند، پس عقوبت [من بر آنان‌] سزاوار آمد.'),
(3985, 38, 15, 'و اینان جز یک فریاد را انتظار نمی‌بَرند که هیچ [مجال‌] سر خاراندنی در آن نیست.'),
(3986, 38, 16, 'و گفتند: «پروردگارا، پیش از [رسیدن‌] روز حساب، بهره ما را [از عذاب‌] به شتاب به ما بده.»'),
(3987, 38, 17, 'بر آنچه می‌گویند صبر کن، و داوود، بنده ما را که دارای امکانات [متعدّد] بود به یاد آور؛ آری، او بسیار بازگشت‌کننده [به سوی خدا] بود.'),
(3988, 38, 18, 'ما کوهها را با او مسخّر ساختیم [که‌] شامگاهان و بامدادان خداوند را نیایش می‌کردند.'),
(3989, 38, 19, 'و پرندگان را از هر سو [بر او] گرد [آوردیم‌] همگی [به نوای دلنوازش‌] به سوی او بازگشت‌کننده [و خدا را ستایشگر] بودند.'),
(3990, 38, 20, 'و پادشاهیش را استوار کردیم و او را حکمت و کلام فیصله‌دهنده عطا کردیم.'),
(3991, 38, 21, 'و آیا خبر دادخواهان -چون از نمازخانه [او] بالا رفتند- به تو رسید؟'),
(3992, 38, 22, 'وقتی [به طور ناگهانی‌] بر داوود درآمدند، و او از آنان به هراس افتاد، گفتند: «مترس، [ما] دو مدّعی [هستیم‌] که یکی از ما بر دیگری تجاوز کرده، پس میان ما به حق داوری کن، و از حق دور مشو، و ما را به راه راست راهبر باش.»'),
(3993, 38, 23, '«این [شخص‌] برادر من است. او را نود و نه میش، و مرا یک میش است، و می‌گوید: آن را به من بسپار، و در سخنوری بر من غالب آمده است.»'),
(3994, 38, 24, '[داوود] گفت: «قطعاً او در مطالبه میش تو [اضافه‌] بر میش‌های خودش، بر تو ستم کرده، و در حقیقت بسیاری از شریکان به همدیگر ستم روا می‌دارند، به استثنای کسانی که ایمان آورده و کارهای شایسته کرده‌اند، و اینها بس اندکند، و داوود دانست که ما او را آزمایش کرده‌ایم. پس، از پروردگارش آمرزش خواست و به رو درافتاد و توبه کرد.'),
(3995, 38, 25, 'و بر او این [ماجرا] را بخشودیم؛ و در حقیقت برای او پیش ما تقرّب و فرجامی خوش خواهد بود.'),
(3996, 38, 26, 'ای داوود، ما تو را در زمین خلیفه [و جانشین‌] گردانیدیم؛ پس میان مردم به حق داوری کن، و زنهار از هوس پیروی مکن که تو را از راه خدا به در کند. در حقیقت کسانی که از راه خدا به در می‌روند، به [سزای‌] آنکه روز حساب را فراموش کرده‌اند عذابی سخت خواهند داشت!'),
(3997, 38, 27, 'و آسمان و زمین و آنچه را که میان این دو است به باطل نیافریدیم، این گمان کسانی است که کافر شده [و حق‌پوشی کرده‌]اند، پس وای از آتش بر کسانی که کافر شده‌اند.'),
(3998, 38, 28, 'یا [مگر] کسانی را که گرویده و کارهای شایسته کرده‌اند، چون مفسدان در زمین می‌گردانیم، یا پرهیزگاران را چون پلیدکاران قرار می‌دهیم؟'),
(3999, 38, 29, '[این‌] کتابی مبارک است که آن را به سوی تو نازل کرده‌ایم تا در [باره‌] آیات آن بیندیشند، و خردمندان پند گیرند.'),
(4000, 38, 30, 'و سلیمان را به داوود بخشیدیم. چه نیکو بنده‌ای. به راستی او توبه‌کار [و ستایشگر] بود.'),
(4001, 38, 31, 'هنگامی که [طرف‌] غروب، اسبهای اصیل را بر او عرضه کردند،'),
(4002, 38, 32, '[سلیمان‌] گفت: «واقعاً من دوستی اسبان را بر یاد پروردگارم ترجیح دادم تا [هنگام نماز گذشت و خورشید] در پس حجاب ظلمت شد.»'),
(4003, 38, 33, '[گفت: « اسبها] را نزد من باز آورید.» پس شروع کرد به دست کشیدن بر ساقها و گردن آنها [و سرانجام وقف کردن آنها در راه خدا].'),
(4004, 38, 34, 'و قطعاً سلیمان را آزمودیم و بر تخت او جسدی بیفکندیم؛ پس به توبه باز آمد.'),
(4005, 38, 35, 'گفت: «پروردگارا، مرا ببخش و مُلکی به من ارزانی دار که هیچ کس را پس از من سزاوار نباشد، در حقیقت، تویی که خود بسیار بخشنده‌ای.»'),
(4006, 38, 36, 'پس باد را در اختیار او قرار دادیم که هر جا تصمیم می‌گرفت، به فرمان او نرم، روان می‌شد.'),
(4007, 38, 37, 'و شیطانها را [از] بنّا و غواص،'),
(4008, 38, 38, 'تا [وحشیان‌] دیگر را که جفت جفت با زنجیرها به هم بسته بودند [تحت فرمانش درآوردیم‌].'),
(4009, 38, 39, '[گفتیم:] «این بخشش ماست، [آن را] بی‌شمار ببخش یا نگاه دار.»'),
(4010, 38, 40, 'و قطعاً برای او در پیشگاه ما تقرّب و فرجام نیکوست.'),
(4011, 38, 41, 'و بنده ما ایوب را به یاد آور، آنگاه که پروردگارش را ندا داد که: «شیطان مرا به رنج و عذاب مبتلا کرد.»'),
(4012, 38, 42, '[به او گفتیم:] «با پای خود [به زمین‌] بکوب، اینک این چشمه‌ساری است سرد و آشامیدنی.»'),
(4013, 38, 43, 'و [مجدداً] کسانش را و نظایر آنها را همراه آنها به او بخشیدیم، تا رحمتی از جانب ما و عبرتی برای خردمندان باشد.'),
(4014, 38, 44, '[و به او گفتیم:] «یک بسته ترکه به دستت برگیر و [همسرت را] با آن بزن و سوگند مشکن.» ما او را شکیبا یافتیم. چه نیکوبنده‌ای! به راستی او توبه‌کار بود.'),
(4015, 38, 45, 'و بندگان ما ابراهیم و اسحاق و یعقوب را که نیرومند و دیده‌ور بودند به یادآور.'),
(4016, 38, 46, 'ما آنان را با موهبت ویژه‌ای -که یادآوری آن سرای بود- خالص گردانیدیم.'),
(4017, 38, 47, 'و آنان در پیشگاه ما جداً از برگزیدگان نیکانند.'),
(4018, 38, 48, 'و اسماعیل و یسع و ذوالکفل را به یاد آور [که‌] همه از نیکانند.'),
(4019, 38, 49, 'این یادکردی است، و قطعاً برای پرهیزگاران فرجامی نیک است.'),
(4020, 38, 50, 'باغهای همیشگی در حالی که درهای [آنها] برایشان گشوده‌است.'),
(4021, 38, 51, 'در آنجا تکیه می‌زنند [و] میوه‌های فراوان و نوشیدنی در آنجا طلب می‌کنند.'),
(4022, 38, 52, 'و نزدشان [دلبران‌] فروهشته‌نگاه همسال است.'),
(4023, 38, 53, 'این است آنچه برای روز حساب به شما وعده داده می‌شد.'),
(4024, 38, 54, '[می‌گویند:] «در حقیقت، این روزیِ ماست و آن را پایانی نیست.»'),
(4025, 38, 55, 'این است [حال بهشتیان‌] و [اما] برای طغیانگران واقعاً بد فرجامی است.'),
(4026, 38, 56, 'به جهنّم درمی‌آیند، و چه بد آرامگاهی است.'),
(4027, 38, 57, 'این جوشاب و چرکاب است، باید آن را بچشند.'),
(4028, 38, 58, 'و از همین گونه، انواع دیگر [عذابها]!'),
(4029, 38, 59, 'اینها گروهی‌اند که با شما به اجبار [در آتش‌] درمی‌آیند. بدا به حال آنها، زیرا آنان داخل آتش می‌شوند.'),
(4030, 38, 60, '[به رؤسای خود] می‌گویند: «بلکه بر خود شما خوش مباد! این [عذاب‌] را شما خود برای ما از پیش فراهم آوردید، و چه بد قرارگاهی است.»'),
(4031, 38, 61, 'می‌گویند: «پروردگارا، هر کس این [عذاب‌] را از پیش برای ما فراهم آورده، عذاب او را در آتش دو چندان کن.»'),
(4032, 38, 62, 'و می‌گویند: «ما را چه شده است که مردانی را که ما آنان را از [زمره‌] اشرار می‌شمردیم نمی‌بینیم؟'),
(4033, 38, 63, 'آیا آنان را [در دنیا] به ریشخند می‌گرفتیم یا چشمها[ی ما] بر آنها نمی‌افتد؟»'),
(4034, 38, 64, 'این مجادله اهل آتش قطعاً راست است.'),
(4035, 38, 65, 'بگو: «من فقط هشداردهنده‌ای هستم، و جز خدای یگانه قهار معبودی دیگر نیست.'),
(4036, 38, 66, 'پروردگار آسمانها و زمین و آنچه میان آن دو است، همان شکست‌ناپذیر آمرزنده.'),
(4037, 38, 67, 'بگو: «این خبری بزرگ است،'),
(4038, 38, 68, '[که‌] شما از آن روی برمی‌تابید.'),
(4039, 38, 69, 'مرا در باره ملاء اعلی هیچ دانشی نبود آنگاه که مجادله می‌کردند.'),
(4040, 38, 70, 'به من هیچ [چیز] وحی نمی‌شود، جز اینکه من هشداردهنده‌ای آشکارم.'),
(4041, 38, 71, 'آنگاه که پروردگارت به فرشتگان گفت: «من بشری را از گِل خواهم آفرید.'),
(4042, 38, 72, 'پس چون او را [کاملاً] درست کردم و از روح خویش در آن دمیدم، سجده‌کنان برای او [به خاک‌] بیفتید.»'),
(4043, 38, 73, 'پس همه فرشتگان یکسره سجده کردند.'),
(4044, 38, 74, 'مگر ابلیس [که‌] تکبر نمود و از کافران شد.'),
(4045, 38, 75, 'فرمود: «ای ابلیس، چه چیز تو را مانع شد که برای چیزی که به دستان قدرت خویش خلق کردم سجده آوری؟ آیا تکبر نمودی یا از [جمله‌] برتری‌جویانی؟»'),
(4046, 38, 76, 'گفت: «من از او بهترم؛ مرا از آتش آفریده‌ای و او را از گِل آفریده‌ای.»'),
(4047, 38, 77, 'فرمود: «پس، از آن [مقام‌] بیرون شو، که تو رانده‌ای.'),
(4048, 38, 78, 'و تا روز جزا لعنت من بر تو باد.»'),
(4049, 38, 79, 'گفت: «پروردگارا، پس مرا تا روزی که برانگیخته می‌شوند مهلت ده.»'),
(4050, 38, 80, 'فرمود: «در حقیقت، تو از مهلت‌یافتگانی،'),
(4051, 38, 81, 'تا روز معین معلوم.»'),
(4052, 38, 82, '[شیطان‌] گفت: «پس به عزّت تو سوگند که همگی را جداً از راه به در می‌برم،'),
(4053, 38, 83, 'مگر آن بندگان پاکدل تو را.»'),
(4054, 38, 84, 'فرمود: «حق [از من‌] است و حق را می‌گویم:'),
(4055, 38, 85, 'هرآینه جهنّم را از تو و از هر کس از آنان که تو را پیروی کند، از همگی‌شان، خواهم انباشت.»'),
(4056, 38, 86, 'بگو: «مزدی بر این [رسالت‌] از شما طلب نمی‌کنم و من از کسانی نیستم که چیزی از خود بسازم و به خدا نسبت دهم.'),
(4057, 38, 87, 'این [قرآن‌] جز پندی برای جهانیان نیست.'),
(4058, 38, 88, 'و قطعاً پس از چندی خبر آن را خواهید دانست.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'نازل شدن [این کتاب‌] از جانب خدای شکست‌ناپذیر سنجیده‌کار است.'),
(4060, 39, 2, 'ما [این‌] کتاب را به حق به سوی تو فرود آوردیم، پس خدا را -در حالی که اعتقاد [خود] را برای او خالص‌کننده‌ای- عبادت کن.'),
(4061, 39, 3, 'آگاه باشید: آیین پاک از آنِ خداست، و کسانی که به جای او دوستانی برای خود گرفته‌اند [به این بهانه که:] ما آنها را جز برای اینکه ما را هر چه بیشتر به خدا نزدیک گردانند، نمی‌پرستیم، البته خدا میان آنان در باره آنچه که بر سر آن اختلاف دارند، داوری خواهد کرد. در حقیقت، خدا آن کسی را که دروغ‌پرداز ناسپاس است هدایت نمی‌کند.'),
(4062, 39, 4, 'اگر خدا می‌خواست برای خود فرزندی بگیرد، قطعاً از [میان‌] آنچه خلق می‌کند، آنچه را می‌خواست برمی‌گزید. منزّه است او، اوست خدای یگانه قهار.'),
(4063, 39, 5, 'آسمانها و زمین را به حق آفرید. شب را به روز درمی‌پیچد، و روز را به شب درمی‌پیچد و آفتاب و ماه را تسخیر کرد. هر کدام تا مدتی معین روانند. آگاه باش که او همان شکست‌ناپذیر آمرزنده است.'),
(4064, 39, 6, 'شما را از نفسی واحد آفرید، سپس جفتش را از آن قرار داد، و برای شما از دامها هشت قسم پدید آورد. شما را در شکمهای مادرانتان آفرینشی پس از آفرینشی [دیگر] در تاریکیهای سه گانه: [مَشیمه و رحم و شکم‌] خلق کرد. این است خدا، پروردگار شما، فرمانروایی [و حکومت مطلق‌] از آنِ اوست. خدایی جز او نیست، پس چگونه [و کجا از حق‌] برگردانیده می‌شوید؟'),
(4065, 39, 7, 'اگر کفر ورزید، خدا از شما سخت بی‌نیاز است و برای بندگانش کفران را خوش نمی‌دارد، و اگر سپاس دارید آن را برای شما می‌پسندد، و هیچ بردارنده‌ای بار [گناه‌] دیگری را برنمی‌دارد، آنگاه بازگشت شما به سوی پروردگارتان است، و شما را به آنچه می‌کردید خبر خواهد داد، که او به راز دلها داناست.'),
(4066, 39, 8, 'و چون به انسان آسیبی رسد، پروردگارش را -در حالی که به سوی او بازگشت‌کننده است- می‌خواند؛ سپس چون او را از جانب خود نعمتی عطا کند، آن [مصیبتی‌] را که در رفع آن پیشتر به درگاه او دعا می‌کرد، فراموش می‌نماید و برای خدا همتایانی قرار می‌دهد تا [خود و دیگران را] از راه او گمراه گرداند. بگو: «به کفرت اندکی برخوردار شو که تو از اهل آتشی.»'),
(4067, 39, 9, '[آیا چنین کسی بهتر است‌] یا آن کسی که او در طول شب در سجده و قیام اطاعت [خدا] می‌کند [و] از آخرت می‌ترسد و رحمت پروردگارش را امید دارد؟ بگو: «آیا کسانی که می‌دانند و کسانی که نمی‌دانند یکسانند؟» تنها خردمندانند که پندپذیرند.'),
(4068, 39, 10, 'بگو: «ای بندگان من که ایمان آورده‌اید، از پروردگارتان پروا بدارید. برای کسانی که در این دنیا خوبی کرده‌اند، نیکی خواهد بود، و زمین خدا فراخ است. بی‌تردید، شکیبایان پاداش خود را بی‌حساب [و] به تمام خواهند یافت.»'),
(4069, 39, 11, 'بگو: «من مأمورم که خدا را -در حالی که آیینم را برای او خالص گردانیده‌ام- بپرستم،'),
(4070, 39, 12, 'و مأمورم که نخستین مسلمانان باشم.»'),
(4071, 39, 13, 'بگو: «من اگر به پروردگارم عصیان ورزم، از عذاب روزی هولناک می‌ترسم.»'),
(4072, 39, 14, 'بگو: «خدا را می‌پرستم در حالی که دینم را برای او بی‌آلایش می‌گردانم.'),
(4073, 39, 15, 'پس هر چه را غیر از او می‌خواهید، بپرستید «[ولی به آنان‌] بگو:» زیانکاران در حقیقت کسانی‌اند که به خود و کسانشان در روز قیامت زیان رسانده‌اند؛ آری، این همان خسران آشکار است.»'),
(4074, 39, 16, 'آنها از بالای سرشان چترهایی از آتش خواهند داشت و از زیر پایشان [نیز] طبق‌هایی [آتشین است‌]، این [کیفری‌] است که خدا بندگانش را به آن بیم می‌دهد. ای بندگان من، از من بترسید.'),
(4075, 39, 17, 'و[لی‌] آنان که خود را از طاغوت به دور می‌دارند تا مبادا او را بپرستند و به سوی خدا بازگشته‌اند آنان را مژده باد، پس بشارت ده به آن بندگان من که:'),
(4076, 39, 18, 'به سخن گوش فرامی‌دهند و بهترین آن را پیروی می‌کنند؛ اینانند که خدایشان راه نموده و اینانند همان خردمندان.'),
(4077, 39, 19, 'پس آیا کسی که فرمان عذاب بر او واجب آمده [کجا روی رهایی دارد]؟ آیا تو کسی را که در آتش است می‌رهانی؟'),
(4078, 39, 20, 'لیکن کسانی که از پروردگارشان پروا داشتند، برای ایشان غرفه‌هایی است که بالای آنها غرفه‌هایی [دیگر] بنا شده است؛ نهرها از زیر آن روان است. وعده خداست؛ خدا خلاف وعده نمی‌کند.'),
(4079, 39, 21, 'مگر ندیده‌ای که خدا از آسمان، آبی فرود آورد پس آن را به چشمه‌هایی که در [طبقات زیرین‌] زمین است راه داد، آنگاه به وسیله آن کشتزاری را که رنگهای آن گوناگون است بیرون می‌آورد، سپس خشک می‌گردد، آنگاه آن را زرد می‌بینی، سپس خاشاکش می‌گرداند. قطعاً در این [دگرگونیها] برای صاحبان خرد عبرتی است.'),
(4080, 39, 22, 'پس آیا کسی که خدا سینه‌اش را برای [پذیرش‌] اسلام گشاده، و [در نتیجه‌] برخوردار از نوری از جانب پروردگارش می‌باشد [همانند فرد تاریکدل است‌]؟ پس وای بر آنان که از سخت‌دلی یاد خدا نمی‌کنند؛ اینانند که در گمراهی آشکارند.'),
(4081, 39, 23, 'خدا زیباترین سخن را [به صورت‌] کتابی متشابه، متضمّن وعد و وعید، نازل کرده است. آنان که از پروردگارشان می‌هراسند، پوست بدنشان از آن به لرزه می‌افتد، سپس پوستشان و دلشان به یاد خدا نرم می‌گردد. این است هدایت خدا، هر که را بخواهد، به آن راه نماید، و هر که را خدا گمراه کند او را راهبری نیست.'),
(4082, 39, 24, 'پس آیا آن کس که [به جای دستها] با چهره خود، گزند عذاب را روز قیامت دفع می‌کند [مانند کسی است که از عذاب ایمن است‌]؟ و به ستمگران گفته می‌شود: «آنچه را که دستاوردتان بوده است بچشید.»'),
(4083, 39, 25, 'کسانی [هم‌] که پیش از آنان بودند به تکذیب پرداختند، و از آنجا که حدس نمی‌زدند عذاب برایشان آمد.'),
(4084, 39, 26, 'پس خدا در زندگی دنیا رسوایی را به آنان چشانید، و اگر می‌دانستند، قطعاً عذاب آخرت بزرگتر است.'),
(4085, 39, 27, 'و در این قرآن از هر گونه مثَلی برای مردم آوردیم، باشد که آنان پندگیرند.'),
(4086, 39, 28, 'قرآنی عربی، بی‌هیچ کژی؛ باشد که آنان راه تقوا پویند.'),
(4087, 39, 29, 'خدا مثَلی زده است: مردی است که چند خواجه ناسازگار در [مالکیّت‌] او شرکت دارند [و هر یک او را به کاری می‌گمارند] و مردی است که تنها فرمانبر یک مرد است. آیا این دو در مثَل یکسانند؟ سپاس خدای را. [نه،] بلکه بیشترشان نمی‌دانند.'),
(4088, 39, 30, 'قطعاً تو خواهی مُرد، و آنان [نیز] خواهند مُرد؛'),
(4089, 39, 31, 'سپس شما روز قیامت پیش پروردگارتان مجادله خواهید کرد.'),
(4090, 39, 32, 'پس کیست ستمگرتر از آن کس که بر خدا دروغ بست، و [سخن‌] راست را چون به سوی او آمد، دروغ پنداشت؟ آیا جای کافران در جهنّم نیست؟'),
(4091, 39, 33, 'و آن کس که راستی آورد و آن را باور نمود؛ آنانند که خود پرهیزگارانند.'),
(4092, 39, 34, 'برای آنان، هر چه بخواهند پیش پروردگارشان خواهد بود. این است پاداش نیکوکاران.'),
(4093, 39, 35, 'تا خدا، بدترین عملی را که کرده‌اند، از ایشان بزداید، و آنان را به بهترین کاری که می‌کرده‌اند، پاداش دهد.'),
(4094, 39, 36, 'آیا خدا کفایت‌کننده بنده‌اش نیست؟ و [کافران‌] تو را از آنها که غیر اویند می‌ترسانند و هر که را خدا گمراه گرداند برایش راهبری نیست.'),
(4095, 39, 37, 'و هر که را خدا هدایت کند گمراه‌کننده‌ای ندارد. مگر خدا نیست که نیرومند کیفرخواه است؟'),
(4096, 39, 38, 'و اگر از آنها بپرسی: «چه کسی آسمانها و زمین را خلق کرده؟» قطعاً خواهند گفت: «خدا.» بگو: «[هان‌] چه تصور می‌کنید، اگر خدا بخواهد صدمه‌ای به من برساند؛ آیا آنچه را به جای خدا می‌خوانید، می‌توانند صدمه او را برطرف کنند؛ یا اگر او رحمتی برای من اراده کند آیا آنها می‌توانند رحمتش را بازدارند؟» بگو: «خدا مرا بس است. اهل توکّل تنها بر او توکّل می‌کنند.»'),
(4097, 39, 39, 'بگو: «ای قوم من، شما بر حسب امکانات خود عمل کنید، من [نیز] عمل می‌کنم، پس به زودی خواهید دانست:'),
(4098, 39, 40, '[که‌] چه کس را عذابی که رسوایش کند خواهد آمد و عذابی پایدار بر او نازل می‌شود.»'),
(4099, 39, 41, 'ما این کتاب را برای [رهبری‌] مردم به حق بر تو فروفرستادیم. پس هر کس هدایت شود، به سود خود اوست، و هر کس بیراهه رود، تنها به زیان خودش گمراه می‌شود، و تو بر آنها وکیل نیستی.'),
(4100, 39, 42, 'خدا روح مردم را هنگام مرگشان به تمامی باز می‌ستاند، و [نیز] روحی را که در [موقع‌] خوابش نمرده است [قبض می‌کند]؛ پس آن [نفسی‌] را که مرگ را بر او واجب کرده نگاه می‌دارد، و آن دیگر [نفسها] را تا هنگامی معیّن [به سوی زندگی دنیا] بازپس می‌فرستد. قطعاً در این [امر] برای مردمی که می‌اندیشند نشانه‌هایی [از قدرت خدا]ست.'),
(4101, 39, 43, 'آیا غیر از خدا شفاعتگرانی برای خود گرفته‌اند؟ بگو: «آیا هر چند اختیار چیزی را نداشته باشند و نیندیشند؟»'),
(4102, 39, 44, 'بگو: «شفاعت، یکسره از آن خداست. فرمانروایی آسمانها و زمین خاص اوست؛ سپس به سوی او باز گردانیده می‌شوید.»'),
(4103, 39, 45, 'و چون خدا به تنهایی یاد شود، دلهای کسانی که به آخرت ایمان ندارند، منزجر می‌گردد، و چون کسانی غیر از او یاد شوند، بناگاه آنان شادمانی می‌کنند.'),
(4104, 39, 46, 'بگو: «بار الها، ای پدیدآورنده آسمانها و زمین، [ای‌] دانای نهان و آشکار، تو خود در میان بندگانت بر سر آنچه اختلاف می‌کردند، داوری می‌کنی.»'),
(4105, 39, 47, 'و اگر آنچه در زمین است، یکسره برای کسانی که ظلم کرده‌اند باشد و نظیرش [نیز] با آن باشد، قطعاً [همه‌] آن را برای رهایی خودشان از سختی عذاب روز قیامت خواهند داد، و آنچه تصوّر[ش را ]نمی‌کردند، از جانب خدا بر ایشان آشکار می‌گردد.'),
(4106, 39, 48, 'و [نتیجه‌] گناهانی که مرتکب شده‌اند، برایشان ظاهر می‌شود، و آنچه را که بدان ریشخند می‌کردند، آنها را فرا می‌گیرد.'),
(4107, 39, 49, 'و چون انسان را آسیبی رسد، ما را فرا می‌خواند؛ سپس چون نعمتی از جانب خود به او عطا کنیم می‌گوید: «تنها آن را به دانش خود یافته‌ام». نه چنان است، بلکه آن آزمایشی است، ولی بیشترشان نمی‌دانند.'),
(4108, 39, 50, 'قطعاً کسانی که پیش از آنان بودند [نیز] این [سخن‌] را گفتند و آنچه به دست آورده بودند، کاری برایشان نکرد.'),
(4109, 39, 51, 'تا [آنکه‌] کیفر آنچه مرتکب شده بودند، بدیشان رسید و کسانی از این [گروه‌] که ستم کرده‌اند، به زودی نتایج سوء آنچه مرتکب شده‌اند، بدیشان خواهد رسید و آنان درمانده‌کننده [ما] نیستند.'),
(4110, 39, 52, 'آیا ندانسته‌اند که خداست که روزی را برای هر کس که بخواهد، گشاده یا تنگ می‌گرداند؟ قطعاً در این [اندازه‌گیری‌] برای مردمی که ایمان دارند نشانه‌هایی [از حکمت‌] است.'),
(4111, 39, 53, 'بگو: «ای بندگان من -که بر خویشتن زیاده‌روی روا داشته‌اید- از رحمت خدا نومید مشوید. در حقیقت، خدا همه گناهان را می‌آمرزد، که او خود آمرزنده مهربان است.'),
(4112, 39, 54, 'و پیش از آنکه شما را عذاب در رسد، و دیگر یاری نشوید، به سوی پروردگارتان بازگردید، و تسلیم او شوید.'),
(4113, 39, 55, 'و پیش از آنکه به طور ناگهانی و در حالی که حدس نمی‌زنید شما را عذاب دررسد، نیکوترین چیزی را که از جانب پروردگارتان به سوی شما نازل آمده است پیروی کنید.»'),
(4114, 39, 56, 'تا آنکه [مبادا] کسی بگوید: «دریغا بر آنچه در حضور خدا کوتاهی ورزیدم؛ بی‌تردید من از ریشخندکنندگان بودم.»'),
(4115, 39, 57, 'یا بگوید: «اگر خدایم هدایت می‌کرد، مسلماً از پرهیزگاران بودم.»'),
(4116, 39, 58, 'یا چون عذاب را ببیند، بگوید: «کاش مرا برگشتی بود تا از نیکوکاران می‌شدم.»'),
(4117, 39, 59, '[به او گویند:] آری، نشانه‌های من بر تو آمد و آنها را تکذیب کردی و تکبّر ورزیدی و از [جمله‌] کافران شدی.'),
(4118, 39, 60, 'و روز قیامت کسانی را که بر خدا دروغ بسته‌اند رو سیاه می‌بینی، آیا جای سرکشان در جهنم نیست؟'),
(4119, 39, 61, 'و خدا کسانی را که تقوا پیشه کرده‌اند، به [پاس‌] کارهایی که مایه رستگاری‌شان بوده، نجات می‌دهد. عذاب به آنان نمی‌رسد و غمگین نخواهند گردید.'),
(4120, 39, 62, 'خدا آفریدگار هر چیزی است، و اوست که بر هر چیز نگهبان است.'),
(4121, 39, 63, 'کلیدهای آسمان و زمین از آن اوست، و کسانی که نشانه‌های خدا را انکار کردند، آنانند که زیانکارانند.'),
(4122, 39, 64, 'بگو: «ای نادانان، آیا مرا وادار می‌کنید که جز خدا را بپرستم؟»'),
(4123, 39, 65, 'و قطعاً به تو و به کسانی که پیش از تو بودند وحی شده است: «اگر شرک ورزی حتماً کردارت تباه و مسلماً از زیانکاران خواهی شد.»'),
(4124, 39, 66, 'بلکه خدا را بپرست و از سپاسگزاران باش.'),
(4125, 39, 67, 'و خدا را آنچنان که باید به بزرگی نشناخته‌اند، و حال آنکه روز قیامت زمین یکسره در قبضه [قدرت‌] اوست، و آسمانها در پیچیده به دست اوست؛ او منزّه است و برتر است از آنچه [با وی‌] شریک می‌گردانند.'),
(4126, 39, 68, 'و در صور دمیده می‌شود، پس هر که در آسمانها و هر که در زمین است بیهوش درمی‌افتد، مگر کسی که خدا بخواهد؛ سپس بار دیگر در آن دمیده می‌شود و بناگاه آنان بر پای ایستاده می‌نگرند.'),
(4127, 39, 69, 'و زمین به نور پروردگارش روشن گردد، و کارنامه [اعمال در میان‌] نهاده شود، و پیامبران و شاهدان را بیاورند، و میانشان به حق داوری گردد، و مورد ستم قرار نگیرند.'),
(4128, 39, 70, 'و هر کسی [نتیجه‌] آنچه انجام داده است به تمام بیابد و او به آنچه می‌کنند داناتر است.'),
(4129, 39, 71, 'و کسانی که کافر شده‌اند، گروه گروه به سوی جهنّم رانده شوند، تا چون بدان رسند، درهای آن [به رویشان‌] گشوده گردد و نگهبانانش به آنان گویند: «مگر فرستادگانی از خودتان بر شما نیامدند که آیات پروردگارتان را بر شما بخوانند و به دیدار چنین روزی شما را هشدار دهند؟» گویند: «چرا»، ولی فرمان عذاب بر کافران واجب آمد.'),
(4130, 39, 72, 'و گفته شود: «از درهای دوزخ درآیید، جاودانه در آن بمانید؛ وه چه بد [جایی‌] است جای سرکشان!»'),
(4131, 39, 73, 'و کسانی که از پروردگارشان پروا داشته‌اند، گروه گروه به سوی بهشت سوق داده شوند، تا چون بدان رسند و درهای آن [به رویشان‌] گشوده گردد و نگهبانان آن به ایشان گویند: «سلام بر شما، خوش آمدید، در آن درآیید [و] جاودانه [بمانید].»'),
(4132, 39, 74, 'و گویند: «سپاس خدایی را که وعده‌اش را بر ما راست گردانید و سرزمین [بهشت‌] را به ما میراث داد، از هر جای آن باغ [پهناور] که بخواهیم جای می‌گزینیم.» چه نیک است پاداش عمل‌کنندگان.'),
(4133, 39, 75, 'و فرشتگان را می‌بینی که پیرامون عرش به ستایش پروردگار خود تسبیح می‌گویند و میانشان به حق داوری می‌گردد و گفته می‌شود: «سپاس، ویژه پروردگار جهانیان است.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حاء، میم.'),
(4135, 40, 2, 'فرو فرستادن این کتاب، از جانب خدای ارجمند داناست،'),
(4136, 40, 3, '[که‌] گناه‌بخش و توبه‌پذیر [و] سخت‌کیفر [و] فراخ‌نعمت است. خدایی جز او نیست. بازگشت به سوی اوست.'),
(4137, 40, 4, 'جز آنهایی که کفر ورزیدند [کسی‌] در آیات خدا ستیزه نمی‌کند، پس رفت و آمدشان در شهرها تو را دستخوش فریب نگرداند.'),
(4138, 40, 5, 'پیش از اینان قوم نوح، و بعد از آنان دسته‌های مخالف [دیگر] به تکذیب پرداختند، و هر امّتی آهنگ فرستاده خود را کردند تا او را بگیرند، و به [وسیله‌] باطل جدال نمودند تا حقیقت را با آن پایمال کنند. پس آنان را فرو گرفتم؛ آیا چگونه بود کیفر من؟'),
(4139, 40, 6, 'و بدین سان فرمان پروردگارت در باره کسانی که کفر ورزیده بودند، به حقیقت پیوست که ایشان همدمان آتش خواهند بود.'),
(4140, 40, 7, 'کسانی که عرش [خدا] را حمل می‌کنند، و آنها که پیرامون آنند، به سپاس پروردگارشان تسبیح می‌گویند و به او ایمان دارند و برای کسانی که گرویده‌اند طلب آمرزش می‌کنند: «پروردگارا، رحمت و دانش [تو بر] هر چیز احاطه دارد؛ کسانی را که توبه کرده و راه تو را دنبال کرده‌اند ببخش و آنها را از عذاب آتش نگاه دار.»'),
(4141, 40, 8, '«پروردگارا، آنان را در باغهای جاوید که وعده‌شان داده‌ای، با هر که از پدران و همسران و فرزندانشان که به صلاح آمده‌اند، داخل کن، زیرا تو خود ارجمند و حکیمی.'),
(4142, 40, 9, 'و آنان را از بدیها نگاه دار، و هر که را در آن روز از بدیها حفظ کنی، البته رحمتش کرده‌ای؛ و این همان کامیابی بزرگ است.»'),
(4143, 40, 10, 'کسانی که کافر بوده‌اند مورد ندا قرار می‌گیرند که: «قطعاً دشمنی خدا از دشمنی شما نسبت به همدیگر سخت‌تر است، آنگاه که به سوی ایمان فرا خوانده می‌شدید و انکار می‌ورزیدید.»'),
(4144, 40, 11, 'می‌گویند: «پروردگارا، دو بار ما را به مرگ رسانیدی و دو بار ما را زنده گردانیدی. به گناهانمان اعتراف کردیم؛ پس آیا راه بیرون‌شدنی [از آتش‌] هست؟»'),
(4145, 40, 12, 'این [کیفر] از آن روی برای شماست که چون خدا به تنهایی خوانده می‌شد، کفر می‌ورزیدید؛ و چون به او شرک آورده می‌شد، آن را باور می‌کردید. پس [امروز] فرمان از آن خدایِ والایِ بزرگ است.'),
(4146, 40, 13, 'اوست آن کس که نشانه‌های خود را به شما می‌نمایاند و برای شما از آسمان روزی می‌فرستد، و جز آن کس که توبه‌کار است [کسی‌] پند نمی‌گیرد.'),
(4147, 40, 14, 'پس خدا را پاکدلانه فرا خوانید، هر چند ناباوران را ناخوش افتد!'),
(4148, 40, 15, 'بالابرنده درجات، خداوند عرش، به هر کس از بندگانش که خواهد آن روح [=فرشته‌] را، به فرمان خویش می‌فرستد، تا [مردم را] از روز ملاقات [با خدا] بترساند.'),
(4149, 40, 16, 'آن روز که آنان ظاهر گردند، چیزی از آنها بر خدا پوشیده نمی‌ماند. امروز فرمانروایی از آنِ کیست؟ از آنِ خداوند یکتای قهّار است.'),
(4150, 40, 17, 'امروز هر کسی به [موجب‌] آنچه انجام داده است کیفر می‌یابد. امروز ستمی نیست؛ آری، خدا زودشمار است.'),
(4151, 40, 18, 'و آنها را از آن روز قریب‌[الوقوع‌] بترسان، آنگاه که جانها به گلوگاه می‌رسد در حالی که اندوه خود را فرو می‌خورند. برای ستمگران نه یاری است و نه شفاعتگری که مورد اطاعت باشد.'),
(4152, 40, 19, '[خدا] نگاههای دزدانه و آنچه را که دلها نهان می‌دارند، می‌داند.'),
(4153, 40, 20, 'و خداست که به حق داوری می‌کند، و کسانی را که در برابر او می‌خوانند [عاجزند و] به چیزی داوری نمی‌کنند؛ در حقیقت، خداست که خود شنوای بیناست.'),
(4154, 40, 21, 'آیا در زمین نگردیده‌اند تا ببینند فرجام کسانی که پیش از آنها [زیسته‌]اند چگونه بوده است؟ آنها از ایشان نیرومندتر [بوده‌] و آثار [پایدارتری‌] در روی زمین [از خود باقی گذاشتند]، با این همه، خدا آنان را به کیفر گناهانشان گرفتار کرد و در برابر خدا حمایتگری نداشتند.'),
(4155, 40, 22, 'این [کیفر] از آن روی بود که پیامبرانشان دلایل آشکار برایشان می‌آوردند ولی [آنها] انکار می‌کردند. پس خدا [گریبان‌] آنها را گرفت، زیرا او نیرومند سخت‌کیفر است.'),
(4156, 40, 23, 'و به یقین، موسی را با آیات خود و حجّتی آشکار فرستادیم،'),
(4157, 40, 24, 'به سوی فرعون و هامان و قارون، [امّا آنان‌] گفتند: «افسونگری شیّاد است.»'),
(4158, 40, 25, 'پس وقتی حقیقت را از جانب ما برای آنان آورد، گفتند: «پسران کسانی را که با او ایمان آورده‌اند بکشید و زنانشان را زنده بگذارید.» و[لی‌] نیرنگ کافران جز در گمراهی نیست.'),
(4159, 40, 26, 'و فرعون گفت: «مرا بگذارید موسی را بکشم تا پروردگارش را بخواند. من می‌ترسم آیین شما را تغییر دهد یا در این سرزمین فساد کند.»'),
(4160, 40, 27, 'و موسی گفت: «من از هر متکبری که به روز حساب عقیده ندارد، به پروردگار خود و پروردگار شما پناه برده‌ام.»'),
(4161, 40, 28, 'و مردی مؤمن از خاندان فرعون که ایمان خود را نهان می‌داشت، گفت: «آیا مردی را می‌کشید که می‌گوید: پروردگار من خداست؟ و مسلماً برای شما از جانب پروردگارتان دلایل آشکاری آورده، و اگر دروغگو باشد دروغش به زیان اوست، و اگر راستگو باشد برخی از آنچه به شما وعده می‌دهد به شما خواهد رسید، چرا که خدا کسی را که افراطکار دروغزن باشد هدایت نمی‌کند.'),
(4162, 40, 29, 'ای قوم من، امروز فرمانروایی از آنِ شماست [و] در این سرزمین مسلطید، و[لی‌] چه کسی ما را از بلای خدا -اگر به ما برسد- حمایت خواهد کرد؟» فرعون گفت: «جز آنچه می‌بینم، به شما نمی‌نمایم، و شما را جز به راه راست راهبر نیستم.»'),
(4163, 40, 30, 'و کسی که ایمان آورده بود، گفت: «ای قوم من، من از [روزی‌] مثل روز دسته‌ها[ی مخالف خدا] بر شما می‌ترسم.'),
(4164, 40, 31, '[از سرنوشتی‌] نظیر سرنوشت قوم نوح و عاد و ثمود، و کسانی که پس از آنها [آمدند]. و [گرنه‌] خدا بر بندگان [خود] ستم نمی‌خواهد.'),
(4165, 40, 32, 'و ای قوم من، من بر شما از روزی که مردم یکدیگر را [به یاری هم‌] ندا درمی‌دهند، بیم دارم.'),
(4166, 40, 33, 'روزی که پشت‌کنان [به عنف‌] بازمی‌گردید، برای شما در برابر خدا هیچ حمایتگری نیست؛ و هر که را خدا گمراه کند او را راهبری نیست.»'),
(4167, 40, 34, 'و به یقین، یوسف پیش از این، دلایل آشکار برای شما آورد، و از آنچه برای شما آورد همواره در تردید بودید تا وقتی که از دنیا رفت، گفتید: «خدا بعد از او هرگز فرستاده‌ای را برنخواهد انگیخت.» این گونه، خدا هر که را افراطگر شکّاک است، بی راه می‌گذارد.'),
(4168, 40, 35, 'کسانی که در باره آیات خدا -بدون حجتی که برای آنان آمده باشد- مجادله می‌کنند، [این ستیزه‌] در نزد خدا و نزد کسانی که ایمان آورده‌اند [مایه‌] عداوت بزرگی است. این گونه، خدا بر دل هر متکبر و زورگویی مُهر می‌نهد.'),
(4169, 40, 36, 'و فرعون گفت: «ای هامان، برای من کوشکی بلند بساز، شاید من به آن راهها برسم:'),
(4170, 40, 37, 'راههای [دستیابی به‌] آسمانها، تا از خدای موسی اطلاع حاصل کنم و من او را سخت دروغپرداز می‌پندارم.» و این گونه برای فرعون، زشتی کارش آراسته شد و از راه [راست‌] بازماند؛ و نیرنگ فرعون جز به تباهی نینجامید.'),
(4171, 40, 38, 'و آن کس که ایمان آورده بود گفت: «ای قوم من، مرا پیروی کنید تا شما را به راه درست هدایت کنم.'),
(4172, 40, 39, 'ای قوم من، این زندگی دنیا تنها کالایی [ناچیز] است، و در حقیقت، آن آخرت است که سرای پایدار است.'),
(4173, 40, 40, 'هر که بدی کند، جز به مانند آن کیفر نمی‌یابد؛ و هر که کار شایسته کند -چه مرد باشد یا زن- در حالی که ایمان داشته باشد، در نتیجه آنان داخل بهشت می‌شوند و در آنجا بی‌حساب روزی می‌یابند.'),
(4174, 40, 41, 'و ای قوم من، چه شده است که من شما را به نجات فرا می‌خوانم و [شما] مرا به آتش فرا می‌خوانید؟'),
(4175, 40, 42, 'مرا فرا می‌خوانید تا به خدا کافر شوم و چیزی را که بدان علمی ندارم با او شریک گردانم؛ و من شما را به سوی آن ارجمند آمرزنده دعوت می‌کنم.'),
(4176, 40, 43, 'آنچه مرا به سوی آن دعوت می‌کنید، به ناچار نه در دنیا و نه در آخرت [درخور] خواندن نیست، و در حقیقت، برگشت ما به سوی خداست، و افراطگران همدمان آتشند.'),
(4177, 40, 44, 'پس به زودی آنچه را به شما می‌گویم به یاد خواهید آورد. و کارم را به خدا می‌سپارم؛ خداست که به [حال‌] بندگان [خود] بیناست.»'),
(4178, 40, 45, 'پس خدا او را از عواقب سوء آنچه نیرنگ می‌کردند حمایت فرمود، و فرعونیان را عذاب سخت فرو گرفت.'),
(4179, 40, 46, '[اینک هر] صبح و شام بر آتش عرضه می‌شوند، و روزی که رستاخیز بر پا شود [فریاد می‌رسد که:] «فرعونیان را در سخت‌ترین [انواع‌] عذاب درآورید.»'),
(4180, 40, 47, 'و آنگاه که در آتش شروع به آوردن حجّت می‌کنند، زیردستان به کسانی که گردنکش بودند، می‌گویند: «ما پیرو شما بودیم؛ پس آیا می‌توانید پاره‌ای از این آتش را از ما دفع کنید؟»'),
(4181, 40, 48, 'کسانی که گردنکشی می‌کردند، می‌گویند: «[اکنون‌] همه ما در آن هستیم. خداست که میان بندگان [خود] داوری کرده است.»'),
(4182, 40, 49, 'و کسانی که در آتشند، به نگهبانان جهنّم می‌گویند: «پروردگارتان را بخوانید تا یک روز از این عذاب را به ما تخفیف دهد.»'),
(4183, 40, 50, 'می‌گویند: «مگر پیامبرانتان دلایل روشن به سوی شما نیاوردند؟» می‌گویند: «چرا.» می‌گویند: «پس بخوانید. و[لی‌] دعای کافران جز در بیراهه نیست.»'),
(4184, 40, 51, 'در حقیقت، ما فرستادگان خود و کسانی را که گرویده‌اند، در زندگی دنیا و روزی که گواهان برپای می‌ایستند قطعاً یاری می‌کنیم؛'),
(4185, 40, 52, '[همان‌] روزی که ستمگران را پوزش‌طلبی‌شان سود نمی‌دهد، و برای آنان لعنت است، و برایشان بدفرجامی آن سرای است.'),
(4186, 40, 53, 'و قطعاً موسی را هدایت دادیم، و به فرزندان اسرائیل تورات را به میراث نهادیم؛'),
(4187, 40, 54, '[که‌] رهنمود و یادکردی برای خردمندان است.'),
(4188, 40, 55, 'پس صبر کن که وعده خدا حق است و برای گناهت آمرزش بخواه و به سپاس پروردگارت، شامگاهان و بامدادان ستایشگر باش.'),
(4189, 40, 56, 'در حقیقت، آنان که در باره نشانه‌های خدا -بی‌آنکه حجّتی برایشان آمده باشد- به مجادله برمی‌خیزند در دلهایشان جز بزرگنمایی نیست [و] آنان به آن [بزرگی که آرزویش را دارند] نخواهند رسید. پس به خدا پناه جوی، زیرا او خود شنوای بیناست.'),
(4190, 40, 57, 'قطعاً آفرینش آسمانها و زمین بزرگتر [و شکوهمندتر] از آفرینش مردم است، ولی بیشتر مردم نمی‌دانند.'),
(4191, 40, 58, 'و نابینا و بینا یکسان نیستند، و کسانی که ایمان آورده و کارهای شایسته کرده‌اند [نیز] با [مردم‌] بدکار [یکسان‌] نیستند؛ چه اندک پند می‌پذیرید.'),
(4192, 40, 59, 'در حقیقت، رستاخیز قطعاً آمدنی است؛ در آن تردیدی نیست، ولی بیشتر مردم ایمان نمی‌آورند.'),
(4193, 40, 60, 'و پروردگارتان فرمود: «مرا بخوانید تا شما را اجابت کنم. در حقیقت، کسانی که از پرستش من کبر می‌ورزند به زودی خوار در دوزخ درمی‌آیند.»'),
(4194, 40, 61, 'خدا [همان‌] کسی است که شب را برای شما پدید آورد تا در آن آرام گیرید، و روز را روشنی‌بخش [قرار داد]. آری، خدا بر مردم بسیار صاحب‌تفضل است، ولی بیشتر مردم سپاس نمی‌دارند.'),
(4195, 40, 62, 'این است خدا، پروردگار شما [که‌] آفریننده هر چیزی است: خدایی جز او نیست. پس چگونه [از او] بازگردانیده می‌شوید؟'),
(4196, 40, 63, 'کسانی که نشانه‌های خدا را انکار می‌کردند، این گونه [از خدا] رویگردان می‌شوند.'),
(4197, 40, 64, 'خدا [همان‌] کسی است که زمین را برای شما قرارگاه ساخت و آسمان را بنایی [گردانید] و شما را صورتگری کرد و صورتهای شما را نیکو نمود و از چیزهای پاکیزه به شما روزی داد. این است خدا پروردگار شما! بلندمرتبه و بزرگ است خدا، پروردگار جهانیان.'),
(4198, 40, 65, 'اوست [همان‌] زنده‌ای که خدایی جز او نیست. پس او را در حالی که دین [خود] را برای وی بی‌آلایش گردانیده‌اید بخوانید. سپاس‌[ها همه‌] ویژه خدا پروردگار جهانیان است.'),
(4199, 40, 66, 'بگو: «من نهی شده‌ام از اینکه جز خدا کسانی را که [شما] می‌خوانید پرستش کنم [آن هم‌] هنگامی که از جانب پروردگارم مرا دلایل روشن رسیده باشد، و مأمورم که فرمانبر پروردگار جهانیان باشم.»'),
(4200, 40, 67, 'او همان کسی است که شما را از خاکی آفرید، سپس از نطفه‌ای، آنگاه از علقه‌ای، و بعد شما را [به صورت‌] کودکی برمی‌آورد، تا به کمال قوّت خود برسید و تا سالمند شوید، و از میان شما کسی است که مرگ پیش‌رس می‌یابد، و تا [بالاخره‌] به مدتی که مقرّر است برسید، و امید که در اندیشه فرو روید.'),
(4201, 40, 68, 'او همان کسی است که زنده می‌کند و می‌میراند، و چون به کاری حکم کند، همین قدر به آن می‌گوید: «باش.» بی‌درنگ موجود می‌شود.'),
(4202, 40, 69, 'آیا کسانی را که در [ابطال‌] آیات خدا مجادله می‌کنند ندیده‌ای [که‌] تا کجا [از حقیقت‌] انحراف حاصل کرده‌اند؟'),
(4203, 40, 70, 'کسانی که کتاب [خدا] و آنچه را که فرستادگان خود را بدان گسیل داشته‌ایم تکذیب کرده‌اند، به زودی خواهند دانست؛'),
(4204, 40, 71, 'هنگامی که غلها در گردنهایشان [افتاده‌] و [با] زنجیرها کشانیده می‌شوند،'),
(4205, 40, 72, 'در میان جوشاب. [و] آنگاه در آتش برافروخته می‌شوند.'),
(4206, 40, 73, 'آنگاه به آنان گفته می‌شود: «آنچه را در برابر خدا [با او] شریک می‌ساختید کجایند؟»'),
(4207, 40, 74, 'می‌گویند: «گمشان کردیم، بلکه پیشتر [هم‌] ما چیزی را نمی‌خواندیم.» این گونه خدا کافران را بی‌راه می‌گذارد.'),
(4208, 40, 75, 'این [عقوبت‌] به سبب آن است که در زمین به ناروا شادی و سرمستی می‌کردید و بدان سبب است که [سخت به خود] می‌نازیدید.'),
(4209, 40, 76, 'از درهای دوزخ درآیید، در آن جاودان [بمانید]. چه بد است جای سرکشان.'),
(4210, 40, 77, 'پس صبر کن که وعده خدا راست است. پس -چه پاره‌ای از آنچه را که به آنان وعده داده‌ایم، به تو بنمایانیم، چه تو را از دنیا ببریم- [در هر صورت آنان‌] به سوی ما بازگردانیده می‌شوند.'),
(4211, 40, 78, 'و مسلّماً پیش از تو فرستادگانی را روانه کردیم. برخی از آنان را [ماجرایشان را] بر تو حکایت کرده‌ایم و برخی از ایشان را بر تو حکایت نکرده‌ایم، و هیچ فرستاده‌ای را نرسد که بی‌اجازه خدا نشانه‌ای بیاورد. پس چون فرمان خدا برسد به حق داوری می‌شود، و آنجاست که باطل‌کاران زیان می‌کنند.'),
(4212, 40, 79, 'خدا [همان‌] کسی است که چهارپایان را برای شما پدید آورد تا از برخی از آنها سواری گیرید و از برخی از آنها بخورید.'),
(4213, 40, 80, 'و در آنها برای شما سودهاست تا با [سوار شدن بر] آنها به مقصودی که در دلهایتان است برسید، و بر آنها و بر کشتی حمل می‌شوید.'),
(4214, 40, 81, 'و نشانه‌های [قدرت‌] خویش را به شما می‌نمایاند. پس کدام یک از آیات خدا را انکار می‌کنید؟'),
(4215, 40, 82, 'آیا در زمین نگشته‌اند تا ببینند فرجام کسانی که پیش از آنان بودند چگونه بوده است؟ [آنها به مراتب از حیث تعداد،] بیشتر از آنان، و [از حیث‌] نیرو و آثار در روی زمین، استوارتر بودند، و[لی‌] آنچه به دست می‌آوردند، به حالشان سودی نبخشید.'),
(4216, 40, 83, 'و چون پیامبرانشان دلایل آشکار برایشان آوردند، به آن چیز [مختصری‌] از دانش که نزدشان بود خرسند شدند، و [سرانجام‌] آنچه به ریشخند می‌گرفتند آنان را فروگرفت.'),
(4217, 40, 84, 'پس چون سختی [عذاب‌] ما را دیدند گفتند: «فقط به خدا ایمان آوردیم و بدانچه با او شریک می‌گردانیدیم کافریم.»'),
(4218, 40, 85, 'و[لی‌] هنگامی که عذاب ما را مشاهده کردند، دیگر ایمانشان برای آنها سودی نداد. سنّت خداست که از [دیرباز] در باره بندگانش چنین جاری شده، و آنجاست که ناباوران زیان کرده‌اند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حاء، میم.'),
(4220, 41, 2, 'وحی [نامه‌]ای است از جانب [خدای‌] رحمتگر مهربان.'),
(4221, 41, 3, 'کتابی است که آیات آن، به روشنی بیان شده. قرآنی است به زبان عربی برای مردمی که می‌دانند.'),
(4222, 41, 4, 'بشارتگر و هشداردهنده است. و[لی‌] بیشتر آنان رویگردان شدند، در نتیجه [چیزی را] نمی‌شنوند.'),
(4223, 41, 5, 'و گفتند: «دلهای ما از آنچه ما را به سوی آن می‌خوانی سخت محجوب و مهجور است. و در گوشهای ما سنگینی و میان ما و تو پرده‌ای است؛ پس تو کار خود را بکن ما [هم‌] کار خود را می‌کنیم.»'),
(4224, 41, 6, 'بگو: «من، بشری چون شمایم، جز اینکه به من وحی می‌شود که خدای شما خدایی یگانه است. پس مستقیماً به سوی او بشتابید و از او آمرزش بخواهید. و وای بر مشرکان.»'),
(4225, 41, 7, 'همان کسانی که زکات نمی‌دهند و آنان که به آخرت ناباورند.'),
(4226, 41, 8, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند آنان را پاداشی بی‌پایان است.'),
(4227, 41, 9, 'بگو: «آیا این شمایید که واقعاً به آن کسی که زمین را در دو هنگام آفرید، کفر می‌ورزید و برای او همتایانی قرار می‌دهید؟ این است پروردگار جهانیان.»'),
(4228, 41, 10, 'و در [زمین‌]، از فراز آن [لنگرآسا] کوهها نهاد و در آن خیر فراوان پدید آورد، و مواد خوراکی آن را در چهار روز اندازه‌گیری کرد [که‌] برای خواهندگان، درست [و متناسب با نیازهایشان‌] است.'),
(4229, 41, 11, 'سپس آهنگ [آفرینش‌] آسمان کرد، و آن بخاری بود. پس به آن و به زمین فرمود: «خواه یا ناخواه بیایید.» آن دو گفتند: «فرمان‌پذیر آمدیم.»'),
(4230, 41, 12, 'پس آنها را [به صورت‌] هفت آسمان، در دو هنگام مقرّر داشت و در هر آسمانی کار [مربوط به‌] آن را وحی فرمود، و آسمان [این‌] دنیا را به چراغها آذین کردیم و [آن را نیک‌] نگاه داشتیم؛ این است اندازه‌گیری آن نیرومند دانا.'),
(4231, 41, 13, 'پس اگر روی برتافتند بگو: «شما را از آذرخشی چون آذرخش عاد و ثمود بر حذر داشتم.»'),
(4232, 41, 14, 'چون فرستادگان [ما] از پیش رو و از پشت سرشان بر آنان آمدند [و گفتند:] «زنهار، جز خدا را مپرستید»، گفتند: «اگر پروردگار ما می‌خواست، قطعاً فرشتگانی فرومی‌فرستاد، پس ما به آنچه بدان فرستاده شده‌اید کافریم.»'),
(4233, 41, 15, 'و امّا عادیان، به ناحق، در زمین سر برافراشتند و گفتند: «از ما نیرومندتر کیست؟» آیا ندانسته‌اند که آن خدایی که خلقشان کرده خود از ایشان نیرومندتر است؟ و در نتیجه آیات ما را انکار می‌کردند.'),
(4234, 41, 16, 'پس بر آنان تندبادی توفنده در روزهایی شوم فرستادیم تا در زندگی دنیا عذاب رسوایی را بدانان بچشانیم و قطعاً عذاب آخرت رسواکننده‌تر است و آنان یاری نخواهند شد.'),
(4235, 41, 17, 'و اما ثمودیان: پس آنان را راهبری کردیم و[لی‌] کوردلی را بر هدایت ترجیح دادند، پس به [کیفر] آنچه مرتکب می‌شدند صاعقه عذاب خفت‌آور آنان را فروگرفت.'),
(4236, 41, 18, 'و کسانی را که ایمان آورده بودند و پروا می‌داشتند رهانیدیم.'),
(4237, 41, 19, 'و [یاد کن‌] روزی را که دشمنان خدا به سوی آتش گردآورده و بازداشت [و دسته دسته تقسیم‌] می‌شوند.'),
(4238, 41, 20, 'تا چون بدان رسند، گوششان و دیدگانشان و پوستشان به آنچه می‌کرده‌اند، بر ضدّشان گواهی دهند.'),
(4239, 41, 21, 'و به پوست [بدن‌] خود می‌گویند: «چرا بر ضدّ ما شهادت دادید؟» می‌گویند: «همان خدایی که هر چیزی را به زبان درآورده ما را گویا گردانیده است، و او نخستین بار شما را آفرید و به سوی او برگردانیده می‌شوید.»'),
(4240, 41, 22, 'و [شما] از اینکه مبادا گوش و دیدگان و پوستتان بر ضدّ شما گواهی دهند [گناهانتان را] پوشیده نمی‌داشتید لیکن گمان داشتید که خدا بسیاری از آنچه را که می‌کنید نمی‌داند.'),
(4241, 41, 23, 'و همین بود گمانتان که در باره پروردگارتان بردید؛ شما را هلاک کرد و از زیانکاران شدید.'),
(4242, 41, 24, 'پس اگر شکیبایی نمایند جایشان در آتش است و اگر از در پوزش درآیند مورد اجابت قرار نمی‌گیرند.'),
(4243, 41, 25, 'و برای آنان دمسازانی گذاشتیم، و آنچه در دسترس ایشان و آنچه در پی آنان بود در نظرشان زیبا جلوه دادند و فرمان [عذاب‌] در میان امّتهایی از جن و انس که پیش از آنان روزگار به سر برده بودند، بر ایشان واجب آمد، چرا که آنها زیانکاران بودند.'),
(4244, 41, 26, 'و کسانی که کافر شدند گفتند: «به این قرآن گوش مدهید و سخن لغو در آن اندازید، شاید شما پیروز شوید.»'),
(4245, 41, 27, 'و قطعاً کسانی را که کافر شده‌اند عذابی سخت می‌چشانیم و حتماً آنها را به بدتر از آنچه می‌کرده‌اند جزا می‌دهیم.'),
(4246, 41, 28, 'آری، سزای دشمنان خدا همان آتش است که در آن، منزل همیشگی دارند. [این‌] جزا به کیفر آن است که نشانه‌های ما را انکار می‌کردند.'),
(4247, 41, 29, 'و کسانی که کفر ورزیدند گفتند: «پروردگارا، آن دو [گمراه‌گری‌] از جن و انس که ما را گمراه کردند به ما نشان ده تا آنها را زیر قدمهایمان بگذاریم تا زبون شوند.»'),
(4248, 41, 30, 'در حقیقت، کسانی که گفتند: «پروردگار ما خداست»؛ سپس ایستادگی کردند، فرشتگان بر آنان فرود می‌آیند [و می‌گویند:] «هان، بیم مدارید و غمین مباشید، و به بهشتی که وعده یافته بودید شاد باشید.'),
(4249, 41, 31, 'در زندگی دنیا و در آخرت دوستانتان ماییم، و هر چه دلهایتان بخواهد در [بهشت‌] برای شماست، و هر چه خواستار باشید در آنجا خواهید داشت؛'),
(4250, 41, 32, 'روزیِ آماده‌ای از سوی آمرزنده مهربان است.»'),
(4251, 41, 33, 'و کیست خوشگفتارتر از آن کس که به سوی خدا دعوت نماید و کار نیک کند و گوید: «من [در برابر خدا] از تسلیم‌شدگانم»؟'),
(4252, 41, 34, 'و نیکی با بدی یکسان نیست. [بدی را] آنچه خود بهتر است دفع کن؛ آنگاه کسی که میان تو و میان او دشمنی است، گویی دوستی یکدل می‌گردد.'),
(4253, 41, 35, 'و این [خصلت‌] را جز کسانی که شکیبا بوده‌اند نمی‌یابند، و آن را جز صاحب بهره‌ای بزرگ، نخواهد یافت.'),
(4254, 41, 36, 'و اگر دمدمه‌ای از شیطان تو را از جای درآورد، پس به خدا پناه ببر که او خود شنوای داناست.'),
(4255, 41, 37, 'و از نشانه‌های [حضور] او شب و روز و خورشید و ماه است؛ نه برای خورشید سجده کنید و نه برای ماه، و اگر تنها او را می‌پرستید آن خدایی را سجده کنید که آنها را خلق کرده است.'),
(4256, 41, 38, 'پس اگر کبر ورزیدند، کسانی که در پیشگاه پروردگار تواند شبانه‌روز او را نیایش می‌کنند و خسته نمی‌شوند.'),
(4257, 41, 39, 'و از [دیگر] نشانه‌های او این است که تو زمین را فسرده می‌بینی و چون باران بر آن فروریزیم به جنبش درآید و بردمد. آری، همان کسی که آن را زندگی بخشید قطعاً زنده‌کننده مردگان است. در حقیقت، او بر هر چیزی تواناست.'),
(4258, 41, 40, 'کسانی که در [فهم و ارائه‌] آیات ما کژ می‌روند بر ما پوشیده نیستند. آیا کسی که در آتش افکنده می‌شود بهتر است یا کسی که روز قیامت آسوده‌خاطر می‌آید؟ هر چه می‌خواهید بکنید که او به آنچه انجام می‌دهید بیناست.'),
(4259, 41, 41, 'کسانی که به این قرآن -چون بدیشان رسید- کفر ورزیدند [به کیفر خود می‌رسند] و به راستی که آن کتابی ارجمند است.'),
(4260, 41, 42, 'از پیش روی آن و از پشت سرش باطل به سویش نمی‌آید؛ وحی [نامه‌]ای است از حکیمی ستوده‌[صفات‌].'),
(4261, 41, 43, 'به تو جز آنچه به پیامبران پیش از تو گفته شده است گفته نمی‌شود. به راستی که پروردگار تو دارای آمرزش و دارنده کیفری پر درد است.'),
(4262, 41, 44, 'و اگر [این کتاب را] قرآنی غیر عربی گردانیده بودیم، قطعاً می‌گفتند: «چرا آیه‌های آن روشن بیان نشده؟ کتابی غیر عربی و [مخاطب آن‌] عرب زبان؟» بگو: «این [کتاب‌] برای کسانی که ایمان آورده‌اند رهنمود و درمانی است، و کسانی که ایمان نمی‌آورند در گوشهایشان سنگینی است و قرآن برایشان نامفهوم است، و [گویی‌] آنان را از جایی دور ندا می‌دهند!'),
(4263, 41, 45, 'و به راستی موسی را کتاب [تورات‌] دادیم، پس در آن اختلاف واقع شد، و اگر از جانب پروردگارت فرمان [مهلت‌] سبقت نگرفته بود، قطعاً میانشان داوری شده بود؛ و در حقیقت آنان در باره آن به شکّی سخت دچارند.'),
(4264, 41, 46, 'هر که کار شایسته کند، به سود خود اوست؛ و هر که بدی کند، به زیان خود اوست، و پروردگار تو به بندگان [خود] ستمکار نیست.'),
(4265, 41, 47, 'دانستن هنگام رستاخیز فقط منحصر به اوست، و میوه‌ها از غلافهایشان بیرون نمی‌آیند و هیچ مادینه‌ای بار نمی‌گیرد و بار نمی‌گذارد مگر آنکه او به آن علم دارد. و روزی که [خدا] آنان را ندا می‌دهد: «شریکان من کجایند؟» می‌گویند: «با بانگ رسا به تو می‌گوییم که هیچ گواهی از میان ما نیست.»'),
(4266, 41, 48, 'و آنچه از پیش می‌خواندند، از [نظر] آنان ناپدید می‌شود و می‌دانند که آنان را روی گریز نیست.'),
(4267, 41, 49, 'انسان از دعای خیر خسته نمی‌شود، و چون آسیبی به او رسد مأیوس [و] نومید می‌گردد.'),
(4268, 41, 50, 'و اگر از جانب خود رحمتی -پس از زیانی که به او رسیده است- بچشانیم، قطعاً خواهد گفت: «من سزاوار آنم و گمان ندارم که رستاخیز برپا شود، و اگر هم به سوی پروردگارم بازگردانیده شوم، قطعاً نزد او برایم خوبی خواهد بود. « پس بدون شکّ، کسانی را که کفران کرده‌اند، به آنچه انجام داده‌اند آگاه خواهیم کرد، و مسلماً از عذابی سخت به آنان خواهیم چشانید.'),
(4269, 41, 51, 'و چون انسان را نعمت بخشیم، روی برتابد و خود را کنار کشد، و چون آسیبی بدو رسد دست به دعای فراوان بردارد.'),
(4270, 41, 52, 'بگو: «به من خبر دهید، اگر [قرآن‌] از نزد خدا [آمده‌] باشد و آن را انکار کرده باشید، چه کسی گمراه‌تر از آن کس خواهد بود که به مخالفتی دور و دراز [دچار] آمده باشد؟»'),
(4271, 41, 53, 'به زودی نشانه‌های خود را در افقها[ی گوناگون‌] و در دلهایشان بدیشان خواهیم نمود، تا برایشان روشن گردد که او خود حق است. آیا کافی نیست که پروردگارت خود شاهد هر چیزی است؟'),
(4272, 41, 54, 'آری، آنان در لقای پروردگارشان تردید دارند. آگاه باش که مسلماً او به هر چیزی احاطه دارد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حاء، میم،'),
(4274, 42, 2, 'عین، سین، قاف.'),
(4275, 42, 3, 'این گونه، خدای نیرومند حکیم به سوی تو و به سوی کسانی که پیش از تو بودند، وحی می‌کند.'),
(4276, 42, 4, 'آنچه در آسمانها و آنچه در زمین است از آنِ اوست و او بلندمرتبه بزرگ است.'),
(4277, 42, 5, 'چیزی نمانده که آسمانها از فرازشان بشکافند و [حال آنکه‌] فرشتگان به سپاس پروردگارشان تسبیح می‌گویند و برای کسانی که در زمین هستند آمرزش می‌طلبند. آگاه باش، در حقیقت خداست که آمرزنده مهربان است.'),
(4278, 42, 6, 'و کسانی که به جای او دوستانی برای خود گرفته‌اند، خدا بر ایشان نگهبان است و تو بر آنان گمارده نیستی.'),
(4279, 42, 7, 'و بدین گونه قرآن عربی به سوی تو وحی کردیم تا [مردم‌] مکّه و کسانی را که پیرامون آنند هشدار دهی، و از روز گردآمدن [خلق‌] -که تردیدی در آن نیست- بیم دهی؛ گروهی در بهشتند و گروهی در آتش.'),
(4280, 42, 8, 'و اگر خدا می‌خواست، قطعاً آنان را امتی یگانه می‌گردانید، لیکن هر که را بخواهد، به رحمت خویش درمی‌آورد و ستمگران نه یاری دارند و نه یاوری.'),
(4281, 42, 9, 'آیا به جای او دوستانی برای خود گرفته‌اند؟ خداست که دوست راستین است، و اوست که مردگان را زنده می‌کند، و هموست که بر هر چیزی تواناست.'),
(4282, 42, 10, 'و در باره هر چیزی اختلاف پیدا کردید، داوریش به خدا [ارجاع می‌گردد]. چنین خدایی پروردگار من است. بر او توکل کردم و به سوی او بازمی‌گردم.'),
(4283, 42, 11, 'پدیدآورنده آسمانها و زمین است. از خودتان برای شما جفتهایی قرار داد، و از دامها [نیز] نر و ماده [قرار داد]. بدین وسیله شما را بسیار می‌گرداند. چیزی مانند او نیست و اوست شنوای بینا.'),
(4284, 42, 12, 'کلیدهای آسمانها و زمین از آنِ اوست. برای هر کس که بخواهد روزی را گشاده یا تنگ می‌گرداند. اوست که بر هر چیزی داناست.'),
(4285, 42, 13, 'از [احکام‌] دین، آنچه را که به نوح در باره آن سفارش کرد، برای شما تشریع کرد و آنچه را به تو وحی کردیم و آنچه را که در باره آن به ابراهیم و موسی و عیسی سفارش نمودیم که: «دین را برپا دارید و در آن تفرقه‌اندازی مکنید.» بر مشرکان آنچه که ایشان را به سوی آن فرا می‌خوانی، گران می‌آید. خدا هر که را بخواهد، به سوی خود برمی‌گزیند، و هر که را که از در توبه درآید، به سوی خود راه می‌نماید.'),
(4286, 42, 14, 'و فقط پس از آنکه علم برایشان آمد، راه تفرقه پیمودند [آن هم‌] به صرف حسد [و برتری جویی‌] میان همدیگر. و اگر سخنی [دایر بر تأخیر عذاب‌] از جانب پروردگارت تا زمانی معیّن، پیشی نگرفته بود، قطعاً میانشان داوری شده بود. و کسانی که بعد از آنان کتاب [تورات‌] را میراث یافتند واقعاً در باره او در تردیدی سخت [دچار]اند.'),
(4287, 42, 15, 'بنابراین به دعوت پرداز، و همان گونه که مأموری ایستادگی کن، و هوسهای آنان را پیروی مکن و بگو: «به هر کتابی که خدا نازل کرده است ایمان آوردم و مأمور شدم که میان شما عدالت کنم؛ خدا پروردگار ما و پروردگار شماست؛ اعمال ما از آنِ ما و اعمال شما از آنِ شماست؛ میان ما و شما خصومتی نیست؛ خدا میان ما را جمع می‌کند، و فرجام به سوی اوست.'),
(4288, 42, 16, 'و کسانی که در باره خدا پس از اجابت [دعوت‌] او به مجادله می‌پردازند، حجّتشان پیش پروردگارشان باطل است، و خشمی [از خدا] برایشان است و برای آنان عذابی سخت خواهد بود.'),
(4289, 42, 17, 'خدا همان کسی است که کتاب و وسیله سنجش را به حق فرود آورد، و تو چه می‌دانی شاید رستاخیز نزدیک باشد.'),
(4290, 42, 18, 'کسانی که به آن ایمان ندارند شتابزده آن را می‌خواهند، و کسانی که ایمان آورده‌اند، از آن هراسناکند و می‌دانند که آن حقّ است. بدان که آنان که در مورد قیامت تردید می‌ورزند، قطعاً در گمراهی دور و درازی‌اند.'),
(4291, 42, 19, 'خدا نسبت به بندگانش مهربان است: هر که را بخواهد روزی می‌دهد و اوست نیرومند غالب.'),
(4292, 42, 20, 'کسی که کِشت آخرت بخواهد، برای وی در کِشته‌اش می‌افزاییم، و کسی که کِشت این دنیا را بخواهد به او از آن می‌دهیم و[لی‌] در آخرت او را نصیبی نیست.'),
(4293, 42, 21, 'آیا برای آنان شریکانی است که در آنچه خدا بدان اجازه نداده، برایشان بنیاد آیینی نهاده‌اند؟ و اگر فرمان قاطع [در باره تأخیر عذاب در کار] نبود مسلماً میانشان داوری می‌شد؛ و برای ستمکاران شکنجه‌ای پر درد است.'),
(4294, 42, 22, '[در قیامت‌] ستمگران را از آنچه انجام داده‌اند، هراسناک می‌بینی و [جزای عملشان‌] به آنان خواهد رسید، و کسانی که ایمان آورده و کارهای شایسته کرده‌اند در باغهای بهشتند. آنچه را بخواهند نزد پروردگارشان خواهند داشت؛ این است همان فضل عظیم.'),
(4295, 42, 23, 'این همان [پاداشی‌] است که خدا بندگان خود را که ایمان آورده و کارهای شایسته کرده‌اند [بدان‌] مژده داده است. بگو: «به ازای آن [رسالت‌] پاداشی از شما خواستار نیستم، مگر دوستی در باره خویشاوندان.» و هر کس نیکی به جای آورد [و طاعتی اندوزد]، برای او در ثواب آن خواهیم افزود. قطعاً خدا آمرزنده و قدرشناس است.'),
(4296, 42, 24, 'آیا می‌گویند: «بر خدا دروغی بسته است؟» پس اگر خدا بخواهد بر دلت مُهر می نهد؛ و خدا باطل را محو و حقیقت را با کلمات خویش پا برجا می‌کند. اوست که به راز دلها داناست.'),
(4297, 42, 25, 'و اوست کسی که توبه را از بندگان خود می‌پذیرد و از گناهان درمی‌گذرد و آنچه می‌کنید می‌داند.'),
(4298, 42, 26, 'و [درخواست‌] کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند اجابت می‌کند و از فضل خویش به آنان زیاده می‌دهد، و[لی‌] برای کافران عذاب سختی خواهد بود.'),
(4299, 42, 27, 'و اگر خدا روزی را بر بندگانش فراخ گرداند، مسلماً در زمین سر به عصیان برمی‌دارند، لیکن آنچه را بخواهد به اندازه‌ای [که مصلحت است‌] فرو می‌فرستد. به راستی که او به [حال‌] بندگانش آگاهِ بیناست.'),
(4300, 42, 28, 'و اوست کسی که باران را -پس از آنکه [مردم‌] نومید شدند- فرود می‌آورد، و رحمت خویش را می‌گسترد و هموست سرپرست ستوده.'),
(4301, 42, 29, 'و از نشانه‌های [قدرت‌] اوست آفرینش آسمانها و زمین و آنچه از [انواع‌] جنبنده در میان آن دو پراکنده است، و او هرگاه بخواهد بر گردآوردن آنان تواناست.'),
(4302, 42, 30, 'و هر [گونه‌] مصیبتی به شما برسد به سبب دستاورد خود شماست، و [خدا] از بسیاری درمی‌گذرد.'),
(4303, 42, 31, 'و شما در زمین درمانده کننده [خدا] نیستید، و جز خدا شما را سرپرست و یاوری نیست.'),
(4304, 42, 32, 'و از نشانه‌های او سفینه‌های کوه‌آسا در دریاست.'),
(4305, 42, 33, 'اگر بخواهد باد را ساکن می‌گرداند و [سفینه‌ها] بر پشت [آب‌] متوقف می‌مانند. قطعاً در این [امر] برای هر شکیبای شکرگزاری نشانه‌هاست.'),
(4306, 42, 34, 'یا به [سزای‌] آنچه [کشتی‌نشینان‌] مرتکب شده‌اند هلاکشان کند، و[لی‌] از بسیاری درمی‌گذرد.'),
(4307, 42, 35, 'و [تا] آنان که در آیات ما مجادله می‌کنند، بدانند که ایشان را [روی‌] گریزی نیست.'),
(4308, 42, 36, 'و آنچه به شما داده شده، برخورداری [و کالای‌] زندگی دنیاست، و آنچه پیش خداست برای کسانی که گرویده‌اند و به پروردگارشان اعتماد دارند بهتر و پایدارتر است.'),
(4309, 42, 37, 'و کسانی که از گناهان بزرگ و زشتکاریها خود را به دور می‌دارند و چون به خشم درمی‌آیند درمی‌گذرند.'),
(4310, 42, 38, 'و کسانی که [ندای‌] پروردگارشان را پاسخ [مثبت‌] داده و نماز برپا کرده‌اند و کارشان در میانشان مشورت است و از آنچه روزیشان داده‌ایم انفاق می‌کنند.'),
(4311, 42, 39, 'و کسانی که چون ستم بر ایشان رسد، یاری می‌جویند [و به انتقام بر می‌خیزند].'),
(4312, 42, 40, 'و جزای بدی، مانند آن، بدی است. پس هر که درگذرد و نیکوکاری کند، پاداش او بر [عهده‌] خداست. به راستی او ستمگران را دوست نمی‌دارد.'),
(4313, 42, 41, 'و هر که پس از ستم [دیدن‌] خود، یاری جوید [و انتقام گیرد] راه [نکوهشی‌] بر ایشان نیست.'),
(4314, 42, 42, 'راه [نکوهش‌] تنها بر کسانی است که به مردم ستم می‌کنند، و در [روی‌] زمین به ناحق سر برمی‌دارند. آنان عذابی دردناک [در پیش‌] خواهند داشت.'),
(4315, 42, 43, 'و هر که صبر کند و درگذرد، مسلماً این [خویشتن داری، حاکی‌] از اراده قوی [در] کارهاست.'),
(4316, 42, 44, 'و هر که را خدا بی‌راه گذارد، پس از او یار [و یاور]ی نخواهد داشت، و ستمگران را می‌بینی که چون عذاب را بنگرند می‌گویند: «آیا راهی برای برگشتن [به دنیا] هست؟»'),
(4317, 42, 45, 'آنان را می‌بینی [که چون‌] بر [آتش‌] عرضه می‌شوند، از [شدت‌] زبونی، فروتن شده‌اند: زیرچشمی می‌نگرند. و کسانی که گرویده‌اند می‌گویند: «در حقیقت، زیانکاران کسانی‌اند که روز قیامت خودشان و کسانشان را دچار زیان کرده‌اند.» آری، ستمکاران در عذابی پایدارند.'),
(4318, 42, 46, 'و جز خدا برای آنان دوستانی [دیگر] نیست که آنها را یاری کنند، و هر که را خدا بی‌راه کذارد هیچ راهی برای او نخواهد بود.'),
(4319, 42, 47, 'پیش از آنکه روزی فرا رسد که آن را از جانب خدا برگشتی نباشد، پروردگارتان را اجابت کنید. آن روز نه برای شما پناهی و نه برایتان [مجال‌] انکاری هست.'),
(4320, 42, 48, 'پس اگر روی برتابند، ما تو را بر آنان نگهبان نفرستاده‌ایم. بر عهده تو جز رسانیدن [پیام‌] نیست، و ما چون رحمتی از جانب خود به انسان بچشانیم، بدان شاد و سرمست گردد، و چون به [سزای‌] دستاورد پیشین آنها، به آنان بدی رسد، انسان ناسپاسی می‌کند.'),
(4321, 42, 49, 'فرمانروایی [مطلق‌] آسمانها و زمین از آنِ خداست؛ هر چه بخواهد می‌آفریند؛ به هر کس بخواهد فرزند دختر و به هرکس بخواهد فرزند پسر می‌دهد.'),
(4322, 42, 50, 'یا آنها را پسر[ان‌] و دختر[انی‌] توأم با یکدیگر می‌گرداند، و هر که را بخواهد عقیم می‌سازد. اوست دانای توانا.'),
(4323, 42, 51, 'و هیچ بشری را نرسد که خدا با او سخن گوید جز [از راه‌] وحی یا از فراسوی حجابی، یا فرستاده‌ای بفرستد و به اذن او هر چه بخواهد وحی نماید. آری، اوست بلندمرتبه سنجیده‌کار.'),
(4324, 42, 52, 'وهمین گونه، روحی از امر خودمان به سوی تو وحی کردیم. تو نمی‌دانستی کتاب چیست و نه ایمان [کدام است؟] ولی آن را نوری گردانیدیم که هر که از بندگان خود را بخواهیم به وسیله آن راه می‌نماییم، و به راستی که تو به خوبی به راه راست هدایت می‌کنی.'),
(4325, 42, 53, 'راه همان خدایی که آنچه در آسمانها و آنچه در زمین است از آنِ اوست. هش‌دار که [همه‌] کارها به خدا بازمی‌گردد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حاء، میم.'),
(4327, 43, 2, 'سوگند به کتاب روشنگر.'),
(4328, 43, 3, 'ما آن را قرآنی عربی قرار دادیم، باشد که بیندیشید.'),
(4329, 43, 4, 'و همانا که آن در کتاب اصلی [=لوح محفوظ] به نزد ما سخت والا و پر حکمت است.'),
(4330, 43, 5, 'آیا به [صِرف‌] اینکه شما قومی منحرفید [باید] قرآن را از شما باز داریم؟'),
(4331, 43, 6, 'و چه بسا پیامبرانی که در [میان‌] گذشتگان روانه کردیم.'),
(4332, 43, 7, 'و هیچ پیامبری به سوی ایشان نیامد، مگر اینکه او را به ریشخند می‌گرفتند.'),
(4333, 43, 8, 'و نیرومندتر از آنان را به هلاکت رسانیدیم و سنّت پیشینیان تکرار شد.'),
(4334, 43, 9, 'و اگر از آنان بپرسی: «آسمانها و زمین را چه کسی آفریده؟» قطعاً خواهند گفت: «آنها را همان قادر دانا آفریده است.»'),
(4335, 43, 10, 'همان کسی که این زمین را برای شما گهواره‌ای گردانید و برای شما در آن راهها نهاد، باشد که راه یابید.'),
(4336, 43, 11, 'و آن کس که آبی به اندازه از آسمان فرود آورد، پس به وسیله آن، سرزمینی مرده را زنده گردانیدیم؛ همین گونه [از گورها] بیرون آورده می‌شوید.'),
(4337, 43, 12, 'و همان کسی که جفتها را یکسره آفرید، و برای شما از کشتیها و دامها [وسیله‌ای که‌] سوار شوید قرار داد.'),
(4338, 43, 13, 'تا بر پشت آن‌[ها] قرار گیرید، پس چون بر آن‌[ها] برنشستید، نعمت پروردگار خود را یاد کنید و بگویید: «پاک است کسی که این را برای ما رام کرد و[گرنه‌] ما را یارای [رام‌ساختن‌] آنها نبود.»'),
(4339, 43, 14, '«و به راستی که ما به سوی پروردگارمان بازخواهیم گشت.»'),
(4340, 43, 15, 'و برای او بعضی از بندگان [خدا] را جزئی [چون فرزند و شریک‌] قرار دادند. به راستی که انسان بس ناسپاس آشکار است.'),
(4341, 43, 16, 'آیا از آنچه می‌آفریند، خود، دخترانی برگرفته و به شما پسران را اختصاص داده است؟'),
(4342, 43, 17, 'و چون یکی از آنان را به آنچه به [خدای‌] رحمان نسبت می‌دهد خبر دهند، چهره او سیاه می‌گردد، در حالی که خشم و تأسف خود را فرو می‌خورد.'),
(4343, 43, 18, 'آیا کسی [را شریک خدا می‌کنند] که در زر و زیور پرورش یافته و در [هنگام‌] مجادله، بیانش غیر روشن است؟'),
(4344, 43, 19, 'و فرشتگانی را که خود، بندگان رحمانند، مادینه [و دختران او] پنداشتند. آیا در خلقت آنان حضور داشتند؟ گواهی ایشان به زودی نوشته می‌شود و [از آن‌] پرسیده خواهند شد.'),
(4345, 43, 20, 'و می‌گویند: «اگر [خدای‌] رحمان می‌خواست، آنها را نمی‌پرستیدیم.» آنان به این [دعوی‌] دانشی ندارند [و] جز حدس نمی‌زنند.'),
(4346, 43, 21, 'آیا به آنان پیش از آن [قرآن‌] کتابی داده‌ایم که بدان تمسک می‌جویند؟'),
(4347, 43, 22, '[نه،] بلکه گفتند: «ما پدران خود را بر آیینی یافتیم و ما [هم با] پی گیری از آنان، راه یافتگانیم.»'),
(4348, 43, 23, 'و بدین گونه در هیچ شهری پیش از تو هشداردهنده‌ای نفرستادیم مگر آنکه خوشگذرانان آن گفتند: «ما پدران خود را بر آیینی [و راهی‌] یافته‌ایم و ما از پی ایشان راهسپریم.»'),
(4349, 43, 24, 'گفت «هر چند هدایت کننده‌تر از آنچه پدران خود را بر آن یافته‌اید برای شما بیاورم؟» گفتند: «ما [نسبت‌] به آنچه بدان فرستاده شده‌اید کافریم.»'),
(4350, 43, 25, 'پس، از آنان انتقام گرفتیم. پس بنگر فرجام تکذیب‌کنندگان چگونه بوده است.'),
(4351, 43, 26, 'و چون ابراهیم به [نا]پدری خود و قومش گفت: «من واقعاً از آنچه می‌پرستید بیزارم،'),
(4352, 43, 27, 'مگر [از] آن کس که مرا پدید آورد؛ و البته او مرا راهنمایی خواهد کرد.»'),
(4353, 43, 28, 'و او آن را در پی خود سخنی جاویدان کرد، باشد که آنان [به توحید] بازگردند.'),
(4354, 43, 29, 'بلکه اینان و پدرانشان را برخوداری دادم تا حقیقت و فرستاده‌ای آشکار به سویشان آمد.'),
(4355, 43, 30, 'و چون حقیقت به سویشان آمد، گفتند: «این افسونی است و ما منکر آنیم.»'),
(4356, 43, 31, 'و گفتند: «چرا این قرآن بر مردی بزرگ از [آن‌] دو شهر فرود نیامده است؟»'),
(4357, 43, 32, 'آیا آنانند که رحمت پروردگارت را تقسیم می‌کنند؟ ما [وسایل‌] معاش آنان را در زندگی دنیا میانشان تقسیم کرده‌ایم، و برخی از آنان را از [نظر] درجات، بالاتر از بعضی [دیگر] قرار داده‌ایم تا بعضی از آنها بعضی [دیگر] را در خدمت گیرند، و رحمت پروردگار تو از آنچه آنان می‌اندوزند بهتر است.'),
(4358, 43, 33, 'و اگر نه آن بود که [همه‌] مردم [در انکار خدا] امتی واحد گردند، قطعاً برای خانه‌های آنان که به [خدای‌] رحمان کفر می‌ورزیدند، سقفها و نردبانهایی از نقره که بر آنها بالا روند قرار می‌دادیم.'),
(4359, 43, 34, 'و برای خانه‌هایشان نیز درها و تختهایی که بر آنها تکیه زنند.'),
(4360, 43, 35, 'و زر و زیورهای [دیگر نیز]. و همه اینها جز متاع زندگی دنیا نیست، و آخرت پیش پروردگار تو برای پرهیزگاران است.'),
(4361, 43, 36, 'و هر کس از یاد [خدای‌] رحمان دل بگرداند، بر او شیطانی می‌گماریم تا برای وی دمسازی باشد.'),
(4362, 43, 37, 'و مسلماً آنها ایشان را از راه باز می‌دارند و [آنها] می‌پندارند که راه یافتگانند.'),
(4363, 43, 38, 'تا آنگاه که او [با دمسازش‌] به حضور ما آید، [خطاب به شیطان‌] گوید: «ای کاش میان من و تو، فاصله خاور و باختر بود، که چه بد دمسازی هستی!»'),
(4364, 43, 39, 'و امروز هرگز [پشیمانی‌] برای شما سود نمی‌بخشد، چون ستم کردید؛ در حقیقت، شما در عذاب، مشترک خواهید بود.'),
(4365, 43, 40, 'پس آیا تو می‌توانی کران را شنوا کنی، یا نابینایان و کسی را که همواره در گمراهی آشکاری است راه نمایی؟'),
(4366, 43, 41, 'پس اگر ما تو را [از دنیا] ببریم، قطعاً از آنان انتقام می‌کشیم،'),
(4367, 43, 42, 'یا [اگر] آنچه را به آنان وعده داده‌ایم به تو نشان دهیم؛ حتماً ما بر آنان قدرت داریم.'),
(4368, 43, 43, 'پس به آنچه به سوی تو وحی شده است چنگ دَرْزَنْ، که تو بر راهی راست قرار داری.'),
(4369, 43, 44, 'و به راستی که [قرآن‌] برای تو و برای قوم تو [مایه‌] تذکّری است، و به زودی [در مورد آن‌] پرسیده خواهید شد.'),
(4370, 43, 45, 'و از رسولان ما که پیش از تو گسیل داشتیم جویا شو؛ آیا در برابر [خدای‌] رحمان، خدایانی که مورد پرستش قرار گیرند مقرر داشته‌ایم؟'),
(4371, 43, 46, 'و همانا موسی را با نشانه‌های خویش به سوی فرعون و سران [قوم‌] او روانه کردیم. پس گفت: «من فرستاده پروردگار جهانیانم.»'),
(4372, 43, 47, 'پس چون آیات ما را برای آنان آورد، ناگهان ایشان بر آنها خنده زدند.'),
(4373, 43, 48, 'و [ما] نشانه‌ای به ایشان نمی‌نمودیم مگر اینکه آن از نظیر [و مشابه‌] آن بزرگتر بود، و به عذاب گرفتارشان کردیم تا مگر به راه آیند.'),
(4374, 43, 49, 'و گفتند: «ای فسونگر، پروردگارت را به [پاس‌] آنچه با تو عهد کرده، برای ما بخوان، که ما واقعاً به راه درست درآمده‌ایم.»'),
(4375, 43, 50, 'و چون عذاب را از آنها برداشتیم، بناگاه آنان پیمان شکستند.'),
(4376, 43, 51, 'و فرعون در [میان‌] قوم خود ندا درداد [و] گفت: «ای مردم [کشور] من، آیا پادشاهی مصر و این نهرها که از زیر [کاخهای‌] من روان است از آنِ من نیست؟ پس مگر نمی‌بینید؟'),
(4377, 43, 52, 'آیا [نه‌] من از این کس که خود بی‌مقدار است و نمی‌تواند درست بیان کند بهترم؟'),
(4378, 43, 53, 'پس چرا بر او دستبندهایی زرین آویخته نشده؟ یا با او فرشتگانی همراه نیامده‌اند؟'),
(4379, 43, 54, 'پس قوم خود را سبک‌مغز یافت [و آنان را فریفت‌] و اطاعتش کردند، چرا که آنها مردمی منحرف بودند.'),
(4380, 43, 55, 'و چون ما را به خشم درآوردند، از آنان انتقام گرفتیم و همه آنان را غرق کردیم.'),
(4381, 43, 56, 'و آنان را پیشینه‌ای [بد] و عبرتی برای آیندگان گردانیدیم.'),
(4382, 43, 57, 'و هنگامی که [در مورد] پسر مریم مثالی آورده شد، بناگاه قوم تو از آن [سخن‌] هِلهِله درانداختند [و اعراض کردند]،'),
(4383, 43, 58, 'و گفتند: «آیا معبودان ما بهترند یا او؟» آن [مثال‌] را جز از راه جدل برای تو نزدند، بلکه آنان مردمی جدل‌پیشه‌اند.'),
(4384, 43, 59, '[عیسی‌] جز بنده‌ای که بر وی منت نهاده و او را برای فرزندان اسرائیل سرمشق [و آیتی‌] گردانیده‌ایم نیست.'),
(4385, 43, 60, 'و اگر بخواهیم قطعاً به جای شما فرشتگانی که در [روی‌] زمین جانشین [شما] گردند قرار دهیم.'),
(4386, 43, 61, 'و همانا آن، نشانه‌ای برای [فهم‌] رستاخیز است، پس زنهار در آن تردید مکن، و از من پیروی کنید؛ این است راه راست!'),
(4387, 43, 62, 'و مبادا شیطان شما را از راه به در برد، زیرا او برای شما دشمنی آشکار است.'),
(4388, 43, 63, 'و چون عیسی دلایل آشکار آورد، گفت: «به راستی برای شما حکمت آوردم، و تا در باره بعضی از آنچه در آن اختلاف می‌کردید برایتان توضیح دهم. پس، از خدا بترسید و فرمانم ببرید.»'),
(4389, 43, 64, 'در حقیقت، خداست که خود پروردگار من و پروردگار شماست. پس او را بپرستید؛ این است راه راست.'),
(4390, 43, 65, 'تا [آنکه‌] از میانشان، احزاب دست به اختلاف زدند، پس وای بر کسانی که ستم کردند از عذاب روزی دردناک!'),
(4391, 43, 66, 'آیا جز [این‌] انتظار می‌برند که رستاخیز -در حالی که حدس نمی‌زنند- ناگهان بر آنان در رسد؟'),
(4392, 43, 67, 'در آن روز، یاران -جز پرهیزگاران- بعضی‌شان دشمن بعضی دیگرند.'),
(4393, 43, 68, 'ای بندگان من، امروز بر شما بیمی نیست و غمگین نخواهید شد.'),
(4394, 43, 69, 'همان کسانی که به آیات ما ایمان آورده و تسلیم بودند.'),
(4395, 43, 70, 'شما با همسرانتان شادمانه داخل بهشت شوید.'),
(4396, 43, 71, 'سینیهایی از طلا و جام‌هایی در برابر آنان می‌گردانند، و در آنجا آنچه دلها آن را بخواهند و دیدگان را خوش آید [هست‌] و شما در آن جاودانید.'),
(4397, 43, 72, 'و این است همان بهشتی که به [پاداش‌] آنچه می‌کردید میراث یافتید.'),
(4398, 43, 73, 'در آنجا برای شما میوه‌هایی فراوان خواهد بود که از آنها می‌خورید.'),
(4399, 43, 74, 'بی‌گمان، مجرمان در عذاب جهنم ماندگارند.'),
(4400, 43, 75, '[عذاب‌] از آنان تخفیف نمی‌یابد و آنها در آنجا نومیدند.'),
(4401, 43, 76, 'و ما بر ایشان ستم نکردیم، بلکه خود ستمکار بودند.'),
(4402, 43, 77, 'و فریاد کشند: «ای مالک، [بگو:] پروردگارت جان ما را بستاند.» پاسخ دهد: « شما ماندگارید.»'),
(4403, 43, 78, 'قطعاً حقیقت را برایتان آوردیم، لیکن بیشتر شما حقیقت را خوش نداشتید.'),
(4404, 43, 79, 'یا در کاری ابرام ورزیده‌اند؟ ما [نیز] ابرام می‌ورزیم.'),
(4405, 43, 80, 'آیا می‌پندارند که ما راز آنها و نجوایشان را نمی‌شنویم؟ چرا، و فرشتگان ما پیش آنان [حاضرند و] ثبت می‌کنند.'),
(4406, 43, 81, 'بگو: «اگر برای [خدای‌] رحمان فرزندی بود، خود من نخستین پرستندگان بودم.»'),
(4407, 43, 82, 'پروردگار آسمانها و زمین [و] پروردگار عرش، از آنچه وصف می‌کنند منزه است.'),
(4408, 43, 83, 'پس آنان را رها کن تا در یاوه‌گویی خود فرو روند و بازی کنند تا آن روزی را که بدان وعده داده می‌شوند دیدار کنند.'),
(4409, 43, 84, 'و اوست که در آسمان خداست و در زمین خداست، و هموست سنجیده‌کار دانا.'),
(4410, 43, 85, 'و خجسته است کسی که فرمانروایی آسمانها و زمین و آنچه میان آن دو است از آنِ اوست، و علم قیامت پیش اوست و به سوی او برگردانیده می‌شوید.'),
(4411, 43, 86, 'و کسانی که به جای او می‌خوانند [و می‌پرستند] اختیار شفاعت ندارند، مگر آن کسانی که آگاهانه به حق گواهی داده باشند.'),
(4412, 43, 87, 'و اگر از آنان بپرسی: «چه کسی آنان را خلق کرده؟» مسلماً خواهند گفت: «خدا.» پس چگونه [از حقیقت‌] بازگردانیده می‌شوند؟'),
(4413, 43, 88, 'و گوید: «ای پروردگار من، اینها جماعتی‌اند که ایمان نخواهند آورد.»'),
(4414, 43, 89, '[و خدا فرمود:] از ایشان روی برتاب و بگو: «به سلامت.» پس زودا که بدانند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حاء، میم.'),
(4416, 44, 2, 'سوگند به کتاب روشنگر،'),
(4417, 44, 3, '[که‌] ما آن را در شبی فرخنده نازل کردیم، [زیرا] که ما هشداردهنده بودیم.'),
(4418, 44, 4, 'در آن [شب‌] هر [گونه‌] کاری [به نحوی‌] استوار فیصله می‌یابد.'),
(4419, 44, 5, '[این‌] کاری است [که‌] از جانب ما [صورت می‌گیرد]. ما فرستنده [پیامبران‌] بودیم.'),
(4420, 44, 6, '[و این‌] رحمتی از پروردگار توست، که او شنوای داناست.'),
(4421, 44, 7, 'پروردگار آسمانها و زمین و آنچه میان آن دو است، اگر یقین دارید.'),
(4422, 44, 8, 'خدایی جز او نیست؛ او زندگی می‌بخشد و می‌میراند؛ پروردگار شما و پروردگار پدران شماست.'),
(4423, 44, 9, 'ولی نه، آنها به شک و شبهه خویش سرگرمند.'),
(4424, 44, 10, 'پس در انتظار روزی باش که آسمان دودی نمایان برمی‌آورد،'),
(4425, 44, 11, 'که مردم را فرو می‌گیرد؛ این است عذاب پر درد.'),
(4426, 44, 12, '[می‌گویند:] «پروردگارا، این عذاب را از ما دفع کن که ما ایمان داریم.»'),
(4427, 44, 13, 'آنان را کجا [جای‌] پند[گرفتن‌] باشد، و حال آنکه به یقین برای آنان پیامبری روشنگر آمده است.'),
(4428, 44, 14, 'پس، از او روی برتافتند و گفتند: «تعلیم‌یافته‌ای دیوانه است.»'),
(4429, 44, 15, 'ما این عذاب را اندکی از شما برمی‌داریم [ولی شما] در حقیقت باز از سر می‌گیرید.'),
(4430, 44, 16, 'روزی که دست به حمله می‌زنیم، همان حمله بزرگ؛ [آنگاه‌] ما انتقام‌کشنده‌ایم.'),
(4431, 44, 17, 'و به یقین، پیش از آنان قوم فرعون را بیازمودیم، و پیامبری بزرگوار برایشان آمد،'),
(4432, 44, 18, 'که [به آنان گفت:] «بندگان خدا را به من بسپارید، زیرا که من شما را فرستاده‌ای امینم.'),
(4433, 44, 19, 'و بر خدا برتری مجویید که من برای شما حجتی آشکار آورده‌ام.'),
(4434, 44, 20, 'و من به پروردگار خود و پروردگار شما پناه می‌برم از اینکه مرا سنگباران کنید.'),
(4435, 44, 21, 'و اگر به من ایمان نمی‌آورید، پس، از من کناره گیرید.»'),
(4436, 44, 22, 'پس پروردگار خود را خواند که: «اینها مردمی گناهکارند.»'),
(4437, 44, 23, '[فرمود:] «بندگانم را شبانه ببر، زیرا شما مورد تعقیب واقع خواهید شد.'),
(4438, 44, 24, 'و دریا را هنگامی که آرام است پشت سر بگذار، که آنان سپاهی غرق‌شدنی‌اند.»'),
(4439, 44, 25, '[وه،] چه باغها و چشمه‌سارانی [که آنها بعد از خود] بر جای نهادند،'),
(4440, 44, 26, 'و کشتزارها و جایگاه‌های نیکو،'),
(4441, 44, 27, 'و نعمتی که از آن برخوردار بودند.'),
(4442, 44, 28, '[آری،] این چنین [بود] و آنها را به مردمی دیگر میراث دادیم.'),
(4443, 44, 29, 'و آسمان و زمین بر آنان زاری نکردند و مهلت نیافتند.'),
(4444, 44, 30, 'و به راستی، فرزندان اسرائیل را از عذاب خفت‌آور رهانیدیم:'),
(4445, 44, 31, 'از [دست‌] فرعون که متکبّری از افراطکاران بود.'),
(4446, 44, 32, 'و قطعاً آنان را دانسته بر مردم جهان ترجیح دادیم.'),
(4447, 44, 33, 'و از نشانه‌ها [ی الهی‌] آنچه را که در آن آزمایشی آشکار بود، بدیشان دادیم.'),
(4448, 44, 34, 'هر آینه این [کافران‌] می‌گویند:'),
(4449, 44, 35, '«جز مرگ نخستین، دیگر [واقعه‌ای‌] نیست و ما زنده‌شدنی نیستیم.'),
(4450, 44, 36, 'اگر راست می‌گویید، پس پدران ما را [باز] آورید.'),
(4451, 44, 37, 'آیا ایشان بهترند یا قوم «تُبّع» و کسانی که پیش از آنها بودند؟ آنها را هلاک کردیم، زیرا که گنهکار بودند.'),
(4452, 44, 38, 'و آسمانها و زمین و آنچه را که میان آن دو است به بازی نیافریده‌ایم؛'),
(4453, 44, 39, 'آنها را جز به حق نیافریده‌ایم، لیکن بیشترشان نمی‌دانند.'),
(4454, 44, 40, 'در حقیقت، روز «جدا سازی» موعد همه آنهاست.'),
(4455, 44, 41, 'همان روزی که هیچ دوستی از هیچ دوستی نمی‌تواند حمایتی کند، و آنان یاری نمی‌شوند،'),
(4456, 44, 42, 'مگر کسی را که خدا رحمت کرده است، زیرا که اوست همان ارجمند مهربان.'),
(4457, 44, 43, 'آری! درخت زقوم،'),
(4458, 44, 44, 'خوراک گناه پیشه است.'),
(4459, 44, 45, 'چون مس گداخته در شکمها می‌گدازد؛'),
(4460, 44, 46, 'همانند جوشش آب جوشان.'),
(4461, 44, 47, 'او را بگیرید و به میان دوزخش بکشانید،'),
(4462, 44, 48, 'آنگاه از عذاب آب جوشان بر سرش فرو ریزید.'),
(4463, 44, 49, 'بچش که تو همان ارجمند بزرگواری!'),
(4464, 44, 50, 'این است همان چیزی که در باره آن تردید می‌کردید.'),
(4465, 44, 51, 'به راستی پرهیزگاران در جایگاهی آسوده [اند]،'),
(4466, 44, 52, 'در بوستانها و کنار چشمه‌سارها.'),
(4467, 44, 53, 'پرنیان نازک و دیبای ستبر می‌پوشند [و] برابر هم نشسته‌اند.'),
(4468, 44, 54, '[آری،] چنین [خواهد بود] و آنها را با حوریان درشت‌چشم همسر می‌گردانیم.'),
(4469, 44, 55, 'در آنجا هر میوه‌ای را [که بخواهند] آسوده خاطر می‌طلبند.'),
(4470, 44, 56, 'در آنجا جز مرگ نخستین، مرگ نخواهند چشید و [خدا] آنها را از عذاب دوزخ نگاه می‌دارد.'),
(4471, 44, 57, '[این‌] بخششی است از جانب پروردگار تو. این است همان کامیابی بزرگ.'),
(4472, 44, 58, 'در حقیقت، [قرآن‌] را بر زبان تو آسان گردانیدیم، امید که پند پذیرند.'),
(4473, 44, 59, 'پس مراقب باش، زیرا که آنان هم مراقبند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حاء، میم.'),
(4475, 45, 2, 'فرو فرستادن این کتاب، از جانب خدای ارجمند سنجیده‌کار است.'),
(4476, 45, 3, 'به راستی در آسمانها و زمین، برای مؤمنان نشانه‌هایی است.'),
(4477, 45, 4, 'و در آفرینش خودتان و آنچه از [انواع‌] جنبنده‌[ها] پراکنده می‌گرداند، برای مردمی که یقین دارند نشانه‌هایی است.'),
(4478, 45, 5, 'و [نیز در] پیاپی آمدن شب و روز، و آنچه خدا از روزی از آسمان فرود آورده و به [وسیله‌] آن، زمین را پس از مرگش زنده گردانیده است؛ و [همچنین در] گردش بادها [به هر سو،] برای مردمی که می‌اندیشند نشانه‌هایی است.'),
(4479, 45, 6, 'این‌[ها]ست آیات خدا که به راستی آن را بر تو می‌خوانیم. پس، بعد از خدا و نشانه‌های او به کدام سخن خواهند گروید؟'),
(4480, 45, 7, 'وای بر هر دروغزن گناه‌پیشه!'),
(4481, 45, 8, '[که‌] آیات خدا را که بر او خوانده می‌شود، می‌شنود و باز به حال تکبر -چنانکه گویی آن را نشنیده است- سماجت می‌ورزد. پس او را از عذابی پردرد خبر ده.'),
(4482, 45, 9, 'و چون از نشانه‌های ما چیزی بداند، آن را به ریشخند می‌گیرد. آنان عذابی خفت‌آور خواهند داشت.'),
(4483, 45, 10, 'پیشاپیش آنها دوزخ است، و نه آنچه را اندوخته و نه آن دوستانی را که غیر از خدا اختیار کرده‌اند، به کارشان می‌آید، و عذابی بزرگ خواهند داشت.'),
(4484, 45, 11, 'این رهنمودی است؛ و کسانی که آیات پروردگارشان را انکار کردند، بر ایشان عذابی دردناک از پلیدی است.'),
(4485, 45, 12, 'خدا همان کسی است که دریا را به سود شما رام گردانید، تا کشتیها در آن به فرمانش روان شوند، و تا از فزون بخشی او [روزی خویش را] طلب نمایید، و باشد که سپاس دارید.'),
(4486, 45, 13, 'و آنچه را در آسمانها و آنچه را در زمین است به سود شما رام کرد؛ همه از اوست. قطعاً در این [امر] برای مردمی که می‌اندیشند نشانه‌هایی است.'),
(4487, 45, 14, 'به کسانی که ایمان آورده‌اند بگو تا از کسانی که به روزهای [پیروزی‌] خدا امید ندارند درگذرند، تا [خدا هر] گروهی را به [سبب‌] آنچه مرتکب می‌شده‌اند به مجازات رساند.'),
(4488, 45, 15, 'هر که کاری شایسته کند، به سود خود اوست، و هر که بدی کند به زیانش باشد. سپس به سوی پروردگارتان برگردانیده می‌شوید.'),
(4489, 45, 16, 'و به یقین، فرزندان اسرائیل را کتاب [تورات‌] و حکم و پیامبری دادیم و از چیزهای پاکیزه روزیشان کردیم و آنان را بر مردم روزگار برتری دادیم.'),
(4490, 45, 17, 'و دلایل روشنی در امر [دین‌] به آنان عطا کردیم، و جز بعد از آنکه علم برایشان [حاصل‌] آمد، [آن هم‌] از روی رشک و رقابت میان خودشان، دستخوش اختلاف نشدند. قطعاً پروردگارت روز قیامت میانشان در باره آنچه در آن اختلاف می‌کردند، داوری خواهد کرد.'),
(4491, 45, 18, 'سپس تو را در طریقه آیینی [که ناشی‌] از امر [خداست‌] نهادیم. پس آن را پیروی کن، و هوسهای کسانی را که نمی‌دانند پیروی مکن.'),
(4492, 45, 19, 'آنان هرگز در برابر خدا از تو حمایت نمی‌کنند [و به هیچ وجه به کار تو نمی‌آیند ] و ستمگران بعضی‌شان دوستان بعضی [دیگر]ند، و خدا یار پرهیزگاران است.'),
(4493, 45, 20, 'این [کتاب‌] برای مردم، بینش‌بخش و برای قومی که یقین دارند، رهنمود و رحمتی است.'),
(4494, 45, 21, 'آیا کسانی که مرتکب کارهای بد شده‌اند پنداشته‌اند که آنان را مانند کسانی قرار می‌دهیم که ایمان آورده و کارهای شایسته کرده‌اند [به طوری که‌] زندگی آنها و مرگشان یکسان باشد؟ چه بد داوری می‌کنند.'),
(4495, 45, 22, 'و خدا آسمانها و زمین را به حق آفریده است، و تا هر کسی به [موجب‌] آنچه به دست آورده پاداش یابد، و آنان مورد ستم قرار نخواهند گرفت.'),
(4496, 45, 23, 'پس آیا دیدی کسی را که هوس خویش را معبود خود قرار داده و خدا او را دانسته گمراه گردانیده و بر گوش او و دلش مُهر زده و بر دیده‌اش پرده نهاده است؟ آیا پس از خدا چه کسی او را هدایت خواهد کرد؟ آیا پند نمی‌گیرید؟'),
(4497, 45, 24, 'و گفتند: «غیر از زندگانی دنیای ما [چیز دیگری‌] نیست؛ می‌میریم و زنده می‌شویم، و ما را جز طبیعت هلاک نمی‌کند.» و[لی‌] به این [مطلب‌] هیچ دانشی ندارند [و] جز [طریق‌] گمان نمی‌سپرند.'),
(4498, 45, 25, 'و چون آیات روشن ما بر آنان خوانده شود، دلیلشان همواره جز این نیست که می گویند: «اگر راست می‌گویید پدران ما را [حاضر] آورید.»'),
(4499, 45, 26, 'بگو: «خدا[ست که‌] شما را زندگی می‌بخشد، سپس می‌میراند، آنگاه شما را به سوی روز رستاخیز -که تردیدی در آن نیست- گرد می‌آورد، ولی بیشتر مردم [این را] نمی‌دانند.»'),
(4500, 45, 27, 'و فرمانروایی آسمانها و زمین از آن خداست، و روزی که رستاخیز بر پا شود، آن روز است که باطل‌اندیشان زیان خواهند دید.'),
(4501, 45, 28, 'و هر امتی را به زانو در آمده می‌بینی؛ هر امتی به سوی کارنامه خود فراخوانده می‌شود [و بدیشان می‌گویند:] «آنچه را می‌کردید امروز پاداش می‌یابید.'),
(4502, 45, 29, 'این است کتاب ما که علیه شما به حق سخن می‌گوید. ما از آنچه می‌کردید، نسخه بر می‌داشتیم.»'),
(4503, 45, 30, 'و اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند، پس پروردگارشان آنان را در جوار رحمت خویش داخل می‌گرداند. این همان کامیابی آشکار است.'),
(4504, 45, 31, 'و اما کسانی که کافر شدند [بدانها می‌گویند:] «پس مگر آیات من بر شما خوانده نمی‌شد؟ و[لی‌] تکبر نمودید و مردمی بدکار بودید.»'),
(4505, 45, 32, 'و چون گفته شد: «وعده خدا راست است و شکی در رستاخیز نیست»، گفتید: «ما نمی‌دانیم رستاخیز چیست. جز گمان نمی‌ورزیم و ما یقین نداریم.»'),
(4506, 45, 33, 'و [حقیقت‌] بدیهایی که کرده‌اند، بر آنان پدیدار می‌شود و آنچه را که بدان ریشخند می‌کردند، آنان را فرو می‌گیرد.'),
(4507, 45, 34, 'و گفته شود: «همان‌گونه که دیدار امروزتان را فراموش کردید، امروز شما را فراموش خواهیم کرد، و جایگاهتان در آتش است و برای شما یاورانی نخواهد بود.»'),
(4508, 45, 35, 'این بدان سبب است که شما آیات خدا را به ریشخند گرفتید و زندگی دنیا فریبتان داد. پس امروز نه از این [آتش‌] بیرون آورده می‌شوند، و نه عذرشان پذیرفته می‌گردد.'),
(4509, 45, 36, 'پس سپاس از آن خداست: پروردگار آسمانها و پروردگار زمین، پروردگار جهانیان.'),
(4510, 45, 37, 'و در آسمانها و زمین، بزرگی از آن اوست، و اوست شکست‌ناپذیر سنجیده‌کار.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حاء، میم.'),
(4512, 46, 2, 'فرو فرستادن این کتاب، از جانب خدای ارجمند حکیم است.'),
(4513, 46, 3, '[ما] آسمانها و زمین و آنچه را که میان آن دو است جز به حق و [تا] زمانی معیّن نیافریدیم، و کسانی که کافر شده‌اند، از آنچه هشدار داده شده‌اند رویگردانند.'),
(4514, 46, 4, 'بگو: «به من خبر دهید، آنچه را به جای خدا فرا می‌خوانید به من نشان دهید که چه چیزی از زمین [را] آفریده یا [مگر] آنان را در [کار] آسمانها مشارکتی است؟ اگر راست می‌گویید، کتابی پیش از این [قرآن ]یا بازمانده‌ای از دانش نزد من آورید.»'),
(4515, 46, 5, 'و کیست گمراه‌تر از آن کس که به جای خدا کسی را می‌خواند که تا روز قیامت او را پاسخ نمی‌دهد، و آنها از دعایشان بی‌خبرند؟'),
(4516, 46, 6, 'و چون مردم محشور گردند، دشمنان آنان باشند و به عبادتشان انکار ورزند.'),
(4517, 46, 7, 'و چون آیات روشن ما بر ایشان خوانده شود، آنان که چون حقیقت به سویشان آمد منکر آن شدند، گفتند: «این سحری آشکار است.»'),
(4518, 46, 8, 'یا می‌گویند: «این [کتاب‌] را بربافته است.» بگو: «اگر آن را بربافته باشم؛ در برابر خدا اختیار چیزی برای من ندارید. او آگاه‌تر است به آنچه [با طعنه‌] در آن فرو می‌روید. گواه بودن او میان من و شما بس است، و اوست آمرزنده مهربان.»'),
(4519, 46, 9, 'بگو: «من از [میان‌] پیامبران، نودرآمدی نبودم و نمی‌دانم با من و با شما چه معامله‌ای خواهد شد. جز آنچه را که به من وحی می‌شود، پیروی نمی‌کنم؛ و من جز هشداردهنده‌ای آشکار [بیش‌] نیستم.»'),
(4520, 46, 10, 'بگو: «به من خبر دهید، اگر این [قرآن‌] از نزد خدا باشد و شما بدان کافر شده باشید و شاهدی از فرزندان اسرائیل به مشابهت آن [با تورات‌] گواهی داده و ایمان آورده باشد، و شما تکبّر نموده باشید [آیا باز هم شما ستمکار نیستید؟] البته خدا قوم ستمگر را هدایت نمی‌کند.»'),
(4521, 46, 11, 'و کسانی که کافر شدند، به آنان که گرویده‌اند گفتند: «اگر [این دین‌] خوب بود، بر ما بدان پیشی نمی‌گرفتند.» و چون بدان هدایت نیافته‌اند، به زودی خواهند گفت: «این دروغی کهنه است.»'),
(4522, 46, 12, 'و [حال آنکه‌] پیش از آن، کتاب موسی، راهبر و [مایه‌] رحمتی بود؛ و این [قرآن‌] کتابی است به زبان عربی که تصدیق‌کننده [آن‌] است، تا کسانی را که ستم کرده‌اند هشدار دهد و برای نیکوکاران مژده‌ای باشد.'),
(4523, 46, 13, 'محققاً کسانی که گفتند: «پروردگار ما خداست» سپس ایستادگی کردند، بیمی بر آنان نیست و غمگین نخواهند شد.'),
(4524, 46, 14, 'ایشان اهل بهشتند که به پاداش آنچه انجام می‌دادند جاودانه در آن می‌مانند.'),
(4525, 46, 15, 'و انسان را [نسبت‌] به پدر و مادرش به احسان سفارش کردیم. مادرش با تحمّل رنج به او باردار شد و با تحمّل رنج او را به دنیا آورد. و باربرداشتن و از شیرگرفتن او سی ماه است، تا آنگاه که به رشد کامل خود برسد و به چهل سال برسد، می‌گوید: «پروردگارا، بر دلم بیفکن تا نعمتی را که به من و به پدر و مادرم ارزانی داشته‌ای سپاس گویم و کار شایسته‌ای انجام دهم که آن را خوش داری، و فرزندانم را برایم شایسته گردان؛ در حقیقت، من به درگاه تو توبه آوردم و من از فرمان‌پذیرانم.»'),
(4526, 46, 16, 'اینانند کسانی که بهترین آنچه را انجام داده‌اند از ایشان خواهیم پذیرفت و از بدیهایشان درخواهیم گذشت؛ در [زمره‌] بهشتیانند؛ [همان‌] وعده راستی که بدانان وعده داده می‌شده است.'),
(4527, 46, 17, 'و آن کس که به پدر و مادر خود گوید: «اف بر شما، آیا به من وعده می‌دهید که زنده خواهم شد و حال آنکه پیش از من نسلها سپری [و نابود] شدند.» و آن دو به [درگاه‌] خدا زاری می‌کنند: «وای بر تو، ایمان بیاور. وعده [و تهدید] خدا حق است.» و[لی پسر] پاسخ می‌دهد: «اینها جز افسانه‌های گذشتگان نیست.»'),
(4528, 46, 18, 'آنان کسانی‌اند که گفتار [خدا] علیه ایشان -همراه با امتهایی از جنیان و آدمیان که پیش از آنان روزگار به سر بردند- به حقیقت پیوست، بی‌گمان آنان زیانکار بودند.'),
(4529, 46, 19, 'و برای هر یک در [نتیجه‌] آنچه انجام داده‌اند درجاتی است، و تا [خدا پاداش‌] اعمالشان را تمام بدهد؛ و آنان مورد ستم قرار نخواهند گرفت.'),
(4530, 46, 20, 'و آن روز که آنهایی را که کفر ورزیده‌اند، بر آتش عرضه می‌دارند [به آنان می گویند:] «نعمتهای پاکیزه خود را در زندگی دنیایتان [خودخواهانه‌] صَرف کردید و از آنها برخوردار شدید؛ پس امروز به [سزای ]آنکه در زمین بناحق سرکشی می‌نمودید و به سبب آنکه نافرمانی می‌کردید، به عذاب خفت‌[آور] کیفر می‌یابید.»'),
(4531, 46, 21, 'و برادر عادیان را به یاد آور، آنگاه که قوم خویش را در ریگستان بیم داد -در حالی که پیش از او و پس از او [نیز] قطعاً هشداردهندگانی گذشته بودند- که: «جز خدا را مپرستید؛ واقعاً من بر شما از عذاب روزی هولناک می‌ترسم.»'),
(4532, 46, 22, 'گفتند: «آیا آمده‌ای که ما را از خدایانمان برگردانی؟ پس اگر راست می‌گویی، آنچه به ما وعده می‌دهی [بر سرمان‌] بیاور.»'),
(4533, 46, 23, 'گفت: «آگاهی فقط نزد خداست، و آنچه را بدان فرستاده شده‌ام به شما می‌رسانم، ولی من شما را گروهی می‌بینم که در جهل اصرار می‌ورزید.»'),
(4534, 46, 24, 'پس چون آن [عذاب‌] را [به صورت‌] ابری روی‌آورنده به سوی وادیهای خود دیدند، گفتند: «این ابری است که بارش‌دهنده ماست.» [هود گفت: «نه،] بلکه همان چیزی است که به شتاب خواستارش بودید: بادی است که در آن عذابی پر درد [نهفته‌] است.'),
(4535, 46, 25, 'همه چیز را به دستور پروردگارش بنیان‌کن می‌کند.» پس چنان شدند که جز سراهایشان دیده نمی‌شد. این چنین گروه بدکاران را سزا می‌دهیم.'),
(4536, 46, 26, 'و به راستی در چیزهایی به آنان امکانات داده بودیم که به شما در آنها [چنان‌] امکاناتی نداده‌ایم، و برای آنان گوش و دیده‌ها و دلهایی [نیرومندتر از شما] قرار داده بودیم، و[لی‌] چون به نشانه‌های خدا انکار ورزیدند [نه‌] گوششان و نه دیدگانشان و نه دلهایشان، به هیچ وجه به دردشان نخورد، و آنچه ریشخندش می‌کردند به سرشان آمد.'),
(4537, 46, 27, 'و بی‌گمان، همه شهرهای پیرامون شما را هلاک کرده و آیات خود را گونه‌گون بیان داشته‌ایم، امید که آنان بازگردند.'),
(4538, 46, 28, 'پس چرا آن کسانی را که غیر از خدا، به منزله معبودانی، برای تقرّب [به خدا] اختیار کرده بودند، آنان را یاری نکردند بلکه از دستشان دادند؟ و این بود دروغ آنان و آنچه برمی‌بافتند.'),
(4539, 46, 29, 'و چون تنی چند از جِن را به سوی تو روانه کردیم که قرآن را بشنوند؛ پس چون بر آن حاضر شدند [به یکدیگر] گفتند: «گوش فرا دهید.» و چون به انجام رسید، هشداردهنده به سوی قوم خود بازگشتند.'),
(4540, 46, 30, 'گفتند: «ای قوم ما، ما کتابی را شنیدیم که بعد از موسی نازل شده [و] تصدیق‌کننده [کتابهای‌] پیش از خود است، و به سوی حق و به سوی راهی راست راهبری می‌کند.'),
(4541, 46, 31, 'ای قوم ما، دعوت‌کننده خدا را پاسخ [مثبت‌] دهید و به او ایمان آورید تا [خدا] برخی از گناهانتان را بر شما ببخشاید و از عذابی پر درد پناهتان دهد.'),
(4542, 46, 32, 'و کسی که دعوت‌کننده خدا را اجابت نکند، در زمین درمانده‌کننده [خدا] نیست و در برابر او دوستانی ندارد. آنان در گمراهی آشکاری‌اند.'),
(4543, 46, 33, 'مگر ندانسته‌اند که آن خدایی که آسمانها و زمین را آفریده و در آفریدن آنها درمانده نگردید؛ می‌تواند مردگان را [نیز] زنده کند؟ آری، اوست که بر همه چیز تواناست.'),
(4544, 46, 34, 'و روزی که کافران بر آتش عرضه می‌شوند [از آنان می‌پرسند:] «آیا این راست نیست؟» می‌گویند: «سوگند به پروردگارمان که آری.» می‌فرماید: «پس به [سزای‌] آنکه انکار می‌کردید عذاب را بچشید.»'),
(4545, 46, 35, 'پس همان گونه که پیامبران نستوه، صبر کردند، صبر کن، و برای آنان شتابزدگی به خرج مده. روزی که آنچه را وعده داده می‌شوند بنگرند، گویی که آنان جز ساعتی از روز را [در دنیا] نمانده‌اند؛ [این‌] ابلاغی است. پس آیا جز مردم نافرمان هلاکت خواهند یافت؟');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'کسانی که کفر ورزیدند و [مردم را] از راه خدا باز داشتند، [خدا] اعمال آنان را تباه خواهد کرد.'),
(4547, 47, 2, 'و آنان که ایمان آورده و کارهای شایسته کرده‌اند و به آنچه بر محمد [ص‌] نازل آمده گرویده‌اند -[که‌] آن خود حق [و] از جانب پروردگارشان است- [خدا نیز] بدیهایشان را زدود و حال [و روز]شان را بهبود بخشید.'),
(4548, 47, 3, 'این بدان سبب است که آنان که کفر ورزیدند، از باطل پیروی کردند، و کسانی که ایمان آوردند از همان حق -که از جانب پروردگارشان است- پیروی کردند. این گونه خدا برای [بیداری‌] مردم مثالهایشان را می‌زند.'),
(4549, 47, 4, 'پس چون با کسانی که کفر ورزیده‌اند برخورد کنید، گردنها[یشان‌] را بزنید. تا چون آنان را [در کشتار] از پای درآوردید، پس [اسیران را] استوار در بند کشید؛ سپس یا [بر آنان‌] منّت نهید [و آزادشان کنید] و یا فدیه [و عوض از ایشان بگیرید]، تا در جنگ، اسلحه بر زمین گذاشته شود. این است [دستور خدا]؛ و اگر خدا می‌خواست، از ایشان انتقام می‌کشید، ولی [فرمان پیکار داد] تا برخی از شما را به وسیله برخی [دیگر] بیازماید، و کسانی که در راه خدا کشته شده‌اند، هرگز کارهایشان را ضایع نمی‌کند.'),
(4550, 47, 5, 'به زودی آنان را راه می‌نماید و حالشان را نیکو می‌گرداند.'),
(4551, 47, 6, 'و در بهشتی که برای آنان وصف کرده، آنان را درمی‌آورد.'),
(4552, 47, 7, 'ای کسانی که ایمان آورده‌اید، اگر خدا را یاری کنید یاریتان می‌کند و گامهایتان را استوار می‌دارد.'),
(4553, 47, 8, 'و کسانی که کفر ورزیدند، نگونساری بر آنان باد؛ و [خدا] اعمالشان را برباد داد.'),
(4554, 47, 9, 'این بدان سبب است که آنان آنچه را خدا نازل کرده است خوش نداشتند، و [خدا نیز] کارهایشان را باطل کرد.'),
(4555, 47, 10, 'مگر در زمین نگشته‌اند، تا ببینند فرجام کسانی که پیش از آنها بودند به کجا انجامیده است؟ خدا زیر و زبرشان کرد و کافران را نظایر [همین کیفرها در پیش‌] است.'),
(4556, 47, 11, 'چرا که خدا سرپرست کسانی است که ایمان آورده‌اند، ولی کافران را سرپرست [و یاری‌] نیست.'),
(4557, 47, 12, 'خدا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، در باغهایی که از زیر [درختان‌] آنها نهرها روان است درمی‌آورد، و [حال آنکه‌] کسانی که کافر شده‌اند، [در ظاهر] بهره می‌برند و همان گونه که چارپایان می‌خورند، می‌خورند، و[لی ]جایگاه آنها آتش است.'),
(4558, 47, 13, 'و بسا شهرها که نیرومندتر از آن شهری بود که تو را [از خود] بیرون راند، که ما هلاکشان کردیم و برای آنها یار [و یاوری‌] نبود.'),
(4559, 47, 14, 'آیا کسی که بر حجتی از جانب پروردگار خویش است، چون کسی است که بدی کردارش برای او زیبا جلوه داده شده و هوسهای خود را پیروی کرده‌اند؟'),
(4560, 47, 15, 'مَثَل بهشتی که به پرهیزگاران وعده داده شده [چون باغی است که‌] در آن نهرهایی است از آبی که [رنگ و بو و طعمش‌] برنگشته؛ و جویهایی از شیری که مزه‌اش دگرگون نشود؛ و رودهایی از باده‌ای که برای نوشندگان لذتی است؛ و جویبارهایی از انگبین ناب. و در آنجا از هر گونه میوه برای آنان [فراهم‌] است و [از همه بالاتر] آمرزش پروردگار آنهاست. [آیا چنین کسی در چنین باغی دل‌انگیز] مانند کسی است که جاودانه در آتش است و آبی جوشان به خوردشان داده می‌شود [تا] روده‌هایشان را از هم فرو پاشد؟'),
(4561, 47, 16, 'و از میان [منافقان‌] کسانی‌اند که [در ظاهر] به [سخنان‌] تو گوش می‌دهند، ولی چون از نزد تو بیرون می‌روند، به دانش یافتگان می‌گویند: «هم اکنون چه گفت؟» اینان همانانند که خدا بر دلهایشان مهر نهاده است و از هوسهای خود پیروی کرده‌اند.'),
(4562, 47, 17, 'و[لی‌] آنان که به هدایت گراییدند [خدا] آنان را هر چه بیشتر هدایت بخشید و [توفیق‌] پرهیزگاری‌شان داد.'),
(4563, 47, 18, 'آیا [کافران‌] جز این انتظار می‌برند که رستاخیز به ناگاه بر آنان فرا رسد؟ و علامات آن اینک پدید آمده است. پس اگر [رستاخیز] بر آنان دررسد، دیگر کجا جای اندرزشان است؟'),
(4564, 47, 19, 'پس بدان که هیچ معبودی جز خدا نیست؛ و برای گناه خویش آمرزش جوی؛ و برای مردان و زنان با ایمان [طلب مغفرت کن‌]؛ و خداست که فرجام و م‌آل [هر یک از] شما را می‌داند.'),
(4565, 47, 20, 'و کسانی که ایمان آورده‌اند می‌گویند: «چرا سوره‌ای [در باره جهاد] نازل نمی شود؟» اما چون سوره‌ای صریح نازل شد و در آن نام کارزار آمد، آنان که در دلهایشان مرضی هست، مانند کسی که به حال بیهوشی مرگ افتاده به تو می‌نگرند.'),
(4566, 47, 21, '[ولی‌] فرمان‌پذیری و سخنی شایسته برایشان بهتر است. و چون کار به تصمیم کشد، قطعاً خیر آنان در این است که با خدا راست‌[دل‌] باشند.'),
(4567, 47, 22, 'پس [ای منافقان،] آیا امید بستید که چون [از خدا] برگشتید [یا سرپرست مردم شدید] در [روی‌] زمین فساد کنید و خویشاوندیهای خود را از هم بگسلید؟'),
(4568, 47, 23, 'اینان همان کسانند که خدا آنان را لعنت نموده و [گوش دل‌] ایشان را ناشنوا و چشمهایشان را نابینا کرده است.'),
(4569, 47, 24, 'آیا به آیات قرآن نمی‌اندیشند؟ یا [مگر] بر دلهایشان قفلهایی نهاده شده است؟'),
(4570, 47, 25, 'بی‌گمان، کسانی که پس از آنکه [راه‌] هدایت بر آنان روشن شد [به حقیقت‌] پشت کردند، شیطان آنان را فریفت و به آرزوهای دور و درازشان انداخت.'),
(4571, 47, 26, 'چرا که آنان به کسانی که آنچه را خدا نازل کرده خوش نمی‌داشتند، گفتند: «ما در کار [مخالفت‌] تا حدودی از شما اطاعت خواهیم کرد.» و خدا از همداستانی آنان آگاه است.'),
(4572, 47, 27, 'پس چگونه [تاب می‌آورند] وقتی که فرشتگان [عذاب‌]، جانشان را می‌ستانند و بر چهره و پشت آنان تازیانه می‌نوازند؟'),
(4573, 47, 28, 'زیرا آنان از آنچه خدا را به خشم آورده پیروی کرده‌اند و خرسندیش را خوش نداشتند؛ پس اعمالشان را باطل گردانید.'),
(4574, 47, 29, 'آیا کسانی که در دلهایشان مرضی هست، پنداشتند که خدا هرگز کینه آنان را آشکار نخواهد کرد؟'),
(4575, 47, 30, 'و اگر بخواهیم، قطعاً آنان را به تو می‌نمایانیم، در نتیجه ایشان را به سیمای [حقیقی‌]شان می‌شناسی و از آهنگ سخن به [حال‌] آنان پی خواهی برد؛ و خداست که کارهای شما را می‌داند.'),
(4576, 47, 31, 'و البته شما را می‌آزماییم تا مجاهدان و شکیبایان شما را باز شناسانیم، و گزارشهای [مربوط به‌] شما را رسیدگی کنیم.'),
(4577, 47, 32, 'کسانی که کافر شدند و [مردم را] از راه خدا باز داشتند و پس از آنکه راه هدایت بر آنان آشکار شد، با پیامبر [خدا] در افتادند، هرگز به خدا گزندی نمی‌رسانند؛ و به زودی [خدا] کرده‌هایشان را تباه خواهد کرد.'),
(4578, 47, 33, 'ای کسانی که ایمان آورده‌اید، خدا را اطاعت کنید و از پیامبر [او نیز] اطاعت نمایید، و کرده‌های خود را تباه مکنید.'),
(4579, 47, 34, 'آنان که کفر ورزیدند و مانع راه خدا شدند، سپس در حال کفر مردند، هرگز خدا از آنان درنخواهد گذشت.'),
(4580, 47, 35, 'پس سستی نورزید و [کافران را] به آشتی مخوانید [که‌] شما برترید و خدا با شماست و از [ارزش‌] کارهایتان هرگز نخواهد کاست.'),
(4581, 47, 36, 'زندگی این دنیا لهو و لعبی بیش نیست، و اگر ایمان بیاورید و پروا بدارید [خدا] پاداش شما را می‌دهد و اموالتان را [در عوض‌] نمی‌خواهد.'),
(4582, 47, 37, 'اگر [اموال‌] شما را بخواهد و به اصرار از شما طلب کند بخل می‌ورزید، و کینه‌های شما را برملا می‌کند.'),
(4583, 47, 38, 'شما همان [مردمی‌] هستید که برای انفاق در راه خدا فرا خوانده شده‌اید. پس برخی از شما بخل می‌ورزند، و هر کس بخل ورزد تنها به زیان خود بخل ورزیده، و [گرنه‌] خدا بی‌نیاز است و شما نیازمندید؛ و اگر روی برتابید [خدا] جای شما را به مردمی غیر از شما خواهد داد که مانند شما نخواهند بود.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'ما تو را پیروزی بخشیدیم [چه‌] پیروزی درخشانی!'),
(4585, 48, 2, 'تا خداوند از گناه گذشته و آینده تو درگذرد و نعمت خود را بر تو تمام گرداند و تو را به راهی راست هدایت کند.'),
(4586, 48, 3, 'و تو را به نصرتی ارجمند یاری نماید.'),
(4587, 48, 4, 'اوست آن کس که در دلهای مؤمنان آرامش را فرو فرستاد تا ایمانی بر ایمان خود بیفزایند. و سپاهیان آسمانها و زمین از آن خداست، و خدا همواره دانای سنجیده‌کار است.'),
(4588, 48, 5, 'تا مردان و زنانی را که ایمان آورده‌اند در باغهایی که از زیر [درختان‌] آن جویبارها روان است، درآورد و در آن جاویدان بدارد، و بدیهایشان را از آنان بزداید؛ و این [فرجام نیک‌] در پیشگاه خدا کامیابی بزرگی است.'),
(4589, 48, 6, 'و [تا] مردان و زنان نفاق‌پیشه و مردان و زنان مشرک را که به خدا گمان بد برده‌اند، عذاب کند؛ بد زمانه بر آنان باد. و خدا بر ایشان خشم نموده و لعنتشان کرده و جهنم را برای آنان آماده گردانیده و [چه‌] بد سرانجامی است،'),
(4590, 48, 7, 'و سپاهیان آسمانها و زمین از آن خداست، و خدا همواره شکست‌ناپذیر سنجیده‌کار است.'),
(4591, 48, 8, '[ای پیامبر،] ما تو را [به سمت‌] گواه و بشارتگر و هشداردهنده‌ای فرستادیم.'),
(4592, 48, 9, 'تا به خدا و فرستاده‌اش ایمان آورید و او را یاری کنید و ارجش نهید، و [خدا] را بامدادان و شامگاهان به پاکی بستایید.'),
(4593, 48, 10, 'در حقیقت، کسانی که با تو بیعت می‌کنند، جز این نیست که با خدا بیعت می‌کنند؛ دست خدا بالای دستهای آنان است. پس هر که پیمان‌شکنی کند، تنها به زیان خود پیمان می‌شکند، و هر که بر آنچه با خدا عهد بسته وفادار بماند، به زودی خدا پاداشی بزرگ به او می‌بخشد.'),
(4594, 48, 11, 'برجای‌ماندگان بادیه‌نشین به زودی به تو خواهند گفت: «اموال ما و کسانمان ما را گرفتار کردند، برای ما آمرزش بخواه.» چیزی را که در دلهایشان نیست بر زبان خویش می‌رانند. بگو: «اگر خدا بخواهد به شما زیانی یا سودی برساند چه کسی در برابر او برای شما اختیار چیزی را دارد؟ بلکه [این‌] خداست که به آنچه می‌کنید همواره آگاه است.'),
(4595, 48, 12, '[نه چنان بود،] بلکه پنداشتید که پیامبر و مؤمنان هرگز به خانمان خود بر نخواهند گشت، و این [پندار] در دلهایتان نمودی خوش یافت، و گمان بد کردید، و شما مردمی در خور هلاکت بودید.»'),
(4596, 48, 13, 'و هر کس به خدا و پیامبر او ایمان نیاورده است [بداند که‌] ما برای کافران آتشی سوزان آماده کرده‌ایم.'),
(4597, 48, 14, 'و فرمانروایی آسمانها و زمین از آن خداست: هر که را بخواهد می‌بخشاید و هر که را بخواهد عذاب می‌کند، و خدا همواره آمرزنده مهربان است.'),
(4598, 48, 15, 'چون به [قصد] گرفتن غنایم روانه شدید، به زودی برجای‌ماندگان خواهند گفت: «بگذارید ما [هم‌] به دنبال شما بیاییم.» [این گونه‌] می‌خواهند دستور خدا را دگرگون کنند. بگو: «هرگز از پی ما نخواهید آمد. آری، خدا از پیش در باره شما چنین فرموده.» پس به زودی خواهند گفت: «[نه،] بلکه بر ما رشگ می‌برید.» [نه چنین است‌] بلکه جز اندکی درنمی‌یابند.'),
(4599, 48, 16, 'به برجای‌ماندگان بادیه‌نشین بگو: «به زودی به سوی قومی سخت زورمند دعوت خواهید شد که با آنان بجنگید یا اسلام آورند. پس اگر فرمان برید خدا شما را پاداش نیک می‌بخشد، و اگر -همچنان که پیشتر پشت کردید- [باز هم‌] روی بگردانید، شما را به عذابی پردرد معذب می‌دارد.»'),
(4600, 48, 17, 'بر نابینا گناهی نیست و بر لنگ گناهی نیست و بر بیمار گناهی نیست [که در جهاد شرکت نکنند]؛ و هر کس خدا و پیامبر او را فرمان برد، وی را در باغهایی که از زیر [درختان‌] آن نهرهایی روان است درمی‌آورد، و هر کس روی برتابد، به عذابی دردناک معذبش می‌دارد.'),
(4601, 48, 18, 'به راستی خدا هنگامی که مؤمنان، زیر آن درخت با تو بیعت می‌کردند از آنان خشنود شد، و آنچه در دلهایشان بود بازشناخت و بر آنان آرامش فرو فرستاد و پیروزی نزدیکی به آنها پاداش داد.'),
(4602, 48, 19, 'و [نیز] غنیمتهای فراوانی خواهند گرفت، و خدا همواره نیرومند سنجیده‌کار است.'),
(4603, 48, 20, 'و خدا به شما غنیمتهای فراوان [دیگری‌] وعده داده که به زودی آنها را خواهید گرفت، و این [پیروزی‌] را برای شما پیش انداخت، و دستهای مردم را از شما کوتاه ساخت، و تا برای مؤمنان نشانه‌ای باشد و شما را به راه راست هدایت کند.'),
(4604, 48, 21, 'و [غنیمتهای‌] دیگر[ی نیز هست‌] که شما بر آنها دست نیافته‌اید [و] خدا بر آنها نیک احاطه دارد، و همواره خداوند بر هر چیزی تواناست.'),
(4605, 48, 22, 'و اگر کسانی که کافر شدند، به جنگ با شما برخیزند، قطعاً پشت خواهند کرد، و دیگر یار و یاوری نخواهند یافت.'),
(4606, 48, 23, 'سنّت الهی از پیش همین بوده، و در سنّت الهی هرگز تغییری نخواهی یافت.'),
(4607, 48, 24, 'و اوست همان کسی که در دل مکه -پس از پیروزکردن شما بر آنان- دستهای آنها را از شما و دستهای شما را از ایشان کوتاه گردانید، و خدا به آنچه می‌کنید همواره بیناست.'),
(4608, 48, 25, 'آنها بودند که کفر ورزیدند و شما را از مسجد الحرام بازداشتند و نگذاشتند قربانی [شما] که بازداشته شده بود به محلش برسد، و اگر [در مکه‌] مردان و زنان با ایمانی نبودند که [ممکن بود] بی‌آنکه آنان را بشناسید، ندانسته پایمالشان کنید و تاوانشان بر شما بماند [فرمان حمله به مکه می‌دادیم‌] تا خدا هر که را بخواهد در جوار رحمت خویش درآورد. اگر [کافر و مؤمن‌] از هم متمایز می‌شدند، قطعاً کافران را به عذاب دردناکی معذب می‌داشتیم.'),
(4609, 48, 26, 'آنگاه که کافران در دلهای خود، تعصّب [آن هم‌] تعصّب جاهلیّت ورزیدند، پس خدا آرامش خود را بر فرستاده خویش و بر مؤمنان فرو فرستاد، و آرمان تقوا را ملازم آنان ساخت، و [در واقع‌] آنان به [رعایت ]آن [آرمان‌] سزاوارتر و شایسته [اتصاف به‌] آن بودند، و خدا همواره بر هر چیزی داناست.'),
(4610, 48, 27, 'حقاً خدا رؤیای پیامبر خود را تحقّق بخشید [که دیده بود:] شما بدون شکّ، به خواست خدا در حالی که سر تراشیده و موی [و ناخن‌] کوتاه کرده‌اید، با خاطری آسوده در مسجد الحرام درخواهید آمد. خدا آنچه را که نمی‌دانستید دانست، و غیر از این، پیروزی نزدیکی [برای شما] قرار داد.'),
(4611, 48, 28, 'اوست کسی که پیامبر خود را به [قصد] هدایت، با آیین درست روانه ساخت، تا آن را بر تمام ادیان پیروز گرداند و گواه‌بودن خدا کفایت می‌کند.'),
(4612, 48, 29, 'محمد [ص‌] پیامبر خداست؛ و کسانی که با اویند، بر کافران، سختگیر [و] با همدیگر مهربانند. آنان را در رکوع و سجود می‌بینی. فضل و خشنودی خدا را خواستارند. علامت [مشخصّه‌] آنان بر اثر سجود در چهره‌هایشان است. این صفت ایشان است در تورات، و مثَل آنها در انجیل چون کشته‌ای است که جوانه خود برآورد و آن را مایه دهد تا ستبر شود و بر ساقه‌های خود بایستد و دهقانان را به شگفت آورد، تا از [انبوهی‌] آنان [خدا] کافران را به خشم دراندازد. خدا به کسانی از آنان که ایمان آورده و کارهای شایسته کرده‌اند، آمرزش و پاداش بزرگی وعده داده‌است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ای کسانی که ایمان آورده‌اید، در برابر خدا و پیامبرش [در هیچ کاری‌] پیشی مجویید و از خدا پروا بدارید که خدا شنوای داناست.'),
(4614, 49, 2, 'ای کسانی که ایمان آورده‌اید، صدایتان را بلندتر از صدای پیامبر مکنید، و همچنانکه بعضی از شما با بعضی دیگر بلند سخن می‌گویید با او به صدای بلند سخن مگویید، مبادا بی‌آنکه بدانید کرده‌هایتان تباه شود.'),
(4615, 49, 3, 'کسانی که پیش پیامبر خدا صدایشان را فرو می‌کشند همان کسانند که خدا دلهایشان را برای پرهیزگاری امتحان کرده است؛ آنان را آمرزش و پاداشی بزرگ است.'),
(4616, 49, 4, 'کسانی که تو را از پشت اتاقها[ی مسکونی تو] به فریاد می‌خوانند، بیشترشان نمی‌فهمند.'),
(4617, 49, 5, 'و اگر صبر کنند تا بر آنان درآیی، مسلماً برایشان بهتر است و خدا آمرزنده و مهربان است.'),
(4618, 49, 6, 'ای کسانی که ایمان آورده‌اید، اگر فاسقی برایتان خبری آورد، نیک وارسی کنید، مبادا به نادانی گروهی را آسیب برسانید و [بعد،] از آنچه کرده‌اید پشیمان شوید.'),
(4619, 49, 7, 'و بدانید که پیامبر خدا در میان شماست. اگر در بسیاری از کارها از [رأی و میل‌] شما پیروی کند، قطعاً دچار زحمت می‌شوید، لیکن خدا ایمان را برای شما دوست‌داشتنی گردانید و آن را در دلهای شما بیاراست و کفر و پلیدکاری و سرکشی را در نظرتان ناخوشایند ساخت. آنان [که چنین‌اند] ره‌یافتگانند.'),
(4620, 49, 8, '[و این‌] بخششی از خدا و نعمتی [از اوست‌]، و خدا دانای سنجیده‌کار است.'),
(4621, 49, 9, 'و اگر دو طایفه از مؤمنان با هم بجنگند، میان آن دو را اصلاح دهید، و اگر [باز] یکی از آن دو بر دیگری تعدّی کرد، با آن [طایفه‌ای‌] که تعدّی می‌کند بجنگید تا به فرمان خدا بازگردد. پس اگر باز گشت، میان آنها را دادگرانه سازش دهید و عدالت کنید، که خدا دادگران را دوست می‌دارد.'),
(4622, 49, 10, 'در حقیقت مؤمنان با هم برادرند، پس میان برادرانتان را سازش دهید و از خدا پروا بدارید، امید که مورد رحمت قرار گیرید.'),
(4623, 49, 11, 'ای کسانی که ایمان آورده‌اید، نباید قومی قوم دیگر را ریشخند کند، شاید آنها از اینها بهتر باشند، و نباید زنانی زنان [دیگر] را [ریشخند کنند ]، شاید آنها از اینها بهتر باشند، و از یکدیگر عیب مگیرید، و به همدیگر لقبهای زشت مدهید؛ چه ناپسندیده است نام زشت پس از ایمان. و هر که توبه نکرد آنان خود ستمکارند.'),
(4624, 49, 12, 'ای کسانی که ایمان آورده‌اید، از بسیاری از گمانها بپرهیزید که پاره‌ای از گمانها گناه است، و جاسوسی مکنید، و بعضی از شما غیبت بعضی نکند؛ آیا کسی از شما دوست دارد که گوشت برادر مرده‌اش را بخورد؟ از آن کراهت دارید. [پس‌] از خدا بترسید، که خدا توبه‌پذیر مهربان است.'),
(4625, 49, 13, 'ای مردم، ما شما را از مرد و زنی آفریدیم، و شما را ملّت ملّت و قبیله قبیله گردانیدیم تا با یکدیگر شناسایی متقابل حاصل کنید. در حقیقت ارجمندترین شما نزد خدا پرهیزگارترین شماست. بی‌تردید، خداوند دانای آگاه است.'),
(4626, 49, 14, '[برخی از] بادیه‌نشینان گفتند: «ایمان آوردیم.» بگو: «ایمان نیاورده‌اید، لیکن بگویید: اسلام آوردیم. و هنوز در دلهای شما ایمان داخل نشده است. و اگر خدا و پیامبر او را فرمان برید از [ارزش‌] کرده‌هایتان چیزی کم نمی‌کند. خدا آمرزنده مهربان است.»'),
(4627, 49, 15, 'در حقیقت، مؤمنان کسانی‌اند که به خدا و پیامبر او گرویده و [دیگر] شکّ نیاورده و با مال و جانشان در راه خدا جهاد کرده‌اند؛ اینانند که راستکردارند.'),
(4628, 49, 16, 'بگو: «آیا خدا را از دین‌[داری‌] خود خبر می‌دهید؟ و حال آنکه خدا آنچه را که در زمین است می‌داند، و خدا به همه چیز داناست.'),
(4629, 49, 17, 'از اینکه اسلام آورده‌اند بر تو منّت می‌نهند؛ بگو: «بر من از اسلام‌آوردنتان منّت مگذارید، بلکه [این‌] خداست که با هدایت‌کردن شما به ایمان، بر شما منّت می‌گذارد، اگر راستگو باشید.'),
(4630, 49, 18, 'خداست که نهفته آسمانها و زمین را می‌داند و خدا[ست که‌] به آنچه می‌کنید بیناست.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف، سوگند به قرآن باشکوه،'),
(4632, 50, 2, '[که آنان نگرویدند،] بلکه از اینکه هشداردهنده‌ای از خودشان برایشان آمد، در شگفت شدند و کافران گفتند: «این [محمّد و حکایت معاد] چیزی عجیب است»'),
(4633, 50, 3, '«آیا چون مُردیم و خاک شدیم [زنده می‌شویم‌]؟ این بازگشتی بعید است.»'),
(4634, 50, 4, 'قطعاً دانسته‌ایم که زمین [چه مقدار] از اجسادشان فرو می‌کاهد. و پیش ما کتاب ضبطکننده‌ای است.'),
(4635, 50, 5, '[نه،] بلکه حقیقت را، وقتی برایشان آمد، دروغ خواندند، و آنها در کاری سردرگم [مانده‌]اند.'),
(4636, 50, 6, 'مگر به آسمان بالای سرشان ننگریسته‌اند که چگونه آن را ساخته و زینتش داده‌ایم و برای آن هیچ گونه شکافتگی نیست.'),
(4637, 50, 7, 'و زمین را گستردیم و در آن لنگر[آسا کوه‌]ها فرو افکندیم و در آن از هر گونه جفت دل‌انگیز رویانیدیم.'),
(4638, 50, 8, '[تا] برای هر بنده توبه‌کاری بینش‌افزا و پندآموز باشد.'),
(4639, 50, 9, 'و از آسمان، آبی پر برکت فرود آوردیم، پس بدان [وسیله‌] باغها و دانه‌های دروکردنی رویانیدیم.'),
(4640, 50, 10, 'و درختان تناور خرما که خوشه‌[های‌] روی هم چیده دارند.'),
(4641, 50, 11, '[اینها همه‌] برای روزیِ بندگان [من‌] است، و با آن [آب‌] سرزمین مرده‌ای را زنده گردانیدیم؛ رستاخیز [نیز] چنین است.'),
(4642, 50, 12, 'پیش از ایشان قوم نوح و اصحاب رَسّ و ثمود،'),
(4643, 50, 13, 'و عاد و فرعون و برادران لوط،'),
(4644, 50, 14, 'و بیشه‌نشینان و قوم تُبّع به تکذیب پرداختند؛ همگی فرستادگان [ما] را به دروغ گرفتند و [در نتیجه‌] تهدید [من‌] واجب آمد.'),
(4645, 50, 15, 'مگر از آفرینش نخستین [خود] به تنگ آمدیم؟ [نه!] بلکه آنها از خلق جدید در شبهه‌اند.'),
(4646, 50, 16, 'و ما انسان را آفریده‌ایم و می‌دانیم که نفس او چه وسوسه‌ای به او می‌کند، و ما از شاهرگ [او] به او نزدیکتریم.'),
(4647, 50, 17, 'آنگاه که دو [فرشته‌] دریافت‌کننده از راست و از چپ، مراقب نشسته‌اند.'),
(4648, 50, 18, '[آدمی‌] هیچ سخنی را به لفظ درنمی‌آورد مگر اینکه مراقبی آماده نزد او [آن را ضبط می‌کند].'),
(4649, 50, 19, 'و سکرات مرگ، به راستی در رسید؛ این همان است که از آن می‌گریختی؛'),
(4650, 50, 20, 'و در صور دمیده شود؛ این است روز تهدید [من‌].'),
(4651, 50, 21, 'و هر کسی می‌آید [در حالی که‌] با او سوق‌دهنده و گواهی‌دهنده‌ای است.'),
(4652, 50, 22, '[به او می‌گویند:] «واقعاً که از این [حال‌] سخت در غفلت بودی. و[لی‌] ما پرده‌ات را [از جلوی چشمانت‌] برداشتیم و دیده‌ات امروز تیز است.»'),
(4653, 50, 23, 'و [فرشته‌] همنشین او می‌گوید: «این است آنچه پیش من آماده است [و ثبت کرده‌ام‌].»'),
(4654, 50, 24, '[به آن دو فرشته خطاب می‌شود:] «هر کافر سرسختی را در جهنم فروافکنید،'),
(4655, 50, 25, '[هر] بازدارنده از خیری، [هر] متجاوز شکاکی'),
(4656, 50, 26, 'که با خداوند، خدایی دیگر قرار داد. [ای دو فرشته،] او را در عذاب شدید فرو افکنید.'),
(4657, 50, 27, '[شیطان‌] همدمش می‌گوید: «پروردگار ما، من او را به عصیان وانداشتم، لیکن [خودش‌] در گمراهی دور و درازی بود.»'),
(4658, 50, 28, '[خدا] می‌فرماید: «در پیشگاه من با همدیگر مستیزید [که‌] از پیش به شما هشدار داده بودم.'),
(4659, 50, 29, 'پیش من حکم دگرگون نمی‌شود، و من [نسبت‌] به بندگانم بیدادگر نیستم.»'),
(4660, 50, 30, 'آن روز که [ما] به دوزخ می‌گوییم: «آیا پر شدی؟» و می‌گوید: «آیا باز هم هست؟»'),
(4661, 50, 31, 'و بهشت را برای پرهیزگاران نزدیک گردانند، بی‌آنکه دور باشد.'),
(4662, 50, 32, '[و به آنان گویند:] این همان است که وعده یافته‌اید [و] برای هر توبه‌کار نگهبان [حدود خدا] خواهد بود:'),
(4663, 50, 33, 'آنکه در نهان از خدای بخشنده بترسد و با دلی توبه‌کار [باز] آید.'),
(4664, 50, 34, 'به سلامت [و شادکامی‌] در آن درآیید [که‌] این روز جاودانگی است.'),
(4665, 50, 35, 'هر چه بخواهند در آنجا دارند، و پیش ما فزونتر [هم‌] هست.'),
(4666, 50, 36, 'و چه بسا نسلها که پیش از ایشان هلاک کردیم که [بس‌] نیرومندتر از اینان بودند و در شهرها پرسه زده بودند [اما سرانجام‌] مگر گریزگاهی بود؟'),
(4667, 50, 37, 'قطعاً در این [عقوبتها] برای هر صاحبدل و حق نیوشی که خود به گواهی ایستد، عبرتی است.'),
(4668, 50, 38, 'و در حقیقت، آسمانها و زمین و آنچه را که میان آن دو است در شش هنگام آفریدیم و احساس ماندگی نکردیم.'),
(4669, 50, 39, 'و بر آنچه می‌گویند صبر کن، و پیش از برآمدن آفتاب و پیش از غروب، به ستایش پروردگارت تسبیح گوی.'),
(4670, 50, 40, 'و پاره‌ای از شب و به دنبال سجود [به صورت تعقیب و نافله‌] او را تسبیح گوی.'),
(4671, 50, 41, 'و روزی که منادی از جایی نزدیک ندا درمی‌دهد، به گوش باش.'),
(4672, 50, 42, 'روزی که فریاد [رستاخیز] را به حق می‌شنوند، آن [روز] روز بیرون آمدن [از زمین‌] است.'),
(4673, 50, 43, 'ماییم که خود، زندگی می‌بخشیم و به مرگ می‌رسانیم و برگشت به سوی ماست.'),
(4674, 50, 44, 'روزی که زمین به سرعت از [اجساد] آنان جدا و شکافته می‌شود؛ این حشری است که بر ما آسان خواهد بود.'),
(4675, 50, 45, 'ما به آنچه می‌گویند داناتریم، و تو به زور وادارنده آنان نیستی؛ پس به [وسیله‌] قرآن هر که را از تهدید [من‌] می‌ترسد پند ده.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهای ذره‌افشان،'),
(4677, 51, 2, 'و ابرهای گرانبار،'),
(4678, 51, 3, 'و سبک سیران،'),
(4679, 51, 4, 'و تقسیم‌کنندگان کار[ها]،'),
(4680, 51, 5, 'که آنچه وعده داده شده‌اید راست است،'),
(4681, 51, 6, 'و [روز] پاداش واقعیت دارد.'),
(4682, 51, 7, 'سوگند به آسمان مشبّک،'),
(4683, 51, 8, 'که شما [درباره قرآن‌] در سخنی گوناگونید.'),
(4684, 51, 9, '[بگوی‌] تا هر که از آن برگشته، برگشته باشد.'),
(4685, 51, 10, 'مرگ بر دروغپردازان!'),
(4686, 51, 11, 'همانان که در ورطه نادانی بی‌خبرند.'),
(4687, 51, 12, 'پرسند: «روز پاداش کی است؟»'),
(4688, 51, 13, 'همان روز که آنان بر آتش، عقوبت [و آزموده‌] شوند.'),
(4689, 51, 14, 'عذاب [موعود] خود را بچشید، این است همان [بلایی‌] که با شتاب خواستار آن بودید.'),
(4690, 51, 15, 'پرهیزگاران در باغها و چشمه سارانند.'),
(4691, 51, 16, 'آنچه را پروردگارشان عطا فرموده می‌گیرند، زیرا که آنها پیش از این نیکوکار بودند.'),
(4692, 51, 17, 'و از شب اندکی را می‌غنودند.'),
(4693, 51, 18, 'و در سحرگاهان [از خدا] طلب آمرزش می‌کردند.'),
(4694, 51, 19, 'و در اموالشان برای سائل و محروم حقی [معین‌] بود.'),
(4695, 51, 20, 'و روی زمین برای اهل یقین نشانه‌هایی [متقاعدکننده‌] است،'),
(4696, 51, 21, 'و در خود شما؛ پس مگر نمی‌بینید؟'),
(4697, 51, 22, 'و روزی شما و آنچه وعده داده شده‌اید در آسمان است.'),
(4698, 51, 23, 'پس سوگند به پروردگار آسمان و زمین، که واقعاً او حق است همان گونه که خود شما سخن می‌گویید.'),
(4699, 51, 24, 'آیا خبر مهمانان ارجمند ابراهیم به تو رسید؟'),
(4700, 51, 25, 'چون بر او درآمدند؛ پس سلام گفتند. گفت: «سلام، مردمی ناشناسید.»'),
(4701, 51, 26, 'پس آهسته به سوی زنش رفت و گوساله‌ای فربه [و بریان‌] آورد.'),
(4702, 51, 27, 'آن را به نزدیکشان برد [و] گفت: «مگر نمی‌خورید؟»'),
(4703, 51, 28, 'و [در دلش‌] از آنان احساس ترسی کرد. گفتند: «مترس.» و او را به پسری دانا مژده دادند.'),
(4704, 51, 29, 'و زنش با فریادی [از شگفتی‌] سر رسید و بر چهره خود زد و گفت: «زنی پیر نازا [چگونه بزاید]؟»'),
(4705, 51, 30, 'گفتند: «پروردگارت چنین فرموده است. او خود حکیم داناست.»'),
(4706, 51, 31, '[ابراهیم‌] گفت: «ای فرستادگان، مأموریت شما چیست؟»'),
(4707, 51, 32, 'گفتند: «ما به سوی مردمی پلیدکار فرستاده شده‌ایم،'),
(4708, 51, 33, 'تا سنگهایی از گِل رُس بر [سر] آنان فرو فرستیم.'),
(4709, 51, 34, '[که‌] نزد پروردگارت برای مسرفان نشان‌گذاری شده است.'),
(4710, 51, 35, 'پس هر که از مؤمنان در آن [شهرها] بود بیرون بردیم.'),
(4711, 51, 36, 'و[لی‌] در آنجا جز یک خانه از فرمانبران [خدا بیشتر] نیافتیم.'),
(4712, 51, 37, 'و در آنجا برای آنها که از عذاب پر درد می‌ترسند، عبرتی به جای گذاشتیم.'),
(4713, 51, 38, 'و [نیز] در [ماجرای‌] موسی، چون او را با حجتی آشکار به سوی فرعون گسیل داشتیم.'),
(4714, 51, 39, 'پس [فرعون‌] با ارکان [دولت‌] خود روی برتافت و گفت: «[این شخص،] ساحر یا دیوانه‌ای است.»'),
(4715, 51, 40, '[تا] او و سپاهیانش را گرفتیم و آنان را در دریا افکندیم در حالی که او [در آخرین لحظه‌] نکوهشگر [خود] بود.'),
(4716, 51, 41, 'و در [ماجرای‌] عاد [نیز]، چون بر [سر] آنها آن باد مُهلِک را فرستادیم.'),
(4717, 51, 42, 'به هر چه می‌وزید آن را چون خاکستر استخوان مرده می‌گردانید.'),
(4718, 51, 43, 'و در [ماجرای‌] ثمود [نیز عبرتی بود]، آنگاه که به ایشان گفته شد: «تا چندی برخوردار شوید.»'),
(4719, 51, 44, 'تا [آنکه‌] از فرمان پروردگار خود سر برتافتند و در حالی که آنها می‌نگریستند، آذرخش آنان را فرو گرفت.'),
(4720, 51, 45, 'در نتیجه نه توانستند به پای خیزند و نه طلب یاری کنند.'),
(4721, 51, 46, 'و قوم نوح [نیز] پیش از آن [اقوام نامبرده همین گونه هلاک شدند]، زیرا آنها مردمی نافرمان بودند.'),
(4722, 51, 47, 'و آسمان را به قدرت خود برافراشتیم، و بی‌گمان، ما [آسمان‌]گستریم!'),
(4723, 51, 48, 'و زمین را گسترانیده‌ایم و چه نیکو گسترندگانیم.'),
(4724, 51, 49, 'و از هر چیزی دو گونه [یعنی نر و ماده‌] آفریدیم، امید که شما عبرت گیرید.'),
(4725, 51, 50, 'پس به سوی خدا بگریزید، که من شما را از طرف او بیم‌دهنده‌ای آشکارم.'),
(4726, 51, 51, 'و با خدا معبودی دیگر قرار مدهید که من از جانب او هشداردهنده‌ای آشکارم.'),
(4727, 51, 52, 'بدین سان بر کسانی که پیش از آنها بودند هیچ پیامبری نیامد جز اینکه گفتند: «ساحر یا دیوانه‌ای است.»'),
(4728, 51, 53, 'آیا همدیگر را به این [سخن‌] سفارش کرده بودند؟ [نه!] بلکه آنان مردمی سرکش بودند.'),
(4729, 51, 54, 'پس، از آنان روی بگردان، که تو در خور نکوهش نیستی.'),
(4730, 51, 55, 'و پند ده، که مؤمنان را پند سود بخشد.'),
(4731, 51, 56, 'و جن و انس را نیافریدم جز برای آنکه مرا بپرستند.'),
(4732, 51, 57, 'از آنان هیچ روزیی نمی‌خواهم، و نمی‌خواهم که مرا خوراک دهند.'),
(4733, 51, 58, 'خداست که خود روزی بخش نیرومند استوار است.'),
(4734, 51, 59, 'پس برای کسانی که ستم کردند بهره‌ای است از عذاب، همانند بهره عذاب یاران [قبلی‌]شان. پس [بگو:] در خواستن عذاب از من، شتابزدگی نکنند.'),
(4735, 51, 60, 'پس وای بر کسانی که کافر شده‌اند از آن روزی که وعده یافته‌اند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به طور،'),
(4737, 52, 2, 'و کتابی نگاشته شده،'),
(4738, 52, 3, 'در طوماری گسترده؛'),
(4739, 52, 4, 'سوگند به آن خانه آباد [خدا]؛'),
(4740, 52, 5, 'سوگند به بام بلند [آسمان‌]،'),
(4741, 52, 6, 'و آن دریای سرشار [و افروخته‌]،'),
(4742, 52, 7, 'که عذاب پروردگارت واقع‌شدنی است؛'),
(4743, 52, 8, 'آن را هیچ بازدارنده‌ای نیست.'),
(4744, 52, 9, 'روزی که آسمان سخت در تب و تاب افتد،'),
(4745, 52, 10, 'و کوهها [جمله‌] به حرکت درآیند.'),
(4746, 52, 11, 'پس وای بر تکذیب‌کنندگان در آن روز.'),
(4747, 52, 12, 'آنان که به یاوه سرگرمند.'),
(4748, 52, 13, 'روزی که به سوی آتش جهنم کشیده می‌شوند [چه‌] کشیدنی.'),
(4749, 52, 14, '[و به آنان گویند:] «این همان آتشی است که دروغش می‌پنداشتید.'),
(4750, 52, 15, 'آیا این افسون است؟ یا شما [درست‌] نمی‌بینید؟'),
(4751, 52, 16, 'به آن درآیید؛ خواه بشکیبید یا نشکیبید، به حال شما یکسان است. تنها به آنچه می‌کردید مجازات می‌یابید.»'),
(4752, 52, 17, 'پرهیزگاران در باغهایی و [در] ناز و نعمتند.'),
(4753, 52, 18, 'به آنچه پروردگارشان به آنان داده دلشادند، و پروردگارشان آنها را از عذاب دوزخ مصون داشته است.'),
(4754, 52, 19, '[به آنان گویند:] «به [پاداش‌] آنچه به جای می‌آوردید بخورید و بنوشید؛ گواراتان باد!»'),
(4755, 52, 20, 'بر تختهایی ردیف هم تکیه زده‌اند و حوران درشت‌چشم را همسر آنان گردانده‌ایم.'),
(4756, 52, 21, 'و کسانی که گرویده و فرزندانشان آنها را در ایمان پیروی کرده‌اند، فرزندانشان را به آنان ملحق خواهیم کرد و چیزی از کار[ها]شان را نمی‌کاهیم. هر کسی در گرو دستاورد خویش است.'),
(4757, 52, 22, 'با [هر نوع‌] میوه و گوشتی که دلخواه آنهاست آنان را مدد [و تقویت‌] می‌کنیم.'),
(4758, 52, 23, 'در آنجا جامی از دست هم می‌ربایند [و بر سرش همچشمی می‌کنند] که در آن نه یاوه گویی است و نه گناه.'),
(4759, 52, 24, 'و برای [خدمت‌] آنان پسرانی است که بر گردشان همی‌گردند، انگاری آنها مرواریدی‌اند که [در صدف‌] نهفته است.'),
(4760, 52, 25, 'و برخی‌شان رو به برخی کنند [و] از هم پرسند،'),
(4761, 52, 26, 'گویند: «ما پیشتر در میان خانواده خود بیمناک بودیم.'),
(4762, 52, 27, 'پس خدا بر ما منت نهاد و ما را از عذاب گرم [مرگبار] حفظ کرد.'),
(4763, 52, 28, 'ما از دیرباز او را می‌خواندیم، که او همان نیکوکار مهربان است.»'),
(4764, 52, 29, 'پس اندرز ده که تو به لطف پروردگارت نه کاهنی و نه دیوانه.'),
(4765, 52, 30, 'یا می‌گویند: «شاعری است که انتظار مرگش را می‌بریم [و چشم به راه بد زمانه بر اوییم‌].»'),
(4766, 52, 31, 'بگو: «منتظر باشید که من [نیز] با شما از منتظرانم.»'),
(4767, 52, 32, 'آیا پندارهایشان آنان را به این [موضعگیری‌] وا می‌دارد یا [نه،] آنها مردمی سرکشند؟'),
(4768, 52, 33, 'یا می‌گویند: «آن را بربافته.» [نه،] بلکه باور ندارند.'),
(4769, 52, 34, 'پس اگر راست می‌گویند، سخنی مثل آن بیاورند.'),
(4770, 52, 35, 'آیا از هیچ خلق شده‌اند؟ یا آنکه خودشان خالق [خود] هستند؟'),
(4771, 52, 36, 'آیا آسمانها و زمین را [آنان‌] خلق کرده‌اند؟ [نه،] بلکه یقین ندارند.'),
(4772, 52, 37, 'آیا ذخایر پروردگار تو پیش آنهاست؟ یا ایشان تسلط [تام‌] دارند؟'),
(4773, 52, 38, 'آیا نردبانی دارند که بر آن [بر شوند و] بشنوند؟ پس باید شنونده آنان برهانی آشکار بیاورد.'),
(4774, 52, 39, 'آیا خدا را دختران است و شما را پسران؟'),
(4775, 52, 40, 'آیا از آنها مزدی مطالبه می‌کنی و آنان از [تعهّد ادای‌] تاوان گرانبارند؟'),
(4776, 52, 41, 'آیا [علم‌] غیب پیش آنهاست و آنها می‌نویسند؟'),
(4777, 52, 42, 'یا می‌خواهند نیرنگی بزنند؟ و [لی‌] آنان که کافر شده‌اند، خود دچار نیرنگ شده‌اند.'),
(4778, 52, 43, 'آیا ایشان را جز خدا معبودی است؟ منزّه است خدا از آنچه [با او] شریک می‌گردانند.'),
(4779, 52, 44, 'و اگر پاره‌سنگی را در حال سقوط از آسمان ببینند می‌گویند: «ابری متراکم است.»'),
(4780, 52, 45, 'پس بگذارشان تا به آن روزی که در آن بیهوش می‌افتند برسند.'),
(4781, 52, 46, 'روزی که نیرنگشان به هیچ‌وجه به کارشان نیاید و حمایت نیابند.'),
(4782, 52, 47, 'و در حقیقت، غیر از این [مجازات‌]، عذابی [دیگر] برای کسانی که ظلم کرده‌اند خواهد بود، ولی بیشترشان نمی‌دانند [که آن عذاب چیست‌].'),
(4783, 52, 48, 'و در برابر دستور پروردگارت شکیبایی پیشه کن که تو خود در حمایت مایی و هنگامی که [از خواب‌] بر می‌خیزی به نیایش پروردگارت تسبیح گوی.'),
(4784, 52, 49, 'و [نیز] پاره‌ای از شب، و در فروشدن ستارگان تسبیح‌گوی او باش.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به اختر [= قرآن‌] چون فرود می‌آید،'),
(4786, 53, 2, '[که‌] یار شما نه گمراه شده و نه در نادانی مانده؛'),
(4787, 53, 3, 'و از سر هوس سخن نمی‌گوید.'),
(4788, 53, 4, 'این سخن بجز وحیی که وحی می‌شود نیست.'),
(4789, 53, 5, 'آن را [فرشته‌] شدیدالقوی به او فرا آموخت،'),
(4790, 53, 6, '[سروش‌] نیرومندی که [مسلّط] درایستاد.'),
(4791, 53, 7, 'در حالی که او در افق اعلی بود؛'),
(4792, 53, 8, 'سپس نزدیک آمد و نزدیکتر شد،'),
(4793, 53, 9, 'تا [فاصله‌اش‌] به قدر [طول‌] دو [انتهای‌] کمان یا نزدیکتر شد؛'),
(4794, 53, 10, 'آنگاه به بنده‌اش آنچه را باید وحی کند، وحی فرمود.'),
(4795, 53, 11, 'آنچه را دل دید انکار[ش‌] نکرد.'),
(4796, 53, 12, 'آیا در آنچه دیده است با او جدال می‌کنید؟'),
(4797, 53, 13, 'و قطعاً بار دیگری هم او را دیده است،'),
(4798, 53, 14, 'نزدیک سدرالمنتهی،'),
(4799, 53, 15, 'در همان جا که جنةالمأوی است.'),
(4800, 53, 16, 'آنگاه که درخت سدر را آنچه پوشیده بود، پوشیده بود.'),
(4801, 53, 17, 'دیده [اش‌] منحرف نگشت و [از حدّ] در نگذشت.'),
(4802, 53, 18, 'به راستی که [برخی‌] از آیات بزرگ پروردگار خود را بدید.'),
(4803, 53, 19, 'به من خبر دهید از لات و عزّی،'),
(4804, 53, 20, 'و منات آن سومین دیگر،'),
(4805, 53, 21, 'آیا [به خیالتان‌] برای شما پسر است و برای او دختر؟'),
(4806, 53, 22, 'در این صورت، این تقسیم نادرستی است.'),
(4807, 53, 23, '[این بتان‌] جز نامهایی بیش نیستند که شما و پدرانتان نامگذاری کرده‌اید [و] خدا بر [حقّانیّت‌] آنها هیچ دلیلی نفرستاده است. [آنان‌] جز گمان و آنچه را که دلخواهشان است پیروی نمی‌کنند، با آنکه قطعاً از جانب پروردگارشان هدایت برایشان آمده است.'),
(4808, 53, 24, 'مگر انسان آنچه را آرزو کند دارد؟'),
(4809, 53, 25, 'آن سرا و این سرا از آن خداست.'),
(4810, 53, 26, 'و بسا فرشتگانی که در آسمانهایند [و] شفاعتشان به کاری نیاید، مگر پس از آنکه خدا به هر که خواهد و خشنود باشد اذن دهد.'),
(4811, 53, 27, 'در حقیقت، کسانی که آخرت را باور ندارند، فرشتگان را در نامگذاری به صورت مؤنّث نام می‌نهند.'),
(4812, 53, 28, 'و ایشان را به این [کار] معرفتی نیست. جز گمان [خود] را پیروی نمی‌کنند، و در واقع، گمان در [وصول به‌] حقیقت هیچ سودی نمی‌رساند.'),
(4813, 53, 29, 'پس، از هر کس که از یاد ما روی برتافته و جز زندگی دنیا را خواستار نبوده است، روی برتاب.'),
(4814, 53, 30, 'این منتهای دانش آنان است. پروردگار تو، خود به [حال‌] کسی که از راه او منحرف شده داناتر، و او به کسی که راه یافته [نیز] آگاه‌تر است.'),
(4815, 53, 31, 'و هر چه در آسمانها و هر چه در زمین است از آن خداست، تا کسانی را که بد کرده‌اند، به [سزای‌] آنچه انجام داده‌اند کیفر دهد، و آنان را که نیکی کرده‌اند، به نیکی پاداش دهد.'),
(4816, 53, 32, 'آنان که از گناهان بزرگ و زشتکاریها -جز لغزشهای کوچک- خودداری می‌ورزند، پروردگارت [نسبت به آنها] فراخ‌آمرزش است. وی از آن دم که شما را از زمین پدید آورد و از همان‌گاه که در شکمهای مادرانتان [در زهدان‌] نهفته بودید به [حال ]شما داناتر است، پس خودتان را پاک مشمارید. او به [حال‌] کسی که پرهیزگاری نموده داناتر است.'),
(4817, 53, 33, 'پس آیا آن کسی را که [از جهاد] روی برتافت دیدی؟'),
(4818, 53, 34, 'و اندکی بخشید و [از باقی‌] امتناع ورزید.'),
(4819, 53, 35, 'آیا علم غیب پیش اوست و او می‌بیند؟'),
(4820, 53, 36, 'یا بدانچه در صحیفه‌های موسی [آمده‌] خبر نیافته است؟'),
(4821, 53, 37, 'و [نیز در نوشته‌های‌] همان ابراهیمی که وفا کرد:'),
(4822, 53, 38, 'که هیچ بردارنده‌ای بار گناه دیگری را بر نمی‌دارد.'),
(4823, 53, 39, 'و اینکه برای انسان جز حاصل تلاش او نیست.'),
(4824, 53, 40, 'و [نتیجه‌] کوشش او به زودی دیده خواهد شد.'),
(4825, 53, 41, 'سپس هر چه تمامتر وی را پاداش دهند.'),
(4826, 53, 42, 'و اینکه پایان [کار] به سوی پروردگار توست.'),
(4827, 53, 43, 'و هم اوست که می‌خنداند و می‌گریاند.'),
(4828, 53, 44, 'و هم اوست که می‌میراند و زنده می‌گرداند.'),
(4829, 53, 45, 'و هم اوست که دو نوع می‌آفریند: نر و ماده،'),
(4830, 53, 46, 'از نطفه‌ای چون فرو ریخته شود.'),
(4831, 53, 47, 'و هم پدید آوردن [عالم‌] دیگر بر [عهده‌] اوست.'),
(4832, 53, 48, 'و هم اوست که [شما را] بی‌نیاز کرد و سرمایه بخشید.'),
(4833, 53, 49, 'و هم اوست پروردگار ستاره «شِعری».'),
(4834, 53, 50, 'و هم اوست که عادیان قدیم را هلاک کرد.'),
(4835, 53, 51, 'و ثمود را [نیز هلاک کرد] و [کسی را] باقی نگذاشت.'),
(4836, 53, 52, 'و پیشتر [از همه آنها] قوم نوح را، زیرا که آنان ستمگرتر و سرکش‌تر بودند.'),
(4837, 53, 53, 'و شهرها[ی سَدوم و عاموره‌] را فرو افکند.'),
(4838, 53, 54, 'پوشاند بر آن [دو شهر، از باران گوگردی‌] آنچه را پوشاند.'),
(4839, 53, 55, 'پس به کدام یک از نعمتهای پروردگارت تردید روا می‌داری؟'),
(4840, 53, 56, 'این [پیامبر نیز] بیم‌دهنده‌ای از [جمله‌] بیم‌دهندگان نخستین است.'),
(4841, 53, 57, '[وه چه‌] نزدیک گشت قیامت.'),
(4842, 53, 58, 'جز خدا کسی آشکارکننده آن نیست.'),
(4843, 53, 59, 'آیا از این سخن عجب دارید؟'),
(4844, 53, 60, 'و می‌خندید و نمی‌گریید؟'),
(4845, 53, 61, 'و شما در غفلتید.'),
(4846, 53, 62, 'پس خدا را سجده کنید و بپرستید.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'نزدیک شد قیامت و از هم شکافت ماه.'),
(4848, 54, 2, 'و هر گاه نشانه‌ای ببینند روی بگردانند و گویند: «سحری دایم است.»'),
(4849, 54, 3, 'و به تکذیب دست زدند و هوسهای خویش را دنبال کردند، و [لی‌] هر کاری را [آخر] قراری است.'),
(4850, 54, 4, 'و قطعاً از اخبار، آنچه در آن مایه انزجار [از کفر] است به ایشان رسید.'),
(4851, 54, 5, 'حکمت بالغه [حق این بود]، ولی هشدارها سود نکرد.'),
(4852, 54, 6, 'پس، از آنان روی برتاب. روزی که داعی [حق‌] به سوی امری دهشتناک دعوت می‌کند،'),
(4853, 54, 7, 'در حالی که دیدگان خود را فروهشته‌اند، چون ملخهای پراکنده از گورها[ی خود] برمی‌آیند.'),
(4854, 54, 8, 'به سرعت سوی آن دعوتگر می‌شتابند. کافران می‌گویند: «امروز [چه‌] روز دشواری است.»'),
(4855, 54, 9, 'پیش از آنان، قوم نوح [نیز] به تکذیب پرداختند و بنده ما را دروغزن خواندند و گفتند: «دیوانه‌ای است.» و [بسی‌] آزار کشید.'),
(4856, 54, 10, 'تا پروردگارش را خواند که: «من مغلوب شدم؛ به داد من برس!»'),
(4857, 54, 11, 'پس درهای آسمان را به آبی ریزان گشودیم.'),
(4858, 54, 12, 'و از زمین چشمه‌ها جوشانیدیم تا آب [زمین و آسمان‌] برای امری که مقدّر شده بود به هم پیوستند.'),
(4859, 54, 13, 'و او را بر [کشتی‌] تخته‌دار و میخ‌آجین سوار کردیم.'),
(4860, 54, 14, '[کشتی‌] زیر نظر ما روان بود. [این‌] پاداش کسی بود که مورد انکار واقع شده بود.'),
(4861, 54, 15, 'و به راستی آن [سفینه‌] را بر جای نهادیم [تا] عبرتی [باشد]؛ پس آیا پندگیرنده‌ای هست؟'),
(4862, 54, 16, 'پس چگونه بود عذاب من و هشدارها[ی من‌]؟'),
(4863, 54, 17, 'و قطعاً قرآن را برای پندآموزی آسان کرده‌ایم؛ پس آیا پندگیرنده‌ای هست؟'),
(4864, 54, 18, 'عادیان به تکذیب پرداختند. پس چگونه بود عذاب من و هشدارها[ی من‌]؟'),
(4865, 54, 19, 'ما بر [سر] آنان در روز شومی، به طور مداوم، تندبادی توفنده فرستادیم،'),
(4866, 54, 20, '[که‌] مردم را از جا می‌کند؛ گویی تنه‌های نخلی بودند که ریشه‌کن شده بودند.'),
(4867, 54, 21, 'پس چگونه بود عذاب من و هشدارها[ی من‌]؟'),
(4868, 54, 22, 'و قطعاً قرآن را برای پندآموزی آسان کرده‌ایم، پس آیا پندگیرنده‌ای هست؟'),
(4869, 54, 23, 'قوم ثمود هشداردهندگان را تکذیب کردند.'),
(4870, 54, 24, 'و گفتند: «آیا تنها بشری از خودمان را پیروی کنیم؟ در این صورت، ما واقعاً در گمراهی و جنون خواهیم بود.»'),
(4871, 54, 25, '«آیا از میان ما [وحی‌] بر او القا شده است؟ [نه،] بلکه او دروغگویی گستاخ است.»'),
(4872, 54, 26, 'به زودی فردا بدانند دروغگوی گستاخ کیست.'),
(4873, 54, 27, 'ما برای آزمایش آنان [آن‌] ماده‌شتر را فرستادیم و [به صالح گفتیم:]«مراقب آنان باش و شکیبایی کن.'),
(4874, 54, 28, 'و به آنان خبر ده که آب میانشان بخش شده‌است: هر کدام را آب به نوبت خواهد بود.»'),
(4875, 54, 29, 'پس رفیقشان را صدا کردند و [او] شمشیر کشید و [شتر را] پی کرد.'),
(4876, 54, 30, 'پس چگونه بود عذاب من و هشدارها[ی من‌]؟'),
(4877, 54, 31, 'ما بر [سر]شان یک فریاد [مرگبار] فرستادیم و چون گیاه خشکیده [کومه‌ها] ریزریز شدند.'),
(4878, 54, 32, 'و قطعاً قرآن را برای پندآموزی آسان کردیم؛ پس آیا پندگیرنده‌ای هست؟'),
(4879, 54, 33, 'قوم لوط هشداردهندگان را تکذیب کردند.'),
(4880, 54, 34, 'ما بر [سر] آنان سنگبارانی [انفجاری‌] فروفرستادیم [و] فقط خانواده لوط بودند که سحرگاهشان رهانیدیم.'),
(4881, 54, 35, '[و این‌] رحمتی از جانب ما بود؛ هر که سپاس دارد، بدین‌سان [او را] پاداش می‌دهیم.'),
(4882, 54, 36, 'و [لوط] آنها را از عذاب ما سخت بیم داده بود، و[لی‌] در تهدیدها[ی ما] به جدال برخاستند.'),
(4883, 54, 37, 'و از مهمان‌[های‌] او کام دل خواستند، پس فروغ دیدگانشان را ستردیم و [گفتیم:] «[مزه‌] عذاب و هشدارهای مرا بچشید.»'),
(4884, 54, 38, 'و به راستی که سپیده‌دم عذابی پیگیر به سر وقت آنان آمد.'),
(4885, 54, 39, 'پس عذاب و هشدارهای مرا بچشید.'),
(4886, 54, 40, 'و قطعاً قرآن را برای پندآموزی آسان کردیم؛ پس آیا پندگیرنده‌ای هست؟'),
(4887, 54, 41, 'و در حقیقت هشداردهندگان به جانب فرعونیان آمدند.'),
(4888, 54, 42, '[اما آنها] همه معجزات ما را تکذیب کردند، تا چون زبردستی زورمند [گریبان‌] آنان را گرفتیم.'),
(4889, 54, 43, 'آیا کافران شما، از اینان [که برشمردیم‌] برترند، یا شما را در نوشته‌ها[ی آسمانی‌] خط امانی است؟'),
(4890, 54, 44, 'یا می‌گویند: «ما همگی انتقام‌گیرنده [و یار و یاور همدیگر]یم!»'),
(4891, 54, 45, 'زودا که این جمع در هم شکسته شود و پشت کنند.'),
(4892, 54, 46, 'بلکه موعدشان قیامت است و قیامت [بسی‌] سخت‌تر و تلخ‌تر است.'),
(4893, 54, 47, 'قطعاً بزهکاران در گمراهی و جنونند.'),
(4894, 54, 48, 'روزی که در آتش به رو کشیده می‌شوند [و به آنان گفته می‌شود:] «لهیب آتش را بچشید [و احساس کنید].»'),
(4895, 54, 49, 'ماییم که هر چیزی را به اندازه آفریده‌ایم.'),
(4896, 54, 50, 'و فرمان ما جز یک بار نیست [آن هم‌] چون چشم به هم زدنی.'),
(4897, 54, 51, 'و هم مسلکان شما را سخت به هلاکت رساندیم؛ پس آیا پندگیرنده‌ای هست؟'),
(4898, 54, 52, 'و هر چه کرده‌اند در کتابها[ی اعمالشان درج‌] است.'),
(4899, 54, 53, 'و هر خرد و بزرگی [در آن‌] نوشته شده.'),
(4900, 54, 54, 'در حقیقت، مردم پرهیزگار در میان باغها و نهرها،'),
(4901, 54, 55, 'در قرارگاه صدق، نزد پادشاهی توانایند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '[خدای‌] رحمان،'),
(4903, 55, 2, 'قرآن را یاد داد.'),
(4904, 55, 3, 'انسان را آفرید،'),
(4905, 55, 4, 'به او بیان آموخت.'),
(4906, 55, 5, 'خورشید و ماه بر حسابی [روان‌]اند.'),
(4907, 55, 6, 'و بوته و درخت چهره‌سایانند.'),
(4908, 55, 7, 'و آسمان را برافراشت و ترازو را گذاشت،'),
(4909, 55, 8, 'تا مبادا از اندازه درگذرید.'),
(4910, 55, 9, 'و وزن را به انصاف برپا دارید و در سنجش مکاهید.'),
(4911, 55, 10, 'و زمین را برای مردم نهاد.'),
(4912, 55, 11, 'در آن، میوه [ها] و نخلها با خوشه‌های غلاف دار،'),
(4913, 55, 12, 'و دانه‌های پوست‌دار و گیاهان خوشبوست.'),
(4914, 55, 13, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4915, 55, 14, 'انسان را از گل خشکیده‌ای سفال مانند، آفرید.'),
(4916, 55, 15, 'و جن را از تشعشعی از آتش خلق کرد.'),
(4917, 55, 16, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4918, 55, 17, 'پروردگار دو خاور و پروردگار دو باختر.'),
(4919, 55, 18, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4920, 55, 19, 'دو دریا را [به گونه‌ای‌] روان کرد [که‌] با هم برخورد کنند.'),
(4921, 55, 20, 'میان آن دو، حد فاصلی است که به هم تجاوز نمی‌کنند.'),
(4922, 55, 21, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4923, 55, 22, 'از هر دو [دریا] مروارید و مرجان برآید.'),
(4924, 55, 23, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4925, 55, 24, 'و او راست در دریا سفینه‌های بادبان‌دار بلند همچون کوهها.'),
(4926, 55, 25, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4927, 55, 26, 'هر چه بر [زمین‌] است فانی‌شونده است.'),
(4928, 55, 27, 'و ذات باشکوه و ارجمند پروردگارت باقی خواهد ماند.'),
(4929, 55, 28, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4930, 55, 29, 'هر که در آسمانها و زمین است از او درخواست می‌کند. هر زمان، او در کاری است.'),
(4931, 55, 30, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4932, 55, 31, 'ای جن و انس، زودا که به شما بپردازیم.'),
(4933, 55, 32, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4934, 55, 33, 'ای گروه جنّیان و انسیان، اگر می‌توانید از کرانه‌های آسمانها و زمین به بیرون رخنه کنید، پس رخنه کنید. [ولی‌] جز با [به دست آوردن‌] تسلطی رخنه نمی‌کنید.'),
(4935, 55, 34, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4936, 55, 35, 'بر سر شما شراره‌هایی از [نوع‌] تفته آهن و مس فرو فرستاده خواهد شد، و [از کسی‌] یاری نتوانید طلبید.'),
(4937, 55, 36, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4938, 55, 37, 'پس آنگاه که آسمان از هم شکافد و چون چرم گلگون گردد.'),
(4939, 55, 38, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4940, 55, 39, 'در آن روز، هیچ انس و جنی از گناهش پرسیده نشود.'),
(4941, 55, 40, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4942, 55, 41, 'تبهکاران از سیمایشان شناخته می‌شوند و از پیشانی و پایشان بگیرند.'),
(4943, 55, 42, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4944, 55, 43, 'این است همان جهنمی که تبهکاران آن را دروغ می‌خواندند.'),
(4945, 55, 44, 'میان [آتش‌] و میان آب جوشان سرگردان باشند.'),
(4946, 55, 45, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4947, 55, 46, 'و هر کس را که از مقام پروردگارش بترسد دو باغ است.'),
(4948, 55, 47, 'پس کدامیک از نعمتهای پروردگارتان را منکرید؟'),
(4949, 55, 48, 'که دارای شاخسارانند.'),
(4950, 55, 49, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4951, 55, 50, 'در آن دو [باغ‌] دو چشمه روان است.'),
(4952, 55, 51, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4953, 55, 52, 'در آن دو [باغ‌] از هر میوه‌ای دو گونه است.'),
(4954, 55, 53, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4955, 55, 54, 'بر بسترهایی که آستر آنها از ابریشم درشت‌بافت است، تکیه آنند و چیدن میوه [از] آن دو باغ [به آسانی‌] در دسترس است.'),
(4956, 55, 55, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4957, 55, 56, 'در آن [باغها، دلبرانی‌] فروهشته‌نگاهند که دست هیچ انس و جنی پیش از ایشان به آنها نرسیده است.'),
(4958, 55, 57, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4959, 55, 58, 'گویی که آنها یاقوت و مرجانند.'),
(4960, 55, 59, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4961, 55, 60, 'مگر پاداش احسان جز احسان است؟'),
(4962, 55, 61, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4963, 55, 62, 'و غیر از آن دو [باغ‌]، دو باغ [دیگر نیز] هست.'),
(4964, 55, 63, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4965, 55, 64, 'که از [شدّت‌] سبزی سیه‌گون می‌نماید.'),
(4966, 55, 65, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4967, 55, 66, 'در آن دو [باغ‌] دو چشمه همواره جوشان است.'),
(4968, 55, 67, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4969, 55, 68, 'در آن دو، میوه و خرما و انار است.'),
(4970, 55, 69, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4971, 55, 70, 'در آنجا [زنانی‌] نکوخوی و نکورویند.'),
(4972, 55, 71, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4973, 55, 72, 'حورانی پرده‌نشین در [دل‌] خیمه‌ها.'),
(4974, 55, 73, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4975, 55, 74, 'دست هیچ انس و جنی پیش از ایشان به آنها نرسیده است.'),
(4976, 55, 75, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4977, 55, 76, 'بر بالش سبز و فرش نیکو تکیه زده‌اند.'),
(4978, 55, 77, 'پس کدام یک از نعمتهای پروردگارتان را منکرید؟'),
(4979, 55, 78, 'خجسته باد نام پروردگار شکوهمند و بزرگوارت،');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'آن واقعه چون وقوع یابد،'),
(4981, 56, 2, '[که‌] در وقوع آن دروغی نیست:'),
(4982, 56, 3, 'پست‌کننده [و] بالابرنده است.'),
(4983, 56, 4, 'چون زمین با تکان [سختی‌] لرزانده شود،'),
(4984, 56, 5, 'و کوهها [جمله‌] ریزه ریزه شوند،'),
(4985, 56, 6, 'و غباری پراکنده گردند،'),
(4986, 56, 7, 'و شما سه دسته شوید:'),
(4987, 56, 8, 'یاران دست راست، کدامند یاران دست راست؟'),
(4988, 56, 9, 'و یاران چپ؛ کدامند یاران چپ؟'),
(4989, 56, 10, 'و سبقت‌گیرندگان مقدمند؛'),
(4990, 56, 11, 'آنانند همان مقربان [خدا]،'),
(4991, 56, 12, 'در باغستانهای پر نعمت.'),
(4992, 56, 13, 'گروهی از پیشینیان،'),
(4993, 56, 14, 'و اندکی از متأخران.'),
(4994, 56, 15, 'بر تختهایی جواهرنشان،'),
(4995, 56, 16, 'که روبروی هم بر آنها تکیه داده‌اند.'),
(4996, 56, 17, 'بر گردشان پسرانی جاودان [به خدمت‌] می‌گردند،'),
(4997, 56, 18, 'با جامها و آبریزها و پیاله‌[ها]یی از باده ناب روان.'),
(4998, 56, 19, '[که‌] نه از آن دردسر گیرند و نه بی‌خرد گردند.'),
(4999, 56, 20, 'و میوه از هر چه اختیار کنند.'),
(5000, 56, 21, 'و از گوشت پرنده هر چه بخواهند.'),
(5001, 56, 22, 'و حوران چشم‌درشت،'),
(5002, 56, 23, 'مثل لؤلؤ نهان میان صدف،'),
(5003, 56, 24, '[اینها] پاداشی است برای آنچه می‌کردند.'),
(5004, 56, 25, 'در آنجا نه بیهوده‌ای می‌شنوند و نه [سخنی‌] گناه‌آلود.'),
(5005, 56, 26, 'سخنی جز سلام و درود نیست.'),
(5006, 56, 27, 'و یاران راست؛ یاران راست کدامند؟'),
(5007, 56, 28, 'در [زیر] درختان کُنار بی‌خار،'),
(5008, 56, 29, 'و درختهای موز که میوه‌اش خوشه خوشه روی هم چیده است.'),
(5009, 56, 30, 'و سایه‌ای پایدار.'),
(5010, 56, 31, 'و آبی ریزان.'),
(5011, 56, 32, 'و میوه‌ای فراوان،'),
(5012, 56, 33, 'نه بریده و نه ممنوع.'),
(5013, 56, 34, 'و همخوابگانی بالا بلند.'),
(5014, 56, 35, 'ما آنان را پدید آورده‌ایم پدید آوردنی،'),
(5015, 56, 36, 'و ایشان را دوشیزه گردانیده‌ایم،'),
(5016, 56, 37, 'شوی دوست همسال،'),
(5017, 56, 38, 'برای یاران راست.'),
(5018, 56, 39, 'که گروهی از پیشینیانند،'),
(5019, 56, 40, 'و گروهی از متأخران.'),
(5020, 56, 41, 'و یاران چپ؛ کدامند یاران چپ؟'),
(5021, 56, 42, 'در [میان‌] باد گرم و آب داغ.'),
(5022, 56, 43, 'و سایه‌ای از دود تار.'),
(5023, 56, 44, 'نه خنک و نه خوش.'),
(5024, 56, 45, 'اینان بودند که پیش از این ناز پروردگان بودند.'),
(5025, 56, 46, 'و بر گناه بزرگ پافشاری می‌کردند.'),
(5026, 56, 47, 'و می‌گفتند: «آیا چون مردیم و خاک واستخوان شدیم، واقعاً [باز] زنده می‌گردیم؟'),
(5027, 56, 48, 'یا پدران گذشته ما [نیز]؟»'),
(5028, 56, 49, 'بگو: «در حقیقت، اولین و آخرین،'),
(5029, 56, 50, 'قطعاً همه در موعد روزی معلوم گرد آورده شوند.»'),
(5030, 56, 51, 'آنگاه شما ای گمراهان دروغپرداز،'),
(5031, 56, 52, 'قطعاً از درختی که از زقّوم است خواهید خورد.'),
(5032, 56, 53, 'و از آن شکمهایتان را خواهید آکند.'),
(5033, 56, 54, 'و روی آن از آب جوش می‌نوشید؛'),
(5034, 56, 55, '[مانند] نوشیدن اشتران تشنه.'),
(5035, 56, 56, 'این است پذیرایی آنان در روز جزا.'),
(5036, 56, 57, 'ماییم که شما را آفریده‌ایم، پس چرا تصدیق نمی‌کنید؟'),
(5037, 56, 58, 'آیا آنچه را [که به صورت نطفه‌] فرو می‌ریزید دیده‌اید؟'),
(5038, 56, 59, 'آیا شما آن را خلق می‌کنید یا ما آفریننده‌ایم؟'),
(5039, 56, 60, 'ماییم که میان شما مرگ را مقدر کرده‌ایم و بر ما سبقت نتوانید جست؛'),
(5040, 56, 61, '[و می‌توانیم‌] امثال شما را به جای شما قرار دهیم و شما را [به صورت‌] آنچه نمی‌دانید پدیدار گردانیم.'),
(5041, 56, 62, 'و قطعاً پدیدار شدن نخستین خود را شناختید؛ پس چرا سر عبرت گرفتن ندارید؟'),
(5042, 56, 63, 'آیا آنچه را کشت می‌کنید، ملاحظه کرده‌اید؟'),
(5043, 56, 64, 'آیا شما آن را [بی‌یاری ما] زراعت می‌کنید، یا ماییم که زراعت می‌کنیم؟'),
(5044, 56, 65, 'اگر بخواهیم قطعاً خاشاکش می‌گردانیم، پس در افسوس [و تعجب‌] می‌افتید.'),
(5045, 56, 66, '[و می‌گویید:] «واقعاً ما زیان زده‌ایم،'),
(5046, 56, 67, 'بلکه ما محروم شدگانیم.»'),
(5047, 56, 68, 'آیا آبی را که می‌نوشید دیده‌اید؟'),
(5048, 56, 69, 'آیا شما آن را از [دل‌] ابر سپید فرود آورده‌اید، یا ما فرودآورنده‌ایم؟'),
(5049, 56, 70, 'اگر بخواهیم آن را تلخ می‌گردانیم، پس چرا سپاس نمی‌دارید؟'),
(5050, 56, 71, 'آیا آن آتشی را که برمی‌افروزید ملاحظه کرده‌اید؟'),
(5051, 56, 72, 'آیا شما [چوب‌] درخت آن را پدیدار کرده‌اید، یا ما پدیدآورنده‌ایم؟'),
(5052, 56, 73, 'ما آن را [مایه‌] عبرت و [وسیله‌] استفاده برای بیابانگردان قرار داده‌ایم.'),
(5053, 56, 74, 'پس به نام پروردگار بزرگت تسبیح گوی.'),
(5054, 56, 75, 'نه [چنین است که می‌پندارید]، سوگند به جایگاه‌های [ویژه و فواصل معیّن‌] ستارگان.'),
(5055, 56, 76, 'اگر بدانید، آن سوگندی سخت بزرگ است!'),
(5056, 56, 77, 'که این [پیام‌] قطعاً قرآنی است ارجمند،'),
(5057, 56, 78, 'در کتابی نهفته،'),
(5058, 56, 79, 'که جز پاک‌شدگان بر آن دست ندارند،'),
(5059, 56, 80, 'وحیی است از جانب پروردگار جهانیان.'),
(5060, 56, 81, 'آیا شما این سخن را سبک [و سست‌] می‌گیرید؟'),
(5061, 56, 82, 'و تنها نصیب خود را در تکذیب [آن‌] قرار می‌دهید؟'),
(5062, 56, 83, 'پس چرا آنگاه که [جان شما] به گلو می‌رسد،'),
(5063, 56, 84, 'و در آن هنگام خود نظاره گرید -'),
(5064, 56, 85, 'و ما به آن [محتضر] از شما نزدیکتریم ولی نمی‌بینید-'),
(5065, 56, 86, 'پس چرا، اگر شما بی‌جزا می‌مانید [و حساب و کتابی در کار نیست‌]،'),
(5066, 56, 87, 'اگر راست می‌گویید، [روح‌] را برنمی‌گردانید؟'),
(5067, 56, 88, 'و اما اگر [او] از مقربان باشد،'),
(5068, 56, 89, '[در] آسایش و راحت و بهشت پر نعمت [خواهد بود].'),
(5069, 56, 90, 'و اما اگر از یاران راست باشد،'),
(5070, 56, 91, 'از یاران راست بر تو سلام باد.'),
(5071, 56, 92, 'و اما اگر از دروغزنان گمراه است،'),
(5072, 56, 93, 'پس با آبی جوشان پذیرایی خواهد شد،'),
(5073, 56, 94, 'و [فرجامش‌] درافتادن به جهنم است.'),
(5074, 56, 95, 'این است همان حقیقت راست [و] یقین.'),
(5075, 56, 96, 'پس به نام پروردگار بزرگ خود تسبیح گوی.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمانها و زمین است، خدا را به پاکی می‌ستایند، و اوست ارجمند حکیم.'),
(5077, 57, 2, 'فرمانروایی آسمانها و زمین از آنِ اوست: زنده می‌کند و می‌میراند، و او بر هر چیزی تواناست.'),
(5078, 57, 3, 'اوست اول و آخر و ظاهر و باطن، و او به هر چیزی داناست.'),
(5079, 57, 4, 'اوست آن کس که آسمانها و زمین را در شش هنگام آفرید؛ آنگاه بر عرش استیلا یافت. آنچه در زمین درآید و آنچه از آن برآید و آنچه در آن بالارود [همه را] می‌داند. و هر کجا باشید او با شماست، و خدا به هر چه می‌کنید بیناست.'),
(5080, 57, 5, 'فرمانروایی [مطلق‌] آسمانها و زمین از آن اوست، و [جمله‌] کارها به سوی خدا بازگردانیده می‌شود.'),
(5081, 57, 6, 'شب را در روز درمی‌آورد و روز را [نیز] در شب درمی‌آورد، و او به راز دلها داناست.'),
(5082, 57, 7, 'به خدا و پیامبر او ایمان آورید، و از آنچه شما را در [استفاده از] آن، جانشین [دیگران‌] کرده، انفاق کنید. پس کسانی از شما که ایمان آورده و انفاق کرده باشند، پاداش بزرگی خواهند داشت.'),
(5083, 57, 8, 'و شما را چه شده که به خدا ایمان نمی‌آورید و [حال آنکه‌] پیامبر [خدا] شما را دعوت می‌کند تا به پروردگارتان ایمان آورید، و اگر مؤمن باشید، بی‌شک [خدا] از شما پیمان گرفته است.'),
(5084, 57, 9, 'او همان کسی است که بر بنده خود آیات روشنی فرو می‌فرستد، تا شما را از تاریکیها به سوی نور بیرون کشاند. و در حقیقت، خدا [نسبت‌] به شما سخت رئوف و مهربان است.'),
(5085, 57, 10, 'و شما را چه شده که در راه خدا انفاق نمی‌کنید و [حال آنکه‌] میراث آسمانها و زمین به خدا تعلق دارد؟ کسانی از شما که پیش از فتح [مکه‌] انفاق و جهاد کرده‌اند، [با دیگران‌] یکسان نیستند. آنان از [حیث ]درجه بزرگتر از کسانی‌اند که بعداً به انفاق و جهاد پرداخته‌اند. و خداوند به هر کدام وعده نیکو داده است، و خدا به آنچه می‌کنید آگاه است.'),
(5086, 57, 11, 'کیست آن کس که به خدا وامی نیکو دهد تا [نتیجه‌اش را] برای وی دوچندان گرداند و او را پاداشی خوش باشد؟'),
(5087, 57, 12, 'آن روز که مردان و زنان مؤمن را می‌بینی که نورشان پیشاپیششان و به جانب راستشان دوان است. [به آنان گویند:] «امروز شما را مژده باد به باغهایی که از زیر [درختان‌] آن نهرها روان است، در آنها جاودانید. این است همان کامیابی بزرگ.'),
(5088, 57, 13, 'آن روز، مردان و زنان منافق به کسانی که ایمان آورده‌اند می‌گویند: «ما را مهلت دهید تا از نورتان [اندکی‌] برگیریم.» گفته می‌شود: «بازپس برگردید و نوری درخواست کنید.» آنگاه میان آنها دیواری زده می‌شود که آن را دروازه‌ای است: باطنش رحمت است و ظاهرش روی به عذاب دارد.'),
(5089, 57, 14, '[دو رویان،] آنان را ندا درمی‌دهند: «آیا ما با شما نبودیم؟» می‌گویند: «چرا، ولی شما خودتان را در بلا افکندید و امروز و فردا کردید و تردید آوردید و آرزوها شما را غرّه کرد تا فرمان خدا آمد و [شیطان ]مغرورکننده، شما را در باره خدا بفریفت.'),
(5090, 57, 15, 'پس امروز نه از شما و نه از کسانی که کافر شده‌اند عوضی پذیرفته نمی‌شود: جایگاهتان آتش است؛ آن سزاوار شماست و چه بد سرانجامی است.»'),
(5091, 57, 16, 'آیا برای کسانی که ایمان آورده‌اند هنگام آن نرسیده که دلهایشان به یاد خدا و آن حقیقتی که نازل شده نرم [و فروتن‌] گردد و مانند کسانی نباشند که از پیش بدانها کتاب داده شد و [عمر و] انتظار بر آنان به درازا کشید، و دلهایشان سخت گردید و بسیاری از آنها فاسق بودند؟'),
(5092, 57, 17, 'بدانید که خدا زمین را پس از مرگش زنده می‌گرداند. به راستی آیات [خود] را برای شما روشن گردانیده‌ایم، باشد که بیندیشید.'),
(5093, 57, 18, 'در حقیقت، مردان و زنان صدقه‌دهنده و [آنان که‌] به خدا وامی نیکو داده‌اند، ایشان را [پاداش‌] دو چندان گردد، و اجری نیکو خواهند داشت.'),
(5094, 57, 19, 'و کسانی که به خدا و پیامبران وی ایمان آورده‌اند، آنان همان راستینانند و پیش پروردگارشان گواه خواهند بود [و] ایشان راست اجر و نورشان؛ و کسانی که کفر ورزیده و آیات ما را تکذیب کرده‌اند آنان همدمان آتشند.'),
(5095, 57, 20, 'بدانید که زندگی دنیا، در حقیقت، بازی و سرگرمی و آرایش و فخرفروشی شما به یکدیگر و فزون‌جویی در اموال و فرزندان است. [مثَل آنها] چون مثَل بارانی است که کشاورزان را رستنی آن [باران‌] به شگفتی اندازد، سپس [آن کشت‌] خشک شود و آن را زرد بینی، آنگاه خاشاک شود. و در آخرت [دنیا پرستان را] عذابی سخت است و [مؤمنان را] از جانب خدا آمرزش و خشنودی است، و زندگانی دنیا جز کالای فریبنده نیست.'),
(5096, 57, 21, '[برای رسیدن‌] به آمرزشی از پروردگارتان و بهشتی که پهنایش چون پهنای آسمان و زمین است [و] برای کسانی آماده شده که به خدا و پیامبرانش ایمان آورده‌اند، بر یکدیگر سبقت جویید. این فضل خداست که به هر کس بخواهد آن را می‌دهد، و خداوند را فزون‌بخشی بزرگ است.'),
(5097, 57, 22, 'هیچ مصیبتی نه در زمین و نه در نفسهای شما [= به شما] نرسد، مگر آنکه پیش از آنکه آن را پدید آوریم، در کتابی است. این [کار] بر خدا آسان است.'),
(5098, 57, 23, 'تا بر آنچه از دست شما رفته اندوهگین نشوید و به [سبب‌] آنچه به شما داده است شادمانی نکنید، و خدا هیچ خودپسند فخرفروشی را دوست ندارد:'),
(5099, 57, 24, 'همانان که بخل می‌ورزند و مردم را به بخل ورزیدن وامی‌دارند. و هر که روی گرداند قطعاً خدا بی‌نیاز ستوده است.'),
(5100, 57, 25, 'به راستی [ما] پیامبران خود را با دلایل آشکار روانه کردیم و با آنها کتاب و ترازو را فرود آوردیم تا مردم به انصاف برخیزند، و آهن را که در آن برای مردم خطری سخت و سودهایی است، پدید آوردیم، تا خدا معلوم بدارد چه کسی در نهان، او و پیامبرانش را یاری می‌کند. آری، خدا نیرومند شکست ناپذیر است.'),
(5101, 57, 26, 'و در حقیقت، نوح و ابراهیم را فرستادیم و در میان فرزندان آن دو، نبوت و کتاب را قرار دادیم: از آنها [برخی‌] راه‌یاب [شد]ند، و[لی‌] بسیاری از آنان بدکار بودند.'),
(5102, 57, 27, 'آنگاه به دنبال آنان پیامبران خود را، پی‌درپی، آوردیم، و عیسی پسر مریم را در پی [آنان‌] آوردیم و به او انجیل عطا کردیم، و در دلهای کسانی که از او پیروی کردند رأفت و رحمت نهادیم و [اما] ترک دنیایی که از پیش خود درآوردند ما آن را بر ایشان مقرّر نکردیم مگر برای آنکه کسب خشنودی خدا کنند، با این حال آن را چنانکه حقّ رعایت آن بود منظور نداشتند. پس پاداش کسانی از ایشان را که ایمان آورده بودند بدانها دادیم و[لی‌] بسیاری از آنان دستخوش انحرافند.'),
(5103, 57, 28, 'ای کسانی که ایمان آورده‌اید، از خدا پروا دارید و به پیامبر او بگروید تا از رحمت خویش شما را دو بهره عطا کند و برای شما نوری قرار دهد که به [برکت‌] آن راه سپرید و بر شما ببخشاید، و خدا آمرزنده مهربان است.'),
(5104, 57, 29, 'تا اهل کتاب بدانند که به هیچ وجه فزون‌بخشی خدا در [حیطه‌] قدرت آنان نیست و فضل [و عنایت، تنها] در دست خداست: به هر کس بخواهد آن را عطا می‌کند، و خدا دارای کرم بسیار است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'خدا گفتار [زنی‌] را که در باره شوهرش با تو گفتگو و به خدا شکایت می‌کرد شنید؛ و خدا گفتگوی شما را می‌شنود، زیرا خدا شنوای بیناست.'),
(5106, 58, 2, 'از میان شما کسانی که زنانشان را «ظهار» می‌کنند [و می‌گویند: پشت تو چون پشت مادر من است‌] آنان مادرانشان نیستند. مادران آنها تنها کسانی‌اند که ایشان را زاده‌اند، و قطعاً آنها سخنی زشت و باطل می‌گویند، و[لی‌] خدا مسلماً درگذرنده آمرزنده است.'),
(5107, 58, 3, 'و کسانی که زنانشان را ظهار می‌کنند، سپس از آنچه گفته‌اند پشیمان می‌شوند، بر ایشان [فرض‌] است که پیش از آنکه با یکدیگر همخوابگی کنند، بنده‌ای را آزاد گردانند. این [حکمی‌] است که بدان پند داده می‌شوید، و خدا به آنچه انجام می‌دهید آگاه است.'),
(5108, 58, 4, 'و آن کس که [بر آزادکردن بنده‌] دسترسی ندارد، باید پیش از تماس [با زن خود] دو ماه پیاپی روزه بدارد؛ و هر که نتواند، باید شصت بینوا را خوراک بدهد. این [حکم‌] برای آن است که به خدا و فرستاده او ایمان بیاورید، و این است حدود خدا. و کافران را عذابی پردرد خواهد بود.'),
(5109, 58, 5, 'بی‌گمان، کسانی که با خدا و فرستاده او مخالفت می‌کنند ذلیل خواهند شد، همان‌گونه که آنان که پیش از ایشان بودند ذلیل شدند، و به راستی آیات روشن [خود] را فرستاده‌ایم، و کافران را عذابی خفت‌آور خواهد بود.'),
(5110, 58, 6, 'روزی که خداوند همه آنان را بر می‌انگیزد و به آنچه کرده‌اند آگاهشان می‌گرداند. خدا [کارهایشان را] برشمرده است و حال آنکه آنها آن را فراموش کرده‌اند، و خدا بر هر چیزی گواه است.'),
(5111, 58, 7, 'آیا ندانسته‌ای که خدا آنچه را که در آسمانها و آنچه را که در زمین است می‌داند؟ هیچ گفتگوی محرمانه‌ای میان سه تن نیست مگر اینکه او چهارمین آنهاست، و نه میان پنج تن مگر اینکه او ششمین آنهاست، و نه کمتر از این [عدد] و نه بیشتر، مگر اینکه هر کجا باشند او با آنهاست. آنگاه روز قیامت آنان را به آنچه کرده‌اند آگاه خواهد گردانید، زیرا خدا به هر چیزی داناست.'),
(5112, 58, 8, 'آیا کسانی را که از نجوا منع شده بودند، ندیدی که باز بدانچه از آن منع گردیده‌اند، برمی‌گردند و با همدیگر به [منظور] گناه و تعدّی و سرپیچی از پیامبر، محرمانه گفتگو می‌کنند و چون به نزد تو آیند، تو را بدانچه خدا به آن [شیوه‌] سلام نگفته سلام می‌دهند و در دلهای خود می‌گویند: «چرا به آنچه می‌گوییم خدا ما را عذاب نمی‌کند؟» جهنم برای آنان کافی است؛ در آن درمی‌آیند، و چه بد سرانجامی است.'),
(5113, 58, 9, 'ای کسانی که ایمان آورده‌اید، چون با یکدیگر محرمانه گفتگو می‌کنید، به [قصد] گناه و تعدی و نافرمانی پیامبر با همدیگر محرمانه گفتگو نکنید، و به نیکوکاری و پرهیزگاری نجوا کنید، و از خدایی که نزد او محشور خواهید گشت پروا دارید.'),
(5114, 58, 10, 'چنان نجوایی صرفاً از [القاآت‌] شیطان است، تا کسانی را که ایمان آورده‌اند دلتنگ گرداند، و[لی‌] جز به فرمان خدا هیچ آسیبی به آنها نمی‌رساند، و مؤمنان باید بر خدا اعتماد کنند.'),
(5115, 58, 11, 'ای کسانی که ایمان آورده‌اید، چون به شما گفته شود: «در مجالس جای باز کنید»، پس جای باز کنید تا خدا برای شما گشایش حاصل کند، و چون گفته شود: «برخیزید»، پس برخیزید. خدا [رتبه‌] کسانی از شما را که گرویده و کسانی را که دانشمندند [بر حسب‌] درجات بلند گرداند، و خدا به آنچه می‌کنید آگاه است.'),
(5116, 58, 12, 'ای کسانی که ایمان آورده‌اید، هرگاه با پیامبر [خدا] گفتگوی محرمانه می‌کنید، پیش از گفتگوی محرمانه خود صدقه‌ای تقدیم بدارید. این [کار] برای شما بهتر و پاکیزه‌تر است؛ و اگر چیزی نیافتید بدانید که خدا آمرزنده مهربان است.'),
(5117, 58, 13, 'آیا ترسیدید که پیش از گفتگوی محرمانه خود صدقه‌هایی تقدیم دارید؟ و چون نکردید و خدا [هم‌] بر شما بخشود، پس نماز را برپا دارید و زکات را بدهید و از خدا و پیامبر او فرمان برید، و خدا به آنچه می‌کنید آگاه است.'),
(5118, 58, 14, 'آیا ندیده‌ای کسانی را که قومی را که مورد خشم خدایند به دوستی گرفته‌اند؟ آنها نه از شمایند و نه از ایشان، و به دروغ سوگند یاد می‌کنند و خودشان [هم‌] می‌دانند.'),
(5119, 58, 15, 'خدا برای آنان عذابی سخت آماده کرده است. راستی که چه بد می‌کردند.'),
(5120, 58, 16, 'سوگندهای خود را [چون‌] سپری قرار داده بودند و [مردم را] از راه خدا بازداشتند و [در نتیجه‌] برای آنان عذابی خفت‌آور است.'),
(5121, 58, 17, 'در برابر خداوند نه از اموالشان و نه از اولادشان هرگز کاری ساخته نیست. آنها دوزخی‌اند [و] در آن جاودانه [می‌مانند].'),
(5122, 58, 18, 'روزی که خدا همه آنان را برمی‌انگیزد، همان‌گونه که برای شما سوگند یاد می‌کردند برای او [نیز] سوگند یاد می‌کنند و چنان پندارند که حق به جانب آنهاست. آگاه باش که آنان همان دروغگویانند.'),
(5123, 58, 19, 'شیطان بر آنان چیره شده و خدا را از یادشان برده است؛ آنان حزب شیطانند. آگاه باش که حزب شیطان همان زیانکارانند.'),
(5124, 58, 20, 'در حقیقت، کسانی که با خدا و پیامبر او به دشمنی برمی‌خیزند، آنان در [زمره‌] زبونان خواهند بود.'),
(5125, 58, 21, 'خدا مقرّر کرده است که: «حتماً من و فرستادگانم چیره خواهیم گردید.» آری، خدا نیرومند شکست‌ناپذیر است.'),
(5126, 58, 22, 'قومی را نیابی که به خدا و روز بازپسین ایمان داشته باشند [و] کسانی را که با خدا و رسولش مخالفت کرده‌اند -هر چند پدرانشان یا پسرانشان یا برادرانشان یا عشیره آنان باشند- دوست بدارند. در دل اینهاست که [خدا] ایمان را نوشته و آنها را با روحی از جانب خود تأیید کرده است، و آنان را به بهشتهایی که از زیر [درختان‌] آن جویهایی روان است در می‌آورد؛ همیشه در آنجا ماندگارند؛ خدا از ایشان خشنود و آنها از او خشنودند؛ اینانند حزب خدا. آری، حزب خداست که رستگارانند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمانها و در زمین است تسبیح‌گوی خدای هستند، و اوست شکست‌ناپذیر سنجیده‌کار.'),
(5128, 59, 2, 'اوست کسی که، از میان اهل کتاب کسانی را که کفر ورزیدند در نخستین اخراج [از مدینه‌] بیرون کرد. گمان نمی‌کردید که بیرون روند و خودشان گمان داشتند که دژهایشان در برابر خدا مانع آنها خواهد بود، و[لی‌] خدا از آنجایی که تصوّر نمی‌کردند بر آنان درآمد و در دلهایشان بیم افکند، [به طوری که‌] خود به دست خود و دست مؤمنان خانه‌های خود را خراب می‌کردند. پس ای دیده‌وران، عبرت گیرید.'),
(5129, 59, 3, 'و اگر خدا این جلای وطن را بر آنان مقرّر نکرده بود، قطعاً آنها را در دنیا عذاب می‌کرد و در آخرت [هم‌] عذاب آتش داشتند.'),
(5130, 59, 4, 'این [عقوبت‌] برای آن بود که آنها با خدا و پیامبرش در افتادند؛ و هر کس با خدا درافتد؛ [بداند که‌] خدا سخت‌کیفر است.'),
(5131, 59, 5, 'آنچه درخت خرما بریدید یا آنها را [دست نخورده‌] بر ریشه‌هایشان بر جای نهادید، به فرمان خدا بود، تا نافرمانان را خوار گرداند.'),
(5132, 59, 6, 'و آنچه را خدا از آنان به رسم غنیمت عاید پیامبر خود گردانید، [شما برای تصاحب آن‌] اسب یا شتری بر آن نتاختید، ولی خدا فرستادگانش را بر هر که بخواهد چیره می‌گرداند، و خدا بر هر کاری تواناست.'),
(5133, 59, 7, 'آنچه خدا از [دارایی‌] ساکنان آن قریه‌ها عاید پیامبرش گردانید، از آن خدا و از آن پیامبر [او] و متعلق به خویشاوندان نزدیک [وی‌] و یتیمان و بینوایان و درراه‌ماندگان است، تا میان توانگران شما دست به دست نگردد. و آنچه را فرستاده [او] به شما داد، آن را بگیرید و از آنچه شما را باز داشت، بازایستید و از خدا پروا بدارید که خدا سخت‌کیفر است.'),
(5134, 59, 8, '[این غنایم، نخست‌] اختصاص به بینوایان مهاجری دارد که از دیارشان و اموالشان رانده شدند: خواستار فضل خدا و خشنودی [او] می‌باشند و خدا و پیامبرش را یاری می‌کنند. اینان همان مردم درست کردارند.'),
(5135, 59, 9, 'و [نیز] کسانی که قبل از [مهاجران‌] در [مدینه‌] جای گرفته و ایمان آورده‌اند؛ هر کس را که به سوی آنان کوچ کرده دوست دارند؛ و نسبت به آنچه به ایشان داده شده است در دلهایشان حسدی نمی‌یابند؛ و هر چند در خودشان احتیاجی [مبرم ]باشد، آنها را بر خودشان مقدّم می‌دارند. و هر کس از خسّت نفس خود مصون ماند، ایشانند که رستگارانند.'),
(5136, 59, 10, 'و [نیز] کسانی که بعد از آنان [=مهاجران و انصار] آمده‌اند [و] می‌گویند: «پروردگارا، بر ما و بر آن برادرانمان که در ایمان آوردن بر ما پیشی گرفتند ببخشای، و در دلهایمان نسبت به کسانی که ایمان آورده‌اند [هیچ گونه‌] کینه‌ای مگذار! پروردگارا، راستی که تو رئوف و مهربانی.»'),
(5137, 59, 11, 'مگر کسانی را که به نفاق برخاستند ندیدی که به برادران اهل کتاب خود -که از در کفر درآمده بودند- می‌گفتند: «اگر اخراج شدید، حتماً با شما بیرون خواهیم آمد، و بر علیه شما هرگز از کسی فرمان نخواهیم برد؛ و اگر با شما جنگیدند، حتماً شما را یاری خواهیم کرد.» و خدا گواهی می‌دهد که قطعاً آنان دروغگویانند.'),
(5138, 59, 12, 'اگر [یهود] اخراج شوند، آنها با ایشان بیرون نخواهند رفت، و اگر با آنان جنگیده شود [منافقان،] آنها را یاری نخواهند کرد، و اگر یاریشان کنند حتماً [در جنگ‌] پشت خواهند کرد و [دیگر] یاری نیابند.'),
(5139, 59, 13, 'شما قطعاً در دلهای آنان بیش از خدا مایه هراسید، چرا که آنان مردمانی‌اند که نمی‌فهمند.'),
(5140, 59, 14, '[آنان، به صورت‌] دسته جمعی، جز در قریه‌هایی که دارای استحکاماتند، یا از پشت دیوارها، با شما نخواهند جنگید. جنگشان میان خودشان سخت است. آنان را متحد می‌پنداری و[لی‌] دلهایشان پراکنده است، زیرا آنان مردمانی‌اند که نمی‌اندیشند.'),
(5141, 59, 15, 'درست مانند همان کسانی که اخیرا [در واقعه بدر] سزای کار [بد] خود را چشیدند؛ و آنان را عذاب دردناکی خواهد بود.'),
(5142, 59, 16, 'چون حکایت شیطان که به انسان گفت: «کافر شو.» و چون [وی‌] کافر شد، گفت: «من از تو بیزارم، زیرا من از خدا، پروردگار جهانیان، می‌ترسم.»'),
(5143, 59, 17, 'و فرجام هردوشان آن است که هر دو در آتش، جاوید می‌مانند؛ و سزای ستمگران این است.'),
(5144, 59, 18, 'ای کسانی که ایمان آورده‌اید، از خدا پروا دارید؛ و هر کسی باید بنگرد که برای فردا[ی خود] از پیش چه فرستاده است؛ و [باز] از خدا بترسید. در حقیقت، خدا به آنچه می‌کنید آگاه است.'),
(5145, 59, 19, 'و چون کسانی مباشید که خدا را فراموش کردند و او [نیز] آنان را دچار خودفراموشی کرد؛ آنان همان نافرمانانند.'),
(5146, 59, 20, 'دوزخیان با بهشتیان یکسان نیستند؛ بهشتیانند که کامیابانند.'),
(5147, 59, 21, 'اگر این قرآن را بر کوهی فرومی‌فرستادیم، یقیناً آن [کوه‌] را از بیم خدا فروتن [و] از هم‌پاشیده می‌دیدی. و این مَثَلها را برای مردم می‌زنیم، باشد که آنان بیندیشند.'),
(5148, 59, 22, 'اوست خدایی که غیر از او معبودی نیست، داننده غیب و آشکار است، اوست رحمتگر مهربان.'),
(5149, 59, 23, 'اوست خدایی که جز او معبودی نیست، همان فرمانروای پاک سلامت‌[بخش، و] مؤمن [به حقیقت حقه خود که‌] نگهبان، عزیز، جبّار [و] متکبّر [است‌]. پاک است خدا از آنچه [با او] شریک می‌گردانند.'),
(5150, 59, 24, 'اوست خدای خالق نوساز صورتگر [که‌] بهترین نامها [و صفات‌] از آنِ اوست. آنچه در آسمانها و زمین است [جمله‌] تسبیح او می‌گویند و او عزیز حکیم است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ای کسانی که ایمان آورده‌اید، دشمن من و دشمن خودتان را به دوستی برمگیرید [به طوری‌] که با آنها اظهار دوستی کنید، و حال آنکه قطعاً به آن حقیقت که برای شما آمده کافرند [و] پیامبر [خدا] و شما را [از مکه‌] بیرون می‌کنند که [چرا ]به خدا، پروردگارتان ایمان آورده‌اید، اگر برای جهاد در راه من و طلب خشنودی من بیرون آمده‌اید. [شما] پنهانی با آنان رابطه دوستی برقرار می‌کنید در حالی که من به آنچه پنهان داشتید و آنچه آشکار نمودید داناترم. و هر کس از شما چنین کند، قطعاً از راه درست منحرف گردیده است.'),
(5152, 60, 2, 'اگر بر شما دست یابند، دشمن شما باشند و بر شما به بدی دست و زبان بگشایند و آرزو دارند که کافر شوید.'),
(5153, 60, 3, 'روز قیامت نه خویشان شما و نه فرزندانتان هرگز به شما سود نمی‌رسانند. [خدا] میانتان فیصله می‌دهد، و خدا به آنچه انجام می‌دهید بیناست.'),
(5154, 60, 4, 'قطعاً برای شما در [پیروی از] ابراهیم و کسانی که با اویند سرمشقی نیکوست: آنگاه که به قوم خود گفتند: «ما از شما و از آنچه به جای خدا می‌پرستید بیزاریم. به شما کفر می‌ورزیم و میان ما و شما دشمنی و کینه همیشگی پدیدار شده تا وقتی که فقط به خدا ایمان آورید.» جز [در] سخن ابراهیم [که‌] به [نا]پدر[ی‌] خود [گفت:] «حتماً برای تو آمرزش خواهم خواست، با آنکه در برابر خدا اختیار چیزی را برای تو ندارم.» «ای پروردگار ما! بر تو اعتماد کردیم و به سوی تو بازگشتیم و فرجام به سوی توست.'),
(5155, 60, 5, 'پروردگارا، ما را وسیله آزمایش [و آماج آزار] برای کسانی که کفر ورزیده‌اند مگردان، و بر ما ببخشای که تو خود توانای سنجیده‌کاری.»'),
(5156, 60, 6, 'قطعاً برای شما در [پیروی از] آنان سرمشقی نیکوست [یعنی‌] برای کسی که به خدا و روز بازپسین امید می‌بندد. و هر کس روی برتابد [بداند که‌] خدا همان بی‌نیاز ستوده‌[صفات‌] است.'),
(5157, 60, 7, 'امید است که خدا میان شما و میان کسانی از آنان که [ایشان را] دشمن داشتید، دوستی برقرار کند، و خدا تواناست، و خدا آمرزنده مهربان است.'),
(5158, 60, 8, '[اما] خدا شما را از کسانی که در [کار] دین با شما نجنگیده و شما را از دیارتان بیرون نکرده‌اند، باز نمی‌دارد که با آنان نیکی کنید و با ایشان عدالت ورزید، زیرا خدا دادگران را دوست می‌دارد.'),
(5159, 60, 9, 'فقط خدا شما را از دوستی با کسانی باز می‌دارد که در [کار] دین با شما جنگ کرده و شما را از خانه‌هایتان بیرون رانده و در بیرون‌راندنتان با یکدیگر همپشتی کرده‌اند. و هر کس آنان را به دوستی گیرد، آنان همان ستمگرانند.'),
(5160, 60, 10, 'ای کسانی که ایمان آورده‌اید، چون زنان با ایمان مهاجر، نزد شما آیند آنان را بیازمایید. خدا به ایمان آنان داناتر است. پس اگر آنان را باایمان تشخیص دادید، دیگر ایشان را به سوی کافران بازنگردانید: نه آن زنان بر ایشان حلالند و نه آن [مردان‌] بر این زنان حلال. و هر چه خرج [این زنان‌] کرده‌اند به [شوهران‌] آنها بدهید، و بر شما گناهی نیست که -در صورتی که مَهرشان را به آنان بدهید- با ایشان ازدواج کنید، و به پیوندهای قبلی کافران متمسک نشوید [و پایبند نباشید ]و آنچه را شما [برای زنان مرتد و فراری خود که به کفار پناهنده شده‌اند] خرج کرده‌اید، [از کافران‌] مطالبه کنید، و آنها هم باید آنچه را خرج کرده‌اند [از شما] مطالبه کنند. این حکم خداست [که‌] میان شما داوری می‌کند، و خدا دانای حکیم است.'),
(5161, 60, 11, 'و در صورتی که [زنی‌] از همسران شما به سوی کفار رفت [و کفار مَهر مورد مطالبه شما را ندادند] و شما غنیمت یافتید؛ پس به کسانی که همسرانشان رفته‌اند، معادل آنچه خرج کرده‌اند بدهید، و از آن خدایی که به او ایمان دارید بترسید.'),
(5162, 60, 12, 'ای پیامبر، چون زنان باایمان نزد تو آیند که [با این شرط] با تو بیعت کنند که چیزی را با خدا شریک نسازند، و دزدی نکنند، و زنا نکنند، و فرزندان خود را نکشند، و بچه‌های حرامزاده پیش دست و پای خود را با بهتان [و حیله‌] به شوهر نبندند، و در [کار] نیک از تو نافرمانی نکنند، با آنان بیعت کن و از خدا برای آنان آمرزش بخواه، زیرا خداوند آمرزنده مهربان است.'),
(5163, 60, 13, 'ای کسانی که ایمان آورده‌اید، مردمی را که خدا بر آنان خشم رانده، به دوستی مگیرید. آنها واقعاً از آخرت سلب امید کرده‌اند، همان گونه که کافران اهل گور قطع امید نموده‌اند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمانها و آنچه در زمین است به تسبیح خدا می‌پردازند، و اوست ارجمند حکیم.'),
(5165, 61, 2, 'ای کسانی که ایمان آورده‌اید، چرا چیزی می‌گویید که انجام نمی‌دهید؟'),
(5166, 61, 3, 'نزد خدا سخت ناپسند است که چیزی را بگویید و انجام ندهید.'),
(5167, 61, 4, 'در حقیقت، خدا دوست دارد کسانی را که در راه او صف در صف، چنانکه گویی بنایی ریخته شده از سرب‌اند، جهاد می‌کنند.'),
(5168, 61, 5, 'و [یاد کن‌] هنگامی را که موسی به قوم خود گفت: «ای قوم من، چرا آزارم می‌دهید، با اینکه می‌دانید من فرستاده خدا به سوی شما هستم؟» پس چون [از حق‌] برگشتند، خدا دلهایشان را برگردانید، و خدا مردم نافرمان را هدایت نمی‌کند.'),
(5169, 61, 6, 'و هنگامی را که عیسی پسر مریم گفت: «ای فرزندان اسرائیل، من فرستاده خدا به سوی شما هستم. تورات را که پیش از من بوده تصدیق می‌کنم و به فرستاده‌ای که پس از من می‌آید و نام او «احمد» است بشارتگرم.» پس وقتی برای آنان دلایل روشن آورد، گفتند: «این سحری آشکار است.»'),
(5170, 61, 7, 'و چه کسی ستمگرتر از آن کس است که با وجود آنکه به سوی اسلام فراخوانده می شود، بر خدا دروغ می‌بندد؟ و خدا مردم ستمگر را راه نمی‌نماید.'),
(5171, 61, 8, 'می‌خواهند نور خدا را با دهان خود خاموش کنند و حال آنکه خدا -گر چه کافران را ناخوش افتد- نور خود را کامل خواهد گردانید.'),
(5172, 61, 9, 'اوست کسی که فرستاده خود را با هدایت و آیین درست روانه کرد، تا آن را بر هر چه دین است فائق گرداند، هر چند مشرکان را ناخوش آید.'),
(5173, 61, 10, 'ای کسانی که ایمان آورده‌اید، آیا شما را بر تجارتی راه نمایم که شما را از عذابی دردناک می‌رهاند؟'),
(5174, 61, 11, 'به خدا و فرستاده او بگروید و در راه خدا با مال و جانتان جهاد کنید. این [گذشت و فداکاری‌] اگر بدانید، برای شما بهتر است.'),
(5175, 61, 12, 'تا گناهانتان را بر شما ببخشاید، و شما را در باغهایی که از زیر [درختان‌] آن جویبارها روان است و [در] سراهایی خوش، در بهشتهای همیشگی درآورد. این [خود] کامیابی بزرگ است.'),
(5176, 61, 13, 'و [رحمتی‌] دیگر که آن را دوست دارید: یاری و پیروزی نزدیکی از جانب خداست. و مؤمنان را [بدان‌] بشارت ده.'),
(5177, 61, 14, 'ای کسانی که ایمان آورده‌اید، یاران خدا باشید، همان گونه که عیسی‌بن‌مریم به حواریون گفت: «یاران من در راه خدا چه کسانی‌اند؟» حواریون گفتند: «ما یاران خداییم.» پس طایفه‌ای کفر ورزیدند، و کسانی را که گرویده بودند، بر دشمنانشان یاری کردیم تا چیره شدند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمانها و آنچه در زمین است، خدایی را که پادشاه پاک ارجمند فرزانه است، تسبیح می‌گویند.'),
(5179, 62, 2, 'اوست آن کس که در میان بی‌سوادان فرستاده‌ای از خودشان برانگیخت، تا آیات او را بر آنان بخواند و پاکشان گرداند و کتاب و حکمت بدیشان بیاموزد، و [آنان‌] قطعاً پیش از آن در گمراهی آشکاری بودند.'),
(5180, 62, 3, 'و [نیز بر جماعتهایی‌] دیگر از ایشان که هنوز به آنها نپیوسته‌اند. و اوست ارجمند سنجیده‌کار.'),
(5181, 62, 4, 'این فضل خداست، آن را به هر که بخواهد عطا می‌کند و خدا دارای فضل بسیار است.'),
(5182, 62, 5, 'مثل کسانی که [عمل به‌] تورات بر آنان بار شد [و بدان مکلف گردیدند] آنگاه آن را به کار نبستند، همچون مثل خری است که کتابهایی را برپشت می‌کشد. [وه‌] چه زشت است وصف آن قومی که آیات خدا را به دروغ گرفتند. و خدا مردم ستمگر را راه نمی‌نماید.'),
(5183, 62, 6, 'بگو: «ای کسانی که یهودی شده‌اید، اگر پندارید که شما دوستان خدایید نه مردم دیگر، پس اگر راست می‌گویید درخواست مرگ کنید.»'),
(5184, 62, 7, 'و[لی‌] هرگز آن را به سبب آنچه از پیش به دست خویش کرده‌اند، آرزو نخواهند کرد، و خدا به [حال‌] ستمگران داناست.'),
(5185, 62, 8, 'بگو: «آن مرگی که از آن می‌گریزید، قطعاً به سر وقت شما می‌آید؛ آنگاه به سوی دانای نهان و آشکار بازگردانیده خواهید شد، و به آنچه [در روی زمین‌] می‌کردید، آگاهتان خواهد کرد.»'),
(5186, 62, 9, 'ای کسانی که ایمان آورده‌اید، چون برای نماز جمعه ندا درداده شد، به سوی ذکر خدا بشتابید، و داد و ستد را واگذارید. اگر بدانید این برای شما بهتر است.'),
(5187, 62, 10, 'و چون نماز گزارده شد، در [روی‌] زمین پراکنده گردید و فضل خدا را جویا شوید و خدا را بسیار یاد کنید، باشد که شما رستگار گردید.'),
(5188, 62, 11, 'و چون داد و ستد یا سرگرمیی ببینند، به سوی آن روی‌آور می‌شوند، و تو را در حالی که ایستاده‌ای ترک می‌کنند. بگو: «آنچه نزد خداست از سرگرمی و از داد و ستد بهتر است، و خدا بهترین روزی دهندگان است.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'چون منافقان نزد تو آیند گویند: «گواهی می‌دهیم که تو واقعاً پیامبر خدایی.» و خدا [هم‌] می‌داند که تو واقعاً پیامبر او هستی، و خدا گواهی می‌دهد که مردم دوچهره سخت دروغگویند.'),
(5190, 63, 2, 'سوگندهای خود را [چون‌] سپری بر خود گرفته و [مردم را] از راه خدا بازداشته‌اند. راستی که آنان چه بد می‌کنند.'),
(5191, 63, 3, 'این بدان سبب است که آنان ایمان آورده، سپس به انکار پرداخته‌اند و در نتیجه بر دلهایشان مُهر زده شده و [دیگر] نمی‌فهمند.'),
(5192, 63, 4, 'و چون آنان را ببینی، هیکلهایشان تو را به تعجب وا می‌دارد، و چون سخن گویند به گفتارشان گوش فرا می‌دهی گویی آنان شمعک‌هایی پشت بر دیوارند [که پوک شده و درخور اعتماد نیستند]: هر فریادی را به زیان خویش می‌پندارند. خودشان دشمنند؛ از آنان بپرهیز؛ خدا بکشدشان؛ تا کجا [از حقیقت‌] انحراف یافته‌اند.'),
(5193, 63, 5, 'و چون بدیشان گفته شود: «بیایید تا پیامبر خدا برای شما آمرزش بخواهد»، سرهای خود را بر می‌گردانند، و آنان را می‌بینی که تکبرکنان روی برمی‌تابند.'),
(5194, 63, 6, 'برای آنان یکسان است: چه برایشان آمرزش بخواهی یا برایشان آمرزش نخواهی، خدا هرگز بر ایشان نخواهد بخشود. خدا فاسقان را راهنمایی نمی‌کند.'),
(5195, 63, 7, 'آنان کسانی‌اند که می‌گویند: «به کسانی که نزد پیامبر خدایند انفاق مکنید تا پراکنده شوند، و حال آنکه گنجینه‌های آسمانها و زمین از آنِ خداست ولی منافقان درنمی‌یابند.'),
(5196, 63, 8, 'می‌گویند: «اگر به مدینه برگردیم، قطعاً آنکه عزتمندتر است آن زبون‌تر را از آنجا بیرون خواهد کرد.» و[لی‌] عزت از آن خدا و از آن پیامبر او و از آن مؤمنان است؛ لیکن این دورویان نمی‌دانند.'),
(5197, 63, 9, 'ای کسانی که ایمان آورده‌اید، [زنهار] اموال شما و فرزندانتان شما را از یاد خدا غافل نگرداند، و هر کس چنین کند، آنان خود زیانکارانند.'),
(5198, 63, 10, 'و از آنچه روزی شما گردانیده‌ایم، انفاق کنید، پیش از آنکه یکی از شما را مرگ فرا رسد و بگوید: «پروردگارا، چرا تا مدتی بیشتر [اجل‌] مرا به تأخیر نینداختی تا صدقه دهم و از نیکوکاران باشم؟»'),
(5199, 63, 11, 'و[لی‌] هر کس اجلش فرا رسد، هرگز خدا [آن را] به تأخیر نمی‌افکند، و خدا به آنچه می‌کنید آگاه است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'هر چه در آسمانها و هر چه در زمین است خدا را تسبیح می‌گویند. او راست فرمانروایی و او راست سپاس و او بر هر چیزی تواناست.'),
(5201, 64, 2, 'اوست آن کس که شما را آفرید؛ برخی از شما کافرند و برخی مؤمن و خدا به آنچه می‌کنید بیناست.'),
(5202, 64, 3, 'آسمانها و زمین را به حق آفرید و شما را صورتگری کرد و صورتهایتان را نیکوآراست، و فرجام به سوی اوست.'),
(5203, 64, 4, 'آنچه را که در آسمانها و زمین است می‌داند، و آنچه را که پنهان می‌کنید و آنچه را که آشکار می‌دارید [نیز] می‌داند، و خدا به راز دلها داناست.'),
(5204, 64, 5, 'آیا خبر کسانی که پیش از این کفر ورزیدند، و فرجام بد کارشان را چشیدند و عذاب پر دردی خواهند داشت، به شما نرسیده است؟'),
(5205, 64, 6, 'این [بدفرجامی‌] از آن روی بود که پیامبرانشان دلایل آشکار برایشان می‌آوردند و[لی‌] آنان [می‌]گفتند: «آیا بشری ما را هدایت می‌کند؟» پس کافر شدند و روی گردانیدند و خدا بی‌نیازی نمود، و خدا بی‌نیاز ستوده‌است.'),
(5206, 64, 7, 'کسانی که کفر ورزیدند، پنداشتند که هرگز برانگیخته نخواهند شد. بگو: «آری، سوگند به پروردگارم، حتماً برانگیخته خواهید شد، سپس شما را به [حقیقت‌] آنچه کرده‌اید قطعاً واقف خواهند ساخت، و این بر خدا آسان است.»'),
(5207, 64, 8, 'پس به خدا و پیامبر او و آن نوری که ما فرو فرستادیم ایمان آورید، و خدا به آنچه می‌کنید آگاه است.'),
(5208, 64, 9, 'روزی که شما را برای روز گردآوری، گرد می‌آورد، آن [روز]، روز حسرت [خوردن‌] است، و هر کس به خدا ایمان آورده، و کار شایسته‌ای کرده باشد، بدیهایش را از او بسترد، و او را در بهشتهایی که از زیر [درختان‌] آن جویبارها روان است درآورد. در آنجا بمانند. این است همان کامیابی بزرگ.'),
(5209, 64, 10, 'و کسانی که کفر ورزیده، و آیات ما را تکذیب کرده‌اند، آنان اهل آتشند [و ] در آن ماندگار خواهند بود، و چه بد سرانجامی است.'),
(5210, 64, 11, 'هیچ مصیبتی جز به اذن خدا نرسد، و کسی که به خدا بگرود، دلش را به راه آورد، و خدا[ست که‌] به هر چیزی داناست.'),
(5211, 64, 12, 'و خدا را فرمان برید و پیامبر [او] را اطاعت نمایید، و اگر روی بگردانید، بر پیامبر ما فقط پیام‌رسانی آشکار است.'),
(5212, 64, 13, 'خدا[ست که‌] جز او معبودی نیست، و مؤمنان باید تنها بر خدا اعتماد کنند.'),
(5213, 64, 14, 'ای کسانی که ایمان آورده‌اید، در حقیقت برخی از همسران شما و فرزندان شما دشمن شمایند، از آنان بر حذر باشید، و اگر ببخشایید و درگذرید و بیامرزید، به راستی خدا آمرزنده مهربان است.'),
(5214, 64, 15, 'اموال شما و فرزندانتان صرفاً [وسیله‌] آزمایشی [برای شما]یند، و خداست که نزد او پاداشی بزرگ است.'),
(5215, 64, 16, 'پس تا می‌توانید از خدا پروا بدارید و بشنوید و فرمان ببرید، و مالی برای خودتان [در راه خدا] انفاق کنید، و کسانی که از خسّت نفس خویش مصون مانند، آنان رستگارانند.'),
(5216, 64, 17, 'اگر خدا را وامی نیکو دهید، آن را برای شما دو چندان می‌گرداند و بر شما می‌بخشاید، و خدا[ست که‌] سپاس‌پذیر بردبار است.'),
(5217, 64, 18, 'دانای نهان و آشکار [و] ارجمند سنجیده‌کار است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'ای پیامبر، چون زنان را طلاق گویید، در [زمان‌بندی‌] عدّه آنان طلاقشان گویید و حساب آن عدّه را نگه دارید، و از خدا، پروردگارتان بترسید. آنان را از خانه‌هایشان بیرون مکنید، و بیرون نروند مگر آنکه مرتکب کار زشت آشکاری شده باشند. این است احکام الهی. و هر کس از مقررات خدا [پای‌] فراتر نهد، قطعاً به خودش ستم کرده است. نمی‌دانی، شاید خدا پس از این، پیشامدی پدید آورد.'),
(5219, 65, 2, 'پس چون عدّه آنان به سر رسید، [یا] به شایستگی نگاهشان دارید، یا به شایستگی از آنان جدا شوید، و دو تن [مرد] عادل را از میان خود گواه گیرید، و گواهی را برای خدا به پا دارید. این است اندرزی که به آن کس که به خدا و روز بازپسین ایمان دارد، داده می‌شود، و هر کس از خدا پروا کند، [خدا] برای او راه بیرون‌شدنی قرار می‌دهد.'),
(5220, 65, 3, 'و از جایی که حسابش را نمی‌کند، به او روزی می‌رساند، و هر کس بر خدا اعتماد کند او برای وی بس است. خدا فرمانش را به انجام‌رساننده است. به راستی خدا برای هر چیزی اندازه‌ای مقرر کرده است.'),
(5221, 65, 4, 'و آن زنان شما که از خون‌دیدن [ماهانه‌] نومیدند، اگر شک دارید [که خون می‌بینند یا نه؟] عدّه آنان سه ماه است، و [دخترانی‌] که [هنوز] خون ندیده‌اند [نیز عدّه‌شان سه ماه است‌]، و زنان آبستن مدّتشان این است که وضع حمل کنند، و هر کس از خدا پروا دارد [خدا] برای او در کارش تسهیلی فراهم سازد.'),
(5222, 65, 5, 'این است فرمان خدا که آن را به سوی شما فرستاده است؛ و هر کس از خدا پروا کند، بدیهایش را از او بزداید و پاداشش را بزرگ گرداند.'),
(5223, 65, 6, 'همانجا که [خود] سکونت دارید، به قدر استطاعت خویش آنان را جای دهید و به آنها آسیب [و زیان‌] مرسانید تا عرصه را بر آنان تنگ کنید. و اگر باردارند خرجشان را بدهید تا وضع حمل کنند. و اگر برای شما [بچه‌] شیر می‌دهند مزدشان را به ایشان بدهید و به شایستگی میان خود به مشورت پردازید، و اگر کارتان [در این مورد] با هم به دشواری کشید [زن‌] دیگری [بچّه را] شیر دهد.'),
(5224, 65, 7, 'بر توانگر است که از دارایی خود هزینه کند، و هر که روزی او تنگ باشد باید از آنچه خدا به او داده خرج کند. خدا هیچ کس را جز [به قدر] آنچه به او داده است تکلیف نمی‌کند. خدا به زودی پس از دشواری آسانی فراهم می‌کند.'),
(5225, 65, 8, 'و چه بسیار شهرها که از فرمان پروردگار خود و پیامبرانش سر پیچیدند و از آنها حسابی سخت کشیدیم و آنان را به عذابی [بس‌] زشت عذاب کردیم.'),
(5226, 65, 9, 'تا کیفر زشت عمل خود را چشیدند، و پایان کارشان زیان بود.'),
(5227, 65, 10, 'خدا برای آنان عذابی سخت آماده کرده است. پس ای خردمندانی که ایمان آورده‌اید، از خدا بترسید. راستی که خدا سوی شما تذکاری فرو فرستاده است:'),
(5228, 65, 11, 'پیامبری که آیات روشنگر خدا را بر شما تلاوت می‌کند، تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند از تاریکیها به سوی روشنایی بیرون برد، و هر کس به خدا بگرود و کار شایسته کند او را در باغهایی که از زیر [درختان‌] آن جویبارها روان است، درمی‌آورد، جاودانه در آن می‌مانند. قطعاً خدا روزی را برای او خوش کرده است.'),
(5229, 65, 12, 'خدا همان کسی است که هفت آسمان و همانند آنها هفت زمین آفرید. فرمان [خدا] در میان آنها فرود می‌آید، تا بدانید که خدا بر هر چیزی تواناست، و به راستی دانش وی هر چیزی را در بر گرفته است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ای پیامبر، چرا برای خشنودی همسرانت، آنچه را خدا برای تو حلال گردانیده حرام می‌کنی؟ خدا[ست که‌] آمرزنده مهربان است.'),
(5231, 66, 2, 'قطعاً خدا برای شما [راه‌] گشودن سوگندهایتان را مقرر داشته است، و خدا سرپرست شماست، و اوست دانای حکیم.'),
(5232, 66, 3, 'و چون پیامبر با یکی از همسرانش سخنی نهانی گفت، و همین که وی آن را [به زن دیگر] گزارش داد و خدا [پیامبر] را بر آن مطلع گردانید [پیامبر] بخشی از آن را اظهار کرد و از بخشی [دیگر] اعراض نمود. پس چون [مطلب‌] را به آن [زن‌] خبر داد، وی گفت: «چه کسی این را به تو خبر داده؟» گفت: «مرا آن دانای آگاه خبر داده است.»'),
(5233, 66, 4, 'اگر [شما دو زن‌] به درگاه خدا توبه کنید [بهتر است‌]، واقعاً دلهایتان انحراف پیدا کرده است. و اگر علیه او به یکدیگر کمک کنید، در حقیقت، خدا خود سرپرست اوست، و جبرئیل و صالح مؤمنان [نیز یاور اویند] و گذشته از این، فرشتگان [هم ]پشتیبان [او] خواهند بود.'),
(5234, 66, 5, 'اگر پیامبر، شما را طلاق گوید، امید است پروردگارش همسرانی بهتر از شما: مسلمان، مؤمن، فرمانبر، توبه کار، عابد، روزه‌دار، بیوه و دوشیزه به او عوض دهد.'),
(5235, 66, 6, 'ای کسانی که ایمان آورده‌اید، خودتان و کسانتان را از آتشی که سوخت آن، مردم و سنگهاست حفظ کنید: بر آن [آتش‌] فرشتگانی خشن [و] سختگیر [گمارده شده‌] اند. از آنچه خدا به آنان دستور داده سرپیچی نمی‌کنند و آنچه را که مأمورند انجام می‌دهند.'),
(5236, 66, 7, 'ای کسانی که کافر شده‌اید، امروز عذر نیاورید، در واقع به آنچه می‌کردید کیفر می‌یابید.'),
(5237, 66, 8, 'ای کسانی که ایمان آورده‌اید، به درگاه خدا توبه‌ای راستین کنید، امید است که پروردگارتان بدیهایتان را از شما بزداید و شما را به باغهایی که از زیر [درختان‌] آن جویبارها روان است درآورد. در آن روز خدا پیامبر [خود] و کسانی را که با او ایمان آورده بودند خوار نمی‌گرداند: نورشان از پیشاپیش آنان، و سمت راستشان، روان است. می‌گویند: «پروردگارا، نور ما را برای ما کامل گردان و بر ما ببخشای، که تو بر هر چیز توانایی.»'),
(5238, 66, 9, 'ای پیامبر، با کافران و منافقان جهاد کن و بر آنان سخت گیر [که‌] جای ایشان در جهنّم خواهد بود و چه بد سرانجامی است.'),
(5239, 66, 10, 'خدا برای کسانی که کفر ورزیده‌اند، زن نوح و زن لوط را مَثَل آورده [که‌] هر دو در نکاح دو بنده از بندگان شایسته ما بودند و به آنها خیانت کردند، و کاری از دست [شوهران‌] آنها در برابر خدا ساخته نبود، و گفته شد: «با داخل شوندگان داخل آتش شوید.»'),
(5240, 66, 11, 'و برای کسانی که ایمان آورده‌اند، خدا همسر فرعون را مثل آورده، آنگاه که گفت: «پروردگارا، پیش خود در بهشت خانه‌ای برایم بساز، و مرا از فرعون و کردارش نجات ده، و مرا از دست مردم ستمگر برهان.»'),
(5241, 66, 12, 'و مریم دخت عمران را، همان کسی که خود را پاکدامن نگاه داشت و در او از روح خود دمیدیم و سخنان پروردگار خود و کتابهای او را تصدیق کرد و از فرمانبرداران بود.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بزرگوار [و خجسته‌] است آنکه فرمانروایی به دست اوست و او بر هر چیزی تواناست.'),
(5243, 67, 2, 'همانکه مرگ و زندگی را پدید آورد تا شما را بیازماید که کدامتان نیکوکارترید، و اوست ارجمند آمرزنده.'),
(5244, 67, 3, 'همان که هفت آسمان را طبقه طبقه بیافرید. در آفرینش آن [خدای‌] بخشایشگر هیچ گونه اختلاف [و تفاوتی‌] نمی‌بینی. بازبنگر، آیا خلل [و نقصانی‌] می‌بینی؟'),
(5245, 67, 4, 'باز دوباره بنگر تا نگاهت زبون و درمانده به سویت بازگردد.'),
(5246, 67, 5, 'و در حقیقت، آسمان دنیا را با چراغهایی زینت دادیم و آن را مایه طرد شیاطین [= قوای مزاحم‌] گردانیدیم و برای آنها عذاب آتش فروزان آماده کرده‌ایم.'),
(5247, 67, 6, 'و کسانی که به پروردگارشان انکار آوردند، عذاب آتش جهنم خواهند داشت و چه بد سرانجامی است.'),
(5248, 67, 7, 'چون در آنجا افکنده شوند، از آن خروشی می‌شنوند در حالی که می‌جوشد.'),
(5249, 67, 8, 'نزدیک است که از خشم شکافته شود. هر بار که گروهی در آن افکنده شوند، نگاهبانان آن از ایشان پرسند: «مگر شما را هشدار دهنده‌ای نیامد؟»'),
(5250, 67, 9, 'گویند: «چرا، هشدار دهنده‌ای به سوی ما آمد و[لی‌] تکذیب کردیم و گفتیم: خدا چیزی فرو نفرستاده است، شما جز در گمراهی بزرگ نیستید.»'),
(5251, 67, 10, 'و گویند: «اگر شنیده [و پذیرفته‌] بودیم یا تعقل کرده بودیم در [میان‌] دوزخیان نبودیم.»'),
(5252, 67, 11, 'پس به گناه خود اقرار می‌کنند. و مرگ باد بر اهل جهنم'),
(5253, 67, 12, 'کسانی که در نهان از پروردگارشان می‌ترسند، آنان را آمرزش و پاداشی بزرگ خواهد بود.'),
(5254, 67, 13, 'و [اگر] سخن خود را پنهان دارید، یا آشکارش نمایید، در حقیقت وی به راز دلها آگاه است.'),
(5255, 67, 14, 'آیا کسی که آفریده است نمی‌داند؟ با اینکه او خود باریک بین آگاه است.'),
(5256, 67, 15, 'اوست کسی که زمین را برای شما رام گردانید، پس در فراخنای آن رهسپار شوید و از روزی [خدا] بخورید و رستاخیز به سوی اوست.'),
(5257, 67, 16, 'آیا از آن کس که در آسمان است ایمن شده‌اید که شما را در زمین فرو برد، پس بناگاه [زمین‌] به تپیدن افتد؟'),
(5258, 67, 17, 'یا از آن کس که در آسمان است ایمن شده‌اید که بر [سر] شما تندبادی از سنگریزه فرو فرستد؟ پس به زودی خواهید دانست که بیم دادن من چگونه است'),
(5259, 67, 18, 'و پیش از آنان [نیز] کسانی به تکذیب پرداختند پس عذاب من چگونه بود؟'),
(5260, 67, 19, 'آیا در بالای سرشان به پرندگان ننگریسته‌اند [که گاه‌] بال می‌گسترند و [گاه‌] بال می‌آنند؟ جز خدای رحمان [کسی‌] آنها را نگاه نمی‌دارد، او به هر چیزی بیناست.'),
(5261, 67, 20, 'یا آن کسی که خود برای شما [چون‌] سپاهی است که یاریتان می‌کند، جز خدای رحمان کیست؟ کافران جز گرفتار فریب نیستند.'),
(5262, 67, 21, 'یا کیست آن که به شما روزی دهد اگر [خدا] روزی خود را [از شما] باز دارد؟ [نه‌] بلکه در سرکشی و نفرت پافشاری کردند.'),
(5263, 67, 22, 'پس آیا آن کس که نگونسار راه می‌پیماید هدایت یافته تر است یا آن کس که ایستاده بر راه راست می‌رود؟'),
(5264, 67, 23, 'بگو: «اوست آن کس که شما را پدید آورده و برای شما گوش و دیدگان و دلها آفریده است. چه کم سپاسگزارید.»'),
(5265, 67, 24, 'بگو: «اوست که شما را در زمین پراکنده کرده، و به نزد او [ست که‌] گرد آورده خواهید شد.»'),
(5266, 67, 25, 'و می‌گویند: «اگر راست می‌گویید، این وعده کی خواهد بود؟»'),
(5267, 67, 26, 'بگو: «علم [آن‌] فقط پیش خداست و من صرفاً هشدار دهنده‌ای آشکارم.»'),
(5268, 67, 27, 'و آنگاه که آن [لحظه موعود] را نزدیک ببینند، چهره‌های کسانی که کافر شده‌اند در هم رود، و گفته شود: «این است همان چیزی که آن را فرا می‌خواندید»'),
(5269, 67, 28, 'بگو: «به من خبر دهید، اگر خدا مرا و هر که را با من است هلاک کند یا ما را مورد رحمت قرار دهد، چه کسی کافران را از عذابی پر درد پناه خواهد داد؟»'),
(5270, 67, 29, 'بگو: «اوست خدای بخشایشگر، به او ایمان آوردیم، و بر او توکل کردیم. و به زودی خواهید دانست چه کسی است که خود در گمراهی آشکاری است.»'),
(5271, 67, 30, 'بگو: «به من خبر دهید، اگر آب [آشامیدنی‌] شما [به زمین‌] فرو رود، چه کسی آب روان برایتان خواهد آورد؟»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون، سوگند به قلم و آنچه می‌نویسند،'),
(5273, 68, 2, '[که‌] تو، به لطف پروردگارت، دیوانه نیستی.'),
(5274, 68, 3, 'و بی‌گمان، تو را پاداشی بی‌منت خواهد بود.'),
(5275, 68, 4, 'و راستی که تو را خویی والاست'),
(5276, 68, 5, 'به زودی خواهی دید و خواهند دید،'),
(5277, 68, 6, '[که‌] کدام یک از شما دستخوش جنونید.'),
(5278, 68, 7, 'پروردگارت خود بهتر می‌داند چه کسی از راه او منحرف شده، و [هم‌] او به راه یافتگان داناتر است.'),
(5279, 68, 8, 'پس، از دروغزنان فرمان مبر.'),
(5280, 68, 9, 'دوست دارند که نرمی کنی تا نرمی نمایند.'),
(5281, 68, 10, 'و از هر قسم خورنده فرو مایه‌ای فرمان مبر:'),
(5282, 68, 11, '[که‌] عیبجوست و برای خبرچینی گام برمی‌دارد،'),
(5283, 68, 12, 'مانع خیر، متجاوز، گناه پیشه،'),
(5284, 68, 13, 'گستاخ، [و] گذشته از آن زنازاده است،'),
(5285, 68, 14, 'به صرف اینکه مالدار و پسردار است،'),
(5286, 68, 15, 'چون آیات ما بر او خوانده شود، گوید: «افسانه‌های پیشینیان است.»'),
(5287, 68, 16, 'زودا که بر بینی‌اش داغ نهیم [و رسوایش کنیم‌].'),
(5288, 68, 17, 'ما آنان را همان گونه که باغداران را آزمودیم، مورد آزمایش قرار دادیم، آنگاه که سوگند خوردند که صبح برخیزند و [میوه‌] آن [باغ‌] را حتماً بچینند.'),
(5289, 68, 18, 'و[لی‌] «ان شاء الله» نگفتند.'),
(5290, 68, 19, 'پس در حالی که آنان غنوده بودند، بلایی از جانب پروردگارت بر آن [باغ‌] به گردش در آمد.'),
(5291, 68, 20, 'و [باغ،] آفت زده [و زمین بایر] گردید.'),
(5292, 68, 21, 'پس [باغداران‌] بامدادان یکدیگر را صدا زدند،'),
(5293, 68, 22, 'که: «اگر میوه می‌چینید، بامدادان به سوی کشت خویش روید.»'),
(5294, 68, 23, 'پس به راه افتادند و آهسته به هم می‌گفتند'),
(5295, 68, 24, '«که: امروز نباید در باغ بینوایی بر شما در آید.»'),
(5296, 68, 25, 'و صبحگاهان در حالی که خود را بر منع [بینوایان‌] توانا می‌دیدند، رفتند.'),
(5297, 68, 26, 'و چون [باغ‌] را دیدند، گفتند: «قطعاً ما راه گم کرده‌ایم.'),
(5298, 68, 27, '[نه‌] بلکه ما محرومیم.'),
(5299, 68, 28, 'خردمندترینشان گفت: «آیا به شما نگفتم: چرا خدا را به پاکی نمی‌ستایید؟»'),
(5300, 68, 29, 'گفتند: «پروردگارا، تو را به پاکی می‌ستاییم، ما واقعاً ستمگر بودیم. «'),
(5301, 68, 30, 'پس بعضی‌شان رو به بعضی دیگر آوردند و همدیگر را به نکوهش گرفتند.'),
(5302, 68, 31, 'گفتند: «ای وای بر ما که سرکش بوده‌ایم'),
(5303, 68, 32, 'امید است که پروردگار ما بهتر از آن را به ما عوض دهد، زیرا ما به پروردگارمان مشتاقیم.'),
(5304, 68, 33, 'عذاب [دنیا] چنین است، و عذاب آخرت، اگر می‌دانستند قطعاً بزرگتر خواهد بود.'),
(5305, 68, 34, 'برای پرهیزگاران، نزد پروردگارشان باغستانهای پر ناز و نعمت است.'),
(5306, 68, 35, 'پس آیا فرمانبرداران را چون بدکاران قرار خواهیم داد؟'),
(5307, 68, 36, 'شما را چه شده؟ چگونه داوری می‌کنید؟'),
(5308, 68, 37, 'یا شما را کتابی هست که در آن فرا می‌گیرید،'),
(5309, 68, 38, 'که هر چه را برمی‌گزینید، برای شما در آن خواهد بود؟'),
(5310, 68, 39, 'یا اینکه شما تا روز قیامت [از ما] سوگندهایی رسا گرفته‌اید که هر چه دلتان خواست حکم کنید؟'),
(5311, 68, 40, 'از آنان بپرس: کدامشان ضامن این [ادعا] یند؟'),
(5312, 68, 41, 'یا شریکانی دارند؟ پس اگر راست می‌گویند شریکانشان را بیاورند.'),
(5313, 68, 42, 'روزی که کار، زار [و رهایی دشوار] شود و به سجده فرا خوانده شوند و در خود توانایی نیابند.'),
(5314, 68, 43, 'دیدگانشان به زیر افتاده، خواری آنان را فرو می‌گیرد، در حالی که [پیش از این‌] به سجده دعوت می‌شدند و تندرست بودند.'),
(5315, 68, 44, 'پس مرا با کسی که این گفتار را تکذیب می‌کند واگذار. به تدریج آنان را به گونه‌ای که در نیابند [گریبان‌] خواهیم گرفت،'),
(5316, 68, 45, 'و مهلتشان می‌دهم، زیرا تدبیر من [سخت‌] استوار است.'),
(5317, 68, 46, 'آیا از آنان مزدی درخواست می‌کنی، و آنان خود را زیر بار تاوان، گرانبار می‌یابند؟'),
(5318, 68, 47, 'یا [علم‌] غیب پیش آنهاست و آنها می‌نویسند؟'),
(5319, 68, 48, 'پس در [امتثال‌] حکم پروردگارت شکیبایی ورز، و مانند همدم ماهی [=یونس‌] مباش، آنگاه که اندوه زده ندا درداد.'),
(5320, 68, 49, 'اگر لطفی از جانب پروردگارش تدارک [حال‌] او نمی‌کرد، قطعاً نکوهش شده بر زمین خشک انداخته می‌شد.'),
(5321, 68, 50, 'پس پروردگارش وی را برگزید و از شایستگانش گردانید.'),
(5322, 68, 51, 'و آنان که کافر شدند، چون قرآن را شنیدند چیزی نمانده بود که تو را چشم بزنند، و می‌گفتند: «او واقعاً دیوانه‌ای است.»'),
(5323, 68, 52, 'و حال آنکه [قرآن‌] جز تذکاری برای جهانیان نیست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن رخ دهنده.'),
(5325, 69, 2, 'چیست آن رخ دهنده؟'),
(5326, 69, 3, 'و چه دانی که آن رخ دهنده چیست؟'),
(5327, 69, 4, 'ثمود و عاد، آن حادثه کوبنده را تکذیب کردند.'),
(5328, 69, 5, 'اما ثمود به [سزای‌] سرکشی [خود] به هلاکت رسیدند.'),
(5329, 69, 6, 'و اما عاد، به [وسیله‌] تندبادی توفنده سرکش هلاک شدند.'),
(5330, 69, 7, '[که خدا] آن را هفت شب و هشت روز پیاپی بر آنان بگماشت: در آن [مدت‌] مردم را فرو افتاده می‌دیدی، گویی آنها تنه‌های نخلهای میان تهی‌اند.'),
(5331, 69, 8, 'آیا از آنان کسی را بر جای می‌بینی؟'),
(5332, 69, 9, 'و فرعون و کسانی که پیش از او بودند و [مردم‌] شهرهای سرنگون شده [سدوم و عاموره‌] مرتکب خطا شدند.'),
(5333, 69, 10, 'و از امر فرستاده پروردگارشان سرپیچی کردند، و [خدا هم‌] آنان را به گرفتنی سخت، فرو گرفت.'),
(5334, 69, 11, 'ما، چون آب طغیان کرد، شما را بر کشتی سوار نمودیم.'),
(5335, 69, 12, 'تا آن را برای شما [مایه‌] تذکری گردانیم و گوشهای شنوا آن را نگاه دارد.'),
(5336, 69, 13, 'پس آنگاه که در صور یک بار دمیده شود.'),
(5337, 69, 14, 'و زمین و کوه‌ها از جای خود برداشته شوند و هر دوی آنها با یک تکان ریز ریز گردند.'),
(5338, 69, 15, 'پس آن روز است که واقعه [آنچنانی‌] وقوع یابد.'),
(5339, 69, 16, 'و آسمان از هم بشکافد، و در آن روز است که آن از هم گسسته باشد.'),
(5340, 69, 17, 'و فرشتگان در اطراف [آسمان‌]اند، و عرش پروردگارت را آن روز، هشت [فرشته ] بر سر خود بر می‌دارند.'),
(5341, 69, 18, 'در آن روز، شما [به پیشگاه خدا] عرضه می‌شوید، [و] پوشیده‌ای از شما پوشیده نمی‌ماند.'),
(5342, 69, 19, 'اما کسی که کارنامه‌اش به دست راستش داده شود، گوید: «بیایید و کتابم را بخوانید.'),
(5343, 69, 20, 'من یقین داشتم که به حساب خود می‌رسم.'),
(5344, 69, 21, 'پس او در یک زندگی خوش است:'),
(5345, 69, 22, 'در بهشتی برین،'),
(5346, 69, 23, '[که‌] میوه هایش در دسترس است.'),
(5347, 69, 24, 'بخورید و بنوشید، گواراتان باد، به [پاداش‌] آنچه در روزهای گذشته انجام دادید.'),
(5348, 69, 25, 'و اما کسی که کارنامه‌اش به دست چپش داده شود، گوید: «ای کاش کتابم را دریافت نکرده بودم.'),
(5349, 69, 26, 'و از حساب خود خبردار نشده بودم.'),
(5350, 69, 27, 'ای کاش آن [مرگ‌] کار را تمام می‌کرد.'),
(5351, 69, 28, 'مال من، مرا سودی نبخشید.'),
(5352, 69, 29, 'قدرت من از [کف‌] من برفت.'),
(5353, 69, 30, '[گویند:] «بگیرید او را و در غل کشید.'),
(5354, 69, 31, 'آنگاه میان آتشش اندازید.'),
(5355, 69, 32, 'پس در زنجیری که درازی آن هفتاد گز است وی را در بند کشید.'),
(5356, 69, 33, 'چرا که او به خدای بزرگ نمی‌گروید.'),
(5357, 69, 34, 'و به اطعام مسکین تشویق نمی‌کرد.'),
(5358, 69, 35, 'پس امروز او را در اینجا حمایتگری نیست.'),
(5359, 69, 36, 'و خوراکی جز چرکابه ندارد،'),
(5360, 69, 37, 'که آن را جز خطاکاران نمی‌خورند.'),
(5361, 69, 38, 'پس نه [چنان است که می‌پندارید]، سوگند یاد می‌کنم به آنچه می‌بینید،'),
(5362, 69, 39, 'و آنچه نمی‌بینید،'),
(5363, 69, 40, 'که [قرآن‌] قطعاً گفتار فرستاده‌ای بزرگوار است.'),
(5364, 69, 41, 'و آن گفتار شاعری نیست [که‌] کمتر [به آن‌] ایمان دارید.'),
(5365, 69, 42, 'و نه گفتار کاهنی [که‌] کمتر [از آن‌] پند می‌گیرید.'),
(5366, 69, 43, '[پیام‌] فرودآمده‌ای است از جانب پروردگار جهانیان.'),
(5367, 69, 44, 'و اگر [او] پاره‌ای گفته‌ها بر ما بسته بود،'),
(5368, 69, 45, 'دست راستش را سخت می‌گرفتیم،'),
(5369, 69, 46, 'سپس رگ قلبش را پاره می‌کردیم،'),
(5370, 69, 47, 'و هیچ یک از شما مانع از [عذاب‌] او نمی‌شد.'),
(5371, 69, 48, 'و در حقیقت، [قرآن‌] تذکاری برای پرهیزگاران است.'),
(5372, 69, 49, 'و ما به راستی می‌دانیم که از [میان‌] شما تکذیب‌کنندگانی هستند.'),
(5373, 69, 50, 'و آن واقعاً بر کافران حسرتی است.'),
(5374, 69, 51, 'و این [قرآن‌]، بی‌شبهه، حقیقتی یقینی است.'),
(5375, 69, 52, 'پس به [پاس‌] نام پروردگار بزرگت تسبیح گوی.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'پرسنده‌ای از عذاب واقع‌شونده‌ای پرسید،'),
(5377, 70, 2, 'که اختصاص به کافران دارد [و] آن را بازدارنده‌ای نیست.'),
(5378, 70, 3, '[و] از جانب خداوند صاحب درجات [و مراتب‌] است.'),
(5379, 70, 4, 'فرشتگان و روح، در روزی که مقدارش پنجاه هزار سال است به سوی «او» بالا می‌روند.'),
(5380, 70, 5, 'پس صبر کن، صبری نیکو.'),
(5381, 70, 6, 'زیرا آنان [عذاب‌] را دور می‌بینند،'),
(5382, 70, 7, 'و [ما] نزدیکش می‌بینیم.'),
(5383, 70, 8, 'روزی که آسمانها چون فلز گداخته شود،'),
(5384, 70, 9, 'و کوهها چون پشم زده گردد،'),
(5385, 70, 10, 'و هیچ دوست صمیمی از دوست صمیمی [حال‌] نپرسد،'),
(5386, 70, 11, 'آنان را به ایشان نشان می‌دهند. گناهکار آرزو می‌کند که کاش برای رهایی از عذاب آن روز، می توانست پسران خود را عوض دهد،'),
(5387, 70, 12, 'و [نیز] همسرش و برادرش را،'),
(5388, 70, 13, 'و قبیله‌اش را که به او پناه می‌دهد،'),
(5389, 70, 14, 'و هر که را که در روی زمین است همه را [عوض می‌داد] و آنگاه خود را رها می‌کرد.'),
(5390, 70, 15, 'نه چنین است. [آتش‌] زبانه می‌کشد،'),
(5391, 70, 16, 'پوست سر و اندام را برکننده است.'),
(5392, 70, 17, 'هر که را پشت کرده و روی برتافته،'),
(5393, 70, 18, 'و گرد آورده و انباشته [و حسابش را نگاه داشته‌] فرا می‌خواند.'),
(5394, 70, 19, 'به راستی که انسان سخت آزمند [و بی‌تاب‌] خلق شده است.'),
(5395, 70, 20, 'چون صدمه‌ای به او رسد عجز و لابه کند.'),
(5396, 70, 21, 'و چون خیری به او رسد بخل ورزد.'),
(5397, 70, 22, 'غیر از نمازگزاران:'),
(5398, 70, 23, 'همان کسانی که بر نمازشان پایداری می‌کنند.'),
(5399, 70, 24, 'و همانان که در اموالشان حقی معلوم است،'),
(5400, 70, 25, 'برای سائل و محروم.'),
(5401, 70, 26, 'و کسانی که روز جزا را باور دارند.'),
(5402, 70, 27, 'و آنان که از عذاب پروردگارشان بیمناکند.'),
(5403, 70, 28, 'چرا که از عذاب پروردگارشان ایمن نمی‌توانند بود.'),
(5404, 70, 29, 'و کسانی که دامن خود را حفظ می‌کنند،'),
(5405, 70, 30, 'مگر بر همسران خود یا کنیزانشان که [در این صورت‌] مورد نکوهش نیستند.'),
(5406, 70, 31, 'و هر کس پا از این [حد] فراتر نهد، آنان همان از حد درگذرندگانند.'),
(5407, 70, 32, 'و کسانی که امانتها و پیمان خود را مراعات می‌کنند.'),
(5408, 70, 33, 'و آنان که بر شهادتهای خود ایستاده‌اند.'),
(5409, 70, 34, 'و کسانی که بر نمازشان مداومت می‌ورزند.'),
(5410, 70, 35, 'آنها هستند که در باغهایی [از بهشت‌]، گرامی خواهند بود.'),
(5411, 70, 36, 'چه شده است که آنان که کفر ورزیده‌اند، به سوی تو شتابان،'),
(5412, 70, 37, 'گروه گروه، از راست و از چپ [هجوم می‌آورند]؟'),
(5413, 70, 38, 'آیا هر یک از آنان طمع می‌بندد که در بهشت پر نعمت درآورده شود؟'),
(5414, 70, 39, 'نه چنین است. ما آنان را از آنچه [خود] می‌دانند آفریدیم.'),
(5415, 70, 40, '[هرگز،] به پروردگار خاوران و باختران سوگند یاد می‌کنم که ما تواناییم،'),
(5416, 70, 41, 'که به جای آنان بهتر از ایشان را بیاوریم؛ و بر ما پیشی نتوانند جست.'),
(5417, 70, 42, 'پس بگذارشان یاوه گویند و بازی کنند تا روزی را که وعده داده شده‌اند ملاقات نمایند.'),
(5418, 70, 43, 'روزی که از گورها[ی خود] شتابان برآیند، گویی که آنان به سوی پرچمهای افراشته می دوند.'),
(5419, 70, 44, 'دیدگانشان فرو افتاده، [غبار] مذلّت آنان را فرو گرفته است. این است همان روزی که به ایشان وعده داده می‌شد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به سوی قومش فرستادیم که: «قومت را، پیش از آنکه عذابی دردناک به آنان رسد، هشدار ده.»'),
(5421, 71, 2, '[نوح‌] گفت: «ای قوم من، من شما را هشدار دهنده‌ای آشکارم،'),
(5422, 71, 3, 'که خدا را بپرستید و از او پروا دارید و مرا فرمان برید.'),
(5423, 71, 4, '[تا] برخی از گناهانتان را بر شما ببخشاید و [اجل‌] شما را تا وقتی معین به تأخیر اندازد. اگر بدانید، چون وقت مقرر خدا برسد، تأخیر بر نخواهد داشت.'),
(5424, 71, 5, '[نوح‌] گفت: «پروردگارا، من قوم خود را شب و روز دعوت کردم،'),
(5425, 71, 6, 'و دعوت من جز بر گریزشان نیفزود.'),
(5426, 71, 7, 'و من هر بار که آنان را دعوت کردم تا ایشان را بیامرزی، انگشتانشان را در گوشهایشان کردند و ردای خویشتن بر سر کشیدند و اصرار ورزیدند و هر چه بیشتر بر کبر خود افزودند.'),
(5427, 71, 8, 'سپس من آشکارا آنان را دعوت کردم.'),
(5428, 71, 9, 'باز من به آنان اعلام نمودم و در خلوت [و] پوشیده نیز به ایشان گفتم.'),
(5429, 71, 10, 'و گفتم: از پروردگارتان آمرزش بخواهید که او همواره آمرزنده است.'),
(5430, 71, 11, '[تا] بر شما از آسمان باران پی در پی فرستد.'),
(5431, 71, 12, 'و شما را به اموال و پسران، یاری کند، و برایتان باغها قرار دهد و نهرها برای شما پدید آورد.'),
(5432, 71, 13, 'شما را چه شده است که از شکوه خدا بیم ندارید؟'),
(5433, 71, 14, 'و حال آنکه شما را مرحله به مرحله خلق کرده است.'),
(5434, 71, 15, 'مگر ملاحظه نکرده‌اید که چگونه خدا هفت آسمان را تُوبرتُو آفریده است؟'),
(5435, 71, 16, 'و ماه را در میان آنها روشنایی‌بخش گردانید و خورشید را [چون‌] چراغی قرار داد.'),
(5436, 71, 17, 'و خدا[ست که‌] شما را [مانند] گیاهی از زمین رویانید؛'),
(5437, 71, 18, 'سپس شما را در آن بازمی‌گرداند و بیرون می‌آورد بیرون‌آوردنی [عجیب‌]!'),
(5438, 71, 19, 'و خدا زمین را برای شما فرشی [گسترده‌] ساخت،'),
(5439, 71, 20, 'تا در راههای فراخ آن بروید.»'),
(5440, 71, 21, 'نوح گفت: «پروردگارا، آنان نافرمانی من کردند و کسی را پیروی نمودند که مال و فرزندش جز بر زیان وی نیفزود.'),
(5441, 71, 22, 'و دست به نیرنگی بس بزرگ زدند.'),
(5442, 71, 23, 'و گفتند: زنهار، خدایان خود را رها مکنید، و نه «وَدّ» را واگذارید و نه «سُواع» و نه «یَغُوث» و نه «یَعُوق» و نه «نَسْر» را.'),
(5443, 71, 24, 'و بسیاری را گمراه کرده‌اند. [بار خدایا،] جز بر گمراهی ستمکاران میفزای.»'),
(5444, 71, 25, '[تا] به سبب گناهانشان غرقه گشتند و [پس از مرگ‌] در آتشی درآورده شدند و برای خود، در برابر خدا یارانی نیافتند.'),
(5445, 71, 26, 'و نوح گفت: «پروردگارا، هیچ کس از کافران را بر روی زمین مگذار،'),
(5446, 71, 27, 'چرا که اگر تو آنان را باقی گذاری، بندگانت را گمراه می‌کنند و جز پلیدکار ناسپاس نزایند.'),
(5447, 71, 28, 'پروردگارا، بر من و پدر و مادرم و هر مؤمنی که در سرایم درآید، و بر مردان و زنان با ایمان ببخشای، و جز بر هلاکت ستمگران میفزای.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: «به من وحی شده است که تنی چند از جنّیان گوش فرا داشتند و گفتند: راستی ما قرآنی شگفت‌آور شنیدیم.'),
(5449, 72, 2, '[که‌] به راه راست هدایت می‌کند. پس به آن ایمان آوردیم و هرگز کسی را شریک پروردگارمان قرار نخواهیم داد.'),
(5450, 72, 3, 'و اینکه او، پروردگار والای ما، همسر و فرزندی اختیار نکرده است.'),
(5451, 72, 4, 'و [شگفت‌] آنکه کم خرد ما، در باره خدا سخنانی یاوه می‌سراید.'),
(5452, 72, 5, 'و ما پنداشته بودیم که انس و جن هرگز به خدا دروغ نمی‌بندند.'),
(5453, 72, 6, 'و مردانی از آدمیان به مردانی از جن پناه می‌بردند و بر سرکشی آنها می‌افزودند.'),
(5454, 72, 7, 'و آنها [نیز] آن گونه که [شما] پنداشته‌اید، گمان بردند که خدا هرگز کسی را زنده نخواهد گردانید.'),
(5455, 72, 8, 'و ما بر آسمان دست یافتیم و آن را پر از نگهبانان توانا و تیرهای شهاب یافتیم.'),
(5456, 72, 9, 'و در [آسمان‌] برای شنیدن، به کمین می‌نشستیم، [اما] اکنون هر که بخواهد به گوش باشد، تیر شهابی در کمین خود می‌یابد.'),
(5457, 72, 10, 'و ما [درست‌] نمی‌دانیم که آیا برای کسانی که در زمینند بدی خواسته شده یا پروردگارشان برایشان هدایت خواسته است؟'),
(5458, 72, 11, 'و از میان ما برخی درستکارند و برخی غیر آن، و ما فرقه‌هایی گوناگونیم.'),
(5459, 72, 12, 'و ما می‌دانیم که هرگز نمی‌توانیم در زمین خدای را به ستوه آوریم، و هرگز او را با گریز [خود] درمانده نتوانیم کرد'),
(5460, 72, 13, 'و ما چون هدایت را شنیدیم بدان گرویدیم؛ پس کسی که به پروردگار خود ایمان آورد، از کمی [پاداش‌] و سختی بیم ندارد.'),
(5461, 72, 14, 'و از میان ما برخی فرمانبردار و برخی از ما منحرفند: پس کسانی که به فرمانند، آنان در جستجوی راه درستند،'),
(5462, 72, 15, 'ولی منحرفان، هیزم جهنم خواهند بود.»'),
(5463, 72, 16, 'و اگر [مردم‌] در راه درست، پایداری ورزند، قطعاً آب گوارایی بدیشان نوشانیم.'),
(5464, 72, 17, 'تا در این باره آنان را بیازماییم، و هر کس از یاد پروردگار خود دل بگرداند، وی را در قید عذابی [روز]افزون درآورد.'),
(5465, 72, 18, 'و مساجد ویژه خداست، پس هیچ کس را با خدا مخوانید.'),
(5466, 72, 19, 'و همین که «بنده خدا» برخاست تا او را بخواند، چیزی نمانده بود که بر سر وی فرو افتند.'),
(5467, 72, 20, 'بگو: «من تنها پروردگار خود را می‌خوانم و کسی را با او شریک نمی‌گردانم.»'),
(5468, 72, 21, 'بگو: «من برای شما اختیار زیان و هدایتی را ندارم.»'),
(5469, 72, 22, 'بگو: «هرگز کسی مرا در برابر خدا پناه نمی‌دهد و هرگز پناهگاهی غیر از او نمی‌یابم.'),
(5470, 72, 23, '[وظیفه من‌] تنها ابلاغی از خدا و [رساندن‌] پیامهای اوست.» و هر کس خدا و پیامبرش را نافرمانی کند قطعاً آتش دوزخ برای اوست و جاودانه در آن خواهند ماند.'),
(5471, 72, 24, '[باش‌] تا آنچه را وعده داده می‌شوند ببینند، آنگاه دریابند که یاور چه کسی ضعیف‌تر و کدام یک شماره‌اش کمتر است.'),
(5472, 72, 25, 'بگو: «نمی‌دانم آنچه را که وعده داده شده‌اید نزدیک است یا پروردگارم برای آن زمانی نهاده است؟»'),
(5473, 72, 26, 'دانای نهان است، و کسی را بر غیب خود آگاه نمی‌کند،'),
(5474, 72, 27, 'جز پیامبری را که از او خشنود باشد، که [در این صورت‌] برای او از پیش رو و از پشت سرش نگاهبانانی بر خواهد گماشت،'),
(5475, 72, 28, 'تا معلوم بدارد که پیامهای پروردگار خود را رسانیده‌اند؛ و [خدا] بدانچه نزد ایشان است احاطه دارد و هر چیزی را به عدد شماره کرده است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ای جامه به خویشتن فرو پیچیده،'),
(5477, 73, 2, 'به پا خیز شب را مگر اندکی،'),
(5478, 73, 3, 'نیمی از شب یا اندکی از آن را بکاه،'),
(5479, 73, 4, 'یا بر آن [نصف‌] بیفزای و قرآن را شمرده شمرده بخوان.'),
(5480, 73, 5, 'در حقیقت ما به زودی بر تو گفتاری گرانبار القا می‌کنیم.'),
(5481, 73, 6, 'قطعاً برخاستن شب، رنجش بیشتر و گفتار [در آن هنگام‌] راستین‌تر است.'),
(5482, 73, 7, '[و] تو را، در روز، آمد و شدی دراز است.'),
(5483, 73, 8, 'و نام پروردگار خود را یاد کن و تنها به او بپرداز.'),
(5484, 73, 9, '[اوست‌] پروردگار خاور و باختر، خدایی جز او نیست، پس او را کارساز خویش اختیار کن.'),
(5485, 73, 10, 'و بر آنچه می‌گویند شکیبا باش و از آنان با دوری گزیدنی خوش فاصله بگیر.'),
(5486, 73, 11, 'و مرا با تکذیب‌کنندگان توانگر واگذار و اندکی مهلتشان ده.'),
(5487, 73, 12, 'در حقیقت پیش ما زنجیرها و دوزخ،'),
(5488, 73, 13, 'و غذایی گلوگیر و عذابی پر درد است.'),
(5489, 73, 14, 'روزی که زمین و کوهها به لرزه درآیند و کوهها به سان ریگ روان گردند.'),
(5490, 73, 15, 'بی‌گمان، ما به سوی شما فرستاده‌ای که گواه بر شماست روانه کردیم، همان گونه که فرستاده‌ای به سوی فرعون فرستادیم.'),
(5491, 73, 16, '[ولی‌] فرعون به آن فرستاده عصیان ورزید، پس ما او را به سختی فرو گرفتیم.'),
(5492, 73, 17, 'پس اگر کفر بورزید، چگونه از روزی که کودکان را پیر می‌گرداند، پرهیز توانید کرد؟'),
(5493, 73, 18, 'آسمان از [بیم‌] آن [روز] در هم شکافد؛ وعده او انجام یافتنی است.'),
(5494, 73, 19, 'قطعاً این [آیات‌] اندرزی است، تا هر که بخواهد به سوی پروردگار خود راهی در پیش گیرد.'),
(5495, 73, 20, 'در حقیقت، پروردگارت می‌داند که تو و گروهی از کسانی که با تواند، نزدیک به دو سوم از شب یا نصف آن یا یک سوم آن را [به نماز] برمی‌خیزید، و خداست که شب و روز را اندازه‌گیری می‌کند. [او ]می‌داند که [شما] هرگز حساب آن را ندارید، پس بر شما ببخشود، [اینک‌] هر چه از قرآن میسر می‌شود بخوانید. [خدا] می‌داند که به زودی در میانتان بیمارانی خواهند بود، و [عده‌ای‌] دیگر در زمین سفر می‌کنند [و] در پی روزی خدا هستند، و [گروهی‌] دیگر در راه خدا پیکار می‌نمایند. پس هر چه از [قرآن‌] میسر شد تلاوت کنید و نماز را برپا دارید و زکات را بپردازید و وام نیکو به خدا دهید؛ و هر کار خوبی برای خویش از پیش فرستید آن را نزد خدا بهتر و با پاداشی بیشتر باز خواهید یافت. و از خدا طلب آمرزش کنید که خدا آمرزنده مهربان است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ای کشیده ردای شب بر سر،'),
(5497, 74, 2, 'برخیز و بترسان.'),
(5498, 74, 3, 'و پروردگار خود را بزرگ دار.'),
(5499, 74, 4, 'و لباس خویشتن را پاک کن.'),
(5500, 74, 5, 'و از پلیدی دور شو.'),
(5501, 74, 6, 'و منّت مگذار و فزونی مطلب.'),
(5502, 74, 7, 'و برای پروردگارت شکیبایی کن.'),
(5503, 74, 8, 'پس چون در صور دمیده شود،'),
(5504, 74, 9, 'آن روز [چه‌] روز ناگواری است!'),
(5505, 74, 10, 'بر کافران آسان نیست.'),
(5506, 74, 11, 'مرا با آنکه [او را] تنها آفریدم واگذار.'),
(5507, 74, 12, 'و دارایی بسیار به او بخشیدم،'),
(5508, 74, 13, 'و پسرانی آماده [به خدمت، دادم‌]،'),
(5509, 74, 14, 'و برایش [عیش خوش‌] آماده کردم.'),
(5510, 74, 15, 'باز [هم‌] طمع دارد که بیفزایم.'),
(5511, 74, 16, 'ولی نه، زیرا او دشمن آیات ما بود.'),
(5512, 74, 17, 'به زودی او را به بالارفتن از گردنه [عذاب‌] وادار می‌کنم.'),
(5513, 74, 18, 'آری، [آن دشمن حق‌] اندیشید و سنجید.'),
(5514, 74, 19, 'کشته بادا، چگونه [او] سنجید؟'),
(5515, 74, 20, '[آری،] کشته بادا، چگونه [او] سنجید.'),
(5516, 74, 21, 'آنگاه نظر انداخت.'),
(5517, 74, 22, 'سپس رو ترش نمود و چهره در هم کشید.'),
(5518, 74, 23, 'آنگاه پشت گردانید و تکبّر ورزید،'),
(5519, 74, 24, 'و گفت: «این [قرآن‌] جز سحری که [به برخی‌] آموخته‌اند نیست.'),
(5520, 74, 25, 'این غیر از سخن بشر نیست.»'),
(5521, 74, 26, 'زودا که او را به سَقَر در آورم.'),
(5522, 74, 27, 'و تو چه دانی که آن سَقَر چیست؟'),
(5523, 74, 28, 'نه باقی می‌گذارد و نه رها می‌کند.'),
(5524, 74, 29, 'پوستها را سیاه می‌گرداند.'),
(5525, 74, 30, '[و] بر آن [دوزخ‌]، نوزده [نگهبان‌] است.'),
(5526, 74, 31, 'و ما موکّلان آتش را جز فرشتگان نگردانیدیم، و شماره آنها را جز آزمایشی برای کسانی که کافر شده‌اند قرار ندادیم، تا آنان که اهل کتابند یقین به هم رسانند، و ایمان کسانی که ایمان آورده‌اند افزون گردد، و آنان که کتاب به ایشان داده شده و [نیز] مؤمنان به شک نیفتند، و تا کسانی که در دلهایشان بیماری است و کافران بگویند: «خدا از این وصف‌کردن، چه چیزی را اراده کرده است؟» این گونه، خدا هر که را بخواهد بیراه می‌گذارد و هر که را بخواهد هدایت می کند، و [شماره‌] سپاهیان پروردگارت را جز او نمی‌داند، و این [آیات‌] جز تذکاری برای بشر نیست.'),
(5527, 74, 32, 'نه چنین است [که می‌پندارند!] سوگند به ماه،'),
(5528, 74, 33, 'و سوگند به شامگاه چون پشت کند،'),
(5529, 74, 34, 'و سوگند به بامداد چون آشکار شود،'),
(5530, 74, 35, 'که آیات [قرآن‌] از پدیده‌های بزرگ است.'),
(5531, 74, 36, 'بشر را هشداردهنده است.'),
(5532, 74, 37, 'هر که از شما را که بخواهد پیشی جوید یا بازایستد.'),
(5533, 74, 38, 'هر کسی در گرو دستاورد خویش است،'),
(5534, 74, 39, 'بجز یاران دست راست:'),
(5535, 74, 40, 'در میان باغها. از یکدیگر می‌پرسند،'),
(5536, 74, 41, 'درباره مجرمان:'),
(5537, 74, 42, '«چه چیز شما را در آتش [سَقَر] درآورد؟»'),
(5538, 74, 43, 'گویند: «از نمازگزاران نبودیم،'),
(5539, 74, 44, 'و بینوایان را غذا نمی‌دادیم،'),
(5540, 74, 45, 'با هرزه‌درایان هرزه‌درایی می‌کردیم،'),
(5541, 74, 46, 'و روز جزا را دروغ می‌شمردیم،'),
(5542, 74, 47, 'تا مرگ ما در رسید.»'),
(5543, 74, 48, 'از این رو شفاعت شفاعت‌کنندگان به حال آنها سودی نمی‌بخشد.'),
(5544, 74, 49, 'چرا آنها از تذکر روی گردانند؟!'),
(5545, 74, 50, 'گویی گورخرانی رمیده‌اند،'),
(5546, 74, 51, 'که از (مقابل) شیری فرار کرده‌اند!'),
(5547, 74, 52, 'بلکه هر کدام از آنها انتظار دارد نامه جداگانه‌ای (از سوی خدا) برای او فرستاده شود!'),
(5548, 74, 53, 'چنین نیست که آنان می‌گویند، بلکه آنها از آخرت نمی‌ترسند!'),
(5549, 74, 54, 'چنین نیست که آنها می‌گویند، آن (قرآن) یک تذکر و یادآوری است!'),
(5550, 74, 55, 'هر کس بخواهد از آن پند می‌گیرد؛'),
(5551, 74, 56, 'و هیچ کس پند نمی‌گیرد مگر اینکه خدا بخواهد؛ او اهل تقوا و اهل آمرزش است!');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'سوگند به روز قیامت،'),
(5553, 75, 2, 'و سوگند به (نفس لوّامه و) وجدان بیدار و ملامتگر (که رستاخیز حقّ است)!'),
(5554, 75, 3, 'آیا انسان می‌پندارد که هرگز استخوانهای او را جمع نخواهیم کرد؟!'),
(5555, 75, 4, 'آری قادریم که (حتی خطوط سر) انگشتان او را موزون و مرتّب کنیم!'),
(5556, 75, 5, '(انسان شک در معاد ندارد) بلکه او می‌خواهد (آزاد باشد و بدون ترس از دادگاه قیامت) در تمام عمر گناه کند!'),
(5557, 75, 6, '(از این‌رو) می‌پرسد: «قیامت کی خواهد بود»!'),
(5558, 75, 7, '(بگو:) در آن هنگام که چشمها از شدّت وحشت به گردش در آید،'),
(5559, 75, 8, 'و ماه بی‌نور گردد،'),
(5560, 75, 9, 'و خورشید و ماه یک جا جمع شوند،'),
(5561, 75, 10, 'آن روز انسان می‌گوید: «راه فرار کجاست؟!»'),
(5562, 75, 11, 'هرگز چنین نیست، راه فرار و پناهگاهی وجود ندارد!'),
(5563, 75, 12, 'آن روز قرارگاه نهایی تنها بسوی پروردگار تو است؛'),
(5564, 75, 13, 'و در آن روز انسان را از تمام کارهایی که از پیش یا پس فرستاده آگاه می‌کنند!'),
(5565, 75, 14, 'بلکه انسان خودش از وضع خود آگاه است،'),
(5566, 75, 15, 'هر چند (در ظاهر) برای خود عذرهایی بتراشد!'),
(5567, 75, 16, 'زبانت را بخاطر عجله برای خواندن آن [= قرآن‌] حرکت مده،'),
(5568, 75, 17, 'چرا که جمع‌کردن و خواندن آن بر عهده ماست!'),
(5569, 75, 18, 'پس هر گاه آن را خواندیم، از خواندن آن پیروی کن!'),
(5570, 75, 19, 'سپس بیان (و توضیح) آن (نیز) بر عهده ماست!'),
(5571, 75, 20, 'چنین نیست که شما می‌پندارید (و دلایل معاد را کافی نمی‌دانید)؛ بلکه شما دنیای زودگذر را دوست دارید (و هوسرانی بی‌قید و شرط را)!'),
(5572, 75, 21, 'و آخرت را رها می‌کنید!'),
(5573, 75, 22, '(آری) در آن روز صورتهایی شاداب و مسرور است،'),
(5574, 75, 23, 'و به پروردگارش می‌نگرد!'),
(5575, 75, 24, 'و در آن روز صورتهایی عبوس و در هم کشیده است،'),
(5576, 75, 25, 'زیرا می‌داند عذابی در پیش دارد که پشت را در هم می‌شکند!'),
(5577, 75, 26, 'چنین نیست (که انسان می‌پندارد! او ایمان نمی‌آورد) تا موقعی که جان به گلوگاهش رسد،'),
(5578, 75, 27, 'و گفته شود: «آیا کسی هست که(این بیمار را از مرگ) نجات دهد؟!»'),
(5579, 75, 28, 'و به جدائی از دنیا یقین پیدا کند،'),
(5580, 75, 29, 'و ساق پاها (از سختی جان دادن) به هم بپیچد!'),
(5581, 75, 30, '(آری) در آن روز مسیر همه بسوی (دادگاه) پروردگارت خواهد بود!'),
(5582, 75, 31, '(در آن روز گفته می‌شود:) او هرگز ایمان نیاورد و نماز نخواند،'),
(5583, 75, 32, 'بلکه تکذیب کرد و روی‌گردان شد،'),
(5584, 75, 33, 'سپس بسوی خانواده خود باز گشت در حالی که متکبّرانه قدم برمی‌داشت!'),
(5585, 75, 34, '(با این اعمال) عذاب الهی برای تو شایسته‌تر است، شایسته‌تر!'),
(5586, 75, 35, 'سپس عذاب الهی برای تو شایسته‌تر است، شایسته‌تر!'),
(5587, 75, 36, 'آیا انسان گمان می‌کند بی‌هدف رها می‌شود؟!'),
(5588, 75, 37, 'آیا او نطفه‌ای از منی که در رحم ریخته می‌شود نبود؟!'),
(5589, 75, 38, 'سپس بصورت خون‌بسته در آمد، و خداوند او را آفرید و موزون ساخت،'),
(5590, 75, 39, 'و از او دو زوج مرد و زن آفرید!'),
(5591, 75, 40, 'آیا چنین کسی قادر نیست که مردگان را زنده کند؟!');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آیا زمانی طولانی بر انسان گذشت که چیز قابل ذکری نبود؟!'),
(5593, 76, 2, 'ما انسان را از نطفه مختلطی آفریدیم، و او را می‌آزماییم؛ (بدین جهت) او را شنوا و بینا قرار دادیم!'),
(5594, 76, 3, 'ما راه را به او نشان دادیم، خواه شاکر باشد (و پذیرا گردد) یا ناسپاس!'),
(5595, 76, 4, 'ما برای کافران، زنجیرها و غُلها و شعله‌های سوزان آتش آماده کرده‌ایم!'),
(5596, 76, 5, 'به یقین ابرار (و نیکان) از جامی می‌نوشند که با عطر خوشی آمیخته است،'),
(5597, 76, 6, 'چشمه‌ای که بندگان خدا از آن می‌نوشند و [به دلخواه خویش‌] جاریش می‌کنند.'),
(5598, 76, 7, '[همان بندگانی که‌] به نذر خود وفا می‌کردند، و از روزی که گزند آن فراگیرنده است می‌ترسیدند.'),
(5599, 76, 8, 'و به [پاس‌] دوستی [خدا]، بینوا و یتیم و اسیر را خوراک می‌دادند.'),
(5600, 76, 9, '«ما برای خشنودی خداست که به شما می‌خورانیم و پاداش و سپاسی از شما نمی‌خواهیم.'),
(5601, 76, 10, 'ما از پروردگارمان از روز عبوسی سخت، هراسناکیم.»'),
(5602, 76, 11, 'پس خدا [هم‌] آنان را از آسیب آن روز نگاه داشت و شادابی و شادمانی به آنان ارزانی داشت.'),
(5603, 76, 12, 'و به [پاس‌] آنکه صبر کردند، بهشت و پرنیان پاداششان داد.'),
(5604, 76, 13, 'در آن [بهشت‌] بر تختها[ی خویش‌] تکیه زنند. در آنجا نه آفتابی بینند و نه سرمایی.'),
(5605, 76, 14, 'و سایه‌ها[ی درختان‌] به آنان نزدیک است، و میوه‌هایش [برای چیدن‌] رام.'),
(5606, 76, 15, 'و ظروف سیمین و جامهای بلورین، پیرامون آنان گردانده می‌شود.'),
(5607, 76, 16, 'جامهایی از سیم که درست به اندازه [و با کمال ظرافت‌] آنها را از کار در آورده‌اند.'),
(5608, 76, 17, 'و در آنجا از جامی که آمیزه زنجبیل دارد به آنان می‌نوشانند.'),
(5609, 76, 18, 'از چشمه‌ای در آنجا که «سلسبیل» نامیده می‌شود.'),
(5610, 76, 19, 'و بر گرد آنان پسرانی جاودانی می‌گردند. چون آنها را ببینی، گویی که مرواریدهایی پراکنده‌اند.'),
(5611, 76, 20, 'و چون بدانجا نگری [سرزمینی از] نعمت و کشوری پهناور می‌بینی.'),
(5612, 76, 21, '[بهشتیان را] جامه‌های ابریشمی سبز و دیبای ستبر در بر است و پیرایه آنان دستبندهای سیمین است و پروردگارشان باده‌ای پاک به آنان می‌نوشاند.'),
(5613, 76, 22, 'این [پاداش‌] برای شماست و کوشش شما مقبول افتاده است.'),
(5614, 76, 23, 'در حقیقت، ما قرآن را بر تو به تدریج فرو فرستادیم.'),
(5615, 76, 24, 'پس در برابر فرمان پروردگارت شکیبایی کن، و از آنان گناهکار یا ناسپاسگزار را فرمان مبر.'),
(5616, 76, 25, 'و نام پروردگارت را بامدادان و شامگاهان یاد کن.'),
(5617, 76, 26, 'و بخشی از شب را در برابر او سجده کن و شب‌[های‌] دراز، او را به پاکی بستای.'),
(5618, 76, 27, 'اینان دنیای زودگذر را دوست دارند، و روزی گرانبار را [به غفلت‌] پشت سر می‌افکنند.'),
(5619, 76, 28, 'ماییم که آنان را آفریده و پیوند مفاصل آنها را استوار کرده‌ایم، و چون بخواهیم، [آنان را] به نظایرشان تبدیل می‌کنیم.'),
(5620, 76, 29, 'این [آیات،] پندنامه‌ای است. تا هر که خواهد، راهی به سوی پروردگار خود پیش گیرد.'),
(5621, 76, 30, 'و تا خدا نخواهد، [شما] نخواهید خواست؛ قطعاً خدا دانای حکیم است.'),
(5622, 76, 31, 'هر که را خواهد، به رحمت خویش در می‌آورد، و برای ظالمان عذابی پردرد آماده کرده است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به فرستادگان پی‌درپی،'),
(5624, 77, 2, 'که سخت توفنده‌اند.'),
(5625, 77, 3, 'و سوگند به افشانندگان افشانگر،'),
(5626, 77, 4, 'که [میان حق و باطل‌] جداگرند،'),
(5627, 77, 5, 'و القاکننده وحی‌اند.'),
(5628, 77, 6, 'خواه عذری باشد یا هشداری.'),
(5629, 77, 7, 'که آنچه وعده یافته‌اید قطعاً رخ خواهد داد.'),
(5630, 77, 8, 'پس وقتی که ستارگان محو شوند،'),
(5631, 77, 9, 'و آنگاه که آسمان بشکافد،'),
(5632, 77, 10, 'و آنگاه که کوه‌ها از جا کنده شوند،'),
(5633, 77, 11, 'و آنگاه که پیمبران به میقات آیند،'),
(5634, 77, 12, 'برای چه روزی تعیین وقت شده است؟'),
(5635, 77, 13, 'برای روز داوری.'),
(5636, 77, 14, 'و تو چه دانی که روز داوری چیست؟'),
(5637, 77, 15, 'آن روز وای بر تکذیب‌کنندگان!'),
(5638, 77, 16, 'مگر پیشینیان را هلاک نکردیم؟'),
(5639, 77, 17, 'سپس از پی آنان پسینیان را می‌بریم.'),
(5640, 77, 18, 'با مجرمان چنین می‌کنیم.'),
(5641, 77, 19, 'آن روز وای بر تکذیب‌کنندگان.'),
(5642, 77, 20, 'مگر شما را از آبی بی‌مقدار نیافریدیم؟'),
(5643, 77, 21, 'پس آن را در جایگاهی استوار نهادیم،'),
(5644, 77, 22, 'تا مدّتی معیّن!'),
(5645, 77, 23, 'و توانا آمدیم، و چه نیک تواناییم.'),
(5646, 77, 24, 'آن روز وای بر تکذیب‌کنندگان.'),
(5647, 77, 25, 'مگر زمین را محل اجتماع نگردانیدیم؟'),
(5648, 77, 26, 'چه برای مردگان چه زندگان.'),
(5649, 77, 27, 'و کوه‌های بلند در آن نهادیم و به شما آبی گوارا نوشانیدیم.'),
(5650, 77, 28, 'آن روز وای بر تکذیب‌کنندگان.'),
(5651, 77, 29, 'بروید به سوی همان چیزی که آن را تکذیب می‌کردید.'),
(5652, 77, 30, 'بروید به سوی [آن‌] دود سه شاخه،'),
(5653, 77, 31, 'نه سایه‌دار است و نه از شعله [آتش‌] حفاظت می‌کند.'),
(5654, 77, 32, '[دوزخ‌] چون کاخی [بلند] شراره می‌افکند،'),
(5655, 77, 33, 'گویی شترانی زرد رنگند.'),
(5656, 77, 34, 'آن روز وای بر تکذیب‌کنندگان.'),
(5657, 77, 35, 'این، روزی است که دم نمی‌زنند.'),
(5658, 77, 36, 'و رخصت نمی‌یابند تا پوزش خواهند.'),
(5659, 77, 37, 'آن روز وای بر تکذیب‌کنندگان.'),
(5660, 77, 38, 'این [همان‌] روز داوری است شما و [جمله‌] پیشینیان را گرد می‌آوریم.'),
(5661, 77, 39, 'پس اگر حیلتی دارید، در برابر من بسگالید.'),
(5662, 77, 40, 'آن روز وای بر تکذیب‌کنندگان.'),
(5663, 77, 41, 'اهل تقوا در زیر سایه‌ها و بر کنار چشمه‌سارانند،'),
(5664, 77, 42, 'با هر میوه‌ای که خوش داشته باشند.'),
(5665, 77, 43, 'به [پاداش‌] آنچه می‌کردید، بخورید و بیاشامید؛ گواراتان باد.'),
(5666, 77, 44, 'ما نیکوکاران را چنین پاداش می‌دهیم.'),
(5667, 77, 45, 'آن روز وای بر تکذیب‌کنندگان.'),
(5668, 77, 46, '[ای کافران،] بخورید و اندکی برخوردار شوید که شما گناهکارید.'),
(5669, 77, 47, '[ولی‌] آن روز وای بر تکذیب‌کنندگان.'),
(5670, 77, 48, 'و چون به آنان گفته شود: «رکوع کنید»، به رکوع نمی‌روند.'),
(5671, 77, 49, 'آن روز وای بر تکذیب‌کنندگان.'),
(5672, 77, 50, 'پس به کدامین سخن پس از [قرآن‌] ایمان می‌آورند؟');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'درباره چه چیز از یکدیگر می‌پرسند؟'),
(5674, 78, 2, 'از آن خبر بزرگ،'),
(5675, 78, 3, 'که در باره آن با هم اختلاف دارند.'),
(5676, 78, 4, 'نه چنان است، به زودی خواهند دانست.'),
(5677, 78, 5, 'باز هم نه چنان است، بزودی خواهند دانست.'),
(5678, 78, 6, 'آیا زمین را گهواره‌ای نگردانیدیم؟'),
(5679, 78, 7, 'و کوهها را [چون‌] میخهایی [نگذاشتیم‌]؟'),
(5680, 78, 8, 'و شما را جفت آفریدیم.'),
(5681, 78, 9, 'و خواب شما را [مایه‌] آسایش گردانیدیم.'),
(5682, 78, 10, 'و شب را [برای شما] پوششی قرار دادیم.'),
(5683, 78, 11, 'و روز را [برای‌] معاش [شما] نهادیم.'),
(5684, 78, 12, 'و بر فراز شما هفت [آسمان‌] استوار بنا کردیم.'),
(5685, 78, 13, 'و چراغی فروزان گذاردیم.'),
(5686, 78, 14, 'و از ابرهای متراکم، آبی ریزان فرود آوردیم،'),
(5687, 78, 15, 'تا بدان دانه و گیاه برویانیم،'),
(5688, 78, 16, 'و باغهای در هم پیچیده و انبوه.'),
(5689, 78, 17, 'قطعاً وعدگاه [ما با شما] روز داوری است:'),
(5690, 78, 18, 'روزی که در «صور» دمیده شود، و گروه گروه بیایید؛'),
(5691, 78, 19, 'و آسمان، گشوده و درهایی [پدید] شود؛'),
(5692, 78, 20, 'و کوهها را روان کنند، و [چون‌] سرابی گردند.'),
(5693, 78, 21, '[آری،] جهنم [از دیر باز] کمینگاهی بوده،'),
(5694, 78, 22, '[که‌] برای سرکشان، بازگشتگاهی است.'),
(5695, 78, 23, 'روزگاری دراز در آن درنگ کنند.'),
(5696, 78, 24, 'در آنجا نه خنکی چشند و نه شربتی،'),
(5697, 78, 25, 'جز آب جوشان و چرکابه‌ای.'),
(5698, 78, 26, 'کیفری مناسب [با جرم آنها].'),
(5699, 78, 27, 'آنان بودند که به [روز] حساب امید نداشتند؛'),
(5700, 78, 28, 'و آیات ما را سخت تکذیب می‌کردند.'),
(5701, 78, 29, 'و حال آنکه هر چیزی را برشمرده [به صورت‌] کتابی در آورده‌ایم.'),
(5702, 78, 30, 'پس بچشید که جز عذاب، هرگز [چیزی‌] بر شما نمی‌افزاییم.'),
(5703, 78, 31, 'مسلماً پرهیزگاران را رستگاری است:'),
(5704, 78, 32, 'باغچه‌ها و تاکستانها،'),
(5705, 78, 33, 'و دخترانی همسال با سینه‌های برجسته،'),
(5706, 78, 34, 'و پیاله‌های لبالب.'),
(5707, 78, 35, 'در آنجا نه بیهوده‌ای شنوند، و نه [یکدیگر را] تکذیب [کنند].'),
(5708, 78, 36, '[این است‌] پاداشی از پروردگار تو، عطایی از روی حساب.'),
(5709, 78, 37, 'پروردگار آسمانها و زمین و آنچه میان آن دو است، بخشایشگری که کس را یارای خطاب با او نیست.'),
(5710, 78, 38, 'روزی که «روح» و فرشتگان به صف می‌ایستند، و [مردم‌] سخن نگویند، مگر کسی که [خدای‌] رحمان به او رخصت دهد، و سخن راست گوید.'),
(5711, 78, 39, 'آن [روز]، روز حق است؛ پس هر که خواهد، راه بازگشتی به سوی پروردگار خود بجوید.'),
(5712, 78, 40, 'ما شما را از عذابی نزدیک هشدار دادیم: روزی که آدمی آنچه را با دست خویش پیش فرستاده است بنگرد؛ و کافر گوید: «کاش من خاک بودم.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به فرشتگانی که [از کافران‌] به سختی جان ستانند،'),
(5714, 79, 2, 'و به فرشتگانی که جان [مؤمنان‌] را به آرامی گیرند،'),
(5715, 79, 3, 'و به فرشتگانی که [در دریای بی‌مانند] شناکنان شناورند،'),
(5716, 79, 4, 'پس در پیشی گرفتن [در فرمان خدا] سبقت‌گیرنده‌اند،'),
(5717, 79, 5, 'و کار [بندگان‌] را تدبیر می‌کنند.'),
(5718, 79, 6, 'آن روز که لرزنده بلرزد،'),
(5719, 79, 7, 'و از پی آن لرزه‌ای [دگر] افتد،'),
(5720, 79, 8, 'در آن روز، دلهایی سخت هراسانند.'),
(5721, 79, 9, 'دیدگان آنها فرو افتاده.'),
(5722, 79, 10, 'گویند: «آیا [باز] ما به [مغاک‌] زمین برمی‌گردیم؟'),
(5723, 79, 11, 'آیا وقتی ما استخوان‌ریزه‌های پوسیده شدیم [زندگی را از سر می‌گیریم‌]؟»'),
(5724, 79, 12, '[و با خود] گویند: «در این صورت، این برگشتی زیان‌آور است.»'),
(5725, 79, 13, 'و[لی‌] در حقیقت، آن [بازگشت، بسته به‌] یک فریاد است [و بس‌].'),
(5726, 79, 14, 'و بناگاه آنان در زمین هموار خواهند بود.'),
(5727, 79, 15, 'آیا سرگذشت موسی بر تو آمد؟'),
(5728, 79, 16, 'آنگاه که پروردگارش او را در وادی مقدس «طوی» ندا درداد:'),
(5729, 79, 17, '«به سوی فرعون برو که وی سر برداشته است؛'),
(5730, 79, 18, 'و بگو: آیا سر آن داری که به پاکیزگی گرایی،'),
(5731, 79, 19, 'و تو را به سوی پروردگارت راه نمایم تا پروا بداری؟»'),
(5732, 79, 20, 'پس معجزه بزرگ [خود] را بدو نمود.'),
(5733, 79, 21, 'و[لی فرعون‌] تکذیب نمود و عصیان کرد.'),
(5734, 79, 22, 'سپس پشت کرد [و] به کوشش برخاست،'),
(5735, 79, 23, 'و گروهی را فراهم آورد [و] ندا درداد،'),
(5736, 79, 24, 'و گفت: «پروردگار بزرگتر شما منم!»'),
(5737, 79, 25, 'و خدا [هم‌] او را به کیفر دنیا و آخرت گرفتار کرد.'),
(5738, 79, 26, 'در حقیقت، برای هر کس که [از خدا] بترسد، در این [ماجرا] عبرتی است.'),
(5739, 79, 27, 'آیا آفرینش شما دشوارتر است یا آسمانی که [او] آن را برپا کرده است؟'),
(5740, 79, 28, 'سقفش را برافراشت و آن را [به اندازه معین‌] درست کرد،'),
(5741, 79, 29, 'و شبش را تیره و روزش را آشکار گردانید،'),
(5742, 79, 30, 'و پس از آن، زمین را با غلتانیدن گسترد،'),
(5743, 79, 31, 'آبش و چراگاهش را از آن بیرون آورد،'),
(5744, 79, 32, 'و کوهها را لنگر آن گردانید،'),
(5745, 79, 33, '[تا وسیله‌] استفاده برای شما و دامهایتان باشد.'),
(5746, 79, 34, 'پس آنگاه که آن هنگامه بزرگ دررسد،'),
(5747, 79, 35, '[آن‌] روز است که انسان آنچه را که در پی آن کوشیده است به یاد آورد'),
(5748, 79, 36, 'و جهنم برای هر که بیند آشکار گردد.'),
(5749, 79, 37, 'اما هر که طغیان کرد،'),
(5750, 79, 38, 'و زندگی پست دنیا را برگزید،'),
(5751, 79, 39, 'پس جایگاه او همان آتش است.'),
(5752, 79, 40, 'و اما کسی که از ایستادن در برابر پروردگارش هراسید، و نفس خود را از هوس باز داشت...'),
(5753, 79, 41, 'پس جایگاه او همان بهشت است.'),
(5754, 79, 42, 'در باره رستاخیز از تو می‌پرسند که فرارسیدنش چه وقت است؟'),
(5755, 79, 43, 'تو را چه به گفتگو در آن.'),
(5756, 79, 44, 'علم آن با پروردگار تو است.'),
(5757, 79, 45, 'تو فقط کسی را که از آن می‌ترسد هشدار می‌دهی.'),
(5758, 79, 46, 'روزی که آن را می‌بینند، گویی که آنان جز شبی یا روزی درنگ نکرده‌اند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'چهره در هم کشید و روی گردانید،'),
(5760, 80, 2, 'که آن مرد نابینا پیش او آمد؛'),
(5761, 80, 3, 'و تو چه دانی، شاید او به پاکی گراید،'),
(5762, 80, 4, 'یا پند پذیرد و اندرز سودش دهد.'),
(5763, 80, 5, 'اما آن کس که خود را بی‌نیاز می‌پندارد،'),
(5764, 80, 6, 'تو بدو می‌پردازی؛'),
(5765, 80, 7, 'با آنکه اگر پاک نگردد، بر تو [مسؤولیتی‌] نیست.'),
(5766, 80, 8, 'و اما آن کس که شتابان پیش تو آمد،'),
(5767, 80, 9, 'در حالی که [از خدا] می‌ترسید،'),
(5768, 80, 10, 'تو از او به دیگران می‌پردازی.'),
(5769, 80, 11, 'زنهار [چنین مکن‌] این [آیات‌] پندی است.'),
(5770, 80, 12, 'تا هر که خواهد، از آن پند گیرد.'),
(5771, 80, 13, 'در صحیفه‌هایی ارجمند،'),
(5772, 80, 14, 'والا و پاک‌شده،'),
(5773, 80, 15, 'به دست فرشتگانی،'),
(5774, 80, 16, 'ارجمند و نیکوکار.'),
(5775, 80, 17, 'کشته باد انسان، چه ناسپاس است'),
(5776, 80, 18, 'او را از چه چیز آفریده است؟'),
(5777, 80, 19, 'از نطفه‌ای خلقش کرد و اندازه مقررش بخشید.'),
(5778, 80, 20, 'سپس راه را بر او آسان گردانید.'),
(5779, 80, 21, 'آنگاه به مرگش رسانید و در قبرش نهاد.'),
(5780, 80, 22, 'سپس چون بخواهد او را برانگیزد.'),
(5781, 80, 23, 'ولی نه! هنوز آنچه را به او دستور داده، به جای نیاورده است.'),
(5782, 80, 24, 'پس انسان باید به خوراک خود بنگرد،'),
(5783, 80, 25, 'که ما آب را به صورت بارشی فرو ریختیم؛'),
(5784, 80, 26, 'آنگاه زمین را با شکافتنی [لازم‌] شکافتیم؛'),
(5785, 80, 27, 'پس در آن، دانه رویانیدیم.'),
(5786, 80, 28, 'و انگور و سبزی،'),
(5787, 80, 29, 'و زیتون و درخت خرما،'),
(5788, 80, 30, 'و باغهای انبوه،'),
(5789, 80, 31, 'و میوه و چراگاه،'),
(5790, 80, 32, '[تا وسیله‌] استفاده شما و دامهایتان باشد.'),
(5791, 80, 33, 'پس چون فریاد گوش‌خراش دررسد؛'),
(5792, 80, 34, 'روزی که آدمی از برادرش،'),
(5793, 80, 35, 'و از مادرش و پدرش.'),
(5794, 80, 36, 'و از همسرش و پسرانش می‌گریزد،'),
(5795, 80, 37, 'در آن روز، هر کسی از آنان را کاری است که او را به خود مشغول می‌دارد.'),
(5796, 80, 38, 'در آن روز، چهره‌هایی درخشانند،'),
(5797, 80, 39, 'خندان [و] شادانند.'),
(5798, 80, 40, 'و در آن روز، چهره‌هایی است که بر آنها غبار نشسته،'),
(5799, 80, 41, '[و] آنها را تاریکی پوشانده است؛'),
(5800, 80, 42, 'آنان همان کافران بدکارند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آنگاه که خورشید به هم درپیچد،'),
(5802, 81, 2, 'و آنگه که ستارگان همی‌تیره شوند،'),
(5803, 81, 3, 'و آنگاه که کوهها به رفتار آیند.'),
(5804, 81, 4, 'وقتی شتران ماده وانهاده شوند،'),
(5805, 81, 5, 'و آنگه که وحوش را همی‌گرد آرند،'),
(5806, 81, 6, 'دریاها آنگه که جوشان گردند،'),
(5807, 81, 7, 'و آنگاه که جانها به هم درپیوندند،'),
(5808, 81, 8, 'پرسند چو زان دخترک زنده به‌گور:'),
(5809, 81, 9, 'به کدامین گناه کشته شده است؟'),
(5810, 81, 10, 'و آنگاه که نامه‌ها زهم بگشایند،'),
(5811, 81, 11, 'و آنگاه که آسمان زجا کنده شود،'),
(5812, 81, 12, 'و آنگه که جحیم را برافروزانند،'),
(5813, 81, 13, 'و آنگه که بهشت را فرا پیش آرند،'),
(5814, 81, 14, 'هر نَفْس بداند چه فراهم دیده.'),
(5815, 81, 15, 'نه، نه! سوگند به اختران گردان،'),
(5816, 81, 16, '[کز دیده‌] نهان شوند و از نو آیند،'),
(5817, 81, 17, 'سوگند به شب چون پشت گرداند،'),
(5818, 81, 18, 'سوگند به صبح چون دمیدن گیرد،'),
(5819, 81, 19, 'که [قرآن‌] سخن فرشته بزرگواری است.'),
(5820, 81, 20, 'نیرومند [که‌] پیش خداوند عرش، بلندپایگاه است.'),
(5821, 81, 21, 'در آنجا [هم‌] مطاع [و هم‌] امین است.'),
(5822, 81, 22, 'و رفیق شما مجنون نیست؛'),
(5823, 81, 23, 'و قطعاً آن [فرشته وحی‌] را در افق رخشان دیده.'),
(5824, 81, 24, 'و او در امر غیب بخیل نیست.'),
(5825, 81, 25, 'و [قرآن‌] نیست سخن دیو رجیم.'),
(5826, 81, 26, 'پس به کجا می‌روید؟'),
(5827, 81, 27, 'این [سخن‌] بجز پندی برای عالمیان نیست؛'),
(5828, 81, 28, 'برای هر یک از شما که خواهد به راه راست رود.'),
(5829, 81, 29, 'و تا خدا، پروردگار جهانها، نخواهد، [شما نیز] نخواهید خواست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'آنگاه که آسمان زهم بشکافد،'),
(5831, 82, 2, 'و آنگاه که اختران پراکنده شوند،'),
(5832, 82, 3, 'و آنگاه که دریاها از جا برکنده گردند،'),
(5833, 82, 4, 'و آنگاه که گورها زیر و زبر شوند،'),
(5834, 82, 5, 'هر نفسی آنچه را پیش فرستاده و بازپس گذاشته، بداند.'),
(5835, 82, 6, 'ای انسان، چه چیز تو را در باره پروردگار بزرگوارت مغرور ساخته؟'),
(5836, 82, 7, 'همان کس که تو را آفرید، و [اندام‌] تو را درست کرد، و [آنگاه‌] تو را سامان بخشید.'),
(5837, 82, 8, 'و به هر صورتی که خواست، تو را ترکیب کرد.'),
(5838, 82, 9, 'با این همه، شما منکر [روز] جزایید.'),
(5839, 82, 10, 'و قطعاً بر شما نگهبانانی [گماشته شده‌]اند:'),
(5840, 82, 11, '[فرشتگان‌] بزرگواری که نویسندگان [اعمال شما] هستند؛'),
(5841, 82, 12, 'آنچه را می‌کنید، می‌دانند.'),
(5842, 82, 13, 'قطعاً نیکان به بهشت اندرند.'),
(5843, 82, 14, 'و بی‌شک، بدکاران در دوزخند.'),
(5844, 82, 15, 'روز جزا در آنجا درآیند،'),
(5845, 82, 16, 'و از آن [عذاب‌] دور نخواهند بود.'),
(5846, 82, 17, 'و تو چه دانی که چیست روز جزا؟'),
(5847, 82, 18, 'باز چه دانی که چیست روز جزا؟'),
(5848, 82, 19, 'روزی که کسی برای کسی هیچ اختیاری ندارد؛ و در آن روز، فرمان از آنِ خداست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای بر کم‌فروشان،'),
(5850, 83, 2, 'که چون از مردم پیمانه ستانند، تمام ستانند؛'),
(5851, 83, 3, 'و چون برای آنان پیمانه یا وزن کنند، به ایشان کم دهند.'),
(5852, 83, 4, 'مگر آنان گمان نمی‌دارند که برانگیخته خواهند شد؟'),
(5853, 83, 5, '[در] روزی بزرگ:'),
(5854, 83, 6, 'روزی که مردم در برابر پروردگار جهانیان به پای ایستند.'),
(5855, 83, 7, 'نه چنین است [که می‌پندارند]، که کارنامه بدکاران در «سجّین» است.'),
(5856, 83, 8, 'و تو چه دانی که «سجّین» چیست؟'),
(5857, 83, 9, 'کتابی است نوشته‌شده.'),
(5858, 83, 10, 'وای بر تکذیب‌کنندگان در آن هنگام:'),
(5859, 83, 11, 'آنان که روز جزا را دروغ می‌پندارند.'),
(5860, 83, 12, 'و جز هر تجاوزپیشه گناهکاری آن را به دروغ نمی‌گیرد.'),
(5861, 83, 13, '[همان که‌] چون آیات ما بر او خوانده شود، گوید: «[اینها] افسانه‌های پیشینیان است.»'),
(5862, 83, 14, 'نه چنین است، بلکه آنچه مرتکب می‌شدند زنگار بر دلهایشان بسته است.'),
(5863, 83, 15, 'زهی پندار، که آنان در آن روز، از پروردگارشان سخت محجوبند.'),
(5864, 83, 16, 'آنگاه به یقین، آنان به جهنم درآیند.'),
(5865, 83, 17, 'سپس [به ایشان‌] گفته خواهد شد: «این همان است که آن را به دروغ می‌گرفتید.»'),
(5866, 83, 18, 'نه چنین است، در حقیقت، کتاب نیکان در «علّیّون» است.'),
(5867, 83, 19, 'و تو چه دانی که «علّیّون» چیست؟'),
(5868, 83, 20, 'کتابی است نوشته‌شده.'),
(5869, 83, 21, 'مقرّبان آن را مشاهده خواهند کرد.'),
(5870, 83, 22, 'براستی نیکوکاران در نعیم [الهی‌] خواهند بود.'),
(5871, 83, 23, 'بر تختها [نشسته‌] می‌نگرند.'),
(5872, 83, 24, 'از چهره‌هایشان طراوت نعمت [بهشت‌] را درمی‌یابی.'),
(5873, 83, 25, 'از باده‌ای مُهر شده نوشانیده شوند.'),
(5874, 83, 26, '[باده‌ای که‌] مُهر آن، مُشک است، و در این [نعمتها] مشتاقان باید بر یکدیگر پیشی گیرند.'),
(5875, 83, 27, 'و ترکیبش از [چشمه‌] «تسنیم» است:'),
(5876, 83, 28, 'چشمه‌ای که مقرّبان [خدا] از آن نوشند.'),
(5877, 83, 29, '[آری، در دنیا] کسانی که گناه می‌کردند، آنان را که ایمان آورده بودند به ریشخند می‌گرفتند.'),
(5878, 83, 30, 'و چون بر ایشان می‌گذشتند، اشاره چشم و ابرو با هم رد و بدل می‌کردند.'),
(5879, 83, 31, 'و هنگامی که نزد خانواده‌[های‌] خود بازمی‌گشتند، به شوخ‌طبعی می‌پرداختند.'),
(5880, 83, 32, 'و چون مؤمنان را می‌دیدند، می‌گفتند: «اینها [جماعتی‌] گمراهند.»'),
(5881, 83, 33, 'و حال آنکه آنان برای بازرسی [کار]شان فرستاده نشده بودند.'),
(5882, 83, 34, 'و[لی‌] امروز، مؤمنانند که بر کافران خنده می‌زنند.'),
(5883, 83, 35, 'بر تختها[ی خود نشسته‌]، نظاره می‌کنند.'),
(5884, 83, 36, '[تا ببینند] آیا کافران به پاداش آنچه می‌کردند رسیده‌اند؟');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آنگاه که آسمان زهم بشکافد،'),
(5886, 84, 2, 'و پروردگارش را فرمان برد و [چنین‌] سزد.'),
(5887, 84, 3, 'و آنگاه که زمین کشیده شود،'),
(5888, 84, 4, 'و آنچه را که در آن است بیرون افکند و تهی شود،'),
(5889, 84, 5, 'و پروردگارش را فرمان برد و [چنین‌] سزد.'),
(5890, 84, 6, 'ای انسان، حقاً که تو به سوی پروردگار خود بسختی در تلاشی، و او را ملاقات خواهی کرد.'),
(5891, 84, 7, 'اما کسی که کارنامه‌اش به دست راستش داده شود،'),
(5892, 84, 8, 'بزودی‌اش حسابی بس آسان کنند،'),
(5893, 84, 9, 'و شادمان به سوی کسانش باز گردد.'),
(5894, 84, 10, 'و اما کسی که کارنامه‌اش از پشت سرش به او داده شود،'),
(5895, 84, 11, 'زودا که هلاک [خویش‌] خواهد،'),
(5896, 84, 12, 'و در آتش افروخته درآید.'),
(5897, 84, 13, 'او در [میان‌] خانواده خود شادمان بود.'),
(5898, 84, 14, 'او می‌پنداشت که هرگز برنخواهد گشت.'),
(5899, 84, 15, 'آری، در حقیقت، پروردگارش به او بینا بود.'),
(5900, 84, 16, 'نه، نه، سوگند به شفق،'),
(5901, 84, 17, 'سوگند به شب و آنچه [شب‌] فروپوشاند،'),
(5902, 84, 18, 'سوگند به ماه چون [بَدر] تمام شود،'),
(5903, 84, 19, 'که قطعاً از حالی به حالی برخواهید نشست.'),
(5904, 84, 20, 'پس چرا آنان باور نمی‌دارند؟'),
(5905, 84, 21, 'و چون بر آنان قرآن تلاوت می‌شود چهره بر خاک نمی‌سایند؟'),
(5906, 84, 22, '[نه!] بلکه آنان که کفر ورزیده‌اند، تکذیب می‌کنند.'),
(5907, 84, 23, 'و خدا به آنچه در سینه دارند داناتر است.'),
(5908, 84, 24, 'پس آنان را از عذابی دردناک خبر ده،'),
(5909, 84, 25, 'مگر کسانی که گرویده و کارهای شایسته کرده‌اند، که آنان را پاداشی بی‌منّت خواهد بود.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمان آکنده ز برج،'),
(5911, 85, 2, 'و به روز موعود،'),
(5912, 85, 3, 'و به گواه و مورد گواهی،'),
(5913, 85, 4, 'مرگ بر آدم‌سوزان خندق.'),
(5914, 85, 5, 'همان آتش مایه‌دار [و انبوه‌].'),
(5915, 85, 6, 'آنگاه که آنان بالای آن [خندق به تماشا] نشسته بودند.'),
(5916, 85, 7, 'و خود بر آنچه بر [سر] مؤمنان می‌آوردند، گواه بودند.'),
(5917, 85, 8, 'و بر آنان عیبی نگرفته بودند جز اینکه به خدای ارجمند ستوده ایمان آورده بودند.'),
(5918, 85, 9, 'همان [خدایی‌] که فرمانروایی آسمانها و زمین از آن اوست و خدا[ست که‌] بر هر چیزی گواه است.'),
(5919, 85, 10, 'کسانی که مردان و زنان مؤمن را آزار کرده و بعد توبه نکرده‌اند، ایشان راست عذاب جهنم، و ایشان راست عذاب سوزان.'),
(5920, 85, 11, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان باغهایی است که از زیر [درختان‌] آن جویها روان است؛ این است [همان‌] رستگاری بزرگ.'),
(5921, 85, 12, 'آری، عقاب پروردگارت سخت سنگین است.'),
(5922, 85, 13, 'هم اوست که [آفرینش را] آغاز می‌کند و بازمی‌گرداند.'),
(5923, 85, 14, 'و اوست آن آمرزنده دوستدار [مؤمنان‌].'),
(5924, 85, 15, 'صاحب ارجمند عرش.'),
(5925, 85, 16, 'هر چه را بخواهد انجام می‌دهد.'),
(5926, 85, 17, 'آیا حدیث [آن‌] سپاهیان:'),
(5927, 85, 18, 'فرعون و ثمود بر تو آمد؟'),
(5928, 85, 19, '[نه،] بلکه آنان که کافر شده‌اند در تکذیب‌اند؛'),
(5929, 85, 20, 'با آنکه خدا از هر سو بر ایشان محیط است.'),
(5930, 85, 21, 'آری، آن قرآنی ارجمند است،'),
(5931, 85, 22, 'که در لوحی محفوظ است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و آن اختر شبگرد.'),
(5933, 86, 2, 'و تو چه دانی که اختر شبگرد چیست؟'),
(5934, 86, 3, 'آن اختر فروزان.'),
(5935, 86, 4, 'هیچ کس نیست مگر اینکه نگاهبانی بر او [گماشته شده‌] است.'),
(5936, 86, 5, 'پس انسان باید بنگرد که از چه آفریده شده است؟'),
(5937, 86, 6, 'از آب جهنده‌ای خلق شده،'),
(5938, 86, 7, '[که‌] از صلب مرد و میان استخوانهای سینه زن بیرون می‌آید.'),
(5939, 86, 8, 'در حقیقت، او [= خدا] بر بازگردانیدن وی بخوبی تواناست.'),
(5940, 86, 9, 'آن روز که رازها [همه‌] فاش شود،'),
(5941, 86, 10, 'پس او را نه نیرویی ماند و نه یاری.'),
(5942, 86, 11, 'سوگند به آسمان بارش‌انگیز،'),
(5943, 86, 12, 'سوگند به زمین شکافدار [آماده کشت‌]،'),
(5944, 86, 13, '[که‌] در حقیقت، قرآن گفتاری قاطع و روشنگر است؛'),
(5945, 86, 14, 'و آن شوخی نیست.'),
(5946, 86, 15, 'آنان دست به نیرنگ می‌زنند.'),
(5947, 86, 16, 'و [من نیز] دست به نیرنگ می‌زنم.'),
(5948, 86, 17, 'پس کافران را مهلت ده، و کمی آنان را به حال خود واگذار.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار والای خود را به پاکی بستای:'),
(5950, 87, 2, 'همان که آفرید و هماهنگی بخشید.'),
(5951, 87, 3, 'و آنکه اندازه‌گیری کرد و راه نمود.'),
(5952, 87, 4, 'و آنکه چمنزار را برآورد؛'),
(5953, 87, 5, 'و پس [از چندی‌] آن را خاشاکی تیره‌گون گردانید.'),
(5954, 87, 6, 'ما بزودی [آیات خود را به وسیله سروش غیبی‌] بر تو خواهیم خواند، تا فراموش نکنی؛'),
(5955, 87, 7, 'جز آنچه خدا خواهد، که او آشکار و آنچه را که نهان است می‌داند.'),
(5956, 87, 8, 'و برای تو آسانترین [راه‌] را فراهم می‌گردانیم.'),
(5957, 87, 9, 'پس پند ده، اگر پند سود بخشد.'),
(5958, 87, 10, 'آن کس که ترسد، بزودی عبرت گیرد.'),
(5959, 87, 11, 'و نگون‌بخت، خود را از آن دور می‌دارد؛'),
(5960, 87, 12, 'همان کس که در آتشی بزرگ در آید؛'),
(5961, 87, 13, 'آنگاه نه در آن می‌میرد و نه زندگانی می‌یابد.'),
(5962, 87, 14, 'رستگار آن کس که خود را پاک گردانید؛'),
(5963, 87, 15, 'و نام پروردگارش را یاد کرد و نماز گزارد.'),
(5964, 87, 16, 'لیکن [شما] زندگی دنیا را بر می‌گزینید؛'),
(5965, 87, 17, 'با آنکه [جهان‌] آخرت نیکوتر و پایدارتر است.'),
(5966, 87, 18, 'قطعاً در صحیفه‌های گذشته این [معنی‌] هست،'),
(5967, 87, 19, 'صحیفه‌های ابراهیم و موسی.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آیا خبر «غاشیه» به تو رسیده است؟'),
(5969, 88, 2, 'در آن روز، چهره‌هایی زبونند،'),
(5970, 88, 3, 'که تلاش کرده، رنج [بیهوده‌] برده‌اند.'),
(5971, 88, 4, '[ناچار] در آتشی سوزان درآیند.'),
(5972, 88, 5, 'از چشمه‌ای داغ نوشانیده شوند.'),
(5973, 88, 6, 'خوراکی جز خار خشک ندارند،'),
(5974, 88, 7, '[که‌] نه فربه کند، و نه گرسنگی را باز دارد.'),
(5975, 88, 8, 'در آن روز، چهره‌هایی شادابند.'),
(5976, 88, 9, 'از کوشش خود خشنودند.'),
(5977, 88, 10, 'در بهشت برین‌اند.'),
(5978, 88, 11, 'سخن بیهوده‌ای در آنجا نشنوند.'),
(5979, 88, 12, 'در آن، چشمه‌ای روان باشد.'),
(5980, 88, 13, 'تختهایی بلند در آنجاست.'),
(5981, 88, 14, 'و قدحهایی نهاده شده.'),
(5982, 88, 15, 'و بالشهایی پهلوی هم [چیده‌].'),
(5983, 88, 16, 'و فرشهایی [زربفت‌] گسترده.'),
(5984, 88, 17, 'آیا به شتر نمی‌نگرند که چگونه آفریده شده؟'),
(5985, 88, 18, 'و به آسمان که چگونه برافراشته شده؟'),
(5986, 88, 19, 'و به کوه‌ها که چگونه برپا داشته شده؟'),
(5987, 88, 20, 'و به زمین که چگونه گسترده شده است؟'),
(5988, 88, 21, 'پس تذکر ده که تو تنها تذکردهنده‌ای.'),
(5989, 88, 22, 'بر آنان تسلّطی نداری،'),
(5990, 88, 23, 'مگر کسی که روی بگرداند و کفر ورزد،'),
(5991, 88, 24, 'که خدا او را به آن عذاب بزرگتر عذاب کند.'),
(5992, 88, 25, 'در حقیقت، بازگشت آنان به سوی ماست،'),
(5993, 88, 26, 'آنگاه حساب [خواستن از] آنان به عهده ماست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپیده‌دم،'),
(5995, 89, 2, 'و به شبهای دهگانه،'),
(5996, 89, 3, 'و به جفت و تاق،'),
(5997, 89, 4, 'و به شب، وقتی سپری شود.'),
(5998, 89, 5, 'آیا در این، برای خردمند [نیاز به‌] سوگندی [دیگر] است؟'),
(5999, 89, 6, 'مگر ندانسته‌ای که پروردگارت با عاد چه کرد؟'),
(6000, 89, 7, 'با عمارات ستون‌دار ارم،'),
(6001, 89, 8, 'که مانندش در شهرها ساخته نشده بود؟'),
(6002, 89, 9, 'و با ثمود، همانان که در درّه، تخته‌سنگها را می‌بریدند؟'),
(6003, 89, 10, 'و با فرعون، صاحب خرگاه‌ها [و بناهای بلند]؟'),
(6004, 89, 11, 'همانان که در شهرها سر به طغیان برداشتند،'),
(6005, 89, 12, 'و در آنها بسیار تبهکاری کردند.'),
(6006, 89, 13, '[تا آنکه‌] پروردگارت بر سر آنان تازیانه عذاب را فرونواخت،'),
(6007, 89, 14, 'زیرا پروردگار تو سخت در کمین است.'),
(6008, 89, 15, 'اما انسان، هنگامی که پروردگارش وی را می‌آزماید، و عزیزش می‌دارد و نعمت فراوان به او می‌دهد، می‌گوید: «پروردگارم مرا گرامی داشته است.»'),
(6009, 89, 16, 'و اما چون وی را می‌آزماید و روزی‌اش را بر او تنگ می‌گرداند، می‌گوید: «پروردگارم مرا خوار کرده است.»'),
(6010, 89, 17, 'ولی نه، بلکه یتیم را نمی‌نوازید؛'),
(6011, 89, 18, 'و بر خوراک‌[دادن‌] بینوا همدیگر را بر نمی‌انگیزید؛'),
(6012, 89, 19, 'و میراث [ضعیفان‌] را چپاولگرانه می‌خورید؛'),
(6013, 89, 20, 'و مال را دوست دارید، دوست داشتنی بسیار.'),
(6014, 89, 21, 'نه چنان است، آنگاه که زمین، سخت در هم کوبیده شود،'),
(6015, 89, 22, 'و [فرمان‌] پروردگارت و فرشته‌[ها] صف‌درصف آیند،'),
(6016, 89, 23, 'و جهنم را در آن روز [حاضر] آورند، آن روز است که انسان پند گیرد؛ و[لی‌] کجا او را جای پندگرفتن باشد؟'),
(6017, 89, 24, 'گوید: «کاش برای زندگانی خود [چیزی‌] پیش فرستاده بودم.»'),
(6018, 89, 25, 'پس در آن روز هیچ کس چون عذاب‌کردن او، عذاب نکند.'),
(6019, 89, 26, 'و هیچ کس چون دربندکشیدن او، دربند نکشد.'),
(6020, 89, 27, 'ای نفس مطمئنّه،'),
(6021, 89, 28, 'خشنود و خداپسند به سوی پروردگارت بازگرد،'),
(6022, 89, 29, 'و در میان بندگان من درآی،'),
(6023, 89, 30, 'و در بهشت من داخل شو.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'سوگند به این شهر،'),
(6025, 90, 2, 'و حال آنکه تو در این شهر جای داری؛'),
(6026, 90, 3, 'سوگند به پدری [چنان‌] و آن کسی را که به وجود آورد؛'),
(6027, 90, 4, 'براستی که انسان را در رنج آفریده‌ایم.'),
(6028, 90, 5, 'آیا پندارد که هیچ کس هرگز بر او دست نتواند یافت؟'),
(6029, 90, 6, 'گوید: «مال فراوانی تباه کردم.»'),
(6030, 90, 7, 'آیا پندارد که هیچ کس او را ندیده است؟'),
(6031, 90, 8, 'آیا دو چشمش نداده‌ایم؟'),
(6032, 90, 9, 'و زبانی و دو لب.'),
(6033, 90, 10, 'و هر دو راه [خیر و شر] را بدو نمودیم.'),
(6034, 90, 11, 'و[لی‌] نخواست از گردنه [عاقبت‌نگری‌] بالا رود!'),
(6035, 90, 12, 'و تو چه دانی که آن گردنه [سخت‌] چیست؟'),
(6036, 90, 13, 'بنده‌ای را آزادکردن،'),
(6037, 90, 14, 'یا در روز گرسنگی، طعام‌دادن:'),
(6038, 90, 15, 'به یتیمی خویشاوند،'),
(6039, 90, 16, 'یا بینوایی خاک‌نشین.'),
(6040, 90, 17, 'علاوه بر این از زمره کسانی باشد که گرویده و یکدیگر را به شکیبایی و مهربانی سفارش کرده‌اند؛'),
(6041, 90, 18, 'اینانند خجستگان.'),
(6042, 90, 19, 'و کسانی که به انکار نشانه‌های ما پرداخته‌اند، آنانند ناخجستگان شوم.'),
(6043, 90, 20, 'بر آنان آتشی سرپوشیده احاطه دارد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشید و تابندگی‌اش،'),
(6045, 91, 2, 'سوگند به مه چون پی [خورشید] رود.'),
(6046, 91, 3, 'سوگند به روز چون [زمین را] روشن گرداند،'),
(6047, 91, 4, 'سوگند به شب چو پرده بر آن پوشد،'),
(6048, 91, 5, 'سوگند به آسمان و آن کس که آن را برافراشت،'),
(6049, 91, 6, 'سوگند به زمین و آن کس که آن را گسترد،'),
(6050, 91, 7, 'سوگند به نفس و آن کس که آن را درست کرد؛'),
(6051, 91, 8, 'سپس پلیدکاری و پرهیزگاری‌اش را به آن الهام کرد،'),
(6052, 91, 9, 'که هر کس آن را پاک گردانید، قطعاً رستگار شد،'),
(6053, 91, 10, 'و هر که آلوده‌اش ساخت، قطعاً درباخت.'),
(6054, 91, 11, '[قوم‌] ثمود به سبب طغیان خود به تکذیب پرداختند.'),
(6055, 91, 12, 'آنگاه که شقی‌ترینشان بر[پا] خاست.'),
(6056, 91, 13, 'پس فرستاده خدا به آنان گفت: «زنهار! ماده‌شتر خدا و [نوبت‌] آب‌خوردنش را [حرمت نهید]».'),
(6057, 91, 14, 'و[لی‌] دروغزنش خواندند و آن [ماده‌شتر] را پی کردند، و پروردگارشان به [سزای‌] گناهشان بر سرشان عذاب آورد و آنان را با خاک یکسان کرد.'),
(6058, 91, 15, 'و از پیامد کار خویش، بیمی به خود راه نداد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب چون پرده افکند،'),
(6060, 92, 2, 'سوگند به روز چون جلوه‌گری آغازد،'),
(6061, 92, 3, 'و [سوگند به‌] آنکه نر و ماده را آفرید،'),
(6062, 92, 4, 'که همانا تلاش شما پراکنده است.'),
(6063, 92, 5, 'اما آنکه [حق خدا را] داد و پروا داشت،'),
(6064, 92, 6, 'و [پاداش‌] نیکوتر را تصدیق کرد،'),
(6065, 92, 7, 'بزودی راه آسانی پیش پای او خواهیم گذاشت.'),
(6066, 92, 8, 'و اما آنکه بخل ورزید و خود را بی‌نیاز دید،'),
(6067, 92, 9, 'و [پاداش‌] نیکوتر را به دروغ گرفت،'),
(6068, 92, 10, 'بزودی راه دشواری به او خواهیم نمود.'),
(6069, 92, 11, 'و چون هلاک شد، [دیگر] مال او به کارش نمی‌آید.'),
(6070, 92, 12, 'همانا هدایت بر ماست.'),
(6071, 92, 13, 'و در حقیقت، دنیا و آخرت از آن ماست.'),
(6072, 92, 14, 'پس شما را به آتشی که زبانه می‌کشد هشدار دادم.'),
(6073, 92, 15, 'جز نگون‌بخت‌تر[ین مردم‌] در آن درنیاید:'),
(6074, 92, 16, 'همان که تکذیب کرد و رخ برتافت.'),
(6075, 92, 17, 'و پاک‌رفتارتر[ین مردم‌] از آن دور داشته خواهد شد:'),
(6076, 92, 18, 'همان که مال خود را می‌دهد [برای آنکه‌] پاک شود،'),
(6077, 92, 19, 'و هیچ کس را به قصد پاداش‌یافتن نعمت نمی‌بخشد،'),
(6078, 92, 20, 'جز خواستن رضای پروردگارش که بسی برتر است [منظوری ندارد].'),
(6079, 92, 21, 'و قطعاً بزودی خشنود خواهد شد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به روشنایی روز،'),
(6081, 93, 2, 'سوگند به شب چون آرام گیرد،'),
(6082, 93, 3, '[که‌] پروردگارت تو را وانگذاشته، و دشمن نداشته است.'),
(6083, 93, 4, 'و قطعاً آخرت برای تو از دنیا نیکوتر خواهد بود.'),
(6084, 93, 5, 'و بزودی پروردگارت تو را عطا خواهد داد، تا خرسند گردی.'),
(6085, 93, 6, 'مگر نه تو را یتیم یافت، پس پناه داد؟'),
(6086, 93, 7, 'و تو را سرگشته یافت، پس هدایت کرد؟'),
(6087, 93, 8, 'و تو را تنگدست یافت و بی‌نیاز گردانید؟'),
(6088, 93, 9, 'و اما [تو نیز به پاس نعمت ما] یتیم را میازار،'),
(6089, 93, 10, 'و گدا را مران،'),
(6090, 93, 11, 'و از نعمت پروردگار خویش [با مردم‌] سخن گوی.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آیا برای تو سینه‌ات را نگشاده‌ایم؟'),
(6092, 94, 2, 'و بار گرانت را از [دوش‌] تو برنداشتیم؟'),
(6093, 94, 3, '[باری‌] که [گویی‌] پشت تو را شکست.'),
(6094, 94, 4, 'و نامت را برای تو بلند گردانیدیم.'),
(6095, 94, 5, 'پس [بدان که‌] با دشواری، آسانی است.'),
(6096, 94, 6, 'آری، با دشواری، آسانی است.'),
(6097, 94, 7, 'پس چون فراغت یافتی، به طاعت درکوش؛'),
(6098, 94, 8, 'و با اشتیاق، به سوی پروردگارت روی آور.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به [کوه‌] تین و زیتون،'),
(6100, 95, 2, 'و طور سینا،'),
(6101, 95, 3, 'و این شهر امن [و امان‌]،'),
(6102, 95, 4, '[که‌] براستی انسان را در نیکوترین اعتدال آفریدیم.'),
(6103, 95, 5, 'سپس او را به پست‌ترین [مراتب‌] پستی بازگردانیدیم؛'),
(6104, 95, 6, 'مگر کسانی را که گرویده و کارهای شایسته کرده‌اند، که پاداشی بی‌منّت خواهند داشت.'),
(6105, 95, 7, 'پس چه چیز، تو را بعد [از این‌] به تکذیب جزا وامی‌دارد؟'),
(6106, 95, 8, 'آیا خدا نیکوترین داوران نیست؟');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت که آفرید.'),
(6108, 96, 2, 'انسان را از عَلَق آفرید.'),
(6109, 96, 3, 'بخوان، و پروردگار تو کریمترین [کریمان‌] است.'),
(6110, 96, 4, 'همان کس که به وسیله قلم آموخت.'),
(6111, 96, 5, 'آنچه را که انسان نمی‌دانست [بتدریج به او] آموخت.'),
(6112, 96, 6, 'حقاً که انسان سرکشی می‌کند،'),
(6113, 96, 7, 'همین که خود را بی‌نیاز پندارد.'),
(6114, 96, 8, 'در حقیقت، بازگشت به سوی پروردگار توست.'),
(6115, 96, 9, 'آیا دیدی آن کس را که باز می‌داشت،'),
(6116, 96, 10, 'بنده‌ای را آنگاه که نماز می‌گزارد؟'),
(6117, 96, 11, 'چه پنداری اگر او بر هدایت باشد،'),
(6118, 96, 12, 'یا به پرهیزگاری وادارد [برای او بهتر نیست‌]؟'),
(6119, 96, 13, '[و باز] آیا چه پنداری [که‌] اگر او به تکذیب پردازد و روی برگرداند [چه کیفری در پیش دارد]؟'),
(6120, 96, 14, 'مگر ندانسته که خدا می‌بیند؟'),
(6121, 96, 15, 'زنهار، اگر باز نایستد، موی پیشانی [او] را سخت بگیریم؛'),
(6122, 96, 16, '[همان‌] موی پیشانی دروغزن گناه‌پیشه را.'),
(6123, 96, 17, '[بگو] تا گروه خود را بخواند.'),
(6124, 96, 18, 'بزودی آتشبانان را فرا خوانیم.'),
(6125, 96, 19, 'زنهار! فرمانش مَبَر، و سجده کن، و خود را [به خدا] نزدیک گردان.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما [قرآن را] در شب قدر نازل کردیم.'),
(6127, 97, 2, 'و از شب قدر، چه آگاهت کرد.'),
(6128, 97, 3, 'شب قدر از هزار ماه ارجمندتر است.'),
(6129, 97, 4, 'در آن [شب‌] فرشتگان، با روح، به فرمان پروردگارشان، برای هر کاری [که مقرّر شده است‌] فرود آیند؛'),
(6130, 97, 5, '[آن شب‌] تا دمِ صبح، صلح و سلام است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کافران اهل کتاب و مشرکان، دست‌بردار نبودند تا دلیلی آشکار بر ایشان آید:'),
(6132, 98, 2, 'فرستاده‌ای از جانب خدا که [بر آنان‌] صحیفه‌هایی پاک را تلاوت کند،'),
(6133, 98, 3, 'که در آنها نوشته‌های استوار است.'),
(6134, 98, 4, 'و اهل کتاب دستخوش پراکندگی نشدند، مگر پس از آنکه برهان آشکار برای آنان آمد.'),
(6135, 98, 5, 'و فرمان نیافته بودند جز اینکه خدا را بپرستند، و در حالی که به توحید گراییده‌اند، دین [خود] را برای او خالص گردانند، و نماز برپا دارند و زکات بدهند، و دین [ثابت و] پایدار همین است.'),
(6136, 98, 6, 'کسانی از اهل کتاب که کفر ورزیده‌اند و [نیز] مشرکان در آتش دوزخند، [و] در آن همواره می‌مانند؛ اینانند که بدترین آفریدگانند.'),
(6137, 98, 7, 'در حقیقت کسانی که گرویده و کارهای شایسته کرده‌اند، آنانند که بهترین آفریدگانند.'),
(6138, 98, 8, 'پاداش آنان نزد پروردگارشان باغهای همیشگی است که از زیر [درختان‌] آن، نهرها روان است، جاودانه در آن «همی» مانند؛ خدا از آنان خشنود است و [آنان نیز] از او خشنود، این [پاداش‌] برای کسی است که از پروردگارش بترسد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'آنگاه که زمین به لرزش [شدید] خود لرزانیده شود،'),
(6140, 99, 2, 'و زمین بارهای سنگین خود را برون افکند،'),
(6141, 99, 3, 'و انسان گوید: «[زمین‌] را چه شده است؟»'),
(6142, 99, 4, 'آن روز است که [زمین‌] خبرهای خود را باز گوید.'),
(6143, 99, 5, '[همان گونه‌] که پروردگارت بدان وحی کرده است.'),
(6144, 99, 6, 'آن روز، مردم [به حال‌] پراکنده برآیند تا [نتیجه‌] کارهایشان به آنان نشان داده شود.'),
(6145, 99, 7, 'پس هر که هموزن ذرّه‌ای نیکی کند [نتیجه‌] آن را خواهد دید.'),
(6146, 99, 8, 'و هر که هموزن ذرّه‌ای بدی کند [نتیجه‌] آن را خواهد دید.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به مادیانهائی که با همهمه تازانند و با سم‌[های‌] خود از سنگ آتش می‌جهانند!'),
(6148, 100, 2, 'و برق [از سنگ‌] همی جهانند،'),
(6149, 100, 3, 'و صبحگاهان هجوم آرند،'),
(6150, 100, 4, 'و با آن [یورش‌]، گردی برانگیزند،'),
(6151, 100, 5, 'و بدان [هجوم‌]، در دل گروهی درآیند،'),
(6152, 100, 6, 'که انسان نسبت به پروردگارش سخت ناسپاس است،'),
(6153, 100, 7, 'و او خود بر این [امر]، نیک گواه است.'),
(6154, 100, 8, 'و راستی او سخت شیفته مال است.'),
(6155, 100, 9, 'مگر نمی‌داند که چون آنچه در گورهاست بیرون ریخته گردد،'),
(6156, 100, 10, 'و آنچه در سینه‌هاست فاش شود،'),
(6157, 100, 11, 'در چنان روزی پروردگارشان به [حال‌] ایشان نیک آگاه است؟');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کوبنده،'),
(6159, 101, 2, 'چیست کوبنده؟'),
(6160, 101, 3, 'و تو چه دانی که کوبنده چیست؟'),
(6161, 101, 4, 'روزی که مردم چون پروانه‌[های‌] پراکنده گردند،'),
(6162, 101, 5, 'و کوه‌ها مانند پشم زده‌شده رنگین شود.'),
(6163, 101, 6, 'اما هر که سنجیده‌هایش سنگین برآید،'),
(6164, 101, 7, 'پس وی در زندگی خوشی خواهد بود!'),
(6165, 101, 8, 'و اما هر که سنجیده‌هایش سبک بر آید،'),
(6166, 101, 9, 'پس جایش «هاویه» باشد.'),
(6167, 101, 10, 'و تو چه دانی که آن چیست؟'),
(6168, 101, 11, 'آتشی است سوزنده.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تفاخر به بیشترداشتن، شما را غافل داشت.'),
(6170, 102, 2, 'تا کارتان [و پایتان‌] به گورستان رسید.'),
(6171, 102, 3, 'نه چنین است، زودا که بدانید.'),
(6172, 102, 4, 'باز هم نه چنین است، زودا که بدانید.'),
(6173, 102, 5, 'هرگز چنین نیست، اگر علم‌الیقین داشتید!'),
(6174, 102, 6, 'به یقین دوزخ را می‌بینید.'),
(6175, 102, 7, 'سپس آن را قطعاً به عین‌الیقین درمی‌یابید.'),
(6176, 102, 8, 'سپس در همان روز است که از نعمت [روی زمین‌] پرسیده خواهید شد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به عصر [غلبه حق بر باطل‌]،'),
(6178, 103, 2, 'که واقعاً انسان دستخوش زیان است؛'),
(6179, 103, 3, 'مگر کسانی که گرویده و کارهای شایسته کرده و همدیگر را به حق سفارش و به شکیبایی توصیه کرده‌اند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر بدگوی عیبجویی،'),
(6181, 104, 2, 'که مالی گرد آورد و برشمردش.'),
(6182, 104, 3, 'پندارد که مالش او را جاوید کرده،'),
(6183, 104, 4, 'ولی نه! قطعاً در آتش خردکننده فرو افکنده خواهد شد.'),
(6184, 104, 5, 'و تو چه دانی که آن آتش خردکننده چیست؟'),
(6185, 104, 6, 'آتش افروخته خدا[یی‌] است.'),
(6186, 104, 7, '[آتشی‌] که به دلها می‌رسد.'),
(6187, 104, 8, 'آنان را در میان فرامی‌گیرد.'),
(6188, 104, 9, '[آتشی که‌] در ستونهایی دراز است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'مگر ندیدی پروردگارت با پیلداران چه کرد؟'),
(6190, 105, 2, 'آیا نیرنگشان را بر باد نداد؟'),
(6191, 105, 3, 'و بر سر آنها، دسته دسته پرندگانی «اَبابیل» فرستاد.'),
(6192, 105, 4, '[که‌] بر آنان سنگهایی از گل [سخت‌] می‌افکندند.'),
(6193, 105, 5, 'و [سرانجام، خدا] آنان را مانند کاه جویده‌شده گردانید.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'برای الفت‌دادن قریش،'),
(6195, 106, 2, 'الفتشان هنگام کوچ زمستان و تابستان، [خدا پیلداران را نابود کرد.]'),
(6196, 106, 3, 'پس باید خداوندِ این خانه را بپرستند؛'),
(6197, 106, 4, 'همان [خدایی‌] که در گرسنگی غذایشان داد، و از بیم [دشمن‌] آسوده‌خاطرشان کرد.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا کسی را که [روز] جزا را دروغ می‌خواند، دیدی؟'),
(6199, 107, 2, 'این همان کس است که یتیم را بسختی می‌راند،'),
(6200, 107, 3, 'و به خوراک‌دادن بینوا ترغیب نمی‌کند.'),
(6201, 107, 4, 'پس وای بر نمازگزارانی'),
(6202, 107, 5, 'که از نمازشان غافلند،'),
(6203, 107, 6, 'آنان که ریا می‌کنند،'),
(6204, 107, 7, 'و از [دادن‌] زکات [و وسایل و مایحتاج خانه‌] خودداری می‌ورزند.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'ما تو را [چشمه‌] کوثر دادیم،'),
(6206, 108, 2, 'پس برای پروردگارت نماز گزار و قربانی کن.'),
(6207, 108, 3, 'دشمنت خود بی‌تبار خواهد بود.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو: «ای کافران،'),
(6209, 109, 2, 'آنچه می‌پرستید، نمی‌پرستم.'),
(6210, 109, 3, 'و آنچه می‌پرستم، شما نمی‌پرستید.'),
(6211, 109, 4, 'و نه آنچه پرستیدید من می‌پرستم.'),
(6212, 109, 5, 'و نه آنچه می‌پرستم شما می‌پرستید.'),
(6213, 109, 6, 'دین شما برای خودتان، و دین من برای خودم.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'چون یاری خدا و پیروزی فرا رسد،'),
(6215, 110, 2, 'و ببینی که مردم دسته‌دسته در دین خدا درآیند،'),
(6216, 110, 3, 'پس به ستایش پروردگارت نیایشگر باش و از او آمرزش خواه، که وی همواره توبه‌پذیر است.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'بریده باد دو دست ابولهب، و مرگ بر او باد.'),
(6218, 111, 2, 'دارایی او و آنچه اندوخت، سودش نکرد.'),
(6219, 111, 3, 'بزودی در آتشی پرزبانه درآید.'),
(6220, 111, 4, 'و زنش، آن هیمه‌کش [آتش فروز]،'),
(6221, 111, 5, 'بر گردنش طنابی از لیف خرماست.');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: «اوست خدای یگانه،'),
(6223, 112, 2, 'خدای صمد [ثابت - متعالی‌]،'),
(6224, 112, 3, '[کسی را] نزاده، و زاده نشده است،'),
(6225, 112, 4, 'و هیچ کس او را همتا نیست.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: «پناه می‌برم به پروردگار سپیده دم،'),
(6227, 113, 2, 'از شرّ آنچه آفریده،'),
(6228, 113, 3, 'و از شرّ تاریکی چون فراگیرد،'),
(6229, 113, 4, 'و از شرّ دمندگان افسون در گره‌ها،'),
(6230, 113, 5, 'و از شرّ [هر] حسود، آنگاه که حسد ورزد.»');
INSERT INTO `fa_fooladvand` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: «پناه می‌برم به پروردگار مردم،'),
(6232, 114, 2, 'پادشاه مردم،'),
(6233, 114, 3, 'معبود مردم،'),
(6234, 114, 4, 'از شرّ وسوسه‌گر نهانی؛'),
(6235, 114, 5, 'آن کس که در سینه‌های مردم وسوسه می‌کند،'),
(6236, 114, 6, 'چه از جنّ و [چه از] انس.»');
