INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداوند بخشنده‌ی مهربان.'),
(2, 1, 2, 'سپاس و ستایش مخصوص خداوندی است که پروردگار جهانیان است.'),
(3, 1, 3, '[خدایی که] بخشنده و مهربان است.'),
(4, 1, 4, '[خدایی که] مالک روز جزاست.'),
(5, 1, 5, '[خدایا!] تنها تو را می‌پرستیم و تنها از تو یاری می‌جوییم.'),
(6, 1, 6, '[خداوندا!] ما را به راه راست هدایت فرما.'),
(7, 1, 7, 'راه کسانی که به آنان نعمت دادی، نه کسانی که مورد غضب واقع شدند و نه گمراهان.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف، لام، میم.'),
(9, 2, 2, 'آن کتاب [با عظمت که] در [حقّانیت] آن هیچ تردیدی راه ندارد، راهنمای پرهیزکاران است.'),
(10, 2, 3, '[متّقین] کسانی هستند که به غیب ایمان دارند و نماز را به پای می‌دارند و از آنچه به آنان روزی داده‌ایم، انفاق می‌کنند.'),
(11, 2, 4, 'و آنان به آنچه بر تو نازل شده و آنچه پیش از تو [بر پیامبران] نازل گردیده، ایمان دارند و هم آنان به آخرت [نیز] یقین دارند.'),
(12, 2, 5, 'تنها آنان از جانب پروردگارشان بر هدایتند و آنان همان رستگاران هستند.'),
(13, 2, 6, 'کسانی که کفر ورزیده‌اند، برای آنها یکسان است که هشدارشان بدهی یا هشدارشان ندهی، آنان ایمان نخواهند آورد.'),
(14, 2, 7, 'خداوند بر دل‌ها و بر گوش آنان مهر زده است و در برابر چشمانشان پرده‌ای است و عذاب بزرگی در انتظار آنهاست.'),
(15, 2, 8, 'و گروهی از مردم کسانی هستند که می‌گویند: «به خدا و روز قیامت ایمان آورده‌ایم!» در حالی که ایمان نیاورده‌اند.'),
(16, 2, 9, 'آنان با خداوند و مؤمنان نیرنگ می‌کنند در حالی که جز خودشان را فریب نمی‌دهند، امّا نمی‌فهمند!'),
(17, 2, 10, 'در دل‌های آنان یک نوع بیماری است؛ خداوند بر بیماری آنان افزوده؛ و به خاطر دروغ‌هایی که می‌گفتند، عذاب دردناکی در انتظار آنهاست.'),
(18, 2, 11, 'و هنگامی که به آنان گفته شود: «در زمین فساد نکنید!» می‌گویند: «ما فقط اصلاح کننده‌ایم!»'),
(19, 2, 12, 'آگاه باشید! اینها همان مفسدانند؛ ولی نمی‌فهمند.'),
(20, 2, 13, 'و چون به آنان گفته شود: «شما نیز همان‌گونه که [سایر] مردم ایمان آورده‌اند، ایمان آورید!» می‌گویند: «آیا ما نیز همانند ساده‌اندیشان و سبک‌مغزان، ایمان بیاوریم؟!» آگاه باشید! آنان خود بی‌خردند، ولی نمی‌دانند.'),
(21, 2, 14, 'و چون با اهل ایمان ملاقات کنند، می‌گویند: «ما [نیز همانند شما] ایمان آورده‌ایم»؛ ولی هرگاه با [همفکرانِ] شیطان‌صفت خود تنها شوند، می‌گویند: «ما با شماییم، ما فقط [اهل ایمان را] مسخره می‌کنیم».'),
(22, 2, 15, 'خداوند آنان را به استهزا می‌گیرد و آنان را در طغیانشان مهلت می‌دهد تا سرگردان شوند.'),
(23, 2, 16, 'آنان کسانی هستند که به بهای [از دست دادن] هدایت، خریدار ضلالت و گمراهی شدند؛ امّا این داد و ستد، سودشان نبخشید و در شمار هدایت‌یافتگان درنیامدند.'),
(24, 2, 17, 'مَثل آنان (منافقان)، مَثل کسی است که آتشی افروخته [تا از تاریکی، رهایی یابد]، پس چون آتش، اطراف خود را روشن ساخت، خداوند روشنایی و نورشان را ببرد و آنان را در تاریکی‌هایی که [هیچ] نمی‌بینند، رهایشان کند.'),
(25, 2, 18, 'آنان [از شنیدن حقّ] کر و [از گفتن حقّ] گنگ و [از دیدن حقّ] کورند، پس ایشان [به سوی حقّ] بازنمی‌گردند.'),
(26, 2, 19, 'یا چون [گرفتاران در] بارانی تند از آسمانند که در شبی تاریک همراه با رعد و برق [در بیابان] ببارد، [آنان] از ترس صاعقه‌ها و بیم مرگ، انگشتان خود را در گوش‌هایشان قرار می‌دهند، و[لی] خداوند بر کافران احاطه دارد.'),
(27, 2, 20, 'نزدیک است که [روشنایی خیره‌کننده‌ی] برق، نور چشمانشان را برباید. هرگاه که [برق آسمان در آن صحرای تاریک و بارانی] برای آنان بدرخشد، [چند گامی] در پرتو آن راه می‌روند؛ ولی همین که تاریکی، ایشان را فراگیرد، می‌ایستند و اگر خداوند بخواهد، شنوایی و بینایی آنان را [از بین] می‌برد، همانا خداوند بر هر چیزی تواناست.'),
(28, 2, 21, 'ای مردم! پروردگارتان را که شما و پیشینیان شما را آفرید، پرستش کنید تا اهل تقوا شوید.'),
(29, 2, 22, 'آن [خداوندی] که زمین را برای شما فرشی [گسترده] و آسمان را بنایی [افراشته] قرار داد و از آسمان، آبی فروفرستاد و به وسیله‌ی آن از میوه‌ها، روزی برای شما بیرون آورد، پس برای خداوند شریک و همتایی قرار ندهید با آن که خودتان می‌دانید [که هیچ یک از آنها، نه شما را آفریده‌اند و نه روزی می‌دهند].'),
(30, 2, 23, 'و اگر در آنچه بر بنده‌ی خود [از قرآن] نازل کرده‌ایم، تردید دارید، یک سوره همانند آن بیاورید و گواهان خود را غیر از خداوند، بر این کار دعوت کنید، اگر راست می‌گویید!'),
(31, 2, 24, 'پس اگر این کار را نکردید، که هرگز نتوانید کرد، از آتشی بترسید که هیزمش مردم [گناهکار] و سنگ‌ها هستند و برای کافران مهیا شده است.'),
(32, 2, 25, 'و به کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، مژده بده که برایشان باغ‌هایی بهشتی است که نهرها از پای [درختان] آن جاری است، هرگاه میوه‌ای از آن [باغ‌ها] روزی آنان شود، می‌گویند: «این همان است که قبلاً نیز به ما روزی داده شده بود.» در حالی که همانند آن نعمت‌ها به ایشان داده شده است، [نه خود آنها] و برای آنان در بهشت همسرانی پاک و پاکیزه است و در آنجا جاودانه‌اند.'),
(33, 2, 26, 'خداوند پروا ندارد از این که به پشه‌ای یا فراتر از آن [در کوچکی] مثال بزند، پس آنهایی که ایمان دارند، می‌دانند که آن [مثال، گویای] حقیقتی است از طرف پروردگارشان، ولی کسانی که کفر ورزیده‌اند، [بهانه‌جویی کرده،] می‌گویند: «خداوند از این مثل چه منظوری داشته است؟» [آری،] خداوند بسیاری را بدان [مثال] گمراه و بسیاری را بدان هدایت می‌فرماید. [امّا آگاه باشید که] خداوند جز افراد فاسق را بدان گمراه نمی‌کند.'),
(34, 2, 27, '[فاسقان] کسانی هستند که پیمان خدا را پس از آن که محکم بستند، می‌شکنند، و پیوندهایی را که خدا دستور داده برقرار سازند، قطع می‌نمایند، و در زمین فساد می‌کنند، قطعاً آنان زیانکارانند.'),
(35, 2, 28, 'چگونه به خداوند کافر می‌شوید، در حالی که شما [اجسام بی‌روح و] مردگانی بودید که او به شما زندگی بخشید، سپس شما را می‌میراند و بار دیگر شما را زنده می‌کند، سپس به سوی او بازگردانده می‌شوید.'),
(36, 2, 29, 'اوست آن کسی که آنچه در زمین است، همه را برای شما آفرید، سپس به آفرینش آسمان پرداخت و آنها را به صورت هفت آسمان، استوار نمود و او بر هر چیزی آگاه است.'),
(37, 2, 30, 'و [به یاد آور] هنگامی که پروردگارت به فرشتگان گفت: «من بر آنم که در زمین جانشینی قرار دهم.» فرشتگان گفتند: «آیا کسی را در زمین قرار می‌دهی که در آن فساد کند و خون‌ها بریزد؟ در حالی که ما با حمد و ستایش، تو را تنزیه و تقدیس می‌کنیم.» خداوند فرمود: «به راستی من حقایقی را می‌دانم که شما نمی‌دانید.»'),
(38, 2, 31, 'و خداوند همه‌ی اسماء (حقایق و اسرار هستی) را به آدم آموخت، سپس آنها را به فرشتگان عرضه داشت و فرمود: «اگر راست می‌گویید، [و از آدم شایسته‌ترید،] از اسامی اینها به من خبر دهید.»'),
(39, 2, 32, 'فرشتگان گفتند: «پروردگارا! تو پاک و منزهی! ما چیزی جز آنچه تو به ما آموخته‌ای نمی‌دانیم، همانا تو دانای حکیمی!»'),
(40, 2, 33, '[خداوند] فرمود: «ای آدم! فرشتگان را از نام‌های آنان خبر ده!» پس چون آدم آنها را از نام‌هایشان خبر داد، خداوند فرمود: «آیا به شما نگفتم که اسرار آسمان‌ها و زمین را می‌دانم و آنچه را آشکار می‌کنید و آنچه را پنهان می‌داشتید، می‌دانم؟»'),
(41, 2, 34, 'و هنگامی که به فرشتگان گفتیم: «برای آدم سجده کنید.» همگی سجده کردند. جز ابلیس که سر باز زد و تکبّر کرد، و از کافران گردید.'),
(42, 2, 35, 'و گفتیم: «ای آدم! تو با همسرت در این باغ سکونت کن و از آن هر چه می‌خواهید به فراوانی و گوارایی بخورید، امّا به این درخت نزدیک نشوید که از ستمگران خواهید شد.»'),
(43, 2, 36, 'پس شیطان آن دو را به لغزش انداخت و آنان را از باغی که در آن بودند، بیرون کرد و [در این هنگام به آنها] گفتیم: «[از این جایگاه] فرود آیید! در حالی که دشمن یکدیگر خواهید بود و برای شما در زمین قرارگاه و تا وقتی معین بهره و برخورداری خواهد بود.»'),
(44, 2, 37, 'پس آدم از سوی پروردگارش کلماتی دریافت نمود، [و توبه کرد،] پس خداوند لطف خود را بر او بازگرداند. همانا او توبه‌پذیر مهربان است.'),
(45, 2, 38, 'گفتیم: «همگی از آن [جایگاه] فرود آیید، و آن‌گاه که هدایتی از طرف من برای شما آمد، پس کسانی که از هدایت من پیروی کنند، نه برایشان بیمی است و نه غمگین خواهند شد.»'),
(46, 2, 39, 'و[لی] کسانی که کافر شدند و آیات ما را تکذیب کردند، اهل دوزخند و جاودانه در آن خواهند بود.'),
(47, 2, 40, 'ای فرزندان اسرائیل! نعمتم را که به شما ارزانی داشتم، به خاطر بیاورید و به پیمانم [که بسته‌اید] وفا کنید، تا من [نیز] به پیمان شما وفا کنم و تنها از من پروا داشته باشید.'),
(48, 2, 41, 'و به آنچه [از قرآن] نازل کردم، ایمان بیاورید که آنچه را در کتاب شما [از نشانه‌های آن] آمده است، تصدیق می‌کند و نخستین کافر به آن نباشید و آیات مرا به بهای ناچیز نفروشید و تنها از من پروا کنید.'),
(49, 2, 42, 'و حقّ را با باطل نپوشانید و حقیقت را با این که می‌دانید، کتمان نکنید.'),
(50, 2, 43, 'و نماز را به پا دارید و زکات را بپردازید و همراه با رکوع‌کنندگان، رکوع نمایید.'),
(51, 2, 44, 'آیا مردم را به نیکی دعوت می‌کنید و خودتان را فراموش می‌نمایید؟ با این که شما کتاب [آسمانی] را می‌خوانید. آیا هیچ فکر نمی‌کنید؟'),
(52, 2, 45, 'و از صبر و نماز یاری جویید، [و با شکیبایی و توجه به پروردگار، نیرو بگیرید،] و این کار جز برای خاشعان، دشوار و سنگین است.'),
(53, 2, 46, 'آنان که می‌دانند به پیشگاه پروردگارشان حاضر خواهند شد و به سوی او بازمی‌گردند.'),
(54, 2, 47, 'ای بنی‌اسرائیل! نعمتم را که به شما ارزانی داشتم، به خاطر بیاورید، و [نیز به یاد آورید که] من، شما را بر جهانیان، برتری بخشیدم.'),
(55, 2, 48, 'و از روزی بترسید که کسی مجازات دیگری را نمی‌پذیرد و شفاعتی از کسی پذیرفته نمی‌شود و غرامتی از او قبول نخواهد شد، و [به هیچ صورت] یاری نخواهند شد.'),
(56, 2, 49, 'و [نیز به خاطر بیاورید] آن زمان که شما را از چنگال فرعونیان رهایی بخشیدیم، که همواره شما را به بدترین صورت آزار می‌دادند، پسران شما را سر می‌بریدند و زنان شما را [برای کنیزی] زنده نگه می‌داشتند و در این امر، برای شما آزمایشی بزرگ و سخت از طرف پروردگارتان بود.'),
(57, 2, 50, 'و هنگامی که دریا را برای شما شکافتیم و شما را نجات دادیم و فرعونیان را غرق کردیم، در حالی که شما تماشا می‌کردید.'),
(58, 2, 51, 'و هنگامی که با موسی چهل شب وعده گذاردیم و شما پس از [آمدن] او [به میقات،] گوساله را [معبود خود] گرفتید، در حالی که ستمکار بودید.'),
(59, 2, 52, 'آن‌گاه پس از آن [انحراف] از شما درگذشتیم تا شاید شکر [این نعمت را] به جای آورید.'),
(60, 2, 53, 'و هنگامی که به موسی کتاب [تورات] و وسیله‌ی تشخیص [حقّ از باطل] دادیم، تا شاید شما هدایت شوید.'),
(61, 2, 54, 'و زمانی که موسی به قوم خود گفت: «ای قوم من! شما با [به پرستش] گرفتن گوساله، به خود ستم کردید. پس به سوی خالق خود توبه کنید و بازگردید و [افرادی از] خودتان را [که گوساله را به پرستش گرفتند،] به قتل برسانید، این کار برای شما در پیشگاه پروردگارتان بهتر است.» پس خداوند توبه‌ی شما را پذیرفت، زیرا او توبه‌پذیر مهربان است.'),
(62, 2, 55, 'و هنگامی که گفتید: «ای موسی! ما هرگز به تو ایمان نمی‌آوریم، مگر این که خدا را آشکارا [به چشم خود] ببینیم.» پس صاعقه [جان] شما را گرفت، در حالی که تماشا می‌کردید.'),
(63, 2, 56, 'سپس شما را پس از مرگتان [حیات بخشیدیم و] برانگیختیم، شاید شکرگزاری کنید.'),
(64, 2, 57, 'و ابر را بر شما سایبان ساختیم و مَنّ و سَلوی را برای شما فرستادیم [و گفتیم]: «از نعمت‌های پاکیزه‌ای که به شما روزی دادیم بخورید». [ولی آنها با بهانه‌جویی‌های خود، کفران نعمت کردند. بدانید که] آنها به ما ستم نکردند، بلکه به خودشان ستم نمودند.'),
(65, 2, 58, 'و [به خاطر بیاورید] زمانی که گفتیم: «وارد این قریه (بیت‌المقدّس) شوید و از [نعمت‌های فراوان] آن، هر چه می‌خواهید با گوارایی بخورید و از درِ [معبد بیت‌المقدّس] سجده‌کنان و خاشعانه وارد شوید و بگویید»: «خداوندا! گناهان ما را بریز!» «تا ما خطاهای شما را ببخشیم و به نیکوکاران، پاداشی افزون خواهیم داد.»'),
(66, 2, 59, 'امّا ستمگران [آن را] به سخن دیگری غیر از آنچه به ایشان گفته شده بود، تبدیل کردند [به جای حِطَََّه، گفتند: «حِنطَه»] پس بر آن ستمکاران به سزای گناهی که مرتکب می‌شدند، عذابی از آسمان فروفرستادیم.'),
(67, 2, 60, 'و زمانی که موسی برای قوم خویش آب طلبید، به او گفتیم: «عصایت را به سنگ بزن!» ناگاه دوازده چشمه‌ی آب از آن شکافت و جوشید، [به طوری که] هر گروهی [از طوایف دوازده‌گانه بنی‌اسرائیل] آبشخور خود را شناخت. [گفتیم:] «از روزیِ الهی بخورید و بیاشامید و در زمین، تبهکارانه فساد نکنید!»'),
(68, 2, 61, 'و زمانی که گفتید: «ای موسی! ما هرگز یک نوع غذا را تحمّل نمی‌کنیم. پس پروردگارت را بخوان تا از آنچه زمین می‌رویاند، از سبزیجات و خیار و سیر و عدس و پیازش، برای ما بیرون آورد.» موسی گفت: «آیا [غذای] پست‌تر را به جای [غذای] بهتر می‌خواهید؟ [اکنون که چنین است، بکوشید از این بیابان خارج و] وارد شهر شوید، که هر چه خواستید برای شما فراهم است.» پس [مهرِ] ذلّت و درماندگی بر آنها زده شد و [بار دیگر] گرفتار غضب پروردگار شدند. این بدان جهت بود که آنها به آیات الهی کفر می‌ورزیدند و پیامبران را به ناحقّ می‌کشتند، و این به سبب آن بود که آنان گنهکار و سرکش و متجاوز بودند.'),
(69, 2, 62, 'کسانی که [به اسلام] ایمان آوردند و کسانی که به آیین یهود گرویدند و نصارا و صابئان، هر کدام که به خدا و روز قیامت ایمان آورند و عمل صالح انجام دهند، پس پاداششان نزد پروردگارشان مسلّم است. و نه ترسی بر آنهاست و نه اندوهگین می‌شوند.'),
(70, 2, 63, 'و [به یاد آورید] زمانی که از شما پیمان گرفتیم و کوه طور را بر فراز شما بالا بردیم [و گفتیم]: «آنچه را [از آیات و دستورات خداوند] به شما داده‌ایم، با قدرت بگیرید و آنچه را در آن هست، به خاطر داشته باشید [و به آن عمل کنید] تا پرهیزکار شوید.»'),
(71, 2, 64, 'سپس شما بعد از این ماجرا [باز هم] روی‌گردان شدید و اگر فضل و رحمت خداوند بر شما نبود، قطعاً از زیانکاران بودید.'),
(72, 2, 65, 'قطعاً از [سرنوشت] کسانی از شما که در روز شنبه، نافرمانی کردند [و به جای تعطیل کردن این روز، دنبال کار رفتند،] آگاهید، ما به آنان گفتیم: «به شکل بوزینه‌هایی طرد شده درآیید!»'),
(73, 2, 66, 'ما این مجازات را درس عبرتی برای مردم آن زمان و نسل‌های بعد از آنان، و پندی برای پرهیزکاران قرار دادیم.'),
(74, 2, 67, 'و هنگامی که موسی به قوم خود گفت: «خداوند به شما فرمان می‌دهد [برای یافتن قاتل] ماده گاوی را ذبح کنید.» گفتند: «آیا ما را به تمسخر می‌گیری؟» [موسی] گفت: «به خدا پناه می‌برم از این که از جاهلان باشم.»'),
(75, 2, 68, '[بنی‌اسرائیل به موسی] گفتند: «برای ما پروردگارت را بخوان تا بر ما روشن کند که آن چگونه [گاوی] است؟» [موسی] گفت: «خداوند می‌فرماید: ماده گاوی که نه پیر و از کار افتاده باشد و نه بِکر و جوان، [بلکه] میان این دو [و میان‌سال] باشد. پس آنچه به شما دستور داده شده [هر چه زودتر] انجام دهید.»'),
(76, 2, 69, '[آنان به موسی] گفتند: «برای ما پروردگارت را بخوان تا بر ما روشن سازد که رنگش چگونه باشد؟» [موسی] گفت: «خداوند می‌گوید: آن ماده گاوی باشد زرد یکدست، که رنگِ آن، بینندگان را شاد و مسرور سازد!»'),
(77, 2, 70, '[بار دیگر به موسی] گفتند: «برای ما پروردگارت را بخوان تا بر ما روشن کند چگونه گاوی باشد؟ زیرا این گاو بر ما مشتبه شده و اگر خداوند بخواهد، [به شناخت آن] هدایت خواهیم شد.»'),
(78, 2, 71, '[موسی] گفت: «خداوند می‌فرماید: آن گاوی است که نه چنان رام باشد که زمین را شخم زند و نه کشت‌زار را آبیاری کند. [بی‌عیب و] سالم است و هیچ لکه‌ای در [رنگ] آن نیست.» گفتند: «الآن حقّ [مطلب] را آوردی!» پس [چنان گاوی را پیدا کردند و] آن را سر بریدند، ولی نزدیک بود انجام ندهند.'),
(79, 2, 72, 'و هنگامی که فردی را به قتل رساندید و سپس درباره‌ی [قاتل] او، به نزاع پرداختید، ولی خداوند آنچه را شما پنهان می‌داشتید، آشکار می‌سازد.'),
(80, 2, 73, 'سپس گفتیم: «قسمتی از گاو ذبح شده را به مقتول بزنید [تا زنده شود و قاتل را معرّفی کند]. خداوند این‌گونه مردگان را زنده می‌کند و آیات خود را به شما نشان می‌دهد، شاید بیندیشید.»'),
(81, 2, 74, 'سپس دل‌های شما بعد از این [همه کفران] سخت شد، همچون سنگ یا سخت‌تر! چرا که از پاره‌ای سنگ‌ها، نهرها شکافته [و جاری] می‌شود و پاره‌ای از آنها، شکاف برمی‌دارد و آب از آن تراوش می‌کند، و بعضی از سنگ‌ها از خوف خدا [از فراز کوه] به زیر می‌افتد. و خداوند از اعمال شما غافل نیست.'),
(82, 2, 75, '[پس ای مؤمنان!] آیا انتظار دارید [که یهودیانِ سرسخت،] به [دین] شما ایمان بیاورند؟ در حالی که عده‌ای از آنان، سخنان خدا را می‌شنیدند و پس از فهمیدنش‌، آن را تحریف می‌کردند با آن که [حقّ را] می‌دانستند؟!'),
(83, 2, 76, 'و [همین یهودیان] هنگامی که مؤمنان را ملاقات می‌کنند، می‌گویند: «ایمان آوردیم.» ولی هنگامی که با یکدیگر خلوت می‌کنند، [بعضی به بعضی دیگر اعتراض کرده،] می‌گویند: «چرا آنچه را که خداوند [درباره‌ی صفات آخرین پیامبر] برای شما [در تورات] گشوده [به مسلمانان] بازگو می‌کنید تا [روز رستاخیز] در پیشگاه پروردگارتان، بر ضدّ شما به آن استدلال کنند؟ پس آیا نمی‌فهمید؟!»'),
(84, 2, 77, 'آیا نمی‌دانند که خداوند آنچه را پنهان می‌دارند و آنچه را آشکار می‌کنند، می‌داند؟!'),
(85, 2, 78, 'و پاره‌ای از آنان (یهودیان) عوامانی هستند که کتاب خدا را جز [مشتی خیالات و] آرزوها نمی‌دانند و تنها به پندارهایشان دل بسته‌اند.'),
(86, 2, 79, 'پس وای بر کسانی که مطالبی را با دست خود می‌نویسند، سپس می‌گویند: «این، از طرف خداست.» تا به آن، بهایِ اندکی بستانند، پس وای بر آنها از آنچه دست‌هایشان نوشت و وای بر آنها از آنچه [از این راه] به دست می‌آورند!'),
(87, 2, 80, 'و [یهود] گفتند: «جز چند روزی محدود، هرگز آتش دوزخ به ما نخواهد رسید.» بگو: «آیا پیمانی از نزد خدا گرفته‌اید که البتّه خداوند هرگز از پیمانش تخلّف نمی‌ورزد، یا چیزی را که نمی‌دانید به خدا نسبت می‌دهید؟!»'),
(88, 2, 81, 'آری، کسانی که مرتکب گناه شوند و گناهشان آنان را فراگیرد [و آثار گناهان سراسر وجودشان را بپوشاند]، آنها اهل آتشند و همیشه در آن خواهند بود.'),
(89, 2, 82, 'و کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، آنان اهل بهشتند و در آن جاوِدانه خواهند ماند.'),
(90, 2, 83, 'و [به یاد آرید] زمانی که از بنی‌اسرائیل پیمان گرفتیم که جز خداوند یگانه را پرستش نکنید و به پدر و مادر و خویشان و یتیمان و بینوایان، احسان کنید و با مردم، به زبان خوش سخن بگویید و نماز را برپای دارید و زکات بدهید. امّا شما جز عدّه‌ی کمی، [از این پیمان] سرپیچی کردید و [از وفای به آن] روی‌گردان شدید.'),
(91, 2, 84, 'و هنگامی که از شما پیمان گرفتیم، خون‌های یکدیگر را نریزید و همدیگر را از سرزمین خود بیرون نکنید، پس شما [به این پیمان] اقرار کردید و خود بر آن گواهی می‌دهید.'),
(92, 2, 85, 'امّا این شما هستید که یکدیگر را به قتل می‌رسانید و جمعی از خودتان را از سرزمینشان [آواره و] بیرون می‌کنید و علیه آنان، به گناه و تجاوز همدیگر را پشتیبانی می‌کنید. ولی اگر همانان به صورت اسیران نزد شما آیند، بازخریدشان می‌کنید [تا آزادشان سازید] در حالی که [نه تنها کشتن، بلکه] بیرون‌راندن آنها [نیز] بر شما حرام بود. آیا به بعضی از دستورات کتاب آسمانی ایمان می‌آورید و به برخی دیگر کافر می‌شوید؟ پس جزای هر کس از شما که این عمل را انجام دهد، جز رسوایی در این جهان، چیزی نخواهد بود و روز قیامت به سوی سخت‌ترین عذاب روانه شوند و خداوند هرگز از آنچه انجام می‌دهید، غافل نیست.'),
(93, 2, 86, 'آنها کسانی هستند که زندگی دنیا را به بهای [از دست دادن] آخرت خریده‌اند، از این رو از عذاب آنها کاسته نمی‌شود و آنها یاری نخواهند شد.'),
(94, 2, 87, 'و به موسی کتاب [تورات] دادیم و از پس او پیامبرانی پی در پی فرستادیم و به عیسی‌بن‌مریم [معجزات و] دلایل روشن بخشیدیم و او را با روح‌القدس، تأیید و یاری نمودیم. پس چرا هرگاه پیامبری چیزی بر خلاف هوای نفس شما آورد، در برابر او تکبّر ورزیدید، [و به جای ایمان آوردنِ به او] جمعی را تکذیب کردید و جمعی را به قتل رساندید؟!'),
(95, 2, 88, 'و آنها [از روی استهزا به پیامبران] گفتند: «دل‌های ما در غلاف است [و ما از گفته‌های شما چیزی نمی‌فهمیم].» چنین نیست، بلکه خداوند به سبب کفرشان، آنها را از رحمت خود دور ساخته [و چیزی درک نمی‌کنند]، پس اندکی ایمان می‌آورند.'),
(96, 2, 89, 'و چون از سوی خداوند، کتابی برای آنها آمد که نشانه‌هایی را که در کتابشان آمده است، راست می‌شمرد، و پیش از این به خود نوید می‌دادند [که با کمک پیامبر جدید] بر کافران پیروز گردند، با این همه، هنگامی که این [کتاب، و پیامبری] را که شناخته بودند، نزد آنان آمد، به او کافر شدند، پس لعنت خدا بر کافران باد!'),
(97, 2, 90, 'چه بد است آنچه که خویشتن را به آن فروختند، که از روی حسد، به آیاتی که خدا فرستاده بود، کافر شدند [و گفتند]: «چرا خداوند از فضل خویش بر هر کس از بندگانش که بخواهد، [آیاتش را] نازل می‌کند؟!» پس به قهر پی در پی الهی گرفتار شدند. و برای کافران، مجازاتی خوارکننده است.'),
(98, 2, 91, 'و هنگامی که به آنها گفته شود: «به آنچه خداوند نازل کرده، ایمان آورید.» می‌گویند: «ما تنها به چیزی ایمان می‌آوریم که بر [پیامبر] خودمان نازل شده باشد.» و به غیر آن کافر می‌شوند، در حالی که آن [قرآن] حقّ است و آنچه را [از نشانه‌های آن] در کتاب ایشان آمده است، تصدیق می‌کند. بگو: «اگر [به آیاتی که بر خودتان نازل شده] مؤمن بودید، پس چرا پیامبران خدا را پیش از این به قتل می‌رساندید؟!»'),
(99, 2, 92, 'و موسی [آن همه دلایل روشن و] معجزات برای شما آورد، ولی شما پس از [غیبت] او گوساله را [به خدایی] گرفتید، در حالی که ستمکار بودید.'),
(100, 2, 93, 'و آن‌گاه که از شما پیمان گرفتیم و کوه طور را بر فراز شما بالا بردیم [و گفتیم]: «دستوراتی که به شما داده‌ایم، محکم بگیرید و گوش دهید [و عمل کنید].» [امّا آنان] گفتند: «شنیدیم و نافرمانی کردیم.» و به سبب کفرشان، به [پرستش] گوساله دل باختند. بگو: «اگر ادّعای ایمان دارید، [بدانید که] ایمانتان شما را به بدچیزی فرمان می‌دهد.»'),
(101, 2, 94, 'بگو: «اگر [آن چنان كه مدّعی هستید] سرای آخرت در نزد خداوند مخصوص شماست، نه سایر مردم، پس آرزوی مرگ كنید، اگر راست می‌گویید.»'),
(102, 2, 95, 'ولی آنها به سبب آنچه از [اعمال بد که از] پیش فرستاده‌اند، هرگز آرزوی مرگ نخواهند كرد و خداوند به حال ستمگران، آگاه است.'),
(103, 2, 96, '[ای پیامبر!] هر آینه یهود را حریص‌ترینِ مردم، حتّی [حریص‌تر] از مشركان، بر زندگی [این دنیا و اندوختن ثروت] خواهی یافت، [تا آنجا كه] هر یك از آنها دوست دارد هزار سال عمر كند، در حالی كه [اگر] این عمر طولانی به آنان داده شود، آنان را از عذاب خداوند باز نخواهد داشت و خداوند به اعمال آنها بیناست.'),
(104, 2, 97, '[یهود می‌گویند: «چون فرشته‌ای كه وحی بر تو نازل می‌كند، جِبرئیل است، ما به تو ایمان نمی‌آوریم.»] بگو: «هر كه دشمن جِبرئیل باشد [در حقیقت دشمن خداست].» چرا كه او به فرمان خدا، قرآن را بر قلب تو نازل كرده است، [قرآنی] كه كتب آسمانی پیشین را تصدیق می‌كند و مایه‌ی هدایت و بِشارت برای مؤمنان است.'),
(105, 2, 98, 'هر كه دشمن خدا و فرشتگان و پیامبران او و جبرئیل و میكائیل باشد، [كافر است و بداند كه] خداوند دشمن كافران است.'),
(106, 2, 99, 'همانا آیات روشنی به سوی تو فرستادیم و جز فاسقان كسی به آنها كفر نمی‌ورزد.'),
(107, 2, 100, 'و آیا چنین نبود كه هر بار یهود پیمانی [با خدا و پیامبر] بستند، جمعی از آنان، آن را دور افكندند؟ حقیقت این است كه بیشتر آنها ایمان ندارند.'),
(108, 2, 101, 'و هنگامی كه فرستاده‌ای [چون پیامبر خاتم] از سوی خدا به سراغشان آمد كه با نشانه‌هایی كه نزد آنها بود، مطابقت داشت، گروهی از اهل كتاب، كتاب خدا را پشت سر افكندند، گویی هیچ از آن آگاهی ندارند.'),
(109, 2, 102, 'و [یهود] آنچه را [از افسون و سحر] که شیاطین [جنّ و انس] در عصر سلیمان می‌خواندند، پیروی کردند و سلیمان هرگز [دست به سحر نیالود و] کافر نشد ولی شیاطین، کفر ورزیدند که به مردم سحر و جادو می‌آموختند و [نیز یهود] از آنچه بر دو فرشته، هاروت و ماروت، در شهر بابل نازل شده بود [پیروی نمودند. آنها سحر را برای آشنایی با طرز ابطال آن به مردم می‌آموختند.] و به هیچ کس چیزی نمی‌آموختند، مگر این که قبلاً به او می‌گفتند: «ما وسیله‌ی آزمایشِ شما هستیم، [با به کار بستن سحر] کافر نشوید [و از این تعلیمات، سوء استفاده نکنید].» ولی آنها از آن دو فرشته مطالبی را می‌آموختند تا بتوانند به وسیله‌ی آن میان مرد و همسرش جدایی بیافکنند. ولی هیچ‌گاه بدون خواست خدا، نمی‌توانند به کسی ضرر برسانند. آنها مطالبی را فرامی‌گرفتند که برای آنان زیان داشت و نفعی نمی‌رساند، و مسلّماً می‌دانستند هر کس خریدار این گونه متاع باشد، بهره‌ای در آخرت نخواهد داشت و به راستی خود را به بد چیزی فروختند، اگر می‌فهمیدند.'),
(110, 2, 103, 'و اگر آنها ایمان آورده و پرهیزکار شده بودند، قطعاً پاداشی که نزد خداست، برای آنان بهتر بود، اگر آگاهی داشتند.'),
(111, 2, 104, 'ای کسانی که ایمان آورده‌اید! [هنگامی که از پیامبر تقاضای مهلت برای درک آیات قرآن می‌کنید، به او] نگویید: «راعِنا، مراعاتمان کن!» بلکه بگویید: «اُنظُرنا، ما را در نظر بگیر!» [زیرا راعِنا، به معنای «ما را تحمیق کن» نیز هست و دستاویزی است برای دشمن]؛ و [این توصیه را] بشنوید، و برای کافران عذاب دردناکی است.'),
(112, 2, 105, 'کافرانِ از اهل کتاب و مشرکان، دوست ندارند که هیچ خیر و نیکی از طرف پروردگارتان بر شما نازل شود. در حالی که خداوند، رحمت خود را به هر که بخواهد، اختصاص می‌دهد، و خداوند صاحب فضل بزرگ است.'),
(113, 2, 106, 'هر [حکم و] آیه‌ای را نَسخ کنیم و یا [نزول] آن را به تأخیر اندازیم، بهتر از آن، یا همانند آن را می‌آوریم، آیا نمی‌دانی که خدا بر هر چیزی تواناست؟'),
(114, 2, 107, 'آیا ندانستی که حکومت آسمان‌ها و زمین تنها از آن خداست؟ و جز خدا برای شما هیچ یاور و سرپرستی نیست.'),
(115, 2, 108, 'آیا می‌خواهید از پیامبرتان سؤالات و درخواست‌هایی [نابجا] کنید، آن‌گونه که پیش از این، موسی [از طرف بنی‌اسرائیل] مورد سؤال قرار گرفت؟ و هر کس [با این بهانه‌جویی‌ها از ایمان سر باز زند و] کفر را به جای ایمان بپذیرد، قطعاً از راه مستقیم گمراه شده است.'),
(116, 2, 109, 'بسیاری از اهل کتاب [نه تنها خودشان ایمان نمی‌آورند، بلکه] از روی حسدی که در درونشان هست، دوست دارند شما را بعد از ایمانتان به کفر بازگردانند، با این که حقّ [بودن اسلام و قرآن،] برای آنان روشن شده است، ولی شما [آنها را] عفو کنید و درگذرید تا خداوند فرمان خویش را بفرستد، همانا خداوند بر هر کاری تواناست.'),
(117, 2, 110, 'و نماز را برپا دارید و زکات را بپردازید، و هر خیری که برای خود از پیش می‌فرستید، آن را نزد خدا [در سرای دیگر] خواهید یافت، همانا خداوند به اعمال شما بیناست.'),
(118, 2, 111, 'و آنها گفتند: «هیچ کس، جز یهود یا نصارا، هرگز داخل بهشت نخواهد شد.» اینها [پندار و] آرزوهای آنهاست، بگو: «اگر راست می‌گویید، دلیل خود را [بر این موضوع] بیاورید!»'),
(119, 2, 112, 'آری، [بهشت در انحصار هیچ گروهی نیست،] کسی که با اخلاص به خدا روی آورد و نیکوکار باشد، پس پاداش او نزد پروردگار اوست، نه ترسی بر آنهاست و نه غمگین خواهند شد.'),
(120, 2, 113, 'و یهودیان گفتند: «نصارا بر حقّ نیستند.» و نصارا گفتند: «یهودیان بر حقّ نیستند.» در حالی که [هر دو گروهِ] آنان، کتاب آسمانی را می‌خوانند! همچنین افراد نادان دیگر [همچون مشرکان که خبر از کتاب ندارند]، همانند سخن آنها را گفتند. پس خداوند در روز قیامت درباره‌ی آنچه در آن اختلاف دارند، میان آنها داوری خواهد نمود.'),
(121, 2, 114, 'کیست ستمکارتر از آن که نگذاشت نام خدا در مساجد الهی برده شود و در خرابی آنها تلاش کرد؟ آنان جز با ترس و خوف، حقّ ورود به مساجد را ندارند. بهره‌ی آنان در دنیا، رسوایی و خواری و در آخرت عذاب بزرگ است.'),
(122, 2, 115, 'مشرق و مغرب از آن خداست، پس به هر سو رو کنید، آنجا رو به خداست، زیرا که خداوند بر همه چیز احاطه دارد و داناست.'),
(123, 2, 116, 'و [برخی از اهل کتاب و مشرکان] گفتند: «خداوند، فرزندی برای خود اختیار کرده است.» منزّه است او، بلکه آنچه در آسمان‌ها و زمین است، از آن اوست و همه در برابر او فرمان برند.'),
(124, 2, 117, 'پدیدآورنده‌ی آسمان‌ها و زمین اوست، و هنگامی که فرمان [آفرینشِ] چیزی را صادر کند، فقط می‌گوید: «باش!» پس آن [چیز، بی‌درنگ] موجود می‌شود.'),
(125, 2, 118, 'افراد ناآگاه گفتند: «چرا خدا با خود ما سخن نمی‌گوید؟ یا آیه و نشانه‌ای برای خود ما نمی‌آید؟» پیشینیانِ آنها نیز، همین‌گونه سخن می‌گفتند، دل‌ها [و افکار]شان مشابه یکدیگر است، ولی ما [به اندازه‌ی کافی] آیات و نشانه‌ها را برای اهل یقین [و حقیقت‌جویان] روشن ساخته‌ایم.'),
(126, 2, 119, '[ای رسول!] ما تو را به حقّ فرستادیم تا بشارت‌گر و بیم‌دهنده باشی و تو مسئول دوزخیان [و جهنّم رفتن آنان] نیستی.'),
(127, 2, 120, '[ای پیامبر!] هرگز یهود و نصارا از تو راضی نخواهند شد تا [آنکه تسلیم خواسته‌ی آنان شوی و] از آیین آنان پیروی کنی. بگو: «هدایت، تنها هدایت الهی است.» و اگر از هوا و هوس‌های آنان پیروی کنی، بعد از آنکه علم [الهی] نزد تو آمد، هیچ سرور و یاوری از ناحیه‌ی خداوند برای تو نخواهد بود.'),
(128, 2, 121, 'کسانی که به آنها کتاب آسمانی داده‌ایم و آنرا چنان که سزاوار است، تلاوت [و عمل] می‌کنند، آنان به آن ایمان می‌آورند و کسانی که به آن کافر شوند، آنان همان زیانکارانند.'),
(129, 2, 122, 'ای بنی‌اسرائیل! نعمت مرا که به شما ارزانی داشتم و شما را بر جهانیان برتری بخشیدم، یاد کنید.'),
(130, 2, 123, 'و بترسید از روزی که هیچکس چیزی [از عذاب خدا] را از دیگری دفع نمی‌کند و هیچ‌گونه عوضی از او قبول نمی‌گردد و هیچ شفاعتی، او را سود ندهد و [از جانب هیچ کسی] یاری نمی‌شود.'),
(131, 2, 124, 'و هنگامی که پروردگارِ ابراهیم، او را با حوادث گوناگونی آزمود و او به خوبی از عهده‌ی آزمایش برآمد. خداوند به او فرمود: «من تو را امام مردم قرار دادم.» ابراهیم گفت: «از دودمان من نیز [امامانی قرار بده].» خداوند فرمود: «پیمان من به ستمکاران نمی‌رسد».'),
(132, 2, 125, 'و [به یادآور] هنگامی که خانه[ی کعبه] را محل بازگشت و اجتماع و مرکز امن برای مردم قرار دادیم [و گفتیم]: «از مقام ابراهیم، جایگاهی برای نماز انتخاب کنید.» و به ابراهیم و اسماعیل تکلیف کردیم که: «خانه‌ی مرا برای طواف‌کنندگان و معتکفان و رکوع‌کنندگانِ سجده‌گزار، پاک و پاکیزه کنید.»'),
(133, 2, 126, 'و هنگامی که ابراهیم گفت: «پروردگارا! این [سرزمین] را شهری امن قرار ده و اهل آن را، آنان که به خدا و روز قیامت ایمان آورده‌اند، از ثمرات [گوناگون] روزی ده!» [خداوند] فرمود: «و به آنهایی که کافر شوند [نیز] بهره‌ی اندکی خواهم داد. سپس آنها را به قهر به سوی عذاب آتش می‌کشانم و چه بد، سرانجامی است.»'),
(134, 2, 127, 'و هنگامی که ابراهیم و اسماعیل، پایه‌های خانه [کعبه] را بالا می‌بردند [و می‌گفتند]: «پروردگارا! از ما بپذیر! همانا تو شنوا و دانایی!'),
(135, 2, 128, 'پروردگارا! ما را تسلیم خود قرار ده و از نسل ما [نیز] امّتی که تسلیم تو باشند، پدید آور و راه و روشِ پرستش را به ما نشان ده و توبه‌ی ما را بپذیر، که همانا تو، توبه‌پذیرِ مهربانی!»'),
(136, 2, 129, '«پروردگارا! در میان آنان پیامبری از خودشان برانگیز تا آیات تو را بر آنها بخواند و آنان را کتاب و حکمت بیاموزد و پاکیزه نماید، که تو خود توانا و حکیمی!»'),
(137, 2, 130, 'کیست که از آیین ابراهیم روی بگرداند، مگر کسی که خود را به نادانی و بی‌خردی زده است؟ و ما او را در این جهان برگزیدیم و قطعاً او در جهان دیگر [نیز] از صالحان است.'),
(138, 2, 131, 'هنگامی که پروردگار ابراهیم به او گفت: «تسلیم شو!» [او فرمان پروردگار را از جان و دل پذیرفت و] گفت: «در برابر پروردگار جهانیان تسلیم شدم.»'),
(139, 2, 132, 'و ابراهیم و یعقوب، فرزندان خود را به همان آیین سفارش کردند [و گفتند]: «فرزندان من! خداوند برای شما این دین [توحیدی] را برگزیده است. پس [تا پایان عمر بر آن باشید و] جز در حال تسلیم [و فرمانبرداری] نمیرید.»'),
(140, 2, 133, 'آیا شما [یهودیان] هنگامی که مرگ یعقوب فرارسید، حاضر بودید!؟ آن هنگام که به فرزندان خود گفت: «پس از من چه می‌پرستید؟» گفتند: «خدای تو و خدای پدرانت ابراهیم و اسماعیل و اسحاق، خداوند یکتا را و ما در برابر او تسلیم هستیم.»'),
(141, 2, 134, 'آنها امّتی بودند که درگذشتند، دستاورد آنها مربوط به خودشان و دستاورد شما نیز مربوط به خودتان است و شما از آنچه آنان انجام داده‌اند، بازخواست نخواهید شد.'),
(142, 2, 135, '[اهل کتاب] گفتند: «یهودی یا نصارا شوید تا هدایت یابید.» بگو: «بلکه [پیروی از] آیین حق‌گرایِ ابراهیم [مایه‌ی هدایت است، زیرا] او از مشرکان نبود.»'),
(143, 2, 136, '[ای مسلمانان!] بگویید: «ما به خدا ایمان آورده‌ایم و به آنچه بر ما نازل شده و آنچه به ابراهیم و اسماعیل و اسحاق و یعقوب و اَسباط [که پیامبرانی از نوادگان یعقوبند]، و آنچه به موسی و عیسی و پیامبران دیگر از طرف پروردگارشان نازل شده است، [ایمان آورده‌ایم و] ما بین هیچ یک از آنها جدایی قائل نیستیم [و میان آنان فرق نمی‌گذاریم و] در برابر خداوند تسلیم هستیم.»'),
(144, 2, 137, 'اگر آنها [نیز] به مانند آنچه شما بدان ایمان آورده‌اید، ایمان بیاورند، همانا هدایت یافته‌اند، و اگر سرپیچی نمایند، پس جز این نیست که آنها [با حقّ] در ستیزند و خداوند شرّ آنان را از تو دفع می‌کند و او شنوا‌ی داناست.'),
(145, 2, 138, '[این است] رنگ‌آمیزی الهی! و کیست بهتر از خدا در نگارگری؟ و ما تنها او را عبادت می‌کنیم.'),
(146, 2, 139, '[ای پیامبر! به اهل کتاب] بگو: «آیا درباره‌ی خداوند با ما مجادله می‌کنید، در حالی که او پروردگار ما و شماست و اعمال ما برای ما و اعمال شما از آنِ شماست. و ما برای او خالصانه عبادت می‌کنیم.»'),
(147, 2, 140, 'یا می‌گویید که: «ابراهیم و اسماعیل و اسحاق و یعقوب، و پیامبران از فرزندان یعقوب، یهودی یا نصرانی بودند!» بگو: «شما بهتر می‌دانید یا خدا؟» و چه کسی ستمکارتر است از آن کس که گواهی الهی [درباره‌ی پیامبران] را که نزد اوست، کتمان کند؟ و خدا از اعمال شما غافل نیست.'),
(148, 2, 141, 'آن امّت بگذشت، آنچه آنان به دست آوردند، برای خودشان است و آنچه شما به دست آوردید، برای خودتان است و شما از آنچه آنان کرده‌اند، سؤال نخواهید شد.'),
(149, 2, 142, 'به زودی مردم بی‌خرد خواهند گفت: «چه چیزی مسلمانان را از [بیت‌المقدّس] قبله‌ای که بر آن بودند، برگردانید؟» بگو: «مشرق و مغرب از آنِ خداست، هرکه را بخواهد به راه راست هدایت می‌کند.»'),
(150, 2, 143, 'و بدین سان ما شما را امّتی میانه قرار دادیم تا بر مردم گواه باشید و پیامبر [نیز] بر شما گواه باشد. و قبله‌ای را که [چندی] بر آن بودی، فقط به خاطر این قرار دادیم تا کسانی را که از پیامبر پیروی می‌کنند از آنها که به عقب بازمی‌گردند، معلوم کنیم. و این [تغییرِ قبله،] دشوار بود، جز بر کسانی که خداوند آنها را هدایت کرده است. و خدا هرگز ایمان شما را [که پیش‌تر به سوی بیت‌المقدّس نماز خوانده‌اید،] تباه نمی‌گرداند. زیرا خداوند نسبت به مردم، رحیم و مهربان است.'),
(151, 2, 144, '[ای پیامبر!] می‌بینیم که تو [در انتظار نزول وحی، چگونه] روی به آسمان می‌کنی، اکنون تو را به سوی قبله‌ای که از آن خشنود باشی، برمی‌گردانیم. پس روی خود را به جانب مسجدالحرام کن. و [شما ای مسلمانان!] هر جا بودید، روی خود را به جانب آن بگردانید. و کسانی که کتاب به آنها داده شده، می‌دانند که این فرمانِ حقّی است که از ناحیه‌ی پروردگار صادر شده و خداوند از اعمال آنها غافل نیست.'),
(152, 2, 145, 'و [ای پیامبر!] اگر هرگونه آیه، [نشانه و دلیلی] برای [این گروه از] اهل کتاب بیاوری، از قبله‌ی تو پیروی نخواهند کرد، و تو نیز پیرو قبله‌ی آنان نیستی، [همان‌گونه که] هیچ‌یک از آنها، از قبله‌ی دیگری پیروی نخواهد کرد! و اگر پس از آنکه علم [وحی] به تو رسیده است، از هوس‌های آنان پیروی کنی، بی‌شک در آن صورت از ستمگران خواهی بود.'),
(153, 2, 146, 'کسانی که به آنان کتاب داده‌ایم، پیامبر را همچون پسران خود می‌شناسند، و گروهی از آنان با آن که حقّ را می‌دانند، کتمان می‌کنند.'),
(154, 2, 147, 'حقّ، [آن چیزی است که] از سوی پروردگار توست، پس هرگز از تردیدکنندگان مباش.'),
(155, 2, 148, 'و هر امّتی قبله‌ای دارد که به آن سوی رو می‌کند. پس [درباره‌ی جهتِ قبله گفتگو نکنید و به جای آن] در نیکی‌ها و اعمال خیر بر یکدیگر سبقت جویید. [و بدانید] هر جا که باشید، خداوند همه شما را [در محشر] حاضر می‌کند، همانا خداوند بر هر کاری تواناست.'),
(156, 2, 149, 'و از هر جا [که به قصد سفر] خارج شدی، [به هنگام نماز] روی خود را به جانب مسجدالحرام بگردان، این دستور حقّی است از طرف پروردگارت، و خداوند از آنچه انجام می‌دهید، غافل نیست.'),
(157, 2, 150, 'و [ای پیامبر!] از هر جا خارج شدی، پس [به هنگام نماز] روی خود را به سوی مسجدالحرام بگردان، و [شما نیز ای مسلمانان!] هر جا بودید روی خود را به سوی آن بگردانید تا مردم، بر ضدّ شما دلیلی نداشته باشند، مگر آنها که ستمکار [و لجوج] هستند. پس از آنها نترسید و تنها از من پروا کنید. و [بدانید تغییر قبله برای آن بود] که نعمت خود را بر شما تمام کنم، و شاید که شما هدایت شوید.'),
(158, 2, 151, 'همان‌گونه [که برای هدایت شما] رسولی در میان شما از نوع خودتان فرستادیم تا آیات ما را بر شما بخواند و شما را پاک سازد و به شما کتاب و حکمت بیاموزد و آنچه را نمی‌توانستید بدانید، به شما یاد دهد.'),
(159, 2, 152, 'پس مرا یاد کنید، تا شما را یاد کنم، و برای من شکر کنید و کفران نورزید.'),
(160, 2, 153, 'ای کسانی که ایمان آورده‌اید! [در حوادث سخت زندگی،] از صبر و نماز کمک بگیرید، همانا خداوند با صابران است.'),
(161, 2, 154, 'و به آنها که در راه خدا کشته می‌شوند مرده نگویید، بلکه آنان زنده‌اند، ولی شما نمی‌فهمید.'),
(162, 2, 155, 'و قطعاً شما را با چیزی از قبیل ترس، گرسنگی، زیان مالی و جانی و کمبود محصولات، می‌آزماییم و صابران [در این حوادث و بلاها] را بشارت بده!'),
(163, 2, 156, '[صابران] کسانی هستند که هرگاه مصیبتی به آنها رسد، می‌گویند: «ما از آنِ خدا هستیم و به سوی او باز می‌گردیم.»'),
(164, 2, 157, 'آنانند که درودها و رحمت پروردگارشان، شامل حالشان شده و همان‌ها هدایت‌یافتگانند.'),
(165, 2, 158, 'صفا و مروه، از شعائر خداست. پس هر که حجّ خانه‌ی خدا و یا عمره به جای آورد، مانعی ندارد که بین صفا و مَروه طواف کند. و [علاوه بر واجبات،] هر کس داوطلبانه کار خیری انجام دهد، همانا خداوند سپاس‌گزار داناست.'),
(166, 2, 159, 'کسانی که دلایل روشن و راهنمایی‌هایی را که ما نازل کرده‌ایم، با آن که برای مردم در کتاب [آسمانی آنها] بیان نموده‌ایم، کتمان می‌کنند، خداوند آنها را لعنت می‌کند و همه لعنت‌کنندگان نیز آنان را لعن می‌نمایند.'),
(167, 2, 160, 'مگر آنها که توبه کردند و [اعمال بد خود را با اعمال نیک] اصلاح نمودند و [آنچه را کتمان کرده بودند،] آشکار ساختند که من [لطف خود را] بر آنان بازمی‌گردانم، زیرا من توبه‌پذیر مهربانم.'),
(168, 2, 161, 'کسانی که کافر شدند و در حال کفر از دنیا رفتند، لعنت خدا و فرشتگان و مردم، همگی بر آنها خواهد بود.'),
(169, 2, 162, '[آنان برای] همیشه در آن [لعنت و دوری از رحمت پروردگار] باقی می‌مانند، نه از عذابِ آنان کاسته می‌شود و نه مهلت داده می‌شوند.'),
(170, 2, 163, 'و معبود شما خدایی یگانه است، جز او معبودی نیست، بخشنده‌ی مهربان است.'),
(171, 2, 164, 'در آفرینش آسمان‌ها و زمین و آمد و شدِ شب و روز و کشتی‌هایی که در دریا در حرکتند تا به مردم سود برسانند و آبی که خداوند از آسمان نازل کرده و با آن زمین مرده را زنده نموده و انواع جنبندگان را در آن گسترده و [همچنین] در تغییر مسیر بادها و ابرهایی که میان آسمان و زمین تحت فرمانند، برای مردمی که می‌اندیشند، نشانه‌هایی گویاست.'),
(172, 2, 165, 'و بعضی از مردم کسانی هستند که معبودهایی غیر از خداوند برای خود برمی‌گزینند و آنها را همچون خدا، دوست دارند. امّا آنان که ایمان دارند، محبتشان به خدا [از محبت مشرکان به معبودهایشان] شدیدتر است و آنها که [به خود] ستم کردند، هنگامی که عذاب خدا را مشاهده کنند، خواهند دانست که تمام نیروها، تنها به دست خداست و خداوند، سخت‌کیفر است.'),
(173, 2, 166, 'در آن هنگام که عذاب را مشاهده کنند و پیوندِ میانشان بریده [و دستشان از همه چیز قطع] گردد، پیشوایان [کفر]، از پیروان خود بیزاری می‌جویند.'),
(174, 2, 167, 'و [در آن هنگام] پیروان می‌گویند: «ای کاش بار دیگر [به دنیا] برمی‌گشتیم تا از این پیشوایان بیزاری جوییم، همان‌گونه که آنها [امروز] از ما بیزاری جستند.» بدین‌سان خداوند اعمال آنها را که مایه‌ی حسرت آنهاست، به آنها نشان می‌دهد و هرگز از آتش دوزخ، بیرون نخواهند آمد.'),
(175, 2, 168, 'ای مردم! از نعمت‌های حلال و پاکیزه‌ای که در زمین است، بخورید و از گام‌های [وسوسه‌انگیز] شیطان، پیروی نکنید. به راستی که او دشمن آشکار شماست.'),
(176, 2, 169, '[شیطان،] شما را فقط به بدی و زشتی فرمان می‌دهد و این‌که آنچه را نمی‌دانید، به خدا نسبت دهید.'),
(177, 2, 170, 'و هنگامی که به آنها گفته شود: «از آنچه خدا نازل کرده است، پیروی کنید» می‌گویند: «بلکه ما از آنچه پدرانِ خود را بر آن یافتیم، پیروی می‌نماییم.» آیا حتی اگر پدران آنها، چیزی را درک نمی‌کردند و به هدایت نمی‌رسیدند [باز هم باید از آنها پیروی کنند]؟'),
(178, 2, 171, 'و مَثَل [تو در دعوت] کافران، چنان است که کسی حیوانی را بانگ زند که جز صدایی و آوایی نمی‌شنود؛ اینان کر و گنگ و کورند و از این روست که نمی‌اندیشند.'),
(179, 2, 172, 'ای کسانی که ایمان آورده‌اید! از نعمت‌های پاکیزه‌ای که به شما روزی داده‌ایم، بخورید و شکر خدا را به جای آورید. اگر فقط او را پرستش می‌کنید.'),
(180, 2, 173, 'خداوند تنها [خوردن گوشت] مردار و خون و گوشت خوک و آنچه را که [هنگام ذبح] نام غیر خدا بر آن برده شده، حرام کرده است، [ولی] آن کس که ناچار شود، در صورتی که زیاده‌طلبی نکند و از حدّ احتیاج نگذراند، گناهی بر او نیست، همانا خداوند بخشنده و مهربان است.'),
(181, 2, 174, 'کسانی که آیات کتاب را که خداوند نازل کرده، کتمان می‌کنند و آن را به بهای کمی می‌فروشند، آنها جز آتش در درونشان فرو نمی‌برند، و خداوند روز قیامت با آنها سخن نمی‌گوید و پاکشان نمی‌کند و برای آنها عذاب دردناکی است.'),
(182, 2, 175, 'آنها کسانی هستند که گمراهی را به بهای از دست دادن هدایت و عذاب را به جای آمرزش خریدند. پس به راستی چقدر در برابر عذاب خداوند تحمّل دارند؟'),
(183, 2, 176, 'آن [عذاب] به جهت آن است که خداوند کتاب [آسمانی] را به حقّ نازل کرده است و البتّه کسانی که در کتاب [خدا، با کتمان و تحریف] اختلاف [ایجاد] کردند، همواره در ستیزه‌ای عمیق هستند.'),
(184, 2, 177, 'نیکی [تنها] این نیست که [به هنگام نماز] روی خود را به سوی مشرق یا مغرب بگردانید، بلکه نیکوکار کسی است که به خدا و روز قیامت و فرشتگان و کتاب آسمانی و پیامبران ایمان آورده و مال [خود] را با همه علاقه‌ای که به آن دارد، به خویشاوندان و یتیمان و بیچارگان و در راه ماندگان و سائلان، و در [راه آزادی] بردگان بدهد، و نماز را برپای دارد و زکات را بپردازد، و [نیز] آنان که چون پیمان می‌بندند، به عهد خود وفا می‌کنند و آنان که در [برابر] سختی‌ها، محرومیت‌ها، بیماری‌ها و در میدان جنگ، استقامت می‌ورزند، اینها کسانی هستند که راست گفتند و اینان همان پرهیزکارانند.'),
(185, 2, 178, 'ای کسانی که ایمان آورده‌اید! [قانون] قِصاص در مورد کشتگان، بر شما [چنین] مقرّر گردیده است: آزاد در برابر آزاد و برده در برابر برده و زن در برابر زن، پس اگر کسی از سوی برادر [دینی] خود [که صاحب خون است،] مورد عفو قرار گیرد، [یا قصاص او به خون‌بها تبدیل شود،] باید شیوه‌ای پسندیده پیش گیرد و به نیکی [دیه را به ولیّ مقتول] بپردازد، این حکم، تخفیف و رحمتی است از ناحیه پروردگارتان، پس کسی که بعد از این [از حکم خدا] تجاوز کند، عذاب دردناکی خواهد داشت.'),
(186, 2, 179, 'ای صاحبان خرد! قصاص برای شما، مایه‌ی حیات است. باشد که شما تقوا پیشه کنید.'),
(187, 2, 180, 'بر شما مقرّر شده که چون [نشانه‌های] مرگ یکی از شما فرارسد، اگر مالی از خود به جای گذارده، برای پدر و مادر و نزدیکان به طور شایسته وصیت کند، این کار، حقّ و شایسته‌ی پرهیزکاران است.'),
(188, 2, 181, 'پس هر کس که آن [وصیت را] بعد از شنیدن، تغییر دهد، گناهش تنها بر همان کسانی است که آن را تغییر می‌دهند، همانا خداوند شنوا و داناست.'),
(189, 2, 182, 'پس کسی که از انحراف وصیت‌کننده [و تمایل او به بعضی وارثان] یا از گناه او [به وصیت،] بترسد و میان آنها را اصلاح دهد، گناهی بر او نیست، همانا خداوند آمرزنده و مهربان است.'),
(190, 2, 183, 'ای کسانی که ایمان آورده‌اید! روزه بر شما مقرّر گردید، همان‌گونه که بر کسانی که پیش از شما بودند، مقرّر شده بود، باشد که پرهیزکار شوید.'),
(191, 2, 184, 'روزهای محدودی [روزه بر شما مقرّر شده است]، ولی هر کس از شما بیمار یا مسافر باشد، پس [به همان] تعداد از روزهای دیگر [را روزه بگیرد]، و بر کسانی که طاقت روزه ندارند [همچون بیماران مزمن و پیران]، لازم است با اطعام مستمندی، کفّاره دهند. و هر کس به میل خود بیشتر نیکی کند، برای او بهتر است، و روزه گرفتن، برایتان بهتر است، اگر [آثار روزه را] بدانید.'),
(192, 2, 185, 'ماه رمضان ماهی است که قرآن در آن نازل شده است. [کتابی که] هدایت‌گر مردم با دلایل روشنِ هدایت و وسیله‌ی تشخیص حقّ از باطل است، پس هر کس از شما که این ماه را دریابد، باید روزه بگیرد. و آن کس که بیمار یا در سفر باشد، روزهای دیگری را به همان تعداد روزه بگیرد. خداوند برای شما آسانی می‌خواهد، نه دشواری. [قضای روزه] برای آن است که شماره‌ی مقرّر روزها را تکمیل کنید و خدا را به خاطر این که شما را هدایت کرده، به بزرگی یاد کنید، باشد که شکرگزار گردید.'),
(193, 2, 186, 'و هرگاه بندگانم از تو درباره‌ی من بپرسند، [بگو] به راستی من نزدیکم؛ دعای نیایش‌گر را آن گاه که مرا می‌خواند، پاسخ می‌گویم. آنها نیز دعوت مرا بپذیرند و به من ایمان آورند، باشد که راه یابند و به رشد برسند.'),
(194, 2, 187, 'در شب‌های روزه‌داریِ [ماه رمضان]، آمیزش با همسرانتان برای شما حلال شد. آنها برای شما [همچون] لباسند و شما برای آنها [همچون] لباس. خداوند می‌دانست که شما به خود خیانت می‌کردید [و آمیزش را که ممنوع بود، انجام می‌دادید]، پس توبه‌ی شما را پذیرفت و از شما درگذشت. اکنون [می‌توانید] با آنها همبستر شوید و آنچه را خداوند بر شما مقرّر فرموده، طلب کنید. و بخورید و بیاشامید تا رشته‌ی سپیدِ صبح از رشته‌ی سیاهِ [شب] برای شما آشکار گردد. سپس روزه را تا شب به اتمام رسانید و در حالی که در مساجد به اعتکاف پرداخته‌اید، با زنان آمیزش نکنید. این حدود و احکام الهی است، پس به آن نزدیک نشوید. خداوند این چنین آیات خود را برای مردم روشن می‌سازد، باشد که پرهیزکار گردند.'),
(195, 2, 188, 'و اموال یکدیگر را به باطل [و ناحقّ] در میان خود نخورید و برای خوردن بخشی از اموال مردم به گناه، آنها را به [عنوان رشوه، به کیسه‌ی] حاکمان و قاضی‌ها سرازیر نکنید. در حالی که خود می‌دانید [این کار، گناه است].'),
(196, 2, 189, 'درباره‌ی [حکمتِ] هلال‌های ماه از تو سؤال می‌کنند، بگو: «برای آن است که مردم، اوقات [کارهای خویش] و زمان حجّ را بشناسند.» و نیکی آن نیست که [در حال احرامِ حج،] از پشت خانه‌ها وارد شوید، [آن چنان که در جاهلیت مرسوم بود،] بلکه نیکی آن است که تقوا پیشه کنید و از درها وارد خانه‌ها شوید، از خدا پروا کنید، باشد که رستگار شوید.'),
(197, 2, 190, 'و در راه خدا با کسانی که با شما می‌جنگند، نبرد کنید، ولی از حدّ تجاوز نکنید، که خداوند تجاوزکاران را دوست ندارد.'),
(198, 2, 191, 'و آنان [مشرکانی که از هیچ جنایتی روی‌گردان نیستند،] را هرجا یافتید، بکشید و از [مکه] همان جایی که شما را بیرون کردند، آنها را بیرون کنید و [بدانید که] فتنه‌انگیزی از کشتار بدتر است. و نزد مسجدالحرام با آنها جنگ نکنید، مگر آن که آنها در آنجا با شما بجنگند، پس اگر با شما پیکار کردند، آنها را به قتل برسانید. چنین است جزای کافران.'),
(199, 2, 192, 'و اگر دست کشیدند، همانا خداوند آمرزنده و مهربان است.'),
(200, 2, 193, 'و با آنها بجنگید تا فتنه و آشوبی باقی نماند و دین، تنها از آنِ خدا شود. پس اگر دست برداشتند، تعدّی جز بر ستمکاران روا نیست.'),
(201, 2, 194, 'ماه حرام در برابر ماه حرام، و [اگر دشمنان احترام آن را شکستند، شما نیز حقّ دارید مقابله به مثل کنید. زیرا] تمام حرمت‌ها[ی شکسته شده، قابل] قصاص است و هر کس به شما حمله کرد، به مانند آن تجاوز، بر او حمله کنید و از خدا بپرهیزید [و زیاده‌روی ننمایید] و بدانید که خداوند با پرهیزکاران است.'),
(202, 2, 195, 'و در راه خدا انفاق کنید و خویش را به دست خود به هلاکت نیافکنید و نیکی کنید که خداوند نیکوکاران را دوست دارد.'),
(203, 2, 196, 'و حجّ و عمره را برای خدا به اتمام برسانید و اگر بازداشته شدید [و موانعی مانند بیماری اجازه نداد که حجّ را کامل کنید، بر شماست] قربانی کردنِ آنچه فراهم است و سرهای خود را نتراشید تا قربانی به قربانگاه برسد و اگر کسی از شما بیمار بود یا ناراحتی در سر داشت [و ناچار بود سر خود را زودتر بتراشد،] باید به عوض آن، روزه بگیرد یا صدقه بدهد و یا گوسفندی را ذبح کند. و چون [از بیماری یا دشمن] در امان شدید، پس هر کس به دنبال عمره تمتّع، حج را آغاز کرده، آنچه را از قربانی که میسّر است [ذبح کند.] و هر کس که قربانی نیافت، سه روز در ایام حج و هفت روز به هنگامی که بازگشتید، روزه بدارد. این، ده روزِ کامل است. [البتّه] این [حجِ تمتّع] برای کسی است که خاندانش ساکن [مکه و] مسجدالحرام نباشند. و از خدا پروا کنید و بدانید که خداوند سخت‌کیفر است.'),
(204, 2, 197, 'حجّ [در] ماه‌های معینی است، پس هر که در این ماه‌ها فریضه‌ی حجّ را ادا می‌کند، [بداند که] آمیزش جنسی و گناه و جدال در حج روا نیست. و آنچه از کارهای خیر انجام دهید، خدا می‌داند. و زاد وتوشه تهیه کنید که البتّه بهترین زاد و توشه، تقواست. ای خردمندان! تنها از من پروا کنید.'),
(205, 2, 198, 'گناهی بر شما نیست که از فضل پروردگارتان [از راه تجارت در ایام حجّ] بهره گیرید. پس چون از عرفات کوچ کردید، خدا را در مَشعَرالحرام یاد کنید. و او را یاد کنید، به پاس آن که شما را هدایت نمود. و قطعاً شما پیش از این، از گمراهان بودید.'),
(206, 2, 199, 'سپس از همان جا که مردم کوچ می‌کنند، [به سوی سرزمین مِنی] کوچ کنید. و ازخداوند طلب آمرزش نمایید که خداوند آمرزنده‌ی مهربان است.'),
(207, 2, 200, 'پس چون مناسک [حجّ] خود را انجام دادید، خدا را یاد کنید، همان‌گونه که پدران خویش را یاد می‌کنید، بلکه بیشتر و بهتر از آن. پس بعضی ازمردم کسانی هستند که می‌گویند: «خداوندا! به ما در دنیا عطا کن!» آنان در آخرت بهره‌ای ندارند.'),
(208, 2, 201, '[امّا] بعضی از مردم می‌گویند: «پروردگارا! در این دنیا به ما نیکی عطا کن و در آخرت نیز نیکی مرحمت فرما و ما را ازعذاب آتش نگه‌دار!»'),
(209, 2, 202, 'آنان از آنچه به دست آورده‌اند، بهره و نصیبی دارند و خداوند به سرعت به حساب هر کس می‌رسد.'),
(210, 2, 203, 'و خدا را در روزهای معینی یاد کنید، پس هر کس شتاب کند [و اعمال مِنی را] در دو روز [انجام دهد،] گناهی بر او نیست و هر که تأخیر کند [و اعمال را در سه روز انجام دهد نیز،] گناهی بر او نیست. [البته این اختیار] برای کسی است که [از محرّماتِ احرام] پرهیز کرده باشد. و از خدا پروا کنید و بدانید شما به سوی او محشور خواهید شد.'),
(211, 2, 204, 'و گفتارِ بعضی از مردم درباره‌ی زندگی دنیا، مایه‌ی اعجاب تو می‌شود و خداوند را بر آنچه در دل دارد، گواه می‌گیرد [در حالی که] او سرسخت‌ترینِ دشمنان است.'),
(212, 2, 205, 'و چون از تو روی گرداند [یا به قدرت و حکومت رسد]، برای فساد در زمین تلاش می‌کند و کشت و زرع و نسلِ انسان را نابود می‌سازد و خداوند فساد و تبهکاری را دوست ندارد.'),
(213, 2, 206, 'و هنگامی که به او گفته شود: «از خدای پروا کن!» غرور و نخوت، او را به گناه می‌کشاند. پس آتش دوزخ برای او کافی است و چه بد جایگاهی است!'),
(214, 2, 207, 'و از میان مردم کسی است که جان خود را برای کسب خشنودی خدا می‌فروشد و خداوند نسبت به بندگان مهربان است.'),
(215, 2, 208, 'ای کسانی که ایمان آورده‌اید! همگی در صلح و آشتی درآیید و گام‌های شیطان را پیروی نکنید که او دشمن آشکار شماست.'),
(216, 2, 209, 'و اگر بعد از آن که نشانه‌های روشن برای شما آمد، دچار لغزش شدید، بدانید که خداوند عزیز و حکیم است.'),
(217, 2, 210, 'آیا [مشرکان] انتظار دارند که خداوند و فرشتگان در سایبان‌هایی از ابر به سوی آنها بیایند [و دلایل تازه‌ای در اختیارشان بگذارند]؟! و [چون آنها مخالفت کنند،] کار خاتمه یابد؟ در حالی که همه امور به سوی خدا بازمی‌گردد.'),
(218, 2, 211, 'از بنی‌اسرائیل بپرس که چه اندازه دلایل روشن به آنها دادیم [و آنان کفران کردند]؟ و هر کس نعمتِ [هدایتِ] خدا را پس از آن که به سراغ او آمد، [کفران و] تبدیل کند همانا خداوند سخت‌کیفر است.'),
(219, 2, 212, 'زندگی دنیا، در چشم کافران جلوه یافته است و افراد باایمان را مسخره می‌کنند. در حالی که مؤمنان و پرهیزکاران در قیامت از آنها برترند. و خداوند هر کس را بخواهد، روزیِ بی‌شمار مرحمت می‌کند.'),
(220, 2, 213, 'مردم [در آغاز،] امّتی یگانه و یکدست بودند [به تدریج جوامع و طبقات پدید آمد و میان آنها اختلافاتی پیدا شد]، پس خداوند پیامبران را بشارت‌آور و بیم‌دهنده برانگیخت و با آنان کتاب آسمانی را به حقّ نازل نمود تا میان مردم در آنچه اختلاف داشتند، حکم کنند. و تنها کسانی که کتاب به آنها داده شده بود، پس از آن که نشانه‌های روشن به آنان رسید، به سبب انحراف از حقّ و ستمگری، در آن اختلاف کردند. پس خداوند کسانی را که ایمان آورده بودند، به حقیقتِ آنچه مورد اختلاف بود، به خواست خود هدایت نمود و خداوند هر کس را بخواهد، به راه راست هدایت می‌کند.'),
(221, 2, 214, 'آیا گمان کردید داخل بهشت می‌شوید، بی آن که [جهت آزمایش،] پیش‌آمدِ ناگواری همچون پیش‌آمدِ گذشتگان به شما برسد؟! آنان گرفتار تنگدستی و ناخوشی شده و چنان زیر و زبر شدند که پیامبر و افرادی که ایمان آورده و همراه او بودند، گفتند: «پس یاری خدا کی خواهد آمد؟! آگاه باشید که یاری خداوند نزدیک است.»'),
(222, 2, 215, 'از تو می‌پرسند چه چیز انفاق کنند؟ بگو: «هر مالی که [می‌خواهید] انفاق کنید، برای پدر و مادر و نزدیکان و یتیمان و مستمندان و در راه ماندگان باشد. و [بدانید] هر کار خیری که انجام دهید، همانا خداوند به آن آگاه است.»'),
(223, 2, 216, 'جهاد [در راه خدا] بر شما مقرّر شد، در حالی که برای شما ناخوشایند است و چه بسا چیزی را خوش نداشته باشید، در حالی که خیر شما در آن است و چه بسا چیزی را دوست دارید، در حالی که ضرر و شرّ شما در آن است و خداوند [صلاح شما را] می‌داند و شما نمی‌دانید.'),
(224, 2, 217, '[ای پیامبر!] از تو درباره‌ی جنگ در ماه حرام سؤال می‌کنند، بگو: «جنگ در آن ماه‌ها گناهی بزرگ است، ولی بازداشتنِ [مردم] از راه خدا و کفر ورزیدنِ به او و [بازداشتن مردم از] مسجدالحرام و اخراج ساکنانش از آنجا، [گناهش] نزد خداوند بزرگتر است؛ [زیرا] فتنه‌انگیزی [و بیرون راندن مؤمنان از دیارشان] از قتل و کشتار بدتر است». [مشرکان] پیوسته با شما می‌جنگند، تا اگر بتوانند شما را از دینتان برگردانند، و هر کس از شما که از دینش برگردد و در حال کفر بمیرد، اعمال آنان در دنیا و آخرت تباه می‌شود و آنان اهل دوزخند و همیشه در آن خواهند بود.'),
(225, 2, 218, 'کسانی که ایمان آورده‌اند و کسانی که مهاجرت نموده و در راه خدا جهاد کرده‌اند، آنها به رحمت پروردگار امیدوار هستند و خداوند آمرزنده و مهربان است.'),
(226, 2, 219, 'از تو درباره شراب و قمار می‌پرسند، بگو: «در آن دو، گناه و زیان بزرگی است و منافعی [مادّی] نیز برای مردم دارند؛ و[لی] گناه آن دو از سودشان بیشتر است». [همچنین] از تو می‌پرسند که چه انفاق کنند؟ بگو: «افزون [بر نیاز خود را]» خداوند این چنین آیات را برای شما روشن می‌سازد تا شاید بیندیشید.'),
(227, 2, 220, 'درباره‌ی دنیا و آخرت [بیندیشید]. و از تو درباره‌ی یتیمان می‌پرسند، بگو: «اصلاح امور آنان بهتر است و اگر زندگی خود را با زندگی آنان بیامیزید، [مانعی ندارد،] آنها برادران شما هستند». خداوند، مفسد را از مصلح بازمی‌شناسد و اگر خدا می‌خواست، شما را در تنگنا می‌نهاد، [و دستور می‌داد در عین سرپرستی یتیمان، اموال آنها را از اموال خود جدا سازید، ولی خداوند چنین نمی‌کند.] همانا او توانا و حکیم است.'),
(228, 2, 221, 'و با زنان مشرک، ازدواج نکنید تا ایمان آورند. و قطعاً کنیز باایمان از زن [آزاد] مشرک بهتر است، هر چند [زیبایی او] شما را به شگفتی آورد. و به مردان مشرک، زن ندهید تا ایمان آورند و قطعاً برده‌ی باایمان از مرد [آزاد] مشرک بهتر است، هر چند [ثروت او] شما را به شگفتی وادارد. آن مشرکان [شما را] به سوی آتش دعوت می‌کنند، در حالی که خداوند با فرمان خود، شما را به بهشت و آمرزش فرامی‌خواند و آیات خویش را برای مردم روشن می‌کند تا شاید متذکر شوند.'),
(229, 2, 222, 'و از تو درباره حِیض [و عادت ماهانه‌ی زنان] سؤال می‌کنند، بگو: «آن، چیزی است رنج‌آور، پس در حال حیض از زنان کناره‌گیری کنید، و با آنها نزدیکی ننمایید تا پاک شوند. پس هنگامی که پاک شدند، آن‌گونه که خداوند به شما فرمان داده با آنها آمیزش کنید. خداوند هم توبه‌کنندگان را دوست دارد، و هم پاکان را.»'),
(230, 2, 223, 'زنان شما کشت‌زارِ شمایند، هر جا و هرگاه که بخواهید، به کشت‌زار خود درآیید و [با پرورش فرزندان صالح، اثر نیکی] برای خود، از پیش بفرستید. و از خدا پروا کنید و بدانید که او را ملاقات خواهید کرد، و به مؤمنان، بشارت ده!'),
(231, 2, 224, 'و خدا را دستاویز سوگندهای خود قرار مدهید تا [بدین بهانه‌] از نیکوکاری و پرهیزکاری و سازش دادنِ میان مردم شانه خالی کنید. همانا خداوند شنوا و داناست.'),
(232, 2, 225, 'خداوند، شما را به خاطر سوگندهای بیهوده و بدون قصد، بازخواست نمی‌کند، امّا به آنچه دل‌های شما قصد کرده، بازخواست می‌کند. و خداوند آمرزنده و بردبار است.'),
(233, 2, 226, 'کسانی که [به قصد آزار همسر،] سوگند می‌خورند که با زنان خویش نیامیزند، باید چهار ماه انتظار بکشند. پس اگر [از سوگند خود دست برداشته و به آشتی] برگشتند، خداوند آمرزنده و مهربان است.'),
(234, 2, 227, 'و اگر تصمیم به طلاق گرفتند، [بدانند که] خدا شنوا و داناست.'),
(235, 2, 228, 'زنانِ طلاق داده شده، باید تا سه مرتبه عادت ماهانه دیدن انتظار بکشند [و عدِّه نگه دارند]، و اگر به خدا و روز رستاخیز ایمان دارند، برای آنها روا نیست که آنچه را خدا در رحم آنها آفریده است، کتمان کنند. و شوهرانشان اگر سر آشتی دارند، به بازآوردن آنها در این مدّت، [از دیگران] سزاوارترند. و برای زنان در برابر وظایفی که بر عهده دارند، حقوق شایسته‌ای مقرّر گردیده است و برای مردان نسبت به آنان، [وظایف و حقوقی] افزون‌تر است و خداوند توانا و حکیم است.'),
(236, 2, 229, 'طلاق [قابل رجوع، حدّاکثر] دو مرتبه است. [و پس از آن] یا باید به طور شایسته همسر خود را نگه‌دارد و یا با نیکی او را رها کند و برای شما مردان روا نیست که چیزی از آنچه به همسرانتان داده‌اید، پس بگیرید، مگر این که دو همسر بترسند که حدود الهی را برپا ندارند. پس اگر بترسید که آنان حدود الهی را رعایت نکنند، مانعی ندارد که زن فدیه و عوضی بدهد، [و طلاق بگیرد.] اینها حدود و مرزهای الهی است، پس از آن تجاوز نکنید. و هر کس از حدود الهی تجاوز کند، ستمکار است.'),
(237, 2, 230, 'پس اگر [برای بار سوم] او را طلاق داد، دیگر آن زن بر او حلال نخواهد بود، مگر آن که به ازدواج مرد دیگری درآید، پس اگر [شوهر دوم] او را طلاق داد، مانعی ندارد که به یکدیگر بازگشت کنند؛ البتّه در صورتی که امید داشته باشند حدود الهی را برپا دارند. و اینها حدود الهی است که خداوند آن را برای افرادی که آگاهند، بیان می‌کند.'),
(238, 2, 231, 'و هنگامی که زنان را طلاق دادید و به پایان مهلتِ [عدِّه] رسیدند، پس یا به طرز شایسته آنها را نگاه دارید [و آشتی کنید] و یا آنها را به طرز پسندیده‌ای رها سازید. و برای آزار رسانیدن، آنان را نگاه ندارید تا [به حقوقشان] تجاوز کنید و کسی که چنین کند، به خویشتن ستم کرده است. و مبادا آیات خدا را به مسخره بگیرید. و نعمتی را که خداوند به شما داده و کتاب آسمانی و حکمتی را که بر شما نازل کرده و شما را با آن پند می‌دهد، به یاد بیاورید و از خدا پروا کنید و بدانید خداوند از هر چیزی آگاه است.'),
(239, 2, 232, 'و هنگامی که زنان را طلاق دادید و مهلتِ [عدّه] خود را به پایان رساندند، مانع آنها نشوید که با همسران [سابق] خویش ازدواج کنند، البتّه در صورتی که میان آنها به طرز پسندیده‌ای، توافق برقرار گردد. این موعظه‌ای است که تنها افرادی از شما، که به خدا و روز قیامت ایمان دارند، از آن پند می‌گیرند. این روش، مایه‌ی پاکی و پاکیزگیِ هر چه بیشتر شماست. و خداوند می‌داند و شما نمی‌دانید.'),
(240, 2, 233, 'مادرانی که می‌خواهند دوران شیردهی را کامل سازند، دو سالِ تمام فرزندانشان را شیر دهند. و خوراک و پوشاک این مادران، به وجهی نیکو بر عهده‌ی [پدر و] صاحب فرزند است. [البته] هیچ کس تکلیفی بیش از مقدار توانایی خود ندارد. هیچ مادری به خاطر فرزندش و نیز هیچ پدری به خاطر فرزندش نباید دچار ضرر شود. و [اگر پدر نباشد، هزینه مادر] بر عهده‌ی وارث اوست. و اگر پدر و مادر با توافق و مشورت یکدیگر بخواهند کودک را [زودتر از دو سال] از شیر بازگیرند، گناهی بر آنها نیست. و اگر خواستید دایه‌ای برای فرزندانتان بگیرید، گناهی بر شما نیست، به شرط این که آنچه را به وجهی پسندیده قرار گذاشته‌اید، بپردازید. و از [مخالفت فرمان] خدا بپرهیزید و بدانید خدا، به آنچه انجام می‌دهید، بیناست.'),
(241, 2, 234, 'و کسانی از شما که می‌میرند و همسرانی باقی می‌گذارند، آن زنان باید چهار ماه و ده روز انتظار بکشند و چون به پایان مهلت [عدّه]شان رسیدند، گناهی بر شما نیست که هر چه می‌خواهند، درباره‌ی خودشان به طور شایسته انجام دهند. [و با مرد دلخواه خود، ازدواج کنند.] و خداوند به آنچه عمل می‌کنید، آگاه است.'),
(242, 2, 235, 'و گناهی بر شما نیست که به طور کنایه [از زنانی که در عدّه هستند،] خواستگاری کنید یا [تصمیم خود را] در دل نهان دارید. خداوند می‌داند که شما آنها را یاد خواهید کرد، ولی با آنها وعده‌ی پنهانی [برای ازدواج] نداشته باشید، مگر آن که [به کنایه] سخن پسندیده بگویید، ولی [در هر حال] اقدام به عقد ازدواج ننمایید تا مدّت مقرّر به سر آید، و بدانید خداوند آنچه را که در دل دارید، می‌داند، پس از [مخالفت با] او بپرهیزید و بدانید خداوند آمرزنده و بردبار است.'),
(243, 2, 236, 'اگر زنان را قبل از آمیزش و یا تعیین مهریّه، [به عللی] طلاق دهید، گناهی بر شما نیست، ولی آنها را [با هدیه‌ای مناسب] بهره‌مند سازید. آن کس که توانایی دارد، به اندازه توانش و آن کس که تنگدست است، به اندازه‌ی وسعش، هدیه‌ای شایسته بدهد. این کار برای نیکوکاران سزاوار است.'),
(244, 2, 237, 'و اگر زنان را قبل از آمیزش طلاق دهید، در حالی که برای آنها مهریه تعیین کرده‌اید، نصف آنچه را تعیین کرده‌اید، [به آنها بدهید،] مگر این که آنها ببخشند و یا [ولیّ آنها، یعنی] کسی که عقد ازدواج به دست اوست، آن را ببخشد، و این که شما گذشت کنید [و تمام مهریه را بپردازید،] به پرهیزکاری نزدیک‌تر است و گذشت و نیکوکاری را در میان خود فراموش نکنید که خداوند به آنچه انجام می‌دهید، بیناست.'),
(245, 2, 238, 'بر انجام همه‌ی نمازها و [به ویژه] نماز وُسطی [ظهر]، مواظبت کنید و برای خدا خاضعانه به پاخیزید.'),
(246, 2, 239, 'پس اگر [از دشمن یا خطری دیگر] بیم داشتید، پیاده یا سواره [نماز گزارید]، و آن‌گاه که ایمِن شدید، [با خواندن نماز به صورت معمول] خدا را یاد کنید؛ آن‌گونه که خداوند به شما آموخته، آنچه را نمی‌دانسته‌اید.'),
(247, 2, 240, 'و کسانی از شما که در آستانه‌ی مرگ قرار می‌گیرند و همسرانی از خود به جای می‌گذارند، درباره‌ی همسرانشان این سفارش است که تا یک سال، آنها را [با پرداخت هزینه‌ی زندگی] بهره‌مند سازند و از خانه بیرون نکنند. ولی اگر آنها خود بیرون رفتند و تصمیم شایسته‌ای درباره‌ی خودشان گرفتند، بر شما گناهی نیست و خداوند توانا و حکیم است.'),
(248, 2, 241, 'و برای زنان طلاق داده شده، بهره [و هدیه] مناسبی است که بر مردانِ پرهیزکار سزاوار است.'),
(249, 2, 242, 'این چنین خداوند آیات خود را برای شما بیان می‌کند، شاید که بیندیشید!'),
(250, 2, 243, 'آیا ندیدی گروهی [از بنی‌اسرائیل] را که از ترس مرگ، [و به بهانه طاعون، از شرکت در میدان جهاد خودداری نمودند و] از خانه‌های خود فرار کردند، در حالی که هزاران نفر بودند، پس خداوند به آنها فرمود: «بمیرید!» سپس آنان را زنده کرد. همانا خداوند نسبت به مردم احسان می‌کند، ولی بیشتر مردم سپاس نمی‌گزارند.'),
(251, 2, 244, 'و در راه خداوند پیکار کنید و بدانید که خداوند شنوا و داناست.'),
(252, 2, 245, 'کیست که به خداوند وام دهد، وامی نیکو تا خداوند آن را برای او چندین برابر بیافزاید و خداوند [روزیِ بندگان را] محدود یا گسترده می‌سازد، و [سرانجام، همگی] به سوی او بازگردانده می‌شوید.'),
(253, 2, 246, 'آیا ندیدی گروهی از بزرگان بنی‌اسرائیل را که بعد از موسی، به پیامبر خود گفتند: «برای ما زمامدار [و فرماندهی] برانگیز تا [زیر فرمان او] در راه خدا پیکار کنیم.» [پیامبرشان] گفت: «اگر دستور جنگ به شما داده شود، [چه بسا] پیکار و جهاد ننمایید.» گفتند: «چگونه ممکن است در راه خدا پیکار نکنیم، در حالی که از سرزمین و [از نزد] فرزندانمان رانده شده‌ایم؟» پس چون دستور جنگ بر آنان مقرّر گشت، جز عدّه‌ی اندکی، همگی سرپیچی کردند. و خداوند به ظالمان آگاه است.'),
(254, 2, 247, 'و پیامبرشان به آنها گفت: «خداوند طالوت را برای زمامداری شما برگزیده است.» گفتند: «چگونه او بر ما حکومت کند، در حالی که ما از او به فرمانروایی شایسته‌تریم، و او ثروت زیادی ندارد.» پیامبرشان گفت: «خداوند او را بر شما برگزیده و توان علمی و جسمی او را افزون نموده است. و خداوند ملکش را به هر کس بخواهد، می‌بخشد. و خداوند [احسانش] وسیع و [به لیاقت‌ها و توانایی‌های افراد] آگاه است.»'),
(255, 2, 248, 'و پیامبرشان به آنها گفت: «نشانه‌ی حکومت او این است که تابوتِ [عهد] به سوی شما خواهد آمد، [همان صندوقی که] آرامشی از پروردگارتان و یادگاری از میراث خاندان موسی و هارون در آن است، [صندوق به سوی شما می‌آید،] در حالی که فرشتگان آن را حمل می‌کنند، در این موضوع، نشانه‌ی روشنی برای شماست، اگر ایمان داشته باشید.»'),
(256, 2, 249, 'پس هنگامی که طالوت، سپاهیان را با خود بیرون برد، به آنها گفت: «خداوند شما را به نهر آبی آزمایش می‌کند، پس هر که از آن بنوشد، از من نیست و هر کس از آن نخورد، از من است، مگر آن که با دست، مشتی برگیرد [و بیاشامد].» پس [همین که به نهر آب رسیدند،] جز اندکی همه از آن نوشیدند. سپس هنگامی که او و یاوران باایمانش از آن نهر گذشتند، [چون دشمن را دیدند، از کمیِ نفرات خود، ترسیدند و] گفتند: «امروز ما توانایی مقابله با جالوت و سپاهیان او را نداریم.» امّا آنان که اعتقاد داشتند خدا را [در روز قیامت] ملاقات خواهند کرد، گفتند: «چه بسا گروهی اندک که با اذن خدا بر گروهی بسیار پیروز شدند.» و خداوند با صابران [و استقامت‌کنندگان] است.'),
(257, 2, 250, 'و هنگامی که در برابر جالوت و سپاهیان او قرار گرفتند، گفتند: «پروردگارا! صبر و شکیبایی بر ما فرو ریز و قدم‌های ما را ثابت و استوار بدار و ما را بر گروه کافران پیروز فرما!»'),
(258, 2, 251, 'سپس طالوت و یارانش به اذن خداوند، سپاه دشمن را در هم شکستند و داوود، جالوت را [که فرمانده سپاهِ دشمن بود،] کشت و خداوند حکومت و حکمت به او عطا نمود و از آنچه می‌خواست به او آموخت. و اگر خداوند [فسادِ] بعضی از مردم را به وسیله‌ی بعضی دیگر دفع نمی‌کرد، قطعاً زمین را فساد فرامی‌گرفت. ولی خداوند نسبت به جهانیان، لطف و احسان دارد.'),
(259, 2, 252, 'اینها، آیات خداوند است که به حقّ بر تو می‌خوانیم و به راستی تو از فرستادگان الهی هستی.'),
(260, 2, 253, 'ما بعضی از آن پیامبران را بر بعضی دیگر برتری دادیم. خداوند با برخی از آنان سخن گفت و درجات بعضی از آنان را بالا برد، و به عیسی‌بن‌مریم نشانه‌های روشن دادیم و او را با روح‌القدس تأیید نمودیم، و اگر خدا می‌خواست، [امّت این پیامبران] بعد از آنان به جنگ و ستیز با هم برنمی‌خاستند، با آن که نشانه‌های روشن برای آنها آمده بود؛ ولی با هم اختلاف کردند، پس برخی از آنان ایمان آورده و بعضی کافر شدند و اگر خدا می‌خواست [مؤمنان و کافران] با هم پیکار نمی‌کردند، ولی خداوند آنچه را اراده کند، انجام می‌دهد. [و اراده‌ی حکیمانه‌ی او بر آزاد گذاردن مردم است.]'),
(261, 2, 254, 'ای کسانی که ایمان آورده‌اید! از آنچه به شما روزی داده‌ایم، انفاق کنید، پیش از آن که روزی فرارسد که در آن، نه داد و ستدی باشد، نه دوستی و نه شفاعتی. و کافران همان ستمکارانند.'),
(262, 2, 255, 'اللّه که جز او معبودی نیست، زنده و پاینده است. نه خواب سبک او را فرامی‌گیرد و نه خواب سنگین. [و لحظه‌ای از تدبیر جهان هستی، غافل نمی‌ماند.] آنچه در آسمان‌ها و آنچه در زمین است، از آنِ اوست. کیست آن که جز به اذن او در پیشگاهش شفاعت کند؟ گذشته و آینده‌ی همگان را می‌داند. و کسی به چیزی از علم او احاطه پیدا نمی‌کند مگر به مقداری که او بخواهد. اَریکه‌ی [حکومت] او آسمان‌ها و زمین را فراگرفته و حفظ و نگاهداشت آنها بر وی دشوار نیست. و او والا و بزرگ است.'),
(263, 2, 256, 'در [پذیرش] دین، هیچ اجباری نیست. راه رشد از گمراهی روشن شده است، پس هر که به طاغوت کافر شود و به خداوند ایمان آورد، قطعاً به دستگیره‌ی محکمی دست یافته، که گسستنی برای آن نیست. و خداوند شنوای داناست.'),
(264, 2, 257, 'خداوند دوست و سرپرست مؤمنان است، آنها را از تاریکی‌ها خارج ساخته، به سوی نور می‌برد. ولی سرپرستان کفّار، طاغوت‌ها هستند که آنان را از نور به تاریکی‌ها سوق می‌دهند، آنها اهل آتشند و همانان همواره در آن خواهند بود.'),
(265, 2, 258, 'آیا نظر نکردی به [حال نمرود،] کسی که خداوند به او پادشاهی داده بود، ولی او با ابراهیم درباره‌ی پروردگارش گفتگو و ستیزه می‌کرد؟ آن‌گاه که ابراهیم گفت: «خدای من همان کسی است که زنده می‌کند و می‌میراند.» او گفت: «من هم زنده می‌کنم و می‌میرانم.» ابراهیم گفت: «خداوند خورشید را از مشرق می‌آورد، پس تو آن را از مغرب برآور!» آن مرد کافر مبهوت و درمانده شد. و خداوند قوم ستمگر را هدایت نمی‌کند.'),
(266, 2, 259, 'یا همانند [عُزَیر،] کسی که از کنار یک آبادی عبور کرد، در حالی که دیوارها و سقف‌هایش فروریخته بود. [او با خود] گفت: «خداوند چگونه اینها را پس از مرگ زنده می‌کند؟»پس خداوند او را یکصد سال میراند و سپس زنده کرد، [و به او] فرمود: «چقدر درنگ کردی؟» گفت: «یک روز، یا قسمتی از یک روز!» فرمود: «بلکه یکصد سال دِرنگ کرده‌ای، به غذا و نوشیدنی خود نگاه کن که تغییر نیافته است. و به الاغ خود نگاه کن [که متلاشی شده است، این ماجرا برای آن است که هم به تو پاسخ گوییم] و [هم] تو را نشانه [رستاخیز] و حجّتی برای مردم قرار دهیم. [اکنون] به استخوان‌ها[ی مرکب خود] بنگر که چگونه آنها را به‌ هم پیوند می‌دهیم و بر آن گوشت می‌پوشانیم.» پس هنگامی که [این حقایق] آشکار شد، گفت: «[اکنون با تمام وجود] می‌دانم که خداوند بر هر کاری تواناست.»'),
(267, 2, 260, 'و هنگامی که ابراهیم گفت: «پروردگارا! به من نشان بده که چگونه مردگان را زنده می‌کنی؟» فرمود: «مگر ایمان نیاورده‌ای؟» گفت: «چرا، ولی برای آن که قلبم آرامش یابد.» [خداوند] فرمود: «چهار پرنده [متفاوت] را برگیر و آنها را نزد خود جمع و قطعه قطعه کن [و در هم بیامیز]، سپس بر هر کوهی قسمتی از آنها را قرار ده، آن‌گاه پرندگان را بخوان، به سرعت به سوی تو می‌آیند. و بدان که خداوند توانای حکیم است.»'),
(268, 2, 261, 'مَثَل کسانی که اموال خود را در راه خدا انفاق می‌کنند، همانند بذری است که هفت خوشه برویاند و در هر خوشه یکصد دانه باشد و خداوند آن را برای هر کس بخواهد دو یا چند برابر می‌کند و خدا وسعت‌بخش داناست.'),
(269, 2, 262, 'کسانی که اموال خود را در راه خدا انفاق می‌کنند و سپس به دنبال آنچه انفاق کرده‌اند، منّت نمی‌گذارند و آزاری نمی‌رسانند، پاداش آنها نزد پروردگارشان [محفوظ] است و نه ترسی دارند و نه غمگین می‌شوند.'),
(270, 2, 263, 'گفتار پسندیده و گذشت [از تندی نیازمندان]، بهتر از صدقه‌ای است که آزاری به دنبال آن باشد. و خدا بی‌نیاز بردبار است.'),
(271, 2, 264, 'ای کسانی که ایمان آورده‌اید! بخشش‌های خود را با منّت و آزار باطل نسازید، همانند کسی که مال خود را برای خودنمایی، به مردم، انفاق می‌کند و به خدا و روز رستاخیز ایمان ندارد. پس مثال او همچون مثل قطعه سنگ صافی است که بر روی آن خاک باشد [و بذرهایی بر آن افشانده شود]، پس رگباری به آن رسد و آن سنگ را صاف [و خالی از خاک و بذر] رها کند! [ریاکاران نیز] از دستاوردشان، هیچ بهره‌ای نمی‌برند و خداوند گروه کافران را هدایت نمی‌کند.'),
(272, 2, 265, 'و مَثَل کسانی که اموال خود را در طلب رضای خداوند و استواری روح خود انفاق می‌کنند، همچون مَثل باغی است که در نقطه‌ای بلند باشد و باران‌های درشت به آن برسد و میوه‌ی خود را دو چندان بدهد. و اگر باران درشتی نبارد، باران‌های ریز و شبنم [به آن می‌رسد تا همیشه، شاداب و پرطراوت باشد.] و خداوند به آنچه انجام می‌دهید، بیناست.'),
(273, 2, 266, 'آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد که از زیر درختان آن، نهرها بگذرد و برای او در آن [باغ] از هرگونه میوه‌ای وجود داشته باشد و در حالی که به سن پیری رسیده و فرزندانی [کوچک و] ناتوان دارد، گردبادی [شدید،] همراه با آتش سوزان به آن باغ بوزد و باغ را یکسره بسوزاند؟ این چنین خداوند آیات را برای شما بیان می‌کند، شاید بیندیشید!'),
(274, 2, 267, 'ای کسانی که ایمان آورده‌اید! از اموال پاک و حلالی که به دست آورده‌اید و از آنچه ما برای شما از زمین رویانیده‌ایم، انفاق کنید و برای انفاق به سراغ چیز‌های ناپاک [و پست] نروید، در حالی که خود شما هم حاضر نیستید آنها را بپذیرید، مگر از روی چشم‌پوشی [و ناچاری]. و بدانید که خداوند بی‌نیاز و ستوده است.'),
(275, 2, 268, '[به هنگام انفاق،] شیطان شما را از فقر و تهیدستی بیم می‌دهد و شما را به فحشا و زشتی‌ها فرامی‌خواند، ولی خداوند از جانب خود به شما وعده آمرزش و فزونی می‌دهد و خدا وسعت‌بخش داناست.'),
(276, 2, 269, '[خداوند] حکمت و بینش را به هر کس بخواهد [و شایسته ببیند]، می‌دهد. و به هر کس حکمت داده شود، خیری فراوان به او داده شده است. و جز خردمندان کسی پند نمی‌گیرد.'),
(277, 2, 270, 'و هر مالی را که انفاق کرده‌اید، یا نذری را که عهد نموده‌اید، پس قطعاً خداوند آن را می‌داند و برای ستمگران [ریاکار و منّت‌گذار،] هیچ یاوری نیست.'),
(278, 2, 271, 'اگر صدقات را آشکارا بدهید، کارِ خوبی است، ولی اگر آنها را پنهان دارید و به نیازمندان بدهید، این برای شما بهتر است و بخشی از گناهان شما را می‌زداید. و خداوند به آنچه انجام می‌دهید، آگاه است.'),
(279, 2, 272, 'هدایت یافتن آنان بر عهده‌ی تو نیست، بلکه خداوند هر که را بخواهد [و شایسته باشد،] هدایت می‌کند. [بنابراین، ترک انفاق به بینوایان غیر مسلمان، برای اجبار آنان به اسلام، صحیح نیست،] و هر مالی که انفاق کنید، به نفع خودتان است و جز برای کسب رضای خدا انفاق نکنید. و هر مالی که انفاق کنید، [پاداش] آن را به طور کامل دریافت می‌کنید و بر شما ستم نخواهد شد.'),
(280, 2, 273, '[انفاق و صدقه] از آنِ نیازمندانی است که در راه خدا در تنگنا قرار گرفته‌اند. [از وطن آواره شده یا در جبهه‌های جهاد هستند.] نمی‌توانند [برای تأمین هزینه‌ی زندگی] سفر کنند. از شدّت عفاف و آبروداری، شخص بی‌اطّلاع آنها را بی‌نیاز می‌پندارد، امّا تو آنها را از چهره‌هایشان می‌شناسی. آنان هرگز با اصرار از مردم چیزی نمی‌خواهند. و هر مالی [به آنان] انفاق کنید، قطعاً خداوند به آن آگاه است.'),
(281, 2, 274, 'کسانی که اموال خود را در شب و روز، پنهان و آشکارا انفاق می‌کنند، اجر و پاداششان نزد پروردگارشان است، و نه ترسی برای آنهاست و نه غمگین می‌شوند.'),
(282, 2, 275, 'کسانی که ربا می‌خورند، از جا برنمی‌خیزند، مگر همانند برخاستن کسی که بر اثر تماس شیطان، آشفته و دیوانه شده [، تعادل خود را از دست داده] است. این [آسیب] بدان سبب است که گفتند: «خرید و فروش مانند رباست.» در حالی که خداوند خرید و فروش را حلال، و ربا را حرام کرده است. پس هر کس که موعظه‌ای از پروردگارش به او رسید و [از رباخواری] خودداری کرد، آنچه در گذشته [به دست آورده] مال اوست، و کار او به خدا واگذار می‌شود. امّا کسانی که [به رباخواری] بازگردند، آنان اهل آتش خواهند بود و در آن جاودانه می‌مانند.'),
(283, 2, 276, 'خداوند ربا را نابود می‌کند، ولی صدقات را افزایش می‌دهد و خداوند هیچ انسان ناسپاس و کافر گنهکاری را دوست نمی‌دارد.'),
(284, 2, 277, 'کسانی که ایمان آورده و کارهای نیکو انجام داده‌اند و نماز برپا داشته و زکات پرداخته‌اند، پاداششان نزد پروردگارشان [محفوظ] است و نه ترسی بر آنهاست و نه غمگین می‌شوند.'),
(285, 2, 278, 'ای کسانی که ایمان آورده‌اید! تقوای الهی پیشه کنید و آنچه را از ربا باقی مانده است، رها کنید، اگر ایمان دارید.'),
(286, 2, 279, 'پس اگر چنین نکردید، با خدا و فرستاده‌اش به جنگ برخاسته‌اید و اگر توبه کنید، [اصل] سرمایه‌های شما از آنِ خودتان است. نه ستم می‌کنید و نه بر شما ستم می‌شود.'),
(287, 2, 280, 'و اگر [بدهکار] تنگدست بود، او را تا هنگام توانایی مهلت دهید و [اگر توان پرداخت ندارد،] اگر ببخشید برای شما بهتر است، اگر [نتایج آن را] بدانید.'),
(288, 2, 281, 'و بترسید از روزی که در آن به پیشگاه خداوند بازگردانده می‌شوید و سپس به هر کس [جزای] آنچه را کسب کرده، بی‌کم و کاست داده می‌شود و به آنان ستمی نخواهد شد.'),
(289, 2, 282, 'ای کسانی که ایمان آورده‌اید! هنگامی که بدهیِ مدّت‌داری [بر اثر وام یا معامله] به یکدیگر پیدا می‌کنید، آن را بنویسید. و باید نویسنده‌ای در میان شما به عدالت [سند را] بنویسد. کسی که توان نوشتن دارد، نباید از نوشتن خودداری کند، همان طور که خدا به او تعلیم داده است. پس باید او بنویسد و آن کس که بدهکار است، املا کند و از خدایی که پروردگار اوست، بپرهیزد و چیزی از آن فروگذار ننماید و اگر بدهکار، سفیه یا ضعیف بود و یا توانایی املا کردن نداشت، باید ولیّ او [به جای او] با رعایت عدالت [مدّت و مقدار بدهی را] املا کند و دو نفر از مردان را شاهد بگیرد و اگر دو مرد نبودند، یک مرد و دو زن از گواهانی که مورد رضایت و اطمینان شما هستند، [انتخاب کنید] تا اگر یکی از آنان فراموش کرد، دیگری به او یادآوری کند و شهود نباید هنگامی که آنها را [برای ادای شهادت] دعوت می‌کنند، خودداری نمایند. و از نوشتن [بدهی‌های] کوچک یا بزرگِ مدّت‌دار، ملول نشوید، این در نزد خدا به عدالت نزدیک‌تر، و برای ادای شهادت استوارتر و برای جلوگیری از شک و تردید بهتر است، مگر این که تجارت و داد و ستد، نقدی باشد که در میان خود دست به دست می‌کنید. در این صورت، ایرادی بر شما نیست که آن را ننویسید. ولی هنگامی که خرید و فروش [نقدی] می‌کنید، [باز هم] شاهد بگیرید و نباید به نویسنده و شاهد [به خاطر حقّ‌گویی] زیانی برسد و اگر چنین کنید از فرمان خداوند خارج شده‌اید. و از [مخالفت فرمان] خدا بپرهیزید و خداوند [راه درست زندگی را] به شما تعلیم می‌دهد و خداوند به هر چیزی داناست.'),
(290, 2, 283, 'و اگر در سفر بودید و نویسنده‌ای نیافتید، وثیقه‌ای بگیرید و اگر به یکدیگر اطمینان داشتید، پس [وثیقه لازم نیست و] کسی که امین شمرده شده، امانت [و بدهی خود را به موقع] بپردازد و از خدایی که پروردگار اوست، پروا کند. و شهادت را کتمان نکنید و هر کس آن را کتمان کند، بی‌شک قلبش گناهکار است. و خداوند به آنچه انجام می‌دهید، داناست.'),
(291, 2, 284, 'آنچه در آسمان‌ها و آنچه در زمین است، تنها از آنِ خداست. اگر آنچه را در دل دارید، آشکار سازید یا پِنهان دارید، خداوند شما را در برابر آن بازخواست می‌کند. پس هر که را بخواهد، [و شایستگی داشته باشد،] می‌بخشد و هر که را بخواهد، [و مستحقّ باشد،] مجازات می‌کند و خداوند بر هر چیزی تواناست.'),
(292, 2, 285, 'پیامبر به آنچه از سوی پروردگارش بر او نازل شده، ایمان آورده است و همه مؤمنان [نیز] به خدا و فرشتگان و کتاب‌ها و فرستادگانش ایمان آورده‌اند [و گفتند]: «ما میان هیچ یک از پیامبران او فرق نمی‌گذاریم [و به همگی ایمان داریم].» و گفتند: «ما [ندای حقّ را] شنیدیم و اطاعت کردیم. پروردگارا! آمرزش تو را [خواهانیم] و بازگشت [ما] به سوی توست.»'),
(293, 2, 286, 'خداوند هیچ کس را جز به اندازه توانایی‌اش تکلیف نمی‌کند، [انسان،] هر کار [نیکی] را انجام دهد، به سود خود انجام داده و هر کار [بدی] کند، به زیان خود کرده است. [مؤمنان می‌گویند:] «پروردگارا! اگر [تکلیفی را] فراموش کردیم یا به خطا رفتیم، ما را مؤاخذه مکن! پروردگارا! تکلیف سنگینی بر ما قرار مده، آن چنان که [به خاطر گناه] بر کسانی که پیش از ما بودند، قرار دادی. پروردگارا! آنچه که طاقت تحمّل آن را نداریم، بر ما مقرّر مکن و از ما درگذر و ما را بیامرز و در رحمت خود قرار ده! تو مولا و سرپرست مایی، پس ما را بر گروه کافران پیروز گردان!»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف، لام، میم.'),
(295, 3, 2, 'خداوند که معبودی جز او نیست، زنده و پاینده است.'),
(296, 3, 3, 'او کتاب را به حقّ بر تو نازل کرد، که آنچه را در کتاب‌های پیشین [از نشانه‌های آن] آمده است، تصدیق می‌کند و تورات و انجیل را فروفرستاد.'),
(297, 3, 4, 'پیش از این برای هدایت مردم، [تورات و انجیل] و اینک [قرآن را، که کتابِ] فرقان [و جداکننده‌ی حقّ از باطل است،] نازل کرد. بی‌گمان کسانی که به آیات الهی کفر ورزند، عذاب شدیدی خواهند داشت و خداوند شکست‌ناپذیرِ صاحبِ انتقام است.'),
(298, 3, 5, 'هیچ چیز، نه در زمین و نه در آسمان، بر خداوند پوشیده نمی‌ماند.'),
(299, 3, 6, 'او کسی است که شما را آن‌گونه که می‌خواهد در رحم‌ها[ی مادران]، صورت می‌دهد. جز او که مقتدر حکیم است، معبودی نیست.'),
(300, 3, 7, 'او کسی است که این کتاب را بر تو نازل کرد. بخشی از آن، آیات محکم است که اصل و اساس کتاب را تشکیل می‌دهد و بخشی دیگر، آیات متشابه است. امّا کسانی که در دل‌هایشان انحراف است، به جهت ایجاد فتنه [و گمراه کردن مردم] و نیز به جهت تفسیر آیه به دلخواه خود، به سراغ آیات متشابه می‌روند، در حالی که تفسیر این آیات را جز خداوند و راسخان در علم نمی‌دانند. آنان که می‌گویند: «ما به آن ایمان آورده‌ایم، همه‌ی آیات از طرف پروردگار ماست، [خواه محکم باشد یا متشابه].» و جز خردمندان پند نگیرند.'),
(301, 3, 8, '[راسخان در علم می‌گویند:] «پروردگارا! پس از آن که ما را هدایت کردی، دل‌های ما را به باطل مایل مگردان و از سوی خود رحمتی بر ما ببخش. به راستی که تو خود بسیار بخشنده‌ای.'),
(302, 3, 9, 'پروردگارا! تو مردم را در روزی که در آن شکی نیست، جمع خواهی کرد. همانا خداوند از وعده‌ی خود تخلّف نمی‌کند.»'),
(303, 3, 10, 'اموال و فرزندان کسانی که کفر ورزیدند، در برابر [عذاب] خدا [در قیامت] هیچ به کارشان نمی‌آید و آنان خود، هیزم دوزخند.'),
(304, 3, 11, '[شیوه‌ی کفّار در انکار و تحریف حقایق،] مانند روش فرعونیان و کسانی است که پیش از آنها بودند. آیات ما را تکذیب کردند، پس خداوند آنها را به [کیفر] گناهانشان گرفت. و خدا سخت‌کیفر است.'),
(305, 3, 12, 'به کسانی که کافر شدند بگو: «[از پیروزی خود در جنگ اُحُد، شاد نباشید،] به زودی شکست می‌خورید و به سوی جهنّم رانده می‌شوید. و چه بد جایگاهی است!»'),
(306, 3, 13, 'به یقین در دو گروهی که [در جنگ بدر] با هم روبه‌رو شدند، برای شما نشانه [و درس عبرتی] بود: گروهی در راه خدا نبرد می‌کردند و گروه دیگر که کافر بودند، به چشم خود مسلمانان را دو برابر می‌دیدند [و این عاملی برای ترس آنان می‌شد] و خداوند هر کس را بخواهد، به یاری خود تأیید می‌کند. در این امر برای اهل بینش، پند و عبرت است.'),
(307, 3, 14, 'عشق و علاقه به زنان و فرزندان و اموال زیاد از طلا و نقره و اسبان ممتاز و چهارپایان و کشت‌زارها که همه از شهوات و خواسته‌های نفسانی است، در نظر مردم جلوه یافته است، [در حالی که] اینها بهره‌ای گذرا از زندگانی دنیاست و سرانجامِ نیکو تنها نزد خداوند است.'),
(308, 3, 15, 'بگو: «آیا شما را به چیزی بهتر از اینها خبر دهم؟ برای کسانی که تقوا داشته باشند، نزد پروردگارشان باغ‌هایی [بهشتی] است که از زیر [درختان] آن نهرها جاری است. برای همیشه در آنجا [بهره‌مند] هستند. و همسرانی پاک [خواهند داشت] و رضا و خشنودی خداوند [شامل حالشان می‌شود]. و خداوند به حال بندگان بیناست.»'),
(309, 3, 16, '[همان] کسانی که می‌گویند: «پروردگارا! به راستی که ما ایمان آورده‌ایم، پس گناهان ما را ببخش و ما را از عذاب دوزخ، نگاه دار!»'),
(310, 3, 17, '[آنان] شکیبا و راستگویند و [در برابر خدا] فرمانبردارِ فروتن هستند و [در راه او] انفاق می‌کنند و در سحرگاهان از خداوند، آمرزش می‌طلبند.'),
(311, 3, 18, 'خدایی که همواره به عدل و قسط قیام دارد، گواهی داده که معبودی جز او نیست و فرشتگان و صاحبان دانش [نیز به یگانگی او گواهی داده‌اند]، جز او که مقتدر حکیم است، معبودی نیست.'),
(312, 3, 19, 'دینِ [مورد پذیرش] نزد خداوند، اسلام است و اهل کتاب اختلافی نکردند، مگر بعد از آن که علم [به حقّانیت اسلام] برایشان حاصل شد، [این اختلاف] از روی حسادت و دشمنی میان آنان بود. و هرکس به آیات خداوند کفر ورزد، پس [بداند که] خدا زودحساب است.'),
(313, 3, 20, 'پس اگر با تو به گفتگو و ستیز و محاجِّه برخاستند، بگو: «من و پیروانم با تمام وجود در برابر خداوند تسلیم شده‌ایم.» و به اهل کتاب و اُمیین [مشرکان بی‌سواد مکه] بگو: «آیا شما هم تسلیم شده‌اید؟» پس اگر تسلیم شدند و اسلام آوردند، هدایت یافته‌اند، و اگر سرپیچی کردند، وظیفه تو فقط رساندن دعوت الهی است، [نه اجبار و اکراه،] و خداوند به حال بندگان بیناست.'),
(314, 3, 21, 'به راستی کسانی که به آیات خداوند کفر می‌ورزند و پیامبران را به ناحقّ می‌کشند و مردمی را که فرمان به عدالت می‌دهند، به قتل می‌رسانند، پس آنان را به عذابی دردناک بشارت ده!'),
(315, 3, 22, 'آنان کسانی هستند که اعمالشان در دنیا و آخرت تباه شده و هیچ یاوری ندارند.'),
(316, 3, 23, 'آیا ندیدی کسانی را که از کتاب [تورات] بهره‌ای داشتند؟ چون به کتاب الهی دعوت می‌شوند تا میانشان حکم کند، گروهی از آنان روی می‌گردانند، در حالی که [از قبول حقّ] ابا دارند.'),
(317, 3, 24, 'این [روی‌گردانی] برای آن بود که اهل کتاب می‌گفتند: «هرگز آتش دوزخ جز چند روزی به ما نمی‌رسد.» و این افتراها [و خیال‌بافی‌ها] آنان را در دینشان فریفته ساخت. [و گرفتار انواع گناهان شدند.]'),
(318, 3, 25, 'پس [کسانی که گمان می‌کنند از قهر خداوند دورند،] چگونه خواهد بود حالشان، آن‌گاه که آنان را در روزی که شکی در آن نیست، گرد آوریم و به هر کس [پاداش یا کیفر] آنچه انجام داده، به طور کامل داده شود؟ و به آنان ستم نخواهد شد.'),
(319, 3, 26, 'بگو: «خداوندا! تو صاحب فرمان و سلطنتی! به هر کس بخواهی، [طبق مصلحت و حکمت خود] حکومت می‌دهی و از هر کس بخواهی، حکومت را می‌گیری و هر که را بخواهی، عزّت می‌بخشی و هر که را بخواهی، خوار می‌کنی. همه خیرها تنها به دست توست. تو بر هر چیز توانایی!'),
(320, 3, 27, 'تو شب را به روز و روز را به شب درآوری و زنده را از مرده و مرده را از زنده بیرون آوری و به هر کس بخواهی، بی‌شمار روزی می‌دهی.»'),
(321, 3, 28, 'نباید اهل ایمان، به جای مؤمنان، کافران را دوست و سرپرست خود برگزینند و هر کس چنین کند، نزد خدا هیچ ارزشی ندارد، مگر آن که از کفّار تقیه کنید و خداوند شما را از [نافرمانی] خود بر حذر می‌دارد و بازگشت [شما] به سوی خداست.'),
(322, 3, 29, 'بگو: «اگر آنچه در سینه‌ها دارید، پنهان سازید یا آشکار نمایید، خداوند آن را می‌داند و [نیز] آنچه را در آسمان‌ها و آنچه را در زمین است، می‌داند و خدا بر هر چیز تواناست.»'),
(323, 3, 30, 'روزی که هر کس، هر کار نیکی انجام داده، حاضر بیابد و آرزو می‌کند ای کاش بین او و هر چه بدی کرده، فاصله‌ای دور می‌بود. و خداوند شما را از [مخالفت امرش] بر حذر می‌دارد و [در عین حال] خداوند به بندگان مهربان است.'),
(324, 3, 31, '[ای پیامبر!] بگو: «اگر خداوند را دوست می‌دارید، پس از من پیروی کنید، تا خدا نیز شما را دوست بدارد و گناهانتان را بر شما ببخشد. و خداوند بسیار بخشنده و مهربان است.»'),
(325, 3, 32, 'بگو: «خدا و رسول را اطاعت کنید.» پس اگر سرپیچی کردند، [بدانید که] خداوند کافران را دوست نمی‌دارد.'),
(326, 3, 33, 'به راستی که خداوند، آدم و نوح و خاندان ابراهیم و خاندان عمران را بر جهانیان برتری داد.'),
(327, 3, 34, 'نسلی [پاک] که برخی از آنها از [نسل] برخی دیگرند و خداوند شنوای داناست.'),
(328, 3, 35, 'هنگامی که همسر عمران گفت: «پروردگارا! من نذر کرده‌ام آنچه را در رحم دارم، برای تو آزاد باشد [و هیچ مسئولیتی به او نسپارم تا تمام وقت خود را صرف خدمت در بیت‌المقدّس کند،] پس از من قبول فرما، که به راستی تو شنوای دانایی!»'),
(329, 3, 36, 'پس چون فرزند را به دنیا آورد، گفت: «پروردگارا! من دختر زاده‌ام.» در حالی که خداوند به آنچه او زاده داناتر بود. «و پسر مانند دختر نیست و من او را مریم نامیدم و او و فرزندانش را از [شرّ] شیطان رانده شده، در پناه تو قرار می‌دهم.»'),
(330, 3, 37, 'پس پروردگارش او را به وجه نیکو پذیرفت و به نحو شایسته پرورش داد و سرپرستی او را به زکریا سپرد. هرگاه زکریا در محرابِ عبادت بر مریم وارد می‌شد، غذای مخصوصی نزد او می‌یافت. [روزی] گفت: «ای مریم! این روزی از کجا نصیب تو شده؟!» او در پاسخ گفت: «آن از نزد خداست. خداوند به هر کس که بخواهد، روزیِ بی‌شمار می‌دهد.»'),
(331, 3, 38, 'در این هنگام زکریا پروردگارش را خواند و گفت: «پروردگارا! از جانب خود نسلی پاک و پسندیده به من [نیز] عطا کن که تو دعا را می‌شنوی.»'),
(332, 3, 39, 'پس در حالی که زکریا در محراب به نماز ایستاده بود، فرشتگان او را ندا دادند که: «خداوند تو را به یحیی بشارت می‌دهد که تصدیق‌کننده‌ی [مسیح،] کلمة اللّه است و سید و سرور، و خویشتن‌دار و پیامبری از صالحان است.»'),
(333, 3, 40, '[زکریا] گفت: «پروردگارا! چگونه برای من پسری خواهد بود؟ در حالی‌که پیریِ من فرارسیده و همسرم نازاست!» [خداوند] فرمود: «این چنین است که خداوند هر چه را بخواهد، انجام می‌دهد.»'),
(334, 3, 41, '[زکریا] گفت: «پروردگارا! برای من نشانه‌ای قرار ده». [خداوند] فرمود: «نشانه تو آن است که تا سه روز [زبان تو، از کار می‌افتد و] با مردم سخن نخواهی گفت، مگر با اشاره و رمز. [البتّه به هنگام ذکر خدا زبانت باز می‌شود. پس] پروردگار خود را بسیار یاد کن و شب‌‌هنگام و صبحگاه او را تسبیح بگو.»'),
(335, 3, 42, 'و هنگامی که فرشتگان گفتند: «ای مریم! خدا تو را برگزیده و پاک ساخته و بر زنان جهان برتری داده است.'),
(336, 3, 43, 'ای مریم! برای پروردگارت خضوع کن و سجده به جای آور و با رکوع‌کنندگان رکوع نما!»'),
(337, 3, 44, '[ای پیامبر!] اینها از خبرهای غیبی است که ما به تو وحی می‌کنیم، حال آن که تو نزد آنان نبودی، آن‌گاه که قلم‌های خود را [برای قرعه‌کشی] می‌افکندند تا کدام یک کفالت و سرپرستیِ مریم را بر عهده بگیرد. و نزد آنها نبودی آن‌گاه که [برای کسب افتخار سرپرستی مریم] با هم کشمکش می‌کردند.'),
(338, 3, 45, '[به یاد آور] هنگامی که فرشتگان گفتند: «ای مریم! خداوند تو را به کلمه و نشانه‌ای از سوی خویش بشارت می‌دهد که نامش مسیح، عیسی پسر مریم است، او که در دنیا و آخرت آبرومند و از مقرّبان است.'),
(339, 3, 46, 'در گهواره، [به اعجاز] و در میان‌سالی [به وحی] با مردم سخن می‌گوید و از شایستگان است.»'),
(340, 3, 47, '[مریم] گفت: «پروردگارا! چگونه ممکن است برای من فرزندی باشد، در حالی که هیچ انسانی با من تماس نداشته است؟» خداوند فرمود: «چنین است [کارِ] پروردگار! او هر چه را بخواهد می‌آفریند. هرگاه اراده کاری کند، فقط به آن می‌گوید: باش، پس موجود می‌شود.»'),
(341, 3, 48, 'و خداوند به او کتاب و حکمت و تورات و انجیل را می‌آموزد.'),
(342, 3, 49, 'و [عیسی را به] پیامبری به سوی بنی‌اسرائیل [فرستاد تا بگوید]: «من از سوی پروردگارتان برای شما نشانه‌ای آورده‌ام. من برای شما از گِل، [مجسّمه‌ای] به شکل پرنده می‌سازم، سپس در آن می‌دمم، پس به اراده و اذن خداوند پرنده‌ای می‌گردد. و نیز با اذن خدا کور مادرزاد و مبتلایان به پیسی را بهبود می‌بخشم و مردگان را زنده می‌کنم و از آنچه می‌خورید و آنچه در خانه‌هایتان ذخیره می‌کنید به شما خبر می‌دهم، به راستی اگر ایمان داشته باشید، در این معجزات برای شما نشانه و عبرتی است.'),
(343, 3, 50, 'من تصدیق‌کننده‌ی توراتی هستم که پیش روی من است و [آمده‌ام] تا برخی از چیزهایی را که بر شما [به عنوان تنبیه] حرام شده بود، برایتان حلال کنم. و از جانب پروردگارتان برای شما نشانه‌ای آورده‌ام. پس، از خداوند پروا و از من اطاعت کنید.'),
(344, 3, 51, 'به راستی که خداوند، پروردگار من و پروردگار شماست، پس او را بپرستید که این راه مستقیم است.»'),
(345, 3, 52, 'پس چون عیسی از بنی‌اسرائیل احساس کفر کرد، گفت: «کیست که یاور من [در حرکت] به سوی خدا شود؟» حواریون گفتند: «ما یاوران [دین] خداییم، به خداوند ایمان آورده‌ایم، و تو گواهی ده که ما تسلیم [خدا] هستیم.'),
(346, 3, 53, 'پروردگارا! به آنچه نازل کرده‌ای، ایمان آوردیم و از فرستاده [تو] پیروی نمودیم، پس ما را در زمره گواهان بنویس.»'),
(347, 3, 54, 'و [یهود برای نابودی عیسی و آیینش] توطئه کردند. خداوند نیز [برای حفظ او و آیینش] تدبیر نمود. و خداوند بهترین تدبیرکننده است.'),
(348, 3, 55, 'هنگامی که خدا فرمود: «ای عیسی! من تو را [از زمین] برگرفته، به سوی خود بالا می‌برم و تو را از کسانی که کافر شدند، پاک [و دور] می‌سازم، و تا روز قیامت پیروان تو را بر کافران برتری خواهم داد.» سپس بازگشت شما به سوی من است، پس در آنچه اختلاف داشتید، میان شما داوری خواهم کرد.'),
(349, 3, 56, 'و امّا گروهی که کافر شدند، [و با شناخت حقّ، آن را انکار کردند،] در دنیا و آخرت به عذابی سخت مجازاتشان خواهم کرد. و برای آنها هیچ یاوری نیست.'),
(350, 3, 57, 'و امّا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، [خداوند] پاداش‌های آنها را به طور کامل خواهد داد. و خداوند ستمگران را دوست نمی‌دارد.'),
(351, 3, 58, '[ای پیامبر!] آنچه بر تو می‌خوانیم، آیه‌ها و اندرز حکمت‌آمیز است.'),
(352, 3, 59, 'مَثلِ [آفرینشِ] عیسی نزد خداوند، همچون مَثلِ [آفرینشِ] آدم است که او را از خاک آفرید و سپس به او فرمود: «باش!» پس [بی‌درنگ] موجود شد.'),
(353, 3, 60, '[سخن] حقّ از جانب پروردگار توست، پس از تردید‌کنندگان مباش.'),
(354, 3, 61, 'هرگاه بعد از علم و دانشی که به تو رسیده است، [باز] کسی درباره عیسی به ستیز و محاجِّه با تو برخیزد بگو: «بیایید ما پسران خود را دعوت کنیم، شما نیز پسران خود را، ما زنان خویش را دعوت نماییم، شما نیز زنان خود را، ما خودمان را [و کسی را که همچون جان ماست،] دعوت کنیم، شما نیز خودتان را، سپس [به درگاه خدا] مُباهله و زاری کنیم و لعنت خدا را بر دروغگویان قرار دهیم.»'),
(355, 3, 62, 'به راستی، داستانِ درستِ [زندگی مسیح] همین است [اُلوهّیت او، یا فرزندِ خدا بودنش، بی‌اساس است.] و هیچ معبودی جز خداوند نیست و به یقین، تنها خدا مقتدر حکیم است.'),
(356, 3, 63, 'پس اگر از حقّ روی‌گردان شدند، خداوند به [حال] مفسدان آگاه است.'),
(357, 3, 64, 'بگو: «ای اهل کتاب! به سوی سخنی بیایید که میان ما و شما مشترک است. که جز خداوند یگانه را نپرستیم و چیزی را شریک او قرار ندهیم و بعضی از ما بعضی دیگر را به جای خدا به خدایی نگیرد.» پس اگر [از این پیشنهاد] سر باز زدند، بگویید: «گواه باشید که ما مسلمان و تسلیم خداییم.»'),
(358, 3, 65, 'ای اهل کتاب! چرا درباره‌ی ابراهیم گفتگو و نِزاع می‌کنید [و هر کدام او را پیرو آیین خود می‌دانید]؟ در حالی که تورات و انجیل، پس از او نازل شده است. چرا نمی‌اندیشید؟!'),
(359, 3, 66, 'هان [ای اهل کتاب!] شما همانان هستید که درباره‌ی [عیسی و] آنچه بدان آگاه بودید، گفتگو و ستیز کردید، پس چرا درباره‌ی [ابراهیم و] آنچه بدان آگاه نیستید، نزاع می‌کنید؟ در حالی که خداوند می‌داند و شما نمی‌دانید.'),
(360, 3, 67, 'ابراهیم نه یهودی بود و نه نصرانی، بلکه او فردی حقّ‌گرا و تسلیم خدا بود و هرگز از مشرکان نبود.'),
(361, 3, 68, 'نزدیک‌ترینِ افراد به ابراهیم، کسانی هستند که از او پیروی کردند و [نیز] این پیامبر و کسانی که [به او] ایمان آورده‌اند. و خداوند سرور و سرپرست مؤمنان است.'),
(362, 3, 69, 'گروهی از اهل کتاب دوست دارند که شما را گمراه کنند، ولی جز خودشان را گمراه نمی‌کنند و [این را] نمی‌فهمند.'),
(363, 3, 70, 'ای اهل کتاب! چرا به آیات خداوند [و نشانه‌های نبوّت رسول خدا] کفر می‌ورزید؟ در حالی که خود [به درستیِ آن] گواهید.'),
(364, 3, 71, 'ای اهل کتاب! چرا حقّ را با باطل مشتبه می‌سازید و [یا] حقیقت را کتمان می‌کنید، در حالی که خود [به حقّانیت آن] آگاهید؟!'),
(365, 3, 72, 'و گروهی از اهل کتاب [از یهود] گفتند: «به آنچه بر مؤمنان نازل شده است، در آغازِ روز [به ظاهر] ایمان بیاورید و در پایان روز کافر شوید، [و این‌گونه مؤمنان را در ایمانشان متزلزل سازید.] شاید [آنها از اسلام] بازگردند.»'),
(366, 3, 73, '[بزرگان یهود، به آنان می‌گفتند:] «جز به کسی که آیین شما را پیروی کند، ایمان نیاورید. [زیرا دینِ حقّ منحصر به ماست.]» [ای پیامبر! به آنان] بگو: «راه سعادت، راهی است که خدا بنماید [و مانعی ندارد] که به امّتی دیگر نیز همانند آنچه [از کتاب و شریعت] به شما داده شده، داده شود، تا در نزد پروردگار با شما بحث و گفتگو کنند.» [به آنان] بگو: «فضل و رحمت به دست خداست، او به هر که بخواهد، عطا می‌کند و خداوند دارای رحمت گسترده و علم بی‌انتهاست.'),
(367, 3, 74, 'هر که را بخواهد، ویژه‌ی رحمت خود می‌کند. و خداوند دارای فضل بزرگ است.»'),
(368, 3, 75, 'و از اهل کتاب کسانی هستند که اگر ثروت زیادی به رسم امانت به آنها بسپاری، آن را به تو بازمی‌گردانند و بعضی از آنان اگر یک دینار هم به آنان بسپاری، آن را به تو برنمی‌گردانند، مگر آن که [برای مطالبه‌ی آن] پیوسته بالای سر آنها ایستاده باشی. این [خیانت در امانت،] به جهت آن است که گفتند: «درباره‌ی غیر یهود هر چه کنیم، بر ما گناهی نیست.» در حالی که آنها آگاهانه بر خداوند دروغ می‌بندند.'),
(369, 3, 76, 'آری، هر کس به عهد خویش وفا کند و تقوا داشته باشد، پس بی‌گمان خداوند پرهیزکاران را دوست می‌دارد.'),
(370, 3, 77, 'کسانی که پیمان خدا و سوگندهای خود را به بهای ناچیزی می‌فروشند، بهره‌ای در آخرت نخواهند داشت و خداوند در قیامت با آنها سخن نمی‌گوید و به آنان نیز نظر [لطف] نمی‌کند، آنها را پاک نمی‌سازد و برای آنان عذابی دردناک است.'),
(371, 3, 78, 'و از اهل کتاب گروهی هستند که زبان خود را به خواندن کتاب می‌چرخانند که شما گمان کنید آن از کتاب آسمانی است، در حالی که از کتاب خدا نیست. و می‌گویند: «آن [چه ما می‌خوانیم] از سوی خداست.» در حالی که از جانب خدا نیست و آنها آگاهانه بر خداوند دروغ می‌بندند.'),
(372, 3, 79, 'هیچ بشری که خداوند به او کتاب و حکم و نبوت داده است، حقّ ندارد به مردم بگوید: «به جای خدا، بندگان من باشید.» پس [شما دانشمندان اهل کتاب نیز] به خاطر سابقه‌ی آموزش کتاب خدا و تدریسی که دارید، خداپرست باشید.'),
(373, 3, 80, 'و [خداوند] به شما فرمان نمی‌دهد که فرشتگان و پیامبران را [به عنوان] پروردگار انتخاب کنید. آیا ممکن است پس از آن که تسلیم خدا شدید، او شما را به کفر فرمان دهد؟!'),
(374, 3, 81, 'و [به یاد آور] هنگامی که خداوند از پیامبرانِ [پیشین و پیروان آنها] پیمان گرفت که هرگاه به شما کتاب و حکمتی دادم، سپس پیامبری به سوی شما آمد که با نشانه‌هایی که نزد شماست، هماهنگ بود، باید به او ایمان بیاورید و او را یاری کنید. [سپس خداوند] فرمود: «آیا به این پیمان اقرار دارید و بار سنگین پیمان مرا [بر دوش] می‌گیرید؟» گفتند: «[آری!] اقرار داریم و پذیرفتیم.» [خداوند] فرمود: «خود شاهد باشید و من [نیز] با شما از گواهانم.»'),
(375, 3, 82, 'پس کسانی که بعد از این [پیمانِ محکم،] روی برگردانند، آنان همان فاسقانند.'),
(376, 3, 83, 'پس آیا آنها جز دین خدا [دینی] می‌جویند؟ در حالی که هر که در آسمان‌ها و زمین است، خواه ناخواه، فقط تسلیم اوست و به سوی او بازگردانده می‌شوند.'),
(377, 3, 84, 'بگو: «به خدا و آنچه بر ما نازل شده و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و پیامبران از نسل یعقوب، نازل گردیده، و آنچه به موسی و عیسی و پیامبران دیگر، از طرف پروردگارشان داده شده، [به همه‌ی آنها] ایمان آوردیم و میان هیچ یک از آنها فرقی نمی‌گذاریم. و ما تنها تسلیم [فرمان] اوییم.»'),
(378, 3, 85, 'و هر کس جز اسلام آیینی برای خود برگزیند، از او پذیرفته نخواهد شد. و او در آخرت از زیانکاران خواهد بود.'),
(379, 3, 86, 'چگونه خداوند گروهی را هدایت کند که بعد از ایمان و گواهی بر حقّانیت رسول و آمدن معجزات و دلایل روشن بر ایشان، باز هم کافِر شدند؟ و خداوند گروه ستمکاران را هدایت نمی‌کند.'),
(380, 3, 87, 'آنان کیفرشان آن است که لعنت خدا و فرشتگان و عموم مردم بر آنان باشد.'),
(381, 3, 88, 'آنان همواره در این لعن [و دوری از رحمت خداوند] می‌مانند، نه عذاب از آنان کاسته گردد و نه به آنها مهلت داده شود.'),
(382, 3, 89, 'مگر کسانی که پس از آن، توبه کنند و [کردار خود را] اصلاح نمایند که خدا آمرزنده و مهربان است.'),
(383, 3, 90, 'البتّه کسانی که پس از ایمان آوردن، کافر شدند و سپس بر کفر خود افزودند، هرگز توبه‌ی آنها پذیرفته نخواهد شد و آنها همان گمراهانند.'),
(384, 3, 91, 'کسانی که کفر ورزیدند و در حال کفر مردند، اگر چه زمین را پر از طلا کرده، به عنوان فدیه و بازخرید [از عذاب] بدهند، هرگز از هیچ یک از آنان پذیرفته نمی‌شود. برای آنان، مجازاتِ دردناکی است و هیچ یاوری ندارند.'),
(385, 3, 92, 'هرگز به نیکی دست نمی‌یابید، مگر آن که از آنچه دوست دارید، انفاق کنید. و بدانید هر چه را انفاق کنید، قطعاً خداوند به آن آگاه است.'),
(386, 3, 93, 'همه‌ی خوراکی‌ها برای بنی‌اسرائیل حلال بود، مگر آنچه یعقوب پیش از نزول تورات بر خود حرام کرده بود. بگو: «اگر راست می‌گویید، تورات را بیاورید و آن را بخوانید»'),
(387, 3, 94, 'پس آنها که بعد از این [علم و آگاهی]، بر خدا دروغ بندند، آنان خود ستمکارند.'),
(388, 3, 95, '[ای پیامبر!] بگو: «خداوند راست گفته [و اینها در آیین پاک ابراهیم نبوده] است، پس از آیین ابراهیم حق‌گرا پیروی کنید که او از مشرکان نبود.»'),
(389, 3, 96, 'نخستین خانه‌ای که برای [عبادت] مردم مقرّر شد، همان است که در سرزمین مکه است، که مایه‌ی برکت و هدایت جهانیان است.'),
(390, 3, 97, 'در آن [خانه] نشانه‌های روشن، [از جمله] مقام ابراهیم است، و هر کس داخل آن [حرم] شود، در امان است و برای خدا بر مردم [واجب] است که قصد حج آن خانه را نمایند، [البتّه] هر که توانایی این راه را دارد. و هرکس کفر ورزد [و با داشتن توانایی به حج نرود، بداند که] خداوند از همه‌ی جهانیان بی‌نیاز است.'),
(391, 3, 98, 'بگو: «ای اهل کتاب! چرا به آیات خداوند کفر می‌ورزید؟ با آن که خداوند بر اعمال شما گواه است.»'),
(392, 3, 99, 'بگو: «ای اهل کتاب! چرا کسانی را که ایمان آورده‌اند، از راه خدا بازمی‌دارید و می‌خواهید این راه را نادرست نشان دهید؟ در حالی که شما [به درستی آن] گواه هستید و خداوند از آنچه انجام می‌دهید، غافل نیست.»'),
(393, 3, 100, 'ای کسانی که ایمان آورده‌اید! اگر گروهی از اهل کتاب را پیروی کنید، شما را بعد از ایمانتان به کفر باز می‌گردانند.'),
(394, 3, 101, 'و چگونه شما کفر می‌ورزید، در حالی که آیات خدا بر شما تلاوت می‌شود و پیامبر او در میان شماست؟ و هر کس به [دین و کتاب] خدا تمسُّک جوید، پس قطعاَ به راه راست هدایت شده است.'),
(395, 3, 102, 'ای کسانی که ایمان آورده‌اید! از خداوند پروا کنید آن‌گونه که سزاوار تقوای اوست. و نمیرید مگر این که مسلمان باشید.'),
(396, 3, 103, 'و همگی به ریسمان خدا چنگ زنید و پراکنده نشوید، و نعمت خدا را بر خود یاد کنید، آن‌گاه که دشمنان یکدیگر بودید، پس خداوند میان دل‌هایتان الفت انداخت و در سایه‌ی نعمت او برادران یکدیگر شدید، و بر لب پرتگاهی از آتش بودید، پس شما را از آن نجات داد. این‌گونه خداوند آیات خود را برای شما بیان می‌کند، شاید هدایت شوید.'),
(397, 3, 104, 'و از میان شما باید گروهی باشند که [دیگران را] به خیر و نیکی دعوت نمایند و امر به معروف و نهی از منکر کنند. و آنان همان رستگارانند.'),
(398, 3, 105, 'و مانند کسانی نباشید که بعد از آن که دلایل روشن برایشان آمد، باز هم اختلاف کرده، پراکنده شدند. و برای آنان عذابی بزرگ است.'),
(399, 3, 106, 'روزی که چهره‌هایی سپید و نورانی، و چهره‌هایی سیاه می‌شوند. پس کسانی که روسیاه شدند، [به آنان گفته می‌شود:] «آیا بعد از ایمانتان کفر ورزیدید؟ پس به خاطر کفرتان، این عذاب را بچشید.»'),
(400, 3, 107, 'و امّا آنان که روسفید شدند، در رحمت خدا جاوِدانه‌اند.'),
(401, 3, 108, 'اینها آیات خداوند است که ما آن را به حقّ بر تو می‌خوانیم. و خدا هیچ ستمی را بر جهانیان روا نمی‌دارد.'),
(402, 3, 109, 'و [چگونه ممکن است خدا ستم کند؟! در حالی که] آنچه در آسمان‌ها و آنچه در زمین است، از آنِ خداست و بازگشت همه‌ی امور تنها به سوی خداست.'),
(403, 3, 110, 'شما بهترین امّتی بوده‌اید که [به عنوان الگو] برای مردم پدیدار شده‌اید. به خوبی‌ها فرمان می‌دهید و از بدی‌ها و زشتی‌ها، نهی می‌کنید و به خدا ایمان دارید. و اگر اهل کتاب [نیز به چنین آیینی] ایمان می‌آوردند، قطعاً برایشان بهتر بود. برخی از آنان مؤمنند، ولی بیشترشان فاسق‌اند.'),
(404, 3, 111, 'اهل کتاب هرگز نمی‌توانند به شما زیان برسانند، جز آزاری اندک، و اگر با شما بجنگند، به شما پشت می‌کنند و می‌گریزند، سپس یاری نخواهند شد.'),
(405, 3, 112, '[دشمنان شما به قدری ترسو و زبون هستند که] هر جا یافت شوند، مهر ذلّت بر آنها خورده است، مگر آن که به ریسمان الهی چنگ زنند [و از انحراف دست برداشته، ایمان آورند] و با مردم پیوندی برقرار کنند. آنها گرفتار خشم خدا گشتند و مهر بیچارگی بر آنها زده شد. این به خاطر آن بود که به آیات خدا کفر می‌ورزیدند و پیامبران را به ناحق می‌کشتند. این بدان سبب بود که نافرمانی کردند و [به حقوق دیگران،] تجاوز می‌نمودند.'),
(406, 3, 113, 'اهل کتاب همه یکسان نیستند، طایفه‌ای از آنها [به طاعت خدا] ایستاده، آیات الهی را در دل شب تلاوت می‌کنند و سر به سجده می‌نهند.'),
(407, 3, 114, 'آنان به خدا و روز قیامت ایمان دارند و امر به معروف و نهی از منکر می‌کنند و در کارهای خیر شتاب می‌ورزند و آنان از افراد صالح و شایسته هستند.'),
(408, 3, 115, 'و آنچه از اعمال نیک انجام دهند، هرگز بدون پاداش نخواهد ماند و خداوند به [حال] پرهیزکاران داناست.'),
(409, 3, 116, 'کسانی که کافر شدند، هرگز اموال و فرزندانشان، در برابر [عذاب] خداوند، برای آنان هیچ سودی نخواهد داشت. و آنها همدم آتشند، همواره در آن ماندگار خواهند بود.'),
(410, 3, 117, 'آنچه در این زندگی دنیا انفاق می‌کنند، چون بادی است که در آن سرما یا گرمای شدیدی باشد، [و] بر کشت‌زارِ قومی که بر خود ستم کرده [و در جایگاه نامناسب، کشت نموده] اند، بوزد، پس آن را از بین ببرد. خداوند به آنان ستم نکرده است، بلکه آنان به خویشتن ستم می‌کنند.'),
(411, 3, 118, 'ای کسانی که ایمان آورده‌اید! غیر خودتان را محرم راز نگیرید. آنان در تباهی شما کوتاهی نمی‌کنند و رنج بردن شما را دوست دارند. به راستی کینه و دشمنی از لحن و سخنانشان پیداست و آنچه در سینه‌هایشان پنهان می‌دارند، بزرگتر است. ما آیاتِ [روشنگر و افشاگر] را برای شما بیان کردیم، اگر بیندیشید.'),
(412, 3, 119, 'هان [ای مسلمانان]! این شمایید که آنان را دوست می‌دارید، ولی آنها شما را دوست نمی‌دارند، در حالی که شما به همه‌ی کتاب‌ها[ی آسمانی] ایمان دارید [ولی آنها به کتاب شما ایمان نمی‌آورند.] و هرگاه با شما دیدار کنند، [منافقانه] می‌گویند: «ما ایمان آوردیم.» و چون [با هم] خلوت کنند، از شدّت خشم بر شما، سر انگشتان خود را می‌گزند. بگو: «به خشم خود بمیرید! خداوند از درون سینه‌ها آگاه است.»'),
(413, 3, 120, 'اگر خوبی به شما رسد، آنان را غمگین می‌سازد و اگر بدی به شما رسد، آنها بدان شادمان می‌شوند. و اگر [در برابرشان] صبر کنید و پرهیزکار باشید، حیله‌ی بدخواهانه‌ی آنان هیچ آسیبی به شما نمی‌رساند. خداوند به آنچه انجام می‌دهند، احاطه دارد.'),
(414, 3, 121, 'و [به یاد آر] هنگامی که بامدادان [برای جنگ اُحد] از نزد خانواده‌ات بیرون شدی [تا] مؤمنان را در مواضع جنگ جای دهی. و خداوند شنوا و داناست.'),
(415, 3, 122, 'آن‌گاه که دو گروه از شما بر آن شدند که [در جنگ] سستی نمایند [و از وسط راه بازگردند]، حال آن که خداوند یاور آنان بود، پس مؤمنان باید تنها بر خدا توکل کنند.'),
(416, 3, 123, 'و همانا خداوند شما را در جنگ بدر در حالی که ناتوان بودید، یاری کرد، پس از خداوند پروا کنید، باشد که سپاسگزاری نمایید.'),
(417, 3, 124, 'و هنگامی که به مؤمنان می‌گفتی: «آیا شما را بس نیست که پروردگارتان، شما را با سه هزار فرشته‌ی فروفرستاده [از سوی خود] یاری کند؟»'),
(418, 3, 125, 'البتّه اگر صبر و مقاومت کنید و پرهیزکاری نمایید، [گر چه] دشمنان با خشم و خروش بر شما بتازند، پروردگارتان شما را با پنج هزار فرشته‌ی نشان‌دار یاری می‌رساند.'),
(419, 3, 126, 'و خداوند این [نزول فرشتگان] را مژده و بشارتی برای شما قرار داد، تا دل‌های شما بدان آرام گیرد و [بدانید که] هیچ پیروزی نیست مگر از جانب خداوند عزیز و حکیم.'),
(420, 3, 127, '[امدادهای الهی برای آن بود] تا بعضی از کفّار را نابود کند یا خوارشان گرداند تا ناامید بازگردند.'),
(421, 3, 128, '[ای پیامبر!] اختیار کار [کافران] به دست تو نیست. خدا، یا بر آنها می‌بخشاید، یا عذابشان می‌کند، زیرا آنان ستمکارند.'),
(422, 3, 129, 'و آنچه در آسمان‌ها و آنچه در زمین است، از آنِ خداست. او [طبق حکمت خود] هر کس را بخواهد می‌آمرزد و هر کس را بخواهد، عذاب می‌کند. و خداوند آمرزنده‌ی مهربان است.'),
(423, 3, 130, 'ای کسانی که ایمان آورده‌اید! ربا را که سودهای افزوده و چند برابر است، نخورید و از خدا پروا کنید تا شاید رستگار شوید.'),
(424, 3, 131, 'و از آتشی که برای کافران آماده شده است، بپرهیزید.'),
(425, 3, 132, 'و خدا و رسول را اطاعت کنید، شاید مورد رحمت قرار گیرید.'),
(426, 3, 133, 'و بشتابید به سوی آمرزشی از جانب پروردگارتان و بهشتی که وسعت آن به قدر آسمان‌ها و زمین است و برای پرهیزکاران آماده شده است.'),
(427, 3, 134, '[همان] کسانی که در توانگری و تنگدستی، انفاق می‌کنند و خشم خود را فرومی‌برند و از [خطای] مردم می‌گذرند، و خداوند نیکوکاران را دوست دارد.'),
(428, 3, 135, 'و آنان که هرگاه کار زشتی انجام دهند و یا به خویشتن ستم کنند، خدا را یاد کرده، برای گناهان خود استغفار می‌کنند. و جز خدا کیست که گناهان را ببخشد؟ و بر آنچه مرتکب شده‌اند، پافشاری نمی‌کنند، چون [به زشتی گناه] آگاهند.'),
(429, 3, 136, 'پاداش چنین کسانی آمرزش پروردگار است، و باغ‌هایی بهشتی که از زیر درختان آنها نهرها جاری است و برای همیشه در آن [باغ‌ها] هستند. و چه نیکوست پاداش عمل‌کنندگان!'),
(430, 3, 137, 'به یقین پیش از شما [اقوام و جوامعی با] آداب و سنت‌هایی [متفاوت، آمده و] از میان رفته‌اند. پس در روی زمین گردش کنید و بنگرید که سرانجام تکذیب‌کنندگانِ چگونه بوده است؟!'),
(431, 3, 138, 'این [قرآن] برای همه مردم، روشنگر [حقایق] و برای پرهیزکاران [وسیله‌ی] هدایت و پندآموزی است.'),
(432, 3, 139, 'و هرگز [در برابر دشمن] سستی نکنید و [از مصیبت‌ها] غمگین مباشید که شما برترید، اگر مؤمن باشید.'),
(433, 3, 140, 'اگر [در جنگ اُحُد] به شما آسیبی رسید، [در جنگ بدر نیز] به آن قومِ [کافر] آسیبی همانند آن رسید. و این روزگار را در میان مردم [دست به دست] می‌گردانیم تا خداوند کسانی را که ایمان آورده‌اند، معلوم دارد و از میان شما گواهانی [بر دیگران] بگیرد. و خداوند ستمگران را دوست نمی‌دارد. [گر چه گاهی به ظاهر پیروز شوند.]'),
(434, 3, 141, 'و [فراز و نشیب‌های جنگ برای آن است] تا خداوند افراد مؤمن را پاک و خالص، و کافران را [به تدریج] محو و نابود گرداند.'),
(435, 3, 142, 'آیا گمان دارید که [با ادّعای ایمان] وارد بهشت می‌شوید، در حالی که هنوز خداوند مجاهدان از شما و صابران را معلوم نساخته است؟'),
(436, 3, 143, 'و شما مرگ [در راه خدا] را پیش از آن که با آن روبه‌رو شوید، سخت آرزو می‌کردید، [اکنون] آن را [در میدان جنگ] دیدید، ولی [ناخوشایند] به آن می‌نگرید.'),
(437, 3, 144, 'و محمّد پیامبری بیش نیست که پیش از او نیز پیامبرانی [آمده و] درگذشته‌اند. پس آیا اگر او بمیرد و یا کشته شود، شما به [دوران جاهلیت و آیین] گذشتگان خود بازمی‌گردید؟ و هر کس به گذشته خود برگردد، هرگز زیانی به خداوند نمی‌رساند. و خداوند به زودی پاداش شاکران را خواهد داد.'),
(438, 3, 145, 'هیچ کس جز به فرمان خدا نمی‌میرد، [و این] سرنوشتی است تعیین شده. و هر کس پاداش دنیا را بخواهد سهمی از آن به او می‌دهیم، و هرکس پاداش آخرت را بخواهد، سهمی از آن به او می‌دهیم، و به زودی سپاسگزاران را پاداش خواهیم داد.'),
(439, 3, 146, 'و چه بسیار پیامبرانی که همراه آنان، خداپرستانِ بسیاری جنگیدند، پس در برابر آنچه در راه خدا به آنان رسید، سستی نکردند و ناتوان نشدند و تن به ذلّت ندادند. و خداوند صابران را دوست دارد.'),
(440, 3, 147, 'و سخنشان تنها این بود که گفتند: «پروردگارا! گناهان ما و زیاده‌روی ما را در کارمان بر ما ببخش و گام‌های ما را استوار بدار و ما را بر گروه کافران یاری ده.»'),
(441, 3, 148, 'پس خداوند پاداش دنیا و پاداش نیک آخرت را به آنان عطا کرد. و خداوند نیکوکاران را دوست می‌دارد.'),
(442, 3, 149, 'ای کسانی که ایمان آورده‌اید! اگر از کسانی که کفر ورزیدند اطاعت کنید، شما را به آیین نیاکانتان برمی‌گردانند، پس زیانکار خواهید شد.'),
(443, 3, 150, '[آنها یار و دوستدار شما نیستند،] بلکه خداوند مولای شماست و او بهترینِ یاوران است.'),
(444, 3, 151, 'به زودی در دل‌های کسانی که کفر ورزیدند، بیم خواهیم افکند، زیرا چیزی را برای خدا شریک قرار داده‌اند که [خداوند] بر [حقّانیت] آن دلیلی نازل نکرده است. و جایگاه آنان آتش دوزخ است و چه بد است جایگاه ستمگران!'),
(445, 3, 152, 'و خداوند به وعده‌ی خود [مبنی بر شکست مشرکان در جنگ اُحد] وفا کرد، در آن هنگام که دشمن را با خواست او از میان برمی‌داشتید، تا آن که سست شدید و در کار [جنگ و تقسیم غنائم] به نزاع پرداختید و پس از آن که آنچه را [از غنائم] دوست داشتید، به شما نشان داد [از دستور پیامبر] نافرمانی کردید. برخی از شما خواهان دنیایند و برخی خواهان آخرت، آن‌گاه خداوند شما را از [تعقیب] آنان منصرف ساخت [و پیروزی شما به شکست انجامید،] تا شما را بیازماید، ولی از [خطای] شما درگذشت. و خداوند نسبت به مؤمنان، دارای فضل و بخشش است.'),
(446, 3, 153, '[به خاطر بیاورید] هنگامی که [در جنگ احد به هنگام فرار، از کوه] بالا می‌رفتید و به هیچ کس توجّه نمی‌کردید، [در حالی که] پیامبر، شما را از پشت سر فرامی‌خواند. پس به سزای آن، خداوند غمی [بزرگ‌تر، که همان شکست از دشمن بود،] بر غم‌هایتان افزود تا دیگر بر آنچه [از غنائم، که] از کفتان رفته، یا [از آسیب و زخم،] که بر شما وارد آمده، اندوه نخورید و [بدانید که] خداوند از آنچه انجام می‌دهید، آگاه است.'),
(447, 3, 154, 'آن‌گاه [خداوند] پس از اندوه شکست، با خوابی سبک [در شب بعد از حادثه اُحُد،] آرامشی بر شما فروفرستاد که گروهی از شما را [که از فرار خود پشیمان بودند] فراگرفت؛ ولی گروه دیگر که در اندیشه جان خویش بودند، و به [وعده‌های] خدا همچون دوران جاهلیت گمان ناروا داشتند، می‌گفتند: «آیا در کار جنگ، برای ما اختیاری هست؟» بگو: «سررشته‌ی همه‌ی کارها [چه پیروزی و چه شکست] به دست خداست.» آنها در دل‌های خود چیزی را پنهان می‌دارند که برای تو آشکار نمی‌سازند، می‌گویند: «اگر اختیار جنگ به دست ما بود، [و از شهر خارج نمی‌شدیم،] در این جا کشته نمی‌شدیم.» [به آنان] بگو: «اگر در خانه‌هایتان نیز بودید، آنهایی که کشته‌شدن بر آنها مقرّر شده بود، به سوی قتلگاه خود روانه می‌شدند و [حادثه‌ی اُحد] برای آن بود که خدا آنچه را در سینه‌های شماست، بیازماید و آنچه را در دل دارید، پاک و خالص گرداند. و خداوند به آنچه در سینه‌هاست داناست.»'),
(448, 3, 155, 'کسانی از شما که روز برخورد دو سپاه روی برگرداندند [و فرار کردند،] جز این نبود که شیطان به خاطر بعضی از کردار [ناپسند]شان آنها را لغزانید و البتّه خداوند از آنها گذشت. به راستی که خداوند آمرزنده‌ی بردبار است.'),
(449, 3, 156, 'ای کسانی که ایمان آوردید! مانند کسانی که راه کفر [و نفاق] در پیش گرفتند، نباشید که چون برادرانشان به مسافرت می‌روند [و بازنمی‌گردند] یا در جنگ شرکت می‌کنند [و کشته می‌شوند]، می‌گویند: «اگر نزد ما بودند، نمی‌مردند و کشته نمی‌شدند.» [شما از این‌گونه سخنان نگویید] تا خدا، [شنیدن چنین سخنانی از شما را] حسرتی در دل‌هایشان قرار دهد. و خداوند [است که] زنده می‌کند و می‌میراند. و خداوند به آنچه انجام می‌دهید، بیناست.'),
(450, 3, 157, 'و اگر در راه خدا کشته شوید یا بمیرید، آمرزش و رحمت خداوند از آنچه [آنان در طول عمر خود،] جمع می‌کنند، بهتر است.'),
(451, 3, 158, 'و اگر بمیرید یا کشته شوید، قطعاً به سوی خداوند برانگیخته می‌شوید.'),
(452, 3, 159, '[ای پیامبر!] به واسطه مهر و رحمتی از جانب خداست که با مردم مهربان گشته‌ای، و اگر خشن و سنگدل بودی، [مردم] از دور تو پراکنده می‌شدند. پس از [خطاهای] آنان درگذر و برای آنها طلب آمرزش کن. و در کارها با آنان مشورت نما، اما هنگامی که تصمیم گرفتی [قاطع باش و] بر خداوند توکل کن. به راستی که خداوند توکل‌کنندگان را دوست دارد.'),
(453, 3, 160, 'اگر خداوند شما را یاری کند، هیچ کس بر شما چیره نخواهد شد، و اگر شما را خوار کند، کیست که بعد از او بتواند شما را یاری کند؟ پس مؤمنان تنها بر خداوند توکل کنند.'),
(454, 3, 161, 'ممکن نیست هیچ پیامبری خیانت کند. و هر کس خیانت کند، آنچه را که [در آن] خیانت نموده، روز قیامت به همراه می‌آورد، آن‌گاه [نتیجه‌ی] اعمال هرکس، به طور کامل به او داده می‌شود. و به آنها ستم نخواهد شد.'),
(455, 3, 162, 'آیا کسی که در پی خشنودی خداوند است، مانند کسی است که به خشم و غضب خدا دچار گشته و جایگاه او جهنّم است؟ و چه بد بازگشت‌گاهی است!'),
(456, 3, 163, 'آنان نزد خداوند [دارای] مقام و درجاتی هستند و خداوند به آنچه انجام می‌دهند، بیناست.'),
(457, 3, 164, 'خداوند بر مؤمنان منّت نهاد که در میان آنها پیامبری از خودشان برانگیخت، تا آیات او را بر آنها تلاوت کند و ایشان را پاک سازد و رشد دهد و به آنان کتاب و حکمت بیاموزد، هر چند که پیش از آن، در گمراهی آشکاری بودند.'),
(458, 3, 165, 'آیا هنگامی که [در جنگ اُحد] مصیبتی به شما رسید که دو برابرش را [در جنگ بدر به دشمن] وارد ساخته بودید، می‌گویید: «این مصیبت از کجاست؟» بگو: «آن از سوی خود شماست. خداوند بر هر کاری تواناست.»'),
(459, 3, 166, 'و آنچه [در اُحد،] روزی که دو گروه با هم نبرد کردند، به شما رسید، به اذن خدا بود [تا شما را آزمایش کند] و مؤمنان را مشخّص نماید.'),
(460, 3, 167, 'و [نیز] برای این بود که منافقان شناخته شوند، [کسانی که چون] به آنها گفته شد: «بیایید در راه خدا بجنگید، یا [لااقل از شهر خود] دفاع کنید.» گفتند: «اگر [فنونِ] جنگی را می‌دانستیم، حتماً از شما پیروی می‌کردیم.» آنها در آن روز، به کفر نزدیک‌تر بودند تا به ایمان. با زبان خود چیزی می‌گویند که در دلشان نیست. و خداوند به آنچه کتمان می‌کنند، آگاه‌تر است.'),
(461, 3, 168, 'کسانی که [از جنگ سر باز زدند و در خانه‌های خود] نشستند، درباره برادران خود گفتند: «اگر از ما پیروی می‌کردند، کشته نمی‌شدند.» به آنان بگو: «اگر راست می‌گویید، مرگ را از خودتان دور سازید.»'),
(462, 3, 169, 'و هرگز گمان مبر کسانی که در راه خدا کشته شده‌اند، مرده‌اند؛ بلکه آنها زنده‌اند و نزد پروردگارشان روزی داده می‌شوند.'),
(463, 3, 170, 'آنان به خاطر آنچه خداوند از فضل خود به آنها داده، شادمانند و به کسانی که به دنبال ایشانند، ولی هنوز به آنها ملحق نشده‌اند، مژده می‌دهند که نه ترسی بر آنهاست و نه اندوهی خواهند داشت.'),
(464, 3, 171, '[آیندگان را] به نعمت و فضل خدا مژده می‌دهند. و این که خداوند پاداش مؤمنان را تباه نمی‌کند.'),
(465, 3, 172, 'آنان که دعوت خدا و پیامبر را [برای شرکتِ دوباره در جهاد علیه کفّار] پذیرفتند، با آن که جراحاتی به آنان رسیده بود، برای کسانی از آنها، که نیکی کردند و پرهیزکار بودند، پاداش بزرگی است.'),
(466, 3, 173, '[همان] کسانی که مردم [منافق] به ایشان گفتند: «بی‌شک مردم [کافر مکه] علیه شما گرد آمده‌اند، پس از آنان بترسید.» [امّا این سخن، به جای ترس] بر ایمانشان افزود و گفتند: «خداوند ما را بس است و او چه خوب نگهبان و یاوری است!»'),
(467, 3, 174, 'پس به واسطه‌ی نعمت و فضل خداوند، [مجروحانی که برای بار دوم آماده دفاع شدند، بدون بروز جنگ، به مقرّ خود] بازگشتند، در حالی که هیچ گزندی به آنان نرسید و [همچنان] خشنودی خدا را دنبال کردند. و خداوند صاحب فضل بزرگی است.'),
(468, 3, 175, 'همانا این شیطان است که دوستان خویش را [از قدرت کفّار] می‌ترساند، پس اگر ایمان دارید، از آنان نترسید و فقط از [مخالفت] من بترسید.'),
(469, 3, 176, 'و [ای پیامبر!] کسانی که در راه کفر می‌شتابند، تو را اندوهگین نکنند. آنان هرگز به خداوند زیانی نمی‌رسانند. خداوند می‌خواهد که برای آنها در قیامت هیچ بهره‌ای قرار ندهد و برای آنان عذابی بزرگ است.'),
(470, 3, 177, 'قطعاً کسانی که کفر را به [بهای] ایمان خریدند، هرگز به خداوند زیانی نمی‌رسانند. و برای آنان عذاب دردناکی است.'),
(471, 3, 178, 'و کسانی که کافر شدند، مپندارند مهلتی که به آنان می‌دهیم، برایشان خوب است، ما به آنان مهلت می‌دهیم تا بر گناه [خود] بیافزایند و برای آنان عذابی خوارکننده است.'),
(472, 3, 179, 'خداوند بر آن نیست که [شما] مؤمنان را به همین‌گونه که اکنون هستید، رها کند، مگر این که [با پیش آوردن آزمایش‌های پی در پی،] ناپاک را از پاک جدا سازد. و خداوند بر آن نیست که شما را بر غیب آگاه کند، [تا مؤمنان و منافقان را از این راه بشناسید،] بلکه از میان پیامبرانش [نیز]، هر کس را بخواهد [برای آگاهی از غیب] برمی‌گزیند، پس به خدا و پیامبرانش ایمان آورید و [بدانید] اگر ایمان بیاورید و تقوا پیشه کنید، برای شما پاداش بزرگی خواهد بود.'),
(473, 3, 180, 'و کسانی که نسبت به [انفاق] آنچه خداوند از فضلش به آنان داده، بخل می‌ورزند، گمان نکنند که [این بخل] به سود آنهاست، بلکه به زیان آنهاست. به زودی در روز قیامت، آنچه نسبت به آن بخل ورزیدند، [همانند] طوقی بر گردنشان آویخته می‌شود. و میراث آسمان‌ها و زمین از آن خداست. و خداوند به آنچه انجام می‌دهید، آگاه است.'),
(474, 3, 181, 'به یقین، خداوند شنید سخن کسانی را که گفتند: «خدا فقیر است و ما توانگریم!» به زودی آنچه را گفتند، و [نیز] این که پیامبران را به ناحق کشتند، خواهیم نوشت و به آنان خواهیم گفت: «بچشید عذاب آتش سوزان را!»'),
(475, 3, 182, 'آن [عذاب] به سبب دستاورد پیشین خودتان است. و[گر نه] خداوند نسبت به بندگان، ستمگر نیست.'),
(476, 3, 183, 'کسانی که گفتند: «خداوند از ما پیمان گرفته که به هیچ پیامبری ایمان نیاوریم، مگر آن که [به عنوان معجزه] یک قربانی برای ما بیاورد، که آتش [صاعقه] آن را بسوزاند!» بگو: «بی‌گمان پیش از من، پیامبرانی [چون زکریا و یحیی،] دلایل روشن و آنچه را گفتید، برای شما آوردند، پس چرا آنان را کشتید، اگر راست می‌گویید؟»'),
(477, 3, 184, 'پس اگر [این بهانه‌جویان] تو را تکذیب کردند، [چیز تازه‌ای نیست، زیرا] پیامبران پیش از تو نیز که همراه با معجزات و نوشته‌ها و کتاب روشنگر آمده بودند، تکذیب شدند.'),
(478, 3, 185, 'هر کسی [طعم] مرگ را می‌چشد. و بی‌گمان روز قیامت پاداش‌های شما به طور کامل داده خواهد شد. پس هر که را از آتش [دوزخ] دور کنند و به بهشت درآورند، قطعاً رستگار است. و زندگانی دنیا، جز مایه‌ی فریب نیست.'),
(479, 3, 186, 'به یقین [همه شما] در اموال و جان‌های خود آزمایش می‌شوید و از کسانی که پیش از شما به آنها کتاب [آسمانی] داده شده و از مشرکان، سخنان آزاردهنده بسیاری می‌شنوید. و اگر صبوری و پرهیزکاری پیشه کنید، [برای شما شایسته‌تر است، زیرا] این [استقامت] از کارهای بزرگ و استوار است.'),
(480, 3, 187, 'و هنگامی که خداوند از کسانی که به آنان کتاب داده شد، پیمان گرفت که حتماً آن را برای مردم بیان نمایید و کتمانش نکنید، پس آنها آن [عهد] را پشت سر افکندند و به بهای ناچیزی فروختند، پس چه بد معامله‌ای کردند!'),
(481, 3, 188, 'گمان مبر کسانی که از اعمال [زشت] خود شادمان می‌شوند، و دوست دارند برای کار [نیک]ی که نکرده‌اند، ستایش شوند، از عذاب الهی رسته‌اند، [بلکه] برای آنها عذابی دردناک است.'),
(482, 3, 189, 'و فرمانروایی آسمان‌ها و زمین از آن خداست. و خداوند بر هر کاری تواناست.'),
(483, 3, 190, 'همانا در آفرینش آسمان‌ها و زمین، و در پیِ یکدیگر آمدن شب و روز، نشانه‌هایی برای خردمندان است.'),
(484, 3, 191, '[همان] کسانی که ایستاده و نشسته، و [خوابیده] بر پهلو، خدا را یاد می‌کنند و در آفرینش آسمان‌ها و زمین می‌اندیشند [و از عمق جان می‌گویند:] «پروردگارا! این هستی را باطل و بی‌هدف نیافریده‌ای، تو [از کار بیهوده] پاک و منزّهی، پس ما را از عذاب آتش نگاه‌دار!'),
(485, 3, 192, 'پروردگارا! هرکه را تو به دوزخ افکنی، بی‌شک او را خوار و رسوا ساخته‌ای. و برای ستمگران هیچ یاوری نیست.'),
(486, 3, 193, 'پروردگارا! ما دعوت منادی ایمان را شنیدیم که ندا می‌داد: «به پروردگارتان ایمان بیاورید.» پس ایمان آوردیم. پروردگارا! گناهان ما را بیامرز و زشتی‌های ما را بپوشان و ما را با نیکان بمیران!'),
(487, 3, 194, 'پروردگارا! آنچه را به وسیله پیامبرانت به ما وعده داده‌ای، به ما مرحمت فرما. و ما را در روز قیامت خوار مساز، که البتّه تو هیچ‌گاه از وعده خود، تخلّف نمی‌کنی.»'),
(488, 3, 195, 'پس پروردگارشان دعای آنان را مستجاب کرد [و فرمود:] «هر یک از شما که عملی را انجام دهد، خواه مرد باشد خواه زن، من آن عمل را تباه نمی‌کنم [زیرا در نزد من، همه] شما همنوع و همانند یکدیگرید، پس کسانی که هجرت کردند و از خانه‌هایشان رانده شدند، و در راه من آزار دیدند، و جنگیدند و کشته شدند، قطعاً لغزش‌هایشان را می‌پوشانم و آنها را در باغ‌هایی که نهرها از زیرشان جاری است، وارد می‌کنم. [این] پاداشی است از سوی خداوند. و پاداش نیکو تنها نزد خداست.'),
(489, 3, 196, 'مبادا تلاش و کوشش و رفت و آمد [تجاری] کافران در شهرها تو را فریب دهد.'),
(490, 3, 197, '[بهره‌ی آنها] کالایی ناچیز است، سپس جایگاه ابدی آنان دوزخ است. و چه بد جایگاهی است!'),
(491, 3, 198, 'ولی کسانی که از [نافرمانی] پروردگارشان می‌پرهیزند، برای آنان باغ‌هایی است که از زیر درختانش نهرها جاری است. جاوِدانه در آن خواهند بود. این [نخستین] پذیرایی از جانب خداوند است، و آنچه در نزد خداست، برای نیکان بهتر است.'),
(492, 3, 199, 'و بعضی از اهل کتاب، کسانی هستند که به خداوند و آنچه به سوی شما نازل شده و آنچه به سوی خودشان نازل گردیده، خاشعانه ایمان می‌آورند و آیات الهی را به بهایی اندک معامله نمی‌کنند. پاداش آنها، نزد پروردگارشان است. بی‌گمان خداوند [اعمال بندگان را] به سرعت حساب می‌کند [و پاداش می‌دهد].'),
(493, 3, 200, 'ای کسانی که ایمان آورده‌اید! [در برابر مشکلات و ناملایمات] صبر کنید و یکدیگر را به صبر فراخوانید [و در برابر دشمنان پایدار باشید] و از مرزها مراقبت کنید و از خداوند پروا داشته باشید، شاید رستگار شوید.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم! از پروردگارتان پروا کنید، همان که شما را از یک انسان آفرید و همسرش را نیز از [جنس] او آفرید و از آن دو، مردان و زنان بسیاری را پراکنده ساخت. و از خدایی که [با سوگند به نام] او از یکدیگر درخواست می‌کنید، پروا کنید. و از [قطع رابطه با] خویشاوندان بپرهیزید که خداوند همواره مراقب شماست.'),
(495, 4, 2, 'و اموال یتیمان را به آنان [باز پس] دهید و اموال بد [خود] را با اموال خوبِ [یتیمان] عوض نکنید و اموال آنان را با اموال خود نخورید. زیرا این گناه بزرگی است.'),
(496, 4, 3, 'و اگر می‌ترسید که نتوانید عدالت [میان همسران] را، در مورد دختران یتیم، مراعات کنید، [بجای آنان] با زنان دلخواه دیگر ازدواج کنید، دو یا سه یا چهار همسر. پس اگر می‌ترسید که نتوانید [میان آنان نیز] به عدالت رفتار کنید، [به] یک همسر یا کنیزی که مال شماست [بسنده کنید]. این کار، به ترک ظلم و ستم نزدیک‌تر است.'),
(497, 4, 4, 'و مهریه زنان را به عنوان هدیه و با رغبت به آنان بدهید. پس اگر با میل و رضایت خویش چیزی از آن را به شما بخشیدند، آن را حلال و گوارا مصرف کنید.'),
(498, 4, 5, 'و اموالتان را که خداوند وسیله قوام و برپایی [زندگی] شما قرار داده، به دست سفیهان نسپارید، [ولی] از درآمد آن به آنها روزی دهید و آنان را بپوشانید و با آنان سخن شایسته و نیکو بگویید.'),
(499, 4, 6, 'و یتیمان را بیازمایید، تا هنگامی که به [بلوغ] برسند، پس اگر در آنان رشد یافتید، اموالشان را به آنها بازگردانید و آن را از [بیم] این که بزرگ شوند [و از شما پس بگیرند] به اسراف و شتاب، مصرف نکنید. و هر که بی‌نیاز است، [از گرفتن اجرت سرپرستی] خودداری ورزد. و آن که نیازمند است، به مقدار متعارف می‌تواند مصرف کند. پس هنگامی که اموالشان را به آنان بازمی‌گردانید، بر آنان گواه و شاهد بگیرید. [این گواهی برای حفظ حقوق یتیمان است، وگرنه] خدا برای محاسبه کافی است.'),
(500, 4, 7, 'برای مردان، از آنچه پدر و مادر و خویشاوندان، [پس از مرگ] بر جای گذاشته‌اند، سهمی است، و برای زنان [نیز] از آنچه پدر و مادر و خویشاوندان بر جای گذاشته‌اند، سهمی است، خواه کم باشد یا زیاد؛ سهمی معین و مقرّر [که پرداخت آن واجب است].'),
(501, 4, 8, 'و اگر هنگام تقسیم [ارث]، خویشاوندانی [که ارث نمی‌برند] و یتیمان و مستمندان حاضر شدند، چیزی از آن اموال را به آنها هم بدهید و با آنان پسندیده سخن بگویید.'),
(502, 4, 9, 'و کسانی که اگر پس از خود فرزندان ناتوانی به جای گذارند، بر [زیردست شدن] آنان می‌ترسند، باید [از ستم بر یتیمان مردم نیز] بترسند و از خدا پروا کنند و [با آنها] سخنی استوار و شایسته بگویند.'),
(503, 4, 10, 'کسانی که اموال یتیمان را به ستم می‌خورند، در حقیقت، آتشی را در شکم خود فرومی‌برند و به زودی در آتشی افروخته وارد خواهند شد.'),
(504, 4, 11, 'خداوند درباره‌ی [ارث] فرزندانتان به شما سفارش می‌کند که سهم یک پسر برابر سهم دو دختر باشد، پس اگر [همه‌ی وارثان] دختر و بیش از دو تن باشند، دو سوّم میراث، سهم آنان است و اگر [تنها] یک دختر باشد، نصف میراث از آنِ اوست. و برای هر یک از پدر و مادرِ [میت]، اگر فرزندی داشته باشد، یک ششم میراث است. و اگر فرزندی نداشت و پدر و مادر، تنها وارث او باشند، مادر یک سوم می‌برد و اگر متوفّی برادرانی داشت، سهم مادر یک ششم می‌شود. [و بقیه، برای پدر است. البتّه تقسیم ارث] پس از عمل به وصیتی است که او سفارش کرده یا پرداخت بدهی است که باید داده شود. شما درنمی‌یابید که پدران [و مادران] و فرزندانتان، کدام‌یک برایتان سودمندترند. این احکام، از سوی خدا واجب شده است، همانا خداوند همواره دانا و حکیم است.'),
(505, 4, 12, 'و [شما مردان] اگر همسرانتان فرزندی نداشته باشند، نیمی از آنچه بر جا نهاده‌اند و اگر فرزندی داشته باشند، یک چهارم از آنچه بر جا گذارده‌اند، از آن شماست. البتّه پس از عمل به وصیتی که کرده‌اند یا ادایِ دِینی که دارند. و برای زنان، یک چهارم میراث شماست، اگر فرزندی نداشته باشید؛ و اگر برای شما فرزندی باشد، یک هشتم میراث شما برای آنهاست. البتّه پس از عمل به وصیتی که کرده‌اید یا پرداخت بدهی که دارید. و اگر مرد یا زنی که از او ارث می‌برند، فرزند و پدر و مادر نداشته و [تنها] برادر یا خواهر داشته باشد، هر یک از آنان یک ششم ارث را می‌برند. و اگر بیش از یکی باشند، همه آنان در یک سوم، به تساوی شریکند، [در هر حال، تقسیم ارث] پس از انجام وصیتِ میت یا ادای دینِ اوست، به شرط آن که او نیز قصد نداشته باشد [از طریق وصیت و اقرار به دین،] به ورثه زیان برساند. این، سفارشی از سوی خداست و خداوند، دانا و بردبار است.'),
(506, 4, 13, 'آن [چه در احکام ارث گفته شد،] حدود و مقررّات الهی است، و هرکس از خدا و رسولش اطاعت کند، خداوند او را به باغ‌هایی وارد کند که از زیر درختانش نهرها جاری است، جاودانه در آن خواهند ماند، و این است کامیابی و رستگاری بزرگ.'),
(507, 4, 14, 'و هرکس خدا و رسولش را نافرمانی کند و از حدود الهی تجاوز نماید، خدا او را به آتشی وارد کند که همیشه در آن می‌ماند و برای او عذابی خوارکننده است.'),
(508, 4, 15, 'و از زنان شما، کسانی که مرتکب فحشا شوند، چهار شاهد از خودتان، علیه آنها بطلبید، پس اگر گواهی دادند، آن زنان را در خانه‌ها[ی خودشان] نگاه دارید، تا مرگشان فرارسد، یا آن که خداوند، [با صدور حکمی دیگر] راه و چاره‌ای برای آنان قرار دهد.'),
(509, 4, 16, 'و آن مرد و زنی از شما، که [همسر ندارند، و] مرتکب آن کار [زشت] می‌شوند، بیازارید [و با اجرای حدّ مجازات کنید]. پس اگر توبه کردند و خود را اصلاح نمودند، از آنان درگذرید. [زیرا] خداوند توبه‌پذیرِ مهربان است.'),
(510, 4, 17, 'پذیرش توبه از سوی خدا، تنها برای کسانی است که از روی جهالت مرتکب گناه می‌شوند، سپس به زودی توبه می‌کنند. خداوند توبه‌ی آنان را می‌پذیرد و خدا، دانا و حکیم است.'),
(511, 4, 18, 'و کسانی که کارهای زشت می‌کنند تا هنگامی که مرگ هر یک از ایشان فرارسد، آن‌گاه می‌گوید: «اکنون توبه کردم.» و نیز آنان که در کفر می‌میرند، توبه‌شان پذیرفته نیست. بلکه برای آنان عذابی دردناک فراهم ساخته‌ایم.'),
(512, 4, 19, 'ای کسانی که ایمان آورده‌اید! برای شما حلال نیست که زنان [بیوه] را [همانند اموال شوهرانشان طبق سنّت جاهلیت] به اکراه به ارث برید و بر آنان سخت نگیرید تا قسمتی از آنچه را [به عنوان مهریه] به آنها داده‌اید، [از دستشان بیرون] برید! مگر آن که آنها عمل زشت آشکاری انجام دهند. و با آنان به نیکویی رفتار کنید و اگر از همسرتان خوشتان نیامد، [تصمیم به جدایی نگیرید،] چه بسا چیزی خوشایند شما نباشد، ولی خداوند، خیر فراوانی در آن قرار داده باشد.'),
(513, 4, 20, 'و اگر خواستید همسر دیگری به جای همسر خود انتخاب کنید و مال فراوانی [به عنوان مهریه] به او داده‌اید، چیزی از آن را پس نگیرید. آیا می‌خواهید با تهمت و گناه آشکار، آن را باز پس گیرید؟!'),
(514, 4, 21, 'و چگونه آن مال را باز پس می‌گیرید، با آن که از یکدیگر کام گرفته‌اید و همسرانتان از شما [هنگام ازدواج، برای پرداخت مهریه] پیمانی محکم گرفته‌اند!'),
(515, 4, 22, 'و زنانی را که پدرانتان با آنها ازدواج کرده‌اند، به همسری نگیرید، مگر آنچه در گذشته [پیش از نزول این حکم] انجام شده است. این گونه ازدواج، کاری زشت و تنفرآور، و راه و روشی نادرست است.'),
(516, 4, 23, '[ازدواج با این افراد] بر شما حرام شده است: مادرانتان و دخترانتان، و خواهرانتان و عمّه‌هایتان، و خاله‌هایتان، و دخترانِ برادر و دخترانِ خواهر، و مادرانی که به شما شیر داده‌اند، و خواهران هم‌شیر [که از آن مادر شیر خورده‌اند]، و مادران همسرانتان و دختران همسرانتان [از شوهر قبلی] که در دامن شما تربیت یافته‌اند، البته در صورتی که با مادرانشان، همبستر شده باشید، اما اگر آمیزش نکرده‌اید [و از آنان جدا شده‌اید]، مانعی ندارد [که با دختران آنها ازدواج کنید]، و [همچنین حرام است ازدواج با] همسران پسرانتان که از نسل شمایند، [نه پسر خوانده‌ها] و نباید دو خواهر را همزمان [به همسری] داشته باشید، مگر آنچه پیشتر رخ داده است. [که گناهش آمرزیده شده، ولی باید از یکی جدا شوید.] همانا خداوند آمرزنده و مهربان است.'),
(517, 4, 24, 'و [ازدواج با] زنان شوهردار [نیز بر شما حرام است،] مگر آنان که [در جنگ با کفّار اسیر کرده و] مالک شده‌اید. [که اسارت آنها در حکم طلاق است. این احکام،] نوشته و قانون خدا بر شماست. و جز اینها [که بیان شد،] بر شما حلال است که [زنان دیگر را] با [پرداخت بخشی از] اموالتان، به قصد پاکدامنی اختیار کنید، نه به قصد زنا. پس هرگاه از زنان [با ازدواج موقّت] کام گرفتید، واجب است مهریه آنها را بپردازید و گناهی بر شما نیست که پس از تعیین مهریه، با رضایت یکدیگر [مقدار آن را] کم یا زیاد کنید. خداوند همواره دانا و حکیم است.'),
(518, 4, 25, 'و هر کس از شما، که توانایی مالی ندارد تا با زنان پاکدامنِ باایمان ازدواج کند، می‌تواند از کنیزان جوان مؤمن که مالک آن هستید، [همسر گزیند] و خداوند به ایمان شما آگاه‌تر است. همه از یکدیگرید، پس آنها را با اجازه‌ی صاحبانشان به همسری درآورید و مهریه آنان را به طور شایسته به خودشان بدهید، به شرط آن که پاکدامن باشند، نه مرتکب فحشا شوند و نه دوست پنهانی بگیرند. پس آن‌گاه که کنیزان، همسردار شدند، اگر کار زشت زنا از آنان سر زد، کیفرشان نصف کیفر زنانِ آزاد است. این [نوع از ازدواج،] برای کسانی از شماست که از افتادن به گناه و فحشا بر خود بترسند. با این همه، صبر کردن [و ازدواج نکردن با کنیزان] برای شما بهتر است. و خداوند، آمرزنده و مهربان است.'),
(519, 4, 26, 'خداوند می‌خواهد [با این قوانین، راه سعادت را] برای شما آشکار سازد و شما را به سنّت‌های [نیک] پیشینیانتان راهنمایی کند و توبه شما را بپذیرد. و خداوند، دانا و حکیم است.'),
(520, 4, 27, 'و خداوند می‌خواهد شما را فرصت توبه دهد [و از آلودگی پاک سازد،] ولی کسانی که پیرو هوس‌هایند، می‌خواهند که شما دستخوش انحرافی بزرگ شوید.'),
(521, 4, 28, 'خداوند می‌خواهد [در کار ازدواج] بر شما آسان گیرد. زیرا انسان، [در برابر خواسته‌های نفسانی] ناتوان آفریده شده است.'),
(522, 4, 29, 'ای کسانی که ایمان آورده‌اید! اموال یکدیگر را در میان خود به باطل [و از طرق نامشروع] نخورید، مگر این که داد و ستدی با رضایت یکدیگر باشد. و خود [و یکدیگر] را نکشید. خداوند همواره با شما مهربان است.'),
(523, 4, 30, 'و هر کس از روی تجاوز و ستم چنان کند [و دست به مال مردم دراز کرده، یا مرتکب خودکشی و خون‌ریزی شود] به زودی او را در آتشی [عظیم] درآوریم و این کار، برای خداوند آسان است.'),
(524, 4, 31, 'اگر از گناهان بزرگی که از آن نهی شده‌اید، پرهیز کنید، بدی‌هایتان را می‌پوشانیم و شما را در جایگاهی ارجمند وارد می‌کنیم.'),
(525, 4, 32, 'هرگز برتری‌هایی را که خداوند برای بعضی از شما بر بعضی دیگر قرار داده، آرزو نکنید. [این تفاوت‌ها، برای حفظ نظام زندگی است. با این حال،] مردان از آنچه به دست آورده‌اند، بهره‌ای دارند و زنان نیز بهره‌ای. [و نباید حقوق هیچ یک پایمال گردد]. و [به جای آرزو و حسادت،] از فضل خداوند درخواست کنید، که خداوند به هر چیزی داناست.'),
(526, 4, 33, 'و برای هر فرد، وارثانی قرار داده‌ایم که از میراث پدر و مادر و نزدیکان ارث برند. و کسانی که با آنان پیمان [زناشویی] بسته‌اید، سهمشان را [از ارث] بپردازید، همانا خداوند بر هر چیز، شاهد و ناظر است.'),
(527, 4, 34, 'مردان، سرپرست امور زنان [و خانواده‌ی خویش] هستنند، به خاطر برتری‌هایی که خداوند [در اداره‌ی خانواده] برای بعضی نسبت به بعضی قرار داده است، و به خاطر آن که از اموالشان، نفقه [زنان و خانواده را] می‌پردازند. پس زنانِ شایسته، مطیع و متواضع‌اند و به پاس حقوقی که خدا [برای آنان قرار داده و] حفظ کرده، در غیاب [همسران خود،]، حافظ [ناموس و حقوق] آنان‌اند. و زنانی را که از سرکشی آنان بیم دارید، [نخست] پند و اندرزشان دهید و [اگر مؤثّر نشد] در بستر از آنان دوری کنید و [اگر پند و قهر اثر نکرد، در محدوده‌ی دستور شرع] آنان را تنبیه کنید، پس اگر از شما اطاعت کردند، [برای ستم و آزار] بهانه‌ای بر آنان مجویید. همانا خداوند بلند مرتبه و بزرگ است.'),
(528, 4, 35, 'و اگر از [ناسازگاری و] جدایی میان آن دو [همسر] بیم داشتید، داوری از خانواده‌ی شوهر، و داوری از خانواده‌ی زن برگزینید [تا به اختلاف آن دو رسیدگی کنند]. اگر بنای اصلاح داشته باشند، خداوند میان آن دو سازگاری خواهد داد، زیرا خداوند، [از نیات همه] باخبر و آگاه است.'),
(529, 4, 36, 'و خدا را بپرستید و هیچ چیز را شریک او قرار ندهید و به پدر و مادر نیکی کنید، و به بستگان و یتیمان و بینوایان و همسایه‌ی نزدیک و همسایه‌ی دور و یار همنشین و در راه مانده و زیردستان و بردگانتان [نیز نیکی کنید.] بی‌گمان خداوند، هر که را متکبّر و فخرفروش باشد، دوست نمی‌دارد.'),
(530, 4, 37, '[همان] کسانی که خود بخل می‌ورزند و مردم را نیز به بخل وامی‌دارند، و آنچه را که خداوند از فضل خویش به آنان بخشیده، پنهان می‌دارند. [این عمل، در حقیقت از کفرشان سرچشمه گرفته] و ما برای کافران عذابی خوارکننده آماده کرده‌ایم.'),
(531, 4, 38, 'و کسانی که اموال خود را از روی ریا و برای نشان دادن به مردم انفاق می‌کنند، و به خدا و روز قیامت ایمان ندارند، و کسی که شیطان همدم او باشد، چه بد همدمی است!'),
(532, 4, 39, 'و اگر به خدا و روز قیامت ایمان می‌آوردند و از آنچه خدا، روزی‌شان کرده انفاق می‌کردند، چه زیانی برایشان داشت؟ و خداوند از نیت آنان آگاه است.'),
(533, 4, 40, 'بی‌گمان خداوند به مقدار ذرّه‌ای هم ستم نمی‌کند، و اگر کار نیکی باشد، آن را دو چندان می‌کند، و از جانب خویش نیز پاداشی بزرگ می‌بخشد.'),
(534, 4, 41, 'پس آن هنگام که از هر امّتی، گواهی [بر اعمالشان] بیاوریم و تو را نیز بر آنان گواه گیریم، [حالشان] چگونه خواهد بود؟'),
(535, 4, 42, 'در آن روز، کسانی که کفر ورزیدند و پیامبر را نافرمانی کردند، آرزو می‌کنند که ای کاش [از خاک برانگیخته نشده و] با زمین یکسان بودند ولی [بدانند که با وجود گواهان] هیچ سخنی را نمی‌توانند از خدا پنهان کنند.'),
(536, 4, 43, 'ای کسانی که ایمان آورده‌اید! در حال مستی به نماز نزدیک نشوید، تا بدانید چه می‌گویید. و نیز در حال جنابت [به مسجد روی نیاورید]، مگر آن که رهگذر باشید [و بی‌توقّف عبور کنید،] تا این که غسل کنید. و اگر بیمار [بودید و استعمال آب برای شما ضرر داشت] یا در سفر بودید، یا یکی از شما از [محلّ] قضای حاجت بازآمد، یا با زنان آمیزش کردید و آب [برای وضو یا غسل] نیافتید، بر خاکی پاک تیمّم کنید، [ابتدا دو کف دست را بر خاک بزنید،] آن‌گاه صورت و دست‌هایتان را مسح کنید، همانا خداوند، بخشنده و آمرزنده است.'),
(537, 4, 44, '[ای پیامبر!] آیا آنان را که بهره‌ای از کتاب آسمانی به آنان داده شده بود، ملاحظه نکرده‌ای؟ گمراهی را برای خود می‌خرند و می‌خواهند شما [نیز] گمراه شوید.'),
(538, 4, 45, 'و خداوند دشمنان شما را بهتر می‌شناسد. و سرپرستی خدا برای شما کافی است و یاری او شما را بس است.'),
(539, 4, 46, 'بعضی از یهودیان، کلمات را از جایگاهش تغییر می‌دهند و با پیچاندن زبانشان و از سر طعنه بر دین، [به پیامبر] می‌گویند: «[ما سخنان تو را] شنیدیم و نافرمانی کردیم. تو نیز [سخن ما را] بشنو که [ای کاش] ناشنوا گردی.» و «راعِنا!» [که در زبان عبری نوعی اهانت است.] و اگر می‌گفتند: «[ما سخنان تو را] شنیدیم و اطاعت کردیم، تو نیز [سخن ما را] بشنو و ما را بنگر.» برایشان بهتر بود و با منطق سازگارتر؛ ولی خداوند آنان را به خاطر کفر و سرسختی‌شان لعنت کرد و جز عدّه‌ای اندک ایمان نمی‌آورند.'),
(540, 4, 47, 'ای کسانی که به شما کتاب داده شده! به آنچه نازل کردیم که هماهنگ با نشانه‌هایی است که [در تورات و انجیل] نزد شماست، ایمان بیاورید، پیش از آن که چهره [انسان]هایی را محو کنیم، و به پشت برگردانیم، یا آنها را از رحمت خود دور سازیم، آن‌گونه که [یهودیانِ متخلّفِ] اهلِ روزِ شنبه را لعنت کردیم. و فرمان خداوند، انجام‌یافتنی است.'),
(541, 4, 48, 'بی‌گمان خداوند این را که به او شرک ورزند، نمی‌بخشد، و کمتر از آن را برای هر که بخواهد می‌بخشد. و هر کس برای خدا، همتایی قرار دهد، گناهی بزرگ مرتکب شده است.'),
(542, 4, 49, 'آیا ندیدی کسانی را که خود را به پاکی می‌ستایند؟ با این که خداوند هر که را بخواهد، می‌ستاید و [در این امر،] کمترین ستمی بر آنان نخواهد شد.'),
(543, 4, 50, 'بنگر که چگونه بر خدا دروغ می‌بندند! و همین گناه آشکار برای [مجازات] آنان بس است.'),
(544, 4, 51, 'آیا ندیدی کسانی که بهره‌ای از کتاب [تورات] دارند، [برای اتحاد با مشرکان مکه، در برابر بت‌های آنان] به [نام‌های] «جِبت» و «طاغوت» [تعظیم کنند و به آنها] ایمان می‌آورند، و درباره‌ی آن کافران می‌گویند: «اینان از کسانی که [به محمّد] ایمان آورده‌اند، راه‌یافته‌ترند.»'),
(545, 4, 52, 'آنانند کسانی که خداوند لعنتشان کرده است و هر که را خدا از رحمت خود دور سازد، هرگز برای او یاوری نخواهی یافت.'),
(546, 4, 53, 'آیا [یهودیانی که علیه مسلمانان توطئه می‌کنند، می‌پندارند که] آنان سهمی در حکومت دارند؟! در حالی که اگر چنین بود، کمترین حقی را به مردم نمی‌دادند.'),
(547, 4, 54, 'یا این که نسبت به [پیامبر و اهل بیتش و مؤمنانِ از] مردم، به خاطر آنچه خدا از فضل خویش به آنان داده است، حسد می‌ورزند. در حالی که [ما پیش از این نیز] به [پیامبرانی چون موسی و عیسی از] خاندان ابراهیم کتاب و حکمت دادیم و [برخی چون یوسف و سلیمان را] فرمانرواییِ بزرگی عطا کردیم.'),
(548, 4, 55, 'البته برخی یهودیان به او ایمان آوردند و برخی از ایمان آوردن مردم جلوگیری کردند، و شعله‌ی سوزان دوزخ، [برای کیفر آنان بس] است.'),
(549, 4, 56, 'به زودی کسانی را که به آیات ما کافر شدند، در آتشی خواهیم افکند که هرگاه پوست بدنشان بسوزد، پوست دیگری جایگزین آن می‌گردانیم تا عذاب را بچشند. به‌راستی خداوند توانا و حکیم است.'),
(550, 4, 57, 'و کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند، به زودی در باغ‌هایی که از زیر [درختان] آن نهرها جاری است، وارد می‌کنیم. جاودانه در آن خواهند ماند، در آنجا همسرانی پاکیزه دارند، و آنان را در سایه‌ای پایدار درآوریم.'),
(551, 4, 58, 'خداوند به شما فرمان می‌دهد که امانت‌ها را به صاحبانش بازگردانید. و هر گاه میان مردم داوری کردید، به عدالت حکم کنید. چه نیکوست آنچه خدا شما را بدان پند می‌دهد. بی‌گمان خداوند شنوای بیناست.'),
(552, 4, 59, 'ای کسانی که ایمان آورده‌اید! خدا را اطاعت کنید و از پیامبر و صاحبان امر خویش [که جانشینان معصوم پیامبرند] اطاعت کنید. و اگر درباره‌ی چیزی نزاع کردید، آن را به [حکم] خدا و پیامبر بازگردانید، اگر به خدا و قیامت ایمان دارید. این [رجوع به قرآن و سنّت برای حلّ اختلاف] بهتر و پایانش نیکوتر است.'),
(553, 4, 60, 'آیا نظر نمی‌کنی به کسانی که می‌پندارند به آنچه بر تو و آنچه پیش از تو نازل شده است، ایمان آورده‌اند؟! در حالی که قصد دارند داوری‌های خود را نزد طاغوت [و حکام باطل] ببرند، با این که به آنها دستور داده شده به طاغوت، کفر ورزند. و شیطان می‌خواهد آنان را گمراه سازد، گمراهی دور و درازی!'),
(554, 4, 61, 'و چون به آنان گفته شود: «به سوی آنچه خداوند نازل کرده و به سوی پیامبر بیایید.» منافقان را می‌بینی که به شدّت از پذیرش دعوت تو روی می‌گردانند.'),
(555, 4, 62, 'پس چگونه وقتی به خاطر کارهایی که در گذشته انجام دادند، گرفتار مصیبت و مشکلی می‌شوند، و [برای رهایی از آن] سراغ تو می‌آیند، به خدا سوگند یاد می‌کنند که هدف ما [از بردن داوری نزد طاغوت] جز نیکی و توافق نبوده است.'),
(556, 4, 63, 'آنان کسانی هستند که خداوند، آنچه را در دل دارند، می‌داند. پس از آنان دوری گزین و پندشان ده و با آنان سخنی رسا بگو که در جانشان بنشیند.'),
(557, 4, 64, 'و ما هیچ پیامبری را نفرستادیم مگر برای آن که با اذن خداوند از او اطاعت کنند و اگر هنگامی که آنان به خود ستم کردند [و فرمان‌های خدا را زیر پا گذاردند] نزد تو می‌آمدند و از خداوند آمرزش می‌خواستند و پیامبر هم برای آنان استغفار می‌کرد، قطعاً خداوند را توبه‌پذیر و مهربان می‌یافتند.'),
(558, 4, 65, 'چنین نیست [که شما گمان می‌کنید]! به پروردگارت سوگند که مردم ایمان نمی‌آورند، مگر آن که در اختلافات خود، تو را داور قرار دهند، و در دل احساس ناراحتی از قضاوت تو نداشته باشند، [بلکه در برابر داوری تو] کاملاً تسلیم باشند.'),
(559, 4, 66, 'و اگر به آنان دستور می‌دادیم که تن به کشتن دهید یا از خانه و سرزمین خود بیرون روید، تنها افرادی اندک، این دستور را انجام می‌دادند. و اگر اندرزهایی را که به آنان داده می‌شد، عمل می‌کردند، برایشان بهتر و در پایداری مؤثرتر بود.'),
(560, 4, 67, 'و در این صورت یقیناً ما از جانب خود پاداشی بزرگ به آنان می‌دادیم.'),
(561, 4, 68, 'و قطعاً آنان را به راهی راست هدایت می‌کردیم.'),
(562, 4, 69, 'و کسانی که از خدا و پیامبر اطاعت کنند، [در قیامت] با کسانی همدم خواهند بود که خداوند بر آنان نعمت داده است: پیامبران، صِدّیقان، شهیدان و صالحان. و اینان چه رفیقان خوبی هستند!'),
(563, 4, 70, 'این فضیلت از سوی خداست و داناییِ خدا [به احوال بندگانش] کافی است.'),
(564, 4, 71, 'ای کسانی که ایمان آورده‌اید! [در برابر دشمن هوشیار باشید،] سلاح خود را برگیرید و به صورت گروه گروه یا دسته‌جمعی، به سوی دشمن حرکت کنید.'),
(565, 4, 72, 'و در میان شما کسانی هستند که [هم خود سستی می‌ورزند و هم] عامل کندی و دلسردی رزمندگان می‌شوند، هر گاه برای شما مشکلی پیش آید، می‌گوید: «خدا بر من منّت نهاده که همراه آنان [در جبهه] حضور نیافتم و شاهد [آن مصیبت] نبودم.»'),
(566, 4, 73, 'و اگر فضل و غنیمتی از سوی خداوند به شما برسد، چنان که گویی میان شما و او هرگز دوستی نبوده [تا نفع شما را نفع خود بداند،] خواهد گفت: «ای کاش [در جهاد] با آنان بودم تا به رستگاریِ بزرگ می‌رسیدم.»'),
(567, 4, 74, 'آری، کسانی باید در راه خدا نبرد کنند که زندگی دنیا را به آخرت می‌فروشند و کسی که در راه خدا می‌جنگد، چه کشته شود و چه پیروز گردد، به زودی پاداشی بزرگ به او خواهیم داد.'),
(568, 4, 75, 'چرا در راه خدا و [برای رهایی] مردان و زنان و کودکان مستضعف نمی‌جنگید؟ همان کسانی که می‌گویند: «پروردگارا! ما را از این شهری که مردمش ستمگرند، بیرون بر و از جانب خود رهبر و سرپرستی برای ما قرار ده، و از سوی خودت، یاوری برای ما تعیین فرما!»'),
(569, 4, 76, 'کسانی که ایمان آورده‌اند، در راه خدا جهاد می‌کنند و کسانی که کافر شده‌اند، در راه طاغوت می‌جنگند. پس با دوستان و یاران شیطان پیکار کنید [و بیم نداشته باشید،] زیرا نیرنگ شیطان سست و ضعیف است.'),
(570, 4, 77, 'آیا نمی‌نگری به کسانی که [پیش از هجرت، هنگامی که] به آنان گفته شد: «[اکنون از مبارزه] دست نگه‌دارید و نماز به پا دارید و زکات بپردازید.» [از این دستور ناراحت بودند]، ولی چون [در مدینه] فرمان جهاد به آنان داده شد، گروهی از آنان از مردمِ [مشرک مکه] چنان می‌ترسیدند که گویا از خدا می‌ترسند، بلکه بیشتر. و گفتند: «پروردگارا! چرا جنگ را بر ما واجب کردی؟ چرا ما را تا مدتی کوتاه مهلت ندادی؟» بگو: «بهره دنیا اندک و ناچیز است و برای کسی که تقوا پیشه کند، آخرت بهتر است. و کمترین ستمی به شما نخواهد شد.»'),
(571, 4, 78, 'هر جا باشید، مرگ شما را درمی‌یابد، هر چند در دژهای محکم باشید. و اگر به آنان [که منافقند،] نیکی و پیروزی برسد، می‌گویند: «این از سوی خداست.» و اگر بدی [و شکستی] به ایشان برسد، می‌گویند: «این از جانب توست.» بگو: «همه از جانب خداست.» پس چرا این گروه حاضر نیستند سخنی را درک کنند؟!'),
(572, 4, 79, '[ای انسان!] هر نیکی به تو برسد، از خداست و هر بدی به تو برسد، از خودِ توست. و [ای پیامبر!] ما تو را برای مردم به رسالت فرستادیم و گواهی خدا [در این باره] کافی است.'),
(573, 4, 80, 'هر کس از پیامبر اطاعت کند، بی‌شک خداوند را اطاعت کرده است. و هر که روی گرداند و سر باز زند، ما تو را بر او نگهبان نفرستاده‌ایم [و تو در برابر او، مسئول نیستی].'),
(574, 4, 81, '[منافقان، در حضور تو] می‌گویند: «فرمانبرداریم!» امّا هنگامی که از نزد تو بیرون می‌روند، گروهی از آنان در جلسات شبانه، بر خلاف گفته‌ی تو [می‌گویند و] اندیشه می‌کنند؛ ولی خداوند، آنچه را در این جلسات می‌گذرد، ثبت می‌کند. پس از آنها روی گردان و بر خداوند توکل کن، و کافی است که خداوند، پشتیبان تو باشد.'),
(575, 4, 82, 'آیا در قرآن نمی‌اندیشند؟ در حالی که اگر از سوی غیر خدا بود، قطعاً اختلاف بسیاری در آن می‌یافتند.'),
(576, 4, 83, 'و هنگامی که خبری امیدبخش [از پیروزی] یا نگران‌کننده [از شکست]، به آنان برسد، [بدون تحقیق،] آن را پخش می‌کنند، در حالی که اگر درباره‌ی آن خبر، به پیامبر و اولیای امر خود مراجعه می‌کردند، کسانی از آنان که قدرت تشخیص دارند، [حقیقت امر را از آنان] درمی‌یافتند. و اگر فضل و رحمت خداوند بر شما نبود، به جز اندکی، همگی از شیطان پیروی می‌کردید.'),
(577, 4, 84, 'پس [ای پیامبر!] در راه خدا پیکار کن، [و بدان که] جز انجام وظیفه‌ی خود تکلیفی نداری و مؤمنان را [نیز به جهاد] تشویق کن، باشد که خداوند گزند کافران را [از شما] بازدارد. و خداوند قدرتمندتر و سخت‌کیفرتر است.'),
(578, 4, 85, 'هر کس در کار نیک میانجی شود، بهره‌ای از [پاداش] آن می‌برد و هر کس در کار بد میانجی شود، بهره‌ای از کیفر آن خواهد داشت. و خداوند بر هر چیزی نگهبان است.'),
(579, 4, 86, 'و هرگاه شما را درود و سلامی گفتند، [در پاسخ،] درودی بهتر از آن بگویید یا همان را بازگویید. خداوند همواره بر هر چیزی حساب‌رس است.'),
(580, 4, 87, 'خداوند که معبودی جز او نیست، همه‌ی شما را در روز قیامت، که هیچ شکی در آن نیست گرد می‌آورد. و راستگوتر از خدا در سخن کیست؟'),
(581, 4, 88, 'چرا درباره‌ی منافقان دو دسته شده‌اید؟ با آن که خداوند آنان را به کیفر کارهای ناپسندی که مرتکب شده‌اند، سرنگون کرده است. آیا می‌خواهید کسانی را که خداوند [بر اثر اعمال زشتشان] گمراه کرده، به راه آورید؟ [در حالی که] هر کس را خداوند گمراه کند، هرگز راهی برای [نجات و هدایت] او نخواهی یافت.'),
(582, 4, 89, 'آنان دوست دارند که شما همچون خودشان کافر شوید، تا با آنان برابر گردید. بنابر این از آنان دوستانی انتخاب نکنید، مگر آن که در راه خدا هجرت نمایند. پس اگر سر باز زدند [و به خیانت و جنایتشان علیه شما ادامه دادند،] آنان را هر جا یافتید، دستگیر کنید و به قتل برسانید. و از بین آنان، هیچ دوست و یاوری نگیرید.'),
(583, 4, 90, 'مگر کسانی که به گروهی که میان شما و آنها پیمانی برقرار است، بپیوندند یا به نزد شما بیایند در حالی که سینه‌هایشان از جنگیدن با شما یا با قوم خود به تنگ آمده باشد. و اگر خدا می‌خواست، مسلّماً آنها را بر شما مسلط می‌نمود و حتماً با شما می‌جنگیدند. پس اگر از شما کناره گرفتند و با شما پیکار نکردند و پیشنهاد صلح و سازش دادند، دیگر خداوند برای شما راهی برای تعرّض و جنگ با آنان قرار نداده است.'),
(584, 4, 91, 'به زودی گروهی دیگر [از منافقانِ اطراف مدینه] را خواهید یافت که می‌خواهند [با برقراری عهد و پیمان و اظهار اسلام،] از جانب شما در امان باشند و [با اظهار کفر،] از قوم خود ایمن گردند. [اما] هر بار که به سوی فتنه‌گری خوانده شوند، با سر در آن فرومی‌روند. پس اگر از [درگیری با] شما کناره نگرفتند و پیشنهاد صلح ندادند و دست [از فتنه و فساد] برنداشتند، آنان را هر جا یافتید، دستگیر کنید و به قتل برسانید. آنها کسانی هستند که ما برای شما علیه آنان حجّت و دلیلی آشکار قرار دادیم.'),
(585, 4, 92, 'و برای هیچ مؤمنی سزاوار نیست که مؤمنی را به قتل برساند، مگر [این که] از روی خطا و اشتباه [مرتکب این کار شود]. و هر کس مؤمنی را به خطا کشت، پس باید برده‌ی مؤمنی را آزاد کند و خون‌بهای کشته را به خانواده‌اش بپردازد، مگر آن که [خانواده‌ی مقتول] بگذرند و اگر [مقتول] با آن که مؤمن است، از قومی است که با شما دشمنند، [جریمه‌اش فقط] آزاد کردن یک برده‌ی مؤمن است [و پرداخت خون‌بها به دشمن لازم نیست]. و اگر از قومی باشد که میان شما و آنان پیمانی برقرار است، باید دیه‌ی او را به خانواده‌اش بپردازد و برده‌ی مؤمنی را نیز آزاد کند. و اگر [برده‌ای برای آزاد کردن، یا پولی برای خرید برده] نیافت، پس دو ماه پیاپی روزه بگیرد. [این تخفیف در حکم، گذشت و] بازگشتی است از جانب خداوند، و خداوند همواره دانا و حکیم است.'),
(586, 4, 93, 'و هر کس مؤمنی را از روی عمد بکشد، کیفرش دوزخ است، همیشه در آن خواهد بود، و خداوند بر او خشم می‌گیرد و او را از رحمت خود دور می‌سازد و برای او عذابی بزرگ آماده ساخته است.'),
(587, 4, 94, 'ای کسانی که ایمان آورده‌اید! هرگاه در راه خدا [برای جهاد] سفر می‌کنید، پس تحقیق کنید و به خاطر این که [غنیمت و] کالای ناپایدار دنیا را به دست آورید، به کسی که نزد شما اظهار اسلام می‌کند، نگویید: «تو مؤمن نیستی!» چرا که [برای شما] غنیمت‌های بسیاری نزد خداوند است. شما نیز خودتان قبلاً این‌گونه بودید، پس خداوند بر شما منّت نهاد [و ایمان در جانتان نفوذ کرد.] پس [در کار جنگ] تحقیق کنید [و زود دست به اسلحه نبرید] که خداوند همواره به آنچه انجام می‌دهید، آگاه است.'),
(588, 4, 95, 'مؤمنانی که بدون بیماری [یا عذر واقعی، از جهاد] کناره می‌گیرند، با مجاهدانی که با اموال و جان‌های خویش در راه خدا پیکار می‌کنند، یکسان نیستند. خداوند مجاهدان با مال و جان را، در رتبه و مقام، بر خانه‌نشینان برتری بخشیده است و به هر یک [به خاطر ایمان و عمل صالح] وعده‌ی [پاداش] نیک داده، ولی جهادکنندگان را بر خانه‌نشینان، به پاداشی بزرگ برتری داده است.'),
(589, 4, 96, '[آن پاداش بزرگ،] درجاتِ [والا] همراه با آمرزش و رحمت از جانب اوست. و خداوند همواره آمرزنده و مهربان است.'),
(590, 4, 97, 'هنگامی که فرشتگان جانِ کسانی را که به خود ستم کرده‌اند، می‌گیرند، از آنان می‌پرسند: «شما در چه وضعی بودید؟» آنها می‌گویند: «ما در سرزمینِ [خود، تحت فشار و] مستضعف بودیم [و به ناچار در جبهه‌ی کافران قرار داشتیم.]» فرشتگان می‌گویند: «مگر زمین خداوند گسترده نبود تا در آن هجرت کنید؟» آنان، [عذری ندارند و] جایگاهشان دوزخ است. و بد سرانجامی است!'),
(591, 4, 98, 'مگر مردان و زنان و کودکان مستضعفی که [برای نجات از محیط کفر،‌] قدرت تدبیر و چاره‌جویی ندارند و راهی [برای هجرت] نمی‌یابند.'),
(592, 4, 99, 'تنها اینانند که امید است خداوند از آنان درگذرد و خداوند همواره آمرزنده و بخشاینده است.'),
(593, 4, 100, 'و هر که در راه خدا [از سرزمین کفر] مهاجرت کند، در روی زمین، مکان‌های بسیار [برای سکونت] و گشایش [امورش] خواهد یافت و هر که از خانه‌اش، به قصد هجرت به سوی خدا و پیامبرش خارج شود، و آن‌گاه مرگش فرارسد، بی‌تردید پاداش او بر خداست. و خداوند همواره آمرزنده و مهربان است.'),
(594, 4, 101, 'و هنگامی که به سفر می‌روید، اگر بیم آن دارید که کفار به شما آسیبی برسانند، گناهی بر شما نیست که نماز را کوتاه کنید. زیرا کافران همواره دشمن آشکار شمایند.'),
(595, 4, 102, '[ای پیامبر!] هرگاه [در سفرهای جهادی] در میان سپاه اسلام بودی و برای آنان نماز برپا داشتی، گروهی از آنان با تو [به نماز] بایستند و سلاح‌های خود را همراه داشته باشند، پس چون سجده کردند، [قیام نمایند و رکعت دوم را فرادا به پایان برند و آن‌گاه برای حفاظت از شما] پشت سرتان قرار گیرند، و گروه دیگر که نماز نخوانده‌اند، [از رکعت دوم] با تو نماز بخوانند و وسایل دفاعی و سلاح‌هایشان را با خود داشته باشند. کافران دوست دارند که شما از سلاح و ساز و برگ جنگی خود غافل شوید تا یکباره بر شما حمله آورند. و اگر نزول باران شما را به رنج انداخت یا بیمار بودید، باکی نیست که اسلحه را فروگذارید و [فقط] وسایل دفاعی را با خود داشته باشید. خداوند برای کافران عذابی خوارکننده فراهم کرده است.'),
(596, 4, 103, 'پس چون نماز را به پایان بردید، خداوند را در [همه] حال یاد کنید: ایستاده و نشسته و در حالی که به پهلو خوابیده‌اید. پس هرگاه آرامش یافتید [و حالت خوف تمام شد،] نماز را [به صورت کامل] برپا دارید. همانا، نماز بر مؤمنان، وظیفه‌ای مقرّر در اوقاتی مشخص است.'),
(597, 4, 104, 'در تعقیب دشمن سستی نکنید، اگر [شما به خاطر جراحت‌ها] درد و رنج می‌برید، آنان نیز همانند شما [جراحت دیده و] زجر می‌کشند، در حالی که شما به [امدادهای غیبی و پاداش] خداوند امید دارید و آنها آن امید را ندارند. و خداوند همواره دانا و حکیم است.'),
(598, 4, 105, 'ما این کتاب را به حقّ بر تو فروفرستادیم تا بر اساس آنچه خدا به تو آموخته و نمایانده است، میان مردم داوری کنی و جانب‌دار خائنان مباش.'),
(599, 4, 106, 'و از خداوند آمرزش بخواه، خداوند همواره آمرزنده مهربان است.'),
(600, 4, 107, 'و از کسانی که به خویشتن خیانت می‌کنند، دفاع مکن، که خداوند خیانت‌پیشگانِ گنهکار را دوست ندارد.'),
(601, 4, 108, '[آنان کارهای خود را] از مردم پنهان می‌کنند، ولی نمی‌توانند از خداوند پنهان دارند، و در مجالس شبانه، هنگامی که سخنانی می‌گویند که مورد رضای خدا نیست، خدا با آنان است و خداوند همواره به آنچه انجام می‌دهند، احاطه دارد.'),
(602, 4, 109, 'آری! شما همان کسانی هستید که در زندگی دنیا از [منافقان و خیانت‌های] آنان جانبداری می‌کردید، امّا کیست که در روز قیامت در برابر خداوند از آنان جانبداری کند؟ یا چه کسی کارساز آنان خواهد شد؟'),
(603, 4, 110, 'و هر کس کار بدی کند یا بر خویشتن ستم نماید، سپس از خداوند طلب آمرزش کند، خداوند را آمرزنده و مهربان خواهد یافت.'),
(604, 4, 111, 'و هر کس گناهی مرتکب شود، تنها به زیان خویش کار کرده، و خداوند همواره دانایی فرزانه است.'),
(605, 4, 112, 'و هر کس خطایی یا گناهی کند، سپس آن را به گردن بی‌گناهی اندازد، به یقین بار تهمت و گناهی آشکار را بر دوش کشیده است.'),
(606, 4, 113, 'و اگر فضل و رحمت خداوند، شامل حال تو نبود، گروهی از منافقان تصمیم داشتند که تو را [در داوری] منحرف کنند؛ امّا جز خودشان را گمراه نمی‌کنند و به تو هیچ زیانی نمی‌رسانند. و خداوند کتاب و حکمت را بر تو نازل کرد، و آنچه را نمی‌دانستی به تو آموخت. و فضل خداوند بر تو، بسی بزرگ بوده است.'),
(607, 4, 114, 'در بسیاری از سخنان درِ گوشی آنان، سودی نیست، مگر [سخن] کسی که به صدقه یا کار نیک یا اصلاح و آشتی میان مردم فرمان دهد. و هر کس برای به دست آوردن خشنودی خدا چنین کند، به زودی پاداشی بزرگ به او خواهیم داد.'),
(608, 4, 115, 'و هر کس پس از آن که راه هدایت برایش آشکار شد، با پیامبر مخالفت کند و از راهی جز راه مؤمنان پیروی کند، او را به همان سوی که روی کرده واگذاریم و وی را به دوزخ افکنیم. و چه بد فرجامی است!'),
(609, 4, 116, 'بی‌گمان خداوند [گناهِ] شرک را [بدون توبه] نمی‌آمرزد؛ ولی پایین‌تر و کمتر از آن را برای هر که بخواهد می‌بخشد. و هر کس برای خدا همتایی قرار دهد، قطعاً دچار گمراهی دور و درازی شده است.'),
(610, 4, 117, 'آنچه آنها غیر از خدا می‌خوانند، تنها بت‌هایی است [که نام‌های] مؤنث [بر آنها نهاده‌اند و در حقیقت]، آنها جز شیطانی سرکش را نمی‌خوانند.'),
(611, 4, 118, '[شیطانی که] خداوند لعنتش کرد و او گفت: «قطعاً از میان بندگانت، [عدّه‌ای را پیرو خود خواهم ساخت و] سهمی معین خواهم گرفت.'),
(612, 4, 119, 'و مسلّماً آنان را گمراه می‌کنم و به آرزوها سرگرمشان می‌سازم و به آنان دستور می‌دهم که [کارهای خرافی انجام دهند،] گوش چهارپایان را بشکافند [تا علامت باشد که این حیوان سهم بت‌هاست،] و به آنان فرمان می‌دهم که آفرینش خدا را دگرگون سازند.» و هر کس به جای خدا، شیطان را دوست و سرپرست خود برگزیند، بی‌گمان به زیانی آشکار دچار گردیده است.'),
(613, 4, 120, '[آری! شیطان] به آنان وعده می‌دهد و به آرزوها، سرگرمشان می‌سازد. و شیطان جز فریب و نیرنگ، وعده‌ای به آنان نمی‌دهد.'),
(614, 4, 121, 'آنان، جایگاهشان دوزخ است و از آن راه گریزی نمی‌یابند.'),
(615, 4, 122, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به زودی آنان را در باغ‌هایی وارد می‌کنیم که از زیرِ [درختان] آن جوی‌ها روان است. جاودانه در آن خواهند بود. وعده‌ی الهی راست است. و چه کسی در سخن گفتن، راستگوتر از خداوند است؟'),
(616, 4, 123, '[کیفر و پاداش الهی،] به دلخواه شما [مسلمانان] و اهل کتاب نیست، هر کس بدی کند، مطابق آن کیفر داده می‌شود. و کسی را جز خداوند، برای خویش سرپرست و یاور نخواهد یافت.'),
(617, 4, 124, 'و کسانی که کارهای شایسته انجام دهند و مؤمن باشند، مرد باشند یا زن؛ به بهشت وارد می‌شوند و کمترین ستمی به آنها نمی‌شود.'),
(618, 4, 125, 'و دین و آیینِ چه کسی بهتر از آن کس است که خود را تسلیم خدا کند و نیکوکار باشد و از آیین ابراهیمِ حق‌گرا پیروی کند؟ و خداوند، ابراهیم را به دوستی خود برگزید.'),
(619, 4, 126, 'و آنچه در آسمان‌ها و آنچه در زمین است، فقط از آن خداوند است. و خداوند همواره بر هر چیز احاطه دارد.'),
(620, 4, 127, '[ای پیامبر!] درباره‌ی [حقوق] زنان از تو فتوا می‌خواهند، بگو: «خدا با آیات قرآن که بر شما خوانده می‌شود، درباره‌ی آنها به شما فتوا می‌دهد، و نیز درباره‌ی زنان یتیمی که حقّشان را نمی‌دهید و می‌خواهید با آنها ازدواج کنید، و کودکان یتیم ناتوان [خدا به شما سفارش می‌کند] که با یتیمان به عدالت رفتار کنید. و [بدانید] هر کار نیکی انجام دهید، خدا از آن آگاه است.»'),
(621, 4, 128, 'و اگر زنی از ناسازگاری یا بی‌اعتنایی شوهرش بیم داشته باشد، مانعی ندارد که آن‌دو به‌‌گونه‌ای پسندیده با یکدیگر مصالحه کنند [و از پاره‌ای حقوق خود، چشم بپوشند.] و صلح و سازش بهتر است؛ ولی جان‌ها در معرض بخل و آزمندی است. و اگر [به همسرانتان] نیکی کنید و [از ناسازگاری] بپرهیزید، بی‌تردید خداوند به آنچه می‌کنید، آگاه است.'),
(622, 4, 129, 'و هرگز نمی‌توانید [در علاقه‌ی قلبی] میان همسران به عدالت رفتار کنید، هر چند کوشش فراوان کنید. پس تمایل خود را یکسره متوجّه یک طرف نسازید تا دیگری را بلاتکلیف رها کنید. و اگر راه صلح و آشتی و تقوا پیش گیرید، بی‌تردید خداوند همواره آمرزنده و مهربان است.'),
(623, 4, 130, 'و اگر آن دو از یکدیگر جدا شوند، خداوند هر یک را از رحمت گسترده‌ی خویش بی‌نیاز می‌کند. و خداوند همواره گشایش‌دهنده و حکیم است.'),
(624, 4, 131, 'و آنچه در آسمان‌ها و آنچه در زمین است، از آن خداست و ما کسانی را که پیش از شما کتاب آسمانی به آنها داده شده بود، و نیز شما را سفارش کردیم که از [نافرمانی] خدا بپرهیزید و اگر کافر شوید، [زیانی به او نمی‌رسانید، زیرا] آنچه در آسمان‌ها و آنچه در زمین است، از آن خداست. و خداوند همواره بی‌نیاز و شایسته ستایش است.'),
(625, 4, 132, 'و آنچه در آسمان‌ها و زمین است، از آنِ خداست و برای کارسازیِ [امور جهان،] خداوند کافی است.'),
(626, 4, 133, 'ای مردم! اگر خدا بخواهد شما را از میان می‌برد و افراد دیگری را می‌آورد و خداوند بر این کار تواناست.'),
(627, 4, 134, 'هر کس که فقط پاداش دنیا را بخواهد، [بداند که] پاداش دنیا و آخرت نزد خداست. و خداوند همواره شنوای بیناست.'),
(628, 4, 135, 'ای کسانی که ایمان آورده‌اید! همواره برپادارنده‌ی عدالت باشید و برای خدا گواهی دهید، گرچه به زیان شما، یا پدر و مادر و نزدیکانتان باشد. اگر [یکی از طرفین نزاع] ثروتمند یا فقیر باشد، [شما بر خلاف حق گواهی ندهید، زیرا] خداوند به [رعایت حال] آنان سزاوارتر است. پس از هوای نفس پیروی نکنید تا بتوانید به عدالت رفتار کنید. و اگر زبان بگردانید [و به ناحق گواهی بدهید] یا [از گواهی حقّ] روی بگردانید، پس [بدانید که] خداوند به آنچه می‌کنید، آگاه است.'),
(629, 4, 136, 'ای کسانی که ایمان آورده‌اید! به خدا و پیامبرش و کتابی که بر رسولش نازل کرده و کتابی که پیشتر فرستاده است، [به راستی] ایمان بیاورید. و هر کس به خدا و فرشتگان و کتب آسمانی و پیامبرانش و روز قیامت کفر ورزد، پس حقیقتاً گمراه شده است، گمراهی دور و درازی.'),
(630, 4, 137, 'کسانی که ایمان آوردند، سپس کافر شدند، بار دیگر ایمان آوردند و دوباره کفر ورزیدند، سپس بر کفر خود افزودند، هرگز امیدی نیست که خداوند آنان را ببخشد و به راه حقّ هدایتشان کند.'),
(631, 4, 138, '[ای پیامبر!] منافقان را به عذابی دردناک مژده بده.'),
(632, 4, 139, 'آنان که کافران را به جای مؤمنان، سرپرست و دوست خود می‌گزینند. آیا عزّت و سربلندی را نزد آنان می‌جویند؟ همانا عزتّ به تمامی از آن خداست.'),
(633, 4, 140, 'و خداوند در قرآن [این حکم را] بر شما نازل کرده است که هرگاه شنیدید آیات خدا مورد انکار یا تمسخر قرار می‌گیرد، با آنان همنشینی نکنید تا به سخن دیگری بپردازند، وگرنه، شما هم مانند آنان خواهید بود. خداوند همه‌ی منافقان و کافران را در دوزخ جمع می‌کند.'),
(634, 4, 141, '[منافقان] کسانی [هستند] که پیوسته در کمین و مراقب شمایند، پس اگر فتح و پیروزی از سوی خدا نصیب شما گردد، می‌گویند: «مگر ما با شما نبودیم؟ [پس باید در غنائم شریک شویم.]» و اگر بهره‌ای نصیب کافران گردد، [به آنان] می‌گویند: «مگر ما بر شما دست نیافتیم و شما را از [گزند] مؤمنان بازنداشتیم؟» آری! خداوند روز قیامت، میان شما داوری خواهد کرد. و هرگز خداوند راهی برای تسلّط کافران بر مؤمنان قرار نداده است.'),
(635, 4, 142, 'منافقان [به گمان خود] با خدا مکر و حیله می‌کنند، و خدا نیز با آنان تدبیر می‌کند [و فریبشان را به خودشان بازمی‌گرداند]. آنها با کسالت به نماز می‌ایستند، آن هم از سر ریاکاری و نشان‌دادنِ خود به مردم، و بسیار کم به یاد خدا هستند.'),
(636, 4, 143, '[منافقان] بین دو گروه [مؤمنان و کافران] دودل و سرگشته‌اند، نه با این گروهند، نه با آن گروه. و هر کس را خداوند به [سبب اعمالش] در گمراهی‌اش رها کند، هرگز برای او راه نجاتی نخواهی یافت.'),
(637, 4, 144, 'ای کسانی که ایمان آورده‌اید! کافران را به جای مؤمنان دوست و سرپرست خود نگیرید، آیا می‌خواهید [با این عمل،] برای خداوند دلیلی آشکار به زیان خودتان قرار دهید؟'),
(638, 4, 145, 'قطعاً منافقان، در پست‌ترینِ طبقات آتشند، و هرگز برای آنان یاوری نخواهی یافت.'),
(639, 4, 146, 'مگر کسانی که توبه کنند و [گذشته‌ی خود را] اصلاح نمایند و به خدا پناه برند و دین خود را برای خدا خالص کنند، اینان در زمره‌ی مؤمنان هستند. و خداوند به زودی مؤمنان را پاداشی بزرگ خواهد داد.'),
(640, 4, 147, 'اگر شکرگزاری کنید و ایمان بیاورید، خدا را با عذاب شما چه کار؟ خداوند همواره قدرشناس و داناست.'),
(641, 4, 148, 'خداوند بدگویی به صدای بلند را دوست ندارد، مگر برای کسی که به او ستم شده باشد و خداوند، شنوای داناست.'),
(642, 4, 149, 'اگر کار نیکی را آشکارا یا پنهان انجام دهید، یا از لغزش و بدی دیگران درگذرید، [بدانید که] خداوند بخشاینده‌ی تواناست.'),
(643, 4, 150, 'قطعاً کسانی که به خداوند و پیامبرانش کفر می‌ورزند و می‌خواهند میان خدا و پیامبرانش فرق بگذارند و می‌گویند: «به بعضی ایمان می‌آوریم و بعضی را انکار می‌کنیم.» و می‌خواهند در این میانه راهی در پیش گیرند.'),
(644, 4, 151, 'آنان به راستی کافرند و ما برای کافران عذابی خوارکننده آماده کرده‌ایم.'),
(645, 4, 152, 'و کسانی که به خدا و پیامبرانش ایمان آورده و میان هیچ‌یک از آنان فرق نگذاشته‌اند، اینانند که خداوند در آینده پاداششان را خواهد داد. و خداوند، همواره بخشاینده و مهربان است.'),
(646, 4, 153, '[ای پیامبر!] اهل کتاب از تو می‌خواهند که یکباره از آسمان بر آنها کتابی فرود آوری، آنها از موسی بزرگ‌تر از آن را خواستند و گفتند: «خدا را آشکارا به ما نشان بده!» پس به سزای ظلمشان صاعقه آنان را فراگرفت، سپس بعد از آن که معجزه‌های روشن برای آنان آمد، گوساله‌پرستی را پیش گرفتند. [بعد از توبه] ما از آن [گناه] درگذشتیم و عفو کردیم و به موسی حجّتی آشکار دادیم.'),
(647, 4, 154, 'و کوه طور را به خاطر پیمان‌گرفتن از آنان بر فراز آنها برافراشتیم و به آنها گفتیم: «[برای توبه،] سجده‌کنان از در [بیت‌المقدّس] وارد شوید.» و به آنان گفتیم: «در روز[های] شنبه [از حکم تحریم صید ماهی] تجاوز نکنید.» و از آنان پیمانی محکم گرفتیم.'),
(648, 4, 155, 'و به خاطر پیمان‌شكنی، و كفر آنان به آیات خدا و كشتن پیامبران به ناحقّ، و این كه [از روی تمسخر] می‌گفتند: «بر دل‌های ما پرده افتاده است [که سخنان پیامبران را درك نمی‌كنیم، خداوند آنان را مجازات کرد.]» بلكه خداوند بر دل‌های آنان به خاطر كفرشان مهر زد و جز اندكی، ایمان نمی‌آورند.'),
(649, 4, 156, 'و [نیز] به خاطر كفرشان و سخنانشان كه تهمت بزرگی به مریم زدند،'),
(650, 4, 157, 'و سخنی كه [مغرورانه] گفتند: «ما مسیح؛ عیسی پسر مریم، فرستاده‌ی خدا را كشتیم.» در حالی كه نه او را كشتند و نه به صلیب كشیدند، بلكه كار بر آنان مُشتَبَه شد [و كسی شبیه به او را كشتند]. و كسانی كه درباره‌ی عیسی اختلاف نظر پیدا كردند، خود در شك بودند و هیچ یك به گفته‌ی خود علم نداشتند و تنها از پندارهای بی‌اساس پیروی می‌كردند، و به یقین او را نكشتند.'),
(651, 4, 158, 'بلكه خداوند او را به سوی خود بالا برد. و خداوند همواره پیروزمندی حكیم است.'),
(652, 4, 159, 'و اهل كتاب همه پیش از مرگ به او ایمان می‌آورند و او روز قیامت، درباره‌ی آنها گواهی خواهد داد.'),
(653, 4, 160, 'پس به سزای ستمی كه از یهودیان سر زد و به خاطر آن كه بسیاری از مردم را از راه خدا بازمی‌داشتند، بخشی از چیزهای پاك و دلپسندی را كه قبلاً برایشان حلال بود، بر آنان حرام كردیم.'),
(654, 4, 161, 'و [نیز این مجازات] به سبب رباخواری آنها بود كه از آن نهی شده بودند و نیز به سبب خوردن مال مردم به ناحقّ. و برای كافرانِشان عذاب دردناكی فراهم ساخته‌ایم.'),
(655, 4, 162, 'ولی استواران در علم از یهود و مؤمنان، به آنچه بر تو و آنچه پیش از تو نازل شده است، ایمان دارند. و به ویژه برپادارندگان نماز، و زكات‌دهندگان، و مؤمنانِ به خدا و روز قیامت، که به زودی آنان را پاداش بزرگی خواهیم داد.'),
(656, 4, 163, '[ای پیامبر!] ما به تو وحی كردیم، آن‌گونه كه به نوح و پیامبران پس از او وحی فرستادیم. و [همچنین] به ابراهیم، اسماعیل، اسحاق، یعقوب، [پیامبران از] فرزندان یعقوب، عیسی، ایوب، یونس، هارون و سلیمان وحی نمودیم. و به داوود، زَبور دادیم.'),
(657, 4, 164, 'و پیامبرانی [فرستادیم] كه پیش از این داستان آنان را برایت بازگفته‌ایم و پیامبرانی كه ماجرای آنان را برای تو نگفته‌ایم. و خداوند با موسی سخن گفت، سخن گفتنی ویژه.'),
(658, 4, 165, 'پیامبرانی كه بشارت‌دهنده و بیم‌دهنده بودند، تا پس از [آمدن] این پیامبران، برای مردم در مقابل خدا [بهانه و] حجّتی باقی نماند. و خداوند همواره شكست‌ناپذیر و حكیم است.'),
(659, 4, 166, '[گر چه كافران بهانه می‌گیرند و به درستیِ كتاب تو گواهی نمی‌دهند،] ولی خداوند، به آنچه بر تو نازل كرده گواهی می‌دهد، كه به علم خود بر تو نازل كرد و فرشتگان نیز [بر حقّانیت تو] شهادت می‌دهند و گواهی خداوند [برای تو] كافی است.'),
(660, 4, 167, 'قطعاً كسانی كه كافر شدند و [مردم را] از راه خدا بازداشتند، به گمراهی ژرفی افتاده‌اند.'),
(661, 4, 168, 'كسانی كه كافر شدند و ستم كردند، خداوند هرگز آنها را نمی‌بخشد، و آنان را به هیچ راهی هدایت نمی‌كند.'),
(662, 4, 169, 'مگر به راه دوزخ، كه برای همیشه در آن ماندگارند. و این [عذاب و كیفر] برای خدا آسان است.'),
(663, 4, 170, 'ای مردم! پیامبرِ [موعود] با آیین حقّ از سوی پروردگارتان به سوی شما آمده است. پس ایمان بیاورید كه برای شما بهتر است، و اگر كفر ورزید، [به خدا زیانی نمی‌رسد كه] آنچه در آسمان‌ها و زمین است از آنِ خداست و خداوند، دانا و حكیم است.'),
(664, 4, 171, 'ای اهل کتاب! در دین خود غلوّ [و زیاده‌روی] نکنید و درباره خدا جز حقّ نگویید. به راستی مسیح، عیسی پسر مریم، فرستاده‌ی خداوند و کلمه‌ی اوست که آن را به [دامان] مریم افکند و روحی است از جانب او. پس به خدا و پیامبرانش ایمان بیاورید، و نگویید: «[خداوند] سه‌گانه است.» [از این حرف] دست بردارید که به نفع شماست. جز این نیست که اللّه خدایی است یکتا، و از این که فرزندی داشته باشد، منزّه است. آنچه در آسمان‌ها و آنچه در زمین است از آن اوست. و خداوند برای کارسازی [و تدبیر امور جهان] کافی است.'),
(665, 4, 172, 'هرگز مسیح از این که بنده‌ی خدا باشد، سر باز نمی‌زند و فرشتگان مقرّب نیز [سرپیچی ندارند]. و هر کس از بندگی خداوند سرپیچی کند و تکبّر ورزد، پس [بداند که] خداوند به زودی همه آنها را نزد خود جمع خواهد کرد.'),
(666, 4, 173, 'پس کسانی که ایمان آوردند و کارهای شایسته انجام دادند، خداوند پاداش آنان را کامل می‌دهد و از فضل خویش بر [پاداش] آنها می‌افزاید. و امّا آنان که سرپیچی کردند و تکبّر ورزیدند، خداوند آنان را به عذابی دردناک کیفر می‌دهد و در برابر [قهر] خدا برای خود دوست و یاوری نخواهند یافت.'),
(667, 4, 174, 'ای مردم! دلیل روشنی از سوی پروردگارتان برای شما آمده است. و نوری روشنگر [همچون قرآن] را به سوی شما فرود آوردیم.'),
(668, 4, 175, 'پس به زودی کسانی را که به خداوند ایمان آوردند و به آن [کتاب آسمانی] چنگ زدند، در رحمت و فضل خویش وارد می‌کند و آنان را به سوی خود در راه راست هدایت می‌‌فرماید.'),
(669, 4, 176, '[ای پیامبر!] از تو فتوا می‌خواهند، بگو: «خدا درباره [ارثِ] کلاله به شما چنین فتوا می‌دهد: اگر مردی بمیرد و فرزندی نداشته باشد، و او را یک خواهر باشد، نصف آنچه بجا گذاشته از آنِ او است و [این برادر] نیز همه اموال خواهر را ارث می‌برد، اگر او فرزندی نداشته باشد، و اگر دو خواهر باشند، دو سوم میراث را ارث می‌برند و اگر چند برادر و خواهر باشند، سهم هر مرد برابر سهم دو زن است. خداوند احکام را برای شما بیان می‌کند تا گمراه نشوید و خداوند به هر چیز داناست.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای کسانی که ایمان آورده‌اید! به پیمان‌ها و قراردادها وفا کنید. چهارپایان برای شما حلال شده است، مگر آنچه بر شما خوانده خواهد شد. و صید را به هنگام احرام حلال نشمرید. خداوند به هرچه اراده کند حکم می‌کند.'),
(671, 5, 2, 'ای کسانی که ایمان آورده‌اید! شعائر الهی [را محترم بشمارید و مخالفتِ با آنها] را حلال ندانید: حرمت ماه حرام، قربانی‌های بی‌نشان، قربانی‌های نشاندار که بر گردنشان بند نهاده‌اند، و نیز حرمت آنها که به قصد خانه خدا برای به دست آوردن فضل پروردگار و خشنودی او می‌آیند، حرمت همه را نگاه دارید. اما هنگامی که از احرام بیرون آمدید، صید کردن برای شما مانعی ندارد. و نباید کینه گروهی که مانع آمدن شما به مسجدالحرام [در سالِ حُدَیبیه] شدند، شما را وادار به تعدّی و تجاوز کند. و یکدیگر را در کار نیک و پرهیزکاری یاری کنید و در راه گناه و تجاوز همدستی ننمایید. و از [مخالفت] خدا بپرهیزید که مجازاتش شدید است.'),
(672, 5, 3, '[گوشتِ] مردار و خون و گوشت خوک و حیوانی که هنگام ذبح نام غیر خدا بر آن برده شود و حیوان خفه شده و به زجر کشته شده و حیوانی که بر اثر پرت شدن از بلندی بمیرد و حیوانی که به ضرب شاخ حیوان دیگری مرده باشد و نیم خورده حیوان درنده، جز آن را که [تا زنده است] سر ببرید و حیوانی که در برابر بت‌ها ذبح شود و [همچنین] تقسیم کردن گوشت حیوان با تیرهای بخت‌آزمایی، [اینها همه] بر شما حرام و آن [کارها هم] گناه است. ـ امروز کافران از [شکست] آیین شما ناامید شدند، پس از آنها نترسید و از [مخالفت] من بترسید! امروز دین شما را برایتان کامل کردم و نعمت خود را بر شما تمام نمودم و اسلام را به عنوان آیین [جاودان] شما پذیرفتم. ـ اما آن که در حال گرسنگی و قحطی، ناگزیر [از خوردن گوشت حرام] شود، بی‌آنکه گرایش به گناه داشته باشد، خداوند آمرزنده و مهربان است.'),
(673, 5, 4, 'از تو می‌پرسند چه چیزهایی برای آنها حلال شده است؟ بگو: «آنچه پاکیزه است، برای شما حلال گردیده و [نیز صید] حیوانات شکاری که از آنچه خداوند به شما تعلیم داده به آنها یاد داده‌اید [برای شما حلال است]. پس از آنچه این حیوانات برای شما [صید می‌کنند و] نگاه می‌دارند، بخورید و نام خدا را [به هنگام فرستادن حیوان برای شکار] بر آن ببرید و از [نافرمانی] خدا بپرهیزید که خداوند سریع‌الحساب است.»'),
(674, 5, 5, 'امروز چیزهای پاکیزه برای شما حلال شد و [همچنین] غذایِ اهلِ کتاب برای شما حلال است و غذای شما برای آنها حلال. و [نیز ازدواج با] زنان پاکدامن از مسلمانان و زنان پاکدامن از اهل کتاب، به شرط آن که مهریه آنها را بپردازید و پاکدامن باشید، نه زناکار و نه گیرندگان دوست پنهان. و کسی که آنچه را باید به آن ایمان بیاورد، انکار کند، اعمال او باطل و بی‌اثر می‌گردد و در سرای دیگر از زیانکاران خواهد بود.'),
(675, 5, 6, 'ای کسانی که ایمان آورده‌اید! هنگامی که برای نماز برمی‌خیزید، صورت و دست‌ها را تا آرنج بشویید و بخشی از سرتان را مسح کنید و نیز پاهای خود را تا برآمدگی روی پا مسح نمایید و اگر جنب هستید، پس [با غسل کردن خود را] پاک کنید. و اگر بیمار یا مسافر باشید یا از قضای حاجت برگشته باشید، یا با زنان، آمیزش جنسی کرده‌اید و آب [برای غسل یا وضو] نیابید، پس با خاک پاکی تیمم کنید و از آن بر بخشی از صورت و دست‌ها نیز بکشید. خداوند نمی‌خواهد که بر شما سخت بگیرد، بلکه می‌خواهد شما را پاک سازد و نعمتش را بر شما تمام نماید، شاید شکر او را به جای آورید.'),
(676, 5, 7, 'و به یاد بیاورید نعمتی را که خدا بر شما ارزانی داشته و [نیز] پیمان محکمش را که با آن از شما میثاق گرفت، آن زمان که گفتید: «شنیدیم و اطاعت کردیم.» و از [مخالفت فرمان] خدا بپرهیزید که خدا از درون سینه‌ها آگاه است.'),
(677, 5, 8, 'ای کسانی که ایمان آورده‌اید! همواره برای خدا قیام کنید و از روی عدالت گواهی دهید. دشمنی با گروهی، شما را به ترک عدالت نکشاند. عدالت بورزید که به پرهیزکاری نزدیک‌تر است. و از [نافرمانی] خدا بپرهیزید که از آنچه انجام می‌دهید، آگاه است.'),
(678, 5, 9, 'خداوند به کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند وعده‌ی آمرزش و پاداشی بزرگ داده است.'),
(679, 5, 10, 'و کسانی که کافر شدند و آیات ما را تکذیب کردند، اهل دوزخند.'),
(680, 5, 11, 'ای کسانی که ایمان آورده‌اید! نعمتی را که خدا به شما بخشیده به یاد آورید، آن زمان که جمعی [از دشمنان] قصد داشتند دستِ [ستم] به سوی شما دراز کنند [و شما را از میان بردارند،] اما خدا دستشان را از شما کوتاه کرد. از خدا بپرهیزید و مؤمنان باید تنها بر خدا توکل کنند.'),
(681, 5, 12, 'خدا از بنی‌اسرائیل پیمان گرفت و از آنها دوازده رهبر و سرپرست برانگیختیم و خداوند [به آنها] فرمود: «من با شما هستم. اگر نماز را برپا دارید و زکات را بپردازید و به رسولان من ایمان بیاورید و آنها را یاری کنید و به خدا وامِ نیکو دهید [و در راه او به نیازمندان کمک کنید]، مسلماً گناهانتان را محو می‌کنم و شما را در باغ‌های بهشت که نهرها از زیر [درختان] آن جاری است، وارد می‌نمایم. پس از آن، هر یک از شما کفر ورزد، بی‌گمان راه راست را گم کرده است.»'),
(682, 5, 13, 'اما به خاطر پیمان‌شکنی، آنها را از رحمت خویش دور ساختیم و دل‌هایشان را سخت و سنگین نمودیم، زیرا سخنان [خدا] را از مورد خود تحریف می‌کنند و بخشی از آنچه را به آنها گوشزد شده بود، فراموش کردند و [ای پیامبر!] پیوسته به خیانت تازه‌ای از این قوم پی می‌بری، جز عدّه‌ی قلیلی از آنها، ولی از آنها در گذر و خطاهایشان را نادیده بگیر که خداوند نیکوکاران را دوست می‌دارد.'),
(683, 5, 14, 'و ما از کسانی که خود را نصارا [و یاور مسیح] نامیدند [نیز] پیمان گرفتیم [که از حقیقت توحید منحرف نشوند]، ولی آنها [هم] بخش مهمّی از آنچه را به آنان تذکر داده شده بود، به دست فراموشی سپردند. از این رو در میان آنها تا روز قیامت دشمنی و کینه افکندیم. و خداوند در آینده آنها را از آنچه انجام داده‌اند، آگاه خواهد فرمود.'),
(684, 5, 15, 'ای اهل کتاب! پیامبر ما به سوی شما آمد و بسیاری از حقایق کتاب آسمانی را که شما پنهان می‌داشتید، روشن می‌سازد و از بسیاری از آن [که فعلاً مورد نیاز نیست،] صرف‌نظر می‌کند. به راستی از سوی خدا نور و کتابی روشنگر به سوی شما آمد.'),
(685, 5, 16, 'خداوند به برکت آن، هر کسی را که در پی خشنودی وی باشد، به راه‌های بی‌گزند، راهنمایی می‌کند و آنان را به اراده خویش از تاریکی‌ها به سوی روشنایی می‌برد و آنها را به راه راست هدایت می‌نماید.'),
(686, 5, 17, 'کسانی که گفتند: «خدا همان مسیح پسر مریم است.» [اینان] به یقین کافر شدند. بگو: «اگر خدا بخواهد مسیح پسر مریم و مادرش و همه‌ی کسانی را که روی زمین هستند، هلاک کند، چه کسی می‌تواند [از این کار] جلوگیری کند؟! [آری،] حکومت آسمان‌ها و زمین و آنچه میان آن دو قرار دارد، از آن خداست. هر چه بخواهد، می‌آفریند، و او بر هر چیز تواناست.»'),
(687, 5, 18, 'یهودیان و مسیحیان گفتند: «ما فرزندان خدا و دوستان او هستیم.» بگو: «پس چرا شما را در برابر گناهانتان مجازات می‌کند؟ بلکه شما هم انسانی هستید از آفریده‌های او. هر کس را بخواهد [و شایسته ببیند]، می‌بخشد و هر کس را بخواهد [و سزاوار باشد] مجازات می‌کند. و حکومت آسمان‌ها و زمین و آنچه در میان آنهاست، از آنِ اوست. و بازگشت [همه] به سوی اوست.»'),
(688, 5, 19, 'ای اهل کتاب! به راستی رسول ما پس از فاصله‌ای میان فرستادگان، به سوی شما آمد در حالی که حقایق را برای شما بیان می‌کند تا مبادا بگویید: «نه بشارت‌دهنده‌ای به سوی ما آمد و نه بیم‌دهنده‌ای.» [هم اکنون،] بشارت‌دهنده و بیم‌دهنده به سوی شما آمد و خداوند بر همه چیز تواناست.'),
(689, 5, 20, 'و [به یاد آورید] هنگامی که موسی به قوم خود گفت: «ای قوم من! نعمت خدا را بر خود یادآور شوید هنگامی که در میان شما پیامبرانی گماشت و شما را صاحب اختیار خود قرار داد و به شما چیزهایی بخشید که به هیچ یک از جهانیان نداده بود.'),
(690, 5, 21, 'ای قوم من! به سرزمین مقدّسی که خداوند برای شما مقرّر داشته، وارد شوید و به پشت سر خود بازنگردید که زیانکار خواهید شد.»'),
(691, 5, 22, 'گفتند: «ای موسی! در آن [دیار] گروهی زورمند و ستمگرند و ما هرگز وارد آن نمی‌شویم تا آنها خارج شوند. اگر آنها از آن خارج شوند، ما وارد خواهیم شد!»'),
(692, 5, 23, '[ولی] دو نفر از مردان خداترس که خداوند به آنها نعمت داده بود گفتند: «شما از دروازه [شهر بر آنان] یورش برید، همین که وارد شدید، پیروز خواهید شد. و بر خدا توکل کنید، اگر ایمان دارید.»'),
(693, 5, 24, '[بنی‌اسرائیل] گفتند: «ای موسی! تا آنها در آنجا هستند، ما هرگز وارد آن نخواهیم شد. تو و پروردگارت بروید و بجنگید، ما همین جا نشسته‌ایم!»'),
(694, 5, 25, '[موسی] گفت: «پروردگارا! من تنها اختیار خودم و برادرم را دارم. میان ما و این قوم نافرمانِ بدکار جدایی انداز!»'),
(695, 5, 26, 'خداوند فرمود: «این سرزمین [مقدّس]، تا چهل سال بر آنها ممنوع است. پیوسته در زمین سرگردان خواهند بود. پس بر این گروه نافرمان دریغ مخور!»'),
(696, 5, 27, 'و داستان دو فرزند آدم را به درستی بر آنها بخوان. هنگامی که هر کدام کاری برای نزدیکی [به پروردگار] انجام دادند، اما از یکی پذیرفته شد و از دیگری پذیرفته نشد. [قابیل که عملش مردود شده بود، به برادرش هابیل] گفت: «من تو را خواهم کشت.» [هابیل] گفت: «[من چه گناهی دارم؟] خدا تنها از پرهیزکاران می‌پذیرد!'),
(697, 5, 28, 'اگر تو برای کشتنِ من دست دراز کنی، من دست به قتل تو دراز نمی‌کنم، چون از پروردگار جهانیان می‌ترسم.'),
(698, 5, 29, 'من می‌خواهم تو با بارِ گناه [کشتن] من و گناه خودت [به سوی خدا] بازگردی و از دوزخیان گردی. و همین است سزای ستمکاران!»'),
(699, 5, 30, 'نفسِ سرکش به تدریج او را به کشتن برادرش ترغیب کرد و [سرانجام] او را کشت و از زیانکاران شد.'),
(700, 5, 31, 'سپس خداوند کلاغی را فرستاد که در زمین بکاود تا به او نشان دهد چگونه جسد برادر خود را دفن کند. او گفت: «وای بر من! آیا من ناتوان‌تر از آنم که مانند این کلاغ باشم و جسد برادر خود را دفن کنم؟» و سرانجام [از کار خود] پشیمان شد.'),
(701, 5, 32, 'به همین جهت بر بنی‌اسرائیل مقرر داشتیم که هر کس انسانی را بدون این که مرتکب قتل یا فسادی در زمین شده باشد، به قتل برساند، چنان است که گویی همه‌ی انسان‌ها را کشته و هر کس انسانی را از مرگ رهایی بخشد، چنان است که گویی همه‌ی مردم را زنده کرده است. و پیامبران ما با دلایل روشن به سوی بنی‌اسرائیل آمدند، اما بسیاری از آنها، بعد از آن، در زمین زیاده‌روی کردند.'),
(702, 5, 33, 'کیفر آنها که با خدا و پیامبرش به جنگ برمی‌خیزند و برای فساد در روی زمین می‌کوشند، جز این نیست که کشته شوند یا به دار آویخته گردند، یا دست راست و پای چپ آنها بریده شود، یا از سرزمین خود تبعید گردند. این رسوایی آنها در دنیاست. و در آخرت مجازات بزرگی دارند.'),
(703, 5, 34, 'مگر آنها که پیش از دست یافتنِ شما بر آنان توبه کنند. پس بدانید [خدا توبه آنها را می‌پذیرد،] خداوند آمرزنده و مهربان است.'),
(704, 5, 35, 'ای کسانی که ایمان آورده‌اید! از [مخالفتِ فرمان] خدا بپرهیزید و وسیله‌ای [از ایمان، عمل صالح و آبرویِ مقرّبانِ درگاهش‌] برای تقرّب به او بجویید. و در راه او جهاد کنید، باشد که رستگار شوید.'),
(705, 5, 36, 'کسانی که کافر شدند، اگر تمام آنچه در زمین است و همانند آن، مال آنها باشد و همه آن را برای نجات از مجازات روز قیامت بدهند، از آنان پذیرفته نخواهد شد و مجازات دردناکی خواهند داشت.'),
(706, 5, 37, 'آنها پیوسته می‌خواهند از آتش خارج شوند، ولی از آن بیرون نخواهند رفت. و برای آنها مجازاتی پایدار است.'),
(707, 5, 38, 'دست مرد دزد و زن دزد را به کیفر عملی که انجام داده‌اند به عنوان یک مجازات الهی قطع کنید. و خداوند توانا و حکیم است.'),
(708, 5, 39, 'اما آن کس که پس از ستم کردن، توبه و جبران نماید، خداوند توبه‌ی او را می‌پذیرد؛ زیرا خداوند آمرزنده و مهربان است.'),
(709, 5, 40, 'آیا نمی‌دانی که تنها خداوند مالک و حکمران آسمان‌ها و زمین است؟! هر کس را بخواهد [و سزاوار ببیند،] مجازات می‌کند و هر کس را بخواهد [و شایسته بداند،] می‌بخشد. و خداوند بر هر چیزی تواناست.'),
(710, 5, 41, 'ای پیامبر! آنها که در کفر شتاب می‌ورزند تو را اندوهگین نسازند. از کسانی که با زبان می‌گویند: «ایمان آورده‌ایم.» در حالی که دل‌هایشان ایمان نیاورده است. و از یهودیانی که به دروغ [و به ظاهر به سخنان تو] گوش می‌دهند، [در حالی که در واقع] آنها جاسوسانِ گروه دیگری هستند که نزد تو نیامده‌اند، آنها سخنان را از مفهوم اصلی‌اش تحریف می‌کنند، [و به این جاسوسان] می‌گویند: «اگر این [حکمی که ما می‌خواهیم] به شما داده شد، [و محمّد بر طبق خواسته‌ی شما داوری کرد،] بپذیرید؛ و اگر داده نشد، [از او‌] دوری کنید.» و کسی را که خدا بخواهد مجازات کند، هرگز در برابر خدا برای او از دست تو کاری بر نمی‌آید. آنها کسانی هستند که خدا نخواسته دل‌هایشان را پاک کند، در دنیا رسوایی و در آخرت مجازاتی بزرگ نصیب آنان خواهد شد.'),
(711, 5, 42, 'آنها [به سخنان تو] گوش می‌دهند تا [دستاویزی بیابند و] آن را تکذیب کنند و مالِ حرام، فراوان می‌خورند. پس اگر نزد تو آمدند، در میان آنان داوری کن یا آنها را به حال خود واگذار. و اگر از آنها صرف نظر کنی، هرگز زیانی به تو نمی‌توانند برسانند. و اگر میان آنها داوری می‌کنی، با عدالت داوری کن که خدا عادلان را دوست دارد.'),
(712, 5, 43, 'و [یهودیان] چگونه تو را داور قرار می‌دهند، در حالی که تورات نزد آنان است و در آن، حکم خدا [بیان شده] است، سپس آنان بعد از این [پذیرشِ داوری تو،‌] پشت می‌کنند؟ آنان مؤمن نیستند.'),
(713, 5, 44, 'ما تورات را که در آن هدایت و نور بود، فروفرستادیم. پیامبران [بنی‌اسرائیل] که تسلیم [فرمان خدا] بودند، طبق آن بر یهود حکم می‌کردند. و نیز علمای بزرگ یهود و دانشمندان باایمان و پاک، بنابر آنچه از کتاب خداوند به آنان سپرده شده بود و بر آن گواه بودند، برای یهودیان داوری می‌کردند. پس [ای دانشمندان!] از مردم نترسید [و احکام خدا را بیان کنید،] و از من بترسید و آیات مرا به بهای اندک نفروشید. و کسانی که طبق آنچه خداوند نازل کرده داوری نکنند، کافرند.'),
(714, 5, 45, 'و بر یهودیان در تورات مقرّر داشتیم که جان در مقابل جان و چشم در مقابل چشم و بینی در برابر بینی و گوش در مقابل گوش و دندان در برابر دندان است و هر جراحتی قصاص دارد. و اگر کسی بگذرد کفّاره‌ی [گناهان] او محسوب می‌شود. و کسانی که طبق آنچه خداوند نازل کرده داوری نکنند، ستمکارند.'),
(715, 5, 46, 'و به دنبال پیامبران پیشین، عیسی پسر مریم را قرار دادیم در حالی که توراتِ پیش از خود را راست می‌شمرد و به او انجیل دادیم که در آن هدایت و نوری بود و تورات را که پیش از آن بود، راست می‌شمرد و هدایت و پند برای پرهیزکاران بود.'),
(716, 5, 47, 'اهل انجیل باید به آنچه خداوند در آن نازل کرده، داوری کنند. و کسانی که بر طبق آنچه خدا نازل کرده، داوری نمی‌کنند، فاسق‌اند.'),
(717, 5, 48, 'و ما این کتاب را به حق بر تو نازل کردیم، در حالی که کتب پیشین را راست می‌شمرد و نگاهبان و حاکم بر آنهاست، پس بر طبق آنچه خدا نازل کرده، در میان آنها حکم کن و به جای آنچه از حق به تو رسیده است، از هوا و هوس‌های آنها پیروی مکن. ما برای هر یک از شما [امّت‌ها] آیین و طریقه‌ی روشنی قرار دادیم. و اگر خدا می‌خواست، همه شما را امّت یگانه قرار می‌داد؛ ولی [خواست‌] تا شما را در آنچه به شما داده است، بیازماید. پس در نیکی‌ها بر یکدیگر سبقت گیرید. بازگشت همه شما به سوی خداست و از آنچه در آن اختلاف می‌کردید، به شما خبر خواهد داد.'),
(718, 5, 49, 'و در میان اهل کتاب طبق آنچه خداوند نازل کرده، داوری کن و از هوس‌های آنان پیروی مکن و بر حذر باش که مبادا تو را نسبت به بخشی از آنچه خداوند بر تو نازل کرده، منحرف سازند. و اگر آنها روی گردانند، بدان که خداوند می‌خواهد آنها را به خاطر پاره‌ای از گناهانشان مجازات کند. و بسیاری از مردم فاسق‌اند.'),
(719, 5, 50, 'آیا آنها حکم جاهلیت را [از تو] می‌خواهند؟! و چه کسی برای گروهی که یقین دارند، بهتر از خدا حکم می‌کند؟'),
(720, 5, 51, 'ای کسانی که ایمان آورده‌اید! یهودیان و مسیحیان را پشتیبان و دوست خود مگیرید، [که] آنها [در برابر شما] دوست و پشتیبان یکدیگرند و کسانی از شما که آنها را ولیّ خود بدانند، از آنها هستند. خداوند گروه ستمکاران را هدایت نمی‌کند.'),
(721, 5, 52, 'پس کسانی را که در دل‌هایشان بیماری است، می‌بینی که در [دوستی با] آنان بر یکدیگر پیشی می‌گیرند و می‌گویند: «می‌ترسیم حادثه‌ای برای ما پیش آید.» چه بسا خداوند پیروزی یا حادثه‌ی دیگری از سوی خود [به نفع مسلمانان] پیش بیاورد و این دسته، از آنچه در دل پِنهان داشتند، پشیمان گردند.'),
(722, 5, 53, 'و آنها که ایمان آورده‌اند می‌گویند: «آیا این [منافقان] همان‌ها هستند که با نهایت تأکید به خدا سوگند یاد کردند که با شما هستند؟!» اعمالشان نابود گشت و زیانکار شدند.'),
(723, 5, 54, 'ای کسانی که ایمان آورده‌اید! هر کس از شما از آیین خود بازگردد، [بداند که‌] خداوند در آینده قومی را می‌آورد که آنها را دوست دارد و آنها [نیز] او را دوست دارند. [آنان‌] با مؤمنان فروتن و در برابر کافران سرسخت و نیرومندند، در راه خدا جهاد می‌کنند و از سرزنش هیچ ملامت‌گری هراس ندارند. این فضل خداست که به هرکس بخواهد عنایت می‌فرماید و خدا رحمت‌گستری داناست.'),
(724, 5, 55, 'سرپرست و ولیّ شما تنها خداست و پیامبر او و کسانی که ایمان آورده‌اند. همان کسانی که نماز برپا می‌دارند و در حال رکوع زکات می‌دهند.'),
(725, 5, 56, 'و کسانی که ولایت خدا و پیامبر او و آن مؤمنان را بپذیرند، بی‌شک حزب خدا پیروز است.'),
(726, 5, 57, 'ای کسانی که ایمان آورده‌اید! کسانی را که آیین شما را به استهزاء و بازی می‌گیرند، از اهل کتاب وکافران، ولیّ خود انتخاب نکنید و اگر ایمان دارید، از خدا پروا کنید.'),
(727, 5, 58, 'و هنگامی که [اذان می‌گویید و مردم را] به نماز فرامی‌خوانید، آن را به مسخره و بازی می‌گیرند. این به خاطر آن است که آنها گروهی هستند که نمی‌اندیشند.'),
(728, 5, 59, 'بگو: «ای اهل کتاب! آیا جز برای این به ما خرده می‌گیرید که ما به خداوند یگانه، و به آنچه بر ما نازل شده، و به آنچه پیش از این نازل گردیده است، ایمان آورده‌ایم. و حال آن که بیشتر شما نافرمانید.»'),
(729, 5, 60, 'بگو: «آیا شما را از جایگاه و کیفری که نزد خدا بدتر از این است، باخبر کنم؟! کسانی که خداوند آنها را از رحمت خود دور ساخته و مورد خشم قرار داده و بعضی از آنها را [مسخ کرده و] به صورت میمون‌ها و خوک‌هایی قرار داده است. آنها در بندگی طاغوت در آمده‌اند. جایگاه آنها بدتر است و از راه راست گمراه‌ترند.»'),
(730, 5, 61, 'و هنگامی که نزد شما می‌آیند، می‌گویند: «ایمان آورده‌ایم.» در حالی که با کفر آمده و با کفر رفته‌اند. و خداوند از آنچه پنهان می‌داشتند، آگاه‌تر است.'),
(731, 5, 62, 'بسیاری از آنها را می‌بینی که در گناه و تجاوز و حرام‌خواری، بر یکدیگر سبقت می‌جویند، چه زشت است کاری که انجام می‌دهند!'),
(732, 5, 63, 'چرا دانشمندان نصارا و یهود، آنها را از سخنان گناه‌آمیز و حرام‌خواری، نهی نمی‌کنند؟! چه زشت است عملی که انجام می‌دادند!'),
(733, 5, 64, 'و یهودیان گفتند: «دستِ خدا بسته است!» دست‌هایشان بسته باد! و به خاطر این سخن از رحمت [الهی] دور شوند! بلکه هر دو دستِ [قدرتِ] او گشاده است. هرگونه بخواهد، می‌بخشد، و این آیات که از طرف پروردگارت بر تو نازل شده، بر طغیان و کفر بسیاری از آنها می‌افزاید، و ما در میان آنها، تا روز قیامت دشمنی و کینه افکندیم. هر زمان آتش جنگی افروختند، خدا آن را خاموش ساخت، و برای فساد در زمین می‌کوشند و خداوند مفسدان را دوست ندارد.'),
(734, 5, 65, 'و اگر اهل کتاب ایمان می‌آوردند و تقوا پیشه می‌کردند، گناهانشان را می‌بخشیدیم و آنها را در باغ‌های پرنعمت بهشت وارد می‌ساختیم.'),
(735, 5, 66, 'و اگر آنها تورات و انجیل و آنچه را از پروردگارشان به سوی آنها فروفرستاده شده است، برپا می‌داشتند، از نعمت‌های آسمانی و زمینی برخوردار می‌شدند. جمعی از آنها میانه‌رو هستند؛ ولی بیشترشان بدکردارند.'),
(736, 5, 67, 'ای پیامبر! آنچه را از سوی پروردگارت به تو نازل شده است، به طور کامل [به مردم] برسان. و اگر چنین نکنی، [گویی هیچ] پیام او را نرسانده‌ای. [و به وظیفه‌ی رسالت خود عمل نکرده‌ای.] و [بدان که] خداوند تو را از [شرّ] مردم حفظ می‌کند. خداوند گروه کافران را هدایت نمی‌کند.'),
(737, 5, 68, 'بگو: «ای اهل کتاب! بر هیچ چیز [از آیین حقّ] نیستید مگر آن که تورات و انجیل و آنچه را از سوی پروردگارتان نازل شده، برپا دارید.» و بی‌گمان آنچه از پروردگارت به سوی تو فرو فرستاده شده، سرکشی و کفر بسیاری از آنان را خواهد افزود، پس بر کافران [و مخالفت آنان] اندوه مخور!'),
(738, 5, 69, 'کسانی که ایمان آوردند و یهودیان و صابئان و نصارا، هر کدام به خدا و روز قیامت ایمان بیاورند و کار شایسته انجام دهند، نه بیمی خواهند داشت و نه اندوهگین می‌گردند.'),
(739, 5, 70, 'ما از بنی‌اسرائیل پیمان گرفتیم و پیامبرانی به سوی آنان فرستادیم. [امّا] هرگاه پیامبری برایشان [سخن و] پیامی آورد که دلخواهشان نبود، دسته‌ای را دروغگو شمردند و دسته‌ای [دیگر] را کشتند.'),
(740, 5, 71, 'آنها گمان کردند کیفر و آزمایشی [برایشان] نخواهد بود، پس [از دیدن حقایق] کور و [از شنیدن سخنان حق،] کر شدند. سپس خداوند [لطف خویش را بر آنان بازگرداند و] توبه‌شان را پذیرفت. دیگر بار بسیاری از آنان [از دیدن و شنیدن آیات الهی] کور و کر شدند. و خداوند به آنچه می‌کنند، بیناست.'),
(741, 5, 72, 'قطعاً کافر شدند کسانی که گفتند: «خداوند، همان مسیح پسر مریم است.» [چگونه چنین می‌گویند] در حالی که مسیح خود گفت: «ای بنی‌اسرائیل! خدای یگانه را بپرستید که پروردگار من و شماست. هر کس برای خدا همتایی قرار دهد، خداوند بهشت را بر او حرام کرده و جایگاهش دوزخ است. و برای ستمگران هیچ یاوری نیست.»'),
(742, 5, 73, 'به راستی کافر شدند آنان که گفتند: «خداوند، سومین [خدا از] سه [خدا] است.» جز خدای یکتا هیچ معبودی نیست. و اگر از آنچه می‌گویند، دست برندارند، عذابی دردناک به کافران آنها [که روی این عقیده ایستادگی کنند،] خواهد رسید.'),
(743, 5, 74, 'پس آیا به سوی خدا بازنمی‌گردند و از او آمرزش نمی‌خواهند؟ در حالی که خداوند، آمرزنده و مهربان است.'),
(744, 5, 75, 'جز این نیست که مسیح پسر مریم، پیامبر [خدا] بود. پیش از او نیز پیامبرانی بودند. و مادرش بسیار راستگو و درستکار بود. آن دو [همچون سایر انسان‌ها] غذا می‌خوردند. [پس هیچ کدام خدا نیستند.] بنگر که چگونه نشانه‌ها[ی حق] را برای مردم بیان می‌کنیم، پس بنگر که این مردم چگونه [از حقّ] روی‌گردان می‌شوند؟'),
(745, 5, 76, '[ای پیامبر! به مردم] بگو: «آیا جز خدا، چیزی را می‌پرستید که اختیار زیان و سود شما را ندارد؟ و خداوند، شنوا و داناست.»'),
(746, 5, 77, '[ای پیامبر!] بگو: «ای اهل کتاب! در دین خود به ناحقّ غلوّ [و زیاده روی] نکنید و از هوس‌های گروهی که از پیش گمراه بودند و بسیاری را گمراه کردند و از راه میانه به بیراهه رفتند، پیروی نکنید.»'),
(747, 5, 78, 'کافران بنی‌اسرائیل، بر زبان داود و عیسی پسر مریم لعنت شدند. این [لعن و نفرین،] بدان سبب بود که نافرمانی کرده و تجاوز می‌نمودند.'),
(748, 5, 79, 'آنان یکدیگر را از کارهای زشتی که انجام می‌دادند، بازنمی‌داشتند. چه بد کاری می‌کردند!'),
(749, 5, 80, 'بسیاری از آنان را می‌بینی که با کافران دوستی می‌ورزند، [و آنان را سرپرست خود می‌گیرند.] به راستی زشت است آنچه خود برای خویش پیش فرستادند که [سبب شد] خداوند بر آنان خشم گرفت. و آنان در عذاب، جاوِدانند.'),
(750, 5, 81, 'و اگر به خداوند و پیامبر و آنچه بر او نازل شده، ایمان می‌آوردند، هرگز کافران را [سرپرست و] دوست خود نمی‌گرفتند؛ ولی بسیاری از آنان فاسق‌اند.'),
(751, 5, 82, 'بی‌گمان یهودیان و مشرکان را سرسخت‌ترینِ مردم در دشمنیِ اهل ایمان می‌یابی. و نزدیک‌ترینشان را در دوستی با مؤمنان، کسانی می‌یابی که می‌گویند: «ما نصارا هستیم.» این [دوستی] به آن جهت است که برخی از آنان کشیشان و راهبانی [حق‌جو] هستند و آنان [در برابر حق] تکبّر نمی‌ورزند.'),
(752, 5, 83, 'و هرگاه آیاتی را که بر پیامبر نازل شده، می‌شنوند، می‌بینی که چشمانشان از این که حقّ را شناخته‌اند، از اشک لبریز می‌شود و می‌گویند: «پروردگارا! ما ایمان آوردیم، پس نام ما را در زمره‌ی گواهی‌دهندگان [به حقّ] بنویس.»'),
(753, 5, 84, 'و «چرا به خداوند و حقّی که برای ما آمده، ایمان نیاوریم؟ در حالی که امید داریم پروردگارمان ما را همراه صالحان وارد [بهشت] سازد.»'),
(754, 5, 85, 'پس خداوند به پاس این سخن، به ایشان باغ‌هایی که از پای [درختان] آن نهرها جاری است، پاداش داد، همواره در آن ماندگارند و این است پاداش نیکوکاران.'),
(755, 5, 86, 'و کسانی که کافر شدند و آیات ما را تکذیب کردند، آنان اهل دوزخند.'),
(756, 5, 87, 'ای کسانی که ایمان آورده‌اید! چیزهای پاکیزه‌ای را که خدا برای شما حلال کرده است، بر خود حرام نکنید و از حد نگذرید که خداوند تجاوزگران را دوست نمی‌دارد.'),
(757, 5, 88, 'و از آنچه خداوند، روزی حلال و پاکیزه به شما بخشیده، بخورید. و از خدایی که به او ایمان دارید، پروا کنید.'),
(758, 5, 89, 'خداوند، شما را به خاطر سوگندهای لغو و بیهوده بازخواست نمی‌کند؛ لیکن برای شکستن سوگندهایی که [از روی اراده] بسته‌اید، مؤاخذه می‌کند. پس کفاره‌ی آن، طعام دادن به ده بینواست، از نوع متوسط آنچه به خانواده‌ی خود می‌خورانید، یا پوشاندن ده فقیر است، یا آزاد کردن برده‌ای. پس کسی که [غذا و لباس نداشت یا برده‌ای] نیافت، سه روز روزه بگیرد. این کفاره‌ی سوگندهای شماست، هرگاه که سوگند خوردید، [و آن را شکستید.] و سوگندهایتان را مراقبت کنید، این گونه خداوند آیات خود را برای شما بیان می‌کند، تا او را شکر کنید.'),
(759, 5, 90, 'ای کسانی که ایمان آورده‌اید! همانا شراب و قمار و بت‌ها و تیرهای قرعه، پلید و از کارهای شیطان است، پس از آنها بپرهیزید، تا رستگار شوید.'),
(760, 5, 91, 'شیطان می‌خواهد به وسیله شراب و قمار، میان شما دشمنی و کینه بیفکند و شما را از یاد خدا و نماز بازدارد. پس آیا دست برمی‌دارید؟'),
(761, 5, 92, 'و خدا را اطاعت کنید و پیامبر را فرمانبردار باشید و [از نافرمانی] بپرهیزید. پس اگر روی‌گردان شدید بدانید که وظیفه‌ی رسول ما ابلاغ آشکار است.'),
(762, 5, 93, 'بر کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، در آنچه [قبل از تحریم شراب] خورده‌اند، باکی نیست، هرگاه که اهل پروا و ایمان و کارهای شایسته باشند، سپس [از محرّمات] پرهیز نمایند و [به تحریم آنها] ایمان آورند، آنگاه [نیز از حرام] بپرهیزند و کار نیک کنند. و خداوند نیکوکاران را دوست دارد.'),
(763, 5, 94, 'ای کسانی که ایمان آورده‌اید! خداوند شما را با چیزی از شکار که دست‌ها و نیزه‌هایتان به آن می‌رسد، می‌آزماید تا خداوند معلوم گرداند چه کسی در باطن از او بیم دارد [و از شکار می‌گذرد]. پس بعد از این هر که تجاوز کند، او را عذابی دردناک است.'),
(764, 5, 95, 'ای کسانی که ایمان آورده‌اید! در حال احرام، شکار نکشید. و هر کس از شما به عمد، شکاری بکشد، کیفر و کفّاره‌اش کشتن نظیر آن حیوان از چهارپایان است که [به این نظیر بودن] دو شاهد عادل از میان خودتان حکم کنند. [این حیوان قربانی] هدیه‌ای است که به کعبه برسد [و آنجا ذبح شود] یا برای جبران آن به فقیران طعام بدهد یا برابر آن روزه بگیرد. [این کفّاره‌های سه‌گانه] برای آن است که جزای کار خود را بچشد. خداوند از گذشته شما گذشت. و هر کس این کار را تکرار کند، خداوند از او انتقام می‌گیرد. و خداوند، شکست‌ناپذیر و انتقام گیرنده است.'),
(765, 5, 96, '[البتّه] شکار دریا و خوراک آن برای شما حلال شده که توشه‌ای برای شما و کاروانیان است، ولی تا وقتی محرم هستید، صید صحرایی بر شما حرام است. و از خداوندی که به سوی او محشور می‌شوید، پروا کنید.'),
(766, 5, 97, 'خداوند، کعبه‌ی بیت‌الحرام را وسیله‌ی سامان‌بخشی و قِوام مردم قرار داده و نیز ماه حرام و قربانی‌های بی‌نشان و نشان‌دار را. این برای آن است که بدانید خداوند آنچه در آسمان‌ها و زمین است، می‌داند و خداوند به هر چیز آگاه است.'),
(767, 5, 98, 'بدانید که خداوند، سخت‌کیفر است و همانا خداوند آمرزنده مهربان است.'),
(768, 5, 99, 'بر پیامبر، جز ابلاغ [آیات الهی] نیست و خداوند، آنچه را آشکار یا کتمان می‌کنید، می‌داند.'),
(769, 5, 100, '[به مردم] بگو: «پلید و پاک، یکسان نیست، گرچه عدد ناپاکان تو را به تعجّب وادارد. پس ای صاحبان خرد! از خدا پروا کنید، باشد که شما رستگار شوید.»'),
(770, 5, 101, 'ای کسانی که ایمان آورده‌اید! از اموری که اگر برایتان آشکار شود، ناراحتتان می‌کند، نپرسید. و اگر هنگام نزول قرآن از آنها سؤال کنید، برایتان روشن می‌شود. خداوند از سؤال‌های نابجای شما گذشت و خدا آمرزنده بردبار است.'),
(771, 5, 102, '[از این‌گونه سؤال‌ها] گروهی از پیشینیان نیز پرسیدند و [چون طاقت عمل نداشتند،] نسبت به آن منکر و کافر شدند.'),
(772, 5, 103, 'هیچ بحیره [حیوان گوش شکافته] و هیچ سائِبه [حیوانی که به خاطر زاد و ولدِ زیاد آن را آزاد کرده‌اند] و هیچ وصیله [حیوان نری که متّصل به ماده، دوقلو به دنیا آمده باشد] و هیچ حام [شتر نری که ده بار برای جفت‌گیری از آن استفاده شده باشد]، خداوند، درباره‌ی این‌ها حکمی نکرده [و شما را از استفاده‌ی گوشت و بارکشی با آنها منع نفرموده و این ممنوعیت‌ها، خرافات زمان جاهلی است]، ولی کافران بر خدا دروغ می‌بندند و بیشتر آنان اهل تعقّل نیستند.'),
(773, 5, 104, 'و هرگاه به آنان گفته شود: «به سوی آنچه خداوند نازل کرده و به سوی پیامبر بیایید.» می‌گویند: «آنچه پدرانمان را بر آن یافتیم، ما را بس است.» آیا هر چند پدرانشان چیزی نمی‌دانستند و [به حقّ] هدایت نشده بودند [باید راه آنان را ادامه دهند؟]'),
(774, 5, 105, 'ای کسانی که ایمان آورده‌اید! بر شما باد [حفظ] خودتان. اگر شما هدایت یافتید، آن که گمراه شد، زیانی به شما نمی‌رساند. بازگشت همه شما به سوی خداست و او شما را به آنچه می‌کردید، آگاه می‌سازد.'),
(775, 5, 106, 'ای کسانی که ایمان آورده‌اید! هرگاه [نشانه‌های] مرگ یکی از شما فرارسد، از میان خود دو نفر عادل را هنگام وصیت به شهادت و گواهی فراخوانید. و اگر در مسافرت بودید و مصیبت مرگ به سراغ شما آمد دو تن از غیر [هم‌کیشان]تان را به گواهی بطلبید و اگر [در صداقت آنان] شک کردید، پس از نماز آن دو را نگاه دارید تا به خداوند قسم یاد کنند که: «ما حاضر نیستیم حقّ را به هیچ قیمتی بفروشیم، هر چند در مورد خویشان باشد. و هرگز شهادت الهی را پنهان نمی‌کنیم که در این صورت از گنهکارانیم.»'),
(776, 5, 107, 'پس اگر معلوم شد که آن دو شاهد گناه کرده‌اند [و سوگندشان ناحقّ بوده]، دو نفر دیگر برخاسته، به خدا سوگند یاد نمایند که: «قطعاً گواهی ما از گواهی آن دو نفر به حقّ نزدیک‌تر است.» [و بگویند:] «ما [از حدّ و حقّ] تجاوز نکرده‌ایم که اگر چنین کنیم، قطعاً از ستمکارانیم.»'),
(777, 5, 108, 'این [روش به درستی] نزدیک‌تر است. برای این که شهادت را به نحو درست ادا کنند و یا بترسند که بعد از سوگند خوردنشان، سوگندهایی برگردانده شود [و جای سوگند آنان را بگیرد]. و از خداوند پروا کنید و [فرمان‌های او را] گوش کنید. و خداوند، گروه فاسق را هدایت نمی‌کند.'),
(778, 5, 109, 'روزی که خداوند، پیامبران را گرد آورد. پس بگوید: «به دعوت شما چه پاسخی داده شد؟» گویند: «ما علمی [به حقیقت امر] نداریم، همانا دانای غیب‌ها تویی تو.»'),
(779, 5, 110, '[به یاد آور] زمانی که خداوند فرمود: ای عیسی پسر مریم! نعمتم را بر تو و بر مادرت یاد کن. آن‌گاه که تو را با روح‌القدس [جبرئیل] تأیید کردم. در گهواره [به اعجاز] و در بزرگسالی [به وحی] با مردم سخن گفتی و آن‌گاه که کتاب و حکمت و تورات و انجیل را به تو آموختم و به اذن من از گِل [چیزی] به صورت پرنده ساختی و در آن دمیدی، پس با اذن من پرنده‌ای شد و با اذن من کور مادرزاد و پیسی گرفته را شفا می‌دادی و آن‌گاه که به اذن من، مردگان را [زنده] از گور بیرون می‌آوردی و [به یاد آور] زمانی که [دستِ ظلمِ] بنی‌اسرائیل را از تو کوتاه کردم، آن‌گاه که تو دلایل روشن برایشان آوردی، پس کافرانِ از ایشان [درباره‌ی معجزات تو] گفتند: «این، چیزی جز سحر آشکار نیست.»'),
(780, 5, 111, 'و زمانی که به حواریون وحی فرستادم که ایمان آورید. گفتند: «ایمان آورده‌ایم و شاهد باش که ما مسلمان و تسلیم هستیم.»'),
(781, 5, 112, 'زمانی که حواریون گفتند: «ای عیسی بن مریم! آیا پروردگارت می‌تواند از آسمان، خوانی [از غذا] برای ما فرود آورد؟» عیسی گفت: «اگر مؤمنید، از خدا پروا کنید!»'),
(782, 5, 113, 'گفتند: «[ما بهانه‌جو نیستیم؛ بلکه] می‌خواهیم از آن بخوریم و دل‌هایمان اطمینان یابد و بدانیم که به ما راست گفته‌ای. و بر آن مائده‌ی آسمانی از گواهان باشیم.»'),
(783, 5, 114, 'عیسی بن مریم گفت: «خداوندا! پروردگارا! مائده‌ای از آسمان بر ما فروفرست که برای نسل کنونی و آیندگان ما عید و نشانه‌ای از تو باشد و ما را روزی ده، که تو بهترینِ روزی‌دهندگانی.»'),
(784, 5, 115, 'خداوند فرمود: «من آن [مائده] را بر شما نازل می‌کنم، امّا هر کدام از شما بعد از آن کفر ورزد، او را چنان عذاب خواهم کرد که هیچ یک از جهانیان را آن‌گونه عذاب نکنم.»'),
(785, 5, 116, 'و زمانی که خداوند فرمود: «ای عیسی بن مریم! آیا تو به مردم گفتی: به غیر از خدا، من و مادرم را به عنوان دو معبود بگیرید؟» [عیسی] گفت: «[خدایا!] تو پاک و منزّهی! مرا نشاید که حرف ناروایی که سزاوار من نیست بگویم. اگر چنین گفته بودم، تو آن را می‌دانستی، تو آنچه را در دل و جان من است، می‌دانی، امّا من از آنچه در ذات توست، بی‌خبرم. همانا، دانای تمام غیب‌ها تویی تو.'),
(786, 5, 117, 'من، به آنان نگفتم مگر آنچه مرا به آن فرمان دادی، که بپرستید خداوندی را که پروردگار من و پروردگار شماست و تا زمانی که در میان آنان بودم، بر آنان شاهد و ناظر بودم. پس چون مرا بازگرفتی، تو خود بر آنان مراقب بودی. و تو بر هر چیز گواهی.'),
(787, 5, 118, '[خدایا!] اگر عذابشان کنی، آنان بندگان تو هستند. و اگر آنان را بیامرزی، همانا تو خود توانمند و حکیمی.»'),
(788, 5, 119, 'خداوند فرمود: «این، روزی است که راستگویان را صداقتشان سود دهد. برایشان باغ‌هایی است که زیر [درختان] آن نهرها جاری است. همیشه در آن ماندگارند، خداوند از آنان راضی است، آنان نیز از او راضی‌اند. این رستگاری بزرگ است.»'),
(789, 5, 120, 'مُلک و حکومت آسمان‌ها و زمین و آنچه در آنهاست، تنها از آن خداست. و او بر هرچیز تواناست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ستایش مخصوص خداوندی است که آسمان‌ها و زمین را آفرید و تاریکی‌ها و روشنی را قرار داد، امّا کافران [دیگران را] با پروردگارشان برابر و همتا می‌گیرند.'),
(791, 6, 2, 'او کسی است که شما را از گل آفرید، پس از آن اَجلی را [برای زندگی شما در دنیا] قرار داد و اَجلی معین نزد اوست. پس [با وجود این] شما شک و شبهه می‌کنید؟'),
(792, 6, 3, 'و اوست که در آسمان‌ها و در زمین خداوند است. نهان و آشکار شما را می‌داند و هر چه را به دست می‌آورید، می‌داند.'),
(793, 6, 4, 'و هیچ نشانه و آیه‌ای از نشانه‌های پروردگارشان برای آنها نمی‌آمد، جز آن که [به جای تصدیق و ایمان] از آن روی برمی‌گرداندند.'),
(794, 6, 5, 'پس همین که حقّ برای آنان آمد، آن را تکذیب کردند، پس به زودی خبرهای مهم [و تلخ درباره‌ی کیفر] آنچه را به مسخره می‌گرفتند، به سراغشان خواهد آمد.'),
(795, 6, 6, 'آیا ندیدند که چه بسیار امت‌های پیش از آنان را هلاک کردیم؟ با آن که در زمین به آنان جایگاه و توانی داده بودیم که به شما نداده‌ایم؟ و [باران و برکت] آسمان را پی در پی برایشان فرستادیم و نهرهای آب از زیر پاهایشان جاری ساختیم، پس آنان را به کیفر گناهانشان هلاک کردیم و نسل دیگری پس از آنان پدید آوردیم.'),
(796, 6, 7, 'و اگر نوشته‌ای را در کاغذی بر تو نازل می‌کردیم که کافران آن را با دست‌های خود لمس می‌کردند، باز هم می‌گفتند: «این، جز جادویی آشکار نیست.»'),
(797, 6, 8, 'و [کافرانِ بهانه‌جو] گفتند: «چرا فرشته‌ای بر او نازل نشده [تا او را در دعوت کمک کند]؟» در حالی که اگر فرشته‌ای نازل می‌کردیم، [باز هم لجاجت می‌کردند و] کار آنان پایان می‌یافت و هیچ مهلتی به آنان داده نمی‌شد.'),
(798, 6, 9, 'و اگر [پیامبر را] فرشته قرار می‌دادیم، حتماً او را به صورت مردی درمی‌آوردیم [که باز همان شبهه را تکرار می‌کردند. پس]، کار را بر آنان مشتَبَه می‌ساختیم، همان‌طور که آنان [با ایرادهای خود] کار را بر دیگران مشتبه می‌سازند.'),
(799, 6, 10, 'و قطعاً پیامبرانی پیش از تو هم استهزا شدند، پس عذابی که به استهزای آن می‌پرداختند، بر مسخره‌کنندگان از ایشان فرودآمد.'),
(800, 6, 11, '[ای پیامبر! به آنان] بگو: «در زمین بگردید، پس بنگرید که سرنوشت تکذیب‌کنندگان چگونه شد؟»'),
(801, 6, 12, 'بگو: «آنچه در آسمان‌ها و زمین است، برای کیست؟» بگو: «برای خداوند است که بر خود، رحمت را مقرّر کرده است.» او قطعاً شما را در روز قیامت که شکی در آن نیست جمع خواهد کرد. کسانی که خود را باختند، همانان ایمان نمی‌آورند.'),
(802, 6, 13, 'و تنها برای اوست آنچه در شب و روز قرار گرفته و او شنوای داناست.'),
(803, 6, 14, 'بگو: «آیا غیر از خدا را سرپرست خود بگیرم؟ در حالی که او آفریدگار آسمان‌ها و زمین است. او همه را طعام می‌دهد، ولی کسی به او طعام نمی‌دهد.» بگو: «من مأمورم که اوّلین کسی باشم که تسلیم فرمان اوست. و [به من گفته شده که] هرگز از مشرکان مباش.»'),
(804, 6, 15, 'بگو: «اگر پروردگارم را نافرمانی کنم، از عذاب روزی بزرگ بیمناکم.'),
(805, 6, 16, 'در آن روز، از هر کس عذاب الهی برداشته شود، قطعاً مشمول رحمت الهی شده و این است رستگاری آشکار.»'),
(806, 6, 17, 'و اگر خداوند تو را با زیان و آسیب درگیر کند، جز خودش هیچ کس برطرف کننده آن نیست. و اگر خیری به تو برساند، پس او بر هر چیز تواناست.'),
(807, 6, 18, 'اوست که بر بندگان خود قاهر و مسلط است. و اوست حکیم آگاه.'),
(808, 6, 19, 'بگو: «چه موجودی در گواهی، برتر و بزرگتر است؟» بگو: «خداوند میان من و شما گواه است. و این قرآن به من وحی شده تا با آن شما را و هرکه را این پیام به او برسد، هشدار دهم. آیا شما گواهی می‌دهید که با خداوند، خدایان دیگری هست؟» بگو: «من گواهی نمی‌دهم.» بگو: «همانا او خدای یکتاست و بی‌تردید من از آنچه شما شرک می‌ورزید، بیزارم.»'),
(809, 6, 20, 'کسانی که به آنان، کتاب [آسمانی] دادیم، [پیامبر را] همانند فرزندان خود می‌شناسند [ولی حاضر به پذیرفتن نیستند]، پس کسانی که به خودشان زیان زدند، ایمان نمی‌آورند.'),
(810, 6, 21, 'و کیست ستمکارتر از آن کس که به خداوند دروغ بندد، یا آیات الهی را تکذیب کند؟ همانا ستمگران رستگار نمی‌شوند.'),
(811, 6, 22, 'و روزی که آنان را یکسره محشور می‌کنیم، سپس به کسانی که شرک ورزیدند، می‌گوییم: کجایند شریکان شما که بر ایشان گمان [خدایی] داشتید؟'),
(812, 6, 23, 'پس [در پیشگاه خداوند] عذری نیابند، جز آن که بگویند: «سوگند به خدا! پروردگارمان، ما هرگز مشرک نبوده‌ایم!»'),
(813, 6, 24, 'بنگر که چگونه علیه خود دروغ گفتند و آنچه به دروغ و افترا به خدا نسبت می‌دادند، از دستشان رفت.'),
(814, 6, 25, 'بعضی از آنها کسانی هستند که [به ظاهر] به سخن تو گوش می‌دهند، ولی ما بر دل‌هایشان پرده‌هایی قرار داده‌ایم تا آن را نفهمند و در گوش‌هایشان [نسبت به شنیدن حقّ] سنگینی قرار دادیم و آنان به قدری لجوجند که اگر هر آیه و معجزه‌ای را ببینند، باز هم به آن ایمان نمی‌آورند، تا آنجا که چون نزد تو آیند، با تو جدال می‌کنند. و کسانی که کافر شدند، می‌گویند: «این نیست جز افسانه‌های پیشینیان.»'),
(815, 6, 26, 'و آنان، مردم را از آن [پیامبر و قرآن] بازمی‌دارند. و خود نیز از آن دور و محروم می‌شوند، [ولی بدانند که] جز خودشان را هلاک نمی‌سازند، و[لی] نمی‌فهمند.'),
(816, 6, 27, 'و اگر آنان را، آن هنگام که بر لبه‌ی آتش [دوزخ] نگه داشته شده‌اند، ببینی، پس می‌گویند: «ای کاش [بار دیگر به دنیا] بازگردانده شویم و آیات پروردگارمان را تکذیب نکنیم و از مؤمنان [واقعی] باشیم.»'),
(817, 6, 28, '[چنین نیست،] بلکه آنچه قبلاً [در دنیا] پنهان می‌کردند، [در آن روز] برایشان آشکار شده و اگر آنان به دنیا بازگردانده شوند، بی‌گمان باز هم به آنچه از آن نهی شده‌اند، بازمی‌گردند. و آنان قطعاً دروغگویانند.'),
(818, 6, 29, 'و گفتند: «جز زندگی دنیوی ما، هیچ زندگی دیگری نیست و ما [پس از مرگ] برانگیحته نمی‌شویم.»'),
(819, 6, 30, 'و اگر ببینی آن‌گاه که در برابر پروردگارشان بازداشته شده‌اند، [خدا] می‌فرماید: «آیا این [رستاخیز،] حقّ نیست؟» می‌گویند: «آری! به پروردگارمان سوگند [که حقّ است].» او می‌فرماید: «پس به کیفر کفرورزی‌هایتان، عذاب را بچشید.»'),
(820, 6, 31, 'قطعاً کسانی که دیدار خداوند [در قیامت] را تکذیب کردند، زیانکار شدند. همین که ساعتِ موعود، ناگهان به سراغشان آید، در حالی که بار سنگین گناهان خویش را بر پشتشان می‌کشند، می‌گویند: «دریغا از آن کوتاهی‌ها که در دنیا کرده‌ایم!» آگاه باشید که چه بد باری است آنچه به دوش می‌کشند!'),
(821, 6, 32, 'و زندگی دنیا جز بازی و سرگرمی نیست. و البتّه خانه‌ی آخرت، برای تقواپیشگان بهتر است. آیا نمی‌اندیشید؟'),
(822, 6, 33, 'البتّه ما می‌دانیم که آنچه می‌گویند، تو را اندوهناک می‌کند، [ولی غم مخور و بدان که] آنان [در واقع] تو را تکذیب نمی‌کنند، بلکه ستمگران، آیات خدا را انکار می‌کنند.'),
(823, 6, 34, 'و پیش از تو نیز پیامبرانی تکذیب شدند، لیکن در برابر آنچه تکذیب شدند و آزار دیدند، صبر کردند، تا یاری ما به آنان رسید. [تو نیز چنین باش؛ زیرا] برای کلمات [و سنّت‌های] خداوند، تغییردهنده‌ای نیست. قطعاً از اخبار پیامبران برای تو آمده است [و با سرگذشت آنان آشنایی].'),
(824, 6, 35, 'و اگر اعراض و بی‌اعتنایی آن [کافر]ان بر تو سنگین است، [بدان که حتی] اگر بتوانی سوراخی در زمین یا نردبانی در آسمان بجویی، تا آیه‌ای برایشان بیاوری [باز هم ایمان نخواهند آورد] و اگر خدا خواسته بود، همه‌ی آنان را بر هدایت گرد می‌آورد [ولی سنّت الهی بر هدایت اجباری نیست]، پس هرگز از جاهلان مباش.'),
(825, 6, 36, 'تنها کسانی [دعوت تو را] می‌پذیرند که گوش شنوا دارند و [آنان که نمی‌گروند،] مردگانی هستند که روز قیامت خداوند آنان را بر می‌انگیزد و سپس همه به سوی او بازگردانده می‌شوند.'),
(826, 6, 37, 'و گفتند: «چرا بر او معجزه‌ای [که ما می‌خواهیم] از سوی پروردگارش نازل نشده؟» بگو: «بی‌تردید خداوند قادر است که معجزه‌ای [را که می‌خواهید] بیاورد؛ ولی بیشتر آنها نمی‌دانند.»'),
(827, 6, 38, 'و هیچ جنبنده‌ای در زمین و هیچ پرنده‌ای که با دو بال خود، پرواز می‌کند، نیست، مگر این که آنها هم امت‌هایی چون شمایند. ما در این کتاب هیچ چیز را فروگذار نکردیم، سپس همه نزد پروردگارشان گرد می‌آیند.'),
(828, 6, 39, 'و کسانی که آیات ما را تکذیب کردند، کر و لال‌هایی هستند درتاریکی‌ها. خداوند، هر که را بخواهد [و سزاوار باشد]، گمراهش کند و هر که را بخواهد [و شایسته بداند] بر راه راست قرارش می‌دهد. [39)'),
(829, 6, 40, 'بگو: «اگر راست می‌گویید، چه خواهید کرد آن گاه که عذاب خدا در دنیا بیاید یا قیامت فرا رسد؟ آیا غیر خدا را می‌خوانید؟»'),
(830, 6, 41, 'بلکه [در هنگامه خطر] فقط او را می‌خوانید، پس اگر بخواهد، آن رنجی که خدا را به خاطر آن می‌خوانید، برطرف می‌کند. و آنچه را برای خداوند شریک می‌پنداشتید [در قیامت] فراموش می‌کنید.'),
(831, 6, 42, 'و به سوی امّت‌های پیش از تو [نیز پیامبرانی] فرستادیم، [و چون مخالفت کردند،] آنان را به تنگدستی و بیماری گرفتار کردیم. باشد که تضرّع کنند.'),
(832, 6, 43, 'پس چرا هنگامی که ناگواری‌های ما به آنان رسید، توبه و زاری نکردند؟ آری! دل‌های آنان سنگ و سخت شده و شیطان کارهایی را که می‌کردند، برایشان زیبا جلوه داده است.'),
(833, 6, 44, 'پس چون اندرزهایی را که به آنان داده شده بود، فراموش کردند، درهای هر گونه نعمت را به رویشان گشودیم، تا آن‌گاه که به آنچه داده شدند، دل خوش کردند، ناگهان آنان را [به کیفر] گرفتیم، پس یک‌باره محزون و نومید گردیدند.'),
(834, 6, 45, 'پس ریشه‌ی گروه ستمکار بریده شد. و سپاس، مخصوص خداوندی است که پروردگار جهانیان است.'),
(835, 6, 46, 'بگو: «چه فکر می‌کنید؟ اگر خداوند، شنوایی و بینایی شما را بگیرد و بر دل‌های شما مهر بزند، جز خداوند، کدام معبودی است که آنها را به شما باز دهد؟» ببین که ما چگونه آیات را به گونه‌های مختلف بیان می‌کنیم؛ امّا آنان روی برمی‌گردانند.'),
(836, 6, 47, 'بگو: «چه خواهید کرد اگر عذاب خداوند، به طور پنهان و ناگهانی یا آشکارا به سراغ شما آید؟ آیا جز گروه ستمگر هلاک خواهند شد؟»'),
(837, 6, 48, 'و ما پیامبران را جز به عنوان بشارت‌دهنده و بیم‌دهنده نمی‌فرستیم. پس کسانی که ایمان آورند و [کار خود را] اصلاح کنند، بیمی بر آنان نیست و اندوهگین نمی‌شوند.'),
(838, 6, 49, 'و کسانی که آیات ما را تکذیب کردند، عذاب الهی به خاطر فسق و گناهشان، دامن‌گیر آنان می‌شود.'),
(839, 6, 50, '[ای پیامبر! به مردم] بگو: «من ادّعا نمی‌کنم که گنجینه‌های خداوند نزد من است و من غیب هم نمی‌دانم و ادّعا نمی‌کنم که من فرشته‌ام. من جز آنچه را به من وحی می‌شود، پیروی نمی‌کنم.» بگو: «آیا نابینا و بینا برابرند؟ آیا نمی‌اندیشید؟»'),
(840, 6, 51, 'و به وسیله‌ی این [قرآن] کسانی را که از محشور شدن نزد پروردگارشان بیم دارند، هشدار ده؛ [زیرا] غیر از خداوند، برای آنان یاور و شفیعی نیست. شاید که پروا نمایند.'),
(841, 6, 52, 'و کسانی را که بامداد و شامگاه پروردگارشان را می‌خوانند، در حالی که رضای او را می‌طلبند، از خود مران. چیزی از حساب آنان بر عهده‌ی تو نیست و از حساب تو نیز چیزی بر عهده آنان نیست که طردشان کنی و در نتیجه ازستمگران شوی.'),
(842, 6, 53, 'و این‌گونه بعضی از مردم را به وسیله‌ی بعضی دیگر آزمایش کردیم، تا بگویند: «آیا اینانند آنها که خداوند، از میان ما بر آنها منّت نهاده است؟» آیا خداوند به حال شاکران داناتر نیست؟'),
(843, 6, 54, 'و هرگاه کسانی که به آیات ما ایمان دارند، نزد تو آیند، پس بگو: «سلام بر شما! پروردگارتان بر خودش رحمت را مقرّر کرده است، که هر کس از شما از روی نادانی کار بدی انجام دهد و پس از آن توبه کند و خود را اصلاح نماید، قطعاً خداوند، آمرزنده و مهربان است.»'),
(844, 6, 55, 'و ما بدین‌گونه آیات را [برای مردم] به روشنی بیان می‌کنیم، [تا حقّ، آشکار] و راه و رسم گناهکاران روشن شود.'),
(845, 6, 56, 'بگو: «من از پرستش آنها که به جای خدا می‌خوانید، نهی شده‌ام.» بگو: «من از هوس‌های شما پیروی نمی‌کنم، که در این صورت گمراه شده و از هدایت‌یافتگان نخواهم بود.»'),
(846, 6, 57, 'بگو: «من از طرف پروردگارم دلیل آشکاری دارم؛ ولی شما آن را دروغ پنداشتید. آنچه را شما [از عذاب الهی] با شتاب می‌طلبید، به دست من نیست. فرمان جز به دست خدا نیست، که حقّ را بیان می‌کند و او بهترین [داور و] جداکننده‌ی حقّ از باطل است.»'),
(847, 6, 58, 'بگو: «اگر آنچه که درباره آن شتاب می‌کنید، نزد من بود [و به درخواست شما عذاب نازل می‌کردم]، قطعاً کار میان من و شما پایان گرفته بود؛ ولی خداوند به حال ستمگران آگاه‌تر است [و به موقع مجازاتشان می‌کند].»'),
(848, 6, 59, 'و گنجینه‌های غیب، تنها نزد خداست. و جز او کسی آن را نمی‌داند و آنچه را در خشکی و دریاست، می‌داند. و هیچ برگی از درخت نمی‌افتد، جز آن که او می‌داند و هیچ دانه‌ای در تاریکی‌های زمین و هیچ تر و خشکی نیست، مگر آن که [علم آن] در کتاب مبین ثبت است.'),
(849, 6, 60, 'و او کسی است که [روح] شما را در شب [به هنگام خواب] می‌گیرد و آنچه را در روز انجام می‌دهید، می‌داند. سپس شما را از خواب برمی‌انگیزد تا مدّت معین [عمر شما] سپری شود. عاقبت، بازگشت شما به سوی اوست. پس شما را به آنچه انجام می‌دهید، خبر می‌دهد.'),
(850, 6, 61, 'و تنها اوست که قهر و اقتدارش برتر از بندگان است و نگهبانانی بر شما می‌فرستد، تا چون مرگ یکی از شما فرارسد، فرستادگان ما [فرشتگان،] جان او را بازگیرند و در کار خود، هیچ کوتاهی نکنند.'),
(851, 6, 62, 'سپس مردم به سوی خدا، مولای حقّشان بازگردانده می‌شوند. آگاه باشید که دادرسی و داوری تنها از آنِ خداوند است و او سریع‌ترینِ حساب‌رَسان است.'),
(852, 6, 63, 'بگو: «چه کسی شما را از تاریکی‌های خشکی و دریا نجات می‌دهد؟ وقتی که او را آشکارا و پنهان می‌خوانید. [و می‌گویید:]» اگر خدا ما را از این ظلمت رهایی دهد، از شکرگزاران خواهیم بود.»'),
(853, 6, 64, 'بگو: «خدا شما را از این ظلمت‌ها و از هر ناگواری دیگر نجات می‌دهد، باز شما شرک می‌ورزید.»'),
(854, 6, 65, 'بگو: «او تواناست که از بالای سرتان یا از زیر پایتان عذابی بر شما بفرستد، یا شما را به صورت گروه‌های گوناگون با هم درگیر کند و طعم تلخ جنگ و خونریزی را توسط یکدیگر به شما بچشاند.» بنگر که چگونه آیات را گونه‌گون بازگو می‌کنیم، باشد که بفهمند.'),
(855, 6, 66, 'و قوم تو این قرآن را تکذیب کردند، با آن که سخن حقّی است. بگو: «من وکیل و عهده‌دار ایمان آوردن شما نیستم.»'),
(856, 6, 67, 'برای هر خبری وقتی مقرّر است [که در آن واقع می‌شود] و به زودی خواهید دانست.'),
(857, 6, 68, 'و هرگاه کسانی را دیدی که در آیات ما [به قصد تخطئه،] کند و کاو می‌کنند، از آنان روی بگردان تا وارد مطلب دیگری شوند. و اگر شیطان تو را به فراموشی انداخت، پس از توجّه، [دیگر] با این قوم ستمگر منشین!'),
(858, 6, 69, 'کسانی که پروا پیشه کردند، به گناه ستمکاران بازخواست نخواهند شد، ولی باید آنان را پند دهند تا شاید پرهیزکار شوند.'),
(859, 6, 70, 'و کسانی که دین خود را به بازی و سرگرمی گرفتند و زندگی دنیا مغرورشان کرده است، آنان را رها کن. و به وسیله‌ی قرآن پندشان ده، تا مبادا به کیفر آنچه کسب کرده‌اند، به هلاکت افتند، در حالی که جز خدا هیچ یاور و شفیعی ندارند. و اگر [انسان در آن روز] هر گونه عوضی [برای کیفر] بپردازد، از او پذیرفته نمی‌شود. آنان کسانی هستند که به سزای آنچه کسب کرده‌اند به هلاکت افتادند. برای آنان شرابی از آب سوزان و عذابی دردناک به کیفر کفرشان خواهد بود.'),
(860, 6, 71, 'بگو: «آیا غیر خداوند، چیزی را بخوانیم که سود و زیانی برای ما ندارد و پس از آن که خداوند هدایتمان کرد، به عقب بازگشته [و کافر] شویم؟ همچون کسی که شیاطین او را در زمین از راه به در برده‌اند و سرگردان مانده است، در حالی که برای او یارانی [دلسوز] است که او را به هدایت دعوت کرده [و می‌گویند:]» «نزد ما بیا [و حقّ را بپذیر].» بگو: «هدایتی که از سوی خدا باشد، هدایت واقعی است و ما مأموریم که برای خدای جهانیان تسلیم باشیم.'),
(861, 6, 72, 'و [به ما امر شده که] نماز را بر پا دارید و از خداوند پروا کنید. و اوست آن که همگان نزدش محشور می‌شوید.»'),
(862, 6, 73, 'و او کسی است که آسمان‌ها و زمین را به حقّ آفرید و روزی که بگوید: موجود شو، بی‌درنگ، موجود می‌شود. سخن او حقّ است. [و روز قیامت،] روزی که در صور دمیده می‌شود، حکومت، تنها از آنِ اوست. دانای غیب و شهود است. و اوست حکیم و آگاه.'),
(863, 6, 74, 'و [یاد کن] آن هنگام که ابراهیم به آزَر [عموی مشرکش که به جای] پدرش [سرپرستی او را به عهده داشت]، گفت: «آیا بت‌ها را خدایان می‌گیری؟ بی‌شک تو و قومت را در گمراهی آشکار می‌بینم.»'),
(864, 6, 75, 'و این گونه ملکوت آسمان‌ها و زمین را به ابراهیم نشان دادیم، تا از اهل یقین باشد.'),
(865, 6, 76, 'پس چون شب بر او پرده افکند، ستاره‌ای دید. گفت: «این پروردگار من است!» پس چون غروب کرد، گفت: «من زوال‌پذیران را دوست ندارم.»'),
(866, 6, 77, 'پس چون ماه را در حال طلوع و درخشش دید، گفت: «این پروردگار من است!» پس چون غروب کرد، گفت: «اگر پروردگارم مرا هدایت نکرده بود، قطعاً از گروه گمراهان بودم.»'),
(867, 6, 78, 'پس چون خورشيد را برآمده ديد گفت: «این است پروردگار من! این بزرگتر [از ماه و ستاره] است.» امّا چون غروب کرد، گفت: «ای قوم من! همانا من از آنچه برای خداوند شریک قرار می‌دهید، بیزارم.'),
(868, 6, 79, 'من از سر اخلاص روی خود را به سوی کسی گرداندم که آسمان‌ها و زمین را پدید آورد و من از مشرکان نیستم.»'),
(869, 6, 80, 'و قومش با وی به گفتگو و ستیزه پرداختند، او گفت: «[ای قوم من!] آیا درباره‌ی خدا با من مُحاجّه و ستیزه می‌کنید؟ و حال آن که او مرا هدایت کرده است. و من از آنچه شما شریک او می‌پندارید، بیم ندارم، مگر آن که پروردگارم چیزی بخواهد، [که اگر هم آسیبی به من برسد، خواست خداست، نه بت‌ها،] علم پروردگارم همه چیز را فراگرفته، پس آیا پند نمی‌گیرید؟'),
(870, 6, 81, 'و چگونه از چیزی که شریک خدا ساخته‌اید بترسم؟ و حال آن که شما آنچه را که خدا هیچ دلیلی درباره‌ی آن بر شما نازل نکرده است، شریک او گرفته و نمی‌ترسید. اگر می‌دانید [بگویید که] کدام یک از ما دودسته، به ایمِنی [در قیامت] سزاوارتر است؟'),
(871, 6, 82, 'کسانی که ایمان آوردند و ایمانشان را به ستم و شرک نیالودند، آنانند که برایشان ایمنی [از عذاب] است و آنان هدایت‌یافتگانند.»'),
(872, 6, 83, 'این [نوع استدلال]، حجّت ماست که در برابر قومش به ابراهیم دادیم، هر کس را که بخواهیم [و شایسته بدانیم]، به درجاتی بالا می‌بریم. پروردگارت حکیم و داناست [و بر اساس علم و حکمت، مقام انسان‌ها را بالا می‌برد].'),
(873, 6, 84, 'و ما به او [ابراهیم]، اسحاق و یعقوب را بخشیدیم و یکایک آنان را هدایت کردیم. و نوح را پیش از آنان هدایت کرده بودیم. و از نسل ابراهیم، داوود و سلیمان و ایوب و یوسف و موسی و هارون را [نیز هدایت کردیم] و ما این‌گونه نیکوکاران را پاداش می‌دهیم.'),
(874, 6, 85, 'و زکریا و یحیی و عیسی و الیاس را [نیز هدایت کردیم و] همه از شایستگانند.'),
(875, 6, 86, 'و اسماعیل و یسع و یونس و لوط را [نیز هدایت کردیم] و همه را بر جهانیان برتری دادیم.'),
(876, 6, 87, 'و از پدران و ذرّیه و برادرانشان، کسانی را [مورد لطف قراردادیم و به خاطر لیاقتشان آنان را به نبوّت] برگزیدیم و به راه راست هدایتشان کردیم.'),
(877, 6, 88, 'آن هدایت خداست. هر کس از بندگانش را که بخواهد، به آن هدایت می‌کند. و اگر شرک ورزند، قطعاً آنچه انجام داده‌اند، از دستشان خواهد رفت.'),
(878, 6, 89, 'آنان کسانی بودند که کتاب آسمانی و حکومت و قضاوت و مقام نبوّت به آنان دادیم. پس اگر این مشرکان به آنها کفر ورزند و نپذیرند [ناراحت نباش، چرا که] ما گروهی دیگر را که به آن کافر نیستند، می‌گماریم.'),
(879, 6, 90, 'آنان کسانی‌اند که خداوند هدایتشان کرده، پس به هدایت آنان اقتدا کن. [ای پیامبر! به مردم] بگو: «من بر این [رسالت و دعوت] از شما مزدی طلب نمی‌کنم. این قرآن جز تذکری برای جهانیان نیست.»'),
(880, 6, 91, 'خداوند را آن‌گونه که شایسته اوست، نشناختند، چرا که گفتند: «خداوند بر هیچ بشری، چیزی نازل نکرده است.» بگو: «کتابی را که موسی آورد و نور و هدایت برای مردم بود، چه کسی نازل کرد؟ کتابی که آن را ورق ورق کرده، بخشی را آشکار و بسیاری را پنهان می‌سازید و آنچه را شما و پدرانتان نمی‌دانستید، [از طریق همان تورات،] به شما آموخته شد.» [ای پیامبر!] بگو: خدا؛ و آنان را رها کن تا در یاوه‌گویی‌های خود غوطه‌ور باشند.'),
(881, 6, 92, 'و این مبارک کتابی است که نازل کرده‌ایم. تصدیق کننده آنچه پیش از آن آمده است، [تا مردم را به پاداش‌های الهی مژده دهی] و اهل مکه و کسانی را که اطراف آن هستند، هشدار دهی. و آنان که به آخرت ایمان دارند، به آن [قرآن] نیز ایمان خواهند آورد. و همانان بر نمازهای خود محافظت دارند.'),
(882, 6, 93, 'و کیست ظالم‌تر از آن که بر خدا دروغی بست یا گفت: «به من وحی شده!» در حالی که چیزی به او وحی نشده است. و نیز آن کس که گفت: «به زودی من هم مثل آنچه خدا نازل کرده، نازل می‌کنم.» و اگر ببینی زمانی را که ستمگران در سکرات مرگ قرار گرفته‌اند و فرشتگان دست‌های خویش را گشوده و فرمان جان دادن می‌دهند، [و می‌گویند:] «امروز به خاطر نسبت‌های ناروا که به خداوند می‌دادید و از آیات او سرپیچی می‌کردید، به عذاب خوارکننده کیفر داده خواهید شد.»'),
(883, 6, 94, 'به راستی همان گونه که نخستین بار شما را آفریدیم، اکنون نیز تک و تنها نزد ما آمدید و همه اموالی را که به شما دادیم، پشت سر گذاشتید و آن شفیعان را که در [سرنوشت] خودتان، شریکان خداوند می‌پنداشتید، همراه شما نمی‌بینیم. به راستی [پیوندهای] میان شما گسسته و آنچه [از شریکان و شفیعان که] می‌پنداشتید، از [دست] شما رفت.'),
(884, 6, 95, 'همانا خداوند، شکافنده‌ی دانه و هسته است. زنده را از مرده بیرون می‌آورد و بیرون آورنده‌ی مرده از زنده است. آن است خداوند شما. پس چگونه [از حقّ] منحرف می‌شوید؟'),
(885, 6, 96, '[خداوند،] شکافنده‌ی سپیده دم است، و شب را مایه‌ی آرامش قرار داد و خورشید و ماه را اسباب شمارشِ [ایام]. این است اندازه‌گیری خداوند قدرتمند دانا.'),
(886, 6, 97, 'و اوست کسی که ستارگان را برای شما قرار داد تا در تاریکی‌های خشکی و دریا، به وسیله آنها راه را بیابید. قطعاً ما نشانه‌ها را برای قومی که آگاه و دانایند، به تفصیل روشن ساختیم.'),
(887, 6, 98, 'و اوست کسی که شما را از یک نفس آفرید. پس برخی قرار یافته [و به دنیا آمده‌اند] و برخی [در پشت پدرها و رحم مادرها] به امانت گذاشته شده‌اند. ما آیات خویش را برای گروهی که می‌فهمند، به تفصیل بیان کردیم.'),
(888, 6, 99, 'و او کسی است که از آسمان، آبی نازل کرد، پس به وسیله‌ی آن، هر گونه گیاه برآوردیم و از آن سبزه‌ها خارج ساختیم. از آن [سبزه‌ها] دانه‌های بر هم نشسته و چیده شده بیرون آوریم. و از شکوفه‌ی خرما خوشه‌هایی نزدیک به هم [پدید آوریم] و نیز باغ‌هایی از [انواع] انگورها و زیتون و انار [پرورش دهیم] که برخی شبیه به هم و برخی غیر متشابهند. به میوه‌ی آن، آن گاه که بار دهد و آن گاه که میوه می‌رسد، بنگرید. همانا در آن، نشانه‌هایی برای گروه با ایمان است.'),
(889, 6, 100, 'و آنان برای خدا شریکانی از جنّ قرار دادند، در حالی که آنان را نیز خدا آفریده است و از روی نادانی برای خداوند، پسران و دخترانی ساخته و پرداخته‌اند. خداوند برتر و منزّه است از آنچه آنان درباره‌ی او وصف می‌کنند.'),
(890, 6, 101, '[خدا،] پدید آورنده‌ی آسمان‌ها و زمین است. چگونه برای او فرزندی باشد؟ در حالی که برای او همسری نبوده است و او هر چیز را آفریده و به هر چیز داناست.'),
(891, 6, 102, 'آن است خداوند، پروردگار شما! معبودی جز او نیست. آفریدگار هر چیز است. پس او را بپرستید. و او نگهبان و مدبّر همه چیز است.'),
(892, 6, 103, 'چشم‌ها او را در نمی‌یابد؛ ولی او چشم‌ها را در می‌یابد. و او دقیق و باریک‌بین و آگاه است.'),
(893, 6, 104, '«بی‌گمان از سوی پروردگارتان، مایه‌های بینش و بصیرت، برای شما آمده است. پس هر که بصیرت یافت، به سود خود اوست. و هر کس کوری گزید، به زیان خویش عمل کرده است. و من نگهبان و ضامن [ایمان] شما نیستم.»'),
(894, 6, 105, 'و بدین‌سان آیات را در شکل‌های گوناگون بیان می‌کنیم، [تا وسیله هدایت شود؛ ولی کافران ایمان نمی‌آورند] و می‌گویند: «تو درس خوانده‌ای!» ما برای کسانی بیان می‌کنیم که اهل فهم و علم باشند.'),
(895, 6, 106, 'تنها آنچه را از سوی پروردگارت به تو وحی شده، پیروی کن. معبودی جز او نیست. و از مشرکان روی بگردان.'),
(896, 6, 107, 'و اگر خداوند می‌خواست، [همه به اجبار ایمان می‌آوردند و] شرک نمی‌ورزیدند. [ولی سنّت الهی چنین نیست] و ما تو را بر آنان نگهبان قرار ندادیم و تو وکیل [ایمان آوردن] آنان نیستی.'),
(897, 6, 108, 'و به [معبود] کسانی که غیر خدا را می‌خوانند، ناسزا نگویید؛ که آنان نیز از روی جهل و دشمنی به خداوند ناسزا می‌گویند. ما این‌گونه عمل هر امّتی را برایشان جلوه دادیم. سپس بازگشت آنان به سوی پروردگارشان است. پس خداوند آنان را به آنچه می‌کردند، خبر می‌دهد.'),
(898, 6, 109, 'و با سخت‌ترینِ سوگندهایشان به خدا قسم یاد کردند که اگر معجزه و نشانه‌ای برایشان بیاید، قطعاً به آن ایمان خواهند آورد. بگو: «معجزات، تنها نزد خداست. و چه می‌دانید که اگر معجزه‌ای هم بیاید، آنان ایمان نخواهند آورد.»'),
(899, 6, 110, 'همان‌گونه که در آغاز ایمان نیاوردند، این بار نیز دلها و چشم‌هایشان را دگرگون و واژگون می‌کنیم و آنان را درطغیانشان رها می‌سازیم، تا سرگردان بمانند.'),
(900, 6, 111, 'و اگر ما فرشتگان را به سوی آنان نازل می‌کردیم و مردگان با آنان سخن می‌گفتند و همه چیز را [به گواهی صدق و اعجاز]، دسته دسته در برابرشان گرد می‌آوردیم، باز هم ایمان نمی‌آوردند. مگر آن که خداوند بخواهد [به اجبار ایمان آورند]، ولی بیشترشان نادانی می‌کنند.'),
(901, 6, 112, 'ما این‌گونه برای هر پیامبری، دشمنی از شیطان‌های انس و جنّ قرار دادیم، که برخی از آنان حرف‌های دلپسند و فریبنده را به برخی دیگر مرموزانه القا می‌کنند. البتّه اگر پروردگارت می‌خواست، چنین نمی‌کردند. [ولی سنّت الهی بر آزادی انسان‌هاست،] پس آنان را و آنچه به دروغ می‌سازند، به خودشان واگذار.'),
(902, 6, 113, 'و [شیاطین، سخنان فریبنده خود را برمردم می‌خوانند] تا گوش دل آنان که به قیامت ایمان ندارند، به آن سخنان مایل شود و آن را بپسندند. و آنان به دست آورند آنچه را به دنبال به دست آوردنش هستند.'),
(903, 6, 114, 'پس [بگو:] «آیا [با این همه دلایل روشن،] غیرِ خدا را به داوری جویم؟ در حالی که او کتاب آسمانی را به تفصیل به سوی شما نازل کرد.» و کسانی که به آنان کتاب داده‌ایم، می‌دانند که این کتاب، از طرف پروردگارت به حقّ نازل گشته است، پس به هیچ وجه از تردیدکنندگان مباش.'),
(904, 6, 115, 'و کلام پروردگارت، در صداقت و عدالت به حدّ کمال و تمام رسید. هیچ تغییر دهنده‌ای برای کلمات او نیست. و او شنوای داناست.'),
(905, 6, 116, 'و اگر از بیشترِ افراد روی زمین، اطاعت کنی، تو را از راه خداوند، منحرف و گمراه می‌کنند. زیرا آنان جز از گمان پیروی نمی‌کنند و آنان، جز به حدس و گمان نمی‌پردازند.'),
(906, 6, 117, 'قطعاً پروردگار تو، خود به کسانی که از راه او گمراه می‌شوند، داناتر است. و او به هدایت‌یافتگان [نیز] آگاه‌تر است.'),
(907, 6, 118, 'پس اگر به آیات الهی ایمان دارید، [تنها از] آنچه نام خداوند بر آن برده شد، بخورید.'),
(908, 6, 119, 'و شما را چه شده که از آنچه نام خداوند بر آن برده شده است، نمی‌خورید؟ [و بی‌جهت حلال ما را بر خود حرام می‌کنید.] در صورتی که خداوند آنچه را بر شما حرام کرده، خودش به تفصیل برای شما بیان نموده است، مگر آنچه [از محرّمات] که به خوردن آن مضطرّ شدید. همانا بسیاری از مردم، دیگران را جاهلانه به خاطر هوس‌های خود گمراه می‌کنند. قطعاً پروردگارت به متجاوزان آگاه‌تر است.'),
(909, 6, 120, 'و گناه آشکار و پنهان را رها کنید. کسانی که مرتکب گناه می‌شوند، به زودی کیفر کارهایی را که مرتکب شده‌اند، خواهند دید.'),
(910, 6, 121, 'و از آنچه نام خداوند بر آن برده نشده، نخورید؛ چرا که خوردن آن گناه است. شیاطین، به دوستان خود القا می‌کنند تا به جدال با شما برخیزند، [که مثلاً چه فرقی است میان حیوان مرده با ذبح شده؟] و اگر از آنان اطاعت کنید، قطعاً شما هم مشرک می‌شوید.'),
(911, 6, 122, 'و آن که [به واسطه‌ی جهل و شرک] مرده بود، پس او را [با هدایت خویش] حیات بخشیدیم و برای او نوری قرار دادیم که با آن در میان مردم راه خود را بیابد، آیا مَثَل او مَثَل کسی است که در ظلمت‌های [جهل و شرک] قرار دارد و از آن بیرون آمدنی نیست؟ این‌گونه برای کافران، کارهایی که می‌کردند، جلوه داده شده بود.'),
(912, 6, 123, 'و ما این‌گونه در هر دیاری، مجرمان بزرگش را می‌گماریم تا در آن قریه‌ها و مناطق، حیله کنند، امّا جز به خودشان نیرنگ نمی‌کنند و نمی‌فهمند [که نتیجه‌ی حیله، به خودشان بازمی‌گردد].'),
(913, 6, 124, 'و چون آیه و نشانه‌ای [برای هدایت آنان] نازل شد، گفتند: «ما هرگز ایمان نمی‌آوریم تا آن که به ما هم مثل آنچه به فرستادگان خدا داده شد، داده شود.» [بگو:] «خداوند بهتر می‌داند که رسالت خود را کجا قرار دهد.» به زودی به آنها که گناه کردند، به سزای آن مکر و نیرنگ‌ها که می‌کردند، خواری و عذاب سخت از نزد خداوند خواهد رسید.'),
(914, 6, 125, 'خداوند هر که را بخواهد هدایت کند، سینه‌اش را برای [پذیرش] اسلام می‌گشاید. و هر که را [به خاطر اعمال خلافش،] بخواهد گمراه کند، سینه‌اش را سخت قرار می‌دهد؛ گویا به زحمت در آسمان بالا می‌رود. خداوند این چنین پلیدیِ [کفر] را بر کسانی که ایمان نمی‌آورند، قرار می‌دهد.'),
(915, 6, 126, 'و این راه پروردگار توست که مستقیم است. قطعاً ما آیات خود را برای قوم و گروهی که پند می‌پذیرند، به تفصیل بیان کرده‌ایم.'),
(916, 6, 127, 'سرای سلامت نزد پروردگارشان، تنها برای آنان است. و او به پاداش اعمالی که انجام می‌دادند، سرپرست و پشتیبان و دوست آنان است.'),
(917, 6, 128, 'و روزی که خداوند، همه را گرد می‌آورد، [سپس به جنّیان خطاب می‌فرماید:] ای گروه جنّ! شما از انسان‌ها [پیروان] بسیاری یافتید. و دوستان ایشان از نسل بشر می‌گویند: «پروردگارا! ما انسان‌ها و شیاطین از یکدیگر بهره گرفتیم و اینک به سرانجامی که برایمان قرار داده بودی، رسیدیم.» خدا می‌فرماید: آتش جایگاه شماست که همیشه در آن خواهید بود، مگر آن که خداوند بخواهد [و شما را ببخشاید]. همانا پروردگار تو حکیم و داناست.'),
(918, 6, 129, 'و بدین‌گونه بعضی ستمگران را بر بعضی مسلّط می‌کنیم، [تا کیفری باشد] برای آنچه عمل می‌کردند.'),
(919, 6, 130, 'ای گروه جنّ و انس! آیا پیامبرانی از خودتان به سویتان نیامدند تا آیات مرا بر شما بخوانند و شما را از دیدار این روز بیمتان دهند؟ گویند: «ما علیه خودمان گواهی می‌دهیم.» و زندگی دنیا آنان را فریب داد و علیه خود شهادت دادند که کافر بوده‌اند.'),
(920, 6, 131, 'این [اتمام حجّت] برای آن است که پروردگارت هرگز از روی ستم، آبادی‌هایی را که اهلش [حق را نشناخته‌اند و از شناخت آن غافلند، نابود نمی‌کند.'),
(921, 6, 132, 'و برای هر کس بر اساس آنچه انجام داده، درجاتی است. و پروردگار تو از آنچه می‌کنند، غافل نیست.'),
(922, 6, 133, 'و پروردگار تو بی‌نیاز و صاحبِ رحمت است. اگر بخواهد، شما را می‌برد و پس از شما هر که را بخواهد، جایگزین می‌کند، همان‌گونه که شما را از نسل قوم دیگری پدید آورد.'),
(923, 6, 134, 'به راستی آنچه به شما وعده می‌دهند، آمدنی است و شما نمی‌توانید [خدا را] ناتوان سازید [و از کیفر و عدل او بگریزید].'),
(924, 6, 135, 'بگو: «ای قوم من! بر جایگاه و سیرت خویش عمل کنید که من نیز [بر اساس سیرت خویش] عمل می‌کنم. و به زودی خواهید دانست که سرانجام آن سرای از کیست. ستمکاران رستگار نمی‌شوند.»'),
(925, 6, 136, 'رو [بت‌پرستان] از آنچه خداوند از کشت و چهارپایان آفریده است، سهمی را برای خدا قرار دادند و به پندار خودشان گفتند: «این قسمت برای خداست و این قسمت برای [بت‌هایی که هم] شریکان [خدایند، هم شریک اموال] ما!» پس آنچه سهم شرکا و بت‌ها بود، به خدا نمی‌رسید؛ ولی هرچه سهم خدا بود، به شرکا می‌رسید. چه بد است آنچه داوری می‌کنند!'),
(926, 6, 137, 'و بدین سان [بت‌ها و خادمان بت‌ها که] شریکان آنها [در اموالشان محسوب می‌شدند،] کشتن فرزند را برای بسیاری از مشرکان [به عنوان قربانی برای بت‌ها] زیبا جلوه دادند تا آنان را به هلاکت کشیده و آیین آنان را مشتَبَه سازند. و اگر خداوند می‌خواست چنین نمی‌کردند [و به اجبار مانعشان می‌شد]، پس آنان را با دروغ‌هایی که می‌بافند، واگذار.'),
(927, 6, 138, 'و مشرکان گفتند: «اینها، دام‌ها و کشت‌هایی ممنوع است. کسی نخورد مگر آن [خادمان بتکده‌ها] که ما بخواهیم. و [اینها] چهارپایانی است که [سوار شدن بر] پشت آنها حرام است.» و چهارپایانی که نام خدا را بر آن نمی‌ََبرند، [این احکام را] به دروغ به خدا نسبت می‌دادند. خداوند به زودی آنان را به کیفر آنچه به او افترا می‌بستند، جزا خواهد داد.'),
(928, 6, 139, 'و گفتند: «آنچه در شکم این چهارپایانِ [نذر شده برای بت‌ها] است، [اگر زنده به دنیا آید،] مخصوص مردان ماست و بر زنان ما حرام است. و اگر مرده به دنیا آید، پس همه، [زن و مرد] در آن شریکند.» خداوند به زودی آنان را به سزای این‌گونه وصف‌های باطل، کیفر خواهد داد. همانا او حکیم و داناست.'),
(929, 6, 140, 'به یقین زیان کردند کسانی که فرزندان خود را سفیهانه و از روی جهل کشتند. و آنچه را خداوند روزی آنان ساخته بود، با افترا بر خدا، حرام کردند. آنان گمراه شدند و هدایت‌یافته نبودند.'),
(930, 6, 141, 'اوست که باغ‌های معروش، [که درختانش روی داربست‌ها قرار می‌گیرند] و باغ‌های غیر معروش، [که درختانش نیاز به داربست ندارند،] را آفرید و همچنین نخل و انواع زراعت را که از نظر میوه و طعم با هم متفاوتند و [نیز] درخت زیتون و انار را که از جهتی با هم شبیه هستند و از جهتی تفاوت دارند. از میوه آن به هنگامی که به ثمر می‌نشیند، بخورید و حقِ آن را به هنگام درو بپردازید و زیاده‌روی نکنید که خداوند مسرفان را دوست نمی‌دارد.'),
(931, 6, 142, '[و او کسی است که] از چهارپایان برای شما حیوانات باربر و حیوانات کوچک آفرید. از آنچه او به شما روزی داده است، بخورید و از گام‌های شیطان، پیروی ننمایید، که او دشمن آشکار شماست.'),
(932, 6, 143, 'هشت جفت از چهارپایان [برای شما آفرید]؛ از میش دو جفت و از بز دو جفت، بگو: «آیا خداوند، نرهای آنها را حرام کرده یا ماده‌های آنها را؟ یا آنچه رحم ماده‌های آنها در بر گرفته است؟ اگر راست می‌گویید [و بر تحریم آنها دلیلی دارید،] به من خبر دهید.»'),
(933, 6, 144, 'و از شتر دو زوج و از گاو هم دو زوج [برای شما آفرید]؛ بگو: «کدام یک از اینها را خدا حرام کرده است؟ نرها را یا ماده‌ها را؟ یا آنچه را رحم ماده‌ها در برگرفته است؟ آیا شما گواه [این تحریم] بودید، هنگامی که خدا به این موضوع توصیه کرد؟ بنابراین چه کسی ستمکارتر است از آنها که بر خدا دروغ می‌بندند، تا مردم را از روی جهل گمراه سازند. خداوند هیچ‌گاه ستمگران را هدایت نخواهد کرد!»'),
(934, 6, 145, 'بگو: «در آنچه بر من وحی شده، هیچ حرامی بر کسی که غذایی می‌خورد، نمی‌یابم؛ به جز این که مردار باشد، یا خونی که [از بدن حیوان] بیرون ریخته، یا گوشت خوک که اینها همه پلیدند، یا حیوانی که در طریق گناه به هنگام سر بریدن، نام غیر خدا، [نام بت‌ها] بر آنها برده شده است.» اما کسانی که ناچار شوند، بدون این‌که به خاطر لذت باشد و یا زیاده از حد بخورند، [گناهی بر آنها نیست،] پروردگار تو آمرزنده‌ی مهربان است.'),
(935, 6, 146, 'و بر یهودیان، هر حیوان ناخن‌دار، [حیواناتی که سم یکپارچه دارند] را حرام کردیم و از گاو و گوسفند، پیه و چربیشان را تحریم نمودیم؛ مگر چربی‌هایی که بر پشت اینها قرار دارد و یا در دو طرف پهلوهاست و یا آنها که با استخوان آمیخته است. این را به خاطر ظلم و ستمی که می‌کردند، به آنها کیفر دادیم و ما راست می‌گوییم.'),
(936, 6, 147, 'اگر تو را تکذیب کنند [و این حقایق را نپذیرند]، به آنها بگو: «پروردگار شما رحمت وسیعی دارد؛ اما در عین حال، مجازات او از مجرمان دفع‌شدنی نیست.»'),
(937, 6, 148, 'به زودی مشرکان [برای تبرئه خویش،] می‌گویند: «اگر خدا می‌خواست، نه ما مشرک می‌شدیم و نه پدران ما. و نه چیزی را تحریم می‌کردیم.» کسانی که پیش از آنها بودند نیز همین گونه دروغ می‌گفتند. و سرانجام [طعم] کیفر ما را چشیدند. بگو: «آیا دلیل قاطعی [بر این موضوع] دارید؟ پس به ما ارائه دهید. شما فقط از پندارهای بی‌اساس پیروی می‌کنید و تخمین‌های نابجا می‌زنید.»'),
(938, 6, 149, 'بگو: «برای خدا دلیل رسا [و قاطع] است، [که بهانه‌ای برای هیچ کس باقی نمی‌گذارد،] اما اگر او بخواهد، همه شما را [به اجبار] هدایت می‌کند.»'),
(939, 6, 150, 'بگو: «گواهان خود را که گواهی می‌دهند خداوند اینها را حرام کرده است، بیاورید.» اگر آنها [به دروغ] گواهی دهند، تو با آنها [هم‌صدا نشو و] گواهی مده. و از هوا و هوس کسانی که آیات ما را تکذیب کردند و کسانی که به آخرت ایمان ندارند و برای خدا شریک قائلند، پیروی مکن.'),
(940, 6, 151, 'بگو: «بیایید آنچه را پروردگارتان بر شما حرام کرده است، برایتان بخوانم: این که چیزی را شریک خدا قرار ندهید، و به پدر و مادر نیکی کنید، و فرزندانتان را از [ترس] فقر نکشید، ما، شما و آنها را روزی می‌دهیم، و نزدیک کارهای زشت و قبیح نروید، چه آشکار باشد و چه پنهان، و نفسی را که خداوند محترم شمرده، به قتل نرسانید، مگر به حق [و از روی استحقاق]. این چیزی است که خداوند شما را به آن سفارش کرده، تا درک کنید.'),
(941, 6, 152, 'و به مال یتیم جز به بهترین صورت نزدیک نشوید، تا به حد رشد برسد. و حق پیمانه و وزن را به عدالت ادا کنید. هیچ کس را، جز به مقدار توانایی تکلیف نمی‌کنیم. و هنگامی که سخنی می‌گویید، عدالت را رعایت نمایید، حتی اگر در مورد نزدیکان بوده باشد. این چیزی است که خداوند شما را به آن سفارش می‌کند تا متذکر شوید.'),
(942, 6, 153, 'و این که، این راه مستقیم من است، از آن پیروی کنید و از راه‌های مختلف پیروی نکنید، که شما را از راه حق دور می‌سازد. این چیزی است که خداوند شما را به آن سفارش می‌کند تا پرهیزکار شوید.»'),
(943, 6, 154, 'سپس به موسی کتاب دادیم. [و نعمت خود را] بر آنها که نیکوکار بودند، تکمیل کردیم و همه چیز را [که مورد نیاز آنها بود، در آن] روشن ساختیم. کتابی که مایه‌ی هدایت و رحمت بود، تا به لقای پروردگارشان [و روز رستاخیز] ایمان بیاورند.'),
(944, 6, 155, 'و این کتابی است پر برکت که ما [بر تو] نازل کردیم. از آن پیروی نمایید و پرهیزکاری پیشه کنید تا مورد رحمت قرار گیرید.'),
(945, 6, 156, 'ما این قرآن را نازل کردیم،] تا نگویید: «کتاب آسمانی تنها بر دو طایفه پیش از ما، [یهود و نصارا] نازل شده بود و ما از بحث و بررسی آنها بی‌خبر بودیم.»'),
(946, 6, 157, 'یا نگویید: «اگر کتاب آسمانی بر ما نازل می‌شد، از آنها هدایت‌یافته‌تر بودیم.» اینک آیات و دلایل روشن از جانب پروردگارتان برای شما آمد و همچنین هدایت و رحمت او. با این حال چه کسی ستمکارتر از آنهایی است که آیات خدا را تکذیب کردند و از آن روی‌گردانیدند؟ به زودی کسانی را که از آیات ما روی‌گردانیدند، به خاطر همین روی‌گردانی بی‌دلیلشان مجازات شدید خواهیم کرد.'),
(947, 6, 158, 'آیا جز این انتظار دارند که فرشتگان به سراغشان بیایند، یا خداوند [خودش] به سوی آنها بیاید؟ یا پاره‌ای از آیات پروردگار [و نشانه‌های رستاخیز]؟ اما آن روز که این نشانه‌ها تحقق پذیرد، ایمان آوردن افرادی که قبلاً ایمان نیاورده‌اند و یا عمل نیکی انجام نداده‌اند، سودی به حالشان نخواهد داشت، بگو: «[اکنون که شما چنین انتظارات غلطی دارید،] انتظار بکشید، ما هم انتظار می‌کشیم!»'),
(948, 6, 159, 'کسانی که آیین خود را پراکنده ساختند و به دسته‌های گوناگون تقسیم شدند، هیچ‌گونه ارتباطی با آنها نداری. کار آنها واگذار به خداست، پس خدا آنها را از آنچه انجام می‌دادند، باخبر می‌کند.'),
(949, 6, 160, 'هر کس کار نیکی بیاورد، ده برابر آن پاداش خواهد داشت. و هر کس کار بدی بیاورد، جز به مقدار آن کیفر نخواهد دید و ستمی بر آنها نخواهد شد.'),
(950, 6, 161, 'بگو: «پروردگارم مرا به راه راست هدایت کرده، آیینی پا بر جا آیین ابراهیم، همان کسی که از آیین‌های خرافیِ محیط خود روی گردانید و از مشرکان نبود.»'),
(951, 6, 162, 'بگو: «نماز و تمام عبادات من و زندگی و مرگ من، همه برای خداوند پروردگار جهانیان است.'),
(952, 6, 163, 'شریکی برای او نیست و به همین، دستور یافته‌ام و من نخستین مسلمانم!»'),
(953, 6, 164, 'بگو: «آیا غیر خدا، پروردگاری بطلبم؟ در حالی که او پروردگار همه چیز است و هیچ‌کس جز برای خود عملی انجام نمی‌دهد و هیچ‌کس بار [گناه] دیگری را به دوش نمی‌کشد، سپس بازگشت شما به سوی پروردگارتان است. و شما را به آنچه در آن اختلاف داشتید، خبر خواهد داد.»'),
(954, 6, 165, 'و او کسی است که شما را جانشینان [خود] در زمین قرار داد و بعضی را بر بعض دیگر، درجاتی برتری داد، تا شما را به آنچه در اختیارتان قرار داده، بیازماید. مسلماً پروردگار تو سریع‌العقاب و آمرزنده مهربان است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف، لام، میم، صاد.'),
(956, 7, 2, 'این، کتابی است که بر تو نازل شده، پس نباید از ناحیه آن، ناراحتی در سینه داشته باشی و هدف، آن است که به وسیله آن [همگان را از عواقب بد عقاید و اعمال نادرستشان] بیم دهی و تذکری است برای مؤمنان.'),
(957, 7, 3, '[بنابراین] آنچه را از طرف پروردگارتان بر شما نازل شده، پیروی کنید و از سرپرستان و معبودهای دیگر جز او پیروی نکنید، اما کمتر متذکر می‌شوید.'),
(958, 7, 4, 'چه بسیار شهرها و آبادی‌ها که آنها را [بر اثر گناه فراوان] هلاک کردیم و عذاب ما شب‌هنگام یا در روز، هنگامی که استراحت کرده بودند، به سراغشان آمد.'),
(959, 7, 5, 'و در آن موقع که عذاب ما به سراغ آنها آمد، سخنی نداشتند جز این که گفتند: «ما ظالم بودیم.» [ولی این اعتراف، به حالشان سودی نداشت.]'),
(960, 7, 6, 'به طور قطع از کسانی که پیامبران را به سوی آنها فرستادیم، سؤال خواهیم کرد و از پیامبران [نیز] سؤال می‌کنیم.'),
(961, 7, 7, 'و مسلماً [اعمال همه را] از روی علمِ [وسیع خود] برای آنان شرح خواهیم داد. و ما غایب نبودیم [، بلکه همه جا حاضر و ناظر بودیم].'),
(962, 7, 8, 'و وزن کردن [اعمال و سنجش ارزش آنها] در آن روز، حقّ است. کسانی که میزان‌های [عملِ] آنها سنگین است، رستگارانند.'),
(963, 7, 9, 'و کسانی که میزان‌های [عملِ] آنها سبک است، افرادی هستند که سرمایه وجود خود را به خاطر ظلم و ستمی که نسبت به آیات ما می‌کردند، از دست داده‌اند.'),
(964, 7, 10, 'و ما تسلط و مالکیت و حکومت بر زمین را برای شما قرار دادیم و انواع وسایل زندگی را برای شما فراهم ساختیم؛ اما کمتر شکرگزاری می‌کنید.'),
(965, 7, 11, 'ما شما را آفریدیم، سپس صورت‌بندی کردیم، سپس به فرشتگان گفتیم: «برای آدم خضوع کنید!» آنها سجده کردند، جز ابلیس که از سجده‌کنندگان نبود.'),
(966, 7, 12, '[خداوند به او] فرمود: «در آن هنگام که به تو فرمان دادم، چه چیز تو را مانع شد که سجده کنی؟» گفت: «من از او بهترم! مرا از آتش آفریده‌ای و او را از خاک!»'),
(967, 7, 13, 'گفت: «از آن [مقام و مرتبه] فرود آی! تو حق نداری در آن [جایگاه] تکبر بورزی. بیرون رو که تو از افراد پست و کوچکی!»'),
(968, 7, 14, '[ابلیس] گفت: «مرا تا روز رستاخیز مهلت ده.»'),
(969, 7, 15, 'فرمود: «تو از مهلت داده‌شدگانی!»'),
(970, 7, 16, 'گفت: «اکنون که مرا گمراه ساختی، من بر سر راه مستقیم تو برای آنها کمین می‌کنم.'),
(971, 7, 17, 'سپس از پیش رو و از پشت سر و از طرف راست و از طرف چپِ آنها، به سراغشان می‌روم. و بیشتر آنها را شکرگزار نخواهی یافت.»'),
(972, 7, 18, 'فرمود: «از آن [مقام] با ننگ و عار و خواری بیرون رو! و سوگند یاد می‌کنم که هر کس از آنها، تو را پیروی کند، جهنم را از شما همگی پر می‌کنم!'),
(973, 7, 19, 'و ای آدم! تو و همسرت در بهشت ساکن شوید و از هر جا که خواستید بخورید؛ اما به این درخت نزدیک نشوید که از ستمکاران خواهید شد.»'),
(974, 7, 20, 'سپس شیطان آنها را وسوسه کرد، تا آنچه را از اندامشان پنهان بود، آشکار سازد و گفت: «پروردگارتان شما را از این درخت نهی نکرده، مگر به خاطر این که فرشته شوید یا جاودانه [در بهشت] بمانید!.»'),
(975, 7, 21, 'و برای آنها سوگند یاد کرد که: «من خیرخواه شما هستم!»'),
(976, 7, 22, 'و به این ترتیب، آنها را با فریب [از مقامشان] فرود آورد. و هنگامی که از آن درخت چشیدند، اندامشان [که پنهان بود،] برای آنها آشکار شد و شروع کردند به قرار دادن برگهای [درختان] بهشتی بر یکدیگر، تا آن را بپوشانند. و پروردگارشان آنها را ندا داد که: «آیا شما را از آن درخت نهی نکردم؟ و نگفتم شیطان برای شما دشمن آشکاری است؟»'),
(977, 7, 23, 'گفتند: «پروردگارا! ما به خویشتن ستم کردیم.» «و اگر ما را نبخشی و بر ما رحم نکنی، از زیانکاران خواهیم بود.»'),
(978, 7, 24, 'فرمود: «[از مقام خویش،] فرود آیید، در حالی که بعضی نسبت به بعض دیگر دشمن خواهید بود، و برای شما در زمین قرارگاه و وسیله بهره‌گیری تا زمانی معین خواهد بود.»'),
(979, 7, 25, 'فرمود: در آن [زمین] زنده می‌شوید و در آن می‌میرید و از آن [در رستاخیز] بیرون خواهید آمد.'),
(980, 7, 26, 'ای فرزندان آدم! لباسی برای شما فرو فرستادیم که اندام شما را می‌پوشاند و مایه‌ی زینت شماست و لباس پرهیزکاری بهتر است. اینها [همه] از آیات خداست، شاید متذکر [نعمت‌های او] شوند.'),
(981, 7, 27, 'ای فرزندان آدم! شیطان شما را نفریبد. آن چنان که پدر و مادر شما را از بهشت بیرون کرد و لباسشان را از تنشان بیرون ساخت تا عورتشان را به آنها نشان دهد. چه این که او و همکارانش شما را می‌بینند و شما آنها را نمی‌بینید. [اما بدانید] ما شیاطین را اولیای کسانی قرار دادیم که ایمان نمی‌آورند.'),
(982, 7, 28, 'و هنگامی که کار زشتی انجام می‌دهند، می‌گویند: «پدران خود را بر این عمل دیدیم و خداوند به ما دستور داده است!» بگو: «خداوند [هرگز] دستور به عمل زشت نمی‌دهد. آیا چیزی بر خدا می‌بندید که نمی‌دانید.»'),
(983, 7, 29, 'بگو: «پروردگارم به عدالت فرمان داده است. و توجه خویش را در هر مسجد [و به هنگام عبادت،] به سوی او کنید و او را بخوانید و دین [خود] را برای او خالص گردانید. [و بدانید] همان گونه که در آغاز شما را آفرید، [بار دیگر در رستاخیز] بازمی‌گردید.»'),
(984, 7, 30, 'جمعی را هدایت کرده و جمعی را [که شایستگی نداشته‌اند،] گمراهی بر آنها مسلم شده است. آنها [کسانی هستند که] شیاطین را به جای خداوند، اولیای خود انتخاب کردند و چنین گمان می‌کنند که هدایت یافته‌اند.'),
(985, 7, 31, 'ای فرزندان آدم! زینت خود را هنگام رفتن به مسجد [با خود] بردارید. و بخورید و بیاشامید و اسراف نکنید که خداوند مسرفان را دوست نمی‌دارد.'),
(986, 7, 32, 'بگو: «چه کسی زینت‌های الهی را که برای بندگان خود آفریده و روزی‌های پاکیزه را حرام کرده است؟» بگو: «اینها در زندگی دنیا برای کسانی است که ایمان آورده‌اند [اگرچه کافران نیز با آنها مشارکت دارند؛ ولی] در قیامت خالص [برای مؤمنان] خواهد بود.» این چنین آیات [خود] را برای کسانی که آگاهند، شرح می‌دهیم.'),
(987, 7, 33, 'بگو: «خداوند تنها اعمال زشت را، چه آشکار باشد، چه پنهان، حرام کرده است و [همچنین] گناه و ستمِ به ناحق و این که چیزی را که خداوند دلیلی برای آن نازل نکرده، شریک او قرار دهید و به خدا مطلبی نسبت دهید که نمی‌دانید.»'),
(988, 7, 34, 'برای هر قوم و جمعیتی، زمان و مدت [معینی] است. و هنگامی که مدت آنها فرارسد، نه ساعتی از آن تأخیر می‌کنند و نه بر آن پیشی می‌گیرند.'),
(989, 7, 35, 'ای فرزندان آدم! اگر رسولانی از خود شما به سراغتان بیایند که آیات مرا برای شما بازگو کنند، [از آنها پیروی کنید؛] زیرا آنها که پرهیزکاری پیشه کنند و عمل صالح انجام دهند، نه ترسی بر آنهاست و نه غمناک می‌شوند.'),
(990, 7, 36, 'و آنها که آیات ما را تکذیب کنند و در برابر آن تکبر ورزند، اهل دوزخند؛ جاودانه در آن خواهند ماند.'),
(991, 7, 37, 'چه کسی ستمکارتر است از آنها که بر خدا دروغ می‌بندند یا آیات او را تکذیب می‌نمایند؟ آنها نصیبشان را از آنچه مقدر شده [از نعمت‌ها و مواهب این جهان،] می‌برند، تا زمانی که فرستادگان ما [فرشتگان قبض ارواح]، به سراغ آنها بروند و جانشان را بگیرند، [فرشتگان] از آنها می‌پرسند: «کجا هستند معبودهایی که غیر از خداوند می‌خواندید؟ [چرا به یاری شما نمی‌آیند؟]» می‌گویند: «آنها گم شدند [و از ما دور گشتند].» و ضد خود گواهی می‌دهند که کافر بودند!'),
(992, 7, 38, '[خداوند به آنها] می‌گوید: «در صف گروه‌های مشابه خود از جنّ و انس در آتش داخل شوید.» هر زمان گروهی وارد می‌شوند، گروه دیگر را لعن می‌کنند تا همگی با ذلت در آن قرار گیرند. پیروان درباره پیشوایان خود می‌گویند: «خداوندا! اینها بودند که ما را گمراه ساختند، پس کیفر آنها را از آتش دو برابر کن [کیفری برای گمراهی‌شان و کیفری به‌خاطر گمراه ساختن ما].» می‌فرماید: «برای هر کدام [از شما] عذاب مضاعف است، ولی نمی‌دانید [زیرا پیروان نیز اگر گرد پیشوایان گمراه را نگرفته بودند، آنها قدرتی برای فریب دیگران نداشتند].»'),
(993, 7, 39, 'و پیشوایان آنها به پیروان خود می‌گویند: «شما امتیازی بر ما ندارید، پس بچشید عذاب [الهی] را در برابر آنچه انجام می‌دادید.»'),
(994, 7, 40, 'آنها که آیات ما را تکذیب کردند و در برابر آن تکبر ورزیدند، درهای آسمان به رویشان گشوده نمی‌شود و داخل بهشت نمی‌شوند، مگر این که شتر از سوراخ سوزن خیاطی بگذرد! این چنین گنهکاران را جزا می‌دهیم.'),
(995, 7, 41, 'برای آنها بسترهایی از [آتش] دوزخ و روی آنها پوشش‌هایی [از آن] است. و این چنین ظالمان را جزا می‌دهیم.'),
(996, 7, 42, 'و کسانی که ایمان آورند و عمل صالح انجام دهند، اهل بهشتند و جاودانه در آن خواهند ماند. [البته] هیچ کس را جز به اندازه تواناییش تکلیف نمی‌کنیم.'),
(997, 7, 43, 'و آنچه در دل‌ها از کینه و حسد دارند، برمی‌کنیم [تا در صفا و صمیمیت با هم زندگی کنند] و از زیر [قصرها و درختان] آنها، نهرها جریان دارد. و می‌گویند: «سپاس مخصوص خداوندی است که ما را به این [همه نعمت] رهنمون شد. و اگر او ما را هدایت نکرده بود، ما راه نمی‌یافتیم. مسلماً فرستادگان پروردگار ما حق می‌گفتند.» و [در این هنگام] ندا داده می‌شود که: «این بهشت را در برابر اعمالی که انجام می‌دادید، به ارث بردید.»'),
(998, 7, 44, 'و بهشتیان، دوزخیان را صدا می‌زنند که: «ما آنچه را پروردگارمان به ما وعده داده بود، همه را حق یافتیم، آیا شما هم آنچه را پروردگارتان به شما وعده داده بود، حق یافتید؟!» در این هنگام، ندادهنده‌ای در میان آنها ندا می‌دهد که: «لعنت خدا بر ستمگران باد!'),
(999, 7, 45, 'همان‌ها که [مردم را] از راه خدا بازمی‌دارند و می‌خواهند آن را کج و ناراست نشان دهند، و آنان به آخرت کافرند.»'),
(1000, 7, 46, 'و در میان آن دو [گروه بهشتیان و دوزخیان]، حجابی قرار دارد. و بر اَعراف مردانی هستند که هر یک از آن دو را از سیمایشان می‌شناسند و به بهشتیان ندا می‌دهند که: «درود بر شما باد!» اما داخل بهشت نمی‌شوند، در حالی که امیدِ آن را دارند.'),
(1001, 7, 47, 'و هنگامی که چشمشان به دوزخیان می‌افتد، می‌گویند: «پروردگارا! ما را با جمعیت ستمگر قرار مده!»'),
(1002, 7, 48, 'و اصحاب اعراف، مردانی [از دوزخیان را] که از سیمایشان آنها را می‌شناسند، صدا می‌زنند و می‌گویند: «دیدید که گردآوری شما [از مال و ثروت و زن و فرزند] و تکبّرهای شما به حالتان سودی نداد؟'),
(1003, 7, 49, 'آیا اینها [که مؤمنند،‌] همانان نیستند که سوگند یاد کردید رحمت خدا هرگز شامل حالشان نخواهد شد؟! [ولی خداوند به خاطر ایمان و اعمال خیرشان، آنها را بخشید. سپس به مؤمنان می‌گویند:] «به بهشت درآیید که نه بیمی بر شماست و نه اندوهگین می‌شوید.»'),
(1004, 7, 50, 'و دوزخیان، بهشتیان را صدا می‌زنند که: «مقداری آب یا از آنچه خدا به شما روزی داده، به ما ببخشید.» آنها می‌گویند: «خداوند اینها را بر کافران حرام کرده است.»'),
(1005, 7, 51, 'همانها که دین و آیین خود را سرگرمی و بازیچه گرفتند و زندگی دنیا آنها را مغرور ساخت. امروز ما آنها را فراموش می‌کنیم؛ چون لقای چنین روزی را فراموش کردند و آیات ما را انکار نمودند.'),
(1006, 7, 52, 'ما کتابی برای آنها آوردیم که آن را براساس علم شرح دادیم. [کتابی] که مایه‌ی هدایت و رحمت برای جمعیتی است که ایمان می‌آورند.'),
(1007, 7, 53, 'آیا آنها انتظار دارند سرانجامِ تهدیدهای الهی را مشاهده کنند؟ آن روز که این امر صورت گیرد، [بیداری سودی نخواهد داشت و] آنها که آن را قبلاً فراموش کرده بودند، می‌گویند: «فرستادگان پروردگار ما به حق آمدند، آیا [امروز] شفیعانی برای ما وجود دارند که ما را شفاعت کنند؟ یا [امکان دارد] بازگردیم و اعمالی غیر از آنچه انجام دادیم، انجام دهیم؟!» [ولی] آنها سرمایه وجود خود را از دست دادند و معبودهایی را که به دروغ ساخته بودند، همگی از نظرشان گم می‌شوند.'),
(1008, 7, 54, 'پروردگار شما، خدایی است که آسمان‌ها و زمین را در شش روز [و دوران] آفرید، سپس به تدبیر جهان هستی پرداخت، با [پرده‌ی تاریک] شب، روز را می‌پوشاند و شب به دنبال روز به سرعت در حرکت است و خورشید و ماه و ستارگان را آفرید، در حالی که رام شده فرمان او هستند. آگاه باشید که آفرینش و تدبیر [جهان] برای او [و به فرمان او] است. پر برکت [و زوال‌ناپذیر] است، خدایی که پروردگار جهانیان است.'),
(1009, 7, 55, 'پروردگار خود را از روی تضرع و پنهانی، بخوانید [و از تجاوز، دست بردارید که] او متجاوزان را دوست نمی‌دارد.'),
(1010, 7, 56, 'و در زمین فساد مکنید بعد از آن که اصلاح شده است و او را با بیم و امید بخوانید، [بیم از مسئولیت‌ها و امید به رحمتش، و نیکی کنید،] زیرا رحمت خدا به نیکوکاران نزدیک است.'),
(1011, 7, 57, 'او کسی است که بادها را مژده‌ای پیشاپیش [بارانِ] رحمتش می‌فرستد تا زمانی که ابرهای سنگین، بار را [بر دوش خود] حمل کنند، در این هنگام آنها را به ‌سوی سرزمین‌های مرده می‌فرستیم و به‌ وسیله آن آب [حیات بخش] نازل می‌کنیم، و با آن از هرگونه میوه‌ای [از خاک تیره] بیرون می‌آوریم و این‌گونه [در قیامت،] مردگان را زنده می‌کنیم تا متذکر شوید.'),
(1012, 7, 58, 'و سرزمین پاکیزه گیاهش به فرمان پروردگار می‌روید؛ و سرزمین پلید [و شوره‌زار،] جز گیاه ناچیز و بی‌ارزش از آن نمی‌روید. این‌گونه آیات را برای آنها که شکرگزارند، بیان می‌کنیم.'),
(1013, 7, 59, 'ما نوح را به سوی قومش فرستادیم، او به آنها گفت: «ای قوم من! [تنها] خداوند یگانه را پرستش کنید که معبودی جز او برای شما نیست، من بر شما از عذاب روز بزرگی می‌ترسم.»'),
(1014, 7, 60, 'اشراف قومش به او گفتند: «ما تو را در گمراهی آشکاری می‌بینیم!»'),
(1015, 7, 61, 'گفت: «ای قوم من! هیچ گمراهی در من نیست، بلکه من فرستاده پروردگار جهانیانم.'),
(1016, 7, 62, 'رسالت‌های پروردگارم را به شما ابلاغ می‌کنم و خیرخواه شما هستم. چیزهایی می‌دانم که شما نمی‌دانید.'),
(1017, 7, 63, 'آیا تعجب کرده‌اید که دستور آگاه‌کننده‌ی پروردگارتان به وسیله مردی از میان شما به شما برسد، تا [از عواقب اعمال خلاف] بیمتان دهد؟ پرهیزکاری پیشه کنید، شاید مشمول رحمت گردید.»'),
(1018, 7, 64, 'اما سرانجام او را تکذیب کردند، و ما، او و کسانی را که با وی در کشتی بودند، رهایی بخشیدیم، و آنها که آیات ما را تکذیب کردند، غرق نمودیم. چه این که آنها جمعیتی کوردل بودند.'),
(1019, 7, 65, 'و به سوی قوم عاد، برادرشان، هود را فرستادیم. گفت: «ای قوم من! خدا را پرستش کنید که جز او معبودی برای شما نیست. آیا پرهیزکاری پیشه نمی‌کنید؟!»'),
(1020, 7, 66, 'اشراف کافر قوم او گفتند: «ما تو را در سفاهت [و نادانی و سبک‌مغزی] می‌بینیم و ما مسلماً تو را از دروغگویان می‌دانیم.»'),
(1021, 7, 67, 'گفت: «ای قوم من! هیچ‌گونه سفاهتی در من نیست، بلکه فرستاده‌ای از طرف پروردگار جهانیانم.'),
(1022, 7, 68, 'رسالت‌های پروردگارم را به شما ابلاغ می‌کنم، و من خیرخواه امینی برای شما هستم.'),
(1023, 7, 69, 'آیا تعجب کرده‌اید که دستور آگاه‌کننده‌ی پروردگارتان به وسیله‌ی مردی از میان شما به شما برسد، تا [از مجازات الهی] بیمتان دهد؟ و به یاد آورید هنگامی که شما را جانشینان قوم نوح قرار داد، و شما را از جهت خلقت گسترش داد [و به نیروی بدنی قوی مجهز ساخت]، پس نعمت‌های خدا را به یاد آورید، شاید رستگار شوید.»'),
(1024, 7, 70, 'گفتند: «آیا به سراغ ما آمده‌ای که تنها خدای یگانه را بپرستیم و آنچه را پدران ما می‌پرستیدند رها کنیم؟ حال که چنین است، اگر راست می‌گویی آنچه [از بلا و عذاب الهی] به ما وعده می‌دهی، بیاور!»'),
(1025, 7, 71, 'گفت: «پلیدی، و غضب پروردگار، شما را فراگرفته است، آیا با من در مورد نام‌هایی مجادله می‌کنید [که به عنوان معبود و خدا] شما و پدرانتان [بر بت‌ها] گذارده‌اید؟ در حالی که خداوند هیچ فرمانی درباره آن نازل نکرده است. بنابراین شما منتظر باشید، من هم با شما انتظار می‌کشم. [شما انتظار شکست من، و من انتظار عذاب الهی برای شما!]»'),
(1026, 7, 72, 'سرانجام او و کسانی را که با او بودند، به رحمت خود نجات بخشیدیم، و ریشه کسانی که آیات ما را تکذیب کردند و ایمان نیاوردند، قطع کردیم.'),
(1027, 7, 73, 'و به سوی [قوم] ثمود، برادرشان صالح را فرستادیم. گفت: «ای قوم من! خدا را بپرستید که جز او معبودی برای شما نیست. دلیل روشنی از طرف پروردگار برای شما آمده: این ناقه‌ی الهی برای شما معجزه‌ای است. او را به حال خود واگذارید که در سرزمین خدا [بچرد و از علف‌های بیابان] بخورد و آن را آزار نرسانید که عذاب دردناکی شما را خواهد گرفت!'),
(1028, 7, 74, 'و به خاطر بیاورید که شما را جانشینان قوم عاد قرار داد، و در زمین مستقر ساخت، که در دشت‌هایش قصرها برای خود بنا می‌کنید و در کوه‌ها برای خود خانه‌ها می‌تراشید. بنابراین، نعمت‌های خدا را متذکر شوید و در زمین فساد نکنید.»'),
(1029, 7, 75, '[ولی] اشراف متکبر قوم او، به مستضعفانی که ایمان آورده بودند، گفتند: «آیا [به‌راستی] شما یقین دارید که صالح از طرف پروردگار خود فرستاده شده است؟» آنها گفتند: «ما به آنچه او بدان مأموریت یافته، ایمان آورده‌ایم.»'),
(1030, 7, 76, 'مستکبران گفتند: «[ولی] ما به آنچه شما به آن ایمان آورده‌اید، کافریم!»'),
(1031, 7, 77, 'سپس [ناقه] را پی کردند، و از فرمان پروردگارشان سرپیچیدند و گفتند: «ای صالح! اگر تو از فرستادگان [خدا] هستی، آنچه ما را با آن تهدید می‌کنی، بیاور!»'),
(1032, 7, 78, 'سرانجام، زمین‌لرزه آنها را فراگرفت، و صبحگاهان، [تنها] جسم بی‌جانشان در خانه‌هایشان باقی مانده بود!'),
(1033, 7, 79, '[صالح] از آنها روی برتافت و گفت: «ای قوم! من رسالت پروردگارم را به شما ابلاغ کردم و شرط خیرخواهی را انجام دادم؛ ولی [چه کنم که] شما خیرخواهان را دوست ندارید؟!»'),
(1034, 7, 80, 'و [به خاطر بیاورید] لوط را، هنگامی که به قوم خود گفت: «آیا عمل شنیعی انجام می‌دهید که احدی از جهانیان پیش از شما انجام نداده است؟!'),
(1035, 7, 81, 'آیا شما از روی شهوت، به سراغ مردان می‌روید، به جای زنان؟ شما جمعیت [منحرف و] تجاوزکاری هستید!»'),
(1036, 7, 82, 'ولی پاسخ قومش چیزی جز این نبود که گفتند: «اینها را از شهر و آبادی خود بیرون کنید که اینها مردمی هستند که پاکدامنی را می‌طلبند [و با ما هم‌صدا نیستند]!»'),
(1037, 7, 83, '[چون کار به اینجا رسید،] ما، او و خاندانش را رهایی بخشیدیم، جز همسرش که با بازماندگان [در شهر] بود.'),
(1038, 7, 84, 'و [سپس چنان] بارانی [از سنگ] بر آنها فرستادیم [که آنها را نابود ساخت]، اکنون بنگر سرانجام کار مجرمان به کجا کشید.'),
(1039, 7, 85, 'و به سوی مَدین، برادرشان شعیب را فرستادیم. گفت: «ای قوم من! خدا را بپرستید که جز او معبودی ندارید. دلیل روشنی از طرف پروردگارتان برای شما آمده است، بنابر این، حق پیمانه و وزن را ادا کنید و از اموال مردم چیزی نکاهید، و در روی زمین، بعد از آن که [در پرتو ایمان و دعوت انبیاء] اصلاح شده است، فساد نکنید. این برای شما بهتر است، اگر با ایمان هستید.'),
(1040, 7, 86, 'و بر سر هر راه ننشینید که [مردم با ایمان را] تهدید کنید و مؤمنان را از راه خدا باز دارید و [با القای شبهات،] آن را کج و ناراست نشان دهید. و به خاطر بیاورید زمانی را که افراد کمی بودید و او شما را فزونی داد، و بنگرید سرانجام مفسدان چگونه بود؟'),
(1041, 7, 87, 'و اگر طایفه‌ای از شما به آنچه من به آن فرستاده شده‌ام، ایمان آورده‌اند، و طایفه‌ای ایمان نیاورده‌اند، صبر کنید تا خداوند میان ما داوری کند، که او بهترینِ داوران است.»'),
(1042, 7, 88, 'اشراف متکبّر از قوم او گفتند: «ای شعیب! سوگند یاد می‌کنیم که تو و کسانی را که به تو ایمان آورده‌اند، از آبادی خود بیرون خواهیم کرد، مگر آنکه به آیین ما بازگردید.» گفت: «آیا اگر چه مایل نباشیم؟!'),
(1043, 7, 89, 'اگر ما به آیین شما بازگردیم، بعد از آن که خدا ما را از آن نجات بخشیده، به خدا دروغ بسته‌ایم، و شایسته نیست که ما به آن بازگردیم، مگر این که خدایی که پروردگار ماست، بخواهد. علم پروردگار ما به همه چیز احاطه دارد، تنها بر خدا توکل کرده‌ایم. پروردگارا! میان ما و قوم ما به حق داوری کن که تو بهترینِ داورانی.»'),
(1044, 7, 90, 'اشراف قوم او که کافر شده بودند، گفتند: «اگر از شعیب پیروی کنید، زیان خواهید کرد.»'),
(1045, 7, 91, 'پس زمین‌لرزه آنها را فراگرفت، و صبحگاهان به صورت اجساد بی‌جانی در خانه‌هاشان مانده بودند.'),
(1046, 7, 92, 'آنها که شعیب را تکذیب کردند، [چنان نابود شدند که] گویا هرگز در آن [خانه‌ها] سکنی نداشتند. آنها که شعیب را تکذیب کردند، خود زیانکار بودند.'),
(1047, 7, 93, 'پس [شعیب] از آنها روی برتافت و گفت: «ای قوم من! من پیام‌های پروردگارم را به شما ابلاغ کردم و خیرخواهی نمودم. با این حال، چگونه بر حال قوم بی‌ایمان تأسف بخورم؟!»'),
(1048, 7, 94, 'ما در هیچ شهر و آبادی، پیامبری نفرستادیم، مگر این که اهل آن را به ناراحتی‌ها و خسارت‌ها گرفتار ساختیم، شاید [به خود آیند و به سوی خدا] بازگردند.'),
(1049, 7, 95, 'سپس [هنگامی که این هشدارها در آنها اثر نگذاشت،] نیکی [و فراوانیِ نعمت و رفاه] به جای بدی [و ناراحتی و گرفتاری] قرار دادیم، آن چنان که فزونی گرفتند [و مغرور شدند] و گفتند: «به پدران ما نیز تنگدستی و توانگری رسید.» چون چنین شد، آنها را ناگهان به اعمالشان گرفتیم.'),
(1050, 7, 96, 'و اگر ساکنان که در شهرها و آبادی‌ها، ایمان بیاورند و تقوا پیشه کنند، برکات آسمان و زمین را بر آنها می‌گشاییم؛ ولی [آنها حقایق را] تکذیب کردند، ما هم آنان را به کیفر اعمالشان مجازات کردیم.'),
(1051, 7, 97, 'آیا اهل این آبادی‌ها از این ایمن هستند که عذاب ما شبانه به سراغ آنها بیاید، در حالی که در خواب باشند؟'),
(1052, 7, 98, 'آیا اهل آبادی‌ها از این ایمنند که عذاب ما، روز به سراغشان بیاید، در حالی که سرگرم بازی هستند؟'),
(1053, 7, 99, 'آیا آنها از مکر الهی غافلند؟ در حالی که جز زیانکاران، خود را از مکر [و مجازات] خدا ایمن نمی‌دانند.'),
(1054, 7, 100, 'آیا کسانی که وارث زمین بعد از صاحبان قبلی آن می‌شوند، عبرت نمی‌گیرند؟ که اگر بخواهیم آنها را نیز به گناهانشان هلاک می‌کنیم و بر دل‌هایشان مهر می‌نهیم تا [صدای حق را] نشنوند.'),
(1055, 7, 101, 'اینها آبادی‌هایی است که اخبار آن را برای تو شرح می‌دهیم. آنها [چنان لجوج بودند که] به آنچه قبلاً تکذیب کرده بودند، ایمان نمی‌آوردند. این چنین خداوند بر دل‌های کافران مهر می‌نهد [و بر اثر لجاجت و ادامه گناه، حس تشخیص را از آنها می‌گیرد].'),
(1056, 7, 102, 'و بیشتر آنها را بر سر پیمان خود ندیدیم. و بیشتر آنها را فاسق و گنهکار یافتیم.'),
(1057, 7, 103, 'سپس به دنبال آنها، موسی را با آیات خویش به سوی فرعون و اطرافیان او فرستادیم. اما آنها [با نپذیرفتنِ این آیات] بر آن ظلم کردند. ببین عاقبت مفسدان به کجا کشید؟'),
(1058, 7, 104, 'و موسی گفت: «ای فرعون! من فرستاده‌ای از پروردگار جهانیانم.'),
(1059, 7, 105, 'سزاوار است که بر خدا جز حق نگویم. من دلیل روشنی از پروردگارتان برای شما آورده‌ام. بنابراین بنی‌اسرائیل را با من بفرست.»'),
(1060, 7, 106, '[فرعون] گفت: «اگر نشانه‌ای آورده‌ای، ارائه بده، اگر از راستگویانی.»'),
(1061, 7, 107, '[موسی] ناگهان عصای خود را افکند و اژدهای آشکاری شد!'),
(1062, 7, 108, 'و دست خود را [از گریبان] بیرون آورد، سفید [و درخشان] برای بینندگان بود!'),
(1063, 7, 109, 'اطرافیان فرعون گفتند: «بدون شک این ساحر دانایی است!'),
(1064, 7, 110, 'می‌خواهد شما را از سرزمینتان بیرون کند، نظر شما چیست؟ [در برابر او چه باید کرد؟]»'),
(1065, 7, 111, '[سپس به فرعون] گفتند: «[کار] او و برادرش را به تأخیر انداز و گرد‌آورندگان را به همه‌ی شهرها بفرست.'),
(1066, 7, 112, 'تا هر ساحر دانا [و کار آزموده]ای را به خدمت تو بیاورند.»'),
(1067, 7, 113, 'ساحران نزد فرعون آمدند و گفتند: «آیا اگر ما پیروز گردیم، اجر و پاداش مهمی خواهیم داشت؟»'),
(1068, 7, 114, 'گفت: «آری، و شما از مقربان خواهید بود!»'),
(1069, 7, 115, '[ساحران] گفتند: «ای موسی! یا تو بیافکن یا ما می‌افکنیم!»'),
(1070, 7, 116, 'گفت: «شما بیافکنید!» هنگامی که [وسایل سحر خود را] افکندند، مردم را چشم‌بندی کردند و ترسانیدند و سحر عظیمی به وجود آوردند!'),
(1071, 7, 117, 'و به موسی وحی کردیم که عصای خود را بیفکن. ناگهان [به صورت مار عظیمی درآمد و] وسایل دروغین آنها را به سرعت برگرفت!'),
(1072, 7, 118, '[در این هنگام] حق آشکار گردید، و آنچه آنها ساخته بودند، باطل شد.'),
(1073, 7, 119, 'در آنجا شکست خوردند و خوار وکوچک گشتند.'),
(1074, 7, 120, 'و ساحران، [همگی] به سجده افتادند.'),
(1075, 7, 121, 'و گفتند: «ما به پروردگارِ جهانیان ایمان آوردیم.»'),
(1076, 7, 122, '«پروردگارِ موسی و هارون.»'),
(1077, 7, 123, 'فرعون گفت: «آیا به او ایمان آوردید، پیش از آن که من به شما اجازه دهم؟ بی‌گمان این توطئه‌ای است که شما در این شهر [و دیار] چیده‌اید تا اهلش را از آن بیرون کنید؛ ولی به زودی خواهید دانست!'),
(1078, 7, 124, 'سوگند می‌خورم که دست‌ها و پاهای شما را به طور مخالف قطع می‌کنم، سپس همگی شما را به دار می‌آویزم.»'),
(1079, 7, 125, '[ساحران] گفتند: «[مهم نیست!] ما به سوی پروردگارمان بازمی‌گردیم.'),
(1080, 7, 126, 'تنها ایراد تو، به ما این است که ما به آیات پروردگار خویش، هنگامی که برای ما آمد، ایمان آورده‌ایم. بار الها! پیمانه‌ی صبر [و استقامت] را تا آخر بر ما بریز، و ما را مسلمان بمیران [و تا پایان عمر با اخلاص و ایمان بدار]!»'),
(1081, 7, 127, 'اشراف قوم فرعون [به او] گفتند: «آیا موسی و قومش را رها می‌کنی، که در زمین فساد کنند و تو و خدایانت را رها سازند؟» گفت: «به زودی پسرانشان را می‌کشیم و دخترانشان را زنده نگه‌می‌داریم [تا خدمت ما کنند]، و ما بر آنها کاملاً مسلط هستیم.»'),
(1082, 7, 128, 'موسی به قوم خود گفت: «از خدا یاری جویید و استقامت پیشه کنید که زمین از آن خداست، و آن را به هر کس بخواهد واگذار می‌کند. و سرانجامِ [نیک] برای پرهیزکاران است.»'),
(1083, 7, 129, 'گفتند: «پیش از آن که به سوی ما بیایی آزار دیدیم، و پس از آمدنت نیز آزار می‌بینیم،» گفت: «امید است پروردگارتان دشمن شما را هلاک کند و شما را در زمین، جانشین [آنها] سازد، و بنگرد چگونه عمل می‌کنید؟»'),
(1084, 7, 130, 'و ما فرعونیان را به خشکسالی و کمبود میوه‌ها گرفتار کردیم، شاید متذکّر گردند.'),
(1085, 7, 131, 'اما [آنها نه تنها پند نگرفتند، بلکه] هنگامی که نیکی [و نعمت] به آنها می‌رسید، می‌گفتند: «به خاطر خود ماست!» ولی موقعی که بدی [و بلا] به آنها می‌رسید، می‌گفتند: «از شومی موسی و کسان اوست!» آگاه باشید! همانا سرنوشت نیک و بدشان نزد خداست. اما بیشتر آنها نمی‌دانند.'),
(1086, 7, 132, 'و گفتند: «هر زمان، نشانه‌ای برای ما بیاوری که سحرمان کنی، ما به تو ایمان نمی‌آوریم!»'),
(1087, 7, 133, 'سپس [بلاها را پشت سر هم بر آنها نازل کردیم،] طوفان و ملخ و آفت گیاهی و قورباغه‌ها و خون را که نشانه‌هایی از هم جدا بودند، بر آنها فرستادیم، [ولی باز بیدار نشدند] و تکبر ورزیدند و جمعیت گنهکاری بودند.'),
(1088, 7, 134, 'هنگامی که بلا بر آنها مسلط می‌شد، می‌گفتند: «ای موسی! از خدایت برای ما بخواه به عهدی که با تو کرده، رفتار کند. اگر این بلا را از ما رفع کنی، قطعاً به تو ایمان می‌آوریم و بنی‌اسرائیل را با تو خواهیم فرستاد.»'),
(1089, 7, 135, 'اما هنگامی که بلا را پس از مدت معینی که به آن می‌رسیدند، از آنها برمی‌داشتیم، پیمان خویش را می‌شکستند!'),
(1090, 7, 136, 'سرانجام ما از آنها انتقام گرفتیم و آنها را در دریا غرق کردیم؛ زیرا آیات ما را تکذیب کردند و از آن غافل بودند.'),
(1091, 7, 137, 'و مشرق‌ها و مغرب‌های پر برکت زمین را به آن قوم تضعیف شده [و ستم‌دیده] واگذار کردیم، و وعده‌ی نیک پروردگارت بر بنی‌اسرائیل، به خاطر صبر و استقامتی که نشان دادند، تحقق یافت. و آنچه فرعونیان [از کاخ‌های مجلل] می‌ساختند، و آنچه از باغ‌های داربست‌دار فراهم ساخته بودند، در هم کوبیدیم!'),
(1092, 7, 138, 'و بنی‌اسرائیل را از دریا عبور دادیم. آنها در مسیر خود به جمعیتی رسیدند که اطراف بت‌هایشان با تواضع و خضوع گرد آمده بودند. [بنی‌اسرائیل] به موسی گفتند: «تو هم برای ما معبودی قرار ده! همان‌گونه که آنها معبودان [و خدایانی] دارند!» گفت: «شما جمعیتی جاهل و نادان هستید!'),
(1093, 7, 139, 'اینها سرانجام کارشان نابودی است، و آنچه انجام می‌دهند، باطل است.»'),
(1094, 7, 140, '[سپس] گفت: «آیا غیر از خداوند معبودی برای شما طلب کنیم؟ خدایی که شما را بر جهانیان برتری داد.»'),
(1095, 7, 141, '[به خاطر بیاورید] زمانی که از [چنگال] فرعونیان نجاتتان بخشیدیم. آنها که مرتباً شما را شکنجه می‌دادند، پسرانتان را می‌کشتند و زنانتان را [برای خدمتکاری] زنده می‌گذاشتند، و در این، آزمایش بزرگی از ناحیه خدا برای شما بود.'),
(1096, 7, 142, 'و ما با موسی، سی شب وعده گذاردیم؛ سپس آن را با ده شب تکمیل نمودیم. به این ترتیب میعاد پروردگارش چهل شب تمام شد. و موسی به برادرش هارون گفت: «جانشین من میان قوم من باش! و [آنها را] اصلاح‌ کن و از روش مفسدان پیروی منما!»'),
(1097, 7, 143, 'و هنگامی که موسی به میعادگاه ما آمد و پروردگارش با او سخن گفت، عرض کرد: «پروردگارا! خودت را به من نشان ده، تا تو را ببینم!» گفت: هرگز مرا نخواهی دید؛ ولی به کوه بنگر! اگر در جای خود ثابت ماند، مرا خواهی دید؛ اما هنگامی که پروردگارش بر کوه جلوه کرد، آن را همسان زمین قرار داد. و موسی مدهوش به زمین افتاد. موقعی که به هوش آمد عرض کرد: «خداوندا! منزهی تو [از اینکه قابل مشاهده باشی]! من به سوی تو بازگشتم، و من [در میان قوم] نخستینِ مؤمنانم.»'),
(1098, 7, 144, '[خداوند] گفت: «ای موسی! من تو را بر مردم، با رسالت‌های خویش و با سخن گفتنم [با تو]، برگزیدم. پس آنچه را به تو داده‌ام، بگیر و از شکرگزاران باش.»'),
(1099, 7, 145, 'و برای او در اَلواح، اندرزی از هر موضوعی نوشتیم، و بیانی از هر چیز کردیم. [و به او گفتیم:] «پس آن را با جدیت بگیر و به قوم خود بگو به نیکوترینِ آنها عمل کنند [و آنها که به مخالفت برخیزند، کیفرشان دوزخ است]، و به زودی [این] جایگاه فاسقان را به شما نشان خواهیم داد.»'),
(1100, 7, 146, 'به زودی کسانی را که در زمین به ناحق تکبر می‌ورزند، از ایمان به آیات خود منصرف می‌سازیم؛ [به طوری که] اگر هر آیه و نشانه‌ای را ببینند، به آن ایمان نمی‌آورند. و اگر راه هدایت را ببینند، آن را راه خود انتخاب نمی‌کنند. و اگر طریق گمراهی را ببینند، راه خود انتخاب می‌کنند. [همه اینها] به خاطر آن است که آیات ما را تکذیب کردند و از آن غافل بودند.'),
(1101, 7, 147, 'و کسانی که آیات ما و لقاء رستاخیز را تکذیب [و انکار] کنند، اعمالشان نابود می‌گردد. آیا جز آنچه را عمل کرده‌اند، پاداش داده می‌شوند؟'),
(1102, 7, 148, 'قوم موسی بعد از [رفتن] او [به میعادگاه خداوند]، از زیورآلات خود گوساله‌ای ساختند. جسد بی‌روحی بود که صدای گاو داشت! آیا آنها نمی‌دیدند که با آنان سخن نمی‌گوید و به راه [راست] هدایتشان نمی‌کند؟ آن را [خدای خود] انتخاب کردند و ظالم بودند.'),
(1103, 7, 149, 'و هنگامی که حقیقت به دستشان افتاد و دیدند گمراه شده‌اند، گفتند: «اگر پروردگار ما، به ما رحم نکند و ما را نیامرزد، قطعاً از زیانکاران خواهیم بود.»'),
(1104, 7, 150, 'و هنگامی که موسی، خشمگین و اندوهناک، به سوی قوم خود بازگشت، گفت: «پس از من بد جانشینانی برایم بودید [و آیین مرا ضایع کردید]، آیا در فرمان پروردگارتان [و تمدید مدت میعاد او، در قضاوت،] عجله نمودید؟!» سپس الواح را افکند و سر برادر خود را گرفت [و با عصبانیت] به سوی خود کشید. او گفت: «فرزند مادرم! این گروه مرا در فشار گذاردند و نزدیک بود مرا به قتل برسانند. بنابر این کاری نکن که دشمنان مرا شماتت کنند، و مرا با گروه ستمکاران قرار مده.»'),
(1105, 7, 151, '[موسی] گفت: «پروردگارا! من و برادرم را بیامرز و ما را در رحمت خود داخل کن، و تو مهربانترینِ مهربانانی!»'),
(1106, 7, 152, 'آنها که گوساله را [معبود خود] انتخاب کردند، به زودی خشم پروردگار و ذلت در زندگی دنیا به آنها می‌رسد، و این چنین کسانی را که [بر خدا] افترا می‌بندند، کیفر می‌دهیم.'),
(1107, 7, 153, 'و آنها که گناه کنند و بعد از آن توبه نمایند و ایمان آورند، [امید عفو او را دارند، زیرا] پروردگار تو در پی آن آمرزنده و مهربان است.'),
(1108, 7, 154, 'و هنگامی که خشم موسی فرو نشست، الواح [تورات] را برگرفت. و در نوشته‌های آن هدایت و رحمت بود برای آنها که از پروردگار خویش می‌ترسند [و از مخالفت فرمانش بیم دارند].'),
(1109, 7, 155, 'و موسی از قوم خود هفتاد تن از مردان را برای میعادگاه ما برگزید. هنگامی که زمین‌لرزه آنها را فرا گرفت [و هلاک شدند]، گفت: پروردگارا! اگر می‌خواستی، می‌توانستی آنها و مرا پیش از این نیز هلاک کنی. آیا ما را به آنچه سفیهانمان انجام داده‌اند، [مجازات و] هلاک می‌سازی؟ این، جز آزمایش تو، چیز دیگری نیست، که هر کس را بخواهی [و سزاوار بدانی]، گمراه می‌سازی، و هر کس را بخواهی [و شایسته ببینی]، هدایت می‌کنی. تو ولیّ مایی! ما را بیامرز و بر ما رحم کن! و تو بهترینِ آمرزندگانی.'),
(1110, 7, 156, 'و برای ما در این دنیا و سرای دیگر، نیکی مقرر دار! چه این که ما به سوی تو بازگشت کرده‌ایم.» [خداوند در برابر این تقاضا، به موسی] گفت: «مجازاتم را به هر کس بخواهم، می‌رسانم، و رحمتم همه چیز را فراگرفته و آن را برای آنها که پرهیزکارند و زکات را می‌پردازند و آنها که به آیات ما ایمان می‌آورند، مقرر خواهم داشت.»'),
(1111, 7, 157, 'آنها که از فرستاده [خدا]، پیامبر امّی پیروی می‌کنند، کسی که صفاتش را در تورات و انجیلی که نزدشان است، می‌یابند، و آنها را به معروف دستور می‌دهد و از منکر باز می‌دارد، پاکیزه‌ها را برای آنها حلال می‌شمرد، ناپاک‌ها را تحریم می‌کند، و بارهای سنگین و زنجیرهایی را که بر آنها بود، [از دوش و گردنشان] بر می‌دارد. آنها که به او ایمان آوردند و حمایتش کردند و یاریش نمودند و از نوری که با او نازل شده، پیروی کردند، آنان رستگارانند.'),
(1112, 7, 158, 'بگو: «ای مردم! من فرستاده خدا به سوی همه شما هستم. همان خدایی که حکومت آسمان‌ها و زمین از آن اوست. معبودی جز او نیست. زنده می‌کند و می‌میراند. پس ایمان بیاورید به خدا و فرستاده‌اش! آن پیامبر درس ناخوانده‌ای که ایمان به خدا و کلماتش دارد و از او پیروی کنید، تا هدایت یابید.»'),
(1113, 7, 159, 'و از قوم موسی، گروهی به سوی حق هدایت می‌کنند و حاکم به حق و عدالتند.'),
(1114, 7, 160, 'و آنها را به دوازده گروه که هر یک شاخه‌ای [از دودمان یعقوب] بودند، تقسیم کردیم. و هنگامی که موسی تقاضای آب [برای قوم تشنه‌کامش در بیابان] کرد، به او وحی فرستادیم که عصای خود را بر سنگ بزن! ناگهان دوازده چشمه از آن بیرون ریخت؛ آن چنان که هر گروه، چشمه خود را می‌شناختند. و ابر را بر سر آنها سایبان ساختیم و بر آنها مَنّ و سَلوی فرستادیم [و به آنان گفتیم:] از روزی‌های پاکیزه که به شما داده‌ایم، بخورید [و سپاس خدا را به جا آورید؛ ولی آنها نافرمانی و ستم کردند]؛ اما به ما ستم نکردند، به خودشان ستم نمودند.'),
(1115, 7, 161, 'و [به خاطر بیاورید] هنگامی را که به آنها گفته شد: «در این قریه [بیت‌المقدّس] ساکن شوید و از هر جا [و به هر کیفیت] بخواهید، از آن بخورید [و بهره گیرید] و بگویید: خداوندا! گناهان ما را بریز! و از در [بیت‌المقدّس] با تواضع وارد شوید.» اگر چنین کنید گناهان شما را می‌بخشیم و نیکوکاران را پاداش بیشتر خواهیم داد.'),
(1116, 7, 162, 'اما کسانی از آنها که ظلم و ستم [بر خویشتن] کردند، این سخن [و آن برنامه‌ها] را دگرگون ساختند و غیر از آنچه به آنها گفته شده بود، انجام دادند؛ لذا به خاطر ستمی که روا داشتند، بلایی از آسمان بر آنها فرستادیم [و مجازاتشان کردیم].'),
(1117, 7, 163, 'و از آنها درباره‌ی [سرگذشت] شهری که در ساحل دریا بود، سؤال کن. و [به خاطر بیاور] هنگامی را که آنها در روز شنبه، [در برابر قانون خدا] تجاوز [و طغیان] می‌کردند. همان هنگام که ماهیانشان روز شنبه [که روز تعطیل و استراحتشان بود،] آشکار می‌شدند؛ اما در غیر روز شنبه به سراغ آنها نمی‌آمدند. این‌گونه آنها را به چیزی آزمایش کردیم که در برابر آن نافرمانی می‌کردند.'),
(1118, 7, 164, 'و هنگامی که گروهی از آنها گفتند: «چرا جمعی [گنهکار] را اندرز می‌دهید که سرانجام خدا آنها را هلاک خواهد کرد و یا به عذاب شدیدی گرفتار خواهد ساخت [آنها را به حال خود واگذارید تا نابود شوند].» گفتند: این اندرزها] برای آن است که عذری به پیشگاه پروردگارتان داشته باشیم [که وظیفه امر به معروف و نهی از منکر را انجام دادیم]. به علاوه، شاید آنها [هشدارهای ما را بپذیرند و از گناه بازایستند و] تقوا پیشه کنند.»'),
(1119, 7, 165, 'اما چون تذکراتی را که به آنها داده شده بود، فراموش کردند، نهی‌کنندگانِ از بدی را رهایی بخشیدیم. و آنها را که ستم کردند، به خاطر نافرمانی‌شان به عذاب شدیدی گرفتار ساختیم.'),
(1120, 7, 166, 'هنگامی که در برابر فرمانی که به آنها داده شده بود، سرکشی کردند، به آنها گفتیم: «به شکل میمون‌ها در آیید و طرد شوید.»'),
(1121, 7, 167, 'و [نیز به یادآور] هنگامی که پروردگارت اعلام کرد: تا روز قیامت کسی را بر آنها مسلط خواهد ساخت که آنها را پیوسته در عذاب سختی قرار دهد؛ زیرا پروردگارت مجازاتش سریع و [در مقابل توبه‌کاران] آمرزنده و مهربان است.'),
(1122, 7, 168, 'و آنها را در زمین به صورت گروه‌هایی پراکنده ساختیم. گروهی از آنها صالح و گروهی غیر آن هستند و آنها را به نیکی‌ها و بدی‌ها آزمودیم، شاید بازگردند.'),
(1123, 7, 169, 'بعد از آن، فرزندانی جانشین آنها شدند که وارث کتاب [تورات] گشتند. [با این حال] متاع این دنیای پست را گرفته، و می‌گفتند: «خداوند به زودی ما را می‌بخشد.» اما اگر متاع دیگری همانند آن به دستشان بیافتد، آن را می‌گیرند [و حکم خدا را پشت سر می‌افکنند]. آیا پیمان کتاب [خدا] از آنها گرفته نشده که بر خدا [دروغ نبندند و] جز حق نگویند، و آنان بارها آن را خوانده‌اند، و سرای آخرت برای آنها که پرهیزکاری پیشه کنند، بهتر است. آیا نمی‌فهمید؟!'),
(1124, 7, 170, 'و آنها که به کتاب [خدا] تمسک جویند و نماز را بر پا دارند، [پاداش بزرگی خواهند داشت؛ زیرا] ما پاداش مصلحان را ضایع نخواهیم کرد.'),
(1125, 7, 171, 'و [نیز به خاطر بیاور] هنگامی که کوه را بر فراز آنها همچون سایبانی بلند کردیم، آن چنان که گمان کردند بر آنها فرود خواهد آمد [و در این حال، از آنها پیمان گرفتیم و گفتیم:] «آنچه را به شما [از احکام و دستورات] داده‌ایم، با قدرت [و جدیت] بگیرید، و آنچه را در آن است، به یاد داشته باشید [و عمل کنید] تا پرهیزکار شوید.»'),
(1126, 7, 172, 'و [به یاد آور] زمانی که پروردگارت، از پشت بنی‌آدم، فرزندان و ذرّیه آنان را برگرفت و آنان را گواه بر خودشان ساخت [و فرمود:] «آیا من پروردگار شما نیستم؟» گفتند: «آری! ما گواهی دادیم [که تو پروردگار مایی]!» [این اقرار گرفتن برای آن بود] تا در روز قیامت نگویید: «ما از این، غافل بودیم.»'),
(1127, 7, 173, 'یا نگویید: «پدران ما پیش از ما مشرک بودند و ما نیز فرزندانی از پسِ آنان بودیم [و ناچار راهشان را ادامه دادیم]. آیا ما را به خاطر عملکرد اهل باطل، مجازات و هلاک می‌کنی؟»'),
(1128, 7, 174, 'و ما این‌گونه آیات خویش را روشن بیان می‌کنیم، و شاید آنان [به سوی خداوند و فطرت پاک توحیدی] بازگردند.'),
(1129, 7, 175, 'و بر آنان، داستانِ آن کس را بخوان که آیات خود را به او داده بودیم، پس او [ناسپاسانه] خود را از آن آیات، جدا ساخت، پس شیطان او را در پی خویش کشید تا از گمراهان شد.'),
(1130, 7, 176, 'و اگر می‌خواستیم، او را به وسیله‌ی آیات بالا می‌بردیم؛ ولی او به زمین چسبید و از هوس خود پیروی کرد. پس مَثَل او مَثَل سگ است، که اگر به آن حمله کنی، دهان باز کرده و پارس می‌کند و زبان بیرون می‌آورد، و اگر او را واگذاری، باز چنین می‌کند. این، مَثَل کسانی است که آیات ما را تکذیب کردند. پس این داستان را [برایشان] باز گو! باشد که بیندیشند.'),
(1131, 7, 177, 'چه بد مَثَلی دارند کسانی که آیات ما را تکذیب کردند. و آنان تنها به خودشان ستم می‌کردند.'),
(1132, 7, 178, 'هر که را خدا هدایت کند، تنها اوست هدایت یافته. و هر که را گمراه کند، پس آنان همان زیانکارانند.'),
(1133, 7, 179, 'و بسیاری از جنّ و انس را برای دوزخ آفریدیم، [و سرانجامشان به آنجا می‌کشد، چرا که] آنان دل‌هایی دارند که با آن حقّ را درک نمی‌کنند و چشمانی دارند که با آن نمی‌بینند و گوش‌هایی دارند که با آن نمی‌شنوند. آنان همچون چهارپایان، بلکه گمراه‌ترند. آنان همان غافلانند.'),
(1134, 7, 180, 'و نیکوترینِ نام‌ها برای خداوند است، پس خداوند را با آنها بخوانید. و کسانی را که در نام‌های خدا کژاندیشی می‌کنند [و آنها را بر غیر او می‌نهند]، رها کنید. آنان به زودی به کیفر آنچه می‌کردند، خواهند رسید.'),
(1135, 7, 181, 'و از کسانی که آفریده‌ایم، گروهی [دیگران را] به حقّ هدایت می‌کنند و به آن حکم می‌نمایند.'),
(1136, 7, 182, 'و کسانی که آیات ما را تکذیب کردند، به تدریج، آنان را از جایی که ندانند، گرفتار خواهیم کرد.'),
(1137, 7, 183, 'و به آنان مهلت می‌دهم [تا پیمانه پر شود]، همانا تدبیر من محکم و استوار است [و هیچ کس را قدرت فرار از آن نیست].'),
(1138, 7, 184, 'آیا آنان فکر نکردند که همنشین آنان، [پیامبر،] هیچ‌گونه جنون ندارد؟ او جز هشداردهنده‌ای آشکار نیست.'),
(1139, 7, 185, 'آیا در ملکوتِ آسمان‌ها و زمین و هر چه خدا آفریده، به دقّت نمی‌نگرند [تا بدانند آفرینش همه‌ی آنها هدفدار است، نه بیهوده]؟ و این که شاید زمان [مرگ] آنان نزدیک شده باشد؟ پس بعد از این [آیات روشن]، به کدام سخن، ایمان خواهند آورد؟'),
(1140, 7, 186, 'هر که را خداوند، [به خاطر فسق و اعمالش،] گمراه کند، برای او هیچ هدایتگری نیست، و آنان را در سرکشی و طغیانشان رها می‌کند تا سرگردان شوند.'),
(1141, 7, 187, 'از تو درباره‌ی قیامت می‌پرسند که چه وقت به پا می‌شود؟ بگو: «علم آن تنها نزد پروردگار من است، جز او کسی نمی‌تواند زمان آن را آشکار سازد. [فرا رسیدن قیامت،] در آسمان‌ها و زمین سنگین است. جز به صورت ناگهانی پیش نمی‌آید.» از تو چنان می‌پرسند که گویا از [زمان] آن آگاهی کامل داری! بگو: «علم آن تنها نزد خداست؛ ولی بیشتر مردم نمی‌دانند.»'),
(1142, 7, 188, 'بگو: «من مالک هیچ سود و زیانی برای خودم نیستم، مگر آنچه را خدا بخواهد، و اگر غیب می‌دانستم، منافع زیادی برای خودم فراهم می‌کردم و هرگز به من زیانی نمی‌رسید. من تنها هشداردهنده و بشارت‌دهنده‌ای هستم برای گروهی که ایمان می‌آورند.»'),
(1143, 7, 189, 'او خدایی است که شما را از یک نفس آفرید و همسرش را از [نوع] او قرار داد تا بدان آرام گیرد. پس چون با او بیامیخت، باری سبک برگرفت [و باردار شد] و [مدّتی] با آن سر کرد، چون زن، سنگین شد، از خداوند، پروردگارشان، درخواست کردند [و گفتند]: اگر فرزند شایسته‌ای به ما بدهی، قطعاً از سپاسگزاران خواهیم بود.»'),
(1144, 7, 190, 'پس چون [خداوند] به آن دو، فرزندی صالح داد، در آنچه به ایشان عطا نمود، برای خدا شریکانی قرار دادند، ولی خداوند از آنچه که آنان شریک او قرار می‌دهند، برتر است.'),
(1145, 7, 191, 'آیا چیزهایی را شریک خدا قرار می‌دهند که چیزی نمی‌آفرینند و خودشان نیز مخلوق‌اند؟!'),
(1146, 7, 192, 'و [این معبودها] قدرت یاری آنان را ندارند و حتّی خودشان را هم نمی‌توانند حمایت کنند.'),
(1147, 7, 193, 'و اگر معبودها را به هدایت فراخوانید، از شما پیروی نمی‌کنند. بر شما یکسان است که آنها را دعوت کنید یا ساکت باشید!'),
(1148, 7, 194, 'کسانی را که به جای خدا می‌خوانید [و می‌پرستید]، بندگانی [و آفریده‌هایی] همچون خود شمایند. پس اگر راست می‌گویید، آنان را بخوانید، تا آنان جوابتان را بدهند.'),
(1149, 7, 195, 'آیا آنان [که معبود خود برگزیده‌اید،] پاهایی دارند که با آن راه بروند؟ یا دست‌هایی دارند که با آن قدرت‌نمایی کنند؟ یا چشم‌هایی دارند که با آنها ببینند؟ یا گوش‌هایی دارند که با آنها بشنوند؟ [ای پیامبر!] بگو: «شریک‌های [خیالی] خودتان را بخوانید و علیه من نقشه بکشید و هیچ مهلت هم به من ندهید!.'),
(1150, 7, 196, 'ولیّ و سرپرست من، خدایی است که این کتاب آسمانی را نازل کرده است و او همه‌ی صالحان را سرپرستی [و هدایت] می‌کند.'),
(1151, 7, 197, '[بت‌ها] و کسانی که به جای خدا می‌خوانید [و می‌پرستید]، نه می‌توانند شما را یاری نمایند و نه خود را یاری می‌کنند.»'),
(1152, 7, 198, 'و اگر آنان را به هدایت فراخوانی، نمی‌شنوند و می‌بینی که آنان به تو نگاه می‌کنند و حال آن که نمی‌بینند.'),
(1153, 7, 199, 'عفو و میانه‌روی را پیشه کن. [عذر مردم را بپذیر و بر آنان آسان بگیر.] و به کارهای عقل‌پسند و نیکو فرمان بده و از جاهلان روی برگردان!'),
(1154, 7, 200, 'و اگر از طرف شیطان [و شیطان‌صفتان،] کمترین وسوسه و تحریک و سوءنیتی به تو رسید، پس به خداوند پناه آور، که قطعاً او شنوا و داناست.'),
(1155, 7, 201, 'کسانی که تقوا پیشه کرده‌اند، هرگاه وسوسه‌های شیطانی به آنان نزدیک شود، متوجّه می‌شوند [و خدا را یاد می‌کنند]، آن‌گاه بینا می‌گردند.'),
(1156, 7, 202, 'و برادرانشان، [شیطان‌صفتان گمراه،] آنان را در انحراف می‌کشند و نگه می‌دارند و هیچ کوتاهی نمی‌کنند.'),
(1157, 7, 203, 'و هرگاه آیه‌ای برای آنان نیاوری، [و چند روزی تلاوت وحی قطع شود،] می‌گویند: «چرا آیه‌ای برنگزیدی؟» بگو: «من فقط چیزی را پیروی می‌کنم که از سوی پروردگارم بر من وحی می‌شود. این [قرآن]، بصیرت‌هایی از سوی پروردگارتان است و برای کسانی که ایمان آورند، مایه‌ی هدایت و رحمت است.»'),
(1158, 7, 204, 'و هرگاه قرآن خوانده شود، به آن گوش دهید و ساکت شوید، باشد که مورد رحمت قرار گیرید.'),
(1159, 7, 205, 'و پروردگارت را از روی تضرّع و خوف، آهسته و آرام، در دل خود و در هر صبح و شام، یاد کن و از غافلان مباش.'),
(1160, 7, 206, 'همانا کسانی که مقرّبان درگاه پروردگارت هستند، از پرستش او سر باز نمی‌زنند، او را تسبیح می‌گویند و برای او سجده می‌کنند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '[ای پیامبر!] از تو درباره‌ی اَنفال [غنائم جنگی و اموال عمومی] می‌پرسند. بگو: «اَنفال، از آنِ خدا و پیامبر است. پس، از خداوند پروا کنید و [روابط] میان خودتان را اصلاح کنید و اگر ایمان دارید، از خدا و رسولش اطاعت کنید.»'),
(1162, 8, 2, 'مؤمنان، تنها کسانی هستند که هرگاه نام خدا برده شود، دل‌هایشان [از عظمت او] لرزان شود و هر گاه آیات خدا بر آنان تلاوت شود، ایمانشان را می‌افزاید و تنها بر پروردگارشان توکل می‌کنند.'),
(1163, 8, 3, 'آنان که نماز را بر پا می‌دارند و از آنچه به ایشان روزی داده‌ایم، انفاق می‌کنند.'),
(1164, 8, 4, 'آنان، همان مؤمنان حقیقی هستند. برای آنان نزد پروردگارشان درجات و آمرزش و روزیِ سخاوتمندانه و نیکوست.'),
(1165, 8, 5, '[ناخشنودی بعضی مسلمانان از تقسیم غنائم،] همانند آن [زمانی] است که پروردگارت تو را [برای جنگ بدر،] از خانه‌ات به حقّ خارج کرد، در حالی‌که گروهی از مؤمنان [از حضور در جنگ] ناخشنود بودند.'),
(1166, 8, 6, 'آنان درباره‌ی حقّ [بودنِ جنگ بدر]، پس از آن که روشن شده بود، با تو مجادله می‌کنند. [چنان که] گویی به سوی مرگ رانده می‌شوند و [نابودی خود را] می‌نگرند.'),
(1167, 8, 7, 'و [به یاد آورید] زمانی که خداوند شما را وعده می‌داد که یکی از دو گروه [کاروان تجارتی، یا لشکر دشمن،] از آنِ شما خواهد بود و شما دوست داشتید گروه غیر مسلّح، در اختیارتان قرار گیرد. در حالی که خداوند می‌خواهد حقّ را با کلمات [و سنّت‌های] خویش استوار ساخته، ریشه‌ی کفّار را برکند. [از این رو، شما را در بدر پیروز ساخت.]'),
(1168, 8, 8, 'تا حقّ را استوار و باطل را نابود سازد، هر چند مجرمان خوش نداشته باشند.'),
(1169, 8, 9, '[به یاد آورید] زمانی که [در جنگ بدر،] از پروردگارتان فریادرسی می‌طلبیدید، پس او دعا و خواسته‌ی شما را اجابت کرد [و فرمود:] «من یاری‌دهنده شما با فرستادن هزار فرشته پیاپی هستم.»'),
(1170, 8, 10, 'و خداوند، آن [نزول فرشتگان] را تنها برای بشارت و اینکه دل‌هایتان با آنان آرامش یابد، قرار داد و نصرت و پیروزی، فقط از سوی خداست. [پس به قدرت خود و امداد فرشتگان هم تکیه نکنید.] همانا خداوند، شکست‌ناپذیرِ حکیم است.'),
(1171, 8, 11, '[به یاد آورید] زمانی که [خداوند] از سوی خود برای آرامش شما، خوابی سبک بر شما مسلّط ساخت و از آسمان بارانی بر شما فرستاد تا شما را با آن پاک کند و پلیدی شیطان را از شما بزداید و دل‌های شما را محکم و گام‌هایتان را با آن استوار سازد.'),
(1172, 8, 12, '[به یاد آور] زمانی که پروردگارت به فرشتگان وحی کرد که من با شمایم، پس شما افراد با ایمان را تقویت کنید، من نیز به زودی در دلهای کافران، رعب و ترس خواهم افکند. پس فراز گردن‌ها را بزنید و همه‌ی سرانگشتانشان را قطع کنید.'),
(1173, 8, 13, 'آن [فرمان]، به جهت آن بود که آنان در برابر خدا و پیامبرش گردن‌کشی کردند و هر کس با خدا و پیامبرش در افتد و مخالفت کند، خداوند دارای کیفری سخت است.'),
(1174, 8, 14, 'آن است [کیفر ما در دنیا]، پس بچشید آن را و [بدانید] عذاب دوزخ برای کافران [مهیا] است.'),
(1175, 8, 15, 'ای کسانی که ایمان آورده‌اید! هرگاه با انبوه کافران برخورد کردید، به آنان پشت نکنید و مگریزید.'),
(1176, 8, 16, 'و جز آنها که کناره‌گیری‌شان برای ساز و برگ نبرد مجدّد، یا پیوستن به گروه دیگری از مسلمانان باشد، هر کس در روز جنگ، پشت به دشمن کند، قطعاً مورد خشم خدا قرار می‌گیرد و جایگاه او جهنّم است و چه بد سرانجامی است!'),
(1177, 8, 17, 'شما کفّار را نکشتید؛ بلکه خداوند آنها را به قتل رساند. [ای پیامبر!] آن‌گاه که تیر افکندی، تو نیافکندی؛ بلکه خدا افکند [تا کافران را بترساند] و تا مؤمنان را از سوی خویش، به آزمونی نیکو بیازماید، زیرا خداوند بسیار شنوا و داناست.'),
(1178, 8, 18, 'آن، [لطف خدا بود.] و خدا، سست‌کننده‌ نیرنگ کافران است.'),
(1179, 8, 19, 'اگر [شما کافران!] در انتظار فتح و پیروزی [اسلام] بودید، که آن فتح آمد [و حقّانیت اسلام آشکار شد] و اگر دست بردارید، برایتان بهتر است و اگر [به کفر و عناد] برگردید، [ما هم به سوی قهر] برمی‌گردیم و هرگز گروه شما، هر چند بسیار باشد، به کارتان نخواهد آمد، چرا که خداوند با مؤمنان است.'),
(1180, 8, 20, 'ای کسانی که ایمان آورده‌اید! از خدا و فرستاده‌ی او اطاعت کنید و در حالی که [سخنان او را] می‌شنوید، از وی سرپیچی نکنید.'),
(1181, 8, 21, 'و مثل کسانی نباشید که گفتند: «شنیدیم!» ولی [در حقیقت] نمی‌شنیدند.'),
(1182, 8, 22, 'بدترینِ جنبندگان نزد خداوند، [کسانی هستند که نسبت به شنیدن حقّ] کر، [و نسبت به گفتن حقّ] لالند، آنها که خردورزی نمی‌کنند.'),
(1183, 8, 23, 'و اگر خداوند خیری در آنان می‌دانست، شنوایشان می‌ساخت، ولی اگر شنوایشان می‌ساخت، باز هم سرپیچی کرده، روی می‌گرداندند.'),
(1184, 8, 24, 'ای کسانی که ایمان آورده‌اید! هرگاه خدا و رسول، شما را به چیزی که حیات‌بخشِ شماست، دعوت می‌کنند، بپذیرید و بدانید خداوند، میان انسان و دل او حایل می‌شود و [بدانید که] به سوی او محشور می‌شوید.'),
(1185, 8, 25, 'و بترسید از فتنه‌ای که تنها دامن‌گیر ستمگرانِ شما نمی‌شود، [بلکه هر که را در مقابل ستمگران سکوت کند، فرامی‌گیرد] و بدانید که خداوند سخت‌کیفر است.'),
(1186, 8, 26, 'و به یاد آورید زمانی را که شما کم بودید و در زمین [مکه] مستضعف بودید. می‌ترسیدید مردم شما را بربایند. پس [خداوند در مدینه] پناهتان داد و با یاری خویش، شما را نیرومند کرد و از پاکیزه‌ها به شما روزی داد، باشد که شما شکرگزاری کنید.'),
(1187, 8, 27, 'ای کسانی که ایمان آورده‌اید! به خدا و پیامبر خیانت نکنید و آگاهانه به امانت‌های خود خیانت نورزید.'),
(1188, 8, 28, 'و بدانید که اموال و فرزندانتان آزمایشی [برای شما] هستند و البتّه نزد خداوند [برای کسانی که از عهده آزمایش برآیند]، پاداشی بزرگ است.'),
(1189, 8, 29, 'ای کسانی که ایمان آورده‌اید! اگر تقوای الهی پیشه کنید، خداوند برایتان فرقانی [که نیروی شناخت حقّ از باطل باشد،] قرار می‌دهد و بدی‌هایتان را می‌پوشاند و شما را می‌آمرزد و خداوند صاحب فضل و بخشش بزرگ است.'),
(1190, 8, 30, 'و [به یاد آور] زمانی که کافران درباره تو نقشه می‌کشیدند تا تو را زندانی کنند، یا تو را بکشند یا [از مکه] تبعید کنند و آنان نقشه می‌کشیدند و خدا هم تدبیر می‌کرد و خداوند، بهترینِ تدبیرکنندگان است.'),
(1191, 8, 31, 'و هرگاه آیات ما بر آنان تلاوت شود، می‌گویند: «شنیدیم، ما نیز اگر بخواهیم، قطعاً مانند این قرآن را بیان می‌کنیم. این، جز افسانه‌های پیشینیان نیست!»'),
(1192, 8, 32, 'و [به یاد آور] زمانی که مخالفان گفتند: «خدایا! اگر این [اسلام وقرآن] همان حقِ‌ّ از جانب توست، پس بر ما از آسمان سنگ‌هایی ببار یا عذابی دردناک برای ما بیاور!»'),
(1193, 8, 33, 'و[لی] تا تو در میان مردم هستی، خداوند بر آن نیست که آنان را عذاب کند و تا آنان استغفار می‌کنند، خداوند عذاب کننده آنان نیست.'),
(1194, 8, 34, 'و چرا خداوند آنها را عذاب نکند؟ با این که آنان [مردم را] از مسجدالحرام باز می‌دارند، در حالی که سرپرست آنجا نیستند. جز افراد پرهیزکار، دیگران حقّ تولیت و سرپرستی آن را ندارند؛ ولی بیشترشان نمی‌دانند.'),
(1195, 8, 35, '[دعا و] نمازشان نزد خانه [کعبه]، جز سوت‌کشیدن و کف‌زدن نبود. پس به خاطر آن که کفر می‌ورزیدید، عذاب [الهی را] بچشید.'),
(1196, 8, 36, 'آنان که کافر شدند، اموال خود را صرف می‌کنند تا از راه خدا جلوگیری کنند. در آینده نیز این گونه خرج‌ها را خواهند داشت، سپس [اموال هزینه شده] مایه‌ی حسرت آنان می‌گردد و آن‌گاه شکست می‌خورند و آنان که کافر شدند، به سوی دوزخ محشور خواهند شد.'),
(1197, 8, 37, '[این حسرت، شکست و عذاب،] برای آن است که خداوند، ناپاک را از پاک جدا کند و پلیدی‌ها را بر هم نهد و همه را متراکم سازد و در دوزخ قرار دهد، آنان همان زیانکارانند.'),
(1198, 8, 38, '[ای پیامبر!] به کافران بگو اگر [از مخالفت] دست بردارند، گذشته‌ی آنان بخشوده می‌شود و اگر [به روش سابق خود] برگردند، مسلماً سنّت الهی در برخورد با گذشتگان، [درباره‌ی آنان نیز] جاری است.'),
(1199, 8, 39, 'و با آنان بجنگید تا فتنه‌ای [از کفر و شرک] بر جای نماند و دین، یکسره برای خدا باشد. پس اگر [از کفر خود] دست کشیدند، خداوند به آنچه انجام می‌دهند، بیناست.'),
(1200, 8, 40, 'و اگر [باز هم] سرپیچی کنند و روی بگردانند، بدانید که خداوند، مولا و سرپرست شماست. چه خوش مولایی و چه نیکو یاوری است!'),
(1201, 8, 41, 'و اگر به خداوند و آنچه بر بنده‌ی خود، روز جدایی [حقّ از باطل]، روز درگیریِ دو گروه [با ایمان و بی‌ایمان در جنگ بدر،] نازل کردیم، ایمان دارید، بدانید که هرچه غنیمت گرفتید، خمس آن برای خداوند و رسول‌خدا و خویشاوندان [او، اهل‌بیت] و یتیمان و بینوایان و در راه‌ماندگان [از سادات] است و خداوند بر هر چیزی تواناست.'),
(1202, 8, 42, 'آن‌گاه که شما در کناره‌ی نزدیک‌تر [به مدینه] بودید و [آنها که همراه ابوجهل از مکه آمده بودند،] در کناره دورتر [از مدینه] و کاروان [تجاری قریش به سرپرستی ابوسفیان] از شما فروتر [و دورتر که توانست بگریزد.] و اگر با هم وعده [جنگ] می‌گذاشتید، در مورد مکان وعده‌گاه خلاف می‌کردید، [زیرا می‌دانستید در چنین موقعیتی شما شکست می‌خورید] ولی [شما در برابر عمل انجام شده قرار گرفتید] تا خدا کاری را که می‌خواست، تحقّق بخشد، هر کس هلاک می‌شود، از روی حجّت و آگاهی هلاک شود و هر کس [به هدایت] حیات یافت، از روی دلیل، حیات یابد و خداوند شنوا و داناست.'),
(1203, 8, 43, '[ای پیامبر! به یاد آور] زمانی که خداوند، عدد آنان را در خوابت، به تو اندک نشان داد و اگر [خداوند] آنان را زیاد نشان می‌داد، حتماً سست می‌شدید و قطعاً در کار [جنگ] نزاع می‌کردید؛ ولی او [شما را از این سستی و نزاع] سالم نگاه داشت. خداوند به آنچه در سینه‌هاست، آگاه است.'),
(1204, 8, 44, 'و [به یاد آورید] زمانی که چون با دشمن برخورد کردید، خداوند دشمنان را در نظر شما کم جلوه داد [تا با جرأت حمله کنید] و شما را نیز در دیدگاه آنان اندک نشان داد [تا از کفّار مکه، کمک نخواهند]. این کار برای آن بود که خداوند کاری را که می‌بایست انجام شود، تحقّق بخشد و همه‌ی کارها به خدا باز می‌گردد.'),
(1205, 8, 45, 'ای کسانی که ایمان آورده‌اید! هرگاه با گروهی [از دشمن] روبرو شدید، ثابت‌قدم باشید و خدا را بسیار یاد کنید، تا رستگار شوید.'),
(1206, 8, 46, 'و از خداوند و فرستاده‌اش اطاعت کنید و با یکدیگر نزاع نکنید که سست می‌شوید و مهابت و قوّتتان از بین می‌رود و صبر کنید. خداوند با صابران است.'),
(1207, 8, 47, 'و مثل کسانی نباشید که از روی سرمستی و خودنمایی و غرور و ریا در برابر مردم، از خانه‌های خود [به سوی جبهه] خارج شدند و مردم را از راه خدا بازمی‌داشتند و حال آن که خداوند، به آنچه انجام می‌دهند، احاطه دارد.'),
(1208, 8, 48, 'و [به یادآور] زمانی که شیطان، کارهای مشرکان را در نظرشان زیبا جلوه داد و گفت: «امروز هیچ کس از مردم، بر شما غالب نخواهد شد و من نیز در کنار شما و پناهِ شمایم.» امّا همین‌ که دو لشکر با هم درآویختند، شیطان به عقب برگشت و گفت: «من از شما بیزارم. من چیزی را می‌بینم که شما نمی‌بینید. من از خدا بیم دارم و خداوند سخت‌کیفر است.»'),
(1209, 8, 49, '[به یاد آور] زمانی که منافقان و بیماردلان [درباره مسلمانان] می‌گفتند: «آنان را دینشان مغرور کرده است. [که بی‌پروا حمله می‌کنند.» غافل از آن که مسلمین بر خدا توکل نموده‌اند.] و کسی که بر خدا توکل کند، پس قطعاً خداوند شکست‌ناپذیرِ حکیم است.'),
(1210, 8, 50, 'و [ای پیامبر!] اگر ببینی آن هنگام که فرشتگان، جان کافران را می‌گیرند؛ بر چهره‌ها و پشت‌های آنان می‌زنند [و می‌گویند:] «بچشید عذاب سوزان را!»'),
(1211, 8, 51, 'این [کیفر]، دستاورد کارهای پیشین شماست و شکی نیست که خداوند بر بندگانش ستمگر نیست.'),
(1212, 8, 52, '[ای پیامبر! روش کافران زمان تو،] مانند روش فرعونیان و کفّار پیش از آنان است که به آیات خداوند کفر ورزیدند. خداوند نیز به کیفر گناهانشان، آنان را گرفتار کرد. قطعاً خداوند، نیرومندِ سخت‌کیفر است.'),
(1213, 8, 53, 'آن [کیفر] بدین سبب است که خداوند، نعمتی را که به قومی عطا کرده، تغییر نمی‌دهد، مگر آن که آنان، آنچه را در خودشان است، تغییر دهند و خداوند، بسیار شنوا و داناست.'),
(1214, 8, 54, '[ای پیامبر! روحیه‌ی مردمِ زمان تو،] همچون خوی و روش فرعونیان و کسانی است که پیش از آنان بودند. آیات پروردگارشان را تکذیب کردند. ما نیز به سزای گناهانشان، هلاکشان ساختیم و فرعونیان را غرق کردیم و همگی ستمگر بودند.'),
(1215, 8, 55, 'قطعاً بدترینِ جنبندگان نزد خداوند، آنانند که کافر شدند، پس آنان ایمان نمی‌آورند.'),
(1216, 8, 56, 'کسانی که از ایشان پیمان گرفته‌ای، سپس پیمان خود را هر بار می‌شکنند و اهل تقوا و پروا نیستند.'),
(1217, 8, 57, 'پس [ای پیامبر!] هرگاه در جنگ به آنان دست یافتی، [با برخوردهای قاطع،] افراد پشت جبهه‌ی آنان را به وحشت افکن و متفرّق ساز! باشد که پند گیرند [و دست از توطئه بردارند].'),
(1218, 8, 58, 'و اگر خوف آن داشتی که گروهی [در پیمان] خیانت کنند، تو نیز عهدشان را به سوی آنان بینداز [و به آنان اعلام کن که همانند خودشان عمل خواهی کرد]. خداوند خائنان را دوست ندارد.'),
(1219, 8, 59, 'و آنان که کفر ورزیده‌اند، گمان نکنند که پیش افتاده‌اند، زیرا آنها نمی‌توانند [ما را] ناتوان کنند.'),
(1220, 8, 60, 'و برای [آمادگیِ مقابله با] دشمنان، هر چه می‌توانید از نیرو و از اسبان سواری فراهم کنید، تا دشمن خدا و دشمن خودتان و نیز [دشمنانی] غیر از اینان، که شما آنان را نمی‌شناسید، ولی خداوند آنها را می‌شناسد، به وسیله‌ی آن بترسانید و در راه خدا هرچه انفاق کنید، پاداش کامل آن به شما می‌رسد و به شما ستم نخواهد شد.'),
(1221, 8, 61, 'و اگر [دشمنان] به صلح تمایل داشتند، [تو نیز] تمایل نشان بده و بر خداوند توکل کن. همانا او شنوا و داناست.'),
(1222, 8, 62, 'و اگر بخواهند با تو نیرنگ کنند، قطعاً خداوند تو را بس است. اوست که با امدادهای خویش و حمایت مؤمنان، تو را یاری کرده است.'),
(1223, 8, 63, 'و [خداوند] میان دل‌های مؤمنان الفت داد. اگر تو همه‌ی آنچه را در زمین است، خرج می‌کردی، نمی‌توانستی میان قلوبشان محبّت و الفت پدید آوری؛ ولی خداوند میان آنان پیوند داد، چرا که او شکست‌ناپذیر و حکیم است.'),
(1224, 8, 64, 'ای پیامبر! برای تو [حمایت] خداوند و پیروان با ایمانت کافی است [و نیازی به حمایت دیگران نداری].'),
(1225, 8, 65, 'ای پیامبر! مؤمنان را بر پیکار [با کفّار] تشویق کن. اگر بیست نفر از شما پایدار باشند، بر دویست نفر پیروز می‌شوند و اگر از شما صد نفر [مقاوم] باشند، بر هزار نفر از کافران غلبه می‌یابند، زیرا آنان گروهی هستند که [آثار ایمان را] نمی‌فهمند.'),
(1226, 8, 66, 'اکنون خداوند، [در امر جهاد] بر شما تخفیف داد و معلوم داشت که در شما ضعف راه یافته است. پس اگر صد نفر از شما پایدار باشند، بر دویست نفر پیروز می‌شوند و اگر از شما هزار نفر باشند، بر دو هزار نفر با خواست خداوند پیروز می‌شوند و خداوند با صابران است.'),
(1227, 8, 67, 'هیچ پیامبری حقِ‌ّ اسیر گرفتن ندارد، مگر بعد از آن که در آن سرزمین، غلبه کامل یابد. شما به فکر متاع زودگذر دنیا هستید، ولی خداوند [برای شما] آخرت را می‌خواهد و خداوند، شکست‌ناپذیر و حکیم است.'),
(1228, 8, 68, 'اگر حکم ازلیِ خداوند نبود [که بدون ابلاغ، هیچ امّتی را کیفر ندهد]، به خاطر آنچه [از اسیران که نابجا] گرفتید، عذابی بزرگ به شما می‌رسید.'),
(1229, 8, 69, 'پس، از آنچه غنیمت گرفتید، که حلال و پاکیزه است، مصرف نمایید و از خداوند پروا کنید، خداوند، آمرزنده و مهربان است.'),
(1230, 8, 70, 'ای پیامبر! به اسیرانی که در دست شمایند، بگو: «اگر خداوند در دل‌های شما خیری بداند، بهتر از آنچه از شما گرفته شده است، به شما خواهد داد [و مسلمان خواهید شد] و گناهانتان را می‌بخشاید و خداوند بخشاینده و مهربان است.»'),
(1231, 8, 71, 'و اگر [اسیران] بخواهند به تو خیانت کنند، از پیش هم، به خدا خیانت کرده‌اند. پس خداوند [تو را] بر آنان غلبه و تسلّط داد و خداوند، دانا و حکیم است.'),
(1232, 8, 72, 'کسانی که ایمان آوردند و هجرت نمودند و با اموال و جان‌هایشان در راه خدا جهاد کردند و آنان که [مجاهدان و مهاجران را] پناه دادند و یاری کردند، آنان دوستدار و حامی و هم‌پیمان یکدیگرند. امّا کسانی که ایمان آوردند، ولی هجرت نکردند، حقّ هیچ‌گونه دوستی و حمایت از آنان را ندارید، تا آن که هجرت کنند و اگر [مؤمنانِ تحت فشار، برای حفظ دینشان] از شما یاری طلب کردند، بر شماست که یاریشان کنید، مگر در برابر قومی که میان شما و آنان پیمان [ترک مخاصمه] است و خداوند به آنچه انجام می‌دهید، بیناست.'),
(1233, 8, 73, 'و کسانی که کفر ورزیدند، یاران یکدیگرند. اگر شما [هم میان خود، همبستگی] نداشته باشید، فتنه و فساد بزرگی در زمین واقع خواهد شد.'),
(1234, 8, 74, 'و کسانی که ایمان آوردند و هجرت کردند و در راه خدا جهاد نمودند و کسانی که پناه دادند و یاری کردند، همانان مؤمنان واقعی‌اند. برای آنان آمرزش و روزی شایسته و کریمانه‌ای است.'),
(1235, 8, 75, 'و کسانی که بعداً ایمان آوردند و همراه شما هجرت و جهاد کردند، آنان از شمایند و در قانون الهی، خویشاوندان نسبت به یکدیگر اولویت دارند. خداوند به هر چیزی بسیار داناست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, '[این آیات، اعلام] بیزاری و برائتی است از سوی خدا و پیامبرش، نسبت به مشرکانی که با آنان پیمان بسته‌اید.'),
(1237, 9, 2, 'پس [ای مشرکان!] به مدّت چهار ماه مهلت دارید که در زمین [آزادانه] بگردید و بدانید که شما نمی‌توانید خدا را مغلوب کنید [و از عذاب او بگریزید] و خداوند، خوارکننده کافران است.'),
(1238, 9, 3, 'این [آیات]، اعلامی است از سوی خدا و پیامبرش [به مردم] در روز حجّ اکبر [عید قربان]، که خدا و رسولش از مشرکان بیزارند. [با این حال،] اگر توبه کنید [و دست از شرک و کفر بردارید،] این برایتان بهتر است و اگر روی بگردانید، پس بدانید که هرگز نمی‌توانید خدا را ناتوان کنید و کافران را به عذابی دردناک بشارت ده.'),
(1239, 9, 4, 'مگر کسانی از مشرکین که با آنان پیمان بسته‌اید و چیزی از تعهّدات خود نسبت به شما فروگذار نکرده و کسی را در برابر شما یاری نداده‌اند. پس پیمانشان را تا پایان مدّتشان نگه‌دارید [و وفادار باشید] که خداوند، اهل تقوا را دوست دارد.'),
(1240, 9, 5, 'پس چون ماه‌های حرام سپری شود، مشرکان را هرجا یافتید، بکشید و دستگیر کنید و در محاصره قرار دهید و در همه جا به کمین آنان بنشینید. پس اگر توبه کردند و نماز بر پا داشتند و زکات پرداختند، راهشان را باز کنید [و آزادشان بگذارید]. که خداوندش، آمرزنده و مهربان است.'),
(1241, 9, 6, 'و [ای پیامبر!] اگر یکی از مشرکان از تو امان و پناه خواست، پس به او پناه بده تا کلام خدا را بشنود، سپس او را به مکان امنش برسان. چرا که آنان گروهی ناآگاهند.'),
(1242, 9, 7, 'چگونه می‌تواند نزد خدا و رسولش، پیمانی با مشرکان [عهدشکن] باشد؟ مگر کسانی که نزد مسجدالحرام با آنان پیمان بستید. پس تا هنگامی که به عهدشان وفادارند، شما هم وفادار بمانید. که خداوند، متقین را دوست دارد.'),
(1243, 9, 8, 'چگونه [می‌توان با آنان پیمانی داشت،] در حالی که اگر بر شما دست یابند، هیچ خویشاوندی و پیمانی را درباره‌ی شما مراعات نمی‌کنند. شما را با زبانِ [نرمِ] خویش راضی می‌کنند، ولی دل‌هایشان پذیرا نیست و بیشترشان فاسق [و پیمان‌شکن]اند.'),
(1244, 9, 9, 'آیات خدا را به بهای اندکی فروختند و [مردم را] از راه خدا بازداشتند. به راستی آنان بد اعمالی انجام می‌دادند!'),
(1245, 9, 10, 'آنان درباره هیچ مؤمنی، حقّ خویشاوندی و عهد و پیمانی را مراعات نخواهند کرد و ایشان همان تجاوزکارانند.'),
(1246, 9, 11, 'امّا اگر توبه کردند و نماز بر پا داشتند و زکات پرداختند، در این صورت، برادران دینی شمایند و ما آیات خود را برای گروهی که می‌دانند [و می‌اندیشند]، به تفصیل بیان می‌کنیم.'),
(1247, 9, 12, 'امّا اگر [به جای توبه،] سوگندهای خویش را پس از بستنِ پیمانشان شکستند و در دین شما، زبان به طعنه گشودند، پس با سران کفر بجنگید. زیرا آنان را [پای‌بندی به] سوگندی نیست، باشد که [با شدّتِ عمل شما،] از کردار خود بازایستند.'),
(1248, 9, 13, 'آیا با گروهی که سوگندها [و پیمان‌های] خود را شکستند و به بیرون کردن پیامبر همّت گماشتند و آنان، نخستین بار جنگ را با شما آغاز کردند، پیکار نمی‌کنید؟ آیا از آنان می‌ترسید؟ اگر ایمان دارید، سزاوارتر آن است که از خدا بترسید!'),
(1249, 9, 14, 'با آنان بجنگید، تا خداوند آنان را به دست‌های شما عذاب کند و خوارشان سازد و شما را بر آنان پیروز کند و دل‌های مؤمنان را شفا بخشد و مرهم نهد.'),
(1250, 9, 15, 'و خداوند [با پیروزی شما و خواری دشمن،] غیظ و خشم دل‌های مؤمنان را از بین ببرد و خداوند لطف خود را بر هر کس بخواهد، برمی‌گرداند و خداوند، دانا و حکیم است.'),
(1251, 9, 16, 'یا پنداشته‌اید که [تنها با ادّعای ایمان] رها می‌شوید، در حالی که هنوز خداوند [با امتحان‌هایش شما را نیازموده، تا] کسانی از شما را که جهاد کرده و جز خدا و پیامبرش و مؤمنان رازداری برنگزیده‌اند، معلوم دارد و خداوند به آنچه می‌کنید، آگاه است.'),
(1252, 9, 17, 'مشرکان که به صراحت بر کفر خویش گواهی می‌دهند، حقّ ندارند مساجد خدا را تعمیر کنند. آنان کارهایشان تباه شده است و در آتش، جاودان خواهند بود.'),
(1253, 9, 18, 'مساجد خدا را تنها کسانی باید آباد کنند که به خدا و روز قیامت ایمان دارند و نماز را به پا داشته، زکات می‌پردازند و جز از خدا نمی‌ترسند. امید است که آنان از ره‌یافتگان باشند.'),
(1254, 9, 19, 'آیا آب‌رسانی به حاجیان و تعمیر مسجدالحرام را همانند [عملِ] کسی قرار داده‌اید که به خدا و روز قیامت ایمان آورده و در راه خدا جهاد کرده است؟ این دو، نزد خدا یکسان نیست و خداوند گروه ستمگر را هدایت نمی‌کند.'),
(1255, 9, 20, 'کسانی که ایمان آوردند و هجرت کردند و در راه خدا با اموال و جان‌های خویش جهاد کردند، بزرگ‌ترین درجه را نزد خداوند دارند و اینان همان رستگارانند.'),
(1256, 9, 21, 'پروردگارشان آنان را به رحمتی از ناحیه خود و رضایت خویش و باغ‌هایی از بهشت، بشارت می‌دهد که در آن نعمت‌هایی جاودانه دارند.'),
(1257, 9, 22, 'آنان تا ابد در آنجا جاودانند. همانا نزد خداوند، پاداشی بزرگ است.'),
(1258, 9, 23, 'ای کسانی که ایمان آورده‌اید! اگر پدران و برادرانتان، کفر را بر ایمان ترجیح دادند ولایت آنان را نپذیرید و کسانی از شما که دوستی و ولایت آنان را بپذیرند، آنان ستمگرانند.'),
(1259, 9, 24, 'بگو: «اگر پدران و فرزندان و برادران و همسران و خاندان شما و ثروت‌هایی که جمع کرده‌اید و تجارتی که از کسادی آن بیم دارید و خانه‌هایی که به آنها دلخوش کرده‌اید، نزد شما از خدا و پیامبرش و جهاد در راه او محبوب‌تر باشد، پس منتظر باشید تا خداوند، فرمان [قهر] خویش را به اجرا درآورد و خداوند، گروه نافرمان را هدایت نمی‌کند.»'),
(1260, 9, 25, 'خداوند در مواضع بسیاری شما را یاری کرده است و در روز حُنَین [نیز شما را یاری کرد]، آن‌گاه که فراوانیِ لشکرتان شما را به غرور و اعجاب واداشت؛ ولی این فزونیِ نفرات، هیچ سودی برای شما نداشت و زمین با همه‌ی گستردگی‌اش بر شما تنگ آمد، پس شما پشت به دشمن، فرار کردید.'),
(1261, 9, 26, 'سپس خداوند، آرامش خود را بر پیامبرش و بر مؤمنان فروفرستاد و سپاهیانی فرستاد که شما آنان را ندیدید و کافران را عذاب کرد و این، کیفر کافران است.'),
(1262, 9, 27, 'سپس خداوند بعد از آن، لطف خود را به هر که بخواهد، برمی‌گرداند. [و توبه‌ی فراریان را می‌پذیرد.] و خداوند، آمرزنده و مهربان است.'),
(1263, 9, 28, 'ای کسانی که ایمان آورده‌اید! قطعاً مشرکان، پلید و ناپاکند. پس بعد از این سال نباید به مسجدالحرام نزدیک شوند و اگر [به خاطر راندن کفّار و قطع داد و ستد،] از فقر و تنگدستی می‌ترسید، پس به زودی خداوند اگر بخواهد، از فضل خویش شما را بی‌نیاز خواهد کرد. قطعاً خداوند آگاه و حکیم است.'),
(1264, 9, 29, 'با کسانی از اهل کتاب که به خدا و روز قیامت ایمان نمی‌آورند و آنچه را خدا و رسولش حرام کرده‌اند، حرام نمی‌شمرند و به دینِ حقّ گردن نمی‌نهند، بجنگید، تا با خواری و ذلّت، به دست خود جِزیه بپردازند.'),
(1265, 9, 30, 'و یهودیان می‌گفتند: «عُزَیر، پسر خداست!» و نصارا می‌گفتند: «مسیح، پسر خداست!» این سخنی است که به زبانشان می‌آورند و به گفتار کسانی که پیش از این کافر شده‌اند، شباهت دارد [که می‌گفتند: فرشتگان دختران خدا هستند]. خدا آنان را بکشد، چگونه [از حقّ] روی‌گردان می‌شوند.'),
(1266, 9, 31, '[اهل کتاب،] دانشمندان و راهبانِ خود و مسیح فرزند مریم را به جای خداوند به خدایی گرفتند، در حالی که دستور نداشتند جز خدای یکتا را که معبودی جز او نیست، بپرستند. خداوند از هر چه برای او شریک می‌پندارند، منزّه است.'),
(1267, 9, 32, 'می‌خواهند نور خدا را با دهان‌هایشان خاموش سازند؛ ولی خداوند جز این نمی‌خواهد که نور خود را به کمال برساند، هر چند کفّار، ناراحت باشند.'),
(1268, 9, 33, 'او کسی است که پیامبر خود را با هدایت و دین حقّ فرستاد، تا آن را بر همه‌ی دین‌ها پیروز گرداند، هر چند مشرکان ناراحت باشند.'),
(1269, 9, 34, 'ای کسانی که ایمان آورده‌اید! بسیاری از دانشمندان و راهبان، اموال مردم را به ناحق می‌خورند و آنان را از راه خدا بازمی‌دارند و کسانی که طلا و نقره می‌اندوزند و آن را در راه خدا انفاق نمی‌کنند، آنان را به عذابی دردناک، بشارت بده!'),
(1270, 9, 35, 'روزی که آن [طلاها و نقره‌ها]، در آتش دوزخ گداخته می‌شود و با آن پیشانی‌ها و پهلوها و پشت‌های آنان را داغ می‌نهند. [فرشتگان عذاب به آنان می‌گویند:] «این است آنچه برای خود اندوختید [و به محرومان ندادید]، پس [مزه‌ی] آنچه را که می‌اندوختید، بچشید!»'),
(1271, 9, 36, 'تعداد ماه‌ها نزد خداوند، در کتابِ خدا و از روزی که آسمان‌ها و زمین را آفرید، دوازده ماه است، که چهار ماه از آن [جنگ] حرام است. این آیینِ ثابت و پابرجاست. پس در این ماه‌ها، با جنگ و خون‌ریزی بر خود ستم نکنید و جملگی با همه‌ی مشرکان بجنگید، آن گونه که آنان همگی با شما می‌جنگند و بدانید که خداوند با پرواپیشگان است.'),
(1272, 9, 37, 'تأخیر [و تغییر ماه‌های حرام به ماه‌های دیگر،] سبب افزایش در کفر است، که به وسیله‌ی آن کافران گمراه می‌شوند. [آنها] یک سال [جنگ در ماه‌های حرام] را [به سلیقه و تمایل و مصلحت‌اندیشی،] حلال می‌دانند و یک سال آن را حرام. تا با تعداد ماه‌هایی که خداوند حرام ساخته، مطابق آید. از این رو، آنچه را خدا حرام کرده، حلال می‌کنند. کارهای ناپسند آنها، در نظرشان زیبا جلوه یافته است و خداوند کافران را هدایت نمی‌کند.'),
(1273, 9, 38, 'ای کسانی که ایمان آورده‌اید! چرا وقتی به شما گفته می‌شود: «در راه خدا [و برای جهاد] حرکت کنید!» سنگین و زمین‌گیر می‌شوید؟ آیا به جای آخرت، به زندگی دنیا راضی شده‌اید؟ پس بدانید بهره‌ی زندگی دنیا در [برابر] آخرت، فقط اندکی است.'),
(1274, 9, 39, 'اگر [به سوی میدان جنگ] نشتابید، خداوند شما را به عذابی دردناک کیفر می‌دهد و گروه دیگری را جایگزین شما می‌کند و شما [با ترک جبهه،] ضرری به خدا نمی‌زنید و خداوند بر هر چیزی تواناست.'),
(1275, 9, 40, 'اگر پیامبر را یاری نکنید، [بدانید] خدا آن زمان که کافران او را [از مکه] بیرون کردند، یاریش کرد. در حالی که او یکی از دو تن بود. زمانی که آن دو در غار [ثور] بودند، [پیامبر] به همراهش [ابوبکر] می‌گفت: «اندوه مدار که خدا با ماست!» پس خدا، آرامش خویش را بر پیامبر نازل کرد و او را با سپاهیانی که آنها را ندیدید، یاری نمود و سخن و طرح کافران را [که قصد داشتند پیامبر را بکشند،] پست‌تر [و خنثی] قرار داد و کلمه [و اراده] خداوند [برای نصرت پیامبرش،] برتر [و پیروز] است و خداوند توانا و حکیم است.'),
(1276, 9, 41, '[به سوی جبهه و جهاد] کوچ کنید، سبک‌بار و سنگین‌بار. [خواه بر شما آسان باشد، خواه دشوار.] و در راه خدا با اموال و جان‌های خود جهاد کنید، اگر می‌دانستید، این برای شما بهتر است.'),
(1277, 9, 42, 'اگر غنیمتی نزدیک و سفری کوتاه [برای جهاد] بود، قطعاً [منافقان] در پی تو می‌آمدند؛ ولی آن راه بر آنان دور و دشوار آمد [و به این بهانه نمی‌آیند] و به خدا سوگند خواهند خورد که: «اگر می‌توانستیم، با شما [به جنگ] بیرون می‌آمدیم!» آنان [با این روش،] خود را هلاک می‌کنند و خداوند می‌داند که آنان دروغگویانند.'),
(1278, 9, 43, 'خداوند از تو درگذشت. چرا پیش از آن که [حال] راستگویان بر تو روشن شود و دروغگویان را بشناسی، به آنان اجازه [مرخصی] دادی؟'),
(1279, 9, 44, 'آنان که به خدا و روز قیامت ایمان دارند، هرگز برای جهاد کردن با مال و جانشان، از تو مرخصی نمی‌طلبند و خداوند به [حال] پرهیزکاران آگاه است.'),
(1280, 9, 45, 'تنها کسانی از تو اجازه می‌گیرند [به جبهه نروند،] که به خدا و روز قیامت ایمان ندارند و دل‌هایشان مرددّ گشته است. پس آنان در شک و تردیدشان سرگردانند.'),
(1281, 9, 46, '[منافقان،] اگر تصمیم جدّی بر رفتن [به جبهه] داشتند، ساز و برگ جهاد آماده می‌کردند ولی خداوند، انگیزه و بسیج آنان را [به خاطر کوردلی و نالایقی،] خوش نداشت و آنان را [از رفتن به جبهه] بازداشت و به آنان گفته شد: «همنشینِ خانه‌نشینان، [کودکان و سالمندان و بیماران] باشید!»'),
(1282, 9, 47, 'اگر [منافقان] همراه شما به جنگ بیرون آمده بودند، جز فساد، [تردید و اضطراب، چیزی‌] بر شما نمی‌افزودند و به سرعت در میان شما رخنه می‌کردند تا فتنه پدید آورند و در میان شما کسانی هستند [تأثیرپذیر که] به سخنان آنان گوش و دل می‌سپارند و خداوند به حال ستمگران آگاه است.'),
(1283, 9, 48, '[منافقان] پیش از این دنبال فتنه‌گری بودند و کارها را برای تو واژگون جلوه می‌دادند، تا آن که حقّ آمد و امر خدا آشکار شد [و پیروز شدید]، اگر چه آنان خوش نداشتند.'),
(1284, 9, 49, 'و برخی از آنان می‌گویند: «به من اجازه بده [به جبهه نیایم] و مرا [به گناه و] فتنه میانداز!» آگاه باشید که اینان در فتنه [و گناه] سقوط کرده‌اند و جهنّم، بر کافران احاطه دارد.'),
(1285, 9, 50, 'اگر به تو نیکی رسد [و پیروز شوی]، منافقان را ناراحت می‌کند، ولی اگر به تو مصیبتی و شکستی برسد، می‌گویند: «ما چاره‌ی خویش را از قبل اندیشیده‌ایم [و این را پیش‌بینی می‌کردیم].» و شادمان برمی‌گردند.'),
(1286, 9, 51, 'بگو: «هرگز جز آنچه خداوند برای ما مقرّر کرده است، به ما نخواهد رسید. او مولای ماست و مؤمنان باید تنها بر خدا توکل کنند.»'),
(1287, 9, 52, '[ای پیامبر!] بگو: «آیا برای ما جز یکی از دو نیکی، [فتح یا شهادت] را انتظار دارید؟ ولی ما منتظریم که عذاب خداوند یا از سوی خودش یا به دست ما، به شما برسد. پس شما منتظر باشید، ما نیز با شما در انتظار می‌مانیم.»'),
(1288, 9, 53, '[به منافقانی که به جای حضور در جبهه، قصد کمک مالی دارند،] بگو: «چه از روی علاقه انفاق کنید و چه از روی کراهت، هرگز از شما پذیرفته نخواهد شد، زیرا شما قومی فاسق بوده‌اید.»'),
(1289, 9, 54, 'و چیزی منافقان را از پذیرفته شدنِ انفاقشان منع نکرد، جز این که به خدا و پیامبرش کافر شدند و نماز را جز از روی کسالت و بی‌حالی به جا نمی‌آورند و جز از روی کراهت و بی‌میلی انفاق نمی‌کنند.'),
(1290, 9, 55, 'پس اموال و فرزندان منافقان، تو را به شگفتی نیندازد. جز این نیست که خداوند می‌خواهد به این وسیله، آنان را در زندگیِ دنیا عذاب کند و جانشان در حال کفر، خارج شود.'),
(1291, 9, 56, 'منافقان به خدا قسم می‌خورند که آنان از شمایند، در حالی که [دروغ می‌گویند و] از شما نیستند، بلکه آنان گروهی هستند که [از شما] می‌ترسند [یا میان دل و زبان و عملشان جدایی است].'),
(1292, 9, 57, 'اگر [منافقان] پناهگاه یا غارها یا گریزگاهی بیابند، البتّه شتابان به آن روی می‌آورند.'),
(1293, 9, 58, 'و بعضی از منافقان، در [تقسیم زکات و] صدقات، به تو عیب می‌گیرند. پس اگر چیزی از آن [اموال] به آنان داده شود، راضی می‌شوند [و تو را عادل می‌شمارند]، ولی اگر چیزی به آنان داده نشود، به ناگاه خشمگین می‌شوند [و تو را به بی‌عدالتی متّهم می‌کنند].'),
(1294, 9, 59, 'اگر آنان به آنچه خدا و پیامبرش به آنان داده‌اند، راضی می‌شدند و می‌گفتند: «خداوند [و آنچه او صلاح بداند،] برای ما بس است، خدا و پیامبرش به زودی از فضل خود به ما خواهند داد و ما تنها به پروردگار، راغب و امیدواریم.» [اگر چنین می گفتند، برای آنان بهتر بود].'),
(1295, 9, 60, 'همانا صدقات [و زکات]، برای نیازمندان و درماندگان وکارگزارانِ زکات و برای جلب دل‌ها و آزادی بردگان و ادای بدهی بدهکاران و [هزینه‌ی جهاد] در راه خدا و تأمینِ در راه مانده است. این دستور، فرمانی است از جانب خدا و خداوند، دانا و حکیم است.'),
(1296, 9, 61, 'برخی از منافقان، پیامبر را آزار می‌دهند و می‌گویند: «او سراپا گوش است. [به سخن هر کسی گوش می‌دهد و خوش باور است.]» بگو: «گوش دادن و خوش باوری به نفع شماست.» او به خداوند ایمان دارد و مؤمنان را تصدیق می‌کند و برای هر کس از شما که ایمان آورد، مایه‌ی رحمت است و آنان که رسول خدا را آزار می‌دهند، عذابی دردناک دارند.'),
(1297, 9, 62, '[منافقان] برای شما به خدا سوگند می‌خورند تا شما را راضی کنند، در حالی‌که اگر ایمان آورده‌اند، شایسته‌تر آن است که خدا و رسولش را راضی کنند.'),
(1298, 9, 63, 'آیا ندانستند که هر کس با خدا و پیامبرش دشمنی کند، کیفرش آتش دوزخ است و پیوسته در آن خواهد بود؟ این است خواری و رسوایی بزرگ [که بهره‌ی مخالفان است]!'),
(1299, 9, 64, 'منافقان بیم دارند که سوره‌ای به زیان آنان نازل شود که از آنچه در درونشان است، خبر دهد. بگو: «[هرچه می‌خواهید] مسخره کنید. قطعاً خداوند آنچه را که [از آشکار شدنش] بیم دارید، آشکار خواهد کرد.»'),
(1300, 9, 65, 'اگر از منافقان [دلیل تمسخرشان را] بپرسی، قطعاً می‌گویند: «ما فقط شوخی و بازی می‌کردیم [، غرضی نداشتیم].» بگو: «آیا خداوند و آیات او و پیامبرش را مسخره می‌کردید!؟»'),
(1301, 9, 66, '[بی‌جهت] عذر و بهانه نیاورید. شما بعد از ایمانتان، کافر شدید. اگر از گروهی از شما [به خاطر توبه یا آن که بار اوّلشان است،] درگذریم، گروهی [دیگر] را به خاطر سابقه‌ی جرمشان کیفر می‌دهیم.'),
(1302, 9, 67, 'مردان و زنانِ منافق، از یکدیگرند، [از یک قماشند،] به منکر فرمان می‌دهند و از معروف نهی می‌کنند و دست‌های خود را [از بخشش و انفاق] می‌بندند. خدا را فراموش کرده‌اند، پس خداوند نیز آنان را فراموش کرده است. همانا منافقان، همان فاسقانند.'),
(1303, 9, 68, 'خداوند به مردان و زنان منافق و به کافران، وعده‌ی آتش دوزخ را داده، که پیوسته در آن خواهند بود. آن [دوزخ] برایشان بس است و خداوند آنان را لعنت کرده [و از لطف خویش دور ساخته] و برایشان عذابی پایدار است.'),
(1304, 9, 69, '[حالِ شما منافقان،] همچون کسانی است که پیش از شما بودند، [با آن که] آنان نیرومندتر از شما و ثروتمندتر و صاحب فرزندان بیشتری بودند. پس آنان از نصیبشان بهره‌مند شدند. شما نیز همان‌گونه که پیشینیانِ شما بهره‌مند شدند، بهره‌ی خود را بردید و [در روش باطل خود] فرو رفتید، چنان که آنان فرو رفتند. آنها اعمالشان در دنیا و آخرت محو شد و آنان همان زیانکارانند.'),
(1305, 9, 70, 'آیا خبر کسانی که پیش از آنان بودند، [سرنوشتِ] قوم نوح و عاد و ثمود و قوم ابراهیم و اصحاب مَدین و شهرهای زیر و رو شده، به آنان نرسیده است؟ پیامبرانشان دلایل روشن برایشان آوردند، [ولی آنان لجاجت کردند و نابود شدند.] پس خداوند به آنان ستم نکرد، بلکه خودشان به خویش ستم می‌کردند.'),
(1306, 9, 71, 'مردان و زنان با ایمان، یار و یاور و اولیای یکدیگرند. به معروف فرمان می‌دهند و از منکرات و بدی‌ها نهی می‌کنند. نماز بر پای داشته، زکات می‌پردازند و از خداوند و پیامبرش پیروی می‌کنند. به زودی خداوند آنان را مشمول رحمت خویش قرار خواهد داد. همانا خداوند، توانای غالب و حکیم است.'),
(1307, 9, 72, 'خداوند به مردان و زنان با ایمان، باغ‌هایی را وعده داده است که از پای [درختان] آن، نهرها جاری است و در آن جاودانند و [نیز] مسکن‌هایی دلپسند در بهشت برین ولی رضایت و خرسندی خداوند، برتر و والاتر است. این همان رستگاری بزرگ است.'),
(1308, 9, 73, 'ای پیامبر! با کافران و منافقان ستیز کن و بر آنان سخت‌گیر و خشن باش که جایگاهشان دوزخ است و بد سرنوشتی دارند!'),
(1309, 9, 74, '[منافقان] به خدا سوگند می‌خورند که [سخنی کفرآمیز] نگفته‌اند، در حالی‌که قطعاً سخن کفر[آمیز] گفته‌اند و پس از اسلام آوردنشان کافر شدند و تصمیم به کاری [چون کشتن پیامبر] گرفتند که به آن دست نیافتند و جز این که خداوند و پیامبرش آنان را از لطف خویش بی‌نیاز کرده، عیبی [برای پیامبر و مؤمنین] نیافتند. [با این حال] اگر توبه کنند، برایشان بهتر است و اگر روی بگردانند، خداوند آنان را در دنیا و آخرت به عذابی دردناک گرفتار می‌کند و در روی زمین هیچ دوست و یاوری برایشان نخواهد بود.'),
(1310, 9, 75, 'و برخی از آنان با خدا پیمان بسته بودند که: «اگر خداوند از فضل خویش به ما عطا کند، حتماً صدقه [و زکات] خواهیم داد و از نیکوکاران خواهیم بود.»'),
(1311, 9, 76, 'پس چون خداوند از فضل خویش به آنان بخشید، بدان بخل ورزیدند و [به پیمان] پشت کرده و روی‌گردان شدند.'),
(1312, 9, 77, 'سرانجام به دنبال آن که با خدا در آنچه پیمان بسته بودند، خلف وعده کردند و بدان سبب که دروغ می‌گفتند، [خداوند، روحِ] نفاق را تا روزی که به دیدار او رسند، در دل‌های آنان قرار داد.'),
(1313, 9, 78, 'آیا ندانستند که خداوند، راز آنان و سخنان آهسته [و درِ گوشی] آنان را می‌داند و این که خداوند، به همه‌ی غیب‌ها داناست؟!'),
(1314, 9, 79, 'منافقان بر مؤمنانی که [علاوه بر زکات،] داوطلبانه صدقات مستحب نیز می‌دهند و همچنین بر مؤمنانِ [تهی‌دستی] که جز به اندازه‌ی توانشان چیزی [برای انفاق و پشتیبانی از جبهه] نمی‌یابند، عیب می‌گیرند و آنان را مسخره می‌کنند. [بدانند که] خداوند آنان را به استهزاء می‌گیرد [و کیفر تمسخرشان را خواهد داد] و عذابی دردناک در انتظار آنهاست.'),
(1315, 9, 80, 'برای منافقان استغفار کنی یا استغفار نکنی، [یکسان است.] اگر هفتاد بار برایشان آمرزش بخواهی، خداوند هرگز آنان را نخواهد بخشید. این [قهر حتمی الهی]، به خاطر آن است که آنان به خدا و پیامبرش کفر ورزیدند و خدا، گروه فاسق را هدایت نمی‌کند.'),
(1316, 9, 81, 'کسانی که بر خلاف [فرمانِ] رسول خدا، از جنگ سر باز زدند و از خانه نشستن خود [به هنگام جنگ تبوک،] شادمان شدند و از این که با اموال و جان‌های خود در راه خدا جهاد کنند، کراهت داشتند و گفتند: «در این گرما [برای جنگ] بیرون نروید!» بگو: آتش دوزخ، سوزان‌تر است!» می‌فهمیدند.'),
(1317, 9, 82, 'پس به سزای آنچه کسب می‌کردند، کم بخندند و بسیار بگریند!'),
(1318, 9, 83, 'پس اگر خداوند تو را [پس از این جنگ،] به سوی طایفه‌ای از منافقان بازگرداند و آنان از تو برای حرکت [به جنگ دیگری،] اجازه‌ی خروج خواستند، بگو: «شما هرگز با من بیرون نخواهید آمد و هرگز همراه من، با هیچ دشمنی نبرد نخواهید کرد، زیرا شما نخستین بار به نشستن در خانه راضی شدید، پس [اکنون نیز] با آنان که از فرمان تخلّف کرده‌اند، [در خانه] بنشینید!»'),
(1319, 9, 84, 'و بر مرده‌ی هیچ یک از منافقان نماز مگزار! و [برای دعا و استغفار] بر قبرش نایست! چون آنان به خدا و پیامبرش کافر شدند و فاسق از دنیا رفتند.'),
(1320, 9, 85, 'اموال و فرزندان آنان، تو را به شگفتی و اعجاب نیاورد! همانا خداوند می‌خواهد آنان را بدین وسیله در دنیا عذاب کند و در حال کفر، جانشان به در آید.'),
(1321, 9, 86, 'و هر گاه سوره‌ای نازل شود که به خداوند ایمان آورید و همراه پیامبرش جهاد کنید، ثروتمندان [منافق]، از تو اجازه‌ی مرخصی [برای فرار از جبهه] می‌خواهند و می‌گویند: «ما را واگذار تا با خانه‌نشینان، [که از جنگ معافند،] باشیم.»'),
(1322, 9, 87, 'آنان راضی شدند که با متخلّفان و خانه‌نشینان باشند و بر دل‌های آنان مهر زده شده است، از این رو نمی‌فهمند.'),
(1323, 9, 88, 'ولی [در مقابلِ منافقانِ رفاه‌طلب و گریزان از جنگ،] پیامبر و مؤمنانِ همراه او، با اموال و جان‌هایشان جهاد کردند و اینانند که همه‌ی خیرات و نیکی‌ها برای آنان است و همانانند رستگاران.'),
(1324, 9, 89, 'خداوند برای آنان، باغ‌هایی [در بهشت] آماده ساخته که نهرها از پای [درختان] آنها جاری است و در آنجا جاودانند. این همان رستگاری بزرگ است.'),
(1325, 9, 90, 'بادیه‌نشینانی که [از شرکت در جنگ] معذور بودند، [نزد تو] آمدند تا به آنان اذن داده شود [که در جنگ شرکت نکنند]، ولی کسانی که به خدا و پیامبرش دروغ گفتند [و عذری نداشتند]، از جنگ بازنشستند. به زودی به کسانی از آنان که کفر ورزیدند، عذابی دردناک خواهد رسید.'),
(1326, 9, 91, 'بر ناتوانان و بیماران [که نمی‌توانند در جهاد شرکت کنند] و تهی‌دستانی که چیزی برای خرج کردن [در راه جهاد] نمی‌یابند، ایرادی نیست. به شرط آن که خیرخواه خدا و پیامبرش باشند، [و از آنچه در توان دارند، مضایقه نکنند.] زیرا بر نیکوکاران، راهِ سرزنش و مؤاخذه‌ای نیست و خداوند بخشنده‌ی مهربان است.'),
(1327, 9, 92, 'و نیز [اشکالی نیست] بر آنان که چون نزد تو آمدند تا آنان را برای شرکت در جبهه بر مرکبی سوار کنی، گفتی: «چیزی نمی‌یابم که شما را بر آن سوار کنم.» و آنان [از نزد تو] برگشتند، در حالی که چشمانشان از اندوه، اشک‌بار بود که چرا چیزی ندارند که خرجِ جهاد کنند. [بر این فقیرانِ عاشق جهاد، برای نرفتن به جبهه، گناهی نیست.]'),
(1328, 9, 93, 'راه [ایراد و مؤاخذه]، تنها بر کسانی است که در عینِ توانگری و ثروتمندی، از تو اذن می‌خواهند [که به جبهه نروند] و راضی شده‌اند که با متخلّفان باشند. خداوند بر دل‌هایشان مهر زده است، از این رو نمی‌دانند.'),
(1329, 9, 94, 'هنگامی که از جهاد برگشتید، [منافقانِ متخلّف از جنگ تبوک،] برای شما عذر می‌آوردند. بگو: «عذرتراشی نکنید! ما هرگز حرف شما را باور نمی‌کنیم. خداوند ما را از اخبار [و احوال] شما آگاه کرده است. خداوند و پیامبرش، عملکرد شما را می‌بینند، آن گاه نزد خدایی که دانای پنهان و آشکار است، بازگردانده می‌شوید و او شما را به آنچه می‌کردید، آگاه خواهد کرد.»'),
(1330, 9, 95, 'هنگامی که از جهاد، نزد منافقان بازگشتید، برای شما به خدا سوگند می‌خورند تا از [گناه و سرزنشِ] آنان چشم بپوشید. پس، از آنان اعراض و دوری کنید که آنان پلیدند و به خاطر آنچه به دست خود کسب می‌کرده‌اند، جایگاهشان دوزخ خواهد بود.'),
(1331, 9, 96, 'برای شما سوگند می‌خورند تا شما از آنان راضی شوید. [بدانید که شما هم] اگر از آنان راضی شوید، قطعاً خداوند از گروه فاسقان، خشنود نخواهد شد.'),
(1332, 9, 97, 'بادیه‌نشینانِ عرب [به خاطر دوری از تعلیم و تربیت و سخنان پیامبر]، در کفر و نفاق شدیدترند و به این که حدود آنچه را خدا بر پیامبرش نازل کرده، ندانند، سزاوارتر و خداوند دانا و حکیم است.'),
(1333, 9, 98, 'بعضی از بادیه‌نشینان، کسانی هستند که آنچه را انفاق می‌کنند، [به خاطر نفاق یا ضعف ایمان،] ضرر حساب می‌کنند و برای شما پیشامدهای بد را انتظار می‌کشند. پیشامد بد برای خود آنهاست! و خداوند شنوا و داناست.'),
(1334, 9, 99, 'امّا برخی از عرب‌های بادیه‌نشین، کسانی هستند که به خداوند و روز قیامت ایمان دارند و آنچه را انفاق می‌کنند، وسیله‌ی نزدیکی به خدا و [جلب] دعا و صلوات رسول می‌دانند. آگاه باشید که همین انفاق‌ها، برای آنان موجب نزدیکی است. به زودی خداوند آنان را در رحمت خویش، وارد می‌کند. خداوند آمرزنده و مهربان است.'),
(1335, 9, 100, 'و خداوند از نخستین پیشگامانِ از مهاجران و انصار و کسانی که با نیکوکاری آنان را پیروی کردند، راضی است و آنان نیز از خدا راضی‌اند و [خدا] برای آنان باغ‌هایی فراهم ساخته که نهرها از پای [درختان] آن جاری است. همیشه در آن جاودانه‌اند. این است رستگاری و کامیابی بزرگ!'),
(1336, 9, 101, 'و از میان بادیه‌نشینانِ پیرامونِ شما، گروهی منافقند و از اهل مدینه نیز عدّه‌ای با نفاق خو گرفته‌اند. تو آنان را نمی‌شناسی، [ولی] ما آنها را می‌شناسیم! به زودی آنان را دو بار عذاب خواهیم کرد. سپس به سوی عذابی سهمگین، بازگردانده می‌شوند.'),
(1337, 9, 102, 'و [از اعراب،] دیگرانی هستند که به گناهان خویش اعتراف کرده‌اند. کار خوب و بد را به هم آمیخته‌اند. امید است خداوند توبه‌ی آنان را بپذیرد [و لطف خویش را به آنان بازگرداند]. همانا خداوند، آمرزنده و مهربان است.'),
(1338, 9, 103, 'از اموالشان صدقه‌ای [به عنوان زکات] بگیر تا بدین وسیله آنان را [از بخل و دنیاپرستی] پاک سازی و رشدشان دهی و بر آنان درود فرست [و دعا کن]. زیرا دعای تو، مایه‌ی آرامش آنان است و خداوند شنوا و داناست.'),
(1339, 9, 104, 'آیا ندانستند که تنها خداوند، از بندگانش توبه را می‌پذیرد و صدقات را می‌گیرد؟ و این که خداوند، بسیار توبه‌پذیر و مهربان است؟'),
(1340, 9, 105, 'و بگو: « [هرگونه که می‌خواهید و می‌توانید،] عمل کنید، که به زودی خداوند و پیامبرش و مؤمنان کار شما را می‌نگرند و به زودی به سوی دانای غیب و آشکار، بازگردانده می‌شوید. پس شما را به آنچه انجام می‌دادید، آگاه خواهد کرد.»'),
(1341, 9, 106, 'و گروه دیگری هستند که کارشان به خواست الهی واگذار شده است؛ یا عذابشان می‌کند و یا [به خاطر توبه و پشیمانی،] به آنان لطف می‌کند و خداوند دانا و حکیم است.'),
(1342, 9, 107, 'و [گروهی دیگر از منافقان،] کسانی هستند که مسجدی برای ضربه زدن به اسلام و برای ترویج کفر و تفرقه افکنی میان مؤمنان و کمین‌گاهی برای [مساعدت] به دشمنان دیرینه‌ی خدا و پیامبرش ساختند و همواره سوگند می‌خورند که: «ما جز خیر، قصدی نداریم‌!» [ولی] خداوند گواهی می‌دهد که آنان دروغگویانند.'),
(1343, 9, 108, 'در آن [مسجد ضرار،] هرگز [برای نماز] نایست! به راستی مسجدی که از روز نخست، بر اساس تقوا بنا شده، سزاوارتر است که در آن نماز بر پا داری. [زیرا] در آن مسجد، مردانی هستند که دوست دارند خود را پاک سازند و خداوند پاکان را دوست می‌دارد.'),
(1344, 9, 109, 'آیا کسی که بنیانِ [کارِ] خود را بر پایه‌ی تقوا و رضای الهی قرار داده بهتر است، یا کسی که بنیاد [کار] خویش را بر لبه‌ی پرتگاهی سست و فرو ریختنی نهاده، که او را در آتشِ دوزخ می‌اندازد؟ خداوند گروه ستمگر را هدایت نمی‌کند.'),
(1345, 9, 110, 'بنیانی را که منافقان بنا نهادند، همواره موجب تحیر و سردرگمی در دل‌های آنان است. [آنها راه نجاتی ندارند،] مگر آن که دل‌هایشان [از نفاق] جدا شود [یا با مرگ، متلاشی گردد] و خداوند دانا و حکیم است.'),
(1346, 9, 111, 'خداوند از مؤمنان، جان‌ها و اموالشان را به بهای بهشت خریده است. آنان در راه خدا می‌جنگند تا بکشند یا کشته شوند. [وفای به این] وعده‌ی حقّ که در تورات و انجیل و قرآن آمده، بر عهده‌ی خداست و چه کسی از خدا به عهدش وفادارتر است؟ پس مژده باد شما را بر این معامله‌ای که به وسیله‌ی آن [با خدا] بیعت کردید و این همان رستگاری بزرگ است.'),
(1347, 9, 112, '[مؤمنانِ مجاهد،] اهل توبه، عبادت، ستایش، سیاحت، رکوع، سجود، امر به معروف و نهی از منکر و حفظِ حدود و مقرّرات الهی‌اند و چنین مؤمنانی را بشارت ده!'),
(1348, 9, 113, 'برای پیامبر و کسانی که ایمان آورده‌اند، سزاوار نیست که برای مشرکان، پس از آن که برایشان آشکار گردید که آنان اهل دوزخند، طلب آمرزش کنند، هر چند خویشاوند آنان باشند.'),
(1349, 9, 114, 'و آمرزش‌خواهی ابراهیم برای [عموی مشرکش که به جای] پدرش [سرپرستی او را به عهده داشت]، فقط به خاطر وعده‌ای بود که به او داده بود، [آن هنگام که از هدایتش ناامید نبود،] پس هنگامی که برایش روشن شد، او دشمن خداست، از او بیزاری جست. همانا ابراهیم، اهل آه و ناله [از ترس خدا] و بردبار بود.'),
(1350, 9, 115, 'خداوند بر آن نیست که قومی را بعد از آن که هدایتشان نمود، بی‌راه بگذارد. مگر آن که آنچه را [که باید از آن] پرهیز کنند، برایشان بیان کند. قطعاً خداوند به هر چیزی داناست.'),
(1351, 9, 116, 'حکومت آسمان‌ها و زمین تنها از آنِ خداست. زنده می‌کند و می‌میراند و شما را جز خداوند، هیچ سرپرست و یاوری نیست.'),
(1352, 9, 117, 'خداوند، لطف خود را بر پیامبر و مهاجران و انصاری که او را در لحظه‌ی دشوارِ [جنگ تبوک] پیروی کردند، سرازیر نمود، پس از آن که نزدیک بود دل‌های بعضی از آنان منحرف شود، [و در جبهه حضور نیابند.] سپس خداوند لطف خود را به آنان بازگرداند. او به آنان رئوف و مهربان است.'),
(1353, 9, 118, 'و [نیز] سه نفری که [از شرکت در جبهه‌ی تبوک به خاطر سستی] وامانده بودند، تا آن گاه که زمین با همه‌ی وسعتش [به سبب قهر و نفرت مردم] بر آنان تنگ شد و از خود به تنگ آمدند و فهمیدند که در برابر خدا، هیچ پناهگاهی جز خود او نیست، پس خداوند لطف خویش را شامل آنان ساخت تا به توبه موفّق شوند. همانا خداوند توبه‌پذیر و مهربان است.'),
(1354, 9, 119, 'ای کسانی که ایمان آورده‌اید! از خدا پروا کنید و با راستگویان باشید.'),
(1355, 9, 120, 'اهل مدینه و بادیه‌نشینان اطرافِ آنان، حقّ ندارند از [فرمانِ] رسول خدا تخلّف کنند و [به جبهه نروند و] جان‌های خود را از جان پیامبر عزیزتر بدانند؛ زیرا هیچ گونه تشنگی، رنج و گرسنگی در راه خدا به آنان نمی‌رسد و هیچ گامی در جایی که کافران را به خشم آورد، برنمی‌دارند و هیچ چیزی از دشمن به آنان نمی‌رسد، مگر آن که برای آنان به پاداش اینها عمل صالح نوشته می‌شود. قطعاً خداوند پاداش نیکوکاران را تباه نمی‌کند.'),
(1356, 9, 121, 'هیچ مال اندک یا فراوانی را [در مسیر جهاد] انفاق نمی‌کنند و هیچ سرزمینی را نمی‌پیمایند، مگر آن که برای آنان ثبت می‌شود، تا خداوند آنان را به بهتر از آنچه می‌کرده‌اند، پاداش دهد.'),
(1357, 9, 122, 'سزاوار نیست که همه‌ی مؤمنان [به جهاد] رهسپار شوند. پس چرا از هر گروهی از ایشان، دسته‌ای کوچ نمی‌کنند تا در دین فقیه شوند و هنگامی که به سوی قوم خویش بازگشتند، آنان را بیم دهند، تا شاید آنان [از گناه و طغیان] حذر کنند.'),
(1358, 9, 123, 'ای کسانی که ایمان آورده‌اید! با کسانی از کفّار که نزدیک شمایند، بجنگید. باید آنان در شما خشونت و صلابت بیابند و بدانید که خداوند، با پرهیزکاران است.'),
(1359, 9, 124, 'و هرگاه سوره‌ای نازل شود، برخی از منافقان گویند: «این سوره، ایمان کدام یک از شما را افزود؟» امّا آنان که ایمان آورده‌اند، [آیات قرآن] بر ایمانشان می‌افزاید و آنان بشارت می‌گیرند.'),
(1360, 9, 125, 'و امّا آنان که در دل‌هایشان بیماری است، [آیات قرآن،] پلیدی بر پلیدی آنان می‌افزاید و در حال کفر می‌میرند.'),
(1361, 9, 126, 'آیا نمی‌بینند که آنان در هر سال، یک یا دو بار آزمایش می‌شوند، امّا نه توبه می‌کنند و نه پند می‌گیرند.'),
(1362, 9, 127, 'و هر گاه سوره‌ای نازل شود، بعضی از آنان به بعضی دیگر نگاه کنند [و بپرسند:] «آیا کسی شما را می‌بیند؟» [مخفیانه از حضور پیامبر] خارج می‌شوند. خداوند دل‌های آنان را [از حق] برگردانده است، زیرا آنان مردم نفهمی هستند.'),
(1363, 9, 128, 'پیامبری از خودتان به سوی شما آمده است که آنچه شما را برنجاند، بر او سخت است، بر هدایت شما حریص و دلسوز و به مؤمنان رئوف و مهربان است.'),
(1364, 9, 129, 'پس اگر [از سخنان خداوند] روی‌‌گردان شدند، بگو: «خداوند مرا کافی است، هیچ معبودی جز او نیست، تنها بر او توکل کرده‌ام و او پروردگار عرش بزرگ است.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، را. این است آیات کتاب استوار و حکمت آمیز.'),
(1366, 10, 2, 'آیا برای مردم شگفت‌آور است که به مردی از خود آنان وحی کردیم که مردم را بترسان و به مؤمنان بشارت بده که برای آنان نزد پروردگارشان جایگاه نیکویی است. کافران گفتند: این مرد، جادوگری آشکار است.'),
(1367, 10, 3, 'پروردگارتان، خدایی است که آسمان‌ها و زمین را در شش روز [و دوران] آفرید، سپس بر عرش [قدرت] قرار گرفت. کار جهان را تدبیر و سامان‌دهی می‌کند. هیچ شفاعت‌کننده‌ای جز با اذن او نیست. آن خداوند است که پروردگار شماست، پس او را بپرستید. آیا پند نمی گیرید؟'),
(1368, 10, 4, 'بازگشت همه‌ی شما به سوی اوست. این وعده‌ی حقّ الهی است. اوست که آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، تا کسانی را که ایمان آورده و عمل صالح انجام داده‌اند، به عدالت پاداش دهد. و برای آنان که کافر شده‌اند، به سزای کفرشان، نوشیدنی از مایع سوزان و عذابی دردناک خواهد بود.'),
(1369, 10, 5, 'اوست که خورشید را درخشنده و ماه را تابان قرار داد و برای حرکتِ ماه منزلگاه‌هایی قرار داد تا شماره‌ی سال‌ها و حساب را بدانید. خداوند اینها را جز بر اساس حقّ نیافریده است. خداوند آیات را برای آنان که [می‌خواهند] بدانند، به تفصیل بیان می‌کند.'),
(1370, 10, 6, 'در رفت و آمد شب و روز و در آنچه خداوند در آسمان‌ها و زمین آفریده است، برای مردم پرهیزکار نشانه‌هایی [بزرگ] است.'),
(1371, 10, 7, 'قطعاً آنان که به دیدار ما [و دریافت نعمت‌های اخروی،] امید ندارند و [تنها] به زندگی دنیا دل‌خوش کرده‌اند و به آن آرام گرفته و تکیه می‌کنند و نیز کسانی که از نشانه‌های [قدرت] ما غافلند،'),
(1372, 10, 8, 'آنان به خاطر آنچه که عمل می‌کردند، جایگاهشان آتش است.'),
(1373, 10, 9, 'به یقین آنان که ایمان آورده و کارهای شایسته انجام داده‌اند، پروردگارشان آنان را به خاطر ایمانشان هدایت می‌کند. و در باغ‌های پرنعمت که نهرها از زیر پایشان جاری است، اقامت دارند.'),
(1374, 10, 10, 'دعا و نیایش آنان در بهشت، این است که: «خدایا! تو پاک و منزّهی!» و درودشان در آنجا، سلام است و پایان نیایش آنان این است که: «سپاس و ستایش مخصوص خداوندی است که پروردگار جهانیان است.»'),
(1375, 10, 11, 'و اگر خداوند برای مردم به همان شتاب که برای خود خیر می‌طلبند، در رساندن بلا به آنان شتاب می‌نمود، قطعاً اجلشان فرارسیده بود. پس کسانی را که به دیدار ما امید [و باور] ندارند، به حال خود رها می‌کنیم تا در سرکشی خویش سرگردان بمانند.'),
(1376, 10, 12, 'و هنگامی که ضرری به انسان برسد، در حالی که به پهلو خوابیده، یا نشسته و یا ایستاده است، ما را می‌خواند؛ ولی چون ضرر را از او برطرف ساختیم، چنان به راه خود می‌رود که گویی هرگز ما را برای گرفتاری که به وی رسیده بود، نخوانده است! این‌گونه برای اسرافکاران، عملشان زیبا جلوه می‌کند.'),
(1377, 10, 13, 'و ما امّت‌های پیش از شما را، چون ستم کردند، هلاک کردیم. پیامبرانشان برای آنها معجزات آوردند، ولی آنها ایمان نیاوردند. ما این‌گونه گروه تبهکاران را کیفر می‌دهیم.'),
(1378, 10, 14, 'سپس شما را پس از [هلاکت] آنان، جانشینانی در زمین قرار دادیم، تا ببینیم شما چگونه عمل می‌کنید.'),
(1379, 10, 15, 'و هرگاه آیات روشن ما بر آنان تلاوت می‌شود، کسانی که به دیدار ما [و دریافت پاداش‌های اخروی] امید و باوری ندارند، می‌گویند: «قرآنی غیر از این بیاور! یا این قرآن را تغییر بده!» [به آنان] بگو: «من حقّ ندارم که آن را از پیش خود تغییر دهم. من تنها آنچه را که به من وحی می‌شود، پیروی می‌کنم. من از عذاب روزی بزرگ بیمناکم، اگر پروردگارم را نافرمانی کنم.»'),
(1380, 10, 16, 'بگو: «اگر خداوند می‌خواست، آن را بر شما نمی‌خواندم و [خداوند] شما را به آن آگاه نمی‌ساخت. من پیش از [آوردن] قرآن، عمری در میان شما بوده‌ام، آیا نمی‌اندیشید؟!»'),
(1381, 10, 17, 'پس کیست ستمکارتر از کسی که بر خدا دروغ بندد؟ یا آیات او را دروغ بشمارد؟ قطعاً مجرمان، رستگار نمی‌شوند.'),
(1382, 10, 18, 'و به جای خدا چیزهایی را می‌پرستند که نه به آنان ضرر می‌رساند و نه سودشان می‌دهد. و می‌گویند: «این بت‌ها، شفیعان ما نزد خدایند.» بگو: «آیا خدا را به چیزی خبر می‌دهید که او خبری از آنها در آسمان‌ها و زمین ندارد؟» منزّه و والاتر است از شریکانی که برایش قرار می‌دهند.'),
(1383, 10, 19, '[در آغاز،] مردم، امّتی واحد [و بر فطرت پاک توحیدی] بودند، پس دچار اختلاف شدند، [گروهی موّحد و گروهی مشرک،] و اگر سنّتِ [مهلت یافتن مردم برای آزمایش،] از سوی پروردگارت مقدّم و مقدّر نگردیده بود، [در همین دنیا] میان مردم در آنچه اختلاف می‌کردند، داوری می‌شد.'),
(1384, 10, 20, 'و می‌گویند: «چرا از سوی پروردگارش بر او آیه و معجزه‌ای نازل نمی‌شود؟» بگو: «غیب از آنِ خداست [نه در اختیار من، یا تابع هوس مردم]. پس در انتظار باشید، من نیز همراه شما از منتظرانم. [که خداوند چگونه پاسخ بهانه‌گیری‌های شما را می‌دهد.]»'),
(1385, 10, 21, 'و هرگاه پس از رنجی که به مردم رسیده، رحمت و لطفی به آنان بچشانیم، [به جای سپاس،] در آیات ما مکر و حیله می‌کنند. بگو: «تدبیر الهی سریع‌تر و نافذتر است.» فرستادگان ما آنچه را مکر و نیرنگ می‌کنید، می‌نویسند.'),
(1386, 10, 22, 'او کسی است که شما را در خشکی و دریا سیر می‌دهد، تا آنگاه که در کشتی باشید و بادی موافق، کشتی‌ها را به جریان در آورد و [مسافران] بدین وسیله خوشحال شوند، ناگهان تندبادی بوزد و موج از هر سو سراغشان آید و گمان برند که در محاصره‌ی بلا گرفتارند، [در این هنگام،] خداوند را با اخلاص می‌خوانند [و می‌گویند:] «اگر ما را از این خطر نجات دهی، قطعاً از شاکران خواهیم بود.»'),
(1387, 10, 23, 'پس چون خداوند نجاتشان داد، در زمین به ناحقّ سرکشی می‌کنند. ای مردم! سرکشی شما فقط به زیانِ خودتان است. کامیابی زندگی دنیا [چند روزی بیش نیست]، سپس بازگشت شما به سوی ماست که شما را به عملتان آگاه خواهیم ساخت.'),
(1388, 10, 24, 'مَثَل زندگی دنیا، همچون آبی است که از آسمان فرود آوردیم. پس گیاهِ زمین از آنچه مردم و چهارپایان می‌خورند با آن در آمیخت [و رویید]، تا آنکه زمین [از آن گیاهان] زینت گرفت و اهل زمین پنداشتند که بر استفاده از آن توانایند. [ناگهان] فرمان ما شبی یا روزی آمد، پس آن را چنان درو کردیم که گویی دیروز گیاهی وجود نداشته است. ما این‌گونه آیات خود را برای گروه اندیشمند تشریح می‌کنیم.'),
(1389, 10, 25, 'و خداوند [مردم را] به سرای سلامت و سعادت، [بهشت] می‌خواند. و هر که را بخواهد، به راه راست هدایت می‌کند.'),
(1390, 10, 26, 'برای آنان که نیکی کنند، پاداشی نیکوتر و افزون‌تر خواهد بود. و بر چهره‌ی آنان غبار ذلّت و خواری ننشیند. آنان اهل بهشتند و همیشه در آنجایند.'),
(1391, 10, 27, 'و آنان که به دنبال کسب بدی‌ها رفتند، به قدر همان بدی‌ها مجازات دارند، و غبار ذلّت و خواری آنان را فرامی‌گیرد. از قهر خداوند، پناه‌دهنده‌ای ندارند، گویا چهره‌هایشان با پاره‌هایی از شبِ تیره پوشانده شده است. آنان اهل دوزخند و در آن جاودانه‌اند.'),
(1392, 10, 28, 'و روزی که همه را گردِ هم آوریم، به مشرکان می‌گوییم: شما و شریکانی که برای خدا می‌پنداشتید، در جای خود بمانید، پس میانشان جدایی می‌افکنیم و معبودها [به مشرکین] می‌گویند: «شما [در حقیقت] ما را نمی‌پرستیدید، [بلکه در پی هوس‌ها و اوهام خود بودید].'),
(1393, 10, 29, 'گواهی خدا میان ما و شما کافی است، به راستی ما از پرستش شما بی‌خبر بودیم.»'),
(1394, 10, 30, 'آنجا هر کس به [جزای] آنچه از پیش فرستاده، مبتلا شود و به سوی سرپرست حقیقی خودشان [خداوند] برگردانده شوند. و آنچه به افترا و دروغ، خدا می‌پنداشتند، از پیش چشمشان محو گردد.'),
(1395, 10, 31, 'بگو: «چه کسی از آسمان و زمین، شما را روزی می‌دهد؟ یا چه کسی مالک گوش و چشم‌هاست؟ و چه کسی زنده را از مرده، و مرده را از زنده، بیرون می‌آورد؟ و چه کسی کار هستی را سامان می‌دهد؟» خواهند گفت: «خدا.» بگو: «آیا از کیفر پرستش غیر خدا، پروا نمی‌کنید؟»'),
(1396, 10, 32, 'پس این چنین خداوند، به حقّ، پروردگار شماست. و پس از حقّ، جز گمراهی چیست؟ پس چگونه [از حقّ] بازگردانده می‌شوید؟!'),
(1397, 10, 33, 'بدین‌سان حکم و سخن پروردگارت بر آنان که نافرمانی کردند، محقّق شد، که آنان ایمان نمی‌آورند.'),
(1398, 10, 34, 'بگو: «آیا از معبودهایی که شما شریک خدا قرار داده‌اید، کسی هست که بتواند آفرینش را آغاز کند، سپس آن را بازگرداند؟» بگو: «[فقط] خداوند، آفرینش را آغاز می‌کند و سپس بازمی‌گرداند. پس چرا [از حقّ] بازگردانده می‌شوید؟!»'),
(1399, 10, 35, 'بگو: «آیا از معبودهایی که شما شریک خدا قرار داده‌اید، کسی هست که به سوی حقّ هدایت کند؟» بگو: «[فقط] خداوند به حقّ هدایت می‌کند. پس آیا کسی که به سوی حقّ هدایت می‌کند، برای پیروی شایسته‌تر است، یا کسی که هدایت نمی‌شود، مگر آنکه هدایتش کنند؟ شما را چه می‌شود؟ چگونه حکم می‌کنید؟!»'),
(1400, 10, 36, 'و بیشترِ آنان، جز از گمان پیروی نمی‌کنند. قطعاً گمان به هیچ وجه [انسان را] ازحقیقت بی‌نیاز نمی‌کند. همانا خداوند به آنچه مردم انجام می‌دهند، آگاه است.'),
(1401, 10, 37, 'و چنان نیست که این قرآن از سوی غیرخدا و به دروغ ساخته شده باشد؛ بلکه تصدیقی است برای کتب آسمانی پیشین و توضیحی از آن کتاب است. شکی نیست که از سوی پروردگار جهانیان است.'),
(1402, 10, 38, 'بلکه می‌گویند: «[محمّد] آن را به دروغ ساخته [و به خدا نسبت داده] است!» بگو: «اگر راست می‌گویید، پس سوره‌ای همانند آن بیاورید و هرکه را غیر از خدا می‌توانید، به یاری بخوانید؟»'),
(1403, 10, 39, 'بلکه چیزی را که به شناخت آن احاطه نداشتند، دروغ پنداشتند، در حالی که سرانجام و حقیقت و باطن آن، هنوز برایشان روشن نشده است.کسانی که پیش از آنان بودند نیز همین‌گونه تکذیب کردند. پس بنگر که سرانجام ستمگران چگونه است؟'),
(1404, 10, 40, 'و بعضی از آنان، کسانی هستند که به قرآن ایمان می‌آورند و برخی، کسانی که به آن ایمان نمی‌آورند. و پروردگارت به فسادکنندگان داناتر است.'),
(1405, 10, 41, 'و اگر تو را تکذیب کردند، بگو: «عمل من برای من، و عمل شما برای شما باشد. شما از آنچه من انجام می‌دهم، بیزارید و من نیز از آنچه شما انجام می‌دهید، بیزارم.»'),
(1406, 10, 42, 'گروهی از آنان به سخن تو گوش فرامی‌دهند، [امّا گویی کرند،] آیا تو می‌توانی سخن خود را به گوش ناشنوایان برسانی؟ هر چند اهل تعقّل نباشند؟'),
(1407, 10, 43, 'و گروهی از مردم کسانی‌اند که به تو نگاه می‌کنند، [امّا گویی هیچ نمی‌بینند.] پس آیا تو می‌توانی نابینایان را هدایت کنی، هر چند نمی‌بینند؟'),
(1408, 10, 44, 'قطعاً خداوند هیچ ظلمی به مردم نمی‌کند، امّا این خود مردمند که به خویشتن ستم می‌کنند.'),
(1409, 10, 45, 'و روزی که خداوند آنان را محشور می‌کند، گویا جز ساعتی از روز را [در دنیا یا برزخ] نمانده‌اند. آن روز یکدیگر را می‌شناسند. قطعاً آنان که دیدار خدا را تکذیب کردند، زیان‌کار شدند و هرگز هدایت‌یافته نبوده‌اند.'),
(1410, 10, 46, '[ای پیامبر!] بخشی از قهر و کیفری که به کافران وعده می‌دهیم، یا [در حال حیات] نشانت می‌دهیم، یا تو را از دنیا می‌بریم [و به کیفر می‌رسند، به هر حال] بازگشت آنان به سوی ماست و خداوند بر آنچه انجام می‌دهند، گواه است.'),
(1411, 10, 47, 'برای هر امّتی پیامبری است. پس هرگاه پیامبرشان بیاید، میان آنان به عدالت داوری می‌شود و ستمی به آنان روا نمی‌گردد.'),
(1412, 10, 48, 'کفّار می‌گویند: «اگر راست می‌گویید، این وعده [عذاب] چه وقت است؟»'),
(1413, 10, 49, 'بگو: «من [حتّی] برای خودم جز آنچه خدا بخواهد، مالک زیان و سودی نیستم [تا چه رسد برای شما]، برای هر امّتی اجل و سرآمدی معین است که هرگاه مدّتشان فرارسد، نه لحظه‌ای تأخیر دارند و نه پیشی می‌گیرند.»'),
(1414, 10, 50, 'بگو: «آیا اندیشیده‌اید، اگر عذاب خداوند شب یا روز به سراغ شما آید [چه می‌کنید؟]» گناهکاران چه چیزی را از او به شتاب می‌خواهند؟'),
(1415, 10, 51, 'آیا پس از آنکه [عذاب] نازل شد، به آن ایمان می‌آورید؟ حالا؟ در حالی که پیش‌تر خواستار زود آمدن عذاب بودید؟'),
(1416, 10, 52, 'سپس به ستمگران گفته شود: «عذاب همیشگی را بچشید! آیا جز به آنچه به دست آورده‌اید، کیفر داده می‌شوید؟»'),
(1417, 10, 53, 'و از تو خبر می‌گیرند که آیا آن [وعده‌ی مجازات] حقّ است؟ بگو: «آری! سوگند به پروردگارم که آن حتمی است، و شما نمی‌توانید [خدا را از انجام آن] عاجز کنید.»'),
(1418, 10, 54, 'اگر برای هر کس که ستم کرده، آنچه در زمین است، می‌بود، قطعاً همه‌ی آن را برای [نجات از قهر الهی و] بازخرید خود می‌داد. آنان با دیدن عذاب، پشیمانی خود را پنهان می‌کنند، و میانشان به عدالت داوری می‌شود و بر آنان ستم نرود.'),
(1419, 10, 55, 'آگاه باشید! آنچه در آسمان‌ها و زمین است، قطعاً از آنِ خداست. آگاه باشید که وعده‌ی خدا حتمی و راست است، لیکن بیشترشان نمی‌دانند.'),
(1420, 10, 56, 'اوست که زنده می‌کند و می‌میراند، و به سوی او بازگردانده می‌شوید.'),
(1421, 10, 57, 'ای مردم! بی‌گمان از سوی پروردگارتان پند و اندرزی برای شما آمد، که مایه‌ی شفاست برای آنچه در سینه‌های شماست، و هدایت و رحمتی برای مؤمنان است.'),
(1422, 10, 58, 'بگو: «[مؤمنان،] تنها به فضل و رحمت خداوند شاد شوند، که آن، بهتر از هرچیزی است که می‌اندوزند.»'),
(1423, 10, 59, 'بگو: «آیا دیدید هر رزقی که خداوند برای شما نازل کرده است، [از پیش خود] بعضی را حرام و بعضی را حلال قرار دادید؟» بگو: «آیا خداوند به شما اجازه داده، یا بر خداوند دروغ می‌بندید؟»'),
(1424, 10, 60, 'کسانی که به خدا دروغ می‌بندند، [درباره‌ی کیفرِ] روز قیامت چه گمانی دارند؟ همانا خداوند دارای بخشش و احسان بر مردم است؛ ولی بیشترشان سپاس‌گزاری نمی‌کنند.'),
(1425, 10, 61, 'و [تو] در هیچ حال [و اندیشه‌ای] نیستی، و هیچ بخشی از قرآن را نمی‌خوانی و [شما مردم!] هیچ عملی را انجام نمی‌دهید، مگر آنکه چون وارد آن [کار] می‌شوید، ما بر شما گواهیم و چیزی به وزنِ ذرّه‌ای، نه در زمین و نه در آسمان، از پروردگارت پوشیده نیست، و نه کوچک‌تر از آن و نه بزرگ‌تر، جز آنکه درکتاب روشن، ثبت است.'),
(1426, 10, 62, 'آگاه باشید که قطعاً بر اولیای خدا ترسی نیست و اندوهگین نمی‌شوند؛'),
(1427, 10, 63, 'آنان که ایمان آورده‌اند و شیوه‌ی آنان پرهیزکاری است.'),
(1428, 10, 64, 'برای آنان، در زندگی دنیا و آخرت بشارت است. تبدیل و تغییری در سخنان [و وعده‌های] الهی نیست، و آن همان رستگاری بزرگ است.'),
(1429, 10, 65, 'گفتار مخالفان، تو را غمگین نسازد؛ زیرا عزّت، به تمامی، از آن خداوند است، و او شنوا و داناست.'),
(1430, 10, 66, 'آگاه باشید! هر کس در آسمان‌ها و هر کس در زمین است، از آنِ خداست. و کسانی که به جای خداوند، شریکانی را می‌خوانند، [از آنها نیز] پیروی نمی‌کنند، بلکه جز گمان خود، چیز دیگری را پیروی نمی‌کنند. و آنان فقط دروغ می‌بافند.'),
(1431, 10, 67, 'اوست کسی که شب را برای شما قرار داد تا در آن آرامش یابید و روز را روشنی‌بخش گردانید [تا به کار بپردازید]. یقیناً در این [نظام حکیمانه و هدفدار،] نشانه‌هایی برای گروهی است که می‌شنوند.'),
(1432, 10, 68, '[مشرکان] گفتند: «خداوند فرزندی گرفته است!» او منزّه است! او بی‌نیاز است! آنچه در آسمان‌ها و زمین است، از آنِ اوست. هیچ دلیلی نزد شما بر این گفتار نیست. آیا آنچه را نمی‌دانید به خدا نسبت می‌دهید؟'),
(1433, 10, 69, 'بگو: «کسانی که بر خداوند دروغ می‌بندند، رستگار نمی‌شوند.»'),
(1434, 10, 70, 'بهره‌ای اندک در دنیا دارند، سپس بازگشت آنان به سوی ماست، آنگاه به سزای کفرشان، عذاب سخت به آنان می‌چشانیم.'),
(1435, 10, 71, 'و بر آنان سرگذشت نوح را بخوان، آنگاه که به قوم خود گفت: «ای قوم من! اگر موقعیت من [در میان شما] و یادآوری‌ام نسبت به آیات خداوند، بر شما سنگین و تحمّل‌ناپذیر است، پس [بدانید که] من تنها بر خداوند توکل کرده‌ام پس شما و شریکانتان فکرتان را جمع کنید تا چیزی از کارتان بر شما پوشیده نماند، سپس درباره‌ی من تصمیم بگیرید و مهلتم ندهید!'),
(1436, 10, 72, 'پس اگر پشت کردید، من از شما اجرتی نخواسته‌ام. پاداش من تنها بر خداست و من مأمورم که تسلیم فرمان او باشم.»'),
(1437, 10, 73, 'پس او را تکذیب کردند. آنگاه ما، او و کسانی را که در کشتی همراه او بودند، نجات دادیم و آنان را جانشینِ [در زمین] قرار دادیم. و همه‌ی کسانی که آیات ما را تکذیب کردند، غرق ساختیم. پس بنگر که پایانِ کارِ هشدار داده‌شدگان چگونه بود!'),
(1438, 10, 74, 'آنگاه پیامبرانی را پس از نوح به سوی قومشان فرستادیم، پس آنان معجزات و دلایل روشن را برای مردم آوردند؛ ولی مردم بر آن نبودند که به آنچه قبلاً تکذیبش کرده بودند، ایمان بیاورند. ما این‌گونه بر دل‌های متجاوزان مهر می‌زنیم.'),
(1439, 10, 75, 'سپس بعد از آنان، موسی و هارون را همراه با معجزات خویش، به سوی فرعون و سران قومش فرستادیم؛ امّا آنان که گروهی تبهکار بودند، تکبر ورزیدند.'),
(1440, 10, 76, 'پس هنگامی که از جانب ما حقّ به سراغشان آمد، گفتند: «قطعاً این یک جادوی آشکار است!»'),
(1441, 10, 77, 'موسی گفت: «آیا چون حقّ به سراغ شما آمد، می‌گویید: آیا این سحر است؟ در حالی که جادوگران هرگز رستگار نمی شوند.»'),
(1442, 10, 78, 'گفتند: «[ای موسی!] آیا آمده‌ای تا ما را از راهی که پدرانمان را بر آن یافته‌ایم، برگردانی، تا بزرگی و حکومت در زمین برای شما دو نفر باشد؟ و[لی] ما به شما دو نفر ایمان‌آور نیستیم.»'),
(1443, 10, 79, 'و فرعون گفت: «هر جادوگر کاردانی را نزد من بیاورید.»'),
(1444, 10, 80, 'پس چون جادوگران آمدند، موسی به آنان گفت: «آنچه را که می‌توانید بیافکنید، بیافکنید!»'),
(1445, 10, 81, 'پس چون [ساحران، ابزار خود را] افکندند، موسی گفت: «آنچه شما آورده‌اید، جادوست. خداوند به زودی آن را باطل خواهد کرد. چرا که خداوند، کار مفسدان را اصلاح نمی‌کند.»'),
(1446, 10, 82, 'و خداوند، حقّ را با کلمات و قدرت و سنّت‌های خویش تثبیت می‌کند، هر چند مجرمان از آن ناخشنود شوند!'),
(1447, 10, 83, '[در آغاز] فقط گروهی اندک از فرزندان قوم موسی، به او ایمان آوردند، در حالی که از فرعون و اشراف آنان ترس داشتند که آنان را شکنجه کنند؛ زیرا فرعون در آن سرزمین، برتری‌جوی و از اسرافکاران بود.'),
(1448, 10, 84, 'و موسی گفت: «ای قوم من! اگر به خداوند ایمان آورده‌اید و تسلیم او هستید، پس فقط بر او توکل کنید!»'),
(1449, 10, 85, 'آنان گفتند: «تنها بر خداوند توکل کرده‌ایم. پروردگارا! ما را گرفتار سختی‌ها و آزمایش ستمگران قرار مده.'),
(1450, 10, 86, 'و ما را به رحمت خویش، از شرّ قوم کافر رهایی بخش!»'),
(1451, 10, 87, 'و به موسی و برادرش وحی کردیم که در مصر، برای قوم خودتان، خانه‌هایی آماده کنید و خانه‌هایتان را مقابل هم بسازید. و نماز به پادارید. و به مؤمنان بشارت بده [که پیروزی با آنهاست].'),
(1452, 10, 88, 'و موسی گفت: «پروردگارا! تو به فرعون و بزرگان قومش در زندگی دنیا، زینت و اموال بسیار داده‌ای. که [به وسیله‌ی آنها،] مردم را از راه تو گمراه می‌سازند. پروردگارا! اموالشان را نابود کن و دل‌هایشان را سخت گردان؛ زیرا که آنان تا عذاب دردناک را نبینند، ایمان نخواهند آورد.»'),
(1453, 10, 89, '[خداوند] فرمود: «دعای شما دو تن مستجاب شد، پس پایدار باشید و از شیوه‌ی نادانان پیروی نکنید.»'),
(1454, 10, 90, 'و ما بنی‌اسرائیل را از دریا عبور دادیم. در حالی که فرعون و لشکریانش از روی ستم و تجاوز آنان را دنبال می‌کردند. تا چون غرقاب، فرعون را فراگرفت، گفت: «ایمان آوردم که معبودی نیست جز همان که بنی‌اسرائیل به او ایمان آورده‌اند، و من از تسلیم شدگانم.»'),
(1455, 10, 91, 'آیا اکنون!؟ در حالی که پیش‌تر نافرمانی می‌کردی و از تبهکاران بودی؟'),
(1456, 10, 92, 'پس امروز جسد تو را [از آب] نجات می‌دهیم تا عبرتی برای آیندگان باشی. یقیناً بسیاری از مردم از نشانه‌های ما غافلند!'),
(1457, 10, 93, 'و ما بنی‌اسرائیل را در جایگاه شایسته‌ای جا دادیم و از چیزهای پاکیزه به آنان روزی بخشیدیم؛ ولی آنان [به نزاع و اختلاف پرداختند. و] اختلاف نکردند، مگر پس از علم و آگاهی [به حقّانیت موسی]. همانا پروردگارت، روز قیامت در آنچه که در آن اختلاف می‌کردند، میان آنان داوری می‌کند.'),
(1458, 10, 94, 'پس اگر در آنچه بر تو نازل کردیم، شک داری، از آن کسانی که کتاب آسمانیِ پیش از تو را می‌خوانند، بپرس. یقیناً حقّ از پروردگارت به سوی تو آمده است، پس از شک‌آوران مباش.'),
(1459, 10, 95, 'و هرگز از کسانی که آیات الهی را تکذیب کردند، مباش که از زیانکاران خواهی بود.'),
(1460, 10, 96, 'کسانی که فرمان پروردگارت علیه آنان [به کیفر اعمالشان] تحقّق یافته، ایمان نمی‌آورند.'),
(1461, 10, 97, 'و اگرچه برای آنان هرگونه معجزه‌ای بیاید، تا آنکه عذاب دردناک الهی را ببینند [که ایمان، آن هنگام، بی‌ثمر است].'),
(1462, 10, 98, 'چرا اهل هیچ شهری [به موقع] ایمان نیاوردند که [ایمانشان] سود بخشد؟ مگر قوم یونس [که وقتی ایمان آوردند،] ما عذاب خوارکننده را در زندگی دنیا از آنان برطرف کردیم و تا مدّتی بهره‌مندشان ساختیم.'),
(1463, 10, 99, 'و اگر پروردگارت می‌خواست، [به اجبار] همه‌ی مردم روی زمین یکجا ایمان می‌آوردند. پس آیا تو مردم را مجبور می‌کنی تا ایمان آورند؟!'),
(1464, 10, 100, 'در حالی که هیچ کس جز به اذن و اراده‌ی الهی توفیق ایمان ندارد. و خداوند پلیدی [تردید و کفر] را بر کسانی که نمی‌اندیشند، مقرّر می‌دارد.'),
(1465, 10, 101, 'بگو: «[به دیده عبرت] بنگرید که در آسمان‌ها و زمین چیست؟» امّا نشانه‌ها و هشدارها برای کسانی که ایمان نمی‌آورند، سودی ندارد.'),
(1466, 10, 102, 'پس آیا [کسانی که در برابر آیات و انبیای الهی سرپیچی می‌کنند،] انتظار چیزی جز نظیر روزگارِ [پر نکبت و ذلّت] پیشینیان را دارند؟ بگو: «در انتظار باشید که من نیز با شما از منتظرانم!»'),
(1467, 10, 103, '[به هنگام نزول مجازات،] پیامبران و مؤمنان را نجات می‌دهیم. این‌گونه بر ما حقّ است که مؤمنان را رهایی بخشیم.'),
(1468, 10, 104, 'بگو: «ای مردم! اگر از دین من در شک و تردیدید، پس [بدانید که من] کسانی را که شما به جای خدا می‌پرستید، نمی‌پرستم؛ بلکه خدایی را می‌پرستم که جان شما را می‌گیرد و مأمورم که از مؤمنان باشم.'),
(1469, 10, 105, 'و به آیینی خالی از هرگونه شرک، روی آور و هرگز از مشرکان مباش.»'),
(1470, 10, 106, 'و غیر از خداوند، چیزی را که نه سودی برای تو دارد، نه زیانی، نخوان. پس اگر چنین کنی، همانا از ستمگران خواهی بود.'),
(1471, 10, 107, 'و اگر خداوند زیانی به تو برساند، جز او كسی توانِ برطرف كردن آن را ندارد. و اگر برای تو خیری بخواهد، هیچ كس مانع فضل او نخواهد شد. خیر را به هر كس از بندگانش كه بخواهد، می‌رساند. و او آمرزنده و مهربان است.'),
(1472, 10, 108, 'بگو: «ای مردم! حقّ از سوی پروردگارتان به سراغ شما آمده است. پس هر كس هدایت یابد، یقیناً به سود خویش هدایت می‌یابد و هر كس گمراه شود، به زیان خویش گمراه می‌شود. و من بر شما وكیل نیستم [تا به اجبار شما را به راه حقّ درآورم].»'),
(1473, 10, 109, 'و [تنها] آنچه را بر تو وحی می‌شود، پیروی كن و پایدار باش، تا آنكه خداوند داوری كند، و او بهترینِ داوران است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف، لام، را. [قرآن،] كتابی كه آیات آن استوار گشته، آن‌گاه از جانب حكیمی آگاه، شرح و تفصیل داده شده است.'),
(1475, 11, 2, 'كه: «جز خداوند یكتا را نپرستید! من از طرف او برای شما هشداردهنده و بشارت‌دهنده‌ام.'),
(1476, 11, 3, 'و از پروردگارتان طلب آمرزش كنید و به سوی او بازگردید تا شما را به بهره‌ای نیكو تا مدّتی معین [كه عمر دارید،] كامیاب كند، و به هر بافضیلتی فزونی بخشد. و اگر روی بگردانید، من از عذاب روزی بزرگ بر شما بیمناكم.'),
(1477, 11, 4, 'بازگشت شما به سوی خداست. و او بر هر چیزی تواناست.»'),
(1478, 11, 5, 'بدانید كه مخالفان برای آن كه خود را از پیامبر پنهان دارند، سر و سینه‌های خود را به هم نزدیک می‌كنند و لباس‌های خویش را بر سر می‌كشند، [غافل از آن كه] خداوند تمام كارهای پنهان و آشكار آنان را می‌داند. همانا او به اسرار درون سینه‌ها آگاه است.'),
(1479, 11, 6, 'و هیچ جنبنده‌ای در زمین نیست، مگر آن که روزی او بر خداست، و خداوند قرارگاه دائمی و جایگاه موقّت او را می‌داند. همه در کتاب آشکاری ثبت است.'),
(1480, 11, 7, 'او کسی است که آسمان‌ها و زمین را در شش روز [و دوران] آفرید و عرش او بر آب قرار داشت، تا شما را بیازماید که کدام یک نیکوکارترید. و اگر بگویی که: «شما پس از مرگ زنده خواهید شد.» کافران خواهند گفت: «این سحری آشکار است!»'),
(1481, 11, 8, 'و هرگاه عذاب را تا مدّت محدودی از آنان به تأخیر بیندازیم، می‌گویند: «چه چیزی جلوی عذاب ما را گرفت؟» بدانید روزی که قهر و عذاب ما به سراغشان بیاید، از ایشان بازگشتی نیست و آنچه بِدان مسخره می‌کردند، آنان را فراخواهد گرفت.'),
(1482, 11, 9, 'و اگر به انسان، از جانب خود نعمتی بچشانیم، سپس آن را از او بازپس‌گیریم، او نومید و ناسپاس خواهد بود.'),
(1483, 11, 10, 'و اگر پس از سختی و محنتی که به انسان رسیده، نعمتی به او بچشانیم، [چنان مغرور می‌شود که] می‌گوید: «گرفتاری‌ها از من دور شد [و دیگر به سراغم نخواهد آمد].» بی‌گمان او شادمان و فخرفروش است.'),
(1484, 11, 11, 'مگر کسانی که اهل صبر و عمل صالح هستند، [که نه با رفتن نعمت، نومید می‌شوند و نه با آمدنش، فخر می‌فروشند.] برای آنان مغفرت و پاداشی بزرگ خواهد بود.'),
(1485, 11, 12, 'پس شاید تو [به خاطر عدم پذیرش مردم، ابلاغِ] بعضی از آنچه را که به تو وحی می‌شود، واگذاری، و سینه‌ات به خاطر آن تنگ شده باشد که می‌گویند: «چرا گنجی بر او نازل نشده، یا فرشته‌ای با او نیامده!؟» تو فقط هشداردهنده‌ای. و خداوند بر هر چیزی ناظر و نگهبان است.'),
(1486, 11, 13, 'یا این که می‌گویند: «[او] قرآن را از پیش خود بافته و ساخته است!» بگو: «اگر راست می‌گویید، شما هم ده سوره مثل این از پیشِ‌خودساخته‌ها، بیاورید و [برای این کار،] غیر خدا هر کس را می‌خواهید دعوت کنید.»'),
(1487, 11, 14, 'پس اگر از عهده‌ی اجابت [درخواست] شما برنیامدند، بدانید آنچه نازل شده، به علم خداست و این که معبودی جز او نیست، پس آیا [در این صورت] تسلیم می‌شوید؟'),
(1488, 11, 15, 'کسانی که زندگانی دنیا و زیبایی‌های آن را بخواهند، ما در همین دنیا [نتیجه‌ی] اعمالشان را به طور کامل می‌دهیم، و در آن، هیچ کم و کاستی نخواهد بود.'),
(1489, 11, 16, 'آنان کسانی هستند که در قیامت، جز آتش چیزی ندارند. و آنچه در دنیا ساخته‌اند بر باد رفته، و اعمالی که انجام می‌دهند، باطل و بی‌اثر خواهد بود.'),
(1490, 11, 17, 'آیا آن کس که دلیل روشنی از طرف پروردگارش دارد و به دنبال او شاهدی از اوست، و پیش از او کتاب موسی [که] رهبر و رحمت بوده است، [بر آمدن او بشارت داده، مانند کسی است که این خصوصیات را نداشته باشد]؟ آنان [که حق‌جو هستند،] به او ایمان می‌آورند، و هر کس از گروه‌های مختلف به او کافر شود، وعده‌گاهش آتش است. پس، از آن در تردید مباش [که] قطعاً آن [وحی، کلامِ] حقّی است از سوی پروردگارت، اگر چه بیشتر مردم ایمان نیاورند.'),
(1491, 11, 18, 'و کیست ستمکارتر از آن که بر خداوند دروغ می‌بندد؟ آنان بر پروردگارشان عرضه می‌شوند و شاهدان خواهند گفت: «اینها همان کسانی هستند که بر پروردگارشان دروغ بستند. بدانید! لعنت خدا بر ستمگران باد!»'),
(1492, 11, 19, 'کسانی که [مردم را] از راه خدا بازمی‌دارند و می‌کوشند تا آن را کج جلوه دهند، در حالی که آنها خودشان به آخرت کافرند.'),
(1493, 11, 20, 'آنها در زمین عاجزکننده نیستند [و نمی‌توانند از خطر فرار کنند.] و در برابر خداوند هیچ یاوری ندارند. عذاب خدا نسبت به آنها مضاعف است. [چرا که هم خود گمراه بودند و هم دیگران را به گمراهی کشاندند.] آنان [از شدّت لجاجت و عناد،] توان شنیدن [حقّ] را ندارند و [آن را] نمی‌بینند.'),
(1494, 11, 21, 'آنها کسانی هستند که [سرمایه‌ی وجود] خویش را باخته و از آنچه به دروغ می‌ساختند [نیز] بازمانده‌اند.'),
(1495, 11, 22, 'به ناچار، آنان در قیامت، زیانکارترینِ افرادند.'),
(1496, 11, 23, 'کسانی که ایمان آورده و کارهای شایسته انجام دهند و در برابر پروردگارشان فروتن باشند، آنان اهل بهشتند و برای همیشه در آنجا خواهند ماند.'),
(1497, 11, 24, 'مَثلِ دو گروهِ [کافر و مؤمن،] همچون نابینا و ناشنوا، و بینا و شنواست. آیا این دو گروه در مَثَل یکسانند؟ پس چرا پند نمی‌گیرید؟'),
(1498, 11, 25, 'و ما نوح را به سوی قومش فرستادیم، [او به مردم گفت:] «من برای شما هشداردهنده‌ی روشنی هستم.'),
(1499, 11, 26, '[دعوت من این است] که جز خداوند را نپرستید، که من از عذاب روزی دردناک بر شما می‌ترسم.»'),
(1500, 11, 27, 'پس سران قومش که کافر بودند، گفتند: «ما تو را انسانی مثل خودمان می‌بینیم. و جز اراذل و اوباش که ساده‌لوحند، کس دیگری را پیرو تو نمی‌یابیم، و برای شما هیچ برتری بر خودمان نمی‌بینیم، بلکه شما را دروغگو می‌پنداریم.»'),
(1501, 11, 28, '[نوح] گفت: «ای قوم من! آیا اگر ببینید که من بر دلیل روشنی از طرف پروردگارم باشم و او از نزد خودش، رحمت [نبوّت] را به من داده باشد که بر شما مخفی مانده است، [آیا باز هم سرپیچی می‌کنید]؟ آیا شما را به پذیرش آن وادار کنیم، در حالی که نسبت به آن کراهت دارید؟'),
(1502, 11, 29, 'ای قوم من! از شما در برابر این دعوت، اجرتی درخواست نمی‌کنم، پاداش من تنها بر خداست. و من کسانی را که ایمان آورده‌اند، [به خواست شما] طرد نمی‌کنم، آنان پروردگارشان را ملاقات خواهند کرد، ولی من شما را قومی جاهل می‌بینم.'),
(1503, 11, 30, 'ای مردم! اگر من آنان را برانم، چه کسی مرا در برابر خدا یاری خواهد کرد؟ چرا نمی‌اندیشید؟'),
(1504, 11, 31, 'من به شما نمی‌گویم که گنجینه‌های الهی نزد من است و نه [این که از پیش خود] غیب می‌دانم، و نمی‌گویم که من فرشته‌ام، و نمی‌گویم آن کسانی که در پیشِ چشمِ شما خوارند، خداوند هرگز به آنان خیری نخواهد رساند؛ [بلکه] خداوند به آنچه در دل آنهاست، آگاه‌تر است. [اگر جز این بگویم،] قطعاً از ستمکاران خواهم بود.»'),
(1505, 11, 32, '[مخالفان] گفتند: «ای نوح! تو با ما جدال زیادی کردی، [دیگر بس است،] اگر راست می‌گویی، آنچه را به ما وعده می‌دهی، بر سر ما بیاور!»'),
(1506, 11, 33, '[نوح] گفت: «اگر خداوند اراده کند، آن را بر سر شما خواهد آورد، و شما قدرت خنثی‌کردن آن را نخواهید داشت.'),
(1507, 11, 34, 'و اگر خداوند بخواهد شما را [به خاطر بی‌لیاقتی] گمراه سازد، [دیگر] نصیحت من به حال شما سودی نخواهد داشت، هر چند بخواهم برای شما خیرخواهی کنم. او پروردگار شماست، و [شما] به سوی او بازخواهید گشت.»'),
(1508, 11, 35, 'یا [مشرکان درباره‌ی پیامبر] می‌گویند: «او، آن [سخنان] را بر خداوند افترا بسته است.» بگو: اگر من چیزی را به دروغ به خداوند نسبت داده‌ام، پس کیفرش بر عهده‌ی خودم خواهد بود. و من از جرم شما بیزارم.»'),
(1509, 11, 36, 'به نوح وحی گردید که جز کسانی که [تاکنون] ایمان آورده‌اند، از قوم تو هرگز ایمان نمی‌آورند، پس از کارهایی که می‌کنند، غمگین مباش.'),
(1510, 11, 37, 'و زیر نظر ما و طبق دستور ما، کشتی بساز. و درباره‌ی کسانی که ستم کرده‌اند، با من سخن مگوی که آنان غرق‌شدنی هستند.'),
(1511, 11, 38, 'و [نوح] مشغول ساختن کشتی شد. [امّا] هر زمان که گروهی از قومش بر او می‌گذشتند، او را مسخره می‌کردند. [او] گفت: «اگر شما ما را مسخره کنید، ما [نیز] همین‌گونه شما را مسخره خواهیم کرد.'),
(1512, 11, 39, 'پس به زودی خواهید دانست که مجازاتِ خوارکننده به سراغ چه کسی خواهد آمد و عذاب جاوِدانه دامن چه کسی را خواهد گرفت.»'),
(1513, 11, 40, '[کفر و استهزا ادامه داشت] تا زمانی که قهر ما آمد و تنور فوران کرد. [به نوح] گفتیم که از هر زوج، یک جفت، [نر و ماده] در آن [کشتی] سوار کن و خانواده‌ی خودت را، جز کسانی که قبلاً در مورد آنها قول [عذاب] داده شده بود. و افرادی را که ایمان آورده‌اند، [سوار کن، امّا] جز گروه کمی، کسی همراه او ایمان نیاورده بود.'),
(1514, 11, 41, 'و [نوح به مسافران کشتی] گفت: «بر آن سوار شوید که حرکت و توقّفش با نام خداست. همانا پروردگار من آمرزنده و مهربان است.»'),
(1515, 11, 42, 'و آن [کشتی] آنها را از لابه‌لای امواجی همچون کوه، پیش می‌برد. نوح، فرزندش را که در گوشه‌ای قرار داشت، صدا زد [و گفت]: «پسرم! با ما سوار شو و با کافران مباش!»'),
(1516, 11, 43, 'گفت: «به زودی به کوهی پناه می‌برم تا مرا از آب حفظ کند.» [نوح] گفت: «امروز جز کسانی که مورد رحم قرار گرفته‌اند، هیچ حافظ [و پناهگاهی] در مقابل قهر الهی نیست.» [در این هنگام] موجی میان آن دو، جدایی انداخت و او غرق گردید.'),
(1517, 11, 44, 'و گفته شد: «ای زمین! آبت را فرو بر، و ای آسمان! [از باریدن] بایست.» آب فرونشست و کار پایان پذیرفت و [کشتی] بر [دامنه کوهِ] جودی پهلو گرفت. و گفته شد: «[رحمت خدا از] قوم ستمگر دور باد!»'),
(1518, 11, 45, 'پس نوح پروردگارش را ندا داد و گفت: «پروردگارا! پسرم از خاندان من است و وعده‌ی تو حقّ است و تو بهترینِ داورانی.»'),
(1519, 11, 46, '[خداوند] فرمود: «ای نوح! او [در واقع] از خاندان تو نیست. او [دارای] کرداری ناشایست است، پس چیزی را که به آن علم نداری، از من مخواه! من تو را اندرز می‌دهم که [مبادا] از جاهلان باشی.»'),
(1520, 11, 47, '[نوح] گفت: «پروردگارا! به تو پناه می‌برم از این که چیزی را که به [خیر و شرّ] آن آگاهی ندارم، از تو درخواست کنم. و اگر تو بر من نبخشایی و بر من ترحّم نکنی، از زیانکاران خواهم بود.»'),
(1521, 11, 48, 'به نوح خطاب شد: [اینک] با سلامی از جانب ما و برکاتی بر تو و بر تمام امّت‌هایی که با تو هستند، فرود آی! و به زودی امّت‌هایی [از نسل همین نجات‌یافتگان] را از نعمت‌ها برخوردار خواهیم ساخت، [امّا] پس از آن، [به خاطر غفلت و کفر و گناه،] عذابی دردناک از طرف ما به آنان خواهد رسید.'),
(1522, 11, 49, '[ای پیامبر!] اینها از اخبار غیبی است که ما آنها را به تو وحی می‌کنیم [و] پیش از این، نه تو و نه قوم تو، از آنها خبری نداشتید، پس [تو نیز مانند نوح] صبر کن که همانا فرجام [نیک]، برای متّقین است.'),
(1523, 11, 50, 'و به سوی قوم عاد، برادرشان هود [را فرستادیم، او به مردم] گفت: «ای قوم من! خدا را بپرستید که هیچ معبودی جز او برای شما نیست. [شما در پرستش بت‌ها به خطا می‌روید و] جز اهل افترا نیستید.'),
(1524, 11, 51, 'ای قوم! من از شما در برابر رسالتم پاداشی درخواست نمی‌کنم. پاداش من تنها بر عهده‌ی کسی است که مرا آفریده است. پس آیا نمی‌اندیشید؟!'),
(1525, 11, 52, 'ای قوم! از پروردگارتان طلب آمرزش کنید، سپس به سوی او بازگردید و توبه نمایید تا او از آسمان پی در پی بر شما باران بفرستد، و نیرویی بر نیروی شما بیافزاید، و به خاطر گناه، روی برنتابید.»'),
(1526, 11, 53, '[مشرکان] گفتند: «ای هود! تو دلیل روشنی برای ما نیاورده‌ای، و ما به خاطر سخنان تو، از خدایانمان دست بر‌نمی‌داریم. و ما از ایمان آورندگانِ به تو نیستیم.'),
(1527, 11, 54, 'ما جز این نمی‌گوییم که بعضی از خدایان ما به تو زیان رسانده [و عقلت را ربوده‌اند. هود] گفت: «من خدا را گواه می‌گیرم و شما [نیز] شاهد باشید که من از آنچه شریک [خدا] قرار می‌دهید بیزارم.'),
(1528, 11, 55, 'از هر چه غیر اوست [بیزارم]، پس همگی علیه من توطئه کنید و مرا مهلت ندهید،'),
(1529, 11, 56, 'من بر خداوندی که پروردگار من و شماست، توکل کرده‌ام، هیچ جنبنده‌ای نیست، مگر آن که او، مهارش را گرفته است به درستی که پروردگار من بر راهی راست است.'),
(1530, 11, 57, 'پس اگر شما روی برگردانید، به راستی که من آنچه را به خاطر آن، به سوی شما فرستاده شدم، به شما رسانده‌ام. و پروردگارم گروه دیگری را جانشین شما خواهد کرد، و شما هیچ ضرری به او نمی‌رسانید. همانا پروردگارم بر هر چیزی نگهبان است.»'),
(1531, 11, 58, 'و زمانی که فرمان [قهر] ما آمد، هود و کسانی را که با او ایمان آورده بودند، به رحمت خود نجات دادیم و از عذاب سخت رهایشان ساختیم.'),
(1532, 11, 59, 'و آن [قوم] عاد، آیات پروردگارشان را منکر شدند و پیامبرانِ او را نافرمانی کرده، از فرمان هر ستمگر لجوجی پیروی نمودند.'),
(1533, 11, 60, 'و [سرانجام] در این دنیا و در روز قیامت، لعنت [و قهر الهی] در پی ایشان شد. بدانید که قوم عاد به پروردگارشان کفر ورزیدند. آگاه باشید که عاد، قوم هود [از رحمت الهی] دور باد!'),
(1534, 11, 61, 'و به سوی قوم ثمود، برادرشان صالح [را فرستادیم]، او گفت: «ای قوم من! خدای یگانه را بپرستید که جز او معبود دیگری برای شما نیست. اوست که شما را از زمین پدید آورد و از شما خواست تا در آن آبادانی کنید. پس، از او آمرزش بخواهید، سپس به سوی او بازگردید. همانا پروردگار من، نزدیک و اجابت‌کننده است.»'),
(1535, 11, 62, '[قوم ثمود] گفتند: «ای صالح! تو به راستی قبل از این، در میان ما مایه‌ی امید بودی. آیا تو ما را از پرستش آنچه پدرانمان می‌پرستیدند، نهی می‌کنی؟ ما نسبت به آنچه ما را به آن می‌خوانی، در شکی هستیم که ما را به تو بدگمان کرده است.»'),
(1536, 11, 63, '[صالح] گفت: «ای قوم من! آیا نظر شما این است که اگر من از طرف پروردگارم معجزه داشته باشم و از جانب او مشمول رحمت شده باشم، [لب فروبندم و تبلیغ نکنم؟] پس اگر من خدا را معصیت کنم، چه کسی مرا در برابر [قهر] او یاری خواهد کرد؟ پس شما جز خسارت و زیان، چیزی به من نمی‌افزایید.'),
(1537, 11, 64, 'ای قوم من! این شتر ماده [که به اراده‌ی] خداوند [آفریده شده،] معجزه‌ای برای شماست، پس او را آزاد بگذارید تا در زمین خدا [چرا کند و] بخورد، و آزاری به آن نرسانید که به زودی عذاب شما را فرا خواهد گرفت.»'),
(1538, 11, 65, 'پس شتر را پِی کردند [و دست و پایش را بریدند]، پس [صالح] گفت: «سه روز در خانه‌هایتان بهره‌مند باشید [که بعد از آن عذاب فراخواهد رسید]، این وعده‌ای است راست و حقیقی.»'),
(1539, 11, 66, 'چون فرمان ما آمد، صالح و کسانی را که به همراه او ایمان آورده بودند، به رحمت خود نجات دادیم و از خواری آن روز [رهایی بخشیدیم. ای رسول ما!] همانا پروردگارت، همان توانای شکست‌ناپذیر است.'),
(1540, 11, 67, 'و ستمگران را صیحه‌ای فراگرفت، پس در خانه‌هایشان به روی در افتادند [و مردند].'),
(1541, 11, 68, 'آن‌چنان که گویی هرگز در آنجا ساکن نبوده‌اند. بدانید که ثمود به پروردگارشان کفر ورزیدند. بدانید که ثمود [از رحمت الهی] دور باد!'),
(1542, 11, 69, 'و فرستادگان ما [که فرشتگانی به صورت انسان بودند]، ابراهیم را مژده آوردند و گفتند: «سلام!» [ابراهیم نیز] گفت: «سلام!» پس زمانی نگذشت که گوساله‌ی بریانی را نزد آنان آورد.'),
(1543, 11, 70, 'پس چون دید که دست آنان به سمت غذا دراز نمی‌شود، نسبت به آنان ناخشنود شد و ترسی از آنان در دل او افتاد، امّا میهمانان گفتند: «نترس! ما برای قوم لوط فرستاده شده‌ایم.»'),
(1544, 11, 71, 'و همسر ابراهیم ایستاده بود، پس خندید، زیرا ما او را به [فرزندی به نام] اسحاق و پس از اسحاق، یعقوب بشارت دادیم.'),
(1545, 11, 72, '[ساره] گفت: «ای وای بر من! آیا دارای فرزند می‌شوم، در حالی که من پیرزنم و این شوهرم پیرمرد؟! واقعاً که این چیز عجیبی است!»'),
(1546, 11, 73, '[فرشتگان] گفتند: «آیا از کار خدا تعجّب می‌کنی؟ رحمت و برکات الهی بر شما خاندان [رسالت] باد! همانا او [پروردگار] ستوده و بزرگوار است.»'),
(1547, 11, 74, 'پس چون ترس و وحشت از ابراهیم برطرف شد و بشارت [فرزند] برای او آمد، درباره قوم لوط با ما چون و چرا می‌کرد.'),
(1548, 11, 75, 'به درستی که ابراهیم، بردبار و دلسوز و اهل توبه بود.'),
(1549, 11, 76, '[فرشتگان گفتند:] ای ابراهیم! از این [تقاضا] صرف نظر کن، فرمان قهر پروردگارت صادر شده و عذابِ غیر قابل برگشت به سراغ آنها خواهد آمد.»'),
(1550, 11, 77, 'و هنگامی که فرستادگان ما، به سراغ لوط آمدند، از آمدن آنان غمگین شد و برای حفظ آنها [از آزار قوم شرور،] به تنگ آمد، [با خود] گفت: «امروز، روز سختی است!»'),
(1551, 11, 78, 'و قوم لوط که سابقه‌ی کارهای بد داشتند، شتابان به سراغ او آمدند. [لوط، به آنان] گفت: «ای قوم! اینها دختران منند، آنها برای شما پاک‌ترند، پس از خدا پروا کنید و مرا در پیش میهمانانم رسوا مسازید. آیا در [میان] شما یک جوانمرد رشید نیست؟»'),
(1552, 11, 79, '[قوم لوط] گفتند: «تو خود می‌دانی که برای ما هیچ حقّی نسبت به دختران تو نیست [و ما رغبتی به ازدواج نداریم]، خودت خوب می‌دانی که ما چه می‌خواهیم.»'),
(1553, 11, 80, '[لوط] گفت: «ای کاش در برابر شما قدرتی داشتم و یا به تکیه‌گاه محکمی پناه می‌بردم!»'),
(1554, 11, 81, '[میهمانان] گفتند: «ای لوط! ما فرستادگان پروردگار تو هستیم، آنان هرگز به تو دسترسی پیدا نخواهند کرد. پس، پاسی از شب گذشته، خانواده‌ات را [از این سرزمین] بیرون ببر و هیچ کس از شما [به پشت سرش] متوجّه نشود؛ ولی همسرت [را با خود همراه مبر، زیرا] آن بلایی که به همه می‌رسد، به او [نیز] خواهد رسید. وقت [نابودی] آنان صبح است. آیا صبح نزدیک نیست؟!»'),
(1555, 11, 82, 'پس هنگامی که فرمان [قهر] ما آمد، آن سرزمین را زیر و رو کردیم و بارانی از سنگ‌، از گل‌های لایه لایه بر آنها فروریختیم.'),
(1556, 11, 83, '[سنگ‌هایی که] نزد پروردگارت نشان‌دار بودند، و این [کیفر] از [سایر] ستمگران [و فاسدانی که در خط قوم لوط باشند،] دور نیست.'),
(1557, 11, 84, 'و به سوی مدین، برادرشان شعیب [را فرستادیم، او] گفت: «ای قوم من! خدا را بپرستید [که] جز او خدای دیگری برای شما نیست. و پیمانه و ترازو را [در هنگام خرید و فروش] کم نگذارید، من شما را به خیر [و صلاح] می‌بینم و به راستی که من از عذاب روزی فراگیر بر شما می‌ترسم.'),
(1558, 11, 85, 'و ای قوم من! پیمانه و ترازو را با انصاف و عدل پر کنید و [از] اشیا [و اجناس و حقوقِ] مردم [چیزی] را نکاهید و مفسدانه، در زمین تباهی مکنید.'),
(1559, 11, 86, 'ذخیره‌ی الهی برای شما بهتر است، اگر ایمان داشته باشید. و من نگهبان شما نیستم.»'),
(1560, 11, 87, '[امّا مردم مدین در جواب] گفتند: «ای شعیب! آیا نمازت به تو فرمان می‌دهد که [به ما بگویی] آنچه را که پدرانمان می‌پرستیدند، رها کنیم؟ یا این که [نتوانیم] آن‌گونه که خود می‌خواهیم، در اموالمان تصرّف کنیم؟! تو که مرد بردبار و فهمیده‌ای هستی!»'),
(1561, 11, 88, '[شعیب] گفت: «ای قوم! به نظر شما اگر من دلیل روشنی از پروردگارم داشته باشم و او مرا رزق نیکویی از سوی خود عطا کرده باشد، [چگونه می‌توانم بر خلاف فرمان او رفتار کنم؟] من نمی‌خواهم چیزی که شما را از آن بازمی‌دارم، خودم مرتکب شوم! من جز اصلاح به مقدار توانم، خواسته‌ی دیگری ندارم و جز به لطف خداوند، توفیقی برای من نیست، [از این روی] بر او توکل کرده‌ام و به سوی او بازمی‌گردم.'),
(1562, 11, 89, 'و ای قوم من! دشمنی و مخالفت با من، شما را به کاری وادار نسازد که [عذابی] مثل آنچه به قوم نوح، یا قوم هود یا قوم صالح رسید، به شما هم برسد. و [ماجرای] قوم لوط [چندان] از شما دور نیست.'),
(1563, 11, 90, '[پس بیایید] و از پروردگارتان آمرزش بطلبید و به سوی او بازگردید. همانا پروردگار من، مهربان و دوستدار [توبه‌کنندگان] است.»'),
(1564, 11, 91, '[کافران] گفتند: «ای شعیب! ما بسیاری از سخنان تو را درک نمی‌کنیم، و تو را در میان خود شخص ضعیفی می‌بینیم. و اگر بستگان تو نبودند، حتماً تو را سنگسار می‌کردیم و تو بر ما [هیچ] برتری و قدرت و عزّتی نداری.»'),
(1565, 11, 92, '[شعیب] گفت: «ای قوم من! آیا قبیله‌ی من نزد شما از خداوند عزیزتر است [که] شما [فرمان] او را پشت سر انداخته‌اید، همانا پروردگار من به آنچه می‌کنید، احاطه دارد.'),
(1566, 11, 93, 'و ای قوم من! هر چه در توان دارید، انجام دهید، من [نیز] کار خودم را خواهم کرد. به زودی خواهید دانست [که] عذاب خوارکننده به سراغ چه کسی خواهد آمد و چه کسی دروغگوست. شما در انتظار باشید، من [نیز] همراه شما منتظرم.»'),
(1567, 11, 94, 'و چون فرمان [قهر] ما آمد، شعیب و کسانی را که با او ایمان آورده بودند، به رحمت خود نجات دادیم و [آن گاه] صیحه‌ی [آسمانی] ستمگران را فرو گرفت، پس در خانه‌هایشان به رو افتادند [و مردند].'),
(1568, 11, 95, '[آن چنان] که گویی هرگز در آن منطقه ساکن نبوده‌اند. آگاه باشید [لطف خدا] از مردم مدین دور باد! همان گونه [که] از قوم ثمود دور شد.'),
(1569, 11, 96, 'و به راستی ما موسی را با معجزه و برهان‌های روشن فرستادیم.'),
(1570, 11, 97, '[او را] به سوی فرعون و اشراف قومش [فرستادیم]، اما آنها از فرمان فرعون پیروی کردند، در حالی که فرمان فرعون مایه‌ی رشد [و نجات] نبود.'),
(1571, 11, 98, 'فرعون در روز قیامت، پیشاپیش قومش حرکت می‌کند، پس آنها را وارد آتش می‌سازد. و چه بد جایگاهی است که به آن وارد می‌شوند!'),
(1572, 11, 99, 'و در این دنیا با لعنتی بدرقه شدند و روز قیامت نیز چنین خواهد بود. [و این] چه بد عطایی است که به آنان داده می‌شود.'),
(1573, 11, 100, '[ای پیامبر!] این [مطالب، گوشه‌ای] از اخبار آبادی‌ها و شهرهایی است که ما آن را برای تو بازگو می‌کنیم، بعضی از آنها [هنوز] پابرجا هستند، ولی بعضی دیگر ویران شده‌اند.'),
(1574, 11, 101, 'و ما به آنان ظلم نکردیم، بلکه آنان خود بر خویشتن ستم روا داشتند. و چون قهر پروردگارت آمد، خدایانی که به جای اللّه می‌خواندند، سودی به حال آنان نبخشید و آنان را جز ضرر و هلاکت چیزی نیافزود.'),
(1575, 11, 102, 'و این چنین است مؤاخذه [و مجازاتِ] پروردگارت، زمانی که شهرها و آبادی‌های ستمگر را [با قهر خود] می‌گیرد. همانا مؤاخذه‌ی او دردناک و شدید است.'),
(1576, 11, 103, 'البتّه در این [یادآوری‌ها،] نشانه [و عبرتی] است برای کسی که از عذاب آخرت بترسد. همان روزی که مردم را در آن گرد می‌آورند و آن روزی که [همه آن را] مشاهده می‌کنند و برای همه نمایان و مشهود است.'),
(1577, 11, 104, 'و ما، آن [روز] را جز تا زمانی چند، به تأخیر نمی‌اندازیم.'),
(1578, 11, 105, 'روزی که [چون] بیاید، هیچ کس جز با اذن او حرفی نمی‌زند، پس [گروهی] از آنها بدبخت و سیه‌روز، و [عدّه‌ای دیگر] خوشبخت و سعادتمندند.'),
(1579, 11, 106, 'امّا کسانی که بدبخت شده‌اند، پس در آتش ناله‌ای [زار] و خروشی [سخت] دارند.'),
(1580, 11, 107, '[و] تا آسمان‌ها و زمین پایدار است، در آن [آتش] جاودانند، مگر آنچه خداوند بخواهد. همانا پروردگارت هر چه را اراده کند، انجام می‌دهد.'),
(1581, 11, 108, 'و امّا کسانی که خوشبخت شده‌اند، پس تا آسمان‌ها و زمین پابرجاست، در بهشت، جاودانند، مگر آنچه پروردگارت بخواهد. [این] عطایی قطع ناشدنی است.'),
(1582, 11, 109, 'پس [ای پیامبر!] از آنچه این کفّار می‌پرستند، در شک و تردید مباش، آنها عبادت نمی‌کنند مگر همان‌گونه که قبلاً پدرانشان [بت‌ها را] عبادت می‌کردند، و ما نصیب آنها را به طور کامل و بی‌کم و کاست خواهیم پرداخت.'),
(1583, 11, 110, 'و به راستی ما به موسی کتاب دادیم، پس در آن اختلاف شد. و اگر سنّت پروردگارت [در مورد تأخیر عذاب کفّار] از پیش مقرّر نگشته بود، حتماً [در همین دنیا] میان آنان داوری و حکم می‌شد. و این یهودیان درباره‌ی تورات، در تردیدی آمیخته با بدگمانی‌اند.'),
(1584, 11, 111, 'و قطعاً پروردگارت [جزای] تمام اعمال آنان را به طور کامل خواهد داد. او به آنچه [مردم] عمل می‌کنند، آگاه است.'),
(1585, 11, 112, 'پس [ای پیامبر!] همان‌گونه که مأمور شده‌ای، استوار باش و [نیز] هر کس که با تو، به سوی خدا آمده است، [استوار باشد،] و سرکشی نکنید که او به آنچه می‌کنید، بیناست.'),
(1586, 11, 113, 'و به ستمگران تمایل و تکیه نکنید که آتشِ [عذاب] شما را فرامی‌گیرد. و [در این صورت] برای شما در برابر خداوند، هیچ دوست و سرپرستی نیست، پس [از هیچ ناحیه‌ای] مورد کمک قرار نخواهید گرفت.'),
(1587, 11, 114, 'و نماز را در دو طرف روز و اوایل شب به پادار، همانا کارهای نیکو [همچون نماز]، بدی‌ها را محو می‌کند. این [فرمان]، تذکری است برای اهل ذکر.'),
(1588, 11, 115, 'و پایدار باش، که خداوند پاداش نیکوکاران را ضایع نمی‌کند.'),
(1589, 11, 116, 'پس چرا در قرن‌های قبل از شما، صاحبان علم و قدرتی نبودند تا مردم را از فساد در زمین بازدارند؟ مگر گروه کمی از کسانی که از میان آنان نجاتشان دادیم. و ستمگران، دنباله‌رو و دلبسته‌ی مال و مقامی شدند که در آن مست و سرکش شده بودند. و آنان مردمی مجرم و گناهکار بودند.'),
(1590, 11, 117, 'و پروردگارت بر آن نبوده است که آبادی‌هایی را که مردمش اصلاح‌گرند، به ظلم و ستم هلاک کند.'),
(1591, 11, 118, 'و اگر پروردگارت می‌خواست، همه‌ی مردم را [با اجبار]، یک امّت واحده [و دارای یک عقیده‌] قرارمی‌داد، در حالی که همچنان اختلاف می‌ورزند.'),
(1592, 11, 119, 'مگر کسی که پروردگارت به او رحم کند، و برای همین [رحمت،] مردم را آفرید. و فرمان پروردگارت صادر شده که دوزخ را از جنّ و انس پر خواهد کرد.'),
(1593, 11, 120, 'و هر یک از سرگذشت پیامبران، که برای تو بازگو کردیم، چیزی است که دلت را بدان محکم گردانیم، و در این [اخبار]، برای تو حقایق و برای اهل ایمان، پند و تذکری آمده است.'),
(1594, 11, 121, 'و به کسانی که ایمان نمی‌آورند، بگو: «هرچه در توان دارید، انجام دهید، ما نیز انجام می‌دهیم.'),
(1595, 11, 122, 'و شما در انتظار باشید، ما هم منتظریم»'),
(1596, 11, 123, 'و نهانِ آسمان و زمین تنها برای خداوند است و تمام امور به او بازگردانده می‌شود. پس بنده‌ی او باش و بر او توکل کن. و پروردگارت از عملکرد شما غافل نیست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، را. این است آیات کتاب روشنگر.'),
(1598, 12, 2, 'ما آن را قرآنی عربی [و فصیح و گویا] نازل کردیم، تا مگر بیندیشید.'),
(1599, 12, 3, '[ای پیامبر!] ما با وحیِ این قرآن به تو، بهترینِ داستان‌ها را برایت حکایت می‌کنیم، در حالی که پیش از این، از آن آگاه نبودی.'),
(1600, 12, 4, 'آن‌گاه که یوسف به پدرش گفت: «پدر! من [درخواب] دیدم که یازده ستاره، و خورشید و ماه در برابرم سجده می‌کنند.»'),
(1601, 12, 5, '[پدر] گفت: «پسر عزیزم! خوابت را برای برادرانت بازگو مکن، که برایت نقشه‌ای [خطرناک] می‌کشند. بی‌گمان شیطان، دشمنی آشکار برای انسان است.'),
(1602, 12, 6, 'و پروردگارت، این‌گونه تو را برمی‌گزیند، و از [دانشِ] تعبیر خواب به تو می‌آموزد و نعمتش را بر تو و بر خاندان یعقوب تمام می‌کند، چنان که پیش از این بر پدرانت ابراهیم و اسحاق تمام کرد. همانا پروردگارت دانای حکیم است.»'),
(1603, 12, 7, 'بی‌تردید در [داستان] یوسف و برادرانش، نشانه‌ها [و عبرت‌ها]یی برای اهل [تحقیق و] پرسش است.'),
(1604, 12, 8, 'آن‌گاه که [برادران او] گفتند: «یوسف و برادرش [بنیامین] نزد پدرمان از ما محبوب‌ترند. در حالی که ما مردانی نیرومندیم.» بی‌تردید پدرمان [در این مهرورزی] در اشتباهی روشن است.»'),
(1605, 12, 9, '[برادران به یکدیگر گفتند:] «یوسف را بکشید، یا او را به سرزمینی دور بیفکنید تا توجه پدر فقط به سوی شما باشد. و پس از آن، [با توبه و عذرخواهی از پدر] افرادی صالح خواهید بود.»'),
(1606, 12, 10, 'یکی از آنان گفت: «یوسف را نکشید.، و اگر می‌خواهید کاری بکنید، او را به نهان‌خانه‌ی چاه بیفکنید تا کاروانی [که از آنجا عبور می‌کند،] او را برگیرد [و با خود به مکان دوری ببرد].»'),
(1607, 12, 11, '[آنها نزد پدر آمدند و] گفتند: «ای پدر! تو را چه شده که ما را بر یوسف امین نمی‌دانی؟ با آن که بی‌تردید ما خیرخواه او هستیم؟!»'),
(1608, 12, 12, '«فردا او را با ما بفرست تا [در صحرا] بگردد و بازی کند. و ما نگهداران [خوبی] برای او خواهیم بود.»'),
(1609, 12, 13, '[پدر] گفت: «بردن او مرا سخت اندوهگین می‌کند، و از این می‌ترسم که شما از او غافل شوید و گرگ او را بخورد.»'),
(1610, 12, 14, '[برادران] گفتند: «اگر با بودن ما که گروهی نیرومندیم، گرگ او را بخورد، در آن صورت ما زیانکار [و بی‌کفایت] خواهیم بود.»'),
(1611, 12, 15, 'پس چون او را [با خود] بردند و تصمیم گرفتند که او را در مخفی‌گاه چاه قرار دهند [چنین کردند] و ما [در همان چاه] به او وحی کردیم که در آینده آنها را از این کارشان خبر خواهی داد، در حالی که آنها نمی‌دانند [که تو همان یوسفی].'),
(1612, 12, 16, 'و شب هنگام، گریان نزد پدرشان آمدند.'),
(1613, 12, 17, 'گفتند: «ای پدر! ما رفتیم مسابقه بدهیم و یوسف را نزد وسایل خود گذاشتیم، اما گرگ او را خورد! و البته تو سخن ما را باور نداری، هر چند راستگو باشیم.»'),
(1614, 12, 18, 'و پیراهن یوسف را، آغشته به خونی دروغین [نزد پدر] آوردند. [یعقوب] گفت: « [چنین نیست که می‌گویید،] بلکه نفسِ شما کاری [زشت] را برای شما آراسته است. پس صبری نیکو [باید]. و بر آنچه وصف می‌کنید، باید از خدا یاری جست.»'),
(1615, 12, 19, 'و [یوسف در چاه بود تا آن که] کاروانی فرارسید و آب‌آور خود را فرستادند. او دَلو خود را به چاه افکند. [یوسف به طناب دلو آویخت. چون یوسف را بالا کشید،] گفت: «مژده باد! این پسری است [زیبا و دوست داشتنی]!» و او را چون کالایی پنهان داشتند [تا کسی ادّعای مالکیت نکند،] در حالی که خداوند به آنچه انجام می‌دادند، آگاه بود.'),
(1616, 12, 20, 'و یوسف را به بهایی اندک، چند درهمی فروختند و نسبت به [فروختن] او بی‌رَغبَت بودند [چرا که می‌ترسیدند رازشان فاش شود].'),
(1617, 12, 21, 'و کسی از [مردم] مصر که یوسف را خرید، به همسرش گفت: «مقام او را گرامی بدار [و او را به دید برده نگاه مکن]، امید است که در آینده برای ما سودمند باشد، یا او را به فرزندی انتخاب کنیم.» و این‌گونه به یوسف، در آن سرزمین، جایگاه [ویژه] و امکانات دادیم تا [اراده ما تحقق یابد و] از دانش تعبیر خواب به او بیاموزیم. و خداوند بر کار خویش تواناست، ولی بیشتر مردم نمی‌دانند.'),
(1618, 12, 22, 'و چون [یوسف] به رشد و قوّت خود رسید، به او حکمت و دانش عطا کردیم. و ما این‌گونه نیکوکاران را پاداش می‌دهیم.'),
(1619, 12, 23, 'و زنی که یوسف در خانه او بود، از یوسف تمنای کامجویی کرد و [برای انجام مقصودش] درها را محکم بست و گفت: «بشتاب، که من از برای تو آماده‌ام.» [یوسف] گفت: «پناه بر خدا که او پروردگار من است و مقام مرا گرامی داشته، [من هرگز به پروردگارم خیانت نمی‌کنم،‌] به یقین ستمگران رستگار نمی‌شوند.»'),
(1620, 12, 24, 'و بی‌گمان [همسر عزیز مصر] قصد او کرد و یوسف نیز اگر برهان پروردگارش را نمی‌دید، قصد او می‌کرد. این‌گونه [ما او را یاری کردیم] تا بدی و فحشا را از او دور سازیم، چرا که او از بندگان خالص‌شده‌ی ما بود.'),
(1621, 12, 25, 'و هر دو به سوی در دویدند و آن زن [که یوسف را تعقیب می‌کرد،] پیراهن او را از پشت درید. ناگهان شوهرش را کنار [آستانه‌ی‌] در یافتند. آن زن [با چهره‌ای حق به جانب برای انتقام از یوسف و تبرئه خویش] گفت: «کیفر کسی که به همسر تو قصد بدی داشته باشد، جز زندان یا شکنجه دردناک چیست؟»'),
(1622, 12, 26, '[یوسف] گفت: «او بود که از من، کام خواست.» و در این هنگام، شاهدی از خانواده زن، شهادت داد که: «اگر پیراهن یوسف از جلو پاره شده، زن راست می‌گوید و او از دروغگویان است.'),
(1623, 12, 27, 'و اگر پیراهن او از پشت پاره شده، زن دروغ می‌گوید و او از راستگویان است.»'),
(1624, 12, 28, 'پس همین که [عزیز مصر] دید پیراهن او از پشت پاره شده است، گفت: «بی‌شک این از نیرنگ شما زنان است. به راستی که نیرنگ شما بزرگ است.»'),
(1625, 12, 29, '«یوسف! از [افشای] این ماجرا صرف‌نظر کن.» و «تو نیز [ای زن] برای گناهت استغفار کن، که قطعاً از خطاکاران بوده‌ای.»'),
(1626, 12, 30, 'گروهی از زنان شهر [که خبردار شدند، زبان به ملامت گشودند و] گفتند: «همسر عزیز از غلام جوانش تمنّای کامجویی کرده.» «و عشق [این جوان]، در قلب او نفوذ کرده است.» «ما او را در گمراهیِ آشکاری می‌بینیم.»'),
(1627, 12, 31, 'پس چون [همسر عزیز، بدگویی زنان مصر و] نیرنگ آنان را شنید، کسی را به سراغ آنها فرستاد و [مجلس باشکوهی ترتیب داد و] برای آنان پشتی [های گران‌بها] آماده کرد و به هر یک چاقویی داد [تا میوه میل کنند]. در این حال، به یوسف گفت: «بر زنان وارد شو!» همین که او را دیدند، بزرگش یافتند و [چُنان مبهوت او شدند که به جای میوه] دست‌های خود را بریدند و گفتند: «پناه بر خدا! این بشر نیست. بلکه فرشته‌ای بزرگوار است!»'),
(1628, 12, 32, '[همسر عزیز] گفت: «این همان کسی است که مرا به خاطر [عشق] او سرزنش می‌کردید. من از او کام خواستم، ولی او خودداری کرد. و اگر آنچه را به او دستور می‌دهم، انجام ندهد، حتماً زندانی خواهد شد و از خوار شدگان خواهد بود.»'),
(1629, 12, 33, '[یوسف] گفت: «پروردگارا! زندان نزد من، محبوب‌تر است از آنچه مرا به سوی آن می‌خوانند. و اگر نیرنگشان را از من نگردانی، به آنان می‌گرایم و از جاهلان خواهم بود.»'),
(1630, 12, 34, 'پروردگارش [درخواست] او را اجابت کرد و نیرنگ زنان را از او گرداند، همانا او شنوای داناست.'),
(1631, 12, 35, 'آن‌گاه، با این که نشانه‌ها [و دلایلی بر پاکدامنی یوسف] دیدند، تصمیم گرفتند که او را تا مدتی زندانی کنند.'),
(1632, 12, 36, 'و با یوسف، دو جوان دیگر وارد زندان شدند. یکی از آن دو گفت: «من در خواب، خودم را دیدم که [برای‌] شراب، [انگور] می‌فشارم.» و دیگری گفت: «من خود را در خواب دیدم که بر سرم نانی می‌برم و پرندگان از آن می‌خورند. ما را از تعبیر خوابمان آگاه ساز که ما تو را از نیکوکاران می‌بینیم.»'),
(1633, 12, 37, '[یوسف] گفت: «من پیش از آن که جیره‌ی غذایی شما برسد، شما را از تأویل خوابتان آگاه خواهم ساخت. این از حقایقی است که پروردگارم به من آموخته است. من آیین قومی را که به خدا ایمان ندارند و به قیامت کفر می‌ورزند، ترک گفتم.'),
(1634, 12, 38, 'و آیین پدرانم، ابراهیم و اسحاق و یعقوب را پیروی کردم. برای ما سزاوار نیست که چیزی را شریک خداوند قرار دهیم. این از فضل خدا بر ما و بر مردم است، ولی بیشتر مردم سپاس‌گزاری نمی‌کنند.'),
(1635, 12, 39, 'ای دو یار زندانی من! آیا خدایان متعدد و گوناگون بهتر است، یا خداوند یکتای مقتدر؟'),
(1636, 12, 40, 'آنچه شما جز خداوند می‌پرستید، اسم‌هایی [بی‌مسمّا] هستند که شما و پدرانتان آنها را [خدا] نامیده‌اید. خداوند هیچ دلیلی بر [حقانیت] آن نفرستاده است. کسی جز خداوند حق فرمانروایی ندارد، او دستور داده که غیر او را نپرستید. این دین پابرجا و استوار است، ولی بیشتر مردم نمی‌دانند.'),
(1637, 12, 41, 'ای دوستان زندانیم! امّا [تعبیر خواب شما چنین است که] یکی از شما دو نفر [آزاد می‌شود و] به ارباب خود باده می‌نوشاند. و اما دیگری به دار آویخته می‌شود، [و آن قدر بالای دار می‌ماند که] پرندگان از سر او می‌خورند. امری که درباره آن از من نظر خواستید، حتمی و قطعی است.»'),
(1638, 12, 42, 'و [آن‌گاه یوسف] به آن زندانی که می‌دانست آزاد می‌شود، گفت: «مرا نزد ارباب خود [، پادشاه مصر] به یاد آور.» [ولی] شیطان یادآوری نزد اربابش را، از خاطرِ او برد و یوسف چند سالی در زندان ماند.'),
(1639, 12, 43, 'و [روزی] پادشاه [مصر] گفت: «من در خواب هفت گاو چاق دیدم که هفت گاو لاغر آنها را می‌خورند، و هفت خوشه‌ی سبز و [هفت خوشه‌ی] خشکیده‌ی دیگر [که خشکیده‌ها بر سبزها پیچیدند و آنها را از بین بردند.]. ای بزرگان قوم! اگر تعبیر خواب می‌کنید، درباره‌ی خوابم به من نظر بدهید.»'),
(1640, 12, 44, 'گفتند: «اینها خواب‌هایی پریشان است، و ما تعبیر خواب‌های آشفته را نمی‌دانیم.»'),
(1641, 12, 45, 'و یکی از آن دو [زندانی] که نجات یافته و پس از گذشت مدتی [یوسف را] به خاطر آورده بود، گفت: «من از تعبیر آن به شما خبر می‌دهم، مرا [به سراغ آن جوان زندانی] بفرستید.»'),
(1642, 12, 46, '«ای یوسف! ای دوست راستگوی! درباره‌ی این [خواب] که هفت گاو چاق را هفت گاو لاغر می‌خورند و هفت خوشه‌ی سبز و [هفت خوشه‌ی] خشکیده دیگر، به ما نظر بده تا به سوی مردم برگردم، شاید آنان [از دانش و بزرگواری تو] آگاه شوند.»'),
(1643, 12, 47, '[یوسف] گفت: «هفت سالِ پیاپی کشت کنید و هر چه درو کردید، جز اندکی را که می‌خورید، در خوشه‌هایش کنار بگذارید [و ذخیره نمایید].'),
(1644, 12, 48, 'آن‌گاه پس از آن، هفت سالِ سخت می‌آید که آنچه را برای آن سال‌ها اندوخته‌اید، خواهید خورد، جز اندکی که [برای بذر] ذخیره می‌کنید.'),
(1645, 12, 49, 'سپس بعد از آن، سالی فرامی‌رسد که باران فراوان نصیب مردم می‌شود و در آن سال، [به خاطر وسعت و فراوانی، مردم از میوه‌ها و دانه‌های روغنی] عصاره می‌گیرند.»'),
(1646, 12, 50, 'و پادشاه گفت: «او را نزد من بیاورید.» ولی هنگامی که فرستاده‌ی پادشاه نزد وی آمد، یوسف گفت: «نزد سرورت برگرد و از او بپرس که ماجرای زنانی که دست‌های خود را بریدند، چه بود؟ بی‌گمان پروردگار من به نیرنگ آنان آگاه است.»'),
(1647, 12, 51, '[پادشاه، آن زنان را طلبید و] گفت: «وقتی یوسف را به سوی خویش دعوت کردید، سرانجام کار شما چه شد؟» زنان گفتند: «پناه بر خدا! ما هیچ‌گونه خِلافی از او ندیدیم.» همسر عزیز [هم] گفت: «اکنون حقیقت آشکار شد. من از او کام خواستم، و بی‌شک او از راستگویان است.»'),
(1648, 12, 52, '[یوسف گفت:] «این [اعاده‌ی حیثیت] برای آن بود که [عزیز مصر] بداند من در نهان به او خیانت نکرده‌ام. و بداند که خداوند نیرنگ خائنان را به سامان نمی‌رساند.'),
(1649, 12, 53, 'و من نفس خود را تبرئه نمی‌کنم، چراکه نفس آدمی همواره به بدی امر می‌کند، مگر آن که پروردگارم رحم کند. پروردگار من آمرزنده‌ی مهربان است.»'),
(1650, 12, 54, 'و پادشاه گفت: «یوسف را نزد من آورید تا وی را [مشاور] ویژه‌ی خود قرار دهم.» پس چون با او صحبت کرد، گفت: «امروز، تو نزد ما دارای جایگاهی والا و فردی امین هستی»'),
(1651, 12, 55, '[یوسف] گفت: «مرا سرپرست خزانه‌های این سرزمین قرار ده، که من در حفظ آن، توانا و آگاهم.»'),
(1652, 12, 56, 'و بدین‌سان، ما به یوسف در آن سرزمین، امکانات و قدرت بخشیدیم تا هر جا بخواهد، مقام گزیند. ما رحمت خود را به هرکس بخواهیم [و شایسته ببینیم]، می‌رسانیم. و پاداش نیکوکاران را تباه نمی‌گردانیم.'),
(1653, 12, 57, 'و قطعاً برای کسانی که ایمان آورده و همواره تقوا پیشه کرده‌اند، پاداش آخرت بهتر است.'),
(1654, 12, 58, '[قحطی فرارسید] و برادران یوسف [در پی گندم، به مصر] آمدند و بر او وارد شدند. یوسف آنان را شناخت، ولی آنها او را نشناختند.'),
(1655, 12, 59, 'و چون یوسف بارهای آنان را آماده ساخت، گفت: [نوبت آینده] برادری را که از پدرتان دارید، نزد من بیاورید. آیا نمی‌بینید که من پیمانه را کامل می‌دهم و بهترین میزبان هستم؟'),
(1656, 12, 60, 'پس اگر او را نزد من نیاوردید، سهمی نزد من نخواهید داشت و پیش من هم نیایید.»'),
(1657, 12, 61, '[برادران] گفتند: «او را با اصرار و التماس از پدرش خواهیم خواست، و حتماً [این کار را] خواهیم کرد.»'),
(1658, 12, 62, 'و [یوسف] به کارگزاران خود گفت: «بهایی را که پرداخته‌اند، در بارهایشان بگذارید، تا چون نزد خانواده‌ی خود بازگشتند، آن را بشناسند و [بار دیگر] بازگردند.»'),
(1659, 12, 63, 'هنگامی که نزد پدرشان بازگشتند، گفتند: «ای پدر! سهمیه ما قطع شد. برادرمان [بنیامین] را با ما بفرست تا سهمیه و پیمانه خود را بگیریم. و ما حتماً نگهبان او هستیم.»'),
(1660, 12, 64, '[یعقوب] گفت: «آیا همان‌گونه که پیش از این، شما را نسبت به برادرش [یوسف] امین دانستم، درباره او هم امین بدانم؟ پس [او را به خدا می‌سپارم که] خداوند بهترین نگاهبان است و او مهربان‌ترینِ مهربانان است.»'),
(1661, 12, 65, 'و هنگامی که بارهای خود را گشودند، دریافتند که اموالشان را به خودشان بازگردانده‌اند. گفتند: «ای پدر! [دیگر] چه می‌خواهیم؟ این سرمایه‌ی ماست که به ما باز پس داده شده و [بار دیگر که می‌رویم، با آن] آذوقه‌ی خانواده‌ی خود را فراهم می‌کنیم و برادرمان را [نیز با خود می‌بریم و از او] حفاظت می‌کنیم. و یک بار شتر زیادتر می‌گیریم. این [که آورده‌ایم] پیمانه‌ای اندک است.'),
(1662, 12, 66, '[پدر] گفت: «من هرگز او را با شما نخواهم فرستاد تا در پیشگاه خداوند [سوگند بخورید و] به من پیمانی محکم بسپارید که او را نزد من بازگردانید، مگر آن که همه شما گرفتار [حادثه‌ای‌] شوید.» پس چون به او پیمان سپردند، گفت: «خداوند بر آنچه می‌گوییم، وکیل است.»'),
(1663, 12, 67, 'و گفت: «ای پسران من! [چون به مصر رسیدید، همه] از یک دروازه [به شهر] وارد نشوید، بلکه از دروازه‌های مختلف وارد شوید [تا توجه مردم به شما جلب نشود]. و [البته با این سفارش،] من نمی‌توانم چیزی از [مقدَّرات] خدا را از شما دور کنم. حکم و فرمان، تنها از آن خداست. بر او توکل دارم و توکل‌کنندگان [نیز] باید تنها بر او توکل کنند.»'),
(1664, 12, 68, 'و چون بدان‌سان که پدرشان به آنها دستور داده بود، وارد [مصر] شدند، این کار در برابر اراده‌ی خداوند هیچ سودشان نبخشید، جز آن که خواسته‌ای که در دل یعقوب بود، برآورده شد [و خاطرش آرام گرفت]. و البته او از دانشی برخوردار بود که ما به او آموخته بودیم، ولی بیشتر مردم نمی‌دانند.'),
(1665, 12, 69, 'و هنگامی که [برادران] بر یوسف وارد شدند، او برادرش [بنیامین] را نزد خود جای داد و گفت: «من برادر تو هستم، پس از آنچه آنها [تا به حال در حق من و تو] انجام می‌دادند، اندوهگین مباش.»'),
(1666, 12, 70, 'پس چون بارهای برادران را آماده ساخت، جام آبخوری [گران‌قیمتی] را در بارِ برادرش [بنیامین] گذاشت، سپس [همین که قافله عازم حرکت شد،] جارچی فریاد برآورد: «ای کاروانیان! بی‌شک شما دزد هستید!»'),
(1667, 12, 71, '[برادران یوسف] به آنها روی کردند و گفتند: «چه چیزی گم کرده‌اید؟»'),
(1668, 12, 72, 'گفتند: «جام مخصوص پادشاه را گم کرده‌ایم. و هر کس که آن را بیاورد، یک بارِ شتر [جایزه] دارد. و من این [پاداش] را ضمانت می‌کنم.»'),
(1669, 12, 73, 'گفتند: «به خدا سوگند خودتان می‌دانید که ما نیامده‌ایم در این سرزمین فساد کنیم، و ما هرگز دزد نبوده‌ایم!»'),
(1670, 12, 74, '[کارگزاران یوسف] گفتند: «اگر دروغ گفته باشید، کیفر دزد [در نزد شما] چیست؟»'),
(1671, 12, 75, 'گفتند: «کسی که جام در بارش پیدا شود، کیفرش خود اوست [و برده شما خواهد شد]. ما ستمکاران را این‌گونه کیفر می‌دهیم.»'),
(1672, 12, 76, 'پس [یوسف] پیش از [بازرسی] بار برادرش به بار دیگر برادران پرداخت، آن‌گاه پیمانه را از بار برادرش بیرون آورد. ما این‌گونه برای یوسف تدبیر کردیم. [زیرا‌] او در آیین پادشاه [مصر] نمی‌توانست برادرش را بازداشت کند، مگر آن که خدا بخواهد [و چنین راه چاره را به او بیاموزد]. ما درجه و مقام هر کس را که بخواهیم [و شایسته باشد،] بالا می‌بریم. و برتر از هر صاحب دانشی، دانشوری است.'),
(1673, 12, 77, '[برادران] گفتند: «اگر او دزدی کرده است، پیش از این نیز برادرش دزدی کرده بود.» یوسف [این تهمت را] در دل خود پنهان داشت و به رویشان نیاورد. [و در دل] گفت: «جایگاه شما بدتر است. و خداوند به آنچه وصف می‌کنید، داناتر است.»'),
(1674, 12, 78, '[برادران] گفتند: «ای عزیز! او پدری دارد پیر و سالخورده [که طاقتِ دوریش را ندارد]، پس یکی از ما را به جای او بگیر [و او را رها کن]. ما تو را از نیکوکاران می‌بینیم.»'),
(1675, 12, 79, '[یوسف] گفت: «پناه بر خدا از این که شخص دیگری را به جای کسی که کالای خود را نزد او یافته‌ایم، بازداشت کنیم، که در این صورت از ستمکاران خواهیم بود.»'),
(1676, 12, 80, 'هنگامی که [برادران] از یوسف ناامید شدند، نجواکنان به کناری رفتند. [برادر] بزرگشان گفت: «مگر نمی‌دانید که پدرتان [برای برگرداندن او،] در پیشگاه خداوند از شما پیمان گرفته و پیش از این نیز درباره‌ی یوسف کوتاهی کرده‌اید؟! من از این سرزمین پا بیرون نمی‌گذارم تا آن که پدرم به من اجازه دهد یا خداوند درباره من داوری کند و او بهترین داور و حاکم است.'),
(1677, 12, 81, 'شما به سوی پدر بازگردید و بگویید: پدر [جان!]، پسرت دزدی کرد، و ما تنها به آنچه [دیدیم و] دانستیم گواهی دادیم. و ما علم غیب نداشتیم [که این اتّفاق می‌افتد].»'),
(1678, 12, 82, '«[اگر به ما اطمینان نداری،] از مردم شهری که در آن بودیم و از کاروانی که با آن آمدیم، تحقیق کن. و بی‌شک ما راستگوییم.»'),
(1679, 12, 83, '[یعقوب] گفت: «[چنین نیست که بنیامین دزدی کرده باشد،] بلکه نفس شما کاری [زشت] را برای شما آراسته است. پس صبری نیکو [پیش می‌گیرم]. امید است که خداوند همه [برادران] را با هم نزد من آوَرَد، چرا که او آگاه و حکیم است.»'),
(1680, 12, 84, 'و [یعقوب] از آنان روی گرداند و گفت: «ای دریغا بر یوسف!» و چشمان او از اندوه [و گریه‌ی زیاد نابینا و] سفید شد و او خشم خود را فرومی‌خورد و بُروز نمی‌داد.'),
(1681, 12, 85, '[فرزندان به پدر] گفتند: «به خدا سوگند آن قدر از یوسف یاد می‌کنی تا سخت بیمار و لاغر شوی، یا جانت را از دست بدهی.»'),
(1682, 12, 86, '[یعقوب] گفت: «من ناله‌ی آشکار و اندوه [پنهان] خود را به درگاه خدا شِکوه می‌برم و از [لطف] خداوند چیزهایی می‌دانم که شما نمی‌دانید.'),
(1683, 12, 87, 'پسرانم! [بار دیگر به مصر] بروید و از یوسف و برادرش خبر بگیرید. و از رحمت خداوند ناامید نشوید، که تنها مردمان کافر از رحمت خداوند نومید می‌شوند.»'),
(1684, 12, 88, 'پس هنگامی که بر یوسف وارد شدند، گفتند: «ای عزیز! سختیِ [قحطی] به ما و خاندانمان آسیب رسانده و [برای خرید گندم] سرمایه‌ای ناچیز با خود آورده‌ایم، اما تو سهمیه ما را کامل بده و بر ما بخشش نما، زیرا که خداوند کریمان و بخشندگان را پاداش می‌دهد.»'),
(1685, 12, 89, '[اینجا بود که یوسف] گفت: «آیا می‌دانید آن‌گاه که در جَهالت و نادانی بودید، با یوسف و برادرش چه کردید؟»'),
(1686, 12, 90, 'گفتند: «آیا به راستی تو همان یوسفی؟!» گفت: « [آری!] من یوسفم، و این برادرم [بنیامین]، که خداوند بر ما منّت گذارده است. بی‌گمان هر کس تقوا پیشه کند، و شکیبایی ورزد، خداوند پاداش [این] نیکوکاران را تباه نمی‌سازد.»'),
(1687, 12, 91, '[برادران] گفتند: «به خدا سوگند که خداوند تو را بر ما برتری بخشیده است. و به راستی که ما خطاکار بوده‌ایم.»'),
(1688, 12, 92, '[یوسف] گفت: «امروز هیچ ملامت و سرزنشی بر شما نیست. خداوند شما را بیامرزد. و او مهربان‌ترینِ مهربانان است.'),
(1689, 12, 93, 'این پیراهن مرا ببرید و آن را بر صورت پدرم بیندازید تا بینا شود و همه‌ی خانواده‌ی خود را نزد من بیاورید.»'),
(1690, 12, 94, 'و چون کاروان [از مصر به سوی کنعان] رهسپار شد، پدرشان [یعقوب] گفت: «من بوی یوسف را احساس می‌کنم، اگر مرا کم‌خرد ندانید.»'),
(1691, 12, 95, '[برخی از خاندانش] گفتند: «به خدا سوگند که تو همچنان در اشتباه دیرینه‌ات هستی [و گمان می‌کنی که یوسف زنده است]!»'),
(1692, 12, 96, 'پس چون [برادری که حامل پیراهن یوسف بود،] مژده‌رسان آمد و آن را بر صورت یعقوب افکند، ناگهان یعقوب بینا شد و گفت: «آیا به شما نگفتم که من از [عنایت] خداوند چیزهایی می‌دانم که شما نمی‌دانید؟!»'),
(1693, 12, 97, '[فرزندان یعقوب] گفتند: «ای پدر! برای گناهانمان [از خداوند] آمرزش بخواه که به راستی ما خطاکار بودیم.»'),
(1694, 12, 98, '[یعقوب] گفت: «به زودی از پروردگارم برای شما آمرزش می‌طلبم، که او، خود آمرزنده و مهربان است.»'),
(1695, 12, 99, 'پس هنگامی که بر یوسف وارد شدند، پدر و مادرش را در کنار خویش جای داد و گفت: «همگی وارد مصر شوید که به خواست خدا در امنیت خواهید بود.»'),
(1696, 12, 100, 'و پدر و مادرش را بر فراز تخت نشاند، و همگی به خاطر او به سجده افتادند. [یوسف] گفت: «ای پدر! این تعبیر خواب پیشین من است که پروردگارم آن را تحقّق بخشید و به من احسان کرد، آن گاه که مرا از زندان بیرون آورد و شما را پس از آن که شیطان میان من و برادرانم را بر هم زد، از بیابان [کنعان به مصر] آورد. به راستی که پروردگارم در آنچه بخواهد، صاحب لطف است. و اوست دانای حکیم.'),
(1697, 12, 101, 'پروردگارا! تو مرا [بهره‌ای] از فرمانروایی دادی و از [دانش] تعبیر خواب به من آموختی. [ای] پدیدآورنده‌ی آسمان‌ها و زمین! در دنیا و آخرت، تنها تو مولای منی! مرا در حالی که تسلیم [امر تو] باشم، بمیران و مرا به شایستگان ملحق بفرما!»'),
(1698, 12, 102, '[ای پیامبر!] این [داستان] از خبرهای بزرگ غیبی است که ما به تو وحی می‌کنیم و تو هنگامی که [برادران یوسف] بر کار خود هم‌داستان شدند و نیرنگ می‌کردند، نزدشان نبودی.'),
(1699, 12, 103, '[ای پیامبر!] بیشتر مردم ایمان نمی‌آورند، هر چند [سخت بکوشی و] بسیار آرزو داشته باشی.'),
(1700, 12, 104, 'و تو بر این [کار] هیچ پاداشی از آنان نمی‌خواهی. این [قرآن] جز پند و تذکّری برای جهانیان نیست.'),
(1701, 12, 105, 'و چه بسیار نشانه در آسمان‌ها و زمین، که [مردم] بر آن می‌گذرند، در حالی که از آن [غافل و] روی‌گردانند.'),
(1702, 12, 106, 'و بیشترشان به خدا ایمان نمی‌آورند، مگر آن که [برای او] شریک می‌گیرند [و ایمان خود را با شرک آلوده می‌کنند].'),
(1703, 12, 107, 'آیا [کافران] از این که عذاب الهی آنها را دربرگیرد و یا قیامت در حالی که بی‌خبرند، ناگهان فرارسد، در امانند؟'),
(1704, 12, 108, '[ای پیامبر!] بگو: «این راه من است. من و پیروانم [مردم را] با بصیرت و روشن‌بینی، به سوی خدا دعوت می‌کنیم. منزّه است خدا [از هر شریک و همتا]! و من برای او شریکی قائل نیستم.»'),
(1705, 12, 109, 'و ما پیش از تو نیز، مردانی از اهالی شهرها و روستاها را [به پیامبری] فرستادیم، و به آنها وحی می‌کردیم. آیا [مخالفان حق‌] در زمین نمی‌گردند تا ببینند سرانجام کسانی که پیش از آنان بوده‌اند، چگونه شد؟ و قطعاً سرای آخرت برای کسانی که تقوا پیشه کرده‌اند، بهتر است. آیا نمی‌اندیشید؟'),
(1706, 12, 110, '[کافران، آن قدر بر انکار پیامبران پافشاری کردند‌] تا آنجا که پیامبران [از هدایت مردم] نومید شدند و [گروهی از مردم‌] پنداشتند که [به آنان] دروغ گفته شده است. در چنین حالی، یاری ما به آنان رسید، پس کسانی که خواستیم، نجات یافتند، و عذاب ما از گناهکاران بازگردانده نمی‌شود.'),
(1707, 12, 111, 'به راستی در سرگذشت آنان، عبرتی برای خردمندان است. این [قرآن،] سخنی ساختگی نیست، بلکه [کتاب‌های آسمانی] پیش از خود را راست می‌شمرد و روشنگرهر چیز و [مایه‌ی] هدایت و رحمت برای اهل ایمان است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، میم، را. آن آیات کتاب [آسمانی] است و آنچه از جانب پروردگارت به سوی تو فرود آمده، حق است؛ ولی بیشتر مردم ایمان نمی‌آورند.'),
(1709, 13, 2, 'خداست آن که آسمان‌ها را بدون ستون‌هایی که آن را ببینید، برافراشت، سپس بر عَرش [که مقام فرمانروایی خداوند بر کلّ آفرینش است،‌] برآمد. و خورشید و ماه را که هر کدام تا زمان معینی حرکت دارند، تسخیر نمود. او کار [هستی] را تدبیر می‌کند، آیات خود را به روشنی بیان می‌کند تا شاید به لقای پروردگارتان یقین کنید.'),
(1710, 13, 3, 'و اوست خدایی که زمین را گسترانید و در آن کوه‌ها و نهرها قرار داد و از هر میوه‌ای، دو جفت در آن قرار داد. او روز را با شب می‌پوشاند. قطعاً در این [امور] برای کسانی که فکر می‌کنند، نشانه‌هایی است.'),
(1711, 13, 4, 'و در زمین قطعاتی است کنار هم. و باغ‌هایی از انواع انگور و کشت‌زار و درختان خرما [و محصولاتی مختلف،] همانند و غیر همانند، که همه با یک آب آبیاری می‌شوند. و بعضی میوه‌ها را در خوردن بر بعضی برتری دادیم. همانا در این [تنوع میوه‌ها و مزه‌ها با آن که از یک آب و خاک تغذیه می‌شوند،] برای کسانی که می‌اندیشند، نشانه‌هایی است.'),
(1712, 13, 5, 'اگر تعجب می‌کنی، پس عجیب، گفتار آنهاست [که می‌گویند:] «آیا آنگاه که خاک شدیم، [بار دیگر زنده می‌شویم و] به آفرینش تازه‌ای درمی‌آییم؟» آنانند کسانی که به پروردگارشان کفر ورزیدند و همانانند که غل‌ها درگردنشان باشد. و همانانند همدم آتش و در آن جاودانه‌اند.'),
(1713, 13, 6, 'و پیش از رحمت و نیکی، به شتاب از تو عذاب و بدی می‌خواهند، در حالی که پیش از آنان عذاب‌ها بوده است. و همانا پروردگارت نسبت به مردم با همه ستمشان، دارای آمرزش است و همانا پروردگارت سخت کیفر است.'),
(1714, 13, 7, 'و کسانی که کفر ورزیدند، می‌گویند: «چرا از طرف پروردگارش نشانه‌ای بر او نازل نشده است؟» [ای پیامبر!] تو فقط بیم‌دهنده‌ای و برای هر قومی راهنمایی است.'),
(1715, 13, 8, 'خداوند می‌داند آنچه را که هر ماده‌ای [در شکم] حمل می‌کند و آنچه را که رحم‌ها جذب می‌کنند [و به مصرف غذای جنین می‌رسانند]، و آنچه را می‌افزایند. و هر چیز نزد او به مقدار و اندازه‌ای است.'),
(1716, 13, 9, 'او به نهان و آشکار آگاه و بزرگ و بلندمرتبه است.'),
(1717, 13, 10, '[برای او] یکسان است که کسی از شما سخن را سرّی بگوید و کسی آن را آشکارا. و کسی که خود را به شب مخفی کند و کسی که در روز آشکارا حرکت نماید.'),
(1718, 13, 11, 'برای انسان، فرشتگانی است که پی‌درپی او را از پیش رو و از پشت سر به فرمان خداوند حفاظت می‌کنند. همانا خداوند [سرنوشت] قومی را تغییر نمی‌دهد تا آن که آنان خود را تغییر دهند. و هرگاه خداوند برای قومی آسیبی بخواهد، هیچ برگشتی برای آن نیست. و در برابر او هیچ دوست و کارساز و حمایت‌کننده‌ای برای آنان نیست.'),
(1719, 13, 12, 'اوست کسی که برق [آسمان] را برای بیم و امید به شما نشان می‌دهد و ابرهای گران‌بار را پدید می‌آورد.'),
(1720, 13, 13, 'رعد با ستایش او و فرشتگان از بیم او تسبیح می‌کنند. و او صاعقه‌ها را فرومی‌فرستد تا هرکه را بخواهد، مورد اصابت قرار دهد، در حالی که آنان درباره خداوند به جدال می‌پردازند و او سخت کیفر است.'),
(1721, 13, 14, 'تنها خواندن او حق است و کسانی را که [مشرکان] جز او می‌خوانند، هیچ پاسخشان نمی‌گویند، مگر مانند کسی که دو دستش را به سوی آب گشوده تا آن را به دهانش برساند و حال آن که نخواهد رسید. و دعا و خواست کافران [از غیر خدا] جز در گمراهی نیست.'),
(1722, 13, 15, 'و کسانی که در آسمان‌ها و زمین هستند، خواه ناخواه، با سایه‌هایشان، بامدادان و شامگاهان، برای خدا سجده می‌کنند.'),
(1723, 13, 16, 'بگو: «پروردگار آسمان‌ها و زمین کیست؟» بگو: «خداست.» بگو: «پس چرا جز او را که مالک هیچ سود و زیانی برای خویشتن نیستند، سرپرست گرفته‌اید؟» بگو: «آیا نابینا و بینا با هم برابرند؟ یا تاریکی‌ها و روشنایی یکسانند؟ یا مگر برای خدا شریکانی قرار داده‌اند که مانند آفریدن خدا [چیزی] آفریده باشند و این آفرینش بر آنان مُشتَبَه شده است؟» بگو: «خداوند، آفریدگار هر چیزی است و اوست یگانه قهّار.»'),
(1724, 13, 17, 'او از آسمان آبی فروفرستاد، پس رودخانه‌ها به اندازه [ظرفیت] خویش جاری شد و سیلاب، کفی را بر خود حمل کرد. و از آنچه برای به دست آوردن زیور یا کالایی، آتش بر آن می‌افروزند [نیز] کفی مانند کفِ سیلاب [حاصل شود]. این‌گونه خداوند حق و باطل را [مَثَل] می‌زند. پس کف به کناری رفته [و نیست شود] و امّا آنچه برای مردم مفید است، در زمین باقی می‌ماند. خداوند این‌گونه مثال‌ها می‌زند.'),
(1725, 13, 18, 'برای کسانی که پروردگارشان را اجابت کردند، نیکوترین [پاداش] است. ولی کسانی که [دعوت] او را نپذیرفتند، اگر هر آنچه در زمین است و مانند آن را با آن داشته باشند، قطعاً حاضرند آن را [برای خود از عذاب] تاوان بدهند. آنانند که برایشان حساب سختی است و جایگاهشان دوزخ است و چه بد جایگاهی است!'),
(1726, 13, 19, 'پس آیا کسی که می‌داند آنچه از جانب پروردگارت به سوی تو نازل شده حق است، مانند کسی است که نابیناست؟ همانا تنها صاحبان خرد پند می‌گیرند.'),
(1727, 13, 20, 'کسانی که به پیمان الهی وفا می‌کنند و عهد [او را] نمی‌شکنند.'),
(1728, 13, 21, 'و کسانی که آنچه را خداوند به پیوند با آن فرمان داده، پیوند می‌دهند و در برابر پروردگارشان [خشوع و] هراس دارند و از سختی حساب می‌ترسند.'),
(1729, 13, 22, 'و کسانی که برای جلب توجّه پروردگارشان صبر پیشه کرده و نماز به پا داشتند و از آنچه روزیشان کردیم، پنهانی و آشکارا انفاق کردند. و بدی را با نیکی پاک می‌کنند. آنانند که سرای آخرت مخصوص آنهاست.'),
(1730, 13, 23, 'باغ‌های جاودانی که آنان و هر کس از پدران و همسران و فرزندانشان که صالح بوده‌اند، بدان داخل می‌شوند. و فرشتگان از هر دری بر آنان وارد می‌شوند.'),
(1731, 13, 24, '[و می‌گویند:] «درود بر شما به خاطر استقامتی که کردید! پس چه نیکوست سرای آخرت!»'),
(1732, 13, 25, 'ولی کسانی که پیمان خدا را پس از محکم‌بستن، می‌شکنند و آنچه را خداوند به پیوندِ آن فرمان داده، قطع می‌کنند و در زمین فساد می‌نمایند، آنانند که برایشان لعنت است و برایشان بدمنزلی است!'),
(1733, 13, 26, 'خداوند برای هر که بخواهد، روزی را گسترش می‌دهد و [برای هر که بخواهد] تنگ می‌گرداند. و [مردم] به زندگی دنیا دل‌خوشند، در حالی که زندگی دنیا در [برابر زندگی] آخرت، جز یک کامیابی [موقت و اندک] نیست.'),
(1734, 13, 27, 'کافران می‌گویند: چرا از طرف پروردگارش معجزه‌ای [به دلخواه ما] بر او نازل نشده است؟» بگو: «خداوند هر که را بخواهد، [به حال خود رها و] گمراه می‌کند و هر کس را که به سوی او رو کرده و توبه نماید، به سوی خویش هدایت می‌نماید.»'),
(1735, 13, 28, 'کسانی که ایمان آورده‌اند و دل‌هایشان به یاد خدا آرام می‌گیرد. بدانید که تنها با یاد خدا دل‌ها آرام می‌گیرد.'),
(1736, 13, 29, 'کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، خوشا به حالشان. و سرانجام نیکویی دارند!'),
(1737, 13, 30, 'این چنین ما تو را در میان اُمتی که پیش از آن اُمت‌های دیگری [آمده و] رفته‌اند، به رسالت فرستادیم، تا آنچه را به سوی تو وحی کرده‌ایم، بر آنان تلاوت کنی. در حالی که آنان به خدای رحمان کفر می‌ورزند. بگو: «او پروردگار من است. معبودی جز او نیست. تنها بر او توکل کرده‌ام و بازگشت من به سوی اوست.»'),
(1738, 13, 31, 'و اگر به واسطه قرآن کوه‌ها به حرکت درآیند، یا زمین قطعه قطعه شود، یا مردگان به سخن آیند، [باز کافران ایمان نمی‌آورند،] بلکه تمام امور به دست خداست. آیا مؤمنان [از این سنگدلان] مأیوس نشده‌اند [و نمی‌دانند] که اگر خدا می‌خواست، قطعاً [با قهر و اجبار،] همه مردم را به راه می‌آورد؟ و پیوسته به کسانی که کفر ورزیدند، به سزای کارهایی که کرده‌اند، مصیبت کوبنده‌ای می‌رسد، یا آن مصیبت به نزدیک خانه آنان فرود می‌آید [تا هر لحظه‌ی زندگی همراه با دغدغه باشند و این دلهره همچنان ادامه دارد] تا آن‌گاه که وعده‌ی الهی فرارسد. البته خداوند وعده‌ای که داده، خلاف نمی‌کند.'),
(1739, 13, 32, 'و همانا پیامبرانی پیش از تو به استهزاء گرفته شدند، امّا من به کسانی که کفر ورزیدند، مهلت دادم. سپس آنان را [به قهر خود] گرفتم، پس [بنگر] کیفر من چگونه بود؟'),
(1740, 13, 33, 'پس آیا آن [خدایی] که بر هر کس و عملکرد او حاکم و ناظر است، [با بُت‌های ساختگی یکی است؟] و آنان برای خدا شریکانی قرار داده‌اند. بگو: «آنها را نام ببرید [چه هستند و چه توانی دارند؟] آیا خدا را از شریکانی در زمین خبر می‌دهید که او آنها را نمی‌شناسد؟ یا سخنی توخالی [می‌گویید].» بلکه برای کسانی که کفر ورزیدند، مکرشان آراسته شده است و از راه [حق] بازداشته شده‌اند. و هر که را خدا گمراه کند، پس برای او هیچ هدایت‌کننده‌ای نیست.'),
(1741, 13, 34, 'برای آنان در زندگی دنیا عذابی است و قطعاً عذاب آخرت سخت‌تر است. و در برابر خدا هیچ محافظی برایشان نیست.'),
(1742, 13, 35, 'مثال بهشتی که به پرهیزکاران وعده داده شده، [چنین است که] نهرها از زیر [درختان] آن جاری است. میوه‌ها و سایه‌اش دائمی است. این است عاقبت کسانی که تقوا پیشه کردند. و سرانجامِ کافران آتش است.'),
(1743, 13, 36, 'و کسانی که به آنان کتاب [آسمانی] دادیم، به آنچه به سوی تو نازل شده دلشاد می‌شوند. و بعضی از گروه‌ها کسانی هستند که بخشی از آن [قرآن] را انکار می‌کنند. بگو: «همانا من مأمورم که خدا را بپرستم و به او شرک نورزم. تنها به سوی او دعوت می‌کنم و بازگشت من به سوی اوست.»'),
(1744, 13, 37, 'و بدین‌سان ما آن [قرآن] را به صورت فرمانی روشن به زبان عربی نازل کردیم. و بدون شک اگر بعد از دانشی که به تو رسیده است، از هوس‌های آنان پیروی کنی، در برابر خدا برای تو هیچ یاور و محافظی نیست.'),
(1745, 13, 38, 'و البته ما قبل از تو [نیز] پیامبرانی فرستاده‌ایم و برای آنان همسران و فرزندانی قرار دادیم. و هیچ پیامبری را نسزد که معجزه‌ای بیاورد، مگر با اذن الهی. برای هر زمان و دوره‌ای کتاب [و قانونی] است.'),
(1746, 13, 39, 'خداوند هر چه را بخواهد، محو یا اثبات می‌کند و اُم‌الکتاب تنها نزد اوست‌.'),
(1747, 13, 40, '[ای پیامبر! در فکر دیدن نتیجه کار مباش، زیرا] اگر بخشی از آنچه را به آنان وعده می‌دهیم [در زمان حیات تو] به تو نشان دهیم، یا تو را از دنیا ببریم، [بدان که ما وعده‌های خود را محَقَّق خواهیم کرد،] پس وظیفه‌ی تو تنها تبلیغ است و حساب تنها با ماست.'),
(1748, 13, 41, 'مگر ندیدند که ما به سراغ زمین می‌آییم، در حالی که از اطرافش آن را می‌کاهیم؟ خداوند حکم می‌کند و هیچ تجدیدنظرکننده‌ای برای حکم او نیست. و او سریعاً به حساب همه می‌رسد.'),
(1749, 13, 42, 'و البته کسانی که پیش از آنان بودند، مکرها کردند؛ ولی [سودی نکرد، زیرا] همه‌ی تدبیرها برای خداست. او می‌داند که هر کس چه به دست می‌آورد. و کفار به زودی خواهند دانست که سرای آخرت از آن کیست.'),
(1750, 13, 43, 'و کسانی که کفر ورزیدند، می‌گویند: «تو فرستاده [خدا] نیستی!» بگو: «گواهی خدا و کسی که علم کتاب نزد اوست، میان من و شما کافی است.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف، لام، را. [این] کتابی است که آن را به سوی تو نازل کردیم تا مردم را به اذن پروردگارشان از تاریکی‌ها [ی شرک و جهل] به سوی نور [ایمان] خارج کنی، به سوی راه خداوند عزیز حمید.'),
(1752, 14, 2, 'خداوندی که آنچه در آسمان‌ها و آنچه در زمین است برای اوست. پس وای بر کافران از عذابی سخت!'),
(1753, 14, 3, 'کسانی که زندگی دنیا را بر آخرت ترجیح می‌دهند و [مردم را] از راه خدا بازمی‌دارند و می‌خواهند آن را منحرف کنند. آنها در گمراهی عمیقی هستند.'),
(1754, 14, 4, 'و هیچ پیامبری را نفرستادیم مگر به زبان قومش، تا [بتواند پیام خدا را] برای مردم بیان کند. پس خداوند هر که را بخواهد [و سزاوار بداند،] گمراه می‌کند و هر که را بخواهد [و شایسته بداند،] هدایت می‌نماید. و اوست عزیز و حکیم.'),
(1755, 14, 5, 'همانا موسی را همراه معجزاتی [به سوی مردم] فرستادیم [و به او گفتیم:] قومت را از تاریکی‌ها به سوی نور خارج ساز و روزهای [نزول قهر یا لطف] خدا را به آنان یادآوری کن. در این [یادآوری] برای کسانی که صبر و مقاومت و سپاس فراوان داشته باشند، نشانه‌هایی از قدرت الهی است.'),
(1756, 14, 6, 'و [به یادآور] هنگامی که موسی به قوم خود گفت: «نعمت خداوند را بر خود یاد کنید، آن‌گاه که شما را از فرعونیان نجات داد. آنها که شما را به بدترین نوع، عذاب می‌کردند، پسرانتان را سر می‌بریدند و زنانتان را [برای کنیزی] زنده نگاه می‌داشتند. و در این امور، از طرف پروردگارتان آزمایش بزرگی بود.»'),
(1757, 14, 7, 'و [نیز به یادآور] هنگامی که پروردگارتان اعلام فرمود: «همانا اگر شکر کنید، قطعاً [نعمتِ‌] شما را می‌افزایم، و اگر ناسپاسی کنید، البته عذاب من سخت است.»'),
(1758, 14, 8, 'و موسی [به بنی‌اسرائیل] گفت: «اگر شما و ساکنان زمین همگی کفر ورزید، [به خداوند زیانی نمی‌رسد، زیرا] خداوند بی‌نیاز و شایسته‌ی ستایش است.»'),
(1759, 14, 9, 'آیا خبر کسانی که قبل از شما بودند، [همچون خبر غرق شدن] قوم نوح و [طوفان قوم] عاد و [صاعقه‌ی قوم] ثمود و کسانی که بعد از آن‌ها بودند، به شما نرسیده است؟ همان‌ها که جز خداوند از آن‌ها آگاه نیست. پیامبرانشان همراه با معجزات به سراغشان آمدند، ولی آن‌ها دست‌هایشان را [به نشانه اعتراض و تمسخر] در دهانشان می‌گذاشتند و می‌گفتند: «ما به آنچه شما به آن فرستاده شده‌اید، کفر می‌ورزیم و ما از آنچه شما ما را به آن دعوت می‌کنید، در شک و تردید هستیم.»'),
(1760, 14, 10, 'پیامبرانشان [در جواب] گفتند: «آیا درباره خدا، آفریدگار آسمان‌ها و زمین، جای شکی هست؟ او که شما را [به راه حق] دعوت می‌کند تا از گناهانتان بگذرد، و تا موعد معین شما را مهلت دهد.» [کفّار] گفتند: «شما تنها انسانی همچون خود مایید، [امتیازی بر ما ندارید،] شما می‌خواهید ما را از آنچه نیاکان ما می‌پرستیدند بازدارید. پس برای ما برهان و معجزه‌ای روشن [آن‌گونه که ما می‌خواهیم،] بیاورید.»'),
(1761, 14, 11, 'پیامبرانشان در جواب گفتند: «[آری!] ما تنها بشری مثل شماییم، ولی خداوند بر هر کس از بندگانش که بخواهد، منّت می‌گذارد [و او را بر مردم مبعوث می‌کند] و ما هرگز نمی‌توانیم معجزه‌ای برای شما جز به فرمان خدا بیاوریم، و مؤمنان باید تنها بر خداوند توکل نمایند.'),
(1762, 14, 12, 'و چرا ما بر خدا توکل نکنیم؟ در حالی که او ما را به راه‌های [سعادت] ما هدایت کرده است؟ قطعاً ما در برابر هر آزاری که شما نسبت به ما روا دارید، مقاومت خواهیم کرد. و اهل توکل باید فقط بر خدا توکل نمایند.»'),
(1763, 14, 13, 'و کفّار به پیامبرانشان گفتند: «ما قطعاً شما را از سرزمین خود بیرون می‌کنیم، مگر آن که در کیش ما درآیید.» پس پروردگارشان به آنان وحی کرد که ما حتماً ستمگران را نابود می‌کنیم.'),
(1764, 14, 14, 'و بعد از [هلاکت] ستمگران، شما را در آن سرزمین سکونت خواهیم داد. این، برای کسانی است که از مقام من پروا کنند و از وعده‌ی تهدیدآمیزم بترسند.'),
(1765, 14, 15, 'و [پیامبران و مؤمنان] در انتظار فتح و پیروزی بودند، و [سرانجام] هر ستمگرِ لجوج، محروم ماند.'),
(1766, 14, 16, 'عاقبتِ این [ستمگرِ لجوج] دوزخ است. و از آبی بدبو و چرکین نوشانده می‌شود.'),
(1767, 14, 17, 'آن را جرعه جرعه می‌آشامد، در حالی که فروبردن آن هرگز گوارا نیست. و مرگ از هر سو به سراغ او می‌آید، ولی او مردنی نیست و پس از آن، عذابی شدید است.'),
(1768, 14, 18, 'مَثل [کارهای] کسانی که به پروردگارشان کفر ورزیدند، همچون خاکستری است که در روزی طوفانی، تندبادی بر آن بوزد، آنان بر حفظ ذره‌ای از آنچه به دست آورده‌اند، قادر نیستند. این، همان گمراهیِ دور و عمیق است.'),
(1769, 14, 19, '[ای انسان!] آیا ندیدی که خداوند آسمان‌ها و زمین را به حق آفرید؟ اگر بخواهد شما را می‌برد و آفریده‌ای جدید می‌آورد.'),
(1770, 14, 20, 'و این تبدیل و جابه‌جایی، برای خداوند سخت نیست.'),
(1771, 14, 21, 'و [در قیامت، مردم] همگی در پیشگاه خدا ظاهر شوند. پس ضعیفان به مستکبران می‌گویند: «ما [در دنیا] پیرو شما بودیم، پس آیا [امروز] می‌توانید چیزی از عذاب خدا را از ما بازدارید؟» آن‌ها می‌گویند: «اگر خداوند ما را [به رهایی از عذاب] هدایت کرد، قطعاً ما شما را هدایت می‌کنیم. بر ما یکسان است که فریاد و ناله بزنیم، یا صبر کنیم، برای ما هیچ راه نجاتی نیست.»'),
(1772, 14, 22, 'و شیطان [از روی ملامت به دوزخیان] می‌گوید: همانا خداوند به شما وعده داد، وعده‌ی راست. و من به شما وعده دادم، ولی با شما وعده‌خلافی کردم. من بر شما تسلّطی نداشتم، جز آن که شما را دعوت کردم و شما [به میل خود] پذیرفتید. پس مرا ملامت و نکوهش نکنید، و خود را سرزنش کنید. [در این روز،] نه من می‌توانم فریادرس شما باشم، و نه شما فریادرس من. من از این که مرا پیش از این شریک خدا قرار داده بودید بیزارم! قطعاً برای ستمگران عذاب دردناکی است!»'),
(1773, 14, 23, 'و کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، به باغ‌هایی که از زیر [درختان] آن نهرها جاری است، داخل شوند. آنان با اذن پروردگارشان برای همیشه در آن خواهند بود و درودشان درآنجا سلام است.'),
(1774, 14, 24, 'آیا ندیدی که خداوند چگونه مثل زده است؟ کلمه‌ی پاک، همانند درختی پاک است که ریشه‌اش ثابت و استوار، و شاخه‌اش در آسمان است.'),
(1775, 14, 25, 'با اذن پروردگارش، همواره میوه می‌دهد. و خداوند برای مردم مثل‌هایی می‌زند، باشد که به یاد آرند و پند گیرند.'),
(1776, 14, 26, 'و مَثَل سخن [و اعتقاد] پلید، همچون درختی پلید [و بی‌ریشه] است که از روی زمین برآمده و هیچ ثباتی ندارد.'),
(1777, 14, 27, 'خداوند در زندگی دنیا و در آخرت، اهل ایمان را با کلام [و عقیده‌ی حقّ و] ثابت، پایدار قرار می‌دهد. و خداوند ستمگران را [به حال خود رها کرده،] گمراه می‌کند. و خداوند آنچه بخواهد [طبق عدل و حکمت خود] انجام می‌دهد.'),
(1778, 14, 28, 'آیا به کسانی که نعمت خداوند را به کفر تبدیل کردند و قوم خود را به سرای هلاکت درآوردند، نگریستی؟'),
(1779, 14, 29, 'آنان به دوزخ درآیند، که چه بد جایگاهی است!'),
(1780, 14, 30, 'آنان برای خداوند شریک‌هایی قرار دادند، تا مردم را از راه خدا گمراه نمایند. بگو: «[چند روزی از زندگی زودگذر دنیا] برخوردار شوید که قطعاً پایان کار شما آتش است.»'),
(1781, 14, 31, '[ای پیامبر!] به بندگان من که ایمان آورده‌اند، بگو: نماز را برپا دارند، و از آنچه به آنان روزی داده‌ایم، پنهان و آشکار انفاق کنند، قبل از آن که روزی فرارسد که در آن نه داد و ستدی باشد، و نه دوستی و رابطه‌ای.'),
(1782, 14, 32, 'خداست که آسمان‌ها و زمین را آفرید، و از آسمان، آبی فروفرستاد، پس با آن برای رزق شما از [زمین] میوه‌ها بیرون آورد. و کشتی را رامِ شما نمود تا به فرمان او در دریا به حرکت درآید، و برای شما نهرها را مسخَّر نمود.'),
(1783, 14, 33, 'و خورشید و ماه را که پیوسته در حرکتند، برای شما رام نمود و نیز شب و روز را برای شما مسخَّر کرد.'),
(1784, 14, 34, 'و [خداوند] از هر آنچه که از او خواستید [و نیاز داشتید،] به شما داده است. و اگر [بخواهید] نعمت خدا را بشمارید، نمی‌توانید آنها را به دقت شُماره کنید. همانا انسان بسیار ستمگر و ناسپاس است.'),
(1785, 14, 35, 'و [به یاد آور] زمانی که ابراهیم گفت: «پروردگارا! این شهر [مکه] را امن قرار ده، و من و فرزندانم را از این که بت‌ها را بپرستیم، دور بدار.'),
(1786, 14, 36, 'پروردگارا! به راستی بت‌ها، بسیاری از مردم را گمراه کرده‌اند، پس هر که مرا پیروی کند، قطعاً او از من است و هر کس با من مخالفت کند، همانا تو بخشنده‌ی مهربانی.'),
(1787, 14, 37, 'پروردگارا! من [یکی] از ذرّیه‌ام را در درّه‌ای [بی‌آب و] بی‌گیاه، در کنار خانه‌ی گرامی و محترم تو ساکن ساختم. پروردگارا! [چنین کردم] تا نماز برپا دارند، پس دل‌های گروهی از مردم را به سوی آنان مایل گردان و آنان را از محصولات و میوه‌ها، روزی ده تا شاید سپاس گزارند.'),
(1788, 14, 38, 'پروردگارا! همانا تو آنچه را پنهان و یا آشکار کنیم، می‌دانی و چیزی در زمین و در آسمان بر خداوند پوشیده نمی‌ماند.'),
(1789, 14, 39, 'ستایش خداوندی را که در پیری، اسماعیل و اسحاق را به من عطا فرمود. قطعاً پروردگارم شنونده دعاست [و آن را مستجاب می‌کند].'),
(1790, 14, 40, 'پروردگارا! مرا برپا دارنده نماز قرار ده، و از نسل و ذریه‌ام نیز. پروردگارا! دعای مرا [،نماز و عبادتم را] بپذیر!'),
(1791, 14, 41, 'پروردگارا! مرا و پدر و مادرم را و مؤمنان را، روزی که حساب برپا می‌شود، ببخشای!»'),
(1792, 14, 42, 'خداوند را از آنچه ستمگران انجام می‌دهند، غافل مپندار. همانا او [کیفر و حساب] آنان را برای روزی به تأخیر می‌اندازد که چشم‌ها [از حیرت و وحشتِ] آن روز خیره می‌ماند.'),
(1793, 14, 43, '[روزی که مجرمان از شدّت وحشت] شتاب‌زده، سرها را بالا گرفته، چشم‌ها و پلک‌هایشان به هم نخورد و دل‌هایشان [از امید و تدبیر] تهی است.'),
(1794, 14, 44, 'و مردم را از روزی که عذاب به سراغشان خواهد آمد، بترسان! [آن روز] کسانی که ظلم کرده‌اند، خواهند گفت: «پروردگارا! ما را تا مدّت کوتاهی مهلت ده، تا دعوت تو را اجابت نماییم و از پیامبران پیروی کنیم.» [به آنان گفته می‌شود:] «آیا شما نبودید که پیش از این سوگند یاد می‌کردید که هرگز برای شما زوال و فنایی نیست؟'),
(1795, 14, 45, 'و در خانه‌های کسانی که [پیش از شما بودند و] به خویشتن ستم کردند، ساکن شدید، و برای شما روشن شد که با آنان چه کردیم و برای شما مثل‌ها زدیم [پس چرا عبرت نگرفتید؟].»'),
(1796, 14, 46, 'به یقین آنان تمام مکر خود را به کار گرفتند، ولی مکر و حیله‌ی آن‌ها نزد خداست، گر چه کوه‌ها از مکرشان از جا کنده شود.'),
(1797, 14, 47, 'مپندارید که خداوند وعده‌ای را که به پیامبرانش داده است، تخلّف می‌کند. قطعاً خداوند شکست‌ناپذیر و صاحبِ انتقام است.'),
(1798, 14, 48, 'روزی که زمین به غیر این زمین و آسمان‌ها [به آسمان‌های دیگر] تبدیل شوند و [همه‌ی مردم] در پیشگاه خداوند یگانه قهّار حاضر شوند.'),
(1799, 14, 49, 'و در آن روز مجرمان را می‌بینی که در غل و زنجیر به هم بسته شده‌اند.'),
(1800, 14, 50, 'جامه‌های آنان از قَطِرانِ [بدبوی قیرگون] است، و صورت‌هایشان را آتش می‌پوشاند.'),
(1801, 14, 51, '[مجرمان کیفر می‌شوند] تا خداوند به هر کس سزای آنچه را کسب کرده، بدهد، چرا که خداوند حساب‌رسی سریع است.'),
(1802, 14, 52, 'این [قرآن] پیام رسا و ابلاغی برای مردم است تا به وسیله‌ی آن، هشدار یابند و بدانند که او معبودی یکتاست. و تا خردمندان پند گیرند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف لام را. آن است آیات کتاب [آسمانی] و قرآن روشن و روشنگر.'),
(1804, 15, 2, 'چه بسا کسانی که کفر ورزیدند، آرزو دارند که ای کاش مسلمان بودند.'),
(1805, 15, 3, 'آنان را [به حال خود] رها کن تا بخورند و بهره‌مند شوند و آرزوها سرگرمشان کند. پس به زودی [نتیجه‌ی این بی‌تفاوتی‌ها را] خواهند فهمید.'),
(1806, 15, 4, 'و ما اهل هیچ قریه‌ای را هلاک نکردیم، مگر آن که برای آن [کارنامه و] کتابی معلوم بود.'),
(1807, 15, 5, 'هیچ اُمتی از اَجَل خود نه پیش می‌افتد و نه پس می‌ماند.'),
(1808, 15, 6, 'و کافران گفتند: «ای کسی‌که [ادعا داری] ذکر [الهی] بر او نازل شده، به یقین تو دیوانه‌ای.'),
(1809, 15, 7, 'اگر از راستگویانی، چرا فرشتگان را پیش ما نمی‌آوری؟»'),
(1810, 15, 8, '[غافل از آن که] ما فرشتگان را جز بر اساس حق نمی‌فرستیم و در آن صورت دیگر کفار مهلت داده نمی‌شوند. [و قهرِ الهی، آنان را خواهد گرفت.]'),
(1811, 15, 9, 'به راستی ما خود، قرآن را نازل کردیم و ما خود، آن را نگاه‌داریم‌.'),
(1812, 15, 10, 'و همانا ما پیش از تو، در میان اقوام و گروه‌های پیشین [نیز پیامبرانی] فرستادیم.'),
(1813, 15, 11, 'و هیچ پیامبری برایشان نمی‌آمد، مگر آن که به استهزای او می‌پرداختند.'),
(1814, 15, 12, 'این‌گونه ما [برای اتمام حجّت،] قرآن را در دل‌های مجرمان راه می‌دهیم.'),
(1815, 15, 13, '[امّا] آنان به آن ایمان نمی‌آورند و سنّت کفّار پیشین نیز این‌گونه بوده است.'),
(1816, 15, 14, 'و اگر از آسمان دری بر روی آنان می‌گشودیم تا از آن بالا روند،'),
(1817, 15, 15, 'قطعاً می‌گفتند: «در حقیقت ما چشم‌بندی شده‌ایم، بلکه ما قومی جادو شده‌ایم.»'),
(1818, 15, 16, 'و ما در آسمان برج‌هایی قرار دادیم و آن را برای بینندگان آراستیم.'),
(1819, 15, 17, 'و آنها را از [دسترس] هر شیطان رانده‌شده‌ای، حفظ کردیم.'),
(1820, 15, 18, 'مگر آن که دزدانه گوش فراداد، که شهابی روشن او را دنبال نمود.'),
(1821, 15, 19, 'و زمین را گستراندیم و در آن کوه‌های استوار افکندیم و در آن از هر چیزِ سنجیده و به اندازه‌ای، رویاندیم.'),
(1822, 15, 20, 'و در آن زمین، برای شما و آن که شما روزی‌دهنده او نیستید، وسیله‌ی زندگی قرار دادیم.'),
(1823, 15, 21, 'و هیچ چیز نیست مگر آن که منابع و گنجینه‌های آن نزد ماست. و ما جز به مقدار معین فرونمی‌فرستیم.‌'),
(1824, 15, 22, 'و بادها را برای بارور ساختن [ابرها و گیاهان] فرستادیم و از آسمان آبی نازل کردیم، پس شما را بدان سیراب نمودیم، در حالی که شما ذخیره‌کننده‌ی آن نیستید، و توان آن را هم ندارید.'),
(1825, 15, 23, 'و البتّه این ماییم که زنده می‌کنیم و می‌میرانیم. و ماییم که [بعد از مرگِ همه، می‌مانیم و] وارث می‌شویم.'),
(1826, 15, 24, 'و بدون شک، ما به پیشینیان از شما علم داریم و به آیندگان نیز آگاهیم.'),
(1827, 15, 25, 'و همانا پروردگار توست که همه‌ی آنان را محشور خواهد کرد، زیرا که او حکیم و علیم است.'),
(1828, 15, 26, 'و به راستی ما [نخستین] انسان را از گِلِ خشکیده‌ای [همچون سفال] که از گِلِ بدبوی [تیره رنگی] گرفته شده بود، آفریدیم.'),
(1829, 15, 27, 'و قبل از انسان، جنّ را از آتشی سوزان و نافذ آفریدیم.'),
(1830, 15, 28, 'و [یاد کن] آن‌گاه که پروردگارت به فرشتگان گفت: «همانا من بشری را از گِلی خشکیده، سیاه و بدبو خلق می‌کنم.'),
(1831, 15, 29, 'پس هرگاه معتدل و استوارش ساختم و از روح خود در او دمیدم، سجده‌کنان پیش او بیفتید.»'),
(1832, 15, 30, 'پس فرشتگان همگی با هم و یک‌سره سجده کردند،'),
(1833, 15, 31, 'جز ابلیس که از بودنِ با سجده‌کنان، خودداری ورزید.'),
(1834, 15, 32, '[خداوند] فرمود: «ای ابلیس! تو را چه شده که همراه سجده‌کنان نیستی؟»'),
(1835, 15, 33, '[ابلیس] گفت: «من این‌گونه نیستم که برای بشری که او را از گِلی خشکیده، سیاه و بدبو آفریده‌ای، سجده کنم.»'),
(1836, 15, 34, '[خداوند] فرمود: «پس از صف فرشتگان خارج شو که همانا تو رانده شده‌ای.'),
(1837, 15, 35, 'و البتّه تا روز جزا بر تو لعنت خواهد بود.»'),
(1838, 15, 36, '[ابلیس] گفت: «پروردگارا! پس مرا تا روزی که مردم برانگیخته شوند، مهلت بده!»'),
(1839, 15, 37, '[خداوند] فرمود: «همانا تو از مهلت‌یافتگانی.'),
(1840, 15, 38, '[امّا نه تا روز قیامت، بلکه] تا روزی که وقت آن معلوم است.»'),
(1841, 15, 39, '[ابلیس] گفت: «پروردگارا! به سبب آن که مرا گمراه ساختی، من هم در زمین [بدی‌ها را] برایشان می‌آرایم و همه را فریب خواهم داد.'),
(1842, 15, 40, 'مگر [آن‌] بندگانت را که خالص‌شدگانند.»'),
(1843, 15, 41, '[خداوند] فرمود: «این [اخلاص و نجات مخلَصین] راه مستقیمی است که خود بر عهده دارم.'),
(1844, 15, 42, 'همانا برای تو بر بندگان [برگزیده‌ی] من تسلطّی نیست، مگر بر گمراهانی که تو را پیروی کنند.'),
(1845, 15, 43, 'و البته دوزخ وعده‌گاه همگی آنهاست.'),
(1846, 15, 44, 'هفت در دارد، برای هر در بخشی از آن گمراهان تقسیم شده‌اند.»'),
(1847, 15, 45, 'همانا پرهیزکاران در باغ‌ها و [کنار] چشمه‌سارانند.'),
(1848, 15, 46, '[به آنان خِطاب می‌شود:] «با سلامت و امنیت به باغ‌ها وارد شوید.»'),
(1849, 15, 47, 'و ما هرگونه کینه‌ای را در سینه‌های آنان برکنده‌ایم، [در نتیجه آنان] برادرانه بر تخت‌ها روبروی یکدیگرند.'),
(1850, 15, 48, 'در آنجا هیچ‌گونه رنجی به آنان نمی‌رسد، و از آنجا بیرون رانده نمی‌شوند.'),
(1851, 15, 49, '[ای پیامبر!] به بندگانم خبر ده که من بسیار بخشنده و مهربانم.'),
(1852, 15, 50, 'و البته عذاب من نیز همان عذاب دردناک است.'),
(1853, 15, 51, 'و آنان را از [داستان] مهمانان ابراهیم خبر ده.'),
(1854, 15, 52, 'آن‌گاه که بر او وارد شدند و سلام کردند. [ابراهیم] گفت: «ما از شما بیمناکیم!»'),
(1855, 15, 53, 'گفتند: «مترس! ما تو را به پسری دانا مژده می‌دهیم.»'),
(1856, 15, 54, 'ابراهیم گفت: «آیا با این که پیریِ من فرارسیده، مرا چنین بشارتی می‌دهید؟ پس به چه چیز [عجیبی] بشارت می‌دهید!»'),
(1857, 15, 55, '[فرشتگان] گفتند: «ما تو را به حقیقت بشارت دادیم، پس از ناامیدان مباش!»'),
(1858, 15, 56, '[ابراهیم] گفت: «جز گمراهان چه کسی از رحمت پروردگارش مأیوس می‌شود؟»'),
(1859, 15, 57, 'سپس گفت: «ای فرستادگان [الهی]! کار شما چیست؟»'),
(1860, 15, 58, 'گفتند: «ما به سوی قومی تبهکار فرستاده شده‌ایم [تا آنان را هلاک کنیم].'),
(1861, 15, 59, 'مگر خاندان لوط که ما آنان را نجات می‌دهیم.'),
(1862, 15, 60, 'مگر همسرش را که مقدّر کرده‌ایم از بازماندگان [در شهر و نابودشوندگان‌] باشد.»'),
(1863, 15, 61, 'پس چون فرستادگان [الهی] به سراغ خاندان لوط آمدند،'),
(1864, 15, 62, '[لوط] گفت: «شما گروهی ناشناس هستید!»'),
(1865, 15, 63, '[فرشتگان] گفتند: «در واقع ما آنچه را [از نزول عذاب که] درباره‌اش تردید داشتند، برای تو آورده‌ایم.'),
(1866, 15, 64, 'و ما به حقّ نزد تو آمده‌ایم و قطعاً ما راستگویانیم.'),
(1867, 15, 65, 'پس خاندانت را پاسی از شب [گذشته] حرکت بده و خودت از پشت سرشان برو و هیچ‌یک از شما [به پشت سرش] توجّه نکند. به آنجا که مأمور شده‌اید، بروید.»'),
(1868, 15, 66, 'و به لوط این امر حتمی را رساندیم که ریشه این گروهِ [تبهکار]، صبحگاهان قطع شده است.'),
(1869, 15, 67, 'و اهل شهر شادی‌کنان [برای تعرض به مهمانان به سوی خانه لوط] آمدند.'),
(1870, 15, 68, 'لوط گفت: «اینان مهمانان منند، آبروی مرا نبرید.'),
(1871, 15, 69, 'و از خدا پروا کنید و مرا خوار و شرمنده مسازید.»'),
(1872, 15, 70, 'آنها گفتند: «آیا ما تو را از [مهمان کردن] مردم منع نکردیم؟»'),
(1873, 15, 71, '[لوط] گفت: «اگر شما قصد کاری [درست] دارید، اینها دختران منند. [با آنان ازدواج کنید و از آلودگی بپرهیزید.]»'),
(1874, 15, 72, '[ای پیامبر!] به جان تو سوگند! که آنان در مستی خود سرگردان بودند.'),
(1875, 15, 73, 'پس به هنگام طلوع آفتاب، صیحه‌ای مرگبار آنان را فراگرفت.'),
(1876, 15, 74, 'پس آن شهر را زیر و رو کردیم و بر آنان سنگ‌هایی از گِلِ سخت باراندیم.'),
(1877, 15, 75, 'همانا در این [سرگذشت]، برای اشارت‌فهمان و تیزبینان، نشانه‌هایی روشن است.'),
(1878, 15, 76, 'و [ویرانه‌های] آن سرزمین، [هنوز] در کنار راه [کاروان‌ها] پا برجاست.'),
(1879, 15, 77, 'همانا در این ماجرا برای مؤمنان نشانه‌ای روشن است.'),
(1880, 15, 78, 'و به راستی اهالی سرزمین اَیکه [نیز] ستمگر بودند.'),
(1881, 15, 79, 'پس از آنان انتقام گرفتیم. و همانا [شهرهای ویران شده‌ی] این دو منطقه، در برابر چشمان شما آشکار است.'),
(1882, 15, 80, 'و [قوم ثمود که] اهالی حِجر [بودند] نیز، پیامبران را تکذیب کردند.'),
(1883, 15, 81, 'و ما آیات خود را به آنان ارائه دادیم، ولی آنها از آیات ما روی‌گردان شدند.'),
(1884, 15, 82, 'و آنان، خانه‌هایی امن در دل کوه‌ها می‌تراشیدند.'),
(1885, 15, 83, '[امّا سرانجام] صیحه [مرگبار]، صبحگاهان آنان را فراگرفت.'),
(1886, 15, 84, 'و آنچه به دست آورده بودند، به کارشان نیامد.'),
(1887, 15, 85, 'و ما آسمان‌ها و زمین و آنچه را میان آنهاست، جز به حق نیافریدیم. و قطعاً ساعت قیامت آمدنی است، پس [ای پیامبر!] به نیکویی [از لغزش‌ها] صرف نظر کن.'),
(1888, 15, 86, 'به راستی پروردگار تو همان آفریدگار داناست.'),
(1889, 15, 87, 'و به یقین ما هفت [آیه از سوره حمد با نزولِ] دوباره، و قرآن بزرگ را به تو دادیم.'),
(1890, 15, 88, '[ای پیامبر!] به آنچه که ما با آن گروه‌هایی از کفار را کامیاب کرده‌ایم، چشم مدوز. و بر آنان اندوه مخور و بال محبت خویش را برای مؤمنان فروگستر.'),
(1891, 15, 89, 'و بگو: «من، همان هشداردهنده‌ی روشنگرم!»'),
(1892, 15, 90, '[ما بر آنها عذابی می‌فرستیم،] همان‌گونه که بر تجزیه‌کنندگان [آیات الهی] فرستادیم.'),
(1893, 15, 91, 'آنان که قرآن را قطعه قطعه کردند. [آنچه به سودشان بود گرفتند، و آنچه نبود، رها کردند.]'),
(1894, 15, 92, 'پس به پروردگارت سوگند، ما از همه آنان بازخواست خواهیم کرد،'),
(1895, 15, 93, 'از آنچه انجام می‌داده‌اند.'),
(1896, 15, 94, 'پس آنچه را بدان مأمور شده‌ای، آشکار کن و از مشرکان روی گردان.'),
(1897, 15, 95, 'همانا ما [شرّ] استهزاکنندگان را از تو بازداشته‌ایم.'),
(1898, 15, 96, 'همانان که معبود دیگری را در کنار خداوند قرار می‌دهند. پس به زودی حقیقت را خواهند فهمید.'),
(1899, 15, 97, '[ای پیامبر!] البتّه ما می‌دانیم که سینه‌ات به خاطر آنچه می‌گویند، تنگ می‌شود.'),
(1900, 15, 98, 'پس [برای تقویت خود،] پروردگارت را با سپاس و ستایش، به پاکی یاد کن و از سجده‌کنندگان باش.'),
(1901, 15, 99, 'و پروردگارت را پرستش کن، تا امر یقینیِ [مرگ] بر تو فرارسد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'فرمان [قهر] خدا آمد. پس در آن شتاب نکنید. او منزه و برتر از هر چیزی است که با او شریک می‌سازند.'),
(1903, 16, 2, 'خداوند فرشتگان را، همراه با وحی که از فرمانِ اوست، بر هر کس از بندگانش که بخواهد، فرومی‌فرستد، که مردم را هشدار دهید که معبودی جز من نیست، پس تنها از من پروا کنید [و مخالف دستورم عمل نکنید.]'),
(1904, 16, 3, 'او آسمان‌ها و زمین را بر اساس حق آفرید، او از آنچه با او شریک می‌سازند، برتر است.'),
(1905, 16, 4, 'او انسان را از نطفه‌ای آفرید، پس آن‌گاه او آشکارا با [خداوند] دشمنی می‌ورزد.'),
(1906, 16, 5, 'و چهارپایان را آفرید، که برای شما در آنها، [وسیله‌ی] پوششی، و بهره‌هایی [دیگر] است و از [گوشت و شیر] آنها می‌خورید.'),
(1907, 16, 6, 'و برای شما در چهارپایان [شب هنگام] که از چراگاه برمی‌گردانید و [بامدادان] که به چراگاه می‌فرستید، شکوه و جلوه‌ای است.'),
(1908, 16, 7, 'و چهارپایان، بارهای سنگین شما را به شهری می‌برند که جز به رنجِ تن نمی‌توانستید به آن برسید. همانا پروردگار شما رئوف و مهربان است.'),
(1909, 16, 8, 'و اسبان و اَستَران و الاغ‌ها را آفرید تا بر آنها سوار شوید و [برای شما] تجمّلی [باشد] و [در آینده] چیزهایی را می‌آفریند که شما نمی‌دانید.'),
(1910, 16, 9, 'و بر خداست که راه میانه [و مستقیم را به مردم نشان دهد،] و برخی از راه‌ها منحرف است. و اگر خداوند بخواهد، همه‌ی شما را [به اجبار] هدایت می‌کند. [ولی سنت خداوند بر آزاد گذاشتن انسان‌هاست.]'),
(1911, 16, 10, 'اوست آن که برای شما از آسمان آبی فرستاد که نوشیدنی شما از آن است. و گیاهانی که [چهارپایان خود را] در آن می‌چرانید، از آن است.'),
(1912, 16, 11, 'خداوند به وسیله آن آب، برای شما کشت‌زار و زیتون و درختان خرما و انگور، و از هر نوع میوه‌ای می‌رویانَد. همانا در این امر برای آنان که می‌اندیشند، نشانه‌ای روشن است.'),
(1913, 16, 12, 'و خداوند شب و روز، و خورشید و ماه را در خدمت و بهره‌دهی شما قرار داد. و ستارگان به فرمان او تسخیر شده‌اند؛ البته در این امر برای گروهی که تعقل می‌کنند، نشانه‌هایی [از عظمت و قدرت] اوست.'),
(1914, 16, 13, 'و [نیز] آنچه را در زمین با رنگ‌های گوناگون برای شما پدید آوَرد. البته در این آفریده‌ها برای پندگیران عبرت و نشانه‌ای روشن است.'),
(1915, 16, 14, 'و اوست آن که دریا را رام کرد تا از آن گوشت تازه بخورید و زیورهایی برای پوشیدن از آن بیرون آورید. و می‌بینی که کشتی‌ها، سینه‌ی دریا را شکافته [و پیش می‌روند.] و به سراغ فضل و لطف او بروید و شاید سپاس‌گزار باشید.'),
(1916, 16, 15, 'و خداوند در زمین کوه‌هایی استوار افکند تا زمین، شما را نلرزانَد. و نهرهایی و راه‌هایی [قرار داد] تا راه یابید.'),
(1917, 16, 16, 'و نشانه‌های دیگری [در زمین قرار داد]، و آنان به وسیله‌ی ستاره، راه می‌یابند.'),
(1918, 16, 17, 'پس آیا آن که می‌آفریند، همانند کسی است که نمی‌آفریند؟ پس آیا یاد نمی‌کنید و پند نمی‌گیرید؟'),
(1919, 16, 18, 'و اگر بخواهید نعمت‌های خداوند را بشمارید، نمی‌توانید شُماره کنید. همانا خداوند آمرزنده‌ی مهربان است.'),
(1920, 16, 19, 'و خداوند آنچه را پنهان می‌کنید و آنچه را آشکار می‌سازید، می‌داند.'),
(1921, 16, 20, 'و کسانی را که به جای خدا می‌خوانند، هیچ چیز نمی‌آفرینند، و خود نیز آفریده شده‌اند.'),
(1922, 16, 21, 'آنها مرد‌گانند، نه زند‌گان، و نمی‌دانند چه وقت برانگیخته خواهند شد.'),
(1923, 16, 22, 'معبود شما، معبودی است یگانه، پس کسانی که به آخرت ایمان ندارند، دل‌هایشان به انکار خو کرده، و همانان مستکبرند.'),
(1924, 16, 23, 'بی‌گمان، آنچه را پنهان می‌کنند و آنچه را آشکار می‌سازند، خداوند می‌داند. همانا او مستکبران را دوست ندارد.'),
(1925, 16, 24, 'و هرگاه به آنان گفته شد: «پروردگارتان چه نازل کرده؟» گفتند: «افسانه‌های پیشینیان!»'),
(1926, 16, 25, 'آنان باید در روز قیامت، هم، بار گناهان خود را به تمامی، به دوش کشند، و هم، بخشی از بار گناهانِ کسانی که بدون علم، آنان را گمراه می‌نمایند. بدانید که چه بد باری را برمی‌دارند!'),
(1927, 16, 26, 'همانا کسانی که قبل از ایشان بودند [نیز] مکر ورزیدند، پس قهر خداوند به سراغ پایه‌های بِنای آنان آمد، پس سقف از بالای سرشان بر آنان فروریخت و از آنجا که اندیشه نمی‌کردند، عذاب الهی آمد.'),
(1928, 16, 27, 'سپس خداوند در روز قیامت، آنان را خوار و رسوا می‌کند و می‌فرماید: «شریکان من که درباره‌ی آنها [با پیامبر و مؤمنان] ستیزه و مخالفت می‌کردید، کجا هستند؟» دانشمندان گویند: «بی‌گمان امروز، ذلت و رسوایی بر کافران است!»'),
(1929, 16, 28, 'آن کسانی که فرشتگان جانشان را می‌گیرند [در حالی که] به خود ستم‌کار بوده‌اند، سر تسلیم فرومی‌آورند [و به دروغ می‌گویند:] «ما هیچ‌گونه کار بدی انجام نداده‌ایم!» آری! قطعاً خداوند به آنچه انجام داده‌اید، آگاه است.'),
(1930, 16, 29, 'پس، از درهای دوزخ وارد شوید که جاوِدانه در آنجا خواهید ماند. و به راستی چه بد است جایگاه متکبران!'),
(1931, 16, 30, 'و به پرهیزکاران گفته شد: «پروردگارتان چه نازل کرده است؟» گفتند: خیر» برای کسانی که نیکی کرده‌اند، در این دنیا نیکی است. و قطعاً سرای آخرت بهتر است. و چه نیکوست سرای پرهیزکاران!'),
(1932, 16, 31, 'به باغ‌هایی همیشگی داخل خواهند شد، که نهرها از زیر [درختان] آن جاری است. هر چه را بخواهند، برایشان در آنجا موجود است. خداوند این‌گونه پرهیزکاران را پاداش می‌دهد.'),
(1933, 16, 32, '[همان] کسانی که فرشتگان [جان‌های] آنان را به طور کامل می‌گیرند، در حالی که [از آلودگی‌های شرک و گناه] پاکند، [و به آنان] می‌گویند: «سلام بر شما! به پاداشِ آنچه همواره انجام می‌دادید، وارد بهشت شوید.»'),
(1934, 16, 33, 'آیا کافران جز آن که فرشتگانِ [مأمور قبض روح،] یا فرمان [قهر] پروردگارت به سراغشان آیند، انتظاری دارند؟ کسانی که پیش از آنان بودند نیز، چنین کردند و خداوند به آنان ظلم نکرد، ولی آنها به خودشان ستم می‌کردند.'),
(1935, 16, 34, 'پس کیفر بدی‌هایی که انجام دادند، به آنان رسید. و آنچه که آنان به مسخره می‌گرفتند، آنان را فراگرفت.'),
(1936, 16, 35, 'و کسانی که شرک ورزیدند، گفتند: «اگر خدا می‌خواست، نه ما و نه پدرانمان، هیچ چیز را جز او نمی‌پرستیدیم. و بدون حکم او، هیچ چیزی را حرام نمی‌کردیم.» کسانی که پیش از اینان بودند نیز، این‌گونه برخورد می‌کردند، پس مگر جز ابلاغ روشن، وظیفه‌ی دیگری بر [عهده‌ی] پیامبران است؟'),
(1937, 16, 36, 'و به راستی، ما در میان هر اُمّتی، پیامبری را برانگیختیم [تا به مردم بگوید] که: «خدا را بپرستید و از طاغوت دوری کنید.» پس گروهی از مردم کسانی هستند که خداوند، هدایتشان کرد، و بر بعضی از آنان، گمراهی سزاوار شد، پس در زمین بگردید، تا ببینید پایان کار تکذیب‌کنندگان چگونه بوده است؟'),
(1938, 16, 37, '[ای پیامبر!] اگر چه بر هدایت آنان حرص می‌ورزی، اما [بدان که] خداوند کسی را که [به خاطر عملش] در گمراهی وانهاده، هدایت نمی‌کند. و برای آنان هیچ یاوری نیست.'),
(1939, 16, 38, 'و با سخت‌ترین سوگندشان، به خدا قسم خوردند که هر کس می‌میرد، خداوند او را برنخواهد انگیخت. آری! [رستاخیز مردگان] وعده‌ی حقی است بر خداوند، اما بیشتر مردم نمی‌دانند.'),
(1940, 16, 39, '[رستاخیز] برای آن است که [خداوند] چیزی را که مردم در آن اختلاف می‌کنند، روشن سازد. و کسانی که کفر ورزیدند، بدانند که آنها خود دروغگو بودند، [نه پیامبران].'),
(1941, 16, 40, '[زنده کردن مردگان برای ما دشوار نیست‌؛ زیرا] فرمان ما درباره چیزی، چون اراده کنیم، تنها این است که می‌گوییم: باش، پس [بی‌دِرنگ‌] خواهد بود.'),
(1942, 16, 41, 'و کسانی که پس از آن که ستم دیدند، در راه خدا هجرت کردند، بی‌شک ما در همین دنیا، جایگاه نیکویی به آنان خواهیم داد. و اگر می‌دانستند، پاداش آخرت بزرگ‌تر است.'),
(1943, 16, 42, 'کسانی که صبر کردند، و بر پروردگارشان توکل می‌نمایند.'),
(1944, 16, 43, 'و ما قبل از تو نیز، جز مردانی که به آنها وحی می‌کردیم، نفرستادیم. [به آنها از آمدن تو تعجب می‌کنند. بگو:] «اگر نمی‌دانید، از اهل ذکر بپرسید.»'),
(1945, 16, 44, '[ما پیامبران پیش از تو را] همراه با معجزات و کتب [آسمانی فرستادیم] و به سوی تو ذکر [یعنی قرآن] را فروفرستادیم، تا برای مردم آنچه را که به سویشان نازل شده، روشن بیان کنی. و شاید بیندیشند.'),
(1946, 16, 45, 'آیا کسانی که بدی‌ها را با حیله و نیرنگ انجام دادند، در امانند از این که خداوند آنان را در زمین فروبرد؟ یا از جایی که پیش‌بینی و اندیشه نمی‌کنند، عذاب الهی به سراغشان آید؟'),
(1947, 16, 46, 'یا [قهر الهی] در حین تلاش و کوششان، ناگهان آنان را بگیرد، پس نتوانند آن عذاب را خنثی و عاجز نمایند؟'),
(1948, 16, 47, 'یا آن که در حال دلهره، آنان را بگیرد. البته پروردگار شما رئوف و مهربان است.'),
(1949, 16, 48, 'آیا چیزهایی را که خداوند آفریده، ندیده‌اند؟ که چگونه از راست و چپ سایه‌هایشان می‌گسترد و در حال سجده، فروتنانه خضوع دارند.'),
(1950, 16, 49, 'و آنچه در آسمان‌ها و آنچه در زمین است، از جنبنده و فرشتگان، تنها برای خداوند سجده می‌کنند و تکبّر و سرپیچی نمی‌کنند.'),
(1951, 16, 50, 'و از پروردگارشان که حاکم بر آنهاست، می‌ترسند و آنچه را که به آنان فرمان داده شده، انجام می‌دهند.'),
(1952, 16, 51, 'و خداوند فرمود: «دو معبود نگیرید. معبود همان خدای یکتاست، پس تنها از من بترسید.»'),
(1953, 16, 52, 'و آنچه در آسمان‌ها و زمین است، از آن اوست. و پرستش و فرمان‌برداری پیوسته برای اوست، پس آیا از غیر خداوند پروا می‌کنید؟'),
(1954, 16, 53, 'و هر نعمتی دارید، از خداوند است. [و] هرگاه نگرانی و بلا به شما برسد، تنها به سوی او ناله می‌کنید.'),
(1955, 16, 54, 'اما همین که نگرانی و مِحنت را از شما برطرف کرد، گروهی از شما نسبت به پروردگارشان شرک می‌ورزند. [و دیگران را در دفع بلا مؤثر می‌دانند.]'),
(1956, 16, 55, 'تا آنچه را به آنان داده‌ایم، [به خاطر شرک‌ورزی] ناسپاسی کنند، پس [چند روزی] بهره‌مند شوید که به زودی خواهید دانست [سزای شما چیست؟]'),
(1957, 16, 56, 'و [مشرکان] سهمی از آنچه را که ما روزی آنان کرده‌ایم، برای بت‌هایی قرار می‌دهند که هیچ نمی‌دانند. به خدا سوگند از آنچه [دروغ‌] می‌بندید، بازخواست خواهید شد.'),
(1958, 16, 57, 'و برای خداوند دختران را قرار می‌دهند، در حالی که او [از فرزند داشتن] منزّه است؛ ولی برای خودشان آنچه را علاقه دارند [پسران را برمی‌گزینند].'),
(1959, 16, 58, 'و هرگاه به یکی از آن مشرکان مژده [تولد] دختر داده شود، چهره‌اش سیاه می‌شود، در حالی که خشم خود را فرومی‌برد.'),
(1960, 16, 59, 'به خاطر مژده تلخی که به او داده شده، از میان مردم فراری می‌شود. آیا این دختر را همراه با ننگ و خفت نگاه دارد؟ یا او را در خاک پنهان کند؟ بدانید که چه بد قضاوت می‌کنند!'),
(1961, 16, 60, 'صفات زشت، برای کسانی است که به آخرت ایمان ندارند؛ ولی برای خدا صفات برتر است. و اوست خداوند عزیز و حکیم.'),
(1962, 16, 61, 'و اگر خداوند مردم را به خاطر ستمشان بازخواست و عذاب کند، هیچ جنبنده‌ای را روی آن [زمین] باقی نمی‌گذارد، ولی [خداوند] کیفر آنان را تا زمان معینی به تأخیر می‌اندازد، پس همین که اجلشان فرارسد، نه ساعتی دیر می‌کنند و نه ساعتی پیش می‌افتند.'),
(1963, 16, 62, 'و مشرکان، آنچه را برای خود نمی‌پسندند، برای خدا قرار می‌دهند و زبانشان به دروغ می‌گوید که نیکوتر برای آنان است. حقا که آتش برای آنان است و آنان پیشگامان [دوزخ]اند.'),
(1964, 16, 63, 'به خدا سوگند که ما به سوی اُمت‌های پیش از تو [نیز] پیامبرانی فرستادیم، پس شیطان کارهای [ناپسند] ایشان را برایشان زیبا جلوه داد، [آن شیطان] امروز نیز سرپرست آنهاست و برای آنان عذابی دردناک است.'),
(1965, 16, 64, 'و ما کتاب را بر تو فرونفرستادیم، مگر برای این که آنچه را در آن اختلاف کرده‌اند، برای آنان روشن بیان کنی. و [این کتاب] برای گروهی که ایمان می‌آورند، مایه هدایت و رحمت است.'),
(1966, 16, 65, 'و خداوند از آسمان آبی فرستاد، پس به واسطه آن زمین را پس از آن که پژمرده بود، زنده کرد. البته در این [باریدن و روییدن] برای گروهی که [سخن حق را با جان و دل] می‌شنوند، نشانه‌ای روشن است.'),
(1967, 16, 66, 'و بی‌گمان برای شما در [آفرینش] چهارپایان عبرتی است. ما از آنچه در شکم دارند، از میان غذاهای هضم شده و خون، شیری ناب به شما می‌نوشانیم که برای نوشندگان گواراست.'),
(1968, 16, 67, 'و از میوه‌های درختان خرما و انگور، هم [نوشیدنی] مست‌کننده، و هم رزق نیکو می‌سازید. البته در این امر برای گروهی که می‌اندیشند، نشانه‌ی روشنی است.'),
(1969, 16, 68, 'و پروردگارت به زنبور عسل وحی کرد که: «از بعضی کوه‌ها و درختان و از داربست‌هایی که مردم می‌سازند، خانه‌هایی بساز.'),
(1970, 16, 69, 'سپس از [گُل] همه‌ی میوه‌ها بخور، آن‌گاه راه‌های پروردگارت را خالصانه طی کن.» از شکم آنها، نوشیدنی رنگارنگ بیرون می‌آید که در آن شفای مردم است. البته در این [زندگی زنبور عسل،] برای کسانی که فکر می‌کنند، قطعاً عبرتی است.'),
(1971, 16, 70, 'و خداوند شما را آفرید، سپس [جان] شما را می‌گیرد و بعضی از شما را به پست‌ترین دوران عمر [و کهن‌سالی،] بازمی‌گرداند، تا آنجا که بعد از دانستن [به خاطر پیری همه را فراموش می‌کند و دیگر] چیزی نمی‌داند. البته خداوند آگاه و توانمند است.'),
(1972, 16, 71, 'و خدا بعضی از شما را بر بعضی دیگر در روزی برتری داده است. پس کسانی که فزونی یافته‌اند، حاضر نیستند از روزی خود به زیردستانشان بپردازند تا با هم برابر شوند. پس آیا نعمت خدا را انکار می‌کنند؟'),
(1973, 16, 72, 'و خداوند از جنس خودتان، همسرانی برایتان قرار داد، و از همسرانتان برای شما، فرزندان و نوادگانی قرار داد و از پاکیزه‌ها شما را روزی داد. پس آیا [باز هم] به باطل ایمان می‌آورند و به نعمت الهی کفر می‌ورزند؟'),
(1974, 16, 73, 'و به جای خداوند بت‌هایی را می‌پرستند که برای آنان مالک هیچ‌گونه رزقی از آسمان‌ها و زمین نیستند و نمی‌توانند [روزی دهند.]'),
(1975, 16, 74, 'پس برای خداوند [این‌گونه] مَثَل‌ها را نزنید که خداوند می‌داند و شما نمی‌داند.'),
(1976, 16, 75, 'خداوند، برده‌ی زرخریدی را مَثَل می‌زند که بر هیچ چیز توانایی ندارد، و کسی که [آزاد است و] ما به او رزق نیکو از جانب خود داده‌ایم، پس او مخفیانه و آشکارا از آن رزق انفاق می‌کند. آیا [این دو نفر] برابرند؟ [اگر نزد شما، توانا و ناتوان برابر نیستند، پس چگونه سنگ و چوب را با خداوند برابر می‌دانید؟] ستایش مخصوص خداست، امّا بیشترشان نمی‌دانند.'),
(1977, 16, 76, 'و خداوند دو مرد را مثال می‌زند که یکی از آن دو گُنگ است که بر هیچ چیز توانایی ندارد و سربار مولای خویش است، به‌گونه‌ای که هر جا او را می‌فرستد، هیچ سودی با خود نمی‌آورد. آیا او با کسی که به عدالت فرمان می‌دهد و خود بر راه راست می‌رود، برابر است؟'),
(1978, 16, 77, 'و غیب آسمان‌ها و زمین از آنِ خداست. و برپایی قیامت [در سرعت و آسانی،] جز مانند چشم برهم‌زدن یا نزدیک‌تر نیست. البته خداوند بر هر چیزی تواناست.'),
(1979, 16, 78, 'و خداوند شما را از شکم مادرانتان بیرون آورد، در حالی که هیچ چیز نمی‌دانستید. و برای شما گوش و چشم‌ها و دل‌ها قرار داد، شاید شما شکر کنید.'),
(1980, 16, 79, 'آیا به پرندگان نمی‌نگرند؟ در حالی که در فضای آسمان رام‌شده [او] هستند. جز خداوند آنها را نگاه نمی‌دارد. البته در این [پرواز] برای گروهی که ایمان می‌آورند، نشانه‌هایی روشن است.'),
(1981, 16, 80, 'و خداوند برای شما از خانه‌هایتان، مایه‌ی آرامش قرار داد. و برای شما از پوست‌های چهارپایان، خیمه‌هایی قرار داد، که روز کوچ شما و روز اقامت شما، [حمل و نقل] آن را سبک و خفیف می‌یابید. و از پشم و کرک و موهای آنها، برای مدتی لوازم و اسباب زندگی قرار داد.'),
(1982, 16, 81, 'و خداوند از آنچه آفریده، برای شما سایه‌هایی قرار داد. و از کوه برایتان، غارهایی نهاد. و برای شما لباس‌هایی مقرر کرد که شما را از گرما [و سرما] حفظ می‌کند. و لباس‌هایی که شما را از آسیب‌های یکدیگر نگاه می‌دارد. این‌گونه [خداوند] نعمت خود را بر شما تمام می‌کند، باشد که خود را تسلیم او کنید.'),
(1983, 16, 82, 'پس [ای پیامبر!] اگر روی برتافتند، تنها تبلیغ روشن بر [عهده] توست.'),
(1984, 16, 83, '[آنها] نعمت خداوند را می‌شناسند، سپس آن را انکار می‌کنند. و بیشترِ آنان کافر و ناسپاسند.'),
(1985, 16, 84, 'و [یاد کن] روزی را که از هر اُمّتی شاهدی برانگیزیم. سپس به کسانی که کفر ورزیدند، اجازه [هیچ کلامی] داده نخواهد شد و آنها برای عذرخواهی فراخوانده نمی‌شوند.'),
(1986, 16, 85, 'و چون کسانی که ستم کردند، عذاب را ببینند، [دیگر] نه [عذاب] از آنان سبک می‌گردد و نه مهلت می‌یابند.'),
(1987, 16, 86, 'و چون کسانی که شرک ورزیدند، شریکان [پنداری] خود را [در قیامت] ببینند، می‌گویند: «پروردگارا! اینان شریکان ما بودند که ما به جای تو می‌خواندیم.» [ولی شریکان به سخن می‌آیند،] حرف آنان را به خودشان برمی‌گردانند، [و پاسخشان می‌دهند] که: «شما بی‌شک دروغگویید!»'),
(1988, 16, 87, 'در چنین روزی به پیشگاه خدا سرِ تسلیم فرود آورند و آنچه را به دروغ می‌بافتند، از آنان محو شود.'),
(1989, 16, 88, 'کسانی که کافر شدند و [مردم را] از راه خدا بازداشتند، به سزای آن که همواره فساد می‌کردند، عذابی بر عذابشان افزودیم.'),
(1990, 16, 89, 'و [یاد کن] روزی را که در هر امّتی، شاهدی بر آنان از خودشان برانگیزیم، و تو را بر آنان گواه آوریم. و بر تو این کتاب را نازل کردیم که روشنگر هر چیز است و مایه‌ی هدایت و رحمت و بشارت برای مسلمانان است.'),
(1991, 16, 90, 'خداوند [مردم را] به عدل و احسان و دادن [حق] خویشاوندان، فرمان می‌دهد و از کارهای زشت و ناپسند و تجاوز، نهی می‌فرماید. او شما را پند می‌دهد، باشد که متذکر شوید.'),
(1992, 16, 91, 'و آن‌گاه که پیمان بستید، به عهد خدا وفادار باشید و سوگندها را پس از استوار ساختنش نشکنید؛ زیرا که خدا را بر خودتان کفیل [و گواه و ضامن] قرار داده‌اید. البته خداوند آنچه را انجام می‌دهید، می‌داند.'),
(1993, 16, 92, 'و مانند آن زن [سبک مغز] نباشید که بافته‌ی خویش را پس از محکم بافتن، وامی‌تابید! [چنین نباشید] که سوگندهایتان را میان خود به بهانه‌ی این که گروهی ز گروهی دیگر از نظر نفرات و امکانات افزون‌ترند، مایه‌ی خیانت و فساد قرار دهید. جز این نیست که خداوند شما را با آن سوگندها، آزمایش می‌کند و حتماً آنچه را که در آن اختلاف می‌کردید، در قیامت برایتان روشن خواهد ساخت.'),
(1994, 16, 93, 'و اگر خداوند خواسته بود، [همه‌ی شما را به اجبار] یک اُمتِ [مؤمن و] متحد قرار داده بود، [تا میان شما هیچ اختلافی نباشد، ولی این اجبار و اکراه، خِلاف سنت و برنامه اوست. بنابراین] هر که را بخواهد، گمراه می‌کند [و به حال خودش وامی‌گذارد] و هر که را بخواهد [و شایسته ببیند،] هدایت می‌کند. و قطعاً شما از آنچه انجام می‌دادید، بازخواست خواهید شد.'),
(1995, 16, 94, 'و سوگندهایتان را مایه‌ی فریب میان خودتان قرار ندهید، تا مبادا گامی بعد از استواری‌اش بلغزد، و به خاطر بازداشتن [مردم] از راه خدا، بدی را خواهید چشید. و برای شما عذابی بزرگ است.'),
(1996, 16, 95, 'و پیمان خدا را با بهای کمی معامله نکنید، زیرا اگر بدانید، آنچه نزد خداست، همان برای شما بهتر است.'),
(1997, 16, 96, 'آنچه نزد شماست، به پایان می‌رسد، و آنچه نزد خداست، باقی و پایدار است. و کسانی که صبر پیشه کردند، پاداششان را بر پایه‌ی نیکوترین کاری که می‌کرده‌اند، خواهیم داد.'),
(1998, 16, 97, 'هر کس از مرد یا زن که مؤمن باشد و کار شایسته کند، حتماً به زندگی پاک و پسندیده‌ای زنده‌اش می‌داریم و قطعاً پاداششان را بر پایه‌ی نیکوترین کاری که می‌کرده‌اند، خواهیم داد.'),
(1999, 16, 98, 'پس هرگاه خواستی قرآن بخوانی، از [شرّ] شیطان رانده‌شده به خداوند پناه ببر!'),
(2000, 16, 99, 'البته شیطان برکسانی که ایمان آورده و بر پروردگارشان توکل می‌کنند، تسلطی ندارد.'),
(2001, 16, 100, 'غلبه و سلطه‌ی شیطان تنها بر کسانی است که [با پیروی از او] سلطه و ولایت او را می‌پذیرند، و [بر] کسانی است که برای خدا شریک قرار داده‌اند.'),
(2002, 16, 101, 'و هرگاه آیه‌ای را [که دربردارنده حکم جدیدی است‌] جایگزین آیه‌ای کنیم، و خداوند به آنچه نازل می‌کند، داناتر است، مخالفان می‌گویند: بی‌گمان تو دروغ‌بافی!» [چنین نیست،] بلکه بیشترشان [اسرار تبدیلِ احکام را] نمی‌دانند.'),
(2003, 16, 102, 'بگو آن [قرآن] را روح‌القُدُس از جانب پروردگارت به حق نازل کرده است، تا کسانی را که ایمان آورده‌اند، ثابت‌قدم و استوار گرداند و برای مسلمانان [مایه‌ی] هدایت و بشارت باشد.'),
(2004, 16, 103, 'و البته ما می‌دانیم که مخالفان تو می‌گویند: «یقیناً بشری آن [قرآن] را به او می‌آموزد.» [این گونه نیست، زیرا] زبان کسی که [این قرآن را] به او نسبت می‌دهند، غیر عربی است، در حالی که این [قرآن] به زبان عربیِ فصیح و روشن است.'),
(2005, 16, 104, 'کسانی که به آیات خداوند ایمان نمی‌آورند، خداوند آنان را هدایت نمی‌کند و برایشان عذابی دردناک است.'),
(2006, 16, 105, 'البته دروغ را تنها کسانی می‌سازند که آیات خدا را باور ندارند و آنان خود، دروغگویانند [نه پیامبری که با تمام وجود به خدا ایمان دارد].'),
(2007, 16, 106, 'کسی که پس از ایمان به خدا، کافر شود، [به عذاب خدا گرفتار آید،] مگر کسی که او را به زور وادار کرده‌اند [که با زبان، اظهار کفر کند، اما] قلبش به ایمان مطمئن است، بلکه کسی که سینه به روی کفر بگشاید [و به استقبال کفر برود]، خشمی سخت از سوی خدا بر آنان است و آنان را عذابی بزرگ خواهد بود.'),
(2008, 16, 107, 'آن [قهر الهی] به خاطر این است که آنان، زندگی دنیا را بر آخرت برگزیدند، و قطعاً خداوند گروه کافران را هدایت نمی‌کند.'),
(2009, 16, 108, 'آنانند کسانی که خداوند [به واسطه‌ی کفرشان‌] بر دلها و گوش و چشمانشان، مهر نهاده است [و نمی‌توانند حقّ را درک کنند]. و آنان همان غافلانند.'),
(2010, 16, 109, 'شک نیست که آنان در آخرت همان زیانکارانند.'),
(2011, 16, 110, 'پروردگارت برای کسانی که پس از آزار دیدن، هجرت کرده، به جهاد برخاسته و پایداری نمودند، از آن پس قطعاً آمرزنده‌ی مهربان است.'),
(2012, 16, 111, 'روزی که هر کس می‌آید، [تنها] از خویش دفاع می‌کند و به هر کس [پاداش و کیفر] آنچه کرده است، تمام داده می‌شود و آنان مورد ستم قرار نمی‌گیرند.'),
(2013, 16, 112, 'و خداوند [برای شما] آبادی‌ای را مثال می‌زند که امن [و آرام] بود، روزیش از هر سو فراوان می‌رسید، اما [مردم آن آبادی] نعمت‌های خدا را ناسپاسی کردند، پس خداوند به [سزای] آنچه انجام می‌دادند، پوشش فراگیر گرسنگی و ترس را بر آنان چشانید.'),
(2014, 16, 113, 'همانا پیامبری از میان خودشان به سراغشان آمد، اما او را تکذیب کردند، پس ایشان را در حالی که ستمکار بودند، عذاب فراگرفت.'),
(2015, 16, 114, 'پس، از آن چیزهای حلال و پاکیزه‌ای که خداوند روزی شما کرده، بخورید. و اگر تنها او را می‌پرستید، نعمت خدا را شکر گزارید.'),
(2016, 16, 115, '[خداوند] فقط مردار و خون و گوشت خوک و آنچه را با نام غیرخدا ذبح کرده‌اند، بر شما حرام کرده است، پس کسی که [به خوردن اینها] ناچار شود، بدون آن که [قصد] تجاوز و تعدی [به حکم خداوند] داشته باشد و [از نیاز ضروری] تجاوز نکند، [گناهی بر او نیست‌] زیرا خداوند بخشنده‌ی مهربان است.'),
(2017, 16, 116, 'و با هر دروغی که به زبانتان می‌آید، نگویید: «این حلال است و آن حرام!» تا بر خدا دروغ بسته باشید. زیرا کسانی که بر خدا دروغ می‌بندند، رستگار نمی‌شوند.'),
(2018, 16, 117, '[سرچشمه همه دروغ‌بستن‌ها رسیدن به دنیایی است که] بهره‌ای اندک است، در حالی که برای آنان [در آخرت] عذابی دردناک است.'),
(2019, 16, 118, 'و برکسانی که یهودی بودند، آنچه را که پیش از این بر تو نقل کردیم، [نیز] حرام کردیم، و ظلمی بر آنان نکردیم، [تحریم ما، کیفر] ستمی بود که آنان بر خویش روا داشته بودند.'),
(2020, 16, 119, 'سپس پروردگارت برای کسانی که از روی نادانی کار بدی کردند، آنگاه توبه کردند و به کار شایسته پرداختند، پس از آن توبه، قطعاً بخشنده و مهربان است.'),
(2021, 16, 120, 'به راستی ابراهیم [به تنهایی] یک اُمت بود. در برابر خدا خاضع و فرمان‌بر و حق‌گرا بود. و از مشرکان نبود.'),
(2022, 16, 121, 'او نسبت به نعمت‌های خداوند شکرگزار بود. [خدا] او را برگزید و به راهی راست هدایتش کرد.'),
(2023, 16, 122, 'و در دنیا به او نیکی دادیم. و قطعاً او در آخرت [نیز] از صالحان و شایستگان است.'),
(2024, 16, 123, 'سپس به تو وحی کردیم که از آیین ابراهیمِ حق‌گرا پیروی کن که از مشرکان نبود.'),
(2025, 16, 124, '[حکم] تعطیلی [روز] شنبه بر کسانی قرار داده شد که در آن اختلاف کردند. و قطعاً پروردگارت در روز قیامت درباره‌ی آنچه آنان [یهودیان] پیوسته در آن اختلاف می‌کردند، قضاوت خواهد کرد.'),
(2026, 16, 125, '[ای پیامبر! مردم را] با حکمت [و گفتار استوار و منطقی] و پند نیکو، به راه پروردگارت بخوان و [با مخالفان] به شیوه‌ای که نیکوتر است، بحث و گفتگو کن. همانا پروردگارت به کسی که از راه او منحرف شده، آگاه‌تر است و [نیز] او هدایت‌یافتگان را بهتر می‌شناسد.'),
(2027, 16, 126, 'و اگر [ستمگر را] کیفر می‌کنید، چنان مجازات کنید که شما را بدان کیفر کردند، و البته اگر شکیبایی ورزید [و از مجازات کردن بگذرید،] این کار برای شکیبایان بهتر است.'),
(2028, 16, 127, '[و ای پیامبر!] صبر پیشه کن و صبر تو جز [به یاری و توفیق] خداوند نیست. و بر آنان اندوه مخور. و از نیرنگی که می‌کنند، دلتنگ مباش.'),
(2029, 16, 128, 'زیرا که خداوند با کسانی است که تقوا پیشه کردند و [با] کسانی [است] که آنان نیکوکارند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاک و منزّه است آن [خدایی‌] كه بنده‌اش [محمّد‌] را شبانه از مسجدالحرام تا مسجد‌الاقصی كه اطرافش را بركت داده‌ایم، برد، تا [بخشی‌] از نشانه‌های خود را به او نشان دهیم. یقیناً او شنوا و بیناست.'),
(2031, 17, 2, 'ما به موسی كتاب دادیم و او را هدایتگر بنی‌اسرائیل قرار دادیم، كه: «غیر از من تكیه‌گاه و كارسازی نگیرید.»'),
(2032, 17, 3, '[ای بنی‌اسرائیل!] ای فرزندانِ كسانی كه آنان را همراهِ نوح، سوار [بر كشتی] كردیم و [نجات دادیم!] همانا نوح، بنده‌ای بسیار شكرگزار بود [شما نیز مانند او باشید، تا نجات یابید].'),
(2033, 17, 4, 'ما در كتاب [تورات]، به بنی‌اسرائیل اعلام كردیم كه قطعاً شما دو بار در زمین فساد می‌كنید و برتری‌جوییِ بزرگی خواهید كرد.'),
(2034, 17, 5, 'پس همین كه وعده‌ی نخستین فتنه فرارسد، گروهی از بندگان توانمند و جنگجوی خویش را [برای سركوب شما] برانگیزیم. پس درون خانه‌ها را جستجو كنند [تا فتنه‌گران را یافته، كیفر دهند] و آن، وعده‌ای قطعی است.'),
(2035, 17, 6, 'سپس، جنگ را به سود شما و علیه آنان برگردانیم و شما را با اموال و فرزندان كمک كنیم و نفرات شما را بیشتر قرار دهیم.'),
(2036, 17, 7, 'اگر نیكی كنید، به خود نیكی كرده‌اید. و اگر بدی كنید، به خود بد كرده‌اید. پس همین كه وعده‌ی دوم فرارسد، [بندگانی دیگر را می‌فرستیم،] تا سیمای شما را زشت نمایند و [برای گرفتن قدرت] به مسجدالاقصی وارد شوند، همان‌گونه كه بار نخست وارد شدند و تا آنچه را زیر سلطه درآورده‌اند، به سختی ویران كنند.'),
(2037, 17, 8, 'امید است كه پروردگارتان بر شما رحم كند و اگر بازگردید [و دوباره فتنه كنید]، ما نیز برمی‌گردیم [و قهر خود را می‌فرستیم]. و ما دوزخ را برای كافران، زندانی سخت قرار داده‌ایم.'),
(2038, 17, 9, 'به راستی این قرآن، به استوارترین راه، هدایت می‌كند. و به مؤمنانی كه كارهای شایسته انجام می‌دهند، بشارت می‌دهد كه برایشان پاداش بزرگی است.'),
(2039, 17, 10, 'و آنان كه به آخرت ایمان نمی‌آورند، برایشان عذابی دردناک، آماده كرده‌ایم.'),
(2040, 17, 11, 'و انسان، همان‌گونه كه نیکی را می‌طلبد، بدی را هم می‌طلبد. و انسان همواره عجول است.'),
(2041, 17, 12, 'و ما شب و روز را دو نشانه قرار دادیم، پس [ماه،] نشانه‌ی شب را زدودیم و [خورشید،] نشانه‌ی روز را روشنگر ساختیم تا فضل و روزی از پروردگارتان بجویید، و تا شمار سال‌ها و حساب را بدانید. و ما هر چیز را به روشنی بیان كردیم.'),
(2042, 17, 13, 'و كارنامه‌ی هر انسانی را در گردنش بسته‌ایم. و روز قیامت برای او نوشته‌ای بیرون می‌آوریم كه آن را در برابر خود گشوده می‌بیند.'),
(2043, 17, 14, '«كتابت را بخوان! كافی است كه امروز، خود حسابگر خویش باشی.»'),
(2044, 17, 15, 'هر كس هدایت یافت، پس به سود خویش هدایت یافته است و هر كس گمراه شد، تنها به زیان خویش گمراه شده است و هیچ كس بار [گناه] دیگری را به دوش نمی‌كشد. و ما تا پیامبری برنیانگیزیم [و اتمام حجت نكنیم، كسی را] عذاب نمی‌كنیم.'),
(2045, 17, 16, 'و هرگاه بخواهیم [مردم] منطقه‌ای را [به كیفر گناهشان] هلاک كنیم، به مرفّهین و خوش‌گذرانانشان فرمانِ [بندگی و دوری از گناه‌] می‌دهیم، چون در آن، سرپیچی و عصیان می‌كنند، مستحق عذاب و قهر الهی می‌شوند، سپس آنجا را به سختی در هم می‌كوبیم.'),
(2046, 17, 17, 'و چه بسیار مردمانی را كه پس از نوح نابود كردیم. و همین كافی است كه پروردگارت به گناهان بندگانش آگاه و بیناست.'),
(2047, 17, 18, 'هر كس پیوسته زندگیِ دنیایِ زودگذر را بخواهد، به مقداری كه بخواهیم و برای هر كس اراده كنیم، در این دنیا به شتاب فراهم می‌كنیم، سپس جهنّم را برایش قرار می‌دهیم كه شرمنده و رانده، وارد آن خواهد شد.'),
(2048, 17, 19, 'و هر كس خواهان آخرت باشد و برای آن تلاش شایسته و در خور كند، و مؤمن باشد، تلاش آنان سپاس گزارده می‌شود.'),
(2049, 17, 20, 'ما از عطای پروردگارت، هر یك از دو گروه دنیاطلب و آخرت‌خواه را [در این دنیا] كمك می‌كنیم. و عطای پروردگارت [در این دنیا از كسی‌] منع نمی‌شود.'),
(2050, 17, 21, 'بنگر كه چگونه بعضی از آنان را بر بعضی دیگر برتری دادیم. و قطعاً درجات آخرت، بزرگ‌تر و ارزشمندتر است.'),
(2051, 17, 22, 'با خداوند، معبود دیگری قرار مده كه نكوهیده و سرافكنده خواهی نشست.'),
(2052, 17, 23, 'پروردگارت مقرّر داشت كه جز او را نپرستید و به پدر و مادر نیكی كنید. اگر یكی از آن دو یا هر دو نزد تو به پیری رسیدند، [در مقابل آنها اظهار ناراحتی مكن، حتی] به آنان «اُف» مگو و آنان را از خود مران. و با آنان سنجیده و بزرگوارانه سخن بگو.'),
(2053, 17, 24, 'و از روی مهربانی و لطف، بال فروتنی خویش را برای آنان فرود آور و بگو: «پروردگارا! بر آن دو رحمت آور، همان‌گونه كه مرا در كودكی تربیت كردند.»'),
(2054, 17, 25, 'پروردگارتان به آنچه در درون شماست، آگاه‌تر [از خودتان] است. اگر صالح باشید، قطعاً او آمرزنده‌ی توبه‌كنندگان است.'),
(2055, 17, 26, 'حقّ خویشاوند و بیچاره و در راه مانده را ادا كن. و هیچ‌گونه ولخرجی و اسرافی مكن.'),
(2056, 17, 27, 'بی‌تردید اسراف‌كاران برادران شیطان‌هایند. و شیطان نسبت به پروردگارش همواره ناسپاس است.'),
(2057, 17, 28, 'و اگر [به خاطر تهی‌دستی] از آنان [كه به پرداخت حقّشان سفارش شدی،‌] روی بگردانی، [و این روی‌گردانی‌] برای طلب رزقی است كه از پروردگارت، رسیدن به آن را امید داری، پس با آنان [تا رسیدن رزق خدا] سخنی نرم و امیدوار كننده بگو.'),
(2058, 17, 29, 'و نه دست خود را به گردنت بسته بدار [كه هیچ انفاقی نكنی]، و نه آن را كاملاً باز بگذار كه [چیزی برای خودت نماند و] سرزنش شده و حسرت خورده [به كنجی] بنشینی.'),
(2059, 17, 30, 'پروردگارت برای هر كس بخواهد، روزی را گشاده یا تنگ می‌سازد، همانا او نسبت به بندگانش آگاه و بیناست.'),
(2060, 17, 31, 'فرزندان خود را از بیم تنگدستی نكشید! ما هستیم كه آنان و شما را روزی می‌دهیم. یقیناً كشتن آنان گناهی است بزرگ!'),
(2061, 17, 32, 'و به زنا نزدیک نشوید، كه كاری زشت، و راهی بد است.'),
(2062, 17, 33, 'و كسی را كه خداوند، [كشتنش را] حرام كرده است، نكشید، مگر به حقّ. و هر كس مظلوم كشته شود، قطعاً برای ولیّ او، تسلّطی بر قاتل قرار داده‌ایم، پس نباید در كشتن [و قصاص] زیاده‌روی كند، چرا كه او مورد حمایت [خداوند] قرار گرفته است.'),
(2063, 17, 34, 'و به مال یتیم، جز به بهترین راه [كه به نفع او باشد،] نزدیک نشوید، تا آن كه به حدّ بلوغ و رشدش برسد. [آن‌گاه اموالش را به او برگردانید.] و به پیمان وفا كنید، كه [در قیامت] از عهد و پیمان سؤال خواهد شد.'),
(2064, 17, 35, 'و چون با پیمانه داد و ستد كنید، پیمانه را تمام دهید و با ترازوی درست وزن كنید، كه این بهتر و سرانجامش نیكوتر است.'),
(2065, 17, 36, 'و از آنچه به آن علم نداری، پیروی مكن. چون گوش و چشم و دل، همه‌ی اینها مورد بازخواست قرار خواهد گرفت.'),
(2066, 17, 37, 'و در زمین با تكبّر و سرمستی راه مرو. قطعاً تو زمین را نخواهی شكافت و در بلندی به كوه‌ها نخواهی رسید.'),
(2067, 17, 38, 'همه‌ی اینها، گناهش نزد پروردگارت ناپسند است.'),
(2068, 17, 39, 'این [دستورات] از حكمت‌هایی است كه پروردگارت بر تو وحی كرده است. و با خدا معبودی دیگر قرار مده كه سرزنش‌شده و رانده شده در دوزخ افكنده خواهی شد.'),
(2069, 17, 40, '[ای مشركان!] آیا پروردگارتان شما را با داشتن پسران، برگزیده و خودش از فرشتگان، دخترانی گرفته است؟ به راستی شما سخن [و تهمت] بزرگی می‌گویید.'),
(2070, 17, 41, 'و به یقین، ما در این قرآن [حقایق را] به گونه‌های مختلف بیان كردیم تا پند گیرند، در حالی كه جز بر رمیدنشان نمی‌افزاید.'),
(2071, 17, 42, 'بگو: «اگر با خداوند، خدایانی بود، آن‌گونه كه مشركان می‌گویند، در آن هنگام آن خدایان در پی یافتن راه نفوذی به سوی خدای صاحب عرش بودند [تا قدرت را از او بگیرند].»'),
(2072, 17, 43, 'خداوند منزّه و برتر است از آنچه می‌گویند. بسی برتر و والاتر!'),
(2073, 17, 44, 'آسمان‌های هفت‌گانه و زمین، و هر كه در آنهاست، تسبیح خداوند را می‌گویند، و هیچ چیز نیست مگر آن كه با ستایش، از او به پاكی یاد می‌كند، ولی شما تسبیح آنها را نمی‌فهمید. همانا او بردبار و آمرزنده است.'),
(2074, 17, 45, 'و هرگاه قرآن می‌خوانی، میان تو و كسانی كه به قیامت ایمان ندارند، حجابی ناپیدا قرار می‌دهیم.'),
(2075, 17, 46, 'و بر دل‌های آنان پوشش‌هایی قرار دادیم تا آن [قرآن] را نفهمند و در گوش‌هایشان سنگینی [نهادیم تا حقّ را نشنوند]. و چون پروردگارت را در قرآن به یگانگی یاد كنی، آنان پشت می‌كنند و گریزان می‌شوند.'),
(2076, 17, 47, 'هنگامی كه به تو گوش می‌دهند، ما به هدفی كه برای آن گوش می‌دهند، داناتریم. و آن‌گاه كه با هم نجوا می‌كنند [نیز، به كارشان آگاه‌تریم‌]؛ آن زمان كه ستمگران می‌گویند: شما از مردی افسون‌شده پیروی می‌کنید.»'),
(2077, 17, 48, '[ای پیامبر!] بنگر كه چگونه برای تو مثل‌ها زدند، و [تو را شاعر، كاهن، ساحر، مجنون و افسون شده خواندند،] در نتیجه گمراه شدند، پس نمی‌توانند راه حقّ را بیابند.'),
(2078, 17, 49, 'و گفتند: «آیا آن‌گاه كه ما استخوان‌هایی پوسیده و پراكنده شویم، آیا به راستی ما با آفرینشی تازه، برانگیخته می‌شویم؟»'),
(2079, 17, 50, 'بگو: «[استخوان خردشده كه آسان است، شما] سنگ باشید یا آهن،'),
(2080, 17, 51, 'یا هر آفریده‌ای از آنچه كه در نظر شما از آن هم سخت‌تر است [باز خدا می‌تواند شما را زنده كند].» آنان به زودی خواهند گفت: «چه كسی ما را بازمی‌گرداند؟» بگو: «همان كسی كه نخستین بار شما را آفرید.» پس به زودی سرهای خویش را [با تعجّب و تمسخر] به سوی تو می‌جنبانند و می‌گویند: «[آن روز] چه هنگام خواهد بود؟» بگو: «شاید نزدیك باشد!»'),
(2081, 17, 52, 'روزی كه شما را [از قبرهایتان] فرامی‌خواند، پس شما ستایش‌گویان اجابت می‌كنید. و می‌پندارید كه جز مدّت كوتاهی [در دنیا یا برزخ] درنگ نكرده‌اید.'),
(2082, 17, 53, 'و به بندگانم بگو: سخنی بگویند كه نیكوتر است، چرا كه شیطان میان آنان فتنه و فساد می‌كند. شیطان همواره برای انسان، دشمنی آشكار بوده است.'),
(2083, 17, 54, 'پروردگار شما به شما داناتر است. اگر بخواهد بر شما رحمت می‌آورد، یا اگر بخواهد [به خاطر كردارتان] شما را عذاب می‌كند. و ما تو را بر آنان نگهبان و كارساز نفرستاده‌ایم [تا به ایمان‌آوردن مجبورشان كنی].'),
(2084, 17, 55, 'و پروردگارت به هر كه در آسمان‌ها و زمین است، داناتر است. و البتّه ما بعضی از پیامبران را بر بعضی دیگر برتری بخشیدیم. و به داوود زبور دادیم.'),
(2085, 17, 56, 'بگو: «كسانی را كه به جای خدا [معبودان خود] پنداشتید، بخوانید. [تا دریابید كه‌] آنها نمی‌توانند آسیب و گزندی را از شما برطرف كنند، و [اختیار] هیچ تغییری را نیز ندارند.»'),
(2086, 17, 57, 'آنان را كه می‌خوانند، [مانند عیسی و فرشتگان،] آنان خود وسیله‌ای برای تقرّب به پروردگارشان می‌جویند، وسیله‌ای هر چه نزدیك‌تر، و به رحمت او امیدوارند و از عذابش بیمناك. همانا عذاب پروردگارت در خور پرهیز و وحشت است.'),
(2087, 17, 58, 'و هیچ منطقه‌ی آبادی نیست، مگر آن كه پیش از روز قیامت، [مردم] آن را هلاك می‌كنیم یا [به خاطرگناهانشان] به شدّت عذاب می‌نماییم. این در كتاب الهی ثبت شده است.'),
(2088, 17, 59, 'و هیچ چیز ما را از فرستادن آیات [و معجزاتی که مردم خواسته‌اند،] بازنداشت، جز این كه امّت‌های پیشین، آنها را تكذیب كردند. [و به این سبب نابود شدند.] ما به قوم ثمود ماده شتری دادیم كه [معجزه‌ای] روشنگر بود، امّا به آن ستم كردند. و ما معجزات را نمی‌فرستیم مگر برای بیم دادن.'),
(2089, 17, 60, 'و [به یادآور] آن‌گاه كه به تو گفتیم: «پروردگارت بر مردم احاطه دارد.» و آن رؤیایی را كه نشانت دادیم، و آن درخت نفرین شده در قرآن را، جز برای آزمایش مردم قرار ندادیم. و ما مردم را بیم می‌دهیم، ولی [هشدار ما] جز طغیان و سركشی بزرگ، چیزی بر آنان نمی‌افزاید.'),
(2090, 17, 61, 'و به [یادآور] زمانی كه به فرشتگان گفتیم: «برای آدم سجده كنید.» پس همه جز ابلیس سجده کردند، او گفت: «آیا برای كسی سجده كنم كه [او را] از گل آفریده‌ای؟!»'),
(2091, 17, 62, 'و گفت: «به من خبر ده، این است آن كسی كه بر من گرامی داشتی؟ اگر تا قیامت مهلتم دهی، یقیناً بر نسل او افسار می‌زنم و آنان را به زیر سلطه می‌كشانم، مگر اندكی را [که مخلَصند و توان فریب آنان را ندارم].»'),
(2092, 17, 63, '[خداوند به ابلیس] فرمود: «برو! هر كس از آدمیان از تو پیروی كند، قطعاً كیفرتان دوزخ است، كیفری سخت و كامل!'),
(2093, 17, 64, 'و [ای شیطان!] هر كس از مردم را می‌توانی با صدای خود بلغزان و نیروهای سواره و پیاده‌ات را ضدّ آنان گسیل دار، و در اموال و فرزندان با آنان شریك شو، و به آنان وعده بده. و شیطان جز فریب، وعده‌ای به آنان نمی‌دهد.'),
(2094, 17, 65, 'قطعاً تو بر بندگان [خالص‌شده‌ی] من تسلّطی نداری. و حمایت و نگهبانی پروردگارت [برای آنان] كافی است.»'),
(2095, 17, 66, 'پروردگار شما كسی است كه برایتان كشتی را در دریا به حركت درمی‌آورد، تا با تلاش خود از فضل و رحمتش بهره برید. البتّه او همواره نسبت به شما مهربان است.'),
(2096, 17, 67, 'و هرگاه در دریا به شما محنت و رنج رسد، هر كه را جز خداوند بخوانید، محو و گم می‌شود. پس چون شما را نجات دهد و به خشكی رساند، از او روی می‌گردانید. و انسان بسیار ناسپاس است.'),
(2097, 17, 68, 'پس آیا ایمِن شده‌اید از این كه شما را در ناحیه‌ی خشكی فروبرد؟ یا باران ریگ بر شما ببارد؟ سپس کارسازی برای خود نیابید.'),
(2098, 17, 69, 'یا ایمن شده‌اید از این كه خداوند دوباره شما را به دریا ببرد و طوفانی شكننده بر شما بفرستد؟ پس شما را به خاطر كفرتان غرق كند. آن‌گاه برای خود هیچ دادخواه و پی‌جویی در برابر قهر ما نیابید.'),
(2099, 17, 70, 'و بی‌گمان فرزندان آدم را گرامی داشتیم. و آنان را در خشكی و دریا [بر مركب‌ها] حمل كردیم و از چیزهای پاكیزه روزی‌شان دادیم و آنان را بر بسیاری از آفریده‌های خود برتری كامل دادیم.'),
(2100, 17, 71, '[یاد كن‌] روزی كه هر گروهی از مردم را با پیشوایشان فرامی‌خوانیم، پس هر كس نامه عملش به دست راستش داده شود، آنان نامه‌ی خود را [با شادی] می‌خوانند. و كمترین ستمی بر آنان نمی‌شود.'),
(2101, 17, 72, 'و هر كس در این دنیا كوردل و گمراه باشد، در آخرت نیز كور، و گمراه‌تر خواهد بود.'),
(2102, 17, 73, 'و بسا نزدیک بود كه تو را از آنچه به تو وحی كردیم، غافل كنند تا چیز دیگری غیر از وحی را به ما نسبت دهی، و آن‌گاه تو را دوست خود گیرند.'),
(2103, 17, 74, 'واگر ما تو را [با نیروی عصمت] استوار نكرده بودیم، نزدیک بود كه اندكی به آنان تمایل پیدا كنی.'),
(2104, 17, 75, 'آن‌گاه دو برابر [كیفر مشركان] در زندگی، و دو برابر در مرگ، عذابت می‌چشاندیم. و برای خویش در برابر قهر ما یاوری نمی‌یافتی.'),
(2105, 17, 76, 'و بسا نزدیک بود كافِران [با نیرنگ یا غلبه،] تو را از این سرزمین بلغزانند تا از آن بیرونت كنند. و در آن هنگام، جز مدّت كمی پس از تو [پایدار] نمی‌ماندند.'),
(2106, 17, 77, 'این، سنّت [هلاكت طغیان‌گران] درباره پیامبرانی که پیش از تو فرستادیم جاری بوده است و برای سنّت ما هیچ تغییری نمی‌یابی.'),
(2107, 17, 78, 'نماز را به هنگام بازگشت خورشید [به سمت مغرب كه شروعِ ظهرِ شرعی است،] تا نهایت تاریكی شب، به پادار. و نیز قرآنِ سپیده‌دمان را. همانا [قَرائت] قرآن [هنگام] فجر [در نماز صبح] مورد مشاهده [فرشتگان] است.'),
(2108, 17, 79, 'و پاسی از شب را بیدار باش و تهجّد و عبادت كن. و این وظیفه‌ای افزون برای توست. باشد كه پروردگارت تو را به مقامی درخور ستایش و پسندیده برانگیزد.'),
(2109, 17, 80, 'و بگو: «پروردگارا! مرا با ورودی نیكو و صادقانه وارد [كارها] كن و با خروجی نیكو بیرون آر! و از نزد خویش برای من برهانی یاری‌گر بگمار!»'),
(2110, 17, 81, 'و بگو: «حقّ آمد و باطل نابود شد. همانا باطل، نابود شدنی است.»'),
(2111, 17, 82, 'و آنچه از قرآن فرومی‌فرستیم، مایه‌ی شفا [ی دل] و رحمتی برای مؤمنان است. و ستمگران را جز زیان نمی‌افزاید.'),
(2112, 17, 83, 'و هرگاه به انسان نعمتی عطا كردیم، [از ما] روی‌گرداند و خود را مغرورانه از ما دور كند. و چون گزندی به او برسد، مأیوس و نومید می‌شود.'),
(2113, 17, 84, 'بگو: «هر كس بر ساختار و خلق و خوی خویش عمل می‌كند، پس پروردگارتان داناتر است كه چه كس ره‌یافته‌تر است.»'),
(2114, 17, 85, 'و از تو درباره‌ی روح می‌پرسند. بگو: «روح از امر پروردگارم [و مربوط به او] است. و از دانش جز اندكی به شما نداده‌اند.»'),
(2115, 17, 86, 'و اگر بخواهیم، آنچه را به تو وحی كرده‌ایم، [از یادت] می‌بریم، سپس كسی را نمی‌یابی كه در برابر ما از تو دفاع كند.'),
(2116, 17, 87, '[محو نشدن وحی از یادها و نوشته‌ها، نیست‌] جز رحمتی از پروردگارت. قطعا فضل خداوند نسبت به تو بزرگ است.'),
(2117, 17, 88, 'بگو: «اگر [تمام] انس و جنّ، گرد آیند تا همانند این قرآن را بیاورند، نمی‌توانند مانندش را بیاورند، هر چند كه بعضی پشتیبان و یاور دیگری باشند.»'),
(2118, 17, 89, 'و به‌راستی در این قرآن، از هرگونه مثال برای مردم آوردیم، ولی بیشتر مردم سر باز زده، جز انكار كاری نكردند!'),
(2119, 17, 90, 'و گفتند: «ما هرگز به تو ایمان نمی‌آوریم، مگر برای ما چشمه‌ای از زمین جاری سازی،'),
(2120, 17, 91, 'یا برای تو باغی از خرما و انگور باشد كه از میان آن، نهرها جاری گردانی،'),
(2121, 17, 92, 'یا آسمان را همان‌گونه كه می‌پنداری [قدرت داری،‌] پاره پاره بر [سر] ما بیافكنی، یا خداوند و فرشتگان را پیش روی ما بیاوری.'),
(2122, 17, 93, 'یا خانه‌ای از زر و زیور داشته باشی، یا در آسمان پرواز كنی. و [البته] به پرواز و بالا رفتن تو [نیز] ایمان نخواهیم آورد، تا آن كه نوشته‌ای بر ما فرود آوری كه آن را بخوانیم.» بگو: «پروردگار من منزّه است [از این كه خواسته‌هایی بیهوده را پاسخ دهد]. آیا جز این است كه من بشری فرستاده شده‌ام؟! [مرا برای هدایت شما فرستاده‌اند، نه برای این كه درخواست‌های بی‌مورد شما را پاسخ دهم.]»'),
(2123, 17, 94, 'و آن‌گاه كه هدایت به سراغ مردم آمد، چیزی مردم را از ایمان آوردن بازنداشت، جز آن كه گفتند: «آیا خداوند، بشری را به عنوان پیامبر، برانگیخته است؟»'),
(2124, 17, 95, 'بگو: «اگر در زمین [به جای بشر] فرشتگانی بودند كه با آرامش راه می‌رفتند، حتماً ما از آسمان فرشته‌ای را به عنوان پیامبر بر آنان می‌فرستادیم.»'),
(2125, 17, 96, 'بگو: «میان من و شما خداوند برای گواهی كافی است، او نسبت به بندگانش آگاه و بیناست.»'),
(2126, 17, 97, 'و هر كه را خدا هدایت كند، پس او هدایت‌یافته است. و هر كه را گمراه سازد، هرگز یاورانی جز خدا برای آنان نخواهی یافت. و آنان را روز رستاخیز گرد می‌آوریم، در حالی كه كور و گنگ و کرند، بر صورت‌هایشان [كشیده می‌شوند،] و جایگاهشان دوزخ است، كه هرگاه [شعله‌ی آن] خاموش شود، آتش آنان را بیافزاییم.'),
(2127, 17, 98, 'آن [دوزخ] كیفر آنان است، بدان جهت كه كافر شدند و گفتند: «آیا پس از آن كه [مردیم و به صورت] استخوان، و [پراکنده و] ریز ریز شدیم، آیا واقعاً به صورت آفریده‌ی جدیدی برانگیخته خواهیم شد؟»'),
(2128, 17, 99, 'آیا نیندیشیده‌اند خداوندی كه آسمان‌ها و زمین را آفریده است، تواناست كه همانند آنان را [نیز] بیافریند. و خداوند برای آنان مدّتی معین كرده كه شكّی در آن نیست، امّا ستمگران سر باز می‌زنند و جز به كفر به چیزی تن نمی‌دهند.'),
(2129, 17, 100, 'بگو: «اگر شما خزانه‌دار رحمت پروردگارم بودید، قطعاً از ترس انفاق [و تهی‌دستی،] چیزی به كسی نمی‌دادید. و انسان تنگ‌نظر و بخیل است!»'),
(2130, 17, 101, 'به راستی به موسی نُه معجزه‌ی روشن دادیم. پس، از [آگاهان‌] بنی‌اسرائیل بپرس. آن‌گاه كه [موسی] به سراغشان آمد، پس فرعون به او گفت: «ای موسی! من تو را افسون‌شده می‌پندارم.»'),
(2131, 17, 102, 'موسی گفت: «تو می‌دانی كه این [معجزات] را فقط پروردگار آسمان‌ها و زمین فرستاده است، برای بصیرت شما. و من تو را ای فرعون! هلاك شده می‌پندارم.»'),
(2132, 17, 103, 'پس [فرعون] تصمیم گرفت كه آنان را از آن سرزمین تبعید كند، پس ما او و همه‌ی همراهانش را [در دریا] غرق كردیم.'),
(2133, 17, 104, 'و پس از آن به بنی‌اسرائیل گفتیم: «در این سرزمین جای گیرید، پس چون موعد آخرت فرارسد، همه‌ی شما را با هم گرد خواهیم آورد.»'),
(2134, 17, 105, 'و آن [قرآن‌] را به حقّ فروفرستادیم و به حقّ فرود آمد و ما تو را جز بشارت‌دهنده و بیم‌رسان نفرستادیم.'),
(2135, 17, 106, 'و قرآنی كه آن را بخش‌بخش كردیم تا آن را با دِرنگ بر مردم بخوانی و آن را به تدریج فروفرستادیم.'),
(2136, 17, 107, 'بگو: «به قرآن ایمان بیاورید یا ایمان نیاورید، [برای خداوند یکسان است]، بی‌گمان چون بر كسانی كه پیش از این [به آنان] دانش داده‌اند، تلاوت شود، سجده‌كنان با چانه [و صورت بر زمین] می‌افتند.»'),
(2137, 17, 108, 'و می‌گویند: «پروردگارمان منزّه است، همانا وعده‌ی پروردگار ما انجام‌شدنی است.»'),
(2138, 17, 109, 'و گریه‌كنان بر چانه [و صورت به زمین] می‌افتند و همواره بر خشوعشان افزوده می‌شود.'),
(2139, 17, 110, 'بگو: «نام اللّه را بخوانید یا نام رحمان را، هر كدام را بخوانید، [او را خوانده‌اید،] بهترینِ نام‌ها از آنِ اوست.» و نمازت را خیلی بلند یا خیلی آهسته نخوان و میان این دو، راهِ [میانه‌ای] را برگزین.'),
(2140, 17, 111, 'و بگو: «ستایش از آنِ خداوندی است كه نه فرزندی برای خود گرفته است، و نه در حاكمیت، شریكی برای او بوده، و نه هرگز به خاطر ذلّت و ناتوانی، یاوری گرفته است.» و او را چنان كه باید، بزرگ بشمار.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ستایش مخصوص خدایی است كه كتاب بر بنده‌ی خود نازل كرد و برای آن هیچ‌گونه انحرافی قرار نداد.'),
(2142, 18, 2, '[كتابی] استوار، تا از عذاب شدیدی كه از سوی اوست، بترساند و به مؤمنانی كه كارهای شایسته انجام می‌دهند، بشارت دهد كه برای آنان پاداشی نیكوست.'),
(2143, 18, 3, 'برای همیشه در آن [بهشت] ماندگارند.'),
(2144, 18, 4, 'و تا بیم دهد به آنان كه گفتند: «خداوند برای خود فرزندی گرفته است!»'),
(2145, 18, 5, 'نه آنان به این [سخن بی‌پایه‌] یقین و دانشی دارند و نه پدرانشان. این سخنی که از دهانشان بیرون می‌آید، تهمت بزرگی است و جز دروغ نمی‌گویند.'),
(2146, 18, 6, 'پس [ای پیامبر!] بیم آن می‌رود که اگر به این سخن [الهی] ایمان نیاورند، تو در پی آنان خود را از شدت اندوه هلاک کنی!'),
(2147, 18, 7, 'همانا ما آنچه را روی زمین است، زینت برای آن قرار دادیم تا آنان را بیازماییم که کدامشان بهتر و نیکوتر عمل می‌کنند.'),
(2148, 18, 8, 'و ما [سرانجام] آنچه را روی آن است، به صورت خاک، و زمینی بی‌گیاه قرار می‌دهیم.'),
(2149, 18, 9, 'آیا پنداشته‌ای که اصحاب کهف و رقیم، از نشانه‌های شگفت ما بودند؟ [ما را نشانه‌هایی شگفت‌انگیزتر از آن نیز هست.]'),
(2150, 18, 10, 'آن‌گاه که آن جوانمردان [از شرّ حکومت طاغوت‌] به غار پناه بردند، گفتند: «پروردگارا! از سوی خود رحمتی به ما عطا کن و برای ما [زمینه‌ی‌] رشدی در کارمان فراهم ساز!»'),
(2151, 18, 11, 'پس ما تا چند سالی که در آن غار بودند، بر گوش‌های آنان [پرده‌ی خواب و بی‌هوشی] زدیم.'),
(2152, 18, 12, 'سپس آنان را برانگیختیم [و بیدارشان کردیم] تا معلوم سازیم کدام یک از آن دو گروه، مدّت خواب و درنگ خود را دقیق‌تر حساب می‌کند.'),
(2153, 18, 13, 'ما داستان آنان را به درستی برای تو حکایت می‌کنیم. آنان جوانمردانی بودند که به پروردگارشان ایمان آوردند و ما بر هدایتشان افزودیم.'),
(2154, 18, 14, 'و ما به دل‌هایشان نیرو و استحکام بخشیدیم، آن‌گاه که آنان به‌پاخاستند و گفتند: «پروردگار ما همان پروردگار آسمان‌ها و زمین است، هرگز جز او [کسی را به] خدایی نخواهیم خواند، چرا که در این صورت سخنی نادرست گفته‌ایم.'),
(2155, 18, 15, 'آنان، قوم [نادان‌] ما هستند که به جای خدا، معبودانی دیگر گرفته‌اند. چرا آنان دلیل آشکاری بر [حقّانیت] خدایان خود نمی‌آورند؟ پس کیست ستمکارتر از کسی که بر خداوند، دروغ بندد؟'),
(2156, 18, 16, 'و آن‌گاه که از مشرکان و آنچه جز خدا می‌پرستند، فاصله گرفتید، پس به غار پناه برید، تا پروردگارتان رحمت خود را برای شما بگسترد، و در کارتان برای شما آسایشی فراهم آورد.»'),
(2157, 18, 17, 'و [اگر آنجا بودی] خورشید را می‌دیدی که هنگام طلوع، به سمت راست آنان متمایل می‌شود و چون غروب کند، ایشان را وامی‌گذارد و به چپ متمایل می‌گردد و آنان در محلّی وسیع از آن غار قرار داشتند. این از آیات و نشانه‌های خداست. هر کس را خدا هدایت کند، او هدایت‌یافته‌ی راستین است و هر که را [به بیراهه واگذارد و] گمراه کند، هرگز برای او یاوری راهنما نخواهی یافت.'),
(2158, 18, 18, 'و آنان را بیدار می‌پنداشتی، [زیرا چشمانشان باز بود،] در حالی که آنان خواب بودند و ما آنان را به پهلوی راست و چپ می‌گرداندیم [تا بدنشان سالم بماند.] و سگشان دست‌های خود را بر آستانه‌ی غار گشوده بود. اگر نگاهشان می‌کردی، پشت کرده، از آنان می‌گریختی و همه‌ی وجودت از آنان پر از ترس می‌شد.'),
(2159, 18, 19, 'و ما آن‌گونه [که خوابشان کردیم]، آنان را [از خواب] برانگیختیم تا میان خود از یکدیگر پرسش کنند. یکی از آنان گفت: «چه مدّت [در این غار] مانده‌اید؟» گفتند: «یک روز، یا بخشی از روز.» [سرانجام] گفتند: «پروردگارتان داناتر است که چه مقدار مانده‌اید. پس یکی از خودتان را با این پولتان به شهر بفرستید تا ببیند کدام‌یک [از مغازه‌داران] غذای پاکیزه‌تری دارد؟ پس غذایی از آن برایتان بیاورد و باید [در این کار] زیرکی و دقّت به خرج دهد، مبادا کسی را از شما آگاه کند!'),
(2160, 18, 20, 'زیرا اگر آنان بر شما دست یابند، سنگسارتان خواهند کرد، یا شما را به آیین خودشان بازخواهند گرداند و در این صورت هرگز رستگار نخواهید شد.»'),
(2161, 18, 21, 'و بدین‌گونه [مردم آن شهر را‌] بر حالشان آگاه کردیم تا بدانند وعده‌ی خدا [در برانگیختن مردگان‌] حقّ است و در فرارسیدن قیامت هیچ تردیدی نیست. هنگامی که [کاشفانِ غار] میان خود در کار آنان نزاع و ستیز داشتند، [یک گروه‌] گفتند: بر روی [جایگاه‌] آنان بِنای یادبودی بسازید، پروردگارشان به [حال] آنان داناتر است.» اما کسانی که بر کارشان [نسبت به اصحاب کهف‌] پیروز شدند، گفتند: «ما بر [جایگاه‌] آنان معبد و مسجدی می‌سازیم.»'),
(2162, 18, 22, 'به زودی خواهند گفت: «آنان سه نفر بودند، چهارمینشان سگشان بود.» و [عدّه‌ای] می‌گویند: «پنج نفر بودند، ششمینِ آنان سگشان بود.» [این سخنی بی‌دلیل و] پرتاب تیرِ گمان به گذشته‌ای ناپیداست و [عدّه‌ای دیگر] می‌گویند: «هفت تن بودند و هشتمین آنان سگشان بود.» بگو: «پروردگارم به تعدادشان داناتر است، [و شمار] آنان را جز اندکی، کسی نمی‌داند.» پس درباره‌ی آنان جز به ظاهر [و آنچه آشکار کرده‌ایم]، مجادله مکن و درباره‌ی ایشان از هیچ‌کس نظر مخواه.'),
(2163, 18, 23, 'درباره‌ی هیچ چیز و هیچ کار، مگو که من، آن را فردا انجام می‌دهم.'),
(2164, 18, 24, 'مگر آنکه [بگویی:] «اگر خدا بخواهد.» و اگر [گفتنِ ان‌شاءاللَّه را] فراموش کردی، [هرگاه یادت آمد،] پروردگارت را یاد کن و بگو: «امید است که پروردگارم مرا به راهی که [به درستی] نزدیک‌تر است، راهنمایی کند.»'),
(2165, 18, 25, 'و آنان در غارشان سیصد و نُه سال ماندند.'),
(2166, 18, 26, '[در برابرِکسانی که اختلاف دارند،] بگو: خداوند به مدّتی که در غار ماندند، داناتر است. غیب آسمان‌ها و زمین از آنِ اوست. چه بینا و شنواست! جز او برای مردم هیچ یار و یاوری نیست و هیچ کس را در حکم و فرمانرواییِ خود شریک نمی‌گیرد.»'),
(2167, 18, 27, 'و آنچه را که از کتاب پروردگارت به تو وحی شده است، [بر مردم] بخوان. [سنّت‌ها و] کلمات الهی را تغییردهنده‌ای نیست و هرگز جز او پناهگاهی نخواهی یافت.'),
(2168, 18, 28, 'و همراه با کسانی که پروردگارشان را هر صبحگاه و شامگاه می‌خوانند و خشنودی او را می‌جویند، خود را شکیبا ساز و دیدگانت را از آنان برمگیر که زیور دنیا را بطلبی و از کسانی که دلشان را از یاد خود غافل کرده‌ایم و در پی هوس خویشند و کارشان بر گزافه و زیاده‌روی است، پیروی مکن.'),
(2169, 18, 29, 'و بگو: «حقّ از سوی پروردگار شما رسیده است، پس هر که خواست ایمان آورد و هر که خواست کافر شود.» همانا ما برای ستمگران آتشی مهیا کرده‌ایم که [شعله‌هایش] همچون سراپرده‌ای آنان را فراگرفته، و اگر فریادرسی خواهند، با آبی [چون مس] گداخته به فریادشان می‌رسند که [گرمای آن] چهره‌ها را بریان می‌کند! چه بد نوشیدنی و چه بد جایگاهی است!'),
(2170, 18, 30, 'آنان که ایمان آورده و کارهای نیک کرده‌اند، [بدانند که] ما پاداش نیکوکاران را تباه نمی‌کنیم.'),
(2171, 18, 31, 'آنان باغ‌هایی جاوِدانه دارند که نهرها از زیر [قصرها و تخت‌هایشان] جاری است. در آنجا با دستبندهایی طلایی آراسته می‌شوند و جامه‌هایی سبز از حریر نازک و دیبای ضخیم می‌پوشند، در آنجا برتخت‌ها تکیه می‌دهند. چه نیکو پاداشی و چه خوش جایگاهی!'),
(2172, 18, 32, '[ای پیامبر!] برای آنان [سرگذشت] دو مردی را مثل بزن که برای یکی از آن دو، دو باغ انگور قرار دادیم و دور آنها را با نخل پوشاندیم و میان آن دو باغ را کشت‌زار قرار دادیم.'),
(2173, 18, 33, 'هر دو باغ، میوه [فراوان] خود را داد و چیزی از آن نکاست و میان آن دو [باغ]، نهری روان ساختیم.'),
(2174, 18, 34, 'و آن مرد را میوه‌ای [فراوان] بود. پس به دوستش که با او گفتگو می‌کرد، گفت: «من از تو مالدارترم، و از نظر نفرات، [نیز] نیرومندترم.»'),
(2175, 18, 35, 'و او در حالی که بر خویش ستمکار بود، به درون باغ آمد [و با غرور] گفت: «گمان ندارم که هرگز این [باغ] نابود شود،'),
(2176, 18, 36, 'و گمان نمی‌کنم که قیامت برپا شود و اگر هم به سوی پروردگارم بازگردانده شوم، یقیناً بهتر از این [باغ] را در بازگشت، خواهم یافت.»'),
(2177, 18, 37, 'دوستش که با او گفتگو می‌کرد، گفت: «آیا به آن کسی کفر می‌ورزی که تو را از خاک، سپس از نطفه آفرید، آن‌گاه تو را مردی کامل و تمام اندام قرار داد؟!'),
(2178, 18, 38, 'ولی من [می‌گویم:] اوست خداوند، پروردگار من و هیچ کس را با پروردگارم شریک نمی‌سازم.'),
(2179, 18, 39, 'و چرا آن‌گاه که وارد باغت شدی، نگفتی: آنچه خدا بخواهد [همان شود!] هیچ نیرویی جز به [قدرت] خدا نیست. اگر مرا از نظر مال و فرزند، کمتر از خویش می‌بینی،'),
(2180, 18, 40, 'پس امید است که پروردگارم بهتر از باغِ تو را به من عطا کند و مجازات حساب شده‌ای بر باغ تو از آسمان بفرستد تا به زمینی بی‌گیاه و لغزنده تبدیل شود،'),
(2181, 18, 41, 'یا آبِ آن فروکش کند، پس هرگز نتوانی آن را بازیابی.»'),
(2182, 18, 42, 'و [سرانجام] میوه‌‌های باغ آن [مرد مغرور، با قهر خدا] احاطه شد [و از بین رفت]. پس او چنان شد که دو دست خویش را به خاطر خرج‌هایی که در باغ کرده بود، [از شدّت حسرت] برهم می‌مالید، در حالی که همه داربست‌ها فروریخته بود و می‌گفت: «ای کاش کسی را شریک پروردگارم قرار نمی‌دادم.»'),
(2183, 18, 43, 'و هیچ گروهی نداشت که او را در برابر [قهر] خدا یاری کنند و نمی‌توانست خودش را یاری کند.'),
(2184, 18, 44, 'آنجا [ثابت شد که] قدرت تنها از آنِ خدای حقّ است و [نزد] اوست بهترین پاداش و نیکوترین فرجام.'),
(2185, 18, 45, 'و [ای پیامبر!] برای آنان زندگی دنیا را مثَل بزن که همچون آبی است که از آسمان فروفرستادیم، پس گیاه زمین به وسیله‌ی آن [چنان انبوه شود که] در هم فرورود، پس ناگهان خشک شود، آن چنان که بادها پراکنده‌اش سازند و خداوند بر هر چیزی تواناست.'),
(2186, 18, 46, 'مال و فرزندان، زینت زندگی دنیایند و کارهای ماندگار شایسته، نزد پروردگارت پاداشی بهتر دارند و امید داشتن به آنها نیکوتر است.'),
(2187, 18, 47, 'و روزی که کوه‌ها را به حرکت درآوریم و زمین را آشکار [صاف و هموار] می‌بینی در حالی که همگان را برانگیخته‌ایم، پس هیچ یک از آنان را نمی‌نهیم.'),
(2188, 18, 48, 'و [در آن روز مردم] صف کشیده، بر پروردگارت عرضه می‌شوند. [خدا به آنان می‌فرماید:] «همان‌گونه که نخستین بار شما را آفریدیم، [امروز هم] به سوی ما آمدید، بلکه پنداشتید ما برایتان وعده‌گاهی نمی‌نهیم؟»'),
(2189, 18, 49, 'و کتاب [و نامه‌ی اعمال] در میان نهاده می‌شود، پس مجرمان را می‌بینی که از آنچه در آن است بیمناکند و می‌گویند: «وای بر ما! این چه کارنامه‌ای است که هیچ کوچک و بزرگی را فروگذار نکرده، مگر اینکه برشمرده است!» آنچه را انجام داده‌اند، [در برابر خود] حاضر می‌یابند و پروردگارت به هیچ‌کس ستم نمی‌کند.'),
(2190, 18, 50, 'و[یاد کن] هنگامی که به فرشتگان گفتیم: «برای آدم سجده کنید.» پس همه جز ابلیس سجده کردند! او از جنّ بود و از فرمان پروردگارش سر برتافت. آیا [با این حال] او و نسل او را به جای من سرپرستان خود می‌گیرید؟ در حالی که آنان دشمن شمایند! ستمگران بد چیزی را به جای خدا برگزیدند!'),
(2191, 18, 51, 'من ابلیس و فرزندانش را نه در آفرینش آسمان‌ها و زمین گواه گرفتم و نه در آفرینش خودشان و من گمراه‌گنندگان را دستیار خود نمی‌گیرم.'),
(2192, 18, 52, 'و روزی که [خداوند به مشرکان] می‌فرماید: «آنان را که شریک من می‌پنداشتید، فرابخوانید [تا کمکتان کنند].» پس آنها را می‌خوانند؛ ولی پاسخی به آنان نمی‌دهند و ما میانشان ورطه هلاکت قرار می‌دهیم.'),
(2193, 18, 53, 'و گناهکاران آتش دوزخ را می‌بینند، پس درمی‌یابند که در آن خواهند افتاد و راه فراری از آن نمی‌یابند.'),
(2194, 18, 54, 'و البتّه ما در این قرآن، از هر مثلی برای مردم، گوناگون بیان کردیم، [ولی مردم به انکار آنها برخاستند.] و آدمی بیش از هر چیزی جدال کننده است.'),
(2195, 18, 55, 'و هنگامی که هدایت به سوی مردم آمد، چه چیزی آنان را از ایمان‌آوردن و آمرزش‌خواهی از پروردگارشان بازداشت؟ جز آنکه سنّت خداوند درباره‌ی پیشینیان برای آنان [نیز] بیاید، یا آنکه عذاب، رویاروی آنان قرار گیرد! [تا ایمان بیاورند و درخواست آمرزش کنند.]'),
(2196, 18, 56, 'و ما پیامبران را جز بشارت‌دهندگان و بیم‌دهندگان نمی‌فرستیم، ولی کافران به باطل مجادله و ستیز می‌کنند تا به وسیله‌ی آن حقّ را درهم کوبند و آنان نشانه‌ها و آیات مرا و آنچه را که به آن بیم داده شدند، به مسخره گرفتند.'),
(2197, 18, 57, 'و کیست ستمکارتر از کسی که آیات پروردگارش را به او یادآور شوند، ولی او از آنها روی گرداند و [گناهان و] دستاورد پیشینه‌ی خویش را فراموش کند؟! البتّه ما بر دل‌هایشان پرده‌هایی نهادیم تا آیات قرآن را نفهمند و در گوش‌هایشان سنگینی قرار دادیم که اگر به سوی هدایتشان فراخوانی، هرگز به راه نخواهند آمد.'),
(2198, 18, 58, 'و پروردگارت آمرزنده‌ی صاحب رحمت است. اگر [بخواهد] مردم را به خاطر آنچه کسب کرده‌اند، مجازات کند، هرچه زودتر عذاب بر آنان می‌فرستد، [ولی چنین نمی‌کند؛] بلکه برای آنان موعدی قرار داده که [با فرارسیدنش] جز خداوند هرگز پناهگاهی نمی‌یابند.'),
(2199, 18, 59, 'و آن آبادی‌ها را هنگامی که ستم کردند، هلاکشان کردیم و برای نابود کردنشان زمانی را قرار دادیم.'),
(2200, 18, 60, 'و [به یادآور] زمانی که موسی به جوان [همراهش] گفت: «من دست از جستجو برنمی‌دارم تا به محلّ برخورد دو دریا برسم، حتّی اگر سال‌ها [به راه خود] ادامه دهم.»'),
(2201, 18, 61, 'پس چون به محلّ تلاقی آن دو [دریا] رسیدند، ماهی خود را [که برای غذا همراه داشتند،] فراموش کردند. ماهی هم راه خود را در دریا برگرفت و رفت.'),
(2202, 18, 62, 'چون از آن محل گذشتند موسی به جوان [همراه] خود گفت: «صبحانه ما را بیاور! به راستی که از این سفر رنج و خستگیِ بسیار دیده‌ایم!»'),
(2203, 18, 63, 'او گفت: «آیا به یاد داری که ما، جایی [لب دریا ایستادیم و برای رفع خستگی‌] پشت به صخره‌ها دادیم؟ من فراموش کردم [ماجرای] ماهی را [برای تو بگویم] و کسی جز شیطان آن را از یاد من نبرد، تا آن را یادآوری کنم و در [کمال] شگفتی، ماهی راه خود را در دریا پیش گرفت [و رفت]!»'),
(2204, 18, 64, '[موسی] گفت: «این همان [نشانه قرارگاهی] است که در پی آن بودیم.» پس از همان راه بازگشتند، در حالی که ردّ پای خود را دنبال می‌کردند.'),
(2205, 18, 65, 'پس [در آنجا] بنده‌ای از بندگان ما را یافتندکه از جانب خود، رحمتی [عظیم] به او عطا کرده بودیم و از نزد خود علمی [فراوان] به او آموخته بودیم.'),
(2206, 18, 66, 'موسی به او گفت: «آیا [اجازه می‌دهی] در پی تو بیایم، تا از آنچه برای رشد وکمال به تو آموخته‌اند، به من بیاموزی؟»'),
(2207, 18, 67, '[خضر] گفت: «تو هرگز نمی‌توانی بر همراهی من صبر کنی.'),
(2208, 18, 68, 'و چگونه بر چیزی که آگاهی کامل به [راز] آن نداری، صبر می‌کنی؟»'),
(2209, 18, 69, '[موسی] گفت: «به خواست خدا مرا شکیبا خواهی یافت و در هیچ کاری، تو را نافرمانی نخواهم کرد.»'),
(2210, 18, 70, '[خضر] گفت: «پس اگر دنبال من آمدی، چیزی از من مپرس، تا آنکه خودم درباره‌ی آن سخن آغاز کنم.»'),
(2211, 18, 71, 'پس آن دو به راه افتادند، تا آنکه سوار کشتی شدند. [خضر] آن را سوراخ کرد. موسی گفت: «آیا آن را شکافتی تا سرنشینانش را غرق کنی؟ راستی که کار ناروایی کردی!»'),
(2212, 18, 72, '[خضر] گفت: «آیا نگفتم تو هرگز نمی‌توانی همراه من شکیبا باشی؟»'),
(2213, 18, 73, '[موسی] گفت: «مرا به خاطر فراموشی‌ام بازخواست مکن و از این کارم بر من سخت مگیر.»'),
(2214, 18, 74, 'پس به راه خود ادامه دادند تا به نوجوانی برخورد کردند، پس [خضر] او را کشت. موسی گفت: «آیا بی‌گناهی را بدون آنکه کسی را کشته باشد، کشتی؟ به راستی کاری زشت و ناپسند کردی!»'),
(2215, 18, 75, 'گفت: «آیا نگفتمت که نمی‌توانی همپای من صبر کنی؟»'),
(2216, 18, 76, '[موسی] گفت: «اگر از این پس چیزی از تو پرسیدم، دیگر با من همراه مباش، قطعاً از سوی من معذور خواهی بود.»'),
(2217, 18, 77, 'پس آن دو به راه خود ادامه دادند تا به یک آبادی رسیدند. از اهل آنجا غذا خواستند. [ولی] آنان از پذیرایی آن دو خودداری کردند. پس آن دو در آنجا دیواری را یافتند که در حال ریزش بود. [خضر،] دیوار را [تعمیر و] برپا کرد. [موسی با تعجّب] گفت: «[اینها که به ما غذا ندادند،] اگر می‌خواستی، برای این کار مزد می‌گرفتی!»'),
(2218, 18, 78, '[خضر] گفت: «این [بار، هنگام] جدایی میان من و توست. اکنون تو را از اسرار آنچه نتوانستی بر آن شکیبایی ورزی، آگاه خواهم کرد.'),
(2219, 18, 79, 'اما آن کشتی، از آنِ بینوایانی بود که در دریا کار می‌کردند و در کمین آنان پادشاهی بود که به زور، هر کشتی [سالمی] را می‌گرفت. من خواستم معیوبش کنم [تا به دست آن ستمگر نیفتد].'),
(2220, 18, 80, 'و امّا آن نوجوان [که او را کشتم،] پدر و مادرش هر دو مؤمن بودند. ترسیدیم که او آن دو را به کفر و طغیان وادارد.'),
(2221, 18, 81, '[از این رو] خواستیم که پروردگارشان به جای او [فرزندی] پاک‌تر و بهتر و با محبّت‌تر به آن دو بدهد.'),
(2222, 18, 82, 'و امّا آن دیوار، از آنِ دو نوجوان یتیم در آن شهر بود، و زیر آن دیوار، گنجی برای آن دو بود و پدرشان مردی صالح بود. پس پروردگارت اراده کرد که آن دو به حدّ رشد خود برسند و گنج خویش را که رحمتی از سوی پروردگارت بود، بیرون آورندو من این کارها را خودسرانه نکردم. این بود تأویل و راز آنچه نتوانستی بر آن صبر و شکیبایی ورزی.»'),
(2223, 18, 83, 'و از تو درباره ذوالقرنین می‌پرسند. بگو: «به زودی بخشی از سرگذشت او را برای شما خواهم خواند.»'),
(2224, 18, 84, 'ما در زمین به او قدرت دادیم و از هر چیزی [که نیاز بود،] وسیله‌ای به او عطا کردیم.'),
(2225, 18, 85, 'پس او راهی را [برای سفر به غرب‌] پی گرفت.'),
(2226, 18, 86, 'تا آن‌گاه که به غروب‌گاهِ خورشید رسید. [غروب‌] خورشید را چنان یافت که در چشمه‌ای تیره و گل‌آلود فرومی‌رود و نزد آن قومی را یافت [که فساد و ستم می‌کردند]. گفتیم: «ای ذوالقرنین! یا [آنها را] عذاب می‌کنی و یا میان آنان نیکی در پیش می‌گیری [و همه را می‌بخشایی].»'),
(2227, 18, 87, '[ذوالقرنین] گفت: «امّا هرکس ستم کند، او را عذاب خواهیم کرد، سپس به سوی پروردگارش بازگردانده می‌شود. او هم وی را به سختی عذاب می‌کند.'),
(2228, 18, 88, 'و امّا هر کس ایمان آورد و کار شایسته کند، پس پاداشی بهتر برای اوست و کار را بر او آسان خواهیم گرفت.»'),
(2229, 18, 89, 'سپس [ذوالقرنین در ادامه] راهی را پی‌گرفت.'),
(2230, 18, 90, 'تا آنکه به محلّ طلوع خورشید رسید، آن را چنان یافت که بر قومی طلوع می‌کند که در برابر [تابش] آن پوششی برای آنان قرار نداده بودیم.'),
(2231, 18, 91, '[سرگذشت ذوالقرنین و ملت‌ها] این‌گونه [بود]، و یقیناً ما به آنچه نزد او بود، احاطه داشتیم. [و کارهایش زیر نظر ما بود].'),
(2232, 18, 92, 'سپس [ذوالقرنین برای سفری دیگر] راهی را پی‌گرفت.'),
(2233, 18, 93, 'تا آن‌گاه که به میان دو کوه رسید، پشت آن دو کوه، مردمی را یافت که گویا هیچ سخنی رانمی‌فهمیدند.'),
(2234, 18, 94, 'گفتند: «ای ذوالقرنین! [قوم] یأجوج و مَأجوج در این سرزمین فساد می‌کنند. آیا اگر ما هزینه را بپردازیم، تو میان ما و آنها سدّی می‌سازی [تا از شرّشان ایمِن شویم]؟»'),
(2235, 18, 95, '[ذوالقرنین] گفت: «آنچه پروردگارم در آن به من قدرت داده، [از کمک مالی شما] بهتر است. پس مرا با نیروی خود کمک کنید تا میان شما و آنان سدّی محکم بسازم.'),
(2236, 18, 96, 'برای من پاره‌های آهن بیاورید [و در شکاف این دو کوه بریزید.]» تا آن‌گاه که میان دو کوه هم سطح و برابر شد. آن‌گاه گفت: «[اینک آتش بیفروزید و در کوره‌ها] بدمید.» تا وقتی که آن‌ها را [مانند] آتشی [سرخ] گرداند. گفت: «[اکنون] مس گداخته بیاورید تا روی آهن‌ها[ی مذاب] بریزم.»'),
(2237, 18, 97, '[چنان سدی ساخته شد که قوم یأجوج و مأجوج] نتوانستند از آن بالا روند و نتوانستند در آن رخنه‌ای پدید آورند.'),
(2238, 18, 98, '[ذوالقرنین] گفت: «این، رحمت بزرگی از پروردگار من است [و تا موعد مقرّر پابرجاست]، پس هرگاه وعده‌ی پروردگار من فرارسد، آن [سدّ] را در هم می‌کوبد. و وعده‌ی پروردگارم حقّ است.»'),
(2239, 18, 99, 'و در آن روز [پایان جهان]، مردم را رها می‌کنیم تا بعضی در بعضی همچون موج درآمیزند، و [چون] در صور دمیده شود، همه را یکجا گرد خواهیم آورد.'),
(2240, 18, 100, 'و آن روز، دوزخ را چنان که باید، بر کافران عرضه خواهیم کرد.'),
(2241, 18, 101, 'آنان که چشمانشان از یاد من در پرده‌ی غفلت بود و [از شدّت تعصّب و لجاجت،] توان شنیدن [سخن حقّ را] نداشتند.'),
(2242, 18, 102, 'آیا آنان که کفر ورزیدند، پنداشتند که [اگر] بندگان مرا به جای من به سرپرستی بگیرند، [آنها را سود می‌بخشد؟ هرگز!] قطعاً ما جهنّم را برای پذیرایی از کافران آماده کرده‌ایم.'),
(2243, 18, 103, 'بگو: «آیا شما را از زیانکارترینِ مردم آگاه کنیم؟'),
(2244, 18, 104, 'آنان که در زندگی دنیا تلاششان [به هدر رفته، و] گم و نابود می‌شود، در حالی که می‌پندارند کار شایسته و نیک انجام می‌دهند.»'),
(2245, 18, 105, 'آنان، کسانی هستند که به آیات پروردگارشان و دیدار او [در قیامت] کفر ورزیدند، پس کارهایشان تباه و نابود شد. ما برای آنان در قیامت، میزانی برپا نخواهیم کرد، [چون کارشان ارزشی ندارد تا نیاز به میزان باشد.]'),
(2246, 18, 106, 'این است که کیفر آنان دوزخ است، به خاطر کفری که ورزیدند و آیات من و فرستادگانم را به مسخره گرفتند.'),
(2247, 18, 107, 'یقیناً آنان که ایمان آوردند و کارهای شایسته کردند، بهشت برین منزلگاه پذیرایی آنهاست.'),
(2248, 18, 108, 'همواره در آن جاوِدانه‌اند و درخواست دگرگونی و جابه‌جایی نمی‌کنند.'),
(2249, 18, 109, 'بگو: «اگر دریا برای [نوشتن] کلمات پروردگارم مرکب شود، پیش از آنکه کلمات پروردگارم پایان پذیرد، [آب] دریا تمام می‌شود، هر چند دریایی همانند آن را به کمک آوریم.»'),
(2250, 18, 110, 'بگو: «بی‌گمان من بشری همچون شمایم، [جز اینکه] به من وحی می‌شود که خدای شما خدای یگانه است. پس هر که به دیدار پروردگارش امید و ایمان دارد، کاری شایسته کند و هیچ‌کس را در عبادت پروردگارش شریک نسازد.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'كاف، ها، یا، عین، صاد.'),
(2252, 19, 2, '[این آیات،] یادی است از لطف پروردگارت به بنده‌اش زكریا.'),
(2253, 19, 3, 'هنگامی كه پروردگارش را با ندایی پنهان خواند.'),
(2254, 19, 4, '[زكریا] گفت: «پروردگارا! به راستی استخوانم سست، و [موی] سرم از پیری، سپید شده است. و پروردگارا! من هرگز در دعا به پیشگاهت [از اجابت] محروم نبوده‌ام.'),
(2255, 19, 5, 'و من برای پس از [مرگ] خود، از بستگانم بیمناكم. و همسرم [از آغاز زندگی‌] نازا بوده است، پس از جانب خود جانشینی به من عطا فرما!'),
(2256, 19, 6, '[جانشینی] كه وارث من و دودمان یعقوب باشد. و پروردگارا! او را پسندیده قرار ده!»'),
(2257, 19, 7, '[خداوند فرمود:] «ای زكریا! ما تو را به پسری نوید می‌دهیم كه نامش یحیی است. پیش از این همنامی برای او قرار نداده‌ایم.»'),
(2258, 19, 8, '[زكریا] گفت: «پروردگارا! چگونه برای من پسری خواهد بود در حالی كه همسرم نازا بوده است و خود نیز از پیری به فرتوتی رسیده‌ام؟!»'),
(2259, 19, 9, '[فرشته وحی] گفت: «چنین است [كه گفتی، ولی‌] پروردگارت فرمود: این [كار] بر من آسان است، و من تو را پیش از این آفریدم، در حالی كه چیزی نبودی.»'),
(2260, 19, 10, '[زكریا] گفت: «پروردگارا! برای من نشانه‌ای قرار ده [تا علم من به یقین تبدیل شود.]» [خداوند] فرمود: «نشانه تو آن است كه تا سه شبانه‌روز [زبان تو، از كار می‌افتد و] با مردم سخن نخواهی گفت، [با اینكه تندرستی.]»'),
(2261, 19, 11, 'آن‌گاه [زكریا] از محراب عبادت به سوی مردمش بیرون شد، پس به آنان اشاره كرد كه: بامدادان و شامگاهان خدا را تسبیح گویید.»'),
(2262, 19, 12, '[پس یحیی را به وی عطا كردیم و به او گفتیم:] «ای یحیی! كتاب [خدا] را با قدرت بگیر و در كودكی به او حكمت [و نبوّت] دادیم.'),
(2263, 19, 13, 'و نیز از جانب خود، مهربانی و پاكی به او دادیم و او تقواپیشه بود.'),
(2264, 19, 14, 'و نسبت به پدر و مادرش نیكوكار بود و هرگز [نسبت به مردم، زورگو،] سركش و نافرمان نبود.'),
(2265, 19, 15, 'و سلامی [شایسته] بر او باد، روزی که زاده شد و روزی که می‌میرد و روزی که زنده برانگیخته می‌شود.'),
(2266, 19, 16, 'و در این كتاب، مریم را یاد كن، هنگامی كه از خاندانش جدا شد و در مكانی شرقی قرار گرفت.'),
(2267, 19, 17, 'آن‌گاه دور از دیگران، برای خود پرده‌ای قرار داد. پس ما روح [الامین] خود، [جبرئیل] را به سوی او فرستادیم که به شكل انسانی خوش‌اندام بر او نمایان شد.'),
(2268, 19, 18, '[مریم به او] گفت: «من از تو به [خدای] رحمان پناه می‌برم، اگر پرهیزكاری [از من دور شو]!»'),
(2269, 19, 19, '[فرشته]گفت: «همانا من فرستاده‌ی پروردگار توام، [و آمده‌ام] تا پسری پاكیزه به تو ببخشم.»'),
(2270, 19, 20, '[مریم] گفت: «چگونه ممكن است برای من فرزندی باشد؟ در حالی كه دست هیچ بشری به من نرسیده است، و بدكاره هم نبوده‌ام؟»'),
(2271, 19, 21, '[فرشته‌] گفت: چنین است! [ولی‌] پروردگارت فرمود: این كار بر من آسان است و برای این [است‌] که او را برای مردم نشانه‌ای [از قدرت خود] و رحمتی از جانب خویش قرار دهیم، و این كاری است شدنی و قطعی.»'),
(2272, 19, 22, 'پس مریم [به عیسی] باردار شد و با وی در مكانی دور خلوت گزید.'),
(2273, 19, 23, 'آن‌گاه دردِ زایمان او را به سوی تنه‌ی درخت خرمایی كشاند. [مریم از شدّت ناراحتی و بیم رسوایی] گفت: «ای كاش پیش از این مرده بودم، و فراموش شده بودم.»'),
(2274, 19, 24, 'پس، از فرودست او را صدا زد كه: « [ای مریم!] غم مخور! همانا پروردگارت زیر [پای] تو چشمه‌ی آبی روان ساخت.'),
(2275, 19, 25, 'و شاخه‌ی این نخل را به سوی خود تكان بده، [تا] رطب تازه بر تو فرو ریزد.'),
(2276, 19, 26, 'پس [از آن رطب] بخور و [از آب نهر] بنوش و چشمت را [به داشتن فرزند] روشن بدار. پس اگر کسی از آدمیان را دیدی، [که درباره‌ی نوزاد می‌پرسند، با اشاره] بگو: «من برای [خداوند] رحمان، روزه‌ی سکوت نذر کرده‌ام، بنابراین امروز با هیچ انسانی سخن نخواهم گفت.»'),
(2277, 19, 27, 'پس مریم در حالی که نوزادش را در آغوش گرفته بود، او را نزد قوم خود آورد. گفتند: «ای مریم! به راستی کار بسیار ناپسندی مرتکب شده‌ای.'),
(2278, 19, 28, 'ای خواهر هارون! پدرت مرد بدی نبود، و مادرت [نیز] بدکاره نبود.»'),
(2279, 19, 29, 'پس مریم به نوزادش اشاره کرد [که از او بپرسید]. گفتند: «چگونه با کسی که در گهواره [و] کودک است سخن بگوییم؟»'),
(2280, 19, 30, '[عیسی به سخن آمد و] گفت: «منم بنده‌ی خدا! او به من کتاب داده و مرا پیامبر کرده است.'),
(2281, 19, 31, 'و هرجا که باشم، خداوند مرا مایه‌ی برکت قرار داده، و تا زنده‌ام مرا به نماز و زکات سفارش کرده است.'),
(2282, 19, 32, 'و مرا نسبت به مادرم نیکوکار قرار داده، و [نسبت به مردم،] ستمگر و سنگدل قرار نداده است.'),
(2283, 19, 33, 'و درود بر من، روزی که زاده شدم، و روزی که می‌میرم، و روزی که زنده برانگیخته می‌شوم.»'),
(2284, 19, 34, 'این است [ماجرای] عیسی، پسر مریم؛ [همان] گفتار درستی که [یهود و نصارا‌] در آن شک می‌کنند.'),
(2285, 19, 35, 'شایسته نیست که خداوند فرزندی برگزیند، او منزّه است، هرگاه انجام کاری را اراده کند، همین‌قدر که بگوید: موجود باش، بی‌درنگ موجود می‌شود.'),
(2286, 19, 36, 'و [عیسی گفت:] «در حقیقت خدا پروردگار من و پروردگار شماست، پس او را بپرستید که راه مستقیم، همین [بندگی او] است.»'),
(2287, 19, 37, 'پس گروه‌هایی از میان پیروانش [درباره وی] به اختلاف پرداختند، [برخی او را خدا دانستند، و برخی فرزند خدا،] پس وای بر کافران از حضور در روز بزرگ [قیامت].'),
(2288, 19, 38, 'چه خوب شنوا و بینایند روزی که به سوی ما می‌آیند، ولی ستمگران امروز در گمراهیِ آشکارند.'),
(2289, 19, 39, 'و آنان را از روز ندامت و حسرت، [روز رستاخیز‌] بترسان، آن هنگام که کار تمام شود [و همه چیز پایان یابد؛ ولی اکنون] آنان در غفلتند و ایمان نمی‌آورند.'),
(2290, 19, 40, 'تنها ماییم که زمین و اهلش را به ارث می‌بریم و [همه] به سوی ما بازگردانده می‌شوند.'),
(2291, 19, 41, 'و در این کتاب، ابراهیم را یاد کن، که او پیامبری راستگو و راست‌کردار بود.'),
(2292, 19, 42, 'آن‌گاه که به [عموی مشرکش آزر که به جای] پدرش [سرپرستی او را به عهده داشت]، گفت: «ای پدر! چرا چیزی را می‌پرستی که نمی‌شنود و نمی‌بیند و تو را از هیچ چیز بی‌نیاز نمی‌کند؟!'),
(2293, 19, 43, 'ای پدر! به راستی دانشی [از جانب حقّ] برای من آمده که برای تو نیامده است؛ بنابراین، از من پیروی کن تا تو را به راه راست هدایت کنم.'),
(2294, 19, 44, 'ای پدر! شیطان را [با اطاعت از او] پرستش مکن، که شیطان [خدای] رحمان را نافرمان است.'),
(2295, 19, 45, 'ای پدر! من می‌ترسم که از جانب [خدای] رحمان عذابی به تو رسد، و تو برای شیطان دوست و یاور باشی.»'),
(2296, 19, 46, 'گفت: «ای ابراهیم! آیا از خدایان من بیزاری؟ اگر [از این روش] دست برنداری، قطعاً تو را سنگسار خواهم کرد و [اکنون] برای مدّتی طولانی از من دور شو!»'),
(2297, 19, 47, '[ابراهیم] گفت: «بدرود! به زودی از پروردگارم برای تو آمرزش می‌خواهم، زیرا او همواره نسبت به من بسیار مهربان بوده است.'),
(2298, 19, 48, 'و من از شما و آنچه غیر خدا می‌خوانید، کناره می‌گیرم و پروردگارم را می‌خوانم. امیدوارم که در خواندن پروردگارم محروم [و بی‌پاسخ] نمانم.»'),
(2299, 19, 49, 'چون ابراهیم از مشرکان و از آنچه به جای خدا می‌پرستیدند، کناره گرفت، ما اسحاق و [فرزندش] یعقوب را به او بخشیدیم و همه را پیامبر قرار دادیم.'),
(2300, 19, 50, 'و از رحمت خویش به آنها عطا کردیم و برای آنان [در میان مردم] نام نیکو و آوازه‌ی بلند قرار دادیم.'),
(2301, 19, 51, 'و در این کتاب، از موسی یاد کن، زیرا که او بنده‌ی [برگزیده و] خالص‌شده [خدا] و فرستاده‌ای پیامبر بود.'),
(2302, 19, 52, 'و او را از جانبِ راستِ [کوهِ] طور ندا دادیم و رازگویان [به خود] نزدیکش ساختیم.'),
(2303, 19, 53, 'و از رحمت خویش، برادرش هارون را که پیامبر بود، به او بخشیدیم. [تا وزیرش باشد.]'),
(2304, 19, 54, 'و در این کتاب از اسماعیل یاد کن که او همواره در وعده‌هایش صادق، و فرستاده‌ای پیامبر بود.'),
(2305, 19, 55, 'و همواره خاندان خود را به نماز و زکات فرمان می‌داد، و پیوسته نزد پروردگارش پسندیده بود.'),
(2306, 19, 56, 'و در این کتاب، از اِدریس یاد کن، به راستی او پیامبری بسیار راستگو بود.'),
(2307, 19, 57, 'و ما او را به مقام والایی بالا بردیم.'),
(2308, 19, 58, 'آنان پیامبرانی بودند که خداوند بر آنها نعمت [ویژه] ارزانی داشت؛ [پیامبرانی] از نسل آدم و از [فرزندان] کسانی که با نوح [در کشتی] سوارشان کردیم، و از نسل ابراهیم و یعقوب، و از کسانی که هدایت نمودیم و برگزیدیم. هرگاه آیات [خدای] رحمان بر آنان خوانده می‌شد، سجده‌کنان و گریان به خاک می‌افتادند.'),
(2309, 19, 59, 'آن‌گاه پس از آنان، جانشینان بد و ناشایستی آمدند که نماز را تباه کردند و هوس‌ها را پی گرفتند. پس به زودی [کیفر] گمراهی خود را خواهند دید.'),
(2310, 19, 60, 'مگر کسانی که توبه کنند و ایمان آورند و کار شایسته کنند، که آنها داخل بهشت می‌شوند و کمترین ستمی به آنان نخواهد شد.'),
(2311, 19, 61, 'بهشت‌های جاویدی که [خدای] رحمان از غیب به بندگانش وعده داده است. حتماً وعده‌ی او شدنی است.'),
(2312, 19, 62, 'در آنجا سخن بیهوده‌ای نمی‌شنوند، بلکه آنچه می‌شنوند فقط سلام و درود است و در آنجا هر بامداد و شامگاه، روزی آنان برایشان [آماده‌] است.'),
(2313, 19, 63, 'این، همان بهشتی است که به هریک از بندگان پرهیزکار خود به میراث می‌دهیم.'),
(2314, 19, 64, '[پس از مدّتی تأخیر در نزول وحی، جبرئیل بر پیامبر نازل شد و گفت:] «ما [فرشتگان] جز به فرمان پروردگارت فرود نمی‌آییم؛ آینده و گذشته‌ی ما و آنچه میان آنهاست، از آنِ اوست و پروردگار تو فراموشکار نیست.'),
(2315, 19, 65, 'پروردگار آسمان‌ها و زمین، و آنچه میان آن دو قرار دارد؛ تنها او را بپرست و برای عبادتش شکیبا باش، آیا برای او همتا و هم‌نامی می‌شناسی؟»'),
(2316, 19, 66, 'و انسان می‌گوید: «آیا آن‌گاه که مردم، دوباره زنده [از خاک‌] بیرونم می‌آورند؟»'),
(2317, 19, 67, 'آیا انسان به یاد نمی‌آورد که ما او را پیش از این آفریده‌ایم، در حالی که هیچ چیز نبود؟'),
(2318, 19, 68, 'پس به پروردگارت سوگند که ما همه‌ی آنها را [که منکر معاد شده‌اند،] با شیطان‌ها گرد می‌آوریم، سپس در حالی که به زانو درآمده‌اند، گرداگرد دوزخ حاضر خواهیم کرد.'),
(2319, 19, 69, 'سپس از هر گروهی، کسانی را که بر [خدای] رحمان سرکش‌تر بودند، جدا خواهیم کرد.'),
(2320, 19, 70, 'آن‌گاه ما بهتر می‌دانیم چه کسانی برای سوختن در دوزخ سزاوارترند.'),
(2321, 19, 71, 'و همه‌ی شما وارد دوزخ می‌شوید. [این امر] از جانب پروردگارت حکمی است قطعی.'),
(2322, 19, 72, 'سپس پرهیزکاران را نجات می‌دهیم و ستمکاران را [که از ضعف و ذلّت] به زانو درآمده [اند]، در آن رها می‌کنیم.'),
(2323, 19, 73, 'و هرگاه آیات روشن ما بر آنان خوانده شود، کسانی که کفر ورزیده‌اند به آنان که ایمان آورده‌اند، می‌گویند: «کدام یک از ما دو گروه، جایگاهش بهتر و محفلش آراسته‌تر است؟!»'),
(2324, 19, 74, 'و چه بسیار ملت‌ها را پیش از آنان [به سبب طغیانشان‌] نابود کردیم که مال و ثروتشان بهتر بود و ظاهری آراسته‌تر داشتند.'),
(2325, 19, 75, 'بگو: «هرکه در گمراهی است، [خداوند] رحمان او را تا مدّتی یاری و مهلت می‌دهد، تا وقتی که آنچه را وعده داده می‌شوند، ببینند، یا عذاب [این دنیا] یا [عذاب] قیامت را. پس [در آن روز] خواهند دانست چه کسی جایگاهش بدتر و لشکرش ناتوان‌تر است.»'),
(2326, 19, 76, 'کسانی که هدایت یافته‌اند، خداوند بر هدایتشان می‌افزاید و ثوابِ نیکی‌های ماندگار، نزد پروردگارت بهتر و خوش‌فرجام‌تر است.'),
(2327, 19, 77, 'پس آیا دیدی کسی را که به آیات ما کفر ورزید و گفت: «قطعاً به من مال و فرزند [بسیار] داده خواهد شد!»'),
(2328, 19, 78, 'آیا او از غیب آگاه است؟ یا از [خدای] رحمان تعهّدی گرفته است؟'),
(2329, 19, 79, 'هرگز [چنین نیست،] به زودی هر چه می‌گوید، [در پرونده‌ی عملش‌] می‌نویسیم و برای همیشه عذابش می‌کنیم.'),
(2330, 19, 80, 'و هر چه را [از مال و فرزند] که می‌گوید، ما وارث خواهیم شد و او بی‌کس و تنها نزد ما خواهد آمد.'),
(2331, 19, 81, 'و به جای خدای یگانه خدایانی [دروغین] برگزیدند تا برای ایشان مایه‌ی عزّت و توانمندی باشد.'),
(2332, 19, 82, 'هرگز [به این آرزو نمی‌رسند؛ بلکه] به زودی [معبودها] پرستش آنان را انکار می‌کنند و در برابر آنان به دشمنی برمی‌خیزند.'),
(2333, 19, 83, 'آیا ندیدی که ما شیطان‌ها را به سوی کافران فرستادیم تا آنان را شدیداً تحریک کنند؟'),
(2334, 19, 84, 'پس بر [عذاب] آنان شتاب مکن. ما حساب آنها را شماره می‌کنیم [تا موعدشان فرارسد و به کیفر آنچه مرتکب می‌شدند، برسند].'),
(2335, 19, 85, 'روزی که ما پرهیزکاران را دسته‌جمعی به سوی [ضیافت خدای] رحمان گردآوریم.'),
(2336, 19, 86, 'و مجرمان را تشنه به سوی دوزخ می‌رانیم.'),
(2337, 19, 87, '[در آن روز،] اختیار شفاعت ندارند مگر کسانی که از نزد [خدای‌] رحمان، پیمانی [بر اذن شفاعت‌] گرفته باشند.'),
(2338, 19, 88, 'و [کافران] گفتند: «[خدای] رحمان [برای خود] فرزندی انتخاب کرده است!»'),
(2339, 19, 89, 'راستی، چیز زشت و زننده‌ای [بر زبان‌] آوردید.'),
(2340, 19, 90, 'نزدیک است از این سخن، آسمان‌ها متلاشی، و زمین شکافته شود و کوه‌ها به شدّت فروریزد.'),
(2341, 19, 91, 'زیرا برای [خدای] رحمان، فرزندی قائل شدند.'),
(2342, 19, 92, 'در حالی که برای [خدای] رحمان، سزاوار نیست که فرزندی برگزیند.'),
(2343, 19, 93, 'تمام موجودات آسمان‌ها و زمین، بنده‌وار نزد [خداوندِ] رحمان می‌آیند.'),
(2344, 19, 94, 'قطعاً آنان را برشمرده و حساب آنها را درست نگه داشته است.'),
(2345, 19, 95, 'و همگی روز قیامت تنها [و تهی‌دست] نزد او خواهند آمد.'),
(2346, 19, 96, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، به زودی [خدای] رحمان برای آنان [در دل‌ها] محبّتی قرار می‌دهد.'),
(2347, 19, 97, 'پس قرآن را بر زبان تو آسان ساختیم تا پرهیزکاران را به وسیله‌ی آن مژده دهی و مردم ستیزه‌جو را بدان بترسانی.'),
(2348, 19, 98, 'و چه بسیار ملّت‌ها را پیش از آنان [به سبب طغیانشان‌] نابود کردیم. آیا کسی از آنها را می‌یابی؟ یا کمترین صدایی از ایشان می‌شنوی؟');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طاها!'),
(2350, 20, 2, 'ما قرآن را بر تو نازل نکردیم که به زحمت و مشقّت بیفتی.'),
(2351, 20, 3, '[بلکه آن را نازل کردیم‌] تا برای کسی که [از خدا] می‌ترسد، مایه‌ی تذکر و یادآوری باشد.'),
(2352, 20, 4, '[این قرآن‌] از سوی کسی نازل شده که زمین و آسمان‌های بلند را آفریده است.'),
(2353, 20, 5, '[خداوند] رحمان بر عرش (تخت فرمانروایی هستی) چیره است.'),
(2354, 20, 6, 'آنچه در آسمان‌ها و آنچه در زمین، و آنچه در میان آنها و آنچه در زیر زمین است، برای اوست.'),
(2355, 20, 7, 'و اگر سخنت را آشکار بگویی [یا پنهان کنی]، او اسرار [شما] و پنهان‌تر [از آن] را می‌داند.'),
(2356, 20, 8, 'خدایی که معبودی جز او نیست. نیکوترینِ نام‌ها مخصوص اوست.'),
(2357, 20, 9, 'و آیا خبر موسی به تو رسیده است؟!'),
(2358, 20, 10, 'آن‌گاه که [در سفر از مَدین به مصر،] آتشی را دید، پس به خانواده‌ی خود گفت: «قدری درنگ کنید، من آتشی دیدم، شاید شعله‌ای از آن را برای شما بیاورم، یا نزد آتش راهنمایی بیابم.»'),
(2359, 20, 11, 'پس همین که نزد آتش آمد، ندا داده شد: «ای موسی!'),
(2360, 20, 12, 'من پروردگار توام! پس کفش‌های خود را از پای بیرون آر! به درستی که تو در سرزمین مقدّس طُوی هستی.'),
(2361, 20, 13, 'و من تو را [به پیامبری] برگزیده‌ام، پس به آنچه وحی می‌شود، گوش ‌فراده!'),
(2362, 20, 14, 'همانا منم اللّه، جز من خدایی نیست، پس مرا بندگی کن و نماز را به پای دار تا به یاد من باشی.'),
(2363, 20, 15, 'همانا قیامت خواهد آمد، [ولی] من می‌خواهم [زمان] آن را پنهان دارم، تا هر کس برابر سعی و تلاش خود، جزا داده شود.'),
(2364, 20, 16, 'پس مبادا کسی که به قیامت ایمان ندارد و پیرو هوای نفس خویش است، تو را از توجّه به قیامت بازدارد که نابود می‌شوی.'),
(2365, 20, 17, 'و ای موسی! آنچه به دست راست تو، چیست؟»'),
(2366, 20, 18, 'گفت: «این، عصای من است که بر آن تکیه می‌دهم، و با آن برای گوسفندانم برگ می‌ریزم، و کارهای دیگری هم برای من از آن برمی‌آید.»'),
(2367, 20, 19, '[خداوند] فرمود: «ای موسی! عصا را بیفکن!»'),
(2368, 20, 20, 'پس آن را افکند، ناگهان ماری شد که می‌شتافت.'),
(2369, 20, 21, '[خداوند] فرمود: «آن را بگیر و نترس، ما به زودی آن را به حال نخستینش برمی‌گردانیم.'),
(2370, 20, 22, 'و دستت را در گریبانت فرو ببر تا بدون هیچ عیبی، سپید و درخشان بیرون آید، [که این نیز‌] معجزه‌ای دیگر است.'),
(2371, 20, 23, 'تا برخی از نشانه‌های بزرگ خویش را به تو نشان دهیم.'),
(2372, 20, 24, '[ای موسی! اکنون] به سوی فرعون برو، که او سرکشی کرده است.'),
(2373, 20, 25, '[موسی] گفت: «پروردگارا! سینه‌ام را گشاده گردان [و بر صبر و حوصله‌ام بیافزا]!'),
(2374, 20, 26, 'و کارم را برایم آسان فرما!'),
(2375, 20, 27, 'و گرهی را [که مانع روان سخن گفتن من است،‌] از زبانم باز کن!'),
(2376, 20, 28, 'تا سخنان مرا [خوب] بفهمند.'),
(2377, 20, 29, 'و از خاندانم دستیاری برایم قرار بده،'),
(2378, 20, 30, 'برادرم هارون را.'),
(2379, 20, 31, 'با او پشتوانه‌ام را محکم ساز.'),
(2380, 20, 32, 'و او را در کارم شریک گردان.'),
(2381, 20, 33, 'تا ما تو را بسیار به پاکی بستاییم.'),
(2382, 20, 34, 'و تو را بسیار یاد کنیم.'),
(2383, 20, 35, '[پروردگارا!] همانا تو همواره به کار و حال ما آگاه و بینایی!»'),
(2384, 20, 36, '[خداوند] فرمود: «ای موسی! آنچه را درخواست کردی، به تو داده شد.'),
(2385, 20, 37, 'و ما بار دیگر بر تو منّت گذاشتیم [و تو را مشمول نعمت‌های خویش ساختیم].'),
(2386, 20, 38, 'آن‌گاه که به مادرت آنچه را باید الهام کرد، وحی کردیم.'),
(2387, 20, 39, 'که کودک خود را در صندوقی بگذار و آن را به دریا بینداز، تا دریا آن را به ساحل افکند و دشمن من و دشمن او آن را برگیرد و من محبّتی از جانب خویش بر تو افکندم [تا تو را دوست بدارند] و تا زیر نظر من ساخته شوی [و پرورش یابی].'),
(2388, 20, 40, 'آن‌گاه که خواهرت [نزد آنان] می‌رفت تا بگوید: «آیا می‌خواهید کسی را به شما نشان دهم که او را سرپرستی کند؟» [و آنان پذیرفتند.] پس [این‌گونه] ما تو را به سوی مادرت بازگرداندیم تا چشم او روشن شود و اندوهگین نگردد و تو شخصی [از فرعونیان] را به قتل رساندی، پس تو را از غم نجات دادیم و تو را در آزمون‌های مختلف آزمودیم. پس چند سالی را در میان مردم مَدین درنگ کردی، [اینک] در زمان مقدّر [برای پذیرش رسالت به اینجا] آمدی.'),
(2389, 20, 41, '[ای موسی!] من تو را برای خودم ساختم [و پروردم].'),
(2390, 20, 42, '[اکنون] تو و برادرت [هارون] با [معجزات و] نشانه‌هایی که من به شما داده‌ام، بروید و درباره ذکر من [که ابلاغ وحی است،] سستی نکنید.'),
(2391, 20, 43, 'به سوی فرعون بروید که او سرکشی کرده است.'),
(2392, 20, 44, 'پس به نرمی با او سخن بگویید، شاید پند پذیرد، یا [از خدا] بهراسد.'),
(2393, 20, 45, '[موسی و هارون] گفتند: «پروردگارا! می‌ترسیم که بر ما پیشی گیرد [و ما را بیازارد]، یا [نپذیرد و] بر سرکشی خود بیافزاید.»'),
(2394, 20, 46, '[خداوند] فرمود: نترسید! من با شما هستم [و همه چیز را] می‌شنوم و می‌بینم.'),
(2395, 20, 47, 'پس به سراغ او بروید و بگویید: ما دو فرستاده‌ی پروردگار توایم، بنی‌اسرائیل را به همراه ما بفرست [و بیش از این] آنها را آزار مده! البتّه ما با معجزه‌ای از جانب پروردگارت به سوی تو آمده‌ایم، و درود بر کسی که هدایت را پیروی کند.'),
(2396, 20, 48, 'به ما وحی گردیده که عذاب بر کسی است که [آیات الهی را] تکذیب کند و سرپیچی نماید.»'),
(2397, 20, 49, '[فرعون] گفت: «ای موسی! پروردگار شما دو نفر کیست؟!»'),
(2398, 20, 50, '[موسی] گفت: «پروردگار ما کسی است که هر چیزی را، آفرینشی که درخور اوست، عطا کرد و آن‌گاه [آن را به راه کمال] هدایت فرمود.»'),
(2399, 20, 51, '[فرعون] گفت: «پس حال مردمان دوره‌های نخستین چه می‌شود [که به اینها ایمان نداشتند]؟!»'),
(2400, 20, 52, '[موسی] گفت: «علم آن در کتابی نزد پروردگار من است. پروردگارم نه اشتباه می‌کند، و نه از یاد می‌برد.»'),
(2401, 20, 53, 'خدایی که زمین را برای شما بستر آسایش قرار داد، و راه‌هایی را در آن برای شما پدید آورد و از آسمان آبی فرستاد. پس با آن، انواعی از گیاهان گوناگون را [از خاک] بیرون آوردیم.'),
(2402, 20, 54, '[هم خودتان از آن] بخورید، و [هم] چهارپایانتان را [در آن] بچرانید. البتّه در این [امور] نشانه‌هایی برای خردمندان است.'),
(2403, 20, 55, 'ما شما را از آن [زمین] آفریدیم، و در آن بازمی‌گردانیم و بار دیگر شما را از همان برمی‌آوریم.'),
(2404, 20, 56, 'و بی‌گمان ما تمام آیات و معجزات خویش را به فرعون نشان دادیم، ولی او تکذیب و خودداری کرد.'),
(2405, 20, 57, '[فرعون]گفت: «ای موسی! آیا تو آمده‌ای تا با جادوی خود، ما را از سرزمینمان بیرون کنی؟!'),
(2406, 20, 58, 'پس ما [هم] حتماً جادویی همانند آن را برای تو خواهیم آورد. پس [اکنون] میان ما و خود زمانی را قرار بده که هیچ کدام از آن تخلّف نکنیم، [آن هم در] سرزمینی هموار، و با فاصله‌ای که نسبت به همه یکسان باشد.»'),
(2407, 20, 59, '[موسی]گفت: «وعده‌گاه [ما و] شما، روز زینت، [روز عید،] و در وقت نیمروز، که همه‌ی مردم جمع شده باشند.»'),
(2408, 20, 60, 'پس فرعون بازگشت و [تمام] مکر و حیله‌ی خود را فراهم آورد، سپس [در روز موعود] بازآمد.'),
(2409, 20, 61, 'موسی به آنان گفت: «وای بر شما! بر خدا دروغ نبندید، که شما را با عذابی [سخت] هلاک و ریشه‌کن خواهد کرد و آن کس که [به خدا] دروغ ببندد، بی‌تردید ناکام و زیانکار می‌گردد.»'),
(2410, 20, 62, 'پس آنان در کارشان به کشمکش پرداختند و رازگویی [خود] را پنهان داشتند.'),
(2411, 20, 63, '[فرعونیان] گفتند: «همانا این دو، (موسی و هارون) قطعاً جادوگرند و می‌خواهند با سحرشان شما را از سرزمینتان بیرون کنند و آیین برتر شما را براندازند.'),
(2412, 20, 64, 'پس [اینک تمام] حیله و نیرنگ خود را گرد آورید، سپس در یک صف [و منظّم] بیایید. که بی‌تردید امروز پیروزی از آنِ کسی است که برتری یابد [و سحر او غالب شود].»'),
(2413, 20, 65, '[ساحران] گفتند: «ای موسی! آیا تو [عصای خود را] می‌افکنی، یا ما اوّل کسی باشیم که بیافکند؟!»'),
(2414, 20, 66, '[موسی] گفت: «بلکه شما بیفکنید.» پس از جادوی آنان چنان به نظرش آمد که ریسمان‌ها و چوبدست‌هایشان می‌شتابد.'),
(2415, 20, 67, 'پس موسی در دل خود احساس ترسی کرد [که مبادا مردم فریب بخورند].'),
(2416, 20, 68, 'ما گفتیم: نترس! که تو خود برتری.'),
(2417, 20, 69, 'و آنچه را در دست راستت داری، بیفکن تا هر چه را آنان ساخته‌اند، در کام خود فروبرد. آنچه آنان ساخته‌اند، نیرنگ جادوگر است و جادوگر هر جا رود، [پیروز و] رستگار نگردد.'),
(2418, 20, 70, '[چون موسی عصایش را افکند، دریافتند که کار موسی معجزه است، نه جادو، پس تمام] ساحران به سجده افتادند و گفتند: «ما به پروردگار هارون و موسی ایمان آوردیم.»'),
(2419, 20, 71, '[فرعون] گفت: «آیا پیش از آنکه به شما اجازه دهم، به او ایمان آوردید؟! بی‌گمان او سردسته‌ی شماست که به شما جادو آموخته است. پس دست‌ها و پاهای شما را یکی از راست و یکی از چپ می‌برم و شما را بر شاخه‌های نخل به دار خواهم آویخت و به زودی خواهید دانست که شکنجه و مجازات کدام یک از ما سخت‌تر و پایدارتر است؟ [من یا خدای موسی‌؟]»'),
(2420, 20, 72, '[ساحران] گفتند: «ما هرگز تو را بر دلایل روشنی که برای ما آمده، و بر کسی که ما را آفریده، ترجیح نخواهیم داد. پس تو هر حکم و قضاوتی که می‌خواهی بکن، تو فقط در این زندگانیِ دنیا حکم می‌کنی.'),
(2421, 20, 73, 'ما به پروردگارمان ایمان آورده‌ایم تا خطاهای ما، و آن جادویی که ما را به آن واداشتی، بر ما بیامرزد و خداوند بهتر و پاینده‌تر است.»'),
(2422, 20, 74, 'چنین است که هرکس گناهکار و مجرم نزد پروردگار خود بیاید، دوزخ برای اوست، نه در آن می‌میرد و نه [با خوشی] زندگی می‌کند.'),
(2423, 20, 75, 'و کسانی که مؤمن نزد او بیایند، در حالی که کارهای شایسته کرده‌اند، پس برای آنان درجات عالی و برتر است.'),
(2424, 20, 76, 'باغ‌های جاویدانی که درزیر [درختان] آنها نهرها جاری است، همیشه در آنجا خواهند بود و این پاداش کسی است که [خود را از کفر و گناه] پاکیزه گرداند.'),
(2425, 20, 77, 'و ما به موسی وحی کردیم که بندگان مرا شبانه [از مصر] کوچ بده و برای آنان راهی خشک در میان دریا بگشا، [چنان که] نه از تعقیب [فرعونیان] بترسی و نه [از غرق شدن،] بیمناک باشی.'),
(2426, 20, 78, 'پس فرعون با سپاهیانش آنان را دنبال کرد، اما [موجی] از دریا آنان را گرفت، و به طور کامل غرق کرد.'),
(2427, 20, 79, 'و فرعون قوم خود را به گمراهی برد و هیچ هدایت نکرد.'),
(2428, 20, 80, 'ای بنی‌اسرائیل! ما شما را از دشمنانتان نجات دادیم و با شما در سمت راست کوه طور، وعده گذاردیم، و بر شما [غذاهای آماده‌ای چون] مَنّ و سَلوی نازل کردیم.'),
(2429, 20, 81, '[اینک] از چیزهای پاکیزه‌ای که روزی شما کرده‌ایم، بخورید، و در آن طغیان نکنید که خشم من شما را فراگیرد و هر کس که خشم من او را فراگیرد، بی‌گمان نابود خواهد شد.'),
(2430, 20, 82, 'و البتّه من آمرزنده‌ی آن کسم که توبه کند و ایمان آورد و کاری شایسته کند، سپس هدایت شود.'),
(2431, 20, 83, 'و [چون موسی با برگزیدگان قومش به کوه طور آمدند، موسی پیش‌تر رسید، به او گفتیم:] ای موسی! چه چیز تو را به شتاب واداشت که پیش از قوم خود آمدی؟»'),
(2432, 20, 84, '[موسی] گفت: «آنها به دنبال من می‌آیند و پروردگارا! من برای خشنودی تو به سویت شتاب کردم.»'),
(2433, 20, 85, '[خدا] فرمود: «ما قوم تو را بعد از [آمدن] تو آزمایش کردیم، و سامری آنها را گمراه کرد.»'),
(2434, 20, 86, 'پس موسی خشگمین و اندوهناک به سوی قوم خود بازگشت [و] گفت: «ای قوم من! آیا پروردگارتان به شما وعده‌ای نیکو نداد [که تورات را بر شما فروفرستد]؟ آیا مدّت [رفتن من] بر شما طولانی شد؟ یا اینکه می‌خواستید خشمی از پروردگارتان بر شما فرود آید، که از وعده‌ی من تخلّف کردید؟!»'),
(2435, 20, 87, '[مردم به موسی] گفتند: «ما به میل و اراده‌ی خود با وعده‌ی تو مخالفت نکردیم، بلکه بارهایی [گران] از زیورآلات قومِ [فرعون] بر دوش ما نهادند، پس ما آنها را [در آتش] افکندیم، آن‌گاه سامِری [نیز] این چنین [طرح] افکند.»'),
(2436, 20, 88, 'پس [سامری] برای آنها پیکر گوساله‌ای پدید آورد که بانگ گاو داشت. آن‌گاه [با پیروانش] گفتند: «این خدای شما و خدای موسی است!» پس سامری [پیمان خدا و همه تعلیمات موسی را] فراموش کرد.'),
(2437, 20, 89, 'آیا نمی‌بینند که [این گوساله، هنگامی که آن را می‌خوانند،] برایشان پاسخی ندارد، و مالک سود و زیانی برای آنها نیست؟!'),
(2438, 20, 90, 'و البته هارون پیش از این به آنان گفته بود: «ای قوم من! شما قطعاً با آن [گوساله]، مورد آزمایش قرار گرفته‌اید و همانا پروردگار شما، [خداوند] رحمان است، پس از من پیروی کنید و دستور مرا اطاعت نمایید.»'),
(2439, 20, 91, '[اما آنان به هارون] گفتند: «ما یکسره در پرستش آن [گوساله] خواهیم بود، تا موسی به سوی ما بازگردد.»'),
(2440, 20, 92, '[هنگامی که موسی بازگشت،‌] گفت: «ای هارون! زمانی که دیدی آنان گمراه شدند، چه چیز تو را بازداشت،'),
(2441, 20, 93, 'از اینکه مرا [در برخورد شدید با فتنه‌گران‌] پیروی کنی؟ آیا از فرمان من سرپیچی کردی؟»'),
(2442, 20, 94, '[هارون] گفت: «[برادرم!] ای فرزند مادرم! [موی] ریش و سر مرا [به بازخواست] مگیر! من ترسیدم [آنها پراکنده شوند و] تو بگویی میان بنی‌اسرائیل تفرقه انداختی و سفارش مرا [در حفظ وحدت‌] پاس نداشتی!»'),
(2443, 20, 95, '[سپس موسی به سامری] گفت: «ای سامری! [منظور تو از] این کار خطرناک [و فتنه] بزرگ که کردی، چه بود؟»'),
(2444, 20, 96, '[سامری] گفت: «من به چیزی پی بردم که [دیگران] به آن پی نبردند، من مشتی از آثار رسول را برگرفتم، پس آن را افکندم و این‌گونه نفسم این کار را در نظرم آراست [و فریب داد].»'),
(2445, 20, 97, '[موسی] گفت: «پس برو! [دور شو!] همانا بهره‌ی تو در دنیا این است که [به دردی مبتلا خواهی شد که پیوسته] می‌گویی: «به من دست نزنید!» و برای تو [در آخرت] میعادگاهی است که هرگز از آن تخلّف نخواهد شد و [اکنون] به سوی معبودت که پیوسته آن را می‌پرستیدی، بنگر! ما آن را می‌سوزانیم [و] خاکستر و ذرات آن را به دریا خواهیم پاشید.'),
(2446, 20, 98, '[ای قوم من!] همانا معبود شما «اللّه» است که معبودی جز او نیست [و] علم او همه چیز را فرا‌گرفته است.»'),
(2447, 20, 99, '[ای پیامبر!] ما این‌گونه از اخبار گذشته بر تو حکایت می‌کنیم و ما از جانب خویش به تو ذکری [چون قرآن] داده‌ایم.'),
(2448, 20, 100, 'پس هرکس از آن روی‌برگرداند، قطعاً در روز قیامت، بار سنگینی را بر دوش خواهد کشید.'),
(2449, 20, 101, 'برای همیشه در آن خواهند ماند و چه بد باری است برای آنها، در روز قیامت!'),
(2450, 20, 102, 'روزی که در صور دمیده می‌شود و ما در آن روز، مجرمان را با چشمان تیره محشور می‌کنیم.'),
(2451, 20, 103, '[آنها] میان خویش آهسته با یکدیگر می‌گویند: «شما [در دنیا] ده روزی بیش درنگ نداشته‌اید.»'),
(2452, 20, 104, '[البتّه] ما به آنچه آنها می‌گویند، داناتریم، آن‌گاه که بهترینِ آنان در رفتار، می‌گوید: «شما یک روز [بیشتر] درنگ نکردید!»'),
(2453, 20, 105, 'و از تو درباره‌ی کوه‌ها [در قیامت] می‌پرسند، بگو: «پروردگار من آنها را از بن برکنده و متلاشی می‌کند.'),
(2454, 20, 106, 'پس آنها را [همچون] کویری صاف و هموار، رها می‌سازد.'),
(2455, 20, 107, 'که در آن هیچ پستی و بلندی مشاهده نمی‌کنی.»'),
(2456, 20, 108, 'در آن روز، [مردم] از دعوت‌کننده‌ای که [هیچ] انحرافی ندارد، پیروی می‌کنند. [و گوش به صدای اسرافیل هستند.] و همه‌ی صداها در برابر [عظمت خدا] رحمان فرومی‌نشیند. پس جز صدایی آهسته [چیزی‌] نمی‌شنوی.'),
(2457, 20, 109, 'در آن روز، شفاعت [هیچ‌کس] سودی ندارد، مگر کسی که [خدای] رحمان به او اجازه دهد و از گفتار او [در مورد شفاعت دیگران‌] راضی باشد.'),
(2458, 20, 110, '[خداوند] به آنچه آنان در پیش روی دارند و آنچه [در دنیا] پشت سر گذاشته‌اند، آگاه است؛ ولی مردم بر او احاطه علمی ندارند.'),
(2459, 20, 111, 'و [در آن روز] همه‌ی چهره‌ها در برابر خداوندِ زنده‌ی پاینده، خوار و فروتن می‌شود و البتّه هر کس که بار ستم برداشت، نومید و زیانکار است.'),
(2460, 20, 112, 'و هر کس برخی [کارهای‌] نیکو انجام دهد و مؤمن باشد، از هیچ ستم وکاستی [در پاداشش] نمی‌ترسد.'),
(2461, 20, 113, 'و بدین‌سان ما آن را قرآنی عربی نازل کردیم و در آن با انواع بیان‌ها، هشدار دادیم، شاید آنها پروا کنند یا برایشان پندی پدید آید.'),
(2462, 20, 114, 'پس [بدان که] بلندمرتبه است خدای فرمانروای بر حقّ! و تو [ای پیامبر!] بر خواندن قرآن، پیش از آنکه وحیِ آن بر تو پایان یابد، شتاب مکن و بگو: «پروردگارا! بر دانش من بیافزای!»'),
(2463, 20, 115, 'و به یقین، پیش از این با آدم پیمان بستیم، امّا او فراموش کرد و ما عزم استواری برای او نیافتیم.'),
(2464, 20, 116, 'و زمانی که به فرشتگان گفتیم: «برای آدم سجده کنید!» پس همگی سجده کردند، مگر ابلیس که سر باز زد.'),
(2465, 20, 117, 'پس گفتیم: «ای آدم! این [ابلیس] دشمن تو و همسر توست، پس [مراقب باش که] شما را از [این] باغ بیرون نکند که به رنج و مشقّت خواهی افتاد.'),
(2466, 20, 118, 'قطعاً تو در آن [باغ]، نه گرسنه می‌شوی و نه برهنه می‌مانی.'),
(2467, 20, 119, 'و در آنجا، نه تشنه می‌شوی، و نه از حرارت آفتاب آزار می‌بینی.»'),
(2468, 20, 120, 'پس شیطان او را وسوسه کرد [و] گفت: «ای آدم! آیا تو را به درخت جاوِدانگی و فرمانرواییِ فناناپذیر راهنمایی کنم؟!»'),
(2469, 20, 121, 'پس [آدم و همسرش فریفته شدند و] هر دو از آن [درخت ممنوعه] خوردند، پس شرمگاهشان برای آنان نمایان شد و [ناچار] هر دو شروع کردند به چسباندن برگ [درختان] بهشت بر خود و [این چنین] آدم پروردگارش را نافرمانی کرد و بیراهه رفت.'),
(2470, 20, 122, 'سپس پروردگارش او را برگزید، پس [لطف خویش را] بر او بازگرداند و او را هدایت کرد.'),
(2471, 20, 123, '[خداوند] فرمود: از آن [بهشت، و مقام والا] فرود آیید [که] برخی از شما دشمن برخی دیگر خواهند بود، پس اگر از سوی من رهنمودی برای شما آمد، هر کس از هدایت من پیروی کند، نه گمراه می‌شود و نه در رنج می‌افتد.'),
(2472, 20, 124, 'و هر کس از یاد من روی گردانَد، بی‌گمان برای او زندگیِ [سخت و] تنگی خواهد بود و ما او را در قیامت نابینا محشور می‌کنیم.'),
(2473, 20, 125, '[او خواهد] گفت: «پروردگارا! چرا مرا نابینا محشور کردی؟ درحالی که من [در دنیا] بینا بودم!»'),
(2474, 20, 126, '[و خداوند خواهد] فرمود: «همان‌گونه که آیات ما به تو رسید و تو آنها را فراموش کردی، امروز هم همان‌طور فراموش گردیده‌ای.»'),
(2475, 20, 127, 'و ما این‌گونه هر کس را که اسراف کند، و به آیات پروردگارش ایمان نیاورد، کیفر می‌دهیم و قطعاً عذاب آخرت سخت‌تر و پایدارتر است.'),
(2476, 20, 128, 'پس آیا برای هدایتشان کافی نیست که ملت‌های پیشین را [که سرکشی و فساد کردند،] نابود کردیم، و [اینان امروز] در خانه‌های آنان رفت و آمد می‌کنند؟! همانا در آن [حوادث] برای خردمندان نشانه‌هاست.'),
(2477, 20, 129, 'و اگر نبود سنّت سابق پروردگارت، و مدتی که [برای زندگی آنان‌] مشخص شده است، حتماً [عذاب الهی بی‌درنگ] لازم می‌آمد.'),
(2478, 20, 130, 'پس [ای پیامبر!] بر آنچه می‌گویند، شکیبا باش و پیش از طلوع آفتاب و قبل از غروب آن، با ستایش پروردگارت [او را] تسبیح کن و [نیز] برخی از ساعات شب و حوالی روز را به نیایش بپرداز، باشد که خشنود شوی.'),
(2479, 20, 131, 'و به آنچه با آن گروه‌هایی از آنان را بهره‌مند گردانده‌ایم، چشم مدوز، که [این] شکوفه و جلوه‌ی زندگی دنیاست [و ما می‌خواهیم] تا آنها را در آن بیازماییم و [به یقین] روزیِ پروردگارت بهتر و پایدارتر است.'),
(2480, 20, 132, 'و خانواده‌ات را به نماز فرمان ده و بر آن شکیبا باش. ما از تو روزی نمی‌خواهیم، [بلکه] ما تو را روزی می‌دهیم، و سرانجامِ [نیکو] برای [اهل] تقواست.'),
(2481, 20, 133, 'و [کافران] گفتند: «چرا [محمّد] از جانب پروردگارش، معجزه و نشانه‌ای برای ما نمی‌آورد؟» آیا دلیل و نشانه‌ی روشن در کتاب‌های [آسمانی] پیشین برایشان نیامده [تا ایمان بیاورند]؟'),
(2482, 20, 134, 'و اگر ما آنان را پیش از این با عذابی هلاک می‌کردیم، می‌گفتند: «پروردگارا! چرا پیامبری برای ما نفرستادی تا ما از آیات تو پیروی کنیم، پیش از آنکه خوار و رسوا شویم.»'),
(2483, 20, 135, 'بگو: «هر یک [از ما و شما] در انتظاریم، پس انتظار بکشید که به زودی خواهید دانست یاران راه راست کیانند و چه کسی راه یافته است.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, '[هنگام] حساب مردم نزدیک شده است، در حالی که آنان در بی‌خبری، [از آن] روی‌گردانند!'),
(2485, 21, 2, 'هیچ پند تازه‌ای از طرف پروردگارشان برای آنان نیامد، مگر این که آن را شنیدند و [باز] سرگرم بازی شدند.'),
(2486, 21, 3, 'در حالی که دل‌های آنان [از حقّ، غافل و به چیز دیگری] سرگرم است و کسانی که [کافر شدند و به خود] ستم کردند، مخفیانه راز می‌گویند که: «آیا جز این است که این [محمّد] بشری همانند شماست؟ آیا با این که می‌بینید به جادو روی می‌آورید؟!»'),
(2487, 21, 4, '[پیامبر] گفت: «پروردگار من، هر سخنی که در آسمان و زمین باشد، می‌داند. او شنوای داناست.»'),
(2488, 21, 5, '[کافران] گفتند: « [آنچه محمّد آورده است، وحی نیست؛] بلکه خواب‌های آشفته است. بلکه آنها را به دروغ به خدا نسبت داده، بلکه او یک شاعر است، پس باید همان‌گونه که [پیامبران] پیشین [با معجزه] فرستاده شده بودند، او [نیز] برای ما معجزه‌ای بیاورد.»'),
(2489, 21, 6, 'تمام آبادی‌هایی که پیش از اینها هلاکشان کردیم، [تقاضای این‌گونه معجزات را کردند؛ ولی همین که خواسته‌شان عملی شد،] ایمان نیاوردند. پس آیا اینان ایمان می‌آورند؟'),
(2490, 21, 7, 'و ما [هیچ پیامبری را] پیش از تو نفرستادیم، مگر این که [آنها نیز] مردانی بودند که ما به ایشان وحی می‌کردیم، پس اگر نمی‌دانید از آگاهان بپرسید.'),
(2491, 21, 8, 'و ما برای آنها بدن‌هایی که به غذا نیاز نداشته باشند، قرار ندادیم و آنها جاویدان نبودند.'),
(2492, 21, 9, 'سپس ما به وعده‌ای که به آنان داده بودیم، وفا کردیم. پس آنان و هر که را خواستیم، نجات دادیم و اسراف‌کاران را نابود کردیم.'),
(2493, 21, 10, 'همانا بر شما کتابی فرستاده‌ایم که وسیله تذکر [و بیداری] شما در آن است، آیا نمی‌اندیشید؟!'),
(2494, 21, 11, 'و ما چه بسیار آبادی‌ها را درهم شکستیم که [مردمش] ستمگر بودند و پس از آنها قومی دیگر را پدید آوردیم.'),
(2495, 21, 12, 'پس چون قهر ما را احساس کردند، ناگهان پا به فرار گذاشتند.'),
(2496, 21, 13, '[از روی استهزا و تحقیر به آنان گفتند:] «فرار نکنید! و به سوی زندگی مرفّهی که در آن نازپرورده بودید و به خانه‌هایتان بازگردید، تا شاید [بار دیگر توسط تهی‌دستان‌] از شما درخواست کمک شود [و شما آنان را با کبر و نِخوت برانید].'),
(2497, 21, 14, '[و چون عذاب ما را جدّی دیدند،] گفتند: «ای وای بر ما! به یقین ما ستمکار بوده‌ایم.»'),
(2498, 21, 15, 'و پیوسته، این سخنِ آنان بود تا ما آنها را درو کردیم و خاموش و بی‌حرکت ساختیم.'),
(2499, 21, 16, 'و ما، آسمان و زمین و آنچه را که میان آنهاست، برای بازی نیافریدیم.'),
(2500, 21, 17, '[به فرض محال] اگر می‌خواستیم سرگرمی برگزینیم، چیزی متناسب [با شأن] خود انتخاب می‌کردیم.'),
(2501, 21, 18, 'بلکه ما حقّ را بر باطل می‌کوبیم تا مغز آن را درهم بشکند [و از هم بپاشد]. پس بی‌درنگ، باطل محو و نابود می‌شود و وای بر شما از آنچه [خداوند را به آن] وصف می‌کنید [و نسبت بیهوده‌کاری به او می‌دهید.]'),
(2502, 21, 19, 'و برای اوست هر که در آسمان‌ها و زمین است و کسانی که نزد او هستند، [فرشتگان،] از عبادت او تکبّر نمی‌ورزند و خسته و درمانده نمی‌شوند.'),
(2503, 21, 20, 'آنان شبانه‌روز بی‌آنکه سستی ورزند، خداوند را به پاکی می‌ستایند.'),
(2504, 21, 21, 'آیا خدایانی که [کفّار] از زمین برگزیده‌اند، مردگان را زنده می‌کنند؟'),
(2505, 21, 22, 'اگر در آسمان‌ها و زمین، خدایان دیگری غیر از اللَّه وجود داشت، حتماً آن دو تباه می‌شدند [و نظام جهان به هم می‌خورد]. پس منزّه است خداوندِ پروردگار عرش، از آنچه [ناآگاهان] وصف می‌کنند.'),
(2506, 21, 23, 'هیچ‌کس نمی‌تواند بر کار او خرده بگیرد؛ ولی در کارهای آنها، جای سؤال و ایراد است.'),
(2507, 21, 24, 'آیا آنها به جای خداوند [یکتا] خدایان دیگری گرفته‌اند؟ بگو: «دلیلتان را بیاورید! این [قرآن] یادنامه‌ی پیروان من و یادنامه‌ی کسانی است که پیش از من بوده‌اند [که همه بر یکتایی خدا هم‌نظرند.]» امّا بیشتر مردم، حقّ را نمی‌دانند، پس [از آن] روی‌گردانند.'),
(2508, 21, 25, 'و هر پیامبری پیش از تو فرستادیم، به او وحی کردیم که معبودی جز من نیست، پس [تنها] مرا بپرستید.'),
(2509, 21, 26, 'و [کافران] گفتند: «[خداوند] رحمان فرزند گرفته است!» منزّه است او! [فرشتگان، فرزند خدا نیستند؛] بلکه بندگانی گرامی هستند.'),
(2510, 21, 27, 'آنان در کلام بر او پیشی نمی‌گیرند و به فرمان او عمل می‌کنند.'),
(2511, 21, 28, '[خداوند] آینده و گذشته‌ی آنها را می‌داند و آنان جز برای کسی که خداوند از او خرسند باشد، شفاعت نمی‌کنند و خود از بیم او هراسانند.'),
(2512, 21, 29, 'و هر که از آنها، [فرشتگان و انسان‌ها و جنّیان] بگوید: من به جای خداوند، معبود هستم!» پس کیفر او را دوزخ قرار می‌دهیم. ما این‌گونه ستمکاران را کیفر خواهیم داد.'),
(2513, 21, 30, 'آیا کافران نمی‌بینند که آسمان‌ها و زمین به هم بسته و پیوسته بودند و ما، آن دو را شکافته و از یکدیگر باز کردیم؟ و هر چیز زنده را از آب پدید آوردیم. آیا باز ایمان نمی‌آورند؟'),
(2514, 21, 31, 'و در زمین، کوه‌های ثابت و استوار قرار دادیم تا [مبادا زمین] آنها را بلرزاند و در [میان] کوه‌ها، [دره‌هایی گشاده و] راه‌های فراخ پدید آوردیم، شاید که آنها راه یابند.'),
(2515, 21, 32, 'و آسمان را سقفی نگه‌داشته قرار دادیم و آنها از نشانه‌های آن روی‌گردانند.'),
(2516, 21, 33, 'و او کسی است که شب و روز و خورشید و ماه را آفرید. هر یک [از آنها] در مداری [معین]، شناور [و در حرکت] است.'),
(2517, 21, 34, 'و ما پیش از تو برای [هیچ] انسانی، جاودانگی قرار ندادیم، پس آیا اگر تو از دنیا بروی، آنان زندگانی جاوید خواهند یافت؟!'),
(2518, 21, 35, 'هر کسی [طعم] مرگ را می‌چشد! و ما شما را با بدی‌ها و خوبی‌ها می‌آزماییم و [سرانجام] به سوی ما بازگردانده می‌شوید.'),
(2519, 21, 36, 'و هرگاه کافران تو را ببینند، کاری جز به مسخره گرفتن تو ندارند، [و با همدیگر می‌گویند:] «آیا این است آن کسی که خدایان شما را [به بدی] یاد می‌کند؟» در حالی که خود، یاد [خدای] رحمان را منکرند [و به او کفر می ورزند].'),
(2520, 21, 37, '[طبیعتِ] انسان [به‌گونه‌ای است که گویا] از عجله آفریده شده است؛ [عذاب را به شتاب می‌خواهد؛ امّا] من به زودی نشانه‌های [عذاب] خود را به شما نشان خواهم داد، پس [آن را] به شتاب از من نخواهید!'),
(2521, 21, 38, '[کافران] می‌گویند: اگر راست می‌گویید، این وعده [قیامت] کی فرامی‌رسد؟»'),
(2522, 21, 39, 'اگر کفّار می‌دانستند زمانی که [آن فرارسد، دیگر] نمی‌توانند شعله‌های آتش را از صورت‌ها و از پشت‌هایشان دور کنند و کسی آنان را یاری نمی‌کند.'),
(2523, 21, 40, 'بلکه [این آتش] ناگهان به سراغشان خواهد آمد و آنان را بهت‌زده خواهد کرد. پس نه قدرت دفع آن را دارند و نه به آنان مهلتی داده خواهد شد.'),
(2524, 21, 41, 'و همانا پیامبران پیش از تو [نیز] مورد استهزا قرار گرفتند. امّا [سرانجام،] آنچه استهزا می‌کردند، دامان مسخره‌کنندگان را گرفت [و مجازات الهی آنها را در هم کوبید]!'),
(2525, 21, 42, 'بگو: «کیست که شما را در شب و روز از [عذاب خدای] رحمان نگاه می‌دارد؟!» امّا آنان از یاد پروردگارشان روی می‌گردانند.'),
(2526, 21, 43, 'یا برای آنان خدایانی است که آنان را در برابر [عذاب] ما نگه دارد؟ اینها قادر بر یاری [و حفظ] خودشان نیستند، [تا چه رسد به دیگران] و از جانب ما نیز همراهی نمی‌شوند.'),
(2527, 21, 44, 'با این وجود، ما آنها و پدرانشان را [از انواع نعمت‌ها] بهره‌مند ساختیم تا آنجا که عمرشان طولانی شد [و مایه‌ی غرور و سرکشی آنان گردید]. آیا نمی‌بینند که ما همواره به زمین می‌پردازیم و از وسعت سرزمین‌ها[یی که در اختیار کافران بوده،‌] می‌کاهیم؟ آیا ایشان [بر ما] غلبه دارند؟'),
(2528, 21, 45, 'بگو: «من تنها با وحی، به شما هشدار می‌دهم!» امّا کسانی که کر هستند، زمانی که بیم داده می‌شوند، آن را نمی‌شنوند!'),
(2529, 21, 46, 'در حالی که اگر دمی از عذاب پروردگارت به آنان برسد، قطعاً خواهند گفت: «ای وای بر ما! به یقین ما ستمکار بوده‌ایم.»'),
(2530, 21, 47, 'و ما برای روز قیامت، ترازوهای عدل برپا خواهیم کرد، پس هیچ ستمی به احدی نخواهد شد و اگر [عملی] همسنگ دانه خردلی [هم] باشد، ما آن را خواهیم آورد و [در دقّت محاسبات همین] بس است که ما حساب‌رس باشیم.'),
(2531, 21, 48, 'و همانا ما به موسی و هارون، [کتابی که‌] جداکننده [حق از باطل‌] و نور و مایه‌ی یادآوری برای پرهیزکاران [است‌،] عطا کردیم.'),
(2532, 21, 49, '[همان] کسانی که از پروردگارشان در نهان می‌ترسند و همانانی که از قیامت در هراسند.'),
(2533, 21, 50, 'و این [قرآن]، مبارک ذکری است که ما آن را فروفرستادیم. آیا شما آن را انکار می‌کنید؟!'),
(2534, 21, 51, 'و همانا ما پیش از این، ابراهیم را به رشد و کمال خود رساندیم و ما به [شایستگی‌های] او آگاه بودیم.'),
(2535, 21, 52, 'آن هنگام که به [آزر، عموی مشرکش که به جای] پدرش [سرپرستی او را به عهده داشت] و به قومش گفت: «چیست این تندیس‌های بی‌روحی که شما همواره آنها را می‌پرستید؟»'),
(2536, 21, 53, 'آنان گفتند: «ما پدرانمان را پرستش‌کنندگان آنها یافته‌ایم!»'),
(2537, 21, 54, '[ابراهیم] گفت: «بی‌تردید شما و پدرانتان در گمراهی آشکار بوده و هستید!»'),
(2538, 21, 55, 'آنان گفتند: «آیا تو سخن حقّی برای ما آورده‌ای؟ یا این که شوخی می‌کنی؟!»'),
(2539, 21, 56, '[ابراهیم] گفت: «البتّه [که حقّ می‌گویم.] پروردگار شما پروردگار آسمان‌ها و زمین است که آنها را به وجود آورده و من بر این حقیقت از گواهانم.'),
(2540, 21, 57, 'و به خداوند سوگند پس از آن که پشت کردید و رفتید، نقشه‌ای برای بت‌هایتان خواهم کشید.»'),
(2541, 21, 58, 'پس [ابراهیم در یک فرصت مناسب] همه‌ی آنها، جز بزرگشان را خرد کرد تا شاید آنها به سراغ آن بیایند.'),
(2542, 21, 59, '[بت‌پرستان چون بت‌های شکسته را دیدند،‌] گفتند: «چه کسی با خدایان ما چنین کرده؟ بی‌گمان او از ستمکاران است.»'),
(2543, 21, 60, 'گفتند: «شنیده‌ایم جوانی که به او ابراهیم می‌گویند، آن [بت]ها را [به بدی] یاد می‌کرد.»'),
(2544, 21, 61, '[بزرگان قوم] گفتند: «پس او را در پیش چشم مردم بیاورید تا آنها [بر مجرم بودن او] گواهی دهند.»'),
(2545, 21, 62, 'گفتند: «ای ابراهیم! آیا تو با خدایان ما این چنین کرده‌ای؟»'),
(2546, 21, 63, '[ابراهیم] گفت: «بلکه این کار را بزرگشان کرده است! از خودشان بپرسید، اگر سخن می‌گویند!»'),
(2547, 21, 64, 'پس به [وجدان‌های] خود بازگشتند، [و به یکدیگر] گفتند: «شما خودتان [با پرستش این سنگ‌های ناتوان] ستمکارید [نه ابراهیم.]»'),
(2548, 21, 65, 'سپس [دوباره] به اندیشه‌های باطلی که در سر داشتند، بازگشتند [و به ابراهیم گفتند:] «به راستی تو خود می‌دانی که اینها سخن نمی‌گویند.»'),
(2549, 21, 66, '[ابراهیم] گفت: «پس آیا شما به جای خداوند، چیزی را می‌پرستید که هیچ سودی برای شما ندارد؟ و هیچ زیانی به شما نمی‌رساند؟!'),
(2550, 21, 67, 'ننگ بر شما و بر هر چه جز خدا می‌پرستید. آیا نمی‌اندیشید؟!»'),
(2551, 21, 68, '[بزرگان قوم] گفتند: «اگر می‌خواهید کاری کنید! او را بسوزانید و خدایانتان را یاری کنید.»'),
(2552, 21, 69, '[آتشی بزرگ فراهم کردند و ابراهیم را به درون آن افکندند؛ ولی ما] گفتیم: ای آتش! بر ابراهیم سرد و سلامت باش!'),
(2553, 21, 70, 'و آنان نیرنگی سخت برای او اراده کردند، ولی ما آنها را از زیانکارترینِ [مردم] قرار دادیم.'),
(2554, 21, 71, 'و ما، او و لوط را [از آن سرزمین پر از شرک و فساد] نجات دادیم و به سوی سرزمینی که در آن برای جهانیان برکت نهاده‌ایم، بردیم.'),
(2555, 21, 72, 'و اسحاق و [فرزندش] یعقوب را، [به عنوان نعمتی] افزون به ابراهیم بخشیدیم و همه را از صالحان قرار دادیم.'),
(2556, 21, 73, 'و آنان را پیشوایانی قرار دادیم که به فرمان ما [مردم را] هدایت می‌کردند و به آنان، انجام کارهای نیک و برپایی نماز و پرداخت زکات را وحی کردیم و آنان فقط ما را عبادت می‌کردند.'),
(2557, 21, 74, 'و به لوط، حکمت و دانش عطا کردیم و او را از قریه‌ای که کارهای زشت می‌کردند، رهانیدیم. به راستی آنان مردمی بد و منحرف بودند.'),
(2558, 21, 75, 'و او را در رحمت خویش وارد کردیم. همانا او از شایستگان بود.'),
(2559, 21, 76, 'و نوح را پیش‌تر [از ابراهیم و لوط]، آن زمان که [ما را] ندا داد، پس [خواسته‌ی] او را اجابت کردیم. او و خاندانش را از بلای بزرگ، نجات بخشیدیم.'),
(2560, 21, 77, 'و او را در برابر مردمی که نشانه‌های ما را تکذیب کردند، یاری دادیم. به درستی که آنان، مردم بدی بودند، پس همه‌ی آنها را غرق کردیم.'),
(2561, 21, 78, 'و داوود و سلیمان [را یاد کن] آن هنگام که درباره‌ی کشت‌زاری که گوسفندان قوم، شبانگاه در آن چریده [و آن را تباه ساخته] بودند، داوری می‌کردند و ما شاهد داوری آنان بودیم.'),
(2562, 21, 79, 'پس ما [حکم] آن را به سلیمان فهماندیم و هر یک [از آن دو] را، حکمت و دانش دادیم و کوه‌ها و پرندگان را با داوود به نیایش و تسبیح واداشتیم و ما کننده [این کار] بودیم.'),
(2563, 21, 80, 'و برای شما به داوود فنّ زره‌سازی را آموختیم، تا شما را از [خطرات] جنگتان نگه دارد. پس آیا شما سپاسگزار هستید؟'),
(2564, 21, 81, 'و برای سلیمان، تندباد را [رام کردیم] که به فرمان او به سوی سرزمینی که درآن برکت قرار دادیم، روان می‌شد و ما به هر چیزی آگاه بوده‌ایم.'),
(2565, 21, 82, 'و گروهی از شیطان‌ها [را نیز رامِ سلیمان قرار دادیم که] برای او غوّاصی می‌کردند و کارهایی غیر از این انجام می‌دادند و ما آنها را [از سرکشی] حفظ می‌کردیم!'),
(2566, 21, 83, 'و [به یاد آور] ایوب را، آن زمان که پروردگارش را ندا داد که: «[پروردگارا!] به من آسیب و سختی رسیده است و تو مهربان‌ترینِ مهربانانی!»'),
(2567, 21, 84, 'پس [دعای] او را مستجاب کردیم و رنج و سختی او را برداشتیم و خانواده‌اش را به او بازگرداندیم و همانندشان را با ایشان [به او عطا کردیم، تا هم] رحمتی از سوی ما [به او باشد و هم مایه‌ی یادآوری] و پندی برای عبادت‌کنندگان.'),
(2568, 21, 85, 'و اسماعیل و ادریس و ذو‌الکفل [را یاد کن که این پیامبران،] همه از شکیبایان بودند.'),
(2569, 21, 86, 'و ما آنان را در رحمت خویش وارد ساختیم. به درستی که آنان از شایستگان بودند.'),
(2570, 21, 87, 'و [یونس] ذوالنون [را به یاد آور.] آن هنگام که خشمگین [از میان قومش بیرون] رفت و پنداشت که [آسوده شد و] ما بر او تنگ نخواهیم گرفت، [امّا همین که در کام نهنگ فرو رفت،] پس در آن تاریکی‌ها[ی شکم نهنگ‌] ندا داد که: « [خداوندا!] جز تو معبودی نیست! تو پاک و منزهی! همانا من از ستمکاران بودم [و نباید مردم را به خاطر سرسختی‌شان رها می‌کردم و تنها می‌گذاشتم].»'),
(2571, 21, 88, 'پس [دعای] او را اجابت کردیم و او را از آن اندوه نجات دادیم و ما این چنین، مؤمنان را نجات می‌دهیم.'),
(2572, 21, 89, 'و زکریا [را یاد کن] آن‌گاه که پروردگارش را ندا داد: «پروردگارا! مرا تنها [و بی‌فرزند و بی‌وارث] مگذار و [البتّه که] تو خود، بهترینِ وارثانی.»'),
(2573, 21, 90, 'پس [دعای] او را مستجاب کردیم و یحیی را به او بخشیدیم و همسرش را [که نازا بود،] برای او شایسته قرار دادیم. [چرا که] آنان در کارهای نیک، شتاب داشتند و ما را از روی امید و بیم می‌خواندند و در برابر ما همواره فروتن بودند.'),
(2574, 21, 91, 'و [به یاد آور مریم را، آن] زنی که دامان خویش را پاک نگاه داشت، پس ما از روح خود در او دمیدیم و او و پسرش [عیسی] را نشانه‌ای برای جهانیان قرار دادیم.'),
(2575, 21, 92, 'این [پیامبران بزرگ و پیروانشان] همه امّت یگانه‌ای بودند، [دارای یک آیین و پیرو یک هدف.] و من پروردگار شما هستم! پس مرا بپرستید!'),
(2576, 21, 93, 'ولی آنها کار خود را در میانشان به تفرقه کشاندند. [بدانند که] همگی به سوی ما بازمی‌گردند.'),
(2577, 21, 94, 'پس هر کس برخی [کارهای‌] شایسته را انجام دهد و مؤمن باشد، برای تلاش او هیچ‌گونه ناسپاسی نخواهد شد و ما [کارهای نیکویش را] برای او ثبت می‌کنیم.'),
(2578, 21, 95, 'و بر [مردم] آبادی‌هایی که آنها را [به خاطر فساد و گناه] هلاک نمودیم، حرام است [که به دنیا بازگردند،] آنان بازنمی‌گردند.'),
(2579, 21, 96, 'تا آن‌گاه که [راه بر] یأجوج و مأجوج [با شکستن سدّ] گشوده شود و آنان از هر بلندی بتازند و به شتاب سرازیر شوند.'),
(2580, 21, 97, 'و وعده‌ی حقّ [در برپایی قیامت] نزدیک گردید. پس در آن هنگام چشم‌های کافران [از وحشت و حیرت] خیره و باز می‌ماند [و با خود می‌گویند:] «ای وای بر ما! به راستی که ما از این [روز] سخت در بی‌خبری بودیم، بلکه ما ستمگر بودیم.»'),
(2581, 21, 98, '[به آنان گفته می‌شود:] «شما و هر آنچه که جز خدا می‌پرستید، هیزم دوزخ خواهد بود. [و قطعاً] در آن وارد خواهید شد.»'),
(2582, 21, 99, 'اگر اینها خدایان [راستین] بودند، وارد آن [دوزخ] نمی‌شدند، در حالی که همگی برای همیشه در آن ماندگار خواهند بود.'),
(2583, 21, 100, 'برای آنان در دوزخ، ناله‌ها و نعره‌هاست و آنان در آنجا [هیچ پاسخی] نمی‌شنوند.'),
(2584, 21, 101, '[امّا مؤمنان،] کسانی که پیش‌تر، از جانب ما به آنان [وعده‌ی] نیکو داده شده است، آنان از آن [دوزخ] دور نگاه داشته خواهند شد.'),
(2585, 21, 102, 'آنان صدای [زبانه کشیدن] آن را نمی‌شنوند و در آنچه [از نعمت‌ها و کامیابی‌ها] که دلخواه آنهاست، جاودانه‌اند.'),
(2586, 21, 103, 'بزرگ‌ترین وحشت [آن روز]، آنان را غمگین [و مضطرب] نمی‌سازد و فرشتگان به [پیشواز و] ملاقات آنان می‌روند [و می‌گویند:] « این است روزِ شما! که به شما وعده می‌دادند.»'),
(2587, 21, 104, 'روزی که آسمان را همچون طومارِ نوشته‌ها درهم می‌پیچیم، [و] همان‌گونه که آفرینشِ نخستین را آغاز کردیم، آن را بازمی‌گردانیم. [این] وعده‌ای است که بر عهده‌ی ماست و قطعاً آن را انجام خواهیم داد.'),
(2588, 21, 105, 'و همانا ما پس از تورات، در زبور نوشتیم که: بی‌گمان بندگان شایسته‌ام وارث [حکومت بر] زمین خواهند شد.'),
(2589, 21, 106, 'همانا در این [پیش‌گویی]، برای گروهی پرستنده، پیامی روشن است.'),
(2590, 21, 107, 'و ما تو را [ای پیامبر!] مایه رحمتی برای جهانیان فرستاده‌ایم.'),
(2591, 21, 108, '[پس به آنان] بگو: «تنها چیزی که به من وحی می‌شود این است که معبود شما فقط خدای یگانه است. پس آیا شما [به حقّ] گردن می‌نهید؟!»'),
(2592, 21, 109, 'پس اگر روی برگرداندند، [به آنان] بگو: «به همه‌ی شما یکسان اعلام [خطر] کردم و نمی‌دانم [زمان وقوع] آن وعده که به شما داده شده، نزدیک است، یا دور!'),
(2593, 21, 110, 'او هم سخن آشکار را می‌داند و هم آنچه را که پنهان می‌کنید.'),
(2594, 21, 111, 'و من نمی‌دانم، شاید این [تأخیر در عذاب] برای شما آزمونی باشد و [فرصتی برای] کامیابی تا مدّتی دیگر.»'),
(2595, 21, 112, '[پیامبر] گفت: «پروردگارا! [میان ما و مشرکان] به حقّ داوری کن! و پروردگار همه‌ی ما [خداوند] مهربان است که در برابر آنچه [او را ناروا] وصف می‌کنید، باید از او یاری جست.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ای مردم! از پروردگارتان پروا کنید، که زلزله‌ی قیامت، حادثه‌ای بزرگ و هولناک است.'),
(2597, 22, 2, 'روزی که آن را مشاهده کنید، [از ترس و وحشت،] هر مادر شیردهی، شیرخواره‌اش را از یاد می‌برد، و هر بارداری، سقط جنین می‌کند. و مردم را مست می‌بینی، ولی مست نیستند، بلکه عذاب خداوند شدید است.'),
(2598, 22, 3, 'و از مردم، کسی هست که ناآگاهانه درباره‌ی خداوند به جدال می‌پردازد و از هر شیطانِ سرکشی پیروی می‌کند.'),
(2599, 22, 4, 'بر آن [شیطان] مقرّر شده که هر کس ولایت او را بپذیرد، قطعاً او را گمراه می‌کند و به آتش سوزانش می‌کشاند.'),
(2600, 22, 5, 'ای مردم! اگر درباره‌ی قیامت شک دارید، [با دقّت در وجود خویش، شک خود را برطرف کنید.] ما شما را از خاک آفریدیم، سپس از نطفه، سپس لخته خون، سپس پاره گوشت، که بعضی خلقتِ کامل یافته، [به دنیا می‌آیند،] و برخی خلقت کامل نیافته، [سقط می‌شوند، چنین کردیم] تا برای شما روشن سازیم [که بر هر چیز تواناییم]. و آنچه را که بخواهیم تا سرآمدی معین در رحم مادران قرار می‌دهیم، سپس شما را به صورت طفل بیرون می‌آوریم، تا به حدّ رشد و بلوغ برسید. و [در این میان] بعضی از شما می‌میرند، و برخی به پست‌ترین مرحله‌ی زندگی [و پیری] می‌رسند تا جایی که دانسته‌های خود را از دست می‌دهند. [همچنین در زمستان] زمین را خشک و مرده می‌بینی، امّا هنگامی که باران بر آن فرومی‌باریم، به حرکت در می‌آید و نُمُوّ می‌کند و انواع گیاهان زیبا می‌رویاند.'),
(2601, 22, 6, '[بیان] این [مطالب] به خاطر آن است که [بدانید] خداوند حقّ است، و اوست که مردگان را زنده می‌کند و بر هر چیزی تواناست.'),
(2602, 22, 7, 'و این که قیامت آمدنی است و شکی در آن نیست. و خداوند تمام کسانی را که در قبرها آرمیده‌اند، زنده خواهد کرد.'),
(2603, 22, 8, 'و از مردم، کسی هست که بدون هیچ دانش و هدایت و کتاب روشنی، درباره‌ی خداوند به جدال می‌پردازد،'),
(2604, 22, 9, 'با تکبر و غرور، می‌خواهد مردم را از راه خدا گمراه کند. در این دنیا رسوایی دارد و در قیامت، عذاب سوزان به او می‌چشانیم.'),
(2605, 22, 10, '«این عذاب دستاوَرد پیشین توست. و قطعاً خدا به بندگانش ستم نمی‌کند.»'),
(2606, 22, 11, 'و از مردم، کسی است که خداوند را تنها با زبان می‌پرستد، [و با حادثه‌ای کوچک می‌لغزد،] پس اگر خیری به او برسد، بدان آرامش یابد، و اگر مصیبت و آزمایشی به او برسد، دگرگون می‌شود [و به سوی کفر می‌رود، چنین کسی] در دنیا و آخرت زیانکار است. این همان زیان آشکار است.'),
(2607, 22, 12, 'او جز خداوند چیزی را می‌خواند که نه زیانی به او می‌رساند و نه سودی. این همان گمراهی دور و دراز است.'),
(2608, 22, 13, 'بلکه کسی را می‌خواند که زیانش از سودش بیشتر است. چه مولا و سرپرست بدی! و چه همدم زشتی!'),
(2609, 22, 14, 'خداوند مؤمنانِ نیکوکار را در باغ‌هایی [از بهشت] وارد می‌کند که نهرها از زیر [درختان] آن جاری است. [آری!] خداوند هرچه اراده کند، انجام می‌دهد.'),
(2610, 22, 15, 'هر کس گمان می‌کند که خداوند هرگز پیامبرش را در دنیا و آخرت یاری نمی‌کند، [و به این خاطر ناامید و خشمگین است‌] طنابی از سقف بیاویزد، سپس [رشته‌ی زندگی خود را] بِبُرَد، آن‌گاه بنگرد که آیا نیرنگش خشم او را فرومی‌نشاند؟!'),
(2611, 22, 16, 'و این‌گونه قرآن را [به صورت] آیاتی روشن فروفرستادیم. و البتّه خداوند هر كس را بخواهد، هدایت می‌كند.'),
(2612, 22, 17, 'خداوند در قیامت، میان مؤمنان [مسلمان]، و یهودیان و صابِئان، و نصارا و مجوس، و مشركان، داوری می‌كند. [و حقّ را از باطل جدا خواهد نمود،] همانا خداوند بر هر چیزی گواه است.'),
(2613, 22, 18, 'آیا ندیدی كه هركه در آسمان‌ها و هر كه در زمین است، و خورشید و ماه و ستارگان، و كوه‌ها و درختان و جنبندگان، و بسیاری از مردم برای خدا سجده می‌كنند؟ البتّه بسیاری از مردم [نیز به خاطر تكبر و لجاجت،] قطعاً گرفتار عذابند. و هر كس را خدا خوار كند، هیچ كس او را گرامی نخواهد داشت. همانا خداوند هر چه را بخواهد، انجام می‌دهد.'),
(2614, 22, 19, 'این دوگروه درباره‌ی پروردگارشان به ستیزه و جدال پرداختند. پس برای كسانی كه كفر ورزیدند، لباس‌هایی از آتش بریده شده است، [و] از بالای سرشان [روی آنها] مایع جوشان ریخته می‌شود.'),
(2615, 22, 20, 'با آن مایع جوشان، آنچه در شكم‌های آنهاست، و [نیز] پوست بدنشان گداخته می‌شود.'),
(2616, 22, 21, 'و گرزهایی آهنین برای [عذاب] آنان [آماده] است.'),
(2617, 22, 22, 'هرگاه بخواهند به خاطر اندوه [فراوان] از دوزخ بیرون آیند، به آن برگردانده می‌شوند [و به آنان گفته می‌شود:] «بچشید عذاب سوزان را!»'),
(2618, 22, 23, 'بی‌گمان خداوند كسانی را كه ایمان آوردند و كارهای نیكو کردند، به باغ‌هایی [بهشتی] وارد می‌كند كه نهرها از زیر [درختان] آن جاری است. در آنجا با دستبندهایی از طلا و مروارید آراسته می‌شوند. و در آنجا لباسشان از ابریشم است.'),
(2619, 22, 24, 'و [بهشتیان] به گفتار پاک [و دلنشین] هدایت می‌شوند. و به راه خداوندی که شایسته ستایش است، راهنمایی می‌گردند.'),
(2620, 22, 25, 'کسانی که کفر ورزیدند و [مؤمنان را] از راه خدا و مسجدالحرام که آن را برای همه‌ی مردم، بومی و غیر بومی، یکسان قرار داده‌ایم، بازمی‌دارند، و [نیز] هر کس بخواهد در آنجا به ستم [از حقّ‌] منحرف شود، به او از عذابی دردناک می‌چشانیم.'),
(2621, 22, 26, 'و [به یادآور] آن‌گاه که مکان کعبه را برای ابراهیم آماده ساختیم، [به او گفتیم:] هیچ‌گونه شرکی نسبت به من روا مدار! و خانه‌ی مرا برای طواف‌کنندگان و قیام‌کنندگان و رکوع‌کنندگانِ سجده‌گزار پاکیزه بدار!'),
(2622, 22, 27, 'و در میان مردم، بانگ حج برآور! تا آنان، پیاده و [یا] سوار بر مرکب‌های چابک، از هر راه دوری به سراغ تو بیایند.'),
(2623, 22, 28, 'تا منافع گوناگون خود را [از برکت این سفر معنوی‌] مشاهده کنند، و نام خدا را در روزهایی معین بر دام‌های زبان بسته‌ای که روزیشان شده، ذکر کنند. پس [چون قربانی کردید،] از [گوشت] آنها بخورید و بینوای فقیر را [نیز] بخورانید.'),
(2624, 22, 29, '[زائران خانه‌ی خدا] سپس باید آلودگی خود را برطرف سازند، و به نذرهای خود وفا کنند، و بر گرد خانه‌ی کهن و آزاد [کعبه] طواف نمایند.'),
(2625, 22, 30, 'این است [مناسک حج] و هر کس آنچه را خداوند محترم شمرده، گرامی بدارد، قطعاً برای او نزد پروردگارش بهتر است. و چهارپایان برای شما حلال شده است، مگر آنچه [حرام بودنش، در آیات دیگر] بر شما خوانده می‌شود. پس از پلیدی بت‌ها دوری کنید و از گفتار باطل بپرهیزید.'),
(2626, 22, 31, '[حج گزارید،] در حالی که همگی خالص برای خدا باشید، و هیچ‌گونه شرکی به خداوند نورزید. و هر کس به خدا شرک ورزد، گویی از آسمان سقوط کرده و پرنده‌ای [شکاری در هوا] او را می‌رباید، یا باد او را به مکانی دور پرتاب می‌کند.'),
(2627, 22, 32, 'این است [دستورات خداوند درباره‌ی حج]. و هر کس شعائر خدا را گرامی بدارد، پس این [بزرگداشت] از تقوای دل‌هاست.'),
(2628, 22, 33, 'برای شما در آن [دام‌های قربانی] تا رسیدن زمان معین [عید قربان] منافعی است، [مانند سواری و تغذیه از شیر آنها،] سپس جایگاه آن [قربانی‌ها،] به سوی [کعبه،] خانه‌ی کهن و آزاد است.'),
(2629, 22, 34, 'و ما برای هر اُمّتی آیینی قرار دادیم، تا هنگام قربانی، نام خدا را بر دام‌هایی که خدا روزی آنان کرده، ببرند. خدای شما معبود یکتاست، پس فقط تسلیم او باشید. و فرمانبردارانِ فروتن را مژده بده.'),
(2630, 22, 35, 'همانان که هرگاه نام خدا برده شود، دل‌هایشان [از خوف خداوند] می‌لرزد، و بر آنچه [از سختی‌ها] به آنان می‌رسد، شکیبایند. و برپاکنندگان نمازند، و از آنچه به آنان روزی داده‌ایم، انفاق می‌کنند.'),
(2631, 22, 36, 'و شترهای چاق را برای شما از شعائر الهی قرار دادیم. در آنها برای شما خیر [و برکت] است. پس در حالی که [برای قربانی] در صف ایستاده‌اند، نام خدا را بر آنها ببرید، پس چون به پهلو افتادند [و جان دادند]، از آنها بخورید. و به [نیازمندان] قانع و [فقیران] درخواست‌کننده بخورانید، این‌گونه دام‌های قربانی را برای شما [رام و] مسخّر کردیم، شاید سپاسگزاری کنید.'),
(2632, 22, 37, 'هرگز گوشت و خون حیوانات قربانی، به خداوند نمی‌رسد؛ بلکه آنچه از طرف شما به او می‌رسد، تقواست. این‌گونه خداوند حیوانات را برای شما مسخّر نمود، تا خدا را به خاطر آن که شما را هدایت کرده است، به بزرگی یاد کنید. و نیکوکاران را مژده بده!'),
(2633, 22, 38, 'قطعاً خداوند از کسانی که ایمان آورده‌اند، دفاع می‌کند. بی‌شک خداوند هیچ خیانتکار ناسپاسی را دوست ندارد.'),
(2634, 22, 39, 'به کسانی که مظلومانه مورد تهاجم و کشتار قرار گرفته‌اند، اجازه‌ی دفاع و جهاد داده شده است. و البتّه خداوند بر یاری آنان تواناست.'),
(2635, 22, 40, 'کسانی که به ناحقّ از خانه و کاشانه‌ی خود رانده شدند [و گناهی نداشتند،] جز این که می‌گفتند: «پروردگار ما خدای یکتاست.» و اگر خداوند بعضی از مردم را به وسیله‌ی بعضی دیگر دفع نمی‌کرد، صومعه‌ها و کنیسه‌ها و کلیساها و مساجدی که نام خداوند در آنها بسیار برده می‌شود، ویران می‌گردید. و قطعاً خداوند کسی که [دین] او را یاری کند، یاری می‌دهد. همانا خداوند نیرومند شکست‌ناپذیر است.'),
(2636, 22, 41, 'همانان که اگر آنان را در زمین به قدرت برسانیم، نماز برپا می‌دارند و زکات می‌دهند و [دیگران را] به خوبی دعوت می‌کنند و از بدی بازمی‌دارند. و پایان کارها با خداوند است.'),
(2637, 22, 42, 'و [ای پیامبر!] اگر تو را تکذیب می‌کنند، قوم نوح و عاد و ثمود نیز که پیش از اینها بودند، [پیامبران را] تکذیب کردند.'),
(2638, 22, 43, 'و قوم ابراهیم و لوط،'),
(2639, 22, 44, 'و مردم مَدْین، [نیز پیامبرشان را تکذیب کردند،] و موسی [نیز از سوی فرعونیان] تکذیب شد، پس به کافران مهلت دادم [تا آن که] آنان را [به قهر] گرفتم. [دیدی] چگونه [کار آنها را] انکار کردم؟!'),
(2640, 22, 45, 'پس چه بسیار آبادی‌هایی که آنها را نابود کردیم و [مردمش] ستمگر بودند، سقف‌هایش فروریخت. و [چه بسیار] چاه [پر آب،] متروک مانده، و کاخ‌های برافراشته [که بی‌ساکن است.]'),
(2641, 22, 46, 'آیا در زمین نگشته‌اند تا برای آنان دل‌هایی پیدا شود که با آن بیندیشند [و حقیقت را بفهمند]؟ یا گوش‌هایی که با آن [حقیقت را] بشنوند؟ البتّه چشم‌های آنان کور نیست، لکن دل‌هایی که در سینه دارند، نابیناست.'),
(2642, 22, 47, 'و [کافران] از تو می‌خواهند که در عذاب شتاب ورزی، و خداوند هرگز از وعده‌ی خود تخلف نمی‌کند. و یک روز نزد پروردگارت، همانند هزار سال [از سال‌هایی] است که شما می‌شمرید.'),
(2643, 22, 48, 'و چه بسیار آبادی‌هایی که به [اهل] آن مهلت دادم، در حالی که ستمگر بودند؛ سپس آن را با قهر خود گرفتم. و بازگشت [همه] به سوی من است.'),
(2644, 22, 49, '[ای پیامبر!] بگو: «ای مردم! من برای شما هشداردهنده‌ای روشنگرم!»'),
(2645, 22, 50, 'پس کسانی که ایمان آوردند و کارهای شایسته انجام دادند، آمرزش و روزی نیکویی دارند.'),
(2646, 22, 51, 'و کسانی که در [انکار و ردّ] آیات ما تلاش کردند و چنین پنداشتند که می‌توانند ما را عاجز کنند، آنان اهل دوزخند.'),
(2647, 22, 52, 'و پیش از تو هیچ رسول و پیامبری را نفرستادیم، مگر این که هرگاه آرزو می‌کرد، [و برای پیشبرد اهداف الهی طرحی می‌ریخت،] شیطان در [طرح و] آرزوی او [خلل] می‌افکند؛ امّا خداوند هر چه را که شیطان می‌افکند، از بین می‌برد، سپس آیات خود را محکم و استوار می‌سازد. و خداوند آگاه و حکیم است.'),
(2648, 22, 53, 'تا [خداوند،] آنچه را شیطان [در دل‌ها] می‌افکند، آزمونی قرار دهد برای آنان که در دل‌هایشان [نوعی] بیماری است، و [نیز] برای سنگ‌دلان. و قطعاً ستمگران در جدایی [و ستیزی] دور و درازند.'),
(2649, 22, 54, 'و نیز [برای آن که] آگاهان بدانند [وحی کدام است، و القائات شیطان کدام. و بدانند] که [طرح و آرزوی پیامبران] حقّی است از طرف پروردگار تو، پس به آن ایمان بیاورند و دل‌هایشان در برابر آن فروتن گردد. و قطعاً خدا کسانی را که ایمان آورده‌اند، به راهی راست هدایت می‌کند.'),
(2650, 22, 55, 'و کسانی که کفر ورزیدند، همواره نسبت به این [قرآن و نبوّت تو] در تردید به سر می‌برند، تا زمانی که قیامت ناگهان برای آنان فرارسد، یا عذاب روز عَقیم به سراغشان آید.'),
(2651, 22, 56, 'آن روز، فرمانروایی [مطلق] برای خداست. او در میان مردم داوری می‌کند. پس کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، در باغ‌های پر نعمت [بهشت] خواهند بود.'),
(2652, 22, 57, 'و کسانی که کفر ورزیدند و آیات ما را تکذیب کردند، پس برای آنان عذاب خوارکننده‌ای است.'),
(2653, 22, 58, 'و کسانی که در راه خدا هجرت کردند، سپس کشته شده یا مرده‌اند، قطعاً خداوند رزق نیکویی به آنان خواهد داد. و خداوند بهترینِ روزی‌دهندگان است.'),
(2654, 22, 59, 'بی‌تردید خداوند آنان را به جایگاهی وارد می‌کند که مورد رضایتشان باشد، همانا خداوند دانا و بردبار است.'),
(2655, 22, 60, '[آری،] چنین است. [اگر] هر کس به همان مقدار که به او ستم شده، [ستمگر را] مجازات کند، ولی باز هم به او ستم شود، قطعاً خداوند او را یاری خواهد کرد. البتّه خداوند بخشایشگر و آمرزنده است.'),
(2656, 22, 61, 'این [وعده‌ی نصرت الهی] به خاطر آن است که خداوند [بر هر چیزی تواناست. اوست که] شب را در روز و روز را در شب فرومی‌برد. البتّه خداوند شنوا و بیناست.'),
(2657, 22, 62, 'این [قدرت بی‌نهایت] به خاطر آن است که خداوند حقّ است و هر چه را غیر از او بخوانند، آن باطل است. البتّه خداوند، بلندمرتبه و بزرگ است.'),
(2658, 22, 63, 'آیا ندیدی که خداوند از آسمان، آبی فروفرستاد، آن‌گاه زمین سرسبز گشت؟ همانا خدا دارای لطف و آگاه است.'),
(2659, 22, 64, 'آنچه در آسمان‌ها و آنچه در زمین است، از آنِ اوست. و در حقیقت، خداوند است که بی‌نیاز و ستوده است.'),
(2660, 22, 65, 'آیا ندیدی که خداوند آنچه را در زمین است، و کشتی‌هایی را که به امر او در دریا می‌روند، برای شما رام کرده است؟ و آسمان [و کرات و سنگ‌های آن‌] را نگاه داشته تا جز به فرمان او بر زمین نیفتد؟'),
(2661, 22, 66, 'و او کسی است که به شما زندگی بخشید، سپس شما را می‌میراند، بار دیگر زنده می‌کند؛ البتّه انسان بسیار ناسپاس است.'),
(2662, 22, 67, 'برای هر امّتی آیینی قرار داده‌ایم که آنها به آن عمل کنند، پس نباید در این امر با تو نزاع کنند [که چگونه آیین تازه‌ای آورده‌ای؟ مردم را] به راه پروردگارت فراخوان! همانا تو بر راهی راست قرار داری.'),
(2663, 22, 68, 'و اگر [کافران] با تو به جدال برخاستند، پس به آنان بگو: «خداوند به آنچه شما می‌کنید، آگاه‌تر است.'),
(2664, 22, 69, 'خداوند در روز قیامت، در آنچه اختلاف داشتید، میان شما داوری خواهد کرد.»'),
(2665, 22, 70, 'آیا نمی‌دانی که خداوند آنچه را در زمین و آسمان است، می‌داند؟ همه‌ی اینها در کتابی [چون لوح محفوظ، ثبت] است. و این [ثبت] بر خداوند، آسان است.'),
(2666, 22, 71, 'و کافران، به جای خداوند، چیزی را می‌پرستند که او هیچ دلیلی بر آن نازل نکرده است. و آنها نسبت به آن هیچ اطلاعی ندارند. و ستمگران را هیچ یاوری نیست.'),
(2667, 22, 72, 'و هنگامی که آیات روشن ما بر آنان خوانده می‌شود، در چهره‌ی کسانی که کفر ورزیدند، [نشانه‌های] انکار را می‌شناسی، [به‌گونه‌ای که] نزدیک است به کسانی که آیات ما را بر آنان می‌خوانند، حمله کنند. [به آنان] بگو: «آیا شما را به چیزی بدتر از این خبر دهم؟ آتشی که خداوند به کافران وعده داده است. و چه بد سرانجامی است!»'),
(2668, 22, 73, 'ای مردم! مثلی زده شده است، پس [با دقّت] به آن گوش فرادهید. كسانی را كه به جای خدای یكتا می‌خوانید، هرگز نمی‌توانند [حتّی‌] مگسی بیافرینند، هر چند برای این كار دست به دست هم دهند. و اگر آن مگس، چیزی از آنان برباید، نمی‌توانند از او بازپس گیرند! طالب و مطلوب هر دو ناتوانند. [هم پرستش‌كنندگان و هم معبودان.]'),
(2669, 22, 74, '[مشركان،] خدا را آن‌گونه كه شایسته‌ی اوست، بزرگ نداشتند. قطعاً خداوند نیرومند و شكست‌ناپذیر است.'),
(2670, 22, 75, 'خداوند از میان فرشتگان و مردم، رسولانی برمی‌انگیزد. همانا خداوند شنوا و بیناست.'),
(2671, 22, 76, 'آینده و گذشته‌ی آنها را می‌داند. و تمام كارها به خدا بازگردانده می‌شود.'),
(2672, 22, 77, 'ای كسانی كه ایمان آورده‌اید! ركوع كنید و سجده نمایید و پروردگارتان را عبادت كنید و كار نیک انجام دهید، باشد كه رستگار شوید.'),
(2673, 22, 78, 'و در راه خدا جهاد كنید، چنان كه شایسته‌ی جهاد [در راه] اوست. او شما را [بر همه‌ی امّت‌ها] برگزید و در دین [اسلام] هیچ‌گونه دشواری بر شما قرار نداد. آیین پدرتان ابراهیم [را پیروی كنید]، او كه شما را از پیش مسلمان نامید، و در این [كتاب نیز چنین است،] تا این پیامبر بر شما گواه باشد، و شما [نیز] بر مردم گواه باشید. پس نماز برپا دارید و زكات بپردازید، و به [ریسمان] خداوند چنگ زنید، كه او مولا و سرپرست شماست. پس چه خوش مولایی و چه خوب یاوری!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'به راستی مؤمنان رستگار شدند.'),
(2675, 23, 2, 'همان كسانی كه در نمازشان فروتنی دارند.'),
(2676, 23, 3, 'و آنان كه از بیهودگی روی‌گردانند.'),
(2677, 23, 4, 'و همان كسانی كه زكات می‌پردازند.'),
(2678, 23, 5, 'و آنان كه دامان خود را نگه می‌دارند.'),
(2679, 23, 6, 'مگر با همسرانشان، یا كنیزانی كه به دست آورده‌اند، پس آنان [در آمیزش با این دو گروه] سرزنش نمی‌شوند.'),
(2680, 23, 7, 'پس كسانی که فراتر از این بجویند، آنان [از حدود الهی‌] تجاوز كرده‌اند.'),
(2681, 23, 8, 'و [مؤمنان رستگار] كسانی هستند كه امانت‌ها و پیمان‌های خود را رعایت می‌كنند.'),
(2682, 23, 9, 'و آنان بر نمازهای خود مواظبت دارند.'),
(2683, 23, 10, 'آنان وارثانند،'),
(2684, 23, 11, 'كسانی كه بهشت برین را ارث می‌برند، و در آن جاوِدانه خواهند ماند.'),
(2685, 23, 12, 'و همانا ما انسان را از عصاره‌ای از گِل آفریدیم.'),
(2686, 23, 13, 'سپس او را به صورت نطفه در جایگاهی استوار قرار دادیم.'),
(2687, 23, 14, 'سپس از نطفه، لخته خونی آفریدیم. آن‌گاه لخته خون را پاره گوشتی ساختیم، و پاره گوشت را به صورت استخوان‌هایی درآوردیم، و استخوان‌ها را گوشتی پوشاندیم. سپس آن را آفرینش تازه‌ای دادیم، پس آفرین بر خدا، و پرخیر و بركت است او كه بهترینِ آفرینندگان است.'),
(2688, 23, 15, 'سپس شما بی‌تردید بعد از آن [مراحل،] می‌میرید.'),
(2689, 23, 16, 'آن‌گاه روز قیامت برانگیخته می‌شوید.'),
(2690, 23, 17, 'و همانا ما بالای سر شما، هفت راه [آسمانی] آفریدیم، و ما از خلق [و جهان هستی] غافل نبوده‌ایم.'),
(2691, 23, 18, 'و از آسمان، آبی به اندازه فروفرستادیم، و آن را در زمین جای دادیم. و همانا ما بر [از بین‌] بُردن آن تواناییم.'),
(2692, 23, 19, 'پس به وسیله‌ی آن [آب] برای شما باغ‌هایی از خرما و انگور پدید آوردیم که در آن باغ‌ها برای شما میوه‌های فراوانی است، و از آنها می‌خورید.'),
(2693, 23, 20, 'و [نیز زیتون‌،] درختی که از طور سینا می‌روید، [و] روغن و خوراک برای خورندگان به بار می‌آورد.'),
(2694, 23, 21, 'و برای شما در چهارپایان عبرتی است، از آنچه در درون آنهاست، به شما [شیر] می‌نوشانیم. و در آنها برای شما منافع بسیاری است. و از [گوشت] آنها می‌خورید.'),
(2695, 23, 22, 'و بر آنها، و بر کشتی‌ها سوار می‌شوید.'),
(2696, 23, 23, 'و همانا نوح را به سوی قومش فرستادیم، پس [به آنها] گفت: «ای قوم من! خداوند [یکتا] را بپرستید. جز او هیچ معبودی برای شما نیست، آیا پروا نمی‌کنید؟»'),
(2697, 23, 24, 'پس اشراف قوم او که کافر شدند، گفتند: «این [نوح] انسانی همانند شماست. او می‌خواهد بر شما برتری جوید. اگر خداوند می‌خواست [برای ما پیامبری بفرستد،] قطعاً فرشتگانی می‌فرستاد. ما چنین چیزی را در میان نیاکان خود نشنیده‌ایم [که بشری ادّعای پیامبری کند!]'),
(2698, 23, 25, 'او فقط مردی است که مبتلا به [نوعی‌] دیوانگی است. پس تا مدّتی درباره‌ی او صبر کنید. [تا از جنون نجات یابد یا بمیرد.]»'),
(2699, 23, 26, 'نوح گفت: «پروردگارا! مرا در برابر تکذیب آنان یاری فرما!»'),
(2700, 23, 27, 'پس ما به نوح وحی کردیم: «زیر نظر ما و [مطابق دستور و آموزش و] وحی ما کشتی بساز.» پس همین که فرمان [قهر] ما آمد و [آب از] تنور جوشید، از تمام حیوانات، یک جفت، [نر و ماده،] و [نیز] خانواده‌ات را واردِ کشتی کن، مگر کسی از آنان که پیش‌تر درباره‌ی او سخن به میان آمده [و وعده‌ی هلاکتش داده شده] است. و درباره ستمگران با من سخن مگو، که قطعاً آنان غرق خواهند شد.'),
(2701, 23, 28, 'پس هنگامی که تو و همراهانت بر کشتی سوار شدید، بگو: «حمد و سپاس برای خداوندی است که ما را از گروه ستمگران نجات داد.»'),
(2702, 23, 29, 'و بگو: «پروردگارا! مرا به جایگاهی خجسته و پر برکت فرودآور، که تو بهترینِ میزبانانی.»'),
(2703, 23, 30, 'البتّه در این داستان نشانه‌هایی است. و همانا ما آزمایش کننده بودیم.'),
(2704, 23, 31, 'سپس بعد از قوم نوح، نسل دیگری پدید آوردیم.'),
(2705, 23, 32, 'و در میان آنان [نیز] پیامبری از خودشان فرستادیم که [به آنان گفت:] «خدا را بپرستید. جز او معبودی برای شما نیست، پس آیا پروا نمی‌کنید؟»'),
(2706, 23, 33, 'و اشرافی از قوم آن پیامبر که کفر ورزیدند و دیدار قیامت را تکذیب کردند، و آنان را در دنیا در ناز و نعمت قرار داده بودیم، [به مردم] گفتند: «این، تنها انسانی مانند شماست. از هر چه می‌خورید، می‌خورد، و از هر چه می‌نوشید، می‌نوشد.'),
(2707, 23, 34, 'و اگر از بشری همانند خودتان اطاعت کنید، قطعاً زیانکارید.'),
(2708, 23, 35, 'آیا [او] به شما وعده می‌دهد که وقتی مردید، و خاک، و استخوان‌هایی [پوسیده] شدید، [دیگر بار از قبر] بیرون آورده می‌شوید؟!'),
(2709, 23, 36, '[هرگز!] دور است، دور! آنچه به شما وعده می‌دهند.'),
(2710, 23, 37, 'جز این زندگی دنیا، [زندگی‌] دیگری نیست. [برخی] می‌میریم و [برخی دیگر] به دنیا می‌آییم. و ما [هرگز پس از مرگ] برانگیخته نخواهیم شد.'),
(2711, 23, 38, 'او فقط مردی است که بر خدا دروغ می‌بندد. و ما هرگز به او ایمان نخواهیم آورد.»'),
(2712, 23, 39, '[پیامبرشان] گفت: «پروردگارا! مرا در برابر تکذیب‌های آنان یاری فرما!»'),
(2713, 23, 40, '[خداوند] فرمود: «قطعاً به زودی پشیمان خواهند شد.»'),
(2714, 23, 41, 'پس [قهر خدا آمد و] صیحه‌ی مرگبار که سزاوارش بودند، آنان را فراگرفت، و آنان را همچون خاشاکی که بر آب افتد، قرار دادیم، پس گروه ستمگر [از رحمت خدا] دور باد!'),
(2715, 23, 42, 'آن‌گاه پس از [نابودی] این گروه، نسل‌های دیگری پدید آوردیم.'),
(2716, 23, 43, 'هیچ امّتی از سرآمد [عمر] خود، نه پیش می‌افتد و نه پس می‌ماند.'),
(2717, 23, 44, 'سپس پیامبرانمان را پی‌درپی فرستادیم. هر بار که پیامبر هر امّتی، به سراغ قومش آمد، مردم او را تکذیب کردند. آن‌گاه آنان را در پی یکدیگر [به هلاکت] آوردیم، و آنها را [موضوع] افسانه‌ها گرداندیم. پس دور [از رحمت خدا] باد قومی که ایمان نمی‌آورند.'),
(2718, 23, 45, 'سپس موسی و برادرش هارون را با معجزات و دلیلی روشن فرستادیم،'),
(2719, 23, 46, 'به سوی فرعون و اشراف قومش، ولی آنان تکبّر ورزیدند. و آنان مردمی برتری‌جوی و گردنکش بودند.'),
(2720, 23, 47, 'پس گفتند: «آیا ما به دو انسان مانند خودمان ایمان بیاوریم، در حالی که قوم این دو برده‌ی ما بودند؟»'),
(2721, 23, 48, 'پس آن دو را تکذیب کردند، و از هلاک‌شدگان گشتند.'),
(2722, 23, 49, 'و به راستی ما به موسی کتاب دادیم، شاید [قومش] هدایت شوند.'),
(2723, 23, 50, 'و ما [عیسی] فرزند مریم و مادرش را معجزه قرار دادیم، و آن دو را در سرزمینی مرتفع که دارای آرامش، امنیت و [آب] گوارا بود، جای دادیم.'),
(2724, 23, 51, 'ای پیامبران! از غذاهای پاکیزه بخورید و کار نیکو کنید. همانا من به آنچه می‌کنید، آگاهم.'),
(2725, 23, 52, 'و البتّه این اُمت شما، اُمت واحدی است، و من پروردگار شمایم؛ پس، از من پروا کنید.'),
(2726, 23, 53, 'امّا مردم، کارشان را در میان خود، به پراکندگی کشاندند، هر گروهی به راهی رفتند، و هر حزب و دسته‌ای به آنچه نزدشان بود، دل خوش کردند.'),
(2727, 23, 54, 'پس آنان را تا مدّتی در گرداب جهلشان واگذار.'),
(2728, 23, 55, 'آیا می‌پندارند آنچه از مال و فرزندان که آنان را بدان یاری می‌رسانیم،'),
(2729, 23, 56, 'شتاب می‌کنیم که خیرهایی به آنان برسانیم؟ [چنین نیست!] بلکه آنان نمی‌فهمند [مال و فرزند وسیله آزمایش است].'),
(2730, 23, 57, 'کسانی که از خوف پروردگارشان بیمناکند،'),
(2731, 23, 58, 'و آنان که به آیات پروردگارشان ایمان دارند،'),
(2732, 23, 59, 'و به پروردگارشان شرک نمی‌ورزند،'),
(2733, 23, 60, 'و کسانی که [در راه خدا] می‌دهند، آنچه را دادند، در حالی که دل‌هایشان ترسان است از این که به سوی پروردگارشان بازمی‌گردند،'),
(2734, 23, 61, 'چنین کسانی در خیرات شتاب می‌ورزند. و همانان هستندکه در [رسیدن به] خیرات از یکدیگر پیشی می‌گیرند.'),
(2735, 23, 62, 'و ما هیچ‌کس را جز به مقدار توانش، تکلیف نمی‌کنیم. و نزد ما کتابی است که به حقّ سخن می‌گوید و به آنان هیچ ستمی نمی‌شود.'),
(2736, 23, 63, 'بلکه دل‌های آنان از این [کتاب] در غَفلَت و بی‌خبری است. و کارهای دیگری جز این دارند که پیوسته آن را انجام می‌دهند.'),
(2737, 23, 64, 'تا زمانی که مرفّهان [مغرور] را به قهر خود گرفتار سازیم. در این هنگام ناله سر می‌دهند.'),
(2738, 23, 65, '«امروز ناله نکنید! قطعاً شما از جانب ما یاری نخواهید شد.»'),
(2739, 23, 66, 'آیات من پیوسته بر شما تلاوت می‌شد؛ ولی شما روی‌گردانده، به عقب بازمی‌گشتید.'),
(2740, 23, 67, 'درحالی که نسبت به آن تکبّر می‌ورزیدید، و در شب‌نشینی [خود درباره‌ی آن‌] هَذیان می‌گفتید.'),
(2741, 23, 68, 'آیا آنها در این گفتار نیندیشیدند؟ یا مطالبی [تازه] برای آنان آمده که برای نیاکانشان نیامده است؟'),
(2742, 23, 69, 'یا این که پیامبرشان را نشناختند [و از سوابق او آگاه نیستند]؟ پس برای همین او را انکار می‌کنند؟'),
(2743, 23, 70, 'یا می‌گویند: «او جنون دارد؟» [چنین نیست!] بلکه او حقّ را برای آنان آورده، امّا بیشترشان [پذیرش] حقّ را خوش ندارند.'),
(2744, 23, 71, 'و اگر حقّ، از هوس‌های آنان پیروی می‌کرد، قطعاً آسمان‌ها و زمین، و کسانی که در آنها هستند، تباه می‌شدند؛ بلکه ما وسیله یادآوری [و بیداری] آنها را برای آنها آورده‌ایم، اما آنان از [قرآنِ] یادآورِ خویش روی‌گردانند.'),
(2745, 23, 72, '[ای پیامبر!] آیا تو از آنها مزدی خواسته‌ای؟ با این که پاداش دائمی پروردگارت بهتر است. و او بهترینِ روزی‌دهندگان است.'),
(2746, 23, 73, 'و قطعاً تو مردم را به راهی راست دعوت می‌کنی.'),
(2747, 23, 74, 'و کسانی که به آخرت ایمان نمی‌آورند، از [این‌] راه منحرفند.'),
(2748, 23, 75, 'و اگر به آنها رحم کنیم و بدبختی‌های آنان را برطرف سازیم، [به‌جای هوشیاری و شکرگزاری،] کوردلانه در طغیانشان لجاجت می‌ورزند.'),
(2749, 23, 76, 'و بی‌گمان آنان را به عذاب گرفتار کردیم؛ ولی آنان در برابر پروردگارشان، نه فروتنی کردند و نه زاری می‌کنند.'),
(2750, 23, 77, '[ما هم این سنگ‌دلان را رها می‌کنیم،] تا زمانی که دری از عذاب شدید به روی آنان بگشاییم، [و چنان گرفتار شوند که] ناگهان در آن [عذاب] نومید شوند.'),
(2751, 23, 78, 'و اوست که برای شما گوش و چشم‌ها و دل‌ها آفرید. چه اندک سپاسگزاری می‌کنید!'),
(2752, 23, 79, 'و اوست که شما را در زمین آفرید، و به سوی او محشور می‌شوید.'),
(2753, 23, 80, 'و اوست که زنده می‌کند و می‌میراند؛ و رفت و آمد شب و روز از آنِ او [و به دست او] است، پس آیا نمی‌اندیشید؟'),
(2754, 23, 81, 'بلکه [آنان نیز] سخنی همانند گفته‌ی پیشینیان گفتند.'),
(2755, 23, 82, 'گفتند: «آیا اگر مردیم، و خاک و استخوان‌هایی [پوسیده] شدیم، آیا ما بار دیگر برانگیخته خواهیم شد؟'),
(2756, 23, 83, 'البتّه به ما و پدرانمان، پیش از این [نیز] این وعده را داده‌اند. این [سخنان و وعده‌ها] جز افسانه‌های پیشینیان نیست.»'),
(2757, 23, 84, '[به این کافران] بگو: «اگر شما می‌دانید، زمین و کسانی که در آن زندگی می‌کنند، از آنِ کیست؟»'),
(2758, 23, 85, 'آنان خواهند گفت: «برای خداست.» بگو: «پس آیا پند نمی‌گیرید؟ [و قدرت او را بر معاد، انکار می‌کنید]؟!»'),
(2759, 23, 86, 'بگو: «پروردگار آسمان‌های هفت‌گانه و پروردگار عرش بزرگ کیست؟»'),
(2760, 23, 87, '[باز هم] خواهند گفت: «برای خداست.» بگو: «آیا پروا نمی‌کنید؟»'),
(2761, 23, 88, 'بگو: «اگر می‌دانید، فرمانروایی همه‌ی موجودات به دست کیست؟ او که [به همه] پناه می‌دهد، ولی هیچ‌کس در برابر او پناه ندارد؟»'),
(2762, 23, 89, 'به زودی خواهند گفت: «برای خداست.» بگو: «پس چگونه [می‌گویید] جادو شده‌اید [و این سخنان سحر و افسون است]؟!»'),
(2763, 23, 90, 'بلکه حقّ را برای آنان آوردیم. و قطعاً آنان دروغ می‌گویند.'),
(2764, 23, 91, 'خداوند، هرگز فرزندی برای خود نگرفته است، و هیچ معبودی با او نیست. [اگر چنین بود،] قطعاً هر معبودی [برای آنکه تدبیرِ امور کند،] آفریده‌های خود را با خود می‌برد. و بی‌تردید بعضی از خدایان بر بعضی دیگر برتری می‌جستند. خداوند از آنچه وصف می‌کنند، منزّه است.'),
(2765, 23, 92, 'او به غیب و شهود، [نهان و آشکار،] آگاه است. و از هر چیزی که برای او شریک می‌گیرند، برتر است.'),
(2766, 23, 93, '[ای پیامبر!] بگو: «پروردگارا! اگر آنچه را به آنان وعده داده شده، به من نشان دهی،'),
(2767, 23, 94, 'پس پروردگارا! مرا در میان گروه ستمکاران قرار مده»'),
(2768, 23, 95, 'و بی‌شک ما می‌توانیم آنچه را به آنان وعده می‌دهیم، به تو نشان دهیم.'),
(2769, 23, 96, '[ای پیامبر!] بدی را به بهترین شیوه دور کن! [و در مقام مقابله به مثل مباش!] ما به آنچه [مخالفان] وصف می‌کنند، آگاه‌تریم.'),
(2770, 23, 97, 'و بگو: «پروردگارا! من از وسوسه‌های شیطان‌ها به تو پناه می‌برم.'),
(2771, 23, 98, 'و پناه می‌برم به تو ای پروردگار! از این که آنان نزد من حاضر شوند.»'),
(2772, 23, 99, '[آنها به کردارهای زشت ادامه می‌دهند،] تا زمانی که مرگ به سراغ یکی از آنان آید، [آن‌گاه] می‌گوید: «پروردگارا! مرا بازگردان!'),
(2773, 23, 100, 'شاید در آنچه از خود به جای گذاشته‌ام، کار نیکی انجام دهم. هرگز!» این سخنی است که می‌گوید، [ولی اگر برگردد، به آن عمل نمی‌کند.] و در پی آنان برزخی است تا روزی که برانگیخته شوند.'),
(2774, 23, 101, 'پس آن‌گاه که در صور دمیده شود، در آن روز، نه میانشان خویشاوندی است، و نه [از حال یکدیگر] می‌پرسند.'),
(2775, 23, 102, 'پس کسانی که ترازوهای اعمالشان سنگین [و باارزش] باشد، آنان همان رستگارانند.'),
(2776, 23, 103, 'و کسانی که ترازوهای اعمالشان سبک [و بی‌ارزش] باشد، آنان کسانی هستند که سرمایه‌ی وجود خود را از دست داده‌اند و همیشه در جهنّم می‌مانند.'),
(2777, 23, 104, 'آتش، صورت آنها را می‌سوزاند. و آنان در دوزخ، چهره‌ای [زشت و] عبوس دارند.'),
(2778, 23, 105, '[خدا به آنان می‌فرماید:] «آیا آیات من بر شما خوانده نمی‌شد، پس آنها را دروغ می‌شمردید؟»'),
(2779, 23, 106, 'می‌گویند: «پروردگارا! شقاوت [و بدبختی] ما بر ما چیره شد. و گروهی گمراه بودیم.'),
(2780, 23, 107, 'پروردگارا! ما را از دوزخ بیرون آور! اگر بار دیگر [به کفر و گناه] بازگشتیم، قطعاً ستمگریم. [و سزاوار عذاب!]»'),
(2781, 23, 108, '[خداوند] می‌فرماید: در آتش دور و گم شوید، و با من سخن مگویید.'),
(2782, 23, 109, '[آیا فراموش کردید که] گروهی از بندگان من می‌گفتند: «پروردگارا! ایمان آوردیم، پس ما را ببخش و بر ما رحم کن. و تو بهترینِ رحم‌کنندگانی.»'),
(2783, 23, 110, '[امّا] شما آنان را به مسخره گرفتید تا آن که [سرگرمی شما به تمسخر آنان‌] یاد مرا از خاطرتان برد، و شما همواره به آنان می‌خندیدید.'),
(2784, 23, 111, 'من امروز به خاطر آن که [مؤمنان در برابر تمسخر شما] صبر کردند، به آنان پاداش دادم. که ایشانند رستگاران.'),
(2785, 23, 112, '[آن‌گاه] از آنان می‌پرسد: «چقدر در زمین ماندید؟»'),
(2786, 23, 113, 'می‌گویند: «یک روز یا بخشی از یک روز، پس از شمارندگان [خود] بپرس!»'),
(2787, 23, 114, 'می‌فرماید: اگر آگاهی داشتید، می‌دانستید که جز اندکی درنگ نکرده‌اید.'),
(2788, 23, 115, 'پس آیا می‌پندارید که ما شما را بیهوده آفریده‌ایم؟ و شما به سوی ما بازگردانده نمی‌شوید؟'),
(2789, 23, 116, 'پس برتر است خداوندی که فرمانروای حقّ است. معبودی جز او نیست، او پروردگار عرش گران‌قدر است.'),
(2790, 23, 117, 'و هر کس با خداوند، معبود دیگری را بخواند، هیچ برهانی بر کار خود ندارد. پس قطعاً حساب او نزد پروردگارش خواهد بود. بی‌گمان کافران رستگار نمی‌شوند.'),
(2791, 23, 118, 'و بگو: «پروردگارا! بیامرز و رحم کن که تو بهترینِ رحم‌کنندگانی!»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '[این] سوره‌ای است که آن را فروفرستادیم و [عمل به] آن را واجب نمودیم و در آن آیاتی روشن فرستادیم. باشد که شما پند گیرید.'),
(2793, 24, 2, 'هر یک از زن و مرد زناکار را صد تازیانه بزنید. و اگر به خدا و روز قیامت ایمان دارید، در اجرای [حکم] دینِ خدا، نسبت به آن دو، گرفتار دلسوزی نشوید. و هنگام کیفر آن دو، گروهی از مؤمنان حاضر و ناظر باشند.'),
(2794, 24, 3, 'مرد زناکار، جز با زن زناکار یا مشرک ازدواج نمی‌کند. و زن زناکار را، جز مرد زناکار یا مشرک، به همسری نمی‌گیرد. و این [زناشویی] بر مؤمنان حرام است!'),
(2795, 24, 4, 'و کسانی که نسبت زنا به زنان پاکدامن می‌دهند و چهار شاهد نمی‌آورند، پس هشتاد تازیانه به آنان بزنید و گواهی آنان را هرگز نپذیرید، که آنان همان فاسقانند.'),
(2796, 24, 5, 'مگر کسانی که پس از آن [تهمت زدن،] توبه کنند و در مقام اصلاح و جبران برآیند، که قطعاً خداوند آمرزنده و مهربان است.'),
(2797, 24, 6, 'و کسانی که به همسران خود نسبت زنا می‌دهند، و جز خودشان شاهدی ندارند، هر یک از آنان برای اثبات ادّعای خود، باید چهار بار به خدا سوگند یاد کند که قطعاً [درباره‌ی همسرش] از راستگویان است.'),
(2798, 24, 7, 'و در پنجمین بار بگوید که لعنت خدا بر او باد اگر [در این اتهام بستن‌،] از دروغگویان باشد!'),
(2799, 24, 8, 'و کیفر [زنا] از آن زن ساقط می‌شود، بدین‌گونه که [در مقام دفاع،] چهار بار به خدا قسم بخورد که آن مرد [در نسبتی که به او می‌دهد] از دروغگویان است.'),
(2800, 24, 9, 'و بار پنجم بگوید که غضب خدا بر او باد، اگر آن مرد از راستگویان باشد.'),
(2801, 24, 10, 'و اگر فضل و رحمت خداوند بر شما نبود، [رسوا می‌شدید. و نظام خانوادگی شما مختل می‌شد.] قطعاً خداوند توبه‌پذیر و حکیم است.'),
(2802, 24, 11, 'کسانی که آن دروغِ بزرگ را [نسبت به یکی از همسران پیامبر به میان‌] آوردند، گروهی از خودِ شما بودند. آن را برای خود شر نپندارید، بلکه آن [در نهایت] به نفع شماست. [زیرا سبب رسوایی منافقان است.] هر کدام از آنان، به اندازه‌ی گناهی که مرتکب شده است، به کیفر می‌رسد. و برای کسی که بخش بزرگ گناه را بر عهده گرفته، عذابی بزرگ است.'),
(2803, 24, 12, 'چرا زمانی که تهمت را شنیدید، مردان و زنان با ایمان، نسبت به خویش گمان خوب نبردند. و نگفتند: «این تهمتی بزرگ و آشکار است!»'),
(2804, 24, 13, 'چرا چهار شاهد بر آن نیاوردند؟ پس چون گواهان لازم را نیاوردند، آنان نزد خدا همان دروغگویانند.'),
(2805, 24, 14, 'و اگر فضل و رحمت الهی در دنیا و آخرت بر شما نبود، به سزای آن سخنان که می‌گفتید، قطعاً عذابی بزرگ به شما می‌رسید.'),
(2806, 24, 15, 'آن‌گاه که از زبان یکدیگر [آن تهمت را] می‌گرفتید و با آنکه هیچ اطّلاعی از آن نداشتید، دهان به دهان می‌گفتید. و این را ساده و ناچیز می‌پنداشتید، در حالی که آن نزد خدا بزرگ است.'),
(2807, 24, 16, 'چرا هنگامی که [آن را] شنیدید، نگفتید: «روا نیست که ما در این [باره از پیش خود] حرفی بزنیم. [پروردگارا!] تو منزّهی! این بهتانی بزرگ است.»'),
(2808, 24, 17, 'خداوند شما را اندرز می‌دهدکه اگر ایمان دارید، هرگز امثال این تهمت‌ها را تکرار نکنید.'),
(2809, 24, 18, 'خداوند برای شما آیات خود را بیان می‌کند. و او دانا و حکیم است.'),
(2810, 24, 19, 'کسانی که دوست دارند فحشا و زشتی‌ها در میان اهل ایمان فاش و آشکار شود، در دنیا و آخرت برای آنها عذاب دردناکی است. و خداوند [عواقب آن را] می‌داند، و شما نمی‌دانید.'),
(2811, 24, 20, 'و اگر فضل و رحمت الهی بر شما نبود و این که خداوند رئوف و مهربان است [شما را سخت کیفر می‌داد].'),
(2812, 24, 21, 'ای کسانی که ایمان آورده‌اید! گام‌های شیطان را پیروی نکنید. و هر کس پیرو گام‌های شیطان شود، [گمراه می‌شود، زیرا] او به فحشا و منکر فرمان می‌دهد. و اگر فضل و رحمت خداوند بر شما نبود، هرگز هیچ یک از شما پاک نمی‌شد؛ ولی خداوند هر کس را بخواهد، پاک می‌سازد. و خداوند شنوا و داناست.'),
(2813, 24, 22, 'آنها که از میان شما دارای برتری [مالی] و وسعت زندگی هستند، نباید سوگند بخورند که به نزدیکان و مستمندان و مهاجرانِ در راه خدا چیزی ندهند؛ بلکه باید [آنان را] ببخشند و از آنان درگذرند. آیا دوست ندارید که خداوند شما را ببخشد؟ و خداوند آمرزنده و مهربان است.'),
(2814, 24, 23, 'کسانی که زنان پاکدامن باایمان را که [از شدّت ایمان از بی‌عفتی] بی‌خبرند، متّهم به فحشا کنند، در دنیا و آخرت از رحمت الهی دورند و برای آنان عذابی بزرگ است.'),
(2815, 24, 24, 'روزی که زبان‌ها و دست‌ها و پاهایشان، به آنچه همواره انجام می‌دادند، به زیان آنان، گواهی می‌دهند.'),
(2816, 24, 25, 'آن روز، خداوند جزای حقّ آنان را بی‌کم و کاست خواهد داد. و آنان خواهند دانست که خداوند همان حقیقت آشکار است.'),
(2817, 24, 26, 'زنان پلید، سزاوار مردان پلیدند، و مردان پلید، سزاوار زنان پلید. زنان پاک، برای مردان پاکند، و مردان پاک، برای زنان پاک. آنان از آنچه درباره‌ی آنها می‌گویند، برکنارند. و برای آنان آمرزش و روزی نیکویی است.'),
(2818, 24, 27, 'ای کسانی که ایمان آورده‌اید! به خانه‌هایی که منزل شما نیست، پیش از آنکه اجازه بگیرید و بر اهل آن سلام کنید، وارد نشوید. این برای شما بهتر است. شاید پند گیرید.'),
(2819, 24, 28, 'پس اگر کسی را در خانه نیافتید، وارد آنجا نشوید، تا آنکه به شما اجازه‌ی ورود داده شود. و اگر به شما گفته شد: «برگردید!» پس برگردید [و آزرده نشوید]. این برای پاک‌تر ماندن شما [بهتر] است. و خداوند به عملکرد شما آگاه است.'),
(2820, 24, 29, 'بر شما گناهی نیست که به منازل غیر مسکونی که در آن کالایی دارید، وارد شوید. و خداوند به آنچه آشکار یا پنهان می‌کنید، آگاه است.'),
(2821, 24, 30, 'به مردان مؤمن بگو: چشم‌های خود را [از نگاه به نامحرمان] فروگیرند، و دامان خود را [از آلودگی] حفظ نمایند. این برای پاک‌تر ماندن آنان [بهتر] است. خداوند به آنچه می‌کنند، آگاه است.'),
(2822, 24, 31, 'و به زنان با ایمان بگو: چشم‌های خود را [از نگاه هوس‌آلود] فروگیرند، و دامان خود را [از آلودگی] حفظ نمایند. و زینت خود را، جز آن مقدار که [به طور طبیعی] نمایان است، آشکار نکنند. و مقنعه‌ی خود را بر گردنشان بیفکنند [تا علاوه بر سر، گردن و سینه‌ی آنان نیز پوشیده باشد]. و زینت خود را آشکار نکنند، مگر برای شوهرشان، یا پدرشان، یا پدرشوهر، یا پسرشان، یا پسرشوهرشان [که از همسر دیگر است]، یا برادرشان، یا پسربرادر، یا پسرخواهر، یا زنان [هم‌کیش] خود، یا آنچه را مالک شده‌اند [از کنیز و برده]، یا مردان سفیه که تمایل جنسی ندارند، یا کودکانی که از امور جنسی زنان آگاه نیستند. و نیز، [هنگام راه رفتن] پای خود را به‌گونه‌ای به زمین نکوبند که زیور پنهانشان دانسته شود. ای مؤمنان! همگی به سوی خدا بازگردید و به درگاه خدا توبه کنید، تا رستگار شوید.'),
(2823, 24, 32, 'پسران و دختران بی‌همسر خود را همسر دهید. و [نیز] غلامان وکنیزان شایسته [ازدواج] را. [و از فقر نترسید که] اگر تنگدست باشند، خداوند از فضل خود بی‌نیازشان می‌گرداند. خداوند، گشایشگر داناست.'),
(2824, 24, 33, 'و کسانی که [امکان] ازدواج نمی‌یابند، پاکدامنی و عفّت پیشه کنند، تا آن که خداوند از فضل و کرم خویش، آنان را بی‌نیاز کند. و هر کدام از غلامان و کنیزان شما که خواهان [قرارداد] بازخرید و آزادی [تدریجی] باشند، اگر در آنان خیر و شایستگی دیدید، تقاضای آنان را بپذیرید [و برای آزاد شدن آنان،] از مالی که خدا به شما داده، به آنان بدهید. و کنیزان جوان خود را که تصمیم بر پاکدامنی دارند، به خاطر رسیدن به مال ناپایدار دنیا، به فحشا وادار نکنید، و هر کس آنان را [به فحشا] وادارد، البتّه خداوند برای آن کنیزان که به اکراه بدان کار وادار گشته‌اند، بخشنده‌ی مهربان است.'),
(2825, 24, 34, 'به راستی ما به سوی شما آیاتی روشنگر، و از کسانی که پیش از شما بودند، نمونه‌هایی برجسته و تاریخی، و پندی برای پرهیزکاران فرستادیم.'),
(2826, 24, 35, 'خداوند، نور آسمان‌ها و زمین است. مثل نور او، همچون چراغدانی است که در آن چراغی [پر فروغ] باشد. آن چراغ در میان شیشه‌ای، و آن شیشه، همچون ستاره‌ای تابان و درخشان. این چراغ از روغن درخت پر برکت زیتون برافروخته است، که نه شرقی است و نه غربی [و پیوسته خورشید بر آن می‌تابد. روغنش به قدری صاف و شفّاف است] که بدون تماس آتش، نزدیک است [شعله‌ور شود و] روشنی دهد. نوری است بر فراز نور دیگر. هر کس را که خداوند بخواهد، به نور خویش هدایت می‌کند. و خداوند برای مردم مثل‌ها می‌زند، و به هر چیزی آگاه است.'),
(2827, 24, 36, '[این نور هدایت] در خانه‌هایی است که خداوند اذن داده [قدر و مَنزِلت آنها] رفعت یابد، و نام او در شود، و در آنها بامدادان و شامگاهان او را تسبیح گویند.'),
(2828, 24, 37, 'مردانی که هیچ تجارت و داد و ستدی، آنان را از یاد خدا و برپاداشتن نماز و پرداخت زکات، غافل نمی‌کند. [آنان] از روزی که در آن، دل‌ها و چشم‌ها دگرگون می‌شود، بیمناکند.'),
(2829, 24, 38, 'تا خداوند به آنان، نیکوتر از آنچه کرده‌اند، پاداش بدهد و از فضل و رحمت خویش، بر پاداششان بیافزاید. و خداوند هر کس را بخواهد، بی‌حساب روزی می‌دهد.'),
(2830, 24, 39, 'و اعمال کسانی که کافر شدند، همچون سرابی در بیابان است که تشنه، آن را آب می‌پندارد؛ [امّا] همین که به سراغ آن می‌آید، آن را چیزی نمی‌یابد، و خدا را نزد آن می‌یابد که حساب او را بی‌کم و کاست می‌دهد. و خداوند زود به حساب‌ها می‌رسد.'),
(2831, 24, 40, 'یا همچون تاریکی‌هایی است در دریایی عمیق و متلاطم،که موجی بزرگ روی آن را می‌پوشاند، و روی آن، موج بزرگ دیگری است و بالای آن موج، ابری است، ظُلُماتی تو در تو. همین که دست خود را برآوَرَد، ممکن نیست کسی دست او را ببیند [تا نجاتش دهد]. و خداوند برای هر کس نوری قرار ندهد، هیچ نوری برای او نخواهد بود.'),
(2832, 24, 41, 'آیا ندیدی که هر کس در آسمان‌ها و زمین است، و پرندگان بال گشوده، برای خدا تسبیح می‌گویند؟ و همه نماز و تسبیح خود را می‌دانند؟ و خداوند به آنچه می‌کنند، داناست.'),
(2833, 24, 42, 'و فرمانروایی آسمان‌ها و زمین، تنها از آنِ خداست. و بازگشت [همه] به سوی اوست.'),
(2834, 24, 43, 'آیا ندیدی که خداوند، ابرها را [به آرامی] می‌راند؟ سپس بین [اجزای] آن پیوند برقرار می‌کند. آن‌گاه آن را متراکم می‌سازد، پس می‌بینی که باران از میان آن بیرون می‌آید. و خداوند از آسمان، از ابرهایی کوه‌پیکر، تگرگ فرومی‌فرستد. پس آن را به هر کس بخواهد، می‌رساند و از هر کس بخواهد، بازمی‌دارد. نزدیک است درخشندگی برق آن، دیدگان را ببرد.'),
(2835, 24, 44, 'خداوند، شب و روز را جا به جا می‌کند. همانا در این [قدرت‌نمایی] برای اهل بصیرت، عبرتی است.'),
(2836, 24, 45, 'و خداوند هر جنبده‌ای را از آب آفرید. پس برخی از آنها بر شکم حرکت می‌کنند، و برخی بر دو پا راه می‌روند، و بعضی بر چهار پا. خداوند هر چه بخواهد، می‌آفریند، زیرا خدا بر هر چیزی تواناست.'),
(2837, 24, 46, 'به‌راستی که آیاتی روشنگر فروفرستادیم. و خداوند هر کس را بخواهد، به راهی راست هدایت می‌کند.'),
(2838, 24, 47, 'و می‌گویند: «ما به خدا و رسول ایمان آوردیم، و اطاعت می‌کنیم.» امّا گروهی از آنان بعد از این اقرار، پشت می‌کنند. و آنان مؤمنِ واقعی نیستند.'),
(2839, 24, 48, 'و هرگاه به سوی خدا و رسولش خوانده شوند، تا [پیامبر] میانشان داوری کند، ناگهان گروهی از آنان، روی‌گردان می‌شوند.'),
(2840, 24, 49, 'و اگر حقّ با آنان باشد، با رضایت و تسلیم به سوی پیامبر می‌آیند.'),
(2841, 24, 50, 'آیا در دل‌هایشان بیماری است؟ یا دچار شک شده‌اند؟ یا بیم آن دارند که خداوند و پیامبرش حقّی از آنان تباه کنند؟ بلکه آنان همان ستمگرانند.'),
(2842, 24, 51, '[ولی] چون مؤمنان را به سوی خدا و رسولش فراخوانند، تا [پیامبر] میانشان داوری کند، سخنشان جز این نیست که می‌گویند: «شنیدیم و اطاعت کردیم!» و اینان همان رستگارانند.'),
(2843, 24, 52, 'و کسانی که از خدا و رسولش پیروی کنند و از خدا بترسند و از او پروا کنند، پس آنان کامیابند.'),
(2844, 24, 53, 'و [منافقان] سخت‌ترینِ سوگندهایشان را به نام خدا خوردند، که اگر [برای جهاد] دستور بدهی، قطعاً از خانه خارج می‌شوند. [به آنان] بگو: «سوگند نخورید! اطاعتِ پسندیده [بهتر از گزافه‌گویی است]! البتّه خداوند به کارهایی که می‌کنید، آگاه است.»'),
(2845, 24, 54, 'بگو: «خدا را اطاعت کنید و پیامبر را فرمان برید!» پس اگر سرپیچی کنید، پیامبر مسئول اعمال خویش است، و شما مسئول اعمال خود! و اگر اطاعتش کنید، هدایت می‌یابید. و پیامبر جز رساندن آشکار [پیام وحی‌]، مسئولیتی ندارد.'),
(2846, 24, 55, 'خداوند به کسانی از شما که ایمان آورده و کارهای شایسته کرده‌اند، وعده داده است که حتماً آنان را در زمین، جانشین [و حکمران] قرار دهد، همان‌گونه که کسانی پیش از ایشان را جانشین کرد. و قطعاً دینی را که خداوند برای آنان پسندیده است، پابرجا و ریشه‌دار خواهد ساخت. و ترسشان را تبدیل به امنیت کند. تا مرا بپرستند [و] چیزی را شریک من نسازند. وکسانی که بعد از این [نعمت‌ها]، کفر ورزند، پس آنان، همان فاسقانند.'),
(2847, 24, 56, 'و نماز را به‌پا دارید و زکات را بپردازید و از پیامبر اطاعت کنید. باشد که مورد رحمت قرار گیرید.'),
(2848, 24, 57, 'هرگز گمان مبر که کافران، ما را در زمین ناتوان و درمانده می‌کنند. [آنان نمی‌توانند از مجازات الهی بگریزند!] جایگاهشان آتش است. و به راستی که سرانجام بدی است!'),
(2849, 24, 58, 'ای کسانی که ایمان آورده‌اید! بردگان شما و کودکانتان که به سنّ بلوغ نرسیده‌اند، باید در سه وقت از شما [برای ورود به اتاق شخصی] اجازه بگیرند: پیش از نماز صبح، و نیمروز، هنگامی که لباس از تن بیرون می‌کنید، و بعد از نماز عشاء. این سه وقت هنگام استراحت و خلوت شماست، در غیر این سه وقت، بر شما و بر آنان گناهی نیست، [که بی‌اجازه وارد شوند؛ زیرا آنان] گرد شما در گردشند و با یکدیگر به سر می‌برید. خداوند این‌گونه آیات را برای شما بیان می‌کند. و خداوند دانایی فرزانه است.'),
(2850, 24, 59, 'و چون کودکان شما به حد بلوغ رسیدند، باید [در تمام اوقات برای ورود به اتاق خصوصی پدر و مادر] اجازه بگیرند. مانند کسانی که پیش از آنان اجازه می‌گرفتند. خداوند این‌گونه آیات خود را برای شما بیان می‌کند. و خداوند دانایی فرزانه است.'),
(2851, 24, 60, 'و بر زنان سالخورده‌ای که امیدی به ازدواج ندارند، گناهی نیست که حجاب و روپوش خود را کنار بگذارند، به شرطی که زیورشان را آشکار و خودآرایی نکنند. و پاکدامنی [و پوشش] برای آنان بهتر است. و خداوند شنوای دانا است.'),
(2852, 24, 61, 'بر نابینا و لنگ و بیمار و بر خود شما ایرادی نیست که [بدون اجازه از این خانه‌ها، غذا] بخورید: از خانه‌های خود، یا خانه‌های پدرانتان، یا خانه‌های مادرانتان، یا خانه‌های برادرانتان، یا خانه‌های خواهرانتان، یا خانه‌های عموهایتان، یا خانه‌های عمّه‌هایتان، یا خانه‌های دایی‌هایتان، یا خانه‌های خاله‌هایتان، یا آن خانه‌هایی که کلیدهایش را در اختیار دارید، یا خانه‌های دوستانتان. بر شما گناهی نیست که دسته‌جمعی بخورید یا پراکنده [و تنها]. و چون وارد هر خانه‌ای شدید، پس بر یکدیگر سلام کنید، سلام و تحیتی از سوی خداوند، سلامی خجسته و پاکیزه! خداوند آیات را این چنین برای شما بیان می‌کند. باشد که بیندیشید.'),
(2853, 24, 62, 'مؤمنان [راستین] تنها کسانی هستند که به خدا و پیامبرش ایمان آورده‌اند و هرگاه در کاری همگانی همراه پیامبر باشند، بدون اجازه‌ی او نمی‌روند. [ای پیامبر!] کسانی که [برای رفتن‌] اجازه می‌گیرند، آنانند که به خدا و پیامبرش ایمان دارند. پس اگر برای برخی از کارهایشان از تو اجازه خواستند، به هر کدام از آنان که خواستی [و مصلحت دانستی،] اجازه بده. و برای آنان از خدا آمرزش بخواه، که خداوند بخشنده‌ی مهربان است.'),
(2854, 24, 63, '[ای مؤمنان!] آن‌گونه که یکدیگر را صدا می‌زنید، پیامبر را صدا نزنید. خدا می‌داند [چه‌] کسانی از شما با پنهان شدن پشت سر دیگران، آهسته [از نزد او] بیرون می‌روند. پس کسانی که از فرمان او سرپیچی می‌کنند، باید بترسند از این که [در دنیا] فتنه‌ای دامنشان را بگیرد، یا [در آخرت‌] به عذابی دردناک گرفتار شوند.'),
(2855, 24, 64, 'آگاه باش! آنچه در آسمان‌ها و زمین است، از آنِ خداست. بی‌شک می‌داند که بر چه کارید. و روزی را که به سوی او بازگردانده می‌شوند، [می‌داند،] پس آنان را از آنچه کرده‌اند، آگاه می‌کند. و خداوند به هر چیزی آگاه است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'مبارک است آن [خداوندی] که قرآن، وسیله‌ی شناخت حقّ از باطل را بر بنده‌اش فروفرستاد، تا برای جهانیان مایه‌ی هشدار باشد.'),
(2857, 25, 2, 'خداوندی که فرمانروایی آسمان‌ها و زمین برای اوست، و هیچ فرزندی نگزیده و در فرمانروایی شریکی ندارد. و هر چیزی را آفرید وآن را به شایستگی اندازه نهاد.'),
(2858, 25, 3, '[مشرکان] به جای او معبودانی گرفتند که هیچ چیز نمی‌آفرینند، و خود آفریده شده‌اند. مالک هیچ زیان و سودی برای خود نیستند. و تسلّطی بر مرگ و حیات و برانگیختن [پس از مرگ] ندارند.'),
(2859, 25, 4, 'و کسانی که کافر شدند، گفتند: «این [قرآن] فقط دروغی بزرگ است که او ساخته است. و گروه دیگری او را بر این کار یاری کرده‌اند.» پس به راستی مرتکب ستمی سنگین و دروغی بزرگ شده‌اند.'),
(2860, 25, 5, 'و گفتند: «[این قرآن،] افسانه‌های پیشینیان است که او نوشتن آنها را [از نویسندگان‌] درخواست کرده است، و آن [نوشته‌] هر صبح و شام بر او خوانده می‌شود [تا از بر کند، و بر ما بخواند، و بگوید: این وحی است]!»'),
(2861, 25, 6, 'بگو: «[این قرآن را] کسی فروفرستاد که رازهای پوشیده در آسمان‌ها و زمین را می‌داند. قطعاً او آمرزنده و مهربان است.»'),
(2862, 25, 7, '[کافران] گفتند: «این چه پیامبری است که [مانند ما] غذا می‌خورد، و در بازارها راه می‌رود؟ چرا فرشته‌ای به سوی او نازل نشده تا همراه او بیم‌دهنده باشد؟'),
(2863, 25, 8, 'یا [چرا از آسمان] بر او گنجی نمی‌افکنند؟ یا چرا او باغی ندارد تا از [میوه‌های] آن بخورد؟» و ستمگران [به مؤمنان] گفتند: «شما جز مردی جادو شده را پیروی نمی‌کنید.»'),
(2864, 25, 9, 'بنگر که چگونه برای تو مَثَل‌ها زدند؟ [و تو را چگونه وصف کردند؟] پس گمراه شدند؛ در نتیجه نمی‌توانند راهی [به حقیقت] بیابند.'),
(2865, 25, 10, 'مبارک است خدایی که اگر بخواهد، بهتر از آنچه را آنها توقّع دارند، برای تو قرار می‌دهد؛ باغ‌هایی که از زیر [درختان] آن نهرها جریان دارد، و قصرهایی برای تو قرار می‌دهد.'),
(2866, 25, 11, '[کافران نه تنها تو،] بلکه قیامت را انکار کرده‌اند. و ما برای منکران قیامت، آتشی فروزان و سوزان آماده کرده‌ایم.'),
(2867, 25, 12, 'چون [دوزخ] از فاصله‌ی دور آنان را ببیند، خشم و خروشی [هولناک] از آن می‌شنوند.'),
(2868, 25, 13, 'و چون به زنجیر بسته شده، در مکانی تنگ انداخته شوند، آنجاست که ناله می‌زنند.'),
(2869, 25, 14, '[به آنان گفته می‌شود:] «امروز یک بار درخواست مرگ نکنید، بلکه بسیار درخواست مرگ کنید.»'),
(2870, 25, 15, '[ای پیامبر!] به مردم بگو: «آیا این [ذلّت و عذاب] بهتر است، یا بهشت جاوِدانی که به پرهیزکاران وعده داده شده، و سزا و سرانجام آنان است؟»'),
(2871, 25, 16, 'جاودانه هر چه بخواهند، در آنجا دارند. [این پاداش] وعده‌ای است درخواست شده، بر عهده‌ی پروردگارت.'),
(2872, 25, 17, 'روزی که خداوند، مشرکان و آنچه را از غیر خدا می‌پرستیدند، [یک جا] گرد می‌آورد، پس [به معبودهای آنان] می‌فرماید: «آیا شما بندگان مرا گمراه کردید؟ یا خودشان راه را گم کردند؟»'),
(2873, 25, 18, 'آنها می‌گویند: تو منزّهی! ما را نرسد که جز تو سرپرستی بگیریم؛ ولی تو اینان و پدرانشان را [از نعمت‌ها] برخوردار کردی، تا آن که یاد [تو] را فراموش کردند، و گروهی هلاک و سر در گم شدند.»'),
(2874, 25, 19, '[خداوند می‌فرماید:] «پس [این معبودها] گفته‌های شما را تکذیب کردند. [اکنون] در چاره‌اندیشی [برای خود] و یاری رساندن [به خویش] توانی ندارید. و هر کس از شما ستم کند [و شرک ورزد]، عذاب بزرگی به او می‌چشانیم.»'),
(2875, 25, 20, 'و ما هیچ پیامبری پیش از تو نفرستادیم، مگر آن که آنان [نیز مانند مردم] غذا می‌خوردند و در بازارها راه می‌رفتند. و ما برخی از شما را وسیله‌ی آزمایش برخی دیگر قرار دادیم، [تا ببینیم] آیا صبر و تحمّل از خود نشان می‌دهید؟ و پروردگارت همواره بیناست.'),
(2876, 25, 21, 'و کسانی که به ملاقات ما امید ندارند، [و رستاخیز را نمی‌پذیرند،] گفتند: «چرا فرشتگان بر ما نازل نمی‌شوند؟ یا چرا پروردگارمان را نمی‌بینیم؟» به راستی خود را بزرگ شمردند و سرکشی بزرگی ورزیدند.'),
(2877, 25, 22, 'روزی که فرشتگان را می‌بینند، آن روز برای گناهکاران مژده‌ای نیست، و [به فرشتگان] می‌گویند: «ما را امان دهید و [عذاب را از ما] دور کنید!»'),
(2878, 25, 23, 'ما به [حساب‌] هر کاری که کرده‌اند، می‌پردازیم، و آن را غباری پراکنده می‌سازیم.'),
(2879, 25, 24, 'آن روز، اهل بهشت، جایگاهی خوش و آسایشگاهی نیکو دارند.'),
(2880, 25, 25, 'و روزی که آسمان با ابرها شکافته می‌شود، و فرشتگان با فرودی ویژه فرود آیند.'),
(2881, 25, 26, 'آن روز، فرمانرواییِ حقّ، از آن [خدای] رحمان است. و روزی است که بر کافران سخت و سنگین است.'),
(2882, 25, 27, 'و روزی که ستمکارِ [مشرک] دو دست خود را [از روی حسرت] به دندان می‌گزد، و می‌گوید: «ای کاش همراه با پیامبر راهی به سوی حق برمی‌گرفتم.'),
(2883, 25, 28, 'وای بر من! ای کاش آن [نابکار و فاسق] را به دوستی برنگزیده بودم!'),
(2884, 25, 29, '[آن دوست] بعد از آن که حقّ به سراغ من آمد، مرا از یادآوری [آن] گمراه ساخت. و شیطان، هنگام امید، انسان را رها می‌کند.»'),
(2885, 25, 30, 'و پیامبر [در روز قیامت،] می‌گوید: «پروردگارا! قوم من این قرآن را رها کردند.»'),
(2886, 25, 31, 'و ما این‌گونه برای هر پیامبری، دشمنی از گناهکاران قرار دادیم. و پروردگارت برای راهنمایی و یاری تو بس است.'),
(2887, 25, 32, 'و کسانی که کفر ورزیدند، گفتند: «چرا قرآن یک‌جا بر او نازل نشده است؟» [غافل از آن که ما] این‌گونه [قرآن را به تدریج فروفرستادیم،] تا دل تو را به آن استوار کنیم و آن را پی‌درپی و به آرامی بر تو خواندیم.'),
(2888, 25, 33, 'و هیچ مَثَلی برای تو نمی‌آورند، مگر آن که پاسخِ به حقّ و بهترین بیان را برای تو می‌آوریم.'),
(2889, 25, 34, 'کسانی که به رو درافتاده، به سوی جهنّم محشور می‌شوند، آنان بدترین جای را دارند، و گمراه‌ترینِ مردمند.'),
(2890, 25, 35, 'و همانا به موسی کتاب دادیم. و برادرش هارون را همراه او وزیر [و دستیارش] قرار دادیم.'),
(2891, 25, 36, 'پس به آن دو گفتیم: «به سوی قومی که آیات ما را تکذیب کردند، بروید.» آن‌گاه آنان را [به‌ خاطر عِنادشان] به سختی قلع و قمع کردیم.'),
(2892, 25, 37, 'و قوم نوح را که پیامبران را تکذیب کردند، غرق نمودیم و آنان را برای مردم [تاریخ] عبرت قرار دادیم. و برای ستمکاران عذابی دردناک آماده کردیم.'),
(2893, 25, 38, 'و قوم عاد و ثمود، و اصحاب رَسّ، و نسل‌های فراوانِ میان آنان را [نابود کردیم].'),
(2894, 25, 39, 'و برای هر یک [از آنها] نمونه‌هایی آوردیم، [و چون عبرت نگرفتند،] همه را به سختی نابود کردیم.'),
(2895, 25, 40, 'به‌راستی [مشرکان مکه، هنگام مسافرت به شام] بر آبادی [قوم لوط] که بارانِ عذاب بر آن باریده، گذر کرده‌اند. پس آیا آنجا را ندیده‌اند؟ چرا! ولی [عبرت نگرفته‌اند، زیرا] آنان به رستاخیز، امید و ایمانی ندارند.'),
(2896, 25, 41, 'و هرگاه تو را ببینند، جز به مسخره‌ات نگیرند: «آیا این همان کسی است که خداوند او را پیامبر قرار داده است؟'),
(2897, 25, 42, 'اگر ما بر پرستش بت‌ها ایستادگی نمی‌کردیم، نزدیک بود که [او،] ما را از خدایانمان منحرف کند.» آن‌گاه که عذاب را ببینند، خواهند دانست که چه کسی گمراه‌تر است!'),
(2898, 25, 43, 'آیا کسی که هوای نفس خود را معبود خود قرار داده است، دیده‌ای؟ آیا تو می‌توانی کارساز او باشی؟'),
(2899, 25, 44, 'آیا گمان می‌کنی که بیشتر کافران می‌شنوند، یا می‌اندیشند؟ آنان همچون چهارپایانند، بلکه گمراه‌ترند!'),
(2900, 25, 45, 'آیا به [قدرت] پروردگارت نمی‌نگری که چگونه سایه را گسترده است؟ و اگر می‌خواست، آن را ساکن [و ثابت] قرار می‌‌داد. سپس خورشید را برای آن راهنما و نمایانگرِ آن قرار دادیم.'),
(2901, 25, 46, 'سپس آن سایه را [با بلند شدن آفتاب‌،] به سوی خود جمع می‌کنیم.'),
(2902, 25, 47, 'و او کسی است که شب را برای شما، پوشش، و خواب را، آرام‌بخش گردانید. و روز را زمان برخاستن [و تلاش] شما قرار داد.'),
(2903, 25, 48, 'و او کسی است که بادها را مژده‌ای پیشاپیش [باران] رحمتش فرستاد. و از آسمان، آبی پاکیزه و پاک‌کننده فروفرستادیم،'),
(2904, 25, 49, 'تا با آن سرزمینی مرده را زنده گردانیم، و آن را به آفریده‌های خود از چهارپایان و مردمان بسیار بنوشانیم.'),
(2905, 25, 50, 'و بی‌گمان، ما آن [آیات] را در میانشان به صورت‌های گوناگون بیان کردیم، تا شاید به یاد [خدا] بیفتند. امّا بیشتر مردم جز ناسپاسی نخواستند.'),
(2906, 25, 51, 'و اگر می‌خواستیم، در هر آبادی، [پیامبر، و] هشداردهنده‌ای برمی‌انگیختیم.'),
(2907, 25, 52, 'پس، از کافران پیروی مکن، و به [وسیله] آن [قرآن]، با آنان به جهادی بزرگ بپرداز.'),
(2908, 25, 53, 'و او خدایی است که دو دریا را به هم پیوست؛ این یکی شیرین و گوارا، و آن دیگری شور و تلخ. و میان این دو حایلی قرار داد که همیشه از هم جدا باشند.'),
(2909, 25, 54, 'و او کسی است که انسان را از آب آفرید و او را [دارای پیوند] نسبی و سببی گردانید [و نسل او را از این دو راه گسترش داد]. و پروردگار تو همواره تواناست.'),
(2910, 25, 55, 'و آنان به جای خدا، چیزهایی را می‌پرستند که نه سودشان می‌دهد و نه زیانشان می‌رساند. و کافران همواره در برابر پروردگارشان، پشتیبان یکدیگرند.'),
(2911, 25, 56, 'و تو را نفرستادیم، مگر [آنکه] مژده‌رسان و بیم‌دهنده [باشی].'),
(2912, 25, 57, 'بگو: «من از شما هیچ مزدی در برابر رسالتم درخواست نمی‌کنم، مگر [این که] کسی بخواهد به سوی پروردگارش راهی در پیش گیرد. [این پاداش من است.]»'),
(2913, 25, 58, 'و بر زنده‌ای که هرگز نمی‌میرد، توکّل کن! و به ستایش او تسبیح گوی! همین بس که او به گناهان بندگانش آگاهی دقیق دارد.'),
(2914, 25, 59, 'خدایی که آسمان‌ها و زمین، و آنچه را میان آنهاست، در شش روز [و دوران] آفرید. سپس بر عرش [قدرت و تدبیر جهان هستی] قرار گرفت. اوست [خدای] رحمان، پس درباره‌ی او از خبیر آگاه بپرس!'),
(2915, 25, 60, 'و چون به آنان گفته شود: «برای [خدای] رحمان سجده کنید!» می‌گویند: رحمان چیست؟ آیا به چیزی که تو فرمانمان می‌دهی، سجده کنیم؟» [این دعوت] بر رمیدگی آنان می‌افزاید.'),
(2916, 25, 61, 'مبارک است کسی که در آسمان بُرج‌هایی قرار داد [که منزلگاه‌های ستارگان است،] و در آن، خورشید و ماه تابانی نهاد.'),
(2917, 25, 62, 'و اوست که شب و روز را برای هر کس که بخواهد عبرت گیرد، یا بخواهد سپاسگزاری نماید، جانشین یکدیگر قرار داد.'),
(2918, 25, 63, 'و بندگان [خدای] رحمان کسانی هستند که بر زمین، بی‌تکبّر راه می‌روند. و هرگاه نادانان، آنان را طرف خِطاب قرار دهند، [و سخنان نابخردانه گویند،] با نرمی پاسخ دهند.'),
(2919, 25, 64, 'و آنان برای پروردگارشان، در حال سجده و قیام، شب‌زنده‌داری می‌کنند.'),
(2920, 25, 65, 'و آنان می‌گویند: «پروردگارا! عذاب جهنّم را از ما بازگردان که عذاب آن دامن‌گیر است.'),
(2921, 25, 66, 'به‌درستی که دوزخ، جایگاه و منزلگاه بدی است!»'),
(2922, 25, 67, 'و آنان که هرگاه انفاق کنند، نه از حد بگذرند، و نه تنگ گیرند و میان این دو، اعتدال دارند.'),
(2923, 25, 68, 'و [بندگان خدا] کسانی هستند که با خداوند، معبود دیگری را نمی‌خوانند، و انسانی را که خداوند [خونش را] حرام کرده است، جز به حقّ نمی‌کشند، و زنا نمی‌کنند، و هر کس این [گناهان] را مرتکب شود، عقوبت گناهش را خواهد دید.'),
(2924, 25, 69, 'در روز قیامت عذاب او دوچندان می‌شود. و همیشه به خواری در آن خواهد ماند.'),
(2925, 25, 70, 'مگر کسانی که توبه کنند و ایمان آورند و کار شایسته کنند. پس اینانند که خداوند بدی‌هایشان را به نیکی تبدیل می‌کند. و خداوند همواره آمرزنده و مهربان است.'),
(2926, 25, 71, 'و هر کس توبه کند و کار شایسته کند، در حقیقت به سوی خدا بازگشتی پسندیده دارد.'),
(2927, 25, 72, 'و [بندگان خدا] کسانی هستند که به دروغ گواهی نمی‌دهند و در مجلس [غِنا، و گفتار و کردار] باطل حاضر نمی‌شوند. و چون بر [گفتار] لغوی عبور کنند، کریمانه بگذرند.'),
(2928, 25, 73, 'و آنان کسانی هستند که چون آیات پروردگارشان به آنان گوشزد شود، کر و کور، [چشم و گوش بسته،] به سجده نمی‌افتند. [بلکه عمل آنان آگاهانه است].'),
(2929, 25, 74, 'و کسانی که می‌گویند: «پروردگارا! به ما از همسران و فرزندانمان آن ده که مایه روشنی چشمان باشد! و ما را پیشوای پرهیزکاران قرار ده!»'),
(2930, 25, 75, 'به آنان به خاطر صبرشان، غرفه [‌های بهشتی] پاداش داده می‌شود و در آنجا با تحیت و سلامی گرم روبرو خواهند شد.'),
(2931, 25, 76, 'در آنجا، جاوِدانه هستند. چه نیکو جایگاه و منزلگاهی است!'),
(2932, 25, 77, 'بگو: «اگر دعای شما نباشد، پروردگار من برای شما ارزشی قائل نیست. شما حقّ را تکذیب کرده‌اید، و به زودی کیفر تکذیبتان، دامن شما را خواهد گرفت.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سین، میم.'),
(2934, 26, 2, 'این است آیات کتاب روشنگر.'),
(2935, 26, 3, '[ای پیامبر!] گویی می‌خواهی به خاطر آن که مشرکان ایمان نمی‌آورند، [از شدت اندوه] خود را هلاک کنی.'),
(2936, 26, 4, 'اگر بخواهیم، معجزه‌ای از آسمان بر آنان فرود آوریم، تا در برابر آن، گردن‌هایشان خاضع گردد [و با اکراه ایمان آورند، امّا سنّت الهی چنین نیست].'),
(2937, 26, 5, 'هیچ تذکر تازه‌ای از [سوی] خداوند مهربان برایشان نیامد، مگر آن که از آن روی‌گردان بودند.'),
(2938, 26, 6, 'پس آنان تکذیب کردند، و به زودی اخبار [کیفر] آنچه به مسخره می‌گرفتند، به آنان خواهد رسید.'),
(2939, 26, 7, 'آیا به زمین نگاه نکردند که از هر گیاهی نیکو در آن بسیار رویانده‌ایم؟'),
(2940, 26, 8, 'قطعاً در این [آفرینش نیکو]، عبرت و نشانه‌ای است؛ ولی بیشترشان مؤمن نبودند.'),
(2941, 26, 9, 'و همانا پروردگارت شکست‌ناپذیر و مهربان است.'),
(2942, 26, 10, 'و [یاد کن] زمانی که پروردگارت موسی را ندا داد که: «به سوی قوم ستمکار برو!'),
(2943, 26, 11, 'قوم فرعون؛ آیا پروا نمی‌کنند؟»'),
(2944, 26, 12, '[موسی] گفت: «پروردگارا! می‌ترسم دروغگویم بشمارند،'),
(2945, 26, 13, 'و سینه‌ام [از این وظیفه سنگین] تنگی می‌کند، و زبانم روان و گویا نیست. پس هارون را [نیز] رسالت بده [تا مرا یاری کند].'),
(2946, 26, 14, 'و [به نظر] آنان بر [گردن] من جرمی است. می‌ترسم مرا بکشند.»'),
(2947, 26, 15, 'خداوند فرمود: چنین نیست! پس [تو و هارون] همراه با معجزات ما بروید، که ما با شما و شنونده [گفتگوهایتان] هستیم.'),
(2948, 26, 16, 'به سراغ فرعون بروید و بگویید: «ما فرستاده‌ی پروردگار جهانیانیم.'),
(2949, 26, 17, 'بنی‌اسرائیل را [آزاد کن و] همراه ما بفرست.»'),
(2950, 26, 18, '[فرعون] گفت: «[ای موسی!] آیا ما تو را در کودکی پرورش ندادیم؟ و سالیانی از عمرت را در نزدمان ماندی؟'),
(2951, 26, 19, 'و با این حال،کارت را کردی، [و یکی از افراد ما را کشتی،] و تو [در برابر زحمات ما] از ناسپاسانی.»'),
(2952, 26, 20, '[موسی] گفت: «آن [کار] را زمانی کردم که بی‌خبر بودم! [و نمی‌دانستم با مشت من در دفاع از مظلوم، آن مرد قِبطی کشته می‌شود.]'),
(2953, 26, 21, 'و چون از شما ترسیدم، از نزدتان گریختم. سپس پروردگارم به من حکمت بخشید، و مرا از پیامبران قرار داد.'),
(2954, 26, 22, 'و [آیا] این که بنی‌اسرائیل را بنده‌ی خود ساخته‌ای، نعمتی است که منّتش را بر من می‌نهی؟»'),
(2955, 26, 23, '[فرعون] گفت: «پروردگار جهانیان چیست؟»'),
(2956, 26, 24, '[موسی] گفت: «پروردگار آسمان‌ها و زمین، و آنچه میان آنهاست، اگر باور دارید.»'),
(2957, 26, 25, 'فرعون به اطرافیان خود گفت: «آیا نمی‌شنوید [چه می‌گوید]؟»'),
(2958, 26, 26, '[بار دیگر موسی] گفت: «او پروردگار شما، و پروردگار نیاکان شماست.»'),
(2959, 26, 27, '[فرعون] گفت: «قطعاً پیامبرتان که به سوی شما فرستاده شده، سخت دیوانه است!»'),
(2960, 26, 28, '[موسی] گفت: «[او] پروردگار مشرق و مغرب است، و آنچه میان آن دو است. اگر بیندیشید.»'),
(2961, 26, 29, '[فرعون] گفت: «[ای موسی!] اگر معبودی جز من برگزینی، قطعاً تو را از زندانیان قرار خواهم داد.»'),
(2962, 26, 30, '[موسی] گفت: «[حتّی] اگر نشانه‌ی آشکاری برایت بیاورم؟»'),
(2963, 26, 31, '[فرعون] گفت: «اگر راست می‌گویی، آن نشانه را بیاور!»'),
(2964, 26, 32, 'پس [موسی] عصای خود را افکند، ناگهان، اژدهایی آشکار شد.'),
(2965, 26, 33, 'و دست خود را [از گریبانش] بیرون آورد، ناگهان آن دست، برای تماشاگران سپید و درخشان نمایان شد.'),
(2966, 26, 34, '[فرعون] به اشراف اطرافش گفت: «به‌راستی این مرد، جادوگری کاردان است.'),
(2967, 26, 35, 'او می‌خواهد با جادویش، شما را از سرزمینتان آواره کند. پس چه نظر می‌دهید؟»'),
(2968, 26, 36, 'گفتند: «کار موسی و برادرش را به تأخیر انداز. و گروهی گردآورنده را به شهرها بفرست.'),
(2969, 26, 37, 'تا هر جادوگر ماهری را نزد تو بیاورند.»'),
(2970, 26, 38, 'پس جادوگران در موعدِ روزی معلوم، گردآوری شدند.'),
(2971, 26, 39, 'و به مردم گفته شد: «آیا شما نیز جمع می‌شوید؟'),
(2972, 26, 40, 'به این امید که اگر جادوگران پیروز شدند، از [آیینِ] آنان پیروی کنیم.»'),
(2973, 26, 41, 'پس همین که جادوگران [نزد فرعون] آمدند، به او گفتند: «آیا اگر ما پیروز شویم، پاداشی داریم؟»'),
(2974, 26, 42, '[فرعون] گفت: «آری! در این صورت [علاوه بر پاداش]، شما از مقرّبان [دربار] خواهید بود.»'),
(2975, 26, 43, '[روز موعود] موسی به آنان گفت: «بیفکنید آنچه را می‌خواهید بیفکنید!»'),
(2976, 26, 44, 'پس [جادوگران] ریسمان‌ها و چوب‌دست‌های خود را افکندند و گفتند: «به عزّت فرعون سوگند، که قطعاً ما پیروزیم.»'),
(2977, 26, 45, 'سپس موسی عصایش را افکند. ناگهان هر چه را به نیرنگ ساخته بودند، بلعید.'),
(2978, 26, 46, 'آن‌گاه جادوگران بی‌اختیار به سجده درافتادند.'),
(2979, 26, 47, 'گفتند: «ما به پروردگار جهانیان ایمان آوردیم.'),
(2980, 26, 48, 'پروردگار موسی و هارون.»'),
(2981, 26, 49, 'فرعون گفت: «آیا پیش از آنکه به شما اجازه دهم، به او ایمان آوردید؟! بی‌گمان او سردسته‌ی شماست که به شما جادو آموخته است. پس به زودی کیفر خود را خواهید دانست. حتماً دست‌ها و پاهای شما را یکی از راست و یکی از چپ می‌برم، و همگیِ شما را به دار خواهم آویخت.»'),
(2982, 26, 50, 'ساحران گفتند: «باکی نیست! ما به سوی پروردگار خود بازمی‌گردیم.'),
(2983, 26, 51, 'ما امید داریم که پروردگارمان خطاهای ما را ببخشد، چرا که ما [در این قوم] نخستینِ مؤمنانیم.»'),
(2984, 26, 52, 'و به موسی وحی کردیم که: «بندگان مرا شبانه کوچ ده، زیرا شما تعقیب خواهید شد.»'),
(2985, 26, 53, 'پس فرعون [آگاه شد و] گردآورندگان [سپاه] را به شهرها فرستاد.'),
(2986, 26, 54, '«اینها گروهی اندکند.'),
(2987, 26, 55, 'که ما را به خشم آورده‌اند.'),
(2988, 26, 56, 'ما همگی آماده‌ی دفاع هستیم.»'),
(2989, 26, 57, 'پس آنان را [به گمان تعقیب بنی‌اسرائیل]، از باغ‌ها و چشمه‌ها بیرون کردیم،'),
(2990, 26, 58, 'و [نیز از] گنج‌ها، و جایگاه نیکو.'),
(2991, 26, 59, 'چنین بود که بنی‌اسرائیل را وارث آنها قرار دهیم.'),
(2992, 26, 60, 'پس هنگام طلوع خورشید، [سپاهیان فرعون] به تعقیب آنان برخاستند.'),
(2993, 26, 61, 'پس زمانی كه دو گروه، یکدیگر را دیدند، یاران موسی گفتند: «آنان به ما دست خواهند یافت.»'),
(2994, 26, 62, '[موسی] گفت: «چنین نیست! قطعاً پروردگارم با من است و مرا هدایت خواهد كرد.»'),
(2995, 26, 63, 'پس به موسی وحی كردیم كه: «عصایت را به دریا بزن!» پس [دریا] شكافت، و هر پاره‌ای چون كوهی بزرگ شد. [و جاده‌ای خشک برای آنها پدیدار گشت.]'),
(2996, 26, 64, 'آن‌گاه گروه دیگر را [که لشكر فرعون بود،] به آنجا نزدیک كردیم.'),
(2997, 26, 65, 'و موسی و هر كه را با او بود، همگی را [از دریا عبور دادیم و] رهایی بخشیدیم.'),
(2998, 26, 66, 'سپس گروه دیگر را غرق ساختیم.'),
(2999, 26, 67, 'البتّه در این ماجرا، نشانه‌ی روشنی است. و بیشترشان مؤمن نبودند.'),
(3000, 26, 68, 'و همانا پروردگارت شكست‌ناپذیر و مهربان است.'),
(3001, 26, 69, '[ای پیامبر!] سرگذشت ابراهیم را بر مردم بخوان!'),
(3002, 26, 70, 'آن‌گاه كه به پدرش و قومش گفت: «شما چه می‌پرستید؟»'),
(3003, 26, 71, 'گفتند: «بت‌هایی را می‌پرستیم و در كنارشان به پرستش ماندگاریم.»'),
(3004, 26, 72, '[ابراهیم] گفت: «آیا هرگاه آنها را می‌خوانید، سخن شما رامی‌شنوند؟'),
(3005, 26, 73, 'یا به شما سود و زیانی می‌رسانند؟»'),
(3006, 26, 74, 'گفتند: «[نه!] بلكه پدرانمان را یافته‌ایم كه این‌گونه [پرستش] می‌كردند!»'),
(3007, 26, 75, '[ابراهیم] گفت: «آیا نظر كرده‌اید در آنچه می‌پرستید؟'),
(3008, 26, 76, 'شما و پدران پیشین شما؟'),
(3009, 26, 77, 'البتّه این بت‌ها دشمن منند، [چون مرا گرفتار عذاب خواهند كرد،] مگر پروردگار جهانیان [كه پرستیدنش مایه‌ی سعادت است].'),
(3010, 26, 78, 'همان كه مرا آفرید، پس او راهنماییم می‌كند.'),
(3011, 26, 79, 'او كه به من می‌خورانَد و می‌نوشانَد.'),
(3012, 26, 80, 'و هرگاه بیمار شوم، او مرا شفا می‌بخشد.'),
(3013, 26, 81, 'و او كسی است كه مرا می‌میراند، سپس زنده‌ام می‌كند.'),
(3014, 26, 82, 'و او كسی است كه امید دارم روز جزا، خطاهای مرا ببخشد.'),
(3015, 26, 83, 'پروردگارا! به من حكمت مرحمت فرما! و مرا به صالحان ملحق كن!'),
(3016, 26, 84, 'و برای من در میان آیندگان، نام نیکو، بر جای بگذار!'),
(3017, 26, 85, 'و مرا از وارثان بهشت پر نعمت قرار ده!'),
(3018, 26, 86, 'و از پدرم، درگذر، که او از گمراهان است.'),
(3019, 26, 87, 'و مرا روزی که همه برانگیخته می‌شوند، خوار مگردان!'),
(3020, 26, 88, 'روزی که مال و فرزندان سودی ندارند،'),
(3021, 26, 89, 'مگر کسی که با روح و قلب پاک، به سوی خدا آید.»'),
(3022, 26, 90, 'و [آن روز،] بهشت برای پرهیزکاران نزدیک گردد.'),
(3023, 26, 91, 'و آتشِ برافروخته برای گمراهان آشکار شود.'),
(3024, 26, 92, 'و به آنان گفته شود: «کجاست آنچه می‌پرستیدید،'),
(3025, 26, 93, 'به جای خدا؟ آیا یاریتان می‌کنند؟ یا می‌توانند از خود دفاع کنند؟»'),
(3026, 26, 94, 'پس همه‌ی معبودان و عابدان گمراه، به دوزخ سرنگون شوند.'),
(3027, 26, 95, 'و لشکریان ابلیس نیز همگی [به دوزخ افکنده شوند].'),
(3028, 26, 96, 'و آنان در دوزخ در حالی که با هم مشاجره می‌کنند، می‌گویند:'),
(3029, 26, 97, '«به خدا سوگند که ما در گمراهی آشکاری بودیم.'),
(3030, 26, 98, 'چون شما [بت‌ها و معبودها] را با پروردگار جهانیان برابر می‌دانستیم.'),
(3031, 26, 99, 'و جز تبهکاران، ما را گمراه نکردند.'),
(3032, 26, 100, 'در نتیجه [امروز] شَفاعت‌گرانی نداریم،'),
(3033, 26, 101, 'و حتّی یک دوست صمیمی نداریم.'),
(3034, 26, 102, 'پس اگر برای ما بازگشتی [به دنیا] بود، از مؤمنان می‌شدیم.»'),
(3035, 26, 103, 'به‌راستی که در این [سرگذشت] درس عبرت و نشانه‌ای است، و بیشترشان مؤمن نبودند.'),
(3036, 26, 104, 'و همانا پروردگارت شکست‌ناپذیر و مهربان است.'),
(3037, 26, 105, 'قوم نوح پیامبران را تکذیب کردند.'),
(3038, 26, 106, 'زمانی که برادرشان نوح به آنان گفت: «آیا پروا نمی‌کنید؟'),
(3039, 26, 107, 'من برای شما پیامبری امین هستم.'),
(3040, 26, 108, 'پس، از خداوند پروا، و از من اطاعت کنید.'),
(3041, 26, 109, 'و من برای این [رسالت]، هیچ مزدی از شما درخواست نمی‌کنم، پاداش من تنها بر پروردگار جهانیان است!'),
(3042, 26, 110, 'پس، از خداوند پروا و از من اطاعت کنید.»'),
(3043, 26, 111, 'آنان گفتند: «آیا ما به تو ایمان بیاوریم، در حالی که فرومایگان از تو پیروی کرده‌اند؟»'),
(3044, 26, 112, '[نوح] گفت: من به عملكَرد گذشته‌ی آنان، آگاهی ندارم.'),
(3045, 26, 113, 'حساب آنان تنها با پروردگار من است، اگر شما بفهمید!'),
(3046, 26, 114, 'و من مؤمنان را از خود نمی‌رانم.'),
(3047, 26, 115, 'من تنها هشداردهنده‌ای روشنگرم!»'),
(3048, 26, 116, 'گفتند: «ای نوح! اگر دست برنداری، قطعاً سنگسار خواهی شد.»'),
(3049, 26, 117, '[نوح] گفت: «پروردگارا! همانا قوم من، مرا تكذیب كردند.'),
(3050, 26, 118, 'پس میان من و آنان راه روشنی بگشا! و مرا و هر كس را كه با من است، [از شرّ كافران] نجات بده!»'),
(3051, 26, 119, 'پس ما نوح و هر كس را كه در آن كشتیِ گران‌بار، با او بود، نجات دادیم.'),
(3052, 26, 120, 'سپس باقی‌ماندگان را غرق كردیم.'),
(3053, 26, 121, 'البتّه در این [ماجرا]، نشانه‌ی بزرگی است؛ و [قوم نوح] بیشترشان مؤمن نبودند.'),
(3054, 26, 122, 'و همانا پروردگارت شكست‌ناپذیر و مهربان است.'),
(3055, 26, 123, 'قوم عاد [نیز] پیامبران را تكذیب كردند.'),
(3056, 26, 124, 'آن‌گاه كه برادرشان هود، به آنان گفت: «آیا پروا نمی‌كنید؟'),
(3057, 26, 125, 'من برای شما پیامبری امین هستم.'),
(3058, 26, 126, 'پس، از خداوند پروا و از من اطاعت كنید.'),
(3059, 26, 127, 'و من برای این [رسالت]، هیچ مزدی از شما درخواست نمی‌كنم، پاداش من تنها بر پروردگار جهانیان است!'),
(3060, 26, 128, 'آیا از سر هوا و هوس، بر هر بلندی، برجی برافراشته می‌سازید؟'),
(3061, 26, 129, 'و كاخ‌های استوار می‌گیرید، به امید آنكه جاوِدانه بمانید؟'),
(3062, 26, 130, 'و هرگاه كسی را كیفر می‌دهید، همچون زورگویان كیفر می‌دهید؟'),
(3063, 26, 131, 'پس، از خداوند پروا و از من اطاعت كنید.'),
(3064, 26, 132, 'و از كسی كه شما را به وسیله آنچه می‌دانید، یاوری كرد، پروا كنید.'),
(3065, 26, 133, '[خداوند] با چارپایان و فرزندان، به شما یاری داده است،'),
(3066, 26, 134, 'و [با] باغ‌ها و چشمه‌ساران.'),
(3067, 26, 135, 'من از عذاب روزی بزرگ بر شما بیمناكم.»'),
(3068, 26, 136, '[قوم عاد] گفتند: «برای ما یكسان است كه ما را اندرز دهی، یا اندرز ندهی.'),
(3069, 26, 137, 'این، جز شیوه‌ی پیشینیان نیست.'),
(3070, 26, 138, 'و ما عذاب نخواهیم شد.»'),
(3071, 26, 139, 'پس هود را تکذیب کردند. آن‌گاه ما آنان را نابود کردیم. البتّه در این [داستان]، بی‌شک نشانه‌ی بزرگی است، و [قوم عاد،] بیشترشان مؤمن نبودند.'),
(3072, 26, 140, 'و همانا پروردگارت شکست‌ناپذیر و مهربان است.'),
(3073, 26, 141, 'قوم ثمود [نیز] پیامبران را تکذیب کردند.'),
(3074, 26, 142, 'آن‌گاه که برادرشان صالح به آنان گفت: «آیا پروا نمی‌کنید؟'),
(3075, 26, 143, 'من برای شما پیامبری امین هستم.'),
(3076, 26, 144, 'پس، از خداوند پروا و از من اطاعت کنید.'),
(3077, 26, 145, 'و من برای این [رسالت]، هیچ مزدی از شما درخواست نمی‌کنم، پاداش من تنها بر پروردگار جهانیان است!'),
(3078, 26, 146, 'آیا [می‌پندارید] در آنچه در این دنیا دارید، با ایمِنی، رها خواهید شد؟'),
(3079, 26, 147, 'در باغ‌ها و چشمه‌ها؟'),
(3080, 26, 148, 'و کشت‌زارها و نخل‌هایی که شکوفه‌اش لطیف و [شاخه‌هایش] بر هم نشسته است.'),
(3081, 26, 149, 'و شما از کوه‌ها، خانه‌هایی برای خوشگذرانی می‌تراشید.'),
(3082, 26, 150, 'پس، از خداوند پروا و از من اطاعت کنید.'),
(3083, 26, 151, 'و از فرمان اسرافکاران پیروی نکنید،'),
(3084, 26, 152, 'آنان که در زمین فساد می‌کنند و اهل اصلاح نیستند.»'),
(3085, 26, 153, '[قوم ثمود، به صالح] گفتند: «بی‌تردید تو جادو شده‌ای!'),
(3086, 26, 154, 'تو نیز بشری همچون مایی. اگر راست می‌گویی، نشانه [و معجزه]ای بیاور!»'),
(3087, 26, 155, '[پس ماده شتری از دل کوه بیرون آمد. صالح] گفت: «این ماده شتری است! سهمی از آب [چشمه‌]، برای اوست. و برای شما روز دیگری معین شده است.'),
(3088, 26, 156, 'و به آن آسیبی نرسانید که عذاب روز سهمگین، شما را فرامی‌گیرد.»'),
(3089, 26, 157, 'پس ناقه را پِی کردند [و آن را کشتند]، سپس [از کرده‌ی خود] پشیمان شدند.'),
(3090, 26, 158, 'پس آنان را عذاب فراگرفت. همانا در این [ماجرا] نشانه و درس عبرتی است. و [قوم ثمود،] بیشترشان مؤمن نبودند.'),
(3091, 26, 159, 'و همانا پروردگارت شکست‌ناپذیر و مهربان است.'),
(3092, 26, 160, 'قوم لوط [نیز] پیامبران را تکذیب کردند.'),
(3093, 26, 161, 'هنگامی که برادرشان لوط، به آنان گفت: «آیا پروا نمی‌کنید؟'),
(3094, 26, 162, 'من برای شما پیامبری امین هستم.'),
(3095, 26, 163, 'پس، از خداوند پروا و از من اطاعت کنید.'),
(3096, 26, 164, 'و من برای این [رسالت]، هیچ مزدی از شما درخواست نمی‌کنم، پاداش من تنها بر پروردگار جهانیان است!'),
(3097, 26, 165, 'آیا از میان مردم جهان، شما به سراغ مردها می‌روید [و با آنان آمیزش می‌کنید]؟'),
(3098, 26, 166, 'و همسرانتان را که پروردگارتان برای شما آفریده، رها می‌کنید؟ بلکه شما مردمی تجاوزکارید.»'),
(3099, 26, 167, '[قوم بدکار] گفتند: «ای لوط! اگر [از حرف‌هایت] دست برنداری، قطعاً بیرون رانده می‌شوی.»'),
(3100, 26, 168, '[لوط] گفت: «من دشمن سرسخت کردار شما هستم.'),
(3101, 26, 169, 'پروردگارا! من و کسان مرا از [شرّ] آنچه می‌کنند، رهایی بخش!»'),
(3102, 26, 170, 'پس او و تمام خاندانش را نجات دادیم،'),
(3103, 26, 171, 'مگر پیرزنی که [زن لوط بود و] در میان بازماندگان، [اهل عذاب] بود.'),
(3104, 26, 172, 'سپس، دیگران را ریشه‌کن کردیم.'),
(3105, 26, 173, 'و بر سر آنان بارانی [از سنگ] فروفرستادیم. پس چه بد است باران هشدار داده‌شدگان!'),
(3106, 26, 174, 'البتّه در این [ماجرا] نشانه‌ای [از قدرت الهی] است. و [قوم لوط،] بیشترشان مؤمن نبودند.'),
(3107, 26, 175, 'و همانا پروردگارت شکست‌ناپذیر و مهربان است.'),
(3108, 26, 176, 'مردم اَیکه [نیز] پیامبران را تکذیب کردند.'),
(3109, 26, 177, 'زمانی که شعیب به آنان گفت: «آیا پروا نمی‌کنید؟'),
(3110, 26, 178, 'من برای شما پیامبری امین هستم.'),
(3111, 26, 179, 'پس، از خداوند پروا و از من اطاعت کنید.'),
(3112, 26, 180, 'و من برای این [رسالت]، هیچ مزدی از شما درخواست نمی‌کنم، پاداش من تنها بر پروردگار جهانیان است!'),
(3113, 26, 181, 'حقّ پیمانه را ادا کنید. و از کم‌فروشان نباشید.'),
(3114, 26, 182, 'و با ترازویِ درست بسنجید.'),
(3115, 26, 183, 'و از اجناس مردم، نکاهید. و در زمین به فساد و تبهکاری نکوشید.'),
(3116, 26, 184, 'و از خدایی که شما و اقوام پیشین را آفریده، پروا کنید.»'),
(3117, 26, 185, '[مردم] گفتند: «بی‌تردید تو جادو شده‌ای!'),
(3118, 26, 186, 'تو نیز بشری همچون مایی. و ما تو را از دروغگویان می‌پنداریم.'),
(3119, 26, 187, 'پس اگر تو از راستگویانی، پاره‌هایی از آسمان را بر سر ما بیفکن!»'),
(3120, 26, 188, '[شعیب] گفت: «پروردگار من به آنچه می‌کنید، آگاه‌تر است.»'),
(3121, 26, 189, 'پس شعیب را تکذیب کردند. و عذابِ روزِ سایبان آنان را فراگرفت. [سایبانی از ابر سیاه و صاعقه‌خیز که بر سر آنها آتش می‌بارید،] آن، عذابِ روز بزرگ و هولناکی بود.'),
(3122, 26, 190, 'بی‌شک در این [ماجرا] نشانه‌ای [از قدرت الهی] است، و [قوم شعیب] بیشترشان مؤمن نبودند.'),
(3123, 26, 191, 'و همانا پروردگارت شکست‌ناپذیر و مهربان است.'),
(3124, 26, 192, 'و البتّه این [قرآن] فرستاده پروردگار جهانیان است.'),
(3125, 26, 193, '[که جبرئیل] فرشته‌ی امین الهی آن را فرود آورده است،'),
(3126, 26, 194, 'بر دل تو، تا از هشداردهندگان باشی.'),
(3127, 26, 195, '[این قرآن] به زبان عربی روشن [و گویا نازل شده،]'),
(3128, 26, 196, 'و [خبر] آن در کتاب‌های [آسمانی] پیشین آمده است.'),
(3129, 26, 197, 'آیا این که دانشمندان بنی‌اسرائیل از آن اطلاع دارند، برای مشرکان نشانه‌ای نیست؟!'),
(3130, 26, 198, 'اگر ما قرآن را بر بعضی غیرعرب‌ها نازل می‌کردیم.'),
(3131, 26, 199, 'پس او، آن را برایشان می‌خواند، به آن ایمان نمی‌آوردند.'),
(3132, 26, 200, 'ما این‌گونه قرآن را [با بیانی رسا و روشن] در دل‌های گناهکاران عبور دادیم،'),
(3133, 26, 201, '[ولی آنان] ایمان نمی‌آورند، مگر آنکه عذاب دردناک را مشاهده کنند.'),
(3134, 26, 202, 'که ناگهان و در حالی که آگاه نباشند، به سراغشان آید.'),
(3135, 26, 203, 'پس می‌گویند: «آیا به ما مهلتی داده خواهد شد؟»'),
(3136, 26, 204, 'آیا عذاب ما را به شتاب می‌جویند؟'),
(3137, 26, 205, 'آیا دیدی که اگر سال‌ها هم آنان را [از نعمت‌های خود] برخوردار کنیم،'),
(3138, 26, 206, 'آن‌گاه عذاب موعود، به آنها خواهد رسید.'),
(3139, 26, 207, 'آنچه از آن برخوردار بودند، در دفع عذاب به کارشان نیاید.'),
(3140, 26, 208, 'و ما [مردم] هیچ دیاری را نابود نکردیم، مگر آن که بیم‌دهندگانی داشتند.'),
(3141, 26, 209, 'تا مایه‌ی پند و عبرت باشد، و ما هرگز ستمکار نبوده‌ایم [که بدون هشدار مجازات کنیم].'),
(3142, 26, 210, 'و قرآن را شیطان‌ها و جنیان فرونفرستاده‌اند.'),
(3143, 26, 211, 'و [نزول قرآن‌] سزاوار آنان نیست. و توان این کار را نیز ندارند.'),
(3144, 26, 212, 'بی‌شک آنها از شنیدن [اخبار آسمانی] برکنارند.'),
(3145, 26, 213, 'پس [ای پیامبر!] با خدا معبود دیگری را نخوان، که از عذاب‌شدگان خواهی بود.'),
(3146, 26, 214, 'و خویشان نزدیکت را هشدار ده!'),
(3147, 26, 215, 'و برای مؤمنانی که از تو پیروی کرده‌اند، بال [فروتنی] خود را بگستر!'),
(3148, 26, 216, 'پس اگر [خویشان نزدیکت،] سرپیچی کردند، بگو: «من قطعاً از آنچه انجام می‌دهید، بیزارم!»'),
(3149, 26, 217, 'و بر [خدای] شکست‌ناپذیر و مهربان توکل کن.'),
(3150, 26, 218, 'خدایی که چون [به نماز] برمی‌خیزی، تو را می‌بیند.'),
(3151, 26, 219, 'و گردش و حرکت تو را در میان سجده‌کنندگان [نیز می‌بیند].'),
(3152, 26, 220, 'زیرا که اوست شنوا و دانا.'),
(3153, 26, 221, 'آیا به شما خبر دهم که شیطان‌ها بر چه کسی فرود می‌آیند؟'),
(3154, 26, 222, 'بر هر دروغ‌ساز گنه‌پیشه‌ای فرود می‌آیند.'),
(3155, 26, 223, '[این افراد،] به سخنان شیطان‌ها گوش فرامی‌دهند، و بیشترشان دروغگویند.'),
(3156, 26, 224, '[پیامبر خدا شاعر نیست؛ زیرا] از شاعران، گمراهان پیروی می‌کنند.'),
(3157, 26, 225, 'آیا ندیدی که آنان در هر وادی [خیالی‌] سرگشته‌اند؟'),
(3158, 26, 226, 'و مطالبی می‌گویند که [خود] به آن عمل نمی‌کنند؟'),
(3159, 26, 227, 'مگر آن شاعرانی که ایمان آوردند و کارهای شایسته کرده‌اند و خدا را بسیار یاد کردند، و پس از آن که مورد ستم قرار گرفتند، [با شعرهای توحیدی،] به دفاع از خویش [و مؤمنان] برخاستند. و کسانی که ستم کرده‌اند، به زودی خواهند دانست که به کدام بازگشت‌گاه بازخواهند گشت!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سین. آن است آیات قرآن و کتاب روشنگر.'),
(3161, 27, 2, 'که [وسیله‌ی] هدایت و بشارت برای اهل ایمان است.'),
(3162, 27, 3, 'کسانی که نماز برپامی‌دارند و زکات می‌پردازند، و تنها ایشانند که به آخرت یقین دارند.'),
(3163, 27, 4, 'کسانی که به آخرت ایمان ندارند، کارهای [زشت]شان را برای آنان زیبا جلوه می‌دهیم تا [همچنان] سرگشته باشند.'),
(3164, 27, 5, 'آنان کسانی هستند که عذاب بد [و دردناک] خواهد داشت. و آنان در قیامت زیانکارترینِ افرادند.'),
(3165, 27, 6, 'به یقین که تو قرآن را از سوی حکیمی دانا دریافت می‌کنی.'),
(3166, 27, 7, '[یاد کن] هنگامی که موسی به خانواده‌ی خود گفت: «من آتشی دیدم، به زودی برای شما خبری از آن خواهم آورد، یا شعله‌ی آتشی برای شما می‌آورم، باشد که خود را گرم کنید.»'),
(3167, 27, 8, 'پس همین که نزد آن آمد، ندا داده شد که: «آن که در آتش است و آن که اطراف آن است، پر برکت باد. و منزّه است خداوندی که پروردگار جهانیان است.'),
(3168, 27, 9, 'ای موسی! این منم! خدای عزیز حکیم!'),
(3169, 27, 10, 'و عصایت را بیفکن!» پس همین که آن را دید چنان جست‌وخیز می‌کند، که گویا ماری کوچک است، پشت‌کنان فرار کرد و به عقب برنگشت. [گفتیم:] «ای موسی! نترس! که فرستادگان در آستان من نمی‌ترسند،'),
(3170, 27, 11, 'مگر کسی که ستم کند؛ سپس بعد از بدی، کار خوبی را جایگزین نماید. همانا که من بخشنده‌ی مهربانم.'),
(3171, 27, 12, 'و دستت را در گریبانت کن؛ تا بدون هیچ عیبی، سپید و درخشان بیرون آید، [این معجزه] در زمره‌ی معجزات نُه‌گانه است [که تو با آنها] به سوی فرعون و قومش [فرستاده می‌شوی] که آنان قومی فاسق‌اند.»'),
(3172, 27, 13, 'پس چون آیات و معجزات روشنگر ما به سویشان آمد، گفتند: «این جادویی است آشکار!»'),
(3173, 27, 14, 'و با آن که در دل به آن یقین داشتند، از روی ستم و برتری‌جویی، انکارش کردند. پس بنگر که فرجام تبهکاران چگونه است؟'),
(3174, 27, 15, 'و به راستی به داود و سلیمان، دانشی [ویژه] عطا کردیم، و آن دو گفتند: «ستایش، مخصوص خداوندی است که ما را بر بسیاری از بندگان مؤمنش برتری بخشید.»'),
(3175, 27, 16, 'و سلیمان، وارث داود شد. و گفت: «ای مردم! به ما زبان پرندگان آموخته‌اند، و از هرچیز، [بهره‌ای] به ما عطا شده؛ بی‌گمان این، همان برتری آشکار است.»'),
(3176, 27, 17, 'و برای سلیمان، سپاهیانش از جنّ و انس و پرنده گردآمدند، و دسته دسته منظم گردیدند.'),
(3177, 27, 18, '[سلیمان با لشکر خود حرکت می‌کرد،] تا به وادی مورچگان رسیدند. مورچه‌ای گفت: «ای مورچگان! به خانه‌های خود بروید تا سلیمان و لشکریانش، شما را ناآگاهانه پایمال نکنند.»'),
(3178, 27, 19, 'پس [سلیمان] از سخن این مورچه، با تبسّمی خندان شد و [به خاطر نعمت فهم گفتار حیوانات‌] گفت: پروردگارا! به من الهام کن [و توفیق ده] تا نعمتی را که به من، و به پدر و مادرم بخشیده‌ای، شکر گزارم. و کار شایسته‌ای که آن را می‌پسندی، انجام دهم. و به رحمتت مرا، در زمره‌ی بندگان شایسته‌ات درآور!»'),
(3179, 27, 20, 'و [سلیمان] جویای حال پرندگان شد و گفت: «مرا چه شده که هدهد را نمی‌بینم؟ [آیا حضور دارد و من او را نمی‌بینم؟] یا از غایبان است؟'),
(3180, 27, 21, 'قطعاً او را کیفری سخت خواهم داد، یا [برای عبرت دیگر پرندگان‌] او را سر می‌برم، مگر آن که [برای غیبتش] دلیلی روشن [و عذری موجّه] برایم بیاورد.»'),
(3181, 27, 22, 'پس دیری نپایید که [هدهد آمد و] گفت: «از چیزی آگاهی یافتم که تو [با همه‌ی عزّت و شوکتی که داری،] از آن آگاه نشده‌ای، و برای تو از [منطقه‌ی] سَبَأ گزارشی مهم و درست آورده‌ام.'),
(3182, 27, 23, 'من زنی را یافتم که بر مردم پادشاهی می‌کرد و از همه چیز برخوردار بود. و تخت بزرگی داشت.'),
(3183, 27, 24, 'آن زن و قومش را چنین یافتم که به جای خدا، برای خورشید سجده می‌کنند، و شیطان، اعمالشان را در نظرشان آراسته، پس آنان را از راه [حقّ] بازداشته است. و آنان هدایت نمی‌شوند.»'),
(3184, 27, 25, '[شیطان، اعمالشان را آراسته بود] که آنان برای خدا سجده نکنند. همان [خداوندی] که چیزهای ناپیدای آسمان‌ها و زمین را بیرون می‌آورد [و آشکار می‌کند]. و عملکرد نهان و آشکار شما را می‌داند.'),
(3185, 27, 26, 'خداوندی که معبودی جز او نیست. پروردگار عرش بزرگ است.'),
(3186, 27, 27, '[سلیمان] گفت: «به زودی خواهیم دید که آیا راست گفته‌ای یا از دروغگویان بوده‌ای؟'),
(3187, 27, 28, 'این نامه‌ی مرا ببر و به سوی آنها بیفکن! آن‌گاه از آنان روی برتاب [و گوشه‌ای کمین کن]، پس بنگر چه پاسخی می‌دهند؟»'),
(3188, 27, 29, '[چون هدهد، نامه را نزد بِلقِیس، ملکه‌ی سبأ افکند، او] گفت: «ای اشراف [و بزرگان کشور]! نامه‌ای پرارزش به سوی من افکنده شد.'),
(3189, 27, 30, 'نامه از سلیمان است و [متن آن] این است»: «به نام خداوند بخشنده‌ی مهربان.'),
(3190, 27, 31, 'بر من برتری مجویید! نزد من آیید، و تسلیم [حقّ و مطیع من] باشید.»'),
(3191, 27, 32, '[بلقیس،] گفت: «ای بزرگان! در کارم نظر دهید، که من هیچ‌کاری را بی‌حضور شما به پایان نبرده‌ام.»'),
(3192, 27, 33, '[بزرگان قوم] گفتند: «ما سخت نیرومند و دلاوریم، و دارای قدرت جنگی فراوان هستیم، ولی اختیار کار با توست، پس بنگر چه فرمان می‌دهی؟»'),
(3193, 27, 34, '[ملکه] گفت: «بی‌شک، هرگاه پادشاهان [با لشکریان] به منطقه‌ی آبادی وارد شوند، [و هجوم بَرند،] آنجا را به تباهی می‌کشند، و عزیزانِ آنجا را خوار می‌کنند. و این، شیوه‌ی همیشگی آنان است.'),
(3194, 27, 35, '[من جنگ را به صلاح نمی‌بینم،] و هدیه‌ای ارزشمند به سویشان می‌فرستم، پس چشم به را هم تا ببینم فرستادگان من با چه خبری بازمی‌گردند. [شاید سلیمان هدیه را بپذیرد و ما درامان بمانیم.]»'),
(3195, 27, 36, 'چون [پیک حامل هدیه،] نزد سلیمان آمد، سلیمان گفت: «آیا مرا با مالی ناچیز یاری می‌کنید؟ آنچه خداوند به من داده بهتر است از آنچه به شما داده است. [من با هدیه‌ی شما شاد نمی‌شوم،] بلکه شمایید که به هدیه‌ی خود شادمانید.'),
(3196, 27, 37, 'به سوی آنان برگرد! [و به آنان اعلام کن] که ما با لشکریانی به سوی آنان خواهیم آمد که تاب رویارویی با آن را ندارند. و آنان را از آنجا با زبونی و خواری بیرون خواهیم راند.»'),
(3197, 27, 38, '[سپس به حاضران] گفت: «ای بزرگان! کدام یک از شما تخت آن [ملکه] را پیش از آن که آنان به حال تسلیم نزد من آیند، برای من می‌آورد؟»'),
(3198, 27, 39, 'یکی از جنّیان کاردان گفت: «من آن را نزد تو می‌آورم، پیش از آن که از جای خود برخیزی. و من بر این [کار] توانا و امینم.»'),
(3199, 27, 40, 'کسی [به نام آصِف بن بَرخیا] که به بخشی از کتاب [الهی] آگاهی داشت، گفت: «من آن [تخت] را پیش از آن که پلک چشمت به هم بخورد، نزد تو می‌آورم!» همین که [سلیمان،] آن را نزد خود مستقرّ دید، [به جای غرور و تکبّر] گفت: «این از لطف پروردگار من است، تا مرا بیازماید که آیا شکرگزارم یا ناسپاس؟ و هر کس شکر کند، قطعاً به سود خویش شکر کرده، و هر کس ناسپاسی ورزد، [زیانی به خدا نمی‌رساند.] پروردگار من بی‌نیاز و کریم است.»'),
(3200, 27, 41, '[آنگاه] گفت: «تخت را [با تغییراتی‌] برایش ناشناس گردانید، تا ببینیم آیا [به شناخت آن‌] راه می‌یابد، یا از کسانی است که [بدین چیزها] راه نمی‌برند.»'),
(3201, 27, 42, 'پس هنگامی که [ملکه آمد، به او گفته شد: «آیا تخت تو این‌گونه است؟» گفت: «گویا خودِ آن است! و ما را پیش از این [که شاهد این معجزات باشیم‌، به حقّانیت و قدرت سلیمان] آگاه کردند. و تسلیم شدیم.»'),
(3202, 27, 43, 'آنچه [آن زن] به جای خدا می‌پرستید، او را [از حقّ] بازداشته بود، و او از قوم کافران بود.'),
(3203, 27, 44, 'به او گفتند: «واردِ صحنِ قصر شو!» پس چون آن را دید، پنداشت بِرکه‌ی آبی است. جامه از ساق پاهایش برگرفت، [سلیمان] گفت: «[این جا آبی نیست،] بلکه قصری است از شیشه و بلور صیقلی.» [ملکه] گفت: «پروردگارا! من به خود ستم کردم؛ و [اکنون] همراه سلیمان، در برابر پروردگار جهانیان، سر تسلیم فرود آورده‌ام!»'),
(3204, 27, 45, 'و به سوی قوم ثمود، برادرشان صالح را فرستادیم که [گفت:] «خداوند را بپرستید!» آن‌گاه مردم دو گروه [مؤمن و کافر] شدند که با یکدیگر ستیزه می‌کردند.'),
(3205, 27, 46, '[صالح] گفت: «ای قوم من! چرا پیش از نیکی، شتابزده خواهان بدی هستید؟ [چرا درخواست عذاب می‌کنید؟] چرا از خداوند آمرزش نمی‌خواهید تا مشمول رحمت گردید؟»'),
(3206, 27, 47, '[مردم به صالح] گفتند: «ما به تو و همراهانت، فال بد زده‌ایم. این قحطی و مشکلاتِ ما، به خاطر وجود شماست!]» [صالح] گفت: «فال، [و سرنوشت نیک و بد] شما نزد خداست، بلکه شما گروهی هستید که مورد آزمایش قرار گرفته‌اید.»'),
(3207, 27, 48, 'و در آن شهر، نُه گروه بودند که در آن سرزمین فساد می‌کردند و اهل اصلاح نبودند.'),
(3208, 27, 49, '[آن گروه‌های مفسد به یکدیگر] گفتند: «به خداوند سوگند یاد کنید [و هم‌قسم شوید] که بر صالح و خانواده‌اش شبیخون می‌زنیم، [و آنها را به قتل می‌رسانیم،] سپس به خونخواهش خواهیم گفت: ما در محل قتل کسانش حاضر نبودیم، و ما قطعاً راست می‌گوییم.»'),
(3209, 27, 50, 'و آنها دست به نیرنگ بزرگی زدند، و ما نیز به تدبیر بزرگی دست زدیم؛ ولی آنها نمی‌فهمیدند.'),
(3210, 27, 51, 'پس بنگر که پایان تدبیر آنان به کجا انجامید؟ ما آنان و قومشان، همگی را نابود کردیم.'),
(3211, 27, 52, 'پس این خانه‌های آنهاست که به خاطر ظلمی که کردند، ویران و خالی شده است. و قطعاً در این [کیفر، عبرت و] نشانه‌ی روشنی است برای اهل علم و آگاهی.'),
(3212, 27, 53, 'و مؤمنان و کسانی را که اهل پروا بودند، نجات دادیم.'),
(3213, 27, 54, 'و لوط را که به قومش گفت: «آیا این کار بسیار زشت را می‌کنید، در حالی که به زشتی آن آگاهید؟'),
(3214, 27, 55, 'آیا شما از روی شهوت، به جای زنان، به سراغ مردان می‌روید؟ بلکه شما مردمی [نادان و] جهالت پیشه‌اید.»'),
(3215, 27, 56, 'ولی پاسخ قومش جز این نبود که گفتند: «خاندان لوط را از شهرتان بیرون کنید؛ زیرا آنان مردمی هستند که پاکدامنی را می‌طلبند، [و با ما همصدا نیستند]!»'),
(3216, 27, 57, 'پس ما لوط و خانواده‌اش را نجات دادیم، جز زنش را که [به خاطر انحراف،] مقدّر کرده بودیم از بازماندگان [و نابودشدگان] باشد.'),
(3217, 27, 58, 'سپس بارانی [از سنگ] بر سر آنها باریدیم، [و همگی زیر آن دفن شدند.] و چه بد است باران هشدارداده‌شدگان!'),
(3218, 27, 59, 'بگو: «سپاس برای خداست و سلام بر آن بندگانش، که آنان را برگزید.» آیا خدا بهتر است یا آنچه که شریک او قرار می‌دهند؟'),
(3219, 27, 60, 'کیست که آسمان‌ها و زمین را آفرید؟ و از آسمان برای شما آبی فروفرستاد، پس به وسیله‌ی آن، باغ‌هایی بهجت‌انگیز رویاندیم. کار شما نبود که درختانش را برویانید. آیا با خدا معبود دیگری هست؟ [نه،] بلکه آنان قومی منحرفند.'),
(3220, 27, 61, 'کیست که زمین را قرارگاهی ساخت و در آن، رودها قرار داد، و برای آن، کوه‌ها را [مانند لنگر،] ثابت و پابرجا نهاد و میان دو دریا[ی شور و شیرین] مانعی قرار داد [که با هم مخلوط نشوند]. آیا با خدا معبود دیگری هست؟ [نه،] بلکه بیشتر آنان نمی‌دانند.'),
(3221, 27, 62, 'کیست که هرگاه درمانده‌ای او را بخواند، اجابت نماید، و بدی و ناخوشی را برطرف کند؟ و شما را جانشینان زمین قرار دهد؟ آیا با خدا معبود دیگری هست؟ چه کم پند می‌پذیرید.'),
(3222, 27, 63, 'کیست که شما را در تاریکی‌های خشکی و دریا هدایت می‌کند؟ و کیست که بادها را مژده‌ای پیشاپیشِ [بارانِ] رحمتش می‌فرستد؟ آیا با خدا معبود دیگری هست؟ خداوند برتر است از آنچه برای او شریک قرار می‌دهند.'),
(3223, 27, 64, 'کیست که آفرینش را آغاز می‌کند، سپس آن را [در قیامت] باز می‌گرداند؟ و کیست که شما را از آسمان و زمین روزی می‌دهد؟ آیا با خداوند معبود دیگری هست؟ بگو: «اگر راست می‌گویید، برهانتان را بیاورید.»'),
(3224, 27, 65, 'بگو: «جز خداوند، هیچ‌کس در آسمان‌ها و زمین، غیب نمی‌داند و کسی نمی‌داند چه زمانی برانگیخته خواهد شد.»'),
(3225, 27, 66, '[چنین نیست که قیامتی در کار نباشد؛] بلکه علم مشرکان نسبت به آخرت، پایان یافته است، بلکه [با شنیدن دلایل، باز هم] از آن در شک هستند، بلکه نسبت به [چگونگی] وقوع آن کورند.'),
(3226, 27, 67, 'و کسانی که کفر ورزیدند، گفتند: «آیا هرگاه ما و پدرانمان، خاک شدیم، آیا حتماً [زنده از گور] بیرون آورده می‌شویم؟'),
(3227, 27, 68, 'در حقیقت به ما و نیاکانمان، پیش‌تر، این وعده داده شده، [اما] این وعده، جز افسانه‌های پیشینیان، چیز دیگری نیست!»'),
(3228, 27, 69, 'بگو: «در زمین بگردید، پس بنگرید که فرجام گنه‌پیشگان چگونه بوده است؟»'),
(3229, 27, 70, 'و بر [انحراف و شوربختی] آنان اندوه مخور! و از آنچه مکر [و توطئه] می‌کنند، دلتنگ مباش!'),
(3230, 27, 71, 'و [کافران] می‌گویند: «اگر راست می‌گویید، این وعده [عذاب] کی خواهد بود؟»'),
(3231, 27, 72, 'بگو: «چه بسا بخشی از آن عذابی که با شتاب می‌خواهید، در پی شما باشد.»'),
(3232, 27, 73, 'و البتّه پروردگار تو بر [این] مردم دارای بخشش است. ولی بیشترشان شکرگزار نیستند.'),
(3233, 27, 74, 'و البتّه پروردگارت، آنچه را در سینه‌هایشان پنهان دارند و آنچه را آشکار می‌کنند، [به خوبی] می‌داند.'),
(3234, 27, 75, 'و هیچ [چیز] پنهانی در آسمان و زمین نیست، مگر این که در کتابی روشن [نزد خداوند ثبت] است.'),
(3235, 27, 76, 'به راستی این قرآن، بیشتر آنچه را بنی‌اسرائیل در آن اختلاف دارند، [به طور صحیح] برآنان حکایت می‌کند.'),
(3236, 27, 77, 'و به راستی که آن [قرآن،] برای مؤمنان، مایه‌ی هدایت و رحمت است.'),
(3237, 27, 78, 'بی‌شک، پروردگارت با حکم خود، میانشان داوری خواهد کرد. و اوست قدرتمند دانا.'),
(3238, 27, 79, 'پس بر خداوند توکل کن! [و بدان] که تو بر حقّ آشکار هستی!'),
(3239, 27, 80, 'بی‌شک، نمی‌توانی دعوت خود را به گوش مردگان برسانی. و نمی‌توانی کران را آن‌گاه که روی برمی‌گردانند و پشت می‌کنند، فراخوانی.'),
(3240, 27, 81, 'تو هدایتگر کوران از گمراهیشان نیستی؛ تو فقط می‌توانی سخن خود را به گوش کسانی برسانی که به آیات ما ایمان آورده‌اند و در برابر حقّ تسلیم هستند.'),
(3241, 27, 82, 'و هرگاه سخن [و وعده‌ی عذاب الهی] بر مردم حتمی شود، جنبنده‌ای را برای آنان از زمین بیرون آوریم، تا با آنان سخن گوید که: «مردم همواره [نشانه‌ها و] آیات ما را باور نمی‌کردند.»'),
(3242, 27, 83, 'و [یاد کن] روزی که از هر امّتی، گروهی از کسانی که آیات ما را انکار می‌‌کنند، محشور می‌کنیم، پس آنان از پراکنده شدن منع می‌شوند.'),
(3243, 27, 84, 'چون گرد آیند، [خداوند] می‌فرماید: «آیا آیات مرا دروغ انگاشتید، و حال آن که به آنها احاطه‌ی علمی نداشتید. آیا شما [در طول زندگی، جز تکذیب] چه می‌کردید؟»'),
(3244, 27, 85, 'و به خاطر ستمی که کردند، حکم [عذاب] بر آنان مقرّر گردید، پس آنها حرفی نمی‌زنند [و سخنی ندارند که بگویند].'),
(3245, 27, 86, 'آیا ندیدند که ما شب را قرار دادیم، تا در آن آرام گیرند، و روز را روشنی‌بخش ساختیم؟ حتماً در این امر، برای کسانی که ایمان دارند، عبرت‌هایی است.'),
(3246, 27, 87, 'و [یاد کن] روزی که در صور دمیده شود، پس هرکه در آسمان‌ها و هر که در زمین است، به هراس می‌افتد، مگر آن کس که خدا بخواهد. و همگی خاضعانه نزد او می‌آیند.'),
(3247, 27, 88, 'و کوه‌ها را می‌بینی و می‌پنداری که بی‌حرکتند، در حالی که آنها همچون ابر در حرکتند. [این] صنعت [ماهرانه‌ی] خداست که هر چیزی را با دقّت ساخته است. او به هرچه انجام می‌دهید، آگاه است.'),
(3248, 27, 89, 'كسانی که كار نیكی بیاورند، [پاداشی] بهتر از عملشان خواهند داشت، و آنان از هراس آن روز، ایمِن هستند.'),
(3249, 27, 90, 'و كسانی كه كار بدی بیاورند، پس به رو در آتش سرنگون شوند، [و به آنان گفته شود:] «آیا جز آنچه می‌كردید، سزا داده می‌شوید؟»'),
(3250, 27, 91, '[ای پیامبر! بگو:] «فقط مأمورم پروردگار [مکّه،] این شهری كه خداوند آن را محترم شمرده، بپرستم. و همه چیز تنها برای اوست. و من مأمورم از اهل تسلیم و طاعت باشم.'),
(3251, 27, 92, 'و قرآن [را بر مردم] تلاوت كنم.» پس هر كس هدایت را بپذیرد، به سود خود پذیرفته، و هركس گمراه شود، پس بگو: «من فقط هشداردهنده‌ام!»'),
(3252, 27, 93, 'و بگو: «ستایش برای خداست. به زودی آیاتش را به شما نشان خواهد داد، و آن را خواهید شناخت.» و پروردگارت از آنچه می‌کنید، غافل نیست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا، سین، میم.'),
(3254, 28, 2, 'آن آیات کتاب روشنگر است.'),
(3255, 28, 3, 'ما بخشی از سرگذشت موسی و فرعون را به درستی بر تو می‌خوانیم، برای [آگاهی] گروهی که ایمان می‌آورند.'),
(3256, 28, 4, 'فرعون، در سرزمین [مصر] سرکشی کرد و مردم آنجا را فرقه فرقه نمود. گروهی از آنان را به زبونی و ناتوانی می‌کشاند، پسرانشان را سر می‌برید و زنانشان را [برای کنیزی] زنده نگه‌می‌داشت. به‌راستی که او از تبهکاران بود.'),
(3257, 28, 5, 'و ما اراده کرده‌ایم بر کسانی که در زمین ناتوان شمرده شده‌اند، منّت‌ گذاریم و آنان را پیشوایان و وارثان [زمین] قرار دهیم.'),
(3258, 28, 6, 'و به آنان در آن سرزمین، قدرت و تمکّن بخشیم و به فرعون و [وزیرش] هامان و سپاهیانشان، که از ایشانند، آنچه را از آن بیمناک بودند، نشان دهیم.'),
(3259, 28, 7, 'و ما به مادر موسی الهام کردیم که: «او را شیر بده! پس هرگاه [از فرعونیان] بر او بیمناک شدی، او را [در صندوقی بگذار و] به دریا بیافکن! و مترس و [از دوریش] غمگین مباش! ما او را به تو بازمی‌گردانیم و او را از پیامبران قرار می‌دهیم.»'),
(3260, 28, 8, 'پس فرعونیان، او را [از آب] برگرفتند تا سرانجام، دشمن و مایه‌ی اندوهشان گردد. همانا فرعون و هامان و سپاهیانشان خطاکار بودند.'),
(3261, 28, 9, 'و همسر فرعون [احساس کرد که آنان قصد کشتن کودک را دارند، پس به فرعون] گفت: «او را نکشید که روشنی چشم من و توست. شاید [در آینده] به ما سودی برساند، یا او را به فرزندی بگیریم.» ولی آنها نمی‌فهمیدند [چه کسی را در آغوش خویش می‌پرورانند].'),
(3262, 28, 10, 'و دل مادر موسی خالی شد. اگر قلب او را استوار نساخته بودیم تا از ایمان آورندگان [به وعده‌ی ما باقی] بماند، نزدیک بود که [با بی‌تابی‌اش،] آن [راز] را فاش کند.'),
(3263, 28, 11, 'و [مادر موسی] به خواهر او گفت: «[صندوق حامل موسی را] دنبال کن!» پس او از دور موسی را زیر نظر داشت، در حالی که [دشمنان] متوجه نبودند.'),
(3264, 28, 12, 'و ما [شیر] زنان شیرده را از پیش بر او ممنوع کردیم، [تا به آغوش مادر بازگردد.] پس [خواهر موسی که مأموران را در جستجوی دایه دید،] گفت: «آیا می‌خواهید شما را به خانواده‌ای راهنمایی کنم که سرپرستی او را برای شما بپذیرند و خیرخواه او باشند؟»'),
(3265, 28, 13, 'پس ما او را به مادرش بازگردانیدیم تا روشنی چشم او باشد و غم نخورد و بداند که وعده‌ی الهی حق است؛ ولی بیشتر مردم نمی‌دانند.'),
(3266, 28, 14, 'چون موسی نیرومند شد و رشد و کمال یافت، به او حکمت و دانش عطا کردیم. و ما این‌گونه نیکوکاران را پاداش می‌دهیم.'),
(3267, 28, 15, 'و موسی وارد شهر شد، در حالی که مردم بی‌خبر بودند. در آنجا به دو مرد برخورد که با یکدیگر زد و خورد می‌کردند، این یکی از طرفداران موسی و آن [دیگری] از دشمنانش بود. پس آن که از پیروان موسی بود، از او در برابر دشمنش یاری خواست. پس موسی مشتی بر [سینه‌ی] او زد که کار او را ساخت. موسی گفت: «این [درگیری شما] از کار شیطان بود. او دشمنِ گمراه کننده‌ی آشکاری است.»'),
(3268, 28, 16, '[موسی] گفت: «پروردگارا! من به خویشتن ستم کرده‌ام. پس مرا بیامرز!» پس [خداوند] او را آمرزید. به درستی که او آمرزنده‌ی مهربان است.'),
(3269, 28, 17, '[سپس موسی] گفت: «پروردگارا! به خاطر [قدرت و] نعمتی که بر من ارزانی داشتی، هرگز پشتیبان تبهکاران نخواهم بود.»'),
(3270, 28, 18, '[موسی، آن شب را] در شهر نگران و ترسان صبح کرد. ناگهان [دید] همان کسی که دیروز از او یاری خواسته بود، [دوباره] وی را به فریادرسی می‌خواند! [به او] گفت: «به راستی که تو در گمراهی آشکاری هستی! [چرا که بدون قدرت و نیرو، با فرعونیان به زد و خورد می‌پردازی.]»'),
(3271, 28, 19, 'پس چون [موسی] خواست با آن کسی که دشمن هر دوی آنها بود، به سختی برخورد کند، گفت: «ای موسی! آیا می‌خواهی مرا [هم] به قتل برسانی؟ همان‌گونه که دیروز یک نفر را کشتی؟! تو جز زورگویی در روی زمین هدفی نداری، و نمی‌خواهی که از مصلحان باشی.»'),
(3272, 28, 20, '[در این هنگام] مردی از دورترین نقطه‌ی شهر [که مرکز استقرار فرعونیان بود،] شتابان آمد و گفت: «ای موسی! سران قوم در مورد کشتن تو به مشورت نشسته‌اند، پس [زود از شهر] بیرون برو، که من از خیرخواهان و دلسوزان توام.»'),
(3273, 28, 21, 'پس [موسی] از آن [شهر] بیرون رفت، در حالی که نگران و ترسان بود. گفت: «پروردگارا! مرا از [دست] این گروه ستمگر نجات بده!»'),
(3274, 28, 22, 'و چون [موسی] به سوی مَدین روی نهاد، گفت: «امید است که پروردگارم مرا به راه راست هدایت فرماید.»'),
(3275, 28, 23, 'و چون موسی به [چاه] آب مَدیَن رسید، گروهی از مردم را در اطراف آن دید که به سیراب کردن [چهارپایان خویش] مشغولند و در کنار آنان، دو زن را دید که [دام‌های خود را از رفتن به سوی آب] دور می‌داشتند. [موسی به آنان] گفت: «منظور شما [از این کناره‌گیری] چیست؟» گفتند: «ما [گوسفندان خود را] آب نمی‌دهیم تا این که همه‌ی چوپانان [دام‌هایشان را] برگردانند و [حضور ما در این‌جا برای آن است که] پدر ما پیرمردی کهن‌سال است.»'),
(3276, 28, 24, 'پس [موسی گوسفندانشان را] برای آنان آب داد، سپس رو به سوی سایه آورد و گفت: «پروردگارا! من به هر خیری که تو بر من بفرستی، نیازمندم.»'),
(3277, 28, 25, 'پس [چیزی نگذشت که] یکی از آن دو [زن] در حالی که با حیا و عفّت، راه می‌رفت، نزد او آمد و گفت: «پدرم از تو دعوت می‌کند تا مزد این که برای ما [دام‌هایمان را] آب دادی، به تو بپردازد.» پس همین که موسی به نزد او آمد و سرگذشت خود را برای او بازگو کرد، شعیب گفت: «نترس! تو از گروه ستمگران نجات یافتی!»'),
(3278, 28, 26, 'یکی از آن دو [دختر] گفت: «ای پدر! او را استخدام کن، زیرا بهترین کسی که [می‌توانی] استخدام کنی، شخصِ توانا و امین است.»'),
(3279, 28, 27, '[شعیب به موسی] گفت: «من قصد دارم یکی از این دو دخترم را به همسری تو درآورم، بر این [شرط] که هشت سال برای من کار کنی و اگر آن به ده سال تمام کردی، [به خواست و] از ناحیه‌ی خودتوست و من نمی‌خواهم بر تو سخت بگیرم. به زودی به خواست خداوند، مرا از صالحان خواهی یافت.»'),
(3280, 28, 28, '[موسی پیشنهاد شعیب را پذیرفت و] گفت: «این [قراردادی] میان من و تو [باشد. البتّه] هر کدام از دو مدّت را که به انجام رساندم، ستمی بر من نخواهد بود، [و من در انتخاب آن آزادم]! و خداوند برآنچه ما می‌گوییم، [گواه و] وکیل است.»'),
(3281, 28, 29, 'پس چون موسی، مدّت [قرارداد] را به پایان رساند و با خانواده‌اش [از مَدین به سوی مصر] رهسپار گردید، از سوی [کوه] طور آتشی را دید. به خانواده‌اش گفت: «قدری درنگ کنید! من آتشی دیدم، [می‌روم تا] شاید خبری برای شما بیاورم، یا شعله‌ای از آتش را، تا [با آن] گرم شوید.»'),
(3282, 28, 30, 'پس همین که [موسی] نزدیک آن [آتش] آمد، [ناگهان] از جانب راست آن درّه، در آن جایگاه مبارک و خجسته، از [میان] یک درخت، ندا داده شد که: ای موسی! همانا من، اللّه، پروردگار جهانیان هستم.'),
(3283, 28, 31, 'و عصایت را بیفکن! پس چون مشاهده کرد آن [عصا] چنان جست و خیز می‌کند گویا ماری چالاک و کوچک است، پشت‌کنان گریخت و به عقب [هم] نگاه نکرد! [ندا رسید که:] «ای موسی! پیش بیا و نترس! همانا تو در امان هستی.'),
(3284, 28, 32, '[ای موسی!] دستت را در گریبان فروببر، [خواهی دید که] بدون هیچ بیماری و نقص و عیبی، سپید و درخشان بیرون خواهد آمد. و برای رهایی از [تعجّب و] ترس، دو دستت را بر سینه بگذار. پس این [دو معجزه، دو دلیل روشن و] دو برهان از طرف پروردگارت به سوی فرعون و اشراف قوم اوست. به‌درستی که آنان گروهی فاسق بوده‌اند.»'),
(3285, 28, 33, '[موسی] گفت: «پروردگارا! من یکی از آنها را کشته‌ام، پس می‌ترسم [پیش از آن که دعوتم را ابلاغ کنم،] مرا به قتل برسانند.'),
(3286, 28, 34, 'و برادرم هارون را که در بیان از من شیواتر است، برای یاری‌ام با من بفرست، تا مرا تصدیق کند. می‌ترسم که [فرعونیان] مرا تکذیب کنند.»'),
(3287, 28, 35, '[خداوند] فرمود: « [نگران مباش!] ما بازوی تو را به واسطه‌ی [همراهیِ] برادرت محکم خواهیم کرد و برای شما، برتری و سلطه قرار خواهیم داد، پس به برکت آیات [و نشانه‌های] ما بر شما دست نخواهند یافت. شما و پیروانتان پیروزید!»'),
(3288, 28, 36, 'پس هنگامی که موسی با [معجزات و] آیات روشن ما به سراغ آنان آمد، گفتند: «این چیزی جز جادوی ساختگی نیست و ما چنین چیزی را در [میان] نیاکان خود نشنیده‌ایم.»'),
(3289, 28, 37, 'و موسی [در پاسخ تکذیب فرعونیان] گفت: «پروردگار من به کسی که از نزد او هدایت آورده، آگاه‌تر است و [می‌داند] سرانجام [نیک] آخرت برای چه کسانی است. بی‌تردید ستمکاران رستگار نخواهند شد.»'),
(3290, 28, 38, 'و فرعون گفت: «ای بزرگان قوم! من جز خودم، معبودی برای شما نمی‌شناسم؛ [امّا برای تحقیق بیشتر،] ای هامان! برای من آتشی بر گِل بیفروز، [و آجر تهیه کن،] پس برجی بلند برای من بساز، شاید [با بالا رفتن از آن] به خدای موسی دست یابم و من او را از دروغگویان می‌پندارم.»'),
(3291, 28, 39, 'او و سپاهیانش به ناحقّ در زمین تکبّر ورزیدند و پنداشتند که آنان به سوی ما بازگردانده نمی‌شوند.'),
(3292, 28, 40, 'پس ما [نیز] او و لشکریانش را [با قهر خود] گرفتیم و به دریا افکندیم. پس بنگر که پایان کار ستمگران چگونه است.'),
(3293, 28, 41, 'و آنان را پیشوایانی قرار دادیم که به آتش [دوزخ] دعوت می‌کنند و [البتّه در] روز قیامت، یاری نخواهند شد.'),
(3294, 28, 42, 'و در این دنیا، لعنتی بدرقه‌ی آنان کردیم و روز قیامت، آنان از زشت‌رویان خواهند بود.'),
(3295, 28, 43, 'و بی‌گمان پس از آن که نسل‌های نخستین را نابود ساختیم، به موسی کتاب [آسمانی تورات را] عطا کردیم [تا وسیله‌ای] برای بصیرت مردم و هدایت و رحمت باشد، شاید که آنان پند گیرند.'),
(3296, 28, 44, 'و [ای پیامبر!] هنگامی که فرمان [نبوّت] را به موسی دادیم، تو در جانب غربی [کوه طور] حضور نداشتی و از شاهدان نبودی.'),
(3297, 28, 45, 'ولی ما [اقوامی را] در روزگاران مختلف پدید آوردیم، پس روزگار درازی بر آنها گذشت، [تا جایی که سرگذشتشان فراموش شد.‌] و تو در میان اهل مَدین اقامت نداشته‌ای تا [از وضع آنان آگاه باشی و بتوانی] آیات ما را [درباره‌ی مردم مَدین،] بر مردم مکه بخوانی، اما سنّت ما این است که افرادی را برای هدایت می‌فرستیم.'),
(3298, 28, 46, 'و آن‌گاه که ما [موسی را] ندا دادیم، تو در کنار کوه طور نبودی، اما [این وحی] رحمتی است از جانب پروردگارت، تا تو [به واسطه‌ی آن] قومی را که پیش از تو هشدار دهنده‌ای برایشان نیامده بود، هشدار دهی، شاید آنان متذکر شوند.'),
(3299, 28, 47, 'اگر مصیبتی به خاطر دستاوردشان به آنان برسد، می‌گویند: «پروردگارا! چرا برای ما پیامبری نفرستادی تا آیات تو را پیروی کنیم و از مؤمنان باشیم؟»'),
(3300, 28, 48, 'پس هرگاه از جانب ما حقّ برای آنان آمد، گفتند: «چرا آنچه [به این پیامبر] داده شده، شبیه آنچه به موسی داده شده، نیست؟» مگر [همین کافران لجوج و بهانه‌گیر] به آنچه پیش‌تر به موسی داده شده بود، کفر نورزیدند؟ [مگر آنها نبودند که] گفتند: «این دو [کتاب تورات و قرآن،] جادوهایی هستند که پشتیبان یکدیگرند!» و گفتند: «ما به همه‌ی آنها کافریم!»'),
(3301, 28, 49, '[ای پیامبر! به آنان] بگو: «اگر راست می‌گویید، شما کتابی از جانب خداوند بیاورید که از این دو [کتاب] هدایت‌بخش‌تر باشد، تا من از آن پیروی کنم.»'),
(3302, 28, 50, 'پس اگر [پیشنهاد] تو را نپذیرفتند، بدان که آنان پیرو هوس‌های خویشند. و کیست گمراه‌تر از آن کس که از هوس خود پیروی کرده و هدایت الهی را نپذیرفته است؟ خداوند، قوم ستمگر را هدایت نمی‌کند.'),
(3303, 28, 51, 'و ما پیوسته برای آنان سخن گفتیم، شاید که پند گیرند و متذکر شوند.'),
(3304, 28, 52, '[برخی از] آنان که پیش از این کتابشان دادیم، همان‌ها به این [قرآن] ایمان می‌آورند.'),
(3305, 28, 53, 'و زمانی که [قرآن] بر آنان تلاوت شود، می‌گویند: «ما به آن ایمان آوردیم. آن، حقّی است از سوی پروردگارمان. ما پیش از این [نیز] اهل تسلیم بوده‌ایم.»'),
(3306, 28, 54, 'آنان به خاطر صبرشان [بر ایمان و عمل به قرآن‌]، دو برابر پاداش داده می‌شوند. و [آنانند که] بدی‌ها را با نیکی دفع می‌کنند و از آنچه روزی آنان کرده‌ایم، انفاق می‌نمایند.'),
(3307, 28, 55, 'و هرگاه [سخن بیهوده و] لغوی را بشنوند، از آن روی برمی‌گردانند و می‌گویند: «کردارِ ما برای ما و کارهای شما برای شما! [ما را به خیر و] شما را به سلامت! ما به سراغ نادانان نمی‌رویم.»'),
(3308, 28, 56, '[ای پیامبر!] تو نمی‌توانی هر که را دوست داری، هدایت کنی، بلکه خداوند است که هر کس را بخواهد، هدایت می‌کند. و او به ره‌یافتگان آگاه‌تر است.'),
(3309, 28, 57, 'و [کافران] گفتند: «اگر ما همراه تو از هدایت [قرآن‌] پیروی کنیم، [قدرتمندان عرب،‌] ما را از سرزمینمان [مکه‌] خواهند ربود!» آیا ما آنان را در حرم امنی قرار ندادیم که همواره هر نوع میوه و محصولی که رزقی از سوی ماست، به سوی آن سرازیر می‌شود؟ ولی بیشتر آنان نمی‌دانند!'),
(3310, 28, 58, 'و چه بسیار مناطقی که ما به خاطر طغیان در زندگی، نابود کردیم. این خانه‌های آنهاست که [خراب و ویران شده و] پس از آنان، جز اندکی [از انسان‌ها در آن] سکونت نکردند. و ما وارث [آنان] بوده‌ایم!'),
(3311, 28, 59, 'و پروردگارت قریه‌ها را نابود نمی‌کند، مگر این که در میان آنان پیامبری برانگیزد، که آیات ما را بر آنان تلاوت کند. و ما هیچ آبادی را هلاک نکردیم، مگر آن که اهل آن ظالم بودند.'),
(3312, 28, 60, 'و آنچه به شما داده شده، بهره‌ی زندگی [زودگذر] دنیا و زینت آن است. و آنچه نزد خداست، بهتر و پایدارتر است. پس آیا نمی‌اندیشید؟!'),
(3313, 28, 61, 'پس آیا کسی که به او وعده‌ای نیکو داده‌ایم و او آن را خواهد دید، مانند کسی است که او را از بهره‌ی زندگی دنیا بهره‌مند ساخته‌ایم، سپس او در روز قیامت از احضارشدگان خواهد بود؟!'),
(3314, 28, 62, 'و روزی که [خداوند] آنها را ندا می‌دهد، پس می‌گوید: «کجایند آن شریکانی که برای من می‌پنداشتید؟!»'),
(3315, 28, 63, 'آن [شریکان،] کسانی که حکم عذاب بر آنان محقّق گشته است، می‌گویند: «پروردگارا! اینان کسانی هستند که ما گمراه کرده‌ایم. همان‌گونه که خود گمراه بودیم، آنها را [نیز] گمراه کردیم، [امّا اینک از آنان] به سوی تو بیزاری می‌جوییم. [اینان در واقع] ما را نمی‌پرستیدند، [بلکه به دنبال هواپرستی و هوسرانی خود بودند.]»'),
(3316, 28, 64, 'و [در این هنگام به مشرکان] گفته می‌شود: «معبودانی را که شریک خدا می‌پنداشتید، بخوانید!» پس آنها را می‌خوانند؛ امّا [معبودان] جوابی به آنها نمی‌دهند. و [در این حال] عذاب الهی را می‌بینند [که آنان را در برمی‌گیرد، پس آرزو می‌کنند] ای کاش، هدایت یافته بودند.'),
(3317, 28, 65, 'و روزی که [خداوند] آنها را ندا می‌دهد، پس می‌گوید: «به پیامبران [و فرستادگان من] چه پاسخی دادید؟»'),
(3318, 28, 66, 'پس در آن روز، همه‌ی اخبار بر آنان پوشیده می‌مانَد، از این رو آنان از یکدیگر [چیزی] نمی‌پرسند.'),
(3319, 28, 67, 'امّا هر کس [در این دنیا] توبه کند و ایمان بیاورد و کار شایسته‌ای کند، پس امید است که از رستگاران باشد.'),
(3320, 28, 68, 'و پروردگارت آنچه را بخواهد، می‌آفریند، و برمی‌گزیند. برای آنان [در برابر خداوند] حقّ انتخابی نیست. منزّه است خدا. و برتر است از آنچه [برای او] شرک می‌ورزند.'),
(3321, 28, 69, 'و پروردگار تو، آنچه را سینه‌هایشان پنهان می‌دارند و آنچه را آشکار می‌سازند، می‌داند.'),
(3322, 28, 70, 'اوست خدایی که معبودی جز او نیست. ستایش در دنیا و آخرت مخصوص اوست. حاکمیت تنها از آنِ اوست و به سوی او بازگردانده می‌شوید.'),
(3323, 28, 71, 'بگو: «آیا اندیشیده‌اید که اگر خدا شب را تا روز رستاخیز بر شما پایدار بدارد، کدام معبودی جز اللّه برای شما روشنی خواهد آورد؟ پس آیا نمی‌شنوید؟»'),
(3324, 28, 72, 'بگو: «آیا اندیشیده‌اید که اگر خداوند، روز را بر شما تا روز رستاخیز جاوِدان بدارد، کدام معبودی جز اللّه برای شما شب خواهد آورد، تا در آن آرامش [و آسایش] یابید؟ پس آیا نمی‌نگرید؟»'),
(3325, 28, 73, 'و از رحمت اوست که برای شما شب و روز را قرار داد تا در آن آرامش یابید و از فضل او [روزی خود را] بجویید، و باشد که شکر گزارید.'),
(3326, 28, 74, 'و روزی که [خداوند] آنها را ندا می‌دهد و می‌گوید: «کجایند آن [همتایان و] شریکانی که برای من می‌پنداشتید؟!»'),
(3327, 28, 75, 'و [در آن روز] از هر امّتی، گواهی بیرون می‌آوریم، پس [به مشرکان] می‌گوییم: «دلیل خود را [بر شرک] بیاورید!» پس می‌فهمند که حقّ، مخصوص خداست و هر چه به دروغ می‌بافتند، از [دست] آنان رفته و محو شده است.'),
(3328, 28, 76, 'قارون از قوم موسی بود و بر آنان ستم کرد. و ما آن‌قدر از گنج‌ها به او داده بودیم که حمل کلیدهایش بر گروهی نیرومند نیز دشوار بود. هنگامی که قومش به او گفتند: «مغرورانه شادی مکن! بی‌تردید خداوند شادمانانِ مغرور را دوست نمی‌دارد.'),
(3329, 28, 77, 'و در آنچه خداوند به تو داده است، سرای آخرت را بجوی، و [در عین حال] بهره‌ات را نیز از این دنیا فراموش مکن. و همان‌گونه که خداوند به تو احسان کرده است، تو نیز [به دیگران] احسان کن! و به دنبال فساد در زمین مباش، که خداوند، فسادگران را دوست نمی‌دارد.»'),
(3330, 28, 78, '[قارون] گفت: «این [ثروت فراوان] را فقط به خاطر دانشی که نزد من است، به من داده‌اند.» او نمی‌دانست که خداوند، پیش از او از میان نسل‌ها، کسانی را نابود کرده است، که از او نیرومندتر و مال‌اندوزتر بودند؟ و گناهکاران، از گناهشان پرسیده نمی‌شوند [و عذاب خداوند ناگهان فرود می‌آید].'),
(3331, 28, 79, '[روزی قارون] با تمام تجمّل خود در میان قومش ظاهر شد. آنان که خواهان زندگانی دنیا بودند [آهی کشیدند و] گفتند: «ای کاش مانند آنچه به قارون داده‌اند، ما نیز داشتیم! راستی که او بهره‌ی بزرگی [از نعمت‌ها] دارد.»'),
(3332, 28, 80, 'و [امّا] کسانی که علم [واقعی] به آنان داده شده بود، گفتند: «وای بر شما! پاداش الهی برای کسانی که ایمان آورند و کار شایسته کنند، [از این مال و ثروت]، بهتر است. و [البتّه] جز صابران، آن [پاداش] را دریافت نخواهند کرد.»'),
(3333, 28, 81, 'پس ما، او و خانه‌اش را به زمین فروبردیم. و هیچ گروهی نبود که او را در برابر قهر خداوند یاری کند، و خودش نیز نمی‌توانست از خویش دفاع نماید.'),
(3334, 28, 82, 'و بامدادان، همان کسانی که موقعیت دیروز قارون را آرزو می‌کردند، می‌گفتند: «ای وای! گویا خداوند، [رزق و] روزی را بر هر کس از بندگانش که بخواهد، گشاده یا تنگ می‌گرداند. اگر خداوند بر ما منت نگذاشته بود، حتماً ما را نیز [با او] به قعر زمین فرومی‌برد. وای! گویا کافران، رستگار نمی‌شوند.»'),
(3335, 28, 83, 'آن سرای آخرت را ما برای کسانی قرار می‌دهیم که خواستار برتری و فساد در زمین نباشند. و سرانجام، [رستگاری] از آنِ پرهیزکاران است.'),
(3336, 28, 84, 'هر کس نیکی آورد، [پاداشی] بهتر از عملش خواهد داشت، و هر کس بدی آورد، پس [بداند] کسانی که کارهای ناروا کنند، جز [به اندازه‌ی] آنچه کرده‌اند، مجازات نمی‌شوند.'),
(3337, 28, 85, 'آن کس که قرآن را [بر تو نازل، و عمل به آن را] بر تو واجب کرد، قطعاً تو را به وعده‌گاه [و زادگاهت] بازخواهد گردانید. بگو: «پروردگارم آگاه‌تر است که چه کسی هدایت را آورده، و چه کسی در گمراهی آشکار است.»'),
(3338, 28, 86, 'و تو امید نداشتی که کتاب [آسمانی] به سوی تو فرستاده شود، لکن مگر رحمتی از سوی پروردگارت بود. پس [به شکرانه‌ی آن] هرگز پشتیبان کافران مباش.'),
(3339, 28, 87, 'و پس از آن که آیات الهی به سوی تو نازل گردید، [وسوسه‌ها و تهدیدهای کافران،] تو را [از تبلیغ آن] بازندارد. و [همچنان دیگران را] به سوی پروردگارت دعوت کن. و هرگز از مشرکان مباش!'),
(3340, 28, 88, 'و [هرگز] معبود دیگری را با اللّه مخوان که معبودی جز او نیست. همه چیز جز ذات [پاک] او نابودشدنی است. فرمانروایی تنها از آن اوست، و همه به سوی او بازگردانده می‌شوید.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف، لام، میم.'),
(3342, 29, 2, 'آیا مردم پنداشتند که چون گفتند: «ایمان آوردیم!» [با گفتن این جمله] رها می‌شوند و دیگر مورد آزمایش قرار نمی‌گیرند؟!'),
(3343, 29, 3, 'در حالی که بی‌تردید کسانی را که پیش از ایشان بودند، آزمودیم. قطعاً خداوند کسانی را که راست گفتند، می‌شناسد، و دروغگویان را [نیز] می‌شناسد'),
(3344, 29, 4, 'آیا کسانی که کارهای بد می‌کنند، پنداشته‌اند که بر ما پیشی خواهند گرفت [و از کیفر ما خواهند گریخت]؟ چه بد داوری می‌کنند.'),
(3345, 29, 5, 'آن کس که به دیدار خداوند امید دارد، [در طاعت و عبادت بکوشد، که] بی‌شک زمان معین شده [از سوی‌] خدا [که روز قیامت است،‌] آمدنی است؛ و اوست شنوای دانا.'),
(3346, 29, 6, 'و هر کس تلاش کند، پس برای خود می‌کوشد؛ زیرا خداوند از جهانیان بی‌نیاز است.'),
(3347, 29, 7, 'و کسانی که ایمان آوردند و کارهای شایسته کردند، قطعاً لغزش‌های آنان را می‌پوشانیم و بی‌شک بهتر از آنچه عمل می‌کرده‌اند، پاداششان می‌دهیم.'),
(3348, 29, 8, 'و به انسان سفارش کردیم که به پدر و مادر خود نیکی کند و اگر آن دو بکوشند تا چیزی را که هیچ دانشی درباره‌ی آن نداری، همتای من قرار دهی، از ایشان اطاعت مکن! بازگشتِ شما به سوی من است، پس شما را از آنچه انجام می‌دادید، آگاه خواهم ساخت.'),
(3349, 29, 9, 'و کسانی که ایمان آوردند و کارهای شایسته کردند، بی‌شک آنان را در زمره‌ی شایستگان درمی‌آوریم.'),
(3350, 29, 10, 'و گروهی از مردم کسانی هستند که می‌گویند: «به خداوند ایمان آورده‌ایم!» پس هرگاه در راه خدا آزار ببینند، اذیت و آزار مردم را همچون عذاب الهی [غیر قابل تحمل‌] می‌شمارند [و از ایمان دست برمی‌دارند]، ولی اگر از جانب پروردگارت یاری و پیروزی [برای شما] برسد، قاطعانه می‌گویند: «ما همواره با شما بوده‌ایم، [و در این پیروزی شریکیم]!» آیا خدا به آنچه در سینه‌های جهانیان می‌گذرد، آگاه‌تر نیست؟!'),
(3351, 29, 11, 'و قطعاً خدا کسانی را که ایمان آورده‌اند، می‌شناسد و منافقان را [نیز] می‌شناسد.'),
(3352, 29, 12, 'و کافران به مؤمنان گفتند: «شما راه ما را پیروی کنید، [اگر گناهی داشته باشد،] ما گناهان شما را بر عهده می‌گیریم.» در حالی که چیزی از گناهانشان را عهده‌دار نیستند؛ قطعاً آنان دروغگویانند.'),
(3353, 29, 13, 'بی‌شک آنان هم بار [خطای] خودشان و [هم] بارهای دیگر را با بار خود بر دوش خواهند کشید و مسلّماً در روز قیامت از آنچه به دروغ می‌بافتند، بازخواست خواهند شد.'),
(3354, 29, 14, 'و به راستی نوح را به سوی قومش فرستادیم، پس در میان آنان، نهصد و پنجاه سال درنگ کرد، [ولی تنها اندکی از مردم ایمان آوردند،] پس طوفان آنان را فراگرفت، در حالی که ستمگر بودند.'),
(3355, 29, 15, 'پس او و سرنشینان کشتی را نجات دادیم، و آن [حادثه] را برای جهانیان، نشانه‌ی [عبرت] قرار دادیم.'),
(3356, 29, 16, 'و [یاد کن] ابراهیم را. آن‌گاه که به قومش گفت: «خدا را بپرستید و از او پروا کنید. این برای شما بهتر است، اگر بدانید.'),
(3357, 29, 17, 'شما به جای خدا بت‌هایی را می‌پرستید و [در نامگذاری آنها به خدایان و الهه‌ها،] دروغی را می‌سازید. کسانی که به جای خدا می‌پرستید، مالک رزق شما نیستند، پس روزی را نزد خدا بجویید و او را بپرستید و برای او شکرگزاری کنید که به سوی او بازگردانده می‌شوید.'),
(3358, 29, 18, 'و اگر [مرا] تکذیب کنید، امّت‌های پیش از شما [نیز پیامبرانشان را] تکذیب کردند. و بر پیامبر جز تبلیغ روشن، [مسئولیتی] نیست.»'),
(3359, 29, 19, 'آیا ندیده‌اند که خداوند چگونه آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند؟ البتّه این [کار] بر خداوند آسان است.'),
(3360, 29, 20, '[ای پیامبر!] بگو: «در زمین بگردید، پس بنگرید که [خداوند] چگونه آفرینش را آغاز کرده است؟ سپس خدا [به همین گونه] جهان آخرت را پدید می‌آورد. همانا خداوند بر هر کاری تواناست.'),
(3361, 29, 21, 'او هر که را بخواهد [و سزاوار ببیند]، عذاب می‌کند، و هر که را بخواهد [و شایسته بداند،] می‌بخشاید. و [همگی] به سوی او بازگردانده می‌شوید.'),
(3362, 29, 22, 'و شما هرگز نمی‌توانید [خدا را] ناتوان سازید [و از کیفر او بگریزید]، نه در زمین و نه در آسمان. و برای شما جز خداوند، ولیّ و یاوری نیست.»'),
(3363, 29, 23, 'و کسانی که به آیات خداوند و دیدار او [در قیامت] کفر ورزیدند، آنان از رحمت من نومیدند. و آنانند که برایشان عذابی دردناک خواهد بود.'),
(3364, 29, 24, 'پس پاسخ قوم ابراهیم تنها این بود که گفتند: «او را بکشید یا بسوزانید!» ولی خداوند او را از آتش [عظیمی که نمرودیان گرد آورده بودند،] نجات داد. همانا در این [ماجرا] برای گروهی که ایمان می‌آورند، نشانه‌هایی [از قدرت خداوند] است.'),
(3365, 29, 25, 'و [ابراهیم] گفت: «شما به جای خدا بت‌هایی را برگزیده‌اید که مایه‌ی پیوند و دوستی شما در زندگی دنیا باشد؛ امّا روز قیامت، یکدیگر را انکار می‌کنید و یکدیگر را لعنت خواهید کرد و جایگاه شما آتش است و یاوری نخواهید داشت.'),
(3366, 29, 26, 'پس لوط به ابراهیم ایمان آورد و گفت: «من به سوی پروردگارم روی آورده‌ام که او شکست‌ناپذیر و حکیم است.»'),
(3367, 29, 27, 'و اسحاق و [فرزندش] یعقوب را به ابراهیم بخشیدیم و در میان فرزندانش پیامبری و کتاب را قرار دادیم و پاداشِ او را در دنیا عطا کردیم و قطعاً او در آخرت از شایستگان است.'),
(3368, 29, 28, 'و [یاد کن] لوط را آن‌گاه که به قوم خود گفت: «شما به کاری زشت می‌پردازید که هیچ یک از جهانیان پیش از شما انجام نداده است؟!'),
(3369, 29, 29, 'شما به سراغ مردان می‌روید و راه [طبیعی زناشویی] را قطع می‌کنید و در مجالس خود، [آشکارا و بی‌پرده] کارهای ناپسند انجام می‌دهید؟» پس پاسخ قومش تنها این بود که گفتند: «اگر تو از راستگویانی، عذاب خدا را برای ما بیاور!»'),
(3370, 29, 30, '[لوط] گفت: «پروردگارا! مرا در برابر این قوم [فاسد و] تبهکار یاری فرما!»'),
(3371, 29, 31, 'و چون فرستادگان ما [که از فرشتگان بودند،] برای ابراهیم مژده‌ی [تولّد فرزند] آوردند، گفتند: «ما نابود کننده‌ی اهل این آبادی هستیم، زیرا مردم آن همواره ستمگر بوده‌اند.»'),
(3372, 29, 32, '[ابراهیم] گفت: «لوط در این آبادی است.» فرشتگان گفتند: «ما به کسانی که در اینجا هستند، آگاه‌تریم؛ قطعاً لوط و خاندانش را نجات می‌دهیم، جز زنش که از بازماندگان [در قهر خدا] است.»'),
(3373, 29, 33, 'و هنگامی که فرستادگان ما نزد لوط آمدند، از آمدنشان بد حال شد و دستش [از حمایت آن فرشتگان در برابر بدکاران] تنگ گردید. [فرشتگان] گفتند: «نترس و غمگین مباش! ما تو و خانواده‌ات را نجات می‌دهیم، مگر زنت، که از بازماندگان [در عذاب] است.'),
(3374, 29, 34, 'ما بر اهل این آبادی به خاطر فِسقی که داشتند، عذابی [سخت] از آسمان فروخواهیم ریخت.»'),
(3375, 29, 35, 'و از آن منطقه [غضب شده]، نشانه‌ای روشن [و درس عبرتی،] برای کسانی که می‌اندیشند، باقی گذاشتیم.'),
(3376, 29, 36, 'و به سوی [مردم] مَدین، برادرشان شعیب را [به رسالت فرستادیم]؛ پس گفت: «ای قوم من! خدا را بپرستید، و به روز قیامت امیدوار باشید. و در زمین [فتنه‌انگیزی و] فساد نکنید.»'),
(3377, 29, 37, 'پس شعیب را تکذیب کردند، [به این سبب،] زلزله [سختی] آنان را فراگرفت، و بامدادان در خانه‌های خود به رو درافتاده [و مرده] بودند.'),
(3378, 29, 38, 'و [قوم] عاد و ثمود را نیز [نابود کردیم]. و بی‌شک [بعضی] از خانه‌های [ویرانه‌ی] آنان برای شما آشکار است. و شیطان عمل‌های [زشت‌] آنان را برایشان آراست، پس آنان را از راه [خدا] بازداشت، در حالی که پیش‌تر [برای شناخت حقّ و باطل] بصیرت داشتند.'),
(3379, 29, 39, 'و قارون و فرعون و هامان را [نیز نابود کردیم]. و به راستی موسی همراهِ دلایلِ روشن [و معجزات] به سوی آنان آمد، [ولی] آنان در زمین تکبّر و سرکشی کردند، و [با این همه] نتوانستند [بر خدا] پیشی گیرند [و از عذاب الهی بگریزند].'),
(3380, 29, 40, 'پس هر یک [از آنان] را به [کیفر] گناهش گرفتار [عذاب] کردیم، پس بر بعضی از آنان باد شدید ریگ‌افشان فرستادیم. و بعضی از آنان را صیحه‌ی آسمانی [و بانگ مرگبار] فراگرفت. و برخی را در زمین فروبردیم. و بعضی دیگر را غرق کردیم. و چنان نبود که خداوند بر آنان ستم کند، بلکه خودشان بر خود ستم کردند.'),
(3381, 29, 41, 'مَثَلِ کسانی که غیر خدا را سرپرست خود برگزیدند، همانند مَثَل عنکبوت است که [برای خود] خانه‌ای ساخته، و البتّه اگر می‌دانستند، سست‌ترینِ خانه‌ها، خانه‌ی عنکبوت است.'),
(3382, 29, 42, 'خداوند آنچه را غیر از او می‌خوانند، هر چه باشد، می‌داند. او شکست‌ناپذیر و حکیم است.'),
(3383, 29, 43, 'و این مثل‌ها را برای مردم می‌زنیم؛ امّا جز دانشمندان و آگاهان، کسی آن را درک نمی‌کند.'),
(3384, 29, 44, 'خداوند آسمان‌ها و زمین را به حقّ آفرید. بی‌گمان در این آفرینش، برای مؤمنان نشانه‌ای قطعی است.'),
(3385, 29, 45, 'آنچه را از کتاب به تو وحی شده، تلاوت کن. و نماز را به‌پادار، که نماز [انسان را] از زشتی‌ها و کارهای ناپسند بازمی‌دارد. و البتّه یاد خدا بزرگ‌تر است. و خداوند آنچه را انجام می‌دهید، می‌داند.'),
(3386, 29, 46, 'و با اهل کتاب، جز با بهترین شیوه، مجادله نکنید، مگر ستمگرانشان. و [به آنان] بگویید: «ما به آنچه بر ما و شما نازل شده، ایمان داریم، و خدای ما و خدای شما یکی است، و ما تسلیم اوییم.»'),
(3387, 29, 47, 'و این‌گونه، کتاب را بر تو فروفرستادیم. پس [بعضی از] کسانی که کتاب به ایشان دادیم، به آن ایمان می‌آورند، و از این گروه [مشرکان نیز] کسانی به آن ایمان می‌آورند. و جز کافران [کسی] آیات ما را انکار نمی‌کند.'),
(3388, 29, 48, 'و تو پیش از این، هیچ نوشته‌ای را نمی‌خواندی، و آن را با دست خود نمی‌نوشتی، مبادا آنان که در صدد [تکذیب و] ابطال [سخنان] تو هستند، [در وحی بودن و حقّانیت قرآن‌] شک و تردید کنند.'),
(3389, 29, 49, 'بلکه آن [قرآن] آیاتی است روشن که در سینه‌های کسانی که به آنان معرفت داده شده، جای دارد. و فقط ستمگران، آیات ما را انکار می‌کنند.'),
(3390, 29, 50, 'و [کافرانِ بهانه‌جو] گفتند: «چرا بر او از طرف پروردگارش، معجزاتی [مانند عصای موسی] نازل نمی‌شود؟» بگو: «معجزات تنها نزد خداست [و به فرمان او نازل می‌شود، نه به میل دیگران]. و من فقط هشداردهنده‌ای آشکارم.»'),
(3391, 29, 51, 'آیا [این معجزه] برای آنان کافی نیست که ما کتاب را بر تو فروفرستادیم تا پیوسته بر آنان تلاوت شود؟ همانا در این کتاب، برای گروهی که ایمان آورند، رحمت، و تذکری قطعی است.'),
(3392, 29, 52, 'بگو: «همین بس که خداوند میان من و شما گواه است. آنچه را در آسمان‌ها و زمین است، می‌داند. و کسانی که [به جای حقّ،] به باطل ایمان آوردند و به خدا کفر ورزیدند، آنان همان زیانکارانند.»'),
(3393, 29, 53, 'آنان با شتاب از تو عذاب می‌خواهند. و اگر مهلت معین [برای آنان مقرّر] نبود، قطعاً عذاب به سراغشان می‌آمد، [ولی بدانند] عذاب، ناگهان به سراغشان خواهد آمد، در حالی که از آن غافلند.'),
(3394, 29, 54, 'آنان با شتاب از تو عذاب می‌خواهند، و حال آن که جهنّم بر کافران احاطه دارد.'),
(3395, 29, 55, 'روزی که عذاب از بالای سرشان و از زیر پاهایشان آنان را فراگیرد. [در آن روز خداوند] می‌فرماید: «بچشید [جزای] کارهایی را که انجام داده‌اید!»'),
(3396, 29, 56, 'ای بندگانِ باایمانِ من! زمین من گسترده است، پس [اگر در سرزمینتان شما را به شرک مجبور می‌کنند، مهاجرت کنید و] تنها مرا بپرستید.'),
(3397, 29, 57, 'هر کسی [طعم] مرگ را می‌چشد! سپس به سوی ما بازگردانده می‌شوید.'),
(3398, 29, 58, 'و قطعاً کسانی را که ایمان آوردند و کارهای شایسته کردند، در غرفه‌هایی از بهشت جای می‌دهیم که نهرها از زیر آنها جاری است؛ همیشه در آنجا خواهند بود. چه خوب است پاداش عمل‌کنندگان!'),
(3399, 29, 59, 'کسانی که [در برابر مشکلات] صبر [و استقامت] کردند و بر پروردگارشان توکل می‌کنند.'),
(3400, 29, 60, 'و چه بسا جنبنده‌ای که نمی‌تواند روزی خود را تأمین کند. خداست که به او و به شما روزی می‌دهد. و اوست شنوای دانا.'),
(3401, 29, 61, 'و اگر از آنان [که مشرکند،] بپرسی: «چه کسی آسمان‌ها و زمین را آفرید، و خورشید و ماه را تسخیر کرد؟» حتماً خواهند گفت: «خدا.» پس به کجا منحرف می‌شوند؟'),
(3402, 29, 62, 'خداوند برای هر کس از بندگانش که بخواهد، روزی را می‌گسترد و یا تنگ می‌کند. همانا خداوند به هر چیزی آگاه است.'),
(3403, 29, 63, 'و اگر از آنان [که شرک می‌ورزند،] بپرسی: «چه کسی از آسمان، آبی فرستاد که با آن، زمین را پس از مرگش زنده کرد؟» حتماً خواهند گفت: «خدا.» بگو: «ستایش مخصوص خداست؛ امّا بیشترِ آنان نمی‌اندیشند.»'),
(3404, 29, 64, 'این زندگی دنیا، چیزی جز سرگرمی و بازیچه نیست. و اگر می‌دانستند، زندگی حقیقی، همان سرای آخرت است.'),
(3405, 29, 65, 'پس هنگامی که بر کشتی سوار شوند [و خطر را احساس کنند،] خدا را با اخلاص می‌خوانند، [و غیر او را فراموش می‌کنند،] پس همین که [خداوند] آنان را به سوی خشکی رساند و نجات داد، باز به شرک برمی‌گردند.'),
(3406, 29, 66, '[بگذار] تا به آنچه به آنان داده‌ایم، ناسپاسی کنند و [از لَذّت‌های زودگذر زندگی] بهره‌مند شوند؛ امّا به زودی خواهند فهمید.'),
(3407, 29, 67, 'آیا ندیدند که ما [شهرشان مکه را] حرمی امن قرار دادیم [که در آن با آسایش و آرامش زندگی می‌کنند]؟ و حال آن که [بیرون از شهر مکه،] مردم از اطرافشان ربوده می‌شوند. پس آیا [با داشتن این نعمت، باز] به باطل ایمان می‌آورند و به نعمت خداوند ناسپاسی می‌ورزند؟'),
(3408, 29, 68, 'و کیست ستمکارتر از کسی که بر خدا دروغ بندد، یا هنگامی که حقّ به سراغش آمد، آن را تکذیب کند؟ آیا در دوزخ برای کافران، جایگاهی نیست؟'),
(3409, 29, 69, 'و کسانی که در [راه] ما [خالصانه با نفس خود یا دشمن ما] جهاد کردند، بی‌تردید آنان را به راه‌های خود، راهنمایی می‌کنیم. و همانا خداوند با نیکوکاران است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف، لام، میم.'),
(3411, 30, 2, 'رومیان [از ایرانیان] شکست خوردند،'),
(3412, 30, 3, '[این شکست،] در نزدیک‌ترین سرزمین [رخ داد]؛ ولی آنان بعد از مغلوب شدن، در آینده‌ی نزدیکی پیروز خواهند شد.'),
(3413, 30, 4, '[این پیروزی،] در چند سال [آینده خواهد بود]، پیش از این و بعد از این، کار، تنها به دست خداست. و در آن روز مؤمنان [با تحقّق یافتن پیشگویی الهی، اطمینان می‌یابند و] شادمان خواهند شد،'),
(3414, 30, 5, 'به سبب نصرت و یاری خدا. [آری، او] هر کس را بخواهد یاری می‌دهد، و او شکست‌ناپذیر و مهربان است.'),
(3415, 30, 6, '[این،] وعده‌ی الهی است. خداوند از وعده‌اش تخلّف نمی‌کند؛ ولی بیشتر مردم نمی‌دانند.'),
(3416, 30, 7, '[مردم] فقط ظاهری از زندگی دنیا را می‌شناسند، و آنان از آخرت غافلند.'),
(3417, 30, 8, 'آیا آنان با خود نیندیشیده‌اند [که‌] خداوند آسمان‌ها و زمین، و آنچه را میان آن دو است، جز بر اساس حقّ و برنامه زمانی معینی نیافریده است؟ و بی‌شک بسیاری از مردم به دیدار پروردگارشان [در قیامت] کفر می‌ورزند.'),
(3418, 30, 9, 'آیا در زمین نگشتند تا ببینند عاقبت کسانی که پیش از آنان بودند، چگونه بود؟ آنها نیرومندتر از اینان بودند و زمین را زیر و رو کردند، و بیش از اینان، آن را آباد ساختند. و پیامبرانشان همراه با دلایل روشن به سراغشان آمدند، [امّا آنها انکار کردند و به قهر خدا گرفتار شدند،] پس خدا بر آن نبود که به آنان ستم کند، بلکه آنها خود به خویش ستم می‌کردند.'),
(3419, 30, 10, 'سپس سرانجامِ کسانی که زشت‌کاری کردند، این شد که آیات خدا را تکذیب کردند و همواره آن را به مسخره می‌گرفتند.'),
(3420, 30, 11, 'خداوند آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، آن‌گاه به سوی او بازگردانده می‌شوید.'),
(3421, 30, 12, 'و روزی که قیامت برپا می‌شود، تبهکاران نومید و غمگین می‌شوند.'),
(3422, 30, 13, 'و برای آنان از شریکانشان شفیعانی نخواهد بود، و به شریکان خود کافر می‌شوند.'),
(3423, 30, 14, 'و روزی که قیامت برپا می‌شود، در آن روز [مردم] از هم جدا می‌شوند.'),
(3424, 30, 15, 'پس کسانی که ایمان آوردند و کارهای نیکو کردند، آنان در باغی [از بهشت] شادمان خواهند بود.'),
(3425, 30, 16, 'و امّا کسانی که کفر ورزیدند و آیات ما و دیدار آخرت را تکذیب کردند، پس آنها به عذاب فراخوانده می‌شوند.'),
(3426, 30, 17, 'پس پاکی سزاوار خداست، هنگامی که وارد شب می‌شوید و هنگامی که به صبح درمی‌آیید.'),
(3427, 30, 18, 'و سپاس و ستایش در آسمان‌ها و زمین، مخصوص اوست. و [نیز] در پایان روز، و آن‌گاه که به نیمروز درمی‌آیید.'),
(3428, 30, 19, '[خداوند] زنده را از مرده بیرون می‌آورد، و مرده را از زنده بیرون می‌کشد. و زمین را پس از مرگش، [در بهار] زنده می‌کند، و شما نیز [روز قیامت] این‌گونه [از خاک] بیرون آورده می‌شوید.'),
(3429, 30, 20, 'و از نشانه‌های او آن است که شما را از خاک آفرید، سپس [به صورت] بشری به هر سوی پراکنده شدید.'),
(3430, 30, 21, 'و از نشانه‌های او آن است که از [جنس] خودتان، همسرانی برای شما آفرید تا در کنار آنان آرامش یابید. و میان شما علاقه‌ی شدید و رحمت قرار داد. بی‌شک در این [نعمت،] برای گروهی که می‌اندیشند، نشانه‌هایی است.'),
(3431, 30, 22, 'و از نشانه‌های او، آفرینش آسمان‌ها و زمین، و تفاوت زبان‌ها و رنگ‌های شماست. همانا در این امر، برای دانشمندان نشانه‌هایی است.'),
(3432, 30, 23, 'و از نشانه‌های او، خوابیدن شما، و تلاش شما در کسب فضل پروردگار، در شب و روز است. همانا در این امر، برای گروهی که [حقایق را] می‌شنوند، نشانه‌هایی است.'),
(3433, 30, 24, 'و از نشانه‌های او آن است که برق [آسمان] را که مایه‌ی بیم و امید است، به شما نشان می‌دهد. و از آسمان، آبی می‌فرستد که زمین را پس از مردنش زنده می‌کند. همانا در این امر، برای گروهی که می‌اندیشند، نشانه‌هایی است.'),
(3434, 30, 25, 'و از نشانه‌های او آن است که آسمان و زمین به فرمان او برپاست، پس هرگاه شما را از زمین فراخواند، ناگهان بیرون می‌آیید [و در صحنه‌ی قیامت حاضر می‌شوید].'),
(3435, 30, 26, 'و هر که در آسمان‌ها و زمین است، از آنِ اوست، همه برای او فرمانبردارند.'),
(3436, 30, 27, 'و اوست کسی که آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند. و این کار برای او [از آفرینش نخستین] آسان‌تر است. و قدرت برتر در آسمان‌ها و زمین مخصوص اوست. و او شکست‌ناپذیر، و دانا به حقایق امور است.'),
(3437, 30, 28, 'او برای شما، مثالی از خودتان زده است: آیا [اگر بردگانی داشته باشید،] آنان را در آنچه ما روزی شما کردیم، شریک می‌کنید، به گونه‌ای که با هم برابر و یکسان باشید؟ آیا همان‌طور که [شما افراد آزاد، در تصرّف اموال] از یکدیگر می‌ترسید از بردگانتان می‌ترسید؟! [پس چگونه مخلوقات را که مملوک او هستید، شریک او می‌دانید؟] ما آیات خود را برای گروهی که می‌اندیشند، این گونه شرح می‌دهیم.'),
(3438, 30, 29, 'آری، کسانی که ستم کردند، بدون آگاهی، پیرو هوس‌های خود شدند. پس کسی را که خدا گمراهش کرد، چه کسی می‌تواند هدایت کند؟ برای آنان هیچ یاوری نیست.'),
(3439, 30, 30, 'پس با گرایش به حقّ، به این دین روی بیاور! [این] فطرت الهی است که خداوند مردم را بر اساس آن آفریده است. برای آفرینش الهی دگرگونی نیست. این است دین پایدار، ولی بیشتر مردم نمی‌دانند.'),
(3440, 30, 31, '[شما نیز بر اساس همان فطرت]، به سوی او روی آورید و از [نافرمانی] او بپرهیزید. و نماز را برپا کنید و از مشرکان نباشید،'),
(3441, 30, 32, 'از کسانی که دین خود را جدا و پراکنده کردند و به دسته‌ها و فرقه‌ها تقسیم شدند. و هر گروهی به آنچه نزد آنهاست، شادمانند.'),
(3442, 30, 33, 'و هرگاه زیانی به مردم رسد، پروردگار خود را می‌خوانند و به سوی او روی می‌آورند؛ امّا چون خداوند از سوی خود رحمتی به آنان بچشاند، گروهی از آنان به پروردگارشان شرک می‌ورزند.'),
(3443, 30, 34, 'تا آنکه در آنچه به ایشان بخشیده‌ایم، ناسپاسی پیشه کنند. پس [چند روزی از نعمت‌های زودگذر دنیا] بهره‌مند شوید که به زودی خواهید دانست [سرانجام کفر و ناسپاسی چیست].'),
(3444, 30, 35, 'آیا بر ایشان حجّت و دلیلی نازل کرده‌ایم که درباره آنچه بدان شرک می‌ورزند، سخن بگوید [و آن را موجّه بداند]؟!'),
(3445, 30, 36, 'و هر گاه به مردم رحمتی بچشانیم، به آن شاد می‌شوند. و اگر به خاطر دستاورد گذشته [و اعمال بد] آنها، ناگواری به آنان برسد، ناگهان مأیوس می‌شوند.'),
(3446, 30, 37, 'آیا ندیدند که خداوند برای هر کس بخواهد روزی را گسترش می‌دهد و یا تنگ می‌سازد؟ البتّه در این امر برای کسانی که ایمان می‌آورند، نشانه‌هایی است.'),
(3447, 30, 38, '[اکنون که دانستی رزق به دست خداست،] پس حقّ خویشاوند و تنگدست و در راه مانده را ادا کن! این کار برای کسانی که خواهان خشنودی خدا هستند، بهتر است و آنان همان رستگارانند.'),
(3448, 30, 39, 'و آنچه شما به [رسم] ربا دادید که بر اموال مردم [رباخوار] افزوده شود، پس نزد خداوند فزونی نخواهد یافت. و آنچه را به عنوان زکات می‌پردازید و رضای خدا را می‌طلبید، پس آنان [پاداشی] چند برابر دارند.'),
(3449, 30, 40, 'خداوند کسی است که شما را آفرید، سپس به شما روزی داد، آن‌گاه شما را می‌میراند، سپس زنده می‌کند. آیا از شریک‌هایی که شما برای خدا گرفته‌اید، کسی هست که ذرّه‌ای از این کارها را انجام دهد؟ او منزّه و برتر است از آنچه شریکش قرار می‌دهند.'),
(3450, 30, 41, 'به خاطر کارهایی که مردم کرده‌اند، فساد در خشکی و دریا آشکار شده است، تا [خداوند] کیفر بعضی اعمالشان را به آنان بچشاند، شاید [به سوی حقّ] بازگردند.'),
(3451, 30, 42, 'بگو: «در زمین بگردید، پس بنگرید عاقبت کسانی که پیش از شما [زندگی می‌کردند و] بیشترشان مشرک بودند، چگونه بود؟»'),
(3452, 30, 43, 'پس به دین استوار ایمان بیاور، پیش از آن که روزی بیاید که برای آن برگشتی از [قهر] خدا ممکن نیست. در آن روز، مردم از هم جدا [و دسته دسته] می‌شوند.'),
(3453, 30, 44, 'هر کس کفر ورزد، کفرش به زیان اوست. و کسانی که کارهای شایسته کنند، [سعادت ابدی را] برای خود آماده می‌کنند.'),
(3454, 30, 45, 'تا [خداوند] کسانی را که ایمان آوردند و کارهای شایسته کردند، از فضل خویش پاداش دهد، [و کافران را محروم کند، زیرا] او کافران را دوست نمی‌دارد.'),
(3455, 30, 46, 'و از نشانه‌های او این است که بادها را مژده‌رسان [پیشاپیش باران] می‌فرستد، تا از رحمتش به شما بچشاند و کشتی‌ها به فرمانش حرکت کنند، و شما از فضل او [روزی] بجویید، شاید شکرگزاری کنید.'),
(3456, 30, 47, 'و البتّه ما پیش از تو پیامبرانی را به سوی قومشان فرستادیم، پس آنان دلایل روشن برای مردم آوردند، [عده‌ای کافر شدند و برخی مؤمن،] پس، از مجرمان انتقام گرفتیم، و یاری مؤمنان حقّی است بر عهده‌ی ما.'),
(3457, 30, 48, 'خداوند کسی است که بادها را می‌فرستد و ابری برمی‌انگیزد و آن را در آسمان، هرگونه بخواهد، می‌گستراند، و آن را بخش‌بخش می‌کند، پس می‌بینی که باران از لابه‌لای آن بیرون می‌زند. پس چون آن [باران] را به کسانی از بندگانش که بخواهد برساند، آنان شادمان می‌شوند.'),
(3458, 30, 49, 'و هر چند پیش از آن که بر آنان فروفرستاده شود، [آری،] پیش از آن، نومید بودند.'),
(3459, 30, 50, 'پس به آثار رحمت خدا بنگر که چگونه زمین را بعد از مرگش زنده می‌کند. همانا خداست زنده‌کننده‌ی مردگان. و او بر هر چیزی تواناست.'),
(3460, 30, 51, 'و اگر بادی [زیانبار] بفرستیم و آن [زمین سرسبز] را زرد شده ببینند، پس از آن [به سبب این آسیب،‌] ناسپاس می‌شوند.'),
(3461, 30, 52, '[ای پیامبر! دل‌های اینان مرده است،] پس تو نمی‌توانی مردگان را شنوا کنی، و این دعوت را به گوش کران برسانی، آن‌گاه که پشت‌کنان روی می‌گردانند.'),
(3462, 30, 53, 'و تو هدایت‌کننده‌ی کوردلان از گمراهی‌شان نیستی. تنها سخنت را به گوش کسانی می‌رسانی که به آیات ما ایمان دارند و اهل تسلیم هستند.'),
(3463, 30, 54, 'خداست که شما را از ناتوانی آفرید، سپس بعد از ناتوانی، [در جوانی] قوّتی بخشید، آن‌گاه بعد از توانایی و قوّت، ضعف و پیری قرار داد. او هر چه بخواهد، می‌آفریند. و اوست دانای توانا.'),
(3464, 30, 55, 'و روزی که قیامت برپا شود، مجرمان سوگند یاد می‌کنند که ساعتی بیشتر درنگ نکرده‌اند. [آنان در دنیا نیز] این‌گونه [ناحقّ سوگند می‌خوردند و] به دروغ کشانده می‌شدند.'),
(3465, 30, 56, 'و کسانی که علم و ایمان به آنان داده شده [به مجرمان] می‌گویند: «قطعاً شما [به موجب آنچه] در کتاب خداست، تا روز رستاخیز [در برزخ] مانده‌اید، پس این، روز رستاخیز است، ولی شما نمی‌دانستید [که قیامت حقّ است].»'),
(3466, 30, 57, 'پس در چنین روزی، پوزش کسانی که ستم کردند، سودی ندارد، و از آنان خواسته نمی‌شود که پوزش بطلبند.'),
(3467, 30, 58, 'و به راستی در این قرآن، برای مردم از هرگونه مثلی آوردیم. و اگر برای آنان نشانه و معجزه‌ای بیاوری، کافران حتماً خواهند گفت: «شما جز بر باطل نیستید [و این‌ها سحر و جادوست].»'),
(3468, 30, 59, 'این‌گونه، خداوند بر دل‌های کسانی که معرفت ندارند، مهر می‌زند.'),
(3469, 30, 60, 'پس صبر پیشه کن که همانا وعده‌ی خدا [بر نصرت تو] حقّ است. و کسانی که [به راه حقّ] یقین ندارند، تو را به خفّت و سبکی نکشانند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف، لام، میم.'),
(3471, 31, 2, 'این، آیاتِ کتابِ سراسر حکمت است.'),
(3472, 31, 3, 'که [مایه‌ی] هدایت و رحمت، برای نیکوکاران است.'),
(3473, 31, 4, 'آنان که نماز به‌پا می‌دارند و زکات می‌پردازند، و تنها آنان به آخرت یقین دارند.'),
(3474, 31, 5, 'آنان بر [راه] هدایتی از [سوی] پروردگارشان هستند. و هم آنانند رستگاران.'),
(3475, 31, 6, 'و برخی از مردم، خریدار سخنان بیهوده و سرگرم‌کننده‌اند، تا بی‌هیچ دانشی، [دیگران را] از راه خدا گمراه کنند و آن را به مسخره گیرند؛ برای آنان عذابی خوارکننده است.'),
(3476, 31, 7, 'و هرگاه آیات ما بر او خوانده شود، مستکبرانه روی برگرداند، چنان که گویی آن را نشنیده، گویا گوش‌های او سنگین است، پس او را به عذابی دردناک بشارت ده!'),
(3477, 31, 8, 'کسانی که ایمان آوردند و کارهای شایسته کرده‌اند، [در بهشت،] باغ‌های پرنعمت دارند.'),
(3478, 31, 9, 'در آن جاوِدانه‌اند. وعده‌ی الهی حقّ است. و اوست شکست‌ناپذیرِ حکیم.'),
(3479, 31, 10, 'او آسمان‌ها را بدون ستونی که آن را ببینید، آفرید. و در زمین کوه‌هایی افکند تا زمین شما را نلرزاند و در آن از هر جنبنده‌ای پراکند و از آسمان، آبی فروفرستادیم، پس [با آن‌] در زمین [انواع گوناگونی] از جفت‌های [گیاهان] نیکو و پرارزش رویاندیم.'),
(3480, 31, 11, '«این آفرینشِ خداست، پس به من نشان دهید که غیر او چه آفریده‌اند؟ آری، ستمگران [مشرک] در گمراهی آشکارند.»'),
(3481, 31, 12, 'و ما به لقمان حکمت دادیم، [و به او گفتیم:] که شکر خدا را به جای آور! و هر کس شکر کند، بی‌شک به سود خویش شکر کرده؛ و هر کس ناسپاسی کند، بی‌تردید خداوند بی‌نیاز و ستوده است.'),
(3482, 31, 13, 'و [یاد کن] هنگامی که لقمان به پسرش ـ در حالی که به او اندرز می‌داد ـ گفت: «پسر عزیزم! چیزی را همتای خدا قرار نده، که همانا شرک ستمی بزرگ است.»'),
(3483, 31, 14, 'و ما به انسان درباره‌ی [نیکی به] پدر و مادرش سفارش کردیم. مادرش او را [در دوران بارداری،] حمل کرد، در حالی که هر روز ناتوان‌تر می‌شد، [و شیر دادن] و از شیر گرفتنش در دو سال است. [به او سفارش کردیم که] برای من و پدر و مادرت سپاس‌گزار. بازگشت [همه] به سوی من است.'),
(3484, 31, 15, 'و اگر آن دو تلاش کردند چیزی را که بدان آگاهی نداری، شریک من سازی، از آنان فرمان مبر؛ ولی با آنان در دنیا به نیکی رفتار کن! و راه کسی را پیروی کن که به سوی من بازآمده است. پس بازگشت شما به سوی من است. من شما را به آنچه عمل می‌کردید، آگاه خواهم ساخت.'),
(3485, 31, 16, '[لقمان فرزندش را نصیحت کرد:] «پسر عزیزم! اگر [عمل تو] همسنگ دانه‌ی خردلی باشد، و در دل تخته‌سنگی، یا در آسمان‌ها، یا در زمین نهفته باشد، خداوند آن را [در قیامت] می‌آورد؛ همانا خداوند دقیق و آگاه است.'),
(3486, 31, 17, 'فرزندم! نماز را برپا دار و امر به معروف و نهی از منکر کن، و بر آنچه از سختی‌ها به تو می‌رسد، شکیبا باش، همانا این [صبر] از امور واجب و مهمّ است.'),
(3487, 31, 18, 'و روی خود را [به تکبّر] از مردم برمگردان، و در زمین با ناز و غرور راه مرو، زیرا خداوند هیچ خودپسند فخرفروشی را دوست ندارد.'),
(3488, 31, 19, 'و در راه رفتن [و رفتار]، میانه‌رو باش. و از صدایت بکاه، که ناخوش‌ترینِ صداها، آوازِ خراناست.»'),
(3489, 31, 20, 'آیا ندیدید که خداوند آنچه را در آسمان‌ها و آنچه را در زمین است، برای شما مسخّر کرده، و نعمت‌های ظاهری و باطنی خود را فراوان و کامل بر شما ارزانی داشته است؟ ولی بعضی از مردم بدون هیچ دانش و هدایتی، و بدون هیچ کتاب روشنگری، درباره‌ی خداوند به جدال و ستیز می‌پردازند.'),
(3490, 31, 21, 'و هرگاه به آنان گفته شود: «از آنچه خدا نازل کرده، پیروی کنید.» می‌گویند: «بلکه ما از چیزی پیروی می‌کنیم که پدرانمان را بر آن یافته‌ایم.» آیا اگر شیطان آنان را به عذاب فروزان فراخواند [باید از او پیروی کنند]؟!'),
(3491, 31, 22, 'و هر کس روی خود را به سوی خدا کند، و نیکوکار باشد، پس بی‌تردید به دستگیره محکمی چنگ زده، و پایان همه‌ی کارها به سوی اوست.'),
(3492, 31, 23, '[ای پیامبر!] هر کس کفر ورزید، کفرش تو را اندوهگین نکند. بازگشت آنان به سوی ماست، و ما آنان را به عملکردشان آگاه خواهیم کرد. قطعاً خداوند به آنچه در سینه‌هاست، آگاه است.'),
(3493, 31, 24, 'آنان را [در دنیا] اندکی بهره‌مند می‌کنیم، سپس به عذاب سختی گرفتارشان خواهیم ساخت.'),
(3494, 31, 25, 'و اگر از آنان بپرسی: «چه کسی آسمان‌ها و زمین را آفریده است؟» حتماً خواهند گفت: «خدا.» بگو: «ستایش از آنِ خداست.» ولی بیشتر مردم نمی‌دانند.'),
(3495, 31, 26, 'آنچه در آسمان‌ها و زمین است، از آنِ خداست. همانا او بی‌نیاز و ستوده است.'),
(3496, 31, 27, 'و اگر آنچه درخت در زمین است، قلم شوند، و دریا [مرکب شود] و از پسِ آن هفت دریا، به کمک آیند، [تا کلمات خدا را بنویسند،] کلمات خدا به پایان نرسد. خداوند شکست‌ناپذیر و حکیم است.'),
(3497, 31, 28, 'آفرینش و رستاخیز [همه‌ی] شما [نزد خدا چیزی] جز مانند [آفریدن و زنده کردن] یک تن نیست. خداوند شنوا و بیناست.'),
(3498, 31, 29, 'آیا ندیدی که خداوند، پیوسته شب را در روز و روز را در شب داخل می‌کند و خورشید و ماه را تسخیر کرده که هر یک تا زمان معینی در حرکتند؟ خداوند به آنچه می‌کنید، آگاه است.'),
(3499, 31, 30, 'این [همه]، دلیل بر آن است که خداوند، حقّ و آنچه جز او می‌خوانند، باطل است و خداوند بلندمرتبه و بزرگ است.'),
(3500, 31, 31, 'آیا ندیدی کشتی‌ها در سایه‌ی نعمت خداوند در دریا حرکت می‌کنند، تا خدا بخشی از آیات [قدرت] خود را به شما نشان دهد؟ در این [حرکت کشتی‌ها]، برای هر شکیبای سپاس‌گزاری، نشانه‌هایی است.'),
(3501, 31, 32, 'و هرگاه موجی [خروشان] همچون ابرهای سایه‌افکن، آنان را بپوشاند، خدا را خالصانه می‌خوانند. پس چون خداوند آنان را به خشکی [رساند و] نجات دهد، بعضی راه اعتدال پیش می‌گیرند [و بعضی راه انحراف،] و جز پیمان‌شکنِ ناسپاس، آیات ما را انکار نمی‌کند.'),
(3502, 31, 33, 'ای مردم! از پروردگارتان پروا کنید و بترسید از روزی که هیچ پدری به کار فرزندش نمی‌آید و هیچ فرزندی به کار پدرش نخواهد آمد. قطعاً وعده‌ی خداوند حقّ است، پس زندگانی دنیا شما را نفریبد و [شیطانِ] فریبکار، شما را به [لطف و رحمت‌] خدا مغرور نکند.'),
(3503, 31, 34, 'بی‌گمان آگاهی از زمان [برپایی قیامت،] مخصوص خداست و اوست که باران را فرومی‌فرستد و آنچه را در رحم‌هاست، می‌داند و هیچ‌کس نمی‌داند که فردا چه به دست می‌آورد و هیچ‌کس نمی‌داند که درچه سرزمینی خواهد مرد. خداوند دانا و آگاه است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف، لام، میم.'),
(3505, 32, 2, 'فروفرستادن این کتاب که شکی در آن نیست، از سوی پروردگارِ جهانیان است.'),
(3506, 32, 3, 'آیا می‌گویند: «[محمّد] آن را به دروغ ساخته [و به خدا نسبت داده] است!» [چنین نیست،‌] بلکه آن همان حقّ است از سوی پروردگارت، تا قومی را که پیش از تو هیچ هشداردهنده‌ای برایشان نیامده است، بیم دهی، تا شاید هدایت یابند.'),
(3507, 32, 4, 'خداوند، کسی است که آسمان‌ها و زمین و آنچه را میان آن دو است، در شش روز [و دوران] آفرید، سپس بر عرش [قدرت و تدبیر جهان] قرار گرفت. برای شما جز او هیچ یاور و شفاعت‌کننده‌ای نیست. پس آیا متذکر نمی‌شوید [و پند نمی‌گیرید]؟'),
(3508, 32, 5, '[خداوند] امر [این جهان] را از آسمان به سوی زمین تدبیر می‌کند، سپس در روزی که مقدار آن هزار سال از سال‌هایی است که می‌شمرید، [نظام این عالم برچیده می‌شود و] به سوی او بالا می‌رود.'),
(3509, 32, 6, 'اوست [خداوندی] که از پنهان و آشکار آگاه، [و] شکست‌ناپذیر و مهربان است.'),
(3510, 32, 7, 'کسی که هر چه را آفرید، نیکو آفرید و آفرینش انسان را از گِل آغاز کرد.'),
(3511, 32, 8, 'سپس نسل او را از عصاره‌ای از آب پست و بی‌مقدار مقرّر فرمود.'),
(3512, 32, 9, 'سپس [اندام] او را درست و تمام ساخت و از روح خویش در وی دمید و برای شما گوش و چشم‌ها و دل‌ها قرار داد، [ولی] اندکی از شما سپاس می‌گزارید.'),
(3513, 32, 10, 'و گفتند: «آیا همین که در زمین [دفن و پوسیده و] گم شدیم، آفرینش تازه‌ای خواهیم یافت؟» آری، آنان به ملاقات پروردگارشان کافرند.'),
(3514, 32, 11, 'بگو: «فرشته‌ی مرگ که بر شما گمارده شده، جانتان را می‌گیرد، سپس به سوی پرودگارتان برگردانده می‌شوید.»'),
(3515, 32, 12, 'و اگر ببینی مجرمان را، هنگامی که نزد پروردگارشان سرهای خود را به زیر افکنده‌اند [و می‌گویند:] «پروردگارا! دیدیم و شنیدیم [آنچه را وعده کرده بودی]، پس ما را [به دنیا] بازگردان تا کاری شایسته کنیم، ما به یقین رسیدیم.»'),
(3516, 32, 13, 'و اگر می‌خواستیم، هر کس را [به اجبار] به هدایت می‌رساندیم؛ ولی [می‌خواهیم که هر کس، خود راهش را برگزیند.] اراده‌ی قطعی من چنین است که دوزخ را از جنّ و انس [که گمراهی را بر راه راست برگزیدند،] پر خواهم کرد.'),
(3517, 32, 14, 'پس [عذاب را] بچشید! به خاطر آن که دیدار چنین روزی را فراموش کردید، ما شما را به فراموشی سپردیم. و به خاطر آنچه می‌کردید، عذاب همیشگی را بچشید.'),
(3518, 32, 15, 'تنها کسانی به آیات ما ایمان دارند که هرگاه این آیات به آنان یادآوری شود، به سجده می‌افتند و با ستایش پروردگارشان تسبیح می‌گویند، و آنان تکبّر نمی‌ورزند.'),
(3519, 32, 16, 'مؤمنان، [به هنگام شب،] پهلوهایشان از بسترها دور می‌ماند، [برای عبادت برمی‌خیزند و] پروردگارشان را از روی بیم و امید می‌خوانند، و از آنچه به آنان روزی داده‌ایم، انفاق می‌کنند.'),
(3520, 32, 17, 'هیچ‌کس نمی‌داند چه پاداش مهمّی که مایه‌ی روشنی چشم‌هاست، برای آنان نهفته است. این پاداش کارهایی است که می‌کرده‌اند.'),
(3521, 32, 18, 'پس آیا کسی که مؤمن است، مانند کسی است که فاسق است؟ [هرگز! این دو گروه] برابر نیستند.'),
(3522, 32, 19, 'امّا کسانی که ایمان آوردند و کارهای نیکو کردند برای پذیرایی از آنها جایگاهی از باغ‌ها[ی بهشت فراهم] است، به خاطر آنچه می‌کرده‌اند.'),
(3523, 32, 20, 'و امّا کسانی که [از مدار ایمان خارج و] فاسق شدند، پس جایگاهشان آتش است. هرگاه اراده کنند که از آن بیرون آیند، به آن بازگردانده می‌شوند و به آنان گفته می‌شود: «بچشید عذاب آتشی را که همواره تکذیبش می‌کردید.»'),
(3524, 32, 21, 'و البتّه غیر از آن عذاب بزرگ‌تر [در قیامت]، از عذاب نزدیک [در این دنیا نیز] به آنان می‌چشانیم، تا شاید به سوی خدا بازگردند.'),
(3525, 32, 22, 'و کیست ستمکارتر از کسی که آیات پروردگارش را به او یادآوری کنند، ولی از آنها روی گرداند؟ البتّه ما از تبهکاران انتقام می‌گیریم.'),
(3526, 32, 23, 'و به راستی ما به موسی کتاب [تورات] دادیم، پس در دیدار او [و دریافت آیات الهی] شک نداشته باش، ما آن [کتاب] را برای بنی‌اسرائیل مایه‌ی هدایت قرار دادیم.'),
(3527, 32, 24, 'و از میان آنان پیشوایانی قرار دادیم که به فرمان ما [مردم را] هدایت کنند. چون آنان، هم صبر کردند و هم به آیات ما همواره یقین داشتند.'),
(3528, 32, 25, 'بی‌گمان پروردگارت در قیامت میان مردم، در آنچه همواره اختلاف داشتند، داوری خواهد کرد.'),
(3529, 32, 26, 'آیا برایشان روشن نشده که ما نسل‌های بسیاری را پیش از ایشان نابود کردیم؟ با این که این‌ها در خانه‌های آن نابودشدگان رفت و آمد دارند. البتّه در این [هلاکت‌ها و جایگزین شدن‌ها، عبرت‌ها و] نشانه‌هایی است، آیا نمی‌شنوند؟'),
(3530, 32, 27, 'آیا ندیدند که ما آب را به سوی زمین بی‌گیاه می‌رانیم، پس با آن، محصولاتی را می‌رویانیم تا چهارپایانشان و خودشان از آن بخورند؟ پس آیا نمی‌بینند؟'),
(3531, 32, 28, 'و کافران می‌گویند: «اگر راست می‌گویید، این پیروزی [شما] چه زمانی است؟!»'),
(3532, 32, 29, 'بگو: «روز پیروزی، ایمان آوردنِ کافران، به حالشان سودی نخواهد داشت، و به آنان مهلت داده نخواهد شد.»'),
(3533, 32, 30, 'پس از آنان روی برگردان. و منتظر [فرارسیدن پیروزی‌] باش، که آنان نیز [به گمان خود] منتظرند [که بر شما پیروز شوند].');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ای پیامبر! از خداوند پروا کن! و از کافران و منافقان اطاعت مکن که خداوند همواره دانایی حکیم است.'),
(3535, 33, 2, 'و از آنچه از سوی پروردگارت به تو وحی می‌شود، پیروی کن که خداوند به آنچه انجام می‌دهید، آگاه است.'),
(3536, 33, 3, 'و بر خداوند توکل کن! و همین بس که خداوند کارساز و نگهبان [تو] است.'),
(3537, 33, 4, 'خداوند برای هیچ مردی در درونش دو دل قرار نداده است. و هرگز همسرانی را که نسبت به آنان ظِِهار می‌کنید، [و با تشبیه آنها به مادرانتان، آنان را بر خود حرام می‌کنید،] مادران شما نگردانیده، [و نیز] پسرخوانده‌های شما را پسران [واقعی] شما قرار نداده است. این سخن [باطل و بی‌پایه‌] شماست که شما به زبان می‌گویید. و خداوند حقّ می‌گوید. و اوست که به راه [راست] هدایت می‌کند.'),
(3538, 33, 5, 'آنان را [که پسرخوانده‌ی شمایند،] به نام پدرانشان بخوانید، که این نزد خداوند عادلانه‌تر است. پس اگر پدرانشان را نمی‌شناسید، آنان برادران دینی و وابستگان شما هستند. و در آنچه [پیش از این] خطا کردید، [و پسرخواندگان را فرزند خود دانستید،] گناهی بر شما نیست؛ ولی در آنچه دل‌هایتان قصد و عمد داشته، [مسئولید.] و خداوند آمرزنده‌ی مهربان است.'),
(3539, 33, 6, 'پیامبر به مؤمنان از خودشان سزاوارتر است. و همسران او [در حرمت ازدواج، مانند] مادران مؤمنانند. و در کتاب خداوند، خویشاوندان [نسبی در ارث بردنِ‌] از یکدیگر، از مؤمنان و مهاجران سزاوارترند، مگر آن که [بخواهید] نسبت به دوستانتان نیکی کنید [و سهمی از اموال خود را در قالب وصیت به آنان بدهید]. این [حکم] در کتاب [الهی] نوشته شده است.'),
(3540, 33, 7, '[و به یاد آور] هنگامی که ما از پیامبران پیمان گرفتیم، و از تو و از نوح و ابراهیم و موسی و عیسی فرزند مریم، و از همه‌ی آنان پیمانی استوار گرفتیم [که در انجام رسالت کوتاهی نکنند].'),
(3541, 33, 8, 'تا [خداوند] راستگویان را از صداقتشان [در ایمان و تیکوکاری] بازخواست کند. و برای کافران عذابی دردناک آماده کرده است.'),
(3542, 33, 9, 'ای کسانی که ایمان آورده‌اید! نعمت خدا را بر خود یاد کنید، آن‌گاه که لشکریانی به سراغتان آمدند، ولی ما تندبادی [سخت] و لشکریانی که آنها را نمی‌دیدید، بر آنان فرستادیم، [و این‌گونه دشمنان را درهم کوبیدیم]، و خداوند به آنچه می‌کنید، بیناست.'),
(3543, 33, 10, 'آن‌گاه که دشمنان از بالا و پایین [شهر] شما به سراغتان آمدند [و مدینه را محاصره کردند]، و آن‌گاه که چشم‌ها [از ترس] خیره شده بود و جان‌ها به حنجره‌ها رسیده بود، و شما به خداوند گمان‌ها[ی بد] می‌بردید.'),
(3544, 33, 11, 'آنجا بود که مؤمنان را آزمودند و سخت لرزاندند.'),
(3545, 33, 12, 'و آن‌گاه که منافقان و کسانی که در دل‌هایشان بیماری [شک و تردید] بود، گفتند: «خدا و پیامبرش فقط وعده‌هایی فریبنده به ما دادند.»'),
(3546, 33, 13, 'و آن‌گاه که گروهی از آنان [به مردم مدینه] گفتند: «ای مردم یثرِب! [در صحنه‌ی جنگ] برای شما جای ماندن نیست، [شما در برابر لشکر دشمن تاب مقاومت ندارید،] پس بازگردید.» و گروهی از آنان از پیامبر، اجازه [بازگشت] می‌خواستند، [و] می‌گفتند: «خانه‌های ما بی‌حفاظ است.» در حالی که بی‌حفاظ نبود، و آنان جز فرار [از جنگ] قصد دیگری نداشتند.'),
(3547, 33, 14, 'و [آن منافقان و بیماردلان چنان بودند که] اگر [دشمنان] از اطراف مدینه بر آنان وارد می‌شدند و از آنان تقاضای فتنه‌گری می‌کردند، به سرعت می‌پذیرفتند و جز مدّت کمی درنگ نمی‌کردند.'),
(3548, 33, 15, 'در حالی که آنان پیش از این با خداوند پیمان بسته بودند که [به دشمن] پشت نکنند. و پیمان الهی بازخواست شدنی است.'),
(3549, 33, 16, 'بگو: «اگر از مرگ یا کشته شدن بگریزید، این فرار هرگز برای شما سودی ندارد و در آن صورت، جز بهره‌ی اندکی نخواهید برد.»'),
(3550, 33, 17, 'بگو: «اگر خداوند برای شما رنجی یا رحمتی اراده کند، کیست که شما را در برابر [اراده] خداوند نگاه دارد؟» در حالی که آنان جز خداوند برای خود، نه کارسازی می‌یابند و نه یاری دهنده‌ای.'),
(3551, 33, 18, 'بی‌شک خداوند از میان شما، کارشکنان [جبهه و جهاد] را خوب می‌شناسد و [نیز] کسانی را که به برادران خود می‌گفتند: «به سوی ما بیایید [و خود را به کشتن ندهید].» [آنان ضعیف و ترسو هستند،] و جز اندکی به جنگ نمی‌روند.'),
(3552, 33, 19, 'در حالی آنان بر یاری شما بخیل هستند که هرگاه [لحظات] بیم [و جنگ] پیش آید، آنان را می‌بینی چنان به تو می‌نگرند که چشمانشان [در حدقه] می‌چرخد، همچون کسی که مرگ او را فراگرفته است. پس همین که ترس از میان رفت، زبان‌های تند و خشن [خود را] بر شما می‌گشایند، [و سهم خود را از غنائم می‌طلبند!] در حالی که نسبت به خیر، بخیلند. آنان ایمان ندارند و خدا اعمالشان را محو نموده است و این کار همواره بر خداوند آسان است.'),
(3553, 33, 20, '[آنان به قدری وحشت‌زده‌اند که] می‌پندارند احزاب [مشرک و یهودِ مهاجم] هنوز نرفته‌اند و اگر [بار دیگر] گروه‌‌های دشمن [به سراغ مسلمانان] آیند، آنان آرزو می‌کنند کاش میان اعراب بادیه‌نشین بودند [و درگیر جنگ نمی‌شدند] و تنها از اخبار [مربوط به] شما جویا می‌شدند و اینان اگر در میان شما بودند، جز اندکی نمی‌جنگیدند.'),
(3554, 33, 21, 'به راستی برای شما در [سیره‌ی] رسول خدا الگو و سرمشقی نیکوست. [البتّه] برای کسانی که به خدا و روز قیامت امید دارند و خدا را بسیار یاد می‌کنند.'),
(3555, 33, 22, 'و همین که مؤمنان دسته‌ها[ی لشکر دشمن] را دیدند، گفتند: این همان است که خدا و رسولش به ما وعده داده‌اند و خدا و رسولش راست گفتند.» [دیدن لشکر دشمن] جز بر ایمان و تسلیم آنان نیافزود.'),
(3556, 33, 23, 'از میان مؤمنان، مردانی هستند که آنچه را با خداوند پیمان بسته بودند، صادقانه وفا کردند، برخی از آنان پیمانشان را به انجام رساندند [و به شهادت رسیدند] و بعضی دیگر در انتظار [شهادت] هستند و هرگز [عقیده و پیمان خود را] تغییر نداده‌اند.'),
(3557, 33, 24, 'تا خداوند صادقان را به خاطر صداقتشان پاداش دهد و منافقان را، اگر بخواهد، عذاب کند، یا [اگر توبه کنند،] لطف خود را بر آنان بازگرداند. زیرا که خداوند آمرزنده‌ی مهربان است.'),
(3558, 33, 25, 'و خداوند کافران [شرکت کننده در جنگ خندق‌] را با خشم و اندوهشان برگرداند، بی آن که هیچ خیری [از پیروزی و غنیمت] به دست آورند و خداوند مؤمنان را از جنگ بی‌نیاز ساخت [و آنان را پیروز کرد] و خداوند همواره توانا و شکست‌ناپذیر است.'),
(3559, 33, 26, 'و خداوند کسانی از اهل کتاب را که از مشرکان عرب پشتیبانی می‌کردند، از برج‌ها و قلعه‌های مرتفعشان پایین کشید و در دل‌های آنان ترس و وحشت افکند، [در نتیجه شما مسلمانان] گروهی [از آن دشمنان] را کشتید و گروهی را اسیر کردید.'),
(3560, 33, 27, 'و سرزمین آنان و خانه‌ها و اموالشان و [نیز] زمینی را که در آن گام ننهاده بودید، در اختیار شما گذاشت و خداوند بر هر کاری تواناست.'),
(3561, 33, 28, 'ای پیامبر! به همسرانت بگو: «اگر شما زندگی دنیا و زینت [و زیور] آن را می‌خواهید، بیایید تا شما را [با پرداخت مهریه‌هایتان] بهره‌مند سازم و به وجهی نیکو [بدون قهر و خشونت] رهایتان کنم.'),
(3562, 33, 29, 'و اگر خدا و رسولش و سرای آخرت را می‌خواهید، پس [بدانید که] قطعاً خدا برای نیکوکارانتان پاداش بزرگی آماده کرده است.»'),
(3563, 33, 30, 'ای همسران پیامبر! هر کس از شما کار زشت [و گناهی] آشکار مرتکب شود، عذابش دو چندان خواهد بود و این [کار] برای خدا آسان است.'),
(3564, 33, 31, 'و هر کس از شما نیز که از خدا و رسولش فروتنانه فرمان برد و کار شایسته کند، پاداش او را دو بار خواهیم داد و برایش روزی کریمانه و پرارزشی فراهم کرده‌ایم.'),
(3565, 33, 32, 'ای همسران پیامبر! شما اگر پرهیزکاری پیشه کنید [از نظر منزلت و موقعیت‌] مانند هیچ یک از زنان نیستید، پس به نرمی و کرشمه سخن نگویید، تا [مبادا] آن که در دلش بیماری است، طمع کند و نیکو و شایسته سخن بگویید.'),
(3566, 33, 33, 'و [ای همسران پیامبر!] در خانه‌های خود قرار گیرید و همچون دوران جاهلیتِ نخستین، با خودآرایی ظاهر نشوید [و زینت‌های خود را آشکار نکنید] و نماز را برپا دارید و زکات بدهید و از خدا و رسولش اطاعت کنید. همانا خداوند اراده کرده است که هرگونه پلیدی را از شما اهل بیت دور کند و شما را [از همه آلودگی‌ها و گناهان‌] کاملاً پاک و پاکیزه گرداند.'),
(3567, 33, 34, 'و [ای همسران پیامبر!] آنچه را که از آیات خدا و حکمت در خانه‌هایتان تلاوت می‌شود، یاد کنید. همانا خداوند [نسبت به شما] دارای لطف و [از کارهای شما] آگاه است.'),
(3568, 33, 35, 'مردان مسلمان و زنان مسلمان و مردان باایمان و زنان باایمان و مردان مطیع خدا و زنان مطیع خدا و مردان راستگو و زنان راستگو و مردان شکیبا و زنان شکیبا و مردان فروتن و زنان فروتن و مردان انفاق‌گر و زنان انفاق‌گر و مردان روزه‌دار و زنان روزه‌دار و مردان پاکدامن و زنان پاکدامن و مردانی که خدا را بسیار یاد می‌کنند و زنانی که بسیار به یاد خدا هستند، خداوند برای [همه‌ی] آنان آمرزش و پاداشی گرانقدر و بزرگ آماده کرده است.'),
(3569, 33, 36, 'و هیچ مرد و زن باایمانی حقّ ندارد هنگامی که خدا و پیامبرش کاری را حکم کنند، از سوی خود امر دیگری را اختیار کند. و هر کس خدا و پیامبرش را نافرمانی نماید، پس بی‌شک به گمراهی آشکاری گرفتار شده است.'),
(3570, 33, 37, 'و زمانی که به [زید بن حارِثه،] کسی که خداوند بر او نعمت [اسلام و ایمان] داده بود و تو [نیز] به او نعمت [آزادی] داده بودی، می‌گفتی: همسرت را نگاه دار [و او را طلاق مده،] و از خدا پروا کن!» و در دلت چیزی را پنهان می‌داشتی که خداوند آن را آشکار می‌کند. [و آن، فرمان خدا به ازدواج با مطلّقه‌ی او بود.] و از مردم می‌ترسیدی [که بگویند با همسر پسرخوانده‌اش ازدواج کرده]؛ در حالی که خداوند سزاوارتر است که از او بترسی. پس چون دوران کامیابی زید از همسرش پایان یافت [و از او جدا شد]، ما او را به همسری تو درآوردیم تا در مورد ازدواج مؤمنان با زنانِ پسرخوانده‌هایشان هنگامی که طلاق گیرند، مشکلی نباشد. و فرمان خداوند همواره انجام‌شدنی است.'),
(3571, 33, 38, 'بر پیامبر در [انجام] آنچه خداوند برای او تعیین کرده، هیچ سختی و منعی نیست؛ چنان که خداوند درباره‌ی پیامبران پیشین نیز این سنّت را قرار داده بود [که آداب و رسوم بی‌اساس را بشکنند] و فرمان خدا همواره سنجیده و اندازه‌گیری شده است.'),
(3572, 33, 39, '[همان] کسانی که پیام‌های الهی را ابلاغ می‌کنند و از خدا می‌ترسند، و از هیچ‌کس جز خدا بیم ندارند. و خداوند برای حساب‌رسی کافی است.'),
(3573, 33, 40, 'محمّد، پدر هیچ یک از مردان شما نبوده و نیست، بلکه رسول خدا و خاتم پیامبران است و خداوند همواره به همه چیز آگاه است.'),
(3574, 33, 41, 'ای کسانی که ایمان آورده‌اید! خدا را بسیار یاد کنید.'),
(3575, 33, 42, 'و او را در هر صبح و شام تسبیح نمایید.'),
(3576, 33, 43, 'او کسی است که بر شما درود می‌فرستد و فرشتگان او نیز [بر شما درود می‌فرستند]، تا شما را از تاریکی‌ها[ی کفر، شرک، جهل و تفرقه] به نور [ایمان، تقوا، علم و وحدت] درآورند. و او همواره به مؤمنان مهربان است.'),
(3577, 33, 44, 'تحیت و درود آنان در روزی که خدا را ملاقات کنند، سلام است. و خداوند برای آنان پاداشی نیکو فراهم کرده است.'),
(3578, 33, 45, 'ای پیامبر! ما تو را گواه [بر مردم] و بشارت‌دهنده و بیم‌دهنده فرستادیم.'),
(3579, 33, 46, 'و تو را دعوت‌کننده به سوی خدا به فرمان او، و چراغی تابان [قرار دادیم].'),
(3580, 33, 47, 'و به مؤمنان بشارت ده که برای آنان از سوی خداوند بخششی بزرگ است.'),
(3581, 33, 48, 'و پیرو کافران و منافقان مباش و به آزارشان اعتنا مکن! و بر خدا توکل نما که خداوند برای کارسازی کافی است.'),
(3582, 33, 49, 'ای کسانی که ایمان آورده‌اید! هرگاه زنان باایمان را به همسری گرفتید و پیش از آن که با آنان آمیزش کنید، طلاقشان دادید، دیگر عدّه‌ای برای شما به عهده‌ی آنان نیست تا حسابش را نگهدارید، پس آنها را [با پرداخت نصف مهریه] بهره‌مند سازید و به طرز شایسته‌ای رهایشان کنید.'),
(3583, 33, 50, 'ای پیامبر! ما برای تو همسرانی را که مهریّه‌شان را پرداخته‌ای، حلال کردیم، و نیز کنیزانی را که خداوند [به عنوان غنایم جنگی] بر تو ارزانی داشته و مالک شدی، و دخترعموها و دخترعمه‌ها و دختردایی‌ها و دخترخاله‌هایت را که با تو هجرت کرده‌اند. و زن باایمانی که خود را به پیامبر ببخشد، [و مهریه‌ای نخواهد،] اگر پیامبر بخواهد، می‌تواند او را به عقد خویش درآورد. [این قانونِ عقد بدون مهریه] مخصوص توست، نه دیگر مؤمنان. ما می‌دانیم که برای مؤمنان درباره‌ی همسران و کنیزانشان چه حکمی کرده‌ایم. [و مصلحت آنان چه حکمی را ایجاب می‌کند. این به خاطر آن است] که برای تو [در انجام رسالت] مشکلی نباشد. و خداوند همواره آمرزنده‌ی مهربان است.'),
(3584, 33, 51, '[ای پیامبر! اختیار با توست. نوبت] هر یک از همسرانت را خواستی، تأخیر می‌اندازی و هر که را بخواهی، نزد خود جای می‌دهی. بر تو گناهی نیست هر کدام را که [برای مدّتی] از او کناره گرفته باشی، دوباره بطلبی. این [حکم‌] برای آن که چشمانشان روشن شود و ناراحت نشوند، و همگی به آنچه به آنان داده‌ای، خشنود شوند، نزدیک‌تر است. و خداوند آنچه را در دل‌های شماست، می‌داند. و خداوند دانا و بردبار است.'),
(3585, 33, 52, 'پس از این [زنانی که برشمردیم]، زنان [دیگر] و [نیز] این که همسرانی [تازه] را جایگزین آنان کنی، بر تو حلال نیست، هر چند حُسن آنان تو را به شگفت آورد. مگر کنیزانی که مالک آنها می‌شوی. و خداوند مراقب هر چیزی است. [و با این حکم، فشار قبایل عرب را برای همسر گرفتن از آنان، از تو برمی‌دارد.]'),
(3586, 33, 53, 'ای کسانی که ایمان آورده‌اید! به خانه‌های پیامبر وارد نشوید، مگر آنکه به شما برای خوردن غذایی اجازه دهند، [به شرط آن که پیش از موعد نیایید] و در انتظار وقت غذا نباشید؛ ولی هنگامی که دعوت شدید، وارد شوید. و وقتی غذا خوردید، پراکنده شوید، و [پس از خوردن غذا] به گفتگو ننشینید. این کار پیامبر را می‌آزارد، امّا او از شما شرم می‌کند [که بیرونتان کند و چیزی نمی‌گوید]؛ ولی خداوند از [گفتن] حقّ شرم ندارد. و هرگاه از همسران پیامبر چیزی از وسایل زندگی [به امانت] خواستید، از پشت پرده بخواهید. این رفتار برای دل‌های شما و دل‌های آنان پاکیزه‌تر است. و شما حقّ ندارید که رسول خدا را بیازارید و پس از او با همسرانش ازدواج کنید، که این [کار] نزد خداوند [گناهی] بزرگ است.'),
(3587, 33, 54, 'اگر چیزی را آشکار نمایید یا پنهانش کنید، بی‌شک خداوند همواره به هر چیزی داناست.'),
(3588, 33, 55, 'بر زنان، [نداشتن حجاب] در [برابر] پدران و پسران و برادران و پسران برادر و پسران خواهر، و دیگر زنان [مسلمان] و کنیزانشان گناهی نیست. و تقوای الهی پیشه کنید؛ زیرا خداوند همواره بر هر چیزی گواه است.'),
(3589, 33, 56, 'به راستی خداوند و فرشتگان او بر پیامبر درود می‌فرستند. ای کسانی که ایمان آورده‌اید! شما [نیز] بر او درود فرستید و به او سلام کنید، و کاملاً تسلیم او باشید.'),
(3590, 33, 57, 'کسانی که خدا و پیامبرش را می‌آزارند، خداوند در دنیا و آخرت آنان را لعنت می‌کند، و برای آنان عذاب خوارکننده‌ای آماده کرده است.'),
(3591, 33, 58, 'و کسانی که مردان و زنان باایمان را بی‌آنکه کاری [ناپسند] کرده باشند، می‌آزارند، بی‌شک بهتان و گناه روشنی را بر دوش کشیده‌اند.'),
(3592, 33, 59, 'ای پیامبر! به همسران و دخترانت و زنان مؤمنان بگو که چادرهایشان را بر [اندام] خود نیک بپوشند این [پوشش‌] مناسب‌تر است، تا [به عفّت و پاکدامنی] شناخته شوند و [از سوی بدکاران] مورد آزار قرار نگیرند. و [اگر تاکنون خطا و کوتاهی از آنها سر زده است، توبه کنند،] خداوند همواره آمرزنده‌ی مهربان است.'),
(3593, 33, 60, 'اگر منافقان و کسانی که در دل‌هایشان بیماری است و آنان که در مدینه شایعه‌پراکنی می‌کنند، [از کارشان] دست برندارند، مسلّماً تو را بر ضّد آنان می‌شورانیم. [تا تبعیدشان کنی.] آن‌گاه جز مدّت کوتاهی نمی‌توانند در کنار تو در این شهر بمانند.'),
(3594, 33, 61, 'آنان لعنت شدگانند. هر کجا یافت شوند، باید دستگیر و به سختی کشته شوند.'),
(3595, 33, 62, 'درباره‌ی کسانی که پیش‌تر بوده‌اند [نیز، همین‌] شیوه و سنّت خدا [جاری بوده‌] است. و در شیوه و سنّت خدا هرگز دگرگونی و تغییری نخواهی یافت.'),
(3596, 33, 63, 'مردم پیوسته درباره‌ی [زمان] قیامت از تو می‌پرسند. بگو: «علم آن تنها نزد خداست.» و تو چه می‌دانی؟ شاید قیامت نزدیک باشد.'),
(3597, 33, 64, 'خداوند کافران را لعنت کرده [و از رحمت خود دور ساخته،] و برای آنان آتش فروزانی فراهم کرده است.'),
(3598, 33, 65, 'آنان برای همیشه در آن می‌مانند و دوست و یاوری نمی‌یابند.'),
(3599, 33, 66, 'روزی که صورت‌های آنان در آتش گردانده می‌شود، [با حسرت] می‌گویند: «ای کاش خدا را اطاعت می‌کردیم و پیرو فرستاده‌اش بودیم.»'),
(3600, 33, 67, 'و می‌گویند: «پروردگارا! ما سران و بزرگانمان را اطاعت کردیم و آنان ما را از راه به‌در کردند.'),
(3601, 33, 68, 'پروردگارا! آنان را دو چندان عذاب ده و کاملاً از رحمتت دورشان ساز!»'),
(3602, 33, 69, 'ای کسانی که ایمان آورده‌اید! [پیامبر را نرنجانید و] مانند کسانی نباشید که موسی را آزردند، پس خداوند او را از آنچه [درباره‌اش به تهمت] می‌گفتند، تبرئه فرمود و او نزد خداوند آبرومند بود.'),
(3603, 33, 70, 'ای کسانی که ایمان آورده‌اید! تقوای الهی پیشه کنید و سخن استوار بگویید.'),
(3604, 33, 71, 'تا خداوند کارهای شما را اصلاح کند و گناهانتان را بیامرزد و هر کس خدا و پیامبرش را اطاعت کند، بی‌شک به رستگاری بزرگی دست یافته است.'),
(3605, 33, 72, 'ما امانت [خود] را بر آسمان‌ها و زمین و کوه‌ها عرضه کردیم اما آنها از به عهده گرفتنش [به سبب این که استعدادش را نداشتند،] سر باز زدند و از آن ترسیدند؛ ولی انسان [چون استعداد آن را داشت و] آن را بر دوش گرفت، امّا [قدر این مقام عظیم را نشناخت و به خود ستم کرد.] همانا او بس ستمکار و نادان بود.'),
(3606, 33, 73, '[امانت عرضه شد] تا خداوند مردان و زنان منافق و مردان و زنان مشرک را [به علّت تباه کردن امانت،] عذاب کند و لطف خود را بر مردان و زنان باایمان برگرداند [و توبه‌ی آنان را بپذیرد]. که خداوند همواره آمرزنده‌ی مهربان است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ستایش مخصوص خدایی است که آنچه در آسمان‌ها و آنچه در زمین است، از آنِ اوست. و در آخرت [نیز] حمد و ستایش تنها برای اوست. و اوست حکیمِ آگاه.'),
(3608, 34, 2, 'آنچه در زمین فرومی‌رود و آنچه از آن خارج می‌شود و آنچه از آسمان نازل می‌شود و آنچه در آن بالا می‌رود، [همه را] می‌داند. و اوست مهربانِ آمرزنده.'),
(3609, 34, 3, 'و کسانی که کافر شدند، گفتند: «قیامت برای ما نخواهد آمد.» بگو: «چرا، به پروردگارم که آگاه از غیب و نهان است سوگند، که به سراغ شما [نیز] خواهد آمد. در آسمان‌ها و زمین، به میزان ذرّه‌ای [چیزی] از خدا پوشیده نیست، نه کوچک‌تر از آن و نه بزرگ‌تر از آن، جز آن که در کتاب روشنِ [الهی ثبت] است.'),
(3610, 34, 4, 'تا کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، پاداش دهد. آنانند که آمرزش و روزی نیکویی خواهند داشت.»'),
(3611, 34, 5, 'و کسانی که در [انکار و محو] آیات ما تلاش کردند که ما را درمانده کنند، آنانند که عذابی [سخت] از کیفری دردناک خواهند داشت.'),
(3612, 34, 6, 'و کسانی که به آنان علم داده شده، می‌بینند که آنچه از پروردگارت بر تو نازل شده، حقّ است و به راه [خدای] شکست‌ناپذیر و ستوده هدایت می‌کند.'),
(3613, 34, 7, 'و کسانی که کفر ورزیدند، [به استهزاء] گفتند: «[ای مردم!] آیا شما را به مردی راهنمایی کنیم که به شما خبر [عجیبی‌] می‌دهد؟ که هرگاه [در قبرها] کاملاً متلاشی شدید، [بار دیگر] درآفرینش تازه‌ای [زنده] خواهید شد.'),
(3614, 34, 8, 'آیا او بر خدا دروغی بسته؟ یا جنونی در اوست؟» [نه! چنین نیست که می‌پندارند؛] بلکه کسانی که به آخرت ایمان ندارند، در عذاب و گمراهی دوری هستند.'),
(3615, 34, 9, 'آیا به آنچه از آسمان و زمین، پیش‌رو و پشت‌سر آنهاست، نگاه نکردند؟ [تا قدرت خدا را ببینند؟] اگر بخواهیم، آنان را در زمین فرومی‌بریم، یا قطعه‌هایی از آسمان را بر سرشان فرود می‌آوریم. همانا در این [تهدید]، برای هر بنده توبه‌کاری عبرت و نشانه‌ای است.'),
(3616, 34, 10, 'و به‌راستی داود را از جانب خود فضیلتی دادیم [و گفتیم:] «ای کوه‌ها! و [ای] پرندگان! با او [در تسبیح خدا] هم‌نوا شوید!» و آهن را برای او نرم کردیم.'),
(3617, 34, 11, '[و به او گفتیم:] «[ای داود!] زره‌های کامل و فراخ بساز! و حلقه‌ها [ی آن‌] را متناسب و هماهنگ اندازه‌گیری کن! و کار شایسته کنید. من به آنچه می‌کنید، بینا هستم.»'),
(3618, 34, 12, 'و برای سلیمان باد را [رام و مسخّر کردیم] که صبحگاهان مسیر یک ماه را می‌پیمود، و عصرگاهان مسیر یک ماه را. و برای او چشمه‌ی مس [گداخته] را روان ساختیم. و گروهی از جنّ پیش روی او به اذن پروردگارش کار می‌کردند. و هر یک از آنان که از فرمان ما سرپیچی می‌کرد، او را از عذاب فروزان می‌چشاندیم.'),
(3619, 34, 13, 'جنّیان، هر چه را سلیمان می‌خواست، برایش می‌ساختند: از محراب‌ها و تندیس‌ها، و ظروف بزرگ مانند حوضچه‌ها و دیگ‌های ثابت. [به آنان گفتیم:] «ای خاندان داود! شکر [این همه نعمت را] به جای آورید.» امّا اندکی از بندگان من سپاسگزارند.'),
(3620, 34, 14, 'پس چون مرگ را بر سلیمان مقّرر داشتیم، [در حالی که بر عصای خود تکیه داشت، از دنیا رفت، و] کسی آنان را از مرگ او آگاه نساخت، مگر جنبنده‌ی زمین، [موریانه،] که عصایش را می‌خورد. پس چون سلیمان به زمین افتاد، جنّیان دریافتند که اگر غیب می‌دانستند، در آن عذاب خوارکننده [که کارهای بسیار پرزحمت و طاقت‌فرسا بود،] نمی‌ماندند.'),
(3621, 34, 15, 'برای قوم سَبَأ در محل سکونتشان نشانه‌ای [از قدرت و رحمت الهی] بود: دو باغ از راست و چپ. [به آنان گفتیم:] «از روزی پروردگارتان بخورید و برای او شکرگزار باشید. شهری پاک و پروردگاری آمرزنده.»'),
(3622, 34, 16, 'پس [از خداوند] روی‌گرداندند. و ما بر آنان سیل [ویرانگر] عَرِم را فرستادیم و دو باغستان آنان را به دو باغ که میوه‌های تلخ و [درختان‌] شوره گز و اندکی از درخت سدر داشت، تبدیل کردیم.'),
(3623, 34, 17, 'آن [کیفر] را به سزای آن که کافر شدند و ناسپاسی کردند، به آنان دادیم. و آیا جز ناسپاس را کیفر می‌دهیم؟'),
(3624, 34, 18, 'و میان مردم سبأ و مناطقی که در آن برکت قرار داده بودیم، آبادی‌هایی [به‌هم پیوسته] آشکار بود، و سفر در میان قریه‌ها را متناسب و به اندازه مقرّر کرده بودیم. [و به آنان گفتیم:] «در این مناطق، شب‌ها و روزها با ایمِنی سفر کنید.»'),
(3625, 34, 19, 'پس گفتند: «پروردگارا! میان سفرهای ما فاصله بیانداز!» [تا در سفرها نیاز به زاد و توشه باشد، و برتری دارا بر ندار روشن شود،] و [این‌گونه آنان‌] بر خویشتن ستم کردند. پس ما آنان را [موضوع] داستان‌هایی [برای عبرت دیگران] قرار دادیم. و آنان را به کلّی پراکنده و متلاشی کردیم. در این [ناسپاسی آنان و قهر ما] نشانه‌هایی برای هر شکیبای سپاسگزاری است.'),
(3626, 34, 20, 'و البتّه ابلیس گمان خود را [برای گمراهی انسان] بر آنان تحقّق یافته دید، پس جز گروه اندکی از مؤمنان، همگی از او پیروی کردند.'),
(3627, 34, 21, 'و ابلیس هیچ‌گونه تسلّط و غلبه‌ای بر آنان نداشت. [کار او تنها وسوسه است،] تا کسی را که به آخرت ایمان دارد [و در برابر وسوسه‌های او مقاومت می‌کند،] از کسی که نسبت به آخرت در تردید است، معلوم کنیم. و پروردگارت بر همه چیز نگهبان است.'),
(3628, 34, 22, 'بگو: «کسانی را که به جای خدا [معبود] پنداشته‌اید، بخوانید. آنان به میزان ذرّه‌ای مالک چیزی در آسمان و زمین نیستند. و شریک‌ها [ی خیالی شما] در [اداره‌ی] زمین و آسمان هیچ‌گونه مشارکتی ندارند. و از میان آنها هیچ پشتیبانی برای خداوند نیست.»'),
(3629, 34, 23, 'و شفاعت نزد خداوند، جز برای کسانی که خداوند اجازه دهد، سودی ندارد. تا چون هراس از دل‌های آنان برطرف شود، [مجرمان] از آنان می‌پرسند: «پروردگارتان چه گفت؟» [شفیعان] پاسخ می‌دهند: «حقّ گفت. [و اجازه‌ی شفاعت شایستگان را داد.] و او بلندمرتبه و بزرگ است.»'),
(3630, 34, 24, '«[ای پیامبر! به مشرکان] بگو: «کیست که از آسمان‌ها و زمین به شما روزی می‌دهد؟» بگو: «خدا! و البتّه [یکی از] ما یا شما بر [طریق] هدایت یا در گمراهی آشکار هستیم.»'),
(3631, 34, 25, 'بگو: «[شما] از آنچه ما مرتکب شده‌ایم، بازخواست نخواهید شد. و ما [نیز] از آنچه شما می‌کنید، بازخواست نخواهیم شد.»'),
(3632, 34, 26, '[به مشرکان] بگو: «پروردگارمان، ما و شما را [در قیامت] گرد می‌آورد، سپس بین ما و شما به حقّ داوری خواهد کرد. و اوست داور دانا.»'),
(3633, 34, 27, 'بگو: «کسانی را که به عنوان شریک به خداوند ملحق کرده‌اید، به من نشان دهید. [آیا شایسته‌ی خدایی هستند؟] هرگز! بلکه فقط اوست خدای نفوذناپذیر حکیم.»'),
(3634, 34, 28, 'و ما تو را نفرستادیم مگر برای این که مردم را [از خطا و گناه] بازداری، [و] مژده‌دهنده و بیم‌دهنده [باشی]. ولی بیشتر مردم نمی‌دانند.'),
(3635, 34, 29, 'و آنان می‌گویند: «اگر راست می‌گویید، این وعده [قیامت] کِی فرامی‌رسد؟»'),
(3636, 34, 30, 'بگو: «وعده‌ی شما روزی است که نه ساعتی از آن تأخیر می‌کنید و نه [از آن] پیش می‌افتید.»'),
(3637, 34, 31, 'و کسانی که کافر شدند، گفتند: «ما هرگز نه به این قرآن، و نه به آن [کتاب‌هایی] که پیش از آن بوده است، ایمان نخواهیم آورد.» [و تعجب می‌کنی] اگر ببینی وقتی که ستمگران [مشرک] در پیشگاه پروردگارشان بازداشت شده‌اند، در حالی که با یکدیگر جدال و گفتگو می‌کنند [و گناه خود را به گردن یکدیگر می‌اندازند]، کسانی که ضعیف [و زیردست] نگاه داشته شده‌اند، به مستکبران می‌گویند: «اگر شما نبودید، حتماً ما مؤمن بودیم.»'),
(3638, 34, 32, '[امّا] مستکبران به زیردستان می‌گویند: «آیا ما شما را از هدایتی که به سراغتان آمد، بازداشتیم؟ [نه!] بلکه شما خود گناهکار بودید.»'),
(3639, 34, 33, 'و زیردستان به مستکبران می‌گویند: «بلکه [مایه‌ی گمراهی ما] نیرنگ شب و روز [شما بود]، آن‌گاه که ما را فرمان می‌دادید به خدا کفر ورزیم و برای او همتایانی قرار دهیم.» و همین که عذاب را ببینند، پشیمانی خود را پنهان می‌کنند [تا بیشتر رسوا نشوند] و ما بر گردن کسانی که کفر ورزیدند، غل‌ها می‌نهیم. آیا جز آنچه می‌کردند، کیفر می‌شوند؟'),
(3640, 34, 34, 'و ما در هیچ دیاری هشدار دهنده‌ای نفرستادیم، مگر آن که افراد خوشگذران آن گفتند: «ما به آنچه شما را به آن فرستاده‌اند، کافریم.»'),
(3641, 34, 35, 'و گفتند: «دارایی و فرزندان ما بیشتر است و [این نشانه‌ی علاقه‌ی خداوند به ماست، پس اگر عذابی هم باشد،] ما عذاب نمی‌شویم.»'),
(3642, 34, 36, 'بگو: «پروردگار من برای هر که بخواهد، روزی را گشایش می‌دهد، یا تنگ می‌گرداند؛ ولی بیشتر مردم نمی‌دانند.»'),
(3643, 34, 37, 'و اموال و فرزندانتان آن‌گونه نیست که شما را به پیشگاه ما نزدیک گرداند، مگر کسانی که ایمان آورده‌اند و کار شایسته کرده‌اند. پس آنانند که دو برابر آنچه کردند، پاداش دارند و آنانند که در غرفه‌ها و طبقات [بهشتی] آسوده‌خاطرند.'),
(3644, 34, 38, 'و کسانی که در [ابطال‌] آیات ما می‌کوشند تا [ما را به خیال خود] درمانده کنند، آنانند که در عذاب احضار می‌شوند.'),
(3645, 34, 39, 'بگو: «بی‌شک، پروردگارم برای هر کس از بندگانش که بخواهد، روزی را گشایش می‌دهد و یا برای او تنگ می‌گرداند و هر چه را انفاق کنید، او جایگزین می‌کند و او بهترینِ روزی‌دهندگان است.»'),
(3646, 34, 40, 'و [به یاد آور] روزی که خداوند همه را محشور می‌کند، سپس به فرشتگان می‌گوید: «آیا این‌ها شما را می‌پرستیدند؟»'),
(3647, 34, 41, 'فرشتگان می‌گویند: «خدایا! تو منزّهی! تو سرپرست مایی، نه آنها! [آنان ما را نمی‌پرستیدند،] بلکه آنان جنّیان را می‌پرستیدند، [و] بیشترشان به آنان ایمان داشتند.»'),
(3648, 34, 42, 'پس امروز برای یکدیگر مالک سود و زیانی نیستید. و به کسانی که ستم کردند، می‌گوییم: «بچشید عذاب آتشی را که آن را دروغ می‌شمردید!»'),
(3649, 34, 43, 'و هرگاه آیات روشن ما بر آنان خوانده شود، می‌گویند: «جز این نیست که این مرد می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند، بازدارد.» و می‌گویند: «این [قرآن] جز دروغی بافته شده نیست.» و کسانی که کفر ورزیدند، همین که حقّ به سراغشان آمد، گفتند: این، جز جادویی آشکار نیست.»'),
(3650, 34, 44, 'و [این در حالی است که‌] ما کتابی [آسمانی] به آنان نداده‌ایم که آن را بخوانند و بیاموزند. [تا با تکیه بر آن، قرآن را دروغی ساختگی بدانند]. و ما پیش از تو بیم‌دهنده‌ای به سوی آنان نفرستاده‌ایم. [تا با اعتماد به گفتار او، نبوّت تو را انکار کنند.]'),
(3651, 34, 45, 'و کسانی که پیش از آنان بودند، [نیز پیامبران را] تکذیب کردند، در حالی که [کافران قوم تو] به یک دهم آنچه [از قدرت و ثروت] به گذشتگان داده بودیم، نرسیده‌اند. آنان پیامبران مرا تکذیب کردند، [و نابود شدند.] پس [بنگر] کیفر من چگونه بود؟'),
(3652, 34, 46, 'بگو: «شما را تنها به یک چیز اندرز می‌دهم: دوتا دوتا یا به تنهایی برای خدا قیام کنید. سپس بیندیشید، که این همنشینتان [محمّد که عمری با پاکی، امانت، راستی و خردورزی در میان شما زندگی کرده است‌،] هیچ‌گونه جنونی ندارد. او برای شما فقط بیم‌دهنده‌ای است از عذاب سختی که پیش روست.»'),
(3653, 34, 47, 'بگو: «هر مزد و پاداشی که از شما خواستم، پس آن به نفع شماست. مزد و پاداش من تنها بر عهده‌ی خداست. و او بر هر چیزی گواه است.»'),
(3654, 34, 48, 'بگو: «پروردگارم که به نهان‌ها بسیار آگاه است، حقّ را [بر دل‌ها] می‌افکند.»'),
(3655, 34, 49, 'بگو: «حقّ آمد، و باطل نه می‌تواند آغازگر چیزی باشد و نه تجدیدکننده‌ی آن.»'),
(3656, 34, 50, 'بگو: «اگر من گمراه شده باشم، به زیان خود گمراه شده‌ام. و اگر هدایت‌یافته باشم، پس به [برکت] چیزی است که پروردگارم به من وحی می‌کند. که اوست شنوای نزدیک.»'),
(3657, 34, 51, 'و اگر ببینی وقتی که [کافران از عذاب سخت] ترسان و وحشت‌زده می‌شوند و راه گریزی در کار نیست، و از مکانی نزدیک [که انتظارش را ندارند،] دستگیر می‌شوند.'),
(3658, 34, 52, '[و در آن حال] می‌گویند: «به او ایمان آوردیم!» و کجا این دست‌یابی به ایمان از راه دور [قیامت تا دنیا] ممکن خواهد بود؟ [که آخرت جای عمل نیست.]'),
(3659, 34, 53, 'در حالی که پیش از این به آن [حقّ] کفر ورزیدند و از دور دست‌ها تیر در تاریکی می‌انداختند.'),
(3660, 34, 54, '[سرانجام] میان آنان و آنچه می‌خواستند، جدایی افتاد. همان‌گونه که از دیرباز با همانندان ایشان چنین شد؛ زیرا آنان همواره در شک و تردید سختی بودند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'ستایش، مخصوص خداوندی است که آفریدگار آسمان‌ها و زمین است. فرشتگان را که دارای بال‌های دوگانه و سه‌گانه و چهارگانه هستند، رسولانی قرارداد. او هر چه را بخواهد، در آفرینش می‌افزاید. خداوند بر هر کاری تواناست.'),
(3662, 35, 2, 'هر رحمتی را که خداوند برای مردم بگشاید، بازدارنده‌ای برای آن نیست و هر چه را خداوند دریغ دارد، غیر او فرستنده‌ای برای آن نیست. و اوست عزیز و حکیم.'),
(3663, 35, 3, 'ای مردم! نعمت‌هایی را که خداوند به شما عطا کرده، یاد کنید. آیا جز خداوند، آفریدگاری هست که از آسمان و زمین به شما روزی دهد؟ جز او معبودی نیست. پس چگونه از حقّ به سوی باطل بازگردانده می‌شوید؟'),
(3664, 35, 4, 'و اگر تو را تکذیب می‌کنند، [نگران مباش! زیرا] پیامبران پیش از تو [نیز] تکذیب شده‌اند و همه‌ی کارها به سوی خداوند بازمی‌گردد.'),
(3665, 35, 5, 'ای مردم! وعده‌ی خداوند [درباره‌ی قیامت‌] حقّ است، پس زندگی [زودگذر] دنیا شما را نفریبد و مبادا [شیطان] فریبکار شما را به [لطف] خدا مغرور سازد.'),
(3666, 35, 6, 'بی‌گمان، شیطان دشمن شماست، پس شما نیز او را دشمن بگیرید. جز این نیست که او گروه خویش را فرامی‌خواند تا [سرانجام] از دوزخیان باشند.'),
(3667, 35, 7, 'کسانی که کفر ورزیدند، برایشان عذابی سخت است و کسانی که ایمان آوردند و کارهای شایسته کرده‌اند، آمرزش و پاداشی بزرگ خواهند داشت.'),
(3668, 35, 8, 'پس آیا کسی که عمل بدش برای او آراسته شده و آن را نیکو می‌بیند، [مانند کسی است که هدایت یافته است؟] پس بی‌شک خداوند هر که را بخواهد، [و سزاوار بداند،] گمراه می‌کند و هر که را بخواهد، [و شایسته بداند،] هدایت می‌نماید. پس [ای پیامبر!] مگذار به خاطر حسرت بر آنان جانت [از دست] برود؛ زیرا خداوند به آنچه می‌کنند، آگاهی کامل دارد.'),
(3669, 35, 9, 'و خداوند است که بادها را فرستاد تا ابر را برانگیزاند، پس آن را به سوی سرزمینی مرده راندیم و به وسیله‌ی آن زمین را بعد از مردنش زنده کردیم. رستاخیز نیز چنین است.'),
(3670, 35, 10, 'هر کس خواهان عزّت است، پس عزّت، همه از آن خداست. تنها سخن [و عقیده‌ی] پاک به سوی او بالا می‌رود و کار شایسته آن را بالا می‌برد و کسانی که برای انجام بدی‌ها نقشه می‌کشند، برایشان عذاب سختی است و نیرنگ آنان است که تباه می‌شود.'),
(3671, 35, 11, 'خداوند، شما را از خاک و سپس از نطفه آفرید؛ آن‌گاه شما را زوج‌هایی قرار داد و هیچ ماده‌ای جز با علم او باردار نمی‌شود و نمی‌زاید و هیچ کس عمر طولانی نمی‌کند، یا از عمرش کاسته نمی‌شود، مگر آن که در کتاب [علم خدا ثبت گردیده] است. قطعاً این امر بر خداوند آسان است.'),
(3672, 35, 12, 'دو دریا یکسان نیستند: این یکی شیرین و گواراست، و آن یکی، شور و تلخ؛ ولی شما از هر دو گوشت تازه می‌خورید، و زیور [و مروارید] بیرون می‌آورید و می‌پوشید. کشتی‌ها را در آن می‌بینی که امواج را می‌شکافند تا از فضل او [روزی] طلبید و شاید شکرگزار باشید.'),
(3673, 35, 13, 'شب را در روز فرومی‌برد و روز را در شب. و خورشید و ماه را رام و مسخّر نمود، هر یک از آنها برای مدّتی معین در حرکتند. این است خداوند پروردگارتان! فرمانروایی، ویژه‌ی اوست. و کسانی که به جای او می‌خوانید، مالک پوست هسته‌ی خرمایی [هم] نیستند.'),
(3674, 35, 14, 'اگر آن بت‌ها را بخوانید، دعای شما را نمی‌شنوند. و اگر بشنوند، پاسخی به شما نمی‌دهند. و روز قیامت شرک شما را انکار خواهند کرد. و هیچ‌کس مانند [خداوند] آگاه، تو را [از حقیقت کارها] باخبر نمی‌کند.'),
(3675, 35, 15, 'ای مردم! شمایید نیازمندان درگاه خداوند! و [تنها] خداوند، بی‌نیاز و ستوده است.'),
(3676, 35, 16, 'اگر بخواهد شما را می‌برد، و آفریده‌ی جدیدی می‌آورد.'),
(3677, 35, 17, 'و این کار بر خداوند سخت نیست.'),
(3678, 35, 18, 'و هیچ‌کس بار [گناه] دیگری را بر دوش نکشد. و اگر سنگین‌باری برای حمل بارش، حتّی از نزدیکان خود دعوتی کند، چیزی از بارش حمل نشود. [ای پیامبر!] تو تنها کسانی را هشدار می‌دهی که در نهان از پروردگارشان می‌ترسند و نماز به‌پا می‌دارند. و هر کس [از گناه] پاک شود، پس این پاکی به نفع خود اوست و بازگشت [همه] تنها به سوی خداوند است.'),
(3679, 35, 19, '[کافر و مؤمن یکسان نیستند، چنان که] نابینا و بینا یکسان نیستند.'),
(3680, 35, 20, 'و تاریکی و روشنایی.'),
(3681, 35, 21, 'و سایه و گرمای آفتاب.'),
(3682, 35, 22, 'و زندگان و مردگان یکسان نیستند. خداوند [حقّ را] به هر کس که بخواهد می‌شنواند، و تو نمی‌توانی به کسانی که در گورها هستند، [حقّ را] بشنوانی.'),
(3683, 35, 23, 'تو فقط بیم دهنده‌ای.'),
(3684, 35, 24, 'یقیناً ما تو را به حقّ نویدبخش و بیم‌دهنده فرستادیم. و هر امّتی در گذشته بیم‌دهنده‌ای داشته است.'),
(3685, 35, 25, 'و اگر تو را تکذیب می‌کنند، [نگران مباش! زیرا] کافرانی که پیش از آنان بودند، نیز [پیامبران پیشین را] تکذیب کردند. پیامبرانشان با معجزه‌ها و دلایل آشکار، و نوشته‌ها، و کتاب روشنگر به سراغشان آمدند. [امّا آنان ایمان نیاوردند.]'),
(3686, 35, 26, 'سپس کسانی را که کفر ورزیدند [به بازخواست و مجازات] گرفتم. پس [بنگر] کیفر من چگونه بود؟'),
(3687, 35, 27, 'آیا ندیده‌ای که خداوند از آسمان، آبی فروفرستاد، پس با آن میوه‌هایی رنگارنگ [از زمین] بیرون آوردیم. و در برخی از کوه‌ها [رگه‌ها و] راه‌هایی است سپید و سرخ، به رنگ‌های گوناگون، و [رگه راه‌هایی] سخت سیاه.'),
(3688, 35, 28, 'و همچنین از مردم و جنبنده‌ها و چهارپایان به رنگ‌های گوناگون [آفریدیم]. از میان بندگان خدا تنها دانشمندان [ربّانی] از او هراس دارند. بی‌شک خداوند، عزیز و آمرزنده است.'),
(3689, 35, 29, 'کسانی که کتاب خدا را تلاوت می‌کنند و نماز برپا می‌دارند و از آنچه ما روزیشان کرده‌ایم، پنهان و آشکار انفاق می‌کنند، به تجارتی امید بسته‌اند که هرگز زیان و کساد ندارد.'),
(3690, 35, 30, 'تا خداوند پاداش آنان را به طور کامل عطا کند و از فضل خویش برآنان بیافزاید، چرا که خداوند آمرزنده و سپاسگزار است.'),
(3691, 35, 31, 'و [ای پیامبر!] آنچه از کتاب به سوی تو وحی کردیم، همان حقّ است که تصدیق‌کننده‌ی [کتاب‌های] پیش از آن است. قطعاً خداوند نسبت به بندگانش آگاه و بیناست.'),
(3692, 35, 32, 'آن‌گاه این کتاب [قرآن] را به گروهی از بندگانمان که [آنان را] برگزیدیم، به میراث دادیم. پس برخی از آنان [به خاطر عمل نکردن به این کتاب] بر خود ستمکارند، و بعضی میانه‌رو. و برخی به اذن الهی در کارهای خیر [بر دیگران‌] سبقت می‌گیرند، که این همان فضل بزرگ الهی است.'),
(3693, 35, 33, '[پاداش آنان] باغ‌هایی همیشه سرسبز است که به آنها وارد خواهند شد. و در آنها با دستبندهایی از طلا و مروارید زینت می‌شوند. و لباسشان در آنجا [از] ابریشم است.'),
(3694, 35, 34, 'و می‌گویند: «سپاس برای خداوندی است که اندوه را از [دل] ما برد. پروردگار ما آمرزنده و سپاسگزار است.'),
(3695, 35, 35, 'خداوندی که ما را از فضل خویش در سرای ابدی جای داد، که در آنجا هیچ رنجی به ما نمی‌رسد و درماندگی به سراغ ما نمی‌آید.»'),
(3696, 35, 36, 'و برای کسانی که کفر ورزیدند، آتش دوزخ است. نه فرمان مرگشان صادر می‌شود که بمیرند، و نه از عذاب آنان می‌کاهند. ما این‌گونه هر ناسپاسی را کیفر می‌دهیم.'),
(3697, 35, 37, 'و آن کافران در دوزخ ناله می‌زنند [و می‌گویند]: «پروردگارا! ما را بیرون بیاور تا کار شایسته‌ای کنیم، غیر از آنچه که می‌کردیم.» [در پاسخ به آنان گفته می‌شود:] «آیا ما به شما چندان عمر ندادیم که در آن هر کس که اهل پند است، متذکر شود؟ و [عبرت گیرد؟ به علاوه] هشداردهنده به سراغتان آمد [ولی شما توجّهی نکردید]، پس اینک بچشید. پس ستمگران هیچ یاوری ندارند.»'),
(3698, 35, 38, 'خداوند به نهان آسمان‌ها و زمین آگاه است. بی‌شک او به راز دل‌ها داناست.'),
(3699, 35, 39, 'اوست که شما را در زمین جانشینان [پیشینیان] قرار داد؛ پس هر کس کفر ورزد، کفرش به زیان [خود] اوست. و کفر کافران نزد پروردگارشان جز دشمنی و خشم نمی‌افزاید. و کفر کافران جز بر زیانشان نمی‌افزاید.'),
(3700, 35, 40, 'بگو: «آیا شریکان خویش را که به جای خداوند می‌خوانید، دیده‌اید؟ به من نشان دهید که چه چیزی را از زمین آفریده‌اند، یا در [آفرینش] آسمان‌ها چه مشارکتی داشته‌اند؟» یا به آنان کتابی داده‌ایم که ایشان، دلیل و حجّتی از آن بر شرک خود دارند؟ بلکه ستمکاران تنها وعده‌های فریبنده به یکدیگر می‌دهند.'),
(3701, 35, 41, 'خداوند آسمان‌ها و زمین را نگاه می‌دارد تا از نظام [و مدار] خود منحرف نشوند. و اگر منحرف گردند، کسی جز او نمی‌تواند آنها را نگاه دارد. البتّه او بردبار و آمرزنده است.'),
(3702, 35, 42, 'و [مشرکان] با سخت‌ترینِ قسم‌ها، به خدا سوگند یاد کردند که اگر هشداردهنده‌ای به سراغشان بیاید، از هر یک از امّت‌های دیگر هدایت‌یافته‌تر شوند؛ پس همین که هشداردهنده‌ای برایشان آمد، جز نفرت [از حقّ] چیزی بر آنان نیفزود.'),
(3703, 35, 43, '[نفرت آنان از حقّ] به خاطر گردن‌کشی در زمین و نیرنگ بدشان بود. و نیرنگ بد، جز اهلش را فرا نگیرد، پس آیا جز سنّت [خداوند در مورد] پیشینیان را انتظار دارند؟ با آن که برای سنّت و قانون الهی هرگز جایگزینی نخواهی یافت. و هرگز برای سنّت و شیوه‌ی الهی تغییری نخواهی یافت.'),
(3704, 35, 44, 'آیا در زمین نگشتند تا ببینند سرانجام کسانی که پیش از ایشان بودند و قدرتشان بیش از آنان بود، چه شد؟ و هیچ چیز در آسمان‌ها و زمین نیست که خداوند را درمانده کند. البتّه او همواره آگاه و تواناست.'),
(3705, 35, 45, 'و اگر خداوند مردم را به سزای آنچه کرده‌اند، بازخواست نماید، هیچ جنبنده‌ای روی زمین باقی نمی‌ماند؛ ولی مردم را تا مدّتی معین مهلت می‌دهد [تا خود را اصلاح کنند]، پس همین که مدّتشان سر آمد، [آنان را بازخواست می‌کند.] پس به راستی خداوند به [احوال] بندگان خود بیناست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یاسین!'),
(3707, 36, 2, 'سوگند به قرآن [محکم و] حکمت‌آموز،'),
(3708, 36, 3, 'که همانا تو از پیامبرانی،'),
(3709, 36, 4, '[و] بر راه راست هستی.'),
(3710, 36, 5, '[قرآن] از جانب خداوند قادر مهربان نازل شده است.'),
(3711, 36, 6, 'تا مردمی را بیم دهی که پدرانشان بیم داده نشده‌اند، از این رو [از حقایق] غافلند.'),
(3712, 36, 7, 'به یقین فرمان [عذاب] بر بیشتر آنان سزاوار گشته است، پس ایشان ایمان نمی‌آورند.'),
(3713, 36, 8, 'ما در گردن‌های آنان غل‌هایی قرار دادیم که تا چانه‌هایشان را می‌پوشاند. چنان که سرهای آنان بالا مانده است.'),
(3714, 36, 9, 'و پیش روی آنان سدّی، و پشت سرشان نیز سدّی قرار دادیم، و به طور فراگیر دیدگانشان را پوشاندیم، پس هیچ چیز را نمی‌بینند.'),
(3715, 36, 10, 'برای آنها یکسان است که هشدارشان بدهی یا هشدارشان ندهی. آنان ایمان نخواهند آورد.'),
(3716, 36, 11, 'تنها کسی را [می‌توانی] هشدار دهی که از [این قرآن که] ذکر [و مایه‌ی یادآوری است،] پیروی کند و در نهان از [خدای] رحمان بترسد. پس او را به آمرزش و پاداشی پرارزش بشارت ده!'),
(3717, 36, 12, 'ما خود مردگان را زنده می‌کنیم. و آنچه را از پیش فرستاده‌اند و آنچه را از آنان بر جای مانده است، می‌نویسیم. و هر چیزی را در پیشوایی روشن برشمرده‌ایم.'),
(3718, 36, 13, 'و برای آنها اهل قریه [اَنطاکیه] را مثال بزن که فرستادگان خدا به سوی آنها آمدند.'),
(3719, 36, 14, 'آن‌گاه که دو نفر [از پیامبران خود] را به سوی آنان فرستادیم، پس تکذیبشان کردند، سپس با سومین [پیامبر، آن دو را] تقویت کردیم. پس گفتند: «ما [از طرف خدا] به سوی شما فرستاده شده‌ایم.»'),
(3720, 36, 15, '[کافران] گفتند: «شما تنها بشری مانند مایید. و [خدای] رحمان چیزی [بر شما] نازل نکرده است. شما فقط دروغ می‌گویید.»'),
(3721, 36, 16, '[پیامبران] گفتند: «پروردگار ما می‌داند که ما به سوی شما فرستاده شده‌ایم.'),
(3722, 36, 17, 'و بر ما، جز تبلیغ آشکار و روشن، وظیفه‌ی دیگری نیست.»'),
(3723, 36, 18, '[کافران] گفتند: «ما [حضور] شما را به فال بد گرفته‌ایم. [وجود شما شوم است و مایه‌ی بدبختی ما!] و اگر از حرفتان دست برندارید، قطعاً شما را سنگسار خواهیم کرد. و از سوی ما شکنجه‌ی دردناکی به شما خواهد رسید.»'),
(3724, 36, 19, '[پیامبران] گفتند: «شومی شما از خود شماست. آیا اگر پند داده شدید [باید فال بد بزنید]؟ بلکه شما مردمی [متجاوز و] اسراف‌کارید.»'),
(3725, 36, 20, 'و [در این میان] از دورترین جای شهر، مردی شتابان آمد و گفت: «ای قوم من! از این فرستادگان پیروی کنید!'),
(3726, 36, 21, 'از کسانی پیروی کنید که پاداشی از شما نمی‌خواهند، و خود هدایت‌یافته‌اند.'),
(3727, 36, 22, 'چرا نپرستم کسی را که مرا آفریده است. و همگی به سوی او بازگردانده می‌شوید؟'),
(3728, 36, 23, 'آیا به جای او، خدایانی را برگزینم که اگر [خداوند] رحمان برای من آسیب و زیانی بخواهد، شَفاعت آنها کمترین سودی برای من ندارد؟ و مرا نمی‌رهانند؟'),
(3729, 36, 24, 'در این صورت، من در گمراهی آشکاری خواهم بود.'),
(3730, 36, 25, '[ای مردم!] من به پروردگارتان ایمان آوردم، پس [شما نیز سخن مرا] بشنوید [و ایمان بیاورید]!»'),
(3731, 36, 26, '[سرانجام او را کشتند. و به او] گفته شد: «به بهشت وارد شو!» گفت: «ای کاش قوم من می‌دانستند.'),
(3732, 36, 27, 'که پروردگارم مرا بخشید و از گرامی‌داشتگان قرارم داد.»'),
(3733, 36, 28, 'و ما پس از [شهادت] او، لشکری از آسمان برای هلاک قوم او نفرستادیم و شیوه و سنّت ما پیش از این هم چنین نبود [که برای عذاب اهل زمین، سپاهی از آسمان بفرستیم].'),
(3734, 36, 29, '[عذابشان] تنها یک صیحه‌ی مرگبار بود. ناگهان همگی خاموش شدند.'),
(3735, 36, 30, 'ای دریغ بر بندگان! که هیچ پیامبری بر آنان نیامد، مگر آن که او را به استهزا گرفتند.'),
(3736, 36, 31, 'آیا [مشرکان مکه‌] ندیدند چه بسیار از اقوام پیش از آنان را [به سبب کفر و طغیانشان‌] نابود کردیم، که آنان هرگز نزد اینان [به این دنیا] برنمی‌گردند؟'),
(3737, 36, 32, 'و همه‌ی آنان در قیامت نزد ما احضار می‌شوند.'),
(3738, 36, 33, 'و زمین مرده، که ما آن را زنده کردیم و دانه‌ای از آن بیرون آوردیم که از آن می‌خورند، برای آنان نشانه‌ای است [بر امکان معاد].'),
(3739, 36, 34, 'و در آن باغ‌هایی از درختان خرما و انگور قرار دادیم و در آن چشمه‌ها شکافته و روان ساختیم.'),
(3740, 36, 35, 'تا از میوه‌ی آن و آنچه دستانشان به عمل آورده است، بخورند. پس چرا سپاسگزاری نمی‌کنند؟'),
(3741, 36, 36, 'منزّه است خدایی که تمام زوج‌ها را آفرید، از آنچه زمین می‌رویاند، و از خود مردم، و از آنچه نمی‌دانند.'),
(3742, 36, 37, 'و نشانه‌ای [دیگر] برای آنان شب است، که ما روز را [مانند پوست] از آن برمی‌کنیم، پس در تاریکی فرومی‌روند.'),
(3743, 36, 38, 'و خورشید به [سوی] قرارگاه خود روان است. آن [نظام]، اندازه‌گیری [خدا] عزیز داناست.'),
(3744, 36, 39, 'و برای ماه نیز منزلگاه‌هایی معین کرده‌ایم، تا همچون شاخه کهنه‌ی خرما، هلالی شکل و زردرنگ برگردد. [و باز به تدریج بدرِ کامل شود.]'),
(3745, 36, 40, 'نه خورشید را سزاست که به ماه رسد، و نه شب را سزد که بر روز پیشی گیرد. هر کدام در سپهر و مدار معینی شناورند.'),
(3746, 36, 41, 'و نشانه‌ای [دیگر] برای آنان، این است که ما فرزندانشان را در کشتی‌های پر از بار سوار کردیم.'),
(3747, 36, 42, 'و برای آنان چیزهای دیگری همانند کشتی آفریدیم که بر آن سوار می‌شوند.'),
(3748, 36, 43, 'و اگر بخواهیم آنان را غرق می‌کنیم، به گونه‌ای که نه فریادرسی برایشان باشد، و نه نجات داده شوند.'),
(3749, 36, 44, 'مگر آن که بار دیگر رحمت ما شامل حالشان شود و تا مدّتی [دیگر از زندگی] برخوردار باشند.'),
(3750, 36, 45, 'و هرگاه به آنان گفته شود: «از آنچه که پیش روی شماست و از آنچه به دنبال شماست، پروا کنید، تا شاید مورد رحمت قرار گیرید.»'),
(3751, 36, 46, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان برای آنان نمی‌آید، مگر آن که از آنها روی‌گردانند.'),
(3752, 36, 47, 'و هرگاه به آنان گفته شود: «از آنچه خداوند روزی شما کرده، بخشش کنید.» کسانی که کفر ورزیدند به کسانی که ایمان آورده‌اند، می‌گویند: «آیا به کسی غذا دهیم که اگر خداوند می‌خواست، [خودش] به او غذا می‌داد؟ شما در گمراهی آشکاری هستید.»'),
(3753, 36, 48, 'و [کافران] می‌گویند: «اگر راست می‌گویید، این وعده‌ی [قیامت] کی فرامی‌رسد؟»'),
(3754, 36, 49, 'آنان جز یک صیحه را انتظار نمی‌کشند که آنان را فراخواهد گرفت، در حالی که به کشمکش و ستیز [در امور دنیوی] سرگرمند.'),
(3755, 36, 50, 'پس در آن حال، نه توان وصیتی دارند، و نه می‌توانند به سوی خانواده‌هایشان بازگردند.'),
(3756, 36, 51, 'و در صور دمیده شود، پس ناگاه آنان از گورها [برخاسته،] شتابان به سوی پروردگارشان می‌آیند.'),
(3757, 36, 52, 'می‌گویند: «وای بر ما! چه کسی ما را از خوابگاهمان برانگیخت؟ این همان است که [خدای] رحمان وعده داد. و پیامبران راست گفتند.»'),
(3758, 36, 53, '[آری!] جز یک بانگ مرگبار نبود، که ناگهان همگی نزد ما احضار شدند.'),
(3759, 36, 54, 'پس در چنین روزی بر هیچ کس ستمی نمی‌شود. و جز آنچه عمل کردید، جزایی داده نمی‌شوید.'),
(3760, 36, 55, 'بی‌شک اهل بهشت در آن روز به خوشی مشغولند.'),
(3761, 36, 56, 'آنان و همسرانشان در زیر سایه‌ها بر تخت‌های آراسته تکیه می‌زنند.'),
(3762, 36, 57, 'در آنجا [هرگونه] میوه برای آنان آماده است. و هر آنچه بخواهند، برایشان فراهم است.'),
(3763, 36, 58, 'سلام، سخنی از [سوی] پروردگار مهربان [که می‌شنوند].'),
(3764, 36, 59, 'و «ای گناهکاران! امروز [از صف بهشتیان] جدا شوید!»'),
(3765, 36, 60, 'ای فرزندان آدم! مگر با شما پیمان نبستم که شیطان را اطاعت نکنید. او برای شما دشمنی آشکار است.'),
(3766, 36, 61, 'و تنها مرا بپرستید، که راه راست همین است.'),
(3767, 36, 62, 'امّا شیطان گروه زیادی از شما را گمراه کرد. پس چرا تعقّل نمی‌کردید؟'),
(3768, 36, 63, 'این همان جهنّمی است که به شما وعده داده می‌شد.'),
(3769, 36, 64, 'امروز وارد آن شوید، به خاطر کفری که داشتید.'),
(3770, 36, 65, 'امروز بر دهان‌هایشان مهر [خاموشی] می‌نهیم، و دست‌هایشان با ما سخن می‌گویند، و پاهایشان به اعمالی که همواره مرتکب می‌شدند، گواهی می‌دهند.'),
(3771, 36, 66, 'و اگر بخواهیم، دیدگانشان را محو می‌کنیم، پس آن‌گاه که خواستند در راه سبقت گیرند، چگونه خواهند دید؟'),
(3772, 36, 67, 'و اگر بخواهیم، آنان را در جای خود مسخ می‌کنیم [و به تندیس‌هایی بی‌روح یا جانورانی زشت مبدّل می‌سازیم،] به گونه‌ای که نه بتوانند به راه خود ادامه دهند، و نه برگردند.'),
(3773, 36, 68, 'و هر کس را عمر دراز دهیم، او را در آفرینش واژگونه می‌کنیم. [فراموشی، ناتوانی و سستی به سراغش می‌آید،] پس آیا نمی‌اندیشند؟'),
(3774, 36, 69, 'و ما به او [که پیامبر خداست،] شعر نیاموختیم و سزاوار او هم نیست. آن [چه به او آموختیم]، فقط یادآوری و قرآن روشنگر است.'),
(3775, 36, 70, 'تا هر که را زنده [دل] است، هشدار دهد و گفتار خدا درباره‌ی کافران محقَّق گردد.'),
(3776, 36, 71, 'آیا ندیدند که از آنچه دست‌های [قدرت] ما ایجاد کرده، برای آنان چهارپایانی آفریدیم که مالک آن هستند؟'),
(3777, 36, 72, 'و چهارپایان را برای آنان رام کردیم. از برخی سواری می‌گیرند و از برخی تغذیه می‌کنند.'),
(3778, 36, 73, 'و در آنها بهره‌های دیگری [چون پشم و پوست‌] و نوشیدنی‌هایی [چون شیر‌] برای مردم است. پس چرا سپاس نمی‌گزارند؟!'),
(3779, 36, 74, 'و به جای خداوند [یگانه]، خدایانی را گرفتند، به این امید که یاری شوند.'),
(3780, 36, 75, '[در حالی که آن] خدایان، توان یاری آنان را ندارند. و اینها [در قیامت] به عنوان لشکر [بت‌ها] احضار می‌شوند.'),
(3781, 36, 76, 'پس سخنان مشرکان، تو را محزون نکند. ما آنچه را پنهان می‌دارند و آنچه را آشکار می‌کنند، می‌دانیم.'),
(3782, 36, 77, 'آیا انسان ندید [و نیاندیشید] که ما او را از نطفه‌ای [بی‌مقدار] آفریدیم؟ پس اینک ستیزه‌جویی آشکار شده است.'),
(3783, 36, 78, 'و برای ما مَثَلی آورد و آفرینش خود را فراموش کرد. گفت: «چه کسی استخوان‌هایی را که پوسیده است، زنده خواهد کرد؟»'),
(3784, 36, 79, '[به او] بگو: «همان کسی که نخستین بار آن را آفرید، آن را زنده خواهد کرد. و او بر هر آفریده‌ای آگاه است.'),
(3785, 36, 80, 'همان که برای شما از درخت سبز، آتش آفرید؛ پس هرگاه بخواهید، از آن آتش می‌افروزید.»'),
(3786, 36, 81, 'آیا کسی که آسمان‌ها و زمین را آفریده است، توانا نیست که همانند آنها را بیافریند؟ آری، و او آفریدگار بسیار داناست.'),
(3787, 36, 82, 'امر و فرمان او چنین است که هرگاه چیزی را اراده کند، تنها به آن می‌گوید: «باش!» پس بی‌درنگ موجود می‌شود.'),
(3788, 36, 83, 'پس منزّه است کسی که حاکمیت و مالکیت همه چیز به دست اوست و به سوی او بازگردانده می‌شوید.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به صف‌بستگان که صفی [باشکوه‌] بسته‌اند،'),
(3790, 37, 2, 'و [انسان را از گناهان‌] به شدّت بازمی‌دارند،'),
(3791, 37, 3, 'و پی‌درپی ذکر خدا را تلاوت می‌کنند،'),
(3792, 37, 4, 'که بی‌شک معبود شما یکتاست.'),
(3793, 37, 5, 'پروردگار آسمان‌ها و زمین، و آنچه میان آن دو است، و پروردگار مشرق‌ها.'),
(3794, 37, 6, 'ما آسمان دنیا را به زیور ستارگان آراستیم.'),
(3795, 37, 7, 'و [آن را] از هر شیطان سرکشی نگاه داشتیم.'),
(3796, 37, 8, 'آن‌ها نمی‌توانند به [اسرار] عالم بالا گوش فرادهند و از هر سو به آنها [شهاب] پرتاب می‌شود،'),
(3797, 37, 9, 'تا رانده شوند. و برای آن‌ها عذابی پاینده است.'),
(3798, 37, 10, 'مگر آن که شیطانی به سرعت خبری را برباید، [و بگریزد،] که شهاب و شراره‌ای نافذ او را دنبال [و نابود] می‌کند.'),
(3799, 37, 11, 'پس از آنان بپرس که آفرینش آنان سخت‌تر است یا کسانی که ما آفریدیم؟ ما آنان را از گِلی چسبنده آفریدیم.'),
(3800, 37, 12, 'بلکه تو [از انکار آنان] تعجب می‌کنی. و آنان [تو را] مسخره می‌کنند.'),
(3801, 37, 13, 'و هرگاه اندرز داده شوند، پند نمی‌گیرند.'),
(3802, 37, 14, 'و هرگاه معجزه‌ای بینند، یکدیگر را به مسخره کردن دعوت می‌کنند.'),
(3803, 37, 15, 'و می‌گویند: «این جز جادویی آشکار نیست!'),
(3804, 37, 16, 'آیا چون مردیم و خاک و استخوان‌هایی [پوسیده] شدیم، آیا [دوباره] برانگیخته خواهیم شد؟!'),
(3805, 37, 17, 'و آیا پدران درگذشته ما نیز [زنده خواهند شد]؟»'),
(3806, 37, 18, 'بگو: «آری! [همه زنده می‌شوید،] و خوار و کوچک خواهید بود.»'),
(3807, 37, 19, 'همانا قیامت با یک صیحه است، پس آنگاه آنان [حیرت‌زده منظره قیامت را] می‌نگرند.'),
(3808, 37, 20, 'و می‌گویند: «وای بر ما! این روز جزاست!»'),
(3809, 37, 21, '[به آنها گفته می‌شود:] «این همان روز جدایی [حقّ از باطل] است که همواره آن را تکذیب می‌کردید.»'),
(3810, 37, 22, '[خداوند به فرشتگان دستور می‌دهد:] «ستمکاران و هم‌ردیفانشان و آنچه را می‌پرستیدند، گردآورید.'),
(3811, 37, 23, 'با آنچه به جای خدا می‌پرستیدند، پس همه را به راه دوزخ راهنمایی کنید.'),
(3812, 37, 24, 'آنان را نگه دارید که حتماً مورد بازخواست قرار می‌گیرند.»'),
(3813, 37, 25, '[به آنان گفته می‌شود:] «چه شد که امروز یکدیگر را یاری نمی‌کنید؟»'),
(3814, 37, 26, '[آری، نمی‌توانند یکدیگر را یاری کنند،] بلکه آنان امروز کاملاً تسلیم [قدرت خداوند] هستند.'),
(3815, 37, 27, 'و به یکدیگر روی کرده، از [حال‌] هم می‌پرسند.'),
(3816, 37, 28, '[پیروان گمراه به پیشوایان گمراه‌کننده] می‌گویند: «بی‌شک شما بودید که از راه خیرخواهی به سوی ما می‌آمدید [ولی ما را به کفر و شرک کشاندید].»'),
(3817, 37, 29, 'آنها می‌گویند: «بلکه شما خود اهل ایمان نبودید.'),
(3818, 37, 30, 'و ما را بر شما سلطه و غلبه‌ای نبود، بلکه شما خود گروهی سرکش بودید.'),
(3819, 37, 31, 'پس وعده‌ی [عذاب] پروردگارمان بر ما محقّق شد. و بی‌گمان ما عذاب را خواهیم چشید.'),
(3820, 37, 32, 'پس ما شما را گمراه کردیم، زیرا که ما خود نیز گمراه بودیم.»'),
(3821, 37, 33, 'پس آنان در آن روز، در عذاب [الهی] شریکند.'),
(3822, 37, 34, 'همانا ما با مجرمان این‌گونه رفتار می‌کنیم.'),
(3823, 37, 35, 'آنان چنین بودند که هرگاه به آنان می‌گفتند: «معبودی جز الله نیست.» [از پذیرش آن] تکبّر می‌ورزیدند.'),
(3824, 37, 36, 'و می‌گفتند: «آیا ما به خاطر شاعری دیوانه، خدایانمان را رها کنیم؟»'),
(3825, 37, 37, '[هرگز چنین نیست!] بلکه او حقّ آورده و پیامبران [پیشین] را تصدیق کرده است.'),
(3826, 37, 38, 'شما نیز عذاب دردناک الهی را خواهید چشید.'),
(3827, 37, 39, 'و جز آنچه کرده‌اید، کیفر نمی‌بینید.'),
(3828, 37, 40, 'مگر بندگان خالص شده‌ی خدا [که از کیفر برکنارند].'),
(3829, 37, 41, 'آنانند که برایشان روزی معین و ویژه‌ای است.'),
(3830, 37, 42, 'انواع میوه‌ها. و آنان مورد احترامند.'),
(3831, 37, 43, 'در باغ‌های پر نعمت [بهشت].'),
(3832, 37, 44, 'بر تخت‌هایی در برابر یکدیگر [تَکیه زده‌اند].'),
(3833, 37, 45, 'جامی لبریز از شرابی پاک و ناب گرداگردشان می‌گردانند،'),
(3834, 37, 46, 'شرابی زلال و درخشان، و لذّت‌بخش برای نوشندگان.'),
(3835, 37, 47, '[شرابی که] نه مایه‌ی فساد عقل است. و نه آنان از نوشیدن آن مست می‌شوند.'),
(3836, 37, 48, 'و نزدشان همسرانی است [زیبا و] درشت چشم که چشم از غیر فرومی‌بندند.'),
(3837, 37, 49, '[از شدّت لطافت و سپیدی] گویی تخم‌مرغ‌هایی هستند که [در زیر بال و پر مرغ،] پوشیده و پنهان مانده [و هرگز دست کسی به آن نرسیده] است.'),
(3838, 37, 50, 'پس به یکدیگر روی کرده، از [حال‌] هم می‌پرسند.'),
(3839, 37, 51, 'یکی از آنان می‌گوید: «[در دنیا] همنشینی داشتم،'),
(3840, 37, 52, 'که پیوسته می‌گفت»: «آیا تو به راستی [قیامت را] باور داری؟'),
(3841, 37, 53, 'که هرگاه مردیم، و خاک، و استخوان‌هایی [پوسیده] شدیم، [دوباره زنده می‌شویم و] کیفر خواهیم شد؟»'),
(3842, 37, 54, '[آن بنده‌ی خدا به دوستان بهشتی‌اش] می‌گوید: «آیا شما می‌توانید [بر حال او] اطّلاع یابید؟»'),
(3843, 37, 55, 'پس از حال او اطّلاع می‌یابد، و او را در میان دوزخ می‌بیند.'),
(3844, 37, 56, 'به او می‌گوید: «به خدا سوگند نزدیک بود که مرا به نابودی بکشانی.'),
(3845, 37, 57, 'و اگر نعمت [و لطف] پروردگارم نبود، قطعاً [من نیز] از احضارشدگان [در دوزخ] بودم.»'),
(3846, 37, 58, '[سپس به یاران بهشتی خود می‌گوید:] «آیا ما [برای همیشه در بهشتیم، و] دیگر نمی‌میریم؟'),
(3847, 37, 59, 'جز همان مرگ نخستین [که در دنیا سراغمان آمد،] و ما هرگز عذاب نخواهیم شد؟'),
(3848, 37, 60, 'قطعاً این رستگاری بزرگی است.»'),
(3849, 37, 61, 'باید اهل عمل برای چنین جایگاهی کار کنند.'),
(3850, 37, 62, 'آیا آن [نعمت‌های بهشتی] برای پذیرایی بهتر است یا درخت زقّوم؟'),
(3851, 37, 63, 'ما درخت زقّوم را مایه‌ی شکنجه، و درد و رنج ستمگران قرار داده‌ایم.'),
(3852, 37, 64, 'این درخت از قعر دوزخ می‌روید.'),
(3853, 37, 65, 'شکوفه‌ی آن مانند سرهای شیطان‌ها [بسیار زشت و بدنما] است.'),
(3854, 37, 66, 'پس دوزخیان از آن می‌خورند و شکم‌ها را از آن پر می‌کنند.'),
(3855, 37, 67, 'سپس بر روی آن [غذا]، مخلوطی از آب جوشان و سوزنده برای آنان خواهد بود.'),
(3856, 37, 68, 'پس بازگشت آنان به سوی دوزخ است.'),
(3857, 37, 69, '[چرا که] آنان پدران خویش را گمراه یافتند.'),
(3858, 37, 70, 'امّا با این حال، در پی آنان شتابان می‌دوند.'),
(3859, 37, 71, 'بی‌گمان قبل از آنان، بیشتر پیشینیان [نیز] گمراه شدند.'),
(3860, 37, 72, 'و قطعاً ما در میانشان بیم‌دهندگانی فرستادیم.'),
(3861, 37, 73, 'پس [اینک] بنگر که سرانجام هشدارداده شدگان چگونه بود؟'),
(3862, 37, 74, '[همه‌ی آنان نابود شدند،] مگر بندگان خالص شده‌ی خدا.'),
(3863, 37, 75, 'و به راستی نوح، ما را [به فریادرسی] ندا داد، [و ندای او را اجابت کردیم] پس چه خوب پاسخ‌دهنده‌ای هستیم!'),
(3864, 37, 76, 'و او و خانواده‌اش را از اندوه بزرگ رهانیدیم.'),
(3865, 37, 77, 'و نسل او را باقی گذاشتیم.'),
(3866, 37, 78, 'و در میان آیندگان برای او [نام نیک] به جا گذاشتیم.'),
(3867, 37, 79, 'سلام بر نوح در میان جهانیان.'),
(3868, 37, 80, 'ما نیکوکاران را اینگونه پاداش می‌دهیم.'),
(3869, 37, 81, 'به راستی که او از بندگان مؤمن ما بود.'),
(3870, 37, 82, 'سپس دیگران را غرق کردیم.'),
(3871, 37, 83, 'و بی‌گمان، ابراهیم از پیروان نوح بود.'),
(3872, 37, 84, 'آن‌گاه که با قلبی سلیم به پیشگاه پروردگارش آمد.'),
(3873, 37, 85, 'آن‌گاه که به سرپرستش و قومش گفت: «این [بت‌ها] چیست که می‌پرستید؟'),
(3874, 37, 86, 'آیا به دروغ، معبودهای دیگری جز خدا می‌خواهید؟'),
(3875, 37, 87, 'شما درباره پروردگار جهانیان چه گمان می‌کنید؟»'),
(3876, 37, 88, 'پس [چون مردم برای برپایی مراسم عید، خواستند از شهر بیرون بروند، ابراهیم] نگاه خاصّی به ستارگان انداخت.'),
(3877, 37, 89, 'و گفت: «من در آستانه‌ی بیماری‌ام [و نمی‌توانم در مراسم شما شرکت کنم.]»'),
(3878, 37, 90, 'مردم از او روی گردانده، برگشتند.'),
(3879, 37, 91, 'پس [ابراهیم] پنهانی به سراغ خدایان آنان رفت و گفت: «چرا غذا نمی‌خورید؟'),
(3880, 37, 92, 'شما را چه شده؟ چرا سخن نمی‌گویید؟»'),
(3881, 37, 93, 'پس به سراغ بت‌ها رفت و با قدرت ضربه محکمی بر آنها فرود آورد [و خردشان کرد].'),
(3882, 37, 94, 'مردم [چون بازگشتند،] شتابان به سوی او روی آوردند.'),
(3883, 37, 95, '[ابراهیم] گفت: «آیا آنچه را خود می‌تراشید، می‌پرستید؟'),
(3884, 37, 96, 'در حالی که خداوند، شما و آنچه را می‌سازید، آفریده است.»'),
(3885, 37, 97, 'گفتند: «برای [کیفر] او بنایی بسازید [که گنجایش آتشی بزرگ داشته باشد،] پس او را در آتش افروخته بیفکنید!»'),
(3886, 37, 98, 'پس خواستند برای او نقشه‌ای بکشند، ولی ما آنان را زیردست و شکست‌خورده قرار دادیم.'),
(3887, 37, 99, '[ابراهیم از این مهلکه به سلامت بیرون آمد] و گفت: «من [از شما کناره می‌گیرم. و] به سوی پروردگارم رهسپارم. او مرا راهنمایی خواهد کرد.'),
(3888, 37, 100, 'پروردگارا! [فرزندی] از صالحان به من عطا فرما!»'),
(3889, 37, 101, 'پس ما او را به نوجوانی بردبار مژده دادیم.'),
(3890, 37, 102, 'پس چون [اسماعیل به سنّی رسید که] در کار و کوشش، پدرش را همراهی کرد، [ابراهیم] گفت: «پسر عزیزم! من [مرتّب] در خواب می‌بینم که تو را ذبح می‌کنم. پس بگو نظرت چیست و آن را چگونه می‌بینی؟» [اسماعیل] گفت: «ای پدر! آنچه را مأمور شده‌ای، انجام بده، که به زودی اگر خدا بخواهد، مرا از صبرکنندگان خواهی یافت.»'),
(3891, 37, 103, 'پس چون هر دو تسلیم [فرمان ما] شدند و ابراهیم، گونه‌ی فرزند را بر خاک نهاد [تا ذبحش کند]، او را ندا دادیم که: «ای ابراهیم!'),
(3892, 37, 104, 'حقّاً که رؤیایت را تحقّق بخشیدی [و امر ما را اطاعت کردی].»'),
(3893, 37, 105, 'ما نیکوکاران را این گونه پاداش می‌دهیم [و نیت خیر آنان را به جای عمل می‌پذیریم].'),
(3894, 37, 106, 'همانا این همان آزمایش آشکار بود.'),
(3895, 37, 107, 'و ما او را در برابر قربانی بزرگی [از ذبح شدن‌] رهانیدیم.'),
(3896, 37, 108, 'در میان آیندگان برای او [نام نیک] به جا گذاشتیم.'),
(3897, 37, 109, 'درود و سلام بر ابراهیم!'),
(3898, 37, 110, 'ما این گونه نیکوکاران را پاداش می‌دهیم.'),
(3899, 37, 111, 'او از بندگان مؤمن ما بود.'),
(3900, 37, 112, 'و او را به اسحاق مژده دادیم که پیامبری از شایستگان بود.'),
(3901, 37, 113, 'و بر ابراهیم و اسحاق، برکت و خیر بسیار عطا کردیم. و از نسل آن دو [برخی] نیکوکارند و [برخی] آشکارا به خود ستم می‌کنند.'),
(3902, 37, 114, 'و به راستی ما بر موسی و هارون منّت نهادیم.'),
(3903, 37, 115, 'و آن دو و قومشان را از اندوه بزرگ نجات دادیم.'),
(3904, 37, 116, 'و آنان را یاری کردیم، پس آنان بودند که پیروز شدند.'),
(3905, 37, 117, 'و به آن دو، کتاب روشنگر دادیم.'),
(3906, 37, 118, 'و آن دو را به راه راست هدایت کردیم.'),
(3907, 37, 119, 'و برای آن دو در میان آیندگان [نام نیک] به جا گذاشتیم.'),
(3908, 37, 120, 'سلام بر موسی و هارون!'),
(3909, 37, 121, 'ما این گونه نیکوکاران را پاداش می‌دهیم.'),
(3910, 37, 122, 'آن دو از بندگان مؤمن ما بودند.'),
(3911, 37, 123, 'و به راستی اِلیاس از فرستادگان [ما] بود.'),
(3912, 37, 124, 'آن‌گاه که به قوم خود گفت: «آیا [از خدا] پروا نمی‌کنید؟'),
(3913, 37, 125, 'آیا [بتِ] بَعل را می‌خوانید و بهترین آفریدگار را رها می‌کنید؟'),
(3914, 37, 126, 'الله را که پروردگار شما و پروردگار پدران نخستین شماست.»'),
(3915, 37, 127, 'پس او را تکذیب کردند. و بی‌شک آنان [برای کیفر] احضار خواهند شد.'),
(3916, 37, 128, 'به جز بندگان خالص شده‌ی خدا [که از هر کیفری در امانند].'),
(3917, 37, 129, 'و برای الیاس در میان آیندگان [نام نیک] بر جای گذاشتیم.'),
(3918, 37, 130, 'سلام و درود بر الیاس.'),
(3919, 37, 131, 'ما این‌گونه نیکوکاران را پاداش می‌دهیم.'),
(3920, 37, 132, 'او از بندگان مؤمن ما بود.'),
(3921, 37, 133, 'و همانا لوط یکی از فرستادگان [ما] بود.'),
(3922, 37, 134, 'آن‌گاه که او و همه‌ی خانواده‌اش را نجات دادیم.'),
(3923, 37, 135, 'جز پیرزنی که در میان بازماندگان [و هلاک‌شدگان] بود.'),
(3924, 37, 136, 'سپس دیگران را نابود کردیم.'),
(3925, 37, 137, 'و شما [اهل مکّه در مسیر سفرهایتان به شام] از کنار [ویرانه‌های شهر] آنان می‌گذرید، صبحگاه،'),
(3926, 37, 138, '[و نیز] شبانگاه؛ پس آیا نمی‌اندیشید؟'),
(3927, 37, 139, 'و همانا یونس از فرستادگان [ما] بود.'),
(3928, 37, 140, 'آن‌گاه که [از قوم خود] به سوی کشتیِ پر [از بار و مسافر] گریخت.'),
(3929, 37, 141, '[کشتی به خطر افتاد، و قرار شد بر اساس قرعه، افرادی به دریا افکنده شوند،] پس یونس با سرنشینان کشتی قرعه انداخت. و از بازندگان شد.'),
(3930, 37, 142, 'پس [او را به دریا افکندند.] ماهی بزرگی او را بلعید و او خود را سرزنش می‌کرد.'),
(3931, 37, 143, '[در شکم ماهی به تسبیح خداوند پرداخت]. پس اگر از تسبیح‌گویان نبود،'),
(3932, 37, 144, 'بی‌تردید تا روز قیامت در شکم ماهی می‌ماند.'),
(3933, 37, 145, 'پس او را در حالی که بیمار بود، به زمین خشکی افکندیم.'),
(3934, 37, 146, 'و بر او بوته‌ای از کدو رویاندیم.'),
(3935, 37, 147, 'و او را [پس از بهبودی،] به سوی یکصد هزار نفر یا بیش‌تر فرستادیم.'),
(3936, 37, 148, 'پس ایمان آوردند. و تا مدّتی آنان را کامیاب و بهره‌مند کردیم.'),
(3937, 37, 149, '[مشرکان] فرشتگان را دختران خدا می‌شمرند! پس از آنان بپرس: آیا دختران برای پروردگارت، و پسران برای آنان است؟!'),
(3938, 37, 150, 'یا ما فرشتگان را زن آفریدیم و آنان ناظر بودند؟!'),
(3939, 37, 151, 'آگاه باشید که آنان از سر تهمت می‌گویند:'),
(3940, 37, 152, '«خدا فرزند آورده است!» و البتّه آنان دروغگویند.'),
(3941, 37, 153, 'آیا [گمان می‌کنید] خداوند دختران را بر پسران برگزیده است؟'),
(3942, 37, 154, 'شما را چه شده است؟ چگونه داوری می‌کنید؟'),
(3943, 37, 155, 'پس آیا پند نمی‌گیرید؟'),
(3944, 37, 156, 'آیا برای شما دلیل روشنی است؟'),
(3945, 37, 157, 'پس اگر راست می‌گویید، کتاب خود را بیاورید.'),
(3946, 37, 158, 'و [گروه دیگری از مشرکان] میان خدا و جنّ نسبتی قائل شدند، در حالی که جنّیان به خوبی می‌دانند که آنها [در روز قیامت] احضار خواهند شد.'),
(3947, 37, 159, 'خداوند از آنچه آنان می‌گویند، منزّه است.'),
(3948, 37, 160, 'جز بندگان خالص شده‌ی خدا [که او را به شایستگی وصف می‌کنند].'),
(3949, 37, 161, 'پس شما و آنچه می‌پرستید،'),
(3950, 37, 162, 'نمی‌توانید [مردم را] بر ضدّ خدا گمراه کنید،'),
(3951, 37, 163, 'مگر کسی را که خودش [با اراده و اختیار] به دنبال دوزخ باشد.'),
(3952, 37, 164, 'و [فرشتگان می‌گویند:] «هیچ کس از ما نیست مگر آن که برای او جایگاهی معین است.'),
(3953, 37, 165, 'و ماییم که [برای اطاعت فرمان خداوند] صف کشیده‌ایم.'),
(3954, 37, 166, 'و ماییم که تسبیح‌گوی هستیم.»'),
(3955, 37, 167, '[مشرکان پیش از بعثت پیامبر] پیوسته و با تأکید می‌گفتند:'),
(3956, 37, 168, '«اگر پندنامه‌ای از نوع [کتاب‌های] پیشینیان نزد ما بود،'),
(3957, 37, 169, 'ما نیز حتما از بندگان برگزیده‌ی خدا بودیم.»'),
(3958, 37, 170, 'پس [همین که قرآن برای آنان آمد،] به آن کفر ورزیدند. و به زودی [نتیجه‌ی کفر خود را] خواهند دانست.'),
(3959, 37, 171, 'و بی‌شک فرمان ما برای بندگانی که به پیامبری فرستاده شده‌اند، از پیش معین شده است.'),
(3960, 37, 172, 'همانا ایشانند که پیروزند.'),
(3961, 37, 173, 'و سپاه ماست که پیروز است.'),
(3962, 37, 174, 'پس تا مدّتی [معین از کافران] روی بگردان.'),
(3963, 37, 175, 'و بنگر که به زودی [کیفر عنادشان را] خواهند دید.'),
(3964, 37, 176, 'پس آیا برای عذاب ما شتاب می‌کنند؟!'),
(3965, 37, 177, 'پس هرگاه [عذاب ما] به آستانه‌ی آنان فرود آید، آنان که هشدار داده شدند، چه بد صبحگاهی خواهند داشت.'),
(3966, 37, 178, 'و تا مدّتی [معین] از آنان روی بگردان.'),
(3967, 37, 179, 'و بنگر، که به زودی خواهند دید [عاقبتشان چه می‌شود].'),
(3968, 37, 180, 'پاک و منزّه است پروردگارت، پروردگار عزیز، از آنچه درباره‌ی او وصف می‌کنند.'),
(3969, 37, 181, 'و سلام بر همه‌ی فرستادگان.'),
(3970, 37, 182, 'و سپاس و ستایش مخصوص خداوندی است که پروردگار جهانیان است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صاد. سوگند به قرآن پندآموز [که تو از پیامبرانی]!'),
(3972, 38, 2, 'آری! کسانی که کفر ورزیدند، سخت در سرکشی و ستیزند.'),
(3973, 38, 3, 'چه بسیار اقوام پیش از اینان که [به خاطر کفر و نفاق] نابودشان کردیم، پس فریاد کمک‌خواهی سردادند، امّا دیگر زمان فرار نبود.'),
(3974, 38, 4, 'و کافران تعجب کردند که هشداردهنده‌ای از خودشان به سراغشان آمده و گفتند: «این، جادوگری دروغگوست.'),
(3975, 38, 5, 'آیا او به جای معبودهای گوناگون، یک معبود قرار داده است؟ البتّه که این، چیزی بسیار عجیب است!»'),
(3976, 38, 6, 'سردمداران کفر، [از مجلس پیامبر] بیرون آمدند [و گفتند]: «بروید و بر پرستش خدایان خود پایدار بمانید، که این چیزی است که [از شما] خواسته شده است.'),
(3977, 38, 7, 'ما این چیزها را [که محمّد می‌گوید،] در آخرین آیین نشنیده‌ایم. این آیین، آیینی ساختگی است.'),
(3978, 38, 8, 'آیا از میان همه ما، قرآن بر او نازل شده است؟» [این حرف‌ها بهانه‌ای بیش نیست؛] بلکه آنان نسبت به قرآن، در شک هستند. آری، آنان هنوز عذاب مرا نچشیده‌اند.'),
(3979, 38, 9, 'مگر گنجینه‌های رحمت پروردگار عزیز و بخشنده‌ات در اختیار آنهاست؟ [تا وحی بر کسانی که آنان می‌خواهند، نازل شود.]'),
(3980, 38, 10, 'یا فرمانروایی آسمان‌ها و زمین، و آنچه میان آنهاست، از آنِ ایشان است؟ [اگر چنین است‌] پس با امکاناتی که دارند، بالا بروند [و از نزول وحی بر کسی که ما می‌خواهیم، جلوگیری کنند!]'),
(3981, 38, 11, 'آنان لشکر کوچکی از احزابِ شکست‌خورده‌اند.'),
(3982, 38, 12, 'پیش از این کافران، قوم نوح، و عاد، و فرعونِ قدرتمند [نیز]، پیامبران را تکذیب کردند.'),
(3983, 38, 13, 'قوم ثمود، و قوم لوط، و مردم ایکه [قوم شعیب،] آنان نیز گروه‌هایی بودند.'),
(3984, 38, 14, 'هر یک از آن گروه‌ها، پیامبران را تکذیب کردند و عذاب الهی در مورد آنان تحقّق یافت.'),
(3985, 38, 15, 'گویا این کافران تنها چشم به راه صیحه‌ای مرگبارند، که به دنبالش بازگشتی برای آنان نیست،'),
(3986, 38, 16, 'و [با تمسخر و غرور] گفتند: «پروردگارا! پیش از [فرارسیدن] روز حساب، سهم ما را [از عذاب] هرچه زودتر به ما بده!»'),
(3987, 38, 17, '[ای پیامبر!] بر آنچه می‌گویند شکیبا باش و بنده ما داود را یاد کن که نیرومند بود، امّا همواره روی به سوی درگاه ما داشت.'),
(3988, 38, 18, 'همانا ما کوه‌ها را رام کردیم که در شامگاهان و بامدادان، همراه او تسبیح گویند.'),
(3989, 38, 19, 'و پرندگان را [نیز] گرد آوردیم، و [همگی با داود تسبیح خدا می‌گفتند و] به سوی خدا بازمی‌گشتند.'),
(3990, 38, 20, 'و فرمانروایی و حکومت او را استوار داشتیم، و به او حکمت، و گفتار پایان‌بخش [در داوری] دادیم.'),
(3991, 38, 21, 'و آیا خبر آن دادخواهان به تو رسیده است که از دیوار محراب [داود] بالا رفتند؟'),
(3992, 38, 22, 'آن‌گاه که [ناگهان] بر او وارد شدند، و [داود] از [مشاهده‌ی] آنان به هراس افتاد. شاکیان گفتند: «نترس! ما دو نفر درگیر شده‌ایم و یکی از ما بر دیگری ستم کرده است، پس میان ما به حقّ داوری کن و ستم روا مدار! و ما را به راه راست راهنمایی فرما!'),
(3993, 38, 23, 'این برادر من است که نود و نُه میش دارد و من تنها یک میش دارم؛ امّا [با آن همه سرمایه، به من] می‌گوید: آن یک میش را هم به من واگذار! و در گفتگو بر من چیره شده است.»'),
(3994, 38, 24, '[داود] گفت: «بی‌تردید او با درخواست افزودن میش تو به میش‌های خودش، به تو ستم کرده است. و البتّه بسیاری از شریکان، بر یکدیگر ستم می‌کنند، مگر کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند. و آنان اندکند.» و [داود] متوجّه شد که ما او را [با این ماجرا] آزمایش کرده‌ایم، پس از پروردگارش آمرزش خواست و به رکوع افتاد و توبه کرد.'),
(3995, 38, 25, 'پس ما آن [قضاوت] را بر او بخشیدیم. و البتّه او نزد ما مقامی والا و سرانجامی نیک دارد.'),
(3996, 38, 26, 'ای داود! ما تو را در زمین جانشین قرار دادیم، پس میان مردم به حقّ داوری کن. و از هوا و هوس‌ها پیروی مکن که تو را از راه خدا منحرف می‌کند. البتّه کسانی که از راه خدا منحرف می‌شوند، برایشان عذاب سختی است، برای آن که روز حساب را فراموش کردند.'),
(3997, 38, 27, 'و ما آسمان و زمین، و آنچه را میان آنهاست، بیهوده نیافریدیم. این، پندار کسانی است که کفر ورزیدند. پس وای بر کافران از آتش [دوزخ]!'),
(3998, 38, 28, 'آیا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، همانند فسادگرانِ در زمین قرار می‌دهیم؟ یا پرهیزکاران را مانند بدکاران قرار می‌دهیم؟'),
(3999, 38, 29, '[این] کتاب مبارکی است که به سوی تو فروفرستادیم، تا در آیات آن تدبّر کنند. و خردمندان پند گیرند.'),
(4000, 38, 30, 'و ما به داود سلیمان را بخشیدیم. او بنده‌ی خوبی بود، زیرا بسیار به درگاه ما روی می‌آورد.'),
(4001, 38, 31, '[یاد کن] آن‌گاه که در پایان روز، اسبان چابک تندرو بر سلیمان عرضه شد [و او مشغول سان دیدن بود].'),
(4002, 38, 32, 'پس گفت: «من دوستی این اسبان را به خاطر یاد پروردگارم دوست دارم» [و فرمان داد آنها را دواندند] تا [از دیدگانش] پنهان شدند.'),
(4003, 38, 33, '[سپس دستور داد:] آنها را نزد من بازگردانید، پس ساق‌ها و گردن‌های اسبان را دست کشید.'),
(4004, 38, 34, 'و به راستی ما سلیمان را آزمودیم. و بر تخت او جسدی [بی‌جان از فرزندش‌] افکندیم، پس به درگاه خدا توبه کرد.'),
(4005, 38, 35, 'و گفت: «پروردگارا! مرا بیامرز و به من فرمانروایی و ملکی عطا کن که پس از من سزاوار هیچ کس نباشد. البتّه تو بسیار بخشنده‌ای.»'),
(4006, 38, 36, 'پس ما باد را مسخّر او ساختیم که به فرمان او هر جا بخواهد، به نرمی و آرامی حرکت کند.'),
(4007, 38, 37, 'و جنّ‌های سرکش را [رامِ او ساختیم،] هر [کدام] بنّایی [می‌دانند، برایش بسازند،] و [هر کدام] غوّاصی [می‌دانند، جواهرات استخراج کنند].'),
(4008, 38, 38, 'و گروه دیگری از جنّ‌ها با غل و زنجیر به هم بسته بودند. [تا نتوانند در حکومت او فتنه و آشوب کنند.]'),
(4009, 38, 39, '«[ای سلیمان!] این بخشش بی‌حساب ماست. به هرکس می‌خواهی ببخش، یا دریغ کن.»'),
(4010, 38, 40, 'و او نزد ما مقامی والا و سرانجامی نیک دارد.'),
(4011, 38, 41, 'و بنده‌ی ما ایوب را یاد کن، آن‌گاه که پروردگارش را ندا داد: «[پروردگارا!] شیطان مرا به رنج و عذاب افکنده است!»'),
(4012, 38, 42, '[به او گفتیم:] «پای خود را بر زمین بزن [تا از زیر پای تو چشمه‌ای جاری کنیم]. این چشمه‌ی آبی خنک برای شستشو و نوشیدن است.»'),
(4013, 38, 43, 'و ما بر اساس رحمت خویش، خانواده‌اش را [که در اثر بیماری سخت و طولانی، از او جدا شده بودند،] به او بخشیدیم و برگرداندیم، و همانند آنها را به آنان افزودیم، تا برای خردمندان پندی باشد.'),
(4014, 38, 44, '[او سوگند یاد کرده بود که به خاطر تخلّف همسرش، او را تنبیه کند؛ امّا چون همسرش وفادار بود به او گفتیم:] «دسته‌ای از شاخه‌های نازک گیاه به دست خود بگیر و با آن بزن، [تا بدن همسرت آزرده نشود.] و سوگند را مشکن!» ما ایوب را صابر یافتیم. او بنده‌ی خوبی بود، زیرا بسیار به درگاه ما روی می‌آورد.'),
(4015, 38, 45, 'و بندگان ما ابراهیم و اسحاق و یعقوب را یاد کن که دارای قوّت و بصیرت بودند.'),
(4016, 38, 46, 'ما آنان را با خلوص ویژه‌ای که یادآور سرای قیامت بود، خالص کردیم.'),
(4017, 38, 47, 'و آنان در پیشگاه ما از برگزیدگان و نیکانند.'),
(4018, 38, 48, 'و اسماعیل و یسع و ذوالکفل را یاد کن که همه از نیکانند.'),
(4019, 38, 49, 'این، یاد [ما از آنان در دنیا] است. و البتّه برای پرهیزکاران بازگشت‌گاهی نیکو خواهد بود.'),
(4020, 38, 50, 'باغ‌های جاودانه‌ای که درهایش به روی آنان گشوده است.'),
(4021, 38, 51, 'در آنجا [بر تخت‌ها] تکیه می‌زنند. [و] میوه‌های فراوان و نوشیدنی در آنجا می‌طلبند.'),
(4022, 38, 52, 'و نزدشان همسرانی است [زیبا] که چشم از غیر فرومی‌بندند، و همتا و هم‌سال آنها هستند.'),
(4023, 38, 53, 'این است آنچه برای روز حساب به شما وعده داده می‌شود.'),
(4024, 38, 54, 'این روزی ماست که برای آن پایانی نیست.'),
(4025, 38, 55, 'این [است پاداش بهشتیان]! و البتّه سرکشان بازگشت‌گاه بدی دارند:'),
(4026, 38, 56, 'دوزخ، که در آن وارد می‌شوند. چه بد بستری است!'),
(4027, 38, 57, 'این آب داغ و چرکابی است که باید آن را بچشند.'),
(4028, 38, 58, 'و عذاب‌های دیگری مانند آن دارند.'),
(4029, 38, 59, '[به پیشوایان گمراه گفته می‌شود:] «این‌ها گروهی [از پیروان شما] هستند که همراهِ شما وارد [دوزخ] می‌شوند.» [در پاسخ می‌گویند:] خوش آمدی برای آنان نیست، زیرا که به آتش درافتند.»'),
(4030, 38, 60, '[پیروان به رهبران] می‌گویند: «بلکه خوش‌آمد بر شما مباد، که شما این عذاب را برای ما پیش فرستادید. پس چه جایگاه بدی است!»'),
(4031, 38, 61, 'می‌گویند: پروردگارا! هرکس این عذاب را برای ما فراهم ساخته است، بر عذابِ او در آتشِ [دوزخ] دو برابر بیافزا!»'),
(4032, 38, 62, 'و می‌گویند: «چرا مردانی را که ما آنها را از اشرار می‌شمردیم، نمی‌بینیم؟'),
(4033, 38, 63, 'آیا ما [به ناحقّ] آنان را به مسخره می‌گرفتیم؟ [و امروز اهل بهشتند؟ یا آن که در دوزخند،] و چشمان ما به آنها نمی‌افتد؟»'),
(4034, 38, 64, 'همانا مجادله‌ی اهل آتش حتمی و واقع‌شدنی است.'),
(4035, 38, 65, 'بگو: «من فقط بیم‌دهنده‌ام. و هیچ معبودی جز خداوند یکتای قهّار نیست.'),
(4036, 38, 66, 'پروردگار آسمان‌ها و زمین، و آنچه میان آن دو است. [خداوند] نفوذناپذیر و بسیار آمرزنده.»'),
(4037, 38, 67, 'بگو: «این [قرآن] خبری بزرگ است،'),
(4038, 38, 68, 'که شما از آن روی‌گردانید.'),
(4039, 38, 69, 'من دانشی به عالم بالا نداشتم، آن‌گاه که [فرشتگان درباره‌ی آفرینش آدم‌] مجادله می‌کردند.'),
(4040, 38, 70, 'به من چیزی وحی نمی‌شود، مگر از آن رو که من هشداردهنده‌ای آشکارم.»'),
(4041, 38, 71, 'آن‌گاه که پروردگارت به فرشتگان گفت: «من بشری از گِل می‌آفرینم.'),
(4042, 38, 72, 'پس همین که او را استوارش ساختم و از روح خود در او دمیدم، سجده‌کنان برای او به خاک بیافتید.»'),
(4043, 38, 73, 'پس فرشتگان همه با هم سجده کردند.'),
(4044, 38, 74, 'مگر ابلیس که تکبّر ورزید و از کافران بود.'),
(4045, 38, 75, '[خداوند] فرمود: «ای ابلیس! چه چیز مانعِ تو شد که برای موجودی که به دستان [قدرت] خویش آفریدم، سجده کنی؟ تکبّر کردی یا از بلندمرتبه‌ها بودی؟!»'),
(4046, 38, 76, 'ابلیس گفت: من از او بهترم! مرا از آتش آفریده‌ای، و او را از گِل!»'),
(4047, 38, 77, '[خداوند] فرمود: «پس، از این درگاه بیرون شو که تو رانده شده‌ای!'),
(4048, 38, 78, 'و لعنت من تا روز رستاخیز بر تو خواهد بود.»'),
(4049, 38, 79, '[ابلیس] گفت: «پروردگارا! پس مرا تا روزی که [خلایق] برانگیخته می‌شوند، مهلت ده.»'),
(4050, 38, 80, '[خداوند] فرمود: «تو از مهلت داده‌شدگانی!'),
(4051, 38, 81, '[اما] تا روز و زمانی معین.»'),
(4052, 38, 82, '[ابلیس] گفت: «به عزّتت سوگند که همه را گمراه خواهم کرد.'),
(4053, 38, 83, 'مگر بندگان خالص شده‌ات را.»'),
(4054, 38, 84, '[خداوند] فرمود: «به حقّ سوگند، و حقّ می‌گویم،'),
(4055, 38, 85, 'که دوزخ را از تو، و تمام کسانی که از تو پیروی کنند، پر خواهم کرد.»'),
(4056, 38, 86, '[ای پیامبر! به مردم] بگو: «من از شما برای رسالتم پاداشی نمی‌طلبم، و از کسانی نیستم که چیزی را از نزد خود می‌سازند [و ادّعای باطل دارند].'),
(4057, 38, 87, 'این [قرآن] تذکر و پندی برای جهانیان است.'),
(4058, 38, 88, 'و بی‌تردید پس از مدّتی خبر [درستی آیات و وعده‌های‌] آن را خواهید دانست.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'نازل شدن [این‌] کتاب [بر تو]، از سوی خداوند شکست‌ناپذیر و حکیم است.'),
(4060, 39, 2, 'ما این کتاب را به سوی تو به حقّ فروفرستادیم، پس خدا را بپرست در حالی که خود را برای او خالص کرده‌ای.'),
(4061, 39, 3, 'آگاه باشید که دین ناب، تنها از آنِ خداست. و کسانی که به جای خداوند، سرپرستان و معبودهایی را برگزیدند [در توجیه کار خود گفتند]: «ما آنها را فقط برای این می‌پرستیم که ما را هر چه بیش‌تر به خدا نزدیک کنند.» خداوند در آنچه اختلاف می‌ورزند، میانشان داوری خواهد کرد. البتّه خدا کسی را که دروغگویی بسیار ناسپاس است، هدایت نمی‌کند.'),
(4062, 39, 4, 'اگر خداوند اراده کرده بود [برای خود] فرزندی بگیرد، قطعاً از آنچه می‌آفریند، آنچه را می‌خواست، برمی‌گزید. [امّا] او منزّه است [از این که فرزندی داشته باشد]. او خداوند یگانه‌ی پیروز است.'),
(4063, 39, 5, 'او آسمان‌ها و زمین را به حقّ آفرید. شب را بر روز و روز را بر شب درمی‌پیچد. و خورشید و ماه را رام ساخت. هر یک تا مدّتی معین در حرکتند. بدانید که او شکست‌ناپذیر و بسیار آمرزنده است.'),
(4064, 39, 6, 'او شما را از یک تن آفرید، سپس همسرش را از نوع او پدید آورد. و برای شما هشت نوع از دام‌ها [چهار جفت نر و ماده‌ی شتر، گاو، گوسفند و بز] فروفرستاد. او شما را در شکم‌های مادرانتان می‌آفریند. آفرینشی پس از آفرینش دیگر، در تاریکی‌های سه‌گانه. [شکم، رحم و کیسه‌ای که جنین در آن زندگی می‌کند.] این است خدا، که پروردگار شماست. فرمانروایی از آنِ اوست. جز او هیچ معبودی نیست. پس چگونه به بی‌راهه می‌روید؟'),
(4065, 39, 7, 'اگر ناسپاسی کنید، پس [بدانید] خداوند از شما بی‌نیاز است و برای بندگانش ناسپاسی را نمی‌پسندد. و اگر شکرگزار باشید، آن را برای شما می‌پسندد. و هیچ کس بار [گناه] دیگری را به دوش نمی‌کشد. سپس بازگشت شما به سوی پروردگارتان است، پس شما را به آنچه می‌کردید، خبر خواهد داد. او به آنچه در سینه‌هاست، بس آگاه است.'),
(4066, 39, 8, 'و هرگاه به انسان آسیبی برسد، پروردگارش را می‌خواند، در حالی که [با توبه] به سوی او بازگشته است. امّا همین که خداوند از جانب خویش نعمت بزرگی به او بدهد، آن آسیب را که پیش‌تر برای برطرف شدنش دعا می‌کرد، فراموش می‌کند و [دوباره] برای خداوند همتایانی قرار می‌دهد تا [خود و دیگران را] از راه او منحرف سازد. بگو: «اندک زمانی با کفر خود کامیاب باش که تو بی‌شک از دوزخیانی!»'),
(4067, 39, 9, 'آیا [چنین کسی بهتر است یا] آن کس که در ساعات شب، در حال سجده و قیام به عبادتی خالصانه مشغول است، [و] از آخرت می‌ترسد و به رحمت پروردگارش امید دارد؟ بگو: «آیا کسانی که می‌دانند و کسانی که نمی‌دانند، برابرند؟» تنها خردمندان پند می‌پذیرند.'),
(4068, 39, 10, 'بگو: ای بندگان من که ایمان آورده‌اید! از پروردگارتان پروا کنید! برای کسانی که در این دنیا نیکی کرده‌اند، [پاداش] نیکی است. و زمین خدا گسترده است. [پس اگر در منطقه‌ای برای حفظ ایمان و نیکی در فشارید، هجرت کنید.] پاداش صابران، بی‌حساب و به طور کامل داده می‌شود.'),
(4069, 39, 11, 'بگو: «من مأمورم که خدا را بپرستم، در حالی که دین را برای او خالص و پیراسته کرده‌ام.'),
(4070, 39, 12, 'و فرمان یافته‌ام که نخستینِ مسلمانان باشم.»'),
(4071, 39, 13, 'بگو: «همانا اگر پروردگارم را نافرمانی کنم، از عذاب روزی بزرگ می‌ترسم.»'),
(4072, 39, 14, 'بگو: «من تنها خدا را می‌پرستم، در حالی که دینم را برای او خالص کرده‌ام.'),
(4073, 39, 15, 'پس شما هر چه را جز او می‌خواهید، بپرستید.» بگو: «به راستی زیانکاران کسانی هستند که [سرمایه‌ی وجودی] خود و بستگانشان را در قیامت از دست داده باشند. آگاه باشید! این همان زیان آشکار است.»'),
(4074, 39, 16, 'برای زیانکاران از بالای سرشان سایه‌بان‌هایی از آتش، و از زیر پایشان سایه‌بان‌هایی است. این [عذابی] است که خداوند بندگانش را به آن می‌ترساند. پس ای بندگان من! از من پروا کنید.'),
(4075, 39, 17, 'و کسانی که از بندگی طاغوت، دوری جستند و به سوی خدا بازگشتند، بر آنان مژده باد! پس بندگان مرا بشارت بده!'),
(4076, 39, 18, 'آنان که سخن را می‌شنوند و بهترینِ آن را پیروی می‌کنند. آنانند که خداوند هدایتشان کرده، و آنانند همان خردمندان.'),
(4077, 39, 19, 'پس آیا کسی را که حکم عذاب بر او حتمی شده است، [می‌توانی هدایت کنی؟] آیا تو می‌توانی کسی را که در آتش است، رهایی بخشی؟'),
(4078, 39, 20, 'ولی کسانی که از پروردگارشان پروا دارند، غرفه‌هایی برای آنها [در بهشت] است که بالای آنها، غرفه‌های دیگری ساخته شده، و از زیر آنها نهرها جاری است. [این،] وعده‌ی الهی است. و خدا در وعده‌اش خلاف نمی‌کند.'),
(4079, 39, 21, 'آیا ندیده‌ای که خداوند از آسمان آبی فروفرستاد، پس آن را به صورت چشمه‌هایی در زمین روان کرد. سپس با آن، کشت‌هایی رنگارنگ برمی‌آورد. آن‌گاه آن کشت‌ها با هیجان رشد می‌کند، [تا آن که می‌خشکد،] پس آن را زرد می‌بینی. سپس آن را به صورت کاه و خاشاک درمی‌آورد. بی‌گمان در این [دگرگونی‌ها] برای خردمندان پند و یادآوری است [که دنیا ناپایدار است].'),
(4080, 39, 22, 'آیا کسی که خداوند سینه‌اش را برای [پذیرش] اسلام گشاده است، پس او از سوی پروردگارش بهره‌مند از نوری است، [مانند کسی است که در تنگنای تعصّب و لجاجت و غرور است؟ نه!] پس وای بر آنان که از سنگدلی یاد خدا نمی‌کنند. آنان در گمراهی آشکارند.'),
(4081, 39, 23, 'خداوند بهترین سخن را در قالب کتابی نازل کرد، [که آیاتش] مشابه [یکدیگر] و هر یک دیگری را شرح می‌کند. از [تلاوت آیات] آن کسانی که از پروردگارشان می‌ترسند، پوستشان به لرزه می‌افتد. سپس به یاد خدا، پوست‌ها و دل‌های آنان نرم و آرام می‌شود. این است هدایت خداوند. که هر کس را بخواهد، [و شایسته بداند،] به آن هدایت می‌کند. و هر کس را خدا [به خاطر فسق و فسادش] گمراه نماید، پس هیچ هدایت‌گری برای او نیست.'),
(4082, 39, 24, 'پس آیا کسی که [با ایمان و نیکوکاری] روی خود را از عذاب بد روز قیامت دور می‌سازد، [مانند کسی است که از قهر الهی در قیامت غافل است]؟ و [در آن روز] به ستمگران گفته می‌شود: «بچشید آنچه را به دست می‌آوردید!»'),
(4083, 39, 25, 'کسانی که پیش از این کافر بودند [نیز، پیامبران را] تکذیب کردند. پس از آنجا که حدس نمی‌زدند، عذاب به سراغشان آمد.'),
(4084, 39, 26, 'پس خداوند در زندگی دنیا، خواری و رسوایی را به آنان چشاند. و قطعاً عذاب آخرت بزرگ‌تر است، اگر می‌دانستند.'),
(4085, 39, 27, 'و همانا ما در این قرآن از هر نوع مَثَلی زدیم، تا شاید پند گیرند.'),
(4086, 39, 28, 'قرآنی عربی [و فصیح]، بی‌هیچ انحراف و کژی. شاید مردم پرهیزکار شوند.'),
(4087, 39, 29, 'خداوند [برای شرک و توحید] مَثَلی زده است: بنده‌ای که چند مالک ناسازگار در مالکیت او شریک باشند، [و دستورات متضادّ به او بدهند،] و بنده‌ای که تنها مطیع یک نفر است، [و فقط از او دستور می‌گیرد،] آیا این دو در مَثَل یکسانند؟ حمد و ستایش مخصوص خداوند است، ولی بیشتر مردم نمی‌دانند.'),
(4088, 39, 30, 'بی‌شک تو خواهی مرد، و آنان [نیز] مردنی هستند.'),
(4089, 39, 31, 'سپس شما روز قیامت نزد پروردگارتان به کشمکش خواهید پرداخت.'),
(4090, 39, 32, 'پس کیست ستمکارتر از کسی که بر خداوند دروغ بست و چون سخن راست برایش آمد، تکذیب کرد؟ آیا برای کافران در دوزخ جایگاهی نیست؟'),
(4091, 39, 33, 'ولی کسی که [دین] راستین آورد و آن را باور کرد، آنانند تقواپیشگان.'),
(4092, 39, 34, 'هر چه بخواهند، نزد پروردگارشان برای آنان فراهم است. این است پاداش نیکوکاران!'),
(4093, 39, 35, 'تا خداوند [در سایه‌ی ایمان و صداقت آنان] بدترین کاری را که مرتکب شده‌اند، از آنها بپوشاند. و آنان را به بهترین کاری که می‌کرده‌اند، پاداش دهد.'),
(4094, 39, 36, 'آیا خداوند برای بنده‌اش کافی نیست؟ و مردم تو را از غیر خدا می‌ترسانند. و هر کس را خدا گمراه کند، پس هیچ راهنمایی نخواهد داشت.'),
(4095, 39, 37, 'و هر که را خداوند هدایت کند، پس هیچ گمراه‌کننده‌ای نخواهد داشت. آیا خداوند، شکست‌ناپذیر و صاحب انتقام نیست؟'),
(4096, 39, 38, 'و اگر [از مشرکان] بپرسی: «چه کسی آسمان‌ها و زمین را آفرید؟» قطعاً خواهند گفت: «خدا!» بگو: «پس آیا در آنچه جز خدا می‌خوانید، نظر و اندیشه کرده‌اید؟! اگر خداوند برای من زیانی را اراده کند، آیا این بت‌ها می‌توانند آن زیان را برطرف کنند؟ یا [اگر] خداوند برای من لطف و رحمتی را بخواهد، آیا آنها می‌توانند مانع آن رحمت شوند؟» بگو: «خداوند برای من کافی است. اهل توکل تنها بر او توکل می‌کنند.»'),
(4097, 39, 39, 'بگو: «ای قوم من! شما بر اساس موضِع خود [هر چه در توان دارید،] کار کنید. من نیز [به وظیفه‌ی خود] عمل می‌کنم، پس به زودی خواهید دانست،'),
(4098, 39, 40, 'که بر چه کسی عذابی می‌رسد که او را [در دنیا] خوار می‌کند و بر او عذاب پایدار [قیامت] فرود می‌آید؟»'),
(4099, 39, 41, 'ما برای [هدایت] مردم، کتاب را به حقّ بر تو فروفرستادیم. پس هر که هدایت را پذیرفت، به سود خود اوست. و هر که گمراه شد، پس تنها به زیان خود اوست. و تو بر مردم وکیل نیستی [تا به اجبار آنان را هدایت کنی].'),
(4100, 39, 42, 'خداوند است که جان‌ها را به هنگام مرگشان به طور کامل می‌گیرد، و [روح] کسی را که نمرده است، در هنگام خواب [می‌گیرد]، پس [روح] آن را که مرگ بر او قطعی شده، نگاه می‌دارد، [و به بدن بازنمی‌گرداند] و [روح‌های] دیگر را [که مرگشان فرانرسیده،] برای مدّتی معین [به جسم آنان] بازمی‌گرداند. بی‌شک در این [امر]، برای گروهی که می‌اندیشند، نشانه‌هایی [بزرگ از قدرت خدا] وجود دارد.'),
(4101, 39, 43, 'آیا [مشرکان، بت‌ها را] به جای خدا شفیعان [خود] گرفته‌اند؟! بگو: «آیا اگر [اینها] مالک هیچ چیز نباشند و قدرت اندیشه نداشته باشند [باز هم از ایشان شفاعت می‌طلبید]؟»'),
(4102, 39, 44, '[ای پیامبر! به آنان] بگو: تمام شفاعت‌ها از آنِ خداست. برای اوست فرمانروایی آسمان‌ها و زمین. سپس به سوی او بازگردانده می‌شوید.»'),
(4103, 39, 45, 'و هرگاه خداوند به یکتایی یاد شود، دل‌های کسانی که به آخرت ایمان ندارند، پر از نفرت می‌شود؛ ولی هرگاه از [معبودانی] غیر او یاد شود، بی‌درنگ شادمان می‌شوند.'),
(4104, 39, 46, 'بگو: «خداوندا! ای پدیدآورنده‌ی آسمان‌ها و زمین! ای دانای نهان و آشکار! تو خود در میان بندگانت در آنچه همواره اختلاف می‌کردند، داوری خواهی کرد.»'),
(4105, 39, 47, 'و کسانی که ستم کرده‌اند، اگر تمام آنچه در زمین است و مانند آن را، با آن داشته باشند، در روز قیامت برای رهایی از عذاب سخت الهی همه‌ی آن را تاوان خواهند داد. [ولی چه سود؟! در آن روز] برای آن ستمگران از سوی خداوند چیزهایی روشن می‌شود که هرگز در محاسباتشان پیش‌بینی نمی‌کردند.'),
(4106, 39, 48, 'و [در آن روز] برای آنان، بدی آنچه مرتکب شده‌اند، روشن خواهد شد. و آن عذابی که به مسخره می‌گرفتند، آنان را فراخواهد گرفت.'),
(4107, 39, 49, 'پس چون سختی و آسیبی به انسان برسد، ما را می‌خواند، سپس همین که از جانب خود نعمتی به او عطا کنیم، می‌گوید: «[این] نعمت‌ها را فقط بر پایه‌ی دانش و کاردانی خودم به من داده‌اند!» [چنین نیست!] بلکه آن نعمت وسیله‌ی آزمایش است؛ ولی بیشتر مردم نمی‌دانند.'),
(4108, 39, 50, 'به راستی کسانی که پیش از آنان بودند [نیز] این سخن را گفتند؛ ولی آنچه به دست آوردند، سودی برایشان نداشت.'),
(4109, 39, 51, 'پس [نتیجه‌ی] کارهای بدی که مرتکب شده بودند، به آنان رسید. و کسانی از اینان که ستم کردند، به زودی بدی آنچه انجام داده‌اند، به ایشان خواهد رسید. و آنان نمی‌توانند ما را درمانده کنند [و از کیفر ما بگریزند].'),
(4110, 39, 52, 'آیا [هنوز] ندانسته‌اند که خداوند روزی را برای هر که بخواهد، گسترده یا تنگ می‌سازد؟! البتّه در این [امر] برای کسانی که ایمان می‌آورند، نشانه‌هایی [از حکمت و قدرت الهی] است.'),
(4111, 39, 53, 'بگو: ای بندگان من که بر نفس خویش تجاوز [و ستم] کرده‌اید! از رحمت خداوند نومید نشوید. خداوند همه‌ی گناهان را می‌بخشد، زیرا که او بسیار آمرزنده و مهربان است.'),
(4112, 39, 54, 'و پیش از آن که عذاب به سراغ شما بیاید و دیگر یاری نشوید، به سوی پروردگارتان بازگردید [و توبه کنید] و تسلیم او شوید.'),
(4113, 39, 55, 'و از بهترینِ آنچه از پروردگارتان به سوی شما نازل شده است، پیروی کنید، پیش از آن که عذاب، ناگهانی و غافلگیرانه، به سراغ شما آید.'),
(4114, 39, 56, 'تا مبادا کسی [در قیامت] بگوید: «دریغا! بر تمام کوتاهی‌هایی که در برابر خداوند داشتم. و من از مسخره‌کنندگان [آیات او] بودم.»'),
(4115, 39, 57, 'یا آن که [از شدّت اندوه] بگوید: «اگر خداوند هدایتم کرده بود، از پرهیزکاران بودم.»'),
(4116, 39, 58, 'یا هنگامی که عذاب را ببیند، بگوید: «ای کاش برای من بازگشتی بود تا از نیکوکاران می‌شدم.»'),
(4117, 39, 59, 'آری! آیات من به سراغ تو آمد، امّا آنها را تکذیب کردی و تکبّر ورزیدی و از کافران بودی.'),
(4118, 39, 60, 'و روز قیامت کسانی را که بر خدا دروغ بستند، روسیاه می‌بینی. آیا در دوزخ جایگاهی برای متکبّران نیست؟'),
(4119, 39, 61, 'و خداوند کسانی را که تقوا پیشه کرده‌اند، به سبب کارهایی که مایه‌ی رستگاریشان بود، نجات می‌دهد، در حالی که هیچ بدی به آنان نمی‌رسد و اندوهگین هم نمی‌شوند.'),
(4120, 39, 62, 'خداوند، آفریدگار هر چیزی است. و اوست که بر هر چیز نگهبان [و ناظر] است.'),
(4121, 39, 63, 'کلیدهای آسمان‌ها و زمین از آنِ اوست. و کسانی که به آیات الهی کفر ورزیدند، آنان همان زیانکارانند.'),
(4122, 39, 64, 'بگو: «ای نادانان! آیا مرا فرمان می‌دهید که جز خدا بپرستم؟»'),
(4123, 39, 65, 'و قطعاً به تو، و به کسانی [از پیامبران] که پیش از تو بوده‌اند، وحی شده است که: «اگر شرک ورزی، بی‌تردید عمل تو تباه می‌شود و قطعاً از زیانکاران خواهی شد.'),
(4124, 39, 66, 'بلکه تنها خدا را بپرست و از سپاسگزاران باش.»'),
(4125, 39, 67, 'و خدا را آن‌گونه که سزاوار ارجمندی اوست، ارج ننهادند [و او را نشناختند]. با آن که روز قیامت، زمین، یکسره در قبضه‌ی [قدرت] اوست. و آسمان‌ها به دست [قدرت] او در هم پیچیده‌اند. او منزّه و برتر است از آنچه [به او] شرک می‌ورزند.'),
(4126, 39, 68, 'و در صور می‌دمند، پس هر که در آسمان‌ها و هر که در زمین است، بی‌هوش می‌شود، [و می‌میرد،] مگر آن کس را که خدا بخواهد. سپس بار دیگر در آن دمیده می‌شود، پس ناگاه آنان [زنده] برپا می‌خیزند و [به اطراف] می‌نگرند.'),
(4127, 39, 69, 'و [در آن روز] زمین به نور پروردگارش روشن می‌گردد. و نامه‌ی اعمال را در میان می‌نهند و پیامبران و گواهان را می‌آورند و میان آنان به حقّ داوری می‌شود، و بر آنان ستم نخواهد رفت.'),
(4128, 39, 70, 'و به هر کس [جزای] آنچه کرده، به تمامی داده می‌شود. و خداوند به آنچه می‌کنند، آگاه‌تر است.'),
(4129, 39, 71, 'و کسانی که کافر شدند، دسته دسته به سوی دوزخ رانده می‌شوند. چون نزدیک آن برسند، درهای دوزخ گشوده می‌شود و نگهبانان آن به ایشان می‌گویند: «آیا از میان شما پیامبرانی به سراغتان نیامدند، تا آیات پروردگارتان را بر شما بخوانند و شما را به دیدار چنین روزی هشدار دهند؟» می‌گویند: چرا، [آنان آمدند؛ و ما نپذیرفتیم و تکذیبشان کردیم،] ولی [حالا دیگر عذر و توبه‌ی ما سودی ندارد، چون که‌] فرمان عذاب بر کافران تحقّق یافته است.»'),
(4130, 39, 72, '[نگهبانان دوزخ] به آنان می‌گویند: «از درهای دوزخ وارد شوید که جاودانه در آن خواهید بود. پس چه بد است منزلگاه متکبّران!»'),
(4131, 39, 73, 'و کسانی که از پروردگارشان پروا داشتند، گروه گروه به سوی بهشت برده می‌شوند. چون نزدیک آن برسند، در حالی که درهای بهشت گشوده شده است، نگهبانان بهشت به آنان می‌گویند: «سلام بر شما! پاک و پسندیده بودید. پس داخل شوید که در آن جاودانه خواهید بود.»'),
(4132, 39, 74, 'و [بهشتیان] می‌گویند: «سپاس خداوندی را که به وعده‌ی خود وفا کرد، و زمین [بهشت] را میراث ما قرار داد. تا از [این] باغ هر جا را که بخواهیم، برگزینیم. پس چه نیکوست پاداش اهل عمل!»'),
(4133, 39, 75, 'و فرشتگان را می‌بینی که گرد عرش حلقه زده‌اند، پروردگارشان را همراه ستایش، تسبیح می‌گویند. و میان اهل محشر به حقّ داوری می‌شود و [بهشتیان همراه با فرشتگان] گویند: «سپاس و ستایش مخصوص خداوندی است که پروردگار جهانیان است.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حا، میم.'),
(4135, 40, 2, 'نازل شدن کتاب، از سوی خداوند شکست‌ناپذیر و داناست.'),
(4136, 40, 3, 'آمرزنده‌ی گناه، پذیرنده‌ی توبه، سخت‌کیفر و صاحب عطای فراوان. هیچ معبودی جز او نیست. بازگشت [همه] به سوی اوست.'),
(4137, 40, 4, 'جز کسانی که کفر ورزیدند، کسی در آیات خداوند جدال و ستیزه نمی‌کند، پس جنب و جوش آنان در شهرها تو را نفریبد.'),
(4138, 40, 5, 'پیش از این [کفّار مکه]، قوم نوح و گروه‌هایی که پس از آنان آمدند، [پیامبرانشان را] تکذیب کردند. و هر امّتی بر آن شد تا پیامبرش را بگیرد [و بیازارد]. و به [وسیله‌ی] باطل ستیز و مجادله کردند تا حقّ را نابود نمایند. پس آنان را [به قهر خود] گرفتم. پس [بنگر که] کیفر من چگونه بوده است؟'),
(4139, 40, 6, 'و این‌گونه وعده‌ی [عذاب] پروردگارت درباره‌ی کسانی که کفر ورزیدند، قطعی شد، که آنان اهل دوزخند.'),
(4140, 40, 7, 'فرشتگانی که عرش [خدا] را حمل می‌کنند و آنان که اطراف آن هستند، پروردگارشان را با سپاس و ستایش، تسبیح می‌کنند و به او ایمان دارند. و برای مؤمنان، آمرزش می‌خواهند [و می‌گویند:] «پروردگارا! رحمت و علم تو همه چیز را فراگرفته است، پس کسانی را که توبه کردند و راه تو را پیروی نمودند بیامرز و آنان را از عذاب دوزخ نگاه دار!'),
(4141, 40, 8, 'پروردگارا! آنان و شایستگان از پدران و همسران و فرزندانشان را در آن باغ‌های جاوِدانی که به ایشان وعده داده‌ای، وارد کن! بی‌گمان تو همان خدای عزیز و حکیمی!'),
(4142, 40, 9, 'و آنان را از بدی‌ها نگاه دار! و هر کس را در آن روز از بدی‌ها حفظ کنی، پس مشمول رحمتت ساخته‌ای. و این همان رستگاری بزرگ است.»'),
(4143, 40, 10, '[فرشتگان] به کافران می‌گویند: «قطعاً دشمنی و خشم خداوند بزرگ‌تر است از دشمنی شما با خودتان، آن‌گاه که [در دنیا] به ایمان دعوت می‌شدید، ولی کفر می‌ورزیدید.»'),
(4144, 40, 11, '[کافران] می‌گویند: «پروردگارا! ما را دو بار میراندی و دو بار زنده کردی. اینک به گناهان خود اعتراف کردیم؛ پس آیا برای بیرون رفتن [از دوزخ] راهی هست؟»'),
(4145, 40, 12, '[به کافران گفته می‌شود:] این [عذاب] برای آن است که هرگاه خداوند به یگانگی خوانده می‌شد، کفر می‌ورزیدید، ولی هرگاه برای او شریکی آورده می‌شد، باور می‌کردید. پس [امروز] حکم برای خداوند بلندمرتبه‌ی بزرگ است.»'),
(4146, 40, 13, 'اوست که آیات خود را به شما نشان می‌دهد و از آسمان برای شما روزی فرومی‌فرستد، و فقط کسانی پند می‌گیرند که [به سوی خدا] بازمی‌گردند.'),
(4147, 40, 14, 'پس خدا را بخوانید در حالی که دین خود را برای او خالص و پیراسته کرده‌اید، هر چند کافران نپسندند.'),
(4148, 40, 15, '[خداوند] درجات [بندگان شایسته] را بالا می‌برد. صاحب عرش است. روح را [که فرشته‌ی وحی است،] به فرمانش بر هر کس از بندگانش که بخواهد، می‌فرستد تا [مردم را] از روز [رستاخیز و] ملاقات بیم دهد.'),
(4149, 40, 16, 'روزی که مردم ظاهر و آشکارند. و هیچ چیز از آنان بر خداوند پنهان نیست. «امروز فرمانروایی از آنِ کیست؟» از آنِ خداوند یکتای قهّار.»'),
(4150, 40, 17, 'امروز، هر کس برابر آنچه به دست آورده است، پاداش داده می‌شود. امروز هیچ ظلمی نیست. خداوند به حساب [بندگانش] زود می‌رسد.'),
(4151, 40, 18, '[ای پیامبر!] مردم را از روز نزدیک بترسان! آن‌گاه که [از شدّت وحشت] جان‌ها به گلوگاه می‌رسد، در حالی که اندوه خویش را فرومی‌برند. [آن روز] ستمکاران هیچ دوست دلسوز و هیچ شفاعت‌گری که سخنش پذیرفته باشد، ندارند.'),
(4152, 40, 19, 'خداوند از خیانت چشم‌ها، و آنچه سینه‌‌ها پنهان می‌کنند، آگاه است.'),
(4153, 40, 20, 'و خداوند به حقّ داوری می‌کند. و معبودهایی که [مشرکان] به جای او می‌خوانند، [ناتوانند و] هیچ حکمی نمی‌کنند. خداوند است که شنوای بیناست.'),
(4154, 40, 21, 'آیا در زمین نگشتند تا بنگرند سرانجام کسانی که پیش از آنان بودند، چگونه بوده است؟ آنان از اینها نیرومندتر بودند، و در زمین آثاری پایدارتر داشتند، پس خداوند آنان را به خاطر گناهانشان [به قهر خود] گرفت، و در برابر [قهر] خداوند هیچ مدافعی نداشتند.'),
(4155, 40, 22, 'این [قهر الهی] به خاطر آن بود که پیامبرانشان همراه با دلایل روشن به سراغ آنان می‌آمدند، ولی آنان کفر ورزیدند، پس خداوند آنان را [به قهر خود] گرفت. او نیرومند و سخت‌کیفر است.'),
(4156, 40, 23, 'و به راستی ما موسی را همراه با معجزات و منطق روشن فرستادیم،'),
(4157, 40, 24, 'به سوی فرعون و هامان و قارون. پس گفتند: «او جادوگری بسیار دروغگوست!»'),
(4158, 40, 25, 'پس چون موسی از سوی ما حقّ را برای آنان آورد، گفتند: «کسانی که همراه با موسی ایمان آورده‌اند، پسرانشان را بکشید، و زنانشان را [برای خدمتکاری] زنده نگه‌دارید!» [غافل از آن که] نیرنگ کافران جز در گمراهی و تباهی نیست.'),
(4159, 40, 26, 'و فرعون گفت: «بگذارید تا من موسی را بکشم و او پروردگارش را بخواند، [تا نجاتش دهد.] من می‌ترسم که او دین شما را تغییر دهد، یا در این سرزمین فساد کند.»'),
(4160, 40, 27, 'و موسی گفت: «من به پروردگارم و پروردگار شما پناه می‌برم از [شرّ] هر متکبّری که به روز حساب ایمان ندارد.»'),
(4161, 40, 28, 'و مردی باایمان از خاندان فرعون که ایمانش را پنهان می‌داشت، گفت: «آیا می‌خواهید مردی را که می‌گوید: پروردگار من خداوند است، بکشید؟! در حالی که او از سوی پروردگارتان با دلایل روشن نزد شما آمده است. اگر او دروغگو باشد، دروغش به زیان خود اوست؛ ولی اگر راستگو باشد، بعضی از آنچه را که به شما وعده می‌دهد، بر سرتان خواهد آمد. خداوند کسی را که اسراف‌کار [و] بسیار دروغگوست، هدایت نمی‌کند.'),
(4162, 40, 29, 'ای قوم من! امروز حکومت برای شماست که در این سرزمین غلبه دارید؛ ولی اگر عذاب خدا به سراغ ما آید، پس چه کسی ما را یاری خواهد کرد؟» فرعون گفت: «من جز آنچه که خود [صلاح] می‌بینم به شما نشان نمی‌دهم. و من شما را جز به راه راست هدایت نمی‌کنم.»'),
(4163, 40, 30, 'و کسی که ایمان آورده بود، گفت: ای قوم من! من از روزی مانند روز [نابودی] آن گروه‌ها بر شما می‌ترسم.'),
(4164, 40, 31, '[از سرنوشتی] مانند سرنوشت قوم نوح و قوم عاد و قوم ثمود و کسانی که پس از آنها آمدند. و خداوند با بندگان سر ستم ندارد.'),
(4165, 40, 32, 'ای قوم من! من از روزی که یکدیگر را به فریاد بخوانید، بر شما بیمناکم.'),
(4166, 40, 33, 'روزی که [به خاطر سختی عذاب] پشت‌کنان از این سو به آن سو می‌گریزید، برای شما هیچ‌گونه پناهگاه و نگهدارنده‌ای [در برابر عذاب الهی] نیست. و هر که را خداوند [بر اثر اعمالش] گمراه کند، [و به حال خود واگذارد،] هیچ هدایت‌گری برای او نیست.'),
(4167, 40, 34, 'به راستی یوسف پیش از این با دلایل روشن نزد شما [مصریان] آمد، امّا شما همواره از آنچه برایتان آورده بود، در شک بودید. تا آن‌گاه که از دنیا رفت، گفتید: «خداوند پس از او هرگز پیامبری نخواهد فرستاد!» این‌گونه، خداوند هر اسراف‌کار بدگمانی را گمراه می‌سازد،'),
(4168, 40, 35, 'کسانی که درباره‌ی آیات خداوند به ستیز و مجادله می‌پردازند، بی‌آنکه هیچ دلیلی برایشان آمده باشد. [این کار] نزد خداوند و نزد کسانی که ایمان آورده‌اند، خشم بزرگی را به دنبال دارد. این‌گونه، خدا بر دل هر متکبّر زورگویی مهر [شوربختی‌] می‌نهد.»'),
(4169, 40, 36, 'و فرعون [به وزیرش] گفت: ای هامان! برای من برجی بلندی بساز! شاید به وسایلی دست‌یابم.'),
(4170, 40, 37, 'وسایل [بالا رفتن به] آسمان‌ها، تا از خدای موسی آگاه شوم و البتّه من او را دروغگو می‌پندارم.» و این‌گونه [شیطان،] کار زشت فرعون را برایش آراست، و از راه حقّ بازماند. و توطئه فرعون جز در نابودی و زیان نبود.'),
(4171, 40, 38, 'و کسی [از فرعونیان] که ایمان آورده بود، گفت: «ای قوم من! از من پیروی کنید، تا شما را به راه راست [و رستگاری] هدایت کنم.'),
(4172, 40, 39, 'ای قوم من! این زندگی دنیا، کالایی [ناچیز] است. و آخرت است که سرای پایدار است.'),
(4173, 40, 40, 'هر کس کار بدی کند، جز همانند آن کیفر داده نمی‌شود. و کسانی که کار شایسته‌ای کنند و مؤمن باشند، چه مرد، چه زن، آنانند که وارد بهشت می‌شوند و در آنجا بی‌حساب روزیشان می‌دهند.'),
(4174, 40, 41, 'و ای قوم من! چگونه است که من شما را به رهایی [از آتش] می‌خوانم، و شما مرا به دوزخ فرامی‌خوانید؟!'),
(4175, 40, 42, 'مرا دعوت می‌کنید تا به خدای یگانه کافر شوم، و چیزی را که به [حقّانیت] آن علم ندارم، شریک او گردانم. و من شما را به سوی [خداوند] شکست‌ناپذیر و بسیار آمرزنده دعوت می‌کنم.'),
(4176, 40, 43, 'ناگزیر آنچه مرا به سوی آن می‌خوانید، نه در دنیا و نه در آخرت، [در خورِ] خواندن نیست. در حالی که بازگشت ما به سوی خداست، و اسراف‌کاران همدم آتشند.'),
(4177, 40, 44, 'پس به زودی آنچه را برای شما می‌گویم، به یاد خواهید آورد. و من کارم را به خدا وامی‌گذارم که او به [احوال] بندگانش بیناست.»'),
(4178, 40, 45, 'پس خداوند او را از بدی‌های آنچه نیرنگ کردند، نگاه داشت. و عذاب سختی خاندان فرعون را فراگرفت.'),
(4179, 40, 46, '[عذاب فرعونیان] آتش است که هر صبح و شام، بر آن عرضه می‌شوند. و روزی که رستاخیز برپا شود، [گفته می‌شود:] «فرعونیان را در سخت‌ترین عذاب وارد کنید!»'),
(4180, 40, 47, 'و [یاد کن] آن‌گاه که در آتش به بحث [و جدل] می‌پردازند. زیردستان به کسانی که تکبّر ورزیدند، می‌گویند: «ما پیرو شما بودیم. پس آیا [امروز] شما می‌توانید بخشی از آتش را از ما دور کنید؟»'),
(4181, 40, 48, 'کسانی که تکبّر می‌ورزیدند، [در پاسخ] می‌گویند: «[اکنون‌] ما همگی در آتش هستیم، زیرا خداوند میان بندگانش [به عدالت] داوری کرده است.»'),
(4182, 40, 49, 'و کسانی که در دوزخند، به نگهبانان دوزخ می‌گویند: «از پروردگارتان بخواهید که یک روز از عذاب ما بکاهد.»'),
(4183, 40, 50, '[نگهبانان دوزخ در پاسخ] می‌گویند: «آیا پیامبرانتان دلایل روشن برای شما نیاوردند؟!» می‌گویند: «چرا!» می‌گویند: «پس [خدا را] بخوانید، و دعا کنید. ولی دعای کافران در گمراهی است. [و به اجابت نمی‌رسد.]»'),
(4184, 40, 51, 'ما پیامبران خود، و کسانی را که ایمان آورده‌اند، هم در زندگی دنیا، و هم روزی که گواهان برمی‌خیزند، یاری می‌کنیم.'),
(4185, 40, 52, 'روزی که عذرخواهی ستمگران سودشان ندهد. لعن و نفرین برای آنان است، و جایگاهشان، سرای بد.'),
(4186, 40, 53, 'و همانا ما به موسی هدایت دادیم. و به بنی‌اسرائیل کتاب آسمانی [تورات] را به میراث دادیم.'),
(4187, 40, 54, '[که] مایه‌ی هدایت و پند برای خردمندان است.'),
(4188, 40, 55, 'پس [ای پیامبر! بر آزار دشمنان] شکیبا باش که وعده‌های خداوند راستین است. و برای گناهت آمرزش بخواه. و شامگاهان و بامدادان، سپاسگزارانه پروردگارت را تسبیح کن.'),
(4189, 40, 56, 'کسانی که در آیات خداوند بی‌آنکه برهانی برایشان آمده باشد، ستیز و مجادله می‌کنند، در سینه‌هایشان جز خودبزرگ‌بینی نیست که به آن نخواهند رسید، [و از مجادله خود نتیجه نخواهند گرفت.] پس به خدا پناه ببر که بی‌شک او شنوای بیناست.'),
(4190, 40, 57, 'قطعاً آفرینش آسمان‌ها و زمین از آفرینش مردم بزرگ‌تر [و مهمّ‌تر] است؛ ولی بیشتر مردم نمی‌دانند.'),
(4191, 40, 58, 'نابینا و بینا یکسان نیستند. و کسانی هم که ایمان آوردند و کارهای شایسته کرده‌اند، با [مردم] بدکار برابر نیستند. چه کم پند می‌پذیرید.'),
(4192, 40, 59, 'حتماً قیامت آمدنی است. شکی در آن نیست، ولی بیشتر مردم ایمان نمی‌آورند.'),
(4193, 40, 60, 'و پروردگارتان فرمود: «مرا بخوانید تا برای شما اجابت کنم. کسانی که از بندگی من سر باز می‌زنند و تکبّر می‌ورزند، به زودی با سرافکندگی وارد دوزخ می‌شوند.»'),
(4194, 40, 61, 'خداوند کسی است که شب را برای شما قرار داد تا در آن آرام گیرید، و روز را [برای کار و تلاش] روشنی‌بخش ساخت. خداوند نسبت به مردم لطفِ بسیار دارد؛ ولی بیشتر مردم شکرگزاری نمی‌کنند.'),
(4195, 40, 62, 'این است خداوندی که پروردگار شما و آفریدگار هر چیز است. معبودی جز او نیست. پس چگونه [از راه حقّ] بازگردانده می‌شوید؟'),
(4196, 40, 63, 'این گونه، کسانی که آیات خدا را انکار می‌کردند، به بیراهه کشیده می‌شوند.'),
(4197, 40, 64, 'خداوند کسی است که زمین را آسایشگاه و آسمان را سر پناه شما قرار داد. و شما را صورت‌گری کرد، پس چهره‌هایتان را نیکو نگاشت. و از پاکیزه‌ها به شما روزی داد. این است خدایی که پروردگار شماست، پس پربرکت و بلندمرتبه است خدایی که پروردگار جهانیان است.'),
(4198, 40, 65, 'اوست زنده‌ای که جز او معبودی نیست. پس او را در حالی بخوانید که دین را برایش خالص کرده‌اید. سپاس و ستایش مخصوص خداوندی است که پروردگار جهانیان است.'),
(4199, 40, 66, 'بگو: «من از آن هنگام که دلایل روشنی از جانب پروردگارم به من رسیده، نهی شده‌ام از این که معبودانی را بپرستم که شما به جای خداوند می‌خوانید. و فرمان یافته‌ام که فقط تسلیم پروردگار جهانیان باشم.»'),
(4200, 40, 67, 'او کسی است که شما را از خاک آفرید، سپس از نطفه، سپس از عَلَقه، سپس شما را به صورت نوزادی بیرون می‌آورد، آن‌گاه [رشدتان می‌دهد،] تا به کمال قوّت خود برسید، سپس پیر شوید. و بعضی از شما پیش از رسیدن به پیری می‌میرید؛ [ولی برخی زنده می‌مانید] تا به سرآمدی که معین است، برسید. و باشد که بیندیشید.'),
(4201, 40, 68, 'او کسی است که زنده می‌کند و می‌میراند. پس چون به [وجود] چیزی حکم کند، فقط به آن می‌گوید: «باش!» پس بی‌درنگ موجود می‌شود.'),
(4202, 40, 69, 'آیا کسانی را که در آیات خداوند، مجادله و ستیز می‌کنند ندیدی که چگونه [از راه حقّ] منصرف می‌شوند؟'),
(4203, 40, 70, 'همان کسانی که این کتاب [آسمانی] و آنچه را که پیامبرانمان را به آن فرستاده‌ایم، دروغ شمردند. پس به زودی [نتیجه‌ی کار خود را] خواهند دانست.'),
(4204, 40, 71, 'آن‌گاه که غل‌ها در گردن‌هایشان باشد و با زنجیرها کشیده می‌شوند.'),
(4205, 40, 72, 'در آب جوشان، و سپس در آتش سوزانده می‌شوند.'),
(4206, 40, 73, 'سپس به آنان گفته می‌شود: «کجایند آن شریک‌هایی که،'),
(4207, 40, 74, 'به جای خداوند می‌پرستیدید؟!» [دوزخیان] می‌گویند: «آنها را گم کرده‌ایم، بلکه [انگار] ما در دنیا چیزی را نمی‌پرستیدیم!» این‌گونه خداوند کافران را گمراه می‌کند.'),
(4208, 40, 75, '[به آنان گفته می‌شود:] «این [عذاب] به خاطر آن است که در زمین به ناحقّ شادی می‌کردید و در ناز و سرمستی به سر می‌بردید.'),
(4209, 40, 76, '[اینک] از درهای دوزخ وارد شوید که جاودانه در آنید. و چه بد است جایگاه متکبّران!»'),
(4210, 40, 77, 'پس [ای پیامبر!] صبر کن که وعده‌ی خداوند حقّ است. پس هرگاه بخشی از آنچه را به آنان وعده داده‌ایم، به تو نشان دهیم، یا [پیش از آن] تو را از دنیا ببریم، [در هر صورت،] به سوی ما بازمی‌گردند.'),
(4211, 40, 78, 'و ما پیش از تو پیامبرانی فرستادیم. داستان برخی از آنان را برای تو بازگو کردیم. و [داستان] بعضی دیگر را برای تو بیان نکردیم. و هیچ پیامبری نمی‌تواند معجزه‌ای جز به فرمان خدا بیاورد. پس چون فرمان خدا آمد، به حقّ داوری خواهد شد، و باطل‌گرایان آنجا زیانکارند.'),
(4212, 40, 79, 'خداوند است که برای شما چهارپایان را قرار داد تا از برخی از آنها سواری بگیرید و از برخی از آنها بخورید.'),
(4213, 40, 80, 'و برای شما در دام‌ها منافع [دیگری نیز] هست. و تا سوار بر آنها، به نیازی که در دل دارید، برسید. و بر آنها و بر کشتی‌ها برده می‌شوید.'),
(4214, 40, 81, 'و خداوند [پیوسته] نشانه‌های خود را به شما نشان می‌دهد، پس کدام یک از نشانه‌های الهی را انکار می‌کنید؟'),
(4215, 40, 82, 'پس آیا در زمین نگشتند تا ببینند سرانجام کسانی که پیش از آنان بوده‌اند، چه شد؟ تعدادشان از اینها بیشتر بود، نیرومندتر بودند و در زمین، آثاری پایدارتر داشتند؛ امّا آنچه به دست آورده بودند، به کارشان نیامد.'),
(4216, 40, 83, 'پس چون پیامبرانشان دلایل روشن برای آنان آوردند، به اندک دانشی که نزد خود داشتند، شادمان شدند، [و منطق پیامبران را به حساب نیاوردند.] و آنچه [از قهر الهی] که همواره به تمسخر می‌گرفتند، آنان را فراگرفت.'),
(4217, 40, 84, 'پس چون عذاب ما را دیدند، گفتند: «به خداوند یکتا ایمان آوردیم، و به معبودانی که [پیش‌تر] با او شریک قرار می‌دادیم، کافر شدیم.»'),
(4218, 40, 85, 'امّا پس از آن که قهر ما را دیدند، دیگر ایمانشان برای آنها سودی نداشت. این شیوه و سنّت الهی است که در میان بندگانش جاری است.، [که ایمانشان را پس از دیدن عذاب نمی‌پذیرد.] و آنجا کافران زیانکارند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حا، میم.'),
(4220, 41, 2, '[این قرآن] از سوی خداوند بخشنده‌ی مهربان نازل شده است.'),
(4221, 41, 3, 'کتابی که آیات آن به روشنی بیان شده است، قرآنی عربی، برای مردمی که می‌دانند.'),
(4222, 41, 4, '[کتابی] نویدبخش و هشداردهنده؛ پس [با این حال] بسیاری از مردم روی‌گردان شدند و [ندای قرآن را] نمی‌شنوند.'),
(4223, 41, 5, 'و گفتند: «دل‌های ما از آنچه ما را به سوی آن دعوت می‌کنی، در پوششی سخت است. و در گوش‌های ما سنگینی است، و میان ما و تو پرده‌ای است. پس تو [کار خود را] انجام بده، ما نیز [کار خود را] می‌کنیم.»'),
(4224, 41, 6, '[ای پیامبر! به مردم] بگو: «من نیز بشری هستم مانند شما [و از جنس خودتان. با این تفاوت که] به من وحی می‌شود که خدای شما فقط خدایی یکتاست. پس بدون انحراف به سوی او رو کنید و از او آمرزش بخواهید. و وای بر مشرکان!'),
(4225, 41, 7, 'کسانی که زکات نمی‌پردازند. و آنان به آخرت کافرند.»'),
(4226, 41, 8, 'کسانی که ایمان آوردند و کارهای شایسته می‌کنند، پاداشی همیشگی [و بی‌منّت] دارند.'),
(4227, 41, 9, 'بگو: «آیا شما به کسی که زمین را در دو روز آفریده است، کفر می‌ورزید و برای او همتایانی قرار می‌دهید؟ اوست پروردگار جهانیان.»'),
(4228, 41, 10, 'و در روی زمین کوه‌هایی استوار قرار داد و در آن [کوه‌ها] خیر فراوان نهاد. و در چهار روز، رزق و روزی اهلِ زمین را مقدّر کرد که برای تمام نیازمندان، کافی است.'),
(4229, 41, 11, 'سپس به [آفرینش] آسمان پرداخت، در حالی که [به صورت] دود بود. پس به آن و زمین فرمود: «خواه یا ناخواه بیایید [و شکل بگیرید].» گفتند: «فرمانبردار آمدیم [و شکل گرفتیم].»'),
(4230, 41, 12, 'پس آنها را در دو روز، در قالب هفت آسمان درآورد. و در هر آسمانی کار آن را وحی [و مقرّر] فرمود. ما آسمان دنیا را به ستارگان آراستیم و آن را وسیله‌ی حفظ [آسمان‌ها] قرار دادیم. این است تقدیر خدای توانای دانا.'),
(4231, 41, 13, 'پس اگر [از حقّ] روی‌گرداندند، بگو: «من شما را از صاعقه‌ای همچون صاعقه‌ی قوم عاد و ثَمود می‌ترسانم.»'),
(4232, 41, 14, 'آن‌گاه که فرستادگان [ما] از هر سو به سراغشان آمدند [و گفتند:] «جز خداوند را نپرستید!» [امّا آنان در پاسخ] گفتند: «اگر پروردگار ما می‌خواست [پیامبری بفرستد،] قطعاً فرشتگانی را می‌فرستاد. پس ما به آنچه شما را به آن فرستاده‌اند، کافریم.»'),
(4233, 41, 15, 'پس قوم عاد به ناحقّ در زمین تکبّر ورزیدند و گفتند: «کیست که از ما نیرومندتر باشد؟» آیا ندیدند که همان خداوندی که آنان را آفرید، نیرومندتر از آنهاست؟ و پیوسته به آیات ما کفر می‌ورزیدند.'),
(4234, 41, 16, 'پس ما در روزهایی شوم، بادی سرد و توفنده و سمّی را بر آنان فرستادیم تا عذاب خواری و رسوایی را در همین دنیا به آنان بچشانیم. و قطعاً عذاب آخرت رسواکننده‌تر است. و آنان یاری نخواهند شد.'),
(4235, 41, 17, 'و امّا قوم ثَمود، آنان را هدایت کردیم؛ ولی کوردلی را بر هدایت ترجیح دادند. پس به کیفر دستاوردشان، صاعقه‌ی عذابِ خفّت‌بار آنان را فروگرفت.'),
(4236, 41, 18, 'و ما [از عاد و ثمود،] آنان را که ایمان آوردند و پرهیزکار بودند، نجات دادیم.'),
(4237, 41, 19, 'و [یاد کن] روزی که دشمنان خدا جمع گشته، به سوی دوزخ آورده می‌شوند و آنان را از حرکت بازمی‌دارند [تا گروه‌های دیگر به آنان ملحق شوند].'),
(4238, 41, 20, 'تا چون به دوزخ برسند، گوش و چشمان و پوستشان به آنچه می‌کردند، گواهی می‌دهند.'),
(4239, 41, 21, 'و [دوزخیان] به پوست [بدن] خود می‌گویند: «چرا علیه ما گواهی دادید؟» می‌گویند: «خداوند ما را به سخن آورد. همان که هر چیز را به سخن می‌آورد. او که نخستین بار شما را آفرید، و فقط به سوی او برگردانده می‌شوید.»'),
(4240, 41, 22, 'شما گمان می‌کردید که خداوند بسیاری از کردارتان را نمی‌داند، ولی نمی‌توانستید چیزی را پنهان کنید که گوش و چشم‌ها و پوستتان بر ضدّ شما گواهی می‌دهند.'),
(4241, 41, 23, 'و این گمان شما نسبت به پروردگارتان بود که شما را به هلاکت افکند و از زیانکاران شدید.'),
(4242, 41, 24, 'پس اگر صبر کنند، [چه سود که] دوزخ جایگاه آنهاست. و اگر تقاضای عفو کنند، از بخشودگان نیستند.'),
(4243, 41, 25, 'و [به خاطر کفر و طغیانشان] برای آنان همنشینانی گماشتیم، که لذّت‌های مادّی حال و آینده را برایشان آراستند. و فرمان عذابی که پیش از آنان گروه‌هایی از جنّ و انس را فراگرفته بود، بر آنان نیز ثابت شد. آنان بی‌تردید زیانکارند.'),
(4244, 41, 26, 'و کسانی که کافر شدند، گفتند: «به این قرآن گوش ندهید، و در [هنگام تلاوت] آن، [سخنان] لغو و بیهوده بیافکنید، [تا کسی آن را نشنود!] شاید پیروز شوید.»'),
(4245, 41, 27, 'پس قطعاً به آنان که کفر ورزیدند، عذاب سختی می‌چشانیم. و حتماً آنان را به بدترین کاری که می‌کردند، کیفر خواهیم داد.'),
(4246, 41, 28, 'سزای دشمنان خدا همین است: آتش [دوزخ]، که در آن، سرایی جاودانه خواهند داشت. به کیفر آن که آیات ما را پیوسته انکار می‌کردند.'),
(4247, 41, 29, 'و کافران گفتند: «پروردگارا! آن جنّیان و انسان‌هایی که ما را گمراه کردند، به ما نشان بده تا آنها را زیر پا‌هایمان بگذاریم تا از پست‌ترین‌ها باشند.»'),
(4248, 41, 30, 'کسانی که گفتند: «پروردگار ما خدای یگانه است.» سپس [بر این عقیده] پایداری کردند، فرشتگان بر آنان فرود می‌آیند [و می‌گویند:] «نترسید و غمگین مباشید! و بشارت باد بر شما بهشتی که پی‌درپی به شما وعده می‌دادند.»'),
(4249, 41, 31, 'ما [فرشتگان] در دنیا و آخرت دوستان شماییم. و برای شما [در بهشت] هر چه دلتان بخواهد، فراهم است. و در آنجا هر چه درخواست کنید، دارید.'),
(4250, 41, 32, '[‌این‌ها همه، نخستین] پذیرایی از جانب خداوند آمرزنده‌ی مهربان است.»'),
(4251, 41, 33, 'و کیست نکوگفتارتر از کسی که [مردم را] به سوی خداوند دعوت کند؟ و [خود نیز] کار شایسته کند و بگوید: «من از تسلیم‌شدگان [در برابر فرمان‌های خدا] هستم!»'),
(4252, 41, 34, 'و نیکی با بدی یکسان نیست. [بدی دیگران را] با شیوه‌ای بهتر [که نیکی است،] دور کن که [اگر چنین کنی،] آن کس که میان تو و او دشمنی است، همچون دوستی نزدیک و مهربان گردد.'),
(4253, 41, 35, 'و این [خصلت و برخورد نیکو] را جز کسانی که اهل صبر و شکیبایی هستند، دریافت نمی‌کنند. و این [رفتار] را تنها کسی خواهد یافت که بهره‌ی بزرگی [از کمالات] داشته باشد.'),
(4254, 41, 36, 'و اگر انگیزه و وسوسه‌ای از سوی شیطان تو را تحریک کند، پس به خداوند پناه ببر که او خود شنوای آگاه است.'),
(4255, 41, 37, 'و از نشانه‌های [قدرت] او، شب و روز، و خورشید و ماه است. نه برای خورشید سجده کنید و نه برای ماه؛ [بلکه] برای خداوندی که آنها را آفریده، سجده کنید، اگر تنها او را می‌پرستید.'),
(4256, 41, 38, 'و اگر [گروهی از مردم از عبادت خداوند] تکبّر ورزیدند، پس کسانی [از فرشتگان و مقرّبان] که نزد پروردگارت هستند، شب و روز برای او تسبیح می‌گویند، و خسته نمی‌شوند.'),
(4257, 41, 39, 'و از نشانه‌های خداوند آن است که زمین را پژمرده [و بی‌جان] می‌بینی، پس همین که [از آسمان] آب را بر آن فروفرستادیم، به جنبش در می‌آید و می‌بالد. البتّه همان کسی که [زمین مرده را] زنده کرد، قطعاً زنده‌کننده‌ی مردگان است. بی‌شک او بر هر کاری تواناست.'),
(4258, 41, 40, 'بی‌گمان کسانی که در آیات ما کج‌اندیشی می‌کنند، [و به تفسیری نادرست متوسّل می‌شوند،] بر ما پوشیده نیستند. پس آیا کسی که در آتش افکنده می‌شود، بهتر است، یا کسی که در روز قیامت در امن و امان می‌آید؟ هر کاری می‌خواهید بکنید، او به آنچه می‌کنید، بیناست!'),
(4259, 41, 41, 'کسانی که چون قرآن به سویشان آمد، به آن کافر شدند، [به کیفر خود می‌رسند.] و این [قرآن] کتابی نفوذناپذیر است.'),
(4260, 41, 42, 'در حال و آینده‌ی آن، باطل راه ندارد. فروفرستاده‌ای از سوی [خداوند] فرزانه‌ی ستوده است.'),
(4261, 41, 43, '[ای پیامبر!] به تو همان را می‌گویند که به پیامبران پیش از تو گفته شده است. پروردگار تو صاحب آمرزش، و کیفری دردناک است.'),
(4262, 41, 44, 'و اگر ما قرآن را غیر عربی قرار داده بودیم، حتماً می‌گفتند: «چرا آیاتش روشن نیست؟ [کتابی] غیر عربی و [مردمی] عرب زبان؟!» بگو: «این قرآن برای کسانی که ایمان آوردند، سراسر هدایت و درمان است. و کسانی که ایمان نمی‌آورند، در گوش‌هایشان سنگینی است. [گویا] کورند، و [قرآن با همه‌ی روشنی‌اش‌] بر آنان پوشیده است. اینانند که [گویی] از جایی دور فراخوانده می‌شوند.»'),
(4263, 41, 45, 'و البتّه ما به موسی کتاب دادیم، پس در آن اختلاف افتاد. [که از پیشگاه حقّ آمده یا ساختگی است.] و اگر نبود شیوه و سنّت سابقِ پروردگارت، [که به مردم مهلت می‌دهد،] قطعاً میانشان داوری شده بود و البتّه اینان نیز [چون قوم موسی] درباره‌ی قرآن در شکی همراه با بدگمانی‌اند.'),
(4264, 41, 46, 'هر کس کار شایسته‌ای کند، پس به سود خود اوست. و هر کس بدی کند، پس به زیان خویش کرده است، و پروردگارت به مردم ستم نمی‌کند.'),
(4265, 41, 47, 'آگاهی به زمان وقوع قیامت، تنها به خداوند بازمی‌گردد. و هیچ میوه‌ای از شکوفه‌های خود خارج نمی‌شود، و هیچ ماده‌ای باردار نمی‌شود و نمی‌زاید، مگر به علم او. و روزی که [خداوند] مشرکان را ندا کند: «شریکان من کجا هستند؟» می‌گویند: «با بانگ رسا به تو می‌گوییم: هیچ گواهی [بر گفتار و عقیده‌ی خود] نداریم.»'),
(4266, 41, 48, 'و هر چه را که از پیش همواره می‌خواندند، از نزد آنان گم می‌شود. و می‌فهمند که هیچ راه فراری ندارند.'),
(4267, 41, 49, 'انسان از درخواست خوشی و رفاه خسته نمی‌شود. و هرگاه شرّی به او برسد، بسیار نومید و بی‌انگیزه می‌شود.'),
(4268, 41, 50, 'و اگر پس از رنجی که به وی رسیده است، رحمتی از خود به او بچشانیم، حتماً می‌گوید: «این [خوشی]، حقّ من است.» [و چنان مغرور می‌شود که می‌گوید:] «گمان نمی‌کنم قیامتی برپا شود. و اگر هم به سوی پروردگارم بازگردانده شوم، نزد او بهترین [پاداش] را خواهم داشت.» پس ما کافران را از آنچه کرده‌اند، آگاه خواهیم ساخت، و قطعاً از عذابی سخت به آنان خواهیم چشاند.'),
(4269, 41, 51, 'و هرگاه به انسان نعمتی بدهیم، [از طاعت و عبادت،] روی می‌گرداند و خود را کنار می‌کشد. و هرگاه آسیبی به او برسد، [برای برطرف شدنش،] به دعای فراوان و طولانی روی می‌آورد.'),
(4270, 41, 52, 'بگو: «به من خبر دهید که اگر [این قرآن] از سوی خداوند باشد، سپس شما به آن کافر شوید، چه کسی گمراه‌تر از [چنین] کسی است که در مخالفتی شدید باشد؟»'),
(4271, 41, 53, 'ما به زودی نشانه‌های خود را در کرانه‌های جهان، و در درون جانشان، به آنان نشان خواهیم داد، تا برایشان روشن شود که قطعاً او حقّ است. آیا کافی نیست که پروردگارت بر همه چیز گواه است؟'),
(4272, 41, 54, 'آگاه باش که آنان درباره‌ی ملاقات پروردگارشان [در قیامت،] در شکی عمیقند. آگاه باش که او به هر چیزی احاطه دارد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حا، میم.'),
(4274, 42, 2, 'عین، سین، قاف.'),
(4275, 42, 3, 'این‌گونه خداوند عزیز حکیم، به تو و به کسانی که پیش از تو بوده‌اند، وحی می‌کند.'),
(4276, 42, 4, 'آنچه در آسمان‌ها و آنچه در زمین است، از آنِ اوست و او بلندمرتبه و بزرگ است.'),
(4277, 42, 5, 'نزدیک است که آسمان‌ها [از عظمت وحی] از فرازشان بشکافند، و فرشتگان پروردگارشان را همراه با سپاس، تسبیح می‌کنند، و برای کسانی که در زمین هستند، آمرزش می‌طلبند. آگاه باشید که فقط خداوند، بسیار بخشنده و مهربان است.'),
(4278, 42, 6, 'و کسانی که به جای او سرورانی را [به پرستش] گرفته‌اند، خداوند مراقب [اعمال و گفتار] آنان است. و تو بر آنان وکیل نیستی [تا به ایمان وادارشان کنی].'),
(4279, 42, 7, 'و این‌گونه قرآنی [به زبان] عربی [فصیح و گویا] به تو وحی کردیم تا مردم مکه و کسانی را که پیرامون آن هستند، هشدار دهی. و آنان را از روز جمع شدن [در قیامت] که شکی در آن نیست، بترسانی. [آن روز] گروهی در بهشت و گروهی در آتش سوزانند.'),
(4280, 42, 8, 'و اگر خدا می‌خواست مردم را امّتی یگانه قرار می‌داد، و [به اجبار هدایت می‌کرد؛ ولی سنّت خدا بر دادن اختیار به مردم است.] هر کس را که بخواهد، [و شایسته بداند،] در رحمت خود وارد می‌کند. و برای ستمگران هیچ سرپرست و یاوری نیست.'),
(4281, 42, 9, 'آیا به جای خداوند، سرپرستان دیگری گرفته‌اند، با آن که تنها خدا ولیّ و سرپرست [واقعی] است. و اوست که مردگان را زنده می‌کند، و اوست که بر هر کاری تواناست.'),
(4282, 42, 10, 'و هر چه را در آن اختلاف دارید، پس حکمش با خداست. [ای پیامبر! بگو:] «این است خدایی که پروردگار من است. تنها بر او توکل کرده‌ام. و به سوی او بازمی‌گردم.»'),
(4283, 42, 11, 'پدیدآورنده‌ی آسمان‌ها و زمین. برای شما از [جنس] خودتان همسرانی قرار داد، و از دام‌ها نیز جفت‌هایی. شما را با این [تدبیر حکیمانه‌] زیاد می‌کند. او را هیچ مانندی نیست. و او شنوای بیناست.'),
(4284, 42, 12, 'کلیدهای آسمان‌ها و زمین برای اوست، برای هر که بخواهد روزی را توسعه می‌دهد، و یا تنگ می‌گیرد. البتّه او به هر چیز آگاه است [و کارهایش بر اساس علم و حکمت است].'),
(4285, 42, 13, 'از [احکام] دین آنچه را به نوح سفارش کرد، برای شما [نیز] مقرّر داشت. و آنچه به سوی تو وحی کردیم، و آنچه ابراهیم و موسی و عیسی را بدان سفارش نمودیم، [آن بود] که دین را به پا دارید، و در آن دچار تفرقه نشوید. آنچه مردم را بدان دعوت می‌کنی، بر مشرکان سنگین است. خداوند هر که را بخواهد، به سوی خویش جلب می‌کند. و هر که را به درگاهش روی آورد، به سوی خویش هدایت می‌کند.'),
(4286, 42, 14, 'و پس از آن که [به حقّانیت دین] کاملاً آگاهی یافتند، به خاطر روحیه‌ی کینه و حسادت که میانشان بود، پراکنده و فرقه فرقه شدند. و اگر سنّتِ سابق خداوند [در مورد مهلت دادن به کافران] تا زمانی معین نبود، میان آنان حکم می‌شد [و اهل باطل نابود می‌شدند]. و یقیناً کسانی که پس از آنان، کتاب آسمانی [قرآن‌] را به میراث بردند، درباره‌ی آن دچار تردیدی سخت هستند.'),
(4287, 42, 15, 'پس [ای پیامبر!] به همین خاطر [آنان را به راه حقّ] دعوت کن! و چنان که فرمان یافته‌ای، پایدار باش! و خواسته‌های آنان را پیروی مکن و [به آنان] بگو: «من به هر کتابی که خدا نازل کرده است، ایمان دارم. و فرمان یافته‌ام تا میان شما دادگری کنم. خداوند، پروردگار ما و شماست. اعمال ما برای ما، و اعمال شما برای شماست. [و هیچ کدام مسئول کارهای دیگری نیستیم.] هیچ حجّتی میان ما و شما [ناگفته] نمانده است. خداوند ما و شما را [در قیامت] جمع خواهد کرد. و بازگشت [همه] به سوی اوست.»'),
(4288, 42, 16, 'و کسانی که درباره‌ی خداوند پس از آن که [مؤمنان] دعوت او را پذیرفته‌اند، بحث و جدل می‌کنند، دلیلشان نزد پروردگارشان باطل و بی‌پایه است. و بر آنهاست خشم الهی و برایشان عذاب سختی [آماده] است.'),
(4289, 42, 17, 'خداست آن که کتاب آسمانی و میزان [سنجش حقّ و باطل‌] را به حقّ فروفرستاد. و چه می‌دانی؟ شاید قیامت نزدیک باشد!'),
(4290, 42, 18, 'کسانی که قیامت را باور ندارند، [برای برپاییِ آن] شتاب می‌کنند، ولی کسانی که ایمان دارند، از آن می‌ترسند و می‌دانند که قیامت حقّ است. بدانید قطعاً کسانی که درباره‌ی قیامت به جدال می‌پردازند، در گمراهی دوری هستند.'),
(4291, 42, 19, 'خداوند نسبت به بندگانش با مهر و لطف رفتار می‌کند. هر که را بخواهد [و صلاح بداند،] روزی می‌دهد. و اوست نیرومند نفوذناپذیر.'),
(4292, 42, 20, 'آن که کشت آخرت را بخواهد، برای او در کشته‌اش می‌افزاییم. و آن که فقط کشت دنیا را بخواهد، اندکی از آن به او می‌دهیم، ولی در آخرت هیچ نصیبی ندارد.'),
(4293, 42, 21, 'آیا [این مشرکان] معبودانی دارند که بدون اذن خداوند، برای آنها احکامی از دین قرار داده‌اند؟ و اگر فرمان قطعی [خداوند بر مهلت دادن به منحرفان] نبود، قطعاً میان مشرکان [به هلاکت] حکم می‌شد. و برای ستمکاران عذابی دردناک است.'),
(4294, 42, 22, 'ستمکاران را خواهی دید که از دستاوردشان هراسانند، در حالی که [کیفر] آن به آنان خواهد رسید. و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در باغ‌های بهشتند. برای آنان نزد پروردگارشان، هر چه بخواهند، [فراهم است.] این همان فضل بزرگ است.'),
(4295, 42, 23, 'این [فضل بزرگ] چیزی است که خداوند به بندگانش مژده می‌دهد. آنان که ایمان آورده و کارهای شایسته کرده‌اند. [ای پیامبر! به مردم] بگو: «من از شما برای این [رسالت] هیچ مزدی نمی‌خواهم، جز آن که دوستدار خاندانم باشید.» و هر کس کار نیکی کند، برای او در نیکویی آن می‌افزاییم. خداوند بسیار آمرزنده و قدردان است.'),
(4296, 42, 24, 'آیا [منافقان] می‌گویند: «[محمّد] بر خدا دروغ بسته است! [و از پیش خود گفته است که خویشاوندانش را دوست بداریم!]» [چنین نیست! آنچه می‌گوید سخن وحی است.] پس اگر خدا بخواهد، بر دل تو مهر می‌نهد. [و اگر خلاف بگویی، قدرت بیان آیات را از تو می‌گیرد.] خداوند باطل را محو می‌کند و حقّ را با کلمات خویش استوار می‌سازد. همانا او به راز سینه‌ها آگاه است.'),
(4297, 42, 25, 'و اوست که توبه را از بندگانش می‌پذیرد، و از گناهان درمی‌گذرد، و آنچه می‌کنید، می‌داند.'),
(4298, 42, 26, 'و [دعای] کسانی را که ایمان آورده و کارهای نیکو کرده‌اند، اجابت می‌کند و از فضل خویش بر آنان می‌افزاید. و برای کافران عذابی شدید است.'),
(4299, 42, 27, 'و اگر خداوند برای بندگانش رزق و روزی را توسعه می‌داد، در زمین طغیان می‌کردند. امّا آنچه را می‌خواهد، به اندازه‌ای [معین] نازل می‌کند، که او به بندگانش آگاه و بیناست.'),
(4300, 42, 28, 'و اوست که باران را پس از این که [مردم از آمدنش‌] نومید شدند، فرومی‌فرستد، و رحمت خویش را می‌گسترد. و اوست سرور ستوده!'),
(4301, 42, 29, 'و از نشانه‌های [قدرت] او آفرینش آسمان‌ها و زمین است، و جنبندگانی که در آن دو پراکنده‌اند. و او هرگاه بخواهد، بر گردآوری آنها تواناست.'),
(4302, 42, 30, 'و هر مصیبتی به شما برسد، پس به خاطر دستاورد خود شماست. و او از بسیاری [گناهانتان] درمی‌گذرد.'),
(4303, 42, 31, 'و شما نمی‌توانید [خدا] را در زمین درمانده کنید، [و از سلطه‌اش بیرون روید.] و شما جز خداوند هیچ سرپرست و یاوری ندارید.'),
(4304, 42, 32, 'و از نشانه‌های [قدرت] او [حرکت] کشتی‌ها در دریاست [که] همچون کوه‌ها [برجسته‌اند].'),
(4305, 42, 33, 'اگر بخواهد باد را ساکن می‌کند، پس کشتی‌ها بر پشت آب راکد می‌مانند. در این [امر] برای هر شکیبای سپاسگزار، نشانه‌هایی [از قدرت خدا] است.'),
(4306, 42, 34, 'یا [کشتی‌ها را] به خاطر آنچه [صاحبانشان] انجام داده‌اند، نابود می‌کند. و از بسیاری درمی‌گذرد [و غرقشان نمی‌کند].'),
(4307, 42, 35, 'و کسانی که در آیات ما مجادله می‌کنند، بدانند که راه گریزی ندارند.'),
(4308, 42, 36, 'آنچه به شما داده شده، بهره‌ی زندگی دنیاست. و آنچه نزد خداست، برای کسانی که ایمان آوردند و به پروردگارشان توکل می‌کنند، بهتر و ماندگارتر است.'),
(4309, 42, 37, 'کسانی که خود را از گناهان بزرگ و زشتی‌ها دور می‌کنند، و چون به خشم آیند، درمی‌گذرند.'),
(4310, 42, 38, 'کسانی که دعوت پروردگارشان را اجابت کردند، و نماز را برپا داشتند، و کارشان میان آنها با مشورت انجام می‌گیرد، و از آنچه روزیشان کرده‌ایم، انفاق می‌کنند.'),
(4311, 42, 39, 'کسانی که چون ستمی به آنان رسد، [تسلیم نمی‌شوند، بلکه] یاری می‌طلبند.'),
(4312, 42, 40, 'و کیفر هر کار بد مجازاتی است همانند آن [نه بیشتر]، پس هر که بگذرد و اصلاح کند، پاداش او بر [عهده‌ی‌] خداست. او ستمکاران را دوست ندارد.'),
(4313, 42, 41, 'و کسانی که پس از ستم [دیدنِ] خود، یاری جویند، [و عادلانه انتقام بگیرند،] راه [نکوهشی] بر آنان نیست.'),
(4314, 42, 42, 'نکوهش فقط بر کسانی است که به مردم ستم می‌کنند و در زمین به ناحقّ سر به طغیان برمی‌دارند. آنانند که برایشان عذاب دردناکی است.'),
(4315, 42, 43, 'و البتّه کسی که صبر کند، و [از انتقام‌] بگذرد، این از کارهای استوار است.'),
(4316, 42, 44, 'و هر که را خدا [به کیفر کردارش] گمراه کند، پس بعد از آن، هیچ یاوری نخواهد داشت، و ستمگران را خواهی دید که چون عذاب الهی را ببینند، می‌گویند: «آیا راهی برای بازگشت [به دنیا] وجود دارد؟»'),
(4317, 42, 45, 'و آنان را می‌بینی که بر آتش عرضه می‌شوند، و در حالی که از شدّت رسوایی سرافکنده‌اند، از گوشه چشم پنهانی می‌نگرند. و کسانی که ایمان آورده‌اند، می‌گویند: «زیانکاران کسانی هستند که روز قیامت، خود و کسانشان را باخته‌اند.» بدانید که ستمگران در عذابی پایدارند.'),
(4318, 42, 46, 'و هیچ سَروری ندارند که به جای خداوند یاریشان کند. و هر کس را که خداوند [به خاطر کردارش] گمراه کند، پس برای او هیچ راه [نجاتی] نیست.'),
(4319, 42, 47, 'پروردگارتان را اجابت کنید، پیش از آن که روزی فرارسد که آن را از جانب خداوند بازگشتی نباشد. در آن روز هیچ پناهگاهی برای شما نیست، و هیچ قدرت انکار نخواهید داشت.'),
(4320, 42, 48, 'پس اگر روی گرداندند، [غمگین مباش!] ما تو را بر آنان نگهبان نفرستادیم، [تا آنان را به ایمان واداری.] تنها رساندن پیام، بر [عهده‌ی] توست. و البتّه هرگاه از جانب خود رحمتی به انسان بچشانیم، به آن شاد و سرمست می‌شود، و اگر به خاطر دستاوردشان، بلایی به آنان برسد، [نعمت‌ها را فراموش می‌کنند. چرا] که انسان بسیار ناسپاس است.'),
(4321, 42, 49, 'فرمانروایی آسمان‌ها و زمین مخصوص خداست. هر چه بخواهد، می‌آفریند. به هر که بخواهد، فرزند دختر می‌بخشد، و به هر کس بخواهد، فرزند پسر.'),
(4322, 42, 50, 'یا پسران و دختران را با هم می‌دهد. و هر که را بخواهد، نازا می‌گذارد. همانا اوست دانای توانا.'),
(4323, 42, 51, 'و هیچ بشری را یارای آن نیست که خداوند با او سخن بگوید، مگر [از طریق] وحی [مستقیم]، یا از پس پرده و حجاب، یا آن که فرستاده‌ای [از فرشتگان] می‌فرستد، پس به اذن او هر چه بخواهد، وحی می‌کند. همانا او والامرتبه و فرزانه است.'),
(4324, 42, 52, 'و بدین‌گونه ما روحی از فرمان خویش را به تو وحی کردیم. تو نمی‌دانستی کتاب و ایمان چیست، ولی ما آن را نوری قرار دادیم که هر کس از بندگانمان را بخواهیم، [و شایسته بدانیم،] با آن هدایت می‌کنیم. و همانا تو به راه راست هدایت می‌کنی.'),
(4325, 42, 53, 'راه خداوندی که آنچه در آسمان‌ها و آنچه در زمین است، از آنِ اوست. بدانید که همه‌ی امور به سوی خدا بازمی‌گردد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حا، میم.'),
(4327, 43, 2, 'به کتاب روشنگر سوگند!'),
(4328, 43, 3, 'ما آن را قرآنی [فصیح و] عربی قرار دادیم، باشد که در آن بیندیشید.'),
(4329, 43, 4, 'وآن درام ُّالکتاب، [لوح محفوظ،] نزد ما، بلندمرتبه و حکمت‌آمیز و استوار است.'),
(4330, 43, 5, 'آیا از آن رو که شما گروهی اسراف‌کارید، ما قرآن را از شما بازگیریم؟'),
(4331, 43, 6, 'و چه بسیار پیامبرانی که در میان پیشینیان فرستادیم.'),
(4332, 43, 7, 'و هر پیامبری به سراغشان می‌آمد، او را مسخره می‌کردند.'),
(4333, 43, 8, 'پس ما کسانی را که نیرومندتر از آنان بودند، نابود کردیم، و سرنوشت پیشینیان تکرار شد.'),
(4334, 43, 9, 'و اگر از آنان بپرسی: «چه کسی آسمان‌ها و زمین را آفرید؟» خواهند گفت: «آنها را [خداوند] قادر دانا آفریده است.»'),
(4335, 43, 10, 'همان که زمین را برای شما محلّ آسایش قرار داد و در آن برای شما راه‌هایی پدید آورد تا شاید راه یابید.'),
(4336, 43, 11, 'و آن که از آسمان به اندازه‌ای معین، آبی فروفرستاد، پس با آن، سرزمین مرده را زنده کردیم. شما نیز این‌گونه [از قبرها] خارج می‌شوید.'),
(4337, 43, 12, 'و همان کسی که همه‌ی جفت‌ها را آفرید. و برای شما کشتی‌ها و چهارپایان قرار داد که بر آنها سوار شوید.'),
(4338, 43, 13, 'تا بر پشت آنها قرار گیرید. پس آن‌گاه که بر آنها سوار می‌شوید، نعمت پروردگارتان را یاد کنید و بگویید: «منزّه است آن که این [مرکب] را برای ما رام کرد وگرنه، ما توانایی این کار را نداشتیم.'),
(4339, 43, 14, 'و ما حتماً به سوی پروردگارمان بازخواهیم گشت.»'),
(4340, 43, 15, 'و برای او از بندگانش جزئی [مانند فرزند] قرار دادند. بی‌گمان انسان، ناسپاسی آشکار است.'),
(4341, 43, 16, 'آیا از آنچه می‌آفریند، دخترانی [برای خود] برگرفته، و شما را به [داشتن] پسران برگزیده است؟'),
(4342, 43, 17, 'در صورتی که هرگاه یکی از آن مشرکان را به آنچه برای [خداوند] رحمان پسندیده است، مژده دهند، [و بگویند که دختردار شده است،] در حالی که خشم و اندوه خود را فرومی‌برد، چهره‌اش سیاه می‌گردد.'),
(4343, 43, 18, 'آیا کسی را که در زر و زیور پرورش یافته و در مجادله بیانش روشن نیست، [فرزند خدا می‌خوانید]؟'),
(4344, 43, 19, 'و فرشتگان را که خود بندگانِ [خدای] رحمان هستند، مؤنث پنداشتند! آیا شاهد آفرینش آنها بوده‌اند؟ گواهی آنان به زودی نوشته می‌شود، و [از آن] بازخواست خواهند شد.'),
(4345, 43, 20, 'و گفتند: «اگر [خدای] رحمان می‌خواست، ما آنها را نمی‌پرستیدیم.» آنان به این [گفتار]، هیچ آگاهی [و دلیل علمی] ندارند. آنان تنها از روی حدس و گمان سخنی می‌گویند و دروغی می‌بافند.'),
(4346, 43, 21, 'یا پیش از این به آنان کتابی داده‌ایم که [در ادّعاهایشان] آن را دستاویز ساخته‌اند؟'),
(4347, 43, 22, '[چنین نیست!] بلکه می‌گویند: «ما پدرانمان را بر آیینی یافتیم و ما نیز به پیروی آنان هدایت‌یافته‌ایم.»'),
(4348, 43, 23, 'و بدین‌گونه، ما پیش از تو در هر شهر و دیاری هشداردهنده‌ای فرستادیم، ثروتمندان مغرور آن گفتند: «ما پدرانمان را بر آیینی یافتیم، و از پیِ آنان می‌رویم.»'),
(4349, 43, 24, '[پیامبرشان] گفت: «هر چند من آیینی هدایت‌بخش‌تر از آنچه پدرانتان را بر آن یافتید، برایتان بیاورم؟» گفتند: «ما به آنچه شما را به آن فرستاده‌اند، کافریم.»'),
(4350, 43, 25, 'پس ما از آنان انتقام گرفتیم. و بنگر که فرجام تکذیب‌کنندگان چگونه بود؟'),
(4351, 43, 26, 'و [به یاد آور] هنگامی که ابراهیم به [آزَر، عموی مشرکش که به جای] پدرش [سرپرستی او را به عهده داشت]، و به قومش گفت: «من از آنچه شما می‌پرستید، بیزارم.'),
(4352, 43, 27, 'مگر [پرستش] کسی که مرا پدید آورد، که البتّه او هدایتم خواهد کرد.»'),
(4353, 43, 28, 'و آن [کلام توحید] را در میان نسل‌های بعد از خود، سخنی ماندگار قرار داد. باشد که آنان [به توحید] بازگردند.'),
(4354, 43, 29, 'بلکه آنان و پدرانشان را [که در مکّه بت می‌پرستیدند، از انواع نعمت‌ها] بهره‌مند ساختم، تا آن که [قرآن] حقّ و فرستاده‌ای روشنگر به سویشان آمد.'),
(4355, 43, 30, 'و چون حقّ به سراغشان آمد، گفتند: «این جادوست! و ما به آن کافریم.»'),
(4356, 43, 31, 'و گفتند: «چرا این قرآن بر مردی بزرگ [و ثروتمند از یکی] از دو قریه‌ی [مکه و طائف] نازل نشده است؟»'),
(4357, 43, 32, 'آیا آنان رحمت پروردگارت را [در مورد تعیین پیامبر، میان خود] تقسیم می‌کنند؟ در حالی که معیشت آنان در زندگی دنیا را ما میانشان تقسیم کرده‌ایم. و درجات بعضی انسان‌ها را بر بعضی دیگر برتری دادیم تا برخی از آنان برخی دیگر را به خدمت گیرند. و رحمت پروردگارت از آنچه آنان می‌اندوزند، بهتر است.'),
(4358, 43, 33, 'و اگر [بیم آن] نبود که مردم یکپارچه [کافر] شوند، برای خانه‌های کافران [به خدای] رحمان، سقف‌ها و نردبان‌هایی از نقره قرار می‌دادیم که بر آنها بالا روند.'),
(4359, 43, 34, 'و برای خانه‌هایشان، درب‌های نقره‌ایِ متعدّد قرار می‌دادیم، و تخت‌هایی سیمین که بر آنها تکیه زنند.'),
(4360, 43, 35, 'و هرگونه زینتی [برای آنان فراهم می‌کردیم]، ولی اینها فقط کالای اندک زندگی دنیاست، و آخرت نزد پروردگارت برای پرهیزکاران است.'),
(4361, 43, 36, 'و هر کس از یاد [خدای] رحمان روی‌گردان شود، شیطانی بر او می‌گماریم که همراه و دمساز وی گردد.'),
(4362, 43, 37, 'و شیطان‌ها آنها را از راه [حقّ] بازمی‌دارند؛ ولی آنها گمان می‌کنند که هدایت‌یافته‌اند.'),
(4363, 43, 38, 'تا هنگامی که [در قیامت] نزد ما آید، [و به شیطان همدم خود] گوید: «ای کاش میان من و تو، فاصله‌ی مشرق و مغرب بود.! چه بد همنشینی بودی!»'),
(4364, 43, 39, '[خداوند به آنان می‌فرماید:] «امروز [آرزوی دوری از شیطان] هرگز برای شما سودی ندارد، چرا که [در دنیا] ستم کردید. [اینک] در عذاب همراه بکدیگرید.»'),
(4365, 43, 40, '[ای پیامبر!] آیا تو می‌توانی سخن خود را به گوش کران برسانی؟ یا کوردلان، و کسانی را که در گمراهی آشکارند، هدایت کنی؟'),
(4366, 43, 41, 'پس اگر تو را ببریم، از آنان انتقام خواهیم گرفت.'),
(4367, 43, 42, 'یا عذابی را که به آنان وعده داده‌ایم، به تو نشان می‌دهیم. بی‌شک ما بر [نابودی] آنان توانا و چیره‌ایم.'),
(4368, 43, 43, 'پس آنچه را به تو وحی شده، محکم بگیر. همانا تو بر راهی راست هستی.'),
(4369, 43, 44, 'و این [قرآن] برای تو و قومت، مایه‌ی یادآوری است. و به زودی [درباره‌ی آن] بازخواست می‌شوید.'),
(4370, 43, 45, 'و از پیامبرانی که پیش از تو فرستاده‌ایم، جویا شو، که آیا به جای [خداوند] رحمان، معبودان دیگری برای پرستش مردم قرار داده‌ایم؟'),
(4371, 43, 46, 'و به راستی ما موسی را همراه با آیات [و معجزات] خود به سوی فرعون و اشراف قومش فرستادیم. پس گفت: «من فرستاده‌ی پروردگار جهانیانم.»'),
(4372, 43, 47, 'پس چون معجزه‌های ما را برای آنان آورد، به آن خندیدند.'),
(4373, 43, 48, 'و ما هر معجزه‌ای به فرعونیان نشان می‌دادیم، از همانندش بزرگ‌تر [و مهمّ‌تر] بود. و ما آنان را به [انواع] عذاب گرفتار کردیم، تا شاید بازگردند.'),
(4374, 43, 49, 'و [وقتی گرفتار شدند، به موسی] گفتند: «ای جادوگر! پروردگارت را به آنچه نزد تو عهد کرده است، برای ما بخوان، [تا عذاب را از ما بردارد، که در آن صورت] قطعاً هدایت تو را خواهیم پذیرفت.»'),
(4375, 43, 50, 'ولی همین که عذاب را از آنان برداشتیم، همان‌دم همگی پیمان شکستند.'),
(4376, 43, 51, 'و فرعون در میان قوم خود ندا داد: «ای قوم من! آیا فرمانرواییِ مصر از آن من نیست؟ و این نهرها از زیر [کاخ] من روان نیست؟ پس آیا [شکوه من و فقر موسی را] نمی‌بینید؟'),
(4377, 43, 52, 'بلکه من از این کسی که بی‌مقدار است، و نمی‌تواند روشن سخن بگوید، بهترم.'),
(4378, 43, 53, 'چرا دستبندهایی از طلا بر او آویخته نیست؟ یا چرا با او فرشتگانی همراه نشده‌اند [تا گفتارش را تأیید کنند]؟»'),
(4379, 43, 54, 'پس [فرعون]، قوم خود را سبک شمرد [و خوار و ذلیل داشت‌،] آنها هم اطاعتش کردند، زیرا قومی فاسق بودند.'),
(4380, 43, 55, 'پس چون ما را به خشم آوردند، از آنان انتقام گرفتیم و همه‌ی آنان را غرق کردیم.'),
(4381, 43, 56, 'پس آنان را پیشگامانی [بد،] و عبرتی برای آیندگان قرار دادیم.'),
(4382, 43, 57, 'و چون [بت‌پرستان] درباره‌ی [عیسی] پسر مریم مثالی زدند ناگهان قوم تو فریاد تمسخر برداشتند.'),
(4383, 43, 58, 'و گفتند: «آیا خدایان ما بهترند یا او؟» این مثال را برای تو تنها از راه جدال [و لجاجت] زدند، بلکه آنان گروهی کینه‌توز و پرخاشگرند.'),
(4384, 43, 59, 'عیسی فقط بنده‌ای بود که ما به او نعمت بخشیدیم و او را برای بنی‌اسرائیل الگو و نمونه قرار دادیم.'),
(4385, 43, 60, 'و اگر می‌خواستیم به جای شما در زمین فرشتگانی می‌گماشتیم که جانشین [شما] گردند.'),
(4386, 43, 61, 'و همانا او، [که مرده زنده می‌کرد،] مایه‌ی علم و آگاهی به قیامت است. پس در قیامت شک نکنید و از من پیروی کنید، که این راهی راست است.'),
(4387, 43, 62, 'و شیطان شما را [از راه خدا] باز ندارد، که او دشمن آشکار شماست.'),
(4388, 43, 63, 'و چون عیسی با دلایل روشن [و معجزات] آمد، گفت: «همانا من برای شما حکمت آورده‌ام، [و آمده‌ام] تا برخی از آنچه را در آن اختلاف دارید، برای شما روشن کنم. پس از خداوند پروا و مرا اطاعت کنید.'),
(4389, 43, 64, 'خداوند، همان پروردگار من و پروردگار شماست، پس او را بپرستید که این راهی راست است.»'),
(4390, 43, 65, 'پس گروه‌هایی از میان آنها [درباره‌ی مسیح] اختلاف ورزیدند، [و برخی او را خدا پنداشتند.] وای بر کسانی که ستم کردند، از عذاب روزی دردناک!'),
(4391, 43, 66, 'آیا جز قیامت را انتظار می‌برند که ناگهان به سراغشان آید، در حالی که بی‌خبر باشند؟'),
(4392, 43, 67, 'دوستانِ [امروز]، در آن روز با یکدیگر دشمنند، مگر پرهیزکاران.'),
(4393, 43, 68, '[خداوند به آنان می‌فرماید:] ای بندگان من! امروز نه ترسی بر شماست و نه غمگین می‌شوید.'),
(4394, 43, 69, 'آنان که به آیات ما ایمان آوردند و همواره تسلیم [حقّ] هستند.'),
(4395, 43, 70, 'شما و همسرانتان شادمانه به بهشت درآیید.'),
(4396, 43, 71, 'ظرف‌هایی از طلا و جام‌هایی [زرین] بر گرد آنان می‌چرخانند. و در آنجا آنچه دل‌ها بخواهد و چشم‌ها از آن لذّت برد، فراهم است. و شما [ای پرهیزکاران!] در آن جاودانه‌اید.'),
(4397, 43, 72, 'و این است بهشتی که به خاطر عملکردتان به میراث برده‌اید.'),
(4398, 43, 73, 'در آنجا برای شما میوه‌های فراوان است که از آنها می‌خورید.'),
(4399, 43, 74, 'بی‌شک گناهکاران در عذاب دوزخ جاودانه‌اند.'),
(4400, 43, 75, '[عذاب] از آنان کاسته نمی‌شود، و آنان در آنجا نومیدند.'),
(4401, 43, 76, 'و ما به آنان ستم نکردیم؛ بلکه آنان خود ستمگر بوده‌اند.'),
(4402, 43, 77, 'و فریاد می‌کشند که: «ای نگهبان [دوزخ! بگو] پروردگارت حکم [مرگ] ما را صادر کند. [تا از این عذاب آسوده شویم.]» او می‌گوید: «شما در این جا ماندنی هستید! [و مرگ در کار نیست.]»'),
(4403, 43, 78, 'به راستی ما حقّ را برای شما آوردیم؛ ولی بیشتر شما حقّ را نمی‌پسندید.'),
(4404, 43, 79, 'بلکه آنان [برای توطئه علیه فرستاده‌ی ما‌] تصمیم قطعی گرفتند، پس ما نیز نقشه‌ی خود را علیه آنان محکم می‌کنیم.'),
(4405, 43, 80, 'آیا آنان می‌پندارند که ما رازهای پنهانی و سخنان درِ گوشی آنان را نمی‌شنویم؟ چرا! و فرستادگان ما نزد آنان حضور دارند و [همه چیز را] ثبت می‌کنند.'),
(4406, 43, 81, '[ای پیامبر! به آنان] بگو: «اگر برای [خداوند] رحمان فرزندی بود، من نخستین پرستنده [او] بودم».'),
(4407, 43, 82, 'منزّه است پروردگار آسمان‌ها و زمین، پروردگار عرش، از آنچه او را وصف می‌کنند.'),
(4408, 43, 83, 'پس آنان را [به حال خود] واگذار تا [در باطل] فروروند و سرگرم باشند، تا روزی را که وعده داده شده‌اند، ببینند.'),
(4409, 43, 84, 'و اوست آن که در آسمان‌ها پرستش می‌شود، و در زمین نیز پرستش می‌شود. و اوست حکیم دانا.'),
(4410, 43, 85, 'بلندمرتبه و پربرکت است، آن که فرمانروایی آسمان‌ها و زمین و آنچه میان آنهاست، از آنِ اوست. و آگاهی از قیامت، مخصوص اوست. و به سوی او بازگردانده می‌شوید.'),
(4411, 43, 86, 'کسانی که به جای او [به پرستش] می‌خوانند، اختیار هیچ‌ گونه شفاعتی ندارند، اختیار شفاعت تنها با کسانی است که شهادت به حقّ داده‌اند، و آنان خود می‌دانند [چه کسی را شفاعت کنند].'),
(4412, 43, 87, 'و اگر از مشرکان بپرسی: چه کسی آنان را آفریده است؟ قطعاً خواهند گفت: خدا.» پس چگونه به انحراف کشانده می‌شوند؟'),
(4413, 43, 88, 'و سخن رسول ما این است: «ای پروردگار! اینان قومی هستند که ایمان نمی‌آورند.»'),
(4414, 43, 89, '[اکنون که از هدایتشان ناامید شده‌ای،] پس از آنان روی‌گردان و بگو: «بدرود!» در آینده خواهند فهمید. [که کیفرشان چیست؟]');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حا، میم.'),
(4416, 44, 2, 'به کتاب روشنگر سوگند.'),
(4417, 44, 3, 'ما آن را در شبی مبارک و فرخنده نازل کردیم. ما همواره هشداردهنده بوده‌ایم.'),
(4418, 44, 4, 'در آن [شب مبارک]، هر امر استواری [طبق حکمت الهی] تفصیل و تبیین می‌شود.'),
(4419, 44, 5, 'امری که از جانب ما است و ما فرستنده [آن] بودیم.'),
(4420, 44, 6, '[اینها همه] رحمت بزرگی از سوی پروردگار توست. او خود شنوای داناست.'),
(4421, 44, 7, 'پروردگار آسمان‌ها و زمین و آنچه میان آنهاست، اگر اهل یقین باشید.'),
(4422, 44, 8, 'هیچ معبودی جز او نیست. اوست که زنده می‌کند و می‌میراند. پروردگار شما و پروردگار نیاکان شماست.'),
(4423, 44, 9, '[کافران این را باور ندارند،] بلکه آنان در شک و تردید خود سرگرمند.'),
(4424, 44, 10, 'پس منتظر روزی باش که آسمان دودِ نمایانی را با خود می‌آورد.'),
(4425, 44, 11, '[دودی که] همه‌ی مردم را فرامی‌گیرد. این است عذابی دردناک!'),
(4426, 44, 12, '[آن روز مردم می‌گویند:] «پروردگارا! عذاب را از ما دفع کن! ما ایمان می‌آوریم.»'),
(4427, 44, 13, 'این پندپذیری و بیداری برای آنان چه سود؟ و حال آن که پیامبری روشنگر به سراغشان آمد.'),
(4428, 44, 14, 'سپس از او روی گرداندند و گفتند: «او جنّ‌زده‌ای است که تعلیمش داده‌اند.»'),
(4429, 44, 15, 'البتّه ما اندک زمانی عذاب را برطرف می‌کنیم، [امّا] شما باز [به کفر خود] برمی‌گردید!'),
(4430, 44, 16, 'روزی که [آنها را] با قدرتی بزرگ خواهیم گرفت، که ما انتقام گیرنده‌ایم.'),
(4431, 44, 17, 'و همانا ما پیش از این کافران، قوم فرعون را آزمودیم و پیامبری بزرگوار برای آنان آمد.'),
(4432, 44, 18, '[موسی به آنان گفت:] «بندگان خدا را به من بسپارید من برای شما پیامبری امین هستم.'),
(4433, 44, 19, 'و بر خداوند برتری نجویید! من برهانی آشکار برای شما آورده‌ام.'),
(4434, 44, 20, 'و از این که مرا سنگسار کنید، به پروردگار خود و پروردگار شما پناه می‌برم.'),
(4435, 44, 21, 'و اگر به من ایمان نمی‌آورید، پس از من کناره بگیرید، و مرا به حال خود واگذارید.»'),
(4436, 44, 22, 'پس [چون فرعونیان سخنش را نپذیرفتند،] پروردگارش را خواند «که آنان قومی گنه‌پیشه‌اند.»'),
(4437, 44, 23, 'پس [خداوند به او فرمود:] «بندگان مرا شبانه کوچ بده! زیرا که شما تحت تعقیب هستید.'),
(4438, 44, 24, 'و از دریا آرام بگذر که آنان لشکری غرق شده خواهند بود.»'),
(4439, 44, 25, 'چه بسیار باغ‌ها و چشمه‌ساران که آنان بر جای گذاشتند،'),
(4440, 44, 26, 'و کشت‌ها و جایگاه‌های عالی،'),
(4441, 44, 27, 'و ناز و نعمتی که در آن خوش بودند.'),
(4442, 44, 28, 'ما این‌گونه [همه را از آنان گرفتیم] و آنها را به قوم دیگر به میراث دادیم.'),
(4443, 44, 29, 'پس نه آسمان بر آنان گریست، و نه زمین. و مهلت نیافتند.'),
(4444, 44, 30, 'و به راستی ما بنی‌اسرائیل را از آن عذاب خوارکننده نجات دادیم.'),
(4445, 44, 31, 'از [عذاب] فرعون، که برتری‌جو و از اسراف‌کاران بود.'),
(4446, 44, 32, 'و همانا ما آنان را با علم [خود] بر جهانیانِ [هم‌عصرشان] برگزیدیم.'),
(4447, 44, 33, 'و نشانه‌هایی [از قدرت خود] را به آنان دادیم که در آن آزمایشی آشکار بود. [اما چه سود؟ که آنان کافر شدند.]'),
(4448, 44, 34, 'البتّه این مشرکان [روزگار تو] پیوسته می‌گویند:'),
(4449, 44, 35, '«جز این مرگ نخستین ما، [چیز دیگری در کار] نیست. و ما بار دیگر زنده نخواهیم شد.'),
(4450, 44, 36, 'پس اگر شما [پیامبران درباره‌ی معاد] راستگو هستید، پدران ما را بازآورید.»'),
(4451, 44, 37, 'آیا مشرکان مکه بهترند یا قوم تُبَّع و دیگرانی که پیش از آنان بودند؟ ما آنان را به خاطر آن که قومی تبهکار بودند، نابود کردیم.'),
(4452, 44, 38, 'و ما آسمان‌ها و زمین، و آنچه را میان آنهاست، به بازی نیافریدیم.'),
(4453, 44, 39, 'ما آنها را فقط بر اساس حقّ آفریدیم؛ ولی بیشتر مشرکان نمی‌دانند.'),
(4454, 44, 40, 'همانا روز جدایی [حقّ از باطل]، وعده‌گاه همه‌ی آنهاست.'),
(4455, 44, 41, 'روزی که هیچ دوستی از دوست خود حمایتی نمی‌کند، و آنان یاری نمی‌شوند.'),
(4456, 44, 42, 'جز کسی که خداوند او را مورد رحمت قرار دهد. اوست که پیروز و مهربان است.'),
(4457, 44, 43, 'بی‌گمان درخت زقّوم،'),
(4458, 44, 44, 'خوراک گناهکاران است.'),
(4459, 44, 45, 'همچون مس گداخته، در شکم آنها می‌جوشد.'),
(4460, 44, 46, 'مانند جوشش آب داغ.'),
(4461, 44, 47, '[به مأموران دوزخ گفته می‌شود:] «این گنه‌کار را بگیرید، و او را به وسط آتش سوزان دوزخ بکشانید.'),
(4462, 44, 48, 'سپس از عذابِ آب سوزان، بر سرش بریزید.'),
(4463, 44, 49, '[به او با تمسخر گفته می‌شود:] «بچش! که تو همان عزیزِ بزرگواری!'),
(4464, 44, 50, 'این همان است که همواره در آن تردید داشتید.»'),
(4465, 44, 51, 'به راستی پرهیزکاران در جایگاهی امن هستند.'),
(4466, 44, 52, 'در میان باغ‌ها و [کنار] چشمه‌ساران.'),
(4467, 44, 53, 'لباس‌های ابریشمی نازک و ضخیم می‌پوشند، در حالی که در برابر هم [بر تخت‌ها] نشسته‌اند.'),
(4468, 44, 54, 'این‌گونه [ما پاداش می‌دهیم.] و حوریان را به همسری آنان درمی‌آوریم.'),
(4469, 44, 55, 'در آن [باغ‌ها]، هر میوه‌ای را [که بخواهند،] با آسودگی می‌طلبند.'),
(4470, 44, 56, 'در بهشت، جز مرگ نخستین [که پشت سر گذاشته‌اند،] مرگی نخواهند چشید. و خداوند آنان را از عذاب سوزان نگاه داشته است.'),
(4471, 44, 57, '[اینها همه،] بخششی است از [سوی] پروردگارت. این است همان کامیابی بزرگ!'),
(4472, 44, 58, 'مسلماً ما قرآن را به زبان تو آسان ساختیم، تا شاید [آنان] پند گیرند.'),
(4473, 44, 59, '[ولی اگر پند نگرفتند،] پس منتظر [سرانجام کار] باش! که آنان نیز منتظرند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حا، میم.'),
(4475, 45, 2, 'نازل شدن کتاب، از سوی خداوند شکست‌ناپذیر و حکیم است.'),
(4476, 45, 3, 'در آسمان‌ها و زمین، برای مؤمنان نشانه‌هایی است.'),
(4477, 45, 4, 'و در آفرینش شما و جنبندگانی که [در هستی] پراکنده ساخته، برای اهل یقین، نشانه‌هایی [از قدرت الهی] است.'),
(4478, 45, 5, 'و شب و روز که در پی یکدیگر می‌آیند و آنچه از روزی که خداوند از آسمان فروفرستاده و زمین را با آن پس از پژمردگی‌اش، سبز و زنده نموده است و گردش بادها، برای کسانی که می‌اندیشند، نشانه‌هایی است.'),
(4479, 45, 6, 'این‌ها آیات الهی است که ما آنها را به حقّ بر تو تلاوت می‌کنیم، [اگر اینها را نپذیرند،] پس به کدام سخن بعد از [سخن] خدا و آیات او، ایمان می‌آورند؟'),
(4480, 45, 7, 'وای بر هر دروغ‌پرداز گناه‌پیشه!'),
(4481, 45, 8, 'که آیات خدا را که پیوسته بر او تلاوت می‌شود، می‌شنود؛ ولی همچون کسی که آنها را نشنیده، متکبّرانه [بر انحراف خود] اصرار می‌ورزد. پس او را به عذابی دردناک بشارت بده!'),
(4482, 45, 9, 'و هرگاه از آیات ما چیزی بفهمد، آن را به مسخره می‌گیرد. آنانند که برایشان عذابی خفّت‌بار است.'),
(4483, 45, 10, 'دوزخ پیش روی آنهاست و دستاوردشان و سرورانی که به جای خداوند برگزیده‌اند، ذرّه‌ای آنان را بی‌نیاز نمی‌کند و عذابی بزرگ [در پیش] دارند.'),
(4484, 45, 11, 'این [قرآن] مایه‌ی هدایت است و کسانی که به آیات پروردگارشان کفر ورزیدند، عذابی شدید و دردناک از هول و اضطراب خواهند داشت.'),
(4485, 45, 12, 'خداوند کسی است که دریا را برای شما رام کرد، تا به فرمان او کشتی‌ها در آن روان شوند و تا از فضل او [روزی] بجویید و شاید سپاسگزاری کنید.'),
(4486, 45, 13, 'و آنچه در آسمان‌ها و آنچه در زمین است، همه از اوست که برای شما رام نمود. قطعاً در این، برای کسانی که فکر می‌کنند، نشانه‌هایی است.'),
(4487, 45, 14, '[ای پیامبر!] به کسانی که ایمان آورده‌اند، بگو: از کسانی که به روزهای خدا امید ندارند، درگذرند [و آنان را به خدا واگذارند،] تا [خداوند در قیامت] هر قومی را به آنچه همواره مرتکب می‌شوند، کیفر دهد.'),
(4488, 45, 15, 'هر کس کار شایسته‌ای کند، پس به سود خود اوست و هر کس کار بدی مرتکب شود، پس به زیان خود اوست. سپس به سوی پروردگارتان بازگردانده می‌شوید.'),
(4489, 45, 16, 'و ما به بنی‌اسرائیل کتاب و حکومت و نبوّت دادیم و از [خوردنی‌های] پاکیزه و دلپسند روزیشان کردیم و آنان را بر جهانیان [آن روزگار] برتری دادیم.'),
(4490, 45, 17, 'و دلایل روشنی در امر [دین] به آنان دادیم، پس اختلاف نکردند، مگر پس از آن که حقیقت برایشان حاصل شد. [و این اختلاف] از روی حسادت و برتری‌جویی بود که میان آنان وجود داشت. بی‌تردید پروردگارت در قیامت درباره‌ی آنچه پیوسته اختلاف می‌کردند، میانشان داوری خواهد کرد.'),
(4491, 45, 18, 'سپس تو را در امر [دین‌]، بر شریعت و آیینی ویژه و کامل قرار دادیم، پس آن را پیروی کن و از هوس‌ها و خواسته‌های نادانان پیروی مکن!'),
(4492, 45, 19, 'آنان در برابر [عذاب] خداوند، هرگز از تو دفاع نمی‌کنند و البتّه ستمگران یاور یکدیگرند و خداوند یار پرهیزکاران است.'),
(4493, 45, 20, 'این [کتاب و آیین] برای مردم، وسیله‌ی بصیرت و هدایت و برای اهل یقین، مایه‌ی رحمت است.'),
(4494, 45, 21, 'آیا کسانی که مرتکب کارهای بد شدند، گمان کردند که ما آنان را همچون کسانی که ایمان آورده و کارهای شایسته کرده‌اند، قرار خواهیم داد؟ و حیات و مرگشان یکسان است؟ چه بد داوری می‌کنند!'),
(4495, 45, 22, 'و خداوند آسمان‌ها و زمین را به حقّ آفرید، تا هر کس به موجب آنچه کسب کرده است، پاداش داده شود و به آنان ستم نمی‌شود.'),
(4496, 45, 23, 'پس آیا دیدی [حال] کسی را که هوای [نفس] خویش را معبود خود قرار داده و خداوند او را با وجود آگاهیش به بیراهه گذاشت و بر گوش و قلبش مهر زد و بر چشمش پرده‌ای نهاد؟ پس بعد از خدا کیست که او را هدایت کند؟ پس آیا پند نمی‌گیرید؟'),
(4497, 45, 24, 'وگفتند: «جز همین زندگی دنیایی، زندگی دیگری نیست. [گروهی] می‌میریم و [گروهی] زنده می‌شویم و ما را چیزی جز طبیعت و روزگار نابود نمی‌کند.» در حالی که هیچ‌گونه علمی بر این ادّعا ندارند. آنان تنها گروهی خیال‌بافند.'),
(4498, 45, 25, 'و هرگاه آیات روشن ما [درباره‌ی معاد] بر آنان تلاوت گردد، دلیل و حجّتی ندارند، مگر آن که می‌گویند: «اگر راست می‌گویید، پدران ما را [زنده کنید و] بیاورید!»'),
(4499, 45, 26, 'بگو: «خداست که شما را زنده می‌کند، سپس می‌میراند و آن‌گاه برای روز قیامت که شکی در آن نیست، همه را گرد می‌آورد؛ ولی بیشتر مردم نمی‌دانند.»'),
(4500, 45, 27, 'فرمانروایی آسمان‌ها و زمین مخصوص خداست و روزی که رستاخیز برپا شود، آن روز یاوه‌سرایان زیانکارند.'),
(4501, 45, 28, 'و [در روز رستاخیز] هر امّتی را [از شدّت خضوع و وحشت] به زانو درآمده می‌بینی، در حالی که هر امّتی به سوی [کارنامه و] کتابش فراخوانده شده است. [و به آنان می‌گوییم:] «امروز آنچه را عمل می‌کردید، به شما پاداش می‌دهند.'),
(4502, 45, 29, 'این است کتاب ما که به حقّ بر ضدّ شما سخن می‌گوید. [و کردار شما را بازگو می‌کند.] ما آنچه را انجام می‌دادید، نسخه‌برداری و ثبت می‌کردیم.»'),
(4503, 45, 30, 'پس کسانی که ایمان آوردند و کارهای شایسته کرده‌اند، پروردگارشان آنان را در رحمت خویش وارد می‌کند. این است آن رستگاری آشکار!'),
(4504, 45, 31, 'و امّا کسانی که کافر شدند، [به آنان می‌گوییم:] مگر آیات من بر شما خوانده نشد؟ پس استکبار ورزیدید و گروهی گنهکار بودید.'),
(4505, 45, 32, 'و هرگاه گفته شد: «وعده‌ی خداوند حقّ است و در قیامت شکی نیست.» گفتید: «ما نمی‌دانیم قیامت چیست؟! ما تنها گمانی [ضعیف] در این‌باره داریم، و ما به هیچ وجه به یقین نرسیده‌ایم.»'),
(4506, 45, 33, 'و [آن روز] بدی‌های اعمالشان برایشان روشن می‌شود و آنچه را به مسخره می‌گرفتند، آنان را فرامی‌گیرد.'),
(4507, 45, 34, 'و به آنان گفته شود: «امروز ما شما را به فراموشی می‌سپاریم، همان‌گونه که شما دیدار امروزتان را به فراموشی سپردید و جایگاه شما آتش است و برای شما هیچ یاوری نیست.'),
(4508, 45, 35, 'این به خاطر آن است که شما آیات خدا را به مسخره گرفتید و زندگی دنیا فریبتان داد.» پس امروز نه از آن [دوزخ] بیرون آورده می‌شوند و نه عذرخواهی آنان را می‌پذیرند.'),
(4509, 45, 36, 'پس سپاس و ستایش مخصوص خداوند است، پروردگار آسمان‌ها و پروردگار زمین، پروردگار جهانیان.'),
(4510, 45, 37, 'و بزرگی در آسمان‌ها و زمین مخصوص اوست و اوست خدای شکست‌ناپذیر و حکیم!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حا، میم.'),
(4512, 46, 2, 'نازل شدن [این‌] کتاب [بر تو]، از سوی خداوند شکست‌ناپذیر و حکیم است.'),
(4513, 46, 3, 'ما آسمان‌ها و زمین و آنچه را میان آنهاست، جز بر اساس حقّ و زمان‌بندی مشخّص نیافریدیم؛ و[لی] کسانی که کفر ورزیدند، از آنچه بیمشان داده‌اند، روی‌گردانند.'),
(4514, 46, 4, 'بگو: «آیا آنچه را که به جای خداوند می‌خوانید، دیده‌اید؟ به من نشان دهید که چه چیزی را از زمین آفریده‌اند، یا در [آفرینش] آسمان‌ها چه مشارکتی داشته‌اند؟ اگر راست می‌گویید، کتابی را که پیش از این [قرآن] بوده، یا دلیلی علمی برای من بیاورید.»'),
(4515, 46, 5, 'و کیست گمراه‌تر از کسی که به جای خداوند، کسانی را می‌خواند که تا روز قیامت پاسخش را نمی‌دهند و حتّی از خوانده‌ شدن خود بی‌خبرند؟'),
(4516, 46, 6, 'و آن‌گاه که مردم محشور شوند، [معبودانی که مردم آنان را می‌خواندند،] با آنها دشمن خواهند بود و عبادتشان را انکار خواهند کرد.'),
(4517, 46, 7, 'و هرگاه آیات روشنگر ما بر آنان خوانده شود، کسانی که کافر شدند، درباره‌ی حقّی که برایشان آمده، می‌گویند: «این جادویی آشکار است!»'),
(4518, 46, 8, 'یا این که می‌گویند: «(محمّد) آن را به دروغ ساخته [و به خدا نسبت داده] است!» بگو: «اگر من آن را بافته و دروغ بسته باشم، شما نمی‌توانید در برابر خداوند از من دفاع کنید. خداوند به آنچه در آن وارد می‌شوید، [و ناروا جدال می‌کنید،] آگاه‌تر است. او برای گواه بودن میان من و شما کافی است و اوست بسیار آمرزنده و مهربان.»'),
(4519, 46, 9, 'بگو: «من در میان پیامبران، بی‌سابقه و نوظهور نیستم و نمی‌دانم با من و شما چگونه رفتار خواهد شد و فقط از آنچه به من وحی می‌شود، پیروی می‌کنم و من تنها هشدار دهنده‌ای آشکارم!»'),
(4520, 46, 10, 'بگو: «اگر [این قرآن] از نزد خدا باشد و شما به آن کفر ورزید، در حالی که گواهی از بنی‌اسرائیل، بر همانند آن گواهی داده و ایمان آورده؛ ولی شما [همچنان] تکبّر ورزیده‌اید، آیا [عاقبت کار را] دیده‌اید؟ خداوند قوم ستمگر را هدایت نمی‌کند.»'),
(4521, 46, 11, 'و کسانی که کافر شدند، درباره‌ی کسانی که ایمان آورده‌اند، می‌گویند: «اگر [آیین محمّد] بهتر بود، آنان [در پذیرفتن آن] بر ما سبقت نمی‌گرفتند.» و چون خود به آن هدایت نیافتند، به زودی خواهند گفت: «این دروغی سابقه‌دار است!»'),
(4522, 46, 12, 'و پیش از آن، کتاب موسی راهبر و [مایه‌ی] رحمت بود، و این کتاب که به زبان عربی است، تصدیق‌کننده [آن تورات] است تا ستمگران را بیم دهد و برای نیکوکاران بشارت باشد.'),
(4523, 46, 13, 'همانا کسانی که گفتند: «پروردگار ما تنها خداست.» سپس [بر گفته‌ی خود] پایداری کردند، بیمی نخواهند داشت و اندوهگین نخواهند شد.'),
(4524, 46, 14, 'آنان به پاداش آنچه انجام می‌دادند، اهل بهشتند و در آن جاودانه خواهند ماند.'),
(4525, 46, 15, 'و ما انسان را سفارش کردیم که به مادر و پدر خود نیکی کند. مادرش با سختی او را باردار بود و با سختی به دنیا آورد و دوران بارداری تا از شیر گرفتنش [دست کم] سی ماه است. تا آن‌گاه که به رشد کامل برسد و چهل ساله شود، بگوید: «پروردگارا! به دلم انداز که نعمتی را که بر من و بر پدر و مادر من لطف کردی، شکرگزارم و کار شایسته‌ای کنم که تو آن را بپسندی و فرزندانم را برای من صالح و شایسته گردان! من به سوی تو بازگشته‌ام و از تسلیم‌شدگانم.»'),
(4526, 46, 16, 'آنانند کسانی که از آنها بهترینِ کارهایی را که کرده‌اند، می‌پذیریم و در زمره‌ی بهشتیان، از بدی‌هایشان می‌گذریم. همان وعده راستی که همواره به آنان داده می‌شد.'),
(4527, 46, 17, 'و آن که به مادر و پدر خود [که درباره قیامت نصیحتش می‌کنند،] می‌گوید: «اَه! [از من دور شوید!] آیا به من وعده می‌دهید که [پس از مرگ از گور] بیرون آورده می‌شوم؟ در حالی که نسل‌های بسیاری پیش از من گذشته‌اند [و هرگز زنده نشده‌اند].» ولی مادر و پدر او، خدا را به فریادرسی می‌خوانند [و به او می‌گویند]: «وای بر تو! ایمان بیاور! «قطعاً وعده‌ی خداوند حقّ است.» امّا او می‌گوید: «این [وعده‌ها چیزی] جز افسانه‌های پیشینیان نیست.»'),
(4528, 46, 18, 'آنان کسانی هستند که [به خاطر انکار وعده‌های حقّ،] در میان امّت‌هایی از جنّ و انس که پیش از آنان بودند، فرمان [عذاب] خدا بر آنان قطعی شده است. زیرا آنان زیانکار بودند.'),
(4529, 46, 19, 'و برای هر یک [از مؤمنان و کافران]، بر پایه‌ی آنچه انجام داده‌اند، درجاتی [از سعادت و شقاوت] است و [خداوند] جزای کارهایشان را کامل می‌دهد و آنان مورد ستم قرار نمی‌گیرند.'),
(4530, 46, 20, 'و روزی که کافران بر آتش عرضه شوند، [به آنان گفته می‌شود:] «شما در زندگی دنیا، نعمت‌های پاکیزه‌ی خود را تلف کردید و از آنها کامیاب شدید، [و برای امروزتان چیزی نیاندوختید،] پس امروز به خاطر گردن‌کشی ناحقّی که در زمین داشتید و پیوسته نافرمانی می‌کردید، به عذاب خوارکننده کیفر داده می‌شوید.»'),
(4531, 46, 21, 'و سرگذشت برادر قوم عاد [حضرت هود] را یاد کن، آن‌گاه که قومش را در سرزمین احقاف هشدار داد. در حالی که پیش از او و پس از او نیز هشداردهندگانی آمده بودند. [او به قومش گفت]: «جز خدا را نپرستید که من بر شما از عذاب روزی بزرگ می‌ترسم.»'),
(4532, 46, 22, 'آنها گفتند: «آیا به سراغ ما آمده‌ای تا ما را از خدایانمان برگردانی؟ پس اگر از راستگویانی، آن را که وعده می‌دهی، برای ما بیاور!»'),
(4533, 46, 23, '[هود] گفت: «علم [به زمان عذاب،] تنها نزد خداست و من آنچه را به آن فرستاده شده‌ام، به شما ابلاغ می‌کنم؛ ولی شما را گروهی جاهل می‌بینم.»'),
(4534, 46, 24, 'پس چون آن توده‌ی ابر را دیدند که به سمت سرزمینشان روی آورده است، گفتند: «این ابری باران زاست!» [هود گفت:] «بلکه آن عذابی است که با شتاب خواستارش بودید. بادی که در آن عذابی دردناک نهفته است.'),
(4535, 46, 25, '[آن باد] به فرمان پروردگارش همه چیز را در هم می‌کوبد.» پس چنان شدند که چیزی جز خانه‌هایشان دیده نمی‌شد. ما این گونه، تبهکاران را کیفر می‌دهیم.'),
(4536, 46, 26, 'و همانا به قوم عاد چنان امکاناتی دادیم که به شما [اهل مکه] ندادیم و به آنان گوش و چشم و دل دادیم؛ ولی گوش و چشم و دل آنان به حالشان سودی نبخشید، زیرا پیوسته آیات خدا را انکار می‌کردند و [سرانجام] عذابی که آن را به مسخره می‌گرفتند، آنان را فراگرفت.'),
(4537, 46, 27, 'و بی‌گمان آبادی‌هایی را که در اطراف شما [اهل مکه] بود، نابود کردیم و [پیش از آن] آیات خود را به صورت‌های گوناگون [برای آنان] بیان کردیم تا شاید بازگردند.'),
(4538, 46, 28, 'پس چرا معبودانی که به جای خداوند برگزیده بودند، به گمان این که آنان را به خدا نزدیک کند، آنان را یاری نکردند؟ بلکه [همه] گم شدند و از نزد آنان دور گشتند و این بود [سرانجام] دروغ و انحراف آنان و آنچه برمی‌بافتند.'),
(4539, 46, 29, 'و آن‌گاه که گروهی از جنّ را به سوی تو روانه ساختیم تا قرآن را بشنوند، پس چون نزد آن حاضر شدند، گفتند: «خاموش باشید! [و گوش فرادهید.]» پس چون [شنیدن آیات] به انجام رسید، به سوی قوم خود بازگشتند تا آنان را هشدار دهند.'),
(4540, 46, 30, 'گفتند: «ای قوم ما! ما [آیات] کتابی را شنیدیم که بعد از موسی نازل شده و کتاب‌های پیش از خود را تصدیق و به سوی حقّ و راه راست هدایت می‌کند.'),
(4541, 46, 31, 'ای قوم ما! دعوت‌گر الهی را اجابت کنید و به او ایمان بیاورید تا [خداوند] پاره‌ای از گناهانتان را بر شما ببخشاید و از عذابی دردناک امانتان دهد.'),
(4542, 46, 32, 'و هر که دعوت‌گر الهی را پاسخ نگوید، پس نمی‌تواند [خدا را] در زمین درمانده کند [و از گستره‌ی قدرت او بیرون رود] و جز خداوند هیچ‌گونه یاوری نخواهد داشت. چنین کسانی در گمراهی آشکارند.»'),
(4543, 46, 33, 'آیا درنیافته‌اند خدایی که آسمان‌ها و زمین را آفریده و در آفرینش آنها درنمانده است، می‌تواند مردگان را زنده کند، آری، او بر هر کاری بسیار تواناست.'),
(4544, 46, 34, 'و روزی که کافران بر آتش عرضه شوند، [به آنان گفته می‌شود:] «آیا این حقّ نیست؟» می‌گویند: «به پروردگارمان سوگند که چنین است!» [خداوند] می‌فرماید: «پس به خاطر کفرتان، عذاب را بچشید.»'),
(4545, 46, 35, 'پس [ای پیامبر!] صبر کن، چنان که پیامبران اولوا العزم صبر کردند و برای [عذاب] آنان شتاب مکن! روزی که آنچه را وعده داده شده‌اند، بنگرند، گویی جز ساعتی از روز [در دنیا] نمانده‌اند. [این] ابلاغی است [برای همگان]، پس آیا جز گروه بدکار نابود می‌شوند؟');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'کسانی که کفر ورزیدند و [مردم را] از راه خدا بازداشتند، [خداوند] اعمالشان را تباه گردانید.'),
(4547, 47, 2, 'و کسانی که ایمان آورده و کارهای نیکو کرده‌اند و به آنچه بر محمّد نازل شده، که حقّ است و از جانب پروردگارشان است، ایمان آورده‌اند، بدی‌های آنان را پوشاند و کارشان را به سامان رساند.'),
(4548, 47, 3, 'این [دوگونه سرانجام]، به خاطر آن است که کسانی که کافر شدند، از باطل پیروی کردند و کسانی که ایمان آوردند، از همان حقّی که از سوی پروردگارشان است، پیروی نمودند. این‌گونه خدا برای مردم، مثال حالشان را بیان می‌کند.'),
(4549, 47, 4, 'پس هرگاه [در میدان جنگ،] با کافران روبرو شدید، گردن‌هایشان را بزنید تا آن که آنان را از پا درآورید. [و چون به دست شما اسیر شوند،] پس آنان را سخت ببندید، [تا نگریزند.] سپس، یا بر آنان منّت نهید [و آزادشان کنید،] و یا با گرفتن فدیه، رهایشان کنید تا هنگامی که جنگ، بارهای سنگین خود را بر زمین نهد. این است [فرمان خداوند] و اگر خداوند می‌خواست، [با دیگر بلایا،] از آن کافران انتقام می‌گرفت؛ ولی [فرمان جنگ داد] تا شما را با یکدیگر بیازماید و کسانی که در راه خدا کشته شدند، [خداوند] هرگز اعمالشان را از بین نمی‌برد.'),
(4550, 47, 5, '[خداوند] به زودی آنان را [به مقامات والا] هدایت می‌کند و حال و کارشان را نیکو می‌گرداند.'),
(4551, 47, 6, 'و آنان را در بهشتی که به آنان شناسانده، وارد می‌سازد.'),
(4552, 47, 7, 'ای کسانی که ایمان آورده‌اید! اگر خدا را یاری کنید، شما را یاری می‌کند و گام‌هایتان را استوار می‌سازد.'),
(4553, 47, 8, 'و کسانی که کفر ورزیدند، بر آنان نگون‌ساری [و مرگ‌] باد! و [خدا] اعمالشان را نابود گردانید.'),
(4554, 47, 9, 'این [نابودی] برای آن است که آنان آنچه را خداوند فروفرستاده است، خوش نداشتند، پس [خدا] کارهایشان را تباه کرد.'),
(4555, 47, 10, 'پس آیا در زمین نگشتند، تا بنگرند سرانجام کسانی که پیش از آنان بودند، چگونه بود؟ خداوند آنان را نابود کرد و برای این کافران نیز همانند آن کیفرها خواهد بود.'),
(4556, 47, 11, 'این به خاطر آن است که خدا، سرپرست و یاور کسانی است که ایمان آورده‌اند و به راستی کافران سرپرست و یاوری ندارند.'),
(4557, 47, 12, 'خداوند کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، به باغ‌هایی [از بهشت] که نهرها از زیر [درختان] آن جاری است، وارد می‌کند و کسانی که کافر شدند، همواره سرگرم بهره‌گیری از [لذّت‌های زودگذر] دنیایند و می‌خورند، همان‌گونه که چهارپایان می‌خورند و [سرانجام،] آتش، جایگاه آنهاست.'),
(4558, 47, 13, 'و چه بسیار شهرها که [مردمانش] از شهری که تو را [از آن] بیرون کردند، نیرومندتر بودند. ما آنان را نابود کردیم و آنان هیچ یاوری نداشتند.'),
(4559, 47, 14, 'پس آیا کسی که از سوی پروردگارش دلیل روشنی دارد، همچون کسی است که بدی کردارش در نظرش آراسته شده و از هوس‌هایش پیروی کرده است؟'),
(4560, 47, 15, 'وصف بهشتی که به پرهیزکاران وعده داده شده، [چنین است:] در آن نهرهایی است از آب که [رنگ و بو و طعمش] برنمی‌گردد و نهرهایی از شیر که مزه‌ی آن دگرگونی ندارد و نهرهایی از شراب که برای نوشندگان لذّت‌بخش است و نهرهایی از عسل ناب و در آنجا از هرگونه میوه برای آنان [فراهم‌] است و [از همه بالاتر،] آمرزشی از سوی پروردگارشان. [آیا اینان] همانند کسانی هستند که در آتش، جاودانه‌اند و آبی جوشان به خوردشان داده می‌شود که دل و روده‌هایشان را پاره پاره می‌کند و اندرونشان را می‌سوزاند؟'),
(4561, 47, 16, 'و گروهی از آنان کسانی هستند که به [سخنان] تو گوش می‌سپارند، امّا همین که از نزد تو بیرون بروند، [از روی تمسخر] به دانش‌یافتگان می‌گویند: «او هم‌اکنون چه گفت؟ [ما که نفهمیدیم.]» آنان کسانی هستند که خداوند بر دل‌هایشان مهر نهاده و از هوای نفسشان پیروی کرده‌اند.'),
(4562, 47, 17, 'و کسانی که هدایت یافته‌اند، هدایتشان را می‌افزاید و روح پرهیزکاری به آنان عطا می‌کند.'),
(4563, 47, 18, 'پس آیا [کافران] چشم به راه قیامت هستند، که ناگهان به سراغشان آید؟ در حالی که نشانه‌های آن آمده است. پس آن‌گاه که [رستاخیز] به سراغشان آید، متذکر شدن چه سودی برایشان خواهد داشت؟!'),
(4564, 47, 19, 'پس بدان که هیچ معبودی جز اللّه نیست و برای گناه خود و مردان و زنان باایمان آمرزش بخواه و خدا، رفت و آمد و آرمیدن شما را می‌داند.'),
(4565, 47, 20, 'و کسانی که ایمان آورده‌اند، می‌گویند: «چرا سوره‌ای [برای جهاد] نازل نمی‌شود؟» پس هنگامی که سوره‌ای محکم و روشن نازل می‌گردد که در آن نامی از جنگ رفته است، بیماردلان را می‌بینی که به تو نگاه می‌کنند، مانند نگاه کردن کسی که از [ترس] مرگ بی‌هوش شده است. پس همان مرگ برایشان سزاوارتر است.'),
(4566, 47, 21, 'فرمانبرداری و سخن سنجیده، [برای آنان شایسته است.] پس هنگامی که امر [کارزار] قطعی شد، اگر به خدا راست بگویند، [و صادقانه جهاد کنند،] قطعاً برایشان بهتر است.'),
(4567, 47, 22, 'پس [ای بیماردلانِ سست ایمان!] اگر روی گردانید، جز این از شما انتظار می‌رود که در زمین فساد کنید و پیوندهای خویشاوندی را بگسلید؟'),
(4568, 47, 23, 'آنانند کسانی که خداوند از رحمت خویش دور و [گوششان را] کر و چشم‌هایشان را کور ساخته است. [نه حقّی می‌شنوند و نه حقّی می‌بینند.]'),
(4569, 47, 24, 'آیا در قرآن تدبّر و تأمّل نمی‌کنند؟ یا بر دل‌هایشان قفل‌هایی خورده است؟'),
(4570, 47, 25, 'کسانی که پس از آن که راه هدایت برایشان روشن شد، به آن پشت کردند، شیطان [رفتار بدشان را] برایشان آراسته و آنان را با آرزوهای دراز فریفته است.'),
(4571, 47, 26, 'این، به خاطر آن است که [بیماردلان] به کسانی که از دستورات الهی ناخشنود بودند، گفتند: «ما در بعضی امور، از شما اطاعت خواهیم کرد.» در حالی که خداوند پنهان‌کاری آنان را می‌داند.'),
(4572, 47, 27, 'پس [حال آنان] چگونه خواهد بود، آن‌گاه که فرشتگان جانشان را بازگیرند، در حالی که به صورت و پشت آنان می‌کوبند.'),
(4573, 47, 28, 'این [نوع جان دادن] برای آن است که آنان از آنچه خداوند را به خشم می‌آورد، پیروی کردند و خشنودی خدا را خوش نداشتند، پس خداوند نیز اعمالشان را تباه کرد.'),
(4574, 47, 29, 'آیا کسانی که در دل‌هایشان بیماری [نفاق و کفر] است، پنداشته‌اند که خداوند هیچ وقت کینه‌ها و حسادتشان را آشکار نخواهد کرد؟'),
(4575, 47, 30, 'و اگر بخواهیم، آنان را به تو می‌نمایانیم، آن‌گاه ایشان را به سیمایشان می‌شناسی و در آهنگ کلامشان آنان را خواهی شناخت و خداوند کارهایتان را می‌داند.'),
(4576, 47, 31, 'ما قطعاً شما را می‌آزماییم، تا از میان شما، مجاهدان و صابران را نشان دهیم و اخبار [و اعمالتان] را آزمایش خواهیم کرد.'),
(4577, 47, 32, 'کسانی که کفر ورزیدند و [مردم را] از راه خدا بازداشتند و پس از آن که راه هدایت برایشان روشن شد، با پیامبر مخالفت کردند، هرگز به خداوند گزندی نمی‌رسانند و به زودی خداوند اعمال شما را تباه می‌سازد.'),
(4578, 47, 33, 'ای کسانی که ایمان آورده‌اید! خدا را اطاعت کنید و از پیامبر پیروی نمایید و [با نافرمانی] کارهایتان را باطل مسازید.'),
(4579, 47, 34, 'کسانی که کافر شدند و [مردم را] از راه خدا بازداشتند، سپس در حال کفر از دنیا رفتند، خداوند هرگز آنان را نخواهد بخشید.'),
(4580, 47, 35, 'پس سستی نورزید تا [دشمنان را] به صلح و سازش دعوت کنید، با این که شما برترید و خداوند با شماست و هرگز چیزی از [پاداش] اعمالتان را کم نمی‌کند.'),
(4581, 47, 36, 'زندگانی دنیا، تنها بازیچه و سرگرمی است و اگر ایمان بیاورید و تقوا پیشه کنید، خداوند به شما پاداش می‌دهد و همه‌ی اموالتان را از شما نمی‌خواهد.'),
(4582, 47, 37, 'اگر خداوند اموالتان را بخواهد و بر آن اصرار ورزد، بخل می‌ورزید و [خداوند] کینه‌های شما را آشکار می‌سازد.'),
(4583, 47, 38, 'آگاه باشید! شمایید که برای انفاق در راه خدا دعوت می‌شوید، پس برخی از شما بخل می‌ورزند و هر کس بخل پیشه کند، نسبت به [نیکبختی‌] خود بخل کرده است و خداوند غنی و بی‌نیاز است و شمایید نیازمندان! و اگر روی بگردانید، قومی جز شما را جایگزین می‌کند که همچون شما نباشند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'به راستی ما پیروزی آشکاری را برای تو پیش آوردیم.'),
(4585, 48, 2, 'تا خداوند پیامدهای ناگوار [دعوتت] را در گذشته و حال بر تو بپوشاند و نعمتش را بر تو تمام کند و تو را به راهی راست هدایت نماید.'),
(4586, 48, 3, 'و خداوند تو را با پیروزیِ شکست‌ناپذیری یاری نماید.'),
(4587, 48, 4, 'اوست که آرامش را بر دل‌های مؤمنان فروفرستاد، تا ایمانی بر ایمانشان بیافزایند و لشکریان آسمان‌ها و زمین برای خداست و خداوند، دانایی فرزانه است.'),
(4588, 48, 5, '[آن آرامش‌بخشی همچنین برای این بود] تا خداوند مردان و زنان باایمان را به باغ‌هایی [بهشتی] که نهرهایی از زیر [درختان] آن جاری است، درآورد، در حالی که جاودانه در آن باشند و گناهانشان را می‌پوشاند و این نزد خداوند، رستگاری بزرگی است.'),
(4589, 48, 6, 'و مردان و زنان منافق و مردان و زنان مشرک را که به خداوند گمان بد دارند، [که پیامبرش را یاری نخواهد کرد،] عذاب کند. پیشامدِ بدِ زمانه بر آنان باد! خداوند بر آنان غضب کرده و از رحمت خود دورشان ساخته و برایشان جهنّم را آماده کرده است و بد سرانجامی است!'),
(4590, 48, 7, 'و لشکریان آسمان‌ها و زمین برای خداوند است و خدا شکست‌ناپذیر حکیم است.'),
(4591, 48, 8, 'ما تو را گواه [بر اعمال امّت] و بشارت‌دهنده و بیم‌دهنده فرستادیم.'),
(4592, 48, 9, 'تا [شما ای مردم!] به خدا و رسولش ایمان آورید و او را یاری کنید و بزرگش بدارید و خداوند را بامداد و شامگاه، تسبیح گویید.'),
(4593, 48, 10, 'کسانی که با تو بیعت می‌کنند، در واقع با خدا بیعت می‌کنند. دست خداست که بالای دست آنهاست. پس هر کس پیمان بشکند، به زیان خود پیمان می‌شکند و هر کس به آنچه با خداوند عهد بسته است، وفا کند، خداوند به زودی پاداش بزرگی به او خواهد داد.'),
(4594, 48, 11, 'به زودی بادیه‌نشینان بازمانده از جنگ و جهادگریزان، به تو خواهند گفت: «اموال و خانواده‌هایمان، ما را مشغول و سرگرم ساخت، پس برای ما [از خداوند] آمرزش بخواه!» آنان به زبان چیزی می‌گویند که در دل‌هایشان نیست. [به آنان] بگو: «اگر خداوند برای شما زیانی اراده کند، یا برای شما سودی بخواهد، کیست که برای شما در برابر او اختیار چیزی را داشته باشد؟ خداوند همواره به آنچه می‌کنید، آگاه است.»'),
(4595, 48, 12, 'بلکه [گریز شما به خاطر آن بود که] گمان کردید پیامبر و مؤمنان هرگز نزد خانواده‌های خود بازنخواهند گشت و این [پندار] در دل‌های شما آراسته شد و گمان بد کردید و [این‌گونه] مردمی بدبخت شدید.'),
(4596, 48, 13, 'و هر کس به خدا و پیامبرش ایمان نیاورد، پس [بداند که] ما برای کافران آتشی شعله‌ور آماده کرده‌ایم.'),
(4597, 48, 14, 'و فرمانروایی آسمان‌ها و زمین برای خداوند است. هر که را بخواهد، [و شایسته بداند،] می‌بخشد و هر که را بخواهد، [و سزاوار بداند،] عذاب می‌کند و خداوند همواره آمرزنده و مهربان است.'),
(4598, 48, 15, 'آن‌گاه که شما برای به دست آوردن غنیمت‌ها روانه [خیبر] می‌شوید، جهادگریزان [حدیبیه] می‌گویند: «بگذارید ما هم به دنبال شما بیاییم.» می‌خواهند کلام خدا را تغییر دهند. [که متخلّفان حدیبیه را از غنایم بعدی محروم کرده است. به آنان] بگو: «شما هرگز دنبال ما نخواهید آمد! خداوند از پیش چنین فرموده است.» پس آنان به زودی می‌گویند: «[خدا ما را بازنداشته، بلکه] شما نسبت به ما حسد می‌ورزید.» [چنین نیست!] بلکه آنان جز اندکی نمی‌فهمند.'),
(4599, 48, 16, 'به جهادگریزان بادیه‌نشین بگو: «به زودی به [رویارویی با] گروهی سرسخت دعوت خواهید شد که با آنان بجنگید تا تسلیم شوند. پس اگر اطاعت کنید، خداوند پاداشی نیک به شما خواهد داد و اگر سرپیچی کنید، همان‌گونه که پیش از این نافرمانی کردید، شما را به عذابی دردناک کیفر خواهد کرد.»'),
(4600, 48, 17, 'بر نابینا و لنگ و بیمار گناهی نیست [که به جهاد نروند] و هر کس خدا و پیامبرش را پیروی کند، [خداوند] او را در باغ‌هایی [بهشتی] درآورد که نهرها از زیر [درختان] آن جاری است و هر کس سرپیچی کند، او را به عذابی دردناک، کیفر خواهد کرد.'),
(4601, 48, 18, 'بی‌تردید خداوند از مؤمنان خشنود شد، آن‌گاه که [در حُدَیبِیه] زیر آن درخت با تو بیعت می‌کردند و خدا آنچه را در دل‌هایشان [از ایمان و صداقت] بود، می‌دانست، پس آرامش را بر آنان فروفرستاد و پیروزی نزدیکی را [در خیبر] پاداش آنان قرار داد.'),
(4602, 48, 19, 'و [نیز] غنیمت‌های بسیاری که به چنگ خواهند آورد و خداوند همواره شکست‌ناپذیر و حکیم است.'),
(4603, 48, 20, 'خداوند، غنیمت‌های بسیاری به شما وعده داده است که آنها را خواهید گرفت. پس این [غنیمت‌های خیبر] را زودتر برای شما فراهم ساخت و دستان [تجاوزگر] مردم را از شما کوتاه کرد، تا برای مؤمنان نشانه [و عبرتی] باشد و شما را به راهی راست هدایت کند.'),
(4604, 48, 21, 'و [غنیمت‌های] دیگری که شما بر آنها قدرت ندارید؛ ولی خداوند بر آنها احاطه دارد و خداوند بر هر چیزی تواناست.'),
(4605, 48, 22, 'و اگر کافران با شما بجنگند، حتماً پشت کرده، می‌گریزند. آن‌گاه هیچ سرپرست و یاوری نخواهند یافت.'),
(4606, 48, 23, '[این پیروزی مؤمنان و شکست کافران] سنّت خداوند است که از پیش جاری بوده است و هرگز برای سنّت‌های خداوند، تغییر و تبدیلی نخواهی یافت.'),
(4607, 48, 24, 'و اوست کسی که پس از آن که شما را [در حدیبیه] بر آنان پیروز ساخت، در درون مکه [نیز] دست آنان را از شما و دست شما را از آنان کوتاه کرد و خداوند به هر کاری که می‌کنید، بیناست.'),
(4608, 48, 25, 'آنان کسانی هستند که کافر شدند و شما را از [ورود به] مسجدالحرام بازداشتند و نگذاشتند قربانی‌هایی که با خود آورده بودید، به قربانگاه برسد و اگر [در مکه] مردان و زنان باایمانی نبودند که بی‌آن که آنان را بشناسید، ندانسته پایمالشان کنید و تاوانشان بر شما بماند، [فرمان حمله به مکه را صادر می‌کردیم؛ ولی این فرمان را ندادیم] تا خداوند هر کس را بخواهد، در رحمت خود درآورد. اگر [مؤمنان و مشرکان] از هم جدا بودند، قطعاً کسانی از اهل مکه را که کفر ورزیدند، به عذابی دردناک کیفر می‌کردیم.'),
(4609, 48, 26, '[یاد کن] آن‌گاه که کافران، در دل‌های خود تعصّب و خشم جای داده بودند، [آن هم] تعصّب شدید جاهلی. پس خداوند آرامش خود را بر پیامبرش و بر مؤمنان فروفرستاد. [و آنان برای صلح حدیبِیه آماده شدند.] و خداوند آنان را به حقیقت تقوا پای‌بند کرد که به آن سزاوارتر و اهل آن بودند و خداوند همواره به همه چیز آگاه است.'),
(4610, 48, 27, 'به راستی خداوند، رؤیای پیامبرش را به حقّ تحقّق بخشید. شما مسلمانان به خواست خدا، آسوده‌خاطر، در حالی که سرهایتان را تراشیده و مو [یا ناخن] کوتاه کرده‌اید و از هیچ‌کس ترس و وحشتی ندارید، وارد مسجدالحرام خواهید شد. خداوند چیزی را دانست که شما نمی‌دانستید، پس غیر از این، پیروزی نزدیکی را [در خیبر] برای شما قرار داد.'),
(4611, 48, 28, 'او کسی است که پیامبرش را همراه با هدایت و دین حقّ فرستاد، تا آن را بر همه دین‌ها پیروز کند و کافی است که خداوند گواه [این] باشد.'),
(4612, 48, 29, 'محمّد، فرستاده‌ی خداست و کسانی که با او هستند، بر کافران، سختگیر و با یکدیگر مهربانند. آنان را پیوسته در رکوع و سجود می‌بینی که بخشش و خشنودی را از خدا می‌جویند. نشانه‌ی آنان در رخسارشان از اثر سجود پیداست. این است وصف ایشان در تورات، و وصف آنان در انجیل این است که وجودشان مانند زراعتی است که جوانه‌اش را از زمین رویانده، پس آن را نیرو داده تا سخت شود و بر ساقه‌ی خود [استوار] بایستد، آن گونه که کشاورزان را به شگفتی و شادی وامی‌دارد. تا خدا به واسطه‌ی [انبوهی و نیرومندی] مؤمنان، کافران را به خشم آورد. [ولی] خداوند به کسانی از کافران که ایمان آورده و کارهای شایسته کنند، وعده‌ی آمرزش و پاداشی بزرگ داده است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ای کسانی که ایمان آورده‌اید! [در هیچ کاری] بر خدا و پیامبر او پیشی نگیرید و از [نافرمانی] خداوند پروا کنید که خداوند شنوا و داناست.'),
(4614, 49, 2, 'ای کسانی که ایمان آورده‌اید! [در گفتگو با پیامبر،] صدایتان را بلندتر از صدای پیامبر نکنید و آن گونه که با یکدیگر بلند گفتگو می‌کنید، با پیامبر بلند سخن مگویید! مبادا [به خاطر این بی‌ادبی،] اعمالتان نابود شود و شما آگاه نشوید.'),
(4615, 49, 3, 'کسانی که نزد رسول خدا صدایشان را پایین می‌آورند، آنان کسانی هستند که خداوند دل‌هایشان را برای [پذیرش] تقوا آزمایش [و آماده] کرده است و آمرزش و پاداشی بزرگ خواهند داشت.'),
(4616, 49, 4, 'کسانی که از پشت اتاق‌ها[ی خانه‌ات] تو را با فریاد صدا می‌زنند، بیشترشان نمی‌اندیشند. [که باید احترام تو را نگاه دارند.]'),
(4617, 49, 5, 'و اگر صبر می‌کردند تا تو خود به نزد آنان بیرون آیی، قطعاً برایشان بهتر بود و خداوند آمرزنده‌ی مهربان است.'),
(4618, 49, 6, 'ای کسانی که ایمان آورده‌اید! اگر فاسقی برای شما خبری مهمّ آورد، درباره‌ی آن بررسی کنید، مبادا [از روی شتاب‌زدگی تصمیمی بگیرید و] ناآگاهانه به گروهی آسیب برسانید، سپس از کرده‌ی خود پشیمان شوید.'),
(4619, 49, 7, 'و بدانید که رسول خدا در میان شماست [و شما باید از او پیروی کنید.] اگر او در بسیاری از کارها پیرو شما باشد، قطعاً به سختی و مشقّت خواهید افتاد؛ ولی خدا ایمان را محبوب شما قرار داده و آن را در دل‌هایتان آراسته است و کفر، بدکاری و نافرمانی را مورد تنفّر شما قرار داده است. آنها [که دارای این ویژگی‌ها هستند،] همان رشدیافتگانند.'),
(4620, 49, 8, '[علاقه به ایمان و تنفّر از کفر،] فضل و نعمتی بزرگ از سوی خداوند است و خداوند دانایی فرزانه است.'),
(4621, 49, 9, 'و اگر دو گروه از مؤمنان به نزاع و جنگ پرداختند، پس میان آنان صلح و آشتی برقرار کنید. سپس اگر یکی از دو گروه بر دیگری تجاوز کرد، با متجاوز بجنگید تا به فرمان خداوند بازگردد. پس اگر متجاوز بازگشت، میان آن دو گروه به عدالت صلح برقرار کنید و عدالت ورزید که خداوند دادگران را دوست دارد.'),
(4622, 49, 10, 'همانا مؤمنان با یکدیگر برادرند، پس میان برادران خود، صلح و آشتی برقرار کنید و از [نافرمانی] خدا پروا کنید تا مورد رحمت قرار گیرید.'),
(4623, 49, 11, 'ای کسانی که ایمان آورده‌اید! مبادا گروهی [از شما]، گروهی دیگر را مسخره کنند. چه بسا که مسخره‌شدگان بهتر از مسخره‌کنندگان باشند و زنان نیز، زنان دیگر را مسخره نکنند، شاید که آنان بهتر از اینان باشند و از یکدیگر عیب‌جویی نکنید و یکدیگر را با لقب‌های بد و ناپسند نخوانید. [زیرا] این که انسانی را پس از ایمان آوردنش به لقب زشت نشان کنند، بد رسمی است و کسانی که [از این اعمال] توبه نکنند، پس آنان همان ستمگرانند.'),
(4624, 49, 12, 'ای کسانی که ایمان آورده‌اید! از بسیاری گمان‌ها دوری کنید، زیرا برخی گمان‌ها گناه است و [در کار دیگران] تجسّس و کنجکاوی نکنید و از یکدیگر غیبت نکنید. آیا هیچ یک از شما دوست دارد که گوشت برادر مرده‌ی خود را بخورد؟ [هرگز!] بلکه آن را ناپسند می‌دانید و از [نافرمانی] خدا پروا کنید. همانا خداوند بسیار توبه‌پذیر و مهربان است.'),
(4625, 49, 13, 'ای مردم! ما شما را از یک مرد و زن آفریدیم و شما را تیره‌ها و قبیله‌ها قرار دادیم، تا یکدیگر را بشناسید. همانا گرامی‌ترینِ شما نزد خدا، باتقواترینِ شماست. خداوند دانای آگاه است.'),
(4626, 49, 14, 'آن اعراب بادیه‌نشین گفتند: «ما ایمان آورده‌ایم.» به آنان بگو: شما هنوز ایمان نیاورده‌اید، بلکه بگویید: اسلام آورده‌ایم، زیرا هنوز ایمان در دل‌های شما وارد نشده است و اگر خدا و رسولش را اطاعت کنید، خداوند ذرّه‌ای از اعمال شما کم نمی‌کند. خداوند آمرزنده‌ی مهربان است.»'),
(4627, 49, 15, 'مؤمنان، تنها کسانی هستند که به خدا و فرستاده‌اش ایمان آوردند، سپس دچار تردید نشدند و با اموال و جان‌های خود در راه خدا جهاد کردند. اینانند که [در ادّعای ایمان] راستگویند.'),
(4628, 49, 16, 'بگو: «آیا شما خدا را از ایمان خود آگاه می‌کنید؟ در حالی که خداوند آنچه را در آسمان‌ها و آنچه را در زمین است، می‌داند و خداوند به هر چیزی داناست.»'),
(4629, 49, 17, 'آنان بر تو منّت می‌گذارند که اسلام آورده‌اند. بگو: «اگر راست می‌گویید، برای اسلام آوردنتان بر من منّت نگذارید؛ بلکه این خداوند است که بر شما منّت دارد که شما را به سوی ایمان هدایت کرده است،'),
(4630, 49, 18, 'خداوند، نهان آسمان‌ها و زمین را می‌داند و خداوند به آنچه می‌کنید، بیناست.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف. سوگند به قرآن باشکوه [که پیامبری تو و برپایی قیامت حقّ است].'),
(4632, 50, 2, '[آنان ایمان نیاوردند،] بلکه از آمدن پیامبری هشداردهنده از میان خودشان در شگفت شدند. پس کافران گفتند: «این [سخنان که از قیامت خبر می‌دهد،] چیز عجیبی است!'),
(4633, 50, 3, 'آیا هنگامی که مردیم و خاک شدیم، [دوباره زنده می‌شویم؟] این بازگشتی دور [از باور] است.»'),
(4634, 50, 4, 'بی‌شک هر چه را زمین از [اجساد] آنان می‌کاهد، می‌دانیم و نزد ما کتابی است که [همه چیز را در خود] نگاه می‌دارد.'),
(4635, 50, 5, 'بلکه سخن حقّ را چون به سراغشان آمد، تکذیب کردند، پس در کاری سردرگم و آشفته‌اند.'),
(4636, 50, 6, 'پس آیا به آسمانِ بالای سرشان نگاه نکرده‌اند که چگونه آن را ساختیم و آراستیم و آن را هیچ شکاف و خللی نیست؟'),
(4637, 50, 7, 'و زمین را گستردیم و کوه‌های استوار در آن افکندیم و از هر نوع گیاه باطراوت در آن رویاندیم.'),
(4638, 50, 8, 'تا برای هر بنده‌ای که [به سوی حقّ] روی می‌آورد، مایه‌ی بینش و پند باشد.'),
(4639, 50, 9, 'و از آسمان، آبی پربرکت فروفرستادیم، پس با آن باغ‌ها و دانه‌های دروشدنی رویاندیم.'),
(4640, 50, 10, 'و نیز درختان بلند خرما، با خوشه‌هایی پر و بر هم چیده [رویاندیم].'),
(4641, 50, 11, 'تا روزی بندگان باشد و با آن سرزمین مرده‌ای را زنده کردیم. رستاخیز [نیز] این‌گونه است.'),
(4642, 50, 12, '[ای پیامبر!] پیش از اینان، قوم نوح و اصحاب رَسّ و قوم ثمود، [نیز پیامبرانشان را] تکذیب کردند.'),
(4643, 50, 13, 'و [نیز قوم] عاد و فرعون و برادران لوط.'),
(4644, 50, 14, 'و اصحاب اَیکه و قوم تُبَّع، همگی پیامبران را تکذیب کردند، پس وعده‌ی عذاب من، بر آنان قطعی شد.'),
(4645, 50, 15, 'مگر در آفرینش نخستین درماندیم [که از بازآفریدن در رستاخیز ناتوان باشیم؟ نه!] بلکه آنان درباره‌ی آفرینش جدید در تردیدند.'),
(4646, 50, 16, 'و همانا ما انسان را آفریده‌ایم و هر چه را که نفسش به او وسوسه می‌کند، می‌دانیم و ما از رگ گردن به او نزدیک‌تریم.'),
(4647, 50, 17, 'آن‌گاه که دو فرشته از راست و چپ، [در کمین] نشسته، [اعمال نیک و بد را] دریافت می‌کنند.'),
(4648, 50, 18, '[انسان] هیچ سخنی بر زبان نمی‌آورد، مگر آن که در کنارش [فرشته‌ی] نگهبانی حاضر و آماده [ثبت] است.'),
(4649, 50, 19, 'و بی‌هوشی مرگ به راستی فرامی‌رسد [و به انسان می‌گویند:] «این همان است که همواره از آن می‌گریختی.»'),
(4650, 50, 20, 'و در صور [رستاخیز] دمیده شود: «این است روز تحقّق وعده‌ی عذاب!»'),
(4651, 50, 21, 'و هر کس [به صحنه‌ی قیامت] می‌آید، سوق‌دهنده‌ای و شاهدی با اوست.'),
(4652, 50, 22, '[خدا به او می‌فرماید:] «همانا از این [روز] در غفلتی [عمیق] بودی، پس پرده‌ی [غفلت] تو را کنار زدیم و امروز چشمت تیزبین شده است.»'),
(4653, 50, 23, 'و [فرشته‌ی] همراه او می‌گوید: «این [نامه‌ی اعمال اوست که] نزد من آماده است.»'),
(4654, 50, 24, '[خداوند می‌فرماید:] «هر کفرپیشه‌ی لجوجی را به دوزخ افکنید!'),
(4655, 50, 25, '[آن که] سخت مانع خیر است و متجاوز و شبهه‌‌افکن.'),
(4656, 50, 26, 'آن که با خداوند یکتا، معبود دیگری قرار داد. پس او را در عذاب سخت بیافکنید!»'),
(4657, 50, 27, 'هم‌نشین او می‌گوید: «پروردگارا! من او را به سرکشی وادار نکردم، بلکه او خودش در گمراهی عمیق و دوری بود.»'),
(4658, 50, 28, '[خداوند می‌فرماید:] «نزد من با یکدیگر مشاجره نکنید! من پیش از این وعده‌ی عذاب را به شما داده بودم.'),
(4659, 50, 29, 'فرمان [افکندن کافر در دوزخ،] نزد من تغییر نمی‌یابد و من هرگز به بندگانم ستم نکنم.»'),
(4660, 50, 30, '[یاد کن] روزی که به دوزخ می‌گوییم: «آیا پر شدی؟» و او می‌گوید: «آیا بیش از این هم هست؟»'),
(4661, 50, 31, 'و بهشت را برای تقواپیشگان نزدیک می‌آورند و فاصله‌ای از آنان ندارد.'),
(4662, 50, 32, '[به آنان می‌گویند:] این است آنچه به شما وعده داده می‌شد. [این پاداش] برای هر کسی است که [در دنیا به سوی خدا] بازگشته [و] حافظ و نگهبان [حدود الهی] بوده است.'),
(4663, 50, 33, 'آن که در نهان از [خدای] رحمان ترسید و با دلی بازگشت‌کننده [به سوی خدا] آمد.'),
(4664, 50, 34, 'به سلامت وارد بهشت شوید! این روزِ جاودانگی است.»'),
(4665, 50, 35, 'برای اهل بهشت در آنجا هر چه بخواهند آماده است و نزد ما [نعمت‌های] افزون‌تری هست.'),
(4666, 50, 36, 'و پیش از اینان چه بسیار نسل‌هایی را نابود کردیم که نیرومندتر از ایشان بودند و [با قدرت خود و کشورگشایی،] به شهرها نفوذ کردند. آیا [برای آنان] راه گریزی بود؟'),
(4667, 50, 37, 'در این [نابودی سرکشان]، پندی است برای کسی که دلی [بیدار] دارد، یا با حضور قلب، [به حقایق] گوش فرادهد.'),
(4668, 50, 38, 'و به راستی ما آسمان‌ها و زمین و آنچه را میان آنهاست، در شش روز [و دوران] آفریدیم و هیچ رنج و خستگی به ما نرسید.'),
(4669, 50, 39, 'پس بر آنچه [مخالفان] می‌گویند، شکیبا باش! و پیش از طلوع خورشید و پیش از غروب، پروردگارت را با سپاس و ستایش، تسبیح گوی!'),
(4670, 50, 40, 'و پاره‌ای از شب را به تسبیح او بپرداز و پس از سجده‌ها [نیز خدا را تسبیح گوی].'),
(4671, 50, 41, 'و گوش فرا ده و منتظر روزی باش که منادی از مکانی نزدیک ندا می‌دهد.'),
(4672, 50, 42, 'روزی که آن صیحه‌ی راستین را [از صور اسرافیل] بشنوند. آن روز، روز رستاخیز است.'),
(4673, 50, 43, 'ماییم که زنده می‌کنیم و می‌میرانیم و بازگشت [همه] به سوی ماست.'),
(4674, 50, 44, 'روزی که زمین به سرعت از روی [اجساد] آنان شکافته شود. [و از گورها بیرون آیند.] این حشر [و گردآوری همه انسان‌ها] بر ما آسان است.'),
(4675, 50, 45, 'ما به آنچه [مخالفان] می‌گویند، آگاه‌تریم و تو بر آنان چیره نیستی، [تا آنان را با زور به راه راست درآوری.] پس هر که را از وعده‌ی عذاب من می‌ترسد، با قرآن پند بده!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهایی که با شدّت چیزها را پراکنده می‌کنند،'),
(4677, 51, 2, 'پس به ابرهای گران‌بار سوگند،'),
(4678, 51, 3, 'پس به کشتی‌هایی که به آسانی در حرکتند، سوگند،'),
(4679, 51, 4, 'پس به فرشتگانی که [به امر خدا] کارها را تقسیم می‌کنند، سوگند،'),
(4680, 51, 5, 'آنچه به شما وعده می‌دهند، قطعاً راست است.'),
(4681, 51, 6, 'و حتماً [روز] جزا واقع‌شدنی است.'),
(4682, 51, 7, 'سوگند به آسمانِ دارای راه‌های بسیار،'),
(4683, 51, 8, 'که شما [درباره‌ی قرآن‌] در گفتاری گوناگون [سرگردان] هستید.'),
(4684, 51, 9, 'هر که [از حقّ] منصرف گشت، به انحراف کشیده خواهد شد.'),
(4685, 51, 10, 'مرگ بر دروغ‌پردازان!'),
(4686, 51, 11, 'آنان که در بی‌خبری و غفلت غرقند.'),
(4687, 51, 12, 'می‌پرسند: «روز جزا چه وقت است؟»'),
(4688, 51, 13, 'روزی است که آنها بر آتش سوزانده می‌شوند.'),
(4689, 51, 14, '[به آنان گفته می‌شود:] «عذابتان را بچشید! این همان عذابی است که [از روی تمسخر] به آن شتاب داشتید.»'),
(4690, 51, 15, 'پرهیزکاران در باغ‌ها و [کنار] چشمه‌سارانند.'),
(4691, 51, 16, 'و آنچه را پروردگارشان به آنان عطا می‌کند، می‌گیرند. زیرا که آنان پیش از این همواره نیکوکار بودند.'),
(4692, 51, 17, 'اندکی از شب را می‌خوابیدند.'),
(4693, 51, 18, 'و سحرگاهان، [از خدا] درخواست آمرزش می‌کردند.'),
(4694, 51, 19, 'و در دارایی‌هایشان، حقّی بود برای نیازمند خواهنده و بینوای درمانده.'),
(4695, 51, 20, 'و در زمین برای اهل یقین، نشانه‌هایی است،'),
(4696, 51, 21, 'و در [آفرینش] خودتان [نیز نشانه‌هایی است]. پس چرا نمی‌نگرید؟'),
(4697, 51, 22, 'و روزی شما و آنچه وعده داده می‌شوید، در آسمان و عالم بالاست.'),
(4698, 51, 23, 'پس به پروردگار آسمان و زمین سوگند که این [سخن] همان گونه که شما سخن می‌گویید، راستین و واقعی است.'),
(4699, 51, 24, '[ای پیامبر!] آیا داستان میهمانان گرامی ابراهیم به تو رسیده است؟'),
(4700, 51, 25, 'آن‌گاه که بر او وارد شدند، و گفتند: «سلام [بر تو]!» [ابراهیم در پاسخ] گفت: «سلام [بر شما که] گروهی ناشناسید.»'),
(4701, 51, 26, 'پس پنهانی به سراغ خانواده‌ی خود رفت و گوساله‌ای فربه را [بریان کرد و] آورد.'),
(4702, 51, 27, 'پس غذا را نزد میهمانان گذاشت، [ولی با تعجّب دید دست به سوی غذا نمی‌برند،] گفت: «چرا نمی‌خورید؟!»'),
(4703, 51, 28, 'پس از [غذا نخوردن] میهمانان، در دلش احساس ترس کرد. آنان گفتند: «نترس! [ما فرشتگان خداییم!]» و او را به نوجوانی دانا مژده دادند.'),
(4704, 51, 29, 'پس همسر ابراهیم [با شنیدن مژده‌ی فرزند] فریادکنان پیش آمد و به صورت خود زد و گفت: «پیرزنی نازا [چگونه صاحب فرزند شود]؟!»'),
(4705, 51, 30, 'گفتند: «پروردگار تو این‌گونه گفته و البتّه او حکیم و داناست.»'),
(4706, 51, 31, '[ابراهیم] گفت: «پس ای فرستادگان! کاری که به دنبال آن آمده‌اید، چیست؟»'),
(4707, 51, 32, 'گفتند: «ما به سوی قومی گناهکار فرستاده شده‌ایم،'),
(4708, 51, 33, 'تا [برای نابودیشان،] سنگ‌هایی از گل بر آنان فروریزیم.'),
(4709, 51, 34, '[سنگ‌هایی که] نزد پروردگارت برای [نابودی] اسراف‌کاران، نشان‌دار شده‌اند.»'),
(4710, 51, 35, 'پس هر که از مؤمنان در آنجا بود، بیرون بردیم.'),
(4711, 51, 36, 'ولی در آنجا جز یک خانواده‌ی فرمان‌بر [که لوط و فرزندانش بودند،] نیافتیم!'),
(4712, 51, 37, 'و در آن [دیار] برای کسانی که از عذاب دردناک می‌ترسند، نشانه‌ای [عبرت‌آموز] به جای گذاشتیم.'),
(4713, 51, 38, 'و در [سرگذشت] موسی [نیز نشانه و عبرتی است]، آن‌گاه که او را با دلیلی روشن و آشکار به سوی فرعون فرستادیم.'),
(4714, 51, 39, 'پس فرعون با تکیه بر قدرت و سپاهی که داشت، روی برگرداند و گفت: «او یا جادوگر است یا دیوانه.»'),
(4715, 51, 40, 'پس ما او و سپاهیانش را [به قهر] گرفتیم و آنان را در دریا افکندیم، در حالی که او [به سبب لجاجت و دشمنی‌] سزاوار سرزنش بود.'),
(4716, 51, 41, 'و در [سرگذشت] قوم عاد [نیز نشانه و عبرتی است]، آن‌گاه که بر آنان باد نازا و هلاک کنده فرستادیم.'),
(4717, 51, 42, '[این باد] هیچ چیزی را که بر آن می‌وزید، باقی نمی‌گذاشت، مگر آن که آن را خرد و تباه می‌کرد.'),
(4718, 51, 43, 'و در [سرگذشت] قوم ثَمود [نیز نشانه‌های عبرتی است]، آن‌گاه که به آنان گفته شد: «تا زمانی کوتاه، [که سه روز بیشتر نیست، از زندگی] بهره ببرید.»'),
(4719, 51, 44, 'آنان از فرمان پروردگارشان سر باز زدند. پس در حالی که می‌نگریستند، صاعقه آنان را فراگرفت.'),
(4720, 51, 45, 'پس، نه توان برخاستن داشتند و نه توانستند از خود دفاع کنند.'),
(4721, 51, 46, 'و پیش از آنان قوم نوح [را نابود کردیم، زیرا] مردمانی نافرمان و بدکار بودند.'),
(4722, 51, 47, 'و آسمان را با قدرتی وصف‌ناپذیر ساختیم و ما همواره آن را وسعت می‌بخشیم.'),
(4723, 51, 48, 'و زمین را گستردیم. پس چه نیکو گستراننده‌ای هستیم!'),
(4724, 51, 49, 'و از هر چیز دو زوج آفریدیم، باشد که یاد کنید [و پند گیرید].'),
(4725, 51, 50, '[ای پیامبر! بگو:] «پس به سوی خدا بگریزید، که من از سوی او برای شما هشداردهنده‌ای آشکارم.'),
(4726, 51, 51, 'و با خداوند، معبود دیگری قرار مدهید که من از سوی او برای شما هشداردهنده‌ای آشکارم.»'),
(4727, 51, 52, '[ای پیامبر!] بدین گونه [که تو را تکذیب کردند،] پیشینیان اینان نیز هر پیامبری برایشان آمد، گفتند: «[او] جادوگر یا دیوانه است.»'),
(4728, 51, 53, 'آیا [کافران در طول تاریخ،] یکدیگر را به این سفارش کرده‌اند [که با پیامبران چنین رفتار کنند]؟ [نه!] بلکه آنان مردمی طغیانگرند.'),
(4729, 51, 54, 'پس، از آنان روی بگردان که تو مورد سرزنش نیستی.'),
(4730, 51, 55, 'و [به مردم] یادآوری کن، که یادآوری برای مؤمنان سودمند است.'),
(4731, 51, 56, 'و جنّ و انسان را نیافریدم مگر برای آن که مرا بپرستند.'),
(4732, 51, 57, 'و من از آنان هیچ رزقی نمی‌خواهم و نمی‌خواهم که به من خوراک بدهند.'),
(4733, 51, 58, 'زیرا خداوند است آن روزی‌رسانِ نیرومندِ استوار.'),
(4734, 51, 59, 'پس کسانی که ستم کردند، سهم بزرگی [از عذاب] دارند، همانند سهم یارانشان [در اقوام ستمگر پیشین]. پس از من درخواست شتاب نکنند [که به آن خواهند رسید].'),
(4735, 51, 60, 'پس وای بر کسانی که کفر ورزیدند، از روزی که وعده داده می‌شوند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به [کوه] طور!'),
(4737, 52, 2, 'و سوگند به کتابی نوشته شده،'),
(4738, 52, 3, 'در صفحه‌ای گشوده!'),
(4739, 52, 4, 'و سوگند به آن خانه‌ی آباد،'),
(4740, 52, 5, 'و سوگند به [آسمان،] این سقف برافراشته!'),
(4741, 52, 6, 'و سوگند به دریای شعله‌ور!'),
(4742, 52, 7, 'که عذاب پروردگارت قطعاً واقع‌شدنی است.'),
(4743, 52, 8, 'بازدارنده‌ای ندارد.'),
(4744, 52, 9, 'روزی که آسمان به شدّت در هم پیچیده شود.'),
(4745, 52, 10, 'و کوه‌ها از جا کنده شده، [چون گرد و غبار] به شتاب روان گردند.'),
(4746, 52, 11, 'پس در آن روز، وای بر تکذیب‌کنندگان!'),
(4747, 52, 12, 'آنان که در باطل فرو رفته‌اند و به یاوه‌سرایی [درباره‌ی آیات الهی] سرگرمند.'),
(4748, 52, 13, 'روزی که به سختی به سوی آتش رانده می‌شوند.'),
(4749, 52, 14, '[و به آنان می‌گوییم:] «این همان آتشی است که پیوسته آن را تکذیب می‌کردید.'),
(4750, 52, 15, 'پس آیا این [عذاب هم] جادوست؟ یا شما [درست‌] نمی‌بینید؟'),
(4751, 52, 16, 'در آن وارد شوید و بسوزید! پس صبر کنید یا صبر نکنید، برای شما یکسان است، که تنها در برابر آنچه می‌کردید، جزا داده می‌شوید.»'),
(4752, 52, 17, 'بی‌گمان پرهیزکاران در باغ‌هایی [بهشتی] و نعمتی فراوانند.'),
(4753, 52, 18, 'به آنچه پروردگارشان به آنان داده، شادمانند و پروردگارشان آنان را از عذاب دوزخ نگاه داشته است.'),
(4754, 52, 19, '[به آنان گفته می‌شود:] «به خاطر اعمالی که انجام می‌دادید، گوارا بخورید و بیاشامید!»'),
(4755, 52, 20, 'بر تخت‌های ردیف شده تکیه می‌کنند و زیبارویانی سیاه‌چشم را به همسری آنان در می‌آوریم.'),
(4756, 52, 21, 'و کسانی که ایمان آوردند و فرزندانشان، در ایمان از آنان پیروی کردند، ما ذریه فرزندانشان را به آنان ملحق می‌کنیم و از [پاداش] عملشان هیچ نمی‌کاهیم. [آری!] هر کس در گرو اعمال خویش است.'),
(4757, 52, 22, 'و پی‌درپی از میوه و گوشتی که بخواهند، به آنان می‌رسانیم.'),
(4758, 52, 23, 'آنان در بهشت، جامی پر شراب را [دوستانه از هم می‌گیرند و] دست به دست می‌دهند که در [نوشیدن] آن نه بیهوده‌گویی است و نه گنهکاری.'),
(4759, 52, 24, 'و [برای خدمت به آنان،] پیوسته نوجوانانی گردشان می‌چرخند، که همچون مروارید در صدفند.'),
(4760, 52, 25, 'و به یکدیگر روی کرده، از [حال‌] هم می‌پرسند.'),
(4761, 52, 26, 'می‌گویند: «ما پیش از این [در دنیا] نسبت به خانواده‌ی خویش خیرخواه بودیم.'),
(4762, 52, 27, 'پس خداوند بر ما منّت نهاد و ما را از عذاب سوزان نگاه داشت.'),
(4763, 52, 28, 'زیرا ما پیش از این همواره او را می‌خواندیم. همانا اوست نیکوکار مهربان.»'),
(4764, 52, 29, 'پس [ای پیامبر! به مردم] یادآوری کن، که به لطف پروردگارت تو نه کاهن و پیشگویی و نه دیوانه و جنّ‌زده.'),
(4765, 52, 30, 'بلکه [کافران] می‌گویند: «او شاعری است که ما برای او چشم به راه حوادث تلخ و مرگ او هستیم.»'),
(4766, 52, 31, '[به آنان] بگو: «در انتظار باشید که من نیز با شما در انتظار می‌مانم. [شما در انتظار مرگ من و من در انتظار پیروزی بر شما.]»'),
(4767, 52, 32, 'آیا خردهایشان آنان را به این [یاوه‌گویی‌ها] وامی‌دارد؟ یا آنان قومی سرکشند؟'),
(4768, 52, 33, 'یا می‌گویند: «[محمّد، قرآن را خود ساخته و] به خدا نسبت داده است!» بلکه آنان [به سبب سرکشی و حسد،] ایمان نمی‌آورند.'),
(4769, 52, 34, 'پس اگر راستگویند، سخنی مانند آن بیاورند.'),
(4770, 52, 35, 'آیا از هیچ آفریده شده‌اند؟ یا خود آفریدگار خویشند؟!'),
(4771, 52, 36, 'آیا آسمان‌ها و زمین را آفریده‌اند؟! بلکه [حقّ این است که آنها] یقین ندارند.'),
(4772, 52, 37, 'آیا گنجینه‌های پروردگارت نزد آنان است؟ یا آنها [براین عالم] چیرگی دارند؟'),
(4773, 52, 38, 'آیا نردبانی دارند که با آن [اسرار آسمان‌ها را] می‌شنوند؟ پس شنونده‌ی آنان دلیلی روشن بیاورد.'),
(4774, 52, 39, 'آیا دختران [که از آنها بیزارید،] از آنِ خدا و پسران از آنِ شماست؟'),
(4775, 52, 40, 'آیا از آنان مزدی درخواست می‌کنی، که پرداخت آن بر آنان سنگین است؟'),
(4776, 52, 41, 'آیا غیب نزد آنان است و ایشان [هرچه را می‌خواهند، در لوح محفوظ] می‌نویسند؟'),
(4777, 52, 42, 'یا می‌خواهند [برای از بین بردن حقّ،] نیرنگی به کار برند؟ [پس بدانند که] کافران، خود گرفتار نیرنگ شده‌اند.'),
(4778, 52, 43, 'یا آنان معبودی جز الله دارند؟ منزّه است خدا از آنچه برای او شریک قرار می‌دهند.'),
(4779, 52, 44, 'آنها اگر ببینند پاره‌ای از آسمان فرو می‌افتد، [باز هم ایمان نمی‌آورند و] می‌گویند: «این ابری انباشته است.»'),
(4780, 52, 45, 'پس آنان را [به حال خود] واگذار تا آن روزشان را ببینند که در آن گرفتار صاعقه می‌شوند.'),
(4781, 52, 46, 'روزی که نیرنگشان، به آنان هیچ سودی نمی‌بخشد و یاری نخواهند شد.'),
(4782, 52, 47, 'و البتّه برای کسانی که ستم کردند، عذابی [دیگر] است جز این؛ ولی بیشتر آنان نمی‌دانند.'),
(4783, 52, 48, 'و برای [انجام] فرمان پروردگارت صبر و پایداری کن، که تو در دید ما [و تحت حمایت ما] هستی و هنگامی که [از خواب] برمی‌خیزی، با ستایش پروردگارت، او را به پاکی ستایش کن!'),
(4784, 52, 49, 'و پاسی از شب و [بامداد،] به هنگام ناپدید شدن ستارگان، خدا را تسبیح گوی!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به ستاره، چون [در افق] فروشود.'),
(4786, 53, 2, 'یار و همنشین شما [محمّد]، نه گمراه شده و نه منحرف گشته است.'),
(4787, 53, 3, 'و از روی هوای نفس سخن نمی‌گوید.'),
(4788, 53, 4, 'سخن او جز وحی که به او نازل می‌شود، نیست.'),
(4789, 53, 5, '[آن وحی که خدای] بسیار نیرومند به او آموخت.'),
(4790, 53, 6, '[همان که] درایت و توانمندی شگفتی دارد، پس چیره است.'),
(4791, 53, 7, 'در حالی که او در افق برتر بود،'),
(4792, 53, 8, 'سپس نزدیک و نزدیک‌تر شد.'),
(4793, 53, 9, 'تا جایی که فاصله‌اش به اندازه‌ی دو کمان یا کمتر شد.'),
(4794, 53, 10, 'پس به بنده‌ی خود وحی کرد، آنچه را وحی کرد.'),
(4795, 53, 11, 'دل، آنچه را دید، دروغ نخواند.'),
(4796, 53, 12, 'پس آیا بر سر آنچه می‌بیند، با وی بحث و جدال می‌کنید؟'),
(4797, 53, 13, 'و بی‌گمان یک بار دیگر هم او را دیده است.'),
(4798, 53, 14, 'نزد سدرة المنتهی.'),
(4799, 53, 15, 'که بهشتِ امن نزد آن است.'),
(4800, 53, 16, 'آن‌گاه که سدرة المنتهی را پوشاند، آن [نوری] که می‌پوشاند.'),
(4801, 53, 17, 'دیده [پیامبر از حقیقت] منحرف نگشت و [از دیدن حقیقت] تجاوز نکرد.'),
(4802, 53, 18, 'همانا او برخی از نشانه‌های بزرگ پروردگارش را دید.'),
(4803, 53, 19, 'آیا [بت‌های] لات و عزّی را دیده‌اید؟'),
(4804, 53, 20, 'و منات، آن سومین بت دیگر.'),
(4805, 53, 21, 'آیا پسر برای شماست و دختر [که از آن بیزارید،] برای خداوند؟'),
(4806, 53, 22, 'این، تقسیمی ناعادلانه است.'),
(4807, 53, 23, 'این بت‌ها، نام‌هایی بیش نیستند که شما و پدرانتان، نام‌گذاری کرده‌اید. خداوند هیچ‌گونه برهانی [بر حقانیت آنها] نفرستاده است. [مشرکان،] تنها از گمان و هوای نفس خود پیروی می‌کنند، در حالی که از سوی پروردگارشان، هدایت به سراغشان آمده است.'),
(4808, 53, 24, 'آیا انسان، به هر چه آرزو دارد، می‌رسد؟ [حتّی آرزویی چون شفاعت بت‌ها؟]'),
(4809, 53, 25, 'پس آخرت و دنیا برای خداوند است.'),
(4810, 53, 26, 'و چه بسا فرشتگانی که در آسمان‌ها هستند، [ولی] شفاعت آنان سودی ندارد، مگر پس از آن که خداوند برای هر که بخواهد و بپسندد، اجازه شفاعت دهد.'),
(4811, 53, 27, 'کسانی که به آخرت ایمان ندارند، فرشتگان را به نام دختران نام‌گذاری می‌کنند.'),
(4812, 53, 28, 'در حالی که آنان [به این امر] هیچ دانشی ندارند. آنان فقط از گمان پیروی می‌کنند و قطعاً گمان، [انسان را] از حقّ بی‌نیاز نمی‌کند.'),
(4813, 53, 29, 'پس، هر که از یاد ما روی گرداند و جز زندگی دنیا را خواستار نبود، از او روی بگردان.'),
(4814, 53, 30, 'آن، آخرین درجه از علم و شناخت آنان است. پروردگارت کسانی را که از راه او گمراه شده‌اند، بهتر می‌شناسد و به کسانی که هدایت یافته‌اند، داناتر است.'),
(4815, 53, 31, 'و آنچه در آسمان‌ها و آنچه در زمین است، برای اوست، تا بدکاران را به آنچه کرده‌اند، کیفر دهد و کسانی را که نیکی کرده‌اند، به بهترین وجه پاداش دهد.'),
(4816, 53, 32, 'کسانی که از گناهان بزرگ و زشتی‌های آشکار، مگر گناهان ناخواسته، پرهیز می‌کنند، بی‌شک آمرزش پروردگارت گسترده است. او به شما داناتر است، آن‌گاه که شما را از زمین پدید آورد و آن‌گاه که شما در شکم مادرانتان جنین بودید. پس خود را بی‌عیب نشمارید که او به تقواپیشگان داناتر است.'),
(4817, 53, 33, 'پس آیا دیدی آن که را [از حقّ] روی گرداند؟'),
(4818, 53, 34, 'و اندکی بخشید و دست کشید؟'),
(4819, 53, 35, 'آیا علم غیب نزد اوست و او می‌بیند [که اگر انفاق کند، فقیر می‌شود]؟'),
(4820, 53, 36, 'آیا از آنچه در تورات موسی آمده، او را خبر نداده‌اند؟'),
(4821, 53, 37, 'و [نیز صُحُفِ] ابراهیم که [حقّ را] به طور کامل ادا کرد؟'),
(4822, 53, 38, 'که هیچ کس بار [گناه] دیگری را به دوش نخواهد گرفت.'),
(4823, 53, 39, 'و این که برای انسان جز آنچه تلاش کرده، [بهره‌ی دیگری] نیست.'),
(4824, 53, 40, 'و این که [بهره‌ی] تلاش و کوشش او به زودی دیده خواهد شد.'),
(4825, 53, 41, 'سپس پاداش کامل آن را به او می‌دهند.'),
(4826, 53, 42, 'و پایان و بازگشت همگان به سوی پروردگار توست.'),
(4827, 53, 43, 'و اوست که می‌خنداند و می‌گریاند.'),
(4828, 53, 44, 'و اوست که می‌میراند و زنده می‌کند.'),
(4829, 53, 45, 'و اوست که دو زوجِ نر و ماده را آفرید،'),
(4830, 53, 46, 'از نطفه‌ای آنگاه که ریخته می‌شود.'),
(4831, 53, 47, 'و پدید آوردن [جهان] دیگر بر [عهده] اوست.'),
(4832, 53, 48, 'و اوست که بی‌نیاز می‌کند و سرمایه می‌بخشد.'),
(4833, 53, 49, 'و اوست پروردگار [ستاره] شِعری [که گروهی آن را می‌پرستیدند].'),
(4834, 53, 50, 'و اوست که نسل پیشین عاد را نابود کرد.'),
(4835, 53, 51, 'و ثمود را، پس [احدی از آنان] باقی نگذاشت.'),
(4836, 53, 52, 'و پیش از آنان، قوم نوح را، که ستمکارتر و سرکش‌تر بوده‌اند.'),
(4837, 53, 53, 'و شهرها [ی قوم لوط] را زیر و رو کرد و فروریخت.'),
(4838, 53, 54, 'پس آن شهرها را چنان که باید، پوشیده داشت.'),
(4839, 53, 55, 'پس [ای انسان!] در کدام یک از نعمت‌های پروردگارت تردید می‌کنی؟'),
(4840, 53, 56, 'این [پیامبر] هشداردهنده‌ای از هشداردهندگان پیشین است.'),
(4841, 53, 57, 'رستاخیز نزدیک شد.'),
(4842, 53, 58, 'جز خداوند، کسی برطرف‌ کننده‌ی آن روز نیست.'),
(4843, 53, 59, 'پس آیا از این سخن، تعجّب می‌کنید؟'),
(4844, 53, 60, 'و می‌خندید و گریه نمی‌کنید؟'),
(4845, 53, 61, 'در حالی که شما غافل و هوسرانید.'),
(4846, 53, 62, 'پس تنها خدا را سجده کنید و [او را] بپرستید.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت بسیار نزدیک شد و ماه از هم شکافت.'),
(4848, 54, 2, 'و اگر [کافران] معجزه‌ای بینند، روی می‌گردانند و می‌گویند: «این جادویی است پی‌درپی و همیشگی!»'),
(4849, 54, 3, 'و [تو و قرآن تو را] تکذیب کردند و از هوس‌های خود پیروی نمودند. در حالی که هر کاری [سرانجام در جایگاه خود] قرار می‌گیرد. [و حقّانیت تو آشکار خواهد شد.]'),
(4850, 54, 4, 'و بی‌گمان از خبرهای مهمّ [گذشتگان]، آنچه مایه‌ی دست برداشتن [از کفر] است، برای آنان آمد.'),
(4851, 54, 5, '[با این که آن خبرها،] حکمتی رسا بود؛ ولی هشدارها [برای افراد لجوج،] سودی نداشت.'),
(4852, 54, 6, 'پس، از آنان روی بگردان! [منتظر باش] روزی را که آن دعوت‌کننده، [آنان را] به سوی چیزی ناخوش [و بس دشوار و هولناک] فرامی‌خواند.'),
(4853, 54, 7, 'آنان در حالی که چشم‌هایشان [از ترس و وحشت] فروافتاده، همچون ملخ‌هایی پراکنده، از قبرها بیرون می‌آیند.'),
(4854, 54, 8, 'سراسیمه به سوی دعوت‌کننده می‌دوند. کافران می‌گویند: این روزی بسیار سخت است!»'),
(4855, 54, 9, 'پیش از اینان، قوم نوح [نیز نشانه‌های ما را] دروغ انگاشتند و بنده ما [نوح] را تکذیب کردند و گفتند: او دیوانه و جنّ‌زده‌ای است که [جنّ‌ها] آزارش داده‌اند.'),
(4856, 54, 10, 'پس نوح پروردگارش را [چنین] خواند: «[پروردگارا!] من مغلوب شده‌ام، پس یاریم کن!»'),
(4857, 54, 11, 'پس ما درهای آسمان را با آبی بسیار فراوان و ریزان گشودیم.'),
(4858, 54, 12, 'و از زمین چشمه‌هایی جوشاندیم، پس آب [زمین و آسمان] بر اساس امری که مقدّر شده بود، به هم پیوست.'),
(4859, 54, 13, 'و نوح را بر آن [کشتی] دارای تخته‌ها و میخ‌ها سوار کردیم.'),
(4860, 54, 14, 'کشتی زیر نظر ما به حرکت درآمد. [این،] پاداش پیامبری بود که به او کفر ورزیدند.'),
(4861, 54, 15, 'و همانا ما آن [کشتی] را به عنوان نشانه‌ای باقی گذاشتیم، پس آیا پندگیرنده‌ای هست؟'),
(4862, 54, 16, 'پس عذاب و هشدار من چگونه بود؟'),
(4863, 54, 17, 'و به راستی ما قرآن را برای یادآوری [و پندپذیری]، آسان [و قابل فهم] کردیم، پس آیا پندپذیری هست؟'),
(4864, 54, 18, 'قوم عاد [پیامبر خود را] تکذیب کردند، پس عذاب و هشدار من چگونه بود؟'),
(4865, 54, 19, 'ما بر آنان در روزی شوم و دنباله‌دار، تندبادی سخت و سرد فرستادیم.'),
(4866, 54, 20, '[تندبادی که] مردم [تنومند عاد] را همچون تنه‌های درخت خرمای ریشه‌کن شده از جا برمی‌کند.'),
(4867, 54, 21, 'پس عذاب و هشدار من چگونه بود؟'),
(4868, 54, 22, 'و به راستی ما قرآن را برای یادآوری [و پندپذیری]، آسان کردیم، پس آیا پندپذیری هست؟'),
(4869, 54, 23, 'قوم ثَمود، هشداردهندگان را تکذیب کردند.'),
(4870, 54, 24, 'پس گفتند: «آیا از بشری تنها که از [میان] ماست، پیروی کنیم؟ در این صورت ما در گمراهی و دیوانگی خواهیم بود.'),
(4871, 54, 25, 'آیا از میان ما، وحی تنها بر صالح نازل شده است؟ بلکه او دروغگو و خودخواه است.»'),
(4872, 54, 26, '«[آنان] در آینده خواهند دانست که دروغگو و خودخواه کیست.'),
(4873, 54, 27, 'ما برای آزمایش آنان آن ماده شتر را می‌فرستیم. پس مراقب [پایان] کار آنان باش و شکیبایی پیشه کن.'),
(4874, 54, 28, 'و به آنان خبر ده که آب، میان آنان و شتر تقسیم شده است. هر کدام در زمان نوبت خود بر سر آب حاضر شوند.»'),
(4875, 54, 29, 'پس آنان رفیقشان را [که برای پی‌کردن ناقه گماشته بودند،] فراخواندند، پس دست به کار شد و شتر را از پای درآورد.'),
(4876, 54, 30, 'پس عذاب و هشدار من چگونه بود؟'),
(4877, 54, 31, 'ما فقط یک صیحه [و صاعقه‌ای مرگبار] را فروفرستادیم، پس آنان مانند گیاه خشک و خردشده‌ای گشتند که [دامداران] در آغل می‌ریزند.'),
(4878, 54, 32, 'و به راستی ما قرآن را برای یادآوری [و پندپذیری]، آسان [و قابل فهم] کردیم، پس آیا پندپذیری هست؟'),
(4879, 54, 33, 'قوم لوط [نیز] هشداردهندگان را تکذیب کردند.'),
(4880, 54, 34, 'ما نیز بر آنان بادی همراه با سنگ فرستادیم، و فقط خانواده‌ی لوط را سحرگاهان نجات دادیم.'),
(4881, 54, 35, '[این نجات] نعمتی از سوی ما بود. ما این‌گونه هر که را شکر کند، پاداش می‌دهیم.'),
(4882, 54, 36, '[لوط،] مردم را از قهر ما هشدار داده بود، امّا در هشدارها جدال و ستیزه کردند.'),
(4883, 54, 37, 'و مهمانانش را از او خواستند ولی ما چشمانشان را نابینا و محو کردیم. [و گفتیم:] «بچشید عذاب و هشدار مرا!»'),
(4884, 54, 38, 'و صبحگاهان عذابی پایدار به سراغشان آمد.'),
(4885, 54, 39, '«پس بچشید عذاب و هشدار مرا!»'),
(4886, 54, 40, 'و به راستی ما قرآن را برای یادآوری [و پندپذیری]، آسان کردیم، پس آیا پندپذیری هست؟'),
(4887, 54, 41, 'و همانا هشداردهندگان به سراغ فرعونیان آمدند.'),
(4888, 54, 42, '[امّا آنان] همه‌ی معجزات ما را تکذیب کردند، پس ما آنان را [به قهر] گرفتیم، چون گرفتنِ زورمندی شکست‌ناپذیر.'),
(4889, 54, 43, 'آیا کافرانِ شما [مردم مکه] از آنان بهترند؟ یا برای شما امان‌نامه‌ای در کتاب‌های آسمانی است؟'),
(4890, 54, 44, 'یا می‌گویند: «ما همگی متّحد و پشتیبان یکدیگریم. [و هیچ قدرتی ما را شکست نخواهد داد.]»'),
(4891, 54, 45, 'به زودی، [این] گروه در هم شکسته خواهد شد و همه پشت‌کنان خواهند گریخت.'),
(4892, 54, 46, 'آری، قیامت وعده‌گاه آنان است و آن روز، سخت‌تر و تلخ‌تر است.'),
(4893, 54, 47, 'که گناهکاران در گمراهی و [آتش و] جنونند.'),
(4894, 54, 48, 'روزی که به رو در آتش کشیده می‌شوند [و به آنان گفته می‌شود:] «سوزندگی و عذاب دردناک دوزخ را بچشید!»'),
(4895, 54, 49, 'ما هر چیز را به اندازه آفریده‌ایم.'),
(4896, 54, 50, 'و فرمان ما تنها یک بار است، مانند یک چشم بر هم زدن.'),
(4897, 54, 51, 'و بی‌تردید ما کسانی را [از پیشینیان] که مانند شما [کافر] بودند، نابود کردیم. پس آیا پندپذیری هست؟'),
(4898, 54, 52, 'و هر کاری که کرده‌اند، در نامه‌ها [ثبت] است.'),
(4899, 54, 53, 'و هرکوچک و بزرگی نوشته شده است.'),
(4900, 54, 54, 'به راستی پرهیزکاران در باغ‌ها [ی بهشتی] و [کنار] جویبارها هستند،'),
(4901, 54, 55, 'درجایگاهی راستین، نزد فرمانروایی مقتدر.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '[خداوندِ] رحمان،'),
(4903, 55, 2, 'قرآن را یاد داد،'),
(4904, 55, 3, 'انسان را آفرید،'),
(4905, 55, 4, 'به او بیان آموخت.'),
(4906, 55, 5, 'خورشید و ماه، با حسابی [دقیق‌] روانند.'),
(4907, 55, 6, 'و گیاه و درخت [برای او] سجده می‌کنند.'),
(4908, 55, 7, 'و [خداوند] آسمان را برافراشت و [برای سنجش هر امری] ترازو نهاد،'),
(4909, 55, 8, 'تا در سنجش و میزان از اندازه نگذرید،'),
(4910, 55, 9, 'و وزن را به عدالت برپا دارید و در ترازو کم نگذارید.'),
(4911, 55, 10, 'و زمین را برای زمینیان قرار داد،'),
(4912, 55, 11, 'که در آن [هرگونه] میوه‌ای است و خرما [با خوشه‌هایی] دارای پوشش،'),
(4913, 55, 12, 'و دانه‌ی پوست‌دار و گیاه خوش‌بو.'),
(4914, 55, 13, 'پس [ای انسان‌ها و ای جنّیان!] کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4915, 55, 14, 'انسان را از گل خشکیده‌ای همچون سفال آفرید،'),
(4916, 55, 15, 'و جنّ را از شعله‌ی آتشی بی‌دود آفرید.'),
(4917, 55, 16, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4918, 55, 17, 'پروردگار دو مشرق و پروردگار دو مغرب.'),
(4919, 55, 18, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4920, 55, 19, 'دو دریا[ی شور و شیرین] را روان ساخت که به هم می‌رسند.'),
(4921, 55, 20, '[امّا] میان آن دو فاصله‌ای قرار داد تا از حد نگذرند [و به هم نیامیزند].'),
(4922, 55, 21, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4923, 55, 22, 'از هر دو [دریا]، مروارید و مرجان بیرون می‌آید.'),
(4924, 55, 23, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4925, 55, 24, 'و کشتی‌های ساخته شده‌ی کوه پیکر در دریا، از آنِ اوست.'),
(4926, 55, 25, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4927, 55, 26, 'هر که روی آن [زمین] است، از میان رفتنی است،'),
(4928, 55, 27, 'و [تنها] ذات پروردگارت که شکوهمند و گرامی است، ماندگار است.'),
(4929, 55, 28, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4930, 55, 29, 'هر که در آسمان‌ها و زمین است، [نیاز خود را] از او می‌خواهد. او هر روز، در شأن و کاری است.'),
(4931, 55, 30, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4932, 55, 31, 'ای جنّ و انس! به زودی به [حساب] شما می‌پردازیم.'),
(4933, 55, 32, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4934, 55, 33, 'ای گروه جنّ و انس! اگر می‌توانید از مرزهای آسمان‌ها و زمین نفوذ کنید، نفوذ کنید. هرگز نمی‌توانید! مگر با قدرتی [برتر].'),
(4935, 55, 34, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4936, 55, 35, 'آن روز شعله‌ای از آتش و مس گداخته بر شما فرومی‌ریزد، پس نمی‌توانید یکدیگر را یاری دهید!'),
(4937, 55, 36, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4938, 55, 37, 'پس آن‌گاه که آسمان شکافته شود و چون روغن گداخته گلگون گردد.'),
(4939, 55, 38, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4940, 55, 39, 'پس در آن روز، هیچ انسان و جنّی، از گناهش پرسیده نشود. [که همه چیز روشن است.]'),
(4941, 55, 40, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4942, 55, 41, 'تبهکاران، به سیمایشان شناخته می‌شوند، پس آنان را به موهای پیش سر و به پاهایشان می‌گیرند [و به دوزخ می‌افکنند.]'),
(4943, 55, 42, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4944, 55, 43, 'این است جهنّمی که تبهکاران انکارش می‌کردند.'),
(4945, 55, 44, 'میان آن [آتش] و آبی داغ و سوزان در حرکتند.'),
(4946, 55, 45, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4947, 55, 46, 'و برای کسی که از مقام پروردگارش بترسد، دو باغ [بهشتی] است.'),
(4948, 55, 47, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4949, 55, 48, 'دو باغ که درختان انبوه و باطراوت دارد.'),
(4950, 55, 49, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4951, 55, 50, 'در آن دو باغ، دو چشمه‌ی همیشه جاری است.'),
(4952, 55, 51, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4953, 55, 52, 'در آن دو باغ، از هر میوه‌ای دو گونه فراهم است.'),
(4954, 55, 53, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4955, 55, 54, '[بهشتیان] بر بسترهایی که آسترشان از دیبا و ابریشم درشت‌بافت است، تکیه می‌زنند و میوه‌های آن دو باغ در دسترس است.'),
(4956, 55, 55, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4957, 55, 56, 'در آن بسترها، همسرانی هستند که نگاهشان از بیگانه کوتاه است و پیش از اینان، هیچ انسان و جنّی آنان را لمس نکرده است.'),
(4958, 55, 57, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4959, 55, 58, 'آنان چون یاقوت و مرجانند.'),
(4960, 55, 59, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4961, 55, 60, 'آیا پاداش نیکی، جز نیکی است؟'),
(4962, 55, 61, 'پس کدام‌یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4963, 55, 62, 'و پایین‌تر از آن [دو باغ]، دو باغ دیگر هست.'),
(4964, 55, 63, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4965, 55, 64, '[آن دو باغِ پایین دست،] از شدّت سرسبزی به سیاهی می‌ماند.'),
(4966, 55, 65, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4967, 55, 66, 'در آن [دو باغ]، دو چشمه‌ی همیشه جوشان است.'),
(4968, 55, 67, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4969, 55, 68, 'در آن [دو باغ]، میوه‌های فراوان و خرما و انار است.'),
(4970, 55, 69, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4971, 55, 70, 'در آن باغ‌ها، دوشیزگانی هستند خوش‌خوی و زیباروی.'),
(4972, 55, 71, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4973, 55, 72, 'حوریانی که در سراپرده‌ها پرده‌نشینند.'),
(4974, 55, 73, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4975, 55, 74, 'پیش از بهشتیان، هیچ انسان یا جنّی آنان را لمس نکرده است.'),
(4976, 55, 75, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4977, 55, 76, 'آنان بر بالش‌هایی تکیه زده‌اند که با پارچه‌هایی سبز رنگ و فرشهایی کمیاب آراسته شده است.'),
(4978, 55, 77, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4979, 55, 78, 'خجسته باد نام پروردگارت که شکوهمند و ارجمند است!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'آن‌گاه که آن واقعه [عظیم قیامت] روی دهد،'),
(4981, 56, 2, 'که در واقع شدن آن دروغی نیست [و سزا نیست کسی آن را دروغ بشمارد].'),
(4982, 56, 3, '[آن واقعه] پست‌کننده [کافران] و بالابرنده [مؤمنان] است.'),
(4983, 56, 4, 'آن‌گاه که زمین به سختی لرزانده شود.'),
(4984, 56, 5, 'و کوه‌ها به شدّت متلاشی شوند.'),
(4985, 56, 6, 'پس به حالت غبار پراکنده درآیند.'),
(4986, 56, 7, 'و [در آن روز] شما سه گروه خواهید بود:'),
(4987, 56, 8, 'پس [گروه نخست] سعادتمندانند، سعادتمندان چه گروهی هستند!'),
(4988, 56, 9, 'و [دسته‌ی دوم] بدبختانند، چه گروهی هستند بدبختان!'),
(4989, 56, 10, 'و [گروه سوم] پیشگامان [کارهای نیکند که درگرفتن پاداش نیز،] پیشگامند.'),
(4990, 56, 11, 'آنان نزدیکان [درگاه خداوند] هستند.'),
(4991, 56, 12, 'در باغ‌های پرنعمت خواهند بود.'),
(4992, 56, 13, '[آنان] گروه زیادی از پیشینیان خواهند بود [که هنگام دعوت پیامبران پیشین به آنها ایمان آوردند].'),
(4993, 56, 14, 'و گروه کمی از امّت آخرین [پیامبر که به او ایمان آوردند].'),
(4994, 56, 15, 'بر تخت‌هایی چیده شده و جواهرنشان،'),
(4995, 56, 16, 'تکیه داده‌اند، در حالی که [شادمان و باصفا] روبروی یکدیگرند.'),
(4996, 56, 17, 'بر گِردشان پسرانی همیشه نوجوان می‌گردند.'),
(4997, 56, 18, 'با جام‌ها و آبریزها و پیاله‌ای از [نوشیدنی‌های گوارا و] روان [از آنان پذیرایی می‌کنند].'),
(4998, 56, 19, 'از آن [نوشیدنی‌ها] نه سردرد می‌گیرند و نه مست می‌شوند.'),
(4999, 56, 20, 'و میوه‌هایی از هر چه انتخاب کنند.'),
(5000, 56, 21, 'و گوشت پرنده از هر نوع که بخواهند.'),
(5001, 56, 22, 'و همسرانی سپید روی، درشت چشم و زیبا،'),
(5002, 56, 23, 'همچون مروارید در صدف.'),
(5003, 56, 24, 'پاداشی در برابر آنچه می‌کردند.'),
(5004, 56, 25, 'در آنجا نه سخن بیهوده‌ای می‌شنوند و نه نسبت گناهی به دیگری،'),
(5005, 56, 26, 'تنها چیزی که می‌شنوند سلام است، سلام!'),
(5006, 56, 27, 'و یاران [راست که کارنامه‌ی آنان را به دست راستشان می‌دهند]، چه [سعادتمندند] یاران راست!'),
(5007, 56, 28, 'در کنار درختان سدر بی‌خار'),
(5008, 56, 29, 'و درختان موز که میوه‌هایش خوشه خوشه روی هم چیده شده است،'),
(5009, 56, 30, 'و سایه‌ای گسترده و پایدار،'),
(5010, 56, 31, 'و آبی ریزان [از آبشارها]،'),
(5011, 56, 32, 'و میوه‌ای فراوان،'),
(5012, 56, 33, 'که نه تمام می‌شود و نه کسی را از آن بازمی‌دارند.'),
(5013, 56, 34, 'و همسرانی والا مرتبه،'),
(5014, 56, 35, 'که ما آنان را به گونه‌ای ویژه پدید آوردیم.'),
(5015, 56, 36, 'پس آنان را دوشیزه قرار دادیم.'),
(5016, 56, 37, 'شوهر دوستانی خوش‌سخن و هم سن و سال.'),
(5017, 56, 38, 'برای یاران راست است.'),
(5018, 56, 39, 'که گروهی از پیشینیان خواهند بود،'),
(5019, 56, 40, 'و گروهی از امّت آخرین [پیامبر].'),
(5020, 56, 41, 'و یاران چپ [که کارنامه‌ی آنان را به دست چپشان می‌دهند]، چه [شوربختند] یاران چپ!'),
(5021, 56, 42, 'در میان باد سوزان و آب داغ،'),
(5022, 56, 43, 'و سایه‌ای از دود غلیظ و سیاهند.'),
(5023, 56, 44, 'که نه خنک است و نه سودبخش.'),
(5024, 56, 45, 'البتّه آنان پیش از این نازپرورده و خوش‌گذران و سرکش بودند.'),
(5025, 56, 46, 'و همواره بر گناه بزرگ پافشاری می‌کردند.'),
(5026, 56, 47, 'و پیوسته می‌گفتند: «آیا هنگامی که ما مردیم و خاک و استخوان‌هایی [پوسیده] شدیم. آیا ما برانگیخته می‌شویم؟'),
(5027, 56, 48, 'و آیا نیاکان ما [نیز برانگیخته می‌شوند]؟»'),
(5028, 56, 49, 'بگو: «به راستی پیشینیان و آیندگان،'),
(5029, 56, 50, 'حتماً برای وعده‌گاهِ روزی معین، گردآورده خواهند شد.'),
(5030, 56, 51, 'سپس، شما ای گمراهان انکارکننده!'),
(5031, 56, 52, 'حتماً از درخت زقّوم، خواهید خورد.'),
(5032, 56, 53, 'و شکم‌ها را از آن پر خواهید کرد.'),
(5033, 56, 54, 'و روی آن، از آب جوشان می‌آشامید.'),
(5034, 56, 55, 'پس مانند شتران عطش‌زده می‌نوشید.»'),
(5035, 56, 56, 'این [نخستین] پذیرایی آنان در روز قیامت است.'),
(5036, 56, 57, 'ما شما را آفریدیم، پس چرا تصدیق نمی‌کنید؟'),
(5037, 56, 58, 'آیا آنچه را [به صورت نطفه] فرومی‌ریزید، دیده‌اید؟'),
(5038, 56, 59, 'آیا شما آن را [به صورت انسان] می‌آفرینید؟ یا ما آفریننده‌ایم؟'),
(5039, 56, 60, 'ماییم که مرگ را در میان شما مقدّر کردیم و هرگز کسی بر [قدرت] ما پیشی نگرفته است.'),
(5040, 56, 61, 'تا همانندانتان را جانشین شما کنیم و شما را در جهانی که نمی‌دانید، پدیدار گردانیم.'),
(5041, 56, 62, 'شما آفرینش نخستین را دانستید، پس چرا [به آفرینش دوباره] متذکر نمی‌شوید؟'),
(5042, 56, 63, 'آیا آنچه را می‌کارید، دیده‌اید؟'),
(5043, 56, 64, 'آیا شما آن را می‌رویانید؟ یا ما رویاننده‌ایم؟'),
(5044, 56, 65, 'اگر بخواهیم آن را خار و خاشاک می‌گردانیم، پس شما از روی تعجّب می‌گویید:'),
(5045, 56, 66, '«ما زیان‌دیده‌ایم!'),
(5046, 56, 67, 'بلکه محروم و بدبختیم!»'),
(5047, 56, 68, 'آیا آبی را که می‌نوشید، دیده‌اید؟'),
(5048, 56, 69, 'آیا شما آن را از ابرِ باران‌زا فروآوردید؟ یا ما فروفرستنده‌ایم؟'),
(5049, 56, 70, 'اگر بخواهیم آن را شور و تلخ می‌گردانیم، پس چرا سپاسگزاری نمی‌کنید؟'),
(5050, 56, 71, 'آیا آتشی را که می‌افروزید، دیده‌اید؟'),
(5051, 56, 72, 'آیا درخت آن را شما آفریده‌اید؟ یا ما آفریننده‌ایم؟'),
(5052, 56, 73, 'ما آن را مایه‌ی یادآوری [آتش قیامت] و نعمتی برای مسافران قرار داده‌ایم.'),
(5053, 56, 74, 'پس به نام پروردگار بزرگت تسبیح گوی!'),
(5054, 56, 75, 'پس سوگند به جایگاه ستارگان!'),
(5055, 56, 76, 'و اگر بدانید، این سوگندی است بزرگ.'),
(5056, 56, 77, 'همانا آن، قرآن کریم است.'),
(5057, 56, 78, 'که در کتابی پوشیده و پنهان جای دارد.'),
(5058, 56, 79, 'جز پاک‌شدگان [از هر نوع آلودگی، دیگران] به [معارف] آن دسترسی ندارند.'),
(5059, 56, 80, 'فرود آمده از سوی پروردگار جهانیان است.'),
(5060, 56, 81, 'پس آیا این سخن [الهی] را سرسری می‌گیرید؟'),
(5061, 56, 82, 'و [نصیب و] روزی خود را تکذیب آن قرار می‌دهید؟'),
(5062, 56, 83, 'پس چرا آن‌گاه که [جان] به گلوگاه می‌رسد،'),
(5063, 56, 84, 'و شما در آن هنگام می‌نگرید [و هیچ کاری از شما ساخته نیست]!'),
(5064, 56, 85, 'و ما از شما به آن [محتَضر] نزدیک‌تریم؛ ولی نمی‌بینید.'),
(5065, 56, 86, 'پس اگر شما [در برابر کردارتان] کیفر‌شدنی نیستید،'),
(5066, 56, 87, 'اگر راست می‌گویید، چرا آن جان را [به محتضَر] برنمی‌گردانید؟'),
(5067, 56, 88, 'پس اگر او از مقرّبان باشد،'),
(5068, 56, 89, 'آسایش و رفاه و بهشت پر نعمت، برای او خواهد بود.'),
(5069, 56, 90, 'و امّا اگر از یاران راست باشد،'),
(5070, 56, 91, '[به او گفته می‌شود:] «از سوی یاران راست، به تو سلام باد!»'),
(5071, 56, 92, 'و امّا اگر از تکذیب‌کنندگان گمراه باشد،'),
(5072, 56, 93, 'با آبی جوشان پذیرایی می‌شود.'),
(5073, 56, 94, 'و ورود به دوزخ [جزای اوست].'),
(5074, 56, 95, 'همانا این [مطلب]، حقّ و یقینی است.'),
(5075, 56, 96, 'پس به نام پروردگار بزرگوارت تسبیح گوی!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمان‌ها و زمین است، برای خداوند تسبیح می‌گوید و اوست عزیز حکیم.'),
(5077, 57, 2, 'فرمانروایی آسمان‌ها و زمین برای اوست. زنده می‌کند و می‌میراند و او بر هر کاری تواناست.'),
(5078, 57, 3, 'اوست آغاز و انجام و آشکار و نهان و او به هر چیزی داناست.'),
(5079, 57, 4, 'او کسی است که آسمان‌ها و زمین را در شش روز [و دوران] آفرید، سپس بر عرش [قدرت] قرار گرفت. [و به تدبیر جهان پرداخت.] آنچه در زمین فرومی‌رود و آنچه از آن بیرون می‌آید و آنچه از آسمان فرود می‌آید و آنچه در آن بالا می‌رود، [همه را] می‌داند و هر جا باشید، او با شماست و خدا به آنچه می‌کنید، بیناست.'),
(5080, 57, 5, 'فرمانروایی آسمان‌ها و زمین از آنِ اوست و همه‌ی کارها به او بازمی‌گردد.'),
(5081, 57, 6, 'از شب می‌کاهد و به روز می‌افزاید و از روز می‌کاهد و به شب می‌افزاید و او به [انگیزه‌ها و] راز دل‌ها داناست.'),
(5082, 57, 7, 'به خدا و پیامبرش ایمان بیاورید و از آنچه شما را در آن جانشین [گذشتگان‌] قرار داد، انفاق کنید. پس کسانی از شما که ایمان آورند و انفاق کنند، پاداشی بزرگ دارند.'),
(5083, 57, 8, 'و چرا به خدا ایمان نمی‌آورید؟ در حالی که پیامبر، شما را فرامی‌خواند تا به پروردگارتان ایمان بیاورید و به راستی خداوند از شما [از راه عقل و فطرت،] پیمان گرفته است، اگر باور دارید.'),
(5084, 57, 9, 'او کسی است که بر بنده‌ی خود [محمّد]، آیات روشن و روشنگر فرومی‌فرستد، تا شما را از تاریکی‌ها به روشنی درآورد و همانا خداوند نسبت به شما رأفت و رحمت بسیار دارد.'),
(5085, 57, 10, 'و چرا در راه خدا انفاق نمی‌کنید؟ در حالی که [هر چه دارید، می‌گذارید و می‌روید و] میراث آسمان‌ها و زمین از آنِ خداست. کسانی از شما که پیش از فتح [مکه] انفاق و جهاد کردند، [با دیگران] یکسان نیستند. آنان از جهت درجه، از کسانی که پس از فتح [مکه] انفاق و جهاد کردند، برترند و خداوند به هر یک وعده نیکو داده است و خداوند به آنچه می‌کنید، آگاه است.'),
(5086, 57, 11, 'کیست آن که به خدا وام دهد، وامی نیکو؟ تا [خداوند] آن را برای او چند برابر کند و برای او پاداشی گران‌مایه باشد.'),
(5087, 57, 12, 'روزی که مردان و زنان باایمان را می‌بینی که نورشان از پیش روی و جانب راستشان به سرعت پیش می‌رود. [به آنان گفته می‌شود:] «مژده باد شما را امروز، به باغ‌هایی [بهشتی] که از زیر [درختان] آن نهرها جاری است. در آنجا جاودانه‌اید. این است آن رستگاری و کامیابی بزرگ.»'),
(5088, 57, 13, 'روزی که مردان و زنان منافق به مؤمنان می‌گویند: «درنگی کنید تا از نور شما پرتوی برگیریم!» و به آنان گفته می‌شود: «به عقب برگردید و [از دنیا] نوری بجویید.» پس میان آنان دیواری زده می‌شود، با دری که درونش، [بهشتِ] رحمت و از جانب بیرونش [جهنّمِ] عذاب است.'),
(5089, 57, 14, '[منافقان، مؤمنان را] ندا می‌دهند که: «مگر ما [در دنیا] با شما نبودیم؟» آنها می‌گویند: «چرا! ولی شما خود را در بلا افکندید و چشم به راه [حوادث ناگوار برای ما] بودید و [در دین حقّ] شک کردید و آرزوها[ی باطل] شما را فریفت، تا آن که فرمان خداوند [بر مرگ شما] فرارسید و [شیطانِ] فریبکار، شما را درباره‌ی خدا فریب داد.»'),
(5090, 57, 15, 'پس امروز، نه از شما و نه از کافران، بدَل و بلاگردانی پذیرفته نمی‌شود. جایگاه شما آتش است. آتش، یاور و سزاوار شماست. بد سرانجامی است!»'),
(5091, 57, 16, 'آیا برای کسانی که ایمان آورده‌اند، زمان آن نرسیده که دل‌هایشان برای یاد خدا و آنچه از حقّ نازل شده، نرم و فروتن گردد؟ و مانند کسانی نباشند که پیش از این، کتاب آسمانی به آنان داده شد، پس زمان طولانی بر آنان گذشت و دل‌هایشان سخت گردید و بسیارشان فاسق گشتند.'),
(5092, 57, 17, 'بدانید که خداوند، زمین را پس از مرگش زنده می‌کند. همانا ما نشانه‌ها را برای شما روشن کردیم، باشد که بیندیشید.'),
(5093, 57, 18, 'مردان و زنانی که صدقه داده‌اند و به خداوند وامی نیکو پرداخته‌اند، برای آنان چند برابر خواهد شد و پاداشی گران‌مایه خواهند داشت.'),
(5094, 57, 19, 'و کسانی که به خدا و پیامبرانش ایمان آورده‌اند، آنانند همان راست‌پیشگان و گواهانِ [اعمال] نزد پروردگارشان. پاداش [کردار] و نور [ایمان]شان را دارند و کسانی که کفر ورزیدند و آیات ما را تکذیب کردند، آنان اهل دوزخند.'),
(5095, 57, 20, 'بدانید که زندگانی دنیا، چیزی جز بازی و سرگرمی و زینت‌طلبی و فخرفروشی در میان خود و افزون‌خواهی بر همدیگر در اموال و فرزندان نیست، همانند بارانی که گیاه [سرسبز حاصل از] آن، کشاورزان را به شگفتی وادارد، سپس پژمرده شود و آن را زرد بینی، سپس کاه و خاشاک شود و در آخرت، [برای گنهکاران] عذابی سخت و [برای فرمانبران] آمرزش و خشنودی از جانب خداوند است و زندگانی دنیا فقط کالای فریبنده است.'),
(5096, 57, 21, 'از هم پیشی بگیرید برای رسیدن به آمرزشی از پروردگارتان و بهشتی که پهنایش همچون پهنای آسمان و زمین است و برای کسانی آماده شده که به خدا و پیامبرانش ایمان دارند. این فضل خداست، به هر که بخواهد، می‌دهد و خداوند صاحب فضل و فزون‌بخشی بزرگ است.'),
(5097, 57, 22, 'هیچ آسیبی در زمین [چون بلاهای طبیعی] و در جانتان [چون بیماری‌ها و سختی‌ها] به شما نمی‌رسد، مگر پیش از آن که آن را پدید آوریم، در کتابی ثبت است. همانا این بر خداوند آسان است.'),
(5098, 57, 23, 'تا بر آنچه از دست دادید، تأسف نخورید و به آنچه به شما داده شد، [سرمستانه] شادمانی نکنید و خداوند هیچ گردن‌کش فخرفروشی را دوست ندارد،'),
(5099, 57, 24, 'آنان که بخل می‌ورزند و مردم را به بخل ورزیدن فرمان می‌دهند و هر کس [از انفاق و قرض] روی گرداند، پس [بداند که] خداوند خود بی‌نیاز و ستوده است.'),
(5100, 57, 25, 'بی‌گمان ما پیامبرانمان را با معجزات و دلایل آشکار فرستادیم و همراه آنان کتاب و وسیله‌ی سنجش فروفرستادیم، تا مردم به دادگری برخیزند و آهن را فروفرستادیم که در آن نیرویی سخت است و سودهایی برای مردم دارد، [تا از آن بهره گیرند.] و تا خداوند معلوم دارد چه کسی او و پیامبرانش را در نهان یاری می‌کند. خداوند، نیرومند و شکست‌ناپذیر است.'),
(5101, 57, 26, 'و همانا نوح و ابراهیم را فرستادیم و در نسل و خاندان آنان نبوّت و کتاب قرار دادیم. پس برخی از آنان هدایت یافتند؛ ولی بسیاری از آنان نافرمان و بدکارند.'),
(5102, 57, 27, 'سپس به دنبال آنان، پیامبران خود را پی‌در‌پی آوردیم و عیسی پسر مریم را در پی آنان آوردیم و به او اِنجیل دادیم و در دل کسانی که او را پیروی کردند، رأفت و رحمت قرار دادیم و [امّا] ترک دنیایی را که از پیش خود درآوردند، ما بر آنان مقرّر نکرده بودیم، مگر [آن که] برای کسب خشنودی خداوند [چنین کنند،] ولی آن‌گونه که باید، حقّ آن را مراعات نکردند. پس به کسانی از آنان که ایمان آورده بودند، پاداش دادیم؛ ولی بسیاری از آنان نافرمان و بدکار بودند.'),
(5103, 57, 28, 'ای کسانی که ایمان آورده‌اید! از [نافرمانی] خدا پروا کنید و به پیامبرش ایمان آورید، تا از رحمت خود، دو بهره به شما عطا کند و برای شما نوری قرار دهد که با آن [در دنیا و آخرت]، راه بروید و شما را بیامرزد و خداوند بسیار آمرزنده و مهربان است.'),
(5104, 57, 29, 'تا اهل کتاب گمان نکنند که [مسلمانان،] راهی به فضل خدا ندارند، و بدانند که فضل [و عنایت، تنها] به دست خداست. آن را به هر کس بخواهد، می‌دهد و خداوند دارای بخشش بزرگ است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'همانا خدا سخن آن زنی را که درباره‌ی همسرش با تو مجادله و به خدا شکوه و گلایه می‌کرد، شنید و خداوند گفتگوی شما دو نفر، را می‌شنید. خداوند شنوای بیناست.'),
(5106, 58, 2, 'کسانی از شما [مردان] که نسبت به همسرانشان ظِهار می‌کنند، [و با تشبیه آنها به مادرانشان، آنان را بر خود حرام می‌نمایند، بدانند که] آنها مادرانشان نیستند. مادرانشان فقط کسانی هستند که آنها را زاده‌اند و آنان [بر اساس عادت جاهلی] سخنی ناپسند و باطل می‌گویند و البتّه خداوند بسیار باگذشت و آمرزنده است.'),
(5107, 58, 3, 'و کسانی که نسبت به همسران خود ظِهار می‌کنند، سپس از آنچه گفته‌اند، [پشیمان می‌شوند و] برمی‌گردند، کفّاره‌ی آن، این است که پیش از آمیزش با همسر، برده‌ای آزاد کنند. این [فرمانی] است که به آن پند داده می‌شوید و خداوند به آنچه می‌کنید، به خوبی آگاه است.'),
(5108, 58, 4, 'پس کسی که [برده‌ای] نیافت، باید پیش از آمیزش با همسر، دو ماه پی‌درپی روزه بگیرد و کسی که نمی‌تواند، باید شصت مسکین را خوراک بدهد. این برای آن است که به خدا و رسولش ایمان بیاورید و این مرزهای [احکام] الهی است و برای کافران [که به احکام عمل نمی‌کنند]، عذابی دردناک است.'),
(5109, 58, 5, 'کسانی که با خدا و پیامبرش مخالفت و دشمنی می‌کنند، خوار و سرنگون می‌شوند، همان گونه که پیشینیانشان خوار و سرنگون شدند و همانا ما نشانه‌های روشنی نازل کردیم و برای کافران عذابی خوارکننده است.'),
(5110, 58, 6, 'روزی که خداوند همه آنان را برمی‌انگیزد، آن‌گاه به آنچه کرده‌اند، آگاهشان می‌سازد. [اعمالی که] خدا همه‌ی آنها را برشمرده است و آنها فراموشش کرده‌اند و خدا بر هر چیز گواه است.'),
(5111, 58, 7, 'آیا ندیدی که خداوند آنچه را در آسمان‌ها و زمین است، می‌داند؟ هیچ نجوایی میان سه نفر رخ نمی‌دهد، مگر آن که او چهارمین آنهاست و نه میان پنج نفر، مگر آن که او ششمین آنان است و نه کمتر از آن و نه بیشتر، مگر این که هر جا باشند، او با آنهاست. سپس آنان را در روز قیامت از آنچه کرده‌اند، آگاه می‌گرداند. خداوند به هر چیزی بسیار داناست.'),
(5112, 58, 8, 'آیا نظر نکردی به کسانی که از سخنان درِ گوشی نهی شدند؟ سپس باز به کاری که از آن منع شده بودند، بازگشتند و برای گناه و ستم و نافرمانیِ پیامبر با یکدیگر نجوا می‌کنند و [با این حال] هنگامی که نزد تو می‌آیند، کلماتی برای تحیت و سلام بر تو به کار می‌برند که [حتّی] خدا تو را با آن تحیت و سلام نگفته است و با خود می‌گویند: «پس چرا خداوند ما را به سبب آنچه می‌گوییم، عذاب نمی‌کند؟» دوزخ برای آنان بس است که وارد آن می‌شوند. پس چه بد سرانجامی است!'),
(5113, 58, 9, 'ای کسانی که ایمان آورده‌اید! هرگاه نجوا می‌کنید، به گناه و ستم و نافرمانیِ پیامبر نجوا نکنید و به کار نیک و تقوا نجوا کنید! و از [نافرمانی] خداوندی که به سویش محشور می‌شوید، پروا کنید.'),
(5114, 58, 10, 'سخنان درِ گوشی از سوی شیطان است، تا مؤمنان را اندوهگین کند. امّا هیچ زیانی به آنان نمی‌رساند، مگر به اجازه‌ی خداوند. پس مؤمنان تنها بر خدا توکل کنند.'),
(5115, 58, 11, 'ای کسانی که ایمان آورده‌اید! چون به شما بگویند: «در مجالس جا باز کنید.» پس جا باز کنید، تا خدا نیز برای شما گشایش دهد و هرگاه بگویند: «برخیزید!» پس بی‌درنگ برخیزید. خدا از میان شما، مؤمنان را به درجه‌ای و دانشمندان را به درجاتی بالا می‌برد و خداوند به آنچه می‌کنید، به خوبی آگاه است.'),
(5116, 58, 12, 'ای مؤمنان! هرگاه خواستید با پیامبر خصوصی گفتگو کنید، پیش از نجوای خود، صدقه‌ای بدهید. این برای شما بهتر و پاکیزه‌تر است و اگر [مالی] نیافتید، [می‌توانید به گفتگو بنشینید.] پس خداوند آمرزنده و مهربان است.'),
(5117, 58, 13, 'آیا نگران شدید از این که پیش از نجوایتان صدقه‌هایی بپردازید؟ حال که چنین نکردید و خداوند از شما درگذشت. پس نماز به پا دارید و زکات بپردازید و خدا و رسولش را اطاعت کنید و خداوند به آنچه می‌کنید، آگاه است.'),
(5118, 58, 14, 'آیا ندیدی کسانی را که با گروهی که مورد خشم خداوند بودند، طرح دوستی ریختند؟ آنها نه از شما هستند و نه از آنان و با این که خود می‌دانند، به دروغ سوگند می‌خورند [که از شما هستند].'),
(5119, 58, 15, 'خدا برای آنان عذاب سختی آماده کرده است، زیرا کاری می‌کردند که بسیار بد است.'),
(5120, 58, 16, 'سوگندهایشان را سپر ساختند و مردم را از راه خدا بازداشتند، پس عذابی خوارکننده دارند.'),
(5121, 58, 17, 'هرگز دارایی و فرزندانشان، در برابر خداوند، برای آنان هیچ سودی نخواهد داشت. آنها همدم آتشند، همواره در آنجا ماندگارند.'),
(5122, 58, 18, 'روزی که خدا همگی آنان را برمی‌انگیزد و همانگونه که برای شما سوگند یاد می‌کنند، برای خداوند سوگند می‌خورند و می‌پندارند که آنان بر چیزی [استوار] هستند. بدانید که آنان همان دروغگویانند.'),
(5123, 58, 19, 'شیطان بر آنان چیره شده است، پس یاد خدا را از خاطرشان برده است. آنان حزب شیطانند. آگاه باشید که حزب شیطان همان زیانکارانند.'),
(5124, 58, 20, 'کسانی که با خدا و پیامبرش دشمنی می‌کنند، آنان در زمره‌ی خوارترینِ مردمند.'),
(5125, 58, 21, 'خداوند حکم کرده است که: «همانا من و پیامبرانم چیره خواهیم شد.» خداوند نیرومند شکست‌ناپذیر است.'),
(5126, 58, 22, 'هیچ گروهی را نمی‌یابی که به خدا و روز قیامت ایمان داشته باشند و [در همان حال] با کسی که با خدا و پیامبرش دشمنی کرده است، مهرورزی کنند. حتّی اگر پدران یا پسران یا برادران یا خویشانشان باشند. آنها کسانی هستند که خداوند، ایمان را در دل‌هایشان ثابت فرموده و با روحی از جانب خویش نیرومندشان ساخته و آنان را در باغ‌هایی [بهشتی] که نهرها از پای [درختان] آنها جاری است، وارد می‌کند. در آنجا جاودانه هستند. خداوند از آنان خشنود است و آنان نیز از او خشنودند. آنان حزب خدا هستند. آگاه باشید که تنها حزب خدا رستگارند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمان‌ها و آنچه در زمین است، برای خدا تسبیح می‌کنند [و او را به پاکی می‌ستایند] و اوست شکست‌ناپذیر حکیم.'),
(5128, 59, 2, 'اوست که کافرانِ اهل کتاب را برای نخستین بار از خانه‌هایشان بیرون راند. با آن که شما گمان نداشتید که آنان بیرون روند و [خودشان] گمان می‌کردند که قلعه‌ها و حصارهایشان بازدارنده‌ی آنان در برابر خداوند است. امّا [قهر] خدا از جایی که گمان نمی‌کردند، به سراغشان آمد و در دل‌هایشان ترس و وحشت افکند، [به گونه‌ای که] خانه‌هایشان را با دست خویش و با دست مؤمنان ویران می‌کردند. پس ای صاحبان بصیرت! عبرت بگیرید.'),
(5129, 59, 3, 'و اگر خداوند، فرمان ترک خانمان را بر آنان لازم و مقرّر نکرده بود، قطعاً در همین دنیا عذابشان می‌کرد و البتّه در آخرت، عذاب آتش دارند.'),
(5130, 59, 4, 'این کیفر به خاطر آن است که آنان با خدا و پیامبرش دشمنی کردند و هرکس با خدا دشمنی کند، پس [بداند که] خداوند سخت‌کیفر است.'),
(5131, 59, 5, '[ای مسلمانان!] آنچه از درخت خرما بریدید، یا آن را بر ریشه‌ها و تنه‌هایش واگذاشتید، همه به خواست خداوند بود و [خداوند می‌خواست] تا نافرمانان بدکار را خوار گرداند.'),
(5132, 59, 6, 'و اموالی که خداوند [در پی غلبه بر یهود و بیرون راندن آنها از دیارشان،‌] به پیامبرش بازگرداند، [به شما تعلّق نمی‌گیرد، زیرا شما برای به دست آوردن آن زحمتی نکشیده‌اید،] نه اسبی بر آن تاختید و نه شتری راندید، بلکه خداوند، پیامبرانش را بر هر که بخواهد، مسلّط می‌سازد و خداوند بر هر کاری تواناست.'),
(5133, 59, 7, 'آنچه را خداوند از [دارایی و زمین‌های] اهل آبادی‌ها به پیامبرش بازگرداند، ویژه‌ی خدا و پیامبر و خویشان [پیامبر] و یتیمان و مستمندان و در راه ماندگان آنهاست. تا [این اموال] تنها میان ثروتمندان شما در گردش نباشد و آنچه پیامبر برای شما آورده، بگیرید و آنچه شما را از آن بازداشته، ترک کنید و از [نافرمانی] خدا پروا کنید که خداوند سخت‌کیفر است.'),
(5134, 59, 8, '[بخشی از این غنیمت‌ها] از آنِ مهاجران فقیری است که از خانه‌ها و اموال خود بیرون رانده شدند و در پی بخشش و خشنودی الهی هستند و خدا و پیامبرش را یاری می‌کنند. آنان همان راستگویانند.'),
(5135, 59, 9, 'و [نیز برای انصار است،] کسانی که پیش از مهاجران در [مدینه،] سرای هجرت و ایمان، جای گرفتند و کسانی را که [از مکه] به سویشان هجرت کردند، دوست می‌دارند و در دلشان، به آنچه به مهاجران داده شده است، نیاز و چشم‌داشتی احساس نمی‌کنند و اگر چه خود شدیداً در فقر هستند؛ ولی مهاجران را بر خود مقدّم می‌دارند و کسانی که از شرّ بخل و آز نفس خویش در امان بمانند، آنان همان رستگارانند.'),
(5136, 59, 10, 'و [نیز] کسانی که پس از مهاجران و انصار آمدند و می‌گویند: «پروردگارا! ما و برادرانمان را که در ایمان بر ما پیشی گرفته‌اند، بیامرز! و در دل‌های ما کینه‌ای از مؤمنان قرار مده! پروردگارا! تو رئوف و مهربانی!»'),
(5137, 59, 11, 'آیا به منافقان نظر نکردی که به برادران خود، کافرانِ اهل کتاب، می‌گویند: «اگر شما رانده شدید، ما نیز با شما بیرون خواهیم آمد و به زیان شما از هیچ کس فرمان نمی‌بریم و اگر با شما جنگیدند، قطعاً ما شما را یاری خواهیم کرد.» و خداوند گواه است که این منافقان دروغگویند.'),
(5138, 59, 12, 'همانا اگر [کافران] رانده شوند، با آنان بیرون نخواهند رفت و اگر مورد تهاجم و جنگ قرار گیرند، آنان را یاری نخواهند کرد و اگر [هم] به یاری آنان برخیزند، [هنگام خطر] به آنان پشت خواهند کرد. سپس [هیچ یک از کافران و منافقان] یاری نمی‌شوند.'),
(5139, 59, 13, 'قطعاً ترس و وحشتی که از شما در دل منافقان است، سخت‌تر از هراسی است که آنان از خدا دارند و این به خاطر آن است که آنان گروهی هستند که فهم و معرفتی ندارند.'),
(5140, 59, 14, 'منافقان به صورت یک پارچه با شما نمی‌جنگند، جز در مناطق حفاظت شده، یا از پشت دیوارها. درگیری آنان میان خودشان سخت است. تو آنان را متّحد می‌پنداری، در حالی که دل‌هایشان پراکنده است. این به خاطر آن است که آنان گروهی هستند که نمی‌اندیشند.'),
(5141, 59, 15, '[داستان یهودیان بنی‌نَضیر] مَثَل کسانی است که چندی پیش از آنان بودند، [و با وعده‌های منافقان اغفال شدند.] آنان تلخی و ناکامی انحراف خود را چشیدند و برایشان عذابی دردناک است.'),
(5142, 59, 16, '[فریب‌خوردن اهل‌کتاب از منافقان،] مانند [نیرنگ] شیطان است، آن‌گاه که به انسان گفت: کافر شو!» پس چون کفر ورزید، گفت: «من از تو بیزارم! من از خداوندی که پروردگار جهانیان است، می‌ترسم.»'),
(5143, 59, 17, 'پس سرانجامشان این شد که هردو جاودانه در آتشند و این کیفر ستمگران است.'),
(5144, 59, 18, 'ای کسانی که ایمان آورده‌اید! از خدا پروا کنید و هر کس به آنچه برای فردای [قیامت] خود فرستاده است، بنگرد. از [نافرمانی] خدا پروا کنید که خداوند به آنچه می‌کنید، به خوبی آگاه است.'),
(5145, 59, 19, 'و همچون کسانی نباشید که خدا را فراموش کردند، پس خدا نیز آنان را دچار خودفراموشی کرد. آنان همان فاسقانند.'),
(5146, 59, 20, 'دوزخیان و بهشتیان برابر نیستند. بهشتیانند که رستگارند.'),
(5147, 59, 21, 'اگر این قرآن را بر کوهی فرومی‌فرستادیم، بی‌شک آن کوه را از ترس خداوند، فروتن و فروپاشیده می‌دیدی و ما این مثال‌ها را برای مردم می‌زنیم، شاید بیندیشند.'),
(5148, 59, 22, 'اوست خدایی که معبودی جز او نیست. دانای پنهان و آشکار است. اوست بخشنده و مهربان.'),
(5149, 59, 23, 'اوست خدایی که معبودی جز او نیست. فرمانروا[ی هستی]، منزّه از هر عیب، سلامت‌بخش، ایمِنی‌بخش، چیره و مسلّط، شکست‌ناپذیر، اراده‌اش نافذ و شایسته‌ی بزرگی است. منزّه است خدا از هر چه [برای او] شریک قرار می‌دهند.'),
(5150, 59, 24, 'اوست خداوندِ آفریننده، پدیدآور و صورت‌گر، بهترینِ نام‌ها [و صفات‌] از آنِ اوست. آنچه در آسمان‌ها و زمین است، همواره تسبیح‌گوی اویند و اوست شکست‌ناپذیر و حکیم.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ای کسانی که ایمان آورده‌اید! اگر برای جهاد در راه من و طلب خشنودیم [از وطن] بیرون آمده‌اید، دشمن من و دشمن خود را دوست نگیرید. شما با آنان طرح دوستی می‌افکنید، در حالی که آنان به آنچه از حقّ برای شما آمده است، کفر ورزیده‌اند. آنان پیامبر و شما را به خاطر ایمان به خداوند، پروردگارتان، [از مکه] بیرون کردند. شما دوستی خود را پنهانی به آنان می‌رسانید، در حالی که من به آنچه پنهان و آنچه آشکار می‌کنید، دانا‌ترم و هر کس از شما چنین کند قطعاً از راه راست گم گشته است.'),
(5152, 60, 2, 'اگر بر شما مسلّط شوند، دشمنان [سرسخت] شما خواهند بود و بر شما به بدی دست دراز می‌کنند و زبانشان را به بدگویی شما خواهند گشود و دوست دارند که شما کافر شوید.'),
(5153, 60, 3, 'در روز قیامت، خویشان و فرزندان [کافر] شما، هیچ کدام سودی برایتان نخواهند داشت. [در آن روز، خداوند] میان شما و آنان جدایی خواهد افکند و خداوند به آنچه می‌کنید، بیناست.'),
(5154, 60, 4, 'همانا برای شما در [روش] ابراهیم و کسانی که با او بودند، سرمشق و الگویی نیکوست، آن‌گاه که به قوم خود گفتند: «ما از شما و از آنچه به جای خدا می‌پرستید، بیزاریم. ما به [آیین] شما کفر ورزیدیم و میان ما و شما، دشمنی و کینه‌ی ابدی پدید آمده است، مگر آن که به خدای یگانه ایمان بیاورید.» [آری، ابراهیم و مؤمنان جز اعلام بیزاری سخنی با بت‌پرستان نداشتند،] مگر سخن ابراهیم به پدرش [کسی که سرپرستی او را به عهده داشت که گفت]: «من برای تو آمرزش خواهم خواست ولی برای تو در برابر خدا، اختیار چیزی را ندارم.» پروردگارا! بر تو توکل کردیم و به سوی تو بازگشتیم و بازگشت به سوی توست.'),
(5155, 60, 5, 'پروردگارا! ما را وسیله‌ی آزمایش [و هدف آزار و سلطه‌ی] کافران قرار مده و ما را بیامرز، پروردگارا! تو خود عزیز و حکیمی.»'),
(5156, 60, 6, 'بی‌تردید برای شما در [روش] آنان، سرمشقی نیکوست. برای کسی که به خدا و روز واپسین امید دارد و هر کس روی برتابد، [بداند که‌] خداوند بی‌نیاز و ستوده است.'),
(5157, 60, 7, 'امید است که خدا میان شما و کسانی که با آنان دشمنی داشتید، [به سبب اسلام آوردنشان در فتح مکه،] دوستی برقرار کند و خداوند تواناست و خداوند آمرزنده‌ی مهربان است.'),
(5158, 60, 8, 'خداوند شما را از نیکی و دادگری با کسانی که با شما به خاطر دین نجنگیدند و شما را از دیارتان بیرون نراندند، بازنمی‌دارد. همانا خداوند عدالت‌پیشگان را دوست دارد.'),
(5159, 60, 9, 'خداوند تنها شما را از دوستی ورزیدن با کسانی نهی می‌کند که به خاطر دین با شما جنگیدند و شما را از سرزمین خود بیرون راندند و در بیرون راندن شما همدستی کردند و هر کس با آنان دوستی ورزد، پس اینان همان ستمکارانند.'),
(5160, 60, 10, 'ای کسانی که ایمان آورده‌اید! هرگاه زنان مؤمن [که از شوهرِ کافر خود جدا شده‌اند،] هجرت‌کنان نزد شما آمدند، آنان را [از نظر ایمان] بیازمایید. خداوند به ایمان آنها دانا‌تر است. پس اگر آنها را باایمان تشخیص دادید، آنان را به سوی کافران بازنگردانید. نه این زنان برای آن کافران حلالند و نه آنان برای اینان و آن [مهریه] را که شوهران کافر پرداخت کرده‌اند، به آن کافران بپردازید و بر شما گناهی نیست که با اینان ازدواج کنید، به شرط آن که مهریه‌ی آنان را بپردازید و به عقد زنان کافر [تان که به کفّار پیوسته‌اند،] پای‌بند مباشید. [که آن عقد باطل شده] و آنچه را شما [برای همسران خود که به کافران پناهنده شده‌اند،] هزینه کرده‌اید، [از کافران] بخواهید، همان‌گونه که آنان آنچه را هزینه کرده‌اند، از شما بخواهند. این حکم الهی است که خداوند میان شما مقرّر داشته و خداوند آگاه حکیم است.'),
(5161, 60, 11, 'و اگر برخی از همسران شما با پیوستن به کفّار از دست شما رفتند، [و شما نتوانستید مهریه‌ای را که پرداخته‌اید، از کافران بگیرید،] پس [هرگاه] شما آنان را تعقیب کردید، [و به غنیمتی دست یافتید،] به آنان که همسرانشان [نزد کفّار] رفته‌اند، معادل آنچه خرج [مهریه] کرده‌اند، بپردازید و از خدایی که به او ایمان دارید، پروا کنید.'),
(5162, 60, 12, 'ای پیامبر! هنگامی که زنان باایمان نزد تو آمدند تا با تو بیعت کنند که چیزی را شریک خدا نگیرند و دزدی نکنند و مرتکب زنا نشوند و فرزندانِ خود را نکشند و با دروغ فرزند حرام‌زاده‌ای را که پیش دست و پای آنان است، بر [شوهر] خویش نبندند و در هیچ کار خیری تو را نافرمانی نکنند، پس با آنان بیعت کن و برای آنان از خداوند آمرزش بخواه که خداوند آمرزنده و مهربان است.'),
(5163, 60, 13, 'ای کسانی که ایمان آورده‌اید! گروهی را که خداوند بر آنان خشم گرفته، به سرپرستی [و دوستی و یاری] نگیرید. آنان از آخرت نومیدند، چنان که کافران از [بازگشت] اهل قبور نومیدند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمان‌ها و آنچه در زمین است، برای خدا تسبیح می‌گویند و اوست شکست‌ناپذیر حکیم.'),
(5165, 61, 2, 'ای کسانی که ایمان آورده‌اید! چرا چیزی می‌گویید که عمل نمی‌کنید؟'),
(5166, 61, 3, 'نزد خدا بسیار موجب خشم است که چیزی را بگویید که عمل نمی‌کنید.'),
(5167, 61, 4, 'خداوند کسانی را دوست دارد که صف‌بسته در راه او پیکار می‌کنند، چنان‌که گویی بنایی بسیار مستحکم از سرب هستند.'),
(5168, 61, 5, 'و [یاد کن‌] آن‌گاه که موسی به قوم خود گفت: «ای قوم من! چرا آزارم می‌دهید؟ با این که می‌دانید من فرستاده‌ی خدا به سوی شما هستم.» پس چون [از حقّ] منحرف شدند، خداوند دل‌های آنان را منحرف ساخت و خداوند مردم نافرمانِ بدکار را هدایت نمی‌کند.'),
(5169, 61, 6, 'و [یاد کن‌] آن‌گاه که عیسی پسر مریم به بنی‌اسرائیل گفت: «من فرستاده‌ی خدا به سوی شما هستم و تورات را که پیش از من بوده است، تصدیق می‌کنم و مژده‌دهنده به پیامبری هستم که پس از من خواهد آمد و نامش احمد است.» پس چون [احمد] دلایل روشنی برای آنان آورد، گفتند: «این جادویی آشکار است.»'),
(5170, 61, 7, 'و کیست ستمکارتر از آن که بر خدا دروغ می‌بندد، در حالی که به اسلام دعوت می‌شود و خداوند مردم ستمکار را هدایت نمی‌کند.'),
(5171, 61, 8, 'آنان تصمیم دارند که نور خدا را با دهان‌های خود خاموش کنند، در حالی که خداوند کامل کننده‌ی نور خویش است، هر چند کافران خوش نداشته باشند.'),
(5172, 61, 9, 'اوست که پیامبرش را همراه با هدایت و دین راستین [به سوی مردم] فرستاد تا آن را بر هر چه دین است، غالب سازد، گر چه مشرکان آن را خوش نداشته باشند.'),
(5173, 61, 10, 'ای کسانی که ایمان آورده‌اید! آیا شما را به تجارتی راهنمایی کنم که از عذابی دردناک [در قیامت] رهایتان می‌کند؟'),
(5174, 61, 11, 'به خدا و پیامبرش ایمان آورید و با اموال و جان‌هایتان در راه خدا جهاد کنید که این برای شما بهتر است، اگر بدانید.'),
(5175, 61, 12, '[در این صورت،] خداوند گناهان شما را می‌آمرزد و شما را به باغ‌هایی [بهشتی] که نهرها از زیر [درختان] آن جاری است و به خانه‌هایی پاکیزه و دل‌پسند در بهشت‌های جاویدان وارد می‌کند. این است رستگاری بزرگ!'),
(5176, 61, 13, 'و [نعمت‌های] دیگری که آن را دوست دارید، [به شما عطا می‌کند.] یاری و نصرت و پیروزیِ نزدیک از سوی خداست و [ای پیامبر!] مؤمنان را بشارت بده!'),
(5177, 61, 14, 'ای کسانی که ایمان آورده‌اید! یاوران [دین] خدا باشید، همان‌گونه که عیسی پسر مریم به [یاران ویژه‌اش،] حواریون گفت: «چه کسانی در راه خداوند، یاوران من خواهند بود؟» حواریون گفتند: «ما یاوران [دین] خداییم.» پس گروهی از بنی‌اسرائیل ایمان آوردند و گروهی کافر شدند. پس کسانی را که ایمان آوردند، بر دشمنشان یاری دادیم، تا پیروز شدند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمان‌ها و آنچه در زمین است، برای خداوند تسبیح می‌گویند، فرمانروا[ی هستی]، منزّه از هر عیب، شکست‌ناپذیرِ حکیم.'),
(5179, 62, 2, 'اوست که در میان مردمِ درس‌ناخوانده، پیامبری از خودشان برانگیخت تا آیات الهی را بر آنان بخواند و آنها را [از آلودگی‌ها] پاک سازد و به آنان کتاب و حکمت بیاموزد. اگر چه آنان پیش از این، در گمراهی آشکاری بودند.'),
(5180, 62, 3, 'و [نیز او را برانگیخت برای] مردمی دیگر که هنوز به آنان نپیوسته‌اند، [و در آینده زاده می‌شوند،] و اوست شکست‌ناپذیر حکیم.'),
(5181, 62, 4, 'این [بعثت] لطف و فضل الهی است که به هر کس بخواهد، [و شایسته بداند،] می‌دهد و خداوند صاحب فضل بزرگ است.'),
(5182, 62, 5, 'مَثَل کسانی که تورات بر دوش آنها نهاده شد، امّا زیر بار آن نرفتند، مانند الاغی است که کتاب‌هایی حمل می‌کند، [ولی چیزی از آن نمی‌فهمد.] چه بد است سرگذشت مردمی که آیات خدا را انکار کردند و خداوند مردم ستمگر را هدایت نمی‌کند.'),
(5183, 62, 6, 'بگو: «ای یهودیان! اگر گمان می‌کنید که شما دوستان خدایید، نه سایر مردم، اگر راست می‌گویید، آرزوی مرگ کنید! [تا به پاداش‌هایی که خداوند برای دوستان خود مقرّر کرده، برسید!]»'),
(5184, 62, 7, 'ولی آنان به خاطر آنچه پیش از این کرده‌اند، هرگز آرزوی مرگ نخواهند کرد و خداوند به [حال‌] ستمگران داناست.'),
(5185, 62, 8, '[ای پیامبر!] بگو: «مرگی که از آن می‌گریزید، قطعاً با شما دیدار خواهد کرد. سپس به نزد آن دانای پنهان و آشکار برگردانده می‌شوید، پس او شما را به آنچه کرده‌اید، آگاه خواهد ساخت.»'),
(5186, 62, 9, 'ای کسانی که ایمان آورده‌اید! آن‌گاه که برای نماز روز جمعه ندا داده شد، پس به سوی یاد خدا بشتابید و داد و ستد را رها کنید که این برای شما بهتر است، اگر بدانید.'),
(5187, 62, 10, 'پس هنگامی که نماز پایان یافت، در زمین پراکنده شوید و از فضل الهی طلب کنید و خدا را بسیار یاد کنید، باشد که رستگار شوید.'),
(5188, 62, 11, 'و [برخی از مردم،] چون داد و ستد یا سرگرمی ببینند، [از صفوف نماز] پراکنده و به سوی آن روانه می‌شوند و تو را ایستاده [در حال خواندن خطبه،] رها می‌کنند. [به آنان] بگو: «آنچه [از فضل و برکت] نزد خداست، از سرگرمی و داد و ستد بهتر است و خداوند بهترینِ روزی‌دهندگان است.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'هنگامی که منافقان نزد تو آیند، می‌گویند: «گواهی می‌دهیم که تو پیامبر خدا هستی!» و خدا می‌داند که تو پیامبرش هستی و خدا گواهی می‌دهد که منافقان دروغگویند.'),
(5190, 63, 2, 'سوگندهای خود را سپر ساختند و [مردم را] از راه خدا بازداشتند. به راستی آنچه می‌کردند، ناپسند است.'),
(5191, 63, 3, 'آن [نفاق] برای آن است که آنان ایمان آوردند، سپس کافر شدند، پس بر دل‌هایشان مهر زده شد و از این رو نمی‌فهمند.'),
(5192, 63, 4, 'و هرگاه آنان را ببینی، قیافه آنان تو را بهشگفتی وامی‌دارد واگر سخن بگویند، [به قدری جذّاب و زیباست که] به کلامشان گوش فرامی‌دهی، [امّا از درون،] گویا چوب‌هایی هستند [خشک و بی‌مغز و به هم] تکیه داده شده. [از شدّت ترس] هر ندایی را به زیان خود می‌پندارند. آنان دشمنند. پس از آنان دوری کن! خدا آنان را بکشد! چگونه از حقّ منحرف می‌شوند؟!'),
(5193, 63, 5, 'و هرگاه به آنان گفته شود: «بیایید تا رسول خدا برای شما آمرزش بخواهد.» [از روی تکبّر و تمسخر،] سرهای خود را برمی‌گردانند و آنان را می‌بینی که متکبّرانه [مردم را از گرایش به حقّ] بازمی‌دارند.'),
(5194, 63, 6, 'برای آنها یکسان است که برایشان استغفار کنی یا استغفار نکنی. هرگز خداوند آنان را نمی‌بخشد، زیرا که خداوند مردم نافرمانِ بدکار را هدایت نمی‌کند.'),
(5195, 63, 7, 'آنان همان کسانی هستند که می‌گویند: «به کسانی که نزد رسول خدا هستند، انفاق نکنید تا [از دور او] پراکنده شوند.» در حالی‌که خزانه‌های آسمان‌ها و زمین برای خداوند است؛ ولی منافقان نمی‌فهمند.'),
(5196, 63, 8, 'می‌گویند: «اگر به مدینه بازگردیم، بی‌تردید عزیزترینِ افراد خوارترینِ مردم را بیرون خواهد کرد.» در حالی که عزّت و اقتدار مخصوص خدا و پیامبرش و مؤمنان است؛ ولی منافقان نمی‌دانند.'),
(5197, 63, 9, 'ای کسانی‌که ایمان آورده‌اید! دارایی‌ها و فرزندانتان شما را از یاد خدا غافل نسازد و کسانی که چنین کنند، آنان همان زیانکارانند.'),
(5198, 63, 10, 'و از آنچه روزی شما کرده‌ایم، انفاق کنید، پیش از آن که مرگ هر یک از شما فرا برسد، پس [در آستانه‌ی رفتن] بگوید: «پروردگارا! چرا [مرگ] مرا تا مدّتی اندک به تأخیر نینداختی، تا صدقه [و زکات] دهم و از شایستگان باشم؟!»'),
(5199, 63, 11, 'و هرگز خداوند مرگ کسی را که اجلش فرارسیده، به تأخیر نمی‌اندازد و خداوند به آنچه می‌کنید، آگاه است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'آنچه در آسمان‌ها و آنچه در زمین است، برای خدا تسبیح می‌گویند. فرمانروایی، ویژه‌ی او و ستایش [نیز] ویژه‌ی اوست و او بر همه چیز تواناست.'),
(5201, 64, 2, 'اوست آن که شما را آفرید، پس برخی از شما کافر و بعضی مؤمن شدید و خداوند به آنچه می‌کنید، بیناست.'),
(5202, 64, 3, 'آسمان‌ها و زمین را به حقّ آفرید و شما را [در رحم مادر] صورت‌گری کرد و صورت شما را نیکو قرار داد و بازگشت [همه] به سوی اوست.'),
(5203, 64, 4, 'آنچه را در آسمان‌ها و زمین است، می‌داند و آنچه را پنهان می‌کنید و آنچه را آشکار می‌سازید، می‌داند و خداوند به آنچه در سینه‌هاست، به خوبی داناست.'),
(5204, 64, 5, 'آیا خبر کسانی که پیش از این کفر ورزیدند، به شما نرسیده است؟ پس جزای کار خود را چشیدند و [در آخرت] عذابی دردناک دارند.'),
(5205, 64, 6, 'این [کیفر]، به خاطر آن است که پیامبرانشان همراه با دلایل روشن به سراغ آنها آمده بودند و آنان گفتند: «آیا بشری [مانند خودمان] ما را هدایت کند؟» از این رو کفر ورزیدند و پشت کردند و خداوند [از ایمانشان] بی‌نیازی نمود و خداوند بی‌نیاز ستوده است.'),
(5206, 64, 7, 'کسانی که کافر شدند، گمان کردند که هرگز برانگیخته نخواهند شد. بگو: «آری، به پروردگارم سوگند که همه شما برانگیخته خواهید شد، سپس شما را به آنچه کرده‌اید، خبر می‌دهند و این بر خداوند آسان است.»'),
(5207, 64, 8, 'پس، به خدا و پیامبرش و نوری [چون قرآن] که فروفرستادیم، ایمان آورید و [بدانید که] خداوند به آنچه می‌کنید، آگاه است.'),
(5208, 64, 9, '[یاد کن] روزی که خداوند شما را برای روز جمع شدن [در قیامت] گرد می‌آورد. آن [روز]، روزِ حسرت و پشیمانی است و هر کس به خداوند ایمان آورد و کار شایسته کند، [خدا] بدی‌هایش را می‌پوشاند و او را به بهشت‌هایی که از زیر [درختان] آنها نهرها جاری است، وارد می‌کند. در آنجا جاودانه‌اند. این است رستگاری بزرگ.'),
(5209, 64, 10, 'و کسانی که کفر ورزیدند و آیات ما را تکذیب کردند، آنان اهل دوزخند. در آنجا جاودانه‌اند و چه بد سرانجامی است!'),
(5210, 64, 11, 'هیچ مصیبتی به کسی نمی‌رسد، جز با اذن خدا و هر کس به خداوند ایمان آورد، او دلش را [به صبر و شکیبایی] هدایت می‌کند و خداوند به همه چیز داناست.'),
(5211, 64, 12, 'و خدا را اطاعت کنید و پیامبر را پیروی نمایید. پس اگر روی بگردانید، [بدانید که] بر رسول ما جز پیام‌رسانیِ آشکار [وظیفه‌ی دیگری] نیست.'),
(5212, 64, 13, 'خداوند، [معبودی است] که جز او معبود دیگری نیست و مؤمنان تنها بر او توکل می‌کنند.'),
(5213, 64, 14, 'ای کسانی که ایمان آورده‌اید! برخی از همسران و فرزندانتان برای شما دشمنند، [و شما را از راه خدا بازمی‌دارند.] پس از آنان بر حذر باشید و اگر [از آزار و رنجی که به شما می‌دهند،] چشم بپوشید و سرزنش آنان را ترک کنید و از آنان بگذرید، پس [بدانید که] خداوند آمرزنده‌ی مهربان است.'),
(5214, 64, 15, 'همانا اموال و فرزندان شما وسیله‌ی آزمایشند و خداوند است که پاداش بزرگ نزد اوست.'),
(5215, 64, 16, 'پس هر قدر می‌توانید، از خداوند پروا کنید و [سخن او را] بشنوید و اطاعت کنید و انفاق کنید که برای شما بهتر است و کسانی که از شرّ بخل و آز نفس خویش در امان بمانند، آنان همان رستگارانند.'),
(5216, 64, 17, 'اگر به خداوند وام دهید، وامی نیکو، آن را برای شما چندین برابر می‌کند و شما را می‌آمرزد و خداوند سپاسگزارِ بردبار است.'),
(5217, 64, 18, 'دانای پنهان و آشکار و نفوذناپذیر حکیم است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'ای پیامبر! چون خواهید زنان را طلاق دهید، در زمان عدّه‌شان، طلاق دهید و حساب [ایام] عدّه را نگه دارید و از [نافرمانی] خدایی که پروردگار شماست، پروا کنید. [در ایام عدّه،] نه شما آنان را از خانه‌هایشان بیرون کنید و نه آنان بیرون روند، مگر آن که مرتکب کار زشت آشکاری شوند، [که بیرون‌کردنشان از خانه جایز است.] این [دستورات]، حدود خداست و هر کس از حدود الهی تجاوز کند، به خویش ستم کرده است. تو نمی‌دانی، شاید خداوند بعد از این، امر تازه‌ای پدید آورد [تا وسیله‌ی صلح و سازش میان آنان شود.]'),
(5219, 65, 2, 'پس چون به پایان مدّت [عدّه] نزدیک شدند، یا [با رجوع به آنان،] به طرز شایسته و پسندیده‌ای نگاهشان دارید و یا [با پرداخت مهریه و سایر حقوق،] به طرز شایسته‌ای از آنان جدا شوید و [هنگام طلاق،] دو [شاهد] عادل از میان خود گواه بگیرید و گواهی را برای خدا به‌پا دارید. هر کس که به خدا و روز قیامت ایمان دارد، این‌گونه پند داده می‌شود و هر کس که از خدا پروا کند، خداوند برای او راه بیرون شدن و رهایی [از مشکلات و تنگناها را] قرار می‌دهد،'),
(5220, 65, 3, 'و او را از جایی که گمان ندارد، روزی می‌دهد و هر کس بر خدا توکل کند، او برایش بس است. خداوند فرمان خود را [به انجام] می‌رساند. همانا خداوند برای هر چیز اندازه‌ای نهاده است.'),
(5221, 65, 4, 'و زنانی که از عادت ماهانه مأیوسند، اگر در وضع آنان شک کنید [که این امر به خاطر سنّ است یا بیماری یا بارداری]، پس عدّه‌ی آنان سه ماه است و هم چنین است حکم زنانی که حیض ندیده‌اند، [با آن که در سنّ حیض هستند.] و [پایان] عدّه‌ی زنان باردار، زایمان آنهاست و هر کس از خدا پروا کند، خداوند برای او در کارش آسانی پدید می‌آورد.'),
(5222, 65, 5, 'این فرمان خداوند است که آن را به سوی شما فروفرستاده است و هر کس از خدا پروا کند، خداوند بدی‌هایش را می‌زداید و اجرش را بزرگ می‌گرداند.'),
(5223, 65, 6, 'زنان طلاق داده را [تا پایان زمان عدّه،] در همان جا که خود سکونت دارید و در توانایی شماست، سکونت دهید و به آنان آسیب نرسانید، تا کار را بر آنان تنگ کنید [و مجبور به ترک خانه شوند] و اگر باردار باشند، تا زمانی که بزایند، نفقه‌ی آنان را بدهید. پس اگر نوزادان شما را شیر دادند، مزدشان را بدهید و میان خود [درباره‌ی نوزاد] به نیکی مشورت و توافق کنید و اگر [توافق] به دشواری کشید، زن دیگری او را شیر دهد.'),
(5224, 65, 7, 'هر کس که دارای وسعت [مالی] است، باید طبق وسع خود نفقه دهد و کسی‌که روزی بر او تنگ شده، باید از آنچه خداوند به او داده [به اندازه‌ی توان خود] انفاق کند. زیرا خداوند کسی را جز به اندازه‌ی آنچه به او داده است، تکلیف نمی‌کند. به زودی خداوند پس از سختی، آسانی و فراخی پدید می‌آورد.'),
(5225, 65, 8, 'و چه بسیار آبادی‌ها که [مردمش] از فرمان پروردگار خود و فرستادگانش سر باز زدند، پس ما سخت به حساب آنان رسیدیم و به عذابی ناشناخته عذابشان کردیم.'),
(5226, 65, 9, 'پس آنان ثمره‌ی تلخ کار خود را چشیدند و سرانجامِ کارشان زیان بود.'),
(5227, 65, 10, 'خداوند برای آنان عذابی سخت آماده کرده است، پس ای خردمندان مؤمن! از خدا پروا کنید. همانا خداوند برای شما [اسباب] یادآوری فرستاده است.'),
(5228, 65, 11, 'پیامبری که آیات روشنگر الهی را بر شما تلاوت می‌کند تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، از تاریکی‌ها به سوی نور بیرون آورد و کسانی که به خدا ایمان آورند و کار شایسته کنند، خداوند آنان را به باغ‌هایی که نهرها از زیر [درختان] آن جاری است، وارد می‌کند. برای همیشه در آنجا جاودانه‌اند. بی‌گمان خداوند روزی آنان را نیکو ساخته است.'),
(5229, 65, 12, 'خداست آن که هفت آسمان را آفرید و از زمین نیز همانند آن‌ها. همواره فرمان او میان آن‌ها نازل می‌شود تا بدانید که خداوند بر هر چیزی تواناست و دانش خدا فراگیرِ همه چیز است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ای پیامبر! چرا چیزی را که خداوند برای تو حلال کرده، به خاطر جلب خشنودی همسرانت، بر خود حرام می‌کنی؟ و خداوند آمرزنده و مهربان است.'),
(5231, 66, 2, 'همانا خداوند، [راه‌] رهایی [از تعهّد] سوگندهایتان را [با پرداخت کفّاره،] برای شما مقرّر کرده است و خداوند سرپرست شماست و او دانای حکیم است.'),
(5232, 66, 3, 'و هنگامی که پیامبر به یکی از همسرانش سخنی را به راز گفت و او آن راز را فاش کرد و خداوند، پیامبر را از افشای آن آگاه نمود، پیامبر بخشی از آن [راز افشا شده‌] را به آن زن گوشزد کرد و از گفتن بخشی دیگر خودداری نمود. پس چون به آن زن خبر داد، گفت: «چه کسی تو را از این [که من راز را فاش کردم،] خبردار کرد؟» پیامبر فرمود: «[خداوند] دانای آگاه به من خبر داد.»'),
(5233, 66, 4, 'اگر شما دو زن، به پیشگاه خدا توبه کنید، [به سود شماست.] زیرا دل‌های شما منحرف گشته است و اگر در برابر پیامبر یاور هم باشید، [کاری از پیش نخواهید برد.] زیرا خداوند و جبرئیل و صالح مؤمنان یار اویند و فرشتگان هم پس از آن پشتیبان او هستند.'),
(5234, 66, 5, 'اگر [پیامبر] شما را طلاق دهد، چه بسا پروردگارش برای او همسرانی بهتر از شما جانشین سازد که مسلمان، مؤمن، فرمان‌بر، توبه‌کار، عبادت‌پیشه و روزه‌گیر باشند. چه بیوه و چه دوشیزه.'),
(5235, 66, 6, 'ای کسانی که ایمان آورده‌اید! خود و خانواده‌ی خود را از آتشی که هیزم آن مردمان و سنگ‌ها هستند، نگاه دارید. [آتشی که] بر آن فرشتگانی درشت‌خو و سخت‌گیر نگهبانند، از آنچه خدا به آنان فرمان داده، سرپیچی نمی‌کنند و آنچه را فرمان یابند، همواره انجام می‌دهند.'),
(5236, 66, 7, '[آنان به کافران می‌گویند:] ای کسانی که کافرشدید! امروز عذرخواهی نکنید که جزای شما تنها همان عملکرد شماست.»'),
(5237, 66, 8, 'ای کسانی که ایمان آوردید! به درگاه خدا توبه کنید، توبه‌ای خالصانه. امید است که پروردگارتان بدی‌های شما را بپوشاند و شما را به باغ‌هایی [بهشتی] که نهرها از زیر [درختان] آن جاری است، وارد کند. روزی که خداوند، پیامبر و کسانی را که با او ایمان آورده‌اند، خوار نکند. نورشان پیشاپیش آنها و از سوی راستشان می‌شتابد، می‌گویند: «پروردگارا! نور ما را کامل کن و ما را بیامرز! همانا تو بر هر چیز توانایی!»'),
(5238, 66, 9, 'ای پیامبر! با کافران و منافقان جهاد کن و بر آنان سخت گیر! و جایگاه آنان دوزخ است و بد بازگشت‌گاهی است!'),
(5239, 66, 10, 'خداوند برای کسانی‌که کفر ورزیدند، زن نوح و زن لوط را مثال زده است که این دو، تحت سرپرستی دو بنده‌ی شایسته از بندگان ما بودند؛ ولی به آن دو خیانت کردند. پس در برابر [کیفر] خداوند برای آن دو زن، هیچ کاری از آن دو [پیامبر] ساخته نبود و به آنان گفته شد: «با [دیگر] واردشوندگان، به آتش [دوزخ] درآیید!»'),
(5240, 66, 11, 'و خداوند برای کسانی که ایمان آوردند، به همسر فرعون مثَل زده است. آن‌گاه که گفت: «پروردگارا! نزد خود در بهشت برای من خانه‌ای بساز! و مرا از فرعون و کردار او نجات بده! و مرا از [این] گروه ستمگر رهایی بخش!»'),
(5241, 66, 12, 'و [خداوند به] مریم دختر عمران [نیز مثَل زده است.] زنی که دامان خود را پاک نگاه داشت، پس ما از روح خود در او دمیدیم [تا عیسی در دامان او متولّد شد،] و او کلمات پروردگارش و کتاب‌های او را تصدیق کرد و از فرمان‌برداران بود.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'پر خیر و برکت و بلند مرتبه است آن [خدایی] که فرمانروایی به دست اوست و او بر هر کاری تواناست.'),
(5243, 67, 2, 'او که مرگ و زندگی را آفرید تا شما را بیازماید که کدام یک از شما کار بهتری می‌کنید و اوست عزیز و آمرزنده.'),
(5244, 67, 3, 'همان کسی که هفت آسمان را بر فراز یکدیگر آفرید. در آفرینش [خداوند] رحمان هیچ نابسامانی و خللی نمی‌بینی. بار دیگر دیده باز کن، آیا هیچ شکاف و رخنه‌ای می‌بینی؟'),
(5245, 67, 4, 'باز، پی در پی چشم خود را برگردان [خواهی دید که] چشم در حالی که خسته و ناتوان است، به سوی تو بازمی‌گردد [بی آن که نقص و خللی مشاهده کند].'),
(5246, 67, 5, 'و همانا ما آسمان دنیا را با چراغ‌هایی آراستیم و آن را وسیله‌ی راندن شیطان‌ها قرار دادیم و برای آنها عذاب فروزان آماده کردیم.'),
(5247, 67, 6, 'و برای کسانی که به پروردگارشان کفر ورزند، عذاب جهنّم است که بد بازگشت‌گاهی است.'),
(5248, 67, 7, 'هنگامی که [کافران] به دوزخ پرتاب شوند، خروش هولناک و دلخراش آن را می‌شنوند، در حالی که جوشش و فوران دارد.'),
(5249, 67, 8, 'نزدیک است [دوزخ] از شدّت خشم، پاره پاره شود. هرگاه گروهی [از دوزخیان] در آن افکنده شوند، نگهبانان دوزخ از آنان می‌پرسند: «آیا هشداردهنده‌ای به سراغ شما نیامد؟»'),
(5250, 67, 9, '[دوزخیان] می‌گویند: «چرا! به راستی هشداردهنده‌ای به سراغ ما آمد؛ ولی ما تکذیب کردیم و گفتیم: خداوند چیزی نازل نکرده است و شما فقط در گمراهی بزرگی هستید.»'),
(5251, 67, 10, 'و می‌گویند: «اگر ما [دعوت پیامبران را] می‌شنیدیم، یا می‌اندیشیدیم، در میان اهل آتش نبودیم.»'),
(5252, 67, 11, 'پس به گناه خود اعتراف می‌کنند، پس لعنت بر اهل آتش باد!'),
(5253, 67, 12, 'همانا برای کسانی که از پروردگارشان در نهان می‌ترسند، آمرزش و پاداشی بزرگ است.'),
(5254, 67, 13, 'گفتارتان را پنهان کنید یا آشکار سازید، او به آنچه در سینه‌هاست، داناست.'),
(5255, 67, 14, 'آیا کسی که آفرید، [از حالات آفریده‌ی خود] آگاه نیست؟ در حالی که او باریک‌بین و آگاه است.'),
(5256, 67, 15, 'اوست که زمین را برای شما رام و آرام ساخت، پس بر شانه‌های آن گام نهید و از روزی خدا بخورید. [و بدانید که] برانگیخته شدن فقط به سوی اوست.'),
(5257, 67, 16, 'آیا خود را از [قهر] کسی که در آسمان است، ایمن می‌دانید که شما را در زمین فروبرد، پس ناگاه زمین به لرزه افتد؟'),
(5258, 67, 17, 'یا خود را از [قهر] کسی که در آسمان است، ایمن می‌دانید که تندبادی از سنگریزه بر شما فروفرستد؟ پس به زودی خواهید دانست که هشدار من چگونه است.'),
(5259, 67, 18, 'همانا کسانی که پیش از آنان بودند، [پیامبران را] تکذیب کردند، پس [بنگر] کیفر من چگونه بود.'),
(5260, 67, 19, 'آیا پرندگان را نمی‌بینند که بر فراز آنان، بال‌های خود را گسترده و [گاه] جمع می‌کنند؟ هیچ‌کس جز [خداوند] رحمان آنها را [در آسمان] نگاه نمی‌دارد. البتّه او به هر چیز بیناست.'),
(5261, 67, 20, 'کیست آن کسی که سپاه شماست و شما را در برابر [قهر خداوند] رحمان یاری می‌دهد؟ کافران تنها دچار فریبند.'),
(5262, 67, 21, 'کیست آن که اگر خداوند روزی‌اش را بازدارد، به شما روزی دهد؟ بلکه آنان در سرکشی و فرار [از حقّ]، لجاجت می‌کنند.'),
(5263, 67, 22, 'پس آیا کسی که به رو افتاده [و خزیده] راه می‌رود، هدایت‌یافته‌تر است؟ یا کسی که ایستاده بر راه راست می‌رود؟'),
(5264, 67, 23, '[ای پیامبر!] بگو: «اوست کسی که شما را پدید آورد و برای شما گوش و چشم‌ها و دل‌ها قرار داد. چه اندک سپاسگزاری می‌کنید!»'),
(5265, 67, 24, 'بگو: «اوست کسی که شما را در زمین آفرید و به سوی او محشور می‌شوید.»'),
(5266, 67, 25, 'و [کافران] می‌گویند: «اگر راست می‌گویید، این وعده کی فرامی‌رسد؟»'),
(5267, 67, 26, 'بگو: «علم [به زمان قیامت،] تنها نزد خداوند است و من تنها بیم‌دهنده‌ای آشکارم.»'),
(5268, 67, 27, 'پس چون آن [دوزخ موعود] را نزدیک ببینند، چهره‌های کافران، زشت و گرفته گردد و [فرشتگان به آنها] می‌گویند: «این است آنچه [در دنیا به مسخره] می‌خواستید!»'),
(5269, 67, 28, '[ای پیامبر!] بگو: «به من خبر دهید، اگر خدا من و کسانی را که با من هستند، نابود کند، یا بر ما رحم نماید، چه کسی کافران را از عذابی دردناک پناه می‌دهد؟»'),
(5270, 67, 29, 'بگو: «او [خدای] رحمان است. ما به او ایمان آورده‌ایم و بر او توکل کرده‌ایم. پس به زودی خواهید دانست، کیست آن که در گمراهی آشکار است.»'),
(5271, 67, 30, 'بگو: «به من خبر دهید اگر آب [مورد استفاده‌ی] شما در زمین فرو رود، کیست که آب روان و گوارا برای شما بیاورد؟»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون. به قلم و آنچه می‌نویسند، سوگند،'),
(5273, 68, 2, 'که تو به لطف پروردگارت دیوانه نیستی.'),
(5274, 68, 3, 'و همانا برای تو پاداشی بی‌پایان است.'),
(5275, 68, 4, 'و راستی که تو اخلاق بسیار بزرگوارانه‌ای داری.'),
(5276, 68, 5, 'پس به زودی می‌بینی و آنها نیز می‌بینند،'),
(5277, 68, 6, 'که کدام یک از شما گرفتار جنونید؟ [تو یا دشمنانت؟]'),
(5278, 68, 7, 'بی‌تردید پروردگارت، خود بهتر می‌داند چه کسی از راه او گمراه شده و او به ره‌یافتگان داناتر است.'),
(5279, 68, 8, 'پس، از تکذیب‌کنندگان اطاعت مکن!'),
(5280, 68, 9, 'آنان دوست دارند که تو سازش کنی و آنان نیز با تو سازش کنند.'),
(5281, 68, 10, 'و از هر فرومایه که بسیار سوگند یاد می‌کند، پیروی مکن!'),
(5282, 68, 11, '[آن که] عیب‌جوست و برای سخن‌چینی در جنب‌وجوش است.'),
(5283, 68, 12, 'سخت بازدارنده [مردم‌] از کار خیر و متجاوز و گنه‌کار است.'),
(5284, 68, 13, 'از این‌ها گذشته، خشن و گستاخ و بی‌تبار است.'),
(5285, 68, 14, 'به خاطر آن که او مال فراوان و فرزندان نیرومند دارد.'),
(5286, 68, 15, 'هرگاه آیات ما بر او تلاوت شود، می‌گوید: «[این،] افسانه‌های پیشینیان است!»'),
(5287, 68, 16, 'به زودی بر بینی‌اش مهر خواری می‌نهیم.'),
(5288, 68, 17, 'ما مردم [مکه] را آزمودیم، همان گونه که صاحبان آن باغ را آزمودیم، آن‌گاه که سوگند یاد کردند، صبحگاهان میوه‌های باغ را بچینند.'),
(5289, 68, 18, 'و چیزی را [برای نیازمندان] کنار نگذارند.'),
(5290, 68, 19, 'پس هنگامی که در خواب بودند، از سوی پروردگارت آفتی بر گرد آن [باغ] چرخید.'),
(5291, 68, 20, 'پس به صورت باغی درآمد که میوه‌هایش چیده و درختانش سوخته است.'),
(5292, 68, 21, 'بامدادان یکدیگر را ندا دادند'),
(5293, 68, 22, 'که: «اگر قصد چیدن میوه دارید، به سوی کشت‌زار و باغتان حرکت کنید.»'),
(5294, 68, 23, 'پس به راه افتادند، در حالی که آهسته با یکدیگر می‌گفتند'),
(5295, 68, 24, 'که: «مبادا امروز بینوایی در آن [باغ] بر شما وارد شود!»'),
(5296, 68, 25, 'بامدادان، با این قرار که تهی‌دستان را محروم کنند، خود را [بر چیدن میوه‌ها] توانا می‌پنداشتند.'),
(5297, 68, 26, 'پس چون [باغ را سوخته] دیدند، گفتند: «ما راه را گم کرده‌ایم.'),
(5298, 68, 27, 'بلکه ما بی‌بهره مانده‌ایم!»'),
(5299, 68, 28, 'عاقل‌ترینِ آنها گفت: «آیا به شما نگفتم چرا خدا را به پاکی نمی‌ستایید؟ [امّا شما پنداشتید خدا درمانده و شما همه‌کاره‌اید].»'),
(5300, 68, 29, 'گفتند: «پروردگار ما منزّه است! قطعاً ما ستمکار بوده‌ایم.»'),
(5301, 68, 30, 'پس به یکدیگر روی کردند، در حالی که به سرزنش هم می‌پرداختند.'),
(5302, 68, 31, 'گفتند: «وای بر ما که سرکش بوده‌ایم!'),
(5303, 68, 32, 'امید است پروردگارمان [ما را ببخشد و] بهتر از آن را برای ما جایگزین کند. ما به پروردگارمان امیدواریم.»'),
(5304, 68, 33, 'این‌گونه است عذاب! و اگر بدانند، عذاب آخرت بزرگتر است.'),
(5305, 68, 34, 'پرهیزکاران نزد پروردگارشان باغ‌های پر نعمت دارند.'),
(5306, 68, 35, 'پس آیا ما تسلیم‌شدگان را همچون گناهکاران قرار می‌دهیم؟'),
(5307, 68, 36, 'شما را چه شده؟ چگونه داوری می‌کنید؟'),
(5308, 68, 37, 'آیا شما کتابی دارید که در آن می‌خوانید،'),
(5309, 68, 38, 'که در آن [جهان]، هر چه را شما بخواهید و برگزینید، برای شما خواهد بود؟'),
(5310, 68, 39, 'یا مگر برای شما پیمان‌هایی رسا به عهده‌ی ماست که تا روز قیامت، هر چه را حکم کنید، برای شما باشد؟'),
(5311, 68, 40, 'از آنان بپرس که کدام یک از آنان ضامن این [ادّعا] است؟'),
(5312, 68, 41, 'یا برای آنان شریکانی است [که در قیامت به دادشان برسند]؟ پس اگر راست‌گو هستند، شریکانشان را بیاورند.'),
(5313, 68, 42, 'روزی که دشواری [کار بر آنان] آشکار شود و به سجده دعوت شوند؛ ولی نتوانند.'),
(5314, 68, 43, 'دیدگانشان [از ترس] فروافتاده و خواری وجودشان را فراگرفته است. آنان [در دنیا] به سجده دعوت می‌شدند، در حالی که تندرست بودند. [ولی سجده نمی‌کردند.]'),
(5315, 68, 44, 'پس مرا با کسی که [قرآن،] این سخن [خدا] را تکذیب می‌کند، واگذار! ما آنان را از راهی که نمی‌دانند، به تدریج [به سوی عذاب] پیش می‌بریم.'),
(5316, 68, 45, 'و به آنان مهلت می‌دهم. البتّه تدبیر من محکم و استوار است.'),
(5317, 68, 46, 'آیا از آنان مزدی درخواست می‌کنی که پرداخت آن بر آنان سنگین است؟'),
(5318, 68, 47, 'یا [رازهای] نهان نزد آنهاست که آنان [از روی آن] می‌نویسند؟'),
(5319, 68, 48, 'پس برای [تحقّق] فرمان پروردگارت صبر کن و همانند [یونس،] همدم ماهی مباش، [که شتاب در عذاب قومش را خواست و در شکم ماهی گرفتار شد،] آن‌گاه که با دلی پر اندوه [خدا را] ندا داد.'),
(5320, 68, 49, 'اگر رحمت و لطفی از سوی پروردگارش او را در نیافته بود، قطعاً نکوهش شده به صحرایی بی‌گیاه افکنده می‌شد.'),
(5321, 68, 50, 'پس خداوند او را برگزید و او را از شایستگان قرار داد.'),
(5322, 68, 51, 'و کافران، چون قرآن را می‌شنوند، نزدیک است تو را چشم بزنند و می‌گویند: «او دیوانه است.»'),
(5323, 68, 52, 'درحالی که آن [قرآن] جز مایه‌ی تذکّر و پند برای جهانیان نیست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن روز راستین!'),
(5325, 69, 2, 'چه روز راستین!'),
(5326, 69, 3, 'و چه می‌دانی آن روز راستین چیست؟'),
(5327, 69, 4, 'قوم ثَمود و عاد آن حادثه‌ی کوبنده را تکذیب کردند.'),
(5328, 69, 5, 'پس قوم ثمود با عذابی سرکش، نابود شدند.'),
(5329, 69, 6, 'و امّا قوم عاد با بادی سرد و سوزان و بنیان‌کن نابود شدند،'),
(5330, 69, 7, 'خدا هفت شب و هشت روزِ پی‌درپی، آن را بر آنان مسلّط کرد. پس [اگر آنجا بودی،] می‌دیدی که در آن ایام، آن قوم از پا افتاده، گویا تنه‌های نخل توخالی هستند.'),
(5331, 69, 8, 'پس آیا هیچ بازمانده‌ای برای آنان می‌بینی؟'),
(5332, 69, 9, 'و فرعون و کسانی که پیش از او بودند و [مردم] شهرهای زیر و رو شده خطا کردند.'),
(5333, 69, 10, 'پس فرستاده‌ی پروردگارشان را نافرمانی کردند و خداوند آنان را به قهری فزاینده و سخت گرفت.'),
(5334, 69, 11, 'همانا [در زمان نوح،] چون [با اراده ما] آب طغیان کرد، شما را در آن کشتی روان سوار کردیم.'),
(5335, 69, 12, 'تا آن را برای شما مایه‌ی تذکری قرار دهیم و گوشی که شنواست، آن را ضبط کند.'),
(5336, 69, 13, 'پس آن‌گاه که در صور یک بار دمیده شود،'),
(5337, 69, 14, 'و زمین و کوه‌ها [از جای] برداشته شوند و با یک [ضربه‌ی] خردکننده خرد و درهم کوبیده شوند،'),
(5338, 69, 15, 'پس در آن روز، واقعه [بزرگ] روی خواهد داد.'),
(5339, 69, 16, 'و آسمان می‌شکافد و در آن روز از متلاشی می‌گردد.'),
(5340, 69, 17, 'و فرشتگان در اطراف آن هستند، [و منتظر فرمانند.] و آن روز عرش پروردگارت را هشت نفر بالای سر خود می‌برند.'),
(5341, 69, 18, 'آن روز شما [به پیشگاه خدا] عرضه می‌شوید، به‌گونه‌ای که هیچ چیز پوشیده‌ای از شما، پنهان نماند.'),
(5342, 69, 19, 'پس آن که کارنامه‌اش به دست راستش داده شود، می‌گوید: «[بیایید] کارنامه‌ی مرا بگیرید و بخوانید!'),
(5343, 69, 20, 'من می‌دانستم که با حساب خود رو‌به‌رو خواهم شد.»'),
(5344, 69, 21, 'پس او در زندگی رضایت‌بخشی است.'),
(5345, 69, 22, 'در بهشتی برین.'),
(5346, 69, 23, 'که میوه‌هایش در دسترس است.'),
(5347, 69, 24, 'بخورید و بیاشامید. گوارایتان باد! به پاداش کارهایی که در دوران گذشته کرده‌اید.'),
(5348, 69, 25, 'و امّا آن که کارنامه‌اش به دست چپش داده شود، می‌گوید: «ای کاش کارنامه‌ام را به من نمی‌دادند،'),
(5349, 69, 26, 'و نمی‌دانستم حسابم چیست.'),
(5350, 69, 27, 'ای کاش مرگ پایان کارم بود.'),
(5351, 69, 28, 'دارایی من به کارم نیامد.'),
(5352, 69, 29, 'قدرتم از دست رفت.»'),
(5353, 69, 30, 'ندا می‌رسد:] «او را بگیرید و در غل و زنجیرش بکشید!'),
(5354, 69, 31, 'سپس او را در آتش شعله‌ور بیافکنید!'),
(5355, 69, 32, 'سپس او را در زنجیری که درازایش هفتاد ذراع باشد، به بند کشید!'),
(5356, 69, 33, 'که او به خدای بزرگ ایمان نمی‌آورد.'),
(5357, 69, 34, 'و [مردم را] به غذا دادن نیازمندان تشویق نمی‌کرد.'),
(5358, 69, 35, 'پس امروز در این جا دوستی صمیمی ندارد.'),
(5359, 69, 36, 'و غذایی ندارد جز چرک و خونابه،'),
(5360, 69, 37, 'که تنها خطاکاران آن را می‌خورند.»'),
(5361, 69, 38, 'پس سوگند به آنچه می‌بینید،'),
(5362, 69, 39, 'و آنچه نمی‌بینید،'),
(5363, 69, 40, 'همانا آن [قرآن] گفتار فرستاده‌ای ارجمند است.'),
(5364, 69, 41, 'و سخن یک شاعر نیست. چه کم ایمان می‌آورید!'),
(5365, 69, 42, 'و سخن کاهن و پیشگو [هم] نیست. چه کم پند می‌پذیرید!'),
(5366, 69, 43, '[بلکه] فروفرستاده‌ای از جانب پروردگار جهانیان است.'),
(5367, 69, 44, 'و اگر [پیامبر] پاره‌ای از گفته‌های ساختگی را به ما نسبت می‌داد،'),
(5368, 69, 45, 'قطعاً او را با قدرت می‌گرفتیم.'),
(5369, 69, 46, 'سپس رگ قلبش را قطع می‌کردیم.'),
(5370, 69, 47, 'پس هیچ یک از شما نمی‌توانست سپر او شود.'),
(5371, 69, 48, 'و به راستی این [قرآن مایه‌ی] یادآوری برای پرهیزکاران است.'),
(5372, 69, 49, 'و ما خوب می‌دانیم که از میان شما انکارکنندگانی هستند.'),
(5373, 69, 50, 'و بی‌شک [این انکار در قیامت] مایه‌ی حسرت کافران خواهد بود.'),
(5374, 69, 51, 'و این که آن، حقیقت یقینی است.'),
(5375, 69, 52, 'پس به نام پروردگار بزرگت تسبیح گوی!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'درخواست‌کننده‌ای، عذابی واقع‌شدنی را درخواست کرد،'),
(5377, 70, 2, 'که برای کافران است و هیچ بازدارنده‌ای برای آن نیست.'),
(5378, 70, 3, 'و از سوی خداوند صاحب درجات والاست.'),
(5379, 70, 4, 'فرشتگان و روح، در روزی که مقدار آن پنجاه هزار سال است، به سوی او بالا می‌روند.'),
(5380, 70, 5, 'پس صبر کن، صبری نیکو.'),
(5381, 70, 6, 'آنان آن روز را دور می‌بینند.'),
(5382, 70, 7, 'و ما آن را نزدیک می‌بینیم.'),
(5383, 70, 8, 'روزی که آسمان مانند مس گداخته شود.'),
(5384, 70, 9, 'و کوه‌ها مانند پشمِ رنگین [حلاّجی شده] گردد.'),
(5385, 70, 10, 'و هیچ دوستی از دوستش [احوالی] نپرسد.'),
(5386, 70, 11, '[خویشاوندان و دوستان] آنان را نشانشان می‌دهند. [ولی از شدّت هراس به آنان توجه نمی‌کنند!] آن روز گنهکار آرزو می‌کند که ای کاش می‌توانست فرزندانش را در برابر عذاب تاوان دهد و فدای خود کند!'),
(5387, 70, 12, 'و نیز همسر و برادرش را!'),
(5388, 70, 13, 'و بستگانش را که [در دنیا] به او پناه می‌دادند،'),
(5389, 70, 14, '[بلکه آرزو دارد] تمام مردم روی زمین را [فدا کند]، تا او را [از عذاب‌] برهاند.'),
(5390, 70, 15, 'هرگز! همانا آن آتش شعله‌ور است،'),
(5391, 70, 16, 'که پوست سر و بدن را به شدّت برمی‌کند.'),
(5392, 70, 17, 'هر کس را [به حقّ] پشت کرده و روی برتافته، فرامی‌خواند.'),
(5393, 70, 18, 'و [نیز کسی را که مال] گرد آورده و انباشته است.'),
(5394, 70, 19, 'به راستی که انسان سخت آزمند [و بی‌تاب‌] آفریده شده است.'),
(5395, 70, 20, 'هرگاه بدی به او برسد، نالان است.'),
(5396, 70, 21, 'و هرگاه خیری به او برسد، بخیل است.'),
(5397, 70, 22, 'مگر نمازگزاران.'),
(5398, 70, 23, 'آنان که بر نمازشان پیوسته و پایدارند.'),
(5399, 70, 24, 'و آنان که در اموالشان حقّی است معین.'),
(5400, 70, 25, 'برای نیازمندِ خواهنده و بینوای درمانده.'),
(5401, 70, 26, 'و آنان که روز جزا را باور دارند.'),
(5402, 70, 27, 'و آنان که از عذاب پروردگارشان بیمناکند.'),
(5403, 70, 28, 'زیرا از عذاب پروردگارشان ایمنی نیست.'),
(5404, 70, 29, 'و آنان که دامان خود را [از شهوت‌ها] نگه می‌دارند.'),
(5405, 70, 30, 'مگر بر همسران یا کنیزانشان که [در این صورت،] مورد سرزنش نیستند.'),
(5406, 70, 31, 'پس کسانی که [در بهره‌گیری جنسی] فراتر از این را بجویند، آنان همان تجاوزگرانند.'),
(5407, 70, 32, 'و آنان که امانت‌ها و پیمان‌های خود را رعایت می‌کنند.'),
(5408, 70, 33, 'و آنان که به [ادای] گواهی متعهد و پای‌بندند.'),
(5409, 70, 34, 'و آنان که بر نمازشان مواظبت دارند.'),
(5410, 70, 35, 'اینان در بهشت‌ها گرامی داشته می‌شوند.'),
(5411, 70, 36, 'پس کافران را چه شده که سراسیمه به سوی تو می‌شتابند؟'),
(5412, 70, 37, 'از راست و چپ، گروه گروه.'),
(5413, 70, 38, 'آیا هر یک از آنان طمع دارد که به بهشتی پرنعمت درآید؟'),
(5414, 70, 39, 'هرگز! ما آنان را از آن چیز [پستی] که می‌دانند، آفریده‌ایم.'),
(5415, 70, 40, 'به پروردگار مشرق‌ها و مغرب‌ها سوگند می‌خورم که ما تواناییم،'),
(5416, 70, 41, 'بر این که بهتر از آنان را به جای ایشان بیاوریم و هرگز کسی بر ما پیشی نگرفته و [در این کار] ناتوان نیستیم.'),
(5417, 70, 42, 'پس آنان را [به حال خود] واگذار، یاوه بگویند و بازی کنند، تا روزی را که وعده داده شده‌اند، ببینند.'),
(5418, 70, 43, 'روزی که شتابان از قبرها بیرون می‌آیند، چنان که گویی به سوی نشان‌هایی نصب‌شده می‌دوند.'),
(5419, 70, 44, 'چشمانشان فروافتاده و ذلّتی [وصف‌ناپذیر] آنان را فراگرفته است. این است آن روزی که به ایشان وعده داده می‌شد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به سوی قومش فرستادیم [و فرمودیم] که: «قوم خود را پیش از آن که عذابی دردناک به سراغشان بیاید، هشدار بده!»'),
(5421, 71, 2, '[نوح] گفت: «ای قوم من! من برای شما هشداردهنده‌ای آشکارم.'),
(5422, 71, 3, 'خدا را بپرستید و از [نافرمانی] او بپرهیزید و مرا اطاعت کنید.'),
(5423, 71, 4, 'تا خداوند بخشی از گناهانتان را ببخشد و عمر شما را تا زمانی معین به عقب اندازد، [و شما را از مرگ‌های قهرآمیز دور دارد،] اگر می‌دانستید، [توجّه می‌کردید که] هنگامی که اجل خدایی فرارسد، تأخیر نخواهد داشت.»'),
(5424, 71, 5, '[نوح] گفت: «پروردگارا! من قوم خود را شب و روز دعوت کردم.'),
(5425, 71, 6, 'ولی دعوت من جز بر گریز آنان [از حقّ] نیافزود.'),
(5426, 71, 7, 'و البتّه هرگاه که آنان را فراخواندم تا [ایمان بیاورند و] تو آنان را بیامرزی، انگشتانشان را در گوش‌هایشان نهادند و لباس‌هایشان را بر سر کشیدند و [بر سرکشی خود] پافشاری کردند و سخت تکبّر ورزیدند.'),
(5427, 71, 8, 'آن‌گاه من آنان را با صدای بلند دعوت کردم.'),
(5428, 71, 9, 'سپس گاهی آشکارا و گاهی در نهان [با آنان سخن] گفتم.'),
(5429, 71, 10, 'پس گفتم: از پروردگارتان آمرزش بخواهید که او بسیار آمرزنده است.'),
(5430, 71, 11, '[اگر آمرزش بخواهید، بارانِ] آسمان را بر شما ریزش‌کنان می‌فرستد.'),
(5431, 71, 12, 'و شما را با دارایی‌ها و فرزندان یاری می‌کند و برای شما باغ‌هایی قرار می‌دهد و برای شما نهرهایی جاری می‌سازد.'),
(5432, 71, 13, 'شما را چه شده که از عظمت خداوند نمی‌ترسید؟'),
(5433, 71, 14, 'در حالی که او شما را در مراحل گوناگون آفریده است.'),
(5434, 71, 15, 'آیا نمی‌بینید که خداوند چگونه هفت آسمان را بر فراز یکدیگر آفرید؟'),
(5435, 71, 16, 'و میان آنها ماه را تابان ساخت و خورشید را چراغی درخشان قرار داد؟'),
(5436, 71, 17, 'و خداوند شما را همچون گیاهی از زمین رویاند.'),
(5437, 71, 18, 'سپس شما را در همان زمین بازمی‌گرداند و شما را بیرون می‌آورد.'),
(5438, 71, 19, 'و خداوند، زمین را برای شما بستری گسترده قرار داده است.'),
(5439, 71, 20, 'تا در راه‌های گشاده‌ی آن رفت و آمد کنید.»'),
(5440, 71, 21, 'نوح گفت: «پروردگارا! مردم مرا نافرمانی کردند و از کسی پیروی کردند که مال و فرزندش جز بر زیانش نیافزود.'),
(5441, 71, 22, 'و نیرنگی بس بزرگ به کار بستند.'),
(5442, 71, 23, 'و [اشراف قوم] گفتند: «معبودهایتان را رها نکنید و دست از [بت‌های] ودّ و سواع و یغوث و یعوق و نسر برندارید.»'),
(5443, 71, 24, '«و بی‌گمان بسیاری را گمراه کردند، پس [پروردگارا!] ستمکاران را جز گمراهی میافزا!»'),
(5444, 71, 25, 'آنان به سبب گناهانشان غرق گشتند و بی‌درنگ در آتشی [سوزان در برزخ] داخل شدند، پس برای خود، جز خدا یاوری نیافتند.'),
(5445, 71, 26, 'و نوح گفت: «پروردگارا! هیچ یک از کافران را بر زمین باقی مگذار!'),
(5446, 71, 27, 'که اگر آنان را باقی گذاری، بندگانت را گمراه می‌کنند و جز فرزندانی بدکار و ناسپاس نیاورند.'),
(5447, 71, 28, 'پروردگارا! مرا و پدر و مادرم را و هر کس را که با ایمان به خانه‌ام وارد شود و همه‌ی مردان و زنان باایمان را بیامرز! و ستمکاران را جز نابودی نیافزا!»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: «بر من وحی شده است که گروهی از جنّ [به قرآن] گوش دادند. پس گفتند:» «ما قرآنی شگفت شنیدیم.'),
(5449, 72, 2, 'که به سوی رشد هدایت می‌کند. پس به آن ایمان آوردیم و هرگز احدی را شریک پروردگارمان قرار نمی‌دهیم.'),
(5450, 72, 3, 'و [ایمان آوردیم به] این که برتر و بلند است عظمت پروردگارمان، نه همسری گرفته و نه فرزندی.'),
(5451, 72, 4, 'و سفیهان ما درباره‌ی خداوند سخنی ناحقّ می‌گفتند. [که او همسر و فرزند دارد.]'),
(5452, 72, 5, 'و ما گمان می‌کردیم که هرگز جنّ و آدمی بر خداوند دروغ نمی‌بندند.'),
(5453, 72, 6, 'و مردانی از آدمیان به مردانی از جنّ پناه می‌بردند، پس [مردان جنّ] بر طغیان و تباهی‌شان افزودند.'),
(5454, 72, 7, 'و آن آدمیان پنداشتند که خدا هرگز کسی را [به پیامبری] برنمی‌انگیزد، چنان که شما [جنّیان] پنداشتید.'),
(5455, 72, 8, 'و ما [برای یافتن خبر،] به آسمان‌ها نزدیک شدیم، پس آنجا را پر از نگهبانان نیرومند و شَهاب‌ها یافتیم.'),
(5456, 72, 9, 'و ما پیش‌تر برای شنیدن [خبرهای عالم بالا و سخنان فرشتگان] در مراکزی از آسمان می‌نشستیم؛ ولی اکنون هر که بخواهد دزدیده گوش کند، شهابی را در کمین خود می‌یابد.'),
(5457, 72, 10, 'و ما نمی‌دانیم آیا برای کسانی که در زمین هستند، شرّی خواسته شده، یا پروردگارشان برای آنها رهیابی [به نیکی و راه راست‌] را اراده کرده است؟'),
(5458, 72, 11, 'و برخی از ما افراد شایسته‌ای هستند و برخی غیر از آن. ما فرقه‌هایی گوناگونیم.'),
(5459, 72, 12, 'و ما می‌دانیم که هرگز نمی‌توانیم خدا را در زمین درمانده کنیم و هرگز نمی‌توانیم از دسترس قدرت او بیرون برویم.'),
(5460, 72, 13, 'و چون هدایت [قرآن] را شنیدیم، به آن ایمان آوردیم. پس هر کس به پروردگارش ایمان آورد، از هیچ کاستی و ستمی نمی‌ترسد.'),
(5461, 72, 14, 'و برخی از ما اهل تسلیم و بعضی، منحرفند. پس کسانی که تسلیم شدند، آنانند که راه رشد و هدایت را می‌جویند.'),
(5462, 72, 15, 'ولی منحرفان، هیزم دوزخند.»'),
(5463, 72, 16, 'و اگر [انسان‌ها و جنّیان] بر راه [حقّ] پایداری کنند، آنان را با آبی فراوان سیراب می‌کنیم،'),
(5464, 72, 17, 'تا آنان را در آن [رفاه و آسایش] بیازماییم و هر کس از یاد پرورگارش روی برگرداند، [خداوند] او را به عذابی سخت و فزاینده گرفتار می‌سازد.'),
(5465, 72, 18, '«و مساجد ویژه‌ی خداست، پس با خداوند هیچ کس را مخوانید!»'),
(5466, 72, 19, 'و چون [محمّد] بنده‌ی خدا [به عبادت] می‌ایستاد و او را می‌خواند، گروهی انبوه نزدیک بود بر سر او بریزند.'),
(5467, 72, 20, 'بگو: من فقط پروردگارم را می‌خوانم و هیچ کس را شریک او نمی‌گیرم.»'),
(5468, 72, 21, 'بگو: «من مالک هیچ سود و زیانی برای شما نیستم.»'),
(5469, 72, 22, 'بگو: «[اگر او را نافرمانی کنم،] هرگز کسی در برابر خدا مرا پناه نمی‌دهد و جز او هرگز پناهگاهی نخواهم یافت.'),
(5470, 72, 23, '[وظیفه‌ی من] تنها تبلیغی از سوی خدا و رساندن پیام‌های اوست.» و کسانی که خدا و پیامبرش را فرمان نبرند، قطعاً آتش دوزخ برایشان خواهد بود. همیشه در آن جاودانه‌اند.'),
(5471, 72, 24, '[آنان در غفلت به سر می‌برند،] تا آن [عذاب] را که وعده داده می‌شوند، ببینند. پس به زودی خواهند دانست چه کسی یاورش ناتوان‌تر و در شمارش کمتر است.'),
(5472, 72, 25, 'بگو: «من نمی‌دانم که آیا آن وعده‌ای که به شما داده‌اند، نزدیک است، یا پروردگارم برای آن مدتی [دراز] قرار می‌دهد؟'),
(5473, 72, 26, 'او دانای غیب است و هیچ کس را بر غیب خود آگاه نمی‌کند،'),
(5474, 72, 27, 'مگر پیامبری را که از او خشنود باشد که [برای او] از پیش رو و از پشت سرش نگاهبانانی می‌گمارد.'),
(5475, 72, 28, 'تا معلوم دارد [که فرستادگان،] پیام‌های پروردگارشان را رسانده‌اند و او به آنچه نزد آنان است، احاطه دارد و هر چیزی را با عدد، شمرده است.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ای جامه به خود پیچیده!'),
(5477, 73, 2, 'شب را [به نماز] برخیز! مگر اندکی [برای استراحت].'),
(5478, 73, 3, 'نیمی از آن، یا اندکی از آن [نیمه] کم کن،'),
(5479, 73, 4, 'یا بر آن بیافزای! و قرآن را شمرده، روشن و با دقّت بخوان!'),
(5480, 73, 5, 'ما سخنی سنگین و گرانمایه بر تو فرود خواهیم آورد.'),
(5481, 73, 6, 'بی‌گمان عبادت شبانه در پابرجایی، سخت‌تر و در گفتار، استوارتر است.'),
(5482, 73, 7, 'تو در روز حرکت و تلاشی طولانی داری.'),
(5483, 73, 8, 'و نام پروردگارت را یاد کن و از همه بریده شو و تنها به او دل ببند!'),
(5484, 73, 9, 'او که پروردگار مشرق و مغرب است، معبودی جز او نیست، پس او را کارساز خود برگزین!'),
(5485, 73, 10, 'و بر آنچه می‌گویند، صبر کن و به‌گونه‌ای نیکو از آنان کناره بگیر!'),
(5486, 73, 11, 'و مرا با تکذیب‌کنندگانِ توانگر و خوشگذران واگذار و آنان را اندکی مهلت بده!'),
(5487, 73, 12, 'البتّه نزد ماست زنجیرها و آتش برافروخته،'),
(5488, 73, 13, 'و غذایی گلوگیر و عذابی دردناک!'),
(5489, 73, 14, 'در روزی که زمین و کوه‌ها به لرزه درآیند و کوه‌ها به‌صورت پشته‌هایی از شنِ روان شوند.'),
(5490, 73, 15, 'ما پیامبری را که گواه بر شماست، به سوی شما فرستادیم، همان‌گونه که پیامبری به سوی فرعون فرستادیم.'),
(5491, 73, 16, 'امّا فرعون، آن پیامبر را نافرمانی کرد و ما او را به کیفری سخت گرفتیم.'),
(5492, 73, 17, 'پس اگر کفر ورزید، چگونه خود را از روزی که کودکان را پیر می‌کند، نگاه می‌دارید؟'),
(5493, 73, 18, 'آسمان به سبب آن روز می‌شکافد. [آری!] وعده‌ی خداوند شدنی است.'),
(5494, 73, 19, 'این [آیات] پندی است. پس هر کس بخواهد، راهی به سوی پروردگارش پیش گیرد.'),
(5495, 73, 20, 'همانا پروردگارت می‌داند که تو و گروهی از کسانی که با تو هستند، نزدیک به دو سوم شب، یا نیمی از آن، یا یک سوم آن را [به عبادت] برمی‌خیزید و خداوند شب و روز را اندازه‌گیری می‌کند. او می‌داند که شما هرگز نمی‌توانید [دو سوم و نصف و یک سوم] آن را [دقیق] اندازه‌گیری کنید، از این روی شما را بخشید. [و حساب دقیق شب را از شما برداشت. اینک‌] هر چه [برای شما ممکن و] آسان است، قرآن بخوانید. خداوند می‌داند که به زودی برخی از شما بیمار خواهند شد و گروه دیگری به سفر می‌روند تا از فضل خدا [روزی] بجویند و گروهی دیگر در راه خدا می‌جنگند [و از تلاوت قرآن بازمی‌مانند]. پس هر چه می‌توانید، از آن بخوانید و نماز به پا دارید و زکات بپردازید و به خداوند وام دهید، وامی نیکو و [بدانید] آنچه برای خود پیش می‌فرستید، آن را نزد خدا نیکوتر و با پاداشی بزرگ‌تر خواهید یافت و از خداوند آمرزش بخواهید که خداوند آمرزنده‌ی مهربان است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ای جامه بر خود کشیده [و در بستر آرمیده]!'),
(5497, 74, 2, 'برخیز و هشدار ده!'),
(5498, 74, 3, 'و پروردگارت را بزرگ بدار!'),
(5499, 74, 4, 'و لباست را پاکیزه کن!'),
(5500, 74, 5, 'و از پلیدی دور شو!'),
(5501, 74, 6, 'و [در بخشش] منّت مگذار و [بخششت را] بزرگ مشمار!'),
(5502, 74, 7, 'و به خاطر پروردگارت صبر کن!'),
(5503, 74, 8, 'پس هنگامی که در صور دمیده شود.'),
(5504, 74, 9, 'پس آن روز، روز سختی است.'),
(5505, 74, 10, 'بر کافران آسان نیست.'),
(5506, 74, 11, 'مرا واگذار با آن که خود به تنهایی [او را] آفریدم.'),
(5507, 74, 12, 'و به او مالی پیوسته و در حال فزونی دادم.'),
(5508, 74, 13, 'و پسرانی که در کنارش حاضرند.'),
(5509, 74, 14, 'و برای او اسباب کامیابی را به طور کامل آماده کردم.'),
(5510, 74, 15, 'با این حال، طمع دارد که بیافزایم.'),
(5511, 74, 16, 'هرگز! زیرا او همواره به آیات ما دشمنی می‌ورزد.'),
(5512, 74, 17, 'به زودی او را به گردنه‌ای سخت گرفتار می‌کنم.'),
(5513, 74, 18, 'همانا او [برای مبارزه با قرآن] فکر کرد و [آن را] سنجید.'),
(5514, 74, 19, 'پس مرگ بر او باد که چگونه سنجید.'),
(5515, 74, 20, 'باز هم مرگ بر او که چگونه سنجید.'),
(5516, 74, 21, 'سپس نگریست.'),
(5517, 74, 22, 'سپس چهره در هم کشید و بی‌میلی نشان داد.'),
(5518, 74, 23, 'سپس پشت کرد و تکبّر ورزید.'),
(5519, 74, 24, 'پس گفت: «این [قرآن] چیزی نیست جز جادوبی که [از پیشینیان] روایت می‌شود.'),
(5520, 74, 25, 'این فقط گفته‌ی بشر است [نه گفتار خدا]!»'),
(5521, 74, 26, 'به زودی او را به سقَر درآورم.'),
(5522, 74, 27, 'و تو نمی‌دانی که سقر چیست.'),
(5523, 74, 28, '[آتشی است سوزنده که‌] نه [چیزی را] باقی می‌گذارد و نه رها می‌کند.'),
(5524, 74, 29, 'پوست بدن را به شدّت دگرگون می‌کند.'),
(5525, 74, 30, 'بر آن [دوزخ]، نوزده نفر گماشته شده‌اند.'),
(5526, 74, 31, 'و نگهبانان آتش را جز از فرشتگان قرار ندادیم و تعداد آنان را فقط آزمونی برای کافران نهادیم، تا اهل کتاب یقین کنند [که قرآن کتاب آسمانی است] و تا کسانی که ایمان آورده‌اند، بر ایمان خود بیافزایند و اهل کتاب و مؤمنان، گرفتار تردید نشوند و کافران و بیماردلان بگویند: «خداوند از این مثل چه منظوری داشته است؟» این گونه خداوند هر کس را بخواهد، گمراه می‌کند و هر که را بخواهد، هدایت می‌نماید و شمار لشکریان پروردگارت را کسی جز او نمی‌داند و این تنها پندی برای بشر است.'),
(5527, 74, 32, 'به ماه سوگند، چنین نیست [که کافران می‌پندارند]!'),
(5528, 74, 33, 'به شب سوگند، آن‌گاه که پشت می‌کند،'),
(5529, 74, 34, 'به صبح سوگند، آن‌گاه که آشکار می‌شود،'),
(5530, 74, 35, 'که این [دوزخ]، یکی از بزرگترین پدیده‌هاست.'),
(5531, 74, 36, 'هشداری برای بشر است.'),
(5532, 74, 37, 'برای هر کس از شما که بخواهد [در کار خیر] پیش افتد یا عقب بماند.'),
(5533, 74, 38, 'هر کسی در گروِ دستاورد خویش است،'),
(5534, 74, 39, 'مگر یاران [سعادتمندی که کارنامه‌ی آنان را به دست] راست [آنها می‌دهند]،'),
(5535, 74, 40, 'که در باغ‌های بهشتند و می‌پرسند'),
(5536, 74, 41, 'از گناهکاران:'),
(5537, 74, 42, '«چه چیزی شما را روانه‌ی [دوزخ] سقر کرد؟»'),
(5538, 74, 43, 'می‌گویند: «ما از نمازگزاران نبودیم.'),
(5539, 74, 44, 'و به بینوایان غذا نمی‌دادیم.'),
(5540, 74, 45, 'و همراه با فرورفتگان، [در گفتار و کردار باطل‌] فرومی‌رفتیم.'),
(5541, 74, 46, 'و پیوسته روز جزا را تکذیب می‌کردیم.'),
(5542, 74, 47, 'تا آن که امر یقینیِ [مرگ] به سراغ ما آمد.»'),
(5543, 74, 48, 'پس شفاعت میانجی‌گران سودشان ندهد.'),
(5544, 74, 49, 'آنان را چه شده که از تذکر [قرآن] روی‌گردانند؟'),
(5545, 74, 50, 'گویا آنان گورِخرانی هستند،'),
(5546, 74, 51, 'که از شیری [شرزه] می‌گریزند.'),
(5547, 74, 52, 'بلکه هر یک از آنان توقّع دارد کتابی سرگشاده [همانند قرآن] به او داده شود.'),
(5548, 74, 53, 'هرگز! [این بهانه است!] بلکه آنان از آخرت نمی‌ترسند.'),
(5549, 74, 54, 'هرگز! [ما کتابی جز قرآن نازل نمی‌کنیم] که آن یادآوری [ارزشمند] است.'),
(5550, 74, 55, 'پس هرکس خواست، از آن پند گیرد.'),
(5551, 74, 56, 'و پند نمی‌گیرند مگر آن که خدا بخواهد. او سزاوار است که از وی پروا کنند و او سزاوار است که [پرهیزکاران را] بیامرزد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'به روز قیامت سوگند می‌خورم.'),
(5553, 75, 2, 'و به نفس سرزنش‌گر سوگند می‌خورم.'),
(5554, 75, 3, 'آیا انسان می‌پندارد که هرگز استخوان‌های او را جمع نمی‌کنیم؟'),
(5555, 75, 4, 'چرا! ما تواناییم [خطوط] سر انگشتان او را [بار دیگر] درست کنیم.'),
(5556, 75, 5, 'ولی انسان می‌خواهد راه فسق و فجور را پیش روی خود باز کند.'),
(5557, 75, 6, '[پس، از روی تردید] می‌پرسد: «روز قیامت چه وقت است؟»'),
(5558, 75, 7, 'پس آن‌گاه که [در آستانه‌ی قیامت] چشم‌ها خیره گردد،'),
(5559, 75, 8, 'و ماه تاریک شود،'),
(5560, 75, 9, 'و خورشید و ماه جمع شوند،'),
(5561, 75, 10, 'در آن روز انسان خواهد گفت: «راه گریز کجاست؟»'),
(5562, 75, 11, 'هرگز! پناهگاهی نیست.'),
(5563, 75, 12, 'آن روز قرارگاه فقط به سوی پروردگار توست.'),
(5564, 75, 13, 'آن روز انسان به آنچه پیش فرستاده و آنچه به جای گذاشته است، خبر داده می‌شود.'),
(5565, 75, 14, 'بلکه انسان بر نفس خویش بیناست.'),
(5566, 75, 15, 'هر چند [برای توجیه گناهانش‌] بهانه‌ها بتراشد.'),
(5567, 75, 16, '[ای پیامبر! به هنگام نزول وحی،] زبان خود را به حرکت در نیاور تا نسبت به تلاوت آن شتاب کنی.'),
(5568, 75, 17, 'گردآوردن و خواندن آن به عهده‌ی ماست.'),
(5569, 75, 18, 'پس چون آن را خواندیم، قرائت آن را دنبال کن.'),
(5570, 75, 19, 'آن‌گاه بیان آن بر عهده ماست.'),
(5571, 75, 20, 'چنین نیست [که شما می‌پندارید قیامتی در کار نیست]، بلکه به این دنیای زودگذر دل‌بسته‌اید.'),
(5572, 75, 21, 'و آخرت را رها کرده‌اید.'),
(5573, 75, 22, 'چهره‌هایی در آن روز شاداب و با طراوت است.'),
(5574, 75, 23, 'و به [لطف و پاداش] پروردگارشان چشم دوخته‌اند.'),
(5575, 75, 24, 'و چهره‌هایی در آن روز گرفته است.'),
(5576, 75, 25, 'زیرا می‌دانند مورد عذابی کمرشکن قرار می‌گیرند.'),
(5577, 75, 26, '[انسان می‌پندارد دنیای زودگذر همیشگی است،] هرگز! آن‌گاه که جان به گلوگاه برسد.'),
(5578, 75, 27, 'و [اطرافیان بیمار] بگویند: «درمان‌کننده [این بیمار] کیست؟»'),
(5579, 75, 28, 'و [محتضَر] بداند که هنگام جدایی فرارسیده است.'),
(5580, 75, 29, 'و ساق‌های پا به یکدیگر بپیچد [و دیگر حرکتی نکند].'),
(5581, 75, 30, 'در آن روز، روانه شدن به سوی پروردگار توست.'),
(5582, 75, 31, 'او که نه حقّ را تصدیق کرد و نه نماز گزارد.'),
(5583, 75, 32, 'بلکه تکذیب کرد و روی گردانید.'),
(5584, 75, 33, 'و سپس با کبر و غرور به سوی خانواده‌اش رفت.'),
(5585, 75, 34, '[عذاب الهی] برای تو شایسته‌تر است، شایسته‌تر!'),
(5586, 75, 35, 'باز هم شایسته‌تر است، شایسته‌تر!'),
(5587, 75, 36, 'آیا انسان می‌پندارد که به حال خود رها می‌شود؟'),
(5588, 75, 37, 'آیا او قطره‌ای از منی نبود که ریخته می‌شد؟'),
(5589, 75, 38, 'سپس خون بسته‌ای شد و [خداوند] آن را آفرینش داد و [اندامش را] استوار ساخت.'),
(5590, 75, 39, 'و دو جنس زن و مرد را از آن پدید آورد.'),
(5591, 75, 40, 'آیا این [خدای نیرومند] توانا نیست که مردگان را زنده کند؟');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آیا بر انسان دوره‌ای از روزگار گذشت که چیز قابل ذکری نبود؟'),
(5593, 76, 2, 'همانا ما انسان را از نطفه‌ای آمیخته آفریدیم و او را از حالتی به حالتی درآوردیم، پس او را شنوا و بینا گردانیدیم.'),
(5594, 76, 3, 'ما راه را به او نشان دادیم، خواه سپاسگرار باشد، یا ناسپاس.'),
(5595, 76, 4, 'ما برای کافران، زنجیرها و غل‌ها و آتشی برافروخته آماده کرده‌ایم.'),
(5596, 76, 5, 'همانا نیکان از پیمانه‌ای می‌نوشند که با کافور [خوشبو] آمیخته شده است.'),
(5597, 76, 6, 'چشمه‌ای که بندگان خدا از آن می‌نوشند و هرگونه بخواهند، آن را جاری می‌سازند.'),
(5598, 76, 7, 'آنان به نذر [خود] وفا می‌کنند و از روزی که شرش فراگیر است، می‌ترسند.'),
(5599, 76, 8, 'و غذای خود را با آن که به آن علاقه [و نیاز] دارند، به بینوا و یتیم و اسیر می‌دهند.'),
(5600, 76, 9, 'که ما فقط برای خشنودی خدا به شما غذا می‌دهیم، نه پاداشی از شما می‌خواهیم و نه سپاسی.'),
(5601, 76, 10, 'و ما از پروردگارمان، به خاطر روزی که به شدت گرفته و بسیار سخت است، می‌ترسیم.'),
(5602, 76, 11, 'پس خداوند آنان را از شر آن روز نگاه می‌دارد و آنان را با شادی و سروری بس بزرگ روبرو می‌کند.'),
(5603, 76, 12, 'و آنها را برای صبری که کردند، بهشتی [برین] و ابریشمی [نازنین] پاداش می‌دهد،'),
(5604, 76, 13, 'در حالی که در آن [بهشت] بر تخت‌ها تکیه زده‌اند، در آنجا نه آفتابی [آزار دهنده] بینند و نه سرمایی [سوزان].'),
(5605, 76, 14, 'و سایه‌های درختان بر سرشان نزدیک و میوه‌هایش در دسترس آنهاست.'),
(5606, 76, 15, 'گرد آنان ظرف‌های نقره‌فام و تنگ‌های بلورین، [پر از غذا و نوشیدنی] گردانده می‌شود.'),
(5607, 76, 16, 'بلورهایی نقره‌فام که [نیکان] آنها را به هر اندازه که خواسته‌اند، اندازه‌گیری کرده‌اند.'),
(5608, 76, 17, 'در آن [بهشت]، جامی نوشانده می‌شود که با زنجبیل [گرم و عطرآگین] آمیخته است.'),
(5609, 76, 18, 'از چشمه‌ای در آنجا که سلسبیل نامیده می‌شود.'),
(5610, 76, 19, 'و بر گردشان پسرانی همیشه نوجوان، [به خدمت] می‌گردند، هرگاه آنان را ببینی، می‌پنداری مرواریدهایی غلطان و پراکنده‌اند.'),
(5611, 76, 20, 'و چون آنجا را بنگری، نعمتی [فراوان و وصف‌ناپذیر] و فرمانروایی بزرگی می‌بینی.'),
(5612, 76, 21, 'بر اندامشان جامه‌های سبز از دیبای نازک و ابریشم درشت بافت است و با دستبندهای نقره آراسته شده‌اند و پروردگارشان شرابی پاک و پاک‌کننده به آنان می‌نوشاند.'),
(5613, 76, 22, '«این پاداشی است برای شما و کوشش شما مورد سپاس است.»'),
(5614, 76, 23, 'همانا ما خود، قرآن را آن‌گونه که باید، بر تو فروفرستادیم.'),
(5615, 76, 24, 'پس در برابر فرمان پروردگارت صبر پیشه کن! و از هیچ گنه‌کار یا ناسپاسِ آنان فرمان نبر!'),
(5616, 76, 25, 'و بامداد و شامگاه، نام پروردگارت را یاد کن!'),
(5617, 76, 26, 'و بخشی از شب را برای او سجده کن! و در بلندای شب به تسبیح او بپرداز!'),
(5618, 76, 27, 'اینان [دنیای] زودگذر را دوست دارند و روز گرانبار [قیامت] را پشت سر می‌اندازند.'),
(5619, 76, 28, 'ماییم که آنان را آفریدیم و پیوندهای مفاصلشان را محکم ساختیم و هرگاه بخواهیم، مانند آنها را چنان که باید به جای آنان می‌آوریم.'),
(5620, 76, 29, 'این [آیات] پندی است. پس هر کس بخواهد، راهی به سوی پروردگارش [در پیش] گیرد.'),
(5621, 76, 30, 'و شما چیزی را نمی‌خواهید، مگر این که خدا بخواهد. خداوند همواره دانایی فرزانه است.'),
(5622, 76, 31, 'هر که را بخواهد، به رحمت خود درمی‌آورد و برای ستمکاران عذابی دردناک آماده کرده است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به فرشتگانی که پی‌درپی فرستاده می‌شوند!'),
(5624, 77, 2, 'پس [سوگند] به فرشتگانی که چون تندبادند!'),
(5625, 77, 3, 'سوگند به فرشتگانی که صحیفه‌های وحی را می‌گشایند!'),
(5626, 77, 4, 'پس [سوگند] به فرشتگانی که به طور کامل [میان حقّ و باطل] جدا کننده‌اند!'),
(5627, 77, 5, 'پس [سوگند] به فرشتگانِ فرود آورنده‌ی وحی!'),
(5628, 77, 6, 'برای [رفع] عذر، یا هشدار و بیم‌دادن.'),
(5629, 77, 7, 'بی‌گمان آنچه به شما وعده می‌دهند، روی‌دادنی است.'),
(5630, 77, 8, 'پس آن‌گاه که ستارگان محو شوند.'),
(5631, 77, 9, 'و آن‌گاه که آسمان شکافته شود.'),
(5632, 77, 10, 'و آن‌گاه که کوه‌ها از جا کنده شوند.'),
(5633, 77, 11, 'و آن‌گاه که برای پیامبران وقت تعیین شود [تا بر امّت‌ها گواهی دهند].'),
(5634, 77, 12, '[این حوادث] برای چه روزی به تأخیر افتاده است؟'),
(5635, 77, 13, 'برای روز جدایی.'),
(5636, 77, 14, 'و تو چه می‌دانی که روز جدایی چیست؟'),
(5637, 77, 15, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5638, 77, 16, 'آیا پیشینیان را نابود نکردیم؟'),
(5639, 77, 17, 'سپس دیگران را در پی آنان آوردیم.'),
(5640, 77, 18, 'ما با مجرمان این‌گونه رفتار می‌کنیم.'),
(5641, 77, 19, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5642, 77, 20, 'آیا ما شما را از آبی پست و ناچیز نیافریدیم؟'),
(5643, 77, 21, 'پس آن را در [رحم مادر] جایگاهی استوار نهادیم،'),
(5644, 77, 22, 'تا مدّتی معین.'),
(5645, 77, 23, 'پس اندازه‌گیری کردیم و ما چه خوب اندازه‌گیری هستیم!'),
(5646, 77, 24, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5647, 77, 25, 'آیا زمین را محل اجتماع [انسان‌ها] قرار ندادیم؟'),
(5648, 77, 26, 'زندگان و مردگان؟'),
(5649, 77, 27, 'و در آن [زمین] کوه‌هایی استوار و بلند قرار دادیم و آبی گوارا به شما نوشاندیم.'),
(5650, 77, 28, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5651, 77, 29, '[آن روز خداوند به مجرمان می‌فرماید:] «بی‌درنگ بروید به سوی [دوزخ!] همان چیزی که پیوسته تکذیبش می‌کردید.'),
(5652, 77, 30, 'بروید به سوی سایه‌ای [از دود انبوه که] سه شاخه [است]،'),
(5653, 77, 31, '[سایه‌ای که] نه مانع از حرارت است و نه از شعله‌های آتش جلوگیری می‌کند.»'),
(5654, 77, 32, 'دوزخ، شراره‌هایی [بلند] چون کاخ پرتاب می‌کند.'),
(5655, 77, 33, '[و آن شراره‌ها در رنگ و شتاب] گویی شترانی زردمویند.'),
(5656, 77, 34, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5657, 77, 35, 'این روزی است که [گناهکاران] سخن نمی‌گویند،'),
(5658, 77, 36, 'و به آنان اجازه داده نمی‌شود تا عذرخواهی کنند.'),
(5659, 77, 37, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5660, 77, 38, 'این روز، روز جدایی است که شما و پیشینیان را گرد آوردیم.'),
(5661, 77, 39, 'پس اگر چاره‌ای دارید، به کار گیرید.'),
(5662, 77, 40, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5663, 77, 41, 'همانا پرهیزکاران در سایه‌سارها و [کنار] چشمه‌هایی هستند.'),
(5664, 77, 42, 'و هر نوع میوه که بخواهند، [فراهم است.]'),
(5665, 77, 43, '«به پاداش کارهایی [نیک] که همواره می‌کردید، بخورید و بیاشامید! گوارایتان باد!»'),
(5666, 77, 44, 'ما نیکوکاران را این گونه پاداش می‌دهیم.'),
(5667, 77, 45, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5668, 77, 46, '[به کافران بگو:] «اندک زمانی [در این دنیا] بخورید و بهره گیرید! که شما گناهکارید [و بی‌تردید به کیفر اعمالتان گرفتار خواهید شد.]»'),
(5669, 77, 47, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5670, 77, 48, 'و هرگاه به آنان گفته شود: «رکوع کنید!» رکوع نمی‌کنند.'),
(5671, 77, 49, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5672, 77, 50, '[اگر این کافران به قرآن ایمان نیاورند،] پس به کدام سخن بعد از آن ایمان می‌آورند؟');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'از چه از یکدیگر می‌پرسند؟'),
(5674, 78, 2, 'از خبر بزرگ!'),
(5675, 78, 3, 'همان که درباره‌ی آن اختلاف دارند.'),
(5676, 78, 4, 'چنین نیست! به زودی خواهند دانست.'),
(5677, 78, 5, 'باز هم چنین نیست! به زودی خواهند دانست.'),
(5678, 78, 6, 'آیا زمین را بستر آرامش قرار ندادیم؟'),
(5679, 78, 7, 'و کوه‌ها را میخ‌هایی [برای استواری آن]؟'),
(5680, 78, 8, 'و شما را جفت‌هایی آفریدیم.'),
(5681, 78, 9, 'و خواب را مایه‌ی آرامش شما قرار دادیم.'),
(5682, 78, 10, 'و شب را پوششی ساختیم.'),
(5683, 78, 11, 'و روز را وقت معاش قرار دادیم.'),
(5684, 78, 12, 'و بر فراز شما هفت [آسمان] استوار بنا کردیم.'),
(5685, 78, 13, 'و چراغی فروزان قرار دادیم.'),
(5686, 78, 14, 'و از ابرهای باران‌زا و فشرده، آبی فراوان و ریزان فروفرستادیم.'),
(5687, 78, 15, 'تا با آن دانه و گیاه بیرون آوریم،'),
(5688, 78, 16, 'و باغ‌هایی پر درخت.'),
(5689, 78, 17, 'همانا روز جدایی، وعده‌گاه [ما با شما] است.'),
(5690, 78, 18, 'روزی که در صور دمیده می‌شود و گروه گروه می‌آیید.'),
(5691, 78, 19, 'و آسمان گشوده شود و به صورت درهایی باز درآید.'),
(5692, 78, 20, 'و کوه‌ها روان شوند و چون سراب گردند.'),
(5693, 78, 21, 'همانا دوزخ، کمین‌گاهی است.'),
(5694, 78, 22, 'بازگشت‌گاهی است برای سرکشان.'),
(5695, 78, 23, 'روزگاری [دراز] در آن می‌مانند.'),
(5696, 78, 24, 'در آن [دوزخ]، نه چیز خنکی می‌چشند و نه آشامیدنی [گوارایی]،'),
(5697, 78, 25, 'مگر آب داغ و چرکابی.'),
(5698, 78, 26, 'این، کیفری است مناسب.'),
(5699, 78, 27, 'آنان بودند که به حساب [قیامت] امید و باوری نداشتند.'),
(5700, 78, 28, 'و آیات ما را به سختی تکذیب می‌کردند.'),
(5701, 78, 29, '[ولی] ما حساب همه چیز را درکتابی داشتیم.'),
(5702, 78, 30, '«پس بچشید که هرگز جز عذاب بر شما نمی‌افزاییم.»'),
(5703, 78, 31, 'بی‌گمان برای پرهیزکاران، رستگاری و کامیابی بزرگی است:'),
(5704, 78, 32, 'باغ‌هایی [خرم و محصور] و انواع انگورها.'),
(5705, 78, 33, 'و دخترانی دلربا و هم‌سال.'),
(5706, 78, 34, 'و جام‌هایی لبریز.'),
(5707, 78, 35, 'در آنجا نه سخن بیهوده‌ای می‌شنوند و نه دروغ و تکذیبی.'),
(5708, 78, 36, '[این] پاداشی [است] از سوی پروردگارت؛ بخششی حساب شده.'),
(5709, 78, 37, '[همان] پروردگار آسمان‌ها و زمین و آنچه میان آنهاست. آن [خدای] بخشنده که هیچ کس اختیار چون و چرا با او ندارد.'),
(5710, 78, 38, 'روزی که روح و فرشتگان به صف بایستند، کسی سخن نمی‌گوید جز آن که [خدای] رحمان به او اجازه دهد و او سخن درست بگوید.'),
(5711, 78, 39, 'آن روز، روز حقّ است. پس هرکس بخواهد، راه بازگشتی به سوی پروردگارش [در پیش] گیرد.'),
(5712, 78, 40, 'همانا ما شما را از عذابی نزدیک بیم دادیم. روزی که انسان به آنچه از پیش فرستاده بنگرد و کافر بگوید: «ای کاش خاک بودم!»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به فرشتگانی که جان [کافران] را به سختی می‌گیرند.'),
(5714, 79, 2, 'سوگند به فرشتگانی که جان [مؤمنان] را به آسانی و نشاط می‌گیرند.'),
(5715, 79, 3, 'سوگند به فرشتگانی که [در انجام فرمان خدا] به سرعت شناورند،'),
(5716, 79, 4, 'و بر یکدیگر پیشی می‌گیرند،'),
(5717, 79, 5, 'و امور [بندگان] را تدبیر می‌کنند.'),
(5718, 79, 6, '[به اینها سوگند که برانگیخته خواهید شد،] روزی که زلزله‌ی وحشتناک همه چیز را به لرزه درآورد.'),
(5719, 79, 7, 'و به دنبال آن لرزه دیگری روی دهد.'),
(5720, 79, 8, 'در آن روز، دل‌هایی ترسان و لرزان است.'),
(5721, 79, 9, 'و چشم‌ها [از ترس] فروافتاده است.'),
(5722, 79, 10, '[آنان که در دنیا] می‌گویند: «آیا ما به زندگی نخستین بازگردانده می‌شویم،'),
(5723, 79, 11, 'آن‌گاه که استخوان‌هایی پوسیده و ریز ریز شدیم؟!»'),
(5724, 79, 12, '[با خود] گویند: «در این صورت، این، بازگشتی زیانبار است.»'),
(5725, 79, 13, 'جز این نیست که آن [بازگشت]، فقط با یک بانگ عظیم است.'),
(5726, 79, 14, 'که ناگهان همگی در صحرای محشر حاضر شوند.'),
(5727, 79, 15, 'آیا سرگذشت موسی به تو رسیده است؟'),
(5728, 79, 16, 'آن‌گاه که پروردگارش او را در سرزمین مقدّس طُوی ندا داد:'),
(5729, 79, 17, '«به سوی فرعون برو که او سرکشی کرده است.'),
(5730, 79, 18, 'پس [به او] بگو:» «آیا می‌خواهی که پاک شوی؟'),
(5731, 79, 19, 'و تو را به سوی پروردگارت راهنمایی کنم تا بترسی [و سرکشی نکنی]؟»'),
(5732, 79, 20, 'پس بزرگترین معجزه را به او نشان داد.'),
(5733, 79, 21, 'امّا او تکذیب کرد و نافرمانی نمود.'),
(5734, 79, 22, 'آن‌گاه پشت کرد، در حالی که [برای خاموشی این دعوت] می‌کوشید.'),
(5735, 79, 23, 'پس [جادوگران را] گرد آورد و فراخواند.'),
(5736, 79, 24, 'و گفت: «من پروردگار برتر شما هستم!»'),
(5737, 79, 25, 'آن‌گاه خداوند او را به کیفر دنیا و آخرت گرفتار کرد.'),
(5738, 79, 26, 'در این [داستان]، برای هر کس که [از عاقبت بد] بترسد، عبرتی است.'),
(5739, 79, 27, 'آیا آفرینش شما سخت‌تر است یا آسمانی که او بنا کرده؟'),
(5740, 79, 28, 'سقفش را برافراشت و آن را استوار ساخت.'),
(5741, 79, 29, 'شبش را تیره و روزش را روشن گرداند.'),
(5742, 79, 30, 'و پس از آن، زمین را گسترش داد.'),
(5743, 79, 31, 'آب‌های آن و چراگاه‌ها و گیاهان آن را پدیدار ساخت.'),
(5744, 79, 32, 'و کوه‌ها را استوار و پابرجا گردانید.'),
(5745, 79, 33, 'تا مایه‌ی برخورداری شما و چارپایان شما باشد.'),
(5746, 79, 34, 'پس هنگامی که آن حادثه‌ی بزرگ‌تر فرارسد.'),
(5747, 79, 35, 'در آن روز، انسان به یاد تلاش‌هایش می‌افتد.'),
(5748, 79, 36, 'و دوزخ برای هر بیننده‌ای آشکار شود.'),
(5749, 79, 37, 'امّا هر که سرکشی کرده،'),
(5750, 79, 38, 'و زندگی دنیا را [بر زندگی جاودان] برگزیده باشد،'),
(5751, 79, 39, 'بی‌شک، دوزخ جایگاه اوست.'),
(5752, 79, 40, 'و امّا کسی که از مقام پروردگارش ترسید و نفسش را از هوس بازداشت،'),
(5753, 79, 41, 'بی‌شک، بهشت جایگاه اوست.'),
(5754, 79, 42, '[ای پیامبر!] از تو درباره‌ی قیامت می‌پرسند که چه وقت برپا می‌شود؟'),
(5755, 79, 43, 'تو را به گفتگو درباره‌ی آن چه کار؟'),
(5756, 79, 44, 'پایان و سرانجام آن با پروردگار توست.'),
(5757, 79, 45, 'وظیفه‌ی تو تنها هشداردادن کسی است که از آن می‌هراسد.'),
(5758, 79, 46, 'روزی که آن را می‌بینند، گویی جز شبی یا روزی [در دنیا و برزخ] درنگ نکرده‌اند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'چهره درهم کشید و روی برتافت.'),
(5760, 80, 2, 'از این که مردی نابینا به سراغ او آمد.'),
(5761, 80, 3, 'و تو چه می‌دانی؟ شاید او در پی پاکی و پارسایی باشد.'),
(5762, 80, 4, 'یا پند گیرد و آن پند سودش بخشد.'),
(5763, 80, 5, 'امّا کسی که خود را بی‌نیاز می‌بیند.'),
(5764, 80, 6, 'پس تو به او روی می‌آوری.'),
(5765, 80, 7, 'با آن که اگر نخواهد خود را پاک سازد، بر تو چیزی نیست.'),
(5766, 80, 8, 'و امّا کسی که شتابان نزد تو آمده،'),
(5767, 80, 9, 'در حالی که [از خدا] می‌ترسد،'),
(5768, 80, 10, 'از او به دیگران می‌پردازی.'),
(5769, 80, 11, 'این چنین [برخوردی شایسته‌] نیست! بی‌تردید این [قرآن] مایه‌ی پند است.'),
(5770, 80, 12, 'پس هر کس بخواهد، آن را یاد کند و از آن پند گیرد.'),
(5771, 80, 13, 'در میان صحیفه‌هایی ارجمند،'),
(5772, 80, 14, 'بلندمرتبه و پاکیزه است.'),
(5773, 80, 15, 'به دست سفیرانی'),
(5774, 80, 16, 'بزرگوار و نیکوکار.'),
(5775, 80, 17, 'مرگ بر این انسان [سرکش] که چه ناسپاس است!'),
(5776, 80, 18, '[مگر خداوند] او را از چه چیز آفریده است؟'),
(5777, 80, 19, 'از نطفه‌ای [ناچیز] او را آفرید و سامانش بخشید.'),
(5778, 80, 20, 'سپس راه [رشد و کمال] را برای او آسان و فراهم نمود.'),
(5779, 80, 21, 'آن‌گاه او را میراند و در گورش نهاد.'),
(5780, 80, 22, 'پس هر زمان که بخواهد، او را برانگیزد.'),
(5781, 80, 23, 'هرگز! هنوز آنچه را به او فرمان داده، بجا نیاورده است.'),
(5782, 80, 24, 'پس باید انسان به غذای خود بنگرد.'),
(5783, 80, 25, 'ما آب را آن‌گونه که باید، [از آسمان] فروریختیم.'),
(5784, 80, 26, 'سپس زمین را به خوبی شکافتیم.'),
(5785, 80, 27, 'و در آن، دانه رویاندیم.'),
(5786, 80, 28, 'و نیز انگور و سبزیجات،'),
(5787, 80, 29, 'و زیتون و خرما،'),
(5788, 80, 30, 'و باغ‌های پردرخت،'),
(5789, 80, 31, 'و میوه و چراگاه،'),
(5790, 80, 32, 'برای برخورداری شما و چهارپایانتان.'),
(5791, 80, 33, 'پس هنگامی که آن صدای هولناک برآید.'),
(5792, 80, 34, 'روزی که انسان از برادرش بگریزد،'),
(5793, 80, 35, 'و [از] مادر و پدرش،'),
(5794, 80, 36, 'و [از] همسر و فرزندانش،'),
(5795, 80, 37, 'در آن روز برای هر یک از آنان گرفتاری است که او را [از دیگران] بازمی‌دارد.'),
(5796, 80, 38, 'چهره‌هایی در آن روز درخشانند،'),
(5797, 80, 39, 'خندان و شادمانند،'),
(5798, 80, 40, 'و بر چهره‌هایی در آن روز، غبار [غم] نشسته.'),
(5799, 80, 41, 'و تیرگی و سیاهی، چهره‌ی آنان را پوشانده است.'),
(5800, 80, 42, 'آنان، همان کافران بدکردارند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آن‌گاه که خورشید درهم‌پیچیده و تاریک شود.'),
(5802, 81, 2, 'و آن‌گاه که ستارگان به خاموشی گرایند.'),
(5803, 81, 3, 'و آن‌گاه که کوه‌ها به حرکت درآیند.'),
(5804, 81, 4, 'و آن‌گاه که اموال با ارزش به حال خود رها شوند.'),
(5805, 81, 5, 'و آن‌گاه که حیوانات وحشی، برانگیخته شوند و گرد آیند.'),
(5806, 81, 6, 'و آن‌گاه که دریاها شعله‌ور و جوشان شوند.'),
(5807, 81, 7, 'و آن‌گاه که جان‌ها جفت و قرین شوند. [نیکان با نیکان و بدان با بدان.]'),
(5808, 81, 8, 'و آن‌گاه که از [دختران] زنده به گور شده پرسیده شود'),
(5809, 81, 9, 'به کدامین گناه کشته شده‌اند؟'),
(5810, 81, 10, 'و آن‌گاه که نامه‌های عمل گشوده شود.'),
(5811, 81, 11, 'و آن‌گاه که آسمان از جای کنده شود.'),
(5812, 81, 12, 'و آن‌گاه که دوزخ شعله‌ور شود.'),
(5813, 81, 13, 'و آن‌گاه که بهشت [برای اهلش] نزدیک آورده شود.'),
(5814, 81, 14, '[در آن هنگام] هر کس آنچه را [در دنیا] آماده کرده است، می‌داند.'),
(5815, 81, 15, 'پس سوگند می‌خورم به ستارگانی که بازمی‌گردند،'),
(5816, 81, 16, 'در حرکتند و پنهان می‌شوند،'),
(5817, 81, 17, 'و سوگند به شب، آن‌گاه که برود،'),
(5818, 81, 18, 'و سوگند به صبح، آن‌گاه که بدمد،'),
(5819, 81, 19, 'که آن [قرآن]، بازخوانده‌ی [جبرئیل،] پیام‌آوری گرامی است.'),
(5820, 81, 20, 'نیرومندی که نزد [خداوندِ] صاحب عرش، دارای مقامی والاست.'),
(5821, 81, 21, '[دیگر فرشتگان] از او فرمان می‌برند و امین است.'),
(5822, 81, 22, 'و همنشینتان [محمّد که عمری با راستی و خردورزی در میان شما زندگی کرده است‌،] هیچ‌گونه جنونی ندارد.'),
(5823, 81, 23, 'و قطعاً آن [فرشته‌] را در افق روشن دیده است.'),
(5824, 81, 24, 'و او بر [رساندن وحی از عالم] غیب بخل نمی‌ورزد. [و آنچه را دریافت کرده، دریغ نمی‌کند.]'),
(5825, 81, 25, 'و آن قرآن، بازخوانده‌ی شیطانِ رانده شده نیست.'),
(5826, 81, 26, 'پس به کجا می‌روید؟'),
(5827, 81, 27, 'آن مایه‌ی تذکّر و پند برای جهانیان است.'),
(5828, 81, 28, 'برای هر کس از شما که بخواهد راستی و درستی پیشه کند.'),
(5829, 81, 29, 'و تا خداوند، پروردگار جهانیان نخواهد، [شما نیز] نخواهید خواست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'آن‌گاه که آسمان شکافته شود.'),
(5831, 82, 2, 'و آن‌گاه که ستارگان پراکنده شوند.'),
(5832, 82, 3, 'و آن‌گاه که دریاها گشوده شوند و به هم راه یابند.'),
(5833, 82, 4, 'و آن‌گاه که قبرها زیر و رو گردد.'),
(5834, 82, 5, '[آن هنگام] هر کس آنچه را از پیش فرستاده و آنچه را از پس فرستاده است، می‌داند.'),
(5835, 82, 6, 'ای انسان! چه چیز تو را در برابر پروردگارت که کریم و بخشنده است، مغرور ساخت؟'),
(5836, 82, 7, 'همان که تو را آفرید و [اندامت را] استوار ساخت و هماهنگ و متعادل کرد.'),
(5837, 82, 8, 'و به هر صورت که خواست، تو را ترکیب کرد.'),
(5838, 82, 9, 'با این همه، [شما روز] جزا را دروغ می‌پندارید.'),
(5839, 82, 10, 'در حالی که قطعاً بر شما نگهبانانی [از فرشتگان] گمارده شده‌اند.'),
(5840, 82, 11, 'نویسندگانی بزرگوار،'),
(5841, 82, 12, 'که به آنچه می‌کنید، آگاهند.'),
(5842, 82, 13, 'بی‌شک، نیکان در ناز و نعمت‌اند.'),
(5843, 82, 14, 'و بدکاران در آتشی شعله‌ور.'),
(5844, 82, 15, 'که در روز جزا به آن وارد شوند،'),
(5845, 82, 16, 'و لحظه‌ای، از آن جدا نشوند.'),
(5846, 82, 17, 'و تو چه می‌دانی که روز جزا چه روزی است؟'),
(5847, 82, 18, 'باز، چه می‌دانی که روز جزا چه روزی است؟'),
(5848, 82, 19, 'روزی که هیچ کس برای دیگری هیچ اختیاری ندارد و در آن روز، فرمان، فرمان خداست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای بر کم‌فروشان!'),
(5850, 83, 2, 'کسانی که هرگاه [برای خرید] از مردم، کالایی را پیمانه می‌کنند تمام و کامل می‌گیرند.'),
(5851, 83, 3, 'ولی هنگامی که می‌خواهند [برای فروش] به آنان پیمانه بدهند، یا برایشان [کالایی را] وزن کنند، کم می‌گذارند.'),
(5852, 83, 4, 'آیا آنان گمان ندارند که برانگیخته خواهند شد؟'),
(5853, 83, 5, 'در روزی بزرگ؟'),
(5854, 83, 6, 'روزی که مردم در پیشگاه پروردگار جهانیان خواهند ایستاد.'),
(5855, 83, 7, 'چنین نیست [که آنان می‌پندارند]! همانا [سرنوشت] پرونده‌ی بدکاران در سِجّین است.'),
(5856, 83, 8, 'و تو چه می‌دانی که سِجّین چیست؟'),
(5857, 83, 9, 'این سرنوشتی است رقم خورده [و حتمی].'),
(5858, 83, 10, 'در آن روز، وای بر تکذیب‌کنندگان!'),
(5859, 83, 11, 'کسانی که روز جزا را انکار می‌کنند.'),
(5860, 83, 12, 'و جز تجاوزگر گناهکار، کسی آن را انکار نمی‌کند.'),
(5861, 83, 13, 'هرگاه آیات ما بر او تلاوت شود، می‌گوید: «[اینها،] افسانه‌های پیشینیان است!»'),
(5862, 83, 14, 'هرگز! بلکه آنچه مرتکب می‌شدند، بر دل‌هایشان زنگار نهاده است.'),
(5863, 83, 15, 'هرگز! آنان در آن روز از [الطاف] پروردگارشان دور و محرومند.'),
(5864, 83, 16, 'سپس به دوزخ وارد خواهند شد.'),
(5865, 83, 17, 'سپس [به آنان] گفته خواهد شد: «این همان چیزی است که آن را دروغ می‌پنداشتید!»'),
(5866, 83, 18, 'چنین نیست [که آنان می‌پندارند]! همانا [سرنوشت] پرونده‌ی نیکوکاران در علّیین است.'),
(5867, 83, 19, 'و تو چه می‌دانی که علّیین چیست؟'),
(5868, 83, 20, 'این [جایگاه بلند و پرشکوه]، سرنوشتی است رقم خورده.'),
(5869, 83, 21, 'که مقرّبان آن را مشاهده می‌کنند.'),
(5870, 83, 22, 'بی‌شک، نیکان در ناز و نعمت‌اند.'),
(5871, 83, 23, 'بر تخت‌ها [تکیه می‌زنند و] می‌نگرند.'),
(5872, 83, 24, 'در چهره‌ی آنان خرّمی و طراوت نعمت را بازمی‌شناسی.'),
(5873, 83, 25, 'از شرابی ناب و سر به مهر به آنان می‌نوشانند.'),
(5874, 83, 26, 'مهر آن از مشک است و هر کس که اهل مسابقه است، شایسته است که رقابت ورزد.'),
(5875, 83, 27, 'و [این باده‌ی ناب] آمیخته‌ای از تسنیم است،'),
(5876, 83, 28, 'چشمه‌ای که مقرّبان [الهی] از آن می‌نوشند.'),
(5877, 83, 29, 'گناهکاران [در دنیا] پیوسته به مؤمنان می‌خندیدند.'),
(5878, 83, 30, 'و هرگاه از کنارشان می‌گذشتند، با چشم و ابرو به یکدیگر اشاره می‌کردند [و آنان را به مسخره می‌گرفتند].'),
(5879, 83, 31, 'و چون به سوی کسانشان بازمی‌گشتند، بذله‌گو بازمی‌گشتند.'),
(5880, 83, 32, 'و هرگاه آنان را می‌دیدند، می‌گفتند: «بی‌شک این گروه گمراهند!»'),
(5881, 83, 33, 'در حالی که آنان را بر مؤمنان مراقب نفرستاده بودند [که قضاوت کنند].'),
(5882, 83, 34, 'پس امروز، مؤمنان به کافران می‌خندند.'),
(5883, 83, 35, 'آنان بر تخت‌های بهشتی نظاره‌گرند،'),
(5884, 83, 36, 'که آیا کافران به کیفر کارهایشان، رسیده‌اند؟');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آن‌گاه که آسمان شکافته شود.'),
(5886, 84, 2, 'و پروردگارش را فرمان برد که چنین سزاوار است.'),
(5887, 84, 3, 'و آن‌گاه که زمین کشیده و هموار شود.'),
(5888, 84, 4, 'و آنچه در درون دارد، بیرون اندازد و خالی گردد.'),
(5889, 84, 5, 'و پروردگارش را فرمان بَرَد و چنین سزاوار است.'),
(5890, 84, 6, 'ای انسان! بی‌تردید تو با تلاشی سخت به سوی پروردگارت رهسپاری و سرانجام به دیدار او خواهی رسید.'),
(5891, 84, 7, 'پس هر کس کارنامه‌اش به دست راستش داده شود،'),
(5892, 84, 8, 'به زودی و به آسانی به حساب او رسیدگی خواهد شد،'),
(5893, 84, 9, 'و شادمان به سوی کسانش [در بهشت] بازمی‌گردد.'),
(5894, 84, 10, 'و امّا هر کس کارنامه‌اش از پشت سر [و به دست چپِ] او داده شود،'),
(5895, 84, 11, 'پس به زودی فریاد فغان و نابودی بر خواهد آورد،'),
(5896, 84, 12, 'و وارد دوزخ خواهد شد.'),
(5897, 84, 13, 'او [در دنیا] در میان کسانش شادمان بود.'),
(5898, 84, 14, 'او می‌پنداشت که هرگز [به سوی پروردگارش] بازنمی‌گردد.'),
(5899, 84, 15, 'چرا! [باز خواهد گشت.] پروردگارش همواره به او بیناست.'),
(5900, 84, 16, 'به شفق، [سرخی پس از غروب] سوگند می‌خورم،'),
(5901, 84, 17, 'و سوگند به شب و آنچه [زیر پرده‌ی سیاه خود] جمع می‌کند،'),
(5902, 84, 18, 'و سوگند به ماه، آن‌گاه که [بَدْرِ] کامل گردد.'),
(5903, 84, 19, 'که شما همواره از حالی به حال دیگر درآیید. [و مراحل زندگی را از دنیا تا قیامت خواهید پیمود.]'),
(5904, 84, 20, 'پس چرا ایمان نمی‌آورند؟'),
(5905, 84, 21, 'و هنگامی که قرآن بر آنان خوانده می‌شود، سجده نمی‌کنند؟'),
(5906, 84, 22, 'بلکه کافران همواره تکذیب می‌کنند.'),
(5907, 84, 23, 'و خدا به آنچه در دل دارند، داناتر است.'),
(5908, 84, 24, 'پس آنان را به عذابی دردناک بشارت بده!'),
(5909, 84, 25, 'مگر کسانی که ایمان آوردند و کارهای شایسته کردند، که آنان پاداشی بی‌پایان دارند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمانِ دارای برج‌ها!'),
(5911, 85, 2, 'و سوگند به روز موعود [جزا]!'),
(5912, 85, 3, 'و سوگند به شاهد [که اعمال امّت را می‌بیند،] و سوگند به مشهود! [که همه آن را خواهند دید.]'),
(5913, 85, 4, 'مرگ بر صاحبان گودال [پر آتش].'),
(5914, 85, 5, 'همان آتش پر هیزم [که مؤمنان را در آن سوزاندند].'),
(5915, 85, 6, 'آن‌گاه که بالای آن نشسته بودند،'),
(5916, 85, 7, 'و تماشاگر شکنجه‌ای بودند که نسبت به مؤمنان روا می‌داشتند.'),
(5917, 85, 8, 'نفرت آنان از مؤمنان فقط برای این بود که به خدای عزیز و ستوده ایمان آورده بودند.'),
(5918, 85, 9, 'همان که فرمانروایی آسمان‌ها و زمین از آنِ اوست و خداوند بر هر چیز گواه است.'),
(5919, 85, 10, 'همانا کسانی که مردان و زنان باایمان را مورد آزار و شکنجه قرار دادند و توبه نکردند، پس برای آنان عذاب دوزخ و نیز عذاب سوزان است.'),
(5920, 85, 11, 'بی‌تردید برای کسانی که ایمان آوردند و کارهای شایسته کردند، باغ‌هایی [بهشتی] است که نهرها از زیر [درختان] آن جاری است. این است رستگاری بزرگ!'),
(5921, 85, 12, 'قهر و مجازات پروردگارت شدید است.'),
(5922, 85, 13, 'اوست که [آفرینش را] آغاز می‌کند و [پس از مرگ‌] بازمی‌گرداند.'),
(5923, 85, 14, 'و اوست بخشنده و دوستدار [مؤمنان].'),
(5924, 85, 15, 'صاحب عرش است و ارجمند و باعظمت.'),
(5925, 85, 16, 'آنچه را بخواهد، حتماً انجام می‌دهد.'),
(5926, 85, 17, 'آیا سرگذشت آن لشکریان به تو رسیده است؟'),
(5927, 85, 18, '[لشکریان] فرعون و ثمود.'),
(5928, 85, 19, '[فقط آنان نبودند که حقّ را تکذیب کردند،] بلکه کافران [قوم تو] در تکذیبی سخت‌ترند.'),
(5929, 85, 20, 'و خداوند از هر سو بر آنان احاطه دارد.'),
(5930, 85, 21, '[کافران قرآن را جادویی از سوی تو می‌دانند، حال آن که چنین نیست.] بلکه آن کتابی خواندنی و ارجمند است.'),
(5931, 85, 22, 'که در لوحی محفوظ است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و ستاره طارِق!'),
(5933, 86, 2, 'و چه می‌دانی که طارِق چیست؟'),
(5934, 86, 3, 'ستاره‌ای است درخشنده.'),
(5935, 86, 4, '[به اینها سوگند که] هر کس مراقب و نگهبانی دارد.'),
(5936, 86, 5, 'پس انسان بنگرد که از چه آفریده شده است؟'),
(5937, 86, 6, 'از آبی جهنده آفریده شده است،'),
(5938, 86, 7, '[آبی] که از میان پشت و پیش بیرون می‌آید.'),
(5939, 86, 8, 'بی‌تردید او بر بازگرداندن انسان تواناست،'),
(5940, 86, 9, 'روزی که رازها آشکار شود.'),
(5941, 86, 10, 'پس برای انسان، هیچ نیرویی [از درون] و یاوری [از بیرون] نیست.'),
(5942, 86, 11, 'سوگند به آسمان باران‌زا!'),
(5943, 86, 12, 'سوگند به زمین که [برای رویش گیاهان] برشکافته است!'),
(5944, 86, 13, 'که بی‌گمان این [قرآن] سخنی است قاطع و جداکننده'),
(5945, 86, 14, 'و بیهوده و شوخی نیست.'),
(5946, 86, 15, 'کافران پیوسته نیرنگ می‌کنند.'),
(5947, 86, 16, 'و من نیز تدبیر می‌کنم.'),
(5948, 86, 17, 'پس کافران را مهلت بده و اندک زمانی آنان را به خود واگذار!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار بلندمرتبه‌ات را به پاکی یاد کن!'),
(5950, 87, 2, 'همان که آفرید و سامان بخشید.'),
(5951, 87, 3, 'و آن که [هر چیز را] اندازه نهاد و هدایت کرد.'),
(5952, 87, 4, 'و آن که چراگاه را رویانید.'),
(5953, 87, 5, 'و پس [از چندی] آن را خشک و تیره کرد.'),
(5954, 87, 6, 'ما [قرآن را] بر تو می‌خوانیم، پس فراموش نخواهی کرد.'),
(5955, 87, 7, 'مگر آنچه را خدا بخواهد که او بی‌گمان آشکار و نهان را می‌داند.'),
(5956, 87, 8, 'و ما تو را برای آسان‌ترین [راه دعوت] آماده می‌سازیم.'),
(5957, 87, 9, 'پس پند بده، اگر پند دادن سودمند باشد.'),
(5958, 87, 10, 'به زودی آن که [از خدا] می‌ترسد، پند گیرد.'),
(5959, 87, 11, 'و بدبخت‌ترینِ افراد از آن دوری کند.'),
(5960, 87, 12, 'همان کس که به آتش بزرگ‌تر درمی‌آید.'),
(5961, 87, 13, 'سپس در آنجا نه بمیرد و نه زندگی کند.'),
(5962, 87, 14, 'به راستی رستگار شد آن کس که خود را پاک گردانید.'),
(5963, 87, 15, 'و نام پروردگارش را یاد کرد و نماز گزارد.'),
(5964, 87, 16, '[بلکه شما منکران لجوج] زندگی دنیا را ترجیح می‌دهید.'),
(5965, 87, 17, 'و حال آن که آخرت بهتر و ماندگارتر است.'),
(5966, 87, 18, 'این [سخن] در کتاب‌های آسمانی پیشین نیزآمده است،'),
(5967, 87, 19, 'کتاب‌های ابراهیم و موسی.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آیا خبر حادثه‌ی فراگیر به تو رسیده است؟'),
(5969, 88, 2, 'در آن روز، چهره‌هایی خوار و زبونند.'),
(5970, 88, 3, '[با آن که] تلاش کرده‌اند و رنج برده‌اند،'),
(5971, 88, 4, '[امّا] به آتشی سوزان درآیند.'),
(5972, 88, 5, 'از چشمه‌ای جوشان نوشانده شوند.'),
(5973, 88, 6, 'و خوراکی جز خار خشک و تلخ ندارند.'),
(5974, 88, 7, 'که نه فربه کند و نه گرسنگی را از میان برد.'),
(5975, 88, 8, 'در چنین روزی چهره‌هایی خرّم و شادابند.'),
(5976, 88, 9, 'از کوشش خود خشنودند.'),
(5977, 88, 10, 'در بهشت برین [جای دارند].'),
(5978, 88, 11, 'آنجا سخن بیهوده‌ای نشنوند.'),
(5979, 88, 12, 'در آنجا چشمه‌ای جاری است.'),
(5980, 88, 13, 'در آنجا تخت‌هایی است پرارج و برافراشته.'),
(5981, 88, 14, 'و سبوهایی چیده شده،'),
(5982, 88, 15, 'و بالش‌هایی ردیف شده،'),
(5983, 88, 16, 'و فرش‌هایی گرانبها که گسترده شده است.'),
(5984, 88, 17, 'پس آیا به شتر نمی‌نگرند که چگونه آفریده شده؟'),
(5985, 88, 18, 'و به آسمان که چگونه برافراشته شده؟'),
(5986, 88, 19, 'و به کوه‌ها که چگونه برپا داشته شده؟'),
(5987, 88, 20, 'و به زمین که چگونه گسترده شده است؟'),
(5988, 88, 21, 'پس یادآوری کن که تو فقط یادآوری کننده‌ای.'),
(5989, 88, 22, 'تو بر آنان تسلّطی نداری [که به ایمان آوردن مجبورشان کنی].'),
(5990, 88, 23, 'ولی آن کس که روی گرداند و کفر ورزید،'),
(5991, 88, 24, 'پس خداوند او را به بزرگ‌ترین عذاب مجازات می‌کند.'),
(5992, 88, 25, 'بازگشت آنان به سوی ماست.'),
(5993, 88, 26, 'سپس حسابشان [نیز] بر عهده‌ی ماست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپیده‌دم!'),
(5995, 89, 2, 'سوگند به شب‌های ده‌گانه!'),
(5996, 89, 3, 'سوگند به زوج و فرد!'),
(5997, 89, 4, 'به شب سوگند آن‌گاه که سپری شود!'),
(5998, 89, 5, 'آیا در این [سوگندها]، برای خردمند، سوگندی مهم نیست؟'),
(5999, 89, 6, 'آیا ندیدی که پروردگارت با قوم عاد چه کرد؟'),
(6000, 89, 7, '[ساکنان شهر] ارم، که دارای قصرهای بلند و بناهای ستون‌دار بود.'),
(6001, 89, 8, '[بناهایی] که مانندش در دیگر شهرها ساخته نشده بود.'),
(6002, 89, 9, 'و با قوم ثمود که در وادی [میان عمّان و حضْرموت]، تخته‌سنگ‌ها را [برای ساختن خانه] می‌بریدند.'),
(6003, 89, 10, 'و [با] فرعون قدرتمند و دارای میخ‌ها؟ [که به چهار میخ می‌کشید و شکنجه می‌کرد.]'),
(6004, 89, 11, 'آنها که در شهرها طغیان کردند.'),
(6005, 89, 12, 'و در آن، فساد و تباهی افزودند.'),
(6006, 89, 13, 'پس پروردگارت تازیانه‌ی عذاب بر آنان فرود آورد.'),
(6007, 89, 14, 'همانا پروردگارت در کمین‌گاه است.'),
(6008, 89, 15, 'امّا انسان، هرگاه پروردگارش او را بیازماید و گرامیش دارد و نعمتش دهد، می‌گوید: «پروردگارم مرا گرامی داشته است.»'),
(6009, 89, 16, 'ولی هرگاه او را بیازماید و روزی او را تنگ سازد، می‌گوید: «پروردگارم مرا خوار کرده است.»'),
(6010, 89, 17, 'چنین نیست! بلکه [خواری شما برای این است که‌] یتیم را گرامی نمی‌دارید.'),
(6011, 89, 18, 'و یکدیگر را بر غذا دادن به بینوا تشویق نمی‌کنید.'),
(6012, 89, 19, 'و میراث را [حقّ و ناحقّ] یک‌جا می‌خورید.'),
(6013, 89, 20, 'و مال را بسیار دوست دارید.'),
(6014, 89, 21, 'چنین نیست [که می‌پندارید، بلکه] آن‌گاه که زمین به شدّت در هم کوبیده شود.'),
(6015, 89, 22, 'و [فرمان‌] پروردگارت فرارسد و فرشتگان صف در صف حاضر شوند،'),
(6016, 89, 23, 'در آن روز دوزخ را بیاورند. آن روز انسان یادآور شود؛ ولی این یادآوری چه سودی برایش دارد؟'),
(6017, 89, 24, 'می‌گوید: «ای کاش برای زندگی [امروز توشه‌ای] از پیش فرستاده بودم.»'),
(6018, 89, 25, 'پس در آن روز [عذاب خدا چنان است که] هیچ‌کس [دشمن خود را] مانند آن عذاب نکند.'),
(6019, 89, 26, 'و هیچ‌کس چون به بند کشیدن او، در بند نکشد.'),
(6020, 89, 27, 'هان ای جان آرام یافته!'),
(6021, 89, 28, 'به سوی پروردگارت بازگرد، که تو از او خشنود و او از تو راضی است.'),
(6022, 89, 29, 'پس در میان بندگان من در آی!'),
(6023, 89, 30, 'و به بهشت من وارد شو!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'به این شهر [مکه] سوگند می‌خورم.'),
(6025, 90, 2, 'در حالی که تو در این شهر ساکن هستی.'),
(6026, 90, 3, 'سوگند به پدر و فرزندی که پدید آورد.'),
(6027, 90, 4, 'که بی‌گمان ما انسان را در رنج و زحمت آفریدیم.'),
(6028, 90, 5, 'آیا می‌پندارد که هرگز کسی بر او دست نمی‌یابد؟'),
(6029, 90, 6, '[با زیاد شمردن انفاق خود] می‌گوید: «مال فراوانی را تباه کردم!»'),
(6030, 90, 7, 'آیا می‌پندارد که هیچ کس او را ندیده است؟ [که اعمال و کردارش را ثبت کند؟]'),
(6031, 90, 8, 'آیا برای او دو چشم قرار ندادیم؟'),
(6032, 90, 9, 'و یک زبان و دو لب؟'),
(6033, 90, 10, 'و دو راه [خیر و شرّ] را به او نمایاندیم.'),
(6034, 90, 11, 'امّا او در آن گردنه‌ی سخت قدم نگذاشت.'),
(6035, 90, 12, 'و چه می‌دانی که آن گردنه‌ی سخت چیست؟'),
(6036, 90, 13, 'آزاد کردن برده،'),
(6037, 90, 14, 'یا غذا دادن به روز گرسنگی و قحطی،'),
(6038, 90, 15, 'به یتیمی خویشاوند،'),
(6039, 90, 16, 'یا بینوایی خاک‌نشین.'),
(6040, 90, 17, 'علاوه بر اینها، از کسانی باشد که ایمان آورده و همدیگر را به شکیبایی و مهربانی سفارش کرده‌اند.'),
(6041, 90, 18, 'آنانند یاران [سعادتمندی که کارنامه‌ی آنان را به دست] راست [آنها می‌دهند].'),
(6042, 90, 19, 'و کسانی که به آیات ما کافر شدند، یاران شومی و شقاوت هستند.'),
(6043, 90, 20, 'بر آنان آتشی سرپوشیده و فراگیر احاطه دارد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشید و روشنایی آن!'),
(6045, 91, 2, 'سوگند به ماه، آن‌گاه که از پی خورشید درآید.'),
(6046, 91, 3, 'سوگند به روز، آن‌گاه که زمین را روشن سازد.'),
(6047, 91, 4, 'سوگند به شب، آن‌گاه که زمین را بپوشاند.'),
(6048, 91, 5, 'سوگند به آسمان و آن که آن را بنا کرد.'),
(6049, 91, 6, 'سوگند به زمین و آن که آن را گسترانید.'),
(6050, 91, 7, 'سوگند به جان [آدمی] و آن که آن را سامان داد،'),
(6051, 91, 8, 'پس پلیدی‌ها و پاکی‌هایش را به او الهام کرد.'),
(6052, 91, 9, '[به اینها سوگند] که به راستی رستگار شد آن که جان خود را از آلودگی پاک کرد.'),
(6053, 91, 10, 'و بی‌شک محروم گشت آن که آن را به پلیدی آلود.'),
(6054, 91, 11, 'قوم ثمود از روی سرکشی، تکذیب کردند.'),
(6055, 91, 12, 'آن‌گاه که بدبخت‌ترینِ آنها [برای کشتن ناقه] به‌پا خاست.'),
(6056, 91, 13, 'پس پیامبر خدا به آنان گفت: «شتر خدا و [نوبت] آبشخورش را [حرمت نهید]!»'),
(6057, 91, 14, 'پس او را تکذیب کردند و ناقه را کشتند و پروردگارشان به سزای گناهشان، آنان را درهم کوبید و [با خاک] یکسانشان کرد.'),
(6058, 91, 15, 'و [خداوند] از عاقبت آن کار [از کسی] بیم ندارد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب، آن‌گاه که [زمین را] فروپوشاند،'),
(6060, 92, 2, 'سوگند به روز، آن‌گاه که روشن شود،'),
(6061, 92, 3, 'سوگند به آن کس که نر و ماده را آفرید،'),
(6062, 92, 4, 'که تلاش‌های شما گوناگون و پراکنده است؛'),
(6063, 92, 5, 'امّا آن که انفاق کرد و [از خدا] پروا داشت،'),
(6064, 92, 6, 'و پاداش نیک [روز جزا] را تصدیق کرد،'),
(6065, 92, 7, 'پس به زودی او را برای پیمودن بهترین راه آماده خواهیم کرد.'),
(6066, 92, 8, 'ولی آن که بخل ورزید و خود را بی‌نیاز پنداشت،'),
(6067, 92, 9, 'و پاداش نیک [روز جزا] را تکذیب کرد،'),
(6068, 92, 10, 'پس به زودی او را به پیمودن دشوارترین راه خواهیم انداخت.'),
(6069, 92, 11, 'و چون هلاک شد، دارایی‌اش برای او کارساز نخواهد بود.'),
(6070, 92, 12, 'بی‌تردید هدایت [مردم] بر عهده‌ی ماست.'),
(6071, 92, 13, 'و بی‌شک آخرت و دنیا از آنِ ماست.'),
(6072, 92, 14, 'پس شما را از آتشی که زبانه می‌کشد، بیم دادم.'),
(6073, 92, 15, 'جز بدبخت‌ترین [مردم] در آن وارد نمی‌شود.'),
(6074, 92, 16, 'همان که [حقّ را] تکذیب کرد و روی گردانید.'),
(6075, 92, 17, 'ولی با تقواترین [مردم] از آن دور داشته خواهد شد.'),
(6076, 92, 18, 'همان که مالش را می‌بخشد تا پاک شود.'),
(6077, 92, 19, 'و از هیچ‌کس نزد او نعمتی [و حقی] نیست که این [انفاق] پاداش آن باشد.'),
(6078, 92, 20, 'تنها رضای پروردگار بلندمرتبه‌اش را می‌جوید.'),
(6079, 92, 21, 'و به زودی خشنود خواهد شد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به روشناییِ آغازِ روز!'),
(6081, 93, 2, 'سوگند به شب، آن‌گاه که آرامش بخشد،'),
(6082, 93, 3, 'که [ای پیامبر!] پروردگارت تو را رها نکرده و [بر تو] خشم نگرفته است.'),
(6083, 93, 4, 'و بی‌تردید آخرت برای تو بهتر از دنیاست.'),
(6084, 93, 5, 'و به زودی پروردگارت [چیزی] به تو عطا می‌کند که خشنود شوی.'),
(6085, 93, 6, 'آیا تو را یتیم نیافت، پس پناه داد و سامانت بخشید؟'),
(6086, 93, 7, 'و تو را سرگشته یافت، پس هدایت کرد.'),
(6087, 93, 8, 'و تو را تهی‌دست یافت، پس بی‌نیاز کرد.'),
(6088, 93, 9, 'پس بر یتیم تندی مکن و خوارش مساز!'),
(6089, 93, 10, 'و نیازمندِ خواهنده را از خود مران!'),
(6090, 93, 11, 'و نعمت پروردگارت را بازگو کن!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, '[ای پیامبر!] آیا سینه‌ات را نگشاده‌ایم؟'),
(6092, 94, 2, 'و بار سنگینت را از [دوش] تو برنداشتیم؟'),
(6093, 94, 3, 'آن [بار گران] که برای تو کمرشکن بود.'),
(6094, 94, 4, 'و نام تو را بلند [آوازه] کردیم.'),
(6095, 94, 5, 'پس [بدان که] با هر سختی، آسانی است.'),
(6096, 94, 6, '[آری!] با هر سختی، آسانی است.'),
(6097, 94, 7, 'پس هرگاه [از کار] فارغ شدی، [برای کار تازه] بکوش و رنج آن را بر خود هموار کن!'),
(6098, 94, 8, 'و مشتاقانه به سوی پروردگارت روی آور!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به انجیر و زیتون!'),
(6100, 95, 2, 'سوگند به طور سینا!'),
(6101, 95, 3, 'سوگند به [مکه] این شهر امن!'),
(6102, 95, 4, 'که ما انسان را در نیکوترین ساختار آفریدیم.'),
(6103, 95, 5, 'سپس او را به پست‌ترین مرحله بازگرداندیم.'),
(6104, 95, 6, 'مگر کسانی که ایمان آوردند و کارهای شایسته کردند که آنان پاداشی همیشه ماندگار دارند.'),
(6105, 95, 7, 'پس [ای انسان!] بعد از این [همه دلایل‌]، چه چیزی تو را به تکذیب قیامت وامی‌دارد؟'),
(6106, 95, 8, 'آیا خدا بهترینِ داوران نیست؟');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت که آفرید.'),
(6108, 96, 2, 'انسان را از خونِ بسته آفرید.'),
(6109, 96, 3, 'بخوان که پروردگار تو از همه گرامی‌تر است.'),
(6110, 96, 4, 'همان که با قلم آموخت.'),
(6111, 96, 5, 'آنچه را انسان نمی‌دانست، به او آموخت.'),
(6112, 96, 6, 'این چنین نیست [که انسان سپاس‌گزار باشد]، بی‌گمان آدمی سرکشی می‌کند.'),
(6113, 96, 7, 'چون که خود را بی‌نیاز می‌پندارد.'),
(6114, 96, 8, 'به راستی بازگشت همه به سوی پروردگار توست.'),
(6115, 96, 9, 'آیا دیدی آن کس را که بازمی‌داشت،'),
(6116, 96, 10, 'بنده‌ای را، آن‌گاه که نماز می‌خواند؟'),
(6117, 96, 11, 'آیا اندیشیده‌ای که اگر [آن نمازگزار] بر هدایت باشد،'),
(6118, 96, 12, 'یا به تقوا فرمان دهد [سزای نهی‌کننده‌ی او چیست]؟'),
(6119, 96, 13, 'آیا اندیشیده‌ای که [آن کافر] اگر [حقّ را] تکذیب کند و روی برگرداند [فقط خود را هلاک ساخته است]؟'),
(6120, 96, 14, 'آیا [انسان] نمی‌داند که خداوند [او را] می‌بیند؟'),
(6121, 96, 15, 'چنین نیست [که او می‌پندارد]! اگر [از کارهایش] دست برندارد، موی پیشانی او را سخت می‌گیریم و [به سوی دوزخ] می‌کشیم،'),
(6122, 96, 16, 'موی پیشانی دروغگوی خطاکار را.'),
(6123, 96, 17, 'پس او اهل محفلش را [به یاری] بخواند.'),
(6124, 96, 18, 'ما هم به زودی مأموران دوزخ را فرامی‌خوانیم.'),
(6125, 96, 19, 'نه! [ای پیامبر!] از او فرمان مبر! و [در برابر خدا] سجده کن! و [به او] نزدیک شو!');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما آن [قرآن] را در شب قدر فروفرستادیم.'),
(6127, 97, 2, 'و چه می‌دانی که شب قدر چیست؟'),
(6128, 97, 3, 'شب قدر از هزار ماه بهتر است.'),
(6129, 97, 4, 'در آن [شب]، فرشتگان و روح به اجازه‌ی پروردگارشان برای [تقدیر] هر کاری فرود می‌آیند.'),
(6130, 97, 5, '[آن شب] تا دمیدن سپیده، [سراسر] سلام و رحمت است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کافرانِ اهل کتاب و مشرکان، [از آیین خود] جدا نمی‌شدند تا آن که دلیلی روشن برایشان بیاید.'),
(6132, 98, 2, 'پیامبری از سوی خدا که کتاب‌هایی پاک می‌خواند.'),
(6133, 98, 3, 'در آن‌ها نوشته‌هایی استوار و با ارزش است.'),
(6134, 98, 4, 'و اهل کتاب [گروه گروه و] پراکنده نشدند مگر پس از آن که دلیل روشن برای آنان آمد [و حجّت تمام شد، سپس درباره‌ی پذیرش آن پراکنده شدند].'),
(6135, 98, 5, 'در حالی که فرمان یافته بودند تنها خدا را بپرستند، دین را برای او خالص ساخته و حق‌گرا باشند و نماز را برپا دارند و زکات بپردازند و این است دین استوار.'),
(6136, 98, 6, 'همانا کسانی از اهل کتاب و مشرکان که کافر شدند، در آتش دوزخند. در آن جاودانه‌اند. آنانند بدترینِ آفریده‌ها.'),
(6137, 98, 7, 'بی‌شک کسانی که ایمان آورده و کارهای شایسته کرده‌اند، آنان بهترینِ آفریدگانند.'),
(6138, 98, 8, 'پاداش اینان نزد پروردگارشان، باغ‌هایی پاینده است که زیر [درختان] آن نهرها جاری است. برای همیشه در آن جاودانه‌اند. خدا از آنان خشنود است و آنان نیز از او خشنودند. این [مقام] ویژه‌ی کسی است که از پروردگارش پروا کند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'آن‌گاه که زمین به لرزش شدید، لرزانده شود،'),
(6140, 99, 2, 'و زمین بارهای سنگین خود را بیرون افکند،'),
(6141, 99, 3, 'و انسان بگوید: «[زمین] چه بر سرش آمده؟»'),
(6142, 99, 4, 'در آن روز زمین خبرهای خود را بازگو می‌کند.'),
(6143, 99, 5, 'زیرا پروردگارت به آن وحی کرده است.'),
(6144, 99, 6, 'در آن روز مردم [به سوی بهشت یا دوزخ‌] پراکنده بازمی‌گردند تا کارهایشان به آنان نشان داده شود.'),
(6145, 99, 7, 'پس هر کس به مقدار ذرّه‌ای کار نیک کند، آن را می‌بیند.'),
(6146, 99, 8, 'و هر کس هم‌وزن ذرّه‌ای کار بد کند، آن را می‌بیند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به اسب‌های دونده که [در میدان جهاد] نفسشان به شماره می‌افتد.'),
(6148, 100, 2, 'و از برخورد نعل‌ها با سنگ‌های بیابان جرقّه می‌جهانند.'),
(6149, 100, 3, 'و بامدادان بر دشمن هجوم می‌برند.'),
(6150, 100, 4, 'که با آن [یورش] گرد و غبار فراوانی برمی‌انگیزند.'),
(6151, 100, 5, 'و بامدادان در میان معرکه و جمع [دشمنان] درمی‌آیند.'),
(6152, 100, 6, '[به اینها سوگند] که انسان نسبت به پروردگارش بسیار ناسپاس است.'),
(6153, 100, 7, 'و بی‌شک خود بر این [ناسپاسی] گواه است.'),
(6154, 100, 8, 'و بی‌تردید سخت شیفته‌ی مال [و ثروت] است.'),
(6155, 100, 9, 'آیا [انسان] نمی‌داند که وقتی آنچه در گورهاست، برانگیخته شود،'),
(6156, 100, 10, 'و آنچه در سینه‌هاست، فاش شود،'),
(6157, 100, 11, 'آن روز، بی‌گمان پروردگارشان به [کار] آنان آگاه است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کوبنده!'),
(6159, 101, 2, '[حادثه‌ی] کوبنده چیست؟'),
(6160, 101, 3, 'و تو چه می‌دانی آن کوبنده چیست؟'),
(6161, 101, 4, 'روزی که مردم [از هراس] مانند پروانه‌ها پراکنده باشند.'),
(6162, 101, 5, 'و کوه‌ها مانند پشمِ رنگین حلاّجی شده گردد.'),
(6163, 101, 6, 'پس هرکس ترازوهای اعمالش سنگین باشد،'),
(6164, 101, 7, 'پس او در زندگی رضایت‌بخشی است.'),
(6165, 101, 8, 'ولی هر کس ترازوهای اعمالش سبک باشد،'),
(6166, 101, 9, 'پس در آغوش آتش است.'),
(6167, 101, 10, 'و تو چه می‌دانی آن چیست؟'),
(6168, 101, 11, 'آتشی است بسیار سوزان.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'زیاده‌خواهی شما را سرگرم کرد.'),
(6170, 102, 2, 'تا آنجا که گورستان‌ها را دیدار کردید [و مردگان را شمردید!]'),
(6171, 102, 3, 'چنین نیست [که می‌پندارید]، به زودی خواهید فهمید [چه سختی‌ها در پیش دارید].'),
(6172, 102, 4, 'آری! چنین نیست [که می‌پندارید]، در آینده خواهید دانست [که با چه عذاب‌هایی روبرویید].'),
(6173, 102, 5, 'چنین نیست [که می‌پندارید]. اگر شما [به آخرت] علم یقینی داشتید،'),
(6174, 102, 6, 'قطعاً دوزخ را خواهید دید.'),
(6175, 102, 7, 'سپس [با وارد شدن در آن،] آن را به دیده‌ی یقین خواهید دید.'),
(6176, 102, 8, 'سپس در آن روز از نعمت‌ها بازخواست خواهید شد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به روزگارِ [غلبه‌ی حقّ بر باطل‌]،'),
(6178, 103, 2, '[که‌] بی‌تردید انسان در زیان‌کاری بزرگی است،'),
(6179, 103, 3, 'مگر آنان که ایمان آوردند و کارهای شایسته کردند و یکدیگر را به حقّ و استقامت سفارش می‌کنند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر عیب‌جوی طعنه‌زن!'),
(6181, 104, 2, 'آن که ثروتی فراهم آورده و [پی در پی] آن را می‌شمارد.'),
(6182, 104, 3, 'می‌پندارد که دارایی‌اش او را جاودان ساخته است.'),
(6183, 104, 4, 'هرگز! سوگند که او در آتش افکنده می‌شود.'),
(6184, 104, 5, 'و چه می‌دانی که آن آتش چیست؟'),
(6185, 104, 6, 'آتش افروخته‌ی خداست،'),
(6186, 104, 7, 'که بر دل‌ها راه می‌یابد.'),
(6187, 104, 8, 'آن آتش بر آنان سربسته است [و راه گریزی ندارند آن نیست]،'),
(6188, 104, 9, '[آتشی] در ستون‌هایی بلند و کشیده.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آیا ندیدی که پروردگارت با فیل‌سواران چه کرد؟'),
(6190, 105, 2, 'آیا نیرنگشان را بی‌اثر نساخت؟'),
(6191, 105, 3, 'و بر سر آنان پرندگانی را دسته دسته فرستاد،'),
(6192, 105, 4, 'که با سنگریزه‌هایی از گلِ سخت آنان را هدف قرار می‌دادند،'),
(6193, 105, 5, 'پس آنان را همچون کاهِ خورده شده [متلاشی] کرد.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'تا قریش را الفت دهد،'),
(6195, 106, 2, 'الفت آنان در سفرهای [بازرگانی] زمستانی و تابستانی [تا در آرامش و امنیت، معاششان را تأمین کنند].'),
(6196, 106, 3, 'پس [به شکرانه‌ی این نعمت،] پروردگار این خانه را بپرستند.'),
(6197, 106, 4, 'همان که آنان را از گرسنگی [و قحطی رهانید و] غذا داد و از ترس، ایمنی‌شان بخشید.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا کسی که پیوسته [روزِ] جزا را انکار می‌کند، دیدی؟'),
(6199, 107, 2, 'او همان کسی است که یتیم را با خشونت می‌راند.'),
(6200, 107, 3, 'و [دیگران را] بر غذا دادن به بینوا تشویق نمی‌کند.'),
(6201, 107, 4, 'پس وای به حال نمازگزاران!'),
(6202, 107, 5, 'آنان که از نمازشان غافل و نسبت به آن سهل‌انگارند.'),
(6203, 107, 6, 'همانان که پیوسته ریا می‌کنند.'),
(6204, 107, 7, 'و از [دادن] زکات دریغ می‌کنند.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, '[ای پیامبر!] بی‌تردید ما به تو خیر فراوان عطا کردیم.'),
(6206, 108, 2, 'پس برای پروردگارت نماز بگزار و شتر قربانی کن!'),
(6207, 108, 3, 'بی‌گمان [دشمن] کینه‌توزت خود بدون نسل است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, '[ای پیامبر!] بگو: «ای کافران!'),
(6209, 109, 2, 'من آنچه را شما می‌پرستید، نمی‌پرستم.'),
(6210, 109, 3, 'و شما پرستنده‌ی آنچه من می‌پرستم، نیستید.'),
(6211, 109, 4, 'و من [نیز] پرستنده‌ی آنچه شما می‌پرستید، نیستم.'),
(6212, 109, 5, 'و شما پرستنده‌ی آنچه من می‌پرستم، نیستید.'),
(6213, 109, 6, 'آیین شما برای خودتان و آیین من برای خودم»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, '[ای پیامبر!] هنگامی که یاری خدا و پیروزی فرارسد،'),
(6215, 110, 2, 'و مردم را ببینی که دسته دسته در دین خدا وارد می‌شوند،'),
(6216, 110, 3, 'پس پروردگارت را با سپاس و ستایش به پاکی یادکن و از او آمرزش بخواه که او همواره توبه‌پذیر است.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'توان ابولهب تباه و او نابود باد!'),
(6218, 111, 2, 'ثروتش و آنچه به دست آورده بود، هیچ به کارش نیامد.'),
(6219, 111, 3, 'به زودی در آتشی شعله‌ور درآید و بسوزد.'),
(6220, 111, 4, 'و زنش، آن هیزم‌کش [آتش‌افروز نیز در آتش درآید].'),
(6221, 111, 5, '[در حالی که] به گردنش ریسمانی از لیف خرماست.');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, '[ای پیامبر!] بگو: «او خداوند یگانه است.'),
(6223, 112, 2, 'خدای بی‌نیاز [که نیازمندان فقط به او رو می‌کنند.]'),
(6224, 112, 3, 'نه فرزند دارد و نه فرزند کسی است.'),
(6225, 112, 4, 'و هیچ همتایی برای او نیست».');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, '[ای پیامبر!] بگو: «پناه می‌برم به پروردگار سپیده‌دم.'),
(6227, 113, 2, 'از شرّ آنچه او آفرید [و دارای شرّ است].'),
(6228, 113, 3, 'و از شرّ تاریکی شب، آن‌گاه که همه جا را فراگیرد.'),
(6229, 113, 4, 'و از شرّ زنان افسون‌گری که در گره‌ها می‌دمند [و جادو می‌کنند].'),
(6230, 113, 5, 'و از شرّ [هر] حسود، آن‌گاه که حسد ورزد.»');
INSERT INTO `fa_gharaati` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, '[ای پیامبر!] بگو: «پناه می‌برم به پروردگار آدمیان،'),
(6232, 114, 2, 'فرمانروای آدمیان،'),
(6233, 114, 3, 'معبود آدمیان،'),
(6234, 114, 4, 'از شرّ وسوسه‌ی شیطان [که به گناه وسوسه می‌کند و پنهان می‌شود]،'),
(6235, 114, 5, 'همان‌ که همواره در سینه‌های آدمیان وسوسه می‌کند،'),
(6236, 114, 6, 'از جنس جنّ باشد یا از آدمیان».');
