INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداوند بخشنده مهربان'),
(2, 1, 2, 'ستایش خدای را که پروردگار جهانیان است.'),
(3, 1, 3, 'خدایی که بخشنده و مهربان است.'),
(4, 1, 4, 'مالک (و پادشاه) روز جزاست (روز کیفر نیک و بد خلق).'),
(5, 1, 5, '(پروردگارا) تنها تو را می‌پرستیم، و از تو یاری می‌جوییم و بس.'),
(6, 1, 6, 'تو ما را به راه راست هدایت فرما.'),
(7, 1, 7, 'راه آنان که به آنها انعام فرمودی، نه راه کسانی که بر آنها خشم فرمودی (قوم یهود) و نه گمراهان (چون اغلب نصاری).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم (از رموز قرآن است).'),
(9, 2, 2, 'این کتاب که هیچ شک در آن نیست، راهنمای پرهیزگاران است.'),
(10, 2, 3, 'آن کسانی که به جهان غیب ایمان آرند و نماز به پا دارند و از هر چه روزیشان کردیم به فقیران انفاق کنند.'),
(11, 2, 4, 'و آنان که ایمان آرند به آنچه به تو و آنچه به (پیغمبران) پیش از تو فرستاده شده و آنها خود به عالم آخرت یقین دارند.'),
(12, 2, 5, 'آنان از لطف پروردگار خویش به راه راستند و آنها به حقیقت، رستگاران عالمند.'),
(13, 2, 6, '(ای رسول ما) کافران را یکسان است که ایشان را بترسانی یا نترسانی، ایمان نخواهند آورد.'),
(14, 2, 7, 'خدا مهر نهاد بر دلها و گوشهای ایشان، و بر چشمهای ایشان پرده افتاده، و ایشان را عذابی سخت خواهد بود.'),
(15, 2, 8, 'و گروهی از مردم (یعنی منافقان) گویند: ما ایمان آورده‌ایم به خدا و به روز قیامت، و حال آنکه ایمان نیاورده‌اند.'),
(16, 2, 9, 'می‌خواهند خدا و اهل ایمان را فریب دهند و حال آنکه فریب ندهند مگر خود را، و این را نمی‌دانند.'),
(17, 2, 10, 'در دلهای ایشان بیماری (جهل و عناد) است، خدا بر بیماری آنها بیفزاید، و برای ایشان است عذابی دردناک، به سبب آنکه پیوسته دروغ می‌گفتند.'),
(18, 2, 11, 'و چون آنان را گویند که فساد در زمین نکنید، گویند: تنها ما کار به صلاح کنیم.'),
(19, 2, 12, 'آگاه باشید آنها خود مفسدند ولی نمی‌دانند.'),
(20, 2, 13, 'و چون به ایشان گویند: ایمان آورید چنانکه دیگران ایمان آوردند؛ گویند: چگونه ایمان آوریم مانند بیخردان؟! آگاه باشید که ایشان خود بی‌خردند ولی نمی‌دانند.'),
(21, 2, 14, 'و چون به اهل ایمان برسند گویند: ما ایمان آوردیم؛ و وقتی با شیاطین خود خلوت کنند گویند: ما با شماییم، جز این نیست که (مؤمنان را) مسخره می‌کنیم.'),
(22, 2, 15, 'خدا به ایشان استهزا کند و در سرکشی مهلتشان دهد که حیران بمانند.'),
(23, 2, 16, 'آنانند که گمراهی را به راه راست خریدند، پس تجارت ایشان سود نکرد و راه هدایت نیافتند.'),
(24, 2, 17, 'مثل ایشان مانند کسی است که آتشی بیفروزد، و همین که روشن کند پیرامون او را، خدا روشنی آنان را ببرد و رها کند ایشان را در تاریکی‌یی که هیچ نبینند.'),
(25, 2, 18, 'آنها کر و گنگ و کورند و (از ضلالت خود) بر نمی‌گردند.'),
(26, 2, 19, 'یا مثل آنان چون کسانی است که در بیابان، بارانی تند که در آن تاریکی و رعد و برق است بر آنان ببارد، و آنان انگشتانشان را از ترس مرگ در گوش نهند مبادا از شدت صدای صاعقه بمیرند، و خدا بر کافران احاطه دارد.'),
(27, 2, 20, 'نزدیک است برق روشنی چشمهایشان را ببرد، هرگاه روشنی بینند می‌روند در آن، و چون تاریک شود بایستند، و اگر خدا می‌خواست گوش آنها را کر و چشم آنان را کور می‌ساخت، که خداوند بر هر چیز تواناست.'),
(28, 2, 21, 'ای مردم، بپرستید خدایی را که آفریننده شما و پیشینیان شماست، باشد که پارسا و منزّه شوید.'),
(29, 2, 22, 'آن خدایی که برای شما زمین را گسترد و آسمان را برافراشت و فرو بارید از آسمان آبی که به سبب آن بیرون آورد میوه‌های گوناگون برای روزی شما، پس کسی را مثل و مانند او قرار ندهید در حالی که می‌دانید (که بی‌مانند است).'),
(30, 2, 23, 'و اگر شما را شکّی است در قرآنی که بر بنده خود (محمد صلّی اللَه علیه و آله و سلّم) فرستادیم، پس بیاورید یک سوره مانند آن، و گواهان خود را بخوانید به جز خدا، اگر راست می‌گویید.'),
(31, 2, 24, 'و اگر این کار را نکردید و هرگز نتوانید کرد پس بپرهیزید از آتشی که هیزم آن مردم بدکار و سنگهای خارا است که (از قهر خدا) برای کافران مهیا شده است.'),
(32, 2, 25, 'و مژده ده کسانی را که ایمان آوردند و نیکوکاری پیشه کردند که جایگاه آنان باغهایی است که نهرها در آن جاری است، و چون از میوه‌های گوناگون آن بهره‌مند شوند گویند: این مانند همان میوه‌هایی است که پیش از این در دنیا ما را نصیب بود، و از نعمتهایی مانند یکدیگر بر آنان آورند، و آنها را در آن جایگاههای خوش، جفتهایی پاک و پاکیزه است و در آن بهشت، جاوید خواهند زیست.'),
(33, 2, 26, 'و خدا را شرم و ملاحظه از آن نیست که به پشه و چیزی بزرگتر از آن مثل زند، پس آنهایی که به خدا ایمان آورده‌اند می‌دانند که آن مثل حق است از جانب او، و اما آنهایی که کافرند می‌گویند: خدا را از این مثل چه مقصود است؟ گمراه می‌کند به آن مثل بسیاری را و هدایت می‌کند بسیاری را! و گمراه نمی‌کند به آن مگر فاسقان را.'),
(34, 2, 27, 'کسانی که عهد خدا را پس از محکم بستن می‌شکنند و رشته‌ای را که خدا امر به پیوند آن کرده می‌گسلند و در میان اهل زمین فساد می‌کنند، ایشان به حقیقت زیانکارند.'),
(35, 2, 28, 'چگونه کافر می‌شوید به خدا و حال آنکه مرده بودید و خدا شما را زنده کرد و دیگر بار بمیراند و باز زنده کند و عاقبت به سوی او باز گردانده می‌شوید؟!'),
(36, 2, 29, 'او خدایی است که همه موجودات زمین را برای شما خلق کرد، و پس از آن به خلقت آسمانها نظر گماشت و هفت آسمان را بر فراز یکدیگر برافراشت و او به همه چیز داناست.'),
(37, 2, 30, 'و (به یاد آر) وقتی که پروردگارت فرشتگان را فرمود که من در زمین خلیفه‌ای خواهم گماشت، گفتند: آیا کسانی در زمین خواهی گماشت که در آن فساد کنند و خونها بریزند و حال آنکه ما خود تو را تسبیح و تقدیس می‌کنیم؟! خداوند فرمود: من چیزی (از اسرار خلقت بشر) می‌دانم که شما نمی‌دانید.'),
(38, 2, 31, 'و خدا همه اسماء را به آدم یاد داد، آن گاه حقایق آن اسماء را در نظر فرشتگان پدید آورد و فرمود: اسماء اینان را بیان کنید اگر شما در دعوی خود صادقید.'),
(39, 2, 32, 'گفتند: منزهی تو، ما نمی‌دانیم جز آنچه تو خود به ما تعلیم فرمودی، که تویی دانا و حکیم.'),
(40, 2, 33, 'فرمود: ای آدم، ملائکه را به اسماء این حقایق آگاه ساز. چون آگاه ساخت، خدا فرمود: آیا شما را نگفتم که من بر غیب آسمانها و زمین دانا و بر آنچه آشکار و پنهان دارید آگاهم؟'),
(41, 2, 34, 'و چون فرشتگان را فرمان دادیم که بر آدم سجده کنید، همه سجده کردند مگر شیطان که ابا و تکبّر ورزید و از فرقه کافران گردید.'),
(42, 2, 35, 'و گفتیم: ای آدم تو با جفت خود در بهشت جای گزین و در آنجا از هر نعمت که بخواهید فراوان برخوردار شوید، ولی به این درخت نزدیک نشوید که از ستمکاران خواهید بود.'),
(43, 2, 36, 'پس شیطان آنها را به لغزش افکند (تا از آن درخت خوردند) و (بدین عصیان) آنان را از آن مقام بیرون آورد، و گفتیم که (از بهشت) فرود آیید که برخی از شما برخی را دشمنید، و شما را در زمین تا روز مرگ قرارگاه و بهره خواهد بود.'),
(44, 2, 37, 'پس آدم از خدای خود کلماتی آموخت و خداوند توبه او را پذیرفت، زیرا خدا بسیار توبه‌پذیر و مهربان است.'),
(45, 2, 38, 'گفتیم: همه از بهشت فرود آیید، تا آن گاه که از جانب من راهنمایی برای شما آید، آنان که پیروی او کنند هرگز بیمناک و اندوهگین نخواهند شد.'),
(46, 2, 39, 'و آنان که کافر شدند و تکذیب آیات ما کردند اهل دوزخند و در آتش آن همیشه معذب خواهند بود.'),
(47, 2, 40, 'ای بنی اسرائیل، به یاد آرید نعمتهایی که به شما عطا کردم، و به عهد من وفا کنید تا به عهد شما وفا کنم، و تنها از من بر حذر باشید.'),
(48, 2, 41, 'و به قرآنی که فرستادم ایمان آورید که تورات شما را تصدیق می‌کند، و اوّل کافر به آن نباشید، و آیات مرا به بهای اندک نفروشید، و تنها از قهر من بپرهیزید.'),
(49, 2, 42, 'و حق را به باطل مپوشانید و حقیقت را پنهان نسازید و حال آنکه (به حقّانیّت آن) واقفید.'),
(50, 2, 43, 'و نماز به پا دارید و زکات بدهید و با خدا پرستان حق را پرستش کنید.'),
(51, 2, 44, 'چگونه شما مردم را به نیکوکاری دستور می‌دهید و خود را فراموش می‌کنید و حال آنکه کتاب خدا را می‌خوانید، چرا اندیشه نمی‌کنید؟'),
(52, 2, 45, 'و یاری جویید از خدا به صبر و نماز، که نماز (با حضور قلب) امری بسیار بزرگ و دشوار است مگر بر خداپرستان فروتن.'),
(53, 2, 46, 'آنان که می‌دانند به پیشگاه خدا حاضر خواهند شد و بازگشتشان به سوی او خواهد بود.'),
(54, 2, 47, 'ای بنی اسرائیل، یاد کنید از نعمتهایی که به شما عطا کردم و (به نعمت کتاب و رسول) شما را بر عالمیان برتری دادم.'),
(55, 2, 48, 'و حذر کنید از آن روزی که هیچ کس به کار دیگری نیاید، و شفاعت هیچ کس پذیرفته نشود، و فدا و عوض از کسی قبول نکنند، و هیچ یاری کننده و فریاد رسی نخواهند داشت.'),
(56, 2, 49, 'و (به یاد آرید) هنگامی که شما را از ستم فرعونیان نجات دادیم که از آنها سخت در شکنجه بودید، (به حدّی که) پسران شما را کشته و زنانتان را زنده نگاه می‌داشتند، و آن بلا و امتحانی بزرگ بود که خدا شما را بدان می‌آزمود.'),
(57, 2, 50, 'و (یاد کنید) هنگامی که دریا را شکافتیم و شما را نجات دادیم و فرعونیان را غرق کردیم و شما مشاهده می‌کردید.'),
(58, 2, 51, 'و (یاد آورید) موقعی که با موسی چهل شب وعده نهادیم، پس شما (در غیبت او) گوساله پرستی اختیار کرده ستمکار و بیدادگر شدید.'),
(59, 2, 52, 'آن گاه شما را پس از چنین کار زشت بخشیدیم، شاید سپاسگزار شوید.'),
(60, 2, 53, 'و (به یاد آورید) وقتی که به موسی کتاب و فرقان عطا کردیم، باشد که به راه حق هدایت یابید.'),
(61, 2, 54, 'و (یاد کنید) وقتی که موسی به قوم خود گفت: ای قوم، شما به نفس خود ستم کردید که (به جهالت) گوساله پرست شدید، اکنون به سوی آفریدگارتان باز گردید و (به کیفر جهالت خود) به کشتن یکدیگر تیغ برکشید، این برای شما در پیشگاه آفریدگارتان بهترین (کفّاره عمل) است. آن گاه از شما در گذشت که او بسیار توبه‌پذیر و مهربان است.'),
(62, 2, 55, 'و (یاد آرید) وقتی که گفتید: ای موسی ما به تو ایمان نمی‌آوریم تا آنکه خدا را آشکار ببینیم، پس صاعقه سوزان بر شما فرود آمد و آن را مشاهده می‌کردید.'),
(63, 2, 56, 'سپس شما را بعد از مرگ برانگیختیم شاید خدا را شکر گزارید.'),
(64, 2, 57, 'و ابر را سایبان شما ساختیم و مرغ بریان و ترانگبین را بر شما فرستادیم (و گفتیم) از این روزیهای پاک که به شما دادیم تناول کنید، ولی (شکر این نعمت را به جا نیاوردند) نه به ما بلکه به خود ستم کردند.'),
(65, 2, 58, 'و (به یاد آرید) وقتی که گفتیم: وارد این قریه (بیت المقدس) شوید و از نعمتهای آن فراوان تناول کنید و از آن در، سجده کنان داخل شوید و بگویید: (خدایا) گناهان ما فرو ریز، تا از خطای شما درگذریم؛ و بر ثواب نیکوکاران خواهیم افزود.'),
(66, 2, 59, 'پس از آن، ستمکاران حکم خدا را به غیر آن که به آنها گفته بودند تبدیل نمودند، و ما بر آن ستمکاران به کیفر بدکاری و نافرمانی آنها عذابی سخت از آسمان نازل کردیم.'),
(67, 2, 60, 'و (به یاد آرید) وقتی که موسی برای قوم خود طلب آب کرد، به او گفتیم: عصای خود را بر سنگ زن، پس دوازده چشمه آب از آن سنگ جوشید و هر سبطی آبشخور خود را دانست (و گفتیم) از آنچه خدا روزی شما ساخته بخورید و بیاشامید و در زمین به فتنه‌انگیزی و فساد نپردازید.'),
(68, 2, 61, 'و (به یاد آرید) وقتی که به موسی اعتراض کردید که ما بر یک نوع طعام صبر نخواهیم کرد، از خدای خود بخواه تا برای ما از زمین نباتاتی برآورد مانند سبزی و خیار و سیر و عدس و پیاز. موسی گفت: آیا می‌خواهید غذای بهتری را که دارید به پست‌تر از آن تبدیل کنید؟ (حال که تقاضای شما این است) به شهر مصر فرود آیید که در آنجا آنچه درخواست کردید مهیّاست. و بر آن‌ها ذلّت و خواری مقدّر گردید، و چون دست از ستمکاری و عصیان برنداشته و به آیات خدا کافر می‌گشتند و انبیا را به ناحق کشتند دیگر بار به خشم و قهر خدا گرفتار شدند.'),
(69, 2, 62, 'هر یک از مسلمانان و یهود و نصاری و صابئان (پیروان حضرت یحیی) که از روی حقیقت به خدا و روز قیامت ایمان آورد و نیکوکاری پیشه کند، البته آنها از خدا پاداش نیک یابند و هیچ گاه بیمناک و اندوهگین نخواهند بود.'),
(70, 2, 63, 'و (ای بنی اسرائیل یاد آرید) وقتی که از شما پیمان گرفتیم و کوه طور را بالای سر شما برافراشتیم، (و دستور دادیم که) احکام تورات را با عقیده محکم پیروی کنید و پیوسته آن را در نظر گیرید، باشد که پرهیزگار شوید.'),
(71, 2, 64, 'بعد از آن همه عهد و پیمان (از خدا) روی گردانیدید؛ و اگر فضل و رحمت خدا شامل حال شما نمی‌شد البته در شمار زیانکاران بودید.'),
(72, 2, 65, 'و محققا دانسته‌اید جماعتی از شما را که عصیان ورزیده حرمت شنبه را نگاه نداشتند، گفتیم: بوزینه شوید و راندگانی (دور از قرب حق).'),
(73, 2, 66, 'و این عقوبت مسخ را کیفر آنها و عبرت اخلاف آنها و پند پرهیزکاران گردانیدیم.'),
(74, 2, 67, 'و (به یاد آرید) وقتی که موسی به قوم خود فرمود که به امر خدا گاوی را ذبح کنید، گفتند: ما را به تمسخر گرفته‌ای؟ موسی گفت: پناه می‌برم به خدا از آنکه (سخن به تمسخر گویم و) از مردم نادان باشم.'),
(75, 2, 68, 'گفتند: از خدایت بخواه که خصوصیت گاو را برای ما معیّن کند. موسی گفت: خدا می‌فرماید: گاوی نه پیر از کار افتاده و نه جوان کار نکرده، بلکه میانه این دو حال باشد، حال انجام دهید آنچه مأمورید.'),
(76, 2, 69, 'گفتند: از خدایت بخواه که رنگ آن گاو را معیّن کند. موسی گفت: خدا می‌فرماید گاو زرد زرّینی باشد که بینندگان را فرح بخشد.'),
(77, 2, 70, 'باز گفتند: از خدایت بخواه چگونگی آن گاو را کاملا برای ما روشن گرداند که هنوز بر ما مشتبه است و (چون رفع اشتباه شود) البته به خواست خدا راه هدایت پیش گیریم.'),
(78, 2, 71, 'موسی گفت: خدا می‌فرماید آن گاو هم آن قدر به کار رام نباشد که زمین شیار کند و آب به کشتزار دهد و هم بی‌عیب و یکرنگ باشد. گفتند: اکنون حقیقت را روشن ساختی. و گاوی بدان اوصاف کشتند، لیکن نزدیک بود باز نافرمانی کنند.'),
(79, 2, 72, 'و (به یاد آرید) وقتی که نفسی را کشتید و یکدیگر را درباره آن متهم کرده و نزاع برانگیختید، و خداوند می‌خواست راز پنهانی شما را آشکار کند.'),
(80, 2, 73, 'پس دستور دادیم بعضی از اعضاء گاو را بر بدن کشته زنید (تا زنده شود و قاتل را معرفی کند). این گونه خداوند مردگان را زنده خواهد فرمود، و قدرت کامله خویش را به شما آشکار می‌نماید، باشد که عقل خود را به کار برید.'),
(81, 2, 74, 'پس با این معجزه باز چنان سخت دل شدید، که دلهایتان چون سنگ یا سخت‌تر از آن شد، چه آنکه از پاره‌ای سنگها نهرها بجوشد و برخی دیگر از سنگها بشکافد و آب از آن بیرون آید و پاره‌ای از ترس خدا فرود آیند. و (ای سنگدلان بترسید که) خدا از کردار شما غافل نیست.'),
(82, 2, 75, 'آیا طمع دارید که یهودان به (دین) شما بگروند در صورتی که گروهی از آنان کلام خدا را شنیده و به دلخواه خود تحریف می‌کنند با آنکه در کلام خود تعقل کرده و معنی آن را دریافته‌اند.'),
(83, 2, 76, 'و هرگاه با مؤمنان روبرو شوند گویند: ما نیز ایمان آورده‌ایم؛ و چون با یکدیگر خلوت کنند گویند: چرا دری که خدا از علوم به روی شما گشوده به روی مسلمانان باز می‌کنید تا به کمک همان علوم با شما نزد خدایتان محاجّه کنند؟ چرا راه عقل و اندیشه نمی‌پویید؟'),
(84, 2, 77, 'آیا نمی‌دانند که خدا بر آنچه پنهان داشته و یا آشکار کنند آگاه است؟'),
(85, 2, 78, 'و بعضی عوام یهود که خواندن و نوشتن هم ندانند تورات را جز آمال باطل خود نپندارند و تنها پابست خیالات خام و بیهوده خویشند.'),
(86, 2, 79, 'پس وای بر آن کسانی که از پیش خود کتاب را نوشته و به خدای متعال نسبت دهند تا به بهای اندک بفروشند، پس وای بر آنها از آن نوشته‌ها و آنچه از آن به دست آرند.'),
(87, 2, 80, 'و یهود گفتند که هیچ وقت آتش عذاب به ما نرسد مگر چند روزی معدود. به آنان بگو: آیا بر آنچه دعوی می‌کنید عهد و پیمانی از خدا گرفته‌اید که خدا هم از آن عهد هرگز تخلف نکند یا چیزی به خیال جاهلانه خود به خدا نسبت می‌دهید؟'),
(88, 2, 81, 'آری، هر کس اعمالی زشت اندوخت و کردار بدش به او احاطه نمود چنین کسانی اهل دوزخ‌اند و در آن آتش پیوسته معذّب خواهند بود.'),
(89, 2, 82, 'و کسانی که ایمان آوردند و کارهای شایسته کردند آنان اهل بهشتند و پیوسته در بهشت جاوید متنعم خواهند بود.'),
(90, 2, 83, 'و (یاد آرید) هنگامی که از بنی اسرائیل عهد گرفتیم که جز خدای را نپرستید و نیکی کنید درباره پدر و مادر و خویشان و یتیمان و فقیران، و به زبان خوش با مردم تکلّم کنید و نماز به پای دارید و زکات (مال خود) بدهید، پس شما عهد شکسته و روی گردانیدید جز چند نفری، و شمایید که از حکم خدا برگشتید.'),
(91, 2, 84, 'و (به یاد آرید) هنگامی که از شما عهد گرفتیم که خون یکدیگر را نریزید و یکدیگر را از خانه و دیار خود نرانید، پس بر آن عهد اقرار کرده و خود شما بر آن گواه می‌باشید.'),
(92, 2, 85, '(با این عهد و اقرار) باز شما (به همان خوی زشت) خون یکدیگر می‌ریزید و گروهی از خودتان را از دیارشان می‌رانید و در بدکرداری و ستم بر ضعیفان همدست و پشتیبان یکدیگرید، و چون اسیر شوند برای آزادی آنها فدیه می‌دهید، در صورتی که به حکم تورات، اخراج کردن آنها بر شما حرام شده است! چرا به برخی از احکام ایمان آورده و به بعضی کافر می‌شوید؟ پس جزای چنین مردم بدکردار چیست به جز ذلّت و خواری در زندگانی این جهان و بازگشتن به سخت‌ترین عذاب در روز قیامت؟ و خدا غافل از کردار شما نیست.'),
(93, 2, 86, 'اینان همان کسانند که (متاع دو روزه) دنیا را خریده و ملک ابدی آخرت را فروختند، پس (در آخرت) عذاب آنها تخفیف نیابد و هیچ یاوری نخواهند داشت.'),
(94, 2, 87, 'و ما به موسی کتاب تورات عطا کردیم و از پی او پیغمبران فرستادیم و عیسی پسر مریم را به معجزات و ادلّه روشن، حجتها دادیم و او را به واسطه روح القدس توانایی بخشیدیم. آیا هر پیغمبری که از جانب خدا اوامری بر خلاف هوای نفس شما آرد گردنفرازی و سرپیچی نموده و (از راه حسد) گروهی را تکذیب کرده و جمعی را به قتل می‌رسانید؟!'),
(95, 2, 88, 'و (آن گروه به پیغمبران) گفتند: دلهای ما در حجاب (غفلت) است (چیزی از سخنان شما در نمی‌یابد). (چنین نیست که گفتند) بلکه خدا بر آنها لعن فرمود، زیرا کافر شدند و میانشان اهل ایمان کم بود.'),
(96, 2, 89, 'و چون کتاب آسمانی قرآن از نزد خدا برای هدایت آنها آمد با وجودی که کتاب (تورات) آنان را تصدیق می‌کرد و با آنکه خود آنها پیش از بعثت (پیامبر اسلام) انتظار غلبه بر کافران داشتند، آن گاه که آمد و شناختند (که همان پیغمبر موعود است) باز به او کافر شدند (و از نعمت وجود او ناسپاسی کردند)، پس خشم خدا بر کافران باد.'),
(97, 2, 90, 'بد معامله‌ای با خود کردند که به نعمت قرآن که خداوند بر آنها نازل کرد کافر شدند از روی حسد و ستمگری که چرا خدا فضل خود را مخصوص بعضی از بندگان گرداند (چرا پیامبری از غیر بنی اسرائیل برانگیخته)؟! و به واسطه این حسد باز خشم دیگری از خدای برای خود گرفتند، و برای کافران عذاب خواری مهیّاست.'),
(98, 2, 91, 'و چون به یهود گویند: به قرآن که خدا فرستاده ایمان آرید، پاسخ دهند که تنها به تورات که بر ما نازل شده ایمان آوریم، و به غیر تورات کافر می‌شوند، در صورتی که قرآن کتاب حق است و کتاب آنها را تصدیق می‌کند. بگو: اگر شما در دعوی ایمان به تورات راستگویید پس چرا پیغمبران پیشین را می‌کشتید؟'),
(99, 2, 92, 'و با آن همه آیات و معجزات روشن که موسی برای شما آشکار نمود باز گوساله پرستی اختیار کردید، و شما مردمی سخت ظالم و ستمکارید.'),
(100, 2, 93, 'و (به یاد آرید) وقتی که از شما پیمان گرفتیم و کوه طور را بر فراز شما بداشتیم که باید آنچه را به شما فرستادیم با ایمان محکم بپذیرید و (سخن حق) بشنوید، شما گفتید: خواهیم شنید و (در نیّت گرفتید که در عمل) عصیان خواهیم کرد. و دلهای آنان از عشق به گوساله پر شد از آن رو که به خدا کافر بودند. بگو: ایمان شما سخت شما را به کار بد و کردار زشت می‌گمارد، اگر ایمان داشته باشید.'),
(101, 2, 94, 'بگو: اگر سرای آخرت را (با آن همه نعمت) خدا به شما اختصاص داده دون سایر مردم، بایستی همیشه به مرگ آرزومند باشید اگر راست می‌گویید.'),
(102, 2, 95, 'و هرگز آرزوی مرگ نخواهند کرد، (زیرا عذاب سخت) به واسطه کردار بد خود (برای خود در آخرت مهیا کرده‌اند)، و خدا آگاه به ستمکاران است.'),
(103, 2, 96, 'و بر تو (و بر همه) پیداست که یهود به حیات مادی حریص‌تر از همه خلق‌اند، حتی از مشرکان، و از این رو هر یهودی آرزوی هزار سال عمر می‌کند، ولی عمر هزار سال هم او را از عذاب خدا نرهاند، و خدا به کردار ناپسند آنان بیناست.'),
(104, 2, 97, 'بگو: هر کس با جبرئیل دشمن است، او به فرمان خدا قرآن را به قلب تو رسانید در حالی که تصدیق سایر کتب آسمانی می‌کند و برای اهل ایمان هدایت و بشارت است.'),
(105, 2, 98, 'هر کس با خدا و فرشتگان و رسولان او و جبرئیل و میکائیل دشمن است (محققا کافر است و) خدا هم دشمن کافران است.'),
(106, 2, 99, 'و ما (برای اثبات پیغمبری تو) آیات و دلایل روشن به تو فرستادیم، و بجز فاسقان (و اهل عناد) کسی انکار آن نخواهد کرد.'),
(107, 2, 100, 'برای چه هر عهد و پیمان که بستند گروهی آن را می‌شکنند؟ (نه اینان) بلکه اکثر آنها اصلا ایمان نخواهند آورد.'),
(108, 2, 101, 'و چون پیغمبری از جانب خدا بر آنان آمد که به راستی کتب آسمانی آنها گواهی می‌داد، گروهی از اهل کتاب، کتاب خدا را پشت سر انداختند (و به حکم آن کتاب که امر به ایمان آوردن به قرآن می‌کرد رفتار نکردند) گویی (از آن کتاب) هیچ نمی‌دانند.'),
(109, 2, 102, 'و پیروی کردند سخنانی را که (دیوان و) شیاطین در ملک سلیمان (به افسون و جادوگری) می‌خواندند، و هرگز سلیمان (با به کار بردن سحر به خدا) کافر نگشت و لکن شیاطین کافر شدند که سحر به مردم می‌آموختند. و آنچه را که بر دو فرشته هاروت و ماروت در بابل نازل شده بود پیروی کردند، در صورتی که آن دو ملک به هیچ کس چیزی نمی‌آموختند مگر آنکه بدو می‌گفتند که کار ما فتنه و امتحان است، مبادا کافر شوی! اما مردم از آن دو ملک چیزی را که ما بین زن و شوهر جدایی افکند می‌آموختند، البته به کسی زیان نمی‌رسانیدند مگر آنکه خدا بخواهد، و چیزی که می‌آموختند به خلق زیان می‌رسانید و سود نمی‌بخشید، و محققا می‌دانستند که هر که چنین کند در عالم آخرت هرگز بهره‌ای نخواهد یافت، و آنان به بهای بدی خود را فروختند، اگر می‌دانستند.'),
(110, 2, 103, 'و محققا اگر ایمان آورده و پرهیزکار می‌شدند بهره‌ای که از خدا نصیب آنها می‌شد بهتر از هر چیز بود، چنانچه می‌دانستند.'),
(111, 2, 104, 'ای اهل ایمان (هنگام تکلم با رسول) به کلمه راعنا (ما را رعایت کن) تعبیر مکنید، بلکه بگویید: انظرنا (ناظر احوال ما باش)؛ و (سخن خدا را) بشنوید و (بدانید که) برای کافران عذاب دردناک مهیا است.'),
(112, 2, 105, 'هرگز کافران اهل کتاب و مشرکان مایل نیستند که بر شما (مسلمین) خیری از جانب خدایتان نازل شود، لیکن خدا هر که را بخواهد به فضل و رحمت خویش مخصوص گرداند، و خدا صاحب فضل عظیم است.'),
(113, 2, 106, 'هر چه از آیات قرآن را نسخ کنیم یا حکم آن را از خاطرها بریم (و متروک سازیم) بهتر از آن یا مانند آن بیاوریم. آیا نمی‌دانی که خدا بر هر چیز قادر است؟'),
(114, 2, 107, 'آیا نمی‌دانی که پادشاهی آسمانها و زمین مختصّ خداوند است و شما را به جز خدا یار و یاوری نخواهد بود؟'),
(115, 2, 108, 'آیا اراده آن دارید که شما نیز از پیغمبر خود در خواست کنید آنچه را که یهود در زمان گذشته از موسی خواستند؟ و هر که ایمان را مبدّل به کفر گرداند، بی‌شک راه راست را گم کرده است.'),
(116, 2, 109, 'بسیاری از اهل کتاب آرزو دارند که شما را از ایمان به کفر برگردانند به سبب رشک و حسدی که در نفس خود بر ایمان شما برند بعد از آنکه حق بر آنها آشکار گردید، پس (اگر ستمی از آنها به شما رسید) در گذرید (و مدارا کنید) تا هنگامی که فرمان خدا برسد، که البته خدا بر هر چیز قادر و تواناست.'),
(117, 2, 110, 'و نماز به پا دارید و زکات بدهید و بدانید که هر چه از اطاعت حق و کار نیکو برای خود پیش می‌فرستید آن را در نزد خدا خواهید یافت، که محققا خداوند به هر کار شما بیناست.'),
(118, 2, 111, 'و یهود گفتند: هرگز به بهشت نرود جز طایفه یهود، و نصاری گفتند: جز طایفه نصاری. این تنها آرزوی باطل آنهاست، بگو: بر این دعوی برهان آورید اگر راست می‌گویید.'),
(119, 2, 112, 'آری، هر که تسلیم حکم خدا گردید و نیکوکار گشت مسلّم اجرش نزد خدا خواهد بود و آنان را هیچ خوف و اندیشه و هیچ اندوهی نخواهد بود.'),
(120, 2, 113, 'یهود بر این دعویند که نصاری را از حق چیزی در دست نیست، و نصاری بر این دعوی که یهود را از حق چیزی در دست نیست، در صورتی که هر دو گروه در خواندن کتاب آسمانی یکسانند. این گونه دعویها نظیر گفتار و مجادلات مردمی است که از کتاب آسمانی بی‌بهره‌اند. و خداوند در این اختلافات روز قیامت میان آنها حکم خواهد فرمود.'),
(121, 2, 114, 'و کیست ستمکارتر از آن که مردم را از ذکر نام خدا در مساجد منع کند و در خرابی آن اهتمام و کوشش نماید؟ چنین گروه را نشاید که در مساجد مسلمین در آیند جز آنکه (بر خود) ترسان باشند. این گروه را در دنیا ذلت و خواری نصیب است و در آخرت عذابی بزرگ.'),
(122, 2, 115, 'مشرق و مغرب هر دو ملک خداست، پس به هر طرف روی کنید به سوی خدا روی آورده‌اید، که خدا (به همه جا) محیط و (به هر چیز) داناست.'),
(123, 2, 116, 'گروهی گفتند: خدا دارای فرزند است. او پاک و منزه از آن است، بلکه هر چه در آسمانها و زمین است ملک اوست و همه فرمانبردار اویند.'),
(124, 2, 117, 'او آفریننده آسمانها و زمین است و چون اراده آفریدن چیزی کند، به محض آنکه گوید: موجود باش، موجود خواهد شد.'),
(125, 2, 118, 'مردم نادان اعتراض کردند که چرا خداوند با ما سخن نگوید، یا معجزه‌ای (و کتابی مطابق میل ما) بر ما نمی‌آید؟ پیشینیان هم به مانند اینان (به نادانی) چنین سخنان می‌گفتند؛ دلهاشان (در نافهمی) به هم شبیه است. ما ادلّه رسالت را برای اهل یقین روشن گردانیدیم.'),
(126, 2, 119, '(ای پیغمبر) ما تو را به حق فرستادیم که مردم را (به نعمت بهشت) مژده دهی و (از عذاب جهنم) بترسانی، و تو مسئول کافران که به راه جهنم رفتند نیستی.'),
(127, 2, 120, 'هرگز یهود و نصاری از تو راضی نخواهند شد مگر آنکه پیروی از آیین آنها کنی. بگو: راهی که خدا بنماید به یقین راه حق تنها همان است؛ و البته اگر از میل و خواهش آنها پیروی کنی بعد از آنکه طریق حق را دریافتی، دیگر از سوی خدا یار و یاوری نخواهی داشت.'),
(128, 2, 121, 'کسانی که کتاب بر آنها فرستادیم آن را خوانده و حق خواندن آن را (در مقام عمل نیز) بجا آورند، آنان اهل ایمانند و آنها که به کتاب خدا کافر شوند (و حق شناسی نکنند) آن گروه زیانکاران عالمند.'),
(129, 2, 122, 'ای بنی اسرائیل، به یاد آرید نعمتی را که به شما عطا کردم و اینکه شما را بر همه مردم فضیلت و برتری دادم.'),
(130, 2, 123, 'و بترسید از روزی که هیچ کس اندکی به کار دیگری نیاید و به حال او سودی نبخشد و از هیچ کس فدایی پذیرفته نشود و شفاعت کسی او را سودمند نبود و آنان را یاوری نباشد.'),
(131, 2, 124, 'و (به یاد آر) هنگامی که خدا ابراهیم را به اموری امتحان فرمود و او همه را به جای آورد، خدا به او گفت: من تو را به پیشوایی خلق برگزینم، ابراهیم عرض کرد: به فرزندان من چه؟ فرمود: (اگر شایسته باشند می‌دهم، زیرا) عهد من به مردم ستمکار نخواهد رسید.'),
(132, 2, 125, 'و (به یاد آر) هنگامی که ما خانه کعبه را مرجع امر دین خلق و مقام امن مقرّر داشتیم و (دستور داده شد که) مقام ابراهیم را جایگاه پرستش خدا قرار دهید و به ابراهیم و اسماعیل سفارش کردیم که حرم مرا (از بت) بپردازید و پاکیزه دارید برای اهل ایمان که به طواف و اعتکاف حرم آیند و در آن نماز و طاعت خدا به جای آرند.'),
(133, 2, 126, 'و (به یاد آر) هنگامی که ابراهیم عرض کرد: پروردگارا، این شهر را محل امن و آسایش قرار ده و اهلش را که ایمان به خدا و روز قیامت آورده‌اند از انواع روزیها بهره‌مند ساز، خدا فرمود: و هر که (با وجود این نعمت) سپاس نگزارد و راه کفر پیماید گر چه در دنیا او را اندکی بهره‌مند کنم، لیکن در آخرت ناچار معذب به آتش دوزخ گردانم که بد منزلگاهی است.'),
(134, 2, 127, 'و (یاد آر) وقتی که ابراهیم و اسماعیل دیوارهای خانه کعبه را بر می‌افراشتند عرض کردند: پروردگارا (این خدمت) از ما قبول فرما، که تویی که (دعای خلق را) شنوا و (به اسرار همه) دانایی.'),
(135, 2, 128, 'پروردگارا، دل ما را تسلیم فرمان خود گردان و فرزندان ما را نیز امتی تسلیم و رضای خود بدار، و راه پرستش و طاعت را به ما بنما و بر ما (وظیفه بندگی را) سهل و آسان‌گیر، که تویی توبه‌پذیر و مهربان.'),
(136, 2, 129, 'پروردگارا، در میان آنان رسولی از خودشان برانگیز که بر آنان تلاوت آیات تو کند و آنان را علم کتاب و حکمت بیاموزد و روانشان را (از هر زشتی) پاک و منزه سازد، که تو بر هر کار که خواهی قدرت و علم کامل داری.'),
(137, 2, 130, 'و چه کس از آیین پاک ابراهیم روی گرداند به جز مردم بیخرد؟! زیرا ما ابراهیم را در دنیا (به شرف رسالت) برگزیدیم و البته در آخرت هم از شایستگان است.'),
(138, 2, 131, 'آن گاه (برگزیده شد) که خدا به او فرمود: ای ابراهیم سر به فرمان خدا فرود آر، عرض کرد: پروردگار عالمیان را مطیع فرمانم.'),
(139, 2, 132, 'و ابراهیم و یعقوب بدین گونه به فرزندان خود سفارش و توصیه نمودند که ای فرزندان من، خدا برای شما آیین پاک برگزید، پس (پیروی کنید و) تا گاه جان سپردن الاّ تسلیم رضای خدا نباشید.'),
(140, 2, 133, 'شما کی و کجا حاضر بودید هنگامی که مرگ یعقوب در رسید و به فرزندان خود گفت که شما پس از مرگ من که را می‌پرستید؟ گفتند: خدای تو را و خدای پدران تو ابراهیم و اسماعیل و اسحاق را که معبود یگانه است و ما مطیع فرمان اوییم.'),
(141, 2, 134, 'آنها گروهی بودند که درگذشتند، هر کار نیک و بد کردند برای خود کردند و شما هم هر چه کنید برای خویش خواهید کرد و شما مسئول کار آنها نخواهید بود.'),
(142, 2, 135, 'یهود و نصاری گفتند که به آیین ما درآیید تا راه راست یافته و طریق حق پویید. (ای پیغمبر در جواب آنها) بگو: بلکه ما آیین ابراهیم را پیروی می‌کنیم که به راه راست توحید بود و از مشرکان نبود.'),
(143, 2, 136, 'بگویید که ما (مسلمین) ایمان به خدا آورده‌ایم و به آن کتابی که بر پیغمبر ما فرستادند و به آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و فرزندان او نازل شده و به آنچه به موسی و عیسی داده شده و هم به آنچه به پیغمبران از جانب خدا داده شده، به همه عقیده‌مندیم و میان هیچ یک از آنان فرق نگذاریم و تسلیم فرمان او هستیم.'),
(144, 2, 137, 'پس اگر به آنچه شما ایمان آوردید یهود و نصاری نیز ایمان آوردند، راه حق یافته‌اند و اگر (از حق) روی بگردانند شک نیست که آنها در ستیز با حق خواهند بود و خداوند تو را از شر و آسیب آنها نگه می‌دارد و خدا شنوا و داناست.'),
(145, 2, 138, 'رنگ خداست (که به ما رنگ ایمان و سیرت توحید بخشیده) و چه رنگی بهتر از رنگ (ایمان به) خدا؟ و ما او را پرستش می‌کنیم.'),
(146, 2, 139, '(ای پیغمبر به اهل کتاب) بگو: در مورد خدا شما را با ما چه جای بحث و جدال است؟ در صورتی که او پروردگار ما و شماست و ما مسئول کار خود و شما مسئول کردار خویش هستید، و ماییم که او را از روی خلوص پرستش می‌کنیم.'),
(147, 2, 140, 'یا آنکه گویید که ابراهیم و اسماعیل و اسحاق و یعقوب و فرزندان او بر آیین یهودیت یا نصرانیّت بودند؟ (خدا مرا فرماید که در پاسخ) بگو: شما بهتر می‌دانید یا خدا؟ و کیست ستمکارتر از آن که شهادت خدا را (درباره این انبیا) کتمان کند؟ و خدا از آنچه می‌کنید غافل نیست.'),
(148, 2, 141, 'آن گروه پیشین همه درگذشتند، هر چه کردند برای خود کردند و شما نیز هر چه کنید به سود خویش کنید و شما مسئول کار آنان نخواهید بود.'),
(149, 2, 142, 'مردم بی‌خرد خواهند گفت: چه موجب شد که مسلمین از قبله‌ای که بر آن بودند (از بیت المقدس) روی به کعبه آوردند؟ بگو: مشرق و مغرب برای خداست و هر که را خواهد به راه راست هدایت می‌کند.'),
(150, 2, 143, 'و ما همچنان شما (مسلمین) را به آیین اسلام هدایت کردیم و به سیرت نیکو بیاراستیم تا گواه مردم باشید و پیغمبر نیز گواه شما باشد (تا شما از وی بیاموزید). و (ای پیغمبر) ما قبله‌ای را که بر آن بودی تغییر ندادیم مگر برای اینکه بیازماییم و جدا سازیم گروهی را که از پیغمبر خدا پیروی می‌کنند از آنان که عقبگرد کنند و (به مخالفت او برخیزند)، و این تغییر قبله بسی بزرگ نمود جز در نظر هدایت یافتگان خدا. و خداوند اجر پایداری شما را در راه ایمان تباه نگرداند که خدا به خلق مشفق و مهربان است.'),
(151, 2, 144, 'ما توجه تو را بر آسمان (به انتظار وحی و تغییر قبله) بنگریم و البته روی تو را به قبله‌ای که به آن خشنود شوی گردانیم، پس روی کن به طرف مسجد الحرام و شما (مسلمین) نیز هر کجا باشید (در نماز) روی بدان جانب کنید. و گروه اهل کتاب به خوبی می‌دانند که این تغییر قبله به حق و راستی از جانب خداست (نه به دلخواه کس) و خدا از کردار آنها غافل نیست.'),
(152, 2, 145, 'و تو (ای پیغمبر) اگر هر قسم معجزه برای اهل کتاب بیاوری پیرو قبله تو نشوند و تو نیز تبعیت از قبله آنان نخواهی کرد و بعضی ملل تابع قبله برخی دیگر نشوند، و اگر تو تابع دلخواه و هوسهای جاهلانه آنها شوی بعد از آنکه از جانب خدا علم یافتی، در این صورت البته از گروه ستمکاران خواهی بود.'),
(153, 2, 146, 'گروهی که ما بر آنها کتاب فرستادیم، (محمد صلّی اللّه علیه و آله و سلّم و حقّانیّت) او را به خوبی می‌شناسند همان گونه که فرزندان خود را، و لکن گروهی از آنان (از راه عناد) حق را کتمان می‌کنند در صورتی که علم به آن دارند.'),
(154, 2, 147, 'حق همان است که از طرف خدا به سوی تو آمد، پس هیچ شبهه به دل راه مده.'),
(155, 2, 148, 'هر کسی را راهی است به سوی حق که بدان راه یابد (یا قبله‌ای است که به آن روی آورد) پس بشتابید به خیرات که هر کجا باشید همه شما را خداوند (به عرصه محشر) خواهد آورد و محققا خدا بر همه چیز تواناست.'),
(156, 2, 149, 'و از هر کجا و به سوی هر دیار بیرون شدی (در نماز) روی به طرف کعبه آور، و این دستور قبله بر وجه صواب و به امر خداست و خدا از کار شما غافل نیست.'),
(157, 2, 150, 'و از هر جا و به هر دیار بیرون شدی روی به جانب کعبه کن و شما مسلمین هم به هر کجا بودید روی بدان جانب کنید تا مردم به حجّت و مجادله بر شما زبان نگشایند-جز گروه ستمکار (معاند) که از (جدل و گفتگوی) آنان هم نیندیشید و از (نافرمانی) من بترسید-و تا نعمت و رحمتم را برای شما به حد کمال رسانم، و باشد که (به طریق صواب) راه یابید.'),
(158, 2, 151, 'چنانکه در میان شما رسولی از خودتان فرستادیم که آیات ما را برای شما تلاوت می‌کند و نفوس شما را (از پلیدی جهل) پاک و منزّه می‌گرداند و به شما تعلیم کتاب و حکمت می‌دهد و آنچه را نمی‌دانید به شما می‌آموزد.'),
(159, 2, 152, 'پس مرا یاد کنید تا شما را یاد کنم و شکر نعمت من به جای آرید و کفران نعمت من نکنید.'),
(160, 2, 153, 'ای اهل ایمان، (در پیشرفت کار خود) از صبر و مقاومت کمک گیرید و به ذکر خدا و نماز توسّل جویید که خدا با صابران است.'),
(161, 2, 154, 'و به آنان که در راه خدا کشته شوند مرده نگویید، بلکه زنده ابدی هستند و لیکن همه شما این حقیقت را درنخواهید یافت.'),
(162, 2, 155, 'و البته شما را به پاره‌ای از سختی‌ها چون ترس و گرسنگی و نقصان اموال و نفوس و آفات زراعت بیازماییم، و صابران را بشارت و مژده بده.'),
(163, 2, 156, 'آنان که چون به حادثه سخت و ناگواری دچار شوند (صبوری پیش گرفته و) گویند: ما به فرمان خدا آمده‌ایم و به سوی او رجوع خواهیم کرد.'),
(164, 2, 157, 'آن گروهند که مخصوص به درود و الطاف الهی و رحمت خاص خدایند و آنها خود هدایت یافتگانند.'),
(165, 2, 158, 'سعی صفا و مروه از شعائر دین خداست، پس هر کس حجّ خانه کعبه یا اعمال مخصوص عمره به جای آورد باکی بر او نیست که سعی صفا و مروه نیز به جای آرد، و هر کس به راه خیر و نیکی شتابد (خدا پاداش وی خواهد داد که) خدا قدردان و (به همه امور خلق) عالم است.'),
(166, 2, 159, 'آن گروه (از علمای اهل کتاب) که آیات واضحه و هدایتی را که فرستادیم کتمان نمایند بعد از آنکه آن را برای (هدایت) مردم در کتاب آسمانی بیان کردیم، آنها را خدا و تمام لعن کنندگان (از جن و انس و ملک) نیز لعن می‌کنند.'),
(167, 2, 160, 'مگر آنهایی که توبه کردند و به اصلاح مفاسد اعمال خود پرداختند و بیان کردند (آنچه را که کتمان کردند)، پس توبه اینان را می‌پذیرم که منم توبه پذیر و مهربان.'),
(168, 2, 161, 'آنان که کافر شده و به عقیده کفر مردند البته بر آن گروه، خدا و فرشتگان و مردمان همه لعن می‌فرستند.'),
(169, 2, 162, 'همیشه در جهنم (به عذاب و شکنجه) باشند، نه بر آنان تخفیف عذاب دهند و نه به نظر رحمت بنگرند.'),
(170, 2, 163, 'و خدای شما، خدای یکتاست، نیست خدایی مگر او که بخشاینده و مهربان است.'),
(171, 2, 164, 'محققا در خلقت آسمانها و زمین و رفت و آمد شب و روز، و کشتی‌ها که بر روی آب برای انتفاع خلق در حرکت است، و بارانی که خدا از بالا فرو فرستاد تا به آن آب، زمین را بعد از مردن زنده کرد و سبز و خرّم گردانید، و در پراکندن انواع حیوانات در زمین، و گردانیدن بادها (به هر طرف) و در خلقت ابر که میان زمین و آسمان مسخّر است، در همه این امور ادلّه‌ای واضح برای عاقلان است.'),
(172, 2, 165, 'برخی از مردم (نادان) غیر خدا را همانند خدا گیرند و چنانکه خدا را بایست دوست داشت با آنها دوستی ورزند، لیکن آنها که اهل ایمانند کمال محبّت و دوستی را فقط به خدا مخصوص دارند. و اگر فرقه مشرکین ستمکار وقتی که عذاب خدا را مشاهده کنند ببینند که قدرت خاص خدا است و عذاب خدا بسیار سخت است (از شرک خود سخت پشیمان شوند).'),
(173, 2, 166, 'هنگامی که بیزاری جویند رؤسا و پیشوایان (باطل) از پیروان خود و عذاب خدا را مشاهده کنند و هر گونه وسیله و اسباب از آنها قطع شود (و روابط باطل به جا نماند).'),
(174, 2, 167, 'و آن پیروان گویند: کاش دیگر بار به دنیا باز می‌گشتیم و از (اطاعت) اینان بیزاری می‌جستیم چنان که اینها (گرهی از کار ما نگشودند و) از ما بیزاری جستند! این گونه خدا، کردار (زشت جاهلانه) آنها را مایه حسرت و پشیمانی آنان کند و آنها از عذاب آتش جهنّم بیرون شدنی نباشند.'),
(175, 2, 168, 'ای مردم، از آنچه در زمین است حلال و پاکیزه را تناول کنید و پیروی نکنید و ساوس شیطان را، محققا شیطان از برای شما دشمنی آشکار است.'),
(176, 2, 169, 'این دشمن است که به شما دستور زشتی و بدکاری می‌دهد و بر آن می‌گمارد که سخنانی از روی جهل و نادانی به خدا نسبت دهید.'),
(177, 2, 170, 'و چون کفار را گویند: پیروی از شریعت و کتابی که خدا فرستاده کنید، پاسخ دهند که ما پیرو کیش پدران خود خواهیم بود. آیا بایست آنها تابع پدران باشند گر چه آن پدران بی‌عقل و نادان بوده و هرگز به حق و راستی راه نیافته باشند؟'),
(178, 2, 171, 'و مثل کافران (در شنیدن سخن انبیاء و درک نکردن معنای آن) چون حیوانی است که آوازش کنند و او از آن آواز (معنایی درک نکرده و) جز صدایی نشنود، کفّار هم (از شنیدن و دیدن حق) کر و گنگ و کورند، زیرا تعقل نمی‌کنند.'),
(179, 2, 172, 'ای اهل ایمان، روزی حلال و پاکیزه‌ای که ما نصیب شما کرده‌ایم بخورید و شکر خدا به جای آرید اگر شما خالص خدا را می‌پرستید.'),
(180, 2, 173, 'به تحقیق، خدا حرام گردانید بر شما مردار و خون و گوشت خوک را و آنچه را که به اسم غیر خدا کشته باشند، پس هر کس که به خوردن آنها محتاج و مضطر شود در صورتی که به آن تمایل نداشته و (از اندازه رمق) تجاوز نکند گناهی بر او نخواهد بود (که به قدر احتیاج صرف کند که) محققا خدا آمرزنده و مهربان است.'),
(181, 2, 174, 'آنان که پنهان دارند آیاتی از کتاب آسمانی را که خدا فرستاده بود و آن را به بهای اندک فروشند، جز آتش جهنّم در شکم نمی‌برند و در قیامت خدا با آنها سخن نگوید و (از پلیدی عصیان) پاکشان نگرداند، و هم آنان را در قیامت عذابی دردناک خواهد بود.'),
(182, 2, 175, 'آنها همان گروهند که اختیار کردند گمراهی را به جای هدایت، و عذاب را به جای آمرزش، پس چقدر بر آتش جهنم سخت جان و پرطاقتند!'),
(183, 2, 176, 'این عذاب از آن روست که خدا کتاب آسمانی را به راستی فرستاد و گروهی که در آن اختلاف و مکابره کردند در اختلافی دور (از حق) خواهند بود.'),
(184, 2, 177, 'نیکوکاری آن نیست که روی به جانب مشرق یا مغرب کنید (چه این چیز بی‌اثری است) لیکن نیکوکاری آن است که کسی به خدای عالم و روز قیامت و فرشتگان و کتاب آسمانی و پیغمبران ایمان آرد و دارایی خود را در راه دوستی خدا به خویشان و یتیمان و فقیران و در راه ماندگان و گدایان بدهد و در آزاد کردن بندگان صرف کند، و نماز به پا دارد و زکات مال (به مستحق) بدهد، و نیز نیکوکار آنانند که با هر که عهد بسته‌اند به موقع خود وفا کنند و در حال تنگدستی و سختی و هنگام کارزار صبور و شکیبا باشند. (کسانی که بدین اوصاف آراسته‌اند) آنها به حقیقت راستگویان و آنها به حقیقت پرهیزکارانند.'),
(185, 2, 178, 'ای اهل ایمان بر شما حکم قصاص کشتگان چنین معین گشت که مرد آزاد در مقابل مرد آزاد، و بنده را به جای بنده، و زن را به جای زن قصاص توانید کرد. و چون صاحب خون از قاتل که برادر دینی اوست بخواهد درگذرد کاری است نیکو، پس قاتل دیه را در کمال خشنودی ادا کند. در این حکم، تخفیف (امر قصاص) و رحمت خداوندی است، و پس از این دستور، هر که تجاوز کند او را عذابی سخت خواهد بود.'),
(186, 2, 179, 'ای عاقلان، حکم قصاص برای حفظ حیات شماست تا مگر (از قتل یکدیگر) بپرهیزید.'),
(187, 2, 180, 'دستور داده شد که چون مرگ یکی از شما فرا رسد اگر دارای متاع دنیاست وصیت کند برای پدر و مادر و خویشان به چیزی شایسته عدل و به قدر متعارف. این کار سزاوار مقام پرهیزکاران است.'),
(188, 2, 181, 'پس هرگاه کسی بعد از شنیدن وصیت، آن را تغییر دهد و (بر خلاف وصیت رفتار کند) گناه این کار بر آنهاست که عمل به خلاف وصیت کنند، و خدا (به گفتار و کردار خلق) شنوا و داناست.'),
(189, 2, 182, 'و هر کس بیم دارد که از وصیت موصی (به وارث او) جفا و ستمی رفته باشد و به اصلاح میان آنان پردازد بر او گناهی نیست و محققا خدا آمرزنده و مهربان است.'),
(190, 2, 183, 'ای اهل ایمان، بر شما هم روزه واجب گردید چنانکه امم گذشته را فرض شده بود، و این دستور برای آن است که پاک و پرهیزکار شوید.'),
(191, 2, 184, 'روزهایی به شماره معیّن روزه دارید (تمام ماه رمضان)، و هر کسی از شما مریض باشد یا مسافر، به شماره آن از روزهای غیر ماه رمضان روزه دارد، و کسانی که روزه را به زحمت توانند داشت عوض هر روز فدا دهند آن قدر که فقیر گرسنه‌ای سیر شود، و هر کس بر نیکی بیفزاید آن برای او بهتر است. و (بی‌تعلّل) روزه داشتن برای شما بهتر خواهد بود اگر (فواید آن را) بدانید.'),
(192, 2, 185, 'ماه رمضان ماهی است که قرآن در آن نازل شده برای هدایت بشر و برای راهنمایی و امتیاز حق از باطل، پس هر که دریابد ماه رمضان را باید آن را روزه بدارد، و هر که ناخوش یا در سفر باشد (به شماره آنچه روزه خورده است) از ماهای دیگر روزه دارد، که خداوند برای شما حکم را آسان خواسته و تکلیف را مشکل نگرفته، و خواسته تا اینکه عدد روزه را تکمیل کرده و خدا را به عظمت یاد کنید که شما را هدایت فرمود، باشد که (از این نعمت بزرگ) سپاسگزار شوید.'),
(193, 2, 186, 'و چون بندگان من (از دوری و نزدیکی) من از تو پرسند، (بدانند که) من به آنها نزدیکم، هرگاه کسی مرا خواند دعای او را اجابت کنم. پس باید دعوت مرا (و پیغمبران مرا) بپذیرند و به من بگروند، باشد که (به سعادت) راه یابند.'),
(194, 2, 187, 'برای شما در شبهای ماه رمضان مباشرت با زنان خود حلال شد، که آنها جامه عفاف شما و شما نیز لباس عفّت آنها هستید. و خدا دانست که شما (در کار مباشرت زنان به نافرمانی و اطاعت شهوت نفس) خود را به ورطه گناه می‌افکنید لذا از حکم حرمت درگذشت و گناه شما را بخشید، از اکنون (در شب رمضان) رواست که با زنهای خود (به حلال) مباشرت کنید و از خدا آنچه مقدّر فرموده بخواهید. و بخورید و بیاشامید تا خط سفیدی روز از سیاهی شب در سپیده دم پدیدار گردد، پس از آن روزه را به پایان برسانید تا اول شب. و با زنان هنگام اعتکاف در مساجد مباشرت نکنید. این احکام، حدود دین خداست، زنهار در آن راه مخالفت مپویید! خداوند این گونه آیات خود را برای مردم بیان فرماید، باشد که پرهیزکار شوند.'),
(195, 2, 188, 'و مال یکدیگر را به ناحق مخورید و آن را به نزد قاضیان نیفکنید که (به وسیله رشوه و زور) پاره‌ای از اموال مردم را بخورید با آنکه (شما بطلان دعوی خود را) می‌دانید.'),
(196, 2, 189, '(ای پیغمبر) از تو سؤال کنند که سبب بدر و هلال ماه چیست؟ جواب ده که در آن تعیین اوقات عبادات حج و معاملات مردم است. و نیکوکاری بدان نیست که از پشت دیوار به خانه درآیید (چه این کاری ناشایسته است) ولی نیکویی آن است که پارسا باشید و به خانه‌ها (و به هر کار) از راه آن داخل شوید، و تقوا پیشه کنید، باشد که رستگار شوید.'),
(197, 2, 190, 'و در راه خدا با آنان که به جنگ و دشمنی شما برخیزند جهاد کنید، لکن از حد تجاوز نکنید که خدا متجاوزان را دوست ندارد.'),
(198, 2, 191, 'و هر کجا مشرکان را یافتید به قتل رسانید و آنان را از همان وطن که شما را آواره کردند برانید، و فتنه‌گری (آنان) سخت‌تر و فسادش بیشتر از جنگ و کشتار است، و در مسجد الحرام با آنها جنگ مکنید مگر آنکه پیشدستی کنند، در این صورت رواست که در حرم آنها را به قتل برسانید، چنین است کیفر کافران.'),
(199, 2, 192, 'پس اگر دست (از شرک و ستم) بدارند (از آنها درگذرید که) خدا آمرزنده و مهربان است.'),
(200, 2, 193, 'و با کفار جهاد کنید تا فتنه و فساد از روی زمین برطرف شود و دین خدا حاکم باشد و بس، و اگر (از فتنه) دست کشیدند (با آنها عدالت کنید که) تجاوز جز بر ستمکاران روا نیست.'),
(201, 2, 194, 'ماه‌های حرام را در مقابل ماه‌های حرام قرار دهید، که اگر حرمت آن را نگاه نداشته و با شما قتال کنند شما نیز قصاص کنید. هر که به ستم بر شما دست دراز کند او را از پای در آورید به قدر ستمی که به شما رسانده است، و از خدا پروا کنید و بدانید که خدا با پرهیزکاران است.'),
(202, 2, 195, 'و (از مال خود) در راه خدا انفاق کنید (لیکن نه به حد اسراف)، و خود را به مهلکه و خطر در نیفکنید، و نیکویی کنید که خدا نیکوکاران را دوست می‌دارد.'),
(203, 2, 196, 'و همه اعمال حج و عمره را برای خدا به پایان رسانید، و اگر ترس و منعی پیش آید قربانی کنید به آنچه مقدور و میسر است، و سر متراشید تا آن گاه که قربانی شما به محل ذبح برسد. و هر کس بیمار باشد یا سردرد بر او عارض شود (سر بتراشد و) از آن فدا دهد به روزه داشتن یا صدقه دادن یا کشتن گوسفند، و پس از آنکه ترس و منع بر طرف شود هر کس از عمره تمتّع به حج باز آید هر چه میسر و مقدور است قربانی کند و هر کس به قربانی تمکّن نیافت سه روز در ایام حجّ روزه بدارد و هفت روز هنگام مراجعت، که ده روز تمام شود. این عمل بر آن کس است که اهل شهر مکّه نباشد. (به این احکام عمل کنید) و از نافرمانی خدا پروا کنید و بدانید که عذاب خدا سخت است.'),
(204, 2, 197, 'حجّ (واجب) در ماههایی معیّن است، پس هر که در این ماهها حج می‌گزارد بایست آنچه میان زن و شوهر رواست ترک کند و کار ناروا (مانند دروغ و بدگویی) را ترک کند و مجادله نکند، و شما هر کار نیک کنید خدا بر آن آگاه است و توشه (تقوا برای راه آخرت) برگیرید که بهترین توشه این راه تقواست و از من پروا کنید ای صاحبان عقل.'),
(205, 2, 198, 'باکی نیست که در هنگام حجّ کسب معاش کرده و از فضل خدا روزی طلبید، پس آن گاه که از عرفات بازگشتید در مشعر ذکر خدا کنید و به یاد خدا باشید که شما را پس از آنکه به ضلالت (کفر) بودید به راه هدایت آورد.'),
(206, 2, 199, 'بعد از آن به طریقی که همه مسلمین باز می‌گردند رجوع کنید و از خدا طلب آمرزش کنید که خدا آمرزنده و مهربان است.'),
(207, 2, 200, 'پس آن گاه که اعمال حجّ را به جا آوردید، همان گونه که پدران خود را یاد می‌کنید بلکه بیش از آن خدا را یاد کنید (و هر حاجت دنیا و آخرت را از خدا بخواهید) بعضی مردم (کوتاه نظر از خدا تمنّای متاع دنیوی تنها کنند و) گویند: پروردگارا، ما را از نعمتهای دنیا بهره‌مند ساز؛ و آنان را از نعمت آخرت نصیبی نیست.'),
(208, 2, 201, 'و بعضی دیگر گویند: خدایا ما را از نعمتهای دنیا و آخرت هر دو بهره‌مند گردان و از عذاب آتش دوزخ نگاه دار.'),
(209, 2, 202, 'هر یک از این دو فرقه از نتیجه اعمال خود بهره‌مند خواهند گشت و خدا به حساب همه زود رسیدگی کند.'),
(210, 2, 203, 'و خدا را یاد کنید (به ذکر تکبیر) در چند روزی معیّن (ایّام تشریق) پس هر که شتاب کند و اعمال را در دو روز انجام دهد گناه نکرده، و هر که تأخیر کند نیز گناه نکرده. این حکم برای کسی است که (از هر گناه یا از محرمات احرام) پرهیزکار بوده است، و از خدا بترسید و بدانید که به سوی خدا باز خواهید گشت.'),
(211, 2, 204, 'و از مردم کس هست که تو را از گفتار دلفریب خود به شگفت آرد (تا از دروغ به متاع دنیا رسد) و خدا را به راستی نیّت خود گواه گیرد حال آنکه این کس بدترین دشمن (اسلام) است.'),
(212, 2, 205, 'و چون از حضور تو دور شود کارش فتنه و فساد در زمین است و بکوشد تا حاصل خلق به باد فنا دهد و نسل (بشر) را قطع کند؛ و خدا فساد را دوست ندارد.'),
(213, 2, 206, 'و چون او را (به نصیحت) گویند: از خدا بترس، خود پسندی او را به بدکاری برانگیزد (و پند نشنود). جهنّم او را کفایت کند که آرامگاه بدی است.'),
(214, 2, 207, 'و بعضی از مردم از جان خود در راه رضای خدا درگذرند؛ و خدا با چنین بندگان رئوف و مهربان است.'),
(215, 2, 208, 'ای اهل ایمان، همه متّفقا در مقام تسلیم خدا در آیید و از وساوس شیطان پیروی مکنید که او همانا شما را دشمنی آشکار است.'),
(216, 2, 209, 'پس اگر باز به راه خطا رفتید با وجود آنکه ادلّه روشن از جانب خدا برای شما آمد، در این صورت بدانید که خدا (بر انتقام خطا کاران) توانا و دانا است.'),
(217, 2, 210, 'آیا کافران جز این انتظار دارند که خدا با ملائکه در پرده‌های ابر بر آنها در آید و حکم (قهر خدا به کیفر کافران) فرا رسد؟ و کارها همه به سوی خدا باز گردد.'),
(218, 2, 211, '(ای پیغمبر) از بنی اسرائیل سؤال کن که ما چقدر آیات و ادلّه روشن بر آنها آوردیم! و هر کس پس از آنکه نعمت و هدایتی که خدا به او داد آن را به کفر مبدّل کند (بداند که) عقاب خدا بر کافران بسیار سخت است.'),
(219, 2, 212, 'حیات (عاریت و متاع) دنیوی برای کافران جلوه نمود و آنها اهل ایمان را فسوس و مسخره می‌کنند ولی مقام تقوا پیشگان روز قیامت برتر از کافران است، و خدا به هر که خواهد روزی بی‌حساب بخشد.'),
(220, 2, 213, 'مردم یک گروه بودند، خدا رسولان را فرستاد که (نیکوکاران را) بشارت دهند و (بدان را) بترسانند، و با آنها کتاب به راستی فرستاد تا در موارد نزاع مردم تنها دین خدا به عدالت حکمفرما باشد؛ و در کتاب حق، اختلاف و شبهه نیفکندند مگر همان گروه که بر آنان کتاب آسمانی آمد، برای تعدی به حقوق یکدیگر، پس خداوند به لطف خود اهل ایمان را از آن ظلمت شبهات و اختلافات به نور حق هدایت فرمود، و خدا هر که را خواهد راه راست بنماید.'),
(221, 2, 214, 'آیا گمان کرده‌اید که به بهشت داخل شوید بدون امتحاناتی که پیش از شما بر گذشتگان آمد؟ که بر آنان رنج و سختیها رسید و همواره پریشان خاطر و هراسان بودند تا آن گاه که رسول و گروندگان با او (از خدا مدد خواستند و) گفتند: بار خدایا، کی ما را یاری کنی؟ (در آن حال خطاب شد: بشارت دهد که) هان! همانا یاری خدا نزدیک است.'),
(222, 2, 215, 'از تو سؤال کنند که چه انفاق کنند؟ بگو: هر چه از مال خود انفاق کنید درباره پدر و مادر و خویشان و یتیمان و فقیران و در راه ماندگان رواست، و آنچه نیکویی کنید خداوند بر آن آگاه است.'),
(223, 2, 216, 'حکم جهاد بر شما مقرّر گردید و حال آنکه بر شما ناگوار و مکروه است، لکن چه بسیار شود که چیزی را مکروه شمارید ولی به حقیقت خیر و صلاح شما در آن بوده، و چه بسیار شود چیزی را دوست دارید و در واقع شرّ و فساد شما در آن است، و خدا (به مصالح امور) داناست و شما نادانید.'),
(224, 2, 217, '(ای پیغمبر) از تو راجع به جنگ در ماه حرام سؤال کنند، بگو: گناهی است بزرگ، ولی بازداشتن خلق از راه خدا و کفر به خدا و پایمال کردن حرمت حرم خدا و بیرون کردن اهل حرم از آن (که مشرکان مرتکب شدند) نزد خدا بسیار گناه بزرگتری است و فتنه‌گری، فساد انگیزتر از قتل است. و کافران پیوسته با شما مسلمین کارزار کنند تا آنکه اگر بتوانند شما را از دین خود برگردانند، و هر کس از شما از دین خود برگردد و به حال کفر باشد تا بمیرد چنین اشخاص اعمالشان در دنیا و آخرت ضایع و باطل گردیده، و آنان اهل جهنّمند و در آن همیشه (معذّب) خواهند بود.'),
(225, 2, 218, 'آنان که به دین اسلام گرویدند و از وطن خود هجرت نموده در راه خدا جهاد کردند اینان امیدوار و منتظر رحمت خدا باشند، که خدا بخشاینده و مهربان است.'),
(226, 2, 219, '(ای پیغمبر) از تو از حکم شراب و قمار می‌پرسند، بگو: در این دو کار گناه بزرگی است و سودهایی برای مردم، ولی زیان گناه آن دو بیش از منفعت آنهاست. و نیز سؤال کنند تو را که چه در راه خدا انفاق کنند؟ جواب ده: آنچه زائد (بر ضروری زندگانی) است. خداوند بدین روشنی آیات خود را برای شما بیان کند، باشد که تفکر نموده و عقل خود به کار بندید،'),
(227, 2, 220, 'در کار دنیا و آخرت (تا از دنیا برای آخرت توشه بردارید). و سؤال کنند تو را که با یتیمان چگونه رفتار کنند؟ بگو: به اصلاح (حال و مصلحت مآل) آنها کوشید بهتر است (تا آنها را بی‌سرپرست گذارید) و اگر با آن‌ها آمیزش کنید رواست که برادر دینی شما هستند، و خدا آگاه است از آن کس که (در کار آنان) نادرستی کند و آن که اصلاح و درستی نماید. و اگر خدا می‌خواست کار را بر شما سخت می‌کرد چه آنکه خدا توانا و دانا است.'),
(228, 2, 221, 'و با زنان مشرک ازدواج مکنید مگر آنکه ایمان آرند و همانا کنیزکی با ایمان بهتر از زن آزاد مشرک است هر چند از (حسن و جمال) او به شگفت آیید، و زن به مشرکان مدهید مگر آنکه ایمان آرند و همانا بنده مؤمن بسی بهتر از آزاد مشرک است هر چند از (مال و حسن) او به شگفت آیید. مشرکان (شما را از راه جهل) به آتش خوانند و خدا از راه لطف و مرحمت به بهشت و مغفرت دعوت کند و خداوند برای مردم آیات خویش بیان فرماید، باشد که هشیار و متذکر گردند.'),
(229, 2, 222, 'و سؤال کنند تو را از عادت شدن زنان، بگو: آن رنجی است (برای زنان) در آن حال از مباشرت آنان دوری کنید و با آنان نزدیکی نکنید تا آنکه پاک شوند، چون خود را پاک کردند از آنجا که خدا دستور داده به آنها نزدیک شوید، که همانا خدا آنان را که پیوسته به درگاهش توبه کنند و هم پاکیزگان دور از هر آلایش را دوست می‌دارد.'),
(230, 2, 223, 'زنان شما کشتزار شمایند، پس (برای کشت فرزند صالح) بدانها نزدیک شوید هرگاه (مباشرت آنان) خواهید، و چیزی برای خود پیش فرستید و خدا ترس باشید و بدانید که محققا نزد خدا خواهید رفت. و (ای رسول) اهل ایمان را بشارت ده.'),
(231, 2, 224, 'و زنهار نام خدا را هدف سوگندهای خود مکنید تا (با این سوگندهای راست و دروغ) از حقوقی که مردم را بر شماست برائت جوئید و خود را نیکوکار و پرهیزکار قلمداد کنید و مصلح میان مردم جلوه دهید؛ و خدا (راست و دروغ و نیک و بد را) شنوا و داناست.'),
(232, 2, 225, 'خدا شما را به سوگندهای لغو مؤاخذه نکند و لیکن به آنچه در دل دارید مؤاخذه خواهد کرد و خدا بسیار آمرزنده و بردبار است.'),
(233, 2, 226, 'آنان که با زنان خود ایلاء کنند (بر ترک مباشرت آنها قسم بخورند) چهار ماه انتظار کشند، اگر (از قسم خود) بازگشتند خداوند آمرزنده و مهربان است.'),
(234, 2, 227, 'و اگر عزم طلاق نمودند خدا (به گفتار و کردارشان) شنوا و داناست.'),
(235, 2, 228, 'و زنهایی که طلاق داده شدند از شوهر نمودن خودداری کنند تا سه پاکی بر آنان بگذرد و آنچه را (حیض یا حملی) که خدا در رحم آنها آفریده کتمان نکنند اگر به خدا و روز قیامت ایمان دارند. و شوهران آنها در زمان عدّه سزاوارترند که آنها را به زنی خود رجوع دهند اگر نیّت سازش دارند، و زنان را بر شوهران حق مشروعی است چنانچه شوهران را بر زنان، لیکن مردان را بر زنان افزونی است، و خدا (بر هر چیز) توانا و داناست.'),
(236, 2, 229, 'طلاق (طلاقی که شوهر در آن رجوع تواند کرد) دو مرتبه است، پس چون طلاق داد یا رجوع کند به سازگاری و یا رها کند به نیکی، و حلال نیست که چیزی از مهر آنان به جور بگیرید، مگر آنکه بترسید حدود دین خدا را راجع به احکام ازدواج نگاه ندارند، در چنین صورت زن هر چه از مهر خود به شوهر (برای طلاق) ببخشد بر آنان روا باشد. این احکام، حدود دین خداست، از آن سرکشی مکنید و کسانی که از احکام خدا سرپیچند آنها به حقیقت ستمکارند.'),
(237, 2, 230, 'پس اگر زن را طلاق (سوم) داد دیگر آن زن بر او حلال نیست تا اینکه زن به دیگری شوهر کند، اگر آن شوهر دوم زن را طلاق داد زن با شوهر اوّل (که سه طلاق داده) توانند به زوجیّت بازگردند اگر گمان برند که احکام خدا را (راجع به امر ازدواج) نگاه خواهند داشت. این است احکام خدا که برای مردم دانا بیان می‌کند.'),
(238, 2, 231, 'و هرگاه زنان را طلاق دادید بایستی تا نزدیک پایان زمان عدّه یا آنها را به سازگاری (در خانه) نگاه دارید و یا به نیکی رها کنید، و روا نیست آنان را به آزار نگاه داشته تا بر آنها ستم کنید. هر کس چنین کند همانا بر خود ظلم کرده است. و آیات خدا را به مسخره نگیرید و به یاد آرید نعمت خدا را که به شما لطف فرمود و (به خصوص نعمت بزرگ) کتاب آسمانی و حکمت الهی را که به شما فرستاد و شما را به اندرز آن بهره‌مند گرداند، و از خدا پروا کنید و بدانید که خدا به همه چیز آگاه است.'),
(239, 2, 232, 'و چون زنان را طلاق دادید و زمان عده آن‌ها به پایان رسید، نباید آنها را از شوهر کردن منع کنید هرگاه به طریق مشروع به ازدواج با مردی تراضی کنند. بدین سخن پند داده شود هر کس به خدا و روز بازپسین ایمان آورده. این دستور برای تزکیه نفوس شما بهتر است و خدا (به مصلحت شما) داناست و شما (خیر و صلاح خود) نمی‌دانید.'),
(240, 2, 233, 'و مادران بایستی دو سال کامل فرزندان خود را شیر دهند، البته آن کسی که خواهد فرزند را شیر تمام دهد؛ و به عهده صاحب فرزند است (یعنی پدر) که خوراک و لباس مادر را به حد متعارف بدهد، هیچ کس را تکلیف جز به اندازه طاقت نکنند، نه مادر باید در نگهبانی فرزند به زحمت و زیان افتد و نه پدر بیش از حدّ متعارف برای کودک متضرر شود. و اگر کودک را پدر نبود وارث باید در نگهداری او به حد متعارف قیام کند. و اگر پدر و مادر به رضایت و مصلحت دید یکدیگر بخواهند فرزند را از شیر بگیرند هر دو را رواست، و اگر خواهید که برای فرزندان دایه بگیرید آن هم روا باشد در صورتی که دایه را حقوقی به متعارف بدهید. و از خدا پروا کنید و بدانید که خداوند به کردار شما بیناست.'),
(241, 2, 234, 'و مردانی که بمیرند و زنانشان زنده مانند، آن زنان باید از شوهر کردن خودداری کنند تا مدّت چهار ماه و ده روز بگذرد، پس از انقضاء این مدت بر شما گناهی نیست که آنان در حق خویش کاری شایسته کنند (از زینت کردن و شوهر نمودن)، و خداوند از کردارتان آگاه است.'),
(242, 2, 235, 'و باکی بر شما نیست که به فکر خواستگاری آن زنان برآیید یا نیّت ازدواج را در دل داشته بدون اظهار، تنها خدا داند که از این پس از دل به زبان خواهید آورد، ولیکن (در عدّه) با آنها پنهانی قرار و پیمانی نگذارید مگر آنکه سخنی به میزان شرع گویید، ولی عزم عقد و ازدواج مکنید تا زمان عدّه آنها منقضی شود، و بدانید که خداوند از نیّات درونی شما آگاه است، پس از او بترسید و بدانید که خدا بسیار آمرزنده و بردبار است.'),
(243, 2, 236, 'باکی بر شما نیست اگر طلاق دهید زنانی را که با آنان مباشرت نکرده و مهری مقرّر نداشته‌اید ولی آنها را به چیزی بهره‌مند سازید، دارا به قدر خود و نادار به قدر خویش به بهره‌ای شایسته او، که این سزاوار مقام نیکوکاران است.'),
(244, 2, 237, 'و اگر زنها را قبل از مباشرت با آن‌ها طلاق دهید، در صورتی که بر آنان مهر مقرّر داشته‌اید بایستی نصف مهری را که تعیین شده به آنها بدهید مگر آنها خود گذشت کنند یا کسی که امر نکاح به دست اوست (مثل پدر یا جدّ) از آن حق در گذرند، و گذشت کردن به تقوا و خدا پرستی نزدیکتر است، و فضیلتهایی که در نیکویی به یکدیگر است فراموش مکنید، (و بدانید) که خداوند به هر کار (نیک و بد) شما بیناست.'),
(245, 2, 238, 'بر نمازها مواظبت و توجه کامل داشته باشید و به خصوص نماز وسطی (صبح) و به طاعت خدا قیام کنید.'),
(246, 2, 239, 'پس اگر شما را بیم خطری از دشمن باشد به هر حال که میسّر است، پیاده یا سواره نماز به جای آرید و آن گاه که ایمنی یافتید خدا را یاد کنید که او شما را به آنچه نمی‌دانستید دانا گردانید.'),
(247, 2, 240, 'مردانی که بمیرند و زنانشان زنده مانند باید وصیّت کنند که آن‌ها را تا یکسال نفقه دهند و از خانه شوهر بیرون نکنند، پس اگر زنها خود خارج شوند شما را گناهی نیست از آنچه آنها درباره خود در حدود شرع بگزینند، و خدا توانا و داناست.'),
(248, 2, 241, 'مردان، زنانی را که طلاق دهند به چیزی بهره‌مند کنند، که این کار سزاوار مردم پرهیزکار است.'),
(249, 2, 242, 'خدا آیات خود را برای شما این گونه روشن بیان می‌کند، شاید خردمندی کنید.'),
(250, 2, 243, 'آیا ندیدید آنهایی را که از ترس مرگ از دیار خود بیرون رفتند که هزارها تن بودند، خدا فرمود که تمام بمیرید (همه مردند) سپس آنها را زنده کرد، زیرا خدا را در حق بندگان فضل و کرم بسیار است، و لیکن بیشتر مردم سپاسگزار نیستند.'),
(251, 2, 244, 'جهاد کنید در راه خدا و بدانید که خدا (به گفتار و کردار خلق) شنوا و دانا است.'),
(252, 2, 245, 'کیست که خدا را قرض الحسنه دهد تا خدا بر او به چندین برابر بیفزاید؟ و خداست که می‌گیرد و می‌دهد، و همه به سوی او باز گردانده می‌شوید.'),
(253, 2, 246, 'آیا ندیدی آن گروه بنی اسرائیل را که پس از وفات موسی از پیغمبر وقت خود تقاضا کردند که پادشاهی برای ما برانگیز تا (به سرکردگی او) در راه خدا جهاد کنیم؛ پیغمبر آنها گفت: اگر جهاد فرض شود مبادا به جنگ قیام نکرده، نافرمانی کنید! گفتند: چگونه شود که ما به جنگ نرویم در صورتی که (دشمنان ما) ما و فرزندان ما را (به ستم) از دیارمان بیرون کردند؟! پس چون (با درخواست آنها) جهاد مقرّر گردید به جز اندکی همه روی گردانیدند، و خدا از کردار ستمکاران آگاه است.'),
(254, 2, 247, 'پیغمبرشان به آنها گفت: خداوند طالوت را به پادشاهی شما برانگیخت. گفتند که از کجا او را بر ما بزرگی و شاهی رواست در صورتی که ما به پادشاهی شایسته‌تر از اوییم و او را مال فراوان نیست. رسول گفت: خداوند او را برگزیده و در دانش و توانایی و قوت جسم فزونی بخشیده، و خدا ملک خود را به هر که خواهد بخشد، که خدا به حقیقت توانگر و داناست.'),
(255, 2, 248, 'پیغمبرشان به آنها گفت که نشانه پادشاهی او این است که تابوتی که در آن سکینه خدا و الواح بازمانده از خانواده موسی و هارون است و فرشتگانش به دوش برند برای شما می‌آید، که در آن برای شما حجتی است روشن اگر اهل ایمان باشید.'),
(256, 2, 249, 'پس هنگامی که طالوت لشکر کشید، سپاه خود را گفت: خدا شما را به نهر آبی آزمایش کند، هر که از آن بیاشامد از من و هم آیین من نیست، و هر که هیچ نیاشامد یا کفی بیش برنگیرد از من و هم آیین من خواهد بود. پس همه سپاهش آشامیدند به جز عده قلیلی از آنها. و چون طالوت و سپاه مؤمنش از نهر گذشتند (و مواجه با دشمن شدند لشکر بیمناک گشته) گفتند: ما را تاب مقاومت جالوت و سپاه او نخواهد بود. آنان که به لقاء رحمت خدا و ثواب آخرت معتقد بودند (ثابت قدم مانده) گفتند: چه بسیار شده که گروهی اندک به یاری خدا بر سپاهی بسیار غالب آمده، و خدا با صابران است.'),
(257, 2, 250, 'و چون آنها در میدان مبارزه جالوت و لشکریان او آمدند گفتند: پروردگارا، به ما صبر و استواری بخش و ما را ثابت قدم دار و بر شکست کافران یاری فرما.'),
(258, 2, 251, 'پس به یاری خدا کافران را شکست دادند و داود (علیه السّلام) امیر آنها جالوت را کشت و خدایش پادشاهی و فرزانگی عطا فرمود و از آنچه می‌خواست بدو بیاموخت. و اگر خدا برخی مردم را در مقابل بعضی دیگر بر نمی‌انگیخت فساد روی زمین را فرا می‌گرفت، و لیکن خدا، خداوند فضل و کرم بر همه اهل عالم است.'),
(259, 2, 252, 'این آیات خداست که به راستی بر تو می‌خوانیم و همانا تو از جمله پیغمبران مرسل هستی.'),
(260, 2, 253, 'این پیغمبران را برخی بر بعضی برتری و فضیلت دادیم، بعضی را خدا با او سخن گفته و بعضی را رفعت مقام داده، و عیسی پسر مریم را معجزات آشکار دادیم و او را به روح القدس نیرو بخشیدیم، و اگر خدا می‌خواست پس از فرستادن پیامبران و معجزات آشکار، مردم با یکدیگر در مقام خصومت و قتال بر نمی‌آمدند، ولیکن آنان برخلاف و دشمنی با هم برخاستند، برخی ایمان آورده و بعضی کافر شدند، و اگر خدا می‌خواست با هم بر سر جنگ نبودند، و لیکن خدا هر چه مشیّتش قرار گیرد خواهد کرد.'),
(261, 2, 254, 'ای اهل ایمان، از آنچه روزی شما کردیم انفاق کنید پیش از آنکه بیاید روزی که نه کسی (برای آسایش خود) چیزی تواند خرید و نه دوستی و شفاعتی به کار آید، و کافران (دریابند که) خود ستمکار بوده‌اند.'),
(262, 2, 255, 'خدای یکتاست که جز او خدایی نیست، زنده و پاینده است، هرگز او را کسالت خواب فرا نگیرد تا چه رسد که به خواب رود، اوست مالک آنچه در آسمانها و زمین است، که را این جرأت است که در پیشگاه او به شفاعت برخیزد مگر به فرمان او؟ علم او محیط است به آنچه پیش نظر خلق آمده و آنچه سپس خواهد آمد و خلق به هیچ مرتبه از علم او احاطه نتوانند کرد مگر به آنچه او خواهد، قلمرو علمش از آسمانها و زمین فراتر، و نگهبانی زمین و آسمان بر او آسان و بی‌زحمت است، و او والا مرتبه و با عظمت است.'),
(263, 2, 256, 'کار دین به اجبار نیست، تحقیقا راه هدایت و ضلالت بر همه کس روشن گردیده، پس هر که از راه کفر و سرکشی دیو رهزن برگردد و به راه ایمان به خدا گراید بی‌گمان به رشته محکم و استواری چنگ زده که هرگز نخواهد گسست، و خداوند (به هر چه خلق گویند و کنند) شنوا و داناست.'),
(264, 2, 257, 'خدا یار اهل ایمان است که آنان را از تاریکیهای جهل بیرون آرد و به عالم نور برد، و آنان که راه کفر گزیدند یار ایشان شیاطین و دیوهایند که آنها را از عالم نور به تاریکیهای گمراهی در افکنند، این گروه اهل دوزخند و در آن همیشه خواهند بود.'),
(265, 2, 258, 'آیا ندیدی که پادشاه زمان ابراهیم (علیه السّلام) به دلیل حکومتی که خدا به او داده بود درباره (یکتایی) خدا با ابراهیم به جدل برخاست؟ چون ابراهیم گفت: خدای من آن است که زنده گرداند و بمیراند، او گفت: من نیز زنده می‌کنم و می‌میرانم. ابراهیم گفت که خداوند خورشید را از طرف مشرق برآورد، تو اگر توانی از مغرب بیرون آر؛ آن نادان کافر در جواب عاجز ماند؛ و خدا راهنمای ستمکاران نخواهد بود.'),
(266, 2, 259, 'یا به مانند آن کس (عزیر) که به دهکده‌ای گذر کرد که خراب و ویران شده بود، گفت: (به حیرتم که) خدا چگونه باز این مردگان را زنده خواهد کرد! پس خداوند او را صد سال میراند سپس زنده کرد و برانگیخت و بدو فرمود که چند مدّت درنگ نمودی؟ جواب داد: یک روز یا پاره‌ای از یک روز درنگ نمودم، خداوند فرمود (نه چنین است) بلکه صد سال است که به خواب مرگ افتاده‌ای، نظر در طعام و شراب خود بنما که هنوز تغییر ننموده، و الاغ خود را بنگر (که اکنون زنده‌اش کنیم تا احوال بر تو معلوم شود) و تا تو را حجت و نشانه‌ای برای خلق قرار دهیم (که امر بعثت را انکار نکنند) و بنگر در استخوانهای آن که چگونه درهمش پیوسته و گوشت بر آن پوشانیم. چون این کار بر او روشن گردید، گفت: به یقین می‌دانم که خدا بر هر چیز قادر است.'),
(267, 2, 260, 'و چون ابراهیم گفت: بار پروردگارا، به من بنما که چگونه مردگان را زنده خواهی کرد؟ خدا فرمود: باور نداری؟ گفت: آری باور دارم، لیکن خواهم (به مشاهده آن) دلم آرام گیرد. خدا فرمود: چهار مرغ بگیر و گوشت آنها را به هم درآمیز نزد خود، آن گاه هر قسمتی را بر سر کوهی بگذار، سپس آن مرغان را بخوان، که به سوی تو شتابان پرواز کنند؛ و بدان که خدا (بر همه چیز) توانا و داناست.'),
(268, 2, 261, 'مثل آنان که مالشان را در راه خدا انفاق می‌کنند به مانند دانه‌ای است که از آن هفت خوشه بروید و در هر خوشه صد دانه باشد، و خداوند از این مقدار نیز برای هر که خواهد بیفزاید، و خدا را رحمت بی‌منتهاست و (به همه چیز) داناست.'),
(269, 2, 262, 'آنان که مالشان را در راه خدا انفاق کنند و در پی انفاق منّتی نگذارده و آزاری نکنند، آنها را پاداش نیکو نزد خدا خواهد بود و از هیچ پیشامدی بیمناک نباشند و هرگز اندوهناک نخواهند بود.'),
(270, 2, 263, '(فقیر سائل را به) زبان خوش و طلب آمرزش (رد کردن) بهتر است از صدقه‌ای که پی آن آزار کنند، و خداوند بی‌نیاز و بردبار است.'),
(271, 2, 264, 'ای اهل ایمان، صدقات خود را به سبب منّت و آزار تباه نسازید مانند آن که مال خود را از روی ریا (برای جلب توجه دیگران) انفاق کند و ایمان به خدا و روز قیامت ندارد؛ مثل این ریاکاران بدان ماند که دانه را (به جای آنکه در زمین قابلی افشانند) بر روی سنگ صاف غبار گرفته‌ای ریزند و تند بارانی غبار آن بشوید و آن سنگ را همان طور صاف و بی‌گیاه به جای گذارد، که نتوانند هیچ حاصلی از آن به دست آورند. و خداوند گروه کافران را راه (سعادت) ننماید.'),
(272, 2, 265, 'و مثل آنان که مالشان را در راه خشنودی خدا انفاق کنند و با کمال اطمینان خاطر، دل به لطف خدا شاد دارند مثل بوستانی است که در زمین بلندی باشد و بر آن باران زیادی به موقع ببارد و حاصلی دو چندان (که منتظرند) دهد، و اگر باران زیاد نیاید، اندک بارد (تا باز به ثمر رسد)، و خدا (به کار شما) بیناست.'),
(273, 2, 266, 'آیا هیچ یک از شما خواهد که او را باغی باشد از درخت خرما و انگور و زیر آن درختان جویهای روان، و در آن هرگونه میوه موجود باشد، آن گاه ضعف و پیری بدو فرا رسد و او را فرزندان خرد و ناتوان باشد، پس در باغ او بادی آتشبار افتد و همه بسوزد؟ (حال کسی که به ریا انفاق کند بدین ماند). خداوند آیات خود را برای شما این چنین روشن بیان کند، باشد که (در مآل کارها و حقیقت احوال) فکر کنید.'),
(274, 2, 267, 'ای اهل ایمان، انفاق کنید از بهترین آنچه اندوخته‌اید، و از آنچه برای شما از زمین می‌رویانیم، و بدها را برای انفاق در نظر نگیرید، در صورتی که خود شما جنس بد را نستانید مگر آنکه از بدی آن چشم پوشی کنید؛ و بدانید که خدا بی‌نیاز و ستوده صفات است.'),
(275, 2, 268, 'شیطان شما را وعده فقر و بی‌چیزی دهد و به کارهای زشت و بخل وادار کند، و خدا به شما وعده آمرزش و احسان دهد، و خدا را رحمت بی‌منتهاست و (به همه امور جهان) داناست.'),
(276, 2, 269, 'خدا فیض حکمت را به هر که خواهد عطا کند، و هر که را به حکمت و دانش رسانند در باره او مرحمت و عنایت بسیار کرده‌اند، و این حقیقت را جز خردمندان متذکر نشوند.'),
(277, 2, 270, 'هر چه انفاق کنید و یا به نذر (و صدقه) دهید همانا خدا می‌داند، و (اگر در حق مسکینان ستم کنید) برای ستمکاران در دو جهان یار و معینی نخواهد بود.'),
(278, 2, 271, 'اگر (به مستحقان) آشکارا صدقه دهید کاری نیکوست، لیکن اگر در پنهانی به فقیران (آبرومند) رسانید برای شما نیکوتر است و خدا (به پاداش آن) برخی از گناهان شما را محو و مستور دارد، و خداوند از (آشکارا و نهان) آنچه می‌کنید آگاه است.'),
(279, 2, 272, '(ای رسول بر تو دعوت خلق است و) هدایت خلق بر تو نیست، خدا هر که را خواهد هدایت کند. و هر قسم انفاق و احسان کنید درباره خویش کرده‌اید، و نبایست انفاق کنید جز در راه رضای خدا، و هر انفاق و احسانی کنید از خدا پاداش تمام به شما رسد و هرگز به شما ستم نخواهد رسید.'),
(280, 2, 273, 'صدقات مخصوص فقیرانی است که در راه خدا بازمانده و ناتوان شده‌اند و توانایی آنکه در زمین بگردند (و کاری پیش گیرند) ندارند و از فرط عفاف چنانند که هر کس از حال آنها آگاه نباشد پندارد غنی و بی‌نیازند، به (فقر) آنها از سیمایشان پی می‌بری، هرگز چیزی از کسی درخواست نکنند. و هر مالی انفاق کنید خدا به آن آگاه است.'),
(281, 2, 274, 'کسانی که مال خود را در شب و روز انفاق کنند نهان و آشکارا، آنان را نزد پروردگارشان پاداش نیکو خواهد بود و هرگز (از حادثه آینده) بیمناک و (از امور گذشته) اندوهگین نخواهند گشت.'),
(282, 2, 275, 'آن کسانی که ربا خورند (از قبر در قیامت) برنخیزند جز به مانند آن که به وسوسه شیطان مخبّط و دیوانه شده. و آنان بدین سبب در این عمل زشت افتند که گویند فرقی بین تجارت و ربا نیست؛ حال آنکه خدا تجارت را حلال و ربا را حرام کرده. پس هر کس که اندرز (کتاب) خدا بدو رسد و از این عمل دست کشد خدا از گذشته او در گذرد و عاقبت کارش با خدا باشد، و کسانی که دست نکشند اهل جهنّمند و در آن جاوید معذّب خواهند بود.'),
(283, 2, 276, 'خدا سود ربا را نابود گرداند و صدقات را افزونی بخشد، و خدا مردم بی‌ایمان گنه پیشه را دوست ندارد.'),
(284, 2, 277, 'آنان که اهل ایمان و نیکوکارند و نماز به پا دارند و زکات بدهند آنان را نزد پروردگارشان پاداش نیکو خواهد بود و هرگز ترس (از آینده) و اندوه (از گذشته) نخواهند داشت.'),
(285, 2, 278, 'ای کسانی که ایمان آورده‌اید، از خدا بترسید و زیادی ربا را رها کنید اگر به راستی اهل ایمانید.'),
(286, 2, 279, 'پس اگر ترک ربا نکردید آگاه باشید که به جنگ خدا و رسول او برخاسته‌اید. و اگر از این کار پشیمان گشتید اصل مال شما برای شماست، که در این صورت به کسی ستم نکرده‌اید و از کسی ستم نکشیده‌اید.'),
(287, 2, 280, 'و اگر (کسی که از او طلبکار هستید) تنگدست شود به او مهلت دهید تا توانگر گردد، و بخشیدن آن (به هنگام تنگدستی وی) به رسم صدقه، برای شما بهتر است اگر (به مصلحت خود) دانایید.'),
(288, 2, 281, 'و بترسید از روزی که شما را به سوی خدا باز گردانند، پس هر کس پاداش عمل خویش خواهد یافت و به هیچ کس ستمی نکنند.'),
(289, 2, 282, 'ای اهل ایمان، چون به قرض و نسیه معامله کنید تا زمانی معیّن، سند و نوشته در میان باشد، و بایست نویسنده درستکاری معامله میان شما را بنویسد، و نباید کاتب از نوشتن خودداری کند، که خدا به وی نوشتن آموخته پس باید بنویسد، و مدیون باید مطالب را املاء کند، و از خدا بترسد و از آنچه مقرّر شده چیزی نکاهد، و اگر مدیون سفیه یا ناتوان (صغیر) است و صلاحیّت املا ندارد ولیّ او به عدل و درستی املا کند و دو تن از مردان خود (از مسلمانان عادل) گواه آرید، و اگر دو مرد نیابید یک مرد و دو زن، از هر که (به عدالت) قبول دارید گواه گیرید تا اگر یک نفر از آن دو زن فراموش کند دیگری به خاطرش آورد، و هرگاه شهود را (به محکمه) بخوانند امتناع از رفتن نکنند، و در نوشتن آن تا تاریخ معیّن مسامحه نکنید چه معامله کوچک و چه بزرگ باشد. این عادلانه‌تر است نزد خدا و محکمتر برای شهادت و نزدیکتر به اینکه شکّ و ریبی در معامله پیش نیاید (که موجب نزاع شود) مگر آنکه معامله نقد حاضر باشد که دست به دست میان شما برود، در این صورت باکی نیست که ننویسید، و گواه گیرید هرگاه معامله کنید، و نبایست به نویسنده و گواه ضرری رسد (و بی‌اجر مانند)، و اگر چنین کنید نافرمانی کرده‌اید. و از خدا بترسید و خداوند هم به شما تعلیم مصالح امور می‌کند و خدا به همه چیز داناست.'),
(290, 2, 283, 'و اگر در سفر باشید و نویسنده‌ای نیابید برای وثیقه دین گروی گرفته شود. و اگر برخی بعضی را امین داند (و به او امانت بسپارد) پس باید ادا کند آن امین آنچه را که به امانت گرفته است و از خدای خود بترسد (و به امانت خیانت نکند)، و کتمان شهادت ننمایید، که هر کس کتمان کند البته به دل گناهکار است و خدا از همه کار شما آگاه است.'),
(291, 2, 284, 'آنچه در آسمانها و زمین است همه ملک خداست و اگر آنچه در دل دارید آشکار و یا مخفی کنید همه را خدا در محاسبه شما خواهد آورد، آن گاه هر که را خواهد ببخشد و هر که را خواهد عذاب کند، و خدا بر هر چیز تواناست.'),
(292, 2, 285, 'این رسول به آنچه خدا بر او نازل کرده ایمان آورده و مؤمنان نیز همه به خدا و فرشتگان خدا و کتب و پیغمبران خدا ایمان آوردند (و گفتند) ما میان هیچ یک از پیغمبران خدا فرق نگذاریم، و (همه یک زبان و یک دل) اظهار کردند که ما (فرمان خدا را) شنیده و اطاعت کردیم، پروردگارا، ما آمرزش تو را خواهیم و (می‌دانیم) بازگشت همه به سوی توست.'),
(293, 2, 286, 'خدا هیچ کس را تکلیف نکند مگر به قدر توانایی او، نیکیهای هر کس به سود او و بدیهایش نیز به زیان خود اوست. پروردگارا، ما را بر آنچه به فراموشی یا به خطا کرده‌ایم مؤاخذه مکن. بار پروردگارا، تکلیف گران و طاقت فرسا که بر پیشینیان ما نهاده‌ای بر ما مگذار. پروردگارا، بار تکلیفی فوق طاقت ما بر دوش ما منه، و بیامرز و ببخش گناه ما را، و بر ما رحمت فرما، تنها آقای ما و یاور ما تویی، پس ما را بر گروه کافران یاری فرما.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف، لام، میم (از حروف مقطعه و اسرار قرآن است).'),
(295, 3, 2, 'خدای یکتاست که جز او خدایی نیست که زنده و پاینده ابدی است.'),
(296, 3, 3, 'آن خدایی که قرآن را به راستی بر تو فرستاد که تصدیق کننده کتب آسمانی قبل از خود است و (پیش از قرآن) تورات و انجیل را فرستاد،'),
(297, 3, 4, 'برای هدایت مردم، و نیز کتاب جدا کننده میان حق و باطل (یعنی قرآن) را فرستاد. همانا آنان که به آیات خدا کافر شدند بر آنها عذاب سختی است، و خدا مقتدر و کیفر کننده ستمکاران است.'),
(298, 3, 5, 'همانا چیزی در آسمان و زمین از خدا پنهان نیست.'),
(299, 3, 6, 'خداست آن که صورت شما را در رحم مادران می‌نگارد هر گونه اراده کند. خدایی جز آن ذات یکتا نیست که (به هر چیز) توانا و داناست.'),
(300, 3, 7, 'اوست خدایی که قرآن را بر تو فرستاد که برخی از آن کتاب آیات محکم است که اصل و مرجع سایر آیات کتاب خداست و برخی دیگر متشابه است، و آنان که در دلشان میل به باطل است از پی متشابه رفته تا به تأویل کردن آن در دین راه شبهه و فتنه‌گری پدید آرند، در صورتی که تأویل آن را کسی جز خداوند و اهل دانش نداند؛ گویند: ما به همه کتاب گرویدیم که همه از جانب پروردگار ما آمده، و به این (دانش) تنها خردمندان آگاهند.'),
(301, 3, 8, 'بار پروردگارا، دلهای ما را به باطل میل مده پس از آنکه به حق هدایت فرمودی و به ما از لطف خود رحمتی عطا فرما، که تویی بسیار بخشنده (بی‌منّت).'),
(302, 3, 9, 'پروردگارا، محققا تو تمام مردم را در روزی که هیچ شبهه در آن نیست جمع سازی، و هرگز خدا نقض وعده خویش نخواهد کرد.'),
(303, 3, 10, 'کافران را هرگز مال و فرزندانشان از عذاب خدا نرهاند، و آنان خود آتش افروز جهنّمند.'),
(304, 3, 11, 'این گروه هم به مانند فرعونیان و پیشینیان (کافر کیش)، آیات ما را تکذیب کردند، خدا هم آنها را به کیفر گناهانشان مجازات کرد و خدا بدکاران را سخت به کیفر رساند.'),
(305, 3, 12, 'بگو به آنان که کافر شدند که به زودی مغلوب شوید و به جهنّم محشور گردید که بسیار بد جایگاهی است.'),
(306, 3, 13, 'نشانه و آیتی (از لطف خدا) برای شما در این بود که چون دو گروه با یکدیگر روبرو شدند (در جنگ بدر) گروهی در راه خدا جهاد می‌کردند و گروه دیگر کافر بودند، و گروه کافر مؤمنان را دو برابر خود به چشم می‌دیدند، و خدا توانایی و یاری دهد به هر که خواهد، و بدین آیت الهی اهل بصیرت عبرت جویند.'),
(307, 3, 14, 'مردم را حبّ شهوات نفسانی، از میل به زنها و فرزندان و همیان‌هایی از طلا و نقره و اسبهای نشان دار نیکو و چهارپایان و مزارع در نظر زیبا و دلفریب است، (لیکن) اینها متاع زندگانی (فانی) دنیاست و نزد خداست منزل بازگشت نیکو.'),
(308, 3, 15, '(ای پیغمبر) بگو: می‌خواهید شما را آگاه گردانم به بهتر از اینها؟ برای آنان که تقوا پیشه کنند نزد خدا باغهای بهشتی است که در زیر درختان آن نهرها جاری است و در آن جاوید و متنّعم هستند و زنان پاکیزه و آراسته‌ای و (از همه بهتر) خشنودی خدا، و خداوند به حال بندگان بیناست.'),
(309, 3, 16, 'آنان که (به درگاه الهی) عرضه دارند: پروردگارا: (به کرم خود) گناهان ما ببخش و ما را از عذاب جهنّم نگاه دار.'),
(310, 3, 17, 'آنان صابران و راستگویان و فرمانبرداران و انفاق کنندگان و استغفار کنندگان در سحرگاهانند.'),
(311, 3, 18, 'خدا به یکتایی خود گواهی دهد که جز ذات اقدس او که نگهبان عدل و درستی است خدایی نیست، و فرشتگان و دانشمندان نیز به یکتایی او گواهی دهند؛ نیست خدایی جز او که (بر همه چیز) توانا و داناست.'),
(312, 3, 19, 'همانا دین (پسندیده) نزد خدا آیین اسلام است، و اهل کتاب راه اختلاف نپیمودند مگر پس از آگاهی به حقّانیت آن، و این خلاف را از راه حسد در میان آوردند. و هر کس به آیات خدا کافر شود (بترسد که) محاسبه خدا زود خواهد بود.'),
(313, 3, 20, 'پس اگر با تو (برای مخالفت با دین حقّ، به باطل) احتجاج کنند، بگو: من و پیروانم خود را تسلیم امر خدا نموده‌ایم. و بگو با اهل کتاب و با امّیان: آیا شما هم (چون حق پدیدار شود) تسلیم حق شده و ایمان آرید؟ و چنانچه ایمان آرند هدایت یافته‌اند و اگر (از حق) روی گردانند بر تو چیزی جز تبلیغ (دین خدا) نیست و خداوند به حال بندگان بیناست.'),
(314, 3, 21, 'همانا آنان که به آیات خدا کافر شوند و انبیاء را بی‌جرم و به ناحق بکشند و آن مردمی را که (خلق را) به درستی و عدل خوانند به قتل رسانند، آنها را به عذاب دردناک بشارت ده.'),
(315, 3, 22, 'آنهایند که اعمالشان در دنیا و آخرت تباه گردیده و کسی آنها را (برای نجات از عذاب) یاری نخواهد کرد.'),
(316, 3, 23, 'آیا ننگریستی آنهایی را که بهره‌ای از کتاب داده‌اند که چون دعوت شوند تا کتاب خدا بر آنها حکم کند گروهی از آنان (از حکم حق) روی گردانند و از آن دوری گزینند؟'),
(317, 3, 24, 'بدین سبب که گویند: ما را هرگز در آتش جز روزهایی چند عذاب نکنند. و این سخنان باطل که به دروغ بر خود بسته‌اند آنها را در دین خود مغرور گردانیده است.'),
(318, 3, 25, 'پس چگونه خواهد بود حال آنها هنگامی که جمع آوریم آنان را در روزی که هیچ شکّی در آن نیست؟ و هر کس به تمام جزای اعمال خود خواهد رسید و به کسی ستم نخواهد شد.'),
(319, 3, 26, 'بگو (ای پیغمبر): بار خدایا، ای پادشاه ملک هستی، تو هر که را خواهی ملک و سلطنت بخشی و از هر که خواهی ملک و سلطنت بازگیری، و هر که را خواهی عزت دهی و هر که را خواهی خوار گردانی، هر خیر و نیکویی به دست توست و تنها تو بر هر چیز توانایی.'),
(320, 3, 27, 'شب را در پرده روز نهان سازی، و روز را در حجاب شب ناپدید گردانی، زنده را از مرده و مرده را از زنده برانگیزی، و به هر که خواهی روزی بی‌حساب عطا کنی.'),
(321, 3, 28, 'نباید اهل ایمان، مؤمنان را وا گذاشته و از کافران دوست گزینند، و هر که چنین کند رابطه او با خدا مقطوع است مگر برای در حذر بودن از شرّ آنها (تقیه کنند). و خدا شما را از (عقاب) خود می‌ترساند و بازگشت همه به سوی خدا خواهد بود.'),
(322, 3, 29, '(ای پیغمبر) بگو: هر چه را در دل پنهان داشته و یا آشکار کنید خدا به همه آنها آگاه است و به هر چه در آسمانها و زمین است داناست، و خدا (به قدرت کامله) بر همه چیز تواناست.'),
(323, 3, 30, 'روزی که هر شخصی هر کار نیکو کرده همه را پیش روی خود حاضر بیند و نیز آنچه بد کرده؛ آرزو کند که ای کاش میان او و کار بدش به مسافتی دور جدایی بود! و خداوند شما را از (عقاب) خود می‌ترساند، و خداوند در حق بندگان بسی مهربان است.'),
(324, 3, 31, 'بگو (ای پیغمبر): اگر خدا را دوست می‌دارید مرا پیروی کنید تا خدا شما را دوست دارد و گناه شما را ببخشد، و خدا آمرزنده و مهربان است.'),
(325, 3, 32, 'بگو: فرمان خدا و رسول را اطاعت کنید و اگر (از آنان) روی گردانید (و کافر شوید) همانا خدا هرگز کافران را دوست ندارد.'),
(326, 3, 33, 'به حقیقت خدا آدم و نوح و خاندان ابراهیم و خاندان عمران را بر جهانیان برگزید.'),
(327, 3, 34, 'فرزندانی هستند برخی از نسل برخی دیگر، و خدا (به اقوال و احوال همه) شنوا و داناست.'),
(328, 3, 35, 'آن گاه که زن عمران گفت: پروردگارا، من عهد کردم فرزندی که در رحم دارم از فرزندی خود در راه خدمت تو آزاد گردانم، این عهد من بپذیر که تویی شنوا و آگاه.'),
(329, 3, 36, 'و چون او را بزاد (از روی حسرت) گفت: پروردگارا، فرزندم دختر است!-و خدا بر آنچه او زاییده داناتر است-و پسر مانند دختر نخواهد بود، و من او را مریم نام نهادم و او و فرزندانش را از شرّ شیطان رانده شده در پناه تو آوردم.'),
(330, 3, 37, 'پس خدا او را به نیکویی پذیرفت و به تربیتی نیکو پرورش داد و زکریّا را برای کفالت و نگهبانی او برگماشت؛ هر وقت زکریّا به صومعه عبادت مریم می‌آمد نزد او رزق شگفت آوری می‌یافت، گفت که ای مریم، این روزی از کجا برای تو می‌رسد؟ پاسخ داد که این از جانب خداست، همانا خدا به هر کس خواهد روزی بی‌حساب دهد.'),
(331, 3, 38, 'در آن هنگام زکریّا (که کرامت مریم مشاهده کرد) پروردگار خود را بخواند، گفت: پروردگارا، مرا (به لطف خویش) فرزندی پاک سرشت عطا فرما، که همانا تویی اجابت کننده دعا.'),
(332, 3, 39, 'پس فرشتگان زکریّا را ندا کردند هنگامی که در محراب عبادت به نماز ایستاده بود که همانا خدا تو را بشارت می‌دهد به (ولادت) یحیی، در حالی که او به کلمه خدا (یعنی نبوّت عیسی) گواهی دهد و او خود پیشوا و پارسا و پیغمبری از شایستگان است.'),
(333, 3, 40, 'زکریّا عرض کرد: پروردگارا، چگونه مرا پسری تواند بود در حالی که مرا سن پیری رسیده و عیالم عجوزی نازاست؟ گفت: چنین است، خدا هر آنچه بخواهد می‌کند.'),
(334, 3, 41, 'گفت: پروردگارا، برای من (به شکرانه این نعمت) آیتی مقرّر فرما، فرمود: تو را آیت این باشد که تا سه روز با مردم سخن جز به رمز نگویی، و پیوسته به یاد خدا باش و (او را) در صبحگاه و شبانگاه تسبیح گوی.'),
(335, 3, 42, 'و (یاد کن) آن گاه که فرشتگان گفتند: ای مریم، همانا خدا تو را برگزید و پاکیزه گردانید و بر زنان جهانیان برتری بخشید.'),
(336, 3, 43, 'ای مریم، فرمانبردار خدا باش و نماز را با اهل طاعت به جای آر.'),
(337, 3, 44, 'این از اخبار غیب است که به تو وحی می‌کنیم، و تو حاضر نبودی آن زمان که قرعه برای نگهبانی و کفالت مریم می‌زدند تا قرعه به نام کدام یک شود، و نبودی نزد ایشان وقتی که بر سر این کار نزاع می‌کردند.'),
(338, 3, 45, 'چون فرشتگان مریم را گفتند که خدا تو را به کلمه خود بشارت می‌دهد که نامش مسیح (عیسی) پسر مریم است، که در دنیا و آخرت آبرومند و از مقرّبان (درگاه خدا) است.'),
(339, 3, 46, 'و با خلق در گهواره سخن گوید بدان گونه که در سنین بزرگی، و او از جمله نیکویان جهان است.'),
(340, 3, 47, 'مریم گفت: پروردگارا، مرا چگونه فرزندی تواند بود و حال آنکه با من مردی نزدیک نشده؟ گفت: چنین است (کار خدا)، خدا هر آنچه بخواهد می‌آفریند؛ چون مشیّت او به چیزی قرار گیرد، به محض اینکه گوید «موجود باش»، همان دم موجود می‌شود.'),
(341, 3, 48, 'و خدا به او تعلیم کتاب و حکمت کند و تورات و انجیل آموزد.'),
(342, 3, 49, 'و او را به رسالت به سوی بنی اسرائیل فرستد که به آنان گوید: من از طرف خدا معجزی آورده‌ام، من از گل مجسمه مرغی ساخته و بر آن (نفس قدسی) بدمم تا به امر خدا مرغی گردد، و کور مادر زاد و مبتلای به پیسی را به امر خدا شفا دهم، و مردگان را به امر خدا زنده کنم، و به شما (از غیب) خبر دهم که در خانه‌هاتان چه می‌خورید و چه ذخیره می‌کنید. این معجزات برای شما حجّت و نشانه (حقانیت من) است اگر اهل ایمان هستید.'),
(343, 3, 50, 'و (آمده‌ام) در حالی که کتاب تورات شما را تصدیق کنم و حلال گردانم بعض چیزهایی را که بر شما حرام شده بود، و از طرف خداوند برای شما معجزی آورده‌ام، پس (ای بنی اسرائیل) از خدا بترسید و مرا اطاعت کنید.'),
(344, 3, 51, 'همانا خداست پروردگار من و شما، او را بپرستید، که همین است راه راست.'),
(345, 3, 52, 'پس چون عیسی به یقین دریافت که قوم ایمان نخواهند آورد، گفت: کیست که با من دین خدا را یاری کند؟ حواریّون (شاگردان خاص او) گفتند: ما یاری کنندگان دین خداییم، به خدا ایمان آورده‌ایم، و گواهی ده که ما تسلیم فرمان اوییم.'),
(346, 3, 53, 'پروردگارا ما به کتابی که فرستادی ایمان آورده و از رسول تو پیروی کردیم، نام ما را در صحیفه اهل یقین ثبت فرما.'),
(347, 3, 54, 'یهود با خدا مکر کردند و خدا هم در مقابل با آنها مکر کرد، و خدا از همه بهتر تواند مکر کرد.'),
(348, 3, 55, '(به یاد آر) وقتی که خدا فرمود: ای عیسی، همانا من (روح) تو را قبض نموده و به سوی (آسمان قرب) خود بالا برم و تو را پاک و منزّه از (معاشرت و آلایش) کافران گردانم و پیروان تو را بر کافران تا روز قیامت برتری دهم، آن گاه بازگشت شما به سوی من خواهد بود، پس (به حق) حکم کنم در آنچه بر سر آن با هم به نزاع بر می‌خاستید.'),
(349, 3, 56, 'پس آن گروهی را که کافر شدند به عذابی سخت در دنیا و آخرت معذّب گردانم، و (برای نجاتشان) هیچ کس به آنها مدد و یاری نخواهد کرد.'),
(350, 3, 57, 'اما آنان که ایمان آورده و نیکوکار شدند، خدا اجر تمام و کامل به آنها عطا کند، و خدا هرگز ستمکاران را دوست نمی‌دارد.'),
(351, 3, 58, 'این سخنان که بر تو می‌خوانیم از آیات الهی و ذکر حکمت آموز است.'),
(352, 3, 59, 'همانا مثل (خلقت) عیسی به امر خدا مثل خلقت آدم است که او را از خاک بساخت، سپس بدان خاک گفت: (بشری به حد کمال) باش، همان دم چنان گشت.'),
(353, 3, 60, 'سخن حق همان است که از جانب خدا به تو رسید، مبادا هیچ گاه در آن شکّ کنی!'),
(354, 3, 61, 'پس هر کس با تو درباره عیسی در مقام مجادله برآید بعد از آنکه به احوال او آگاهی یافتی، بگو: بیایید ما و شما فرزندان و زنان و کسانی را که به منزله خودمان هستند بخوانیم، سپس به مباهله برخیزیم (در حق یکدیگر نفرین کنیم) تا دروغگویان (و کافران) را به لعن و عذاب خدا گرفتار سازیم.'),
(355, 3, 62, 'این داستان به حقیقت سخن حق است، و جز آن خدای یکتا خدایی نیست، و همانا خداست که (بر همه کار) توانا و (به حقایق) داناست.'),
(356, 3, 63, 'پس اگر روی از حق بگردانند (باید بترسند که) خدا از (کردار) مفسدان آگاه است.'),
(357, 3, 64, 'بگو: ای اهل کتاب، بیایید از آن کلمه حق که میان ما و شما یکسان است پیروی کنیم که به جز خدای یکتا را نپرستیم، و چیزی را با او شریک قرار ندهیم، و برخی برخی را به جای خدا به ربوبیّت تعظیم نکنیم. پس اگر از حق روی گردانند بگویید: شما گواه باشید که ما تسلیم فرمان خداوندیم.'),
(358, 3, 65, 'ای اهل کتاب، چرا در آیین ابراهیم با یکدیگر مجادله می‌کنید (که هر یک او را به خود نسبت می‌دهید)؟! در صورتی که تورات و انجیل شما فرستاده نشد مگر بعد از ابراهیم؛ آیا تعقل نمی‌کنید؟'),
(359, 3, 66, 'گیرم در آنچه می‌دانید شما را مجادله روا باشد، چرا در آنچه علم ندارید جدل و گفتگو به میان آورید؟! و خدا (همه چیز را) می‌داند و شما نمی‌دانید.'),
(360, 3, 67, 'ابراهیم به آیین یهود و نصاری نبود، لیکن به دین حنیف توحید و اسلام بود و هرگز از آنان که به خدا شرک آورند نبود.'),
(361, 3, 68, 'نزدیکترین مردم به ابراهیم کسانی هستند که از او پیروی کنند و این پیغمبر و امّتش که اهل ایمانند؛ و خدا دوستدار و سرپرست مؤمنان است.'),
(362, 3, 69, 'گروهی از اهل کتاب انتظار و آرزوی آن دارند که شما را گمراه کنند، و گمراه نمی‌کنند مگر خودشان را، و (این را) نمی‌فهمند.'),
(363, 3, 70, 'ای اهل کتاب، چرا به آیات خدا کافر شوید و حال آنکه شما به صدق آن گواهید؟!'),
(364, 3, 71, 'ای اهل کتاب، چرا حق را به باطل مشتبه سازید (تا چراغ حق را به باد شبهات خاموش کنید) و حق را پوشیده می‌دارید در صورتی که (به حقّانیّت آن) آگاهید؟!'),
(365, 3, 72, 'و گروهی از اهل کتاب گفتند: به دین و کتابی که برای مسلمانان نازل شده اول روز ایمان آرید و آخر روز کافر شوید، شاید (به این حیله) آنها نیز (از اسلام) برگردند.'),
(366, 3, 73, 'و ایمان نیاورید مگر به آن کس که پیرو دین شما باشد-بگو: راه سعادت آن است که خدا بنماید-تا مبادا معلوم شود که به امتی دیگر نیز مانند شریعت شما عطا می‌گردد، یا بتوانند با شما نزد پروردگار احتجاج کنند. بگو: فضل و رحمت به دست خداست، به هر که خواهد عطا کند، و خدا را رحمت بی‌منتهاست و به همه امور داناست.'),
(367, 3, 74, 'هر که را مشیّت او تعلّق بگیرد مخصوص به فضل و رحمت خود گرداند، و خدا را فضل و رحمت بی‌منتهاست.'),
(368, 3, 75, 'بعضی از اهل کتاب (به آن حد درستکارند که) اگر مال بسیار به آنها بسپاری ردّ امانت کنند، و برخی دیگر از اهل کتاب (تا آن اندازه نادرستند که) اگر یک دینار به او امانت دهی رد نکنند جز آنکه بر مطالبه آن سخت‌گیری کنی، از این رو که گویند: برای ما (پیروان تورات) به هر وسیله خوردن مال امیّان (غیر اهل تورات) گناهی ندارد، و این سخن را به دروغ به خدا نسبت دهند در صورتی که می‌دانند (که به خدا نسبت دروغ می‌دهند).'),
(369, 3, 76, 'آری، هر کس به عهد خود وفا کند و خدا ترس و پرهیزکار شود همانا خدا پرهیزکاران را دوست می‌دارد.'),
(370, 3, 77, 'آنان که عهد خدا و سوگند خدا را به بهایی اندک بفروشند اینان را در آخرت بهره‌ای نیست و خدا با آنها (از خشم) سخن نگوید و در قیامت (به نظر رحمت) بدانها ننگرد و (از پلیدی گناه) پاکیزه‌شان نگرداند، و آنان را عذابی دردناک خواهد بود.'),
(371, 3, 78, 'و همانا برخی از اهل کتاب، قرائت کتاب آسمانی را تغییر و تبدیل می‌دهند تا شما آنچه آنها از پیش خود خوانده‌اند از کتاب خدا محسوب دارید، و هرگز آن تحریف شده از کتاب خدا نخواهد بود، و گویند: این از جانب خدا نازل شده، در صورتی که هرگز از خدا نیست. و با آنکه می‌دانند به خدا دروغ می‌بندند.'),
(372, 3, 79, 'هیچ بشری را نرسد که خدا وی را به رسالت برگزیند و به او کتاب و حکمت و نبوت بخشد آن گاه به مردم گوید: مرا به جای خدا بپرستید، بلکه (پیغمبران بر حسب وظیفه خود به مردم گویند) خداشناس و خداپرست باشید چنانکه این حقیقت را از کتاب به دیگران می‌آموزید و خود نیز می‌خوانید.'),
(373, 3, 80, 'و هرگز خدا به شما امر نکند که فرشتگان و پیامبران را خدایان خود گیرید؛ چگونه ممکن است شما را به کفر خواند پس از آنکه (به خدای یگانه) اسلام آورده باشید؟!'),
(374, 3, 81, 'و (یاد کن) آن گاه که خدا از پیغمبران (و امّتهاشان) پیمان گرفت که چون به شما کتاب و حکمت بخشیدم سپس بر شما (اهل کتاب) رسولی از جانب خدا آمد که گواهی به راستی کتاب و شریعت شما می‌داد به او ایمان آورده و یاری او کنید؛ خدا فرمود: آیا اقرار داشته و پیمان مرا بر این امر پذیرفتید؟ همه گفتند: اقرار داریم. خدا فرمود: گواه باشید، من هم با شما گواهم.'),
(375, 3, 82, 'پس کسانی که بعد از آمدن آن رسولان روی (از حق) بگردانند، محققا آنان گروه فاسقان خواهند بود.'),
(376, 3, 83, 'آیا کافران، دینی غیر دین خدا را می‌طلبند؟ و حال آنکه هر که در آسمانها و زمین است خواه ناخواه مطیع امر خداست و همه به سوی او رجوع خواهند کرد.'),
(377, 3, 84, 'بگو: ما به خدای عالم و شریعت و کتابی که به خود ما نازل شده و آنچه به ابراهیم و اسماعیل و اسحاق و یعقوب و فرزندانش نازل شده و آنچه به موسی و عیسی و پیغمبران دیگر از جانب پروردگارشان داده شده (به همه ایمان آورده‌ایم) فرقی میان هیچ یک از پیغمبران نگذاریم و ما مطیع فرمان خداییم.'),
(378, 3, 85, 'هر کس غیر از اسلام دینی اختیار کند هرگز از وی پذیرفته نیست، و او در آخرت از زیانکاران است.'),
(379, 3, 86, 'چگونه خداوند گروهی را که بعد از ایمان به خدا و گواهی دادن به راستی رسول او و بعد از ادلّه روشن باز کافر شدند، به راه راست هدایت کند؟ و خدا هرگز گروه ستمکاران را رهبری نخواهد کرد.'),
(380, 3, 87, 'کیفر آن گروه کافر این است که خدا و فرشتگان و همه مردمان بر آنان لعنت کنند.'),
(381, 3, 88, 'جاوید در جایگاه لعنت (که جهنّم است) بمانند و بر آنها عذاب خدا تخفیف نیابد و هرگز نظر رحمت به آنها نکنند.'),
(382, 3, 89, 'مگر آنهایی که بعد از عصیان و بدکاری توبه کنند و (هر بدی که کرده‌اند) اصلاح نمایند، که البته خدا بر آنها آمرزنده و مهربان است.'),
(383, 3, 90, 'همانا آنان که بعد از ایمان کافر شدند و بر کفر خود افزودند، هرگز توبه آنها پذیرفته نشود، و همانا گمراهان هم آنان خواهند بود.'),
(384, 3, 91, 'البته آنان که کافر شدند و بر کفر خویش مردند، اگر هر یک برای آزادی خویش (از عذاب خدا) برابر تمام زمین طلا به فدا آرند هرگز از ایشان پذیرفته نشود، آنها را عذاب دردناک مهیّا باشد و یارانی نخواهند داشت.'),
(385, 3, 92, 'شما هرگز به مقام نیکوکاران و خاصان خدا نخواهید رسید مگر از آنچه دوست می‌دارید و محبوب شماست در راه خدا انفاق کنید، و آنچه انفاق کنید خدا بر آن آگاه است.'),
(386, 3, 93, 'همه طعامها بر بنی اسرائیل حلال بود مگر آن را که یعقوب پیش از نزول تورات بر خود حرام کرد. بگو: اگر راست می‌گویید حکم تورات را آورده، تلاوت کنید.'),
(387, 3, 94, 'پس کسانی که بعد از این حجّت بر خدا دروغ بندند، البته ستمکاران هم آنها خواهند بود.'),
(388, 3, 95, 'بگو: سخن خدا راست است، پس باید از آیین ابراهیم پیروی کنید که بر آیین پاک توحید بود و هرگز از مشرکان نبود.'),
(389, 3, 96, 'اول خانه‌ای که برای عبادت خلق بنا شد همان خانه (کعبه) است که در بکّه است که در آن برکت و هدایت خلایق است.'),
(390, 3, 97, 'در آن خانه آیات (ربوبیّت) هویداست، مقام ابراهیم خلیل است، و هر که در آنجا داخل شود ایمن باشد. و مردم را حجّ و زیارت آن خانه به امر خدا واجب است بر هر کسی که توانایی برای رسیدن به آنجا دارد، و هر که کافر شود (به خود زیان رسانده و) خدا از جهانیان بی‌نیاز است.'),
(391, 3, 98, 'بگو: ای اهل کتاب، چرا به آیات خدا کافر می‌شوید حال آنکه خدا گواه اعمال شماست؟!'),
(392, 3, 99, 'بگو: ای اهل کتاب، چرا اهل ایمان را از راه خدا باز می‌دارید و اهل ایمان را به راه باطل می‌خوانید در صورتی که (به زشتی این عمل) آگاهید؟! و (بترسید که) خدا از کردار شما غافل نیست.'),
(393, 3, 100, 'ای کسانی که ایمان آورده‌اید، اگر پیروی بسیاری از اهل کتاب کنید شما را بعد از ایمان به کفر بر می‌گردانند.'),
(394, 3, 101, 'و چگونه کافر خواهید شد در صورتی که آیات خدا بر شما تلاوت می‌شود و پیغمبر خدا میان شماست؟ و هر کس به دین خدا متمسّک شود محققا به راه مستقیم هدایت یافته است.'),
(395, 3, 102, 'ای اهل ایمان، از خدا بترسید چنانچه شایسته خدا ترس بودن است، و نمیرید جز به دین اسلام.'),
(396, 3, 103, 'و همگی به رشته (دین) خدا چنگ زده و به راههای متفرّق نروید، و به یاد آرید این نعمت بزرگ خدا را که شما با هم دشمن بودید، خدا در دلهای شما الفت و مهربانی انداخت و به لطف و نعمت خدا همه برادر دینی یکدیگر شدید، و در پرتگاه آتش بودید، خدا شما را نجات داد. بدین گونه خدا آیاتش را برای (راهنمایی) شما بیان می‌کند، باشد که هدایت شوید.'),
(397, 3, 104, 'و باید برخی از شما مسلمانان، خلق را به خیر و صلاح دعوت کنند و امر به نیکوکاری و نهی از بدکاری کنند، و اینها (که واسطه هدایت خلق هستند) رستگار خواهند بود.'),
(398, 3, 105, 'و شما مسلمانان مانند مللی نباشید که پس از آنکه آیات روشن برای (هدایت) آنها آمد راه تفرقه و اختلاف پیمودند، که البته برای آنها عذاب بزرگ خواهد بود.'),
(399, 3, 106, 'در روزی که گروهی روسفید و گروهی روسیاه باشند، اما سیه رویان (را نکوهش کنند که) چرا بعد از ایمان باز کافر شدید؟ اکنون بچشید عذاب را به کیفر کفر و عصیان خود.'),
(400, 3, 107, 'و اما روسفیدان در رحمت خدا (یعنی بهشت) درآیند و در آن جاوید متنعّم باشند.'),
(401, 3, 108, 'اینها آیات الهی است که برای تو به راستی می‌خوانیم، و خداوند هرگز اراده ستم به هیچ کس از مردم عالم نخواهد کرد.'),
(402, 3, 109, 'و هر آنچه در آسمانها و آنچه در زمین است همه ملک خداست و بازگشت همه امور (و موجودات) به سوی اوست.'),
(403, 3, 110, 'شما (مسلمانان حقیقی) نیکوترین امتی هستید که پدیدار گشته‌اید (برای اصلاح بشر، که مردم را) به نیکوکاری امر می‌کنید و از بدکاری باز می‌دارید و ایمان به خدا دارید. و اگر اهل کتاب همه ایمان می‌آوردند بر آنان چیزی بهتر از آن نبود، لیکن برخی از آنان با ایمان و بیشترشان فاسق و بدکارند.'),
(404, 3, 111, 'هرگز (یهودان) به شما آسیب سخت نتوانند رسانید مگر آنکه شما را اندکی بیازارند، و اگر به کارزار شما آیند از جنگ خواهند گریخت و از آن پس هیچ وقت منصور نخواهند بود.'),
(405, 3, 112, 'آنها محکوم به ذلّت و خواری هستند به هر کجا که یافت شوند، مگر به دین خدا و عهد مسلمین درآیند. و آنان پیوسته مستحق خشم خدا و اسیر بدبختی و ذلّت شدند از این رو که به آیات خدا کافر شده و پیغمبران حق را به ناحق می‌کشتند، و این نابکاریها به خاطر نافرمانی و ستمگری همیشه آنها بود.'),
(406, 3, 113, 'همه اهل کتاب یکسان نیستند، طایفه‌ای از آنها معتدل و به راه راست‌اند، در دل شب به تلاوت آیات خدا و نماز و طاعت حق مشغولند.'),
(407, 3, 114, 'ایمان به خدا و روز قیامت می‌آورند و امر به نیکویی و نهی از بدکاری می‌کنند و در نکوکاری می‌شتابند و آنها خود مردمی نیکوکارند.'),
(408, 3, 115, 'هر کار نیک کنند از ثواب آن محروم نخواهند شد و خدا دانا به حال پرهیزکاران است.'),
(409, 3, 116, 'هرگز کافران را بسیاری مال و اولاد از عذاب خدا نتواند رهانید، و آنها اهل جهنم و جاوید در آن معذّب خواهند بود.'),
(410, 3, 117, 'مثل مالی که کافران برای پیشرفت مقاصد دنیوی خود صرف کنند (و از آن بهره نیابند) بدان ماند که باد سرد شدیدی به کشتزار ستمکاران برسد و همه را نابود کند (که حاصلی از آن به دست نیاید). و خدا ستمی بر آنها نکرده، لیکن آنها به خود ستم می‌کنند.'),
(411, 3, 118, 'ای اهل ایمان، از غیر همدینان خود دوست صمیمی همراز نگیرید، چه آنکه آنها از خلل و فساد در کار شما ذرّه‌ای کوتاهی نکنند، آنها مایلند شما همیشه در رنج باشید، دشمنی شما را بر زبان هم آشکار سازند و محققا آنچه در دل دارند بیش از آن است. ما به خوبی برای شما بیان آیات کردیم اگر عقل را به کار بندید.'),
(412, 3, 119, 'آگاه باشید، چنانچه شما آنها را دوست می‌دارید آنان شما را دوست نمی‌دارند، و شما به همه کتب آسمانی ایمان دارید، و آنها در مجامع شما اظهار ایمان کرده و چون تنها شوند از شدت کینه بر شما سر انگشت خشم به دندان گیرند. بگو: بدین خشم بمیرید، همانا خدا از درون دلها آگاه است.'),
(413, 3, 120, 'اگر شما (مسلمین) را خوشحالی پیش آید از آن غمگین شوند، و اگر به شما حادثه ناگواری رسد خوشحال شوند. اما اگر شما صبر پیشه کنید و پرهیزکار باشید مکرر و عداوت آنان آسیبی به شما نرساند، که همانا خدا بر آنچه می‌کنید محیط و آگاه است.'),
(414, 3, 121, '(و به یاد آرای پیغمبر) صبحگاهی که از خانه خود به جهت صف آرایی مؤمنان برای جنگ بیرون شدی، و خدا (به همه گفتار و کردار تو) شنوا و دانا بود.'),
(415, 3, 122, 'آن‌گاه که دو طایفه از شما بد دل و ترسناک (و در اندیشه فرار از جنگ) شدند و خدا یار آنها بود، و همیشه باید اهل ایمان به خدا توکل کنند.'),
(416, 3, 123, 'و خدا شما را در جنگ بدر یاری کرد با آنکه ضعیف بودید. پس پرهیزکار شوید، باشد که شکر نعمتهای او به جای آرید.'),
(417, 3, 124, 'آن‌گاه که به مؤمنین می‌گفتی: آیا این شما را کفایت نمی‌کند که خدا سه هزار فرشته به یاری شما فرو فرستد؟!'),
(418, 3, 125, 'بلی اگر صبر و مقاومت پیشه کنید و پرهیزکار باشید، چون کافران بر سر شما شتابان بیایند خدا پنج‌هزار فرشته را با پرچمی که نشان مخصوص سپاه اسلام است به مدد شما می‌فرستد.'),
(419, 3, 126, 'و خدا آن وعده را نداد مگر آنکه به شما مژده فتح دهد و تا دل شما مطمئن شود؛ و فتح و پیروزی نصیب کسی نشود مگر از جانب خدای توانای دانا.'),
(420, 3, 127, 'تا گروهی از کافران را هلاک گرداند یا خوار کند، تا (از مقصود خود) ناامید باز گردند.'),
(421, 3, 128, '(ای پیغمبر) به دست تو کاری نیست، اگر خدا بخواهد (به لطف خود) از آن کافران درگذرد و اگر بخواهد آنها را عذاب کند چون مردمی ستمکارند.'),
(422, 3, 129, 'هر چه در آسمانها و هر چه در زمین است مِلک خداست، هر که را خواهد ببخشد و هر که را خواهد عذاب کند، و خدا بسیار آمرزنده و مهربان است.'),
(423, 3, 130, 'ای کسانی که ایمان آورده‌اید، ربا مخورید که دائم سود بر سرمایه افزایید تا چند برابر شود، و از خدا بترسید، باشد که رستگاری یابید.'),
(424, 3, 131, 'و بپرهیزید از آتش عذابی که برای کافران مهیا ساخته‌اند.'),
(425, 3, 132, 'و از خدا و رسول او فرمان برید، باشد که مشمول لطف و رحمت خدا شوید.'),
(426, 3, 133, 'و بشتابید به سوی مغفرت پروردگار خود و به سوی بهشتی که پهنای آن همه آسمانها و زمین را فرا گرفته و مهیّا برای پرهیزکاران است.'),
(427, 3, 134, 'آنهایی که از مال خود در حال وسعت و تنگدستی انفاق کنند و خشم خود فرونشانند و از (بدیِ) مردم درگذرند، و خدا دوستدار نکوکاران است.'),
(428, 3, 135, 'و آنان که اگر کار ناشایسته کنند و یا ظلمی به نفس خویش نمایند خدا را به یاد آرند و از گناه خود (به درگاه خدا) توبه کنند-و کیست جز خدا که گناه خلق را بیامرزد؟-و آنها که اصرار در کار زشت نکنند چون به زشتی معصیت آگاهند.'),
(429, 3, 136, 'آنهایند که پاداش عملشان آمرزش پروردگار است و باغهایی که زیر درختان آنها نهرها جاری است، جاویدان در آن بهشت‌ها متنعّم هستند، چه نیکو است پاداش نیکوکاران!'),
(430, 3, 137, 'پیش از شما مللی بودند و رفتند، پس در اطراف زمین گردش کنید تا ببینید چگونه تکذیب‌کنندگانِ (وعده‌های خدا) هلاک شدند.'),
(431, 3, 138, 'این آیات حجت و بیانی است برای (عموم) مردم، و راهنما و پندی برای پرهیزکاران.'),
(432, 3, 139, 'شما هرگز سستی نکنید و (از فوت متاع دنیا) اندوهناک نشوید، زیرا شما فیروزمندترین و بلندترین ملل دنیایید اگر در ایمان ثابت قدم باشید.'),
(433, 3, 140, 'اگر (در اُحد) به شما آسیبی رسید، به دشمنان شما نیز (در بدر) آسیب رسید (پس مقاومت کنید). این روزگار را با (اختلاف احوال) میان خلایق می‌گردانیم تا خداوند مقام اهل ایمان را معلوم کند و از شما مؤمنان (آن را که ثابت است) گواهِ دیگران گیرد. و خدا ستمکاران را دوست ندارد.'),
(434, 3, 141, 'و تا آنکه اهل ایمان را (از هر عیب) پاک کند و کافران را نابود گرداند.'),
(435, 3, 142, 'آیا گمان می‌کنید به بهشت داخل خواهید شد حال آنکه هنوز خدا از شما آنان را که (در راه دین) جهاد کرده و (در سختی‌ها) مقاومت کنند معلوم نگردانیده است؟!'),
(436, 3, 143, 'شما همانید که آرزوی کشته شدن (در راه دین) می‌کردید، پیش از آنکه با آن روبرو شوید، پس چگونه امروز که به آن مأمور شدید از مرگ نگران می‌شوید؟!'),
(437, 3, 144, 'محمد (صلّی اللّه علیه و آله و سلّم) جز یک پیامبر نیست که پیش از او نیز پیغمبرانی بودند و درگذشتند، آیا اگر او به مرگ یا شهادت درگذشت شما باز به دین جاهلیّت خود رجوع خواهید کرد؟ پس هر که مرتد شود به خدا ضرری نخواهد رسانید، و البته خدا جزای نیک اعمال به شکرگزاران عطا کند.'),
(438, 3, 145, 'و هیچ کس جز به فرمان خدا نخواهد مُرد، که اجل هر کس (در لوح قضای الهی) به وقت معین ثبت است، و هر که برای متاع دنیا کوشش کند از دنیا بهره‌مندش کنیم، و هر که ثواب آخرت خواهد از نعمت آخرت برخوردارش گردانیم، و البته سپاسگزاران را جزای نیک دهیم.'),
(439, 3, 146, 'چه بسیار رخ داده که پیغمبری جمعیت بسیار (از پیروانش) همراه او جنگیدند و با این حال، اهل ایمان با سختی‌هایی که در راه خدا به آنها رسید هرگز بیمناک و زبون نشدند و سر زیر بار دشمن فرو نیاوردند و (صبر و ثبات پیش گرفتند، که) خدا صابران را دوست می‌دارد.'),
(440, 3, 147, 'و آنها جز این نمی‌گفتند که پروردگارا، از گناه و ستمی که ما درباره خود کرده‌ایم درگذر و ما را ثابت قدم بدار و بر (محو) گروه کافران مظفّر و منصور گردان.'),
(441, 3, 148, 'پس خدا پاداش (فتح و فیروزی) در دنیا، و ثواب نیکو در آخرت نصیبشان گردانید، که خدا نیکوکاران را دوست می‌دارد.'),
(442, 3, 149, 'ای اهل ایمان، اگر پیروی کافران کنید شما را (از دین اسلام) باز به کفر برمی‌گردانند، آن‌گاه شما هم از زیانکاران خواهید گشت.'),
(443, 3, 150, '(ای مسلمانان، کافران یار شما نیستند) بلکه خدا یار شماست و او بهترین یاری‌کنندگان است.'),
(444, 3, 151, 'به زودی در دل کافران بیم و هراس افکنیم، زیرا که آنان برای خدا چیزی را شریک قرار دادند که خدا اصلاً بر آن دلیلی فرو نفرستاده است. و منزلگاه آنان آتش است و سرای ستمکاران بد منزلگاهی است.'),
(445, 3, 152, 'و به حقیقت، خداوند صدق وعده خود را به شما نمود آن‌گاه که به فرمان خدا کافران را به خاک هلاک می‌افکندید تا وقتی که در کار جنگ (احد) سستی کرده و اختلاف برانگیختید و نافرمانی (حکم پیغمبر) نمودید پس از آنکه خدا آنچه آرزوی شما بود به شما نمود، منتها برخی جهت دنیا و برخی جهت آخرت می‌کوشیدید، سپس شما را از پیشرفت بازداشت تا شما را بیازماید، و خدا از تقصیر شما درگذشت، که خدا را با اهل ایمان عنایت و رحمت است.'),
(446, 3, 153, '(به یاد آرید) هنگامی که به مکانهای دوردست می‌گریختید و توجه به احدی نداشتید در حالی که پیغمبر شما را به یاری دیگران در صف کارزار می‌خواند! پس خداوند (به کیفر این بی‌ثباتی) غمی بر غم شما افزود تا از این پس بر آنچه از دست رفت یا برای رنج و المی که به شما می‌رسد اندوهناک نشوید، و خدا به هر چه کنید آگاه است.'),
(447, 3, 154, 'پس از آن غم، خدا شما را ایمنی بخشید که خواب آسایش، گروهی از شما را فرا گرفت و گروهی هنوز در غم جان خود بودند و از روی نادانی به خدا گمان ناحق می‌بردند (از روی انکار) می‌گفتند: آیا ممکن است ما را قدرتی به دست آید؟ بگو: تنها خداست که بر عالم هستی فرمانرواست. (منافقان از ترس مؤمنان) خیالات باطل خود را که در دل می‌پرورند با تو اظهار نمی‌دارند، (با خود) می‌گویند: اگر ما را قدرت و پیروزی بود (شکست نمی‌خوردیم) و در اینجا کشته نمی‌شدیم. بگو: اگر در خانه‌های خود هم بودید باز کسانی که سرنوشت آنان (در قضای الهی) کشته شدن است (از خانه‌ها) به پای خود به قتلگاه بیرون می‌آمدند، تا خدا آنچه در سینه دارید بیازماید و آنچه در دل دارید پاک و خالص گرداند، و خدا از راز درون سینه‌ها آگاه است.'),
(448, 3, 155, 'کسانی از شما که در روز برخورد دو گروه (مسلمان و کافر در جنگ احد) پشت کردند و فراری شدند شیطان آنها را به سبب نافرمانی و بدکرداریشان به لغزش افکند، و همانا خدا از آنها درگذشت، که خدا بسیار آمرزنده و مهربان است.'),
(449, 3, 156, 'ای گرویدگان (به دین اسلام)، شما مانند کسانی نباشید که راه کفر (و نفاق) پیمودند و درباره برادران و خویشان خود که به سفر رفته و یا به جنگ حاضر شدند (و مردند یا به شهادت رسیدند) گفتند: اگر نزد ما می‌ماندند به چنگ مرگ نمی‌افتادند و کشته نمی‌شدند. این آرزوهای باطل را خدا حسرت دلهای آنان خواهد کرد، و خداست که زنده می‌گرداند و می‌میراند و خدا به هر چه کنید آگاه است.'),
(450, 3, 157, 'اگر در راه خدا کشته شده یا بمیرید (در آن جهان به آمرزش و رحمت خدا نائل شوید، که) همانا آمرزش و رحمت خدا بهتر از هر چیز است که (در حیات دنیا برای خود) فراهم آورند.'),
(451, 3, 158, 'و اگر (در راه خدا) بمیرید یا کشته شوید (به رحمت ایزدی در پیوسته و) به سوی خدا محشور خواهید شد.'),
(452, 3, 159, 'به مرحمت خدا بود که با خلق مهربان گشتی و اگر تندخو و سخت‌دل بودی مردم از گِرد تو متفرق می‌شدند، پس از (بدیِ) آنان درگذر و برای آنها طلب آمرزش کن و (برای دلجویی آنها) در کارِ (جنگ) با آنها مشورت نما، لیکن آنچه تصمیم گرفتی با توکل به خدا انجام ده، که خدا آنان را که بر او اعتماد کنند دوست دارد.'),
(453, 3, 160, 'اگر خدا شما را یاری کند محال است کسی بر شما غالب آید، و اگر به خواری واگذارد آن کیست که بتواند بعد از آن شما را یاری کند؟ و اهل ایمان تنها به خدا باید اعتماد کنند.'),
(454, 3, 161, 'و هیچ پیغمبری خیانت نخواهد کرد، و هر کس خیانت کند روز قیامت با همان خیانت بیاید (و به کیفر آن برسد) و هر کس هر عمل به جا آرد به جزای کامل آن می‌رسد و به هیچ کس ستمی نخواهد شد.'),
(455, 3, 162, 'آیا کسی که (به ایمان) در راه رضای خدا قدم بردارد مانند کسی است که (به کفر) راه غضب خدا پیماید؟ که منزلگاه او جهنم است و بسیار بد جایگاهی است.'),
(456, 3, 163, 'این دو گروه را (به مراتب ایمان و کفر) نزد خدا درجات (مختلفه) است و خدا به هر چه می‌کنند بیناست.'),
(457, 3, 164, 'خدا بر اهل ایمان منّت گذاشت که رسولی از خودشان در میان آنان برانگیخت که بر آنها آیات او را تلاوت می‌کند و آنان را پاک می‌گرداند و به آنها علم کتاب (احکام شریعت) و حقایق حکمت می‌آموزد، و همانا پیش از آن در گمراهی آشکار بودند.'),
(458, 3, 165, 'آیا هر گاه به شما آسیبی رسد در صورتی که دو برابر آن آسیب را به دشمنان رساندید (باز از روی تعجب) گویید: این از کجاست؟ بگو: این مصیبت از دست خود کشیدید، همانا خدا بر هر چیز تواناست.'),
(459, 3, 166, 'و آنچه در روز (اُحد هنگام) مقابله دو صف کارزار به شما رسید به قضا و مشیّت نافذ خدا بود (تا آنکه بیازماید اهل ایمان را) و تا معلوم کند چه کسانی ثابت‌قدم در ایمانند.'),
(460, 3, 167, 'و تا معلوم کند حالِ آنان که نفاق و دورویی کردند و (چون) به آنها گفته شد بیایید در راه خدا جهاد و یا دفاع کنید (عذر آوردند و) گفتند که اگر ما به فنون جنگی دانا بودیم از شما تبعیت نموده و به کارزار می‌آمدیم. اینان به کفر نزدیکترند تا به ایمان؛ به زبان چیزی اظهار کنند که در دل خلاف آن پنهان داشته‌اند، و خدا بر آنچه پنهان می‌دارند آگاه‌تر از خود آنهاست.'),
(461, 3, 168, 'آن کسانی که در جنگ با سپاه اسلام همراهی نکرده و درباره برادران خود گفتند: اگر آنان نیز سخنان ما را شنیده بودند (و به جنگ احد نرفته بودند) کشته نمی‌شدند، بگو: (شما که برای حفظ حیات دیگران چاره توانید کرد) مرگ را از خود دور کنید اگر راست می‌گویید.'),
(462, 3, 169, 'البته نپندارید که شهیدان راه خدا مرده‌اند، بلکه زنده‌اند (به حیات ابدی و) در نزد خدا متنعّم خواهند بود.'),
(463, 3, 170, 'آنان به فضل و رحمتی که خدا نصیبشان گردانیده شادمانند، و دلشادند به حال آن مؤمنان که هنوز به آنها نپیوسته‌اند و بعداً در پی آنها به سرای آخرت خواهند شتافت که بیمی بر آنان نیست و غمی نخواهند داشت.'),
(464, 3, 171, 'دلشادند به نعمت و فضل خدا و این که خداوند اجر اهل ایمان را هرگز ضایع نگذارد.'),
(465, 3, 172, 'آنان که دعوت خدا و رسول را اجابت کنند پس از آنکه به آنها رنج و الم رسید، از آنها هر کس نیکوکار و پرهیزکار شد اجر عظیم خواهد یافت.'),
(466, 3, 173, 'آن مؤمنانی که چون مردم (منافق) به آنها گفتند: لشکری بسیار بر علیه شما مؤمنان فراهم شده، از آنان در اندیشه و بر حذر باشید، (این تبلیغات) بر ایمانشان بیفزود و گفتند: تنها خدا ما را کفایت کند و او نیکو وکیل و کارسازی است.'),
(467, 3, 174, 'آن گروه مؤمنان با نعمت و فضل خدا در حالی بازگشتند که بر آنان هیچ الم و رنجی پیش نیامد و پیرو رضای خدا شدند، و خدا صاحب فضل و رحمتی بزرگ است.'),
(468, 3, 175, 'تنها این شیطان است که می‌ترساند دوستداران خود را، شما مسلمانان از آنان بیم و اندیشه مکنید و از من بترسید اگر اهل ایمانید.'),
(469, 3, 176, '(ای پیغمبر) تو اندوهناک مباش که گروهی به راه کفر می‌شتابند، آنها هرگز زیانی به خدا نرسانند و خدا می‌خواهد آنها را در آخرت هیچ نصیبی ندهد، و نصیبشان عذاب بزرگ و سخت دوزخ خواهد بود.'),
(470, 3, 177, 'آنان که خریدار کفر شدند به عوض ایمان، هرگز زیانی به خدا نمی‌رسانند، و آنان را عذابی دردناک است.'),
(471, 3, 178, 'آنان که به راه کفر رفتند گمان نکنند که مهلتی که ما به آنها می‌دهیم به حال آنان بهتر خواهد بود، بلکه آنها را (برای امتحان) مهلت می‌دهیم تا بر گناه و سرکشی خود بیفزایند، و آنان را عذابی خوارکننده است.'),
(472, 3, 179, 'خداوند هرگز مؤمنان را بدین حال کنونی که برآنید وانگذارد تا آنکه (با آزمایش خود) بدسرشت را از پاک گوهر جدا کند. و خدا همه شما را از سرّ غیب آگاه نسازد و لیکن (به این مقام) از پیغمبران خود هر که را مشیّت او تعلق گرفت برگزیند، پس شما به خدا و پیغمبرانش بگروید، که هر گاه ایمان آرید و پرهیزکار شوید اجر عظیم خواهید یافت.'),
(473, 3, 180, 'آنان که بخل نموده و از مالی که خدا از فضل خویش به آنها داده حقوق فقیران را ادا نمی‌کنند گمان نکنند این بخل به نفع آنها خواهد بود بلکه به ضرر آنهاست، چه آنکه مالی که در آن بخل ورزند در قیامت زنجیر گردن آنها شود، و تنها خدا وارث آسمانها و زمین است و خدا به کردار شما آگاه است.'),
(474, 3, 181, 'هر آینه خدا شنید سخن (جاهلانه) آن کسان را که گفتند: خدا فقیر است و ما دارا. البته ما گفتارشان را ثبت خواهیم کرد با این گناه بزرگشان که انبیاء را به ناحق کشتند، و گوییم: بچشید عذاب آتش سوزان را.'),
(475, 3, 182, 'این عذاب به خاطر کارهایی است که به دست خود پیش فرستادید، و خدا هرگز در حق بندگان ستم نخواهد کرد.'),
(476, 3, 183, 'آنان که گفتند: خدا از ما پیمان گرفته که به هیچ پیغمبری ایمان نیاوریم تا آنکه او قربانیی آورد که در آتش بسوزد. بگو که پیش از من رسولانی آمده و برای شما هر گونه معجزه آورده و این را هم که خواستید آوردند، پس اگر راست می‌گویید چرا آن پیامبران را کشتید؟!'),
(477, 3, 184, 'پس اگر تو را تکذیب کردند (غمگین مباش که) پیغمبران پیش از تو هم که معجزات و زبورها و کتاب آسمانی روشن آوردند نیز تکذیب کردند.'),
(478, 3, 185, 'هر نفسی شربت مرگ را خواهد چشید و محققاً روز قیامت همه شما به مزد اعمال خود کاملاً خواهید رسید، پس هر کس را از آتش جهنم دور دارند و به بهشت ابدی درآرند چنین کس سعادت ابد یافت، که زندگی دنیا جز متاعی فریبنده نیست.'),
(479, 3, 186, 'محققاً شما را در مال و جان آزمایش خواهند کرد و از آنها که پیش از شما کتاب آسمانی به آنها داده شد و از مشرکان زخم زبان بسیار خواهید شنید، و اگر صبر پیشه کرده و پرهیزکار شوید (البته ظفر یابید) که ثبات و تقوا سبب قوت اراده در کارهاست.'),
(480, 3, 187, 'و چون خدا از آنان که کتاب به آنها داده شد پیمان گرفت که حقایق کتاب آسمانی را برای مردم بیان کنید و کتمان مکنید، پس آنها عهد خدا را پشت سر انداخته و آیات الهی را به بهایی اندک فروختند، پس بد معامله‌ای کردند.'),
(481, 3, 188, '(ای پیغمبر) مپندار آنهایی که به کردار زشت خود شادمانند و دوست دارند که مردم به اوصاف پسندیده‌ای که هیچ در آنها نیست آنها را ستایش کنند، البته گمان مدار که از عذاب خدا رهایی دارند، که آنها را عذابی دردناک خواهد بود.'),
(482, 3, 189, 'خداست مالک آسمانها و زمین، و خدا بر هر چیز تواناست.'),
(483, 3, 190, 'محققا در خلقت آسمانها و زمین و رفت و آمد شب و روز دلائل روشنی است برای خردمندان عالم.'),
(484, 3, 191, 'آنها که در هر حالت، ایستاده و نشسته و خفته خدا را یاد کنند و دائم در خلقت آسمان و زمین بیندیشند و گویند: پروردگارا، تو این دستگاه با عظمت را بیهوده نیافریده‌ای، پاک و منزهی، ما را به لطف خود از عذاب دوزخ نگاهدار؛'),
(485, 3, 192, 'ای پروردگار ما، هر که را در آتش افکنی او را سخت خوار کرده‌ای، و ستمکاران هیچ یاوری ندارند؛'),
(486, 3, 193, 'پروردگارا، ما صدای منادیی که خلق را به ایمان فرا می‌خواند که به پروردگارتان ایمان آورید، شنیدیم و ایمان آوردیم، پروردگارا، از گناهان ما درگذر و زشتی کردار ما بپوشان و هنگام جان سپردن ما را با نیکان و صالحان محشور گردان؛'),
(487, 3, 194, 'پروردگارا، ما را از آنچه به زبان رسولان خود وعده دادی نصیب فرما و ما را در روز قیامت محروم مگردان، که تو هرگز در وعده تخلّف نمی‌ورزی.'),
(488, 3, 195, 'پس خدا دعاهای ایشان را اجابت کرد که البته من (که پروردگارم) عمل هیچ کس از مرد و زن را بی‌مزد نگذارم، زیرا شما از یکدیگرید (و همه در نظر من یکسانید)، پس آنان که از وطن خود هجرت نمودند و از دیار خویش بیرون رانده شدند و در راه من رنج کشیدند و جهاد کرده و کشته شدند همانا بدیهای آنان را بپوشانم و آنها را به بهشتهایی درآورم که زیر درختانش نهرها جاری است. این پاداشی است از جانب خدا، و نزد خدا است پاداش نیکو.'),
(489, 3, 196, '(دنیا) تو را نفریبد چون بینی که کافران در شهرها به راحتی می‌گردند.'),
(490, 3, 197, 'این متاعی اندک است و پس از این جهان منزلگاه آنان جهنم است و بد آرامگاهی است.'),
(491, 3, 198, 'لیکن آنان که خداترس و با تقوایند منزلگاهشان بهشت‌هایی است که در زیر درختانش نهرها جاری است و در آن جاودانند، در حالتی که خدا بر آنها خوان احسان خود گسترده، و آنچه نزد خداست برای نیکان بهتر است.'),
(492, 3, 199, 'همانا برخی از اهل کتاب کسانی‌اند که به خدا و کتاب آسمانی شما و کتاب آسمانی خودشان ایمان آورند در حالی که مطیع فرمان خدا بوده و آیات خدا را به بهای اندک نفروشند، آن طایفه را نزد خدا پاداش نیکوست، و البته خدا حساب خلق را سریع و آسان خواهد رسید.'),
(493, 3, 200, 'ای اهل ایمان، (در کار دین) صبور باشید و یکدیگر را به صبر و مقاومت سفارش کنید و مهیّا و مراقب کار دشمن بوده و خداترس باشید، باشد که فیروز و رستگار گردید.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم، بترسید از پروردگار خود، آن خدایی که همه شما را از یک تن بیافرید و هم از آن، جفت او را خلق کرد و از آن دو تن خلقی بسیار در اطراف عالم از مرد و زن پراکند، و بترسید از آن خدایی که به نام او از یکدیگر مسئلت و درخواست می‌کنید و درباره ارحام کوتاهی نکنید، که خدا مراقب اعمال شماست.'),
(495, 4, 2, 'و اموال یتیمان را پس از بلوغ به دست آنها دهید، و مال بد و نامرغوب خود را به مرغوب (آنها) تبدیل نکنید و اموال آنان را به ضمیمه اموال خود مخورید، که این گناهی بس بزرگ است.'),
(496, 4, 3, 'و اگر بترسید که مبادا درباره یتیمان (ازدواج با دختران یتیم) مراعات عدل نکنید پس آن کس از زنان را به نکاح خود درآرید که شما را نیکو (و مناسب با عدالت) است: دو یا سه یا چهار (نه بیشتر) و اگر بترسید که (چون زنان متعدّد گیرید) راه عدالت نپیموده و به آنها ستم می‌کنید پس تنها یک زن اختیار کنید و یا چنانچه کنیزی دارید به آن اکتفا کنید، که این نزدیکتر به عدالت و ترک ستمکاری است.'),
(497, 4, 4, 'و مهر زنان را در کمال رضایت به آنها بپردازید، پس اگر چیزی از مهر خود را از روی رضا و خشنودی به شما بخشیدند، از آن برخوردار شوید که شما را حلال و گوارا خواهد بود.'),
(498, 4, 5, 'و اموالی که خدا قوام زندگی شما را به آن مقرّر داشته به تصرّف سفیهان ندهید و از مالشان نفقه و لباس به آنها دهید و با آنان سخن نیکو و دلپسند گویید.'),
(499, 4, 6, 'یتیمان را آزمایش کنید تا هنگامی که بالغ شده و قدرت بر نکاح پیدا کنند آن‌گاه اگر آنها را دانا به درک مصالح زندگی خود یافتید اموالشان را به آنها باز دهید، و به اسراف و عجله مال آنها را حیف و میل مکنید بدین اندیشه که مبادا بزرگ شوند (و اموالشان را از شما بگیرند). و هر کس (از اولیاء یتیم) داراست (از تصرف در مال او) خودداری کند، و هر که فقیر است (در مقابل نگهبانی او از مال یتیم) به قدر متعارف ارتزاق کند، پس آن‌گاه که مالشان را به آنها رد کردید باید بر ردّ مال بر آنها گواه گیرید، و گواهی خدا برای محاسبه خلق کافی است.'),
(500, 4, 7, 'برای فرزندان پسر، سهمی از تَرَکه ابوین و خویشان است و برای فرزندان دختر نیز سهمی از ترکه ابوین و خویشان است، چه مال اندک باشد یا بسیار، نصیب هر کس از آن معین گردیده.'),
(501, 4, 8, 'و چون در تقسیم (ترکه میّت، اشخاصی از) خویشان و یتیمان و فقیران حاضر آیند، به چیزی از آن مال آنها را روزی دهید و با آنان سخن نیکو و دلپسند گویید.'),
(502, 4, 9, 'و باید بترسند (از مکافات عمل خود) کسانی که اگر کودکان ناتوان از خود باقی می‌گذارند بر آنان بیم دارند، پس باید از خدا بترسند و (در حق ایتام مردم) سخن به اصلاح و درستی گویند.'),
(503, 4, 10, 'آنان که اموال یتیمان را به ستمگری می‌خورند، در حقیقت آنها در شکم خود آتش جهنّم فرو می‌برند و به زودی در آتش فروزان خواهند افتاد.'),
(504, 4, 11, 'حکم خدا در حق فرزندان شما این است که پسر دو برابر دختر ارث برد، پس اگر همه دختر و بیش از دو تن باشند فرض همه دو ثلث [فرض‌ها یعنی سهمیه‌های معیّن ارث، شش نوع است: نصف، رُبع (یک چهارم)، ثُمن (یک هشتم)، سُدس (یک ششم)، ثلث (یک سوم)، ثُلُثان (دو سوم).] ترکه است، و اگر یک دختر باشد نصف، و فرض هر یک از پدر و مادر یک سُدس تَرَکه است در صورتی که میّت را فرزند باشد و اگر فرزند نباشد و وارث منحصر به پدر و مادر بود، در این صورت مادر یک ثلث می‌برد (باقی را پدر)، و اگر میّت را برادرانی باشد در این فرض مادر یک سُدس خواهد برد، پس از آنکه حقّ وصیّت و بدهی که به مال میّت تعلق گرفته جدا شود. شما این را که پدران و فرزندان کدام یک به خیر و صلاح (و به ارث بردن) به شما نزدیکترند نمی‌دانید. (این احکام) فریضه‌ای است که خدا معیّن فرموده، که خدا به هر چیز دانا و آگاه است.'),
(505, 4, 12, 'سهم ارث شما مردان از تَرَکه زنانتان نصف است در صورتی که آنها را فرزند نباشد و اگر فرزند باشد برای شما ربع خواهد بود، پس از خارج کردن حق وصیت و بدهی که به دارایی آنها تعلق گرفته است. و سهم ارث زنان ربع ترکه شما مردان است اگر دارای فرزند نباشید، و چنانچه فرزند داشته باشید هشت یک خواهد بود از ترکه شما، پس از اداء حقّ وصیّت و بدهی شما. و اگر مردی یا زنی بمیرد که وارثش کلاله او باشند (یعنی پدر و مادر و فرزند نداشته باشد) و یک برادر و یا خواهر (اُمّی) داشته باشد در این فرض سهم ارث هر یک نفر از آنها یک سُدس خواهد بود و اگر بیش از یک نفر باشد همه آنها ثلث ترکه را به اشتراک ارث برند، بعد از خارج کردن بدهی و حقّ وصیّت میّت در صورتی که وصیّت یا بدهی زیان‌آور نباشد. این حکمی است که خدا سفارش فرموده، و خدا (به همه احوال بندگان) دانا و (به هر چه ناروا کنند) بردبار است.'),
(506, 4, 13, 'اینها اوامر و احکام خداست، و هر کس پیرو امر خدا و رسول اوست او را به بهشتهایی درآورد که در زیر درختانش نهرها جاری است و آنجا منزل ابدی مطیعان خواهد بود، این است سعادت و فیروزی عظیم.'),
(507, 4, 14, 'و هر که نافرمانی خدا و رسول او کند و تجاوز از حدود و احکام الهی نماید او را به آتش درافکند که همیشه در آن (معذّب) است و همواره در عذاب خواری خواهد بود.'),
(508, 4, 15, 'زنانی از (جامعه) شما که عمل ناشایسته کنند چهار شاهد مسلمان بر آنها بخواهید، چنانچه شهادت دادند آن زنان را در خانه نگه دارید تا عمرشان به پایان رسد یا خدا برای آنها راهی پدیدار گرداند.'),
(509, 4, 16, 'و هر کس از شما (مسلمانان) عمل ناشایست مرتکب شود، چه زن و چه مرد، آنان را (به سرزنش و توبیخ) بیازارید، چنانچه توبه کردند و به کار شایسته پرداختند متعرض آنها نشوید، که خدا توبه‌پذیر و مهربان است.'),
(510, 4, 17, 'محققا خدا توبه آنهایی را می‌پذیرد که عمل ناشایسته را از روی نادانی مرتکب شوند و پس از آن به زودی توبه کنند، البته خدا آنها را می‌بخشد و خدا (به مصالح خلق) دانا و آگاه است.'),
(511, 4, 18, 'و کسانی که (تمام عمر) به اعمال زشت اشتغال ورزند تا آن‌گاه که یکیشان مشاهده مرگ کند در آن ساعت پشیمان شود و گوید: اکنون توبه کردم، توبه چنین کسی پذیرفته نخواهد شد، و آنان که به حال کفر بمیرند (توبه آنها نیز قبول نشود)، بر اینان عذابی دردناک مهیا ساختیم.'),
(512, 4, 19, 'ای اهل ایمان، برای شما حلال نیست که زنان را به اکراه و جبر به میراث گیرید (مانند جاهلیّت)، و بر آنان سخت‌گیری و بهانه‌جویی مکنید که قسمتی از آنچه مهر آنها کرده‌اید به جور بگیرید، مگر آنکه عمل زشتی از آنها آشکار شود. و در زندگانی به آنها به انصاف رفتار نمایید، و چنانچه دلپسند شما نباشند (اظهار کراهت مکنید) چه بسا چیزها ناپسند شماست و حال آنکه خدا در آن خیر بسیار مقدّر فرماید.'),
(513, 4, 20, 'و اگر خواستید زنی را رها کرده و زنی دیگر اختیار نمایید و مال بسیار مهر او کرده‌اید نباید چیزی از مهر او بازگیرید، آیا به وسیله تهمت زدن به زن و گناهی آشکار مهر او را می‌گیرید؟!'),
(514, 4, 21, 'و چگونه مهر آنان را خواهید گرفت در حالی که هر کدام به حقّ خود رسیده‌اید و آن زنان (مَهر را در مقابل عقد زوجیّت و) عهد محکم از شما گرفته‌اند.'),
(515, 4, 22, 'و نکاح مکنید زن پدر را (بعد از این حکم) الاّ آنچه از پیش کرده‌اید، زیرا این کاری است زشت و مبغوض (خدا) و بد راه و رسمی است.'),
(516, 4, 23, 'حرام شد بر شما (ازدواج با) مادران و دختران و خواهران و عمّه‌ها و خاله‌ها و دختران برادر و دختران خواهر و مادران رضاعی و خواهران رضاعی و مادران زن و دختران زن که در دامن شما تربیت شده‌اند اگر با آن زن مباشرت کرده باشید، و اگر دخول با آن زن نکرده (طلاق دهید) باکی نیست (که با دختر او ازدواج کنید). و نیز حرام شد زن فرزندان صلبی و نیز حرام شد جمع میان دو خواهر مگر آنچه پیش از نزول این حکم کرده‌اید (که خدا از آن درگذشت) که خدا (در حق بندگان) بخشنده و مهربان است.'),
(517, 4, 24, 'و نکاح زنان شوهردار نیز (برای شما حرام شد) مگر آن زنانی که (در جنگهای با کفّار، به حکم خدا) متصرّف شده‌اید. این حکم خدا بر شماست، و هر زنی غیر آنچه ذکر شد شما را حلال است که به مال خود به طریق زناشویی بگیرید نه آنکه زنا کنید، پس چنانچه از آنها بهره‌مند شوید آن مهر معیّن که حق آنهاست به آنان بپردازید، و باکی نیست بر شما که بعد از تعیین مهر هم به چیزی با هم تراضی کنید (و بدانید که) البته خدا دانا و آگاه است.'),
(518, 4, 25, 'و هر که را توانایی آن نباشد که زنان پارسای با ایمان (و آزاد) گیرد پس، از کنیزان مؤمنه که مالک آن شدید به زنی اختیار کند. خدا آگاه‌تر است به (مراتب) ایمان شما که شما اهل ایمان بعضی از جنس بعضی دیگرید (و همه مؤمن و در رتبه یکسانید)، پس با کنیزان مؤمنه با اذن مالکشان ازدواج کنید و مهر آنها را به طرز پسندیده بدهید، کنیزانی که عفیف باشند نه زناکار و رفیق و دوست‌باز، پس چون شوهر کردند چنانچه عمل زشتی از آنها سر زند بر آنها نصف عذاب (یعنی حدّ) زنان پارسای آزاد است. این حکم درباره کسی است که بترسد مبادا به رنج افتد، و صبر کردن برای شما بهتر است، و خدا بخشنده و مهربان است.'),
(519, 4, 26, 'خدا می‌خواهد (راه سعادت را) برای شما بیان کند و شما را به آداب (ستوده) آنان که پیش از شما بودند رهبر گردد و بر شما ببخشاید، که خدا دانا و به حقایق امور آگاه است.'),
(520, 4, 27, 'و خدا می‌خواهد بر شما (به رحمت و مغفرت) بازگشت فرماید، و مردم هوسناکِ پیرو شهوات می‌خواهند که شما (از راه حقّ و رحمت) دور و منحرف گردید.'),
(521, 4, 28, 'خدا می‌خواهد کار بر شما آسان کند، که انسان ضعیف خلق شده است.'),
(522, 4, 29, 'ای اهل ایمان، مال یکدیگر را به ناحق مخورید مگر آنکه تجارتی باشد که از روی رضا و رغبت کرده (و سودی برید)، و یکدیگر را نکشید، که البته خدا به شما بسیار مهربان است.'),
(523, 4, 30, 'و هر کس از روی دشمنی و ستمگری چنین کند، پس او را به زودی در آتش دوزخ درآوریم، و این کار (انتقام کشیدن از ظالمان) برای خدا آسان است.'),
(524, 4, 31, '(ای اهل ایمان) چنانکه از گناهان بزرگی که شما را از آن نهی کرده‌اند دوری گزینید، ما از گناهان دیگر شما درگذریم و شما را به مقامی نیکو برسانیم.'),
(525, 4, 32, 'آرزو (و توقّع بیجا) در فضیلت و مزیّتی که خدا به آن بعضی را بر بعضی برتری داده مکنید؛ که هر یک از مرد و زن از آنچه اکتساب کنند بهره‌مند شوند. و هر چه می‌خواهید از فضل خدا درخواست کنید (نه از خلق) که خدا به همه چیز داناست.'),
(526, 4, 33, 'و ما برای هر چیزی از آنچه پدر و مادر و خویشان به جای گذاشته‌اند وارثانی قرار داده‌ایم، و با هر که عهد و پیمان (حقوقی) بسته‌اید بهره آنان را بدهید (و نقض عهد نکنید) که خدا بر هر چیز گواه است.'),
(527, 4, 34, 'مردان را بر زنان تسلط و حق نگهبانی است به واسطه آن برتری که خدا برای بعضی بر بعضی مقرر داشته و هم به واسطه آنکه مردان از مال خود نفقه دهند، پس زنان شایسته مطیع شوهران و در غیبت آنان حافظ (حقوق آنها) باشند از آن رو که خدا هم (حقوق زنان را) حفظ فرموده است. و زنانی که از نافرمانی آنان (در حقوق همسری) بیمناکید باید نخست آنان را موعظه کنید و (اگر مطیع نشدند) از خوابگاه آنان دوری گزینید و (اگر باز مطیع نشدند) آنان را به زدن تنبیه کنید، چنانچه اطاعت کردند دیگر راهی بر آنها مجویید، که همانا خدا بزرگوار و عظیم الشأن است.'),
(528, 4, 35, 'و چنانچه بیم آن دارید که نزاع سخت بین آنها پدید آید، از طرف کسان مرد و کسان زن داوری برگزینید، که اگر مقصود اصلاح داشته باشند خدا میان ایشان موافقت و سازگاری برقرار کند، که خدا دانا و آگاه است.'),
(529, 4, 36, 'و خدای یکتا را پرستید و هیچ چیزی را شریک وی نگیرید و نسبت به پدر و مادر و خویشان و یتیمان و فقیران و همسایه دور و نزدیک و دوستان موافق و رهگذران و بندگان که زیردست تصرف شمایند نیکی کنید، که خدا مردم متکبّر خودپسند را دوست ندارد.'),
(530, 4, 37, 'همان گروه که بخل می‌ورزند و مردم را به بخل وادار می‌کنند و آنچه را که خدا از فضل خود به آنها بخشیده (از آیات و احکام آسمانی) کتمان می‌کنند. و ما برای کافران عذابی خوارکننده مهیّا داشته‌ایم.'),
(531, 4, 38, 'و هم آنان که اموال خود را به قصد ریا و خودنمایی به مردم می‌بخشند و به خدا و روز قیامت نمی‌گروند. و هر که را شیطان یار است بسیار بد یاری است.'),
(532, 4, 39, 'چه زیانی به آنها می‌رسید اگر به خدا و روز قیامت ایمان آورده و از بهره‌ای که خدا به آنان داده چیزی انفاق می‌کردند؟! و خدا به (احوال و نیّات) آنان دانا است.'),
(533, 4, 40, 'خداوند به مقدار ذرّه‌ای به کسی ستم نکند، و اگر عمل نیکی باشد زیاد گرداند و (به نیکان) جانب خود مزدی بزرگ عطا کند.'),
(534, 4, 41, 'پس چگونه است (حال آنان) آن‌گاه که از هر طایفه‌ای گواهی آریم و تو را بر این امّت به گواهی خواهیم؟'),
(535, 4, 42, 'در آن روز آنان که به راه کفر رفته و نافرمانی رسول کردند آرزو کنند که ای کاش با خاک زمین یکسان بودند، و از خدا سخنی پنهان نتوانند کرد.'),
(536, 4, 43, 'ای اهل ایمان، هرگز در حال مستی به نماز نیایید تا بدانید چه می‌گویید (و چه می‌کنید) و نه در حال جنابت (به مساجد آیید) مگر آنکه رهگذر باشید تا وقتی که غسل کنید. و اگر بیمار بودید یا آنکه در سفر باشید یا قضاء حاجتی دست داده باشد یا با زنان مباشرت کرده‌اید و آب برای تطهیر و غسل نیافتید پس به خاک پاک تیمم کنید، آن‌گاه صورت و دستها را (بدان) مسح کنید، که خدا بخشنده و آمرزنده است.'),
(537, 4, 44, 'آیا ندیدی و ننگریستی به (حال) آنان که اندک بهره‌ای از علم کتاب یافتند که خریدار ضلالت هستند و همی خواهند که شما (اهل ایمان) نیز گمراه شوید؟!'),
(538, 4, 45, 'و خدا داناتر است به دشمنان شما، و همین بس که خدا دوستدار و یاور (شما) باشد.'),
(539, 4, 46, 'گروهی از یهود کلمات (خدا) را از جای خود تغییر داده و گویند: (فرمان خدا را) شنیدیم و (از آن) سرپیچیم و (به جسارت گویند) بشنو که (کاش) ناشنوا باشی؛ و گویند ما را رعایت کن. و گفتار ایشان زبان‌بازی و طعنه و تمسخر به دین است. و اگر (به احترام) می‌گفتند که ما (فرمان حق را) شنیدیم و (تو را) اطاعت کنیم و تو نیز (سخن ما) بشنو و به حال ما بنگر، هر آینه آنان را نیکوتر بود و به صواب نزدیکتر، لیکن چون کافر شدند خدا آنها را لعنت کرد، و به جز اندکی ایمان نخواهند آورد.'),
(540, 4, 47, 'ای کسانی که به شما کتاب آسمانی داده شد، به قرآنی که فرستادیم که مصدّق تورات و انجیل شماست ایمان آورید پیش از آنکه بر چهره‌هایی (از اثر ضلالت) خط خذلان کشیم و آنها را واژگون کنیم یا مانند اصحاب سَبت بر آنها لعنت و عذاب فرستیم، و (بترسید که) قضای خدا واقع‌شدنی است.'),
(541, 4, 48, 'محققاً خدا گناه شرک را نخواهد بخشید و سوای شرک را برای هر که خواهد می‌بخشد، و هر کس که شرک به خدا آورد به دروغی که بافته گناهی بزرگ مرتکب شده است.'),
(542, 4, 49, 'نمی‌بینی آنان را که دعوی پاکدلی کنند؟ خداست که هر که را خواهد از رذائل پاک و منزّه دارد، و به قدر فتیلی (رشته خرما) به کسی ستم نشود.'),
(543, 4, 50, 'بنگر چگونه بر خدا دروغ می‌بافند! و همین (افترا) بس گناهی آشکار است.'),
(544, 4, 51, 'ندیدی آنان که بهره‌ای از کتاب آسمانی داشتند (یعنی جهودان) چگونه به (بتان) جِبت و طاغوت می‌گروند و درباره کافران مشرک می‌گویند که راه آنان به صواب نزدیکتر از طریقه اهل ایمان است؟!'),
(545, 4, 52, 'این گروهند که خدا آنان را لعنت کرد، و هر که را خدا لعن کند دیگر مددکار و یاوری برای او نتوانی یافت.'),
(546, 4, 53, 'مگر آنان که از احسان به خلق به هسته خرمایی بخل می‌ورزند بهره‌ای از مُلک خدا خواهند یافت؟'),
(547, 4, 54, 'بلکه حسد می‌ورزند با مردم (یعنی پیامبر و مسلمین) چون آنها را خدا به فضل خود برخوردار نمود. که البته ما به آل ابراهیم کتاب و حکمت دادیم و به آنها ملک و سلطنتی بزرگ عطا کردیم.'),
(548, 4, 55, 'آن‌گاه برخی بدو گرویدند و برخی مانع ایمان به او شدند؛ پس (آنان را) کیفر آتش افروخته دوزخ بس است.'),
(549, 4, 56, 'آنان که به آیات ما کافر شدند به زودی در آتش دوزخشان درافکنیم که هر چه پوست تن آنها بسوزد آنان را پوست دیگری جایگزین کنیم تا (سختی) عذاب را بچشند، که همانا خدا مقتدر و کارش از روی حکمت است.'),
(550, 4, 57, 'آنان را که ایمان آوردند و نیکوکار شدند به زودی در بهشتی درآوریم که نهرها از زیر درختانش جاری است و در آن زندگانی جاوید کنند و برای آنها در بهشت جفتهای پاکیزه است و آنان را به سایه رحمت ابدی خود درآوریم.'),
(551, 4, 58, 'همانا خدا به شما امر می‌کند که امانتها را به صاحبانش باز دهید و چون حاکم بین مردم شوید به عدالت داوری کنید. همانا خدا شما را پند نیکو می‌دهد، که خدا شنوا و بیناست.'),
(552, 4, 59, 'ای اهل ایمان، فرمان خدا و رسول و فرمانداران (از طرف خدا و رسول) که از خود شما هستند اطاعت کنید، پس اگر در چیزی کار به نزاع کشد آن را به حکم خدا و رسول بازگردانید اگر به خدا و روز قیامت ایمان دارید. این کار برای شما بهتر و خوش‌عاقبت‌تر خواهد بود.'),
(553, 4, 60, 'آیا ننگری به حال آنان که به گمان (و دعوی) خود ایمان به قرآن تو و کتبی که پیش از تو فرستاده شده آورده‌اند، چگونه باز می‌خواهند داوری به طاغوت برند در صورتی که مأمورند که به طاغوت کافر شوند؟! و شیطان خواهد که آنان را گمراه کند گمراهی دور (از هر سعادت).'),
(554, 4, 61, 'و چون به آنها گفته شود که به حکم خدا و رسول بازآیید، گروه منافق را بینی که مردم را از گرویدن به تو منع می‌کنند.'),
(555, 4, 62, 'پس چگونه خواهد بود (حال آنها) چون مصیبتی از کردار زشت خود به آنان رسد؟ آن‌گاه نزد تو آیند و به خدا سوگند خورند که ما (در کار خود) قصدی جز نیکویی و موافقت نداشتیم!'),
(556, 4, 63, 'اینانند که خدا از قلب آنها آگاه است، تو (ای رسول) از آنان روی بگردان و آنها را نصیحت کن و به گفتار دلنشین و مؤثر با ایشان سخن بگو.'),
(557, 4, 64, 'ما رسولی نفرستادیم مگر بر این مقصود که خلق به امر خدا اطاعت او کنند. و اگر هنگامی که آنان (گروه منافق) بر خود ستم کردند به تو رجوع می‌کردند و از کردار خود به خدا توبه نموده و تو هم برای آنها استغفار می‌کردی و از خدا آمرزش می‌خواستی، در این حال البته خدا را توبه‌پذیر و مهربان می‌یافتند.'),
(558, 4, 65, 'نه چنین است، قسم به خدای تو که اینان (به حقیقت) اهل ایمان نمی‌شوند مگر آنکه در خصومت و نزاعشان تنها تو را حَکَم کنند و آن‌گاه به هر حکمی که کنی اعتراض نداشته، کاملاً (از دل و جان) تسلیم (فرمان تو) باشند.'),
(559, 4, 66, 'و اگر ما به آنها حکم می‌کردیم که خود (یعنی یکدیگر) را بکشید یا از دیار خود (برای جهاد) بیرون روید به جز اندکی اطاعت امر نمی‌کردند، و اگر به آنچه پندشان می‌دهند عمل می‌کردند البته نیکوتر و کاری محکم و با اساس‌تر برای آنها بود.'),
(560, 4, 67, 'و در آن صورت محققا ما آنها را از نزد خود مزدی بزرگ (به پاداش طاعت) عطا می‌کردیم.'),
(561, 4, 68, 'و بی‌شک آنان را به راه راست هدایت می‌نمودیم.'),
(562, 4, 69, 'و آنان که اطاعت خدا و رسول کنند البته با کسانی که خدا به آن‌ها لطف فرموده یعنی با پیغمبران و صدّیقان و شهیدان و نیکوکاران محشور خواهند شد، و اینان نیکو رفیقانی هستند.'),
(563, 4, 70, 'این فضل و بخشش از سوی خداست، و علم (ازلی) خدا کفایت کند.'),
(564, 4, 71, 'ای اهل ایمان سلاح جنگ برگیرید و آن‌گاه دسته دسته یا همه یک‌باره متّفق (برای جهاد) بیرون روید.'),
(565, 4, 72, 'و همانا گروهی از شما (در کار جنگ) کندی و درنگ می‌کنند، اگر برای شما حادثه ناگواری پیش آید (به شماتت) گویند: خدا ما را مورد لطف خود قرار داد که با آنان حاضر نشدیم.'),
(566, 4, 73, 'و اگر فضل خدا شامل حال شما گردد آن‌چنان که گویی میان شما و آنان ابداً دوستی نبوده (با تأسف) گویند: ای کاش ما نیز با آنان به جهاد رفته بودیم تا (از غنیمت) نصیب فراوان می‌بردیم.'),
(567, 4, 74, 'پس کسانی باید در راه خدا جهاد کنند که (دست از جان شسته‌اند و) زندگی این جهان را به آن جهان می‌فروشند. و هر کس در راه خدا جهاد کند و کشته شود یا فاتح گردد، زود باشد که او را اجری عظیم دهیم.'),
(568, 4, 75, 'چرا در راه خدا جهاد نمی‌کنید و در راه (آزادی) جمعی ناتوان از مرد و زن و کودک که (در مکه اسیر ظلم کفارند و) دائم می‌گویند: بار خدایا، ما را از این شهری که مردمش ستمکارند بیرون آر و از جانب خود برای ما (بیچارگان) نگهدار و یاوری فرست.'),
(569, 4, 76, 'اهل ایمان در راه خدا، و کافران در راه طاغوت (شیطان) جهاد می‌کنند، پس (شما مؤمنان) با دوستان شیطان بجنگید (و از آنها بیم و اندیشه مکنید) که مکر و سیاست شیطان بسیار سست و ضعیف است.'),
(570, 4, 77, 'آیا نمی‌نگری به حال مردمی که (گفتند: به ما اجازه جنگ ده و) به آنها گفته شد: اکنون از جنگ خودداری کرده، به نماز و زکات قیام کنید؛ پس آن‌گاه که بر آنها حکم جهاد آمد در آن هنگام گروهی از آنان به همان اندازه که باید از خدا ترسند یا بیش از آن، از مردم ترسیدند و گفتند: ای خدا، چرا بر ما حکم جنگ را فرض کردی؟ چرا عمر ما را تا اجل نزدیک طبیعی به تأخیر نیفکندی؟ بگو: زندگانی دنیا متاعی اندک است و جهان آخرت برای هر کس که خداترس باشد بهتر از دنیاست، و (در آنجا) کمترین ستمی به شما نخواهد شد.'),
(571, 4, 78, 'هر کجا باشید اگر چه در کاخهای بسیار محکم، شما را مرگ فرا رسد. و آنان را اگر خوشی و نعمتی فرا رسد گویند: این از جانب خداست، و اگر زحمتی پیش آید گویند: این از جانب توست! بگو: همه از جانب خدا است. چرا این قوم (جاهل) از فهم هر سخن دورند؟!'),
(572, 4, 79, 'هر چه از انواع نیکویی به تو رسد از جانب خداست و هر بدی رسد از خودِ توست، و ما تو را به رسالت برای مردم فرستادیم، و تنها گواهی خدا کافی است.'),
(573, 4, 80, 'هر که رسول را اطاعت کند خدا را اطاعت کرده، و هر که مخالفت کند (کیفر مخالفتش با خداست و) ما تو را به نگهبانی آنها نفرستاده‌ایم.'),
(574, 4, 81, 'و (منافقان، روز) نزد تو به زبان اظهار فرمانبرداری کنند و چون از حضور تو دور شوند شبانه گروهی خلاف گفته تو را در دل گیرند، و خدا اندیشه شبانه آنها را خواهد نوشت. پس (ای پیغمبر) از آنها روی بگردان و به خدا اعتماد کن، که تنها خدا تو را نگهبان کافی است.'),
(575, 4, 82, 'آیا در قرآن از روی فکر و تأمل نمی‌نگرند؟ و اگر از جانب غیر خدا بود در آن اختلافی بسیار می‌یافتند.'),
(576, 4, 83, 'و (منافقان) چون امری به آنان برسد که باعث ایمنی یا ترس (مسلمین) است (و باید پنهان داشت) آن را منتشر می‌سازند، در صورتی که اگر آن را به رسول و به صاحبان حکم که از خودشان هستند گزارش می‌دادند همانا آنان که اهل بصیرتند در آن واقعه صلاح‌اندیشی می‌کردند. و اگر نه این بود که فضل خدا شامل حال شماست، همانا به جز اندکی همه شیطان را پیروی می‌کردید.'),
(577, 4, 84, 'پس (ای پیغمبر) تو خود تنها در راه خدا به کارزار برخیز، که جز بر خودت مکلف نیستی و مؤمنان را نیز ترغیب کن، باشد که خدا آسیب کافران را از شما بازدارد، که قدرت خدا بیشتر و عذابش سخت‌تر است.'),
(578, 4, 85, 'هر که سبب کار نیکویی شود هم او نصیبی کامل از آن بَرَد، و هر که وسیله کار قبیحی گردد از آن سهمی بسزا خواهد یافت، و خدا بر همه چیز (از نیک و بد اعمال خلق) مراقبی تواناست.'),
(579, 4, 86, 'هر گاه شما را تحیت و سلامی گفتند شما نیز باید به تحیت و سلامی بهتر از آن یا مانند آن پاسخ دهید، که خدا به حساب هر چیزی کاملاً خواهد رسید.'),
(580, 4, 87, 'خدای یگانه، هیچ خدایی جز او نیست؛ محققاً همه شما را در روز قیامت که شک در آن نیست جمع آورد، و کیست که راست‌تر از خدا سخن گوید؟'),
(581, 4, 88, 'چرا شما درباره منافقین دو فرقه شدید؟ با آنکه خدا آنها را به کیفر اعمال زشتشان (به حکم کفار) بازگرداند، آیا شما می‌خواهید کسی را که خدا گمراه کرده هدایت کنید؟ در حالی که هر که را خدا گمراه کرد (یعنی هدایت خدا را نپذیرفت) هرگز تو بر هدایت او راهی نخواهی یافت.'),
(582, 4, 89, '(کافران) آرزو کنند که شما (مسلمین) هم به مانند آنها کافر شوید تا همه برابر (در کفر) باشید. پس از آنان تا در راه خدا هجرت نکنند دوست نگیرید، و اگر مخالفت کردند آنها را در هر کجا یافتید به قتل رسانید و از آنها یاور و دوستی نباید اختیار کنید.'),
(583, 4, 90, 'مگر کسانی که به قومی که بین شما با آنها عهد و پیمانی است در پیوسته باشند یا (بر این عهد نزد شما آیند) که از جنگ با شما و با قوم خودشان (که دشمنان شما هستند) هر دو خودداری کنند و از جنگ دلتنگ باشند، (با این دو طایفه از کافران که در حقیقت به شما پناهنده‌اند نباید قتال کنید.) و اگر خدا می‌خواست آنها را بر شما مسلط می‌کرد تا با شما قتال می‌کردند، پس هر گاه از شما کناره گرفتند و با شما نجنگیدند و تسلیم شما شدند خدا برای شما راهی بر علیه آنها نگشوده است.'),
(584, 4, 91, 'گروهی دیگر را خواهید یافت که می‌خواهند از شما و از قوم خود ایمنی یابند، هرگاه که راه فتنه‌گری بر آنها باز شود به کفر خود بازگردند. پس اگر از شما کناره نگرفتند و تسلیم شما نشدند و از اذیت شما دست نکشیدند در این صورت آنها را هر جا یافتید بگیرید و به قتل رسانید، ما شما را بر (جان و مال) این گروه تسلطی کامل بخشیدیم.'),
(585, 4, 92, 'هیچ مؤمنی را نرسد که مؤمن دیگری را به قتل رساند مگر آنکه به اشتباه و خطا مرتکب آن شود، و در صورتی که به خطا هم مؤمنی را مقتول ساخت باید (به کفّاره آن) بنده مؤمنی را آزاد کند و خونبهای آن را به صاحبش تسلیم نماید مگر آنکه دیه را ورثه (به قاتل) ببخشند. و اگر مقتولِ مؤمن از قومی باشد که با شما دشمن و محاربند، قاتل دیه ندهد، لیکن بر او است که بنده مؤمنی را آزاد کند، و اگر مقتول از قومی است که بین شما با آنها پیمان برقرار بوده پس خونبها را به صاحب خون پرداخته و بنده مؤمنی (به کفاره آن) آزاد کند، و اگر (بنده‌ای) نیابد بایستی دو ماه متوالی روزه دارد. این توبه‌ای است که از طرف خدا پذیرفته است و خدا دانا و حکیم است.'),
(586, 4, 93, 'و هر کس مؤمنی را به عمد بکشد مجازات او (آتش) جهنم است، که در آن جاوید (معذّب) خواهد بود و خدا بر او خشم و لعن کند و عذابی بسیار شدید بر او مهیا سازد.'),
(587, 4, 94, 'ای اهل ایمان، چون در راه خدا (برای جهاد با کفّار) بیرون روید (در کار دشمن) تحقیق و جستجو کنید و به آن کس که اظهار اسلام کند و به شما سر تسلیم فرود آورد نسبت کفر مدهید تا (مال و جانش را بر خود حلال کنید و) از متاع ناچیز دنیا چیزی غنیمت برید، که غنایم بی‌شمار نزد خداست. اسلام شما هم اول امر همچنین اظهار و تسلیمی بیش نبود تا وقتی که خدا بر شما منّت گذاشت، پس اکنون باید تحقیق کنید که خدا به هر چه کنید آگاه است.'),
(588, 4, 95, 'هرگز مؤمنانی که بدون عذر از جهاد بازنشستند با آنان که به مال و جان در راه خدا جهاد کنند یکسان نخواهند بود، خدا مجاهدان (فداکار) به مال و جان را بر بازنشستگان (از جهاد) بلندی و برتری بخشیده و همه (اهل ایمان) را وعده پاداش نیکو فرموده، و خداوند مجاهدان را بر بازنشستگان به اجر و ثوابی بزرگ برتری داده است.'),
(589, 4, 96, '(این) درجات و بخشایش و رحمت اوست و خدا آمرزنده و مهربان است.'),
(590, 4, 97, 'آنان که فرشتگان، جانشان را در حالی که ظالم به خود بوده‌اند می‌گیرند، از آنها پرسند که در چه کار بودید؟ پاسخ دهند که ما در روی زمین مردمی ضعیف و ناتوان بودیم. فرشتگان گویند: آیا زمین خدا پهناور نبود که در آن هجرت کنید؟! و مأوای ایشان جهنم است و آن بد جایگاه بازگشتی است.'),
(591, 4, 98, 'مگر آن گروه از مردان و زنان و کودکان که ناتوان بودند و گریز و چاره‌ای برایشان میسّر نبود و راهی (به نجات خود) نمی‌یافتند.'),
(592, 4, 99, 'آنها امیدوار به عفو و بخشش خدا باشند، که خدا گناهشان را می‌بخشد و خدا بخشنده و آمرزنده است.'),
(593, 4, 100, 'و هر کس در راه خدا (از وطن خویش) هجرت کند در زمین برای آسایش و گشایش امورش جایگاه بسیار خواهد یافت، و هر گاه کسی از خانه خویش برای هجرت به سوی خدا و رسول بیرون آید و در سفر، مرگ وی فرا رسد اجر و ثواب چنین کسی بر خداست و خدا پیوسته آمرزنده و مهربان است.'),
(594, 4, 101, 'و هنگامی که در سفر باشید باکی بر شما نیست که نماز را کوتاه به جای آرید (چهار رکعت را دو رکعت کنید) هر گاه بیم آن داشته باشید که کافران شما را به رنج و هلاکت اندازند، که دشمنی کفار نسبت به شما کاملاً آشکار است.'),
(595, 4, 102, 'و هرگاه شخص تو در میان سپاه اسلام باشی و نماز بر آنان به پا داری باید آنها با تو مسلّح به نماز ایستند، و چون سجده نماز به جای آوردند (و نماز را فُرادی تمام کردند) در پشت سرِ شما رفته و گروهی که نماز نخوانده بیایند و با تو نماز گزارند و البته با لباس جنگ و اسلحه، زیرا کافران آرزو و انتظار دارند که شما از اسلحه و اسباب خود غفلت کنید تا ناگهان یکباره بر شما حمله ور شوند. و چنانچه بارانی یا مرضی شما را از برگرفتن سلاح به رنج اندازد باکی نیست که اسلحه را فروگذارید ولی از دشمن برحذر باشید؛ خدا برای کافران عذابی سخت خوارکننده مهیا ساخته است.'),
(596, 4, 103, 'پس آن‌گاه که از نماز فراغت یافتید باز در همه احوال، ایستاده یا نشسته یا بر پهلو خفته (در هر حال که توانید) یاد خدا کنید، سپس که (از آسیب دشمن) ایمن شدید در این حال نماز (کامل) به جای آرید، که برای اهل ایمان، حُکمی معیّن و واجب و لازم است.'),
(597, 4, 104, 'و نباید در کار دشمن سستی و کاهلی کنید؛ که اگر شما به رنج و زحمت می‌افتید آنها نیز (از دست شما) رنج می‌کشند با این فرق که شما به لطف خدا امیدوارید و آنها امیدی ندارند. و خدا دانا و حکیم است (و امیدواران را محروم نمی‌سازد).'),
(598, 4, 105, '(ای پیغمبر) ما قرآن را به حق به سوی تو فرستادیم تا به آنچه خدا (به وحی خود) بر تو پدید آورده میان مردم حکم کنی، و نباید به نفع خیانتکاران (با مؤمنان) به خصومت برخیزی.'),
(599, 4, 106, 'و از خدا آمرزش طلب، که خدا آمرزنده و مهربان است.'),
(600, 4, 107, 'و هرگز به خاطر مردمی که با نفوس خود خیانت می‌کنند (با اهل ایمان) جدال مکن، که همانا خدا آن را که خیانتکار و بد عمل است دوست نمی‌دارد.'),
(601, 4, 108, '(مردم خیانتکار در کار خیانت) از مردم شرم می‌کنند ولی از خدا شرم نمی‌کنند و حال آنکه او با آنهاست هنگامی که شبانگاه سخنانی ناپسند در دل می‌اندیشند، و خدا (در آن حال هم) به هر چه کنند آگاه است.'),
(602, 4, 109, 'به فرض که شما خود از طرف آنها در زندگانی دنیا دفاع توانید کرد، کدام کس طرفداری آنها را روز قیامت خواهد نمود؟ و چه کس به وکالت آنها تواند برخاست؟'),
(603, 4, 110, 'و هر که عمل زشتی از او سرزند یا به خویشتن ستم کند سپس از خدا طلب آمرزش و عفو نماید خدا را بخشنده و مهربان خواهد یافت.'),
(604, 4, 111, 'هر که گناهی کند به خود زیان رسانده، و خدا دانا و کارش از روی حکمت است.'),
(605, 4, 112, 'و هر کس خطا یا گناهی از او سر زند و تهمت به بی‌گناهی بندد مرتکب بهتان و گناه بزرگ و آشکاری شده است.'),
(606, 4, 113, 'و اگر فضل و رحمت خدا شامل حال تو نبود گروهی از آنان (دشمنان اسلام) همت بر آن گماشته بودند که تو را از راه صواب دور سازند، حال آنکه آنها خود را از راه حق و صواب دور ساخته و به تو هیچ زیانی نتوانند رسانید و خدا به تو این کتاب (قرآن مجید) و (مقام) حکمت (و نبوت) را عطا کرده و آنچه را که نمی‌دانستی به تو بیاموخت، و خدا را با تو لطف عظیم و عنایت بی‌اندازه است.'),
(607, 4, 114, 'هیچ فایده و خیری در سخنان سرّی آنها نیست مگر آنکه کسی به صدقه دادن و نیکویی کردن و اصلاح میان مردم امر کند؛ و هر که در طلب رضای خدا چنین کند به زودی به او اجر عظیم کرامت کنیم.'),
(608, 4, 115, 'و هر کس پس از روشن بودن راه حق بر او، با رسول او به مخالفت برخیزد و راهی غیر طریق اهل ایمان پیش گیرد وی را به همان (طریق باطل و راه ضلالت) که برگزیده واگذاریم و او را به جهنم درافکنیم، که آن مکان بسیار بد منزلگاهی است.'),
(609, 4, 116, 'خدا گناه شرک را نخواهد بخشید و ما دون شرک را بر هر که مشیّتش تعلّق گیرد می‌بخشد. و هر که به خدا شرک آرد به گمراهی سخت و دور (از سعادت) در افتاده است.'),
(610, 4, 117, '(مشرکان) نمی‌خوانند غیر خدای عالم جز دخترانی را (بتهایی را که نام دختران بر آنها نهاده‌اند) و نمی‌خوانند جز شیطان سرکش را.'),
(611, 4, 118, 'که خدا آن (شیطان) را از درگاه رحمت خود دور کرده (زیرا به مجادله با خدا برخاست) و گفت: من از بندگان تو قسمتی معین را زیر بار طاعت خود خواهم کشید.'),
(612, 4, 119, 'و سخت گمراهشان کنم و به آرزو (های باطل و دور و دراز) درافکنم و دستور دهم تا گوش حیوانات ببرند (که اینها نصیب بتهاست) و امر کنم تا خلقت خدا را تغییر دهند، و (ای بندگان بدانید) هر کس شیطان را دوست گیرد نه خدا را، سخت زیان کرده زیانی آشکار.'),
(613, 4, 120, '(شیطان) آنان را بسیار وعده دهد و آرزومند و امیدوار کند، ولی آنان را چیزی به جز غرور و فریب وعده نمی‌دهد.'),
(614, 4, 121, 'ایشان را منزلگاه جهنم است و از آن مَفرّ و گریزگاهی نخواهند یافت.'),
(615, 4, 122, 'و آنان که به خدا گرویدند و کارهای شایسته کردند به زودی آنها را به بهشتی درآوریم که زیر درختانش نهرها جاری است و همیشه برای ابد در آن باشند. وعده خدا حق است، و کیست در گفتار راستگوتر از خدا؟!'),
(616, 4, 123, 'کار به آمال و آرزوی شما و آرزوی اهل کتاب (یهود و نصاری) درست نشود؛ هر آن که کار بد کند کیفر آن را خواهد دید و به جز خدا احدی را یار و یاور خود نتواند یافت.'),
(617, 4, 124, 'و هر که از زن و مرد کاری شایسته کند با ایمان به خدا، چنین کسان به بهشت درآیند و به قدر نقیری (پرده هسته خرمایی) به آنان ستم نکنند.'),
(618, 4, 125, 'دین چه کس بهتر از آن کسی است که خود را تسلیم (حکم) خدا نموده و هم نیکوکار است و پیروی از آیین ابراهیم حنیف نموده؟! آن ابراهیمی که خدا او را به مقام دوستی خود برگزیده است.'),
(619, 4, 126, 'هر چه در آسمانها و زمین است مِلک خداست و او به همه چیز احاطه و آگاهی دارد.'),
(620, 4, 127, '(ای پیغمبر) درباره زنان از تو فتوا خواهند، بگو: خدا و آنچه از آیات کتاب او بر شما تلاوت شود هم در حق زنان و دختران یتیمی که مهر و حقوق لازمه آنها را ادا نکرده و مایل به نکاح آنان هستید و هم در حق فرزندان ناتوان فتوا خواهد داد، و نیز (دستور می‌دهد که) درباره یتیمان عدالت پیشه گیرید، و (در نیکی کوشید که) هر کار نیک به جای آرید خدا بر آن آگاه است.'),
(621, 4, 128, 'و اگر زنی بیم داشت که شوهرش با وی مخالفت و بد سلوکی کند یا از او دوری گزیند باکی نیست که هر دو تن به گونه‌ای به راه صلح و سازش باز آیند، که صلح به هر حال بهتر است، و نفوس را بخل و حرص فرا گرفته. و اگر (درباره یکدیگر) نیکویی کرده و پرهیزکار باشید، خدا به هر چه کنید آگاه است.'),
(622, 4, 129, 'شما هرگز نتوانید میان زنان به عدالت رفتار کنید هر چند راغب و حریص (بر عدل و درستی) باشید، پس به تمام میل خود یکی را بهره‌مند و آن دیگر را محروم نکنید تا او را معلّق و بلا تکلیف گذارید. و اگر سازش کنید و پرهیزکار باشید همانا خدا بخشنده و مهربان است.'),
(623, 4, 130, 'و اگر از یکدیگر جدا شوند، باز خدا هر یک را از دیگری به رحمت واسع خود بی‌نیاز خواهد کرد، و خدا را رحمت بی‌منتهاست و (به حقیقت هر چیز) داناست.'),
(624, 4, 131, 'و هر چه در آسمانها و زمین است ملک خداست، و ما هم به آنان که پیش از شما به آنها کتاب داده شد و هم به شما سفارش اکید کردیم که پرهیزکار و خدا ترس باشید، و اگر هم کافر شوید ملک آسمان و زمین او راست (به طاعت شما محتاج نیست) و خدا بی نیاز و ستوده است.'),
(625, 4, 132, 'و آنچه در آسمانها و آنچه در زمین است همه ملک اوست، و تنها خدا به نگهبانی (خلق) کافی است.'),
(626, 4, 133, 'ای مردم، خدا اگر بخواهد همه شما را (به کلی از دنیا) می‌برد و قومی دیگر می‌آورد؛ و البته خدا بر این کار مقتدر و تواناست.'),
(627, 4, 134, 'هر که ثواب و نعمت دنیا را طالب است (از خدا بخواهد که) ثواب دنیا و آخرت نزد خداست، و خدا شنوا و بیناست.'),
(628, 4, 135, 'ای اهل ایمان، نگهدار عدالت باشید و برای خدا گواهی دهید هر چند بر ضرر خود یا پدر و مادر و خویشان شما باشد، (برای هر کس شهادت می‌دهید) اگر فقیر باشد یا غنی، خدا به (رعایت حقوق) آنها اولی است، پس شما (در حکم و شهادت) پیروی هوای نفس نکنید تا مبادا عدالت نگاه ندارید. و اگر زبان را (در شهادت به نفع خود) بگردانید یا (از بیان حق) خودداری کنید خدا به هر چه کنید آگاه است.'),
(629, 4, 136, 'ای کسانی که (به زبان) ایمان آورده‌اید، (به حقیقت و از دل هم) ایمان آورید به خدا و رسول او و کتابی که به رسول خود فرستاده و کتابی که پیش از او فرستاده (تورات و انجیل). و هر که به خدا و فرشتگان و کتابها (ی آسمانی) و رسولان او و روز قیامت کافر شود به گمراهی سخت و دور (از سعادت) درافتاده است.'),
(630, 4, 137, 'آنان که نخست ایمان آورده سپس کافر شدند، باز ایمان آورده دیگر بار کافر شدند، پس بر کفر خود افزودند، اینان را خدا نخواهد بخشید و به راهی هدایت نخواهد فرمود.'),
(631, 4, 138, 'منافقان را بشارت ده که بر آنان عذابی دردناک خواهد بود.'),
(632, 4, 139, 'آن گروه که کافران را دوست گیرند و مؤمنان را ترک گویند، آیا نزد کافران عزّت می‌طلبند؟ عزّت همه نزد خداست.'),
(633, 4, 140, 'و محققاً خداوند در کتاب خود این حکم را بر شما فرستاد که چون آیات خدا را شنیدید که بدان کافر شوند و استهزا کنند پس با آن گروه (منافق) مجالست مکنید تا در حدیثی دیگر داخل شوند، که (اگر با آنان همنشین شوید) آن گاه شما هم مانند آنان هستید. و خدا منافقان را با کافران در جهنم جمع خواهد کرد.'),
(634, 4, 141, 'منافقان آنانی هستند که نگران و مراقب حال شمایند، چنانچه برای شما فتح و ظفری پیش آید (برای اخذ غنیمت) گویند: نه آخر ما با شما بودیم؟! و اگر کافران را فتح و بهره‌ای رسد به آنها گویند: نه ما شما را به اسرار مسلمانان آگاه کردیم و شما را از آسیب مؤمنان نگهداری نمودیم؟! پس خدا فردای قیامت میان شما و آنان حکم کند، و خداوند هیچ گاه برای کافران نسبت به اهل ایمان راه تسلّط باز نخواهد نمود.'),
(635, 4, 142, 'همانا منافقان با خدا مکر و حیله می‌کنند و خدا نیز با آنان مکر می‌کند، و چون به نماز آیند با حال بی‌میلی و کسالت نماز کنند و برای ریاکاری آیند و ذکر خدا را جز اندک نکنند.'),
(636, 4, 143, 'دو دل و مردّد باشند، نه به سوی مؤمنان یکدل می‌روند و نه به جانب کافران. و هر که را خدا گمراه کند پس هرگز برای او راه هدایتی نخواهی یافت.'),
(637, 4, 144, 'ای اهل ایمان، مبادا کافران را به دوستی گرفته و مؤمنان را رها کنید! آیا می‌خواهید برای خدا بر (عقاب و کفر و عصیان) خود حجّتی آشکار قرار دهید؟!'),
(638, 4, 145, 'البته منافقان را در جهنم پست‌ترین جایگاه است و برای آنان هرگز یاری نخواهی یافت.'),
(639, 4, 146, 'مگر آنها که توبه نموده و تباهکاری خود را اصلاح کردند و به (دین) خدا در آویختند (و به کتاب حق متوسل شدند) و دین خود را برای خدا خالص گردانیدند، پس این گروه در صف مؤمنان باشند و به زودی خدا به مؤمنان اجر بزرگ عطا خواهد فرمود.'),
(640, 4, 147, 'خداوند چه غرضی دارد که شما را عذاب کند اگر (لطف خدا را) شکر گزارید و (به او) ایمان آرید؟! خدا شکر نعمت از شما می‌پذیرد و علمش (به صلاح خلق) محیط است.'),
(641, 4, 148, 'خدا دوست نمی‌دارد که کسی به گفتار زشت (به عیب خلق) صدا بلند کند مگر آن که ظلمی به او رسیده باشد، همانا که خدا شنوا و داناست.'),
(642, 4, 149, 'اگر (درباره خلق) آشکارا یا پنهانی نیکی کنید یا از بدی دیگران درگذرید خدا همیشه از بدیها در می‌گذرد با آنکه در انتقام تواناست.'),
(643, 4, 150, 'آنان که به خدا و رسولان او کافر شوند و خواهند که میان خدا و پیغمبرانش جدایی اندازند و گویند: ما به برخی (از انبیاء) ایمان آورده و به پاره‌ای ایمان نیاوریم و خواهند که میان کفر و ایمان راهی اختیار کنند؛'),
(644, 4, 151, 'به حقیقت کافر اینهایند و ما برای کافران عذابی خوار کننده مهیا ساخته‌ایم.'),
(645, 4, 152, 'و آنان که ایمان به خدا و رسولانش آوردند و تفرقه میان خدا و هیچ یک از پیغمبران نیفکندند، اینها را خدا به زودی اجرشان را عطا کند و خدا پیوسته بخشنده و مهربان است.'),
(646, 4, 153, '(ای پیغمبر) اهل کتاب از تو درخواست کنند که از آسمان کتابی (یکجا) برای آنان فرود آوری، همانا از موسی نیز درخواستی بالاتر از این کردند و گفتند: خدا را به چشم ما آشکار بنما، پس به سبب این جهل و سرکشی صاعقه سوزان آنها را درگرفت، و پس از آن همه آیات روشن، باز گوساله‌پرستی اختیار کردند، باز ما از این کار (زشت آنان) درگذشتیم و به موسی حجّتی آشکار بخشیدیم.'),
(647, 4, 154, 'و کوه طور را برای (اتمام حجّت و اخذ) پیمان بر فراز سر آنها بلند گردانیدیم و آنها را گفتیم به حال سجده بدین درگاه درآیید و گفتیم از حکم روز شنبه تعدی مکنید، و از آنها پیمانی سخت گرفتیم.'),
(648, 4, 155, 'پس چون پیمان شکستند و به آیات خدا کافر شدند و پیغمبران را به ناحق کشتند و (همه این قبایح به جای آوردند) بدین عذر که گفتند: دلهای ما در پرده است (و دروغ گفتند) بلکه خدا به سبب کفر، آنها را مهر بر دل نهاد، که به جز اندکی ایمان نیاورند؛'),
(649, 4, 156, 'و هم به واسطه کفرشان و بهتان بزرگشان بر مریم؛'),
(650, 4, 157, 'و هم از این رو که گفتند: ما مسیح عیسی بن مریم رسول خدا را کشتیم، در صورتی که او را نه کشتند و نه به دار کشیدند بلکه امر بر آنها مشتبه شد؛ و همانا آنان که درباره او عقاید مختلف اظهار داشتند از روی شک و تردید سخنی گفتند و عالم به او نبودند جز آنکه از پی گمان خود می‌رفتند؛ و به طور یقین مسیح را نکشتند؛'),
(651, 4, 158, 'بلکه خدا او را به سوی خود بالا برد، و پیوسته خدا مقتدر و کارش از روی حکمت است؛'),
(652, 4, 159, 'و هیچ کس از اهل کتاب نیست جز آنکه پیش از مرگ به وی (عیسی) ایمان خواهد آورد و در روز قیامت او بر آنان گواه خواهد بود؛'),
(653, 4, 160, 'پس به جهت (این همه) ظلمی که یهود کردند و هم بدین جهت که بسیاری مردم را از راه خدا منع کردند، ما نعمت‌های پاکیزه‌ای را که بر آنها حلال بود حرام گردانیدیم؛'),
(654, 4, 161, 'و هم بدین جهت که ربا می‌گرفتند، در صورتی که از ربا خوردن نهی شده بودند، و هم از آن رو که اموال مردم را به باطل می‌خوردند. و ما برای کافران آنها عذابی دردناک مهیا ساخته‌ایم.'),
(655, 4, 162, 'لیکن کسانی از آنها که در علم قدمی ثابت دارند و به آنچه بر تو و پیغمبران پیش از تو نازل شده می‌گروند و به ویژه مؤمنانِ دیگر آنان که نماز به پا می‌دارند و زکات می‌دهند و به خدا و روز قیامت ایمان می‌آورند، به زودی خدا آنان را اجر و ثواب عظیم عطا خواهد کرد.'),
(656, 4, 163, 'ما به تو وحی کردیم چنانکه به نوح و پیغمبران بعد از او و همچنین به ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط (فرزندان یعقوب) و عیسی و ایّوب و یونس و هارون و سلیمان وحی نمودیم و به داود هم زبور عطا کردیم.'),
(657, 4, 164, 'و رسولانی هم که شرح حال آنها را از پیش بر تو حکایت کردیم و آنهایی را که حکایت ننمودیم (به همه خدا وحی فرستاد) و خدا با موسی (در طور) آشکار و روشن سخن گفت.'),
(658, 4, 165, 'رسولانی را فرستاد که (نیکان را) بشارت دهند و (بدان را) بترسانند تا آنکه پس از فرستادن رسولان، مردم را بر خدا حجّتی نباشد، و خدا همیشه مقتدر و کارش بر وفق حکمت است.'),
(659, 4, 166, '(اگر اینان به نبوت تو گواهی نمی‌دهند) لیکن خدا به آنچه بر تو فرستاده گواهی دهد، که آن را به علم خود فرستاده و فرشتگان نیز گواهی دهند، و گواهی خدا کفایت است.'),
(660, 4, 167, 'آنان که کافر شده (و مردم را) از راه خدا باز داشتند همانا سخت به گمراهی افتاده و از راه نجات دورند.'),
(661, 4, 168, 'آنان که کافر و ستمگر شدند هیچ گاه خدا آنان را نخواهد آمرزید و به راهی هدایت نخواهد نمود؛'),
(662, 4, 169, 'مگر به راه دوزخ، که در آن جاوید خواهند بود و خدا را این کار (انتقام از ستمکاران) آسان است.'),
(663, 4, 170, 'ای مردم، همانا این پیغمبر به حق (برای هدایت شما) از جانب پروردگارتان آمده، پس ایمان آرید که برای شما بهتر است، و اگر کافر شوید پس (از این کفر به خود زیان رسانیده‌اید و خدا از کفرتان بی‌زیان و از اطاعت شما بی‌نیاز است، زیرا) هر چه در آسمانها و زمین است همه ملک خداست و خدا (به احوال هر مؤمن و کافر) دانا و (در ثواب و عقاب آنان) درستکار است.'),
(664, 4, 171, 'ای اهل کتاب، در دین خود اندازه نگه دارید، و درباره خدا جز به راستی سخن نگویید؛ در حق مسیح عیسی بن مریم جز این نشاید گفت که او رسول خداست و کلمه الهی است که به مریم فرستاده و روحی از عالم الوهیت است؛ پس به خدا و همه فرستادگانش ایمان آورید و به تثلیث قائل نشوید (اب و ابن و روح القدس را خدا نخوانید)، از این گفتار شرک باز ایستید که برای شما بهتر است، جز خدای یکتا خدایی نیست و منزّه و برتر از آن است که او را فرزندی باشد، هر چه در آسمان و زمین است همه ملک او است و خدا تنها به نگهبانی (همه موجودات) کافی است.'),
(665, 4, 172, 'هرگز مسیح از بنده خدا بودن اِبا و استنکاف ندارد و فرشتگان مقرّب نیز (به بندگی او معترفند). و هر کس از بندگی خدا سر پیچد و دعوی برتری کند، زود باشد که خدا همه را به سوی خود محشور سازد.'),
(666, 4, 173, 'پس آنان را که ایمان آورده و نیکوکار شده اجر و ثواب تمام و کامل می‌دهد و به فضل خود بر آن می‌افزاید، و آنان را که سرپیچی و سرکشی کرده به عذابی دردناک معذّب خواهد فرمود و آنان (بر نجات خود) جز خدا هیچ کس را دوست و یاور نخواهند یافت.'),
(667, 4, 174, 'ای مردم، برای هدایت شما از جانب خدا برهانی (محکم) آمد، و نوری تابان به شما فرستادیم.'),
(668, 4, 175, 'پس آنان که به خدا گرویدند و به او متوسل شدند به زودی آنها را به جایگاه رحمت و فضل خود درآورده و به راهی راست به سوی خود رهبری نماید.'),
(669, 4, 176, '(ای پیغمبر) از تو درباره کلاله (یعنی برادر و خواهر پدری یا پدری و مادری) فتوا خواهند، بگو: خدا چنین فتوا می‌دهد که هر گاه کسی بمیرد در حالی که فرزند نداشته باشد و او را خواهری باشد، وی را نصف ترکه است و او نیز از خواهر ارث برد اگر خواهر را فرزند نباشد. و اگر میت را دو خواهر باشد آنها را دو ثلث ترکه است. و اگر میت را چندین برادر و خواهر است در این صورت ذکور دو برابر اِناث ارث برند. خدا (احکام خود را) برای شما بیان می‌کند تا گمراه نشوید، و خدا به همه چیز دانا است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای اهل ایمان، (هر عهد که با خدا و خلق بستید) به عهد و پیمان خود وفا کنید (و بدانید) بهائم بسته زبان برای شما حلال گردید جز آنچه (بعداً) برایتان تلاوت خواهد شد و جز آن صیدی که بر شما در حال احرام حلال نیست. همانا خدا به هر چه خواهد حکم کند.'),
(671, 5, 2, 'ای اهل ایمان، حرمت شعائر خدا (مناسک حج) و ماه حرام را نگه دارید و متعرض هَدْی و قلائد (قربانیان حاجیان چه علامت‌دار و چه بدون علامت) نشوید و تعرض زائران خانه محترم کعبه را که در طلب فضل خدا و خشنودی او آمده‌اند حلال نشمارید، و چون از احرام بیرون شدید (می‌توانید) صید کنید. و عداوت گروهی که از مسجد الحرام منعتان کردند شما را بر ظلم و بی‌عدالتی وادار نکند، و باید با یکدیگر در نیکوکاری و تقوا کمک کنید نه بر گناه و ستمکاری. و از خدا بترسید که عقاب خدا سخت است.'),
(672, 5, 3, 'برای شما مؤمنان گوشت مردار و خون و گوشت خوک و آن ذبیحه‌ای که به نام غیر خدا کشتند و همچنین هر حیوانی که به خفه کردن یا به چوب زدن یا از بلندی افکندن یا به شاخ زدن به هم بمیرند و نیم‌خورده درندگان جز آن را که قبلاً تذکیه کرده باشید حرام است و نیز آن را که برای بتان می‌کشند و آن را که به تیرها قسمت می‌کنید، که این کار فسق است-امروز کافران از این که به دین شما دستبرد زنند و اختلالی رسانند طمع بریدند، پس شما از آنها بیمناک نگشته و از من بترسید. امروز (به عقیده امامیه و برخی اهل سنّت روز غدیر خم و خلافت علی (ع) است) دین شما را به حد کمال رسانیدم و بر شما نعمتم را تمام کردم و بهترین آیین را که اسلام است برایتان برگزیدم-پس هر گاه کسی در ایام قحطی و سختی از روی اضطرار نه به قصد گناه از آنچه حرام شده مرتکب شود، خدا بخشنده و مهربان است.'),
(673, 5, 4, '(ای پیغمبر) از تو سؤال می‌کنند که چه چیز بر آنها حلال گردیده؟ بگو: برای شما هر چه پاکیزه است حلال شده، و صیدی که به سگان شکاری از آنچه خدا به شما آموخته است آموخته‌اید که برای شما نگاه دارند و نام خدا را بر آن صید یاد کنید و بخورید حلال شده. و از خدا بترسید که زود به حساب (خلق) می‌رسد.'),
(674, 5, 5, 'امروز هر چه پاکیزه است شما را حلال شد و طعام اهل کتاب برای شما و طعام شما برای آنها حلال است. و نیز حلال شد نکاح زنان پارسای مؤمنه و زنان پارسای اهل کتاب در صورتی که شما اجرت و مهر آنان را بدهید و پاکدامن باشید نه زناکار و رفیق‌باز. و هر کس به ایمان (دین اسلام) کافر شود عمل او تباه شده و در آخرت از زیانکاران خواهد بود.'),
(675, 5, 6, 'ای اهل ایمان، چون خواهید برای نماز برخیزید صورت و دستها را تا مرفق (آرنج) بشویید و سر و پاها را تا برآمدگی پا مسح کنید، و اگر جُنُب هستید پاکیزه شوید (غسل کنید) و اگر بیمار یا مسافرید یا یکی از شما را قضاء حاجتی دست داده و یا با زنان مباشرت کرده‌اید و آب نیابید در این صورت به خاک پاک و پاکیزه‌ای روی آرید و به آن خاک صورت و دستها را مسح کنید (تیمّم کنید). خدا نمی‌خواهد هیچ گونه سختی برای شما قرار دهد، و لیکن می‌خواهد تا شما را پاکیزه گرداند و نعمت خود را بر شما تمام کند، باشد که شکر او به جای آرید.'),
(676, 5, 7, 'و یاد کنید نعمت خدا را که به شما ارزانی داشت و عهد او را که با شما استوار کرد آن گاه که گفتید: (امر تو را) شنیدیم و اطاعت کردیم. و از خدا بترسید که خدا به نیّات قلبی (شما) آگاه است.'),
(677, 5, 8, 'ای اهل ایمان، برای خدا پایدار و استوار بوده و به عدالت و راستی و درستی گواه باشید، و البته شما را نباید عداوت گروهی بر آن دارد که از راه عدل بیرون روید، عدالت کنید که به تقوا نزدیکتر (از هر عمل) است، و از خدا بترسید، که البته خدا به هر چه می‌کنید آگاه است.'),
(678, 5, 9, 'خدا به آنان که ایمان آورده و کار شایسته کردند وعده آمرزش و اجر عظیم فرموده است.'),
(679, 5, 10, 'و آنان که کافر شدند و آیات ما را تکذیب کردند اهل جهنم خواهند بود.'),
(680, 5, 11, 'ای اهل ایمان، یاد آرید نعمت خدا را بر خود آن گاه که گروهی همّت گماشتند که بر شما دست یازند و خدا دست (ستم) آنها را از شما کوتاه نمود. و از خدا بترسید، و باید اهل ایمان تنها بر خدا توکل کنند.'),
(681, 5, 12, 'و همانا خدا از بنی اسرائیل عهد گرفت و از میان آنها دوازده نفر بزرگ برانگیختیم (که پیشوای هر سبطی باشند) و خدا بنی اسرائیل را گفت که من با شمایم هر گاه نماز به پا دارید و زکات بدهید و به فرستادگان من ایمان آورید و آنها را اعزاز و یاری کنید و خدا را قرض نیکو دهید، در این صورت گناهان شما بیامرزم و شما را در بهشتی داخل گردانم که زیر درختانش نهرها جاری است. پس هر کس از شما پس از عهد خدا کافر شد سخت از راه راست دور افتاده است.'),
(682, 5, 13, 'پس چون پیمان شکستند، آنان را لعنت کردیم و دلهایشان سخت گردانیدیم (که موعظه در آن اثر نکرد)، کلمات خدا را از جای خود تغییر می‌دهند و از آن کلمات که به آن‌ها پند داده شد نصیب بزرگی را از دست دادند. و دائم بر خیانتکاری آن قوم مطلع می‌شوی جز قلیلی از ایشان (که با ایمان و نکوکارند)، پس تو از آنها درگذر و (کار بدشان را) عفو کن، که خدا نیکوکاران را دوست می‌دارد.'),
(683, 5, 14, 'و از آنان که گفتند: ما به کیش عیسی هستیم عهد گرفتیم (که پیرو کتاب و رسول خدا باشند) آنان نیز از آنچه (در انجیل) پند داده شدند نصیب بزرگی را از دست دادند، ما هم (به کیفر عملشان) آتش جنگ و دشمنی را تا قیامت میان آنها افروختیم، و به زودی خدا آنها را بر (عاقبت بدِ) آنچه می‌کنند آگاه خواهد ساخت.'),
(684, 5, 15, 'ای اهل کتاب، تحقیقاً رسول ما به سوی شما آمده که حقایق و احکام بسیاری از آنچه از کتاب آسمانی را پنهان می‌دارید برای شما بیان می‌کند و از سرِ بسیاری (از خطاهای شما) درمی‌گذرد. همانا از جانب خدا برای (هدایت) شما نوری (عظیم) و کتابی آشکار آمد.'),
(685, 5, 16, 'خدا بدان کتاب هر کس را که از پی خشنودی او رود به راههای سلامت هدایت کند و آنان را از تاریکی‌های (جهل و گناه) بیرون آورد و به عالم نور داخل گرداند و به راه راست رهبری کند.'),
(686, 5, 17, 'همانا آنان که گفتند: «خدا همان مسیح بن مریم است» کافر شدند. بگو (ای پیغمبر): کدام قدرت می‌تواند در برابر خدا کمترین مانعی ایجاد کند اگر خدا بخواهد عیسی بن مریم و مادرش و هر که در روی زمین است همه را هلاک گرداند؟ و مُلک آسمانها و زمین و هر چه بین آنهاست همه از آنِ خداست، هر چه را بخواهد خلق می‌کند و او بر (ایجاد و هلاک) همه چیز تواناست.'),
(687, 5, 18, 'یهود و نصاری گفتند: ما پسران خدا و دوستان اوییم. بگو (ای پیغمبر): اگر چنین است پس او چرا شما را به گناهانتان عذاب می‌کند؟ بلکه شما هم بشری هستید از آنها که خدا خلق کرده، هر که را بخواهد می‌بخشد و هر که را بخواهد عذاب می‌کند، و فرمانروایی آسمانها و زمین و آنچه بین آنها است از آنِ خداست و بازگشت همه به سوی اوست.'),
(688, 5, 19, 'ای اهل کتاب، تحقیقاً رسول ما به سوی شما آمد که برای شما (حقایق دین را) بیان می‌کند در روزگاری که پیغمبری نبود، تا نگویید: رسولی که بشارت و بیم (به ثواب و عقاب) دهد بر ما نیامد. پس آن رسول بشارت ده و بیم‌آور به سوی شما آمد؛ و خدا بر هر چیز تواناست.'),
(689, 5, 20, 'و (یاد کن) هنگامی که موسی به قوم خود گفت: ای قوم، نعمت خدا را به خاطر آرید آن گاه که در میان شما پیغمبران فرستاد و شما را پادشاهی داد و به شما آنچه را که به هیچ یک از اهل عالم نداد عطا کرد.'),
(690, 5, 21, 'ای قوم، به سرزمین مقدسی که خدا سرنوشت شما کرد داخل شوید و پشت (به حکم خدا) مکنید، که زیانکار می‌شوید.'),
(691, 5, 22, 'گفتند: ای موسی، در آن سرزمین مقدس گروهی مقتدر و قاهر و ستمکار هستند و هرگز تا آن‌ها از آنجا بیرون نشوند ما داخل نخواهیم شد، هر گاه آنان بیرون شدند ما داخل می‌شویم.'),
(692, 5, 23, 'دو نفر مرد خدا ترس (یوشع و کالب) که مورد لطف خدا بودند گفتند: شما بر آنها از این در درآیید، چون در آمدید آن گاه محققاً (بر آنها) غالب خواهید شد، و بر خدا توکل کنید اگر به او گرویده‌اید.'),
(693, 5, 24, 'باز قوم گفتند: ای موسی، هرگز ما در آنجا مادامی که آنها باشند ابداً در نیاییم، پس تو برو به اتفاق پروردگارت با آنها قتال کنید ما اینجا خواهیم نشست.'),
(694, 5, 25, 'موسی گفت: خدایا، من جز بر خود و برادرم مالک و فرمانروا نیستم، پس تو میان ما و این قوم فاسق جدایی انداز.'),
(695, 5, 26, 'خدا گفت: (چون مخالفت امر کردند) شهر را بر آنها حرام کرده، چهل سال بایستی در بیابان حیران و سرگردان باشند، پس تو بر این گروه فاسق متأسف و اندوهگین مباش.'),
(696, 5, 27, 'و بخوان بر آنها به حقیقت حکایت دو پسر آدم (قابیل و هابیل) را، که چون تقرب به قربانی جستند از یکی پذیرفته شد و از دیگری پذیرفته نشد. (قابیل به برادرش هابیل) گفت: البته تو را خواهم کشت. (هابیل) گفت: بی‌تردید خدا (قربانی را) از متقیان خواهد پذیرفت.'),
(697, 5, 28, 'سوگند که اگر تو به کشتن من دست برآوری من هرگز به کشتن تو دست دراز نخواهم کرد، که من از خدای جهانیان می‌ترسم.'),
(698, 5, 29, 'من خواهم که گناه (کشتن) من و گناه (مخالفت) تو هر دو به تو باز گردد تا تو اهل آتش جهنم شوی، که آن آتش جزای ستمکاران است.'),
(699, 5, 30, 'آن گاه (پس از این گفتگو) هوای نفس او را بر کشتن برادرش ترغیب نمود تا او را به قتل رساند و بدین سبب از زیانکاران گردید.'),
(700, 5, 31, 'آن گاه خدا کلاغی را برانگیخت که زمین را به چنگال حفر نماید تا به او بنماید چگونه بدن مرده برادر را زیر خاک پنهان کند. (قابیل) گفت: وای (بر من) آیا من از آن عاجزترم که مانند این کلاغ باشم تا جسد برادر را زیر خاک پنهان کنم؟ پس (برادر را به خاک سپرد و) از این کار سخت پشیمان گردید.'),
(701, 5, 32, 'بدین سبب بر بنی اسرائیل حکم نمودیم که هر کس نفسی را بدون حق و یا بی‌آنکه فساد و فتنه‌ای در زمین کرده، بکشد مثل آن باشد که همه مردم را کشته، و هر کس نفسی را حیات بخشد (از مرگ نجات دهد) مثل آن است که همه مردم را حیات بخشیده. و هر آینه رسولان ما به سوی آنان با معجزات روشن آمدند سپس بسیاری از مردم بعد از آمدن رسولان باز روی زمین بنای فساد و سرکشی را گذاشتند.'),
(702, 5, 33, 'همانا کیفر آنان که با خدا و رسول به جنگ برخیزند و در زمین به فساد کوشند جز این نباشد که آن‌ها را به قتل رسانده، یا به دار کشند و یا دست و پایشان به خلاف یکدیگر بِبُرند و یا به نفی و تبعید از سرزمین (صالحان) دور کنند. این ذلت و خواری عذاب دنیوی آنهاست و اما در آخرت به عذابی بزرگ معذّب خواهند بود.'),
(703, 5, 34, 'مگر آنان که توبه کنند پیش از آنکه بر آنها دست یابید، پس بدانید که خداوند بخشنده و مهربان است.'),
(704, 5, 35, 'ای اهل ایمان، از خدا بترسید و به سوی او وسیله جویید و در راه او جهاد کنید، باشد که رستگار شوید.'),
(705, 5, 36, 'آنان که کافر شدند اگر دو برابر آنچه در زمین است فدا آرند تا به آن خود را از عذاب قیامت رهانند هرگز از آنها قبول نشود و آنان را عذاب دردناک خواهد بود.'),
(706, 5, 37, 'و آنها دائم آرزو کنند که از آتش دوزخ بیرون شوند و هرگز بِدَر نخواهند شد، که عذاب آنها دائم و پایدار است.'),
(707, 5, 38, 'دست زن و مرد دزد را به کیفر اعمالشان ببُرید، این عقوبتی است که خدا مقرر کرده و خدا مقتدر و به مصالح خلق داناست.'),
(708, 5, 39, 'پس هر که پس از ستمی که کرده توبه نمود و کار خود را اصلاح کرد از آن پس خدا او را خواهد بخشید، که خدا بخشنده و مهربان است.'),
(709, 5, 40, 'آیا ندانستی که ملک آسمان و زمین خدا راست؟ هر که را خواهد عذاب کند و از هر که خواهد درگذرد، و خدا بر همه چیز تواناست.'),
(710, 5, 41, 'ای پیغمبر، غمگین مباش از آنان که به زبان اظهار ایمان کنند و به دل ایمان ندارند و به راه کفر شتابند، و اندوهناک مباش از آن یهودانی که جاسوسی کنند و سخنان دروغ (و فتنه‌خیز به جای کلمات حق) به آن قومی که (از کبر) نزد تو نیامدند می‌رسانند؛ کلمات حق را بعد از آنکه به جای خود مقرّر گشت تغییر دهند و گویند: اگر حکم قرآن این گونه آورده شد بپذیرید و الاّ دوری گزینید. و هر کس را خدا به آزمایش و رسوایی افکند هرگز تو او را از (قهر) خدا نتوانی رهانید، آنها کسانی هستند که خدا نخواسته دلهاشان را پاک گرداند، و آنان را در دنیا ذلّت و خواری و در آخرت عذاب بزرگ مهیّاست.'),
(711, 5, 42, 'آنها جاسوسان دروغ‌زن و خورندگان مال حرامند، اگر به نزد تو آمدند خواهی میان آنها حکم کن یا روی از آنها بگردان، و چنانچه روی از آنها بگردانی هرگز کوچکترین زیان به تو نتوانند رسانید، و اگر حکم کردی میان آن‌ها به عدالت حکم کن، که خدا دوست می‌دارد آنان را که حکم به عدل کنند.'),
(712, 5, 43, 'و چگونه آنها به حکم تو سر فرود آرند در صورتی که تورات نزد آنها است و حکم خدا در آن است و با این حال از آن روی می‌گردانند؟ (پس چنانچه تو نیز از تورات حکم کنی بدان راضی نشوند) چه آنکه اینان به خدا ایمان ندارند.'),
(713, 5, 44, 'ما تورات را فرستادیم که در آن هدایت و روشنایی است و پیغمبرانی که تسلیم امر خدا هستند بدان کتاب بر یهودان حکم کنند و نیز خداشناسان و علمایی که مأمور نگهبانی (احکام) کتاب خدا هستند و بر (صدق) آن گواهی دادند. پس هرگز (در اجرای احکام) از کسی نترسید و از (انتقام) من بترسید و آیات مرا به بهای اندک معامله نکنید، که هر کس به خلاف آنچه خدا فرستاده حکم کند چنین کس از کافران خواهد بود.'),
(714, 5, 45, 'و در تورات بر بنی اسرائیل حکم کردیم که نَفْس را در مقابل نَفْس قصاص کنید و چشم را مقابل چشم و بینی را به بینی و گوش را به گوش و دندان را به دندان، و هر زخمی را قصاص خواهد بود. پس هر گاه کسی حق قصاص را ببخشد (نیکی کرده و) کفاره (گناه) او خواهد شد، و هر کس به خلاف آنچه خدا فرستاده حکم کند چنین کس از ستمکاران خواهد بود.'),
(715, 5, 46, 'و از پی آن رسولان باز عیسی پسر مریم را فرستادیم که تصدیق به درستی توراتی که پیش او بود داشت و انجیل را نیز به او دادیم که در آن هدایت و روشنی (دلها) است، و تصدیق به درستی تورات که پیش از او بود دارد و راهنمایی خلق و اندرز برای پرهیزکاران عالم است.'),
(716, 5, 47, 'و اهل انجیل باید به آنچه خدا در آن کتاب فرستاد حکم کنند، و هر کس به خلاف آنچه خدا فرستاده حکم کند چنین کس از فاسقان خواهد بود.'),
(717, 5, 48, 'و ما این (قرآن عظیم) را به حق بر تو فرستادیم که تصدیق به درستی و راستی همه کتب که در برابر اوست نموده و بر حقیقت کتب آسمانی پیشین گواهی می‌دهد. پس حکم کن میان آنها به آنچه خدا فرستاد و در اثر پیروی از خواهش‌های ایشان حکم حقی که بر تو آمده وامگذار، ما برای هر قومی از شما شریعت و طریقه‌ای مقرّر داشتیم، و اگر خدا می‌خواست همه شما را یک امت می‌گردانید، و لیکن (این نکرد) تا شما را به احکامی که در کتاب خود به شما فرستاد بیازماید. پس به کارهای نیک سبقت گیرید، که بازگشت همه شما به سوی خداست و در آنچه اختلاف می‌نمایید شما را به (حقیقت) آن آگاه خواهد ساخت.'),
(718, 5, 49, 'و (تو ای پیغمبر) به آنچه خدا فرستاده میان آنان حکم کن و پیرو خواهش‌های ایشان مباش، و بیندیش که مبادا تو را فریب دهند و در بعض احکام که خدا به تو فرستاده تقاضای تغییر کنند. پس اگر از حکم خدا روی گردانیدند (باک مدار) بدان که خدا می‌خواهد آنها را به عقوبت بعض گناهانشان گرفتار سازد، و همانا بسیاری از مردم فاسق و بدکارند.'),
(719, 5, 50, 'آیا باز تقاضای تجدید حکم زمان جاهلیت را دارند؟ و کدام حکم از احکام خدا برای اهل یقین نیکوتر خواهد بود؟!'),
(720, 5, 51, 'ای اهل ایمان، یهود و نصاری را به دوستی مگیرید، آنان بعضی دوستدار بعضی دیگرند، و هر که از شما مؤمنان با آنها دوستی کند به حقیقت از آنها خواهد بود؛ همانا خدا ستمکاران را هدایت نخواهد نمود.'),
(721, 5, 52, 'گروهی منافق را که دلهاشان ناپاک است خواهی دید که در (راه دوستی) ایشان می‌شتابند و می‌گویند: ما از آن می‌ترسیم که (در گردش روزگار) مبادا آسیبی از آنها به ما رسد. باشد که خدا فتحی پیش آورد و یا امری دیگر از طرف خود، تا منافقان از آنچه (به نفاق) در دل نهان کردند سخت پشیمان شوند.'),
(722, 5, 53, 'و اهل ایمان گویند: آیا اینان هستند که با جدیت و مبالغه بسیار به خدا سوگند یاد می‌کردند که ما از شما هستیم؟! اعمالشان باطل گردید و سخت زیانکار شدند.'),
(723, 5, 54, 'ای گروهی که ایمان آورده‌اید، هر که از شما از دین خود مُرتَد شود به زودی خدا قومی را که بسیار دوست دارد و آنها نیز خدا را دوست دارند و نسبت به مؤمنان سرافکنده و فروتن و به کافران سرافراز و مقتدرند (به نصرت اسلام) برانگیزد که در راه خدا جهاد کنند و (در راه دین) از نکوهش و ملامت احدی باک ندارند. این است فضل خدا، به هر که خواهد عطا کند و خدا را رحمت وسیع بی‌منتهاست و (به احوال همه) دانا است.'),
(724, 5, 55, 'ولیّ امر و یاور شما تنها خدا و رسول و مؤمنانی خواهند بود که نماز به پا داشته و به فقرا در حال رکوع زکات می‌دهند (به اتفاق مفسّران مراد علی علیه السّلام است).'),
(725, 5, 56, 'و هر کس که خدا و رسول و اهل ایمان را ولیّ و فرمانروای خود داند (فیروز است) که تنها لشکر خدا فاتح و غالب خواهد بود.'),
(726, 5, 57, 'ای اهل ایمان، با آن گروه از اهل کتاب و کافران که دین شما را به فسوس و بازیچه گرفتند دوستی مکنید و از خدا بترسید اگر به او ایمان آورده‌اید.'),
(727, 5, 58, 'و چون شما ندای نماز بلند کنید آن را مسخره و بازی فرض کنند، زیرا آن قوم مردمی بی‌خرد و نادانند.'),
(728, 5, 59, 'بگو: ای اهل کتاب، آیا جز آنکه ما مسلمین به خدا و کتاب خودمان و کتاب شما ایمان آوردیم و شما (ایمان نیاورده و) اکثر فاسقید چیز دیگری موجب کینه و انکار شما بر ما هست؟'),
(729, 5, 60, 'بگو (ای پیغمبر): آیا شما را آگاه سازم که کدام قوم را نزد خدا بدترین پاداش است؟ کسانی که خدا بر آنها لعن و غضب کرده و برخی از آنان را به بوزینه و خوک مسخ نموده و آن کس که بندگی طاغوت (شیطان) کرده. این گروه را (نزد خدا) بدترین منزلت است و آنها گمراه‌ترین مردم از راه راستند.'),
(730, 5, 61, 'و چون آنان در نزد شما آیند اظهار کنند ما ایمان آورده‌ایم و حال آنکه با همان کفر و انکار که (به اسلام) درآمدند باز (از اسلام) بیرون شدند، و خدا به کفر و نفاقی که در درون دل پنهان می‌دارند داناتر است.'),
(731, 5, 62, 'بسیاری از آنها را بنگری که در گناه و ستمکاری و خوردن حرام می‌شتابند؛ بسیار بد کاری را پیشه خود نمودند.'),
(732, 5, 63, 'چرا علما و روحانیون آنها را از گفتار زشت و خوردن مال حرام باز نمی‌دارند؟ همانا کاری بسیار زشت را پیشه خود نمودند.'),
(733, 5, 64, 'یهود گفتند: دست (قدرت) خدا بسته است! به واسطه این گفتار (دروغ) دست آنها بسته شده و به لعن خدا گرفتار گردیدند، بلکه دو دست (قدرت) خدا گشاده است و هر گونه بخواهد (بر خلق) انفاق می‌کند. و همانا قرآنی که به تو نازل گشت بر کفر و طغیان بسیاری از اهل کتاب بیفزاید و ما (به کیفر آن) تا قیامت آتش کینه و دشمنی را در میان آنها برافروختیم، هر گاه برای جنگ (با مسلمانان) آتشی برافروختند خدا آن آتش را خاموش ساخت، و آنها در روی زمین به فسادکاری می‌کوشند، و هرگز خدا مردم ستمکار مفسد را دوست نمی‌دارد.'),
(734, 5, 65, 'و چنانچه اهل کتاب ایمان آرند و تقوا پیشه کنند ما البته گناهانشان را محو و مستور می‌سازیم و آنها را در بهشت پر نعمت داخل می‌گردانیم.'),
(735, 5, 66, 'و چنانچه آنها به دستور تورات و انجیل خودشان و قرآنی که از سوی پروردگارشان به سوی آنها نازل شده قیام می‌کردند البته به هر گونه نعمت از بالا و پایین برخوردار می‌شدند، (لیکن) برخی از آنان مردمی میانه‌رو و معتدل، و بسیاری از آنها بسی زشتکار و بد عملند.'),
(736, 5, 67, 'ای پیغمبر، آنچه از خدا بر تو نازل شد (به خلق) برسان که اگر نرسانی تبلیغ رسالت و اداء وظیفه نکرده‌ای، و خدا تو را از (شر) مردمان محفوظ خواهد داشت، (و دل قوی دار که) خدا کافران را (به هیچ راه موفقیتی) راهنمایی نخواهد کرد.'),
(737, 5, 68, '(ای پیغمبر) بگو: ای اهل کتاب، شما ارزشی ندارید تا آنکه به دستور تورات و انجیل و قرآنی که به شما از جانب خدا فرستاده شده قیام کنید. و همانا قرآنی که به تو از سوی پروردگارت نازل شد (به جای آنکه به آن ایمان آرند) بر کفر و سرکشی بسیاری از آنان خواهد افزود، در این صورت (تو ای پیغمبر) بر حال کافران نباید تأسف خوری.'),
(738, 5, 69, 'البته هر کس از گرویدگان به اسلام و یهودان و ستاره‌پرستان و نصاری که به خدا و روز قیامت ایمان آورد و نیکوکار شود هرگز آنان را ترسی و اندوهی (در عالم آخرت) نخواهد بود.'),
(739, 5, 70, 'ما از بنی اسرائیل پیمان گرفتیم و پیغمبرانی بر آنها فرستادیم؛ اما هر گاه رسولی آمد که بر خلاف هوای نفس آنها سخن گفت، گروهی را تکذیب کرده و گروهی را می‌کشتند.'),
(740, 5, 71, 'و گمان کردند که بر آنها فتنه و امتحانی نخواهد بود، پس (از دیدن و شنیدن امر خدا) کور و کر شدند، (و پس از آن همه اعمال زشت) باز خدا توبه آنها را پذیرفت، اما باز هم بسیاری دیگر کور و کر شدند؛ و خدا به هر چه می‌کنند آگاه است.'),
(741, 5, 72, 'آنان که قائل به خدایی مسیح پسر مریم شدند محققاً کافر گشتند، در حالی که خود مسیح گفت: ای بنی اسرائیل، خدایی را که آفریننده من و شماست بپرستید، که هر کس به خدا شرک آرد خدا بهشت را بر او حرام گرداند و جایگاهش آتش دوزخ باشد، و ستمکاران را هیچ کس یاری نخواهد کرد.'),
(742, 5, 73, 'البته آنان که گفتند خدا یکی از سه (عنصر تشکیل دهنده خدا) است کافر گردیدند، و حال آنکه جز خدای یگانه خدایی نیست. و اگر از این گفتار (تثلیث) زبان نبندند البته آن کافران را عذابی دردناک خواهد رسید.'),
(743, 5, 74, 'آیا نبایستی به سوی خدا بازگشته و توبه کنند و از او طلب آمرزش نمایند؟ حال آنکه خدا بخشنده و مهربان است.'),
(744, 5, 75, 'مسیح پسر مریم پیغمبری بیش نبود که پیش از او پیغمبرانی آمده‌اند و مادرش هم زنی راستگو و با ایمان بود و هر دو (به حکم بشریت) غذا تناول می‌کردند. بنگر چگونه ما آیات خود را روشن بیان می‌کنیم، آن گاه بنگر که آنان چگونه از حق برگردانیده می‌شوند؟'),
(745, 5, 76, 'بگو: آیا شما کسی را غیر از خدا می‌پرستید که مالک هیچ سود و زیانی نسبت به شما نخواهد بود؟! و خداست که شنوا و داناست.'),
(746, 5, 77, 'بگو: ای اهل کتاب، در دین خود به ناحق غلو نکنید و از پی خواهش‌های آن گروه که خود پیش از این گمراه شدند و بسیاری را گمراه کردند و از راه راست دور افتادند نروید.'),
(747, 5, 78, 'کافران بنی اسرائیل به زبان داود و عیسی پسر مریم از آن رو لعنت کرده شدند که نافرمانی (خدا) نموده و (از حکم حق) سرکشی می‌کردند.'),
(748, 5, 79, 'آنها هیچ گاه یکدیگر را از کار زشت خود نهی نمی‌کردند، و آنچه می‌کردند بسی قبیح و ناشایسته بود.'),
(749, 5, 80, 'بسیاری از آنها را خواهی دید که با کافران دوستی می‌کنند. ذخیره‌ای که برای خود پیش می‌فرستند و آن غضب خداست بسیار بد ذخیره‌ای است و آنها در عذاب جاوید خواهند بود.'),
(750, 5, 81, 'و اگر به خدا و پیغمبر و کتابی که بر او نازل شده ایمان آورده بودند البته کافران را دوست خود نمی‌گرفتند، ولیکن بسیاری از آنها فاسق و بدکردارند.'),
(751, 5, 82, 'هر آینه دشمن‌ترین مردم نسبت به مسلمانان، یهود و مشرکان را خواهی یافت، و با محبت‌تر از همه کس به اهل ایمان آنان را که گویند: ما نصرانی هستیم. این (دوستی نصاری نسبت به مسلمین) بدین سبب است که برخی از آنها دانشمند و پارسا هستند و تکبر و گردنکشی (بر حکم خدا) نمی‌کنند.'),
(752, 5, 83, 'و چون آیاتی را که به رسول فرستاده شده استماع کنند می‌بینی اشک از دیده آنها جاری می‌شود، زیرا حقانیت آن را شناخته‌اند؛ گویند: بارالها، ما ایمان آوردیم، پس نام ما را در زمره گواهان صدق (او) بنویس.'),
(753, 5, 84, 'و (گویند) چرا ما ایمان به خدا و کتاب حق که ما را آمده است نیاوریم در صورتی که امید آن داریم که خدا ما را داخل در زمره صالحان گرداند؟'),
(754, 5, 85, 'پس خدا بر آنچه گفتند پاداش نیکو به آنها داد؛ بهشت‌هایی که از زیر درختانش نهرها جاری است که در آن زندگانی جاوید خواهند داشت، و این پاداش نیکوکاران است.'),
(755, 5, 86, 'و آنان که کافر شدند و تکذیب آیات ما نمودند ایشان اهل جهنّمند.'),
(756, 5, 87, 'ای اهل ایمان، حرام نکنید طعامهای پاکیزه‌ای که خدا بر شما حلال نموده، و (از حدود و احکام خدا) تجاوز نکنید، که خدا تجاوزکنندگان را دوست نمی‌دارد.'),
(757, 5, 88, 'هر چیز حلال و پاکیزه که خدا روزی شما کرده از آن بخورید، و بپرهیزید از (مخالفت) آن خدایی که به او گرویده‌اید.'),
(758, 5, 89, 'خدا به قسمهای لغو بیهوده، شما را مؤاخذه نخواهد کرد، ولیکن بر (شکستن) آن قسمی که از روی عقیده قلبی یاد کنید مؤاخذه خواهد نمود. و کفاره آن قسم طعام دادن ده فقیر است از آن طعام متوسط که بر اهل خود غالباً تهیه می‌کنید، یا جامه بر آنها پوشانده و یا یک بنده آزاد کنید، و هر کس توانایی آن ندارد سه روز روزه بدارد. این است کفاره (شکستنِ) سوگندهایی که یاد می‌کنید؛ و باید (حرمت) سوگندهای خود را نگاه دارید. خدا بدین گونه روشن آیات خود را برای شما بیان می‌کند، شاید که شکر او به جای آرید.'),
(759, 5, 90, 'ای اهل ایمان، شراب و قمار و بت‌پرستی و تیرهای گروبندی همه اینها پلید و از عمل شیطان است، از آن البته دوری کنید تا رستگار شوید.'),
(760, 5, 91, 'شیطان قصد آن دارد که به وسیله شراب و قمار، میان شما عداوت و کینه بر انگیزد و شما را از ذکر خدا و نماز باز دارد، پس شما آیا از آن دست برمی‌دارید؟'),
(761, 5, 92, 'و خدا و رسول خدا را اطاعت کنید و بترسید، که هر گاه روی (از طاعت خدا) بگردانید پس بدانید که بر رسول ما جز آنکه به آشکار حکم خدا را ابلاغ کند تکلیفی نخواهد بود.'),
(762, 5, 93, 'بر آنان که ایمان آوردند و نیکوکار شدند باکی نیست در آنچه از مأکولات خوردند، هر گاه تقوا پیشه گرفته و ایمان آرند و کارهای نیک کنند، باز پرهیزکار شوند و ایمان آورند، باز پرهیزکار و نیکوکار شوند؛ و خدا نیکوکاران را دوست می‌دارد.'),
(763, 5, 94, 'ای اهل ایمان، خدا شما را به چیزی از صید می‌آزماید که در دسترس شما و تیرهای شما آیند تا بداند که چه کسی از خدا در باطن می‌ترسد. پس هر که از این به بعد (از حدود الهی) تجاوز کند او را عذابی سخت دردناک خواهد بود.'),
(764, 5, 95, 'ای مؤمنان، در حال احرام صید را نکشید و هر کس آن را عمداً کشت مثل آن صید را از جنس چهارپایان، که به مثلیّت آن دو مؤمن عادل حکم کنند، به عنوان هَدْی به کعبه رساند یا چند مسکین را طعام دهد یا معادل آن روزه بدارد، تا بچشد عقوبت مخالفتش را. و خدا از گذشته درگذشت، ولی هر که دیگر بار (به مخالفت) باز گردد خدا از وی انتقام می‌کشد، و خدا مقتدر و انتقام کشنده است.'),
(765, 5, 96, 'بر شما صید دریا و طعام آن حلال گردید تا شما و کاروانان از آن بهره‌مند شوید، ولی صید بیابان تا زمانی که مُحرِم هستید بر شما حرام است. و بترسید از آن خدایی که به سوی او محشور می‌شوید.'),
(766, 5, 97, 'خدا کعبه را خانه حرام قرار داد برای نگهبانی مردم، و نیز ماه حرام را قرار داد و نیز هَدْی و قلائد (قربانی نشان‌دار و بی‌نشان) را قرار داد، تا بدانید که خدا به هر چه در آسمانها و زمین است آگاه است و خدا به همه چیز داناست.'),
(767, 5, 98, 'بدانید که خدا هم عِقابش بسیار سخت و دشوار است و هم خدا بسیار بخشنده و مهربان است.'),
(768, 5, 99, 'بر پیغمبر جز تبلیغ (احکام الهی) وظیفه‌ای نیست، و خدا هر چه را آشکار داشته و یا پنهان دارید همه را می‌داند.'),
(769, 5, 100, 'بگو (ای پیغمبر): هرگز مردم پاک و ناپاک یکسان نخواهند بود هر چند (در جهان) بسیاریِ پلیدان تو را به شگفت آرد. پس ای خردمندان از خدا بترسید، باشد که رستگار شوید.'),
(770, 5, 101, 'ای اهل ایمان، هرگز از چیزهایی مپرسید که اگر فاش گردد شما را زشت و بد می‌آید و غمناک می‌کند، و اگر پرسش آن را به هنگام نزول آیات قرآن واگذارید برای شما (هر چه مصلحت است) آشکار می‌گردد. خدا از عقاب سؤالات بی‌جای شما درگذشت، و خدا بخشنده و بردبار است.'),
(771, 5, 102, 'قومی پیش از شما هم سؤال از آن امور نمودند، آن گاه که برایشان بیان شد به آن کافر شدند.'),
(772, 5, 103, 'خدا برای بَحیره و سائبه و وصیله و حام (نام شتران و گوسفندانی که مشرکان با شرایط خاصی آزاد می‌کردند) حکمی مقرّر نفرموده، لیکن کافران بر خدا دروغ می‌بندند، چه آنکه بیشتر آنان عقل را به کار نمی‌بندند.'),
(773, 5, 104, 'و چون به آنها گفته شود: بیایید از حکم کتابی که خدا فرستاده و از دستور رسول او پیروی کنید، گویند: آن دینی که پدران خود را بر آن یافتیم ما را کفایت است. آیا باید از پدران خود هر چند مردمی جاهل بوده و به حق راه نیافته باشند باز پیروی کنند؟'),
(774, 5, 105, 'ای اهل ایمان، شما (ایمان) خود را محکم نگاه دارید، که اگر همه عالم گمراه شوند و شما به راه هدایت باشید زیانی از آنها به شما نرسد. بازگشت همه شما به سوی خداست و همه شما را به آنچه کردید آگاه می‌سازد.'),
(775, 5, 106, 'ای اهل ایمان، چون یکی از شما را هنگام مرگ فرا رسید، برای وصیّت خود دو شاهد عادل را گواه گیرید که از خودتان باشند، یا دو تن دیگر از غیر خودتان (از غیر مسلمانان) گواه گیرید اگر در سفر به شما مصیبت مرگ رسد (و شاهد خودی نیابید)؛ آن دو شاهد را نگاه دارید اگر از آنها بدگمانید، تا بعد از نماز سوگند خورند که ما برای شهادت خود هرگز بهایی نمی‌خواهیم هر چند شهادت بر خویشانمان باشد و گواهی خود را که به امر خداست کتمان نخواهیم کرد، که اگر کتمان شهادت کنیم البته از گنهکاران خواهیم بود.'),
(776, 5, 107, 'پس اگر بر احوال آن دو شاهد اطلاعی حاصل شد که مرتکب گناهی شده‌اند (و در شهادت خیانت کرده‌اند) دو شاهد دیگر که احق (به ارث یا شهادت) باشند به جای آنها قیام کنند و به خدا سوگند یاد کنند که شهادت ما راست‌تر از شهادت آن دو شاهد پیشین است و ما (از حق اصلاً) تجاوز نکردیم، که در آن صورت از ستمکاران باشیم.'),
(777, 5, 108, 'این گونه که بیان شد نزدیکتر به آن است که شهادت را بر وجه خود ادا کنند، یا از اینکه باز قسم را (اوصیاء بر ورثه) رد کنند بیمناک باشند. و از خدا بترسید و (سخن حق را) بشنوید، و خدا مردم بدکار را هدایت نخواهد کرد.'),
(778, 5, 109, 'روزی که خدا همه پیغمبران را جمع گرداند و آن گاه گوید: چگونه امت از دعوت شما اجابت کردند؟ گویند: ما نمی‌دانیم، تویی که به باطن همه کاملاً دانایی.'),
(779, 5, 110, '(ای پیغمبر، مردم را متذکر گردان) آن گاه که خدا گفت: ای عیسیِ مریم: به خاطر آر نعمتی را که به تو و مادرت عطا کردم آن گاه که تو را به تأیید روح قدسی توانا ساختم که در گهواره و بزرگسالی با مردم سخن می‌گفتی، و آن گاه که تو را تعلیم کتاب و حکمت کردم و به تو علم تورات و انجیل آموختم، و هنگامی که از گِل، شکل مرغی به امر من ساخته و در آن می‌دمیدی تا به امر من مرغی می‌گردید، و کور مادرزاد و پیس را به امر من شفا می‌دادی، و آن گاه که مردگان را به امر من (از قبر) بیرون می‌آوردی، و آن گاه که (دست ستم) بنی اسرائیل را از سر تو کوتاه کردم وقتی که تو با معجزات روشن بر (هدایت) آنها آمدی و کافران بنی اسرائیل گفتند که اینها جز سحری آشکار نیست.'),
(780, 5, 111, 'و (یاد کن) هنگامی که به حواریّین وحی کردیم که به من و به رسول من ایمان آورید، گفتند: ایمان آوردیم، (خدایا) گواه باش که ما تسلیم امر توایم.'),
(781, 5, 112, '(یاد آر) هنگامی که حواریّین گفتند: ای عیسیِ مریم، آیا خدای تو تواند که برای ما از آسمان مائده‌ای فرستد؟ عیسی در جواب گفت: اگر ایمان آورده‌اید از خدا بترسید (و شکّ در قدرت خدا نکنید).'),
(782, 5, 113, 'گفتند: می‌خواهیم هم از آن مائده آسمانی تناول کنیم و هم دلهای ما مطمئن شود و هم به راستی عهدهای تو پی بریم و بر آن (به مشاهده) از گواهان باشیم.'),
(783, 5, 114, 'عیسیِ مریم گفت: بارالها، ای پروردگار ما، تو بر ما از آسمان مائده‌ای فرست تا (این روز) برای ما و کسانی که پس از ما آیند روز عید مبارکی گردد و آیت و حجّتی از جانب تو برای ما باشد، و ما را روزی ده، که تو بهترین روزی دهندگانی.'),
(784, 5, 115, 'خدا گفت: من آن مائده را برای شما می‌فرستم، ولی هر که بعد از نزول مائده کافر شود وی را عذابی کنم که هیچ یک از جهانیان را چنان عذاب نکنم.'),
(785, 5, 116, 'و (یاد کن) آن گاه که (در قیامت) خدا گوید: ای عیسیِ مریم، آیا تو مردم را گفتی که من و مادرم را دو خدای دیگر سوای خدای عالم اختیار کنید؟ عیسی گوید: خدایا، تو منزهی، هرگز مرا نرسد که چنین سخنی به ناحق گویم، چنانچه من این گفته بودمی تو می‌دانستی، که تو از سرائر من آگاهی و من از سرّ تو آگاه نیستم، همانا تویی که به همه اسرار غیب جهانیان کاملاً آگاهی.'),
(786, 5, 117, 'من به آنها هرگز چیزی نگفتم جز آنچه تو مرا بدان امر کردی، که خدای را پرستید که پروردگار من و شماست، و من بر آن مردم گواه و ناظر اعمال بودم مادامی که در میان آنها بودم، و چون روح مرا گرفتی تو خود نگهبان و ناظر اعمال آنان بودی، و تو بر همه چیز عالم گواهی.'),
(787, 5, 118, 'اگر آنان را عذاب کنی باز (خدایا) همه بندگان تو هستند، و اگر از گناه آنها درگذری باز توانا و درست کرداری.'),
(788, 5, 119, 'خدا گوید: این روزی است که صادقان از راستی خود بهره‌مند می‌شوند، برای آنها بهشتهایی است که از زیر درختانش نهرها جاری است و در آن به نعمت ابدی متنعّمند؛ خدا از آنها خشنود و آنها از خدا خشنودند. این است فیروزی و سعادت بزرگ.'),
(789, 5, 120, 'فرمانروایی آسمانها و زمین و هر چه در آنهاست خدای راست، و او بر هر چیز تواناست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ستایش خدای راست که آسمانها و زمین را آفرید و تاریکی‌ها و روشنایی را مقرّر داشت (و با آنکه نظم آسمان و زمین دلیل یکتایی اوست) باز کافران به پروردگار خود شرک می‌آورند.'),
(791, 6, 2, 'اوست خدایی که شما را از گِلی آفرید، پس فرمان اجل و مرگ را بر همه کس معین کرد، و اجلی که معین و معلوم است به نزد اوست، باز شما شک در آیات او می‌کنید.'),
(792, 6, 3, 'و اوست خدا در همه آسمانها و در زمین، که از نهان و آشکار شما با خبر است و بدانچه می‌کنید آگاه است.'),
(793, 6, 4, 'و هیچ آیتی از آیات الهی بر اینان نیامد جز آنکه (از روی جهل و عناد) از آن روی گردانیدند.'),
(794, 6, 5, 'حق را که بر آنها آمد جدّاً تکذیب کردند، پس به زودی خبر آنچه که آن را به سخریه گرفتند به آنان می‌رسد.'),
(795, 6, 6, 'آیا ندیدند پیش از ایشان چه بسیار گروهی را هلاک نمودیم و حال آنکه در زمین به آنها قدرت و تمکّنی داده بودیم که شما را نداده‌ایم؟ و از آسمان بر آنها باران (رحمت ظاهر و رحمت باطن که کتاب آسمانی است) پیوسته فرستادیم و نهرها در زمین جاری ساختیم، پس چون نافرمانی کردند آن‌ها را هلاک نمودیم و گروهی دیگر بعد از آنها پدید آوردیم.'),
(796, 6, 7, 'و اگر ما کتابی بر تو فرستیم در کاغذی که آن را به دست خود لمس کنند، باز کافران گویند: این نیست مگر سحری آشکار.'),
(797, 6, 8, 'و گفتند: چرا (اگر محمد صلّی اللّه علیه و آله و سلّم رسول است) بر او فرشته‌ای نازل نشد؟ و چنانچه فرشته‌ای بفرستیم کار تمام شود و دیگر لحظه‌ای آنها مهلت نخواهند یافت.'),
(798, 6, 9, 'و چنانچه رسول را فرشته‌ای قرار می‌دادیم باز هم او را به صورت مردی در می‌آوردیم و بی‌شک سبب می‌شدیم که همان اشتباهی که برای دیگران پیش می‌آوردند برای خودشان پیش آید.'),
(799, 6, 10, '(ای پیغمبر) پیش از تو هم امم گذشته پیغمبران خود را سخت استهزاء کردند، پس آنان را کیفر این عمل دامنگیر شد.'),
(800, 6, 11, 'بگو (ای پیغمبر) که در روی زمین بگردید آن گاه بنگرید که عاقبت سخت آنها که تکذیب کردند چگونه بود!'),
(801, 6, 12, '(به مشرکان) بگو که آنچه در آسمانها و زمین است ملک کیست؟ (اگر آنها نگویند) تو باز گو همه ملک خداست که بر خویش رحمت و بخشایش را فرض و لازم کرده، البته شما را در روز قیامت که بی‌هیچ شک خواهد آمد جمع می‌گرداند، ولی کسانی که خود را به زیان افکندند ایمان نمی‌آورند.'),
(802, 6, 13, 'و هر چه در شب و روز آرامش یافته همه ملک خداست و او شنوا و داناست.'),
(803, 6, 14, 'بگو: آیا غیر خدا را به یاری و دوستی برگزینم؟ در صورتی که آفریننده آسمان و زمین خداست و او روزی می‌بخشد و خود از طعام بی‌نیاز است. بگو: من مأمورم که اول شخصی که تسلیم حکم خداست باشم. و البته از گروهی که به خدا شرک آورند نباش.'),
(804, 6, 15, 'بگو: من اگر نافرمانی خدای خود کنم از عذاب آن روز بزرگ سخت می‌ترسم.'),
(805, 6, 16, 'هر کس در آن روز از عذاب برهد همانا به رحمت خدا نائل گردیده است، و این فیروزی و سعادتی آشکار است.'),
(806, 6, 17, 'و اگر از خدا به تو ضرری رسد هیچ کس جز خدا نتواند تو را از آن ضرر برهاند، و اگر از او به تو خیری رسد (هیچ کس تو را از آن منع نتواند کرد) که او بر هر چیز تواناست.'),
(807, 6, 18, 'بگو: خداست که کمال اقتدار و توانایی را بر بندگان دارد و اوست که درستکار و آگاه است.'),
(808, 6, 19, 'بگو: گواهی کدام چیز بزرگتر است (از گواهی خدا)؟ بگو: خدا میان من و شما گواه است و وحی شده به من آیات این قرآن تا به آن شما و هر کس را که خبر این قرآن به او رسد بترسانم. آیا شما به راستی گواهی دهید که با خدای یکتا خدایان دیگری وجود دارد؟ بگو: من گواهی ندهم. بگو: محقّقاً او خدایی یکتاست و من بیزارم از آنچه شما شریک خدا قرار می‌دهید.'),
(809, 6, 20, 'آنان که ما کتاب به آنها فرستادیم، او را (یعنی محمّد صلّی اللّه علیه و آله و سلّم را) به مانند فرزندان خود می‌شناسند. آنان که خود را به زیان انداختند (به او) ایمان نمی‌آورند.'),
(810, 6, 21, 'و کیست ستمکارتر از آن کس که بر خدا دروغ بست یا آیات او را تکذیب کرد؟ هرگز ستمکاران را رستگاری نخواهد بود.'),
(811, 6, 22, 'و (یاد کن) روزی که همه آنها را جمع آوریم پس به آنها که شرک آوردند گوییم: به کجا شدند آنها که به گمان شما شریک خدا بودند؟'),
(812, 6, 23, 'آن گاه پس از آن همه فریفتگی به بتان، در پیشگاه حق عذری نیابند جز آنکه (از بتان بیزاری جسته و) گویند: سوگند به خدا، پروردگارمان، که هرگز شرک نیاوردیم.'),
(813, 6, 24, 'بنگر که چگونه بر خود دروغ بستند و آنچه به دروغ به خدا بسته و شریک قرار می‌دادند همه از دست آنها رفت و محو و نابود شد.'),
(814, 6, 25, 'و بعضی از آنان به سخن تو گوش فرا دارند، ولی ما پرده بر دلهاشان نهاده‌ایم که فهم آن نتوانند کرد و در گوشهای آنها سنگینی نهاده‌ایم، و اگر همه آیات الهی را مشاهده کنند باز بدان ایمان نمی‌آورند تا آنجا که چون نزد تو آیند در مقام مجادله بر آمده، آن کافران گویند: این آیات چیزی جز افسانه‌های پیشینیان نیست.'),
(815, 6, 26, 'همین کسانند که هم مردم را از (فیض) آن منع می‌کنند و هم خود را محروم می‌دارند و غافل از آنند که تنها خود را به هلاکت می‌افکنند.'),
(816, 6, 27, 'و اگر ببینی حال آنها را هنگامی که بر آتش دوزخشان بازدارند که در آن حال (با نهایت حسرت) گویند: ای کاش ما را به دنیا باز می‌گردانیدند تا دیگر آیات خدا را تکذیب نکرده و ایمان می‌آوردیم!'),
(817, 6, 28, 'بلکه (وبال) آنچه پیش از این پنهان می‌داشتند بر آنها آشکار شده و اگر بار دیگر هم (به دنیا) برگردند باز همان اعمال زشتی را که از آن نهی شدند اعاده خواهند کرد، و آنان دروغ می‌گویند.'),
(818, 6, 29, 'و کافران گفتند: جز همین زندگی دنیایی ما دیگر زندگی دیگری نخواهد بود و ما هرگز (بعد از مرگ) زنده نخواهیم شد.'),
(819, 6, 30, 'و اگر سختی حال آنها را آن‌گاه که در پیشگاه (عدل) خدا بازداشته شوند مشاهده کنی! که خدا به آنها خطاب کند: آیا این (عذاب قیامت) حق نبود؟ جواب دهند: چرا، سوگند به ذات پروردگارمان (که حق بود) پس خدا گوید که اینک عذاب را به کیفر کفرتان بچشید.'),
(820, 6, 31, 'آنان که لقاء خدا را تکذیب کردند البته زیانکار شدند، تا آن گاه که ساعت قیامت ناگهان آنها را فرا رسد گویند: دریغا که در این باره کوتاهی کردیم! پس بار گناهان خویش را بر پشت گیرند. زنهار که بد بار گرانی به دوش می‌گیرند.'),
(821, 6, 32, 'و زندگی دنیا جز بازی و هوسرانی هیچ نیست و همانا سرای دیگر برای اهل تقوا نیکوتر است، آیا تعقل نمی‌کنید؟'),
(822, 6, 33, 'ما به تحقیق می‌دانیم که کافران در تکذیب تو سخنانی می‌گویند که تو را افسرده و غمگین می‌سازد. (دل شاد دار) که آن ستمکاران نه تو را تکذیب می‌کنند بلکه آیات خدا را انکار می‌کنند.'),
(823, 6, 34, 'و همانا پیغمبران پیش از تو را هم تکذیب کردند و آنها بر همه تکذیبها و اذیت‌های منکران صبر و تحمل کردند تا آن گاه که یاری ما شامل حال آنها شد. و (دل قوی‌دار که) هیچ کس کلمات خدا را تغییر نتواند داد و تحقیقاً اخبار پیغمبران پیش به تو رسیده است.'),
(824, 6, 35, 'و چنانچه انکار و اعتراض آنها تو را سخت و گران می‌آید اگر توانی نقبی در زمین بساز یا نردبانی بر آسمان برافراز تا آیتی بر آنها آوری! و اگر خدا می‌خواست همه را مجتمع بر هدایت می‌کرد؛ پس تو البته از جاهلان مباش.'),
(825, 6, 36, '-تنها زنده دلان عالم که گوش شنوا دارند اجابت می‌کنند، و مردگان را خدا برمی‌انگیزد، آن گاه خلایق به سوی حق بازگردانده می‌شوند.'),
(826, 6, 37, '-و کافران گفتند: چرا بر او معجزه‌ای از خدا فرو نیامد؟ بگو: خدا بر اینکه آیتی فرستد قادر است، و لیکن بیشتر آنان نمی‌دانند.'),
(827, 6, 38, '-و هر جنبنده‌ای در زمین و هر پرنده‌ای در هوا که به دو بال پرواز می‌کند همگی طایفه‌هایی مانند شما (نوع بشر) هستند. ما در کتاب (آفرینش، بیان) هیچ چیز را فرو گذار نکردیم، آن گاه همه به سوی پروردگار خود محشور می‌شوند.'),
(828, 6, 39, '-و آنان که تکذیب آیات ما کردند کر و گنگ در ظلمات (جهل) به سر برند. مشیّت الهی هر کس را خواهد گمراه سازد و هر که را خواهد به راه راست هدایت کند.'),
(829, 6, 40, '-بگو: اگر راست می‌گویید، مرا گویید که اگر عذاب خدا یا ساعت مرگ بر شما فرا رسد آیا (در آن ساعت سخت) غیر خدا را می‌خوانید؟!'),
(830, 6, 41, '-بلکه در آن هنگام تنها خدا را می‌خوانید تا اگر مشیّت او قرار گرفت شما را از سختی برهاند، و آنچه را که با خدا شریک قرار می‌دهید به کلی فراموش می‌کنید.'),
(831, 6, 42, '-و همانا ما پیغمبرانی به سوی امتان پیش از تو فرستادیم، پس (چون اطاعت نکردند) به بلاها و مصیبتها گرفتارشان ساختیم، شاید به درگاه خدا گریه و زاری کنند.'),
(832, 6, 43, '-پس چرا وقتی که بلای ما به آنها رسید تضرّع و زاری نکردند (تا نجات یابند)؟ ولی (بدین سبب که نکردند) دلهاشان را قساوت فرا گرفت و شیطان کردار زشت آنان را در نظرشان زیبا نمود.'),
(833, 6, 44, '-پس چون آنچه به آنها تذکر داده شد همه را فراموش کردند ما هم ابواب هر چیز را به روی آن‌ها گشودیم تا چون به نعمتی که به آنها داده شد شادمان و مغرور شدند ناگاه آنها را (به کیفر اعمالشان) گرفتار کردیم و آن هنگام (خوار و) نا امید گردیدند.'),
(834, 6, 45, '-پس ریشه گروه ظالمان کنده شد، و ستایش خدای را که پروردگار جهانیان است.'),
(835, 6, 46, '-بگو: مرا گویید، اگر خدا گوش و چشمهای شما را گرفت و مهر بر دل شما نهاد (تا کور و کر و جاهل شدید) کدام خدایی غیر از خدا هست که این نعمت‌ها را به شما باز دهد؟! بنگر که ما چگونه آیات را به انواع گوناگون بیان می‌کنیم باز آنها اعراض می‌کنند!'),
(836, 6, 47, '-بگو: چه خواهید کرد اگر عذاب خدا شما را به ناگاه، پنهان یا آشکار در رسد، آیا کسی به جز گروه ستمکار هلاک خواهد شد؟'),
(837, 6, 48, 'و ما پیغمبران را جز برای آنکه مژده دهند و بترسانند نفرستادیم، پس هر کس ایمان آورد و کار شایسته کرد هرگز بر آنان بیمی نیست و ابدا اندوهگین نخواهند بود.'),
(838, 6, 49, 'و هر که آیات ما را تکذیب کرد به آنان در اثر فسق و زشتکاری عذاب خواهد رسید.'),
(839, 6, 50, '-بگو: من شما را نمی‌گویم که گنج‌های خدا نزد من است و نه آنکه از غیب (الهی) آگاهم و نمی‌گویم که من فرشته‌ام، من پیروی نمی‌کنم جز آنچه را که به من وحی می‌رسد. بگو: آیا کور و بینا برابرند؟ آیا فکر و اندیشه نمی‌کنید؟'),
(840, 6, 51, '-و آنان را که از احضار در پیشگاه عدل خدا ترسانند به آیات قرآن متنبّه ساز و بترسان که جز خدا آنها را یاور و شفیعی نیست، باشد که پرهیزکار شوند.'),
(841, 6, 52, 'و آنان را که صبح و شام خدا را می‌خوانند و قصدشان فقط خداست از خود مران، که نه چیزی از حساب آنها بر تو و نه چیزی از حساب تو بر آنهاست، پس اگر آن خداپرستان را از خود برانی از ستمکاران خواهی بود.'),
(842, 6, 53, 'و همچنین ما برخی را به برخی دیگر بیازمودیم تا آنکه به (طعن) گویند: آیا بر این فقیران، خدا در میان ما (به نعمت اسلام) منّت نهاد؟! آیا خدا (از این منکران) به احوال سپاسگزاران (فقرا) داناتر نیست؟!'),
(843, 6, 54, 'هرگاه آنان که به آیات ما می‌گروند نزد تو آیند بگو: سلام بر شما باد، خدا بر خود رحمت و مهربانی را فرض نمود، که هر کس از شما کار زشتی به نادانی کرد و بعد از آن توبه کند و اصلاح نماید البته خدا بخشنده و مهربان است.'),
(844, 6, 55, 'و همچنین ما آیات را مفصل بیان کنیم (تا حق روشن شود) و تا راه گنهکاران معین و آشکار شود.'),
(845, 6, 56, 'بگو که مرا از پرستش آن خدایان باطل که شما می‌پرستید منع فرموده‌اند. بگو: من پیروی هوسهای شما نکنم تا مبادا گمراه شده و راه هدایت نیابم.'),
(846, 6, 57, 'بگو: من هر چه می‌گویم با برهانی از سوی پروردگارم است و شما تکذیب آن می‌کنید. عذابی که شما بدان تعجیل می‌کنید امر آن به دست من نیست، فرمان جز خدا را نخواهد بود، که به حق دستور می‌دهد و او بهترین حکم فرمایان است.'),
(847, 6, 58, 'بگو: اگر به دست من بود عذابی که به تعجیل می‌طلبید کار میان من و شما خاتمه می‌یافت، و خدا به احوال ظالمان داناتر است.'),
(848, 6, 59, 'و کلیدهای خزائن غیب نزد اوست، کسی جز او بر آن آگاه نیست و نیز آنچه در خشکی و دریاست همه را می‌داند و هیچ برگی از درخت نمی‌افتد مگر آنکه او آگاه است و نه هیچ دانه‌ای در زیر تاریکی‌های زمین و نه هیچ تر و خشکی، جز آنکه در کتابی مبین مسطور است.'),
(849, 6, 60, '-و اوست خدایی که چون شب (به خواب می‌روید) جان شما را برمی‌گیرد (و نزد خود می‌برد) و کردار شما را در روز می‌داند، و پس از آن (خواب) شما را بر می‌انگیزد تا اجلی که در قضا و قدر او معین است به پایان رسد، سپس (هنگام مرگ) به سوی او باز می‌گردید آن گاه به نتیجه آنچه کرده‌اید شما را آگاه گرداند.'),
(850, 6, 61, 'و اوست خدایی که قهر و اقتدارش مافوق بندگان است و فرشتگانی را به نگهبانی بر شما می‌فرستد، تا آن گاه که هنگام مرگ یکی از شما فرا رسد رسولان ما او را می‌میرانند و در قبض روح شما هیچ کوتاهی نخواهند کرد.'),
(851, 6, 62, 'سپس به سوی خدای عالم که به حقیقت مولای بندگان است بازگردانده می‌شوند. آگاه باشید حکم (خلق) با خداست و او زودترین حسابرسان است.'),
(852, 6, 63, 'بگو: آن کیست که شما را از تاریکی‌های بیابان و دریا نجات می‌دهد گاهی که او را به تضرع و زاری و از باطن قلب می‌خوانید که اگر ما را از این مهلکه نجات داد پیوسته شکرگزار او خواهیم بود؟'),
(853, 6, 64, 'بگو: خداست که شما را از آن تاریکی‌ها نجات می‌دهد و از هر اندوهی می‌رهاند، باز هم به او شرک می‌آورید.'),
(854, 6, 65, 'بگو: او تواناست که بر شما عذابی از آسمان یا زمین فرستد یا شما را به اختلاف کلمه و پراکندگی درافکند و بعضی را به عذاب بعضی گرفتار کند بنگر چگونه ما آیات خود را به طرق مختلف بیان می‌کنیم، شاید مردم چیزی بفهمند.'),
(855, 6, 66, 'و قوم تو آن را تکذیب کردند در صورتی که حق محض همان بود. بگو: من نگهبان شما نیستم.'),
(856, 6, 67, 'برای هر خبری وقت معینی است و به زودی آگاه می‌شوید.'),
(857, 6, 68, 'و چون گروهی را دیدی که برای خرده گیری و طعن زدن در آیات ما گفتگو می‌کنند از آنان دوری گزین تا در سخنی دیگر وارد شوند، و چنانچه شیطان البته فراموشت ساخت بعد از آنکه متذکّر (کلام خدا) شدی دیگر با گروه ستمکاران مجالست مکن.'),
(858, 6, 69, 'بر کسانی که پرهیزکارند عقوبت حساب بدکاران نخواهد بود، لیکن بر آنهاست که (بدان را پند داده و) متذکر سازند، شاید پرهیز کنند.'),
(859, 6, 70, 'و آنان را که دین خود را بازیچه و سرگرمی گرفتند و زندگانی دنیا آنها را فریب داد به حال خود واگذار، همین قدر (آنها را) به این قرآن تذکر ده، تا مبادا کسی به عمل خود عاقبت گرفتار شود و او را جز خدا دادرس و شفیعی نباشد، و اگر هر چه (برای آسایش خود از عذاب) فدا دهد از او نپذیرند. آنها همان کسانی هستند که عاقبت به کیفر اعمالشان به هلاکت می‌رسند و به کیفر کفرشان شرابی از حمیم جهنم و عذابی دردناک دارند.'),
(860, 6, 71, 'بگو: آیا ما خدا را رها کرده و چیزی را (مانند بتان) که هیچ قادر به نفع و ضرر ما نباشد به خدایی بخوانیم و بعد از آنکه خدا ما را هدایت نمود باز به خوی جاهلیت برگردانده شویم؟! مانند کسی که فریب و اغوای شیطان او را در زمین سرگردان ساخته است در حالی که او را یارانی است که او را به راه راست می‌خوانند که نزد ما بیا! بگو: هدایت خدا به حقیقت هدایت است و ما مأموریم که تسلیم فرمان خدای جهانیان باشیم.'),
(861, 6, 72, 'و نماز به پا دارید و از خدا بترسید و اوست که به سوی او محشور می‌شوید.'),
(862, 6, 73, 'و اوست خدایی که آسمانها و زمین را به حق آفرید و روزی که بگوید: موجود باش، آن چیز بی‌درنگ موجود خواهد شد. سخن او حق است و پادشاهی و فرمان عالم روزی که در صور بدمند تنها با اوست و دانای نهان و آشکار است و هم او به تدبیر خلق دانا و (بر همه چیز) آگاه است.'),
(863, 6, 74, 'و (یاد کن) وقتی که ابراهیم به پدرش آزر (عمو و مربیش بود) گفت: آیا بتها را به خدایی اختیار کرده‌ای؟! من تو و پیروانت را در گمراهی آشکار می‌بینم.'),
(864, 6, 75, 'و این چنین ما به ابراهیم ملکوت و باطن آسمانها و زمین را ارائه دادیم (تا یکتایی پروردگارش را دریابد) و تا به مقام اهل یقین رسد.'),
(865, 6, 76, 'پس چون شب بر او نمودار شد ستاره درخشانی دید، گفت: این پروردگار من است. چون آن ستاره غروب کرد گفت: من چیزهای غروب کردنی و ناپدید شدنی را دوست ندارم (به خدایی نخواهم گرفت).'),
(866, 6, 77, 'پس چون ماه تابان را دید گفت: این خدای من است. وقتی آن هم ناپدید گردید گفت: اگر خدای من مرا هدایت نکند همانا من از گمراهانم.'),
(867, 6, 78, 'پس چون خورشید درخشان را دید گفت: این است خدای من، این بزرگتر است. چون آن هم ناپدید گردید گفت: ای قوم من، من از آنچه شما شریک خدا قرار می‌دهید بیزارم.'),
(868, 6, 79, 'من با ایمان خالص روی به سوی خدایی آوردم که آفریننده آسمانها و زمین است و من هرگز از مشرکان نیستم.'),
(869, 6, 80, 'قوم ابراهیم با او در مقام خصومت و احتجاج برآمدند، گفت: آیا با من درباره خدا محاجّه می‌کنید؟ و حال آنکه خدا مرا هدایت کرده و هیچ از آنچه شما شریک خدا می‌خوانید بیمی ندارم مگر آنکه خدا بر من چیزی بخواهد، پروردگار من علمش به همه موجودات محیط است، آیا متذکر نمی‌شوید؟!'),
(870, 6, 81, 'و من چگونه از آنچه شما شریک خدا قرار می‌دهید بترسم در حالی که شما از شرک آوردن به خدا نمی‌ترسید با آنکه هیچ برهان و حجتی را بر آن شرک بر شما نازل نکرده است؟! آیا کدام یک از ما دو گروه به ایمنی سزاوارتریم اگر شما فهم سخن می‌کنید؟!'),
(871, 6, 82, 'آنان که ایمان آورده و ایمان خود را به ستمی نیالودند ایمنی آنها راست و هم آنها حقیقتا هدایت یافته‌اند.'),
(872, 6, 83, 'این است حجّتی که ما ابراهیم را بر قومش دادیم. ما هر که را بخواهیم به درجات رفیع بالا می‌بریم، که خدای تو به نظام و صلاح عالمیان آگاه و داناست.'),
(873, 6, 84, 'و ما به ابراهیم، اسحاق و یعقوب را عطا کردیم و همه را به راه راست بداشتیم و نوح را پیش از ابراهیم و فرزندانش داود و سلیمان و ایوب و یوسف و موسی و هارون را هدایت نمودیم و این چنین نیکوکاران را پاداش می‌دهیم.'),
(874, 6, 85, 'و هم زکریّا و یحیی و عیسی و الیاس را (هدایت کردیم) که همه از نیکوکارانند.'),
(875, 6, 86, 'و نیز اسماعیل و یسع و یونس و لوط را (هدایت کردیم) و ما همه آن پیغمبران را بر عالمیان شرافت و برتری دادیم.'),
(876, 6, 87, 'و نیز برخی از پدران و فرزندان و برادران آنها را فضیلت داده و آنان را بر دیگران برگزیدیم و به راه راست هدایت نمودیم.'),
(877, 6, 88, 'این است راهنمایی خدا که به آن هر یک از بندگانش را بخواهد هدایت می‌کند، و اگر به خدا شرک می‌آوردند اعمال آنها نابود می‌شد.'),
(878, 6, 89, 'آنها کسانی بودند که ما به آنها کتاب و فرمان و مقام نبوّت عطا کردیم، پس اگر این قوم به اینها کافر شوند ما قومی را که هرگز کافر نشوند (مانند علی و شیعیانش که در دل و جان ایمان دارند) بدینها برگمارده‌ایم.'),
(879, 6, 90, 'آنها کسانی بودند که خدا خود آنها را هدایت نمود، تو نیز از راه آنها پیروی نما، و (امت را) بگو که من مزد رسالت از شما نمی‌خواهم، این نیست جز یاد آوری و پندی برای اهل عالم (که به یاد خدا متذکر شوند).'),
(880, 6, 91, 'و آنها خدا را آن گونه که باید، نشناختند که گفتند: خدا بر هیچ کس از بشر کتابی نفرستاده. بگو: کتاب توراتی را که موسی آورد و در آن نور (علم) و هدایت خلق بود چه کسی بر او فرستاد که شما آیات آن را در اوراق نگاشته، بعضی را آشکار می‌نمایید و بسیاری را پنهان می‌دارید و آنچه را شما و پدرانتان نمی‌دانستید به شما آموختند؟ بگو: خداست (که رسول و کتاب فرستد)، سپس آنها را بگذار تا به بازیچه خود فرو روند.'),
(881, 6, 92, 'و این (قرآن) کتابی است که ما فرستادیم با خیر و برکت بسیار و گواه صدق سایر کتب آسمانی که در مقابل اوست (تا مردم را به پاداش الهی مژده دهی) و تا خلق را از اهل مکه و هر که به اطراف آن است بیم دهی. و آنان که به آخرت ایمان آوردند به این کتاب نیز ایمان خواهند آورد و آنها (اوقات) نمازشان را محافظت می‌نمایند.'),
(882, 6, 93, 'و کیست ستمکارتر از آن که بر خدا دروغی بندد و یا وحی به او نرسیده گوید: به من وحی می‌رسد، و نیز گوید: من هم محققا مانند آن (کتاب) که خدا فرستاده خواهم آورد. و اگر (فضاحت و سختی حال) ستمکاران را ببینی آن گاه که در سکرات موت گرفتار می‌شوند! و فرشتگان (برای قبض روح آنها) دست (قهر و قدرت) بر آورند و گویند: جان از تن به در کنید، امروز کیفر عذاب و خواری می‌کشید چون بر خدا سخن به ناحق می‌گفتید و از (حکم) آیات او گردنکشی و تکبّر می‌نمودید.'),
(883, 6, 94, 'و محققا شما یکایک به سوی ما باز آمدید آن گونه که اول بار شما را بیافریدیم و آنچه را که (از مال و جاه) به شما داده بودیم همه را پشت سر وانهادید و آن شفیعان را که به خیال باطل شریک ما در خود می‌پنداشتید با شما نمی‌بینیم! همانا میان شما و آنان جدایی افتاد و آنچه (شفیع خود) می‌پنداشتید از دست شما رفت.'),
(884, 6, 95, 'خداست که (در جوف زمین) دانه و هسته را می‌شکافد، زنده را از مرده و مرده را از زنده پدید می‌آرد، آن (که چنین تواند کرد) خداست، پس به کجا گردانیده می‌شوید (و نسبت خدایی را به آنان که نتوانند می‌دهید).'),
(885, 6, 96, 'خداست شکافنده پرده صبحگاهان، و شب را برای آسایش (خلق) مقرّر داشته و خورشید و ماه را به نظمی معین (او به گردش) درآورده، این تقدیر خدای مقتدر داناست.'),
(886, 6, 97, 'و نیز اوست که چراغ ستارگان را برای رهیابی شما در تاریکی‌های بیابان و دریا قرار داده است. همانا ما آیات خود را برای مردم دانا به تفصیل بیان کردیم.'),
(887, 6, 98, 'و هم اوست خدایی که همه شما را از یک تن در آرامگاه (رحم) و ودیعتگاه (صلب) بیافرید. ما نیکو آیات خود را برای اهل بصیرت مفصل بیان نمودیم.'),
(888, 6, 99, 'و هم اوست خدایی که از آسمان آبی فرو بارید پس هر نبات را بدان رویاندیم و سبزه‌ها را از زمین برون آوردیم و از آن سبزه‌ها دانه‌هایی که بر روی هم چیده شده بیرون آریم و از شکوفه نخل خوشه‌هایی است به هم پیوسته، و باغهای انگور و زیتون و انار که برخی شبیه و برخی نامشابه به هم است (خلق کنیم)، شما در آن باغها هنگامی که میوه آن پدید آید و برسد (با چشم تعقل) بنگرید، که در آن آیات و نشانه‌هایی (از قدرت خدا) برای اهل ایمان هویداست.'),
(889, 6, 100, 'و گروهی از کافران اهریمنان را شریک خدا شمردند در حالی که آن‌ها آفریده خدا هستند و گروهی (عزیر و مسیح و فرشتگان را) از روی نادانی پسران و دخترانی برای او تراشیدند، در صورتی که او از همه این نسبت‌ها برتر و منزه است.'),
(890, 6, 101, 'اوست پدید آرنده آسمانها و زمین، چگونه وی را فرزندی تواند بود در حالی که او را جفتی نیست؟! و او همه چیز را آفریده و به همه چیز داناست.'),
(891, 6, 102, 'این است (وصف) پروردگار شما، که جز او هیچ خدایی نیست، آفریننده هر چیز است، پس او را پرستش کنید، و او نگهبان همه موجودات است.'),
(892, 6, 103, 'او را هیچ چشمی درک ننماید و او همه بینندگان را مشاهده می‌کند و او لطیف و نامرئی و (به همه چیز خلق) آگاه است.'),
(893, 6, 104, 'آیات الهی و کتب آسمانی که سبب بصیرت شماست البته از طرف پروردگارتان آمد، پس هر کس بصیرت یافت خود به سعادت رسید و هر کس که کور بماند خود در زیان افتاد، و من نگهبان شما نیستم.'),
(894, 6, 105, 'و این چنین ما آیات قرآن را به انواع گوناگون بیان کنیم (تا وسیله هدایت شود) ولی عاقبت کافران نادان گویند: تو (اینها را) به درس آموخته‌ای! و تا آن را برای اهل دانش بیان کنیم.'),
(895, 6, 106, 'ای پیغمبر هر چه از خدا به تو وحی شد از آن پیروی کن، که خدایی جز آن ذات یکتا نیست و از مشرکان روی بگردان.'),
(896, 6, 107, 'و اگر خدا می‌خواست آنها شرک نمی‌آوردند، و ما تو را نگهبان ایشان نکردیم و تو وکیل آنها نیستی.'),
(897, 6, 108, 'و (شما مؤمنان) به آنچه مشرکان غیر از خدا می‌خوانند دشنام ندهید تا مبادا آنها از روی ظلم و جهالت خدا را دشنام دهند. این چنین ما عمل هر قومی را در نظرشان زینت داده‌ایم، سپس بازگشت آنها به سوی پروردگارشان است و خدا آنان را به کردارشان آگاه می‌گرداند.'),
(898, 6, 109, 'و به خدا به سخت ترین سوگندهای خود قسم یاد کردند که البته اگر آیتی بیاید ایمان آرند. بگو: آیات از طرف خداست، و شما مؤمنان چه می‌دانید؟ (و چگونه به گفته این کافران مطمئن می‌شوید؟ آنها همان مردم معاندند که) اگر آیتی هم آید هرگز ایمان نمی‌آورند.'),
(899, 6, 110, 'و ما دل و دیده اینان را چنانکه اول بار ایمان نیاوردند اکنون نیز از ایمان بگردانیم و آنها را به حال طغیان و سرکشی واگذاریم تا در ورطه ضلالت فرومانند.'),
(900, 6, 111, 'و اگر فرشتگان را بر آنها می‌فرستادیم و مردگان با آنان سخن می‌گفتند و هر چیزی را رویارویشان فراهم می‌آوردیم باز ایمان نمی‌آوردند مگر به مشیّت خدا، لیکن اکثرشان (مشیّت نافذ حق را) نمی‌دانند.'),
(901, 6, 112, 'و همچنین ما هر پیغمبری را از شیطانهای انس و جن دشمنی در مقابل برانگیختیم که آنها برخی با برخی دیگر برای اغفال مؤمنان سخنان آراسته ظاهر فریب اظهار کنند. و اگر پروردگار تو می‌خواست چنین نمی‌کردند، پس اینها را با دروغشان واگذار.'),
(902, 6, 113, '(تا بیازماییمشان) و تا به گفتار (فریبنده) آن اهریمنان آنان که به آخرت ایمان نیاورند دل سپرده و بدان خشنود باشند و تا آنچه می‌توانند به گناه و تبهکاری آلوده شوند.'),
(903, 6, 114, 'آیا من غیر خدا حاکم و داوری بجویم و حال آنکه او خدایی است که کتابی (چون قرآن) که همه چیز در آن بیان شده به شما فرستاد؟ و آنان که به آنها کتاب فرستادیم (یهود و نصاری) می‌دانند که این قرآن از خدای تو به حق بر تو فرستاده شده، پس در آن البته هیچ شک و تردید راه مده.'),
(904, 6, 115, 'کلام خدای تو از روی راستی و عدالت به حد کمال رسید و هیچ کس تبدیل و تغییر آن کلمات نتواند کرد و او خدای شنوا و داناست.'),
(905, 6, 116, 'و اگر پیروی از اکثر مردم روی زمین کنی تو را از راه خدا گمراه خواهند کرد، که اینان جز از پی گمان (و هوا و هوس) نمی‌روند و جز اندیشه باطل و دروغ چیزی در دست ندارند.'),
(906, 6, 117, 'محققا خدای تو خود داناتر است به حال آن که از راه او گمراه است و آن که به راه او هدایت یافته است.'),
(907, 6, 118, 'پس شما مؤمنان چنانچه به آیات خدا ایمان دارید از آنچه نام خدا بر آن ذکر شده تناول کنید.'),
(908, 6, 119, 'چرا از آنچه نام خدا بر آن ذکر شده نمی‌خورید (و بر خود حرام می‌کنید)؟ در صورتی که آنچه خدا بر شما حرام کرده به تفصیل بیان نموده جز آنچه به آن ناگزیر می‌شوید که باز حلال است. و بسیاری از مردم به هوای نفس خود از روی جهل (دیگران را) به گمراهی کشند. همانا خدا به تجاوز کنندگان (از حدود) داناتر است.'),
(909, 6, 120, 'و هر گناه و عمل زشت را در ظاهر و باطن ترک کنید، که محققا کسانی که کسب گناه کنند به زودی به کیفر آن خواهند رسید.'),
(910, 6, 121, 'و از آنچه نام خدا بر آن ذکر نشده نخورید که آن فسق و تبه کاری است. و اهریمنان سخت به دوستان و پیروان خود وسوسه کنند تا با شما به جدل و منازعه برخیزند. و اگر شما هم از آنان پیروی کنید (مانند آنها) مشرک خواهید شد.'),
(911, 6, 122, 'و آیا کسی که مرده (جهل و ضلالت) بود ما او را زنده کردیم و به او روشنی (علم و دیانت) دادیم تا به آن روشنی میان مردم (سرافراز) رود مثل او مانند کسی است که در تاریکی‌ها (ی جهل) فرو رفته و از آن به در نتواند گشت؟ (آری) کردار بد کافران در نظرشان چنین جلوه‌گر شده است.'),
(912, 6, 123, 'و همچنین ما قرار دادیم که در هر دیاری رؤسای بدکار ستمگر (با مردم آنجا) مکر اندیشند، و در حقیقت مکر جز با خویشتن نمی‌کنند و به این هم آگاه نیستند.'),
(913, 6, 124, 'و چون آیتی برای (هدایت) آنها آمد گفتند: ما ایمان نیاوریم تا مانند آنچه به رسولان خدا داده شد به ما نیز داده شود. خدا بهتر می‌داند که در کجا رسالت خود را مقرر دارد. به زودی مجرمان را خدا خوار سازد و عذابی سخت به واسطه مکری که می‌اندیشیدند بر آنان فرو فرستد.'),
(914, 6, 125, 'پس هر که را خدا هدایت او خواهد قلبش را برای پذیرش اسلام باز و روشن گرداند و هر که را خواهد گمراه نماید (به حال گمراهی واگذارد) دل او را از پذیرفتن ایمان تنگ و سخت گرداند که گویی می‌خواهد از زمین بر فراز آسمان رود. این چنین خدا آنان را که به حق نمی‌گروند مردود و پلید می‌گرداند.'),
(915, 6, 126, 'و این راه خدای توست که مستقیم است. ما آیات (خود) را برای گروهی که بدان پند می‌گیرند به خوبی روشن ساختیم.'),
(916, 6, 127, 'آنها را نزد خدا دار سلامت و خانه آسایش است و خدا دوستدار و سرپرست آنهاست برای آنکه نیکوکار بودند.'),
(917, 6, 128, 'و (یاد آر) روزی که همه آنها را محشور می‌کند (و به شیاطین خطاب کند که) ای گروه جن، شما بسیاری از انسانها را پیرو خود ساختید. در آن حال دوستداران شیاطین از جنس بشر گویند: پروردگارا، ما بعضی از (اضلال) بعضی دیگر بهره‌مند گردیده و به اجلی که معین نموده‌ای رسیدیم. خدا گوید: آتش منزلگاه شماست و همیشه در آن خواهید بود مگر آنچه خدا بخواهد (که برخی را بیرون آورد)، که پروردگار تو درست کردار و داناست.'),
(918, 6, 129, 'و همچنین ما برخی ستمکاران را بر بعض دیگر (به مخالفت) برگماریم به سبب آنچه کسب می‌کردند.'),
(919, 6, 130, '(آن گاه خدا گوید) ای گروه جن و انس، مگر برای هدایت شما از جنس خود شما رسولانی نیامدند که آیات مرا برای شما می‌خواندند و شما را از مواجه شدن با این روز سخت می‌ترساندند؟ آنها (با نهایت پشیمانی) جواب دهند که ما (به جهالت و بدی) بر خود گواهی می‌دهیم. و زندگانی دنیا آنها را مغرور ساخت و در آن حال (می‌فهمند و) بر خود گواهی می‌دهند که به راه کفر می‌رفتند.'),
(920, 6, 131, 'این (فرستادن رسل) برای این است که خدا اهل دیاری را تا (اتمام حجت نکرده و) آنها غافل و جاهل باشند به ستم هلاک نگرداند.'),
(921, 6, 132, 'و هر کس (از بندگان) به عملی که کرده رتبه خواهد یافت، و خدای تو از عمل هیچ کس غافل نخواهد بود.'),
(922, 6, 133, 'و خدای تو از خلق بی‌نیاز و (به همه) مهربان است و اگر بخواهد، شما را ببرد (و همه را فانی گرداند) آن گاه هر که را بخواهد جانشین شما کند چنان که شما را از نسل گروهی دیگر پدید آورد.'),
(923, 6, 134, 'هر چه به شما وعده دادند محققا خواهد آمد و شما (بر قدرت و مقدّرات خدا) غالب نخواهید شد.'),
(924, 6, 135, 'بگو: ای قوم من، شما را هر چه در خور است و بر آن توانایی دارید عمل کنید، من نیز در خور خویش عمل (نیک) می‌کنم، آن گاه البته (من به اجر خود رسم و) شما به زودی آگاه خواهید شد که آن کس که عاقبت منزلگاه خوش دارد کیست، البته ستمکاران را رستگاری نیست.'),
(925, 6, 136, 'و برای خدا از روییدنیها و چهارپایانی که آفریده نصیبی معین کردند و به گمان خودشان گفتند: این سهم برای خدا و این سهم برای دیگر شریکان و بتان ما. پس آن سهمی که شریکانشان را بود به خدا نمی‌رسید و آن که برای خدا بود به شریکان می‌رسید! حکمی سخت (جاهلانه و) ناشایسته می‌کنند.'),
(926, 6, 137, 'و همچنین در نظر بسیاری از مشرکان، عمل کشتن فرزندان را بتهای ایشان نیکو جلوه داده تا آنکه آنان را با این عمل هلاک سازند و در دین فطریشان به غلط و اشتباه اندازند. و اگر خدا می‌خواست چنین نمی‌کردند، پس آنها را با آنچه (از خرافات) می‌بافند واگذار.'),
(927, 6, 138, 'و به پندار (جاهلانه) خویش گفتند: اینها چهار پایان و زراعتهایی است که بر همه ممنوع (و مخصوص بتان) است، نباید از آن بخورد مگر آن کس که ما بخواهیم، و چهارپایانی است که سواری آنها حرام است، و چهارپایانی را نیز بدون ذکر نام خدا ذبح می‌کنند. چون در این احکام به خدا دروغ بستند خدا به زودی آنها را مجازات خواهد کرد.'),
(928, 6, 139, 'و گفتند: آنچه در شکم این چهارپایان است مخصوص مردان ماست و بر زنان ما حرام است، و اگر مرده باشد همه (مردان و زنان) در آن شریک باشند. خداوند آنها را به مجازات این گفتار به زودی می‌رساند و خدا درست کردار و داناست.'),
(929, 6, 140, 'البته آنان که فرزندان خود را به سفاهت و نادانی کشتند و آنچه را که خدا نصیبشان کرد با افترا به خدا حرام شمردند زیانکارند. اینان سخت گمراه شدند و هدایت نیافتند.'),
(930, 6, 141, 'و او آن خدایی است که برای شما بستانها از درختان داربستی و درختان آزاد و درختان خرما و زراعتها که میوه و دانه‌های گوناگون آرند و زیتون و انار و میوه‌های مشابه و نامشابه آفرید. شما هم از آن میوه‌ها هرگاه برسد تناول کنید و حق آن را (زکات فقیران را) در روز درو و جمع‌آوری آن بدهید و اسراف نکنید، که خدا مسرفان را دوست نمی‌دارد.'),
(931, 6, 142, 'و از بعضی چهارپایان استفاده باربری و سواری کنید. از آنچه روزی شما کرده بخورید و از پی اغوای شیطان نروید، که او شما را دشمنی آشکار است.'),
(932, 6, 143, '(برای شما بندگان) هشت جفت، از جنس برّه دو صنف (نر و ماده) و از جنس بز دو صنف (آفرید و حلال کرد). بگو: آیا از این دو جنس نر یا ماده یا بچه‌ای که در شکم ماده‌هاست کدام یک را خدا حرام کرده است؟ شما به من مدرک قطعی ارائه دهید اگر راست می‌گویید.'),
(933, 6, 144, 'و نیز از جنس شتر دو صنف (نر و ماده) و از جنس گاو دو صنف (آفرید و حلال نمود). به آنها بگو: آیا از این دو جنس نر یا ماده یا بچه‌ای که در شکم ماده‌هاست کدام را حرام کرده است؟ مگر شما حاضر بودید که خدا بر این حرمت به شما توصیه نموده؟ کیست ستمکارتر از آن کس که به خدا دروغ بست تا به جهل خود مردم را گمراه کند؟! البته خدا ستمکاران را هدایت نخواهد کرد.'),
(934, 6, 145, 'بگو: در احکامی که به من وحی شده من چیزی را که برای خورندگان طعام حرام باشد نمی‌یابم جز آنکه میته باشد یا خون ریخته (جهنده) یا گوشت خوک که پلید است و یا حیوانی که بدون ذکر نام خدا از روی فسق و نافرمانی ذبح کنند. و (در همین ها که حرام شده) هرگاه کسی به خوردن آنها مضطر گردید صرف کند، در صورتی که از روی ستم و از راه تعدی (از فرمان خدا) نباشد، همانا خدای تو بسیار بخشنده و مهربان است.'),
(935, 6, 146, 'و بر یهودان، هر حیوان ناخن دار و از جنس گاو و گوسفند نیز پیه آنها را حرام کردیم مگر آنچه در پشت آنهاست یا به بعض روده‌ها یا به استخوانها آمیخته است. آنها چون ستم کردند ما به آن حکم آنها را مجازات کردیم، و البته سخنان ما راست است.'),
(936, 6, 147, 'پس اگر تو را تکذیب کنند بگو: خدای شما با آنکه دارای رحمت بی منتها است عذابش از بدکاران باز داشته نخواهد شد.'),
(937, 6, 148, 'آنان که شرک آوردند خواهند گفت: اگر خدا می‌خواست ما و پدرانمان مشرک نمی‌شدیم و چیزی را حرام نمی‌کردیم. بدین گفتار، پیشینیان ایشان نیز تکذیب (رسل) کردند تا آنکه طعم عذاب ما را چشیدند. بگو: آیا شما بر این سخن مدرک قطعی دارید تا به ما ارائه دهید؟ همانا شما پیروی جز از خیالات باطل نمی‌کنید و جز به گزافه و دروغ سخن نمی‌گویید.'),
(938, 6, 149, 'بگو: برای خدا حجّت بالغه و رساست، اگر می‌خواست همه شما را هدایت می‌کرد.'),
(939, 6, 150, 'بگو: گواهان خود را بیاورید بر اینکه خدا این را و آن را حرام کرده. پس هرگاه گواهی دادند (چون دروغ می‌گویند) تو با آنها گواهی مده و پیروی مکن از هوای نفس آنها که آیات خدا را تکذیب کردند و به قیامت ایمان نمی‌آورند و برای خدای خود همتا قرار می‌دهند.'),
(940, 6, 151, 'بگو: بیایید تا آنچه خدا بر شما حرام کرده همه را بر شما بخوانم، و آن اینکه به هیچ وجه شرک به خدا نیاورید، و به پدر و مادر احسان کنید، و دیگر اولاد خود را از بیم فقر نکشید، ما شما و آنها را روزی می‌دهیم، و دیگر به کارهای زشت آشکار و نهان نزدیک نشوید و نفسی را که خدا حرام کرده (محترم شمرده) جز به حق به قتل نرسانید. خدا شما را به این اندرزها سفارش نموده، باشد که تعقل کنید.'),
(941, 6, 152, 'و هرگز به مال یتیم نزدیک نشوید جز به آن وجه که نیکوتر است تا آنکه به حدّ رشد و کمال رسد. و به راستی و عدالت، کیل و وزن را تمام بدهید. ما هیچ کس را جز به قدر توانایی او تکلیف نکرده‌ایم. و هرگاه سخنی گویید به عدالت گرایید هر چند درباره خویشاوندان باشد، و به عهد خود وفا کنید. این است سفارش خدا به شما، باشد که متذکر و هوشمند شوید.'),
(942, 6, 153, 'و این است راه راست من، از آن پیروی کنید و از راههای دیگر که موجب تفرقه شما از راه اوست متابعت نکنید. این است سفارش خدا به شما، شاید پرهیزکار شوید.'),
(943, 6, 154, 'آن گاه به موسی کتاب دادیم برای تکمیل نفس هر نیکوکار و برای تفصیل و بیان حکم هر چیز و برای هدایت و رحمت بر خلق، باشد که (مردم) به لقاء خدا ایمان آورند.'),
(944, 6, 155, 'و این (قرآن) کتابی است که ما با برکت و خیر بسیار فرستادیم، از آن پیروی کنید و پرهیزکار شوید، شاید مشمول مرحمت خدا گردید.'),
(945, 6, 156, '(قرآن را برای این فرستادیم) تا نگویید تورات و انجیل تنها بر دو طایفه یهود و نصاری پیش از ما فرستاده شد و ما از تعلیم درس (کتابهای الهی) آنان غافل و بی بهره ماندیم.'),
(946, 6, 157, 'یا گویید: اگر کتاب بر ما فرستاده می‌شد ما بهتر از آنان به راه هدایت می‌شتافتیم. پس به شما هم از طرف پروردگارتان حجت آشکار و هدایت و رحمت رسید، اکنون کیست ستمکارتر از آن که آیات الهی را تکذیب کند و از آنها روی گرداند؟! به زودی آنان را که از آیات ما روی گردانیده و مخالفت کنند به عذاب سخت مجازات کنیم به کیفر آنکه از آیات ما اعراض می‌کردند.'),
(947, 6, 158, 'آیا (منکران) انتظاری دارند جز آنکه فرشتگان (قهر) بر آنها درآیند و یا (امر) خدا (و یا قضای الهی) بر آنان فرا رسد (و همه هلاک شوند) یا برخی دلایل و آیات خدا آشکار شود؟ روزی که بعضی آیات (قهر) خدای تو بر آنها برسد آن روز هیچ کس را ایمانش نفع نبخشد اگر قبل از آن ایمان نیاورده و یا در ایمان خود کسب خیر و سعادت نکرده باشد. بگو: شما در انتظار (نتیجه اعمال زشت خود) باشید ما هم در انتظار هستیم.'),
(948, 6, 159, 'آنان که دین را پراکنده نمودند و (در آن) فرقه فرقه شدند (چشم از آنها بپوش) چنین کسان به کار تو نیایند، (و از دست آنها غمگین مباش که مجازات) کار آنها با خداست و بعد از این به آنچه می‌کردند آگاهشان می‌گرداند.'),
(949, 6, 160, 'هر کس کار نیکو کند او را ده برابر آن خواهد بود و هر کس کار زشت کند به قدر کار زشتش مجازات شود و بر آنها اصلا ستم نخواهد شد.'),
(950, 6, 161, 'بگو: محققا مرا خدایم به راه راست هدایت کرده، به دینی استوار، همان آیین پاک ابراهیم که وجودش از لوث شرک و عقاید باطل مشرکین منزه بود.'),
(951, 6, 162, 'بگو: همانا نماز و طاعت و تمام اعمال من و حیات و ممات من همه برای خداست که پروردگار جهانیان است.'),
(952, 6, 163, 'که او را شریک نیست و به همین (اخلاص کامل) مرا فرمان داده‌اند و من اول کسی هستم که تسلیم امر خدایم.'),
(953, 6, 164, 'بگو: آیا غیر از خدا را به ربوبیّت گزینم در صورتی که خدا پرورنده همه موجودات است؟ و هیچ کس چیزی نیندوخت مگر بر خود، و هیچ نفسی بار (گناه) دیگری را بر دوش نگیرد. سپس بازگشت همه شما به سوی پروردگارتان است و او شما را به آنچه خلاف در آن می‌کردید آگاه خواهد ساخت.'),
(954, 6, 165, 'و او خدایی است که شما را جانشین گذشتگان اهل زمین مقرر داشت و رتبه بعضی را از بعضی بالاتر قرار داد تا شما را در آنچه به شما داده (در این تفاوت رتبه‌ها) بیازماید، که همانا خدا زود کیفر و بسیار بخشنده و مهربان است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المص (بعضی در تفسیر آن گفته‌اند: خدای مهربان صاحب اقتدار بردبار).'),
(956, 7, 2, 'این کتابی است که به تو نازل شد، پس تو دلتنگ و رنجه خاطر (از انکار مردم) نسبت به آن مباش، تا (مردمان را) به (آیات عذاب) آن بترسانی، و اهل ایمان را سبب یادآوری باشد.'),
(957, 7, 3, '(ای اهل ایمان) از آنچه از خدایتان به سوی شما فرستاده شده پیروی کنید و پیرو دستورهای غیر او نباشید و جز خدا را به دوستی مگیرید. اما اندکی بدین پند متذکر می‌گردید.'),
(958, 7, 4, 'و چه بسیار اهالی شهرها را که هلاک کردیم و آنها را عذاب ما آمد آن گاه که در آسایش شب یا به خواب راحت نیمروز بودند.'),
(959, 7, 5, 'پس در هنگامی که عذاب ما به آنان رسید جز این اعتراف نکردند که گفتند: ما خود ستمکار و مستحق عذاب بودیم.'),
(960, 7, 6, 'البته ما هم از امتها که پیامبران به سویشان آمدند و هم از پیغمبران آنها باز خواست خواهیم کرد.'),
(961, 7, 7, 'پس بر آنان (حکایت حال آنها را تماما) به علم و بصیرت کامل بیان کنیم، و ما از کردار آنان غایب و غافل نبوده‌ایم.'),
(962, 7, 8, 'و روز محشر حقا روز سنجیدن اعمال است، پس آنان که در آن میزان حق وزین و نیکوکار بودند البته رستگار خواهند بود.'),
(963, 7, 9, 'و آنان که در آن میزان سبک وزن بودند (یعنی بی ایمان و بد عملند) آنان کسانی هستند که به حقیقت به خود زیان رسانیده‌اند چون به آیات ما ستم می‌کردند.'),
(964, 7, 10, 'و همانا ما شما (فرزندان آدم) را در زمین تمکین و اقتدار بخشیدیم و در آن بر شما معاش و روزی مقرر داشتیم، لیکن اندک شکر نعمتهای خدای را به جا می‌آرید.'),
(965, 7, 11, 'و همانا شما آدمیان را بیافریدیم و آن گاه که بدین صورت کامل آراستیم، فرشتگان را به سجده آدم مأمور کردیم، همه سجده کردند جز شیطان که از جمله سجده کنندگان نبود.'),
(966, 7, 12, 'خدای متعال بدو فرمود: چه چیز تو را مانع از سجده (آدم) شد که چون تو را امر کردم نافرمانی کردی؟ پاسخ داد که من از او بهترم، که مرا از آتش و او را از خاک آفریده‌ای.'),
(967, 7, 13, 'خدا به شیطان فرمود: از این مقام فرود آ، که تو را نرسد که در این مقام بزرگی و نخوت ورزی، بیرون شو که تو از زمره پست ترین فرومایگانی.'),
(968, 7, 14, 'شیطان گفت: (حال که رانده درگاه شدم) مرا تا به روزی که خلایق برانگیخته شوند مهلت ده.'),
(969, 7, 15, 'خدا فرمود: البته مهلت خواهی داشت.'),
(970, 7, 16, 'شیطان گفت که چون تو مرا گمراه کردی من نیز البته در کمین بندگانت در سر راه راست تو می‌نشینم.'),
(971, 7, 17, 'آن گاه از پیش روی و از پشت سر و طرف راست و چپ آنان در می‌آیم (و هر یک از قوای عامله و ادراکی آنها را به میل باطل می‌کشم)، و بیشتر آنان را شکر گزار نعمتت نخواهی یافت.'),
(972, 7, 18, 'خدا گفت: از این مقام بیرون شو که تو نکوهیده و رانده درگاه مایی، هر که از فرزندان آدم تو را پیروی کند جهنم را از تو و آنان به یقین پر می‌گردانم.'),
(973, 7, 19, 'و ای آدم، تو با جفتت در بهشت منزل گزینید و از هر جا (و هر چه) بخواهید تناول کنید و لیکن نزدیک این درخت نروید (درخت گندم یا انگور یا سیب یا غیره) که از ستمکاران خواهید گشت.'),
(974, 7, 20, 'آن گاه شیطان، آدم و حوّا هر دو را به وسوسه فریب داد تا زشتیهایشان را که از آنان پوشیده بود بر ایشان پدیدار کند و گفت: خدایتان شما را از این درخت نهی نکرد جز برای اینکه مبادا (در بهشت) دو فرشته شوید یا عمر جاودان یابید.'),
(975, 7, 21, 'و بر آنان سوگند یاد کرد که من خیر خواه شما هستم.'),
(976, 7, 22, 'پس آنان را به فریب و دروغی (از آن مقام بلند) فرود آورد، پس چون از آن درخت تناول کردند زشتیهایشان (مانند عورات و سایر زشتیهای پنهان) بر آنان آشکار گردید و بر آن شدند که از برگ درختان بهشت خود را بپوشانند، و پروردگارشان آنها را ندا کرد که آیا من شما را از این درخت منع نکردم و نگفتم که شیطان دشمن آشکار شماست؟'),
(977, 7, 23, 'گفتند: خدایا، ما بر خویش ستم کردیم و اگر تو ما را نبخشی و به ما رحمت و رأفت نفرمایی سخت از زیانکاران خواهیم بود.'),
(978, 7, 24, 'خدا گفت: (از بهشت) فرود آیید که برخی با برخی دیگر مخالف و دشمنید، و زمین تا هنگامی معین (یعنی وقت مرگ و قیامت) جایگاه شماست.'),
(979, 7, 25, 'خدا گفت: در این زمین زندگانی کنید و در آن بمیرید و هم از آن باز برانگیخته گردید.'),
(980, 7, 26, 'ای فرزندان آدم، محققا ما لباسی که ستر عورات شما کند و جامه‌هایی که به آن تن را بیارایید برای شما فرستادیم، و لباس تقوا نیکوترین جامه است. این سخنان همه از آیات خداست (که به بندگان فرستادیم) شاید خدا را یاد آرند.'),
(981, 7, 27, 'ای فرزندان آدم، مبادا شیطان شما را فریب دهد چنان که پدر و مادر شما را از بهشت بیرون کرد، در حالی که جامه از تن آنان بر می‌کند تا قبایح آنان را در نظرشان پدیدار کند، همانا آن شیطان و بستگانش شما را می‌بینند از جایی که شما آنها را نمی‌بینید. ما نوع شیطان را دوستدار و سرپرست کسانی قرار داده‌ایم که ایمان نمی‌آورند.'),
(982, 7, 28, 'و چون کار زشتی کنند گویند: ما پدران خود را بدین کار یافته‌ایم و خدا ما را به آن امر نموده. بگو: هرگز خدا امر به اعمال زشت نکند. آیا چیزی را که نمی‌دانید به خدا نسبت می‌دهید؟!'),
(983, 7, 29, 'بگو: پروردگار من شما را به عدل و درستی امر کرده (نه به اعمال زشت)، و (نیز فرموده که) در هر عبادت روی به حضرت او آرید و خدا را از سر اخلاص در دین بخوانید چنانکه شما را در اول بیافرید دیگر بار (در محشر) به سویش باز آیید.'),
(984, 7, 30, 'گروهی را (به طاعت خود) رهنورد هدایت کرد، و گروهی (به پیروی شیطان) گمراهی بر آنان حتمی شد، چون خدا را گذارده و شیطانها را به دوستی اختیار کردند و گمان می‌کنند که به راه راست هدایت یافته‌اند.'),
(985, 7, 31, 'ای فرزندان آدم، زیورهای خود را در مقام هر عبادت به خود برگیرید و بخورید و بیاشامید و اسراف مکنید، که خدا مسرفان را دوست نمی‌دارد.'),
(986, 7, 32, 'بگو: چه کسی زینت‌های خدا را که برای بندگان خود آفریده حرام کرده و از صرف رزق حلال و پاکیزه منع کرده؟ بگو: این نعمتها در دنیا برای اهل ایمان است و خالص اینها (یعنی لذات کامل بدون الم، و نیکوتر از اینها) در آخرت برای آنان خواهد بود. ما آیات خود را برای اهل دانش چنین مفصل و روشن بیان می‌کنیم.'),
(987, 7, 33, 'بگو که خدای من هر گونه اعمال زشت را چه در آشکار و چه در نهان و گناهکاری و ظلم به ناحق و شرک به خدا را که بر آن شرک هیچ دلیلی نفرستاده است و این که چیزی را که نمی‌دانید به خدا نسبت دهید، همه را حرام کرده است.'),
(988, 7, 34, 'و هر قومی را دوره‌ای و اجل معینی است که چون اجلشان فرا رسد لحظه‌ای پس و پیش نخواهند شد.'),
(989, 7, 35, 'ای فرزندان آدم، چون پیغمبرانی از جنس شما بیایند و آیات مرا بر شما بخوانند، پس هر که تقوا پیشه کرد و به کار شایسته شتافت هیچ ترس و اندوهی بر آنها نخواهد بود.'),
(990, 7, 36, 'و آنان که آیات ما را تکذیب کرده و از سرکشی و تکبر سر بر آن فرود نیاوردند آنها اهل دوزخند و در آن جاوید (معذب) خواهند بود.'),
(991, 7, 37, 'پس کیست ستمکارتر از آن کس که بر خدای دروغ بندد یا آیات او را تکذیب کند؟ آنان از کتاب (قضا و قدر الهی یا از نامه اعمال) به بهره خویش خواهند رسید، تا هنگامی که فرستادگان ما (ملک الموت و فرشتگان قبض روح) بر آنان فرا رسند که قبض روح آنان کنند به آنان گویند: چه شدند آنهایی که به جای خدا به ربوبیت می‌خواندید (مانند بتان جمادی و غیر جمادی و هواهای نفسانی)؟ پاسخ دهند که آنها همه از نظر ما ناپدید و نابود شدند. و آنها بر زیان خویش گواهی دهند که کافر بوده‌اند.'),
(992, 7, 38, 'خداوند گوید: شما هم با آن گروه از جن و انس که پیش از شما بودند به دوزخ داخل شوید. در آن وقت هر قومی که به دوزخ شوند قوم دیگر (از همکیشان خود) را لعن کنند، تا آن گاه که همه در آتش دوزخ فراهم شوند زمره آخرین درباره فرقه اول (یا مرئوسان درباره رئیسان) گویند که خدایا، اینان ما را گمراه کردند، پس عذابشان را از آتش افزون و مضاعف گردان. خدا گوید: همه را عذاب مضاعف است و لیکن شما آگاه نیستید.'),
(993, 7, 39, 'و گروه مقدم به طایفه مؤخّر پاسخ دهند که شما را بر ما برتری نباشد (ما و شما در گناه یکسانیم). پس (به آنها خطاب کنند که) بچشید عذاب را به کیفر آنچه از اعمال ناشایسته مرتکب می‌شدید.'),
(994, 7, 40, 'همانا آنان که آیات ما را تکذیب کردند و از کبر و نخوت سر بر آن فرود نیاوردند هرگز درهای آسمان به روی آنان باز نشود و به بهشت در نیایند تا آنکه شتر در چشمه سوزن در آید (یعنی داخل شدنشان به بهشت بدان ماند که شتر به چشمه سوزن رود و این در عادت محال باشد). و این گونه گنهکاران (متکبّر) را مجازات سخت خواهیم کرد.'),
(995, 7, 41, 'بر آنان از آتش دوزخ بسترها گسترده و سراپرده‌ها افراشته‌اند و ستمکاران را چنین مجازات سخت خواهیم کرد.'),
(996, 7, 42, 'و آنان که ایمان آوردند و (به قدر وسع) در کار نیک و شایسته کوشیدند-زیرا ما کسی را بیش از وسع تکلیف نکنیم-آنها اهل بهشت و جاودان در آن متنعمند.'),
(997, 7, 43, 'و زنگار هر کینه (و حسد و خوی زشت) را از آیینه دل آنان بزداییم و در بهشت بر زیر قصرهایشان نهرها جاری شود و گویند: ستایش خدای را که ما را بر این مقام رهنمایی کرد، که اگر هدایت و لطف الهی نبود ما به خود به این مقام راه نمی‌یافتیم، همانا رسولان خدایمان به حق آمدند (و ما را بر این مقام رهبری کردند). و اهل بهشت را ندا کنند که این است بهشتی که از اعمال صالح شما را به ارث داده‌اند.'),
(998, 7, 44, 'و بهشتیان دوزخیان را ندا کنند که آنچه پروردگارمان به ما وعده داد (از مقامات بهشتی) ما به حق و حقیقت یافتیم، آیا شما نیز بدانچه پروردگارتان وعده داد (از عذاب دوزخ) به حقیقت رسیدید؟ گویند: بلی. آن گاه میان آنها منادیی ندا کند که لعنت خدا بر ستمکاران باد.'),
(999, 7, 45, 'آنهایی که از راه خدا باز می‌دارند و آن را کج می‌طلبند (به شبهه راه راست مردم را کج می‌کنند) و آنها به قیامت ایمان ندارند.'),
(1000, 7, 46, 'و میان این دو گروه حجاب و پرده‌ای است (از سرشت نیک و بد که مانع دیدار یکدیگر است) و بر اعراف (یعنی جایگاهی میان دوزخ و بهشت) مردانی هستند که همه را به سیمایشان می‌شناسند و بهشتیان را که هنوز داخل بهشت نشده و چشم امید به دخول آن دارند ندا کنند که سلام بر شما باد.'),
(1001, 7, 47, 'و چون نظر آنها بر اهل دوزخ افتد گویند: پروردگارا، ما را با این ستمکاران به یک جای قرار مده.'),
(1002, 7, 48, 'و اهل اعراف مردانی را که به سیمایشان می‌شناسند آواز دهند و گویند: (دیدید که) جمع مال و جاه و آن فخر و تکبّرتان به حال شما اصلا سودمند نبود!'),
(1003, 7, 49, 'آیا آنهایی (از مؤمنان) که سوگند یاد می‌کردید که خدا آنان را مشمول عنایت و رحمت خود نمی‌گرداند اکنون مقامشان را می‌بینید؟ (به همانها امروز خطاب شود که) در بهشت بی هیچ خوف و اندیشه و بی هیچ گونه حزن و اندوه داخل شوید.'),
(1004, 7, 50, 'و اهل دوزخ بهشتیان را آواز کنند که ما را از آبهای گوارا یا از نعم بهشتی که خدا روزی شما کرده بهره‌مند کنید. آنها پاسخ دهند که خدا این آب و طعام را بر کافران حرام گردانیده است.'),
(1005, 7, 51, 'آنهایی که دین خدا را فسوس و بازیچه گرفتند و (متاع) حیات دنیا آنها را مغرور و غافل کرد، امروز ما هم آنها را (به رحمت) در نظر نمی‌آوریم چنانکه آنان دیدار چنین روزشان را به خاطر نیاوردند و آیات ما را انکار می‌کردند.'),
(1006, 7, 52, 'و محققا ما برای ایشان کتابی آوردیم که در آن هر چیز را بر اساس علم و دانش تفصیل دادیم برای هدایت و رحمت آن گروه که ایمان می‌آورند.'),
(1007, 7, 53, 'آیا جز این انتظار دارند که تأویل آن (آیات و مآل اعمال آنها هنگام مرگ یا ظهور قیامت) به آنان برسد؟ آن روز که تأویل آن فرا رسد کسانی که آن را پیش از این فراموش کرده بودند خواهند گفت: همانا رسولان پروردگارمان حق را برای ما آوردند (کاش مخالفت نمی‌کردیم) آیا کسی به شفاعت ما بر خواهد خاست یا بار دیگر به دنیا باز می‌گردیم تا غیر از این اعمال زشت به اعمال صالحی بپردازیم؟ راستی که آن کافران خود را در زیان سخت افکنده‌اند و آنچه به دروغ می‌بافتند همه نابود شده و از دستشان رفته است.'),
(1008, 7, 54, 'پروردگار شما آن خدایی است که آسمانها و زمین را در شش روز خلق کرد، آن گاه بر عرش (فرمانروایی) قرار گرفت، روز را به پرده شب (و به عکس) در پوشاند که با شتاب در پی آن پوید، و نیز خورشید و ماه و ستارگان را مسخّر فرمان خود آفرید. آگاه باشید که ملک آفرینش خاص خداست و حکم نافذ فرمان اوست، بس منزه و بلند مرتبه است خدای یکتا که پروردگار عالمیان است.'),
(1009, 7, 55, 'خدای خود را به تضرع و زاری و نهانی (به صدای آهسته که به اخلاص نزدیک است) بخوانید (و از اندازه مگذرید) که خدا هرگز متجاوزان را دوست نمی‌دارد.'),
(1010, 7, 56, 'و هرگز در زمین پس از آنکه کار آن (به امر حق و شرع رسول حق) نظم و صلاح یافت به فساد و تباهکاری برنخیزید، و خدا را هم از راه ترس و هم از روی امید بخوانید، که البته رحمت خدا به نیکوکاران نزدیک است.'),
(1011, 7, 57, 'و او خدایی است که بادها را به بشارت باران رحمت خویش در پیش فرستد، تا چون بار ابرهای سنگین را بر دارند ما آنها را به شهر و دیاری که (از بی آبی) مرده است برانیم و بدان سبب باران فرو فرستیم و هرگونه ثمر و حاصل از آن بر آوریم، همین گونه مردگان را هم از خاک برانگیزیم، باشد که (بر اهوال قیامت خود) متذکر گردید.'),
(1012, 7, 58, 'زمین پاک نیکو گیاهش به اذن خدایش (نیکو) بر آید، و از زمین خشنِ ناپاک بیرون نیاید جز گیاه اندک و کم‌ثمر. این گونه ما آیات (قدرت) را به هر بیان توضیح می‌دهیم برای قومی که شکر خدا به جای آرند.'),
(1013, 7, 59, 'همانا نوح را به قومش فرستادیم، گفت: ای قوم، خدای یکتا را پرستید که جز او شما را خدایی نیست، من بر شما از عذاب روزی بزرگ (یعنی قیامت) می‌ترسم.'),
(1014, 7, 60, 'مهتران قومش گفتند که ما تو را در گمراهی آشکاری می‌بینیم.'),
(1015, 7, 61, 'نوح گفت: ای قوم، من اصلا در گمراهی نیستم لیکن من رسولی از سوی رب العالمینم.'),
(1016, 7, 62, 'پیغام خدایم را به شما می‌رسانم و به شما نصیحت و خیرخواهی می‌کنم و از سوی خدا به اموری آگاهم (مانند قیامت و حساب و کیفر اعمال) که شما آگاه نیستید.'),
(1017, 7, 63, 'آیا تعجب کرده و در نظر شما شگفت‌انگیز است که مردی از خودتان از جانب خدا به یادآوری شما فرستاده شده تا مگر شما را (از عذاب قیامت) بترساند و تا پرهیزکار شوید و باشد که مورد لطف خدا گردید؟'),
(1018, 7, 64, 'پس آن رسول را تکذیب کردند، ما هم او و پیروانش را که با او در کشتی بودند نجات بخشیدیم و آنان را که آیات ما را تکذیب کردند غرق کردیم، که مردمی کوردل بودند.'),
(1019, 7, 65, 'و به قوم عاد برادرشان (یعنی مردی از آن قوم) هود را فرستادیم، بدانها گفت: ای قوم، خدای یکتا را پرستید که جز او شما را خدایی نیست، آیا پرهیزکار نمی‌شوید؟'),
(1020, 7, 66, 'مهتران قومش که کافر شدند گفتند: ما تو را سفیه و بی‌خرد می‌یابیم و گمان می‌کنیم که تو سخت از دروغگویان باشی.'),
(1021, 7, 67, 'پاسخ داد که ای قوم، مرا سفاهتی نیست لیکن من (راستگو و) رسولی از سوی رب العالمینم.'),
(1022, 7, 68, 'پیغام خدایم را به شما می‌رسانم و من برای شما ناصح و خیرخواهی امینم.'),
(1023, 7, 69, 'آیا تعجب کرده‌اید که مردی از جانب خدا به یادآوری شما فرستاده شده تا شما را (از هول و عذاب قیامت) بترساند؟ و متذکر باشید که خدا شما را پس از هلاک قوم نوح جانشین آن گروه کرد و در خلقت (و نعمت) شما بیفزود، پس انواع نعمتهای خدا را به یاد آرید، شاید که رستگار شوید.'),
(1024, 7, 70, 'قوم هود گفتند: آیا تو بر آن به سوی ما آمده‌ای که خدا را به یکتایی پرستیم و از بتهایی که پدران ما می‌پرستیدند دست برداریم؟ (این کار نخواهیم کرد) تو اگر راست می‌گویی هر عذابی که به ما وعده می‌دهی زود به انجام رسان.'),
(1025, 7, 71, 'هود پاسخ داد که (در این صورت) پلیدی و خباثت، و غضب خدایتان بر شما حتم گردید، آیا با من (که شما را به خدای یکتا می‌خوانم) درباره الفاظ بی‌معنی و اسماء بی‌مسمّایی که خود و پدرانتان (بر آن بتان) نهاده‌اید مجادله می‌کنید و حال آنکه خدا در آن بتان هیچ حقیقت و برهانی ننهاده؟ پس شما بایستی در انتظار (عذاب خدا) باشید که من نیز با شما انتظار (عذابتان) دارم.'),
(1026, 7, 72, 'پس (موقع عذاب که رسید) ما هود و پیروانش را به رحمت خود (از عذاب) رهانیدیم و آنان که آیات (و رسول) ما را تکذیب کردند و ایمان نیاوردند همه را بنیادشان برانداختیم.'),
(1027, 7, 73, 'و بر قوم ثمود برادرشان صالح را فرستادیم، گفت: ای قوم، خدای یکتا را پرستید که جز او شما را خدایی نیست، اکنون معجزی واضح و آشکار از طرف خدایتان آمده، این ناقه خداست که شما را آیت و معجزی است بزرگ، او را واگذارید تا در زمین خدا چرا کند و قصد سوئی درباره او مکنید که به عذابی دردناک گرفتار خواهید شد.'),
(1028, 7, 74, 'متذکر باشید و فراموش نکنید که خدا شما را پس از (هلاک) قوم عاد جانشین اقوام سلف نمود و در زمین منزل داد که از اراضی سهله (خاک نرم) قصرهای عالی و از کوه سخت به تراشیدن سنگ، منزلهای محکم بنا می‌کنید، پس نعمتهای خدا را یاد کنید و در زمین به فساد و تبهکاری برنخیزید.'),
(1029, 7, 75, 'رؤسا و گردنکشان قوم صالح با ضعفا و فقیرانی که به او ایمان آوردند (به تمسخر) گفتند: آیا شما اعتقاد دارید که صالح را خدا به رسالت فرستاده؟ مؤمنان جواب دادند: بلی (ما بر این عالم و مطمئنّیم و) ما بی‌هیچ شک به آیینی که او (از طرف خدا) بدان فرستاده شده ایمان داریم.'),
(1030, 7, 76, 'گردنکشان بی‌ایمان گفتند: ما هم بی‌هیچ شک به آنچه شما ایمان دارید کافریم.'),
(1031, 7, 77, 'آن گاه ناقه صالح را پی کردند و از امر خدای خود سرباز زدند و (با پیغمبرشان صالح از روی تهکّم و تمسخر) گفتند که ای صالح، اگر تو از رسولان خدایی اکنون عذابی که (بر پی کردن ناقه و نافرمانی خدا) ما را وعده کردی بیار.'),
(1032, 7, 78, 'پس زلزله‌ای آنها را درگرفت تا آنکه همه در خانه‌های خود از پای درآمدند (برخی مفسرین گفتند: علامات عذاب بر آنها آمد و پس از سه روز هلاک شدند).'),
(1033, 7, 79, 'پس (چون علایم عذاب رسید) صالح از آنان روی گردانید (و از ایمان آنها ناامید شد) و گفت: ای قوم، من ابلاغ رسالت از خدای خود نمودم و شما را نصیحت کردم لیکن شما (از جهل و غرور) ناصحان را دوست نمی‌دارید.'),
(1034, 7, 80, 'و لوط را (به رسالت فرستادیم) که به قوم خود گفت: آیا عمل زشتی که پیش از شما هیچ کس بدان مبادرت نکرده به جا می‌آورید؟!'),
(1035, 7, 81, 'شما زنان را ترک کرده و با مردان سخت شهوت می‌رانید! آری شما قومی (در فساد و بزهکاری) اسرافکارید.'),
(1036, 7, 82, 'آن قوم، پیغمبر خود لوط را پاسخی ندادند جز آنکه گفتند: او و پیروانش را از شهر بیرون کنید که آنان مردمی هستند که (این کار را پلید دانسته و از آن) تنزه می‌جویند.'),
(1037, 7, 83, 'ما هم او و اهل بیتش همه را (از بلای قومش) نجات دادیم مگر زن او را که (بی‌ایمان و) از بازماندگان (در عذاب) بود.'),
(1038, 7, 84, 'و بر آن قوم بارانی (از سنگریزه) فرو باریدیم، بنگر که مآل کار بدکاران چیست.'),
(1039, 7, 85, 'و به اهل مدین برادر آنها شعیب را فرستادیم، گفت: ای قوم، خدای یکتا را بپرستید که شما را جز او خدایی نیست، اکنون از جانب پروردگارتان بر شما برهانی روشن آمد پس در سنجش، کیل و وزن را تمام دهید و به مردم کم‌فروشی نکنید، و در زمین پس از نظم و اصلاح آن (با آمدن قوانین آسمانی)، به فساد برنخیزید. این کار (یعنی خدا را به یگانگی پرستیدن و با خلق به عدالت کوشیدن و در وزن خیانت نکردن) برای شما بسیار بهتر است اگر (به خدا و روز قیامت) ایمان دارید.'),
(1040, 7, 86, 'و به هر طریق در کمین گمراه کردن خلق و ترسانیدن و بازداشتن مردم با ایمان از راه خدا منشینید و راه خدا را کج مجویید (و آن را وارونه جلوه مدهید) و به یاد آرید زمانی را که عده قلیلی بودید (و دشمنانتان بسیار) و خدا بر عده شما افزود، و بنگرید عاقبت حال مفسدان چگونه بود!'),
(1041, 7, 87, 'و اگر به آنچه من از طرف خدا مأمور به تبلیغ آن شده‌ام گروهی ایمان آوردند و گروهی ایمان نیاوردند (و به خصومت برخاستند) شما مؤمنان که ایمان آورده‌اید صبر پیشه کنید تا خدا میان ما و آنها داوری کند که او بهترین داوران است.'),
(1042, 7, 88, 'رؤسا و گردنکشان قوم او گفتند: ای شعیب ما تو و پیروانت را از شهر خویش بیرون می‌کنیم مگر آنکه به کیش ما برگردید. شعیب گفت: اگر چه با این نفرت که ما از آیین شما داریم (باز هم به آن بازگردیم).'),
(1043, 7, 89, 'اگر ما به دین شما برگردیم با وجود آنکه خدا ما را از آن نجات داده همانا به خدا افترا و دروغ بسته‌ایم، و هرگز نشود که ما به آیین شما رجوع کنیم مگر آنکه مشیّت خدا قرار گیرد، که او پروردگار ماست، پروردگار ما بر همه چیز کاملا آگاه است، ما بر خدا توکل کردیم، پروردگارا، تو بین ما و امت، به حق داوری کن که تو بهترین داورانی.'),
(1044, 7, 90, 'سران کافر امتش گفتند: اگر پیروی شعیب کنید البته در زیان خواهید افتاد.'),
(1045, 7, 91, 'پس زلزله آن منکران را درگرفت، پس شب را صبح کردند در حالی که در خانه‌های خود به خاک هلاک افتادند.'),
(1046, 7, 92, 'آنان که شعیب را تکذیب کردند هلاک شدند و اثری از آنها باقی نماند، گویی در آن دیار نبودند. آنان که شعیب را تکذیب کردند خود زیانکار شدند.'),
(1047, 7, 93, 'آن گاه شعیب از آن مردم بی‌ایمان روی گردانید و گفت: ای قوم، من به شما ابلاغ رسالتهای خدای خود کردم و شما را اندرز و نصیحت نمودم، اکنون من چگونه بر هلاک کافران غمگین باشم!'),
(1048, 7, 94, 'و ما هیچ پیغمبری به هیچ شهر و دیاری نفرستادیم مگر آنکه اهلش را به شداید و محن مبتلا ساختیم تا به درگاه خدا تضرع و زاری کنند.'),
(1049, 7, 95, 'سپس آن سختی‌ها را به آسایش و خوشی مبدّل کردیم تا به کلی حال خود را فراموش کرده و گفتند: آن رنج و خوشیها به پدران ما نیز رسید (و این عادت روزگار است که گاهی خوش و گاهی ناخوش است)، ما هم به ناگاه آنان را گرفتیم در حالی که (از توجه به آن عقاب) غافل بودند.'),
(1050, 7, 96, 'و چنانچه مردم شهر و دیارها همه ایمان آورده و پرهیزکار می‌شدند همانا ما درهای برکاتی از آسمان و زمین را بر روی آنها می‌گشودیم و لیکن (چون آیات و پیغمبران ما را) تکذیب کردند ما هم آنان را سخت به کیفر کردار زشتشان رسانیدیم.'),
(1051, 7, 97, 'آیا اهل شهر و دیارها (که به نافرمانی خدا و اعمال نکوهیده مشغولند) از آن ایمنند که شبانگاه که در خوابند عذاب ما آنها را فرا گیرد؟'),
(1052, 7, 98, 'آیا اهل شهر و دیارها (که از یاد خدا و طاعت او غافلند) از آن ایمنند که به روز که سرگرم بازیچه دنیا هستند عذاب ما آنان را فرا رسد؟'),
(1053, 7, 99, 'آیا از مکر خدا (یعنی آزمایش و مجازات خدا) غافل و ایمن گردیده‌اند؟ که از آن مکر و انتقام البته کسی غافل نشود مگر مردم زیانکار.'),
(1054, 7, 100, 'آیا این مردمی را که بعد از اقوامی که همه مردند وارث زمین شدند (خدا) آگاه نفرموده که اگر ما بخواهیم به کیفر گناهانشان می‌رسانیم و بر دلهایشان مهر می‌نهیم که دیگر (به گوش دل چیزی) نشنوند؟'),
(1055, 7, 101, 'این است شهرهایی که ما بر تو از اخبار اهلش بیان کردیم، همه را رسولان حق با ادلّه روشن آمدند اما بر آن نبودند که به آنچه پیش از آن تکذیب کرده بودند ایمان بیاورند. این چنین خدا دلهای کافران را مهر می‌زند.'),
(1056, 7, 102, 'و اکثر مردمان را در عهد استوار ندیدیم بلکه بیشترشان را عهدشکن و بزهکار یافتیم.'),
(1057, 7, 103, 'بعد از آن رسولان، باز ما موسی را با آیات و ادلّه روشن خود به سوی فرعون و مهتران قومش فرستادیم، آنها هم با آن آیات مخالفت و به آنها ستم کردند بنگر تا عاقبت به روزگار آن تبهکاران چه رسید!'),
(1058, 7, 104, 'و موسی خطاب کرد که ای فرعون، محققا من فرستاده‌ای از خدای عالمیانم.'),
(1059, 7, 105, 'سزاوار آنم که از طرف خدا چیزی جز به حق نگویم، همانا دلیلی بس روشن (معجزه بسیار آشکار و حجتی محکم و استوار) از جانب پروردگارتان برای شما آورده‌ام، پس بنی‌اسرائیل را با من بفرست.'),
(1060, 7, 106, 'فرعون پاسخ داد که اگر دلیلی برای صدق خود داری بیار اگر از راستگویانی.'),
(1061, 7, 107, 'موسی هم عصای خود را بیفکند به ناگاه آن عصا اژدهایی عظیم پدیدار گردید.'),
(1062, 7, 108, 'و دست (از گریبان خود) برآورد که ناگاه بینندگان را سپید و رخشان بود.'),
(1063, 7, 109, 'مهتران قوم فرعون گفتند که این شخص ساحری سخت ماهر و داناست.'),
(1064, 7, 110, 'اراده آن دارد که شما را از سرزمین خود بیرون کند، اکنون در کار او چه دستور می‌دهید؟'),
(1065, 7, 111, 'قوم به فرعون گفتند که موسی و برادرش (هارون) را زمانی بازدار و گردآورانی به شهرها بفرست.'),
(1066, 7, 112, 'تا ساحران زبردست دانا را به حضور تو جمع آورند.'),
(1067, 7, 113, 'و عدّه بسیاری ساحران به حضور فرعون گرد آمده، فرعون را گفتند: اگر بر موسی غالب آییم برای ما اجر و مزد شایان خواهد بود؟'),
(1068, 7, 114, 'فرعون پاسخ داد: بلی و علاوه بر آن نزد من از مقربان خواهید گشت.'),
(1069, 7, 115, 'ساحران به موسی گفتند: اکنون خواهی نخست تو عصای خود بیفکن یا ما بساط سحر خویش بیندازیم.'),
(1070, 7, 116, 'موسی گفت: شما اول اسباب خود بیفکنید. چون بساط خود انداختند به جادوگری چشم خلق بربستند و مردم را سخت به هراس افکندند، و سحری بس عظیم و هول‌انگیز برانگیختند.'),
(1071, 7, 117, 'و ما به موسی وحی نمودیم که عصای خود را بیفکن، (چون عصا انداخت) ناگاه معجزه او هر چه ساحران بافته بودند یکباره همه را ببلعید.'),
(1072, 7, 118, 'پس حق ثابت شد و اعمال ساحران همه باطل گردید.'),
(1073, 7, 119, 'عاقبت فرعونیان در آنجا مغلوب شده و خوار و سرشکسته بازگشتند.'),
(1074, 7, 120, 'و ساحران بی‌اختیار به سجده افتادند.'),
(1075, 7, 121, 'گفتند: ما ایمان آوردیم به خدای عالمیان،'),
(1076, 7, 122, 'خدای موسی و هارون.'),
(1077, 7, 123, 'فرعون گفت: چگونه پیش از دستور و اجازه من به او ایمان آوردید؟ همانا این مکری است که در این شهر اندیشیده‌اید که مردم این شهر را از آن بیرون کنید، پس به زودی خواهید دانست!'),
(1078, 7, 124, 'همانا دست و پای شما را یکی از راست و یکی از چپ بریده و آن گاه همه را به دار خواهم آویخت.'),
(1079, 7, 125, 'ساحران گفتند: ما به سوی خدای خود باز می‌گردیم.'),
(1080, 7, 126, 'و کینه و انتقام تو از ما تنها به جرم آن است که ما به آیات خدا چون برای (هدایت) ما آمد ایمان آوردیم، بار خدایا، به ما صبر و شکیبایی ده و ما را به آیین اسلام (یعنی با تسلیم و رضای به حکم خدا) بمیران.'),
(1081, 7, 127, 'و سران قوم فرعون گفتند: آیا موسی و قومش را واگذاری تا در این سرزمین فساد کنند و تو و خدایان تو را رها سازند؟ فرعون گفت: به زودی پسرانشان را کشته و زنانشان را زنده گذاریم (و در رنج و عذاب خدمت و اسارت نگاه داریم) و ما بر آنها غالب و مقتدریم.'),
(1082, 7, 128, 'موسی به قوم خود گفت: از خدا یاری خواهید و صبر کنید که زمین ملک خداست، آن را به هر کس از بندگان خود خواهد به میراث دهد، و حسن عاقبت مخصوص اهل تقواست.'),
(1083, 7, 129, 'قوم موسی به او گفتند که ما هم پیش از آمدن تو (به رسالت) و هم بعد از آنکه آمدی به رنج و شکنجه (دشمن) بوده‌ایم، موسی گفت: امید است که خدا دشمن شما را هلاک نماید و شما را در زمین جانشین کند آن‌گاه بنگرد تا شما چه خواهید کرد.'),
(1084, 7, 130, 'و فرعونیان را سخت به قحط و تنگی معاش و نقص و آفت برکشت و زرع مبتلا کردیم تا شاید متذکر شوند.'),
(1085, 7, 131, 'پس هرگاه که نیکویی (و پیشامد خوشی) بدانها می‌رسید به شایستگی خود نسبت می‌دادند، و هرگاه بدی (و پیشامد ناگواری مانند قحط و سختی) بر آنها می‌آمد فال بد به موسی و همراهانش می‌زدند. آگاه باشند که فال بد آنها نزد خداست (یعنی رنج و سختی‌ها که بر آنان پیش آید کیفر از جانب خداست) لیکن اکثر آنها بر این آگاه نیستند.'),
(1086, 7, 132, 'و گفتند: هر آیه و معجزه‌ای که برای ما بیاوری که ما را بدان سحر کنی هرگز ما به تو ایمان نخواهیم آورد.'),
(1087, 7, 133, 'پس بر آنها طوفان و ملخ و شپشک و وزغ و خون (خون شدن آب) آن نشانه‌های آشکار (قهر و غضب) را فرستادیم، باز طریق کبر و گردنکشی پیش گرفتند و قومی گناهکار بودند.'),
(1088, 7, 134, 'و چون بلا بر آنها واقع شد گفتند که ای موسی، خدایت را به عهدی که با تو دارد بخوان (تا این عذاب و بلا را از ما دور کند)، که اگر رفع بلا کردی البته ایمان به تو می‌آوریم و بنی اسرائیل را به همراهی تو می‌فرستیم.'),
(1089, 7, 135, 'پس چون بلا را تا مدتی که متعهد شدند ایمان آورند از سر آنها رفع کردیم، باز پیمان را شکستند.'),
(1090, 7, 136, 'ما هم از آنان انتقام کشیدیم و آنها را به دریا غرق کردیم به کیفر آنکه آیات ما را تکذیب کرده و از آن غفلت نمودند.'),
(1091, 7, 137, 'و طایفه‌ای را که فرعونیان ذلیل و ناتوان می‌داشتند وارث مشرق و مغرب زمین با برکت (مصر و شامات) گردانیدیم و آن سخن و وعده نیکوی خدای تو بر بنی اسرائیل تحقق یافت به پاداش صبری که (در مصائب) کردند، و آنچه را فرعون و قومش می‌ساختند و می‌افراشتند (از صنایع و عمارات و کاخهای با عظمت) ویران نمودیم.'),
(1092, 7, 138, 'و بنی اسرائیل را از دریا به ساحل رسانیدیم، پس به قومی که بر پرستش بتان خود متوقف بودند برخورده، گفتند: ای موسی برای ما خدایی مثل خدایانی که این بت‌پرستان راست مقرر کن. موسی گفت: شما سخت مردم نادانی هستید.'),
(1093, 7, 139, 'خدایانی که این جماعت بت‌پرستان راست فانی، و آیین و کارهاشان باطل و تباه است.'),
(1094, 7, 140, 'موسی گفت: آیا غیر خدا را برای شما به خدایی طلبم در صورتی که خداست که شما را بر عالمیان برتری و فضیلت بخشید!'),
(1095, 7, 141, 'و (یاد آرید) هنگامی که شما را از چنگ فرعونیان نجات دادیم که شما را سخت به عذاب و شکنجه می‌داشتند، پسرانتان را کشته و زنان را زنده (به اسارت و خدمتکاری) می‌داشتند و این شما را از جانب خدا امتحان و تنبیه بزرگی بود.'),
(1096, 7, 142, 'و ما با موسی سی شب وعده قرار دادیم و ده شب دیگر بر آن افزودیم تا آنکه زمان وعده پروردگارش به چهل شب تکمیل شد. و موسی به برادر خود هارون گفت: تو اکنون جانشین من در قوم من باش و راه صلاح پیش گیر و پیرو راه اهل فساد مباش.'),
(1097, 7, 143, 'و چون (قوم تقاضای دیدن خدا کردند) موسی (با هفتاد نفر بزرگان قومش که انتخاب شده بودند) وقت معین به وعده‌گاه ما آمد و خدایش با وی سخن گفت، موسی (به تقاضای جاهلانه قوم خود) عرض کرد که خدایا خود را به من آشکار بنما که جمال تو را مشاهده کنم. خدا در پاسخ او فرمود که مرا تا ابد نخواهی دید ولیکن به کوه بنگر، اگر کوه طور (بدان صلابت، هنگام تجلی) به جای خود برقرار ماند تو نیز مرا خواهی دید. پس آن‌گاه که تجلی خدایش بر کوه تابش کرد کوه را مندکّ و متلاشی ساخت و موسی بی‌هوش افتاد. سپس که به هوش آمد عرض کرد: خدایا تو منزه و برتری (از رؤیت و حس جسمانی، از چنین اندیشه) به درگاه تو توبه کردم و من (از قوم خود) اول کسی هستم که (به تو و تنزّه ذات پاک تو از هر آلایش جسمانی) ایمان دارم.'),
(1098, 7, 144, 'خدا فرمود: ای موسی من تو را به مقام پیام‌رسانی خود و همصحبتی خویش بر سایر مردم برگزیدم، پس آنچه را که به تو دادم کاملا فراگیر و از شکرگزاران باش.'),
(1099, 7, 145, 'و در الواح (تورات آسمانی) از هر موضوع برای نصایح و پند و هم تحقیق هر چیز (برای علم و معرفت) به موسی نوشتیم (و دستور دادیم) که (حقایق و احکام) آن را به قوت (عقل و ایمان) فراگیر و قوم را دستور ده که نیکوتر مطالب آن را اخذ کنند، که (پستی) مقام و منزلت فاسقان را به زودی به شما نشان خواهم داد.'),
(1100, 7, 146, 'من آنان را که در زمین به ناحق کبر و بزرگمنشی کنند از آیات خود رو گردان و معرض خواهم ساخت، و اگر هر آیتی بینند به آن ایمان نیاورند و اگر راه رشد و هدایت یابند آن را نپیمایند و اگر راه جهل و گمراهی یابند آن را پیش گیرند. این (ضلالت آنها) بدین جهت است که آیات ما را تکذیب کردند و از آنها غافل و معرض بودند.'),
(1101, 7, 147, 'و آنان که آیات (آسمانی) ما و لقاء عالم آخرت را تکذیب کردند اعمالشان (که جز برای دنیا نبود) تباه و باطل شود، آیا جز آنچه می‌کردند کیفر بینند؟'),
(1102, 7, 148, 'و قوم موسی بعد از او (یعنی بعد از رفتن او به طور برای وعده حق) مجسمه گوساله‌ای که صدایی و خواری داشت از طلا و زیورهای خود ساخته و پرستیدند. آیا ندیدند که آن مجسمه بی‌روح با آنها سخنی نمی‌گوید و آنها را به راهی هدایت نمی‌کند؟ (آری دیدند و از جهل و عناد باز) پی آن گوساله گرفتند و مردمی ستمکار بودند.'),
(1103, 7, 149, 'و چون به خود آمدند و از آن عمل پشیمان شدند و دیدند که درست گمراهند (با خود) گفتند: اگر خدا ما را نبخشد و از ما نگذرد سخت از زیانکاران خواهیم بود.'),
(1104, 7, 150, 'و چون موسی به حال خشم و تأسف به سوی قوم خود بازگشت، به آنان گفت: شما پس از من بسیار بد حفظ الغیب من کردید! آیا در امر خدای خود عجله کردید! و الواح را به زمین انداخت و (از فرط غضب) سر برادرش (هارون) را گرفته به سوی خود می‌کشید، هارون گفت: ای فرزند مادرم، این قوم مرا خوار و زبون داشتند تا آنجا که نزدیک بود مرا به قتل رسانند، پس تو دشمنان را بر من شاد مگردان و مرا در عداد مردم ستمکار مشمار.'),
(1105, 7, 151, 'موسی (بر او شفقت کرد و) گفت: پروردگارا، من و برادرم را بیامرز و ما را در ظلّ رحمت خود داخل گردان، و تو مهربان‌ترین مهربانانی.'),
(1106, 7, 152, 'آنان که گوساله را به پرستش گرفتند (آتش قهر و) غضب خدا (در آخرت) و ذلت و خواری در حیات دنیا محقّقاً به آنها خواهد رسید، و این چنین ما دروغ‌پردازان را کیفر خواهیم کرد.'),
(1107, 7, 153, 'و آنان که مرتکب عمل زشت شدند و پس از آن عمل بد توبه کردند و (به اخلاص) ایمان آوردند، خدای تو بر آنها بعد از توبه، به یقین بخشنده و مهربان است.'),
(1108, 7, 154, 'و چون غضب موسی فرو نشست الواح تورات را بر گرفت، و در صحیفه آن تورات هدایت و رحمت الهی بود بر آن گروهی که از (قهر) خدای خود می‌ترسند.'),
(1109, 7, 155, 'و موسی هفتاد مرد از قوم خود برای وعده‌گاه ما انتخاب کرد، پس چون لرزشی سخت (به جرم درخواست دیدن خدا به چشم) آنها را در گرفت، موسی گفت: پروردگارا، اگر مشیّت نافذت تعلق گرفته بود که همه آنها و مرا هلاک کنی (کاش) پیشتر (از این وعده) می‌کردی، آیا ما را به فعل سفیهان ما هلاک خواهی کرد؟ این کار جز فتنه و امتحان تو نیست، که به این امتحان هر که را خواهی گمراه و هرکه را خواهی هدایت می‌کنی، تویی مولای ما، پس بر ما ببخش و ترحم کن، که تویی بهترین آمرزندگان.'),
(1110, 7, 156, 'و سرنوشت ما را هم در این دنیا و هم در عالم آخرت نیکویی و ثواب مقدر فرما، که ما به سوی تو بازگشته‌ایم. خدا فرمود: عذابم را به هر که خواهم رسانم، و رحمت من همه موجودات را فرا گرفته و البته آن را برای آنان که راه تقوا پیش گیرند و زکات می‌دهند و به آیات ما می‌گروند حتم و لازم خواهم کرد.'),
(1111, 7, 157, 'هم آنان که پیروی کنند از آن رسول (ختمی) و پیغمبر امّی که در تورات و انجیلی که در دست آنهاست (نام و نشان و اوصاف) او را نگاشته می‌یابند که آنها را امر به هر نیکویی و نهی از هر زشتی خواهد کرد و بر آنان هر طعام پاکیزه و مطبوع را حلال، و هر پلید منفور را حرام می‌گرداند، و احکام پر رنج و مشتقی را که چون زنجیر به گردن خود نهاده‌اند از آنان برمی‌دارد (و دین آسان و موافق فطرت بر خلق می‌آورد). پس آنان که به او گرویدند و از او حرمت و عزت نگاه داشتند و یاری او کردند و نوری را که به او نازل شد پیروی نمودند آن گروه به حقیقت رستگاران عالمند.'),
(1112, 7, 158, '(ای رسول ما به خلق) بگو که من بر همه شما جنس بشر رسول خدایم، آن خدایی که فرمانروایی آسمانها و زمین از آن اوست، هیچ خدایی جز او نیست که زنده می‌کند و می‌میراند، پس باید ایمان به خدا آرید و هم به رسول او پیغمبر امّی (که از هیچ کس جز خدا تعلیم نگرفته) آن پیغمبری که بس به خدا و سخنان او ایمان دارد، و پیرو او شوید، باشد که هدایت یابید.'),
(1113, 7, 159, 'و جماعتی از قوم موسی هستند که به دین حق هدایت کنند و به آن دین حکم و دادگری نمایند.'),
(1114, 7, 160, 'و قوم موسی را به دوازده سبط منشعب کردیم که هر سبطی طایفه‌ای باشند. و چون امت موسی (در آن بیابان بی‌آب) از او آب طلبیدند به موسی وحی کردیم که عصای خود را بر سنگ زن، پس دوازده چشمه آب از آن سنگ جوشید و جاری شد، هر قبیله آبشخور خود را دانستند. و ما به واسطه ابر بر سر آنها سایه افکندیم و نیز بر آنها من و سلوی (مرغ و ترنجبین) فرستادیم (تا تغذیه کنند، و گفتیم) از لذیذ و پاکیزه قوتی که روزیتان کردیم تناول کنید. و آنها نه بر ما بلکه بر خویش ستم می‌کردند.'),
(1115, 7, 161, 'و چون به قوم موسی امر شد که در این شهر (بیت المقدس) مسکن کنید و از هر چه خواستید از طعامهای این شهر تناول کنید و در دعا بگویید که پروردگارا، گناهان ما را بریز، و به این در به تواضع و سجده درآیید تا از خطاهای شما درگذریم، که ما نیکوکاران را فزون‌تر احسان می‌کنیم.'),
(1116, 7, 162, 'آن گاه ستمکاران آنها این امر و گفتاری را که به آنها گفته شده بود به گفتار دیگری تبدیل کردند، ما هم به کیفر مخالفت و ستمکاری بر آنها از آسمان عذابی سخت فرود آوردیم.'),
(1117, 7, 163, 'و (ای رسول ما) بنی اسرائیل را از آن قریه که در ساحل دریا بود (دِه اَیْله و مَدْین) بازپرس آن گاه که از حکم (تعطیل روز) شنبه تجاوز کردند، آن‌گاه که (برای امتحان) به روز شنبه ماهیان در مشارع پیرامون دریا پدیدار شده و در غیر آن روز اصلا پدید نمی‌آمدند. بدین گونه ما آنان را به سبب فسق و نافرمانی آزمایش می‌کردیم.'),
(1118, 7, 164, 'و چون (پند و موعظه ناصحین در آنها اثری نکرد) جمعی از آن گروه گفتند: چرا قومی را که از جانب خدا به هلاک و یا به عذاب سخت محکومند باز موعظه می‌کنید؟ ناصحان گفتند: پند ما معذرت (و اتمام حجت) است از جانب پروردگارتان، و دیگر آنکه شاید (اثر کند و) تقوا پیشه کنند.'),
(1119, 7, 165, 'و چون هر چه به آنها تذکر داده شد در آن غفلت ورزیدند ما هم آن جماعت را که از کار بد منع می‌نمودند نجات بخشیدیم و آنان را که ظلم و ستمکاری کردند به کیفر فسقشان به عذابی سخت گرفتار کردیم.'),
(1120, 7, 166, 'پس چون سرکشی و تکبر کرده و آنچه ممنوع بود مرتکب شدند به آنها گفتیم به شکل بوزینه شوید و بسی دور و بازمانده از رحمت خدا باشید.'),
(1121, 7, 167, 'و آن‌گاه که خدای تو اعلام نمود که تا روز قیامت کسی را به عقوبت و عذاب سخت بر آنها برانگیزد، که پروردگار تو همانا زود کیفر کننده (ستمکاران) و (به خلق) بسیار بخشنده و مهربان است.'),
(1122, 7, 168, 'و بنی اسرائیل را در روی زمین به شعبی متفرق ساختیم، بعضی از آنها صالح و درستکارند و برخی فروتر از آنان (در درجه و منزلت)، و آنها را به خوشی‌ها و ناخوشی‌ها بیازمودیم، باشد که (به حکم حق) بازگردند.'),
(1123, 7, 169, 'پس از آنکه پیشینیانشان درگذشتند اخلاف و بازماندگانشان وارث کتاب آسمانی شدند در حالی که متاع پست این دنیا را برمی‌گزینند و گویند ما بخشیده خواهیم شد، و چنانچه مثل آن مال از متاع دنیا که یافتند باز (از هر راه حرام و خیانت) بیابند برگیرند. آیا از آنان پیمان کتاب آسمانی گرفته نشد که به خدا جز حرف حق و سخن راست نسبت ندهند و آنچه در کتاب است درس گیرند؟ و منزل ابدی آخرت برای مردم پرهیزکار بسی بهتر است، آیا تعقل نمی‌کنید؟'),
(1124, 7, 170, 'و آنان که متوسل به کتاب آسمانی شوند و نماز به پا دارند، ما اجر درستکاران را ضایع نخواهیم گذاشت.'),
(1125, 7, 171, 'و آن‌گاه که کوه طور را از جای برکندیم و مانند قطعه ابری بر فراز سر آنها برآوردیم که پنداشتند بر آنها فرو خواهد افتاد (و امر کردیم) دستور توراتی که به شما آمد با قوت (ایمان و عقیده محکم) اخذ کنید و آنچه در آن مذکور است متذکر شوید، باشد که پرهیزکار شوید.'),
(1126, 7, 172, 'و به یاد آر هنگامی که خدای تو از پشت فرزندان آدم ذرّیّه آنها را برگرفت و آنها را بر خودشان گواه ساخت که آیا من پروردگار شما نیستم؟ همه گفتند: بلی، ما گواهی دهیم. (برخی مفسرین گفتند: مراد ظهور ارواح فرزندان آدم است در نشأه ذرّ و عالم روح و گواهی آنها به نور تجرّد و شهود به توحید خدا و ربّانیت او در عوالم ملک و ملکوت.) (و ما این گواهی گرفتیم) که دیگر در روز قیامت نگویید: ما از این واقعه غافل بودیم.'),
(1127, 7, 173, 'یا آنکه نگویید که منحصرا پدران ما به دین شرک بودند و ما هم فرزندان بعد از آنها بودیم، آیا به عمل زشت اهل باطل ما را به هلاکت خواهی رسانید؟'),
(1128, 7, 174, 'و بدین گونه ما آیات خود را تفصیل می‌دهیم (تا آیات ما را بفهمند) و باشد که بازگردند.'),
(1129, 7, 175, 'و بخوان بر این مردم (بر قوم یهود) حکایت آن کس (بَلْعَم باعور) را که ما آیات خود را به او عطا کردیم، و او از آن آیات بیرون رفت و شیطان او را تعقیب کرد تا از گمراهان عالم گردید.'),
(1130, 7, 176, 'و اگر می‌خواستیم به آن آیات او را رفعت مقام می‌بخشیدیم، لیکن او به زمین (تن) فروماند و پیرو هوای نفس گردید، و در این صورت مثل و حکایت حال او به سگی ماند که اگر بر او حمله کنی و یا او را به حال خود واگذاری به عوعو زبان کشد. این است مثل مردمی که آیات ما را بعد از علم به آن تکذیب کردند. پس این حکایت بگو، باشد که به فکر آیند.'),
(1131, 7, 177, 'مثل حال گروهی که آیات ما را تکذیب کردند و به خویش ستم می‌کردند بسیار بد مثلی است.'),
(1132, 7, 178, 'هر که را خدا هدایت فرماید هم اوست که هدایت یافته، و آنها را که به گمراهی واگذارد هم آنان زیانکاران عالمند.'),
(1133, 7, 179, 'و محققا بسیاری از جن و انس را برای جهنم آفریدیم، چه آنکه آنها را دلهایی است بی‌ادراک و معرفت، و دیده‌هایی بی‌نور و بصیرت، و گوشهایی ناشنوای حقیقت، آنها مانند چهارپایانند بلکه بسی گمراه‌ترند، آنها همان مردمی هستند که غافل‌اند.'),
(1134, 7, 180, 'و خدا را نیکوترین نامهاست، بدانها خدا را بخوانید، و آنان را که در نامهای او به انحراف می‌گرایند به خود واگذارید، که به زودی کردار بدشان را مجازات خواهند دید.'),
(1135, 7, 181, 'و از خلقی که آفریده‌ایم فرقه‌ای به حق هدایت می‌کنند و به حق حکم و دادگری می‌کنند.'),
(1136, 7, 182, 'و آنان که آیات ما را تکذیب کردند به زودی آنها را به عذاب و هلاکت می‌افکنیم از جایی که فهم آن نکنند.'),
(1137, 7, 183, 'روزی چند به آنها مهلت دهیم، که همانا مکر و عقاب من بس شدید است.'),
(1138, 7, 184, 'آیا این مردم فکر نکردند که صاحب آنها (یعنی پیامبر) هرگز جنونی در او نیست؟ او فقط ترساننده خلق است (از اهوال قیامت) به بیانی روشن.'),
(1139, 7, 185, 'آیا فکر و نظر در ملکوت آسمانها و زمین و در هر چه خدا آفریده نکردند و در این که اجل و مرگ آنها بسا باشد که به آنان بسیار نزدیک شده باشد؟ پس به چه حدیثی بعد از این (کتاب مبارک آسمانی) ایمان خواهند آورد؟'),
(1140, 7, 186, 'هر که را خدا گمراه خواست هیچ کس رهنمای او نباشد، و چنین گمراهان را خدا در طغیان و سرکشی خود به حیرت و ضلالت واگذارد.'),
(1141, 7, 187, '(ای رسول ما) از تو احوال و ساعت قیامت را سؤال خواهند کرد که چه وقت فراخواهد رسید؟ پاسخ ده که علم آن منحصرا نزد ربّ و خدای من است، کسی به جز او آن را به هنگامش آشکار نکند، (شأن) آن ساعت در آسمانها و زمین بسی سنگین و عظیم است، نیاید شما را مگر ناگهانی. از تو می‌پرسند گویی تو کاملا بدان آگاهی. بگو: علم آن ساعت محققا نزد خداست، لیکن اکثر مردم آگاه نیستند.'),
(1142, 7, 188, 'بگو که من مالک نفع و ضرر خویش نیستم مگر آنچه خدا خواسته، و اگر من از غیب (جز آنچه به وحی می‌دانم) آگاه بودم بر خیر و نفع خود همیشه می‌افزودم و هیچ‌گاه زیان و رنج نمی‌دیدم، من نیستم مگر رسولی ترساننده، و بشارت دهنده گروهی که اهل ایمانند.'),
(1143, 7, 189, 'اوست خدایی که همه شما را از یک تن بیافرید و از (نوع) او نیز جفتش را مقرر داشت تا به او انس و آرام گیرد، و چون با او خلوت کرد باری سبک برداشت، پس با آن بار حمل چندی بزیست تا سنگین شد، آن‌گاه هر دو خدا و پروردگار خود را خواندند که اگر به ما فرزندی صالح (و تندرست) عطا کردی البته از شکرگزاران خواهیم بود.'),
(1144, 7, 190, 'پس چون به آن پدر و مادر فرزندی صالح (و تندرست) عطا کرد مشرک شدند و برای خدا در آنچه به آنها عطا کرد شریک قرار دادند (یعنی فرزندان خود را به نام بتها نامیدند مثل عبد اللات و عبد العزی)، و خدای تعالی برتر است از آنچه شریک او سازند.'),
(1145, 7, 191, 'آیا این چیزهایی را که هیچ موجود را خلق نتوانند کرد و خود مخلوق (خدا) هستند شریک خدا قرار می‌دهند؟'),
(1146, 7, 192, 'در صورتی که آنها نه هرگز به مشرکان و نه به خود یاری توانند کرد.'),
(1147, 7, 193, 'و اگر آنها را (یعنی بت‌پرستان را) به راه هدایت بخوانید پیروی شما (مؤمنان) نخواهند کرد، بر شما یکسان است که آنها را بخوانید یا خاموش بمانید.'),
(1148, 7, 194, 'غیر خدا هر آن کس را که شما (مشرکان و یهود و نصاری) به خدایی می‌خوانید (مانند بتان و عیسی و عُزَیر و غیره) به حقیقت همه مثل شما بندگانی هستند، اگر راستگویید آنها را بخوانید تا مشکلات و حوائج شما را روا کنند.'),
(1149, 7, 195, 'آیا آن بتان را پایی است که راهی پیمایند یا دستی که از آستین قدرت بیرون آرند، یا چشم و گوشی که به آن ببینند و بشنوند؟ بگو: شریکان (باطل) خود را بخوانید و هر حیلت که با من می‌توانید به کار برید بی‌آنکه هیچ مرا مهلت بدهید.'),
(1150, 7, 196, 'مرا دوست و یاور به حقیقت خدایی است که این کتاب (قرآن) را فرستاده و اوست که نیکوکاران را دوستی و سرپرستی می‌کند.'),
(1151, 7, 197, 'و آنهایی را که شما خدا می‌خوانید جز خدای یکتا هیچ یک قدرت بر یاری شما بلکه بر یاری خود ندارند.'),
(1152, 7, 198, 'و اگر آنها را به راه هدایت خوانید نخواهند شنید (یعنی بتان چون گوش و بت‌پرستان چون هوش ندارند دعوت اسلام را نپذیرند. و ای رسول ما یا ای اهل عقل و بینش) تو می‌بینی که آنها (بتان یا بت‌پرستان) در تو می‌نگرند ولی نمی‌بینند.'),
(1153, 7, 199, 'طریقه عفو و بخشش پیش‌گیر و امت را به نیکوکاری امر کن و از مردم نادان روی بگردان.'),
(1154, 7, 200, 'و چنانچه بخواهد از طرف شیطان (انس و جن) در تو وسوسه و جنبشی پدید آید به خدا پناه بر که او به حقیقت شنوا و داناست.'),
(1155, 7, 201, 'چون اهل تقوا را از شیطان وسوسه و خیالی به دل فرا رسد همان دم خدا را به یاد آرند و همان لحظه بصیرت و بینایی پیدا کنند.'),
(1156, 7, 202, 'و مشرکان را برادرانشان (یعنی شیاطین انس و جن) به راه ضلالت و گمراهی می‌کشند و هیچ کوتاهی نمی‌کنند.'),
(1157, 7, 203, 'و هر زمان که تو آیتی بر آنها نیاوری بر تو اعتراض کنند که چرا آیتی فراهم نساختی؟ بگو: من تنها پیروی از آنچه از خدایم به من وحی رسد خواهم کرد، این آیات قرآن است مایه بصیرت‌هایی از جانب پروردگار شما و هدایت و رحمتی برای گروهی که ایمان آورند.'),
(1158, 7, 204, 'و چون قرآن قرائت شود همه گوش بدان فرا دهید و سکوت کنید، باشد که مورد لطف و رحمت حق شوید.'),
(1159, 7, 205, 'خدای خود را با تضرّع و پنهانی و بی‌آنکه آواز برکشی در صبح و شام یاد کن و از غافلان مباش.'),
(1160, 7, 206, 'آنان (یعنی ارواح و فرشتگانی) که در حضور پروردگار تواند هیچ‌گاه از بندگی او سرکشی نکنند و پیوسته به تسبیح و تنزیه ذات احدیت و به سجده او مشغولند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '(ای رسول ما) امت از تو حکم انفال را سؤال کنند (یعنی غنایمی که بدون جنگ از دشمنان به دست مسلمانان آید و زمینهای خراب بی‌مالک، معادن، بیشه‌ها، فراز کوهها، کف رودها، ارث کسی که وارث ندارد، قطایع ملوک، خلاصه ثروتی که بی‌رنج مردم حاصل شود) جواب ده که انفال مخصوص خدا و رسول است (که رسول و جانشینانش به هر کس و هر قدر صلاح دانند ببخشند) پس شما مؤمنان باید از خدا بترسید و در رضایت و مسالمت و اتحاد بین خودتان بکوشید و خدا و رسول او را اطاعت کنید اگر اهل ایمانید.'),
(1162, 8, 2, 'مؤمنان حقیقی آنانند که چون ذکری از خدا شود دلهاشان ترسان و لرزان شود و چون آیات خدا را بر آنها تلاوت کنند بر مقام ایمانشان بیفزاید و به خدای خود در هر کار توکل می‌کنند.'),
(1163, 8, 3, 'آنان که نماز را به پا می‌دارند و از هر چه روزی آنها کرده‌ایم انفاق می‌کنند.'),
(1164, 8, 4, 'آنها به راستی و حقیقت اهل ایمانند که نزد خدا مراتب بلند و آمرزش و روزی نیکو دارند.'),
(1165, 8, 5, 'همان گونه که خدا تو را از خانه خود به حق (در جنگ بدر) بیرون آورد و گروهی از مؤمنان، سخت رأی خلاف و کراهت اظهار کردند (و تو به مخالفت آنها از حکم حق بازنگشتی، در امر انفال و غیر آن هم به خواهش مردم توجه مکن، پیرو امر خدا باش و از خلق ابدا اندیشه مدار).'),
(1166, 8, 6, '(مردم نادان) در حکم حق با آنکه حق آشکار و روشن گردید با تو جدل و نزاع خواهند کرد (و چنان حکم حق و طاعت خدا بر آنها دشوار و سخت است) که گویی معاینه می‌نگرند که آنها را به جانب مرگ می‌کشند.'),
(1167, 8, 7, 'به یاد آر هنگامی که خدا به شما وعده فتح بر یکی از دو طایفه را می‌داد (غلبه بر سپاه قریش یا کاروان تجاری شام) و شما مسلمین مایل بودید که آن طایفه که شوکت و سلاحی همراه ندارند نصیب شما شود، و خدا می‌خواست که حق را با سخنان خود (که وعده پیروزی حق را داده است) ثابت گرداند و از بیخ و بن ریشه کافران را برکند.'),
(1168, 8, 8, 'تا حق را محقق و پایدار کند و باطل را محو و نابود سازد هر چند بدکاران را خوش نیاید.'),
(1169, 8, 9, '(به یاد آرید) هنگامی که استغاثه و زاری به پروردگار خود می‌کردید، پس دعای شما را اجابت کرد که من سپاهی منظم از هزار فرشته به مدد شما می‌فرستم.'),
(1170, 8, 10, 'و این مدد فرشتگان را خدا نفرستاد مگر آنکه بشارت و مژده فتح باشد و تا دلهای شما (به وعده خدا) مطمئن شود و (بدانید که) نصرت و فیروزی نیست مگر از جانب خدا، که خدا را کمال قدرت و حکمت است.'),
(1171, 8, 11, '(یاد آرید) هنگامی که خواب راحت را بر شما فرو پوشاند برای اینکه از جانب او ایمنی یابید و از آسمان آبی می‌فرستاد که شما را به آن آب پاک گرداند و وسوسه و کید شیطان را از شما دور سازد و دلهای شما را محکم و استوار گرداند و گامهای شما را استوار بدارد.'),
(1172, 8, 12, '(و یاد آر ای رسول) آن‌گاه که پروردگار تو به فرشتگان وحی کرد که من با شمایم، پس مؤمنان را ثابت قدم بدارید، که همانا من ترس در دل کافران می‌افکنم، پس گردنها را بزنید و همه انگشتان را قطع کنید.'),
(1173, 8, 13, 'این کیفر کافران برای آن است که با خدا و رسول او سخت ضدّیت و مخالفت کردند و هر کس با خدا و رسول او راه شقاق و مخالفت پیماید (بترسد که) عقاب خدا بسیار سخت است.'),
(1174, 8, 14, 'این (عذاب مختصر قتل و اسارت) را بچشید و (بدانید که) برای کافران در قیامت عذاب سخت آتش دوزخ مهیّاست.'),
(1175, 8, 15, 'ای اهل ایمان، هرگاه با گروه مهاجم کافران در میدان کارزار روبرو شوید مبادا پشت به آنها کرده و از جنگ بگریزید.'),
(1176, 8, 16, 'و هر که در روز جنگ به آنها پشت نماید و فرار کند به طرف غضب و خشم خدا روی آورده و جایگاهش دوزخ که بدترین منزل است خواهد بود مگر آنکه از میمنه به میسره و یا از قلب به جناح برای مصالح جنگی رود یا از فرقه‌ای به یاری فرقه‌ای دیگر شتابد.'),
(1177, 8, 17, '(ای مؤمنان) نه شما بلکه خدا کافران را کشت و (ای رسول) چون تو تیر (یا خاک) افکندی نه تو بلکه خدا افکند (تا کافران را شکست دهد) و برای آنکه مؤمنان را به آزمونی نیکو از سوی خود بیازماید، که خدا شنوا و داناست.'),
(1178, 8, 18, 'این (فتح و غلبه در جنگ به خواست خدا) شما را نصیب شد و محققا خدا بی‌اثر کننده مکر کفار است.'),
(1179, 8, 19, '(ای کافران) اگر شما از خدا فتح و نصرت می‌طلبید اینک فتح کامل شما را رسید (دین اسلام و قرآن که بهترین فتح و فیروزی و کاملترین وسیله سعادت خلق است از جانب خدا آمد) اکنون اگر (از کفر و عناد با حق) دست بردارید شما را بهتر است و اگر دیگر بار بازآیید ما هم شما را باز کیفر و عذاب کنیم و هرگز جمعیت و سپاه شما هر چند بسیار باشد شما را سودمند نخواهد بود، و خدا البته با اهل ایمان است.'),
(1180, 8, 20, 'ای کسانی که ایمان آورده‌اید، خدا و رسول را اطاعت کنید و از او سرنپیچید در حالی که شما (سخنان حق را) می‌شنوید.'),
(1181, 8, 21, 'و شما مؤمنان چون کسانی (منافقان) نباشید که گفتند شنیدیم، و به حقیقت نمی‌شنوند.'),
(1182, 8, 22, 'بدترین جانوران نزد خدا کسانی هستند که (از شنیدن و گفتن حرف حق) کر و لالند که اصلا تعقل نمی‌کنند.'),
(1183, 8, 23, 'اگر خدا در آنها خیر و صلاحی می‌دید آنها را شنوا می‌کرد و اگر هم شنوا کند باز رو گردانند و اعراض کنند.'),
(1184, 8, 24, 'ای اهل ایمان، چون خدا و رسول شما را به آنچه مایه حیات ابدی شماست (یعنی ایمان) دعوت کنند اجابت کنید و بدانید که خدا در میان شخص و قلب او حایل می‌شود (و از اسرار درونی همه آگاه است) و همه به سوی او محشور خواهید شد.'),
(1185, 8, 25, 'و بترسید از بلایی که چون آید تنها مخصوص ستمکاران شما نباشد (بلکه ظالمان و مظلومان همه را فرا گیرد) و بدانید که عقاب خدا بسیار سخت است.'),
(1186, 8, 26, 'و به یاد آرید زمانی که عده قلیلی بودید که شما را ضعیف و خوار در زمین (مکه) می‌شمردند و از مردم (مشرک) بر خود ترسان بودید که مبادا شما را (از روی زمین) برچینند، بعد از آن خدا شما را در پناه خود آورد و به یاری خود نیرومندی و نصرت به شما عطا کرد و از پاکیزه‌ترین (غنائم و) طعامها روزی شما فرمود، باشد که شکر به جای آرید.'),
(1187, 8, 27, 'ای کسانی که ایمان آوردید، زنهار (در کار دین) با خدا و رسول و در امانتهای خود با یکدیگر خیانت مکنید در صورتی که می‌دانید.'),
(1188, 8, 28, 'و محققا بدانید که شما را اموال و فرزندان، فتنه و ابتلایی بیش نیست و در حقیقت اجر عظیم نزد خداست.'),
(1189, 8, 29, 'ای اهل ایمان، اگر خدا ترس و پرهیزکار شوید خدا به شما فرقان بخشد (یعنی دیده بصیرت دهد تا به نور باطن حق را از باطل فرق گذارید) و گناهان شما را بپوشاند و شما را بیامرزد، که خدا دارای فضل و رحمت بزرگ است.'),
(1190, 8, 30, 'و (به یاد آر) وقتی که کافران با تو مکر می‌کردند تا تو را به بند کشند یا به قتل رسانند یا از شهر بیرون کنند، و آنها (با تو) مکر می‌کنند خدا هم (با آنها) مکر می‌کند و خدا بهتر از هر کس مکر تواند کرد.'),
(1191, 8, 31, 'و چون بر آنان آیات ما تلاوت شود گویند: شنیدیم، اگر ما هم می‌خواستیم مانند آن می‌گفتیم، این چیزی جز افسانه پیشینیان نیست.'),
(1192, 8, 32, 'و آن‌گاه که گفتند: خدایا اگر این قرآن به راستی بر حق و از جانب توست پس یا بر سرمان سنگهایی از آسمان ببار یا ما را به عذابی دردناک گرفتار ساز!'),
(1193, 8, 33, 'ولی خدا تا تو (پیغمبر رحمت) در میان آنها هستی آنان را عذاب نخواهد کرد و نیز مادامی که به درگاه خدا توبه و استغفار کنند باز خدا آنها را عذاب نکند.'),
(1194, 8, 34, 'و آنها چه کردند که خدا عذابشان نکند؟! در صورتی که راه مسجد الحرام را (به روی بندگان خدا) می‌بندند و آنها سرپرستان (شایسته تولیت) آن نیستند، سرپرستان آن به جز اهل تقوا نباشند، لیکن اکثر مردم آگاه نیستند.'),
(1195, 8, 35, 'و نماز آنها در خانه کعبه جز صفیر و کف‌زدنی (که خلق را از خانه خدا منع می‌کند) چیزی دیگر نیست، پس (در آخرت به آنها خطاب شود) بچشید طعم عذاب را به کیفر آنکه به خدا کافر بودید.'),
(1196, 8, 36, 'کافران اموالشان را انفاق می‌کنند برای این مقصود که راه خدا را ببندند پس به زودی مالهایشان بر سر این خیال باطل برود و حسرتش بر دل آنها بماند و آن‌گاه مغلوب نیز خواهند شد، و کافران را به سوی جهنم جمعا رهسپار سازند.'),
(1197, 8, 37, 'تا آنکه خدا پلید را از پاکیزه جدا سازد و پلیدان را بعضی با بعضی دیگر درآمیزد و با هم گرد آورد آن‌گاه همه را در آتش دوزخ افکند، که آنها زیانکاران عالمند.'),
(1198, 8, 38, '(ای رسول ما) کافران را بگو که اگر (از کفر خود) دست کشیده و به راه ایمان بازآیند هر چه از پیش کرده‌اند بخشیده شود، و اگر باز (به کفر و عصیان) روی آرند سنّت الهی درباره پیشینیان درگذشته است (یعنی حکم ازلی بر این قرار گرفته که مؤمنان را عزیز و بهشتی کند و کافران را ذلیل و دوزخی گرداند).'),
(1199, 8, 39, 'و (ای مؤمنان) با کافران بجنگید تا دیگر فتنه و فسادی نماند و آیین همه دین خدا گردد، و چنانچه دست (از کفر) کشیدند خدا به اعمالشان بیناست.'),
(1200, 8, 40, 'و اگر پشت کردند، پس بدانید که البته خدا یار شماست، که بهترین یار و بهترین یاور است.'),
(1201, 8, 41, 'و (ای مؤمنان) بدانید که هر چه غنیمت و فایده برید خمس آن خاص خدا و رسول و خویشان او و یتیمان و فقیران و در راه سفر ماندگان (از خاندان او) است، اگر به خدا و به آنچه بر بنده خود (محمّد صلّی اللّه علیه و آله و سلم) در روز فرقان، روزی که دو سپاه (اسلام و کفر در جنگ بدر) روبرو شدند نازل کرده‌ایم ایمان آورده‌اید، و خدا بر هر چیز تواناست.'),
(1202, 8, 42, 'همان زمانی که سپاه شما در وادی نزدیک و دشمن به مکانی دور (از شهر مدینه) بود و آن کاروان (تجاری ابو سفیان) فرو دست شما قرار داشتند، و اگر این کارزار به وعده و قرار شما با دشمن مقرر می‌شد در وعده‌گاه (از خوف و اندیشه در جنگ) اختلاف می‌کردید لیکن برای آنکه حکم ازلی و قضای حتمی را که خدا مقدر فرموده اجرا سازد (این رویارویی رخ داد) تا هر که هلاک شدنی است بعد از اتمام حجت هلاک شود و هر که لایق حیات ابدی است به اتمام حجت به حیات ابدی رسد و همانا خدا شنوا و داناست.'),
(1203, 8, 43, 'و (یاد آر ای رسول) آن‌گاه که خدا دشمنانت را در خواب به تو اندک نشان داد (تا قویدل باشی) و اگر سپاه دشمن را بسیار به چشم تو نشان داده بود (شما مسلمانان) کاملا هراسان و بددل شده و در امر (رفتن به جنگ) جدل و مخالفت می‌کردید، لیکن خدا (شما را از آسیب دشمن) به سلامت داشت، که او دانا و متصرف در اندیشه‌های درونی دلهای خلق است.'),
(1204, 8, 44, 'و (یاد آر) زمانی که خدا دشمنان را هنگامی که مقابل شدید در چشم شما کم نمودار کرد (تا از آنها نیندیشید) و شما را نیز در چشم دشمن کم نمود (تا تجهیز کامل نکنند) تا خداوند آن را که در قضای حتمی خود مقدر نموده (یعنی غلبه اسلام) اجرا فرماید، و به سوی اوست بازگشت امور.'),
(1205, 8, 45, 'ای کسانی که ایمان آورده‌اید، هر گاه با فوجی (از دشمن) مقابل شدید پایداری کنید و خدا را پیوسته یاد آرید، باشد که فیروزمند و فاتح گردید.'),
(1206, 8, 46, 'و همه پیرو فرمان خدا و رسول باشید و هرگز راه اختلاف و تنازع نپویید که در اثر تفرقه ترسناک و ضعیف شده و قدرت و عظمت شما نابود خواهد شد، بلکه همه باید یکدل، پایدار و صبور باشید، که خدا همیشه با صابران است.'),
(1207, 8, 47, 'و شما مؤمنان مانند آن کسان (یعنی کافران) نباشید که برای هوس و غرور و ریا و تظاهر از دیار خویش خارج شدند و از راه خدا منع می‌کنند. و (بترسند که) علم خدا به هر چه کنند محیط است.'),
(1208, 8, 48, 'و (یاد آر) آن گاه که شیطان کردار (زشت) آنان را در نظرشان زیبا نمود و گفت: امروز احدی از مردم بر شما غالب نخواهد گشت و من هنگام سختی یار و فریادرس شما خواهم بود (و بدین سخنان کفار قریش را مغرور کرد) تا آن‌گاه که دو سپاه (اسلام و کفر) روبرو شدند شیطان پای به فرار گذاشت و گفت که من از شما بیزارم، من قوایی (از فرشتگان آسمان) می‌بینم که شما نمی‌بینید و من از خدا می‌ترسم، و عقاب خداوند بسیار سخت است.'),
(1209, 8, 49, 'و (یاد آر) آن‌گاه که منافقان و بیماردلان با هم می‌گفتند که این مسلمین به دین خود مغرور و فریفته‌اند، و حال آنکه هر کس بر خدا توکل کند خدا غالب مقتدر و داناست.'),
(1210, 8, 50, 'و اگر بنگری سختی حال کافران را هنگامی که فرشتگان جان آنها را می‌گیرند و بر روی و پشت آنها می‌زنند و (می‌گویند) بچشید طعم عذاب سوزنده را!'),
(1211, 8, 51, 'این عقوبت اعمال زشتی است که به دست خویش فرستادید و خدا به هیچ یک از بندگان هرگز کمترین ستم نخواهد کرد.'),
(1212, 8, 52, 'همان‌طور که خوی فرعونیان و کافران پیش از زمان آنها بر این بود که به آیات خدا کافر شدند، خدا هم آنان را به کیفر گناهانشان بگرفت، که همانا خدا توانا و سخت‌کیفر است.'),
(1213, 8, 53, 'این (عذاب) از آن جهت است که خدا بر آن نیست که نعمتی را که به قومی عطا کرد تغییر دهد تا وقتی که آن قوم حال خود را تغییر دهند، و خدا شنوا و داناست.'),
(1214, 8, 54, 'همانطور که خوی فرعونیان و کافران پیش از آنان بر این بود که آیات خدای خود را تکذیب کردند ما هم آنها را به کیفر گناهانشان هلاک نمودیم و فرعونیان را غرق کردیم و همه آنان که هلاک نمودیم ظالم و ستمکار بودند.'),
(1215, 8, 55, 'بدترین جانوران نزد خدا آنان هستند که کافر شدند و ابدا ایمان نخواهند آورد.'),
(1216, 8, 56, 'کسانی از آنان که با آنان عهد بستی (مانند یهود بنی قریظه) آن‌گاه عهد خود را در هر مرتبه می‌شکنند و راه تقوا نپیمایند.'),
(1217, 8, 57, 'پس چون بر آنان دست یابی چندان به تهدید و مجازات آنان پرداز که پیروانشان را پراکنده سازی، باشد که متذکر شوند.'),
(1218, 8, 58, 'و چنانچه از خیانتکاری گروهی (از معاهدین خود) سخت می‌ترسی در این صورت تو نیز با حفظ عدل و درستی، عهد آنها را نقض کن، که خدا خیانتکاران را دوست نمی‌دارد.'),
(1219, 8, 59, 'کافران هرگز نپندارند که پیش افتادند، (نه) هرگز (خدا و رسول خدا را) زبون نتوانند کرد.'),
(1220, 8, 60, 'و شما (ای مؤمنان) در مقام مبارزه با آن کافران خود را مهیا کنید و تا آن حد که بتوانید از آذوقه و تسلیحات و آلات جنگی و اسبان سواری زین کرده برای تهدید و تخویف دشمنان خدا و دشمنان خودتان فراهم سازید و نیز برای قوم دیگری که شما بر (دشمنی) آنان مطلع نیستید (مراد منافقانند که ظاهرا مسلم و باطنا کافر محض‌اند) و خدا بر آنها آگاه است. و آنچه در راه خدا صرف می‌کنید خدا تمام و کامل به شما عوض خواهد داد و هرگز به شما ستم نخواهد شد.'),
(1221, 8, 61, 'و اگر دشمنان به صلح و مسالمت تمایل داشتند تو نیز مایل به صلح باش و کار خود به خدا واگذار که خدا شنوا و داناست.'),
(1222, 8, 62, 'و اگر دشمنان به فکر فریب دادن تو باشند البته خدا تو را کفایت خواهد کرد، اوست که به نصرت خود و یاری مؤمنان تو را مؤیّد و منصور گردانید.'),
(1223, 8, 63, 'و الفت داد دلهای مؤمنان را، دلهایی که اگر تو با تمام ثروت روی زمین می‌خواستی میان آنها الفت دهی نتوانستی، لیکن خدا تألیف قلوب آنها کرد که او مقتدر و داناست.'),
(1224, 8, 64, 'ای رسول، خدا تو را کفایت است و مؤمنانی که پیروان تواند.'),
(1225, 8, 65, 'ای رسول، مؤمنان را بر جنگ ترغیب کن که اگر بیست نفر از شما صبور و پایدار باشند بر دویست نفر غالب خواهند شد و اگر صد نفر بوده بر هزار نفر از کافران غلبه خواهند کرد، زیرا آنها گروهی بی‌دانشند.'),
(1226, 8, 66, 'اکنون (پس از جنگ بدر) خدا بر شما تخفیف داد و معلوم کرد که در شما ضعف (ایمان) راه یافته، پس اگر صد نفر از شما صبور و پایدار باشند بر دویست نفر و اگر هزار، بر دو هزار آنان به اذن خدا غالب خواهند شد، و خدا با صابران است.'),
(1227, 8, 67, 'هیچ پیغمبری را روا نباشد که اسیران جنگی بگیرد (تا از آنان فدا گرفته و آنان را رها کند) مگر تا زمانی که خون (ناپاکان را) در زمین بسیار بریزد. شما (ای اصحاب رسول) متاع دنیا را می‌خواهید و خدا آخرت را می‌خواهد، و خدا مقتدر و کارش همه از روی حکمت است.'),
(1228, 8, 68, 'اگر نبود حکم سابق از خدا (که بر پیغمبر اسلام فدا و غنایم را روا دانست) همانا در آنچه (از آن اسیران) گرفتید به شما عذاب سخت رسیده بود.'),
(1229, 8, 69, 'پس اکنون از هر چه غنیمت گرفتید بخورید حلال و گوارای شما باد، و خداترس و پرهیزکار باشید، که خدا آمرزنده و مهربان است.'),
(1230, 8, 70, 'ای رسول، به اسیرانی که در دست شما مسلمین‌اند بگو که اگر خدا در دل شما خیر و هدایتی مشاهده کند (و ایمان آرید) در مقابل آنچه از شما گرفته شده بهتر از آن را عطا می‌کند و از گناهان شما می‌گذرد که خدا آمرزنده و مهربان است.'),
(1231, 8, 71, 'و اگر قصد خیانت با تو دارند پیش از این با خدا هم خیانت کردند و خدا (تو را) بر آنها تمکین و تسلط داد و خدا دانا و درستکار است.'),
(1232, 8, 72, 'آنان که به خدا ایمان آوردند و از وطن خود هجرت نمودند و در راه خدا با مال و جانشان کوشش و فداکاری کردند (یعنی مهاجرین مکه) و هم آنان که (به مهاجرین) منزل دادند و یاری کردند (یعنی انصار مدینه) آنها دوستدار و مددکار یکدیگرند و آنهایی که ایمان آورده‌اند لیکن مهاجرت نکرده‌اند هرگز شما دوستدار و طرفدار آنها نباشید تا وقتی که هجرت گزینند، ولی اگر از شما در کار دین و پیشرفت اسلام مدد خواستند بر شماست که آنها را یاری کنید مگر آنکه با قومی که با شما عهد و پیمان (مسالمت) بسته‌اند به خصومت برخیزند. و خدا به هر چه می‌کنید بیناست.'),
(1233, 8, 73, 'آنان که کافر شدند نیز دوستدار و مددکار یکدیگرند و شما مسلمین اگر آن را (که خدا دستور داده از اتفاق و اخوت و غیره) کار نبندید همانا فتنه و فسادی بزرگ روی زمین رخ خواهد داد.'),
(1234, 8, 74, 'و آنان که ایمان آوردند و هجرت گزیدند و در راه خدا کوشش و جانفشانی کردند و هم آنان که (مهاجران را با فداکاری) منزل دادند و یاری کردند آنها به حقیقت اهل ایمانند و هم آمرزش خدا و روزی نیکوی بهشتی مخصوص آنهاست.'),
(1235, 8, 75, 'و آنان که بعد از شما ایمان آوردند و (یا بعد از صلح حدیبیه) هجرت گزیدند و به اتفاق شما جهاد کردند آنها نیز از شما مؤمنان هستند، و ارث مراتب خویشاوندان در کتاب خدا بعضی بر بعض دیگر مقدم شده [یعنی قانون ارث بردن برادران دینی از یکدیگر، نسخ شده است. (م)]، که خدا به هر چیز داناست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'این اعلام بیزاری خدا و رسول اوست به مشرکانی که شما مسلمین با آنان عهد بسته‌اید (و آنان عهد شکستند).'),
(1237, 9, 2, 'پس به شما مشرکان تا چهار ماه دیگر مهلت داده می‌شود که در زمین (مکه) گردش و آسایش کنید، و بدانید که شما بر قدرت خدا غالب نخواهید شد و همانا خدا کافران را خوار و ذلیل خواهد کرد.'),
(1238, 9, 3, 'و در بزرگترین روز حج (روز عرفه یا روز عید که همه در مکه جمعند یا در آن روز که مؤمنان و مشرکان همه به مکه حاضرند) خدا و رسولش به مردم اعلام می‌دارند که خدا و رسول از مشرکین بیزارند. پس اگر شما مشرکان توبه کردید برایتان بسی بهتر خواهد بود، و اگر رو بگردانید بدانید که شما بر قدرت خدا غالب نیایید، و مژده عذابی دردناک به آن کافران برسان.'),
(1239, 9, 4, 'مگر آن گروه از مشرکان که با آنها عهد بسته‌اید و هیچ عهد شما نشکستند و هیچ یک از دشمنان شما را یاری نکرده باشند، پس با آنها تا مدتی که مقرر داشته‌اید عهد نگاه دارید، که خدا متقیان را دوست می‌دارد.'),
(1240, 9, 5, 'پس چون ماههای حرام (ذیقعده، ذیحجه، محرم و رجب که مدت امان است) درگذشت آن‌گاه مشرکان را هر جا یابید به قتل برسانید و آنها را دستگیر و محاصره کنید و هر سو در کمین آنها باشید، چنانچه توبه کرده و نماز به پا داشتند و زکات دادند پس از آنها دست بدارید، که خداوند بسیار آمرزنده و مهربان است.'),
(1241, 9, 6, 'و اگر یکی از مشرکان به تو پناه آورد (که از دین آگاه شود) بدو پناه ده تا کلام خدا بشنود و پس از شنیدن سخن خدا او را به مأمن و منزلش برسان، زیرا که این مشرکان مردمی نادانند.'),
(1242, 9, 7, 'چگونه با مشرکانی که عهد خدا و رسول شکستند خدا و رسولش عهد آنان نگه دارند؟ لیکن با آن مشرکان که در مسجد الحرام عهد بسته‌اید تا زمانی که آنها بر عهد خود پایدارند شما هم عهد آنها را بپایید، که خدا متقیان را دوست می‌دارد.'),
(1243, 9, 8, 'چگونه (با مشرکان عهدشکن وفای به عهد توان کرد) در صورتی که آنها اگر بر شما ظفر یابند مراعات هیچ علاقه خویشی و عهد و پیمان را نخواهند کرد! به زبان‌بازی و سخنان فریبنده شما را خشنود می‌سازند در صورتی که در دل جز کینه شما ندارند و بیشتر آنان فاسق و نابکارند.'),
(1244, 9, 9, 'آنان آیات کتاب خدا را به بهایی اندک فروختند تا آنکه راه خدا را (به روی خلق) بستند، همانا بسیار بد می‌کرده‌اند.'),
(1245, 9, 10, 'آنان در حق اهل ایمان هیچ مراعات حق خویشی یا عهد و پیمان را نمی‌کنند و هم آنها بسیار متعدی و ستمکارند.'),
(1246, 9, 11, 'پس اگر توبه کرده و نماز به پا داشتند و زکات مال دادند در این صورت برادر دینی شمایند. و ما آیات خود را برای اهل دانش و معرفت مفصّلا بیان می‌کنیم.'),
(1247, 9, 12, 'و اگر آنها سوگند خود را بعد از عهدی که بسته‌اند بشکنند و در دین شما تمسخر و طعن زنند در این صورت با آن پیشوایان کفر و ضلالت کارزار کنید که آنها را عهد و سوگند استواری نیست، باشد که بس کنند.'),
(1248, 9, 13, 'آیا با قومی که عهد و پیمان خود را شکستند و اهتمام کردند که رسول خدا را (از شهر و وطن خود) بیرون کنند قتال و کار زار نمی‌کنید؟ در صورتی که آنها اول بار به دشمنی و قتال شما برخاستند، آیا از آنها ترس و اندیشه دارید؟ و حال آنکه سزاوارتر آن است که از خدا بترسید اگر اهل ایمانید.'),
(1249, 9, 14, 'شما (ای اهل ایمان) با آن کافران به قتال و کارزار برخیزید تا خدا آنان را به دست شما عذاب کند و خوار گرداند و شما را بر آنها منصور و غالب نماید و دلهای (پر درد و غم) گروهی اهل ایمان را (به فتح و ظفر بر کافران) شفا بخشد.'),
(1250, 9, 15, 'و تا خشم دلهای آنان فرونشاند، و خدا بر هر که می‌خواهد به لطف و رحمت باز می‌گردد و خداوند دانا و درستکار است.'),
(1251, 9, 16, 'آیا چنین می‌پندارید که شما را (بدون آزمایش) به حال خود رها می‌کنند در صورتی که هنوز خدا معلوم نگردانیده که از شما چه کسانی اهل مجاهده‌اند و جز خدا و رسول و مؤمنان را هرگز دوست خود و همراز خویش نگزیده‌اند، و خدا از همه کردار شما آگاه است.'),
(1252, 9, 17, 'مشرکان را نرسد که مساجد خدا را تعمیر کنند در صورتی که بر کفر خود گواهند. اینانند که اعمالشان نابود خواهد شد و در آتش دوزخ جاوید معذّب خواهند بود.'),
(1253, 9, 18, 'منحصرا تعمیر مساجد خدا به دست کسانی است که به خدا و روز قیامت ایمان آورده و نماز به پا دارند و زکات مال خود بدهند و از غیر خدا نترسند، پس امید است که از هدایت‌یافتگان راه خدا باشند.'),
(1254, 9, 19, 'آیا رتبه سقایت و آب دادن به حاجیان (یعنی مقام عباس) و تعمیر کردن مسجد الحرام (یعنی مقام شَیْبه) را با (مقام) آن کس که به خدا و به روز قیامت ایمان آورده و در راه خدا جهاد کرده (چون علی علیه السلام) یکسان شمرید؟ هرگز آنان نزد خدا یکسان نخواهند بود، و خدا ظالمان را هدایت نخواهد کرد.'),
(1255, 9, 20, 'آنان که ایمان آوردند و از وطن هجرت گزیدند و در راه خدا به مال و جانشان جهاد کردند آنها را نزد خدا مقام بلندتری است و آنان بالخصوص رستگاران و سعادتمندان دو عالمند.'),
(1256, 9, 21, 'پروردگارشان آنان را به رحمت بی‌منتهای خود و به مقام رضا و خشنودی خویش و به بهشت‌هایی که در آنجا آنها را نعمت جاودانی است بشارت دهد.'),
(1257, 9, 22, 'در آن بهشت ابدی همیشه متنعّم خواهند بود، که نزد خدا پاداشی بزرگ است.'),
(1258, 9, 23, 'ای اهل ایمان، شما پدران و برادران خود را نباید دوست بگیرید اگر که آنها کفر را بر ایمان بگزینند، و هر کس از شما آنان را دوست گیرد چنین کسانی بی‌شک ستمکارند.'),
(1259, 9, 24, '(ای رسول ما امت را) بگو که اگر شما پدران و پسران و برادران و زنان و خویشاوندان خود و اموالی که جمع آورده‌اید و مال‌التجاره‌ای که از کسادی آن بیمناکید و منازلی را که به آن دل خوش داشته‌اید بیش از خدا و رسول و جهاد در راه او دوست می‌دارید منتظر باشید تا خدا امر خود را جاری سازد (و اسلام را بر کفر غالب و فاتح گرداند و شما دنیاطلبان بدکار از فعل خود پشیمان و زیانکار شوید) و خدا فسّاق و بدکاران را هدایت نخواهد کرد.'),
(1260, 9, 25, 'همانا خدا شما مسلمین را در مواقعی بسیار یاری کرد و نیز در جنگ حنین که فریفته و مغرور بسیاری لشکر اسلام شدید و آن لشکر زیاد اصلا به کار شما نیامد و زمین بدان فراخی بر شما تنگ شد تا آنکه همه رو به فرار نهادید.'),
(1261, 9, 26, 'آن‌گاه خدا وقار و سکینه خود را (یعنی شکوه و سطوت و جلال ربّانی را) بر رسول خود و بر مؤمنان نازل فرمود و لشکرهایی (از فرشتگان) که شما نمی‌دیدید (به مدد شما) فرستاد و کافران را (پس از آنکه غالب و قاهر بودند) به عذاب و ذلّت افکند، و این است کیفر کافران.'),
(1262, 9, 27, 'آن‌گاه خدا بعد از آن واقعه (جنگ حنین) از سر تقصیر هر که می‌خواهد (از مؤمنانی که فرار کرده هر که توبه نمود، و از کافران هر که ایمان آورد) در می‌گذرد و خداوند بسیار آمرزنده و مهربان است.'),
(1263, 9, 28, 'ای کسانی که ایمان آورده‌اید، محققا بدانید که مشرکان نجس و پلیدند و بعد از این سال (که عهدشان به پایان می‌رسد) نباید قدم به مسجد الحرام گذارند و اگر (در اثر دور شدن تجارت و ثروت آنها از شما) از فقر می‌ترسید خدا اگر بخواهد شما را به فضل خود (از خلق و از مشرکان) بی‌نیاز خواهد کرد، که خدا دانا و در کمال عنایت و حکمت است.'),
(1264, 9, 29, '(ای اهل ایمان) با هر که از اهل کتاب (یهود و نصاری) که ایمان به خدا و روز قیامت نیاورده و آنچه را خدا و رسولش حرام کرده حرام نمی‌دانند و به دین حق (و آیین اسلام) نمی‌گروند قتال و کارزار کنید تا آن‌گاه که با دست خود با ذلت و تواضع جزیه دهند.'),
(1265, 9, 30, 'و یهود گفتند: عُزَیر پسر خداست و نصاری گفتند: مسیح پسر خداست! این سخنی است که تنها بر زبان می‌رانند و با گفتار و کیش کافران مشرک پیشین نزدیکی و مشابهت دارند، خدا آنها را هلاک و نابود کند! آنان را (از حق) به کجا می‌گردانند؟!'),
(1266, 9, 31, 'علماء و راهبان خود را (از نادانی) به مقام ربوبیت شناخته و خدا را نشناختند و نیز مسیح پسر مریم را (که متولد از مادر معین و حادث و مخلوق خداست) به الوهیت گرفتند در صورتی که مأمور نبودند جز آنکه خدای یکتایی را پرستش کنند که جز او خدایی نیست، که منزه و برتر از آن است که با او شریک قرار می‌دهند.'),
(1267, 9, 32, 'کافران می‌خواهند که نور خدا را با دهانشان (به نَفَس تیره و گفتار جاهلانه خود) خاموش کنند و خدا نگذارد تا آنکه نور خود را در منتهای ظهور و حد اعلای کمال برساند هر چند کافران ناراضی و مخالف باشند.'),
(1268, 9, 33, 'اوست خدایی که رسول خود را به هدایت و دین حق فرستاد تا آن را بر همه ادیان عالم تسلط و برتری دهد هر چند مشرکان ناراضی و مخالف باشند.'),
(1269, 9, 34, 'ای اهل ایمان، بسیاری از علماء و راهبان (یهود و نصاری) اموال مردم را به باطل طعمه خود می‌کنند و (خلق را) از راه خدا منع می‌کنند و کسانی که طلا و نقره را گنجینه و ذخیره می‌کنند و در راه خدا انفاق نمی‌کنند آنها را به عذابی دردناک بشارت ده.'),
(1270, 9, 35, 'روزی که آن طلا و نقره ذخائرشان در آتش دوزخ گداخته شود و پیشانی و پشت و پهلوی آنها را به آن داغ کنند (و فرشتگان عذاب به آنها گویند) این است نتیجه آنچه بر خود ذخیره کردید، اکنون بچشید (آتش حسرت و عذاب) همان سیم و زری که اندوخته می‌کردید.'),
(1271, 9, 36, 'همانا عدد ماههای سال نزد خدا در کتاب (تکوین و تشریع) خدا دوازده ماه است از آن روزی که خدا آسمان و زمین را بیافرید و از آن دوازده ماه چهار ماه ماههای حرام است (نزد اکثر مفسرین آن چهار ماه ذیقعده و ذیحجه و محرم و رجب است) این است دین استوار و محکم، پس در آن ماهها ظلم و ستم (به جنگ و خونریزی) در حق خود و یکدیگر مکنید و متفقا همه با مشرکان قتال و کارزار کنید چنانکه مشرکان نیز همه متّفقا با شما به جنگ و خصومت برمی‌خیزند و بدانید که خدا با اهل تقواست.'),
(1272, 9, 37, 'نسی‌ء (یعنی ماهی را تغییر و تبدیل کردن به ماهی دیگر و حکم ماه حرامی را در هر سه سال، سالی ده روز به ماهی متأخر انداختن که بدعت زمان جاهلیت بود) افزایش در کفر است که کافران را بدان به جهل و گمراهی کشند، سالی ماه حرام را حلال می‌شمرند و سالی دیگر حرام تا بدین وسیله با عده ماههایی که خدا حرام کرده سازگاری و برابری داشته باشند و حرام خدا را حلال گردانند. اعمال زشت آنها در نظرشان زیبا نمود، و خدا هرگز کافران را هدایت نخواهد کرد.'),
(1273, 9, 38, 'ای کسانی که ایمان آورده‌اید، جهت چیست که چون به شما امر شود که برای جهاد در راه خدا بی‌درنگ خارج شوید (چون بار گران) به خاک زمین سخت دل بسته‌اید؟ آیا راضی به زندگانی دنیا عوض آخرت شده‌اید؟ در صورتی که متاع دنیا در پیش عالم آخرت اندکی بیش نیست.'),
(1274, 9, 39, 'اگر بیرون نشوید خدا شما را به عذابی دردناک معذب خواهد کرد و قومی دیگر به جای شما برمی‌گمارد و شما به خدا زیانی نمی‌رسانید، و خدا بر هر چیز تواناست.'),
(1275, 9, 40, 'اگر شما او را (یعنی رسول خدا را) یاری نکنید البته خداوند او را یاری کرد هنگامی که کافران او را که یکی از دو تن بود (از مکه) بیرون کردند، آن‌گاه که در غار بودند (و خدا بر در غار کوه پرده عنکبوتان و آشیانه کبوتران گماشت تا دشمنان که به عزم کشتنش آمده بودند او را نیافتند) و آن‌گاه که او به رفیق و همسفر خود (ابو بکر که پریشان و مضطرب بود) می‌گفت: مترس که خدا با ماست. آن زمان خدا وقار و آرامش خاطر خود را بر او فرستاد و او را به سپاه و لشکرهای غیبی خود که شما آنان را ندیدید مدد فرمود و ندای کافران را پست گردانید و ندای خداست که مقام بلند دارد، و خدا را کمال قدرت و دانایی است.'),
(1276, 9, 41, 'برای جنگ با کافران، سبکبار و مجهز بیرون شوید و در راه خدا به مال و جان جهاد کنید، این کار شما را بسی بهتر خواهد بود اگر مردمی با فکر و دانش باشید.'),
(1277, 9, 42, '- (ای رسول) اگر این مردم را برای منفعت آنی و فوری و سفر کوتاه (تفریحی) دعوت کنی البته تو را پیروی خواهند کرد و لیکن این سفر (به تبوک برای جنگ با روم) بر آنان دراز و دشوار آمد، و مؤکد به خدا سوگند می‌خورند که اگر توانایی داشتیم همانا با شما بیرون می‌آمدیم، اینان خود را به دست هلاکت می‌سپارند و خدا می‌داند که آنها به حقیقت دروغ می‌گویند.'),
(1278, 9, 43, '- (ای رسول) خدا تو را ببخشاید چرا پیش از آنکه دروغگویان از راستگویان بر تو معلوم شوند به آنها اجازه دادی؟ (بهتر آن بود که رخصت در ترک جهاد ندهی تا آن را که به راستی ایمان آورده از آن که دروغ می‌گوید امتحان کنی).'),
(1279, 9, 44, '-مسلمانانی که به حقیقت به خدا و روز قیامت ایمان دارند از تو رخصت ترک جهاد نخواهند تا به مال و جان خود در راه خدا جهاد کنند، و خدا به احوال متقیان آگاه است.'),
(1280, 9, 45, '-تنها آنهایی که ایمان به خدا و روز قیامت نیاورده و دلهاشان در شک و ریب است از تو اجازه معافی از جهاد می‌خواهند، و آن منافقان پیوسته در شک و تردید مانده و سرگردانند.'),
(1281, 9, 46, '-و اگر آنان قصد سفر جهاد داشتند درست مهیای آن می‌شدند لیکن خدا هم از برانگیختن آنها کراهت داشت پس آنها را باز داشت و حکم شد (که ای منافقان نالایق) شما هم با معذوران (کور عاجز در خانه خود) بنشینید.'),
(1282, 9, 47, '-اگر این منافق مردم هم در میان شما مؤمنان بیرون می‌آمدند جز خیانت و مکر در سپاه شما چیزی نمی‌افزودند و هر چه می‌توانستند در کار شما اخلال و خرابی می‌کردند، برای شما از هر سو در جستجوی فتنه برمی‌آمدند، و هم در میان لشکر شما از آنان جاسوسهایی وجود دارد، و خدا به احوال ظالمان داناست.'),
(1283, 9, 48, 'از این پیش (در جنگ خندق و احد) هم آنان در صدد فتنه‌گری بودند و کارها را به تو برعکس می‌نمودند تا آن گاه که حق روی آورد و امر خدا آشکار گردید (یعنی اسلام را فتح نصیب کرد) در صورتی که آنها کمال کراهت را داشتند.'),
(1284, 9, 49, 'و برخی از آن مردم منافق با تو گویند که به ما در جهاد اجازه معافی ده و ما را در آتش فتنه (و گناه نافرمانی) میفکن، آگاه باش که آنها خود به فتنه و امتحان در افتادند و همانا دوزخ به آن کافران احاطه دارد.'),
(1285, 9, 50, 'اگر تو را حادثه‌ای خوش نصیب شود سخت بر آنها ناگوار آید و اگر تو را زحمتی و رنجی پیش آید (خوشحال شده و) گویند: ما در کار خود پیش‌بینی کردیم (که به جنگ نرفتیم)، و آنها باز می‌گردند در حالی که شادند.'),
(1286, 9, 51, 'بگو: هرگز جز آنچه خدا برای ما خواسته به ما نخواهد رسید، اوست مولای ما، و البته اهل ایمان در هر حال باید بر خدا توکل کنند.'),
(1287, 9, 52, 'بگو که آیا شما منافقان جز یکی از دو نیکویی (بهشت و یا فتح) چیزی می‌توانید بر ما انتظار برید؟ ولی ما درباره شما منتظریم که از جانب خدا به عذابی سخت گرفتار شوید یا به دست ما هلاک شوید، بنابراین شما در انتظار باشید که ما هم مترصد و منتظر هستیم.'),
(1288, 9, 53, 'بگو که شما (هر چه خواهید) انفاق کنید (صرف مخارج جنگ و تبلیغات مزوّرانه کنید) چه از روی میل و چه کراهت، هرگز از شما پذیرفته نخواهد شد، زیرا (کار شما خودنمائی است و) شما مردمی بوده که سخت به فسق خو گرفته‌اید.'),
(1289, 9, 54, 'و هیچ مانعی از قبول نفقات آنها نبود جز آنکه آنها به خدا و رسول او (باطنا) کافرند و نماز نگزارند جز به حال کسالت و انفاق نکنند جز آنکه سخت کراهت دارند.'),
(1290, 9, 55, 'مبادا تو از کثرت اموال و اولاد آنها در شگفت آیی! خدا می‌خواهد آنها را به همان مال و فرزند در زندگانی دنیا به عذاب افکند و ساعت مرگ جان آنها به درآید در حالتی که کافر باشند.'),
(1291, 9, 56, 'و آنها (برای آنکه نفاق خود را پنهان کنند) دائم به خدا قسم یاد می‌کنند که ما هم به راستی از شما مؤمنانیم و حال آنکه از شما و هم عقیده شما نیستند و لیکن این قوم (از قدرت اسلام) می‌ترسند.'),
(1292, 9, 57, 'اگر پناهگاهی یا سنگری در مغار کوهها یا هر گریزگاهی دیگر بیابند (که از تسلط مسلمانان ایمن باشند) البته بدان جا با کمال تعجیل می‌گریزند.'),
(1293, 9, 58, 'و بعضی از آن مردم منافق در (تقسیم) صدقات بر تو اعتراض و خرده‌گیری کنند، اگر به آنها از آن عطا شود رضایت داشته و اگر از آن چیزی به آنها داده نشود سخت خشمگین شوند.'),
(1294, 9, 59, 'و چقدر بهتر بود اگر آنها به آنچه خدا و رسول به آنها عطا کردند راضی بودند و می‌گفتند که خدا ما را کفایت است، او و هم رسولش از لطف عمیم به ما عطا خواهند کرد، ما تنها به خدا مشتاقیم.'),
(1295, 9, 60, '(مصرف) صدقات منحصرا مختص است به (این هشت طایفه): فقیران و عاجزان و متصدیان اداره صدقات و کسانی که باید تألیف قلوب آنها کرد (یعنی برای متمایل کردن بیگانگان به دین اسلام) و آزادی بندگان و قرض‌داران و در راه خدا (یعنی در راه تبلیغ و رواج دین خدا) و به راه درماندگان. این مصارف هشتگانه فرض و حکم خداست، که خدا دانا و بر تمام مصالح خلق آگاه است.'),
(1296, 9, 61, 'و بعضی (از منافقان) هستند که دائم پیغمبر را می‌آزارند و (چون عذر دروغ آنها به حلم خود می‌پذیرد) می‌گویند: او شخص ساده و زودباوری است. بگو زودباوری او لطفی به نفع شماست، که به خدا ایمان آورده و به مؤمنان هم اطمینان دارد و برای مؤمنان (حقیقی) شما وجودش رحمت است، و برای آنها که رسول را آزار دهند عذابی دردناک مهیّاست.'),
(1297, 9, 62, '(منافقان) برای اغفال و خشنود کردن شما مؤمنان به نام خدا سوگند می‌خورند در صورتی که اگر ایمان داشتند سزاوارتر این بود که خدا و رسول را از خود خشنود کنند.'),
(1298, 9, 63, 'آیا ندانسته‌اند که هر کس با خدا و رسولش به عداوت برخیزد آتش دوزخ کیفر دائمی اوست؟ و این به حقیقت ذلت و خواری بزرگ است.'),
(1299, 9, 64, 'منافقان از آن روزی می‌ترسند که خدا سوره‌ای بفرستد که آنها را از آنچه در درون آنهاست آگاه کند. بگو: اکنون تمسخر کنید، خدا آنچه را که از آن می‌ترسید آشکار خواهد ساخت.'),
(1300, 9, 65, 'و اگر از آنها بپرسی (که چرا سخریه و استهزاء می‌کنید؟) پاسخ دهند که ما به مزاح و مطایبه سخن می‌راندیم. بگو: آیا با خدا و آیات و رسول او تمسخر می‌کردید؟!'),
(1301, 9, 66, 'عذر نیاورید، که شما بعد از ایمان کافر شدید، اگر از برخی (نادانان و ساده لوحان) شما درگذریم گروهی (فتنه‌گر) را نیز عذاب خواهیم کرد که مردمی بسیار زشتکار بوده‌اند.'),
(1302, 9, 67, 'مردان و زنان منافق، متّفق با همدیگر و طرفدار یکدیگرند، مردم را به کار بد وا می‌دارند و از کار نیکو منع می‌کنند و دست خود را (از انفاق در راه خدا) می‌بندند، و چون خدا را فراموش کردند خدا نیز آنها را فراموش کرد (یعنی به خود واگذاشت تا از هر سعادت محروم شوند)، در حقیقت منافقان بدترین زشتکاران عالمند.'),
(1303, 9, 68, 'خدا مرد و زن از منافقان و کافران را وعده آتش دوزخ و خلود در آن داده، همان دوزخ برای (کیفر) آنها کافی است و خدا آنها را لعن کرده و عذابی ابدی دارند.'),
(1304, 9, 69, '(شما هم دنیا پرستید) به مانند آنهایی که پیش از شما بودند در صورتی که پیشینیان از شما قوی‌تر بودند و مال و اولادشان بیشتر بود، پس به سهم خود (از متاع فانی دنیا دو روزی) متمتّع بودند، اکنون هم که نوبت به شما رسید به تمتّع دنیا به سهم خود مانند آنها سرگرم شدید و هم (در شهوات دنیا) به مانند آنها فرو رفتید! آنان مردمی هستند که اعمالشان در دنیا و آخرت نابود و باطل گشت و هم آنان به حقیقت زیانکاران عالمند.'),
(1305, 9, 70, 'آیا اخبار پیشینیانشان مانند قوم نوح و عاد و ثمود و قوم ابراهیم و اهل مَدْیَن و مُؤْتَفِکات (یعنی شهرهای ویران شده قوم لوط) به آنها نرسید که رسولان الهی آیات و معجزات آشکار بر آنها آوردند (ولی آنها نپذیرفتند و هلاک شدند؟ آری) خدا هیچ ستمی بر آنها نکرد بلکه آنها خود در حق خویش ستم می‌کردند.'),
(1306, 9, 71, 'و مردان و زنان مؤمن همه یاور و دوستدار یکدیگرند، خلق را به کار نیکو وادار و از کار زشت منع می‌کنند و نماز به پا می‌دارند و زکات می‌دهند و حکم خدا و رسول او را اطاعت می‌کنند، آنان را البته خدا مشمول رحمت خود خواهد گردانید، که خدا صاحب اقتدار و درست کردار است.'),
(1307, 9, 72, 'خدا اهل ایمان را از مرد و زن وعده فرموده که در بهشت خلد ابدی که زیر درختانش نهرها جاری است درآورد و در عمارات نیکو و پاکیزه بهشت عدن منزل دهد و برتر و بزرگتر از هر نعمت، مقام رضا و خشنودی خداست و آن به حقیقت فیروزی بزرگ است.'),
(1308, 9, 73, 'ای پیغمبر با کافران و منافقان جهاد و مبارزه کن و بر آنها بسیار سخت‌گیر، و مسکن و مأوای آنها دوزخ است که بسیار بد بازگشتگاه و منزلگاهی است.'),
(1309, 9, 74, '(منافقان) قسم به خدا یاد می‌کنند که (حرف کفر) بر زبان نیاورده‌اند، و (چنین نیست) البته سخن کفر گفته و پس از اظهار اسلام کافر شدند و همت بر آنچه موفق بر آن نشدند گماشتند (یعنی همت بر قتل رسول و اخراج او و هر گونه فساد در دین او گماشتند ولی موفق نشدند) آنها به جای آنکه از آن بی‌نیازی که به فضل خدا و رسول نصیب آنها شد شکر گویند در مقام انتقام و دشمنی برآمدند، اکنون هم اگر باز توبه کنند برای آنها بهتر است و اگر روی بگردانند آنها را خدا در دنیا و آخرت به عذابی دردناک معذب خواهد فرمود و دیگر در همه روی زمین یک نفر دوستدار و یاوری برای آنها نخواهد بود.'),
(1310, 9, 75, 'و بعضی از آنها این گونه با خدا عهد بستند که اگر نعمت و رحمتی نصیب ما کند البته صدقه (یعنی زکات) پردازیم و از نیکان می‌شویم.'),
(1311, 9, 76, 'و (با این عهد) باز چون خدا از فضل و نعمت خود نصیب آنها کرد بر آن بخل ورزیدند و (از آن عهد) روی گردانیده و (از حق) اعراض کردند.'),
(1312, 9, 77, 'در نتیجه (این تکذیب و نقض عهد) خدا هم دل آنها را ظلمتکده نفاق گردانید تا روزی که به کیفر بخل و اعمال زشت خود برسند، زیرا با خدا خلف وعده کرده و دروغ می‌گفتند.'),
(1313, 9, 78, 'آیا ندانستند که خدا از باطن آنها و سخنان سرّی ایشان آگاه است و خدا دانای غیب و عالم به اسرار پنهانی است؟!'),
(1314, 9, 79, 'آن کسانی که عیب‌جویی می‌کنند بر آن مؤمنانی که به صدقات مستحب فقیران را دستگیری می‌کنند و همچنین مسخره می‌کنند مؤمنانی را که از اندک چیزی که مقدور آنهاست هم (در راه خدا) مضایقه نمی‌کنند، خدا هم آنها را البته مسخره و مجازات می‌کند و به آنها عذابی دردناک خواهد رسید.'),
(1315, 9, 80, '(ای پیغمبر) تو بر آن مردم منافق خواهی طلب مغفرت بکن یا نکن، اگر هفتاد مرتبه هم بر آنها (از خدا) آمرزش طلبی خدا هرگز آنان را نخواهد بخشید، زیرا آنها (از راه فسق و سرکشی) به خدا و رسول او کافر شدند و خدا فاسقان را هرگز هدایت نخواهد کرد.'),
(1316, 9, 81, 'آنهایی که از جهاد در رکاب رسول خدا باز نهاده شدند از این بازماندنشان خوشحالند و مجاهده به مال و جانشان در راه خدا را خوش نداشتند و (مؤمنان را هم از جهاد منع کرده و به آنها) گفتند: شما در این هوای سوزان از وطن خود بیرون نروید! آنان را بگو: آتش دوزخ بسیار سوزان‌تر است، اگر می‌فهمیدند.'),
(1317, 9, 82, 'اکنون آنها باید خنده کم و گریه بسیار کنند به کیفر اعمالی که می‌کردند.'),
(1318, 9, 83, 'پس اگر خدا تو را به سوی گروهی از آن بازماندگان (به مدینه) برگرداند و آنها (به ملاقات تو آمده و) اجازه جهاد خواهند، به آنها بگو: ابدا شما با من به جنگ نخواهید آمد و با هیچ کس از دشمنان من جهاد نخواهید کرد، شما هستید که اول بار برای تخلفتان از سفر جهاد اظهار مسرّت می‌کردید، اکنون هم با بازماندگان به جای خود بنشینید.'),
(1319, 9, 84, 'و هیچ گاه به نماز میت آن منافقان حاضر مشو و بر (جنازه و) قبر آنها (به دعا) مایست، که آنها به خدا و رسولش کافر شدند و در حال فسق و بدکاری مردند.'),
(1320, 9, 85, 'و بسیاری اموال و اولاد آن منافقان تو را به شگفت نیارد، که خدا خواهد آنها را به آن مال و اولاد در دنیا معذب گرداند و جانشان به حال کفر به درآید.'),
(1321, 9, 86, 'و هرگاه سوره‌ای نازل شود که امر به ایمان به خدا و جهاد با رسول (در راه دین خدا) کند ثروتمندان آن منافقان از حضور تو تقاضای معافی از جهاد کرده و گویند: ما را از معاف‌شدگان محسوب دار.'),
(1322, 9, 87, 'بدان راضی بودند که با زنان و کودکان و عجزه در خانه بنشینند (و به جهاد حاضر نشوند)، و دلهای آنها نقش کفر و ظلمت گرفته و دیگر هیچ درک حقایق نکنند.'),
(1323, 9, 88, 'اما رسول و مؤمنان اصحابش به مال و جانشان در راه خدا جهاد کردند و آنهایند که همه خیرات و نیکوییها (ی دو عالم) مخصوص آنهاست و هم آنان سعادتمندان عالمند.'),
(1324, 9, 89, 'خدا بر آنها باغهایی که به زیر درختانش نهرها جاری است مهیا فرموده که در آن تا ابد متنعّم باشند و این به حقیقت سعادت و فیروزی بزرگ است.'),
(1325, 9, 90, 'و گروهی از اعراب بادیه (نزد تو) آمده و عذر می‌آورند که اجازه معافی از جهاد یابند و نیز گروهی که تکذیب خدا و رسول را کرده (بدون هیچ عذری) از جهاد بازنشستند، به زودی کافران از این دو طایفه را عذابی دردناک خواهد رسید.'),
(1326, 9, 91, 'بر ناتوانان و بیماران و فقیران که خرج سفر (و نفقه عیال خود را) ندارند گناهی بر ترک جهاد نیست هرگاه که با خدا و رسول او خیرخواهی و اخلاص ورزند (که این کار نیکوست و) بر نیکوکاران عالم از هیچ راه حرج و زحمتی نیست و خدا بسیار آمرزنده و مهربان است.'),
(1327, 9, 92, 'و همچنین بر آن مؤمنانی که چون مهیای جهاد شده و نزد تو آیند که زاد و لوازم سفر آنها را مهیا سازی و تو پاسخ دهی که من مالی که به شما مساعدت کنم ندارم برمی‌گردند در حالی که از شدت حزن اشک از چشمانشان جاری است که چرا نمی‌توانند مخارج سفر خود فراهم سازند (بر آنها هم حرج و گناهی بر ترک جهاد نیست).'),
(1328, 9, 93, 'منحصرا گناه و عقوبت آنها راست که با وجود تمکن و دارایی از تو رخصت معافیت از جنگ می‌طلبند و خوش دارند که با بازماندگان (یعنی زنان و کودکان) در خانه بنشینند (و به جهاد حاضر نشوند) و خدا بر دل آنها نقش کفر و ظلمت زده به طوری که هیچ درک نکنند.'),
(1329, 9, 94, 'وقتی که شما (از جنگ سالم و فاتح) به سوی آنها بر می‌گردید آنها به عذرهای بی‌جا می‌پردازند، به آنها پاسخ ده که ما هرگز به این عذر تراشی‌ها تصدیق شما نکنیم، بی‌شک خدا حقیقت حال شما را بر ما روشن گردانید، و به زودی خدا و رسولش کردار شما را دیده، آن گاه به سوی خدایی که دانای غیبت و شهود است بازتان گردانند پس شما را به کردارتان واقف کند.'),
(1330, 9, 95, 'چون شما به سوی آنها بازگردید قسم‌های مؤکّد به خدا برای شما یاد کنند که از آنها چشم‌پوشی کنید. از آنها اعراض کنید که مردمی پلیدند و به کیفر کردار خود به آتش دوزخ مأوی خواهند یافت.'),
(1331, 9, 96, '(آن مردم منافق) برای اینکه شما از آنها راضی شوید قسمها یاد می‌کنند، پس اگر شما مؤمنان هم از آنها راضی شوید خدا هرگز از آن گروه فاسق راضی نخواهد شد.'),
(1332, 9, 97, 'اعراب بادیه‌نشین در کفر و نفاق از دیگران سخت‌تر و به جهل و نادانی احکام خدا که بر رسولش فرستاده سزاوارترند، و خدا دانا و حکیم است.'),
(1333, 9, 98, 'و برخی از اعراب بادیه‌نشین مخارجی را که (در راه جهاد دین) می‌کنند بر خود ضرر و زیانی می‌پندارند و برای شما مسلمین مترصد حوادث و عواقب ناگوارند و حال آنکه عواقب و حوادث بد بر خود آنها خواهد بود و خدا شنوا و داناست.'),
(1334, 9, 99, 'و برخی دیگر از همان اعراب بادیه‌نشین ایمان واقعی به خدا و قیامت آورده و آنچه را در راه خدا انفاق می‌کنند موجب تقرب نزد خدا و دعای خیر رسول دانند، آری آگاه شوید که انفاق آنها موجب قربشان به خداست، و البته خدا آنان را در (سرای) رحمت خود داخل می‌گرداند، که خدا بسیار آمرزنده و مهربان است.'),
(1335, 9, 100, 'و آنان که سبقت به ایمان گرفتند از مهاجر و انصار (و در دین ثابت ماندند) و آنان که به نیکی پیروی آنان کردند، خدا از آنها خشنود است و آنها از خدا خشنودند، و خدا برای همه آنها بهشت‌هایی که از زیر درختان آنها نهرها جاری است مهیا ساخته که در آن بهشت تا ابد متنعّم باشند، این به حقیقت سعادت بزرگ است.'),
(1336, 9, 101, 'و بعضی از اعراب بادیه‌نشین اطراف شما (اطراف مدینه) منافقند و بعضی اهل شهر مدینه هم منافق و بر نفاق ماهر و ثابتند که تو از آنها آگاه نیستی، ما از آنها آگاهیم، آنان را دو بار عذاب خواهیم کرد (در دنیا قبل از مرگ و در برزخ بعد از مرگ) و عاقبت هم به عذاب سخت ابدی دوزخ بازگردانیده می‌شوند.'),
(1337, 9, 102, 'و بعضی دیگر از آنها به گناهان خود اعتراف کردند که عمل صالح و فعل قبیح هر دو به جای آوردند، امید باشد که خداوند توبه آنان بپذیرد که البته خدا بسیار آمرزنده و مهربان است.'),
(1338, 9, 103, 'تو از اموال مؤمنان صدقات را دریافت دار که بدان صدقات نفوس آنها را پاک و پاکیزه می‌سازی و رشد و برکت می‌دهی، و آنها را به دعای خیر یاد کن که دعای تو در حق آنان موجب تسلّی خاطر آنها شود و خدا شنوا و داناست.'),
(1339, 9, 104, 'آیا مؤمنان هنوز ندانسته‌اند که محققا خداست که توبه بندگان را می‌پذیرد و صدقه آنها را قبول می‌فرماید و خداست که بسیار توبه پذیر و بر خلق مهربان است؟'),
(1340, 9, 105, 'و بگو که هر عمل کنید خدا آن عمل را می‌بیند و هم رسول و مؤمنان بر آن آگاه می‌شوند، آن گاه به سوی خدایی که دانای عوالم غیب و شهود است بازگردانیده شوید و او شما را به کردارتان واقف سازد.'),
(1341, 9, 106, 'و برخی دیگر (از گناهکاران) آنهایی هستند که کارشان بر مشیّت خدا موقوف است یا آنان را عذاب کنید و یا از گناهشان درگذرد، و خدا دانا و حکیم است.'),
(1342, 9, 107, 'و (گروهی دیگر از منافقانند) آنان که مسجدی برای زیان به اسلام برپا کردند و مقصودشان کفر و عناد و تفرقه کلمه بین مسلمین و ساختن کمینگاهی برای دشمنان دیرینه خدا و رسول بود، و با این همه قسم‌های مؤکّد یاد می‌کنند که ما جز قصد خیر غرضی نداریم، و خدا گواهی می‌دهد که محققا دروغ می‌گویند.'),
(1343, 9, 108, 'تو (ای رسول ما) هرگز در مسجد آنها (به نماز) مایست که همان مسجد (قبا) که بنیانش از اول بر پایه تقوای محکم بنا گردیده سزاوارتر است به اینکه در آن اقامه نماز کنی، که در آن مسجد مردان پاکی که مشتاق تهذیب نفوس خودند درآیند، و خدا مردان پاک مهذّب را دوست می‌دارد.'),
(1344, 9, 109, 'آیا کسی که مسجدی به نیت تقوا تأسیس کرده و رضای حق را طالب است مانند کسی است که بنایی سازد بر پایه سستی در کنار مسیل (لبه پرتگاه دوزخ) که زود به ویرانی کشد و عاقبت او را به آتش دوزخ درافکند؟! و خدا هرگز ستمکاران را هدایت نخواهد فرمود.'),
(1345, 9, 110, 'بنیانی که آنها بنا کردند دلهای ایشان را همیشه به حیرت و شک و ریب افکند تا آنکه از آن دل برکنند، و خدا دانا و حکیم است.'),
(1346, 9, 111, 'همانا خدا جان و مال اهل ایمان را به بهای بهشت خریداری کرده، آنها در راه خدا جهاد می‌کنند پس (دشمنان دین را) به قتل می‌رسانند و (یا خود) کشته می‌شوند، این وعده قطعی است بر خدا و عهدی است که در تورات و انجیل و قرآن یاد فرموده، و از خدا با وفاتر به عهد کیست؟ پس از این معامله‌ای که کردید بسی شاد باشید که این به حقیقت سعادت و فیروزی بزرگ است.'),
(1347, 9, 112, 'اینان همان از گناه پشیمانان، خداپرستان، حمد و شکر نعمت‌گزاران، روزه‌داران، نماز با خضوع گزاران، امر به معروف و نهی از منکر کنندگان، و نگهبانان حدود الهی‌اند، و مؤمنان را بشارت ده.'),
(1348, 9, 113, 'پیغمبر و گرویدگان به او نباید برای مشرکان هر چند خویشان آنها باشند از خدا آمرزش طلبند بعد از آنکه آنها را اهل دوزخ شناختند.'),
(1349, 9, 114, 'و ابراهیم [چون در آیه قبل فرمود که مؤمن نباید بر خویشانش هم اگر مشرک و کافرند ترحم و شفقت کند ممکن است کسی از جهل اعتراض کند که پس ابراهیم خلیل چرا بر عمویش آزر طلب آمرزش از خدا کرد با آنکه مشرک و کافر بود و در این آیه جواب این اشکال داده شده که دعا و استغفار ابراهیم برای عموی کافرش طبق عهدی بود که با او کرد که اگر ایمان آورد در حقش از خدا طلب رحمت و مغفرت کند و چون بر کفر و شرک باقی ماند ابراهیم هم بر او طلب رحمت و آمرزش نکرد.] هم که برای پدرش (یعنی عمویش) از خدا آمرزش خواست این نبود مگر به موجب عهدی که با او کرده بود (که ایمان آورد) و چون بر او محقق شد که وی دشمن خداست از او بیزاری جست، که ابراهیم شخصی بسیار خداترس و بردبار بود.'),
(1350, 9, 115, 'و خدا بعد از آنکه قومی را هدایت کرد دیگر گمراه نکند تا بر آنها آنچه را باید از آن بپرهیزند معین و روشن بیان کند، که خدا محققا به همه چیز داناست.'),
(1351, 9, 116, 'محققا و منحصرا ملک و پادشاهی آسمانها و زمین از آن خداست که خلق را زنده کند و بمیراند، و شما را جز خدا سرپرست و یاوری نیست.'),
(1352, 9, 117, 'همانا خدا بر پیغمبر و اصحابش از مهاجر و انصار که در ساعت سختی-که نزدیک بود دلهای فرقه‌ای از آنها (از رنج و تعب در سختیهای جنگ تبوک و غیره) بلغزد-پیروی از رسولش کردند باز لطف فرمود و از لغزشهاشان درگذشت، که او درباره رسول و مؤمنان به یقین مشفق و مهربان است.'),
(1353, 9, 118, 'و بر آن سه تن [آن سه نفر مرارة بن ربیع و هلال بن امیه و کعب بن مالک بودند که از زحمت جنگ و سختی حال و رنج و تعب و گرما از رفتن به جنگ در تبوک خودداری کردند و بعد در حضور پیغمبر آمدند و عذرخواهی کردند و توبه آنها پذیرفته شد.] که (از جنگ تبوک) باز نهاده شدند (و مردم به دستور پیامبر صلّی اللّه علیه و آله و سلّم از آنان بریدند) تا آنکه زمین با همه پهناوری بر آنها تنگ شد و بلکه از خود دلتنگ شدند و دانستند که از (غضب) خدا جز به (لطف) او ملجأ و پناهی نیست، پس خدا بر آنها باز لطف فرمود تا توبه کنند، که خداوند بسیار توبه‌پذیر و مشفق و مهربان است.'),
(1354, 9, 119, 'ای اهل ایمان، خداترس باشید و با مردان راستگوی با ایمان بپیوندید.'),
(1355, 9, 120, 'اهل مدینه و بادیه‌نشینان اطرافشان نباید هرگز از (همراهی) پیغمبر تخلف کنند و نه هرگز جان خود را از جان او عزیزتر شمارند، زیرا هیچ رنج تشنگی و خستگی و گرسنگی در راه خدا نکشند و هیچ قدمی در جایی که کفار را خشمگین کند ننهند و هیچ دستبردی به دشمنان نرسانند جز آنکه در مقابل هر یک از این رنج و آلام عمل صالحی در نامه اعمالشان نوشته شود که خدا هرگز اجر نیکوکاران را ضایع نخواهد گذاشت.'),
(1356, 9, 121, 'و هیچ مالی کم یا زیاد (در راه خدا) انفاق نکنند و هیچ وادیی نپیمایند جز آنکه در نامه عمل آنها نوشته شود تا خداوند بسیار بهتر از آنچه کردند اجر به آنها عطا فرماید.'),
(1357, 9, 122, 'و (هنگامی که رسول فرمان خروج برای جنگ دهد) نباید مؤمنان همگی بیرون روند (و رسول را تنها گذارند) پس چرا از هر طایفه‌ای جمعی برای جنگ و گروهی نزد رسول برای آموختن علم دین مهیا نباشند تا قوم خود را چون به نزدشان بازگشتند بیم رسانند، باشد که (از نافرمانی خدا) حذر کنند.'),
(1358, 9, 123, 'ای اهل ایمان، با کافران از آنان که با شما نزدیکترند شروع به جهاد کنید و باید کفار در شما درشتی و نیرومندی و قوت و پایداری حس کنند، و بدانید که خدا همیشه با پرهیزکاران است.'),
(1359, 9, 124, 'و هرگاه سوره‌ای نازل شود برخی (از همین منافقان) هستند که به دیگران می‌گویند: این سوره بر ایمان کدام یک از شما افزود؟ آنان که به حقیقت اهل ایمانند همه را بر ایمان بیفزود و شادمان و مسرور شدند.'),
(1360, 9, 125, 'و اما آنان که دلهاشان به مرض (شک و نفاق) مبتلاست هم بر خبث ذاتی آنها خباثتی افزود تا به حال کفر جان دادند.'),
(1361, 9, 126, 'آیا (منافقان) نمی‌بینند که آنها در هر سالی یک بار یا دو بار البته امتحان می‌شوند؟ باز هم پشیمان نشده و (خدا را) یاد نمی‌کنند.'),
(1362, 9, 127, 'و هرگاه سوره‌ای نازل شود بعضی از آنها به بعضی دیگر نگاه و اشاره کرده، گویند: آیا شما را کسی (از مؤمنان در محضر پیغمبر) دیده و شناخته (یا نه)؟ آن گاه همه بر می‌گردند. خدا دلهاشان را برگرداند، که مردمی بسیار بی‌شعور و نادانند.'),
(1363, 9, 128, 'همانا رسولی از جنس شما برای (هدایت) شما آمد که فقر و پریشانی و جهل و فلاکت شما بر او سخت می‌آید و بر (آسایش و نجات) شما بسیار حریص و به مؤمنان رئوف و مهربان است.'),
(1364, 9, 129, 'پس (ای رسول) هرگاه مردم روگردانیدند بگو: خدا مرا کفایت است که جز او خدایی نیست، من بر او توکل کرده‌ام و او رب عرش بزرگ است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الر (از اسرار وحی الهی است) این است آیات الهی که به حقّ و راستی گویاست.'),
(1366, 10, 2, 'آیا مردم تعجب کردند از اینکه ما یکی از افراد آنها را به وحی و رسالت خود برگزیدیم (و گفتیم) که خلق را (از عذاب قیامت) بترسان و مؤمنان را بشارت ده که به راستی مقامشان نزد خدا رفیع است! کافران گفتند: این شخص ساحری آشکار است.'),
(1367, 10, 3, 'خالق و پروردگار شما به حقیقت خداست که جهان را از آسمانها و زمین در شش روز (یعنی مقدار زمان شش روز یا شش مرتبه وجودی) خلق فرمود. آن گاه ذات مقدسش بر عرش فرمانروایی قرار گرفت، امر آفرینش را نیکو ترتیب می‌دهد، هیچ کس شفیع و واسطه جز به رخصت او نخواهد بود، چنین خدایی به حقیقت پروردگار شماست، او را به یگانگی پرستید، چرا متذکر نمی‌شوید؟'),
(1368, 10, 4, 'بازگشت شما همه به سوی او خواهد بود، این به حقیقت وعده خداست که او در اول، خلق را می‌آفریند و آن‌گاه (به سوی خود) بر می‌گرداند تا آنان را که ایمان آورده و عمل صالح کردند به عدل و احسان، ثواب و جزای خیر دهد و آنان که کافر شدند به کیفر کفرشان به شرابی از آب جوشان دوزخ و عذابی دردناک معذّب خواهند گشت.'),
(1369, 10, 5, 'اوست خدایی که آفتاب را رخشان و ماه را تابان فرمود و سیر ماه را در منازلی معین کرد تا بدین واسطه شماره سنوات و حساب ایام را بدانید. اینها را خدا جز به حق نیافریده. خدا آیات خود را برای اهل علم و معرفت مفصل بیان می‌کند.'),
(1370, 10, 6, 'به حقیقت در رفت و آمد شب و روز و در هر چیزی که خدا در آسمانها و زمین خلق فرموده برای اهل خرد و تقوا علامت و نشانه‌ها (ی قدرت خدا) پدیدار است.'),
(1371, 10, 7, 'البته آنهایی که به لقاء ما دل نسبته و امیدوار نیستند و به زندگی پست دنیا دلخوش و دلبسته‌اند و آنهایی که از آیات و نشانه‌های ما غافلند.'),
(1372, 10, 8, 'هم اینانند که عاقبت به کردار زشت خود در آتش دوزخ مأوی گیرند.'),
(1373, 10, 9, 'آنان که ایمان آورده و نیکوکار شدند پروردگارشان به سبب همان ایمان آنها را (به راه سعادت و طریق بهشت) رهبری کند، در آن بهشتهای پرنعمت نهرها از زیر پای آنان جاری است.'),
(1374, 10, 10, 'و در آن بهشت زبان شوق به تقدیس خدا گشایند که بار الها تو از هر نقص و آلایش پاک و منزهی و درود آنها در بهشت «سلام» است و آخرین سخنشان حمد پروردگار عالمیان است.'),
(1375, 10, 11, 'و اگر خدا به عقوبت عمل زشت مردم و دعای شرّی که در حق خود می‌کنند به مانند خیرات تعجیل می‌فرمود مردم همه محکوم مرگ و هلاک می‌شدند و لیکن ما آنان را که به لقای ما امیدوار نیستند به همان حال سرگردانی در کفر و طغیان رها می‌سازیم.'),
(1376, 10, 12, 'و هرگاه آدمی را رنج و زیانی رسد همان لحظه به هر حالت باشد از خفته و نشسته و ایستاده فورا ما را به دعا می‌خواند و آن گاه که رنج و زیانش را بر طرف سازیم باز به حال غفلت و غرور چنان باز می‌گردد که گویی هیچ ما را برای دفع ضرر و رنجی که به او رسیده بود نخوانده است! اعمال زشت تبهکاران این چنین در نظرشان زیبا جلوه داده شده است.'),
(1377, 10, 13, 'و محقّقا ما اقوام و مللی را پیش از شما به کیفر ظلمشان سخت به دست هلاک سپردیم و نیز به کیفر آنکه پیغمبرانی با آیات و معجزات بر آنها آمد باز هیچ ایمان نیاوردند. ما این گونه مردم بد عمل را به کیفر می‌رسانیم.'),
(1378, 10, 14, 'سپس ما بعد از آنها شما را در زمین جانشین کردیم تا بنگریم که چگونه عمل خواهید کرد.'),
(1379, 10, 15, 'و هرگاه آیات روشن ما بر خلق تلاوت شود منکران معاد که امیدوار به لقاء ما نیستند گویند که قرآنی غیر از این بیاور یا همین را مبدل ساز. بگو: مرا نرسد که از پیش خود قرآن را تبدیل کنم من جز آنچه را که به من وحی می‌شود پیروی نمی‌کنم، من اگر عصیان پروردگارم کنم از عذاب روز بزرگ قیامت سخت می‌ترسم.'),
(1380, 10, 16, 'بگو اگر خدا نمی‌خواست هرگز بر شما تلاوت این قرآن نمی‌کردم و او هم شما را به آن آگاه نمی‌ساخت، زیرا من عمری پیش از این میان شما زیستم (که دعوی رسالت نداشتم) آیا عقل و فکرتان را کار نمی‌بندید؟'),
(1381, 10, 17, 'پس کیست ستمکارتر از آن که به خدا نسبت دروغ دهد یا آیات او را تکذیب کند؟ و البته بدکاران هرگز به فلاح و رستگاری نخواهند رسید.'),
(1382, 10, 18, 'و بت‌هایی را به جای خدا پرستش می‌کنند که آن بتان به آنها هیچ ضرر و نفعی نمی‌رسانند، و می‌گویند که این بتان شفیع ما نزد خدا هستند بگو: شما می‌خواهید خدا را یاد آور سازید به چیزی که خدا در همه آسمانها و زمین علم به آن ندارد؟ خدا از آنچه شریک او قرار می‌دهند منزه و برتر است.'),
(1383, 10, 19, 'و مردم (در فطرت توحید) یک طایفه بیش نبودند پس از آن فرقه فرقه شدند (و به انواع شرک و دینهای باطل گرویدند) و اگر کلمه‌ای که در ازل از حق سبقت یافته (که برای آزمایش مهلت یابند) نبود البته اختلافاتشان خاتمه یافته و حکم به هلاکت کافران داده می‌شد.'),
(1384, 10, 20, 'و گویند: چرا بر او آیت و معجزی از جانب خدایش نیامد (که قهرا مردم مطیع شوند)؟ پاسخ ده که دانای غیب خداست و بس، اینک شما منتظر (عذاب خدا) باشید من هم با شما منتظر (نصرت او) می‌باشم.'),
(1385, 10, 21, 'و ما هرگاه بر آدمیان بعد از آنکه آنان را رنج و زیانی رسید رحمتی فرستیم آن‌گاه در محو آیات ما مکر و سیاست به کار می‌برند. بگو: مکر و سیاست الهی سریعتر است، همانا رسولان ما (فرشتگان و قوای عالم) مکرهای شما را می‌نویسند.'),
(1386, 10, 22, 'اوست آن که شما را در برّ و بحر سیر می‌دهد تا آن‌گاه که در کشتی نشینید و کشتی با باد ملایمی سرنشینان را به حرکت آرد و آنها بدان شادمان و خوشوقت باشند ناگاه باد تندی بر کشتی بوزد و سرنشینان از هر جانب به امواج خطر در افتند و خود را در ورطه هلاکت ببینند آن زمان خدا را به اخلاص و دین فطرت بخوانند که (بار الها) اگر ما را از این خطر نجات بخشی دیگر همیشه شکر و سپاس تو خواهیم کرد.'),
(1387, 10, 23, 'اما پس از آنکه خدا از غرق نجاتشان داد باز در زمین به نا حق ظلم و ستمگری آغاز کنند. ای مردم شما هر ظلم و ستم کنید منحصرا به نفس خویش کنید در پی متاع فانی دنیا، آن‌گاه در آخرت به سوی ما باز می‌گردید و شما را به آنچه می‌کرده‌اید آگاه می‌سازیم.'),
(1388, 10, 24, 'محقّقا در مثل، زندگانی دنیا به آبی ماند که از آسمانها فرو فرستادیم تا به آن باران انواع مختلف گیاه زمین از آنچه آدمیان و حیوانات تغذیه کنند در هم رویند تا آن‌گاه که زمین (از خرمی و سبزی) به خود زیور بسته و آرایش کند و مردمش خود را بر آن قادر و متصرف پندارند ناگهان فرمان ما به شب یا روز در رسد و آن همه زیب و زیور زمین را دور کند و چنان خشک شود که گویی دیروز هیچ نبوده است. این گونه آیات خود را به تفصیل برای اهل فکرت بیان می‌کنیم.'),
(1389, 10, 25, 'و خدا به سر منزل سعادت و سلامت می‌خواند و هر که را می‌خواهد به راه مستقیم هدایت می‌کند.'),
(1390, 10, 26, 'مردم نیکوکار به نیکوترین پاداش عمل خود و زیادتیی (از لطف خدا) نایل شوند و هرگز بر رخسار آنها گرد خجلت و ذلت ننشیند، آنانند اهل بهشت و در آنجا تا ابد متنعّمند.'),
(1391, 10, 27, 'و کسانی که مرتکب اعمال بد شدند به قدر همان اعمال زشت مجازات شوند و گرد خواری و رسوایی بر رخسارشان نشیند، و نگهبانی از (عذاب) خدا نیابند، گویی روی آنها را قطعه‌هایی از شب ظلمانی پوشانده‌اند، آنان اهل آتش دوزخ و در آن همیشه معذبند.'),
(1392, 10, 28, 'و ما روزی همه خلق را جمع آریم آن‌گاه به مشرکان گوییم: شما و بتهاتان در مکان خود بایستید، سپس میانشان جدایی افکنیم، و بتان و سایر معبودان باطل زبان گشوده گویند: شما هرگز ما را پرستش نمی‌کردید.'),
(1393, 10, 29, 'پس شهادت و حکم خدا میان ما و شما کفایت کند که ما از پرستش شما غافل بودیم.'),
(1394, 10, 30, 'در آن روز هر شخصی جزاء اعمال نیک و بدی که پیش از این کرده خواهد دید و همه به سوی خدا معبود و مولای حقیقی خود بازگردانده شوند، و خدایان باطل که به دروغ به خدایی می‌بستند همه از دستشان برود.'),
(1395, 10, 31, 'مشرکان را بگو: کیست که از آسمان و زمین به شما روزی می‌دهد؟ یا کیست که گوش و چشمها عطا می‌کند؟ و کیست که از مرده زنده و از زنده مرده برمی‌انگیزد؟ و کیست که فرمانش عالم آفرینش را منظم می‌دارد؟ پس خواهند گفت: خدای یکتاست. به آنها بگو: پس چرا خدا ترس نمی‌شوید؟'),
(1396, 10, 32, 'چنین خدای قادر یکتایی به حقیقت پروردگار شماست و بعد از حق و حقیقت چه باشد غیر گمراهی؟ پس به کجا می‌برندتان؟'),
(1397, 10, 33, 'این چنین حکم شقاوت و کلمه عذاب بر فاسقان محقق شد که دیگر ایمان نمی‌آورند.'),
(1398, 10, 34, 'بگو که آیا هیچ یک از بتان و خدایان شما بر این کار قادر است که در آغاز خلق را بیافریند و سرانجام برگرداند؟ بگو: تنها خدای یکتاست که در اول خلایق را خلق کرده آن گاه همه را (به سوی خود) بازمی‌گرداند، پس کجا می‌گردانندتان؟'),
(1399, 10, 35, 'باز بگو: آیا هیچ یک از بتان و خدایان شما مشرکان (کسی را) به راه حق هدایت تواند کرد؟ بگو: تنها خداست که به راه حق هدایت می‌کند، آیا آن که به راه حق رهبری می‌کند سزاوارتر به پیروی است یا آن که خود هدایت نیابد مگر آنکه هدایتش کنند؟ پس شما مشرکان را چه شده، چگونه قضاوت می‌کنید؟'),
(1400, 10, 36, 'و اکثر این مردم جز از خیال و گمان باطل خود از چیزی پیروی نمی‌کنند در صورتی که گمان و خیالات موهوم هیچ از حق بی‌نیاز نمی‌گرداند (و به علم یقین نمی‌رساند) و خدا به هر چه این کافران می‌کنند آگاه است.'),
(1401, 10, 37, 'و این قرآن نه بدان پایه است که کسی جز به وحی خدا تواند بافت، لیکن سایر کتب آسمانی را تصدیق می‌کند و کتاب الهی را به تفصیل بیان می‌کند که بی‌هیچ شک نازل از جانب خدای عالمیان است.'),
(1402, 10, 38, 'بلکه کافران می‌گویند قرآن را (محمد) خود بافته است. بگو: اگر راست می‌گویید شما خود و هم از هر کس نیز می‌توانید کمک بطلبید و یک سوره مانند آن بیاورید.'),
(1403, 10, 39, 'بلکه انکار چیزی را می‌کنند که علمشان به آن احاطه نیافته و حقیقت و باطن آن هنوز به آنان نرسیده است، پیشینیان هم این چنین رسل و آیات خدا را تکذیب کردند، پس بنگر عاقبت کار ستمکاران عالم به کجا کشید!'),
(1404, 10, 40, 'و برخی از مردم به قرآن ایمان می‌آورند و برخی ایمان نمی‌آورند، و خدای تو حال تبهکاران را بهتر می‌داند.'),
(1405, 10, 41, 'و اگر تو را تکذیب کردند بگو: عمل من برای من و عمل شما برای شما (هر کس جزای خود را خواهد یافت) شما بری از کردار من هستید و من بیزار از کردار شما.'),
(1406, 10, 42, 'برخی از این منکران (چون قرائت قرآن کنی) به ظاهر به تو گوش کنند (ولی به معنی نمی‌شنوند) آیا تو کران را گر چه هیچ عقل و ادراکی ندارند از کلام خدا توانی چیزی بشنوانی؟'),
(1407, 10, 43, 'و برخی از منکران (هنگام تلاوت قرآن به چشم ظاهر) در تو می‌نگرند (ولی به مقام باطن تو پی نمی‌برند) آیا تو کوران را گر چه هیچ نبینند هدایت توانی کرد؟'),
(1408, 10, 44, 'خدا هرگز به مردم ستم نمی‌کند ولی مردم خود در حق خویش ستم می‌کنند.'),
(1409, 10, 45, 'و روزی که همه خلایق را جمع آرد گویا (در دنیا) ساعتی از روز بیش درنگ نکرده‌اند، (در آن روز) یکدیگر را کاملا می‌شناسند، آنان که لقای خدا را انکار کردند بسیار زیان کرده‌اند و هرگز راه نیافته‌اند.'),
(1410, 10, 46, 'و اگر ما بعضی از عقاب آن منکران را که وعده کردیم (در حیات دنیا) به تو نشان دهیم یا (به تأخیر افکنده و پیشتر از عقاب آنها) قبض روح تو کنیم باز بازگشت آنان در قیامت به سوی ماست، آن گاه خدا بر اعمال آنها گواه و آگاه است.'),
(1411, 10, 47, 'و برای هر امتی رسولی است که هرگاه رسول آنها آمد (و حجت بر آنان تمام شد) میان آنها حکم به عدل شود و بر هیچ کس (در کیفر و پاداش) ستم نخواهد شد.'),
(1412, 10, 48, 'و (کافران) گویند: پس این وعده قیامت کی خواهد بود اگر شما راستگویید؟'),
(1413, 10, 49, 'پاسخ ده که من مالک نفع و ضرر خود نیستم (تا چه رسد به دیگران) مگر هر چه خدا خواهد، برای هر امتی اجل معینی است که چون فرا رسد ساعتی پس و پیش نخواهند شد.'),
(1414, 10, 50, 'بگو: مرا خبر دهید که اگر شب یا روز عذاب خدا شما را فرا رسد (چه راه مفری دارید و) گناهکاران چه چیزی را از او به تعجیل می‌طلبند؟'),
(1415, 10, 51, 'آیا آن گاه که عذاب واقع شد به او ایمان می‌آورید؟ الآن (ایمان آوردید و به جزع و توبه برخاستید)؟ و حال آنکه قبلا عذاب را به تعجیل می‌خواستید.'),
(1416, 10, 52, 'آن گاه به ستمکاران گویند: بچشید عذاب ابدی را، آیا این عذاب جز نتیجه اعمال زشت شماست؟'),
(1417, 10, 53, 'کافران از تو می‌پرسند که آیا این (وعده عذاب) حق است؟ بگو: آری قسم به خدای من که البته حق است و شما از آن هیچ مفرّی ندارید.'),
(1418, 10, 54, 'و اگر در (آن روز) مردم ستمکار مالک روی زمین باشند آرزو کنند که همه دارایی خود را فدا دهند (تا مگر خویشتن را از عذاب برهانند و نتوانند) و چون عذاب را مشاهده کنند حسرت و پشیمانی خود را پنهان دارند (که شماتت از مردم نکشند) و در حق آنها حکم به عدل شود و به آنان هیچ ستمی نخواهد شد.'),
(1419, 10, 55, 'آگاه باشید که هر چه در آسمانها و زمین است ملک خداست و هم آگاه باشید که وعده خدا همه حق محض است، ولی اکثر خلق از آن آگه نیستند.'),
(1420, 10, 56, 'خداست که زنده می‌کند و می‌میراند و باز همه به سوی او بازگردانده می‌شوید.'),
(1421, 10, 57, 'ای مردم عالم، به حقیقت نامه‌ای که همه پند و اندرز و شفای دلها و هدایت و رحمت بر مؤمنان است از جانب خدایتان آمد.'),
(1422, 10, 58, 'بگو که باید منحصرا به فضل و رحمت خدا شادمان شوند (و به نزول قرآن مسرور باشند) که آن بهتر و مفیدتر از ثروتی است که می‌اندوزند.'),
(1423, 10, 59, 'باز (به مشرکان عرب) بگو: با من بگویید که آیا رزقی که خدا برای شما فرستاده (و حلال فرموده) و شما از پیش خود بعضی را حرام و بعضی را حلال می‌کنید، آیا این به دستور خداست یا بر خدا افترا می‌بندید؟'),
(1424, 10, 60, 'و آنان که بر خدا دروغ می‌بندند مگر چه گمان دارند به روز قیامت؟! البته خدا را با بندگان فضل و احسان بسیار است ولی اکثر مردم شکر نمی‌کنند.'),
(1425, 10, 61, 'و تو در هیچ حالی نباشی و هیچ آیه‌ای از قرآن تلاوت نکنی و به هیچ عملی تو و امت وارد نشوید جز آنکه ما همان لحظه شما را مشاهده می‌کنیم و هیچ چیز به وزن ذره‌ای در همه زمین و آسمان از خدای تو پنهان نیست و کوچکتر از ذره و بزرگتر از آن هر چه هست همه در کتاب مبین حق (و لوح علم الهی) مسطور است.'),
(1426, 10, 62, 'آگاه باشید که دوستان خدا هرگز هیچ ترسی (از حوادث آینده عالم) و هیچ حسرت و اندوهی (از وقایع گذشته جهان) در دل آنها نیست.'),
(1427, 10, 63, 'آنان که اهل ایمان و خداترسند.'),
(1428, 10, 64, 'آنها را پیوسته بشارت است هم در حیات دنیا (به مکاشفات در عالم خواب) و هم در آخرت (به نعمتهای بهشت). سخنان خدا را تغییر و تبدیلی نیست، این است فیروزی بزرگ.'),
(1429, 10, 65, 'و سخن منکران خاطرت را غمگین نسازد، که هر عزت و اقتداری مخصوص خداست و او شنوا و داناست.'),
(1430, 10, 66, 'آگاه باش که هر که در همه آسمانها و هر که در زمین است ملک خداست و مشرکان که غیر از خدا را می‌خوانند چه چیزی را پیروی می‌کنند؟ آنچه پیروی می‌کنند گمان باطلی بیش نیست و جز آنکه دروغی بافند و تخمینی زنند کاری ندارند.'),
(1431, 10, 67, 'اوست خداوندی که شب تار را برای آسایش شما و روز را روشن (برای امر معاش) مقرر فرمود، که در آن نشانه‌هایی (از قدرت خدا) برای مردمی که بشنوند پدیدار است.'),
(1432, 10, 68, 'کافران قائل شدند که خدا فرزندی اتخاذ کرده (چنین نیست) او منزه است و هم بی‌نیاز که هر چه در آسمانها و زمین است همه ملک اوست، و شما کافران و مشرکان بر این قول خود هیچ دلیلی ندارید، آیا بی‌دلیل و از روی جهالت نسبت به خدا چنین دروغ سخن می‌گویید؟!'),
(1433, 10, 69, 'بگو: هرگز آنان که به خدا نسبت دروغ دهند نجات و رستگاری ندارند.'),
(1434, 10, 70, '(این گفتار باطل، همه برای طمع) متاع دنیاست، سپس بازگشتشان به سوی ما خواهد بود و ما آنها را به کیفر کفرشان عذابی سخت بچشانیم.'),
(1435, 10, 71, 'و حکایت نوح را بر اینان باز خوان که به امتش گفت: ای قوم اگر مقام رسالت و اندرز من به آیات خدا بر شما گران می‌آید، من تنها به خدا توکل کردم، شما هم به اتفاق بتان و خدایان باطل خود هر مکر و تدبیری (بر آزار من) دارید انجام دهید تا امر بر شما پوشیده نباشد و درباره من هر مکر و اندیشه دارید به کار برید و مهلتم ندهید.'),
(1436, 10, 72, 'پس اگر شما روی گردانیده (و نصیحت مرا نپذیرفتید) من از شما اجری نخواسته بلکه اجر من تنها بر خداست و من خود از جانب حق مأمورم که از اهل اسلام و تسلیم حکم او باشم.'),
(1437, 10, 73, 'قوم نوح باز او را تکذیب کردند، ما هم او و پیروانش را که با او در کشتی بودند نجات داده و خلفای روی زمین قرار دادیم و آنان که آیات ما را تکذیب کردند همه را غرق کردیم، بنگر تا عاقبت انذار شدگان به کجا کشید!'),
(1438, 10, 74, 'آن گاه بعد از نوح پیغمبرانی را به امتانشان فرستادیم، آنها با آیات و معجزات بر آن امتها آمدند و آنان هم همان آیاتی را که پیشینیان تکذیب کردند، تکذیب کرده و ایمان نیاوردند. این چنین ما هم بر دل‌های سرکشان مهر برنهیم.'),
(1439, 10, 75, 'آن‌گاه بعد از آن رسولان، موسی و هارون را با معجزات خود به سوی فرعون و اشراف مملکت او فرستادیم، آنها هم گردنکشی کردند و مردمی تبهکار بودند.'),
(1440, 10, 76, 'و چون حق از جانب ما بر آنها آمد گفتند: این سحر بودنش بر همه آشکار است.'),
(1441, 10, 77, 'موسی به آنان گفت: آیا به آیات حق که برای شما آمد نسبت سحر می‌دهید؟ آیا این سحر است و حال آنکه ساحران را هرگز فلاح و فیروزی نخواهد بود؟!'),
(1442, 10, 78, 'پاسخ دادند که آیا تو آمده‌ای که ما را از عقاید و آدابی که پدران خود را بر آن یافته‌ایم بازگردانی تا خود و برادرت هارون در زمین سلطنت یابید و بر ما حکمفرما شوید؟ ما هرگز به شما ایمان نخواهیم آورد.'),
(1443, 10, 79, 'و فرعون (به اتباعش) گفت: هر کجا ساحری ماهر و داناست همه را نزد من حاضر کنید.'),
(1444, 10, 80, 'آن گاه که ساحران همه حاضر شدند موسی به ساحران گفت که شما اول بساط سحر خود را بیندازید.'),
(1445, 10, 81, 'آن‌گاه که ساحران بساط جادو انداختند، موسی گفت: خدا البته سحر شما را باطل خواهد کرد، که خدا هرگز عمل مفسدان را اصلاح نکند.'),
(1446, 10, 82, 'و خدا به (آیات و) کلمات خود حق را تا ابد پایدار گرداند هر چند بدکاران عالم راضی نباشند.'),
(1447, 10, 83, 'پس کسی به موسی ایمان نیاورد جز قبیله او آن هم با حال خوف و تقیه از فرعون و مهتران اتباعش که مبادا فرعون در صدد فتنه و قتلشان برآید که فرعون در سرزمین مصر بسیار علوّ و سرکشی داشت و سخت متعدّی و ستمکار بود.'),
(1448, 10, 84, 'و موسی گفت که ای قوم من، شما اگر به حقیقت ایمان به خدا آورده و اگر به راستی تسلیم فرمان او هستید بر او توکل کنید.'),
(1449, 10, 85, 'پیروان موسی نیز همه گفتند: ما بر خدا توکل کردیم، بار الها ما را دستخوش فتنه اشرار و قوم ستمکار مگردان.'),
(1450, 10, 86, 'و ما را به رحمت و لطف خود از شر کافران نجات ده.'),
(1451, 10, 87, 'و به موسی و برادرش (هارون) وحی کردیم که شما برای پیروانتان در شهر مصر منزل گیرید و (اکنون که از بیم فرعونیان به مساجد نتوانید رفت)، خانه‌هایتان را قبله و معبد خود قرار دهید و نماز به پا دارید و (تو هم ای رسول) مؤمنان را (به فتح در دنیا و بهشت در آخرت) بشارت ده.'),
(1452, 10, 88, 'و موسی عرض کرد: بار الها تو به فرعون و فرعونیان در حیات دنیا ملک و اموال و زیورهای بسیار بخشیدی که-پروردگارا-بدین وسیله بندگان را از راه تو گمراه کنند، بار خدایا، اموال آنها را نابود گردان و دلهایشان را سخت بربند که ایمان نیاورند تا هنگامی که عذاب دردناک را مشاهده کنند.'),
(1453, 10, 89, 'خدا فرمود: دعای شما مستجاب شد، پس هر دو استوار و پا برجا باشید و از راه مردم جاهل پیروی مکنید.'),
(1454, 10, 90, 'و ما بنی اسرائیل را از دریا گذرانیدیم پس آنگه فرعون و سپاهش به ظلم و ستمگری آنها را تعقیب کردند تا چون هنگام غرق فرعون فرا رسید گفت: اینک من ایمان آوردم که حقّا جز آن کسی که بنی اسرائیل به او ایمان دارند خدایی در عالم نیست و من هم از مسلمانان و اهل تسلیم فرمان او هستم.'),
(1455, 10, 91, 'اکنون باید ایمان بیاوری؟ در صورتی که از این پیش عمری به (کفر و) نافرمانی زیستی و از مردم ظالم بدکار بودی.'),
(1456, 10, 92, 'پس ما امروز (تو را غرق دریای هلاک کرده و) بدنت را برای عبرت آیندگان به ساحل نجات می‌رسانیم با آنکه بسیاری از مردم از آیات قدرت ما غافل هستند.'),
(1457, 10, 93, 'و محققا ما بنی اسرائیل را (پس از هلاک دشمنان) به مقام صدق (و منزل آسایش) مسکن دادیم و از نعمتهای پاکیزه (در ملک شام و مصر) روزی آنها کردیم، و همیشه بنی اسرائیل (در ایمان به رسول خاتم) اتفاق داشتند تا زمانی که آن رسول با قرآن آسمانی برای هدایت آنها آمد آن‌گاه آغاز اختلاف کردند (و رسالت پیغمبر بزرگ را از عناد و لجاج منکر شدند. ای رسول ما) خدای تو روز قیامت میان آنها در آنچه اختلاف می‌کردند حکم خواهد کرد.'),
(1458, 10, 94, 'پس اگر شک و ریبی از آنچه به تو فرستادیم در دل داری از خود علمای اهل کتاب که کتاب آسمانی پیش از تو را می‌خوانند بپرس. همانا (کتاب آسمانی) حق از جانب خدایت بر تو آمد و ابدا نباید شک و ریبی در دل راه دهی.'),
(1459, 10, 95, 'و نباید هرگز از آنان که تکذیب آیات خدا کردند باشی که از زیانکاران عالم خواهی شد.'),
(1460, 10, 96, 'البته آنان که حکم عذاب خدا بر آنها حتم است ایمان نمی‌آورند.'),
(1461, 10, 97, 'اگر چه هر گونه معجزه و آیتی از جانب خدا بر آنان بیاید، تا وقتی که عذاب دردناک را به چشم مشاهده کنند.'),
(1462, 10, 98, 'پس چرا باید از هیچ شهری در وقتی که ایمانشان سود می‌بخشد ایمان نیاورند (و تا زمان معاینه عذاب لجاجت کنند) و تنها قوم یونس باشند که چون ایمان آوردند ما عذاب ذلت را در دنیا از آنها برداشتیم و تا زمانی معین (که وقت مرگ طبیعی بود) آنها را متمتّع و بهره‌مند گردانیدیم.'),
(1463, 10, 99, 'و اگر خدای تو (در مشیّت ازلی) می‌خواست اهل زمین همه یکسره ایمان می‌آوردند، آیا تو می‌خواهی تا به جبر و اکراه همه را مؤمن و خداپرست گردانی؟'),
(1464, 10, 100, 'و هیچ یک از نفوس بشر را تا خدا رخصت ندهد ایمان نیاورد و پلیدی (کفر و جهالت) را خدا برای مردم بی‌خرد که عقل را کار نبندند مقرر می‌دارد.'),
(1465, 10, 101, 'بگو: در آسمانها و زمین بنگرید تا چه مشاهده می‌کنید. و (گر چه) مردمی را که نخواهند ایمان آورند دلایل و آیات و بیم دادنهای الهی هرگز سودمند نمی‌افتد.'),
(1466, 10, 102, 'پس مردمی که ایمان نمی‌آورند منتظر چیستند جز آنکه روزگار هلاکت و عذابی که پیشینیان دیدند به چشم ببینند؟ بگو که شما (نزول عذاب را)، منتظر باشید که من هم با شما منتظر (نزول فتح و نصرت خدا) می‌باشم.'),
(1467, 10, 103, 'آن گاه ما رسولان خود و مؤمنان را نجات می‌دهیم، ما بر خود فرض کردیم که این چنین اهل ایمان را نجات بخشیم.'),
(1468, 10, 104, 'بگو که ای مردم، اگر شما به آیین من شک دارید پس (یقین دارید که) من هرگز خدایان باطلی را که شما می‌پرستید نخواهم پرستید، بلکه تنها خدای یکتایی را که مرگ (و زندگانی) همه شما خلایق به امر اوست می‌پرستم و مأمورم که خود از اهل ایمان باشم.'),
(1469, 10, 105, 'و (خدا مرا امر کرده به) این که روی به جانب دین حنیف (اسلام و آیین پاک توحید) آور و هرگز هم آیین مشرکان مباش.'),
(1470, 10, 106, 'و غیر از خدای یکتا هیچ یک از این خدایان باطل را که به حال تو نفع و ضرری ندارند به خدایی مخوان و گر نه از ستمکاران خواهی بود.'),
(1471, 10, 107, 'و اگر خدا بر تو ضرری خواهد هیچ کس جز او دفع آن ضرر نتواند، و اگر خیر و رحمتی خواهد باز احدی منع فضل او نتواند، که فضل و رحمت خود را به هر کس از بندگان بخواهد البته می‌رساند و اوست خدای بسیار آمرزنده و مهربان.'),
(1472, 10, 108, 'بگو: ای مردم به حقیقت (کتاب و رسول) حق از جانب خدایتان برای شما آمد، پس هر کس هدایت یافت نفعش بر خود اوست و هر کس به راه گمراهی شتافت زیانش بر خود اوست و من نگهبان شما (از مؤاخذه خدا) نیستم.'),
(1473, 10, 109, 'و تو خود از وحی خدای که بر تو می‌رسد پیروی کن و راه صبر پیش گیر تا وقتی که خدا (میان تو و مخالفانت) حکم کند و او بهترین حکمفرمایان عالم است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الر (اسرار این حرف نزد خدا و رسول است)، این قرآن کتابی است دارای آیاتی محکم که از جانب خدایی حکیم و آگاه به تفصیل و بسیار روشن بیان گردیده است.'),
(1475, 11, 2, '(تا تذکر دهد به خلق) که جز خدای یکتا هیچ کس را نپرستید، که من حقّا برای اندرز و بشارت شما امت آمده‌ام.'),
(1476, 11, 3, 'و (تا به شما بگویم که از گناهانتان) آمرزش از خدا طلبید و به درگاه او توبه و انابه کنید تا شما را تا اجل معین و هنگام مرگ لذت و بهره نیکو بخشید و در حق هر مستحق رحمتی تفضل فرماید، و اگر روی بگردانید سخت از عذاب روز بزرگ قیامت بر شما می‌ترسم.'),
(1477, 11, 4, 'رجوع شما به سوی خداست و او بر همه چیز تواناست.'),
(1478, 11, 5, 'آگه باشید که آنان (یعنی منافقان امت) روی دلها از خدا می‌گردانند (و از حضور رسول و استماع کلام خدا دوری جسته و احتراز می‌کنند) تا خود را از او پنهان دارند، آگه باش که هر گه سر در جامه خود بپیچند (که از حق پنهان شوند) خدا هر چه پنهان یا آشکار کنند همه را می‌داند، که او بر درون دلها محققا آگاه است.'),
(1479, 11, 6, 'و هیچ جنبنده در زمین نیست جز آنکه روزیش بر خداست و خدا قرارگاه (منزل دائمی) و آرامشگاه (جای موقت) او را می‌داند، و همه احوال خلق در دفتر علم ازلی خدا ثبت است.'),
(1480, 11, 7, 'و اوست خدایی که آسمانها و زمین را در فاصله شش روز آفرید و عرش با عظمت او (شاید یک معنی عرش که روح و حقیقت انسان است اینجا مراد باشد) بر آب قرار داشت (شاید مراد از آب علم باشد) تا شما را بیازماید که عمل کدام یک از شما نیکوتر است. و محققا اگر به این مردم بگویی که پس از مرگ زنده خواهید شد، همانا کافران خواهند گفت که این سخن را هرگز حقیقتی جز سحر آشکار نیست.'),
(1481, 11, 8, 'و اگر ما عذاب را از آن منکران معاد تا وقت معین (هنگام مرگ) به تعویق اندازیم آنها گویند: چه موجب تأخیر عذاب شده؟ آگه باشند که چون هنگام عذاب برسد هرگز از آنان بازگردانده نشود و آنچه بدان تمسخر می‌کردند سخت آنان را فرا گیرد.'),
(1482, 11, 9, 'و اگر ما بشر را به نعمت و رحمتی برخوردار کنیم، سپس (چون کفران کرد) آن نعمت را از او بازگیریم او سخت به خوی نومیدی و کفران درافتد.'),
(1483, 11, 10, 'و اگر آدمی را به نعمتی پس از محنتی که به او رسیده باشد رسانیم (مغرور و غافل شود و) گوید که دیگر روزگار زحمت و رنج من سرآمده، سرگرم شادمانی و مفاخرت گردد.'),
(1484, 11, 11, 'مگر آنان که دارای ملکه صبر و عمل صالحند که بر آنها آمرزش حق و اجری بزرگ است.'),
(1485, 11, 12, '(ای رسول ما) مبادا بعض آیاتی را که به تو وحی شده (درباره کافران به ملاحظاتی) تبلیغ نکنی و از قول مخالفانت که می‌گویند (اگر این مرد پیغمبر است) چرا گنج و مالی ندارد و یا فرشته آسمان همراه او نیست دلتنگ شوی، که وظیفه تو تنها نصیحت و اندرز خلق است، و حاکم و نگهبان هر چیز خداست.'),
(1486, 11, 13, 'آیا کافران می‌گویند این قرآن را خود او به هم بافته و به خدا نسبت می‌دهد؟! بگو: اگر راست می‌گویید شما هم با کمک همه فصحای عرب بدون وحی خدا ده سوره بربافته مانند این قرآن بیاورید.'),
(1487, 11, 14, 'پس هرگاه کافران جواب شما را ندادند در این صورت بدانید که این کتاب به علم خدا نازل شده و دعوتش این است که هیچ خدایی جز آن ذات یکتای الهی نیست، پس آیا شما مردم تسلیم (حکم خدا و رسول) خواهید شد؟'),
(1488, 11, 15, 'کسانی که طالب تعیّش مادی و زینت دنیوی هستند ما مزد سعی آنها را در همین دنیا کاملا می‌دهیم و هیچ از اجر عملشان در دنیا کم نخواهد شد.'),
(1489, 11, 16, 'ولی هم اینان هستند که دیگر در آخرت نصیبی جز آتش دوزخ ندارند و همه اعمالشان در راه دنیا (پس از مرگ) ضایع و باطل می‌گردد.'),
(1490, 11, 17, 'آیا پیغمبری که از جانب خدا دلیلی روشن (مانند قرآن) دارد و گواهی صادق (مانند علی علیه السّلام) در پی اوست و به علاوه کتاب تورات موسی هم که پیشوا و رحمت حق بر خلق بود پیش از او بوده و اینان (از اهل کتاب) به آن ایمان دارند (به آمدن آن پیامبر بشارت داده، چنین پیامبری مانند آن کس است که این امتیازات را ندارد)؟ و هر کس از طوایف بشر به او کافر شود وعده گاهش البته آتش دوزخ است، پس در آن هیچ شک مدار که این حق و درست از سوی پروردگار توست و لیکن اکثر مردم (به آن) ایمان نمی‌آورند.'),
(1491, 11, 18, 'و در جهان از آنهایی که به خدا نسبت دروغ دادند ستمکارتر کیست؟ آنان بر خدای خود عرضه داشته شوند و گواهان (محشر) گویند: اینان هستند که بر خدای خود دروغ بستند، آگاه باشید که لعن خدا بر ستمکاران عالم است.'),
(1492, 11, 19, 'آن ستمکاران که (بندگان را) از راه خدا باز می‌دارند و سعی می‌کنند که راه حق را کج کنند (و خلق را به راه باطل کشند) و هم آنها که منکر قیامتند.'),
(1493, 11, 20, 'اینها هیچ قدرت و راه مفرّی در زمین (از قهر خدا) ندارند و جز خدا یار و مددکاری نخواهند یافت، عذابشان دو چندان شود، و هرگز گوششان هم در شنوایی (سخن راست) و چشمشان در بینایی (حق و حقیقت) به آنها مدد نتواند کرد.'),
(1494, 11, 21, 'همین مردم هستند که نفوس خود را سخت در زیان افکندند و هر دروغی که می‌بسته همه از دستشان برود و محو و نابود شود.'),
(1495, 11, 22, 'ناگزیر در عالم آخرت زیانکارترین مردم آنها هستند.'),
(1496, 11, 23, 'اما آنان که به خدا ایمان آورده و به اعمال صالح پرداختند و به درگاه خدای خود خاضع و خاشع گردیدند آنها البته اهل بهشت جاویدند که در آن بهشت متنعّم ابدی خواهند بود.'),
(1497, 11, 24, 'حال این دو گروه (کفر و ایمان) در مثل به شخص کور و کر و شخص شنوا و بینا مانند است، آیا حال این هر دو شخص یکسان است؟! پس چرا متذکر نمی‌شوید؟!'),
(1498, 11, 25, 'و به حقیقت ما نوح را به سوی قومش به رسالت فرستادیم (او قومش را گفت) که من با بیان روشن برای نصیحت و هشدار شما آمدم.'),
(1499, 11, 26, 'که غیر خدای یکتا کسی را نپرستید، که من از عذاب روز سخت و دردناک قیامت بر شما می‌ترسم.'),
(1500, 11, 27, 'سران کافران قومش پاسخ دادند که ما تو را مانند خود بشری بیشتر نمی‌دانیم و در بادی نظر، آنان که پیرو تواند اشخاصی پست و بی‌قدر بیش نیستند و ما هیچ‌گونه مزیتی برای شما نسبت به خود نمی‌بینیم، بلکه شما را دروغگو می‌پنداریم.'),
(1501, 11, 28, 'نوح قومش را پاسخ داد که ای قوم، شما چه می‌گویید، اگر مرا دلیل روشن و رحمت مخصوص از جانب پروردگار عطا شده باشد ولی حقیقت حال بر شما پوشیده مانده باشد آیا (جهالت نیست که) ما شما را (به رحمت و سعادت) اجبار کنیم با آنکه شما تنفّر اظهار می‌کنید؟'),
(1502, 11, 29, 'ای قوم، من از شما در عوض هدایت ملک و مالی نمی‌خواهم، اجر من تنها بر خداست و من هرگز آن مردم با ایمان را از خود دور نمی‌کنم، که آنها به شرف ملاقات خدا می‌رسند، ولی به نظر من شما خود مردم نادانی هستید.'),
(1503, 11, 30, 'و ای قوم، اگر من آن مردم (پاک خداپرست) را از خود برانم (و خدا از من برنجد) به مدد که از خشم خدا نجات یابم؟ آیا پند نمی‌گیرید؟'),
(1504, 11, 31, 'من شما را نمی‌گویم که خزائن خدا نزد من است و نه مدعیم که از علم غیب حق آگاهم و نه دعوی کنم که فرشته آسمانم و هرگز مؤمنان پاکی را که به چشم شما خوارند نخواهم گفت که خدا آنها را هیچ خیری نرساند، که خدا بر دلهای با اخلاص آنها داناتر است که (آنها را عزیز و گرامی داشته و) اگر من آنها را خوار شمارم از ستمکاران عالم به شمار باشم.'),
(1505, 11, 32, 'قوم گفتند: ای نوح، تو با ما جدل و گفتگوی بسیار کردی، اکنون اگر راست می‌گویی بر ما عذابی که وعده می‌دهی بیار.'),
(1506, 11, 33, 'نوح گفت: آن وعده را اگر خدا خواهد به شما می‌رساند و هیچ از آن مفرّی ندارید.'),
(1507, 11, 34, 'و اگر بخواهم شما را پند دهم دیگر پند و نصایح من سود نکند اگر خدا خواهد که شما را گمراه کند (یعنی پس از اتمام حجت به تعلیمات رسالت به حال گمراهی خود واگذارد)، او خدای شماست و به سوی او بازگردانده می‌شوید.'),
(1508, 11, 35, 'آیا باز خواهند گفت که او از پیش خود این سخنان (قرآن) را فرا بافته؟ بگو: اگر چنین باشد عقوبت آن گناه بر من است (نه بر شما) و من از (کفر و) گناه شما بیزارم.'),
(1509, 11, 36, 'و به نوح وحی شد که جز همین عده که ایمان آورده‌اند دیگر ابدا هیچ کس از قومت ایمان نخواهد آورد، پس تو بر کفر و عصیان این مردم لجوج محزون مباش.'),
(1510, 11, 37, 'و به ساختن کشتی در حضور و مشاهده ما و به دستور ما مشغول شو و درباره ستمکاران که البته باید غرق شوند دیگر با من سخن مگوی.'),
(1511, 11, 38, 'و نوح به ساختن کشتی پرداخت و هرگاه گروهی از قومش بر او می‌گذشتند وی را مسخره و استهزاء می‌کردند و نوح در جواب آنها می‌گفت: اگر (امروز) شما ما را مسخره می‌کنید ما هم (روزی) شما را مسخره کنیم همان‌گونه که شما مسخره می‌کنید.'),
(1512, 11, 39, 'و به زودی معلوم شما شود که کدام یک به عذاب ذلت و خواری گرفتار و عذاب دائم خدا را مستوجب خواهد شد.'),
(1513, 11, 40, '(نوح به ساختن کشتی و قوم به تمسخر پرداختند) تا وقتی که فرمان (قهر) ما فرا رسید و از تنور آتش آب بجوشید، در آن هنگام به نوح خطاب کردیم که از هر جفت حیوان دو فرد (نر و ماده) با جمیع خانواده‌ات-جز آن (پسرت کنعان و زنت) که وعده هلاکش در علم ازلی گذشته-و هر که ایمان آورده همه را در کشتی سوار کن (که از غرقاب برهند). و گرویدگان به نوح عده قلیلی بیش نبودند.'),
(1514, 11, 41, 'و نوح دستور داد که شما مؤمنان به کشتی در آیید تا به نام خدا کشتی هم روان شود و هم به ساحل نجات رسد، که خدای من البته بسیار آمرزنده و مهربان است.'),
(1515, 11, 42, 'و آن کشتی آنان را در میان امواجی مانند کوه گردش می‌داد که در آن حال نوح فرزندش را که به کناری رفته بود ندا کرد که ای پسر، تو هم با ما بدین کشتی درآی و با کافران همراه مباش.'),
(1516, 11, 43, 'آن پسر (نادان نااهل) پدر را پاسخ داد که من به زودی بر فراز کوهی روم که از خطر آبم نگه دارد، نوح گفت: امروز هیچ کس را از (قهر) خدا جز لطف او پناه نیست. و موج میان آنها جدایی افکند و پسر با کافران به دریا غرق شد.'),
(1517, 11, 44, 'و به زمین خطاب شد که ای زمین، فورا آب خود را فرو بر، و به آسمان امر شد که باران را قطع کن، و آب (به یک لحظه) خشک شد و حکم (قهر الهی) انجام یافت و کشتی بر کوه جودی قرار گرفت، و فرمان هلاک ستمکاران در رسید.'),
(1518, 11, 45, 'و نوح به درگاه خدا عرض کرد: بار پروردگارا، فرزند من از اهل بیت من است (که وعده لطف و نجات به آنها دادی) و وعده تو هم حتمی است و قادرترین حکمفرمایانی.'),
(1519, 11, 46, 'خدا خطاب کرد که ای نوح، فرزند تو هرگز با تو اهلیت ندارد، زیرا او را عمل بسیار ناشایسته است، پس تو از من تقاضای امری که هیچ از حال آن آگه نیستی مکن، من تو را پند می‌دهم که از مردم جاهل مباش.'),
(1520, 11, 47, 'نوح عرض کرد: بار الها، من پناه می‌برم به تو که دیگر چیزی که نمی‌دانم از تو تقاضا کنم، و اگر گناه مرا نبخشی و بر من ترحم نفرمایی من از زیانکاران عالم خواهم بود.'),
(1521, 11, 48, 'و خطاب شد که ای نوح، از کشتی فرود آی که سلام ما و برکات و رحمت ما بر تو و بر آن امم و قبایلی که همیشه (در خداپرستی) با تواند اختصاص یافته، و امتهایی دیگر هستند که (خودسر و ستمگر شوند و) ما به آنها بهره‌ای (از دنیا) دهیم سپس عذابی سخت (در قیامت) از ما به آنان خواهد رسید.'),
(1522, 11, 49, 'این (حکایت نوح) از اخبار غیب است که به تو وحی می‌کنیم و پیش از آنکه ما به تو وحی کنیم تو و قومت هیچ از آن آگاه نبودید، پس تو در طاعت حق راه صبر پیش گیر که عاقبت نیکو اهل تقوا راست.'),
(1523, 11, 50, 'و ما برای (هدایت) قوم عاد برادرشان (یعنی مردی از طایفه آنها) هود را فرستادیم، گفت که ای مردم، خدای یکتا را پرستش کنید که جز او شما را خدایی نیست و شما (در گفتاری که از بتان و خدایان باطل به میان آورده‌اید بدانید که) افترا زننده‌ای بیش نیستید.'),
(1524, 11, 51, 'ای قوم، من از شما مزد رسالت نمی‌خواهم، اجر من جز بر خدا که مرا آفریده، نیست، آیا فکر و عقل را کار نمی‌بندید؟'),
(1525, 11, 52, 'و ای قوم، از خدای خود آمرزش طلبید و به درگاه او توبه کنید تا از آسمان بر شما رحمت فراوان نازل گرداند و بر قوت و توانایی شما بیفزاید، و زنهار به نابکاری و عصیان روی مگردانید.'),
(1526, 11, 53, 'قوم هود وی را پاسخ دادند که ای هود، تو برای ما دلیلی (روشن بر دعوی رسالت خود) نیاوردی و ما هرگز از خدایان خود به مجرد حرف تو دست نمی‌کشیم و ما هرگز به تو ایمان نخواهیم آورد.'),
(1527, 11, 54, 'تنها چیزی که (درباره تو) می‌گوییم این است که برخی از خدایان ما تو را آسیب جنون رسانیده است. هود به آنها گفت: من خدا را گواه می‌گیرم و شما هم گواه باشید که من از خدایانی که غیر خدای یکتا می‌پرستید بیزارم.'),
(1528, 11, 55, 'شما هم هر فکر و تدبیری در کار من دارید بیندیشید و هیچ مهلتم ندهید.'),
(1529, 11, 56, 'من بر خدا که پروردگار من و شماست توکل کرده‌ام، که زمام اختیار هر جنبنده‌ای به دست اوست و البته پروردگار من (در کار بندگان) به راه راست است.'),
(1530, 11, 57, 'پس اگر شما روی بگردانید من به وظیفه خود که ابلاغ رسالت (و اتمام حجت) بر شماست قیام کردم، و خدای من قومی دیگر را جانشین شما خواهد کرد و به او هیچ ضرری نتوانید رسانید، که پروردگار من بر هر چیز نگهبان است.'),
(1531, 11, 58, 'و چون فرمان (قهر) ما (به هلاکت قوم) در رسید ما به فضل و رحمت خود هود و کسانی را که با او ایمان آوردند نجات بخشیدیم و آنها را از عذاب بسیار سختی ایمن ساختیم.'),
(1532, 11, 59, 'و همین قوم عاد (امت هود) هستند که آیات خدا را انکار و پیغمبران او را نافرمانی کردند و پیرو امر هر شخصی جبار و عنودی شدند.'),
(1533, 11, 60, 'و آنها به لعن خدا هم در حیات دنیا و هم در قیامت گرفتار گردیدند، آگاه باشید که قوم عاد به خدای خود کافر شدند، بدانید که قوم عاد، امت هود از رحمت خدا دورند.'),
(1534, 11, 61, 'و باز ما به سوی قوم ثمود برادرشان صالح را به رسالت فرستادیم، صالح به قوم خود گفت: ای قوم، خدای یگانه را که شما را جز او خدایی نیست پرستش کنید، او خدایی است که شما را از زمین بیافرید و برای عمارت و آباد ساختن آن برگماشت، پس شما از خدای خود آمرزش طلبید و به درگاه او توبه کنید که البته خدای من (به همه) نزدیک است و شنونده و اجابت کننده است.'),
(1535, 11, 62, 'قوم گفتند: ای صالح، تو پیش از آنکه دعوی نبوت کنی در میان ما مورد امیدواری بودی، آیا می‌خواهی ما را از پرستش خدایانی که پدران ما می‌پرستیدند منع کنی؟ ما به دعوی نبوتت سخت بدگمان و بی‌عقیده هستیم.'),
(1536, 11, 63, 'صالح گفت: آیا رأی شما چیست اگر من بر دعوی خود معجز و دلیلی از طرف خدا در دست داشته باشم و او مرا از نزد خود رحمتی (مقام نبوت) داده باشد، اگر باز هم فرمان او نبرم در این صورت مرا در برابر (قهر) خدا که یاری تواند داد؟ که شما بر من جز ضرر و زیان چیزی نخواهید افزود.'),
(1537, 11, 64, 'و ای قوم، این ناقه آیت خداست و معجز برای شماست، او را به حال خود آزاد گذارید تا در زمین خدا چرا کند و قصد آزار او مکنید و گر نه شما را به زودی عذابی در خواهد گرفت.'),
(1538, 11, 65, 'قوم ناقه را پی کردند، صالح هم گفت که تا سه روز دیگر در منازل خود از زندگی تمتّع برید که (سپس همه هلاک خواهید شد و) این وعده البته دروغ نیست.'),
(1539, 11, 66, 'چون وقت فرمان (قهر) ما فرا رسید تنها صالح و مؤمنان به او را به رحمت خاص خود از بلا و از خواری و رسوایی آن روز نجات دادیم، که خدای تو مقتدر و تواناست.'),
(1540, 11, 67, 'و آن گاه ستمکاران را (شب) صیحه عذاب آسمانی بگرفت و صبحگاه در دیارشان بی‌حس و حرکت و خاموش ابدی شدند.'),
(1541, 11, 68, 'چنان هلاک شدند که گویی هرگز در آن دیار نبودند. آگاه باشید که قوم ثمود، به خدای خود کافر شدند، آگاه باشید که قوم ثمود دور از رحمت ابدی خدا گردیدند.'),
(1542, 11, 69, 'و محققا فرستادگان ما (فرشتگان آسمان) بر ابراهیم (خلیل) بشارت آورده و او را سلام گفته و او پاسخ سلام بداد، آن گاه ابراهیم بی‌درنگ از گوشت گوساله کبابی آورد.'),
(1543, 11, 70, 'و چون دید که آنان به طعام دست دراز نمی‌کنند آنان را بیگانه شمرد و در حال از آنها دلش متوحش و بیمناک گردید، آنان گفتند: مترس که ما فرستاده خدا به قوم لوط می‌باشیم.'),
(1544, 11, 71, 'و زن ابراهیم ایستاده بود که (از فرط شوق) متبسّم (یا حائض) گردید، پس ما آن زن را به فرزندی به نام اسحاق و سپس (فرزندش) یعقوب بشارت دادیم.'),
(1545, 11, 72, 'زن گفت: ای وای! آیا می‌شود من بزایم با آنکه پیری سالخورده‌ام و شوهرم نیز مردی پیر و فرتوت است؟ این چیزی بسیار شگفت‌انگیز است.'),
(1546, 11, 73, 'فرشتگان با او گفتند: آیا از کار خدا عجب داری؟ (عجب مدار) که رحمت و برکات خدا مخصوص شما اهل بیت است، زیرا خدا بسیار ستوده صفات و بزرگوار است.'),
(1547, 11, 74, 'چون ابراهیم را وحشت از دل برفت و بشارت (فرزند) بیامد در آن حال برای خلاص قوم لوط با ما به گفتگو و التماس در آمد.'),
(1548, 11, 75, 'که همانا ابراهیم بسیار بردبار بود و بسیار دعا و تضرع داشت و بسیار مغفرت و آمرزش می‌طلبید.'),
(1549, 11, 76, '(خطاب شد) ای ابراهیم از این خواهش درگذر که هنگام حکم (قهر) الهی بر این قوم فرا رسیده و بر آنها عذابی که حتمی است و بازگشت ندارد خواهد رسید.'),
(1550, 11, 77, 'و چون فرستادگان ما (فرشتگان قهر) به لوط وارد شدند به آمدن آنان پریشان خاطر و دلتنگ شد و گفت: این روز بسیار سختی است.'),
(1551, 11, 78, 'و (چون فرشتگان به صورت جوانان زیبا به خانه لوط درآمدند) قوم لوط به قصد عمل زشتی که در آن سابقه داشتند به سرعت به درگاه او وارد شدند، لوط به آنها گفت: این دختران من (جنس زنان امتم که به منزله دختران منند) برای شما پاکیزه و نیکوترند، از خدا بترسید و مرا نزد مهمانانم خوار و سرشکسته مکنید، آیا در میان شما یک مرد رشید نیست؟'),
(1552, 11, 79, 'قوم لوط گفتند: تو خود می‌دانی که ما را رغبت و میلی به آن دختران نیست و تو به خوبی می‌دانی که مطلب ما چیست.'),
(1553, 11, 80, 'لوط گفت: ای کاش مرا بر منع شما اقتداری بود یا (حالی که بر منعتان قدرت ندارم از شر شما) به رکن محکمی پناه می‌بردم.'),
(1554, 11, 81, 'فرشتگان به لوط گفتند: ما رسولان پروردگار توایم و هرگز دست آزار قوم به تو نرسد، تو اهل بیت خود را در پاسی از شب از این دیار بیرون بر و از شما هیچ کس نباید وابماند جز آن زن (کافر) تو که آن هم با قوم تو باید هلاک شود. همانا وعده عذاب صبحگاه است، آیا صبح نزدیک نیست؟!'),
(1555, 11, 82, 'چون فرمان (قهر) ما در رسید دیار آن قوم نابکار را ویران و زیر و زبر ساختیم و بر سر آنها مرتب سنگ گل (هلاکت کننده) فرو ریختیم.'),
(1556, 11, 83, 'که آن سنگها از امر خدا نشاندار و معین بود و البته چنین هلاکتی از ظالمان عالم دور نخواهد بود.'),
(1557, 11, 84, 'و ما به سوی اهل مدین برادرشان شعیب را (به رسالت فرستادیم)، وی گفت: ای قوم، خدای یکتا را که شما را جز او خدایی نیست پرستش کنید و در کیل و وزن کم‌فروشی مکنید، من خیر شما را در آن می‌بینم (که با همه عدل و انصاف کنید) و (اگر ظلم کنید) من می‌ترسم از روزی که عذابی سخت شما را فراگیرد.'),
(1558, 11, 85, 'ای قوم، در سنجش وزن و کیل اجناس عدالت کنید و آن را تمام دهید و به مردم کم نفروشید و در زمین به فساد برنخیزید.'),
(1559, 11, 86, '(و بدانید که) آنچه خدا بر شما باقی گذارد برای شما بهتر است (از آن زیادتی که به خیانت و کم و گران فروشی به دست می‌آورید) اگر واقعا به خدا ایمان دارید. و من نگهبان شما (از عذاب خدا) نیستم.'),
(1560, 11, 87, 'قوم گفتند: ای شعیب، آیا این نماز تو، تو را مأمور می‌کند که ما دست از پرستش خدایان پدرانمان و از تصرف در اموال به دلخواه خودمان برداریم؟ (آفرین) تو بسیار مرد بردبار خردمندی هستی!'),
(1561, 11, 88, 'شعیب گفت: ای قوم، رأی و نظریه شما چیست؟ آیا اگر مرا از جانب پروردگارم حجت روشن و دلیل قاطع باشد و از خود بر من رزق نیکو رسانده باشد (باز اطاعت او نکنم)؟ و (بدانید که) غرض من در آنچه شما را نهی می‌کنم ضدّیت و مخالفت با شما نیست بلکه تا بتوانم تنها مقصودم اصلاح امر شماست و از خدا در هر کار توفیق می‌طلبم و بر او توکل کرده و به درگاه او انابه و بازگشت دارم.'),
(1562, 11, 89, 'شعیب باز گفت: ای قوم، ضدّیت با من شما را بر آن وادار نکند که بر شما هم بلایی مانند قوم نوح یا قوم هود یا صالح از جانب خدا نازل شود و (به خصوص که) قوم لوط دورانشان دور از شما نیست.'),
(1563, 11, 90, 'و از خدای خود آمرزش طلبید و به درگاهش توبه و انابه کنید که خدای من بسیار مهربان و دوستدار بندگان است.'),
(1564, 11, 91, 'قوم پاسخ دادند که ای شعیب، ما بسیاری از آنچه می‌گویی نمی‌فهمیم و تو را در میان خود شخصی بی‌ارزش و ناتوان می‌بینیم و اگر (ملاحظه) طایفه تو نبود سنگسارت می‌کردیم، که تو را نزد ما عزت و احترامی نیست.'),
(1565, 11, 92, 'شعیب گفت: ای قوم، آیا طایفه من عزتش نزد شما بیش از خداست؟ و خدا را به کلّی فراموش کردید؟ همانا خدای من به هر چه شما می‌کنید کاملا آگاه است.'),
(1566, 11, 93, 'ای قوم، شما هر کار می‌توانید انجام دهید من هم هر چه موظفم خواهم کرد، به زودی شما خواهید دانست که عذاب خواری و رسوایی بر کدام یک از من و شما می‌آید و دروغگو کیست، پس شما منتظر (نزول عذاب خدا) باشید که من هم (بر خود لطف و بر شما قهر حق را) منتظرم.'),
(1567, 11, 94, 'و هنگامی که حکم (قهر) ما فرا رسید، ما شعیب و کسانی را که با او ایمان آوردند به لطف و مرحمت خود نجات دادیم، و ستمکاران را صیحه عذاب فرا گرفت که صبحگاه همه در دیار خود هلاک شدند.'),
(1568, 11, 95, 'چنان هلاک شدند که گویی هرگز در آن دیار نبودند، آگاه باشید که اهل مدین هم مانند کافران قوم ثمود از رحمت خدا دور شدند.'),
(1569, 11, 96, 'آن گاه موسی عمران را با آیات خود و حجت روشن (به رسالت) فرستادیم.'),
(1570, 11, 97, 'به سوی فرعون و اشراف قومش، و آنان پیرو حکم و امر فرعون شدند با آنکه هیچ هدایت و رشدی در اطاعت امر فرعون نبود.'),
(1571, 11, 98, 'فرعون پیروان خود را در قیامت پیشوایی کند و آنها را (با خود) به آتش دوزخ در افکند، که بسیار بد ورودگاه و منزلگاهی است.'),
(1572, 11, 99, 'و فرعونیان را در این جهان بد لعنتی در پی است و آن بد عطا و بخششی است.'),
(1573, 11, 100, '(ای رسول ما) این بعضی از اخبار دیار ستمکاران است که بر تو حکایت می‌کنیم که برخی از آن دیار هنوز معمور است و برخی دیگر شهرها داس مرگ اهلش را درو کرد.'),
(1574, 11, 101, 'و آنها که به هلاکت رسیدند نه ما بر آنها بلکه خود بر خویشتن ستم کردند و هنگامی که امر (قهر) خدای تو بر هلاکشان در رسید همه خدایان باطلی که غیر خدا می‌پرستیدند هیچ دفع هلاکت از آنان ننمودند و جز بر خسران و تباهی آنها نیفزودند.'),
(1575, 11, 102, 'و این گونه است مؤاخذه پروردگارت هرگاه بخواهد دیار ستمکاران را ویران کند، که انتقام و مؤاخذه خدا بسیار دردناک و شدید است.'),
(1576, 11, 103, 'همانا در این هلاک بدکاران آیت و عبرتی است بر آن کس که از عذاب روز محشر بترسد که روز محشر روزی است که همه خلق را در آن جمع آورند و آن روز، روز حضور همگان است.'),
(1577, 11, 104, 'و ما آن روز را به تأخیر نیفکنیم جز تا وقتی که (در علم ما) معین است.'),
(1578, 11, 105, 'در آن روز که فرا رسد هیچ کس جز به فرمان خدا سخن نگوید، پس برخی شقی و بد روزگارند و بعضی سعید و خوشوقت.'),
(1579, 11, 106, 'اما اهل شقاوت همه در آتش دوزخند در حالی که سخت آه و ناله حسرت و عربده می‌کشند.'),
(1580, 11, 107, 'آنها در آتش دوزخ تا آسمان و زمین باقی است مخلّدند مگر آنچه مشیّت پروردگار تو باشد، که البته خدا هر چه خواهد می‌کند.'),
(1581, 11, 108, 'و اما اهل سعادت هم تمام در بهشت ابد تا آسمان و زمین باقی است مخلّدند مگر آنچه مشیّت پروردگار تو باشد، که عطایی ابدی و نامقطوع است.'),
(1582, 11, 109, 'پس تو شک نداشته باش که اینان عبادت بتها را جز به پیروی و تقلید (جاهلانه) پدرانشان نمی‌کنند و ما آنچه سهم (عذاب) این مشرکان است بی کم و کاست خواهیم داد.'),
(1583, 11, 110, 'و محققا ما کتاب تورات را برای موسی فرستادیم آن‌گاه در آن اختلاف کردند، و اگر کلمه سابقه الهی و مشیّت ازلی خدا (بر تأخیر عذاب خلق تا قیامت) نبود همانا میان آنها حکم (به عذاب) می‌شد. و این مردم از این گونه وعد و وعیدها همیشه بدگمان و در شکّند و بر خود ریب و شبهه می‌کنند.'),
(1584, 11, 111, 'و محققا خدای تو همه خلق را به جزای همه اعمالشان می‌رساند، که او بر همه کردار خلق آگاه است.'),
(1585, 11, 112, 'پس تو چنان که مأموری استقامت و پایداری کن و کسی که با همراهی تو به خدا رجوع کند نیز پایدار باشد، و (هیچ از حدود الهی) تجاوز نکنید که خدا به هر چه شما می‌کنید بصیر و داناست.'),
(1586, 11, 113, 'و (شما مؤمنان) هرگز نباید با ظالمان همدست و دوست و بدانها دلگرم باشید و گر نه آتش (کیفر آنان) در شما هم خواهد گرفت و در آن حال جز خدا هیچ دوستی نخواهید داشت و هرگز کسی یاری شما نخواهد کرد.'),
(1587, 11, 114, 'و نماز را در دو طرف (اول و آخر) روز به پا دار و نیز در ساعات آغازین شب، که البته حسنات و نکوکاریها، سیّئات و بدکاریها را نابود می‌سازد، این (نماز یا این سخن که حسنات شما سیّئات را محو می‌کند) یادآوریی است برای اهل ذکر و پندی بر مردم آگاه است.'),
(1588, 11, 115, 'و صبر کن که خدا هرگز اجر نیکوکاران را ضایع نگذارد.'),
(1589, 11, 116, 'پس چرا در امم گذشته مردمی با عقل و ایمان وجود نداشت که (خلق را) از فساد و اعمال زشت نهی کنند مگر عده قلیلی که نجاتشان دادیم، و ستمکاران از پی تعیّش به نعمتهای دنیوی رفتند و مردمی فاسق بدکار بودند.'),
(1590, 11, 117, 'و خدا بر آن نیست که هیچ قومی و هیچ اهل دیاری را در صورتی که آنها مصلح و نیکوکار باشند به ظلم هلاک کند.'),
(1591, 11, 118, 'و اگر خدای تو می‌خواست همه (ملل و مذاهب) خلق را یک امت می‌گردانید و لیکن دائم با هم در اختلاف خواهند بود.'),
(1592, 11, 119, 'مگر آن کس که خدای تو بر او رحم آورد و برای همین آفریدشان، و کلمه (قهر) خدای تو به حتم و لزوم پیوست که فرموده دوزخ را از کافران جن و انس پر خواهم ساخت.'),
(1593, 11, 120, 'و ما از همه این حکایات و اخبار انبیا بر تو بیان می‌کنیم از آنچه که قلب تو را به آن قوی و استوار گردانیم، و در این شرح حال رسولان آنچه حق و صواب است بر تو آمده و اهل ایمان را نیز پند و عبرت و تذکر باشد.'),
(1594, 11, 121, 'و با آنان که ایمان نمی‌آورند بگو که شما هر چه بتوانید به زشتکاری و معصیت خدا بپردازید ما هم به کار اطاعت مشغول خواهیم بود.'),
(1595, 11, 122, 'و شما (جزای کردار خود را) منتظر باشید ما هم (پاداش عمل خود را از حق) منتظریم.'),
(1596, 11, 123, 'و هر چه در آسمانها و زمین پنهان است همه برای خداست و امور عالم همه به خدا بازگردانده می‌شود (و به دست قدرت اوست)، او را بپرست و بر او توکل کن، و پروردگار تو از آنچه می‌کنید غافل نیست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الر (این حروف مقطعه رموز خدا و رسول است) این است آیات کتاب الهی که حقایق را آشکار می‌سازد.'),
(1598, 12, 2, 'این قرآن مجید را ما به عربی فصیح فرستادیم، باشد که شما (به تعلیمات او) عقل و هوش یابید.'),
(1599, 12, 3, 'ما به بهترین روش به وحی این قرآن بر تو حکایت می‌کنیم و تو پیش از این وحی هیچ از آن آگاه نبودی.'),
(1600, 12, 4, '(اکنون متذکر شو خواب یوسف را) آن‌گاه که یوسف به پدر خود گفت: ای پدر در عالم رؤیا دیدم که یازده ستاره و خورشید و ماه مرا سجده می‌کردند.'),
(1601, 12, 5, 'یعقوب گفت: ای فرزند عزیز، زنهار خواب خود را بر برادران حکایت مکن که (به اغوای شیطان) بر تو مکر (و حسد) خواهند ورزید، زیرا دشمنی شیطان بر آدمی بسیار آشکار است.'),
(1602, 12, 6, 'و این (تعبیر خواب تو) چنین است که خدا تو را برگزیند و از علم تأویل خوابها بیاموزد و نعمت و لطفش را در حق تو و آل یعقوب مانند پدرانت ابراهیم و اسحاق تمام گرداند، که خدای تو دانا و حکیم است.'),
(1603, 12, 7, 'همانا در حکایت یوسف و برادرانش برای دانش طلبان و اهل تحقیق عبرت و حکمت بسیار مندرج است.'),
(1604, 12, 8, '(پس حکایت را بر امت بگو) هنگامی که برادران یوسف گفتند ما با آنکه چندین برادر نیرومندیم پدر چنان دلبسته یوسف و برادر اوست که آنها را تنها بیش از همه ما برادران دوست می‌دارد، همانا ضلالت پدرمان (در حب یوسف) نیک پدیدار است.'),
(1605, 12, 9, 'باید یوسف را بکشید یا در دیاری (دور از پدر) بیفکنید تا روی پدر یک جهت به طرف خودتان باشد و بعد از این عمل (توبه کرده و) مردمی صالح و درستکار باشید.'),
(1606, 12, 10, 'یکی از برادران یوسف اظهار داشت که اگر ناچار سوء قصدی دارید البته باید از کشتن وی صرف نظر کنید ولی او را به چاهی درافکنید تا کاروانی او را برگیرد (و با خود به دیار دور برد).'),
(1607, 12, 11, '(بعد از این رأی و تصمیم نزد پدر رفته و) گفتند: ای پدر، چرا تو بر یوسف از ما ایمن نیستی در صورتی که ما برادران همه خیرخواه یوسفیم؟'),
(1608, 12, 12, 'فردا او را با ما فرست که در چمن و مراتع بگردد و بازی کند و البته ما همه نگهبان اوییم.'),
(1609, 12, 13, 'یعقوب گفت: من از آن دلتنگ می‌شوم که او را ببرید و ترسان و پریشان خاطرم که از او غفلت کنید و طعمه گرگان شود.'),
(1610, 12, 14, 'برادران گفتند اگر با آنکه ما چند مرد نیرومند به همراه اوییم باز گرگ او را بخورد پس ما بسیار مردم ضعیف زیانکاری خواهیم بود.'),
(1611, 12, 15, 'چون او را به صحرا بردند و بر این عزم متّفق شدند که او را به قعر چاه درافکنند (چنین کردند) و ما به او وحی نمودیم که البته تو روزی برادران را به کار بدشان آگاه می‌سازی و آنها تو را نشناخته و درک مقام تو نمی‌کنند.'),
(1612, 12, 16, 'و برادران شبانگاه با چشم گریان نزد پدر بازگشتند.'),
(1613, 12, 17, 'گفتند: ای پدر، قصه این است که ما به صحرا رفته و سرگرم مسابقه بودیم و یوسف را بر سر متاع خود گذاردیم و او را گرگ طعمه خود ساخت، و هر چند ما راست بگوییم تو باز از ما باور نخواهی کرد.'),
(1614, 12, 18, 'و پیراهن یوسف را آلوده به خون دروغ نمودند (و نزد پدر آوردند) یعقوب گفت: بلکه امری (زشت قبیح) را نفس مکّار در نظر شما بسیار زیبا جلوه داده، در هر صورت صبر جمیل کنم، که بر رفع این بلیّه که شما اظهار می‌دارید بس خداست که مرا یاری تواند کرد.'),
(1615, 12, 19, 'باری کاروانی آنجا رسید و سقّای قافله را برای آب فرستادند، دلو را به چاه فرستاد (همین که از آن چاه برآورد) گفت: به به از این بشارت و خوشبختی که رخ داده، این پسری است (که نصیب ما شده است). و او را پنهان داشتند که سرمایه تجارت کنند و خدا به آنچه می‌کردند آگاه بود.'),
(1616, 12, 20, 'و او را به بهایی اندک و درهمی ناچیز فروختند و در او زهد و بی‌رغبتی نمودند.'),
(1617, 12, 21, 'و عزیز مصر که او را خریداری کرد به زن خویش سفارش غلام را نمود که مقامش بسیار گرامی دار که این غلام امید است به ما نفع بسیار بخشد یا او را به فرزندی برگیریم. و ما این چنین یوسف را به تمکین و اقتدار رسانیدیم و برای آنکه او را از علم تعبیر خوابها بیاموزیم، و خدا بر کار خود غالب است ولی بسیاری مردم (بر این حقیقت) آگه نیستند.'),
(1618, 12, 22, 'و چون یوسف به سن رشد و کمال رسید او را مقام حکمت و نبوت (یا مسند حکمفرمایی) و مقام دانش عطا کردیم و این چنین ما نکوکاران عالم را پاداش می‌بخشیم.'),
(1619, 12, 23, 'و بانوی خانه به میل نفس خود با او بنای مراوده گذاشت و (روزی) درها را بست و یوسف را به خود دعوت کرد و اشاره کرد که من برای تو آماده‌ام، یوسف جواب داد: به خدا پناه می‌برم، او خدای من است، مرا مقامی منزه و نیکو عطا کرده (چگونه خود را به ستم و عصیان آلوده کنم)، که هرگز ستمکاران رستگار نمی‌گردند.'),
(1620, 12, 24, 'آن زن باز در وصل او اصرار و اهتمام کرد و یوسف هم اگر لطف خاص خدا و برهان روشن حق را ندیده بود (به میل طبیعی) در وصل آن زن اهتمام کردی، ولی ما این چنین کردیم تا قصد بد و عمل زشت را از او بگردانیم که همانا او از بندگان برگزیده ما بود.'),
(1621, 12, 25, 'و هر دو (برای گریختن) به جانب در شتافتند و زن پیراهن یوسف را از پشت بدرید و در آن حال آقای آن زن (یعنی شوهرش) را بر در منزل یافتند، زن گفت: جزای آن که با اهل تو قصد بد کند جز آنکه یا به زندانش برند یا به عقوبت سخت کیفر کنند چه خواهد بود؟'),
(1622, 12, 26, 'یوسف جواب داد: این زن خود (با وجود انکار من) با من قصد مراوده کرد. و بر صدق دعویش شاهدی از بستگان زن گواهی داد گفت: اگر پیراهن یوسف از پیش دریده، زن راستگو و یوسف از دروغگویان است.'),
(1623, 12, 27, 'و اگر پیراهن او از پشت سر دریده، زن دروغگو و یوسف از راستگویان است.'),
(1624, 12, 28, 'چون شوهر دید که پیراهن از پشت سر دریده است گفت: این از مکر شما زنان است، که مکر و حیله شما زنان بسیار بزرگ و حیرت‌انگیز است.'),
(1625, 12, 29, 'ای یوسف، از این درگذر (یعنی قضیه را از همه پنهان دار) و (زن را گفت) تو هم از گناه خود توبه کن که سخت از خطاکاران بوده‌ای.'),
(1626, 12, 30, 'و زنانی در شهر مصر گفتند: زن عزیز مصر قصد مراوده با غلام خویش داشته و حب او وی را شیفته و فریفته خود ساخته و ما او را (از فرط محبت) کاملا در ضلالت می‌بینیم.'),
(1627, 12, 31, 'چون (زلیخا) ملامت زنان مصری را درباره خود شنید فرستاد و از آنها دعوت کرد و (مجلسی بیاراست و) به احترام هر یک بالش و تکیه‌گاهی بگسترد و به دست هر یک کاردی (و ترنجی) داد و (آن گاه با زیب و زیور یوسف را بیاراست و) به او گفت که به مجلس این زنان درآ، چون زنان مصری یوسف را دیدند بس بزرگش یافتند و دستهای خود (به جای ترنج) بریدند و گفتند حاش للّه که این پسر نه آدمی است بلکه فرشته بزرگ حسن و زیبایی است.'),
(1628, 12, 32, 'گفت: این است غلامی که مرا در محبتش ملامت کردید! آری من خود از وی تقاضای مراوده کردم و او عفت ورزید و اگر از این پس هم خواهش مرا رد کند البته زندانی شود و از خوارشدگان گردد.'),
(1629, 12, 33, 'یوسف گفت: ای خدا، مرا رنج زندان خوشتر از این کار زشتی است که اینان از من تقاضا دارند و اگر تو حیله این زنان را از من دفع نفرمایی به آنها میل کرده و از اهل جهل (و شقاوت) گردم.'),
(1630, 12, 34, 'خدایش هم دعای او را مستجاب کرده و مکر و دسایس آن زنان را از او بگردانید، که خداوند شنوا و داناست.'),
(1631, 12, 35, 'و با آنکه دلایل روشن (پاکدامنی و عصمت یوسف) را دیدند باز چنین صلاح دانستند که وی را چندی زندانی کنند.'),
(1632, 12, 36, 'و با یوسف دو جوان دیگر هم (از ندیمان و خاصان شاه) زندانی شدند. یکی از آنها گفت: من در خواب دیدمی که انگور برای شراب می‌افشرم، و دیگری گفت: من در خواب دیدمی که بر بالای سر خود طبق نانی می‌برم و مرغان هوا از آن به منقار می‌خورند، (یوسفا) ما را از تعبیر آن آگاه کن، که تو را از نیکوکاران (و دانشمندان عالم) می‌بینیم.'),
(1633, 12, 37, 'یوسف در پاسخ آنها گفت: من شما را پیش از آنکه طعام آید و تناول کنید به تعبیر خوابتان آگاه می‌سازم، که این علم از چیزهایی است که خدای به من آموخته است، زیرا که من آیین گروهی را که به خدا بی‌ایمان و به آخرت کافرند ترک گفتم.'),
(1634, 12, 38, 'و از آیین پدرانم ابراهیم (خلیل) و اسحاق و یعقوب (که دین توحید و خداپرستی است) پیروی کردم، در آیین ما هرگز نباید چیزی را با خدا شریک گردانیم، این از فضل و عطای خداست بر ما و بر همه مردم لیکن اکثر مردمان شکر به جا نمی‌آورند.'),
(1635, 12, 39, 'ای دو رفیق زندان من، آیا خدایان متفرق (بی‌حقیقت مانند بتان و فراعنه و غیره) بهترند یا خدای یکتای قاهر و غالب؟'),
(1636, 12, 40, '(و بدانید که) آنچه غیر از خدا می‌پرستید، جز اسمائی (بی‌حقیقت و الفاظی بی‌معنی) نیست که شما خود و پدرانتان نامیده (و ساخته) اید، خدا هیچ حجتی بر آن نفرستاده، تنها حکمفرمای عالم وجود خداست، امر فرموده که جز آن ذات پاک یکتا کسی را نپرستید، این توحید آیین محکم است لیکن اکثر مردم بر این حقیقت آگه نیستند.'),
(1637, 12, 41, 'ای دو رفیق زندان من، اما یکی از شما ساقی شراب شاه خواهد شد و اما آن دیگری به دار آویخته شود و مرغان مغز سر او را بخورند. در قضای الهی راجع به امری که سؤال کردید چنین حکم شده است.'),
(1638, 12, 42, 'آن گاه از رفیقی که اهل نجاتش یافت درخواست کرد که مرا نزد خواجه خود یاد کن (باشد که چون بی‌تقصیرم بیند از زندانم برهاند) اما شیطان از خاطر آن یار زندانی برد که یوسف را نزد خواجه‌اش یاد کند، بدین سبب در زندان چندین سال محبوس بماند.'),
(1639, 12, 43, 'و پادشاه مصر (با ملازمان و دانشمندان دربار خود) گفت: من خوابی دیدم که هفت گاو فربه را هفت گاو لاغر می‌خورند و هفت خوشه سبز دیدم و هفت خوشه خشک (که خوشه‌های سبز را نابود کردند)، ای بزرگان ملک مرا به تعبیر این خوابم اگر علم خواب می‌دانید آگاه گردانید.'),
(1640, 12, 44, 'آنها گفتند: خوابهای پریشان است و ما تعبیر خوابهای پریشان نمی‌دانیم.'),
(1641, 12, 45, 'و آن (رفیق زندانی یوسف) که نجات یافته بود و بعد از چندین سال به یاد یوسف افتاد گفت: من شما را به تعبیر این خواب آگاه می‌سازم، مرا (نزد یوسف زندانی) فرستید.'),
(1642, 12, 46, '(در زندان رفت و گفت) یوسفا! ای که هر چه گویی همه راست گویی، ما را به تعبیر این خواب که هفت گاو فربه را هفت گاو لاغر می‌خوردند و هفت خوشه سبز و هفت خوشه خشک (که آنها را نابود ساختند) آگاه گردان، باشد که نزد مردم بازگردم و (شاه و دیگران همه تعبیر خواب و مقام تو را) بدانند.'),
(1643, 12, 47, 'یوسف گفت: باید هفت سال متوالی زراعت کنید و هر خرمن را که درو کنید جز کمی که قوت خود می‌سازید همه را با خوشه در انبار ذخیره کنید.'),
(1644, 12, 48, 'که چون این هفت سال بگذرد هفت سال قحطی پیش آید که ذخیره شما را بخورند جز اندکی که (برای تخم کاشتن) در انبار نگه دارید.'),
(1645, 12, 49, 'آن گاه بعد از سنوات قحط و شدت مجاعه باز سالی آید که مردم در آن به آسایش و وسعت و فراوانی نعمت می‌رسند.'),
(1646, 12, 50, 'شاه گفت: زود او را نزد من بیاورید. چون فرستاده شاه نزد یوسف آمد یوسف به او گفت: باز گرد و شاه را بپرس چه شد که زنان مصری همه دست خود بریدند؟ آری خدای من به مکر آنان (و بی‌گناهی من) آگاه است.'),
(1647, 12, 51, 'شاه (با زنان مصر) گفت: حقیقت حال خود را که خواهان مراوده با یوسف بودید بگویید، همه گفتند: حاش للّه که ما از یوسف هیچ بدی ندیدیم. در این حال زن عزیز مصر اظهار کرد که الآن حقیقت آشکار شد، من با یوسف عزم مراوده داشتم و او البته از راستگویان است.'),
(1648, 12, 52, '(یوسف در ادامه سخن خود به فرستاده شاه گفت) من این کشف حال برای آن خواستم تا عزیز مصر بداند که من هرگز در نهانی به او خیانت نکردم و بداند که خدا هرگز مکر و خدعه خیانتکاران را به مقصود نمی‌رساند.'),
(1649, 12, 53, 'و من (خودستایی نکرده و) نفس خویش را از عیب و تقصیر مبرّا نمی‌دانم، زیرا نفس امّاره انسان را به کارهای زشت و ناروا سخت وا می‌دارد جز آنکه خدای من رحم کند، که خدای من بسیار آمرزنده و مهربان است.'),
(1650, 12, 54, 'شاه گفت: او را نزد من آرید تا او را از خاصان خود گردانم. چون با او هرگونه سخن به میان آورد به او گفت: تو امروز نزد ما صاحب منزلت و امین هستی.'),
(1651, 12, 55, '(یوسف به شاه) گفت: در این صورت مرا به خزانه‌داری مملکت منصوب دار که من در حفظ دارایی و مصارف آن دانا و بصیرم.'),
(1652, 12, 56, 'و این چنین ما یوسف را در زمین (مصر) بدین منزلت رسانیدیم که هر جا خواهد جای گزیند و فرمان براند، که هر کس را ما بخواهیم به لطف خاص خود مخصوص می‌گردانیم و اجر هیچ کس از نیکوکاران را (در دنیا) ضایع نمی‌گذاریم.'),
(1653, 12, 57, 'و البته اجر عالم آخرت برای اهل ایمان و مردم پرهیزکار بسیار بهتر (از اجر و مقام دنیوی) است.'),
(1654, 12, 58, 'و برادران یوسف (که در کنعان به قحطی مبتلا شدند چهل سال بعد از فروختن یوسف، به مصر) نزد وی آمدند در حالی که او برادران را شناخت ولی آنها وی را نشناختند.'),
(1655, 12, 59, '(برادران از یوسف در مقابل متاعی که آورده بودند طعام خواستند) و یوسف چون بار غلّه آنها را بست گفت: می‌خواهم در سفر دیگر برادر پدری خود (بنیامین) را نزد من بیاورید، نمی‌بینید که من پیمانه را تمام می‌دهم و بهترین میزبانم؟'),
(1656, 12, 60, 'و اگر آن برادر را همران نیاورید نزد من پیمانه خواربار نخواهید داشت و نزدیک من نشوید.'),
(1657, 12, 61, 'برادران گفتند: تا بتوانیم می‌کوشیم که پدرش را راضی کنیم و حتما چنین خواهیم کرد.'),
(1658, 12, 62, 'آن گاه یوسف به غلامانش گفت: متاع این کنعانیان را در میان بارهاشان بگذارید که چون نزد کسان خود رفته (و متاع خود را دیدند) آن را بازشناسند، شاید که (این احسان موجب شود) باز نزد من مراجعه کنند.'),
(1659, 12, 63, 'چون برادران نزد پدر بازگشتند گفتند: ای پدر (با همه کرم و سخای خدیو مصر) غلّه (بسیار) به ما عطا نشد (و وعده داد که اگر برادر خود را همراه آورید به شما گندم فراوان خواهم داد) پس برادرمان را با ما فرست تا مقدار کافی غله تهیه کنیم و البته ما کاملا نگهبان او خواهیم بود.'),
(1660, 12, 64, 'یعقوب گفت: آیا من همان قدر درباره این برادر به شما مطمئن و ایمن باشم که پیش از این درباره برادرش (یوسف) مطمئن بودم؟ البته خدا بهترین نگهبان و مهربانترین مهربانان است.'),
(1661, 12, 65, 'چون برادران بارها را گشوده و متاعشان را به خود رد شده یافتند گفتند: ای پدر، ما دیگر چه می‌خواهیم؟ این سرمایه ماست که به ما بازگردانده شده (با همین مال التجاره باز به مصر می‌رویم) و غله برای اهل بیت خود تهیه کرده و برادر خود را هم در کمال مراقبت حفظ می‌کنیم و بار شتری بر این قوت کم که اکنون آورده‌ایم می‌افزاییم.'),
(1662, 12, 66, 'یعقوب گفت: تا شما برای من به خدا عهد و قسم یاد مکنید که او را برگردانید مگر آنکه (به قهر خدا) گرفتار و هلاک شوید من هرگز او را همراه شما نخواهم فرستاد. پس چون برادران عهد و قسم به خدا یاد کردند یعقوب گفت: خدا بر قول ما وکیل و گواه است (و او را فرستاد).'),
(1663, 12, 67, 'و گفت: ای پسران من (سفارش می‌کنم که چون به مصر برسید) همه از یک در وارد نشوید بلکه از درهای مختلف درآیید و (بدانید که) من در برابر (قضا و قدر) خدا هیچ سودی به حال شما نخواهم داشت، که فرمانروایی عالم جز خدا را نیست، بر او توکل کردم و باید همه صاحبان مقام توکل هم بر او اعتماد کنند.'),
(1664, 12, 68, 'چون آنها به ملک مصر به طریقی که پدر دستور داده بود وارد شدند البته این کار هیچ سودی برایشان در برابر (قضا و قدر) خدا نداشت جز آنکه در دل یعقوب غرضی بود که (از چشم بد گزندی نبینند) ادا نمود، و او بسیار دانشمند بود، زیرا ما او را (به وحی خود) علم آموختیم و لیکن اکثر مردم نمی‌دانند.'),
(1665, 12, 69, 'و چون برادران بر یوسف وارد شدند او برادر خود را در کنار خویش جای داد و به او اظهار داشت که همانا من برادر توام و دیگر بر آنچه برادران (بر یوسف) می‌کردند محزون مباش.'),
(1666, 12, 70, 'چون بار آن قافله را مهیا ساخت جام (زرین شاه) را در رحل برادر نهاد و آن‌گاه از غلامان منادیی ندا کرد که ای اهل قافله، شما بی‌شک دزدید.'),
(1667, 12, 71, 'آنها رو به غلامان کرده گفتند که مگر چه چیز از شما مفقود شده است؟'),
(1668, 12, 72, 'غلامان گفتند: جام شاه ناپیداست و من (که رئیس انبارم) یک بار شتر طعام ضمانت کنم بر آن کس که جام را پیدا کرده بیاورد.'),
(1669, 12, 73, 'برادران گفتند: به خدا سوگند که شما به خوبی حال ما را دانسته و شناخته‌اید که برای فساد در این سرزمین نیامده و دزد نبوده‌ایم.'),
(1670, 12, 74, 'غلامان گفتند: اگر کشف شد که دروغ می‌گویید کیفر آن چیست؟'),
(1671, 12, 75, 'گفتند: جزاء آن کس که این جام در رحل او یافت شود آن است که هم او را به بندگی برگیرند، که ما (دزدان و) ستمکاران را چنین به کیفر می‌رسانیم.'),
(1672, 12, 76, 'پس (یوسف یا مأمور او) شروع در تحقیق از بارهای ایشان پیش از بار برادر کرد، آخر آن مشربه را از بار برادر خود (بنیامین) بیرون آورد. این تدبیر را ما به یوسف آموختیم، که در آیین ملک این نبود که بتوان آن برادر را به گرو بگیرد، جز آنکه خدا بخواهد (و دستوری از طریق وحی به یوسف بیاموزد. و ما که خدای جهانیم) هر کس را بخواهیم به مراتب بلند می‌رسانیم و (تا مردم بدانند که) فوق هر دانشمندی دانشمندتری وجود دارد.'),
(1673, 12, 77, 'برادران گفتند که اگر این دزدی کند (بعید نیست که) برادرش (یوسف) نیز از این پیش دزدی کرد. یوسف باز قضیه را در دل پنهان کرد و به آنها اظهار نکرد (و در دل) گفت: شما بسیار مردم بدتری هستید و خدا به حقیقت آنچه نسبت می‌دهید آگاه‌تر است.'),
(1674, 12, 78, 'برادران گفتند: ای عزیز مصر، او را پدر پیری است (که به او علاقه شدیدی دارد، لطفی کن و) یکی از ما را به جای او نگاه دار، که تو به چشم دل ما از نیکان عالمی.'),
(1675, 12, 79, 'یوسف گفت: معاذ اللّه که ما جز آن که متاع خود را نزد او یافته‌ایم دیگری را بگیریم، که اگر چنین کنیم بسیار مردم ستمکاری هستیم.'),
(1676, 12, 80, 'چون برادران از او مأیوس شدند با خود خلوت کرده و در سخن، سرّ خود به میان آوردند، برادر بزرگ گفت: آیا نه این است که پدر از شما عهد و سوگند به نام خدا گرفته است و از این پیش هم درباره یوسف مقصر بودید؟ (ما دیگر با چه آبرو نزد پدر رویم؟) من که هرگز از این سرزمین برنخیزم تا پدرم اجازه دهد یا خدای عالم حکمی درباره من فرماید، که او بهترین حکمفرمایان است.'),
(1677, 12, 81, 'شما نزد پدر باز شوید و بگویید که فرزندت (بنیامین در مصر) سرقت کرد (و بدین جرم گرفتار شد) و ما جز بر آنچه دانستیم گواهی ندادیم و (لیکن حقیقت امر هر چه بود) ما حافظ اسرار غیب نبودیم.'),
(1678, 12, 82, 'و از مردم آن شهر و از آن قافله که ما با آن آمدیم حقیقت را جویا شو تا صدق دعوی ما کاملا بر تو معلوم گردد.'),
(1679, 12, 83, '(آنها نزد پدر آمده و قضیه را اظهار داشتند) یعقوب گفت (این قضیه هم مانند یوسف و گرگ حقیقت ندارد) بلکه چیزی از اوهام عالم نفس بر شما جلوه نموده، پس من باز هم راه صبر نیکو پیش گیرم، که امید است خدا همه ایشان را به من باز رساند، که او خدایی دانا و درستکار است.'),
(1680, 12, 84, 'آن گاه یعقوب (از شدت حزن) روی از آنها بگردانید و گفت: وا اسفا بر فراق یوسف! و از گریه غم چشمانش سفید شد و سوز هجران و داغ دل بنهفت.'),
(1681, 12, 85, 'فرزندانش (به ملامت) گفتند: به خدا سوگند که تو آن قدر دائم یوسف یوسف کنی تا از غصه فراقش مریض شوی و یا خود را به دست هلاک سپاری.'),
(1682, 12, 86, 'یعقوب گفت: من با خدا غم و درد دل خود گویم و از (لطف بی‌حساب) خدا چیزی دانم که شما نمی‌دانید.'),
(1683, 12, 87, 'ای فرزندان من، بروید (به ملک مصر) و از حال یوسف و برادرش تحقیق کرده و جویا شوید و از رحمت خدا نومید مباشید که هرگز جز کافران هیچ کس از رحمت خدا نومید نیست.'),
(1684, 12, 88, 'برادران (به امر پدر باز به مصر نزد یوسف آمده) چون بر او وارد شدند گفتند: ای عزیز مصر، ما با همه اهل بیت خود به فقر و قحطی و بیچارگی گرفتار شدیم و با متاعی ناچیز و بی‌قدر (حضور تو) آمدیم، پس بر قدر احسانت نسبت به ما بیفزا و از ما به صدقه دستگیری کن، که خدا صدقه بخشندگان را نیکو پاداش می‌دهد.'),
(1685, 12, 89, 'گفت: شما برادران یوسف در دوران جهل و نادانی فهمیدید که با یوسف و برادرش چه کردید؟'),
(1686, 12, 90, 'آنان گفتند: آیا تو همان یوسف هستی؟ پاسخ داد که آری من همان یوسفم و این برادر من (بنیامین) است، خدا بر ما منّت نهاد (و ما را به دیدار هم پس از چهل سال رسانید) که البته هر کس تقوا و صبر پیشه کند خدا اجر نیکوکاران را ضایع نگذارد.'),
(1687, 12, 91, 'برادران گفتند: به خدا که خدا تو را بر ما (به ملک و عزت و عقل و حسن و کمال) برگزید و ما (در حق تو) مقصر و خطاکار بودیم.'),
(1688, 12, 92, 'یوسف گفت: امروز هیچ خجل و متأثر نباشید، که خدا گناه شما ببخشد و او مهربان‌ترین مهربانان است.'),
(1689, 12, 93, 'اکنون این پیراهن مرا نزد پدرم (یعقوب) برده و به روی او افکنید تا دیدگانش باز بینا شود آن‌گاه (او و) همه اهل بیت و خویشان خود را (از کنعان به مصر) نزد من آرید.'),
(1690, 12, 94, 'و چون کاروان از مصر بیرون آمد یعقوب گفت: اگر مرا تخطئه نکنید من بوی یوسف را می‌شنوم.'),
(1691, 12, 95, 'شنوندگان گفتند: قسم به خدا که تو از قدیم الایام تا کنون (از شوق یوسف) حواست پریشان و عقلت مشوّش است (که هنوز بوی یوسف می‌شنوی).'),
(1692, 12, 96, 'پس از آنکه بشیر آمد (و بشارت یوسف آورد) پیراهن او را به رخسارش افکند و دیده انتظارش (به وصل) روشن شد، گفت: به شما نگفتم که از (لطف) خدا به چیزی آگاهم که شما آگه نیستید؟!'),
(1693, 12, 97, 'در آن حال برادران یوسف عرضه داشتند: ای پدر بر تقصیراتمان از خدا آمرزش طلب که ما خطای بزرگ مرتکب شده‌ایم.'),
(1694, 12, 98, 'پدر گفت: به زودی از درگاه خدا برای شما آمرزش می‌طلبم که او بسیار آمرزنده و مهربان است.'),
(1695, 12, 99, 'پس چون بر یوسف وارد شدند، یوسف پدر و مادر خود را در آغوش آورد و (از آنجا که به استقبالشان آمده بود) گفت: به شهر مصر در آیید که ان شاء اللّه ایمن خواهید بود.'),
(1696, 12, 100, 'و پدر و مادر را بر تخت بنشاند و آنها همگی پیش او (به شکرانه دیدار او، خدا را) سجده کردند، و یوسف در آن حال گفت: ای پدر، این بود تعبیر خوابی که از این پیش دیدم، که خدای من آن خواب را واقع و محقق گردانید و درباره من احسان فراوان فرمود که مرا از تاریکی زندان نجات داد و شما را از بیابان دور به اینجا آورد پس از آنکه شیطان میان من و برادرانم فساد کرد، که خدای من لطف و کرمش به آنچه مشیّتش تعلق گیرد شامل شود و هم او دانا و محکم کار است.'),
(1697, 12, 101, 'بار الها، تو مرا از سلطنت (و عزت) بهره‌داری و از علم رؤیا و تعبیر خوابها بیاموختی، ای آفریننده زمین و آسمان، تویی ولی نعمت و محبوب من در دنیا و آخرت، مرا به تسلیم و رضای خود بمیران و به صالحانم بپیوند.'),
(1698, 12, 102, '(ای رسول ما) این حکایت از اخبار غیب بود که بر تو به وحی می‌رسانیم و (گر نه) تو آنجا که برادران یوسف بر مکر و حیله تصمیم گرفتند حاضر نبودی.'),
(1699, 12, 103, 'و تو هر چند جهد و ترغیب در ایمان مردم کنی باز اکثر آنان ایمان نخواهند آورد (دل پاکت را زیاد رنجه مدار).'),
(1700, 12, 104, 'و تو از امت خود اجر رسالت نمی‌خواهی، این کتاب غرضی جز آنکه اهل عالم را متذکر و بیدار سازد ندارد.'),
(1701, 12, 105, 'و (این مردم بی‌خرد) چه بسیار بر آیات و نشانه‌ها (ی قدرت حق) در آسمانها و زمین می‌گذرند و از آن روی می‌گردانند.'),
(1702, 12, 106, 'و اکثر خلق به خدا ایمان نمی‌آورند مگر آنکه مشرک باشند (و جز خدا امور دیگر را نیز مؤثر در انتظام عالم دانند).'),
(1703, 12, 107, 'آیا (مردم کافر) ایمن از آنند که عذابی از (قهر) خدا بر آنها احاطه کند یا آنکه ساعت مرگ و قیامتشان ناگهان فرا رسد که در آن حال غافل باشند؟'),
(1704, 12, 108, 'بگو: طریقه من و پیروانم همین است که خلق را به خدا با بینایی و بصیرت دعوت کنیم، و خدا را از شرک و شریک منزه دانم و هرگز به خدای یکتا شرک نیاورم.'),
(1705, 12, 109, 'و ما هیچ کس را پیش از تو به رسالت نفرستادیم جز آنکه رسولان همه (مانند تو) مردانی بودند از اهل شهرهای دنیا که به وحی ما مؤیّد شدند، (اینان که به انکار رسول به راه کفر و باطل می‌روند) آیا در روی زمین سیر نکرده‌اند تا عاقبت حال پیشینیانشان را (که چگونه هلاک شدند) بنگرند؟ و محققا سرای آخرت برای اهل تقوا (از حیات دنیا) بسیار نیکوتر است، آیا تعقل نمی‌کنید؟!'),
(1706, 12, 110, '(مردم با انبیاء چندان ضدیت کردند) تا آنجا که رسولان مأیوس شده و گمان کردند که وعده نصرت خدا خلاف خواهد شد (یا گمان کردند که دیگر هیچ کس تصدیق آنها نخواهد کرد) در آن حال یاری ما بدیشان فرا رسید تا هر که ما خواستیم نجات داده شد، و نیز قهر و انتقام ما از بدکاران عالم باز گردانده نخواهد شد.'),
(1707, 12, 111, 'همانا در حکایت آنان برای صاحبان عقل عبرت کامل خواهد بود. این قرآن نه سخنی است که فرا توان بافت لیکن کتب آسمانی پیش از خود را هم تصدیق کرده و هر چیزی را (که راجع به سعادت دنیا و آخرت خلق است) مفصل بیان می‌کند و برای اهل ایمان هدایت و (سعادت و) رحمت خواهد بود.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'المر (رمزی بین خدا و رسول است) این است آیات کتاب خدا و قرآنی که به حق و راستی بر تو از جانب پروردگارت نازل گردید و لیکن اکثر مردمان به آن ایمان نمی‌آورند.'),
(1709, 13, 2, 'خداست آن ذات پاکی که آسمانها را چنان که می‌نگرید بی‌ستون برافراشت آن‌گاه بر عرش (قدرت بر وجود کل) قرار گرفت و خورشید و ماه را مسخّر اراده خود ساخت که هر کدام در وقت خاص به گردش آیند، امر عالم را منظم می‌سازد و آیات (قدرت) را با دلایلی مفصل بیان می‌دارد، باشد که شما به ملاقات پروردگار خود یقین کنید.'),
(1710, 13, 3, 'و اوست خدایی که بساط زمین را بگسترد و در آن کوهها برافراشت و نهرها جاری ساخت و از هرگونه میوه یک جفت پدید آورد، شب تار را به روز روشن بپوشاند، همانا در این امور متفکران را دلایلی روشن (بر قدرت آفریدگار) است.'),
(1711, 13, 4, 'و در زمین قطعاتی مجاور و متصل است (که آثار هر قطعه مباین قطعه دیگر است) زمینی برای تاکستان و باغ انگور قابل است، و یک جا برای زراعت غلاّت، و زمینی برای نخلستان آن هم نخلهای گوناگون که همه با یک آب مشروب می‌شوند ولی ما بعضی را در نوع میوه بر بعضی برتری می‌دهیم، و این امور عاقلان را ادلّه واضحی (بر حکمت صانع) است.'),
(1712, 13, 5, 'و اگر تو را جای تعجب (به کار منکران) است عجب قول منکران معاد است که می‌گویند: آیا ما چون خاک شدیم باز از نو خلق خواهیم شد؟ اینان هستند که به خدای خود کافر شدند و هم اینان زنجیرها (ی قهر و عذاب) بر گردن دارند و هم اینان اصحاب دوزخند و در آن همیشه معذبند.'),
(1713, 13, 6, '(ای رسول ما) کافران پیش از تقاضای آمرزش و احسان (به تمسخر) از تو تقاضای تعجیل در عذاب می‌کنند؟ در صورتی که چه عقوبت‌های عبرت‌آموز بر امّتان کافر گذشته رسید! و خدا بر ظلم خلق هم بسیار صاحب عفو و مغفرت است و هم صاحب قهر و انتقام سخت است.'),
(1714, 13, 7, 'و باز کافران (به طعنه) می‌گویند که چرا از خدایش بر او آیت و معجزه‌ای فرستاده نشد؟ (باید بدانند که) تنها وظیفه تو اندرز و ترسانیدن (خلق از نافرمانی خدا) است و هر قومی را (از طرف خدا) رهنمایی است.'),
(1715, 13, 8, 'تنها خدا می‌داند که بار حمل آبستنان عالم چیست و بار رحمها چه نقصان و چه زیادت آرند، و مقدار همه چیز در علم ازلی خدا معین است.'),
(1716, 13, 9, 'اوست عالم به عوالم غیب و شهود و بزرگ خدای متعال (برتر از هر وصف و ادراک عقول).'),
(1717, 13, 10, 'در پیشگاه علم ازلی آن کس از شما که سخن به سرّ گوید یا آشکار و آن که در ظلمت شب پنهان است یا در روشنی روز به کار و فعالیت مشغول است، همه یکسانند (و خدا بر همه آگاه است).'),
(1718, 13, 11, 'برای هر کس پاسبانها از پیش رو و پشت سر برگماشته شده که به امر خدا او را نگهبانی کنند. خدا حال هیچ قومی را دگرگون نخواهد کرد تا زمانی که خود آن قوم حالشان را تغییر دهند (و از نیکی به بدی شتابند)، و هرگاه خدا اراده کند که قومی را (به بدی اعمالشان) عقاب کند هیچ راه دفاعی نباشد و برای آنان هیچ کس را جز خدا یارای آنکه آن بلا بگرداند نیست.'),
(1719, 13, 12, 'اوست که برای بیم (از قهر) و امید (به رحمت) خود برق را به شما می‌نماید و ابرهای سنگین را پدید می‌آورد.'),
(1720, 13, 13, 'و رعد (و برق و همه قوای عالم غیب و شهود) و جمیع فرشتگان همه از بیم (قهر) خدا به تسبیح و ستایش او مشغولند، و صاعقه‌ها را بر سر هر قومی بخواهد می‌فرستد، باز هم کافران در (قدرت) خدا جدل می‌کنند با آنکه او سخت انتقام است.'),
(1721, 13, 14, 'دعوت خدا (و رسولانش) به حق و حقیقت است و بتهایی که مشرکان به جای خدا می‌خوانند هیچ حاجتی را از آنها برنیاورند و تنها مانند آن کس‌اند که به سوی آبی دست دراز کند که بیاشامد ولی آب به دهانش نرسد، و دعای کافران جز در (حرمان و) ضلالت نیست.'),
(1722, 13, 15, 'هر که در آسمانها و زمین است با همه آثار وجودیش به رغبت و اشتیاق و به اکراه و الزام، شب و روز به سجده (و طاعت) خدا مشغول است.'),
(1723, 13, 16, 'بگو که آفریننده آسمانها و زمین کیست؟ بگو: خداست، پس بگو: آیا شما خدا را گذارده و غیر خدا (مانند بتان و فرعونان) را برای نگهبانی و یاری خود برگرفتید در صورتی که آنها بر سود و زیان خود هم قادر نیستند؟ آن گاه بگو: آیا چشم نابینا (ی جاهل) و دیده بینا (ی عالم) یکسان است؟ یا ظلمات (شرک و بت پرستی) با نور (معرفت و خدا پرستی) مساوی است؟ یا آنکه این مشرکان شریکانی برای خدا قرار دادند که آنها هم مانند خدا چیزی خلق کردند و بر مشرکان، خلق خدا و خلق شریکان خدا مشتبه گردید؟ (هرگز چنین نیست) بگو: تنها خدا خالق هر چیز است و او خدای یکتایی است که همه عالم مقهور اراده اوست.'),
(1724, 13, 17, 'خدا از آسمان آبی نازل کرد که در هر رودی به قدر وسعت و ظرفیتش سیل آب جاری شد و بر روی سیل کفی بر آمد چنانکه فلزاتی را نیز که برای تجمل و زینت (مانند طلا و نقره) یا برای اثاث و ظروف (مانند آهن و مس) در آتش ذوب کنند مثل آب کفی برآورد، خدا به مثل این (آب و فلزات و کف روی آنها) برای حق و باطل مثل می‌زند که (باطل چون) آن کف به زودی نابود می‌شود و اما (حق چون) آن آب و فلز که به خیر و منفعت مردم است در زمین درنگ می‌کند. خدا مثلها را بدین روشنی بیان می‌کند.'),
(1725, 13, 18, 'آنان که دعوت حق را اجابت کرده و به خدا ایمان آوردند بر آنها بهترین پاداش و خوش‌ترین زندگانی است، و آنان که اجابت نکردند اگر مالک همه آنچه در روی زمین است باشند هر آینه آن را فدای آسایش خود کنند (مگر از عذاب برهند و هرگز نجات نیابند)، آنها را حساب سخت و جایگاه دوزخ باشد که بسیار بد آرامگاهی است.'),
(1726, 13, 19, 'آیا مسلمانی که به یقین می‌داند که این قرآن به حق از جانب خدا بر تو نازل شده است با کافر نابینای جاهل یکسان است؟ تنها عاقلان متذکر این حقیقتند.'),
(1727, 13, 20, 'عاقلان آنهایند که به عهد خدا وفا می‌کنند و پیمان حق را نمی‌شکنند.'),
(1728, 13, 21, 'و هم به آنچه خدا امر به پیوند آن کرده (مانند صله رحم و محبت اهل ایمان و علم) می‌پیوندند و از خدای خود می‌ترسند و از سختی هنگام حساب می‌اندیشند.'),
(1729, 13, 22, 'و هم در طلب رضای خدا راه صبر پیش می‌گیرند و نماز به پا می‌دارند و از آنچه نصیبشان کردیم پنهان و آشکار انفاق می‌کنند و در عوض بدیهای مردم نیکی می‌کنند، اینان هستند که عاقبت منزلگاه نیکو یابند.'),
(1730, 13, 23, 'که آن منزل بهشتهای عدن است که در آن بهشتها خود و همه پدران و زنان و فرزندان شایسته آنها داخل می‌شوند در حالی که فرشتگان بر (تهنیت) آنها از هر در وارد می‌گردند.'),
(1731, 13, 24, '(و می‌گویند) سلام و تحیت بر شما باد که صبر پیشه کردید، و بس نیک است سرانجام این سرای.'),
(1732, 13, 25, 'و آنان که پس از پیمان بستن (با خدا و رسول) عهد خدا را می‌شکنند و هم از آنچه خدا امر به پیوند آن کرده پاک می‌گسلند و در روی زمین فساد و فتنه بر می‌انگیزند اینان را لعن (خدا) و منزلگاه عذاب سخت دوزخ نصیب است.'),
(1733, 13, 26, 'خدا هر که را خواهد فراخ روزی و هر که را خواهد تنگ روزی می‌گرداند، و (این مردم کافر) به زندگانی و متاع دنیا دلشادند در صورتی که دنیا در قبال آخرت متاعی (موقت و ناقابل) بیش نیست.'),
(1734, 13, 27, 'و کافران (مکه) می‌گویند: چرا آیت و حجت قاطعی از خدا بر (اثبات نبوت) او نازل نشد؟ تو به آنها بگو که (حجت قاطعی مانند قرآن و معجزات دیگر آمد اکنون) خدا هر که را خواهد گمراه و هر که را که به درگاه او تضرع و انابه کند هدایت می‌کند.'),
(1735, 13, 28, 'آنها که به خدا ایمان آورده و دلهاشان به یاد خدا آرام می‌گیرد، آگاه شوید که تنها یاد خدا آرام‌بخش دلهاست.'),
(1736, 13, 29, 'آنان که به خدا ایمان آورده و به کار نیکو پرداختند خوشا بر احوال آنها، و بازگشت و مقام نیکو آنها راست.'),
(1737, 13, 30, 'ما تو را میان خلقی به رسالت فرستادیم که پیش از این هم (پیغمبران و) امت‌های دیگر به جایشان بوده و در گذشتند (امر تازه‌ای نیست که تو را به رسالت فرستادیم) تا بر آنها آنچه از ما به وحی بر تو رسد تلاوت کنی در حالی که آنها به خدای مهربان کافر می‌شوند، بگو: او خدای من است و جز او خدایی نیست، من بر او توکل کرده‌ام و روی امیدم همه به سوی اوست.'),
(1738, 13, 31, 'و اگر قرآنی بود که با (اعجاز بیان) آن کوهها به رفتار می‌آمد و زمین از هم می‌شکافت و با مردگان سخن گفته می‌شد (همین قرآن با عظمت است که با وجود آن باز ایمان نمی‌آورند). بلکه فرمان همه عالم با خداست (هر چه خواهد به مشیّت ازلی می‌کند) آیا مؤمنان هنوز ندانسته‌اند که خدا اگر بخواهد همه مردم را (به الزام و جبر) هدایت می‌کند؟ و (این نه مصلحت است زیرا باید) کافران پیوسته از کردار زشتشان به کیفر و سرکوبی رسند یا مصیبتی نزدیک دیار آنها فرود آید تا آن‌گاه که (در محشر هم) وعده خدا (بر هلاک آنها) فرا رسد، که خدا خلاف وعده نخواهد کرد.'),
(1739, 13, 32, 'و مردم به رسولان پیش از تو هم استهزاء بسیار کردند من هم به کافران فرصت دادم آن گاه ایشان را به عقوبت گرفته و با چه عقاب سخت به کیفر رسانیدم.'),
(1740, 13, 33, 'آیا خدایی که نگهبان همه نفوس عالم با آثارشان است (فراموش کردند)؟ و برای خدا شریکانی جعل کردند، بگو که نام خدایان خود برگویید (که اینها کیستند و اثر اینان چیست و از چه رو مستحق پرستش شدند؟) یا این که شما مردم نادان می‌خواهید خدا را به چیزی که در زمین از آن آگاه نیست آگه سازید؟! یا آنکه به ظاهر سخنی (که خود هم حقیقتی بر آن قائل نیستید) می‌گویید؟ بلکه کافران را مکر و فسونشان در نظر زیبا آمده و از راه خدا باز ماندند، و هر کس را خدا به گمراهی واگذارد دیگر هیچ کس وی را هدایت نتواند کرد.'),
(1741, 13, 34, 'چنین گمراهان در زندگانی دنیا (به انواع بلا) معذبند و البته عذاب عالم آخرت سخت‌تر است، و از (قهر) خدا ابدا نگهداری ندارند.'),
(1742, 13, 35, 'وصف بهشتی که متقیان را وعده دادند چنان است که نهرها زیر درختانش جاری است و مأکولاتش همیشگی و سایه آن برقرار است، این بهشت سرانجام اهل تقواست و سرانجام کافران آتش دوزخ است.'),
(1743, 13, 36, 'و آنان که ما بر آنها کتاب فرستادیم (یعنی مؤمنان اهل کتاب و مسلمانان) به این (کتاب با عظمت قرآن) که بر تو نازل شد بسیار خوشوقتند، و جماعتی از آنها (مانند کفار اهل کتاب و قریش) برخی آیات را انکار می‌کنند. بگو: من مأمورم که خدای یکتا را پرستم و هرگز به او شرک نیاورم، به سوی او دعوت می‌کنم و بازگشت من به سوی اوست.'),
(1744, 13, 37, 'و همچنین ما قرآن را کتابی با حکمت و فصاحت عربی فرستادیم، و اگر با این علم و دانش که بر تو آمد باز پیرو میل جاهلانه آنها شدی دیگر مدد و نگهبانی از (قهر) خدا نخواهی داشت.'),
(1745, 13, 38, 'و محققا ما رسولانی پیش از تو فرستادیم و برای همه آنان (مانند تو) زنان و فرزندان مقرر نمودیم (یعنی همه رسل بشر بودند، یکی فرشته نبود که از لوازم بشری بی‌نیاز باشد) و هیچ پیغمبری را نرسد که بی اذن خدا آیت و معجزی آورد، که هر امری را وقتی معین مرقوم است.'),
(1746, 13, 39, 'خدا هر چه را خواهد (از احکام یا حوادث عالم) محو و هر چه را خواهد اثبات می‌کند و اصل کتاب (آفرینش) نزد اوست.'),
(1747, 13, 40, 'و اگر ما (در حیات تو) بر تو برخی از آن وعده عذابی که به کافران دادیم پدیدار کنیم یا آنکه (پیش از آن وقت) تو را به دیدار آخرت بریم، به هر حال بر تو تبلیغ (حکم خدا) و بر ما حساب (خلق) است (و تعیین وقت عذاب و حساب یا عفو بندگان از وظایف نبوت نیست).'),
(1748, 13, 41, 'آیا مردم ندیدند که ما (به فرستادن رسولان) عزم سرزمین (کافران جاهل) کرده و از هر طرف آن می‌کاهیم (و بر بلاد و قدرت اهل ایمان می‌افزاییم)؟ و تنها خداست که در جهان فرمان می‌دهد و هیچ کس بر رد حکمش قادر نیست و اوست که در یک لحظه حساب همه خلق بی‌شمار را می‌کند.'),
(1749, 13, 42, 'و پیش از اینان هم بسیاری کافران مکرها (بر ضد رسولان) کردند (و عاقبت همه هلاک شدند) که همه مکر و تدبیرها نزد خداست، او می‌داند هر کس به چه کار و اندیشه است، و کافران به زودی خواهند یافت که عاقبت خوش و منزل سعادت برای کیست.'),
(1750, 13, 43, 'و کافران بر تو اعتراض کنند که تو رسول خدا نیستی، بگو: تنها گواه بین من و شما خدا و عالمان حقیقی به کتاب (خدا که انبیاء و اولیاء و علماء ربّانیند) کافی خواهد بود.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الر (حروف مقطعه و اسرار الهی است) این قرآن کتابی است که ما به تو فرستادیم تا مردم را به امر خدایشان از ظلمات (جهل و کفر) بیرون آری و به عالم نور رسانی و به راه خدای مقتدر ستوده صفات رهسپار گردانی.'),
(1752, 14, 2, 'خدایی که هر چه در آسمانها و زمین است همه ملک اوست، و وای بر کافران از عذابی سخت.'),
(1753, 14, 3, 'یعنی آنان که زندگانی دنیا را بر آخرت مقدم و محبوب‌تر دارند و خلق را از راه خدا باز دارند و آن راه راست را (به شک و شبهات) کج کنند، آنان در گمراهی بسیار دورند.'),
(1754, 14, 4, 'و ما هیچ رسولی در میان قومی نفرستادیم مگر به زبان آن قوم تا بر آنها (معارف و احکام الهی را) بیان کند، آن‌گاه خدا هر که را خواهد به ضلالت وا می‌گذارد و هر که را خواهد به مقام هدایت می‌رساند و او خدای مقتدر داناست.'),
(1755, 14, 5, 'و ما موسی عمران را با آیات خود (با تورات و معجزات، بر فرعونیان) فرستادیم که قومت را از ظلمات (جهل و گمراهی) بیرون آور و به عالم نور رسان و روزهای خدا را به یاد آنها آور، که در این یادآوری بر هر شخصی که صبور و شکرگزار است دلایل روشنی نهفته است (ایّام اللّه را برخی به ایام عهد الست و روزگار عالم ذر، و برخی به ایام ظهور پیغمبران و برخی حوادث عظیم لطف بر مؤمنان و قهر بر کافران تفسیر کرده‌اند).'),
(1756, 14, 6, 'و (ای رسول ما) یاد کن وقتی که موسی به قوم خود گفت: به خاطر آورید این نعمت بزرگ خدا را بر خودتان که شما را از فرعونیان نجات داد که آنها شما را به ظلم و شکنجه سخت می‌افکندند و پسرانتان را کشته و دخترانتان را (به ذلت و بیچارگی) زنده می‌گذاشتند و در این امور ابتلا و امتحان بزرگی از جانب خدایتان بر شما بود.'),
(1757, 14, 7, 'و (باز به خاطر آرید) وقتی که خدا اعلام فرمود که شما بندگان اگر شکر نعمت به جای آرید بر نعمت شما می‌افزایم و اگر کفران کنید عذاب من بسیار سخت است.'),
(1758, 14, 8, 'و (باز به یاد آرید که) موسی گفت: اگر شما و همه اهل زمین یک مرتبه کافر شوید خدا (از همه) بی‌نیاز و ستوده صفات است.'),
(1759, 14, 9, 'آیا اخبار بسیاری از پیشینیان شما مانند قوم نوح و عاد و ثمود و اقوام بعد از اینها که جز خدا کسی بر احوالشان آگاه نیست به شما نرسیده که پیغمبرانشان آیات و معجزات روشن بر آنها آوردند و آنها (از تعجب و خشم) دست به دهان فرو برده می‌گفتند که ما به هر چه شما پیغمبران مأمور تبلیغ آن هستید کافریم و هم در آنچه شما ما را بدان دعوت می‌کنید شک و تردید داریم.'),
(1760, 14, 10, 'رسولان در جواب آنها گفتند: آیا در خدا که آفریننده آسمانها و زمین است شک توانید کرد؟ خدایی که شما را به مغفرت و آمرزش از گناهانتان می‌خواند و می‌خواهد به اجل معین و عمر طبیعی برساند. باز کافران گفتند: ما شما (پیغمبران) را مثل خود بشری بیشتر نمی‌دانیم که می‌خواهید ما را از آنچه پدران ما می‌پرستیدند منع کنید، پس (اگر به راستی پیغمبر هستید) برای ما حجت و معجزی آشکار (که ما را مجبور به ایمان کند) بیاورید.'),
(1761, 14, 11, 'رسولان باز به کافران پاسخ دادند که آری ما هم مانند شما بشری بیش نیستیم لیکن خدا بر هر کس از بندگان که بخواهد (به نعمت بزرگ نبوت) منّت می‌گذارد، و ما را نرسد که برای شما آیت و معجزی الاّ به اذن و دستور خدا بیاوریم، و مؤمنان (در هر حال) باید تنها به خدا توکل کنند.'),
(1762, 14, 12, 'و چرا ما بر خدا توکل نکنیم در صورتی که خدا ما را به راه راستمان هدایت فرموده؟ و البته (در راه اطاعت و رضای خدا) بر آزار و ستمهای شما صبر خواهیم کرد، و ارباب توکل باید (در همه حال خوش و ناخوش) تنها بر خدا توکل کنند.'),
(1763, 14, 13, 'باز کافران به رسولان پاسخ دادند که ما البته شما را از شهر و دیار خود بیرون می‌کنیم مگر آنکه به آیین ما برگردید، در این حال (که رسولان مأیوس از ایمان کافران شدند) خدا به آنها وحی فرمود که (غم مخورید) البته ما ستمکاران را هلاک خواهیم کرد.'),
(1764, 14, 14, 'و محققا ما شما رسولان (و پیروانتان) را در سرزمین کافران پس از هلاک آنها (با آسایش و ایمنی) ساکن می‌گردانیم و این (آسایش) نصیب کسی است که از مقام من خائف است و از وعده قهر و عقاب من می‌ترسد.'),
(1765, 14, 15, 'و (از خدا) فتح و پیروزی خواستند (و البته فتح و فیروزی بر حسب وعده ما نصیب رسولان خداست) و نصیب هر ستمگر جبار هلاکت و حرمان است.'),
(1766, 14, 16, 'از پی گردنکش عنود آتش دوزخ خواهد بود و آبی که به او آشامند (در دوزخ) آب پلید چرکین است.'),
(1767, 14, 17, 'که آن آب پلید را جرعه جرعه می‌آشامد و هیچ گوارای او نشود، و از هر جانب مرگ به وی روی آور شود ولی نمیرد و فراروی او عذابی سخت خواهد بود.'),
(1768, 14, 18, 'مثل اعمال کسانی که به خدا کافر شدند به خاکستری می‌ماند که در روز تند باد شدید همه به باد فنا رود، که از همه کوشش خود هیچ نتیجه نبرند. این همان ضلالت (و حسرت) دور (از طریق نجات) است.'),
(1769, 14, 19, '(ای بشر) آیا ندانستی که خدا آسمانها و زمین را به حق و برای مقصود بزرگی آفریده و اگر بخواهد شما (جنس بشر) همه را در زمین نابود می‌سازد و خلقی دیگر از نو می‌آفریند؟'),
(1770, 14, 20, 'و این کار اصلا بر خدا دشوار نیست.'),
(1771, 14, 21, 'و (روزی که) مردم از قبرها برانگیخته و به پیشگاه خدا حاضر شوند آن گاه ضعیفان به گردنکشان گویند: ما (در دنیا) تابع رأی شما بودیم آیا (امروز) شما هیچ از عذاب خدا ما را کفایت خواهید کرد؟ جواب دهند که اگر ما را از خدا سعادت هدایت بود ما هم شما را هدایت می‌کردیم، اکنون هر چه جزع و التماس کنیم یا صبر و تحمل، یکسان است و هیچ (از عذاب) گریزگاهی نداریم.'),
(1772, 14, 22, 'و چون حکم (قیامت) به پایان رسید (و اهل بهشت از اهل دوزخ جدا شدند) در آن حال شیطان (برای نکوهش و تمسخر کافران) گوید: خدا به شما به حق و راستی وعده داد و من به خلاف حقیقت شما را وعده دادم و خلف وعده کردم و بر شما (برای وعده دروغ خود) هیچ حجت و دلیل قاطعی نیاوردم و تنها شما را فراخواندم و شما اجابتم کردید، پس (امروز شما ابلهان که سخن بی‌دلیل مرا پذیرفتید) مرا ملامت مکنید بلکه نفس (پر طمع) خود را ملامت کنید، که امروز نه من فریادرس شما خواهم بود و نه شما فریادرس من توانید بود، من به شرکی که شما به (اغوای) من آوردید معتقد نیستم، آری (در این روز) ستمکاران عالم را عذابی دردناک خواهد بود.'),
(1773, 14, 23, 'و آنان را که به خدا ایمان آورده و عمل صالح کردند در بهشتهایی برند که زیر درختانش نهرها جاری است و همیشه به فرمان خدای خود در آن بهشت مخلدند و تحیت آنان در آنجا سلام است.'),
(1774, 14, 24, '(ای رسول) آیا ندیدی که چگونه خدا کلمه پاکیزه را به درخت پاک و زیبایی مثل زده که اصل ساقه آن برقرار باشد و شاخه آن به آسمان (رفعت و سعادت) بر شود؟'),
(1775, 14, 25, 'و آن درخت پاک و زیبا به اذن خدا همه اوقات میوه‌های مأکول و خوش دهد (مثل جان پاک با دانش و معرفت و افکار و کردار نیکو در منفعت دائم برای خود و دیگران بدان درخت زیبای پر ثمر ماند). و خدا (این گونه) مثل‌های واضح برای تذکر مردم می‌آورد.'),
(1776, 14, 26, 'و مثل کلمه کفر (و روح پلید) مانند درخت پلیدی است که ریشه‌اش به قلب زمین نرود بلکه بالای زمین افتد و (زود خشک شود و) هیچ ثبات و بقایی ندارد.'),
(1777, 14, 27, 'خدا اهل ایمان را با عقیده ثابت در حیات دنیا و در آخرت پایدار می‌دارد و خداوند ستمکاران را به حال گمراهی وا می‌گذارد و خدا هر چه بخواهد (به اختیار مطلق) می‌کند.'),
(1778, 14, 28, 'هیچ ندیدی حال مردمی را که نعمت خدا را به کفر مبدل ساختند و (خود و) قوم خود را به دیار هلاکت رهسپار کردند؟'),
(1779, 14, 29, 'یعنی به دوزخ که بدترین جایگاه است درآیند.'),
(1780, 14, 30, 'و در مقابل خدا امثال و اضدادی (مانند فرعونان و بتان) جعل کردند که (خود و خلق را) از راه خدا گمراه کنند. (ای رسول ما، به این مردم بت‌تراش و بت پرست) بگو که (این دو روزه دنیا) به لذات دنیوی مشغول باشید که بازگشت شما به آتش دوزخ خواهد بود.'),
(1781, 14, 31, 'به آن بندگان من که ایمان آوردند بگو نماز به پا دارند و از آنچه روزی آنها کردیم در نهان و آشکار انفاق کنند پیش از آنکه بیاید روزی که نه چیزی توان خرید و نه دوستی کسی (جز خدا) به کار آید.'),
(1782, 14, 32, 'خداست آن که آسمانها و زمین را آفرید و باران را از آسمان فرو بارید تا بدان انواع ثمرات و حبوبات را برای روزی شما برآورد، و کشتی‌ها را مسخر شما کرد تا به امر او به روی آب دریا روان شود و نهرها را (به روی زمین) به اختیار شما جاری گردانید.'),
(1783, 14, 33, 'و خورشید و ماه گردنده و شب و روز را برای شما مسخر کرد.'),
(1784, 14, 34, 'و از انواع نعمتهایی که از او درخواست کردید به شما عطا فرمود، و اگر نعمتهای (بی انتهای) خدا را بخواهید به شماره آورید هرگز حساب آن نتوانید کرد، (با این همه لطف و رحمت خدا) باز انسان سخت ستمگر و کفر کیش و ناسپاس است.'),
(1785, 14, 35, 'و (یادآر) وقتی که ابراهیم عرض کرد: پروردگارا، این شهر (مکه) را مکان امن و امان قرار ده و من و فرزندانم را از پرستش بتان دور دار.'),
(1786, 14, 36, 'پروردگارا، این بتان (و بت‌تراشان) بسیاری از مردم را گمراه کردند، پس هر کس (در راه توحید و خداپرستی) پیرو من است او از من است و هر که مخالفت من کند (و راه شرک و عصیان پوید اختیارش با توست که) تو خدای بسیار بخشنده و مهربانی.'),
(1787, 14, 37, 'پروردگارا، من برخی از ذرّیّه و فرزندان خود را به وادی بی‌کشت و زرعی نزد بیت‌الحرام تو مسکن دادم، پروردگارا، تا نماز را به پا دارند، پس تو دلهایی از مردمان را به سوی آنها مایل گردان و به انواع ثمرات آنها را روزی ده، باشد که شکر تو به جای آرند.'),
(1788, 14, 38, 'پروردگارا، تو به هر چه ما پنهان و آشکار کنیم بر همه آگاهی (که تو خدایی) و بر خدا البته هیچ چیز در زمین و آسمان پنهان نیست.'),
(1789, 14, 39, 'ستایش خدای را که به من در زمان پیری دو فرزندم اسماعیل و اسحاق را عطا فرمود (و درخواست مرا اجابت کرد) که پروردگار من البته دعای بندگان را شنواست.'),
(1790, 14, 40, 'پروردگارا، مرا و از ذرّیّه‌ام نیز کسانی را نمازگزار گردان، و بار الها دعای مرا بپذیر.'),
(1791, 14, 41, 'بار الها، روزی که (میزان عدل و) حساب به پا می‌شود (تو در آن روز سخت) بر من و والدین من و همه مؤمنان (از کرم) ببخشا.'),
(1792, 14, 42, 'و (ای رسول ما) هرگز مپندار که خدا از کردار ستمکاران غافل است، بلکه (کیفر) ظالمان را به تأخیر می‌افکند تا آن روزی که چشمها (یشان) در آن روز خیره و حیران است.'),
(1793, 14, 43, '(در آن روز سخت آن ستمکاران) همه شتابان و هراسان سر به بالا کرده و چشمها واله مانده و دلهاشان به دهشت و اضطراب است.'),
(1794, 14, 44, 'و (ای رسول ما) مردم را از روزی که هنگام عذاب (و کیفر اعمالشان) فرا می‌رسد بترسان (و آگاهشان ساز) که ستمکاران خلق (چون سختی عذاب را بنگرند از حسرت و پشیمانی) خواهند گفت: پروردگارا (عذاب) ما را اندک مدتی به تأخیر افکن تا دعوت تو را اجابت کنیم و پیرو رسولان (تو) شویم (و از هر کار بد بازگردیم. به آنها پاسخ آید که) آیا شما بارها پیش از این سوگند یاد نمی‌کردید که ما را ابدا زوال و هلاکی نخواهد بود؟'),
(1795, 14, 45, 'و شما (ستمکاران بودید که) در منازل ستمگران پیش از خود مسکن گزیدید و مشاهده کردید که عاقبت ما چه بر سر آنها آوردیم و بر شما سرگذشت آنها را مثل آوردیم (تا مگر عبرت گیرید و از کار زشت و ستمگری دست کشید، ولی هرگز پند نگرفته و بیدار نشدید).'),
(1796, 14, 46, 'و آن ستمکاران بزرگترین مکر و مهمترین سیاست خویش را به کار بردند ولی مکر آنها پیش (قدرت و تقدیر) خدا (هیچ) است هر چند مکر (و سیاست) آنها به پایه‌ای باشد که کوهها بدان از جای کنده شوند.'),
(1797, 14, 47, 'پس هرگز مپندار که خدا وعده خود با رسولانش را خلاف کند که البته خدا مقتدر و انتقام کشنده است.'),
(1798, 14, 48, 'روزی که زمین (به امر خدا) به غیر این زمین مبدل شود و هم آسمانها دگرگون شوند و تمام خلق در پیشگاه (حکم) خدای یکتای قادر قاهر حاضر شوند.'),
(1799, 14, 49, 'و (در آن روز) بدکاران را زیر زنجیر (قهر خدا) مشاهده خواهی کرد.'),
(1800, 14, 50, '(و بینی که) پیراهنهای از مس گداخته آتشین بر تن دارند و در شعله آتش چهره آنها پنهان است.'),
(1801, 14, 51, '(این گونه عذاب برای آن است) تا خدا هر شخص را به کیفر کردارش برساند، که خدا در یک لحظه به حساب خلق خواهد رسید.'),
(1802, 14, 52, 'این (قرآن عظیم) حجت بالغ برای جمیع مردم است (تا خلایق از آن پند گرفته) و تا بدان وسیله خداشناس و خداترس شوند و تا عموم بشر خدا را به یگانگی بشناسند و صاحبان عقل، متذکر و هوشیار گردند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الر (اسرار این حروف نزد خدا و رسول است) این است آیات کتاب خدا و قرآنی که (راه حق و باطل را) روشن و آشکار می‌گرداند.'),
(1804, 15, 2, 'کافران (چون چشم حقیقت بین بگشایند) ای بسا آرزو کنند که کاش مسلم و خداپرست بودند.'),
(1805, 15, 3, '(ای رسول ما) این کافران (لجوج) را واگذار تا به خورد و خواب طبیعت و لذات حیوانی سرگرم باشند و آمال و اوهام دنیوی آنان را غافل گرداند، پس (نتیجه این کامرانی بیهوده را) به زودی خواهند یافت.'),
(1806, 15, 4, 'و ما هیچ ملک و ملتی را هلاک نکردیم جز به هنگامی معین.'),
(1807, 15, 5, 'هیچ قومی از اجل خود (از آنچه در علم حق معین است یک لحظه) پس و پیش نخواهند افتاد.'),
(1808, 15, 6, 'و کافران گفتند: ای کسی که (مدعی آنی که) قرآن از جانب خدا بر تو نازل شده تو (به عقیده ما) محققا دیوانه‌ای.'),
(1809, 15, 7, 'اگر راست می‌گویی (و بر تو فرشتگان خدا نازل می‌شوند) چرا فرشتگان را نزد ما نمی‌آری؟'),
(1810, 15, 8, 'ما فرشتگان را جز به حق (و حکمت و مصلحت) نمی‌فرستیم، و آن گاه که بفرستیم دیگر کافران مهلتی نخواهند یافت.'),
(1811, 15, 9, 'البته ما قرآن را بر تو نازل کردیم و ما هم آن را محققا محفوظ خواهیم داشت.'),
(1812, 15, 10, 'و ما پیش از تو هم رسولانی بر امم سالفه فرستادیم.'),
(1813, 15, 11, 'و هیچ رسولی بر آن مردم نمی‌آمد جز آنکه به استهزای او می‌پرداختند.'),
(1814, 15, 12, 'این گونه ما قرآن را (روشن) در دل زشتکاران وارد سازیم،'),
(1815, 15, 13, 'لیکن به آن ایمان نمی‌آورند، و البته عادت امم سابقه هم (مانند امت تو بر همین انکار و استهزا نسبت به رسولان و کتب آسمانی) گذشت.'),
(1816, 15, 14, 'و اگر ما بر این کافران امتت دری از آسمان بگشاییم تا دائم بر آسمانها عروج (یا فرشتگان بر آنها نزول) کنند،'),
(1817, 15, 15, 'باز هم خواهند گفت: چشمان ما را فرو بسته‌اند، بلکه در ما سحر و جادویی به کار برده‌اند.'),
(1818, 15, 16, 'و همانا ما در آسمان کاخهای بلند برافراشتیم و بر چشم بینایان عالم آن کاخها را به زیب و زیور بیاراستیم.'),
(1819, 15, 17, 'و آن را از (دستبرد) هر شیطان مردودی محفوظ داشتیم.'),
(1820, 15, 18, 'لیکن هر شیطانی برای سرقت سمع (یعنی برای دزدیدن و دریافتن سخن فرشتگان عالم بالا) به آسمان نزدیک شود تیر شهاب و شعله آسمانی او را تعقیب کند.'),
(1821, 15, 19, 'و زمین را هم ما بگستردیم و در آن کوههای عظیم بر نهادیم و در آن از هر گیاه و هر نبات مناسب و موافق حکمت و عنایت برویانیدیم.'),
(1822, 15, 20, 'و در این زمین هم لوازم معاش و زندگانی شما را مهیا کردیم و هم بر سایر حیوانات که شما به آنها روزی نمی‌دهید قوت و غذا فراهم ساختیم.'),
(1823, 15, 21, 'و هیچ چیز در عالم نیست جز آنکه منبع و خزانه آن نزد ماست ولی ما از آن بر عالم خلق الاّ به قدر معین (که مصلحت است) فرو نمی‌فرستیم.'),
(1824, 15, 22, 'و ما بادهای باردار کننده (رحم طبیعت) را فرستادیم، آن‌گاه باران را از آسمان فرود آوردیم تا به آن آب، شما را سیراب گردانیدیم و گر نه شما نمی‌توانستید آبهایی (برای وقت حاجت خود در هر جای زمین از کوه و دره و بیابان و صحرا) منبع ساخته و جاری سازید.'),
(1825, 15, 23, 'و محققا ماییم که زنده می‌کنیم و می‌میرانیم و وارث (همه خلق که فانی می‌شوند) ما هستیم.'),
(1826, 15, 24, 'و البته ما به همه گذشتگان و آیندگان شما آگاهی کامل داریم.'),
(1827, 15, 25, 'و البته خدای تو همه خلایق را در قیامت محشور خواهد کرد، که کار او از روی علم و حکمت است.'),
(1828, 15, 26, 'و همانا ما انسان را از گل و لای کهنه متغیر بیافریدیم.'),
(1829, 15, 27, 'و طایفه دیوان را پیشتر از آتش گدازنده، خلق کردیم.'),
(1830, 15, 28, 'و (یاد آر) آن گاه که پروردگارت به فرشتگان عالم اظهار فرمود که من بشری از ماده گل و لای کهنه متغیر، خلق خواهم کرد.'),
(1831, 15, 29, 'پس چون آن (عنصر) را معتدل بیارایم و در آن از روح خویش بدمم همه (از جهت حرمت و عظمت آن روح الهی) بر او سجده کنید.'),
(1832, 15, 30, 'پس همه فرشتگان سجده کردند.'),
(1833, 15, 31, 'مگر ابلیس که از همراهی با سجده کنندگان امتناع ورزید.'),
(1834, 15, 32, 'خدا فرمود که ای شیطان، برای چه تو با ساجدان عالم سر فرود نیاوردی؟'),
(1835, 15, 33, 'شیطان پاسخ داد که من هرگز به بشری که از گل و لای کهنه متغیر خلقت کرده‌ای سجده نخواهم کرد.'),
(1836, 15, 34, 'خدا هم (به او قهر و عتاب) فرمود که از صف ساجدان (و از بهشت مطیعان) خارج شود که تو رانده درگاه ما شدی.'),
(1837, 15, 35, 'و لعنت ما تا روز جزا بر تو محقق و حتمی گردید.'),
(1838, 15, 36, 'شیطان از خدا درخواست کرد که پروردگارا، پس مرا تا روز قیامت که خلق مبعوث می‌شوند مهلت و طول عمر عطا فرما.'),
(1839, 15, 37, 'خدا فرمود: آری تو را مهلت خواهد بود.'),
(1840, 15, 38, 'تا به وقت معین و روز معلوم (شاید مرا تا قیامت یا نفخه صور اول یا ظهور دولت ولی عصر یا ظهور حکومت عقل انسان بر نفس و هوای او باشد).'),
(1841, 15, 39, 'شیطان گفت: خدایا، چنانکه مرا به گمراهی و هلاکت کشاندی من نیز در زمین (هر باطلی را) در نظر فرزندان آدم جلوه می‌دهم (تا از یاد تو غافل شوند) و همه آنها را به گمراهی و هلاکت خواهم کشاند.'),
(1842, 15, 40, 'به جز بندگان پاک و برگزیده تو را.'),
(1843, 15, 41, 'خدا فرمود: همین (اخلاص و پاکی سریرت) راه مستقیم به (درگاه رضای) من است.'),
(1844, 15, 42, 'و هرگز تو را بر بندگان (برگزیده) من تسلط و غلبه نخواهد بود لیکن اقتدار و سلطه تو بر مردم گمراهی است که پیرو تو شوند.'),
(1845, 15, 43, 'و البته وعده‌گاه جمیع آن مردم گمراه نیز آتش دوزخ خواهد بود.'),
(1846, 15, 44, 'که آن دوزخ را هفت در است که هر دری برای ورود دسته‌ای از گمراهان معین گردیده است.'),
(1847, 15, 45, 'البته اهل تقوا در باغها و نهرهای جاری خواهند بود.'),
(1848, 15, 46, '(به آنها خطاب شود که) شما با درود و سلام و با کمال ایمنی و احترام به بهشت ابد وارد شوید.'),
(1849, 15, 47, 'و ما آیینه دلهای پاک آنها را از کدورت کینه و حسد و هر خلق ناپسند به کلی پاک و پاکیزه ساختیم و همه برادروار روبروی یکدیگر بر تختهای عزت بنشینند.'),
(1850, 15, 48, 'در حالتی که هیچ رنج و زحمت در آنجا به آنها نرسد و هرگز از آن بهشت ابد بیرونشان نکنند.'),
(1851, 15, 49, '(ای رسول ما) بندگان مرا آگاه ساز که من بسیار آمرزنده و مهربانم.'),
(1852, 15, 50, 'و نیز عذاب من بسیار سخت و دردناک است.'),
(1853, 15, 51, 'و هم بندگانم را از حکایت (فرشتگان) مهمانان ابراهیم آگاه ساز.'),
(1854, 15, 52, 'که چون آن مهمانان بر ابراهیم وارد شدند و بر او سلام دادند ابراهیم (مضطرب شده) گفت: ما از شما (که بدون اذن و بی‌موقع بر ما در آمدید) بیمناکیم.'),
(1855, 15, 53, 'فرشتگان گفتند: هیچ مترس که ما آمده‌ایم تو را به پسری دانا بشارت دهیم.'),
(1856, 15, 54, 'ابراهیم گفت: آیا مرا در این سن پیری مژده فرزند می‌دهید؟ نشانه این مژده چیست؟'),
(1857, 15, 55, 'گفتند: ما تو را به حق و حقیقت بشارت دادیم و تو هرگز نومید مباش.'),
(1858, 15, 56, 'ابراهیم گفت: به جز مردم گمراه چه کسی از لطف خدای خود نومید می‌شود؟'),
(1859, 15, 57, 'آن گاه ابراهیم پرسش کرد که ای رسولان حق، باز گویید که بر چه کار مبعوث شده‌اید؟'),
(1860, 15, 58, 'فرشتگان پاسخ دادند که ما بر هلاک قومی زشتکار فرستاده شده‌ایم.'),
(1861, 15, 59, '(که آن قوم مجرم لوطند که به جرم عمل بد هلاک شوند) مگر اهل بیت لوط که همه آنها را از بلا نجات خواهیم داد.'),
(1862, 15, 60, 'جز زن لوط که آن هم (چون با بدان همدست است) چنین مقرر داشتیم که از بازماندگان باشد (و با زشتکاران هلاک شود).'),
(1863, 15, 61, 'و چون فرشتگان بر لوط و خانواده او وارد شدند.'),
(1864, 15, 62, 'لوط با فرشتگان گفت: شما اشخاصی نا آشنایید!'),
(1865, 15, 63, 'فرشتگان پاسخ دادند که ما بر انجام چیزی (یعنی وعده عذاب) که قومت در آن به شک و انکار بودند فرستاده شدیم.'),
(1866, 15, 64, 'و به حق و راستی به سوی تو آمده‌ایم و آنچه گوییم صدق محض است.'),
(1867, 15, 65, 'پس تو ای لوط، خانواده‌ات را در پاسی از شب از این دیار بیرون بر و خود از پی آنان روانه شو و هیچ یک از شما باز پس ننگرد بلکه بدان سو که مأمورید به سرعت روان شوید.'),
(1868, 15, 66, 'و بر او این فرمان را وحی کردیم که قومت تا آخرین افراد، صبحگاه هلاک می‌شوند.'),
(1869, 15, 67, 'و (در آن شب که فرشتگان به شکل جوانهای زیبا به خانه لوط در آمدند) قوم لوط آگاه شده خوشحال (به خیال کامرانی به خانه او) آمدند.'),
(1870, 15, 68, 'لوط به قوم خود گفت: این جوانان در منزل من مهمانند، مرا شرمسار مکنید.'),
(1871, 15, 69, 'و از خدا بترسید و مرا خجل مگردانید.'),
(1872, 15, 70, 'قوم گفتند: مگر ما تو را از (میزبانی و حمایت) عالمیان منع نکردیم؟'),
(1873, 15, 71, 'لوط به قوم خود گفت: من این دخترانم را به نکاح شما می‌دهم تا اگر خیال عملی دارید انجام دهید.'),
(1874, 15, 72, '(ای محمد) به جان تو قسم که این مردم دنیا همیشه مست شهوات نفسانی و به حیرت و غفلت و گمراهی خواهند بود.'),
(1875, 15, 73, 'باری، قوم لوط هنگام طلوع آفتاب به صیحه آسمانی همه هلاک شدند.'),
(1876, 15, 74, 'و شهر و دیار آنها را زیر و زبر ساخته و آن قوم را سنگباران عذاب کردیم.'),
(1877, 15, 75, 'و در این عذاب هوشمندان را عبرت و بصیرت بسیار است.'),
(1878, 15, 76, 'و این ویرانه اکنون بر سر راهی است بر قرار.'),
(1879, 15, 77, 'همانا در این عقوبت بدکاران عالم، اهل ایمان را آیت و عبرتی است.'),
(1880, 15, 78, 'و اهل شهر ایکه (قوم شعیب) هم بسیار مردم ستمکاری بودند.'),
(1881, 15, 79, 'آنها را نیز به کیفر رسانیدیم، و این هر دو شهر (یعنی شهر سدوم و ایکه یا مدین که دیار قوم لوط و شعیب بود، برای اهل اعتبار) بر رهگذری آشکار قرار دارند.'),
(1882, 15, 80, 'و اصحاب حجر (قوم صالح) هم رسولان را به کلی تکذیب کردند.'),
(1883, 15, 81, 'و از آیاتی که ما بر آنان فرستادیم رو گردانیدند.'),
(1884, 15, 82, 'و در کوهها منزل می‌ساختند تا از خطر ایمن باشند.'),
(1885, 15, 83, 'پس صبحگاهی صیحه عذاب (قهر ما) همه آنها را نابود کرد.'),
(1886, 15, 84, 'و به فراز کوه با همه ذخائر و ثروت از هلاک ایمن نگردیدند.'),
(1887, 15, 85, 'و ما آسمانها و زمین و هر چه را که در بین آنهاست جز به حق (و برای مقصودی صحیح و حکمتی بزرگ) خلق نکرده‌ایم، و البته ساعت قیامت (که غرض و مقصود اصلی خلقت شماست) خواهد آمد، اکنون تو (ای رسول ما، از این منکران) نیکو در گذر (یعنی با خلق خوش آنها را به حق دعوت کن و اگر نپذیرند دلتنگ مباش).'),
(1888, 15, 86, 'محققا پروردگار تو آفریننده دانایی است (که بی‌مقصود خلق را نیافریده، البته هر کسی به نتیجه عملش خواهد رسید).'),
(1889, 15, 87, '(ای محمد) همانا هفت آیت با ثنا (و دعا که قسمت بین خدا و عبد شده و در هر نماز دو بار خوانده می‌شود، یعنی سوره حمد) و این قرآن با عظمت را بر تو فرستادیم.'),
(1890, 15, 88, 'توبه این ناقابل متاع دنیوی که به طایفه‌ای از مردم کافر دادیم البته چشم مدوز و بر اینان اندوه مخور و اهل ایمان را زیر پر و بال خود گیر (و با کمال حسن خلق بپروران).'),
(1891, 15, 89, 'و بگو: من همان رسولم که برای اندرز و ترسانیدن (خلق از عذاب قهر خدا) با دلیلی روشن آمده‌ام.'),
(1892, 15, 90, 'آن گونه عذابی که بر کسانی که آیات خدا را قسمت کردند (یعنی یهود و نصاری) نازل نمودیم.'),
(1893, 15, 91, 'آنان که قرآن را (به میل خود و غرض فاسد خویش) جزء جزء و پاره پاره کردند (بعضی را قبول و بعضی را رد کردند).'),
(1894, 15, 92, 'قسم به خدای تو که از همه آنها سخت مؤاخذه خواهیم کرد.'),
(1895, 15, 93, 'از آنچه می‌کرده‌اند.'),
(1896, 15, 94, 'پس تو آشکارا آنچه مأموری (به خلق) برسان و از مشرکان روی بگردان.'),
(1897, 15, 95, 'همانا ما تو را از شر استهزا کنندگان محفوظ نمودیم.'),
(1898, 15, 96, 'آنان که با خدای یکتا خدایی دیگر قرار دهند، پس به زودی خواهند دانست.'),
(1899, 15, 97, 'و ما محققا می‌دانیم که تو از آنچه امت (در طعنه و تکذیب تو) می‌گویند سخت دلتنگ می‌شوی.'),
(1900, 15, 98, 'پس به ذکر اوصاف کمال پروردگارت تسبیح گو و از نمازگزاران باش (تا به یاد خدا شاد خاطر شوی).'),
(1901, 15, 99, 'و دایم به پرستش خدای خود مشغول باش تا یقین (ساعت مرگ و هنگام لقای ما) بر تو فرا رسد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'فرمان خدا (به ظهور قیامت و یا به عذاب شمشیر بر مشرکان) به زودی فرا می‌رسد پس (هان ای کافران، شما به تمسخر و هزل) تقاضای تعجیل آن مکنید، خدای یکتا متعالی و منزه از شرک مشرکان است.'),
(1903, 16, 2, 'خدا فرشتگان را با روح به امر خود بر هر یک از بندگان که خواهد می‌فرستد که خلق را (اندرز داده و از عقوبت شرک به خدا) بترسانید (و به بندگان بفهمانید) که خدایی جز من نیست، پس از من بترسید.'),
(1904, 16, 3, 'خدا آسمانها و زمین را به حق (و برای غرضی محقق و حکمتی بزرگ) آفرید، او از آنچه مشرکان شرک آرند متعالی و منزه است.'),
(1905, 16, 4, 'خدا انسان را از آب نطفه‌ای (بی قدر و ضعیف) بیافرید، آن‌گاه او (اصل خلقت خود را فراموش کرده و با خالق) آشکارا به خصومت می‌خیزد.'),
(1906, 16, 5, 'و چهارپایان را خلقت کرد که به (موی و پشم) آنها گرم می‌شوید و از آنها فواید بسیار دیگر برده و از (شیر و گوشت) آنها غذای مأکول می‌سازید.'),
(1907, 16, 6, 'و هنگامی که آنها را شبانگاه از چرا برمی‌گردانید یا صبحگاه به چرا بیرون می‌برید زیب و افتخار شما باشند.'),
(1908, 16, 7, 'و بارهای شما را (به آسانی) از شهری به شهر دیگر برند که خود شما (بدون بار) جز با مشقّت بسیار بدان جا نتوانید رسید (چه رسد با بار)؛ راستی که پروردگار شما رئوف و مهربان است.'),
(1909, 16, 8, 'و اسبان و استران و خران را برای سواری و تجمّل شما آفرید و چیز دیگری هم که شما هنوز نمی‌دانید (برای سواری شما) خواهد آفرید.'),
(1910, 16, 9, 'و بر خداست بیان راه عدل و راستی، و بعضی راهها جور و ناراستی است. و اگر خدا می‌خواست (بدون آزمایش بلکه به جبر) شما را همگی به راه هدایت (و جنّت) می‌کشید (لیکن این خلاف مصلحت و نظام الهی است).'),
(1911, 16, 10, 'اوست خدایی که آب باران را از آسمان فرو فرستاد که هم از آن می‌آشامید و هم درخت و گیاه از آن می‌روید که به میوه آن شما، و به برگش حیوانات شما، برخوردار می‌شوید.'),
(1912, 16, 11, 'و هم زراعتهای شما را از آن آب باران برویاند و درختان زیتون و خرما و انگور و از هر گونه میوه بپرورد؛ همانا در این کار آیت و نشانه‌ای (از رحمت و قدرت الهی) برای اهل فکرت پدیدار است.'),
(1913, 16, 12, 'و هم شب و روز و خورشید و ماه را برای زندگانی شما در گردون مسخر ساخت، و ستارگان آسمان هم به فرمان او مسخرند؛ همانا در این کار آیتها و نشانه‌هایی (از قدرت خدا) برای اهل خرد پدیدار است.'),
(1914, 16, 13, 'و نیز آنچه در زمین برای شما آفرید و به انواع گوناگون و اشکال رنگارنگ در آورد (همه را مسخر شما کرد)؛ همانا در این کار هم آیت و نشانه‌ای (از الهیت) بر مردم هشیار پدیدار است.'),
(1915, 16, 14, 'و هم او خدایی است که دریا را برای شما مسخر کرد تا از گوشت تر و تازه (ماهیان حلال) آن تغذیه کنید و از زیورهای آن (مانند درّ و مرجان) استخراج کرده و تن را بیارایید، و کشتی‌ها را در آن روان بینی که سینه آب را می‌شکافند (تا به تجارت و سفر رفته) و تا از فضل خدا روزی طلبید، و باشد که شکر خدا به جای آرید.'),
(1916, 16, 15, 'و نیز کوههای بزرگ را در زمین بنهاد تا زمین شما را به لرزه و اضطراب نیفکند و نهرها جاری کرد و راهها پدیدار ساخت تا مگر هدایت شوید.'),
(1917, 16, 16, 'و نشانه‌هایی (در زمین مانند کوهها و دریاها و رودها و جنگلها مقرر داشت) و آنان به ستارگان (در شبها) هدایت یابند.'),
(1918, 16, 17, 'آیا آن خدایی که خلق می‌کند مانند آن کس (از فراعنه و بتان) است که هیچ چیز در عالم خلق نمی‌کند؟ آیا متذکر و هوشیار نمی‌شوید؟'),
(1919, 16, 18, 'و اگر بخواهید که نعمتهای خدا را شماره کنید هرگز نتوانید، که خدا در حق بندگان بسیار آمرزنده و مهربان است.'),
(1920, 16, 19, 'و خدا هر چه را پنهان و آشکار دارید به همه آگاه است.'),
(1921, 16, 20, 'و آنچه که مشرکان غیر از خدا (چون بتها و فراعنه) به خدایی می‌خوانند چیزی خلق نکنند و خود نیز مخلوقند.'),
(1922, 16, 21, 'اینها مرده و از حیات بی‌بهره‌اند و هیچ حس و شعور ندارند که در چه هنگام مبعوث می‌شوند (و که آنها را برمی‌انگیزد).'),
(1923, 16, 22, 'خدای شما خدایی یکتاست، و آنان که به عالم آخرت ایمان ندارند (اگر هم به زبان گویند) به دل منکر (مبدأ متعال) اند و (از اطاعت حکم خدا) تکبر و سرپیچی می‌کنند.'),
(1924, 16, 23, 'محققا خدا بر کارهای باطن و ظاهر آنها آگاه است (و به کیفر اعمالشان می‌رساند) که او هرگز متکبران و گردنکشان را دوست نمی‌دارد.'),
(1925, 16, 24, 'و هرگاه به این مردم (مستکبر) گفته شود که خدایتان چه فرستاده؟ گویند: این آیات همه افسانه‌های پیشینیان است.'),
(1926, 16, 25, 'تا آنکه آنان (در نتیجه انکار روز قیامت) بار سنگین گناه خود را کاملا با برخی از بار گناه کسانی که از جهل گمراهشان کردند به دوش نهند، (عاقلان) آگاه باشند که آن بار وزر و گناه بسیار بد عاقبت است.'),
(1927, 16, 26, 'آن کافرانی که پیش از اینان بودند نیز (مانند اینها) مکرها (برای پامال کردن حق) اندیشیدند لیکن خدا بنای آنها را از پایه ویران کرد و سقف بر سرشان فرو ریخت و عذاب خدا از جایی که نمی‌فهمیدند آنها را فرا رسید.'),
(1928, 16, 27, 'آن گاه روز قیامت هم آنان را خوار و ذلیل خواهد کرد و آن روز خواهد گفت: کجایند شریکان من که برای طرفداری آنها خصومت و جنگ به پا می‌کردید؟ (در آن هنگام) اهل علم و دانش اظهار کنند که امروز ذلت و خواری و عذاب به کافران اختصاص دارد.'),
(1929, 16, 28, 'آنها که فرشتگان در حالی جانشان را می‌گیرند که (در دنیا) به نفس خود ستم کردند، در آن حال مرگ (اجبارا) سر تسلیم پیش دارند (و گویند) که ما ابدا کار بد نمی‌کردیم؛ چرا، خدا به هر چه می‌کردید آگاه است.'),
(1930, 16, 29, 'پس (فرشتگان خطاب کنند که) از هر در به دوزخ داخل شوید که در آنجا همیشه معذب خواهید بود. و جایگاه متکبران (که دوزخ است) بسیار بد منزلگاهی است.'),
(1931, 16, 30, 'و چون به اهل تقوا گفته شود که خدای شما چه آیاتی فرستاد؟ گویند: نیکو آیاتی فرستاد. آنان که نیکوکارند هم در حیات دنیا نیکی و خوشی بینند و هم دار آخرت (آنها را) بهترین منزل است و البته خانه اهل تقوا بسیار مجلّل و نیکو خواهد بود.'),
(1932, 16, 31, 'که باغهای بهشت عدن باشد که در زیر درختانش نهرها جاری است، متقیان در آن داخل شوند در حالی که هر چه بخواهند و میل کنند در آنجا بر ایشان حاضر است. باری این است اجر و جزایی که خدا به اهل تقوا عطا خواهد کرد.'),
(1933, 16, 32, 'آنان که در حال پاکیزگی (از شرک) فرشتگان قبض روحشان کنند به آنها گویند که شما به موجب اعمال نیکویی که در دنیا به جا آوردید اکنون به بهشت ابدی درآیید.'),
(1934, 16, 33, 'این کافران منکر جز آنکه فرشتگان (غضب خدا) بر سر آنها بیایند (و ترسان و لرزان شوند) یا (به هلاک آنان) حکم خدا در رسد دیگر چه انتظار می‌برند؟ منکران پیشین هم مانند اینان عمل کردند (که همه هلاک شدند) و خدا اصلا به آنها ستم نکرد بلکه آنها خود به نفس خویش ستم می‌کردند.'),
(1935, 16, 34, 'تا آنکه به کیفر اعمال زشت خود رسیدند و آن عذابی که به آن تمسخر می‌کردند آنها را احاطه کرد.'),
(1936, 16, 35, 'و مشرکان گویند: اگر خدا می‌خواست هرگز نه ما و نه پدرانمان چیزی جز آن خدای یکتا نمی‌پرستیدیم و بی‌خواست او چیزی را حرام نمی‌کردیم. مشرکان پیش از اینان هم کردار و گفتارشان همین بود (که به در جبر و انکار عمل خویش می‌زدند) پس در این صورت آیا برای رسولان ما جز تبلیغ رسالت (و اتمام حجت) تکلیفی هست؟'),
(1937, 16, 36, 'و همانا ما در میان هر امتی پیغمبری فرستادیم که خدای یکتا را پرستید و از بتان و فرعونان دوری کنید، پس بعضی مردم را خدا هدایت کرد و بعضی دیگر ضلالت و گمراهی بر آنان حتمی شد، اکنون در روی زمین گردش کنید تا بنگرید عاقبت آنان که (انبیاء را) تکذیب کردند به کجا رسید (و آنها چگونه هلاک ابدی شدند).'),
(1938, 16, 37, '(ای رسول ما) تو اگر چه بسیار حریص و مشتاق هدایت خلق هستی و لیکن (بدان که) خدا آنان را که (پس از اتمام حجت) گمراه کرده دیگر هدایت نکند و آنها را یاوری نخواهد بود.'),
(1939, 16, 38, 'و کافران با مبالغه و تأکید کامل به خدا قسم یاد می‌کنند که هرگز کسی که مرد خدا او را زنده نخواهد کرد (و قیامتی وجود نخواهد داشت) چرا، البته قیامت وعده حتمی خداست و لیکن اکثر مردم از آن آگاه نیستند.'),
(1940, 16, 39, 'تا (در آن روز محشر) آنچه را که در آن اختلاف می‌کنند آشکار و مبین گرداند و تا کافران کاملا به دروغ و اندیشه غلط خود آگاه شوند.'),
(1941, 16, 40, 'ما به امر نافذ خود هر چه را اراده کنیم همین که گوییم موجود باش، همان لحظه موجود خواهد شد.'),
(1942, 16, 41, 'و آنان که در راه خدا مهاجرت کردند پس از آنکه ستمها (در وطن خود از کافران) کشیدند ما در دنیا به آنها جایگاه آسایش و نیکو می‌دهیم در صورتی که اگر بدانند اجری که در آخرت به آنها عطا خواهیم کرد بزرگتر (و بسیار بهتر و نیکوتر) است.'),
(1943, 16, 42, '(این اجر بزرگ در دنیا و عقبی به) آن کسانی (عطا می‌شود) که (در راه دین) صبر کردند و بر خدای خود در کارها توکل می‌نمایند.'),
(1944, 16, 43, 'و ما پیش از تو غیر رجال مؤیّد به وحی خود کسی را به رسالت نفرستادیم، پس اگر نمی‌دانید از اهل ذکر (یعنی علما و دانشمندان هر امت) سؤال کنید.'),
(1945, 16, 44, '(ما هر رسولی را) با معجزات و کتب و آیات وحی فرستادیم و بر تو این ذکر (یعنی قرآن) را نازل کردیم تا برای مردم آنچه را که به آنان فرستاده شده بیان کنی و برای آنکه عقل و فکرت کار بندند.'),
(1946, 16, 45, 'آنان که بر کردار زشت خود مکرها می‌اندیشند آیا از این بلا ایمنند که خدا ناگاه همه را به زمین فرو برد یا از جایی که پی نبرند عذابی به آنان رسد؟'),
(1947, 16, 46, 'یا آنکه در سفر که سرگرم رفت و آمدند ناگاه به مؤاخذه سخت بگیرد؟ و آنان البته (بر قدرت حق) غالب نتوانند شد.'),
(1948, 16, 47, 'یا آنکه دایم آنها را به حال ترس بگیرد (خدا که این زشتکاران را به این انواع بلاها گرفتار نکرد بدین جهت است) که پروردگار شما بسیار مشفق و مهربان است.'),
(1949, 16, 48, 'آیا چشم نگشودند تا ببینند که هر موجودی که خدا آفریده چگونه آثار وجودی و سایه‌های آن به هر جانب از راست و چپ (مستقیم یا غیر مستقیم) روانه می‌شود و همه به سجده خدا با کمال فروتنی مشغولند؟'),
(1950, 16, 49, 'و هر چه در آسمانها و زمین است از جنبندگان و نیز فرشتگان همه بی‌هیچ تکبر به سجده و عبادت خدا مشغولند.'),
(1951, 16, 50, 'و از قهر خدا که فوق همه آنهاست می‌ترسند و هر چه مأمورند اطاعت می‌کنند.'),
(1952, 16, 51, 'و خدای یکتای عالم فرموده که به راه شرک و دو خدایی نروید، که خدا یکی است پس تنها از من بترسید و بس.'),
(1953, 16, 52, 'و هر چه در آسمانها و زمین است همه ملک خداست و دین و اطاعت همیشه مخصوص اوست، آیا شما بندگان از کسی غیر از خدای مقتدر می‌ترسید؟'),
(1954, 16, 53, 'و شما بندگان با آنکه هر نعمت که دارید همه از خداست و چون بلایی به شما رسد به درگاه او پناه جسته و به او (در رفع بلا) استغاثه می‌کنید.'),
(1955, 16, 54, 'باز وقتی که بلا را از سر شما رفع کرد گروهی از شما به خدای خود شرک می‌آورند.'),
(1956, 16, 55, 'تا با وجود آن همه نعمت که به آنها دادیم باز به راه کفر و کفران روند. باری (این دو روزه دنیا) به کامرانی بپردازید که به زودی خواهید دانست.'),
(1957, 16, 56, 'و این مشرکان برای بتان از روی جهل نصیبی از رزقی که ما به آنها دادیم قرار می‌دهند [مثل آنچه در سوره انعام آیه 136 آمده‌]، سوگند به خدای یکتا که البته از این دروغ (و عقاید باطل) بازخواست خواهید شد.'),
(1958, 16, 57, 'و این مشرکان، فرشتگان را با آنکه خدا منزه از فرزند است دختران خدا دانسته و حال آنکه برای خود پسران را که خوشایندشان است قرار می‌دهند.'),
(1959, 16, 58, 'و چون یکی از آنها را به فرزند دختری مژده آید (از شدت غم و حسرت) رخسارش سیاه شده و سخت دلتنگ و خشمگین می‌شود.'),
(1960, 16, 59, 'و از عار این مژده روی از قوم خود پنهان می‌دارد (و به فکر می‌افتد) که آیا آن دختر را با ذلت و خواری نگه دارد و یا زنده به خاک گور کند؟ آگاه باشید که آنها بسیار بد حکم می‌کنند.'),
(1961, 16, 60, 'اوصاف کسانی که (به خدا و) به قیامت ایمان ندارند زشت است (و اعمالشان مانند زنده به گور کردن دختران هم از روی قساوت و جهالت و خودخواهی و بی‌رحمی است) اما خدا (و خداشناسان) را پسندیده و عالیترین اوصاف کمال است و او مقتدر و داناست.'),
(1962, 16, 61, 'و اگر خدا از ظلم و ستمگریهای خلق انتقام کشد جنبنده‌ای در زمین نخواهد گذاشت و لیکن او (عذاب) آنها را تأخیر می‌افکند تا وقتی معین (که مقتضای مصلحت و حکمت است) ولی آن گاه که اجل آنها در رسید دیگر یک لحظه پس و پیش نخواهند شد.'),
(1963, 16, 62, 'و این مشرکان آنچه را که بر خود نمی‌پسندند (چون داشتن فرزند دختر) به خدا نسبت می‌دهند و به دروغ می‌گویند که عاقبت نیکو دارند در صورتی که محققا کیفر آنها آتش دوزخ است و زودتر از دیگران هم به دوزخ می‌روند.'),
(1964, 16, 63, 'به خدا سوگند که ما رسولانی پیش از تو بر امم سابقه فرستادیم (تا مگر سعادت یابند) ولی شیطان اعمال (زشت) آنها را در نظرشان زیبا جلوه داد، پس امروز (یعنی روز محشر) شیطان یار آنهاست و به عذاب دردناک گرفتار خواهند بود.'),
(1965, 16, 64, 'و ما این کتاب را بر تو نفرستادیم مگر برای این که حقیقت را در آنچه (از توحید خدا و معاد و رسالت و غیره) مردم اختلاف می‌کنند روشن کنی و برای اهل ایمان هدایت و رحمت باشد.'),
(1966, 16, 65, 'و خدا از آسمان باران را فرستاد تا زمین را پس از مرگ زنده کرد، البته در این کار آیتی (از قدرت و حکمت الهی) بر آنان که بشنوند پدیدار است.'),
(1967, 16, 66, 'و البته برای شما ملاحظه حال چهار پایان (از شتر و گاو و گوسفند) همه عبرت و حکمت است، که ما از آنچه در شکم آن است از میان (دو جسم ناپاک) سرگین و خون، شما را شیر پاک می‌نوشانیم که در طبع همه نوشندگان گواراست.'),
(1968, 16, 67, 'و هم از میوه‌های درخت خرما و انگور که از آن نوشابه‌های شیرین و رزق حلال نیکو به دست آرید، که در این کار نیز آیتی (از قدرت حق) برای خردمندان پدیدار است.'),
(1969, 16, 68, 'و خدایت به زنبور عسل وحی کرد که از کوهها و درختان و سقفهای رفیع، منزل گیر.'),
(1970, 16, 69, 'و سپس از انواع میوه‌های شیرین (و از حلاوت و شهد گلهای خوشبو) تغذیه کن و راه پروردگارت را به اطاعت بپوی، آن‌گاه از درون آن، شربت شیرینی به رنگهای مختلف بیرون آید که در آن شفاء مردمان است. در این کار نیز آیتی (از قدرت خدا) برای متفکران پیداست.'),
(1971, 16, 70, 'و خدا شما را آفریده و سپس می‌میراند؛ و بعضی از شما را به سن انحطاط پیری می‌رسانند که هر چه دانسته همه را فراموش می‌کند. همانا خداست که همیشه دانا و تواناست.'),
(1972, 16, 71, 'و خدا رزق بعضی از شما را بر بعضی دیگر فزونی داده، آنان که رزقشان افزون شده (زیادی) رزقشان را به زیردستان و غلامان نمی‌دهند تا باهم مساوی شوند (لیکن مقام خدایی را به بتی یا بشری می‌دهند تا با خدا شریک و مساوی دانند، زهی جهل و ناسپاسی!) آیا نعمت ایمان به خدا را (به عصیان و شرک) باید انکار کنند؟!'),
(1973, 16, 72, 'و خدا از جنس خودتان برای شما جفتهایی آفرید و از آن جفتها پسران و دختران و دامادان و نوادگان بر شما خلق فرمود، و از نعمتهای پاکیزه لذیذ روزی داد؛ آیا مردم باز به باطل می‌گروند و به نعمت خدا کافر می‌شوند؟!'),
(1974, 16, 73, 'و بت‌پرستان خدا را (که نعمتهای بی‌شمار به آنها داده) نمی‌پرستند و به جای او بتهایی را پرستش می‌کنند که از آسمان و زمین مالک چیزی که روزی به مشرکان دهند اصلا نیستند و توانایی (بر هیچ کار) ندارند.'),
(1975, 16, 74, 'پس برای خدا مثل و مانند نشمارید (و به خدای یکتا بگروید) که خدا داناست و شما نادانید.'),
(1976, 16, 75, 'خدا مثلی زده (بشنوید): آیا بنده مملوکی که قادر بر هیچ چیز (حتی بر نفس خود) نیست با مردی آزاد که ما به او رزقی نیکو (و مال حلال بسیار) عطا کردیم که پنهان و آشکار هر چه خواهد از آن انفاق می‌کند، این دو یکسانند؟ (هرگز یکسان نیستند. مثل بت و خدا و بت‌پرست و خداپرست بدین مثال ماند). ستایش مخصوص خداست و لیکن اکثر مردم آگاه نیستند.'),
(1977, 16, 76, 'و خدا مثلی زده (بشنوید): دو نفر مرد یکی بنده‌ای باشد گنگ و از هر جهت عاجز و کلّ بر مولای خود که از هیچ راه، خیری به مالک خویش نرساند و دیگری مردی (آزاد و مقتدر) که به عدالت و احسان فرمان دهد و خود هم به راه مستقیم باشد، آیا این دو نفر یکسانند؟ (هرگز یکسان نیستند. مثل کافر و مؤمن بدین مثال ماند).'),
(1978, 16, 77, 'و تنها خدا بر غیب آسمانها و زمین آگاه است و بس، و کار ساعت قیامت (در سرعت و آسانی) مانند چشم بر هم زدن یا نزدیک‌تر بیش نیست، که خدا البته بر هر چیز تواناست.'),
(1979, 16, 78, 'و خدا شما را از بطن مادران بیرون آورد در حالی که هیچ نمی‌دانستید و به شما گوش و چشم و قلب اعطا کرد تا مگر (دانا شوید و) شکر (این نعمتها) به جای آرید.'),
(1980, 16, 79, 'آیا در مرغان هوا نمی‌نگرند که در جوّ آسمان مسخّرند و به جز خدا کسی نگهبان آنها نیست؟! در این امر برای اهل ایمان نشانه‌هایی (از قدرت حق) پدیدار است.'),
(1981, 16, 80, 'و خدا برای سکونت (دائم) شما منزلهایتان را مقرر داشت و (برای سکونت موقت سفر) از پوست و موی چهارپایان خیمه‌ها را برای شما قرار داد تا وقت حرکت و سکون سبک وزن و قابل انتقال باشد، و از پشم و کرک و موی آنها اثاثیه منزل (مانند گلیم و نمد و قالی) و متاع و اسباب زندگانی (و لباسهای فاخر) برای شما خلق فرمود که تا هنگامی معین از آن استفاده کنید.'),
(1982, 16, 81, 'و خدای عالم برای شما سایبانها از آنچه آفریده (از درختان و سقف و دیوار و کوهها) مهیّا ساخت و از غارهای کوه پوشش و اتاقها برایتان قرار داد (تا از سرما و گرما پناهی گیرید) و نیز لباسی که شما را از گرمای آفتاب (و سرمای زمستان) بپوشاند خلق کرد و نیز برای آنکه در جنگ محفوظ مانید لباسی (از آهن) مقرّر گردانید. این چنین نعمت‌های خود را بر شما تمام و کامل می‌کند تا مگر (منعم را بشناسید و) مطیع و تسلیم (امر او) باشید.'),
(1983, 16, 82, 'پس اگر باز روی بگردانند (ای رسول ما) بر تو تبلیغ روشن رسالت (و اتمام حجت) بیش نیست.'),
(1984, 16, 83, 'نعمت خدا را دانسته و شناخته باز انکار می‌کنند و اکثر اینان کافرند.'),
(1985, 16, 84, 'و (به یاد خلق آور) روزی که (در قیامت) ما از هر امتی (رسول و) شاهدی برانگیزیم آن‌گاه به کافران اجازه سخن و اعتذار داده نشود و نه توبه و انابه و عذری از آنان پذیرند.'),
(1986, 16, 85, 'و روزی که ستمکاران عذاب خدا را به چشم ببینند دیگر هیچ نه تخفیف عذاب و نه مهلت (آسایش) خواهند یافت.'),
(1987, 16, 86, 'و چون مشرکان شریکان خود را مشاهده کنند گویند: بارالها، اینانند که ما به جای تو پرستش می‌کردیم (آنها را که ما را فریب دادند به جای ما مجازات کن)، شریکان جواب دهند که شما البته دروغ می‌گویید (ما هرگز شما را به پرستش خود نخواندیم).'),
(1988, 16, 87, 'و در آن روز همه کفار و مشرکین تسلیم فرمان خدا شوند و هر چه غیر از خدا جعل کرده و می‌پرستیدند همه از نظرشان محو و نابود شود.'),
(1989, 16, 88, 'آنان که خود کافر شدند و راه خدا را نیز به روی خلق بستند ما آنها را به کیفر افساد و اخلال (در کار خلق) عذابی فوق عذاب (کافران دیگر) بیفزاییم.'),
(1990, 16, 89, 'و روزی که ما در میان هر امتی از (رسولان) خودشان گواهی بر آنان برانگیزیم و تو را (ای محمد) بر این امت گواه آریم (آن روز سخت را یاد کن و امت را متذکر ساز)، و ما بر تو این کتاب (قرآن عظیم) را فرستادیم تا حقیقت هر چیز را روشن کند و برای مسلمین هدایت و رحمت و بشارت باشد.'),
(1991, 16, 90, 'همانا خدا (خلق را) فرمان به عدل و احسان می‌دهد و به بذل و عطاء خویشاوندان امر می‌کند و از افعال زشت و منکر و ظلم نهی می‌کند و به شما پند می‌دهد، باشد که موعظه خدا را بپذیرید.'),
(1992, 16, 91, 'و چون با خدا (و رسول و بندگانش) عهدی بستید بدان عهد وفا کنید و هرگز سوگند و پیمان را که مؤکّد و استوار کردید مشکنید، چرا که خدا را بر خود ناظر و گواه گرفته‌اید؛ همانا خدا به هر چه می‌کنید آگاه است.'),
(1993, 16, 92, 'و (در مثل) مانند زنی که رشته خود را پس از تابیدن محکم وا تابید نباشید، که عهد و قسم‌های استوار و محکم خود را برای فریب یکدیگر و فسادکاری به کار برید برای آنکه قومی بر قوم دیگر تفوق دارند، زیرا خدا شما را به این عهد و قسمها می‌آزماید و در روز قیامت همه (تقلّبها و) اختلافات شما را بر شما آشکار خواهد ساخت.'),
(1994, 16, 93, 'و اگر خدا می‌خواست (به مشیّت ازلی) همه بشر را یک امت قرار می‌داد (و هیچ‌گونه اختلافات نژاد و زبان و ملیت در بشر نمی‌گذاشت) و لیکن (این اختلافات برای امتحان است و پس از امتحان) هر که را بخواهد به گمراهی وا می‌گذارد و هر که را بخواهد هدایت می‌کند، و البته آنچه می‌کرده‌اید از همه سؤال خواهید شد.'),
(1995, 16, 94, 'و عهد و سوگندهای خود را برای فریب یکدیگر به کار مبرید تا آن که ثابت قدم است نیز (به فریب و سوگند دروغ) بلغزد و از این که راه خدا را (بر خلق) بستید همه به سختی مبتلا شوید و به عذابی بزرگ گرفتار گردید.'),
(1996, 16, 95, 'و عهد خدا را به بهایی اندک معامله نکنید؛ که آنچه (از نعمت ابد) نزد خداست اگر بفهمید بسیار شما را بهتر است (از آن منفعتی که به نقض عهد بیابید).'),
(1997, 16, 96, 'آنچه نزد شماست همه نابود خواهد شد و آنچه نزد خداست (از ذخائر اعمال نیک و ثواب آخرت، تا ابد) باقی خواهد بود و البته اجری که به صابران بدهیم اجری است بسیار بهتر از عملی که به جا می‌آوردند.'),
(1998, 16, 97, 'هر کس از مرد و زن کار نیکی به شرط ایمان به خدا به جای آرد ما او را به زندگانی خوش و با سعادت زنده ابد می‌گردانیم و اجری بسیار بهتر از عمل نیکی که می‌کردند به آنان عطا می‌کنیم.'),
(1999, 16, 98, '(ای رسول ما) چون خواهی تلاوت قرآن کنی اول از شر وسوسه شیطان مردود به خدا پناه بر.'),
(2000, 16, 99, 'که البته شیطان را هرگز بر کسانی که به خدا ایمان آورده و بر پروردگارشان توکل و اعتماد می‌کنند تسلط نخواهد بود.'),
(2001, 16, 100, 'تنها تسلط شیطان بر آن نفوسی است که او را دوست گرفته‌اند و کسانی که (به اغوای او) به خدا شرک می‌آورند.'),
(2002, 16, 101, 'و ما هرگاه آیتی را (از راه مصلحت) نسخ کرده و به جای آن آیتی دیگر آوریم-در صورتی که خدا بهتر داند تا چه چیز (به صلاح خلق) نازل کند- (کافران اعتراض بر نسخ آیه کرده و) می‌گویند تو (بر خدا) همیشه افترا می‌بندی. (چنین نیست) بلکه اکثر اینها نمی‌دانند (که این نزول آسمانی است).'),
(2003, 16, 102, 'بگو که این آیات را روح القدس از جانب پروردگارت به حقیقت و راستی نازل کرده تا اهل ایمان را ثابت قدم گرداند و برای مسلمین هدایت و بشارت باشد.'),
(2004, 16, 103, 'و ما کاملا آگاهیم که کافران (معاند) می‌گویند: آن کس که مطالب این قرآن را به رسول می‌آموزد بشری است، (مقصودشان بلعام ترسا یا عایش یا یعیش یا غلام رومی یا سلمان فارسی بود) در صورتی که زبان آن کس که این قرآن را به او نسبت می‌دهند اعجمی (غیر فصیح) است و این قرآن به زبان عربی فصیح و روشن است.'),
(2005, 16, 104, 'البته آنان که به آیات خدا ایمان نمی‌آورند (پس از اتمام حجت کامل) خدا هرگز هدایتشان نخواهد کرد و آنها را عذابی دردناک خواهد رسید.'),
(2006, 16, 105, 'دروغ را آن کسانی به خدا می‌بندند که ایمان به آیات خدا ندارند و این کافران البته خود مردمی دروغگویند.'),
(2007, 16, 106, 'هر کس بعد از آنکه به خدا ایمان آورده باز کافر شد-نه آنکه به زبان از روی اجبار کافر شود و دلش در ایمان ثابت باشد (مانند عمار یاسر) بلکه به اختیار کافر شد و با رضا و رغبت و هوای نفس، دلش آکنده به ظلمت کفر گشت-بر آنها خشم و غضب خدا و عذاب بزرگ دوزخ خواهد بود.'),
(2008, 16, 107, 'این غضب و عذاب بر آنها بدین سبب است که حیات (فانی) دنیا را بر (حیات ابدی) آخرت برگزیدند و این که خدا هرگز کافران را هدایت نخواهد کرد.'),
(2009, 16, 108, 'همین‌ها هستند که خدا بر دلها و گوش و چشمهایشان مهر (قهر) زده است و اینها همان مردمند که (از خدا و قیامت) غافلند.'),
(2010, 16, 109, 'بی‌گمان آنان در عالم آخرت بسیار زیانکارند.'),
(2011, 16, 110, 'آن‌گاه محققا خدا بر مؤمنانی که از شهر و دیار خود چون به شر و فتنه کفار مبتلا شدند ناگزیر هجرت کردند و در راه دین جهاد و صبر بسیار نمودند، آری خدا از این پس بر آنها بسیار آمرزنده و مهربان خواهد بود.'),
(2012, 16, 111, '(یاد کن بر امت) آن روزی که (از شدت ابتلاء و سختی) هر نفسی می‌آید در حالی که (برای رفع عذاب) به جدل و دفاع از خود برمی‌خیزد، و هر کس را به جزاء عمل او کاملا می‌رسانند و بر آنها ستم نمی‌کنند.'),
(2013, 16, 112, 'و خدا بر شما حکایت کرد و مثل آورد مثل شهری را که در آن امنیت کامل حکمفرما بود و اهلش در آسایش و اطمینان زندگی می‌کردند و از هر جانب روزی فراوان به آنها می‌رسید تا آنکه اهل آن شهر نعمتهای خدا را کفران کردند، خدا هم به موجب آن کفران و معصیت طعم گرسنگی و بیمناکی را به آنها چشانید و چون لباس، سراپای وجودشان را پوشاند.'),
(2014, 16, 113, 'و رسولی از خود آنها بر آنها آمد ولی او را تکذیب کردند، و عذاب بر آنها فرا رسید در حالی که ستمکار بودند.'),
(2015, 16, 114, 'پس شما (مؤمنان از حال آنها عبرت گیرید و) از آنچه خدا روزی حلال و طیّب شما قرار داده تناول کنید و شکر نعمت خدا به جای آرید اگر حقیقتا خدا را می‌پرستید.'),
(2016, 16, 115, 'خدا بر شما بندگان تنها مردار و خون و گوشت خوک و آنچه را که به نام غیر خدا ذبح کنند حرام گردانید، و اگر کسی مضطر و ناچار شود بی‌آنکه قصد تجاوز (از اندازه) و تعدی (از حکم خدا) نماید (باز از همین حرامها به قدر ضرورت تناول کند) خدا البته بسیار آمرزنده و مهربان است.'),
(2017, 16, 116, 'و شما نباید از پیش خود به دروغ چیزی را حلال و چیزی را حرام گویید و به خدا نسبت دهید تا بر خدا دروغ بندید، که آنان که بر خدا دروغ بندند هرگز روی رستگاری نخواهند دید.'),
(2018, 16, 117, '(آنچه از این طریق به دست آورند) متاعی قلیل است و (در آخرت) عذابی دردناک خواهند داشت.'),
(2019, 16, 118, 'و ما همان چیزها که بر تو پیش از این شرح کردیم (از مردار و خون و خنزیر و ذبیحه به نام غیر خدا) بر یهودیان حرام کردیم، و ما بر آنان ستم نکردیم لیکن آنها خود بر نفس خویش ستم می‌کردند (که حلال خدا را حرام کردند).'),
(2020, 16, 119, 'باز هم خدا بر آنان که از روی جهالت و نادانی عمل زشتی انجام داده و سپس به درگاه او توبه کرده و (فساد آن عمل زشت را) اصلاح کنند، بعد از توبه خدا بسیار آمرزنده و مهربان است.'),
(2021, 16, 120, 'همانا ابراهیم (خلیل) امتی مطیع و فرمانبردار و یکتاپرست بود و هرگز به خدای یکتا شرک نیاورد.'),
(2022, 16, 121, 'همیشه شکرگزار نعمتهای خدا بود که خدا او را (به رسالت) برگزید و به راه مستقیمش هدایت فرمود.'),
(2023, 16, 122, 'و او را در دنیا نیکویی (و سعادت) عطا کردیم و در آخرت از صالحان و نیکان خواهد بود.'),
(2024, 16, 123, 'آن گاه بر تو وحی کردیم که (در دعوت به خداپرستی و توحید و بسط معارف الهی) آیین ابراهیم را تعقیب کن که پاک و یکتا پرست بود و هرگز به خدای یکتا شرک نیاورد.'),
(2025, 16, 124, '(حکم حرمت) روز شنبه تنها بر یهود که در آن راه اختلاف پیمودند (و حرمتش نگاه نداشتند) مقرر گردید، و خدای تو البته روز قیامت در آنچه (خلق) در آن اختلاف و نزاع برپا می‌کردند حکم خواهد کرد.'),
(2026, 16, 125, '(ای رسول ما خلق را) به حکمت (و برهان) و موعظه نیکو به راه خدایت دعوت کن و با بهترین طریق با اهل جدل مناظره کن (وظیفه تو بیش از این نیست) که البته خدای تو (عاقبت حال) کسانی را که از راه او گمراه شده و آنان را که هدایت یافته‌اند بهتر می‌داند.'),
(2027, 16, 126, 'و اگر به شما مسلمانان کسی عقوبت و ستمی رسانید شما باید به قدر آن در مقابل انتقام کشید (نه بیشتر) و اگر صبوری کنید البته برای صابران بهتر خواهد بود.'),
(2028, 16, 127, 'و تو (ای رسول) صبر و تحمل پیشه کن که صبر تو تنها به (توفیق) خداست، و بر آنها (که ترک کفر و عناد نمی‌کنند) غمگین مشو و از مکر و حیله آنان دلتنگ مباش.'),
(2029, 16, 128, 'محققا خدا یار و یاور متقیان و نیکوکاران عالم است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاک و منزه است خدایی که در (مبارک) شبی بنده خود (محمّد) را از مسجد حرام (مکّه معظّمه) به مسجد اقصایی که پیرامونش را مبارک و پر نعمت ساختیم سیر داد تا آیات و اسرار غیب خود را به او بنماییم که او (خدا) به حقیقت شنوا و بیناست.'),
(2031, 17, 2, 'و به موسی کتاب (تورات) را فرستادیم و آن را وسیله هدایت بنی اسرائیل قرار دادیم (و گفتیم) که غیر من هیچ کس را حافظ و کارساز فرا نگیرید.'),
(2032, 17, 3, '(ای) فرزندان کسانی که به کشتی نوحشان بردیم (و از هلاک نجاتشان دادیم، چنانکه) نوح بسیار بنده شکرگزاری بود (شما هم مانند او شاکر باشید تا نجات یابید).'),
(2033, 17, 4, 'و در کتاب (تورات، یا در لوح محفوظ و کتاب تکوین الهی) خبر دادیم و چنین مقدر کردیم که شما بنی اسرائیل دو بار حتما در زمین فساد و خونریزی می‌کنید و تسلط و سرکشی سخت ظالمانه می‌یابید (یک بار به قتل اشعیا و مخالفت ارمیا، و بار دیگر به قتل زکریا و یحیی).'),
(2034, 17, 5, 'پس چون وقت انتقام اول فرا رسد بندگان سخت جنگجو و نیرومند خود را (چون بخت النصر) بر شما برانگیزیم تا آنجا که در درون خانه‌های شما نیز جستجو کنند. و این وعده (انتقام خدا) حتمی است.'),
(2035, 17, 6, 'آن‌گاه شما را به روی آنها برگردانیم و بر آنها غلبه دهیم و به مال و فرزندان مدد بخشیم و عدّه (جنگجویان) شما را بیشتر (از دشمن) گردانیم.'),
(2036, 17, 7, 'اگر نیکی و احسان کردید به خود کرده‌اید و اگر بدی و ستم کردید باز به خود کرده‌اید. و آن‌گاه که وقت (انتقام ظلم) دیگر شما (که کشتن یحیی و زکریاست یا عزم قتل عیسی) فرا رسید (باز بندگانی قوی و جنگ آور را بر شما مسلط می‌کنیم) تا اثر بیچارگی و خوف و اندوه بر رخسار شما نشانند و به مسجد بیت المقدس (معبد بزرگ شما) مانند بار اول درآیند و به هر چه رسند نابود سازند.'),
(2037, 17, 8, 'امید است خدا به شما (اگر توبه کرده و صالح شوید) باز مهربان گردد و اگر (به عصیان و ستمگری) برگردید ما هم (به عقوبت و مجازات شما) بازمی‌گردیم و جهنم را زندان کافران قرار داده‌ایم.'),
(2038, 17, 9, 'همانا این قرآن (خلق را) به راست و استوارترین طریقه هدایت می‌کند و اهل ایمان را که نیکوکار باشند به اجر و ثواب بزرگ بشارت می‌دهد.'),
(2039, 17, 10, 'و بر آنان که به عالم آخرت ایمان نمی‌آورند البته عذاب دردناک مهیا ساخته‌ایم.'),
(2040, 17, 11, 'انسان با شوق و رغبتی که خیر و منفعت خود را می‌جوید (چه بسا به نادانی) با همان شوق و رغبت شر و زیان خود را می‌طلبد، و انسان بسیار بی‌صبر و شتاب‌کار است.'),
(2041, 17, 12, 'و ما شب و روز را دو آیت و نشانه‌ای (از قدرت خود) قرار دادیم آن‌گاه آیت شب (روشنی ماه) را زدودیم و آیت روز (خورشید) را تابان ساختیم تا شما در روز (روزی حلال) از فضل خدا طلب کنید و تا آنکه شمار سالها و حساب (اوقات) را عموما بدانید و ما (برای پند و صلاح بندگان) هر چیزی را مفصل بیان کردیم.'),
(2042, 17, 13, 'و ما مقدرات و نتیجه اعمال هر انسانی را طوق گردن او ساختیم (که ملازم و قرین همیشگی او باشد) و روز قیامت کتابی (که نامه اعمال اوست) بر او بیرون آریم در حالی که آن نامه چنان باز باشد که همه اوراق آن را یک مرتبه ملاحظه کند.'),
(2043, 17, 14, 'تو خود کتاب اعمالت را بخوان (و بنگر تا در دنیا چه کرده‌ای) که تو خود تنها برای رسیدگی به حساب خویش کافی هستی.'),
(2044, 17, 15, 'هر کس راه هدایت یافت تنها به نفع و سعادت خود یافته، و هر که به گمراهی شتافت آن هم به زیان و شقاوت خود شتافته، و هیچ کس بار گناه دیگری را به دوش نگیرد، و ما تا رسول نفرستیم (و بر خلق اتمام حجّت نکنیم) هرگز کسی را عذاب نخواهیم کرد.'),
(2045, 17, 16, 'و ما چون اهل دیاری را بخواهیم به کیفر گناه هلاک سازیم پیشوایان و متنعّمان آن شهر را امر کنیم (به طاعت، لیکن) آنها راه فسق (و تبهکاری و ظلم) در آن دیار پیش گیرند (و مردم هم به راه آنها روند) پس آنجا تنبیه و عقاب لزوم خواهد یافت، آن گاه همه را هلاک می‌سازیم.'),
(2046, 17, 17, 'و چه بسیاری از ملل و اقوامی را بعد از نوح هلاک کردیم و تنها خدای تو که بر گناهان بندگانش آگاه و با خبر است کفایت می‌کند (که به مصلحت هر که را خواهد عفو و هر که را خواهد عقاب کند).'),
(2047, 17, 18, 'هر کس متاع عاجل و زودگذر دنیا را طالب است متاع دنیا را به او می‌دهیم (لیکن باز) به هر که خواهیم و هر چه مشیّت ازلی ما باشد، سپس (در عالم آخرت به کیفر کردارش) دوزخ را نصیب او کنیم که با نکوهش و مردودی به جهنم درآید.'),
(2048, 17, 19, 'و هر که طالب حیات آخرت باشد و برای آن به قدر لزوم و طاقت بکوشد البته به شرط ایمان (به خدا) سعی چنین کسانی مقبول و مأجور خواهد بود.'),
(2049, 17, 20, 'و ما به هر دو فرقه (از دنیا طلبان و آخرت طلبان) به لطف پروردگارت مدد خواهیم داد، که لطف و عطای پروردگار تو از هیچ‌کس دریغ نخواهد شد.'),
(2050, 17, 21, 'بنگر تا ما چگونه (در دنیا) بعضی مردم را بر بعضی فضیلت و برتری بخشیدیم (تا بدانی که) البته مراتب آخرت بسیار بیش از درجات دنیاست و برتری خلایق بر یکدیگر به مراتب افزون از حد تصور است.'),
(2051, 17, 22, 'هرگز با خدای یکتا شرک و شریک میاور و گر نه به نکوهش و خذلان ابدی خواهی نشست.'),
(2052, 17, 23, 'و خدای تو حکم فرموده که جز او را نپرستید و درباره پدر و مادر نیکویی کنید و چنانکه یکی از آنها یا هر دو در نزد تو پیر و سالخورده شوند (که موجب رنج و زحمت تو باشند) زنهار کلمه‌ای که رنجیده خاطر شوند مگو و بر آنها بانگ مزن و آنها را از خود مران و با ایشان به اکرام و احترام سخن‌گو.'),
(2053, 17, 24, 'و همیشه پر و بال تواضع و تکریم را با کمال مهربانی نزدشان بگستران و بگو: پروردگارا، چنانکه پدر و مادر، مرا از کودکی (به مهربانی) بپروردند تو در حق آنها رحمت و مهربانی فرما.'),
(2054, 17, 25, 'خدایتان به آنچه در دلهای شماست داناتر است، اگر همانا در دل اندیشه صلاح دارید خدا هر که را با نیّت پاک به درگاه او تضرع و توبه کند البته خواهد بخشید.'),
(2055, 17, 26, 'و (ای رسول ما) تو خود حقوق خویشاوندان و ارحام خود را ادا کن و نیز فقیران و رهگذران بیچاره را به حق خودشان برسان و هرگز (در کارها) اسراف روا مدار.'),
(2056, 17, 27, 'که مبذّران و مسرفان برادران شیطانهایند، و شیطان است که سخت کفران (نعمت) پروردگار خود کرد.'),
(2057, 17, 28, 'و چنانچه از ارحام و فقیران ذوی الحقوق مذکور چون فعلا نادار هستی ولی در آتیه به لطف خدا میدواری اکنون اعراض کرده و توجه به حقوقشان نتوانی کرد باز گفتار خوش به آنان بگو.'),
(2058, 17, 29, 'و نه هرگز دست خود (در احسان به خلق) محکم به گردنت بسته‌دار، و نه بسیار باز و گشاده‌دار، که (هر کدام کنی) به نکوهش و درماندگی خواهی نشست.'),
(2059, 17, 30, 'همانا خدای تو هر که را خواهد روزی وسیع دهد و هر که را خواهد تنگ روزی گرداند، که او به (صلاح کار) بندگان خود کاملا آگاه و بصیر است.'),
(2060, 17, 31, 'هرگز فرزندان خود را از ترس فقر به قتل مرسانید (زنده به گور مکنید) که ما رازق آنها و شما هستیم، زیرا این قتل فرزندان بسیار گناه بزرگی است.'),
(2061, 17, 32, 'و هرگز به عمل زنا نزدیک نشوید، که کاری بسیار زشت و راهی بسیار ناپسند است.'),
(2062, 17, 33, 'و هرگز نفس محترمی که خدا قتلش را حرام کرده مکشید مگر آنکه به حکم حق مستحق قتل شود، و کسی که خونش به مظلومی و ناحق ریخته شود ما به ولیّ او حکومت و تسلط (بر قاتل) دادیم پس (در مقام انتقام) آن ولی در قتل و خونریزی اسراف نکند که او از جانب ما مؤید و منصور خواهد بود.'),
(2063, 17, 34, 'و هرگز به مال یتیم نزدیک نشوید مگر آنکه راه خیر و طریق بهتری (به نفع یتیم) منظور دارید، تا آنکه به حد بلوغ و رشد خود برسد، و همه به عهد خود باید وفا کنید که البته (در قیامت) از عهد و پیمان سؤال خواهد شد.'),
(2064, 17, 35, 'و هر چه را به پیمانه (یا وزن) می‌سنجید تمام بپیمایید (و کم و گران نفروشید) و (همه چیز را) با ترازوی درست و عادلانه بسنجید، که این کاری بهتر و عاقبتش نیکوتر است.'),
(2065, 17, 36, 'و (ای انسان) هرگز آنچه را که بدان علم و اطمینان نداری دنبال مکن که چشم و گوش و دل همه مسئولند.'),
(2066, 17, 37, 'و هرگز در زمین به کبر و ناز مرو و غرور و نخوت مفروش، که به نیرو زمین را نتوانی شکافت و به کوه در سربلندی نخواهی رسید.'),
(2067, 17, 38, 'که این قبیل کارها و اندیشه‌های بد گناهش نزد خدا ناپسند است.'),
(2068, 17, 39, 'اینها پاره‌ای از حکمتهاست که پروردگارت به تو وحی کرد، و هرگز با خدای یکتا کسی را به خدایی مپرست و گر نه ملامت زده و مردود به دوزخ درخواهی افتاد.'),
(2069, 17, 40, 'آیا خدا شما را به فرزندان پسر برگزیده و خود از فرشتگان دخترانی برگرفته است؟! این رأی و گفتار شما (مشرکان، افترایی عظیم و گناهی) بسیار بزرگ است.'),
(2070, 17, 41, 'و ما در این قرآن انواع سخنان را نیکو بیان کردیم تا خلق متذکر شوند و از آن پند گیرند و لیکن بدان را به جز نفرت حاصلی نمی‌افزاید.'),
(2071, 17, 42, 'بگو: اگر با خدای یکتا چنانکه آنها می‌گویند خدایان دیگری بود در این صورت آن خدایان بر خدای عرش (به تنازع) راه می‌گرفتند (و تمانع آنان موجب اختلال نظام عالم می‌شد. پس چون عالم را نظام ثابتی است دلیل است که با خدای یگانه خدایان دیگری نیست).'),
(2072, 17, 43, 'خدا از آنچه (مشرکان نادان) می‌گویند بسیار برتر و منزه‌تر است.'),
(2073, 17, 44, 'هفت آسمان و زمین و هر که در آنهاست همه به ستایش و تنزیه خدا مشغولند و موجودی نیست در عالم جز آنکه ذکرش تسبیح و ستایش حضرت اوست و لیکن شما تسبیح آنها را فهم نمی‌کنید. همانا او بسیار بردبار و آمرزنده است.'),
(2074, 17, 45, 'و چون تو قرآن تلاوت کنی ما میان تو و آنها که به قیامت ایمان ندارند حجابی بداریم که آنها از فهم حقایق آن دور و مستور مانند.'),
(2075, 17, 46, 'و ما بر دلهای تیره آن کافران پرده‌ای افکندیم که قرآن را فهم نکنند و در گوشهایشان هم سنگینی نهادیم، و چون تو در قرآن خدایت را به وحدانیت و یگانگی یاد کنی آنان روی گردانیده و به پشت گریزان می‌شوند.'),
(2076, 17, 47, 'ما به خیالات باطلی که وقت شنیدن گفتار تو در دل خود می‌کنند (از خود آنها) آگاه‌تریم و آن‌گاه که به رازگویی می‌پردازند و آن مردم ستمکار گویند که شما جز شخصی مفتون و سحرزده را پیروی نمی‌کنید.'),
(2077, 17, 48, 'بنگر تا چه نسبتها به تو می‌دهند! پس گمراه شدند و هیچ راه خلاصی نمی‌یابند.'),
(2078, 17, 49, 'و (کافران به معاد) گفتند: آیا ما چون استخوان پوسیده و غبار پراکنده شویم باز روزی از نو زنده و برانگیخته خواهیم شد؟!'),
(2079, 17, 50, 'بگو که شما سنگ باشید یا آهن.'),
(2080, 17, 51, 'یا خلقتی (سخت‌تر از سنگ و آهن) از آنچه در دلتان بزرگ می‌نماید (باز به امر خدا زنده می‌شوید)، پس خواهند گفت: که ما را دوباره زنده می‌کند؟ بگو: همان خدایی که هم اول بار شما را آفرید؛ آن‌گاه آنها سر به سوی تو جنبانده گویند: پس این وعده کی خواهد بود؟ بگو: شاید که از حوادث نزدیک باشد (زیرا مرگ به هر کس نزدیک است و قیامتش به مرگ برپا می‌شود).'),
(2081, 17, 52, 'روزی که خدا شما را بخواند و شما (سر از خاک بیرون کرده و) با حمد و ستایش او را اجابت کنید و تصور می‌کنید که جز اندک زمانی (در گورها) درنگ نکرده‌اید.'),
(2082, 17, 53, 'و بندگانم را بگو که همیشه سخن بهتر را بر زبان آرند، که شیطان (بسیار شود به یک کلمه زشت) میان آنان دشمنی و فساد برمی‌انگیزد، زیرا دشمنی شیطان با آدمیان واضح و آشکار است.'),
(2083, 17, 54, 'خدا صلاح حال شما را بهتر می‌داند، اگر بخواهد (و صلاح بداند) به شما لطف و مهربانی کند یا اگر بخواهد (و مصلحت باشد) مجازات و عذاب فرماید، و ما تو را وکیل و نگهبان بندگان نفرستاده‌ایم (بر تو تنها رسالت و اتمام حجت است).'),
(2084, 17, 55, 'و خدای تو به آن که در آسمانها و زمین است داناتر (و به قابلیت هر موجودی آگاه‌تر) است، و همانا ما بعضی از انبیاء را بر بعضی دیگر برتری دادیم و به داوود زبور را عطا کردیم.'),
(2085, 17, 56, 'بگو: این بتها را که به جز خدا (مؤثر در حوائج خود) می‌پندارید بخوانید تا ببینید که نه دفع ضرری از شما توانند کرد و نه تغییر حالی به شما توانند داد.'),
(2086, 17, 57, 'آنهایی که (کافران به خدایی) می‌خوانند آنان خود به درگاه خدا وسیله تقرب می‌جویند، تا که مقرب‌تر باشد؟ و امیدوار به رحمت و ترسان از عذاب اویند، که البته از عذاب پروردگارت باید سخت هراسان بود.'),
(2087, 17, 58, 'و هیچ شهر و دیاری نیست جز آنکه ما پیش از ظهور روز قیامت اهل آن شهر را یا هلاک کرده یا به عذاب سخت معذب می‌کنیم. این حکم در کتاب (علم ازلی حق) مسطور است.'),
(2088, 17, 59, 'و ما را از فرستادن آیات و معجزات (دلخواه آنان) جز تکذیب پیشینیان چیزی مانع نبود، و به ثمود (قوم صالح) آن ناقه را که آیتی روشن بود (و همه مشاهده کردند) بدادیم، اما درباره آن ظلم کردند (و ناقه را پی نمودند)، و ما معجزات و آیات را جز برای آنکه (مردم از خدا) بترسند (و هدایت شوند) نمی‌فرستیم.'),
(2089, 17, 60, 'و (ای رسول ما، به یاد آر) وقتی که به تو گفتیم خدا البته به (همه افعال و افکار) مردم محیط است، و ما رؤیایی را که به تو ارائه دادیم و درختی را که به لعن در قرآن یاد شده (درخت نژاد بنی امیه و همه ظالمان عالم) قرار ندادیم جز برای آزمایش و امتحان مردم. و ما آنها را می‌ترسانیم و لیکن آیات بر آنها (از فرط شقاوت) جز طغیان بزرگ چیزی نیفزاید.'),
(2090, 17, 61, 'و (یاد آر) وقتی که فرشتگان را امر به سجده آدم کردیم پس همه سجده کردند جز شیطان که گفت: آیا من سجده به آدمی که از گل آفریدی فرود آرم؟!'),
(2091, 17, 62, 'گفت: با من بگو آیا این آدم خاکی را بر من فضیلت و برتری دادی؟ (ای خدا) اگر اجل مرا تا قیامت به تأخیر افکنی به جز قلیلی (چون معصومین و خواص مؤمنین) همه اولاد آدم را مهار می‌کنم (و به دار هلاک می‌کشانم).'),
(2092, 17, 63, 'خدا به شیطان گفت: برو که هر کس از اولاد آدم پیرو تو گردد با تو به دوزخ که پاداش کامل شماست کیفر خواهد شد.'),
(2093, 17, 64, '(برو) و هر که را توانستی با آواز خود تحریک کن و به لغزش افکن، و با جمله لشکر سوار و پیاده‌ات بر آنها بتاز و در اموال و اولاد هم با ایشان شریک شو و به آنها وعده (های دروغ و فریبنده) بده، و (ای بندگان بدانید که) وعده شیطان چیزی جز غرور و فریب نخواهد بود.'),
(2094, 17, 65, 'همانا تو را بر بندگان (خاص) من تسلط نیست و تنها محافظت و نگهبانی خدای تو (ای رسول، از شیطان آنها را) کافی است.'),
(2095, 17, 66, 'پروردگار شما همان است که به دریا برای شما کشتی‌ها را به حرکت می‌آورد تا از فضل خدا (یعنی روزی) طلبید، که او درباره شما بسیار مهربان است.'),
(2096, 17, 67, 'و چون در دریا به شما خوف و خطری رسد در آن حال به جز خدا همه آنهایی که به خدایی می‌خوانید از یاد شما بروند، و آن‌گاه که خدا شما را به ساحل سلامت رسانید باز از خدا روی می‌گردانید، و انسان بسیار کفر کیش و ناسپاس است.'),
(2097, 17, 68, 'آیا مگر ایمنید از این که شما را در خشکی زمین فرو برد یا (تندباد غضبش) بر سرتان سنگ بارد، آن‌گاه بر خود پناه و نگهبانی نیابید؟!'),
(2098, 17, 69, 'یا مگر از آن ایمنید که بار دیگر خدا شما را به اراده خود به دریا برگرداند و تندبادی بفرستد تا همه را به کیفر کفر به دریا غرق سازد؟ آن‌گاه کسی را از (قهر) ما بدان غرقه کردن دادخواه و فریادرس خود نیابید؟!'),
(2099, 17, 70, 'و محققا ما فرزندان آدم را بسیار گرامی داشتیم و آنها را (بر مرکب) در برّ و بحر سوار کردیم و از هر غذای لذیذ و پاکیزه آنها را روزی دادیم و آنها را بر بسیاری از مخلوقات خود برتری و فضیلت کامل بخشیدیم.'),
(2100, 17, 71, '(به یاد آر) روزی که ما هر گروهی از مردم را با پیشوایشان (به پیشگاه حقیقت) می‌خوانیم، پس هر کس نامه عملش را به دست راست دهند آنها نامه خود را قرائت کنند و کمترین ستمی به آنها نخواهد شد.'),
(2101, 17, 72, 'هر کس در این جهان نابینا (ی کفر و جهل و ضلالت) است در عالم آخرت نیز (از مشاهده بهشتیان و شهود و جمال حق) نابینا و گمراه‌تر خواهد بود.'),
(2102, 17, 73, 'و نزدیک بود که کافران تو را فریب داده و از آنچه به تو وحی کردیم (که به بتان هیچ اعتنا مکن) غافل شوی و چیز دیگر بر ما فرابندی، آن‌گاه یقینا مشرکان تو را دوست خود می‌گرفتند.'),
(2103, 17, 74, 'و اگر ما تو را ثابت قدم نمی‌گردانیدیم نزدیک بود که به آن مشرکان اندک تمایل و اعتمادی پیدا کنی.'),
(2104, 17, 75, 'و در آن صورت به تو جزاء این عمل را می‌چشاندیم و عذاب تو را در حیات دنیا و پس از مرگ مضاعف می‌گردانیدیم و آن‌گاه از (قهر و خشم) ما بر خود هیچ یاوری نمی‌یافتی.'),
(2105, 17, 76, 'و نزدیک بود که کافران تو را از سرزمین خود جنبانده تا از آنجا بیرونت کنند، و در این صورت پس از آن اندک زمانی بیش زیست نمی‌کردند (که به عذاب ما همه هلاک می‌شدند).'),
(2106, 17, 77, 'ما آیین همه پیمبرانی را که پیش از تو فرستادیم نیز همین (توحید و خداپرستی و هلاک کافران و نجات اهل ایمان) قرار دادیم، و این طریقه ما را تغییرپذیر نخواهی یافت.'),
(2107, 17, 78, 'نماز را وقت زوال آفتاب تا اول تاریکی شب به پا دار (یعنی نماز ظهرین را از اول ظهر و نماز عشائین را از بدو تاریکی شب به پا دار) و نماز صبح را نیز به جای آر که آن نماز به حقیقت مشهود (نظر فرشتگان شب و فرشتگان روز) است.'),
(2108, 17, 79, 'و بعضی از شب را به خواندن قرآن (در نماز) بیدار و متهجّد باش، که نماز شب خاص توست، باشد که خدایت تو را به مقام محمود (شفاعت کبری) مبعوث گرداند.'),
(2109, 17, 80, 'و بگو که بارالها مرا (همیشه به هر جا روم به مکّه یا مدینه یا عالم قبر و محشر) به قدم صدق داخل و به قدم صدق خارج گردان و به من از جانب خود حجت روشنی که دائم یار و مددکار باشد عطا فرما.'),
(2110, 17, 81, 'و (به امت) بگو که (دین) حق آمد و باطل نابود شد، که باطل خود لایق محو نابودی ابدی است.'),
(2111, 17, 82, 'و ما آنچه از قرآن فرستیم شفا (ی دل) و رحمت (الهی) برای اهل ایمان است و ظالمان را به جز زیان (و شقاوت) چیزی نخواهد افزود.'),
(2112, 17, 83, 'و ما هرگاه به انسان نعمتی عطا کنیم از آن رو بگرداند و دوری جوید، و هرگاه شر و بلایی به او روی آورد به کلی مأیوس و ناامید شود.'),
(2113, 17, 84, 'بگو که هر کس بر حسب ذات و طبیعت خود عملی انجام خواهد داد، و خدای شما به آن که راهیافته‌تر است (از همه کس) آگاهتر است.'),
(2114, 17, 85, 'و تو را از حقیقت روح می‌پرسند، جواب ده که روح از (عالم) امر خداست (و بی‌واسطه جسمانیات به امر الهی به بدنها تعلق می‌گیرد) و (شما پی به حقیقت آن نمی‌برید زیرا) آنچه از علم به شما دادند بسیار اندک است.'),
(2115, 17, 86, 'و اگر ما بخواهیم آنچه را که به وحی بر تو آوردیم (از قرآن و علم و آیین) همه را باز می‌بریم و آن‌گاه تو بر (قهر) ما هیچ کارساز و مددکاری در این باره نخواهی یافت.'),
(2116, 17, 87, 'مگر رحمت و لطف پروردگار (از تو مدد کند) که فضل (و رحمت) او بر تو بزرگ و بسیار است.'),
(2117, 17, 88, 'بگو که اگر جن و انس متّفق شوند که مانند این قرآن کتابی بیاورند هرگز نتوانند مثل آن آورند هر چند همه پشتیبان یکدیگر باشند.'),
(2118, 17, 89, 'و همانا ما در این قرآن برای مردم هر گونه مثال آوردیم (تا مگر هدایت شوند) لیکن اکثر مردم به جز از کفر و عناد از هر چیز امتناع کردند.'),
(2119, 17, 90, 'و گفتند: ما هرگز به تو ایمان نخواهیم آورد تا آنکه از زمین برای ما (به اعجاز) چشمه آبی بیرون آری.'),
(2120, 17, 91, 'یا آنکه تو را باغی از خرما و انگور باشد که در میان آن باغ نهرهای آب جاری گردانی.'),
(2121, 17, 92, 'یا آنکه بنابر پندار و دعوی خودت پاره‌ای از آسمان را بر سر ما فرود آوری یا آنکه خدا را با فرشتگانش مقابل ما حاضر آری.'),
(2122, 17, 93, 'یا آنکه خانه‌ای از زر (و کاخی زرنگار) دارا باشی یا آنکه بر آسمان بالا روی و باز هم هرگز ایمان به آسمان رفتنت نیاوریم تا آنکه بر ما کتابی نازل کنی که آن را قرائت کنیم. بگو: خدای من منزه است (از آنکه من، او یا فرشتگان او را حاضر سازم) آیا من فرد بشری بیشترم که (از جانب خدا) به رسالت آمده‌ام؟'),
(2123, 17, 94, 'و مردم را چیزی از (هدایت و) ایمان بازنداشت وقتی که هدایت (قرآن) بر آنان آمد جز این (خیال باطل) که گفتند: آیا خدا بشری را به رسالت فرستاده است؟'),
(2124, 17, 95, 'بگو: اگر فرشتگان را در زمین مسکن و قرارگاه بودی ما هم فرشته‌ای را از آسمان به رسالت بر آنها می‌فرستادیم (که رسول باید از جنس امت باشد و گرنه حجت و معجزش پذیرفته نشود).'),
(2125, 17, 96, '(ای پیغمبر، پس از اتمام حجت به امت) بگو: خدا شاهد میان من و شما کافی است که او به (احوال) بندگانش آگاه و بصیر است.'),
(2126, 17, 97, 'و هر که را خدا رهنمایی کند آن کس به حقیقت هدایت یافته و هر که را به گمراهی واگذارد دیگر جز خدا هیچ (رهنما و) دوست و نگهبانی بر آنها نخواهی یافت و چون روز قیامت شود بر روی صورت (بکشانیم و) کور و گنگ و کر محشورشان خواهیم کرد؛ به دوزخ مسکن کنند که هرگه آن دوزخ آتشش خاموش شود باز شدیدتر بر آنها سوزان و فروزانش کنیم.'),
(2127, 17, 98, 'این است کیفر آن کافران چون به آیات ما کافر شدند و گفتند: آیا پس از آنکه ما استخوانی پوسیده و غباری پراکنده شویم از نو باز برانگیخته می‌شویم؟'),
(2128, 17, 99, 'آیا ندیدند که آن خدایی که آسمانها و زمین را آفرید قادر است که مانند اینها را باز خلق کند و بر آنها وقت روز موعودی که بی‌شک خواهد آمد مقرر گرداند؟! (آری با این همه آیات و حجت قاطع) باز ستمکاران جز راه کفر و عناد نپیمودند.'),
(2129, 17, 100, 'بگو که شما اگر دارای گنجهای رحمت (بی‌انتهای) خدای من شوید باز هم از ترس فقر و خوف درویشی، بخل از انفاق خواهید کرد، که انسان طبعا بسیار ممسک و بخیل است.'),
(2130, 17, 101, 'و همانا ما به موسی نه آیت روشن و معجز آشکار عطا کردیم، این حکایت را از بنی اسرائیل سؤال کن که موسی بر آنها آمد، پس فرعون به او گفت: ای موسی، من تو را سحرزده (یا ساحر و سحر آموز) می‌پندارم.'),
(2131, 17, 102, 'موسی به فرعون پاسخ داد که تو خود کاملا دانسته‌ای که این آیات را برای هدایت و بینایی (خلق) جز خدای آسمانها و زمین نفرستاده و من ای فرعون، تو را شخصی جاهل و لایق هلاک می‌پندارم.'),
(2132, 17, 103, 'آن‌گاه فرعون اراده آن کرد که موسی و قومش را از زمین (مصر) براندازد، ما هم او و همدستانش را تمام به دریا غرق کردیم.'),
(2133, 17, 104, 'و بعد از آن بنی اسرائیل را فرمان دادیم که در آن زمین ساکن شوید تا از آن پس که وعده آخرت فرا رسد همه شما را باز جمعا مبعوث گردانیم (و هر کس را به کیفر و پاداش عمل خود برسانیم).'),
(2134, 17, 105, 'و ما این آیات (قرآن عظیم) را به حق فرستادیم و به حق و راستی هم نازل شد، و نفرستادیم تو را جز برای آنکه بشارت دهی و بترسانی.'),
(2135, 17, 106, 'و قرآنی را جزء جزء (و به تدریج) بر تو فرستادیم که تو نیز بر مردم به تدریج قرائت کنی (و با صبر و تأنّی به هدایت خلق پردازی)، و این قرآن کتابی از تنزیلات بزرگ ماست.'),
(2136, 17, 107, 'بگو که شما به این کتاب ایمان بیاورید یا نیاورید (مرا یکسان است که) البته آنها که پیش از این به مقام علم و دانش رسیدند هرگاه این آیات بر ایشان تلاوت شود همه با کمال خضوع و فروتنی سر طاعت بر حکم آن فرود آورند.'),
(2137, 17, 108, 'و گویند: پروردگار ما پاک و منزه است، البته وعده خدای ما محققا واقع خواهد شد.'),
(2138, 17, 109, 'و آنها با چشم گریان همه سر به خاک عبودیت نهاده، و پیوسته بر فروتنی و ترسشان (از خدا) می‌افزاید.'),
(2139, 17, 110, 'بگو که خواه خدا را به اسم اللّه یا به اسم رحمان بخوانید، به هر اسمی بخوانید اسماء نیکو همه مخصوص اوست. و تو در نماز نه صدا را بسیار بلند و نه بسیار آهسته گردان بلکه حدّ وسط را اختیار کن.'),
(2140, 17, 111, 'و بگو: ستایش مخصوص خداست که نه هرگز فرزندی برگرفته و نه شریکی در ملک خود دارد و نه هرگز عزت و اقتدار او را نقصی رسد که به دوست و مددکاری نیازمند شود، و پیوسته ذات الهی را به بزرگترین اوصاف کمال ستایش کن.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ستایش و سپاس مخصوص خداست که بر بنده خاص خود (محمّد صلّی اللّه علیه و آله و سلّم) این کتاب بزرگ (قرآن) را نازل کرد و در آن هیچ نقص و کژی ننهاد.'),
(2142, 18, 2, 'کتابی است در نهایت استواری تا (به این کتاب بزرگ خلق را) از عذاب سخت خود بترساند و اهل ایمان را که اعمال آنها نیکوست به اجر بسیار نیکو بشارت دهد.'),
(2143, 18, 3, 'که در آن سر منزل پر نعمت بهشتی زندگانی ابدی خواهند داشت.'),
(2144, 18, 4, 'و بترساند آنان را که گفتند: خدا فرزندی (برای خود) برگرفته است.'),
(2145, 18, 5, 'آنها نه خود و نه پدرانشان از روی علم و دانش سخن نمی‌گویند، این کلمه بزرگی است که از دهنشان خارج می‌شود و جز دروغ چیزی نمی‌گویند.'),
(2146, 18, 6, 'نزدیک است که اگر امت تو به این سخن (قرآن) ایمان نیاورند جان عزیزت را از شدت حزن و تأسف بر آنان هلاک سازی!'),
(2147, 18, 7, 'ما آنچه را که در زمین جلوه‌گر است زینت و آرایش ملک زمین قرار دادیم تا مردم را امتحان کنیم که کدام یک عملشان نیکوتر خواهد بود.'),
(2148, 18, 8, 'و ما آنچه را زیور زمین گردانیدیم باز همه را به دست ویرانی و فنا می‌دهیم و زمین را دشتی خشک و بی‌گیاه خواهیم ساخت.'),
(2149, 18, 9, 'تو پنداری که (قصه) اصحاب کهف و رقیم (در مقابل این همه آیات قدرت و عجایب حکمتهای ما) از آیات عجیب ماست؟!'),
(2150, 18, 10, 'آن‌گاه که آن جوانمردان (از بیم دشمن) در غار کوه پنهان شدند و از درگاه خدا مسئلت کردند بار الها، تو در حق ما به لطف خاص خود رحمتی عطا فرما و بر ما وسیله رشد و هدایتی کامل مهیّا ساز.'),
(2151, 18, 11, 'پس ما (در آن غار از خواب) بر گوش و هوش آنها تا چند سالی پرده بیهوشی زدیم.'),
(2152, 18, 12, 'پس از آن خواب آنان را برانگیختیم تا معلوم گردانیم کدام یک از آن دو گروه (مؤمنان اهل کتاب که در خواب اصحاب کهف اختلاف داشتند) مدت درنگ در آن غار را بهتر احصا خواهند کرد (و پی به قدرت خدا می‌برند).'),
(2153, 18, 13, 'ما قصه آنان را بر تو به درستی حکایت خواهیم کرد. آنها جوانمردانی بودند که به خدای خود ایمان آوردند و ما بر مقام (ایمان و) هدایتشان بیفزودیم.'),
(2154, 18, 14, 'و دلهای (پاک) آنها را سخت استوار و دلیر ساختیم آن‌گاه که آنها قیام کرده و گفتند: خدای ما پروردگار آسمانها و زمین است و ما هرگز جز خدای یکتا هیچ کس را به خدایی نمی‌خوانیم، که اگر بخوانیم سخت راه خطا و ظلم پیموده‌ایم.'),
(2155, 18, 15, 'این قوم ما که خدایانی غیر خدای یگانه برگرفتند چرا هیچ دلیلی روشن بر خدایی آنها نمی‌آورند؟ پس چه کسی ستمکارتر از آن کس که افترا و دروغ بر خدا می‌بندد؟!'),
(2156, 18, 16, 'و آن‌گاه اصحاب کهف با یکدیگر گفتند که شما چون از این مشرکان و خدایان باطلشان که جز خدا می‌پرستند دوری جستید باید به غار کوه گریخته و پنهان شوید تا خدا از رحمت خود به شما گشایش و توسعه بخشد و اسباب کار شما را با (روزی حلال و) آسایش مهیّا سازد. (آن‌گاه به غار کوه رفته و به خواب شدند..'),
(2157, 18, 17, 'و گردش آفتاب را چنان مشاهده می‌کنی که هنگام طلوع از سمت راست غار آنها برکنار و هنگام غروب نیز از جانب چپ ایشان به دور می‌گردد و آنها در فضای گسترده غار قرار دارند (و کاملا از حرارت خورشید در آسایشند). این حکایت جوانمردان کهف یکی از آیات الهی است؛ هر کس را خدا راهنمایی کند او به حقیقت هدایت یافته و هر که را در گمراهی واگذارد دیگر هرگز برای چنین کس هیچ یار راهنمایی نخواهی یافت.'),
(2158, 18, 18, 'و آنها را بیدار پنداری و حال آنکه در خوابند و ما (برای رفع خستگی) آنان را به پهلوی راست و چپ می‌گردانیدیم و سگ آنها دو دست بر در آن غار گسترده داشت، و اگر بر حال ایشان مطلع شدی از آنها گریختی و از هیبت و عظمت آنان بسیار هراسان گردیدی.'),
(2159, 18, 19, 'و همچنین (که خوابشان کردیم) آنان را از خواب برانگیختیم تا (از زمان خوابشان که بر خود آنها مشتبه و نامعلوم بود) از یکدیگر بپرسند، یکی پرسید: چند مدت (در غار) درنگ کردید؟ جواب دادند: یک روز تمام یا که برخی از روز. دیگر بار گفتند: خدایتان داناتر است که چند مدت در غار بوده‌اید، باری شما یکی را با این درهمهاتان به شهر بفرستید و باید بنگرد که کدامشان طعام پاکیزه‌تر و حلال‌تر دارند تا از آن روزیی برایتان فراهم آرد و باید با دقت و ملاحظه این کار را انجام دهد و نباید هیچ‌کس را از حال شما آگاه سازد.'),
(2160, 18, 20, 'زیرا که اگر (این مردم کافر) بر شما آگهی و ظفر یابند شما را یا سنگسار خواهند کرد یا به آیین خودشان برمی‌گردانند و در آن صورت دیگر روی رستگاری نخواهید دید.'),
(2161, 18, 21, 'و این چنین ما مردم را بر حال اصحاب کهف آگاه ساختیم تا خلق بدانند که وعده خدا (به روز معاد و زنده کردن مردگان) به حق بوده و ساعت قیامت البته بی‌هیچ شک خواهد آمد، آن‌گاه که میانشان تنازع و خلاف در امر آنها بود (یعنی در عدد یا آیین آنها که قائل به معاد و قیامت بودند) پس بعضی گفتند: باید گرد آنها حصار و بنایی بسازیم (که در حبس از خلق مستور و ممنوع شوند) خدا به احوال آنها آگاه‌تر است و آنان که بر واقع احوال آنها ظفر و اطلاع یافتند (یعنی خداپرستان و نیکان امت با خود) گفتند: البته بر ایشان مسجدی بنا کنیم.'),
(2162, 18, 22, 'بعضی خواهند گفت که عده آن اصحاب کهف سه نفر بود چهارمین هم سگ آنها، و برخی دیگر از روی خیالبافی و غیب‌گویی می‌گویند عده آنها پنج نفر بود ششمین سگ آنها، و برخی دیگر گویند: هفت نفر بودند و هشتمین سگ آنها. بگو: خدای من به عده آنها آگاه‌تر است، که بر عدد آنها به جز افراد قلیلی (مانند انبیاء و اولیاء که از طرف حق به وحی دانسته‌اند) هیچ کس آگاه نیست، پس تو (با اهل کتاب) درباره آنها مجادله مکن جز آنکه هر چه به ظاهر وحی دانستی اظهار کن و دیگر هرگز فتوا از احدی در این باب مپرس.'),
(2163, 18, 23, 'و تو هرگز درباره چیزی مگو که من این کار را فردا خواهم کرد.'),
(2164, 18, 24, 'مگر آنکه بگویی (ان شاء اللّه) اگر خدا بخواهد، و چون فراموش کنی باز خدا را به یاد آر و به خلق بگو (این قدر بر سر قصه یاران کهف بحث و جدل بر پا مکنید که) امید است خدای من مرا به حقایقی بهتر و علومی برتر از این قصه هدایت فرماید.'),
(2165, 18, 25, 'و آنها در کهف کوه سیصد سال، نه سال هم افزون درنگ کردند (یهود بر علی علیه السّلام در این آیه اعتراض کردند که نه سال زیاده در تورات ما نیست. حضرت پاسخ داد که این نه سال بر این است که سال شما شمسی و سال ما قمری است).'),
(2166, 18, 26, 'بگو: خدا به زمان اقامت آنان (در غار کوه) داناتر است که او به همه اسرار غیب آسمانها و زمین محیط است، و چقدر هم بینا و شنواست! و هیچ کس جز او سرپرست و نگهبان خلق نیست واحدی را در حکم خود شریک نمی‌سازد.'),
(2167, 18, 27, 'و آنچه از کتاب خدا بر تو وحی شد (بر خلق) تلاوت کن، که کلمات او را هیچ کس تغییر نتواند داد، و هرگز جز درگاه او پناهی نخواهی یافت.'),
(2168, 18, 28, 'و همیشه خویش را با کمال شکیبایی به محبت آنان که صبح و شام خدای خود را می‌خوانند و رضای او را می‌طلبند وادار کن، و مبادا دیدگانت از آنان بگردد از آن رو که به زینتهای دنیا مایل باشی، و هرگز از آن که ما دل او را از یاد خود غافل کرده‌ایم و تابع هوای نفس خود شده و به تبهکاری پرداخته متابعت مکن.'),
(2169, 18, 29, 'و بگو: دین حق همان است که از جانب پروردگار شما آمد، پس هر که می‌خواهد ایمان آرد و هر که می‌خواهد کافر شود، ما برای کافران ستمکار آتشی مهیا ساخته‌ایم که شعله‌های آن مانند خیمه‌های بزرگ گرد آنها احاطه کند و اگر (از شدت عطش) شربت آبی درخواست کنند آبی مانند مس گداخته سوزان به آنها دهند که رویها را بسوزاند و آن آب بسیار بد شربتی و آن دوزخ بسیار بد آسایشگاهی است.'),
(2170, 18, 30, 'آنان که ایمان آوردند و نیکوکار شدند ما هم اجر نیکوکاران را ضایع نخواهیم گذاشت.'),
(2171, 18, 31, 'آنهایند که بهشتهای عدن که نهرها زیر درختانش جاری است خاص آنهاست در حالی که آنان را در آن بهشت به دستبندها و زیورهای زرین بیارایند و لباس‌های سبز حریر و دیبا درپوشند و بر تخت‌ها تکیه زنند، که آن بهشت نیکو اجری و خوش آرامگاهی است.'),
(2172, 18, 32, 'و دو مرد (مؤمن و کافر) را برای امت مثل آر که به یکی از آنها دو باغ انگور دادیم و به نخل خرما اطرافش را پوشاندیم و عرصه میان آنها را کشتزار (برای گندم و سایر حبوبات) قرار دادیم.'),
(2173, 18, 33, 'آن دو باغ کاملا میوه‌های خود را بی‌هیچ آفت و نقصان بداد و در وسط آنها جوی آبی نیز روان ساختیم.'),
(2174, 18, 34, 'و این مرد که دو باغ میوه بسیار داشت با رفیق خود (که مردی مؤمن و فقیر بود) در مقام گفتگو و مفاخرت برآمد و گفت: من از تو به دارایی بیشتر و از حیث خدم و حشم نیز عزیزتر و توانمندترم.'),
(2175, 18, 35, 'و روزی به باغ خود در حالی که به نفس خود ستمکار بود (چون عمر را به کفر و گناه و غفلت از خدا می‌گذرانید با کمال غرور) داخل شد، گفت: گمان ندارم هرگز این باغ نابود شود.'),
(2176, 18, 36, 'و نیز گمان نمی‌کنم که روز قیامتی به پا شود و اگر (به فرض هم خدا و قیامتی باشد و) من به سوی خدای خود بازگردم البته (در آن جهان نیز) از این باغ دنیا منزلی بهتر خواهم یافت.'),
(2177, 18, 37, 'رفیق (با ایمان و فقیر) او در مقام گفتگو (و اندرز) بدو گفت: آیا به خدایی که نخست از خاک و بعد از نطفه تو را آفرید و آن‌گاه مردی کامل و آراسته خلقت ساخت کافر شدی؟!'),
(2178, 18, 38, 'لیکن من پروردگارم آن خدای یکتاست و هرگز به خدای خود احدی را شریک نخواهم ساخت.'),
(2179, 18, 39, 'و تو چرا وقتی به باغ خود درآمدی نگفتی که همه چیز به خواست خداست و جز قدرت خدا قوه‌ای نیست؟ اگر تو مرا از خود به مال و فرزند کمتر دانی (به خود مغرور مشو که).'),
(2180, 18, 40, 'امید است خدا مرا بهتر از باغ تو (در آخرت و دنیا) بدهد و بر بوستان تو آتشی فرستد که باغت یکسره نابود و با خاک صرف یکسان گردد.'),
(2181, 18, 41, 'یا آبش به زمین فرو رود و دیگر هرگز نتوانی آب به دست آری (و باغت از بی‌آبی خشک شود).'),
(2182, 18, 42, 'و ثمره و میوه‌هایش همه نابود گردید و او (از شدت حزن و اندوه) بر آنچه در باغ خرج کرده بود دست بر دست می‌زد که بنا و اشجارش همه ویران و خشک شده بود، و می‌گفت: ای کاش من احدی را به خدای خود شریک نمی‌ساختم.'),
(2183, 18, 43, 'و ابدا جز خدا هیچ گروهی نبود که آن گنهکار کافر را از قهر و خشم خدا یاری و حمایت کند و نه خود مدافع خویش بود.'),
(2184, 18, 44, 'آنجا ولایت و حکمفرمایی خاص خداست که به حق فرمان دهد و بهترین اجر و ثواب و عاقبت نیکو را هم او عطا کند.'),
(2185, 18, 45, 'و برای امت، زندگانی دنیا را چنین مثل زن که ما آب بارانی از آسمان نازل کنیم و به آن آب، درختان و نباتات گوناگون زمین درهم پیچیده و خرم بروید، سپس همه درهم شکسته و خشک شود و به دست بادها زیر و زبر و پراکنده گردد، و خدا بر هر چیز اقتدار کامل دارد.'),
(2186, 18, 46, 'مال و فرزندان زیب و زینت حیات دنیاست و اعمال صالح که (تا قیامت) باقی است نزد پروردگار از نظر ثواب بهتر و از نظر امیدمندی نیکوتر است.'),
(2187, 18, 47, 'و (یاد کن) روزی که ما کوهها را به رفتار آریم و زمین را صاف و بدون پست و بلندی آشکارا ببینی و همه را (در صف محشر از قبرها) برانگیزیم و یکی را فرو نگذاریم.'),
(2188, 18, 48, 'و خلایق را در صفی بر خدا عرضه کنند (و به کافران گفته شود: دیدید) همان گونه که اول بار شما را آفریدیم باز به سوی ما باز آمدید، اما شما (به خیال باطل) پنداشتید که هرگز برای شما وعده گاهی نمی‌نهیم (و معادی در کار نیست).'),
(2189, 18, 49, 'و کتاب اعمال نیک و بد خلق را پیش نهند، آن گاه اهل عصیان را از آنچه در نامه عمل آنهاست ترسان و هراسان بینی در حالی که (با خود) گویند: ای وای بر ما، این چگونه کتابی است که اعمال کوچک و بزرگ ما را سر مویی فرو نگذاشته جز آنکه همه را احصا کرده است؟! و در آن کتاب همه اعمال خود را حاضر بینند و خدا به هیچ کس ستم نخواهد کرد.'),
(2190, 18, 50, 'و (یاد آر) وقتی که به فرشتگان فرمان دادیم که بر آدم همه سجده کنید، و آنها تمام سر به سجده فرود آوردند جز شیطان که از جنس جن (دیو) بود بدین جهت از طاعت خدای خود سرپیچید، پس آیا (شما فرزندان آدم) مرا فراموش کرده و شیطان و فرزندانش را دوست خود می‌گیرید در صورتی که آنها شما را سخت دشمنند؟ ظالمان را (که به جای خدا شیطان را به طاعت برگزیدند) بسیار بد بدلی است.'),
(2191, 18, 51, 'من در وقت آفرینش آسمانها و زمین و یا خلقت خود این مردم آنها را حاضر و گواه نساختم (و کمک از کسی نخواستم) و هرگز گمراه کنندگان را به مددکاری نگرفتم.'),
(2192, 18, 52, 'و (یاد آر) روزی که خدا به کافران گوید: اکنون آنان را که شریک من گمان داشتید بخوانید (تا به فریاد شما رسند)، آنها بخوانند و هیچ یک اجابت نکنند، و میان همه آنها (یعنی مشرکان و معبودانشان) جایگاهی سخت مهلک مقرر سازیم.'),
(2193, 18, 53, 'و آن‌گاه مردم بدکار آتش دوزخ را به چشم مشاهده کنند و بدانند که در آن خواهند افتاد و از آن ابدا مفرّی ندارند.'),
(2194, 18, 54, 'و ما در این قرآن هرگونه مثال و بیان برای (هدایت) خلق آوردیم و لیکن آدمی بیشتر از هر چیز (با سخن حق) به جدال و خصومت برخیزد.'),
(2195, 18, 55, 'و چون هدایت (الهی قرآن) به خلق رسید چیزی مردم را منع نکرد که آن هدایت را بپذیرند و به درگاه پروردگار خود توبه و استغفار کنند به جز آنکه (خواستند مستحق شوند تا) سنّت (عقوبت و هلاک) پیشینیان به اینان هم برسد و یا با مجازات و عذاب خدا روبرو شوند.'),
(2196, 18, 56, 'و ما رسولان را جز برای بشارت (نیکان) و ترسانیدن (بدان) نمی‌فرستیم و کافران با سخنان بیهوده باطل می‌خواهند جدل کرده و حق را پایمال کنند، و آیات مرا و آنچه برای تنبیه و انذارشان آمد به استهزاء گرفتند.'),
(2197, 18, 57, 'و کیست ستمکارتر از آن کسی که متذکر آیات خدایش ساختند و بدانها پندش دادند و باز از آنها اعراض کرد و از اعمال زشتی که کرده بود به کلی فراموش کرد و ما (پس از اتمام حجت) بر دلهایشان پرده‌ها (ی جهل و قساوت) انداختیم تا دیگر آیات ما را فهم نکنند، و گوش آنها را (از شنیدن سخن حق) سنگین ساختیم و اگر به هدایتشان بخوانی دیگر ابدا هدایت نخواهند یافت.'),
(2198, 18, 58, 'و خدای تو بسیار آمرزنده و بی‌نهایت دارای کرم و رحمت است، اگر خواهد تا خلق را به کردارشان مؤاخذه کند همانا در عذابشان تعجیل و تسریع کند و لیکن برای (عذاب) آنان وقت معین (در عالم قیامت) مقرر است که از آن هرگز پناه و گریزگاهی نخواهند یافت.'),
(2199, 18, 59, 'و این است شهر و دیارهایی که اهل آن را چون ظلم و ستم کردند هلاک ساختیم و بر وقت هلاکشان موعدی معلوم مقرر گردانیدیم.'),
(2200, 18, 60, 'و (به یاد آر) وقتی که موسی به شاگردش (یوشع که وصی و خلیفه او بود) گفت: من دست از طلب برندارم تا به مجمع البحرین (به محل برخورد دو دریا) برسم یا سالها عمر در طلب بگذرانم.'),
(2201, 18, 61, 'و چون موسی و شاگردش بدان مجمع البحرین رسیدند ماهی غذای خود را فراموش کردند و آن ماهی راه به دریا برگرفت و رفت.'),
(2202, 18, 62, 'پس چون که از آن مکان بگذشتند موسی به شاگردش گفت: چاشت ما را بیاور که ما در این سفر رنج بسیار دیدیم.'),
(2203, 18, 63, 'وی گفت: در نظر داری آنجا که بر سر سنگی منزل گرفتیم؟ من (آنجا) ماهی را فراموش کردم و آن را جز شیطان از یادم نبرد و شگفت آنکه ماهی بریان راه دریا گرفت و برفت.'),
(2204, 18, 64, 'موسی گفت: آنجا همان مقصدی است که ما در طلب آن بودیم، و از آن راهی که آمدند به آنجا برگشتند.'),
(2205, 18, 65, 'در آنجا بنده‌ای از بندگان خاص ما را یافتند که او را رحمت و لطف خاصی از نزد خود عطا کردیم و هم از نزد خود وی را علم (لدنّی و اسرار غیب الهی) آموختیم.'),
(2206, 18, 66, 'موسی به آن شخص دانا (و خضر زمان) گفت: آیا من تبعیت (و خدمت) تو کنم تا از علم لدنّی خود مرا بیاموزی؟'),
(2207, 18, 67, 'آن عالم پاسخ داد که تو هرگز نمی‌توانی که (تحمل اسرار کرده و) با من صبر پیشه کنی.'),
(2208, 18, 68, 'و چگونه صبر توانی کرد بر چیزی که از علم آن آگهی کامل نیافته‌ای؟'),
(2209, 18, 69, 'موسی گفت: به خواست خدا مرا با صبر و تحمل خواهی یافت و هرگز در هیچ امر با تو مخالفت نخواهم کرد.'),
(2210, 18, 70, 'آن عالم گفت: پس اگر تابع من شدی دیگر از هر چه من کنم هیچ سؤال مکن تا وقتی که من خود تو را از آن راز آگاه سازم.'),
(2211, 18, 71, 'سپس هر دو با هم برفتند تا وقتی که در کشتی سوار شدند. آن عالم کشتی را بشکست، موسی گفت: ای مرد، آیا کشتی شکستی تا اهل آن را به دریا غرق کنی؟! بسیار کار منکر و شگفتی به جای آوردی.'),
(2212, 18, 72, 'گفت: آیا من نگفتم که هرگز ظرفیت و توانایی آنکه با من صبر کنی نداری؟'),
(2213, 18, 73, 'موسی گفت: (این یک بار) بر من مگیر که شرط خود را فراموش کردم و مرا تکلیف سخت طاقت فرسا نفرما.'),
(2214, 18, 74, 'و باز هم روان شدند تا به پسری برخوردند، او پسر را (بی‌گفتگو) به قتل رسانید. باز موسی گفت: آیا نفس محترمی که کسی را نکشته بود بی‌گناه کشتی؟ همانا کار بسیار منکر و ناپسندی کردی.'),
(2215, 18, 75, 'گفت: آیا با تو نگفتم که تو هرگز ظرفیت و مقام آنکه با من صبر کنی نخواهی داشت؟'),
(2216, 18, 76, 'موسی گفت: اگر بار دیگر از تو مؤاخذه و اعتراضی کردم از آن بعد با من ترک صحبت و رفاقت کن که از (تقصیر) من عذر موجه (بر متارکه دوستی) به دست خواهی داشت.'),
(2217, 18, 77, 'باز با هم روان شدند تا وارد بر شهری شدند و از اهل آن شهر طعام خواستند، مردم از طعام دادن و مهمانی آنها ابا کردند (آنها هم از آن شهر به عزم خروج رفتند تا) در (نزدیکی دروازه آن) شهر به دیواری که نزدیک به انهدام بود رسیدند و آن عالم به استحکام و تعمیر آن پرداخت. باز موسی گفت: روا بود که بر این کار اجرتی می‌گرفتی.'),
(2218, 18, 78, 'آن عالم گفت: این (سه بار کم‌ظرفی و بی‌صبری و اعتراض، عذر) مفارقت بین من و توست، من همین ساعت تو را بر اسرار کارهایم که بر فهم آن صبر و ظرفیت نداشتی آگاه می‌سازم.'),
(2219, 18, 79, 'اما آن کشتی، صاحبش خانواده فقیری بود که در دریا کار می‌کردند (و از آن کشتی کسب و ارتزاق می‌نمودند) خواستم چون کشتی‌ها (ی بی‌عیب) را پادشاه در سر راه به غصب می‌گرفت این کشتی را ناقص کنم (تا برای آن فقیران باقی بماند).'),
(2220, 18, 80, 'و اما آن پسر (کافر بود و) پدر و مادر او مؤمن بودند، از آن باک داشتیم که آن پسر آنها را به خوی کفر و طغیان خود درآورد.'),
(2221, 18, 81, 'خواستیم تا به جای او خدایشان فرزندی بهتر و صالح‌تر و مهذب‌تر و نزدیکتر به ارحام پرستی به آن پدر و مادر بخشد.'),
(2222, 18, 82, 'و اما آن دیوار از آن دو طفل یتیمی در آن شهر بود که پدری صالح داشتند و زیر آن گنجی برای آن دو نهفته بود، خدایت خواست تا به لطف خود آن اطفال به حد رشد رسند و خودشان گنج را استخراج کنند. و من این کارها نه از پیش خود (بلکه به امر خدا) کردم. این است مآل و باطن کارهایی که تو طاقت و ظرفیت بر انجام آن نداشتی.'),
(2223, 18, 83, 'و از تو (ای رسول) سؤال از ذوالقرنین می‌کنند، پاسخ ده که من به زودی حکایتی از او بر شما خواهم خواند.'),
(2224, 18, 84, 'ما او را در زمین تمکن و قدرت بخشیدیم و از هر چیزی رشته‌ای به دست او دادیم.'),
(2225, 18, 85, 'او هم رشته‌ای را پی گرفت.'),
(2226, 18, 86, 'تا هنگامی که (در سیر خود) به مغرب رسید خورشید را (که در دریای محیط غروب می‌کرد) چنین یافت که در چشمه آب تیره‌ای رخ نهان می‌کند و آنجا قومی را یافت که (چون کافر بودند) به ذوالقرنین دستور دادیم که درباره این قوم یا قهر و عذاب (اگر ایمان نیاوردند) یا لطف و رحمت (اگر ایمان آرند) به جای آور.'),
(2227, 18, 87, 'ذو القرنین به آن قوم گفت: اما هر کس (از شما) ظلم و ستم کرده او را به کیفر خواهیم رسانید و سپس هم که (بعد از مرگ) به سوی خدا بازگردد خدا او را به عذابی بسیار سخت کیفر خواهد کرد.'),
(2228, 18, 88, 'و اما هر کس به خدا ایمان آورد و نیکو کردار باشد نیکوترین اجر خواهد یافت و هم ما امر را بر او سهل و آسان گیریم.'),
(2229, 18, 89, 'و باز وسیله و رشته‌ای را پی گرفت (و سفر را ادامه داد).'),
(2230, 18, 90, 'تا چون به مشرق زمین رسید آنجا دید که خورشید بر قومی می‌تابد که ما میان آنها و آفتاب ساتری قرار نداده‌ایم (یعنی لباس و خیمه و مسکنی که از حرارت خورشید سایبان کنند نداشتند).'),
(2231, 18, 91, 'همچنین بود، و البته ما از احوال او کاملا باخبر بودیم.'),
(2232, 18, 92, 'باز وسیله و رشته‌ای را پی گرفت (و به سمت شمال سفر را ادامه داد).'),
(2233, 18, 93, 'تا چون رسید میان دو سد (دو کوه بین دو کشور در شمال یا جنوب خاک ترکستان) آنجا قومی را یافت که سخنی فهم نمی‌کردند (و سخت وحشی و زبان نفهم بودند)'),
(2234, 18, 94, 'آنان گفتند: ای ذوالقرنین، قومی به نام یأجوج و مأجوج در این سرزمین (پشت این کوه) فساد (و خونریزی و وحشیگری) بسیار می‌کنند، آیا چنانچه ما خرج آن را به عهده گیریم سدی میان ما و آنها می‌بندی (که ما از شر آنان آسوده شویم).'),
(2235, 18, 95, 'ذوالقرنین گفت: تمکن و ثروتی که خدا به من عطا فرموده (از هزینه شما) بهتر است (نیازی به کمک مادی شما ندارم) اما شما با من به قوت بازو کمک کنید (مرد و کارگر از شما، وسایل و هزینه آن با من) تا سدی محکم برای شما بسازم (که به کلی مانع دستبرد آنها شود).'),
(2236, 18, 96, 'برایم قطعات آهن بیاورید. (آن‌گاه دستور داد که زمین را تا به آب بکنند و از عمق زمین تا مساوی دو کوه از سنگ و آهن دیواری بسازند) تا چون میان آن دو کوه را برابر ساخت گفت: در آتش بدمید؛ تا چون آهن را بسان آتش بگداخت، گفت: برایم مس گداخته آورید تا بر آن فرو ریزم.'),
(2237, 18, 97, 'از آن پس آن قوم نه هرگز بر بالای آن سد شدن و نه بر شکستن آن سد و رخنه در آن توانایی یافتند.'),
(2238, 18, 98, 'ذو القرنین گفت که این (قدرت و تمکن بر بستن سد) لطف و رحمتی از خدای من است و آن‌گاه که وعده خدای من فرا رسد (که روز قیامت یا روز ظهور حضرت ولی اللّه اعظم است) آن سد را متلاشی و هموار با زمین گرداند و البته وعده خدای من محقق و راست است.'),
(2239, 18, 99, 'و روز آن وعده که فرا رسد (طایفه یأجوج و مأجوج یا) همه خلایق محشر چون موج مضطرب و سرگردان باشند و درهم آمیزند، و نفخه صور دمیده شود و همه خلق را در صحرای قیامت جمع آریم.'),
(2240, 18, 100, 'و در آن روز دوزخ را (که انکار می‌کردند) آشکار به کافران بنماییم.'),
(2241, 18, 101, 'آن کافرانی که بر چشم (قلب) شان پرده (غفلت) بود و از یاد من غافل بودند و (از فرط هوای نفس) هیچ توانایی بر شنیدن (آیات و کلمه الهی) نداشتند.'),
(2242, 18, 102, 'آیا کافران پندارند که بندگان من (چون فرشتگان و حضرت مسیح) را به جای من دوست و یاور خود می‌گیرند؟! ما برای کافران دوزخ را منزلگاه قرار داده‌ایم.'),
(2243, 18, 103, 'بگو که آیا می‌خواهید شما را بر زیانکارترین مردم آگاه سازیم؟'),
(2244, 18, 104, '(زیانکارترین مردم) آنها هستند که (عمر و) سعی‌شان در راه دنیای فانی تباه گردید و به خیال باطل می‌پنداشتند که نیکوکاری می‌کنند.'),
(2245, 18, 105, 'همین دنیا طلبانند که به آیات خدای خود کافر شدند و (روز) ملاقات خدا را انکار کردند، لذا اعمالشان همه تباه گشته و روز قیامت آنها را هیچ وزن و ارزشی نخواهیم داد.'),
(2246, 18, 106, 'این است که اینان چون کافر شده و آیات و پیمبران مرا استهزاء کردند به آتش دوزخ کیفر خواهند یافت.'),
(2247, 18, 107, 'آنان که به خدا ایمان آورده و نیکوکار شدند البته آنها در بهشت فردوس منزل خواهند یافت.'),
(2248, 18, 108, 'همیشه در آن بهشت ابد منزل یافته و هرگز از آنجا انتقال نخواهند یافت.'),
(2249, 18, 109, 'بگو که اگر دریا برای (نوشتن) کلمات پروردگار من مرکب شود پیش از آنکه کلمات پروردگارم به آخر رسد دریا خشک خواهد شد هر چند دریایی دیگر باز ضمیمه آن کنیم (یعنی عوالم وجود که کلمات تکوینی الهی است بی‌حد و نامتناهی است).'),
(2250, 18, 110, 'بگو: جز این نیست که من مانند شما بشری هستم (دعوی احاطه به جهان‌های نامتناهی و کلیه کلمات الهی نکنم، تنها فرق من با شما این است) که به من وحی می‌رسد که خدای شما خدای یکتاست، پس هر کس به لقای (رحمت) پروردگارش امیدوار است باید نیکوکار شود و هرگز در پرستش خدایش احدی را با او شریک نگرداند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کهیعص (این حروف رموز و اسراری است در میان خدا و رسول صلّی اللّه علیه و آله و سلّم).'),
(2252, 19, 2, 'در این آیات پروردگار تو از رحمتش بر بنده خاصّ خود زکریا سخن می‌گوید.'),
(2253, 19, 3, '(یاد کن حکایت او را) وقتی که خدای خود را پنهانی (و از صمیم قلب) ندا کرد.'),
(2254, 19, 4, 'عرض کرد که پروردگارا، استخوان من سست گشت و فروغ پیری بر سرم بتافت و با وجود این من از دعا به درگاه کرم تو خود را هرگز محروم ندانسته‌ام.'),
(2255, 19, 5, 'و من از این وارثان کنونی (که پسر عموهای من هستند) برای پس از خود بیمناکم (مبادا که پس از من در مال و مقامم خلف صالح نباشند و راه باطل پویند) و زوجه من هم نازا و عقیم است، تو از لطف خاص خود فرزندی به من عطا فرما.'),
(2256, 19, 6, 'که او وارث من و همه آل یعقوب باشد و تو ای خدا او را وارثی پسندیده و صالح مقرر فرما.'),
(2257, 19, 7, 'ای زکریا همانا تو را به فرزندی که نامش یحیی است و از این پیش همنام و همانندش (در تقوا) نیافریدیم بشارت می‌دهیم.'),
(2258, 19, 8, 'زکریا (از فرط شوق و شگفتی و برای حصول اطمینان) عرض کرد: الها، مرا از کجا پسری تواند بود در صورتی که زوجه من نازاست و من هم از شدت پیری خشک و فرتوت شده‌ام؟!'),
(2259, 19, 9, '(فرشته‌ای از زبان خدا) گفت: چنین خواهد شد؛ پروردگارت گوید: این کار برای من بسیار آسان است و منم که تو را پس از هیچ و معدوم صرف بودن، نعمت وجود بخشیدم.'),
(2260, 19, 10, 'زکریا باز عرضه داشت: بارالها، برای من نشانه و حجتی بر این بشارت قرار ده، خدا فرمود: بدین نشان که تا سه شبانه‌روز با مردم تکلم نکنی (و بر سخن قادر نباشی) با آنکه تندرست هستی.'),
(2261, 19, 11, 'پس زکریا در حالی که از محراب (عبادت) بر قومش بیرون آمد به آنان اشاره کرد که صبح و شام به تسبیح و نماز قیام کنید.'),
(2262, 19, 12, '(چون زکریا ذکر تسبیح و نماز کرد ما به او یحیی را عطا کردیم و چون سه ساله شد به وی خطاب کردیم که) ای یحیی، تو کتاب آسمانی ما را به قوت (نبوت) فراگیر، و به او در همان سن کودکی مقام نبوت بخشیدیم.'),
(2263, 19, 13, 'و نیز او را به لطف خاص خود رحمت و مهربانی و پاکی و پارسایی دادیم و او طریق تقوا پیش گرفت.'),
(2264, 19, 14, 'و هم در حق پدر و مادر خود بسیار نیکوکار بود و هرگز به احدی ستم نکرد و معصیت خدا را مرتکب نگردید.'),
(2265, 19, 15, 'و سلام حق بر او باد در روز ولادتش و روز وفاتش و روزی که زنده برانگیخته خواهد شد.'),
(2266, 19, 16, 'و یاد کن در کتاب خود احوال مریم را آن‌گاه که از اهل خانه خویش کنار گرفته به مکانی به مشرق (بیت المقدس برای عبادت) روی آورد.'),
(2267, 19, 17, 'و آن‌گاه که از همه خویشانش به کنج تنهایی محتجب و پنهان گردید ما روح خود را (روح القدس که فرشته اعظم است) بر او فرستادیم و او در صورت بشری زیبا و راست اندام بر او ظاهر شد.'),
(2268, 19, 18, 'مریم گفت: من از تو پناه به خدای رحمان می‌برم اگر مرد پرهیزکاری هستی.'),
(2269, 19, 19, 'گفت: من فرستاده خدای توام، آمده‌ام تا تو را پسری بخشم بسیار پاکیزه و پاک سیرت.'),
(2270, 19, 20, 'مریم گفت: از کجا مرا پسری تواند بود در صورتی که دست بشری به من نرسیده و کار ناشایسته‌ای هم نکرده‌ام؟'),
(2271, 19, 21, 'گفت: این چنین کار البته خواهد شد، خدایت فرموده که بسیار بر من آسان است (تا قدرت ما را بر آن دریابی) و تا این پسر را آیت بزرگ و رحمت واسع خود برای خلق گردانیم؛ و قضای الهی بر این کار رفته است.'),
(2272, 19, 22, 'پس مریم (به مشاهده جبرئیل، در گریبانش نسیم اهتزازی یافت و) به آن پسر بار برداشت و (برای آنکه از سرزنش قوم جاهل برکنار باشد) با آن به مکانی دور خلوت گزید.'),
(2273, 19, 23, 'آن‌گاه درد زاییدن او را به کنار درخت خرمایی کشانید (و از شدت حزن و اندوه با خود) گفت: ای کاش من از این پیش مرده بودم و از صفحه عالم به کلی نامم فراموش شده بود.'),
(2274, 19, 24, 'پس از زیر آن درخت (روح القدس یا فرزندش عیسی) او را ندا کرد که غمگین مباش که خدای تو از زیر قدم تو چشمه آبی جاری کرد.'),
(2275, 19, 25, 'شاخ درخت را به سوی خود حرکت ده تا از آن بر تو رطب تازه فرو ریزد.'),
(2276, 19, 26, 'پس (از این رطب) تناول کن و (از این چشمه آب) بیاشام و چشم خود (به عیسی) روشن دار، و هر کس از جنس بشر را که ببینی (به اشاره) با او بگو: برای خدای رحمن نذر روزه (سکوت) کرده‌ام و امروز با هیچ انسانی سخن نخواهم گفت.'),
(2277, 19, 27, 'آن‌گاه کودک را برداشته نزد قوم خود آورد گفتند: ای مریم عجب کاری منکر و شگفت‌آور کردی!'),
(2278, 19, 28, 'ای خواهر هارون، تو را نه پدری ناصالح بود و نه مادری بدکار (پس تو دختر بکر از کجا فرزند یافتی).'),
(2279, 19, 29, 'مریم به اشاره حواله به طفل کرد، آنها گفتند: ما چگونه با طفل گهواره‌ای سخن گوییم؟'),
(2280, 19, 30, 'آن طفل (به امر خدا به زبان آمد و) گفت: همانا من بنده خاص خدایم که مرا کتاب آسمانی و شرف نبوت عطا فرمود.'),
(2281, 19, 31, 'و مرا هر کجا که باشم مایه برکت (و رحمت) گردانید، و تا زنده‌ام به عبادت نماز و زکات سفارش کرد.'),
(2282, 19, 32, 'و مرا نیکوکار به مادرم ساخت و مرا ستمکار و شقی نگردانید.'),
(2283, 19, 33, 'و سلام حق بر من است روزی که به دنیا آمدم و روزی که از جهان بروم و روزی که (برای زندگانی ابدی) باز زنده برانگیخته شوم.'),
(2284, 19, 34, 'این است (قضیه) عیسی بن مریم که مردم در امر خلقتش شک و ریب دارند، اکنون به سخن حق و صواب، حقیقت حالش بیان گردید.'),
(2285, 19, 35, 'خدا را هرگز نشاید که فرزندی اتخاذ کند، که وی منزه از آن است، (او قادری است که) چون حکم نافذش به ایجاد چیزی تعلق گیرد همین که گوید: «موجود باش» بی‌درنگ آن چیز موجود می‌شود.'),
(2286, 19, 36, 'خدای یکتا پروردگار من و شماست، او را پرستید که راه راست همین راه خداپرستی است.'),
(2287, 19, 37, 'آن‌گاه طوایف مردم (یعنی نصاری) در میان خود (درباره مسیح پسر مریم) اختلاف کردند (برخی او را از کفر و جهل، خدا دانستند و برخی پسر خدا و برخی پسر یوسف نجارش خواندند)، پس وای بر این مردم کافر از حضور در آن روز بزرگ قیامت.'),
(2288, 19, 38, 'روز محشر که خلق به سوی ما بازآیند چقدر شنوا و بینا شوند! ولی امروز (که باید حقیقت را بشنوند و ببینند) مردم ستمکار همه به گمراهی و ضلالت آشکار فرو رفته‌اند.'),
(2289, 19, 39, 'و (ای رسول ما) امت را از روز غم و حسرت (یعنی روز مرگ و قیامت) بترسان که آن روز دیگر کارشان گذشته است و آنها سخت از آن روز غافلند و به آن ایمان نمی‌آورند.'),
(2290, 19, 40, 'تنها ماییم که (پس از فنای خلق و انتقالشان به عالم آخرت) زمین و اهلش را وارث می‌شویم و همه به سوی ما بازگردانده خواهند شد.'),
(2291, 19, 41, 'و یاد کن در کتاب خود شرح حال ابراهیم را که او شخص بسیار راستگو و پیغمبری بزرگ بود.'),
(2292, 19, 42, 'هنگامی که با پدر خود (یعنی عموی خویش آزر) گفت: ای پدر، چرا بتی جماد را که چشم و گوش (و حس و هوشی) ندارد و هیچ رفع حاجتی از تو نتواند کرد پرستش می‌کنی؟'),
(2293, 19, 43, 'ای پدر، مرا (از وحی خدا) علمی آموختند که تو را آن علم نیاموخته‌اند، پس تو مرا پیروی کن تا به راه راستت هدایت کنم.'),
(2294, 19, 44, 'ای پدر، هرگز شیطان را نپرست که شیطان سخت با خدای رحمان مخالفت و عصیان کرد.'),
(2295, 19, 45, 'ای پدر، از آن سخت می‌ترسم که از خدای مهربان بر تو قهر و عذاب رسد و (به دوزخ) با شیطان یار و یاور باشی.'),
(2296, 19, 46, 'گفت: ای ابراهیم، تو مگر از خدایان من روگردان و بی‌عقیده شدی؟ چنانچه دست (از مخالفت بتان) برنداری بی‌گمان تو را سنگسار کنم، و گرنه سالها از من به دور باش.'),
(2297, 19, 47, 'باز ابراهیم گفت: سلامت باشی، من از خدای خود برای تو آمرزش خواهم خواست، که او بسیار در حقّ من مهربان است.'),
(2298, 19, 48, 'من از شما و بتانی که به جای خدا می‌پرستید دوری کرده و خدای یکتایم را می‌خوانم و امیدوارم که چون او را بخوانم از درگاه لطفش محروم نمانم (و مرا از شرّ مشرکان حفظ کند).'),
(2299, 19, 49, 'و چون ابراهیم از آن قوم و بتانی که به جای خدا می‌پرستیدند دوری گزید ما هم (به لطف و رحمت خود) به او اسحاق و (فرزند او) یعقوب را عطا کردیم و به همه شرف نبوت بخشیدیم.'),
(2300, 19, 50, 'و آنها را از رحمت خود بهره‌مند ساختیم و بر زبان عالمی آوازه نکونامی‌شان را بلند گردانیدیم.'),
(2301, 19, 51, 'و یاد کن در کتاب خود شرح حال موسی را که او بنده‌ای برگزیده و رسولی بزرگ و مبعوث به پیمبری بود.'),
(2302, 19, 52, 'و ما او را از جانب راست (وادی مقدس) طور ندا کردیم و به مقام قرب خود برای استماع کلام خویش برگزیدیم.'),
(2303, 19, 53, 'و از لطف و مرحمتی که داشتیم برادرش هارون پیامبر را نیز (برای مشارکت و مساعدت او) به او عطا کردیم.'),
(2304, 19, 54, 'و یاد کن در کتاب خود شرح حال اسماعیل را که بسیار در وعده صادق و رسول و پیغمبری بزرگوار بود.'),
(2305, 19, 55, 'و همیشه اهل بیت خود را به ادای نماز و زکات امر می‌کرد و او نزد خدایش بنده‌ای پسندیده بود.'),
(2306, 19, 56, 'و یاد کن در کتاب خود احوال ادریس را که او شخصی بسیار راستگو و پیغمبری عظیم الشأن بود.'),
(2307, 19, 57, 'و مقام او را بلند و مرتبه‌اش را رفیع گردانیدیم.'),
(2308, 19, 58, 'اینان (که از زکریا تا ادریس اوصافشان یاد شد) همان رسولانی هستند که خدا از میان همه اولاد آدم و اولاد آنان که با نوح در کشتی نشاندیم و اولاد ابراهیم و یعقوب و دیگر کسان که هدایت کرده و برگزیدیم، آنها را به لطف و انعام خود مخصوص گردانید (و حال آنها در بندگی چنان است) که هرگاه آیات خدای رحمن بر آنها تلاوت شود با گریه (شوق و محبت) روی اخلاص بر خاک نهند.'),
(2309, 19, 59, 'سپس جانشین آن مردم خداپرست قومی شدند که نماز را ضایع گذارده و شهوتهای نفس را پیروی کردند و اینها به زودی کیفر گمراهی را خواهند دید.'),
(2310, 19, 60, 'مگر آن کس که توبه کند و ایمان آرد و نیکوکار شود، که آنان در این صورت بی‌هیچ ستم به بهشت ابد داخل خواهند شد.'),
(2311, 19, 61, 'آن بهشتهای عدنی که خدای مهربان برای بندگان (صالح) خود در غیب این جهان وعده فرمود، و البته وعده خدا یقین به انجام می‌رسد.'),
(2312, 19, 62, 'در آن بهشت هرگز سخن لغوی نشنوند بلکه همه گفتارشان سلام (و ستایش یکدیگر) است و در آنجا روزی آنها صبح و شام (بی‌هیچ رنج) به آنها می‌رسد.'),
(2313, 19, 63, 'این همان بهشتی است که ما بندگان پاک متقی خود را بالخصوص وارث آن می‌گردانیم.'),
(2314, 19, 64, 'و ما (رسولان و فرشتگان خدا) جز به امر خدای تو از عالم بالا نازل نمی‌شویم، اوست که بر همه جهانهای پیش رو و پشت سر ما و بین آنها هر چه هست به احاطه علمی آگاه است و پروردگارت هرگز چیزی را فراموش نخواهد کرد.'),
(2315, 19, 65, 'همان خدایی که آفریننده آسمانها و زمین است و هر چه بین آنهاست، پس باید همان خدای یکتا را پرستش کنی و البته در راه بندگی او صبر و تحمل کن (که اگر او را به خدایی نپرستی) آیا دیگری را مانند او به نام خدا (یی لایق پرستش) خواهی یافت؟'),
(2316, 19, 66, 'و انسان (بیچاره در امر معاد شک کند و) گوید: آیا چون من بمیرم باز به زودی مرا از قبر بیرون آورده و زنده خواهند کرد؟!'),
(2317, 19, 67, 'آیا آدمی (برای ایمان به معاد) متذکر آن نمی‌شود که اول که هیچ محض و معدوم صرف بود ما او را ایجاد کردیم؟'),
(2318, 19, 68, 'قسم به خدای تو که البته آنها را با شیاطین در قیامت محشور می‌گردانیم، آن‌گاه همه را احضار خواهیم کرد تا گرد آتش جهنم به زانو درآیند.'),
(2319, 19, 69, 'سپس هر که عتوّ و سرکشی بر فرمان خدای مهربان بیشتر کرده یک یک را از هر فرقه بیرون آریم (تا به دوزخ درافکنیم).'),
(2320, 19, 70, 'آن‌گاه ما آنهایی را که سزاوارتر به درآمدن به آتش دوزخند بهتر می‌شناسیم.'),
(2321, 19, 71, 'و هیچ یک از شما (نوع بشر) باقی نماند جز آنکه به دوزخ وارد شود؛ این حکم حتمی پروردگار توست.'),
(2322, 19, 72, 'پس از ورود همه در دوزخ، ما افرادی را که خداترس و باتقوا بوده‌اند نجات خواهیم داد و ستمکاران را فرو گذاریم تا در آن آتش به زانو درافتند.'),
(2323, 19, 73, 'و هرگاه (در فضیلت مؤمن بر کافر) آیات واضحه ما بر مردم تلاوت شود کافران (غنی و متکبر) به مؤمنان (در مقام مفاخره) گویند: کدام یک از ما دو فرقه مقاماتمان بهتر و مجلس و انجمنمان نیکوتر خواهد بود؟'),
(2324, 19, 74, 'در صورتی که ما پیش از این کافران چه گروه‌های بسیاری را که از اینها داراتر و خوشتر بودند همه را هلاک گردانیدیم.'),
(2325, 19, 75, 'بگو که هر کس که به راه کفر و ضلالت رفت خدای مهربان به او مهلت می‌دهد تا آن ساعت که وعده عذاب را به چشم خود ببینند که یا عذاب (قتل و اسیری) چشد یا ساعت قیامت وی را فرا رسد، پس کافران به زودی خواهند دانست که (از مؤمن و کافر) کدام یک روزگارش بدتر و سپاهش ضعیف‌تر است!'),
(2326, 19, 76, 'و خدا هدایت یافتگان را بر هدایت می‌افزاید و اعمال صالحی که (اجرش نعمت) ابدی است نزد پروردگار تو بهتر (از مال و جاه فانی دنیا) است هم از جهت ثواب الهی و هم از جهت حسن عاقبت اخروی.'),
(2327, 19, 77, '(ای رسول) دیدی حال آن کس را که به آیات ما کافر شد (مانند عاص بن وائل کافر) و (به خبّاب بن ارت مؤمن به استهزاء و مسخره) گفت: من البته مال و فرزند بسیار (در بهشت هم) خواهم داشت (و آنجا طلب تو را می‌دهم)؟!'),
(2328, 19, 78, 'آیا بر عالم غیب آگاهی یافته یا از خدای رحمن (بر خود) عهدی گرفته است (که در عالم آخرت هم دارای نعمت و ثروت باشد).'),
(2329, 19, 79, 'هرگز چنین نیست، ما البته آنچه گوید خواهیم نوشت و سخت بر عذابش خواهیم افزود.'),
(2330, 19, 80, 'و آنچه (از مال و متاع و فرزند) که (به مفاخرت و غرور دایم) در گفتار داشت ما وارث آن شویم و او بی‌کس و تنها به جانب ما بازآید.'),
(2331, 19, 81, 'و مشرکان، خدای یگانه را ترک گفته و خدایان باطل (مانند بتها و فراعنه) را برای عزت و احترام دنیوی برگرفتند.'),
(2332, 19, 82, 'چنین نیست، بلکه به زودی پرستش آن خدایان باطل را انکار کنند و به خصومت آنها برخیزند.'),
(2333, 19, 83, 'آیا ندیدی که ما شیاطین را بر سر کافران فرستادیم تا سخت آنها را تحریک کنند (و به وهم و خیالات باطل دنیا آنان را به گناه اندازند و از سعادت ابد محرومشان سازند).'),
(2334, 19, 84, 'پس تعجیل در کار آنها مکن که ما حساب (روز و ساعت عذاب) آنان را کاملا در نظر داریم.'),
(2335, 19, 85, '(یاد آور) روزی که متقیان را به سوی خدای مهربان به اجتماع محشور گردانیم.'),
(2336, 19, 86, 'و بدکاران را تشنه لب به سوی آتش دوزخ برانیم.'),
(2337, 19, 87, 'در آن روز هیچ کس مالک شفاعت نباشد مگر کسی که (به پرستش حق) از خدای مهربان عهدنامه (توحید کامل و شفاعت) دریافته است.'),
(2338, 19, 88, 'و کافران گفتند که خدای رحمان فرزند برگرفته!'),
(2339, 19, 89, '(ای کافران) همانا شما سخنی بسیار زشت و منکر بر زبان آوردید.'),
(2340, 19, 90, 'نزدیک است از این گفته زشت و عقیده باطل آسمانها از هم فرو ریزد و زمین بشکافد و کوهها متلاشی گردد.'),
(2341, 19, 91, 'چرا که برای خدای مهربان فرزندی (مسیح و عزیر و ملایک را) دعوی کردند.'),
(2342, 19, 92, 'در صورتی که هرگز خدای رحمن را (که منزه از مثل و مانند است) فرزند داشتن سزاوار نخواهد بود.'),
(2343, 19, 93, 'بلکه هیچ موجودی در آسمانها و زمین نیست جز اینکه خدا را بنده فرمانبردار است.'),
(2344, 19, 94, 'محقّقا او به شماره همه موجودات کاملا آگاه است.'),
(2345, 19, 95, 'و تمام آنها روز قیامت منفرد و تنها به پیشگاه حضرتش حضور می‌یابند.'),
(2346, 19, 96, 'همانا آنان که به خدا ایمان آوردند و نیکوکار شدند خدای رحمان آنها را (در نظر خلق و حق) محبوب می‌گرداند.'),
(2347, 19, 97, 'ما حقایق این قرآن را بی‌تردید به زبان تو سهل و آسان کردیم تا به آن اهل تقوا را بشارت دهی و معاندان لجوج را بترسانی.'),
(2348, 19, 98, 'و چه بسیار اقوامی از اهل عناد را که پیش از این معاندین هلاک ساختیم! آیا چشم تو به احدی از آنها دیگر خواهد افتاد؟ یا کمترین صدایی از آنان خواهی شنید؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طه (ای مشتاق حق و هادی خلق).'),
(2350, 20, 2, 'ما قرآن را از آن بر تو نازل نکردیم که (از کثرت عبادت خدا و جهد و کوشش در هدایت خلق) خویشتن را به رنج درافکنی.'),
(2351, 20, 3, 'تنها غرض از نزول قرآن آن است که مردم خداترس را متذکر و بیدار سازی.'),
(2352, 20, 4, 'این کتاب بزرگ فرستاده آن کسی است که زمین و آسمانهای بلند را آفرید.'),
(2353, 20, 5, 'آن خدای مهربانی که بر عرش (عالم وجود و کلیه جهان آفرینش به علم و قدرت) مستولی و محیط است.'),
(2354, 20, 6, 'هر چه در آسمانها و زمین و بین آنها و زیر کره خاک موجود است همه ملک اوست.'),
(2355, 20, 7, 'و اگر به آواز بلند یا آهسته سخن گویی (یکسان است که) همانا خدا بر نهان و مخفی‌ترین امور جهان کاملا آگاه است.'),
(2356, 20, 8, 'به جز خدای یکتا که همه اسماء و صفات نیکو مخصوص اوست خدایی نیست.'),
(2357, 20, 9, 'و آیا داستان موسی به تو رسیده است؟'),
(2358, 20, 10, 'آن‌گاه که آتشی مشاهده کرد و به اهل بیت خود (که در بیابان سرد به آتش محتاج بودند) گفت: اندکی مکث کنید که از دور آتشی به چشم دیدم، باشد که یا پاره‌ای از آن آتش بر شما بیاورم یا از آن (به جایی) راه یابم.'),
(2359, 20, 11, 'چون موسی به آن آتش نزدیک شد (در میان درختی آتشی روشن دید و از آنجا) ندا شد که ای موسی،'),
(2360, 20, 12, 'منم پروردگار تو، نعلین (همه علایق غیر مرا) از خود به دور کن که اکنون در وادی مقدس طوی (و مقام قرب ما) قدم نهاده‌ای.'),
(2361, 20, 13, 'و من تو را (به رسالت خود) برگزیدم، در این صورت به سخن وحی گوش فرا ده.'),
(2362, 20, 14, 'منم خدای یکتا که هیچ خدایی جز من نیست، پس مرا (به یگانگی) بپرست و نماز را مخصوصا برای یاد من به پادار.'),
(2363, 20, 15, 'و محققا ساعت قیامت خواهد رسید و من خواهم که آن ساعت را پنهان دارم تا (مردم آزمایش شوند و) هر نفسی به پاداش اعمالش در (آن روز) برسد.'),
(2364, 20, 16, 'پس زنهار مردمی که به قیامت ایمان ندارند و پیرو هوای نفسند تو را از یاد آن (روز هولناک) بازندارند و غافل نکنند و گرنه هلاک خواهی شد.'),
(2365, 20, 17, 'و ای موسی، اینک بازگو تا چه به دست راست داری؟'),
(2366, 20, 18, 'موسی عرضه داشت: این عصای من است که بر آن تکیه می‌زنم و با آن از درختان بر گوسفندانم برگ می‌ریزم و حوائجی دیگر (چون مدد راه رفتن و دفع دشمن و غیره) نیز به آن انجام می‌دهم.'),
(2367, 20, 19, 'خدا فرمود: ای موسی این عصا بیفکن.'),
(2368, 20, 20, 'موسی چون آن را به زمین افکند عصا اژدهایی مهیب شد که (به هر سو) می‌شتافت.'),
(2369, 20, 21, 'باز (حضرت احدیت) فرمود: عصا را برگیر و از آن مترس که ما آن را به حالت اولش برمی‌گردانیم.'),
(2370, 20, 22, 'و دست به زیر بغل خود بر تا دستی بی‌هیچ عیب سپید و رخشان بیرون آید و این معجزه دیگر تو خواهد بود.'),
(2371, 20, 23, 'تا باز هم از آیات بزرگ خود به تو ارائه دهیم.'),
(2372, 20, 24, 'اینک (به رسالت) به جانب فرعون روانه شو که وی سخت طغیان کرده است.'),
(2373, 20, 25, 'موسی عرضه داشت: پروردگارا، شرح صدرم عطا فرما (که از جفا و آزار مردم تنگدل نشوم).'),
(2374, 20, 26, 'و کار مرا برایم آسان گردان.'),
(2375, 20, 27, 'و عقده را از زبانم بگشا.'),
(2376, 20, 28, 'تا مردم سخنم را نیکو فهم کنند.'),
(2377, 20, 29, 'و نیز از اهل بیت من یکی را وزیر و معاون من فرما.'),
(2378, 20, 30, 'برادرم هارون را (وزیر من گردان).'),
(2379, 20, 31, 'پشت مرا به او محکم کن.'),
(2380, 20, 32, 'و او را در امر (رسالت با) من شریک ساز.'),
(2381, 20, 33, 'تا دائم به ستایش و سپاس تو پردازیم.'),
(2382, 20, 34, 'و تو را بسیار یاد کنیم.'),
(2383, 20, 35, 'که همانا بس تویی بصیر به احوال ما.'),
(2384, 20, 36, 'پروردگار فرمود: ای موسی آنچه از ما خواستی همه به تو اعطا گردید.'),
(2385, 20, 37, 'و همانا ما بر تو بار دیگر نیز منّت بزرگی نهادیم.'),
(2386, 20, 38, 'آن هنگام که (برای حفظ جان تو از بلای فرعونیان) به مادرت وحی مهمی نمودیم.'),
(2387, 20, 39, 'که کودک خود را در صندوقی گذار و به دریا افکن و باید که امواج دریا کودک را به ساحل رساند تا دشمن من و آن طفل (یعنی فرعون) از دریا طفل را برگیرد؛ و من به لطف خود از تو بر دلها (ی دشمن و دوست) محبت افکندم (تا تو را دوست بدارند) و تا تربیت و پرورشت زیر نظر من انجام گیرد.'),
(2388, 20, 40, 'آن‌گاه که (از مادر دور افتادی) خواهرت (در پی تو) روان بود (تا تو را نزد فرعونیان یافت) و می‌گفت: می‌خواهید یکی را که (شیر و تربیت) این طفل را تکفل کند به شما معرفی کنم؟ پس ما (بدین وسیله) تو را به مادرت برگردانیدیم تا به دیدار تو دیده‌اش روشن گردد و دیگر اندوه نخورد. و (باز منّت دیگر آنکه) یک نفر (از فرعونیان) را کشتی و ما از غم آن نیز تو را نجات دادیم و بارها تو را به امتحان و ابتلای سخت بیازمودیم، پس چند سالی در میان مردم مدین زیستی تا آنکه حال ای موسی به موقع آمدی (تا به مقام نبوت برسی).'),
(2389, 20, 41, 'و تو را برای (مقام رسالت و محبت) خود پروردم.'),
(2390, 20, 42, 'اکنون تو و برادرت با معجزات و آیاتی که به شما دادم (از پی رسالت خود) بروید و سستی در ذکر من (در نزد فرعون) روا مدارید.'),
(2391, 20, 43, 'بروید به سوی فرعون که او سخت به راه طغیان شتافته است.'),
(2392, 20, 44, 'و با او کمال آرامی و نرمی سخن گویید، باشد که متذکر شود یا (از خدا) بترسد (و ترک ظلم کند).'),
(2393, 20, 45, 'موسی و هارون عرض کردند: بارالها، می‌ترسیم که فرعون بی‌درنگ بر ما ظلم و عقوبت کند یا بر کفر و سرکشی خود بیفزاید.'),
(2394, 20, 46, 'خدا فرمود: هیچ نترسید که من با شمایم (و همه گفتار و رفتار شما را با او) می‌شنوم و می‌بینم.'),
(2395, 20, 47, 'اینک هر دو به جانب فرعون رفته و بگویید که ما دو رسول پروردگار توایم، پس بنی اسرائیل را با ما فرست و (بیش از این) عذاب به آنها نکن، که همانا ما با آیت و معجزه‌ای از جانب خدای آفریننده تو نزد تو آمده‌ایم، و سلام (حق) بر آن کس که طریق هدایت را پیروی کند.'),
(2396, 20, 48, 'به ما چنین وحی شده که همانا عذاب (سخت خدا) بر آن کس است که (خدا و رسولانش را) تکذیب کند و (از حق) روی بگرداند.'),
(2397, 20, 49, 'فرعون گفت: ای موسی، خدای شما کیست؟'),
(2398, 20, 50, 'موسی پاسخ داد که خدای ما آن کسی است که همه موجودات عالم را نعمت وجود خاص خودش بخشیده و سپس (به راه کمالش) هدایت کرده است.'),
(2399, 20, 51, 'فرعون گفت: پس حال اقوام سلف (که خداشناس و خداپرست نبودند) چیست؟'),
(2400, 20, 52, 'موسی پاسخ داد که احوالشان به علم ازلی خدای من در کتاب (لوح محفوظ) ثبت است؛ هرگز از حال احدی خدای مرا خطا و فراموشی نیست.'),
(2401, 20, 53, 'همان خدایی که زمین را آسایشگاه شما قرار داد و در آن راهها برای شما پدید آورد؛ و هم از آسمان آب نازل کرد تا به آن آب آسمانی انواع نباتات مختلف از زمین برویانیدیم.'),
(2402, 20, 54, 'شما از آن نعمتها تناول کنید و چهارپایانتان را هم بچرانید، که همانا در این کار آیاتی (از ربوبیت) برای خردمندان پدیدار است.'),
(2403, 20, 55, 'ما شما را هم از این خاک آفریدیم و هم به این خاک بازمی‌گردانیم و هم بار دیگر (روز قیامت) از این خاک بیرون می‌آوریم.'),
(2404, 20, 56, 'و همانا ما به فرعون (به واسطه موسی) همه آیات روشن خود را نمودیم و او همه را تکذیب کرد و از آنها سرباز زد.'),
(2405, 20, 57, 'فرعون گفت: ای موسی، تو آمده‌ای به طمع آنکه ما را از کشورمان به سحر و شعبده خود بیرون کنی؟'),
(2406, 20, 58, 'ما هم در مقابل سحر تو سحری البته خواهیم آورد پس میان ما و خودت موعدی معین کن که بی‌آنکه هیچ‌یک از ما خلف وعده کنیم در سرزمین مسطحی (که خلایق ببینند، برای سحر و ساحری) مهیا شویم.'),
(2407, 20, 59, 'موسی گفت: وعده ما و شما روز زینت (یعنی روز عید قبطیان) باشد و مردم پیش از ظهر (به موعد برای مشاهده) همه گرد آورده شوند.'),
(2408, 20, 60, 'آن‌گاه فرعون (از موسی) رو گردانید و به تدبیر جمع آوری سحر و ساحران پرداخت، سپس (با ساحران بسیار به وعده‌گاه) آمد.'),
(2409, 20, 61, 'موسی ساحران را گفت: وای بر شما! زنهار بر خدا (به سحر) دروغ مبندید که به عذابی بنیاد شما را بر باد هلاک دهد، و هر کس به خدا افترا بست سخت زیانکار شد.'),
(2410, 20, 62, 'پس آنها در کارشان به گفت و شنید پرداختند و (در نتیجه گفتند که کار موسی سحر و ساحری نیست ولی) راز خود را پنهان داشتند.'),
(2411, 20, 63, 'فرعونیان گفتند: این دو تن (موسی و هارون) دو ساحرند که می‌خواهند به سحرانگیزی خود، شما مردم را از سرزمین خود بیرون کنند و طریقه نیکوی شما را (که اطاعت فرعون است) از میان ببرند.'),
(2412, 20, 64, 'پس (ای ساحران) باید شما با هر مکر و تدبیری توانید مهیا شده و (مقابل این دو ساحر) صف آرایی کنید، که امروز آن‌کس که غلبه و برتری یابد محققا او فیروزی یافته است.'),
(2413, 20, 65, 'ساحران گفتند: ای موسی تو نخست به کار خواهی پرداخت یا ما اول بساط خود بیفکنیم؟'),
(2414, 20, 66, 'موسی گفت: شما اول بساط خود درافکنید، (آنها بساط خویش افکندند) که ناگاه در اثر سحر آنان رسنها و چوبهاشان پنداشتی در نظر به جنبش و رفتار آمد.'),
(2415, 20, 67, 'در آن حال موسی در دل خویش سخت بترسید (که مبادا امر بر مردم مشتبه شود و میان سحر ساحران با معجز موسی فرق نگذارند و حجت او بر خلق آشکار نگردد).'),
(2416, 20, 68, 'ما گفتیم: مترس که تو البته همیشه غلبه و برتری خواهی داشت.'),
(2417, 20, 69, 'و اینک عصایی که در دست داری بیفکن تا (اژدها شود و یکباره) بساط سحر و ساحری اینان را فرو بلعد، که کار اینان حیله ساحری بیش نیست و ساحر هر جا رود (و هر چه کند) هرگز فلاح و فیروزی نخواهد یافت.'),
(2418, 20, 70, 'پس همه ساحران به سجده افتاده و گفتند: ما به خدای موسی و هارون ایمان آوردیم.'),
(2419, 20, 71, 'فرعون گفت: شما چرا پیش از آنکه من اجازه دهم به موسی ایمان آوردید؟ معلوم است که او در سحر معلم شما بوده، باری من شما را دست و پا بر خلاف یکدیگر می‌برم و بر تنه نخلهای خرما به دار می‌آویزم و خواهید دانست که عذاب (من و موسی) کدام سخت‌تر و پاینده‌تر است.'),
(2420, 20, 72, 'ساحران به فرعون پاسخ دادند که ما تو را هرگز بر این معجزات آشکار که به ما آمده و بر خدایی که ما را آفریده مقدم نخواهیم داشت، پس در حق ما هر چه توانی بکن که هر ظلمی کنی همین حیات دو روزه دنیاست.'),
(2421, 20, 73, 'ما به راستی به خدای خود ایمان آوردیم تا از خطاهای ما درگذرد و گناه سحری که تو ما را به اجبار بر آن داشتی ببخشد، و (لطف و مغفرت) خدا بهتر و پاینده‌تر (از حیات فانی دنیا) است.'),
(2422, 20, 74, 'که همانا هر کس به خدای خود طاغی و گنهکار وارد شود جزاء او جهنّم است که در آنجا نه بمیرد (تا از عذاب برهد) و نه زنده شود (که از لذت زندگی برخوردار باشد).'),
(2423, 20, 75, 'و هر کس به خدای خود مؤمن باشد و با اعمال صالح بر او وارد شود اجر آنها هم عالی‌ترین درجات بهشتی است.'),
(2424, 20, 76, 'آن بهشتهای عدنی که دائم زیر درختانش نهرها جاری است و تا ابد در آنجا (از نعمت و حیات برخوردار) هستند و آن بهشت پاداش کسی است که خود را (از کفر و عصیان و شرک و طغیان) پاک و پاکیزه گرداند.'),
(2425, 20, 77, 'و همانا به موسی وحی کردیم که بندگان (مؤمن) مرا شبانه (از شهر مصر) بیرون برو (به اعجاز و لطف ما) راهی خشک از میان دریا بر آنها پدید آور که نه از تعقیب و رسیدن فرعونیان ترسناک باشی و نه (از غرق شدن) اندیشه داری.'),
(2426, 20, 78, 'و فرعون با سپاهش از پی آنها تاختند پس موج دریا چنان آنها را فرو برد که از آنان اثری باقی نگذاشت.'),
(2427, 20, 79, 'و فرعون پیروان خود را علاوه بر اینکه هدایت نکرد سخت به ضلالت و بدبختی افکند.'),
(2428, 20, 80, 'ای بنی اسرائیل، محققا ما شما را از شر دشمن نجات دادیم و با شما در جانب راست کوه طور وعده نهادیم (تا به مناجات موسی کلام حق را بشنوید) و (در بیابان که سرگردان شدید) برای قوت شما ترنجبین و مرغ بریان فرستادیم.'),
(2429, 20, 81, '(و دستور دادیم که) از این رزق حلال و پاکیزه که نصیبتان کردیم تناول کنید و در آن (به کفر نعمت و ترک شکرگزاری) طغیان و سرکشی مکنید وگرنه مستحق غضب و خشم من می‌شوید، و هر کس مستوجب خشم من گردید همانا خوار و هلاک خواهد شد.'),
(2430, 20, 82, 'و البته بر آن کس که (از کفر) توبه کند و (به خدا) ایمان آرد و نکوکار گردد و درست به راه هدایت رود مغفرت و آمرزش من بسیار است.'),
(2431, 20, 83, 'و ای موسی بازگو که بر قومت چرا سبقت گرفته و با شتاب به وعده‌گاه آمدی؟'),
(2432, 20, 84, 'موسی عرضه داشت: هم اینک قوم از پی من هستند و من خود برای خشنودی تو تعجیل کرده و بر آنها تقدم جستم.'),
(2433, 20, 85, 'خدا فرمود: همانا ما قوم تو را پس از آمدن تو آزمایش کردیم و سامری آنان را گمراه کرد.'),
(2434, 20, 86, 'موسی (که از فتنه سامری آگاه شد) متأسف و غضبناک به سوی قوم بازگشت و گفت: ای قوم من، مگر خدای شما به شما وعده احسان نداد (که شما را نعمت کتاب بزرگ تورات عطا کند)؟ آیا وعده خدا بر شما طولانی شد، یا مایل شدید که مستوجب قهر و غضب خدای خود شوید، که وعده مرا خلاف کردید (و عهد من شکستید).'),
(2435, 20, 87, 'قوم به موسی گفتند: ما به میل و اختیار خود خلاف وعده تو نکردیم و لیکن اسباب تجمل و زینت بسیاری (از فرعونیان) بار دوش ما نهادند و ما آنها را در آتش افکندیم و چنین سامری (فتنه‌انگیز بر ما) القا کرد.'),
(2436, 20, 88, 'آن‌گاه سامری با آن زر و زیورها مجسّمه گوساله‌ای بساخت که صدایی شگفت داشت و (با این شعبده سامری و پیروانش) گفتند: خدای شما و خدای موسی همین گوساله است که موسی فراموش کرده است.'),
(2437, 20, 89, 'آیا این گوساله‌پرستان نمی‌نگرند که آن گوساله هیچ حرف و اثری و هیچ خیر و شری به آنها عاید نمی‌سازد؟'),
(2438, 20, 90, 'و هارون هم پیش از آنکه موسی باز آید به آنان گفت: ای قوم، این گوساله اسباب فتنه و امتحان شما گردیده و محققا آفریننده شما خدای مهربان است (نه این گوساله سامری) پس شما پیرو من شوید و امر مرا فرمان برید.'),
(2439, 20, 91, 'قوم گفتند: ما بر این پرستش گوساله ثابت هستیم تا وقتی که موسی به سوی ما بازگردد.'),
(2440, 20, 92, 'موسی (چون بازگشت با عتاب به هارون) گفت: ای هارون مانع تو چه بود که چون دیدی که قوم گمراه شدند،'),
(2441, 20, 93, 'از پی من نیامدی؟ آیا نافرمانی امر من کردی؟!'),
(2442, 20, 94, 'هارون گفت: ای برادر مهربان (بر من قهر و عتاب مکن و) سر و ریش من مگیر، ترسیدم بگویی تو میان بنی اسرائیل تفرقه انداختی و به سخنم وقعی ننهادی.'),
(2443, 20, 95, 'آن‌گاه موسی گفت: ای سامری این فتنه چه بود که تو برپا کردی؟'),
(2444, 20, 96, 'سامری گفت: من چیزی را دیدم که قوم ندیدند، من مشتی خاک از اثر قدم رسول (حق، جبرئیل) برگرفته و (در گوساله) ریختم و نفس من چنین (فتنه انگیزی را) به نظرم جلوه داد.'),
(2445, 20, 97, 'موسی گفت: از میان ما برو که تو در زندگانی دنیا (به مرضی معذب خواهی شد که همه از تو متنفر شوند و) دایم گویی کسی مرا نزدیک نشود، و (در آخرت) هم وعده‌گاهی (در دوزخ) داری که با تو تخلف نخواهد شد، و اکنون این خدایت را که بر پرستش و خدمتش ایستادی بنگر که آن را در آتش می‌سوزانیم و خاکسترش را به آب دریا می‌دهیم.'),
(2446, 20, 98, 'تنها خدای شما آن یگانه خدایی است که جز او هیچ خدایی نیست و علمش به همه ذرات عوالم هستی محیط است.'),
(2447, 20, 99, 'و ما اخبار گذشتگان دیگر را این چنین بر تو حکایت خواهیم کرد و از نزد خود این ذکر (یعنی کتاب عظیم الشأن قرآن) را به تو عطا کردیم.'),
(2448, 20, 100, 'هر کس از این ذکر اعراض کند روز قیامت بار سنگینی از گناه را به دوش خواهد داشت.'),
(2449, 20, 101, 'اینان در عذاب آن اعمال زشت مخلدند و سخت است بسی در قیامت بار اعمال زشتی که به دوش گرفته‌اند.'),
(2450, 20, 102, 'روزی که در صور دمیده شود و (در آن روز) بدکاران را کبود چشم محشور کنیم (و چشم کبودشان نشانه جرم خواهد بود).'),
(2451, 20, 103, 'و آنها (از هول و هراس آن روز) با یکدیگر آهسته زیر لب گویند که (ای افسوس) ده روزی بیش (در زندگی دنیا) درنگ نکردید (و نعمت ابدی بهشت را برای دنیای فانی از دست بدادید).'),
(2452, 20, 104, 'ما به آنچه می‌گویند بهتر آگاهیم، که از آن بدکاران بهتر و درست‌ترینشان به آنها خواهد گفت: شما روزی بیش درنگ نکردید (دریغا چه زود عمر در شهوت و معصیت بگذشت و جرم و عقابش بماند!)'),
(2453, 20, 105, 'و (ای رسول) از تو پرسند که کوهها (در روز قیامت) چه می‌شود؟ جواب ده که خدای من کوهها را چنان از بنیاد برکند که خاک شده و خاکش بر باد دهد.'),
(2454, 20, 106, 'آن‌گاه پست و بلندیهای زمین را چنان هموار گرداند.'),
(2455, 20, 107, 'که در آن ابدا هیچ بلندی و پستی نخواهی دید.'),
(2456, 20, 108, 'در آن روز خلایق همه از پی کسی که آنها را به راه مستقیم به عرصه قیامت دعوت کند (یعنی اسرافیل) ناچار خواهند رفت، و صداها پیش خدای رحمان خاشع و خاموش گردد که جز زیر لب و آهسته صدایی نخواهی شنید.'),
(2457, 20, 109, 'در آن روز، شفاعت هیچ‌کس سود نبخشد جز آن‌کس که خدای رحمان به او رخصت شفاعت داده و سخنش را پسندیده باشد.'),
(2458, 20, 110, 'و خدا (به علم ازلی) بر همه آینده و گذشته خلایق آگاه است و خلق را ابدا به او احاطه و آگاهی نیست.'),
(2459, 20, 111, 'و بزرگان عالم همه در پیشگاه عزت آن خدای حی توانا ذلیل و خاضعند و (در آن روز) هر که بار ظلم و ستم به دوش دارد سخت زبون و زیانکار است.'),
(2460, 20, 112, 'و هر کس اعمالش نیکو است و (به خدا هم) ایمان دارد از هیچ ستم و آسیبی بیمناک نخواهد بود.'),
(2461, 20, 113, 'و ما این‌گونه این (قرآن عظیم) را قرآنی با فصاحت کامل عربی بر تو فرستادیم و در آن وعده‌ها و اهوال قیامت را به انواع گوناگون تذکر دادیم، باشد که مردم پرهیزکار شوند یا پندی از نو بر ایشان آورد.'),
(2462, 20, 114, 'پس بلند مرتبه است و بزرگوار خدایی که به حق و راستی پادشاه ملک وجود است و تو (ای رسول) پیش از آنکه وحی قرآن تمام و کامل به تو رسد تعجیل در (تلاوت و تعلیم) آن مکن و دائم بگو: پروردگارا بر علم من بیفزا.'),
(2463, 20, 115, 'و همانا ما پیش از این با آدم عهدی بستیم (که فریب شیطان نخورد) و او فراموش کرد و در آن عهد او را استوار و ثابت قدم نیافتیم.'),
(2464, 20, 116, 'و (یاد آر) هنگامی که فرشتگان را گفتیم به آدم سجده کنید، همه سجده کردند جز ابلیس که امتناع ورزید.'),
(2465, 20, 117, 'آن‌گاه گفتیم: ای آدم محققا این شیطان با تو و جفتت دشمن است، مبادا شما را از بهشت بیرون آرد و از آن پس به شقاوت و بدبختی گرفتار شوی.'),
(2466, 20, 118, 'همانا برای تو این هست که نه هرگز آنجا گرسنه شوی و نه برهنه و عریان مانی.'),
(2467, 20, 119, 'و نه هرگز در آنجا به تشنگی و به گرمای آفتاب آزار بینی.'),
(2468, 20, 120, 'باز شیطان در او وسوسه کرد، گفت: ای آدم آیا (میل داری) تو را بر درخت ابدیت و ملک جاودانی دلالت کنم؟'),
(2469, 20, 121, 'پس آدم و حوّا (فریب خوردند و) از آن درخت تناول کردند، بدین جهت (لباسهای بهشتی از تنشان دور و عیوب و) عورت آنها در نظرشان پدیدار شد و شروع کردند به ساتری از برگ درختان بهشت خود را پوشاندن. و آدم نافرمانی خدای کرد و گمراه شد.'),
(2470, 20, 122, 'سپس خدا او را (به مقام نبوتش) برگزید و توبه او را پذیرفت و هدایتش فرمود.'),
(2471, 20, 123, 'آن‌گاه خدا (به آدم و حوا) فرمود: اکنون از عالی رتبه بهشت فرود آیید که برخی از شما با برخی دیگر دشمنید، پس اگر از جانب من برای شما راهنمایی بیاید آن هنگام هر که از راه من پیروی کند نه هرگز گمراه شود و نه شقی و بدبخت گردد.'),
(2472, 20, 124, 'و هر کس از یاد من اعراض کند همانا (در دنیا) معیشتش تنگ شود و روز قیامتش نابینا محشور کنیم.'),
(2473, 20, 125, 'گوید: الها، چرا مرا نابینا محشور کردی و حال آنکه من بینا بودم؟'),
(2474, 20, 126, 'خدا به او فرماید: (آری) بدین گونه آیات ما برای هدایت تو آمد و همه را به طاق فراموشی و غفلت نهادی، و امروز هم تو فراموش (و بی‌بهره) خواهی شد.'),
(2475, 20, 127, 'و این چنین ما هر کس را که ظلم و نافرمانی کند و ایمان به آیات پروردگارش نیاورد مجازات سخت می‌کنیم با آنکه عذاب آخرتش سخت‌تر و پاینده‌تر خواهد بود.'),
(2476, 20, 128, 'آیا این کفار قریش را از مشاهده حال طوایف بسیاری از گذشتگان که ما همه را هلاک کردیم و اینان در منازل و عمارات آنها می‌روند (عبرت و پند و) هدایت نبود؟ بی‌شک احوال گذشتگان برای خردمندان بسیار مایه عبرت و آیه هدایت است.'),
(2477, 20, 129, 'اگر نه این بود که کلمه پروردگار (و تقدیر ازلیش بر این کار) سبقت یافته (که کافران و بدکاران این امت در قیامت به کیفر رسند) همانا عذاب (در دنیا بر آنها) لزوم می‌یافت و آن اجل معین فرا می‌رسید.'),
(2478, 20, 130, 'پس تو بر آنچه (امت جاهل بر انکار و طعن تو) می‌گویند صبر و تحمل پیش گیر و خدای را پیش از طلوع خورشید و بعد از غروب آن و ساعاتی از شب تار و اطراف روز روشن ستایش و تسبیح گو، باشد که (به مقام رفیع شفاعت) خشنود شوی.'),
(2479, 20, 131, 'و هرگز به متاع ناچیزی که به قومی از آنان (قومی کافر و جاهل) در جلوه حیات دنیای فانی برای امتحان داده‌ایم چشم آرزو مگشا، و رزق خدای تو بسیار بهتر و پاینده‌تر است.'),
(2480, 20, 132, 'تو اهل بیت خود را به نماز و طاعت خدا امر کن و خود نیز بر نماز و ذکر حق صبور باش، ما از تو روزی (کسی را) نمی‌طلبیم بلکه ما به تو (و دیگران) روزی می‌دهیم، و عاقبت نیکو مخصوص (اهل) پرهیزکاری و تقواست.'),
(2481, 20, 133, 'و کافران گفتند: چرا (محمد) آیت و دلیلی روشن از جانب خدا برای ما نمی‌آورد؟ آیا آیات بیّنه کتب پیشین (چون تورات و انجیل و صحف که در همه ذکر اوصاف نبوت هست) بر آنان نیامد؟'),
(2482, 20, 134, 'و اگر ما پیش از فرستادن پیغمبر، کافران (و مشرکان قریش) را همه به نزول عذاب هلاک می‌کردیم البته آنها می‌گفتند: پروردگارا چرا بر ما رسولی نفرستادی تا از آیات تو پیروی کنیم پیش از آنکه به این عذاب و ذلت و خواری گرفتار شویم؟'),
(2483, 20, 135, 'بگو: هر یک (از ما و شما) منتظریم، پس مترصد و منتظر (امر خدا) باشید که به زودی خواهید دانست (ما و شما) کدام به راه مستقیم سعادت رفته و طریق هدایت یافته‌ایم.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'روز حساب مردم بسیار نزدیک شده و مردم سخت غافلند و اعراض می‌کنند.'),
(2485, 21, 2, 'این مردم غافل را هیچ پند و موعظه تازه‌ای از جانب پروردگارشان نیاید جز آنکه آن پند را شنیده و باز به بازی دنیا و لهو و لعب عمر می‌گذرانند.'),
(2486, 21, 3, 'دلهاشان به لهو و بازیچه دنیا متوجه است؛ و مردم ستمکار پنهان و آهسته با یکدیگر می‌گویند: آیا این شخص جز آن است که بشری مانند شماست؟ چرا شما که مردمی بصیر و دانا هستید سحر او را می‌پذیرید؟'),
(2487, 21, 4, 'پیامبر گفت: خدای من هر حرفی که خلق در آسمان و زمین گویند همه را می‌داند، که او خدای شنوا و داناست.'),
(2488, 21, 5, 'بلکه این مردم (غافل نادان) گفتند که سخنان قرآن خواب و خیالی بی‌اساس است، بلکه این کلمات را خود فرابافته بلکه شاعر بزرگی است وگرنه باید مانند پیغمبران گذشته آیت و معجزه‌ای برای ما بیاورد.'),
(2489, 21, 6, 'پیش از اهل مکه هم ما اهل هر شهری که ایمان (به خدا و روز قیامت) نیاوردند همه را هلاک کردیم آیا اهل مکه ایمان خواهند آورد؟'),
(2490, 21, 7, 'و ما پیش از تو کسی را به رسالت نفرستادیم جز مردانی (پاک) را که به آنها (هم مانند تو) وحی می‌فرستادیم؛ شما اگر خود نمی‌دانید بروید و از اهل ذکر (و دانشمندان امت) سؤال کنید.'),
(2491, 21, 8, 'و ما پیغمبران را (چون فرشته) بدون بدن دنیوی قرار ندادیم تا به غذا و طعام محتاج نباشند و در دنیا هم همیشه زنده نماندند.'),
(2492, 21, 9, 'آن‌گاه ما به وعده‌ای که به آنها دادیم وفا کردیم و آنان را با هر که خواستیم (از شر دشمنان) نجات دادیم و مسرفان ظالم را هلاک گردانیدیم.'),
(2493, 21, 10, 'همانا ما به سوی شما امت کتابی که مایه شرافت و عزت شماست فرستادیم، آیا نباید در این کتاب بزرگ تعقل کرده و حقایق آن را فهم کنید؟'),
(2494, 21, 11, 'و چه بسیار مردم ستمگری در شهر و دیارها بودند که ما آنها را درهم شکسته و هلاک ساختیم و قومی دیگر به جای آنها بیافریدیم.'),
(2495, 21, 12, 'پس هنگامی که آن ستمکاران عذاب ما را به چشم مشاهده کردند از آن دیار رو به فرار نهادند.'),
(2496, 21, 13, '(در آن حال به آنها خطاب شد که) مگریزید و رو به کامرانیها و خانه‌های خود آرید (و به اصلاح فسادکاری خویش بپردازید) که روزی ممکن است از آن بازخواست شوید.'),
(2497, 21, 14, 'ظالمان به حسرت و ندامت گفتند: وای بر ما که سخت ستمکار بودیم.'),
(2498, 21, 15, 'و پیوسته همین گفتار (حسرت بار) بر زبانشان بود تا آنکه ما همه را طعمه شمشیر مرگ و خاموش هلاکت ساختیم.'),
(2499, 21, 16, 'و ما آسمان و زمین و آنچه را که بین زمین و آسمان است به بازیچه نیافریدیم.'),
(2500, 21, 17, 'اگر می‌خواستیم جهان را به بازی گرفته و کاری بیهوده انجام دهیم می‌کردیم.'),
(2501, 21, 18, 'بلکه ما همیشه حق را بر باطل غالب و فیروز می‌گردانیم تا باطل را محو و نابود سازد و باطل بی‌درنگ نابود می‌شود، و وای بر شما که خدا را به وصف کار باطل و بازیچه متّصف می‌گردانید.'),
(2502, 21, 19, 'و هر چه در آسمانها و زمین است همه ملک خداست، و فرشتگان و ارواح قدسی که در پیشگاه حضرتش مقربند هیچ‌گاه از بندگیش گردنکشی و سرپیچی نکنند و (از عبادتی که به شوق و رغبت می‌کنند) هرگز خسته و ملول نشوند.'),
(2503, 21, 20, 'همه به شب و روز بی‌آنکه هیچ سستی کنند به تسبیح و ستایش او مشغولند.'),
(2504, 21, 21, 'مگر این مردم (مشرک نادان) خدایانی از همین زمین برگرفته‌اند که جان آفرین باشند؟'),
(2505, 21, 22, 'اگر در آسمان و زمین غیر از خدای یکتا خدایانی وجود داشت همانا خلل و فساد در آسمان و زمین راه می‌یافت، پس (از نظم ثابت عالم) بدانید که پادشاه ملک وجود خدای یکتاست و از توصیف و اوهام مشرکان پاک و منزه است.'),
(2506, 21, 23, 'و او بر هر چه می‌کند بازخواست نشود ولی خلق از کردارشان بازخواست می‌شوند.'),
(2507, 21, 24, 'آیا خدا را رها کرده و خدایان باطل بی‌اثر را برگرفتند؟ بگو: برهانتان چیست؟ بیاورید. این ذکر (خدای یکتا و دعوت به توحید، سخن من و عالمان) امت من است و همه انبیاء و دانشمندان پیش از من، اما بیشتر این مشرکان به حق دانا نیستند که از آن اعراض می‌کنند.'),
(2508, 21, 25, 'و ما هیچ رسولی را پیش از تو به رسالت نفرستادیم جز آنکه به او وحی کردیم که به جز من خدایی نیست، پس مرا به یکتایی پرستش کنید و بس.'),
(2509, 21, 26, 'و مشرکان گفتند که خدای رحمان دارای فرزند است، (حاشا) خدا پاک و منزه از آن است، بلکه (کسانی را که مشرکان فرزند خدا پنداشتند از فرشتگان و مسیح و عزیر) همه بندگان گرامی و مقرب خدا هستند.'),
(2510, 21, 27, 'که هرگز پیش از امر خدا کاری نخواهند کرد و هر چه کنند به فرمان او کنند.'),
(2511, 21, 28, 'هر چه آنان از ازل کرده و تا ابد می‌کنند همه را خدا می‌داند و هرگز از احدی جز آن کسی که خدا از او راضی است شفاعت نکنند و آنها دائم از خوف (قهر) خدا هراسانند.'),
(2512, 21, 29, 'و هر کس از آنها بگوید که من خدای عالمم دون خدای به حق، ما او را به آتش دوزخ کیفر خواهیم کرد، که سرکشان و ستمکاران را البته چنین مجازات می‌کنیم.'),
(2513, 21, 30, 'آیا کافران ندیدند که آسمانها و زمین بسته بود ما آنها را بشکافتیم و از آب هر چیز زنده‌ای را آفریدیم؟ پس چرا باز (مردم طبیعت و ماده‌پرست) به خدا ایمان نمی‌آورند؟'),
(2514, 21, 31, 'و در روی زمین کوههای استوار قرار دادیم تا خلق را از اضطراب زمین حفظ کند، و نیز راهها در کوه و جاده‌های پهناور در زمین برای هدایت و راهیابی مردم مقرر فرمودیم.'),
(2515, 21, 32, 'و آسمان را سقفی محفوظ و طاقی محکم آفریدیم، و این کافران از مشاهده آیات آن اعراض می‌کنند.'),
(2516, 21, 33, 'و اوست خدایی که شب و روز و خورشید و ماه را بیافرید که هر یک در مدار معینی سیر می‌کنند.'),
(2517, 21, 34, 'و ما به هیچ کس پیش از تو عمر ابد ندادیم (تا به تو دهیم) آیا اگر تو بمیری آنان ابدا به دنیا زنده مانند؟!'),
(2518, 21, 35, 'هر نفسی مرگ را می‌چشد، و ما شما را به بد و نیک و خیرات و شرور عالم مبتلا کنیم تا شما را بیازماییم و (هنگام مرگ) به سوی ما بازگردانده می‌شوید.'),
(2519, 21, 36, 'و کافران هنگام ملاقات تو، تو را جز به استهزاء نگرفته (و گویند) آیا این شخص است که بتان را که خدایان شما هستند (به اهانت و بدی) یاد می‌کند؟ و (ای رسول ما بدان که) محققا آنها به ذکر خدای مهربان (و کتاب و آیات او) کافر هستند.'),
(2520, 21, 37, 'آدمی در خلقت و طبیعت بسیار شتابکار است، (ای مردم) ما آیات (قدرت و حکمت بالغه) خود را به زودی به شما می‌نمایانیم (و شما را عذاب می‌کنیم) پس تعجیل مدارید.'),
(2521, 21, 38, 'و کافران می‌گویند: این وعده قیامت اگر راست می‌گویید کی خواهد بود؟'),
(2522, 21, 39, 'اگر کافران بدانند وقتی که آتش دوزخ هر طرف از پیش روی و پشت سر به آنها احاطه می‌کند که نه خود دفع آن توانند و نه از سوی کسی یاری شوند (آن وقت از قیامت آگاه می‌شوند و دیگر درخواست عذاب نکنند).'),
(2523, 21, 40, 'بلکه واقعه مرگ و قیامت ناگهانی فرا رسد و آنها را مبهوت و حیران کند که نه قدرت بر رد آن داشته و نه بر تأخیر آن مهلتی توانند یافت.'),
(2524, 21, 41, 'و همانا به رسولان پیش از تو نیز بسیار تمسخر و استهزاء شد تا آنکه کیفر آن استهزاء به آن استهزا کنندگان احاطه کرد.'),
(2525, 21, 42, 'بگو: کیست که شما را در شب و روز از (قهر) خدای مهربان محافظت می‌کند؟ بلکه این مردم از یاد خدای خود اعراض می‌کنند.'),
(2526, 21, 43, 'آیا برای این مشرکان غیر ما خدایانی هست که بتوانند عذاب ما را از اینان منع کنند در صورتی که نه آن خدایان قدرت بر دفاع از خود دارند و نه آنها از سوی ما همراهی و حمایت شوند؟'),
(2527, 21, 44, 'بلکه تنها ماییم که این مردم و پدران پیشینشان را متمتّع (به نعمتهای دنیا) کردیم تا آنکه عمر دراز کردند، آیا مردم نمی‌بینند که ماییم که اراده کنیم و زمین (و اهلش) را از هر طرف (به مرگ و فنا) می‌کاهیم، آیا این خلق (عاجز بر ما) غلبه توانند کرد؟'),
(2528, 21, 45, 'بگو که من شما را به وحی خدا (از عذابش) می‌ترسانم ولی گوش کر هنگام وعظ و اندرز، سخن و دعوت حق را نمی‌شنود.'),
(2529, 21, 46, 'و اگر از جانب خدای تو بر این کافران شمّه‌ای از عذاب فرا رسد در آن حال (به حسرت و پشیمانی) خواهند گفت: ای وای بر ما که مردم ستمکاری بودیم.'),
(2530, 21, 47, 'و ما ترازوهای عدل را به روز قیامت خواهیم نهاد و هیچ ستمی به هیچ نفسی نخواهد شد و اگر عملی به قدر دانه خردلی باشد در حساب آریم و تنها علم ما برای حسابگری کفایت خواهد کرد.'),
(2531, 21, 48, 'و همانا ما به موسی و برادرش هارون کتاب تورات را عطا کردیم که جدا سازنده میان حق و باطل و روشنی‌بخش (دلها) و یادآور متقیان است.'),
(2532, 21, 49, 'متقیان همانهایی هستند که از خدای خود در نهان می‌ترسند و از ساعت قیامت و روز جزا سخت هراسانند.'),
(2533, 21, 50, 'و این قرآن کتابی است با اندرز و تذکر و برکت بسیار که ما آن را فرستادیم، آیا شما آن را انکار خواهید کرد؟'),
(2534, 21, 51, 'و همانا ما پیش از این ابراهیم را کاملا به رشد و کمال خود رسانیدیم (تا بیرق توحید و خداپرستی را در عالم برافرازد) و ما به شایستگی او بر این مقام دانا بودیم.'),
(2535, 21, 52, 'هنگامی که با پدرش (یعنی عمویش) و با قومش گفت: این مجسمه‌های بی‌روح و بتهای بی‌اثر چیست که شما به خدایی می‌پرستید و بر آن عمری متوقف شده‌اید؟'),
(2536, 21, 53, 'آنها ابراهیم را پاسخ دادند که ما پدران خود را بر پرستش این بتان یافته‌ایم (و ما پیروی آنها خواهیم کرد).'),
(2537, 21, 54, 'ابراهیم گفت: همانا شما خود و پدرانتان همه سخت در گمراهی بوده و هستید.'),
(2538, 21, 55, 'قوم به ابراهیم گفتند: آیا تو (بر علیه شرک و اثبات توحید) حرف حق و حجت قاطعی برای ما آورده‌ای یا سخنی به بازیچه و هزل می‌رانی؟'),
(2539, 21, 56, 'ابراهیم پاسخ داد که خدای شما همان خدایی است که آفریننده آسمانها و زمین است و من بر این سخن به یقین گواهی می‌دهم.'),
(2540, 21, 57, 'و به خدا قسم که من این بتهای شما را چاره‌ای کنم (و با هر تدبیری توانم درهم می‌شکنم) بعد از آنکه شما (برای تفرج و رسوم عید به صحرا روید و از بتخانه) روی گردانید.'),
(2541, 21, 58, '(در آن موقع به بتخانه شد) و همه بتها را درهم شکست به جز بت بزرگ آنها را تا (در مقام شکایت) به او رجوع کنند.'),
(2542, 21, 59, 'قوم ابراهیم گفتند: کسی که چنین کاری با خدایان ما کرده همانا بسیار ستمکار است.'),
(2543, 21, 60, '(آنان که لعن بتان را از ابراهیم شنیده بودند) گفتند که ما جوانی ابراهیم نام را شنیده‌ایم که بتان را (به بدی و زشتی) یاد می‌کرد.'),
(2544, 21, 61, 'قوم گفتند: او را حاضر سازید در حضور جماعت تا بر این کار او گواهی دهند.'),
(2545, 21, 62, 'به او گفتند: ای ابراهیم آیا تو با خدایان ما چنین کردی؟'),
(2546, 21, 63, 'ابراهیم (در مقام احتجاج) گفت: بلکه این کار را بزرگ آنها کرده است، شما از این بتان سؤال کنید اگر سخن می‌گویند.'),
(2547, 21, 64, 'آن‌گاه با خود فکر کردند و باهم گفتند: البته شما ستمکارید (که این بتان عاجز و بی‌اثر را می‌پرستید نه ابراهیم که آنها را درهم شکسته است).'),
(2548, 21, 65, 'و سپس همه سر به زیر شدند و گفتند: تو می‌دانی که این بتان را نطق و گویایی نیست.'),
(2549, 21, 66, 'ابراهیم گفت: پس چرا خدا را (که هر نفعی به دست اوست) رها کرده و بتهایی را می‌پرستید که هیچ نفع و ضرری برای شما ندارند؟'),
(2550, 21, 67, 'اف بر شما و بر آنچه به جز خدای یکتا می‌پرستید، آیا شما عقل خود را هیچ‌کار نمی‌بندید؟'),
(2551, 21, 68, 'قوم گفتند: ابراهیم را بسوزانید و خدایان خود را یاری کنید اگر (بر رضای خدایان) کاری خواهید کرد.'),
(2552, 21, 69, '(پس آن قوم آتشی سخت افروختند و ابراهیم را در آن افکندند) ما خطاب کردیم که ای آتش سرد و سالم برای ابراهیم باش (آتش به خطاب خدا گل و ریحان گردید).'),
(2553, 21, 70, 'و قوم در مقام کید و کینه او برآمدند و ما (کیدشان را باطل کرده) آنها را به سخت‌ترین زیان و حسرت انداختیم.'),
(2554, 21, 71, 'و ما ابراهیم را با (برادرزاده‌اش) لوط (از شر نمرودیان) برهانیدیم و به (شام) سرزمینی که مایه برکت جهانیان قرار دادیم آنها را بفرستادیم.'),
(2555, 21, 72, 'و به او اسحاق و افزون بر آن (فرزندزاده‌اش) یعقوب را عطا کردیم و همه را صالح و شایسته (مقام نبوت) گردانیدیم.'),
(2556, 21, 73, 'و آنان را پیشوای مردم ساختیم تا (خلق را) به امر ما هدایت کنند و هر کار نیکو را (از انواع عبادات و خیرات) و خصوص اقامه نماز و اداء زکات را به آنها وحی کردیم و آنها هم به عبادت ما پرداختند.'),
(2557, 21, 74, 'و لوط را هم مقام حکمفرمایی (و یا حکمت و نبوت) و علم عطا نمودیم و او را از شهری که اهلش به اعمال زشت (و کار پلید لواط) می‌پرداختند نجات دادیم که آنها بسیار بدکار و فاسق مردمی بودند.'),
(2558, 21, 75, 'و او را در رحمت خود داخل کردیم زیرا که از مردم بسیار صالح و نیکوکار به شمار بود.'),
(2559, 21, 76, 'و (یاد کن حکایت) نوح را که پیش از این (خدا را به یاری خود) خواند، ما هم او را پاسخ دادیم و او و اهل بیتش (و گرویدگانش) را از اندوه و بلای سخت (طوفان) نجات دادیم.'),
(2560, 21, 77, 'و او را بر دفع آن قومی که آیات ما را تکذیب کردند نصرت دادیم، که آن قوم بسیار بدکار (و کافر) بودند و ما همه آنها را یکسر (به طوفان) غرق کردیم.'),
(2561, 21, 78, 'و (یاد کن احوال) داود و سلیمان را وقتی که درباره گوسفندان بی‌شبانی که در مزرعی چریدند (و آن را تباه کردند) قضاوت می‌نمودند (که از شیرشان صاحب زرع به قدر زراعتش استفاده کند) و ما بر حکم آنها گواه بودیم.'),
(2562, 21, 79, 'و ما آن قضاوت را به سلیمان به وحی آموختیم و به هر یک (از سلیمان و داود) مقام حکمفرمایی و دانش عطا کردیم و کوهها و مرغان را مسخر داود گردانیدیم که با (آهنگ تسبیح و نغمه) او تسبیح می‌کردند، و ما این معجزات را از او پدید آوردیم.'),
(2563, 21, 80, 'و ما به داود صنعت زره ساختن برای شما آموختیم تا شما را از زخم شمشیر و آزار یکدیگر محفوظ دارد، آیا شکر به جای می‌آورید؟'),
(2564, 21, 81, 'و باد تند سیر صرصر را ما مسخر سلیمان گردانیدیم که به امر او بدان سرزمین (سرزمین شامات) که با برکت برای جهانیان کردیم حرکت می‌کرد، و ما به همه امور عالم داناییم.'),
(2565, 21, 82, 'و نیز برخی از دیوان را مسخر سلیمان کردیم که به دریا برای او غواصی کنند و یا به کارهای دیگر در دستگاه او بپردازند، و ما نگهبان دیوان (برای حفظ ملک سلیمان) بودیم.'),
(2566, 21, 83, 'و (یاد کن حال) ایوب را وقتی که پروردگار خود را خواند که مرا بیماری و رنج سخت رسیده و تو (بر بندگانت) از همه مهربانان عالم مهربان‌تری.'),
(2567, 21, 84, 'پس ما دعای او را مستجاب کردیم و درد و رنجش را برطرف ساختیم و اهل و فرزندانش را (که از او گرفته بودیم) با عده دیگر به مثل آنها باز به او عطا کردیم تا هم لطف و رحمتی در حق او کرده باشیم و هم اهل عبادت متذکر (لطف و احسان ما) شوند.'),
(2568, 21, 85, 'و (نیز یاد آر حال) اسماعیل و ادریس و ذو الکفل را که همه بندگان صابر ما بودند.'),
(2569, 21, 86, 'و ما آنها را به رحمت (خاص) خود درآوردیم، زیرا آنان از نیکان عالم به شمار بودند.'),
(2570, 21, 87, 'و (یاد آر حال) ذو النون (یعنی یونس) را هنگامی که (از میان قوم خود) غضبناک بیرون رفت و چنین پنداشت که ما هرگز او را در مضیقه و سختی نمی‌افکنیم (تا آنکه به ظلمات دریا و شکم ماهی در شب تار گرفتار شد) آن گاه در آن ظلمتها فریاد کرد که خدایی به جز ذات یکتای تو نیست، تو (از شریک و هر عیب و آلایش) پاک و منزهی و من از ستمکاران بودم (که بر نفس خود ستم کردم).'),
(2571, 21, 88, 'پس ما دعای او را مستجاب کردیم و او را از گرداب غم نجات دادیم و اهل ایمان را هم این گونه نجات می‌دهیم.'),
(2572, 21, 89, 'و (یاد آر حال) زکریا را هنگامی که خدای خود را ندا کرد که بار الها، مرا یک تن و تنها وامگذار (و به من فرزندی که وارث من باشد عطا فرما) که تو بهترین وارث اهل عالم هستی.'),
(2573, 21, 90, 'ما هم دعای او را مستجاب کردیم و یحیی را به او عطا فرمودیم و جفتش را (که نازا بود) برای او شایسته (همسری و قابل ولادت) گردانیدیم، زیرا آنها در کارهای خیر تعجیل می‌کردند و در حال بیم و امید ما را می‌خواندند و همیشه به درگاه ما خاضع و خاشع بودند.'),
(2574, 21, 91, 'و (یاد کن حال) آن زن (یعنی مریم) را که دامنش را پاکیزه نگاه داشت و ما در آن از روح خود بدمیدیم و او را با فرزندش (عیسی) معجز و آیتی بزرگ برای اهل عالم قرار دادیم.'),
(2575, 21, 92, 'این طریقه شما آیین یگانه (و پاک اسلام) است و من یکتا پروردگار و آفریننده شما هستم، پس تنها مرا پرستش کنید.'),
(2576, 21, 93, 'و امت (با این سفارش) باز در کار دین، بین خود تفرقه و اختلاف انداختند (و ملت واحد را پریشان و متفرق ساختند ولی) باز رجوع همه به سوی ما خواهد بود.'),
(2577, 21, 94, 'پس هر کس اعمالش نیکو و دارای ایمان است سعیش (در راه دین) ضایع نخواهد شد و ما آن را کاملا می‌نویسیم.'),
(2578, 21, 95, 'و اهل دیاری را که ما هلاک گردانیم دیگر زندگانی بر آنها حرام است و هرگز (به دنیا یا به ایمان) باز نخواهند گشت.'),
(2579, 21, 96, 'تا روزی که راه یأجوج و مأجوج باز شود و آنان از هر جانب (پست و) بلند زمین شتابان درآیند (که روز قیامت یا قیام ولی عصر علیه السّلام مقصود است).'),
(2580, 21, 97, 'و وعده (ثواب و عقاب) حق بسیار نزدیک شود و ناگهان چشم کافران از حیرت بی‌حرکت فرو ماند (و فریاد کنند) ای وای بر ما که از این روز غافل بودیم بلکه سخت به راه ستمکاری شتافتیم.'),
(2581, 21, 98, '(و به آن کافران خطاب شود) البته شما و آنچه غیر خدا می‌پرستید امروز همه آتش افروز دوزخید و در آن آتش وارد می‌شوید.'),
(2582, 21, 99, 'اگر این بتان (که شما می‌پرستید) به راستی خدایان بودند به دوزخ نمی‌شدند در صورتی که (شما و بتانتان) همه در آتش مخلد خواهید بود.'),
(2583, 21, 100, 'آن کافران را در دوزخ زفیر و ناله بسیار دردناکی است و در آنجا هیچ سخنی (که مایه امید و نشاط شود) نخواهند شنید.'),
(2584, 21, 101, 'و البته (مؤمنان) آنان که توفیق و وعده نیکوی ما (در سرنوشت ازلی) بر آنها سبقت یافته از آن دوزخ به دور خواهند بود.'),
(2585, 21, 102, 'آنها هرگز آواز جهنم را نخواهند شنید و به آنچه مشتاق و مایل آنند تا ابد متنعّمند.'),
(2586, 21, 103, 'و هیچ گاه فزع اکبر و هنگامه بزرگ قیامت آنها را محزون نخواهد ساخت و با آنان فرشتگان (رحمت) ملاقات کنند (و گویند) این است آن روز (سعادت) شما که در دنیا به شما وعده می‌دادند.'),
(2587, 21, 104, 'روزی که آسمانها را مانند طومار در هم پیچیم و به حال اول که آفریدیم باز برگردانیم، این وعده ماست که البته انجام خواهیم داد.'),
(2588, 21, 105, 'و ما بعد از تورات در زبور (داود) نوشتیم که البته بندگان نیکوکار من ملک زمین را وارث و متصرف خواهند شد.'),
(2589, 21, 106, 'بی‌شک در این قرآن برای اهل عبادت مایه وصول به مقصد حق نهفته است.'),
(2590, 21, 107, 'و (ای رسول) ما تو را نفرستادیم مگر آنکه رحمت برای اهل عالم باشی.'),
(2591, 21, 108, 'بگو که بر من به یقین و درست این وحی می‌رسد که خدای شما خدایی است یکتا، پس آیا شما تسلیم (امر او) خواهید شد؟'),
(2592, 21, 109, 'و چنانچه کافران (از حق) رو گردانیدند به آنان بگو: من (بر حسب وظیفه رسالت) شما را یکسان آگاه کردم و دیگر نمی‌دانم که آن وعده روز سخت قیامت که به شما دادند دور یا نزدیک خواهد بود.'),
(2593, 21, 110, 'همانا خدا به همه سخنان آشکار و اندیشه‌های پنهان شما آگاه است.'),
(2594, 21, 111, 'و خود ندانم شاید این (تأخیر عذاب) برای شما امتحانی باشد و تمتّعی (در دنیا) تا به هنگامی (که مرگ فرا رسد).'),
(2595, 21, 112, 'رسول گفت: الها، تو به حق (میان ما) حکم کن، و پروردگار ما همان خدای مهربان است و بر ابطال آنچه شما بر خلاف حق می‌گویید تنها از او یاری باید خواست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ای مردمان، خدا ترس و پرهیزکار باشید که زلزله روز قیامت بر خلایق بسیار حادثه بزرگ و واقعه سختی خواهد بود.'),
(2597, 22, 2, 'آن روز که آن هنگامه بزرگ را مشاهده کنید خواهید دید که هر زن شیرده طفل خود را (از هول) فراموش کند و هر آبستن بار رحم را بیفکند، و مردم را (از وحشت آن روز) بی‌خود و مست بنگری در صورتی که مست نیستند و لیکن عذاب خدا سخت است.'),
(2598, 22, 3, 'و از مردم کسی هست که از جهل و نادانی در کار خدا جدل کند و از پی هر شیطان گمراه کننده‌ای رود.'),
(2599, 22, 4, '(در لوح تقدیر) بر آن شیطان چنین فرض و لازم شده که هر کس او را دوست و پیشوای خود سازد وی او را گمراه کند و به عذاب سوزان دوزخش رهبر شود.'),
(2600, 22, 5, 'ای مردم، اگر شما در (روز قیامت و قدرت خدا بر) بعث مردگان شک و ریبی دارید (برای رفع شک خود بدین دلیل توجه کنید که) ما شما را نخست از خاک آفریدیم آن گاه از آب نطفه، آن گاه از خون بسته، آنگاه از پاره‌ای گوشت با آفرینشی تمام و ناتمام، تا (در این انتقال و تحولات قدرت خود را) بر شما آشکار سازیم و (از نطفه‌ها) آنچه را مشیّت ما تعلق گیرد در رحمها قرار می‌بخشیم تا به وقتی معین، آن گاه شما را به صورت طفلی (چون گوهر از صدف رحم) بیرون آریم تا (زیست کرده و) سپس به حد بلوغ و رشد خود برسید و برخی از شما (در این بین) بمیرد و برخی به سن پیری و دوران ضعف و ناتوانی رسد تا آنجا که پس از دانش و هوش خرف شود و هیچ فهم نکند، و (دلیل دیگر از ادله قدرت خدا بر معاد آن که) زمین را بنگری وقتی خشک و بی‌گیاه باشد آن گاه چون باران بر آن فرو باریم سبز و خرم شود و (تخم‌ها در آن) نمو کند و از هر نوع گیاه زیبا برویاند.'),
(2601, 22, 6, 'این (آثار قدرت) دلیل است که خدا حق است و هم او البته مردگان را زنده خواهد کرد و او محققا بر هر چیز تواناست.'),
(2602, 22, 7, 'و محققا ساعت قیامت بی‌هیچ شک بیاید و خدا به یقین مردگان را از قبرها برانگیزد.'),
(2603, 22, 8, 'و از مردم کس هست که از روی جهل و گمراهی و بی‌هیچ هدایت و کتاب و حجت روشن در کار خدا جدل می‌کند.'),
(2604, 22, 9, 'با تکبر و نخوت از حق اعراض کرده تا (خلق را) از راه خدا گمراه گرداند، چنین کسی را در دنیا ذلت و خواری نصیب باشد و در آخرت عذاب آتش سوزانش خواهیم چشانید.'),
(2605, 22, 10, 'این عذاب آتش به سزای همان اعمال زشتی است که با دست خود پیش فرستادی و این که خدا هرگز کمترین ستم در حق بندگان نخواهد کرد.'),
(2606, 22, 11, 'و از مردم کس هست که خدا را به زبان و به ظاهر می‌پرستد (نه از باطن و حقیقت) از این رو هرگاه خیر و نعمتی به او رسد اطمینان خاطر پیدا کند و اگر آزمونی (از شر و فقر و آفتی) به او رسد (از دین خدا) رو بگرداند. چنین کس در دنیا و آخرت زیانکار است و این (نفاق و دورویی) زیانی است که بر همه کس آشکار است.'),
(2607, 22, 12, '(این کافر نگون‌بخت) خدا را رها کرده و چیزی را می‌خواند (و می‌پرستد) که هیچ نفع و ضرری به حال او ندارد، و این حقا همان گمراهی دور (از هر سعادت) است.'),
(2608, 22, 13, 'چیزی را که به ضرر نزدیک‌تر از نفع است می‌خواند (و می‌پرستد)، وی بسیار بد یاوری یافته و بسیار بد دمساز و پشتیبانی اختیار کرده است.'),
(2609, 22, 14, 'همانا خدا آنان را که اهل ایمان و نیکوکارند داخل بهشت‌هایی کند که زیر درختانش نهرها جاری است، که خدا هر چه اراده کند خواهد کرد.'),
(2610, 22, 15, 'آن کس که پندارد خدا هرگز او را (یعنی رسولش را) در دنیا و آخرت یاری نخواهد کرد (و اینک نصرت‌های الهی را در حق او می‌بیند) پس طنابی به سقف بندد و خود را از آن بیاویزد تا خفه شود، آن گاه بنگرد که آیا این حیله و کید او خشمش را از بین می‌برد؟'),
(2611, 22, 16, 'و ما همچنین (مانند سایر کتب آسمانی) این قرآن بزرگ را به صورت آیاتی روشن فرستادیم، و خدا (به این آیات) هر که را بخواهد هدایت می‌کند.'),
(2612, 22, 17, 'البته خدا بین اهل ایمان و یهود و صابئان و نصاری و گبران و آنان که به خدا شرک آوردند محققا روز قیامت جدایی افکند (و هر کس را به جایگاه استحقاقش برد) که خدا بر (احوال و پاداش) همه موجودات عالم (بصیر و) گواه است.'),
(2613, 22, 18, 'آیا (ای هوشمند به چشم بصیرت) مشاهده نکردی که هر که در آسمانها و هر که در زمین است و خورشید و ماه و ستارگان و کوهها و درختان و جنبندگان و بسیاری از آدمیان همه (با کمال شوق) به سجده خدا (و اطاعت او) مشغولند و بسیاری از مردم هم (در اثر کفر و عصیان) مستوجب عذاب حق شدند؟ و هر که را خدا خوار و ذلیل گرداند دیگر کسی او را عزیز و گرامی نتواند کرد که البته خدا هر چه مشیّت کامله‌اش تعلق گیرد خواهد کرد.'),
(2614, 22, 19, 'این دو گروه (مؤمن و کافر) مخالف و دشمن یکدیگرند که در (دین) خدای خود باهم به جدال برخاستند، و کافران را لباسی از آتش دوزخ به قامت بریده‌اند و بر سر آنان آب سوزان جهنم فرو ریزند.'),
(2615, 22, 20, 'که آنچه در درون آنهاست (از امعاء و احشاء همه) و پوست بدنشان به آن آب سوزان گداخته شود.'),
(2616, 22, 21, 'و گرزگران و عمودهای آهنین بر (سر) آنها مهیا باشد.'),
(2617, 22, 22, 'هرگاه خواهند از دوزخ به درآیند تا از غم و اندوه آن نجات یابند باز (فرشتگان عذاب) آنان را به دوزخ برگردانند و (گویند باز باید) عذاب آتش سوزان را بچشید.'),
(2618, 22, 23, 'البته خداوند آنان را که ایمان آوردند و نیکوکار شدند همه را در بهشت‌هایی داخل گرداند که زیر درختانش نهرها جاری است و در آنجا طلا و مروارید بر دست زیور بندند و تن به جامه حریر بیارایند.'),
(2619, 22, 24, 'و به گفتار خوش و طریق خدای ستوده هدایت شوند.'),
(2620, 22, 25, 'آنان که کافر شده و (مردم را) از راه خدا منع می‌کنند و نیز از مسجد الحرامی که (ما حرمت احکام) آن را برای اهل آن شهر و بادیه‌نشینان یکسان قرار دادیم مانع می‌شوند، و هر کسی که در آنجا اراده الحاد و تعدی کرده و (به خلق) ظلم و ستم کند همه را (به کیفر کفر و ظلمشان) از عذابی دردناک می‌چشانیم.'),
(2621, 22, 26, 'و (یاد آر) آن گاه که ما ابراهیم را در آن بیت الحرام تمکین دادیم (و به او وحی کردیم) که با من هیچ کس را شریک و انباز نگیر و خانه مرا برای طواف حاجیان و نمازگزاران و رکوع و سجود کنندگان (از لوث بتان و بت پرستان) پاک و پاکیزه دار.'),
(2622, 22, 27, 'و در میان مردم به (اداء مناسک) حج اعلام کن تا خلق پیاده و سواره بر شتران لاغر اندام تیزرو از هر راه دور به سوی تو جمع آیند.'),
(2623, 22, 28, 'تا (در آنجا) بر سر منافع (دنیوی و اخروی بسیار) خود حضور یابند و نام خدا را در ایامی معین یاد کنند که آنها را از حیوانات بهائم (یعنی شتر و گاو و گوسفند) روزی داده است. پس از آن تناول کرده و فقیران بیچاره را نیز (از قربانیها) طعام دهید.'),
(2624, 22, 29, 'آن گاه باید مناسک حج و حلق و تقصیر را به جای آرند (یعنی سر بتراشند و ناخن و موی بسترند تا از احرام به درآیند) و به هر نذر و عهدی که (در حج) کردند (یا به هر پیمانی که با خدا و خلق بسته‌اند) وفا کنند و طواف را گرد خانه عتیق (بیت الحرام کعبه) به جای آرند.'),
(2625, 22, 30, 'این است (احکام حج) و هر کس اموری را که خدا حرمت نهاده بزرگ و محترم شمارد البته این برایش نزد خدا بهتر خواهد بود. و چهارپایان غیر آنچه تلاوت خواهد شد [در سوره مائده آیه 3 آمده است. (م)] همه برای شما حلال گردید، پس از پلید حقیقی یعنی بتها اجتناب کنید و نیز از قول باطل (مانند دروغ و شهادت ناحق و سخنان لهو و غنا) دوری گزینید.'),
(2626, 22, 31, 'و خاص و خالص بی‌هیچ شائبه شرک خدا را پرستید، و هر کس به خدا شرک آرد (در عجز و بیچارگی) بدان ماند که از آسمان درافتد و مرغان (در فضا) بدنش را به منقار (قطعه قطعه) بربایند یا بادی تند او را به مکانی دور (از هر وسیله نجات) درافکند.'),
(2627, 22, 32, 'این است (سخن حق) و هر کس شعائر (دین) خدا را بزرگ و محترم دارد این از صفت دلهای باتقواست.'),
(2628, 22, 33, 'شما را در این شعائر و احکام الهی (یا این شتران یا تجارات) تا وقتی معین منفعتهاست، آن گاه محل هدی و سایر مناسک حج، حرم و بیت العتیق است (که پرستشگاه دیرین و معبد محترم خداست).'),
(2629, 22, 34, 'و ما برای هر امتی شریعت و معبدی مقرر فرمودیم تا به ذکر نام خدا پردازند که آنها را از بهائم (یعنی گاو و گوسفند و شتر) روزی داد پس خدای شما خدایی است یکتا، همه تسلیم (فرمان) او باشید، و تو (ای رسول ما) متواضعان و مطیعان را (به سعادت ابدی) بشارت ده.'),
(2630, 22, 35, 'آنهایی که چون یاد خدا شود دلهاشان هراسان شود و (در راه او) هر چه مصیبت بینند صبور باشند و نماز به پا دارند و از آنچه روزیشان کردیم انفاق کنند.'),
(2631, 22, 36, 'و (نحر) شتران فربه را برای شما از شعائر خدا (و احکام حج) مقرر داشتیم که در آن قربانی شما را خیر و صلاح است، پس هنگام ذبح آنها تا برپا ایستاده‌اند نام خدا را یاد کنید و چون پهلوشان به زمین افتد (و نحر کامل شوند) از گوشت آنها تناول نموده و به فقیر وسائل هم اطعام کنید. ما این چنین این بهائم را مسخر و مطیع شما ساختیم تا شکر (نعمتهای ما را) به جای آرید.'),
(2632, 22, 37, '(بدانید که) هرگز گوشت و خون این قربانیها نزد خدا (به درجه قبول) نمی‌رسد لیکن تقوای شماست که به (پیشگاه قبول) او خواهد رسید. این چنین این بهایم را مسخر شما ساخته تا خدا را به پاس آنکه شما را هدایت فرمود تکبیر و تسبیح گویید (و شکر نعمتش به جای آرید) و تو (ای رسول) نیکوکاران را (به سعادت ابد) بشارت ده.'),
(2633, 22, 38, 'خدا مؤمنان را از هر مکر و شر دشمن نگاه می‌دارد، که خدا هرگز خیانتکار کافر ناسپاس را دوست نمی‌دارد.'),
(2634, 22, 39, 'به مسلمانان که مورد قتل (و غارت) قرار گرفته‌اند رخصت (جنگ با دشمنان) داده شد، زیرا آنها از دشمن سخت ستم کشیدند و همانا خدا بر یاری آنها قادر است.'),
(2635, 22, 40, 'آن مؤمنانی که به ناحق از خانه‌هاشان آواره شده (و جرمی نداشتند) جز آنکه می‌گفتند: پروردگار ما خدای یکتاست. و اگر خدا (رخصت جنگ ندهد و) دفع شر بعضی از مردم را به بعض دیگر نکند همانا صومعه‌ها و دیرها و کنشت‌ها و مساجدی که در آن (نماز و) ذکر خدا بسیار می‌شود همه خراب و ویران شود. و هر که خدا را یاری کند البته خدا او را یاری خواهد کرد، که خدا را منتهای اقتدار و توانایی است.'),
(2636, 22, 41, '(آنان که خدا را یاری می‌کنند) آنهایی هستند که اگر در روی زمین به آنان اقتدار و تمکین دهیم نماز به پا می‌دارند و زکات می‌دهند و امر به معروف و نهی از منکر می‌کنند و (از هیچ کس جز خدا نمی‌ترسند چون می‌دانند که) عاقبت کارها به دست خداست.'),
(2637, 22, 42, 'و اگر کافران امت، تو را تکذیب کنند (افسرده خاطر مباش که) پیش از اینان قوم نوح و عاد و ثمود نیز (رسولان حق را) تکذیب کردند.'),
(2638, 22, 43, 'و همچنین قوم ابراهیم و قوم لوط.'),
(2639, 22, 44, 'و نیز (قوم شعیب) اصحاب مدین (همه رسولان خود را تکذیب نمودند) و موسی نیز تکذیب شد و من هم کافران را (برای امتحان) مهلت دادم سپس آنها را (به عقوبت) گرفتم، و چقدر مؤاخذه و عقاب من (بر کافران) سخت است!'),
(2640, 22, 45, 'پس چه بسیار شهر و دیاری که ما اهلش را در آن حال که به ظلم و ستم مشغول بودند به خاک هلاک نشاندیم و اینک آن شهرها از بنیاد ویران است و چه چاه و قناتهای آب که معطل بماند و چه قصرهای عالی بی‌صاحب گشت.'),
(2641, 22, 46, 'آیا (این کافران) در روی زمین به سیر و تماشا نرفتند تا دلهاشان بینش و هوش یابد و گوششان به حقیقت شنوا گردد؟ که (این کافران را) چشمهای سر گرچه کور نیست لیکن چشم باطن و دیده دلها کور است.'),
(2642, 22, 47, 'و کافران (به سخریه) از تو تقاضای تعجیل در عذاب می‌کنند و هرگز خدا در وعده خود (به عذاب آنان) خلف نخواهد کرد، و همانا یک روز نزد خدا (و از نظر حلم او) چون هزار سال به حساب شماست.'),
(2643, 22, 48, 'و بسا شهر و دیاری که به اهلش با آنکه ستمکار بودند مهلت دادم تا روزی آنها را به انتقام گرفتم و بازگشت (خلق) به سوی من است (و هر نیک و بد را جزا خواهم داد).'),
(2644, 22, 49, 'بگو که ای مردم، من برای شما رسول ترساننده مشفقی آشکار بیش نیستم.'),
(2645, 22, 50, 'پس آنان که ایمان آوردند و نیکوکار گردیدند بر آنها آمرزش حق و رزق با لطف و کرامت عالی است.'),
(2646, 22, 51, 'و آنان که در رد و انکار آیات ما سعی و کوشش کردند تا ما را به زانو درآورند آنها اهل آتش دوزخند.'),
(2647, 22, 52, 'و ما پیش از تو هیچ رسول و پیمبری نفرستادیم جز آنکه چون آیاتی (برای هدایت خلق) تلاوت کرد شیطان (جن و انس) در آن آیات الهی القاء دسیسه کرد، آن گاه خدا آنچه شیطان القاء می‌کند محو و نابود می‌سازد سپس آیات خود را محکم و استوار می‌گرداند، و خدا دانا (به حقایق امور) و درستکار (در نظام عالم) است.'),
(2648, 22, 53, 'تا خدا به آن القائات شیطان کسانی را که دلهایشان مبتلا به مرض (نفاق و شک یا کفر) و قساوت است بیازماید (و باطن آنها را پدیدار سازد) و همانا (کافران و) ستمکاران عالم سخت در ستیزه و دشمنی دور (از حق) می‌باشند.'),
(2649, 22, 54, 'و تا آنکه اهل علم و معرفت به یقین بدانند که این آیات قرآن به حق از جانب پروردگار تو نازل گردیده که بدان ایمان آورند و دلهاشان پیش او خاضع و خاشع شود، و البته خدا اهل ایمان را به راهی راست هدایت خواهد کرد.'),
(2650, 22, 55, 'و آنان که کافرند در (اینکه نزول) قرآن (از جانب خداست یا در وعد و وعید قرآن) همیشه شک دارند تا وقتی که ناگهان ساعت مرگ (یا قیامت) فرا رسد یا عذاب روز عقیم بی‌خیر (که شب آسایش و خیر و سعادت در پی ندارد) بر آنها فرود آید.'),
(2651, 22, 56, 'در آن روز سلطنت و حکمفرمایی تنها مخصوص خداست، که میان آنان حکم می‌کند، پس آنان که ایمان آورده و نیکوکار شدند در بهشت پر نعمتند.'),
(2652, 22, 57, 'و آنان که کافر شده و تکذیب آیات ما کردند آنها را عذابی خوار کننده و ذلّت بار است.'),
(2653, 22, 58, 'و آنان که در راه خدا از وطن خود هجرت گزیده و کشته شدند یا مرگشان فرا رسید البته خدا رزق و روزی نیکویی (در بهشت ابد) نصیبشان می‌گرداند، و همانا خداوند بهترین رزق و روزی بخشنده است.'),
(2654, 22, 59, 'و بی‌گمان آنها را (در بهشت) منزلی عنایت کند که بسیار بدان خشنود باشند، و همانا خدا (بر احوال خلق) دانا و (بر گناهانشان) بردبار است.'),
(2655, 22, 60, '(سخن حق) این است، و هر کس به همان قدر ظلمی که به او شده در مقام انتقام برآید و باز بر او ظلم شود البته خدا او را یاری می‌کند (در این آیه ترغیب خلق است برگرفتن حق مظلوم از ظالم)، همانا خدا را عفو و آمرزش بسیار است.'),
(2656, 22, 61, 'این است (برهان حق) که خدا شب تار را در روز روشن پنهان می‌کند و روز روشن را در شب تار، و خدا (به صدای اهل عالم) شنوا و (به دقایق امور خلق) بیناست.'),
(2657, 22, 62, 'حقیقت این است که خدای یکتا حق مطلق است و هر چه جز او خوانند باطل صرف است، و علوّ مقام و بزرگی شأن مخصوص ذات پاک خداست.'),
(2658, 22, 63, '(ای بشر) آیا ندیدی که خدا از آسمان آبی فرو بارد و زمین سبز و خرم گردد؟ (این دلیل قدرت و رحمت آفریننده است)، همانا خدا را (با خلق) عنایت و لطف بسیار است و (به دقیق‌ترین امور عالم) آگاه است.'),
(2659, 22, 64, 'آنچه در آسمانها و آنچه در زمین است همه ملک خداست و تنها خداست که بی‌نیاز و به همه اوصاف کمال آراسته است.'),
(2660, 22, 65, '(ای بشر) آیا ندیدی که هر چه در زمین است خدا مسخر شما گردانید و کشتی به فرمان او در دریا سیر می‌کند و آسمان را (دستور) او نگاه می‌دارد که بر زمین نیفتد مگر به اذن او؟ همانا خدا به مردم بسیار رئوف و مهربان است.'),
(2661, 22, 66, 'و اوست خدایی که شما را اول بار زنده کرد و دیگر بار بمیراند و باز دوباره زنده کند، (با وجود این آیت قدرت و نعمت بزرگ) باز انسان بسیار ناسپاس و کافر کیش است.'),
(2662, 22, 67, 'ما برای هر امتی پرستشگاهی مقرر کردیم تا به خدا توجه کنند (و آنجا جهت وحدت و انس و الفت آن امت شود) پس (ای رسول) نباید مردم (در امر کعبه و قربانگاه و سایر دستورهای الهی) با تو به منازعت برخیزند و تو (خلق را) به سوی خدا دعوت کن که خود به راهی راست و هدایتی کامل هستی.'),
(2663, 22, 68, 'و اگر (کافران) با تو (در اوامر حق) جدل و خصومت کنند آنها را بازگو که خدا به آنچه می‌کنید بهتر آگاه است.'),
(2664, 22, 69, 'خدا در روز قیامت میان شما در آنچه (با من) خلاف و نزاع می‌کردید حکم خواهد فرمود.'),
(2665, 22, 70, '(ای بشر) آیا ندانستی که خدا از آنچه در آسمان و زمین است آگاه است؟ و این (جهان و همه حوادث آن) در کتابی (کتاب علم خدا) محفوظ و مسطور است و این (حفظ و نگهبانی) بر خدا کاری بسیار سهل است.'),
(2666, 22, 71, 'و (این مشرکان) خدا را رها کرده و چیزی غیر او را می‌پرستند که بر پرستش آن هیچ دلیل و برهانی نفرستاده و نه خود علم و بصیرتی بدان دارند، (مشرکان، نادان و ستمکارند) و هرگز ستمکاران را یار و یاوری نخواهد بود.'),
(2667, 22, 72, 'و هرگاه بر این کافران معاند آیات روشن ما تلاوت شود در چهره آن کافران به حدی اثر مخالفت و انکار مشاهده کنی که نزدیک است (از فرط غضب) بر مؤمنانی که آیات ما را بر آنان قرائت می‌کنند حمله‌ور شوند، (به آنها) بگو: آیا شما را به عذابی بدتر از این خبر دهم؟ آن آتش دوزخ است که خدا آن را به کافران وعده داده است و آنجا بسیار بد بازگشتگاهی خواهد بود.'),
(2668, 22, 73, 'ای مردم (مشرک کافر) مثلی زده شده بدان گوش فرا دارید (تا حقیقت حال خود بدانید): آن بتهای جماد که به جای خدا (معبود خود) می‌خوانید هرگز بر خلقت مگسی هر چند همه اجتماع کنند قادر نیستند، و اگر مگس (ناتوان) چیزی از آنها بگیرد قدرت بر باز گرفتن آن ندارند، (بدانید که) طالب و مطلوب (یعنی بت و بت‌پرست یا عابد و معبود یا مگس و بتان) هر دو ناچیز و ناتوانند.'),
(2669, 22, 74, '(این مشرکان) مقام خدا را آن گونه که شایسته اوست نشناختند، (و گرنه جماد ناتوانی را خدا نمی‌خواندند) خدا ذاتی است بی‌نهایت توانا و بی‌همتای شکست ناپذیر.'),
(2670, 22, 75, 'خداست که از میان فرشتگان و آدمیان رسولانی برمی‌گزیند، که همانا خدا (به سخن عالمیان) شنوا و (به لیاقت آنان) بیناست.'),
(2671, 22, 76, 'او به علم ازلی آنچه در نظر این مردم پیدا و آنچه ناپیداست همه را می‌داند و بازگشت کلیه امور به سوی خداست.'),
(2672, 22, 77, 'ای اهل ایمان، در برابر خدا رکوع و سجود آرید و (با توجه و بی‌ریا و خالص) پروردگار خود را پرستید و کار نیکو کنید، باشد که رستگار شوید.'),
(2673, 22, 78, 'و حق جهاد در راه او را (با دشمنان دین و با نفس امّاره) به جای آرید (و در طلب رضای او به قدر طاقت بکوشید) او شما را برگزیده (و به دین خود سرافراز کرده) و در مقام تکلیف بر شما مشقت و رنج ننهاده (و این آیین اسلام) مانند آیین پدر شما ابراهیم (خلیل است)، او (خدا) شما امت را پیش از این (در صحف او) و در این قرآن مسلمان نامیده تا این رسول بر شما و شما بر سایر خلق گواه (خداپرستی) باشید، پس نماز به پا دارید و زکات بدهید و به خدا (و کتاب او) متوسل شوید، که او مولی (و پادشاه و نگهبان و حافظ و ناصر) شماست و نیکو مولی و نیکو ناصری است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'همانا اهل ایمان به فیروزی و رستگاری رسیدند.'),
(2675, 23, 2, 'آنان که در نماز خود خاضع و خاشعند.'),
(2676, 23, 3, 'و آنان که از لغو و سخن باطل اعراض و احتراز می‌کنند.'),
(2677, 23, 4, 'و آنان که زکات (مال خود را به فقرا) می‌دهند.'),
(2678, 23, 5, 'و آنان که فروج و اندامشان را از عمل حرام نگاه می‌دارند.'),
(2679, 23, 6, 'مگر بر جفتهاشان (که زنان عقدی آنها باشند) یا کنیزان ملکی متصرفی آنها که هیچ‌گونه ملامتی (در مباشرت این زنان) بر آنها نیست.'),
(2680, 23, 7, 'و کسی که غیر این (زنان حلال) را به مباشرت طلبد البته چنین کسانی ستمکار و متعدی خواهند بود.'),
(2681, 23, 8, 'و آنان که به امانتها و عهد و پیمان خود کاملا وفا می‌کنند.'),
(2682, 23, 9, 'و آنان که بر نمازهاشان (و همه اوقات و شرایط ظاهر و باطن نماز) محافظت دارند.'),
(2683, 23, 10, 'چنین کسان وارث (مقام عالی بهشت) اند.'),
(2684, 23, 11, 'که بهشت فردوس ارث آن خوبان و منزلگاه ابدی آن پاکان است.'),
(2685, 23, 12, 'و همانا ما آدمی را از گل خالص آفریدیم.'),
(2686, 23, 13, 'آن‌گاه او را نطفه گردانیده و در جای استوار (صلب و رحم) قرار دادیم.'),
(2687, 23, 14, 'آن‌گاه نطفه را علقه و علقه را گوشت پاره و باز آن گوشت را استخوان ساختیم و سپس بر استخوانها گوشت پوشانیدیم (و پیکری کامل کردیم) پس از آن (به دمیدن روح پاک مجرد) خلقتی دیگرش انشا نمودیم؛ آفرین بر (قدرت کامل) خدای که بهترین آفرینندگان است.'),
(2688, 23, 15, 'باز شما آدمیان همه خواهید مرد.'),
(2689, 23, 16, 'و آن‌گاه روز قیامت به یقین تمام مبعوث خواهید شد.'),
(2690, 23, 17, 'و همانا مافوق شما (خاکیان) هفت آسمان (عالم پاک) را فراز یکدیگر آفریدیم و لحظه‌ای از توجه به خلق غافل نبوده و نخواهیم بود.'),
(2691, 23, 18, 'و ما آب را به قدر معین از آسمان نازل و در زمین ساکن ساختیم و محققا بر بردن و نابود ساختن آن نیز قادریم.'),
(2692, 23, 19, 'آن‌گاه ما به آن آب برای شما باغها و نخلستانهای خرما و انگور ایجاد کردیم که شما را در آنها میوه‌های گوناگون بسیاری است که از آن تناول می‌کنید.'),
(2693, 23, 20, 'و نیز درختی که از طور سینا برآید و روغن زیت بار آورد و نان خورش مردم گردد.'),
(2694, 23, 21, 'و بی‌شک برای شما در (مطالعه حال) چهارپایان و حیوانات اهلی عبرت خواهد بود که از شیر آنها به شما می‌آشامانیم و در آنها برای شما منافع بسیاری است (مانند فرشها و لباسها و اثاثیه و باربری و فواید بسیار دیگر) و از گوشت آنها نیز تناول می‌کنید.'),
(2695, 23, 22, 'و بر آن چهارپایان و نیز بر کشتیها سوار می‌شوید.'),
(2696, 23, 23, 'و همانا ما پیغمبر خود نوح را (به رسالت) به سوی امتش فرستادیم. نوح به قوم خود گفت که خدا را پرستید که جز آن ذات یکتا شما را خدایی نیست، آیا شما مردم خداترس و باتقوا نمی‌شوید؟'),
(2697, 23, 24, 'اشراف قوم او که کافر شدند (به مردم) گفتند که این شخص جز آنکه بشری است مانند شما که می‌خواهد بر شما برتری یابد فضیلت دیگری ندارد و اگر خدا می‌خواست (رسول بر بشر بفرستد) همانا از جنس فرشتگان می‌فرستاد، ما این سخنانی که این شخص می‌گوید در میان پدران پیشین خود هیچ نشنیده‌ایم.'),
(2698, 23, 25, 'این شخص جز مردی دیوانه به شمار نیست، پس باید تا مدتی با او (مدارا کنید و) انتظار برید (تا یا از مرض جنون بهبود یابد یا بمیرد).'),
(2699, 23, 26, 'نوح عرض کرد: خدایا، تو مرا بر اینان که تکذیب من کردند یاری فرما.'),
(2700, 23, 27, 'ما هم به او وحی کردیم که در حضور ما و به وحی و دستور ما به ساختن کشتی پرداز، تا آن‌گاه که فرمان (طوفان از جانب) ما آمد و آب از تنور فوران کرد پس در آن کشتی با خود از هر نوع جفتی (نر و ماده) همراه بر و اهل بیت خود را هم جز آن کس که در علم ازلی ما هلاک او گذشته (یعنی زن و فرزند نااهلت را) تمام به کشتی بنشان، و به شفاعت ستمکاران با من سخن مگو، که البته آنها باید همه غرق شوند.'),
(2701, 23, 28, 'پس چون با همراهانت در کشتی نشستی بگو: ستایش خدای را که ما را از (ظلم) ستمکاران نجات داد.'),
(2702, 23, 29, 'و باز بگو: پروردگارا، مرا به منزلی مبارک فرود آور که تو بهترین کسی هستی که به منزل خیر و سعادت توانی فرود آورد.'),
(2703, 23, 30, 'همانا در این حکایت آیت و عبرتهاست و البته ما (بندگان را به این‌گونه حوادث) آزمایش کننده‌ایم.'),
(2704, 23, 31, 'پس از هلاک قوم نوح باز قوم دیگری ایجاد کردیم.'),
(2705, 23, 32, 'و در میان آن قوم باز رسولی از خودشان (یعنی هود را) فرستادیم که خدای یکتا را پرستش کنید که جز او شما را خدایی نیست، آیا خداترس و باتقوا نمی‌شوید؟'),
(2706, 23, 33, 'و باز اشراف و اعیان قوم وی که کافر شدند و عالم آخرت را تکذیب کردند و ما متنعّمشان در حیات دنیا کرده بودیم (به مردم) گفتند که این شخص بشری مانند شما بیش نیست که از هر چه شما بشر می‌خورید و می‌آشامید او هم می‌خورد و می‌آشامد.'),
(2707, 23, 34, 'و شما مردم اگر بشری مانند خود را اطاعت کنید بسیار زیانکار خواهید بود.'),
(2708, 23, 35, 'آیا به شما نوید می‌دهد که پس از آنکه مردید و استخوانهای شما هم پوسید و خاک شد باز (از گور) بیرونتان آرند؟!'),
(2709, 23, 36, 'هیهات، هیهات که این وعده‌ها که به شما می‌دهند راست باشد!'),
(2710, 23, 37, 'زندگانی جز این چند روزه حیات دنیایمان بیش نیست که زنده شده و خواهیم مرد و دیگر هرگز برانگیخته نخواهیم شد.'),
(2711, 23, 38, 'و این شخص جز آنکه مردی است که دروغ و افترا بر خدا می‌بندد هیچ مزیّت ندارد و ما هرگز به او ایمان نخواهیم آورد.'),
(2712, 23, 39, 'آن رسول هم عرض کرد: خدایا، مرا بر این قوم که تکذیب من کردند یاری فرما.'),
(2713, 23, 40, 'خدا فرمود: اندک زمانی نگذرد که این قوم سخت پشیمان خواهند شد.'),
(2714, 23, 41, 'تا آن‌گاه که صیحه و بانگ آسمانی (عذاب) آنها را بگرفت و ما آنان را خار و خاشاک بیابان ساختیم، که ستمکاران را (از رحمت الهی) دوری باد.'),
(2715, 23, 42, 'پس از هلاک اینان باز اقوامی دیگر پدید آوردیم.'),
(2716, 23, 43, 'هیچ قومی از اجل خود پس و پیش نخواهند افتاد.'),
(2717, 23, 44, 'سپس پیغمبران خود را پی در پی فرستادیم و هر قومی که رسول بر آنها آمد آن رسول را تکذیب و انکار کردند، ما هم آنها را از پی یکدیگر همه را هلاک کردیم و داستانهای هلاک آن اقوام را عبرت دیگران ساختیم، که قوم بی‌ایمان را (از رحمت خدا) دوری باد.'),
(2718, 23, 45, 'آن‌گاه موسی و برادرش هارون را فرستادیم با آیات و معجزات خود و حجّت روشن.'),
(2719, 23, 46, 'به سوی فرعون و اشراف قومش، آنها هم تکبر و نخوت کردند، زیرا آنان مردمی متکبّر و سرکش بودند.'),
(2720, 23, 47, 'از این رو گفتند: چرا ما به دو بشر مثل خودمان ایمان آریم در صورتی که طایفه این دو مرد ما را اطاعت و بندگی می‌کنند؟'),
(2721, 23, 48, 'پس موسی و هارون را تکذیب کرده و بدین سبب همه هلاک شدند.'),
(2722, 23, 49, 'و همانا ما به موسی کتاب تورات را فرستادیم، باشد که آنها هدایت شوند.'),
(2723, 23, 50, 'و ما پسر مریم و مادرش را آیت و معجزه بزرگ گردانیدیم و هر دو را به سرزمین بلندی که آنجا مکانی هموار و چشمه‌سار بود (شام و بیت المقدس) منزل دادیم.'),
(2724, 23, 51, 'ای رسولان، از غذاهای پاکیزه (حلال) تناول کنید و به نیکوکاری و اعمال صالح پردازید که من به هر چه می‌کنید آگاهم.'),
(2725, 23, 52, 'و این مردم همه یک دین و متفق الکلمه یک امت شمایند و من یگانه خدای شما هستم، پس از من بترسید.'),
(2726, 23, 53, 'آن‌گاه مردم (با وجود این سفارش خدا) امر (دین) خود را پاره پاره کردند (و در آیین، فرقه فرقه شدند) و هر گروهی به آنچه نزد خود پسندیدند دلخوش گشتند.'),
(2727, 23, 54, 'پس بگذار که این بی‌خبران در جهل و غفلت خود به سر برند تا هنگامی معین (که روز مرگ و قیامت باشد).'),
(2728, 23, 55, 'آیا این مردم کافر می‌پندارند این که ما آنها را مدد به مال و فرزندان می‌کنیم،'),
(2729, 23, 56, 'برای آن است که می‌خواهیم در حق ایشان مساعدت و تعجیل به خیرات دنیا کنیم؟ (نه چنین است) بلکه (برای امتحان است و) آنها نمی‌فهمند.'),
(2730, 23, 57, 'همانا آن مؤمنانی که از خوف خدا ترسان و هراسانند.'),
(2731, 23, 58, 'و آنان که به آیات خدای خود ایمان می‌آورند.'),
(2732, 23, 59, 'و آنان که هرگز به پروردگارشان مشرک نمی‌شوند.'),
(2733, 23, 60, 'و آنان که آنچه وظیفه بندگی و ایمان است به جای آورده و باز از این که (روزی) به خدای خود رجوع می‌کنند دلهاشان ترسان است.'),
(2734, 23, 61, 'چنین بندگانی تعجیل در خیرات می‌کنند و اینان هستند که به کارهای نیکو سبقت می‌جویند.'),
(2735, 23, 62, 'و ما هیچ نفسی را بیش از وسع و توانایی او تکلیف نمی‌کنیم و نزد ما کتابی است (از لوح محفوظ الهی) که آن کتاب سخن به حق گوید و به هیچ کس هرگز ستم نخواهد شد.'),
(2736, 23, 63, 'بلکه دلهای کافران از این (کتاب حق) در جهل و غفلت است و اعمالی که این کافران عامل آنند غیر اعمال آن اهل ایمان است (که در آیات سابق ذکر شد).'),
(2737, 23, 64, '(این کافران به همین کردار بد مشغولند) تا آن‌گاه که ما متنعّمان آنها را به عذاب بگیریم در آن حال فریاد آنها بلند شود.'),
(2738, 23, 65, '(و ما فرماییم که) امروز فریاد مکنید، که از ما به شما هیچ مدد و یاری نخواهد رسید.'),
(2739, 23, 66, 'زیرا همانا آیات من بر شما تلاوت می‌شد و شما واپس می‌رفتید (و بر کفر می‌افزودید).'),
(2740, 23, 67, 'در حالتی که به هذیان و افسانه‌های شبانه خود (از شنیدن کلام خدا) تکبر و دوری می‌کردید.'),
(2741, 23, 68, 'آیا در این سخن (خدا و قرآن بزرگ) فکر و اندیشه نمی‌کنند (تا حقانیت آن را درک کنند) یا آنکه کتاب و رسول تنها بر اینان آمده و بر پدران پیشین آنها نیامده؟'),
(2742, 23, 69, 'یا آنکه اینان رسول خود را نشناخته‌اند که او را انکار و تکذیب می‌کنند.'),
(2743, 23, 70, 'یا آنکه می‌گویند که این رسول را جنونی عارض شده (که دعوی رسالت می‌کند)؟ (چنین نیست) بلکه دین حق را بر آنها آورده و لیکن اکثر آنها از حق روگردان و متنفرند.'),
(2744, 23, 71, 'و اگر حق تابع هوای نفس آنان شود همانا آسمانها و زمین و هر که در آنهاست فاسد خواهد شد. (و کافران از جهل نسبت جنون به رسول ما دادند؛ چنین نیست) بلکه ما اندرز قرآن بزرگ را بر آنها فرستادیم و آنان از این اندرز خود اعراض کنند.'),
(2745, 23, 72, 'مگر تو از این امت خرج و مزد رسالت می‌خواهی؟ (هرگز!) مزدی که خدا به تو دهد (از هر چیز) بهتر است، که خدا بهترین روزی دهنده است.'),
(2746, 23, 73, 'و البته تو خلق را به راه راست می‌خوانی.'),
(2747, 23, 74, 'و لیکن آنان که به آخرت ایمان نمی‌آورند از آن راه راست برمی‌گردند.'),
(2748, 23, 75, 'و اگر ما با آنها مهربانی کرده و هر گونه الم و رنج و عذابی که دارند برطرف سازیم آنها سخت‌تر در طغیان و سرکشی خود فرو می‌روند.'),
(2749, 23, 76, 'و همانا ما آنها را به عذاب سخت گرفتار کردیم و باز آن کافران به تضرع و توبه و ناله رو به درگاه خدای خود نیاوردند.'),
(2750, 23, 77, 'تا آنکه بر آنها دری از بلا و عذاب سخت گشودیم که دیگر ناگاه در آن عذاب (از هر سو) نومید شدند.'),
(2751, 23, 78, 'و اوست خدایی که برای شما گوش و چشمها و قلبها آفرید (ولی) عده بسیار کمی از شما شکر او به جای می‌آورید.'),
(2752, 23, 79, 'و اوست خدایی که شما را در زمین پدید آورد و باز (روز قیامت) رجوع و حشر شما به سوی او خواهد بود.'),
(2753, 23, 80, 'و اوست خدایی که زنده گرداند و بمیراند و اختلاف و حرکت شب و روز به امر اوست، آیا شما عقل خود را کار نمی‌بندید (تا خداشناس شوید و اطاعت امر او کنید).'),
(2754, 23, 81, 'بلکه این مردم کافر هم همان سخن کافران پیشین را گفتند.'),
(2755, 23, 82, 'که گفتند: از کجا که چون ما مردیم و استخوانی پوسیده و خاک شدیم باز مبعوث و زنده شویم؟!'),
(2756, 23, 83, 'از این وعده‌ها بسیار به ما و پیش از این به پدران ما داده شد ولی اینها جز افسانه‌های پیشینیان نیست.'),
(2757, 23, 84, 'بگو که زمین و هر کس که در او موجود است از کیست اگر شما فهم و دانش دارید؟'),
(2758, 23, 85, 'البته جواب خواهند داد که از خداست، بگو: پس چرا متذکر (قدرت خدا) نمی‌شوید (تا به عالم آخرت ایمان آرید).'),
(2759, 23, 86, 'باز به آنها بگو: پروردگار آسمانهای هفتگانه و خداوند عرش بزرگ کیست؟'),
(2760, 23, 87, 'البته باز جواب دهند که آنها از خداست، بگو: پس چرا متقی و خداترس نمی‌شوید؟'),
(2761, 23, 88, 'باز بگو: آن کیست که (ملک و) ملکوت همه عالم به دست اوست و او به همه پناه دهد و از همه حمایت کند ولی از (عذاب) او کسی پناه نتواند داد؟ اگر می‌دانید (کیست باز گویید).'),
(2762, 23, 89, 'محققا خواهند گفت که اینها همه از خداست. بگو: پس چرا به فریب و فسون مفتون می‌شوید؟ (و از خدا غافل می‌گردید؟)'),
(2763, 23, 90, 'بلکه ما حق را به ایشان فرستادیم و آنها دروغ می‌گویند.'),
(2764, 23, 91, 'خدا هرگز فرزندی اتخاذ نکرده و هرگز خدایی با او شریک نبوده، که اگر شریکی بود در این صورت هر خدایی مخلوق خود را به سویی بردی (و نظم وحدانی عالم از هم گسیختی) و بعضی از خدایان بر بعضی دیگر علو و برتری جستی! خدا از آنچه مشرکان گویند پاک و منزه است.'),
(2765, 23, 92, 'او دانا به عالم غیب و شهود است و ذات پاکش از شرک و شریک که برایش آرند برتر و والاتر است.'),
(2766, 23, 93, 'بگو: بارالها، اگر وعده‌های عذاب این کافران را به من بنمایانی،'),
(2767, 23, 94, 'پس مرا در میان قوم ستمکار وامگذار.'),
(2768, 23, 95, 'و البته ما قادریم عذابی را که به کافران وعده می‌دهیم به تو بنمایانیم.'),
(2769, 23, 96, 'تو آزار و بدیهای امت را به آنچه نیکوتر است دفع کن ما (جزای) گفتار آنها را بهتر می‌دانیم.'),
(2770, 23, 97, 'و بگو: بارالها، من از وسوسه و فریب شیاطین (انس و جن) به تو پناه می‌آورم.'),
(2771, 23, 98, 'و هم به تو پناه می‌برم بار خدایا از آنکه شیاطین به مجلسم حضور به هم رسانند (و مرا از یاد تو غافل سازند).'),
(2772, 23, 99, '(کافران در جهل و غفلتند) تا آن گاه که وقت مرگ هر یک از آنها فرا رسد، در آن حال گوید: بارالها، مرا به دنیا بازگردانید.'),
(2773, 23, 100, 'تا شاید به تدارک گذشته عملی صالح به جای آرم. (به او خطاب شود که) هرگز نخواهد شد، این کلمه‌ای است که (از حسرت) همی بگوید (و ثمری نمی‌بخشد) و در برابر آنها عالم برزخ است تا روزی که برانگیخته شوند.'),
(2774, 23, 101, 'پس آن‌گاه که نفخه صور قیامت دمیده شد دیگر نسب و خویشی در میانشان نماند و کسی از کس دیگر حال نپرسد.'),
(2775, 23, 102, 'پس (در آن روز) هر که اعمالش سنگین و وزین است آنان رستگارانند.'),
(2776, 23, 103, 'و هر که اعمالش سبک وزن است آنان کسانی هستند که نفس خویش را در زیان افکنده، به دوزخ مخلد خواهند بود.'),
(2777, 23, 104, 'آتش دوزخ صورتهای آنها را می‌سوزاند و در جهنم، زشت منظر خواهند زیست.'),
(2778, 23, 105, '(و به آنها خطاب شود) آیا آیات من برای شما تلاوت نمی‌شد؟ و شما تکذیب آیات من می‌کردید؟'),
(2779, 23, 106, 'گویند: بارالها (رحم کن که) شقاوت بر ما غلبه کرد و کار ما به گمراهی کشید.'),
(2780, 23, 107, 'پروردگارا، ما را از جهنم بیرون آر، که اگر دیگر بار عصیان تو کردیم همانا بسیار ستمکار (و محکوم به هر گونه کیفر و عذاب سخت) خواهیم بود.'),
(2781, 23, 108, 'باز به آنان خطاب سخت شود: (ای سگان) دور شوید و به دوزخ درآیید و با من لب از سخن فروبندید.'),
(2782, 23, 109, 'زیرا شمایید که چون طایفه‌ای از بندگان (صالح) من عرض می‌کردند: بارالها، ما به تو ایمان آوردیم، تو از گناهان ما درگذر و در حق ما لطف و مهربانی فرما، که تو بهترین مهربانان هستی.'),
(2783, 23, 110, 'در آن وقت (شما کافران) آن بندگان خاص مرا تمسخر می‌کردید تا آنجا که این سبب شد که مرا به کلی فراموش کردید و بر آن خداپرستان خنده و استهزاء می‌نمودید.'),
(2784, 23, 111, 'من هم امروز جزای صبر (بر آزار و سخریه شما) را به آن بندگان پاک خود خواهم داد که آنها امروز سعادتمند و رستگاران عالمند.'),
(2785, 23, 112, '(آن‌گاه خدا به کافران) گوید که می‌دانید شما چند سال در زمین درنگ کردید؟'),
(2786, 23, 113, 'آنها پاسخ دهند که یک روز بود یا یک جزء از روز؛ (اگر ما خطا می‌گوییم) از شمارندگان (یعنی فرشتگانی که حساب عمر خلق دانند) باز پرس.'),
(2787, 23, 114, 'خدا فرماید: شما اگر (از حال زندگانی خود) آگاه بودید می‌دانستید که مدت درنگتان در دنیا بسیار اندک بوده است.'),
(2788, 23, 115, 'آیا چنین پنداشتید که ما شما را به عبث و بازیچه آفریده‌ایم و هرگز به سوی ما بازگردانده نمی‌شوید؟!'),
(2789, 23, 116, 'پس (بدانید که) خدای پادشاه به حق، برتر است (از آنکه از او فعل عبث و بیهوده صادر شود) که هیچ خدایی به جز همان پروردگار عرش مبارک نیست.'),
(2790, 23, 117, 'و هر کس غیر خدا کسی دیگر را به الهیت خواند که هیچ دلیلی بر آن ندارد حساب کار او نزد خدای اوست و البته کافران را (در روز کیفر) فلاح و رستگاری نیست.'),
(2791, 23, 118, 'و بگو: بارالها، بیامرز و ببخشای و تو بهترین بخشندگان عالم وجودی.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'این سوره را فرستادیم و (احکامش را) فریضه (بندگان) کردیم و در آن آیات روشن (توحید و معارف و احکام الهی) نازل ساختیم، باشد که شما متذکر (آن حقایق) شوید.'),
(2793, 24, 2, 'باید هر یک از زنان و مردان زنا کار را به صد تازیانه مجازات و تنبیه کنید و هرگز درباره آنان در دین خدا رأفت و ترحم روا مدارید اگر به خدا و روز قیامت ایمان دارید، و باید عذاب آن بدکاران را جمعی از مؤمنان مشاهده کنند.'),
(2794, 24, 3, 'مرد زناکار جز با زن زناکار و مشرک نکاح نمی‌کند و زن زانیه را هم جز مرد زانی و مشرک به نکاح نخواهد گرفت و این کار بر مردان مؤمن حرام است.'),
(2795, 24, 4, 'و آنان که به زنان با عفت مؤمنه نسبت زنا دهند آن‌گاه چهار شاهد (عادل) بر دعوی خود نیاورند آنان را به هشتاد تازیانه کیفر دهید، و دیگر هرگز شهادت آنها را نپذیرید، و آنان مردمی فاسق و نادرستند.'),
(2796, 24, 5, 'مگر آنهایی که بعد از آن فسق و بهتان به درگاه خدا توبه کنند و در مقام اصلاح عمل خود برآیند، در این صورت خدا البته بسیار آمرزنده و مهربان است.'),
(2797, 24, 6, 'و آنان که به زنان خود نسبت زنا دهند و جز خود بر آن شاهد و گواهی ندارند (چون حد لازم آید، برای رفع حد) باید از آن زن و مرد نخست مرد چهار مرتبه شهادت و قسم به نام خدا یاد کند که البته او (در این دعوی زنا) از راستگویان است.'),
(2798, 24, 7, 'و بار پنجم قسم یاد کند که لعن خدا بر او باد اگر از دروغگویان باشد.'),
(2799, 24, 8, 'و (چون بعد از این پنج قسم شوهر، بر زن حد لازم آید) برای رفع عذاب حد، آن زن نیز نخست چهار مرتبه شهادت و قسم به نام خدا یاد کند که البته شوهرش دروغ می‌گوید.'),
(2800, 24, 9, 'و بار پنجم قسم یاد کند که غضب خدا بر او اگر این مرد (در این دعوی) از راستگویان باشد.'),
(2801, 24, 10, 'و اگر فضل و رحمت خدا شامل حال شما مؤمنان نبود و اگر نه این بود که خدای مهربان البته توبه‌پذیر است و کار به حکمت کند (حدود و تکلیف را چنین آسان نمی‌گرفت و به توبه رفع عذاب از شما نمی‌کرد).'),
(2802, 24, 11, 'همانا کسانی که آن بهتان را بستند (و به عایشه تهمت کار ناشایست زدند که رسول و مؤمنان را بیازارند) گروهی همدست از (منافقان) شمایند؛ نپندارید ضرری از آن به آبروی شما می‌رسد بلکه خیر و ثواب نیز (چون بر تهمت صبر کنید از خدا) خواهید یافت؛ هر یک از آنها به عقاب گناه خود خواهند رسید؛ و آن کس از منافقان که رأس و منشأ این بهتان بزرگ گشت هم او به عذابی بزرگ معذب خواهد شد.'),
(2803, 24, 12, 'آیا سزاوار این نبود که چون (از منافقان) چنین بهتان و دروغها شنیدید مردان و زنان مؤمن حسن ظنّشان درباره یکدیگر بیشتر شده و گویند: این دروغی است آشکار؟'),
(2804, 24, 13, 'چرا (منافقان) بر دعوی خود چهار شاهد (عادل) اقامه نکردند؟ پس حالی که شاهد نیاوردند البته نزد خدا مردمی دروغزنند.'),
(2805, 24, 14, 'و اگر فضل و رحمت خدا در دنیا و عقبی شامل حال شما (مؤمنان) نبود به مجرّد خوض در این‌گونه سخنان (نفاق انگیز) به شما عذابی بزرگ می‌رسید.'),
(2806, 24, 15, 'زیرا شما آن سخنان را از زبان یکدیگر تلقّی کرده و حرفی بر زبان می‌گفتید که علم به آن نداشتید و این کار را سهل و کوچک می‌پنداشتید در صورتی که نزد خدا (گناهی) بسیار بزرگ بود.'),
(2807, 24, 16, 'چرا به محض شنیدن این سخن نگفتید که هرگز ما را تکلم به این روا نیست، پاک خدایا، این بهتانی بزرگ است؟'),
(2808, 24, 17, 'خدا به شما (مؤمنان) موعظه و اندرز می‌کند که زنهار دیگر گرد این چنین سخن نگردید اگر اهل ایمانید.'),
(2809, 24, 18, 'و خدا آیات خود را برای شما بیان می‌کند، و او (به حقایق امور و سرائر خلق) دانا و (به مصالح بندگان و نظام عالم کاملا) آگاه است.'),
(2810, 24, 19, 'آنان که (چون عبد اللّه ابیّ سلول و منافقان دیگر) دوست می‌دارند که در میان اهل ایمان کار منکری اشاعه و شهرت یابد آنها را در دنیا و آخرت عذابی دردناک خواهد بود و خدا (فتنه‌گری و دروغشان را) می‌داند و شما نمی‌دانید.'),
(2811, 24, 20, 'و اگر فضل و رحمت خدا و رأفت و مهربانی او شامل حال شما (مؤمنان) نبود (در عقاب گناهتان تعجیل کردی و توبه نپذیرفتی).'),
(2812, 24, 21, 'ای کسانی که ایمان آورده‌اید، زنهار پیروی شیطان مکنید، که هر کس قدم به قدم از پی شیطان رفت او به کار زشت منکر وامی‌دارد، و اگر فضل و رحمت خدا شامل شما نبود احدی از شما (از گناه و زشتی) پاک و پاکیزه نشدی، لیکن خدا هر کس را می‌خواهد منزه و پاک می‌گرداند، و خدا شنوا و داناست.'),
(2813, 24, 22, 'و نباید صاحبان ثروت و نعمت از شما درباره خویشاوندان خود و در حق مسکینان و مهاجران راه خدا از بخشش و انفاق کوتاهی کنند، باید مؤمنان عفو و صفح پیشه کنند و از بدیها درگذرند، آیا دوست نمی‌دارید که خدا هم در حق شما مغفرت (و احسان) فرماید؟ و خدا بسیار آمرزنده و مهربان است.'),
(2814, 24, 23, 'کسانی که به زنان باایمان عفیفه (مبرّا و) بی‌خبر از کار بد تهمت زنند محققا در دنیا و آخرت ملعون و محروم از رحمت حق هستند و هم آنان به عذابی بزرگ معذب خواهند شد.'),
(2815, 24, 24, 'در روزی که زبان و دست و پای ایشان بر اعمال (ناشایسته) آنها گواهی دهد.'),
(2816, 24, 25, 'در آن روز خدا حساب و کیفر شایسته آنها را تمام و کامل خواهد پرداخت و خواهند دانست که خداوند حق آشکار است.'),
(2817, 24, 26, 'زنان بدکار و ناپاک شایسته مردانی بدین وصفند و مردان زشتکار و ناپاک نیز شایسته زنانی بدین صفتند و (بالعکس) زنان پاکیزه نیکو لا یق مردانی چنین و مردان پاکیزه نیکو لایق زنانی همین گونه‌اند، و این پاکیزگان از سخنان بهتانی که ناپاکان درباره آنان گویند منزهند و بر ایشان آمرزش و رزق نیکوست.'),
(2818, 24, 27, 'ای اهل ایمان، هرگز به هیچ خانه مگر خانه‌های خودتان تا با صاحبش انس و اجازه ندارید وارد نشوید و (چون رخصت یافته و داخل شوید) به اهل آن خانه نخست سلام کنید که این (ورود با اجازه و تحیت، برای حسن معاشرت و ادب انسانیت) شما را بسی بهتر است، باشد که متذکر (شئون یکدیگر و حافظ آداب رفاقت) شوید.'),
(2819, 24, 28, 'و اگر کسی را نیافتید باز وارد نشوید تا اجازه یابید، و چون (به خانه‌ای درآمدید و) گفتند: برگردید، به زودی بازگردید که این برای تنزیه و پاکی شما بهتر است، و خدا به هر چه می‌کنید داناست.'),
(2820, 24, 29, 'باکی بر شما نیست که به خانه غیر مسکونی که در آن متاعی دارید (بی‌اجازه) در آیید، و (بدانید که) خدا به همه کار آشکار و پنهان شما آگاه است.'),
(2821, 24, 30, '(ای رسول ما) مردان مؤمن را بگو تا چشمها (از نگاه ناروا) بپوشند و فروج و اندامشان را (از کار زشت با زنان) محفوظ دارند که این بر پاکیزگی (جسم و جان) آنان اصلح است، و البته خدا به هر چه کنید کاملا آگاه است.'),
(2822, 24, 31, 'و زنان مؤمن را بگو تا چشمها (از نگاه ناروا) بپوشند و فروج و اندامشان را (از عمل زشت) محفوظ دارند و زینت و آرایش خود جز آنچه قهرا ظاهر می‌شود (بر بیگانه) آشکار نسازند، و باید سینه و بر و دوش خود را به مقنعه بپوشانند و زینت و جمال خود را آشکار نسازند جز برای شوهران خود یا پدران یا پدران شوهر یا پسران خود یا پسران شوهر یا برادران خود یا پسران برادران و پسران خواهران خود یا زنان خود (یعنی زنان مسلمه) یا کنیزان ملکی خویش یا مردان اتباع خانواده که رغبت به زنان ندارند یا اطفالی که هنوز بر عورت و محارم زنان آگاه نیستند (و از غیر این اشخاص مذکور احتجاب و احتراز کنند) و آن طور پای به زمین نزنند که خلخال و زیور پنهان پاهایشان معلوم شود. و ای اهل ایمان، همه به درگاه خدا توبه کنید، باشد که رستگار شوید.'),
(2823, 24, 32, 'و البته باید مردان بی‌زن و زنان بی‌شوهر و کنیزان و بندگان شایسته خود را به نکاح یکدیگر درآورید، اگر آن مردان و زنان فقیرند خدا به لطف و فضل خود آنان را بی‌نیاز و مستغنی خواهد فرمود و خدا رحمتش وسیع و نامتناهی و (به احوال بندگان) آگاه است.'),
(2824, 24, 33, 'و آنان که وسیله نکاح نیابند باید عفت نفس پیشه کنند تا خدا آنها را به لطف و فضل خود بی‌نیاز گرداند. و از بردگانتان آنان که تقاضای مکاتبه کنند (یعنی خواهند که خود را از مولا به مبلغی مشروط یا مطلق خریداری کنند) تقاضای آنها را اگر خیر و صلاحی در ایشان مشاهده کنید بپذیرید و (برای کمک به آزاد شدن آنها) از مال خدا که به شما اعطا فرموده (به عنوان زکات و صدقات در وجه مال المکاتبه) به آنها بدهید، و کنیزکان خود را که مایلند به عفت، زنهار برای طمع مال دنیا جبرا به زنا وادار مکنید، که هر کس آنها را اکراه به زنا کند خدا در حق آنها که مجبور بودند بسیار آمرزنده و مهربان است (لیکن شما را که آنها را به زنا مجبور کنید به جای آنان عقاب خواهد کرد).'),
(2825, 24, 34, 'و همانا ما به سوی شما آیاتی روشن نازل گردانیدیم و داستانی از آنان که پیش از شما درگذشتند و موعظه‌ای برای اهل تقوا فرستادیم.'),
(2826, 24, 35, 'خدا نور (وجودبخش) آسمانها و زمین است، داستان نورش به مشکاتی ماند که در آن روشن چراغی باشد و آن چراغ در میان شیشه‌ای که از تلألؤ آن گویی ستاره‌ای است درخشان، و روشن از درخت مبارک زیتون که (با آنکه) شرقی و غربی نیست (شرق و غرب جهان بدان فروزان است) و بی‌آنکه آتشی زیت آن را برافروزد خود به خود (جهانی را) روشنی بخشد، پرتو آن نور (حقیقت) بر روی نور (معرفت) قرار گرفته است. و خدا هر که را خواهد به نور خود (و اشراقات وحی خویش) هدایت کند و (این) مثلها را خدا برای مردم (هوشمند) می‌زند (که به راه معرفتش هدایت یابند) و خدا به همه امور داناست!'),
(2827, 24, 36, '(و آن مشکات) در خانه‌هایی (قرار دارد مانند معابد و مساجد و منازل انبیاء و اولیاء) که خدا رخصت داده که آنجا رفعت یابد و در آنها ذکر او شود و صبح و شام تسبیح و تنزیه ذات پاک او کنند.'),
(2828, 24, 37, 'پاکمردانی که هیچ کسب و تجارت آنان را از یاد خدا و به پا داشتن نماز و دادن زکات غافل نگرداند و از روزی که دل و دیده‌ها در آن روز حیران و مضطرب است ترسان و هراسانند.'),
(2829, 24, 38, 'تا خدا در مقابل بهترین اعمال ایشان جزاء و ثواب کامل عطا فرماید و از فضل و احسان خویش بر آنها بیفزاید، و خدا هر که را خواهد روزی بی‌حد و حساب بخشد.'),
(2830, 24, 39, 'و آنان که کافرند اعمالشان در مثل به سرابی ماند در بیابان هموار بی‌آب که شخص تشنه آن را آب پندارد (و به جانب آن شتابد) چون بدانجا رسید هیچ آب نیابد، و (آن کافر) خدا را حاضر و ناظر اعمال خویش بیند که به حساب کارش تمام و کامل برسد و خدا به یک لحظه حساب تمام خلایق می‌کند.'),
(2831, 24, 40, 'یا (مثل اعمال کافران) به ظلمات دریای موّاج عمیقی ماند که امواج آن (که شک و جهل و اعمال زشت است) بعضی بالای بعضی دیگر دریا را بپوشاند و ابر تیره (کفر) نیز فراز آن برآید تا ظلمت‌ها چنان متراکم فوق یکدیگر قرار گیرد که چون دست بیرون آرد آن را نتواند دید، و هر که را خدا نور (علم و معرفت و ایمان) نبخشید هرگز (جان) روشنی نخواهد یافت.'),
(2832, 24, 41, 'آیا ندیدی که هر کس در آسمانها و زمین است تا مرغهایی که در هوا پر گشایند همه به تسبیح و ثنای خدا مشغولند؟ و همه آنان صلاة و تسبیح خود بدانند، و خدا به هر چه کنند آگاه است.'),
(2833, 24, 42, 'و (بدانید که) ملک و پادشاهی آسمانها و زمین خاص خداست و بازگشت (همه خلایق) به سوی خداست.'),
(2834, 24, 43, 'آیا ندیدی که خدا ابر را (از هر طرف) براند و به هم در پیوندد و باز انبوه و متراکم سازد آن گاه بنگری قطرات باران از میان ابر فرو ریزد؟ و نیز از آسمان، از کوههایی (از ابرهای منجمد شده) که در آن است تگرگ فرو بارد و زیان آن را به هر که خود خواهد برساند (و اشجار و کشتزار او را تباه گرداند) و آن را از هر که خواهد باز دارد (تا تگرگش زیان نرساند) و روشنی برق آن چنان بتابد که خواهد روشنی دیده‌ها را از بین ببرد.'),
(2835, 24, 44, 'خدا شب و روز را (بر یکدیگر) می‌گرداند، همانا صاحبان بصیرت را در این (آیت الهی و حکمت خدا) عبرتی است.'),
(2836, 24, 45, 'و خدا هر حیوانی را از آب آفرید، که بعضی (مانند مارها) بر شکم روند و برخی (مانند انسان) بر دو پا و برخی (چون اسب و گاو و شتر) بر چهار پا حرکت کنند. و خدا هر چه خواهد بیافریند که خدا بر همه کار تواناست.'),
(2837, 24, 46, 'همانا ما آیات و ادله بسیار روشن فرستادیم. و خدا هر که را خواهد به راه راست هدایت می‌کند.'),
(2838, 24, 47, 'و (منافقان هم مانند مؤمنان) می‌گویند که ما به خدا و رسول ایمان آورده و اطاعت می‌کنیم، و لیکن با این همه قول باز گروهی (وقت عمل، از حق) روی می‌گردانند و اصلا آنان ایمان ندارند.'),
(2839, 24, 48, 'و هر گاه به سوی خدا و رسول خوانده شوند تا (رسول به حکم خدا) میان آنها قضاوت کند گروهی از ایشان (از این دعوت) اعراض می‌کنند.'),
(2840, 24, 49, 'و اگر دعوی خود را بر حق بدانند البته با کمال انقیاد به سوی رسول می‌آیند.'),
(2841, 24, 50, 'آیا در دلهایشان مرض (جهل و نفاق) است یا شک و ریبی دارند یا می‌ترسند که خدا و رسول (در مقام قضاوت) بر آنها جور و ستمی کنند؟ (خدا ظلم نخواهد کرد) بلکه آنها خود مردمی ظالم و متعدیند.'),
(2842, 24, 51, 'آن مؤمنانند که چون به سوی حکم خدا و رسولشان بخوانند تا (رسول به حکم خدا) میان آنها حکم کند (از دل و جان) خواهند گفت که شنیده و اطاعت می‌کنیم. و رستگاران عالم به حقیقت اینها هستند.'),
(2843, 24, 52, 'و هر کس فرمان خدا و رسول را اطاعت کند و خدا ترس و پرهیزگار باشد چنین کسان را فیروزی و سعادت خواهد بود.'),
(2844, 24, 53, 'و منافقان شدیدترین قسم را به نام خدا یاد کنند که اگر تو آنها را امر کنی البته همه (به جهاد دشمن) بیرون خواهند رفت، بگو: سوگند یاد مکنید، که طاعت پسندیده و مطلوب است (نه سوگند دروغ منافقانه. و بترسید که) خدا به هر چه می‌کنید کاملا آگاه است.'),
(2845, 24, 54, 'بگو که خدا را فرمان برید و رسول را اطاعت کنید. پس اگر اطاعت نکردید بر او بار رسالت اوست و بر شما بار تکلیف خویش است، و اگر او را اطاعت کنید هدایت (و سعادت) خواهید یافت، و بر رسول جز ابلاغ روشن رسالت تکلیفی نیست.'),
(2846, 24, 55, 'خدا به کسانی از شما بندگان که (به خدا و حجّت عصر علیه السّلام) ایمان آرند و نیکوکار گردند وعده فرمود که (در ظهور امام زمان) در زمین خلافتشان دهد چنانکه امم صالح پیمبران سلف را جانشین پیشینیان آنها نمود، و دین پسندیده آنان را (که اسلام واقعی است بر همه ادیان) تمکین و تسلط عطا کند و به همه آنان پس از خوف و اندیشه از دشمنان ایمنی کامل دهد که مرا به یگانگی، بی هیچ شائبه، شرک و ریا پرستش کنند، و بعد از آن هر که کافر شود پس آنان به حقیقت همان فاسقان تبهکارند.'),
(2847, 24, 56, 'و نماز را به پا دارید و زکات بدهید و رسول ما را اطاعت کنید، باشد که مورد لطف و رحمت شوید.'),
(2848, 24, 57, '(ای رسول) هرگز مپندار که کافران در زمین معجز و قدرتی خواهند یافت، و جایگاهشان (نیز در آخرت) دوزخ است که بسیار منزلگاه بدی است.'),
(2849, 24, 58, 'ای کسانی که ایمان آورده‌اید (بدانید که) بندگان ملکی شما و اطفال شما که هنوز به وقت احتلام و زمان بلوغ نرسیده‌اند باید (شبانه روزی) سه مرتبه از شما اجازه ورود بخواهند: یک بار پیش از نماز صبح و دیگر هنگام ظهر که جامه‌ها را از تن برمی‌گیرید و دیگر پس از نماز خفتن، که این سه وقت هنگام عورت و خلوت شماست (اکثر برهنه یا در لباس کوتاهید) و بعد از این سه بار اجازه، دیگر باکی بر شما و آنها نیست که (بی‌دستور) با بندگان و اطفال خود گرد یکدیگر جمع شوید و هر ساعت در کارها به شما مراجعه کنند. خدا آیات را بر شما چنین روشن بیان می‌کند، و خدا (به کار بندگان) دانا و (به مصالح خلق) آگاه است.'),
(2850, 24, 59, 'و آن گاه که اطفال شما به حد بلوغ و احتلام رسیدند باید مانند سایر بالغان (البته همه وقت) با اجازه وارد شوند. خدا آیات خود را برای شما بدین روشنی بیان می‌کند، و خدا (به صلاح بندگان) دانا و (در وضع تکلیف) محکم کار است.'),
(2851, 24, 60, 'و زنان سالخورده که (از ولادت و عادت) باز نشسته‌اند و امید ازدواج و نکاح ندارند بر آنان باکی نیست در صورتی که اظهار تجملات و زینت خود نکنند که جامه‌های خود (یعنی لباسهای رو مانند عبا و چادر و روپوش و امثال آن) را نزد نامحرمان برگیرند، و باز هم عفت و تقوا گزینی (و بر نگرفتن جامه) بر آنان (در دین و دنیا) بهتر است، و خدا (به سخنان خلق) شنوا و (به اغراض و نیّات آنها) آگاه است.'),
(2852, 24, 61, 'بر نابینایان و لنگان و بیماران باکی نیست (که به جهاد نروند یا تندرستان با آنها معاشر و همسفره شوند) و نیز باکی بر شما نیست که از خانه‌های خود و پدران خود و مادرانتان و برادران و خواهران و عمو و عمه و خالو و خاله خویش غذا تناول کنید یا آنکه از هر جا که کلید آن در دست شماست یا خانه رفیق خود باکی نیست که از مجموع یا هر یک از این خانه‌ها طعامی خورید. و (با وجود این دستور باز) هرگاه بخواهید به خانه‌ای (از اینها) داخل شوید نخست بر خویش سلام کنید (یعنی چون به خانه یا مسجدی درآیید بر مسلمانان و همدینان خودتان یا اگر کسی نباشد بر نفس خود باز سلام کنید و خلاصه باخبر وارد شوید) که سلام تحیّتی با برکت و نیکو از جانب خداست. خدا آیات خود را این گونه برای شما روشن بیان می‌کند باشد که (در آنها) تعقل کنید (و طریق سعادت و هدایت بازجویید).'),
(2853, 24, 62, 'مؤمنان حقیقی آنهایی هستند که به خدا و رسولش ایمان (و انقیاد کامل) دارند و هرگاه در کاری اجتماعشان به حضور رسول (صلّی اللّه علیه و آله و سلّم) لازم باشد حاضر آیند و تا اجازه نخواهند هرگز از محضر او بیرون نمی‌روند. و (ای رسول ما) آنان که از تو اجازه می‌خواهند اینان به حقیقت اهل ایمان به خدا و رسولند، پس چون از تو اجازت طلبند که بعضی مشاغل و امور خود را انجام دهند از آنان هر که را خواهی اجازه ده و بر آنها از خدا طلب مغفرت و آمرزش کن، که خدا بسیار آمرزنده و مهربان است.'),
(2854, 24, 63, '(ای مؤمنان) شما دعای رسول (صلّی اللّه علیه و آله و سلّم) و ندا کردن او را مانند ندای بین یکدیگر (بدون حفظ ادب مقام رسالت) قرار مدهید (یا دعای او را مانند دعای خود نامستجاب مپندارید)، خدا به حال آنان که (برای سرپیچی از حکمش) به یکدیگر پناه برده و از میان شما رخ پنهان می‌دارند آگاه است. پس باید کسانی که امر خدا را مخالفت می‌کنند و از آن روی می‌گردانند بترسند که مبادا به فتنه‌ای بزرگ (تسلط سلطان جور) یا عذاب دردناک دیگر (مانند قتل و اسارت) گرفتار شوند.'),
(2855, 24, 64, 'آگاه باشید که آنچه در آسمانها و زمین است همه ملک خداست، شما به هر حالی باشید البته خدا از آن آگاه است. و روزی که بندگان را به سوی او بازگردانند (یعنی هنگام مرگ یا قیامت) آن روز به (جزای) هر نیک و بدی که کرده‌اند آگاهشان می‌گرداند، و خدا به هر چیزی کاملا داناست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بزرگوار است پاک خداوندی که فرقان را بر بنده خاص خود نازل فرمود تا (به اندرزهای وی) اهل عالم را متذکر و خدا ترس گرداند.'),
(2857, 25, 2, 'آن خدایی که پادشاهی آسمانها و زمین از آن اوست و هرگز فرزندی نگرفته و شریکی در ملک هستی نداشته و همه موجودات را او خلق کرده و حد و قدر هر چیز را معین فرموده است.'),
(2858, 25, 3, 'و (مشرکان نادان) خدا را که مبدأ کل و مرجع همه امور عالم است فراموش کرده و غیر او (یعنی بتهای بی اثر) را به خدایی برگرفتند و حال آنکه آن بتان هیچ چیزی نتوانند آفرید و خودشان هم مخلوقند و نه هیچ بر نفع و ضرر خود قادرند و نه امر موت و حیات و بعث به دست آنهاست.'),
(2859, 25, 4, 'و کافران گفتند این کتاب (که محمّد و حیش می‌شمرد) جز آنکه به دروغ از خود فرابافته و دیگران (از اهل کتاب) نیز (در قصص و احکام) به او کمک کرده‌اند بچیز دیگری نیست. و البته این سخن کافران ظلمی بزرگ (درباره قرآن الهی) و نسبتی نا حق (در حقّ پیغمبر خدا) است.'),
(2860, 25, 5, 'و باز گفتند که این کتاب افسانه‌های پیشینیان و حکایات سابقین است که خود از آنها نسخه برداشته و (اصحابش) صبح و شام بر او املا و قرائت می‌کنند (تا کامل و آراسته گردانند).'),
(2861, 25, 6, 'بگو: این کتاب را آن خدایی فرستاده که از اسرار آسمانها و زمین آگاه است (و شما از این انکار و کفر و گناه باز گردید که) هم او البته بسیار آمرزنده و مهربان است.'),
(2862, 25, 7, 'و باز کافران گفتند: چرا این رسول (اگر به راستی پیغمبر خداست) غذا تناول می‌کند و در (کوچه و) بازارها راه می‌رود؟ چرا فرشته محسوس و ظاهری بر او نازل نشده تا به همراه او بیم دهد (و گواه صدق او باشد).'),
(2863, 25, 8, 'یا چرا بر این رسول گنجی فرو نیفتد یا چرا باغی ندارد که از میوه‌هایش تناول کند؟ و ستمکاران (پس از این همه چون و چراها به مردم) گفتند که شما پیروی نمی‌کنید مگر مردی را که سحر و شعبده از راهش برده است.'),
(2864, 25, 9, 'بنگر تا چه داستان و مثلها برای تو زدند؟ پس چنان گمراه شدند که دیگر هیچ راه رشد و هدایتی نتوانند یافت.'),
(2865, 25, 10, 'بزرگوار است پاک خدایی که اگر خواستی برای تو بهتر از آنچه این کافران گفتند قرار دادی و آن بهشتهایی است که زیر درختانش نهرها جاری است و (در آن بهشت) قصرهای بسیار عالی خاص تو قرار می‌داد.'),
(2866, 25, 11, 'بلکه این کافران ساعت قیامت را تکذیب کردند و ما آتش سوزان دوزخ را بر آن که قیامت را تکذیب کرد مهیا داشته‌ایم.'),
(2867, 25, 12, 'چون آتش دوزخ آنان را از مکانی دور ببیند خروش و فریاد وحشتناک دوزخ را از دور به گوش خود می‌شنوند.'),
(2868, 25, 13, 'و چون آن کافران را در زنجیر بسته به مکان تنگی از جهنم درافکنند در آن حال همه فریاد و اویلا از دل برکشند.'),
(2869, 25, 14, '(و به آنها عتاب شود که) امروز فریاد حسرت و ندامت شما یکی دو تا نیست بلکه بسیار از این آه و واویلاها از دل برکشید.'),
(2870, 25, 15, 'بگو: آیا این بهتر است یا بهشت ابدی که به متّقیان وعده دادند که آن بهشت پاداش و منزل ایشان است.'),
(2871, 25, 16, 'که در آن بهشت هر چه خواهند و آرزو کنند بر آنها حاضر است و آن مقام همیشگی آنهاست، وعده‌ای است بر خدا که مؤمنان از او درخواست کرده‌اند و او اجابت فرموده است.'),
(2872, 25, 17, 'و روزی که این مشرکان را با معبودانی که به جای خدا می‌پرستیدند به عرصه قیامت محشور گرداند، آن گاه بدان معبودان گوید: آیا شما بندگان مرا از راه بردید یا خود به راه ضلالت شتافتند؟'),
(2873, 25, 18, 'آنها گویند: پاک و منزه پروردگارا، ما خود را هرگز سزاوار آن نمی‌دانستیم که جز تو کسی را دوست خود اختیار کنیم، و لیکن تو این کافران و پدرانشان را متمتّع (به دنیا و نعمتهای آن) گردانیدی تا آنکه ذکر تو (و قرآن عظیم تو که سرمایه سعادت بود) را فراموش کردند و مردمی شقی و تبه روزگار بودند.'),
(2874, 25, 19, 'پس آن معبودان، شما مشرکان را در آنچه گویید تکذیب کردند (و در اثر آن به عذاب ما گرفتار شدید) پس نه شما توانید آن عذاب را برطرف گردانید و نه یاری خود توانید کرد، و هر کسی از شما بندگان (با شرک آوردن) ظلم و ستم نماید ما او را به عذابی بزرگ گرفتار می‌گردانیم.'),
(2875, 25, 20, 'و ما هیچ رسولی پیش از تو به خلق نفرستادیم مگر آنکه آنها هم (مانند تو) غذا می‌خوردند و در میان بازارها راه می‌رفتند (پس امّت به این عذر تکذیب رسالت تو نتوانند کرد)، و ما بعضی از شما بندگان را سبب آزمایش بعضی دیگر می‌گردانیم تا آیا صبر (در طاعت خدا) خواهید کرد؟ و پروردگار تو (به احوال و اعمال همه خلق) بیناست.'),
(2876, 25, 21, 'و آنان که امید لقای ما را ندارند (و به قیامت معتقد نیستند) گویند: چرا فرشتگان بر ما نازل نشدند؟ یا چرا ما خدا را به چشم نمی‌بینیم (تا در ایمان، به رسول محتاج نباشیم)؟ همانا آنان در حق خویش راه تکبر و نخوت پیش گرفتند و به سرکشی و طغیانی شدید شتافتند.'),
(2877, 25, 22, 'روزی که فرشتگان را ببینند مجرمان در آن روز بشارتی (از فرشته) نیابند و به آنها گویند: محروم و ممنوع (از لقاء رحمت و جنّت خدا) باشید.'),
(2878, 25, 23, 'و ما توجه به اعمال (فاسد بی‌خلوص و حقیقت) آنها کرده و همه را (باطل و نابود چون) غباری پراکنده می‌گردانیم.'),
(2879, 25, 24, 'اهل بهشت در آن روز مسکن و مقرّی بهتر و آرامش و خوابگاهی نیکوتر خواهند داشت.'),
(2880, 25, 25, 'و (یاد کن) روزی که آسمان با ابر از هم شکافته و فرشتگان با سرعت تمام (به امر حق) فرود آیند.'),
(2881, 25, 26, 'پادشاهی به حق در آن روز خاص خدای رحمان است، و بر کافران روز بسیار سختی خواهد بود.'),
(2882, 25, 27, 'و روزی که شخص ظالم پشت دست حسرت به دندان گرفته و گوید: ای کاش من (در دنیا) با رسول حق راهی (برای دوستی و طاعت) پیش می‌گرفتم.'),
(2883, 25, 28, 'وای بر من، ای کاش که فلان (مرد کافر و رفیق فاسق) را دوست نمی‌گرفتم.'),
(2884, 25, 29, 'رفاقت او مرا از پیروی قرآن پس از آنکه به من رسید محروم ساخت و گمراه گردانید، آری شیطان برای انسان مایه خذلان (و گمراهی) است.'),
(2885, 25, 30, 'در آن روز رسول (به شکوه از امت در پیشگاه رب العزّه) عرض کند: بارالها (تو آگاهی که) امّت من این قرآن را به کلّی متروک و رها کردند.'),
(2886, 25, 31, 'و همچنین ما برای هر پیغمبری دشمنی از جنس بدکاران (امّتش) قرار دادیم، و تنها خدای تو برای هدایت و نصرت و یاری تو کفایت است.'),
(2887, 25, 32, 'و باز (کافران جاهل) به اعتراض گفتند که چرا این قرآن (اگر از جانب خداست) یکجا برای رسول نازل نشد؟ ما آن را این چنین فرستادیم تا (تدریجا) تو را به آیات آن دل آرام کنیم و اطمینان قلب دهیم و (بدین سبب) آیات آن را بر تو مرتب به ترتیبی روشن و به آهستگی برخواندیم.'),
(2888, 25, 33, 'و کافران بر تو هیچ مثل (باطل و اعتراض ناحق) نیاورند مگر آنکه ما در مقابل برای تو سخن حق را با بهترین بیان پاسخ آنها آریم.'),
(2889, 25, 34, 'آنان که به رو به سوی آتش دوزخ برندشان به بدترین مکان شتافته و سخت‌ترین راه ضلالت یافته‌اند.'),
(2890, 25, 35, 'و همانا ما به موسی کتاب (تورات) را عطا کردیم و برادرش هارون را وزیر او قرار دادیم.'),
(2891, 25, 36, 'آن گاه گفتیم که رو به جانب آن قوم که آیات ما را تکذیب کردند روید. پس آنها را (پس از تکذیب آن دو) سخت هلاک گردانیدیم.'),
(2892, 25, 37, 'و نیز قوم نوح چون رسولان حق را تکذیب کردند ما آنها را به طوفان هلاک سپردیم و آیت عبرت مردم ساختیم، و برای ستمکاران عذاب دردناک مهیا گردانیده‌ایم.'),
(2893, 25, 38, 'و نیز قوم عاد و ثمود و اصحاب رسّ (شاید قوم شعیب‌اند که نزد چشمه یا چاه آبی که رسّ نامند منزل داشتند) و طوایف دیگر بسیاری بین اینها همه را به کیفر کردارشان هلاک کردیم.'),
(2894, 25, 39, 'و ما برای هر یک از این طوایف مثلها (و پندها برای هدایت و اتمام حجت) زدیم و همه را به کلی هلاک ساختیم.'),
(2895, 25, 40, 'و همانا این کافران آمدند و دیدند آن دیار (قوم لوط) را که بر آن باران عذاب و هلاکت ببارانیدیم، آیا اینان به چشم خود دیار خراب آنها را ندیده‌اند؟ (آری دیدند) و لیکن امید و بیمی از قیامت و کیفر ندارند.'),
(2896, 25, 41, 'این کافران هرگاه تو را ببینند (از حسد) کاری ندارند جز آنکه تو را تمسخر کرده (و گویند) آیا این مرد همان است که خدا به رسالت بر خلق فرستاده؟'),
(2897, 25, 42, 'نزدیک بود که او ما را اگر بر بت‌پرستی خود پایدار نبودیم به کلی گمراه کند و از پرستش خدایانمان باز دارد. و (این مشرکان که از جهل، توحید را گمراهی خواندند) چون عذاب حق را مشاهده کنند خواهند دانست که گمراه‌تر از آنها در عالم نیست.'),
(2898, 25, 43, 'آیا دیدی حال آن کس را که (از غرور) هوای نفسش را خدای خود ساخته؟ آیا تو حافظ و نگهبان او (از هلاکت) توانی شد؟'),
(2899, 25, 44, 'یا پنداری که اکثر این کافران حرفی می‌شنوند یا فکر و تعقلی دارند؟ اینان (در بی‌عقلی) بس مانند چهار پایانند بلکه (نادان‌تر و) گمراه‌ترند.'),
(2900, 25, 45, 'آیا ندیدی که خدای تو چگونه سایه را با آنکه اگر خواستی ساکن کردی (بر سر عالمیان) بگسترانید؟ آن گاه آفتاب را بر آن دلیل قرار دادیم.'),
(2901, 25, 46, 'سپس ظلّ آن آفتاب (منبسط) را به سوی خود تدریجا قبض می‌کنیم.'),
(2902, 25, 47, 'و او خدایی است که شب را برای شما لباس گردانید (تا همه در سیاه جامه شب مستور شوید) و خواب را مایه سکون و آرامش شما قرار داد و روز روشن را برای جنبش و کار مقرر داشت.'),
(2903, 25, 48, 'و او خدایی است که بادها را برای بشارت پیشاپیش باران رحمت خود فرستاد، و از آسمان آبی طاهر و مطهّر نازل کردیم.'),
(2904, 25, 49, 'تا به آن باران زمین خشک و مرده را زنده سازیم و آنچه آفریدیم از چهارپایان و آدمیان بسیار همه را از آن سیراب گردانیم.'),
(2905, 25, 50, 'و ما تغییرات و گردش باد و باران (و سایر انقلابات عالم) را بین مردم آوردیم تا پند گرفته و متذکر حق شوند، لیکن اکثر مردم جز راه کفران و جحود پیش نگرفتند.'),
(2906, 25, 51, 'و اگر ما می‌خواستیم در بین مردم هر قریه پیغمبری که (خلق را از عذاب خدا) بترساند می‌فرستادیم.'),
(2907, 25, 52, 'پس تو هرگز تابع کافران مباش و با آنها چنان که مخالفت قرآن و دین حق کنند سخت جهاد و کارزار کن.'),
(2908, 25, 53, 'و او خدایی است که دو دریا را به هم درآمیخت که این آب گوارا و شیرین و آن دیگر شور و تلخ است، و بین این دو آب (در عین به هم آمیختن) واسطه و حایلی قرار داد که همیشه از هم منفصل و جدا باشند.'),
(2909, 25, 54, 'و او خدایی است که از آب (نطفه) بشر را آفرید و بین آنها خویشی نسب و بستگی ازدواج قرار داد، و خدای تو (بر هر چیز) قادر است.'),
(2910, 25, 55, 'و (این مشرکان نادان) به جای خدا بتهایی که هیچ سود و زیانی به حال آنان ندارد می‌پرستند، و کافر پشت به امر پروردگار خود می‌کند.'),
(2911, 25, 56, 'و ما تو را نفرستادیم مگر برای آنکه (خلق را به رحمت ما) بشارت دهی و (از عذاب ما) بترسانی.'),
(2912, 25, 57, 'بگو: من از شما امّت مزد رسالت نمی‌خواهم، اجر من همین بس که هر که بخواهد (از پی من) راهی به سوی خدای خود پیش گیرد.'),
(2913, 25, 58, 'و تو بر خدای زنده ابدی که هرگز نمیرد توکل کن و به ستایش ذات او وی را تسبیح و تنزیه گو، و هم او که به گناه بندگانش کاملا آگاه است کفایت است (هر که را بخواهد می‌بخشد و هر که را بخواهد مؤاخذه می‌کند).'),
(2914, 25, 59, 'آن خدایی که آسمانها و زمین و هر چه در بین آنهاست همه را در شش روز بیافرید آن گاه همین خدای رحمان بر عرش (قدرت و فرمانروایی) قرار گرفت، پس از خداشناسی آگاه، حقیقت را بازجو.'),
(2915, 25, 60, 'و چون به این مردم کافر گفته شود بیایید خدای رحمان را سجده کنید، در جواب گویند: خدای رحمان چیست؟ آیا ما به آنچه تو امر می‌کنی سجده کنیم؟ و دعوت به خدای یکتا (به جای اطاعت) بر نفرتشان بیفزاید.'),
(2916, 25, 61, 'بزرگوار آن خدایی که در آسمان برجها مقرر داشت و در آن چراغ روشن خورشید و ماه تابان را روشن ساخت.'),
(2917, 25, 62, 'و او خدایی است که شب و روز را جانشین یکدیگر قرار داد برای آن کس که خواهد به شب یا روز متذکر (خدا) شود یا شکر او به جای آرد.'),
(2918, 25, 63, 'و بندگان خاص خدای رحمان آنان هستند که بر روی زمین به تواضع و فروتنی راه روند و هرگاه مردم جاهل به آنها خطاب (و عتابی) کنند با سلامت نفس (و زبان خوش) جواب دهند.'),
(2919, 25, 64, 'و آنان هستند که شب را به سجده و قیام نماز در برابر خدایشان روز کنند.'),
(2920, 25, 65, 'و آنان هستند که دایم (به دعا و تضرّع) گویند: پروردگارا عذاب جهنم را از ما بگردان، که سخت عذاب مهلک و دایمی است.'),
(2921, 25, 66, 'که آنجا بسیار بد قرارگاه و بد منزلگاهی است.'),
(2922, 25, 67, 'و آنان هستند که هنگام انفاق (به مسکینان) اسراف نکرده و بخل هم نورزند، بلکه احسان آنها در حد میانه و اعتدال باشد.'),
(2923, 25, 68, 'و آنان هستند که با خدای یکتا کسی را شریک نمی‌خوانند و نفس محترمی را که خدا حرام کرده جز به حق به قتل نمی‌رسانند، و هرگز گرد عمل زنا نمی‌گردند، که هر که این عمل کند کیفر گناهش را خواهد دید.'),
(2924, 25, 69, 'عذابش در قیامت مضاعف شود و با ذلّت و خواری به دوزخ مخلّد گردد.'),
(2925, 25, 70, 'مگر آن کسانی که از گناه توبه کنند و عمل صالح به جای آرند، پس خدا گناهان آنها را بدل به حسنات گرداند، و خداوند بسیار آمرزنده و مهربان است.'),
(2926, 25, 71, 'و هر کس توبه کند و نیکوکار شود البته توبه‌اش به درگاه (قبول) خدا خواهد رسید.'),
(2927, 25, 72, 'و آنان هستند که به ناحق شهادت ندهند و هرگاه به عمل لغوی (از مردم غافل) بگذرند بزرگوارانه از آن درگذرند.'),
(2928, 25, 73, 'و آنان هستند که هرگاه به آیات خدای خود متذکرشان سازند کر و کورانه در آن آیات ننگرند (بلکه با دل آگاه و چشم بینا مشاهده آن کنند تا بر مقام معرفت و ایمانشان بیفزاید).'),
(2929, 25, 74, 'و آنان هستند که گویند: پروردگارا، ما را زنان و فرزندانی مرحمت فرما که مایه چشم روشنی ما باشند، و ما را (سر خیل پاکان و) پیشوای اهل تقوا قرار ده.'),
(2930, 25, 75, 'چنین بندگان را به پاداش صبرشان (در راه عبادت)، عالی غرفه‌های جنّت و قصرهای بهشتی دهند و در آنجا با تحیّت و سلام روبرو شوند.'),
(2931, 25, 76, 'در حالی که در آن بهشت که بسیار نیکو منزل و مقامی است تا ابد مخلّد و متنعّم خواهند بود.'),
(2932, 25, 77, 'بگو که اگر دعای شما (و ناله و زاری و توبه شما) نبود خدا به شما چه توجه و اعتنایی داشت؟ که شما کافران (آیات حق را) تکذیب کردید و به کیفر همیشگی آن گرفتار خواهید شد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسم (اسراری است بین خدا و رسول)'),
(2934, 26, 2, 'این قرآن آیات کتاب روشن خداست.'),
(2935, 26, 3, '(ای رسول ما) تو چنان در اندیشه هدایت خلقی که خواهی جان عزیزت را از غم اینکه ایمان نمی‌آورند هلاک سازی!'),
(2936, 26, 4, 'ما اگر بخواهیم از آسمان آیت قهری نازل گردانیم که همه به جبر گردن زیر بار (ایمان به) آن فرود آرند.'),
(2937, 26, 5, 'و (وای بر اینان که) هیچ آیت و ذکر تازه‌ای از خدای رحمان بر آنها نیاید جز آنکه از آن اعراض می‌کنند.'),
(2938, 26, 6, 'همانا این کافران آیات خدا را تکذیب کردند و به زودی خبر آنچه بدان استهزاء می‌کنند به آنان خواهد رسید.'),
(2939, 26, 7, 'آیا در زمین به دیده عبرت نظر نکردند که ما چه انواع گوناگون از نباتات پر سود از آن برویانیدیم؟'),
(2940, 26, 8, 'همانا در این کار آیتی (از لطف و رحمت و علم و قدرت خدا بر هوشمندان) آشکار است و باز اکثر اینان ایمان نمی‌آورند.'),
(2941, 26, 9, 'و همانا پروردگار تو بسیار مقتدر و مهربان است.'),
(2942, 26, 10, 'و (یاد آر) هنگامی که خدایت به موسی ندا کرد که اینک به سوی قوم ستمکار روی آور.'),
(2943, 26, 11, 'به سوی قوم ستمکار فرعون که آیا باز هم نمی‌خواهند خدا ترس و پرهیزگار شوند؟'),
(2944, 26, 12, 'موسی عرض کرد: ای پروردگار، از آن می‌ترسم که فرعونیان سخت مرا تکذیب کنند.'),
(2945, 26, 13, 'و (از کفر آنها) دلتنگ شوم و عقده زبانم (به هدایت آنان) باز نگردد، پس به سوی هارون (برادرم) فرست (تا با من به رسالت روانه شود).'),
(2946, 26, 14, 'و بر من از این قوم (قبطی) گناهی است که می‌ترسم به قتلم رسانند.'),
(2947, 26, 15, 'خدا به موسی فرمود: هرگز (مترس که شما را نمی‌کشند)، پس با برادرت هارون بدین معجزات و آیات ما به سوی آنان بروید که ما همه جا با شما هستیم و گفتار شما را می‌شنویم.'),
(2948, 26, 16, 'هر دو به اتفاق به جانب فرعون روید و بگویید که ما رسول پروردگار جهانیم.'),
(2949, 26, 17, 'با این پیام که طایفه بنی اسرائیل را با ما (از مصر به فلسطین) بفرست.'),
(2950, 26, 18, 'فرعون گفت: تو نه آن کودکی که ما پروردیم و سالها عمرت در نزد ما گذشت؟'),
(2951, 26, 19, 'و آن فعل زشت (قتل نفس) از تو سر زد و (به خدایی ما) کافر بودی؟'),
(2952, 26, 20, 'موسی گفت: آری من چنین فعلی را مرتکب شدم (ولی نه بر عمد بلکه) در آن هنگام من (برای نجات سبطیان از ظلم قبطیان) به وادی حیرت سرگردان و از گمشدگان (دیار شما) بودم (و کاری به قصد نجات مظلومی کردم به قتل خطایی منتهی شد).'),
(2953, 26, 21, 'و آن گاه از ترس شما گریختم تا آنکه خدای من مرا علم و حکمت عطا فرمود و از پیغمبران خود قرار داد.'),
(2954, 26, 22, 'و (بازگو) این که طایفه بنی اسرائیل را بنده خود کرده‌ای این هم نعمتی است که منّت آن بر من می‌نهی؟'),
(2955, 26, 23, 'باز فرعون به موسی گفت: ربّ العالمین چیست؟'),
(2956, 26, 24, 'موسی جواب داد: خدای آسمانها و زمین است و آنچه ما بین آنهاست، اگر به یقین باور دارید.'),
(2957, 26, 25, 'فرعون روی به درباریانش کرد و گفت: آیا نمی‌شنوید (که این مرد چه دعوی بی‌دلیل می‌کند).'),
(2958, 26, 26, 'موسی گفت: همان خدایی است که شما و پدران پیشین شما را بیافرید.'),
(2959, 26, 27, 'باز فرعون گفت: این رسولی که به سوی شما به رسالت فرستاده شده سخت دیوانه است.'),
(2960, 26, 28, 'باز موسی گفت: همان آفریننده مشرق و مغرب (و روز و شب) است و هر چه بین اینها موجود است، اگر شما (در قدرت حق) تعقل کنید.'),
(2961, 26, 29, 'باز فرعون گفت: اگر غیر من خدایی را بپرستی البته تو را به زندان خواهم کشید.'),
(2962, 26, 30, 'موسی باز پاسخ داد: اگر هم حجت و معجزی (بر صدق دعوی خود) بر تو آورده باشم (بازم به زندان کشی).'),
(2963, 26, 31, 'فرعون گفت: آن معجزه را بیار اگر راست می‌گویی؟'),
(2964, 26, 32, 'در آن موقع موسی عصای خود بیفکند که ناگاه اژدهایی عظیم پدیدار گشت.'),
(2965, 26, 33, 'و نیز دست خود (از گریبان) بیرون آورد که ناگاه سپید و رخشان به چشم بینندگان آشکار گردید.'),
(2966, 26, 34, 'فرعون رو به درباریانش کرد و گفت: این مرد ساحری بسیار ماهر و داناست.'),
(2967, 26, 35, 'که می‌خواهد بدین سحر و شعبده‌ها شما (مردم مصر) را از کشور خود آواره کند، حال شما چه می‌فرمایید؟'),
(2968, 26, 36, 'آنها به فرعون گفتند: او و برادرش را زمانی باز دار و افرادی را برای جمع کردن مردم در شهرها بفرست.'),
(2969, 26, 37, 'تا ساحران ماهر زبر دست را (برای دفاع او) نزدت حاضر آورند.'),
(2970, 26, 38, 'آن گاه ساحران را بر حسب وعده به روز معیّن حاضر ساختند.'),
(2971, 26, 39, 'و مردم شهر را گفتند: چه بهتر که همه در آن روز جمع باشید (تا واقعه را مشاهده کنید).'),
(2972, 26, 40, 'باشد که ما هم اگر ساحران غالب شدند از آنان پیروی کنیم.'),
(2973, 26, 41, 'و چون ساحران حضور فرعون آمدند با وی گفتند: آیا اگر (بر موسی) غالب آییم اجری خواهیم داشت؟'),
(2974, 26, 42, 'فرعون گفت: آری البته علاوه بر اجر خدمت از مقربان (درگاهم) نیز خواهید شد.'),
(2975, 26, 43, '(چون ساحران به معارضه حاضر شدند) موسی به آنها گفت: اینک شما بساط سحر خود هر چه می‌خواهید به کار اندازید.'),
(2976, 26, 44, 'ساحران هم رسن و طنابها و چوبهای سحر خود را بیفکندند و به عزت فرعون قسم یاد کردند که (بر موسی) البته غالب خواهیم شد.'),
(2977, 26, 45, 'در آن حال موسی عصای خود را بیفکند که ناگاه (اژدهایی عظیم شد و) همه وسایل سحر انگیزی ساحران را به کام فرو می‌برد.'),
(2978, 26, 46, 'ساحران که این معجزه بدیدند (و قطعا دانستند سحر نیست، پیش موسی) به سجده افتادند.'),
(2979, 26, 47, 'گفتند: ما به خدای عالمیان ایمان آوردیم.'),
(2980, 26, 48, 'پروردگار موسی و هارون.'),
(2981, 26, 49, 'فرعون به ساحران گفت: چرا بی‌اجازه من ایمان به موسی آوردید؟ همانا معلوم است که این استاد بزرگ شماست که شما را ساحری آموخته! پس به زودی کیفر خود را خواهید دید، دست و پای شما را به اختلاف (دو طرف چپ و راست) قطع می‌کنم آن گاه همه را به دار می‌کشم.'),
(2982, 26, 50, 'ساحران گفتند: (از این دار و قتل به ما) هیچ زیانی نخواهد رسید چون (بعد از مرگ) به سوی خدای خود باز می‌گردیم.'),
(2983, 26, 51, 'ما از خدا امید آن داریم که به لطف خویش از گناهان ما درگذرد چون اول ما به او ایمان آوردیم.'),
(2984, 26, 52, 'و ما به موسی وحی کردیم که بندگان مرا شبانه (از شهر مصر) بیرون بر که شما را (فرعونیان) تعقیب خواهند کرد.'),
(2985, 26, 53, 'آن گاه فرعون رسول برای جمع آوری لشکر به شهرها فرستاد.'),
(2986, 26, 54, '(و برای تبلیغات به آنها می‌گفتند که) همانا طایفه بنی اسرائیل عده قلیلی هستند.'),
(2987, 26, 55, 'که ما را به خشم آورده‌اند.'),
(2988, 26, 56, 'و ما لشکری همه نیرومند و مسلح به اسلحه کاملیم.'),
(2989, 26, 57, 'پس (با وجود این همه دعوی‌ها) ما آنها را از باغهای مصفّا با نهرهای آب روان بیرون کردیم.'),
(2990, 26, 58, 'و نیز از گنج‌ها و ثروتها و مقامات عالیه ریاست بیرون آوردیم (و به دریای هلاک افکندیم).'),
(2991, 26, 59, 'آری این چنین کردیم و (قوم ضعیف) بنی اسرائیل را وارث آن شهر و دیارها و ثروت و مقامها گردانیدیم.'),
(2992, 26, 60, 'پس صبحگاه فرعونیان موسی و بنی اسرائیل را تعقیب کردند.'),
(2993, 26, 61, 'چون دو لشکر روبرو شدند اصحاب موسی گفتند: اینک به دست فرعونیان خواهیم افتاد.'),
(2994, 26, 62, 'موسی گفت: هرگز چنین نیست، که خدا با من است و مرا به یقین راهنمایی خواهد کرد.'),
(2995, 26, 63, 'پس ما به موسی وحی کردیم که عصای خود را به دریا (ی نیل) زن، چون زد دریا شکافت و آب هر قطعه دریا مانند کوهی بزرگ بر روی هم قرار گرفت.'),
(2996, 26, 64, 'و دیگران (یعنی فرعونیان) را (از پی بنی اسرائیل) به نزدیک دریا آوردیم.'),
(2997, 26, 65, 'و موسی و کلیه همراهانش را به ساحل سلامت رسانیدیم.'),
(2998, 26, 66, 'آن گاه قوم دیگر همه را به دریا غرق کردیم.'),
(2999, 26, 67, 'همانا در این هلاک فرعونیان آیت بزرگی (برای عبرت و موعظه مردم) بود لیکن اکثر خلق ایمان نیاوردند.'),
(3000, 26, 68, 'و همانا خدای تو خدای بسیار مقتدر و مهربان است.'),
(3001, 26, 69, 'و (ای رسول) حکایت ابراهیم را بر امت بیان کن.'),
(3002, 26, 70, 'هنگامی که با پدر (یعنی عمو، زیرا پدران پیغمبران همه موحّدند) و با قومش گفت: شما چه معبودی را می‌پرستید؟'),
(3003, 26, 71, 'جواب دادند که ما بتهایی را می‌پرستیم و ثابت بر پرستش آنها بوده و هستیم.'),
(3004, 26, 72, 'ابراهیم گفت: آیا هر گاه این بتهای جماد را بخوانید سخن شما را می‌شنوند؟'),
(3005, 26, 73, 'یا به حال شما هیچ سود و زیانی توانند داشت؟'),
(3006, 26, 74, 'گفتند: (نه) بلکه ما پدران خود را بر پرستش این بتان یافته‌ایم (و هر چند جماد بی‌اثری باشند می‌پرستیم).'),
(3007, 26, 75, 'ابراهیم باز به آنها گفت: آیا می‌دانید که این بتهایی که شما مردم اینک می‌پرستید،'),
(3008, 26, 76, 'و پدران شما از قدیم می‌پرستیدند،'),
(3009, 26, 77, 'آنها مرا دشمن‌اند جز خدای یکتای عالمیان؟'),
(3010, 26, 78, 'همان خدایی که مرا بیافرید و به راه راستم هدایت می‌فرماید.'),
(3011, 26, 79, 'و همان خدایی که مرا غذا می‌دهد و سیراب می‌گرداند.'),
(3012, 26, 80, 'و چون بیمار شوم مرا شفا می‌دهد.'),
(3013, 26, 81, 'و همان خدایی که مرا (از حیات چند روزه دنیا) می‌میراند و سپس (به حیات ابدی آخرت) زنده می‌گرداند.'),
(3014, 26, 82, 'و همان خدایی که چشم امید دارم که روز جزا گناهم را بیامرزد.'),
(3015, 26, 83, 'بارالها، مرا حکمی ببخش (بر این مشرکان فرمانروایی ده) و به (رسل و) بندگان صالح خود ملحق ساز.'),
(3016, 26, 84, 'و نامم بر زبان اقوام آتیه نیکو، و سخنم دلپذیر گردان.'),
(3017, 26, 85, 'و مرا از وارثان بهشت پر نعمت قرار ده.'),
(3018, 26, 86, 'و از پدرم (یعنی عمویم) درگذر، که وی سخت از گمراهان است.'),
(3019, 26, 87, 'و روزی که خلق را از قبرها برانگیزند در آن روز مرا رسوا (و هلاک) مگردان.'),
(3020, 26, 88, 'آن روزی که مال و فرزندان (هیچ به حال انسان) سود نبخشند.'),
(3021, 26, 89, 'و تنها آن کس سود برد که با دل با اخلاص پاک (از شرک و ریب و ریا) به درگاه خدا آید.'),
(3022, 26, 90, 'و بهشت را به اهل تقوا نزدیک سازند.'),
(3023, 26, 91, 'و دوزخ را بر گمراهان پدیدار گردانند.'),
(3024, 26, 92, 'و به کافران گفته شود: بتهایی که پرستش می‌کردید به کجا شدند؟'),
(3025, 26, 93, 'آن بتهایی که به جای خدا پرستش می‌کردید آیا می‌توانند به شما اینک یاری کرده یا از جانب خود دفاع کنند؟'),
(3026, 26, 94, 'در آن حال کافران و معبودان باطلشان هم به رو در آتش دوزخ افتند.'),
(3027, 26, 95, 'و نیز تمام سپاه شیطان (به جهنم در آیند).'),
(3028, 26, 96, 'و در دوزخ به مجادله و خصومت با یکدیگر گویند:'),
(3029, 26, 97, 'به خدا قسم که ما در گمراهی بسیار آشکاری بودیم.'),
(3030, 26, 98, 'که شما (بتها) را مانند خدای عالمیان پرستش می‌کردیم.'),
(3031, 26, 99, 'و ما را گمراه نکردند جز قوم تبهکار.'),
(3032, 26, 100, 'پس (در این روز سخت) نه شفیعی داریم،'),
(3033, 26, 101, 'و نه یک دوست صمیمی (که از ما حمایتی کند).'),
(3034, 26, 102, 'ای کاش که بار دیگر (به دنیا) باز می‌گشتیم تا (به خدای یگانه) ایمان می‌آوردیم.'),
(3035, 26, 103, 'همانا در این (ندامت گمراهان) آیت عبرتی (برای دیگران) بود و لیک اکثر مردم باز ایمان نیاوردند.'),
(3036, 26, 104, 'و همانا خدای تو خدای بسیار مقتدر و مهربان است.'),
(3037, 26, 105, 'قوم نوح هم پیغمبران خدا را تکذیب کردند.'),
(3038, 26, 106, 'هنگامی که مهربان برادرشان نوح آنها را گفت: آیا خدا ترس و متّقی نمی‌شوید؟'),
(3039, 26, 107, 'من برای شما بسیار رسولی امین (و خیر خواهی مشفق) هستم.'),
(3040, 26, 108, 'پس از خدا بترسید و راه طاعت من پیش گیرید.'),
(3041, 26, 109, 'و من اجری از شما برای رسالت نمی‌خواهم و چشم پاداش جز به خدای عالم ندارم.'),
(3042, 26, 110, 'پس از خدا بترسید و راه طاعت من پیش گیرید.'),
(3043, 26, 111, 'قوم نوح (از کبر و خود پرستی) پاسخ دادند که ما چگونه به تو ایمان آوریم در صورتی که پیروانت معدودی مردم خوار و فرومایه‌اند؟'),
(3044, 26, 112, 'نوح گفت: مرا چه کار که افعال و احوال پیروانم را بدانم (که از طبقه عالی یا دانی‌اند)؟'),
(3045, 26, 113, 'اگر شعور و معرفتی دارید بدانید که حساب کار آنها بر کسی جز خدای من نخواهد بود.'),
(3046, 26, 114, 'و من هرگز مؤمنان به حق را (هر چند فقیر باشند) از خود نرانم.'),
(3047, 26, 115, 'من جز آنکه مردم را اندرز کنم و از خدا بترسانم وظیفه‌ای ندارم.'),
(3048, 26, 116, 'باز قوم نوح گفتند: ای نوح، اگر ترک این سخنان نگویی سخت سنگسار می‌شوی.'),
(3049, 26, 117, 'نوح گفت: پروردگارا، قوم من سخت مرا تکذیب کردند.'),
(3050, 26, 118, 'پس بین من و آنها حکم فرما و به ما گشایشی عطا کن، و من و مؤمنانی که با من همراهند از شر قوم نجات ده.'),
(3051, 26, 119, 'ما هم او را با همه آنان که در آن کشتی انبوه در آمدند به ساحل سلامت رساندیم.'),
(3052, 26, 120, 'آن‌گاه باقی آن قوم سرکش را پس از نجات نوح و مؤمنان به دریا غرق کردیم.'),
(3053, 26, 121, 'همانا در این (نجات مؤمنان و هلاک کافران قوم نوح) آیت عبرتی است، و اکثرشان ایمان نیاوردند.'),
(3054, 26, 122, 'و همانا خدای تو خدای بسیار مقتدر و مهربان است.'),
(3055, 26, 123, 'قوم عاد نیز رسولان خدا را تکذیب کردند.'),
(3056, 26, 124, 'هنگامی که مهربان برادرشان هود به آنها گفت: آیا متقی و خدا ترس نمی‌شوید؟'),
(3057, 26, 125, 'من برای شما پیغمبری بسیار (خیر خواه و) امینم.'),
(3058, 26, 126, 'پس از خدا بترسید و راه اطاعت من پیش گیرید.'),
(3059, 26, 127, 'و من از شما اجری برای رسالت نمی‌خواهم و چشم پاداش جز به خدای عالم ندارم.'),
(3060, 26, 128, 'آیا بنا می‌کنید به هر سرزمین مرتفع عمارت و کاخ برای آنکه به بازی عالم سرگرم شوید (و از یاد خدا غافل مانید).'),
(3061, 26, 129, 'و عمارتهای محکم بنا می‌کنید به امید آنکه در آن عمارات عمر ابد کنید؟'),
(3062, 26, 130, 'و چون (به ظلم و بیداد خلق) دست گشایید کمال قساوت و خشم کار بندید؟'),
(3063, 26, 131, 'پس از خدا بترسید و مرا اطاعت کنید.'),
(3064, 26, 132, 'و بترسید از آن خدایی که شما را به آنچه خود می‌دانید مدد و قوت بخشید.'),
(3065, 26, 133, 'مدد بخشید شما را به خلق چهار پایان و فرزندان.'),
(3066, 26, 134, 'و نیز مدد بخشید شما را به ایجاد باغها و چشمه‌های آب.'),
(3067, 26, 135, 'همانا من از عذاب روز بزرگ قیامت بر شما می‌ترسم.'),
(3068, 26, 136, 'گفتند: تو این همه وعظ و نصیحت کنی یا هیچ نکنی به حال ما یکسان است.'),
(3069, 26, 137, 'این (مسأله حیات و موت کار طبیعت و خوی جهان و) سرنوشت همه پیشینیان است.'),
(3070, 26, 138, 'و ما هرگز (پس از مرگ دیگر زنده نمی‌شویم و هرگز قیامت و ثواب و) عقابی نخواهیم داشت.'),
(3071, 26, 139, 'الغرض، رسول حق را تکذیب کردند و ما هم آنان را هلاک گردانیدیم، همانا در این (هلاک قوم عاد) آیت عبرتی است، و بیشتر آنها ایمان نیاوردند.'),
(3072, 26, 140, 'و همانا خدای تو بسیار مقتدر و مهربان است.'),
(3073, 26, 141, 'قوم ثمود نیز رسولان خدا را تکذیب کردند.'),
(3074, 26, 142, 'هنگامی که مهربان برادرشان صالح به آنها گفت: آیا متّقی و خدا ترس نمی‌شوید؟'),
(3075, 26, 143, 'من برای شما پیغمبری بسیار امین (و خیر خواه) هستم.'),
(3076, 26, 144, 'پس از خدا بترسید و راه طاعت من پیش گیرید.'),
(3077, 26, 145, 'و من اجری از شما برای رسالت نمی‌خواهم و چشم پاداش جز به خدای عالم ندارم.'),
(3078, 26, 146, 'آیا (تصور می‌کنید که تا ابد) ایمن از مرگ در این ناز و نعمت دنیا خواهید ماند؟'),
(3079, 26, 147, '(گمان دارید) در این باغهای باصفا و چشمه‌های آب گوارا،'),
(3080, 26, 148, 'و مزارع و کشتزار و نخل و آن شکوفه‌های لطیف و زیبایش (همیشه زنده‌اید).'),
(3081, 26, 149, 'و خیال می‌کنید دائم در این عماراتی که در کوهستان (و ییلاقات) با کمال دقت بنا کرده‌اید به آسایش تعیّش می‌کنید؟'),
(3082, 26, 150, 'پس از خدا بترسید و راه اطاعت من پیش گیرید.'),
(3083, 26, 151, 'و از رفتار رؤسای مسرف و ستمگر پیروی نکنید.'),
(3084, 26, 152, 'که آن مردم در زمین همه گونه فساد می‌کنند و هیچ گونه به اصلاح (حال خلق) نمی‌پردازند.'),
(3085, 26, 153, 'قوم صالح گفتند: به یقین تو را سحر کرده‌اند (که دعوی نبوت می‌کنی).'),
(3086, 26, 154, '(و گرنه) تو هم مثل ما بشری بیش نیستی، اگر راست می‌گویی معجزی بیاور.'),
(3087, 26, 155, 'صالح گفت: این ناقه (معجزه من) است، آب نهر را روزی ناقه بنوشد و روزی شما بنوشید.'),
(3088, 26, 156, 'و هرگز سوء قصدی بر ناقه مکنید که به عذاب روزی بزرگ و سخت گرفتار خواهید شد.'),
(3089, 26, 157, 'قوم صالح ناقه را پی کردند، سپس از کار خود پشیمان شدند.'),
(3090, 26, 158, 'آن گاه عذاب موعود آنان را درگرفت (و هلاک گردیدند). همانا در هلاک این قوم آیت عبرتی (برای دیگران) بود، و اکثر آنها ایمان نیاوردند.'),
(3091, 26, 159, 'و همانا خدای تو بسیار مقتدر و مهربان است.'),
(3092, 26, 160, 'قوم لوط نیز پیغمبران خدا را تکذیب کردند.'),
(3093, 26, 161, 'هنگامی که مهربان برادر آنها لوط به آنان گفت: آیا خدا ترس و پرهیزکار نمی‌شوید؟'),
(3094, 26, 162, 'من برای شما پیغمبری بسیار امین (و خیر خواه) هستم.'),
(3095, 26, 163, 'از خدا بترسید و راه طاعت من پیش گیرید.'),
(3096, 26, 164, 'و من از شما اجری برای رسالت خود نمی‌خواهم و چشم پاداش جز به خدای عالم ندارم.'),
(3097, 26, 165, 'آیا از میان خلایق عمل زشت منکر را با مردان انجام می‌دهید؟'),
(3098, 26, 166, 'و زنان را که خدا همسر شما آفریده رها می‌کنید؟ آری شما بسیار مردم متعدی نابکاری هستید.'),
(3099, 26, 167, 'قوم در جواب گفتند: ای لوط، اگر از این پس دست از این نهی و منع بر نداری از شهر رانده خواهی شد.'),
(3100, 26, 168, 'لوط باز گفت: من خود دشمن این کار زشت شما هستم.'),
(3101, 26, 169, 'بار الها، من و اهل بیتم را از (عقاب) این عمل زشت قوم نجات بخش.'),
(3102, 26, 170, 'ما هم او را با همه اهل بیتش نجات دادیم.'),
(3103, 26, 171, 'جز پیرزنی که (زن لوط بود و) در اهل عذاب باقی ماند.'),
(3104, 26, 172, 'آن گاه (جز اهل بیت لوط) دیگران را به خاک هلاک نشانیدیم.'),
(3105, 26, 173, 'و آنها را به سنگباران عذاب که باران بسیار سختی بر سر بیم داده شدگان (بد کار) است هلاک ساختیم.'),
(3106, 26, 174, 'همانا در این هلاک قوم لوط نیز آیت عبرتی (برای دیگران) بود، و اکثر آنها ایمان نیاوردند.'),
(3107, 26, 175, 'و همانا خدای تو بسیار مقتدر و مهربان است.'),
(3108, 26, 176, 'اصحاب ایکه (یعنی امت شعیب ع) هم پیغمبران خدا را تکذیب کردند.'),
(3109, 26, 177, 'هنگامی که (رسولشان) شعیب به آنها گفت: آیا خداترس و پرهیزکار نمی‌شوید؟'),
(3110, 26, 178, 'من برای شما رسولی بسیار امین (و خیر خواه) هستم.'),
(3111, 26, 179, 'از خدا بترسید و راه طاعت من پیش گیرید.'),
(3112, 26, 180, 'و من از شما هرگز اجر رسالت نمی‌خواهم و جز به خدای عالم به کسی چشم پاداش ندارم.'),
(3113, 26, 181, 'پیمانه را تمام بدهید و از کم فروشان مباشید.'),
(3114, 26, 182, 'و (اجناس را) با ترازوی درست بسنجید.'),
(3115, 26, 183, 'و آنچه به مردم می‌فروشید (تمام بدهید و) از وزن و پیمان کم نگذارید، و در زمین به ظلم و فساد کاری برنخیزید.'),
(3116, 26, 184, 'و از خدایی که شما و کفر پیشگان پیشین را آفریده بترسید (و خلاف امر او مکنید).'),
(3117, 26, 185, 'قوم شعیب در پاسخ او گفتند: تو را بی‌شک به سحر و شعبده مفتون کرده‌اند.'),
(3118, 26, 186, 'و تو جز آنکه بشری هستی مانند ما هیچ گونه مزیّتی بر دیگران نداری. و ما تو را (در دعوت نبوت) دروغگو می‌پنداریم.'),
(3119, 26, 187, 'اگر راست می‌گویی (که پیغمبر خدایی) قطعه‌ای از آسمان را بر سر ما فرود آور (تا تصدیق تو کنیم).'),
(3120, 26, 188, 'شعیب گفت: خدای من بهتر بر (صدق دعوی من و کذب قول و زشتی) افعال شما آگاه است.'),
(3121, 26, 189, 'باز او را تکذیب کردند و به عذاب سخت روز سایه‌بان (یعنی روزی که مردم از شدت گرما به سایه‌بانی پناه می‌بردند و باز از گرمی هلاک می‌شدند) گرفتار شدند که عذاب آن روز بسیار بزرگ و سخت بود.'),
(3122, 26, 190, 'همانا در این هلاک قوم شعیب نیز آیت عبرتی (برای دیگران) بود، و اکثر آنها ایمان نیاوردند.'),
(3123, 26, 191, 'و همانا خدای تو بسیار مقتدر و مهربان است.'),
(3124, 26, 192, 'و این قرآن به حقیقت از جانب خدای عالمیان نازل شده.'),
(3125, 26, 193, '(جبرئیل) روح الامین (فرشته بزرگ خدا) آن را نازل گردانیده.'),
(3126, 26, 194, 'و آن را بر قلب تو فرود آورده تا از عقاب خدا بترسانی.'),
(3127, 26, 195, 'به زبان عربی فصیح.'),
(3128, 26, 196, 'و (ذکر عظمت) این قرآن در کتب انبیاء پیشین مسطور است.'),
(3129, 26, 197, 'آیا این خود آیت و برهان روشنی (بر کافران) نیست که علماء بنی اسرائیل (از کتب انبیاء سلف) بر این قرآن آگاهند؟'),
(3130, 26, 198, 'و اگر ما این کتاب (عربی) را بر بعض مردم عجم نازل می‌گردانیدیم.'),
(3131, 26, 199, 'و آن رسول آن را (به زبان تازی) بر عجم‌ها قرائت می‌کرد آنان ایمان نمی‌آوردند (به این عذر که قرآن چون به زبان ما نیست ما فهم آن نکرده و اعجاز آن را درک نمی‌کنیم، اما شما قوم عرب با چه عذر ایمان نمی‌آورید؟)'),
(3132, 26, 200, 'ما این قرآن را این چنین در دل تبهکاران گذراندیم (و بر آنها به این کتاب اتمام حجت کردیم).'),
(3133, 26, 201, 'اما به این قرآن ایمان نمی‌آورند تا عذاب دردناک را مشاهده کنند.'),
(3134, 26, 202, 'که به ناگاه آن عذاب که سخت از آن غافلند به آنها فرا رسد.'),
(3135, 26, 203, 'و آن کافران گویند: آیا بر ما مهلتی منظور می‌شود (تا زمانی از عذاب بیاساییم؟'),
(3136, 26, 204, 'آیا (اکنون که هنگام عذابشان نیست از تمسخر) عذاب و انتقام ما را به تعجیل می‌طلبند؟'),
(3137, 26, 205, 'چه خواهی دید اگر ما چند سالی آنها را در دنیا متنعّم سازیم.'),
(3138, 26, 206, 'سپس به عذابی که بر آنان وعده شده یکسر هلاک شوند.'),
(3139, 26, 207, 'آیا بهره‌ای که از مال دنیا داشتند هیچ آنها را از عذاب خواهد رهانید؟'),
(3140, 26, 208, 'و ما اهل هیچ دیاری را تا رسولانی به هدایت و اتمام حجت بر آنها نفرستادیم هلاک نکردیم.'),
(3141, 26, 209, 'این (هلاک بدان) پند و موعظه است (برای خوبان) و ما هرگز به کسی ستم نکردیم.'),
(3142, 26, 210, 'و این قرآن را شیاطین فرود نیاوردند (بلکه جبرئیل امین، فرشته خدا نازل ساخته).'),
(3143, 26, 211, 'نه هرگز این گونه سخنان از شیاطین شایسته است و نه قدرت (بر نزول آن) دارند.'),
(3144, 26, 212, 'که البته آنها از استماع وحی الهی معزولند.'),
(3145, 26, 213, 'پس با خدای یکتا احدی را معبود مخوان و گرنه از اهل عذاب خواهی شد.'),
(3146, 26, 214, 'و (نخست) خویشان نزدیکت را (از خدا) بترسان.'),
(3147, 26, 215, 'و پر و بال مرحمت بر تمام پیروان با ایمانت به تواضع بگستران.'),
(3148, 26, 216, 'و هرگاه قوم فرمانت را مخالفت کردند به آنها بگو: من خود از کردار (بد) شما بیزارم.'),
(3149, 26, 217, 'و توکل بر آن خدای مقتدر مهربان کن.'),
(3150, 26, 218, 'آن خدایی که چون (از شوقش به نماز) برخیزی تو را می‌نگرد.'),
(3151, 26, 219, 'و به انتقال تو در اهل سجود (و به دوران تحوّلت از اصلاب شامخه به ارحام مطهره) آگاه است.'),
(3152, 26, 220, 'که او خدای شنوا و دانا (به گفتار و کردار خلق) است.'),
(3153, 26, 221, '(بگو به کافران که) آیا می‌خواهید من شما را آگاه سازم که شیاطین بر چه کسان نازل می‌شوند؟'),
(3154, 26, 222, 'شیاطین بر هر شخص بسیار دروغگوی بدکار نازل می‌شوند.'),
(3155, 26, 223, 'گوش فرا می‌دهند (تا حرف مؤمنان و قوای قدسی را ربوده و به اتباع خود برسانند) و اکثرشان دروغ می‌گویند (و اندک راست آنها هم برای شبهه کاری و فساد و فتنه انگیزی است).'),
(3156, 26, 224, 'و شاعران (یاوه سرای کفار مانند عالمان بی‌عمل و مدعیان باطل) را مردم جاهل گمراه پیروی کنند.'),
(3157, 26, 225, 'آیا ننگری که آنها خود به هر وادی حیرت سرگشته‌اند؟'),
(3158, 26, 226, 'و آنها بسیار سخنان می‌گویند که یکی را عمل نمی‌کنند.'),
(3159, 26, 227, 'مگر آن شاعران که اهل ایمان و نیکوکار بوده و یاد خدا بسیار کردند و برای انتقام از (هجو و) ستمی که در حق آنها (و سایر مؤمنان) شده (به نظم سخن و طبع شعر) به انتقام و دفاع برخاستند (و به شمشیر زبان با دشمنان دین جهاد کردند، آنان را مؤمنان پیروی خواهند کرد) و آنان که ظلم و ستم کردند به زودی خواهند دانست که به چه کیفر گاهی و دوزخ انتقامی بازگشت می‌کنند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طس (شاید اشاره به سوره است یا نام خدا یا رسول است که به آن قسم یاد شده) این آیات قرآن (بزرگ) و کتاب روشن خداست.'),
(3161, 27, 2, 'که هدایت و بشارت برای اهل ایمان است.'),
(3162, 27, 3, 'آنان که نماز به پا می‌دارند و زکات می‌دهند و کاملا به عالم آخرت یقین دارند.'),
(3163, 27, 4, 'همانا آنان که ایمان به عالم آخرت نمی‌آورند (پس از اتمام حجت) ما اعمالشان را در نظرشان جلوه دهیم و آنها به کلی گمراه و سرگشته شوند.'),
(3164, 27, 5, 'آنها را هم (در دنیا) سخت‌ترین عذاب است و هم در آخرت زیانکارترین خلقند.'),
(3165, 27, 6, 'و تو (ای رسول ما) آیات قرآن (عظیم) از جانب خدایی حکیم و دانا (به وحی) بر تو القا می‌شود.'),
(3166, 27, 7, '(به یاد آر) هنگامی که موسی (وقت بیچارگی و درد حمل زن در آن بیابان سرد و ظلمت نوری دید و) به اهل بیتش گفت: مرا آتشی به نظر آمد می‌روم تا از آن به زودی خبری برایتان بیاورم یا شاید برای گرم شدن شما شعله‌ای برگیرم.'),
(3167, 27, 8, 'چون موسی بدان آتش نزدیک شد او را ندا کردند که آن کس را که در (اشتیاق) این آتش است یا به گردش (در طلب) است (مقدمش به لطف حق) مبارک باد، و منزه است خدا که آفریننده عوالم بی‌حد و نهایت است.'),
(3168, 27, 9, '(از آن آتش شجر طور ندا آمد) ای موسی، همانا منم خدای مقتدر دانای درستکار.'),
(3169, 27, 10, 'و تو عصای خود بیفکن، چون موسی (عصا افکند و ناگاه) بر آن نگریست که به جنبش و هیجان در آمد چنانکه اژدهایی مهیب گردید، رو به فرار نهاد و دیگر واپس نگردید، (در آن حال بدو خطاب شد) ای موسی مترس که پیغمبران در حضور من هرگز نمی‌ترسند.'),
(3170, 27, 11, '(ستمکاران عالم باید بترسند) مگر کسی که ستم و کار بد کند آن‌گاه به زودی (توبه کند و) نیکویی را جایگزین بدی کند (تا خدایش ببخشد) که همانا خدا بسیار آمرزنده و مهربان است.'),
(3171, 27, 12, 'و دست در گریبان خود کن تا چون بیرون آید نه از مرض (برص، بلکه از نظر لطف خدا) سپید و روشن (چون ماه تابان) گردد، آن گاه با نه معجز الهی (یعنی عصا و ید بیضا و ملخ و قمّل و ضفدع و خون و شکافتن دریا و کوه و طوفان) به سوی فرعون و قومش که گروهی فاسق و نابکارند به رسالت روانه شو.'),
(3172, 27, 13, 'اما چون آیات و معجزات ما به طور روشن به آنها ارائه شد گفتند که این سحر بودنش روشن و آشکار است.'),
(3173, 27, 14, 'و با آنکه پیش نفس خود به یقین دانستند که آنها معجزه خداست باز از کبر و نخوت و ستمگری انکار آن کردند، پس بنگر تا عاقبت آن مردم فاسد به کجا انجامید (و چگونه هلاک شدند).'),
(3174, 27, 15, 'و همانا ما به داود و سلیمان مقام دانشی بزرگ عطا کردیم و گفتند: ستایش و سپاس خدای را که ما را بر بسیاری از بندگان با ایمانش فضیلت و برتری عطا فرمود.'),
(3175, 27, 16, 'و سلیمان وارث (ملک) داود شد (و مقام سلطنت و خلافت یافت) و گفت: ای مردم، ما را زبان مرغان آموختند و از هرگونه نعمت عطا کردند، این همان فضل و بخشش آشکار است.'),
(3176, 27, 17, 'و سپاهیان سلیمان از گروه جن و انس و مرغان (هر سپاهی تحت فرمان رئیس خود) در رکابش گرد آورده شدند.'),
(3177, 27, 18, 'تا چون به وادی مورچگان رسید موری (یعنی پیشوای موران) گفت: ای موران، همه به خانه‌های خود اندر روید مبادا سلیمان و سپاهیانش ندانسته شما را پایمال کنند.'),
(3178, 27, 19, 'سلیمان از گفتار مور بخندید و گفت: پروردگارا، مرا توفیق شکر نعمت خود که به من و پدر و مادر من عطا فرمودی عنایت فرما و مرا به عمل صالح خالصی که تو بپسندی موفق بدار و مرا به لطف و رحمت خود در صف بندگان خاص شایسته‌ات داخل گردان.'),
(3179, 27, 20, 'و سلیمان (از میان سپاه خود) جویای حال مرغان شد (هدهد را در مجمع مرغان نیافت، به رئیس مرغان) گفت: هدهد کجا شد که به حضور نمی‌بینمش؟ بلکه (بی‌اجازه من) غیبت کرده است؟'),
(3180, 27, 21, '(چنانچه بدون عذر بی رخصت غایب شده) همانا او را به عذابی سخت معذب گردانم یا آنکه سرش از تن جدا کنم یا که (برای غیبتش) دلیلی روشن (و عذری صحیح) بیاورد.'),
(3181, 27, 22, 'پس از اندک مکثی هدهد حاضر شد و گفت: من به چیزی که تو از آن (در جهان) آگاه نشده‌ای خبر یافتم و از ملک سبا تو را خبری راست و مهم آوردم.'),
(3182, 27, 23, 'همانا (در آن ملک) زنی را یافتم که بر مردم آن کشور پادشاهی داشت و به آن زن هر گونه دولت و نعمت (و زینت امور دنیوی) عطا شده بود و (علاوه بر اینها) تخت با عظمتی داشت.'),
(3183, 27, 24, 'آن زن را با تمام رعیتش یافتم که به جای خدا خورشید را می‌پرستیدند و شیطان اعمال زشت آنان را در نظرشان زیبا جلوه داده و آنها را به کلی از راه خدا باز داشته و آنها هم به حق راه یافته نیستند.'),
(3184, 27, 25, 'تا خدای را که (نور او) در آسمان و زمین هر پنهان (در ظلمت عدم) را به عرصه ظهور آورد و بر نهان و آشکار شما آگاه است پرستش نکنند.'),
(3185, 27, 26, 'در صورتی که خدای یکتا که جز او هیچ خدایی نیست پروردگار عرش با عظمت (و ملک بی‌نهایت) است (و تنها سزاوار پرستش اوست).'),
(3186, 27, 27, 'سلیمان هدهد را گفت: باید تحقیق کنیم تا صدق و کذب سخنت را دریابیم.'),
(3187, 27, 28, 'اینک نامه مرا ببر و به سوی آنان بیفکن و از نزد آنها باز شو آن گاه بنگر تا پاسخ چه می‌دهند.'),
(3188, 27, 29, '(بلقیس رو به رجال دربارش کرد و) گفت: ای رجال کشور، نامه بزرگی به من رسیده است.'),
(3189, 27, 30, 'که آن نامه از جانب سلیمان و عنوانش به نام خدای بخشنده مهربان است.'),
(3190, 27, 31, '(و بعد چنین نگاشته) که بر من برتری مجویید و تسلیم امر من شوید.'),
(3191, 27, 32, 'آن‌گاه گفت: ای رجال کشور، شما به کار من رأی دهید که من تاکنون بی‌حضور شما به هیچ کار تصمیم نگرفته‌ام.'),
(3192, 27, 33, 'رجال ملک به او اظهار داشتند که ما دارای نیروی کامل و مردان جنگجوی مقتدری هستیم، لیکن اختیار با شما تا به فکر روشن چه دستور فرمایی.'),
(3193, 27, 34, 'بلقیس گفت: پادشاهان چون به دیاری حمله آرند آن کشور را ویران سازند و عزیزترین اشخاص مملکت را ذلیل‌ترین افراد گیرند و رسم و سیاستشان بر این کار خواهد بود.'),
(3194, 27, 35, 'و (صلاح این است که) حال من هدیه‌ای بر آنان بفرستم تا ببینم فرستادگانم از جانب سلیمان پاسخ چگونه باز می‌آرند.'),
(3195, 27, 36, 'چون فرستاده بلقیس حضور سلیمان رسید (به هدیه او اعتنایی نکرد) گفت: شما خواهید که مرا به مال دنیا مدد کنید؟ آنچه خدا به من (از ملک و مال بی‌شمار) عطا فرموده بسیار بهتر از این مختصر هدیه شماست که به شما داده، بلکه شما مردم دنیا خود بدین هدایا شاد می‌شوید.'),
(3196, 27, 37, 'ای فرستاده بلقیسیان (با هدایا) به سوی آنان باز شو که ما با لشکری بی‌شمار که هیچ با آن مقاومت نتوانند کرد بر آنها خواهیم آمد و آنها را با ذلت و خواری از آن ملک بیرون می‌کنیم (مگر آنکه به دین توحید و خداپرستی بگروند).'),
(3197, 27, 38, 'آن گاه سلیمان گفت: ای بزرگان دربار، کدام یک از شما تخت بلقیس را پیش از آنکه تسلیم امر من شوند خواهد آورد؟ (تا چون اعجاز مرا مشاهده کند از روی ایمان تسلیم شود).'),
(3198, 27, 39, '(از آن میان) عفریتی از جن گفت: من پیش از آنکه تو از جایگاه (قضاوت) خود برخیزی آن را به حضورت آرم و من بر آوردن تخت او قادر و امینم.'),
(3199, 27, 40, 'آن کس که به علمی از کتاب الهی دانا بود (یعنی آصف بن برخیا یا خضر که دارای اسم اعظم و علم غیب بود) گفت که من پیش از آنکه چشم بر هم زنی تخت را بدین جا آرم (و همان دم حاضر نمود)، چون سلیمان سریر را نزد خود مشاهده کرد گفت: این توانایی از فضل خدای من است تا مرا بیازماید که (نعمتش را) شکر می‌گویم یا کفران می‌کنم، و هر که شکر کند شکر به نفع خویش کرده و هر که کفران کند همانا خدا (از شکر خلق) بی‌نیاز و (بر کافر هم به لطف عمیم) کریم است.'),
(3200, 27, 41, 'آن گاه سلیمان گفت: تخت او را (به تغییر شکل و هیأت) بر او ناشناس گردانید تا بنگریم که وی سریر خود را خواهد شناخت یا نه.'),
(3201, 27, 42, 'هنگامی که بلقیس آمد از او پرسیدند که عرش تو چنین است؟ وی گفت: گویا همین است و ما از این پیش بدین امور دانا و تسلیم (امر خدا) بودیم.'),
(3202, 27, 43, 'و او را پرستش غیر خدا (مانند آفتاب و سایر اجرام علوی، از خدا پرستی) باز داشته و از فرقه کافران مشرک به شمار بود.'),
(3203, 27, 44, 'آن‌گاه او را گفتند که در ساحت این قصر داخل شو، وی چون کوشک را مشاهده کرد (از فرط صفا و تلألؤ) پنداشت که لجّه آبی است و جامه از ساقهای پا برگرفت، سلیمان گفت: این قصری است از آبگینه صاف، بلقیس گفت: بار الها، من سخت بر نفس خویش ستم کردم و اینک با (رسول تو) سلیمان تسلیم فرمان یکتا پروردگار عالمیان گردیدم.'),
(3204, 27, 45, 'و همانا ما به قوم ثمود برادرشان صالح را به رسالت فرستادیم که خدای یکتا را پرستید، پس قوم بر دو فرقه شدند (یک فرقه مؤمن و دیگر کافر) و با هم به مخاصمه و جدال می‌پرداختند.'),
(3205, 27, 46, 'صالح گفت: ای قوم، چرا پیش از نکوکاری به بدکاری می‌شتابید؟ چرا (از کردار زشت) استغفار به درگاه خدا نمی‌کنید تا شاید مورد عفو و رحمت واقع شوید؟'),
(3206, 27, 47, 'آن کافران گفتند: ما به وجود تو و پیروانت فال بد می‌زنیم (که ظهور تو اسباب رنج و خصومت و عذاب و شومی حال ما شده است). صالح گفت: این فال بد شما نزد خدا (محققا معلوم) است بلکه شما گرفتار امتحان و ابتلا شده‌اید (و خود موجب گرفتاریتان هستید).'),
(3207, 27, 48, 'و در شهرستان قوم صالح نه (نفر از رؤسای) قبیله بودند که دائم در زمین به فتنه و فساد می‌پرداختند و هرگز قدمی به صلاح (خلق) بر نمی‌داشتند.'),
(3208, 27, 49, 'آن رؤسای مفسد (به قوم صالح) گفتند که شما هم قسم شوید که با هم شبیخون زنیم و شبانه صالح را به قتل برسانیم، آن گاه به وارث او همه شهادت خواهیم داد که ما در مکان ارتکاب قتل (او و) اهلش حاضر نبودیم و البته ما راستگوییم.'),
(3209, 27, 50, 'و آن کافران (در قتل صالح) مکر و حیله به کار بردند و ما هم آنها را از جایی که هیچ نمی‌فهمیدند به کیفر مکرشان رسانیدیم.'),
(3210, 27, 51, 'بنگر که پایان مکرشان چه شد؟ عاقبت ما همه آنان را بستگانشان هلاک کردیم.'),
(3211, 27, 52, 'این است خانه‌های بی‌صاحب ایشان که چون ظلم کردند همه ویران شد و در این کار برای دانایان آیت عبرت است.'),
(3212, 27, 53, 'و (از آن قوم) اهل ایمان را که پرهیزکار و خدا ترس بودند همه را (از عذاب) نجات دادیم.'),
(3213, 27, 54, 'و (پیغمبر خود) لوط را فرستادیم وقتی که به قوم خود گفت: آیا شما با آنکه بصیرت (به زشتی عمل خود) دارید باز مرتکب چنین کار منکری می‌شوید؟'),
(3214, 27, 55, 'آیا شما با مردان شهوترانی کرده و زنان را ترک می‌گویید؟ بلکه شما بسیار مردم جهالت پیشه و گمراهی هستید.'),
(3215, 27, 56, 'قوم لوط به او جوابی ندادند جز آنکه (با یکدیگر) گفتند که لوط را با همه اهل بیتش از شهر خود بیرون کنید زیرا که (از این کار) تنزّه و دوری می‌جویند.'),
(3216, 27, 57, 'ما هم لوط را با همه اهل بیتش جز زن (کافر) او که مقدر کردیم که میان اهل عذاب باقی ماند همه را نجات دادیم.'),
(3217, 27, 58, 'و بر سر آنها سنگ‌های هلاک باراندیم و بسیار بد باران هلاکتی است باران آن قوم بدکار.'),
(3218, 27, 59, '(ای رسول) بگو: ستایش مخصوص خداست و سلام خاص بر بندگان برگزیده خدا، آیا خدا بهتر است یا آنچه آنان شریک خدا می‌شمارند؟'),
(3219, 27, 60, 'یا آن خدایی که آسمانها و زمین را خلق کرده و از آسمان برای شما باران فرستاد تا به آن درختان و باغ و بستانها را در کمال سبزی و خرمی رویاندیم که هرگز شما (از پیش خود) قادر بر رویانیدن آن درختان نبودید؟ آیا با وجود خدای یکتا خدایی هست؟ (هرگز خدایی نیست) بلکه این مشرکان (از نادانی) روی (از خدا) می‌گردانند.'),
(3220, 27, 61, 'یا آن کس که زمین را آرامگاه (شما) قرار داد و در آن نهرهای آب جاری کرد و در آن کوهها بر افراشت و میان دو دریا (به قطعه‌ای از زمین) حایل گردانید (یا دو دریای شور و شیرین را بی‌فاصله، منع از اختلاط آب آنها کرد)؟ آیا با وجود خدای یکتا خدایی هست؟ (هرگز نیست) بلکه اکثر مردم (بر این حقیقت) آگاه نیستند.'),
(3221, 27, 62, 'یا آن کس که دعای بیچاره مضطر را به اجابت می‌رساند و رنج و غم آنان را بر طرف می‌سازد و شما (مسلمین) را جانشینان اهل زمین قرار می‌دهد؟ آیا با وجود خدای یکتا خدایی هست؟ (هرگز نیست لیکن) اندکی متذکر (این حقیقت) هستند.'),
(3222, 27, 63, 'یا آن کس که در تاریکیهای برّ و بحر عالم شما را هدایت می‌کند و آن کس که بادها را به مژده باران رحمت خود می‌فرستد؟ آیا با وجود خدای یکتا خدایی هست؟ (هرگز نیست)، خداوند بسی برتر و بالاتر است از آنچه (مشرکان جاهل) شریک وی می‌شمرند.'),
(3223, 27, 64, 'یا آن کس که نخست آفرینش را آغاز می‌کند آن‌گاه همه را (به سوی خود) باز می‌گرداند، و آن کس که از آسمان و زمین به شما روزی می‌دهد؟ آیا با وجود خدای یکتا خدایی هست؟ بگو: اگر راست می‌گویید (که غیر خدا هم کسی در آفرینش مؤثر است) بر دعوی خود برهانی بیاورید.'),
(3224, 27, 65, 'بگو که در همه آسمانها و زمین جز خدا کسی از علم غیب آگاه نیست و اینان هیچ نمی‌دانند که چه هنگام زنده و برانگیخته خواهند شد.'),
(3225, 27, 66, 'بلکه به روز قیامت دانا شوند و تدارک کار کنند (هیهات) بلکه اینان از قیامت هم در شک و ریبند بلکه به کلی از عالم آخرت (بی‌خبر و از مشاهده آن جهان) کورند.'),
(3226, 27, 67, 'و کافران گفتند: آیا ما و پدرانمان چون (مردیم و یکسر) خاک شدیم باز از خاک بیرون آورده شویم؟'),
(3227, 27, 68, 'این خبر به ما و پیش از ما به پدران ما وعده داده شد، لیکن این سخنان چیزی جز افسانه‌های پیشینیان نیست.'),
(3228, 27, 69, 'بگو: در زمین سیر کنید تا بنگرید عاقبت کار بدکاران به کجا کشید (و چگونه همه هلاک شدند).'),
(3229, 27, 70, 'و (ای رسول) تو بر (کفر و بدبختی) این کافران‌اند اندوهناک مشو و از مکر آنان نیز دلتنگ مباش.'),
(3230, 27, 71, 'و کافران می‌گویند: پس این وعده قیامت اگر راست می‌گوید کی خواهد بود؟'),
(3231, 27, 72, 'بگو: بعضی از آن وعده که به وقوعش تعجیل دارید بدین زودی شاید در پی شما آید (که به مرگ الهی یا شمشیر مسلمین همه هلاک شوید و به دوزخ روید).'),
(3232, 27, 73, 'و همانا خدای تو درباره خلق دارای فضل و رحمت بسیار است و لیکن اکثر مردم شکر نعمتش بجا نیاورند.'),
(3233, 27, 74, 'و همانا خدای تو بر آنچه (خلق) در دلها پنهان کنند یا آشکار سازند به همه آگاه است.'),
(3234, 27, 75, 'و هیچ امری در آسمان و زمین پنهان نیست جز آنکه در کتاب (علم الهی) آشکار است.'),
(3235, 27, 76, 'همانا این قرآن بر بنی اسرائیل اکثر معارف و احکامی را که در آن اختلاف می‌کنند کاملا بیان می‌کند (و حکم واقعی تورات را آشکار می‌سازد).'),
(3236, 27, 77, 'و هم این کتاب خدا برای اهل ایمان هدایت و رحمت کامل است.'),
(3237, 27, 78, '(ای رسول ما) خدای تو (در قیامت) میان اختلافات این مردم حکم خواهد کرد که او خدای مقتدر و داناست.'),
(3238, 27, 79, 'پس تو بر خدا توکل کن که تو البته بر حقّی و حقانیّتت بر همه آشکار است.'),
(3239, 27, 80, 'همانا تو نتوانی که مردگان (و مرده دلان کفر) را سخنی بشنوانی و یا کران (باطن) را که از گفتارت روی می‌گردانند به دعوت (به حقیقت) شنوا کنی.'),
(3240, 27, 81, 'و تو هرگز نتوانی که (این) کوران (باطن) را از گمراهیشان هدایت کنی، تنها آنان که به آیات ما ایمان می‌آورند و تسلیم امر ما هستند تو آنها را می‌توانی سخن بشنوانی (و به راه حق هدایت کنی).'),
(3241, 27, 82, 'و هنگامی که وعده عذاب کافران به وقوع پیوندد (و یا زمان انتقام به ظهور قائم فرا رسد) جنبنده‌ای از زمین بیرون آریم که با آنان تکلم کند، که مردم به آیات ما (بعد از این) از روی یقین نمی‌گروند.'),
(3242, 27, 83, 'و (ای رسول، امت را به یاد آر) روزی که (به عرصه قیامت یا رجعت به دنیا در ظهور امام عصر) از هر قومی یک دسته را که تکذیب آیات ما می‌کنند بر می‌انگیزیم و آنها (برای سؤال) باز داشته خواهند شد.'),
(3243, 27, 84, 'تا آن گاه که همه باز آیند خدا به آنها بفرماید که آیا آیات مرا که به آن احاطه علمی نیافتید تکذیب کردید یا آنکه در مقام عمل (با آن آیات) چه می‌کردید؟'),
(3244, 27, 85, 'و فرمان عذاب شدید به کیفر (کفر و) ظلم بر آنها برسد و هیچ سخن دیگر نتوانند گفت.'),
(3245, 27, 86, 'آیا کافران ندیدند که ما (به قدرت کامله خود) شب را (تاریک) برای سکون و آرامش خلق قرار دادیم و روز را روشن (برای کسب و کار)؟ همانا در این وضع شب و روز آیات و نشانه‌هایی (از قدرت الهی) برای اهل ایمان پدیدار است.'),
(3246, 27, 87, 'و (باز به امت یاد آور شو) روزی که در صور (اسرافیل) دمیده شود، آن روز هر که در آسمانها و هر که در زمین است جز آن که خدا خواسته همه ترسان و هراسان باشند و همه منقاد و ذلیل (به محشر) به نزد او آیند.'),
(3247, 27, 88, 'و در آن هنگام کوهها را بنگری و جامد و ساکن تصور کنی در صورتی که مانند ابر (تند سیر) در حرکتند. صنع خداست که هر چیزی را در کمال اتقان و استحکام ساخته، که علم کامل او به افعال همه شما خلایق محیط است.'),
(3248, 27, 89, 'کسانی که (در قیامت) کار نیکو آورند پاداش بهتر از آن یابند و در آن روز از هول و هراس ایمن باشند.'),
(3249, 27, 90, 'و کسانی که بدکار و زشت کردار آیند به رو در آتش جهنم افتند. (و به آنها خطاب شود که) آیا (این عذاب آتش دوزخ) جز آن است که جزای اعمال شماست؟'),
(3250, 27, 91, '(ای رسول بگو) من مأمورم که منحصرا خدای این بلد (مکّه معظّمه) را که بیت الحرامش قرار داده و آن خدا مالک هر چیز عالم است، پرستش کنم و باز مأمورم که از تسلیم شدگان فرمان او باشم.'),
(3251, 27, 92, 'و نیز مأمورم که قرآن را (به حکم وظیفه رسالت بر امت) تلاوت کنم، پس هر کس هدایت یافت به نفع خود هدایت یافته و هر کس گمراه شد بگو که من جز آنکه از پیمبرانی هستم که برای (ارشاد و هدایت و) ترسانیدن خلق آمده‌ام وظیفه دیگر ندارم.'),
(3252, 27, 93, 'و بگو: ستایش مخصوص خداست، به زودی آیات (قدرت) خود را به شما ارائه خواهد داد پس آن را بشناسید، و خدای تو هرگز غافل از کردار شما نیست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طسم (شاید اشاره به اسماء اللّه باشد یا نام سوره است و قسم به آن یاد شده).'),
(3254, 28, 2, 'این است آیات کتاب خدا که روشن کننده (راه حق از باطل و هدایت از گمراهی) است.'),
(3255, 28, 3, 'ما از حکایت موسی و فرعون به حق بر تو بیان می‌کنیم تا اهل ایمان از دانستن آن منتفع شوند.'),
(3256, 28, 4, 'همانا فرعون در زمین (مصر) تکبر و گردنکشی آغاز کرد و میان اهل آن سرزمین تفرقه و اختلاف افکند، طایفه‌ای از آنها (بنی اسرائیل) را سخت ضعیف و ذلیل می‌کرد، پسرانشان را می‌کشت (که مبادا به ظهور موسی قدرت یابند) و زنانشان را زنده می‌گذاشت (که به خدمت پردازند). همانا فرعون مردی بسیار مفسد و بد اندیش بود.'),
(3257, 28, 5, 'و ما اراده داشتیم که بر آن طایفه ضعیف و ذلیل کرده شده در آن سرزمین منّت گذارده و آنها را پیشوایان (خلق) قرار دهیم و وارث (ملک و جاه فرعونیان) گردانیم.'),
(3258, 28, 6, 'و در زمین به آنها قدرت و تمکین بخشیم و به چشم فرعون و هامان و لشکریانشان آنچه را که از آن اندیشناک و ترسان بودند از سوی آنان بنماییم.'),
(3259, 28, 7, 'و به مادر موسی وحی کردیم که طفلک را شیر ده و چون (از آسیب فرعونیان) بر او ترسان شوی به دریایش افکن و دیگر هرگز مترس و محزون مباش که ما او را به تو باز آوریم و هم از پیغمبران مرسلش گردانیم.'),
(3260, 28, 8, 'اهل بیت فرعون (آسیه) موسی را (از دریا) بر گرفت تا در نتیجه دشمن و مایه اندوه آل فرعون شود (و به مشیّت خدا فرعون دشمن بزرگ خود موسی را به دامان بپرورد) که همانا فرعون و هامان و لشکریانشان بسیار نادان و خطا کار بودند.'),
(3261, 28, 9, 'و زن فرعون گفت: این کودک (مایه نشاط دل و) نور دیده من و توست، او را مکشید، باشد که در خدمت ما سودمند افتد و یا او را به فرزندی خود برگیریم، و آنها (از حقیقت حال موسی و تقدیر ازلی حق) بی‌خبر بودند.'),
(3262, 28, 10, 'و مادر موسی دلش (از هر چیز جز یاد طفلش) فارغ شد (و به حدی غم و اندوه قلبش را فرا گرفت که) اگر نه ما دلش را بر جای نگاه داشتیم تا ایمانش بر قرار ماند نزدیک بود راز درونش را آشکار سازد.'),
(3263, 28, 11, 'و (آن گاه مادر موسی) به خواهر او گفت که از پی طفلم رو. خواهر رفت و موسی را از دور دید (و شناخت) ولی آل فرعون بی‌خبر بودند.'),
(3264, 28, 12, 'و ما پیش از آن، شیر هر دایه‌ای را بر او حرام کردیم، و خواهر موسی گفت: آیا مایلید که من شما را بر خانواده‌ای که دایه و پرستار این طفل شوند و در کمال محبت و مهربانی تربیت کنند رهنمایی کنم؟'),
(3265, 28, 13, 'و بدین وسیله ما موسی را به مادرش برگردانیدیم تا دیده‌اش روشن شود و حزن و اندوهش به کلی برطرف گردد و نیز به طور یقین بداند که وعده خدا حق است، لیکن اکثر آنان از این حقیقت آگاه نیستند.'),
(3266, 28, 14, 'و چون موسی به سن عقل و رشد رسید و حد کمال یافت ما به او حکم نبوت و مقام علم عطا کردیم و چنین پاداش به مردم نیکوکار می‌دهیم.'),
(3267, 28, 15, 'و موسی (روزی) بی‌خبر اهل مصر به شهر در آمد، آنجا دید که دو مرد با هم به قتال مشغولند، این یک از شیعیان وی (یعنی از بنی اسرائیل) بود و آن یک از دشمنان (یعنی از فرعونیان) بود، در آن حال آن شخص شیعه از موسی دادخواهی و یاری بر علیه آن دشمن خواست، موسی مشتی سخت بر آن دشمن زد، قضا را بدان ضربت او را کشت. موسی گفت: این کار از فریب و وسوسه شیطان بود (تا این کافر ظالم را با مؤمنی به قتال برانگیخت و عاقبت به هلاکت رسید) که دشمنی شدید شیطان و گمراه ساختن آدمیان به سختی آشکار است.'),
(3268, 28, 16, '(آن‌گاه موسی) گفت: ای خدا، من بر خویش ستم کردم (که به این عمل خود را به فتنه فرعونیان انداختم) تو از من در گذر، خدا هم از او در گذشت که اوست بسیار آمرزنده و مهربان.'),
(3269, 28, 17, 'موسی باز گفت: ای خدا، به شکرانه این نعمت (نیرو) که مرا عطا کردی من هم بدکاران را هرگز یاری نخواهم کرد.'),
(3270, 28, 18, 'موسی (پس از کشته شدن قبطی و نجات سبطی) از توقف به شهر مصر بیمناک شد و مراقب دشمن بود، ناگاه همان کس که روز گذشته از او یاری جسته بود باز او را به داد خواهی خواند. موسی به او گفت: پیداست تو سخت گمراهی!'),
(3271, 28, 19, 'و چون خواست دست به قبطی دشمن دراز کند (قبطی) فریاد کرد: ای موسی مرا هم می‌خواهی مانند شخص دیروز به قتل رسانی؟ معلوم است که تو در این سرزمین قصدی جز گردنکشی و جباری نداری و هیچ نخواهی مشفق و مصلح باشی.'),
(3272, 28, 20, 'و در این حال مردی (مؤمن) از دورترین نقاط شهر مصر شتابان آمد و گفت: ای موسی رجال دربار فرعون در کار تو شورا می‌کنند که تو را به قتل رسانند، به زودی از شهر بیرون گریز، که من درباره تو بسیار مشفق و مهربانم.'),
(3273, 28, 21, 'موسی از شهر مصر با حال ترس و نگرانی و مراقبت از دشمن بیرون رفت، گفت: بار الها، مرا از شر (این) قوم ستمکار نجات ده.'),
(3274, 28, 22, 'و چون رو به جانب شهر مدین آورد، با خود گفت: امید است که خدا مرا به راه‌مستقیم هدایت فرماید.'),
(3275, 28, 23, 'و چون بر سر چاه آبی حوالی شهر مدین رسید آنجا جماعتی را دید که (حشم و گوسفندانشان را) سیراب می‌کردند و دو زن را یافت که دور از مردان در کناری به جمع آوری و منع اختلاط گوسفندانشان مشغول بودند. موسی گفت: شما اینجا چه می‌کنید و کار مهمتان چیست؟ آن دو زن پاسخ دادند که ما گوسفندان خود را سیراب نمی‌کنیم تا مردان گوسفندانشان را سیراب کرده باز گردند و پدر ما شیخی سالخورده و فرتوت است (و مرد دیگری نداریم، ناچار ما را به شبانی گوسفندان فرستاده است).'),
(3276, 28, 24, 'موسی گوسفندانشان را سیراب کرد و (با حالی خسته) رو به سایه درختی آورد و گفت: بار الها، من به خیری (یعنی زندگانی و قوت و غذایی) که تو (از خوان کرمت) نازل فرمایی محتاجم.'),
(3277, 28, 25, '(موسی هنوز لب از دعا نبسته بود که دید) یکی از آن دو دختر که با کمال (و قار و) حیا راه می‌رفت باز آمد و گفت: پدرم از تو دعوت می‌کند تا در عوض سقایت و سیراب کردن گوسفندان ما به تو پاداشی دهد. چون موسی نزد او (یعنی شعیب پدر آن دختر) رسید و سرگذشت خود را بر او حکایت کرد (شعیب) گفت: اینک هیچ مترس که از شر قوم ستمکار نجات یافتی.'),
(3278, 28, 26, 'یکی از آن دو دختر (صفورا) گفت: ای پدر این مرد را (که به شجاعت و امانت آراسته است) به خدمت خود اجیر کن که بهتر کسی که باید به خدمت برگزینی کسی است که توانا و امین باشد.'),
(3279, 28, 27, 'شعیب (به موسی) گفت: من اراده آن دارم که یکی از این دو دخترم را به نکاح تو در آورم بر این مهر که هشت سال خدمت من کنی و اگر ده سال تمام کنی (آن دو سال) به میل و اختیار تو، و من (در این نکاح) رنج بر تو نمی‌خواهم نهاد، ان شاء اللّه مرا شخص شایسته (این خدمت) خواهی یافت.'),
(3280, 28, 28, 'موسی گفت: این عهد میان من و توست هر کدام از دو مدت را (در خدمت) به انجام رسانم بر من ستمی نباشد (و نباید باز خواست شوم) و خدا بر این قول (و عهد) ما وکیل است.'),
(3281, 28, 29, 'آن‌گاه که موسی عهد خدمت به پایان رسانیده و با اهل بیت خود (از حضور شعیب) رو به دیار خویش کرد (در راه) آتشی از جانب طور دید، به اهل بیت خود گفت: شما در اینجا مکث کنید که از دور آتشی به نظرم رسید (می‌روم تا) شاید از آن خبری بیاورم یا برای گرم شدن شما شعله‌ای برگیرم.'),
(3282, 28, 30, 'چون موسی به آن آتش نزدیک شد به او از جانب راست آن وادی در آن بارگاه مبارک از آن درخت (مقدس) ندایی رسید که ای موسی منم خدای یکتا پروردگار جهانیان.'),
(3283, 28, 31, 'و تو (در این مقام) عصای خود بیفکن. چون (عصا افکند و) بر آن نگریست و دید که گویی اژدهایی مهیب و سبک خیز است رو به فرار نهاد و واپس نگردید. (در آن حال بدو خطاب شد) ای موسی پیش آی و مترس که تو از ایمنان هستی.'),
(3284, 28, 32, 'دست خود را در گریبان بر تا بی هیچ نقص (برص و) مرضی، سفید و روشن (چون ماه تابان) بیرون آید، و تا (به ظهور این معجزه) از وحشت و اضطراب بیاسایی باز دست به گریبان بر (تا به حال اول برگردد). این دو (عصا و ید بیضا) از جانب خدایت دو برهان (کامل و دو معجز بزرگ الهی بر رسالت تو) است (اینک برو به رسالت) به سوی فرعون و فرعونیان که قومی فاسق و نابکارند.'),
(3285, 28, 33, 'موسی گفت: پروردگارا، من از فرعونیان یک نفر را کشته‌ام و می‌ترسم که مرا به قتل رسانند.'),
(3286, 28, 34, 'و (با این حال اگر از رسالت ناگزیرم) برادرم هارون را نیز که ناطقه‌اش فصیح‌تر از من است با من یار و شریک درکار رسالت فرما تا مرا تصدیق و ترویج کند که می‌ترسم این فرعونیان سخت تکذیب رسالتم کنند.'),
(3287, 28, 35, 'خدا به او فرمود که ما به همدستی برادرت (هارون) بسیار بازویت را قوی می‌گردانیم و به شما در عالم، قدرت و حکومتی می‌دهیم که هرگز دشمنان به شما دست نیابند، با این آیات و معجزات (که شما را عطا کردیم) شما و پیروانتان غالب خواهید بود.'),
(3288, 28, 36, 'چون موسی با معجزات و آیات ما که (حقانیتش) بر همه روشن بود به سوی فرعونیان آمد باز (آن قوم خود سر و لجوج نادان) گفتند: این معجزات تو جز سحری که ساختگی توست چیز دیگری نیست و ما این گفتار و دعوی که تو داری هیچ از پدران پیشینمان (امم سابقه) نشنیده‌ایم.'),
(3289, 28, 37, 'و موسی گفت: خدای من به احوال رسولی که از جانب او به هدایت خلق آمده داناتر است و بهتر می‌داند که (از ما مؤمنان و شما کافران) کدام یک عاقبت خوش در دار عقبی خواهیم داشت، محققا ستمکاران عالم هرگز فلاح و رستگاری نخواهند یافت.'),
(3290, 28, 38, 'و فرعون (با بزرگان قوم خود چنین) گفت: ای مهتران، من هیچ کس را غیر خودم خدای شما نمی‌دانم، (و با وزیرش گفت:) ای هامان خشتی در آتش پخته و از آن برای من قصری بلند پایه بنا کن تا من بر خدای موسی مطلع شوم هر چند او را (در دعوی رسالت) از دروغگویان می‌پندارم.'),
(3291, 28, 39, 'و خلاصه فرعون و سپاهیانش به ناحق در زمین سرکشی و تکبر کردند و چنین پنداشتند که به سوی ما بازگردانده نخواهند شد.'),
(3292, 28, 40, 'ما هم از آن ظالم و لشکر و اتباعش سخت مؤاخذه کردیم و همه را به دریا افکندیم، پس بنگر تا عاقبت کار ستمکاران به کجا کشید؟!'),
(3293, 28, 41, 'و ما آن قوم (ظالم) را پیشوایان (ضلالت و) دعوت به دوزخ قرار دادیم و روز قیامت یاری نخواهند شد.'),
(3294, 28, 42, 'و آنها را در این دنیا در پی کردارشان لعن ابد فرستادیم و در آخرت هم از زشترویان (و مردودان درگاه ما) خواهند بود.'),
(3295, 28, 43, 'و همانا ما پس از آنکه (طوایفی از) امم گذشته را (به کیفر کفر) هلاک کردیم به موسی برای بصیرت و هدایت و رحمت خلق کتاب (تورات) را عطا کردیم، باشد که مردم متذکر شوند (و راه خدا پرستی پیش گیرند).'),
(3296, 28, 44, 'و تو آن هنگام که ما به موسی مقام نبوت و فرمان الهی عطا کردیم در جانب غربی کوه طور اصلا نبودی و حضور نداشتی (و حکایت او به وحی بر تو روشن گردید).'),
(3297, 28, 45, 'و لیکن ما امم و قبایلی را بیافریدیم که عمر دراز یافتند (و همه را به غفلت و جهالت به سر بردند و این خبرها کهنه شد و ما مجددا به تو وحی کردیم) و نیز تو میان اهل مدین نبودی که اینک آیات ما را (درباره آنان) بر اینان (اهل مکه) تلاوت می‌کنی و لیکن ماییم که (تو را لایق پیمبری دانسته) به رسالت فرستادیم (و به وحی خود از هر چیزت آگاه کردیم).'),
(3298, 28, 46, 'و تو وقتی که ما (به موسی) ندا کردیم در جانب کوه طور نبودی و لیکن (وحی قصص موسی و سایر رسل به مقتضیات) لطف پروردگار توست (به تو) تا قومی را که بر آنان پیمبر منذری پیش از تو نیامده (و کتاب آسمانی به دست ندارند از خدا) بترسانی، باشد که متذکر شوند (و راه هدایت پیش گیرند).'),
(3299, 28, 47, 'و تا اگر بلا و عذابی به موجب کردار زشت خودشان به آنها رسید نگویند: پروردگارا، چرا برای ما رسولی نفرستادی تا آیات تو را پیروی کنیم و از اهل ایمان شویم؟'),
(3300, 28, 48, 'پس هنگامی که (رسول) حق از جانب ما به آنها آمد گفتند: چرا به این رسول مانند موسی معجزاتی (نظیر عصا و ید بیضا و الواح تورات) داده نشد؟ آیا این مردم از این پیش به موسی هم (با همه این معجزات) کافر نشدند که گفتند: این دو (قرآن و تورات) سحر و جادوگری است که پشتیبان یکدیگرند و گفتند: ما به همه اینها سخت بی‌ایمان و بی‌عقیده‌ایم؟'),
(3301, 28, 49, 'بگو: پس شما اگر راست می‌گویید کتابی که از این دو کتاب آسمانی (تورات و قرآن) خلق را بهتر هدایت کند از جانب خدا بیاورید تا من از آن پیروی کنم.'),
(3302, 28, 50, 'پس اگر به تقاضای تو جواب نتوانند داد در این صورت بدان که این مردم تنها پیرو هوای نفسند و کیست (ستمگر و) گمراه‌تر از آن کسی که راه هدایت خدا را رها کرده و از هوای نفس خود پیروی کند؟ البته خدا قوم ستمکار را (پس از اتمام حجت) هرگز هدایت نخواهد کرد.'),
(3303, 28, 51, 'و همانا ما برای (هدایت) این مردم (آیات قرآن را) سخن پیوسته (یعنی مرتب و مربوط به سعادت دنیا و آخرت خلق) آوردیم تا مگر متذکر (حقایق آن) شوند.'),
(3304, 28, 52, 'آنان که ما به آنها پیش از این، کتاب (آسمانی تورات و انجیل را) فرستادیم (یعنی موسی و عیسی و پیروان حقیقی آنها) به این (کتاب آسمانی قرآن) البته آنان ایمان می‌آورند.'),
(3305, 28, 53, 'و چون آیات ما بر آنها تلاوت شود گویند: به آن ایمان آوردیم که این قرآن به حق از جانب پروردگار ما نازل شده، ما پیش از این نیز تسلیم فرمان خدا بودیم.'),
(3306, 28, 54, 'اینان را دو بار پاداش نیکو دهند، زیرا صبر و ثبات (در دین خود و اسلام هر دو) ورزیدند و بدی را به نیکی دفع می‌کنند و از آنچه روزی آنها کردیم انفاق می‌کنند.'),
(3307, 28, 55, 'و چون سخن لغوی (از دشمنان دین) بشنوند از آن اعراض کنند و گویند: اعمال ما از ما و اعمال شما از شما، بروید سلامت باشید، که ما هرگز مردم (هرزه گوی) نادان را نمی‌طلبیم.'),
(3308, 28, 56, '(ای رسول ما، با آنکه تو هادی خلقی) چنین نیست که هر کس را تو دوست بداری هدایت توانی کرد لیکن خدا هر که را خواهد هدایت می‌کند و او به حال آنان که قابل هدایتند آگاه‌تر است.'),
(3309, 28, 57, 'و (برخی از اهل مکه) گفتند: اگر ما با تو طریق هدایت را (که اسلام است) پیروی کنیم ما را از سرزمین خود به زودی بربایند (و به دست ملت عرب که متفق بر علیه اسلامند از وطن آواره شویم). آیا ما (مکه را) بر ایشان حرمی امن قرار ندادیم که به این مکان (بی‌آب و گیاه) انواع نعمتها و ثمرات که ما روزیشان کردیم از هر طرف بیاورند؟ لیکن حقیقت این است که اکثر آنها نادانند.'),
(3310, 28, 58, 'و چه بسیار شده که ما اهل دیاری را که به هوس‌رانی و خوش گذرانی پرداختند هلاک کردیم، و این خانه‌های (ویران) آنهاست که بعد از آنها جز عده قلیلی در آن دیار سکونت نیافت و تنها ما وارث (دیار آنها) بودیم.'),
(3311, 28, 59, 'و پروردگار تو بر آن نیست که اهل هیچ شهر و دیاری را تا در مرکز آن رسولی نفرستد که آیات ما را بر آنها تلاوت کند هلاک کند، و ما هیچ دیاری را هلاک نکنیم مگر آنکه اهلش ظالم و بیدادگر باشند.'),
(3312, 28, 60, 'و آنچه از نعمتها (ی این عالم) به شما داده شده متاع و زیور (بی‌قدر) زندگانی دنیاست و آنچه نزد خداست بسیار بهتر و باقی‌تر است، آیا فهم و عقل کار نمی‌بندید؟'),
(3313, 28, 61, 'آیا آن کس که ما به او وعده نیکو (ی بهشت ابدی) داده‌ایم مانند کسی است که او را از متاع دنیا (ی فانی) بهره‌مند ساختیم سپس در قیامت او را (برای حساب و مجازات) حاضر آرند.'),
(3314, 28, 62, 'و (یاد آور) روزی که خدا آن مشرکان را ندا کند و گوید که کجا شدند آن خدایانی که شریکان من می‌پنداشتید؟'),
(3315, 28, 63, 'در آن حال آنان که وعده عذابشان محقق و حتمی شده (یعنی پیشوایان کفر) گویند: پروردگارا، ما که این مردم را گمراه کردیم سببش گمراهی ما بود که خلق را هم مانند خود گمراه می‌خواستیم، و امروز (از پیروان خود) به سوی تو بیزاری می‌جوییم که آنها (از روی عقیده) ما را نمی‌پرستیدند (بلکه به هوای نفس خود در پی ما آمدند).'),
(3316, 28, 64, 'و (در آن روز به مردم بت پرست) گویند: اینک آنان را که شریک خدا گرفتید بخوانید (تا شما را از عذاب برهانند)، آنها را بخوانند ولی جواب به ایشان ندهند و چون عذاب را به چشم ببینند آرزو کنند که ای کاش (مانند مؤمنان) هدایت یافته بودند.'),
(3317, 28, 65, 'و روزی که خدا خلق را ندا کند که شما چگونه به رسولان (حق) پاسخ دادید؟'),
(3318, 28, 66, 'پس راه هر گونه عذر و سخن، آن روز بر آنها پوشیده و مسدود شده و هیچ (از هول عذاب) سؤال و جواب نکنند.'),
(3319, 28, 67, 'اما آن کس که توبه کرده و ایمان آورده و عمل صالح به جای آورده امید است که از رستگاران باشد.'),
(3320, 28, 68, 'و خدای تو هر چه خواهد بیافریند و (هر کس را صلاح داند) بر گزیند، دیگران را (در نظم عالم) هیچ اختیار و گزینشی نیست، ذات پاک الهی منزه و برتر از آن است که به او شرک آورند.'),
(3321, 28, 69, 'و خدای تو به آنچه خلق در دل پنهان کنند و یا آشکار سازند به همه آگاه است.'),
(3322, 28, 70, 'و اوست خدای یکتا که جز او هیچ خدایی نیست، هم ستایش در اول و آخر عالم (در این جهان و آن جهان) مخصوص او، هم حکم و سلطنت جهان با او و هم رجوع شما به سوی اوست.'),
(3323, 28, 71, '(به این مردم مشرک) بگو: چه تصور می‌کنید اگر خدا ظلمت شب را بر شما تا قیامت پاینده و ابدی گرداند جز خدا کیست خدایی که بتواند برای شما روشنی روز پدید آرد؟ آیا سخن نمی‌شنوید؟'),
(3324, 28, 72, 'باز بگو: چه تصور می‌کنید اگر خدا برای شما روز را تا قیامت پاینده و ابدی قرار دهد جز خدا کیست خدایی که برای آرامش و استراحت شما شب را پدید گرداند؟ آیا چشم بصیرت (به حکمت گردش روز و شب) نمی‌گشایید؟'),
(3325, 28, 73, 'یکی از (هزاران لطف و) رحمت‌های خدا آن است که شب و روز را برای شما قرار داده تا در شب آرام گیرید و در روز از فضل خدا (یعنی روزی) طلب کنید، و باشد که شکرگزار (نعمتهای خدا) شوید.'),
(3326, 28, 74, 'و (یاد آر) روزی که خدا مشرکان را ندا کرده و گوید که آن بتان و معبودان (باطل) که شریک من می‌پنداشتید کجا رفتند؟'),
(3327, 28, 75, 'و (آن روز) ما از هر امتی شاهدی بر گرفته و گوییم اینک برهانی (بر آن دعویهای باطل که در دنیا داشتید) اقامه کنید. آن‌گاه (آنها رسوا شده و) بدانند که حق و حقیقت با خدا (و رسولان او) است و آنچه به دروغ فرا می‌بافتند همه نابود گردد و از دستشان برود.'),
(3328, 28, 76, 'همانا قارون یکی از (ثروتمندان) قوم موسی بود که بر آنها طریق ظلم و طغیان پیش گرفت و ما آن قدر گنج و مال به او دادیم که بر دوش بردن کلید آن گنجها صاحبان قوّت را خسته کردی، هنگامی که قومش به او گفتند: آن قدر مغرور و شادمان (به ثروت خود) مباش که خدا هرگز مردم پر غرور و نشاط را دوست نمی‌دارد.'),
(3329, 28, 77, 'و به هر چیزی که خدا به تو عطا کرده بکوش تا (ثواب و سعادت) دار آخرت تحصیل کنی و لیکن بهره‌ات را هم از (لذّات و نعم حلال) دنیا فراموش مکن و (تا توانی به خلق) نیکی کن چنانکه خدا به تو نیکویی و احسان کرده و هرگز در روی زمین فتنه و فساد برمینگیز، که خدا مفسدان را ابدا دوست نمی‌دارد.'),
(3330, 28, 78, 'قارون گفت: این مال و ثروت فراوان به علم و تدبیر خودم به من داده شد! آیا ندانست که خدا پیش از او چه بسیار امم و طوایفی را که از او قوت و ثروت و جمعیتشان بیشتر بود هلاک کرد؟ و هیچ از گناه بدکاران سؤال نخواهد شد (بلکه یکسر به دوزخ روند).'),
(3331, 28, 79, 'آن‌گاه قارون (روزی) با زیور و تجمل بسیار بر قومش در آمد، مردم دنیا طلب (که او را دیدند) گفتند: ای کاش همان قدر که به قارون از مال دنیا دادند به ما هم عطا می‌شد که او بهره بزرگ و حظّ وافری را داراست.'),
(3332, 28, 80, 'و اما صاحبان مقام علم و معرفت (به آن دنیا پرستان) گفتند: وای بر شما (و همت پست شما) ثواب خدا برای آن کس که به خدا ایمان آورده و نیکوکار گردیده بسی بهتر (و بالاتر از این دارایی دو روزه قارون) است، ولی هیچ کس جز آنان که صبر پیشه کنند بدان ثواب و مقام نخواهد رسید.'),
(3333, 28, 81, 'پس (از اندک زمانی) ما هم او را با خانه (و گنج و دارایی) اش به زمین فرو بردیم و هیچ حزب و جمعیتی نداشت که او را در برابر خدا یاری کنند و خود هم نتوانست خویش را یاری دهد.'),
(3334, 28, 82, 'صبحگاه هم آنان که روز گذشته (ثروت و) مقام او را آرزو می‌کردند با خود می‌گفتند: ای وای (بر ما و آرزوهای ما) گویی خداست که هر که از بندگان خود را خواهد روزی فراوان دهد و (هر که را خواهد) تنگ روزی کند، اگر نه این بود که خدا بر ما منّت گذاشت (و غرور و ثروت قارون نداد) ما را هم (مثل او) در زمین فرو بردی! ای وای که گویا کافران را هرگز فلاح و رستگاری نیست!'),
(3335, 28, 83, 'ما این دار (بهشت ابدی) آخرت را برای آنان که در زمین اراده علوّ و فساد و سرکشی ندارند مخصوص می‌گردانیم و حسن عاقبت خاص پرهیزکاران است.'),
(3336, 28, 84, 'هر که عمل نیک آرد پاداش بهتر از آن یابد، و هر کس که عمل بد کند بدکاران جز به همان اعمال بد مجازات نشوند..'),
(3337, 28, 85, '(ای رسول ما، یقین دان که) آن خدایی که (احکام) قرآن را بر تو فرض گردانید (و ابلاغ آن را وظیفه تو قرار داد) البته تو را به جایگاه خود (مکه یا بهشت ابد) باز خواهد گرداند. (تو با مردم) بگو که خدای من به حال آن که محقق در هدایت و آن که در ضلالت آشکار است (از هر کس) داناتر است.'),
(3338, 28, 86, 'و تو خود امید آنکه این کتاب بزرگ (قرآن) بر تو نازل شود نداشتی جز آنکه لطف و رحمت خدا تو را به رسالت برگزید، پس (به شکرانه این رحمت) هیچ گاه به کافران نصرت و یاری مکن.'),
(3339, 28, 87, 'و زنهار! کافران تو را از (تبلیغ) آیات خدا پس از آنکه بر تو نازل شد منع نکنند، و (همیشه خلق را) به سوی پروردگارت دعوت کن و از مشرکان (و هم عقیدت و خوی آنان) مباش.'),
(3340, 28, 88, 'و هرگز با خدای یکتا دیگری را به خدایی مخوان، که جز او هیچ خدایی نیست، هر چیزی جز ذات پاک الهی هالک الذّات و نابود است، فرمان با او و رجوع شما به سوی اوست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الم (این حرف اشاره به اسماء الهی یا اسراری بین خدا و رسول اوست).'),
(3342, 29, 2, 'آیا مردم چنین پنداشتند که به صرف اینکه گفتند ما ایمان (به خدا) آورده‌ایم رهاشان کنند و هیچ امتحانشان نکنند؟'),
(3343, 29, 3, 'و محققا ما اممی را که پیش از اینان بودند به امتحان و آزمایش آوردیم، و همانا خدا دروغگویان و راستگویان را کاملا می‌شناسد.'),
(3344, 29, 4, 'آیا آنان که کار زشت و اعمال ناشایسته مرتکب می‌شوند پنداشتند که بر ما سبقت گیرند (و از عقاب ما خلاصی یابند)؟ بسیار بد حکم می‌کنند.'),
(3345, 29, 5, 'هر کس که به لقاء خدا (و مشاهده جمال و وصول به رحمت و جنّت او) امیدوار و مشتاق است (بداند که) هنگام اجل و وعده معین خدا البته فرا رسد و او (به گفتار و کردار خلق) شنوا و داناست.'),
(3346, 29, 6, 'و هر که (در راه ایمان) جهد و کوشش کند به سود خود کند که خدا از (طاعت و عبادت) عالمیان البته بی‌نیاز است.'),
(3347, 29, 7, 'و آنان که ایمان آورده و نیکوکار شدند البته ما هم گناهانشان را محو و مستور کنیم و بهتر از آن اعمال نیکشان به آنها پاداش دهیم.'),
(3348, 29, 8, 'و ما به آدمی سفارش کردیم که در حق پدر و مادر خود نیکی کند، و اگر آنها (خود مشرک باشند و) کوشند تا تو به من (که خدای یگانه‌ام) از روی جهل و نادانی شرک آوری هرگز اطاعت امر آنها مکن، که رجوع شما به سوی من است و من شما را به پاداش هر عمل که به جای آورده‌اید آگاه می‌گردانم.'),
(3349, 29, 9, 'و آنان که ایمان آورده و کار نیکو کردند آنان را البته (به بهشت) در صف صالحان (و رتبه نیکان) آریم.'),
(3350, 29, 10, 'و بعضی مردم (از راه نفاق) می‌گویند: ما به خدا ایمان آوردیم، و چون رنج و آزاری در راه خدا ببینند فتنه و عذاب خلق را با عذاب خدا برابر شمرند (یعنی همان قدر که ترس عذاب خدا آنها را از کفر به ایمان می‌خواند ترس عذاب مردم هم از ایمان به کفر می‌کشد) و هر گاه ظفر و نصرتی از جانب خدایت (به مؤمنان) رسید (آن منافقان) گویند: ما هم با شما (و هم آیین شما) بودیم. آیا خدا بر آنچه در دلهای خلایق است داناتر نیست؟'),
(3351, 29, 11, 'و البته خدا هم به احوال اهل ایمان داناست و هم از درون منافقان آگاه است.'),
(3352, 29, 12, 'و کافران به اهل ایمان گفتند: شما طریقه ما را پیروی کنید (اگر به خطا رفتید) بار خطاهای شما را ما به دوش می‌گیریم، در صورتی که هیچ از بار گناهشان را به دوش نگیرند، آنها دروغ می‌گویند.'),
(3353, 29, 13, 'و آنها علاوه بر بار سنگین گناهان خود بار گناهان هر که را گمراه کرده‌اند نیز به دوش می‌گیرند و روز قیامت از آنچه دروغ می‌بستند سخت مؤاخذه می‌شوند.'),
(3354, 29, 14, 'و همانا نوح را (به رسالت) به سوی قومش فرستادیم و او هزار سال پنجاه سال کم میان قوم درنگ کرد، و چون همه ستمگر و ظالم بودند همه را طوفان هلاک فرا گرفت.'),
(3355, 29, 15, 'و تنها خود نوح را با اصحاب کشتی او (از غرق) نجات دادیم و آن کشتی را برای خلق عالم آیتی (از قدرت و رحمت خویش) قرار دادیم.'),
(3356, 29, 16, 'و ابراهیم را (نیز فرستادیم) که به قوم خود گفت: خدا را بپرستید و از او بترسید که پرستش و ترس خدا اگر بدانید (از هر چیز) برای شما بهتر است.'),
(3357, 29, 17, 'و بدانید که شما به جای خدا بتهایی (جماد و بی‌اثر) را می‌پرستید و دروغی می‌سازید (که نام خدا بر آنها می‌نهید، اینک بدانید که) آنچه را جز خدا می‌پرستید قادر بر رزق شما نیستند، پس روزی را از نزد خدا طلبید و او را پرستید و شکر (نعمت) وی به جای آرید (و بدانید که باز) رجوع شما به سوی اوست.'),
(3358, 29, 18, 'و اگر شما (مردم مکه، رسالت این پیامبر را) تکذیب می‌کنید (عجبی نیست زیرا) پیش از شما هم بسیاری از امم (پیمبران خود را) تکذیب کردند، و بر رسول جز آنکه به آشکار تبلیغ رسالت کند تکلیفی نیست.'),
(3359, 29, 19, 'آیا (مردم بارها به چشم خود) ندیدند که خدا چگونه ابتدا خلق را ایجاد می‌کند و باز به اصل خود برمی‌گرداند؟ این کار بر خدا بسیار آسان است.'),
(3360, 29, 20, 'بگو که در زمین سیر کنید و ببینید که خدا چگونه خلق را ایجاد کرده (تا از مشاهده اسرار خلقت نخست بر شما به خوبی روشن شود که) سپس خدا نشأه آخرت را ایجاد خواهد کرد، همانا خدا بر هر چیز تواناست.'),
(3361, 29, 21, 'هر که را (از اهل گناه) بخواهد (به عدل) عذاب می‌کند و هر که را بخواهد (به لطف و رحمت) می‌بخشد و شما را به سوی او باز می‌گردانند.'),
(3362, 29, 22, 'و شما (مخلوق عاجز) نه هرگز (خدای قادر مطلق را) در زمین و آسمان عاجز توانید کرد و نه جز خدا نگهبان و یاوری دارید.'),
(3363, 29, 23, 'و آنان که به آیات خدا و شهود لقای او کافر شدند آنها از رحمت (و نعمت بهشت) من ناامیدند و سخت به عذاب دردناک گرفتار خواهند شد.'),
(3364, 29, 24, '(بعد از این همه نصایح و اندرز ابراهیم) باز قوم جز آنکه گفتند: او را بکشید و در آتش بسوزانید پاسخ ندادند، و خدا او را از آتش (نمرودیان) نجات داد. در این حکایت برای قومی که به خدا ایمان آرند آیتهایی (از قدرت الهی) پدیدار است.'),
(3365, 29, 25, 'باز ابراهیم گفت: (ای مردم شما خود می‌دانید که) بتهایی را که به جای خدای یکتا به خدایی بر گرفته‌اید تنها برای حفظ دوستی (و استفاده مادی دو روزه) دنیای خود اتخاذ کرده‌اید و چون روز قیامت شود در آن روز شما از یکدیگر بیزاری جویید و به یکدیگر لعن و نفرین کنید، و جایگاه ابدی تمام شما آتش دوزخ است و هیچ یاوری ابدا ندارید.'),
(3366, 29, 26, 'پس (خواهر زاده‌اش) لوط به او ایمان آورد، و (ابراهیم به او) گفت که من (از این دیار شرک) هجرت به سوی خدای خود خواهم کرد که او (بر حفظ من از دشمن) مقتدر و (به صلاح امورم) داناست.'),
(3367, 29, 27, 'و ما اسحاق و (فرزند زاده‌اش) یعقوب را به او عطا کردیم و در خاندانش مقام نبوت و کتاب آسمانی برقرار نمودیم و در دنیا هم (به نعمت فرزندان صالح و نام نیکو و سایر نعم الهی) اجر او را دادیم و در آخرت نیز وی در صف صالحان (و هم رتبه مقربان) خواهد بود.'),
(3368, 29, 28, 'و لوط (به رسالت فرستادیم) که به قوم خود گفت: شما به عمل زشتی اقدام می‌کنید که هیچ کس در عالم پیش از شما مرتکب نشده است!'),
(3369, 29, 29, 'آیا شما به شهوترانی با مردان خلوت کرده و راه (فطرت زناشویی) را قطع می‌کنید و در مجامع خود (بی هیچ خجلت) به کار قبیح می‌پردازید؟! قوم لوط جز آنکه (به طعنه) گفتند: اگر راست می‌گویی عذاب خدا را بر ما نازل کن به او ابدا جوابی ندادند.'),
(3370, 29, 30, 'لوط گفت: پروردگارا، مرا بر (محو) این قوم فاسد یاری کن.'),
(3371, 29, 31, 'و چون رسولان ما (فرشتگان عالم قدس) به ابراهیم بشارت (فرزند) آوردند گفتند: ما (به امر خدا) اهل این دیار را که قومی سخت ظالم و بدکارند هلاک می‌کنیم.'),
(3372, 29, 32, 'ابراهیم به فرشتگان گفت: لوط هم (که بنده صالح موحد پاکی است) در این دیار است! پاسخ دادند که ما به احوال آن که در این دیار است بهتر آگاهیم، ما او را با همه اهل بیتش غیر زن وی که در میان اهل هلاکت باقی ماند البته نجات خواهیم داد.'),
(3373, 29, 33, 'و چون رسولان ما نزد لوط آمدند، لوط از آمدن آنها (که ناشناس و با صورت زیبا بودند) اندوهگین و دلتنگ گردید، و فرشتگان گفتند: هیچ بیمناک و غمگین مباش که ما تو را با همه اهل بیتت غیر آن زن کافرت که در میان اهل هلاک واماند نجات می‌دهیم.'),
(3374, 29, 34, 'ما بر اهل این دیار که فاسق و بدکارند از آسمان عذابی سخت نازل کنیم.'),
(3375, 29, 35, 'و همانا از آن دیار لوط آثار خرابی را وا گذاشتیم تا آیتی روشن برای (عبرت) عاقلان باشد.'),
(3376, 29, 36, 'و شعیب را که برادر مهربانی برای قومش بود (به رسالت) به سوی اهل مدین (فرستادیم)، او با قوم گفت که ای قوم، همیشه خدا را پرستید و به روز قیامت (و سعادت آخرت) امیدوار باشید و هرگز در روی زمین فساد و تبهکاری مکنید (که آسایش دو گیتی در این نصایح است).'),
(3377, 29, 37, 'قوم شعیب او را تکذیب کردند و (به کیفر آن، شبی) زلزله‌ای سخت آنان را در گرفت و صبحگاه همه را در خانه‌هاشان مرگ به زانو در آورد.'),
(3378, 29, 38, 'و قوم عاد و ثمود را که منازل (خراب و ویران) شان را به چشم می‌بینید آنها نیز با آنکه بینا و هوشیار بودند چون شیطان اعمال زشتشان را در نظرشان نیکو جلوه داد و از راه (حق) آنها را باز داشت همه را هلاک و نابود گردانیدیم.'),
(3379, 29, 39, 'و قارون و فرعون و هامان را هم که موسی (علیه السّلام) با معجزات روشن به هدایت آنها آمد (و به او نگرویدند و) باز در زمین تکبر و ظلم و طغیان کردند (همه را هلاک کردیم) و (بر قهر حق) سبقت نگرفتند (و راه نجاتی نیافتند).'),
(3380, 29, 40, 'ما هر طایفه‌ای را به کیفر گناهش مؤاخذه کردیم، بعضی را بر سرشان سنگ بلا فرو باریدیم و برخی را صیحه عذاب آسمانی در گرفت و برخی را به زلزله زمین و گروهی دیگر را به غرق دریا به هلاکت رسانیدیم، و خدا به آنان هیچ ستم نکرد لیکن آنها خود در حق خویش ستم می‌کردند.'),
(3381, 29, 41, 'مثل حال آنان که خدا را فراموش کرده و غیر خدا را به دوستی و سرپرستی برگرفتند (در سستی و بی‌بنیادی) حکایت خانه‌ای است که عنکبوت بنیاد کند و اگر بدانند سست‌ترین بنا خانه عنکبوت است.'),
(3382, 29, 42, 'خدا بر آنچه که به غیر او بخوانند (و پرستش کنند) آگاه است و او (بر انتقام اهل شرک و ریا) مقتدر و (به دقایق امور عالم) داناست.'),
(3383, 29, 43, 'و ما این همه مثلها را برای مردم می‌زنیم (تا حقایق برای آنها روشن شود) و لیکن به جز مردم دانشمند کسی تعقل در آنها نخواهد کرد.'),
(3384, 29, 44, 'خدا آسمانها و زمین را به حق (و در کمال حکمت) آفریده (نه بر باطل و لغو و عبث) و در این خلقت آسمان و زمین آیت و نشانه‌ای (از ربوبیت) برای اهل ایمان کاملا پدیدار است.'),
(3385, 29, 45, '(ای رسول ما) آنچه را که از کتاب (آسمانی قرآن) بر تو وحی شد (بر خلق) تلاوت کن و نماز را (که بزرگ عبادت خداست) به جای آر، که همانا نماز است که (اهل نماز را) از هر کار زشت و منکر باز می‌دارد و همانا ذکر خدا بزرگتر (و برتر از حدّ اندیشه خلق) است، و خدا به هر چه (برای خشنودی او و به یاد او) کنید آگاه است.'),
(3386, 29, 46, 'و شما مسلمانان با اهل کتاب (یهود و نصاری و مجوس) جز به نیکوترین طریق بحث و مجادله مکنید مگر با ستمکاران از آنها، و (با اهل کتاب) بگویید که ما به کتاب آسمانی قرآن که بر ما نازل شده و به کتب آسمانی شما به همه ایمان آورده‌ایم و خدای ما و شما یکی است و ما تسلیم و مطیع فرمان اوییم.'),
(3387, 29, 47, 'و ما همچنان (که بر رسولان پیشین کتاب آسمانی تورات و انجیل و زبور فرستادیم) بر تو هم (ای رسول) کتاب (آسمانی قرآن) را نازل کردیم، پس آنان که به آن کتب که بر ایشان فرستادیم ایمان آوردند به این کتاب نیز ایمان آرند و از این گروه (اهل مکه) هم بعضی به آنان ایمان آورند و به جز کافران هیچ کس آیات ما را انکار نخواهد کرد.'),
(3388, 29, 48, 'و تو از این پیش نه توانستی کتابی خواند و نه خطی نگاشت که در آن صورت مبطلان (منکر قرآن) در نبوتت شک و ریبی می‌کردند (و می‌گفتند این کتاب به وحی خدا نیست و خود از روی کتب سابقین جمع و تألیف کرده است).'),
(3389, 29, 49, 'بلکه این قرآن آیات روشن الهی است در لوح سینه آنان که (از خدا نور) علم و دانش یافتند، و آیات ما را جز مردم ستمکار انکار نکنند.'),
(3390, 29, 50, 'و کافران گفتند: (این مرد اگر رسول خداست) چرا بر او آیات و معجزاتی از جانب خدایش نازل نشد؟ بگو: آیات نزد خدا (و به امر خدا) است، من (که رسولم) جز آنکه (خلق را از انتقام خدا) بترسانم وظیفه ندارم.'),
(3391, 29, 51, 'آیا اینان را (که تقاضای معجزه می‌کنند) این معجزه کفایت نکرد که چنین کتاب بزرگ را که بر آنان تلاوت می‌شود ما بر تو فرستادیم؟ و همانا در آن برای اهل ایمان البته رحمت و یاد آوری و پندی خواهد بود.'),
(3392, 29, 52, 'بگو: میان من و شما خدا گواهی کافی است، که او به هر چه در آسمانها و زمین است آگاه است. و آنان که به باطل گرویدند و به خدا کافر شدند هم آنان به حقیقت زیانکاران عالمند.'),
(3393, 29, 53, 'منکران (به تمسخر) از تو تقاضای تعجیل در نزول عذاب می‌کنند، و اگر وقت معین آن (در علم ازلی، قیامت) نبود عذاب حق بر آنها می‌رسید، و البته ناگهانی در حالی که غافل و بی‌خبر باشند بر آنان خواهد آمد.'),
(3394, 29, 54, '(و این کافران) از تو به تعجیل عذاب می‌طلبند در صورتی که شراره عذاب جهنم به کافران احاطه نموده است.'),
(3395, 29, 55, '(اینان را متذکر ساز به آن) روزی که عذاب خدا از بالا و زیر پا آنها را فرو گیرد، و (خدا یا منادی حق) گوید: (امروز) بچشید کیفر آنچه (در دنیا) عمل می‌کردید.'),
(3396, 29, 56, 'این بندگان با ایمان من، زمین من بسیار وسیع است در این صورت (اگر از جور کفار در سرزمینی به تنگ آمده و به کفر مجبور شوید از آنجا به دیار دیگر هجرت کنید و هر کجا باشید) مرا منحصرا به اخلاص پرستش کنید.'),
(3397, 29, 57, 'هر نفسی (طعم ناگوار) مرگ را خواهد چشید، و پس از مرگ شما را به سوی ما باز گردانند.'),
(3398, 29, 58, 'و آنان که ایمان آوردند و به اعمال نیکو پرداختند البته آنها را به عمارات عالی بهشتی که زیر درختانش نهرها جاری است منزل دهیم که در آن زندگانی ابدی کنند، آنجا پاداش نیکویی برای نیکوکاران عالم است.'),
(3399, 29, 59, 'آنان که (در راه دین خدا) صبر و شکیبایی پیشه کردند و بر پروردگار خود توکل می‌نمودند.'),
(3400, 29, 60, 'و (در کار رزق به خدا توکل کنید نه بر سعی خود که) چه بسیار حیوانات که خود بار روزی خود نکشند، خدا (بدون هیچ کوشش) به آنها و هم به شما روزی می‌رساند، و او شنوا (ی دعای محتاجان) و دانا (به احوال بندگان) است.'),
(3401, 29, 61, 'و اگر از این کافران مشرک سؤال کنی که آسمانها و زمین را که آفریده و خورشید و ماه مسخر فرمان کیست؟ به یقین جواب دهند که خدا (آفریده)، پس چگونه (با این اقرار، از توحید و معرفت خدا) رویگردان می‌شوند؟'),
(3402, 29, 62, 'خداست که هر کس از بندگان را خواهد وسیع روزی و یا تنگ روزی می‌گرداند، که همانا او به هر چیزی (از مصالح خلایق) داناست.'),
(3403, 29, 63, 'و اگر از این کافران مشرک سؤال کنی که آن کیست که از آسمان باران نازل سازد تا زمین را به آن پس از (فصل خزان و) مرگ (گیاهان، باز به نسیم جانبخش بهار) زنده گرداند؟ به یقین جواب دهند که آن خداست. بگو: ستایش مخصوص خدای یکتاست. آری و لیک اکثر این مردم عقل خود را به کار نگیرند (تا این حقیقت را دریابند).'),
(3404, 29, 64, 'این زندگانی چند روزه دنیا (اگر نه در پرستش حق و سعادت آخرت صرف شود) افسوس و بازیچه‌ای بیش نیست، و زندگانی اگر مردم بدانند به حقیقت‌دار آخرت است (که حیاتش جاوید و نعمتش بی‌رنج و زوال است).'),
(3405, 29, 65, 'این مردم مشرک چون به کشتی نشینند (و به دست امواج خطر افتند در آن حال) تنها خدا را به اخلاص کامل در دین می‌خوانند، و چون از خطر دریا به ساحل نجاتشان رساند (باز به خدای یکتا) مشرک می‌شوند.'),
(3406, 29, 66, 'تا هر نعمتی را که ما به آنها عطا کرده‌ایم کفران کنند و (تا مدتی) بهره‌مند باشند، اما به زودی خواهند دانست.'),
(3407, 29, 67, 'آیا (کافران اهل مکه) ندیدند که آن شهر را حرم امن و امان قرار دادیم در صورتی که از اطرافشان مردم (ضعیف) را به قتل و غارت می‌ربایند؟ آیا باز به باطل می‌گروند و به نعمت خدا کافر می‌شوند؟'),
(3408, 29, 68, 'آیا هیچ کس از آن که بر خدا دروغ بست و (رسول) حق را که برای (هدایت) او آمد تکذیب کرد (در عالم) ستمکارتر هست؟ آیا جایگاه کافران (ستمکار) در آتش دوزخ نیست؟'),
(3409, 29, 69, 'و آنان که در (راه) ما (به جان و مال) جهد و کوشش کردند محققا آنها را به راه‌های (معرفت و لطف) خویش هدایت می‌کنیم، و همیشه خدا یار نکوکاران است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الم (اشاره به اسماء یا اسرار کتاب الهی است).'),
(3411, 30, 2, 'رومیان، مغلوب (فارسیان) شدند.'),
(3412, 30, 3, 'در (جنگی که به) نزدیکترین زمین (به دیار عرب واقع شد که حوالی شام یا اطراف جزیره باشد) و آنها پس از مغلوب شدن فعلی به زودی (بر فارسیان) غلبه خواهند کرد.'),
(3413, 30, 4, '(این غلبه رومیان) در اند سالی خواهد شد (و بدانید که این حادثه و) کلیه امور عالم از قبل از این و بعد از این همه به دست خداست، و آن روز (که رومیان فاتح شوند چون بعد از آن خدا وعده یاری به اهل اسلام داده) مؤمنان شاد می‌شوند.'),
(3414, 30, 5, 'به یاری خدا، که خدا هر که را بخواهد نصرت و فیروزی می‌بخشد و اوست که مقتدر و مهربان است.'),
(3415, 30, 6, 'این وعده خداست که خدا هرگز خلاف وعده نکند، و لیکن اکثر مردم (از این حقیقت) آگاه نیستند.'),
(3416, 30, 7, '(اکثر) آنان به امور ظاهری از زندگی دنیا آگاهند و از عالم آخرت (و وعده ثواب و عقاب حق) به کلی بی‌خبرند.'),
(3417, 30, 8, 'آیا در پیش نفوس خود تفکر نکردند (تا بدین حکمت پی‌برند) که خدا آسمانها و زمین و هر چه در بین آنهاست همه را جز به حق (و برای حکمت و مصلحت) و به وقت (و حد) معین نیافریده است؟ و بسیاری از مردم به شهود و لقای خدا (در عالم غیب و وعده ثواب و عقاب بهشت و دوزخ و قیامت) به کلی کافر و بی‌عقیده‌اند.'),
(3418, 30, 9, 'آیا در زمین سیر نکردند تا ببینند که عاقبت کار پیشینیانشان (چون قوم عاد و ثمود) چه شد؟ در صورتی که از اینها بسیار تواناتر بودند و بیش از اینها در زمین کشتزار ساختند و کاخ و عمارت برافراشتند و پیامبرانشان با آیات و معجزات برای (هدایت) آنان آمدند (لیکن چون نپذیرفتند همه به کیفر کفر هلاک شدند). و خدا درباره آنها هیچ ستم نکرد بلکه آنها خود در حق خویش ستم می‌کردند.'),
(3419, 30, 10, 'آخر سرانجام کار آنان که بسیار به اعمال زشت و کردار بد پرداختند این شد که آیات خدا را تکذیب و تمسخر کردند (زیرا معصیت، دل را تاریک کند و چون بسیار شود به ظلمت کفر انجامد).'),
(3420, 30, 11, 'خداست که خلق را نخست (از عدم) پدید آرد و باز (پس از مرگ و فنا) باز گرداند و همه به حضرت او بازگردانیده می‌شوید.'),
(3421, 30, 12, 'و روزی که ساعت قیامت بر پا شود بدکاران نومید و اندوهگین شوند.'),
(3422, 30, 13, 'و هیچ شفیع و مددکاری بر خود از آنان که شریک حق گرفتند نمی‌یابند بلکه به آن شریکان و خدایان باطل کافر می‌شوند.'),
(3423, 30, 14, 'و روزی که ساعت قیامت بر پا شود در آن روز خلایق (بر حسب مراتب طاعت و معرفت و کفر و عصیان) فرقه فرقه شوند.'),
(3424, 30, 15, 'اما آن فرقه که ایمان آوردند و به نیکوکاری پرداختند مسرور (و محترم) به باغ بهشت منزل گیرند.'),
(3425, 30, 16, 'و اما آن فرقه که کافر شدند و آیات ما و دیدار آخرت را تکذیب کردند آنان را (برای کیفر) در عذاب دوزخ حاضر کنند.'),
(3426, 30, 17, 'پس خدا را هنگام شام و صبحگاه (در نماز مغرب و صبح) تسبیح و ستایش گویید.'),
(3427, 30, 18, 'و سپاس (اهل عالم) در آسمانها و زمین مخصوص اوست و شما نیز در تاریکی شب و نیمه روز (در نماز عشاء و ظهر و عصر) به ستایش او مشغول شوید.'),
(3428, 30, 19, '(آن خدایی را تسبیح و ستایش گویید که) زنده را از مرده و مرده را از زنده بیرون آورد (یعنی آدمی را از نطفه و نطفه را از آدمی برانگیزد) و زمین را پس از (فصل خزان و) مرگ (گیاهان باز به نسیم جانبخش بهارش) زنده گرداند، و همین گونه شما را هم (پس از مرگ زنده کنند و از خاک) بیرون آرند.'),
(3429, 30, 20, 'و یکی از نشانه‌های (قدرت) او این است که (پدر) شما آدمیان را از خاک خلق کرد سپس (به توالد) بشری (منشعب و نژادهایی مختلف) شدید که (در همه روی زمین) منتشر گشتید.'),
(3430, 30, 21, 'و باز یکی از آیات (لطف) او آن است که برای شما از جنس خودتان جفتی بیافرید که در کنار او آرامش یافته و با هم انس گیرید و میان شما رأفت و مهربانی برقرار فرمود. در این امر نیز برای مردم بافکرت ادله‌ای (از علم و حکمت حق) آشکار است.'),
(3431, 30, 22, 'و یکی از آیات (قدرت) او خلقت آسمانها و زمین است و یکی دیگر اختلاف زبانها و رنگهای شما آدمیان، که در این امور نیز ادله‌ای (از صنع و حکمت حق) برای دانشمندان عالم آشکار است.'),
(3432, 30, 23, 'و یکی از آیات او همین است که شما در شب و روز به خواب رفته (و بیدار می‌شوید) و از فضل خدا (یعنی روزی) طلب می‌کنید. در این امر هم ادله‌ای (از قدرتش) برای قومی که (سخن حق) بشنوند پدیدار است.'),
(3433, 30, 24, 'و یکی از آیات او آن است که (رعد و) برق را به شما می‌نمایاند که هم شما را (از صاعقه عذاب) می‌ترساند و هم (به باران رحمت خود) امیدوار می‌گرداند، و نیز از آسمان باران می‌فرستد تا زمین را پس از مرگش باز زنده گرداند. در این امر نیز ادله‌ای (از قدرت ایزد) برای اهل خرد آشکار است.'),
(3434, 30, 25, 'و یکی از آیات (قدرت) او آنکه آسمان و زمین به فرمان او برپاست، سپس که شما را از خاک برخواند (و زنده گرداند) همگی سر از قبرها بیرون می‌آورید.'),
(3435, 30, 26, 'و هر کس در آسمانها و زمین است همه بنده و مملوک او و باکمال خضوع و خشوع مطیع فرمان اوست.'),
(3436, 30, 27, 'و او خدایی است که نخست خلایق را بیافریند و آن‌گاه (به عالم معاد) باز گرداند و این اعاده (مثل ایجاد) بر او بسیار سهل و آسان است و آن ذات یکتا را در آسمانها و زمین مظهر و مثالی عالی‌تر (از حد وصف و تعقل) است و مقتدر کامل و دانا (به حقایق امور) تنها اوست.'),
(3437, 30, 28, 'خدا برای (هدایت) شما هم از عالم خود شما مثالی زد: آیا هیچ یک از غلام و کنیزان ملکی شما در آنچه (از مال و حقوق و مقام) که ما روزی شما گردانیدیم با شما شریک هستند تا شما و آنها در آن چیز بی‌هیچ مزیت مساوی باشید و همانقدر بیمی که شما از یکدیگر دارید هم از آنان دارید؟ (هرگز ندارید و هیچ آنها را شریک و مساوی با خود نمی‌دانید پس چگونه مخلوقات مملوک خدا را شریک خدا گرفته و معبود خود می‌گردانید؟) ما چنین مفصل و روشن آیات خود را برای مردم با عقل و هوش بیان می‌کنیم.'),
(3438, 30, 29, 'بلکه مردم ستمکار (مشرک) هوای نفس خود را از جهل و نادانی پیروی کردند، پس آن را که خدا (پس از اتمام حجت) به گمراهی واگذارد که می‌تواند هدایت کند؟ و آن گمراهان ستمکار را (در قیامت) هیچ یار و یاوری نخواهد بود.'),
(3439, 30, 30, 'پس تو مستقیم روی به جانب آیین پاک اسلام آور در حالی که از همه کیشها روی به خدا آری، و پیوسته از طریقه دین خدا که فطرت خلق را بر آن آفریده است پیروی کن که هیچ تغییری در خلقت خدا نباید داد، این است آیین استوار حق، و لیکن اکثر مردم (از حقیقت آن) آگاه نیستند.'),
(3440, 30, 31, '(شما اهل ایمان با پیروی همین دین فطرت) به درگاه خدا باز آیید و خدا ترس باشید و نماز به پا دارید و هرگز از فرقه مشرکان نباشید.'),
(3441, 30, 32, 'از آنان که دین (فطرت) خود را متفرق و پراکنده ساختند و فرقه فرقه شدند و هر گروهی به آنچه دارند (از اوهام باطل و عقیده و خیالات فاسد خود) دلشادند.'),
(3442, 30, 33, 'و مردم (عادتشان این است که) هر گاه رنج و المی سخت به آنها رسد در آن حال خدای خود را به دعا می‌خوانند و به درگاه او با تضرع و اخلاص روی می‌کنند و پس از آنکه خدا به آنها رحمت خود را چشانید (و از آن سختی نجاتشان داد) آن‌گاه باز گروهی از آنها به خدای خود مشرک می‌شوند.'),
(3443, 30, 34, 'تا نعمتی را که به آنها عطا کردیم کفران کنند. (باری، ای کافران ناسپاس) اینک (به هوا و هوس) تمتّع برید که به زودی به (کیفر اعمال خود) آگاه می‌شوید.'),
(3444, 30, 35, 'مگر ما دلیل و حجتی فرستادیم که درباره شرک و دعوت به بت پرستی ایشان سخن گوید (و برهان بر صحت شرک آورد).'),
(3445, 30, 36, 'و مردم (بر این عادتند که) هرگاه ما رحمتی به آنها چشانیم شاد شده و اگر رنج و بلایی از کرده خودشان ببینند در آن حال (به جای توبه به درگاه خدا، از رحمتش به کلی) نومید می‌شوند.'),
(3446, 30, 37, 'آیا ندیدند که همانا خدا هر که را خواهد وسیع روزی کند و هر که را خواهد تنگ روزی گرداند؟ همانا در این امر ادله روشنی (از حکمت الهی) برای اهل ایمان پدیدار است.'),
(3447, 30, 38, 'پس حقوق ارحام و خویشان و مسکینان و در راه ماندگان را ادا کن که این برای آنان که مشتاق لقای خدا هستند بهترین کار است و هم اینان (که نیکی کنند) رستگاران عالمند.'),
(3448, 30, 39, 'و آن سودی که شما به رسم ربا دادید که بر اموال مردم (ربا خوار) بیفزاید (و یا هدیه به اغنیاء دهید تا خود نفع زیاد دنیوی برید) نزد خدا هرگز نیفزاید (بلکه محو و نابود شود) و آن زکاتی که از روی شوق و اخلاص به خدا (به فقیران) دادید (ثوابش چندین برابر شود و) همین زکات دهندگان هستند که (نزد حق ثواب و برکات و دارایی) چند برابر دارند.'),
(3449, 30, 40, 'خداست آن کسی که شما را خلق کرده و روزی بخشیده سپس بمیراند و باز (در قیامت) زنده گرداند، آیا آنان را که شریک خدا دانید هیچ از این کارها توانند کرد؟ خدا از آنچه به او شریک گیرند پاک و منزه‌تر و بالاتر است.'),
(3450, 30, 41, 'فساد و پریشانی به کرده بد خود مردم در همه برّ و بحر زمین پدید آمد تا خدا هم کیفر بعضی اعمالشان را به آنها بچشاند، باشد که (از گنه پشیمان شده و به درگاه خدا) باز گردند.'),
(3451, 30, 42, 'بگو که در زمین سیر کنید پس بنگرید تا عاقبت طوایف پیش از خود که اکثرشان (کافر و) مشرک بودند چه شد؟'),
(3452, 30, 43, 'پس تو روی به دین استوار (اسلام و خدا پرستی) آور پیش از آنکه روزی بیاید که هیچ کس نتواند از امر خدا آن را برگرداند (و) در آن روز (بزرگ قیامت) خلایق فرقه فرقه شوند (گروهی دوزخی و گروهی بهشتی گردند).'),
(3453, 30, 44, 'هر که به راه کفر (و عصیان) رود زیان کفرش بر خود اوست و هر که صالح و نیکوکار شود چنین کسانی برای شخص خود (در بهشت) آسایشگاهی خوش فراهم می‌سازند.'),
(3454, 30, 45, 'تا خدا از فضل و کرم به آنان که ایمان آورده و نیکوکار شدند پاداش دهد، که او کافران را دوست ندارد.'),
(3455, 30, 46, 'و از جمله آیات (قدرت) او آن است که بادهای بشارت آور می‌فرستد (تا شما را مژده آرد) و تا شما را به چیزی از رحمت (بی‌انتهای) خود بهره‌مند گرداند و تا کشتی به فرمان او روان گردد و تا از فضل و کرمش (انواع نعمتها) تحصیل کنید، و باشد که شکر (نعمتش) به جای آرید.'),
(3456, 30, 47, 'و ما پیش از تو پیمبرانی به سوی قومشان فرستادیم و آنها معجزات و ادله روشن بر آنان آوردند، پس از کافران بدکار انتقام کشیدیم، و بر ما نصرت و یاری اهل ایمان حتم است.'),
(3457, 30, 48, 'خدا آن کسی است که بادها را می‌فرستد که ابر را در فضا برانگیزد پس به هر گونه که مشیّتش تعلق گیرد در اطراف آسمان متصل و منبسط کند و باز متفرق گرداند، آن گاه باران را بنگری که قطره قطره از درونش بیرون ریزد، و چون آن را به (کشتزار و صحرای) هر قومی از بندگانش که بخواهد فرو بارد به یک لحظه آن قوم مسرور و شادمان گردند.'),
(3458, 30, 49, 'و همانا پیش از آنکه باران (رحمت حق) بر آنان ببارد به حال یأس و نومیدی می‌زیستند.'),
(3459, 30, 50, 'پس (ای بشر دیده باز کن و) آثار رحمت (نامنتهای) الهی را مشاهده کن که چگونه زمین را پس از مرگ (و دستبرد خزان باز به نفس باد بهار) زنده می‌گرداند! محققا همان خداست که مردگان را هم (پس از مرگ) باز زنده می‌کند و او بر همه امور عالم تواناست.'),
(3460, 30, 51, 'و اگر باز بادی فرستیم که آن کشت سبز را زرد و پژمرده بنگرند (همه آن نعمتهای گذشته را فراموش کنند و) به کفر (و کفران نعمت حق) بر می‌گردند.'),
(3461, 30, 52, 'پس تو (این مردم) مرده (دل بی‌ایمان) را نتوانی (سخن حقّ) بشنوانی و دعوت خود را به گوش (این) کران که (مخصوصا از تو به کبر و نخوت) رو می‌گردانند برسانی.'),
(3462, 30, 53, 'و تو مردمی را که کور (دل و کافر) هستند نتوانی از ضلالتشان به راه هدایت آری، تنها تو آنان را که به آیات ما ایمان می‌آورند و در پی آن ایمان تسلیم امر ما شوند توانی (هدایت کنی و سخن خدا را) به گوش هوششان برسانی.'),
(3463, 30, 54, 'خدا آن کسی است که شما را در ابتدا از جسم ضعیف (نطفه) بیافرید آن گاه پس از ضعف و ناتوانی (کودکی) توانا کرد و باز از توانایی (و قوای جوانی) به ضعف و سستی و پیری بر گردانید، که او هر چه بخواهد و مشیّتش تعلق گیرد می‌آفریند، و او دانا و تواناست.'),
(3464, 30, 55, 'و روزی که ساعت قیامت بر پا شود بدکاران قسم یاد کنند که (در دنیا و قبر و برزخ) ساعتی بیش درنگ نکردند. همین گونه (عادتشان از دیرین بود که) از راستی و حقیقت به ناراستی و دروغ می‌پرداختند.'),
(3465, 30, 56, 'و آنان که مقام علم و ایمان داده شده‌اند (به آن فرقه بدکار) گویند: شما تا روز قیامت که هم امروز است در کتاب خدا (یعنی عالم علم خدا) مهلت یافتید و لکن بر آن آگاه نبودید.'),
(3466, 30, 57, 'پس در آن روز آنان که (در دنیا به خود و به خلق خدا) ستم کردند عذرشان مفید نیفتد و از آنان نخواهند که توبه و عذرخواهی کنند.'),
(3467, 30, 58, 'البته ما در این قرآن (عظیم) برای (هدایت) مردم هر گونه مثل زدیم و اگر تو بر این مردم هر گونه معجز و آیتی بیاوری باز کافران محققا (از روی عناد) خواهند گفت: شما (مسلمین و رسولتان خلق را) به باطل و اوهام می‌خوانید.'),
(3468, 30, 59, 'این چنین خدا بر دلهای اهل جهل (پس از اتمام حجت) مهر (شقاوت) نهد (که هیچ سخن حق را نپذیرند).'),
(3469, 30, 60, 'پس تو صبر پیشه کن، که وعده خدا البته حق و حتمی است و مراقب باش که مردم بی‌علم و یقین و ایمان (مقام حلم و وقار) تو را به خفت و سبکی نکشانند..');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الم (این حروف گفته شد که اسرار الهی است).'),
(3471, 31, 2, 'این (قرآن عظیم و) آیات کتاب حکیم است.'),
(3472, 31, 3, 'که هدایت و رحمت است برای نیکوکاران عالم.'),
(3473, 31, 4, 'آنان که نماز به پا می‌دارند و زکات می‌دهند و به عالم آخرت کاملا یقین دارند.'),
(3474, 31, 5, 'هم آنان از (لطف) پروردگار خویش به راه راستند و هم آنان رستگاران عالمند.'),
(3475, 31, 6, 'و برخی از مردمان (فاسد فتنه انگیز مانند نضر حارث) کسی است که گفتار و سخنان لغو و باطل را (مانند قصّه‌های دروغ و افسانه‌های شهوت‌انگیز مفسد اخلاق و سرود مطرب) به هر وسیله تهیّه می‌کند تا (خلق را) به جهالت از راه خدا (و آموختن علوم و معارف قرآن) گمراه سازد و آیات قرآن را به تمسخر و استهزا گیرد، این مردمان (فاسد کافر) به عذاب، با خواری و ذلّت گرفتار شوند.'),
(3476, 31, 7, 'و هر گاه بر او تلاوت آیات ما شود چنان با غرور و تکبّر پشت گرداند که گویی هیچ آن آیات الهی را نشنید، پنداری از هر دو گوش کر است، این کس را به عذاب دردناک بشارت ده.'),
(3477, 31, 8, 'آنان که به خدا ایمان آوردند و به اعمال نیکو پرداختند باغهای پر نعمت (بهشت جاودانی) مخصوص آنهاست.'),
(3478, 31, 9, 'که در آن بهشت ابد همیشگی خواهند بود، این وعده خدا محقّق و حتمی است، و او مقتدر و آگاه است.'),
(3479, 31, 10, '(سقف رفیع) آسمانها را بی‌ستونی که به حسّ مشاهده کنید خلق کرده و کوههای بزرگ را در زمین بنهاد تا شما را نجنباند و در روی زمین انواع مختلف حیوانات را منتشر و پراکنده ساخت، و هم از آسمان آب باران فرود آوردیم و به آن آب در روی زمین نباتات پرفایده برویانیدیم.'),
(3480, 31, 11, 'این همه آفریده خداست، حال شما (مشرکان) بگویید آنان که به جز خدا (معبود شما) هستند در جهان چه آفریده‌اند؟ (هیچ نیافریده‌اند) بلکه ستمکاران (مشرک که آنها را شریک خدا گرفتند) دانسته در گمراهی آشکار هستند.'),
(3481, 31, 12, 'و ما به لقمان مقام (علم و) حکمت عطا کردیم (و فرمودیم) که (بر این نعمت بزرگ) خدا را شکر کن، و هر کس شکر حق گوید به نفع خود اوست و هر که ناسپاسی و کفران کند خدا (از شکر و سپاس خلق) بی‌نیاز و (به ذات خود) ستوده صفات است.'),
(3482, 31, 13, 'و (یاد کن) وقتی که لقمان در مقام پند و موعظه به فرزندش گفت: ای پسر عزیزم، هرگز شرک به خدا نیاور که شرک بسیار ظلم بزرگی است.'),
(3483, 31, 14, 'و ما به هر انسانی سفارش کردیم که در حقّ پدر و ما در خود نیکی کن خصوص مادر که چون بار حمل فرزند برداشته و تا مدّت دو سال که طفل را از شیر باز گرفته (هر روز) بر رنج و ناتوانیش افزوده است، (و فرمودیم که) شکر من و شکر پدر و مادرت بجای آور، که بازگشت (خلق) به سوی من خواهد بود.'),
(3484, 31, 15, 'و اگر پدر و مادر تو را بر شرک به خدا که آن را به حق نمی‌دانی وادار کنند در این صورت دیگر آنها را اطاعت مکن و لیک در دنیا با آنها به حسن خلق مصاحبت کن و از راه آن کس که به درگاه من رجوع و انابه‌اش بسیار است پیروی کن، که (پس از مرگ) رجوع شما به سوی من است و من شما را به پاداش اعمالتان آگه خواهم ساخت.'),
(3485, 31, 16, 'ای فرزندم، خدا اعمال بد و خوب خلق را گر چه به مقدار خردلی در میان سنگی یا در (طبقات) آسمان‌ها یا زمین پنهان باشد همه را (در محاسبه) می‌آورد، که خدا توانا و آگاه است.'),
(3486, 31, 17, 'ای فرزند عزیزم، نماز را به پا دار و امر به معروف و نهی از منکر کن و (بر این کار از مردم نادان) هر آزار بینی صبر پیش گیر، که این نشانه‌ای از عزم ثابت (مردم بلند همّت) در امور عالم است.'),
(3487, 31, 18, 'و هرگز به تکبّر و ناز از مردم (اهل نیاز) رخ متاب و در زمین با غرور و تبختر قدم بر مدار، که خدا هرگز مردم متکبر خودستا را دوست نمی‌دارد.'),
(3488, 31, 19, 'و در رفتارت میانه روی اختیار کن و سخن آرام گو (نه با فریاد بلند) که زشت‌ترین صداها صوت الاغ است.'),
(3489, 31, 20, 'آیا شما مردم به حسّ مشاهده نمی‌کنید که خدا انواع موجوداتی که در آسمانها و زمین است برای شما مسخّر کرده و نعمت‌های ظاهر و باطن خود را برای شما فراوان فرموده؟ و (با وجود این) برخی از مردم از روی جهل و گمراهی و بی‌خبری از کتاب روشن (حق) در (دین) خدای متعال مجادله می‌کنند.'),
(3490, 31, 21, 'و چون به این مردم (اهل جدل و عناد) گویند که بیایید از کتابی که خدا فرستاده پیروی کنید، جواب دهند ما تنها از طریقی که پدران خود را بر آن یافته‌ایم پیروی می‌کنیم. آیا هر چند آنان را شیطان به آتش دوزخ بخواند (باز پیرو آنها می‌شوند).'),
(3491, 31, 22, 'و هر کس روی تسلیم و رضا به سوی خدا آرد و نکوکار باشد چنین کس به محکم‌ترین رشته الهی چنگ زده است، و (بدانید که) پایان کارها به سوی خداست.'),
(3492, 31, 23, '(ای رسول ما) تو از کفر هر که کافر شود محزون مباش، که رجوعشان به سوی ماست و ما آنان را سخت از (کیفر) کردارشان آگه می‌سازیم، که همانا خدا از اسرار دلهای خلق هم آگاه است.'),
(3493, 31, 24, 'اندک زمانی آن کافران را (به لذّات فانی دنیا) برخوردار می‌سازیم آن گاه (بعد از مرگ) به عذاب سختشان به ناچار گرفتار خواهیم کرد.'),
(3494, 31, 25, 'و اگر از این کافران سؤال کنی آن کیست که آسمانها و زمین را آفریده؟ البتّه جواب دهند خداست. باز گو: ستایش خدای را (که کافران نیز معترفند) بلکه اکثر آنها (بر این حقیقت که آفریننده جهان خداست اگر به زبان گویند به دل) آگاه نیستند.'),
(3495, 31, 26, 'هر چه در آسمان و زمین است همه ملک خداست، که خدا ذاتش بی‌نیاز و اوصافش همه پسندیده است.'),
(3496, 31, 27, 'و اگر هر درخت روی زمین (در کف نویسندگان عالم) قلم شود و آب دریا به اضافه هفت دریای دیگر مرکب گردد باز نگارش کلمات خدا (که موجودات بی‌نهایت کتاب آفرینش است) ناتمام بماند، که همانا خدا را اقتدار بی‌نهایت و حکمت بی‌پایان است.'),
(3497, 31, 28, 'خلقت و بعثت همه شما (خلایق بی‌شمار در آسانی بر خدا) جز مثل یک تن واحد نیست، که همانا خدا شنوا و بیناست.'),
(3498, 31, 29, 'آیا (ای رسول) ندیدی (و ای مردم به حسّ مشاهده نمی‌کنید) که خدا شب را در روز و روز را در شب داخل کند و خورشید و ماه را مسخّر ساخته که هر یک تا وقت معیّنی (و به نظم ثابت مخصوصی) گردش کنند؟ و (نمی‌دانید که) خدا به هر چه می‌کنید آگاه است؟'),
(3499, 31, 30, 'این قدرت کامل بدین سبب است که خدای یکتا حقّ مطلق و آنچه به جز او به خدایی می‌خوانند همه باطل محض است و تنها خدا (که حقّ و وجود حقیقی است) بلند مرتبه و بزرگوار است.'),
(3500, 31, 31, 'آیا نمی‌بینی که چگونه کشتی به دریا به لطف و احسان خدا اسیر می‌کند تا به شما بعضی از آیات قدرتش را بنمایاند؟ همانا در این کار آیاتی (از عجایب حکمتهای خدا) البتّه بر هر شخص صبور (در بلا) و شکر گزار (در نعمت) پدیدار است.'),
(3501, 31, 32, 'و هرگاه که (دریا طوفانی شود و) موجی مانند کوهها آنها را فرو گیرد در آن حال خدا را با عقیده پاک و اخلاص کامل در دین می‌خوانند و چون باز به ساحل نجاتشان رساند بعضی بر سبیل قصد (طاعت و شکر خدا) باقی مانند (و بعضی به کفر شتابند) و آیات ما را انکار نمی‌کند جز آن کس که غدّار و عهد شکن و کافر و ناسپاس است.'),
(3502, 31, 33, 'ای مردم، از خدا بترسید و بیندیشید از آن روزی که نه هیچ پدری ذره‌ای به کار فرزند آید و نه هیچ فرزندی ذره‌ای به کار پدر آید، البتّه وعده خدا حق و حتمی است، پس زنهار شما را زندگانی دنیا فریب ندهد و شیطان فریبنده شما را از (عقاب) خدا (به عفو و کرمش) سخت مغرور نگرداند.'),
(3503, 31, 34, 'همانا علم ساعت (قیامت) نزد خداست و او باران را فرو بارد و او آنچه (از نر و ماده و زشت و زیبا) که در رحمهای آبستن است می‌داند و هیچ کس نمی‌داند که فردا (از سود و زیان) چه خواهد کرد و هیچ کس نمی‌داند که به کدام سرزمین مرگش فرا می‌رسد (و به خاک می‌رود)، که تنها خدا دانا و آگاه است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الم (از اسرار یا متشابهات یا اشاره به اسماء الهی یا نام این سوره قرآن است).'),
(3505, 32, 2, 'تنزیل این کتاب بزرگ (قرآن) بی هیچ شک از جانب پروردگار عالمیان است.'),
(3506, 32, 3, 'بلکه کافران می‌گویند قرآن را او از پیش خود (بی‌مدد وحی خدا) فرابافته. (چنین نیست) بلکه آن به حقّ و راستی از جانب خدا فرستاده شده تا تو قومی را که رسولی پیش از تو به سوی آنان نیامده (از معصیت خدا) بترسانی، باشد که راه هدایت پیش گیرند.'),
(3507, 32, 4, 'خدا آن کسی است که آسمانها و زمین و هر چه در بین آنهاست همه را در مقدار شش روز بیافرید، آن گاه بر عرش (فرمانروایی) قرار گرفت، شما را غیر او هیچ (در عالم) یار و یاور و شفیع و مددکاری نیست، آیا تذکر نمی‌یابید؟'),
(3508, 32, 5, 'اوست که امر عالم را (به نظام احسن و اکمل) از آسمان تا زمین تدبیر می‌کند سپس در روزی که مقدارش به حساب شما بندگان هزار سال است باز (حقایق و ارواح) به سوی او بالا می‌رود.'),
(3509, 32, 6, 'این است همان خدایی که بر غیب و شهود عالم دانا و (بر جهانیان) مقتدر و مهربان است.'),
(3510, 32, 7, 'آن خدایی که هر چیز را به نیکوترین وجه خلقت کرد و آدمی را نخست از خاک (پست بدین حسن و کمال) بیافرید.'),
(3511, 32, 8, 'آن‌گاه خلقت نژاد نوع او را از چکیده‌ای از آب بی‌قدر (و نطفه بی‌حس) مقرر گردانید.'),
(3512, 32, 9, 'سپس آن (نطفه بی‌جان) را نیکو بیاراست و از روح (قدسی الهی) خود در آن بدمید، و شما را دارای گوش و چشمها و قلبها (با حسّ و هوش) گردانید، باز بسیار اندک شکر و سپاسگزاری نعمتهای حق می‌کنید.'),
(3513, 32, 10, 'کافران (از روی تعجب و انکار) گویند که آیا پس از آنکه ما در زمین (خاک و در خاک) نابود و گم شدیم باز از نو زنده خواهیم شد؟! (بلی البتّه زنده خواهید شد) بلکه آنها به شهود و لقاء خدایشان کافرند.'),
(3514, 32, 11, 'بگو: فرشته مرگ که مأمور قبض روح شماست جان شما را خواهد گرفت و پس از مرگ به سوی خدای خود بازگردانیده می‌شوید.'),
(3515, 32, 12, 'و اگر تو حال بدکاران را (در قیامت) ببینی که چگونه در حضور خدای خود سر به زیر و ذلیلند (و با آه و ناله حسرت) گویند: پروردگارا، اینک (عذاب تو را) به چشم دیدیم و (سخن رسولانت را) به گوش گرفتیم، پس ما را (به دنیا) باز گردان تا صالح و نیکوکار شویم که اکنون (وعده تو را) به چشم یقین مشاهده کردیم!'),
(3516, 32, 13, 'و اگر ما (به مشیّت ازلی) می‌خواستیم، هر نفسی را به کمال هدایتش (به جبر و الزام) می‌رسانیدیم و لیکن وعده حقّ و حتمی من است که دوزخ را البته از همه (کافران) جنّ و انس پر سازم.'),
(3517, 32, 14, 'پس (خطاب رسد: امروز) شما ای کافران (عذاب را) به کیفر غفلت از ملاقات این روزتان بچشید که ما هم شما را فراموش کردیم (یعنی از نظر لطف انداختیم) و اینک عذاب ابدی را به پاداش اعمال زشتی که می‌کردید بچشید.'),
(3518, 32, 15, 'تنها کسانی به آیات ما ایمان می‌آورند که چون متذکّر آیات ما شوند به سجده رخ بر خاک نهند و تسبیح و تنزیه و ستایش پروردگار کنند و ابدا به کبر و نخوت سر از فرمان حق نکشند.'),
(3519, 32, 16, '(شبها) پهلو از بستر خواب تهی کنند و با بیم و امید (و ناله اشتیاق در نماز شب) خدای خود را بخوانند و آنچه روزی آنها کرده‌ایم (به مسکینان) انفاق کنند.'),
(3520, 32, 17, 'پس هیچ کس نمی‌داند که به پاداش نیکوکاریشان چه نعمت و لذّتهای بی‌نهایت که روشنی‌بخش (دل و) دیده است در عالم غیب برایشان ذخیره شده است.'),
(3521, 32, 18, 'آیا آن کس که ایمان آورده (حالش در قیامت) مانند کسی است که کافر بوده؟ هرگز یکسان نخواهند بود.'),
(3522, 32, 19, 'اما آنان که (در دنیا) با ایمان و نیکو کار بودند به پاداش اعمال صالح منزلگاه پر نعمت در بهشت ابد یابند.'),
(3523, 32, 20, 'و اما آنان که سر از طاعت حق کشیده و فاسق شدند منزلگاهشان در آتش دوزخ است و هر چه کوشند و خواهند که از آن آتش بیرون آیند باز (فرشتگان عذاب) بدان دوزخ برگردانندشان و گویند: عذاب آتشی را که (در دنیا) تکذیب می‌کردید اینک بچشید.'),
(3524, 32, 21, 'و البته ما کافران را عذاب نزدیک‌تر (و کمتر به بلاهای دنیا یا در برزخ) بچشانیم غیر آن عذاب بزرگتر و شدیدتر (قیامت یا قیام حجّت عصر علیه السّلام) تا مگر (متنبّه و پشیمان از کفر و عصیان شوند و به سوی خدا) باز گردند.'),
(3525, 32, 22, 'و کدامین کس در عالم از آن کس که به آیات خدا پند و تذکّرش دادند و باز اعراض کرد ستمکارتر است؟ البته ما از بدکاران انتقام خواهیم کشید.'),
(3526, 32, 23, 'و همانا ما به موسی کتاب (تورات) را عطا کردیم پس تو در ملاقات او (یعنی ملاقات موسی با خدا در قیامت یا شب معراج با تو) هیچ شک مدار، و ما او را سبب هدایت بنی اسرائیل قرار دادیم.'),
(3527, 32, 24, 'و برخی از آن بنی اسرائیل را امام و پیشوایانی که خلق را به امر ما (نه به امر خلق) هدایت کنند قرار دادیم برای آنکه (در راه حق) صبر کردند و در آیات ما مقام یقین یافتند.'),
(3528, 32, 25, 'البته خدای تو به روز قیامت در آنچه این مردم (از آیات و احکام الهی) اختلاف کلمه می‌انگیختند حکم خواهد کرد.'),
(3529, 32, 26, 'آیا آن طوایف و قبایلی را که ما پیش از این مردم کنونی هلاک کردیم و اینان اینک در دیارشان به جای آنها رفت و آمد می‌کنند هلاک آنها موجب هدایت و عبرت اینان نگردید؟ البته در این هلاک پیشینیان آیاتی (از پند و عبرت خلق) است، آیا باز هم سخن نمی‌شنوند؟'),
(3530, 32, 27, 'آیا ندیدند که ما آب باران را به سوی زمین خشک بی‌آب و گیاه می‌بریم آن گاه حاصلی که خود و چهارپایانشان از آن بخورند می‌رویانیم؟ آیا باز هم چشم بصیرت نمی‌گشایند (تا لطف رحمت واسع خدا را به خلق بنگرند).'),
(3531, 32, 28, 'و کافران می‌گویند: این فتح و گشایش (یعنی روز قیامت یا فتح مکه به دست مسلمین که وعد و وعید می‌دهید) کی خواهد بود اگر راست می‌گویید؟'),
(3532, 32, 29, 'بگو که (امروز باید ایمان آرید که) روز فتح، ایمان آنان که کافر بودند سود نبخشد و آنها را مهلت ندهند.'),
(3533, 32, 30, 'پس از آن کافران اعراض و دوری کن و منتظر (فتح و فیروزی خدا) باش، که آنها هم منتظر (قوّت خویش و ضعف و شکست تو) هستند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ای پیغمبر (گرامی) همیشه خدا ترس و پرهیزکار باش و هرگز تابع رأی کافران و منافقان مباش که همانا خدا دانا و حکیم است.'),
(3535, 33, 2, 'و تنها از آنچه به تو از جانب خدایت وحی می‌شود پیروی کن (و بدان و به امت بگو) که خدا به هر چه می‌کنید کاملا آگاه است.'),
(3536, 33, 3, 'و در کارها بر خدا توکّل کن، و تنها خدا برای مدد و نگهبانی کفایت است.'),
(3537, 33, 4, 'خدا در درون یک مرد دو قلب (و دو مبدأ ادراک و اراده) قرار نداده و نیز زنانتان را که مادر بخوانید مادر شما و پسر دیگری را که فرزند بخوانید پسر شما قرار نداده، این گفتار شما زبانی و غیر واقع است، و خدا سخن به حقّ می‌گوید و اوست که (شما را) به راه حقیقت راهنمایی می‌کند.'),
(3538, 33, 5, 'شما پسر خوانده‌ها را به پدرانشان نسبت دهید که این نزد خدا به عدل و راستی نزدیکتر است و اگر پدرانشان را نشناسید (باز فرزند و ارث‌بر شما نیستند بلکه) در دین برادران و یاران شما هستند، و در کار ناشایسته‌ای که به خطا کنید بر شما گناهی نیست لیکن آن کار زشتی که به عمد و با اراده قلبی می‌کنید بر آن گناه مؤاخذه می‌شوید، و (اگر از این هم پشیمان شوید و توبه کنید باز) خدا بسیار آمرزنده و مهربان است.'),
(3539, 33, 6, 'پیغمبر اولی و سزاوارتر به مؤمنان است از خود آنها (یعنی مؤمنان باید حکم و اراده او را مقدم بر اراده خود بدارند و از جان و مال در اطاعتش مضایقه نکنند) و زنان او (در اطاعت و عطوفت و حرمت نکاح، به حکم) مادران مؤمنان هستند و خویشاوندان نسبی شخص (در حکم ارث) بعضی بر بعضی دیگر در کتاب خدا مقدمند از انصار و مهاجرین (که با هم عهد برادری بسته‌اند) مگر آنکه به نیکی و احسان بر دوستان خود (از مهاجر و انصار) وصیتی کنید که این (تقدم وصیت بر ارث خویشان) هم در کتاب حق مسطور گردیده است.'),
(3540, 33, 7, 'و (یادآر) آن گاه که ما از پیغمبران عهد و میثاق گرفتیم و هم از تو و از نوح و ابراهیم و موسی و عیسی بن مریم از همه پیمان محکم گرفتیم (که با هر مشقّت و زحمتی است رسالت خدا را به خلق ابلاغ کنند).'),
(3541, 33, 8, 'تا راستگویان عالم (یعنی انبیاء و مؤمنان امت) را از حقیقت و صدق ایمانشان باز پرسد (و آنها را پاداش کامل تبلیغ رسالت و عمل صالح دهد) و اهل کفر را (به کیفر تکذیب رسل) عذابی سخت دردناک مهیا کرده است.'),
(3542, 33, 9, 'ای اهل ایمان، به یاد آرید نعمتی را که خدا به شما عطا کرد وقتی که لشکر بسیاری (از کافران) بر شما حمله‌ور شدند، پس ما بادی تند و سپاهی بسیار (از فرشتگان) که به چشم نمی‌دیدید فرستادیم، و خدا خود به اعمال شما بینا بود.'),
(3543, 33, 10, '(یاد آرید) وقتی که (در جنگ احزاب) لشکر کفار از بالا و زیر بر شما حمله‌ور شدند و آن‌گاه که چشمها حیران شد و جانها به گلو رسید و به وعده خدا گمانهای مختلف بردید (مؤمنان حقیقی به وعده حق و فتح اسلام خوش گمان و دیگران بدگمان و ترسان بودند).'),
(3544, 33, 11, 'در آنجا مؤمنان امتحان شدند و (ضعیفان در ایمان) سخت متزلزل گردیدند.'),
(3545, 33, 12, 'و نیز در آن هنگام منافقان و آنان که در دلهاشان مرض (شک و ریب) بود (با یکدیگر) می‌گفتند: آن وعده (فتح و نصرتی) که خدا و رسول به ما دادند غرور و فریبی بیش نبود.'),
(3546, 33, 13, 'و در آن وقت طایفه‌ای از آن کفار و منافقان دین گفتند: ای یثربیان، دیگر شما را (در اردوگاه) جای ماندن نیست (که همه کشته خواهید شد، از گرد پیغمبر متفرق شوید و به مدینه) باز گردید. و در آن حال گروهی از آنها (برای رفتن) از پیغمبر اجازه خواسته و می‌گفتند: خانه‌های ما دیوار و حفاظی ندارد، در صورتی که (دروغ می‌گفتند و) خانه‌هاشان بی‌حفاظ نبود، و مقصودشان جز فرار (از جبهه جنگ) نبود.'),
(3547, 33, 14, 'و اگر دشمنان دین از اطراف به شهر و خانه‌شان (از پی غارت) هجوم آرند سپس از آنها تقاضای بازگشت به کفر و شرک کنند آنان اجابت خواهند کرد در صورتی که اندک زمانی بیش در مدینه (با آن شرک) زیست نخواهند کرد (و سپس همه هلاک می‌شوند).'),
(3548, 33, 15, 'و آن منافقان از این پیش (در جنگ احد که اکثر فرار کردند) با خدا عهد محکم بسته بودند که به جنگ پشت نکنند، و از عهد خدا پرسش خواهد شد.'),
(3549, 33, 16, '(ای رسول ما، منافقان را) بگو: اگر از مرگ یا قتل فرار می‌کنید آن فرار هرگز به نفع شما نیست چه آنکه اندک زمانی بیش (از زندگی) کامیاب نخواهید شد.'),
(3550, 33, 17, 'بگو: اگر خدا به شما اراده بلا و شری کند یا اراده لطف و مرحمتی فرماید آن کیست که شما را از اراده خدا منع تواند کرد؟ و هرگز خلق جز خدا هیچ یار و یاوری نخواهند یافت.'),
(3551, 33, 18, 'محققا خدا از حال آن مردم (منافق) که مسلمین را از جنگ می‌ترسانند و باز می‌دارند و به برادران (و طایفه) خود می‌گویند: با ما متفق باشید (نه با مؤمنان) به خوبی آگاه است، و آنها جز اندک زمانی (آن هم برای نفاق و ریا کاری) به جنگ حاضر نمی‌شوند.'),
(3552, 33, 19, 'آنها (به هر نوع کمک مالی و غیره) بر شما مؤمنان بخل می‌ورزند و هر گاه جنگ و خطری پیش آید آنها را چنان بینی که از شدت ترس با چشمی که از بیم دوران می‌زند به تو نگاه می‌کنند مانند کسی که از سختی مرگ حال بیهوشی به او دست دهد، و باز وقتی که جنگ و خطر بر طرف شد (و فتح و غنیمتی به دست آمد) سخت با زبان تند و گفتار خشن با کمال حرص و بخل شما را بیازارند (و مطالبه غنیمت کنند) اینان هیچ ایمان نیاورده‌اند، خدا هم اعمالشان را (چون همه ریاست) محو و نابود می‌گرداند و این بر خدا آسان است.'),
(3553, 33, 20, 'و آن منافقان گمان کنند که لشکرهای کافران هنوز از دور مدینه نرفته و شکست نخورده‌اند و اگر آن دشمنان بار دیگر بر علیه السلام لشکر کشند باز منافقان آرزوشان این است که در میان اعراب بادیه (از جنگ آسوده و برکنار) باشند و از اخبار جنگی شما جویا شوند، و اگر هم در میان (سپاه) شما در آیند جز اندکی (آن هم به ریا و نفاق) به جنگ نپردازند.'),
(3554, 33, 21, 'البته شما را به رسول خدا (چه در صبر و مقاومت با دشمن و چه دیگر اوصاف و افعال نیکو) اقتدایی نیکوست، برای آن کس که به (ثواب) خدا و روز قیامت امیدوار باشد و یاد خدا بسیار کند.'),
(3555, 33, 22, 'و مؤمنان چون لشکر و نیروهای کفار را به چشم دیدند گفتند: این همان (جنگی) است که خدا و رسول (از پیش) ما را وعده دادند و خدا و رسول راست گفتند، و این دیدار دشمن جز بر ایمان و تسلیمشان نیفزود.'),
(3556, 33, 23, 'برخی از آن مؤمنان، بزرگ مردانی هستند که به عهد و پیمانی که با خدا بستند کاملا وفا کردند، پس برخی پیمان خویش گزاردند (و بر آن عهد ایستادگی کردند تا به راه خدا شهید شدند مانند عبیده و حمزه و جعفر) و برخی به انتظار (فیض شهادت) مقاومت کرده و هیچ عهد خود را تغییر ندادند (مانند علی علیه السّلام که در کوفه به محراب عبادت شهید گشت).'),
(3557, 33, 24, 'تا آنکه خدا آن مردان راستگوی (با وفا) را از صدق ایمانشان پاداش نیکو بخشد و منافقان را (به عدل) عذاب کند اگر بخواهد یا (به لطف) توبه‌شان بپذیرد که خدا بسیار آمرزنده و مهربان است.'),
(3558, 33, 25, 'و خدا کافران را (در جنگ احزاب) با همان خشم و غضبی که (به مؤمنان) داشتند بی‌آنکه هیچ خیر و غنیمتی به دست آورند ناامید برگردانید و خدا امر جنگ را (به فرستادن باد صرصر و سپاه فرشتگان غیبی) از مؤمنان کفایت فرمود، و خدا بسیار توانا و مقتدر است.'),
(3559, 33, 26, 'و آن گروه اهل کتاب (از یهودان) را که پشتیبان و کمک مشرکان بودند خدا از حصار و سنگرهاشان فرود آورد و در دلشان (از شما مسلمین) ترس افکند تا آنکه گروهی از آنها را به قتل رسانیده و گروهی را اسیر می‌کردید.'),
(3560, 33, 27, 'و شما را وارث سرزمین و دیار و اموال آنها کرد و نیز سرزمینی را که هیچ بر آن قدم (به جنگ) ننهادید نصیب شما گردانید، و خدا بر هر چیز تواناست.'),
(3561, 33, 28, 'ای پیغمبر (گرامی) با زنان خود بگو که اگر شما زندگانی با زیب و زیور دنیا را طالبید بیایید تا من مهر شما را پرداخته و همه را به خوبی و خرسندی طلاق دهم.'),
(3562, 33, 29, 'و اگر طالب خدا و رسول و مشتاق دار آخرت هستید همانا خدا برای نیکوکاران از شما زنان (در قیامت) اجر عظیم آماده کرده است.'),
(3563, 33, 30, 'ای زنان پیغمبر، از شما هر که به کار ناروای آشکاری دانسته اقدام کند او را دو برابر دیگران عذاب کنند و این بر خدا سهل و آسان است.'),
(3564, 33, 31, 'و هر که از شما مطیع فرمان خدا و رسول باشد و نیکوکار شود پاداشش را دو بار عطا کنیم و برای او روزی بسیار نیکو (در بهشت ابد) مهیا سازیم.'),
(3565, 33, 32, 'ای زنان پیغمبر، شما مانند دیگر زنان نیستید (بلکه مقامتان رفیع‌تر است) اگر خدا ترس و پرهیزکار باشید، پس زنهار نازک و نرم (با مردان) سخن مگویید مبادا آن که دلش بیمار (هوا و هوس) است به طمع افتد (بلکه متین) و درست و نیکو سخن گویید.'),
(3566, 33, 33, 'و در خانه‌هایتان بنشینید و آرام گیرید (و بی‌حاجت و ضرورت از منزل بیرون نروید) و مانند دوره جاهلیت پیشین با آرایش و خود آرایی بیرون نیایید، و نماز به پا دارید و زکات (مال به فقیران) بدهید و از امر خدا و رسول اطاعت کنید. خدا چنین می‌خواهد که هر رجس و آلایشی را از شما خانواده (نبوت) ببرد و شما را از هر عیب پاک و منزه گرداند (ذیل آیه موافق اخبار شیعه و اهل سنت راجع به شخص پیغمبر و علی و فاطمه و حسنین علیهم السّلام است و اگر راجع به زنان پیغمبر بود بایستی ضمیر مؤنث-عنکنّ-ذکر شود و به سیاق جمل صدر آیه باشد).'),
(3567, 33, 34, 'و از آن همه آیات الهی و حکمتی که در خانه‌های شما تلاوت می‌شود متذکر شوید (و پند گیرید، و بدانید) که همانا خدا را (به خلق) لطف و مهربانی است و (به حال همه) آگاه است.'),
(3568, 33, 35, 'همانا کلیه مردان و زنان مسلمان و مردان و زنان با ایمان و مردان و زنان اهل طاعت و عبادت و مردان و زنان راستگوی و مردان و زنان صابر و مردان و زنان خدا ترس خاشع و مردان و زنان خیر خواه مسکین نواز و مردان و زنان روزه‌دار و مردان و زنان با حفاظ خوددار از تمایلات حرام و مردان و زنانی که یاد خدا بسیار کنند، بر همه آنها خدا مغفرت و پاداشی بزرگ مهیا ساخته است..'),
(3569, 33, 36, 'و هیچ مرد و زن مؤمنی را در کاری که خدا و رسول حکم کنند اراده و اختیاری نیست (که رأی خلافی اظهار نمایند) و هر کس نافرمانی خدا و رسول او کند دانسته به گمراهی سختی افتاده است (پیغمبر زینب دختر عمه‌اش را به زید، غلام آزاد کرده خود تزویج کرد و زینب گفت: من از اشراف قریشم غلامی را به شوهری نپذیرم. این آیه نازل شد و پذیرفت).'),
(3570, 33, 37, 'و (یاد کن) وقتی که با آن کس که خدایش نعمت اسلام بخشید و تواش نعمت آزادی (یعنی زید بن حارثه، به نصیحت) می‌گفتی برو زن خود را نگه دار و از خدا بترس (و طلاقش مده)، و آنچه در دل پنهان می‌داشتی (که زینب را بگیری و حرمت ازدواج با زن پسر خوانده را که در جاهلیّت بود منسوخ کنی) خدا می‌خواست آشکار سازد و تو از (مخالفت و سرزنش) خلق می‌ترسیدی و از خدا سزاوارتر بود بترسی. پس (بدین غرض) چون زید از آن زن کام دل گرفت (و طلاقش داد) او را ما به نکاح تو در آوردیم تا مؤمنان در نکاح زنان پسرخوانده خود که از آنها کامیاب شدند (و طلاق دادند) بر خویش حرج و گناهی نپندارند، و فرمان خدا انجام شدنی است.'),
(3571, 33, 38, 'پیغمبر را در حکمی که خدا (در نکاح زنان پسر خوانده) برای او مقرر فرموده گناهی نیست، سنّت الهی در میان آنان که در گذشتند هم این است (که انبیاء را توسعه در امر نکاح و تحلیل برخی محرمات است) و فرمان خدا حکمی نافذ و حتمی و به اندازه و حساب شده است.'),
(3572, 33, 39, '(این سنّت خداست در حق) آنان که تبلیغ رسالت خدا کنند و از خدا می‌ترسند و از هیچ کس جز خدا نمی‌ترسند و خدا برای حساب به تنهایی کفایت می‌کند.'),
(3573, 33, 40, 'محمّد (صلّی اللّه علیه و آله و سلّم) پدر هیچ یک از مردان شما (زید یا عمرو) نیست (پس زینب زن فرزند پیغمبر نبود و پس از طلاق او را تواند گرفت) لیکن او رسول خدا و خاتم انبیاست، و خدا همیشه (حکمش وفق حکمت و مصلحت است، زیرا او) بر همه امور عالم آگاه است.'),
(3574, 33, 41, 'ای کسانی که ایمان آورده‌اید ذکر حق و یاد خدا (به دل و زبان) بسیار کنید.'),
(3575, 33, 42, 'و دایم صبح و شام به تسبیح و تنزیه ذات پاکش بپردازید.'),
(3576, 33, 43, 'اوست خدایی که هم او و هم فرشتگانش بر شما بندگان رحمت می‌فرستند تا شما را از ظلمت‌ها (ی جهل و نقص و گمراهی) بیرون آرد و به عالم نور (علم و ایمان) رساند، و او بر اهل ایمان بسیار مهربان است.'),
(3577, 33, 44, 'تحیّت مؤمنان (و پذیرایی ایشان) روزی که به لقاء رحمت حق نائل شوند سلام (خدا و بشارت لطف الهی) خواهد بود، و بر آنان پاداش با کرامت و شرافت مهیا فرموده است.'),
(3578, 33, 45, 'ای پیامبر (گرامی) ما تو را به رسالت فرستادیم تا (بر نیک و بد خلق) گواه باشی و (خوبان را به رحمت الهی) مژده دهی و (بدان را از عذاب خدا) بترسانی.'),
(3579, 33, 46, 'و به اذن حق (خلق را) به سوی خدا دعوت کنی و (در این شب ظلمانی جهان) چراغ فروزان عالم باشی.'),
(3580, 33, 47, 'و مؤمنان را بشارت ده که خدا را بر آنان فضل (و رحمت عظیم و ثواب) بزرگ خواهد بود (که از حد تصور آنها بیرون است).'),
(3581, 33, 48, 'ای رسول، هرگز به فرمان کافران و منافقان مباش و از جور و آزارشان درگذر و کار خود به خدا واگذار، که خدا بر کفالت و کارسازی (امور خلق) کفایت است.'),
(3582, 33, 49, 'ای مردان با ایمان، هر گاه زنان مؤمنه را به عقد خود در آورده و پیش از آنکه با آنها نزدیکی کنید طلاقشان دادید در این صورت از شما نگه داشتن عدّه بر آنها نیست، پس آنها را به چیزی بهره‌مند و به نیکی رها سازید.'),
(3583, 33, 50, 'ای پیغمبر (گرامی) ما زنانی را که مهرشان ادا کردی بر تو حلال کردیم و نیز کنیزانی را که به غنیمت خدا تو را نصیب کرد و ملک تو شد و نیز دختران عمو و دختران عمّه‌ها و دختران خالو و دختران خاله‌هایت را آنها که با تو از وطن خود هجرت کردند و نیز زن مؤمنه‌ای را که خود را به رسول (بی‌شرط و مهر) ببخشد و رسول هم به نکاحش مایل باشد، که این حکم (هبه و بخشیدن زن و حلال شدن او) مخصوص توست دون مؤمنان، که ما حکم زنان عقدی و کنیزان ملکی مؤمنان را (که پیشتر با شرایط و عدد و حقوق آنها بر شوهر همه را بیان کردیم) می‌دانیم که چه مقرر کرده‌ایم. (این زنان همه را که بر تو حلال کردیم و تو را مانند مؤمنان امّتت به احکام نکاح مقیّد نکردیم) بدین سبب است که بر وجود (عزیز) تو در امر نکاح هیچ حرج و زحمتی نباشد. و خدا را (بر بندگان، خصوص بر تو) مغفرت و رحمت بسیار است.'),
(3584, 33, 51, 'تو (ای رسول) هر یک از زنانت را خواهی نوبتش مؤخّر دار و هر که را خواهی به خود بپذیر و هم آن را که (به قهر) از خود راندی اگرش (به مهر) خواندی باز بر تو باکی نیست، این (آزادی و مختار مطلق بودنت) بر زنان بهتر و شادمانی دل و روشنی دیده آنهاست و سبب آنکه محزون نباشند و به آنچه ایشان را عطا کردی همه خشنود باشند. و خدا به هر چه در دل شما مردم است آگاه است و خدا (بر نیک و بد خلق) دانا و (بر عفوشان کریم و بر انتقامشان) بردبار است.'),
(3585, 33, 52, '(ای رسول) بعد از این (زنان) دیگر نه عقد هیچ زن بر تو حلال است و نه مبدّل کردن این زنان به دیگر زن هر چند از حسنشان به شگفت آیی و بسیار در نظرت زیبا آیند مگر که کنیزی مالک شوی. و خدا بر هر چیز (و بر حدودش) مراقب و نگهبان است.'),
(3586, 33, 53, 'ای کسانی که ایمان آوردید به خانه‌های پیغمبر داخل مشوید مگر آنکه اذنتان دهند و بر سفره طعام دعوت کنند در آن حال هم نباید زودتر از وقت آمده و به پختن و آماده شدن آن چشم انتظار گشایید بلکه موقعی که دعوت شده‌اید بیایید و چون غذا تناول کردید زود (از پی کار خود) متفرق شوید نه آنجا برای سرگرمی و انس به گفتگو پردازید، که این کار پیغمبر را آزار می‌دهد و او به شما از شرم اظهار نمی‌دارد ولی خدا را بر اظهار حق خجلتی نیست و هر گاه از زنان رسول متاعی می‌طلبید از پس پرده طلبید، که حجاب برای آنکه دلهای شما و آنها پاک و پاکیزه بماند بهتر است و نباید هرگز رسول خدا را (در حیات) بیازارید و نه پس از وفات هیچ گاه زنانش را به نکاح خود در آورید، که این کار نزد خدا (گناهی) بسیار بزرگ است.'),
(3587, 33, 54, 'و (از خدا بترسید که) هر چیزی را اگر آشکار یا پنهان کنید خدا (بر آن و) بر همه امور جهان کاملا آگاه است.'),
(3588, 33, 55, 'و زنان را باکی نیست که بر پدران و فرزندان و برادران و برادرزادگان و خواهر زادگان و زنان مسلمان و کنیزان ملکی (و نابالغ غلامان بی‌ریبه) بی‌حجاب در آیند (و از غیر اینها باید احتجاب کنند) و (ای بانوان) از خدا (در خیانت و زشتی پنهان) بترسید، که خدا بر همه چیز کاملا گواه و آگاه است.'),
(3589, 33, 56, 'خدا و فرشتگانش بر (روان پاک) این پیغمبر صلوات و درود می‌فرستند شما هم ای اهل ایمان، بر او صلوات و درود بفرستید و با تعظیم و اجلال بر او سلام گویید و تسلیم فرمان او شوید.'),
(3590, 33, 57, 'آنان که خدا و رسول را (به عصیان و مخالفت) آزار و اذیّت می‌کنند خدا آنها را در دنیا و آخرت لعن کرده (و از رحمت خود دور گرداند) و بر آنان عذابی با ذلّت و خواری مهیّا ساخته است.'),
(3591, 33, 58, 'و آنان که مردان و زنان با ایمان بی‌تقصیر و گناه را بیازارند (بترسند که) دانسته بار تهمت و گناه آشکار بزرگی را برداشته‌اند.'),
(3592, 33, 59, 'ای پیغمبر (گرامی) با زنان و دختران خود و زنان مؤمنان بگو که خویشتن را به چادر فرو پوشند، که این کار برای اینکه آنها (به عفّت و حرّیت) شناخته شوند تا از تعرض و جسارت (هوس رانان) آزار نکشند بسیار نزدیکتر است و خدا (در حق خلق) بسیار آمرزنده و مهربان است.'),
(3593, 33, 60, 'البته (بعد از این) اگر منافقان و آنان که در دلهاشان مرض (و ناپاکی) است و هم آنها که در مدینه (بر ضد اسلام) تبلیغات سوء می‌کنند (و دل اهل ایمان را مضطرب و هراسان می‌سازند) دست (از این زشتی و بدکاری) نکشند ما هم تو را بر (قتال) آنها بر انگیزیم (و بر جان و مال آنها مسلط گردانیم) که از آن پس جز اندک زمانی در مدینه در جوار تو زیست نتوانند کرد.'),
(3594, 33, 61, 'این مردم (پلید بدکار) رانده درگاه حقّند، باید هرجا یافت شوند آنان را گرفته و جدّا به قتل رساند.'),
(3595, 33, 62, 'این سنّت خدا (و طریقه حقّ) است که در همه ادوار امم گذشته برقرار بوده (که منافقان و فتنه انگیزان را رسولان حق به قتل رسانند) و بدان که سنّت خدا هرگز مبدّل نخواهد گشت.'),
(3596, 33, 63, 'مردم از تو می‌پرسند که ساعت قیامت کی خواهد بود؟ جواب ده که آن را خدا می‌داند و بس. و تو چه می‌دانی (بگو به این مردم غافل) شاید آن ساعت بسیار موقعش نزدیک باشد.'),
(3597, 33, 64, 'خدا کافران را لعن کرده و بر آنان آتش افروخته دوزخ مهیّا گردانیده است.'),
(3598, 33, 65, 'که در آن دوزخ همیشه معذّبند و (بر نجات خود) هیچ یار و یاوری نخواهند یافت.'),
(3599, 33, 66, 'در آن روز صورتهاشان در آتش همی بگردد و (فریاد پشیمانی از دل برکشند و) گویند: ای کاش که ما (در دنیا) خدا و رسول را اطاعت می‌کردیم.'),
(3600, 33, 67, 'و گویند: ای خدا، ما اطاعت امر بزرگان و پیشوایان (فاسد) خود را کردیم که ما را به راه ضلالت کشیدند.'),
(3601, 33, 68, 'و تو ای خدا عذاب آنان را (سخت و) مضاعف ساز و به لعن و غضب بزرگ و شدید گرفتارشان گردان.'),
(3602, 33, 69, 'ای کسانی که ایمان آورده‌اید، شما مانند آن مردمی نباشید که پیغمبرشان موسی را (به تهمت زنا و قتل هارون و دیگر عیب‌جویی‌ها) بیازردند تا آنکه خدا او را از آنچه گفتند منزّه ساخت (و پاکدامنیش بر همه روشن گردانید) و او نزد خدا (پیغمبری) آبرومند و محترم بود.'),
(3603, 33, 70, 'ای اهل ایمان، متّقی و خدا ترس باشید و همیشه به حقّ و صواب سخن گویید.'),
(3604, 33, 71, 'تا خدا اعمال شما را (به لطف خود) اصلاح فرماید و از گناهان شما در گذرد. و هر که خدا و رسول را اطاعت کند البته به سعادت و فیروزی بزرگ نائل گردیده است.'),
(3605, 33, 72, 'ما بر آسمانها و زمین و کوههای عالم (و قوای عالی و دانی ممکنات) عرض امانت کردیم (و به آنها نور معرفت و طاعت و عشق و محبّت کامل حق یا بار تکلیف یا نماز و طهارت یا مقام خلافت و ولایت و امامت را ارائه دادیم) همه از تحمّل آن امتناع ورزیده و اندیشه کردند و انسان (ناتوان) آن را بپذیرفت، انسان هم (در مقام آزمایش و اداء امانت) بسیار ستمکار و نادان بود (که اکثر به راه جهل و عصیان شتافت).'),
(3606, 33, 73, '(این عرض امانت) برای این بود که خدا مرد و زن منافق و مرد و زن مشرک همه را به قهر و عذاب گرفتار کند و از مرد و زن مؤمن (به رحمت) در گذرد، و خدا بسیار آمرزنده و مهربان است..');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ستایش و سپاس مخصوص خداست که هر آنچه در آسمانها و زمین است همه ملک اوست و در عالم آخرت نیز شکر و سپاس مخصوص خداست (زیرا نعمتهای دنیا و آخرت همه عطای اوست) و او حکیم و آگاه است.'),
(3608, 34, 2, 'او هر چه درون زمین رود (از اموات و بذرها و هسته‌ها و قطرات) و هر چه از زمین به درآید (از درختان و حبوبات و چشمه‌ها و معادن) و هر چه از آسمان فرود آید (از فرشتگان و ارواح و ارزاق) و هر چه در آسمان بالا رود (از اعمال صالح و ارواح پاک) همه را می‌داند و اوست که بسی مهربان و بخشاینده است.'),
(3609, 34, 3, 'و کافران (منکر قیامت) گفتند که هرگز ساعت قیامتی بر ما نمی‌آید، بگو: چرا، به خدای من قسم که البته (ساعت قیامت) شما را خواهد آمد، خدایی که بر غیب جهان آگاه است و مقدار ذره‌ای (از موجودات عالم) در آسمانها و زمین از او پوشیده نیست و نه کمتر و نه بیشتر از ذره‌ای جز آنکه در کتاب روشن (علم ازلی حق) ثبت است.'),
(3610, 34, 4, 'تا (مطابق آن علم) آنان را که ایمان آورده و به اعمال صالح پرداختند (در قیامت) پاداش نیکو دهد، که آمرزش حق و رزق نیکوی بهشتی مخصوص آنهاست.'),
(3611, 34, 5, 'و آنان که (از کفر و عناد) در (محو و نابود ساختن) آیات ما سعی و کوشش کردند تا مگر (رسول ما را) ناتوان کنند (و از قهر و قدرت ما بیاسایند) بر آنها عذاب سخت دردناک خواهد بود.'),
(3612, 34, 6, 'و آنان که اهل دانش و معرفت گردیدند (یعنی مؤمنان و نیکان اهل کتاب) یقین دارند که قرآنی که بر تو نازل شده است از جانب پروردگار تو به حق نازل گردیده است و رهنمای خلق به راه خدای بی‌همتای ستوده صفات است.'),
(3613, 34, 7, 'و کافران (به تمسخر و استهزاء مؤمنان، با مردم) گفتند که آیا می‌خواهید شما را به مردی رهبری کنیم که شما را خبر می‌دهد که پس از آنکه مردید و ذرّات جسمتان متفرق و پراکنده گردید از نو باز زنده خواهید شد.'),
(3614, 34, 8, 'آیا این مرد دانسته به خدا دروغ می‌بندد یا جنون بر این گفتارش وا می‌دارد؟ (اینها که می‌پندارید هیچ نیست) بلکه (قیامت به زودی بیاید و) آنان که به عالم آخرت ایمان نمی‌آورند (آن جا) در عذاب و (این جا) در گمراهی دور (از نجات) گرفتارند.'),
(3615, 34, 9, 'آیا (کافران منکر قیامت) به آسمان و زمین که بر آنها از پیش و پس احاطه کرده ننگریستند (تا از حکمت خلقت پی به زندگانی ابدی قیامت برند) که اگر ما بخواهیم آنها را به زمین فرو می‌بریم یا یک قطعه از آسمان را بر سرشان فرود آوریم؟ همانا در آن آیتی (از حکمت و قدرت ما) بر هر بنده خدا شناس با اخلاص به خوبی پدیدار است.'),
(3616, 34, 10, 'و ما حظّ و بهره داود را به فضل و کرم خود کاملا افزودیم (و امر کردیم که) ای کوهها و ای مرغان شما نیز با (تسبیح و نغمه الهی) داود هم آهنگ شوید، و آهن سخت را (چون موم) بر دست او نرم گردانیدیم.'),
(3617, 34, 11, '(و به او دستور دادیم) که از آهن زره فراخ و گشاد بساز و حلقه زره به اندازه و یک شکل گردان و خود با قومت همه نیکوکار باشید که من کاملا به هر چه کنید بینایم.'),
(3618, 34, 12, 'و باد را مسخّر سلیمان ساختیم که (بساطش را) صبحگاه یک ماه راه می‌برد و عصر یک ماه، و معدن مس گداخته (یا نفت یا معادن دیگر) را برای او جاری گردانیدیم، و برخی از دیوان به اذن پروردگار در حضورش به خدمت می‌پرداختند و از آنها هر که سر از فرمان ما بپیچد عذاب آتش سوزانش می‌چشانیم.'),
(3619, 34, 13, 'آن دیوان بر او هر چه می‌خواست از (کاخ و عمارات و) معابد عالی و نقوش و تمثالها و ظروف بزرگ مانند حوضها و دیگهای عظیم که بر زمین کار گذاشته بودی همه را می‌ساختند. (و گفتیم که) ای آل داود شما شکر و ستایش خدا به جای آرید و (هر چند) از بندگان من عدّه قلیلی شکر گزارند.'),
(3620, 34, 14, 'و چون ما بر سلیمان مرگ را مأمور ساختیم (دیوان را) بر مرگ او به جز حیوان زمین (موریانه) که عصای او را می‌خورد (و جسد سلیمان که تا یک سال بر آن عصا تکیه داشت بر روی زمین افتاد) کسی دیگر به مرگ او رهبر نگشت. پس چون جسد سلیمان به زمین افتاد دیوان که از مرگ سلیمان آگاه شدند اگر از اسرار غیب آگاه بودند تا دیر زمانی در عذاب و ذلّت و خواری باقی نمی‌ماندند (و از اعمال شاقّه‌ای که به اجبار انجام می‌دادند همان دم که سلیمان مرد دست می‌کشیدند).'),
(3621, 34, 15, 'و برای (اولاد و قوم) سبا در وطنشان (سرزمین یمن و شام) از راست و چپ (جنوب و شمال) دو سلسله باغ و بوستان آیتی (از لطف و رحمت حق) بود (همه پر نعمت، و به آنها گفتیم که) بخورید از رزق خدای خود و شکر وی به جای آرید، که مسکنتان شهری نیکو و پر نعمت است و خدای شما غفور (و مهربان) است.'),
(3622, 34, 16, 'با وجود این باز (از شکر خدا و طاعت حق) اعراض کردند، ما هم سیلی سخت بر هلاک ایشان فرستادیم و به جای آن دو نوع باغ پر نعمت دو باغ دیگرشان دادیم که بار درختانش تلخ و ترش و بد طعم و شوره گز و اندکی درخت سدر بود.'),
(3623, 34, 17, 'این کیفر کفران آنها بود، و آیا ما تا کسی کفران نکند مجازاتش خواهیم کرد؟'),
(3624, 34, 18, 'و ما به میان آنها و شهرهایی که پر نعمت و برکت گردانیدیم باز قریه‌هایی نزدیک به هم قرار دادیم با فاصله کوتاه و سیر سفری معیّن (و آنها را گفتیم که) در این ده و شهرهای نزدیک به هم شبان و روزان با ایمنی کامل مسافرت کنید.'),
(3625, 34, 19, 'باز آنها گفتند: بار الها، سفرهای ما را دور و دراز گردان، و (به این تقاضای بی‌جا) بر خویش ستم کردند، ما هم آنها را داستانها و مثلهای عبرت آموز کردیم و به کلی متفرق و پراکنده ساختیم. در این عقوبت برای مردمان پر صبر و شکر آیاتی (از عبرت و حکمت) آشکار است.'),
(3626, 34, 20, 'و شیطان گمان باطل خود را درباره مردم، سخت به صدق و حقیقت رساند، پس جز فرقه کمی از اهل ایمان همه پیرو او شدند.'),
(3627, 34, 21, 'در صورتی که شیطان بر مردمان تسلطی نداشت (بلکه از حرص دنیا پیرو او شدند. و شیطان را بر این نداشتیم) مگر برای آنکه (به امتحان) آن کس را که به عالم قیامت ایمان آورد از آن کس که شک دارد معلوم گردانیم. و خدای تو بر هر چیز نگهبان و آگاه است.'),
(3628, 34, 22, '(ای رسول ما، مشرکان را) بگو: آنهایی را که به جز خدا شما مؤثر پنداشتید بخوانید (خواهید دانست که) هیچ یک مقدار ذره‌ای در آسمانها و زمین مالک نیستند و با خدا شرکتی در خلقت آسمان و زمین نداشته و به او در آفرینش یاری و کمکی نکرده‌اند.'),
(3629, 34, 23, 'و شفاعت کسی نزد خدا هم جز آن که خدا به او اذن شفاعت داده سودمند نیست (پس از اینان چشم شفاعت مدارید و خاصّان و انبیاء را در دنیا و آخرت شفیع خود قرار دهید) تا چون (آن خاصان) از دلهاشان اضطراب (جلال و سطوت الهی) بر طرف شود پرسند: خدای شما (در باب شفاعت) چه فرمود؟ پاسخ دهند که حقّ (و درستکاری و شفاعت مؤمنان نه مشرکان و منافقان) فرمود، و اوست خدای بلند مرتبه بزرگوار (و هیچ کس را جرأت مخالفت امرش نیست).'),
(3630, 34, 24, 'بگو: آن که از (نعم) آسمانها و زمین به شما روزی می‌دهد کیست؟ بگو: آن خداست، و ما (که موحدیم) یا شما (که مشرکید) کدام در هدایت یا ضلالتیم (به زودی معلوم شما خواهد شد).'),
(3631, 34, 25, 'باز بگو: (خدا داور عدل است، پس) شما مسئول جرم ما نبوده و ما هم مسئول کردار زشت شما هرگز نخواهیم بود.'),
(3632, 34, 26, 'باز بگو که خدا بین همه ما جمع کند آن گاه میان ما به حق، فتح و داوری خواهد فرمود و اوست که عقده گشا (ی مشکلات) و دانا (ی اسرار عالم) است.'),
(3633, 34, 27, 'باز بگو: شما به من آنان را که به عنوان شریک به خدا بستید نشان دهید (چه قدرت و اثری در عالم دارند؟) هیچ و هرگز، بلکه خداست آن که مقتدر (بر جهان) و با علم و حکمت (در ایجاد) است.'),
(3634, 34, 28, 'و ما تو را جز برای اینکه عموم بشر را (به رحمت خدا) بشارت دهی و (از عذابش) بترسانی نفرستادیم، و لیکن اکثر مردم (از این حقیقت) آگاه نیستند.'),
(3635, 34, 29, 'و (کافران) می‌گویند: پس این وعده قیامتی که شما پیغمبران می‌دهید اگر راست می‌گویید که خواهد بود؟'),
(3636, 34, 30, 'بگو: وعده گاه شما روزی است که ساعتی از آن تقدیم و تأخیر نخواهید داشت.'),
(3637, 34, 31, 'و کافران مشرک گفتند: ما هرگز به این قرآن و به کتابهایی که پیش از این فرستاده‌اند (از تورات و انجیل و زبور و غیره) ایمان نخواهیم آورد. (وای بر آنها) و اگر روزی که آن ستمکاران را در پیشگاه خدا باز داشته‌اند مشاهده کنی در حالی که با یکدیگر به خصومت و گفتگو برخاسته و ضعیفان تابع به رؤسای گردنکش خطاب کنند که اگر اغوای شما نبود ما البته ایمان می‌آوردیم!'),
(3638, 34, 32, 'باز رؤسای متکبّر با پیروان ضعیف خویش گویند: آیا با آنکه (از جانب خدا) راه هدایت را به شما نمودند باز ما (به اجبار) شما را از هدایت منع کردیم؟ (هرگز چنین نیست) بلکه شما خود مردم بدکاری بودید.'),
(3639, 34, 33, 'باز آنان که ضعیف و تابع بودند به رؤسای متکبر جواب دهند: بلکه مکر و فریب شب و روز شما بود که پیوسته ما را وا می‌داشتید که به خدای یکتا کافر شویم و بر او شریکانی قرار دهیم. و چون عذاب قیامت را به چشم مشاهده کنند سخت اظهار پشیمانی کنند، و ما زنجیرهای عقاب به گردن (همه فرماندهان و فرمانبران) اهل کفر نهیم، آیا این رنج و شکنجه جز کیفر کردار زشت آنهاست؟'),
(3640, 34, 34, 'و ما هیچ رسول بیم دهنده‌ای در دیاری نفرستادیم جز آنکه ثروتمندان عیّاش آن دیار (به رسولان) گفتند: ما به آنچه شما را به رسالت آن فرستاده‌اند کافریم.'),
(3641, 34, 35, 'و باز گفتند که ما بیش از شما مال و فرزند داریم و (چون نعمت دنیای ما افزون‌تر است در آخرت هم) هرگز رنج و عذابی نخواهیم داشت.'),
(3642, 34, 36, 'بگو: خدای من هر که را بخواهد روزی وسیع دهد و (هر که را بخواهد) تنگ روزی گرداند و لیکن اکثر مردم (از این حقیقت) آگاه نیستند.'),
(3643, 34, 37, 'و هرگز اموال و اولاد شما چیزی که شما را به درگاه ما مقرّب گرداند نیست مگر آنکه با ایمان و عمل صالح کسی مقرّب شود که آنان پاداش اعمال صالحشان مضاعف و افزون است و در غرفه‌های بهشت ابدی (از هر غم و رنج) ایمن و آسوده خاطرند.'),
(3644, 34, 38, 'و آنان که در (محو و نابودی) آیات (و رسل) ما می‌کوشند تا (رسولان) ما را زبون و مغلوب خود کنند آنها را البته برای عذاب و انتقام حاضر خواهند کرد و به کیفر سخت می‌رسانند.'),
(3645, 34, 39, 'بگو: خدای من هر که از بندگان خود را خواهد وسیع روزی یا تنگ روزی می‌گرداند، و شما هر چه (در راه رضای حق) انفاق کنید به شما عوض می‌بخشد و او بهترین روزی دهندگان است.'),
(3646, 34, 40, '(و یاد آر از آن) روزی که خدا همه مردم را به عرصه محشر جمع آورد آن گاه به فرشتگان گوید: آیا این گروه شما را معبود خود می‌گرفتند؟'),
(3647, 34, 41, 'فرشتگان گویند: بار الها، تو از هر شرک و نقصی پاک و منزّهی، تو خدا و یاور مایی نه ایشان، بلکه اینان جنّ و شیاطین را می‌پرستیدند و به طوری که اکثرشان گرویده به آنها بودند.'),
(3648, 34, 42, '(و به مشرکان خطاب شود) پس امروز شما هیچ مالک نفع و ضرر یکدیگر نیستند. و ما آنان را که ظلم و ستم کردند گوییم: اینک بچشید عذاب آتشی را که تکذیب آن می‌کردید.'),
(3649, 34, 43, 'و چون (توسط رسول) بر کافران آیات روشن ما تلاوت شود گویند: این شخص جز آنکه مردی است که می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند برگرداند هیچ مزیّت دیگری ندارد. و نیز گفتند: این قرآنش هم جز آنکه خود فرابافته (و به دروغ به خدا نسبت می‌دهد) چیز دیگری نباشد. و کافران چون آیات حقّ بر (هدایت) آنها آمد گفتند که این کتاب جز آنکه سحری آشکار است چیز دیگری نیست.'),
(3650, 34, 44, 'در صورتی که ما هیچ کتاب و سندی که آن را بخوانند (و به آن سند تکذیب تو کنند) و یا رسولی پیش از تو (که به قول او بر ردّ تو دلیل آرند) بر این مشرکان نفرستادیم.'),
(3651, 34, 45, 'و کفار پیش از اینان هم پیغمبرانشان را تکذیب کردند و این کافران امت تو به عشر آنچه به آن گذشتگان دادیم (از ثروت و جاه) نرسیدند، پس آنها که رسولان مرا تکذیب کردند چگونه به عذاب من گرفتار شدند (اینان هم مانند آنان به کیفر می‌رسند).'),
(3652, 34, 46, 'بگو که من به یک سخن شما را پند می‌دهم و آن سخن این است که شما خالص برای خدا دو نفر دو نفر با هم یا هر یک تنها قیام کنید و (درباره من) عقل و فکرت کار بندید تا به خوبی دریابید که صاحب شما امت را جنون نیست، او جز این نیست که رسول خداست و از عذاب سخت روز قیامت که شما را در پیش است می‌ترساند.'),
(3653, 34, 47, 'باز بگو: آنچه من از شما مزد رسالت خواستم آن هم برای شما باشد (یعنی هرگز مزدی از شما نخواسته و نمی‌خواهم. و شاید معنی این باشد که مودّت و حبّ ذوی القربی که از شما مزد رسالت خواستم آن هم برای ثواب و سعادت خود شماست) تنها پاداش (رسالت) من بر خداست و بس، و او بر هر چیز دانا و گواه است.'),
(3654, 34, 48, 'باز بگو: خدای من حق را (به وحی بر من) القا می‌فرماید و او به اسرار عوالم غیب آگاه است.'),
(3655, 34, 49, 'باز بگو: (کتاب و رسول و یا شمشیر) حق آمد و دیگر باطل در اول و آخر (در دنیا و آخرت) محو و نابود است.'),
(3656, 34, 50, 'باز بگو: اگر من (به عقیده شما) گمراه شده‌ام زیانش بر من است (نه بر شما)، و اگر هدایت یافته‌ام آن به وحی خدای من به من است (نه به هوش و تدبیر من) که خدا البته (دعای بندگان را) شنوا و (به قلوب خلق) نزدیک است.'),
(3657, 34, 51, 'و اگر تو سختی حال مجرمان را مشاهده کنی هنگامی که ترسان و هراسانند، در آن حال هیچ (از عذاب و قهر الهی) فوت نشوند و از مکان نزدیکی دستگیر شوند.'),
(3658, 34, 52, 'و کافران (در آن حال که عذاب دوزخ را به چشم ببینند پشیمان شده و) گویند: ما به حق ایمان آوردیم. و با این همه دوری (از مقام ایمان) کجا بدان مقام در قیامت نائل توانند شد؟'),
(3659, 34, 53, 'در صورتی که آنها پیش از این به حق کافر بودند و از جایی دور (از مقام علم و یقین) نسبت ناروا (ی جنون و سحر و کذب به رسول حق) می‌دادند.'),
(3660, 34, 54, 'و (امروز) میان آنها و آرزوهایشان (که به دنیا بر گردند یا توبه و ایمانشان را بپذیرند تا از عذاب رهایی یابند) به کلی دوری و مباینت افکندند چنان که به امثال آنها از امم گذشته که آنها هم در شک و ریب بودند نیز چنین کردند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سپاس خدای راست که آفریننده آسمانها و زمین است و فرشتگان را رسولان (پیمبران خود) گردانید و دارای دو و سه و چهار بال و پر (قدرت) قرار داد، هر چه بخواهد در آفرینش می‌افزاید که خدا بر (بعث و ایجاد) هر چیز قادر است.'),
(3662, 35, 2, 'دری که خدا از رحمت به روی مردم بگشاید هیچ کس نتواند بست و آن در که او ببندد هیچ کس جز او نتواند گشود، و اوست خدای بی‌همتای با حکمت و اقتدار.'),
(3663, 35, 3, 'ای مردم، متذکر شوید که چه نعمتها خدا به شما عطا فرمود، آیا جز خدا آفریننده‌ای هست که از آسمان و زمین به شما روزی دهد؟ هرگز جز آن خدای یکتا خدایی نیست، پس این مشرکان چگونه شما را (از درگاه حق رو به بتان باطل) می‌گردانند؟'),
(3664, 35, 4, '(ای رسول ما غمین مباش) اگر تو را این امت (نادان) تکذیب کنند پیمبران پیش از تو را نیز (امتهاشان) تکذیب کردند و بازگشت امور خلق به سوی خداست.'),
(3665, 35, 5, 'ای مردم، البته وعده (قیامت در کتاب) خدا حق است پس مبادا که زندگانی دنیا (ی فانی) شما را مغرور سازد و مبادا شیطان فریبنده (از قهر و انتقام حق) به (عفو) خدا مغرورتان گرداند.'),
(3666, 35, 6, 'شیطان سخت شما را دشمن است شما هم او را دشمن دارید، او حزب و سپاهش را فرا می‌خواند تا همه (مانند او) اهل دوزخ باشند.'),
(3667, 35, 7, 'آنان که به خدا کافر شدند کیفر آنها عذاب سخت دوزخ است و آنان که ایمان آوردند و نیکوکار گردیدند پاداش آنها مغفرت (خدا) و اجر بزرگ (بهشت ابد) خواهد بود.'),
(3668, 35, 8, 'آیا آن کس که کردار زشتش به چشم زیبا جلوه داده شده و (از خود پسندی) آن را نیکو بیند (مانند مرد حقیقت بین و نیکو کردار است)؟ پس خدا هر که را خواهد به گمراهی واگذارد و هر که را خواهد هدایت فرماید. پس مباد نفس شریفت بر این مردم به غم و حسرت افتد، که خدا به هر چه اینان کنند کاملا آگاه است.'),
(3669, 35, 9, 'و خداست آن که بادها را بفرستد که ابرها را بر می‌انگیزد و آن ابر را ما به شهر و دیار مرده برانیم و به بارانش زمین را پس از مرگ (خزان) زنده گردانیم. حشر و نشر مردگان هم (به قیامت) همین گونه است.'),
(3670, 35, 10, 'هر که طالب عزت است (بداند که همانا در ملک وجود) تمام عزت خاص خدا (و خداپرستان) است. کلمه نیکو (ی توحید و روح پاک آسمانی) به سوی خدا بالا رود و عمل نیک خالص آن را بالا برد. و بر آنان که به مکر و تزویر اعمال بد کنند عذاب سخت خواهد بود و فکر و مکرشان به کلی نابود خواهد شد.'),
(3671, 35, 11, 'و خدا شما (نوع بشر) را (نخست مردی) از خاک بیافرید و سپس از نطفه (آن مرد دیگری را) خلق کرد و بعد از آن شما را جفت (مرد و زن) قرار داد، و هیچ زنی بار نگیرد و نزاید جز به علم و اراده او، و کسی عمر طولانی نکند یا از عمرش کاسته نشود جز آنکه همه در کتاب (علم ازلی حق) ثبت است و این (ضبط اعمار و آجال خلق) بر خدا بسیار آسان است.'),
(3672, 35, 12, 'و هرگز آن دو دریا که آب این یک شیرین و گوارا و آن دگر شور و تلخ است یکسان نیستند، با وجود این شما از هر دو، گوشت تازه تناول می‌کنید و زیورها (چون لؤلؤ و مرجان) از آن استخراج کرده که در پوشیده و زیب و زیور تن می‌سازید، و در آن کشتیها روان بینی تا از فضل خدا (کسب و تجارت کرده و روزی) طلبید و باشد که شکر گزار (نعمتش) گردید.'),
(3673, 35, 13, 'خداست که شب را درون پرده روز پنهان سازد و روز را درون پرده شب، و خورشید و ماه را مسخّر کرده که هر یک به مقدار معیّن و مدار خاصی می‌گردند، اوست خدای آفریننده و پروردگار شما، که همه ملک هستی از اوست، و به غیر او معبودانی که به خدایی می‌خوانید در جهان دارای پوست هسته خرمایی نیستند.'),
(3674, 35, 14, 'اگر آنها را بخوانید (چون جمادند) نشنوند و اگر بشنوند (مانند عیسی و عزیر و فرشتگان و فراعنه چون بنده‌اند بی‌اذن خدا) به شما جواب ندهند و روز قیامت از این که آنان را شریک خدا دانستید (و به پرستش آنها پرداختید) بیزاری جویند. و (ای رسول و ای امّت) هیچ کس مانند (خدای) دانا تو را (به حقیقت) آگاه نگرداند.'),
(3675, 35, 15, 'ای مردم، شما همه به خدا فقیر و محتاجید و تنها خداست که بی‌نیاز و غنیّ بالذّات و ستوده صفات است.'),
(3676, 35, 16, 'اگر بخواهد همه شما را به دیار عدم فرستد و خلقی از نو به عرصه وجود آرد.'),
(3677, 35, 17, 'و هیچ این کار بر خدا دشوار نیست.'),
(3678, 35, 18, 'و هیچ کس بار گناه دیگری را به دوش نگیرد و آن که بارش سنگین است اگر دیگری را هر چند خویش (و پدر و فرزند هم) باشد کمک بر سبکباری خود طلبد ابدا باری از دوشش برداشته نشود. و تو تنها آنان را که در خلوت و پنهانی از خدای خود می‌ترسند و نماز به پا می‌دارند توانی خدا ترس و پرهیزگار گردانی. و هر کس خود را (از کفر و گناه و اخلاق زشت) پاک و منزه ساخت سود و سعادتش بر خود اوست و بازگشت همه به سوی خداست.'),
(3679, 35, 19, 'و هرگز (کافر تاریک جان) کور و (مؤمن روشن روان) بینا یکسان نیستند.'),
(3680, 35, 20, 'و هیچ ظلمت با نور مساوی نخواهد بود.'),
(3681, 35, 21, 'و هرگز آفتاب و سایه هم رتبه نباشند.'),
(3682, 35, 22, 'و ابدا زندگان (علم و ایمان) با مردگان (جهل و عصیان) برابر نیستند. (ای رسول بدان که) خدا هر که را بخواهد شنوا (ی کلام حق) سازد و اما تو آن کس را که در گورستان (کفر و جهالت و شهوت پرستی) فرو رفته هرگز شنوا نتوانی کرد.'),
(3683, 35, 23, 'تو جز آنکه (خلق را از کیفر اعمال بد) بترسانی به کاری مأمور نیستی.'),
(3684, 35, 24, 'ما تو را به حق و راستی به سوی خلق فرستادیم تا (خوبان را به بهشت باد) بشارت دهی و (بدان را از قهر حق) بترسانی، و هیچ امتی نبوده جز آنکه در میانشان ترساننده‌ای (و رهنمایی) بوده است.'),
(3685, 35, 25, 'و اگر امت تو را تکذیب کردند پیشینیان هم پیغمبران خود را که با آیات و معجزات و کتب و حجّتهای روشن برای هدایتشان آمدند همه را تکذیب کردند.'),
(3686, 35, 26, 'من هم از آن کافران مؤاخذه شدید کردم پس (بنگر که) قهر و عذاب من چگونه (و چه سخت) بود.'),
(3687, 35, 27, 'آیا ندیدی که خدا باران را از آسمان فرود آورد، و به آن انواع میوه‌های گوناگون و رنگارنگ پدید آوردیم؟ و (در زمین) از کوهها طرق زیاد و اصناف و رنگهای مختلف سفید و سرخ و سیاه هست (که خدا خلقت فرمود).'),
(3688, 35, 28, 'و از اصناف مردم و اجناس جنبندگان و حیوانات نیز رنگهای مختلف هست (که خدا آفرید) و از میان اصناف بندگان تنها مردمان عالم و دانا مطیع و خدا ترسند. البته خدا عزیز و توانا و بسیار بخشنده گناهان است.'),
(3689, 35, 29, 'آنها که کتاب خدا را تلاوت کرده و نماز به پا می‌دارند و از آنچه ما روزیشان کرده‌ایم پنهان و آشکار (به فقیران) انفاق می‌کنند (از لطف خدا) امید تجارتی دارند که هرگز زیان و زوال نخواهد یافت.'),
(3690, 35, 30, 'تا خدا به آنها پاداش کامل عطا کند و از فضل و کرم باز بر ثوابشان بیفزاید که او (خطای مؤمنان را) بسیار بیامرزد و به شکر گزاران احسان فراوان کند.'),
(3691, 35, 31, 'و آنچه از کتاب (آسمانی قرآن) بر تو وحی فرستادیم آن حق است که کتب آسمانی (تورات و انجیل) را که پیش از اوست تصدیق می‌کند (و آنها حقانیت آن را تصدیق می‌کنند. تو از تکذیب کافران میندیش که) همانا خدا به اعمال بندگان خود کاملا بصیر و آگاه است.'),
(3692, 35, 32, 'آن گاه (پس از آن پیمبران سلف) ما آن خاندان را که از بندگان خود برگزیدیم (یعنی رسول خاتم و آلش صلّی اللّه علیه و آله را) وارث علم قرآن گردانیدیم. باز هم بعضی از آنها (یعنی فرزندان او) به نفس خود ظلم کردند و بعضی راه عدل پیمودند و برخی به هر عمل خیر (با جان و دل) به دستور حق سبقت گیرند. این رتبه در حقیقت همان فضل بزرگ (و عطای بی‌منتهای الهی) است.'),
(3693, 35, 33, 'همان بهشت ابدی که در آن داخل شوند و با زر و گوهرهای آن (به دست و بدن) زیور گردند و جامه حریر و پرند در بر کنند.'),
(3694, 35, 34, 'و گویند: حمد خدا را که حزن و اندوه ما ببرد، همانا خدای ما بسیار بخشنده جرم گناهکاران و پاداش دهنده سپاس شکر گزاران است.'),
(3695, 35, 35, 'شکر آن خدای را که از لطف و کرم، ما را به منزل دائمی (بهشت) وارد کرد، که در اینجا هیچ رنج و المی به ما نرسد و ابدا ضعف و خستگی نخواهیم یافت.'),
(3696, 35, 36, 'و آنان که کافر شدند عذاب ایشان آتش دوزخ است که نه حکم مرگ بر آنان رود تا بمیرند و نه از عذابش کاسته شود (تا آسوده شوند). این گونه هر کافر لجوجی را کیفر می‌کنیم.'),
(3697, 35, 37, 'و آن کفّار در آتش دوزخ فریاد و ناله کنند که ای پروردگار، ما را (از این عذاب) بیرون آور تا بر خلاف گذشته به اعمال نیک بپردازیم. (خطاب شود) آیا شما را به اندازه‌ای عمر ندادیم که هر که اهل تذکر و پند شنیدن است در این مدت متذکر شود (و به توبه پردازد)؟ و آیا بیم دهندگان بر شما نیامدند؟ پس (امروز) عذاب دوزخ را بچشید که ستمکاران را هیچ یار و نجات دهنده‌ای نخواهد بود.'),
(3698, 35, 38, 'خدا به اسرار غیب آسمانها و زمین داناست و به افکار و اندیشه دلها هم کاملا آگاه است.'),
(3699, 35, 39, 'اوست خدایی که شما را در زمین جانشین (امم گذشته) قرار داد، اینک هر کس کافر شود زیان کفر بر خود اوست و کفر کافران نزد خدایشان چیزی جز خشم و غضب حق بر آنان نیفزاید و کفر کافران چیزی جز خسارت و زیان بر آنها نخواهد افزود.'),
(3700, 35, 40, '(ای رسول ما، مشرکان را) بگو: شما که خدای یگانه را رها کرده و بتان را شریک خدا می‌خوانید با من بگویید و نشان دهید که این بتان چه چیزی از زمین آفریده‌اند؟ یا آیا شرکتی در خلقت آسمانها (با خدا) داشته‌اند؟ یا کتابی به آنها داده‌ایم که (بر عقیده شرک خود) از آن برهانی آرند؟ (هیچ یک از اینها نیست) بلکه ستمکاران مشرک را حجّتی جز وعده دروغی که به آن یکدیگر را می‌فریبند چیزی در دست نیست.'),
(3701, 35, 41, 'محققا خدا آسمانها و زمین را از اینکه از جای خود بلغزند نگاه می‌دارد، و اگر رو به لغزش و انحراف از مسیر خود نهند گذشته از او هیچ کس آنها را محفوظ نتواند داشت، (و بدانید) که خدا (بر کفر و گناه خلق) بسیار بردبار و آمرزنده است.'),
(3702, 35, 42, 'و (مشرکان عرب) محکم‌ترین قسم به نام خدا یاد می‌کردند که اگر بیم کننده‌ای از جانب خدا برای (هدایت) آنها بیاید از هر یک از امم (یهود و نصاری زودتر و) بهتر هدایت یابند. و آن گاه که بیم کننده‌ای آمد بر آنها چیزی جز مخالفت و نفرت نیفزود.'),
(3703, 35, 43, 'بدین جهت که می‌خواستند در زمین تکبر و گردنکشی کنند و مکر در اعمال بد اندیشند، و مکر زشت و فکر بدکاری جز بر صاحبش احاطه نکند، پس آیا اینان جز آنکه به طریقه امم گذشته هلاک شوند انتظاری دارند؟ و طریقه خدا (در هلاک بداندیشان) را هرگز مبدّل نخواهی یافت و طریقه حق (و سنّت الهی) را هرگز تغییر پذیر نخواهی یافت.'),
(3704, 35, 44, 'آیا این مردم در روی زمین سیر نکردند تا عاقبت کار ستمکاران پیش از خود را که نیرو و اقتدارشان هم بسیار بیش از اینان بود (به دیده عبرت) بنگرند که چگونه شد؟ و هیچ موجودی در آسمانها و زمین از قدرت خدا نتواند کاست، که همانا خدا در ازل و ابد عالم قادر مطلق است..'),
(3705, 35, 45, 'و اگر خدا از کردار زشت خلق مؤاخذه کند در پشت زمین هیچ جنبنده‌ای باقی نگذارد و لیکن (کیفر) خلق را به تأخیر می‌افکند تا به وقت معیّن (که حکمتش اقتضا کند) و چون هنگام اجل آنان فرا رسد خدا به احوال بندگانش کاملا بیناست (و مقدار مجازات و عفو هر کسی را می‌داند).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یس (ای سیّد رسولان و ای کاملترین انسان)'),
(3707, 36, 2, 'قسم به قرآن حکمت بیان.'),
(3708, 36, 3, 'که تو البته از پیمبران خدایی.'),
(3709, 36, 4, 'که به راهی راست فرستاده شدی.'),
(3710, 36, 5, '(این قرآن تو کتابی است که) از جانب خدای مقتدر مهربان نازل شده است.'),
(3711, 36, 6, 'تا قومی که پدرانشان به کتب آسمانی پیشین بیم داده (و وعظ و اندرز) شدند تو هم خود آنها را (به این قرآن) بیم دهی (و از قهر حق بترسانی) که ایشان سخت غافلند.'),
(3712, 36, 7, 'البته وعده عذاب ما بر اکثر آنان حتمی و لازم گردید از این رو ایمان نمی‌آورند.'),
(3713, 36, 8, 'ما هم بر گردن آنها تا زنخ زنجیرهای عذاب نهادیم در حالی که (از جهل و عناد مانند شتر) سر بلند کرده و چشم بر بسته‌اند.'),
(3714, 36, 9, 'و (راه خیر را) از پیش و پس بر آنها سد کردیم و بر چشم (هوش) شان هم پرده افکندیم پس هیچ (راه حق) نمی‌بینند.'),
(3715, 36, 10, 'و تو آنها را بترسانی یا نترسانی یکسان است (چون دانسته با حق عناد می‌ورزند) هرگز ایمان نمی‌آورند.'),
(3716, 36, 11, 'تو تنها کسی را بترسانی و اندرز کنی (و سودمند افتد) که پیرو آیات قرآن شده و از (قهر) خدای مهربان به خلوت و در پنهان می‌ترسد، اینان را به مغفرت خدا و پاداش با لطف و کرم او بشارت ده.'),
(3717, 36, 12, 'ما مردگان را باز زنده می‌گردانیم و کردار گذشته و آثار وجودی آینده‌شان همه را ثبت خواهیم کرد، و در لوح محفوظ و روشن خدا (یا قلب امام خلیفة اللّه) همه چیز را به شماره آورده‌ایم.'),
(3718, 36, 13, 'برای این مردم حال آن قریه (انطاکیه) را مَثَل زن که رسولان حق برای هدایت آنها آمدند.'),
(3719, 36, 14, 'که نخست دو تن از رسولان را فرستادیم و چون تکذیب کردند باز رسول سومی برای مدد و نصرت مأمور کردیم، و همه گفتند: ما (از جانب خدا) به رسالت برای هدایت شما آمده‌ایم.'),
(3720, 36, 15, 'گفتند: شما جز اینکه مانند ما مردم بشری هستید مقام دیگری ندارید و هرگز خدای رحمان چیزی را فرو نفرستاده است و جز اینکه شما مردم دروغگویی هستید هیچ در کار نیست.'),
(3721, 36, 16, 'رسولان باز گفتند: خدای ما می‌داند که محققا ما فرستاده او به سوی شما هستیم.'),
(3722, 36, 17, 'و بر ما جز آنکه واضح ابلاغ رسالت کنیم هیچ تکلیفی نیست.'),
(3723, 36, 18, 'باز منکران گفتند که ما وجود شما را به فال بد گرفتیم، اگر (از این دعوی) دست بر ندارید البته سنگسارتان خواهیم کرد و از ما به شما رنج و شکنجه سخت خواهد رسید.'),
(3724, 36, 19, 'رسولان گفتند: آن فال بد که می‌گویید اگر بفهمید و متذکر شوید (آن جهلی است که) با خود شماست، بلکه شما مردم مسرف (و پر هوا و هوس) هستید.'),
(3725, 36, 20, 'و مردی شتابان از دورترین نقاط شهر (انطاکیه، حبیب نام) فرا رسید گفت: ای قوم من، این رسولان خدا را پیروی کنید.'),
(3726, 36, 21, 'از آنان که هیچ اجر و مزد رسالتی از شما نمی‌خواهند و خود (به راه حق) هدایت یافته‌اند پیروی کنید.'),
(3727, 36, 22, 'و چرا باید من خدای آفریننده خود را نپرستم در صورتی که بازگشت شما (و همه خلایق) به سوی اوست.'),
(3728, 36, 23, 'آیا من به جای آن خدای آفریننده یکتا خدایانی را معبود خود گیرم که اگر آن خدای رحمن بخواهد به من رنج و زیانی رسد هیچ شفاعت آن خدایان از من دفع زیان نکرده و نجاتم نتوانند داد؟'),
(3729, 36, 24, 'در این صورت پیداست که من بسیار زیانکار خواهم بود.'),
(3730, 36, 25, 'پس، از من (ای رسولان) بشنوید (و گواه باشید) که به خدای (فرستنده) شما ایمان آوردم.'),
(3731, 36, 26, '(و به این مرد با ایمان پس از کشته شدن، در عالم برزخ) گفته شد: بیان داخل بهشت شو، گفت: ای کاش ملّت من هم (از این نعمت بزرگ) آگاه بودند.'),
(3732, 36, 27, 'که خدا چگونه در حق من مغفرت و رحمت فرمود و مرا مورد لطف و کرم قرار داد.'),
(3733, 36, 28, 'و ما پس از او (یعنی حبیب) بر قومش هیچ لشکری از آسمان نفرستادیم (تا بر ایمان مجبورشان کنند) و هیچ این کار (با ملتی) نکرده‌ایم (و نخواهیم کرد).'),
(3734, 36, 29, 'نبود عقوبتشان جز یک صیحه عذاب آسمانی که به ناگاه همه هلاک شدند.'),
(3735, 36, 30, 'وای بر حال این بندگان (گمراه لجوج) که هیچ رسولی برای هدایت آنها نیامد جز آنکه او را به تمسخر و استهزا گرفتند.'),
(3736, 36, 31, 'آیا ندیدند چه بسیار طوایفی را پیش از اینها هلاک کردیم که دیگر ابدا به (دیار) اینان باز نگردند؟'),
(3737, 36, 32, 'و هیچ کس (از گذشته و آینده در عالم) نیست جز آنکه همه نزد ما حاضر آورده می‌شوند.'),
(3738, 36, 33, 'و یک برهان برای آنان (بر این که ما مردگان را زنده می‌کنیم) آن است که زمین مرده را (به باران رحمت) زنده کرده و از آن دانه‌ای که قوت و روزی خلق شود می‌رویانیم.'),
(3739, 36, 34, 'و در زمین باغها از نخل خرما و انگور قرار دادیم و در آن چشمه‌های آب جاری کردیم.'),
(3740, 36, 35, 'تا مردم از میوه آن باغها تناول کنند و از انواع غذاهایی که (از این میوه‌ها و نباتات) به دست خود عمل می‌آورند نیز تغذیه نمایند. آیا نباید شکر آن نعمتها به جای آرند.'),
(3741, 36, 36, 'پاک و منزه است خدایی که همه ممکنات عالم را جفت آفریده چه از نباتات (و حیوانات) و چه از نفوس بشر و دیگر مخلوقات که اینان از آنها آگه نیستند.'),
(3742, 36, 37, 'و برهان دیگر برای خلق (در اثبات قدرت حق) وجود شب است که ما چون پرده روز را از آن برگیریم ناگهان همه آنان (و همه چیز) در تاریکی فرو می‌روند.'),
(3743, 36, 38, 'و نیز خورشید تابان بر مدار معیّن خود دایم بی‌هیچ اختلاف به گردش است. این طرح و تدبیر خدای دانای مقتدر است (و برهان دیگر بر قدرت اوست).'),
(3744, 36, 39, 'و نیز (گردش) ماه را (در) منازل معیّن مقدّر کردیم تا مانند شاخه خشکیده خرما (زرد و لاغر به منزل اول) باز گردد (و این بر قدرت حق برهان دیگری است).'),
(3745, 36, 40, 'نه (در گردش منظم عالم) خورشید را شاید که به ماه فرا رسد و نه شب بر روز سبقت گیرد و هر یک بر مدار معیّنی (در این دریای بی‌پایان) شناورند.'),
(3746, 36, 41, 'و برهان دیگر برای آنان آن که ما فرزندان آنان (یعنی بشر) را در کشتی پربار سوار گردانیدیم.'),
(3747, 36, 42, 'و نیز برای آنها به مانند کشتی چیزی که بر آن سوار شوند خلق کردیم (شاید اتومبیل و ترن و طیّاره مقصود باشد).'),
(3748, 36, 43, 'و اگر بخواهیم همه را به دریا غرق کنیم که ابدا نه فریاد رسی و نه راه نجاتی یابند.'),
(3749, 36, 44, 'مگر باز لطف و رحمت ما آنها را نجات دهد و تا وقت معیّن بهره زندگی بخشد.'),
(3750, 36, 45, 'و چون مردم را گویند که در امر گذشته و آینده (دنیا و آخرت) خویش (اندیشه کنید و) تقوا پیشه کنید شاید مورد لطف و رحمت خدا گردید (روی بگردانند).'),
(3751, 36, 46, 'و بر این مردم (غافل) هیچ از آیات الهی نیاید جز آنکه از آن اعراض کنند.'),
(3752, 36, 47, 'و چون به آنها گویند که از آنچه خدا روزی شما قرار داده (چیزی برای خدا به فقیران) انفاق کنید، کافران به اهل ایمان جواب دهند آیا ما به کسی که اگر خدا می‌خواست به او هم روزی می‌داد اطعام و دستگیری کنیم؟ شما پیداست که سخت در غلط و گمراهی هستید (و راه راست آن است که چنان که خدا به فقیران احسان نکرده ما هم نکنیم).'),
(3753, 36, 48, 'و کافران (از روی تمسخر به رسول و مؤمنان) گویند: پس این وعده (قیامت و بهشت و دوزخ) اگر راست می‌گویید کی خواهد بود؟'),
(3754, 36, 49, 'این منکران قیامت انتظار نکشند جز یک صیحه (اسرافیل حق) را که (به مرگ) همه را فرا گیرد در حالی که (در کارهای دنیا یا در صیحه دوم قیامت) با هم به بحث و جدل مشغول‌اند.'),
(3755, 36, 50, 'پس (در آن لحظه مرگ) نه توانایی سفارشی دارند و نه به اهل بیت خود رجوع توانند کرد.'),
(3756, 36, 51, 'و (چون بار دوم) در صور دمیده شود به ناگاه همه از قبرها به سوی خدای خود به سرعت می‌شتابند.'),
(3757, 36, 52, 'و گویند: ای وای بر ما، که ما را از خوابگاهمان برانگیخت؟ این همان وعده خدای مهربان است و رسولان همه راست گفتند.'),
(3758, 36, 53, 'و آن جز یک صیحه (و یک لحظه بیش) نباشد که ناگاه تمام خلایق محشر به پیشگاه ما حاضر آورده خواهند شد.'),
(3759, 36, 54, 'پس در آن روز کمترین ظلمی به هیچ کس نشود و جز آنچه عمل کرده‌اید ابدا جزایی نخواهید یافت.'),
(3760, 36, 55, 'اهل بهشت آن روز خوش به وجد و نشاط مشغولند.'),
(3761, 36, 56, 'آنان با زنان و اقرانشان در سایه‌ها (ی درختان بهشت) بر تختها (ی عزّت) تکیه کرده‌اند.'),
(3762, 36, 57, 'برای آنها در آنجا میوه‌های گوناگون و هر چه بخواهند آماده است.'),
(3763, 36, 58, '(فرشتگان رحمت) بر آنان از خدای مهربان سلام و تحیّت رسانند.'),
(3764, 36, 59, 'و (به فاسقان خطاب شود) ای بد کاران، امروز شما (از صف نیکوان) جدا شوید.'),
(3765, 36, 60, '(خطاب آید) ای آدم زادگان، آیا به شما سفارش ننمودم که شیطان را نپرستید، زیرا روشن است که او دشمن بزرگ شماست؟!'),
(3766, 36, 61, 'و مرا پرستش کنید، که این راه مستقیم (سعادت ابدی) است؟!'),
(3767, 36, 62, 'و همانا خلق بسیاری از شما نوع بشر را (این دیو) به گمراهی کشید، آیا عقل و فکرت کار نمی‌بستید (تا از مکر و فریبش بپرهیزید).'),
(3768, 36, 63, 'این همان دوزخی است که به شما (پیروان شیطان) وعده می‌دادند.'),
(3769, 36, 64, 'امروز در آتش آن به کیفر کفرتان داخل شوید.'),
(3770, 36, 65, 'امروز بر دهان آن کافران مهر خموشی نهیم و دستهایشان با ما سخن گوید و پاهایشان به آنچه می‌کردند گواهی دهد.'),
(3771, 36, 66, 'و اگر ما بخواهیم، دیده‌هایشان را (به گمراهی) محو و نابینا کنیم تا چون به راه سبقت گیرند کجا (با کوری و گمراهی) بصیرت یابند؟'),
(3772, 36, 67, 'و اگر بخواهیم همان جا صورت آنها را مسخ کنیم (تا به شکل سگان و بوزینگان شوند) که نه (از آن صورت یا از آن جا) بتوانند گذشت و نه (به صورت اول) بازگشت.'),
(3773, 36, 68, 'و ما هر کس را عمر دراز دهیم (به پیری) در خلقتش تغییر دهیم. آیا (در این کار) تعقل نمی‌کنند (که اگر عمر به دست طبیعت بود پس از کمال به نقصان باز نمی‌گشت).'),
(3774, 36, 69, 'و نه ما او را (یعنی محمّد صلّی اللّه علیه و آله و سلّم را) شعر آموختیم و نه شاعری شایسته مقام اوست، بلکه این کتاب چیزی جز ذکر الهی و قرآن روشن بیان خدا نیست.'),
(3775, 36, 70, 'تا هر که را زنده‌دل است (به آیاتش پند دهد و از خدا و قیامت) بترساند و بر کافران (نیز به اتمام حجت) وعده عذاب حتم و لازم گردد.'),
(3776, 36, 71, 'آیا کافران ندیدند که بر آنها به دست (قدرت) خود از جمله آفریدگان چهارپایانی خلقت کردیم تا مالک آنها شوند؟'),
(3777, 36, 72, 'و آن حیوانات (با عظمت و قوت) را مطیع و رام آنها ساختیم که هم بر آنها سوار شوند و هم از آنها غذا تناول کنند.'),
(3778, 36, 73, 'و برای آنها در آن حیوانات منافع (بسیاری از پوست و پشم و کرک و غیره) و آشامیدنی‌های فراوان (از شیر و ماست و روغن و غیره) هست، آیا شکر (این نعمتها را) نباید به جای آرند؟'),
(3779, 36, 74, 'و (مشرکان از جهل) به جای خدا خدایانی دیگر اتخاذ کردند تا مگر (از جانب آنها) یاری شوند.'),
(3780, 36, 75, 'هرگز آن خدایان کمترین نصرتی به آنها نتوانند کرد و خود این مشرکان معبودانشان را سپاهی حاضر خدمت هستند که همگی را (در قیامت در آتش دوزخ) حاضر آرند.'),
(3781, 36, 76, 'پس سخن این مشرکان تو را محزون نکند. ما هر آنچه پنهان و آشکار گویند همه را می‌دانیم (و به کیفر کفرشان می‌رسانیم).'),
(3782, 36, 77, 'آیا انسان ندید که ما او را از نطفه ناقابل (چنین آراسته) خلقت کردیم؟ آن گاه (به جای آنکه شکر گزار باشد) دشمن آشکار ما گردید.'),
(3783, 36, 78, 'و برای ما مثلی (جاهلانه) زد و آفرینش خود را فراموش کرد، گفت: این استخوانهای پوسیده را باز که زنده می‌کند؟'),
(3784, 36, 79, 'بگو: آن خدایی زنده می‌کند که اول بار آنها را ایجاد کرد و او به هر آفریده‌ای داناست.'),
(3785, 36, 80, 'آن خدایی که از درخت سبز و تر برای انتفاع شما آتش قرار داده که (برای حاجتی که خواهید) بر می‌افروزید.'),
(3786, 36, 81, 'آیا آن خدایی که (خلقت با عظمت) آسمانها و زمین را آفریده بر آفرینش (موجود ضعیفی) مانند این کافران قادر نیست (که چون مردند باز آنها را زنده گرداند)؟ چرا (البته قادر است) و او آفریننده و داناست.'),
(3787, 36, 82, 'فرمان نافذ او چون اراده خلقت چیزی کند به محض اینکه گوید: «موجود باش» بلا فاصله موجود خواهد شد.'),
(3788, 36, 83, 'پس منزه و پاک است خدایی که (ملک و) ملکوت هر موجود به دست (قدرت) او و بازگشت شما همه خلایق به سوی اوست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'قسم به صف زدگان (چون فوج فرشتگان یا نماز گزاران یا سپاه اهل ایمان) که صف آرایی کرده‌اند.'),
(3790, 37, 2, 'قسم به منع و زجر کنندگان (از عمل معصیت کاران، یا رانندگان ابر و باران).'),
(3791, 37, 3, 'قسم به تلاوت کنندگان ذکر (همه کتب آسمانی یا آیات قرآن).'),
(3792, 37, 4, 'که محققا خدای شما یکی است.'),
(3793, 37, 5, 'همان خدای یکتا که آفریننده آسمانها و زمین است و هر چه بین آنهاست و آفریننده مشرق‌ها (و مغرب‌ها) است.'),
(3794, 37, 6, 'ما نزدیکترین آسمان را به زیور انجم بیاراستیم.'),
(3795, 37, 7, 'و (به شهاب آن انجم) از تسلط هر شیطان سرکش گمراه محفوظ داشتیم.'),
(3796, 37, 8, 'تا شیاطین هیچ از وحی و سخنان فرشتگان عالم بالا نشنوند و از هر طرف به قهر رانده شوند.'),
(3797, 37, 9, 'هم به قهر برانندشان و هم به عذاب دائم (قیامت) گرفتار شوند.'),
(3798, 37, 10, 'جز آنکه کسی (از شیاطین چون خواهد از عالم بالا) خبری برباید هم او را تیر شهاب فروزان تعقیب کند.'),
(3799, 37, 11, 'از این منکران قیامت بپرس که آیا خلقت آنها سخت‌تر است یا موجودات (با عظمت) دیگر که ما خلق کرده‌ایم؟ و ما اینان را در اول از گل چسبنده بیافریدیم.'),
(3800, 37, 12, 'بلکه تو (از انکار منکران) تعجب کردی و آنها هم به تمسخر پرداختند.'),
(3801, 37, 13, 'و هر گاه به آنها پند و اندرز دهند هیچ متذکر نمی‌شوند.'),
(3802, 37, 14, 'و چون آیت و معجزی مشاهده کنند باز هم به فسوس و مسخره پردازند.'),
(3803, 37, 15, 'و گویند: این خود پیداست که سحری بیش نیست.'),
(3804, 37, 16, '(و با تعجب و انکار گویند) آیا چون ما مردیم و خاک و استخوان پوسیده شدیم باز زنده و برانگیخته می‌شویم؟'),
(3805, 37, 17, 'یا پدران گذشته ما زنده می‌شوند؟!'),
(3806, 37, 18, 'بگو: آری شما (و هم پدرانتان) خوار و ذلیل به محشر مبعوث خواهید شد.'),
(3807, 37, 19, 'تنها به یک صیحه (اسرافیل) است که ناگاه همه (زنده شده و از قبرها بر خیزند و عرصه قیامت را) بنگرند.'),
(3808, 37, 20, 'و گویند: ای وای بر ما، این است روز جزای اعمال.'),
(3809, 37, 21, 'این است روز حکم (بر نیک و بد) که شما تکذیب آن می‌کردید.'),
(3810, 37, 22, '(و خطاب شود که) اینک ستمکاران را حاضر کنید با همسران (یا پیروان) و آنچه معبود ایشان بود،'),
(3811, 37, 23, 'که به جز خدا می‌پرستیدند، و همه را به راه دوزخ راه نمایید.'),
(3812, 37, 24, 'و (در موقف حساب) نگاهشان دارید که در کارشان سخت مسئولند.'),
(3813, 37, 25, '(به آنان خطاب شود که) چرا شما (در دفع عذاب) به یکدیگر نصرت نمی‌کنید؟'),
(3814, 37, 26, 'بلکه در این روز آنها همه سخت تسلیم و ذلیلند.'),
(3815, 37, 27, 'و در آن هنگامه برخی از آنان به بعضی دیگر رو نموده (یعنی مرئوسین با رؤسا) سؤال و جواب کنند.'),
(3816, 37, 28, 'گویند: شما بودید که از سمت راست (و چپ برای فریب) ما می‌آمدید.'),
(3817, 37, 29, 'آنها جواب دهند (که به ما مربوط نیست) بلکه شما خود ایمان نیاوردید.'),
(3818, 37, 30, 'و ما بر شما تسلط و حجتی نداشتیم بلکه شما خودتان طاغی و سرکش مردمی بودید.'),
(3819, 37, 31, 'پس امروز بر همه ما وعده (انتقام) خدایمان حتمی است، و البته عذاب را خواهیم چشید.'),
(3820, 37, 32, 'و ما شما را بدین سبب گمراه کردیم که خود گمراه بودیم.'),
(3821, 37, 33, 'و در آن روز آنها همه در عذاب مشترکند.'),
(3822, 37, 34, 'ما چنین از بد کاران انتقام می‌کشیم.'),
(3823, 37, 35, 'آنها بودند که چون لا اله الاّ اللّه (کلمه توحید) به ایشان گفته می‌شد (از قبول آن) سرکشی می‌کردند.'),
(3824, 37, 36, 'و می‌گفتند: آیا ما به خاطر شاعر دیوانه‌ای دست از خدایان خود برداریم؟!'),
(3825, 37, 37, '(چنین نیست که آنها پنداشتند) بلکه (محمّد صلی الله علیه و آله پیغمبر بزرگی است که برای تعلیم و هدایت خلق عالم) با برهان روشن حق آمد و (حجت و معجزاتش) صدق پیغمبران پیشین را نیز اثبات کرد.'),
(3826, 37, 38, 'شما (که او را تکذیب کردید امروز) عذاب سخت و دردناک خواهید چشید.'),
(3827, 37, 39, 'و جز به کردارتان مجازات نمی‌شوید.'),
(3828, 37, 40, '(امروز همه مسئولند) جز بندگان پاک برگزیده خدا.'),
(3829, 37, 41, 'آنان را (در بهشت ابد) روزی جسمانی و روحانی (بی حساب) معیّن است.'),
(3830, 37, 42, 'میوه‌های گوناگون بهشتی، و هم آنها (در آن عالم جاودانی) بزرگوار و محترمند.'),
(3831, 37, 43, 'در آن بهشت پر نعمت متنعّمند.'),
(3832, 37, 44, 'بر تختهای عالی رو به روی یکدیگر نشسته‌اند.'),
(3833, 37, 45, '(حور و غلمان) با جام شراب طهور بر آنان دور می‌زنند.'),
(3834, 37, 46, 'شرابی سپید و روشن که آشامندگان لذت کامل برند.'),
(3835, 37, 47, 'نه در آن می‌خمار و دردسری و نه مستی و مدهوشی است.'),
(3836, 37, 48, 'و در بزم حضورشان حوران زیبا چشمی است که (جز به شوهر خود) به هیچ کس ننگرند.'),
(3837, 37, 49, 'گویی آن حوران (در سفیدی و لطافت) بیضه مکنونند [یعنی تخم شتر مرغ که بسیار خوشرنگ است و شتر مرغ آن را با پر خویش می‌پوشاند تا غبار بر آن ننشیند. (م)].'),
(3838, 37, 50, 'در آنجا مؤمنان بعضی با بعضی به صحبت روی کنند.'),
(3839, 37, 51, 'یکی از آنها گوید: (ای رفیقان بهشتی) مرا (در دنیا) همنشینی (کافر) بود.'),
(3840, 37, 52, 'که با من می‌گفت: آیا تو (وعده‌های بهشت و قیامت را) باور می‌کنی؟'),
(3841, 37, 53, 'آیا چون مردیم و استخوان ما خاک راه شد باز (زنده شویم و) پاداش و کیفری یابیم؟'),
(3842, 37, 54, 'باز (این گوینده بهشتی به رفیقان) گوید: آیا می‌خواهید نظر کنید (و آن رفیق کافر را اینک در دوزخ بنگرید).'),
(3843, 37, 55, 'آن گاه خود بنگرد و او را در میان دوزخ (معذّب) بیند.'),
(3844, 37, 56, 'به او گوید: قسم به خدا که نزدیک بود مرا (همچون خود) هلاک گردانی.'),
(3845, 37, 57, 'و اگر نعمت و لطف خدای من (نگهدار) نبود من هم به دوزخ (نزد تو) از حاضر شدگان بودم.'),
(3846, 37, 58, '(و از خوشحالی گوید) آیا دیگر ما را مرگی نخواهد بود؟'),
(3847, 37, 59, 'جز همان مرگ اول (که در دنیا مردیم) و دیگر هیچ رنج و عذابی بر ما نخواهد بود؟'),
(3848, 37, 60, 'این است همان سعادت و رستگاری بزرگ.'),
(3849, 37, 61, 'آنان که سعی و عملی می‌کنند باید بر مثل این (بهشت جاودانی) کنند (نه بر دنیای دو روزه فانی).'),
(3850, 37, 62, 'آیا این پذیرایی (و مقام عالی) بهتر است یا درخت زقّوم جهنم؟'),
(3851, 37, 63, 'که آن درخت را ما بلای جان ستمکاران عالم گردانیدیم.'),
(3852, 37, 64, 'آن زقوم به حقیقت درختی است که از بن دوزخ برآید.'),
(3853, 37, 65, 'میوه‌اش (در خباثت) گویی سرهای شیاطین است.'),
(3854, 37, 66, 'اهل دوزخ از آن درخت (خباثت) آن طور می‌خورند که شکمها پر می‌سازند.'),
(3855, 37, 67, 'پس از خوردن زقّوم دوزخ بر آنها آمیزه‌ای از شراب سوزان خواهد بود.'),
(3856, 37, 68, 'و باز هم رجوعشان به سوی (عذاب دایم) جهنّم است.'),
(3857, 37, 69, 'چرا که آنها پدرانشان را در ضلالت و گمراهی یافتند.'),
(3858, 37, 70, 'و باز از پی آنها شتابان می‌رفتند.'),
(3859, 37, 71, 'و قبل از اینان بیشتر پیشینیان هم سخت گمراه بودند.'),
(3860, 37, 72, 'و ما رسولانی برای بیم و پند بر آنها فرستادیم.'),
(3861, 37, 73, '(همه را تکذیب کردند) پس بنگر تا عاقبت کار آن امتهای بیم داده شده (گمراه) به چه (سختی و هلاکت) انجامید.'),
(3862, 37, 74, 'جز بندگان پاک برگزیده خدا (همه هلاک شدند).'),
(3863, 37, 75, 'و همانا نوح ما را (بر یاری خود) ندا کرد و ما او را چه نیکو اجابت کردیم.'),
(3864, 37, 76, 'و او را با اهل بیتش (و پیروانش) همه را از اندوه و بلای بزرگ (هلاکت غرق) نجات دادیم.'),
(3865, 37, 77, 'و نژاد و اولاد او را روی زمین باقی داشتیم.'),
(3866, 37, 78, 'و در میان آیندگان نام نیکویش بگذاشتیم.'),
(3867, 37, 79, 'سلام و تحیّت بر نوح (خدا پرست) در میان عالمیان باد.'),
(3868, 37, 80, 'ما نیکوکاران را چنین پاداش می‌دهیم.'),
(3869, 37, 81, 'که او به حقیقت از بندگان با ایمان ما بود.'),
(3870, 37, 82, 'آن گاه دیگران را همه غرق دریای هلاک گردانیدیم.'),
(3871, 37, 83, 'و از پیروان نوح (در دعوت به توحید و خدا پرستی) به حقیقت ابراهیم (خلیل) بود.'),
(3872, 37, 84, 'که با قلبی پاک و سالم (از شرک) به سوی پروردگار خویش آمد.'),
(3873, 37, 85, 'هنگامی که با پدر (یعنی عموی) خود و قومش گفت: شما به پرستش چه مشغولید؟'),
(3874, 37, 86, 'آیا رواست که به دروغ خدایانی به جای خدای یکتا بر می‌گزینید؟'),
(3875, 37, 87, 'پس در این صورت به خدای جهانیان چه گمان می‌برید (و از قهر و انتقام خدا چگونه نمی‌ترسید).'),
(3876, 37, 88, 'آن گاه (ابراهیم تدبیری اندیشید و) به ستارگان آسمان نگاهی کرد.'),
(3877, 37, 89, 'و با قومش گفت که من بیمارم (و نتوانم به جشن عید بتان آیم).'),
(3878, 37, 90, 'قوم از او دست کشیده برگشتند (و از بتخانه در پی نشاط عید به باغ و صحرا شتافتند).'),
(3879, 37, 91, 'ابراهیم (که بتخانه را خلوت یافت) قصد بتهای آنان کرد و گفت: آیا شما غذا نمی‌خورید؟'),
(3880, 37, 92, 'چرا سخن نمی‌گویید؟ (شما چه خدایان بی‌اثر باطلی هستید!)'),
(3881, 37, 93, 'و محکم (با تبر) بر بتان زد (و جز بت بزرگ همه را درهم شکست).'),
(3882, 37, 94, 'قوم (آگه شدند و) با شتاب (از پی انتقام) به سوی او آمدند.'),
(3883, 37, 95, 'ابراهیم گفت: آیا رواست که شما چیزی به دست خود بتراشید و آن را پرستش کنید؟'),
(3884, 37, 96, 'در صورتی که شما و آنچه (از بتان) می‌سازید همه را خدا آفریده.'),
(3885, 37, 97, '(قوم حجت و برهانش نشنیدند و) گفتند: باید بر او آتشخانه‌ای بسازید و او را در آتش افکنید.'),
(3886, 37, 98, 'نمرودیان قصد مکر و ستمش کردند ما هم آنان را خوار و نابود ساختیم (و آتش را بر او گلستان کردیم).'),
(3887, 37, 99, 'و ابراهیم (پس از نجات از بیداد نمرودیان به قومش) گفت: من (با کمال اخلاص) به سوی خدای خود می‌روم که البته هدایتم خواهد فرمود.'),
(3888, 37, 100, 'بار الها، مرا فرزند صالحی که از بندگان شایسته تو باشد عطا فرما.'),
(3889, 37, 101, 'پس مژده پسر بردباری به او دادیم.'),
(3890, 37, 102, 'آن گاه که (آن پسر رشد یافت و) با او به سعی و عمل شتافت (یا در جهد و عبادت یا در سعی صفا و مروه با پدر همراه شد) ابراهیم گفت: ای فرزند عزیزم، من در عالم خواب چنین می‌بینم که تو را قربانی (راه خدا) می‌کنم، در این واقعه تو را چه نظری است؟ جواب داد: ای پدر، هر چه مأموری انجام ده که ان شاء اللّه مرا از بندگان با صبر و شکیبا خواهی یافت.'),
(3891, 37, 103, 'پس چون هر دو تسلیم امر حق گشتند و او را (برای کشتن) به روی در افکند.'),
(3892, 37, 104, 'و ما در آن حال (که کارد به گلویش کشید) خطاب کردیم که ای ابراهیم،'),
(3893, 37, 105, 'تو مأموریت عالم رؤیا را انجام دادی (کارد را از گلویش بردار). ما نیکو کاران را چنین نیکو پاداش می‌دهیم.'),
(3894, 37, 106, 'این ابتلا همان امتحانی است که (حقیقت حال اهل ایمان را) روشن می‌کند (که در راه خدا از هر چیز می‌گذرند).'),
(3895, 37, 107, 'و بر او (گوسفندی فرستاده و) ذبح بزرگی فدا ساختیم.'),
(3896, 37, 108, 'و ثنای او را در میان آیندگان وا گذاردیم.'),
(3897, 37, 109, 'سلام و تحیّت خدا بر ابراهیم باد.'),
(3898, 37, 110, 'ما نیکوان را این چنین پاداش نیکو می‌دهیم.'),
(3899, 37, 111, 'زیرا او از بندگان با ایمان ما بود.'),
(3900, 37, 112, 'و باز مژده فرزندش اسحاق را که پیغمبری از شایستگان است به او دادیم.'),
(3901, 37, 113, 'و بر ابراهیم و اسحاق برکت و خیر بسیار عطا کردیم، و از فرزندانشان برخی صالح و نیکوکار و برخی دانسته و آشکار به نفس خود ستمکار شدند.'),
(3902, 37, 114, 'و همانا ما بر موسی و هارون منّت گذاردیم.'),
(3903, 37, 115, 'و هر دو را با قومشان از بلای بزرگ (فرعونیان) نجات دادیم.'),
(3904, 37, 116, 'و آنها را یاری دادیم تا (بر فوعونیان) به حقیقت غالب شدند.'),
(3905, 37, 117, 'و به آن دو (پیغمبر) کتاب (تورات) روشن بیان را عطا کردیم.'),
(3906, 37, 118, 'و هر دو را به راه راست (خداپرستی) هدایت کردیم.'),
(3907, 37, 119, 'و برایشان نام نیک در میان آیندگان باقی گذاردیم.'),
(3908, 37, 120, 'سلام و تحیّت بر موسی و هارون باد.'),
(3909, 37, 121, 'ما چنین نیکوان را پاداش نیکو می‌دهیم.'),
(3910, 37, 122, 'زیرا هر دو از بندگان خاص با ایمان ما بودند.'),
(3911, 37, 123, 'و همانا الیاس یکی از رسولان بزرگ خدا بود.'),
(3912, 37, 124, 'وقتی که قومش را گفت: آیا خداترس و پرهیزکار نمی‌شوید (که ترک بت و خود پرستی کنید).'),
(3913, 37, 125, 'آیا بت بعل نام را (خدا) می‌خوانید و خدای بهترین آفریننده عالم را ترک می‌گویید؟'),
(3914, 37, 126, 'همان خدایی که آفریننده شما و پدران گذشته شماست (نه بتان جماد).'),
(3915, 37, 127, 'قوم الیاس (رسالت) او را تکذیب کردند، بدین سبب (برای انتقام و عذاب به محشر) احضار می‌شوند.'),
(3916, 37, 128, '(و همه هلاک شوند) جز بندگان پاک و برگزیده خدا.'),
(3917, 37, 129, 'و از او در میان آیندگان نام نیکو باقی گذاردیم.'),
(3918, 37, 130, 'سلام بر الیاس (و آل یاسین) باد.'),
(3919, 37, 131, 'ما نیکوان را چنین پاداش نیکو می‌دهیم.'),
(3920, 37, 132, 'زیرا او از بندگان خاص با ایمان ما بود.'),
(3921, 37, 133, 'و لوط هم یکی از رسولان خدا بود.'),
(3922, 37, 134, 'وقتی که ما (خواستیم قوم او را کیفر کنیم) او و اهل بیتش همه را نجات دادیم.'),
(3923, 37, 135, 'به جز پیر زالی (همسر او) که در میان قومی که هلاک شدند باقی ماند.'),
(3924, 37, 136, 'پس (از نجات لوط و اهلش) دیگران که بدکار بودند همه را هلاک ساختیم.'),
(3925, 37, 137, 'و شما مردم اینک بر دیار ویران قوم لوط می‌گذرید (و خرابه‌های آن را به چشم می‌نگرید) در صبح،'),
(3926, 37, 138, 'و شام، آیا (چشم عبرت نمی‌گشایید و) عقل را کار نمی‌بندید؟'),
(3927, 37, 139, 'و یونس نیز یکی از رسولان بزرگ خدا بود.'),
(3928, 37, 140, 'آن گاه که به کشتی پر جمعیتی گریخت (زیرا به قومش وعده عذاب داد و آنها از دل توبه و انابه کردند و خدا رفع عذاب کرد. او به دریای روم گریخت تا نزد قوم خجل و شرمنده نرود).'),
(3929, 37, 141, '(کشتی به خطر افتاد و اهل کشتی معتقد شدند که خطا کاری در میان آنهاست، خواستند قرعه زنند تا خطا کار را به قرعه تعیین کرده و غرق کنند) یونس قرعه زد و به نام خودش افتاد و از مغلوب شدگان (و غرق شوندگان) گردید.'),
(3930, 37, 142, 'و ماهی دریا او را به کام فرو برد و او در خور ملامت بود.'),
(3931, 37, 143, 'و اگر او به ستایش و تسبیح خدا نپرداختی،'),
(3932, 37, 144, 'تا قیامت در شکم ماهی زیست کردی.'),
(3933, 37, 145, 'باز او را (پس از چندین روز) از بطن ماهی به صحرای خشکی افکندیم در حالی که بیمار و ناتوان بود.'),
(3934, 37, 146, 'و (در آن صحرا) بر او درختی از کدو رویانیدیم (تا به برگش سایه‌بان و پوشش کند).'),
(3935, 37, 147, 'و او را بر قومی بالغ بر صد هزار یا افزون (به رسالت) فرستادیم.'),
(3936, 37, 148, 'و آن قوم چون ایمان آوردند ما هم تا هنگامی معیّن (مدت عمر آن قوم، از نعمت خود) بهره‌مندشان گردانیدیم.'),
(3937, 37, 149, 'پس تو از این مشرکان (که فرشتگان را دختران خدا می‌خوانند) بپرس که آیا خدا را فرزندان دختر است و آنان را پسر؟'),
(3938, 37, 150, 'یا آنکه چون ما فرشتگان را خلق می‌کردیم آنجا حاضر بودند و دیدند که آنها را زن آفریدیم؟'),
(3939, 37, 151, 'بدان که اینان (از جهل خود) بر خدا دروغی بسته و می‌گویند.'),
(3940, 37, 152, 'خدا را فرزند می‌باشد. البته دروغ می‌گویند.'),
(3941, 37, 153, 'آیا خدا دختران را بر پسران برگزید؟'),
(3942, 37, 154, 'چرا چنین جاهلانه حکم می‌کنید؟'),
(3943, 37, 155, 'آیا (با این ادلّه روشن) باز متذکر نمی‌شوید؟'),
(3944, 37, 156, 'آیا (برای عقیده باطل خود) دلیل روشنی دارید؟'),
(3945, 37, 157, 'کتاب و برهان‌تان را بیاورید اگر راست می‌گویید.'),
(3946, 37, 158, 'و (فاسدتر از این عقیده آنکه) بین خدا و جنّ نسبت و خویشی برقرار کردند در صورتی که جنّیان می‌دانند که (این عقاید وهم و خرافات است و به محشر برای کیفر) احضار خواهند شد.'),
(3947, 37, 159, 'خدا ذات پاکش از این اوصاف که (از جهل) به او نسبت می‌دهند منزّه است (و همه شما مسئولید).'),
(3948, 37, 160, 'جز بندگان پاک برگزیده خدا.'),
(3949, 37, 161, 'که شما و معبودانتان،'),
(3950, 37, 162, 'نتوانستید آنها را مفتون و گمراه کنید.'),
(3951, 37, 163, 'جز آن کس را که اهل دوزخ است.'),
(3952, 37, 164, '(ای رسول ما، مشرکان را بگو که فرشتگان می‌گویند) هیچ کس از ما فرشتگان نیست جز آنکه او را (در بندگی حق) مقامی معین است.'),
(3953, 37, 165, 'و همه (به فرمان خدا) نیکو صف آراسته‌ایم.'),
(3954, 37, 166, 'و دایم به تسبیح و ستایش او مشغولیم.'),
(3955, 37, 167, 'و همانا کافران می‌گفتند.'),
(3956, 37, 168, 'که اگر نزد ما کتابی از رسولان پیشین بود.'),
(3957, 37, 169, 'ما هم البته از بندگان برگزیده خدا می‌بودیم.'),
(3958, 37, 170, 'و (دروغ می‌گفتند، زیرا آن قرآن بزرگ خدا آمد و) به آن کافر شدند و به زودی (کیفر خود را) خواهند دانست.'),
(3959, 37, 171, 'و همانا عهد ما در باره بندگانی که به رسالت فرستادیم (چنین در علم ازلی) سبقت گرفته است.'),
(3960, 37, 172, 'که البته آنها (بر کافران) فتح و فیروزی یابند.'),
(3961, 37, 173, 'و همیشه سپاه ما (بر دشمن) غالبند.'),
(3962, 37, 174, 'پس اینک روی از آنها بگردان تا به وقتی معیّن.'),
(3963, 37, 175, 'و (عذاب دنیا و آخرت) آنها را به چشم ببین که آنها هم به زودی (فتح و فیروزی تو را) خواهند دید.'),
(3964, 37, 176, 'آیا کافران (به تمسخر، از تو) عذاب ما را با تعجیل می‌طلبند؟'),
(3965, 37, 177, '(بدانند که) چون عذاب قهر الهی پیرامون دیارشان فرود آید (بر آن کافران شبی بگذرد که) صبح بسیار بدی خواهند داشت.'),
(3966, 37, 178, 'اینک روی از آنها بگردان تا به وقتی معین.'),
(3967, 37, 179, 'و (عذاب و ذلّت آنها را) به چشم ببین که آنها هم به زودی (فتح و فیروزی تو را) خواهند دید.'),
(3968, 37, 180, 'پاک و منزه است پروردگار تو که خدایی مقتدر و بی‌همتاست و از توصیف (جاهلانه) خلق مبرّاست.'),
(3969, 37, 181, 'و سلام و تحیت الهی بر رسولان گرامی او باد.'),
(3970, 37, 182, 'و ستایش مخصوص خداست که آفریننده جهان‌ها و جهانیان است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص (رمز خدا و رسول است یا اشاره است به صمد و صانع و صادق از اسماء الهی یا به صفیّ و مصطفی که القاب پیغمبر است) قسم به قرآن صاحب مقام ذکر (و عزّت و شرافت و پند آموز عالمیان).'),
(3972, 38, 2, '(که محمّد صلّی اللّه علیه و آله و سلّم رسول حق و قرآن بزرگ معجز اوست) بلکه کافران (که منکر اویند) در مقام غرور و سرکشی و عداوت حق هستند.'),
(3973, 38, 3, 'ما پیش از اینها طوایف بسیاری را به هلاکت رسانیدیم و آن هنگام فریادها کردند و هیچ راه نجاتی بر آنها نبود.'),
(3974, 38, 4, 'و (مشرکان مکّه) تعجب کردند که رسولی از همان نژاد عرب برای تذکر و پند آنان آمد، و آن کافران گفتند: او ساحری دروغگوست.'),
(3975, 38, 5, 'آیا او چندین خدای ما را منحصر به یک خدا کرده (و به معبود یگانه دعوت می‌کند)؟ این بسیار تعجب آور و حیرت انگیز است.'),
(3976, 38, 6, 'و گروهی از سران قوم به راه افتاده (و چنین رأی دادند) که باید طریقه خود را ادامه دهید و در پرستش خدایان خود ثابت قدم باشید، این کاری است که مراد همه است (یا این قیام پیغمبر بر محو و نابود ساختن بتان از پیشامدهای بد روزگار است که باید با آن مقاومت کنیم).'),
(3977, 38, 7, 'این را (که دعوی محمّد است در توحید و یگانگی خدا) در آخرین ملّت (هم که ملّت و آیین مسیح است) نشنیده‌ایم (زیرا ملّت عیسی نیز به سه خدا و اقانیم سه گانه معتقد است)، این جز بافندگی و دروغ چیز دیگری نیست.'),
(3978, 38, 8, 'آیا میان همه ما (بزرگان عرب) قرآن (مخصوصا) بر او فرود آمد؟ (در صورتی که او هیچ امتیازی بر ما نداشت!) بلکه این کافران از (وحی) قرآن من در شک و ریبند، بلکه هنوز عذاب (قهر) مرا نچشیده‌اند (که دست از کفر بر نمی‌دارند).'),
(3979, 38, 9, 'آیا گنجهای رحمت خدای تو که در کمال اقتدار و بخشندگی است نزد این مردم است (تا مقام رسالت را به هر که خداهند بخشند).'),
(3980, 38, 10, 'یا مگر سلطنت آسمانها و زمین و هر چه بین آنهاست با این کافران است؟ پس (اگر چنین است) به هر سبب (که می‌توانند بر آسمانها) بالا روند (تا وحی را بر هر که خواهند فرود آرند).'),
(3981, 38, 11, 'این نالایق سپاه کفر (بدخواه اسلام) از احزاب مغلوب و نابود شدنی در آنجاست. [یعنی در بدر. و این از اخبار غیبی قرآن کریم است. (م).'),
(3982, 38, 12, 'پیش از این مشرکان هم قوم نوح و طایفه عاد (قوم هود) و فرعون (و فرعونیان) صاحب قدرت نیز تکذیب (پیغمبران خدا) کردند.'),
(3983, 38, 13, 'و طایفه ثمود (امت صالح) و قوم لوط و اصحاب ایکه (امت شعیب) این اقوام هم احزاب و لشکرهای بسیار (بر علیه انبیاء) بودند (و همه هلاک شدند).'),
(3984, 38, 14, 'و آنان به جز تکذیب انبیا فکری و عملی نداشتند، بدین جهت عقاب من بر آنها حتم و واجب گردید.'),
(3985, 38, 15, 'و این مردم جز یک صیحه (آسمانی که بر هلاکشان ندا کند) انتظاری ندارند و دیگر آن را باز گشتی نیست (دیگر ابدا به دنیا باز نمی‌گردند).'),
(3986, 38, 16, 'و کافران (به تمسخر) گفتند: ای خدا، حساب نامه اعمال ما را تعجیل کن و پیش از روز حساب انداز.'),
(3987, 38, 17, 'تو بر سخنان (طعن و سخریّه) آنها صبر کن و از بنده ما داود یاد کن که (در اجرای امر ما) بسیار نیرومند بود (از احدی اندیشه نداشت) و دایم توبه و انابه می‌کرد.'),
(3988, 38, 18, 'ما کوهها را با او مسخر کردیم که شب و روز خدا را تسبیح و ستایش می‌کردند.'),
(3989, 38, 19, 'و مرغان را مسخر (نغمه خوش او) کردیم که نزد او مجتمع گردند و همه به دربارش از هر جانب باز آیند (و در ستایش خدا با او هم آهنگ شوند).'),
(3990, 38, 20, 'و ملک و شاهی او را نیرومند ساختیم و به او قوّه درک حقایق و سخن قطعی در داوری (یا تمیز حقّ از باطل) عطا کردیم.'),
(3991, 38, 21, 'و آیا حکایت آن (دو فرشته به صورت) خصم به تو رسیده است که از بالای غرفه عبادتگاه (داود بی‌اجازه) بر او وارد شدند؟'),
(3992, 38, 22, 'هنگامی که (در محراب) بر داود داخل شدند و او از آنان سخت هراسان شد (که مبادا دشمن باشند) آنان بدو گفتند: مترس، ما دو تن (یا دو گروه) خصم یکدیگریم که برخی بر دیگری ستم کرده (و به حکومت پیش تو آمده‌ایم)، میان ما به حق حکم کن و با هیچ یک جور و طرفداری مکن و ما را به راه راست دلالت فرما.'),
(3993, 38, 23, 'این برادر من نود و نه رأس میش داراست و من یک میش، این یک را هم گفته به من واگذار، و با من به قهر و غلبه و تهدید آمیز خطاب کرده است.'),
(3994, 38, 24, 'داود گفت: البته بر تو ظلم کرده که خواسته است یک میش تو را به میش‌های (نود و نه گانه) خود اضافه کند، و بسیار معاشران و شریکان در حق یکدیگر ظلم و تعدی می‌کنند مگر آنان که اهل ایمان و عمل صالح هستند که آنها هم بسیار کم‌اند. و (پس از این قضاوت بی‌تأمّل و سریع) داود دانست که ما او را سخت امتحان کرده‌ایم، در آن حال از خدا عفو و آمرزش طلبید و به سجده افتاد و با تواضع و فروتنی (به درگاه خدا) بازگشت.'),
(3995, 38, 25, 'ما هم از آن کرده عفو کردیم و از او درگذشتیم و او نزد ما بسیار مقرب و نیکو منزلت است.'),
(3996, 38, 26, '(و او را گفتیم که) ای داود، ما تو را در روی زمین مقام خلافت دادیم، پس میان خلق خدا به حق حکم کن و هرگز هوای نفس را پیروی نکن که تو را از راه خدا گمراه سازد و آنان که از راه خدا گمراه شوند چون روز حساب و قیامت را فراموش کرده‌اند به عذاب سخت معذّب خواهند شد.'),
(3997, 38, 27, 'و ما آسمان و زمین و هر چه را بین آنهاست بازیچه و باطل خلق نکرده‌ایم، این گمان کافران است، و وای بر کافران از آتش دوزخ.'),
(3998, 38, 28, 'آیا ما آنان را که ایمان آورده و به اعمال نیکو پرداختند مانند مردم (بی ایمان) مفسد در زمین قرار می‌دهیم؟ یا مردمان با تقوا و خداترس را مانند فاسقان بدکار قرار می‌دهیم (و مانند آنان جزا خواهیم داد).'),
(3999, 38, 29, '(این قرآن بزرگ) کتابی مبارک و عظیم الشّأن است که به تو نازل کردیم تا امت در آیاتش تفکر کنند و صاحبان مقام عقل متذکر (حقایق آن) شوند.'),
(4000, 38, 30, 'و به داود (فرزندش) سلیمان را عطا کردیم، او بسیار نیکو بنده‌ای بود، زیرا بسیار به درگاه خدا با تضرع و زاری رجوع می‌کرد.'),
(4001, 38, 31, '(یاد کن) وقتی که بر او اسبهای بسیار تندرو و نیکو را (هنگام عصر) ارائه دادند (و او به باز دید اسبان پرداخت برای جهاد در راه خدا و از نماز عصر غافل ماند).'),
(4002, 38, 32, 'در آن حال گفت: (افسوس) که من از علاقه و حبّ اسبهای نیکو از ذکر و نماز خدا غافل شدم تا آنکه آفتاب در حجاب شب رخ بنهفت.'),
(4003, 38, 33, 'آن گاه (با فرشتگان موکّل آفتاب) خطاب کرد که (به امر خدا) آفتاب را بر من بازگردانید (چون برگشت اداء نماز کرد) و شروع به دست کشیدن بر ساق و یال و گردن اسبان کرد (و همه را برای جهاد در راه خدا وقف کرد).'),
(4004, 38, 34, 'و همانا ما سلیمان را در مقام امتحان آوردیم و کالبدی بر تخت وی افکندیم (برخی مفسران گفتند: یعنی دیوی را به جای او بر تخت بنشانیدیم. و برخی گفتند: چون گفت که من بر بستر صد زن خویش وارد شوم تا صد فرزند یابم و نگفت ان شاء اللّه و به خواست خدا و ذکر مشیّت الهی استثنا نکرد خدا از همه زنانش یک جسد بی‌جانی بر او به وجود آورد) آن گاه متذکر شد و باز به درگاه خدا توبه و انابه کرد.'),
(4005, 38, 35, 'عرض کرد: بار الها، به لطف و کرمت از خطای من در گذر و مرا ملک و سلطنتی عطا فرما که پس از من احدی را نسزد، که تو تنها بخشنده بی عوضی.'),
(4006, 38, 36, 'ما هم باد را مسخر فرمان او کردیم که به امرش هر جا می‌خواست به آرامی روان می‌شد.'),
(4007, 38, 37, 'و دیو و شیاطین را هم که بناهای عالی می‌ساختند و از دریا جواهرات گرانبها می‌آوردند نیز مسخر امر او کردیم.'),
(4008, 38, 38, 'و دیگران از شیاطین را (که در پی اضلال خلق بودند، به دست او) در غل و زنجیر کشیدیم.'),
(4009, 38, 39, 'این (نعمت سلطنت و قدرت) عطای ماست، اینک بی‌حساب به هر که خواهی عطا کن و از هر که خواهی منع.'),
(4010, 38, 40, 'و همانا او نزد ما بسیار مقرب و نیکو منزلت است.'),
(4011, 38, 41, 'و یاد کن از بنده ما ایّوب هنگامی که به درگاه خدای خود عرض کرد: (پروردگارا) شیطان مرا سخت رنج و عذاب رسانیده (تو از کرم نجاتم بخش).'),
(4012, 38, 42, '(خطاب کردیم که) پای به زمین زن (زد و چشمه آبی پدید آمد، گفتیم) این آبی است سرد برای شستشو و نوشیدن (در آن شستشو کن و از آن بیاشام تا از هر درد و الم بیاسایی).'),
(4013, 38, 43, 'و ما اهل و فرزندانی که از او مردند و به قدر آنها هم علاوه به او عطا کردیم تا در حق او لطف و رحمتی کنیم و تا صاحبان عقل (نتیجه صبر در بلا را) متذکر شوند.'),
(4014, 38, 44, 'و (ایوب را گفتیم) دسته‌ای از چوبهای باریک (خرما) به دست گیر و (بر تن زن خود که بر زدنش قسم یاد کردی) بزن و عهد و قسمت را نشکن (و زن را هم بی گناه نیازار) ما ایوب را بنده صابری یافتیم، نیکو بنده‌ای بود که دایم رجوع و توجهش به درگاه ما بود.'),
(4015, 38, 45, 'و باز یاد کن از بندگان خاص ما ابراهیم و اسحاق و یعقوب که همه (در انجام رسالت) صاحب اقتدار و بصیرت بودند.'),
(4016, 38, 46, 'ما آنان را خالص و پاکدل برای تذکر سرای آخرت گردانیدیم.'),
(4017, 38, 47, 'و آنها نزد ما از برگزیدگان و خوبان عالم بودند.'),
(4018, 38, 48, 'و باز یاد کن از اسماعیل و یسع و ذو الکفل که همه از نیکوان جهان بودند.'),
(4019, 38, 49, 'این آیات پند و یاد آوری (نیکان) است، و البته در جهان جاودانی برای اهل تقوا بسیار نیکو منزلگاهی است.'),
(4020, 38, 50, 'باغهای بهشت ابد که درهایش به روی آنان باز است.'),
(4021, 38, 51, 'در آنجا بر تختها تکیه زنند و میوه‌های بسیار خوش و شراب می‌طلبند.'),
(4022, 38, 52, 'و در خدمت آنها حوران جوان شوهر دوست با عفّتند.'),
(4023, 38, 53, 'این (نعمت ابد) همان است که در روز حساب به آنها وعده‌تان می‌دادند.'),
(4024, 38, 54, 'این است همان رزق بی انتهای ابدی ما.'),
(4025, 38, 55, 'حقیقت حال خوبان این است، و اهل کفر و طغیان را بدترین منزلگاه است.'),
(4026, 38, 56, 'آنان به دوزخ در آیند که بسیار بد آرامگاهی است.'),
(4027, 38, 57, 'این است (از قهر حق عذابشان) و آنجا آب گرم عفن حمیم و غسّاق [حمیم: آب جوشان. غسّاق: زرداب چرکین گند آلود. (م)] را هم باید بچشند و بنوشند.'),
(4028, 38, 58, 'و از این عذابهای گوناگون دیگر.'),
(4029, 38, 59, 'این گروهی هستند که با شما (رؤسای کفر و ضلالت) به دوزخ در آمدند (در این حال رؤسا گویند) بدا بر حال اینان که در عذاب آتش فروزان شدند.'),
(4030, 38, 60, 'اهل دوزخ در جواب (رؤسایشان) گویند: بلکه بدا بر احوال خود شما، زیرا شما آن را برای ما پیش فرستادید (و سبب شدید)، که بسیار آرامگاه بدی است.'),
(4031, 38, 61, 'باز تابعان به درگاه خدا عرض کنند: پروردگارا، آن کس که برای ما این عذاب را پیش فرستاد تو در آتش، عذابش را چندین برابر بیفزا.'),
(4032, 38, 62, 'و (اهل دوزخ با یکدیگر) گویند: چه شده که ما مردانی (مؤمن) را که از سفله و اشرار می‌شمردیم نمی‌بینیم؟'),
(4033, 38, 63, 'آیا (چون) ما آنها را (در دنیا) مسخره و استهزاء می‌کردیم (اینک به دوزخ در نیامده‌اند)؟ یا (آنها اهل دوزخ هستند و) چشمان ما بر آنها نمی‌افتد؟'),
(4034, 38, 64, 'این منازعه اهل آتش دوزخ محقق و حتمی است.'),
(4035, 38, 65, 'بگو که من رسولی منذر (و ناصح) بیش نیستم و جز خدای فرد قهّار (که بر همه عالم غالب و قاهر است) خدایی نیست.'),
(4036, 38, 66, 'آفریننده آسمانها و زمین و هر چه بین آنهاست، همان خدای مقتدر و بسیار آمرزنده.'),
(4037, 38, 67, 'بگو آن (حکایت که از قیامت و اهل بهشت و دوزخ برای شما آوردم) خبر بزرگ عالم است.'),
(4038, 38, 68, '(و دریغا که) شما از شنیدن آن خبر بزرگ اعراض می‌کنید.'),
(4039, 38, 69, 'مرا بر فرشتگان عالم بالا که (در قضیّه خلق آدم یا غیر آن) خصومت و گفتگو داشتند علمی (پیش از وحی خدا) نبود.'),
(4040, 38, 70, 'و به من وحی نمی‌رسد جز اینکه من با بیان روشن و آشکار (خلق را از عذاب خدا) بترسانم.'),
(4041, 38, 71, '(یاد کن) هنگامی که خدایت به فرشتگان گفت که من بشری از گل خواهم آفرید.'),
(4042, 38, 72, 'پس آن گاه که او را به خلقت کامل بیاراستم و از روح خود در او بدمیدم بر او به سجده در افتید.'),
(4043, 38, 73, 'پس تمام فرشتگان بدون استثنا سجده کردند.'),
(4044, 38, 74, 'مگر شیطان که غرور و تکبر ورزید و از زمره کافران گردید.'),
(4045, 38, 75, 'خدا به شیطان فرمود: ای ابلیس، تو را چه مانع شد که به موجودی (با قدر و شرافت) که من به دو دست (علم و قدرت) خود آفریدم سجده کنی؟ آیا تکبر و نخوت کردی یا از فرشتگان بلند رتبه عالم قدس اعلا بودی (که نمی‌بایست سجده کنند).'),
(4046, 38, 76, 'شیطان گفت: من از او بهترم، که مرا از آتش (نورانی سرکش) و او را از گل (تیره پست) خلقت کرده‌ای.'),
(4047, 38, 77, 'خدا فرمود (ای شیطان جاهل خودبین) اینک از این جایگاه بیرون رو که تو (غرور و تکبر کردی و) سخت رانده درگاه ما شدی.'),
(4048, 38, 78, 'و بر تو لعنت (و غضب) من تا روز جزا حتمی و محقّق است.'),
(4049, 38, 79, 'شیطان عرض کرد: پروردگارا، پس مهلتم ده که تا روز قیامت زنده مانم.'),
(4050, 38, 80, 'خدا فرمود: از مهلت یافتگانت قرار دادیم.'),
(4051, 38, 81, 'تا روز معیّن و وقت معلوم (که صلاح نظام عالم می‌دانیم).'),
(4052, 38, 82, 'شیطان گفت: به عزّت و جلال تو قسم که خلق را تمام گمراه خواهم کرد.'),
(4053, 38, 83, 'مگر خاصان از بندگانت را که دل از غیر بریدند و برای تو خالص شدند.'),
(4054, 38, 84, 'خدا فرمود: به حق سوگند و کلام من حق و حقیقت است.'),
(4055, 38, 85, 'که جهنم را از (جنس) تو و پیروانت از آنان تمام پر خواهم کرد.'),
(4056, 38, 86, '(ای رسول، به امت) بگو: من مزد رسالت از شما نمی‌خواهم و من (بی حجت و برهان الهی مقام وحی و رسالت را) بر خود نمی‌بندم.'),
(4057, 38, 87, 'این قرآن نیست جز اندرز و پند برای اهل عالم.'),
(4058, 38, 88, 'و شما منکران بر صدق و حقیقت این مقال پس از چندی (هنگام مرگ و انتقال به آخرت) به خوبی آگاه می‌شوید.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'این کتاب (عظیم الشّأن قرآن) از جانب خدای مقتدر حکیم نازل گردیده است.'),
(4060, 39, 2, 'ما این کتاب را بر تو به حق فرستادیم پس (به شکرانه این نعمت بزرگ) خدای را پرستش کن در حالی که دین را برای او خالص گردانیده باشی (و دل از غیر حق بگردانی).'),
(4061, 39, 3, 'آگاه باشید که دین خالص (بی هیچ شرک و ریا) برای خداست، و آنان که غیر خدا را (چون بتان و فرعونان) به دوستی و پرستش برگرفتند (گفتند) ما آن بتان را نمی‌پرستیم مگر برای اینکه ما را به درگاه خدا نیک مقرب گردانند. خدا البته (این عذر نپذیرد و) میان آنها در آنچه اختلاف می‌کنند حکم خواهد کرد، که خدا هرگز آن کس را که بسیار دروغگو و کافر نعمت است هدایت نخواهد کرد.'),
(4062, 39, 4, 'اگر خدا می‌خواست فرزندی اختیار کند همانا هر که را از مخلوقاتش خواستی بر می‌گزید (و لیکن این نقص است و) او (از هر گونه نقص و آلایش) پاک و منزه است، او خدای فرد قهّار بی‌مانند است.'),
(4063, 39, 5, 'آسمانها و زمین را به حق و راستی (و برای حکمت و غرض ثابتی) ایجاد کرد، شب را بر روز و روز را بر شب بپوشاند و خورشید و ماه را مسخر کرده، هر یک (بر مدار خود) به وقت معیّن گردش می‌کنند. آگه باشید که او خدای مقتدر مطلق و بسیار آمرزنده است.'),
(4064, 39, 6, 'او شما را از یک تن (آدم) آفرید پس، از آن جفت او (حوّا) را قرار داد و برای استفاده شما هشت قسم از چهارپایان ایجاد کرد، و شما را در باطن رحم مادران در سه تاریکی (مشیمه و رحم و بطن) با تحولات گوناگون (بدین خلقت زیبا) می‌آفریند، این خداست پروردگار شما که سلطان ملک وجود اوست، هیچ خدایی جز او نیست، پس ای مشرکان (نادان) از درگاه او به کجا می‌برندتان؟'),
(4065, 39, 7, 'اگر همه کافر شوید خدا از (طاعت) همه شما البته بی‌نیاز است و کفر (و شرک) را بر بندگانش نمی‌پسندد و اگر (طاعت و) شکرش به جای آرید از شما آن پسند اوست و (بدانید که در قیامت) هرگز بار گناه کسی را دیگری به دوش نخواهد گرفت و پس از این (زندگانی دنیای فانی) باز گشت همه شما به سوی خداست و او شما را به هر چه می‌کرده‌اید آگاه می‌سازد، که او از اسرار دلهای خلق به خوبی آگاه است.'),
(4066, 39, 8, 'و (بی چاره) انسان را هر گاه فقر و مصیبت و رنجی پیش آید در آن حال به دعا و توبه و انابه به درگاه خدای خود رود و چون نعمت و ثروتی از سوی خود به او عطا کند خدایی را که از این پیش می‌خواند به کلی فراموش سازد و برای خدای یکتا شریک و همتاهایی قرار دهد تا (خلق را) از راه خدا گمراه سازد. بگو: (ای نگون بخت) اندکی به کفر (و عصیان) لذّت و آسایش جو، که عاقبت از اهل آتش دوزخ خواهی بود.'),
(4067, 39, 9, '(آیا آن کس که شب و روز به کفر و عصیان مشغول است بهتر است) یا آن کس که ساعات شب را به طاعت خدا به سجود و قیام پردازد و از عذاب آخرت ترسان و به رحمت الهی امیدوار است؟ بگو: آیا آنان که اهل علم و دانشند با مردم جاهل نادان یکسانند؟ منحصرا خردمندان متذکر این مطلبند.'),
(4068, 39, 10, '(ای رسول ما، از قول من به امت) بگو: ای بندگان من که ایمان آورده‌اید، خدا ترس و پرهیزکار باشید، که آنان که متّقی و نیکوکارند (علاوه بر آخرت) در دنیا (هم) نصیبشان نیکویی و خوشی است، و زمین خدا بسیار پهناور است (اگر در مکانی ایمان و حفظ تقوا مشکل شد به شهر و دیاری دیگر روید و به راه دین صبر پیشه کنید که) البته صابران به حد کامل و بدون حساب پاداش داده خواهند شد.'),
(4069, 39, 11, 'بگو که من خود مأمورم که تنها خدا را پرستش کنم و دین را برای او خالص گردانم (و هرگز شرک نیاورم).'),
(4070, 39, 12, 'و باز مأمورم که در اسلام و در تسلیم امر خدا مقام اوّلیّت را دارا باشم.'),
(4071, 39, 13, 'باز بگو که من اگر خدای خود را نافرمانی کنم البته از عذاب روز بزرگ قیامت می‌ترسم.'),
(4072, 39, 14, 'باز بگو: من خدای یکتا را می‌پرستم و دینم را خاص و خالص برای او می‌گردانم (و غیر او را باطل محض می‌دانم).'),
(4073, 39, 15, 'شما هم هر که را می‌خواهید جز خدا بپرستید (که از غیر خدا جز زیان نمی‌بینید). بگو که زیانکاران آنان هستند که خود و اهل بیت خود را (از کفر و عصیان) در قیامت به خسران جاودان در افکنند، آگاه باشید که این همان زیان است که (در قیامت) بر همه آشکار است.'),
(4074, 39, 16, 'بر آنها از بالا و زیر، سایبان آتش دوزخ است. آن آتشی است که خدا از آن بندگان خود را می‌ترساند، که ای بندگان من از (آتش قهر) من بترسید.'),
(4075, 39, 17, 'و آنان که از پرستش طاغوت دوری جستند و به درگاه خدا با توبه و انابه باز گشتند آنها را بشارت و مژده رحمت است، (ای رسول) تو هم بندگان مرا (به لطف و رحمت من) بشارت آر.'),
(4076, 39, 18, 'آن بندگانی که سخن بشنوند و به نیکوتر آن عمل کنند، آنان هستند که خدا آنها را (به لطف خاص خود) هدایت فرموده و هم آنان به حقیقت خردمندان عالمند.'),
(4077, 39, 19, 'آیا کسی که وعده عذاب خدا بر او محقّق و حتمی (و به دوزخ قهر و شقاوت معذّب) است تو (به پند و نصیحت) می‌توانی از آن آتش قهرش برهانی؟!'),
(4078, 39, 20, 'لیکن آنان که متّقی و خدا ترس شدند برای آنها (در بهشت رضوان و باغ فردوس جاویدان) کاخ و عمارتها بالای یکدیگر بنا شده است که در جلو آن عمارات نهرهای آب جاری است. (این بهشت ابد) وعده خداست که خدا هرگز وعده خود را خلاف نخواهد کرد.'),
(4079, 39, 21, 'آیا (نشانه لطف‌های خدا را در دنیا) نمی‌بینی که خدا از آسمان آب باران نازل گردانید و آن را در روی زمین در نهرها و جویها روان ساخت آن گاه انواع نباتات گوناگون بدان برویاند باز (از سبزی و خرّمی) رو به خزان آرد و نخست بنگری که زرد شود و آن گاه خدا (چوب و علف) خشکش گرداند؟ همانا در آن صاحبان عقل را تذکر (قدرت و رحمت الهی) است.'),
(4080, 39, 22, 'آیا آن کس را که خدا برای اسلام شرح صدر (و فکر روشن) عطا فرمود که وی به نور الهی روشن است (چنین کس با مردم کافر بی نور تاریک دل یکسان است)؟ پس وای بر آنان که از قساوت (و شقاوت) دلهاشان از یاد خدا فارغ است! اینان هستند که دانسته به ضلالت و گمراهیند.'),
(4081, 39, 23, 'خدا قرآن را فرستاد که بهترین حدیث (و نیکوترین سخن آسمانی خدا) است، کتابی که آیاتش همه (در کمال فصاحت و اعجاز) با هم مشابه است و در آن ثنای خدا (و خاصان خدا) مکرر می‌شود، که از تلاوت (آیات قهر) آن خدا ترسان را لرزه بر اندام افتد و (با آیات رحمت) باز آرام و سکونت یابند و دلهایشان به ذکر خدا مشغول گردد. این (کتاب) همان (رحمت و) هدایت خداست که هر که را خواهد به آن رهبری فرماید، و هر کس را خدا به گمراهیش واگذارد دیگر هیچ هدایت کننده‌ای نخواهد داشت.'),
(4082, 39, 24, 'آیا آن کس که روز قیامت برای پرهیز از سختی عذاب دوزخ ناچار است که رخسارش را مقابل آتش سپر گرداند (مانند کسی است که از آن عذاب ایمن و آسوده است)؟ و (آن روز) به ظالمان خطاب شود که اینک کیفر ستمهایی که می‌کردید بچشید.'),
(4083, 39, 25, 'آنان که پیش از این امت بودند نیز (پیغمبران خود را) تکذیب کردند پس، از جایی که پی نمی‌بردند عذاب (قهر حق) بر آنها فرود آمد.'),
(4084, 39, 26, 'پس خدا عذاب ذلّت و خواری را در زندگانی دنیا به آنها چشانید در صورتی که عذاب آخرت آنها اگر بدانند بسیار سخت‌تر است.'),
(4085, 39, 27, 'و ما در این قرآن برای (هدایت) مردم هر گونه مثلهای روشن آوردیم، باشد که خلق متذکر (حقایق آن) شوند.'),
(4086, 39, 28, 'همان قرآن عربی فصیح که در آن هیچ ناراستی و حکم ناصواب نیست، باشد که مردم (از آن پند گرفته و) پرهیزگار شوند.'),
(4087, 39, 29, 'خدا (برای کفر و ایمان) مثلی زده (بیاموزید) آیا شخصی که اربابانی متعدد دارد همه مخالف یکدیگر (و هر کدام او را به کاری پر رنج و زحمت می‌گمارند) با آن شخصی که تسلیم امر یک نفر است (که به او مهربان است و هیچ تکلیف سختی نمی‌کند) حال این دو شخص یکسان است؟ خدا را سپاس (که حالشان هرگز یکسان نیست) لیکن اکثر این مردم درک (این حقیقت) نمی‌کنند.'),
(4088, 39, 30, '(ای رسول عزیز ما) شخص تو و این خلق البته به مرگ از دار دنیا خواهید رفت.'),
(4089, 39, 31, 'آن گاه روز قیامت همه در پیشگاه عدل پروردگار خویش با یکدیگر ستیزه (و دادخواهی) می‌کنید.'),
(4090, 39, 32, 'پس (در آن روز) از آن کسی که بر خدا دروغ بسته و وعده صدق رسولان خدا را که به او رسیده تکذیب کرده هیچ کس ستمکارتر هست؟ آیا منزلگاه کافران در آتش دوزخ نیست؟'),
(4091, 39, 33, 'و آن کس که (وعده) صدق را آورد و (آن کس که) آن را تصدیق کرد (و به خدا و قیامت ایمان آورد) هم آنها به حقیقت اهل تقوا هستند.'),
(4092, 39, 34, 'برای آن بندگان نزد خدایشان از هر نعمتی که بخواهند مهیّاست، که این پاداش نیکو کاران عالم است.'),
(4093, 39, 35, 'تا خدا زشت‌ترین گناهانشان را مستور و محو گرداند و بسی بهتر از این اعمال نیکشان به آنها پاداش عطا کند.'),
(4094, 39, 36, 'آیا خدای (مهربان) برای بنده‌اش کافی نیست؟ و مردم (مشرک) تو را از قدرت غیر خدا می‌ترسانند! و هر که را خدا به گمراهی خود واگذارد دیگر او را هیچ راهنمایی نخواهد بود.'),
(4095, 39, 37, 'و هر کس را خدا هدایت کند دیگر احدی او را گمراه نتواند کرد. آیا خدا مقتدر و غالب (بر تمام قوای عالم) و منتقم (از همه ستمکاران جهان) نیست؟'),
(4096, 39, 38, 'و اگر از این مشرکان بپرسی که زمین و آسمانها را که آفریده است؟ البته جواب دهند: خدا آفریده. پس به آنها بگو: چه تصور می‌کنید، آیا همه بتهایی که جز خدا می‌خوانید اگر خدا بخواهد مرا رنجی رسد آن بتان می‌توانند آن را رفع کنند؟ یا اگر خدا بخواهد مرا به رحمتی رساند بتان می‌توانند آن رحمت را از من باز دارند؟ بگو: خدا مرا کافی است، که متوکلان عالم بر او توکل می‌کنند.'),
(4097, 39, 39, 'باز بگو: ای قوم، شما را آنچه میسّر است (و در خور جهل و گمراهی شماست) عمل کنید، من هم (آنچه روا دانم) عمل می‌کنم و به زودی معلوم شما گردد.'),
(4098, 39, 40, 'که عذاب با ذلّت و خواری که را فرا رسد و آلام همیشگی (دوزخ) بر که فرود آید؟'),
(4099, 39, 41, '(ای رسول) این کتاب الهی را ما به حقّ برای (هدایت) خلق بر تو فرستادیم، اینک هر که هدایت یافت نفع آن و هر که به گمراهی شتافت زیان آن بر شخص اوست و تو (پس از تبلیغ رسالت و اتمام حجّت) دیگر وکیل خلق و نگهبان امّت (از قهر حقّ) نخواهی بود.'),
(4100, 39, 42, 'خداست آن که وقت مرگ ارواح خلق را می‌گیرد و آن کس را که هنوز مرگش فرا نرسیده نیز در حال خواب روحش را قبض می‌کند، سپس آن را که حکم به مرگش کرده جانش را نگاه می‌دارد و آن را که نکرده (به بدنش) می‌فرستد تا وقت معیّن (مرگ). در این کار نیز ادله‌ای (از قدرت الهی) برای متفکران پدیدار است.'),
(4101, 39, 43, 'بلکه این مردم کافر مشرک خدا را وا گذارده و بتهایی (بی اثر) را شفیعان خود بر گرفتند. بگو: اگر چه این بتان کمتر چیزی در جهان مالک نباشند و عقل و ادراکی هیچ نداشته باشند (باز شفیع شما توانند شد).'),
(4102, 39, 44, 'بگو: شفاعت همه خلقان با خداست، که سلطان ملک آسمانها و زمین و است و پس از مرگ و فنا بازگشت همه شما به سوی اوست.'),
(4103, 39, 45, 'و چون نزد مردم بی‌ایمان به آخرت، خدا را به یکتایی یاد کنند (آنها از ذکر حق) سخت ملول و دلتنگ می‌شوند و هرگاه ذکر غیر خدا (از بتها و امور مادی) کنند خرّم و دلشاد می‌گردند.'),
(4104, 39, 46, 'بگو: پروردگارا، ای خالق آسمانها و زمین، ای دانای عالم پیدا و پنهان، تو خود میان بندگان در آنچه خلاف و نزاع برانگیزند حکم خواهی کرد.'),
(4105, 39, 47, 'و اگر مردم ستمکار و ظالم هر آنچه در زمین است دو برابر دارا باشند البته خواهند که برای نجات خود از سختی عذاب قیامت همه را فدا کنند، و البته (آن روز) از (قهر) خدا عذابی که گمان نمی‌بردند بر آنان پدید آید.'),
(4106, 39, 48, 'و (آن در) نتیجه زشتی و بدکاریهاشان ظاهر شود و عذاب قیامت که با انکار و استهزاء تلقّی می‌کردند بر آنان احاطه کند.'),
(4107, 39, 49, 'آری آدمی (ناسپاس) چون رنج و دردی به او رسد ما را به دعا می‌خواند و باز چون (آن رنج و عذاب را برداشتیم و) نعمت و دولت به او دادیم گوید: این نعمت دانسته (و به استحقاق) نصیب من گردید. (چنین نیست) بلکه آن امتحان وی است و لیکن اکثر مردم آگه نیستند.'),
(4108, 39, 50, 'پیشینیان هم چنین می‌گفتند (که ما مال و دولت را به تدبیر و لیاقت خود یافتیم) اما (خطا گفتند که وقت مرگ و هلاک) مال و دولتی که اندوختند هیچ به فریادشان نرسید.'),
(4109, 39, 51, 'و کیفر زشتی و بدکاریها که (برای کسب مال) انجام دادند به آنها رسید. و ستمکاران از این مردم نیز به زودی کیفر کردار بدشان را خواهند یافت و هرگز از قهر و قدرت خدا رهایی نمی‌یابند.'),
(4110, 39, 52, 'آیا مردم ندانستند که خدا البته هر که را خواهد روزی وسیع دهد و هر که را خواهد تنگ روزی سازد؟ در این (اختلاف روزی خلق) هم ادله‌ای (از قدرت الهی) برای اهل ایمان پدیدار است.'),
(4111, 39, 53, '(ای رسول رحمت) بدان بندگانم که (به عصیان) اسراف بر نفس خود کردند بگو: هرگز از رحمت (نامنتهای) خدا نا امید مباشید، البته خدا همه گناهان را (چون توبه کنید) خواهد بخشید، که او خدایی بسیار آمرزنده و مهربان است.'),
(4112, 39, 54, 'و به درگاه خدای خود به توبه و انابه باز گردید و تسلیم امر او شوید پیش از آنکه عذاب به شما فرا رسد و هیچ نصرت و نجاتی نیابید.'),
(4113, 39, 55, 'و بهترین دستور کتابی را که بر شما از جانب خدایتان نازل شده است پیروی کنید پیش از آنکه عذاب (قهر خدا به کیفر گناهان) بر شما ناگهان فرود آید و شما آگاه نباشید.'),
(4114, 39, 56, 'تا مبادا کسی فریاد واحسرتا بر آرد و گوید: ای وای بر من که جانب امر خدا را فرو گذاشتم و وعده‌های خدا را مسخره و استهزا نمودم.'),
(4115, 39, 57, 'یا آنکه (از فرط پشیمانی در پی آرزوی محال آید و) گوید: (افسوس) اگر خدا مرا (به لطف خاص) هدایت فرمودی من نیز از اهل تقوا بودم.'),
(4116, 39, 58, 'یا آنکه چون عذاب (خدا) را به چشم مشاهده کند گوید: (ای فریاد) کاش بار دیگر (به دنیا) باز می‌گشتم تا از نیکوکاران می‌شدم.'),
(4117, 39, 59, 'آری (ای بدبخت) آیات کتاب من که محقّقا برای (هدایت) تو آمد تو آن را تکذیب کردی و راه تکبر و سرکشی پیش گرفتی و از زمره کافران بد کیش گردیدی.'),
(4118, 39, 60, 'و روز قیامت کسانی را که بر خدا دروغ بستند بنگری که همه رویشان سیاه شده است (رسوای خدا و خلق گردیدند) آیا متکبران را نه منزل در دوزخ است؟'),
(4119, 39, 61, 'و خدا اهل تقوا را به موجبات رستگاری (و اعمال صالح) از عذاب نجات خواهد داد که هیچ رنج و المی به آنها نرسد و هرگز غم و اندوهی بر دلشان راه نیابد.'),
(4120, 39, 62, 'خدا آفریننده هر چیز است و بر هر چیز هم او نگهبان است.'),
(4121, 39, 63, 'کلیدهای خزائن آسمانها و زمین او راست و آنان که به آیات خدا کافر شدند هم آنان زیانکاران عالمند.'),
(4122, 39, 64, '(ای رسول به مشرکان) بگو: ای مردم نادان مرا امر می‌کنید که غیر خدا را پرستش کنم؟'),
(4123, 39, 65, '(هنوز مشرکان جاهل از تو موحد کامل طمع شرک دارند؟) و حال آنکه به تو و به رسولان پیش از تو چنین وحی شده که اگر به خدا شرک آوری عملت محو و نابود می‌گردد و سخت از زیانکاران خواهی گردید.'),
(4124, 39, 66, 'بلکه تنها خدا را پرستش کن و از شکر گزاران (نعمت الهی) باش.'),
(4125, 39, 67, 'و (آنان که غیر خدا را طلبیدند) خدا را چنان که شاید به عظمت نشناختند، و اوست که روز قیامت زمین در قبضه قدرت او و آسمانها در پیچیده به دست سلطنت اوست. آن ذات پاک یکتا منزّه و متعالی از شرک مشرکان (بلکه از فکر موحدان) است.'),
(4126, 39, 68, 'و صیحه‌ای در صور (اسرافیل) بدمند تا جز آن که خدا (بقای او) خواسته دیگر هر که در آسمانها و زمین است همه یکسر مدهوش مرگ شوند، آن گاه صیحه دیگری در آن دمیده شود که ناگاه خلایق همه (از خواب مرگ) بر خیزند و نظاره (واقعه محشر) کنند.'),
(4127, 39, 69, 'و زمین (محشر) به نور پروردگار خود روشن گردد و نامه (اعمال خلق در پیشگاه عدل حق) نهاده شود و انبیاء و شهداء (بر گواهی) احضار شوند و میان خلق به حق حکم شود و به هیچ کس ابدا ظلمی نخواهد شد.'),
(4128, 39, 70, 'و هر کس به پاداش عملش تمام برسد و خدا از هر کس به افعال خلق آگاه‌تر است.'),
(4129, 39, 71, 'و آنان را که کافر شدند فوج فوج به جانب دوزخ رانند تا چون آنجا رسند درهای جهنّم (به رویشان) بگشایند و خازنان دوزخ به آنها گویند: مگر پیغمبرانی از خودتان برای (هدایت) شما نیامدند و آیات الهی را برایتان تلاوت نکردند و شما را از ملاقات این روز سخت نترسانیدند؟ جواب دهند: بلی و لیکن (افسوس که ما پند نگرفتیم و به کفر و عصیان خود را مستحق عذاب حرمان کردیم و) وعده عذاب بر کافران محقّق و حتمی گردید.'),
(4130, 39, 72, 'آن گاه به آن کافران خطاب شود: اینک از هر در به دوزخ داخل شوید و در آن عذاب جاودان بمانید که متکبران را (دوزخ) بسیار بد منزلگاهی است.'),
(4131, 39, 73, 'و متقیان خدا ترس را فوج فوج به سوی بهشت برند تا چون بدانجا رسند و همه درهای بهشت (به رویشان به احترام) بگشایند و خازنان بهشتی (به تهنیت) گویند: سلام بر شما باد (خوشا به حال شما) که چه خوش عیش ابدی نصیب شما گردید حالی در این بهشت ابد در آیید و جاودان متنعّم باشید.'),
(4132, 39, 74, '(مؤمنان به بهشت در آیند) و گویند: ستایش خدای را که وعده لطف و رحمتش را بر ما محقق فرمود و ما را وارث همه سرزمین بهشت گردانید که هر جای آن بخواهیم منزل گزینیم. (بلی آن روز) پاداش نیکوکاران بسیار نیکو خواهد بود.'),
(4133, 39, 75, 'و (در آن روز) فرشتگان (رحمت) را مشاهده کنی که گرداگرد عرش (با عظمت الهی) در آمده و به تسبیح و ستایش خدایشان مشغولند و میان اهل بهشت و دوزخ به حق حکم شود و (همه زبان به حمد خدا گشایند و) گویند: سپاس و ستایش خاصّ خدای یکتا پروردگار جهانیان است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حم (از اسرار خدا و رسول است).'),
(4135, 40, 2, 'نزول این کتاب آسمانی (قرآن) از جانب خدای مقتدر داناست.'),
(4136, 40, 3, 'خدایی که بخشنده گناه و پذیرنده توبه (بندگان با ایمان) و منتقم سخت (از مردم ظالم بی‌ایمان) و صاحب رحمت و نعمت است، جز او هیچ خدایی نیست، بازگشت همه به سوی اوست.'),
(4137, 40, 4, 'و (بدان ای رسول که) در آیات خدا کسی به (انکار و) جدل جز مردم کافر برنخیزد پس تو از اینکه کافران بر شهر و دیارها مسلط و متصرّفند دل نبازی (که اگر به آنها مهلتی دادیم موقع انتقام خواهد رسید).'),
(4138, 40, 5, '(اگر امت تو را تکذیب کردند غم مدار که) پیش از اینان هم قوم نوح و طوایف بعد از نوح نیز (پیغمبران را) تکذیب کردند و هر امتی همّت گماشت که پیغمبر خود را دستگیر (و هلاک) گرداند و جدل و گفتار باطل به کار گرفت تا برهان حق را پایمال سازد، من هم آنها را (به کیفر کفر) گرفتم و چگونه عقوبت سخت کردم!'),
(4139, 40, 6, 'و همین گونه وعده خدای تو بر (عقاب و کیفر) کافران (امتت) محقق و حتم است که آنها همه اهل دوزخ هستند.'),
(4140, 40, 7, 'فرشتگانی که عرش (با عظمت الهی) را بر دوش گرفته و آنان که پیرامون عرشند به تسبیح و ستایش حق مشغولند، هم خود به خدا ایمان دارند و هم برای اهل ایمان از خدا آمرزش و مغفرت می‌طلبند که ای پروردگاری که علم و رحمت بی‌منتهایت همه اهل عالم را فرا گرفته است، گناه آنان که توبه کردند و راه (رضای) تو را پیمودند ببخش و آنان را از عذاب دوزخ محفوظ دار.'),
(4141, 40, 8, 'پروردگارا، تو آنها را به بهشت عدنی که وعده‌شان فرمودی با پدران (و مادران) صالح و هم جفت و فرزندانشان و اصل گردان، که همانا تویی خدای با حکمت و اقتدار.'),
(4142, 40, 9, 'و هم آن مؤمنان را از ارتکاب اعمال زشت نگاه دار، که هر که را تو از زشتکاری امروز محفوظ داری در حقّ او رحمت و عنایت بسیار فرموده‌ای و آن به حقیقت رستگاری بزرگ (از بلایا و فیروزی عظیم در آخرت) خواهد بود.'),
(4143, 40, 10, 'آنان که کافر شدند (چون به دوزخ در افتند و از فرط خشم دشمن جان خود شوند) به آنها خطاب شود که خشم و قهر خدا بسیار از خشم شما بر خود سخت‌تر است چرا که شما را به ایمان دعوت می‌کردند و شما کافر می‌شدید.'),
(4144, 40, 11, 'در آن حال کافران گویند: پروردگارا، تو ما را دو بار بمیراندی و باز زنده کردی (یک بار در دنیا میراندی و در قبور و عالم برزخ زنده کردی، دوم بار در قبور میراندی و به قیامت زنده کردی) تا ما به گناهان خود اعتراف کردیم، آیا اینک ما را راهی هست که از این عذاب دوزخ بیرون آییم؟'),
(4145, 40, 12, '(به آنها گویند) این عذاب برای آن است که شما چون خدا به یکتایی یاد می‌شد به او کافر می‌شدید و اگر بر او شریک می‌گرفتند ایمان می‌آوردید، پس اینک حکم (عفو و عقوبت شما) با خدای متعال بزرگوار است.'),
(4146, 40, 13, 'اوست خدایی که به شما آیات (قدرت) خود را (در عالم آفاق و انفس) پدیدار می‌سازد و از آسمان (به وسیله برف و باران) بر شما روزی می‌فرستد، و متذکر این معنی نشوند مگر کسانی که دایم رو به درگاه خدا آرند.'),
(4147, 40, 14, 'پس خدا را بخوانید در حالی که دین را برای او خالص کرده باشید هر چند کافران نخواهند.'),
(4148, 40, 15, 'که او دارای شئون و مراتب رفیع و صاحب عرش (عظیم) است، روح (همان فرشته جان بخش) را از امر خود بر هر که از بندگانش بخواهد می‌فرستد تا خلق را از روز ملاقات (ثواب و عقاب) بترساند.'),
(4149, 40, 16, 'آن روزی که همه خلق پدیدار شوند (و نیک و بدشان آشکار گردد) که هیچ کرده آنها بر خدا پنهان نباشد. در آن روز سلطنت عالم با کیست؟ با خدای یکتای بس قاهر منتقم است.'),
(4150, 40, 17, 'آن روز هر نفسی به آنچه کرده جزا خواهد یافت، آن روز بر هیچ کس ستم نخواهد شد که خدا (ی عادل) حساب خلق به یک لحظه می‌کند.'),
(4151, 40, 18, 'و (ای رسول) امت را از روز نزدیک (قیامت) بترسان که (از هول و ترس) آن روز جانها به گلو می‌رسد و از بیم آن حزن و خشم خود فرو می‌برند و ستمکاران را هیچ یار گرم و دلسوز و یاوری که شفاعتش پذیرفته شود نخواهد بود.'),
(4152, 40, 19, 'و خدا بر نگاه خیانت چشم خلق و اندیشه‌های نهانی دلها (ی مردم) آگاه است.'),
(4153, 40, 20, 'و خدا به حق حکم می‌کند و غیر او آنچه را به خدایی می‌خوانند هیچ حکم (و اثری) در جهان نتوانند داشت، خداست که (دعای خلق را) شنوا و (به احوال بندگان) بیناست.'),
(4154, 40, 21, 'آیا (مردم) در روی زمین سیر و سفر نکردند تا عاقبت کار پیشینیانشان را ببینند که آنها با آنکه بسیار از اینان قوی‌تر و آثار وجودیشان در روی زمین بیشتر بود سرانجام خدا به کیفر گناهانشان بگرفت (و همه را هلاک ساخت) و از (قهر و انتقام) خدا هیچ کس نگهدارشان نبود.'),
(4155, 40, 22, 'این هلاکت پیشینیان بدین سبب بود که پیمبرانشان با آیات و ادلّه روشن به سوی آنها می‌آمدند و آنان (از جهل و عناد) کافر شدند، خدا هم آنان را (به عقوبت) گرفت، که خدا بسیار مقتدر و سخت کیفر است.'),
(4156, 40, 23, 'و همانا موسی را با آیات و معجزات و حجّت آشکار فرستادیم.'),
(4157, 40, 24, 'به سوی فرعون و هامان و قارون، آنها گفتند: او ساحر بسیار دروغگویی است.'),
(4158, 40, 25, 'و آن گاه که او از جانب ما به صدق و حقیقت به سوی آنان آمد (فرعون و قومش به مردم) گفتند: بروید پسران آنان که به موسی ایمان آوردند بکشید و زنانشان را زنده گذارید (تا ضعیف و نابود شوند. و ما بالعکس آنها را قوی کردیم) آری مکر و تدبیر کافران جز در ضلالت (و خسرانشان) به کار نیاید.'),
(4159, 40, 26, 'و فرعون (به درباریانش) گفت: بگذارید تا من موسی را به قتل رسانم و او خدای خود را به یاری طلبد، می‌ترسم که آیین شما را تغییر دهد و در زمین فتنه و فسادی برانگیزد.'),
(4160, 40, 27, 'و موسی گفت: من به خدای آفریننده من و شما (و همه عالم) از شرّ هر (کافر) متکبری که به روز حساب ایمان نمی‌آورد پناه می‌برم.'),
(4161, 40, 28, 'و مرد با ایمانی از آل فرعون که ایمانش را پنهان می‌داشت (با فرعونیان) گفت: آیا مردی را به جرم اینکه می‌گوید: پروردگار من خداست می‌کشید، در صورتی که با معجزه و ادلّه روشن از جانب خدایتان برای (هدایت) شما آمده است؟! اگر او دروغگوست گناه دروغش بر خود اوست و لیکن اگر راستگو باشد از وعده‌های او بعضی به شما برسد (و همه هلاک خواهید شد) که خدا البته مردم مسرف (ستمکار) و دروغگو را هرگز هدایت نخواهد کرد.'),
(4162, 40, 29, 'ای قوم، امروز ملک و سلطنت و چیرگی بر زمین (مصر) با شماست، لیکن اگر قهر و انتقام خدا بر ما برسد کیست که ما را از آن نجات تواند داد؟ فرعون باز گفت: جز آن که رأی (به قتل موسی) دادم رأی دیگر نمی‌دهم (و جز این صلاح نمی‌دانم) و شما را جز به راه صواب دلالت نمی‌کنم.'),
(4163, 40, 30, 'باز همان شخص با ایمان (به فرعونیان) گفت: ای قوم، من بر شما می‌ترسم از روزی مانند روز سخت احزاب (و امم پیشین).'),
(4164, 40, 31, 'مثل روزگار ناگوار قوم نوح و عاد و ثمود و امم بعد از اینان (که همه به کیفر کفر و عصیان هلاک شدند) و خدا هیچ اراده ظلم در حق بندگان نکند (و هلاک آنها به سوء اختیار خود است).'),
(4165, 40, 32, 'و ای قوم، من بر شما از (عذاب) روز قیامت که خلق (از سختی آن به فریاد آیند و) یکدیگر را بخوانند سخت می‌ترسم.'),
(4166, 40, 33, 'روزی که (از عذاب آن) به پشت بگریزید و هیچ از (قهر) خدا پناهی نیابید. و هر که را خدا در گمراهی واگذارد دیگر برای او راهنمایی نیست.'),
(4167, 40, 34, 'و از این پیش یوسف (کنعان) به سوی شما (مصریان) با معجزات و ادلّه روشن (به رسالت) آمد و دایم از آیاتی که برای شما آورد در شک و ریب بماندید تا آنکه یوسف از دنیا برفت، گفتید که دیگر خدا پس از یوسف هرگز رسولی نمی‌فرستد. بلی این چنین خدا مردم مسرف (ستمگری) را که (در آیات خدا و معجزات رسل) در شک و ریبند به گمراهی و خذلان خود وا می‌گذارد.'),
(4168, 40, 35, 'آنان که در آیات خدا بی‌هیچ حجت و برهان به جدال و انکار برمی‌خیزند این کار، سخت خدا (و رسول) و اهل ایمان را به خشم و غضب آرد. بلی این گونه خدا بر دل هر متکبر خود کامه‌ای مهر (شقاوت) می‌زند (که با سعادت خود دشمن می‌شود).'),
(4169, 40, 36, 'و فرعون (به وزیرش) گفت: ای هامان، برای من کاخی بلند پایه (آسمان خراش) بنیاد کن تا شاید به درها راه یابم.'),
(4170, 40, 37, 'به درهای آسمانها و بر خدای موسی آگاه شوم (و از صدق و کذب موسی تحقیقی کنم) و من هنوز موسی را دروغگوی می‌پندارم. و این چنین در نظر جاهلانه فرعون عمل زشتش زیبا نمود و راه حق (و خدا پرستی) بر او مسدود شد، و مکر و تدبیر فرعون جز بر زیان و هلاکش به کار نیامد.'),
(4171, 40, 38, 'و باز همان شخص مؤمن گفت: ای قوم، مرا پیروی کنید تا شما را به راه حق و صواب دلالت کنم.'),
(4172, 40, 39, 'ای قوم، این زندگانی (فانی) دنیا متاع ناچیزی بیش نیست و سرای آخرت منزلگاه ابدی (و حیات جاودانی) است.'),
(4173, 40, 40, '(و بدانید که) هر کس کار بدی (در دنیا) کرده (آنجا) الاّ به مثل آن مجازات نشود، و هر که از مرد و زن عمل صالح به جا آورده در صورتی که با ایمان باشند آنان در بهشت جاودان داخل شوند و آنجا از رزق بی‌حساب (و نعمت بی‌شمار) برخوردار گردند.'),
(4174, 40, 41, 'و ای قوم، چرا من شما را به راه نجات (و طریق بهشت) دعوت می‌کنم و شما مرا به سوی آتش دوزخ می‌خوانید؟'),
(4175, 40, 42, 'شما مرا دعوت می‌کنید که به خدا کافر شوم و از بی‌دانشی بتی را که در جهان هیچ مؤثر نمی‌دانم شریک خدا قرار دهم، و من شما را به سوی خدای مقتدر بسیار با بخشش و آمرزش می‌خوانم.'),
(4176, 40, 43, 'بی‌شک آنچه شما مرا به سوی او می‌خوانید (از بتها و فراعنه و معبودان باطل) آن هیچ دعوتی (و اثر سودمندی) در دنیا و آخرت ندارد و محققا (بدانید که در قیامت) بازگشت ما به سوی خداست و البته مسرفان (ستمکاران فاسق در آنجا) همه اهل آتش دوزخند.'),
(4177, 40, 44, 'پس به زودی گفتارم را (هنگام پاداش عمل) متذکر می‌شوید و من کار خود به خدا وامی‌گذارم، که او کاملا بر احوال بندگان بیناست.'),
(4178, 40, 45, '(قوم به جای آنکه پند مؤمن آل فرعون را بشنوند قصد آزارش کردند) پس خدا از شر و مکر فرعونیان او را محفوظ داشت و عذاب سخت آل فرعون را فرا گرفت (و همه به دریای هلاک غرق شدند).'),
(4179, 40, 46, '(اینک که در عالم برزخند) آنها صبح و شام بر آتش دوزخ عرضه می‌شوند، و چون روز قیامت بر پا شود (خطاب آید که) فرعونیان را به سخت‌ترین عذاب (جهنم) وارد کنید.'),
(4180, 40, 47, 'و (یاد آر) وقتی که در آتش دوزخ با هم به احتجاج و مجادله برآیند، ضعیفان ملّت به پیشوایان گردنکش خود می‌گویند: ما (در دنیا) پیروی شما کردیم (که گمراه شدیم) آیا می‌توانید امروز شما هم به عوض آن از آتش عذاب ما بکاهید؟'),
(4181, 40, 48, 'پیشوایان متکبر پاسخ دهند که ما و شما همه باید در آتش دوزخ (یکسان معذّب) باشیم، که خدا میان بندگان البته (به عدل) حکم فرموده است.'),
(4182, 40, 49, 'و اهل دوزخ به خازنان جهنم گویند: از خدای خود بخواهید که روزی عذاب ما را تخفیف دهد.'),
(4183, 40, 50, 'آنها پاسخ دهند که آیا پیغمبران شما با معجزات و ادلّه روشن برای (هدایت) شما نیامدند؟ دوزخیان گویند: آری آمدند (ما پیروی آنان نکردیم و بدین روز سیاه افتادیم). پس خازنان جهنّم گویند: اینک هر چه می‌خواهید دعا (و ناله) کنید، که دعای کافران جز در ضلالت (و زیان) نخواهد بود.'),
(4184, 40, 51, 'ما (که خدای تواناییم) البته رسولان خود و اهل ایمان را هم در دنیا ظفر و نصرت می‌دهیم و هم روز قیامت که گواهان (اعمال نیک و بد) به شهادت برخیزند (مؤمنان را به مقامات بهشتی پاداش می‌دهیم).'),
(4185, 40, 52, 'در آن روز ستمکاران را (پشیمانی و) عذرخواهی سود ندهد و بر آنها خشم و لعن و منزلگاه بد (جهنّم) مهیّاست.'),
(4186, 40, 53, 'و همانا ما به موسی مقام هدایت عطا کردیم و بنی اسرائیل (قوم او) را وارث کتاب (بزرگ تورات) گردانیدیم.'),
(4187, 40, 54, 'تا آن قوم هدایت یابند و خردمندان پند گیرند.'),
(4188, 40, 55, 'پس (ای رسول ما، بر آزار امت) صبر کن که البته وعده (ثواب و عقاب) خدا حق است و بر گناه خود از خدا آمرزش طلب کن (یعنی بر گناه امت نادان از درگاه خدا آمرزش خواه) و صبح و شام به تسبیح و ذکر و ستایش پروردگار خود پرداز.'),
(4189, 40, 56, 'آنان که در آیات خدا بی‌هیچ حجت و برهان که آنان را آمده باشد راه انکار و جدل پیمایند جز تکبر و نخوت (و قصد ریاست) چیزی در دل ندارند که به آرزوی دل هم آخر نخواهند رسید، پس تو (از شر و فتنه آنها) پناه به درگاه خدا بر، که خدا شنوا و بیناست.'),
(4190, 40, 57, 'البته خلقت آسمانها و زمین بسیار بزرگتر و مهمتر از خلقت بشر است و لیکن اکثر مردم این معنا را درک نمی‌کنند.'),
(4191, 40, 58, 'و هرگز (جاهل) نابینا و شخص (عالم) بینا یکسان نیستند و هم آنان که ایمان آورده و نیکوکار شدند (نزد خدا) با (کافران) بدکردار مساوی (در درجات آخرت) نیستند، لیکن بسیار کم این حقیقت را متذکر می‌شوید.'),
(4192, 40, 59, 'البته ساعت قیامت بی‌هیچ شک می‌آید و لیکن بیشتر مردم (تا ساعت مرگ به آن) ایمان نمی‌آورند.'),
(4193, 40, 60, 'و خدای شما فرمود که مرا با (خلوص دل) بخوانید تا دعای شما مستجاب کنم. آنان که از (دعا و) عبادت من اعراض و سرکشی کنند زود با ذلت و خواری در دوزخ شوند.'),
(4194, 40, 61, 'خداست آن که شب تار را برای آسایش و استراحت شما قرار داده و روز را (برای کسب و کار شما به چراغ خورشید) روشن گردانید، که خدا را در حق مردمان فضل و احسان است و لیکن اکثر مردم شکر (نعمتش) به جا نمی‌آرند.'),
(4195, 40, 62, 'همان خدا پروردگار شما آفریننده همه موجودات عالم است، جز او هیچ خدایی نیست، پس (ای بندگان) از درگاه یگانه معبود حق به کجا بازگردانیده (و فریفته معبودان باطل) می‌شوید؟'),
(4196, 40, 63, 'آنان که آیات (و رسل) الهی را انکار کردند هم این گونه (از بندگی حق) باز گردانیده (و مغرور معبودان باطل) می‌شوند.'),
(4197, 40, 64, 'خداست آن که زمین را آرامگاه شما قرار داد و آسمان را (کاخی رفیع) برافراشت و شما را به نیکوترین صورتها بیافرید و از غذاهای لذیذ خوش به شما روزی داد، این خدا پروردگار شماست، زهی برتر و بزرگوار است خدای یکتا پروردگار عالمیان.'),
(4198, 40, 65, 'او خدای زنده ابد است، جز او هیچ خدایی نیست، پس تنها او را بخوانید و به اخلاص در دین بنده او باشید، که ستایش و سپاس مخصوص خدای یکتا آفریدگار عالمیان است.'),
(4199, 40, 66, '(ای رسول ما، بت پرستان را) بگو که مرا از پرستش معبودان باطلی که شما به جای خدا می‌پرستید البته منع کرده‌اند چرا که بر من از خدای خود (به وحی) آیات و ادلّه روشنی آمده است و من مأمورم که تنها تسلیم امر خدای عالمیان باشم.'),
(4200, 40, 67, 'اوست خدایی که شما را از خاک (ناچیز) بیافرید و سپس از قطره آب نطفه و آن گاه از خون بسته علقه، پس شما را (از رحم مادر) طفلی بیرون آورد تا آنکه به سنّ رشد و کمال برسید و باز پیری سالخورده شوید و برخی از شما پیش از سنّ پیری وفات کنند و تا همه به اجلی معین برسید و (این چنین کردیم تا) مگر (قدرت خدا را) تعقّل کنید.'),
(4201, 40, 68, 'اوست خدایی که (خلائق را) زنده می‌کند و می‌میراند و چون به خلقت چیزی حکم نافذ و مشیّت کاملش تعلّق گیرد به محض اینکه گوید: موجود باش، بی‌درنگ موجود می‌شود.'),
(4202, 40, 69, 'آیا کسانی را که در آیات ما راه جدال و انکار پیمودند ندیدی که چگونه (از حق) بازگردانیده شوند؟'),
(4203, 40, 70, 'آنان که کتاب ما را و احکامی که رسولان خود را بر ابلاغ آن فرستادیم تکذیب کردند به زودی (کیفر کردارشان را) خواهند دانست.'),
(4204, 40, 71, 'آن گاه که گردنهاشان با غل و زنجیرها (ی آتشین) بسته شود و کشیده شوند.'),
(4205, 40, 72, 'در حمیم دوزخ (و آب گرم و عفن جهنم)، سپس در آتش سوخته و افروخته شوند.'),
(4206, 40, 73, 'آن گاه به آنها گویند: کجا رفتند معبودان باطلی که شریک خدا می‌شمردید،'),
(4207, 40, 74, 'و از خدا روی می‌گردانیدید؟ آن مشرکان جواب گویند: آن بتها همه از نظر ما محو و نابود شدند، بلکه ما از این پیش چیزی را به خدایی نمی‌خواندیم. خدا این گونه مردم کافر (مشرک) را گمراه می‌گرداند (یعنی از رحمت خود محروم می‌کند).'),
(4208, 40, 75, 'این قهر و عذاب شما کافران بدین سبب است که در دنیا از پی تفریح و هوسرانی باطل بودید و دایم به نشاط و شهوت پرستی سرگرم شدید.'),
(4209, 40, 76, 'اینک (به کیفر کفرتان) به درهای دوزخ درآیید که آنجا جاودان معذّب خواهید بود، که متکبران را (که سر از فرمان حق کشیدند در آخرت) بسیار منزلگاه بدی است.'),
(4210, 40, 77, 'پس (ای رسول، چند روزی بر آزار کافران امت) صبر کن که البته وعده (ثواب و عقاب) خدا حق است، که اگر آنها را در حیات تو و مقابل چشم تو به بعضی از وعده‌های خود که به آنها می‌دادیم (به شمشیر تو) عقوبت کنیم یا چنانچه تو را قبض روح کنیم باز رجوع آنها به سوی ماست (و در قیامت به کیفر اعمالشان می‌رسانیم).'),
(4211, 40, 78, 'و همانا ما رسولان بسیاری پیش از تو فرستادیم که احوال بعضی را بر تو حکایت کردیم و برخی را نکردیم و هیچ رسولی جز به امر خدا نشاید معجز و آیتی (برای امت) بیاورد، و چون فرمان خدا (بر غلبه حقّ و محو باطل) فرا رسد آن روز (بر همه) به حق حکم کنند و آنجا کافران مبطل زیانکار شوند.'),
(4212, 40, 79, 'خداست آن که برای شما آدمیان چهار پایان را آفرید تا بر بعضی سوار شوید و از برخی تغذیه کنید.'),
(4213, 40, 80, 'و نیز نفع بسیار دیگر از آنها ببرید و (از سواری و باربری و شیار زمین) حوائج و اغراضی که در دل دارید با آنها (در سفر و حضر) انجام دهید و بر آنها (در خاک) و بر کشتی‌ها (در آب) سوار می‌شوید.'),
(4214, 40, 81, 'و خدا آیات (قدرت و رحمت) خود را به شما ارائه دهد، پس کدام یک از آیات الهی را انکار توانید کرد؟'),
(4215, 40, 82, 'آیا این مردم در زمین به سیر و سفر نرفتند تا عاقبت حال پیشینیانشان را که از اینها بسیار بیشتر و قوی‌تر و مؤثرتر در زمین بودند مشاهده کنند؟ (که چگونه همه هلاک شدند) و آنچه اندوختند آنها را (از مرگ و هلاکت) حفظ و حمایت نکرد.'),
(4216, 40, 83, 'پس آن گاه که رسولانشان با معجزات و ادلّه روشن به سوی آنها آمدند آن مردم (نادان) به دانش و عقاید باطل خود شاد و مغرور شدند و وعده عذابی که مسخره می‌کردند همه را فرا گرفت.'),
(4217, 40, 84, 'و چون شدت قهر و عذاب ما را به چشم دیدند در آن حال (از کفر و شرک پشیمان شده و) گفتند: ما به خدای یکتا ایمان آوردیم و به همه بتهایی که شریک خدا می‌گرفتیم کافر شدیم.'),
(4218, 40, 85, 'اما ایمانشان پس از دیدن مرگ و مشاهده عذاب ما بر آنها هیچ سودی نبخشید. سنّت خدا (و حکمت الهی از ازل) چنین در میان بندگان حکمفرما بوده (که رسول و کتاب به امر و نهی و وعده ثواب و عقاب و نجات مؤمنان و هلاک کافران بفرستد تا خلق به اطاعت و عصیان امتحان شوند) و آنجا کافران زیانکار شدند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حم (رمز رسالت است یا قسم به خدای حمید مجید)'),
(4220, 41, 2, '(که این قرآن) تنزیلی از جانب خدای بخشنده مهربان است.'),
(4221, 41, 3, 'کتابی است که آیات جامعش به صورت قرآنی به زبان فصیح عربی برای دانشمندان روشن گردیده است.'),
(4222, 41, 4, 'قرآنی است که (نیکان را به وعده رحمت حق) بشارت می‌دهد و (بدان را از عذاب قهر) می‌ترساند اما اکثر مردم اعراض کرده و (اندرز و نصایحش) نمی‌شنوند.'),
(4223, 41, 5, 'و مشرکان گفتند: دلهای ما از قبول دعوتت سخت محجوب و گوش ما از شنیدن سخت سنگین و میان ما و تو حجاب (ضخیم و فاصله بسیار) است، تو به کار (دین) خود پرداز ما هم البته (به کیش خویش) عمل می‌کنیم.'),
(4224, 41, 6, 'بگو که من هم مانند شما بشری هستم جز آنکه مرا وحی می‌رسد که پروردگار شما خدای یکتای بی‌همتاست، پس همه مستقیم به راه دین او شتابید و از او آمرزش طلبید. و وای به حال مشرکان.'),
(4225, 41, 7, 'آنان که زکات (به فقیران بینوا) هرگز نمی‌دهند و به عالم آخرت به کلی کافرند.'),
(4226, 41, 8, 'آنان که ایمان آورده و نیکوکار شدند البته پاداش بی‌پایان خواهند یافت.'),
(4227, 41, 9, 'بگو که آیا شما به خدا که زمین (جهان) را در دو روز بیافرید کافر می‌شوید و بر او مثل و مانند قرار می‌دهید؟! او خدای جهانیان است (نه بتها و معبودان شما. در این آیه شریفه شاید یک معنا عالم اجسام باشد و دو روز، دو نوع بسیط و مرکب یا جواهر و اعراض اجسام مقصود باشد).'),
(4228, 41, 10, 'و او روی زمین کوهها برافراشت و انواع برکات (و منابع از معادن و چشمه‌ها و درختان) بسیار در آن قرار داد و قوت و ارزاق اهل زمین را در چهار روز (برای هر شهر و دیاری) مقدّر و معیّن فرمود و روزی طلبان را یکسان در کسب روزی خود گردانید (تا همه روزی خورند).'),
(4229, 41, 11, 'و آن گاه به خلقت آسمانها توجه کامل فرمود که آسمانها دودی بود، پس (به امر نافذ تکوینی) به آسمان و زمین فرمود که همه (به سوی خدا و اطاعت فرمان حق) به شوق و رغبت یا به جبر و کراهت بشتابید. آنها عرضه داشتند: ما با کمال شوق و میل می‌شتابیم.'),
(4230, 41, 12, 'آن گاه نظم هفت آسمان را در دو روز استوار فرمود و در هر آسمانی (نظم) امرش را وحی فرمود، و آسمان (محسوس) دنیا را به چراغهای رخشنده (مهر و ماه و انجم) زیب و زیور دادیم و آن را (از ورود شیاطین) حفظ کردیم. این (نظام آسمان و زمین) تقدیر خدای مقتدر داناست.'),
(4231, 41, 13, 'پس اگر کافران (پس از این ادلّه و آیات الهی از خدا) اعراض کردند به آنها بگو: من شما را از صاعقه‌ای مانند صاعقه هلاک عاد و ثمود ترسانیدم.'),
(4232, 41, 14, 'که بر آنها از هر جانب رسولان حق آمدند (و گفتند) که جز خدای یکتا را نپرستید. کافران باز گفتند: اگر خدا می‌خواست (که ما ایمان آوریم) فرشتگان را به رسالت می‌فرستاد، پس ما به کتب و احکام شما (رسولان بشری) کافریم.'),
(4233, 41, 15, 'اما قوم عاد در زمین به ناحق تکبر و سرکشی کردند و گفتند که از ما نیرومندتر (در جهان) کیست؟ آیا آنها ندیده و ندانستند که خدایی که آنها را خلق فرموده بسیار از آنان تواناتر است؟ و آنها آیات (قدرت) ما را (با وجود این برهان) انکار می‌کردند.'),
(4234, 41, 16, 'ما هم بر هلاک آن قوم عاد باد تندی در ایّام نحس شوم فرستادیم تا به آنها عذاب ذلّت و خذلان را در دنیا بچشانیم در صورتی که خواری عذاب آخرت بیش از دنیاست و آنجا هیچ کس یاری آنها نخواهد کرد.'),
(4235, 41, 17, 'اما قوم ثمود را نیز (رسول فرستادیم و) هدایت کردیم لیکن آنها خود کوری (جهل و ضلالت) را بر هدایت بگزیدند، پس آنها را هم صاعقه عذاب خواری و هلاکت به کیفر کردارشان فرا گرفت.'),
(4236, 41, 18, 'و آنان که ایمان آوردند و خدا ترس و پرهیزکار بودند همه را نجات دادیم.'),
(4237, 41, 19, 'و روزی که همه دشمنان خدا را گرد آورده و به سوی آتش دوزخ کشانند و آنجا برای جمع آوری بازشان دارند.'),
(4238, 41, 20, 'تا چون همه بر در دوزخ رسند آن هنگام گوش و چشمها و پوست بدنهاشان بر جرم و گناه آنها گواهی دهند.'),
(4239, 41, 21, 'و آنها به اعضاء بدن گویند: چرا بر اعمال ما شهادت دادید؟ آن اعضاء جواب گویند: خدایی که همه موجودات را به نطق آورد ما را نیز گویا گردانید و او شما را نخستین بار بیافرید و باز به سوی او بازگردانیده می‌شوید.'),
(4240, 41, 22, 'و شما که (اعمال زشت خود را) پنهان می‌داشتید برای این نبود که گوش و چشمهای شما و پوست بدنهایتان امروز شهادت ندهند (زیرا تصور نمی‌کردید که اعضای شما هم شاهد اعمال شما هستند) و لیکن (گناه را پنهان می‌کردید) به گمانتان که اکثر اعمال زشتی که (از خلق) پنهان می‌کنید از خدا هم پنهان است و بر آن آگاه نیست.'),
(4241, 41, 23, 'و همین گمان باطل شما در باره خدا بود که موجب (معصیت و) هلاکت شما گردید و امروز همه از زیانکاران شدید.'),
(4242, 41, 24, 'اینک اگر صبر و تحمل کنند (چگونه بتوانند که) جای در آتش دارند و اگر فریاد و بیتابی کنند فریادرس و دادخواهی ندارند.'),
(4243, 41, 25, 'و ما رفیقان و یارانی (از شیاطین) بر آنها گماشتیم تا آنچه پیش روی آنهاست (از نعمت و لذتهای فانی دنیا) در نظرشان جلوه دادند و آنچه از عقب دارند (از نعمت و لذات ابدی آخرت فراموش کردند) و وعده عذاب الهی بر آنها حتم و لازم گردید و چون امّتانی از جنّ و انس که پیش از آنها (غرق دنیا بودند و با کفر و عصیان) درگذشتند، سخت زبون و زیانکار شدند.'),
(4244, 41, 26, 'و کافران (به مردم) گفتند: به این قرآن گوش فرا ندهید و سخنان لغو و باطل در آن القاء کنید تا مگر بر او غالب شوید.'),
(4245, 41, 27, 'ما هم کافران را البته عذابی سخت بچشانیم و بدتر از آنچه می‌کردند کیفر کنیم.'),
(4246, 41, 28, 'این است جزای دشمنان خدا که همان آتش دوزخ است که منزل ابدی آنهاست به کیفر آنکه آیات (و رسل) ما را انکار می‌کردند.'),
(4247, 41, 29, 'و کافران (چون مشاهده عذاب کنند با حسرت و ندامت) گویند: پروردگارا، آن دو گروه از جنّ و انس را که ما را گمراه کردند به ما نشان ده تا آنها را زیر پا بیفکنیم تا پست و ذلیل‌ترین مردم شوند.'),
(4248, 41, 30, 'آنان که گفتند: محققا پروردگار ما خدای یکتاست و بر این ایمان پایدار ماندند فرشتگان رحمت بر آنها نازل شوند (و مژده دهند) که دیگر هیچ ترسی (از وقایع آینده) و حزن و اندوهی (از گذشته خود) نداشته باشید و شما را به همان بهشتی که (انبیا) وعده دادند بشارت باد.'),
(4249, 41, 31, 'ما در دنیا و آخرت یاران و دوستداران شماییم و برای شما در بهشت ابد هر چه مایل باشید یا آرزو و تقاضا کنید همه مهیّاست.'),
(4250, 41, 32, 'این سفره احسان را خدای غفور مهربان (به پاداش ثبات ایمان) برای شما گسترده است.'),
(4251, 41, 33, 'در جهان از آن کس که (چون پیغمبران) خلق را به سوی خدا خواند و نیکوکار گردید و گفت که من از تسلیم شوندگان خدایم کدام کس بهتر و نیکو گفتارتر است؟'),
(4252, 41, 34, 'و هرگز نیکی و بدی در جهان یکسان نیست، همیشه بدی (خلق) را به بهترین شیوه (که خیر و نیکی است پاداش ده و) دور کن تا همان کس که گویی با تو بر سر دشمنی است دوست و خویش تو گردد.'),
(4253, 41, 35, 'و لیکن به این مقام بلند (یعنی در پاداش بدی نیکی کردن) کسی نمی‌رسد جز آنان که (در راه دینداری) دارای مقام صبر و ثبات و (در معرفت الهی) صاحب حظّ بزرگند.'),
(4254, 41, 36, 'و هر گاه از شیطان تو را وسوسه و تحریکی رسد به خدا پناه بر که او شنوا و داناست.'),
(4255, 41, 37, 'و از جمله آیات قدرت الهی خلقت شب و روز و خورشید و ماه است، نباید هرگز پیش خورشید و ماه سجده برید، بلکه اگر به حقیقت خدا پرستید خدایی را که خورشید و ماه را آفریده است سجده و پرستش کنید.'),
(4256, 41, 38, 'پس اگر کافران (از پرستش خدا) تکبر و بزرگ منشی ورزند فرشتگان (و قوای بی‌نهایت عالم بالا) که نزد خدایند شب و روز بی‌هیچ خستگی و ملال به تسبیح و طاعت حضرت حق مشغولند.'),
(4257, 41, 39, 'و از جمله آیات قدرت الهی آنکه زمین را بنگری خشک و پژمرده که چون ما بر آن آب باران فرود آریم بردمد و گیاه برآورد و اهتزاز و نشاط و خرّمی یابد. باری، آن کس که زمین را زنده کند مردگان را هم زنده گرداند که او بر هر چیز قادر است.'),
(4258, 41, 40, 'آنان که در آیات ما سخت راه کفر و عناد پیمایند هرگز از نظر ما پنهان نیستند. آیا کسی که روز قیامت به آتش دوزخ درافتد بهتر است یا آن کس که ایمن (از عذاب) به محشر وارد شود؟ باری، امروز به اختیار خود هر چه می‌خواهید بکنید که خدا به تمام اعمال شما بیناست.'),
(4259, 41, 41, 'همانا آنان که به این قرآن که برای هدایت و یادآوری آنها آمد کافر شدند (چقدر نادانند) در صورتی که این کتاب به حقیقت صاحب عزت (و معجز بزرگ) است.'),
(4260, 41, 42, 'که هرگز از پیش و پس (گذشته و آینده حوادث عالم) باطل بدان راه نیابد (و تا قیامت حکومت و حکمتش باقی است) زیرا آن فرستاده خدای (مقتدر) حکیم ستوده صفات است.'),
(4261, 41, 43, '(ای رسول ما) بر تو وحی نمی‌شود جز آنچه به رسولان پیشین گفته شد، که خدایت بسیار دارای آمرزش و بخشش و هم صاحب قهر و عقاب دردناک است.'),
(4262, 41, 44, 'و اگر ما این قرآن را به زبان عجم می‌فرستادیم کافران می‌گفتند: چرا آیات این کتاب مفصل و روشن (به زبان عرب) نیامد (تا ما قوم عرب ایمان آوریم)؟ ای عجب آیا کتاب عجمی بر رسول و امت عربی نازل می‌شود؟! (اکنون که بدون عذر ایمان نمی‌آرند) به آنها بگو: این قرآن برای اهل ایمان هدایت و شفاست و اما آنان که ایمان نمی‌آرند گوشهایشان (از شنیدن کلام حق) گران است و این قرآن بر آنها موجب کوری (جهل و ضلالت) است، آن مردم (نادان به این کتاب حق گوش فرا نمی‌دارند، گویی که) از مکانی بسیار دور (از سعادت و ایمان) به این کتاب حق دعوت می‌شوند.'),
(4263, 41, 45, 'و ما به موسی کتاب (تورات) را دادیم پس در آن راه مخالفت و اختلاف پیش گرفتند و اگر آن کلمه (رحمت) از (لطف) خدا سبقت نیافته بود (که تعجیل در عذاب نکند) همانا میان آن امت حکم عذاب می‌رسید، و هر چند که آنها سخت در نزول آن (عذاب) در شک و ریبند (که تو را در وعده عذاب قیامت هم تکذیب می‌کنند).'),
(4264, 41, 46, 'هر کس کار نیکی کند به نفع خود و هر که بد کند بر ضرر خویش کرده است و خدا هیچ بر بندگان ستم نخواهد کرد.'),
(4265, 41, 47, 'از ساعت قیامت تنها خدا آگاه است، و هیچ میوه‌ای از غنچه خود بیرون نیاید و هیچ آبستنی بار بر ندارد و نزاید مگر به علم ازلی او. و (یاد آر) روزی که خدا به مشرکان خطاب کند: آن معبودان باطلی که شریک من پنداشتید کجا رفتند؟ مشرکان گویند: بار الها، ما حضور تو عرضه داشتیم که از ما هیچ کس گواه نیست (که تو شریک داشته‌ای. یعنی از شرک بیزاری می‌جویند).'),
(4266, 41, 48, 'و معبودان باطلی که در دنیا عبادت می‌کردند همه از نظرشان محو و نابود شود و آن زمان بدانند که (از آتش قهر خدا) هیچ مفرّ و نجاتی بر آنها نیست.'),
(4267, 41, 49, 'آدمی (حریص، از خدا) به دعا دایم تمنّای خیر می‌کند و هرگز خستگی و سیری ندارد و لیکن اگر به وی شرّ و آسیبی رسد (از رحمت الهی) زود مأیوس و ناامید می‌گردد.'),
(4268, 41, 50, 'و اگر ما به انسان (مغرور کم ظرف) پس از رنج و ضرری که به او رسیده نعمت و رحمتی نصیب کنیم البته خواهد گفت که این نعمت برای من (از لیاقت من) است، و گمان نمی‌کنم که قیامتی بر پا شود و به فرض اینکه به سوی خدایم برگردم باز هم برای من نزد خدا بهترین نعمت خواهد بود. ما البته کافران را به (کیفر) اعمالشان آگاه می‌سازیم و عذابی بسیار سخت می‌چشانیم.'),
(4269, 41, 51, 'و ما هر گاه به انسان (بی‌حوصله کم ظرف) نعمتی عطا کنیم رو بگرداند و (از شکر خدا) دوری جوید، و هر گاه شرّ و بلایی به او روی آورد آن گاه دایم زبان به دعا گشاید (و اظهار عجز نماید).'),
(4270, 41, 52, '(کافران را) بگو: چه می‌پندارید اگر (قرآن) از جانب خدا باشد و شما به آن کافر شده باشید آیا گمراه‌تر از آن کس که (مانند شما) به کفر و شقاق دور (از راه سعادت) است در جهان کسی تواند بود؟'),
(4271, 41, 53, 'ما آیات (قدرت و حکمت) خود را در آفاق جهان و نفوس بندگان کاملا هویدا و روشن می‌گردانیم تا ظاهر و آشکار شود که خدا (و آیات حکمت و قیامت و رسالتش همه) بر حق است. آیا همین حقیقت که خدا بر همه موجودات عالم پیدا و گواه است کفایت (از برهان) نمی‌کند؟'),
(4272, 41, 54, 'الا (ای اهل ایمان) بدانید که کافران از لقای خدای خود در شک و انکارند و باز بدانید که خدا را بر همه موجودات عالم احاطه کامل است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حم‌'),
(4274, 42, 2, 'عسق (اسرار کتاب خداست)'),
(4275, 42, 3, 'این گونه به سوی تو و رسولان پیش از تو خدای مقتدر دانا وحی می‌کند.'),
(4276, 42, 4, 'آنچه در آسمانها و آنچه در زمین است همه ملک اوست و او خدای بلند مرتبه بزرگ است.'),
(4277, 42, 5, 'نزدیک است که آسمانها از فراز هم یکی پس از دیگری شکافته (و درهای آن به وحی رسولان باز) شود، و فرشتگان (رحمت) به ستایش خدای خود تسبیح گویند و برای اهل زمین از خدا مغفرت و آمرزش طلبند (و ندا کنند که) الا ای بندگان، بدانید که خداست آن ذاتی که بسیار آمرزنده و مهربان است.'),
(4278, 42, 6, 'و آنان که غیر خدا را معبود و محبوب خویش برگرفتند خدا رقیب و نگهبان بر (اعمال) آنهاست و تو وکیل (کار و مسئول کردار) آنان نخواهی بود.'),
(4279, 42, 7, 'و این چنین قرآن فصیح عربی را ما به تو وحی کردیم تا مردم امّ القری (شهر مکه) و هر که را که در اطراف آن است (از خدا) بترسانی و هم از (سختی) روز بزرگ قیامت که همه جمع آیند و هیچ شک در آن نیست و گروهی در بهشت جاودان و فرقه‌ای در آتش سوزان روند بیم دهی.'),
(4280, 42, 8, 'و اگر خدا می‌خواست (و در ازل صلاح نظام عالم می‌دانست) تمام خلایق را (به قهر) یک فرقه (مؤمن) قرار می‌داد و لیکن (برای امتحان چنین نخواسته تا) هر که را بخواهد به رحمت خود داخل کند و ستمکاران را هیچ یار و ناصری نباشد.'),
(4281, 42, 9, 'آری، مشرکان غیر خدا را یار و دوستدار خود برگرفتند و حال آنکه خدا منحصرا دوست و یاور بندگان است و اوست که مردگان را زنده می‌کند و اوست که بر هر چیز تواناست.'),
(4282, 42, 10, 'و آنچه در آن اختلاف و نزاع کردید حکم آن به خدا (و احکام خدا) راجع است، همان خدا (ی آفریننده عالم و آدم) پروردگار من است که بر او توکل کرده و به درگاه او به تضرّع باز می‌گردم.'),
(4283, 42, 11, 'خدا آفریننده آسمانها و زمین است، برای شما آدمیان از جنس خودتان زنان را هم جفت شما قرار داد و نیز چهار پایان را جفت (نر و ماده) آفرید، و به این تدبیر (ازدواج) شما را خلق بی‌شمار کند. آن خدای یکتا را هیچ مثل و مانندی نیست و او شنوا و بیناست.'),
(4284, 42, 12, 'کلید (گنج نعمتهای) آسمانها و زمین او راست، هر که را خواهد رزق وسیع دهد و هر که را خواهد تنگ روزی کند، که او به هر چیز آگاه است.'),
(4285, 42, 13, 'خدا شرع و آیینی که برای شما مسلمین قرار داد حقایق و احکامی است که نوح را هم به آن سفارش کرد و بر تو نیز همان را وحی کردیم و به ابراهیم و موسی و عیسی هم آن را سفارش نمودیم که دین خدا را بر پا دارید و هرگز تفرقه و اختلاف در دین مکنید. مشرکان را که به خدای یگانه و ترک بتان دعوت می‌کنی (قبولش) بسیار در نظرشان بزرگ می‌آید. (باری از انکار آنها میندیش که) خدا هر که را بخواهد به سوی خود (و مقام رسالت خویش) برمی‌گزیند و هر که را به درگاه خدا به تضرّع و دعا باز آید هدایت می‌فرماید.'),
(4286, 42, 14, 'و مردم (در دین) راه تفرقه و اختلاف نپیمودند مگر پس از آنکه علم و برهان (از جانب حق) بر آنها آمد و لیکن دانسته برای تعدی و ظلم به یکدیگر اختلاف کردند، و اگر آن کلمه (رحمت) از (لطف) خدا سبقت نگرفته بود (که) تا وقتی معین (تعجیل در عذاب نکند) البته میان مردم (ستمکار) حکم (به هلاک) می‌شد. و آنان که پس از گذشتگان وارث کتاب آسمانی شدند (مانند یهود و نصارا) در آن کتاب آسمانی سخت در شک و ریب بماندند.'),
(4287, 42, 15, 'بدین سبب تو همه را (به دین اسلام و کلمه توحید) دعوت کن و چنانکه مأموری پایداری کن و پیرو هوای نفس مردم مباش و بگو که من به کتابی که خدا فرستاد (قرآن) ایمان آورده‌ام و مأمورم که میان شما به عدالت حکم کنم، خدای یگانه پروردگار همه ما و شماست، (پاداش) عمل ما بر ما و عمل شما بر شماست (و پس از تبلیغ رسالت) دیگر هیچ حجت و گفت و گویی بین ما و شما باقی نیست. خدا (روز جزا برای حکم حق) میان ما جمع می‌کند و بازگشت همه به سوی اوست.'),
(4288, 42, 16, 'و آنان که در دین خدا (از حسد و عناد با رسولش) جدل و احتجاج برانگیزند پس از آنکه خلق دعوت او را پذیرفتند حجت آنها نزد خدا لغو و باطل است و بر آنها قهر و غضب و عذاب سخت خواهد بود.'),
(4289, 42, 17, 'خداست آن که کتاب (آسمانی) را به حق و نیز ترازوی عدالت را فرستاد، و تو چه دانی؟ ممکن است ساعت قیامت بسیار نزدیک باشد.'),
(4290, 42, 18, 'آنان که به ساعت قیامت ایمان نمی‌آورند (به تمسخر) تقاضای تعجیل در ظهور قیامت می‌کنند، امّا اهل ایمان از آن روز سخت ترسانند و می‌دانند که آن روز (و همه وعده‌های آخرت) بر حق است. الا (ای مردم) بدانید آنان که در قیامت جدل و انکار می‌کنند سخت در گمراهی دور (از سعادت) اند.'),
(4291, 42, 19, 'خدا را به بندگان لطف و محبت بسیار است، هر که را بخواهد روزی می‌دهد و او توانای مطلق و مقتدر و غالب است.'),
(4292, 42, 20, 'هر کس حاصل کشت آخرت را بخواهد ما بر تخمی که کاشته می‌افزاییم و هر که تنها حاصل کشت دنیا را بخواهد او را هم از آن نصیب می‌کنیم ولی در آخرت (از نعمت ابدی آن چون نخواسته) نصیبی نخواهد یافت.'),
(4293, 42, 21, 'آیا خدایان باطل مشرکان بر آنها شرع و احکامی که خدا اجازه نفرموده جعل کرده‌اند؟ و اگر کلمه فصل (یعنی حکم تأخیر عذاب) نبود میان آنها (به هلاکت) حکم می‌شد، و ستمکاران را البته (روزی) عذاب دردناک خواهد بود.'),
(4294, 42, 22, '(در آن روز) ظالمان را بینی که از (کیفر) کردار خود سخت ترسان و هراسانند و البته به کیفر خواهند رسید، و آنان که به خدا ایمان آوردند و نیکوکار شدند در باغهای بهشت منزل یافته و نزد خدای خود هر چه خواهند بر آنان مهیّاست. این همان فضل و رحمت نامنتهای خداست (که نصیب اهل ایمان است).'),
(4295, 42, 23, 'این (بهشت ابد) همان است که خدا به بندگانی که ایمان آورده و نیکوکار شدند بشارت آن را داده است. بگو: من از شما اجر رسالت جز این نخواهم که مودّت و محبّت مرا در حقّ خویشاوندان منظور دارید (و دوستدار آل محمّد باشید، که این اجر هم به نفع امت و برای هدایت یافتن آنهاست)، و هر که کاری نیکو انجام دهد ما نیز در آن مورد بر نیکوییش بیفزاییم که خدا بسیار آمرزنده گناهان و پذیرنده شکر بندگان است.'),
(4296, 42, 24, 'بلکه (مردم نادان) خواهند گفت: او (محمّد صلّی اللّه علیه و آله و سلّم) بر خدا دروغ بسته (که محبّت اهل بیت را بر امت واجب کرده. چنین نیست و هرگز رسولی بر خدا دروغ نتواند بست که) اگر خدا بخواهد بر قلب تو مهر می‌نهد و به کلمات (وحی) خود سخن باطل را محو و نابود و حق را ثابت و برقرار می‌گرداند، که خدا به اسرار دلهای خلق کاملا آگاه است.'),
(4297, 42, 25, 'و اوست خدایی که توبه بندگانش را می‌پذیرد و گناهان را می‌بخشد و هر چه کنید می‌داند.'),
(4298, 42, 26, 'و دعای آنان را که ایمان آورده و نیکوکار شوند مستجاب می‌گرداند و از فضل و کرم خود بر ثواب آنها می‌افزاید، و برای کافران عذابی سخت خواهد بود.'),
(4299, 42, 27, 'و اگر خدا روزی بندگان را وسیع و فراوان کند در روی زمین ظلم و طغیان بسیار کنند لیکن به اندازه‌ای که بخواهد (و صلاح داند) نازل می‌گرداند، که خدا به احوال بندگانش آگاه و بیناست.'),
(4300, 42, 28, 'و اوست خدایی که باران را پس از نومیدی خلق می‌فرستد و رحمت (و نعمت) خود را فراوان می‌گرداند و اوست خداوندگار محبوب الذّات ستوده صفات.'),
(4301, 42, 29, 'و از جمله آیات (قدرت) او خلقت آسمانها و زمین است و هم آنچه در آنها از انواع جنبندگان پراکنده است، و او بر جمع آوری موجوداتی که (در آسمانها و کرات بی‌شمار عالم) پراکنده است هر وقت بخواهد قادر است.'),
(4302, 42, 30, 'و آنچه از رنج و مصائب به شما می‌رسد همه از دست (اعمال زشت) خود شماست در صورتی که خدا بسیاری از اعمال بد را عفو می‌کند.'),
(4303, 42, 31, 'و شما در زمین هیچ قدرتی ندارید و از کوچکترین قوای جهان زبون و عاجزید و غیر خدا در عالم هیچ یار و یاوری ندارید.'),
(4304, 42, 32, 'و یکی از آیات (قدرت) الهی سیر و گردش کشتیهاست که در آب دریا مانند کوههای بلند در حرکت است.'),
(4305, 42, 33, 'اگر خدا بخواهد باد را سکون و آرامش دهد تا کشتیها بر پشت آب از جنبش بایستد. در این کار برای مردم با صبر شکر گزار ادلّه‌ای (از قدرت خدا) نمودار است.'),
(4306, 42, 34, 'یا اگر بخواهد خدا کشتیهایشان را به جرم بدکاری به دریا غرق می‌کند و (اگر بخواهد) بسیاری از جرم شما را می‌بخشد.'),
(4307, 42, 35, 'و تا آنان که در آیات ما راه جدال و انکار می‌پیمایند بدانند که بر آنها (از قهر و عذاب ما) هیچ مفرّ و نجاتی نیست.'),
(4308, 42, 36, 'پس چیزی که نصیب شما گردیده متاع (فانی) زندگی دنیاست و آنچه نزد خداست بسیار بهتر و باقی‌تر است اما آن مخصوص است به آنان که به خدا ایمان آورده‌اند و بر پروردگار خود توکل می‌کنند.'),
(4309, 42, 37, 'و آنان که از گناهان بزرگ و زشتکاری می‌پرهیزند و چون (بر کسی) خشم و غضب کنند (بر او) می‌بخشند.'),
(4310, 42, 38, 'و آنان که امر خدایشان را اجابت کردند و نماز به پا داشتند و کارشان را به مشورت یکدیگر انجام می‌دهند و از آنچه روزی آنها کردیم (به فقیران) انفاق می‌کنند.'),
(4311, 42, 39, 'و آنان که چون ظلمی بر آنها هجوم کند (از مؤمنان یاری طلبیده) انتقام می‌جویند.'),
(4312, 42, 40, 'و انتقام بدی (مردم) به مانند آن بد رواست (نه بیشتر) و باز اگر کسی عفو کرده و (بین خود و خصم خود را به عفو) اصلاح نمود اجر او بر خداست، که خدا ستمکاران را هیچ دوست نمی‌دارد.'),
(4313, 42, 41, 'و هر کس پس از ظلمی که بر او رفته انتقام طلبد، بر اینان هیچ گناه و مؤاخذه نیست.'),
(4314, 42, 42, 'تنها راه مؤاخذه بر آنهایی است که به مردم ظلم کنند و در زمین به ناحق شرارت انگیزند، بر آنها (در دنیا انتقام و در آخرت) عذاب دردناک است.'),
(4315, 42, 43, 'و هر که (بر ظلم کسی) صبر کند (و با قدرت انتقام ببخشد) این مقام (حلم و بردباری است که) از عزم در امور (الهی و تسلط بر نفس و قوت عقل و اراده) است.'),
(4316, 42, 44, 'و هر که را خدا به گمراهی خود واگذارد دیگر هیچ کس پس از خدا یار و یاور او نیست. و ستمکاران را بنگری که چون عذاب (قیامت) را به چشم ببینند در آن حال گویند: آیا راهی به بازگشت (به دنیا) برای ما هست؟'),
(4317, 42, 45, 'و آن ظالمان را بنگری که به دوزخشان متوجه گردانند و آنها با ترس و ذلّت از گوشه چشم (بر آتش دوزخ) می‌نگرند و در آن حال مؤمنان (ایمن و شادان) گویند: آری، زیانکاران آنان هستند که نفوس خود و اهل بیت خود را در امروز قیامت به زیان عذاب انداخته‌اند. الا (ای مردم) بدانید که ستمکاران عالم به عذاب ابدی گرفتارند.'),
(4318, 42, 46, 'و آنها را غیر خدا هیچ یار و یاوری نباشد که یاریشان دهند. و هر که را خدا به گمراهی واگذارد دیگر هیچ راه نجاتی بر او نخواهد بود.'),
(4319, 42, 47, '(بندگان غافل) دعوت خدای خود را اجابت کنید پیش از آنکه بیاید روزی که نه از (قهر) خدا راه نجاتی یابید و نه ملجأ و پناهی دارید و نه کسی از شما دفاع و انکاری تواند کرد.'),
(4320, 42, 48, '(ای رسول، تو این آیات قیامت را بر خلق برسان) پس اگر باز اعراض کردند دیگر تو را نگهبان آنها نفرستادیم، بر تو جز ابلاغ رسالت تکلیفی نیست، و ما چون به انسان (بی‌صبر کم ظرف) از لطف و رحمت خود بهره‌ای بخشیم بدان شاد شود و اگر به کیفر کردار خود رنج و عذابی به آنان رسد آدمی سخت راه کفران پوید.'),
(4321, 42, 49, 'تنها خدا راست ملک آسمانها و زمین، هر چه بخواهد می‌آفریند، به هر که خواهد فرزندان اناث (دختر) و به هر که خواهد فرزندان ذکور (پسر) عطا می‌کند.'),
(4322, 42, 50, 'یا در یک رحم فرزندان پسر و دختر قرار می‌دهد، و هر که را خواهد عقیم (نازا) می‌گرداند، که او (به صلاح خلق) دانا و (به هر چه خواهد) تواناست.'),
(4323, 42, 51, 'و هیچ بشری را یارای آن نباشد که خدا با او سخن گوید مگر به وحی (و الهام خدا) یا از پس پرده غیب عالم (و حجاب ملکوت جهان) یا رسولی (از فرشتگان عالم بالا) فرستد تا به امر خدا هر چه او خواهد وحی کند، که او خدای بلند مرتبه حکیم است.'),
(4324, 42, 52, 'و همین گونه ما روح (و فرشته بزرگ) خود را به فرمان خویش (برای وحی) به تو فرستادیم، تو از آن پیش که وحی بر تو رسد نه دانستی کتاب (خدا) چیست و نه فهم کردی که راه ایمان و شرع کدام است و لیکن ما آن (کتاب و شرع) را نور (وحی و معرفت) گردانیدیم که هر کس از بندگان خود را بخواهیم به آن نور هدایت می‌کنیم و اینک تو (که به نور وحی ما هدایت یافتی خلق را) به راه راست هدایت خواهی کرد.'),
(4325, 42, 53, 'یعنی به راه خدا همان خدایی که هر چه در آسمانها و زمین است همه ملک اوست، الا بدانید که رجوع تمام امور (عالم آفرینش) به سوی خداست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حم (رسول و جانشینانش بر اسرار این حرف آگاهند. یا اشاره به دو اسم حمید و مجید خداست)'),
(4327, 43, 2, 'قسم به این کتاب روشن بیان.'),
(4328, 43, 3, 'که ما آن را قرآنی به لسان فصیح عربی مقرر داشتیم تا مگر شما (بندگان در فهم آن) عقل و فکرت کار بندید.'),
(4329, 43, 4, 'و همانا این کتاب نزد ما در لوح محفوظ که اصل مخزن کتب آسمانی است بسی بلند پایه و محکم اساس است.'),
(4330, 43, 5, 'آیا ما از متذکر ساختن شما (به قرآن) چون (به انکار آن) بر خویش ستم می‌کنید صرف نظر کنیم؟'),
(4331, 43, 6, 'و چقدر پیمبرانی در اقوام پیشین (برای هدایت خلق) فرستادیم.'),
(4332, 43, 7, 'و بر مردم هیچ رسولی نمی‌آمد جز آنکه او را به مسخره (و انکار) می‌گرفتند.'),
(4333, 43, 8, 'ما هم قوی‌ترین سرکشانشان را هلاک کردیم و شرح حال پیشینیان (برای عبرت مردم) گذشت.'),
(4334, 43, 9, 'و اگر از این کافران بپرسی که آسمانها و زمین را که آفریده؟ البته جواب دهند خدای مقتدر دانا آفریده است.'),
(4335, 43, 10, 'همان خدایی که زمین را مهد آسایش شما بندگان قرار داد و در آن راهها بر (تحصیل امر معاش و معاد) شما پدید آورد تا مگر هدایت یابید.'),
(4336, 43, 11, 'و آن خدایی که از (باران) آسمان آبی به قدر و اندازه نازل کرد و به آن صحرا و دیار مرده خشک را زنده گردانیدیم. همین گونه شما هم (از قبرها) بیرون آورده می‌شوید.'),
(4337, 43, 12, 'و آن خدایی که همه موجودات عالم را جفت آفرید و برای سواری شما بندگان کشتیها و چهار پایان را (در آب و خاک) مقرر فرمود.'),
(4338, 43, 13, 'تا چون با کمال تسلط بر پشت آنها نشستید متذکر نعمت خدایتان شوید و گویید: پاک و منزّه خدایی که این (انواع کشتی و چهار پایان قوی) را مسخر ما گردانید و گرنه ما هرگز قادر بر آن نبودیم.'),
(4339, 43, 14, 'و بازگشت ما البته به سوی خدای ما خواهد بود.'),
(4340, 43, 15, 'و مشرکان برای خدا چیزی را (مانند فرشته) که بنده و مخلوق اوست جزء (وجود و فرزند واقعی او) قرار دادند (زهی جهل و کفر و سفاهت) که انسان بسیار ناسپاس و کفرش آشکار است.'),
(4341, 43, 16, '(ای مشرکان) آیا خدا از مخلوقات خود بر خویش دختران را برگزید و شما را به پسران امتیاز داد؟'),
(4342, 43, 17, 'و حال آنکه هر کدام از مشرکان را به دختری که به خدا نسبت دادند مژده دهند (ننگ دارد و) رویش (از غم) سیاه می‌شود در حالی که پر از خشم و اندوه است.'),
(4343, 43, 18, 'آیا کسی که به زیب و زیور پرورده می‌شود (یعنی دختران) و در خصومت (از حفظ حقوق خود) عاجز است (چنین کس لایق فرزندی خداست).'),
(4344, 43, 19, 'و فرشتگان را که مخلوق و بندگان خدا هستند دختر می‌خوانند. آیا در وقت خلقت آنها حاضر بودند؟ (و دیدند که آنها دخترند؟ اگر آنها بر این گواهی دهند) البته شهادت (کذب) شان (در نامه عملشان) نوشته شده و بر آن سخت مؤاخذه می‌شوند.'),
(4345, 43, 20, 'و مشرکان گویند: اگر خدا می‌خواست ما آنها (فرشتگان و بتان) را نمی‌پرستیدیم. گفتار آنها نه از روی علم و دانش است بلکه به وهم و پندار باطل خویش می‌گویند.'),
(4346, 43, 21, 'یا مگر بر آنها از این پیش کتابی فرستادیم که (در این سخن جبر و عقیده باطل) به آن کتاب استدلال می‌کنند؟'),
(4347, 43, 22, 'بلکه گفتند: ما پدران خود را به عقاید و آیینی یافتیم و البته ما هم که در پی آنها رویم بر هدایت هستیم.'),
(4348, 43, 23, 'و همچنین ما هیچ رسولی پیش از تو در هیچ شهر و دیاری نفرستادیم جز آنکه اهل ثروت و مال آن دیار به رسولان گفتند که ما پدران خود را بر آیین و عقایدی یافتیم و از آنها البته پیروی خواهیم کرد.'),
(4349, 43, 24, 'آن رسول ما به آنان گفت: اگر چه من به آیینی بهتر از دین (باطل) پدرانتان شما را هدایت کنم (باز هم پدران را تقلید می‌کنید)؟ آنها پاسخ دادند (به هر تقدیر) ما به آنچه شما را به رسالت آن فرستاده‌اند کافریم.'),
(4350, 43, 25, 'ما هم از آن مردم (پر غرور و عناد) انتقام کشیدیم، بنگر تا عاقبت حال کافران مکذّب به کجا کشید؟'),
(4351, 43, 26, '(یاد آر) وقتی که ابراهیم با پدر (یعنی عموی خود) و قومش گفت: من از معبودان شما سخت بیزارم.'),
(4352, 43, 27, 'و جز آن خدایی که مرا آفریده و البته مرا هدایت خواهد کرد نمی‌پرستم.'),
(4353, 43, 28, 'و خدا این خدا پرستی را در همه ذرّیّت او تا قیامت کلمه باقی گردانید، باشد که (همه فرزندانش به خدای یکتا) رجوع کنند.'),
(4354, 43, 29, '(و من تعجیل در عقوبت کافران نکردم) بلکه آنان و پدرانشان را (مهلت داده و از عمر) بهره‌مند کردم تا آنکه دین حق و رسول مبین بر آنها آمد.'),
(4355, 43, 30, 'و چون حق به سوی آنها آمد گفتند: این (قرآن) سحر است و ما به (وحی بودن) آن ایمان نداریم.'),
(4356, 43, 31, 'و باز گفتند: چرا این قرآن بر آن مرد بزرگ قریه مکّه و طایف (ولید و حبیب یا عروة بن مسعود) نازل نشد؟'),
(4357, 43, 32, 'آیا آنها باید فضل و رحمت خدای تو را تقسیم (و مقام نبوت را تعیین) کنند؟ در صورتی که ما خود معاش و روزی آنها را در حیات دنیا تقسیم کرده‌ایم و بعضی را بر بعضی به درجاتی (از مال و جاه دنیوی) برتری داده‌ایم تا بعضی از مردم (به ثروت) بعضی دیگر را مسخر خدمت کنند، و (بهشت، دار) رحمت خدا از آنچه (از مال دنیا) جمع می‌کنند بسی بهتر است.'),
(4358, 43, 33, 'و اگر نه این بود که مردم همه یک نوع و یک امّتند ما (از پستی و بی‌قدری دنیا) برای آنان که کافر به خدا می‌شوند سقف خانه‌هاشان را از نقره خام قرار می‌دادیم و (چندین طبقه که) نردبامهایی (نصب کرده) که بر سقف بالا روند.'),
(4359, 43, 34, 'و نیز بر منزلهاشان (از بزرگی و وسعت) درهای بسیار و تختهای زرنگار که بر آن تکیه زنند قرار می‌دادیم.'),
(4360, 43, 35, 'و عماراتشان به زر و زیور می‌آراستیم، و اینها همه متاع (پست فانی مردم) دنیاست و (نعمتهای ابدی) آخرت نزد خدای تو مخصوص اهل تقواست.'),
(4361, 43, 36, 'و هر که از یاد خدا (و حکم قرآن) رخ بتابد شیطانی را بر او برانگیزیم تا یار و همنشین دایم وی باشد.'),
(4362, 43, 37, 'و آن شیاطین همیشه آن مردم از خدا غافل را از راه خدا باز دارند (و به ضلالت در افکنند) و پندارند که هدایت یافته‌اند.'),
(4363, 43, 38, '(و چنین کس سرگرم دنیا شود) تا وقتی که به سوی ما باز آید آن گاه با نهایت حسرت گوید: ای کاش میان من و تو (ای شیطان) فاصله‌ای به دوری مشرق و مغرب بود که تو بسیار همنشین و یار بد اندیشی بر من بودی.'),
(4364, 43, 39, 'و (ای ستمکاران، پشیمانی) هرگز در آن روز به حال شما سودی ندارد، زیرا در دنیا ظلم کردید، که امروز با شیاطین البته در عذاب دوزخ شریک هستید.'),
(4365, 43, 40, 'آیا تو این کران را سخنی توانی آموخت یا این کوران (باطن) و آن را که دانسته به گمراهی می‌رود هدایت توانی کرد؟'),
(4366, 43, 41, 'پس اگر ما تو را به جوار خود بریم بعد از تو سخت از آنها انتقام می‌کشیم.'),
(4367, 43, 42, 'یا عذابی را که به آنها وعده دادیم به تو می‌نمایانیم (و به شمشیر تو از آنها انتقام می‌کشیم) که ما همه گونه بر (هلاک) آنها قادریم.'),
(4368, 43, 43, 'پس به قرآنی که تو را وحی می‌شود تمسّک کن که البته تو به راه راست (و طریق حق) هستی.'),
(4369, 43, 44, 'و قرآن برای تو و (مؤمنان) قومت شرف و نام بلندی است، و البته شما امت را باز می‌پرسند (که با قرآن چه کردید؟).'),
(4370, 43, 45, 'و از رسولانی که پیش از تو فرستادیم باز پرس (یعنی از سیرت آنها باز جو) که آیا ما جز خدای یکتای مهربان خدایان دیگری را معبود مردم قرار دادیم؟'),
(4371, 43, 46, 'و ما موسی را با آیات و معجزاتی که به او دادیم به سوی فرعون و اشراف قومش (به رسالت) فرستادیم، او به آنها گفت: من رسول ربّ العالمینم.'),
(4372, 43, 47, 'چون آیات ما را بر آنها آورد آن را مسخره کرده و بر او خندیدند.'),
(4373, 43, 48, 'و ما هیچ معجز و آیتی به آنان نمی‌نمودیم جز آنکه از آیت دیگر بزرگتر (و در دلالت بر نبوت موسی روشن‌تر) بود (اما همه را تکذیب کردند) و ما هم آنها را به عذاب و بلایا گرفتار کردیم تا مگر (به سوی خدا) باز آیند.'),
(4374, 43, 49, 'و گفتند: ای ساحر بزرگ، تو از خدای خود چون تعهدی با تو دارد (که دعایت مستجاب کند) بخواه (تا عذاب از ما بردارد) و ما بدین شرط البته هدایت می‌شویم.'),
(4375, 43, 50, 'پس آن گاه که (به دعای موسی) ما عذاب را از آنها برداشتیم باز آنها نقض عهد کردند.'),
(4376, 43, 51, 'و فرعون در میان قومش آوازه بلند کرد که ای مردم، آیا کشور با عظمت مصر از من نیست؟ و چنین نهرها از زیر قصر من جاری نیست؟ آیا (عزت و جلال مرا در عالم به چشم) مشاهده نمی‌کنید؟'),
(4377, 43, 52, 'بلکه من (به ریاست و سلطنت) بهترم تا چنین مرد فقیر خواری که هیچ منطق و بیان روشنی ندارد؟'),
(4378, 43, 53, '(و اگر موسی رسول خداست) چرا (او را دستگاهی نیست و) طوق زرین بر دست ندارد یا چرا فرشتگان آسمان همراه او نیامده‌اند.'),
(4379, 43, 54, 'و (به این تبلیغات دروغ و باطل) قومش را ذلیل و زبون داشت تا همه مطیع فرمان وی شدند که آنها مردمی فاسق و نابکار بودند.'),
(4380, 43, 55, 'پس آن گاه که فرعون و فرعونیان ما را به خشم آوردند ما هم از آنان انتقام کشیدیم و همه را غرق (دریای هلاک) نمودیم.'),
(4381, 43, 56, 'و هلاک آن قوم را مایه عبرت آیندگان قرار دادیم.'),
(4382, 43, 57, 'و چون به عیسی فرزند مریم مثلی زده شد (او را به آدم در نداشتن پدر مثل زد) قوم تو از آن به فریاد آمدند (و سخت بر آنان گران بود).'),
(4383, 43, 58, 'و مشرکان به اعتراض گفتند: آیا خدایان ما بهترند یا او (عیسی بن مریم؟ اگر به قول محمّد، غیر خدا همه معبودان در آتش بسوزند لازم آید عیسی هم بسوزد) و این سخن را با تو جز به جدل و انکار نگفتند، که آنها قومی حریف جدل و خصومتند.'),
(4384, 43, 59, 'عیسی نبود جز بنده خاصی که ما او را به نعمت (رسالت) برگزیدیم و بر بنی اسرائیل مثل (و حجت) قرار دادیم.'),
(4385, 43, 60, 'و اگر ما بخواهیم، به جای شما آدمیان فرشتگان را در زمین جانشین می‌گردانیم.'),
(4386, 43, 61, 'و (نزول) عیسی علم و نشانه ساعت قیامت است (و مبشر حضرت محمّد صلّی اللّه علیه و آله و سلّم خواهد بود)، زنهار در آن ساعت شک و ریب روا مدارید و (امر) مرا پیروی کنید که راه راست همین است.'),
(4387, 43, 62, 'و مبادا شیطان شما را (از راه حق) بازگرداند، که دشمنی او شما را آشکار است.'),
(4388, 43, 63, 'و چون عیسی با ادلّه و معجزات (برای هدایت خلق) آمد گفت: من آمده‌ام با حکمت و برهان (و کتاب انجیل آسمانی تا شما را هدایت کنم) و تا بعض احکامی که در آن اختلاف می‌کنید (از تورات) بیان سازم، پس خدا ترس و پرهیزکار شوید و مرا اطاعت کنید.'),
(4389, 43, 64, 'همانا خدای یکتا پروردگار من و شماست، تنها پرستش او کنید که این راه راست است.'),
(4390, 43, 65, 'باز فرق یهود و نصارا بین خود اختلاف انداختند، پس وای بر ستمکاران عالم از عذاب دردناک روز قیامت.'),
(4391, 43, 66, 'آیا به جز ساعت قیامت را انتظار می‌کشند که ناگاه بر آنها فرا می‌رسد و آنها غافل (از خدا) و بی‌خبر (از روز قیامت) اند؟'),
(4392, 43, 67, 'در آن روز دوستان همه با یکدیگر دشمنند به جز متقیان.'),
(4393, 43, 68, '(آن روز خطاب شود) الا ای بندگان (صالح) من، امروز شما را هیچ ترس و حزنی نخواهد بود.'),
(4394, 43, 69, 'آنان که به آیات ما ایمان آوردند و تسلیم امر ما شدند.'),
(4395, 43, 70, '(به همه خطاب رسد که) شما با همسرانتان مسرور و شادمان در بهشت جاوید وارد شوید.'),
(4396, 43, 71, 'و بر آن مؤمنان کاسه‌های زرین و کوزه‌های بلورین (مملوّ از انواع طعام لذیذ و شراب طهور) دور زنند و در آنجا هر چه نفوس را بر آن میل و اشتهاست و چشمها را شوق و لذت، مهیّا باشد و شما مؤمنان در آن بهشت جاویدان متنعّم خواهید بود.'),
(4397, 43, 72, 'این همان بهشتی است که از اعمال (صالح) خود به ارث یافتید.'),
(4398, 43, 73, 'برای شما در آنجا انواع میوه فراوان است که از آن (هر چه خواهید) تناول کنید.'),
(4399, 43, 74, 'بد کاران عالم هم آنجا سخت در عذاب آتش جهنم مخلّدند.'),
(4400, 43, 75, 'و هیچ از عذابشان کاسته نشود و امید نجات و خلاصی ندارند.'),
(4401, 43, 76, 'و ما به آنها ظلم و ستمی نکردیم لیکن آنان خود مردمی ستمکار و ظالم بودند.'),
(4402, 43, 77, 'و (آن دوزخیان، مالک دوزخ را) ندا کنند که ای مالک، از خدای خود بخواه که ما را بمیراند (تا از عذاب برهیم). جواب دهد: شما (در این عذاب) همیشه خواهید بود.'),
(4403, 43, 78, 'ما راه خدا و آیین حق را برای شما مردم آوردیم و لیکن اکثرتان از قبول حق کراهت (و اعراض) داشتید.'),
(4404, 43, 79, 'بلی کافران بر کار (کفر و عصیان) تصمیم گرفتند ما هم (بر کیفر و انتقام ایشان) تصمیم گرفتیم.'),
(4405, 43, 80, 'آیا گمان می‌کنند که سخنان سرّی و پنهان که به گوش هم می‌گویند نمی‌شنویم؟ بلی می‌شنویم و رسولان ما (فرشتگان) همان دم آن را می‌نویسند.'),
(4406, 43, 81, 'بگو: اگر خدا را فرزندی بود اوّل من او را می‌پرستیدم.'),
(4407, 43, 82, 'خدای آسمانها و زمین و پروردگار عرش (عظیم) از آنچه کافران توصیف او کنند پاک و منزّه است.'),
(4408, 43, 83, 'ای رسول، کافران را بگذار تا (به عالم حیوانیت) فرو روند و (به بازیچه دنیا) سرگرم باشند تا روزی را که به آنها وعده شده ببینند.'),
(4409, 43, 84, 'و آن ذات یگانه است که در آسمان و زمین (و در همه عوالم نامتناهی) او خداست و هم او (به نظام کامل آفرینش) به حقیقت با حکمت و داناست.'),
(4410, 43, 85, 'و بزرگوار خدایی که آسمانها و زمین و هر چه بین آنهاست همه ملک اوست و علم ساعت قیامت نزد اوست و شما همه به سوی او بازگردانیده می‌شوید.'),
(4411, 43, 86, 'و غیر خدای یکتا آنان را که به خدایی می‌خوانند قادر بر شفاعت کسی نیستند مگر (عزیر و عیسی و فرشتگان) کسانی که با علم الیقین بر توحید حق گواهی دهند (و شفاعت اهل حق کنند).'),
(4412, 43, 87, 'و اگر از مشرکان باز پرسی که آنها را که آفریده است؟ به یقین جواب دهند: خدا آفریده. پس به کجایشان می‌گردانند؟'),
(4413, 43, 88, '(خدا هم از ساعت قیامت آگاه است) و هم از گفتار و دادخواهی رسولش که گوید: خدایا اینها قومی هستند که هیچ ایمان نمی‌آورند.'),
(4414, 43, 89, '(پاسخ دهیم: رسولا، اکنون که از ایمان قوم مأیوسی) پس روی از آنها بگردان و بگو به سلامت (تا بروند در ضلالت) که به زودی (بر کیفر کفر و عصیانشان) آگاه می‌شوند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حم (قسم به حمید و مجید دو نام مقدس الهی)'),
(4416, 44, 2, 'قسم به این کتاب روشن بیان.'),
(4417, 44, 3, 'که ما آن را در شبی مبارک (شب قدر) فرستادیم، که ما بیم کننده‌ایم (و خلق را از عذاب قیامت آگاه کنیم و بترسانیم).'),
(4418, 44, 4, 'در آن شب هر امر استواری (با حکمت و تدبیر نظام احسن) معیّن و ممتاز می‌گردد.'),
(4419, 44, 5, 'تعیین آن امر البته از جانب ما که فرستنده پیغمبرانیم خواهد بود.'),
(4420, 44, 6, '(این ارسال رسول از) لطف و رحمت پروردگار توست که شنوا و داناست.'),
(4421, 44, 7, 'همان پروردگاری که خالق آسمانها و زمین و هر آنچه میان آنهاست اوست اگر یقین دارید.'),
(4422, 44, 8, 'هیچ خدایی غیر او نیست، او زنده می‌گرداند و باز می‌میراند، او خدای آفریننده شما و پدران پیشین شماست.'),
(4423, 44, 9, '(کافران را به خدا و قیامت ایمان نیست) بلکه با شک و ریب (و استهزاء) به بازیچه (دنیا) مشغولند.'),
(4424, 44, 10, '(ای رسول) منتظر باش روزی را که (بر عذاب کافران) آسمان دودی پدید آرد پیدا و آشکار.'),
(4425, 44, 11, 'آن دود آسمانی که عذابی دردناک است مردم را احاطه کند.'),
(4426, 44, 12, '(و کافران فریاد برآرند که) پروردگارا، این عذاب از ما بر طرف ساز که ما البته ایمان می‌آوریم.'),
(4427, 44, 13, 'کجا متذکر شده و ایمان می‌آورند در صورتی که رسول ما با آیات روشن بیان آمد (و ایمان نیاوردند).'),
(4428, 44, 14, 'پس (از مشاهده آیات) باز از او اعراض کردند و گفتند: او شخص دیوانه‌ای است که (مردم قرآن را) به او آموخته‌اند.'),
(4429, 44, 15, 'ما تا زمانی اندک (که برای امتحان) عذاب را (از شما) برمی‌داریم باز (به کفر خود) بر می‌گردید.'),
(4430, 44, 16, '(ای رسول منتظر باش) آن روز بزرگ که ما (آنها را) به عذاب سخت بگیریم که البته ما (از آنها) انتقام خواهیم کشید.'),
(4431, 44, 17, 'و ما پیش از این امت قوم فرعون را آزمودیم و رسولی بزرگوار (مانند موسی) به سوی آنها فرستادیم.'),
(4432, 44, 18, '(او گفت) که (ای فرعونیان) امر بندگان خدا را به من واگذارید، که من بر شما به یقین رسول امین پروردگارم.'),
(4433, 44, 19, 'و زنهار بر خدا تکبر و طغیان مجویید که من بر شما حجت آشکار آوردم.'),
(4434, 44, 20, 'و من به خدای خود و خدای شما پناه می‌برم از اینکه عزم آزار و سنگسار من کنید.'),
(4435, 44, 21, 'و اگر به (رسالت) من ایمان نمی‌آورید پس مرا به حال خود واگذارید (و در پی قتل و آزارم بر نیایید).'),
(4436, 44, 22, '(فرعونیان دست از ستمش برنداشتند) پس دعا کرد که ای خدا، اینان مردمی سخت مجرم و بدکارند.'),
(4437, 44, 23, '(خدا فرمود) پس تو بندگان (با ایمان) مرا شبانه از شهر بیرون بر، چرا که فرعونیان شما را تعقیب کنند.'),
(4438, 44, 24, 'آن گاه دریا را (همان گونه که بر تو بشکافتیم) آرام و خشک بگذار و بگذر که فرعون و لشکریانش تمام به دریا غرق شوند.'),
(4439, 44, 25, 'چه بسیار باغ و بستانها و چشمه‌های آب،'),
(4440, 44, 26, 'و کشت و زرعها و مقام و منزلهای عالی رها کردند و رفتند.'),
(4441, 44, 27, 'و ناز و نعمت وافری که در آن غرق بودند و از همه چشم پوشیدند.'),
(4442, 44, 28, 'این چنین بود و ما آن ناز و نعمتها را از قوم گذشته ارث به قوم دیگر دادیم.'),
(4443, 44, 29, 'و بر مرگ گذشتگان هیچ چشم آسمان و زمین نگریست و بر هلاکشان مهلت ندادند.'),
(4444, 44, 30, 'و البته ما بنی اسرائیل را از عذاب ذلّت و خواری نجات دادیم.'),
(4445, 44, 31, 'از شر فرعون که سخت متکبر و ستمکار بود آسوده ساختیم.'),
(4446, 44, 32, 'و آنها را در آن دور بر عالمیان به علم و دانش (کتاب آسمانی تورات) برگزیدیم.'),
(4447, 44, 33, 'و آیات و معجزاتی (از رحمت و غضب) بر آنها به دست موسی آوردیم که در آن کاملا و آشکارا آزمایش شوند.'),
(4448, 44, 34, 'همانا اینان (یعنی مشرکان مانند دهریان) البته خواهند گفت،'),
(4449, 44, 35, 'که ما جز این مرگ اول (دیگر هیچ مرگ و زندگی) نداریم و هیچ (در قیامت) زنده نخواهیم شد.'),
(4450, 44, 36, 'پس پدران ما را اگر راست می‌گویید بیاورید.'),
(4451, 44, 37, 'آیا اینان بهترند (و نیرومندتر) یا قوم تبّع و اقوام پیش از آنان که چون بسیار مردم بدکار مجرمی بودند ما همه را هلاک کردیم.'),
(4452, 44, 38, 'و ما آسمانها و زمین و آنچه را بین آنهاست به بازیچه خلق نکردیم.'),
(4453, 44, 39, 'آنها را جز به حق (و از روی حکمت و مصلحت) نیافریدیم و لیکن اکثر مردم از آن آگاه نیستند.'),
(4454, 44, 40, 'همانا روز قیامت که روز فصل و جدایی (مؤمن و کافر) است وعده‌گاه جمیع خلایق است.'),
(4455, 44, 41, 'روزی که هیچ حمایت خویش و یار و یاوری کسی را از عذاب نرهاند و احدی را نصرت نکنند.'),
(4456, 44, 42, 'مگر آن که خدا به او رحم کند، که او تنها بر خلق مقتدر و مهربان است.'),
(4457, 44, 43, 'همانا درخت زقّوم جهنّم،'),
(4458, 44, 44, 'قوت و غذای بدکاران است.'),
(4459, 44, 45, 'که آن غذا در شکمهاشان چون مس گداخته در آتش می‌جوشد.'),
(4460, 44, 46, 'آن سان که آب به روی آتش جوشان است.'),
(4461, 44, 47, '(خطاب قهر رسد که) این بدکار را بگیرید و به میان دوزخ افکنید.'),
(4462, 44, 48, 'پس از آن آب جوشان بر سرش فرو ریزید.'),
(4463, 44, 49, '(و به استهزاء وی گویید: عذاب دوزخ را) بچش که تو بسیار (نزد خود) توانمند و گرامی هستی.'),
(4464, 44, 50, 'این همان عذابی است که از آن در شک و انکار بودید.'),
(4465, 44, 51, 'همانا (آن روز سخت) آنان که متّقی و خداترس بودند مقام امن و امان یابند.'),
(4466, 44, 52, 'در باغها و کنار چشمه‌ها و نهرها بیارامند.'),
(4467, 44, 53, 'لباس از سندس و استبرق (حریر نازک و ستبر) پوشند و رو به روی هم بر تختها تکیه زنند.'),
(4468, 44, 54, 'همچنین است و با حوریان زیبا چشمشان جفت قرار داده‌ایم.'),
(4469, 44, 55, 'در آن بهشت پر نعمت از هر نوع میوه‌ای بخواهند بر آنان حاضر و (از هر درد و رنج و زحمت) ایمن و آسوده‌اند.'),
(4470, 44, 56, 'و جز آن مرگ اول (که از دنیا مردند) دیگر هیچ طعم مرگ را نمی‌چشند و خدا آنها را از عذاب دوزخ محفوظ خواهد داشت.'),
(4471, 44, 57, 'این به فضل و رحمت خدای توست و همین به حقیقت سعادت و فیروزی بزرگ است.'),
(4472, 44, 58, 'و ما این قرآن را (با این آیات وعد و وعید با بیان فصیح) به زبان تو آسان کردیم تا مگر خلقان متذکر (حقایق آن) شوند.'),
(4473, 44, 59, 'پس (از تلاوت این آیات و اتمام حجت بر کافران) منتظر (عذاب بر آنان) باش چنان که آنها هم (بر تو) انتظار (روزگار بد و حوادث ناگوار) دارند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حم (قسم به نام خدای حمید و مجید که).'),
(4475, 45, 2, 'تنزیل این کتاب عظیم از جانب خدای مقتدر حکیم است.'),
(4476, 45, 3, 'همانا در خلقت آسمانها و زمین برای اهل ایمان آیات و ادلّه‌ای (از قدرت الهی) پدیدار است.'),
(4477, 45, 4, 'و در خلقت خود شما آدمیان و انواع بی‌شمار حیوان که در روی زمین پراکنده است هم آیات و براهینی (از قدرت حق) برای اهل یقین آشکار است.'),
(4478, 45, 5, 'و نیز در رفت و آمد شب و روز و آنچه خدا از آسمان روزی (برای خلق از برف و باران) می‌فرستد و زمین را پس از خزان مرگ دیگر بار بدانها زنده می‌سازد و در وزش بادهای عالم، در همه این موجودات برای خردمندان جهان آیاتی (از قدرت صانع) پیداست.'),
(4479, 45, 6, 'اینها آیات خداست که ما بر تو به حق تلاوت می‌کنیم، پس بعد از خدا و آیات روشن او دیگر به چه برهان ایمان می‌آورند؟'),
(4480, 45, 7, 'وای بر هر دروغگوی بسیار زشتکار.'),
(4481, 45, 8, 'آن که آیات خدا را که بر او تلاوت می‌شود شنیده و بر تکبر و طغیان اصرار می‌کند چنانکه گویی هیچ آیات را نشنیده است، چنین کسانی را به عذاب دردناک بشارت ده.'),
(4482, 45, 9, 'و چون از آیات ما چیزی بداند آن را به مسخره می‌گیرد، چنین مردم کافر را (در دنیا و آخرت) عذاب ذلّت و خواری مهیّاست.'),
(4483, 45, 10, 'آنها را آتش دوزخ در پیش است و آنچه اندوختند هیچ آنان را نجات و رهایی ندهد و آنچه غیر خدا دوست گرفتند به کارشان نیاید و کیفر آنها بزرگ عذاب قهر خداست.'),
(4484, 45, 11, 'این قرآن هدایت خلق است و آنان که به آیات خدای خود کافر شدند جزای آنها عذاب دردناک دوزخ است.'),
(4485, 45, 12, 'خداست آن که برای شما دریا را مسخر گردانید تا کشتی به امر او (آسان) در آب جاری شود و از آن (به تجارت و سفر) از فضل خدا (روزی) طلبید و باشد که شکر نعمتش به جای آرید.'),
(4486, 45, 13, 'و آنچه در آسمانها و زمین است تمام را مسخر شما گردانید و اینها همه از سوی اوست. در این کار نیز برای مردم با فکرت آیاتی (از قدرت الهی) کاملا پدیدار است.'),
(4487, 45, 14, 'به آنان که ایمان آورده‌اند بگو که از (جور جهالتهای) مردمی که به ایّام الهی (و روز وعده‌های خدا) امیدوار نیستند درگذرند تا خدا عاقبت هر قومی را به پاداش اعمال خود برساند.'),
(4488, 45, 15, 'هر کس کار نیک کند به نفع خود و هر که بد کند به زیان خویش کرده است، آن گاه (برای پاداش نیک و بد) به سوی خدای خود بازگردانیده می‌شوید.'),
(4489, 45, 16, 'و ما بنی اسرائیل را کتاب (آسمانی تورات) و حکم و نبوت عطا کردیم و از هر روزی لذیذ و حلال نصیب آنها گردانیدیم و آنان را بر اهل دوران خود فضیلت دادیم.'),
(4490, 45, 17, 'و نیز به آن قوم آیات و معجزات روشن در امر (دین و نظم دنیا) عطا نمودیم و آنها خلاف و نزاع بر نینگیختند مگر دانسته، برای ظلم و تعدّی به حقوق یکدیگر. البته خدا بین نزاع و اختلافات آنها روز قیامت داوری خواهد کرد.'),
(4491, 45, 18, 'سپس ما تو را بر شریعت کامل در امر دین مقرر فرمودیم. تو آن را کاملا پیروی کن و هیچ پیرو هوای نفس مردم (مشرک) نادان مباش.'),
(4492, 45, 19, 'آن مردم هیچ تو را در برابر (عذاب) خدا به کار نیایند، و ستمکاران عالم به نفع خود در ظلم و ستم دوستدار و مددکار یکدیگرند و خدا دوستدار و مددکار متقیان است.'),
(4493, 45, 20, 'این قرآن برای عموم مردم مایه بصیرت بسیار و برای اهل یقین موجب هدایت و رحمت پروردگار است.'),
(4494, 45, 21, 'آیا آنان که مرتکب اعمال زشت و تبهکاری شدند گمان کردند رتبه آنها را مانند کسانی که ایمان آورده و نیکوکار شدند قرار می‌دهیم تا در مرگ و زندگانی هم با مؤمنان یکسان باشند؟ حکم آنها اندیشه بسیار باطل و جاهلانه‌ای است.'),
(4495, 45, 22, 'و خدا آسمانها و زمین را به حق آفریده است (و در هر چه خلق کرده مصلحت و مقصودی داشته) و عاقبت هر نفسی بی هیچ ستمی پاداش هر عملی که کرده است خواهد یافت.'),
(4496, 45, 23, '(ای رسول ما) آیا می‌نگری آن را که هوای نفسش را خدای خود قرار داده و خدا او را دانسته (و پس از اتمام حجت) گمراه ساخته و مهر (قهر) بر گوش و دل او نهاده و بر چشم وی پرده ظلمت کشیده؟ پس او را بعد از خدا دیگر که هدایت خواهد کرد؟ آیا متذکر این معنی نمی‌شوید؟'),
(4497, 45, 24, 'و کافران گفتند که زندگی ما جز همین نشأه دنیا و مرگ و حیات طبیعت نیست و جز دهر و طبیعت کسی ما را نمی‌میراند. و این سخن نه از روی علم و دلیل بلکه از روی جهل و وهم و خیال می‌گویند.'),
(4498, 45, 25, 'و چون آیات و ادلّه روشن ما بر آن کافران تلاوت شود دیگر حجت و عذری نداشته جز اینکه گویند: اگر راست می‌گویید پدران ما را (زنده کنید و) بیاورید.'),
(4499, 45, 26, 'بگو: خدا شما را زنده کند سپس بمیراند و باز تمام شما را جمع گرداند به روز قیامت که آن روز بی شک و ریب بیاید و لیکن اکثر مردم نمی‌دانند.'),
(4500, 45, 27, 'آسمانها و زمین همه ملک خداست و روزی که ساعت قیامت بر پا شود آن روز اهل باطل (و منکران معاد) زیانکار خواهند بود.'),
(4501, 45, 28, 'و در آن روز هر گروهی را بنگری که به زانو در آمده و هر فرقه به سوی کتاب و نامه عمل خود خوانده شود (و ندا شود که) امروز همه به پاداش اعمال خود می‌رسید.'),
(4502, 45, 29, 'این کتاب ضبط ما به حق بر شما سخن می‌گوید، که ما هر چه می‌کرده‌اید همه را درست می‌نگاشتیم.'),
(4503, 45, 30, 'اما آنان که ایمان آورده و نیکوکار شدند آنها را خدا در رحمت خود داخل سازد که آن سعادت و فیروزی معیّن و معلومی است.'),
(4504, 45, 31, 'و اما آنان که کافر شدند (چون از شدت عذاب بنالند به آنها گوییم) آیا بر شما آیات مرا تلاوت نمی‌کردند و شما تکبر و عصیان کرده و مردمی بدکار و مجرم بودید؟'),
(4505, 45, 32, 'و چون به شما گفته شد که وعده خدا حق است و در قیامت هیچ شک و ریب نیست شما گفتید: قیامت چیست؟ نمی‌فهمیم، بس خیالی پیش خود می‌کنیم و به آن هیچ یقین نداریم.'),
(4506, 45, 33, 'و در آن هنگام بر آنان نتیجه اعمال بدشان پدید آید و عذابی که به آن تمسخر می‌کردند به آنها احاطه کند.'),
(4507, 45, 34, 'و با آنها خطاب شود که امروز ما شما را (از رحمت خود) فراموش می‌کنیم چنانکه شما ملاقات این روز سخت را فراموش کردید. اینک منزل شما آتش دوزخ است و بر نجات خود هیچ یار و یاوری ندارید.'),
(4508, 45, 35, 'این عذاب شما کیفر آن است که به آیات خدا تمسخر کردید و مغرور زندگانی دنیا شدید. پس کافران امروز از آتش دوزخ رهایی ندارند و هیچ عذر و توبه ایشان نپذیرند.'),
(4509, 45, 36, 'باری ستایش و سپاس مخصوص خداست که به یکتایی آفریننده آسمانها و زمین و همه جهانهای بی نهایت است.'),
(4510, 45, 37, 'و مقام جلال و کبریایی در آسمانها و زمین مخصوص اوست و او یکتا خدای مقتدر حکیم است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حم (قسم به خدای حمید و مجید که)'),
(4512, 46, 2, 'تنزیل این قرآن عظیم از جانب خدای مقتدر حکیم است.'),
(4513, 46, 3, 'ما آسمانها و زمین و آنچه را بین آنهاست جز به حق (و برای حکمت و مصلحت خلق) و جز در وقت معیّن نیافریده‌ایم و آنان که کافرند از هر چه پند و اندرزشان کنند و بیمشان دهند روی می‌گردانند.'),
(4514, 46, 4, 'بگو: جز خدا همه آن بتهایی که به خدایی می‌خوانید به من نشان دهید که آیا در زمین چیزی آفریده‌اند؟ یا شرکتی با خدا در خلقت آسمانها دارند؟ اگر از کتب آسمانی پیشین دلیلی بر خدایی بتان دارید یا کمتر اثر و نشان بر درستی عقیده شرک یافته‌اید اگر راست می‌گویید بر من بیاورید.'),
(4515, 46, 5, 'و کیست گمراه‌تر از آن که جز خدا کسی را بپرستد که هیچ در حوائج تا قیامت او را اجابت نکند و از هر چه بخوانندشان خود بی خبر باشند؟'),
(4516, 46, 6, 'و چون در قیامت خلق محشور شوند آنجا معبودان باطل با مشرکان دشمن و از پرستش آنها بیزارند.'),
(4517, 46, 7, 'و هر گاه آیات روشن ما را بر مردم تلاوت کنند آنان که کافر شدند چون آیات الهی آمد گویند که این کتاب سحر آشکارایی است.'),
(4518, 46, 8, 'بلکه کافران می‌گویند که (محمّد) خود این آیات قرآن را فرا بافته است. بگو: اگر این قرآن را من از خود بافته باشم شما از قهر خدا هیچ قدرت بر نجات من ندارید، او به افکاری که در آن فرو می‌روید داناتر است، گواه میان من و شما هم او کافی است و اوست خدای بسیار آمرزنده و مهربان.'),
(4519, 46, 9, 'بگو: من از بین رسولان، اولین پیغمبر نیستم (که تازه در جهان آوازه رسالت بلند کرده باشم تا تعجب و انکار کنید) و نمی‌دانم که با من و شما عاقبت چه می‌کنند؟ من جز آنچه بر من وحی می‌شود پیروی نمی‌کنم و جز آنکه با بیان روشن (خلق را آگاه کنم و از خدا) بترسانم وظیفه‌ای ندارم.'),
(4520, 46, 10, 'بگو: چه تصور می‌کنید، اگر از جانب خدا باشد و شما به آن کافر شوید در صورتی که از بنی اسرائیل شاهدی (چون عبد الله سلام عالمی) بر حق بودن قرآن گواهی دهد و ایمان آرد و شما تکبر ورزید (جز آن است که به عذاب خدا گرفتار می‌شوید)؟ آری البته خدا مردم ستمکار را هدایت نخواهد کرد.'),
(4521, 46, 11, 'و کافران گفتند: اگر دین اسلام بهتر (از بت‌پرستی) بود مردم (فقیر زبونی) از ما در ایمان به آن سبقت نمی‌گرفتند. و آنها چون به قرآن هدایت نمی‌شوند خواهند گفت که این کتاب دروغی از گفتار پیشینیان است.'),
(4522, 46, 12, 'و پیش از قرآن کتاب تورات بر موسی که امام و پیشوای مهربان خلق بود نازل گردید و این کتاب قرآن مصدّق کتب آسمانی پیشین به زبان فصیح عربی نازل شده تا ستمکاران عالم را (از عذاب خدا) بترساند و نیکوکاران را بشارت (به رحمت ایزد) دهد.'),
(4523, 46, 13, 'آنان که گفتند: آفریننده ما خداست و بر این سخن پایدار و ثابت ماندند بر آنها هیچ ترس و بیمی و حزن و اندوهی (در دنیا و عقبی) نخواهد بود.'),
(4524, 46, 14, 'آنان اهل بهشتند و به پاداش اعمال نیک همیشه در بهشت ابد مخلّد خواهند بود.'),
(4525, 46, 15, 'و ما انسان را به احسان در حق پدر و مادر خود سفارش کردیم، که مادر با رنج و زحمت بار حمل او کشید و باز با درد و مشقّت وضع حمل نمود و سی ماه تمام مدت حمل و شیرخواری او بود تا وقتی که طفل به حد رشد رسید و آدمی چهل ساله گشت (و عقل و کمال یافت آن گاه سزد که) عرض کند: بار خدایا، مرا بر نعمتی که به من و پدر و مادر من عطا فرمودی شکر بیاموز و به کار شایسته‌ای که رضا و خشنودی تو در آن است موفق دار و فرزندان مرا صالح گردان، من به درگاه تو باز آمدم و از تسلیمان فرمان تو شدم.'),
(4526, 46, 16, 'این بندگانند که به نیکوترین اعمال خود مقبول درگاه ما شده و در زمره اهل بهشت از گناهانشان می‌گذریم. این وعده صدقی است که به آنها بشارت می‌داده‌اند.'),
(4527, 46, 17, 'و (چقدر ناخلف است) فرزندی که پدر و مادر را گفت: افّ بر شما باد، آیا به من وعده می‌دهید که پس از مرگ مرا زنده کرده و از قبر بیرون می‌آرند در صورتی که پیش از من گروه و طوایف بسیاری رفتند (و یکی باز نیامد)؟ آن گاه پدر و مادر به خداست غاثه کنند (که پروردگارا، تو فرزند ما را هدایت کن و به او گویند) که وای بر تو، ایمان بیار، البته وعده خدا بر حق و حقیقت است، باز (آن نا اهل فرزند) گوید: این سخنان جز افسانه و اوهام پیشینیان نیست.'),
(4528, 46, 18, 'اینان در میان طوایف بسیاری از جن و انس که از این پیش (به کفر) مردند کسانی هستند که وعده عذاب خدا بر آنها حتم است و اینها به حقیقت زیانکاران عالمند.'),
(4529, 46, 19, 'و برای تمام (مردم مؤمن و کافر) به مقتضای اعمالشان درجاتی معین است (در عالم قیامت تا عدل خدا درباره آنها آشکار شود) و تا همه را به پاداش و مزد اعمال خود کاملا برساند و هیچ ستمی به آنها نخواهد شد.'),
(4530, 46, 20, 'و روزی که کافران را بر آتش دوزخ عرضه کنند (فرشتگان قهر و عذاب به آنها گویند) شما (لذّات بهشتی و) خوشیهایتان را در زندگانی دنیا (به شهوت رانی و ظلم و عصیان) از بین بردید و بدان لذّات دنیوی بر خوردار و دلباخته بودید، پس امروز به عذاب ذلّت و خواری مجازات می‌شوید چون در زمین به ناحق تکبر می‌کردید و راه فسق و تبهکاری پیش می‌گرفتید.'),
(4531, 46, 21, 'و یاد آر حال هود پیغمبر قوم عاد را که چون در سرزمین احقاف امتش را وعظ و اندرز کرد و (از قهر خدا) بترسانید و پیش از هود و بعد از او هم بسیار پیمبر بر انذار خلق آمد. باری هود قومش را گفت: هرگز جز خدای عالم کسی را نپرستید که من بر شما از عذاب روز بزرگ قیامت می‌ترسم.'),
(4532, 46, 22, 'قوم هود در پاسخ گفتند: آیا تو آمده‌ای ما را از پرستش بتان که خدایان ما هستند منع کنی؟! پس تو (زود) وعده عذابی را که به ما می‌دهی بیار اگر راست می‌گویی.'),
(4533, 46, 23, 'هود در جواب گفت: علمش نزد خداست و من آنچه را که بر آن رسالت داشتم به شما ابلاغ کردم و لیکن شما را بسیار مردم نادانی می‌بینم.'),
(4534, 46, 24, 'پس چون آن عذاب را به شکل ابری دیدند که بر رودخانه‌هاشان روی آورد گفتند: این ابری است که بر ما باران می‌بارد. (هود به آنها گفت: چنین نیست) بلکه اثر عذابی است که به تعجیل درخواستید، این باد سختی است که در آن عذابی دردناک است.'),
(4535, 46, 25, 'این بادی است که هر چیزی را به امر خدای خود نابود و هلاک می‌سازد. پس شبی صبح کردند که جز خانه‌های ویران آنها دیده نمی‌شد (و اثری از آنان نماند). بلی ما بدین گونه قوم بدکار را کیفر می‌دهیم.'),
(4536, 46, 26, 'و به آن قوم عاد تمکین و قوتی دادیم که شما امت را چنان نیروی بدنی ندادیم و با آنکه بر آنها گوش و چشم و قلب مدرک قرار دادیم (تا با قوای ادراکی خداشناس شوند) هیچ این قوای مدرکه آنها را از عذاب نرهانید بدین جهت که آیات خدا را انکار می‌کردند، و آن عذابی که بدان استهزا می‌کردند به آنها فرا رسید.'),
(4537, 46, 27, 'و ما چه بسیار اهل شهر و دیار را که در اطراف شما بودند (به کیفر کفر) همه را هلاک کردیم و آیات (عبرت برای مردم) را به گونه‌های مختلف بیان داشتیم تا مگر (از کفر و گناه به درگاه خدا) باز گردند.'),
(4538, 46, 28, 'پس چرا جز خدا معبودانی که به آنها تقرب می‌جستند هیچ آنها را یاری نکردند؟ بلکه از نظرشان محو و نابود شدند؟ و این خدایی آن بتها سخنان بی حقیقت و دروغی بود که خود می‌بافتند.'),
(4539, 46, 29, 'و (یاد آر) وقتی که ما تنی چند از جنّیان را متوجه تو گردانیدیم که استماع آیات قرآن کنند، چون نزد رسول رسیدند با هم گفتند: گوش فرا دهید. چون قرائت تمام شد به سوی قومشان برای هشدار (و تبلیغ و هدایت) باز گردیدند.'),
(4540, 46, 30, 'گفتند: ای طایفه ما، ما (آیات) کتابی را شنیدیم که پس از موسی نازل شده در حالی که کتب آسمانی تورات و انجیل را که در مقابل او بود به راستی تصدیق می‌کرد و (خلق را) به سوی حق و طریق راست هدایت می‌فرمود.'),
(4541, 46, 31, 'ای طایفه ما، شما هم داعی حق (محمد مصطفی ص) را اجابت کنید و به او ایمان آرید تا خدا از گناهانتان در گذرد و شما را از عذاب دردناک قیامت نگاه دارد.'),
(4542, 46, 32, 'و هر که داعی حق را اجابت نکند در زمین مفر و پناهی از قهر خدا نتواند یافت و جز او هیچ یار و یاوری نخواهد داشت و چنین کسان در ضلالت و گمراهی آشکار هستند.'),
(4543, 46, 33, 'آیا کافران ندیدند خدایی که آسمانها و زمین را آفرید و هیچ از آفرینش آنها فرو نماند البته هم او قادر بر آن است که مردگان را باز زنده کند؟ آری البته او بر هر چیز قادر است.'),
(4544, 46, 34, 'و روزی که کافران را بر آتش دوزخ عرضه کنند (به آنها گویند) آیا این وعده دوزخ حقیقت نبود؟ گویند: بلی به پروردگارمان سوگند که حق بود. خدا گوید: پس امروز به کیفر کفرتان سختی عذاب دوزخ را بچشید.'),
(4545, 46, 35, '(ای رسول ما) تو هم مانند پیغمبران اولوا العزم (در تبلیغ دین خدا و تحمل اذیّت امت) صبور باش و بر امت به عذاب تعجیل مکن تا روزی که آنچه وعده داده شده به چشم ببینند آن روز پندارند که (در دنیا) به جز ساعتی از روز درنگ نداشتند. (این قرآن و آیات وعده و وعیدش) تبلیغ رسالت و اتمام حجتی است، پس آیا (به قیامت) جز مردم (شهوت پرست) فاسق هیچ کس هلاک خواهد شد؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'آنان که کافر شدند و راه دین خدا را (بر خلق) بستند خداوند اعمال آنها را تباه و باطل ساخت.'),
(4547, 47, 2, 'و آنان که گرویدند و نیکوکار شدند و به قرآنی که بر محمّد (صلی اللّه علیه و آله و سلم) نازل شد که البته بر حق و از جانب خدایشان بود ایمان آوردند خدا از گناهانشان درگذشت و امر (دنیا و دین) آنها را اصلاح فرمود.'),
(4548, 47, 3, 'این (تباهی حال کافران و اصلاح مؤمنان) بدان سبب است که کافران پیرو (آراء و عقاید اهل) باطل گردیدند و اهل ایمان پیرو (قرآن) حق که از جانب خدایشان نازل گردید شدند. این گونه صریح خدا مثال حال مردم را بیان می‌کند.'),
(4549, 47, 4, 'شما مؤمنان چون (در میدان جنگ) با کافران رو به رو شوید باید (شجاعانه) آنها را گردن زنید تا آن گاه که از خونریزی بسیار دشمن را از پا در آوردید پس از آن، اسیران جنگ را محکم به بند کشید که بعدا یا بر آنها منّت نهید (و آنها را آزاد گردانید) یا فدا گیرید تا در نتیجه جنگ سختیهای خود را فروگذارد. این حکم فعلی است، و اگر خدا بخواهد خود از کافران انتقام می‌کشد (و همه را بی زحمت جنگ شما هلاک می‌کند) و لیکن (با این جنگ کفر و ایمان) می‌خواهد شما را به یکدیگر امتحان کند، و آنان که در راه خدا کشته شدند خدا هرگز اعمالشان را ضایع نگرداند.'),
(4550, 47, 5, 'آنها را البته به راه سعادت هدایت کند و امورشان را اصلاح فرماید.'),
(4551, 47, 6, 'و در بهشتی که قبلا به منزل و مقاماتشان همه را شناسا کرده وارد کند.'),
(4552, 47, 7, 'ای اهل ایمان، شما اگر خدا را یاری کنید (یعنی دین و پیغمبر خدا را) خدا هم شما را یاری کند و ثابت قدم گرداند.'),
(4553, 47, 8, 'و آنان که کافر شدند نابود و هلاک شوند و خدا اعمالشان را ضایع و باطل سازد.'),
(4554, 47, 9, 'این (هلاک کفار) بدان سبب است که آنها از قرآنی که خدا نازل فرمود کراهت و اعراض داشتند پس خدا اعمالشان را محو و نابود فرمود.'),
(4555, 47, 10, 'آیا در زمین به سیر و سفر نمی‌روند تا به چشم خود عاقبت حال پیشینیان را مشاهده کنند که چگونه آنها را خدا هلاک کرد؟ و این کافران هم مانند آنها به کیفر کفر خود البته می‌رسند.'),
(4556, 47, 11, 'این (معامله خدا با کافران و مؤمنان) بدان سبب است که البته خدا یار و مولای مؤمنان است و کافران هیچ مولا و یاور و یاری ندارند.'),
(4557, 47, 12, 'البته خدا آنان را که ایمان آورده و نیکوکار شوند در بهشتی که زیر درختانش نهرها جاری است داخل گرداند و آنان که به راه کفر شتافتند به تمتّع و شهوت‌رانی و شکم‌پرستی مانند حیوانات پردازند و عاقبت منزل آنها آتش دوزخ خواهد بود.'),
(4558, 47, 13, 'چه شهرهای بسیار که از (شهر مکّه) وطن تو که کفّار از آن خارجت کردند محکم بنیان‌تر بود ما اهلش را هلاک کردیم و بر نجات خود هیچ یاوری نداشتند.'),
(4559, 47, 14, 'آیا آن کس که از خدای خود حجت علمی و برهانی (مانند قرآن) در دست دارد مانند کسانی است که عمل زشتشان در نظر زیبا جلوه کرده و پیرو هوای نفسانی خود شدند؟'),
(4560, 47, 15, 'داستان بهشتی که به متقیان وعده دادند این است که در آن باغ بهشت نهرهایی از آب زلال دگرگون ناشدنی است و نهرها از شیر بی آنکه هرگز طعمش تغییر کند و نهرها از شراب ناب که نوشندگان را به حد کمال لذّت بخشد و نهرها از عسل مصفّی و تمام انواع میوه‌ها بر آنان مهیّاست و (فوق همه لذّات) مغفرت و لطف پروردگارشان، (آیا حال آن که در این بهشت ابد است) مانند کسی است که در آتش مخلّد است و آب جوشنده حمیم به خوردشان دهند تا اندرونشان را پاره پاره گرداند؟'),
(4561, 47, 16, 'و بعضی از مردم منافق به گفتارت کاملا گوش می‌دهند تا وقتی که از حضورت خارج شوند با اهل علم (اصحابت به تمسخر و اهانت) می‌گویند: رسول باز از سر نو چه گفت؟ اینان هستند که خدا بر دلهاشان مهر (قهر) نهاد و پیرو هوای نفس خود گردیدند.'),
(4562, 47, 17, 'و آنان که هدایت یافتند خدا بر هدایت و ایمانشان بیفزود و (پاداش) تقوای آنها را به آنان عطا فرمود.'),
(4563, 47, 18, '(کافران که ایمان نمی‌آرند) باز انتظاری دارند جز آنکه ساعت قیامت ناگاه فرا رسد؟ که شروط و علائم قیامت بسیاری پدید آمد، و پس از آنکه قیامت بیاید در آن حال تذکر و یادآوری آنان چه فایده بخشد؟'),
(4564, 47, 19, 'باز هم بدان که هیچ خدایی جز خدای یکتا نیست و تو بر گناه خود [منظور از گناه آن حضرت نافرمانی خدا نیست، زیرا پیامبر (ص) معصوم است. به تفاسیر مراجعه شود. (م)] و برای مردان و زنان با ایمان آمرزش طلب، و خدا منازل انتقال شما به عالم آخرت و مسکن همیشگی شما همه را می‌داند.'),
(4565, 47, 20, 'و مؤمنان می‌گویند: چه شده که سوره‌ای (در حکم جهاد کفّار) نازل نشد؟ در صورتی که چون سوره‌ای محکم و صریح آید و در آن ذکر جنگ شود آنان را که دلهاشان مریض (نفاق) است بنگری که مانند کسی که از ترس، حال بی هوشی بر او دست می‌دهد در تو نگاه می‌کنند، آری مرگ و هلاک بر آنها سزاوارتر است.'),
(4566, 47, 21, '(راه سعادت) طاعت خدا و گفتار نیکو است و پس از آنکه امر (دین یا کار جنگ) به عزم و لزوم پیوست اگر (منافقین) با خدا به راستی رفتار کنند بر آنها بهتر خواهد بود.'),
(4567, 47, 22, 'شما منافقان اگر (از فرمان خدا و اطاعت قرآن) روی بگردانید آیا جز این امید می‌برید که در زمین فساد و قطع رحم کنید؟'),
(4568, 47, 23, 'همین منافقانند که خدا آنها را لعن کرده و گوش و چشمشان را کر و کور گردانید (تا به جهل و شقاوت بمیرند).'),
(4569, 47, 24, 'آیا منافقان در آیات قرآن تفکر نمی‌کنند یا بر دلهاشان خود قفل‌ها (ی جهل و نفاق) زده‌اند.'),
(4570, 47, 25, 'آنان که پس از بیان شدن راه هدایت بر آنها باز به دین پشت کرده و مرتد شدند شیطان کفر را در نظرشان جلوه‌گر ساخت و به آمال و آرزوهای دراز فریبشان داد.'),
(4571, 47, 26, 'این (برگشتن از دین) برای آن بود که آن منافقان به دشمنان قرآن (پنهانی) گفتند: ما البته با شما (بر مخالفت محمّد) تا بتوانیم موافقت می‌کنیم. و خدا از این سخن پنهانی آنها آگاه است.'),
(4572, 47, 27, 'پس با چه حال سختی اینان رو به رو شوند هنگامی که فرشتگان (عذاب) جانشان بگیرند و بر روی و پشت آنها (تازیانه قهر) زنند؟!'),
(4573, 47, 28, 'این عذاب بدین سبب است که از پی راهی که موجب خشم خداست رفتند و راه رضا و خشنودی او را خوش نداشتند خدا هم اعمالشان را محو و باطل گردانید.'),
(4574, 47, 29, 'آیا آنان که در دل مرض (نفاق پنهان) دارند پندارند که خدا کینه درونی آنها را آشکار نمی‌سازد؟'),
(4575, 47, 30, 'اگر ما می‌خواستیم حقیقت آنها را (به وحی) بر تو آشکار می‌ساختیم تا به باطن آنها از سیمای ظاهرشان پی برده و در طیّ سخن کاملا آنها را بشناسی. و (ای منافقان و ای مؤمنان) خدا به همه کارهای شما آگاه است.'),
(4576, 47, 31, 'و البته ما (به حکم جهاد) شما را در مقام امتحان می‌آوریم تا (مقام) آن را که در راه خدا جهاد و مجاهده و کوشش دارد و (بر رنج آن) صبر می‌کند معلوم سازیم و اخبار و اظهارات شما (منافقان) را نیز (به مقام عمل) بیازماییم.'),
(4577, 47, 32, 'آنان که کافر شدند و راه خدا را (به روی خلق) بستند و با رسول او پس از آنکه راه هدایت بر آنها روشن شد باز مخالفت کردند به خدا ضرری نمی‌رسانند (و زیان مخالفت بر خود آنهاست) و اعمال آنها را خدا البته بی اثر و محو و نابود می‌گرداند.'),
(4578, 47, 33, 'ای اهل ایمان، خدا را اطاعت کنید و رسول او را اطاعت کنید و اعمال خود را ضایع و باطل نگردانید.'),
(4579, 47, 34, 'آنان که کافر شدند و راه خدا را (به روی خلق) بستند و به حال کفر مردند دیگر ابدا خدا آنها را نخواهد بخشید.'),
(4580, 47, 35, 'پس (شما ای اهل ایمان در کار دین) سستی روا مدارید و (از ترس جنگ، کافران را) دعوت به صلح مکنید، که شما غالب و بلند مقام‌تر خواهید بود و خدا با شماست و از (ثواب) اعمال شما هیچ نمی‌کاهد.'),
(4581, 47, 36, 'البته زندگانی دنیا به جز بازیچه و هوسرانی نیست و اگر ایمان آرید و پرهیزکار شوید پاداش اعمال شما را خواهد داد و از اموال شما چیزی (مزد هدایت) نمی‌خواهد.'),
(4582, 47, 37, 'اگر از مال شما خدا چیزی به اصرار هم بخواهد باز بخل می‌ورزید و کینه و خبث درونی شما را آشکار می‌سازد.'),
(4583, 47, 38, 'آری شما همان مردمید که برای انفاق در راه (تبلیغ دین) خدا دعوت می‌شوید باز بعضی از شما بخل می‌ورزند، و هر که بخل کند بر ضرر خود اوست و گرنه خدا (از خلق) بی نیاز است و شما فقیر و نیازمندید. و اگر شما روی بگردانید خدا قومی غیر شما که مانند شما (بخیل) نیستند (بلکه بسی از شما بهتر و فداکارترند) به جای شما پدید آرد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, '(ای رسول ما غم مدارکه) البته ما تو را به فتح آشکاری فیروز می‌گردانیم (که نه بس شهر مکّه بلکه شهر دلهای اهل عالم را فتح کنی).'),
(4585, 48, 2, 'تا خدا از گناه گذشته و آینده تو در گذرد (مفسرین در این آیه گویند: مراد از گناه پیغمبر گناه او بود به عقیده مشرکان که دعوت او را به توحید خدا بزرگترین گناه او می‌شمردند و مقصود از گناه گذشته و آینده دعوت قبل از هجرت و بعد از هجرت بود) و نعمت خود را بر تو به حد کمال رساند و تو را به راه مستقیم (شرع اسلام به وحی خود) هدایت کند.'),
(4586, 48, 3, 'و خدا تو را به نصرتی با عزت و کرامت یاری کند.'),
(4587, 48, 4, 'اوست خدایی که سکینت و وقار بر دلهای مؤمنان آورد تا بر یقین و ایمانشان بیفزاید، و سپاه قوای آسمانها و زمین همه لشکر خداست و خدا دانا و به حکمت نظام آفرینش آگاه است.'),
(4588, 48, 5, 'این (فتح یا فزودن ایمان) برای آن است که خدا می‌خواهد مردان و زنان مؤمن را تا ابد در بهشت‌هایی که زیر درختانش نهرها جاری است داخل گرداند و گناهانشان را تمام ببخشد و این به حقیقت نزد خدا فیروزی بزرگ است.'),
(4589, 48, 6, 'و نیز خدا خواهد تا همه منافقان و مشرکان را از مرد و زن عذاب کند که آنها به خدا بد گمان بودند (و وعده فتح خدا را دروغ پنداشتند) در صورتی که (وعده خدا صدق است و) روزگار بد و هلاکت ابد بر خود آنهاست و خدا بر آنان خشم و لعن کرد و جهنم را که بسیار بد منزلگاهی است بر ایشان مهیّا ساخت.'),
(4590, 48, 7, 'و سپاه قوای آسمانها و زمین لشکر خداست و خدا بسیار مقتدر و به تدبیر نظام عالم داناست.'),
(4591, 48, 8, 'ما شخص تو را به عالم فرستادیم که شاهد (نیک و بد امت) باشی و (خلق را به لطف و رحمت حق) بشارت دهی و (از قهر و عذاب او) بترسانی.'),
(4592, 48, 9, 'تا شما (صالحان امت) به خدا و رسول او ایمان آورده و (دین) او را یاری کنید و به تعظیم جلالش صبح و شام تسبیح او گویید.'),
(4593, 48, 10, 'همانا مؤمنانی که (در حدیبیّه) با تو بیعت می‌کنند به حقیقت با خدا بیعت می‌کنند (چون تو خلیفه خدایی و همانا دست تو) دست خداست بالای دست آنها، پس از آن هر که نقض بیعت کند بر زیان و هلاک خویش به حقیقت اقدام کرده و هر که به عهدی که با خدا بسته است وفا کند به زودی خدا به او پاداش بزرگ عطا خواهد کرد.'),
(4594, 48, 11, 'اعراب بادیه که (از حضور در جنگها و در سفر فتح مکّه تخلّف می‌ورزند و) باز نهاده می‌شوند خواهند گفت که ما را (محافظت) اهل بیت و اموالمان (از آمدن در رکابت) بازداشت، اینک از خدا بر گناه ما آمرزش طلب. (این مردم منافق) چیزی که هیچ به دل عقیده ندارند به زبان می‌آورند، به آنها بگو: اگر خدا اراده کند که ضرر یا نفعی به شما رساند آن کیست که خلاف آن کاری تواند کرد؟ بلکه خدا به هر چه می‌کنید آگاه است.'),
(4595, 48, 12, 'بلکه شما پنداشتید که رسول و مؤمنان به او (همه کشته می‌شوند و) به سوی (وطن و) اهل بیت خود دیگر بر نخواهند گشت و این خیال در دل شما به خطا جلوه کرد و بسیار گمان بد و اندیشه باطلی کردید و مردمی در خور قهر و هلاکت بودید.'),
(4596, 48, 13, 'و هر که به خدا و رسول او ایمان نیاورد ما هم بر آن کافران عذاب آتش دوزخ را مهیّا ساخته‌ایم.'),
(4597, 48, 14, 'و ملک آسمانها و زمین همه خاصّ خداست، او هر که را بخواهد می‌بخشد و هر که را بخواهد عذاب می‌کند و خدا بسیار آمرزنده و مهربان است.'),
(4598, 48, 15, 'کسانی که (در حدیبیّه تخلّف کردند و) باز نهاده شدند و همراهی با شما ننمودند باز چون برای (فتح خیبر و) گرفتن غنایم حرکت کنید خواهند گفت: بگذارید تا ما هم از شما تبعیت کنیم (و همراه شما بیاییم). غرضشان این است که سخن خدا را (که فرمود غنایم خیبر را تنها حاضران حدیبیّه خواهند گرفت) تغییر دهند. بگو: شما به حقیقت هرگز ما را پیروی نمی‌کنید، خدا از این پیش درباره شما چنین خبر داده. باز (آن مردم بی وفای بد عهد) خواهند گفت: (چنین نیست) بلکه شما با ما حسد می‌ورزید. بلکه ایشان جز عدّه قلیلی همه جاهل و نادانند.'),
(4599, 48, 16, 'با اعرابی که (از جنگ) باز نهاده شدند بگو: (اگر به حقیقت قصد متابعت دارید) به زودی برای جنگ با قومی شجاع و نیرومند (فارس و روم) دعوت می‌شوید که با آنها یا جنگ و مبارزه کنید یا آنکه مسلمان شوند. اگر اطاعت کردید خدا به شما اجر نیکو خواهد داد و اگر نافرمانی کنید چنانکه از این پیش (در حدیبیّه) مخالفت کردید خدا شما را به عذابی دردناک معذّب خواهد کرد.'),
(4600, 48, 17, 'برای شخص نابینا و لنگ و مریض (در تخلّف از جنگ) حرج و گناهی نیست، و هر که خدا و رسول او را فرمان برد او را به باغهای بهشتی داخل کند که زیر درختانش نهرها جاری است و هر که مخالفت کند او را به عذابی دردناک معذّب نماید.'),
(4601, 48, 18, 'خدا از مؤمنانی که زیر درخت (معهود حدیبیّه) با تو بیعت کردند به حقیقت خشنود گشت و از وفا و خلوص قلبی آنها آگاه بود که وقار و اطمینان کامل بر ایشان نازل فرمود و به فتحی نزدیک (که فتح خیبر بود) پاداش داد.'),
(4602, 48, 19, 'و به غنیمتهای فراوان که (از خیبریان) خواهند گرفت موفق داشت، و خدا مقتدر و داناست.'),
(4603, 48, 20, 'خدا به شما (لشکر اسلام) وعده گرفتن غنیمتهای بسیار داده که این (یک غنیمت خیبر) را برای شما تعجیل در انجام آن فرمود و دست مردم (کافر) را از سر شما کوتاه کرد (تا همپیمانان یهود عبرت گیرند) و تا (این فتح و غنیمت) آیت و دلیل اهل ایمان (بر صدق وعده خدا) باشد و خدا شما را به راه راست هدایت فرماید.'),
(4604, 48, 21, 'و خدا به شما باز وعده غنیمتهای دیگری فرموده که هنوز بر آن قادر نیستید و علم خدا محیط بر آن است و خدا بر هر چیز تواناست.'),
(4605, 48, 22, 'و اگر کافران با شما مسلمین به جنگ برخیزند از قتال شما پشت گردانیده و فرار کنند و دیگر هیچ ناصر و یاوری برای خود نیابند.'),
(4606, 48, 23, 'سنّت الهی (و قانون نظام ربّانی) بر این بوده (که حق بر باطل غالب شود) و ابدا در سنّت خدا تغییری نخواهی یافت.'),
(4607, 48, 24, 'و او خدایی است که دست کفّار را از سر شما و دست شما را هم به زمین مکّه پس از فیروزیتان از سر آنها کوتاه کرد (که به امر خدا پیغمبر روز فتح مکّه برای حفظ جان دشمنان خانه‌هایی را پناه قرار داد) و خدا به هر چه می‌کنید بیناست.'),
(4608, 48, 25, 'هم آنان بودند که کافر شدند و راه مسجد الحرام را بر شما بستند و قربانی شما را از رسیدن به محل خود (مکّه) منع کردند. و اگر مردان مؤمن و زنان مؤمنه‌ای که شما اکنون نمی‌شناسید (در مکه) وجود نداشتند که اگر حمله کنید آنها را ندانسته پامال هلاک می‌سازید پس دیه و غرامت خون آن مؤمنان به گردن شما می‌ماند (اگر این قضیّه نبود به شما اجازه حمله به مکه داده می‌شد و لیکن این اجازه به تأخیر افتاد) تا خدا هر که را بخواهد در رحمت خود داخل گرداند (و به نور اسلام هدایت کند). اگر (عناصر کفر و ایمان) از یکدیگر جدا بودند همانا کسانی از آنان را که کافرند به عذابی دردناک معذّب می‌ساختیم.'),
(4609, 48, 26, 'آن گاه که کافران در دلها ناموس و حمیّت، آن هم حمیّت جاهلیّت پروردند (که نگذاشتند در عهدنامه صلح حدیبیّه کلمه بسم اللّه و رسول اللّه بنویسند و مؤمنان را بر سر حمیّت و غیرت ایمانی آوردند) پس خدا وقار و اطمینان خاطر بر رسول خود و بر مؤمنان نازل کرد و آنان را با کلمه (اخلاص و مقام) تقوا ملازم کرد که آنها سزاوارتر بر این مقام بودند و اهلیّت آن داشتند و خدا به همه امور عالم داناست.'),
(4610, 48, 27, 'البته خدا صدق و حقیقت خواب رسولش را آشکار و محقق ساخت که در عالم رؤیا دید شما مؤمنان به خواست خدا البته به مسجد الحرام با دل ایمن وارد شوید و سرها بتراشید و اعمال تقصیر بی ترس و هراس به جای آرید، و خدا آنچه را (از مصالح صلح حدیبیه) شما نمی‌دانستید می‌دانست و قبل از آن (که فتح مکه کنید) فتح نزدیک (حدیبیّه و خیبر) را مقرر داشت.'),
(4611, 48, 28, 'او خدایی است که رسول خود را با هدایت (قرآن) و دین حق فرستاد تا آن را بر همه ادیان دنیا غالب گرداند و (بر حقیقت این سخن) گواهی خدا کافی است.'),
(4612, 48, 29, 'محمد (صلی اللّه علیه و آله و سلم) فرستاده خداست و یاران و همراهانش بر کافران بسیار قویدل و سخت و با یکدیگر بسیار مشفق و مهربانند، آنان را در حال رکوع و سجود نماز بسیار بنگری که فضل و رحمت خدا و خشنودی او را می‌طلبند، بر رخسارشان از اثر سجده نشانه‌های نورانیّت پدیدار است. این وصف حال آنها در کتاب تورات و انجیل مکتوب است که (مثل حال آن رسول) به دانه‌ای ماند که چون نخست سر از خاک برآورد جوانه و شاخه‌ای نازک و ضعیف باشد بعد از آن قوّت یابد تا آنکه ستبر و قوی گردد و بر ساق خود راست و محکم بایستد که دهقانان را (در تماشای خود) حیران کند (همچنین محمد صلی اللّه علیه و آله و سلم و اصحابش از ضعف به قوّت رسند) تا کافران عالم را (از قدرت و قوّت خود) به خشم آرند. خدا وعده فرموده که هر کس از آنها ثابت ایمان و نیکوکار شود گناهانش ببخشد و اجر عظیم عطا کند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ای کسانی که ایمان آورده‌اید (در هیچ کار) بر خدا و رسول تقدّم مجویید و از خدا پروا کنید، که خدا شنوا و داناست.'),
(4614, 49, 2, 'ای اهل ایمان، فوق صوت پیغمبر صدا بلند مکنید و بر او فریاد بر مکشید چنانکه با یکدیگر بلند سخن می‌گویید، که اعمال نیکتان (در اثر بی ادبی) محو و باطل شود و شما فهم نکنید.'),
(4615, 49, 3, 'آنان که نزد رسول خدا به صدای آرام و آهسته سخن گویند آنها هستند که در حقیقت خدا دلهایشان را برای تقوا آزموده. آنهایند که آمرزش و اجر عظیم دارند.'),
(4616, 49, 4, 'به حقیقت مردمی که تو را از پشت حجره‌هایت به صدای بلند می‌خوانند اکثر مردم بی عقل و شعوری هستند.'),
(4617, 49, 5, 'و اگر آنها صبر می‌کردند تا وقتی که تو بر ایشان خارج شوی بسیار بر آنها بهتر بود و (باز هم اگر توبه کنند) خدا بسیار آمرزنده و مهربان است.'),
(4618, 49, 6, 'ای مؤمنان، هر گاه فاسقی خبری برای شما آورد تحقیق کنید، مبادا از روی نادانی به قومی رنجی رسانید و سخت از کار خود پشیمان گردید.'),
(4619, 49, 7, 'و بدانید رسول خدا در میان شماست، که اگر در بسیاری از امور رأی شما را پیروی کند خود به رنج و زحمت می‌افتید و لیکن خدا مقام ایمان را محبوب شما گردانید و در دلهاتان نیکو بیاراست و کفر و فسق و معصیت را زشت و منفور در نظرتان ساخت. اینان به حقیقت اهل صواب و هدایتند.'),
(4620, 49, 8, 'این مقام بر آنان به فضل خدا و نعمت الهی حاصل گردید و خدا (به احوال بندگان) دانا و (به صلاح نظام عالم) آگاه است.'),
(4621, 49, 9, 'و اگر دو طایفه از اهل ایمان با هم به قتال و دشمنی برخیزند البته شما مؤمنان بین آنها صلح برقرار دارید و اگر یک قوم بر دیگری ظلم کرد با آن طایفه ظالم قتال کنید تا به فرمان خدا باز آید، پس هر گاه به حکم حق برگشت با حفظ عدالت میان آنها را صلح دهید و همیشه (با هر دوست و دشمنی) عدالت کنید که خدا بسیار اهل عدل و داد را دوست می‌دارد.'),
(4622, 49, 10, 'به حقیقت مؤمنان همه برادر یکدیگرند پس همیشه بین برادران ایمانی خود صلح دهید و خداترس و پرهیزکار باشید، باشد که مورد لطف و رحمت الهی گردید.'),
(4623, 49, 11, 'ای اهل ایمان، مؤمنان هرگز نباید قومی قوم دیگر را مسخره و استهزا کنند، شاید آن قوم که مسخره می‌کنند از خود آنان بهتر باشند. و نیز بین زنان با ایمان قومی دیگری را سخریّه نکنند که بسا آن قوم از خود آن زنان بهتر باشند، و هرگز عیبجویی (از همدینان) خود مکنید و به نام و لقب‌های زشت یکدیگر را مخوانید که پس از ایمان به خدا نام فسق (بر مؤمن نهادن) بسیار زشت است و هر که توبه نکند چنین کسان بسیار ظالم و ستمکارند.'),
(4624, 49, 12, 'ای اهل ایمان، از بسیار پندارها در حق یکدیگر اجتناب کنید که برخی ظنّ و پندارها معصیت است و نیز هرگز (از حال درونی هم) تجسس مکنید و غیبت یکدیگر روا مدارید، هیچ یک از شما آیا دوست می‌دارد که گوشت برادر مرده خود را خورد؟ البته کراهت و نفرت از آن دارید (پس بدانید که مثل غیبت مؤمن به حقیقت همین است) و از خدا پروا کنید، که خدا بسیار توبه پذیر و مهربان است.'),
(4625, 49, 13, 'ای مردم، ما همه شما را نخست از مرد و زنی آفریدیم و آن گاه شعبه‌های بسیار و فرق مختلف گردانیدیم تا یکدیگر را بشناسید، همانا بزرگوار و با افتخارترین شما نزد خدا با تقواترین شمایند، همانا خدا کاملا دانا و آگاه است.'),
(4626, 49, 14, 'اعراب (بر تو منّت گذارده و) گفتند: ما (بی جنگ و نزاع) ایمان آوردیم، بگو: شما که ایمانتان (از زبان) به قلب وارد نشده به حقیقت هنوز ایمان نیاورده‌اید لیکن بگویید ما اسلام آوردیم (و از خوف جان به ناچار تسلیم شدیم)، و اگر خدا و رسول وی را اطاعت کنید او از (اجر) اعمال شما هیچ نخواهد کاست (و از گناه گذشته می‌گذرد) که خدا بسیار آمرزنده و مهربان است.'),
(4627, 49, 15, 'منحصرا مؤمنان واقعی آن کسانند که به خدا و رسول او ایمان آوردند و بعدا هیچ گاه شک و ریبی به دل راه ندادند و در راه خدا به مال و جانشان جهاد کردند. اینان به حقیقت راستگو هستند.'),
(4628, 49, 16, 'بگو: آیا شما می‌خواهید خدا را به دین خود آگاه سازید (که اظهار دیانت می‌کنید)؟ و حال آنکه خدا آنچه در آسمانها و زمین است همه را می‌داند و به کلّیّه امور عالم داناست.'),
(4629, 49, 17, 'آنها بر تو به مسلمان شدن منّت می‌گذارند، بگو: شما به اسلام خود بر من منّت منهید بلکه اگر راست می‌گویید (و ایمان حقیقی دارید) خدا بر شما منّت دارد که شما را به سوی ایمان هدایت فرموده است.'),
(4630, 49, 18, 'خدا اسرار غیب آسمانها و زمین را می‌داند و به آنچه شما بندگان می‌کنید بیناست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'ق (قسم به قدس و قدرت و) قسم به قرآن با مجد و عظمت.'),
(4632, 50, 2, '(که منکران ایمان نیاوردند) بلکه از آمدن رسولی از خودشان که آنها را هشدار و اندرز دهد به شگفت آمده و کفار (نادان) گفتند: این (دعوی رسالت و خبر از قیامت) بسیار چیز عجیبی است.'),
(4633, 50, 3, 'آیا ما پس از آنکه مردیم و یکسره خاک شدیم (باز زنده می‌شویم)؟ این بازگشت بسیار بعید است.'),
(4634, 50, 4, '(تعجب نکنند که) ما به آنچه زمین از آنها بکاهد کاملا آگاهیم و کتابی نگاهدارنده (لوح محفوظ که مشتمل بر همه حقایق عالم است) نزد ماست.'),
(4635, 50, 5, 'بلکه کافران چون (رسول) حق آمد او را تکذیب کردند و (در کار بزرگ رسالت و قرآن با عظمت) در حالی مضطرب و سرگردان ماندند (گاهی از جهل، افسانه پیشینیان شمردند و گاهی سحر و شعر پنداشتند).'),
(4636, 50, 6, 'پس مگر (منکران حق) آسمان را فراز خود نمی‌نگرند که ما چگونه بنای محکم اساس نهاده‌ایم و آن را به زیور (ستارگان رخشان) آراسته‌ایم و هیچ شکاف و خللی در آن راه ندارد؟'),
(4637, 50, 7, 'و زمین را نمی‌نگرند که آن را بگستردیم و در آن کوههای استوار بیفکندیم و هر نوع گیاه با حسن و طراوت از آن برویانیدیم؟'),
(4638, 50, 8, 'تا موجب بصیرتی باشد (در برهان الهیّت) و تذکری برای هر بنده‌ای که رو به درگاه خدا آرد.'),
(4639, 50, 9, 'و ما از آسمان رحمت آب باران با برکت را نازل کردیم و باغهای میوه و دانه‌های درو شدنی برویانیدیم.'),
(4640, 50, 10, 'و نیز نخلهای بلند خرما که میوه آن منظم روی هم چیده شده است برانگیختیم.'),
(4641, 50, 11, 'اینها را رزق بندگان قرار دادیم و به باران زمین مرده را زنده ساختیم (تا خلق بدانند که) همچنین پس از مرگ سر از خاک بیرون می‌کنند.'),
(4642, 50, 12, 'پیش از اینان هم قوم نوح و اصحاب رسّ (که پیغمبر خود را به چاه افکندند) و قوم ثمود هم تکذیب رسولان کردند.'),
(4643, 50, 13, 'و قوم عاد و فرعون و قوم لوط.'),
(4644, 50, 14, 'و اصحاب ایکه (امت شعیب) و قوم تبّع (پادشاه یمن) همه رسولان حق را تکذیب کردند تا وعده عذاب من بر آنان حتم و واجب گردید.'),
(4645, 50, 15, 'آیا در اول بار که آفرینش را آوردیم هیچ درماندیم؟ (پس در خلقت معاد دیگر بار هم عاجز نیستیم) بلکه این منکران در شک و ریب از خلقت نو (و نشأه معاد) اند.'),
(4646, 50, 16, 'و ما انسان را خلق کرده‌ایم و از وساوس و اندیشه‌های نفس او کاملا آگاهیم که ما از رگ گردن او به او نزدیکتریم.'),
(4647, 50, 17, 'آن گاه که دو ملک دریافت دارنده (یعنی فرشتگان مراقب و حاضر مأمور نگارش اعمال خیر و شر او) از طرف راست و چپ (به مراقبت او) بنشسته و (اعمال آدمی را) دریافت می‌دارند.'),
(4648, 50, 18, 'سخنی بر زبان نیاورد جز آنکه همان دم فرشته‌ای مراقب و آماده است.'),
(4649, 50, 19, 'و (به هوش آیید که) هنگام بیهوشی و سختی مرگ به حق و حقیقت فرا رسید، آری همان مرگی که از آن دوری می‌جستی.'),
(4650, 50, 20, 'و آن گاه در صور بدمند (و ملایک ندا کنند که) این است روز تهدید (های الهی به خلق).'),
(4651, 50, 21, 'و هر نفسی را فرشته‌ای (برای حساب به محشر) کشاند و فرشته‌ای (بر نیک و بدش) گواهی دهد.'),
(4652, 50, 22, 'و تو (ای آدمی نادان) از این (روز سخت مرگ) در غفلت بودی تا آنکه ما پرده از کار تو برانداختیم و امروز چشم بصیرتت بیناتر گردید.'),
(4653, 50, 23, 'و قرین وی (یعنی فرشته موکّل و شاهد او بدو) گوید: این همان اعمالی است که نزد من (برای امروز تو) محفوظ و مهیّاست.'),
(4654, 50, 24, '(و خطاب آید که) امروز هر کافر معاند را به دوزخ درافکنید.'),
(4655, 50, 25, 'همان کافری که از هر کار خیر مانع می‌گشت و (به حق خدا و خلق) ستم می‌کرد و (در کار خدا و قیامت) به شک و ریب بود.'),
(4656, 50, 26, 'همان کافر مشرک که با خدای یکتا خدای دیگری جعل کرد، پس او را در عذاب سخت جهنم درافکنید.'),
(4657, 50, 27, 'آن گاه قرین او (شیطان) گوید: بار الها، من او را به طغیان و عصیان نکشیدم بلکه او خود در ضلالت دور (از اطاعت و سعادت) افتاد.'),
(4658, 50, 28, '(آن گاه خدا به عتاب) گوید: در حضور من خصومت مکنید که من قبلا وعده عذاب را به شما گوشزد کردم.'),
(4659, 50, 29, 'دیگر وعده عذاب من مبدّل نخواهد شد و هیچ (در کیفر) ستمی به بندگان نخواهم کرد.'),
(4660, 50, 30, 'روزی که جهنّم را گوییم: آیا مملوّ (از وجود کافران) شدی؟ و او گوید: آیا (دوزخیان) بیش از این هم هستند؟'),
(4661, 50, 31, 'و بهشت را برای اهل تقوا نزدیک آرند تا هیچ دور از آن نباشند (و رنج حرکت نکشند).'),
(4662, 50, 32, 'این بهشت همان است که وعده داده شدید، برای هر بنده‌ای که به درگاه خدا بازگشته و نفس را (از حرام) نگه داشت.'),
(4663, 50, 33, 'آن کس که از خدای مهربان در باطن ترسید و با قلب خاشع و نالان به درگاه او باز آمد.'),
(4664, 50, 34, 'در آن بهشت با تحیّت و سلام حق در آیید که این روز دخول ابدی در بهشت جاودانی است.'),
(4665, 50, 35, 'بر آن بندگان در آنجا هر چه بخواهند مهیّاست و باز افزون‌تر از آن نزد ما خواهد بود.'),
(4666, 50, 36, 'و چقدر طوایفی را پیش از اینان ما هلاک کردیم که با قهر و قوّت‌تر از اینان بودند و در هر دیار راه جستند. آیا هیچ راه نجاتی یافتند؟'),
(4667, 50, 37, 'در این هلاک پیشینیان پند و تذکر است آن را که قلب هوشیاری داشته باشد یا گوش فرا دهد و توجه کامل کند و گواهی دهد.'),
(4668, 50, 38, 'و همانا ما آسمانها و زمین و آنچه بین آنهاست همه را در شش روز آفریدیم و هیچ رنج و خستگی به ما نرسید.'),
(4669, 50, 39, 'پس تو (ای رسول) بر آنچه می‌گویند (و می‌کنند از آزار و تکذیب) صبر کن (تا ما آنها را کیفر دهیم) و به حمد و ستایش خدایت تسبیح‌گو پیش از طلوع خورشید (به نماز صبح) و پیش از غروب آن (به نماز ظهر و عصر).'),
(4670, 50, 40, 'و باز برخی از شب را (به نماز مغرب و عشاء) و هم در عقب سجده‌ها به تسبیح خدا پرداز.'),
(4671, 50, 41, 'و به ندای منادی حق (اسرافیل) روزی که از مکان نزدیک (مردگان را برای زنده شدن) ندا کند گوش فرا دار (که هم اکنون به گوش دلت ندای او را بشنوی).'),
(4672, 50, 42, 'روزی که خلق آن صیحه به حق را بشنوند آن روز هنگام خروج (از قبرها) است.'),
(4673, 50, 43, 'البته ما خلق را زنده ساخته و می‌میرانیم و بازگشت همه به سوی ماست.'),
(4674, 50, 44, 'روزی که زمین از (احاطه بر) آنها بشکافد و همه (به عرصه قیامت) بشتابند و این جمع‌آوری خلق بر ما سهل و آسان باشد.'),
(4675, 50, 45, 'ما به گفتار مردم (مغرض نادان) داناتریم و تو بر آن مردم، جبّار و مسلّط نیستی (تو رسول حقّی) پس آن کس را که از وعده عذاب قیامت من ترسان است به آیات قرآن متذکر ساز.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'قسم به نفس بادهای عالم که (به امر حق بذرافشانی کنند و تخم نباتات را به هر جا) نیکو بپرا کنند.'),
(4677, 51, 2, 'قسم به ابرها که بار سنگین (باران را به امر حق) به دوش گیرند (تا به هر جا مأمورند فرو بارند).'),
(4678, 51, 3, 'قسم به کشتی‌ها که آسان (به روی آب) روان شوند.'),
(4679, 51, 4, 'قسم به فرشتگان که کار جهانیان را (به اذن خدا) قسمت کنند.'),
(4680, 51, 5, 'قسم به اینان که آنچه شما را وعده دادند همه صدق و حقیقت است.'),
(4681, 51, 6, 'و البته جزای اعمال، روزی واقع خواهد گردید.'),
(4682, 51, 7, 'قسم به آسمان که در آن راههای بسیار است.'),
(4683, 51, 8, 'که شما (مردم نادان) در اختلاف سخن باز ماندید (و حقیقت قرآن و رسول حق را نیافته شعر و سحر و ساحرش خواندید).'),
(4684, 51, 9, 'از این قرآن هر که را شایسته فهم آن نبود هم آخر روگردان کنند.'),
(4685, 51, 10, 'کشته باد و نابود آنان که دروغ بندند (و قرآن را سحر و فسانه برخوانند).'),
(4686, 51, 11, 'آنان که در ضلالت و غفلت زیستند.'),
(4687, 51, 12, 'باز می‌پرسند که پس این روز جزاکی خواهد بود؟'),
(4688, 51, 13, 'آن روز خواهد بود که آنها را به آتش بسوزانند و معذّب کنند.'),
(4689, 51, 14, '(و خطاب قهر کنند که) اینک بچشید کیفر خود را، این همان عذابی است که خود به تعجیل می‌خواستید.'),
(4690, 51, 15, '(آن روز) متقیان همه در بهشت و بر لب چشمه‌های آب غنوده‌اند.'),
(4691, 51, 16, 'همان نعمتها که خدایشان به آنها عطا کند فراگیرند چرا که پیش از آن مردمی نیکوکار بودند.'),
(4692, 51, 17, 'از شب اندکی را خواب می‌کردند.'),
(4693, 51, 18, 'و سحرگاهان از درگاه خدا طلب آمرزش و مغفرت می‌کردند.'),
(4694, 51, 19, 'و در اموالشان بر فقیر سائل و محروم حقّی منظور می‌داشتند.'),
(4695, 51, 20, 'و (بدانید که) در روی زمین برای اهل یقین ادله‌ای (از قدرت الهی) پدیدار است.'),
(4696, 51, 21, 'و هم در نفوس خود شما مردم، آیا نمی‌نگرید؟'),
(4697, 51, 22, 'و روزی شما با همه وعده‌ها که به شما می‌دهند در آسمان است.'),
(4698, 51, 23, 'پس به خدای آسمان و زمین قسم که این (وعده و رزق مقدّر) به مانند تکلّمی که با یکدیگر می‌کنید حقّ و حقیقت است.'),
(4699, 51, 24, 'آیا حکایت مهمانان گرامی ابراهیم (فرشتگان) به تو رسیده است؟'),
(4700, 51, 25, 'هنگامی که بر او وارد شدند و سلام گفتند و او جواب سلام گفت و فرمود که شما مردمی ناشناس می‌باشید.'),
(4701, 51, 26, 'آن‌گاه پنهانی نزد اهل بیت خود رفت و کباب گوساله فربهی نزد مهمانان باز آورد.'),
(4702, 51, 27, 'غذا را نزد آنها گذارد (آنها توجهی به غذا نکردند) ابراهیم گفت: شما غذا تناول نمی‌کنید؟'),
(4703, 51, 28, 'آن گاه (از اینکه بی‌اجازه بر او وارد شده و غذا هم نخوردند) سخت از آنها بیم و اندیشه کرد، آن فرشتگان گفتند: هیچ مترس، و او را به پسری دانا (یعنی اسحاق) بشارت دادند.'),
(4704, 51, 29, 'در آن حال زن ابراهیم (ساره) با فریاد شادمانی روی به آنها آورد و (از شوق) سیلی به صورت زد و گفت: (ای عجب) من زنی پیر و نازا هستم (چگونه پسری توانم زایید).'),
(4705, 51, 30, 'فرشتگان گفتند: خدای تو چنین فرموده که او به مصالح خلق آگاه و (به حال بندگان) داناست.'),
(4706, 51, 31, 'ابراهیم گفت: ای رسولان حق، باز گویید که شما برای چه کار مأمورید؟'),
(4707, 51, 32, 'گفتند: ما بر قوم بدکاری فرستاده شده‌ایم (یعنی قوم لوط).'),
(4708, 51, 33, 'تا بر سر آنها از گل سنگباران کنیم.'),
(4709, 51, 34, 'که آن سنگها نزد پروردگار تو معیّن و نشاندار برای ستمکاران است.'),
(4710, 51, 35, 'پس، از اهل ایمان هر که بود از آن دیار خارج کردیم.'),
(4711, 51, 36, 'و در همه آن دیار جز یک خانه (لوط) دیگر مسلم خداپرست نیافتیم.'),
(4712, 51, 37, 'و در آن دیار (که ویران کردیم) برای آنان که از عذاب دردناک (قهر خدا) می‌ترسند آیت عبرتی واگذاردیم.'),
(4713, 51, 38, 'و نیز در (رسالت) موسی (آیت عبرت است) که با معجزه روشن به سوی فرعونیانش فرستادیم.'),
(4714, 51, 39, 'و فرعون به غرور ملک و قدرت (از طاعت حق) سرکشید و گفت که موسی ساحر یا دیوانه است.'),
(4715, 51, 40, 'ما هم او را با همه سپاهش (به قهر) گرفتیم و به دریا انداختیم که در خور هر نکوهش و ملامت بود.'),
(4716, 51, 41, 'و نیز در قوم عاد که بر هلاکشان تندباد خزان فرستادیم (عبرت خلق است).'),
(4717, 51, 42, 'که آن باد هلاک به چیزی نمی‌گذشت جز آنکه آن را مانند استخوان پوسیده می‌گردانید.'),
(4718, 51, 43, 'و هم در قوم ثمود (بر خلق عبرتی است) که به آنان گفته شد: اینک گرم تعیّش و تمتّع (حیوانی) باشید تا هنگام معیّن (که وعده انتقام حق فرا می‌رسد).'),
(4719, 51, 44, 'آنها هم از فرمان خدای خود سرکشیدند پس آنها را صاعقه آتش درگرفت در حالی که (هلاک خویش را) به چشم مشاهده می‌کردند.'),
(4720, 51, 45, 'در حالی که نه هیچ توانایی برخاستن (و گریختن) داشتند و نه هیچ یار و مددکاری یافتند.'),
(4721, 51, 46, 'و پیش از این اقوام هم قوم نوح را هلاک کردیم که مردمی فاسق و نابکار بودند.'),
(4722, 51, 47, 'و کاخ رفیع آسمان را ما به قدرت خود برافراشتیم و ماییم که مقتدریم.'),
(4723, 51, 48, 'و زمین را بگستردیم و چه نیکو مهدی بگستردیم.'),
(4724, 51, 49, 'و از هر چیزی دو نوع (نر و ماده) بیافریدیم تا مگر متذکر (حکمت خدا) شوید.'),
(4725, 51, 50, 'باری، به درگاه خدا گریزید، که من از جانب او با بیانی روشن شما را می‌ترسانم.'),
(4726, 51, 51, 'و هرگز با خدای یکتا خدایی دیگر نپرستید که من از جانب او با بیانی روشن شما را می‌ترسانم.'),
(4727, 51, 52, 'همچنان (که تو را ای رسول تکذیب کردند) هیچ رسولی بر امم پیشین نیامد جز آنکه (او را تکذیب کرده و) گفتند: او ساحر یا دیوانه است.'),
(4728, 51, 53, 'آیا بر این تکذیب، مردم اعصار به یکدیگر سفارش کرده‌اند؟ (یا نه؟) بلکه این مردم (همه بالذّات) گروهی سرکش و نافرمانند.'),
(4729, 51, 54, 'پس (تو هم ای رسول ما) از این مردم کافر روی بگردان که (اتمام حجّت کردی و) دیگر هیچ در خور نکوهش و ملامت نیستی.'),
(4730, 51, 55, 'و (امّت را) تذکّر و پند می‌ده که پند و تذکّر (اگر کافران را نفع ندهد) مؤمنان را سودمند افتد.'),
(4731, 51, 56, 'و من جنّ و انس را نیافریدم مگر برای اینکه مرا (به یکتایی) پرستش کنند.'),
(4732, 51, 57, 'من از (خلق) آنها رزق و طعام (و هیچ گونه سودی) بر خود نخواستم.'),
(4733, 51, 58, 'همانا روزی بخشنده خلق تنها خداست که صاحب قوّت و قدرت ابدی است.'),
(4734, 51, 59, 'پس این ستمکاران را هم گناهی و کیفری مانند اصحاب و امثالشان (از امم پیشین) خواهد بود، باری آن را به عجله از من نخواهند (و به مهلتی که دادیم مغرور نشوند البتّه روز انتقامشان می‌رسد).'),
(4735, 51, 60, 'پس (آن گاه که زمان کیفر فرا رسید) وای بر آنان که کافر شدند از آن روز سختی که (رسولان) به آنها وعده می‌دهند (که در دنیا به قتل و اسارت و در عقبی به قهر و عقوبت محکومند).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'قسم به طور سینا.'),
(4737, 52, 2, 'قسم به کتاب مسطور (قرآن).'),
(4738, 52, 3, 'که در صحیفه‌ای گشوده فرستاد.'),
(4739, 52, 4, 'قسم به بیت المعمور (کعبه اهل زمین یا مسجد ملایک آسمان).'),
(4740, 52, 5, 'قسم به طاق بلند آسمان.'),
(4741, 52, 6, 'قسم به دریای آتش فروزان (که خدا روز قیامت دریا را آتش سازد و بر دوزخیان فرو ریزد).'),
(4742, 52, 7, 'قسم به این امور که البته عذاب خدای تو واقع خواهد شد.'),
(4743, 52, 8, 'هیچ کس دافع آن نخواهد بود.'),
(4744, 52, 9, 'روزی که آسمان سخت جنبش کند.'),
(4745, 52, 10, 'و کوهها تند به گردش آید.'),
(4746, 52, 11, 'در آن روز سخت وای بر آنان که (وعده حق و کتاب و رسول او را) تکذیب کردند.'),
(4747, 52, 12, 'آنان که به بازیچه دنیا فرو شدند.'),
(4748, 52, 13, 'آن روز آنها را سخت به آتش دوزخ برانند.'),
(4749, 52, 14, 'این همان آتشی است که تکذیب آن می‌کردید.'),
(4750, 52, 15, 'آیا این (آتش دوزخ) هم به نظرتان سحر است یا آنکه هنوز چشم بصیرت باز نمی‌کنید؟'),
(4751, 52, 16, 'باری، به دوزخ در شوید که صبر و بی‌صبری به حالتان یکسان است، و بس آنچه کردید امروز همان را جزا یابید.'),
(4752, 52, 17, 'متقیان هم در باغهای بهشت پر نعمتند.'),
(4753, 52, 18, 'آنان به نعمتی که خدایشان نصیبشان فرموده دلشادند و خدایشان از عذاب دوزخ محفوظشان داشته است.'),
(4754, 52, 19, 'بخورید و بیاشامید شما را گوارا باد به پاداش اعمال نیکی که انجام می‌دادید.'),
(4755, 52, 20, 'در حالی که نزدیک هم بر تختها (ی عزّت) تکیه زده‌اند و ما حور العین را همجفت آنان گردانیده‌ایم.'),
(4756, 52, 21, 'و آنان که ایمان آوردند و فرزندانشان هم در ایمان پیرو ایشان شدند ما آن فرزندان را به آنها برسانیم و از پاداش عمل آنان هیچ نکاهیم. هر نفسی در گرو عملی است که اندوخته است.'),
(4757, 52, 22, 'و بر آن بهشتیان از هر نوع میوه و گوشت‌های لذیذ که مایل باشند فراوان بیفزاییم.'),
(4758, 52, 23, 'آنها در بهشت جام شراب گوارا را چنان سریع از دست هم بگیرند که گویی منازعه می‌کنند در صورتی که آنجا کار لغو و باطل و نزاع و خلاف و بزهکاری هیچ نیست.'),
(4759, 52, 24, 'و پسرانی مانند لؤلؤ مکنون گردشان (بر انجام خدمت) می‌گردند.'),
(4760, 52, 25, 'و آنها با هم به صحبت رو کرده و از سرگذشت یکدیگر پرسش کنند.'),
(4761, 52, 26, 'و با هم گویند: ما از پیش میان اهل و قبیله خود خداترس بودیم.'),
(4762, 52, 27, 'خدا هم بر ما منّت نهاد و ما را از عذاب سموم دوزخ محفوظ داشت.'),
(4763, 52, 28, 'زیرا ما از این پیش خدا را به حقیقت پرستش می‌کردیم که او خود (بر بندگانش) بسیار نیکخواه و مهربان است.'),
(4764, 52, 29, 'پس (ای رسول ما، خلق را) متذکر ساز که به نعمت پروردگارت از جنون و کهانت هیچ در تو نیست.'),
(4765, 52, 30, 'بلکه (کافران نادان) گویند: (محمّد) شاعری است ماهر و ما حادثه مرگ او را انتظار داریم.'),
(4766, 52, 31, 'بگو: شما به انتظار (مرگ من) باشید که من هم از منتظران (مرگ و هلاک شما) هستم.'),
(4767, 52, 32, 'آیا زیرکی و خیالات باطلشان بر این وا می‌دارد (که تکذیب تو کنند) یا (از جهل و کبر و نخوت) خود قومی سرکش و نافرمانند؟'),
(4768, 52, 33, 'بلکه می‌گویند: او قرآن را از پیش خود فرابافته است (چنین نیست) بلکه آنها ایمان نمی‌آورند.'),
(4769, 52, 34, 'پس اگر راست می‌گویند آنها هم کلامی مانند قرآن بیاورند.'),
(4770, 52, 35, 'آیا این خلق (بدون خالق) از نیستی صرف به وجود آمدند؟ یا (خویشتن را) خود خلق کردند؟'),
(4771, 52, 36, 'یا آنکه آسمانها و زمین را این مردم آفریدند؟ نه، بلکه به یقین (خدا را) نشناختند.'),
(4772, 52, 37, 'آیا گنجهای رحمت پروردگارت نزد آنهاست یا هیچ قدرت و سلطنتی دارند؟'),
(4773, 52, 38, 'یا نردبامی دارند که به آسمان بر شوند و (سخن وحی فرشتگان) بشنوند؟ اگر چنین است شنونده‌شان حجت روشن بیاورد.'),
(4774, 52, 39, 'آیا خدا را دختران و شما را پسران خواهد بود (که ملایک را دختران خدا دانید).'),
(4775, 52, 40, 'آیا تو (ای رسول) از آنها اجر رسالت خواهی که زیر بار سنگین غرامت بمانند؟'),
(4776, 52, 41, 'آیا علم غیب با آنهاست که از آن عالم خبری نگارند؟'),
(4777, 52, 42, 'یا که می‌خواهند با تو مکر و تزویری اندیشند؟ کافران خود به مکر (و انتقام حق) گرفتارند.'),
(4778, 52, 43, 'آیا بر آنها خدایی جز خدای یکتا هست؟ که خدا از هر چه شرک و شریک او گیرند پاک و منزه است.'),
(4779, 52, 44, 'این کافران اگر هم سقوط قطعه‌ای از آسمان را به چشم ببینند باز خواهند گفت: این پاره ابری متراکم است.'),
(4780, 52, 45, 'اینان را به (جهل) خود رها کن تا به روز سختشان که آن روز یکسر بیهوش و هلاک می‌شوند عاقبت برسند.'),
(4781, 52, 46, 'آن روزی که مکر و تدبیرشان هیچ به کارشان نیاید و هیچ کس یاریشان نکند.'),
(4782, 52, 47, 'و برای ستمکاران عالم (در دنیا) عذابی پیش از آن (جهنم قیامت) خواهد بود ولیکن اکثرشان نمی‌دانند.'),
(4783, 52, 48, 'باری بر حکم خدا صبر کن که تو منظور نظر مایی. و چون برخیزی (به نماز یا هر کاری) به ستایش خدای خود تسبیح گوی.'),
(4784, 52, 49, 'و از شبانگاه هم پاره‌ای به تسبیح خدا پرداز و هنگام فرو رفتن ستارگان هم (به نماز صبح) تسبیح خدا گوی.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'قسم به ستاره چون فرود آید.'),
(4786, 53, 2, 'که صاحب شما (محمّد مصطفی ص) هیچ گاه در ضلالت و گمراهی نبوده است.'),
(4787, 53, 3, 'و هرگز به هوای نفس سخن نمی‌گوید.'),
(4788, 53, 4, 'سخن او هیچ غیر وحی خدا نیست.'),
(4789, 53, 5, 'او را (جبرئیل) همان (فرشته) بسیار توانا (به وحی خدا) علم آموخته است.'),
(4790, 53, 6, 'همان ملک مقتدری که به خلقت کامل (و صورت ملکوتی بر رسول) جلوه کرد.'),
(4791, 53, 7, 'و آن رسول در افق اعلا (ی کمال و مشرق انسانیت) بود.'),
(4792, 53, 8, 'آن گاه نزدیک آمد و بر او (به وحی حق) نازل گردید.'),
(4793, 53, 9, '(بدان نزدیکی که) با او به قدر دو کمان یا نزدیکتر از آن شد.'),
(4794, 53, 10, 'پس (خدا) به بنده خود وحی فرمود آنچه که هیچ کس درک آن نتواند کرد.'),
(4795, 53, 11, 'آنچه (در غیب عالم) دید دلش هم به حقیقت یافت و کذب و خیال نپنداشت.'),
(4796, 53, 12, 'آیا (شما کافران) با رسول بر آنچه (در شب معراج) به چشم مشاهده کرد ستیزه می‌کنید؟'),
(4797, 53, 13, 'و یکبار دیگر هم او را (یعنی جبرئیل را) رسول مشاهده کرد.'),
(4798, 53, 14, 'در نزد (مقام) سدرة المنتهی (که آن درختی است در سمت راست عرش که منتهای سیر عقلی فرشتگان و ارواح مؤمنان تا آنجاست و بر مقام بالاتر آگاه نیستند).'),
(4799, 53, 15, 'بهشتی که مسکن متقیان است در همان جایگاه سدره است.'),
(4800, 53, 16, 'چون سدره را می‌پوشاند (از نور عظمت حق) آنچه که احدی از آن آگه نیست.'),
(4801, 53, 17, 'چشم (محمّد ص از حقایق آن عالم) آنچه را باید بنگرد بی هیچ کم و بیش مشاهده کرد.'),
(4802, 53, 18, 'آنجا از بزرگتر آیات حیرت‌انگیز پروردگارش را به حقیقت دید.'),
(4803, 53, 19, 'آیا دو بت بزرگ لات و عزّای خود را دیدید (که بی اثر است).'),
(4804, 53, 20, 'و منات سومین بت دیگرتان را دانستید (که جمادی بی‌نفع و ضرر است).'),
(4805, 53, 21, 'آیا شما را فرزند پسر و خدا را دختر است؟'),
(4806, 53, 22, 'اگر چنین بودی باز هم تقسیمی ناروا و نادرست بودی.'),
(4807, 53, 23, 'این بتها جز نامهایی که شما و پدرانتان بر آنها نهاده‌اید چیز دیگری نیست و خدا هیچ دلیلی بر (معبودیت) آنها نازل نفرموده؛ مشرکان چیزی غیر گمان باطل و هوای نفس فاسد خود را پیروی نمی‌کنند با آنکه از جانب خدایشان هدایت بر آنها آمد.'),
(4808, 53, 24, 'آیا برای آدمی هر چه آرزو کند حاصل می‌شود؟'),
(4809, 53, 25, 'در صورتی که (انسان مالک هیچ نیست و) دنیا و آخرت همه ملک خداست.'),
(4810, 53, 26, 'و بسیار ملک در آسمانها هست که شفاعتشان سودمند نیست جز به امر خدا و بر آن کس که خدا بخواهد و از او خشنود باشد.'),
(4811, 53, 27, 'آنان که به آخرت ایمان ندارند فرشتگان را نام دختران خدا نهادند.'),
(4812, 53, 28, 'و حال آنکه هیچ علم به آن ندارند و جز در پی گمان و پندار نمی‌روند و ظنّ و گمان هم در فهم حق و حقیقت هیچ سودی ندارد.'),
(4813, 53, 29, 'پس از هر کسی که از یاد ما (و قرآن ما) رو گردانید و جز زندگانی دنیای فانی را نخواست به کلی اعراض کن.'),
(4814, 53, 30, 'منتهای علم و فهم این مردم تا همین حد است، خدا به حال آن که از راه حق گمراه شد و آن که هدایت یافت کاملا آگاه است.'),
(4815, 53, 31, 'و آنچه در آسمانها و زمین است همه ملک خداست (و حکم اعمال خلق با اوست) تا بدکاران را به کیفر رساند و نیکوکاران را پاداش نیکوتر عطا کند.'),
(4816, 53, 32, 'آنان که از گناهان بزرگ و اعمال زشت دوری کنند مگر آنکه کمی (یعنی گناه صغیره‌ای یا تخیّل و وسوسه‌ای به غلبه طبیعت و عادت) از آنها سر زند، که مغفرت پروردگارت بسیار وسیع است، او به حال شما آگاه‌تر است آن گاه که شما را از خاک زمین آفرید و هنگامی که در رحم مادرها جنین بودید، پس خودستایی مکنید، او به حال هر که متّقی (و در خور ستایش) است از شما داناتر است.'),
(4817, 53, 33, '(ای رسول) دیدی آن کس را که روی (از جنگ احد) بگردانید؟'),
(4818, 53, 34, 'و اندک صدقه‌ای داد سپس به کلّی قطع احسان کرد؟ (مفسّرین گفتند: آیات مربوط به عثمان است که شتری با بار به عبد اللّه سعد داد که در محشر بار گناه عثمان را به دوش گیرد).'),
(4819, 53, 35, 'آیا علم غیب نزد اوست و او (به احوال آن جهان) بیناست (که می‌داند رفیقش می‌تواند بار گناه او را به دوش گیرد).'),
(4820, 53, 36, 'یا که آگه نشد به آنچه (راجع به مجازات) در تورات موسی عمران است؟'),
(4821, 53, 37, 'و هم در صحف ابراهیم خلیل وفادار؟'),
(4822, 53, 38, 'که هیچ کس بار گناه دیگری را (در قیامت) به دوش نخواهد گرفت.'),
(4823, 53, 39, 'و (نمی‌داند) اینکه برای آدمی جز آنچه به سعی و عمل خود انجام داده (ثواب و جزایی) نخواهد بود؟'),
(4824, 53, 40, 'و اینکه البته پاداش سعی و عمل او را (در دنیا و برزخ) به وی بنمایند.'),
(4825, 53, 41, 'سپس (در آخرت) پاداش کامل آن را به او بدهند.'),
(4826, 53, 42, 'و اینکه کار خلق عالم به سوی خدا منتهی می‌شود.'),
(4827, 53, 43, 'و هم اوست که (بندگان را) شاد و خندان سازد و غمین و گریان گرداند.'),
(4828, 53, 44, 'و هم اوست که بمیراند و باز زنده فرماید.'),
(4829, 53, 45, 'و اوست که خلق را (برای انس با هم) جفت نر و ماده آفریده است.'),
(4830, 53, 46, 'از نطفه‌ای آفریده که (از مردان به رحم زنان) می‌ریزد.'),
(4831, 53, 47, 'و هم بر اوست که ایجاد نشأه آخرت کند.'),
(4832, 53, 48, 'و هم اوست که (بندگان را) بی‌نیاز کند و سرمایه (هر سعادت) بخشد.'),
(4833, 53, 49, 'و هم اوست آفریننده ستاره شعری (پس آفریننده را پرستید نه ستاره را).'),
(4834, 53, 50, 'و هم اوست که نخستین قوم عاد را هلاک ساخت.'),
(4835, 53, 51, 'و قوم ثمود را، و هیچ باقی نگذاشت.'),
(4836, 53, 52, 'و پیش از اینان قوم نوح را که ظالم و سرکش‌تر بودند هلاک گردانید.'),
(4837, 53, 53, 'و شهرهای قوم لوط را واژگون ساخت.'),
(4838, 53, 54, 'تا آنکه بر آنها عذابی بسیار سخت احاطه کرد.'),
(4839, 53, 55, 'پس (ای بشر) به کدام یک از نعمتهای پروردگارت جدل و انکار می‌کنی؟'),
(4840, 53, 56, 'این رسول هم مانند رسولان پیشین ترساننده خلق (از قهر خدا) است.'),
(4841, 53, 57, 'روز قیامت بسیار نزدیک شده است.'),
(4842, 53, 58, 'هیچ کس غیر خدا آن روز را آشکار نتواند ساخت.'),
(4843, 53, 59, 'آیا از این سخن تعجب می‌کنید،'),
(4844, 53, 60, 'و (به فسوس و مسخره بر آن) می‌خندید و (به روزگار سخت خود) نمی‌گریید،'),
(4845, 53, 61, 'و شما سخت غافلید (از این خواب غفلت برخیزید و).'),
(4846, 53, 62, 'بعد از این به سجده و عبادت خدا پردازید.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'آن ساعت (قیامت یا قیام رسول حق) نزدیک آمد و ماه آسمان شکافته شد (این آیه راجع به شقّ القمر است که کفّار از رسول اکرم درخواست کردند).'),
(4848, 54, 2, 'و اگر کافران، بزرگ آیت و معجزی (چون شکافتن ماه) هم ببینند باز اعراض کرده و گویند که این سحری نیرومند و کامل است.'),
(4849, 54, 3, 'و آنها (رسول حق را) تکذیب کرده و پیرو هوای نفس باطل خود شدند و هر (حق و باطل) امری را عاقبت (در بهشت یا دوزخ) مقرّی خواهد بود.'),
(4850, 54, 4, 'و اخباری که مایه عبرت و انزجار از معصیت است کاملا به مردم رسید.'),
(4851, 54, 5, '(قرآن) حکمت بالغه خداست و (اگر از آن پند نگیرید) دیگر از این پس هیچ اندرز و پند (شما را) سودی نخواهد بخشید.'),
(4852, 54, 6, 'پس روی از کافران بگردان (تا) روزی که ندا کننده‌ای (چون اسرافیل، خلق را) به عالمی حیرت آور و قیامتی هول‌انگیز دعوت کند.'),
(4853, 54, 7, 'که (از هول آن) کافران به خواری چشم بر هم نهند و سر از قبرها بر آورده مانند ملخ به عرصه محشر منتشر شوند.'),
(4854, 54, 8, 'در آن حال منادی محشر را ناچار به سرعت اجابت کنند، کافران با هم گویند: این روز همان روز سخت است (که ما انکار می‌کردیم).'),
(4855, 54, 9, 'پیش از اینان هم قوم نوح بنده ما نوح را تکذیب کردند و گفتند: او مردی دیوانه است، و (با زجر و ستم بسیار که از آنها کشید از دعوت به حق) بازداشته شد.'),
(4856, 54, 10, 'تا آنکه به درگاه خدایش دعا کرد که من سخت مغلوب قوم شده‌ام تو مرا یاری فرما.'),
(4857, 54, 11, 'ما هم درهای آسمان گشودیم و سیلابی از آسمان فرو ریختیم.'),
(4858, 54, 12, 'و در زمین چشمه‌ها جاری ساختیم تا آب آسمان و زمین با هم به طوفانی که مقدّر حتمی بود اجتماع یافت.'),
(4859, 54, 13, 'و نوح را در کشتی محکم اساس برنشاندیم.'),
(4860, 54, 14, 'که آن کشتی با نظر و حفظ و عنایت ما روان می‌گشت (و چنین کردیم) تا به نوح که مورد کفر و انکار قوم قرار گرفت پاداش دهیم.'),
(4861, 54, 15, 'و آن کشتی را محفوظ داشتیم تا آیت عبرت خلق شود، آیا کسی هست که از آن پند گیرد؟'),
(4862, 54, 16, 'پس (ای بدکاران عالم بنگرید که) سختی عذاب و تنبیه من چگونه بود!'),
(4863, 54, 17, 'و ما قرآن را برای وعظ و اندرز بر فهم آسان کردیم آیا کسی هست که از آن پند گیرد؟'),
(4864, 54, 18, 'قوم عاد نیز (پیغمبرشان هود را) تکذیب کردند، پس باز بنگرید که عذاب و تنبیه من چگونه سخت بود!'),
(4865, 54, 19, 'ما بر هلاک آنها تند بادی در روز نحسی پایدار فرستادیم.'),
(4866, 54, 20, 'که آن باد مردم را از جا بر می‌کند چنانکه ساق درخت خرما از ریشه افکنند.'),
(4867, 54, 21, 'پس باز بنگرید که عذاب و تنبیه من چگونه سخت بود!'),
(4868, 54, 22, 'و ما قرآن را برای وعظ و اندرز بر فهم آسان کردیم آیا کسی هست که از آن پند گیرد؟'),
(4869, 54, 23, 'قوم ثمود هم آیات حق را تکذیب کردند.'),
(4870, 54, 24, 'و گفتند: آیا سزد که ما یک بشری از جنس خودمان را پیروی کنیم؟ در این صورت به گمراهی و جنون سخت در افتاده‌ایم.'),
(4871, 54, 25, 'آیا بین ما افراد بشر تنها بر او وحی رسید؟! (چنین نیست) بلکه او مرد دروغگوی بی‌باک و خودپسندی است.'),
(4872, 54, 26, 'این منکران به زودی فردای قیامت کاملا معلومشان شود که دروغگوی بی‌باک و خودپسند کیست.'),
(4873, 54, 27, 'ما (به صالح گفتیم که) برای امتحان آنها ناقه را (به درخواست آنها از سنگ) بیرون می‌فرستیم و تو مراقب حالشان باش و (بر آزارشان) صبر پیشه گیر.'),
(4874, 54, 28, 'و به آنها خبر ده که نوشیدن آب چشمه بین آنها و ناقه تقسیم شده (که یک روز سهم ناقه و روزی برای آنهاست) و هر یک بر سهم خود حاضر آیند.'),
(4875, 54, 29, 'آن قوم شقی صاحب و رئیس خود را خواندند تا مجهز شد و ناقه را پی کرد.'),
(4876, 54, 30, 'پس (باز بنگرید که) عذاب و تنبیه من چگونه سخت بود!'),
(4877, 54, 31, 'ما بر هلاک آنها یک صیحه (عذاب آسمانی) فرستادیم و همه مانند گیاه خشک و خرد شدند.'),
(4878, 54, 32, 'و ما قرآن را برای وعظ و اندرز بر فهم آسان کردیم آیا کسی هست که از آن پند گیرد؟'),
(4879, 54, 33, 'قوم لوط نیز آیات و رسول حق را تکذیب کردند.'),
(4880, 54, 34, 'ما هم جز خانواده لوط که به وقت سحر نجاتشان دادیم همه آنان را به سنگباران هلاک نابود کردیم.'),
(4881, 54, 35, 'نجات اهل بیت لوط هم به نعمت و لطف ما بود، بلی ما چنین شکرگزاران را پاداش می‌دهیم.'),
(4882, 54, 36, 'لوط آن قوم را از قهر و مؤاخذه ما ترسانید باز آنها در آیات ما شک و انکار کردند.'),
(4883, 54, 37, 'و قوم از او مهمانانش را (که فرشتگانی زیبا بودند، بر سوء قصد) طلبیدند، ما هم چشم آنها را بی‌نور و نابینا کردیم (و گفتیم که) اینک عذاب قهر و انتقام مرا بچشید.'),
(4884, 54, 38, 'و شبی به صبحگاه رسید که (مرگ آمد و) عذاب دایم (دوزخ) گریبانشان گرفت.'),
(4885, 54, 39, '(و گفتیم) اینک عذاب قهر و انتقام مرا بچشید.'),
(4886, 54, 40, 'و ما قرآن را برای وعظ و اندرز بر فهم آسان کردیم آیا کسی هست که از آن پند گیرد؟'),
(4887, 54, 41, 'و بر ارشاد فرعونیان هم رسول و آیات الهی آمد.'),
(4888, 54, 42, 'آنها همه آیات ما را تکذیب کردند، ما هم به قهر و اقتدار خود از آنها سخت مؤاخذه کردیم.'),
(4889, 54, 43, 'آیا کفار شما (مکّیان) بهتر (و قوی‌تر) از آن امم گذشته‌اند یا شما را برائت و امانی در کتب (آسمانی از عذاب خدا) هست؟'),
(4890, 54, 44, 'بلکه می‌گویند: ما با هم مجتمع و بسیاریم و پیروزیم.'),
(4891, 54, 45, 'به زودی آن جماعت (در جنگ بدر) شکست خورده و به جنگ پشت کنند.'),
(4892, 54, 46, 'بلکه ساعت قیامت وعده‌گاه آنهاست و آن روزی بسیار سخت‌تر و ناگوارتر (از روز بدر) است.'),
(4893, 54, 47, 'البته بدکاران عالم (در دنیا) به گمراهی و (در آخرت) در آتش سوزان دوزخند.'),
(4894, 54, 48, 'روزی که آنها را به رو در آتش دوزخ کشند (و گویند) اینک الم جهنم را بچشید!'),
(4895, 54, 49, 'ما هر چه آفریدیم به اندازه آفریدیم.'),
(4896, 54, 50, 'و فرمان ما (در همه عالم) یکی است (و هیچ تبدیل‌پذیر نیست چه در امر ایجاد و چه در کار معاد، و در سرعت) به مانند چشم به هم زدنی انجام یابد.'),
(4897, 54, 51, 'و ما بسیاری از پیشینیان امثال شما را هلاک کردیم آیا کسی هست که از آن پند و عبرت گیرد؟'),
(4898, 54, 52, 'و هر عملی که کردند در کتب‌نامه عملشان ثبت است.'),
(4899, 54, 53, 'و هر امر کوچک و بزرگ (در آنجا) نگاشته است.'),
(4900, 54, 54, 'محققا اهل تقوا در باغها و کنار نهرها (ی بهشت ابد) منزل گزینند.'),
(4901, 54, 55, 'در منزلگاه صدق و حقیقت نزد خداوند عزّت و سلطنت جاودانی متنعّمند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'خدای بخشنده،'),
(4903, 55, 2, '(به رسولش) قرآن آموخت.'),
(4904, 55, 3, 'انسان را خلق کرد.'),
(4905, 55, 4, 'به او تعلیم نطق و بیان فرمود.'),
(4906, 55, 5, 'خورشید و ماه به حساب معیّن به گردشند.'),
(4907, 55, 6, 'و گیاهان و درختان هم به سجده او سر به خاک اطاعت نهاده‌اند.'),
(4908, 55, 7, 'و آسمان را او کاخی رفیع گردانید و میزان (عدل و نظم) را در عالم وضع فرمود.'),
(4909, 55, 8, '(و حکم کرد) که هرگز در میزان (عدل و احکام شرع من) تعدّی و نافرمانی مکنید.'),
(4910, 55, 9, 'و هر چیز را به ترازوی عدل و انصاف بسنجید و هیچ در میزان کم فروشی و نادرستی مکنید.'),
(4911, 55, 10, 'و زمین را (با هزاران نعمت) برای (زندگانی) خلق مقرّر فرمود.'),
(4912, 55, 11, 'که در آن میوه‌های گوناگون و نخل خرمای با پوشش برگ و شکوفه و غلاف است.'),
(4913, 55, 12, 'و هم حبوبات متنوع که دارای برگ و گل است و نیز ریاحین است.'),
(4914, 55, 13, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4915, 55, 14, 'انسان را خدا از خشک گلی مانند گل کوزه گران (بدین حسن و زیبایی) آفرید.'),
(4916, 55, 15, 'و جنّیان را از رخشنده شعله آتش خلق کرد.'),
(4917, 55, 16, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4918, 55, 17, 'آن خدایی که آفریننده دو مشرق و دو مغرب است (یکی مشرق و مغرب تابستان مطلع سرطان، و یکی زمستان مطلع جدی).'),
(4919, 55, 18, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4920, 55, 19, 'اوست که دو دریا (ی آب شور و گوارا) را به هم در آمیخت تا به هم برخورد کنند.'),
(4921, 55, 20, 'و میان آن دو دریا برزخ و فاصله‌ای است که تجاوز به حدود یکدیگر نمی‌کنند.'),
(4922, 55, 21, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4923, 55, 22, 'از آن دو دریا لؤلؤ و مرجان گرانبها بیرون آید.'),
(4924, 55, 23, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4925, 55, 24, 'و او راست کشتیهای بزرگ بادبان برافراشته مانند کوه که به دریا در گردشند.'),
(4926, 55, 25, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4927, 55, 26, 'هر که روی زمین است دستخوش مرگ و فناست.'),
(4928, 55, 27, 'و زنده ابدی ذات خدای با جلال و عظمت توست.'),
(4929, 55, 28, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4930, 55, 29, 'هر که در آسمانها و زمین است همه از او (حوائج خود را) می‌طلبند و او هر روز به شأن و کاری (در تکمیل و افاضه به خلق) پردازد.'),
(4931, 55, 30, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4932, 55, 31, 'ای گروه انس و جنّ به زودی به حساب کار شما هم خواهیم پرداخت.'),
(4933, 55, 32, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4934, 55, 33, 'ای گروه جنّ و انس، اگر می‌توانید از اطراف آسمانها و زمین (و از قبضه قدرت الهی) بیرون شوید، بیرون شوید (ولی این خیال محالی است زیرا) هرگز خارج از ملک و سلطنت خدا نتوانید شد.'),
(4935, 55, 34, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4936, 55, 35, '(اگر به کفر و طغیان گرایید) بر شما شراره‌های آتش و مس گداخته فروریخته شود و هیچ نصرت و نجاتی نیابید.'),
(4937, 55, 36, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4938, 55, 37, 'پس آن‌گاه که آسمان شکافته شود تا چون گل سرخ گون و چون روغن مذاب و روان گردد (آن روز سخت هولناک از گنه پشیمان شوید).'),
(4939, 55, 38, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4940, 55, 39, 'پس در آن روز هیچ از گناه انس و جنّ باز نپرسند (چون خدا و فرشتگان از گناهان خلق آگاهند).'),
(4941, 55, 40, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4942, 55, 41, '(آن روز) بدکاران به سیمایشان شناخته شوند، پس موی پیشانی آنها را با پاهایشان بگیرند (و در آتش دوزخ افکنند).'),
(4943, 55, 42, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4944, 55, 43, 'این همان دوزخی است که بدکاران تکذیب می‌کردند.'),
(4945, 55, 44, 'اینک میان آن جهنّم و در حمیم سوزان آن می‌گردند.'),
(4946, 55, 45, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4947, 55, 46, 'و هر که از مقام (قهر و کبریایی) خدایش بترسد او را دو باغ بهشت خواهد بود.'),
(4948, 55, 47, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4949, 55, 48, 'در آن دو بهشت انواع گوناگون میوه‌ها و نعمتهاست.'),
(4950, 55, 49, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4951, 55, 50, 'در آن دو بهشت دو چشمه آب (تسنیم و سلسبیل) روان است.'),
(4952, 55, 51, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4953, 55, 52, 'در آن دو بهشت از هر میوه‌ای دو نوع است.'),
(4954, 55, 53, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4955, 55, 54, 'در حالتی که بهشتیان بر بسترهایی که آستر آنها از حریر و استبرق است (در کمال عزّت) تکیه زده‌اند و میوه درختانش در همان تکیه گاه در دسترس آنهاست.'),
(4956, 55, 55, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4957, 55, 56, 'در آن بهشتها زنان زیبای با حیائی است (که به چشم پر ناز جز به شوهر خود ننگرند) و دست هیچ کس از جن و انس پیش از آنها بدان زنان نرسیده است.'),
(4958, 55, 57, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4959, 55, 58, 'آن زنان حور العین (در صفا و لطافت) گویی یاقوت و مرجانند.'),
(4960, 55, 59, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4961, 55, 60, 'آیا پاداش نکویی و احسان جز نکویی و احسان است؟'),
(4962, 55, 61, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4963, 55, 62, 'و ورای آن دو بهشت دو بهشت دیگر است.'),
(4964, 55, 63, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4965, 55, 64, 'که درختان آن دو بهشت در منتهای سبزی و خرّمی است.'),
(4966, 55, 65, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4967, 55, 66, 'در آن دو بهشت دیگر هم دو چشمه آب گوارا می‌جوشد.'),
(4968, 55, 67, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4969, 55, 68, 'در آن دو بهشت نیز هرگونه میوه خوش و خرما و انار بسیار است.'),
(4970, 55, 69, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4971, 55, 70, 'در آن بهشتها نیکو زنان با حسن و جمال بسیارند.'),
(4972, 55, 71, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4973, 55, 72, 'حورانی در سراپرده‌های خود (مستور از چشم بیگانگان).'),
(4974, 55, 73, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4975, 55, 74, 'که پیش از شوهران دست هیچ کس از جن و انس بدان زنان نرسیده است.'),
(4976, 55, 75, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4977, 55, 76, 'در حالتی که بهشتیان (با حور العین) بر رفرف سبز (وجد و عزّت) و بساط زیبا (ی فخر و دولت) تکیه زده‌اند.'),
(4978, 55, 77, '(الا ای جنّ و انس) کدامین نعمتهای خدایتان را انکار می‌کنید؟'),
(4979, 55, 78, 'بزرگوار و مبارک نام پروردگار توست که خداوند جلال و عزّت و احسان و کرامت است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'هنگامی که آن واقعه بزرگ (قیامت) واقع می‌گردد.'),
(4981, 56, 2, 'که در وقوعش هیچ کذب و جای هیچ شک و ریب نیست.'),
(4982, 56, 3, 'آن روز (قومی را به دوزخ) خوار و ذلیل کند و (طایفه‌ای را به جنّت) سربلند و رفیع گرداند.'),
(4983, 56, 4, 'آن‌گاه که زمین شدید به حرکت و لرزه در آید.'),
(4984, 56, 5, 'و کوههای سخت متلاشی شوند.'),
(4985, 56, 6, 'و مانند ذرّات گرد در هوا پراکنده گردند.'),
(4986, 56, 7, 'و شما خلایق بر سه دسته مختلف شوید.'),
(4987, 56, 8, 'گروهی راستان، اصحاب یمین باشند که چقدر حالشان (در بهشت ابد) نیکوست!'),
(4988, 56, 9, 'و گروهی ناراستان، اصحاب شومی و شقاوتند که چقدر روزگارشان (در دوزخ) سخت است.'),
(4989, 56, 10, 'و (طایفه سوم) آنان که (مشتاقانه در ایمان) بر همه پیشی گرفتند و (در اطاعت خدا و رسول) مقام تقدم یافتند.'),
(4990, 56, 11, 'آنان به حقیقت مقربان درگاهند.'),
(4991, 56, 12, 'آنان در بهشت پرنعمت جاودانی متنعّمند.'),
(4992, 56, 13, 'آنها جمعی بسیار از امم پیشینیان هستند.'),
(4993, 56, 14, 'و عدّه قلیلی از متأخّران (یا بسیاری از مقدّمان امّت محمّد ص و قلیلی از مردم آخر زمان این امّت باشند).'),
(4994, 56, 15, 'آنان بر سریرهای زربفت مرصّع به انواع جواهر تکیه زنند.'),
(4995, 56, 16, 'همه (شادان) با یاران و دوستان رو به روی یکدیگر بر آن سریرهای عزّت می‌نشینند.'),
(4996, 56, 17, 'و پسرانی زیبا که حسن و جوانیشان همیشگی و ابدی است گرد آنها به خدمت می‌گردند.'),
(4997, 56, 18, 'با کوزه‌ها (ی بلورین) و مشربه‌ها (ی زرّین) و جامهای پر از شراب ناب.'),
(4998, 56, 19, 'نه هرگز از آنها (هر چه نوشند) دردسری یابند و نه مستی عقل و رنج خمار کشند.'),
(4999, 56, 20, 'و میوه خوش از هر چه برگزینند،'),
(5000, 56, 21, 'و گوشت مرغان و هر غذا که مایل باشند،'),
(5001, 56, 22, 'و زنان سیه چشم زیبا صورت،'),
(5002, 56, 23, 'که (در بهاء و لطافت) چون درّ و لؤلؤ مکنونند (بر آنها مهیّاست).'),
(5003, 56, 24, '(این نعمتهای الهی) پاداش اعمال نیک آن بهشتیان است.'),
(5004, 56, 25, 'نه آنجا هیچ حرفی لغو و بیهوده شنوند و نه به یکدیگر گناهی بربندند.'),
(5005, 56, 26, 'هیچ جز سلام و تحیّت و احترام هم نگویند و نشنوند.'),
(5006, 56, 27, 'و اصحاب یمین هم چه خوش روزگارند!'),
(5007, 56, 28, 'در سایه درختان سدر پرمیوه بی‌خار.'),
(5008, 56, 29, 'و درختان پربرگ سایه‌دار.'),
(5009, 56, 30, 'و در سایه بلند درختان.'),
(5010, 56, 31, 'و در طرف نهر آبهای روان زلال.'),
(5011, 56, 32, 'و میوه‌های بسیار.'),
(5012, 56, 33, 'که هیچ وقت منقطع نشود و هیچ کس بهشتیان را از آن میوه‌ها منع نکند.'),
(5013, 56, 34, 'و فرشهای پربها (یا فراش و زنان زیبا).'),
(5014, 56, 35, 'که آنها را ما در کمال حسن و زیبایی بیافریده‌ایم.'),
(5015, 56, 36, 'و همیشه آن زنان را با کره گردانیده‌ایم.'),
(5016, 56, 37, 'و شوهر دوست و با غنج و ناز و جوان و همسالان دلنواز.'),
(5017, 56, 38, 'این نعمتهای بهشتی مخصوص اصحاب یمین است.'),
(5018, 56, 39, 'که جمعی از پیشینیان،'),
(5019, 56, 40, 'و جمعی از امت رسول آخر زمان هستند.'),
(5020, 56, 41, 'و اما اصحاب شومی و شقاوت (که نامه عملشان به دست چپ است) چقدر روزگارشان سخت است!'),
(5021, 56, 42, 'آنها در عذاب باد سموم و آب گرم باشند.'),
(5022, 56, 43, 'و سایه‌ای از دود آتش دوزخ.'),
(5023, 56, 44, 'که نه هرگز سرد شود و نه خوش نسیم گردد.'),
(5024, 56, 45, 'این عذاب آنها را بدین سبب است که از این پیش به ناز و نعمت پرداختند.'),
(5025, 56, 46, 'و بر گناه بزرگ (شرک و عناد) لجاجت و اصرار داشتند.'),
(5026, 56, 47, 'و دایم می‌گفتند: آیا ما چون مردیم و خاک و استخوان پوسیده شدیم باز هم ما زنده می‌شویم؟'),
(5027, 56, 48, 'و آیا پدران گذشته ما زنده خواهند شد؟'),
(5028, 56, 49, 'بگو: البته تمام خلق اولین و آخرین،'),
(5029, 56, 50, 'همه در وعده گاه روز معیّن محشر گرد آورده می‌شوند.'),
(5030, 56, 51, 'آن گاه شما ای گمراهان منکر (قیامت).'),
(5031, 56, 52, 'از درخت زقّوم تلخ دوزخ البته خواهید خورد.'),
(5032, 56, 53, 'تا آنکه شکم را از آن پر می‌سازید.'),
(5033, 56, 54, 'آن‌گاه همه از آب گرم جهنم بر روی آن می‌آشامند.'),
(5034, 56, 55, 'بدان‌سان از عطش، آن آب را می‌نوشید که شتران تشنه آب می‌آشامند.'),
(5035, 56, 56, 'این است طعام و شراب کافران در روز جزا.'),
(5036, 56, 57, 'ما شما را بیافریدیم پس چرا تصدیق نمی‌کنید؟'),
(5037, 56, 58, 'آیا ندیدید (و به حقیقت در نیافتید) که نخست شما نطفه‌ای (بی‌قدر و قابلیّت) بودید؟'),
(5038, 56, 59, 'آیا شما خود آن نطفه را (به صورت فرزند انسان) می‌آفرینید یا ما آفریننده‌ایم؟'),
(5039, 56, 60, 'ما مرگ را بر همه شما مقدّر ساختیم و هیچ کس بر قدرت ما سبق نتواند برد.'),
(5040, 56, 61, 'در اینکه شما را فانی کرده و خلقی دیگر مثل شما بیافرینیم و شما را به صورتی (در جهانی دیگر) که اکنون از آن بی‌خبرید برانگیزیم.'),
(5041, 56, 62, 'و بی‌شک شما از نشأه اوّل خود آگاه شدید (که از عدم به وجودتان آوردیم) پس چرا متذکر (عالم آخرت) نمی‌شوید؟'),
(5042, 56, 63, 'آیا دیدید تخمی را که در زمین می‌کارید؟'),
(5043, 56, 64, 'آیا شما آن تخم را می‌رویانید یا ما رویاننده‌ایم؟'),
(5044, 56, 65, 'اگر ما بخواهیم کشت و زرع شما را خشک و تباه می‌سازیم تا با حسرت و ندامت به سخنان بیهوده پردازید.'),
(5045, 56, 66, '(و گویید) که ما سخت در زیان و غرامت افتادیم.'),
(5046, 56, 67, 'بلکه به کلی محروم گردیدیم.'),
(5047, 56, 68, 'آیا آبی را که شما می‌نوشید متوجهید؟'),
(5048, 56, 69, 'آیا شما آن آب را از ابر فرو ریختید یا ما نازل ساختیم؟'),
(5049, 56, 70, 'اگر می‌خواستیم آن آب را شور و تلخ می‌گردانیدیم، پس چرا شکرگزاری نمی‌کنید؟!'),
(5050, 56, 71, 'آیا آتشی که روشن می‌کنید می‌نگرید؟'),
(5051, 56, 72, 'آیا شما درخت آن را آفریدید یا ما آفریدیم؟'),
(5052, 56, 73, 'ما آن را مایه پند و عبرت و توشه مسافران (کوه و بیابان عالم) گردانیدیم.'),
(5053, 56, 74, 'پس (ای رسول) به نام بزرگ خدای خود تسبیح گو.'),
(5054, 56, 75, 'سوگند به مواقع نزول ستارگان (یا آیات کریمه قرآن).'),
(5055, 56, 76, 'و این سوگند اگر بدانید بسی سوگند بزرگی است.'),
(5056, 56, 77, 'که این قرآن کتابی بسیار بزرگوار و سودمند و گرامی است.'),
(5057, 56, 78, 'که در لوح محفوظ سرّ حق مقام دارد.'),
(5058, 56, 79, 'که جز دست پاکان (و فهم خاصّان) بدان نرسد.'),
(5059, 56, 80, 'تنزیلی از پروردگار عالم است.'),
(5060, 56, 81, 'آیا با این سخن (آسمانی) باز انکار و نفاق می‌ورزید؟'),
(5061, 56, 82, 'و بهره خود را تکذیب آن قرار می‌دهید؟'),
(5062, 56, 83, 'پس چرا هنگامی که جان کسی به گلو رسد.'),
(5063, 56, 84, 'و شما وقت مرگ (بر بالین آن مرده حاضرید و او را) می‌نگرید.'),
(5064, 56, 85, 'و ما به او از شما نزدیکتریم لیکن شما بصیرت ندارید.'),
(5065, 56, 86, 'پس چرا اگر حیات به دست شما و طبیعت است و شما را آفریننده‌ای نیست.'),
(5066, 56, 87, 'روح را دوباره به بدن مرده باز نمی‌گردانید اگر راست می‌گویید؟'),
(5067, 56, 88, 'پس (بدانید آن که بمیرد) اگر از مقربان درگاه خداست.'),
(5068, 56, 89, 'آنجا در آسایش و نعمت و بهشت ابدی است.'),
(5069, 56, 90, 'و اگر از اصحاب یمین است.'),
(5070, 56, 91, 'پس (وی را بشارت دهید که) تو را (از هر رنج و درد و الم) ایمنی و سلامت است.'),
(5071, 56, 92, 'و اما اگر از منکران و گمراهان است.'),
(5072, 56, 93, 'نصیبش حمیم جهنم است.'),
(5073, 56, 94, 'و جایگاهش آتش دوزخ است.'),
(5074, 56, 95, 'این (وعد و وعید) البته یقین و حق و حقیقت است.'),
(5075, 56, 96, 'پس به نام بزرگ خدای خود تسبیح گوی.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'هر چه در آسمانها و زمین است همه به تسبیح و ستایش یکتا خدایی که مقتدر و حکیم است مشغولند.'),
(5077, 57, 2, 'آن خدایی که فرمانروایی آسمانها و زمین از آن اوست، او زنده می‌گرداند و باز می‌میراند و اوست که بر همه چیز تواناست.'),
(5078, 57, 3, 'اوّل و آخر هستی و پیدا و پنهان وجود همه اوست و او به همه امور عالم داناست.'),
(5079, 57, 4, 'اوست خدایی که آسمانها و زمین را در شش روز (و شش مرتبه کلی ظهور وجود) بیافرید آن‌گاه بر عرش (تدبیر عالم) قرار گرفت، او هر چه در زمین فرو رود و هر چه از آن برآید و آنچه از آسمان نازل شود و آنچه به آن بالا رود همه را می‌داند و هر کجا باشید او با شماست و خدا به هر چه کنید بیناست.'),
(5080, 57, 5, 'آسمانها و زمین همه ملک اوست و رجوع تمام امور عالم به سوی خداست.'),
(5081, 57, 6, 'شب را در (پرده زرین) روز نهان کند و روز را در (خیمه سیاه) شب پنهان سازد و به اسرار دلهای خلق هم او آگاه است.'),
(5082, 57, 7, '(الا ای بندگان) به خدا و رسول او ایمان آرید و از آنچه شما را در آن وارث گذشتگان گردانید (به راه خدا) انفاق کنید، پس بر آنان که از شما ایمان آوردند و انفاق کردند پاداش بزرگ (بهشت ابد) خواهد بود.'),
(5083, 57, 8, 'و برای چه شما به خدا ایمان نیاورید در صورتی که رسول حق شما را (به آیات و معجزات) به راه ایمان به پروردگارتان دعوت می‌کند و خدا هم (در فطرت و عقل) از شما پیمان ایمان گرفته است اگر (قابلیت) ایمان دارید.'),
(5084, 57, 9, 'اوست خدایی که بر بنده خود (محمّد مصطفی صلّی اللّه علیه و آله) آیات قرآن روشن بیان را نازل می‌کند تا شما را از ظلمات (جهل و عصیان) بیرون آرد و به نور (علم و ایمان) رهبری کند و خدا بسیار در حق شما مشفق و مهربان است.'),
(5085, 57, 10, 'و برای چه در راه خدا انفاق نکنید در صورتی که (شما با ملک و مالتان روز مرگ فانی شوید و) وارث تمام (اهل) آسمانها و زمین خداست؟! آن مسلمانانی که پیش از فتح (مکه زمان ضعف اسلام) در راه دین انفاق و جهاد کردند با دیگران مساوی نیستند، آنها اجر و مقامشان بسیار عظیم‌تر از کسانی است که بعد از فتح (و قدرت اسلام) انفاق و جهاد کردند و لیکن خدا به همه وعده نیکوترین پاداش را (که بهشت ابد است) داده و خدا به هر چه کنید آگاه است.'),
(5086, 57, 11, 'آن کیست که به خدا قرض نیکو دهد (یعنی قرض الحسنه و صدقه دهد و احسان به فقیران کند) تا خدا بر او چندین برابر گرداند و او را پاداشی با لطف و کرامت باشد؟'),
(5087, 57, 12, '(ای رسول ما یاد آر) روزی که مردان و زنان مؤمن شعشعه نور ایمانشان پیش رو و سمت راست آنها بشتابد (و آنان را مژده دهند که) امروز شما را به بهشتی که نهرها زیر درختانش جاری است بشارت باد که در آن بهشت جاودان خواهید بود. این همان سعادت و فیروزی بزرگ است.'),
(5088, 57, 13, 'و باز آن روز مردان و زنان منافق به اهل ایمان گویند: مشتابید و به ما فرصتی دهید تا ما هم از نور شما روشنایی برگیریم. در پاسخ به آنها گویند: واپس گردید (یعنی اگر می‌توانید به دنیا بازگردید) و از آنجا نور طلبید. در این گفتگو باشند که بین آن دوزخیان با اهل بهشت حصاری حایل گردد و بر آن حصار دری باشد که باطن و درون آن در، بهشت رحمت است و از جانب ظاهر آن عذاب جهنم خواهد بود.'),
(5089, 57, 14, 'باز منافقان (بهشتیان را) ندا کنند که آخر نه ما هم با شما بودیم؟ آنها جواب دهند: بلی راست می‌گویید لیکن شما (به نفاق با خدا) خود را به فتنه افکندید و انتظار (هلاکت مؤمنان) داشتید و (در کار دین) به شک و ریب بودید و آرزوهای دنیا فریبتان داد تا وقتی که امر خدا (یعنی مرگ) فرا رسید و شیطان فریبنده شما را غافل و مغرور گردانید.'),
(5090, 57, 15, 'پس امروز (بر نجات هیچ یک) از شما منافقان و کافران فدیه و عوض نپذیرند، همه منزلگاهتان آتش دوزخ است و آن آتش شما را سزاوارتر است و بسیار به بد منزلی باز می‌گردید.'),
(5091, 57, 16, 'آیا نوبت آن نرسید که گرویدگان (ظاهری از باطن به خدا بگروند و) دلهاشان به یاد خدا خاشع گردد و به آنچه از حق نازل شد بذل توجه کنند و مانند کسانی که پیش از این برایشان کتاب آسمانی آمد (یعنی یهود و نصاری) نباشند که دوره طولانی (زمان فترت) بر آنها گذشت و دلهاشان زنگ قساوت گرفت و بسیاری فاسق و نابکار شدند؟'),
(5092, 57, 17, 'بدانید که خداست که زمین را پس از مرگ (خزان) زنده می‌گرداند. ما آیات و ادلّه (قدرت خود) را برای شما بیان کردیم تا مگر فکر و عقل به کار بندید.'),
(5093, 57, 18, 'همانا مردان و زنانی که در راه خدا (به فقیران) صدقه و احسان کنند و به خدا قرض نیکو دهند (یعنی به محتاجان قرض الحسنه دهند) احسان آنها را چندین برابر سازند و پاداش با لطف و کرامت نیز خواهند داشت.'),
(5094, 57, 19, 'و آنان که به خدا و رسولانش ایمان آوردند به حقیقت راستگویان عالمند و برایشان نزد خدا اجر شهیدان است، پاداش اعمال و نور ایمانشان را (در بهشت) می‌یابند. و آنان که کافر شده و آیات ما را تکذیب کردند آنها اهل آتش دوزخند.'),
(5095, 57, 20, 'بدانید که زندگانی دنیا به حقیقت بازیچه‌ای است طفلانه و لهو و عیّاشی و زیب و آرایش و تفاخر و خودستایی با یکدیگر و حرص افزودن مال و فرزندان. در مثل مانند بارانی است که گیاهی در پی آن از زمین بروید که برزگران (یا کفار دنیاپرست) را به شگفت آرد و سپس بنگری که زرد و خشک شود و بپوسد. و در عالم آخرت (دنیا طلبان را) عذاب سخت جهنم و (مؤمنان را) آمرزش و خشنودی حق نصیب است. و باری، بدانید که زندگانی دنیا جز متاع فریب و غرور چیزی نیست.'),
(5096, 57, 21, 'به سوی آمرزش پروردگارتان بشتابید و به راه بهشتی که عرضش به قدر پهنای آسمان و زمین است و برای اهل ایمان به خدا و پیمبرانش مهیّا گردیده. این فضل خداست که به هر کس خواهد عطا می‌کند و خدا دارای فضل و کرم بسیار عظیم است.'),
(5097, 57, 22, 'هر رنج و مصیبتی که در زمین (از قحطی و آفت و فقر و ستم) یا در نفس خویش (چون ترس و غم و درد و الم) به شما رسد همه در کتاب (لوح محفوظ ما) پیش از آنکه همه را (در دنیا) ایجاد کنیم ثبت است و البته این کار بر خدا آسان است.'),
(5098, 57, 23, '(این تقدیر حق را بدانید) تا هرگز بر آنچه از دست شما رود دلتنگ نشوید و به آنچه به شما می‌دهد مغرور و دلشاد نگردید، و خدا دوستدار هیچ متکبر خودستایی نیست.'),
(5099, 57, 24, 'همان متکبرانی که خودشان (در احسان به فقیران) بخل می‌ورزند و مردم را هم به بخل و منع احسان وا می‌دارند. و هر که روی (از اطاعت حق) بگرداند خدا به کلی بی‌نیاز و ستوده صفات است.'),
(5100, 57, 25, 'همانا ما پیمبران خود را با ادلّه و معجزات فرستادیم و با ایشان کتاب و میزان عدل نازل کردیم تا مردم به راستی و عدالت گرایند، و آهن (و دیگر فلزات) را که در آن هم سختی (جنگ و کارزار) و هم منافع بسیار بر مردم است (نیز برای حفظ عدالت) آفریدیم، و تا معلوم شود که خدا و رسلش را با ایمان قلبی که یاری خواهد کرد؟ (هر چند) که خدا بسیار قوی و مقتدر (و از یاری خلق بی‌نیاز) است.'),
(5101, 57, 26, 'و البته ما نوح و ابراهیم را فرستادیم و در میان فرزندانشان نبوت و کتاب آسمانی قرار دادیم، پس آنها برخی به راه حق هدایت یافتند و بسیاری به فسق و بدکاری شتافتند.'),
(5102, 57, 27, 'و از پی آنان باز رسولان دیگر و سپس عیسی مریم را فرستادیم و به او کتاب آسمانی انجیل را عطا کردیم و در دل پیروان (حقیقی) او رأفت و مهربانی نهادیم و لیکن رهبانیّت و ترک دنیا را از پیش خود بدعت انگیختند، ما بر آنها جز آنکه رضا و خشنودی خدا را طلبند (در کتاب انجیل) ننوشتیم و باز آنها چنان که باید و شاید همه مراعات آن را نکردند، ما هم به آنان که ایمان آوردند پاداش و اجرشان را عطا کردیم و لیکن از آنها بسیاری به راه فسق و تبهکاری شتافتند.'),
(5103, 57, 28, 'ای کسانی که به حق گرویدید، اینک خداترس و متّقی شوید و به رسولش (محمّد مصطفی صلّی اللّه علیه و آله) نیز ایمان آرید تا خدا شما را از رحمتش دو بهره نصیب گرداند (یکی بر ایمان به رسولان پیشین و یکی بر ایمان به دین رسول ختمی مرتبت) و نوری شما را عطا کند که بدان نور راه (بهشت ابد) پیمایید و از گناه شما درگذرد، و خدا بسیار آمرزنده و مهربان است.'),
(5104, 57, 29, 'تا آنان که اسلام نیاوردند از اهل کتاب بدانند که آنها هیچ به رحمت و فضل الهی دستی ندارند و هر فضلی و رحمتی به دست خداست، که او به هر که خواهد می‌بخشد و خدا دارای فضل و رحمت بزرگ و بی‌انتها است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, '(ای رسول ما) خدا سخن آن زن را که درباره شوهرش با تو به مجادله برخاسته و شکوه او به خدا می‌برد محققا شنید و گفتگوهای شما را نیز خدا می‌شنود که خدا البته شنوا و بیناست. (اوس بن صامت با زن خود خوله بنت ثعلبه ظهار کرد یعنی گفت: «تو بر من چون مادری». و این کار در حکم طلاق و حرمت آور بود. زن نزد رسول آمد و حکم پرسید. رسول حکم به حرمت فرمود. زن باز مجادله کرد و آخر گفت: من به خدا شکوه می‌کنم از تنهایی و غربت خود. و آیه بعد راجع به حکم ظهار نازل شد و بر این فعل حرام کفّاره معیّن گردید).'),
(5106, 58, 2, 'کسانی از شما که با زنان خود ظهار کنند آنها مادر حقیقی شوهران نخواهند شد بلکه مادر ایشان جز آن که آنها را زاییده نیست و این مردم سخنی ناپسند و باطل می‌گویند، و خدا را عفو و بخشش بسیار است.'),
(5107, 58, 3, 'و آنان که با زنان خود ظهار کنند آن‌گاه از گفته خود برگردند، پیش از تماس و مجامعت باید برده‌ای آزاد کنند. به این عمل پند و موعظه می‌شوید (تا دیگر به این کار حرام اقدام نکنید) و خدا به تمام کردار شما آگاه است.'),
(5108, 58, 4, 'و هر که برده نیابد (یعنی بر آزاد کردن برده توانایی ندارد) باز باید پیش از جماع دو ماه پی در پی روزه بگیرد، و باز اگر نتواند باید شصت مسکین را طعام دهد. این (حکم کفّاره) برای آن است که شما به خدا و رسول او ایمان (کامل) آرید. و این احکام حدود الهی است و کافران را عذاب دردناک دوزخ مهیّاست.'),
(5109, 58, 5, 'آنان که با خدا و رسول او سخت مخالفت می‌کنند آنها هم مانند کافران پیش به رو در (آتش عذاب) افتند. و ما آیات روشن بیان نازل کردیم، و کافران را عذاب ذلت و خواری مهیّاست.'),
(5110, 58, 6, 'در روزی که خدا جمیع خلایق را (برای حساب) برانگیزد تا آنها را به (نتیجه) اعمالشان که او تمام را به شماره ضبط فرموده و آنها فراموش کرده‌اند آگاه سازد، و خدا بر همه موجودات عالم گواه است.'),
(5111, 58, 7, 'آیا ندیدی که آنچه در آسمانها و زمین است خدا بر آن آگاه است؟ هیچ رازی سه کس با هم نگویند جز آنکه خدا چهارم آنها و نه پنج کس جز آنکه خدا ششم آنها و نه کمتر از آن و نه بیشتر جز آنکه هر کجا باشند او با آنهاست سپس روز قیامت همه را به (نتیجه نیک و بد) اعمالشان آگاه خواهد ساخت که خدا به کلیه امور عالم داناست.'),
(5112, 58, 8, 'آیا آنان (یعنی یهود و منافقان) را که از نجوا و راز گفتن با هم ممنوع شدند ندیدی که باز هم با یکدیگر بر بزهکاری و دشمنی و مخالفت رسول راز گفتن از سرگرفتند؟ و هر گاه حضور تو آیند (از مکر و خدیعت) تو را سلام و تحیّتی گویند که خدا نفرموده است و پیش خود می‌گویند: (اگر این شخص رسول خداست) چرا خدا بر این سلام (تمسخر آمیزی) که به او می‌کنیم ما را عذاب نمی‌کند؟! عذاب دوزخ آنها را کفایت است که در آنجا که بسیار بد منزلگاهی است پیوسته معذّب خواهند شد.'),
(5113, 58, 9, 'ای کسانی که ایمان آورده‌اید، شما هر گاه با هم، سخنی به راز گویید هرگز بر بزهکاری و دشمنی و مخالفت رسول نگویید بلکه بر نیکویی و تقوا راز به میان آرید و از خدا که به سوی او محشور خواهید شد بترسید و پرهیزکار شوید.'),
(5114, 58, 10, 'همیشه نجوا و راز گفتن از (نفوس شریره) شیطان است که می‌خواهد مؤمنان را دلتنگ و پریشان خاطر کند در صورتی که هیچ زیان به آنها نمی‌رساند جز آنکه امر خدا باشد، و مؤمنان باید همیشه بر خدا توکل کنند.'),
(5115, 58, 11, 'ای اهل ایمان، هرگاه شما را گفتند که در مجالس خود جای را (بر یکدیگر) فراخ دارید، جای باز کنید (و برای نشستن مکان بالاتر و نزدیک‌تر به پیغمبر تنازع و تزاحم مکنید) تا خدا بر توسعه (مکان و مقام و منزلت) شما بیفزاید و هرگاه گفتند که از جای خود (برای توسعه مجلس یا کار خیر دیگری) برخیزید، برخیزید، تا خدا مقام اهل ایمان و دانشمندان شما را (در دو جهان) رفیع گرداند، و خدا به هر چه کنید به همه آگاه است.'),
(5116, 58, 12, 'ای اهل ایمان، هر گاه بخواهید که با رسول سخن سرّی گویید (یا سؤالی کنید) پیش از این کار باید مبلغی صدقه دهید که این صدقه برای شما بهتر و پاکیزه‌تر است (که شما را از سؤالات بی‌جا بر کنار و از بخل و لئامت پاک می‌گرداند) و اگر (از فقر) چیزی (برای صدقه) نیابید در این صورت خدا بسیار آمرزنده و مهربان است.'),
(5117, 58, 13, 'آیا از اینکه پیش از راز گفتن با رسول صدقه دهید (از فقر) ترسیدید؟ پس حال که اداء صدقه نکردید و خدا هم شما را بخشید اینک نماز به پا دارید و زکات بدهید و خدا و رسول او را اطاعت کنید، و خدا به هر چه کنید آگاه است.'),
(5118, 58, 14, 'آیا آن (منافق) مردمی را ندیدی که با (یهود) قومی که خدا بر آنها غضب کرده است دوستی کردند؟ آنها به حقیقت نه از شما مسلمین و نه از فرقه یهودند و به دروغ قسم یاد می‌کنند (که ما اهل ایمانیم) در صورتی که خود می‌دانند.'),
(5119, 58, 15, 'خدا بر آنها عذابی سخت مهیّا کرده، زیرا بسیار زشت کردارند.'),
(5120, 58, 16, 'آن مردم منافق سوگند و قسمهای خود را سپر قرار دادند پس (خلق را) از راه خدا بازداشتند، پس بر آنها کیفر عذاب ذلّت و خواری خواهد بود.'),
(5121, 58, 17, 'و هرگز مال و فرزندان، آنان را از (عذاب قهر) خدا ابدا نرهاند، آنها اهل آتش دوزخند و در آن همیشه معذّبند.'),
(5122, 58, 18, 'روزی که خدا همه آنها را (برای انتقام) برانگیزد پس چنان که برای شما قسم یاد می‌کنند برای خدا هم به دروغ سوگند می‌خورند و می‌پندارند که اثری بر آنها خواهد داشت. الا (ای اهل ایمان) بدانید که آنها بسیار مردم دروغگویی هستند.'),
(5123, 58, 19, 'شیطان بر (دل) آنها سخت احاطه کرده که فکر و ذکر خدا را به کلی از یادشان برده، آنان حزب شیطانند، الا (ای اهل ایمان) بدانید که حزب شیطان به حقیقت زیانکاران عالمند.'),
(5124, 58, 20, 'آنان که با خدا و رسول او عناد و مخالفت می‌کنند آنها در میان (خلق) خوار و ذلیل‌ترین مردمند.'),
(5125, 58, 21, 'خدا (در لوح محفوظ) نگاشته و حتم گردانیده که البته من و رسولانم (بر دشمنان) غالب شویم، که خدا بسیار قوی و مقتدر است.'),
(5126, 58, 22, 'هرگز مردمی را که ایمان به خدا و روز قیامت آورده‌اند چنین نخواهی یافت که دوستی با دشمنان خدا و رسول او کنند هر چند آن دشمنان پدران یا فرزندان یا برادران یا خویشان آنها باشند. این مردم پایدارند که خدا بر دلهاشان (نور) ایمان نگاشته و به روح (قدسی) خود آنها را مؤید و منصور گردانیده و آنها را به بهشتی داخل کند که نهرها زیر درختانش جاری است و جاودان در آنجا متنعّمند، خدا از آنها خشنود و آنها هم از خدا خشنودند، اینان به حقیقت حزب خدا هستند، الا (ای اهل ایمان) بدانید که حزب خدا رستگاران عالمند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'هر چه در آسمانها و زمین است همه به تسبیح و ستایش یکتا خدای عالم که مقتدر و حکیم است مشغولند.'),
(5128, 59, 2, 'اوست خدایی که کافران اهل کتاب را (یعنی یهود بنی نضیر را که به مکر، قصد قتل پیغمبر صلّی اللّه علیه و آله کردند) برای اولین بار همگی را از دیارشان بیرون کرد و هرگز شما مسلمین گمان نمی‌کردید که آنها از دیار خود بیرون روند و آنها هم حصارهای محکم خود را نگهبان خود از (قهر و انتقام) خدا می‌پنداشتند تا آنکه (عذاب) خدا از آنجا که گمان نمی‌بردند بدانها فرا رسید و در دلشان (از سپاه اسلام) ترس افکند تا به دست خود و به دست مؤمنان خانه‌هاشان را ویران می‌کردند، پس ای هوشیاران عالم پند و عبرت گیرید.'),
(5129, 59, 3, 'و اگر خدا بر آنها حکم جلاء وطن نمی‌کرد (سخت‌تر از آن) در دنیا (به قتل و اسارت مانند کفار بدر) معذّبشان می‌کرد و (به هر حال) عذاب آتش در آخرت بر آنها خواهد بود.'),
(5130, 59, 4, 'این (جلاء وطن و آوارگی آنها) برای این بود که آنان با خدا و رسول او سخت دشمنی و مخالفت کردند و هر که با خدا (و رسولش) دشمنی آغازد عقاب خدا بسیار سخت است.'),
(5131, 59, 5, 'آنچه از درختان خرما را (که در دیار بنی نضیر) بریدید و آنچه را بر پا گذاشتید همه به امر خدا (و صلاح اسلام) و برای خواری و سرکوبی جهودان فاسق نابکار بود.'),
(5132, 59, 6, 'و آنچه را که خدا از (مال) آنها به رسم غنیمت به رسول خود باز داد (متعلق به رسول است، زیرا) شما سپاهیان اسلام بر آن هیچ اسب و شتری نتاختید و لیکن خدا رسولانش را بر هر که خواهد مسلّط می‌گرداند و خدا بر هر چیز تواناست.'),
(5133, 59, 7, 'آنچه را که خدا از اموال کافران دیار به رسول خود غنیمت داد آن متعلق به خدا و رسول و (ائمه) خویشاوندان رسول و یتیمان و فقیران و در راه ماندگان (ایشان) است. این حکم برای آن است که غنایم، دولت توانگران را نیفزاید (بلکه به مبلغان دین و فقیران اسلام تخصیص یابد) و شما آنچه رسول حق دستور دهد (و منع یا عطا کند) بگیرید و هر چه نهی کند واگذارید و از خدا بترسید که عقاب خدا بسیار سخت است.'),
(5134, 59, 8, 'مقام بلند (یا غنائم) خاصّ فقیران مهاجرین است که آنها را از وطن و اموالشان به دیار غربت راندند در صورتی که (چشم از خانه پوشیده و) در طلب فضل و خشنودی خدا می‌کوشند و خدا و رسول او را یاری می‌کنند، اینان به حقیقت راستگویان عالمند.'),
(5135, 59, 9, 'و هم آن جماعت انصار که پیش از (هجرت) مهاجرین (در مکّه به رسول ایمان آوردند و) مدینه را خانه ایمان گردانیدند و مهاجرین را که به سوی آنها آمدند دوست می‌دارند و در دل خود هیچ حاجتی (و حسد و بخلی) نسبت به آنچه (از غنائم بنی نضیر) که به آنها داده شد نمی‌یابند و هر چند به چیزی نیازمند باشند باز مهاجران را بر خویش مقدم می‌دارند (و جانشان به کلی از بخل و حسد و حرص دنیا پاک است) و هر کس را از خوی بخل و حرص دنیا نگاه دارند آنان به حقیقت رستگاران عالمند.'),
(5136, 59, 10, 'و آنان که پس از مهاجرین و انصار آمدند (یعنی تابعین و سایر مؤمنین تا روز قیامت) دایم به درگاه خدا عرض می‌کنند: پروردگارا، بر ما و برادران دینی‌مان که در ایمان پیش از ما شتافتند ببخش و در دل ما هیچ کینه و حسد مؤمنان قرار مده، پروردگارا، تویی که بسیار رؤوف و مهربانی.'),
(5137, 59, 11, '(ای رسول) آیا آنان را که نفاق ورزیدند ندیدی؟ که با برادرانشان همان یهود اهل کتاب که کافر شدند (در پیوسته و) گویند: اگر شما از دیارتان اخراج شوید البته ما هم به همراهی شما خارج خواهیم شد و در راه حمایت شما از احدی ابدا اطاعت نخواهیم کرد و اگر (مسلمانان) با شما جنگ کردند البته شما را مدد می‌کنیم، و خدا گواهی دهد که محققا آنها دروغ می‌گویند.'),
(5138, 59, 12, 'اگر آنها اخراج شوند هرگز منافقان با آنها خارج نمی‌شوند و اگر (مسلمانان) به جنگشان آیند هرگز یاریشان نمی‌کنند و اگر هم یاریشان کنند البته به جنگ پشت کنند و دیگر نصرت نخواهند یافت.'),
(5139, 59, 13, 'این مردم منافق در دلشان از شما بیشتر ترس دارند تا از خدا، زیرا آنها مردمی هستند که هیچ فهم و شعوری ندارند.'),
(5140, 59, 14, '(یهودان از ترس) بر جنگ با شما جمع نمی‌شوند مگر در قریه‌های محکم حصار یا از پس دیوار (دشمنی مکر و حیله و جاسوسی)، کارزار بین خودشان سخت است، شما آنها را جمع و متّفق می‌پندارید در صورتی که دلهاشان سخت متفرق است، زیرا آن قوم دارای فهم و عقل نیستند.'),
(5141, 59, 15, 'مثل حال اینان هم مانند همان قوم کافر پیشین است (یعنی یهود قینقاع یا کفّار بدر) که (در دنیا) بدین زودی کیفر کردارشان را چشیدند و (در قیامت) هم عذاب دردناک بر آنها مهیّاست.'),
(5142, 59, 16, '(این منافقان) در مثل مانند شیطانند که انسان را گفت: به خدا کافر شو، پس از آنکه آدمی کافر شد بدو گوید: از تو بیزارم، که من از (عقاب) پروردگار عالمیان سخت می‌ترسم.'),
(5143, 59, 17, 'پس عاقبت شیطان و آدمی که به امر او کافر شد این است که هر دو در آتش دوزخ مخلّدند و آن دوزخ کیفر ستمکاران عالم است.'),
(5144, 59, 18, 'الا ای اهل ایمان، خداترس شوید و هر نفسی نیک بنگرد تا چه عملی برای فردای قیامت خود پیش می‌فرستد، و از خدا بترسید که او به همه کردارتان به خوبی آگاه است.'),
(5145, 59, 19, 'و مانند آنان نباشید که به کلی خدا را فراموش کردند، خدا هم (حظّ روحانی و بهره ابدی) نفوس آنها را از یادشان برد، آنان به حقیقت بدکاران عالمند.'),
(5146, 59, 20, 'هرگز اهل جهنم و اهل بهشت با هم یکسان نیستند، اهل بهشت به حقیقت سعادتمندان عالمند.'),
(5147, 59, 21, 'اگر ما این قرآن (عظیم الشأن) را (به جای دلهای خلق) بر کوه نازل می‌کردیم مشاهده می‌کردی که کوه از ترس و عظمت خدا خاشع و ذلیل و متلاشی می‌گشت. و این امثال را برای مردم بیان می‌کنیم باشد که اهل (عقل و) فکرت شوند.'),
(5148, 59, 22, 'اوست خدای یکتایی که غیر او خدایی نیست که دانای نهان و آشکار عالم است، اوست بخشنده و مهربان.'),
(5149, 59, 23, 'اوست خدای یکتایی که غیر او خدایی نیست، سلطان مقتدر عالم، پاک از هر نقص و آلایش، منزّه از هر عیب و ناشایست، ایمنی بخش دلهای هراسان، نگهبان جهان و جهانیان، غالب و قاهر بر همه خلقان، با جبروت و عظمت، بزرگوار و برتر (از حدّ فکرت)، زهی منزّه و پاک خدای یکتا که از هر چه بر او شریک پندارند منزّه و (از آنچه در وهم و خیال و عقل اندیشند) مبرّ است.'),
(5150, 59, 24, 'اوست خدای آفریننده عالم امکان و پدید آرنده جهان و جهانیان، نگارنده صورت خلقان، او را نامهای نیکو بسیار است، آنچه در آسمانها و زمین است همه به تسبیح و ستایش او مشغولند و اوست یکتا خدای مقتدر حکیم.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ای کسانی که به خدا ایمان آورده‌اید، هرگز نباید کافران را که دشمن من و شمایند یاران خود برگرفته و طرح دوستی با آنها افکنید در صورتی که آنان به کتابی که بر شما آمد یعنی قرآن حق سخت کافر شدند، رسول خدا و شما مؤمنان را به جرم ایمان به خدا از وطن خود آواره می‌کنند، پس نباید اگر شما برای جهاد در راه من و طلب رضا و خشنودی من بیرون آمده‌اید پنهانی با آنها دوستی کنید، و من به اسرار نهان و اعمال آشکار شما داناترم، و هر که از شما چنین کند سخت به راه ضلالت شتافته است.'),
(5152, 60, 2, 'هر گاه آنها بر شما تسلط یابند باز همان دشمن دیرینند و هر چه بتوانند به دست و زبان بر عداوت شما می‌کوشند و چقدر دوست می‌دارند که شما کافر شوید.'),
(5153, 60, 3, 'هرگز روز قیامت خویشان و فرزندان شما (اگر مؤمن نباشند) هیچ سودی برایتان ندارند که در آن روز خدا میان شما به کلی جدایی می‌افکند و خدا به هر چه کنید بیناست.'),
(5154, 60, 4, 'برای شما مؤمنان بسیار پسندیده و نیکوست که به ابراهیم و اصحابش اقتدا کنید که آنها به قوم (مشرک) خود گفتند: ما از شما و بتهای شما که به جای خدا می‌پرستید به کلی بیزاریم، ما مخالف و منکر شماییم و همیشه میان ما و شما کینه و دشمنی خواهد بود تا وقتی که تنها به خدای یگانه ایمان آرید، الاّ آنکه ابراهیم به پدر (یعنی عموی) خود گفت: (اگر ایمان آری) من برای تو از خدا آمرزش می‌طلبم (که از شرک و عصیان گذشته تو درگذرد) و (هر گاه ایمان نیاوری) دیگر من بر نجات تو از (قهر و غضب) خدا هیچ کاری نتوانم کرد، بار الها، ما بر تو توکل کردیم و رو به درگاه تو آوردیم و بازگشت تمام خلق به سوی توست.'),
(5155, 60, 5, 'پروردگارا، ما را مایه فتنه و امتحان کافران مگردان و پروردگارا، ما را بیامرز که تنها تویی که (بر هر کار) بسیار مقتدری و (به صلاح خلق) کاملا آگاهی.'),
(5156, 60, 6, 'البته برای شما مؤمنان هر که به خدا و (ثواب) عالم آخرت امیدوار است اقتداء به ابراهیم و یارانش نیکوست، و هر که روی (از خدا) بگرداند خدا (از طاعت خلق) کاملا بی‌نیاز و به ذات خود ستوده صفات است.'),
(5157, 60, 7, 'امید است که خدا دوستی میان شما و دشمنانتان (به فتوحات اسلام و ایمان کفّار) برقرار گرداند و خدا قادر و خدا بسیار آمرزنده و مهربان است.'),
(5158, 60, 8, 'خدا شما را از آنان که با شما در دین قتال و دشمنی نکرده و شما را از دیارتان بیرون ننمودند نهی نمی‌کند که با آنان نیکی کنید و به عدالت و انصاف رفتار نمایید، که خدا مردم با عدل و داد را بسیار دوست می‌دارد.'),
(5159, 60, 9, 'و تنها شما را از دوستی کسانی نهی می‌کند که در دین با شما قتال کرده و از وطنتان بیرون کردند و بر بیرون کردن شما همدست شدند، زنهار آنها را دوست نگیرید و کسانی که با آنان دوستی و یاوری کنند ایشان به حقیقت ظالم و ستمکارند.'),
(5160, 60, 10, 'ای کسانی که ایمان آورده‌اید، زنانی که به عنوان اسلام و ایمان (از دیار خود) هجرت کرده و به سوی شما آمدند (ممکن است جاسوس باشند) شما از آنها تحقیق کرده و امتحانشان کنید و البته خدا به (صدق و کذب) ایمانشان داناتر است، اگر با ایمانشان شناختید آنها را دیگر به شوهران کافرشان بر مگردانید که هرگز این زنان مؤمن بر آن کفّار و آن شوهران کافر بر این زنان حلال نیستند ولی مهر و نفقه‌ای که شوهران مخارج آن زنان کرده‌اند به آنها بپردازید، و باکی نیست که شما با آنان نکاح کنید در صورتی که اجر و مهرشان را بدهید. و هرگز متوسل به حفاظت (و عقد و پیمان) زنان کافره نشوید و (اگر زنانتان از اسلام به کفر برگشتند و نزد کافران رفتند) از کفّار مهر و نفقه مطالبه کنید، آنها هم (اگر زنانشان ایمان آوردند و نزد شما آمدند) مهر و نفقه طلبند. این حکم خداست که میان شما حکم می‌کند و خدا (به حقایق امور) دانا و (به مصالح خلق) آگاه است.'),
(5161, 60, 11, 'و اگر از زنان شما کسانی (مرتد شده) به سوی کافران رفتند و شما در مقام انتقام بر آمدید و غنیمت بردید، به مردانی که زنانشان رفته‌اند به قدر همان مهر و نفقه‌ای که خرج کرده‌اند بدهید، و از خدایی که به او ایمان آورده‌اید بترسید و پرهیزکار شوید.'),
(5162, 60, 12, 'الا ای پیغمبر (گرامی) چون زنان مؤمن آیند که با تو بیعت کنند که دیگر هرگز شرک به خدا نیاورند و سرقت و زنا کاری نکنند و اولاد خود را به قتل نرسانند و بر کسی افترا و بهتان میان دست و پای خود نبندند (یعنی فرزندی را که میان دست و پای خود پرورده و علم به آن از انعقاد نطفه او دارند به دروغ به کسی غیر پدرش نبندند) و با تو در هیچ امر معروفی مخالفت نکنند، بدین شرایط با آنها بیعت کن و بر آنان از خدا آمرزش و غفران طلب، که خدا بسیار آمرزنده و مهربان است.'),
(5163, 60, 13, 'الا ای اهل ایمان، هرگز قومی را که خدا بر آنان غضب کرده (یعنی جهودان را) یار و دوستدار خود مگیرید که آنها از عالم آخرت به کلی مأیوسند چنانکه کافران از اهل قبور نومیدند (یعنی فرقی میان جهودان و اهل کتاب که خود را معتقد به خدا و قیامت می‌دانند با کافران بی‌عقیده در مقام عمل هیچ نیست که هیچ کدام طالب آخرت و در فکر قیامت ابدا نیستند).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'هر چه در آسمانها و زمین است همه به تسبیح و ستایش خدای عالم که مقتدر و حکیم است مشغولند.'),
(5165, 61, 2, 'الا ای کسانی که ایمان آورده‌اید، چرا چیزی به زبان می‌گویید که در مقام عمل خلاف آن می‌کنید؟'),
(5166, 61, 3, 'این عمل که سخن بگویید و خلاف آن کنید بسیار سخت خدا را به خشم و غضب می‌آورد.'),
(5167, 61, 4, 'خدا آن مؤمنان را که در راه او در صف جهاد با کافران، مانند بنیان و سدّی آهنین همدست و پایدارند بسیار دوست می‌دارد.'),
(5168, 61, 5, '(یاد آر) وقتی که موسی به قوم خود گفت: ای قوم، برای چه مرا رنج و آزار می‌دهید (و تهمت سحر و کذب و عیبهای دیگر بر من می‌بندید) در صورتی که بر شما معلوم است که من رسول خدا به سوی شما هستم؟ باز چون (از حق) روی گردانیدند خدا هم دلهای بی‌نورشان را (از سعادت و اقبال به حق) بگردانید و خدا هرگز مردم نابکار فاسق را هدایت نخواهد کرد.'),
(5169, 61, 6, 'و هنگامی که عیسی مریم به بنی اسرائیل گفت: من همانا رسول خدا به سوی شما هستم و به حقانیت کتاب تورات که مقابل من است تصدیق می‌کنم و نیز (شما را) مژده می‌دهم که بعد از من رسول بزرگواری که نامش (در انجیل من) احمد است بیاید. چون آن رسول ما با آیات و معجزات به سوی خلق آمد گفتند: این سحری آشکار است.'),
(5170, 61, 7, 'و آیا از آن کس که به راه اسلام و سعادتش می‌خوانند و او همان دم بر خدا افترا و دروغ می‌بندد کسی ستمکارتر هست؟ و خدا هم هیچ قوم ستمکاری را هدایت نخواهد کرد.'),
(5171, 61, 8, 'کافران می‌خواهند تا نور خدا را به گفتار باطل (و طعن و مسخره) خاموش کنند و البته خدا نور خود را هر چند کافران خوش ندارند کامل و محفوظ خواهد داشت.'),
(5172, 61, 9, 'اوست خدایی که رسولش (محمّد مصطفی صلّی اللّه علیه و آله) را به هدایت خلق و ابلاغ دین حق فرستاد تا آن را هر چند مشرکان خوش ندارند بر همه ادیان عالم غالب گرداند.'),
(5173, 61, 10, 'ای اهل ایمان، آیا شما را به تجارتی سودمند که شما را از عذاب دردناک (آخرت) نجات بخشد دلالت کنم؟'),
(5174, 61, 11, 'به خدا و رسول او ایمان آرید و به مال و جان در راه خدا جهاد کنید، که این کار (از هر تجارت) اگر دانا باشید برای شما بهتر است.'),
(5175, 61, 12, 'تا خدا گناهان شما ببخشد و در بهشتی که در زیر درختانش نهرها جاری است داخل گرداند و در بهشتهای عدن جاودانی منزلهای نیکو عطا فرماید. این همان رستگاری بزرگ است.'),
(5176, 61, 13, 'و باز تجارتی دیگر که آن را هم دوست دارید نصرت و یاری خدا (در جنگ) و فتح نزدیک (سپاه اسلام) است، و اهل ایمان را (به این دو نعمت) بشارت ده.'),
(5177, 61, 14, 'ای اهل ایمان، شما هم یاران خدا باشید چنان که عیسی مریم به حواریین گفت: کیست مرا برای خدا یاری کند؟ آنها گفتند: ما یاران خداییم. پس (با آن همه معجزات) طایفه‌ای از بنی اسرائیل به او ایمان آوردند و طایفه‌ای کافر شدند، ما هم آنان را که ایمان آوردند مؤید و منصور گردانیدیم تا بر دشمنشان ظفر یافتند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'هر چه در آسمانها و زمین است همه به تسبیح و ستایش خدا که پادشاهی منزّه و پاک و مقتدر و حکیم است مشغولند.'),
(5179, 62, 2, 'اوست خدایی که میان عرب امّی (یعنی قومی که خواندن و نوشتن نمی‌دانستند) پیغمبری بزرگوار از همان مردم برانگیخت که بر آنان آیات وحی خدا تلاوت می‌کند و آنها را (از لوث جهل و اخلاق زشت) پاک می‌سازد و شریعت و احکام کتاب سماوی و حکمت الهی می‌آموزد و همانا پیش از این همه در ورطه جهالت و گمراهی آشکار بودند.'),
(5180, 62, 3, 'و نیز قوم دیگری از آنان را (که به روایت از پیغمبر مراد عجمند) که هنوز به عرب (در اسلام) ملحق نشده‌اند هدایت فرماید که او خدای مقتدر و همه کارش به حکمت و مصلحت است.'),
(5181, 62, 4, 'این (رسالت و نزول قرآن) فضل و کرامت خداست که به هر که بخواهد می‌دهد و خدا را فضل و رحمت نامنتهاست.'),
(5182, 62, 5, 'وصف حال آنان که علم تورات بر آنان نهاده شد (و بدان مکلف شدند) ولی آن را حمل نکردند (و خلاف آن عمل نمودند) در مثل به حماری ماند که بار کتابها بر پشت کشد (و از آن هیچ نفهمد و بهره نبرد)، آری مثل قومی که حالشان این است که آیات خدا را تکذیب کردند بسیار بد است و خدا هرگز ستمکاران را (به راه سعادت) رهبری نخواهد کرد.'),
(5183, 62, 6, 'بگو: ای جماعت یهود، اگر پندارید که شما به حقیقت دوستداران خدایید نه مردم دیگر، پس تمنّای مرگ کنید اگر راست می‌گویید.'),
(5184, 62, 7, 'و حال آنکه در اثر آن کردار بد که به دست خود (برای آخرت خویش) پیش فرستاده‌اند ابدا آرزوی مرگ نخواهند کرد (بلکه از مرگ سخت ترسان و هراسانند) و خدا به (کردار) ستمکاران داناست.'),
(5185, 62, 8, 'بگو: عاقبت مرگی که از آن می‌گریزید شما را البته ملاقات خواهد کرد و پس از مرگ به سوی خدایی که دانای پیدا و پنهان است بازگردانیده می‌شوید و او شما را به آنچه می‌کرده‌اید آگاه می‌سازد.'),
(5186, 62, 9, 'ای کسانی که ایمان آورده‌اید، هر گاه (شما را) برای نماز روز جمعه بخوانند فی الحال به ذکر خدا بشتابید و کسب و تجارت رها کنید که این اگر بدانید برای شما بهتر خواهد بود.'),
(5187, 62, 10, 'پس آن‌گاه که نماز پایان یافت (بعد از ظهر جمعه باز در پی کسب و کار خود رفته و) روی زمین منتشر شوید و از فضل و کرم خدا (روزی) طلبید، و یاد خدا بسیار کنید تا مگر رستگار و سعادتمند گردید.'),
(5188, 62, 11, 'و این مردم (سست ایمان) چون تجارتی یا لهو و بازیچه‌ای ببینند بدان شتابند و تو را در نماز تنها گذارند؛ بگو که آنچه نزد خداست (یعنی ثواب آخرت و بهشت ابد) بسیار برای شما از لهو و لعب و تجارت (های دنیا) بهتر است و خدا بهترین روزی‌دهنده است. (در شأن نزول آیه جابر روایت کرد که قومی با رسول صلّی اللّه علیه و آله و سلّم نماز جمعه می‌خواندند، آواز طبل کاروان تجارت شنیدند، همه از پی تجارت و صدای لهو طبل رفتند جز هشت یا دوازده تن. رسول صلّی اللّه علیه و آله و سلّم فرمود: اگر این عده هم می‌رفتند عذاب خدا بر امت نازل می‌شد)');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, '(ای رسول ما) چون منافقان ریاکار نزد تو آیند گویند که ما به یقین و حقیقت گواهی می‌دهیم که تو رسول خدایی؛ و خدا می‌داند که تو رسول اویی و خدا هم گواهی می‌دهد که منافقان سخن دروغ می‌گویند.'),
(5190, 63, 2, 'قسمهای (دروغ) خود را سپر جان خویش (و مایه فریب خلق) قرار داده‌اند و بدین وسیله راه خدا را (به روی خلق) بسته‌اند، آنان آنچه می‌کنند بسیار بد می‌کنند.'),
(5191, 63, 3, 'این (نفاق و بدکاریشان) برای آن است که آنها (به زبان) ایمان آوردند و سپس (به دل) کافر شدند، پس مُهر (قهر و ظلمت) بر دلهاشان نهاده شد تا هیچ (از حقایق ایمان) درک نکنند.'),
(5192, 63, 4, 'تو چون آن منافقان را مشاهده کنی ظاهر و وجود جسمانی آنها (به آراستگی فریبنده) تو را به شگفت آرد و اگر سخن گویند (بس خوش‌گفتار و چرب زبانند) به سخن‌هاشان گوش فرا خواهی داد (ولی از باطن و درون) گویی که چوبی خشک تکیه کرده بر دیوارند (و هیچ عقل و ایمان و معرفت ندارند. و چون در باطن نادرست و بداندیشند) هر صدایی بشنوند بر زیان خویش پندارند. دشمنان به حقیقت اینان هستند، از ایشان بر حذر باش، خدایشان بکشد، (از حق) به کجا بازگردانیده می‌شوند؟'),
(5193, 63, 5, 'و هر گاه به آنها گویند: بیایید تا رسول خدا برای شما (از حق) آمرزش طلبد سر بپیچند و بنگری که با تکبر و نخوت (از حق) روی می‌گردانند.'),
(5194, 63, 6, '(ای رسول) تو (از خدا) بر آنان آمرزش بخواهی یا نخواهی به حالشان یکسان است خدا هرگز آنها را نمی‌بخشد که همانا قوم نابکار فاسق را خدا هیچ وقت (به راه سعادت) هدایت نخواهد کرد.'),
(5195, 63, 7, 'اینها همان مردم بدخواهند که می‌گویند: بر اصحاب رسول خدا انفاق مال مکنید تا (از گِردش) پراکنده شوند در صورتی که خدا را گنجهای آسمانها و زمین است لیکن منافقان درک آن نمی‌کنند.'),
(5196, 63, 8, 'آنها (پنهانی با هم) می‌گویند: اگر به مدینه مراجعت کردیم البته اربابان عزّت و ثروت، مسلمانان ذلیل فقیر را از شهر بیرون کنند، و حال آنکه عزّت مخصوص خدا و رسول و اهل ایمان است (و ذلّت خاصّ کافران) ولیکن منافقان از این معنی آگه نیستند.'),
(5197, 63, 9, 'الا ای اهل ایمان، مبادا هرگز مال و فرزندانتان شما را از یاد خدا غافل سازند، و البته کسانی که به امور دنیا از یاد خدا غافل شوند آنها به حقیقت زیانکاران عالمند.'),
(5198, 63, 10, 'و از آنچه روزی شما کردیم (در راه خدا) انفاق کنید پیش از آنکه مرگ بر یکی از شما فرا رسد و در آن حال (به حسرت) بگوید: پروردگارا، چرا اجل مرا اندکی تأخیر نینداختی تا صدقه و احسان بسیار کنم و از نکوکاران شوم؟!'),
(5199, 63, 11, 'و خدا هرگز اجل هیچ کس را از وقتش که فرا رسد تأخیر نیفکند و خدا به هر چه کنید آگاه است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'هر چه در آسمانها و زمین است همه به تسبیح و ستایش خدا مشغولند که سلطنت ملک هستی و ستایش (اهل عالم) برای اوست و او بر هر چیز تواناست.'),
(5201, 64, 2, 'اوست خدایی که شما را آفرید، باز شما بندگان فرقه‌ای کافر ناسپاس و بعضی مؤمن خداشناس هستید و خدا به هر چه کنید کاملاً بیناست.'),
(5202, 64, 3, 'خدا آسمانها و زمین را به حق (و نظم احسن اتقن) آفرید و شما آدمیان را به زیباترین صورت برنگاشت، و بازگشت همه خلایق به سوی اوست.'),
(5203, 64, 4, 'او آنچه را که در آسمانها و زمین است و آنچه را که شما پنهان و آشکار کنید همه را می‌داند و خدا به اسرار دلها هم داناست.'),
(5204, 64, 5, 'آیا حکایت حال پیشینیان که کافر شدند به شما نرسید؟ که (هم در دنیا) به کیفر کردارشان رسیدند و (هم در آخرت) عذاب دردناک بر آنان مهیّاست.'),
(5205, 64, 6, 'این (عذاب) برای آن بود که رسولان آنها با ادلّه و معجزات به سویشان می‌آمدند و آنها (به تکذیب و طعن) می‌گفتند: آیا بشرهایی (مانند ما) رهنمایی ما توانند کرد؟ پس کافر شدند و روی گردانیدند و البته خدا (از کفر و ایمان خلق) بی‌نیاز است که خدا غنیّ بالذّات و ستوده به جمیع صفات است.'),
(5206, 64, 7, 'کافران گمان کردند که هرگز (پس از مرگ) برانگیخته نمی‌شوند. بگو: چرا، به خدای من سوگند که البته برانگیخته می‌شوید و سپس به (نتیجه) اعمال خود آگاهتان گردانند و این کار بر خدا بسیار آسان است.'),
(5207, 64, 8, 'پس به خدا ایمان آرید و به رسول او و نور علم و حکمتی که (بندگان را در آیات قرآن) فرستاده‌ایم بگروید، و (بترسید که) خدا به هر چه کنید آگاه است.'),
(5208, 64, 9, '(یاد آرید) روزی که خدا همه شما را به عرصه محشر (برای حساب) جمع می‌گرداند و آن روز روز غبن و پشیمانی (بدکاران) است. و هر که به خدا ایمان آرد و نیکوکار شود خدا گناهانش بپوشد و در باغهای بهشتی که زیر درختانش نهرها جاری است داخل گرداند که در آن بهشت ابداً جاودان متنعّم باشند و این به حقیقت سعادت و رستگاری بزرگ خواهد بود.'),
(5209, 64, 10, 'و آنان که کافر شدند و آیات ما را تکذیب کردند آنها اهل آتش دوزخ و در آن جاودان مخلّدند و آنجا بسیار بد منزلگاهی است.'),
(5210, 64, 11, 'هیچ رنج و مصیبتی (به شما) نرسد مگر به فرمان خدا، و هر که به خدا ایمان آرد خدا دلش را (به مقام عالی رضا و تسلیم) هدایت کند و خدا به همه امور عالم داناست.'),
(5211, 64, 12, 'و فرمان خدا و هم اوامر رسول حق را اطاعت کنید که اگر روی بگردانید بر رسول ما جز تبلیغ رسالت (و اتمام حجت) با بیان روشن تکلیفی نخواهد بود.'),
(5212, 64, 13, 'خدای یکتاست که جز او خدایی نیست و تنها بر خدا اهل ایمان توکل باید کنند.'),
(5213, 64, 14, 'الا ای اهل ایمان، زنان و فرزندان شما هم برخی (که شما را از طاعت و جهاد و هجرت در دین باز دارند) دشمن شما هستند، از آنان حذر کنید، و اگر (از عقاب آنها پس از توبه آنان) عفو و آمرزش و چشم‌پوشی کنید خدا هم (در حق همه شما) بسیار آمرزنده و مهربان است.'),
(5214, 64, 15, 'به حقیقت، اموال و فرزندان شما اسباب فتنه و امتحان شما هستند (چندان به آنها دل نبندید) و (بدانید که) نزد خدا اجر عظیم (بهشت ابد) خواهد بود.'),
(5215, 64, 16, 'پس تا بتوانید خدا ترس و پرهیزکار باشید و (سخن حق) بشنوید و اطاعت کنید و از مال خود برای (ذخیره آخرت) خویش (به فقیران) انفاق کنید. و کسانی که از خوی لئامت و بخل نفس خود محفوظ مانند آنها به حقیقت رستگاران عالمند.'),
(5216, 64, 17, 'اگر به خدا (یعنی بندگان محتاج خدا) قرض نیکو دهید خدا برای شما چندین برابر گرداند و هم از گناه شما در گذرد و خدا بر شکر و احسان خلق نیکو پاداش دهنده است و (بر گناهشان) بسیار بردبار است.'),
(5217, 64, 18, 'او دانای عوالم غیب و شهود و سلطان مقتدر ملک وجود و آگاه از صلاح نظام آفرینش است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'الا ای پیغمبر گرامی (امت را بگو) هر گاه زنان را طلاق دهید به وقت عدّه آنها (یعنی در زمان پاکی از خون حیض که در آن پاکی هم نزدیکی با آنها نکرده‌اید) طلاق دهید و زمان عدّه را (مدت سه پاکی) بشمارید، و از خدا که آفریننده شماست بترسید. و آن زنان را (تا در عدّه‌اند) از خانه بیرون مکنید و آنان نیز (بدون ضرورت) بیرون نروند مگر آنکه کار زشتی آشکار مرتکب شوند. و این احکام حدود شرع خداست و هر کس از حدود الهی تجاوز کند به خویشتن ستم کرده است. تو ندانی شاید خدا (پس از طلاق) کاری از نو پدید آرد (یعنی میل رجوع در شما حادث گرداند).'),
(5219, 65, 2, 'آن‌گاه که (نزدیک است) مدّت عدّه را به پایان رسانند باز یا به نیکویی نگاهشان دارید یا به خوشرفتاری رهاشان کنید و نیز (بر طلاق آنها) دو مرد مسلمان عادل گواه گیرید، و (شما شاهدان نیز) برای خدا شهادت را برپا کنید. بدین وعظ الهی آن کس که به خدا و روز قیامت ایمان آورد پند داده می‌شود، و هر کس خدا ترس و پرهیزکار شود خدا راه بیرون شدن (از عهده گناهان و بلا و حوادث سخت عالم) را بر او می‌گشاید.'),
(5220, 65, 3, 'و از جایی که گمان نَبَرد به او روزی عطا کند، و هر که بر خدا توکل کند خدا او را کفایت خواهد کرد که خدا امرش را نافذ و روان می‌سازد و بر هر چیز قدر و اندازه‌ای مقرّر داشته است (و به هیچ تدبیری سر از تقدیرش نتوان پیچید).'),
(5221, 65, 4, 'و از زنان شما آنان که از حیض (و فرزند، به ظاهر) نومیدند اگر باز شک (در تحقق سنّ یأسشان) دارید عدّه (طلاق) آنان سه ماه است و نیز زنانی که حیض ندیده (لیکن در سنّ حیض باشند آنها هم سه ماه عدّه نگه دارند)، و زنان حامله مدّت عدّه‌شان تا وقت زاییدن است. و هر که متّقی و خداترس باشد خدا (مشکلات) کار او را آسان می‌گرداند.'),
(5222, 65, 5, 'این احکام فرمان خداست که به شما نازل کرد و هر که از خدا بترسد خدا گناهانش بپوشد و او را پاداش بزرگ (بهشت ابد) عطا کند.'),
(5223, 65, 6, '(زنانی که طلاق رجعی دهید تا در عدّه‌اند) آنها را در همان منزل خویش که میسّر شماست بنشانید و به ایشان (در نفقه و سکنی) آزار و زیان نرسانید تا آنان را در مضیقه و رنج در افکنید (که به ناچار از حق خود بگذرند) و به زنان مطلّقه اگر حامله باشند تا وقت وضع حمل نفقه دهید آن‌گاه اگر فرزند شما را شیر دهند اجرتشان را بپردازید و (در کار فرزند) میان خود به نیکی مشورت و سازش کنید. و اگر با هم سختگیری کنید (مادر صرف‌نظر کند تا) زن دیگری او را شیر دهد.'),
(5224, 65, 7, 'مرد دارا به وسعت و فراوانی نفقه زن شیرده دهد، و آن که نادار و تنگ معیشت است از آنچه خدا به او داده انفاق کند. که خدا هیچ کس را جز به مقدار آنچه (توانایی) داده تکلیف نمی‌کند، و خدا به زودی بعدِ هر سختی آسانی قرار دهد.'),
(5225, 65, 8, 'و چه بسیار مردم دیاری که از امر خدا و رسولانش سرپیچیدند (و کافر شدند) ما هم آنان را به حساب سخت مؤاخذه کردیم و به عذاب بسیار شدید معذّب ساختیم.'),
(5226, 65, 9, 'تا کیفر کردار خود چشیدند و عاقبت کارشان زیانکاری (و حسرت) گردید.'),
(5227, 65, 10, '(و) خدا بر آنان عذاب سخت (جهنم) مهیّا ساخت، پس شما ای خردمندانی که ایمان آورده‌اید از خدا بترسید (و راه طاعت پیش گیرید) که خدا برای (هدایت) شما ذکر (یعنی قرآن) نازل کرد.'),
(5228, 65, 11, '(و نیز فرستاد) رسول بزرگواری را که برای شما آیات روشن بیان خدا را تلاوت می‌کند تا آنان را که ایمان آورده و نیکوکار شدند از ظلمات (جهل) به سوی نور (معرفت و ایمان) باز آرد، و هر کس به خدا ایمان آورده و نیکوکار شود خدا او را در باغهای بهشتی در آرد که نهرها زیر درختانش جاری است و همیشه در آن بهشت ابد متنعّمند، که خدا (در آنجا) رزق بسیار نیکو بر او مهیّا ساخته است.'),
(5229, 65, 12, 'خدا آن کسی است که هفت آسمان را آفرید و مانند آن آسمان‌ها از (هفت طبقه) زمین خلق فرمود؛ امر نافذ او در بین هفت آسمان و زمین نازل می‌شود تا بدانید که خدا بر هر چیز توانا و به احاطه علمی بر همه امور عالم آگاه است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ای پیغمبر گرامی برای چه آن را که خدا بر تو حلال فرمود تو بر خود حرام می‌کنی تا زنانت را از خود خشنود سازی؟ در صورتی که خدا بسیار آمرزنده و مهربان است (بر هیچ کس خصوص بر تو رسول گرامیش سخت نخواهد گرفت. در تفسیر وارد است که روزی حَفْصه با اجازه رسول خدا صلّی اللّه علیه و آله و سلّم به خانه پدرش عمر رفت، پیغمبر با ماریه در حجره حَفْصه خلوت کرد. ناگه او رسید و غوغا انگیخت که تو در حجره من با کنیزی خلوت کردی و آبروی مرا نزد زنانت بردی. حضرت برای خشنودی او فرمود: من ماریه را بر خود حرام کردم ولی این سرّ نزد تو است با هیچ کس مگوی. او به فور عایشه را هم آگه ساخت، او هم با رسول راجع به ماریه گفتگو کرد. حضرت سوگند خورد که ماریه را ترک گوید. آن دو زن شاد شدند و این آیه نازل گردید).'),
(5231, 66, 2, 'خدا مقرر کرد برای شما که سوگندهای خود را (به کفّاره) بگشایید، و خدا مولای شما بندگان (و حکمش نافذ) است و هم او (به هر چیز عالم) دانا و به حکمت (تمام امور خلق) آگاه است.'),
(5232, 66, 3, 'وقتی پیغمبر با بعضی زنان خود (یعنی با حفصه راجع به ماریه یا راجع به ریاست ابوبکر و عمر) سخنی به راز گفت و آن زن (چون خیانت کرده و دیگری یعنی عایشه را) بر سرّ پیغمبر آگه ساخت و خدا به رسولش خبر داد او بر آن زن برخی را اظهار کرد و به رویش آورد و برخی را پرده‌داری نمود و اظهار نکرد؛ چون او را از افشاء آن سرّ آگاه ساخت آن زن گفت: رسولا، تو را که واقف ساخت (که من سرّ تو بر کسی فاش کرده‌ام)؟ رسول گفت: مرا خدای دانای آگاه خبر داد.'),
(5233, 66, 4, 'اینک اگر هر دو زن به درگاه خدا توبه کنید (رواست که) البته دلهای شما (خلاف رضای پیغمبر) میل کرده است، و اگر با هم بر آزار او اتفاق کنید خدا یار و نگهبان اوست و جبریل امین و مردان صالح با ایمان (یعنی علی علیه السّلام به روایت عامّه و خاصّه) و فرشتگان حق یار و مددکار اویند.'),
(5234, 66, 5, 'امید است که اگر پیغمبر شما را طلاق داد خدا زنانی بهتر از شما به جایتان با او همسر کند که همه با مقام تسلیم و ایمان و خضوع اطاعت کنند و اهل توبه و عبادت باشند و رهسپار (طریق معرفت)، چه بکر چه غیر بکر.'),
(5235, 66, 6, 'ای کسانی که ایمان آورده‌اید، خود را با خانواده خویش از آتش دوزخ نگاه دارید چنان آتشی که مردم (دل‌سخت کافر) و سنگ (خارا) آتش‌افروز اوست و بر آن دوزخ فرشتگانی بسیار درشت‌خو و دل‌سخت مأمورند که هرگز نافرمانی خدا را (در اجرای قهر و غضب حق) نخواهند کرد و آنچه به آنها حکم شود انجام دهند.'),
(5236, 66, 7, '(به کافران گویند) ای کافران، امروز عذرخواهی مکنید (که پذیرفته نیست) تنها آنچه (در دنیا) می‌کرده‌اید به آن مجازات می‌شوید.'),
(5237, 66, 8, 'الا ای مؤمنان، به درگاه خدا توبه نصوح (با خلوص و دوام) کنید، باشد که پروردگارتان گناهانتان را مستور گرداند و شما را در باغهای بهشتی که زیر درختانش نهرها جاری است داخل کند در آن روزی که خدا پیغمبر خود و گرویدگان به او را ذلیل نسازد (بلکه عزیز و سرفراز ابد گرداند، در آن روز) نور (ایمان و عبادت) آنها در پیش رو و سمت راست ایشان می‌رود (و راه بهشتشان می‌نماید) و در آن حال (به شوق و نشاط) گویند: پروردگارا، تو نور ما را به حد کمال رسان و ما را (به لطف و کرم خود) ببخش که البته تو بر هر چیز توانایی.'),
(5238, 66, 9, 'الا ای رسول گرامی، اینک با کفار و منافقان به جهاد و کارزار پرداز و بر آنها (تا ایمان نیاورده‌اند) سخت گیر و (بدان که) مأوای آنها دوزخ است که بسیار بد منزلگاهی است.'),
(5239, 66, 10, 'خدا برای کافران زن نوح و زن لوط را مثال آورد که تحت (فرمان) دو بنده صالح ما بودند و به آنها (نفاق و) خیانت کردند و آن دو شخص (با وجود مقام نبوت) نتوانستند آنها را از (قهر) خدا برهانند و به آنها حکم شد که با دوزخیان در آتش درآیید.'),
(5240, 66, 11, 'و باز خدا برای مؤمنان (آسیه) زن فرعون را مَثَل آورد هنگامی که (از شوهر کافرش بیزاری جست و) عرض کرد: بار الها (من از قصر فرعونی و عزت دنیوی او گذشتم) تو خانه‌ای برای من نزد خود در بهشت بنا کن و مرا از شرّ فرعون (کافر) و کردارش و از قوم ستمکار نجات بخش.'),
(5241, 66, 12, 'و نیز مریم دخت عمران را (مَثَل آورد) که رَحِمش را پاکیزه داشت و ما در آن از روح قدسی خویش بدمیدیم، و او کلمات پروردگار خود و کتب آسمانی او را (با کمال ایمان) تصدیق کرد و از بندگان مطیع خدا به شمار بود.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بزرگوار خدایی که سلطنت ملک هستی به دست قدرت اوست و بر همه چیز تواناست.'),
(5243, 67, 2, 'خدایی که مرگ و زندگانی را آفرید که شما بندگان را بیازماید تا کدام نیکوکارتر (و خلوص اعمالش بیشتر) است و او مقتدر و بسیار آمرزنده است.'),
(5244, 67, 3, 'آن خدایی که هفت آسمان بلند را به طبقاتی منظم بیافرید و هیچ در نظم خلقت خدای رحمان بی‌نظمی و نقصان نخواهی یافت، باز بارها به دیده عقل بنگر تا هیچ نقص و خلل هرگز در آن توانی یافت؟'),
(5245, 67, 4, 'باز دوباره به چشم بصیرت دقت کن تا دیده خرد زبون و خسته (نقصی نیافته) به سوی تو باز گردد.'),
(5246, 67, 5, 'و ما آسمان دنیا را به چراغهای انجم رخشان زیب و زیور دادیم و به تیر شهاب آن ستارگان شیاطین را راندیم و عذاب آتش فروزان را بر آنها مهیّا ساختیم.'),
(5247, 67, 6, 'و بر آنان که به خدای خود کافر شدند عذاب جهنّم که بسیار بد منزلگاهی است مهیّاست.'),
(5248, 67, 7, 'که چون به آن جهنّم در افتند فریاد منکری چون شهیق خران از آتش چون دیگ جوشان می‌شنوند.'),
(5249, 67, 8, '(دوزخ از خشم کافران) نزدیک است شکافته و قطعه قطعه شود، هر فوجی را که به آتش در افکنند خازنان جهنّم از آنها پرسند: آیا پیغمبری برای راهنمایی شما نیامد؟'),
(5250, 67, 9, 'آنها در جواب گویند: آری، ما را رسول بیم دهنده حق آمد ولی ما تکذیب او کردیم و گفتیم که خدا چیزی (از آسمان) نفرستاده و جز اینکه شما رسولان سخت به گمراهی و ضلالتید هیچ نیست.'),
(5251, 67, 10, 'و گویند: اگر ما در دنیا (سخن انبیا) می‌شنیدیم یا به دستور عقل رفتار می‌کردیم (امروز) از دوزخیان نبودیم.'),
(5252, 67, 11, 'آنجا به گناه خود معترف شوند (که سودی ندارد و خطاب قهر فرا رسد) که اهل آتش افروخته (قهر، از رحمت حق) دور باد.'),
(5253, 67, 12, 'همانا آنان که از خدای خود در پنهان می‌ترسند آنها را آمرزش و پاداش بزرگ (بهشت ابد) خواهد بود.'),
(5254, 67, 13, 'و شما سخن پنهان گویید یا آشکار، در علم حق یکسان است، که خدا به اسرار دلها هم البته داناست.'),
(5255, 67, 14, 'آیا آن خدایی که خلق را آفریده عالم به اسرار آنها نیست؟ و حال آنکه او بر باطن و ظاهر همه امور عالم آگاه است.'),
(5256, 67, 15, 'او آن خدایی است که زمین را برای شما نرم و هموار گردانید پس شما در پست و بلندی‌های آن حرکت کنید و از روزی او خورید و (بدانید که) بازگشت همه خلایق به سوی اوست.'),
(5257, 67, 16, 'آیا از (قهر) خدایی که در آسمان مقتدر و حکمفرماست ایمنید که شما را به زمین فرو برد در حالی که زمین به موج و اضطراب در افتد؟'),
(5258, 67, 17, 'آیا از قهر خدای مقتدری که در (زمین و) آسمان حکمفرماست ایمنید که تندبادی بفرستد و بر سر شما سنگ ببارد تا بدانید که وعده عذاب من چگونه است؟'),
(5259, 67, 18, 'و همانا امم پیش از اینها نیز (رسولان ما را) تکذیب کردند پس (بنگرید) چگونه سخت آنها را هلاک کردم.'),
(5260, 67, 19, 'آیا مرغان هوا را نمی‌نگرند که بالای سرشان پر گشوده، گاه بی‌حرکت و گاه با حرکت بال پرواز می‌کنند؟ کسی جز خدای مهربان آنها را (در فضا) نگاه نمی‌دارد، که او به احوال همه موجودات کاملا بیناست.'),
(5261, 67, 20, 'آیا آن کیست که سپاه و مددکار شما باشد و همه‌گونه شما را در برابر خدای مهربان یاری تواند کرد؟ پس کافران جز در غرور و فریبی بیش نیستند.'),
(5262, 67, 21, 'آیا آن کیست که اگر خدا از شما رزق خود باز گیرد او روزی به شما تواند داد؟ بلکه کافران دانسته در طغیان و عصیان لجاج ورزند و از حق اعراض می‌کنند.'),
(5263, 67, 22, 'آیا آن کس که (در کفر و جهالت) سرنگون به رو در افتاده یا آن که با قامت راست به راه راست (ایمان) است کدام بهتر هدایت یافته‌اند؟'),
(5264, 67, 23, 'بگو: اوست خدایی که شما را از نیستی به هستی آورد و دو گوش و چشم (شنوا و بینا) و دل (هوشیار) به شما عطا کرد (تا شکر نعمتش گویید) حال آنکه بسیار کم از نعم او شکرگزاری می‌کنید.'),
(5265, 67, 24, 'بگو: اوست خدایی که شما را در زمین (از خاک) برانگیخت و (پس از مرگ) باز به سوی او محشور می‌شوید.'),
(5266, 67, 25, 'و کافران (به تمسخر) گویند: این وعده قیامت کی خواهد بود اگر شما راست می‌گویید؟'),
(5267, 67, 26, 'بگو: علم آن تنها نزد خداست و من منحصرا وظیفه‌ام این است که شما را (از عذاب حق) بترسانم.'),
(5268, 67, 27, 'که چون کافران آن عذاب سخت را نزدیک خود به چشم مشاهده کنند رخسار آنها (از خوف) زشت و سیاه شود و به آنها گویند: این همان عذابی است که جدّا درخواست می‌کردید.'),
(5269, 67, 28, 'بگو: چه تصور می‌کنید؟ اگر خدا من و مؤمنانی که با منند همه را هلاک کند یا لطف و رحمت فرماید آیا آنان که کافرند چه کسی از عذاب دردناک (دوزخ) نجاتشان خواهد داد؟'),
(5270, 67, 29, 'بگو: (آن که من شما را به سوی او می‌خوانم) او خدای مهربان است که ما به او ایمان آورده و بر او توکل کردیم و به زودی شما را معلوم شود کیست که آشکارا به ضلالت و گمراهی است؟'),
(5271, 67, 30, 'بگو: چه تصوّر می‌کنید؟ اگر آب (که مایه زندگانی) شما (است) همه به زمین فرو رود (جز خدا) کیست که باز آب روان و گوارا برای شما پدید آرد؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'ن (قسم به نون که شاید نام نور و ناصر حق یا لوح نور خداست) و قسم به قلم (علم فعلی ازلی) و آنچه (تا ابد در لوح محفوظ عالم) خواهند نگاشت.'),
(5273, 68, 2, 'که تو به لطف و رحمت پروردگارت هرگز دیوانه نیستی.'),
(5274, 68, 3, 'و البته تو را پاداشی نامحدود است.'),
(5275, 68, 4, 'و در حقیقت تو به نیکو خلقی عظیم آراسته‌ای.'),
(5276, 68, 5, 'و به زودی تو و مخالفانت مشاهده می‌کنید.'),
(5277, 68, 6, 'که از شما کدام یک (به اوهام و خیالات باطل) مفتون و دیوانه‌اند.'),
(5278, 68, 7, 'البته خدایت داناتر است که از راه خدا گمراه کیست و بهتر می‌داند که هدایت یافتگان عالم کیانند.'),
(5279, 68, 8, 'پس هرگز از مردم کافری که آیات خدا را تکذیب می‌کنند پیروی مکن.'),
(5280, 68, 9, 'کافران بسیار مایلند که تو با آنها مداهنه و مداراکنی (و متعرض بتهاشان نشوی) تا آنها هم (به نفاق) با تو مداهنه و مدارا کنند.'),
(5281, 68, 10, 'و تو هرگز اطاعت مکن احدی از منافقان دون را که دایم (به دروغ) سوگند می‌خورند.'),
(5282, 68, 11, 'و آن کس که دایم عیبجویی و سخن‌چینی می‌کند.'),
(5283, 68, 12, 'و خلق را هر چه بتواند از خیر باز می‌دارد و به ظلم و بدکاری می‌کوشد.'),
(5284, 68, 13, 'با این همه عیب، باز متکبر است و خشن با آنکه حرامزاده و بی‌اصل و نسب است.'),
(5285, 68, 14, '(پیرو چنین کسی) برای این که مال و فرزندان بسیار دارد (نباید شد).'),
(5286, 68, 15, 'در صورتی که چون آیات ما را بر او تلاوت کنند گوید: این سخنان افسانه پیشینیان است.'),
(5287, 68, 16, 'به زودی بر خرطوم و بینی‌اش داغ (شمشیر) نهیم. (این آیات در حقّ ولید مغیره عمّ ابو جهل که روز بدر بر حسب وعده حق به شمشیر علی علیه السلام بینی‌اش بشکافت نازل آمد ولی بر هر که مانند اوست تا ابد شامل است).'),
(5288, 68, 17, 'ما کافران را (به قحط و سختی) آزمودیم چنانکه اهل آن بستان را آزمودیم، که قسم خوردند که صبحگاه میوه‌اش را بچینند (تا فقیران آگاه نشوند).'),
(5289, 68, 18, 'و هیچ استثناء نکردند (ان شاء اللَه نگفتند و به خواست خدا معتقد نبودند).'),
(5290, 68, 19, 'بدین سبب همان شب هنوز به خواب بودند که از جانب خدا آتش عذابی نازل شد.'),
(5291, 68, 20, 'و بامدادان درختهای آن بستان چون خاکستری سیاه گردید.'),
(5292, 68, 21, 'صبحگاه یکدیگر را صدا کردند.'),
(5293, 68, 22, 'که برخیزید اگر میوه بستان را خواهید چید به بستانتان روید.'),
(5294, 68, 23, 'آنها سوی بستان روان شده و آهسته سخن می‌گفتند.'),
(5295, 68, 24, 'که امروز مواظب باشید فقیری در این باغ بر شما وارد نشود.'),
(5296, 68, 25, 'و صبحدم با شوق و عزم و توانایی (بر منع فقیران) به باغ رفتند.'),
(5297, 68, 26, 'چون باغ را به آن حال دیدند با خود گفتند: (باغ ما نه اینست) ما یقین راه را گم کرده‌ایم؟'),
(5298, 68, 27, 'یا بلکه (باغ همان است و) ما (به قهر خدا از میوه‌اش) محروم شده‌ایم؟'),
(5299, 68, 28, 'یک نفر بهترین اولاد و عادلترین‌شان به آنها گفت: من به شما نگفتم چرا (شکر نعمت و) تسبیح و ستایش خدا را بجا نیاورید؟'),
(5300, 68, 29, 'آنان همه گفتند: خدای ما (از ظلم) منزّه است، آری ما خود (در حق خویش) ستم کردیم.'),
(5301, 68, 30, 'و رو به یکدیگر کرده به ملامت و نکوهش هم پرداختند.'),
(5302, 68, 31, 'گفتند: ای وای بر ما که سخت سرکش بودیم.'),
(5303, 68, 32, 'اینک امیدواریم که پروردگار ما به جای آن، بستان بهتری به ما عطا کند، که (از این پس) ما همیشه به خدای خود مشتاقیم.'),
(5304, 68, 33, 'این گونه است عذاب (دنیا) و البته عذاب آخرت بسیار سخت‌تر است اگر مردم بدانند.'),
(5305, 68, 34, '(در آخرت) برای متقیان نزد پروردگارشان باغهای بهشت پرنعمت خواهد بود.'),
(5306, 68, 35, 'آیا هرگز اهل اسلام را با کافران بدکار یکسان قرار می‌دهیم؟'),
(5307, 68, 36, 'شما را چیست؟ چگونه شما (این چنین) حکم (ظالمانه) می‌کنید؟'),
(5308, 68, 37, 'آیا شما را کتابی است (آسمانی) که در آن این حکم می‌خوانید؟'),
(5309, 68, 38, 'که هر آنچه به هوای نفس بخواهید شما را فراهم است؟'),
(5310, 68, 39, 'یا شما را بر ما (به عدم عقاب) عهد و سوگند دائمی تا روز قیامت است که هر چه حکم فرمایید به یقین بر شما فراهم است؟'),
(5311, 68, 40, 'از آنان بپرس تا کدام ضامن این پیمانند؟'),
(5312, 68, 41, 'یا آنها گواهانی (بر دعوی خود) دارند؟ اگر راست می‌گویند آن گواهان را حاضر کنند.'),
(5313, 68, 42, 'در آن روز بسیار سخت به سجده خدا خوانده شوند و نتوانند.'),
(5314, 68, 43, 'چشمانشان ذلیل و ترسان، ذلّت و حسرت آنها را فرا گرفته و از این پیش (در دنیا) با تن سالم به سجده خدا آنها را می‌خواندند (ولی اجابت نکردند).'),
(5315, 68, 44, '(ای رسول، تو کیفر) مکذّبان و منکران قرآن را به من واگذار که ما آنها را از آنجا که نفهمند (و گمان نعمت کنند) به عذاب سخت در افکنیم.'),
(5316, 68, 45, 'و آنها را مهلت دهم که البته کید من قوی و بسیار سخت است.'),
(5317, 68, 46, 'آیا تو از کافران امت مزد رسالت خواهی که بار غرامتش بر آنها گران است؟'),
(5318, 68, 47, 'یا آنکه از عالم غیب آگاهند تا از آنجا چیزی بر نویسند؟'),
(5319, 68, 48, 'باز تو برای حکم خدای خود صبر کن و مانند صاحب ماهی (یونس که از خشم در عذاب امت تعجیل کرد) مباش که (عاقبت پشیمان شد و در ظلمت زندان شکم ماهی افتاد و) به حال غم و اندوه (خدا را بر نجات خود) خواند.'),
(5320, 68, 49, 'که اگر لطف و رحمت پروردگارش در نیافتی با نکوهش به صحرای بی‌آب و گیاه در افتادی (و هلاک شدی).'),
(5321, 68, 50, 'باز (چون به درگاه حق نالید) خدایش برگزید و از صالحانش گردانید.'),
(5322, 68, 51, 'البته نزدیک است که کافران به چشمان (بد) خود (چشم زخمت زنند و) از پایت در آورند، آن گاه که آیات قرآن بشنوند و (چون در فصاحتش حیرت کنند از شدت حسد) گویند که این شخص عجب دیوانه است.'),
(5323, 68, 52, 'در حالی که این کتاب الهی جز ذکر و پند و حکمت برای عالمیان هیچ نیست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'قیامت آن روز حق و حقیقت است (که حقوق خلق و حقایق امور در آن به ظهور رسد).'),
(5325, 69, 2, 'آن روز حق و حقیقت چه (روز هولناک سختی) است؟'),
(5326, 69, 3, 'چگونه سختی و عظمت آن روز را درک توانی کرد؟'),
(5327, 69, 4, 'قوم عاد و ثمود آن روز کوبنده را تکذیب کردند.'),
(5328, 69, 5, 'اما قوم ثمود به کیفر طغیان هلاک شدند.'),
(5329, 69, 6, 'اما قوم عاد نیز به بادی تند و سرکش به هلاکت رسیدند.'),
(5330, 69, 7, 'که آن باد تند را خدا هفت شب و روز پی در پی بر آنها مسلط کرد که دیدی آن مردم گویی ساقه نخل خشکی بودند که به خاک در افتادند.'),
(5331, 69, 8, 'آیا هیچ بینی که (دیگر به روزگار) از آنان اثری باشد؟'),
(5332, 69, 9, 'و فرعون و اقوام پیش از او و مردم آن آبادیهای زیر و رو شده (یعنی قوم زشتکار لوط) به کفر و خطاکاری برخاستند.'),
(5333, 69, 10, 'و با رسول پروردگارشان مخالفت کردند، خدا هم آنان را به عذابی سخت گرفتار ساخت.'),
(5334, 69, 11, '(شما مردم فرزندان نوحید که) ما چون طوفان دریا طغیان کرد شما را به کشتی نشاندیم (و نجاتتان دادیم).'),
(5335, 69, 12, 'تا آن (غرق کفار و نجات مؤمنان) را مایه پند و عبرت شما مردم قرار دهیم و لیکن گوش شنوای هوشمندان این پند و تذکر را تواند فرا گرفت.'),
(5336, 69, 13, 'باز (به یاد آر) چون در صور (اسرافیل) یک بار بدمند.'),
(5337, 69, 14, 'و زمین و کوهها را بر دارند و یک مرتبه همه را خرد و متلاشی سازند.'),
(5338, 69, 15, 'آن‌گاه آن واقعه بزرگ قیامت واقع گردد.'),
(5339, 69, 16, 'و بنای مستحکم آسمان (از دهشت و عظمت) آن روز سست شود و سخت درهم شکافد.'),
(5340, 69, 17, 'و فرشتگان بر اطراف آسمان (منتظر فرمان حق) باشند و عرش پروردگارت را در آن روز هشت ملک مقرّب بر فراز سرشان (یا فراز سر مردم) بردارند.'),
(5341, 69, 18, 'آن روز که (در پیشگاه حساب) شما را حاضر کنند هیچ کار از اسرار مخفی شما هم پنهان نخواهد ماند.'),
(5342, 69, 19, 'اما کسی که نامه اعمال او را به دست راستش دهند (با کمال نشاط و شادمانی و سربلندی به اهل محشر) گوید: بیایید نامه مرا بخوانید.'),
(5343, 69, 20, 'من ملاقات این روز حسابم را اعتقاد داشتم (و در دنیا به گناه نپرداختم).'),
(5344, 69, 21, 'این چنین کس در عیش و زندگانی خوش خواهد بود.'),
(5345, 69, 22, 'در بهشت عالی رتبه (ابدی).'),
(5346, 69, 23, 'که میوه‌های آن همیشه در دسترس (بهشتیان) است.'),
(5347, 69, 24, '(و خطاب رسد که) از طعام و شرابهای لذیذ و گوارای بهشتی هر چه خواهید تناول کنید، شما را گوارا باد، که این پاداش اعمال ایّام گذشته دنیاست که (بر امروز خویش) پیش فرستادید.'),
(5348, 69, 25, 'و اما آن کس که کتاب عملش به دست چپ دهند (با کمال شرمندگی و اندوه) گوید: ای کاش نامه مرا به من نمی‌دادند.'),
(5349, 69, 26, 'و من هرگز از حساب اعمالم آگاه نمی‌شدم.'),
(5350, 69, 27, 'ای کاش مرگ مرا از چنگ این غصه و عذاب نجات می‌داد.'),
(5351, 69, 28, '(ای داد که) مال و ثروت من امروز به فریاد من نرسید.'),
(5352, 69, 29, 'همه قدرت و حشمتم نابود گردید.'),
(5353, 69, 30, '(و خطاب قهر رسد که) او را بگیرید و در غل و زنجیر کشید.'),
(5354, 69, 31, 'تا بازش به دوزخ در افکنید.'),
(5355, 69, 32, 'آن‌گاه به زنجیری که طولش هفتاد ذراع است (به آتش) در کشید.'),
(5356, 69, 33, 'که او به خدای بزرگ ایمان نیاورده.'),
(5357, 69, 34, 'و هرگز مسکینی را بر سفره طعام خود به رغبت نخوانده است.'),
(5358, 69, 35, 'بدین سبب امروز هیچ خویش و دوستداری که به فریادش رسد در اینجا ندارد.'),
(5359, 69, 36, 'و طعامی غیر از غسلین (چرک و پلیدی دوزخیان) نصیبش نیست.'),
(5360, 69, 37, 'و آن طعام را کسی جز اهل دوزخ نمی‌خورد.'),
(5361, 69, 38, 'قسم به آنچه (از آثار حق) می‌بینید.'),
(5362, 69, 39, 'و آنچه نمی‌بینید.'),
(5363, 69, 40, 'که قرآن به حقیقت (وحی خدا و) کلام رسول بزرگواری است.'),
(5364, 69, 41, 'و آن نه سخن شاعری (و گفتار خیالی و موهومی) است (گر چه) اندکی از شما مردم به آن ایمان می‌آورید.'),
(5365, 69, 42, 'و نه حرف کاهن غیبگوست (گر چه) اندکی از شما مردم متذکر حقایق آن می‌شوید.'),
(5366, 69, 43, 'این قرآن تنزیل خدای عالمیان است.'),
(5367, 69, 44, 'و اگر (محمّد صلّی اللّه علیه و آله و سلّم) از دروغ به ما برخی سخنان را می‌بست،'),
(5368, 69, 45, 'محققا ما او را (به قهر و انتقام) از یمینش می‌گرفتیم.'),
(5369, 69, 46, 'و رگ و تینش را قطع می‌کردیم.'),
(5370, 69, 47, 'و شما هیچ یک بر دفاع از او قادر نبودید.'),
(5371, 69, 48, 'و این قرآن به حقیقت پند و تذکر پرهیزکاران عالم است.'),
(5372, 69, 49, 'و البته ما می‌دانیم که شما برخی تکذیب آن می‌کنید.'),
(5373, 69, 50, 'و تکذیبش عاقبت مایه ندامت کافران است.'),
(5374, 69, 51, 'و این حق و حقیقت محض است.'),
(5375, 69, 52, 'پس به نام خدای بزرگوار خود تسبیح گوی و آن را به ستایش یاد کن.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'سائلی از عذاب قیامت که وقوعش حتمی است (از رسول) پرسید (که آن عذاب چه مردمی راست).'),
(5377, 70, 2, '(بداند که) بر فرقه کافران است و هیچ کس از آنان دفع نتواند کرد.'),
(5378, 70, 3, 'که آن عذاب از (قهر) خدای مالک آسمانهاست (یا مراد عذاب واقع بر نعمان بن حارث است که به نصب خلافت علی علیه السلام بر رسول صلی اللّه علیه و آله و سلّم اعتراض کرد، رسول صلّی اللّه علیه و آله و سلّم فرمود: به امر خدا کردم. او گفت: اگر چنین است خدایا بر من عذابی فرست. فورا سنگی از آسمان بر فرقش رسید و هلاک شد).'),
(5379, 70, 4, 'فرشتگان و روح الامین (برای اخذ فرمان) به سوی (عرش) خدا بالا روند در روزی که مدتش پنجاه هزار سال خواهد بود.'),
(5380, 70, 5, 'پس صبر نیکو پیش گیر.'),
(5381, 70, 6, 'که این مردم آن روز را بسیار دور (از امکان) بینند.'),
(5382, 70, 7, 'و ما نزدیک (به وقوع) می‌بینیم.'),
(5383, 70, 8, 'روزی که آسمان چون فلز گداخته شود.'),
(5384, 70, 9, 'و کوهها مانند پشم زده متلاشی گردند.'),
(5385, 70, 10, 'و هیچ کس از خویش خود جویا نشود.'),
(5386, 70, 11, 'چون حقیقت حالشان به آنها بنمایند آن روز کافر بدکار آرزو کند که کاش توانستی فرزندانش را فدای خود سازد و از عذاب برهد.'),
(5387, 70, 12, 'و هم زن و برادرش.'),
(5388, 70, 13, 'و هم خویشان و قبیله‌اش را که همیشه حمایتش می‌کردند.'),
(5389, 70, 14, 'و هر که را روی زمین است خواهد همه را فدای خویش گردند تا مگر او را (از آن عذاب) برهاند.'),
(5390, 70, 15, 'و هرگز نجات نیابد که آتش دوزخ بر او شعله‌ور است.'),
(5391, 70, 16, 'تا سر و صورت و اندمش پاک بسوزد.'),
(5392, 70, 17, 'دوزخ کسی را می‌خواند که از خدا رو گردانیده و با حق مخالفت کرده.'),
(5393, 70, 18, 'و مال دنیا را جمع کرده و همه را ذخیره نموده است.'),
(5394, 70, 19, 'که انسان مخلوقی طبعا سخت حریص و بی‌صبر است.'),
(5395, 70, 20, 'چون شر و زیانی به او رسد سخت جزع و بی‌قراری کند.'),
(5396, 70, 21, 'و چون مال و دولتی به او رو کند منع (احسان) نماید.'),
(5397, 70, 22, 'مگر نماز گزاران حقیقی.'),
(5398, 70, 23, 'آنان که دایم در نماز (و طاعت الهی) عمر گذرانند.'),
(5399, 70, 24, 'و آنان که در مال و دایی خود حقّی معیّن و معلوم گردانند.'),
(5400, 70, 25, 'تا به فقیران سائل و فقیران آبرومند محروم رسانند.'),
(5401, 70, 26, 'و آنان که روز قیامت و جزا را تصدیق کنند.'),
(5402, 70, 27, 'و آنان که از قهر و عذاب خدای خویش سخت ترسانند.'),
(5403, 70, 28, 'که از عذاب خدا هیچ کس ایمن نتواند بود.'),
(5404, 70, 29, 'و آنان که اندام خود را (از شهوت رانی) نگاه می‌دارند.'),
(5405, 70, 30, 'مگر بر زنان حلال و کنیزان ملکی خویش که (از اعمال شهوت با آنها) هیچ ملامت ندارند.'),
(5406, 70, 31, 'و هر که غیر از این جوید اینان به حقیقت متعدی و ستمکارند.'),
(5407, 70, 32, 'و آنان که امانت و عهد و پیمانشان را رعایت کنند.'),
(5408, 70, 33, 'و آنان که برای گواهی به حق قیام کنند.'),
(5409, 70, 34, 'و آنان که نماز خود را (به وقت و شرایط و حضور قلب) محافظت کنند.'),
(5410, 70, 35, 'آنان در باغها (ی بهشت ابد) با عزّت و احترام متنعّمند.'),
(5411, 70, 36, '(ای رسول) چه شده است کافران را که (با تمسخر) به جانبت می‌شتابند؟!'),
(5412, 70, 37, 'از راست و چپ گروه گروه (باز پراکنده می‌شوند و به دین خدا نمی‌گروند).'),
(5413, 70, 38, 'آیا هر یک طمع دارند که باز آنها را در بهشت با ناز و نعمت داخل کنند؟!'),
(5414, 70, 39, 'هرگز این نشود (چون) آنها خود می‌دانند که ما آنها را از چه (نطفه پستی) آفریده‌ایم (باز به آفریننده خود ایمان نمی‌آورند و به کفر می‌شتابند و طمع آن دارند که چون مؤمنان به بهشت روند).'),
(5415, 70, 40, 'چنین نیست، سوگند به خدای مشرق‌ها و مغرب‌ها که ما قادریم.'),
(5416, 70, 41, 'که به جای آنها خلقی بهتر از آنها بیافرینیم و هرگز کسی (به قدرت و قوت) بر ما سبقت نخواهد یافت.'),
(5417, 70, 42, 'پس آنها را به کفر و ضلالت خود بگذار که به بازیچه دنیا دل بازند تا با روزی که وعده عذاب آنهاست رو به رو شوند.'),
(5418, 70, 43, 'آن روزی که به سرعت سر از قبرها برآورده، گویی به سوی بتها (و نتیجه پرستش غیر خدا) می‌شتابند.'),
(5419, 70, 44, 'در حالی که چشمهاشان (از هول و وحشت قیامت) به خواری فرو افتاده و ذلّت (کفر و عصیان) بر آنها احاطه کرده، این همان روزی است که به آنها وعده می‌دادند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به سوی قومش (به رسالت) فرستادیم که قوم خود را (از قهر خدا) به اندرز و پند بترسان از آنکه بر آنان عذاب دردناک فرا رسد.'),
(5421, 71, 2, 'نوح گفت: ای قوم، من برای شما رسولی هستم که شما را با بیانی روشن از عذاب قهر خدا می‌ترسانم.'),
(5422, 71, 3, '(و از راه خیر خواهی به شما می‌گویم) که خدای را به یکتایی بپرستید و از او بترسید و پرهیزکار باشید و مرا پیروی کنید.'),
(5423, 71, 4, 'تا خدا به لطف و کرم از گناهان شما در گذرد و اجلتان را تا وقت (عمر طبیعی) معیّن به تأخیر افکند، که اجل الهی چون وقتش فرا رسد اگر بدانید دگر هیچ تأخیر نیفتد.'),
(5424, 71, 5, '(و او به درگاه خدا نالید و) گفت: بارالها، من قوم خود را شب و روز دعوت کردم.'),
(5425, 71, 6, 'اما دعوت و نصیحتم جز بر فرار و اعراض آنها نیفزود.'),
(5426, 71, 7, 'و هر چه آنان را به مغفرت و آمرزش تو خواندم انگشت بر گوش نهادند و جامه به رخسار افکندند (تا مرا نبینند و سخنم را نشنوند) و (بر کفر) اصرار و لجاج ورزیدند و سخت راه تکبر و نخوت پیمودند.'),
(5427, 71, 8, 'باز هم آنها را به صدای بلند دعوت کردم.'),
(5428, 71, 9, 'و آن گاه آشکار و علنی خواندم و در خلوت و پنهانی خواندم (به هیچ وجه اثری نکرد).'),
(5429, 71, 10, 'باز گفتم: به درگاه خدای خود توبه کنید و آمرزش طلبید که او بسیار خدای آمرزنده‌ای است.'),
(5430, 71, 11, 'تا باران آسمان بر شما فراوان نازل کند.'),
(5431, 71, 12, 'و شما را به مال بسیار و پسران متعدد مدد فرماید و باغهای خرّم و نهرهای جاری به شما عطا کند.'),
(5432, 71, 13, 'چرا شما مردم خدای را به عظمت و وقار باور ندارید؟'),
(5433, 71, 14, 'و حال آنکه او شما را (از نطفه‌ای) به انواع خلقت و اطوار گوناگون (ارمتب آدمیّت) بیافرید.'),
(5434, 71, 15, 'آیا ندیدید که خدا چگونه هفت آسمان را به طبقاتی (بسیار منظم و محکم) خلق کرد،'),
(5435, 71, 16, 'و در آن آسمانها ماه شب را فروغی تابان و خورشید روز را چراغی فروزان ساخت؟'),
(5436, 71, 17, 'و خدا شما را مانند نباتات مختلف از زمین برویانید.'),
(5437, 71, 18, 'آن گاه بار دیگر (پس از مرگ) به زمین باز گرداند و دیگر بار هم شما را (از خاک به روز حساب) برانگیزد.'),
(5438, 71, 19, 'و خدا زمین را برای شما چون بساط (پرنعمت) بگسترانید.'),
(5439, 71, 20, 'تا در زمین راههای مختلف وسیع بپیمایید.'),
(5440, 71, 21, 'نوح گفت: پروردگارا (با این همه تبلیغ و دعوت و اتمام حجت باز) این قوم مرا مخالفت کردند و پیرو کسی (از ثروتمندان) شدند که او خود مال و فرزندش هم جز بر زیانش نیفزود.'),
(5441, 71, 22, 'و (بر ضد من) بزرگترین مکر و حیله به کار بردند.'),
(5442, 71, 23, 'و گفتند: هرگز خدایان خود را رها نکنید و به خصوص دست از پرستش (این پنج بت) ودّ و سواع و یغوث و یعوق و نسر هرگز برمدارید.'),
(5443, 71, 24, 'و آنها بسیاری از خلق را گمراه کردند، و تو ستمکاران را هیچ جز بر ضلالت و عذابشان میفزای.'),
(5444, 71, 25, 'آن قوم از کثرت کفر و گناه عاقبت به آب دریا غرق شدند و به آتش دوزخ در افتادند و جز خدا بر خود هیچ یار و یاری نیافتند.'),
(5445, 71, 26, 'و نوح عرض کرد: پروردگارا از این کافران دیّاری بر روی زمین باقی مگذار.'),
(5446, 71, 27, 'که اگر از آنها هر که را باقی گذاری بندگان (پاک با ایمان) تو را گمراه می‌کنند و فرزندی هم جز بدکار و کافر از آنان به ظهور نمی‌رسد.'),
(5447, 71, 28, 'بار الها، مرا و پدر و مادر من و هر که با ایمان به خانه (یا به کشتی) من داخل شود و همه مردان و زنان با ایمان عالم را ببخش و بیامرز و ستمکاران را جز بر هلاک و عذابشان میفزای.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, '(ای رسول ما) بگو: مرا وحی رسیده که گروهی از جنّیان (آیات قرآن را هنگام قرائت من) استماع کرده‌اند و پس از شنیدن گفته‌اند که ما از قرآن آیات عجیبی شنیدیم.'),
(5449, 72, 2, 'که به راه خیر و صلاح هدایت می‌کند، بدین سبب ما به آن ایمان آورده و دیگر هرگز به خدای خود مشرک نخواهیم شد.'),
(5450, 72, 3, 'و همانا بسیار بلند مرتبه است شأن و اقتدار پروردگار ما، او همجفت و فرزندی هرگز نگرفته (و از این آلایشهای جسمانی پاک و مبرّا) است.'),
(5451, 72, 4, 'و البته سفیهان و بی‌خردان ما به خدا دروغ می‌بستند (که به او فرزندی مسیح و عزیر و فرشتگان را نسبت دادند).'),
(5452, 72, 5, 'و ما چنین می‌پنداشتیم که هرگز هیچ قومی از جنّ و انس بر خدای متعال دروغ و افترا نخواهد گفت.'),
(5453, 72, 6, 'و همانا مردانی از نوع بشر به مردانی از گروه جنّ پناه می‌بردند (و خود را در بیابانها به جنّیان می‌سپردند) و جنّیان بر غرور و جهل آنها سخت می‌افزودند (آیه چنین تفسیر شده که زمان جاهلیّت عرب در بیابان پناه به بزرگ جنّیان آنجا می‌برد تا ایمن باشد آن‌گاه آسوده می‌خفت و مال و جانش به خطر می‌افتاد).'),
(5454, 72, 7, 'و آنها هم مانند شما (آدمیان گروهی کافر شده و) گمان کردند که خدا احدی را (به قیامت) زنده نخواهد کرد.'),
(5455, 72, 8, 'و جنّیان گفتند ما به آسمان بر شدیم (تا اسرار وحی را استراق کنیم) لیکن یافتیم که آنجا فرشته نگهبان با قدرت و تیر شهاب آتشبار فراوان است.'),
(5456, 72, 9, 'و ما (پیش از نزول قرآن) در کمین شنیدن سخنان آسمانی و اسرار وحی می‌نشستیم اما اینک هر که (از اسرار وحی الهی) سخنی بخواهد شنید تیر شهاب در کمینگاه اوست.'),
(5457, 72, 10, 'و ما هنوز نمی‌دانیم که آیا عاقبت برای مردم زمین (با این مذاهب و اعمال مختلف که دارند) شرّ و فساد خواسته‌اند یا که خدای آنها بر هدایت آنان رشد و خیر و صلاح آنها را منظور دارد؟'),
(5458, 72, 11, 'و همانا برخی از ما صالح و نیکوکار هستیم و برخی بر خلاف آن، عقیده و اعمال ما هم بسیار متفرق و مختلف است.'),
(5459, 72, 12, 'و ما چنین دانسته‌ایم که هرگز در زمین بر قدرت خدا غلبه و از محیط اقتدارش فرار نتوانیم کرد.'),
(5460, 72, 13, 'و ما چون به سخنان هدایت (یعنی آیات قرآن) گوش فرا دادیم (برخی) ایمان آوردیم و هر که به خدای خود ایمان آورد دیگر از نقصان خیر و ثواب و از احاطه رنج و عذاب بر خود هیچ نترسد.'),
(5461, 72, 14, 'و از ما جنّیان هم بعضی مسلمان و بی‌آزار و برخی کافر و ستمکارند، و آنان که اسلام آوردند راستی به راه رشد و صواب شتافتند.'),
(5462, 72, 15, 'و اما ستمکاران ما هیزم بر آتش جهنّم گردیدند.'),
(5463, 72, 16, 'و اگر بر طریقه (اسلام و ایمان) پایدار بودند آب (علم و رحمت و رزق) فراوان نصیبشان البته می‌گردانیدیم.'),
(5464, 72, 17, 'تا به آن نعمت آنها را امتحان و آزمایش کنیم، و هر که از یاد خدای خود اعراض کند خدا او را به عذابی بسیار سخت درآورد.'),
(5465, 72, 18, 'و مساجد مخصوص (پرستش ذات یکتای) خداست پس نباید با خدا احدی غیر او را پرستش کنید.'),
(5466, 72, 19, 'و چون بنده خاصّ خدا (محمّد صلّی اللّه علیه و آله و سلّم) قیام کرد و خدا را می‌خواند طایفه جنّیان (برای استماع قرآن) چنان گرد او ازدحام کردند که نزدیک بود بر سر هم فرو ریزند.'),
(5467, 72, 20, 'بگو: من تنها خدای خود را می‌پرستم و هرگز احدی را با او شریک نمی‌گردانم.'),
(5468, 72, 21, 'و باز بگو: من مالک و قادر بر خیر و شرّ شما نیستم.'),
(5469, 72, 22, 'باز بگو که (اگر خدا بر من قهر و غضب کند) ابدا کسی مرا از (قهر) خدا در پناه نتواند گرفت و به غیر او هیچ گریز گاهی هرگز نخواهم یافت.'),
(5470, 72, 23, 'تنها مأمن و پناه من ابلاغ احکام خدا و رسانیدن پیغام الهی است، و آنان که نافرمانی خدا و رسول او کنند البته کیفرشان آتش دوزخ است که در آن تا ابد مخلّد باشند.'),
(5471, 72, 24, '(ای رسول، کافران را رها کن) تا روزی که عذاب موعود خود را به چشم ببینند، آن زمان خواهند دانست که (تو و آنها) کدامتان یارانش ضعیف و ناتوان‌تر و سپاهش کمتر است؟'),
(5472, 72, 25, 'بگو: من خود ندانم که عذاب موعود شما وقتش نزدیک است یا خدا تا آن روز مدتی (بسیار) قرار داده است.'),
(5473, 72, 26, 'او دانای غیب عالم است و هیچ کس را بر غیب خود آگاه نمی‌کند.'),
(5474, 72, 27, 'مگر آن کس را که به پیامبری برگزیده است که فرشتگان را از پیش رو و پشت سر او می‌فرستد (تا اسرار وحی را شیاطین به سرقت گوش نربایند).'),
(5475, 72, 28, 'تا بداند (و معلوم سازد) که آن رسولان پیغامهای پروردگار خود را به خلق کاملا رسانیده‌اند، و البته خدا به آنچه نزد رسولان است احاطه کامل دارد و به شماره هر چیز در عالم به خوبی آگاه است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'الا ای رسولی که در جامه (فکرت و خاموشی) خفته‌ای.'),
(5477, 73, 2, '(هان) شب را (به نماز و طاعت خدا) برخیز مگر کمی.'),
(5478, 73, 3, 'که نصف یا چیزی کمتر از نصف باشد (به استراحت پرداز).'),
(5479, 73, 4, 'یا چیزی بر نصف بیفزا و به تلاوت آیات قرآن با توجه کامل مشغول باش.'),
(5480, 73, 5, 'ما کلام بسیار سنگین (و گرانمایه قرآن) را بر تو القا می‌کنیم.'),
(5481, 73, 6, 'البته نماز شب (و دعا و ناله سحر) بهترین شاهد اخلاص و صفای قلب و دعوی صدق ایمان است.'),
(5482, 73, 7, 'تو را روز روشن (در طلب روزی) وقت کافی و فرصت وسیع است.'),
(5483, 73, 8, 'و (دایم در شب و روز) نام خدا را یاد کن و به کلی از غیر او علاقه ببر و به او پرداز.'),
(5484, 73, 9, 'همان خدای مشرق و مغرب عالم که جز او هیچ خدایی نیست او را بر خود وکیل و نگهبان اختیار کن.'),
(5485, 73, 10, 'و بر طعن و یاوه‌گویی کافران و مکذّبان صبور و شکیبا باش و به طرزی نیکو (که مقتضای مدارا و حلم و بزرگواری است) از آنان دوری گزین.'),
(5486, 73, 11, 'و کار آن کافران مغرور نعمت و مال (حرام) را به من واگذار و اندک زمانی به آنها مهلت ده (تا وقت انتقام به زودی فرا رسد).'),
(5487, 73, 12, 'که البته نزد ما غل و زنجیر عذاب و آتش دوزخ مهیّاست.'),
(5488, 73, 13, 'و طعامی که (از فرط غصه و اندوه) گلوگیر آنها می‌شود و عذاب دردناک بر آنهاست.'),
(5489, 73, 14, 'آن روزی که زمین و کوهها به لرزه درآید و کوهها تلّ ریگی شود و چون موج روان گردد.'),
(5490, 73, 15, 'ما به شما (امت آخر زمان) رسولی فرستادیم که شاهد اعمال (و حجت کامل) شماست چنانکه بر فرعون هم رسول فرستادیم.'),
(5491, 73, 16, 'تا فرعون نافرمانی آن رسول کرد ما هم او را به قهر و انتقام سخت بگرفتیم.'),
(5492, 73, 17, 'پس اگر کافر شوید چگونه (از عذاب حق) نجات یابید در روزی که کودک از هول و سختی آن پیر شود؟'),
(5493, 73, 18, 'و آسمان از وحشت آن شکافته شود و وعده الهی به وقوع انجامد.'),
(5494, 73, 19, 'این آیات (قرآن) برای تذکر و یادآوری فرستاده شد تا هر که بخواهد راهی به سوی خدای خود پیش گیرد.'),
(5495, 73, 20, 'خدا به حال تو آگاه است که تو و جمعی هم از آنان که با تواند (اغلب) نزدیک دو ثلث یا نصف یا (لا اقل) ثلث شب را همیشه به طاعت و نماز می‌پردازید، و خدا (گردش) شب و روز را مقدّر می‌کند، خدا می‌داند که شما هرگز تمام ساعات شب را (به عبادت) ضبط نخواهید کرد لذا از شما (اوقات خواب و کارهای دیگر را) در گذشت تا هر چه (از شب را بی‌مشقت و) آسان است به تلاوت قرآن پردازید. خدا بر احوال شما آگاه است که برخی مریض و ناتوانند و برخی به سفر از کرم خدا روزی می‌طلبند و برخی در راه خدا به جنگ و جهاد مشغولند، پس در هر حال آنچه میسّر و آسان باشد به قرائت قرآن پردازید و نماز به پا دارید و زکات مالتان (به فقیران) بدهید و به خدا قرض نیکو دهید (یعنی برای خدا به محتاجان احسان کنید و قرض الحسنه دهید)، و هر عمل نیک برای آخرت خود پیش فرستید (پاداش) آن را البته نزد خدا بیابید و آن اجر و ثواب آخرت (که بهشت ابد است، از متاع دنیا) بسی بهتر و بزرگتر است، و دایم از خدا آمرزش طلبید که خدا بسیار آمرزنده و مهربان است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'الا ای رسولی که خود را به لباس (حیرت و فکرت) در پیچیده‌ای.'),
(5497, 74, 2, 'برخیز و به اندرز و پند، خلق را خدا ترس گردان.'),
(5498, 74, 3, 'و خدایت را به بزرگی و کبریایی یاد کن.'),
(5499, 74, 4, 'و لباس (جان و تن) خود را از هر عیب و آلایش پاک و پاکیزه دار.'),
(5500, 74, 5, 'و از ناپاکی (بت و بت‌پرستان) به کلی دوری گزین.'),
(5501, 74, 6, 'و بر هر که احسان کنی ابدا منّت مگذار و عوض افزون مخواه.'),
(5502, 74, 7, 'و برای خدایت صبر و شکیبایی پیش‌گیر.'),
(5503, 74, 8, 'تا آن‌گاه که (هنگامه قیامت برپا شود و) در صور (اسرافیل) بدمند.'),
(5504, 74, 9, 'آن روز بسیار روز سختی است.'),
(5505, 74, 10, 'کافران را هیچ گونه در آن راحتی و آسایش نیست.'),
(5506, 74, 11, 'به من واگذار کار انتقام آن کس را که من او را تنها آفریدم.'),
(5507, 74, 12, 'و به او مال و ثروت فراوان بذل کردم.'),
(5508, 74, 13, 'و پسران بسیار حاضر به خدمت نصیب گردانیدم.'),
(5509, 74, 14, 'و اقتدار و مکنت و عزّت دادم.'),
(5510, 74, 15, 'و (با کفران این نعمتها) باز هم از من طمع افزونی آن دارد.'),
(5511, 74, 16, 'هرگز (بر نعمتش نیفزایم) که او با آیات ما دشمنی و عناد ورزید.'),
(5512, 74, 17, 'به زودی او را به سختی و دشواری (آتش دوزخ) درافکنم.'),
(5513, 74, 18, 'اوست که فکر و اندیشه بدی کرد (که رسول خدا را به سحر و ساحری نسبت داد).'),
(5514, 74, 19, 'و خدایش بکشد که چقدر اندیشه غلطی کرد.'),
(5515, 74, 20, 'باز هم خدایش بکشد که چه فکر خطایی نمود.'),
(5516, 74, 21, 'پس باز اندیشه کرد.'),
(5517, 74, 22, 'و (به اظهار تنفّر از اسلام) رو ترش کرد و چهره در هم کشید.'),
(5518, 74, 23, 'آن‌گاه روی گردانید و تکبر و نخوت آغاز کرد.'),
(5519, 74, 24, 'و گفت: این (قرآن) به جز سحر و بیان سحرانگیزی که (از ساحران گذشته) نقل می‌شود هیچ نیست.'),
(5520, 74, 25, 'این آیات گفتار بشری بیش نیست.'),
(5521, 74, 26, 'من این (منکر و مکذّب قرآن) را به آتش دوزخ درافکنم.'),
(5522, 74, 27, 'و تو چگونه توانی یافت که سختی عذاب دوزخ تا چه حد است؟'),
(5523, 74, 28, 'شراره آن دوزخ از دوزخیان هیچ باقی نگذارد و همه را بسوزاند و محو گرداند.'),
(5524, 74, 29, 'آن آتش بر آدمیان رو نماید (و خرمن گنهکاران را به باد دهد).'),
(5525, 74, 30, 'بر آن آتش نوزده تن (فرشته عذاب) موکّلند.'),
(5526, 74, 31, 'و ما خازنان دوزخ را غیر فرشتگان (عذاب) قرار ندادیم و عدد آنها را جز برای فتنه و محنت کفّار (نوزده) نگردانیدیم تا آنکه اهل کتاب هم یقین کنند (که ذکر این عدد مطابق تورات و انجیل است با آنکه صاحب قرآن به کتب آسمانی عالم نبوده و البته کلامش به وحی خداست و ایمان آرند) و آن بر یقین مؤمنان هم بیفزاید و دیگر در دل اهل کتاب و مؤمنان به اسلام هیچ شک و ریبی نماند و تا آنان که در دلهاشان مرض (شک و جهالت) است و کافران نیز (به طعنه) گویند که خدا از این مثل (که عدد فرشتگان عذاب را نوزده شمرده است نه بیش و کم) چه منظور داشت؟ بلی این چنین (قرار داد تا) هر که را خواهد به ضلالت بگذارد و هر که را خواهد هدایت نماید و هیچ کس از (عده بی‌حد) لشکرهای پروردگارت غیر او آگاه نیست و این (آیات ذکر دوزخ) جز برای پند و موعظه بشر نخواهد بود.'),
(5527, 74, 32, 'چنین (که کافران گویند) نیست، قسم به ماه تابان.'),
(5528, 74, 33, 'و قسم به شب تار چون باز گردد.'),
(5529, 74, 34, 'و قسم به صبح چون جهان را روشن سازد.'),
(5530, 74, 35, 'که این (آیات قرآن) یکی از بزرگترین آیات خداست.'),
(5531, 74, 36, 'در آن پند و اندرز آدمیان است.'),
(5532, 74, 37, 'برای هر یک از شما آدمیان که بخواهد (در مقام ایمان و طاعت و سعادت) پیش افتد یا باز ماند.'),
(5533, 74, 38, 'هر نفسی در گرو عملی است که انجام داده است.'),
(5534, 74, 39, 'مگر اهل یمین.'),
(5535, 74, 40, 'آنان در باغهای بهشت (متنعّمند و) سؤال می‌کنند.'),
(5536, 74, 41, 'از احوال دوزخیان گنهکار.'),
(5537, 74, 42, 'که شما را چه عمل به عذاب دوزخ در افکند؟'),
(5538, 74, 43, 'آنان جواب دهند که ما از نمازگزاران نبودیم.'),
(5539, 74, 44, 'و مسکینی را طعام نمی‌دادیم.'),
(5540, 74, 45, 'و ما با اهل باطل به بطالت می‌پرداختیم.'),
(5541, 74, 46, 'و ما روز جزا را تکذیب می‌کردیم.'),
(5542, 74, 47, 'تا آنکه یقین (که ساعت مرگ است) بر ما فرا رسید.'),
(5543, 74, 48, 'پس شفاعت شفیعان در حق آنان هیچ سودی نبخشد.'),
(5544, 74, 49, 'اینک چرا از یاد آن روز سخت خود (و از ذکر و اندرز قرآن) اعراض می‌کنند؟'),
(5545, 74, 50, 'گویی گورخران گریزانی هستند.'),
(5546, 74, 51, 'که از شیر درنده می‌گریزند.'),
(5547, 74, 52, 'بلکه هر یک از آنها می‌خواهند که برایشان هم (مانند پیمبران) صحیفه وحی آسمانی باز آید (تا ایمان آرند).'),
(5548, 74, 53, 'هرگز (چنین نیست که پنداشتند) بلکه از (عذاب) آخرت نمی‌ترسند.'),
(5549, 74, 54, 'چنین نیست (که آنها پنداشتند) قرآن محققا همه پند و یادآوری است.'),
(5550, 74, 55, 'تا هر که خواهد متذکر آن شود.'),
(5551, 74, 56, 'و نخواهند متذکر شوند جز آنکه خدا بخواهد (یعنی بدون مشیّت و لطف الهی کسی بهشتی و سعادتمند نخواهد شد. از او رواست تقوا و ترس و بیم و شوق و امیدواری که) او اهل تقوا و اهل آمرزش و مغفرت است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'چنین نیست (که کافران پنداشتند) قسم به روز (بزرگ) قیامت.'),
(5553, 75, 2, 'و قسم به نفس پر حسرت و ملامت (یعنی نفس انسان که در قیامت خود را بر تقصیر و گناه بسیار ملامت کند و حسرت خورد). [جواب قسم محذوف است، یعنی به اینها سوگند می‌خورم که آدمیان را در روز قیامت برخواهم انگیخت. (م) ژ)]'),
(5554, 75, 3, 'آیا آدمی پندارد که ما دیگر ابدا استخوانهای (پوسیده) او را باز جمع نمی‌کنیم؟'),
(5555, 75, 4, 'بلی (استخوانهای او را جمع می‌کنیم) در حالی که ما قادریم که سرانگشتان او را هم منظم درست گردانیم.'),
(5556, 75, 5, 'بلکه انسان می‌خواهد آنچه (از عمرش) در پیش است همه را به فجور و هوای نفس گذراند.'),
(5557, 75, 6, '(که دایم با شک و انکار) می‌پرسد کی روز قیامت (و حساب) خواهد بود؟'),
(5558, 75, 7, '(بگو) روزی که چشمهای خلقان (از وحشت و هول) خیره بماند.'),
(5559, 75, 8, 'و ماه تابان تاریک شود.'),
(5560, 75, 9, 'و میان خورشید و ماه جمع گردد (و هر دو بی‌نور شوند).'),
(5561, 75, 10, 'در آن روز انسان گوید: (ای وای از سختی عذاب) کجا مفرّ و پناهی خواهد بود؟'),
(5562, 75, 11, 'هرگز مفرّ و پناهگاهی نیست.'),
(5563, 75, 12, 'آن روز جز درگاه خدا آرامگاهی هیچ نیست.'),
(5564, 75, 13, 'آن روز آدمی به (نتیجه) هر نیک و بدی که در مقدّم و مؤخّر عمر کرده آگاه خواهد شد.'),
(5565, 75, 14, 'بلکه انسان خود بر (نیک و بد) خویش به خوبی بیناست.'),
(5566, 75, 15, 'هر چند (پرده‌های) عذر (بر چشم بصیرت) خود بیفکند.'),
(5567, 75, 16, '(ای رسول در حال وحی) با شتاب و عجله زبان به قرائت قرآن مگشای.'),
(5568, 75, 17, 'که ما خود قرآن را مجموع و محفوظ داشته و بر تو فرا خوانیم.'),
(5569, 75, 18, 'و آن‌گاه که بر خواندیم تو پیرو قرآن آن باش.'),
(5570, 75, 19, 'پس از آن بر ماست که (حقایق) آن را بر تو بیان کنیم.'),
(5571, 75, 20, 'هرگز، بلکه شما (کافران لجوج) تمام دنیای نقد عاجل را دوست دارید.'),
(5572, 75, 21, 'و به کلی کار آخرت را واگذارید.'),
(5573, 75, 22, 'آن روز رخسار طایفه‌ای (از شادی) بر افروخته و نورانی است.'),
(5574, 75, 23, 'و به چشم قلب جمال حق را مشاهده می‌کنند.'),
(5575, 75, 24, 'و رخسار گروهی دیگر عبوس و غمگین است.'),
(5576, 75, 25, 'که می‌دانند حادثه ناگواری در پیش است که پشت آنها را می‌شکند.'),
(5577, 75, 26, 'چنین نیست (که منکران قیامت پنداشتند. باش تا) آن گاه که جانشان به گلو رسد.'),
(5578, 75, 27, 'و (اهلش) گویند: کیست که چاره درد این بیمار تواند کرد؟'),
(5579, 75, 28, 'و بیمار خود یقین به مفارقت از دنیا کند (که ملک موت را به چشم ببیند).'),
(5580, 75, 29, 'و ساقهای پا (از شدت غم عقبی و حسرت دنیا) به هم در پیچد.'),
(5581, 75, 30, 'در آن روز خلق را به سوی خدا خواهند کشید.'),
(5582, 75, 31, 'پس (آن روز وای بر آن که چون ابو جهل) حق را تصدیق نکرد و نماز (و طاعتش) بجا نیاورد.'),
(5583, 75, 32, 'بلکه (خدا را) تکذیب کرد و (از حکمش) رو بگردانید.'),
(5584, 75, 33, 'و آن گاه با تکبر و نخوت به سوی اهل خویش روی آورد.'),
(5585, 75, 34, '(به وی خطاب شود) وای (بر زندگی) و (صد) وای بر (مرگ) تو.'),
(5586, 75, 35, 'پس (از مرگ هم) وای (بر برزخ) و (صد) وای بر (روز محشر) تو.'),
(5587, 75, 36, 'آیا آدمی می‌پندارد که او را مهمل (از تکلیف و ثواب و عقاب) گذارند (و غرضی در خلقتش منظور ندارند).'),
(5588, 75, 37, 'آیا آدمی قطره آب نطفه نبود که (در رحم) ریزند؟'),
(5589, 75, 38, 'و پس از نطفه خون بسته شد و آن گاه (خدایش به این صورت زیبای حیرت انگیز) آفرید و آراسته ساخت.'),
(5590, 75, 39, 'پس آن گاه از او دو صنف نر و ماده پدید آورد.'),
(5591, 75, 40, 'آیا چنین خدای (با قدرت و حکمت) باز نتواند مردگان را زنده گرداند؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آیا بر انسان روزگارانی نگذشت که چیزی هیچ لایق ذکر نبود؟'),
(5593, 76, 2, 'ما انسان را از آب نطفه مختلط (بی حس و شعور) خلق کردیم و او را می‌آزماییم و بدین سبب دارای قوای چشم و گوش (و مشاعر و عقل و هوش) گردانیدیم.'),
(5594, 76, 3, 'ما به حقیقت راه (حق و باطل) را به او نمودیم حال خواهد (هدایت پذیرد و) شکر (این نعمت) گوید و خواهد (آن نعمت را) کفران کند.'),
(5595, 76, 4, 'ما برای (کیفر) کافران غل و زنجیرها و آتش سوزان مهیّا ساخته‌ایم.'),
(5596, 76, 5, 'نکوکاران عالم (که در این آیه حضرت علی و فاطمه و حسنین علیهم السّلام و شیعیانشان به اجماع خاصّه و اخبار عامّه مقصودند) در بهشت از جام شرابی نوشند که طبعش (در لطف و رنگ و بوی) کافور است.'),
(5597, 76, 6, 'از سرچشمه گوارایی آن بندگان خاص خدا می‌نوشند که به اختیارشان هر کجا خواهند جاریش سازند.'),
(5598, 76, 7, 'به عهد و نذر خود وفا می‌کنند و از روزی که شر و سختیش همه اهل محشر را فراگیرد می‌ترسند.'),
(5599, 76, 8, 'و هم بر دوستی او (یعنی خدا) به فقیر و طفل یتیم و اسیر طعام می‌دهند.'),
(5600, 76, 9, '(و گویند) ما فقط برای رضای خدا به شما طعام می‌دهیم و از شما هیچ پاداش و سپاسی هم نمی‌طلبیم.'),
(5601, 76, 10, 'ما از (قهر) پروردگار خود به روزی که (از رنج و سختی آن رخسار خلق) در هم و غمگین است می‌ترسیم.'),
(5602, 76, 11, 'خدا هم از شر و فتنه آن روز آنان را محفوظ داشت و به آنها روی خندان و دل شادمان عطا نمود.'),
(5603, 76, 12, 'و پاداش آن صبر کامل بر ایثارشان باغ بهشت و لباس حریر بهشتی لطف فرمود.'),
(5604, 76, 13, 'که در آن بهشت بر تختها (ی عزت) تکیه زنند و آنجا نه آفتابی سوزان بینند و نه سرمای ز مهریر (بلکه در هوایی بسیار خوش و باغی وسیع و دلکش تفرج کنند).'),
(5605, 76, 14, 'و سایه درختان بهشتی بر سر آنها و میوه‌هایش در دسترس و به اختیار آنهاست.'),
(5606, 76, 15, 'و (ساقیان زیبای حور و غلمان) با جامهای سیمین و کوزه‌های بلورین بر آنها دور زنند.'),
(5607, 76, 16, 'که آن بلورین کوزه‌ها به رنگ نقره خام و به اندازه و تناسب (اهلش) مقدر کرده‌اند.'),
(5608, 76, 17, 'و آنجا شرابی که طبعش چون زنجبیل (گرم و عطرآگین) است به آنها بنوشانند.'),
(5609, 76, 18, 'در آنجا چشمه‌ای است که سلسبیلش نامند.'),
(5610, 76, 19, 'و دور آن بهشتیان پسرانی زیبا که تا ابد نوجوانند و خوش سیما به خدمت می‌گردند که در آنها چو بنگری (از فرط صفا) گمان بری که لؤلؤ منثورند.'),
(5611, 76, 20, 'و چون آن جایگاه نیکو را مشاهده کنی عالمی پر نعمت و کشوری بی نهایت بزرگ خواهی یافت.'),
(5612, 76, 21, 'بر بالای بهشتیان، لطیف دیبای سبز و حریر ستبر است و بر دستهاشان دستبند نقره خام، و خدایشان شرابی پاک (و گوارا از کوثر عنایت) بنوشاند.'),
(5613, 76, 22, 'این بهشت به حقیقت پاداش (اعمال) شماست و سعی و اشتیاقتان (در راه طاعت حق) مشکور و مقبول است.'),
(5614, 76, 23, 'محققا ما این قرآن (عظیم الشأن) را بر تو فرو فرستادیم.'),
(5615, 76, 24, 'پس بر اطاعت حکم پروردگارت صبور و شکیبا باش و هیچ از مردم بدکار کفر کیش اطاعت مکن.'),
(5616, 76, 25, 'و نام خدایت را صبح و شام (به عظمت) یاد کن.'),
(5617, 76, 26, 'و شب را برخی (در نماز) به سجده خدا پرداز و شام دراز به تسبیح و ستایش او صبح گردان.'),
(5618, 76, 27, 'این مردم کافر غافل همه دنیای نقد عاجل را دوست می‌دارند و آن روز (قیامت) سخت سنگین را به کلی از یاد می‌برند.'),
(5619, 76, 28, 'ما اینان را آفریدیم و محکم بنیان ساختیم و هرگاه بخواهیم همه را فانی ساخته و مانندشان قوم دیگر خلق می‌کنیم.'),
(5620, 76, 29, 'این آیات پند و تذکری است، پس هر که (سعادت و حسن عاقبت) بخواهد راهی به سوی خدای خود پیش گیرد.'),
(5621, 76, 30, 'و شما (کافران، راه حق را) نمی‌خواهید مگر خدای بخواهد (و به قهر شما را به این راه بدارد)، که البته خدا دانا و به صلاح بندگان آگاه است.'),
(5622, 76, 31, 'هر که را بخواهد در (بهشت) رحمت خود داخل گرداند و برای ستمکاران عالم عذاب دردناک مهیّا ساخته است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'قسم به رسولانی که از پی هم (بر خیر و سعادت خلق) فرستاده شدند.'),
(5624, 77, 2, 'قسم به فرشتگانی که به سرعت تند باد (به انجام حکم حق) می‌شتابند.'),
(5625, 77, 3, 'قسم به آنان که (وحی حق و شرع الهی را در جهان) نیکو نشر می‌دهند.'),
(5626, 77, 4, 'و قسم به حق آنان که (به حکم خدا راه هدایت را از ضلالت ممتاز کرده و بین حق و باطل را بر خلق) کاملا جدا می‌کنند.'),
(5627, 77, 5, 'و قسم به آنان که ذکر (خدا و کتاب الهی) را (به انبیاء) وحی می‌کنند.'),
(5628, 77, 6, 'تا عذر و حجت (نیکان) و بیم و تهدید (بدان) شود.'),
(5629, 77, 7, '(قسم به همه اینان) که آنچه (از ثواب و عقاب آخرت) به شما وعده دهند البته واقع خواهد شد.'),
(5630, 77, 8, 'وقتی که ستارگان همه بی نور و محو شوند.'),
(5631, 77, 9, 'و آن گاه که آسمان شکافته شود.'),
(5632, 77, 10, 'و آن گاه که کوهها (چون خاک بر دم باد) پراکنده شوند.'),
(5633, 77, 11, 'و آن گاه که به رسولان، وقت (گواهی بر امتان) را اعلام کنند.'),
(5634, 77, 12, 'برای چه روزی وقت (وعده حق) معین شد؟'),
(5635, 77, 13, 'برای (روز قیامت) همان روزی که (بین حق و باطل و سعید و شقی) جدایی افتد.'),
(5636, 77, 14, 'و تو چگونه توانی عظمت و سختی آن روز جدایی را تصور کنی؟'),
(5637, 77, 15, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5638, 77, 16, 'آیا ما پیشینیان را (به کیفر کفر) هلاک نکردیم؟'),
(5639, 77, 17, 'از پی آنان آیندگان را (که منکران قرآن باشند) نیز هلاک می‌کنیم.'),
(5640, 77, 18, 'ما بدکاران عالم را همین گونه هلاک می‌سازیم.'),
(5641, 77, 19, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5642, 77, 20, 'آیا ما شما آدمیان را از آب نطفه بی قدر (بدین زیبایی) نیافریدیم؟'),
(5643, 77, 21, 'و آن نطفه را (از صلب پدر) به قرارگاه استوار (رحم) منتقل ساختیم.'),
(5644, 77, 22, 'تا مدتی معین و معلوم (در رحم بماند).'),
(5645, 77, 23, 'و ما تقدیر (مدت رحم و تعیین سرنوشت او تا ابد) کردیم که نیکو مقدّر (حکیمی) هستیم.'),
(5646, 77, 24, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5647, 77, 25, 'آیا ما زمین را جامع (حوایج و کفایت بر مسکن و هر امر بشر) قرار ندادیم؟'),
(5648, 77, 26, 'تا زندگان روی زمین تعیّش کنند و مردگان درونش پنهان شوند.'),
(5649, 77, 27, 'و در آن کوههای بلند برافراشتیم و (از ابر و باران) به شما آب زلال گوارا نوشانیدیم.'),
(5650, 77, 28, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5651, 77, 29, '(خطاب رسد: ای منکران) شما امروز به سوی آن دوزخی که تکذیب می‌کردید بروید.'),
(5652, 77, 30, 'بروید زیر سایه دودهای آتش دوزخ که از سه جانب (شما را) احاطه کند.'),
(5653, 77, 31, 'نه بر سر شما سایه خواهد افکند و نه از شرار آتش هیچ نجاتتان خواهد داد.'),
(5654, 77, 32, 'آن آتش هر شراری بیفکند شعله‌اش مانند قصری است.'),
(5655, 77, 33, 'گویی آن شراره (در بزرگی و رنگ) به شتران زرد موی مانند است.'),
(5656, 77, 34, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5657, 77, 35, 'این روزی است که (کافران بر نجات خود) سخنی نتوانند گفت.'),
(5658, 77, 36, 'و به آنها رخصت عذر خواهی نخواهند داد.'),
(5659, 77, 37, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5660, 77, 38, 'این روز فصل (حکم و جدایی نیک و بد) است که شما را با همه در گذشتگان پیشین (به عرصه محشر) جمع گردانیده‌ایم.'),
(5661, 77, 39, 'پس اگر فکر و حیلتی (بر نجات خود) توانید بر من حیلت کنید.'),
(5662, 77, 40, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5663, 77, 41, '(آن روز سخت) بندگان با تقوا و پرهیزگار در سایه درختان بهشت و طرف نهرهای جاری متنعّمند.'),
(5664, 77, 42, 'و از هر نوع میوه مایل باشند فراوان فراهم است.'),
(5665, 77, 43, 'بخورید و بیاشامید که شما را گوارا باد به پاداش اعمال نیکی که در دنیا به جای می‌آوردید.'),
(5666, 77, 44, 'ما البته نیکوکاران عالم را چنین پاداش می‌دهیم.'),
(5667, 77, 45, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5668, 77, 46, '(ای کافران شما هم که ایمان به لذّات روحانی و تنعّم و نشاط ابدی آخرت ندارید پس به خوی حیوانیت) بخورید و تمتّع برید به عمر کوتاه دنیا که شما بسیار مردم بزهکاری هستید.'),
(5669, 77, 47, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5670, 77, 48, 'و هرگاه به آنها گفته شود که نماز و طاعت خدا را به جای آرید اطاعت نکنند.'),
(5671, 77, 49, 'وای آن روز به حال آنان که (آیات خدا را) تکذیب کردند.'),
(5672, 77, 50, '(این کافران) پس از آیات خدا (که آن را تکذیب کردند) باز به کدامین حدیث ایمان می‌آورند؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, '(مردم) از چه خبر مهمی پرسش و گفتگو می‌کنند؟'),
(5674, 78, 2, 'از خبر بزرگ (قیامت).'),
(5675, 78, 3, 'که در آن با هم به جدال و اختلاف کلمه برخاستند.'),
(5676, 78, 4, 'چنین نیست (که منکران پنداشته‌اند) به زودی (به وقت مرگ) خواهند دانست.'),
(5677, 78, 5, 'و باز چنین نیست (که منکران پنداشته‌اند) البته به زودی آگاه می‌شوند.'),
(5678, 78, 6, 'آیا ما زمین را مهد آسایش خلق نگردانیدیم؟'),
(5679, 78, 7, 'و کوهها را عماد و نگهبان آن نساختیم؟'),
(5680, 78, 8, 'و شما را جفت (زن و مرد) آفریدیم.'),
(5681, 78, 9, 'و خواب را برای شما مایه (قوم حیات و) استراحت قرار دادیم.'),
(5682, 78, 10, 'و (پرده سیاه) شب را ساتر (احوال خلق) گردانیدیم.'),
(5683, 78, 11, 'و روز روشن را برای تحصیل معاش آنان مقرر داشتیم.'),
(5684, 78, 12, 'و بر فراز شما هفت آسمان محکم بنا کردیم.'),
(5685, 78, 13, 'و چراغی (چون خورشید) رخشان بر افروختیم.'),
(5686, 78, 14, 'و از فشار و تراکم ابرها آب باران فرو ریختیم.'),
(5687, 78, 15, 'تا بدان آب، دانه و گیاه رویانیم.'),
(5688, 78, 16, 'و باغهای پر درخت (و انواع میوه‌ها) پدید آوریم.'),
(5689, 78, 17, 'همانا روز فصل (یعنی روز قیامت که در آن فصل خصومتها شود) وعده گاه خلق است.'),
(5690, 78, 18, 'آن روزی که در صور بدمند (تا مردگان زنده شوند) و فوج فوج (به محشر) در آیید.'),
(5691, 78, 19, 'و درهای آسمان به ابواب مختلف گشوده شود (تا هر فوجی از دری شتابند).'),
(5692, 78, 20, 'و کوهها را به گردش آرند تا به مانند سراب گردد.'),
(5693, 78, 21, 'همانا دوزخ کمینگاه (کافران و بد کاران) است.'),
(5694, 78, 22, 'آن دوزخ جایگاه مردم سرکش ستمکار است.'),
(5695, 78, 23, 'که در آن قرنها بمانند (و عذاب کشند).'),
(5696, 78, 24, 'هرگز در آنجا هیچ هوی خنک و شراب (طهور) نیاشامند.'),
(5697, 78, 25, 'مگر آبی پلید و سوزان که حمیم و غسّاق جهنم است به آنها دهند.'),
(5698, 78, 26, 'کیفری که با اعمال آنها موافق است.'),
(5699, 78, 27, 'زیرا آنها به حقیقت امید به روز حساب نداشتند.'),
(5700, 78, 28, 'و آیات ما را سخت تکذیب کردند.'),
(5701, 78, 29, 'و حال آنکه حساب هر چیزی را ما در کتابی (به احصاء و شماره) رقم کرده‌ایم.'),
(5702, 78, 30, 'پس بچشید (کیفر تکذیب و بدکاری را) که هرگز بر شما چیزی جز رنج و عذاب دوزخ نیفزاییم.'),
(5703, 78, 31, 'متقیان را در آن جهان مقام گشایش و هر گونه آسایش است.'),
(5704, 78, 32, 'باغها و تاکستانهاست.'),
(5705, 78, 33, 'و دختران زیبای دلربا که همه در خوبی و جوانی مانند یکدیگرند.'),
(5706, 78, 34, 'و جامهای پر از شراب (طهور و انواع نوشابه‌های شیرین و خوش).'),
(5707, 78, 35, 'هرگز در آنجا سخن بیهوده و دروغ نشنوند.'),
(5708, 78, 36, 'این (نعمتهای ابدی) مزدی به عطاء و حساب پروردگار توست.'),
(5709, 78, 37, 'خدایی که آسمانها و زمین و همه مخلوقاتی که در بین آسمان و زمین است بیافریده همان خدای مهربان که (در عین مهربانی) کسی (از قهر و سطوتش) با او به گفتگو نتواند لب گشود.'),
(5710, 78, 38, 'روزی که آن فرشته بزرگ روح القدس با همه فرشتگان صف زده و به نظم برخیزند و هیچ کس سخن نگوید جز آن کسی که خدای مهربانش به سخن اذن دهد و او سخن به صواب گوید (و به اذن خدا شفاعت گنهکاران کند).'),
(5711, 78, 39, 'چنین روز حتمی و محقق خواهد بود، پس هر که می‌خواهد نزد خدای خود (در آن روز) مقام و منزلتی یابد (امروز در راه ایمان و طاعت بکوشد).'),
(5712, 78, 40, 'ما شما را از روز عذاب که نزدیک است ترسانیده و آگاه ساختیم، روزی که هر کس هر چه کرده در پیش روی خود حاضر بیند و کافر در آن روز گوید که ای کاش خاک بودم (تا چنین به آتش کفر خود نمی‌سوختیم).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'قسم به فرشتگانی که (جان کافران را) به سختی بگیرند.'),
(5714, 79, 2, 'قسم به فرشتگانی که (جان اهل ایمان را) به آسایش و نشاط ببرند.'),
(5715, 79, 3, 'و قسم به فرشتگانی که با کمال سرعت (فرمان حق را) انجام دهند.'),
(5716, 79, 4, 'قسم به فرشتگانی که (درنظم عالم) بر هم سبقت گیرند.'),
(5717, 79, 5, 'قسم به فرشتگانی که (به فرمان حق) به تدبیر نظام خلق می‌کوشند (که قیامتی و حشری خواهد آمد).'),
(5718, 79, 6, 'روزی که آن لرزاننده (یعنی نفخه صور الهی، جهان را) بلرزاند.'),
(5719, 79, 7, 'و لرزاننده دیگر (یعنی نفخه صور دیگر) از پی آن در آید.'),
(5720, 79, 8, 'در آن روز سخت دلهای برخی مضطرب و هراسان شود.'),
(5721, 79, 9, 'که (از ترس) چشمها به زیر افکنند.'),
(5722, 79, 10, '(مردم غافل دنیا) گویند: آیا ما پس از مرگ باز زندگانی دیگری خواهیم یافت؟'),
(5723, 79, 11, 'چگونه چون مردیم و استخوان ما پوسید زنده خواهیم شد؟'),
(5724, 79, 12, 'و گویند: در این صورت که دیگر بار زنده شویم بسی زیانکار خواهیم بود.'),
(5725, 79, 13, 'جز یک صدای مهیب (که در صور اسرافیل کنند) نشنوند.'),
(5726, 79, 14, 'و ناگاه همه از خاک بر شوند و به صحرای قیامت رهسپار گردند.'),
(5727, 79, 15, 'آیا خبر موسی عمران به تو رسیده؟'),
(5728, 79, 16, 'آن گاه که خدایش او را در وادی مقدس طوی (قرب طور) ندا کرد.'),
(5729, 79, 17, 'که به سوی فرعون برو که او سخت به راه طغیان رفته است.'),
(5730, 79, 18, 'پس بگو: میل داری که (از پلیدی خود پرستی و شرک) پاک و پاکیزه شوی؟'),
(5731, 79, 19, 'و تو را به راه خدا هدایت کنم تا بترسی (و به درگاه عظمت و قدرت او خاشع و فروتن شوی).'),
(5732, 79, 20, 'پس آن آیت و معجزه بزرگتر را به او نمود.'),
(5733, 79, 21, 'فرعون تکذیب و نافرمانی کرد.'),
(5734, 79, 22, 'از آن پس (که معجزه موسی دید) باز روی از حق بگردانید و (برای دفع موسی) به جهد و کوشش برخاست.'),
(5735, 79, 23, 'پس (با رجال بزرگ دربار خود) انجمن کرد و ندا داد.'),
(5736, 79, 24, 'و گفت: منم خدای بزرگ شما.'),
(5737, 79, 25, 'خدا هم او را به عقاب دنیا و آخرت گرفتار کرد.'),
(5738, 79, 26, 'تا به هلاکت او اهل ترس از خدا عبرت گیرند.'),
(5739, 79, 27, 'آیا بنای شما آدمیان استوارتر است یا بنای آسمان بلند که خدا آفرید؟'),
(5740, 79, 28, 'که سقفی بس بلند و محکم بنیان در کمال زیبایی استوار ساخت.'),
(5741, 79, 29, 'و شامش را تیره و روزش را روشن گردانید.'),
(5742, 79, 30, 'و زمین را پس از آن بگسترانید.'),
(5743, 79, 31, 'و از آن آب و گیاه پدید آورد.'),
(5744, 79, 32, 'و کوهها را بر روی آن استوار ساخت.'),
(5745, 79, 33, 'تا (از آن آب و گیاه که از بیابان و کوه بر انگیزد) قوت شما و چهارپایانتان بر آید.'),
(5746, 79, 34, 'پس چون آن واقعه بزرگ (و حادثه عظیم قیامت) پدید آید.'),
(5747, 79, 35, 'در آن روز آدمی هر چه کرده به یاد آرد.'),
(5748, 79, 36, 'و دوزخ برای بینندگان آشکار شود.'),
(5749, 79, 37, 'پس هر کس (از حکم شرع خدا) سرکش و طاغی شد.'),
(5750, 79, 38, 'و زندگی دنیا را برگزید.'),
(5751, 79, 39, 'دوزخ جایگاه اوست.'),
(5752, 79, 40, 'و هر کس از حضور در پیشگاه عزّ ربوبیت بترسید و از هوای نفس دوری جست.'),
(5753, 79, 41, 'همانا بهشت منزلگاه اوست.'),
(5754, 79, 42, 'از تو سؤال کنند که قیامت کی بر پا شود؟'),
(5755, 79, 43, 'تو را چه کار است دیگر که از آن به یاد آری؟ (بسیار یاد آور شدی و منکران از تو نپذیرفتند).'),
(5756, 79, 44, 'کار آن ساعت به خدای تو منتهی شود.'),
(5757, 79, 45, 'تو را جز این نباشد که اهل ایمان را هر کس از یاد آن روز هراسان می‌شود به اهوال آن روز آگاه سازی.'),
(5758, 79, 46, 'چون آن روز را مردم ببینند گویی همه عمر دنیا شامگاهی یا چاشتگاهی بیش نبوده است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'عبوس و ترشرو گشت.'),
(5760, 80, 2, 'چون آن مرد نابینا حضورش آمد.'),
(5761, 80, 3, 'و تو چه می‌دانی ممکن است او مردی پارسا و پاکیزه صفات باشد.'),
(5762, 80, 4, 'یا (به تعلیم رسول) به یاد خدا آید و ذکر حق او را سودمند افتد. (مراد عبد اللّه مکتوم است که چون حضور رسول برای تعلیم قرآن آمد جمعی از اکابر کفار قریش نزد حضرت بودند، حضرت از حرص بر ایمان و هدایت آنها به او توجه کامل نکرد و به صحبت مشغول بود و مرد متشخصی از امویان بر آن نابینا ترشرویی و اظهار انزجار نمود. بر نکوهش او و حرمت عبد اللّه این آیات نازل شد).'),
(5763, 80, 5, 'اما آن که دارا (و مغرور دنیا) است.'),
(5764, 80, 6, 'تو به او توجه کنی (و حریص بر ایمان او باشی).'),
(5765, 80, 7, 'در صورتی که اگر او به پارسایی نپردازد بر تو تکلیفی نیست.'),
(5766, 80, 8, 'اما آن کس که (چون عبد اللّه مکتوم به شوق دل برای ذکر خدا) به سوی تو می‌شتابد.'),
(5767, 80, 9, 'و او مرد خدا ترس و با تقوایی است.'),
(5768, 80, 10, 'تو از توجه به او خود داری می‌کنی!'),
(5769, 80, 11, 'این روا نیست، که آیات حق برای پند و تذکر (همه خلق) است.'),
(5770, 80, 12, 'تا هر که بخواهد پند گیرد.'),
(5771, 80, 13, 'آیات الهی در صحیفه‌هایی مکرّم (یعنی لوح محفوظ یا کتب آسمانی یا قلوب پاکان) نگاشته است.'),
(5772, 80, 14, 'که آن صحیفه‌ها بسی بلند مرتبه و پاک و منزه (از خطا) است.'),
(5773, 80, 15, '(سپرده) به دست سفیران حق (و فرشتگان وحی) است.'),
(5774, 80, 16, 'که ملائکه مقرب عالی رتبه با حسن و کرامتند.'),
(5775, 80, 17, 'ای کشته باد انسان (بی ایمان) که تا این حد کفر و عناد می‌ورزد!'),
(5776, 80, 18, '(نمی‌نگرد که) خدا و را از چه چیز خلق کرده است؟'),
(5777, 80, 19, 'از آب نطفه (بی قدری بدین صورت زیبا) خلقتش فرمود'),
(5778, 80, 20, 'سپس راه (خروج از نقص به کمال) را بر او سهل و آسان گردانید.'),
(5779, 80, 21, 'آن گاه (به وقت معین) او را بمیراند و به خاک قبر سپرد.'),
(5780, 80, 22, 'و سپس هر وقت خواهد باز او را از قبر بر انگیزد.'),
(5781, 80, 23, 'چنین نیست (که منکران معاد می‌پندارند) بشر کافر هنوز آنچه خدا امر کرده به جا نیاورده (تا سعادت ابد یابد).'),
(5782, 80, 24, 'آدمی باید به قوت و غذای خود به چشم خود بنگرد.'),
(5783, 80, 25, 'که ما آب باران فرو ریختیم.'),
(5784, 80, 26, 'آن گاه خاک زمین بشکافتیم.'),
(5785, 80, 27, 'و حبوبات برویانیدیم.'),
(5786, 80, 28, 'و باغ انگور و نباتاتی که هر چه بدروند باز بروید.'),
(5787, 80, 29, 'و درخت زیتون و نخل خرما.'),
(5788, 80, 30, 'و باغهای پر از درختان کهن.'),
(5789, 80, 31, 'و انواع میوه‌ها و علفها و مرتعها.'),
(5790, 80, 32, 'تا شما آدمیان و حیوانات شما هم از آن بهره‌مند شوید.'),
(5791, 80, 33, '(به یاد آرید) آن گاه که ندای مهیب قیامت (به گوش همه خلق) برسد.'),
(5792, 80, 34, 'آن روز که (از وحشت و هولناکی) هر کس از برادرش می‌گریزد.'),
(5793, 80, 35, 'و بلکه از مادر و پدرش.'),
(5794, 80, 36, 'و از زن و فرزندانش هم می‌گریزد.'),
(5795, 80, 37, 'در آن روز هر کس چنان گرفتار شأن و کار خود است که به هیچ کس نتواند پرداخت.'),
(5796, 80, 38, 'آن روز طایفه‌ای (که اهل ایمانند) رخسارشان فروزان است.'),
(5797, 80, 39, 'خندان و شادمانند.'),
(5798, 80, 40, 'و رخسار گروهی گرد آلود غم و اندوه است.'),
(5799, 80, 41, 'و به رویشان خاک سیاه ذلّت و خجلت نشسته.'),
(5800, 80, 42, 'آنها همان کافران و بدکاران عالمند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'هنگامی که آفتاب تابان تاریک شود.'),
(5802, 81, 2, 'و هنگامی که ستارگان آسمان تیره شوند (و فرو ریزند).'),
(5803, 81, 3, 'و آن گاه که گوهها به رفتار آیند.'),
(5804, 81, 4, 'و آن گاه که شتران ده ماهه آبستن را (که نزد عرب بسیار عزیز است) به کلی رها کنند.'),
(5805, 81, 5, 'و هنگامی که وحوش (و طیور نیز به عرصه قیامت) محشور شوند.'),
(5806, 81, 6, 'و هنگامی که دریاهای آب (چون آتش سوزان) شعله‌ور گردد.'),
(5807, 81, 7, 'و هنگامی که نفوس خلق همه با همجنس خود در پیوندند.'),
(5808, 81, 8, 'و هنگامی که از دختران زنده به گور شده باز پرسند.'),
(5809, 81, 9, 'که (آن بی گناهان) به چه جرم و گناه کشته شدند؟!'),
(5810, 81, 10, 'و هنگامی که نامه اعمال خلق گشوده شود.'),
(5811, 81, 11, 'و هنگامی که آسمان را از جای بر کنند.'),
(5812, 81, 12, 'و هنگامی که آتش دوزخ را سخت بیفروزند.'),
(5813, 81, 13, 'و هنگامی که بهشت را (به اهلش) نزدیک سازند.'),
(5814, 81, 14, 'در آن هنگام (که روز قیامت است) هر نفسی هر آنچه بر خود حاضر کرده همه را بداند.'),
(5815, 81, 15, 'چنین نیست (که منکران قرآن پندارند) قسم یاد می‌کنم به ستارگان باز گردنده.'),
(5816, 81, 16, 'که به گردش آیند و در مکان خود رخ پنهان کنند.'),
(5817, 81, 17, 'قسم به شب تار هنگامی که روی جهان را تاریک گرداند.'),
(5818, 81, 18, 'و قسم به صبح روشن وقتی که دم زند (و عالم را به روی خود بیفروزد).'),
(5819, 81, 19, '(قسم به این آیات) که همانا قرآن کلام رسول بزرگوار حق (جبرئیل) است.'),
(5820, 81, 20, 'که فرشته با قوّت و قدرت است و نزد خدای مقتدر عرش با جاه و منزلت است.'),
(5821, 81, 21, 'و در آنجا فرمانده فرشتگان و امین وحی خداست.'),
(5822, 81, 22, 'و رسول عصر شما هرگز دیوانه نیست.'),
(5823, 81, 23, 'و او امین وحی را در افق اعلای مشرق درست مشاهده کرد.'),
(5824, 81, 24, 'و این رسول شما بر وحی از عالم غیب بخل نمی‌ورزد (و شما را از علم وحی خود آگاه می‌سازد).'),
(5825, 81, 25, 'و قرآن او هرگز کلام شیطانی مردود نیست.'),
(5826, 81, 26, 'پس شما به کجا می‌روید؟'),
(5827, 81, 27, 'این (قرآن عظیم) جز پند و نصیحت اهل عالم هیچ نیست.'),
(5828, 81, 28, 'تا هر که از شما بندگان بخواهد راه راست (حق پرستی و سعادت ابد) پیش گیرد.'),
(5829, 81, 29, 'و شما (کافران، راه حق را) نمی‌خواهید مگر خدای آفریننده عالم بخواهد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'هنگامی که آسمان شکافته شود.'),
(5831, 82, 2, 'و هنگامی که ستارگان آسمان فرو ریزند.'),
(5832, 82, 3, 'و هنگامی که آب دریاها روان گردد (تا به هم پیوسته همه یک دریا شود).'),
(5833, 82, 4, 'و هنگامی که قبرها زیر و رو شود (و خلایق از آنها بر انگیخته شوند).'),
(5834, 82, 5, 'آن هنگام است که هر شخصی به هر چه مقدّم و مؤخّر انجام داده همه را بداند (شاید مراد از مقدم عملی است که پیش از رفتن از دنیا برای آخرت به جای آورده، و مؤخر آن اعمالی که پس از مرگ به او می‌رسد مانند باقیات صالحات و اعمال خیری که وصیت کرده و وقف و صدقات جاری و فرزند صالح یا کتاب علمی و سنّت حسنه).'),
(5835, 82, 6, 'ای انسان، چه باعث شد که به خدای کریم بزرگوار خود مغرور گشتی (و نافرمانی او کردی).'),
(5836, 82, 7, 'آن خدایی که تو را به وجود آورد و به صورتی تمام و کامل بیاراست و به اعتدال (اندام و تناسب قوا) برگزید.'),
(5837, 82, 8, 'و حال آنکه به هر صورتی که خواستی (جز این صورت زیبا هم) خلق توانستی کرد.'),
(5838, 82, 9, 'چنین نیست (که شما کافران پنداشتید که معاد و قیامتی نیست) بلکه شما (از جهل) روز جزا را تکذیب می‌کنید.'),
(5839, 82, 10, 'البته نگهبانها بر (مراقبت احوال) شما مأمورند.'),
(5840, 82, 11, 'که آنها نویسندگان (اعمال شما و فرشته) مقرب خدایند.'),
(5841, 82, 12, 'شما هر چه کنید همه را می‌دانند.'),
(5842, 82, 13, 'همانا نیکو کاران عالم در بهشت پر نعمت متنعّمند.'),
(5843, 82, 14, 'و بدکاران در آتش دوزخ معذبند.'),
(5844, 82, 15, 'روز جزا به آن دوزخ در افتند.'),
(5845, 82, 16, 'و هیچ از آن آتش دور نتوانند بود.'),
(5846, 82, 17, 'و تو چگونه به روز با عظمت جزا (ای بشر امروز کاملا) آگاه توانی شد؟'),
(5847, 82, 18, 'باز هم عظمت آن روز جزا را چگونه توانی دانست؟'),
(5848, 82, 19, 'آن روز هیچ کس برای کسی قادر بر هیچ کار نیست و تنها حکم و فرمان در آن روز با خدای یکتاست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای به حال کم فروشان.'),
(5850, 83, 2, 'آنان که چون به کیل (یا وزن) چیزی از مردم بستانند تمام بستانند.'),
(5851, 83, 3, 'و چون چیزی بدهند در کیل و وزن به مردم کم دهند.'),
(5852, 83, 4, 'آیا آنها نمی‌دانند که (پس از مرگ برای مجازات) بر انگیخته می‌شوند،'),
(5853, 83, 5, 'در روزی که آن بسیار روز بزرگی است؟'),
(5854, 83, 6, 'روزی که مردم تمام در حضور پروردگار عالم (برای حساب) می‌ایستند.'),
(5855, 83, 7, 'چنین نیست (که منکران پندارند) البته (روز قیامت) بد کاران با نامه عمل سیاهشان در عذاب سجّینند.'),
(5856, 83, 8, 'و چگونه به حقیقت سجّین آگاه توانی شد؟'),
(5857, 83, 9, 'کتابی است (که به قلم حق) نوشته شده.'),
(5858, 83, 10, 'وای آن روز به حال منکران و تکذیب کنندگان.'),
(5859, 83, 11, 'آنان که روز جزا را تکذیب می‌کنند.'),
(5860, 83, 12, 'در صورتی که آن روز را کسی تکذیب نمی‌کند مگر هر ظالم و بد کاری در عالم.'),
(5861, 83, 13, 'که بر او چون آیات ما تلاوت شود گوید: این سخنان افسانه پیشینیان است.'),
(5862, 83, 14, 'چنین نیست، بلکه ظلمت ظلم و بد کاریهاشان بر دلهای تیره آنها غلبه کرده است (که قرآن را انکار می‌کنند).'),
(5863, 83, 15, 'چنین نیست (که می‌پندارند)، آنها در آن روز از (رحمت و کرم) پروردگارشان محجوب و محرومند.'),
(5864, 83, 16, 'سپس آنها را به آتش دوزخ در افکنند.'),
(5865, 83, 17, 'و به آنان گویند: این همان دوزخی است که تکذیب آن می‌کردید.'),
(5866, 83, 18, 'چنین نیست (که شما کافران پنداشتید، امروز) نکو کاران عالم با نامه اعمالشان در بهشت علّیّین روند.'),
(5867, 83, 19, 'و چگونه به حقیقت علّیّین آگاه توانی شد؟'),
(5868, 83, 20, 'کتابی است (که به قلم حق) نوشته شده.'),
(5869, 83, 21, 'که مقربان درگاه حق به مشاهده آن مقام نائل شوند.'),
(5870, 83, 22, 'محققا نیکوکاران در بهشت ابد متنعّمند.'),
(5871, 83, 23, 'آنجا بر تختها (ی عزت تکیه زنند و رحمت و نعمتهای خدا را) بنگرند.'),
(5872, 83, 24, 'در رخسارشان نشاط و شادمانی نعیم بهشتی خواهی دید.'),
(5873, 83, 25, '(ساقیان حور و غلمان) به آنها شراب ناب سر به مهر بنوشانند.'),
(5874, 83, 26, 'که به مشک مهر کرده‌اند و راغبان (عاقل) بر این نعمت و شادمانی ابدی باید به شوق و رغبت بکوشند.'),
(5875, 83, 27, 'و ترکیب طبع آن شراب ناب از (جشمه‌ای از) عالم بالاست.'),
(5876, 83, 28, 'سر چشمه‌ای که مقربان خدا از آن می‌نوشند.'),
(5877, 83, 29, 'همانا (در دنیا) بد کاران بر اهل ایمان می‌خندیدند.'),
(5878, 83, 30, 'و چون به آنها می‌گذشتند به چشم طعن و استهزا می‌نگریستند.'),
(5879, 83, 31, 'و چون به سوی کسان خود باز می‌گشتند به سخن مزاح و فکاهی (به نکوهش نماز و طاعت مؤمنان) با هم تفریح می‌کردند.'),
(5880, 83, 32, 'و چون مؤمنان را می‌دیدند می‌گفتند که اینان به حقیقت مردم گمراهی هستند.'),
(5881, 83, 33, 'در صورتی که آن بدان را موکّل کار و نگهبان اعمال مؤمنان نفرستاده بودند (تا بدین سخنان پردازند).'),
(5882, 83, 34, 'پس امروز هم اهل ایمان به کفار می‌خندند.'),
(5883, 83, 35, 'در حالی که بر تختها (ی عزت تکیه زده و دوزخیان را) مشاهده می‌کنند.'),
(5884, 83, 36, 'آیا کافران به نتیجه افعال زشتی که می‌کردند رسیدند؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'هنگامی که آسمان شکافته شود.'),
(5886, 84, 2, 'و به فرمان حق گوش فرا دارد و البته سزد که فرمان او پذیرد.'),
(5887, 84, 3, 'و هنگامی که زمین وسیع و منبسط شود.'),
(5888, 84, 4, 'و هر چه در درون دل پنهان داشته (از مردگان و گنجها و معادن) همه را به کلی بیرون افکند و تهی گردد.'),
(5889, 84, 5, 'و به فرمان خدا گوش فرا دهد د البته سزد که فرمان او پذیرد (در آن هنگام قیامت بر پا شود).'),
(5890, 84, 6, 'ای انسان البته با هر رنج و مشقت (در راه طاعت و عبادت حق بکوش که) عاقبت حضور پروردگار خود می‌روی.'),
(5891, 84, 7, 'اما آن کس که (در آن روز) نامه اعمالش به دست راست دهند.'),
(5892, 84, 8, 'آن کس بسیار حسابش آسان کنند.'),
(5893, 84, 9, 'و او به سوی کسان و خویشانش (در بهشت) مسرور و شادمان خواهد رفت.'),
(5894, 84, 10, 'و اما آن کس که نامه اعمالش را از پشت سر (به دست چپ) دهند.'),
(5895, 84, 11, 'او بر هلاک خود آه و فریاد حسرت بسیار کند.'),
(5896, 84, 12, 'و به آتش سوزان دوزخ در افتد.'),
(5897, 84, 13, 'که او میان کسانش بسیار مغرور و مسرور بود.'),
(5898, 84, 14, 'و گمان کرد که ابدا (به سوی خدا) باز نخواهد گشت.'),
(5899, 84, 15, 'بلی خدا به احوال و اعمال او کاملا آگاه است.'),
(5900, 84, 16, 'چنین نیست (که خدا از فعل مخلوقش آگه نباشد) قسم به شفق (و روشنی او وقت غروب، هنگام نماز مغرب).'),
(5901, 84, 17, 'و قسم به شب تار و آنچه در خود گرد آورده (و در دل شب خزیده و آرام گرفته).'),
(5902, 84, 18, 'و قسم به ماه تابان هنگام بدر که تمام فروزان شود.'),
(5903, 84, 19, '(قسم به این امور) که شما حوال گوناگون و حوادث رنگارنگ (از نخستین خلقت تا مرگ و برزخ و ورود به بهشت و دوزخ) خواهید یافت.'),
(5904, 84, 20, 'پس کافران از چه رو ایمان نمی‌آورند؟'),
(5905, 84, 21, 'و چون بر آنان آیات قرآن تلاوت شود سجده حق نمی‌کنند؟'),
(5906, 84, 22, 'بلکه آن مردم کافر (قرآن خدا را) تکذیب می‌کنند.'),
(5907, 84, 23, 'و خدا به آنچه در دل پنهان می‌دارند (از خود آنها) داناتر است.'),
(5908, 84, 24, 'پس آنها را به عذاب دردناک (دوزخ) بشارت ده.'),
(5909, 84, 25, 'مگر آنان که ایمان آرند و نیکوکار شوند که آنها را اجری بی منّت و ثوابی بی نهایت (در بهشت جاودانی) خواهد بود.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'قسم به آسمان بلند که دارای کاخهای با عظمت است.'),
(5911, 85, 2, 'و قسم به روز موعود (قیامت که وعده گاه خلایق است).'),
(5912, 85, 3, 'و قسم به شاهد (عالم، پیغمبر خاتم صلّی اللّه علیه و آله و سلّم) و به مشهود (او قیامت و امت).'),
(5913, 85, 4, 'قسم به اینان که اصحاب اخدود (یعنی آن قوم کافری که بر مؤمنان به مکر مسلط شدند و آنها را به خندقهای آتش در افکندند) همه کشته (یعنی ملعون و مطرود) شدند.'),
(5914, 85, 5, 'همان آتش سختی که (آن مردم ظالم به جان خلق) بر افروختند.'),
(5915, 85, 6, 'که بر کنار آن خندقهای آتش بنشستند.'),
(5916, 85, 7, 'و سوختن مؤمنانی که به آتش افکندند مشاهده می‌کردند.'),
(5917, 85, 8, 'و با آن مؤمنان که بسوختند هیچ عداوتی نداشتند جز آنکه آنان به خدای مقتدر ستوده صفات ایمان آورده بودند.'),
(5918, 85, 9, 'آن خدایی که فرمانروایی آسمانها و زمین از آن اوست، و خدا بر هر چه در عالم است گواه است.'),
(5919, 85, 10, 'آنان که مردان و زنان با ایمان را به آتش فتنه سوختند و توبه نکردند، بر آنها عذاب جهنم و آتش سوزان دوزخ مهیاست.'),
(5920, 85, 11, 'آنان که به خدا ایمان آورده و نیکوکار شدند بر آنها البته (در بهشت) باغهایی است که زیر درختانش نهرها جاری است. این بهشت ابد به حقیقت سعادت و فیروزی بزرگ است.'),
(5921, 85, 12, 'همانا مؤاخذه و انتقام خدا بسیار سخت است.'),
(5922, 85, 13, 'اوست که به حقیقت نخست خلق را بیافریند و باز (پس از مرگ به عرصه قیامت) برگرداند.'),
(5923, 85, 14, 'و هم او بسیار آمرزنده و دوستدار مؤمنان است.'),
(5924, 85, 15, 'و خداوند عرش (عظیم) و هم با اقتدار و عزت است.'),
(5925, 85, 16, 'هر چه بخواهد آن را در کمال قدرت و اختیار انجام دهد.'),
(5926, 85, 17, 'آیا احوال جنود (شاهان گذشته) بر تو حکایت شده است؟'),
(5927, 85, 18, 'داستان فرعون و قوم ثمود (که به کیفر کفر و دشمنی با رسل هلاک شدند).'),
(5928, 85, 19, 'بلی آنان که کافرند به تکذیب می‌پردازند.'),
(5929, 85, 20, 'و خدا بر همه آنها محیط است.'),
(5930, 85, 21, 'بلکه این کتاب قرآن بزرگوار و ارجمند الهی است.'),
(5931, 85, 22, 'که در لوح محفوظ حق (و صفحه عالم ازلی) نگاشته است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'قسم به آسمان و طارق آن.'),
(5933, 86, 2, 'و چگونه توانی طارق آسمان را بدانی؟'),
(5934, 86, 3, 'طارق همان کوکب درخشان است که نورش نفوذ کند.'),
(5935, 86, 4, '(قسم به اینان) که هیچ شخصی نیست جز آنکه او را البته (از طرف خدا) مراقب و نگهبانی هست.'),
(5936, 86, 5, 'پس انسان مغرور باید بنگرد که از چه آفریده شده است؟'),
(5937, 86, 6, 'از آب نطفه جهنده‌ای خلقت گردیده.'),
(5938, 86, 7, 'که از میان صلب (پدر) و سینه (مادر) بیرون آید.'),
(5939, 86, 8, '(خدایی که او را از این نا چیز بی جان آفرید و جان بخشید) او البته بر زنده کردنش دوباره (پس از مرگ) قادر است.'),
(5940, 86, 9, 'روزی که اسرار باطن شخص آشکار شود.'),
(5941, 86, 10, 'و آن روز قوّتی در خویش و یاوری بر نجات خود نیابد.'),
(5942, 86, 11, 'قسم به آسمان فرو ریزنده باران.'),
(5943, 86, 12, 'و قسم به زمین گیاه روینده (برای خلقان).'),
(5944, 86, 13, 'که قرآن به حقیقت کلام جدا کننده (حق از باطل) است.'),
(5945, 86, 14, 'و هرگز سخن هزل بیهوده نیست.'),
(5946, 86, 15, 'دشمنان اسلام هر چه بتوانند کید و مکر (بر محو اسلام) می‌کنند.'),
(5947, 86, 16, 'و من هم در مقابل مکرشان مکر خواهم کرد.'),
(5948, 86, 17, 'پس اندکی کافران را مهلت ده و آنها را فرو گذار (تا وقت کیفرشان فرا رسد).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, '(ای رسول ما) نام خدای خود را که برتر و بالاتر (از همه موجودات) است به پاکی یاد کن.'),
(5950, 87, 2, 'آن خدایی که (عالم را) خلق کرد و (همه را) به حد کمال خود رسانید.'),
(5951, 87, 3, 'آن خدایی که (هر چیز را) قدر و اندازه‌ای داد و (به راه کمالش) هدایت نمود.'),
(5952, 87, 4, 'آن خدایی که گیاه را سبز و خرم از زمین برویانید.'),
(5953, 87, 5, 'و آن گاه خشک و سیاهش گردانید.'),
(5954, 87, 6, 'ما تو را قرائت آیات قرآن چندان آموزیم که هیچ فراموش نکنی.'),
(5955, 87, 7, 'مگر آنچه خدا خواهد (که از یادت برد) که او به امور آشکار و پنهان عالم آگاه است.'),
(5956, 87, 8, 'و ما تو را بر طریقه (شریعت سهل و) آسان موفق می‌داریم.'),
(5957, 87, 9, 'پس (به آیات الهی خلق را) متذکر ساز اگر سودمند افتد.'),
(5958, 87, 10, 'البته هر که خدا ترس باشد (به این تذکر) پند می‌گیرد.'),
(5959, 87, 11, 'و آن که شقی‌ترین مردم است از آن دوری گزیند.'),
(5960, 87, 12, 'همان کس که عاقبت به آتش بسیار سخت دوزخ در افتد.'),
(5961, 87, 13, 'و در آن دوزخ نه بمیرد (تا راحت شود) و نه زنده ماند (و برخوردار از زندگانی باشد).'),
(5962, 87, 14, 'حقّا فلاح و رستگاری یافت آن کسی که تزکیه نفس کرد.'),
(5963, 87, 15, 'و به ذکر نام خدا به نماز و طاعت پرداخت.'),
(5964, 87, 16, '(اما شما مردم از جهل و غفلت از پی این سعادت نروید) بلکه زندگانی دنیا را بگزینید و عزیز دارید.'),
(5965, 87, 17, 'در صورتی که منزل آخرت بسی بهتر و پاینده‌تر (از دنیای چند روزه) است.'),
(5966, 87, 18, 'این گفتار به حقیقت در کتب پیشین ذکر شده.'),
(5967, 87, 19, 'بخصوص در صحف ابراهیم و تورات موسی (مفصل بیان گردیده است).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, '(ای رسول ما) آیا خبر هولناک قیامت و بلیّه عالم گیر محشر بر تو حکایت شده است؟'),
(5969, 88, 2, 'که آن روز رخسار گروهی (کافر و متکبر) ترسناک و ذلیل باشد.'),
(5970, 88, 3, 'و همه کارشان رنج و مشقّت است.'),
(5971, 88, 4, 'به آتش فروزان دوزخ در آیند.'),
(5972, 88, 5, 'از چشمه آب گرم جهنم آبشان نوشانند.'),
(5973, 88, 6, 'طعامی غیر ضریع دوزخ (که علفی بد طعم و بوست) غذای آنها نیست.'),
(5974, 88, 7, 'که آن طعام (هر چه خورند) نه فربه‌شان کند و نه سیرشان گرداند.'),
(5975, 88, 8, 'جمعی دیگر رخسارشان شادمان و خندان است.'),
(5976, 88, 9, 'از سعی و کوشش خود (در طاعت خدا) خشنودند.'),
(5977, 88, 10, 'در بهشت بلند مرتبت مقام عالی یافته‌اند.'),
(5978, 88, 11, 'و در آنجا هیچ سخن زشت و بیهوده نشنوند.'),
(5979, 88, 12, 'در آن بهشت چشمه‌ها (ی آب زلال و گوارا) جاری است.'),
(5980, 88, 13, 'و هم آنجا تختهای عالی و کرسیهای بلند پایه نهاده‌اند.'),
(5981, 88, 14, 'و قدحهای بزرگ (بهترین شراب) گذاشته‌اند.'),
(5982, 88, 15, 'و مسند و بالشهای لطیف مرتب داشته‌اند.'),
(5983, 88, 16, 'و فرشهای عالی گرانبها گسترده‌اند.'),
(5984, 88, 17, 'آیا مردم در خلقت شتر نمی‌نگرند که چگونه (به انواع حکمت و منفعت برای بشر) خلق شده است؟'),
(5985, 88, 18, 'و در خلقت کاخ بلند آسمان فکر نمی‌کنند که چگونه آن را بر افراشته‌اند؟'),
(5986, 88, 19, 'و کوهها را نمی‌بینند که چگونه بر زمین کوبیده‌اند؟'),
(5987, 88, 20, 'و به زمین نظر نمی‌کنند که چگونه گسترده‌اند؟'),
(5988, 88, 21, 'پس خلق را (به حکمتهای الهی) متذکر ساز که وظیفه پیغمبری تو غیر از این نیست.'),
(5989, 88, 22, 'تو مسلط و توانا بر (تبدیل کفر و ایمان) آنها نیستی.'),
(5990, 88, 23, 'جز آنکه هر کس (پس از تذکر) روی از حق بگرداند و کافر شود.'),
(5991, 88, 24, 'او را خدا به عذابی بزرگ (در دوزخ) معذب کند.'),
(5992, 88, 25, 'البته باز گشت آنها به سوی ماست.'),
(5993, 88, 26, 'آن گاه حسابشان بر ما خواهد بود.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'قسم به صبحگاه (هنگامی که خدا جهان را به نور خورشید تابان روشن می‌سازد).'),
(5995, 89, 2, 'و قسم به ده شب (اول ذیحجه که بندگان خدا به دعا و ذکر مشغولند).'),
(5996, 89, 3, 'قسم به حق جفت (که کلیه موجودات عالم است) و به حقّ فرد (که ذات یکتای خداست).'),
(5997, 89, 4, 'و قسم به شب تار هنگامی که برود.'),
(5998, 89, 5, 'آیا در این امور که (قسم به آنها یاد شد) نزد اهل خرد لیاقت سوگند نیست؟'),
(5999, 89, 6, 'آیا ندیدی که خدای تو با عاد (قوم هود) چه کرد؟'),
(6000, 89, 7, 'و نیز به اهل شهر ارم (یا قوم ارم) که صاحب قدرت و عظمت بودند چگونه کیفر داد؟'),
(6001, 89, 8, 'در صورتی که مانند آن شهری (در استحکام و بزرگی و تنعّم) در بلاد عالم ساخته نشده بود.'),
(6002, 89, 9, 'و نیز به قوم ثمود که در دل آن وادی سنگ را شکافته و کاخها بر خود از سنگ می‌ساختند چه کیفر سخت داد؟'),
(6003, 89, 10, 'و نیز فرعون (و فرعونیان) را که صاحب قدرت و سپاه بسیار بود (چگونه به دریای هلاک غرق نمود).'),
(6004, 89, 11, 'آنان که در روی زمین ظلم و طغیان کردند.'),
(6005, 89, 12, 'و بسیار فساد و فتنه انگیختند.'),
(6006, 89, 13, 'تا آنکه خدای تو بر آنها تازیانه عذاب پی در پی فرستاد.'),
(6007, 89, 14, 'خدای تو البته در کمینگاه (ستمکاران) است.'),
(6008, 89, 15, 'اما انسان (کم ظرف ضعیف بی صبر) چون خدا او را برای آزمایش و امتحان کرامت و نعمتی بخشد در آن حال (مغرور ناز و نعمت شود و) گوید: خدا مرا عزیز و گرامی داشت.'),
(6009, 89, 16, 'و چون او را باز برای آزمودن تنگ روزی کند (دلتنگ و غمین شود و) گوید: خدا مرا خوار گردانید.'),
(6010, 89, 17, 'چنین نیست بلکه (به گناه بخل و طمع خوار شوید چون) هرگز یتیم نوازی نکنید.'),
(6011, 89, 18, 'و فقیر را بر سفره طعام خود به میل و رغبت ننشانید.'),
(6012, 89, 19, 'و مال ارث را به تمامی می‌خورید (و مراعات حق وارثان ضعیف مانند زنان و دختران و صغیران را نمی‌کنید).'),
(6013, 89, 20, 'و سخت فریفته و مایل به مال دنیا می‌باشید (و به ثواب آخرت هیچ نمی‌پردازید).'),
(6014, 89, 21, 'چنین نیستت (که دنیا طلبان پندارند روز قیامتی نیست) روزی کهه (از زلزله پی در پی) زمین به کلی خرد و متلاشی شود.'),
(6015, 89, 22, 'و (آن هنگام امر) خدا و فرشتگان صف در صف به عرصه محشر آیند.'),
(6016, 89, 23, 'و آن روز جهنم را (پدید) بیاورند، همان روز آدمی متذکر کار خود گردد، و آن تذکر چه سود به حال او بخشد؟'),
(6017, 89, 24, '(با حسرت و ندامت) گوید: ای کاش برای زندگانی ابدی امروزم کار خیری انجام می‌دادم.'),
(6018, 89, 25, 'و آن روز به مانند عذاب خدا هیچ کس عذاب نکند.'),
(6019, 89, 26, 'و به مانند به بند هلاک کشیدن او هیچ کس به بند نکشد.'),
(6020, 89, 27, '(آن هنگام به اهل ایمان خطاب لطف رسد که) ای نفس (قدسی) مطمئن و دل آرام.'),
(6021, 89, 28, 'به حضور پروردگارت باز آی که تو خشنود به (نعمتهای ابدی) او و او راضی از توست.'),
(6022, 89, 29, 'باز آی و در صف بندگان خاص من در آی.'),
(6023, 89, 30, 'و در بهشت من داخل شو.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'نه، سوگند به این بلد (مکّه معظّم و مسجد کعبه محترم).'),
(6025, 90, 2, 'و حال آنکه تو (ای رسول گرامی) در این بلد منزل داری.'),
(6026, 90, 3, 'و قسم به پدر (بزرگوار انسان، آدم صفی) و فرزندان (خدا پرست) او.'),
(6027, 90, 4, 'که ما انسان را به حقیقت در رنج و مشقّت آفریدیم (و به بلا و محنتش آزمودیم).'),
(6028, 90, 5, 'آیا انسان پندارد که هیچ کس بر او توانایی ندارد؟'),
(6029, 90, 6, 'می‌گوید: من مال بسیاری تلف کردم.'),
(6030, 90, 7, 'آیا پندارد احدی او را ندیده (و افکار و اعمال بدش را ندانسته و ریا و نفاقش را نمی‌داند).'),
(6031, 90, 8, 'آیا ما به او دو چشم عطا نکردیم؟'),
(6032, 90, 9, 'و زبان و دو لب به او ندادیم؟'),
(6033, 90, 10, 'و راه خیر و شر را به او ننمودیم؟'),
(6034, 90, 11, 'باز هم به عقبه (تکلیف) تن در نداد.'),
(6035, 90, 12, 'و چگونه توان دانست که آن عقبه (تکلیف و ایمان) چیست؟'),
(6036, 90, 13, 'آن بنده (در راه خدا) آزاد کردن است.'),
(6037, 90, 14, 'و طعام دادن در روز قحطی و گرسنگی است.'),
(6038, 90, 15, 'به یتیم خویشاوندان خود.'),
(6039, 90, 16, 'یا به فقیر مضطرّ خاک نشین.'),
(6040, 90, 17, 'و آن گاه هم از آنان باشد که ایمان آورده و یکدیگر را به اهمیتت صبر و مهربانی با خلق سفارش می‌کنند.'),
(6041, 90, 18, 'که آنها اهل یمین و سعادتند (و نامه عمل به دست راست گیرند).'),
(6042, 90, 19, 'و آنان که به آیات ما (قرآن و دیگر کتب آسمانی) کافر شدند آنها اهل شومی و شقاوتند (نامه عمل به دست چپ دارند).'),
(6043, 90, 20, 'بر آن کافران آتشی سر پوشیده (که از آن راه گریزی نیست) احاطه خواهد کرد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'قسم به آفتاب و تابش آن (هنگام رفعتش).'),
(6045, 91, 2, 'و قسم به ماه آن گاه که در پی آفتاب تابان در آید.'),
(6046, 91, 3, 'و قسم به روز هنگامی که جهان را روشن سازد.'),
(6047, 91, 4, 'و به شب وقتی که عالم را در پرده سیاهی کشد.'),
(6048, 91, 5, 'و قسم به آسمان بلند و آن که این کاخ رفیع را بنا کرد.'),
(6049, 91, 6, 'و به زمین و آن که آن را بگسترد.'),
(6050, 91, 7, 'و قسم به نفس ناطقه انسان و آن که او را نیکو به حد کمال بیافرید.'),
(6051, 91, 8, 'و به او شر و خیر او را الهام کرد.'),
(6052, 91, 9, '(قسم به این آیات الهی) که هر کس نفس ناطقه خود را از گناه و بدکاری پاک و منزه سازد به یقین (در دو عالم) رستگار خواهد بود.'),
(6053, 91, 10, 'و هر که او را (به کفر و گناه) پلید گرداند البته (در دو جهان) زیانکار خواهد گشت.'),
(6054, 91, 11, 'طایفه ثمود از غرور و سرکشی (پیغمبر خود صالح را) تکذیب کردند.'),
(6055, 91, 12, 'هنگامی که شقی‌ترینشان (قدار بن سالف) بر انگیخته شد (تا ناقه صالح را پی کند).'),
(6056, 91, 13, 'و رسول خدا (صالح) به آنها گفت: این ناقه آیت خداست، از خدا بترسید و آن را سیراب گردانید.'),
(6057, 91, 14, 'آن قوم رسول را تکذیب و ناقه او را پی کردند، خدا هم آنان را به کیفر (ظلم و) گناهشان هلاک ساخت و شهرشان را با خاک یکسان نمود.'),
(6058, 91, 15, 'و هیچ باک از هلاک آنها نمی‌داشت (یا آنها هیچ از عاقبت بدگناه خود نمی‌ترسیدند).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'قسم به شب تار هنگامی که (جهان را در پرده سیاه) بپوشاند.'),
(6060, 92, 2, 'و قسم به روز هنگامی که روشن و فروزان گردد.'),
(6061, 92, 3, 'و قسم به مخلوقات عالم که خدا همه را جفت نر و ماده بیافرید.'),
(6062, 92, 4, '(قسم به این آیات الهی) که سعی و کوشش شما مردم بسیار مختلف است (برخی بر صلاح و برخی بر فساد می‌کوشید).'),
(6063, 92, 5, 'اما هر کس عطا و احسان کرد و خدا ترس و پرهیزکار شد.'),
(6064, 92, 6, 'و به نیکویی (یعنی به نعیم آخرت با هر خیر و سعادت که در قرآن بیان شده) تصدیق کرد.'),
(6065, 92, 7, 'ما هم البته کار او را (در دو عالم) سهل و آسان می‌گردانیم.'),
(6066, 92, 8, 'اما هر کس بخل ورزید و خود را (از لطف خدا) بی نیاز دانست.'),
(6067, 92, 9, 'و نیکویی را تکذیب کرد.'),
(6068, 92, 10, 'پس به زودی کار او را (در دو عالم) دشوار می‌کنیم.'),
(6069, 92, 11, 'و گاه عذاب و هلاکت داراییش وی را هیچ نجات نتواند داد.'),
(6070, 92, 12, 'و البته بر ماست که (خلق را) هدایت کنیم.'),
(6071, 92, 13, 'و ملک دنیا و آخرت از ماست (به هر که بخواهیم و صلاح دانیم می‌بخشیم).'),
(6072, 92, 14, 'من شما را از آتش شعله‌ور دوزخ ترسانیدم و آگاه ساختم.'),
(6073, 92, 15, 'که هیچ کس در آن آتش در نیفتد مگر شقی‌ترین خلق.'),
(6074, 92, 16, 'همان کس که (آیات و رسل حق را) تکذیب کرد و روی از آن بگردانید.'),
(6075, 92, 17, 'و اهل تقوا را از آن آتش دور سازند.'),
(6076, 92, 18, 'آن کس که مال خود را (به فقیران اسلام) به وجه زکات بدهد.'),
(6077, 92, 19, 'و حال آنکه هیچ کس بر وی حق نعمت ندارد تا به پاداش آن بدهد.'),
(6078, 92, 20, '(او احسان نکند) جز در طلب رضای خدای خود که برتر و بالاترین موجودات است.'),
(6079, 92, 21, 'و البته (در بهشت آخرت به آن نعمتهای ابدی) خشنود خواهد گردید.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'قسم به روز روشن (یا هنگام ظهر آن).'),
(6081, 93, 2, 'و قسم به شب به هنگام آرامش آن.'),
(6082, 93, 3, 'که خدای تو هیچ گاه تو را ترک نگفته و بر تو خشم ننموده است (چهل روز بر رسول وحی نیامد، دشمنان به طعنه گفتند: خدا از محمد قهر کرده. این آیه رد بر آنهاست).'),
(6083, 93, 4, 'و البته عالم آخرت برای تو بسی بهتر از نشأه دنیاست.'),
(6084, 93, 5, 'و پروردگار تو به زودی به تو چندان عطا کند که تو راضی شوی (در دنیا نصرت و در آخرت مقام شفاعت به تو بخشد).'),
(6085, 93, 6, 'آیا خدا تو را یتیمی نیافت که در پناه خود جای داد؟'),
(6086, 93, 7, 'و تو را (در بیابان مکه) ره گم کرده و حیران یافت (در طفولیت که حلیمه دایه‌ات آورد تا به جدّت عبد المطّلب سپارد در راه مکه گم شدی، حلیمه و عبد المطلب سخت پریشان شدند و خدا زود آنها را به تو) ره نمایی کرد.'),
(6087, 93, 8, 'و باز تو را فقیر (الی الله) یافت (به دولت نبوت) توانگر کرد (و نعمت رسالت عطا نمود).'),
(6088, 93, 9, 'پس تو هم یتیم را هرگز میازار.'),
(6089, 93, 10, 'و فقیر و گدای سائل را به زجر مران.'),
(6090, 93, 11, 'و اما نعمت پروردگارت را (که مقام نبوت و تقرب کامل به خدا یافتی، بر امت) باز گو.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, '(ای رسول گرامی) آیا ما تو را (به نعمت حکمت و رسالت) شرح صدر (و بلندی همّت) عطا نکردیم؟'),
(6092, 94, 2, 'و بار سنگین (گناه) را (به اعطای مقام عصمت) از تو برداشتیم.'),
(6093, 94, 3, 'در صورتی که آن بار سنگین ممکن بود پشت تو را گران دارد.'),
(6094, 94, 4, 'و نام نکوی تو را (به رغم دشمنان، در عالم) بلند کردیم.'),
(6095, 94, 5, 'پس (بدان که به لطف خدا) با هر سختی البته آسانی هست.'),
(6096, 94, 6, 'و با هر سختی البته آسانی هست.'),
(6097, 94, 7, 'پس چون از نماز و طاعت پرداختی برای دعا همت مصروف دار.'),
(6098, 94, 8, 'و به سوی خدای خود همیشه متوجه و مشتاق باش.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'قسم به تین و زیتون (دو میوه معروف انجیر و زیت یا دو معبد بزرگ کعبه و بیت المقدس).'),
(6100, 95, 2, 'و قسم به طور سینا.'),
(6101, 95, 3, 'و قسم به این شهر امن و امان (مکّه معظّم).'),
(6102, 95, 4, 'که ما انسان را در نیکوترین صورت (در مراتب وجود) بیافریدیم.'),
(6103, 95, 5, 'سپس (به کیفر کفر و گناهش) به اسفل سافلین (جهنم و پست‌ترین رتبه امکان) برگردانیدیم.'),
(6104, 95, 6, 'مگر آنان که ایمان آورده و نیکوکار شدند که به آنها پاداش دائمی (بهشت ابد) عطا کنیم.'),
(6105, 95, 7, 'پس (ای انسان مشرک ناسپاس) چه تو را بر آن داشت که دین حق و روز جزا را تکذیب کنی؟'),
(6106, 95, 8, 'آیا خدا مقتدرترین و عادل‌ترین حکمفرمایان عالم نیست؟');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, '(ای رسول گرامی برخیز و قرآن را) به نام پروردرگارت که خدای آفریننده عالم است (بر خلق) قرائت کن.'),
(6108, 96, 2, 'آن خدایی که آدمی را از خون بسته (که تحول نطفه است) بیافرید.'),
(6109, 96, 3, 'بخوان و (بدان که) پروردگار تو کریم‌ترین کریمان عالم است.'),
(6110, 96, 4, 'آن خدایی که بشر را علم نوشتن به قلم آموخت.'),
(6111, 96, 5, 'به آدمی آنچه را که نمی‌دانست تعلیم داد.'),
(6112, 96, 6, 'راستی که انسان سرکش و مغرور می‌شود.'),
(6113, 96, 7, 'چون که خود را در غنا و دارایی ببیند.'),
(6114, 96, 8, 'محققا (پس از مرگ) باز گشت به سوی پروردگار تو خواهد بود.'),
(6115, 96, 9, 'دیدی آن کس را که منع (و تمسخر) می‌کرد؟'),
(6116, 96, 10, 'آن بنده خدای را که به نماز مشغول شد؟ (مراد ابو جهل است که بر نماز، پیغمبر و اصحابش را به مسخرگی می‌آزرد).'),
(6117, 96, 11, 'آیا چه می‌بینی اگر آن بنده (یا رسول) به راه راست باشد.'),
(6118, 96, 12, 'و خلق را به تقوا و پرهیزکاری امر کند (حال آنکه او را از نماز باز می‌دارد چگونه خواهد بود).'),
(6119, 96, 13, 'آیا شما مردم بر این کس که حق را تکذیب می‌کند و (از رسول او) رو می‌گرداند چه رأی می‌دهید؟'),
(6120, 96, 14, 'آیا او ندانست که خدا (اعمال زشتش را) می‌بیند (و از او روزی انتقام می‌کشد).'),
(6121, 96, 15, 'هرگز (این را نداند)، اگر او (از کفر و ظلم و تکذیبش) دست نکشد البته ما موی پیشانیشن (به قهر و انتقام) بگیریم.'),
(6122, 96, 16, 'آن پیشانی دروغزن خطا پیشه را (به خاک هلاک کشیم).'),
(6123, 96, 17, 'آن گاه او هر که از قبیله و عشیره خود را خواهد بخواند (که از هلاکش برهانند و هیچ کس نتواند).'),
(6124, 96, 18, 'ما هم زبانیه دوزخ را (که فرشتگان قهر و عذاب و مأموران آتش جهنم‌اند، بر گرفتن او) می‌خوانیم.'),
(6125, 96, 19, '(ای رسول گرامی) چنین نیست (که ابو جهل پنداشته که تو را به زجر و ظلم مطیع خود تواند کرد) تو هیچ از او اطاعت مکن و به نماز و سجده خدا پرداز و به حق نزدیک شو (که سجده و نماز موجب قرب حضرت بی نیاز است).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما این قرآن عظیم الشأن را در شب قدر نازل کردیم.'),
(6127, 97, 2, 'و چه تو را به عظمت این شب قدر آگاه تواند کرد؟'),
(6128, 97, 3, 'شب قدر از هزار ماه بهتر و بالاتر است.'),
(6129, 97, 4, 'در این شب فرشتگان و روح (یعنی جبرئیل) به اذن خدا (بر مقام ولایت نبی و امام عصر علیه السّلام) از هر فرمان و دستور الهی نازل می‌شوند (و سرنوشت و مقدرات خلق را نازل می‌گردانند).'),
(6130, 97, 5, 'این شب (رحمت و) سلامت و تهنیت است تا صبحگاه.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کافران اهل کتاب و مشرکان (از کفر و عصیان) منفک نبودند (و به راه صواب هدایت نمی‌یافتند) تا آنکه برهان و حجت حق بر آنها بیاید.'),
(6132, 98, 2, 'رسولی از جانب خدا (چون محمّد صلّی اللّه علیه و آله و سلّم) که کتب آسمانی پاک و منزه (از خطا و تحریف) را بر آنها تلاوت کند.'),
(6133, 98, 3, 'که در آن کتب نامه‌های حقیقت و راستی (و نگهبان سنّت عدل الهی) مسطور است.'),
(6134, 98, 4, 'و اهل کتاب (در حق این رسول گرامی) راه تفرقه و خلاف نپیمودند مگر پس از آنکه آنها را حجت کامل (در کتب آسمانی بر حقانیت رسول) آمد (و دانسته بر انکار او لجاج و عناد ورزیدند).'),
(6135, 98, 5, 'در صورتی که (در کتب آسمانی) امر نشده بودند مگر بر اینکه خدا را به اخلاص کامل در دین (اسلام) پرستش کنند و از غیر دین حق روی بگردانند و نماز به پا دارند و زکات (به فقیران) بدهند. این است دین درست.'),
(6136, 98, 6, 'محقّقا آنان که از اهل کتاب کافر شدند (و عیسی و عزیر و رهبانان و احبار را به مقام ربوبیت خواندند) آنها با مشرکان همه در آتش دوزخند و در آن همیشه معذبند، آنها به حقیقت بدترین خلقند.'),
(6137, 98, 7, 'آنان که ایمان آوردند و نیکوکار شدند آنها به حقیقت بهترین اهل عالمند.'),
(6138, 98, 8, 'پاداش آنها نزد خدایشان باغهای بهشت عدن است که نهرها زیر درختانش جاری است و در آن بهشت ابد جاودان متنعّمند، خدا از آنها خشنود و آنها هم از خدا خشنودند. این بهشت مخصوص کسی است که از خدا ترسد (و به طاعت او پردازد).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'هنگامی که زمین به سخت‌ترین زلزله خود به لرزه در آید.'),
(6140, 99, 2, 'و بارهای سنگین اسرار درون خویش (که گنجها و معادن و اموات و غیره است) همه را از دل خاک بیرون افکند.'),
(6141, 99, 3, 'و (آن روز) آدمی (از فرط حیرت و اضطراب) گوید: زمین را چه پیش آمده؟'),
(6142, 99, 4, 'آن هنگام زمین مردم را به حوادث خویش آگاه می‌سازد.'),
(6143, 99, 5, 'که خدا به او چنین الهام کند (تا به سخن آید و خلق را به اخبارش آگه نماید).'),
(6144, 99, 6, 'درآن روز قیامت مردم از قبرها پراکنده بیرون آیند تا (پاداش نیک و بد) اعمال آنها را (در حساب و میزان حق) به آنان بنمایند.'),
(6145, 99, 7, 'پس هر کس به قدر ذره‌ای کار نیک کرده باشد (پاداش) آن را خواهد دید.'),
(6146, 99, 8, 'و هر کس به قدر ذره‌ای کار زشتی مرتکب شده آن هم به کیفرش خواهد رسید.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'قسم به اسبانی که (سواران اسلام در جهاد با کفار تاختند تا جایی که) نفسشان به شماره افتاد.'),
(6148, 100, 2, 'و در تاختن از سم خود بر سنگ آتش افروختند.'),
(6149, 100, 3, 'و (بر دشمن شبیخون زدند تا) صبحگاه (آنها را) به غارت گرفتند.'),
(6150, 100, 4, 'و گرد و غبار از دیار کفار بر انگیختند.'),
(6151, 100, 5, 'و سپاه دشمن را همه در میان گرفتند.'),
(6152, 100, 6, '(قسم به اسبان این مجاهدان دین خدا) که انسان نسبت به پروردگارش کافر نعمت و ناسپاس است.'),
(6153, 100, 7, 'و (خدا و یا) خود او بر این ناسپاسی محققا گواه است.'),
(6154, 100, 8, 'و هم او بر حب مال دنیا سخت فریفته و بخیل است.'),
(6155, 100, 9, 'آیا آدمی نمی‌داند که روزی آنچه (از مردگان) در دل قبرهاست همه بیرون ریخته می‌شود؟'),
(6156, 100, 10, 'و آنچه در دلها پنهان است همه را پدیدار سازند؟'),
(6157, 100, 11, 'محققا آن روز پروردگارشان (نیک و بد کردار) آنها کاملا آگاه است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'قارعه.'),
(6159, 101, 2, 'قارعه چیست؟ (همان روز وحشت و اضطراب مرگ و قیامت است که دلهای خلایق را از ترس در هم کوبد).'),
(6160, 101, 3, 'و چگونه حالت آن روز هولناک را تصور توانی کرد؟'),
(6161, 101, 4, 'در آن روز سخت، مردم مانند ملخ هر سو پراکنده شوند.'),
(6162, 101, 5, 'و کوهها همچون پشم زده شده متلاشی گردد.'),
(6163, 101, 6, 'پس عمل هر کس را در میزان حق وزنی باشد،'),
(6164, 101, 7, 'او در بهشت به آسایش و زندگانی خوش خواهد بود.'),
(6165, 101, 8, 'و عمل هر کس بی قدر و سبک وزن باشد،'),
(6166, 101, 9, 'جایگاهش در قعر هاویه (جهنم) است.'),
(6167, 101, 10, 'و چگونه سختی هاویه را تصور توانی کرد؟'),
(6168, 101, 11, 'هاویه همان آتش سخت سوزنده و گدازنده است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'شما مردم را افتخار به بسیاری اموال و فرزند و عشیره سخت (از یاد خدا و مرگ) غافل داشته است.'),
(6170, 102, 2, 'تا آنجا که به ملاقات (اهل) قبور رفتید (و به جای آنکه عبرت گیرید آنجا هم به قبرهای مردگان خود بر هم مفاخرت کردید).'),
(6171, 102, 3, 'نه چنین است، به زودی خواهید دانست (که پس از مرگ به برزخ چه سختیها در پیش دارید).'),
(6172, 102, 4, 'باز هم چنین نیست، حقا خواهید دانست (که به قیامت با چه عذابها مواجهید).'),
(6173, 102, 5, 'نه چنین است، حقا اگر به طور یقین می‌دانستید (چه حادثه بزرگی در پیش دارید هرگز به بازی دنیا از عالم آخرت غافل نمی‌شدید).'),
(6174, 102, 6, 'البته (پس از مرگ) دوزخ را مشاهده خواهید کرد.'),
(6175, 102, 7, 'و سپس به چشم یقین آن دوزخ را می‌بینید.'),
(6176, 102, 8, 'آن گاه در آن روز از نعمتها شما را باز می‌پرسند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'قسم به عصر (نورانی رسول صلّی اللّه علیه و آله و سلّم یا دوران ظهور ولی عصر علیه السّلام).'),
(6178, 103, 2, 'که انسان هم در خسارت و زیان است.'),
(6179, 103, 3, 'مگر آنان که ایمان آورده و نیکوکار شدند و به درستی و راستی و پایداری (در دین) یکدیگر را سفارش کردند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر عیبجوی هرزه زبان.'),
(6181, 104, 2, 'همان کسی که مالی جمع کرده و دایم به حساب و شماره‌اش سر گرم است.'),
(6182, 104, 3, 'پندارد که مال و دارایی دنیایش عمر ابدش خواهد بخشید.'),
(6183, 104, 4, 'چنین نیست، بلکه محققا به آتش در هم شکننده دوزخ در افتد.'),
(6184, 104, 5, 'آتشی که چگونه تصور سختی آن توانی کرد؟'),
(6185, 104, 6, 'آن آتش را (خشم) خدا افروخته.'),
(6186, 104, 7, 'شراره آن بر دلها (ی نا پاک پر از حرص دنیای کافران) شعله‌ور است.'),
(6187, 104, 8, 'آتشی که بر آنها از هر سو سخت احاطه کرده و راه گریزی ندارند.'),
(6188, 104, 9, 'و مانند ستونهای بلند زبانه کشیده است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آیا ندیدی که خدای تو با اصحاب فیل (سپاه فیل سوار ابرهه) چه کرد؟'),
(6190, 105, 2, 'آیا کید و تدبیر آنها را (که برای خرابی کعبه اندیشیدند) تباه نکرد؟'),
(6191, 105, 3, 'و بر هلاک آنها مرغانی گروه گروه فرستاد.'),
(6192, 105, 4, 'تا آن سپاه را به سنگهای سجّیل (دوزخی) سنگباران کردند.'),
(6193, 105, 5, 'و تنشان را چون علفی زیر دندان حیوان خرد گردانید.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, '(خدا با اصحاب فیل چنین کرد) برای آنکه قریش (خداشناس شوند و) با هم انس و الفت گیرند.'),
(6195, 106, 2, 'الفتی که در سفرهای زمستان و تابستان آنان ثابت و بر قرار بماند.'),
(6196, 106, 3, 'پس (به شکرانه این دوستی) باید یگانه خدای این خانه (کعبه) را پرستند.'),
(6197, 106, 4, 'همان خدایی که به آنها هنگام گرسنگی طعام داد و از ترس و خطراتشان ایمن ساخت.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا دیدی آن کس را که روز جزا را انکار می‌کرد؟'),
(6199, 107, 2, 'این همان شخص (بی رحم) است که یتیم را (از در خود) به قهر می‌راند.'),
(6200, 107, 3, 'و (کسی را) بر اطعام فقیر ترغیب نمی‌کند.'),
(6201, 107, 4, 'پس وای بر آن نماز گزاران.'),
(6202, 107, 5, 'که دل از یاد خدا غافل دارند.'),
(6203, 107, 6, 'همانان که (اگر طاعتی کنند به) ریا و خود نمایی کنند.'),
(6204, 107, 7, 'و زکات و احسان را (از فقیران و هر خیر کوچک را حتی قرض را هم از محتاجان) منع کنند.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'ما تو را کوثر (یعنی عطای بسیار چون کثرت فرزند) بخشیدیم.'),
(6206, 108, 2, 'پس تو هم برای خدایت به نماز (و طاعت) و قربانی (و مناسک حج) بپرداز.'),
(6207, 108, 3, 'که محققا دشمن بدگوی تو (عاص بن وائل) مقطوع النسل است (و نسل تو تا قیامت به کثرت و برکت و عزت باقی است).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو که ای کافران (مشرک).'),
(6209, 109, 2, 'من آن بتان را که شما به خدایی می‌پرستید هرگز نمی‌پرستم.'),
(6210, 109, 3, 'و شما هم آن خدای یکتایی را که من پرستش می‌کنم پرستش نمی‌کنید.'),
(6211, 109, 4, 'نه من هرگز خدایان باطل شما را عبادت می‌کنم.'),
(6212, 109, 5, 'و نه شما یکتا خدای معبود مرا عبادت خواهید کرد.'),
(6213, 109, 6, 'پس اینک دین (شرک و جهل) شما برای شما باشد و دین (توحید و خدا پرستی) من هم برای من (تا روزی که به امر حق شما را از شرک برگردانم و به راه توحید خدا و خدا پرستی هدایت کنم).');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'چون هنگام فتح و فیروزی با یاری خدا فرا رسد (مراد فتح مکه است).'),
(6215, 110, 2, 'و مردم را بنگری که فوج فوج به دین خدا داخل می‌شوند (و تو را به رسالت تصدیق می‌کنند).'),
(6216, 110, 3, 'در آن وقت خدای خود را حمد و ستایش کن و (از لوث شرک) پاک و منزّه‌دان و از او مغفرت و آمرزش طلب که او خدای بسیار توبه پذیر است.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ابو لهب (که دایم در پی آزار و دشمنی پیغمبر بود با تمام اقتدار و دارایی) نابود شد و دو دستش (که سنگ به رسول می‌افکند) قطع گردید.'),
(6218, 111, 2, 'مال و ثروتی که اندوخت هیچ به کارش نیامد و از هلاکش نرهانید.'),
(6219, 111, 3, 'زود باشد که به دوزخ در آتشی شعله‌ور در افتد.'),
(6220, 111, 4, 'و نیز همسرش (امّ جمیل خواهر ابو سفیان) که هیزم آتش افروز دوزخ باشد.'),
(6221, 111, 5, 'در حالی که (با ذلت و خواری) طنابی از لیف خرما به گردن دارد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: حقیقت این است که خدا یکتاست.'),
(6223, 112, 2, 'آن خدایی که (از همه عالم) بی نیاز (و همه عالم به او نیازمند) است.'),
(6224, 112, 3, 'نه کسی فرزند اوست و نه او فرزند کسی است.'),
(6225, 112, 4, 'و نه هیچ کس مثل و مانند و همتای اوست.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: من پناه می‌جویم به خدای فروزنده صبح روشن.'),
(6227, 113, 2, 'از شرّ مخلوقات (شریر و مردم بد اندیش).'),
(6228, 113, 3, 'و از شرّ شب تار هنگامی که (از پی آزار) در آید (و حشرات موذی و جنایتکاران و فتنه انگیران را به کمک ظلمتش به ظلم و جور و ستم بر انگیزد).'),
(6229, 113, 4, 'و از شرّ زنان افسونگر که (به جادو) در گره‌ها بدمند.'),
(6230, 113, 5, 'و از شرّ حسود بد خواه چون شراره آتش رشک و حسد بر افروزد.');
INSERT INTO `fa_ghomshei` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: من پناه می‌جویم به پروردگار آدمیان.'),
(6232, 114, 2, 'پادشاه آدمیان.'),
(6233, 114, 3, 'یکتا معبود آدمیان.'),
(6234, 114, 4, 'از شرّ آن وسوسه‌گر نهانی.'),
(6235, 114, 5, 'آن شیطان که وسوسه و اندیشه بد افکند در دل مردمان.'),
(6236, 114, 6, 'چه آن شیطان از جنس جن باشد و یا از نوع انسان.');
